var book = {
  "name": "1 Krónika",
  "numChapters": 29,
  "chapters": {
    "1": {
      "1": "<span>1</span> Ádám, Sét, Enós,",
      "2": "<span>2</span> Kénán, Mahalalél, Jered,",
      "3": "<span>3</span> Énók, Metúselah, Lámek.",
      "4": "<span>4</span> Nóé, Sém, Hám és Jáfet.",
      "5": "<span>5</span> Jáfet fiai: Gómer, Mágóg, Mádaj, Jáván, Túbal, Mesek és Tírász.",
      "6": "<span>6</span> Gómer fiai pedig: Askenaz, Rífat és Tógarmá.",
      "7": "<span>7</span> Jáván fiai pedig: Elísá, Tarsísá, Kittím és Ródáním.",
      "8": "<span>8</span> Hám fiai voltak: Kús, Micraim, Pút és Kánaán.",
      "9": "<span>9</span> Kús fiai: Szebá, Havílá, Szabtá, Ramá és Szabteká. Ramá fiai pedig: Sebá és Dedán.",
      "10": "<span>10</span> Kús nemzette Nimródot is. Ő hatalmassá kezdett lenni a földön.",
      "11": "<span>11</span> Micraim nemzette a lúdiakat, az anámiakat, a lehábiakat és a naftúhiakat;",
      "12": "<span>12</span> a patrósziakat és a kaszlúhiakat, akiktől a filiszteusok és a kaftóriak származnak.",
      "13": "<span>13</span> Kánaán nemzette elsőszülöttjét, Szidónt és Hétet",
      "14": "<span>14</span> meg a jebúsziakat, emóriakat, girgásiakat,",
      "15": "<span>15</span> a hivvieket, arkiakat és színieket,",
      "16": "<span>16</span> az arvádiakat, cemáriakat és hamátiakat.",
      "17": "<span>17</span> Sém fiai voltak: Élám, Assúr, Arpaksad, Lúd, Arám, Úc, Húl, Geter és Mesek.",
      "18": "<span>18</span> Arpaksad nemzette Selahot, és Selah nemzette Hébert.",
      "19": "<span>19</span> Hébernek is lett két fia. Az egyiknek Peleg volt a neve, mert az ő idejében osztották föl a földet. Testvérének Joktán volt a neve.",
      "20": "<span>20</span> Joktán nemzette Almódádot, Selefet, Hacarmávetet és Jerahot,",
      "21": "<span>21</span> Hadórámot, Úzált és Diklát,",
      "22": "<span>22</span> Ébált, Abimáélt és Sebát,",
      "23": "<span>23</span> Ófírt, Havílát és Jóbábot. Ezek mind Joktán fiai.",
      "24": "<span>24</span> Sém, Arpaksad, Selah,",
      "25": "<span>25</span> Héber, Peleg, Reú,",
      "26": "<span>26</span> Szerúg, Náhór, Táré,",
      "27": "<span>27</span> Abrám, azaz Ábrahám.",
      "28": "<span>28</span> Ábrahám fiai: Izsák és Izmael.",
      "29": "<span>29</span> Nemzetségeik pedig ezek: Izmael elsőszülöttje Nebájót, azután Kédár, Abdeél és Mibszám,",
      "30": "<span>30</span> Mismá és Dúmá, Masszá, Hadad és Témá,",
      "31": "<span>31</span> Jetúr, Náfis és Kédmá. Ezek Izmael fiai.",
      "32": "<span>32</span> Ketúrának pedig, Ábrahám másodfeleségének fiai, akiket szült, ezek voltak: Zimrán, Joksán, Medán, Midján, Jisbák és Súah. Joksán fiai: Sebá és Dedán.",
      "33": "<span>33</span> Midján fiai: Éfá, Éfer, Hanók, Abídá és Eldáá. Mindezek Ketúrá fiai voltak.",
      "34": "<span>34</span> Ábrahám nemzette Izsákot. Izsák fiai: Ézsau és Izráel.",
      "35": "<span>35</span> Ézsau fiai: Elifáz, Reúél, Jeús, Jalám és Kórah.",
      "36": "<span>36</span> Elifáz fiai: Témán, Ómár, Cefí, Gatám, Kenaz, Timná és Amálék.",
      "37": "<span>37</span> Reúél fiai: Nahat, Zerah, Sammá és Mizzá.",
      "38": "<span>38</span> Széír fiai: Lótán és Sóbál, Cibón, Aná, Dísón, Écer és Dísán.",
      "39": "<span>39</span> Lótán fiai pedig: Hóri és Hómám. Lótán húga pedig Timná.",
      "40": "<span>40</span> Sóbál fiai: Alján és Mánahat, Ébál, Sefí és Ónám. Cibón fiai pedig: Ajjá és Aná.",
      "41": "<span>41</span> Aná fia Dísón, Dísón fiai pedig: Hamrán, Esbán, Jitrán és Kerán.",
      "42": "<span>42</span> Écer fiai: Bilhán, Zaaván és Jaakán. Dísón fia Úc és Arán is.",
      "43": "<span>43</span> Ezek azok a királyok, akik Edóm földjén uralkodtak, mielőtt Izráel fiainak királyuk lett volna: Bela, Beór fia, városát Dinhábának hívták.",
      "44": "<span>44</span> Bela meghalt, és Jóbáb, a bocrai Zerah fia lett a király.",
      "45": "<span>45</span> Amikor Jóbáb meghalt, a Témán földjéről való Húsám lett a király.",
      "46": "<span>46</span> Meghalt Húsám is, és Hadad, Bedad fia uralkodott utána, aki megverte a midjániakat Móáb mezején. Városának a neve Avít volt.",
      "47": "<span>47</span> Hadad is meghalt, és a maszrékai Szamlá uralkodott utána.",
      "48": "<span>48</span> Szamlá holta után a folyó melletti Rehóbótból való Saul lett a király.",
      "49": "<span>49</span> Saul is meghalt, és Baal-Hánán, Akbór fia uralkodott utána.",
      "50": "<span>50</span> Baal-Hánán halála után Hadad lett a király. Városának neve Páí, feleségének a neve pedig Mehétabél, aki Mézáháb leányának, Matrédnak volt leánya.",
      "51": "<span>51</span> Hadad halála után ezek voltak Edóm nemzetségfői: Timmá, Aljá, Jetét,",
      "52": "<span>52</span> Oholíbámá, Élá és Pínón,",
      "53": "<span>53</span> Kenaz nemzetségfő, Témán és Mibcár nemzetségfő,",
      "54": "<span>54</span> Magdíél nemzetségfő és Írám nemzetségfő. Ezek voltak Edóm nemzetségfői."
    },
    "2": {
      "1": "<span>1</span> Ezek Izráel fiai: Rúben, Simeon, Lévi, Júda, Issakár és Zebulon,",
      "2": "<span>2</span> Dán, József, Benjámin, Naftáli, Gád és Ásér.",
      "3": "<span>3</span> Júda fiai: Ér, Ónán és Sélá. Ez a három született neki a kánaáni Súah leányától. De Ér, Júda elsőszülött fia gonosz volt az ÚR szemében, és megölte őt az ÚR.",
      "4": "<span>4</span> A menye, Támár szülte neki Pérecet és Zerahot. Júda fiai összesen öten voltak.",
      "5": "<span>5</span> Pérec fiai Hecrón és Hámul.",
      "6": "<span>6</span> Zerah fiai pedig: Zimri és Étán, Hémán, Kalkól és Dára, összesen öten.",
      "7": "<span>7</span> Karmi fia Ákán, Izráel megrontója, mivel lopott a kiirtandó dolgokból.",
      "8": "<span>8</span> Étán fia Azarjá.",
      "9": "<span>9</span> Hecrónnak ezek a fiai, akik neki születtek: Jerahmeél, Rám és Kelúbaj.",
      "10": "<span>10</span> Rám Ammínádábot nemzette, Ammínádáb pedig Nahsónt, Júda fiainak fejedelmét.",
      "11": "<span>11</span> Nahsón nemzette Szalmát, Szalmá pedig nemzette Boázt.",
      "12": "<span>12</span> Boáz nemzette Óbédot, Óbéd nemzette Isait.",
      "13": "<span>13</span> Isai pedig nemzette Eliábot, az ő elsőszülöttjét, és másodikként Abinádábot, harmadikként pedig Simát.",
      "14": "<span>14</span> Negyedikként Netanélt, ötödikként Raddajt.",
      "15": "<span>15</span> Hatodikként Ócemet, hetedik fiaként pedig Dávidot.",
      "16": "<span>16</span> És nővéreiket, Cerúját és Abigailt. Cerújának pedig ez a három fia volt: Absaj, Jóáb és Aszáél.",
      "17": "<span>17</span> Abigail szülte Amászát, Amászá apja pedig az Izmael nemzetségéből való Jeter volt.",
      "18": "<span>18</span> Kálébnak pedig, Hecrón fiának Azúbá nevű feleségétől és Jerióttól ezek a fiai születtek: Jéser, Sóbáb és Ardón.",
      "19": "<span>19</span> Azúbá meghalt, és Káléb feleségül vette Efrátot, és ő szülte neki Húrt.",
      "20": "<span>20</span> Húr nemzette Úrit, Úri nemzette Becalélt.",
      "21": "<span>21</span> Azután bement Hecrón Mákírnak, Gileád apjának leányához. Hatvanesztendős korában vette el, és ő szülte neki Szegúbot.",
      "22": "<span>22</span> Szegúb nemzette Jáírt, akinek huszonhárom városa volt Gileád földjén.",
      "23": "<span>23</span> De Gesúr és Arám elvette tőlük Jáír falvait, Kenátot és mezővárosait, hatvan várost. Ezek mind Mákírnak, Gileád apjának a fiaié voltak.",
      "24": "<span>24</span> Hecrón halála után Káléb Efratába ment, akkor szülte neki Abijjá, Hecrón felesége Ashúrt, Tekóa atyját.",
      "25": "<span>25</span> Jerahmeélnek, Hecrón elsőszülöttjének fiai voltak: Rám, az elsőszülött, Búná, Óren, Ócem és Ahijjá.",
      "26": "<span>26</span> Egy másik felesége is volt Jerahmeélnek, név szerint Atárá. Ő volt Ónám anyja.",
      "27": "<span>27</span> Rámnak, Jerahmeél elsőszülöttjének fiai voltak: Maac, Jámín és Éker.",
      "28": "<span>28</span> Ónám fia volt Sammaj és Jádá. Sammaj fiai: Nádáb és Abisúr.",
      "29": "<span>29</span> Abisúr feleségének a neve Abihail volt, ő szülte neki Ahbánt és Mólídot.",
      "30": "<span>30</span> Nádáb fiai voltak: Szeled és Appaim. Szeled utód nélkül halt meg.",
      "31": "<span>31</span> Appaim fia Jisi, Jisi fia Sésán, Sésán fia Ahlaj.",
      "32": "<span>32</span> Jádának, Sammaj testvérének fiai: Jeter és Jónátán. Jeter utód nélkül halt meg.",
      "33": "<span>33</span> Jónátán fiai: Pelet és Zázá. Ezek Jerahmeél fiai voltak.",
      "34": "<span>34</span> Sésánnak nem voltak fiai, csak leányai. De volt Sésánnak egy Jarhá nevű egyiptomi szolgája.",
      "35": "<span>35</span> Sésán e Jarhá nevű szolgájának feleségül adta leányát, aki Attajt szülte neki.",
      "36": "<span>36</span> Attaj nemzette Nátánt, Nátán nemzette Zábádot.",
      "37": "<span>37</span> Zábád nemzette Eflált, Eflál nemzette Óbédot.",
      "38": "<span>38</span> Óbéd nemzette Jéhút, Jéhú nemzette Azarját.",
      "39": "<span>39</span> Azarjá nemzette Helecet, Helec nemzette Elászát.",
      "40": "<span>40</span> Elászá nemzette Sziszmajt, Sziszmaj nemzette Sallumot.",
      "41": "<span>41</span> Sallum nemzette Jekamját, Jekamjá nemzette Elisámát.",
      "42": "<span>42</span> Kálébnak, Jerahmeél testvérének fiai voltak: Mésá, az elsőszülöttje. Ő volt Zíf atyja; és az ő fia volt Marésá, Hebrón atyja.",
      "43": "<span>43</span> Hebrón fiai voltak: Kórah, Tappúah, Rekem és Sema.",
      "44": "<span>44</span> Sema nemzette Rahamot, Jorkoám apját. Rekem nemzette Sammajt.",
      "45": "<span>45</span> Sammaj fia pedig Máón volt. Ez a Máón volt Bétcúr atyja.",
      "46": "<span>46</span> Éfá pedig, Káléb másodfelesége szülte Háránt, Mócát és Gázézt. Hárán nemzette Gázézt.",
      "47": "<span>47</span> Johdaj fiai voltak: Regem, Jótám, Gésán, Pelet, Éfá és Sáaf.",
      "48": "<span>48</span> Káléb másodfelesége, Maaká szülte Sebert és Tirhanát.",
      "49": "<span>49</span> Ő szülte Sáafot, Madmanná apját, Sevát, Makbéna apját és Gibeá apját. Káléb leánya Akszá volt.",
      "50": "<span>50</span> Ezek voltak Káléb fiai. Ben-Húr, Efrátá elsőszülöttje: Sóbál, Kirjat-Jeárim atyja,",
      "51": "<span>51</span> Szalmá, Betlehem atyja, Háréf, Bét-Gádér atyja.",
      "52": "<span>52</span> Sóbálnak, Kirjat-Jeárim atyjának is voltak fiai: Háróe, a fél Menuhót ura,",
      "53": "<span>53</span> továbbá Kirjat-Jeárim nemzetségei: a jítriek, pútiak, sumátiak, misráiak. Ezektől származtak a coráiak és az estáuliak.",
      "54": "<span>54</span> Szalmá fiai voltak: Betlehem és a netófáiak, Atrót, Bét-Jóáb és a mánahtiak fele, a córiak.",
      "55": "<span>55</span> A Jabécban lakó írástudók nemzetségei voltak: tirátiak, simátiak, szúkátiak. Ezek voltak a kéniek, akik Hammattól, Bét-Rékáb házának atyjától származtak."
    },
    "3": {
      "1": "<span>1</span> Ezek voltak Dávid fiai, akik Hebrónban születtek: Amnón volt az elsőszülöttje, a jezréeli Ahinóamtól; a második Dániel volt, a karmeli Abigailtól;",
      "2": "<span>2</span> a harmadik Absolon, aki Maakának, a gesúri Talmaj király leányának volt fia; a negyedik Adónijjá, Haggít fia;",
      "3": "<span>3</span> az ötödik Sefatjá, Abitáltól. A hatodik Jitreám, Eglá nevű feleségétől.",
      "4": "<span>4</span> E hat fia született Hebrónban, ahol hét esztendeig és hat hónapig uralkodott. Jeruzsálemben pedig harminchárom esztendeig uralkodott.",
      "5": "<span>5</span> Jeruzsálemben ez a négy született neki Batsúától, Ammiél leányától: Simá, Sóbáb, Nátán és Salamon.",
      "6": "<span>6</span> És Jibhár, Elisámá és Elifelet,",
      "7": "<span>7</span> Nógah, Nefeg és Jáfía",
      "8": "<span>8</span> és Elisámá, Eljádá és Elifelet: ők kilencen.",
      "9": "<span>9</span> Ezek mind Dávid fiai másodfeleségeinek fiain és a húgukon, Támáron kívül.",
      "10": "<span>10</span> Salamonnak pedig Roboám volt a fia. Az ő fia Abijjá, annak a fia Ászá, az ő fia pedig Jósáfát.",
      "11": "<span>11</span> Az ő fia Jórám, annak a fia Ahazjá, az ő fia pedig Jóás.",
      "12": "<span>12</span> Az ő fia Amacjá, annak a fia Azarjá, az ő fia pedig Jótám.",
      "13": "<span>13</span> Az ő fia Áház, annak a fia Ezékiás, az ő fia pedig Manassé.",
      "14": "<span>14</span> Ennek fia Ámón, ennek fia Jósiás.",
      "15": "<span>15</span> Jósiás fiai: elsőszülöttje Jóhanán, a második Jójákim, a harmadik Cidkijjá, a negyedik Sallum.",
      "16": "<span>16</span> Jójákim fiai: az ő fia, Jekonjá és ennek fia, Cidkijjá.",
      "17": "<span>17</span> Jekonjá fia volt Asszír, ennek a fia Sealtiél",
      "18": "<span>18</span> és Malkirám, Pedájá, Senaccar, Jekamjá, Hósámá és Nedabjá.",
      "19": "<span>19</span> Pedájá fia volt: Zerubbábel és Simei. Zerubbábel fiai voltak: Mesullám, Hananjá, és nővérük volt Selómit.",
      "20": "<span>20</span> Rajtuk kívül ezek öten: Hasubá, Óhel, Berekjá, Haszadjá és Júsab-Heszed.",
      "21": "<span>21</span> Hananjá fia volt: Pelatjá és Jesajá, az ő fia volt Refájá, Arnán, Óbadjá és Sekanjá.",
      "22": "<span>22</span> Sekanjá utódai hatan voltak: Semajá és Semajá fiai: Hattús, Jigál, Báríah, Nearjá és Sáfát.",
      "23": "<span>23</span> Nearjának három fia volt: Eljóénaj, Ezékiás és Azrikám.",
      "24": "<span>24</span> Eljóénajnak hét fia volt: Hódavjáhú, Eljásib, Pelájá, Akkúb, Jóhánán, Delájá és Anáni."
    },
    "4": {
      "1": "<span>1</span> Júda fiai voltak: Pérec, Hecrón, Karmi, Húr és Sóbál.",
      "2": "<span>2</span> Reájá, Sóbál fia nemzette Jahatot. Jahat nemzette Ahúmajt és Lahadot. Ezek a córiak nemzetségei.",
      "3": "<span>3</span> Ezeknek volt Étám az apjuk: Jizreel, Jismá, Jidbás, nővérük neve pedig Haclelpóni volt.",
      "4": "<span>4</span> Penuél Gedór apja, Ezer pedig Húsá apja volt. Ezek Húrnak, Efráta elsőszülöttjének fiai, aki Betlehem apja volt.",
      "5": "<span>5</span> Ashúrnak, Tekóa atyjának két felesége volt, Helá és Naará.",
      "6": "<span>6</span> Naará szülte neki Ahuzzámot, Héfert, Témenit és az ahastáriakat. Ezek voltak Naará fiai.",
      "7": "<span>7</span> Helá fiai voltak: Ceret, Jecóhar és Etnán.",
      "8": "<span>8</span> Kóc nemzette Ánúbot, Haccóbébát és Aharhélnak, Hárúm fiának nemzetségeit.",
      "9": "<span>9</span> Jabéc pedig tiszteletre méltóbb volt testvéreinél. Anyja nevezte el Jabécnek, mert ezt mondta: Bizony, fájdalommal szültem őt!",
      "10": "<span>10</span> Jabéc Izráel Istenét hívta segítségül, és ezt mondta: Bárcsak valóban megáldanál engem, és kiszélesítenéd határomat, és kezed velem lenne, és megoltalmaznál minden veszedelemtől, hogy ne érjen fájdalom! És megadta neki Isten, amit kért.",
      "11": "<span>11</span> Kelúb pedig, Súhá testvére nemzette Mehírt. Ő Estón apja.",
      "12": "<span>12</span> Estón nemzette Bét-Ráfát, Pászéahot és Tehinnát, Ír-Náhás apját. Ezek a Rékától való férfiak.",
      "13": "<span>13</span> Kenaz fiai voltak: Otniél és Szerájá. Otniél fia Hatat.",
      "14": "<span>14</span> Meónótaj nemzette Ofrát, Szerájá nemzette Jóábot, a géharásimbeliek apját, akik mesteremberek voltak.",
      "15": "<span>15</span> Kálébnak, Jefunne fiának fiai voltak: Írú, Élá és Naam. Élá fia volt Kenaz.",
      "16": "<span>16</span> Jehallelél fiai voltak: Zíf, Zífá, Tirjá és Aszarél.",
      "17": "<span>17</span> Ezrá fiai voltak: Jeter, Mered, Éfer és Jálón. Ezután a felesége terhes lett Mirjámmal, majd Sammajjal és Jisbáhhal, Estemóa apjával.",
      "18": "<span>18</span> Ennek felesége, Jehudijjá szülte Jeredet, Gedór apját és Hebert, Szókó apját és Jekútiélt, Zánóah apját. Ezek Bitjának, a fáraó leányának a fiai, akit Mered vett feleségül.",
      "19": "<span>19</span> Hódijjá feleségének, Naham nővérének a fiai voltak: a geremi Keílá apja és a maakái Estemóa.",
      "20": "<span>20</span> Simon fiai voltak: Amnón, Rinná, Benhánán és Tilón. Jisi fia volt Zóhét és Benzóhét.",
      "21": "<span>21</span> Júda fiának, Sélának fiai voltak: Ér, Léká apja, Laddá, Marésá apja és a takácsok házának nemzetségei BétAsbéában,",
      "22": "<span>22</span> azután Jókim és Kózébá lakosai és Jóás és Száráf, akik Móáb urai voltak, és visszatértek Lehembe. Régi dolgok ezek!",
      "23": "<span>23</span> Ezek voltak a fazekasok, akik Netáimban és Gedérában laktak. A király mellett laktak ott, az ő szolgálatában.",
      "24": "<span>24</span> Simeon fiai voltak: Nemuél, Jámín, Járib, Zerah, Saul.",
      "25": "<span>25</span> Ennek a fia Sallum, ennek a fia Mibszám, az ő fia Mismá volt.",
      "26": "<span>26</span> Mismá utódai: Hammuél, ennek a fia Zakkúr, ennek meg Simei.",
      "27": "<span>27</span> Simeinek tizenhat fia és hat leánya volt. De testvéreinek nem sok fia volt, s általában háznépük nem volt oly népes, mint Júda fiaié.",
      "28": "<span>28</span> Beérsebában, Móládában és Hacar-Súálban laktak,",
      "29": "<span>29</span> továbbá Bilhában, Ecemben és Tóládban,",
      "30": "<span>30</span> Betuélben, Hormában és Ciklagban,",
      "31": "<span>31</span> Bét-Markábótban, Hacar-Szúszimban, Bét-Biriben és Saaraimban. Ezek voltak a városaik mindaddig, amíg Dávid királlyá nem lett.",
      "32": "<span>32</span> Településeik pedig: Étám, Ain, Rimmón, Tóken és Ásán, ez az öt város",
      "33": "<span>33</span> és mindazok a falvak, amelyek e városok körül voltak Baalig. Ezek voltak a lakóhelyeik, és volt nekik nemzetségtáblázatuk is.",
      "34": "<span>34</span> Mesóbáb, Jamlék és Jósá, Amacjá fia.",
      "35": "<span>35</span> És Jóél, Jéhú, Jósibjá fia, aki meg Szerájá fia, s aki Aszíél fia volt.",
      "36": "<span>36</span> És Eljóénaj, Jaakóbá, Jesóhájá, Aszájá, Adíél, Jeszímiél és Benájá.",
      "37": "<span>37</span> Zízá, Sifi fia, aki Allón fia, aki Jedajá fia, aki Simrí fia, aki Semajá fia.",
      "38": "<span>38</span> Ezek a név szerint felsoroltak nemzetségfők voltak, akiknek családjai igen elszaporodtak atyjuk házában.",
      "39": "<span>39</span> Ezért elindultak Gedór felé a völgy keleti részéig, hogy ott keressenek jószágaiknak legelőt.",
      "40": "<span>40</span> Találtak is zsíros és jó legelőt. Az a föld pedig tágas, nyugalmas és békességes volt. Azelőtt Hámból valók laktak ott,",
      "41": "<span>41</span> de Ezékiásnak, Júda királyának idejében e név szerint megnevezettek elmentek, lerombolták azoknak sátrait, és kiirtották a meúniakat, akiket ott találtak. Így van ez mindmáig. A helyükre telepedtek, mert ott állataik számára legelőket találtak.",
      "42": "<span>42</span> És közülük, Simeon fiai közül néhányan, körülbelül ötszázan elmentek a Széír-hegységbe. Elöljáróik Jisi fiai, Pelatjá, Nearjá, Refájá és Uzzíél voltak.",
      "43": "<span>43</span> Mind levágták azokat, akik még Amálék nemzetségéből megmaradtak, és ott telepedtek le mindmáig."
    },
    "5": {
      "1": "<span>1</span> Rúbennek, Izráel elsőszülöttjének fiai. Ő volt az elsőszülött, de amikor beszennyezte apjának ágyát, elsőszülöttségi jogát József fiai kapták, aki Izráel fia volt. Ezért nincs besorolva származás szerint az elsőszülöttek közé.",
      "2": "<span>2</span> Habár Júda tekintélyesebb volt testvéreinél, és fejedelem is került ki belőle, de az elsőszülöttségi jog Józsefé lett.",
      "3": "<span>3</span> Ezek Rúbennek, Izráel elsőszülöttjének fiai: Hanók, Pallú, Hecrón és Karmi.",
      "4": "<span>4</span> Jóél fiai: Semajá, az ő fia Góg, az ő fia Simei.",
      "5": "<span>5</span> Az övé Míká, az ő fia Reájá, az övé meg Baal.",
      "6": "<span>6</span> Az ő fia Beérá, akit fogságba vitt TiglatPileszer asszír király. Ő volt a rúbeniek fejedelme.",
      "7": "<span>7</span> Testvérei pedig családjaik, nemzetségük megszámlálása szerint ezek voltak: az első Jeiél és Zekarjáhú,",
      "8": "<span>8</span> Bela, Ázáz fia, aki Sema fia, aki Jóél fia volt. Ő Aróérban lakott egészen a Nébóig és Baal-Meónig,",
      "9": "<span>9</span> napkeleti irányban pedig az Eufrátesz folyótól a puszta széléig terjeszkedett, mivel jószágai igen elszaporodtak Gileád földjén.",
      "10": "<span>10</span> Saul idejében pedig hadat indítottak a hagriak ellen, akik elhullottak kezük által, és azoknak sátraiban laktak Gileád egész keleti vidékén.",
      "11": "<span>11</span> Gád fiai pedig velük szemben, Básán földjén laktak Szalkáig.",
      "12": "<span>12</span> Jóél volt a nemzetségfő, Sáfám pedig a második őutána. Janaj és Sáfát is Básánban lakott.",
      "13": "<span>13</span> Testvéreik családjaik szerint ezek: Míkáél, Mesullám, Seba, Jóraj, Jakán, Zía és Héber, ők heten.",
      "14": "<span>14</span> Ezek Abihail fiai, aki Húri fia, aki Járóah fia, aki Gileád fia, aki Míkáél fia, aki Jesísa fia, aki Jahdó fia, aki Búz fia.",
      "15": "<span>15</span> És Ahi, Gúni fiának, Abdiélnek a fia volt a család feje.",
      "16": "<span>16</span> Ezek Gileádban, Básánban és az ezekhez tartozó mezővárosokban laktak, és Sárón minden legelőjén, egész a határukig.",
      "17": "<span>17</span> Mindnyájukat megszámlálták Jótámnak, Júda királyának idejében és Jeroboámnak, Izráel királyának idejében.",
      "18": "<span>18</span> Rúben fiai és a gádiak közül meg a Manassé törzsének egyik feléből való felvértezett fegyveres férfiak, íjászok, a hadakozásban jártas negyvennégyezer-hétszázhatvan fegyverforgató férfi",
      "19": "<span>19</span> harcra kelt a hagriak ellen, Jetúr, Náfis és Nódáb ellen.",
      "20": "<span>20</span> Legyőzték őket, és kezükbe kerültek a hagriak és mindazok, akik velük voltak, mert Istenhez kiáltottak harc közben, és ő meghallgatta őket, mert benne bíztak.",
      "21": "<span>21</span> És elvitték az állataikat, ötvenezer tevét, kétszázötvenezer juhot, kétezer szamarat, valamint százezer embert.",
      "22": "<span>22</span> Sebesülés miatt is sokan elestek, mert Istentől volt az a harc, és azok helyén laktak a fogságig.",
      "23": "<span>23</span> Manassé fél törzsének fiai pedig azon a földön laktak, amely Básántól BaalHermónig, Szenírig és a Hermón-hegyig terjedt, mert igen megsokasodtak.",
      "24": "<span>24</span> Családjaik nemzetségfői voltak: Éfer, Jisi, Eliél, Azriél, Jirmejá, Hódavjá és Jahdiél, igen erős férfiak, híres emberek, családjaik nemzetségfői.",
      "25": "<span>25</span> De vétkeztek atyáik Istene ellen, mert az ország népeinek bálványisteneivel paráználkodtak, amely népeket Isten kipusztított előlük.",
      "26": "<span>26</span> Ezért fölindította Izráel Istene Púl asszír királynak és Tiglat-Pileszer asszír királynak a lelkét, hogy fogságba vigye a rúbenieket, a gádiakat és Manassé törzsének a felét. Elvitte őket Halahba, Hábórba, Hárába és a Gózán folyója mellé. Így van ez mindmáig."
    },
    "6": {
      "1": "<span>1</span> Lévi fiai: Gérsón, Kehát és Merári.",
      "2": "<span>2</span> Kehát fiai pedig: Amrám, Jichár, Hebrón és Uzzíél.",
      "3": "<span>3</span> Amrám gyermekei: Áron, Mózes és Mirjám. Áron fiai pedig: Nádáb, Abihú, Eleázár és Ítámár.",
      "4": "<span>4</span> Merári fiai Mahli és Musi voltak. Ezek Lévi nemzetségei családjaik szerint: Eleázár nemzette Fineást, Fineás nemzette Abisúát.",
      "5": "<span>5</span> Abisúa nemzette Bukkit, Bukki nemzette Uzzit.",
      "6": "<span>6</span> Uzzi nemzette Zerahját, Zerahjá nemzette Merájótot.",
      "7": "<span>7</span> Merájót nemzette Amarját, Amarjá nemzette Ahitúbot.",
      "8": "<span>8</span> Ahitúb nemzette Cádókot, Cádók nemzette Ahimaacot.",
      "9": "<span>9</span> Ahimaac nemzette Azarját, Azarjá nemzette Jóhánánt.",
      "10": "<span>10</span> Jóhánán nemzette Azarját, ő volt a pap abban a házban, amelyet Salamon Jeruzsálemben épített.",
      "11": "<span>11</span> Azarjá nemzette Amarját, Amarjá nemzette Ahitúbot.",
      "12": "<span>12</span> Ahitúb nemzette Cádókot, Cádók nemzette Sallumot.",
      "13": "<span>13</span> Sallum nemzette Hilkijját. Hilkijjá nemzette Azarját.",
      "14": "<span>14</span> Azarjá nemzette Szeráját, Szerájá nemzette Jehócádákot.",
      "15": "<span>15</span> Jehócádák is fogságba ment, amikor az ÚR Júdát és Jeruzsálemet fogságba vitette Nebukadneccar által.",
      "16": "<span>16</span> Lévi fiai Gérsón, Kehát és Merári voltak.",
      "17": "<span>17</span> Név szerint ezek voltak Gérsón fiai: Libni és Simei.",
      "18": "<span>18</span> Kehát fiai voltak: Amrám, Jichár, Hebrón és Uzzíél.",
      "19": "<span>19</span> Merári fiai: Mahli és Musi. Ezek Lévi háznépei családjaik szerint.",
      "20": "<span>20</span> Gérsón fia Libni, az ő fia Jahat, az ő fia Zimmá;",
      "21": "<span>21</span> ennek a fia Jóáh, az ő fia Iddó, az ő fia Zerah, ennek a Jeotraj.",
      "22": "<span>22</span> Kehát fia Ammínádáb, ennek a fia Kórah, és az ő fia Asszír.",
      "23": "<span>23</span> Ennek a fia Elkáná, az ő fia Ebjászáf, ennek a fia pedig Asszír.",
      "24": "<span>24</span> Ennek a fia Tahat, az ő fia Úriél, ennek a fia Uzzijjá, és ennek a fia Saul.",
      "25": "<span>25</span> Elkáná fiai Amászaj és Ahimót voltak,",
      "26": "<span>26</span> ennek a fia Elkáná. Ennek a fia Cófaj, és az ő fia Nahat;",
      "27": "<span>27</span> ennek a fia Eliáb, az ő fia Jeróhám, ennek a fia Elkáná.",
      "28": "<span>28</span> Sámuel fiai: Jóél volt az elsőszülött, a második Abijjá.",
      "29": "<span>29</span> Merári utódai voltak: Mahli, ennek a fia Libni, az ő fia Simei, és ennek a fia Uzzá.",
      "30": "<span>30</span> Az ő fia Simá, ennek a fia Haggijjá, és ennek a fia Aszájá.",
      "31": "<span>31</span> Ők azok, akiket Dávid állított be az ÚR házába az éneklés vezetésére, mikor Isten ládáját odahelyezték,",
      "32": "<span>32</span> és amíg Salamon föl nem építette az ÚR házát Jeruzsálemben, hogy a gyülekezet sátra előtt szolgáljanak énekléssel, és szolgálatban álljanak rendtartásuk szerint.",
      "33": "<span>33</span> Ezek azok, akik fiaikkal együtt szolgáltak ott: a kehátiak fiai közül Hémán főénekes, Jóél fia, aki Sámuel fia,",
      "34": "<span>34</span> aki Elkáná fia, aki Jeróhám fia, aki Eliél fia, aki Tóah fia,",
      "35": "<span>35</span> aki Cúf fia, aki Elkáná fia, aki Mahat fia, aki Amászaj fia,",
      "36": "<span>36</span> aki Elkáná fia, aki Jóél fia, aki Azarjá fia, aki Cefanjá fia,",
      "37": "<span>37</span> aki Tahat fia, aki Asszír fia, aki Ebjászáf fia, aki Kórah fia,",
      "38": "<span>38</span> aki Jichár fia, aki Kehát fia, aki Lévi fia, aki Izráel fia.",
      "39": "<span>39</span> Testvére, Ászáf állt a jobb keze felől. Ászáf Berekjáhú fia, aki Simá fia volt,",
      "40": "<span>40</span> aki Míkáél fia, aki Baaszéjá fia, aki Malkijjá fia,",
      "41": "<span>41</span> aki Etni fia, aki Zerah fia, aki Adájá fia,",
      "42": "<span>42</span> aki Étán fia, aki Zimmá fia, aki Simei fia,",
      "43": "<span>43</span> aki Jahat fia, aki Gérsón fia, aki Lévi fia.",
      "44": "<span>44</span> Továbbá bal kéz felől álltak Merári fiai, akik testvéreik voltak. Étán, Kísi fia, aki Abdi fia, aki Mallúk fia,",
      "45": "<span>45</span> aki Hasabjá fia, aki Amacjá fia, aki Hilkijjá fia,",
      "46": "<span>46</span> aki Amci fia, aki Báni fia, aki Semer fia,",
      "47": "<span>47</span> aki Mahli fia, aki Músi fia, aki Merári fia, aki Lévi fia.",
      "48": "<span>48</span> Testvéreik, a léviták voltak az Isten háza hajlékának egyéb szolgálataira rendelve.",
      "49": "<span>49</span> Áron és fiai pedig az egészen elégő áldozat oltára mellé és a füstölőoltár mellé, a szentek szentjének szolgálatához és Izráel megszentelésére voltak rendelve, pontosan úgy, ahogy Mózes, Isten szolgája megparancsolta.",
      "50": "<span>50</span> Áron utódai pedig ezek voltak: Eleázár volt a fia, ennek a fia Fineás, az ő fia Abisúa.",
      "51": "<span>51</span> Az ő fia Bukki, ennek a fia Uzzi, az ő fia Zerahjá.",
      "52": "<span>52</span> Ennek a fia Merájót, ennek a fia Amarjá, az ő fia Ahitúb,",
      "53": "<span>53</span> ennek a fia Cádók, s ennek a fia Ahimaac volt.",
      "54": "<span>54</span> Ezek voltak a keháti nemzetségből Áron fiainak a lakóhelyei, területükön levő sátortáborai letelepedésük szerint az ő vidékükön, mert ez jutott nekik sorsolás által.",
      "55": "<span>55</span> Ők kapták a Júda földjén levő Hebrónt a körülötte levő legelőkkel.",
      "56": "<span>56</span> De a város földjeit és falvait Kálébnak, Jefunne fiának adták.",
      "57": "<span>57</span> Áron fiainak adták Júda városai közül a menedékvárosokat, Hebrónt, Libnát és legelőit, Jattírt és Estemóát és legelőiket,",
      "58": "<span>58</span> Hílézt és legelőit, Dobrat és legelőit,",
      "59": "<span>59</span> Ásánt és legelőit, Bétsemest és legelőit.",
      "60": "<span>60</span> Benjámin törzséből pedig: Gebát és legelőit, Álemetet és legelőit, Anátót várost és legelőit. Nemzetségeiknek szám szerint tizenhárom városuk volt.",
      "61": "<span>61</span> Kehát többi fiának pedig, akik a törzs nemzetségéből még hátravoltak, Manassé törzsének a feléből tíz várost adtak sorsolás által.",
      "62": "<span>62</span> Gérsón fiainak nemzetségeik szerint Issakár törzséből, Ásér törzséből, Naftáli törzséből és Manassé törzséből Básánban tizenhárom várost adtak.",
      "63": "<span>63</span> Mérári fiainak nemzetségeik szerint Rúben törzséből, Gád törzséből és Zebulon törzséből sorsolás által tizenkét város jutott.",
      "64": "<span>64</span> Így adtak Izráel fiai a lévitáknak városokat a legelőkkel együtt.",
      "65": "<span>65</span> Sorsolás által adták oda Júda fiainak törzséből, Simeon fiainak törzséből és Benjámin fiainak törzséből ezeket a név szerint megnevezett városokat.",
      "66": "<span>66</span> Akik Kehát fiainak nemzetségeiből valók, akiknek a határában levő városok Efraim törzséből valók voltak,",
      "67": "<span>67</span> azoknak adták át a menedékvárosokat, Sikemet és legelőit az Efraim hegyvidékén, Gézert és legelőit,",
      "68": "<span>68</span> Jokmeámot és legelőit, Béthórónt és legelőit,",
      "69": "<span>69</span> Ajjálónt és legelőit, GatRimmónt és legelőit.",
      "70": "<span>70</span> Manassé törzsének a feléből Ánért és legelőit, Bileámot és legelőit. Ezeket adták Kehát többi fia nemzetségének.",
      "71": "<span>71</span> Gérsón fiainak adták Manassé fél törzsének a nemzetségeiből Básánban Gólánt és legelőit, Astárótot és legelőit.",
      "72": "<span>72</span> Issakár törzséből Kedest és legelőit, Dobratot és legelőit,",
      "73": "<span>73</span> Rámótot és legelőit, Ánémot és legelőit.",
      "74": "<span>74</span> Ásér törzséből Másált és legelőit, Abdónt és legelőit,",
      "75": "<span>75</span> Húkókot és legelőit, Rehóbot és legelőit.",
      "76": "<span>76</span> Naftáli nemzetségéből Galileában Kedest és legelőit, Hammónt és legelőit, Kirjátaimot és legelőit.",
      "77": "<span>77</span> Merári többi fiának Zebulon törzséből Rimmónót és legelőit, Tábórt és legelőit.",
      "78": "<span>78</span> A Jordánon túl, Jerikóval szemben, a Jordántól keletre eső részen a Rúben törzséből a pusztában Becert és legelőit, Jahcát és legelőit,",
      "79": "<span>79</span> Kedémótot és legelőit, Méfaatot és legelőit.",
      "80": "<span>80</span> Gád törzséből Gileádban Rámótot és legelőit, Mahanaimot és legelőit,",
      "81": "<span>81</span> Hesbónt és legelőit, Jazért és legelőit."
    },
    "7": {
      "1": "<span>1</span> Issakárnak négy fia volt: Tólá, Púá, Jásúb és Simrón.",
      "2": "<span>2</span> Tólá fiai voltak: Uzzi, Refájá, Jeriél, Jahmaj, Jibszám és Sámuel, akik családfők voltak atyáik családjaiban. Tólá fiai erős harcosok voltak nemzetségükben, számuk Dávid király idejében huszonkétezerhatszáz volt.",
      "3": "<span>3</span> Uzzi fia volt Jizrahjá. Jizrahjá fiai: Míkáél, Óbadjá, Jóél, Jissijjá; mind az öten főemberek voltak.",
      "4": "<span>4</span> Közéjük tartozott nemzetségeik és családjaik szerint harminchatezer hadi szolgálatra rendelt férfi, minthogy sok feleségük és sok fiuk is volt.",
      "5": "<span>5</span> Testvéreik is, Issakár egész nemzetsége, vitéz harcosok voltak, szám szerint összesen nyolcvanhétezren.",
      "6": "<span>6</span> Benjámin fiai hárman voltak: Bela, Beker és Jedíaél.",
      "7": "<span>7</span> Bela fiai: Ecbón, Uzzi, Uzzíél, Jerimót és Íri, ez az öt családfő, vitéz harcosok, akik szám szerint huszonkétezerharmincnégyen voltak.",
      "8": "<span>8</span> Beker fiai voltak: Zemírá, Jóás, Eliezer, Eljóénaj, Omri, Jerémót, Abijjá, Anátót és Álemet. Ezek mind Bekertől lettek.",
      "9": "<span>9</span> Nemzetségeikből szám szerint a családfők és a hős harcos férfiak húszezerkétszázan voltak hadra foghatók.",
      "10": "<span>10</span> Jedíaél fia Bilhán volt. Bilhán fiai: Jeús, Benjámin, Éhúd, Kenaaná, Zétán, Tarsis és Ahisahar.",
      "11": "<span>11</span> Mindezek Jedíaél fiai voltak, családfők és hős harcosok, akik tizenhétezer-kétszázan voltak fegyverforgatók.",
      "12": "<span>12</span> Ír fiai voltak: Suppim és Huppím. Husim Ahér fia.",
      "13": "<span>13</span> Naftáli fiai voltak: Jahaciél, Gúni, Jécer és Sallum, Bilhá unokái.",
      "14": "<span>14</span> Manassé fia volt Aszriél, akit másodfelesége szült. Ő szülte Mákírt, Gileád atyját is.",
      "15": "<span>15</span> Mákír pedig feleségül vette Huppím és Suppim Maaká nevű húgát. Egy másik fiának neve Celofhád. Celofhádnak csak leányai lettek.",
      "16": "<span>16</span> Maaká, Mákír felesége fiút szült, akit Peresnek nevezett el, öccsét pedig Seresnek. Ennek a fiai Úlám és Rekem voltak.",
      "17": "<span>17</span> Úlám fia Bedán volt. Ezek Gileádnak, Mákír fiának, Manassé unokájának a fiai.",
      "18": "<span>18</span> Nővére, Móleket szülte Ishódot, Abiezert és Mahlát.",
      "19": "<span>19</span> Semídá fia Ahján, Sekem, Likhi és Aniám voltak.",
      "20": "<span>20</span> Efraim fia pedig Sútelah volt. Ennek a fia Bered, az ő fia Tahat, ennek a fia Eládá, az ő fia Tahat.",
      "21": "<span>21</span> Ennek a fia Zábád, az ő fiai Sútelah, Ézer és Elád. Ezeket megölték annak a földnek a lakosai, a gátiak, mert lementek, hogy elhajtsák a jószágaikat.",
      "22": "<span>22</span> Apjuk, Efraim sokáig siratta őket, ezért elmentek hozzá a testvérei, hogy vigasztalják.",
      "23": "<span>23</span> Azután bement a feleségéhez, aki fogant méhében, és fiút szült, és Beríának nevezte, mivel szerencsétlenség érte a házat.",
      "24": "<span>24</span> Leánya pedig Seerá volt, aki Alsó- és Felső-Béthórónt és UzénSeerát építette.",
      "25": "<span>25</span> Refah is az ő fia volt, ennek a fia Resef. Az ő fia Telah, ennek a fia Tahan.",
      "26": "<span>26</span> Az ő fia Ladán, ennek a fia Ammihúd, az ő fia Elisámá,",
      "27": "<span>27</span> ennek a fia Nún, az ő fia pedig Józsué.",
      "28": "<span>28</span> Az ő birtokuk és lakóhelyük voltak: Bétel és falvai, keletre Naarán, nyugatra Gézer és falvai, Sikem és falvai, egészen Ajjáig és falvaiig.",
      "29": "<span>29</span> És Manassé fiainak kezében voltak: Bétseán és falvai, Taanak és falvai, Megiddó és falvai, Dór és falvai. Ezekben laktak Izráel fiának, Józsefnek az utódai.",
      "30": "<span>30</span> Ásér fiai voltak: Jimná, Jisvá, Jisvi, Beríá, húguk pedig Szerah volt.",
      "31": "<span>31</span> Beríá fiai voltak: Heber és Malkiél, aki Birzávit apja volt.",
      "32": "<span>32</span> Heber nemzette Jaflétot, Sómért, Hótámot és húgukat, Súát.",
      "33": "<span>33</span> Jaflét fiai voltak: Pászak, Bimhál és Asvát. Ezek Jaflét fiai.",
      "34": "<span>34</span> Sómér fiai voltak: Ahi, Rohgá, Jehubbá és Arám.",
      "35": "<span>35</span> Testvérének, Hélemnek fia volt Cófah, Jimná, Séles és Ámal.",
      "36": "<span>36</span> Cófah fiai voltak: Szúah, Harnefer, Súál, Béri és Jimrá,",
      "37": "<span>37</span> Becer, Hód, Sammá, Silsá, Jitrán és Beérá.",
      "38": "<span>38</span> Jeter fiai voltak: Jefunne, Piszpá és Ará.",
      "39": "<span>39</span> Ullá fiai voltak: Árah, Hanniél és Ricjá.",
      "40": "<span>40</span> Mindezek Ásér utódai, családfők, válogatott harcosok, a főemberek legjobbjai. Amikor megszámlálták őket nemzetségük rendje szerint, huszonhatezren voltak a hadra fogható férfiak."
    },
    "8": {
      "1": "<span>1</span> Benjámin nemzette elsőszülöttjeként Belát, másodikként Asbélt, harmadiknak Ahrahot,",
      "2": "<span>2</span> negyediknek Nóhát és ötödikként Ráfát.",
      "3": "<span>3</span> Bela fiai voltak: Addár, Gérá és Abihúd,",
      "4": "<span>4</span> Abisúa, Naamán, Ahóah,",
      "5": "<span>5</span> Gérá, Sefúfán és Húrám.",
      "6": "<span>6</span> Ezek voltak Éhúd fiai, Geba lakóinak családfői, de száműzték őket Mánáhatba:",
      "7": "<span>7</span> nevezetesen Naamán, Ahijjá és Gérá; ő vitte fogságba őket, és ő nemzette Uzzát és Ahihúdot.",
      "8": "<span>8</span> Saharaim pedig Móáb mezején nemzette Húsimot és Baarát, miután elbocsátotta feleségeit.",
      "9": "<span>9</span> Hódes nevű feleségétől nemzette Jóbábot, Cibját, Mésát és Malkámot,",
      "10": "<span>10</span> Jeúcot, Szokját és Mirmát. Ezek a fiai családfők voltak.",
      "11": "<span>11</span> Húsimtól nemzette Abitúbot és Elpaalt.",
      "12": "<span>12</span> Elpaal fiai voltak: Éber, Misám és Semed. Ő építette Ónót, Lódot és falvait.",
      "13": "<span>13</span> Beríá és Sema, Ajjálón lakóinak családfői űzték el Gát lakóit.",
      "14": "<span>14</span> Ahjó, Sásák, Jerémót,",
      "15": "<span>15</span> Zebadjá, Arád, Eder,",
      "16": "<span>16</span> Míkáél, Jispá és Jóhá voltak Beríá fiai.",
      "17": "<span>17</span> Zebadjá, Mesullám, Hizki, Heber,",
      "18": "<span>18</span> Jismeraj, Jizlíá és Jóbáb voltak Elpaal fiai.",
      "19": "<span>19</span> Jákim, Zikri, Zabdi,",
      "20": "<span>20</span> Eliénaj, Cilletaj, Eliél,",
      "21": "<span>21</span> Adájá, Berájá és Simrát voltak Simei fiai.",
      "22": "<span>22</span> Jispán, Éber, Eliél,",
      "23": "<span>23</span> Abdón, Zikri, Hánán,",
      "24": "<span>24</span> Hananjá, Élám, Antótijjá,",
      "25": "<span>25</span> Jifdejá, Penúél voltak Sásák fiai.",
      "26": "<span>26</span> Samseraj, Seharjá, Ataljá,",
      "27": "<span>27</span> Jaaresjá, Élijjá és Zikri voltak Jeróhám fiai.",
      "28": "<span>28</span> Ezek a családfők, akik származásuk szerint főemberek voltak, Jeruzsálemben laktak.",
      "29": "<span>29</span> Gibeónban lakott Jeiél, Gibeón atyja. Feleségének Maaká volt a neve.",
      "30": "<span>30</span> Elsőszülöttje Abdón volt, azután Cúr, Kís, Baal, Nádáb,",
      "31": "<span>31</span> Gedór, Ahjó és Zeker következett.",
      "32": "<span>32</span> Majd Miklót, aki Simát nemzette. Ezek is a testvéreik mellett, Jeruzsálemben laktak más testvéreikkel együtt.",
      "33": "<span>33</span> Nér nemzette Kíst, Kís nemzette Sault, Saul nemzette Jónátánt, Malkisúát, Abinádábot és Esbaalt.",
      "34": "<span>34</span> Jónátán fia Meribbaal volt. Meribbaal nemzette Míkát.",
      "35": "<span>35</span> Míká fiai Pítón, Melek, Taréa és Áház voltak.",
      "36": "<span>36</span> Áház nemzette Jehóaddát, Jehóaddá pedig Álemetet, Azmávetet és Zimrit, Zimri nemzette Mócát,",
      "37": "<span>37</span> Mócá pedig Binát. Az ő fia volt Refájá, ennek a fia Elászá, az ő fia Ácél.",
      "38": "<span>38</span> Továbbá Ácélnak hat fia volt, név szerint ezek: Azrikám, Bókerú, Jismáél, Searjá, Óbadjá és Hánán. Mindezek Ácél fiai voltak.",
      "39": "<span>39</span> Testvérének, Éseknek ezek a fiai: Úlám az elsőszülöttje, Jeús a második és Elifelet a harmadik.",
      "40": "<span>40</span> Úlám fiai erős harcosok, íjászok voltak, s gyermekeik és unokáik százötvenre szaporodtak. Mindezek Benjámin fiai voltak."
    },
    "9": {
      "1": "<span>1</span> Egész Izráelt megszámlálták nemzetségeik szerint, és beírták őket Izráel királyainak könyvébe. A júdaiakat hűtlenségük miatt Babilóniába vitték.",
      "2": "<span>2</span> Az első lakosok, akik visszakerültek birtokaikra és városaikba, izráeliek voltak: papok, léviták és templomszolgák.",
      "3": "<span>3</span> Jeruzsálemben Júda és Benjámin fiai közül, valamint Efraim és Manassé fiai közül valók laktak:",
      "4": "<span>4</span> Útaj, Ammihúd fia, aki Omri fia, aki Imri fia, aki Báni fia, Perecnek, Júda fiának az utódai közül.",
      "5": "<span>5</span> A silóiak közül való volt Aszájá, az elsőszülött és fiai.",
      "6": "<span>6</span> Zerah fiai közül Jeúél és rokonai, hatszázkilencvenen.",
      "7": "<span>7</span> Benjámin fiai közül: Szallú, Mesullám fia, aki Hódavjá fia, aki Hasszenúá fia volt.",
      "8": "<span>8</span> Jibnejá, Jeróhám fia és Élá, Uzzi fia, aki Mikri fia és Mesullám, Sefatjá fia, aki Reuél fia, aki Jibnijjá fia volt.",
      "9": "<span>9</span> Testvéreik származásuk szerint kilencszázötvenhatan voltak. Ezek a férfiak családfők voltak, ki-ki a maga atyjának háza népe szerint.",
      "10": "<span>10</span> A papok közé tartozott: Jedajá, Jehójárib és Jákín,",
      "11": "<span>11</span> Azarjá, Hilkijjá fia, aki Mesullám fia, aki Cádók fia, aki Merájót fia, aki Ahitúbnak, Isten háza felügyelőjének a fia volt.",
      "12": "<span>12</span> Továbbá Adájá, Jeróhám fia, aki Pashúr fia, aki Malkijjá fia és Maszaj, Adíél fia, aki Jahzérá fia, aki Mesullám fia, aki Mésillémit fia, aki Immér fia volt,",
      "13": "<span>13</span> valamint testvéreik. Családfőik ezerhétszázhatvanan voltak, buzgók Isten háza dolgának szolgálatában.",
      "14": "<span>14</span> A léviták közé tartozott Semajá, Hassúb fia, aki Azrikám fia, aki Hasabjá fia volt, Merári fiai közül.",
      "15": "<span>15</span> Bakbakar, Heres és Gálál és Mattanjá, Míká fia, aki Zikri fia, aki Ászáf fia volt.",
      "16": "<span>16</span> Azután Óbadjá, Semajá fia, aki Gálál fia, aki Jedútún fia volt, és Berekjá, Ászá fia, aki Elkáná fia volt, aki a netófáiak falvaiban lakott.",
      "17": "<span>17</span> Kapuőrök voltak: Sallum, Akkúb, Talmón és Ahíman. Testvérük, Sallum volt a fejük.",
      "18": "<span>18</span> Helyük mindmáig ott van napkelet felől a királyi kapunál. Ők voltak a kapuőrök Lévi fiainak a táborában.",
      "19": "<span>19</span> Sallum pedig, Kóré fia, aki Ebjászáf fia, aki Kórah fia volt, testvéreivel együtt, akik a kórahiak családjából származtak, a szolgálat munkájában a hajlék küszöbének őrizői voltak úgy, mint már atyáik is, akik az ÚR táborának bejáratát őrizték,",
      "20": "<span>20</span> amikor Fineás, Eleázár fia volt az elöljárójuk, akivel az ÚR volt.",
      "21": "<span>21</span> Zekarjá, Meselemjá fia a gyülekezet sátra bejáratának kapuőre volt.",
      "22": "<span>22</span> Ezek kétszáztizenketten mind választott kapuőrök voltak. Őket falvaikban, nemzetségük szerint megszámlálták; már Dávid és Sámuel, a látó rendelte őket erre a tisztségre.",
      "23": "<span>23</span> Ők és fiaik őrizték az ÚR házának, a sátornak a kapuit.",
      "24": "<span>24</span> A négy égtáj irányába voltak őrök: keletre, nyugatra, északra és délre.",
      "25": "<span>25</span> Testvéreiknek a falvakból időről időre hét napra fel kellett menniük hozzájuk.",
      "26": "<span>26</span> Mert a kapuőrök négy főemberének állandó megbízatása volt. Ezek a léviták őrizték a kamrákat és Isten házának kincseit.",
      "27": "<span>27</span> Az ÚR háza körül éjszakáztak, mivel az ő tisztük volt az őrködés, és minden reggel ők nyitották ki az ajtókat.",
      "28": "<span>28</span> Közülük néhányan a szolgálati edényekre viseltek gondot, mert mind a kivitelnél, mind a visszavitelnél számba vették azokat.",
      "29": "<span>29</span> Néhányat közülük kiválasztottak az egyéb edényekről, a lisztről, a borról, az olajról, a tömjénről és a fűszerekről való gondoskodásra.",
      "30": "<span>30</span> A papok fiai közül valók készítették fűszerekből a drága kenetet is.",
      "31": "<span>31</span> Továbbá a léviták közül való Mattitjá, a kórahi Sallum elsőszülöttje a serpenyőkre viselt gondot.",
      "32": "<span>32</span> A kehátiak fiait és testvéreiket rendelték a szent kenyér szolgálatára, hogy minden szombaton elkészítsék.",
      "33": "<span>33</span> Az énekesek és a kamrákban tartózkodó léviták közül a családfők mentesek voltak más feladatok alól, mert éjjel és nappal szolgálattal tartoztak.",
      "34": "<span>34</span> A léviták családfői voltak ezek, származásuk szerint főemberek, és ők Jeruzsálemben laktak.",
      "35": "<span>35</span> Gibeónban lakott Jeiél, Gibeón atyja, és feleségének Maaká volt a neve.",
      "36": "<span>36</span> Elsőszülött fia Abdón volt, azután Cúr, Kís, Baal, Nér és Nádáb következett, majd",
      "37": "<span>37</span> Gedór, Ahjó, Zekarjá és Miklót.",
      "38": "<span>38</span> Miklót nemzette Simámot. Ezek is Jeruzsálemben laktak rokonaikkal együtt.",
      "39": "<span>39</span> Nér nemzette Kíst, Kís nemzette Sault, Saul pedig Jónátánt, Malkisúát, Abinádábot és Esbaalt.",
      "40": "<span>40</span> Jónátán fia Meribbaal volt. Meribbaal nemzette Míkát.",
      "41": "<span>41</span> Míká fiai Pítón, Melek, Taréa és Áház voltak.",
      "42": "<span>42</span> Áház nemzette Jarát, Jará nemzette Álemetet, Azmávetet és Zimrit, Zimri pedig nemzette Mócát.",
      "43": "<span>43</span> Mócá nemzette Binát, ennek a fia Refájá, az ő fia Elászá, az ő fia Ácél volt.",
      "44": "<span>44</span> Ácélnak hat fia volt, név szerint ezek: Azrikám, Bókerú, Jismáél, Searjá, Óbadjá és Hánán. Ezek Ácél fiai."
    },
    "10": {
      "1": "<span>1</span> A filiszteusok háborút viseltek Izráel ellen, és megfutamodott Izráel népe a filiszteusok elől, és sokan a sebeik miatt el is hulltak Gilbóa hegyén.",
      "2": "<span>2</span> A filiszteusok pedig Saulnak és fiainak a nyomába eredtek, és megölték a filiszteusok Jónátánt, Abinádábot és Malkisúát, Saul fiait.",
      "3": "<span>3</span> A viadal igen heves volt Saul körül, és amikor rátaláltak az íjászok, a nyilakkal megsebesítették.",
      "4": "<span>4</span> Erre Saul így szólt a fegyverhordozójához: Vond ki fegyveredet, és döfj át vele, hogy ha ideérnek e körülmetéletlenek, ne gyalázzanak meg engem! De fegyverhordozója vonakodott, mert igen félt. Erre Saul megragadta a kardját, és beledőlt.",
      "5": "<span>5</span> Amikor a fegyverhordozója látta, hogy Saul immár meghalt, ő is kardjába dőlt, és meghalt.",
      "6": "<span>6</span> Így halt meg Saul, három fia és vele együtt egész háznépe.",
      "7": "<span>7</span> Mikor pedig mindazok az izráeliek, akik a völgyben voltak, látták, hogy ők megfutamodtak, és hogy Saul és fiai meghaltak, pusztán hagyták városaikat, és elmenekültek. Akkor eljöttek a filiszteusok, és betelepedtek azokba.",
      "8": "<span>8</span> Másnap történt, hogy a filiszteusok eljöttek, hogy kifosszák az elesetteket. Megtalálták Sault és fiait, amint holtan feküdtek a Gilbóa-hegyen.",
      "9": "<span>9</span> Kifosztották őt, fejét vették, elvették fegyvereit, és elküldték ezeket a filiszteusok minden tartományába körös-körül, hogy hírül adják bálványaiknak és a népnek.",
      "10": "<span>10</span> Fegyvereit isteneik házában helyezték el, fejét pedig Dágón templomában akasztották föl.",
      "11": "<span>11</span> Mikor azonban meghallották a jábés-gileádiak, hogy mit cselekedtek a filiszteusok Saullal,",
      "12": "<span>12</span> fölkelt az összes erős férfi, és elvitték Saulnak és fiainak a testét. Jábésba vitték és eltemették a csontjaikat a tölgyfa alatt Jábésban, és hét napig böjtöltek.",
      "13": "<span>13</span> Így halt meg Saul gonoszsága miatt, mivel vétkezett az ÚR ellen. Az ÚR szavát nem tartotta meg, sőt a halottidézőt is megkereste, hogy megkérdezze,",
      "14": "<span>14</span> és nem az URat kérdezte meg. Ezért elveszítette őt az ÚR, és országát Dávidnak, Isai fiának adta."
    },
    "11": {
      "1": "<span>1</span> Ezután az izráeliek mindnyájan Dávidhoz gyűltek Hebrónba, és azt mondták: Íme, mi a te csontod és a te tested vagyunk.",
      "2": "<span>2</span> Ezelőtt is, még mikor Saul volt a király, te voltál az, aki harcba vitted és visszahoztad Izráelt, és ezt mondta neked a te URad, Istened: Te legelteted népemet, Izráelt, és te leszel népemnek, Izráelnek a fejedelme.",
      "3": "<span>3</span> Elment tehát Izráel minden véne a királyhoz Hebrónba, és Dávid szövetséget kötött velük Hebrónban az ÚR előtt. És Izráel királyává kenték Dávidot, ahogyan az ÚR kijelentette Sámuel által.",
      "4": "<span>4</span> Akkor Dávid és egész Izráel elment Jeruzsálem alá. Ez Jebúsz, ahol a jebúsziak laktak.",
      "5": "<span>5</span> Jebúsz lakói ezt mondták Dávidnak: Ide be nem jössz! Dávid azonban bevette Sion sziklavárát, és ez lett Dávid városa.",
      "6": "<span>6</span> Mert azt mondta Dávid: Aki elsőnek vág le egy jebúszit, az elöljáró és vezér lesz! Mivel legelsőnek Jóáb, Cerújá fia jutott föl, ő lett elöljáróvá.",
      "7": "<span>7</span> Azután ott lakott Dávid a várban, azért nevezték el azt Dávid városának.",
      "8": "<span>8</span> És kiépítette a várost körös-körül, Millót és környékét, Jóáb pedig a város fönnmaradó részét.",
      "9": "<span>9</span> És Dávid egyre jobban emelkedett, mert vele volt a Seregek URa.",
      "10": "<span>10</span> Ezek voltak Dávid legkiválóbb vitézei, akik mellette voltak, akik erősen munkálkodtak vele az ő királyságáért egész Izráellel együtt, hogy királlyá válasszák őt Izráel fölött az ÚR beszéde szerint.",
      "11": "<span>11</span> Ezek voltak szám szerint a vitézei, akik körülötte voltak: Jásobám, Hakmóni fia, harminc fő elöljárója. Ő egy ízben, mikor fölemelte dárdáját, háromszáz embert sebesített meg vele.",
      "12": "<span>12</span> Utána Eleázár, az ahóhi Dódó fia, a három hős egyike.",
      "13": "<span>13</span> Ő volt Dáviddal EfeszDammimban, ahová összegyűltek harcolni a filiszteusok. Egy darab árpaföld volt ott. Amikor a nép elmenekült a filiszteusok elől,",
      "14": "<span>14</span> ők megálltak ott, annak a darab földnek a közepén, megvédték azt, és megverték a filiszteusokat. Így szerzett nekik az ÚR nagy szabadulást.",
      "15": "<span>15</span> Máskor a harminc vezér közül hárman lementek Dávidhoz, a kősziklához, az Adullám barlangjába, a filiszteusok pedig a Refáim-völgyben ütöttek tábort.",
      "16": "<span>16</span> Dávid akkor a sziklavárban volt, a filiszteusok hada pedig Betlehemnél.",
      "17": "<span>17</span> Dávid vizet kívánt, és így szólt: Ó, ki hozna nekem inni a Betlehem kapuja előtt való forrás vizéből?",
      "18": "<span>18</span> Akkor keresztülvágták magukat hárman a filiszteusok táborán, és vizet merítettek a betlehemi forrásból, amely a kapu előtt volt, majd fölvitték, és odamentek vele Dávidhoz. De Dávid nem akart inni, hanem kiöntötte azt az ÚRnak.",
      "19": "<span>19</span> Ezt mondta: Az én Istenem őrizzen meg attól, hogy ezt cselekedjem. E férfiak vérét igyam-e meg, akik életüket halálra szánták? Mert ők ezt életük kockáztatásával hozták. És semmiképpen sem akart inni. Ezt cselekedte a három vitéz.",
      "20": "<span>20</span> Abisaj, Jóáb testvére volt ennek a háromnak az elöljárója. Ő ragadott dárdát úgy, hogy háromszáz embert sebesített meg. Ő volt a leghíresebb a három közül.",
      "21": "<span>21</span> A három közül a másik kettőnél híresebb volt, azért lett a parancsnokuk. De egymaga mégsem ért föl a hárommal.",
      "22": "<span>22</span> A kabceéli Benájá, Jójádá fia is vitéz férfiú volt, nagy dolgokat cselekedett. Ő vágta le Móáb két bajnokát, ő ment le a verembe, és ölte meg az oroszlánt, amikor egyszer hó esett.",
      "23": "<span>23</span> Ugyanő ölt meg egy öt könyök magas egyiptomi férfit, akinek lándzsája akkora volt, mint a szövőszék zugolyfája. Egy szál bottal szállt szembe vele, kiragadta az egyiptomi kezéből a lándzsát, és saját lándzsájával átütötte.",
      "24": "<span>24</span> Ezeket cselekedte Benájá, Jójádá fia, és ő is híres volt a három hős között.",
      "25": "<span>25</span> Híres volt a harminc között, de azzal a hárommal nem ért föl. Dávid a testőrök parancsnokává tette.",
      "26": "<span>26</span> A sereg vitézei pedig ezek: Aszáél, Jóáb testvére, Elhánán, a betlehemi Dódó fia,",
      "27": "<span>27</span> a haróri Sammót, a pelóni Helec,",
      "28": "<span>28</span> a tekóai Írá, Ikkés fia, az anátóti Abiezer,",
      "29": "<span>29</span> a húsái Szibbekaj, az ahóhi Ílaj,",
      "30": "<span>30</span> a netófái Mahraj, a netófái Héled, Baaná fia,",
      "31": "<span>31</span> a benjámini Gibeából való Ítaj, Ríbaj fia, a pirátóni Benájá,",
      "32": "<span>32</span> a nahalé-gaasi Húraj, az arbai Abiél,",
      "33": "<span>33</span> a baharúmi Azmávet, a saalbóni Eljahbá,",
      "34": "<span>34</span> a gizóni Hásém fiai, a harári Jónátán, Ságé fia,",
      "35": "<span>35</span> a harári Ahiám, Szákár fia, Elifal, Úr fia,",
      "36": "<span>36</span> a mekérai Héfer, a pelóni Ahijjá,",
      "37": "<span>37</span> a karmeli Hecró, Naaraj, Ezbaj fia,",
      "38": "<span>38</span> Jóél, Nátán testvére, Mibhár, Hagri fia,",
      "39": "<span>39</span> az ammóni Celek, a béróti Nahraj, Jóábnak, Cerújá fiának fegyverhordozója,",
      "40": "<span>40</span> a jeteri Írá, a jeteri Gáréb,",
      "41": "<span>41</span> a hettita Úriás, Zábád, Ahlaj fia,",
      "42": "<span>42</span> Adiná, a rúbeni Sizá fia, aki a rúbeniek elöljárója volt, és vele harmincan voltak.",
      "43": "<span>43</span> Hánán, Maaká fia és a mitnibeli Jósáfát,",
      "44": "<span>44</span> az asterái Uzzijjá, az aróéri Hótám fiai: Sámá és Jeiél,",
      "45": "<span>45</span> Jedíaél, Simri fia és testvére, a tíci Jóhá,",
      "46": "<span>46</span> a mahavimi Eliél, Jeribaj és Jósajá, Elnáam fiai; a móábi Jitmá,",
      "47": "<span>47</span> Eliél, Óbéd és a mecóbájái Jaasziél."
    },
    "12": {
      "1": "<span>1</span> Ezek azok, akik Dávidhoz mentek Ciklagba, amikor Kís fia, Saul miatt még számkivetésben volt. Ők is a hősök közé tartoztak, akik segítették a harcban.",
      "2": "<span>2</span> Íjászok voltak, jobb és bal kézzel egyaránt tudtak parittyakővel hajítani és nyíllal lőni. Saul rokonságához tartoztak Benjáminból.",
      "3": "<span>3</span> Vezérük Ahiezer volt és Jóás, a gibeai Semáá fiai és Jeziél meg Pelet, Azmávet fiai, továbbá Beráká és Jéhú, Anátótból,",
      "4": "<span>4</span> a gibeóni Jismajá, a harminc hős egyike, akiknek elöljárójuk is volt.",
      "5": "<span>5</span> Jirmejá, Jahaziél, Jóhánán és a gedérái Józábád.",
      "6": "<span>6</span> Elúzaj, Jerimót, Bealjá, Semarjáhú, a hárífi Sefatjáhú,",
      "7": "<span>7</span> Elkáná, Jissijjáhú, Azarél, Jóezer és a Kórah nemzetségéből való Jásobám.",
      "8": "<span>8</span> Jóélá és Zebadjá, a gedóri Jeróhám fiai.",
      "9": "<span>9</span> Gádiak is csatlakoztak Dávidhoz, amikor a pusztában volt a sziklavárban. Erős és harcos férfiak, pajzsosok, dárdások, akiknek az arca olyan volt, mint az oroszláné, és fürgeségük mint a zergéké a hegyen.",
      "10": "<span>10</span> Ézer volt az első, Óbadjá a második, Eliáb a harmadik.",
      "11": "<span>11</span> Mismanná a negyedik, Jirmejá az ötödik,",
      "12": "<span>12</span> Attaj a hatodik, Eliél a hetedik volt.",
      "13": "<span>13</span> A nyolcadik Jóhánán, a kilencedik Elzábád.",
      "14": "<span>14</span> A tizedik Jirmejáhú, a tizenegyedik Makbannaj volt.",
      "15": "<span>15</span> Ezek voltak a parancsnokok a seregben Gád fiai közül. A legkisebb is fölért százzal, a legnagyobb fölért ezerrel.",
      "16": "<span>16</span> Ezek keltek át a Jordánon az első hónapban, noha az ár túlnőtt a parton, és elűzték mindazokat, akik a völgyben voltak keletre és nyugatra.",
      "17": "<span>17</span> Benjámin és Júda fiai közül is jöttek Dávidhoz a sziklavárába.",
      "18": "<span>18</span> Dávid kiment eléjük, és így szólt hozzájuk: Ha békés szándékkal jöttök, hogy segítségemre legyetek, szívem egy lesz veletek. De ha meg akartok csalni, hogy eláruljatok az ellenségeimnek, holott semmi erőszakot nem követtem el, lássa meg, és büntessen meg titeket atyáink Istene.",
      "19": "<span>19</span> A lélek ekkor felindította Amászajt, a harmincnak parancsnokát, és így szólt: Ó, Dávid, tied vagyunk, és veled leszünk, Isai fia! Békesség, békesség neked! Békesség segítőidnek is, mert megsegít téged Istened! Ekkor magához fogadta őket Dávid, és főemberekké tette őket a seregben.",
      "20": "<span>20</span> Ezenfelül manassébeliek is pártoltak át Dávidhoz, amikor a filiszteusokkal együtt harcolni ment Saul ellen. De nem segítette a filiszteusokat Dávid, mert fejedelmeik tanácsot tartottak, és elküldték őt, ezt mondván: A mi fejünkkel fog még ez visszamenni urához, Saulhoz.",
      "21": "<span>21</span> Amikor visszatért Ciklagba, hozzá pártolt Manassé fiai közül Adnah, Józábád, Jedíaél, Míkáél, Józábád, Elihú és Cilletaj, akik a Manassé törzséből való ezrek vezérei voltak.",
      "22": "<span>22</span> Ezek segítették Dávidot az ellenség seregei ellen, mert egyenként is mind erős vitézek voltak, és parancsnokok a hadseregben.",
      "23": "<span>23</span> Azonkívül naponként mentek mások is Dávidhoz, hogy segítségére legyenek, míg olyan naggyá lett serege, mint Isten serege.",
      "24": "<span>24</span> Szám szerint ezek voltak azok a harcra fölkészített elöljárók, akik Dávidhoz mentek Hebrónba, hogy őt Saul helyett királlyá válasszák az országban Isten ígérete szerint:",
      "25": "<span>25</span> Júda fiai közül hatezer-nyolcszáz harcra fölkészített ember, akik pajzsot és dárdát viseltek.",
      "26": "<span>26</span> Simeon fiai közül hétezer-egyszáz viadalra fölkészített vitéz férfi,",
      "27": "<span>27</span> Lévi fiai közül négyezer-hatszáz,",
      "28": "<span>28</span> Jehójádá is, aki Áron fiai között elöljáró volt, és vele háromezer-hétszáz ember",
      "29": "<span>29</span> és az ifjú Cádók, aki igen erős volt, atyja házából huszonkét főemberrel.",
      "30": "<span>30</span> Benjámin fiai közül, akik Saul testvérei voltak, háromezren, mert akkor még közülük sokan hűségesen őrizték Saul házát.",
      "31": "<span>31</span> Efraim fiai közül való volt húszezer-nyolcszáz kiváló vitéz, akik híres férfiak voltak nemzetségükben.",
      "32": "<span>32</span> Manassé fél törzséből való volt tizennyolcezer ember, akiket név szerint jelöltek ki, hogy menjenek el, és válasszák királlyá Dávidot.",
      "33": "<span>33</span> Issakár fiai közül ott voltak, akik fölismerték az alkalmas időt, és tudták, mit kell Izráelnek cselekednie, kétszáz főember és parancsnokságuk alatt minden rokonuk.",
      "34": "<span>34</span> Zebulon fiai közül azok, akik kimentek mint harcosok, minden harci szerszámmal fölszerelve, ötvenezren voltak, állhatatos szívvel felkészítve a viadalra,.",
      "35": "<span>35</span> Naftáli nemzetségéből ezer főember jött, és velük együtt harminchétezren pajzzsal és dárdával.",
      "36": "<span>36</span> A dániak közül huszonnyolcezer-hatszáz harcra fölkészített férfi.",
      "37": "<span>37</span> És Ásér fiai közül a harcosok és ütközetre fölkészítettek negyvenezren voltak.",
      "38": "<span>38</span> A Jordánon túl lakók közül, azaz a rúbeniek, gádiak közül és a Manassé törzsének másik feléből százhúszezren jöttek teljes harci felszereléssel.",
      "39": "<span>39</span> Mindezek a harcosok harcra fölkészítve, egy lélekkel mentek Hebrónba, hogy Dávidot egész Izráel felett királlyá tegyék, sőt egész Izráel többi része is egy szívvel azon volt, hogy Dávidot válasszák királlyá.",
      "40": "<span>40</span> És ott maradtak Dáviddal harmadnapig, és ettek és ittak, mert testvéreik gondoskodtak róluk.(41) Még a szomszédságukból és máshonnan is, el egészen Issakárig, Zebulonig és Naftáliig, bőven hoztak kenyeret, eleséget, lisztet, fügét, aszúszőlőt, bort, olajat, vágómarhát, juhokat hoztak szamarakon, tevéken, öszvéreken és ökrökön, mert nagy öröm volt Izráelben."
    },
    "13": {
      "1": "<span>1</span> Dávid tanácsot tartott az ezredek és századok fejeivel és valamennyi vezérrel,",
      "2": "<span>2</span> majd azt mondta Izráel egész gyülekezetének: Ha tetszik nektek, és ha az ÚRtól, a mi Istenünktől van, küldjünk üzenetet mindenfelé testvéreinkhez, akik otthon maradtak Izráel minden tartományában, és velük együtt a papokhoz és lévitákhoz, városaik és földjeik szerint, hogy ők is gyűljenek hozzánk.",
      "3": "<span>3</span> És hozzuk ide mihozzánk Istenünk ládáját, mert Saul idejében nem törődtünk vele.",
      "4": "<span>4</span> Erre az egész gyülekezet azt mondta, hogy így kell tenni, mert helyesnek látszott ez a dolog az egész nép előtt.",
      "5": "<span>5</span> Összegyűjtötte azért Dávid egész Izráelt az egyiptomi Síhórtól egészen a Hamátba vezető útig, hogy Isten ládáját elhozzák Kirjat-Jeárimból.",
      "6": "<span>6</span> Fölvonult tehát Dávid és vele egész Izráel Baalába, Kirjat-Jeárimba, amely Júdában van, hogy elhozzák onnan Istennek, a kerúbok között ülő ÚRnak a ládáját, akinek a nevét segítségül hívták.",
      "7": "<span>7</span> Isten ládáját egy új szekéren vitték el Abinádáb házából. Uzzá és Ahjó vezette a szekeret.",
      "8": "<span>8</span> Dávid pedig és az egész Izráel táncolt Isten előtt teljes erővel, énekekkel, citerákkal, hegedűkkel, dobokkal, cintányérokkal és kürtökkel.",
      "9": "<span>9</span> Mikor Kídón szérűjéhez értek, Uzzá kinyújtotta a kezét, hogy megfogja a ládát, mert megbotlottak az ökrök.",
      "10": "<span>10</span> Ezért fölgerjedt az ÚR haragja Uzzá ellen, és lesújtott rá, mivel kinyújtotta kezét a láda felé, s meghalt ott, az Isten színe előtt.",
      "11": "<span>11</span> Akkor Dávid igen megdöbbent, hogy az ÚR ilyen csapással sújtotta Uzzát. Azért azt a helyet mind e mai napig Perec-Uzzának nevezik.",
      "12": "<span>12</span> Azon a napon Dávid félni kezdett Istentől, és ezt mondta: Hogy merjem bevinni magamhoz Isten ládáját?!",
      "13": "<span>13</span> Ezért Dávid nem magához vitette be a ládát Dávid városába, hanem a gáti Óbéd-Edóm házában helyezte el.",
      "14": "<span>14</span> Isten ládája három hónapig Óbéd-Edóm családjánál, az ő házában volt, és megáldotta az ÚR Óbéd-Edóm háza népét és mindenét, amije volt."
    },
    "14": {
      "1": "<span>1</span> Hírám, Tírusz királya követeket küldött Dávidhoz, meg cédrusfákat, kőműveseket és ácsmestereket, hogy palotát építsenek neki.",
      "2": "<span>2</span> És fölismerte Dávid, hogy az ÚR megerősítette őt a királyságban Izráel fölött, mivel igen felmagasztalta országát az ő népéért, Izráelért.",
      "3": "<span>3</span> Dávid még több feleséget vett Jeruzsálemben, és nemzett még más fiakat és leányokat is.",
      "4": "<span>4</span> Ez a nevük azoknak, akik Jeruzsálemben születtek neki: Sammúa, Sóbáb, Nátán és Salamon,",
      "5": "<span>5</span> Jibhár, Elisúa és Elpelet,",
      "6": "<span>6</span> Nógah, Nefeg és Jáfía,",
      "7": "<span>7</span> Elisámá, Beeljádá és Elifelet.",
      "8": "<span>8</span> Amikor megtudták a filiszteusok, hogy Dávidot királlyá kenték egész Izráel fölött, feljött az összes filiszteus, hogy megkeressék Dávidot. Amikor Dávid meghallotta ezt, kivonult ellenük.",
      "9": "<span>9</span> A filiszteusok pedig odaértek, és ellepték a Refáim-völgyet.",
      "10": "<span>10</span> Ekkor Dávid megkérdezte Istent: Menjek-e a filiszteusok ellen, kezembe adod-e őket? Az ÚR ezt felelte neki: Menj, és kezedbe adom őket!",
      "11": "<span>11</span> Fölmentek tehát BaalPerácimba, és ott Dávid megverte őket. Ekkor ezt mondta Dávid: Isten szétoszlatta az ellenségeimet kezem által úgy, ahogy a víz szétoszlik. Azért nevezték azt a helyet BaalPerácimnak.",
      "12": "<span>12</span> A filiszteusok otthagyták bálványisteneiket, Dávid pedig megparancsolta, hogy égessék el azokat.",
      "13": "<span>13</span> Ismét fölkerekedtek a filiszteusok, és ellepték a völgyet.",
      "14": "<span>14</span> Azért Dávid ismét megkérdezte Istent, és Isten azt felelte neki: Ne vonulj föl utánuk, hanem kerüld meg őket, és a szederfák irányából támadj rájuk.",
      "15": "<span>15</span> És mikor lépések zaját fogod hallani a szederfák teteje felől, akkor vonulj ki a harcra, mert előtted megy Isten, hogy megverje a filiszteusok táborát.",
      "16": "<span>16</span> Dávid azért úgy cselekedett, ahogy Isten meghagyta neki, és Gibeóntól fogva szinte Gézerig vágták a filiszteusok táborát.",
      "17": "<span>17</span> Dávid híre pedig elterjedt az országokban, és az ÚR félelmet keltett iránta minden nép között."
    },
    "15": {
      "1": "<span>1</span> Azután Dávid házakat építtetett magának a városában, és helyet készített Isten ládájának, és sátrat állított fel neki.",
      "2": "<span>2</span> Akkor mondta ezt Dávid: Nem szabad másnak hordoznia Isten ládáját, hanem csak a lévitáknak, mert az ÚR őket választotta ki, hogy azt hordozzák és szolgálják örökké.",
      "3": "<span>3</span> Összegyűjtötte azért Dávid Jeruzsálembe egész Izráel népét, hogy az ÚR ládáját a helyére vitesse, amelyet számára készített.",
      "4": "<span>4</span> Összegyűjtötte Dávid Áron fiait és a lévitákat is.",
      "5": "<span>5</span> Kehát fiainak vezetőjét, Úriélt és százhúsz atyjafiát,",
      "6": "<span>6</span> Merári fiainak vezetőjét, Aszáját és kétszázhúsz atyjafiát,",
      "7": "<span>7</span> Gérsón fiainak vezetőjét, Jóélt és százharminc atyjafiát,",
      "8": "<span>8</span> Elicáfán fiainak vezetőjét, Semaját és kétszáz atyjafiát,",
      "9": "<span>9</span> Hebrón fiainak vezetőjét, Eliélt és nyolcvan atyjafiát,",
      "10": "<span>10</span> Uzzíél fiainak vezetőjét, Ammínádábot és száztizenkét atyjafiát.",
      "11": "<span>11</span> Akkor Dávid hívatta Cádók és Ebjátár papokat, a léviták közül pedig Úriélt, Aszáját, Jóélt, Semaját, Eliélt és Ammínádábot,",
      "12": "<span>12</span> és ezt mondta nekik: Ti vagytok a léviták családfői. Szenteljétek meg magatokat atyátokfiaival együtt, és vigyétek az ÚRnak, Izráel Istenének ládáját arra a helyre, amelyet készítettem számára.",
      "13": "<span>13</span> Minthogy az első alkalommal nem tettétek ezt, az ÚR, a mi Istenünk csapást bocsátott reánk, mert nem jártunk el a törvény szerint.",
      "14": "<span>14</span> Megszentelték azért magukat a papok és a léviták, hogy fölvigyék az ÚRnak, Izráel Istenének ládáját.",
      "15": "<span>15</span> Majd a léviták fiai Isten ládáját a rudakkal a vállukra vették úgy, ahogyan Mózes meghagyta az ÚR beszéde szerint.",
      "16": "<span>16</span> És Dávid megparancsolta a léviták fejedelmeinek, hogy állítsanak oda atyjukfiai közül énekeseket zeneszerszámokkal, lantokkal, citerákkal és cintányérokkal, és hangosan, nagy örömmel énekeljenek.",
      "17": "<span>17</span> Erre a léviták kiválasztották Hémánt, Jóél fiát és testvérei közül Ászáfot, Berekjáhú fiát és testvéreik, Mérári fiai közül Étánt, Kúsájáhú fiát.",
      "18": "<span>18</span> Velük együtt másodrenden testvéreiket: Zekarjáhút, Bént, Jaaziélt, Semirámótot, Jehiélt, Unnit, Eliábot, Benájáhút, Maaszéjáhút, Mattitjáhút, Elifléhút, Miknéjáhút, Óbéd-Edómot és Jeiélt, a kapuőröket.",
      "19": "<span>19</span> Az énekesek ezek voltak: Hémán, Ászáf és Étán, hogy a réz cintányérokat megzendítse;",
      "20": "<span>20</span> Zekarjá, Aziél, Semirámót, Jehiél, Unni, Eliáb, Maaszéjáhú és Benájáhú pedig a magas hangú lantokat.",
      "21": "<span>21</span> Mattitjáhú, Elifléhú, Miknéjáhú, Óbéd-Edóm, Jeiél és Azazjáhú a mély hangú citerákon játszottak.",
      "22": "<span>22</span> Kenanjáhú volt a léviták vezére az éneklésben, ő irányította az éneklést, mivel értett hozzá.",
      "23": "<span>23</span> Berekjá és Elkáná őrködtek a ládánál.",
      "24": "<span>24</span> Sebanjáhú, Jósáfát, Netanél, Amászaj, Zekarjáhú, Benájáhú és Eliezer papok kürtöltek Isten ládája előtt. Óbéd-Edóm és Jeiél is őrködött a ládánál.",
      "25": "<span>25</span> Dávid, Izráel vénei és az ezredek parancsnokai pedig elmentek, hogy örvendezve fölvigyék az ÚR szövetségládáját Óbéd-Edóm házából.",
      "26": "<span>26</span> Majd, mivel Isten megsegítette a lévitákat, akik az ÚR szövetségládáját vitték, hét bikát és hét kost áldoztak.",
      "27": "<span>27</span> Dávid pedig bíborból készült palástba öltözve együtt énekelt az összes lévitával, akik a ládát vitték, és az énekesekkel és Kenanjával, az éneklés vezetőjével. Dávidon pedig gyolcsból csinált éfód volt.",
      "28": "<span>28</span> És egész Izráel vitte az ÚR szövetségének ládáját nagy örömmel, kürtszóval, trombitákkal, cintányérokkal, lantokkal és citerákkal.",
      "29": "<span>29</span> Mikor pedig immár az ÚR szövetségládája Dávid városába jutott, Míkal, Saul leánya kitekintett az ablakon, és látta, hogy Dávid király táncol és vigad, szívében megvetette őt."
    },
    "16": {
      "1": "<span>1</span> Mikor pedig bevitték Isten ládáját és elhelyezték a sátor közepén, amelyet Dávid állított fel neki, egészen elégő és békeáldozatokat mutattak be Isten előtt.",
      "2": "<span>2</span> Amikor Dávid elvégezte az égőáldozat és a békeáldozat bemutatását, az ÚR nevében megáldotta a népet.",
      "3": "<span>3</span> Azután minden izráeli férfinak és asszonynak kiosztott egy-egy kenyeret és egy-egy darab húst meg aszúszőlőt.",
      "4": "<span>4</span> És szolgákat rendelt az ÚR ládája elé a léviták közül, hogy hirdessék, magasztalják és dicsérjék az URat, Izráel Istenét.",
      "5": "<span>5</span> Ászáf volt a vezető, a helyettese Zekarjá, továbbá Jeiél, Semirámót, Jehiél, Mattitjá, Eliáb, Benájáhú és Óbéd-Edóm. Jeiél lanton és citerán, Ászáf pedig cintányérokon játszott.",
      "6": "<span>6</span> Benájáhú és Jahaziél papok állandóan kürtöltek az ÚR szövetségládája előtt.",
      "7": "<span>7</span> Azon a napon nevezte ki először Dávid Ászáfot és atyjafiait az ÚR magasztalására.",
      "8": "<span>8</span> Dicsérjétek az URat, hívjátok segítségül a nevét, hirdessétek nagy tetteit minden nép között.",
      "9": "<span>9</span> Énekeljetek neki, zengjetek neki dicséretet, beszéljétek el minden csodáját.",
      "10": "<span>10</span> Dicsőítsétek szent nevét; örvendezzen a szíve azoknak, akik keresik az URat.",
      "11": "<span>11</span> Keressétek az URat és az ő erejét, keressétek orcáját szüntelen.",
      "12": "<span>12</span> Emlékezzetek meg csodálatos dolgairól, amelyeket cselekedett, csodáiról és szájának ítéleteiről.",
      "13": "<span>13</span> Ó, Izráel utódai, akik szolgái vagytok! Jákóbnak fiai, akiket kiválasztott!",
      "14": "<span>14</span> Ő az ÚR, a mi Istenünk! Az egész földnek szólnak rendelkezései!",
      "15": "<span>15</span> Emlékezzetek örökké szövetségére és beszédére, amelyet parancsolt ezer nemzedéken át;",
      "16": "<span>16</span> szövetségére, amelyet Ábrahámmal szerzett, és Izsáknak tett esküjére.",
      "17": "<span>17</span> Örök végzésként állította azt Jákób elé, Izráelnek örökkévaló szövetségül,",
      "18": "<span>18</span> ezt mondván: Neked adom Kánaán földjét, hogy örökségetek legyen.",
      "19": "<span>19</span> Akkor még igen kevesen voltatok, és jövevények voltatok azon a földön.",
      "20": "<span>20</span> Az egyik néptől a másikig és az egyik országból a másik országba vándoroltak.",
      "21": "<span>21</span> Mégsem engedte, hogy bárki bántsa őket, sőt még a királyokat is megintette miattuk.",
      "22": "<span>22</span> Ezt mondta: Fölkentjeimet ne bántsátok, prófétáimnak se ártsatok!",
      "23": "<span>23</span> Mind az egész föld énekeljen az ÚRnak, napról napra hirdessétek szabadítását!",
      "24": "<span>24</span> Beszéljetek dicsőségéről a nemzetek között, csodálatos dolgairól minden nép között!",
      "25": "<span>25</span> Mert nagy az ÚR, és igen dicsérendő, félelmesebb minden istennél.",
      "26": "<span>26</span> Mert a népek minden istene csak bálvány, de az ÚR teremtette az egeket.",
      "27": "<span>27</span> Dicsőség és tisztesség jár őelőtte, erő és vigasság van lakóhelyén.",
      "28": "<span>28</span> Magasztaljátok az URat, népeknek törzsei, magasztaljátok az ÚR dicsőségét és erejét!",
      "29": "<span>29</span> Adjatok dicsőséget az ÚR nevének, hozzatok ajándékot, jöjjetek színe elé, imádjátok az URat szent ékességben.",
      "30": "<span>30</span> Reszkessen előtte az egész föld! A földkerekség is szilárdan áll, hogy ne ingadozzék.",
      "31": "<span>31</span> Örüljenek az egek, örvendezzen a föld, és mondják el a népek között: uralkodik az ÚR!",
      "32": "<span>32</span> Zúgjon a tenger a benne levőkkel, örvendezzen a mező és minden rajta lévő!",
      "33": "<span>33</span> Ujjonganak majd az erdő fái az ÚR előtt, amikor eljön, hogy megítélje a földet.",
      "34": "<span>34</span> Adjatok hálát az ÚRnak, mert jó, mert irgalmassága örökkévaló.",
      "35": "<span>35</span> Mondjátok: Segíts meg minket, szabadító Istenünk, gyűjts össze minket, és szabadíts meg a pogányoktól, hogy hálát adhassunk szent nevednek, és dicsérhessünk, magasztalhassunk!",
      "36": "<span>36</span> Áldott az ÚR, Izráel Istene öröktől fogva mindörökké! És az egész nép így felelt: Ámen! És dicsérte az URat.",
      "37": "<span>37</span> Ott hagyta azért Dávid az ÚR szövetségládájánál Ászáfot és testvéreit, hogy a láda előtt mindennap állandóan szolgáljanak,",
      "38": "<span>38</span> továbbá Óbéd-Edómot és hatvannyolc testvérét. Óbéd-Edóm, Jedútún fia és Hószá voltak a kapuőrök.",
      "39": "<span>39</span> Cádók papot pedig és testvéreit, a papokat az ÚR hajléka előtt hagyta a magaslaton, amely Gibeónban van,",
      "40": "<span>40</span> hogy égőáldozatot mutassanak be az ÚRnak szüntelen az égőáldozat oltárán reggel meg este egészen úgy, ahogy meg van írva az ÚR törvényében, melyet Izráelnek parancsolt.",
      "41": "<span>41</span> Hémánt és Jedútúnt is velük hagyta, és másokat is választott, akiket név szerint megneveztek, hogy az URat dicsérjék, mert az ő irgalmassága örökkévaló!",
      "42": "<span>42</span> Hémánnál és Jedútúnnál voltak a kürtök, a cintányérok és az istenes énekeket kísérő hangszerek. Jedútún fiait pedig kapuőrökké tette.",
      "43": "<span>43</span> Akkor eltávozott az egész nép, ki-ki a maga házába. Dávid pedig visszatért, hogy saját háza népét is megáldja."
    },
    "17": {
      "1": "<span>1</span> Mikor Dávid már a palotájában lakott, azt mondta egyszer Nátán prófétának: Íme, én cédrusfából készült palotában lakom, az ÚR szövetségének ládája pedig sátorlapok alatt van.",
      "2": "<span>2</span> Akkor ezt mondta Dávidnak Nátán: Cselekedd meg, ami a szívedben van, mert Isten veled lesz!",
      "3": "<span>3</span> De azon az éjjelen Isten szava így szólt Nátánhoz:",
      "4": "<span>4</span> Menj el, és mondd meg szolgámnak, Dávidnak: Ezt mondja az ÚR: Ne te építs nekem házat lakásul,",
      "5": "<span>5</span> mert mindmáig nem laktam házban azóta, mióta Izráel fiait kihoztam, hanem egyik hajlékból a másikba mentem és sátorból sátorba.",
      "6": "<span>6</span> Amerre Izráel egész népével jártam, szóltam-e így egyszer is valakinek Izráel bírái közül, akiknek azt parancsoltam, hogy legeltessék népemet: Miért nem csináltatok nekem cédrusfából házat?",
      "7": "<span>7</span> Most azért ezt mondd szolgámnak, Dávidnak: Ezt mondja a Seregek URa: Én választottalak ki téged a juhok mellől a pásztorkunyhóból, hogy vezére légy népemnek, Izráelnek.",
      "8": "<span>8</span> És veled voltam mindenütt, amerre csak jártál, minden ellenségedet elpusztítottam előled, sőt mindezen felül olyan nevet szereztem neked, amilyen csak a föld hatalmasainak van.",
      "9": "<span>9</span> Lakóhelyet is adtam népemnek, Izráelnek, és elültettem őt. Saját helyén lakik, nem mozdítják ki többé helyéből, nem fogják sanyargatni a gonoszok, mint azelőtt.",
      "10": "<span>10</span> Attól az időtől fogva, hogy megparancsoltam, hogy bírák legyenek népem, Izráel fölött, minden ellenségedet megaláztam. Azt is kijelentettem neked, hogy az ÚR házat épít neked.",
      "11": "<span>11</span> Amikor betelnek életed napjai, hogy atyáidhoz térj pihenni, föltámasztom utánad ivadékodat, aki fiaid közül való lesz, és megerősítem országát.",
      "12": "<span>12</span> Ő épít nekem házat, és megerősítem trónját mindörökké.",
      "13": "<span>13</span> Atyja leszek neki, ő pedig fiam lesz, és irgalmasságomat nem vonom meg tőle, mint ahogy az előtted valótól megvontam.",
      "14": "<span>14</span> Hanem megerősítem őt az én házam és királyságom szolgálatában mindörökre, és trónja örökké erős lesz.",
      "15": "<span>15</span> Pontosan e beszédek és e látás szerint szólt Nátán Dávidnak.",
      "16": "<span>16</span> Akkor bement Dávid király, leült az ÚR előtt, és ezt mondta: Ki vagyok én, ó, URam Isten, s micsoda az én házam népe, hogy eddig eljuttattál engem?",
      "17": "<span>17</span> Sőt még ez is kevés volt előtted, ó, Istenem, hanem a jövendőre nézve is tettél ígéretet szolgád háza felől, és mint rangjában legföljebb levő embert, úgy tekintettél engem, ÚR Isten!",
      "18": "<span>18</span> Kérhetne-e Dávid, a te szolgád több megtiszteltetést tőled? Hiszen te jól ismered szolgádat!",
      "19": "<span>19</span> Ó, URam, a te szolgádért és szíved szerint cselekedted mindezt a nagy dolgot, hogy kijelentetted mindezeket a csodálatos dolgokat.",
      "20": "<span>20</span> Ó, URam, nincs senki hozzád fogható, és nincs nálad nagyobb Isten, egészen úgy, ahogy fülünkkel hallottuk.",
      "21": "<span>21</span> És van-e még egy olyan nemzet e földön, mint a te néped, Izráel, az egyetlen olyan nemzet a földön, amelyért elment Isten, hogy kiszabadítsa és a maga népéül válassza, hogy nagy és félelmes nevet szerezz magadnak, kiűzve a pogányokat néped elől, amelyet megszabadítottál Egyiptomból?",
      "22": "<span>22</span> Így tetted Izráel népét a te népeddé örökre, és te, URam, Istenük lettél.",
      "23": "<span>23</span> Most azért, URam, az ígéretedet, amelyet szolgád és az ő háza felől adtál, erősítsd meg örökre, és cselekedj úgy, amint megmondtad.",
      "24": "<span>24</span> Maradjon meg és magasztaltassék föl a te neved mindörökké, hogy mondhassák: A Seregek URa Izráel Istene; ő az Istene Izráelnek. És Dávidnak, a te szolgádnak háza pedig bizton megáll színed előtt.",
      "25": "<span>25</span> Minthogy te, Istenem, szolgád füle hallatára kijelentetted, hogy házat készítesz neki, ezért indult fel szolgád, hogy így könyörögjön előtted.",
      "26": "<span>26</span> Valóban, URam, te vagy az Isten, és te ígérted szolgádnak ezt a jót.",
      "27": "<span>27</span> Most azért áldd meg kegyelmesen szolgád házát, hogy örökre színed előtt legyen. Mert ha te, URam, megáldod, örökre áldott lesz."
    },
    "18": {
      "1": "<span>1</span> Ezután leverte Dávid a filiszteusokat, megalázta őket, és Gát városát falvaival együtt elvette a filiszteusok kezéből.",
      "2": "<span>2</span> Móábot is megverte, és a móábiak Dávid adófizető szolgái lettek.",
      "3": "<span>3</span> Továbbá megverte Dávid Hamátban Hadadezert, Cóbá királyát is, amikor elindult, hogy az Eufrátesz folyamig terjessze ki birodalmának határát.",
      "4": "<span>4</span> Dávid elfogott ezer harci szekeret, hétezer lovast és húszezer gyalogost. Az összes szekérhez való ló inát elvágatta Dávid, és csak száz szekérbe valót tartott meg belőlük.",
      "5": "<span>5</span> És amikor eljöttek Damaszkuszból az arámok, hogy megsegítsék Hadadezert, Cóbá királyát, Dávid levágott az arámok közül huszonkétezer férfit.",
      "6": "<span>6</span> Majd Dávid tiszttartókat rendelt az arám Damaszkuszba. Az arámok Dávid adófizető szolgái lettek. És megsegítette az ÚR Dávidot mindenütt, ahová csak ment.",
      "7": "<span>7</span> Elvette Dávid az aranypajzsokat, amelyeket Hadadezer szolgái viseltek, és Jeruzsálembe vitette.",
      "8": "<span>8</span> Dávid igen sok rezet hozott Hadadezer városaiból, Tibhatból és Kúnból. Abból csináltatta Salamon a réztengert, az oszlopokat és a rézedényeket.",
      "9": "<span>9</span> Amikor Tói, Hamát királya meghallotta, hogy Dávid megverte Hadadezernek, Cóbá királyának egész hadát,",
      "10": "<span>10</span> Dávid királyhoz küldte fiát, Hadórámot, hogy köszöntse és megáldja, mivel megküzdött Hadadezerrel, és megverte őt. Mert Tói is harcolt Hadadezer ellen. És sok arany-, ezüst- és rézedényt küldött Dávidnak.",
      "11": "<span>11</span> Dávid király ezeket is az ÚRnak szentelte, azzal az ezüsttel és arannyal együtt, amelyet a többi néptől, az edómiaktól, móábiaktól, ammóniaktól, filiszteusoktól és az amálékiaktól szerzett.",
      "12": "<span>12</span> Abisaj, Cerújá fia pedig megvert tizennyolcezer edómit a Sós-völgyben.",
      "13": "<span>13</span> Dávid tiszttartókat rendelt Edómba, és az összes edómi Dávid szolgája lett. És megsegítette az ÚR Dávidot mindenütt, ahová csak ment.",
      "14": "<span>14</span> Így uralkodott Dávid egész Izráel fölött, jogot és igazságot szolgáltatott egész népének.",
      "15": "<span>15</span> Jóáb, Cerújá fia a sereg parancsnoka, Jehósáfát, Ahilúd fia pedig emlékíró volt.",
      "16": "<span>16</span> Cádók, Ahitúb fia és Abímelek, Ebjátár fia voltak a papok, Savsá pedig a kancellár.",
      "17": "<span>17</span> És Benájá, Jehójádá fia a kerétiek és a pelétiek parancsnoka volt, Dávid fiai pedig a legelsők voltak a király mellett."
    },
    "19": {
      "1": "<span>1</span> Történt ezután, hogy meghalt Náhás, Ammón fiainak a királya, és utána a fia lett a király.",
      "2": "<span>2</span> Dávid azt mondta: Jóindulattal leszek Náhás fiához, Hánúnhoz, mert apja is jót tett velem. Ezért Dávid követeket küldött hozzá, hogy vigasztalják őt apja halála miatt. El is mentek Dávid szolgái Ammón fiainak a földjére Hánúnhoz, hogy vigasztalják.",
      "3": "<span>3</span> Akkor azt mondták Ammón fiainak a főemberei Hánúnnak: Azt hiszed, hogy Dávid apád iránti tiszteletből küldött hozzád vigasztalókat? Nem inkább azért jöttek-e az ő szolgái hozzád, hogy kifürkésszék, feldúlják és kikémleljék az országot?",
      "4": "<span>4</span> Elfogatta azért Hánún Dávid szolgáit, és megnyíratta őket, ruhájukat félig levágatta deréktól lefelé, és úgy bocsátotta el őket.",
      "5": "<span>5</span> Azok elmentek, Dávidot pedig értesítették, hogy mi történt a férfiakkal. Mivel azokat a férfiakat igen nagy szégyenbe hozták, eléjük küldött a király, és azt üzente: Maradjatok Jerikóban, amíg meg nem nő a szakállatok. Akkor térjetek vissza!",
      "6": "<span>6</span> Miután Ammón fiai látták, hogy meggyűlöltették magukat Dáviddal, elküldtek Hánún és Ammón fiai ezer talentum ezüstöt Aram-Naharaimba, Aram-Maakába és Cóbába, hogy harci szekereket és lovasokat fogadjanak fel.",
      "7": "<span>7</span> Fölfogadtak tehát maguknak harminckétezer harci szekeret, és Maaká királyát is hadinépével együtt, akik eljöttek, és tábort ütöttek Médebá előtt. Ammón fiai is összegyűltek városaikból, és hadba vonultak.",
      "8": "<span>8</span> Amint ezt meghallotta Dávid, elküldte Jóábot és a vitézek egész seregét.",
      "9": "<span>9</span> Ammón fiai kivonultak, csatarendbe álltak a város kapuja előtt. A királyok pedig, akik segítségére jöttek, külön álltak fel a mezőn.",
      "10": "<span>10</span> Látta Jóáb, hogy elöl is, hátul is ellenség áll. Ezért kiválasztotta egész Izráel harcosai közül a legvitézebbeket, és felsorakoztatta őket az arámok ellen.",
      "11": "<span>11</span> A hadinép többi részét pedig testvérére, Abisajra bízta, és ezek az Ammón fiaival szemben álltak csatarendbe.",
      "12": "<span>12</span> Azt mondta: Ha az arámok erősebbek lennének nálam, légy segítségemre! Ha pedig az Ammón fiai nálad erősebbek lennének, én segítlek meg.",
      "13": "<span>13</span> Légy erős, sőt legyünk bátrak mindnyájan népünkért és Istenünk városaiért. Az ÚR pedig cselekedje azt, ami neki tetszik.",
      "14": "<span>14</span> Akkor Jóáb és hada rohamra indult az arámok ellen, és azok megfutamodtak előle.",
      "15": "<span>15</span> Az Ammón fiai pedig, amikor látták, hogy futnak az arámok, ők is megfutamodtak az ő testvére, Abisaj elől, és a városba menekültek. Jóáb pedig Jeruzsálembe ment.",
      "16": "<span>16</span> Amikor az arámok látták, hogy Izráel megverte őket, követeket küldtek, és segítségül hívták a folyamon túl lakó arámokat. Sófak, Hadadezer seregeinek vezére állt az élükön.",
      "17": "<span>17</span> Mikor pedig hírül adták ezt Dávidnak, ő összegyűjtötte egész Izráelt, és a Jordán vizén átkelve elment hozzájuk, és fölsorakozott ellenük. Amikor Dávid csatasorba állt az arámok ellen, azok megütköztek vele.",
      "18": "<span>18</span> De az arámok megfutamodtak Izráel elől, és levágott Dávid az arámok közül hétezer harci szekerest és negyvenezer gyalogost. Sőt Sófakot, a sereg vezérét is megölte.",
      "19": "<span>19</span> Mikor pedig Hadadezer szolgái látták, hogy Izráel legyőzte őket, békét kötöttek Dáviddal, és szolgáltak neki. Ezután az arámok nem akarták többször megsegíteni Ammón fiait."
    },
    "20": {
      "1": "<span>1</span> Az esztendő fordultával, amikor a királyok harcba szoktak menni, elindította Jóáb a hadat, és elpusztította Ammón fiainak földjét. Odaért Rabbához, és körülzárta. Dávid azonban Jeruzsálemben maradt. Jóáb elfoglalta és lerombolta Rabbát.",
      "2": "<span>2</span> Dávid levette királyuk fejéről a koronát, amely egy talentum arany súlyú volt, s drágakövekkel volt kirakva, és az Dávid fejére került. A városból is tömérdek zsákmányt vitt el.",
      "3": "<span>3</span> A város népét pedig kihozatta, és fűrésszel, vascsákánnyal és fejszékkel való munkára fogta őket. Így tett Dávid Ammón fiainak valamennyi városával. Majd az egész néppel visszatért Jeruzsálembe.",
      "4": "<span>4</span> Ezután ismét harc tört ki Gézerben a filiszteusokkal. Akkor ölte meg a húsái Szibbekaj a refáiak nemzetségéből való Szippajt, és azok behódoltak.",
      "5": "<span>5</span> Ismét harc tört ki a filiszteusok ellen, ebben ölte meg Elhánán, Jáír fia a gáti Lahmit, Góliát testvérét. Dárdájának nyele akkora volt, mint a szövőszék zugolyfája.",
      "6": "<span>6</span> Ezután Gátnál is harc tört ki; volt ott egy magas termetű férfi, akinek hat-hat, vagyis huszonnégy ujja volt. A ráfáiaktól származott ő is.",
      "7": "<span>7</span> Mivel szidalommal illette Izráelt, megölte őt Jónátán, Dávid testvérének, Simeának a fia.",
      "8": "<span>8</span> Ezek ugyanannak a gáti Ráfának a fiai voltak, és Dávidnak vagy szolgáinak keze által vesztek el."
    },
    "21": {
      "1": "<span>1</span> A Sátán Izráel ellen támadt, és felindította Dávidot, hogy megszámlálja Izráelt.",
      "2": "<span>2</span> Azt mondta azért Dávid Jóábnak és a hadinép elöljáróinak: Menjetek el, számláljátok meg Izráelt Beérsebától Dánig, és hozzátok hozzám az eredményt, hadd tudjam meg a számukat.",
      "3": "<span>3</span> Akkor azt mondta Jóáb: Szaporítsa meg népét az ÚR százannyira, mint amennyi most. Hát nem mindannyian a te szolgáid-e azok, uram, királyom? Miért kutatja ezt az én uram? Miért bűnhődjék hát ezért Izráel?",
      "4": "<span>4</span> De a király szava erősebb volt Jóábénál. Elment azért Jóáb, és bejárta egész Izráelt, azután visszatért Jeruzsálembe.",
      "5": "<span>5</span> Azután jelentette Dávidnak a nép számát. Egész Izráelben ezerszer ezer és százezer fegyverforgató férfi volt. Júda fiai közül pedig négyszázhetvenezer fegyverforgató férfi.",
      "6": "<span>6</span> Lévi és Benjámin fiait azonban nem számlálta közéjük, mert sehogy sem tetszett Jóábnak a király parancsa.",
      "7": "<span>7</span> De Istennek sem tetszett e dolog, ezért meg is verte Izráelt.",
      "8": "<span>8</span> Dávid akkor azt mondta Istennek: Igen vétkeztem azzal, hogy ezt műveltem. Most azért bocsásd meg szolgád vétkét, mert fölöttébb esztelenül cselekedtem!",
      "9": "<span>9</span> Akkor így szólt az ÚR Gádhoz, Dávid látnokához:",
      "10": "<span>10</span> „Menj el, és mondd meg Dávidnak: Ezt mondja az ÚR: Három dolgot tárok eléd, válassz magadnak közülük egyet, hogy azt cselekedjem veled.”",
      "11": "<span>11</span> Elment tehát Gád próféta Dávidhoz, és azt mondta neki: „Így szól az Úr: Válassz magadnak!",
      "12": "<span>12</span> Három esztendeig tartó éhínség legyen, vagy három hónapig dúljanak szorongatóid, és érjen utol ellenséged fegyvere, vagy az ÚR fegyvere, a döghalál legyen három napig földeden, és az ÚR angyala pusztítson Izráel egész határában? Azért fontold meg, mit feleljek annak, aki engem hozzád küldött.”",
      "13": "<span>13</span> Dávid ezt felelte Gádnak: Nagy az én szorongattatásom! Hadd essem inkább az ÚR kezébe, mert igen nagy az ő irgalmassága, és ne essem emberek kezébe!",
      "14": "<span>14</span> Az ÚR tehát dögvészt bocsátott Izráelre, és meghaltak Izráelből hetvenezren.",
      "15": "<span>15</span> Ezenkívül az ÚR egy angyalt is bocsátott Jeruzsálemre, hogy pusztítsa el. De mikor ölni kezdte volna a népet, odatekintett az ÚR, megkönyörült veszedelmükön, és azt mondta a pusztító angyalnak: Elég immár, vond vissza a kezed! Az ÚR angyala ekkor a jebúszi Ornán szérűjénél állt.",
      "16": "<span>16</span> Akkor Dávid fölemelte szemét, és látta, hogy az ÚR angyala ott áll a föld és az ég között, kivont karddal a kezében, amelyet Jeruzsálem ellen emelt föl. Ekkor Dávid és a vének is zsákruhába öltöztek, és arcra borultak.",
      "17": "<span>17</span> Dávid pedig ezt mondta Istennek: Hát nem én számláltattam-e meg a népet? Én vagyok, aki vétkeztem, és igen gonoszul cselekedtem! De ez a nyáj mit tett? Ó, én URam, Istenem, forduljon ellenem a kezed és az én házam népe ellen, de ne sújtsa a népedet ez a csapás.",
      "18": "<span>18</span> Akkor az ÚR angyala szólt Gádnak, hogy mondja meg Dávidnak, menjen föl, és építsen oltárt az ÚRnak a jebúszi Ornán szérűjén.",
      "19": "<span>19</span> Fölment tehát Dávid Gád beszéde szerint, amelyet az ÚR nevében mondott.",
      "20": "<span>20</span> Amikor Ornán hátratekintett, és meglátta az angyalt, négy fiával együtt elrejtőzött. Ornán ugyanis éppen búzát csépelt.",
      "21": "<span>21</span> Amikor Dávid Ornánhoz érkezett, Ornán föltekintett, meglátta Dávidot, kiment a szérűről, és arccal a földre borult Dávid előtt.",
      "22": "<span>22</span> Dávid így szólt Ornánhoz: Add nekem ezt a szérűt, hogy oltárt építsek rajta az ÚRnak. Valós árán add nekem, hogy megszűnjék ez a csapás a népen!",
      "23": "<span>23</span> Ornán azt felelte Dávidnak: Legyen a tied, és az én uram, királyom azt cselekedje vele, ami neki tetszik! Sőt az ökröket is odaadom égőáldozatul, és a cséplőszerszámokat tűzifának, a gabonát pedig ételáldozatul. Mindezt ajándékul adom.",
      "24": "<span>24</span> Dávid király azt mondta Ornánnak: Nem úgy, hanem teljes áron akarom megvenni tőled. Mert nem veszem el azt, ami a tied, az ÚR számára, és nem akarok neki a máséból égőáldozatot áldozni.",
      "25": "<span>25</span> Így Dávid hatszáz aranysékelt adott Ornánnak a szérűért.",
      "26": "<span>26</span> És Dávid oltárt épített az ÚRnak. Égő- és békeáldozatot mutatott be, és segítségül hívta az URat, aki meghallgatta őt, és a mennyből tüzet bocsátott az égőáldozat oltárára.",
      "27": "<span>27</span> Az ÚR parancsolt az angyalnak, és az visszatette kardját a hüvelyébe.",
      "28": "<span>28</span> Abban az időben ott áldozott Dávid, mivel látta, hogy az ÚR meghallgatta őt a jebúszi Ornán szérűjén.",
      "29": "<span>29</span> Az ÚR hajléka ugyanis, amelyet Mózes készített a pusztában, és az égőáldozat oltára is akkor Gibeón magaslatán volt.",
      "30": "<span>30</span> Dávid azonban nem mert fölmenni oda, az Isten színe elé, hogy tőle tudakozódjék, mert igen megrettent az ÚR angyalának kardjától."
    },
    "22": {
      "1": "<span>1</span> Azután ezt mondta Dávid: Ez az ÚR Isten háza és az égőáldozat oltára Izráel számára.",
      "2": "<span>2</span> És Dávid megparancsolta, hogy gyűjtsék össze az Izráel földjén levő jövevényeket, akiket kőfejtésre rendelt, hogy faragni való köveket vágjanak Isten házának építéséhez.",
      "3": "<span>3</span> Továbbá sok vasat is szerzett Dávid az ajtókhoz való szögekhez és a sarokvasakhoz, és rezet is bőségesen, minden mérték nélkül.",
      "4": "<span>4</span> Továbbá számtalan cédrusfát is, mert a szidóniak és a tírusziak bőségesen szállították azt neki.",
      "5": "<span>5</span> Dávid azt mondta: A fiam, Salamon még gyermek és tapasztalatlan, az ÚRnak pedig nagy házat kell építeni, hogy hírneve és ékessége mindent fölülmúljon az egész világon. Ezért én mindent előkészítek neki. Dávid azért mindent meg is szerzett nagy bőségesen, mielőtt meghalt.",
      "6": "<span>6</span> És hívatta a fiát, Salamont, és meghagyta neki, hogy az ÚRnak, Izráel Istenének házat építsen.",
      "7": "<span>7</span> Dávid azt mondta Salamonnak: Édes fiam, elgondoltam a szívemben, hogy az én Istenem, az ÚR nevének házat építek.",
      "8": "<span>8</span> De az ÚR így szólt hozzám: Sok vért ontottál, és sokat hadakoztál. Nem építhetsz házat az én nevemnek, mert sok vért ontottál ki a földre énelőttem.",
      "9": "<span>9</span> De íme, fiad fog születni, aki a nyugalom embere lesz, mert nyugalmat adok neki minden körülötte levő ellenségétől. Azért nevezik Salamonnak, mert békességet és nyugalmat adok Izráelnek az ő idejében.",
      "10": "<span>10</span> Ő épít nevemnek házat, fiam lesz, és én atyja leszek neki, és megerősítem királyságának trónját Izráelben mindörökké.",
      "11": "<span>11</span> Most hát, édes fiam, legyen veled az ÚR, hogy sikerüljön házat építened az ÚRnak, a te Istenednek, ahogy szólt felőled.",
      "12": "<span>12</span> De adjon neked az ÚR értelmet és bölcsességet, amikor Izráel fölé rendel, hogy az ÚRnak, a te Istenednek törvényét megőrizzed.",
      "13": "<span>13</span> Akkor jól lesz dolgod, ha megtartod és teljesíted azokat a rendeléseket és a végzéseket, amelyeket az ÚR Mózes által parancsolt Izráelnek. Légy bátor, légy erős, ne félj, és ne rettegj!",
      "14": "<span>14</span> Íme, én is az én szegénységemben szereztem az ÚR házának építésére százezer talentum aranyat és ezerszer ezer talentum ezüstöt, a rezet és a vasat pedig meg sem lehet mérni, mert igen bőven van; és fát és követ is szereztem. Te is szerezz még ezekhez.",
      "15": "<span>15</span> Van sok kézművesed, kővágód, kő- és fafaragód és mindenféle dologban jártas bölcs mesterembered.",
      "16": "<span>16</span> Az aranynak, ezüstnek, vasnak és a réznek se szeri, se száma: azért kelj föl, láss hozzá, és az ÚR legyen veled!",
      "17": "<span>17</span> Dávid pedig megparancsolta Izráel összes főemberének, hogy ők is legyenek segítségére fiának, Salamonnak, és azt mondta:",
      "18": "<span>18</span> Hát nincsen-e veletek az ÚR, a ti Istenetek, aki nyugalmat adott nektek körös-körül? Mert az én kezembe adta e föld lakosait, és meghódolt e föld az ÚR előtt és az ő népe előtt.",
      "19": "<span>19</span> Most azért keressétek az URat, a ti Isteneteket teljes szívetek és lelketek szerint, keljetek föl, és készítsétek el az ÚR Isten szentélyét, hogy bevihessétek az ÚR szövetségének ládáját és az Istennek szentelt edényeket abba a házba, amely az ÚR nevének épül."
    },
    "23": {
      "1": "<span>1</span> Amikor Dávid megöregedett, és betelt az élettel, fiát, Salamont tette Izráel királyává.",
      "2": "<span>2</span> Összegyűjtötte Izráel összes fejedelmét meg a papokat és a lévitákat is.",
      "3": "<span>3</span> Megszámlálták a lévitákat harmincesztendőstől fogva fölfelé, és a férfiak száma harmincnyolcezer fő volt.",
      "4": "<span>4</span> Ezek közül huszonnégyezren az ÚR háza körüli teendőkre viseljenek gondot, és hatezren tiszttartók és bírák legyenek.",
      "5": "<span>5</span> Négyezren kapuőrök, négyezren pedig dicsérjék az URat mindenféle hangszerrel, amelyeket az ÚR dicséretére készíttettem.",
      "6": "<span>6</span> Azután Dávid Lévi fiai szerint csoportokba osztotta őket: gérsóniakra, kehátiakra és meráriakra.",
      "7": "<span>7</span> A gérsóniak közt volt Ladán és Simei.",
      "8": "<span>8</span> Ladán három fia: Jehiél az első, aztán Zétám és Jóél.",
      "9": "<span>9</span> Simeinek is három fia volt: Selómit, Haziél és Hárán. Ők voltak Ladán családfői.",
      "10": "<span>10</span> Simei fiai voltak: Jahat, Zízá, Jeús és Beríá, ezek négyen voltak Simei fiai.",
      "11": "<span>11</span> Jahat volt az első és Zízá a második. De mivel Jeúsnak és Beríának nem sok fia volt, családjuk csak egy ágnak számított.",
      "12": "<span>12</span> Kehát fiai négyen voltak: Amrám, Jichár, Hebrón és Uzzíél.",
      "13": "<span>13</span> Amrám fia volt Áron és Mózes. És Áront kiválasztották, hogy fölszenteljék a szentek szentje számára őt és fiait mindörökké, hogy jó illattal áldozzanak az ÚR színe előtt, és szolgáljanak neki, s az ő nevében áldják meg a népet mindenkor.",
      "14": "<span>14</span> Mózesnek, Isten emberének fiait pedig Lévi nemzetségei közé számították.",
      "15": "<span>15</span> Mózes fia volt Gérsóm és Eliezer.",
      "16": "<span>16</span> Gérsóm fiai közül Sebuél volt az első.",
      "17": "<span>17</span> Eliezer fia volt Rehabjá, a családfő. Eliezernek nem volt több fia, de Rehabjá fiai igen megsokasodtak.",
      "18": "<span>18</span> Jichár fiai között Selómit volt a családfő.",
      "19": "<span>19</span> Hebrón fiai között Jerijjáhú volt az első, Amárja a második, Jahaziél a harmadik és Jekamám a negyedik.",
      "20": "<span>20</span> Uzzíél fiai között Míká volt az első, és Jissijjá a második.",
      "21": "<span>21</span> Merári fia volt Mahli és Músi. Mahli fiai Eleázár és Kís.",
      "22": "<span>22</span> Eleázár úgy halt meg, hogy nem voltak fiai, hanem leányai, akiket a saját atyjukfiai, Kís fiai vettek el.",
      "23": "<span>23</span> Músi fiai hárman voltak: Mahli, Éder és Jerémót.",
      "24": "<span>24</span> Ezek voltak Lévi fiai családjaik szerint, a családfők nevük összeírása szerint, fejenként, akik az ÚR házában szolgáltak húszéves kortól fölfelé.",
      "25": "<span>25</span> Mert ezt mondta Dávid: Nyugalmat adott népének az ÚR, Izráel Istene, és Jeruzsálemben lakozik mindörökké.",
      "26": "<span>26</span> Azért a lévitáknak sem kell többé hordozniuk Isten hajlékát és mindazt a fölszerelést, amely az ő szolgálatához való.",
      "27": "<span>27</span> Azért Dávid utolsó rendelete szerint megszámlálták Lévi fiait húszéves kortól fölfelé.",
      "28": "<span>28</span> Áron fiainak szolgálatában tevékenykedtek az ÚR házában és annak udvaraiban, kamráiban és mindenféle szent edény tisztán tartásában és az Isten házának szolgálatában.",
      "29": "<span>29</span> Hogy gondot viseljenek a sorba rakott kenyerekre, az ételáldozathoz való finomlisztre, a kovásztalan lepényekre, a serpenyőkre, a főtt étekre, minden űr- és hosszmértékre,",
      "30": "<span>30</span> és hogy álljanak készen az ÚRnak való hálaadásra és dicséretére mind reggel, mind este.",
      "31": "<span>31</span> Hogy mikor az ÚRnak égőáldozatot mutatnak be minden szombaton, a hónapok első napjain és a szokott ünnepeken, meghatározott szám szerint álljanak szüntelen az ÚR előtt.",
      "32": "<span>32</span> És hogy gondoskodjanak a gyülekezet sátra körüli szolgálatról, a szentély körüli szolgálatról és a teendőkről atyjukfiainak, Áron fiainak a szolgálatánál az ÚR házában."
    },
    "24": {
      "1": "<span>1</span> Áron fiainak rendje. Áron fiai: Nádáb, Abíhú, Eleázár és Ítámár.",
      "2": "<span>2</span> Nádáb és Abíhú még apjuk előtt meghaltak, és mivel nem voltak fiaik, ezért Eleázár és Ítámár viselte a papságot.",
      "3": "<span>3</span> Dávid beosztotta Eleázár fiai közül Cádókot, Ítámár fiai közül pedig Ahimeleket tisztük szerinti szolgálatra.",
      "4": "<span>4</span> Mivel Eleázár fiai között több főember volt található, mint Ítámár fiai között, Eleázár fiai közül tizenhat családfőt osztottak be, Ítámár fiai közül pedig nyolcat.",
      "5": "<span>5</span> Sorsolással osztották be őket, válogatás nélkül ezeket is, meg azokat is. Így lettek a szent hely elöljárói és Isten fejedelmei az Eleázár és az Ítámár fiai közül valók egyaránt.",
      "6": "<span>6</span> Semajá, Netanél fia, a Lévi nemzetségéből való kancellár írta fel őket a király és a fejedelmek előtt, Cádók pap és Ahimelek, Ebjátár fia előtt, továbbá a papok és a léviták családfői előtt. Egy családot sorsoltak ki Eleázárnak, majd még egyet, és egyet Ítámárnak.",
      "7": "<span>7</span> Az első sorshúzás Jehójáribra esett, a második Jedajára,",
      "8": "<span>8</span> a harmadik Hárimra, a negyedik Szeórimra,",
      "9": "<span>9</span> az ötödik Malkijjára, a hatodik Mijjáminra,",
      "10": "<span>10</span> a hetedik Hakkócra, a nyolcadik Abijjára,",
      "11": "<span>11</span> a kilencedik Jésúára, a tizedik Sekanjáhúra,",
      "12": "<span>12</span> a tizenegyedik Eljásibra, a tizenkettedik Jákimra,",
      "13": "<span>13</span> a tizenharmadik Huppára, a tizennegyedik Jesebábra,",
      "14": "<span>14</span> a tizenötödik Bilgára, a tizenhatodik Immérre,",
      "15": "<span>15</span> a tizenhetedik Hézírre, a tizennyolcadik Happiccécre,",
      "16": "<span>16</span> a tizenkilencedik Petahjára, a huszadik Jehezkélre,",
      "17": "<span>17</span> a huszonegyedik Jákínra, a huszonkettedik Gámúlra,",
      "18": "<span>18</span> a huszonharmadik Delájáhúra, a huszonnegyedik Maazjáhúra.",
      "19": "<span>19</span> Ezek tiszte volt szolgálatukban, hogy bejárjanak az ÚR házába sorban atyjuk, Áron rendelése szerint, amint az ÚR, Izráel Istene megparancsolta neki.",
      "20": "<span>20</span> Lévi többi fiai ezek voltak: Amrám fia volt Súbáél, Súbáél fia Jehdejáhú.",
      "21": "<span>21</span> Aki Rehabjáhútól származott: Jissijjá, Rehabjáhú első fia.",
      "22": "<span>22</span> Jicháritól származott Selómót, Selómót fia volt Jahat.",
      "23": "<span>23</span> Hebrón fiai közül Jerijjáhú volt az első, Amarjáhú a második, Jahaziél a harmadik, Jekamám a negyedik.",
      "24": "<span>24</span> Uzzíél fia volt Míká, Míká fia Sámír.",
      "25": "<span>25</span> Míká testvére Jissijjá volt, Jissijjá fia pedig Zekarjáhú.",
      "26": "<span>26</span> Merári fia volt Mahli és Músi. Jaazijjáhú fia Benó volt.",
      "27": "<span>27</span> Merári fiai voltak: Jaazijjáhútól, fiától származott Sóham, Zakkúr és Ibri.",
      "28": "<span>28</span> Mahlitól volt Eleázár, neki nem voltak fiai.",
      "29": "<span>29</span> Kístől származott Jerahmeél, Kís fia.",
      "30": "<span>30</span> Músi fiai voltak: Mahli, Éder és Jerimót. Ezek a léviták családjaik szerint.",
      "31": "<span>31</span> Ezekre is ugyanúgy sorsot vetettek, mint testvéreikre, Áron fiaira Dávid király, Cádók és Ahimelek, továbbá a papok és a léviták családfői előtt a családfőkre ugyanúgy, mint legkisebb testvérükre."
    },
    "25": {
      "1": "<span>1</span> Dávid és a sereg fővezérei a szolgálatra különválasztották Ászáf, Hémán és Jedútún fiai közül azokat, akik prófétai lélekkel játszottak citerákon, lantokon és cintányérokon. Azok sora, akiket erre a szolgálatra rendeltek, ez volt:",
      "2": "<span>2</span> Ászáf fiai közül: Zakkúr, Jószéf, Netanjá és Aszarélá, Ászáf fiai. Őket Ászáf vezette prófétai lélekkel, aki a király felügyelete alá tartozott.",
      "3": "<span>3</span> A jedútúniak közül: Jedútún hat fia, Gedaljáhú, Ceri, Jesajáhú, Hasabjáhú, Mattitjáhú és Simei, akik apjuk, Jedútún mellett prófétai lélekkel játszottak citerán az ÚR tiszteletére és dicséretére.",
      "4": "<span>4</span> Hémán fiai közül: Hémán fiai: Bukkijjáhú, Mattanjáhú, Uzzíél, Sebuél, Jerimót, Hananjá, Hanáni, Eliátá, Giddalti, Rómamti-Ezer, Josbekásá, Mallóti, Hótir, Mahaziót.",
      "5": "<span>5</span> Ezek mind Hémán fiai, aki a király látnoka volt, hogy Isten beszédeivel a hatalmát fölemelje. Isten Hémánnak tizennégy fiút és három leányt adott.",
      "6": "<span>6</span> Ezek mindnyájan apjuk vezetésével az ÚR házában énekeltek cintányérokkal, lantokkal és citerákkal Isten házának a szolgálatában a király, Ászáf, Jedútún és Hémán irányítása szerint.",
      "7": "<span>7</span> Kétszáznyolcvannyolc volt a száma testvéreikkel együtt azoknak, akiket megtanítottak az ÚR énekeire; és mindnyájan képzettek voltak.",
      "8": "<span>8</span> Sorsot vetettek a szolgálat sorrendjére nézve a fiatalokra és az öregekre, tanítókra és a tanítványokra egyaránt.",
      "9": "<span>9</span> A sorsolás először Ászáf fiára, Jószéfre esett, a második Gedaljáhúra, akik testvéreikkel és fiaikkal együtt összesen tizenketten voltak.",
      "10": "<span>10</span> A harmadik Zakkúrra; fiaival és testvéreivel együtt összesen tizenketten voltak.",
      "11": "<span>11</span> A negyedik sorsolás Jicrire esett; fiaival és testvéreivel együtt összesen tizenketten voltak.",
      "12": "<span>12</span> Az ötödik Netanjáhúra; fiaival és testvéreivel együtt összesen tizenketten voltak.",
      "13": "<span>13</span> A hatodik Bukkijjáhúra; fiaival és testvéreivel együtt összesen tizenketten voltak.",
      "14": "<span>14</span> A hetedik Jeszarélára; fiaival és testvéreivel együtt tizenketten voltak.",
      "15": "<span>15</span> A nyolcadik Jesajáhúra; fiaival és testvéreivel együtt összesen tizenketten voltak.",
      "16": "<span>16</span> A kilencedik Mattanjáhúra; fiaival és testvéreivel együtt összesen tizenketten voltak.",
      "17": "<span>17</span> A tizedik Simeire; fiaival és testvéreivel együtt összesen tizenketten voltak.",
      "18": "<span>18</span> A tizenegyedik Azarélra; fiaival és testvéreivel együtt összesen tizenketten voltak.",
      "19": "<span>19</span> A tizenkettedik Hasabjára; fiaival és testvéreivel együtt összesen tizenketten voltak.",
      "20": "<span>20</span> A tizenharmadik Súbáélra; fiaival és testvéreivel együtt összesen tizenketten voltak.",
      "21": "<span>21</span> A tizennegyedik Mattitjáhúra; fiaival és testvéreivel együtt összesen tizenketten voltak.",
      "22": "<span>22</span> A tizenötödik Jerémótra; fiaival és testvéreivel együtt összesen tizenketten voltak.",
      "23": "<span>23</span> A tizenhatodik Hananjáhúra; fiaival és testvéreivel együtt tizenketten voltak.",
      "24": "<span>24</span> A tizenhetedik Josbekására; fiaival és testvéreivel együtt összesen tizenketten voltak.",
      "25": "<span>25</span> A tizennyolcadik Hanánira; fiaival és testvéreivel együtt összesen tizenketten voltak.",
      "26": "<span>26</span> A tizenkilencedik Mallótira; fiaival és testvéreivel együtt összesen tizenketten voltak.",
      "27": "<span>27</span> A huszadik Elijjátára; fiaival és testvéreivel együtt összesen tizenketten voltak.",
      "28": "<span>28</span> A huszonegyedik Hótírra; fiaival és testvéreivel együtt összesen tizenketten voltak.",
      "29": "<span>29</span> A huszenkettedik Giddaltira; fiaival és testvéreivel együtt összesen tizenketten voltak.",
      "30": "<span>30</span> A huszonharmadik Mahaziótra; fiaival és testvéreivel együtt tizenketten voltak.",
      "31": "<span>31</span> A huszonnegyedik Rómamti-Ezerre; fiaival és testvéreivel együtt tizenketten voltak."
    },
    "26": {
      "1": "<span>1</span> A kapuőrök rendje ez: a kórahiak közül Meselemjáhú, Kóré fia, aki Ászáf fiai közül való volt.",
      "2": "<span>2</span> Meselemjáhú fiai közül Zekarjáhú az elsőszülött volt, Jedíaél a második, Zebadjáhú a harmadik és Jatniél a negyedik.",
      "3": "<span>3</span> Élám az ötödik, Jehóhánán a hatodik, Eljehóénaj a hetedik.",
      "4": "<span>4</span> Óbéd-Edóm fiai közül: Semajá volt az elsőszülött, Jehózábád a második, Jóáh a harmadik, Szákár a negyedik, Netanél az ötödik,",
      "5": "<span>5</span> Ammiél a hatodik, Jisszákár a hetedik, Peulletaj a nyolcadik; mert Isten megáldotta őt.",
      "6": "<span>6</span> Fiának, Semajának is születtek fiai, akik családjuk urai voltak, lévén erős vitézek.",
      "7": "<span>7</span> Semajá fiai: Otni, Refáél, Óbéd, Elzábád, akinek a testvérei, Elihú és Szemakjáhú szintén igen erős férfiak voltak.",
      "8": "<span>8</span> Ezek mindnyájan Óbéd-Edóm fiai közül valók. Mind ő maguk, mind fiaik és testvéreik derék férfiak voltak, alkalmasak a szolgálatra. Hatvanketten származtak Óbéd-Edómtól.",
      "9": "<span>9</span> Meselemjáhú fiai és testvérei is mind a tizennyolcan erős férfiak voltak.",
      "10": "<span>10</span> A Mérári fiai közül való Hószának is voltak fiai. Simri volt közülük az első, bár nem ő volt az elsőszülött, de az apja őt tette meg elsőnek.",
      "11": "<span>11</span> Hilkijjáhú a második, Tebaljáhú a harmadik, Zekarjáhú a negyedik. Hószá fiai és testvérei összesen tizenhárman voltak.",
      "12": "<span>12</span> Ezek között osztották el a kapuőrzés tisztségét a főemberek szerint, hogy az ő atyjukfiaival együtt vigyázzanak és szolgáljanak az ÚR házában.",
      "13": "<span>13</span> Kicsinyre és nagyra egyaránt sorsot vetettek családjaik szerint, mindegyik kapuhoz.",
      "14": "<span>14</span> A keleti kapu őrzése sors szerint Selemjáhúra esett. Zekarjáhúra pedig, a fiára, aki bölcs tanácsadó volt, az északi kapu esett sors szerint.",
      "15": "<span>15</span> Óbéd-Edómnak a déli kapu jutott, fiainak pedig a tárház.",
      "16": "<span>16</span> Suppimnak és Hószának a nyugati kapu a Salleket-kapuval együtt a felmenő töltésen, egyik őrhely a másikkal szemben.",
      "17": "<span>17</span> Keleten hat lévita volt, északon naponként négy, délen is naponként négy, a tárháznál pedig kettő-kettő.",
      "18": "<span>18</span> A külső részen nyugaton négy volt a felmenő töltésen, kettő a csarnok mellett.",
      "19": "<span>19</span> Ez volt a Kórah fiai és Mérári fiai közül való kapuőrök beosztása.",
      "20": "<span>20</span> A léviták közül Ahijjá volt az Isten háza kincstárának és az Istennek szentelt kincsnek a felügyelője.",
      "21": "<span>21</span> Ladán fiai, a gérsóni Ladán fiai, a gérsóni Ladán családfői voltak: Jehiéli",
      "22": "<span>22</span> meg Jehiéli fiai: Zétám és testvére, Jóél, akik az ÚR háza kincstárának felügyelői voltak.",
      "23": "<span>23</span> Az Amrám, Jichár, Hebrón és Uzzíél nemzetségéből valók is.",
      "24": "<span>24</span> Sebuél pedig, Gérsómnak, Mózes fiának a leszármazottja volt a kincstárnokok felügyelőinek feje.",
      "25": "<span>25</span> Atyjafiai pedig, akik Eliezertől származtak, ezek: fia, Rehabjáhú, az ő fia Jesajáhú, az ő fia Jórám, az ő fia Zikri, az ő fia Selómit.",
      "26": "<span>26</span> Ez a Selómit és az ő testvérei voltak a felügyelői minden megszentelt kincsnek, amelyet Dávid király, a családfők, az ezredesek, a századosok és a hadsereg vezérei odaszenteltek Istennek;",
      "27": "<span>27</span> a hadizsákmányt szentelték oda az ÚR házának fenntartására.",
      "28": "<span>28</span> Amit csak odaszentelt Sámuel próféta, Saul, Kís fia, Abnér, Nér fia és Jóáb, Cérújá fia; mindaz, amit bárki odaszentelt Istennek, Selómitnak és testvéreinek a gondjára volt bízva.",
      "29": "<span>29</span> A jicháriak közül Kenanjáhút és fiait mint tiszttartókat és bírákat Izráel külső dolgaival bízták meg.",
      "30": "<span>30</span> A hebróniak közül Hasabjáhúra és testvéreire, erre az ezerhétszáz erős férfira bízták Izráel népe között, a Jordán vizén túl, nyugat felé, hogy gondoskodjanak az ÚR minden ügyéről és a király szolgálatáról.",
      "31": "<span>31</span> A hebróniak közül Jerijjá volt az első. A hebróniak nemzetségeit és családjait megkeresték Dávid királyságának negyvenedik esztendejében, és bátor férfiakat találtak közöttük Jazér-Gileádban.",
      "32": "<span>32</span> Atyjafiai erős férfiak voltak, kétezer-hétszáz családfő, akiket Dávid király felügyelővé tett a rúbenieken, a gádiakon és Manassé törzsének egyik felén az Istent és a királyt illető minden dologban."
    },
    "27": {
      "1": "<span>1</span> Izráel fiai szám szerint, a családfők, ezredesek, századosok és azok elöljárói, akik a király szolgálatában álltak a seregek minden dolgában, és hónapról hónapra felváltva az egész esztendőn át fegyverben álltak, a csapatok egyenként huszonnégyezer emberből álltak.",
      "2": "<span>2</span> Az első csapat vezére az első hónapban Jásobám, Jabdiél fia volt. Csapata huszonnégyezer főből állt.",
      "3": "<span>3</span> Ő Pérec fiai közül származott, és az első hónapra rendelt sereg minden elöljárójának ő volt a feje.",
      "4": "<span>4</span> A második csapat vezére a második hónapban az ahóhi Dódaj volt csapatával. Egyik csapatának Miklót volt a vezetője. Az ő csapata is huszonnégyezer főből állt.",
      "5": "<span>5</span> A harmadik seregnek a harmadik hónapban Benájá volt a vezére, aki Jójádá főpap fia volt. Az ő csapata is huszonnégyezer főből állt.",
      "6": "<span>6</span> Ez a Benájá a harminc vitéz közé tartozott, sőt a harmincnak a feje volt. Csapatának elöljárója fia, Ammizábád volt.",
      "7": "<span>7</span> A negyedik csapat vezére a negyedik hónapban Aszáél, Jóáb testvére volt, helyettese pedig fia, Zebadjá. Az ő csapata is huszonnégyezer főből állt.",
      "8": "<span>8</span> Az ötödik vezére az ötödik hónapban a jizráhi Samhút volt. Az ő csapata is huszonnégyezer főből állt.",
      "9": "<span>9</span> A hatodik vezére a hatodik hónapban a tekóai Írá, Ikkés fia volt. Az ő csapata is huszonnégyezer főből állt.",
      "10": "<span>10</span> A hetedik vezére a hetedik hónapban a pelóni Hélec volt, Efraim fiai közül. Az ő csapata is huszonnégyezer főből állt.",
      "11": "<span>11</span> A nyolcadik vezére a nyolcadik hónapban a húsái Szibbekaj volt, a zerahiak közül. Az ő csapata is huszonnégyezer főből állt.",
      "12": "<span>12</span> A kilencedik vezére a kilencedik hónapban az anátóti Abiezer volt, a benjáminiak közül. Az ő csapata is huszonnégyezer főből állt.",
      "13": "<span>13</span> A tizedik vezére a tizedik hónapban a netófái Mahraj volt, a zerahiak közül. Az ő csapata is huszonnégyezer főből állt.",
      "14": "<span>14</span> A tizenegyedik vezére a tizenegyedik hónapban a pirátóni Benájá volt, Efraim fiai közül. Az ő csapata is huszonnégyezer főből állt.",
      "15": "<span>15</span> A tizenkettedik vezére a tizenkettedik hónapban a netófái Heldaj volt, Otniél nemzetségéből. Az ő csapata is huszonnégyezer főből állt.",
      "16": "<span>16</span> Izráel törzseinek az élén a következők álltak: a rúbeniek fejedelme Eliezer volt, Zikri fia. A simeoniaké Sefatjáhú, Maaká fia.",
      "17": "<span>17</span> A lévitáké Hasabjá, Kemúél fia, az ároniaké Cádók;",
      "18": "<span>18</span> Júdáé Elihú, Dávid testvérei közül. Issakáré Omri, Míkáél fia.",
      "19": "<span>19</span> Zebuloné Jismajáhú, Óbadjáhú fia. Naftálié Jerimót, Azriél fia.",
      "20": "<span>20</span> Efraim fiaié Hóséa, Azazjáhú fia. Manassé törzsének az egyik feléé Jóél, Pedájáhú fia.",
      "21": "<span>21</span> Manassé törzse Gileád földjén lakó felének Jiddó, Zekarjáhú fia. Benjáminé Jaasziél, Abnér fia.",
      "22": "<span>22</span> Dáné Azarél, Jeróhám fia. Ezek voltak Izráel törzseinek fejedelmei.",
      "23": "<span>23</span> Nem számláltatta meg Dávid azokat, akik húsz esztendőn aluliak voltak, mert az ÚR megígérte, hogy úgy megsokasítja Izráelt, mint az ég csillagait.",
      "24": "<span>24</span> Jóáb, Cerújá fia elkezdte ugyan a számlálást, de nem fejezte be, mivel emiatt támadt Isten haragra Izráel ellen. Ezért nem került be ez a szám Dávid király krónikájának számadatai közé.",
      "25": "<span>25</span> A királyi kincstár gondnoka Azmávet, Adíél fia volt. A mezőkön, városokban, falvakban és várakban lévő raktárainak felügyelője pedig Jehónátán, Uzzijjáhú fia volt.",
      "26": "<span>26</span> A föld művelésére rendelt mezei munkások felügyelője Ezri, Kelúb fia volt;",
      "27": "<span>27</span> a szőlőművesek fölött a rámai Simei, a szőlőkben levő borospincék fölött a sefemi Zabdi volt a felügyelő.",
      "28": "<span>28</span> A Sefélá-alföld olajfáinak és a mezőn lévő fügefák gondozói fölött a gádéri Baalhánán volt, az olajraktárak fölött pedig Jóás.",
      "29": "<span>29</span> A Sárón mezején legelő szarvasmarhákra a sáróni Sitraj, a völgyekben legelő jószágra pedig Sáfát, Adlaj fia ügyelt.",
      "30": "<span>30</span> A tevék fölött a jismáéli Óbíl, a szamarak fölött a mérónóti Jehdejáhú,",
      "31": "<span>31</span> a juhok fölött a hagri Jázíz volt a felügyelő. Mindezek Dávid király jószágainak gondnokai voltak.",
      "32": "<span>32</span> Jónátán, Dávid nagybátyja tanácsos volt, értelmes és írástudó ember. Jehiél, Hakmóni fia pedig a király fiaival volt.",
      "33": "<span>33</span> Ahitófel is a király tanácsosa volt, az arki Húsaj pedig a király barátja.",
      "34": "<span>34</span> Ahitófel helyettese Jehójádá, Benájáhú fia és Ebjátár volt. A királyi hadsereg fővezére pedig Jóáb volt."
    },
    "28": {
      "1": "<span>1</span> Dávid összegyűjtötte Jeruzsálembe Izráel összes vezetőjét, a törzsek fejedelmeit, a király szolgálatában levő csapatok vezéreit, az ezredeseket, a századosokat, a király és fiai minden jószágának és vagyonának gondnokait udvari szolgáikkal és a hősöket a sereg minden vitézével együtt.",
      "2": "<span>2</span> Ekkor fölállt Dávid király, és így szólt: Testvéreim és népem, hallgassatok meg! Elhatároztam magamban, hogy az ÚR szövetségládájának állandó hajlékot és Istenünk lába zsámolyának házat készíttetek. Elő is készítettem mindent az építéshez.",
      "3": "<span>3</span> De Isten így szólt hozzám: Nem építhetsz házat nevemnek, mert hadakozó ember vagy, már sok vért ontottál.",
      "4": "<span>4</span> Mégis engem választott ki az ÚR, Izráel Istene atyám egész háza népéből, hogy Izráel népének királya legyek mindvégig. Mert Júdát választotta a fejedelemségre és Júda házában pedig az én atyám háza népét. Atyámnak fiai közül pedig bennem telt kedve, hogy királlyá tegyen egész Izráel fölött.",
      "5": "<span>5</span> Valamennyi fiam közül (mert az ÚR sok fiút adott nekem) Salamon fiamat választotta ki, hogy az ÚR királyságának trónján üljön Izráel fölött.",
      "6": "<span>6</span> És ezt mondta nekem: Fiad, Salamon építi föl házamat és udvaraimat, mert én őt fiamul választottam, én pedig atyja leszek neki.",
      "7": "<span>7</span> Megerősítem királyságát mindörökké, ha parancsaimat és végzéseimet szorgalmasan megtartja úgy, mint ahogyan a mai napig tette.",
      "8": "<span>8</span> Most azért az egész Izráelnek, az ÚR gyülekezetének szeme láttára és Istenünk füle hallatára mondom: őrizzétek meg, sőt keressétek az ÚRnak, a ti Isteneteknek minden parancsolatát, hogy birtokolhassátok ezt a jó földet és hogy örökségül hagyhassátok fiaitokra is magatok után mindörökké.",
      "9": "<span>9</span> Te azért, fiam, Salamon, ismerd meg atyád Istenét, és szolgálj neki teljes szívvel és jókedvvel. Mert megvizsgál az ÚR minden szívet, és jól ért minden emberi gondolatot. Ha keresni fogod, megtalálod, ha ellenben elhagyod őt, ő is elhagy téged mindörökké.",
      "10": "<span>10</span> Lásd meg azért, hogy az ÚR választott ki téged, hogy szent házat építs neki: légy hát erős, és készítsd el.",
      "11": "<span>11</span> Akkor átadta Dávid a fiának, Salamonnak a csarnoknak és épületeinek, a kincstárnak, a felső szobáknak és a belső szobáknak, az engesztelés helyének tervrajzát",
      "12": "<span>12</span> és mindazét, amit szívében elgondolt az ÚR házának udvarai felől, az Isten házának kincstára és a szent kincsek számára körös-körül levő minden kamra felől;",
      "13": "<span>13</span> továbbá a papok és léviták csoportjainak háza felől, az ÚR háza szolgálatának minden munkájához és az ÚR házának szolgálatához szükséges minden eszköz felől.",
      "14": "<span>14</span> Azután megadta az arany súlyát a szertartások eszközei számára; az összes ezüsteszköz súlyát a szertartások eszközei számára.",
      "15": "<span>15</span> Vagyis az arany mécsestartók és aranymécsesek súlyát, kimérve minden egyes mécsestartónak és mécseseiknek a súlya szerint, s az ezüst mécsestartóknak is kimérve a mécsestartó és mécsesei súlya szerint, mindegyik mécsestartónak a rendeltetése szerint.",
      "16": "<span>16</span> Megadta az arany súlyát, kimérve a szent kenyerek asztalaihoz asztalonként; hasonlóképpen az ezüstét is az ezüstasztalokhoz.",
      "17": "<span>17</span> Továbbá a villákhoz, a hintőedényekhez és a kancsókhoz való színaranynak meg az aranykelyheknek a súlyát: meghatározott mérték szerint adta meg az arany súlyát minden kehelyhez. Ugyanígy az ezüstkelyheknek a súlyát meghatározott mérték szerint minden pohárhoz, kehelyhez.",
      "18": "<span>18</span> A füstölőoltár számára megtisztított arany súlyát, az aranykerúbok szekerének a tervrajzát, akik kiterjesztett szárnyakkal befedik az ÚR szövetségládáját.",
      "19": "<span>19</span> Mindezeket az ÚR keze által írtam meg, ő értette meg velem, hogy milyen legyen ennek az alkotásnak a formája.",
      "20": "<span>20</span> Ezek után Dávid azt mondta fiának, Salamonnak: Légy bátor és erős, kezdj hozzá, semmit ne félj, és ne rettegj, mert az ÚR Isten, az én Istenem veled lesz, nem hagy el téged, nem is távozik el tőled, míg be nem fejezel az ÚR házának szolgálatához szükséges minden munkát!",
      "21": "<span>21</span> Íme, itt vannak a papok és a léviták csapatai Isten házának minden szolgálatára, veled lesznek minden munkában készségesen és bölcsességgel. Ők, a fejedelmek meg az egész nép minden feladatodban segítenek."
    },
    "29": {
      "1": "<span>1</span> Azután ezt mondta Dávid király az egész gyülekezetnek: Ím, látjátok, egyedül Salamon fiamat választotta ki Isten, de ő még gyermek és tapasztalatlan. A munka pedig nagy, mert nem emberé lesz az a ház, hanem az ÚR Istené.",
      "2": "<span>2</span> Én pedig minden erőm szerint szereztem Istenem háza számára bőségesen aranyat az aranyeszközökhöz, ezüstöt az ezüsthöz, rezet a rézhez, vasat a vashoz, fát a fához; ónixköveket, foglalatba való köveket, vörös és tarka színű köveket, mindenféle drágakövet és fehér márványkövet is bőséggel.",
      "3": "<span>3</span> Ezenfölül, mivel nagyon kedvelem Istenem házát, ami kincsem, aranyam és ezüstöm van, azt odaadom Istenem házának amellett, amit szereztem a szent ház számára:",
      "4": "<span>4</span> háromezer talentum aranyat, mégpedig ófíri aranyat és hétezer talentum tiszta ezüstöt a templom falainak beborítására,",
      "5": "<span>5</span> aranyat az aranyeszközökhöz, ezüstöt az ezüsteszközökhöz és mindenhez, ami szükséges a mesteremberek munkájához. És ha valaki önként akar még adni, töltse meg a kezét ma, és adjon, amit akar az ÚRnak!",
      "6": "<span>6</span> Ekkor szabad akaratuk szerint ajándékokat adtak a családfők, Izráel törzseinek fejedelmei, az ezredesek, a századosok és a királyi udvartartás vezetői.",
      "7": "<span>7</span> Isten házának a munkálataira adtak ötezer talentum és tízezer dárik aranyat, tízezer talentum ezüstöt, tizennyolcezer talentum rezet és százezer talentum vasat.",
      "8": "<span>8</span> Akinek drágakövei voltak, az odaadta az ÚR házának kincseihez, a gérsóni Jehiél kezéhez.",
      "9": "<span>9</span> És örvendezett a nép, hogy önként adakozhatnak, mert tiszta szívükből adakoztak az ÚRnak. Dávid király is nagy örömmel örvendezett.",
      "10": "<span>10</span> Akkor Dávid hálát adott az ÚRnak az egész gyülekezet előtt, és ezt mondta: Áldott vagy te, URam, atyánknak, Izráelnek Istene öröktől fogva mindörökké!",
      "11": "<span>11</span> Ó, URam, tied a nagyság, a hatalom, a dicsőség, a győzelem és a méltóság, sőt minden, ami a mennyben és a földön van! Tied, ó, URam, az uralom, magasztos vagy te minden fejedelem fölött!",
      "12": "<span>12</span> A gazdagság és a dicsőség mind tőled van, és te uralkodsz mindezen. A te kezedben van mind az erő, mind a hatalom. A te kezedben van mindennek a felmagasztalása és megerősítése.",
      "13": "<span>13</span> Most azért, ó, Istenünk, hálát adunk neked, és dicsérjük fenséges nevedet.",
      "14": "<span>14</span> Mert mi vagyok én, és mi az én népem, honnan volt erőnk ilyen önkéntes adakozásra, ahogy tettük? Bizony tőled van minden, és azokat adtuk most neked, amiket a te kezedből kaptunk.",
      "15": "<span>15</span> Mert mi csak jövevények és zsellérek vagyunk előtted, mint minden ősünk is. Életünk napjai olyanok e földön, mint az árnyék, melyben nincs állandóság.",
      "16": "<span>16</span> Ó, mi URunk, Istenünk! Mindez a gazdagság, amit gyűjtöttünk, hogy neked és a te szent nevednek házat építsünk, a te kezedből való, és mindazok a tiéid.",
      "17": "<span>17</span> Jól tudom, ó, én Istenem, hogy te a szívet vizsgálod, és az egyenességet szereted. Én mindezeket tiszta szívemből, önként, nagy jókedvvel adtam, s látom, hogy néped is, amely itt jelen van, nagy örömmel, önszántából adta ezeket neked.",
      "18": "<span>18</span> Ó, URam, Ábrahámnak, Izsáknak és Izráelnek, a mi atyáinknak Istene, tartsd meg mindörökké ezt a készséget néped szívében, és szívüket igazgasd tefeléd!",
      "19": "<span>19</span> Salamonnak pedig, a fiamnak, adj tökéletes szívet, hogy parancsaidat, intelmeidet és rendeléseidet megőrizhesse, és mindent aszerint cselekedjen, és fölépíthesse azt az erősséget, amelyet előkészítettem.",
      "20": "<span>20</span> Azután így szólt Dávid az egész gyülekezethez: Áldjátok az URat, a ti Isteneteket! Ekkor az egész gyülekezet áldotta az URat, atyáik Istenét, majd meghajoltak és leborultak az ÚR előtt és a király előtt.",
      "21": "<span>21</span> Másnap pedig áldozatokat mutattak be az ÚRnak. Véres- és egészen elégő áldozatokat áldoztak az egész Izráelért: ezer bikát, ezer kost, ezer bárányt italáldozataikkal és sok más áldozattal együtt.",
      "22": "<span>22</span> Ettek-ittak az ÚR előtt azon a napon nagy vigasságban. Azután Salamont, Dávid fiát másodszor is királlyá tették és felkenték az ÚR fejedelmévé. Cádókot pedig fölkenték a főpapságra.",
      "23": "<span>23</span> És Salamon az ÚR trónjára ült, mint király, apja, Dávid után, és mindenben szerencsés volt, és engedelmeskedett neki egész Izráel.",
      "24": "<span>24</span> A fejedelmek és a vitézek, sőt Dávid király fiai is mindnyájan kezet adtak, hogy Salamon királynak engedelmeskedni fognak.",
      "25": "<span>25</span> És igen felmagasztalta az ÚR Salamont egész Izráel népe előtt, és olyan királyi méltóságot adott neki, amelyhez hasonló egy királynak sem volt előtte Izráelben.",
      "26": "<span>26</span> Így uralkodott Dávid, Isai fia egész Izráel fölött.",
      "27": "<span>27</span> Az az idő pedig, amíg Izráel fölött uralkodott, negyven esztendő volt. Hebrónban hét esztendeig, Jeruzsálemben pedig harminchárom évig uralkodott.",
      "28": "<span>28</span> Jó vénségében halt meg, életével, gazdagságával és minden dicsőségével megelégedve. Utána fia, Salamon lett a király.",
      "29": "<span>29</span> Dávid király dolgai pedig az elsőtől az utolsóig íme, megírattak Sámuelnek, a látónak a könyvében, Nátán prófétának és Gádnak, a látónak a könyvében",
      "30": "<span>30</span> egész uralkodásával, hőstetteivel és mindazzal együtt, ami az ő idejében történt vele, Izráellel és a többi ország királyságaival."
    }
  }
};
module.exports = book;