var book = {
	"name": "Masmur",
	"numChapters": 150,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> Beberekat meh orang ti enda nitihka penemu orang ke jai, lalu enda nitihka teladan orang ke bedosa ti enda begulai enggau orang ke ngeleseka Allah Taala,",
			"2": "<sup>2</sup> tang gaga ati ke Adat TUHAN, lalu belematika Adat nya siang malam.",
			"3": "<sup>3</sup> Orang nya baka kayu ti ditanam ba tebing sungai, ti bebuah ninting musin lalu daun iya nadai kala layu. Iya mujur dalam semua utai ti dikereja iya.",
			"4": "<sup>4</sup> Tang orang ke jai enda baka nya. Iya baka rumput ti rangkai ti dikebuka ribut.",
			"5": "<sup>5</sup> Nya alai orang ke jai deka diukum Allah Taala lebuh sida diakim, lalu orang ke bedosa deka diseraraka ari orang ke lurus;",
			"6": "<sup>6</sup> laban TUHAN deka ngintu orang ke lurus, tang jalai pengidup orang ke jai deka dirusak."
		},
		"2": {
			"1": "<sup>1</sup> Nama kebuah orang bansa bukai bepekat lalu bekira deka ngereja utai ti nadai guna?",
			"2": "<sup>2</sup> Bala raja sida angkat ngelaban, lalu bala pemerintah sida sama bepekat ngelaban TUHAN enggau ngelaban raja ti udah dipilih Iya.",
			"3": "<sup>3</sup> Ku sida, “Aram kitai nganchurka kuasa sida. Aram kitai muai perintah sida.”",
			"4": "<sup>4</sup> TUHAN ti duduk di serega ketawa; Iya ngeleseka sida.",
			"5": "<sup>5</sup> Udah nya Iya deka bejaku ngagai sida enggau pengeringat, sereta ngenakutka sida ngena pengeringat Iya.",
			"6": "<sup>6</sup> “Aku udah nyiri raja Aku, ba bukit Aku ti kudus di Sion,” ku TUHAN bejaku.",
			"7": "<sup>7</sup> Ku raja nya bejaku, “Aku deka madahka adat ti udah diberi TUHAN. Ku TUHAN bejaku ngagai aku: ‘Nuan tu anak Aku. Sehari tu Aku udah nyadi Apai nuan.",
			"8": "<sup>8</sup> Pinta ari Aku, lalu Aku deka ngaga mayuh bengkah bansa nyadika pesaka nuan, lalu semua dunya deka diempu nuan.",
			"9": "<sup>9</sup> Nuan deka mechahka sida ngena tungkat besi, lalu ngeredakka sida baka ke ngeredakka periuk tanah.’ ”",
			"10": "<sup>10</sup> Nya alai diatu, O kita ke raja, kita enda tau enda pintar; Bejaga kita, O kita ke pemerintah dunya.",
			"11": "<sup>11</sup> Betuaika TUHAN enggau ati ti nangi. Getar-getar",
			"12": "<sup>12</sup> nyium kaki Iya, enggai ke Iya ringat lalu kita mati enggau ngenyit; laban Iya jampat ringat."
		},
		"3": {
			"1": "<sup>1</sup> O TUHAN, naka meh pemayuh munsuh aku! Naka meh pemayuh orang ke angkat ngelaban aku!",
			"2": "<sup>2</sup> Sida bejakuka aku. Ku sida, “Allah Taala enggai nulung iya.”",
			"3": "<sup>3</sup> Tang Nuan, O TUHAN, baka terabai ti ngelingi aku, Nuan ngasuh aku menang lalu ngasuh aku berani baru.",
			"4": "<sup>4</sup> Aku ngangauka TUHAN, lalu Iya nyaut aku ari bukit Iya ti kudus.",
			"5": "<sup>5</sup> Aku gali lalu tinduk, lalu aku dani baru, laban TUHAN nyaga aku.",
			"6": "<sup>6</sup> Aku enda takutka beribu-ribu iku munsuh ke ngelingi aku.",
			"7": "<sup>7</sup> Angkat meh Nuan, O TUHAN! Selamatka aku, O Allah Taala aku! Laban Nuan nampar kuyu semua munsuh aku, Nuan matahka ngeli semua orang ke jai.",
			"8": "<sup>8</sup> Pengelepas endang enggi TUHAN. Berekatka bala nembiak Nuan!"
		},
		"4": {
			"1": "<sup>1</sup> O Allah Taala ti penyaga aku, saut aku lebuh aku besampi! Nuan udah nulung aku lebuh aku tusah. Kasihka aku diatu, lalu dingaka sampi aku.",
			"2": "<sup>2</sup> O mensia, ni pengelama kita deka mechat aku? Ni pengelama kita deka mengkang rindu ngena jaku ti nadai reti, lalu begigaka utai ti bula?",
			"3": "<sup>3</sup> Tang kita enda tau enda nemu, TUHAN udah milih orang ke lurus nyadika enggi Iya; TUHAN ninga lebuh aku ngangauka Iya.",
			"4": "<sup>4</sup> Kita tau ringat, tang anang bedosa; Berandau enggau ati kita empu ba peninduk kita, lalu anang engkeretik.",
			"5": "<sup>5</sup> Beri piring ti ngena, lalu panggaika pengarap kita ba TUHAN.",
			"6": "<sup>6</sup> Mayuh orang deka nyebut: “Naka meh pengerindu meda utai ti manah! Peda meh kami enggau penelap Nuan, O TUHAN!”",
			"7": "<sup>7</sup> Nuan udah nyimpan mayuh agi pengaga ke dalam ati aku ari pengaga ti dikembuan sida lebuh sida mayuh gandum enggau wain.",
			"8": "<sup>8</sup> Aku deka gali lalu tinduk dalam pemaik. laban semina Nuan siku aja, O TUHAN, ke ulih ngasuh aku diau likun."
		},
		"5": {
			"1": "<sup>1</sup> Pendingka jaku aku, O TUHAN, lalu dingaka demuh aku.",
			"2": "<sup>2</sup> Dingaka sabak aku, O Allah Taala enggau Raja aku, laban aku besampi ngagai Nuan.",
			"3": "<sup>3</sup> O TUHAN, lebuh pagi hari Nuan mendingka munyi nyawa aku; Lebuh pagi hari aku nyendiaka piring ke Nuan, lalu nengkila.",
			"4": "<sup>4</sup> Laban Nuan ukai Allah Taala ti rinduka penyai; Penyai sigi enda begulai enggau Nuan.",
			"5": "<sup>5</sup> Nuan enggai meda orang ke sumbung; Nuan begedika semua orang ke ngereja penyai.",
			"6": "<sup>6</sup> Nuan ngerusak semua orang ke bejaku bula; TUHAN begedika orang ke mangah, ke nipu orang.",
			"7": "<sup>7</sup> Tang ketegal pengerindu Nuan ti besai aku deka tama ngagai rumah Nuan. Aku deka nyembah Nuan dalam Rumah Sembiang Nuan ti kudus enggau ati ti nangika Nuan.",
			"8": "<sup>8</sup> Iring meh aku O TUHAN ngena pengelurus Nuan laban aku bisi munsuh; Gaga jalai Nuan lurus di mua aku.",
			"9": "<sup>9</sup> Laban jaku sida bula magang; Ati sida semina deka ngerusak. Batang rekung sida nya kubur ti telenga, lalu dilah sida semina ngelangkung aja.",
			"10": "<sup>10</sup> Ukum meh sida, O Allah Taala. Awakka akal sida ngerusak sida empu. Buai sida laban sida udah ngereja mayuh macham penyalah, laban sida udah angkat ngelaban Nuan.",
			"11": "<sup>11</sup> Tang awakka semua orang ke ngiga pengelikun dalam Nuan, gaga ati; lalu awakka sida belagu gaga ati; lalu jaga meh sida, ngambika orang ke rinduka nama Nuan deka gaga ati ke Nuan.",
			"12": "<sup>12</sup> Laban Nuan merekatka orang ke lurus, O TUHAN; Nuan mungkur iya ngena pengerindu baka ke mungkur iya ngena terabai."
		},
		"6": {
			"1": "<sup>1</sup> TUHAN, anang nganu aku lebuh Nuan ringat! Anang ngukum aku lebuh Nuan ringat!",
			"2": "<sup>2</sup> Kasihka aku, O TUHAN, laban aku lelak; O TUHAN, geraika aku, laban aku nyau lembut tulang.",
			"3": "<sup>3</sup> Ati aku balat tusah. Ni pengelama baru Nuan deka nulung aku, O TUHAN?",
			"4": "<sup>4</sup> Nguing meh Nuan ngagai aku, O TUHAN lalu selamatka aku. Lepaska aku ketegal pengerindu Nuan ti meruan belama iya.",
			"5": "<sup>5</sup> Laban orang ke mati nadai agi ingatka Nuan; Di dunya pemati, nadai orang muji Nuan.",
			"6": "<sup>6</sup> Aku nyau lelak bedemuh. Genap malam peninduk aku basah laban ai mata. Panggal aku nyau basah laban ai mata aku.",
			"7": "<sup>7</sup> Mata aku nyau bangkung laban ati aku balat tusah. Mata aku nyau rapa laban semua munsuh aku.",
			"8": "<sup>8</sup> Rari ari aku, semua kita ke ngereja penyai; laban TUHAN udah ninga sabak aku.",
			"9": "<sup>9</sup> TUHAN udah ninga pinta aku; TUHAN nerima sampi aku.",
			"10": "<sup>10</sup> Semua munsuh aku deka malu sereta takut; sida deka nikal lalu dikeleseka enda lama da agi."
		},
		"7": {
			"1": "<sup>1</sup> O TUHAN Allah Taala aku, aku ngenduh ngagai Nuan; Selamat lalu lepaska aku ari semua orang ke begagaika aku,",
			"2": "<sup>2</sup> enggai ke sida nyarikka aku baka singa, narit aku, lalu nadai orang ngelepaska aku.",
			"3": "<sup>3</sup> O TUHAN Allah Taala aku, enti aku udah ngereja tu, enti jari aku udah ngereja penyalah,",
			"4": "<sup>4</sup> enti aku udah malas pangan aku ngena penyai tauka ngenchuri utai ari munsuh aku enggau nadai kebuah,",
			"5": "<sup>5</sup> awakka munsuh aku begagaika aku lalu ulih nitihka aku, lalu awakka iya ngelunjak aku ba tanah, lalu nejuka semengat aku begulai enggau amau.",
			"6": "<sup>6</sup> Angkat meh Nuan O TUHAN, mai pengeringat Nuan! Bediri meh Nuan TUHAN ngelaban pengeringat bala munsuh aku. Dani meh Nuan, O Allah Taala aku; Nuan deka ngakim.",
			"7": "<sup>7</sup> Gempuruka semua bansa ngelingi Nuan, lalu perintah sida ari serega.",
			"8": "<sup>8</sup> Nuan endang ngakim semua mensia. Akim meh aku, O TUHAN, nitihka pengelurus aku nitihka pemetul ati aku.",
			"9": "<sup>9</sup> Awakka penyai orang ke jai abis magang, tang awakka Nuan ke lurus, Nuan ke meresa semua runding enggau ati, nyadi Allah Taala ti lurus.",
			"10": "<sup>10</sup> Allah Taala endang terabai aku. Iya nyelamatka orang ke lurus.",
			"11": "<sup>11</sup> Allah Taala endang Hakim ti lurus, Allah Taala seruran ngukum orang ke jai.",
			"12": "<sup>12</sup> Enti sida enda ngubah ulah diri, Allah Taala deka ngansah pedang Iya. Iya udah narit lalu nyendiaka panah Iya.",
			"13": "<sup>13</sup> Iya udah nyendiaka senyata Iya ti bisa lalu ngichingka laja Iya ti beapi-api.",
			"14": "<sup>14</sup> Peda chara orang ke jai berundingka jalai ngereja penyai. Iya berunding deka ngaga pengachau, lalu ngemulaka orang.",
			"15": "<sup>15</sup> Iya numbak lubang kebung, lalu iya empu labuh ngagai lubang kebung nya.",
			"16": "<sup>16</sup> Penyai iya empu ninggang pala iya empu lalu pemangah iya empu ninggang puchuk pala iya empu.",
			"17": "<sup>17</sup> Aku deka meri terima kasih ngagai TUHAN ketegal pengelurus Iya, lalu aku deka belagu muji nama TUHAN ti Pemadu Tinggi."
		},
		"8": {
			"1": "<sup>1</sup> O TUHAN, ti TUHAN kami. Naka meh pemesai kuasa nama Nuan di serata dunya! Nuan ke bempu mulia ti tinggi ngilah langit, ke dipuji",
			"2": "<sup>2</sup> bala nembiak enggau bala anak mit. Nuan udah ngaga kuta kena Nuan ngelaban munsuh Nuan, kena Nuan nagang munsuh enggau orang ke ngiga balas.",
			"3": "<sup>3</sup> Lebuh aku engkadah ke langit ti udah digaga Nuan, engkadah ngagai bulan enggau bintang ti udah di atur Nuan,",
			"4": "<sup>4</sup> aku lalu nanya: ‘Sapa mensia, alai Nuan deka bedulika sida? Sapa anak mensia, alai Nuan deka ibuhka sida?’",
			"5": "<sup>5</sup> Tang taja pia, Nuan udah ngaga iya semina baruh mimit ari Nuan Empu, lalu Nuan udah meri iya mulia enggau basa nyadika makota iya.",
			"6": "<sup>6</sup> Nuan udah ngasuh iya merintah semua utai ti digaga Nuan; Nuan udah ngengkahka semua utai di baruh kaki iya,",
			"7": "<sup>7</sup> semua jelu bedus enggau sapi, enggau jelu babas,",
			"8": "<sup>8</sup> semua burung enggau ikan enggau semua utai ti idup dalam tasik.",
			"9": "<sup>9</sup> O TUHAN, ti TUHAN kami, naka meh pemesai kuasa nama Nuan di serata dunya!"
		},
		"9": {
			"1": "<sup>1</sup> Aku deka meri terima kasih ngagai TUHAN enggau pengabis ati aku. Aku deka nusui pasal semua utai ti badas ti udah dikereja Nuan.",
			"2": "<sup>2</sup> Aku deka gaga ati belagu ngemesaika Nuan. Aku deka belagu muji Nuan, Allah Taala ti Pemadu Bekuasa.",
			"3": "<sup>3</sup> Lebuh Nuan pegari, bala munsuh aku nikal, rebah lalu mati.",
			"4": "<sup>4</sup> Nuan endang lurus sereta betul ati lebuh Nuan ngakim, lalu pemutus penemu Nuan ngelantangka ati aku.",
			"5": "<sup>5</sup> Nuan udah ngukum semua bansa orang kapir lalu ngerusak orang ke jai. Sida nadai agi dikingatka.",
			"6": "<sup>6</sup> Munsuh kami abis lengis belama iya. Nuan udah ngerusak nengeri sida, lalu sida nadai agi dikingatka.",
			"7": "<sup>7</sup> Tang TUHAN endang Raja belama iya. Iya udah nudukka kerusi diraja Iya ba endur alai Iya ke ngakim.",
			"8": "<sup>8</sup> Iya merintah dunya ngena pengelurus. Iya ngakim semua bansa enggau lurus.",
			"9": "<sup>9</sup> TUHAN nyadi kuta alai orang ngenduh lebuh sida kena perinsa, nyadi kuta alai sida diau lebuh maya tusah.",
			"10": "<sup>10</sup> Orang ke nemu Nuan, TUHAN, deka manggaika pengarap sida ba Nuan. Nuan enda ninggalka orang ke datai ngagai Nuan.",
			"11": "<sup>11</sup> Belagu pantun puji ke TUHAN ti diau di Sion! Tusui ngagai semua bansa utai ti udah dikereja Iya!",
			"12": "<sup>12</sup> Allah Taala ingatka orang ke merinsa. Iya nadai kala enda ingatka sabak sida, lalu Iya ngukum orang ke udah ngereja utai ti salah ngagai sida.",
			"13": "<sup>13</sup> Kasihka aku, O TUHAN! Peda Nuan chara munsuh aku merinsaka aku! Selamatka aku ari pemati, O TUHAN,",
			"14": "<sup>14</sup> ngambika aku ulih bediri di mua orang di Jerusalem lalu madahka ngagai sida semua utai ti ngasuh aku muji Nuan, Aku deka rami gaga laban Nuan udah nyelamatka aku.",
			"15": "<sup>15</sup> Orang kapir udah labuh ngagai lubang kebung ti udah ditumbak sida empu; lalu kaki sida udah disintak jarin ti udah ditan sida empu.",
			"16": "<sup>16</sup> TUHAN udah mandangka Diri ngena pengelurus pemutus penemu Iya, lalu orang ke jai udah disintak pengawa ti dikereja jari sida empu.",
			"17": "<sup>17</sup> Orang ke jai, semua bansa ti enda ingatka Allah Taala, deka dibuai ngagai dunya pemati.",
			"18": "<sup>18</sup> Laban orang ke suntuk deka seruran dikingatka, lalu pengadang orang ke seranta enda lenyau belama iya.",
			"19": "<sup>19</sup> Datai meh Nuan TUHAN! Anang nejuka orang ngelaban Nuan! Awakka semua bansa orang kapir diakim Nuan.",
			"20": "<sup>20</sup> Asuh sida takut, O TUHAN. Awakka sida nemu diri semina mensia aja."
		},
		"10": {
			"1": "<sup>1</sup> Nama kebuah Nuan kelalu jauh, O TUHAN? Nama kebuah Nuan belalai lebuh kami kena laban penusah?",
			"2": "<sup>2</sup> Orang ke jai sumbung, lalu merinsaka orang ke seranta. Tangkap meh sida ngena panjuk ti udah digaga sida empu.",
			"3": "<sup>3</sup> Laban orang ke jai temegahka pengingin ati sida, lalu orang ke amu nyumpah sereta nulak TUHAN.",
			"4": "<sup>4</sup> Orang ke jai enda ibuhka TUHAN. Ngena penyumbung sida empu, sida ngumbai Allah Taala nadai guna.",
			"5": "<sup>5</sup> Orang ke jai mujur dalam semua utai. Sida enda meretika atur Allah Taala. Sida enchebirka mulut ngeleseka bala munsuh sida.",
			"6": "<sup>6</sup> Ku sida berunding dalam ati diri, “Kitai tetap enda lebu. Kitai enda dituntung penusah.”",
			"7": "<sup>7</sup> Sida nyumpah orang, bejaku bula, lalu ngemba orang. Sida jampat nyebut jaku ti jai, ti mandangka pemenchi.",
			"8": "<sup>8</sup> Sida belalai ba genturung pendiau orang, deka munuh orang ke nadai penyalah. Sida nengkilunka orang ke nadai daya.",
			"9": "<sup>9</sup> Sida ngipa baka singa. Sida ngipa orang ke seranta. Sida nangkap orang ke seranta ngena jarin.",
			"10": "<sup>10</sup> Orang ke nadai daya alah. Pengering tulang ti ngalahka sida.",
			"11": "<sup>11</sup> Ku orang ke jai berunding dalam ati diri, “Allah Taala enggai ibuh! Iya udah mejamka mata Iya, lalu enda ulih meda aku!”",
			"12": "<sup>12</sup> O TUHAN, ukum meh orang ke jai nya! Kingatka orang ke kena perinsa!",
			"13": "<sup>13</sup> Nama kebuah orang ke jai ngeleseka Allah Taala lalu berunding dalam ati diri, “Iya enggai ngukum aku”?",
			"14": "<sup>14</sup> Tang Nuan meda. Nuan endang seruran meda penusah enggau pemerinsa lalu seruran sedia deka nulung. Orang ke nadai daya, nyerahka diri ngagai Nuan. Nuan endang seruran nulung orang ke suntuk.",
			"15": "<sup>15</sup> Rusak meh kuasa orang ke jai. Ukum meh sida ketegal penyalah ti udah dikereja sida datai ke sida badu agi ngereja utai nya.",
			"16": "<sup>16</sup> TUHAN endang nyadi Raja belama lama iya. Orang ke nyembah petara bukai deka lesap ari menua tu.",
			"17": "<sup>17</sup> O TUHAN, Nuan endang deka mendingka sampi orang ke baruh ati. Nuan meri sida pemerani.",
			"18": "<sup>18</sup> Nuan endang deka ningaka sabak orang ke kena tekan, enggau orang ke nadai apai indai. Nuan deka meri atur ti manah ngagai sida, ngambika orang badu agi ngemba sida."
		},
		"11": {
			"1": "<sup>1</sup> Aku ngarapka TUHAN deka ngintu aku likun. Naka meh pemeli nuan lebuh nuan nyebut ngagai aku, ‘Terebai meh nuan kebukai baka burung ti terebai ngagai bukit,",
			"2": "<sup>2</sup> laban orang ke jai udah narit panah lalu nyelintingka laja sida ari endur ti petang, ngagai orang ke badas.",
			"3": "<sup>3</sup> Nadai utai ulih dikereja orang ke badas lebuh semua utai udah rusak magang.’",
			"4": "<sup>4</sup> TUHAN bisi dalam Rumah Sembiang Iya ti kudus. Kerusi diraja Iya endang di serega. Iya ngemataka mensia di semua endur lalu nemu utai ti dikereja sida.",
			"5": "<sup>5</sup> Iya meresa orang ke badas enggau orang ke jai. Iya begedika orang ke mangah.",
			"6": "<sup>6</sup> Iya mubuhka bara api enggau api merirang ngagai orang ke jai. Iya ngukum sida ngena ribut ti angat.",
			"7": "<sup>7</sup> TUHAN endang lurus sereta rinduka pengawa ti manah. Orang ke ngereja pengawa ti manah deka diau begulai enggau TUHAN."
		},
		"12": {
			"1": "<sup>1</sup> Tulung kami, O TUHAN! Orang ke badas nadai agi tinggal. Orang ke lurus nadai agi ditemu.",
			"2": "<sup>2</sup> Semua sida ngemulaka pangan diri. Sida nipu pangan diri ngena jaku ti ngelangkung.",
			"3": "<sup>3</sup> Tagang dilah ti ngelangkung nya, O TUHAN! Tutup nyawa ti temegah, ti nyebut,",
			"4": "<sup>4</sup> ‘Kami bulih utai ti dikedekaka kami ngena jaku kami empu. Kami deka nyebut utai enggau sepeneka kami empu, lalu nadai orang ulih nagang kami.’",
			"5": "<sup>5</sup> “Tang diatu Aku deka datai,” ku TUHAN bejaku, “laban orang ke suntuk ditekanka orang lalu orang ke kena perinsa, bedemuh natka diri pedis. Aku deka meri sida pengelikun ti dikedekaka sida.”",
			"6": "<sup>6</sup> Semaya TUHAN endang tau dikarapka. Semaya Iya baka pirak ti tulin, ti dituchi tujuh kali dalam serumbung api.",
			"7": "<sup>7</sup> Orang ke jai bisi di semua endur, lalu semua orang bela muji utai ti jai. Ibun kami likun, O TUHAN, lalu jaga kami ari orang ke baka nya."
		},
		"13": {
			"1": "<sup>1</sup> Ni pengelama Nuan agi mengkang enda ingatka aku, O TUHAN? Belama lama iya? Ni pengelama Nuan agi mengkang belalai ari aku?",
			"2": "<sup>2</sup> Ni pengelama aku enda tau enda natka penusah? Ni pengelama ati aku enda tau enda sinu siang malam? Ni pengelama munsuh aku bekuasa atas aku?",
			"3": "<sup>3</sup> Malik ngagai aku, O TUHAN Allah Taala aku, lalu saut aku. Pulaika pengering aku ngagai aku baru. Anang nejuka aku mati.",
			"4": "<sup>4</sup> Anang nejuka munsuh aku nyebut, ‘Kitai udah ngalahka iya.’ Anang nejuka sida ngalah lalu ketawaka aku.",
			"5": "<sup>5</sup> Aku manggaika diri ba pengerindu Nuan ti meruan belama iya. Aku deka gaga ati laban Nuan deka ngidupka aku.",
			"6": "<sup>6</sup> Aku deka belagu ke Nuan, O TUHAN, laban Nuan udah manah enggau aku."
		},
		"14": {
			"1": "<sup>1</sup> Ku orang ke beli bejaku dalam ati diri, ‘Allah Taala endang sigi nadai!’ Semua sida nya jai magang, lalu sida udah ngereja utai ti balat jai. Nadai siku sida ngereja utai ti ngena.",
			"2": "<sup>2</sup> TUHAN nilik ke baruh ari serega ngagai kitai mensia deka meda enti kitai ti nyembah Iya bisi bepenemu-dalam.",
			"3": "<sup>3</sup> Tang semua sida udah salah magang. Semua sida bela jai magang. Nadai siku sida ngereja utai ti ngena, siku pen nadai.",
			"4": "<sup>4</sup> Ku TUHAN nanya, ‘Kati sida enda nemu? Kati semua orang ke jai nya nadai nemu utai? Sida ngerumpak bala nembiak Aku, lalu sida nadai kala besampi ngagai Aku.’",
			"5": "<sup>5</sup> Tang ila sida deka balat takut laban Allah Taala deka setanah enggau orang ke ngasika Iya.",
			"6": "<sup>6</sup> Orang ke jai nguji ngemuntanka pelan orang ke baruh ati, tang TUHAN nyaga orang ke baruh ati.",
			"7": "<sup>7</sup> Aku bebendar besampi minta pengelepas datai ari Sion ngagai orang Israel. Naka meh pengelantang ati orang Israel lebuh TUHAN ngasuh sida kaya-raja baru!"
		},
		"15": {
			"1": "<sup>1</sup> TUHAN, sapa tau tama ngagai Rumah Sembiang Nuan? Sapa tau nyembah Nuan di Sion, bukit Nuan ti kudus?",
			"2": "<sup>2</sup> Semina orang ke ngasika Allah Taala dalam semua utai lalu seruran ngereja utai ti ngena, ke nyebut utai ti amat sereta tau dikarapka,",
			"3": "<sup>3</sup> lalu enda betenahka orang. Sida nadai ngereja penyalah ngagai pangan diri lalu sida enda mega nusui berita ngapa pasal pangan diri.",
			"4": "<sup>4</sup> Sida begedika orang ke ditulak Allah Taala, tang masa orang ke nangika TUHAN. Sida seruran ngamatka semaya sida, enda ngira pemesai ungkus nya.",
			"5": "<sup>5</sup> Sida nyali orang duit, lalu enda minta anak duit nya, lalu sida enda ulih diumpan makai suap nyadika saksi ngelaban orang ke nadai penyalah. Barang sapa ngereja utai tu, deka diau likun berari."
		},
		"16": {
			"1": "<sup>1</sup> Jaga aku, O Allah Taala. Aku ngarapka Nuan meri aku pengelikun.",
			"2": "<sup>2</sup> Aku nyebut ngagai TUHAN, “Nuan TUHAN aku. Semua utai ti manah ti dikemisi aku, datai ari Nuan magang.”",
			"3": "<sup>3</sup> Naka meh pemanah bala nembiak TUHAN ti tetap ati ngagai Iya! Aku bisi amat ati deka begulai enggau sida.",
			"4": "<sup>4</sup> Orang ke nyembah petara bukai, deka ditinggang mayuh macham penusah. Aku enggai enggau sida ngenjuk piring. Aku enggai nyembah petara sida.",
			"5": "<sup>5</sup> Semina Nuan aja, TUHAN, ke dikemisi aku lalu Nuan meri aku semua utai ti dipeguna aku. Pengidup aku ba jari Nuan.",
			"6": "<sup>6</sup> Naka meh pemanah pemeri ti diberi Nuan ngagai aku. Naka meh pemanah sida!",
			"7": "<sup>7</sup> Aku muji TUHAN, laban Iya ngiring aku, lalu lebuh malam hari ati aku empu ngajar aku.",
			"8": "<sup>8</sup> Aku seruran nemu TUHAN bisi enggau aku. Iya semak aku, lalu nadai utai ulih ngelebuka aku.",
			"9": "<sup>9</sup> Nya alai aku meri terima kasih sereta gaga ati, lalu aku berasai likun,",
			"10": "<sup>10</sup> laban Nuan nyaga aku ari kuasa pemati. Aku udah betuaika Nuan enggau ati ti tetap, lalu Nuan enda ninggalka aku di dunya pemati.",
			"11": "<sup>11</sup> Nuan nunjukka ngagai aku jalai ti nuju ngagai pengidup. Ati aku gaga laban Nuan bisi enggau aku lalu ati aku lantang belama iya."
		},
		"17": {
			"1": "<sup>1</sup> O TUHAN, pendingka aku ke minta atur ti lurus. Pendingka sabak aku ke minta tulung! Dingaka sampi aku, laban sampi aku endang amat.",
			"2": "<sup>2</sup> Nuan deka kasihka aku lebuh Nuan ngakim aku, laban Nuan nemu utai ti ngena.",
			"3": "<sup>3</sup> Nuan nemu ati aku. Nuan udah datai ngagai aku lebuh malam hari. Nuan udah meresa semua ati aku lalu nadai tetemuka pengingin ti jai dalam aku. Aku nadai bejaku jai",
			"4": "<sup>4</sup> baka orang bukai. Aku udah ngasika pesan Nuan lalu enda kasar enggau orang.",
			"5": "<sup>5</sup> Aku endang seruran bejalai niti jalai Nuan lalu nadai kala limpang ari jalai nya.",
			"6": "<sup>6</sup> Aku besampi ngagai Nuan, O Allah Taala, laban Nuan nyaut aku. Nya alai singitka pending Nuan ngagai aku, lalu pendingka jaku aku.",
			"7": "<sup>7</sup> pandangka pengerindu Nuan ti manah, lalu selamatka aku. Lebuh aku ngimbai Nuan, aku likun ari munsuh.",
			"8": "<sup>8</sup> Lindung aku baka Nuan ke nyaga mata Nuan Empu. Lalaika aku di baruh sayap Nuan",
			"9": "<sup>9</sup> ari serang orang ke jai. Munsuh seruran bekelingika aku.",
			"10": "<sup>10</sup> Sida nadai penyiru, lalu bejaku sumbung.",
			"11": "<sup>11</sup> Sida ngelingi aku diatu, di sebarang endur aku bejalai, ngiga peluang kena ngalahka aku.",
			"12": "<sup>12</sup> Sida ngipa aku baka singa, laban sida deka encharikka aku.",
			"13": "<sup>13</sup> Datai meh Nuan TUHAN! Serang meh munsuh aku lalu alahka sida! Selamatka aku ngena pedang Nuan ari jari orang ke jai.",
			"14": "<sup>14</sup> Selamatka aku ari orang ke bisi semua utai ti dikedekaka sida. Ukum meh sida ngena pemerinsa ti udah ditaga Nuan ke sida. Awakka ukum nya chukup ke bala anak sida enggau ke bala uchu-ambu sida!",
			"15": "<sup>15</sup> Tang aku deka meda Nuan, laban aku nadai ngereja penyalah. Lalu lebuh aku dani, aku deka gaga ati laban Nuan bisi enggau aku."
		},
		"18": {
			"1": "<sup>1</sup> Aku rinduka Nuan, TUHAN ke penyaga aku.",
			"2": "<sup>2</sup> TUHAN nyadika penyaga aku. Iya nyadi kuta aku ti tegap. Allah Taala aku nyadika penyaga aku, lalu aku likun begulai enggau Iya. Iya ngelindung aku baka terabai. Iya nyaga aku ngambika aku likun.",
			"3": "<sup>3</sup> Aku ngangauka TUHAN, lalu Iya ngelepaska aku ari jari munsuh aku. Puji TUHAN!",
			"4": "<sup>4</sup> Kuasa pemati bekelingika aku. Kuasa pengerusak ngelamun aku baka umbak.",
			"5": "<sup>5</sup> Kuasa pemati bekelingika aku, lalu pendam nan panjuk kena nyintak aku.",
			"6": "<sup>6</sup> Lebuh aku dituntung penusah, aku ngangauka TUHAN. Aku ngangauka Allah Taala aku. Iya ninga aku dalam rumah Iya. Iya ninga kangau aku.",
			"7": "<sup>7</sup> Udah nya tanah begetar sereta berenyang. Pugu bukit beguyat sereta berenyang, laban Allah Taala ringat.",
			"8": "<sup>8</sup> Asap lalu pansut ari lubang idung Iya, lalu daun api ti nyelar, enggau bara api pansut ari nyawa Iya.",
			"9": "<sup>9</sup> Iya ensiatka langit lalu nurun ngena mua hari ti petang di baruh kaki Iya.",
			"10": "<sup>10</sup> Iya laju terebai nepan ba sayap melikat cherub Iya. Iya bejalai ngena sayap ribut.",
			"11": "<sup>11</sup> Iya nyerayung Diri ngena pemetang. Mua hari ti tebal, ti penuh laban ai, bekelingika Iya.",
			"12": "<sup>12</sup> Ujan batu enggau api ti nyelar pansut ari kilat di mua Iya lalu ngebuska mua hari ti chelum.",
			"13": "<sup>13</sup> Udah nya TUHAN lalu bejaku ari langit munyi guntur, lalu munyi nyawa Iya ti Pemadu Tinggi bisi didinga.",
			"14": "<sup>14</sup> Iya nimbakka laja Iya lalu mambarka bala munsuh Iya. Ngena selar kilat, Iya ngasuh sida rari.",
			"15": "<sup>15</sup> Buntut tasik ayan, lalu pugu dunya pegari, lebuh Nuan ngerara munsuh Nuan, O TUHAN, lalu merakka nyawa Nuan ngagai sida laban Nuan ringat.",
			"16": "<sup>16</sup> TUHAN lalu nyengkau aku ari atas, lalu megai aku. Iya narit aku ari ai ti dalam.",
			"17": "<sup>17</sup> Iya ngelepaska aku ari bala munsuh aku ti bekuasa enggau ari semua orang ke begedika aku, laban sida kelalu kering.",
			"18": "<sup>18</sup> Lebuh aku dituntung penusah, sida nyerang aku, tang TUHAN nyaga aku.",
			"19": "<sup>19</sup> Iya nyaup aku ngerarika diri ari utai ti tau nganu. Iya ngidupka aku laban Iya gaga ati ke aku.",
			"20": "<sup>20</sup> TUHAN ngupah aku laban aku ngereja utai ti ngena. Iya merekatka aku laban aku nadai penyalah.",
			"21": "<sup>21</sup> Aku udah ngasika adat TUHAN. Aku nadai kala ninggalka Allah Taala aku.",
			"22": "<sup>22</sup> Aku udah nitihka semua adat Iya. Aku nadai kala enda ngasika pesan Iya.",
			"23": "<sup>23</sup> Iya nemu aku nadai penyalah, nemu aku udah nagang diri ngereja utai ti salah.",
			"24": "<sup>24</sup> Nya alai Iya ngupah aku, laban aku ngereja utai ti ngena, laban Iya nemu aku nadai penyalah.",
			"25": "<sup>25</sup> O TUHAN, Nuan endang tetap ati ngagai orang ke tetap ati ngagai Nuan. Nuan badas enggau orang ke pemadu badas.",
			"26": "<sup>26</sup> Nuan mandangka Diri tuchi ngagai orang ke tuchi, tang Nuan bemunsuh enggau orang ke jai.",
			"27": "<sup>27</sup> Nuan ngelepaska orang ke baruh ati, tang ngemaruhka orang ke sumbung.",
			"28": "<sup>28</sup> O TUHAN, Nuan meri aku penampak. Nuan muai pemetang ari aku.",
			"29": "<sup>29</sup> Nuan meri aku pengering kena aku nyerang munsuh aku enggau kuasa kena aku ngalahka kuta sida.",
			"30": "<sup>30</sup> Naka meh pemanah utai dikereja Allah Taala! Jaku Iya endang tau dikarapka! Iya baka terabai ke semua orang ke minta diri dijaga Iya.",
			"31": "<sup>31</sup> TUHAN siku aja ti Allah Taala. Allah Taala siku aja ti nyadika kuta kitai.",
			"32": "<sup>32</sup> Iya nya meh Allah Taala ti ngasuh aku kering, ke ngasuh jalai aku likun.",
			"33": "<sup>33</sup> Iya ngeringka kaki aku. Iya ngasuh aku likun ba sebelah bukit.",
			"34": "<sup>34</sup> Iya matihka aku beperang, ngambika aku ulih ngena panah temaga.",
			"35": "<sup>35</sup> O TUHAN, Nuan nyaga aku lalu ngelepaska aku. Nuan udah ngibun aku, lalu ngaga aku nyadi orang ke tinggi pangkat, lalu kuasa Nuan udah ngasuh aku likun.",
			"36": "<sup>36</sup> Nuan udah nagang aku kena tangkap, lalu aku nadai kala rebah.",
			"37": "<sup>37</sup> Aku begagaika munsuh aku, lalu nangkap sida. Aku enda badu ngapa datai ke aku udah ngerusak sida.",
			"38": "<sup>38</sup> Aku mantap sida, lalu sida enda angkat agi. Sida garing-garing alah di mua aku.",
			"39": "<sup>39</sup> Nuan ngasuh aku kering beperang lalu ngasuh aku menang ngelaban munsuh aku.",
			"40": "<sup>40</sup> Nuan ngasuh munsuh aku rari ari aku. Aku ngerusak sida ke begedika aku.",
			"41": "<sup>41</sup> Sida nyabak minta tulung, tang nadai orang deka ngidupka sida. Sida ngangauka TUHAN, tang Iya enda nyaut.",
			"42": "<sup>42</sup> Aku nginsarka sida baka amau ti dikebuka ribut. Aku ngindik sida baka luchak ti ba jalai raya.",
			"43": "<sup>43</sup> Nuan udah ngelepaska aku ari orang ke seruran angkat ngelaban lalu ngasuh aku merintah mayuh bengkah bansa. Orang ke enda dikelala aku diatu udah nyadi nembiak aku.",
			"44": "<sup>44</sup> Orang ari bansa bukai meremi di mua aku. Lebuh sida ninga aku, sida ngasi.",
			"45": "<sup>45</sup> Sida enda berani agi lalu datai getar-getar ari kuta sida.",
			"46": "<sup>46</sup> TUHAN endang idup! Puji meh Iya ke nyaga aku! Tusui pasal pemesai Allah Taala ti ngelepaska aku.",
			"47": "<sup>47</sup> Iya ngasuh aku menang ngelaban munsuh aku. Iya ngalahka semua bansa ti dipegai aku",
			"48": "<sup>48</sup> lalu ngelepaska aku ari bala munsuh aku. O TUHAN, Nuan udah ngasuh aku menang ngelaban munsuh aku lalu nyaga aku ari orang ke mangah.",
			"49": "<sup>49</sup> Nya alai aku muji Nuan di mua semua bansa. Aku belagu muji Nuan.",
			"50": "<sup>50</sup> Allah Taala ngasuh raja Iya menang. Iya seruran ngayanka pengerindu ti meruan belama iya ngagai orang ke udah dipilih Iya, ngagai David enggau peturun iya belama iya."
		},
		"19": {
			"1": "<sup>1</sup> Langit mandangka mulia Allah Taala. Langit ngayanka enggau nyata utai ti udah dikereja Iya.",
			"2": "<sup>2</sup> Hari siti nusui berita nya ngagai hari siti. Malam siti nusui berita nya ngagai malam siti,",
			"3": "<sup>3</sup> ukai ngena jaku tauka leka jaku tauka ngena munyi utai ti didinga.",
			"4": "<sup>4</sup> Tang taja pia, pesan sida ngelekai di serata dunya lalu didinga nyentuk ngagai ujung dunya. Allah Taala udah ngaga rumah di langit ke mata hari.",
			"5": "<sup>5</sup> Mata hari nya pansut pagi hari baka lelaki ke gaga ditikah, baka pelumba ke gagit ati deka belumba belanda.",
			"6": "<sup>6</sup> Mata hari nya belabuh ba ujung langit puting setak lalu meraka ngagai puting setak. Nadai utai ulih ngelalaika diri ari pengangat iya.",
			"7": "<sup>7</sup> Adat TUHAN endang pemadu badas. Iya meri pengering ti baru. Pesan TUHAN endang tau dikarapka meri penemu-dalam ngagai orang ke nadai penemu-dalam.",
			"8": "<sup>8</sup> Adat TUHAN endang ngena, lalu ngelantangka ati. Pesan TUHAN endang terang lalu iya ngasuh orang meretika utai.",
			"9": "<sup>9</sup> Bebasaka TUHAN nya utai ti manah, lalu basa nya deka majak belama iya. Atur ti diberi TUHAN endang lurus. Atur nya endang nadai kala berat sepiak.",
			"10": "<sup>10</sup> Utai nya lebih agi dikinginka ari emas ti pemadu manah, manis agi ari ai manyi ti pemadu tulin.",
			"11": "<sup>11</sup> Utai nya meri penemu ngagai aku, ke nembiak Nuan. Aku diupah ketegal ngasika utai nya.",
			"12": "<sup>12</sup> Nadai siku kitai ulih meda penyalah diri. Selamatka aku, TUHAN, ari penyalah ti enda dipeda!",
			"13": "<sup>13</sup> Intu aku likun mega ari dosa ti dikereja enggau sengaja. Anang nejuka dosa nya merintah aku. Nya baru aku deka nyadi pemadu badas sereta dilepaska ari penyai dosa.",
			"14": "<sup>14</sup> Awakka jaku enggau runding aku manah dipeda Nuan, O TUHAN! Laban Nuan Empu ke alai aku ngenduh, sereta nyadi penebus aku!"
		},
		"20": {
			"1": "<sup>1</sup> Awakka TUHAN nyaut nuan lebuh nuan dituntung penusah! Awakka Allah Taala Jakop nyaga nuan!",
			"2": "<sup>2</sup> Awakka Iya ngirumka tulung ari Rumah Sembiang Iya lalu nyaup nuan ari Bukit Sion.",
			"3": "<sup>3</sup> Awakka Iya nerima semua piring nuan lalu gaga ati ke semua piring tunu nuan.",
			"4": "<sup>4</sup> Awakka Iya meri ngagai nuan utai ti dikinginka nuan lalu ngasuh semua pelan nuan mujur magang.",
			"5": "<sup>5</sup> Nya baru kami deka manjung gaga ati ke pemenang nuan lalu muji Allah Taala kitai kena ngerami pemenang nuan. Awakka TUHAN nyaut semua peminta nuan.",
			"6": "<sup>6</sup> Diatu baru aku nemu TUHAN meri pemenang ngagai raja ti udah dipilih Iya. Iya nyaut raja nya ari serega Iya ti kudus lalu ulih kuasa Iya, Iya meri raja nya pemenang ti besai.",
			"7": "<sup>7</sup> Sekeda orang manggaika pengandal sida ba kerita perang sida lalu sida ti bukai manggaika pengandal sida ba kuda sida, tang kami manggaika pengandal kami ba kuasa TUHAN Allah Taala kami.",
			"8": "<sup>8</sup> Sida deka tesangkut lalu tetingkap, tang kami deka angkat lalu bediri tegap.",
			"9": "<sup>9</sup> O TUHAN, beri meh pemenang ngagai raja. Saut meh kami lebuh kami ngangau."
		},
		"21": {
			"1": "<sup>1</sup> Raja gaga ati, O TUHAN, laban Nuan udah meri iya pengering. Iya gaga ati laban Nuan udah ngasuh iya menang.",
			"2": "<sup>2</sup> Nuan udah meri iya utai ti dikinginka iya. Nuan udah nyaut peminta iya.",
			"3": "<sup>3</sup> Nuan udah datai ngagai iya mai berekat ti besai lalu ngerasukka makota emas ba pala iya.",
			"4": "<sup>4</sup> Iya minta diri idup, lalu Nuan meri iya pengidup, enggau umur ti panjai.",
			"5": "<sup>5</sup> Mulia iya besai laban tulung ari Nuan. Nuan udah ngasuh iya tebilang nama sereta bemulia.",
			"6": "<sup>6</sup> Berekat Nuan meruan enggau iya belama iya, lalu iya gaga ati begulai enggau Nuan.",
			"7": "<sup>7</sup> Raja manggaika pengandal iya ba TUHAN ti Pemadu Bekuasa. Lalu iya deka seruran diau likun laban pengerindu TUHAN endang meruan.",
			"8": "<sup>8</sup> Raja deka nangkap semua munsuh iya. Iya deka nangkap semua orang ke begedika iya.",
			"9": "<sup>9</sup> Lebuh iya pegari, iya deka ngerusak sida baka api. TUHAN deka ringat lalu makai sida, lalu api deka makai sida.",
			"10": "<sup>10</sup> Nadai siku ari peturun sida deka idup. Raja deka munuh semua sida.",
			"11": "<sup>11</sup> Sida ngaga pelan, lalu bepekat ngelaban iya, tang sida deka puntan.",
			"12": "<sup>12</sup> Iya deka nimbakka laja iya ngagai sida lalu ngasuh sida nguing lalu rari.",
			"13": "<sup>13</sup> Kami muji Nuan, O TUHAN, laban pengering Nuan endang amat besai! Kami deka belagu lalu muji kuasa Nuan."
		},
		"22": {
			"1": "<sup>1</sup> Allah Taala aku, O Allah Taala aku, nama kebuah Nuan ninggalka aku? Nama kebuah Nuan jauh ari aku lalu enda nulung aku, enda ninga demuh aku?",
			"2": "<sup>2</sup> O Allah Taala aku, aku ngangauka Nuan lebuh siang hari, tang Nuan enda nyaut. Aku ngangau lebuh malam hari, tang aku enda ulih belelak.",
			"3": "<sup>3</sup> Tang Nuan kudus duduk ba kerusi diraja Nuan dipuji orang Israel.",
			"4": "<sup>4</sup> Aki ini kami udah manggaika pengarap sida ba Nuan. Sida udah ngarapka Nuan, lalu Nuan udah ngelepaska sida.",
			"5": "<sup>5</sup> Sida ngangauka Nuan, lalu sida diselamatka. Sida manggaika pengandal sida ba Nuan, lalu sida enda asa ngapa.",
			"6": "<sup>6</sup> Tang aku tu ulat, ukai mensia; dikeleseka orang, dikelanyaika semua orang!",
			"7": "<sup>7</sup> Semua orang ke meda aku, ketawaka aku magang. Sida enchebirka mulut ngagai aku, lalu ngilikka pala sida.",
			"8": "<sup>8</sup> Ku sida bejaku, “Panggaika pengarap nuan ba TUHAN; awakka Iya ngelepaska nuan. Awakka Iya nyelamatka nuan ke dikerinduka Iya!”",
			"9": "<sup>9</sup> Nuan ngasuh aku ada enggau likun, lalu lebuh aku agi mit, Nuan ngibun aku likun.",
			"10": "<sup>10</sup> Aku udah manggaika diri ba Nuan kenyau ari ada, lalu Nuan endang Allah Taala aku.",
			"11": "<sup>11</sup> Anang jauh ari aku, laban penusah nyau semak, lalu aku nadai orang nulung.",
			"12": "<sup>12</sup> Bala munsuh ke ngelingi aku nyau baka raban sapi ti lelaki. Semua sida ngelingi aku magang, baka sapi ti lelaki ti mangah di Basan.",
			"13": "<sup>13</sup> Sida nukangka nyawa sida ngagai aku, baka singa ti lapar sereta negung.",
			"14": "<sup>14</sup> Pengering aku nyau abis, lenyau baka ai ti siuh ba tanah. Semua tulang aku nyau enda betampung agi. Ati aku baka lilin; iya lela dalam dada aku.",
			"15": "<sup>15</sup> Batang rekung aku nyau rangkai baka amau, lalu dilah aku nyau lekat ba kelakanit aku. Nuan ngalika aku malai amau pemati.",
			"16": "<sup>16</sup> Bala orang ke jai bekelingika aku. Orang ke ngereja penyai ngelulung aku. Sida nguyaska jari enggau kaki aku.",
			"17": "<sup>17</sup> Semua tulang aku nyau ayan magang. Bala munsuh ketawa meda aku.",
			"18": "<sup>18</sup> Sida bedua gari aku entara bala sida, lalu bechabut undi ke serayung aku.",
			"19": "<sup>19</sup> O TUHAN, anang jauh ari aku! Datai jampat lalu lepaska aku!",
			"20": "<sup>20</sup> Selamatka aku ari pedang. Selamatka aku ari raban ukui tu.",
			"21": "<sup>21</sup> Selamatka aku ari raban singa tu. Aku nadai daya agi di mua raban sapi ti siga tu.",
			"22": "<sup>22</sup> Aku deka madahka ngagai bala aku utai ti udah dikereja Nuan. Aku deka muji Nuan ba gempuru sida, munyi tu:",
			"23": "<sup>23</sup> ‘Puji meh TUHAN, kita ke nangika Iya! Kemesaika Iya, kita ke peturun Jakop! Sembah meh Iya, kita orang Israel!",
			"24": "<sup>24</sup> Iya nadai kala enda ibuhka orang ke seranta tauka nejuka sida kena perinsa. Iya enda ninggalka sida, tang nyaut sida lebuh sida ngangauka Iya.”",
			"25": "<sup>25</sup> Lebuh orang begempuru, aku deka muji Nuan ketegal utai ti udah dikereja Nuan. Di mua orang ke nyembah Nuan aku deka ngenjuk piring ti udah disemaya aku.",
			"26": "<sup>26</sup> Orang ke seranta deka makai datai ke kenyang. Orang ke datai ngagai TUHAN, deka muji Iya. Awakka sida nyamai diau belama iya!",
			"27": "<sup>27</sup> Semua bansa deka ingatka TUHAN. Sida deka nguing ngagai Iya ari serata dunya. Semua bansa deka nyembah Iya.",
			"28": "<sup>28</sup> TUHAN endang Raja, lalu Iya merintah semua bansa.",
			"29": "<sup>29</sup> Semua orang ke sumbung di dunya tu deka meremi di mua Iya; semua mensia deka meremi di mua Iya.",
			"30": "<sup>30</sup> Serak ti dudi deka betuaika Iya. Sida deka bejakuka pasal TUHAN ngagai serak ke nangkanka sida.",
			"31": "<sup>31</sup> Orang ke apin ada, deka dipadahka: ‘TUHAN udah ngelepaska bala nembiak Iya.’"
		},
		"23": {
			"1": "<sup>1</sup> TUHAN pengibun aku. Aku nadai suntuk utai.",
			"2": "<sup>2</sup> Iya ngasuh aku belelak ba padang rumput ti galak lalu nguluka aku ngagai ai ti tenang.",
			"3": "<sup>3</sup> Iya ngasuh aku kering baru. Iya ngiring aku nengah jalai ti ngena, ketegal nama Iya.",
			"4": "<sup>4</sup> Taja pen aku bejalai nengah darung kelemayang pemati, aku enda takut, laban Nuan bisi enggau aku. Pemalu enggau tungkat Nuan ngasuh aku likun.",
			"5": "<sup>5</sup> Nuan nyendiaka pengerami makai besai ke aku di mua munsuh aku. Nuan nata pala aku ngena minyak lalu ngisi jalung aku penuh.",
			"6": "<sup>6</sup> Aku nemu, pemanah enggau pengerindu Nuan deka meruan enggau aku sepemanjai umur aku. Lalu aku deka diau dalam rumah TUHAN belama lama iya."
		},
		"24": {
			"1": "<sup>1</sup> Dunya enggau semua utai ti dalam dunya endang enggi TUHAN. Dunya enggau semua orang ke diau di dunya endang enggi TUHAN;",
			"2": "<sup>2</sup> laban Iya udah nudukka dunya ba tasik enggau ba sungai.",
			"3": "<sup>3</sup> Sapa tau niki ngagai bukit TUHAN? Sapa tau bediri dalam Rumah Sembiang Iya ti kudus?",
			"4": "<sup>4</sup> Orang ke tuchi jari, tuchi ati, ti enda nyembah engkeramba lalu enda ngaga semaya ti bula.",
			"5": "<sup>5</sup> TUHAN deka merekat lalu ngidupka sida. Allah Taala ke meri sida pengelepas, deka ngumbai sida nadai penyalah.",
			"6": "<sup>6</sup> Nya meh orang ke ngiga Iya, ke ngiga Allah Taala Jakop.",
			"7": "<sup>7</sup> Tukangka pintu kuta kita. Buka pintu ti lama, ngambika Raja ke bemulia ulih tama.",
			"8": "<sup>8</sup> Sapa Raja ke bemulia nya? Iya nya TUHAN, ke kering sereta bekuasa, TUHAN ti menang beperang.",
			"9": "<sup>9</sup> Tukangka pintu kuta kita. Buka pintu ti lama, ngambika Raja ke bemulia ulih tama.",
			"10": "<sup>10</sup> Sapa Raja ke bemulia nya? TUHAN ti Pemadu Bekuasa, Iya nya meh Raja ke bemulia!"
		},
		"25": {
			"1": "<sup>1</sup> O TUHAN, aku besampi ngagai Nuan.",
			"2": "<sup>2</sup> Aku manggaika pengarap aku ba Nuan, O Allah Taala aku. Anang nejuka aku alah enggai ke aku malu. Anang nejuka munsuh aku ketawaka aku!",
			"3": "<sup>3</sup> Orang ke manggaika pengarap diri ba Nuan, enda alah, tang orang ke angkat ngelaban Nuan enggau nadai kebuah, deka alah.",
			"4": "<sup>4</sup> Ajar aku jalai Nuan, O TUHAN. Awakka aku nemu jalai Nuan.",
			"5": "<sup>5</sup> Ajar meh aku diau nitihka pemendar Nuan, laban Nuan endang Allah Taala aku, ke ngelepaska aku. Aku endang seruran arapka Nuan.",
			"6": "<sup>6</sup> O TUHAN, kingatka penelap enggau pengerindu Nuan ti meruan belama iya, ti udah diayanka Nuan kenyau ari dulu kelia.",
			"7": "<sup>7</sup> Ampun meh dosa enggau penyalah ti udah dikereja aku lebuh aku agi biak. Kingatka aku, O TUHAN, ulih pengerindu Nuan ti meruan belama iya, enggau ulih pemadas Nuan!",
			"8": "<sup>8</sup> Laban TUHAN endang lurus sereta badas, Iya ngajar orang ke bedosa jalai ti patut ditengah sida.",
			"9": "<sup>9</sup> Iya nguluka orang ke baruh ati nengah jalai ti ngena lalu ngajar sida peneka Iya.",
			"10": "<sup>10</sup> Enggau ati ti tetap sereta enggau pengerindu, Iya nguluka semua orang ke nitihka sempekat Iya, sereta ngasika adat Iya.",
			"11": "<sup>11</sup> Kingatka semaya Nuan, O TUHAN, lalu ampun dosa aku, laban dosa aku mayuh.",
			"12": "<sup>12</sup> Orang ke nangika TUHAN deka belajar ari Iya jalai ti patut ditengah sida.",
			"13": "<sup>13</sup> Sida deka seruran kaya-raja, lalu bala anak sida deka bempu menua tu.",
			"14": "<sup>14</sup> TUHAN nyadika pangan orang ke ngasika Iya lalu Iya netapka sempekat Iya baru enggau sida.",
			"15": "<sup>15</sup> Aku endang seruran merening ngagai TUHAN, laban Iya deka ngelepaska aku ari panjuk.",
			"16": "<sup>16</sup> Malik ngagai aku, O TUHAN, lalu kasihka aku, laban aku seilu-ilu sereta kena perinsa.",
			"17": "<sup>17</sup> Buai pengirau ari ati aku lalu bai aku pansut ari semua penusah aku.",
			"18": "<sup>18</sup> Peda pemerinsa enggau penusah aku, lalu ampun semua dosa aku.",
			"19": "<sup>19</sup> Peda Nuan pemayuh munsuh aku. Peda Nuan pemalat sida begedika aku.",
			"20": "<sup>20</sup> Jaga aku lalu selamatka aku. Anang ngasuh aku malu laban aku ngenduh ngagai Nuan.",
			"21": "<sup>21</sup> Awakka pemanah enggau pemetul ati aku nyaga aku, laban aku manggaika pengarap aku ba Nuan.",
			"22": "<sup>22</sup> Lepaska Israel, O Allah Taala, ari semua penusah sida."
		},
		"26": {
			"1": "<sup>1</sup> Padahka aku tu nadai penyalah, O TUHAN, laban aku udah ngereja utai ti ngena lalu udah manggaika pengarap aku ba Nuan enggau pengabis ati aku.",
			"2": "<sup>2</sup> Peresa lalu abas meh ati aku, TUHAN. Peresa meh pengingin enggau runding aku.",
			"3": "<sup>3</sup> Laban pengerindu Nuan ti meruan belama iya ngiring aku, lalu aku nitihka Nuan enggau ati ti tetap. Tauka idup taluk ngagai Nuan.",
			"4": "<sup>4</sup> Aku enda begulai enggau orang ke bula, lalu aku enggai begulai enggau orang ke pura-pura ngapa.",
			"5": "<sup>5</sup> Aku begedi begulai enggau orang ke ngereja penyai lalu aku enggai duduk enggau orang ke jai.",
			"6": "<sup>6</sup> TUHAN, aku masu jari aku laban aku nadai penyalah lalu nyembah ba alta Nuan,",
			"7": "<sup>7</sup> belagu ngena pantun terima kasih lalu nusui pasal semua kereja ti nyelai ti udah dikereja Nuan.",
			"8": "<sup>8</sup> Aku rinduka rumah alai Nuan diau, O TUHAN, endur alai mulia Nuan.",
			"9": "<sup>9</sup> Anang muai aku baka Nuan ke muai orang ke bedosa. Anang nejuka aku diukum baka Nuan ke ngukum orang ke udah munuh orang,",
			"10": "<sup>10</sup> orang ke seruran ngereja penyai lalu selalu sedia deka makai suap.",
			"11": "<sup>11</sup> Tang aku ngereja utai ti ngena. Kasihka aku, lalu selamatka aku!",
			"12": "<sup>12</sup> Aku likun ari semua penusah. Aku muji TUHAN lebuh aku begempuru enggau bala nembiak Iya."
		},
		"27": {
			"1": "<sup>1</sup> TUHAN meh penampak enggau pengelepas aku. Aku enda takut. TUHAN nyadika kuta nyawa aku; nya alai aku enda takut.",
			"2": "<sup>2</sup> Lebuh orang ke jai ngamuk aku, deka makai aku, iya nya bala munsuh aku, sida deka tesangkut lalu rebah.",
			"3": "<sup>3</sup> Taja pen semua bala soldadu ngelulung aku, aku enda takut. Taja pen munsuh nyerang aku, aku deka mengkang berani.",
			"4": "<sup>4</sup> Aku udah minta siti utai ari TUHAN, utai ti digiga aku; iya nya, aku minta diri diau dalam rumah TUHAN seumur idup, ngambika aku ulih belematika pemadas TUHAN, lalu betanya ngagai Iya dalam Rumah Sembiang Iya.",
			"5": "<sup>5</sup> Laban Iya deka ngelindung aku lebuh maya tusah. Iya deka ngelalaika aku dalam Rumah Sembiang Iya lalu ngengkahka aku ba bukit batu.",
			"6": "<sup>6</sup> Nya alai aku deka ngalahka munsuh ke ngelulung aku. Enggau panjung gaga ati aku deka ngenjuk piring dalam Rumah Sembiang Iya. Aku deka belagu muji TUHAN.",
			"7": "<sup>7</sup> Dingaka aku, TUHAN, lebuh aku ngangauka Nuan! Kasihka aku, lalu saut meh aku!",
			"8": "<sup>8</sup> Lebuh Nuan nyebut, ‘Datai kitu lalu sembah Aku,’ aku nyaut, ‘Aku deka datai, TUHAN.’",
			"9": "<sup>9</sup> Anang ngelalaika Diri ari aku! Anang ringat ngagai aku. Anang nulak nembiak Nuan. Nuan udah nulung aku. Anang ninggalka aku. Anang ngelengkaka aku, O Allah Taala ti Penebus aku.",
			"10": "<sup>10</sup> Apai enggau indai aku nyangka deka ninggalka aku, tang TUHAN deka ngibun aku.",
			"11": "<sup>11</sup> Ajar meh aku, TUHAN, jalai Nuan, lalu uluka aku nengah jalai ti rata, laban aku mayuh munsuh.",
			"12": "<sup>12</sup> Anang nyerahka aku ngagai munsuh ke nyerang aku ngena pemula enggau jaku emba.",
			"13": "<sup>13</sup> Aku nemu diri deka idup lalu meda pemadas TUHAN dalam pengidup tu.",
			"14": "<sup>14</sup> Panggaika pengarap ba TUHAN. Kering sereta berani; Panggaika pengarap ba TUHAN."
		},
		"28": {
			"1": "<sup>1</sup> O TUHAN, ke penyaga aku, aku ngangauka Nuan. Pendingka sabak aku! Enti Nuan enda nyaut aku, aku deka nurun enggau orang ngagai dunya pemati.",
			"2": "<sup>2</sup> Dingaka aku lebuh aku nyabak ngagai Nuan minta tulung, lebuh aku ngangkatka jari aku ngagai Rumah Sembiang Nuan ti kudus.",
			"3": "<sup>3</sup> Anang ngukum aku baka Nuan ke ngukum orang ke jai, enggau orang ke ngereja penyai, ke bejaku manis ngagai pangan diri tang bisi pemenchi dalam ati sida.",
			"4": "<sup>4</sup> Ukum meh sida ketegal utai ti udah dikereja sida, ketegal penyai ti udah dikereja sida. Ukum meh sida ketegal semua pengawa sida. Beri ngagai sida ukum ti patut diterima sida!",
			"5": "<sup>5</sup> Sida nadai beratika utai ti udah dikereja TUHAN tauka utai ti udah digaga TUHAN. Nya alai TUHAN deka ngukum sida lalu ngerusak sida belama iya.",
			"6": "<sup>6</sup> Beri puji ngagai TUHAN laban Iya udah ningaka aku ke nyabak minta tulung.",
			"7": "<sup>7</sup> TUHAN ngelindung sereta nyaga aku. Aku manggaika pengarap aku ba Iya. Iya nulung aku, lalu ngasuh ati aku gaga. Aku belagu muji Iya ngena lagu gaga ati.",
			"8": "<sup>8</sup> TUHAN ngelindung bala nembiak Iya. Iya nyaga sereta ngidupka raja ti udah dipilih Iya.",
			"9": "<sup>9</sup> Lepaska bala nembiak Nuan, TUHAN, lalu berekatka sida ke enggi Nuan. Awakka Nuan nyadi pengibun sida, lalu ngibun sida belama iya."
		},
		"29": {
			"1": "<sup>1</sup> Puji meh TUHAN, kita orang serega. Puji meh mulia enggau kuasa Iya.",
			"2": "<sup>2</sup> Puji meh nama TUHAN ti bemulia. Meremi di mua Iya ti kudus lebuh Iya pegari.",
			"3": "<sup>3</sup> Nyawa TUHAN bisi didinga di tasik. Allah Taala ti bemulia bejaku munyi guntur, lalu munyi nyawa Iya belauh ba tasik.",
			"4": "<sup>4</sup> Nyawa TUHAN ti bekuasa sereta bemulia bisi didinga.",
			"5": "<sup>5</sup> Munyi nyawa TUHAN matahka kayu sidar, nyengkaum kayu sidar di Lebanon.",
			"6": "<sup>6</sup> Iya ngasuh semua bukit di menua Lebanon merejuk baka anak sapi lalu ngasuh Bukit Hermon engkechit baka sapi ti lelaki.",
			"7": "<sup>7</sup> Munyi nyawa TUHAN ngasuh kilat nyelar.",
			"8": "<sup>8</sup> Munyi nyawa Iya ngasuh menua puang berenyang. Iya ngusak menua puang Kades.",
			"9": "<sup>9</sup> Munyi nyawa TUHAN ngusak kayu oak lalu ngugurka daun kayu lebuh semua orang dalam Rumah TUHAN manjung nyebut, “Muliaka Allah Taala!”",
			"10": "<sup>10</sup> TUHAN merintah ai ti dalam. Iya nyadi Raja belama iya.",
			"11": "<sup>11</sup> TUHAN meri pengering ngagai bala nembiak Iya lalu meri sida pemaik."
		},
		"30": {
			"1": "<sup>1</sup> Aku muji Nuan, O TUHAN, laban Nuan udah ngidupka aku lalu enda nyagi munsuh aku ketawaka aku.",
			"2": "<sup>2</sup> Aku nyabak minta tulung Nuan, O TUHAN Allah Taala aku, lalu Nuan ngeraika aku.",
			"3": "<sup>3</sup> Nuan nagang aku mati; lebuh aku mansang ngagai dunya pemati, Nuan ngidupka aku baru.",
			"4": "<sup>4</sup> Belagu muji TUHAN semua kita ke nembiak Iya! Kingatka utai ti udah dikereja Iya ti Kudus, lalu beri terima kasih ngagai Iya!",
			"5": "<sup>5</sup> Iya ringat enda lama, tang pemadas Iya tan seumur idup. Ai mata nyangka lilih lebuh malam hari, tang pengaga datai lebuh pagi hari.",
			"6": "<sup>6</sup> Aku berasai likun, lalu bejaku dalam ati diri, ‘Aku sigi enda ulih dialahka munsuh.’",
			"7": "<sup>7</sup> Nuan udah manah enggau aku, TUHAN. Nuan udah nyaga aku baka kuta ti ba bukit. Tang Nuan ngelalaika Diri ari aku, lalu aku takut.",
			"8": "<sup>8</sup> Aku ngangauka Nuan, O TUHAN. Aku minta Nuan nulung aku,",
			"9": "<sup>9</sup> Nama penguntung Nuan enti aku mati? Nama penguntung Nuan enti aku dibuai ngagai kubur? Kati orang ke mati ulih muji Nuan? Kati sida ulih nusui pasal penetap ati Nuan?",
			"10": "<sup>10</sup> Dingaka aku, TUHAN, lalu kasihka aku! Tulung aku, TUHAN!",
			"11": "<sup>11</sup> Nuan udah ngubah ati aku ti tusah lalu ngasuh aku betanda gaga ati. Nuan udah muai ati aku ti tusah lalu malut aku ngena pengaga.",
			"12": "<sup>12</sup> Nya alai aku enda tau enda bemunyi. Aku deka belagu muji Nuan. O TUHAN, Nuan meh Allah Taala aku. Aku deka meri terima kasih ngagai Nuan belama iya."
		},
		"31": {
			"1": "<sup>1</sup> Aku datai ngagai Nuan, O TUHAN, minta Nuan nyaga aku. Anang nejuka aku alah. Nuan endang Allah Taala ti lurus. Aku besampi minta Nuan ngelepaska aku!",
			"2": "<sup>2</sup> Dingaka aku, lalu selamatka sida diatu! Awakka aku ngenduh ba Nuan lalu Nuan ngelindung aku, penyaga ke ngelepaska aku.",
			"3": "<sup>3</sup> Nuan nyadi endur alai aku ngenduh, nyadika penyaga aku. Iring lalu uluka aku, munyi ti udah disemaya Nuan.",
			"4": "<sup>4</sup> Jaga meh aku likun ari panjuk ti udah ditan orang kena sida nyintak aku, laban aku ngenduh ba Nuan.",
			"5": "<sup>5</sup> Aku nyerahka diri ngagai jari Nuan. Nuan udah nebus aku, O TUHAN, laban Nuan endang Allah Taala ti tetap ati.",
			"6": "<sup>6</sup> Nuan begedika orang ke nyembah engkeramba, tang aku manggaika pengarap aku ba TUHAN.",
			"7": "<sup>7</sup> Aku deka lantang sereta rami gaga ketegal pengerindu Nuan ti meruan belama iya, laban Nuan udah meda pemerinsa aku. Nuan nemu penusah aku.",
			"8": "<sup>8</sup> Nuan nadai kala nyerahka aku ngagai jari munsuh aku. Nuan udah ngemendarka aku bejalai ngagai sebarang endur aku deka.",
			"9": "<sup>9</sup> Kasihka aku, O TUHAN, laban aku dituntung penusah. Mata aku nyau bangkung laban ati aku balat tusah. Aku tu lelak amat.",
			"10": "<sup>10</sup> Aku lelak laban ati aku balat tusah, lalu umur aku nyau pandak laban aku udu nyabak. Aku lembut tulang laban semua penusah aku. Indah tulang aku deh nyau lupis.",
			"11": "<sup>11</sup> Semua munsuh aku, lebih agi orang ke berimbai pendiau enggau aku, kejika aku. Orang ke ngelala aku, takutka aku. Lebuh sida meda aku ba jalai raya, sida rari.",
			"12": "<sup>12</sup> Aku nyau baka orang ke udah mati, ti enda dikingatka orang agi. Aku tu baka periuk ti udah pechah.",
			"13": "<sup>13</sup> Aku ninga mayuh munsuh betelai. Aku dikelingi utai ti balat ngenakut. Sida ngiga chara kena ngelaban aku, bepekat deka munuh aku.",
			"14": "<sup>14</sup> Tang aku manggaika pengarap aku ba Nuan, O TUHAN, laban Nuan endang Allah Taala aku.",
			"15": "<sup>15</sup> Aku endang seruran diibun Nuan. Selamatka aku ari bala munsuh aku, ari orang ke merinsaka aku.",
			"16": "<sup>16</sup> Perening ngagai nembiak Nuan enggau penelap. Selamatka aku laban pengerindu Nuan endang meruan.",
			"17": "<sup>17</sup> Aku ngangauka Nuan, O TUHAN. Anang nejuka aku dipechat. Awakka orang ke jai, dipechat. Awakka sida enda bemunyi mansang ngagai dunya pemati.",
			"18": "<sup>18</sup> Padamka jaku orang ke bula, semua orang ke sumbung, enggau tinggi ati, ke bejaku enda bebasaka orang ke lurus.",
			"19": "<sup>19</sup> Naka meh pemanah utai ti badas ti disimpan Nuan ke orang ke ngemesaika Nuan! Semua orang nemu pemadas Nuan, pasal Nuan ke nyaga enggau likun orang ke manggaika pengarap sida ba Nuan.",
			"20": "<sup>20</sup> Nuan ngelalaika sida ba endur Nuan Empu lalu ngibun sida likun ari akal orang bukai. Nuan ngelalaika sida ngambika sida likun ari jaku pechat munsuh.",
			"21": "<sup>21</sup> Puji TUHAN! Naka meh pemanah Iya ngayanka pengerindu Iya ke aku lebuh aku dikelulung sereta diserang munsuh!",
			"22": "<sup>22</sup> Aku takut, lalu berunding ngumbai Iya udah muai aku ari Iya. Tang Iya ninga sabak aku, lebuh aku ngangauka Iya, minta tulung.",
			"23": "<sup>23</sup> Rindu meh kita ke TUHAN, semua kita ke nembiak Iya. TUHAN nyaga orang ke tetap ati, tang ngukum orang ke sumbung nitihka penyumbung sida.",
			"24": "<sup>24</sup> Seruran kering, seruran berani, semua kita ke andal ati ke TUHAN."
		},
		"32": {
			"1": "<sup>1</sup> Beberekat meh orang lebuh dosa enggau penyalah sida diampun.",
			"2": "<sup>2</sup> Beberekat meh orang ke enda dikumbai TUHAN bisi penyalah lalu enda nipu orang.",
			"3": "<sup>3</sup> Lebuh aku enda ngaku dosa aku, aku lelak nyabak sepemanjai hari.",
			"4": "<sup>4</sup> Siang malam Nuan ngukum aku, TUHAN. Aku nyau nadai pengering agi, baka ai ti rangkai laban panas.",
			"5": "<sup>5</sup> Udah nya aku ngaku dosa aku ngagai Nuan, lalu aku enda ngelalaika penyalah diri. Aku ngaku utai nya ngagai Nuan, lalu Nuan ngampun semua dosa aku.",
			"6": "<sup>6</sup> Nya alai semua nembiak Nuan ke taluk ngagai Nuan patut besampi ngagai Nuan lebuh sida dituntung penusah ngambika penusah nya enda datai ba sida.",
			"7": "<sup>7</sup> Nuan nyadi endur alai aku belalai. Nuan deka ngelepaska aku ari penusah. Aku belagu enggau nyawa ti inggar muji pengelepas ti datai ari Nuan, laban Nuan nyaga aku.",
			"8": "<sup>8</sup> Ku TUHAN bejaku, “Aku deka nunjukka jalai ti patut ditengah nuan. Aku deka ngajar sereta meri nuan jaku lalau.",
			"9": "<sup>9</sup> Anang beli baka kuda tauka baka keledai, ti enda tau enda ditarit ngena repik besi enggau tali ba mulut iya ngambika iya deka nurut.",
			"10": "<sup>10</sup> Orang ke jai sigi enda tau enda natka pemerinsa, tang orang ke arapka TUHAN deka dijaga ulih pengerindu Iya ti meruan belama iya.",
			"11": "<sup>11</sup> Lantang sereta rami gaga meh kita ke lurus ketegal utai ti udah dikereja TUHAN. Manjung gaga ati meh kita ke ngasika Iya!"
		},
		"33": {
			"1": "<sup>1</sup> Rami gaga ati meh kita ke lurus. Puji meh Iya, semua kita ke ngasika Iya.",
			"2": "<sup>2</sup> Beri terima kasih ngagai TUHAN ngena belikan. Belagu ke Iya ngena sape.",
			"3": "<sup>3</sup> Nyanyi lagu ti baru ke Iya. Tebah belikan enggau pengabis pengelandik kita, sereta enggau panjung kita!",
			"4": "<sup>4</sup> Laban jaku TUHAN endang amat, lalu semua pengawa Iya endang dikereja Iya enggau ati ti tetap.",
			"5": "<sup>5</sup> TUHAN rinduka atur ti lurus sereta betul. Dunya penuh laban pengerindu Iya ti meruan belama iya.",
			"6": "<sup>6</sup> TUHAN bejaku, langit lalu nyadi, mata hari, bulan enggau bintang nyadi laban jaku Iya.",
			"7": "<sup>7</sup> Iya ngelumuka semua ai tasik ba siti endur. lalu nyimpan tasik ti besai ba endur ti dalam.",
			"8": "<sup>8</sup> Nangika TUHAN meh semua kita dunya! Awakka semua bansa di dunya nangika Iya!",
			"9": "<sup>9</sup> Lebuh Iya bejaku, dunya nyadi. Lebuh Iya bejaku, semua utai pegari.",
			"10": "<sup>10</sup> TUHAN ngemuntanka juluk ati semua bansa. Iya nagang sida bejalaika pelan sida.",
			"11": "<sup>11</sup> Tang pelan Iya Empu tan belama iya. Juluk ati Iya meruan belama iya.",
			"12": "<sup>12</sup> Lantang meh bansa ti ngaku TUHAN nyadika Allah Taala sida. Beberekat meh orang ke udah dipilih TUHAN nyadika nembiak Iya!",
			"13": "<sup>13</sup> TUHAN nilik ke baruh ari serega lalu meda semua kitai mensia.",
			"14": "<sup>14</sup> Ari endur alai Iya ke merintah, Iya nilik ke baruh ngagai semua mensia ti diau di dunya.",
			"15": "<sup>15</sup> Iya nempa semua runding sida lalu nemu semua utai ti dikereja sida.",
			"16": "<sup>16</sup> Raja ukai menang laban pemayuh bala soldadu iya. Soldadu ukai menang ulih pengering sida.",
			"17": "<sup>17</sup> Kuda ti dikena beperang enda ulih ngasuh menang. Pengering sida enda ulih ngidupka sida.",
			"18": "<sup>18</sup> TUHAN ngemataka semua orang ke ngasika Iya, ke manggaika diri ba pengerindu Iya ti meruan belama iya.",
			"19": "<sup>19</sup> Iya ngelepaska sida ari pemati. Iya ngidupka sida lebuh maya lapar pungkang.",
			"20": "<sup>20</sup> Kitai andal ati ke TUHAN. Iya nyaga sereta nyaup kitai.",
			"21": "<sup>21</sup> Kitai gaga ati ketegal Iya. Kitai manggaika pengarap kitai ba nama Iya ti kudus.",
			"22": "<sup>22</sup> Awakka pengerindu Nuan meruan enggau kami, O TUHAN, lebuh kami manggaika pengandal ati kami ba Nuan."
		},
		"34": {
			"1": "<sup>1</sup> Aku deka seruran meri terima kasih ngagai TUHAN. Aku enggai badu muji Iya.",
			"2": "<sup>2</sup> Aku deka muji Iya ketegal utai ti udah dikereja Iya. Awakka semua orang ke kena tekan, mending lalu gaga ati!",
			"3": "<sup>3</sup> Aram kitai nusui pemesai kuasa TUHAN. Aram kitai sama sereta muji nama Iya!",
			"4": "<sup>4</sup> Aku besampi ngagai TUHAN, lalu Iya nyaut aku. Iya ngelepaska aku ari semua penakut aku.",
			"5": "<sup>5</sup> Orang ke kena tekan engkadah ngagai Iya, lalu gaga ati. Sida enda asa ngapa.",
			"6": "<sup>6</sup> Orang ke seranta ngangauka Iya, lalu Iya nyaut. Iya ngelepaska sida ari semua penusah sida.",
			"7": "<sup>7</sup> Melikat TUHAN nyaga orang ke ngemesaika TUHAN lalu ngelepaska sida ari utai ti tau nganu sida.",
			"8": "<sup>8</sup> Awakka kita empu ngasaika pemadas TUHAN. Beberekat meh orang ke nemu pengelikun lebuh sida begulai enggau Iya.",
			"9": "<sup>9</sup> Nangika TUHAN, semua kita ke nembiak Iya, laban orang ke ngasika Iya, nadai kala suntuk utai.",
			"10": "<sup>10</sup> Indah singa deh bejalai lapar laban sida nadai pemakai, tang orang ke ngasika TUHAN, nadai suntuk utai.",
			"11": "<sup>11</sup> Datai kitu, bala kita anak, lalu pendingka aku. Aku deka ngajar kita jalai ngemesaika TUHAN.",
			"12": "<sup>12</sup> Kati kita minta diri nyamai idup? Kati kita minta diri panjai umur lalu lantang diau?",
			"13": "<sup>13</sup> Enti kita dekaka nya, anang bejaku jai lalu anang bula.",
			"14": "<sup>14</sup> Lengkaka penyai, lalu kereja utai ti badas. Giga pemaik enggau pengabis ati kita.",
			"15": "<sup>15</sup> TUHAN ngemataka orang ke lurus lalu mendingka sabak sida.",
			"16": "<sup>16</sup> Tang Iya ngelaban orang ke ngereja penyai, ngambika lebuh sida mati, sida enda dikingatka agi.",
			"17": "<sup>17</sup> Orang ke lurus ngangauka TUHAN, lalu TUHAN ninga, lalu Iya ngelepaska sida ari semua penusah sida.",
			"18": "<sup>18</sup> TUHAN diau semak orang ke tusah ati lalu ngidupka sida ke nadai agi pengadang.",
			"19": "<sup>19</sup> Orang ke badas natka mayuh macham penusah, tang TUHAN ngelepaska sida ari semua penusah nya.",
			"20": "<sup>20</sup> TUHAN nyaga sida. Siti pen nadai meh tulang sida patah.",
			"21": "<sup>21</sup> Penyai deka munuh orang ke jai. Orang ke begedika orang ke lurus, deka diukum.",
			"22": "<sup>22</sup> TUHAN deka nebus bala nembiak Iya. Nadai siku orang ke ngenduh ngagai TUHAN deka diukum."
		},
		"35": {
			"1": "<sup>1</sup> Laban meh orang ke ngelaban aku, O TUHAN; serang meh orang ke nyerang aku!",
			"2": "<sup>2</sup> Bai terabai enggau senyata Nuan, lalu tulung aku.",
			"3": "<sup>3</sup> Kedarka sangkuh enggau kapak perang Nuan ngelaban orang ke begagaika aku. Aku minta Nuan besemaya ngidupka aku.",
			"4": "<sup>4</sup> Awakka orang ke nguji munuh aku alah lalu dipechat! Awakka orang ke bepekat ngelaban aku, nikal sereta charut runding!",
			"5": "<sup>5</sup> Awakka sida baka rumput ti rangkai ti dikebuka ribut, lebuh melikat TUHAN muru sida!",
			"6": "<sup>6</sup> Awakka jalai ti ditengah sida petang sereta lichin, lebuh melikat TUHAN begagaika sida!",
			"7": "<sup>7</sup> Laban sida manjuk aku enggau nadai kebuah, lalu numbak lubang ti dalam kena sida nangkap aku.",
			"8": "<sup>8</sup> Awakka pengerusak ninggang sida lebuh sida enda ngadang. Lalu awakka sida disintak panjuk sida empu lalu dirusak!",
			"9": "<sup>9</sup> Nya baru aku deka gaga ati ke TUHAN, gaga ati laban Iya udah ngelepaska aku.",
			"10": "<sup>10</sup> Enggau pengabis ati aku, aku deka nyebut ngagai TUHAN, “Nadai orang ke sebaka enggau Nuan, O TUHAN. Nuan nyaga orang ke lemi, ari orang ke kering, orang ke seranta, ari orang ke nekanka pengidup sida.”",
			"11": "<sup>11</sup> Orang ke jai besaksi ngelaban aku lalu bedawa ngumbai aku ngereja penyalah ti enda ditemu aku.",
			"12": "<sup>12</sup> Sida malas pemanah aku ngena penyai, lalu ati aku balat tusah.",
			"13": "<sup>13</sup> Tang lebuh sida sakit, aku ngerasukka pekayan ulit. Aku bepasa. Aku besampi ngendukka pala,",
			"14": "<sup>14</sup> baka ke besampika pangan tauka menyadi. Aku bejalai nensam laban aku ngulit, baka orang ke ngulitka indai diri.",
			"15": "<sup>15</sup> Tang lebuh aku kena laban penusah, semua sida gaga ati, lalu begempuru ngelingi aku, lalu beduan aku. Orang kampar bepaluka aku, lalu majak bepaluka aku.",
			"16": "<sup>16</sup> Sida ngeleseka aku enda ngetu-ngetu, lalu sida begerit ngeli meda aku.",
			"17": "<sup>17</sup> Ni pengelama Nuan semina merening aja, O TUHAN? Selamatka aku ari serang sida. Selamatka aku ari bala singa tu!",
			"18": "<sup>18</sup> Nya baru aku deka meri terima kasih ngagai Nuan lebuh bala nembiak Nuan begempuru. Aku deka muji Nuan di mua semua sida.",
			"19": "<sup>19</sup> Anang nejuka bala munsuh aku, orang ke bula nya, ketawa meda aku alah. Anang nejuka orang ke begedika aku enggau nadai kebuah, tuchum gaga ati meda aku balat tusah.",
			"20": "<sup>20</sup> Sida nadai nyebut utai ti manah, tang nempaka berita ti bula pasal orang ke rinduka pemaik.",
			"21": "<sup>21</sup> Ku sida manjung bedawaka aku, “Kami bisi meda utai ti udah dikereja nuan!”",
			"22": "<sup>22</sup> Tang Nuan, O TUHAN, udah meda nya. Nya alai, anang enda bemunyi, TUHAN. Anang jauh ari aku!",
			"23": "<sup>23</sup> Angkat meh Nuan, O TUHAN, lalu jaga aku. Angkat meh Nuan, O Allah Taala aku, lalu bejaku ngarika aku.",
			"24": "<sup>24</sup> Nuan endang lurus, O TUHAN, nya alai bejaku meh Nuan madahka aku tu nadai penyalah. Anang nejuka munsuh aku ketawaka aku.",
			"25": "<sup>25</sup> Anang nejuka sida bejaku enggau pangan diri, “Kitai udah muai iya! Nya aja utai ti dikedekaka kitai!”",
			"26": "<sup>26</sup> Awakka orang ke ketawaka pemerinsa aku alah sereta charut runding. Awakka orang ke ngumbai diri badas agi ari aku, malu sereta dipechat.",
			"27": "<sup>27</sup> Awakka orang ke ingin deka meda aku dikumbai nadai penyalah, manjung gaga ati, lalu enda ngetu nyebut, “Naka meh pemesai kuasa TUHAN! Iya gaga ati meda nembiak Iya mujur.”",
			"28": "<sup>28</sup> Nya baru aku deka nusui pasal pengelurus Nuan, lalu muji Nuan sepemanjai hari."
		},
		"36": {
			"1": "<sup>1</sup> Dosa bejaku terus ngagai buntut ati orang ke jai. Sida nulak Allah Taala, lalu enda nangika Iya.",
			"2": "<sup>2</sup> Laban sida balat muji diri, lalu ngumbai Allah Taala enda nemu lalu begedika dosa sida.",
			"3": "<sup>3</sup> Mulut sida penuh laban akal ti jai enggau jaku ti nipu. Sida enggai agi bekira ngereja utai ti manah.",
			"4": "<sup>4</sup> Lebuh sida gali ba peninduk sida, sida berundingka pelan kena ngereja penyai; Utai ti dikereja sida nadai manah siti, lalu sida nadai kala nulak penyai.",
			"5": "<sup>5</sup> O TUHAN, pengerindu Nuan ti meruan belama iya endang tinggi agi ari langit. Penetap ati Nuan tinggi agi ari remang.",
			"6": "<sup>6</sup> Pengelurus Nuan tinggi baka bukit ti pemadu tinggi. Pengelurus atur Nuan baka penalam tasik. Mensia enggau jelu diibun Nuan magang.",
			"7": "<sup>7</sup> Naka meh pemadas pengerindu Nuan ti meruan belama iya, O Allah Taala! Kami belindung di baruh sayap Nuan.",
			"8": "<sup>8</sup> Kami makai mayuh macham pemakai ti disediaka Nuan ke kami. Nuan meri kami ai irup ari sungai Nuan ti manah.",
			"9": "<sup>9</sup> Nuan endang pun pengidup, lalu kami ulih meda penampak laban penampak Nuan.",
			"10": "<sup>10</sup> Awakka Nuan seruran rinduka orang ke nemu Nuan, lalu ngereja utai ti manah ngagai orang ke lurus.",
			"11": "<sup>11</sup> Anang nejuka orang ke sumbung ngamuk aku, tauka nejuka orang ke jai muru aku.",
			"12": "<sup>12</sup> Peda endur alai orang ke jai udah rebah. Dia meh endur alai sida garing-garing, enda ulih angkat agi."
		},
		"37": {
			"1": "<sup>1</sup> Anang tusah ati ketegal orang ke jai. Anang ngining orang ke ngereja penyalah.",
			"2": "<sup>2</sup> Enda lama da agi sida deka lesap baka rumput, layu baka babas.",
			"3": "<sup>3</sup> Panggaika pengarap kita ba TUHAN, lalu kereja utai ti badas; nya baru kita deka diau di menua tu, lantang sereta likun.",
			"4": "<sup>4</sup> Gaga ati ke TUHAN, lalu Iya deka meri nuan utai ti dikinginka nuan.",
			"5": "<sup>5</sup> Serahka pengidup nuan ngagai TUHAN. Panggaika pengarap nuan ba Iya, lalu Iya deka nulung nuan.",
			"6": "<sup>6</sup> Iya deka ngasuh pemadas nuan manchar baka penampak, lalu ngasuh pengelurus nuan manchar terang baka penampak tengah hari.",
			"7": "<sup>7</sup> Diau jenuh di mua TUHAN, lalu anti Iya enggau ati ti liat. Anang irau ketegal orang bukai mujur, tauka ketegal orang bejalaika pelan sida ke jai.",
			"8": "<sup>8</sup> Anang ringat, lalu badu agi pedis ati! Anang irau, laban pengirau semina ngujung ngagai penyai.",
			"9": "<sup>9</sup> Laban orang ke jai deka diburu; tang orang ke nganti TUHAN, deka bempu menua tu.",
			"10": "<sup>10</sup> Enda lama da agi orang ke jai deka abis; taja pen kita bebendar begigaka sida, tang kita nadai tetemuka sida.",
			"11": "<sup>11</sup> Tang orang ke baruh ati deka bempu menua tu lalu diau lantang ngena pengaya sida.",
			"12": "<sup>12</sup> Orang ke jai kerik-kerik ngeli bepekat ngelaban orang ke lurus;",
			"13": "<sup>13</sup> tang TUHAN ketawaka orang ke jai, laban Iya nemu timpuh sida nyau semak.",
			"14": "<sup>14</sup> Orang ke jai nyintak pedang, lalu narit panah sida deka munuh orang ke seranta enggau orang ke suntuk, deka munuh orang ke lurus;",
			"15": "<sup>15</sup> tang pedang sida deka ngerejang ati sida empu, lalu panah sida deka dipatahka.",
			"16": "<sup>16</sup> Manah agi diau lurus lalu bempu utai ti mimit aja, ari ke bempu mayuh pengaya tang nyadi orang ke jai;",
			"17": "<sup>17</sup> laban TUHAN deka muai kuasa orang ke jai, tang nyaga orang ke lurus.",
			"18": "<sup>18</sup> TUHAN nyaga orang ke nadai penyalah, lalu sida deka bempu menua tu belama iya.",
			"19": "<sup>19</sup> Sida enda merinsa lebuh maya ti jai, lalu lebuh lapar pungkang nyadi, sida deka mayuh pemakai.",
			"20": "<sup>20</sup> Tang orang ke jai deka mati, lalu bala munsuh TUHAN deka nyadi baka bungai babas; Sida deka lesap baka asap.",
			"21": "<sup>21</sup> Orang ke jai nginjau utai, lalu enda mayar utai nya pulai baru, tang orang ke lurus meri enggau ati ti gerah mudah lalu majak meri;",
			"22": "<sup>22</sup> laban orang ke diberekatka TUHAN, deka bempu menua tu, tang orang ke disumpah TUHAN, deka diburu ari menua tu.",
			"23": "<sup>23</sup> Singkang kitai diiring TUHAN, lebuh Iya gaga ati ke ulah kitai;",
			"24": "<sup>24</sup> taja pen kitai tesangkut, tang kitai enda rebah, laban TUHAN deka megai jari kitai.",
			"25": "<sup>25</sup> Lebuh aku agi biak, enggau lebuh aku nyau tuai, aku nadai kala meda orang ke lurus ditinggalka TUHAN, tauka meda bala anak sida nyanda pemakai.",
			"26": "<sup>26</sup> Sida endang seruran meri orang utai enggau ati ti gerah mudah, lalu nyali utai ngagai orang, lalu bala anak sida nyadika berekat.",
			"27": "<sup>27</sup> Lengkaka penyai, lalu kereja pemadas; ngambika kita diau di menua tu belama iya,",
			"28": "<sup>28</sup> laban TUHAN rinduka atur ti lurus; Iya enda ninggalka bala nembiak Iya ke tetap ati ngagai Iya. Orang ke lurus deka dijaga Iya belama iya, tang peturun orang ke jai, deka diburu.",
			"29": "<sup>29</sup> Orang ke lurus deka bempu menua tu lalu diau ditu belama iya.",
			"30": "<sup>30</sup> Mulut orang ke lurus mansutka penemu-dalam, lalu dilah sida seruran bejakuka atur ti lurus.",
			"31": "<sup>31</sup> Adat Allah Taala endang meruan dalam ati sida lalu sida enda limpang ari adat nya.",
			"32": "<sup>32</sup> Orang ke jai nengkilunka orang ke lurus deka nguji munuh sida,",
			"33": "<sup>33</sup> tang TUHAN enggai nyerahka orang ke lurus ngagai jari munsuh sida, tauka nejuka sida diukum lebuh sida kena pechara.",
			"34": "<sup>34</sup> Anti meh TUHAN, lalu titihka jalai Iya, lalu Iya deka ngangkatka kita, ngambika kita bempu menua tu; kita deka meda orang ke jai nya dirusak.",
			"35": "<sup>35</sup> Aku bisi udah kala meda orang ke jai nekanka pengidup orang, ngeninggika diri baka kayu sidar ti di menua Lebanon.",
			"36": "<sup>36</sup> Tang lebuh aku mansa udah lepas nya, sida nadai agi dia; taja pen aku begigaka sida, tang aku nadai tetemuka sida.",
			"37": "<sup>37</sup> Peratika orang ke nadai penyalah. Peratika orang ke lurus, laban orang ke diau bebaik enggau orang bukai, deka bisi peturun.",
			"38": "<sup>38</sup> Tang orang ke ngereja penyalah, deka dirusak magang, lalu peturun sida deka diburu.",
			"39": "<sup>39</sup> TUHAN ngelepaska orang ke lurus lalu nyaga sida lebuh penusah nyadi.",
			"40": "<sup>40</sup> TUHAN nulung sida, lalu ngelepaska sida. Iya ngelepaska sida ari orang ke jai, lalu nyelamatka sida, laban sida belindung ba Iya."
		},
		"38": {
			"1": "<sup>1</sup> O TUHAN, anang ringat lalu anang nganu aku!",
			"2": "<sup>2</sup> Nuan udah ngemakalka aku ngena laja Nuan. Nuan udah ngerebahka aku.",
			"3": "<sup>3</sup> Aku pedis laban Nuan ringat ngagai aku. Tubuh aku abis diempa penyakit laban dosa aku.",
			"4": "<sup>4</sup> Aku lemas dilamun bah dosa aku. Dosa aku kelalu berat.",
			"5": "<sup>5</sup> Laban aku udah beli, rumbak aku nyau bau but.",
			"6": "<sup>6</sup> Aku nyau bungkuk, lalu kunduk-kunduk. Aku ngulit sepemanjai hari.",
			"7": "<sup>7</sup> Aku sakit chelap-angat lalu aku nyau deka mati.",
			"8": "<sup>8</sup> Aku balat lelak, sereta nadai daya agi. Ati aku tusah, lalu aku bedemuh laban pedis.",
			"9": "<sup>9</sup> O TUHAN, Nuan nemu utai ti dikinginka aku. Nuan ninga semua demuh aku.",
			"10": "<sup>10</sup> Tungkul ati aku ketap-ketap. Aku nadai agi pengering, lalu mata aku nyau kabur.",
			"11": "<sup>11</sup> Bala pangan aku, enggau orang ke berimbai pendiau enggau aku, enggai datai semak ngagai aku, ketegal rumbak aku. Indah kami sebilik deh enggai datai semak aku.",
			"12": "<sup>12</sup> Orang ke deka munuh aku, nan panjuk kena sida nyintak aku, lalu orang ke deka ngemediska aku, ngemba madahka diri deka ngerusak aku. Sida nadai nemu badu bepekat ngelaban aku.",
			"13": "<sup>13</sup> Aku tu baka orang ke bengal lalu enda ulih ninga, baka orang ke bisu lalu enda ulih bejaku.",
			"14": "<sup>14</sup> Aku tu baka orang ke enda nyaut, laban sida enda ulih ninga.",
			"15": "<sup>15</sup> Tang aku manggaika pengarap aku ba Nuan, O TUHAN, lalu Nuan, O TUHAN Allah Taala aku, deka nyaut aku.",
			"16": "<sup>16</sup> Anang nejuka munsuh aku ketawaka penusah aku. Anang nejuka sida temegah ketegal aku alah!",
			"17": "<sup>17</sup> Aku nyau deka rebah lalu tubuh aku seruran pedis.",
			"18": "<sup>18</sup> Aku ngaku dosa aku. Dosa aku ngasuh ati aku irau.",
			"19": "<sup>19</sup> Bala munsuh aku bepengerai sereta kering. Mayuh orang begedika aku enggau nadai kebuah.",
			"20": "<sup>20</sup> Orang ke malas pemanah ngena penyai, ngelaban aku ketegal aku nguji ngereja utai ti ngena.",
			"21": "<sup>21</sup> Anang ninggalka aku, O TUHAN. O Allah Taala aku, anang jauh ari aku!",
			"22": "<sup>22</sup> Tulung meh aku diatu, O TUHAN ti Penebus aku!"
		},
		"39": {
			"1": "<sup>1</sup> Ku aku bejaku, “Aku deka bejimat nyaga ulah diri enggai ke dilah aku mai aku bedosa. Aku enggai nyebut utai lebuh orang ke jai bisi semak aku.”",
			"2": "<sup>2</sup> Aku enda bemunyi, enda bejaku. Nyebut pasal utai ti manah pen enda. Tang pemerinsa aku semina mansang balat-sebalat,",
			"3": "<sup>3</sup> lalu ati aku kelalu balat irau. Makin balat agi aku berunding, makin balat agi ga ati aku tusah. Aku nyau enda ulih nagang diri nanya:",
			"4": "<sup>4</sup> “TUHAN, ni pengelama aku agi idup? Kemaya aku mati? Padahka maya seput aku deka abis.”",
			"5": "<sup>5</sup> Naka meh pemandak umur aku diberi Nuan! Ba Nuan, umur aku endang amat pandak. Iya ti bendar, semua utai ti idup nadai lebih ari kesuh ribut,",
			"6": "<sup>6</sup> baka ayuk. Semua utai ti dikereja kami, nadai guna magang. Kami nugungka pengaya, tang enda nemu sapa ti deka bempu utai nya.",
			"7": "<sup>7</sup> Enti pia, nama utai ti patut diadang aku, O TUHAN? Aku manggaika pengandal aku ba Nuan.",
			"8": "<sup>8</sup> Selamatka aku ari semua dosa aku, lalu anang nejuka orang ke beli beduan aku.",
			"9": "<sup>9</sup> Aku enggai agi bemunyi. Aku enggai agi mansutka jaku, laban Nuan Empu ke ngasuh aku merinsa baka tu.",
			"10": "<sup>10</sup> Anang agi nganu aku! Aku nyau deka mati dipalu Nuan.",
			"11": "<sup>11</sup> Nuan ngukum dosa kami ngena jaku anu Nuan, lalu Nuan baka indu gegat, ngerusak utai ti dikerinduka kami. Iya ti bendar, kami tu nadai lebih ari kesuh ribut!",
			"12": "<sup>12</sup> Dingaka sampi aku, O TUHAN, lalu pendingka sabak aku. Datai lalu tulung aku lebuh aku nyabak. Baka semua aki ini aku, Aku tu semina nyadi temuai Nuan enda lama aja.",
			"13": "<sup>13</sup> Awakka aku diau kediri, ngambika aku tau lantang mimit sebedau aku mupuk, lalu nadai agi."
		},
		"40": {
			"1": "<sup>1</sup> Aku nganti TUHAN enggau ati ti liat. Iya nyingitka pending Iya, lalu ninga sabak aku.",
			"2": "<sup>2</sup> Iya narit aku pansut ari lubang pemati, ari tanah pasir ti tau nginsap lalu munuh. Iya ngengkahka aku ba atas batu lalu nyaga aku.",
			"3": "<sup>3</sup> Iya ngajar aku nyanyi lagu ti baru, lagu ti dikena muji Allah Taala kitai. Mayuh orang ke meda utai tu deka takut lalu deka manggaika pengarap sida ba TUHAN.",
			"4": "<sup>4</sup> Beberekat meh orang ke manggaika pengarap ba TUHAN, ti enda nyembah engkeramba tauka enggau orang besembahka petara ti pelesu.",
			"5": "<sup>5</sup> Nuan udah ngereja mayuh macham utai ke kami, O TUHAN Allah Taala kami. Nadai orang sebaka enggau Nuan! Nuan udah ngaga mayuh bengkah pelan ti manah ke kami. Aku enda ulih ngenang semua pelan nya, laban pelan nya kelalu mayuh!",
			"6": "<sup>6</sup> Nuan enda begunaka piring enggau pemeri. Nuan nadai minta jelu ti ditunu segala ba alta tauka minta piring pengampun dosa. Tang Nuan udah meri aku pending kena aku ningaka Nuan",
			"7": "<sup>7</sup> nya alai aku nyaut, “Tu aku. Atur ti diberi Nuan ngagai aku bisi dalam Bup Adat.",
			"8": "<sup>8</sup> Naka meh pengerindu ati aku ngereja peneka Nuan, O Allah Taala aku! Aku nyimpan ajar Nuan dalam ati aku.”",
			"9": "<sup>9</sup> Lebuh semua nembiak Nuan begempuru, O TUHAN, aku madahka berita manah pasal Nuan ke ngelepaska kami. Nuan nemu, aku sigi enggai badu nusui berita nya.",
			"10": "<sup>10</sup> Aku nadai kala ngelalaika berita pasal pengelepas dalam ati aku. Aku endang seruran bejakuka pasal penetap ati enggau pengelepas Nuan. Lebuh semua nembiak Nuan begempuru, aku nadai kala enda bejakuka pasal penaluk enggau pengerindu Nuan ti meruan belama iya.",
			"11": "<sup>11</sup> O TUHAN, aku nemu Nuan enggai badu kasihka aku. Pengerindu enggau penaluk Nuan deka seruran nyaga aku.",
			"12": "<sup>12</sup> Aku dikelulung mayuh macham penusah ti enda ulih diitung! Dosa aku udah tetitihka aku, lalu aku enda ulih meda agi. Sida mayuh agi ari lambar buk ti ba pala aku, lalu aku nyau nadai pemerani agi.",
			"13": "<sup>13</sup> Selamatka aku, O TUHAN! Tulung meh aku enggau naka penyampat!",
			"14": "<sup>14</sup> Awakka orang ke nguji munuh aku alah sereta charut runding. Awakka sida ke lantang ati ketegal penusah aku nikal lalu dipechat.",
			"15": "<sup>15</sup> Awakka orang ke beduan aku tusah ati alah.",
			"16": "<sup>16</sup> Awakka semua orang ke datai ngagai Nuan lantang sereta gaga ati. Awakka semua orang ke meri terima kasih ketegal pengelepas ti diberi Nuan seruran nyebut, “Naka meh pemesai mulia TUHAN!”",
			"17": "<sup>17</sup> Aku lemi sereta seranta, O TUHAN, tang Nuan nadai kala enda ingatka aku. Nuan meh nyadi Penebus enggau Allah Taala aku. Datai jampat lalu tulung aku!"
		},
		"41": {
			"1": "<sup>1</sup> Beberekat meh orang ke nemu siru enggau orang ke seranta, laban TUHAN deka nulung sida lebuh sida dituntung penusah.",
			"2": "<sup>2</sup> TUHAN deka nyaga sida lalu ngidupka sida. Iya deka ngasuh sida diau lantang senang di menua Israel. Iya enggai nejuka sida pedis laban munsuh.",
			"3": "<sup>3</sup> TUHAN deka nulung sida lebuh sida sakit lalu deka ngeraika sida.",
			"4": "<sup>4</sup> Ku aku bejaku, “Aku udah bedosa ngelaban Nuan, O TUHAN. Kasihka aku, lalu geraika aku.”",
			"5": "<sup>5</sup> Bala munsuh aku nyebut utai ti jai pasal aku. Sida keran deka meda aku mati lalu enda dikingatka agi.",
			"6": "<sup>6</sup> Orang ke datai ngabas aku, ukai datai mai ati ti lurus. Sida ngeluluka berita ti jai pasal aku, lalu udah nya bejalai lalu besiburka berita nya di semua endur.",
			"7": "<sup>7</sup> Semua orang ke begedika aku, betelai enggau pangan diri pasal aku, berundingka utai ti pemadu jai pasal aku.",
			"8": "<sup>8</sup> Ku sida, “Iya sakit lalu nyau deka mati. Iya enda ulih angkat agi.”",
			"9": "<sup>9</sup> Indah pangan rapat aku, iya ke pemadu dikarapka aku, ke saum empa saum suap enggau aku, deh udah nguing ngelaban aku.",
			"10": "<sup>10</sup> Kasihka aku, O TUHAN, lalu asuh aku gerai baru, lalu aku deka malas munsuh aku.",
			"11": "<sup>11</sup> Sida enda ulih menang ngelaban aku, lalu aku deka nemu Nuan gaga ati ke aku.",
			"12": "<sup>12</sup> Nuan deka nulung aku, laban aku ngereja utai ti ngena. Nuan deka mai aku seruran begulai enggau Nuan belama iya.",
			"13": "<sup>13</sup> Puji meh TUHAN Allah Taala ke disembah orang Israel! Puji meh Iya diatu enggau belama iya! Amin! Amin!"
		},
		"42": {
			"1": "<sup>1</sup> Baka rusa ti tetengukka ai sungai ti chelap, baka nya meh aku lelengauka Nuan, O Allah Taala.",
			"2": "<sup>2</sup> Aku aus ke Nuan, O Allah Taala ti idup. Kemaya aku ulih besembah di mua Nuan?",
			"3": "<sup>3</sup> Siang malam aku nyabak, lalu semina ai mata aja nyadika pemakai aku. Bala munsuh aku seruran nanya, “Dini alai Allah Taala nuan?”",
			"4": "<sup>4</sup> Ati aku balat tusah lebuh aku ngingatka utai ti udah, lebuh aku bejalai enggau orang ngagai rumah Allah Taala lalu nguluka sida lebuh sida bejalai, bala orang ke lantang ati, ke belagu sereta manjung muji Allah Taala.",
			"5": "<sup>5</sup> Nama kebuah ati aku tusah? Nama kebuah ati aku enda lantang? Aku deka manggaika pengandal aku ba Allah Taala, lalu aku deka sekali agi muji Iya ke Penebus enggau Allah Taala aku.",
			"6": "<sup>6</sup> Di menua ti udah alai aku dibuai, ati aku balat tusah. Nya alai aku nguingka runding aku ngagai TUHAN. Iya udah ngasuh semengat aku balat sinu. Penyarut inggar ninggang aku baka ai bah, baka ai wung ti belauh labuh ngagai Sungai Jordan ari bukit Hermon enggau ari bukit Misar.",
			"8": "<sup>8</sup> Awakka TUHAN mandangka pengerindu Iya ti enda berubah, lebuh siang hari, ngambika aku bisi pantun malam hari, sampi ti diberi ngagai Allah Taala ti ngidupka aku.",
			"9": "<sup>9</sup> Ngagai Allah Taala ti nyaga aku, aku nyebut, “Nama kebuah Nuan enda ingatka aku? Nama kebuah aku enda tau enda mengkang natka pemerinsa ba jari munsuh ke merinsaka aku?”",
			"10": "<sup>10</sup> Ati aku anchur laban jaku pechat sida, lebuh sida majak nanya aku, “Dini alai Allah Taala nuan?”",
			"11": "<sup>11</sup> Nama kebuah ati aku balat tusah? Nama kebuah ati aku balat enda lantang? Aku manggaika pengandal aku ba Allah Taala, lalu aku deka sekali agi muji Iya ke Penebus enggau Allah Taala aku."
		},
		"43": {
			"1": "<sup>1</sup> O Allah Taala, awakka Nuan madahka aku nadai penyalah, lalu bejaku ngarika aku ngelaban orang ke jai. Selamatka aku ari orang ke bula sereta jai!",
			"2": "<sup>2</sup> Nuan nyadika pengelindung aku. Nama kebuah nuan ninggalka aku? Nama kebuah aku enda tau enda mengkang natka pemerinsa ba jari munsuh ti merinsaka aku?",
			"3": "<sup>3</sup> Asuh penampak enggau pemendar Nuan nguluka aku lalu mai aku pulai baru ngagai Sion, bukit Nuan ti kudus, enggau ngagai Rumah Sembiang Nuan, ti alai Nuan diau.",
			"4": "<sup>4</sup> Nya baru aku deka mansang ngagai alta Nuan, O Allah Taala, ngagai Allah Taala ti penatai pengelantang ati aku. Aku deka nebah belikan aku, lalu belagu muji Nuan, O Allah Taala, Allah Taala aku.",
			"5": "<sup>5</sup> Nama kebuah ati aku balat tusah? Nama kebuah ati aku enda lantang? Aku deka manggaika pengandal aku ba Allah Taala, lalu aku deka sekali agi muji Iya ke Penebus enggau Allah Taala aku."
		},
		"44": {
			"1": "<sup>1</sup> O Allah Taala, kami udah ninga ngena pending kami empu, lalu bala sida aki ini kami udah nusui ngagai kami, pasal pengawa ti besai ti udah dikereja Nuan lebuh maya sida dulu kelia,",
			"2": "<sup>2</sup> pasal Nuan ke muru mayuh bengkah bansa orang kapir lalu ngentapka bala nembiak Nuan diau di menua orang nya; pasal Nuan ke ngukum bansa bukai lalu ngasuh bala nembiak Nuan Empu nyadi kaya-raja.",
			"3": "<sup>3</sup> Bala nembiak Nuan ukai ngalahka menua tu ngena pedang sida. Sida menang ukai laban kuasa sida empu tang laban kuasa enggau pengering Nuan, laban Nuan bisi enggau sida, ti nunjukka Nuan rinduka sida.",
			"4": "<sup>4</sup> Nuan endang Raja enggau Allah Taala aku Nuan ngasuh bala nembiak Nuan menang,",
			"5": "<sup>5</sup> lalu ulih kuasa nama Nuan, kami ngalahka munsuh kami.",
			"6": "<sup>6</sup> Aku enda ngarapka panah aku tauka pedang aku ulih ngidupka aku,",
			"7": "<sup>7</sup> laban Nuan Empu udah ngelepaska kami ari bala munsuh kami lalu ngalahka orang ke begedika kami.",
			"8": "<sup>8</sup> Kami deka seruran muji Nuan lalu meri terima kasih ngagai Nuan belama iya.",
			"9": "<sup>9</sup> Tang diatu Nuan udah nulak kami, lalu nejuka kami alah. Nuan nadai agi bejalai enggau bala soldadu kami.",
			"10": "<sup>10</sup> Nuan ngasuh kami rari ari munsuh kami, lalu sida ngerampas reta tengkira kami.",
			"11": "<sup>11</sup> Nuan nejuka kami dibunuh baka jelu bedus. Nuan ngachirka kami ngagai menua bukai.",
			"12": "<sup>12</sup> Nuan nyual bala nembiak Nuan Empu ngena rega ti murah baka ke nadai rega.",
			"13": "<sup>13</sup> Orang ke berimbai pendiau enggau kami, meda utai ti dikereja Nuan ngagai kami, lalu sida ngelese sereta ketawaka kami.",
			"14": "<sup>14</sup> Nuan udah ngaga kami nyadika leka jaku sema ba bansa bukai. Sida ngilikka pala sida ngelanyaika kami.",
			"15": "<sup>15</sup> Aku endang seruran dipechat. Aku nyau malu",
			"16": "<sup>16</sup> ninga jaku kelese enggau jaku pechat ti disebut munsuh enggau orang ke begedika aku.",
			"17": "<sup>17</sup> Semua utai tu udah nyadi ba kami, taja pen kami nadai kala enda ingatka Nuan tauka ngelanggar sempekat ti udah digaga Nuan enggau kami.",
			"18": "<sup>18</sup> Kami nadai udah kala enda taluk ngagai Nuan. Kami nadai udah kala enda ngasika pesan Nuan.",
			"19": "<sup>19</sup> Tang taja pia, Nuan udah ninggalka kami nadai daya begulai enggau jelu ti siga. Nuan udah ninggalka kami dalam pemetang.",
			"20": "<sup>20</sup> Enti kami udah badu agi nyembah Allah Taala kami, lalu besampi ngagai petara orang bansa bukai,",
			"21": "<sup>21</sup> Nuan tetap nemu nya, laban Nuan nemu resia runding kami.",
			"22": "<sup>22</sup> Tang ketegal Nuan kami seruran dibunuh baka bedus ti deka disayat.",
			"23": "<sup>23</sup> Angkat, TUHAN! Nama kebuah Nuan tinduk? Angkat! Anang nulak kami belama iya!",
			"24": "<sup>24</sup> Nama kebuah Nuan belalai ari kami? Anang enda ingatka pemerinsa enggau penusah kami!",
			"25": "<sup>25</sup> Kami rebah redak ke tanah. Kami alah lalu gali malai amau.",
			"26": "<sup>26</sup> Datai lalu tulung kami! Selamatka kami ketegal pengerindu Nuan ti meruan belama iya!"
		},
		"45": {
			"1": "<sup>1</sup> Jaku ti manah datai ba ati aku, lebuh aku ngarang pantun tu ke raja. Dilah aku baka pinsil orang ke landik nulis, sedia deka bepantun.",
			"2": "<sup>2</sup> Nuan tu lelaki ke pemadu sigat, ke seruran nelap bejaku. Allah Taala endang seruran merekatka nuan.",
			"3": "<sup>3</sup> Tangkin meh pedang nuan, raja ke bekuasa. Nuan bemulia sereta kering.",
			"4": "<sup>4</sup> Tepan meh kuda nuan Raja ketegal pemenang nuan ngambika nyaga pemendar enggau atur ti lurus! Pengering nuan deka ngasuh nuan menang!",
			"5": "<sup>5</sup> Laja nuan tajam, ngebuska tungkul ati bala munsuh nuan. Semua bansa rebah semak kaki nuan.",
			"6": "<sup>6</sup> Perintah ti udah diberi Allah Taala ngagai nuan deka tan belama lama iya. Nuan merintah nembiak nuan enggau lurus.",
			"7": "<sup>7</sup> Nuan rinduka utai ti ngena, lalu begedika utai ti jai. Nya kebuah Allah Taala, ti Allah Taala nuan, udah milih nuan lalu udah nuang mayuh agi pengelantang ngagai nuan ari ti dituang ngagai raja bukai.",
			"8": "<sup>8</sup> Bau rengut mur, enggau garu mengkang lekat ba gari nuan. Bala orang ke nebah musik merindang nuan di istana ti dilagu enggau gading.",
			"9": "<sup>9</sup> Bala anak indu raja bukai sama begulai enggau bala anak indu nuan; kuin ke bengepan ngena emas ti pemadu manah bediri ari kanan kerusi diraja nuan.",
			"10": "<sup>10</sup> Nuan ti deka nyadi bini raja, pendingka utai ti disebut aku: anang agi ingatka bala nembiak nuan enggau kaban belayan nuan.",
			"11": "<sup>11</sup> Pemajik nuan deka ngasuh raja ingin rinduka nuan. Iya tuan nuan, nya alai nuan enda tau enda ngasika iya.",
			"12": "<sup>12</sup> Orang ari Tire deka mai pemeri ngagai nuan. Orang ke kaya deka nguji ngulihka ati nuan.",
			"13": "<sup>13</sup> Anak indu raja bisi di istana - naka meh pemajik gamal iya! Baju iya digaga ari ubung emas.",
			"14": "<sup>14</sup> Bengepan ngena baju ti mayuh chura, iya dibai ngagai raja, dititihka bala indu ke ngiring iya, lalu sida nya mega dibai ngagai raja.",
			"15": "<sup>15</sup> Enggau ati ti lantang sereta gaga sida datai lalu tama ngagai istana raja.",
			"16": "<sup>16</sup> Nuan, raja aku, deka mayuh anak lelaki ti deka nganti bala aki ini nuan nyadi raja, lalu nuan deka ngasuh sida merintah semua dunya.",
			"17": "<sup>17</sup> Pantun aku deka ngasuh Nuan tampak nama belama iya, lalu semua orang deka muji nuan belama lama iya."
		},
		"46": {
			"1": "<sup>1</sup> Allah Taala endang pengelindung enggau pengering kitai, seruran sedia deka nulung lebuh maya penusah.",
			"2": "<sup>2</sup> Nya alai kitai enda takut, enti sema tanah berenyang lalu bukit labuh ngagai buntut tasik,",
			"3": "<sup>3</sup> enti sema tasik begelumbang lalu bukit berenyang.",
			"4": "<sup>4</sup> Bisi siti sungai ti mai pengaga ngagai nengeri Allah Taala, ngagai rumah kudus Allah Taala ti Pemadu Tinggi.",
			"5": "<sup>5</sup> Allah Taala diau dalam nengeri nya, lalu nengeri nya enda rusak. Tumu pagi hari Iya nulung nengeri nya.",
			"6": "<sup>6</sup> Semua bansa bukai deka takut, perintah beguyat. Allah Taala deka bejaku munyi guntur, lalu dunya deka lela.",
			"7": "<sup>7</sup> TUHAN ti Pemadu Bekuasa bisi enggau kitai. Allah Taala Jakop ngelindung kitai.",
			"8": "<sup>8</sup> Datai lalu peda utai ti dikereja TUHAN. Peda pemanah utai ti dikereja Iya di dunya.",
			"9": "<sup>9</sup> Iya ngasuh perang badu agi nyadi di serata dunya. Iya matahka panah, ngeredakka sangkuh, lalu nunu terabai.",
			"10": "<sup>10</sup> Ku Iya bejaku, “Badu agi beperang; Aku dekaka kita nemu, Aku tu Allah Taala. Aku ditinggika ulih semua bansa, ditinggika di serata dunya!”",
			"11": "<sup>11</sup> TUHAN ti Pemadu Bekuasa bisi enggau kitai. Allah Taala Jakop ngelindung kitai."
		},
		"47": {
			"1": "<sup>1</sup> Tempapka jari kita enggau ati ti gaga, kita semua bansa! Puji meh Allah Taala ngena lagu ti inggar!",
			"2": "<sup>2</sup> Nangi meh kita ke TUHAN ti Pemadu Bekuasa. Iya Raja ke besai, ke merintah semua dunya.",
			"3": "<sup>3</sup> Iya ngalahka semua mensia ke kitai, lalu ngengkahka semua bansa di baruh kaki kitai.",
			"4": "<sup>4</sup> Iya milih menua alai kitai diau, nyadi pesaka bala nembiak Iya, ke dikerinduka Iya.",
			"5": "<sup>5</sup> Allah Taala niki ngagai kerusi diraja Iya. Munyi panjung gaga ati, enggau munyi terumpit bisi didinga, lebuh TUHAN niki.",
			"6": "<sup>6</sup> Belagu muji Allah Taala. Belagu muji Raja kitai!",
			"7": "<sup>7</sup> Allah Taala nya Raja ke semua dunya. Puji Iya ngena lagu!",
			"8": "<sup>8</sup> Allah Taala duduk ba kerusi diraja Iya ti kudus. Iya merintah semua bansa.",
			"9": "<sup>9</sup> Bala orang ke merintah semua bansa begempuru enggau bala nembiak Allah Taala ke disembah Abraham. Laban Allah Taala bekuasa agi ari semua bala soldadu, nya alai Iya merintah semua."
		},
		"48": {
			"1": "<sup>1</sup> TUHAN endang besai lalu patut dipuji dalam nengeri Allah Taala kitai, ba bukit Iya ti kudus.",
			"2": "<sup>2</sup> Sion, bukit Allah Taala, endang tinggi sereta manah. Nengeri Raja Ti Besai mai pengaga ngagai semua dunya.",
			"3": "<sup>3</sup> Allah Taala udah nunjukka pengelikun endang bisi ba Iya dalam kuta nengeri nya.",
			"4": "<sup>4</sup> Bala raja begempuru lalu datai nyerang Bukit Sion.",
			"5": "<sup>5</sup> Tang lebuh sida meda nengeri nya, sida tekejut. Sida panik, lalu rari.",
			"6": "<sup>6</sup> Sida takut, sereta tusah ati, baka indu ke radai deka beranak,",
			"7": "<sup>7</sup> baka kapal ti dipangkaka biaubalai.",
			"8": "<sup>8</sup> Kami bisi ninga pasal utai ti udah dikereja Allah Taala, lalu diatu kami udah meda utai nya ba nengeri Allah Taala, TUHAN ti Pemadu Bekuasa. Iya deka ngibun nengeri nya enggau likun belama iya.",
			"9": "<sup>9</sup> Dalam Rumah Sembiang Nuan, O Allah Taala, kami belematika pengerindu Nuan ti meruan belama iya.",
			"10": "<sup>10</sup> Nuan dipuji orang di semua endur, lalu penampak nama Nuan ngelekai di serata dunya. Nuan merintah enggau lurus.",
			"11": "<sup>11</sup> Awakka orang Sion gaga! Awakka orang dalam nengeri di menua Judah rami gaga laban Nuan meri atur ti lurus.",
			"12": "<sup>12</sup> Bejalai meh kita ngelingi Sion lalu itung semua menara iya.",
			"13": "<sup>13</sup> Peratika dinding, lalu peresa kuta, ngambika kita ulih nusui ngagai serak ti nangkanka kita:",
			"14": "<sup>14</sup> “Tu meh Allah Taala ti Allah Taala kitai belama lama iya. Iya deka ngiring kitai belama iya.”"
		},
		"49": {
			"1": "<sup>1</sup> Dingaka tu, semua kita! Pendingka tu, semua kita di semua endur,",
			"2": "<sup>2</sup> kita ke besai, enggau ke mit, ke kaya enggau ke seranta.",
			"3": "<sup>3</sup> Runding aku deka terang. Aku deka nyebut penemu-dalam.",
			"4": "<sup>4</sup> Aku deka mendingka jaku-dalam lalu nerangka reti iya lebuh aku nebah belikan.",
			"5": "<sup>5</sup> Aku enda takut lebuh aku dituntung penusah, lebuh aku dikelulung bala munsuh,",
			"6": "<sup>6</sup> dikelulung orang ke jai, ke bekering ba pengaya lalu temegahka pemayuh pemisi sida.",
			"7": "<sup>7</sup> Kitai sigi enda ulih nebus diri empu. Kitai enda ulih mayar rega nyawa kitai ngagai Allah Taala,",
			"8": "<sup>8</sup> laban bayar nyawa mensia kelalu besai. Utai ti dibayar kitai, sigi nadai kala chukup",
			"9": "<sup>9</sup> kena nagang kitai tama ngagai kubur lalu ngasuh kitai idup belama iya.",
			"10": "<sup>10</sup> Semua orang nemu, orang ke bepenemu-dalam endang mati magang, baka orang ke lap sereta beli. Semua sida bela ninggalka pengaya sida ke peturun sida.",
			"11": "<sup>11</sup> Kubur sida nyadika rumah sida belama iya. Dia meh endur alai sida deka diau belama iya, taja pen sida suba udah bisi tanah ke diri empu.",
			"12": "<sup>12</sup> Peninggi pangkat kitai enda ulih nagang kitai mati. Kitai deka mati baka jelu.",
			"13": "<sup>13</sup> Peda utai ti nyadi ba orang ke bekering ba diri empu, pengujung orang ti udah puas ati ke pengaya diri,",
			"14": "<sup>14</sup> sida diletak mati baka bedus, lalu pemati deka nyadi pengibun sida. Orang ke lurus deka menang ngelaban sida, lebuh tubuh sida deka buruk dalam dunya pemati, jauh ari menua diri empu.",
			"15": "<sup>15</sup> Tang Allah Taala deka ngelepaska aku. Iya deka ngelepaska aku ari kuasa pemati.",
			"16": "<sup>16</sup> Anang tusah ati lebuh orang nyadi kaya, lebuh pengaya iya nambah-menambah.",
			"17": "<sup>17</sup> Iya enda ulih mai utai nya lebuh iya mati. Pengaya iya enda mupuk enggau iya ngagai kubur.",
			"18": "<sup>18</sup> Enti sema orang puas ati ke pengidup diri lalu dipuji ketegal iya mujur,",
			"19": "<sup>19</sup> iya deka nitihka aki ini iya lebuh iya mati, ngagai pemetang ti meruan belama iya.",
			"20": "<sup>20</sup> Peninggi pangkat kitai enda ulih nagang kitai mati. Kitai deka mati baka jelu."
		},
		"50": {
			"1": "<sup>1</sup> TUHAN Allah Taala ti Pemadu Bekuasa bejaku. Iya ngangauka semua mensia di dunya ari timur nyentuk ke barat.",
			"2": "<sup>2</sup> Allah Taala manchar ari Sion, nengeri ti pemadu manah gaya.",
			"3": "<sup>3</sup> Allah Taala kitai datai, tang ukai enda bemunyi. Api ti mau bisi di mua Iya, ribut biaubalai ngelingi Iya.",
			"4": "<sup>4</sup> Iya ngasuh serega enggau dunya nyadika saksi ngemataka Iya ngakim bala nembiak Iya.",
			"5": "<sup>5</sup> Ku Iya bejaku, “Gempuru ngagai Aku bala nembiak Aku ke tetap ati, orang ke udah ngaga sempekat enggau Aku ngena piring.”",
			"6": "<sup>6</sup> Langit madahka Allah Taala endang lurus, madahka Iya endang Hakim.",
			"7": "<sup>7</sup> Pendingka Aku, kita ke nembiak Aku. Aku deka bejaku. Aku deka besaksi ngelaban kita, Israel. Aku tu Allah Taala, ti Allah Taala kita.",
			"8": "<sup>8</sup> Aku ukai nganu kita ketegal piring kita tauka ketegal piring tunu ti selalu dibai kita ngagai Aku.",
			"9": "<sup>9</sup> Aku enda begunaka sapi ti lelaki ari umai kita tauka kambin ari raban jelu kita.",
			"10": "<sup>10</sup> Semua jelu di babas endang enggi Aku magang, sama enggau sapi ti ba beribu igi munggu.",
			"11": "<sup>11</sup> Semua burung di babas, endang enggi Aku magang, sama enggau semua utai ti idup di padang.",
			"12": "<sup>12</sup> “Enti sema Aku lapar, enda meh Aku minta pemakai ari kita, laban dunya, enggau semua utai ti dalam iya, endang enggi Aku magang.",
			"13": "<sup>13</sup> Kati Aku makai isi sapi tauka ngirup darah kambin?",
			"14": "<sup>14</sup> Beri sampi terima kasih kita nyadika piring ngagai Allah Taala. Lalu beri ngagai Iya ti Pemadu Bekuasa semua utai ti udah disemaya kita.",
			"15": "<sup>15</sup> Kangauka Aku lebuh kita kena laban penusah. Aku deka ngelepaska kita, lalu kita deka muji Aku.”",
			"16": "<sup>16</sup> Tang ku Allah Taala bejaku ngagai orang ke jai, “Nama kebuah kita besebutka pesan Aku? Nama kebuah kita berandauka sempekat Aku?",
			"17": "<sup>17</sup> Kita enggai ngelakka Aku ngajar kita. Kita nulak pesan Aku.",
			"18": "<sup>18</sup> Kita bepangan enggau semua penchuri ke dipeda kita, lalu kita begulai enggau orang ke butang.",
			"19": "<sup>19</sup> “Kita endang seruran sedia deka bejaku jai. Kita enda kakang bula.",
			"20": "<sup>20</sup> Kita sedia deka bedawaka kaban belayan kita empu lalu begigaka penyalah sida.",
			"21": "<sup>21</sup> Kita udah ngereja semua utai nya, lalu Aku enda bejaku seleka, nya alai kita ngumbai Aku baka kita. Tang diatu Aku nganu kita lalu nerangka pekara tu ngagai kita.",
			"22": "<sup>22</sup> Pendingka tu, kita ke enggai ibuhka Aku, enggai ke Aku ngerusak kita, lalu nadai orang deka ngidupka kita.",
			"23": "<sup>23</sup> Meri terima kasih nya piring ti ngemesaika Aku, lalu Aku tetap deka ngelepaska semua orang ke ngasika Aku.”"
		},
		"51": {
			"1": "<sup>1</sup> Kasihka aku, O Allah Taala, laban pengerindu Nuan endang meruan. Laban pengasih Nuan endang besai, buai meh dosa aku!",
			"2": "<sup>2</sup> Buai abis semua penyai aku lalu tuchi aku ari dosa aku!",
			"3": "<sup>3</sup> Aku ngelala penyalah diri. Aku endang seruran nemu dosa diri.",
			"4": "<sup>4</sup> Aku udah bedosa ngelaban Nuan - ngelaban Nuan siku aja, lalu udah ngereja utai ti dikumbai Nuan jai. Nya alai Nuan endang sigi ngereja utai ti ngena lebuh Nuan ngakim aku. Nuan endang patut ngukum aku.",
			"5": "<sup>5</sup> Aku endang jai kenyau ari ada. Kenyau ari maya aku dikandungka, aku endang bedosa.",
			"6": "<sup>6</sup> Nuan begunaka pemendar enggau ati ti lurus, nya alai isi meh runding aku enggau penemu-dalam ti datai ari Nuan.",
			"7": "<sup>7</sup> Buai dosa aku, lalu aku deka nyadi tuchi. Basu aku, lalu aku deka nyadi burak agi ari seleju.",
			"8": "<sup>8</sup> Asuh aku ninga munyi pengaga enggau pengelantang, lalu taja pen Nuan udah ngalah lalu ngeredakka aku, aku deka nyadi lantang baru.",
			"9": "<sup>9</sup> Ampun meh dosa aku lalu buai abis semua penyai aku.",
			"10": "<sup>10</sup> Gaga ati aku tuchi, O Allah Taala, lalu simpan roh ti baru sereta taluk, dalam aku.",
			"11": "<sup>11</sup> Anang muai aku ari mua Nuan. Anang ngambi Roh Kudus Nuan ari aku.",
			"12": "<sup>12</sup> Pulaika aku baru ngagai pengaga ti datai ari pengelepas Nuan, lalu asuh aku rajin ngasika Nuan.",
			"13": "<sup>13</sup> Nya baru aku deka ngajar pasal pesan Nuan ngagai orang ke bedosa, lalu sida deka pulai baru ngagai Nuan.",
			"14": "<sup>14</sup> Anang munuh aku, O Allah Taala, lalu selamatka aku, lalu aku deka gaga ati madahka pengelepas ti diberi Nuan.",
			"15": "<sup>15</sup> Tulung aku bejaku, O TUHAN, lalu aku deka muji Nuan.",
			"16": "<sup>16</sup> Nuan enda rinduka piring, nya kebuah aku enda ngenjuk sida ngagai Nuan. Nuan enda rinduka piring tunu.",
			"17": "<sup>17</sup> Piring aku nya ati ti baruh, O Allah Taala. Nuan enggai nulak ati ti baruh, ati ti nesal.",
			"18": "<sup>18</sup> O Allah Taala, kasihka Sion, lalu tulung iya. Gaga baru meh dinding kuta Jerusalem.",
			"19": "<sup>19</sup> Nya baru Nuan deka gaga ati ke piring ti menyana enggau piring tunu. Lalu sapi ti lelaki deka diberi nyadika piring ba alta Nuan."
		},
		"52": {
			"1": "<sup>1</sup> Nama kebuah nuan temegah bejakuka penyai diri, nuan ke kering? Penetap ati Allah Taala endang meruan belama iya.",
			"2": "<sup>2</sup> Nuan ngaga pelan kena ngerusak orang bukai. Dilah nuan tajam baka lungga. Nuan endang seruran nempa pemula.",
			"3": "<sup>3</sup> Nuan rinduka penyai lebih agi ari pemanah, lalu rinduka pemula lebih agi ari pemendar.",
			"4": "<sup>4</sup> Nuan rindu ngemediska orang ngena jaku nuan, nuan ke pemula!",
			"5": "<sup>5</sup> Nya alai Allah Taala deka ngerusak nuan belama iya. Iya deka nangkap nuan, lalu nabanka nuan ari rumah nuan. Iya deka muai nuan ari dunya orang ke idup.",
			"6": "<sup>6</sup> Orang ke lurus deka meda nya, lalu takut. Udah nya sida deka ketawaka nuan, lalu bejaku,",
			"7": "<sup>7</sup> “Peda: tu siku orang ke enda minta diri dilindung Allah Taala, tang bekering ba pengaya lalu belindung ba pemayuh pemisi iya.”",
			"8": "<sup>8</sup> Tang aku tu baka kayu kana ti tumbuh dalam rumah Allah Taala. Aku manggaika diri ba pengerindu Iya ti meruan belama iya.",
			"9": "<sup>9</sup> Aku deka seruran meri terima kasih ngagai Nuan, O Allah Taala, ketegal utai ti udah dikereja Nuan. Di mua bala nembiak Nuan, aku deka madahka pemadas Nuan."
		},
		"53": {
			"1": "<sup>1</sup> Ku orang ke beli berunding dalam ati diri, “Allah Taala endang nadai.” Semua sida endang jai magang, lalu sida udah ngereja utai ti udu jai. Nadai siku ti ngereja utai ti ngena.",
			"2": "<sup>2</sup> Allah Taala nilik ke baruh ari serega ngagai mensia deka nemu enti sida bisi pintar, ti nyembah Iya.",
			"3": "<sup>3</sup> Tang semua sida udah limpang. Semua sida bela jai magang. Nadai siku sida ngereja utai ti ngena, siku pen nadai.",
			"4": "<sup>4</sup> “Kati nuan enda nemu?” ku Allah Taala nanya. “Kati orang ke ngereja penyai nya nadai nemu utai? Sida beridup ngerumpak bala nembiak Aku, lalu sida nadai kala besampi ngagai Aku.”",
			"5": "<sup>5</sup> Tang udah nya sida deka balat takut. Nadai sida udah kala takut ngemalat nya, laban Allah Taala deka mambarka tulang bala orang ke jai. Allah Taala udah nulak sida, nya alai orang Israel deka ngalahka sida abis lengis.",
			"6": "<sup>6</sup> Aku bebendar besampi minta pemenang datai ari Sion ngagai orang Israel. Naka meh pengelantang ati orang Israel lebuh Allah Taala ngasuh sida nyadi kaya-raja baru!"
		},
		"54": {
			"1": "<sup>1</sup> Selamatka aku ngena kuasa Nuan, O Allah Taala. Selamatka aku ngena pengering Nuan!",
			"2": "<sup>2</sup> Dingaka sampi aku, O Allah Taala. Pendingka jaku aku!",
			"3": "<sup>3</sup> Orang ke sumbung deka datai lalu nyerang aku. Orang ke jai nguji munuh aku, Orang nya enda begunaka Allah Taala.",
			"4": "<sup>4</sup> Tang Allah Taala endang penyaup aku. TUHAN endang penyaga aku.",
			"5": "<sup>5</sup> Awakka Allah Taala ngukum bala munsuh aku ngena penyai sida empu. Iya deka ngerusak sida laban Iya endang tetap ati.",
			"6": "<sup>6</sup> Aku deka gaga ati ngenjuk piring ngagai Nuan, O TUHAN. Aku deka meri terima kasih ngagai Nuan laban Nuan endang badas.",
			"7": "<sup>7</sup> Nuan udah ngelepaska aku ari semua penusah aku, lalu aku udah meda bala munsuh aku alah."
		},
		"55": {
			"1": "<sup>1</sup> Dingaka sampi aku, O Allah Taala. Anang nulak peminta aku!",
			"2": "<sup>2</sup> Pendingka aku, lalu saut aku. Aku nyau lelak laban ati ti irau.",
			"3": "<sup>3</sup> Aku balat takut diemba bala munsuh aku, alah ditekanka orang ke jai. Sida ngasuh penusah ninggang aku. Sida pedis ati sereta begedika aku.",
			"4": "<sup>4</sup> Aku balat takut, lalu penakut aku ke pemati nyau kelalu berat ba ati aku.",
			"5": "<sup>5</sup> Aku takut, lalu getar-getar. Aku alah laban penakut.",
			"6": "<sup>6</sup> Naka meh pemanah enti aku bisi sayap baka burung achang. Aku tetap deka terebai kebukai lalu belelak.",
			"7": "<sup>7</sup> Aku deka terebai jauh lalu diau di menua puang.",
			"8": "<sup>8</sup> Aku deka beguai-guai ngiga endur alai belindung ari ribut ti deras enggau ari kabut.",
			"9": "<sup>9</sup> Anang nyagi munsuh aku meretika jaku pangan diri, O TUHAN! Aku meda pengachau nyadi dalam nengeri nya,",
			"10": "<sup>10</sup> ngelingi iya siang malam, ngisi iya enggau pengawa ti salah sereta enggau penusah.",
			"11": "<sup>11</sup> Pengerusak nyadi di semua endur. Jalai raya nyau penuh laban orang ke nekanka pengidup orang bukai, sereta nipu pangan diri.",
			"12": "<sup>12</sup> Enti sema munsuh beduan aku, aku ulih natka nya. Enti sema munsuh temegah madahka diri udah ngalahka aku, aku ulih belalai ari iya.",
			"13": "<sup>13</sup> Tang nuan tu pangan aku, sulu aku, pangan rapat aku.",
			"14": "<sup>14</sup> Tua udah suah berandau enggau pangan diri lalu sama nyembah Allah Taala dalam Rumah Sembiang.",
			"15": "<sup>15</sup> Awakka bala munsuh aku mati pandak umur. Awakka sida nurun idup-idup ngagai dunya pemati! Penyai endang bisi dalam rumah sida enggau dalam ati sida.",
			"16": "<sup>16</sup> Tang aku ngangauka TUHAN Allah Taala minta tulung, lalu Iya deka ngidupka aku.",
			"17": "<sup>17</sup> Lebuh pagi hari, tengah hari, enggau malam jaku putap enggau demuh aku niki ngagai Iya, lalu Iya deka ninga nyawa aku.",
			"18": "<sup>18</sup> Iya deka mai aku pulai baru enggau likun ari endur alai aku ke beperang ngelaban munsuh ke udu mayuh.",
			"19": "<sup>19</sup> Allah Taala, ke endang seruran merintah, deka ninga aku, lalu ngalahka sida, laban sida enggai berubah, lalu sida enda nangika Iya.",
			"20": "<sup>20</sup> Pangan aku ke dulu menya ngamuk bala pangan iya. Iya bulaka semaya diri.",
			"21": "<sup>21</sup> Jaku iya manis, tang ati iya penuh laban pemenchi. Jaku iya alus, tang nyayat baka pedang ti tajam.",
			"22": "<sup>22</sup> Serahka penusah nuan ngagai TUHAN, lalu Iya deka nyaga nuan. Iya nadai kala nejuka orang ke lurus alah.",
			"23": "<sup>23</sup> Tang Nuan, O Allah Taala, deka muai orang ke pemunuh, enggau orang ke bula ngagai kubur sebedau sida datai setengan umur. Tang aku deka manggaika pengarap aku ba Nuan."
		},
		"56": {
			"1": "<sup>1</sup> Kasihka aku, O Allah Taala, laban aku kena serang. Bala munsuh aku seruran merinsaka aku.",
			"2": "<sup>2</sup> Bala munsuh aku nyerang aku sepemanjai hari. Mayuh amat sida ngelaban aku.",
			"3": "<sup>3</sup> Lebuh aku takut, O TUHAN ti Pemadu Bekuasa, aku manggaika pengarap aku ba Nuan.",
			"4": "<sup>4</sup> Aku manggaika pengarap aku ba Allah Taala, lalu aku enda takut. Aku muji semaya Iya. Nama utai ti ulih dikereja mensia ngagai aku?",
			"5": "<sup>5</sup> Bala munsuh aku ngachau aku sepemanjai hari. Sida seruran berundingka chara kena ngemediska aku!",
			"6": "<sup>6</sup> Sida begempuru ba endur alai sida belalai lalu nengkilaka semua utai ti dikereja aku, ngarapka diri ulih munuh aku.",
			"7": "<sup>7</sup> Ukum meh sida nya, O Allah Taala, ketegal penyai ti dikereja sida. Awakka Nuan ringat lalu ngalahka bala orang nya.",
			"8": "<sup>8</sup> Nuan nemu pemalat ati aku tusah. Nuan nemu pemayuh ai mata aku labuh. Ukai enda semua ai mata aku nya udah dituliska Nuan magang dalam bup Nuan?",
			"9": "<sup>9</sup> Lebuh aku ngangauka Nuan, bala munsuh aku deka nyurutka diri. Aku nemu nya laban Allah Taala setanah enggau aku,",
			"10": "<sup>10</sup> iya nya TUHAN ti bempu semaya ti dipuji aku.",
			"11": "<sup>11</sup> Aku manggaika pengarap aku ba TUHAN, lalu aku enda takut. Nama utai ti ulih dikereja mensia ngagai aku?",
			"12": "<sup>12</sup> O Allah Taala, aku deka meri Nuan utai ti udah disemaya aku. Aku deka meri Nuan piring terima kasih aku,",
			"13": "<sup>13</sup> laban Nuan udah ngelepaska semengat aku ari pemati, lalu udah nagang aku alah. Nya alai aku bejalai di mua Allah Taala, dalam penampak ti manchar ngagai orang ke idup."
		},
		"57": {
			"1": "<sup>1</sup> Kasihka aku, O Allah Taala, kasihka aku, laban aku belindung ba Nuan. Aku diau likun di baruh sayap Nuan nyentuk ngagai maya biaubalai nyau teduh.",
			"2": "<sup>2</sup> Aku ngangauka Allah Taala ti Pemadu Tinggi, Allah Taala ti meri semua utai ti dipeguna aku.",
			"3": "<sup>3</sup> Iya deka nyaut aku ari serega lalu ngelepaska aku. Iya deka ngalahka orang ke nekanka pengidup aku. Allah Taala deka ngayanka pengerindu Iya ti meruan belama iya, enggau penetap ati Iya.",
			"4": "<sup>4</sup> Aku dikelulung munsuh ke baka jelu singa ti lapar deka makai mensia. Ngeli sida baka sangkuh enggau laja. Dilah sida baka pedang ti tajam.",
			"5": "<sup>5</sup> Ayanka pemesai kuasa Nuan di langit, O Allah Taala, lalu ayanka mulia Nuan di serata dunya.",
			"6": "<sup>6</sup> Bala munsuh aku udah mintarka pukat kena sida nangkap aku. Ati aku balat tusah. Sida udah numbak lubang kebung ba jalai ti ditengah aku, tang sida empu udah labuh ngagai lubang kebung nya.",
			"7": "<sup>7</sup> Aku nadai irau mimit, O Allah Taala. Aku deka belagu muji Nuan!",
			"8": "<sup>8</sup> Dani meh nuan, O semengat aku! Dani meh kita belikan enggau engkeratung aku! Aku deka ngerak mata hari.",
			"9": "<sup>9</sup> Aku deka meri terima kasih ngagai Nuan, O TUHAN, di mua semua bansa bukai. Aku deka muji Nuan di mua semua bansa.",
			"10": "<sup>10</sup> Pengerindu Nuan ti meruan belama iya tinggi nyentuk ke langit. Penetap ati Nuan ngeninggi remang.",
			"11": "<sup>11</sup> Ayanka pemesai kuasa Nuan di langit, O Allah Taala, lalu ayanka mulia Nuan di serata dunya."
		},
		"58": {
			"1": "<sup>1</sup> Kati kita petara bisi udah kala meri pemutus penemu ti lurus? Kati kita udah kala ngakim enggau enda bepilih mata?",
			"2": "<sup>2</sup> Nadai kala! Kita semina ngira penyai ti ulih dikereja kita, lalu ngereja penyai enggau pengawa ti kasar enggau orang di menua tu.",
			"3": "<sup>3</sup> Orang ke jai endang ngereja penyalah seumur idup. Sida nusui utai ti bula kenyau ari ada.",
			"4": "<sup>4</sup> Sida penuh laban rachun baka ular. Sida nensang pending sida baka ular belalang ti bengal",
			"5": "<sup>5</sup> ti enda ninga nyawa orang ke bemain ular, tauka pelian manang.",
			"6": "<sup>6</sup> Patahka taring singa ti mangah nya, O Allah Taala.",
			"7": "<sup>7</sup> Awakka sida lesap baka ai ti rarat. Awakka sida anchur baka rumput ti dilunjak ba tengah jalai raya.",
			"8": "<sup>8</sup> Awakka sida nyadi baka tekuyung ti luluh nyadi lendir. Awakka sida nyadi baka anak mit ke mati lulus sebedau sida meda penampak.",
			"9": "<sup>9</sup> Sebedau sida nemu diri, sida ditebas baka rumput. Ulih pengeringat Iya, Allah Taala deka ngebuka sida idup-idup.",
			"10": "<sup>10</sup> Orang ke lurus deka gaga ati lebuh sida meda orang ke bedosa diukum. Sida deka bejalai ngelunjak darah orang ke jai.",
			"11": "<sup>11</sup> Orang deka nyebut, “Orang ke lurus endang amat diupah. Allah Taala ti ngakim dunya, endang amat bisi.”"
		},
		"59": {
			"1": "<sup>1</sup> Selamatka aku ari bala munsuh aku, O Allah Taala aku. Jaga aku ari orang ke nyerang aku!",
			"2": "<sup>2</sup> Selamatka aku ari bala orang ke jai nya. Selamatka aku ari bala pemunuh nya!",
			"3": "<sup>3</sup> Peda sida ngipa deka munuh aku. Orang ke jai nya begempuru deka ngelaban aku. Sida beguai-guai mansang ngambi endur diri",
			"4": "<sup>4</sup> ukai ketegal dosa tauka penyalah aku, O TUHAN.",
			"5": "<sup>5</sup> Angkat meh Nuan, TUHAN Allah Taala ti Pemadu Bekuasa, lalu tulung aku. O Allah Taala ke disembah orang Israel, awakka Nuan Empu meda! Angkat, lalu ukum meh semua bansa orang kapir nya. Anang kasihka orang ke jai, ke nyual bansa diri!",
			"6": "<sup>6</sup> Lebuh hari nyau lemai sida datai baru, ngengar baka ukui lebuh sida segau-segau bejalai ba sebelah nengeri.",
			"7": "<sup>7</sup> Pendingka jaku pechat enggau jaku emba sida. Dilah sida baka pedang dalam nyawa sida, tang sida ngumbai orang nadai ninga sida.",
			"8": "<sup>8</sup> Tang Nuan ketawaka sida, O TUHAN. Nuan ngeleseka semua bansa orang kapir.",
			"9": "<sup>9</sup> Aku andal ati ke pengering Nuan. Nuan meh nyadika kuta aku, O Allah Taala.",
			"10": "<sup>10</sup> Allah Taala rinduka aku, lalu Iya deka datai ngagai aku. Iya deka ngasuh aku meda bala munsuh aku alah.",
			"11": "<sup>11</sup> Anang munuh sida, O Allah Taala, enggai ke bala nembiak aku enda ingat. Pambarka sida ngena pengering Nuan, lalu alahka sida, O TUHAN, ti nyadi terabai kami.",
			"12": "<sup>12</sup> Dosa mengkang ba ujung mulut sida. Semua utai ti disebut sida endang dosa magang. Awakka sida ditangkap ketegal penyumbung sida! Laban sida nyumpah sereta bula,",
			"13": "<sup>13</sup> rusak meh sida ngena pengeringat Nuan. Rusak meh sida abis lengis. Nya baru semua orang deka nemu Allah Taala endang amat merintah Israel, lalu perintah Iya ngerembai di serata dunya.",
			"14": "<sup>14</sup> Lebuh hari nyau lemai sida datai baru, ngengar baka ukui lebuh sida segau-segau bejalai ba sebelah nengeri,",
			"15": "<sup>15</sup> baka ukui ti segau-segau ngiga pemakai lalu ngengar enti sida nadai tetemuka pemakai ti chukup.",
			"16": "<sup>16</sup> Tang aku deka belagu ngenang pengering Nuan. Tiap pagi aku deka belagu enggau nyawa ti inggar ngenang pengerindu Nuan ti meruan belama iya. Laban Nuan nyadika kuta aku, alai aku belindung lebuh penusah nuntung.",
			"17": "<sup>17</sup> Aku deka muji Nuan ke penyaga aku. Aku belindung ba Allah Taala ti rinduka aku."
		},
		"60": {
			"1": "<sup>1</sup> Nuan udah nulak lalu ngalahka kami, O Allah Taala. Nuan udah ringat ngagai kami, tang pulai meh Nuan baru ngagai kami.",
			"2": "<sup>2</sup> Nuan udah ngasuh tanah berenyang, lalu Nuan udah merekahka tanah. Geraika tanah nya diatu, laban iya nyau deka tebererak.",
			"3": "<sup>3</sup> Nuan udah ngasuh bala nembiak Nuan balat amat merinsa. Kami nyau guir-guir bejalai baka ke mabuk.",
			"4": "<sup>4</sup> Nuan udah meri jaku tangkan ngagai orang ke nangika Nuan, ngambika sida ulih ngerarika diri ari pengerusak.",
			"5": "<sup>5</sup> Lepaska kami ngena pengering Nuan. Saut sampi kami, ngambika orang ke dikerinduka Nuan dilepaska.",
			"6": "<sup>6</sup> Ari Endur Iya ti Kudus Allah Taala udah besemaya, “Aku deka menang lalu bedua menua Sekem lalu meri Lebak Sukot ngagai bala nembiak Aku.",
			"7": "<sup>7</sup> Giliat endang enggi Aku, Manasi mega enggi Aku. Epraim nya selapuk kebal Aku lalu Judah nya tungkat diraja Aku.",
			"8": "<sup>8</sup> Tang Aku deka ngena Moap nyadika dulang alai Aku bebasu, lalu Aku deka nikauka kasut Aku ngagai Edom, nyadika kelai Aku bempu menua nya. Kati orang Pilistin ngumbai diri deka manjung menang ngelaban Aku?”",
			"9": "<sup>9</sup> O Allah Taala, sapa deka mai aku ngagai nengeri ti bekuta nya? Sapa deka nguluka aku ngagai Edom?",
			"10": "<sup>10</sup> Kati Nuan amat udah nulak kami? Kati Nuan enggai bejalai enggau bala soldadu kami?",
			"11": "<sup>11</sup> Tulung meh kami ngelaban munsuh. Tulung ari mensia endang nadai reti.",
			"12": "<sup>12</sup> Enti Allah Taala setanah enggau kitai, kitai deka menang. Iya deka ngalahka bala munsuh kitai."
		},
		"61": {
			"1": "<sup>1</sup> Dingaka sabak aku, O Allah Taala. Pendingka sampi aku!",
			"2": "<sup>2</sup> Enggau ati ti balat tusah, sereta jauh ari rumah, aku ngangauka Nuan! Bai aku ngagai endur alai ngenduh ti likun,",
			"3": "<sup>3</sup> laban Nuan penyaga aku, ti kering nyaga aku ari munsuh.",
			"4": "<sup>4</sup> Awakka aku diau ba Endur Kudus Nuan seumur idup. Awakka aku diau likun di baruh sayap Nuan.",
			"5": "<sup>5</sup> Nuan udah ninga semaya aku, O Allah Taala, lalu Nuan udah meri aku utai ti diempu orang ke ngemesaika Nuan.",
			"6": "<sup>6</sup> Asuh raja gayu. Asuh iya idup belama iya!",
			"7": "<sup>7</sup> Awakka iya merintah belama iya di mua Nuan, O Allah Taala. Jaga iya ngena pengerindu Nuan ti meruan belama iya, enggau ngena penetap ati Nuan.",
			"8": "<sup>8</sup> Nya baru aku deka selalu belagu muji Nuan, lebuh aku meri ngagai Nuan tiap hari, utai ti udah disemaya aku."
		},
		"62": {
			"1": "<sup>1</sup> Aku deka liat ati nganti Allah Taala; laban pengelepas aku datai ari Iya.",
			"2": "<sup>2</sup> Semina Iya siku aja ke nyaga sereta ngelepaska aku. Iya kuta aku, lalu aku enda alah.",
			"3": "<sup>3</sup> Ni pengelama semua kita agi nyerang orang ke lemi baka pagar ti udah runtuh?",
			"4": "<sup>4</sup> Kita semina deka ngasuh iya labuh ari endur iya ti tinggi. Kita rindu bula. Kita merekatka iya ngena mulut kita, tang kita nyumpah iya dalam ati kita.",
			"5": "<sup>5</sup> Aku manggaika diri semina ba Allah Taala siku aja, laban pengadang aku datai ari Iya.",
			"6": "<sup>6</sup> Ka semina Iya siku aja ke nyaga sereta ngelepaska aku. Iya kuta aku, lalu aku enda alah.",
			"7": "<sup>7</sup> Pengelepas enggau basa aku dipanggaika ba Allah Taala. Iya kuta aku ti tegap. Aku belindung ba Allah Taala.",
			"8": "<sup>8</sup> Panggaika pengarap kita berari ba Allah Taala, kita ke nembiak aku. Padahka ngagai Iya semua penusah kita, laban kitai belindung ba Iya.",
			"9": "<sup>9</sup> Semua mensia sigi baka kesuh seput. Tinggi tauka baruh pangkat, semua sida endang nadai guna magang. Enti sida ditimbang, sida nadai pemerat. Sida lempung agi ari kesuh seput.",
			"10": "<sup>10</sup> Anang manggaika diri ba pemangah. Anang ngarapka diri deka bulih utai ari pengawa ngerumpak. Enti sema pengaya kita nambah-menambah, anang meh kita bekering ba pengaya nya.",
			"11": "<sup>11</sup> Aku suah udah ninga Allah Taala madahka kuasa endang enggi Iya",
			"12": "<sup>12</sup> lalu pengerindu Iya meruan belama. Nuan Empu, O TUHAN, ngupah genap iku orang nitihka utai ti dikereja iya."
		},
		"63": {
			"1": "<sup>1</sup> O Allah Taala, Nuan endang Allah Taala aku, lalu aku lelengauka Nuan. Aku aus ke Nuan. Semengat aku aus ke Nuan baka menua ti rangkai, kusi, ransi nadai ai.",
			"2": "<sup>2</sup> Awakka aku meda Nuan dalam Endur Nuan Ti Kudus. Awakka aku meda kuasa enggau mulia Nuan.",
			"3": "<sup>3</sup> Pengerindu Nuan ti meruan belama iya, endang manah agi ari pengidup. Nya alai aku deka muji Nuan.",
			"4": "<sup>4</sup> Aku deka muji Nuan seagi aku idup. Aku deka ngangkatka jari besampi ngagai Nuan.",
			"5": "<sup>5</sup> Semengat aku deka diberi makai besai lalu kenyang, lalu aku deka belagu pantun gaga ati muji Nuan.",
			"6": "<sup>6</sup> Lebuh aku gali, aku ngingatka Nuan. Semalam-malam aku berundingka Nuan,",
			"7": "<sup>7</sup> laban Nuan endang seruran nulung aku. Aku belagu gaga ati belindung di baruh sayap Nuan.",
			"8": "<sup>8</sup> Aku empakap ba Nuan, lalu jari nuan meri aku pengelikun.",
			"9": "<sup>9</sup> Orang ke nguji munuh aku deka nurun ngagai dunya pemati.",
			"10": "<sup>10</sup> Sida deka mati beperang, lalu bangkai sida deka diempa ukui kampung.",
			"11": "<sup>11</sup> Raja deka rami gaga laban Allah Taala meri iya pemenang. Orang ke ngaga semaya dalam nama Allah Taala, deka muji Iya, tang mulut orang ke bula, deka ditutup."
		},
		"64": {
			"1": "<sup>1</sup> Aku kena laban penusah, O Allah Taala, pendingka sampi aku! Aku takutka bala munsuh aku, selamatka aku!",
			"2": "<sup>2</sup> Jaga aku ari akal orang ke jai, ari bala orang ke jai.",
			"3": "<sup>3</sup> Sida ngansah dilah sida baka pedang lalu ngesuhka jaku ti jai baka laja.",
			"4": "<sup>4</sup> Enggau nadai pemalu mimit sida jampat ngelekaika pemula sida lalu ngerusak orang ke manah ngena jaku petenah sida.",
			"5": "<sup>5</sup> Sida meransang pangan diri lebuh sida bepekat deka ngereja penyai. Sida berandauka endur alai sida deka nan panjuk sida. Ku sida bejaku, “Nadai orang ulih meda panjuk nya.”",
			"6": "<sup>6</sup> Sida ngaga pelan ti jai, lalu ku sida, “Kitai udah ngaga pelan kena ngereja penyalah ti enda ulih dipansik.” Ati enggau runding mensia endang enda ulih disagam.",
			"7": "<sup>7</sup> Tang Allah Taala nimbakka laja Iya ngagai sida, lalu tekala nya sida bakal.",
			"8": "<sup>8</sup> Iya deka ngerusak sida ketegal jaku nya. Semua orang ke meda sida, bela ngilikka pala.",
			"9": "<sup>9</sup> Semua sida deka takut magang. Sida deka berundingka utai ti udah dikereja Allah Taala lalu nusui utai ti udah dikereja Iya.",
			"10": "<sup>10</sup> Semua orang ke lurus deka rami gaga ketegal utai ti udah dikereja TUHAN. Sida deka diau likun dalam Iya. Semua orang ke badas deka muji Iya."
		},
		"65": {
			"1": "<sup>1</sup> O Allah Taala, kami endang patut muji Nuan di Sion lalu ngamatka semaya kami enggau Nuan,",
			"2": "<sup>2</sup> laban Nuan nyaut sampi. Semua mensia deka datai ngagai Nuan.",
			"3": "<sup>3</sup> Lebuh kami alah laban penyai ti dikereja kami, Nuan ngampun penyalah kami.",
			"4": "<sup>4</sup> Beberekat meh orang ke dipilih Nuan, orang ke dibai Nuan diau dalam Endur Nuan Ti kudus. Kami deka puas ati ke utai ti manah dalam rumah Nuan, berekat dalam Rumah Nuan ti kudus.",
			"5": "<sup>5</sup> Nuan meri kami pemenang kena Nuan nyaut kami, lalu Nuan ngereja pengawa ti badas kena Nuan ngelepaska kami. Mensia di serata dunya enggau di seberai tasik ti jauh, manggaika pengarap sida ba Nuan.",
			"6": "<sup>6</sup> Nuan nudukka bukit ba endur sida ngena pengering Nuan, ngayanka kuasa Nuan ti besai.",
			"7": "<sup>7</sup> Nuan ngasuh tasik teduh nagang gelumbang besegum mangka. Nuan ngasuh semua bansa badu agi begau.",
			"8": "<sup>8</sup> Semua dunya nangi sereta takut meda pengawa ti besai ti dikereja Nuan. Pengawa ti dikereja Nuan ngasuh orang manjung gaga ati di serata dunya.",
			"9": "<sup>9</sup> Nuan ngayanka diri sinu ati ke menua lebuh Nuan ngasuh hari ujan. Nuan ngasuh menua ami. Nuan ngisi sungai enggau ai. Nuan ngasuh utai tumbuh. Baka tu ku jalai Nuan ngereja nya:",
			"10": "<sup>10</sup> Nuan ngasuh ai ujan manjah tanah ti udah dichangkul lalu ngasuh tanah serap diisi ai. Nuan ngasuh tanah lembut ngena ujan ti mesi-mesi lalu ngasuh utai ti ditanam tumbuh.",
			"11": "<sup>11</sup> Naka meh pemayuh utai ti diasuh Nuan ketau kami! Nuan meri kami mayuh utai di sebarang endur Nuan bejalai.",
			"12": "<sup>12</sup> Padang rumput nyau penuh laban jelu tupi. Serimbing bukit nyau penuh laban pengaga.",
			"13": "<sup>13</sup> Padang nyau penuh laban jelu bedus. Emperan nyau penuh laban gandum. Semua utai manjung magang, lalu belagu gaga ati."
		},
		"66": {
			"1": "<sup>1</sup> Puji meh Allah Taala ngena panjung gaga ati, semua mensia!",
			"2": "<sup>2</sup> Belagu ngemuliaka nama Iya Muliaka Iya ngena pantun puji!",
			"3": "<sup>3</sup> Sebut ngagai Allah Taala, “Naka meh pemadas utai ti dikereja Nuan! Kuasa Nuan endang besai datai ke bala munsuh Nuan nyau nangi lalu meremi di mua Nuan.",
			"4": "<sup>4</sup> Semua orang di dunya tu nyembah Nuan magang. Sida belagu muji Nuan. Sida belagu muji nama Nuan.”",
			"5": "<sup>5</sup> Datai kitu lalu peda utai ti udah dikereja Allah Taala, pengawa ti badas ti dikereja Iya ba orang mayuh.",
			"6": "<sup>6</sup> Iya ngubah tasik nyadi tanah rangkai. Aki ini kami bejalai meraka sungai. Dia meh endur alai kami ke rami gaga ketegal utai ti udah dikereja Iya.",
			"7": "<sup>7</sup> Iya merintah belama iya ngena kuasa Iya lalu ngemataka semua bansa. Awakka orang nadai angkat ngelaban Iya.",
			"8": "<sup>8</sup> Puji meh Allah Taala kami, kita semua bansa. Awakka puji kita didinga.",
			"9": "<sup>9</sup> Iya udah ngasuh kami mengkang idup lalu enggai nejuka kami alah.",
			"10": "<sup>10</sup> Nuan udah ngabas ati kami, O Allah Taala. Nuan udah nguji ati kami baka ke nuchi pirak ngena api.",
			"11": "<sup>11</sup> Nuan nejuka kami disintak panjuk lalu ngengkahka utai ti berat ba belakang kami.",
			"12": "<sup>12</sup> Nuan nejuka bala munsuh kami ngindik kami. Kami bejalai ngerenchah api enggau ai bah, tang diatu Nuan udah mai kami ngagai endur ti luas.",
			"13": "<sup>13</sup> Aku deka mai piring tunu ngagai rumah Nuan. Aku deka meri ngagai Nuan utai ti udah disemaya aku.",
			"14": "<sup>14</sup> Aku deka meri ngagai Nuan utai ti udah dipadahka aku ngagai Nuan lebuh aku kena tuntung penusah.",
			"15": "<sup>15</sup> Aku deka meri bedus ti deka ditunu ba alta. Aku deka meri sapi ti lelaki enggau kambin nyadika piring, lalu asap deka niki ke langit.",
			"16": "<sup>16</sup> Datai lalu mending, semua kita ke nangika Allah Taala, aku deka nusui ngagai kita utai ti udah dikereja Iya ke aku.",
			"17": "<sup>17</sup> Aku udah nyabak ngangauka Iya, lalu aku udah muji Iya ngena lagu.",
			"18": "<sup>18</sup> Enti aku udah sayauka dosa diri, TUHAN tentu enggai mendingka aku.",
			"19": "<sup>19</sup> Tang Allah Taala endang amat udah ningaka aku. Iya udah mendingka sampi aku.",
			"20": "<sup>20</sup> Pujika Allah Taala, laban Iya enda nulak sampi aku tauka nagang Diri seruran rinduka aku."
		},
		"67": {
			"1": "<sup>1</sup> Allah Taala, kasihka kami lalu berekatka kami. Peda meh kami enggau penelap Nuan,",
			"2": "<sup>2</sup> ngambika semua dunya nemu peneka Nuan, lalu semua bansa nemu pengelepas Nuan.",
			"3": "<sup>3</sup> Awakka semua bansa muji Nuan, O Allah Taala, awakka semua bansa muji Nuan!",
			"4": "<sup>4</sup> Awakka semua bansa lantang lalu belagu gaga ati, laban Nuan ngakim semua bansa enggau pengelurus lalu ngiring genap bengkah bansa di dunya tu.",
			"5": "<sup>5</sup> Awakka semua bansa muji Nuan, O Allah Taala. Awakka semua bansa muji Nuan!",
			"6": "<sup>6</sup> Tanah udah mansutka utai ti diketau. Allah Taala ti Allah Taala kami, udah merekatka kami.",
			"7": "<sup>7</sup> Allah Taala udah merekatka kami. Awakka semua bansa di semua endur ngemesaika Iya."
		},
		"68": {
			"1": "<sup>1</sup> Allah Taala angkat lalu ngachirka bala munsuh Iya. Orang ke begedika Iya, rari alah.",
			"2": "<sup>2</sup> Sida diburu Iya baka asap ti dikebuka ribut. Baka lilin ti lela dipandang api, baka nya meh orang ke jai mati di mua Allah Taala.",
			"3": "<sup>3</sup> Tang orang ke lurus deka lantang sereta rami gaga di mua Iya. Sida lantang sereta manjung gaga ati.",
			"4": "<sup>4</sup> Belagu meh kita ke Allah Taala, belagu meh kita muji nama Iya. Sediaka jalai ke Iya ti nepan ba atas mua hari. Nama Iya TUHAN. Awakka kita gaga di mua Iya!",
			"5": "<sup>5</sup> Allah Taala, ti diau dalam Rumah kudus Iya, ngibun orang ke nadai apai indai, lalu nyaga sida indu ke balu.",
			"6": "<sup>6</sup> Iya meri rumah ngagai orang ke seilu-ilu diau kediri lalu nguluka orang jil pansut lantang ati ngagai pengelepas, tang orang ke angkat ngelaban deka diau di menua ti nadai orang nguan.",
			"7": "<sup>7</sup> O Allah Taala, lebuh Nuan nguluka bala nembiak Nuan, lebuh Nuan bejalai nengah menua puang,",
			"8": "<sup>8</sup> tanah berenyang, lalu hari ujan, laban Allah Taala Bukit Sinai datai, laban Allah Taala ke disembah orang Israel datai.",
			"9": "<sup>9</sup> Nuan ngasuh hari ujan lalu ngasuh menua Nuan ti kusi nyadi manah baru.",
			"10": "<sup>10</sup> Bala nembiak Nuan berumah dia. Ulih pemadas Nuan, Nuan meri utai ngagai orang ke seranta.",
			"11": "<sup>11</sup> TUHAN meri pesan, lalu mayuh bala indu mai berita tu:",
			"12": "<sup>12</sup> “Bala raja enggau bala soldadu sida rari!” Sida indu ke di rumah bedua utai ti udah dirampas, iya nya:",
			"13": "<sup>13</sup> anak patung burung achang ti disampak enggau pirak, ti bisi sayap ti bekebelik laban emas ti alus. (Nama kebuah sekeda kita diau dalam kandang bedus lebuh maya perang?)",
			"14": "<sup>14</sup> Lebuh Allah Taala ti Pemadu Bekuasa ngachirka bala raja ba Bukit Salmon, Iya ngasuh seleju labuh ba bukit nya.",
			"15": "<sup>15</sup> Naka meh pemesai bukit Basan, bukit ti mayuh tuchung!",
			"16": "<sup>16</sup> Nama kebuah nuan, O bukit ti mayuh tuchung, merening ke baruh enggau ati ti kepapas, ngagai bukit ti udah dipilih Allah Taala alai Iya diau? TUHAN deka diau dia belama iya!",
			"17": "<sup>17</sup> TUHAN datai ari Sinai enggau beribu-ribu buah kerita perang Iya ngagai endur ti kudus.",
			"18": "<sup>18</sup> Iya niki ngagai endur ti tinggi, mai mayuh orang tangkap enggau Iya. Iya nerima pemeri ari orang ke udah angkat ngelaban. TUHAN Allah Taala deka diau dia.",
			"19": "<sup>19</sup> Puji meh TUHAN, ke mai ma kitai tiap hari. Iya nya meh Allah Taala ti ngelepaska kitai.",
			"20": "<sup>20</sup> Allah Taala kitai nya Allah Taala ti meri kita pengelepas. Iya nya meh Allah Taala, ti TUHAN kitai, ke ngelepaska kitai ari pemati.",
			"21": "<sup>21</sup> Allah Taala tetap deka mechahka pala bala munsuh Iya, pala orang ke majak bedosa.",
			"22": "<sup>22</sup> TUHAN udah madahka, “Aku deka mai bala munsuh nuan pulai ari Basan. Aku deka mai sida pulai ari buntut tasik,",
			"23": "<sup>23</sup> ngambika kita ulih bejalai ngelunjak darah sida, lalu ukui kita nilat darah sida enggau naka peneka sida.”",
			"24": "<sup>24</sup> Pemenang Nuan, O Allah Taala, dipeda semua orang, lebuh Allah Taala, ti Raja aku, mansang ngagai endur Iya ti kudus.",
			"25": "<sup>25</sup> Bala orang ke belagu bejalai di mua, bala orang ke nebah musik bejalai di belakang, lalu ba entara sida nya bala indu ke biak, ke nebah tamborin.",
			"26": "<sup>26</sup> “Puji meh Allah Taala lebuh bala nembiak Iya begempuru. Puji meh TUHAN, semua kita ke peturun Jakop!”",
			"27": "<sup>27</sup> Sida ke terubah datai nya bala Benjamin, raban bansa ti pemadu mit, ditangkanka bala tuai bansa Judah enggau raban sida, dientudika bala tuai Sebulun enggau Naptali.",
			"28": "<sup>28</sup> Ayanka kuasa Nuan, O Allah Taala, kuasa ti udah dikena Nuan ngarika kami",
			"29": "<sup>29</sup> ari Rumah Nuan di Jerusalem, alai bala raja ke mai pemeri ngagai Nuan.",
			"30": "<sup>30</sup> Rara meh Ejip, jelu ti siga dalam madang buluh. Rara meh semua bansa, raban sapi ti lelaki enggau anak sida, datai ke semua sida nyau meremi lalu meri pirak sida ngagai Nuan. Achirka bala orang ke rindu beperang!",
			"31": "<sup>31</sup> Bala pengari perintah deka datai ari Ejip; orang Etiopia deka ngangkatka jari sida besampi ngagai Allah Taala.",
			"32": "<sup>32</sup> Belagu muji Allah Taala, semua kita perintah dunya, belagu muji TUHAN,",
			"33": "<sup>33</sup> ke Iya ti bejalai nengah langit, langit ti dulu kelia. Pendingka panjung Iya enggau penginggar nyawa Iya.",
			"34": "<sup>34</sup> Padahka kuasa Allah Taala. Iya nyadi Raja merintah Israel. Langit ngayanka kuasa Iya.",
			"35": "<sup>35</sup> Naka meh pemesai kuasa Allah Taala ke disembah orang Israel lebuh Iya datai ari endur Iya ti kudus, Iya ti Allah Taala ke disembah orang Israel! Iya meri pengering enggau kuasa ngagai nembiak Iya. Pujika Allah Taala!"
		},
		"69": {
			"1": "<sup>1</sup> Selamatka aku, O Allah Taala! Ai nyau naka rekung aku.",
			"2": "<sup>2</sup> Aku tengelam ba tanah rebuh ti dalam, lalu nadai tanah ti kering. Aku nyau datai ba ai ti dalam, lalu aku nyau deka lemas laban gelumbang.",
			"3": "<sup>3</sup> Aku nyau lelak ngangau minta tulung, lalu batang rekung aku nyau pedis. Mata aku nyau pedis, beperening ngiga tulung ari Nuan.",
			"4": "<sup>4</sup> Orang ke begedika aku enggau nadai kebuah nyau mayuh agi ari lambar buk ti ba pala aku. Bala munsuh aku nusui utai ti bula ngelaban aku. Sida kering, lalu deka munuh aku. Sida mejalka aku mulaika utai ti ukai dichuri aku.",
			"5": "<sup>5</sup> O Allah Taala, aku enda ngelalaika dosa aku ari Nuan. Nuan nemu pemeli aku ngelamatu.",
			"6": "<sup>6</sup> O TUHAN ti Pemadu Bekuasa, anang nejuka aku mai pemalu ngagai orang ke manggaika pengarap sida ba Nuan! O Allah Taala ke disembah orang Israel, anang nejuka aku mai pemalu ngagai orang ke nyembah Nuan!",
			"7": "<sup>7</sup> Aku udah dipechat lalu dibalut pemalu ketegal Nuan.",
			"8": "<sup>8</sup> Ba kaban belayan aku, aku tu baka orang kampar. Ba kami sebilik, aku tu baka orang bansa bukai.",
			"9": "<sup>9</sup> Pengangkun aku ke Rumah Nuan mau baka api. Jaku orang ke mechat Nuan, ninggang aku.",
			"10": "<sup>10</sup> Aku ngemaruhka diri bepasa, lalu orang mechat aku.",
			"11": "<sup>11</sup> Aku ngerasukka pekayan ulit, lalu sida ketawaka aku.",
			"12": "<sup>12</sup> Sida berandauka aku ba sebelah jalai raya, lalu orang ke pemabuk nyindir aku ngena pantun.",
			"13": "<sup>13</sup> Tang aku deka besampi ngagai Nuan, O TUHAN. Saut meh aku, O Allah Taala, ba maya ti ku ati Nuan. Saut meh aku laban pengerindu Nuan endang besai, laban Nuan ngamatka semaya Nuan ti deka ngelepas.",
			"14": "<sup>14</sup> Selamatka aku ngambika aku enda tengelam ba tanah ti rebuh. Ibun aku likun ari bala munsuh aku, likun ari ai ti dalam.",
			"15": "<sup>15</sup> Anang nejuka ai bah ngampuhka aku. Anang nejuka aku lemas dalam tasik tauka tengelam ba kubur.",
			"16": "<sup>16</sup> Saut aku, O TUHAN, laban pengerindu Nuan endang meruan. Ulih pengasih Nuan, malik meh Nuan ngagai aku!",
			"17": "<sup>17</sup> Anang belalai ari nembiak Nuan, laban aku balat dituntung penusah. Saut meh aku diatu!",
			"18": "<sup>18</sup> Datai ngagai aku, lalu selamatka aku. Selamatka aku ari bala munsuh aku.",
			"19": "<sup>19</sup> Nuan nemu pemalat aku dipechat, pemalat aku malu, lalu enda dibasa. Nuan meda semua munsuh aku.",
			"20": "<sup>20</sup> Jaku pechat orang udah ngeruntuhka ati aku, lalu ati aku balat tusah. Aku udah ngadang orang deka sinu ati ke aku, tang nadai orang sinu ati ke aku. Aku andalka pengelantang, tang aku nadai nemu pengelantang.",
			"21": "<sup>21</sup> Lebuh aku lapar, sida meri aku rachun. Lebuh aku aus, sida meri aku ai chuka.",
			"22": "<sup>22</sup> Awakka pengerami makai besai sida, ngerusak sida. Awakka gawai sida ngasuh sida alah.",
			"23": "<sup>23</sup> Asuh sida buta! Asuh tulang belakang sida lemi!",
			"24": "<sup>24</sup> Bubuhka pengeringat Nuan ba sida. Awakka pengeringat Nuan ngalahka sida.",
			"25": "<sup>25</sup> Awakka kem sida nadai orang nguan. Awakka nadai orang agi idup dalam langkau kain sida.",
			"26": "<sup>26</sup> Laban sida merinsaka orang ke udah diukum Nuan, berandauka pemerinsa orang ke udah bakal laban Nuan.",
			"27": "<sup>27</sup> Tuliska magang semua dosa sida. Anang meri sida bagi dalam pengelepas Nuan.",
			"28": "<sup>28</sup> Awakka nama sida dibuai ari bup pengidup. Awakka nama sida enda ditulis dalam list nama bala nembiak Nuan.",
			"29": "<sup>29</sup> Tang aku pedis sereta tusah ati. Angkatka aku, O Allah Taala, lalu selamatka aku!",
			"30": "<sup>30</sup> Aku deka bepantun muji Allah Taala. Aku deka meri terima kasih ngagai Iya kena aku madahka pemesai kuasa Iya.",
			"31": "<sup>31</sup> Tu deka ngerinduka ati TUHAN lebih agi ari jelu sapi ti diberi nyadika piring, lebih agi ari sapi ti lelaki, ti diberi nyadika piring.",
			"32": "<sup>32</sup> Lebuh orang ke kena tekan meda nya, sida deka gaga. Orang ke nyembah Allah Taala, deka diperansang.",
			"33": "<sup>33</sup> TUHAN mendingka orang ke suntuk lalu ingatka bala nembiak Iya ke dalam rumah jil.",
			"34": "<sup>34</sup> Pujika Allah Taala, O serega enggau dunya, tasik enggau semua utai ti idup dalam sida.",
			"35": "<sup>35</sup> Iya deka ngelepaska Jerusalem lalu nirika baru nengeri di menua Judah. Bala nembiak Iya deka diau dia, lalu bempu menua nya.",
			"36": "<sup>36</sup> Peturun bala nembiak Iya deka bempu menua nya nyadika pesaka sida, lalu orang ke rinduka Iya, deka diau dia."
		},
		"70": {
			"1": "<sup>1</sup> Selamatka aku, O Allah Taala! TUHAN, tulung meh aku diatu!",
			"2": "<sup>2</sup> Awakka orang ke nguji munuh aku alah sereta charut runding. Awakka orang ke rindu meda penusah aku nikal lalu dipechat.",
			"3": "<sup>3</sup> Awakka orang ke beduan aku tusah ati ke diri ke alah.",
			"4": "<sup>4</sup> Awakka semua orang ke datai ngagai Nuan lantang sereta gaga ati. Awakka semua orang ke meri terima kasihka pengelepas Nuan seruran nyebut, “Naka meh pemesai Allah Taala kitai!”",
			"5": "<sup>5</sup> Aku lemi sereta seranta. Datai ngagai aku enggau jampat, O Allah Taala. Nuan endang Juruselamat enggau TUHAN aku. Datai enggau jampat lalu tulung aku!"
		},
		"71": {
			"1": "<sup>1</sup> TUHAN, aku datai ngagai Nuan minta Nuan nyaga aku. Anang nejuka aku alah!",
			"2": "<sup>2</sup> Laban Nuan lurus, nya alai tulung meh aku lalu lepaska aku. Pendingka aku, lalu selamatka aku!",
			"3": "<sup>3</sup> Awakka Nuan nyadi pengelindung aku sereta nyadi kuta ti tegap nyaga aku, laban Nuan nyadika batu enggau kuta aku.",
			"4": "<sup>4</sup> O Allah Taala aku, lepaska aku ari orang ke jai, ari kuasa orang ke merinsaka orang, enggau ari orang ke jai ati.",
			"5": "<sup>5</sup> TUHAN ti Pemadu Tinggi, aku manggaika pengandal ati aku ba Nuan. Aku endang udah manggaika diri ba Nuan kenyau ari aku agi biak.",
			"6": "<sup>6</sup> Aku udah ngarapka Nuan seumur idup. Nuan udah nyaga aku kenyau ari aku ada. Aku deka seruran muji Nuan.",
			"7": "<sup>7</sup> Pengidup aku udah nyadika teladan orang mayuh, laban Nuan udah kering nyaga aku.",
			"8": "<sup>8</sup> Aku muji Nuan sepemanjai hari, lalu madahka mulia Nuan.",
			"9": "<sup>9</sup> Anang nulak aku ketegal aku nyau tuai. Anang ninggalka aku ketegal aku nyau lemi.",
			"10": "<sup>10</sup> Bala munsuh aku deka munuh aku. Sida berandau lalu bepekat deka ngelaban aku.",
			"11": "<sup>11</sup> Ku sida, “Allah Taala udah ninggalka iya. Aram kitai nitihka iya, lalu nangkap iya. Nadai orang deka ngelepaska iya.”",
			"12": "<sup>12</sup> O Allah Taala, anang jauh ari aku; O Allah Taala aku, datai enggau jampat lalu tulung aku!",
			"13": "<sup>13</sup> Awakka orang ke nyerang aku alah lalu dirusak. Awakka orang ke nguji ngemediska aku malu lalu dipechat.",
			"14": "<sup>14</sup> Aku deka seruran manggaika pengandal ati aku ba Nuan. Aku deka bebendar agi muji Nuan.",
			"15": "<sup>15</sup> Aku deka nusui pasal pemadas Nuan. Aku deka bejakuka pasal pengelepas Nuan sepemanjai hari, taja pen aku enda nemu semua utai nya.",
			"16": "<sup>16</sup> Aku deka bejalai ngena pengering TUHAN Allah Taala. Aku deka madahka pemadas Nuan siku aja.",
			"17": "<sup>17</sup> Nuan udah ngajar aku kenyau ari aku agi biak, lalu aku mengkang madahka pemadas pengawa Nuan.",
			"18": "<sup>18</sup> Taja pen aku nyau tuai, lalu buk aku nyau buban, anang meh Nuan ninggalka aku, O Allah Taala! Awakka Nuan seruran enggau aku lebuh aku madahka pasal kuasa enggau pengering Nuan ngagai semua turun-menurun jemah ila.",
			"19": "<sup>19</sup> O Allah Taala, pengelurus Nuan nyau ngeninggi langit. Nuan udah ngereja pengawa ti besai. Nadai orang sebaka enggau Nuan.",
			"20": "<sup>20</sup> Nuan udah ngasuh penusah enggau pemerinsa ninggang aku, tang Nuan deka ngasuh aku kering baru. Nuan deka nagang aku tama ngagai kubur.",
			"21": "<sup>21</sup> Nuan deka ngaga aku besai agi ari ti udah. Nuan deka merindang aku baru.",
			"22": "<sup>22</sup> Aku endang amat deka muji Nuan ngena belikan. Aku deka muji penetap ati Nuan, O Allah Taala aku. Aku deka nebah belikan lalu belagu ke Nuan Allah Taala ti Kudus ke disembah orang Israel.",
			"23": "<sup>23</sup> Aku deka manjung gaga ati lebuh aku nebah musik ke Nuan. Aku deka belagu enggau pengabis pengering, semengat enggau roh aku laban Nuan udah ngelepaska aku.",
			"24": "<sup>24</sup> Aku deka bejakuka pasal pengelurus Nuan sepemanjai hari, laban orang ke nguji ngemediska aku udah alah lalu dipechat."
		},
		"72": {
			"1": "<sup>1</sup> Ajar raja ngambika iya ngakim ngena pengelurus Nuan, O Allah Taala. Bagi ngagai iya atur Nuan ti lurus,",
			"2": "<sup>2</sup> ngambika iya megai bala nembiak Nuan enggau pengelurus lalu merintah orang ke seranta ngena atur ti lurus.",
			"3": "<sup>3</sup> Awakka menua kami nyadi kaya-raja. Awakka menua kami ngasaika pengelurus.",
			"4": "<sup>4</sup> Awakka raja ngakim orang ke seranta ngena ati ti betul. Awakka iya nulung orang ke suntuk lalu ngalahka orang ke nekanka pengidup sida.",
			"5": "<sup>5</sup> Awakka bala nembiak Nuan nyembah Nuan seagi mata hari manchar, seagi bulan meri penampak belama iya.",
			"6": "<sup>6</sup> Awakka raja nyadi baka ujan ti labuh ninggang umai, baka ujan ti beberis ninggang tanah.",
			"7": "<sup>7</sup> Awakka pengelurus ngerembai ngagai semua endur seumur raja, lalu awakka pengaya tan, seagi bulan meri penampak.",
			"8": "<sup>8</sup> Perintah iya deka ngerembai ari tasik siti ngagai tasik siti, ari Eupratis di serata dunya.",
			"9": "<sup>9</sup> Semua bansa ti nguan menua puang deka meremi di mua iya. Bala munsuh iya deka nyungkurka diri ke tanah.",
			"10": "<sup>10</sup> Bala raja menua Spain enggau raja mayuh bengkah pulau deka meri iya pemeri. Raja menua Sheba enggau Seba deka mai pemeri ngagai iya.",
			"11": "<sup>11</sup> Semua raja deka meremi di mua iya. Semua bansa deka bekereja ke iya.",
			"12": "<sup>12</sup> Iya ngelepaska orang ke seranta, ke ngangauka iya, enggau sida ke suntuk sereta enda dikibuhka orang.",
			"13": "<sup>13</sup> Iya sinu ati ke orang ke lemi sereta seranta. Iya ngidupka nyawa sida ke suntuk.",
			"14": "<sup>14</sup> Iya ngelepaska sida ari orang ke nekanka pengidup sida, enggau ari orang ke mangah. Iya amat balat sayauka sida.",
			"15": "<sup>15</sup> Gayu-guru meh raja! Awakka iya diberi emas ari Sheba. Awakka orang besampika iya belama. Awakka Allah Taala seruran merekatka iya!",
			"16": "<sup>16</sup> Awakka gandum seruran mayuh di menua. Awakka bukit penuh laban utai ti ditanam, ngelebat sida ke di menua Lebanon. Awakka nengeri penuh laban mensia, baka padang ti penuh laban rumput.",
			"17": "<sup>17</sup> Awakka nama raja seruran dikingatka. Awakka penampak nama iya meruan baka mata hari. Awakka semua bansa minta Allah Taala merekatka sida baka Iya ti udah merekatka raja.",
			"18": "<sup>18</sup> Pujika TUHAN Allah Taala ke disembah orang Israel! Semina Iya siku aja ti ngereja utai ti badas nya.",
			"19": "<sup>19</sup> Pujika nama Iya ti bemulia belama iya! Awakka mulia Iya ngemenuhka dunya. Amin! Amin!",
			"20": "<sup>20</sup> Nya meh naka sampi David anak Jesi."
		},
		"73": {
			"1": "<sup>1</sup> Allah Taala endang manah enggau orang Israel, enggau orang ke tuchi ati.",
			"2": "<sup>2</sup> Tang aku nyau deka enda agi kempang ati. Pengarap aku nyau deka abis",
			"3": "<sup>3</sup> laban aku ngining orang ke sumbung lebuh aku meda orang ke jai nyamai diau.",
			"4": "<sup>4</sup> Sida nadai natka diri pedis. Sida kering sereta bepengerai.",
			"5": "<sup>5</sup> Sida enda merinsa baka orang bukai. Sida enda ditinggang penusah baka orang bukai.",
			"6": "<sup>6</sup> Nya alai sida betenggakka penyumbung lalu nyerayung diri enggau pemangah.",
			"7": "<sup>7</sup> Ati sida nuang penyai, lalu sida kiruh bekiraka akal ti jai.",
			"8": "<sup>8</sup> Sida ketawaka orang bukai, lalu bejakuka utai ti jai. Sida sumbung lalu begagaka pelan kena nekanka pengidup orang bukai.",
			"9": "<sup>9</sup> Sida bejaku jai pasal Allah Taala ti di serega lalu ngeninggika diri meri atur ngagai semua orang di dunya,",
			"10": "<sup>10</sup> datai ke bala nembiak Allah Taala Empu nyau nguingka diri ngagai sida lalu gagit ati arapka sebarang utai ti disebut sida.",
			"11": "<sup>11</sup> Ku sida, “Allah Taala enda nemu. Allah Taala ti Pemadu Tinggi enda nemu.”",
			"12": "<sup>12</sup> Nya meh gaya orang ke jai. Sida endang mayuh utai, lalu selalu bulih mayuh agi utai.",
			"13": "<sup>13</sup> Enti pia, nama guna aku diau tuchi lalu enda bedosa?",
			"14": "<sup>14</sup> O Allah Taala, Nuan udah ngasuh aku natka pemerinsa sepemanjai hari. Genap pagi Nuan udah nganu aku.",
			"15": "<sup>15</sup> Enti aku bisi udah nyebut utai munyi nya, aku tentu enda ngaga diri baka nembiak Nuan.",
			"16": "<sup>16</sup> Aku udah nguji berundingka pasal penusah tu, tang utai tu kelalu mar ke aku",
			"17": "<sup>17</sup> datai ke aku nyau mansang ngagai Rumah Nuan. Nya baru aku nemu utai ti deka nyadi ba orang ke jai.",
			"18": "<sup>18</sup> Nuan ngengkahka sida ba endur ti lichin lalu ngasuh sida labuh ngagai pengerusak!",
			"19": "<sup>19</sup> Sida dirusak tekala nya, lalu pengujung sida amat jai.",
			"20": "<sup>20</sup> Sida baka mimpi ti lenyau pagi hari. Lebuh Nuan dani, O TUHAN, sida lesap.",
			"21": "<sup>21</sup> Lebuh runding aku jai lalu ati aku pedis,",
			"22": "<sup>22</sup> aku beli baka jelu. Aku enda meretika Nuan.",
			"23": "<sup>23</sup> Tang aku seruran semak Nuan, lalu Nuan megai jari aku.",
			"24": "<sup>24</sup> Nuan ngiring aku ngena ajar Nuan lalu ba pengujung iya, Nuan deka nerima aku ngena basa.",
			"25": "<sup>25</sup> Nama utai bukai ti dikemisi aku di serega kelimpah ari Nuan Empu? Laban aku bisi Nuan, nama utai bukai agi dikedekaka aku di dunya tu?",
			"26": "<sup>26</sup> Runding enggau tubuh aku nyangka tau nyadi lemi, tang Allah Taala nyadika pengering aku. Semina Iya siku aja diguna aku.",
			"27": "<sup>27</sup> Orang ke ninggalka Nuan tentu deka mati. Nuan deka ngerusak sida ke enda tetap ati ngagai Nuan.",
			"28": "<sup>28</sup> Tang aku empu, naka meh pengelantang aku diau semak Allah Taala, dijaga TUHAN ti Pemadu Tinggi, lalu madahka pasal semua utai ti udah dikereja Iya!"
		},
		"74": {
			"1": "<sup>1</sup> Nama kebuah Nuan ninggalka kami baka tu, O Allah Taala? Kati Nuan deka ringat belama iya ngagai bala nembiak Nuan?",
			"2": "<sup>2</sup> Kingatka bala nembiak Nuan, ti udah dipilih Nuan Empu dulu kelia, ti udah dibai Nuan pansut ari pengidup sida ke nyadi ulun, lalu nyadi raban bansa Nuan Empu. Kingatka Bukit Sion, ti udah alai Nuan diau dulu kelia.",
			"3": "<sup>3</sup> Bejalai meh Nuan nengah semua tembawai tu. Bala munsuh kami udah ngerusak semua utai dalam Rumah Sembiang.",
			"4": "<sup>4</sup> Bala munsuh Nuan udah manjung dalam Rumah Nuan madahka pemenang diri. Sida udah nuatka menira kena sida nandaka diri menang.",
			"5": "<sup>5</sup> Gamal sida baka orang ke kereja batang ke betebangka kayu ngena kapak.",
			"6": "<sup>6</sup> Sida mechahka semua papan ukir ngena kapak enggau tukul sida.",
			"7": "<sup>7</sup> Sida ngerusak lalu nunu Rumah Nuan. Sida ngamahka endur alai Nuan disembah.",
			"8": "<sup>8</sup> Sida endang deka ngalahka kami abis. Sida nunu semua endur ti kudus di menua tu.",
			"9": "<sup>9</sup> Semua tanda ti kudus udah abis magang. Bala nabi nadai agi tinggal, lalu nadai orang nemu pengelama utai tu deka nyadi.",
			"10": "<sup>10</sup> Ni pengelama bala munsuh kami deka ketawaka Nuan, O Allah Taala? Kati sida deka mechat nama Nuan belama iya?",
			"11": "<sup>11</sup> Nama kebuah Nuan enggai nulung kami? Nama kebuah Nuan diau jenuh lalu melawikka jari?",
			"12": "<sup>12</sup> Tang Nuan endang Raja kami kenyau ari pun, O Allah Taala. Nuan suah udah ngelepaska kami.",
			"13": "<sup>13</sup> Ngena pengering kuasa Nuan, Nuan udah melah tasik lalu mechahka pala naga tasik.",
			"14": "<sup>14</sup> Nuan udah ngeredakka pala naga tasik lalu ngasuh bangkai iya diempa jelu di menua puang.",
			"15": "<sup>15</sup> Nuan ngasuh ai pansut ari mata ai, lalu ngasuh ai engkeching. Nuan udah ngerangkaika sungai ti besai.",
			"16": "<sup>16</sup> Nuan udah ngaga siang enggau malam. Nuan udah ngengkahka mata hari enggau bulan ba endur sida.",
			"17": "<sup>17</sup> Nuan ngeletak pemesai dunya. Nuan ngeletak musin panas enggau musin chelap.",
			"18": "<sup>18</sup> Tang ingat, O TUHAN, bala munsuh Nuan ketawaka Nuan. Sida nya orang ke enda arapka Nuan, sereta ngeleseka Nuan.",
			"19": "<sup>19</sup> Anang ninggalka bala nembiak Nuan ti nadai daya tu ba jari munsuh sida ke jai. Anang enda ingatka bala nembiak Nuan ke diperinsaka orang!",
			"20": "<sup>20</sup> Kingatka sempekat ti udah digaga Nuan enggau kami. Utai ti kasar bisi ba semua teluk ti petang di menua tu.",
			"21": "<sup>21</sup> Anang nejuka orang ngemaluka sida ke kena tekan. Awakka orang ke seranta enggau suntuk muji Nuan.",
			"22": "<sup>22</sup> Angkat meh Nuan, Allah Taala, lalu bejaku ngarika Nuan Empu! Kingatka orang bansa bukai ke ketawaka Nuan sepemanjai hari.",
			"23": "<sup>23</sup> Anang enda ingatka panjung munsuh Nuan, penginggar bala munsuh Nuan ti enda nemu badu."
		},
		"75": {
			"1": "<sup>1</sup> Kami meri terima kasih ngagai Nuan, O Allah Taala. Kami meri terima kasih ngagai Nuan! Kami madahka pemesai kuasa Nuan lalu nusui pasal utai ti badas ti udah dikereja Nuan.",
			"2": "<sup>2</sup> Ku Allah Taala bejaku, “Aku udah ngeletak maya alai ngakim, lalu Aku deka ngakim enggau lurus.",
			"3": "<sup>3</sup> Taja pen semua utai ti idup nyau begetar magang lalu tanah pen berenyang, tang Aku deka ngasuh pugu dunya meruan tegap.",
			"4": "<sup>4</sup> Aku ngasuh orang ke jai badu agi ngenyumbungka diri.",
			"5": "<sup>5</sup> Aku ngasuh sida badu agi temegah bejaku.”",
			"6": "<sup>6</sup> Laban pemutus pechara ukai datai ari timur tauka ari barat, tauka ari utara, tauka ari selatan,",
			"7": "<sup>7</sup> tang Allah Taala Empu ke ngakim, ke ngukum sekeda lalu ngelepaska sida ti bukai.",
			"8": "<sup>8</sup> TUHAN megai mangkuk ba jari Iya, ti penuh laban wain pengeringat Iya ti bisa. Iya nuang wain nya, lalu semua orang ke jai bela ngirup wain nya datai ke nadai setitik agi tinggal.",
			"9": "<sup>9</sup> Tang aku enggai badu bejaku ngenang pasal Allah Taala Jakop tauka belagu muji Iya.",
			"10": "<sup>10</sup> Iya deka ngerusak kuasa orang ke jai, tang kuasa orang ke lurus deka ditambahka."
		},
		"76": {
			"1": "<sup>1</sup> Orang di menua Judah endang nemu Allah Taala. Nama Iya endang dikemesaika orang di menua Israel.",
			"2": "<sup>2</sup> Iya bisi rumah di Jerusalem. Iya diau ba Bukit Sion.",
			"3": "<sup>3</sup> Dia meh endur alai Iya ke matahka panah munsuh, terabai, enggau pedang, enggau semua senyata sida.",
			"4": "<sup>4</sup> Naka meh pemesai mulia Nuan, O Allah Taala! Naka meh pemesai kuasa Nuan lebuh Nuan pulai ari bukit alai Nuan ti udah ngalahka bala munsuh Nuan.",
			"5": "<sup>5</sup> Semua utai bala soldadu sida ke berani udah abis dirampas lalu sida diatu tinduk baka orang ke mati. Semua pengering enggau pengelandik sida nadai guna agi.",
			"6": "<sup>6</sup> Lebuh Nuan ngemba sida, O Allah Taala Jakop, kuda enggau orang ke nepan kuda mati.",
			"7": "<sup>7</sup> Nuan, O TUHAN, endang diketakutka semua mensia. Nadai orang tan bediri di mua Nuan lebuh Nuan ringat.",
			"8": "<sup>8</sup> Nuan meri pemutus pechara ari serega. Dunya takut lalu enda engkeretik,",
			"9": "<sup>9</sup> lebuh Nuan angkat lalu madahka pemutus pechara, kena Nuan ngelepaska semua orang ke kena tekan di dunya tu.",
			"10": "<sup>10</sup> Pengeringat mensia semina ngasuh Nuan balat agi dipuji. Orang ke enda mati beperang deka ngintu gawai Nuan.",
			"11": "<sup>11</sup> Beri ngagai TUHAN Allah Taala kita utai ti udah disemaya kita. Bai meh pemeri ngagai Iya, semua kita bansa ti semak. Allah Taala ngenakutka semua mensia.",
			"12": "<sup>12</sup> Iya ngemaruhka raja ti sumbung lalu ngemba raja ti besai."
		},
		"77": {
			"1": "<sup>1</sup> Aku nyabak duie-duie ngagai Allah Taala. Aku nyabak duie-duie, lalu Iya ninga aku.",
			"2": "<sup>2</sup> Lebuh maya tusah, aku besampi ngagai TUHAN. Semalam-malam aku ngangkatka jari besampi, tang aku nadai nemu utai ti ulih merindang aku.",
			"3": "<sup>3</sup> Lebuh aku berundingka Allah Taala, aku bedemuh. Lebuh aku belematika Iya, semengat aku alah.",
			"4": "<sup>4</sup> Iya nagang aku tinduk semalam-malam. Ati aku balat irau, datai ke aku nyau enda ulih bejaku.",
			"5": "<sup>5</sup> Aku berundingka utai ti udah nyadi lalu ngingatka taun ti dulu kelia.",
			"6": "<sup>6</sup> Semalam-malam nya aku bebendar amat berunding. Aku belemati, lalu tu utai ti ditanyaka aku dalam ati aku:",
			"7": "<sup>7</sup> “Kati Allah Taala deka seruran nulak kitai? Kati Iya enggai agi gaga ati ke kitai?",
			"8": "<sup>8</sup> Kati Iya udah badu rinduka kitai? Kati semaya Iya nadai diguna agi?",
			"9": "<sup>9</sup> Kati Allah Taala enda ingat kasihka kitai? Kati pengeringat Iya udah dibali Iya nyadi penyiru?”",
			"10": "<sup>10</sup> Udah nya aku bejaku, “Utai ti balat ngenusahka ati aku nya: Allah Taala nadai bekuasa agi.”",
			"11": "<sup>11</sup> Aku deka ngingatka pemesai kereja Nuan, O TUHAN. Aku deka ngingatka utai ti nyelai ti udah dikereja Nuan dulu kelia.",
			"12": "<sup>12</sup> Aku deka berundingka semua utai ti udah dikereja Nuan. Aku deka belematika semua pengawa Nuan ti besai.",
			"13": "<sup>13</sup> Semua utai ti dikereja Nuan, O Allah Taala, endang kudus magang. Nadai petara bukai ke bekuasa baka Nuan.",
			"14": "<sup>14</sup> Nuan tu meh Allah Taala ti ngereja kereja ajih. Nuan udah ngayanka kuasa Nuan ngagai semua bansa.",
			"15": "<sup>15</sup> Ngena kuasa Nuan, Nuan udah nebus bala nembiak Nuan, iya nya peturun Jakop enggau Josep.",
			"16": "<sup>16</sup> Lebuh tasik meda Nuan, O Allah Taala, sida takut, lalu buntut tasik begetar.",
			"17": "<sup>17</sup> Mua hari mubuhka ai ujan. Guntur meletup di langit lalu kilat nyelar.",
			"18": "<sup>18</sup> Guntur Nuan inggar deram-deram, lalu kilat ti nyelar meri penampak ngagai dunya. Dunya begetar sereta berenyang.",
			"19": "<sup>19</sup> Nuan bejalai ngerenchah gelumbang. Nuan meraka tasik ti dalam, tang bekau kaki Nuan enda ulih dipeda.",
			"20": "<sup>20</sup> Nuan nguluka bala nembiak Nuan baka pengibun bedus lalu Moses seduai Aron nyadi tuai sida."
		},
		"78": {
			"1": "<sup>1</sup> Pendingka ajar aku, kita ke nembiak aku, lalu peratika utai ti disebut aku.",
			"2": "<sup>2</sup> Aku deka bejaku ngena jaku sempama lalu nerangka utai ti nyelai ti nyadi dulu kelia,",
			"3": "<sup>3</sup> utai ti udah didinga sereta ditemu kitai, utai ti udah dipadahka aki ini kitai ngagai kitai.",
			"4": "<sup>4</sup> Kitai enda tau ngelalaika utai nya ari bala anak kitai. Kitai deka madahka utai nya ngagai serak ti nangkanka kitai pasal kuasa TUHAN, enggau pasal pemesai pengawa Iya enggau utai ti badas ti udah dikereja Iya.",
			"5": "<sup>5</sup> Iya meri adat ngagai orang Israel enggau pesan ngagai peturun Jakop. Iya meri atur ngagai bala aki ini kitai ngasuh sida ngajar Adat Iya ngagai bala anak sida,",
			"6": "<sup>6</sup> ngambika serak ke nangkanka sida ulih belajarka adat nya lalu madahka adat nya ngagai bala anak sida ke nangkanka sida.",
			"7": "<sup>7</sup> Nya meh jalai sida deka manggaika pengarap sida ba Allah Taala lalu seruran ingatka utai ti udah dikereja Iya, sereta seruran ngasika pesan Iya.",
			"8": "<sup>8</sup> Sida enda baka aki ini sida ke seruran angkat ngelaban sereta enda ngasi, ti nadai kala manggaika pengarap sida tegap ba Allah Taala lalu enda tetap ati ngagai Iya.",
			"9": "<sup>9</sup> Orang Epraim, ke besenyataka panah enggau laja, rari lebuh beperang.",
			"10": "<sup>10</sup> Sida enda ngingatka sempekat sida enggau Allah Taala. Sida enggai ngasika Adat Iya.",
			"11": "<sup>11</sup> Sida enda ingatka utai ti udah dikereja Iya, iya nya kereja ajih ti udah dipeda sida kereja Iya.",
			"12": "<sup>12</sup> Lebuh bala aki ini sida ngemata, Allah Taala ngereja kereja ajih ba emperan Zoan, di menua Ejip.",
			"13": "<sup>13</sup> Iya melah tasik, lalu mai sida bejalai nengah tasik nya. Iya ngasuh ai bediri baka dinding.",
			"14": "<sup>14</sup> Lebuh siang hari, Iya nguluka sida ngena mua hari lalu lebuh malam hari Iya nguluka sida ngena penampak daun api.",
			"15": "<sup>15</sup> Iya melah batu di menua puang lalu meri sida mayuh ai.",
			"16": "<sup>16</sup> Iya ngasuh ai pansut ari batu lalu ngasuh ai nya rarat baka sungai.",
			"17": "<sup>17</sup> Tang sida majak bedosa ngelaban Allah Taala, lalu di menua puang nya sida angkat ngelaban Iya ti Pemadu Tinggi.",
			"18": "<sup>18</sup> Sida endang sengaja nguji Allah Taala lebuh sida minta pemakai ti dikedekaka sida.",
			"19": "<sup>19</sup> Sida bejaku ngelaban Allah Taala, lalu ku sida, “Kati Allah Taala ulih meri pemakai di menua puang tu?",
			"20": "<sup>20</sup> Iya amat udah malu batu, lalu ai rarat ari batu nya baka ai bah. Tang kati Iya ulih mega meri kitai roti lalu meri dagin ngagai bala nembiak Iya?”",
			"21": "<sup>21</sup> Nya alai TUHAN ringat lebuh Iya ninga sida. Iya nyerang bala nembiak Iya ngena api, lalu Iya majak ringat ngagai sida,",
			"22": "<sup>22</sup> laban sida enda arapka Iya lalu enda arapka Iya deka ngidupka sida.",
			"23": "<sup>23</sup> Tang Iya bejaku ngagai langit di atas ngasuh pintu langit tebuka.",
			"24": "<sup>24</sup> Iya meri sida gandum ari serega, lebuh Iya meri sida mana nyadika pemakai sida.",
			"25": "<sup>25</sup> Nya alai sida makai pemakai melikat, lalu Allah Taala meri sida sepemayuh ti dikedekaka sida.",
			"26": "<sup>26</sup> Iya mega ngasuh ribut timur muput, lalu ngena kuasa Iya, Iya nyungkak ribut muput ari selatan.",
			"27": "<sup>27</sup> Lalu ngagai bala nembiak Iya, Iya nganjung burung ngemayuh igi pasir ti ba tebing tasik.",
			"28": "<sup>28</sup> Burung nya labuh ba tengah-tengah kem ngelingi semua langkau kain.",
			"29": "<sup>29</sup> Nya alai orang mayuh makai, lalu kenyang magang. Allah Taala meri sida utai ti dikedekaka sida.",
			"30": "<sup>30</sup> Tang sebedau sida puas ati makai utai nya, lebuh pemakai nya agi dalam nyawa sida,",
			"31": "<sup>31</sup> Allah Taala lalu ringat ngagai sida lalu munuh bala lelaki sida ke kering, orang ke pemadu kering di menua Israel.",
			"32": "<sup>32</sup> Tang taja pia, orang mayuh majak bedosa. Taja pen Iya ngereja kereja ajih, sida mengkang enggai manggaika pengarap sida ba Iya.",
			"33": "<sup>33</sup> Nya alai Iya ngabiska umur sida baka kesuh seput lalu ngapuska nyawa sida ngena penusah ti datai enggau ngenyit.",
			"34": "<sup>34</sup> Genap kali Iya munuh sekeda bala sida, sida ti bukai nguingka diri ngagai Iya. Sida nesal ati, lalu bebendar besampi ngagai Iya.",
			"35": "<sup>35</sup> Sida ingatka Allah Taala endang penyaga sida, ingatka Iya ti Pemadu Bekuasa udah datai nulung sida.",
			"36": "<sup>36</sup> Tang jaku sida bula semua. Utai ti disebut sida ukai datai ari ati.",
			"37": "<sup>37</sup> Sida enda taluk ngagai Iya. Sida enda tetap ati ngagai sempekat sida enggau Iya.",
			"38": "<sup>38</sup> Tang Allah Taala kasihka nembiak Iya. Iya ngampun dosa sida lalu enda ngerusak sida. Iya suah nagang Diri ringat lalu nagang Diri ngeransi.",
			"39": "<sup>39</sup> Iya ingatka sida endang semina mensia aja, baka ribut ti mansa lalu nadai datai agi.",
			"40": "<sup>40</sup> Sida suah amat angkat ngelaban Iya di menua puang. Sida suah ngasuh ati Iya tusah!",
			"41": "<sup>41</sup> Sida enda nemu badu nguji Allah Taala lalu ngenusahka ati Allah Taala ke disembah orang Israel ti kudus.",
			"42": "<sup>42</sup> Sida enda ingatka pemesai kuasa Iya enggau hari Iya ngelepaska sida ari bala munsuh sida",
			"43": "<sup>43</sup> sereta ngereja pengawa ti besai enggau kereja ajih ba emperan Zoan di menua Ejip.",
			"44": "<sup>44</sup> Iya ngasuh sungai bebali nyadi darah, lalu orang Ejip nadai ai irup.",
			"45": "<sup>45</sup> Iya ngasuh lalat datai kena merinsaka sida, enggau engkatak kena ngerusak menua sida.",
			"46": "<sup>46</sup> Iya ngasuh buntak makai utai ti ditanam sida lalu ngerusak umai sida.",
			"47": "<sup>47</sup> Iya ngerusak batang anggur sida ngena ujan batu enggau kayu ara sida ngena ambun ti meku.",
			"48": "<sup>48</sup> Iya munuh jelu sapi sida ngena ujan batu enggau jelu tupi sida ngena kilat.",
			"49": "<sup>49</sup> Iya ngasuh sida balat tusah lebuh Iya mubuhka pengeringat enggau ransi Iya, ti datai mai pemati.",
			"50": "<sup>50</sup> Iya enda nagang diri ringat tauka ngalau nyawa sida, tang munuh sida ngena penyakit ti balat.",
			"51": "<sup>51</sup> Iya munuh anak tuai ari semua ruang bilik orang Ejip.",
			"52": "<sup>52</sup> Udah nya, Iya lalu nguluka bala nembiak Iya, baka pengibun bedus lalu ngiring sida nengah menua puang.",
			"53": "<sup>53</sup> Iya nguluka sida enggau likun, lalu sida enda takut. Tang tasik ngelamun bala munsuh sida.",
			"54": "<sup>54</sup> Iya mai sida ngagai menua Iya ti kudus, ngagai bukit ti udah dialahka Iya Empu.",
			"55": "<sup>55</sup> Iya muru orang ke nguan menua nya, lebuh bala nembiak Iya nyerang. Iya magi menua sida ngagai raban bansa orang Israel lalu meri rumah orang nya ngagai bala nembiak Iya.",
			"56": "<sup>56</sup> Tang sida angkat ngelaban Allah Taala ti Pemadu Bekuasa lalu nguji Iya. Sida enda ngasika adat Iya,",
			"57": "<sup>57</sup> tang ngelaban sereta enda taluk baka aki ini sida, enda tau dikarapka, baka laja ti bingkuk.",
			"58": "<sup>58</sup> Sida nyungkak TUHAN ringat ngena endur alai orang kapir nyembah petara sida, lalu nyungkak Iya pedis ati ngena engkeramba sida.",
			"59": "<sup>59</sup> Allah Taala ringat lebuh Iya meda utai nya, nya alai Iya nulak semua nembiak Iya.",
			"60": "<sup>60</sup> Iya ninggalka langkau kain Iya di Siloh, rumah ti udah alai Iya diau begulai enggau kitai.",
			"61": "<sup>61</sup> TUHAN nejuka bala munsuh kitai ngerampas Peti Lampang Batu Adat, ti nyadika tanda kuasa enggau mulia Iya.",
			"62": "<sup>62</sup> Iya ringat ngagai bala nembiak Iya Empu lalu nejuka sida dibunuh munsuh.",
			"63": "<sup>63</sup> Orang ke bujang mati beperang, lalu sida ke dara nadai orang enggau jadi.",
			"64": "<sup>64</sup> Bala imam mati dibunuh ngena pedang, lalu balu sida enda tau ngulit.",
			"65": "<sup>65</sup> Udah nya TUHAN angkat baka ke dani tinduk, baka orang ke baru gerai mabuk.",
			"66": "<sup>66</sup> Iya muru bala munsuh Iya lalu sida rari lalu Iya ngemaluka sida belama iya.",
			"67": "<sup>67</sup> Tang Iya nulak peturun Josep. Iya enda milih raban bansa Epraim,",
			"68": "<sup>68</sup> tang milih raban bansa Judah enggau Bukit Sion, ti balat dikerinduka Iya.",
			"69": "<sup>69</sup> Dia Iya ngaga Rumah Sembiang Iya baka rumah Iya ti di serega. Iya ngaga Rumah Sembiang nya tegap baka dunya empu, kukuh belama iya.",
			"70": "<sup>70</sup> TUHAN milih nembiak Iya David. Iya ngambi David ari padang rumput,",
			"71": "<sup>71</sup> alai David ke ngibun raban bedus iya, lalu ngasuh iya nyadi raja menua Israel, nyadi pengibun ke bala nembiak Allah Taala.",
			"72": "<sup>72</sup> David ngibun sida enggau pengabis pengangkun ati iya lalu nguluka sida enggau pengelandik."
		},
		"79": {
			"1": "<sup>1</sup> O Allah Taala, orang bansa bukai udah nyerang menua Nuan. Sida udah ngamahka Rumah Nuan ti kudus lalu ngaga Jerusalem nyadi tembawai.",
			"2": "<sup>2</sup> Sida ninggalka bangkai bala nembiak Nuan diempa burung, ninggalka bangkai bala ulun Nuan diempa jelu babas.",
			"3": "<sup>3</sup> Sida ngasuh darah bala nembiak Nuan nyaya baka ai di serata nengeri Jerusalem, lalu nadai orang nguburka sida.",
			"4": "<sup>4</sup> Semua bansa ti ngelingi kami, mechat kami. Sida ketawaka kami, lalu ngeleseka kami.",
			"5": "<sup>5</sup> TUHAN, kati Nuan deka ringat ngagai kami belama iya? Kati pengeringat Nuan deka majak mau baka api?",
			"6": "<sup>6</sup> Kuingka pengeringat Nuan ngagai bansa ti enda nyembah Nuan, ngagai orang ke enda besampi ngagai Nuan.",
			"7": "<sup>7</sup> Laban sida udah munuh bala nembiak Nuan. Sida udah ngerusak menua Nuan.",
			"8": "<sup>8</sup> Anang ngukum kami ketegal dosa aki ini kami. Kasihka kami diatu. Kami nadai pengadang agi.",
			"9": "<sup>9</sup> Tulung kami, O Allah Taala, lalu selamatka kami. Lepaska kami, lalu ampun dosa kami ketegal nama Nuan.",
			"10": "<sup>10</sup> Anang nejuka orang bansa bukai nanya ngagai kami, “Dini alai Allah Taala kita?” Awakka kami meda Nuan ngukum bansa bukai laban sida udah munuh bala nembiak Nuan.",
			"11": "<sup>11</sup> Pendingka orang jil bedemuh. lalu enggau kuasa Nuan ti besai, lepaska orang ke diukum bunuh.",
			"12": "<sup>12</sup> TUHAN, balas tujuh kali lipat jaku pechat ti udah dikena orang bansa bukai mechat Nuan.",
			"13": "<sup>13</sup> Nya baru kami ke nembiak Nuan, ke ari raban bedus Nuan, deka meri terima kasih ngagai Nuan belama iya lalu muji Nuan belama iya jemah ila."
		},
		"80": {
			"1": "<sup>1</sup> Pendingka kami, O Pengibun orang Israel. Dingaka kami, Nuan ke nguluka raban bedus Nuan. Nuan ke duduk ba kerusi diraja Nuan ari atas melikat cherub ti besayap,",
			"2": "<sup>2</sup> pandangka Diri ngagai raban bansa Epraim, Benjamin enggau Manasi. pandangka pengering Nuan ngagai kami. Datai lalu selamatka kami!",
			"3": "<sup>3</sup> Bai kami pulai baru, O Allah Taala! Ayanka pengasih Nuan ke kami, ngambika kami diidupka!",
			"4": "<sup>4</sup> O TUHAN ti Pemadu Bekuasa, ni pengelama Nuan deka mengkang ringat ke sampi bala nembiak Nuan?",
			"5": "<sup>5</sup> Nuan udah ngasuh penyinu ati kami nyadika pemakai kami, enggau sejalung ai mata nyadika ai irup kami.",
			"6": "<sup>6</sup> Nuan nejuka bansa ti ngelingi kami berebutka menua kami. Bala munsuh kami mechat kami.",
			"7": "<sup>7</sup> Bai kami pulai baru, O Allah Taala ti Pemadu Bekuasa! Ayanka pengasih Nuan ke kami, ngambika kami diidupka!",
			"8": "<sup>8</sup> Nuan mai batang anggur pansut ari menua Ejip. Nuan muru semua bansa bukai, lalu nanam batang anggur nya ba menua sida.",
			"9": "<sup>9</sup> Nuan ngupas endur alai iya tumbuh. Urat iya ngunjam, lalu nyerangkap di serata menua nya.",
			"10": "<sup>10</sup> Batang anggur nya ngayung semua bukit. Dan iya ngayung kayu sidar ti besai.",
			"11": "<sup>11</sup> Iya nyuntaika dan iya ngagai Tasik Tengah nyentuk ngagai Sungai Eupratis.",
			"12": "<sup>12</sup> Nama kebuah Nuan ngeruntuhka pagar ti ngelingi iya? Diatu semua orang ke mansa ulih ngenchuri buah iya.",
			"13": "<sup>13</sup> Babi utan ngelunjak iya, lalu jelu babas makai buah iya.",
			"14": "<sup>14</sup> Kuingka Diri ngagai kami, O Allah Taala ti Pemadu Bekuasa! Tilik ari serega ngagai kami di baruh ditu. Datai lalu selamatka bala nembiak Nuan!",
			"15": "<sup>15</sup> Datai lalu selamatka batang anggur ti udah ditanam Nuan, batang anggur ti muda tu, ti udah diasuh Nuan kering!",
			"16": "<sup>16</sup> Bala munsuh kami udah nunu iya, lalu nebang iya. Perening ngagai sida enggau pengeringat, lalu rusak sida!",
			"17": "<sup>17</sup> Ibun sereta jaga bala orang ke udah dipilih Nuan, bansa ti udah diasuh Nuan kering.",
			"18": "<sup>18</sup> Kami enggai agi ninggalka Nuan. Idupka kami, lalu kami deka muji Nuan.",
			"19": "<sup>19</sup> Bai kami pulai baru, O TUHAN Allah Taala ti Pemadu Bekuasa. Ayanka pengasih Nuan ke kami, ngambika kami diidupka."
		},
		"81": {
			"1": "<sup>1</sup> Manjung gaga ati ke Allah Taala ti nyaga kitai. Belagu muji Allah Taala Jakop!",
			"2": "<sup>2</sup> Tebah musik enggau tamborin. Tebah musik ti manah didinga ngena belikan enggau engkeratung.",
			"3": "<sup>3</sup> Sepu terumpit lebuh kita begawai, lebuh maya anak bulan, enggau lebuh bulan peranama.",
			"4": "<sup>4</sup> Tu adat menua Israel, atur ti diletak Allah Taala Jakop.",
			"5": "<sup>5</sup> Iya meri adat nya ngagai orang Israel lebuh Iya nyerang menua Ejip. Aku ninga munyi nyawa ti enda dikelala bejaku,",
			"6": "<sup>6</sup> “Aku ngingkaska ma ti berat ari belakang kita. Aku ngasuh kita ngelengkaka batu bata ari jari kita.",
			"7": "<sup>7</sup> Lebuh kita kena laban penusah, kita ngangauka Aku, lalu Aku ngelepaska kita. Ari endur alai Aku ke belalai dalam biaubalai, Aku nyaut kita. Aku nguji kita ba mata ai di Meribah.",
			"8": "<sup>8</sup> Pendingka jaku tangkan Aku, kita ke nembiak Aku. Israel, naka meh pemanah enti kita deka mendingka Aku!",
			"9": "<sup>9</sup> Kita enda tau sekali-kali nyembah petara bukai.",
			"10": "<sup>10</sup> Aku tu TUHAN Allah Taala kita, ti udah mai kita pansut ari menua Ejip. Perangapka nyawa kita, ngambika Aku nyapi kita.",
			"11": "<sup>11</sup> “Tang bala nembiak Aku enggai mendingka Aku. Israel enggai ngasika Aku.",
			"12": "<sup>12</sup> Nya alai Aku nejuka sida nitihka pengekieh sida lalu ngereja sebarang utai ti dikedekaka sida.",
			"13": "<sup>13</sup> Naka meh pemanah enti bala nembiak Aku deka mendingka Aku. Naka meh pemanah enti sida deka ngasika Aku!",
			"14": "<sup>14</sup> Aku deka ngalahka munsuh sida enggau jampat lalu ngalahka semua munsuh sida.",
			"15": "<sup>15</sup> Orang ke begedika Aku deka meremi enggau ati ti nangi di mua Aku. Ukum sida deka mengkang belama iya.",
			"16": "<sup>16</sup> Tang aku deka nupi kita ngena gandum ti pemadu manah lalu ngasuh kita kenyang ngena ai manyi.”"
		},
		"82": {
			"1": "<sup>1</sup> Allah Taala nguluka orang serega baum. Iya meri pemutus penemu ngagai bala petara:",
			"2": "<sup>2</sup> “Kita enda tau enda badu agi ngakim ngena chara ti enda lurus. Kita enda tau agi ngeringka orang ke jai!",
			"3": "<sup>3</sup> Beri atur ti lurus ngagai orang ke seranta enggau orang ke nadai apai indai; jaga hak orang ke seranta, enggau ke merinsa.",
			"4": "<sup>4</sup> Lepaska sida ari kuasa orang ke jai.",
			"5": "<sup>5</sup> “Naka meh penadai penemu kita! Naka meh pemeli kita! Kita tu amat jai, lalu atur ti lurus udah lesap ari dunya tu.",
			"6": "<sup>6</sup> Ku Aku bejaku, ‘Kita tu petara. Semua kita tu anak Allah Taala ti Pemadu Tinggi.’",
			"7": "<sup>7</sup> Tang kita deka mati baka mensia. Umur kita deka abis baka umur anak raja.”",
			"8": "<sup>8</sup> Datai meh Nuan, O Allah Taala, lalu perintah meh dunya tu. Semua bansa endang enggi Nuan magang."
		},
		"83": {
			"1": "<sup>1</sup> O Allah Taala, anang enda bemunyi. Anang enda engkebut, anang diau jenuh!",
			"2": "<sup>2</sup> Peda bala munsuh Nuan angkat ngelaban, lalu orang ke begedika Nuan angkat ngelaban.",
			"3": "<sup>3</sup> Sida begagaka pelan ti resia kena sida ngelaban bala nembiak Nuan. Sida bepekat ngelaban orang ke dijaga Nuan.",
			"4": "<sup>4</sup> Ku sida bejaku, “Aram kitai ngerusak bansa sida, ngambika bansa orang Israel nadai agi dikingatka belama iya.”",
			"5": "<sup>5</sup> Sida lalu besetujuka pelan nya, lalu beserakup ngelaban Nuan:",
			"6": "<sup>6</sup> orang Edom enggau orang Ismail, orang Moap enggau orang Hagri,",
			"7": "<sup>7</sup> orang Gebal, Amon, enggau orang Amalek, orang Pilistin, enggau orang Tire.",
			"8": "<sup>8</sup> Asiria mega setanah enggau sida nyadi raban ti kering ke orang Amon enggau orang Moap, ti peturun Lot.",
			"9": "<sup>9</sup> Laban meh sida baka Nuan ti udah ngelaban orang Midian, baka Nuan ti udah ngelaban Sisera seduai Jabin ba Sungai Kison.",
			"10": "<sup>10</sup> Nuan udah ngalahka sida di En-dor, lalu bangkai sida but ba tanah.",
			"11": "<sup>11</sup> Serang meh bala tuai sida baka Nuan ti udah nyerang Orep seduai Sip. Alahka semua orang ke merintah sida, baka Nuan ti udah ngalahka Sebah seduai Salmuna,",
			"12": "<sup>12</sup> ke nyebut, “Kitai deka ngerampas menua Allah Taala nyadika menua kitai empu.”",
			"13": "<sup>13</sup> Taburka sida baka amau, O Allah Taala, baka rumput ti rangkai ti dikebuka ribut,",
			"14": "<sup>14</sup> baka api ti nganguska utan, baka daun api ti nyelar nganguska munggu,",
			"15": "<sup>15</sup> baka nya meh Nuan muru sida ngena biaubalai Nuan lalu ngenakutka sida ngena ribut Nuan ti balat.",
			"16": "<sup>16</sup> Asuh sida berasai malu, O TUHAN, ngambika sida ngiga nama Nuan.",
			"17": "<sup>17</sup> Awakka sida alah lalu takut belama iya. Awakka sida mati sereta abis dipechat.",
			"18": "<sup>18</sup> Awakka sida nemu Nuan siku aja ti TUHAN, Raja ti Pemadu Tinggi ke serata dunya."
		},
		"84": {
			"1": "<sup>1</sup> Naka meh pemanah Rumah Nuan, O TUHAN ti Pemadu Bekuasa!",
			"2": "<sup>2</sup> Naka meh pengelengau ati aku ke Rumah TUHAN. Lalu enggau pengabis semengat, roh enggau tubuh aku, aku belagu muji Allah Taala ti idup.",
			"3": "<sup>3</sup> Indah pipit deh bisi endur alai diau, lalu lelayang bisi sarang ke diri empu, alai iya nyimpan anak iya, ba alta Nuan, O TUHAN ti Pemadu Bekuasa, Raja enggau Allah Taala aku.",
			"4": "<sup>4</sup> Beberekat meh orang ke diau dalam Rumah Nuan, ke seruran belagu muji Nuan.",
			"5": "<sup>5</sup> Beberekat meh orang ke manggaika diri ba pengering Nuan, ke gagit ati deka nangga Bukit Sion.",
			"6": "<sup>6</sup> Lebuh sida bejalai nengah lebak ti rangkai, sida ngaga endur nya nyadi endur ti mayuh mata ai. Ujan lebuh musin ruruh daun ngasuh endur nya penuh laban letung.",
			"7": "<sup>7</sup> Sida mansang kering-sekering lebuh sida bejalai. Sida deka meda Allah Taala ti ari atas semua petara, di Sion.",
			"8": "<sup>8</sup> O TUHAN Allah Taala ti Pemadu Bekuasa, dingaka sampi aku. Pendingka aku, O Allah Taala Jakop!",
			"9": "<sup>9</sup> Nuan meh terabai kami, O Allah Taala, Ayanka pengerindu Nuan ngagai iya ti udah dipilih Nuan.",
			"10": "<sup>10</sup> Sehari dalam Rumah Nuan manah agi ari seribu hari ba endur bukai. Manah agi aku jaga ba mua pintu Rumah Allah Taala aku ari ti diau dalam langkau kain orang ke jai.",
			"11": "<sup>11</sup> Laban TUHAN Allah Taala baka mata hari enggau baka terabai; Iya nelap sereta meri basa. Iya nadai kala enggai meri utai ti manah ngagai orang ke ngereja utai ti ngena.",
			"12": "<sup>12</sup> TUHAN ti Pemadu Bekuasa, beberekat meh orang ke arapka Nuan!"
		},
		"85": {
			"1": "<sup>1</sup> TUHAN, Nuan udah kasihka menua Nuan. Nuan udah ngasuh Israel nyadi kaya-raja baru.",
			"2": "<sup>2</sup> Nuan udah ngampun dosa nembiak Nuan lalu ngampun penyalah sida.",
			"3": "<sup>3</sup> Nuan enda agi ringat ngagai sida lalu nagang Diri ringat pedis ati ke sida.",
			"4": "<sup>4</sup> Bai kami pulai baru, O Allah Taala ti Juruselamat kami lalu badu agi enda lantang ati ngagai kami!",
			"5": "<sup>5</sup> Kati Nuan deka ringat ngagai kami belama iya? Kati pengeringat Nuan enda nemu badu?",
			"6": "<sup>6</sup> Asuh kami kering baru, ngambika kami ke nembiak Nuan muji Nuan.",
			"7": "<sup>7</sup> O TUHAN, ayanka ngagai kami pengerindu Nuan ti meruan belama iya, lalu tulung kami, selamatka kami.",
			"8": "<sup>8</sup> Aku mendingka utai ti disebut TUHAN Allah Taala. Iya besemaya deka meri pemaik ngagai kitai ke nembiak Iya, enti kitai enda pulai baru ngagai ulah kitai ti beli.",
			"9": "<sup>9</sup> Iya tentu sedia deka ngelepaska orang ke ngemesaika Iya, lalu mulia Iya deka diau di menua kitai.",
			"10": "<sup>10</sup> Pengerindu enggau penetap ati deka betemu. Pengelurus enggau pemaik deka beberap.",
			"11": "<sup>11</sup> Penaluk mensia deka niki ari dunya, lalu pengelurus Allah Taala deka nilik ke baruh ari serega.",
			"12": "<sup>12</sup> TUHAN deka ngasuh kitai nyadi kaya-raja, lalu kitai deka bulih ngetau.",
			"13": "<sup>13</sup> Pengelurus deka bejalai dulu ari TUHAN lalu nyendiaka jalai Iya."
		},
		"86": {
			"1": "<sup>1</sup> Pendingka aku, O TUHAN, lalu saut aku, laban aku nadai daya agi sereta lemi.",
			"2": "<sup>2</sup> Selamatka aku ari pemati, laban aku taluk ngagai Nuan. Selamatka aku, laban aku tu nembiak Nuan lalu aku manggaika pengarap aku ba Nuan.",
			"3": "<sup>3</sup> Nuan endang Allah Taala aku, nya alai kasihka aku. Aku besampi ngagai Nuan sepemanjai hari.",
			"4": "<sup>4</sup> Asuh ati nembiak Nuan gaga, O TUHAN, laban aku besampi ngagai Nuan.",
			"5": "<sup>5</sup> Nuan endang manah enggau kami, sereta ngampun. Nuan penuh laban pengerindu ke semua orang ke besampi ngagai Nuan.",
			"6": "<sup>6</sup> Pendingka sampi aku, O TUHAN. Dingaka aku ke nyabak minta tulung.",
			"7": "<sup>7</sup> Aku ngangauka Nuan lebuh maya tusah, laban Nuan nyaut sampi aku.",
			"8": "<sup>8</sup> Nadai petara bukai ke baka Nuan, O TUHAN. Nadai siku ti udah ngereja utai baka ke udah dikereja Nuan.",
			"9": "<sup>9</sup> Semua bansa ti udah digaga Nuan deka datai lalu meremi ngagai Nuan. Sida deka muji pemesai kuasa Nuan.",
			"10": "<sup>10</sup> Nuan endang bekuasa, sereta ngereja utai ti badas. Semina Nuan siku aja ti Allah Taala.",
			"11": "<sup>11</sup> Ajar aku, O TUHAN, utai ti diasuh Nuan kereja aku, ngambika aku tetap ati ngasika Nuan. Ajar aku betuaika Nuan enggau pengabis pengangkun ati aku.",
			"12": "<sup>12</sup> Aku deka muji Nuan enggau pengabis ati aku, O TUHAN Allah Taala aku. Aku deka ngemesaika Nuan belama iya.",
			"13": "<sup>13</sup> Naka meh pemesai pengerindu Nuan ti meruan belama iya. Nuan udah ngelepaska aku ari kubur.",
			"14": "<sup>14</sup> O Allah Taala, orang ke sumbung datai ngelaban aku, bala orang ke jai nguji munuh aku, orang ke enggai mendingka Nuan.",
			"15": "<sup>15</sup> Tang Nuan, O TUHAN, endang Allah Taala ti kasih sereta rindu, seruran liat ati, seruran nelap sereta tetap ati.",
			"16": "<sup>16</sup> Nguing ngagai aku, lalu kasihka aku. Keringka aku, lalu selamatka aku, laban aku betuaika Nuan baka indai aku suba.",
			"17": "<sup>17</sup> Ayanka ngagai aku tanda pemadas Nuan, TUHAN, ngambika orang ke begedika aku nemu malu lebuh sida meda Nuan udah merindang sereta nulung aku."
		},
		"87": {
			"1": "<sup>1</sup> TUHAN ngaga nengeri Iya ba bukit ti kudus.",
			"2": "<sup>2</sup> Iya rinduka nengeri Jerusalem lebih ari Iya ti rinduka endur bukai di menua Israel.",
			"3": "<sup>3</sup> Nengeri Allah Taala, pendingka utai ti manah ti disebut Iya pasal nuan.",
			"4": "<sup>4</sup> Ngagai orang ke nemu aku, aku madahka pasal Ejip enggau Babilon. Pilistin, Tire, enggau Etiopia mega. Lalu sida deka nyebut, “Iya tu udah diadaka din.”",
			"5": "<sup>5</sup> Ba pasal Sion, orang deka nyebut, “Iya tu enggau iya nya endang ada ditu”, laban Allah Taala ti Pemadu Bekuasa deka ngeringka iya.",
			"6": "<sup>6</sup> TUHAN deka nuliska nama semua bansa lalu nyengkaum semua sida nyadi peranak nengeri Jerusalem magang.",
			"7": "<sup>7</sup> Sida betanda sereta belagu, “Penatai semua berekat kitai endang ari Sion.”"
		},
		"88": {
			"1": "<sup>1</sup> TUHAN Allah Taala ti Juruselamat aku, aku nyabak sepemanjai hari, lalu lebuh malam hari, aku datai ngadap ngagai Nuan.",
			"2": "<sup>2</sup> Dingaka sampi aku. Pendingka kangau aku ke minta tulung!",
			"3": "<sup>3</sup> Mayuh amat penusah udah ninggang aku datai ke aku nyau deka mati.",
			"4": "<sup>4</sup> Aku tu baka semua orang bukai ke nyau deka mati. Aku nadai pengering agi.",
			"5": "<sup>5</sup> Aku ditinggalka begulai enggau orang ke mati. Aku tu baka orang ke udah dibunuh, ke garing-garing dalam kubur, ti enda dikingatka Nuan agi, ti enda ulih ditulung agi.",
			"6": "<sup>6</sup> Nuan udah muai aku ngagai buntut lubang kubur, ngagai lubang kebung ti pemadu petang sereta dalam.",
			"7": "<sup>7</sup> Pengeringat Nuan berat amat ninggang aku, lalu aku nyau redak dilamun umbak iya.",
			"8": "<sup>8</sup> Nuan udah ngasuh bala pangan aku ninggalka aku. Nuan udah ngasuh sida keji meda aku. Aku dikelulung, lalu enda ulih rari.",
			"9": "<sup>9</sup> Mata aku nyau kabur laban merinsa. O TUHAN, tiap hari aku ngangauka Nuan lalu ngangkatka jari aku besampi.",
			"10": "<sup>10</sup> Kati Nuan ngereja kereja ajih ke orang ke mati? Kati sida ke mati angkat lalu muji Nuan?",
			"11": "<sup>11</sup> Kati pengerindu Nuan ti meruan belama iya bisi dikenang dalam kubur? Kati penetap ati Nuan dikenang ba endur alai pengerusak?",
			"12": "<sup>12</sup> Kati kereja ajih Nuan bisi dipeda ba endur ti petang nya? Kati pemadas Nuan bisi ditemu di dunya pemati?",
			"13": "<sup>13</sup> O TUHAN, aku ngangauka Nuan minta tulung. Tiap pagi aku besampi ngagai Nuan.",
			"14": "<sup>14</sup> Nama kebuah Nuan nulak aku, O TUHAN? Nama kebuah Nuan ninggalka aku?",
			"15": "<sup>15</sup> Kenyau ari aku agi biak, aku udah merinsa lalu nyau deka mati. Aku nyau lelak natka pemerat ukum ari Nuan.",
			"16": "<sup>16</sup> Pemalat pengeringat Nuan ngelamun aku. Serang Nuan ngerusak aku.",
			"17": "<sup>17</sup> Sida ngelulung aku baka ai bah sepemanjai hari. Sida datai semak ngagai aku ari semua endur.",
			"18": "<sup>18</sup> Nuan udah ngasuh pangan aku ninggalka aku. Ka semina pemetang aja nyadika sulu aku."
		},
		"89": {
			"1": "<sup>1</sup> O TUHAN, aku deka seruran belagu ngenang pengerindu Nuan ti meruan belama iya. Aku deka madahka penetap ati Nuan belama iya.",
			"2": "<sup>2</sup> Aku nemu pengerindu Nuan deka meruan belama iya, lalu penetap ati Nuan deka meruan baka langit.",
			"3": "<sup>3</sup> Ku Nuan, “Aku udah ngaga sempekat enggau siku orang ke udah dipilih Aku. Aku udah besemaya enggau nembiak Aku David,",
			"4": "<sup>4</sup> ‘Peturun nuan deka betampung nyadi raja. Aku deka ngasuh perintah nuan tan belama iya.’ ”",
			"5": "<sup>5</sup> Serega belagu muji pemanah utai ti dikereja Nuan. Sida ke kudus belagu muji penetap ati Nuan, O TUHAN.",
			"6": "<sup>6</sup> Nadai orang di langit sebaka enggau Nuan, O TUHAN. Nadai orang di serega sebaka enggau Nuan.",
			"7": "<sup>7</sup> Raban kunsil ti kudus nangi magang ke Nuan. Semua sida bela takutka Nuan.",
			"8": "<sup>8</sup> O TUHAN Allah Taala ti Pemadu Bekuasa, nadai orang bekuasa baka Nuan. O TUHAN, Nuan endang tetap ati dalam semua utai.",
			"9": "<sup>9</sup> Nuan merintah tasik ti begelumbang, lalu Nuan ngasuh gelumbang badu agi mangka.",
			"10": "<sup>10</sup> Nuan ngalahka Rahap lalu munuh iya. Nuan ngalahka bala munsuh Nuan ngena pengering Nuan.",
			"11": "<sup>11</sup> Serega endang enggi Nuan, lalu baka nya mega dunya. Nuan udah ngaga dunya enggau semua utai ti dalam iya.",
			"12": "<sup>12</sup> Nuan udah ngaga utara enggau selatan. Bukit Tabor enggau Bukit Hermon belagu gaga ati ke Nuan.",
			"13": "<sup>13</sup> Naka meh pemesai kuasa Nuan! Naka meh pengering Nuan!",
			"14": "<sup>14</sup> Perintah Nuan beduduk ba pengelurus enggau atur ti manah. Pengerindu enggau penetap ati Nuan nyata ba semua utai ti dikereja Nuan.",
			"15": "<sup>15</sup> Beberekat meh orang ke nyembah Nuan ngena lagu, orang ke diau dalam penampak penelap Nuan!",
			"16": "<sup>16</sup> Sida rami gaga sepemanjai hari ketegal Nuan, lalu sida muji Nuan ketegal pemadas Nuan.",
			"17": "<sup>17</sup> Nuan udah ngasuh kami menang. Ulih pengerindu Nuan, Nuan udah ngasuh kami menang.",
			"18": "<sup>18</sup> Nuan, O TUHAN, udah milih orang nyaga kami laban Nuan, O Allah Taala ti Kudus orang Israel, udah meri kami raja.",
			"19": "<sup>19</sup> Dalam genera dulu kelia, Nuan udah nyebut ngagai bala nembiak Nuan ke tetap ati, “Aku udah meri tulung ngagai siku soldadu ti tebilang nama. Aku udah meri kerusi diraja ngagai iya ti udah dipilih Aku ari orang mayuh.",
			"20": "<sup>20</sup> Aku udah nyiri nembiak Aku David nyadi raja lebuh Aku nata iya ngena minyak ti kudus.",
			"21": "<sup>21</sup> Pengering Aku deka seruran enggau iya. Kuasa Aku deka ngasuh iya kering.",
			"22": "<sup>22</sup> Bala munsuh iya enda ulih ngelaban iya. Orang ke jai enda ulih ngalahka iya.",
			"23": "<sup>23</sup> Aku deka nginsarka bala munsuh iya, lalu munuh semua orang ke begedika iya.",
			"24": "<sup>24</sup> Aku deka rinduka iya sereta tetap ati ngagai iya. Aku deka ngasuh iya seruran menang.",
			"25": "<sup>25</sup> Aku deka ngerembaika perintah iya kenyau ari Tasik Tengah nyentuk ngagai Sungai Eupratis.",
			"26": "<sup>26</sup> Iya deka nyebut ngagai aku, ‘Nuan Apai enggau Allah Taala aku. Nuan Penyaga enggau Juruselamat aku.’",
			"27": "<sup>27</sup> Aku deka ngaga iya nyadi anak tuai Aku, ke bekuasa agi ari semua raja.",
			"28": "<sup>28</sup> Aku deka seruran ngamatka semaya Aku enggau iya, lalu sempekat Aku enggau iya deka meruan belama iya.",
			"29": "<sup>29</sup> Perintah iya deka meruan tan baka langit. Peturun iya deka betampung nyadi raja.",
			"30": "<sup>30</sup> Tang enti peturun iya ngelanggar Adat Aku, lalu enda nitihka pesan Aku,",
			"31": "<sup>31</sup> enti sida enda ngasika atur Aku lalu enda nitihka pesan Aku,",
			"32": "<sup>32</sup> Aku deka ngukum sida ketegal dosa sida. Aku deka ngasuh sida merinsa ketegal penyalah sida.",
			"33": "<sup>33</sup> Tang Aku enda badu rinduka David lalu ngamatka semaya Aku enggau iya.",
			"34": "<sup>34</sup> Aku enggai ngelanggar sempekat Aku enggau iya tauka matak sebarang semaya ti udah digaga Aku enggau iya.",
			"35": "<sup>35</sup> Aku udah besemaya sekali aja dalam nama Aku ti kudus: Aku enggai sekali-kali bulaka David.",
			"36": "<sup>36</sup> Iya deka meruan bisi peturun, lalu Aku deka ngemataka perintah iya seagi-agi mata hari manchar.",
			"37": "<sup>37</sup> Perintah iya deka meruan tan belama iya baka bulan, ti nyadika saksi ti tetap ati di langit.”",
			"38": "<sup>38</sup> Tang Nuan ringat ngagai raja ti udah dipilih Nuan. Nuan udah ninggalka iya lalu nulak iya.",
			"39": "<sup>39</sup> Nuan udah mungkir ari sempekat Nuan enggau nembiak Nuan lalu nikauka makota iya ke tanah.",
			"40": "<sup>40</sup> Nuan udah ngeruntuhka dinding kuta nengeri iya lalu ninggalka kuta iya nyadi tembawai.",
			"41": "<sup>41</sup> Semua orang ke mansa, ngenchuri utai iya. Semua orang ke berimbai pendiau enggau iya, ketawaka iya.",
			"42": "<sup>42</sup> Nuan udah ngasuh bala munsuh iya menang. Nuan udah ngasuh sida lantang ati magang.",
			"43": "<sup>43</sup> Nuan udah ngasuh senyata iya nadai tau diguna, lalu nejuka iya alah beperang.",
			"44": "<sup>44</sup> Nuan udah ngerampas tungkat diraja ari jari iya lalu nikauka kerusi diraja iya ke tanah.",
			"45": "<sup>45</sup> Nuan udah ngasuh iya jampat tuai lalu ngemaluka iya.",
			"46": "<sup>46</sup> TUHAN, kati Nuan deka ngelalaika Diri belama iya? Ni pengelama pengeringat Nuan agi mau baka api?",
			"47": "<sup>47</sup> Kingatka pemandak umur aku. Kingatka Diri ti udah ngaga semua kami mensia!",
			"48": "<sup>48</sup> Sapa ulih idup lalu enda mati? Baka ni ku kami mensia ulih nagang diri tama ngagai kubur?",
			"49": "<sup>49</sup> TUHAN, dini alai tanda pengerindu Nuan? Dini alai semaya ti udah digaga Nuan enggau David?",
			"50": "<sup>50</sup> Anang enda ingatka aku, ke nembiak Nuan, ke dipechat orang, ke natka diri disumpah semua orang kapir.",
			"51": "<sup>51</sup> Bala munsuh Nuan mechat raja ti udah dipilih Nuan, O TUHAN! Sida mechat iya di sebarang endur iya bejalai.",
			"52": "<sup>52</sup> Puji meh TUHAN belama iya! Amin! Amin!"
		},
		"90": {
			"1": "<sup>1</sup> O TUHAN, Nuan endang seruran nyadika rumah kami.",
			"2": "<sup>2</sup> Sebedau Nuan nempa bukit tauka nempa dunya, Nuan endang Allah Taala ti meruan belama iya, nadai pun nadai ujung, lalu Nuan deka nyadi Allah Taala belama iya.",
			"3": "<sup>3</sup> Nuan udah ngasuh kami pulai ngagai gaya asal kami. Nuan ngubah kami nyadi amau baru.",
			"4": "<sup>4</sup> Ba Nuan, seribu taun baka sehari. Sida iya baka kemari ti nadai agi, baka ke sejam lebuh malam hari.",
			"5": "<sup>5</sup> Nuan nabanka kami baka ai bah. Kami tu baka mimpi, enda tan lama. Kami tu baka rumput ti matuk pagi hari,",
			"6": "<sup>6</sup> tumbuh lalu bebungai lebuh pagi hari, lalu udah nya, rangkai lalu mati lemai nya.",
			"7": "<sup>7</sup> Kami rusak laban pengeringat Nuan. Nuan ngeransi, lalu kami takut.",
			"8": "<sup>8</sup> Nuan ngengkahka dosa kami di mua Nuan; indah dosa ti dikereja kami belalai, deh ditemu Nuan.",
			"9": "<sup>9</sup> Umur kami nyau pandak laban pengeringat Nuan; umur kami nyau abis baka kesuh seput.",
			"10": "<sup>10</sup> Umur kami semina 70 taun aja, lapan puluh taun, enti kami regas; tang umur kami semina mai pemerinsa enggau ati ti tusah. Umur jampat abis, lalu kami nadai agi.",
			"11": "<sup>11</sup> Sapa udah kala ngasaika diri ditempuh kuasa pengeringat Nuan? Sapa nemu pemalat kami takutka diri diransi Nuan?",
			"12": "<sup>12</sup> Ajar kami pasal pemandak umur kami, ngambika kami tau nyadi orang ke pintar.",
			"13": "<sup>13</sup> Ni pengelama Nuan agi ringat? Kasihka kami ke nembiak Nuan, O TUHAN!",
			"14": "<sup>14</sup> Isi kami genap pagi enggau pengerindu Nuan ti meruan belama iya, ngambika kami ulih belagu lalu gaga ati sepemanjai umur kami.",
			"15": "<sup>15</sup> Beri kami diatu pengelantang ngenyampau penusah ti udah diberi Nuan ngagai ati kami dalam berapa taun kami ke natka diri merinsa.",
			"16": "<sup>16</sup> Awakka kami ke nembiak Nuan, meda pengawa Nuan ti besai. Awakka peturun kami meda pemesai kuasa mulia Nuan.",
			"17": "<sup>17</sup> O TUHAN Allah Taala kami, awakka berekat Nuan seruran enggau kami. Asuh kami mujur dalam semua utai ti dikereja kami!"
		},
		"91": {
			"1": "<sup>1</sup> Barang sapa ke datai ngagai TUHAN ngiga pengelikun, barang sapa ke dijaga Iya ti Pemadu Bekuasa,",
			"2": "<sup>2</sup> tau nyebut ngagai Iya, “Nuan endang penyaga enggau pengelindung aku. Nuan endang Allah Taala aku. Aku manggaika pengarap aku ba Nuan.”",
			"3": "<sup>3</sup> Iya deka ngibun nuan likun ari semua penusah ti enda dipeda, enggau ari semua penyakit ti tau munuh.",
			"4": "<sup>4</sup> Iya deka mungkur nuan ngena sayap Iya. Nuan deka likun diibun Iya. Penetap ati Iya deka ngelindung sereta nyaga nuan.",
			"5": "<sup>5</sup> Anang nuan takutka utai ti tau ngemedis lebuh malam hari tauka diserang enggau ngenyit lebuh siang hari",
			"6": "<sup>6</sup> tauka penyakit ti nyerang dalam petang tauka utai ti jai ti munuh lebuh siang hari.",
			"7": "<sup>7</sup> Seribu iku deka rebah ngimbai nuan, sepuluh ribu iku ngelingi nuan, tang nuan enda bebadi.",
			"8": "<sup>8</sup> Nuan deka malik, lalu tepedaka chara orang ke jai diukum.",
			"9": "<sup>9</sup> Nuan udah ngasuh TUHAN nyaga nuan lalu Allah Taala ti Pemadu Tinggi ngelindung nuan.",
			"10": "<sup>10</sup> Nya alai nuan enda ditinggang penusah, lalu pengachau pen nadai mega datai semak rumah nuan.",
			"11": "<sup>11</sup> Allah Taala deka ngasuh bala melikat Iya ngibun nuan lalu nyaga nuan di sebarang endur nuan bejalai.",
			"12": "<sup>12</sup> Sida deka nyambut nuan ngena jari sida ngambika kaki nuan enda terempas ba batu.",
			"13": "<sup>13</sup> Nuan deka ngelunjak singa enggau ular, singa ti mangah enggau ular ti bisa.",
			"14": "<sup>14</sup> Ku Allah Taala bejaku, “Aku deka ngelepaska orang ke rinduka Aku lalu nyaga orang ke ngaku Aku nyadika TUHAN sida.",
			"15": "<sup>15</sup> Lebuh sida ngangauka Aku, Aku deka nyaut sida. Lebuh sida kena laban penusah, Aku deka enggau sida. Aku deka ngelepaska sida lalu masa sida.",
			"16": "<sup>16</sup> Aku deka ngupah sida ngena umur ti panjai. Aku deka ngelepaska sida.”"
		},
		"92": {
			"1": "<sup>1</sup> Naka meh pemanah meri terima kasih ngagai Nuan, O TUHAN, belagu ngemesaika Nuan, O Allah Taala ti Pemadu Tinggi,",
			"2": "<sup>2</sup> genap pagi madahka pengerindu Nuan ti meruan belama iya enggau genap malam madahka penetap ati Nuan,",
			"3": "<sup>3</sup> ngena musik ari engkeratung enggau belikan.",
			"4": "<sup>4</sup> Pengawa Nuan ti besai, O TUHAN, ngasuh ati aku gaga. Ketegal utai ti udah dikereja Nuan, aku belagu gaga ati.",
			"5": "<sup>5</sup> Naka meh pemesai kereja Nuan, O TUHAN! Naka meh penalam runding Nuan!",
			"6": "<sup>6</sup> Nya utai ti enda ulih ditemu orang ke beli, ti enda ulih disagam orang ke lap.",
			"7": "<sup>7</sup> Orang ke jai nyangka ulih tumbuh baka rumput, orang ke ngereja penyalah nyangka tau nyadi kaya-raja, tang sida deka dirusak abis lengis,",
			"8": "<sup>8</sup> laban Nuan, TUHAN, endang bekuasa atas semua belama iya.",
			"9": "<sup>9</sup> Kami nemu munsuh Nuan deka mati, lalu semua orang ke jai deka alah magang.",
			"10": "<sup>10</sup> Nuan udah ngasuh aku kering baka sapi ti siga. Nuan udah merekatka aku enggau pengelantang.",
			"11": "<sup>11</sup> Aku udah meda bala munsuh aku alah lalu ninga sabak orang ke jai.",
			"12": "<sup>12</sup> Orang ke lurus deka galak baka kayu engkerema. Sida deka tumbuh baka kayu sidar di menua Lebanon.",
			"13": "<sup>13</sup> Sida baka kayu ti ditanam dalam rumah TUHAN, ti galak dalam Rumah Sembiang Allah Taala kami,",
			"14": "<sup>14</sup> ti agi bebuah lebuh sida nyau tuai lalu seruran gadung sereta kering.",
			"15": "<sup>15</sup> Tu nunjukka TUHAN endang lurus ati, lalu nadai utai ti enda lurus ba penyaga aku."
		},
		"93": {
			"1": "<sup>1</sup> TUHAN endang Raja! Iya begari ngena mulia enggau pengering. Dunya diatur tegap lalu enda ulih dikinsitka.",
			"2": "<sup>2</sup> Kerusi diraja Nuan, O TUHAN, endang udah tegap kenyau ari pun lalu Nuan endang udah bisi sebedau dunya nyadi.",
			"3": "<sup>3</sup> O TUHAN, tasik ti dalam nyau berauh. Sida inggar amat begelumbang.",
			"4": "<sup>4</sup> TUHAN merintah ari atas semua di serega, besai agi ari munyi pangka gelumbang, bekuasa agi ari gelumbang ti di tasik.",
			"5": "<sup>5</sup> Adat Nuan endang meruan belama iya, O TUHAN, lalu Rumah Nuan endang amat kudus, belama lama iya."
		},
		"94": {
			"1": "<sup>1</sup> O TUHAN, Nuan Allah Taala ti ngukum, pandangka pengeringat Nuan!",
			"2": "<sup>2</sup> Nuan nyadi Hakim ke kami semua. Angkat meh Nuan lalu ukum orang ke sumbung ketegal utai ti udah dikereja sida!",
			"3": "<sup>3</sup> Ni pengelama orang ke jai agi majak gaga ati? Ni pengelama agi, TUHAN?",
			"4": "<sup>4</sup> Ni pengelama orang ke ngereja penyalah agi majak sumbung lalu temegah bejakuka penyai ti dikereja sida?",
			"5": "<sup>5</sup> Sida nginsarka bala nembiak Nuan, O TUHAN. Sida nekanka pengidup orang ke diempu Nuan.",
			"6": "<sup>6</sup> Sida munuh indu ke balu enggau anak ti nadai apai indai, lalu munuh orang kampar ti diau di menua kami.",
			"7": "<sup>7</sup> Ku sida, “Nadai TUHAN ulih meda kitai. Allah Taala ke disembah orang Israel enda ulih meda kitai.”",
			"8": "<sup>8</sup> Nembiak aku, nama kebuah kita beli baka nya? Kemaya kita ulih mereti?",
			"9": "<sup>9</sup> Indah pending kitai deh digaga Allah Taala - kati Iya enda ulih ninga? Lalu mata kitai ditempa Iya - kati Iya enda ulih meda?",
			"10": "<sup>10</sup> Semua bansa diukum Iya magang - kati Iya enggai ngukum sida? Iya nyadi pengajar ke semua kitai - kati Iya nadai penemu?",
			"11": "<sup>11</sup> TUHAN nemu runding kitai. Iya nemu pemeli runding kitai.",
			"12": "<sup>12</sup> O TUHAN, beberekat meh orang ke diajar Nuan, orang ke diajar Nuan adat Nuan!",
			"13": "<sup>13</sup> Nuan ngasuh sida belelak ari hari ti penuh laban penusah nyentuk ngagai maya lubang kebung ditumbak ke orang ke jai.",
			"14": "<sup>14</sup> TUHAN enggai ninggalka bala nembiak Iya. Iya enggai ngelengkaka sida ke endang pesaka Iya.",
			"15": "<sup>15</sup> Atur ti lurus deka ditemu baru dalam kot, lalu semua orang ke lurus deka nyukung nya.",
			"16": "<sup>16</sup> Sapa orang ke udah ngarika aku ngelaban orang ke jai? Sapa orang ke udah setanah enggau aku ngelaban orang ke ngereja penyai?",
			"17": "<sup>17</sup> Enti TUHAN enda nulung aku, aku tentu udah jampat lenyau ngagai dunya pemati.",
			"18": "<sup>18</sup> Ku aku, “Aku labuh!” Tang pengerindu Nuan ti meruan belama iya, O TUHAN, ngasuh aku ulih bediri tegap.",
			"19": "<sup>19</sup> Lebuh ati aku mayuh pengirau, Nuan merindang aku, lalu ngasuh ati aku gaga.",
			"20": "<sup>20</sup> Nuan nadai kaul enggau bala hakim ti jai, ke ngaga atur ti jai lalu ngumbai atur nya betul,",
			"21": "<sup>21</sup> ke bepekat ngelaban orang ke lurus lalu meri ukum bunuh ngagai orang ke nadai penyalah.",
			"22": "<sup>22</sup> Tang TUHAN ngeringka aku. Allah Taala aku nyaga aku.",
			"23": "<sup>23</sup> Iya deka ngukum sida ketegal penyai sida lalu ngerusak sida ketegal dosa sida. TUHAN Allah Taala kitai deka ngerusak sida."
		},
		"95": {
			"1": "<sup>1</sup> Aram kitai muji TUHAN! Aram kitai belagu gaga ati ke Allah Taala ti nyaga kitai!",
			"2": "<sup>2</sup> Aram kitai datai ngagai Iya mai lagu terima kasih lalu nyanyi pantun puji enggau ati ti gaga.",
			"3": "<sup>3</sup> Laban TUHAN endang Allah Taala ti bekuasa Raja ke bekuasa ari atas semua petara.",
			"4": "<sup>4</sup> Iya merintah semua dunya, kenyau ari lubang gua ti pemadu dalam nyentuk ngagai bukit ti pemadu tinggi.",
			"5": "<sup>5</sup> Iya merintah tasik ti udah digaga Iya Empu enggau tanah ti udah ditempa jari Iya Empu.",
			"6": "<sup>6</sup> Aram kitai meremi lalu nyembah Iya. Aram kitai besugang di mua TUHAN ti udah ngaga kitai!",
			"7": "<sup>7</sup> Iya endang Allah Taala kitai. Kitai tu orang ke diibun Iya, raban ke ditupi Iya. Sehari tu dingaka utai ti disebut Iya:",
			"8": "<sup>8</sup> “Anang kieh, baka aki ini kita di Meribah, baka sida sehari nya di menua puang di Masah.",
			"9": "<sup>9</sup> Dia meh alai sida ke ngabas ati Aku sereta nguji Aku, taja pen sida udah meda utai ti udah dikereja Aku.",
			"10": "<sup>10</sup> Empat puluh taun Aku udah kejika orang nya. Ku Aku, ‘Naka meh pemalat sida tu enda taluk! Sida enggai ngasika pesan Aku.’",
			"11": "<sup>11</sup> Aku ringat, lalu besemaya ngena sumpah. ‘Kita enda ulih tama ngagai menua ti alai Aku deka ngasuh kita belelak.’ ”"
		},
		"96": {
			"1": "<sup>1</sup> Nyanyi lagu ti baru ke TUHAN! Belagu ke TUHAN, semua dunya!",
			"2": "<sup>2</sup> Belagu ke TUHAN, lalu puji Iya! Padahka tiap hari berita manah pasal Iya ti udah ngelepaska kitai.",
			"3": "<sup>3</sup> Padahka mulia Iya ngagai semua bansa; padahka ngagai semua mensia pasal kereja Iya ti besai.",
			"4": "<sup>4</sup> TUHAN endang besai kuasa, lalu patut dipuji. Iya endang patut dikemesaika lebih ari semua petara.",
			"5": "<sup>5</sup> Petara semua orang bansa bukai endang semina engkeramba aja, tang TUHAN udah ngaga langit.",
			"6": "<sup>6</sup> Iya dikelingi pemadas enggau mulia. Kuasa enggau mulia ngemenuhka Rumah Sembiang Iya.",
			"7": "<sup>7</sup> Puji meh TUHAN, semua mensia di dunya. Puji meh mulia enggau kuasa Iya.",
			"8": "<sup>8</sup> Puji meh nama TUHAN ti bemulia. Bai meh piring lalu datai ngagai Rumah Sembiang Iya.",
			"9": "<sup>9</sup> Sembah meh TUHAN ngena pekayan ti dikena kita besembiang; Awakka semua dunya begetar di mua Iya!",
			"10": "<sup>10</sup> Sebut ngagai semua bansa, “TUHAN endang Raja! Dunya udah diletak tegap, lalu enda ulih dikinsitka. Iya deka ngakim semua bansa ngena atur ti lurus.”",
			"11": "<sup>11</sup> Gaga ati meh kita, dunya enggau langit! Inggar meh kita, tasik, enggau semua utai ti idup dalam kita.",
			"12": "<sup>12</sup> Gaga ati meh kita, umai, enggau semua utai ti dalam kita! Kayu ti di utan deka manjung gaga ati",
			"13": "<sup>13</sup> lebuh TUHAN datai lalu merintah dunya. Iya deka merintah semua bansa di dunya ngena atur ti lurus sereta betul."
		},
		"97": {
			"1": "<sup>1</sup> TUHAN endang Raja! Dunya, awakka ati nuan gaga! Rami gaga ati meh kita menua ti ba tebing tasik!",
			"2": "<sup>2</sup> Iya dikelingi mua hari enggau pemetang. Iya merintah ngena pengelurus enggau atur ti betul.",
			"3": "<sup>3</sup> Api bejalai di mua Iya lalu nganguska munsuh ti ngelingi Iya.",
			"4": "<sup>4</sup> Kilat Iya ngenampakka dunya. Dunya meda kilat nya, lalu begetar.",
			"5": "<sup>5</sup> Bukit luluh baka lilin, di mua TUHAN, di mua TUHAN ti bekuasa atas semua dunya.",
			"6": "<sup>6</sup> Serega madahka pasal pengelurus Iya, lalu semua bansa meda mulia Iya.",
			"7": "<sup>7</sup> Semua orang ke nyembah engkeramba dipechat magang. Semua petara meremi di mua TUHAN.",
			"8": "<sup>8</sup> Orang di Sion gaga ati, lalu semua nengeri di menua Judah rami gaga ke atur Nuan, O TUHAN.",
			"9": "<sup>9</sup> TUHAN ti Pemadu Bekuasa, Nuan endang Raja ke semua dunya. Nuan endang besai agi ari semua petara.",
			"10": "<sup>10</sup> TUHAN rinduka orang ke begedika penyai. Iya nyaga seput bala nembiak Iya. Iya ngelepaska sida ari kuasa orang ke jai.",
			"11": "<sup>11</sup> Penampak manchar ngagai orang ke lurus, enggau pengaga ngagai orang ke manah.",
			"12": "<sup>12</sup> Gaga ati meh semua kita ke lurus ketegal utai ti udah dikereja TUHAN! Kingatka utai ti udah dikereja Allah Taala ti kudus, lalu beri terima kasih ngagai Iya."
		},
		"98": {
			"1": "<sup>1</sup> Nyanyi pantun ti baru ke TUHAN. Iya udah ngereja utai ti badas! Ngena kuasa Iya enggau pengering Iya ti kudus Iya udah bulih pemenang.",
			"2": "<sup>2</sup> TUHAN madahka pemenang Iya. Iya ngasuh semua bansa nemu kuasa Iya ti ngelepas.",
			"3": "<sup>3</sup> Iya ngamatka semaya Iya ngagai orang Israel ngena ati ti tetap enggau pengerindu ti meruan belama iya. Semua bansa di semua endur udah meda pemenang Allah Taala kami.",
			"4": "<sup>4</sup> Belagu gaga ati ke TUHAN, semua kita dunya. Puji Iya ngena pantun enggau panjung gaga ati!",
			"5": "<sup>5</sup> Belagu muji TUHAN! Tebah musik ngena belikan!",
			"6": "<sup>6</sup> Sepu terumpit enggau tanduk lalu manjung gaga ati ke TUHAN ti Raja kitai.",
			"7": "<sup>7</sup> Inggar meh nuan tasik, enggau semua utai ti idup dalam nuan. Belagu meh nuan dunya, enggau semua ke idup ba nuan!",
			"8": "<sup>8</sup> Tempapka jari kita, sungai. Kita bukit, belagu sama sereta enggau ati ti gaga di mua TUHAN,",
			"9": "<sup>9</sup> laban Iya datai deka merintah dunya. Iya deka merintah semua bansa di dunya ngena atur ti lurus sereta betul."
		},
		"99": {
			"1": "<sup>1</sup> TUHAN endang Raja lalu orang mayuh begetar magang. Iya duduk ba kerusi diraja Iya ari atas melikat cherub ti besayap, lalu tanah berenyang.",
			"2": "<sup>2</sup> TUHAN bekuasa di Sion. Iya bekuasa atas semua bansa.",
			"3": "<sup>3</sup> Semua orang deka muji pemesai mulia nama Iya. Kudus meh Iya!",
			"4": "<sup>4</sup> Raja Ti Bekuasa, Nuan rinduka utai ti ngena. Nuan udah numbuhka atur ti lurus di menua Israel. Nuan udah mai pengelurus enggau atur ti betul.",
			"5": "<sup>5</sup> Puji meh TUHAN Allah Taala kitai. Sembah meh Iya di mua kerusi diraja Iya! Kudus meh Iya!",
			"6": "<sup>6</sup> Moses seduai Aron udah nyadi imam Iya, lalu Samuel udah besampi ngagai Iya. Sida ngangauka TUHAN, lalu Iya nyaut sida.",
			"7": "<sup>7</sup> Iya bejaku ngagai sida ari gentilang remang. Sida ngasika adat enggau jaku ti diberi Iya ngagai sida.",
			"8": "<sup>8</sup> O TUHAN Allah Taala kami, Nuan udah nyaut bala nembiak Nuan. Nuan udah nunjukka Diri Allah Taala ti ngampun, taja pen Nuan ngukum sida ketegal dosa sida.",
			"9": "<sup>9</sup> Puji meh TUHAN Allah Taala kitai, lalu sembah meh Iya ba bukit Iya ti kudus! TUHAN Allah Taala kitai endang kudus!"
		},
		"100": {
			"1": "<sup>1</sup> Nyanyi gaga ati ke TUHAN meh semua kita dunya!",
			"2": "<sup>2</sup> Sembah meh TUHAN enggau ati ti gaga, Datai ngagai Iya enggau lagu lantang ati!",
			"3": "<sup>3</sup> Anang enda ngaku TUHAN nya Allah Taala! Iya ngaga kitai, lalu kitai endang enggi Iya. Kitai tu nembiak Iya, kitai tu raban domba Iya.",
			"4": "<sup>4</sup> Tama nengah pintu Rumah Sembiang mai sampi terima kasih. Mansang ngagai endur ti kudus enggau pantun puji. Beri terima kasih ngagai Iya, lalu puji Iya.",
			"5": "<sup>5</sup> TUHAN endang badas. Pengerindu Iya meruan belama iya, lalu penetap ati Iya meruan belama iya."
		},
		"101": {
			"1": "<sup>1</sup> Lagu aku ngenang pasal penaluk enggau atur ti lurus, lalu aku nyanyi nya ke Nuan, O TUHAN.",
			"2": "<sup>2</sup> Aku nadai jai ulah. Kemaya Nuan deka datai ngagai aku? Aku deka idup tuchi dalam rumah aku",
			"3": "<sup>3</sup> lalu aku enggai meda penyai. Aku begedika utai ti dikereja orang ke ninggalka Allah Taala. Aku enggai bekaul enggau sida.",
			"4": "<sup>4</sup> Aku enggai enda betul ati lalu aku enggai bekaul enggau penyai.",
			"5": "<sup>5</sup> Aku deka muru sebarang orang ke betelai bejaku jai pasal orang bukai. Aku enggai meda orang ti sumbung enggau ti tinggi ati.",
			"6": "<sup>6</sup> Aku deka rinduka orang ke tetap ati ngagai Allah Taala lalu deka ngemendarka sida diau dalam istana aku. Orang ke endang amat betul ati deka dikemendarka bekereja ke aku.",
			"7": "<sup>7</sup> Orang ke bula enda tau diau dalam istana aku. Orang ke pura-pura ngapa enda tau diau di mua aku.",
			"8": "<sup>8</sup> Tiap hari aku deka ngerusak orang ke jai di menua kitai. Aku deka muai semua orang ke jai ari nengeri TUHAN."
		},
		"102": {
			"1": "<sup>1</sup> Pendingka sampi aku, O TUHAN, lalu dingaka sabak aku ke minta tulung!",
			"2": "<sup>2</sup> Lebuh aku dituntung penusah, anang ninggalka aku! Pendingka aku, lalu saut aku enggau jampat, lebuh aku ngangau!",
			"3": "<sup>3</sup> Aku mati lalu lesap baka asap. Tubuh aku angat asai api.",
			"4": "<sup>4</sup> Aku layu baka rumput ti rangkai. Aku nadai agi tetengukka pemakai.",
			"5": "<sup>5</sup> Aku bedemuh inggar. Aku semina tinggal kulit enggau tulang.",
			"6": "<sup>6</sup> Aku nyau baka burung ti di menua puang, baka burung puk ti diau ba tembawai.",
			"7": "<sup>7</sup> Aku gali tang enda ulih tinduk. Aku baka burung ti seilu-ilu ba perabung rumah.",
			"8": "<sup>8</sup> Bala munsuh aku mechat aku sepemanjai hari. Sida ke ngeleseka aku, ngena nama aku nyadika jaku sema.",
			"9": "<sup>9</sup> Ketegal pengeringat enggau pemedis ati Nuan, aku makai amau, lalu ai mata aku nyau bechampur enggau ai irup aku. Nuan nyengkau aku lalu nikauka aku.",
			"11": "<sup>11</sup> Pengidup aku nyau baka ayuk ti nyadi lebuh lemai hari. Aku tu nyau baka rumput ti rangkai.",
			"12": "<sup>12</sup> Tang Nuan, O TUHAN, endang Raja belama iya. Semua turun-menurun deka ingatka Nuan.",
			"13": "<sup>13</sup> Nuan deka angkat lalu sinuka Sion. Maya udah datai alai Nuan patut kasihka iya. Tu meh maya ti ngena.",
			"14": "<sup>14</sup> Bala nembiak Nuan rinduka iya, taja pen iya udah rusak. Sida sinu ati ke iya, taja pen iya nyau nyadi tembawai.",
			"15": "<sup>15</sup> Semua bansa deka nangika TUHAN. Semua raja di dunya deka nangika kuasa Iya.",
			"16": "<sup>16</sup> Lebuh TUHAN ngaga Sion baru, Iya deka mandangka pemesai kuasa Iya.",
			"17": "<sup>17</sup> Iya deka ningaka bala nembiak Iya ti udah ditinggalka Iya, lalu mendingka sampi sida.",
			"18": "<sup>18</sup> Tuliska utai ti udah dikereja TUHAN ke rebak ti dudi, ngambika orang ke apin ada diatu, deka muji Iya.",
			"19": "<sup>19</sup> TUHAN nilik ke baruh ari endur Iya ti kudus ba endur ti tinggi, Iya nilik ke baruh ari serega ngagai dunya.",
			"20": "<sup>20</sup> Iya ninga demuh orang jil lalu ngelepaska orang ke kena ukum bunuh.",
			"21": "<sup>21</sup> Nya alai nama Iya deka disebut di Sion, lalu Iya deka dipuji di Jerusalem",
			"22": "<sup>22</sup> lebuh semua bansa enggau perintah begempuru lalu nyembah TUHAN.",
			"23": "<sup>23</sup> TUHAN udah ngasuh aku lemi lebuh aku agi biak. Iya udah ngasuh umur aku pandak.",
			"24": "<sup>24</sup> O Allah Taala, anang ngambi aku diatu sebedau aku tuai umur. O TUHAN, Nuan idup belama iya.",
			"25": "<sup>25</sup> Dulu kelia Nuan ngaga dunya, lalu Nuan ngaga langit ngena jari Nuan.",
			"26": "<sup>26</sup> Sida deka lesap, tang Nuan deka meruan belama iya. Sida deka luya baka kain. Nuan deka muai sida baka kain, lalu sida deka lesap.",
			"27": "<sup>27</sup> Tang Nuan endang meruan enda berubah belama iya lalu umur Nuan enda nemu abis.",
			"28": "<sup>28</sup> Bala anak kami deka diau likun, lalu ari jaga Nuan peturun sida deka diau likun."
		},
		"103": {
			"1": "<sup>1</sup> Puji meh TUHAN, O semengat aku! Semua semengat, roh enggau tubuh aku, puji meh nama Iya ti kudus!",
			"2": "<sup>2</sup> Semengat aku, puji meh TUHAN, lalu anang enda ingatka penelap Iya.",
			"3": "<sup>3</sup> Iya ngampun semua dosa aku lalu ngeraika semua penyakit aku.",
			"4": "<sup>4</sup> Iya ngelepaska aku ari kubur lalu merekatka aku ngena pengerindu enggau pengasih.",
			"5": "<sup>5</sup> Iya ngemuaska ati aku enggau utai ti manah, ngambika aku seruran biak sereta kering baka menaul.",
			"6": "<sup>6</sup> TUHAN ngeringka orang ke kena tekan lebuh Iya ngakim lalu meri sida atur ti lurus.",
			"7": "<sup>7</sup> Iya mandangka pelan Iya ngagai Moses lalu ngasuh orang Israel meda pengawa ti besai ti dikereja Iya.",
			"8": "<sup>8</sup> TUHAN endang kasih sereta rindu, lubah ringat, sereta penuh laban pengerindu ti meruan belama iya.",
			"9": "<sup>9</sup> Iya enda majak nganu. Iya enda ringat belama iya.",
			"10": "<sup>10</sup> Iya enda ngukum kitai nitihka dosa kitai tauka malas kitai nitihka penyai kitai.",
			"11": "<sup>11</sup> Baka langit ti tinggi ari atas dunya, baka nya meh pemesai pengerindu Iya ke orang ke nangika Iya.",
			"12": "<sup>12</sup> Baka timur ti jauh ari barat, baka nya meh penyauh Iya muai dosa kitai.",
			"13": "<sup>13</sup> Baka apai ke nelap enggau anak diri, baka nya meh TUHAN nelap enggau orang ke nangika Iya.",
			"14": "<sup>14</sup> Iya nemu utai ti udah dikena ngaga kitai. Iya ingatka kitai tu endang amau.",
			"15": "<sup>15</sup> Ba pasal kitai empu, kitai tu baka rumput. Kitai tumbuh lalu idup baka bungai babas.",
			"16": "<sup>16</sup> Udah nya ribut muputka iya, lalu iya nadai agi, nadai agi dipeda orang.",
			"17": "<sup>17</sup> Tang pengerindu TUHAN deka meruan belama iya ba orang ke nangika Iya, lalu pengelurus Iya deka meruan ari serak siti ngagai serak siti,",
			"18": "<sup>18</sup> orang ke nitihka sempekat TUHAN sereta tetap ati ngasika pesan Iya.",
			"19": "<sup>19</sup> TUHAN udah ngengkahka kerusi diraja Iya di serega. Iya endang Raja ari atas semua.",
			"20": "<sup>20</sup> Puji meh TUHAN, kita melikat ti kering sereta bekuasa, ke ngasika pesan Iya, ke mendingka utai ti disebut Iya.",
			"21": "<sup>21</sup> Puji meh TUHAN, semua kita kuasa ti di serega, kita ke nembiak Iya, ti ngereja peneka Iya!",
			"22": "<sup>22</sup> Puji meh TUHAN, semua utai ti idup ti udah digaga Iya di semua endur ti diperintah Iya. Puji meh TUHAN, O semengat aku! Puji meh TUHAN, O semengat aku!"
		},
		"104": {
			"1": "<sup>1</sup> O TUHAN Allah Taala aku, naka meh pemesai kuasa Nuan! Nuan ngerasukka mulia raja.",
			"2": "<sup>2</sup> Nuan nyerayung Diri ngena penampak. Nuan udah mintarka langit baka langkau kain",
			"3": "<sup>3</sup> lalu ngaga rumah Nuan ba atas ai ti di langit Nuan ngaga mua hari nyadika kerita perang Nuan lalu nepan ba sayap ribut.",
			"4": "<sup>4</sup> Nuan mai pesan Nuan ngena ribut lalu kilat ti nyelar nyadi ulun Nuan.",
			"5": "<sup>5</sup> Nuan udah ngengkahka dunya tegap ba pugu iya, lalu iya enda dikinsitka.",
			"6": "<sup>6</sup> Nuan udah ngengkahka tasik ba atas pugu dunya baka baju panjai, lalu ai ngelamun bukit.",
			"7": "<sup>7</sup> Lebuh Nuan ngerara ai, sida rari. Sida beterudus rari, lebuh Nuan merakka nyawa Nuan ngagai sida.",
			"8": "<sup>8</sup> Sida rarat nurun bukit ngagai lebak, ngagai endur ti udah disediaka Nuan ke sida.",
			"9": "<sup>9</sup> Nuan udah ngeletak garis ti enda tau dilanggar sida, ngambika sida enda ngelamun semua dunya baru.",
			"10": "<sup>10</sup> Nuan ngasuh mata ai mansutka ai ba lebak, lalu sungai rarat ba entara bukit.",
			"11": "<sup>11</sup> Jelu babas ngirup ai ari sida. Dia meh endur alai keledai kampung ngirup lalu enda agi aus.",
			"12": "<sup>12</sup> Ba kayu ti semak nya, burung ngaga sarang lalu belagu.",
			"13": "<sup>13</sup> Ari langit Nuan ngasuh ai ujan labuh ninggang bukit, lalu dunya penuh laban berekat Nuan.",
			"14": "<sup>14</sup> Nuan ngasuh rumput tumbuh ngambika bisi diempa jelu sapi, lalu meri kami utai ngambika bisi ditanam kami, ngambika kami bulih asil ari tanah,",
			"15": "<sup>15</sup> enggau wain kena kami ngelantangka diri, minyak kana kena kami ngasuh mua kami beperenching, enggau roti ti meri kami pengering.",
			"16": "<sup>16</sup> Kayu sidar di Lebanon ditinggang ujan ti bangat, kayu ti ditanam TUHAN Empu.",
			"17": "<sup>17</sup> Dia meh endur alai burung ngaga sarang sida. Burung undan ngaga sarang ba puchuk kayu ru.",
			"18": "<sup>18</sup> Kambin babas diau ba bukit ti tinggi, lalu jelu dumbang belalai ba sebelah tingkah.",
			"19": "<sup>19</sup> Nuan udah ngaga bulan kena ngelai musin. Mata panas nemu maya diri padam.",
			"20": "<sup>20</sup> Nuan udah ngaga malam, lalu lebuh hari nyau petang semua jelu babas pansut magang.",
			"21": "<sup>21</sup> Singa ti agi biak negung lebuh sida ngiga pemakai, begigaka pemakai ti diberi Allah Taala.",
			"22": "<sup>22</sup> Lebuh mata hari tumbuh, sida pulai baru lalu gali dalam lubang sida.",
			"23": "<sup>23</sup> Nya baru mensia nurun gawa lalu majak gawa datai ke lemai.",
			"24": "<sup>24</sup> TUHAN, Nuan udah ngaga mayuh macham utai! Naka meh pemintar Nuan ngaga semua utai nya! Dunya nyau penuh laban utai ti idup ti digaga Nuan.",
			"25": "<sup>25</sup> Tasik ti luas sereta besai pen bisi, endur alai utai ti idup diau, ti besai enggau ti mit, ti enda ulih diitung.",
			"26": "<sup>26</sup> Kapal belayar ba tasik, lalu jelu ti besai bemain dalam iya, iya nya naga tasik ti udah ditempa Nuan,",
			"27": "<sup>27</sup> Semua sida ngarapka Nuan meri sida pemakai nitihka musin.",
			"28": "<sup>28</sup> Nuan meri pemakai ngagai sida, lalu sida makai. Nuan meri sida pemakai, lalu sida kenyang.",
			"29": "<sup>29</sup> Lebuh Nuan nguing kebukai, sida tusah ati. Lebuh Nuan ngambi seput ti meri pengidup, sida mati lalu pulai nyadi amau baru.",
			"30": "<sup>30</sup> Tang lebuh Nuan meri sida seput, sida idup. Nuan meri pengidup ti baru ngagai dunya.",
			"31": "<sup>31</sup> Awakka mulia TUHAN meruan belama iya! Awakka TUHAN gaga ati ke utai ti udah digaga Iya!",
			"32": "<sup>32</sup> Iya merening ngagai dunya, lalu dunya begetar. Iya negu bukit, lalu bukit berasap.",
			"33": "<sup>33</sup> Aku deka belagu ke TUHAN seumur idup. Seagi-agi aku idup, aku deka belagu muji Allah Taala aku.",
			"34": "<sup>34</sup> Awakka Iya gaga ati ke lagu aku, laban Iya ngasuh ati aku gaga.",
			"35": "<sup>35</sup> Awakka orang ke bedosa dibuai ari dunya. Awakka orang ke jai abis lengis. Puji meh TUHAN, O semengat aku! Puji meh TUHAN!"
		},
		"105": {
			"1": "<sup>1</sup> Beri terima kasih ngagai TUHAN. Padahka pemesai kuasa Iya. Tusui ngagai semua bansa utai ti udah dikereja Iya.",
			"2": "<sup>2</sup> Belagu muji TUHAN. Tusui pasal utai ti badas ti udah dikereja Iya.",
			"3": "<sup>3</sup> Gaga ati meh kitai ke enggi Iya. Awakka semua orang ke nyembah Iya rami gaga magang.",
			"4": "<sup>4</sup> Mansang meh kitai ngagai TUHAN minta Iya nulung, lalu sembah Iya berari.",
			"5": "<sup>5</sup> Kita peturun Abraham ke nembiak Iya kita peturun Jakop ti udah dipilih Iya: Kingatka kereja ajih ti udah dikereja Allah Taala enggau ukum ti udah diletak Iya.",
			"7": "<sup>7</sup> TUHAN endang Allah Taala kitai. Pesan Iya endang ke semua dunya.",
			"8": "<sup>8</sup> Iya deka ingatka sempekat Iya belama iya, semaya ti diberi Iya ngagai seribu serak.",
			"9": "<sup>9</sup> Iya deka ingatka sempekat ti udah digaga Iya enggau Abraham enggau semaya ti digaga Iya enggau Isak.",
			"10": "<sup>10</sup> TUHAN ngaga sempekat enggau Jakop, sempekat ti deka meruan belama iya.",
			"11": "<sup>11</sup> Ku Iya bejaku, “Aku deka meri menua Kanaan ngagai nuan. Menua nya deka nyadika pesaka nuan.”",
			"12": "<sup>12</sup> Lebuh sida agi mimit tubuh, nadai dipedulika orang, lalu diau nyadi orang kampar,",
			"13": "<sup>13</sup> bejalai segau-segau ari menua siti ngagai menua siti, ari perintah siti ngagai perintah siti,",
			"14": "<sup>14</sup> Iya enggai nejuka orang nekanka sida. Kena Iya nyaga sida, Iya meri jaku tangkan ngagai bala raja:",
			"15": "<sup>15</sup> “Anang negu bala nembiak ti udah dipilih Aku. Anang ngemediska bala nabi Aku”",
			"16": "<sup>16</sup> Lebuh TUHAN ngasuh lapar pungkang nyadi di menua nya lalu ngerampas semua pemakai sida,",
			"17": "<sup>17</sup> Iya ngirumka siku orang bejalai dulu ari sida, orang ke benama Josep, ti udah dijual nyadi ulun.",
			"18": "<sup>18</sup> Kaki iya ditanchang ngena rantai, lalu rekung iya dikungkung ngena besi,",
			"19": "<sup>19</sup> nyentuk ngagai maya utai ti udah ditujum iya, dikamatka, nyentuk ngagai maya Jaku Allah Taala madahka Josep endang madahka utai ti bendar.",
			"20": "<sup>20</sup> Udah nya raja menua Ejip ke merintah mayuh bengkah bansa ngasuh orang ngelepaska iya.",
			"21": "<sup>21</sup> Iya ngeletak Josep nyadi tuai megai perintah lalu ngasuh iya merintah semua menua nya,",
			"22": "<sup>22</sup> bekuasa atas bala pemesai raja lalu diberi kuasa meri atur ngagai bala penyanding penemu raja.",
			"23": "<sup>23</sup> Udah nya Jakop mansang ngagai menua Ejip lalu ngentapka diri di menua nya.",
			"24": "<sup>24</sup> TUHAN meri mayuh anak ngagai bala nembiak Iya lalu ngasuh sida kering agi ari bala munsuh sida.",
			"25": "<sup>25</sup> Iya ngasuh orang Ejip begedika bala nembiak Iya lalu nipu bala nembiak Iya.",
			"26": "<sup>26</sup> Udah nya Iya ngirumka nembiak Iya Moses, enggau Aron, ti udah dipilih Iya.",
			"27": "<sup>27</sup> Sida ngereja kereja ti nyelai enggau kereja ajih Allah Taala di menua Ejip.",
			"28": "<sup>28</sup> Allah Taala ngasuh menua Ejip dibalut pemetang, tang orang Ejip ngelaban jaku Iya.",
			"29": "<sup>29</sup> Iya ngasuh sungai sida bebali nyadi darah lalu munuh semua ikan.",
			"30": "<sup>30</sup> Menua sida penuh laban engkatak. Indah istana raja deh penuh laban engkatak.",
			"31": "<sup>31</sup> Allah Taala bejaku, lalu lalat enggau kerengit malut semua menua nya.",
			"32": "<sup>32</sup> Iya ngasuh ujan batu enggau kilat nyadi di menua nya kena nganti ujan.",
			"33": "<sup>33</sup> Iya ngerusak batang anggur enggau kayu ara sida lalu matahka semua kayu.",
			"34": "<sup>34</sup> Iya bejaku, lalu bejuta-juta iku buntak datai.",
			"35": "<sup>35</sup> Bala buntak nya makai semua utai ti tumbuh di menua nya, makai semua utai ti ditanam.",
			"36": "<sup>36</sup> Iya munuh anak tuai ti lelaki ari semua ruang bilik orang Ejip.",
			"37": "<sup>37</sup> Udah nya Iya nguluka orang Israel pansut. Sida mai pirak enggau emas, lalu semua sida bela bepengerai sereta regas magang.",
			"38": "<sup>38</sup> Orang Ejip takutka sida lalu gaga ati lebuh sida angkat.",
			"39": "<sup>39</sup> Allah Taala ngengkahka siti mua hari atas bala nembiak Iya enggau daun api ti meri sida penampak lebuh malam hari.",
			"40": "<sup>40</sup> Sida minta, lalu Iya ngirumka burung empitu. Iya meri sida pemakai ari serega kena ngenyangka sida.",
			"41": "<sup>41</sup> Iya malu batu, ai lalu pansut, lalu rarat nengah menua puang baka sungai.",
			"42": "<sup>42</sup> Iya ingatka semaya Iya ti kudus, ti udah digaga Iya enggau nembiak Iya Abraham.",
			"43": "<sup>43</sup> Nya alai Iya nguluka bala nembiak Iya pansut, lalu sida belagu sereta manjung gaga ati.",
			"44": "<sup>44</sup> Iya meri sida menua orang bansa bukai lalu ngasuh sida ngerampas umai orang nya,",
			"45": "<sup>45</sup> ngambika bala nembiak Iya ngasika Adat Iya lalu nitihka semua pesan Iya. Puji TUHAN!"
		},
		"106": {
			"1": "<sup>1</sup> Puji TUHAN! Beri terima kasih ngagai TUHAN, laban Iya endang manah. Pengerindu Iya meruan belama iya.",
			"2": "<sup>2</sup> Sapa ulih madahka pasal semua pengawa ti besai ti udah dikereja Iya? Sapa ulih muji Iya enggau chukup?",
			"3": "<sup>3</sup> Beberekat meh orang ke ngasika pesan Iya, ke seruran ngereja utai ti ngena.",
			"4": "<sup>4</sup> Kingatka aku, O TUHAN, lebuh Nuan nulung bala nembiak Nuan. Tulung aku lebuh Nuan ngelepaska sida.",
			"5": "<sup>5</sup> Awakka aku meda pengaya bala nembiak Nuan, lalu sama enggau ngasaika pengelantang bansa Nuan, gaga ati temegah enggau sida ke diempu Nuan.",
			"6": "<sup>6</sup> Kami udah bedosa baka aki ini kami. Kami udah bepenyalah sereta jai.",
			"7": "<sup>7</sup> Aki ini kami di menua Ejip enda meretika pemadas pengawa Allah Taala. Sida enda ingatka penyuah Iya udah mandangka pengerindu Iya, lalu sida angkat ngelaban Allah Taala ti Pemadu Bekuasa ba tasik.",
			"8": "<sup>8</sup> Tang Iya nyelamatka sida, munyi ti udah disemaya Iya, kena Iya ngayanka pemesai kuasa Iya.",
			"9": "<sup>9</sup> Iya bejaku ngagai Tasik Mirah, lalu tasik nya rangkai. Iya nguluka bala nembiak Iya meraka nengah tanah ti rangkai.",
			"10": "<sup>10</sup> Iya ngelepaska sida ari jari orang ke begedika sida. Iya ngelepaska sida ari kuasa bala munsuh sida.",
			"11": "<sup>11</sup> Tang ai ngelamun bala munsuh sida. Nadai siku sida agi idup.",
			"12": "<sup>12</sup> Nya baru bala nembiak Iya arapka semaya Iya, lalu belagu muji Iya.",
			"13": "<sup>13</sup> Tang sida jampat enda ingatka utai ti udah dikereja Iya lalu ngereja utai enda nganti jaku lalau ari Iya.",
			"14": "<sup>14</sup> Sida balat tetengukka makai utai lebuh sida di menua puang lalu sida nguji Allah Taala.",
			"15": "<sup>15</sup> Nya alai Iya meri sida utai ti dipinta sida, tang Iya mega ngirumka penyakit ti balat ngagai sida.",
			"16": "<sup>16</sup> Menua puang nya meh endur alai sida ke kepapaska Moses seduai Aron, nembiak TUHAN ti kudus.",
			"17": "<sup>17</sup> Udah nya tanah merangap lalu nelan Datan lalu ngelamun Abiram enggau sida sebilik.",
			"18": "<sup>18</sup> Api nurun ngagai orang ke nitihka sida, lalu nganguska orang ke jai nya.",
			"19": "<sup>19</sup> Sida ngaga siku anak sapi ari emas di Sinai lalu nyembah engkeramba nya.",
			"20": "<sup>20</sup> Sida besilihka mulia Allah Taala enggau engkeramba jelu ti makai rumput.",
			"21": "<sup>21</sup> Sida enda ingatka Allah Taala ti udah ngelepaska sida ngena pengawa Iya ti besai di menua Ejip.",
			"22": "<sup>22</sup> Naka meh pemadas utai ti dikereja Iya dia! Naka meh penyelai utai ti nyadi ba Tasik Mirah!",
			"23": "<sup>23</sup> Lebuh Allah Taala madahka Diri deka ngerusak bala nembiak Iya, Moses, ulun ke dipilih Iya bediri nyakal Allah Taala lalu nagang pengeringat Iya ngerusak sida.",
			"24": "<sup>24</sup> Udah nya sida nulak menua ti manah nya, laban sida enda arapka semaya Allah Taala.",
			"25": "<sup>25</sup> Sida diau dalam langkau kain lalu mutap, lalu enggai mendingka TUHAN.",
			"26": "<sup>26</sup> Nya alai Iya lalu nangkan sida enggau jaku madahka Diri deka ngasuh sida mati di menua puang",
			"27": "<sup>27</sup> lalu ngachirka peturun sida ba sebelah bansa orang kapir, lalu nejuka sida mati di menua bukai.",
			"28": "<sup>28</sup> Udah nya, di Peor bala nembiak Allah Taala lalu enggau orang besembahka Baal lalu makai piring ti udah diberi ngagai petara ti ukai petara ti idup.",
			"29": "<sup>29</sup> Sida nyungkak TUHAN ringat ketegal utai ti dikereja sida. Penyakit ti balat lalu nyadi ba bala sida.",
			"30": "<sup>30</sup> Tang Pinehas angkat lalu ngukum orang ke udah salah, lalu penyakit nya enda agi ngerampit.",
			"31": "<sup>31</sup> Lalu ketegal nya Iya dikumbai lurus ari serak siti ngagai serak siti belama iya.",
			"32": "<sup>32</sup> Ba mata ai di Meribah, orang mayuh nyungkak TUHAN ringat, lalu ngenusahka Moses.",
			"33": "<sup>33</sup> Sida nyungkak Moses balat amat ringat datai ke nyau bejaku enda berunding.",
			"34": "<sup>34</sup> Sida enda ngasika jaku TUHAN ti ngasuh sida munuh orang kapir,",
			"35": "<sup>35</sup> tang jadi enggau orang bansa bukai lalu idup nitihka jalai orang kapir.",
			"36": "<sup>36</sup> Bala nembiak Allah Taala nyembah engkeramba, lalu tu ngujungka sida dirusak.",
			"37": "<sup>37</sup> Sida nyerahka anak lelaki enggau anak indu sida nyadika piring ngagai engkeramba orang Kanaan.",
			"38": "<sup>38</sup> Sida munuh anak mit ti nadai penyalah, lalu ngamahka menua laban sida bebunuhka anak mit nya.",
			"39": "<sup>39</sup> Sida ngaga diri enda tuchi ketegal utai ti dikereja sida sereta enda tetap ati ngagai Allah Taala.",
			"40": "<sup>40</sup> Nya alai TUHAN ringat ngagai bala nembiak Iya. Iya nyau kejika sida.",
			"41": "<sup>41</sup> Iya nyerahka sida ngagai jari orang kapir, lalu munsuh sida merintah sida.",
			"42": "<sup>42</sup> Sida ditekanka bala munsuh sida, lalu sida nunduk ngagai munsuh sida.",
			"43": "<sup>43</sup> TUHAN suah udah ngelepaska bala nembiak Iya, tang sida rindu agi angkat ngelaban Iya lalu dosa sida nambah-menambah.",
			"44": "<sup>44</sup> Tang taja pia, TUHAN ninga sida lebuh sida nyabak, lalu Iya ibuhka penusah sida.",
			"45": "<sup>45</sup> Ketegal sida, Iya ingatka sempekat Iya, lalu ketegal pemesai pengerindu Iya, Iya kasihka sida.",
			"46": "<sup>46</sup> Iya ngasuh semua orang ke nekanka sida sinu ati ke sida.",
			"47": "<sup>47</sup> Lepaska kami, O TUHAN Allah Taala kami, lalu bai kami pulai ari semua bansa, ngambika kami meri terima kasih lalu muji nama Nuan ti kudus.",
			"48": "<sup>48</sup> Puji meh TUHAN Allah Taala ke disembah orang Israel. Puji meh Iya diatu enggau ke belama iya! Awakka semua orang nyebut, “Amin!” Puji TUHAN!"
		},
		"107": {
			"1": "<sup>1</sup> “Beri terima kasih ngagai TUHAN, laban Iya endang badas. Pengerindu Iya meruan belama iya!”",
			"2": "<sup>2</sup> Sebut semua leka jaku tu betandu-tandu kena muji TUHAN, semua kita ke udah dilepaska Iya ari penusah kita.",
			"3": "<sup>3</sup> lalu udah mai kita pulai baru ari menua bukai, ari timur enggau ari barat, ari utara enggau ari selatan.",
			"4": "<sup>4</sup> Sekeda bejalai segau-segau ba sebelah menua puang nadai tetemuka batang jalai ngagai nengeri ti deka alai sida diau.",
			"5": "<sup>5</sup> Sida lapar sereta aus lalu nadai ngadang diri ulih idup.",
			"6": "<sup>6</sup> Udah nya lebuh sida kena laban penusah lalu ngangauka TUHAN, Iya ngelepaska sida ari penusah sida.",
			"7": "<sup>7</sup> Iya nguluka sida nengah jalai ti rurus ngagai nengeri ti deka alai sida diau.",
			"8": "<sup>8</sup> Awakka sida meri terima kasih ngagai TUHAN ketegal pengerindu Iya ti meruan belama iya, ketegal utai ti badas ti udah dikereja Iya ke sida.",
			"9": "<sup>9</sup> Sida ke aus diberi Iya ai lalu sida ke lapar dikenyangka Iya ngena utai ti manah.",
			"10": "<sup>10</sup> Sekeda sida diau dalam pemetang, orang jil ke merinsa ditanchang ngena rantai,",
			"11": "<sup>11</sup> laban sida udah angkat ngelaban pesan Allah Taala ti Pemadu Bekuasa lalu udah nulak ajar Iya.",
			"12": "<sup>12</sup> Sida nyau lembut tulang laban bebendar gawa. Sida rebah, lalu nadai orang nulung.",
			"13": "<sup>13</sup> Udah nya lebuh sida kena laban penusah, sida ngangauka TUHAN, lalu Iya ngelepaska sida ari penusah sida.",
			"14": "<sup>14</sup> Iya mai sida pansut ari pemetang nya lalu mutuska rantai ti nanchang sida.",
			"15": "<sup>15</sup> Sida enda tau enda meri terima kasih ngagai TUHAN ketegal pengerindu Iya ti meruan belama iya, ketegal utai ti badas ti udah dikereja Iya ke sida.",
			"16": "<sup>16</sup> Iya ngeruntuhka pintu ti digaga ari temaga lalu matahka alang besi.",
			"17": "<sup>17</sup> Sekeda sida beli, natka pemerinsa ketegal dosa sida enggau ketegal penyai sida.",
			"18": "<sup>18</sup> Sida nyau deka mutah meda pemakai lalu nyau deka mati.",
			"19": "<sup>19</sup> Udah nya lebuh sida kena laban penusah, sida ngangauka TUHAN, lalu Iya ngelepaska sida ari penusah sida.",
			"20": "<sup>20</sup> Iya ngeraika sida ngena jaku Iya lalu ngelepaska sida ari kubur.",
			"21": "<sup>21</sup> Awakka sida meri terima kasih ngagai TUHAN ketegal pengerindu Iya ti meruan belama iya, ketegal utai ti badas ti udah dikereja Iya ke sida.",
			"22": "<sup>22</sup> Awakka sida meri piring terima kasih ngagai Iya, lalu enggau lagu gaga ati, nusui semua utai ti udah dikereja Iya.",
			"23": "<sup>23</sup> Sekeda sida belayar ngena kapal ba Tasik Tengah, ngulihka pengidup di tasik.",
			"24": "<sup>24</sup> Sida meda utai ti ulih dikereja TUHAN, meda kereja badas ti dikereja Iya di tasik.",
			"25": "<sup>25</sup> Iya bejaku, lalu ribut biaubalai muput lalu ngasuh gelumbang nyadi.",
			"26": "<sup>26</sup> Kapal terangkat tinggi disaguka gelumbang lalu terensam ke dalam ai; bala kelasi lalu takut ditempuh utai ti baka nya.",
			"27": "<sup>27</sup> Sida tesangkut sereta guir-guir baka orang ke mabuk, lalu nadai daya agi.",
			"28": "<sup>28</sup> Udah nya lebuh sida kena laban penusah, sida ngangauka TUHAN, lalu Iya ngelepaska sida ari penusah sida.",
			"29": "<sup>29</sup> Iya ngasuh biaubalai teduh lalu gelumbang pen badu agi besegum.",
			"30": "<sup>30</sup> Sida gaga ati laban biaubalai nyau nadai agi, lalu Iya mai sida enggau likun ngagai pengkalan ti digusung sida.",
			"31": "<sup>31</sup> Awakka sida meri terima kasih ngagai TUHAN ketegal pengerindu Iya ti meruan belama iya, ketegal utai ti badas ti udah dikereja Iya ke sida.",
			"32": "<sup>32</sup> Awakka sida ngemesaika Iya lebuh orang mayuh begempuru lalu muji Iya di mua bala tuai.",
			"33": "<sup>33</sup> TUHAN udah ngasuh sungai rangkai lalu ngasuh mata ai badu agi mansutka ai.",
			"34": "<sup>34</sup> Iya ngasuh tanah ti ami nyadi tanah ti masin sereta kusi ketegal penyai orang ke diau dia.",
			"35": "<sup>35</sup> Iya ngubah menua puang nyadi letung, lalu tanah ti rangkai nyadi mata ai.",
			"36": "<sup>36</sup> Iya ngasuh orang ke lapar diau dia, lalu sida ngaga sebuah nengeri alai diau.",
			"37": "<sup>37</sup> Sida nanam utai ba umai, lalu nanam anggur lalu bulih amat ngetau.",
			"38": "<sup>38</sup> Iya merekatka bala nembiak Iya, lalu sida mayuh anak, lalu Iya ngasuh jelu sida mayuh anak.",
			"39": "<sup>39</sup> Lebuh bala nembiak Allah Taala alah, lalu dikemaruhka ketegal sida ditekan sereta diperinsa, lalu tusah ati,",
			"40": "<sup>40</sup> Iya ngeleseka orang ke nekanka sida lalu ngasuh sida bejalai segau-segau ba sebelah menua puang ti nadai meruan batang jalai.",
			"41": "<sup>41</sup> Tang Iya ngelepaska orang ke suntuk ari pemerinsa sida lalu ngasuh ruang bilik sida nambah-menambah baka raban jelu bedus.",
			"42": "<sup>42</sup> Orang ke lurus meda nya, lalu sida gaga ati, tang semua orang ke jai nadai agi bemunyi.",
			"43": "<sup>43</sup> Awakka orang ke bepenemu-dalam berundingka utai tu. Awakka sida berundingka pengerindu TUHAN ti meruan belama iya."
		},
		"108": {
			"1": "<sup>1</sup> Ati aku endang tetap, O Allah Taala! Ati aku endang amat tetap! Aku deka belagu muji Nuan! Dani meh nuan, O semengat aku!",
			"2": "<sup>2</sup> Dani meh kita, O belikan enggau engkeratung aku! Aku deka ngerak mata hari.",
			"3": "<sup>3</sup> Aku deka meri terima kasih ngagai Nuan, O TUHAN, di mua semua bansa. Aku deka muji Nuan di mua semua mensia.",
			"4": "<sup>4</sup> Pengerindu Nuan ti meruan belama iya endang ngelui serega. Penetap ati Nuan nyentuk ke langit.",
			"5": "<sup>5</sup> pandangka pemesai kuasa Nuan di langit, O Allah Taala, enggau mulia Nuan di serata dunya.",
			"6": "<sup>6</sup> Selamatka kami ngena pengering Nuan. Saut meh sampi aku, ngambika orang ke dikerinduka Nuan, dilepaska.",
			"7": "<sup>7</sup> Ku Allah Taala bejaku ari endur Iya ti kudus, “Enggau ati ti gaga, Aku deka magi menua Sekem lalu meri Lebak Sukot ngagai bala nembiak Aku.",
			"8": "<sup>8</sup> Menua Giliat endang enggi Aku. Manasi mega enggi Aku. Epraim nya selapuk kebal Aku lalu Judah nya tungkat diraja Aku.",
			"9": "<sup>9</sup> Tang Aku deka ngena Moap nyadika dulang alai Aku bebasu, lalu Aku deka nikauka kasut Aku ngagai Edom, nyadika tanda Aku bempu menua nya. Aku deka manjung gaga ati menang ngelaban orang Pilistin.”",
			"10": "<sup>10</sup> O Allah Taala, sapa deka mai aku ngagai nengeri ti bekuta nya? Sapa deka nguluka aku ngagai Edom?",
			"11": "<sup>11</sup> Kati Nuan amat udah nulak kami? Kati Nuan enggai bejalai enggau bala soldadu kami?",
			"12": "<sup>12</sup> Tulung meh kami ngelaban munsuh. Tulung ari mensia sigi nadai guna.",
			"13": "<sup>13</sup> Enti Allah Taala setanah enggau kitai, kitai deka menang. Iya deka ngalahka bala munsuh kitai."
		},
		"109": {
			"1": "<sup>1</sup> Aku muji Nuan, O Allah Taala. Anang Nuan enda bemunyi!",
			"2": "<sup>2</sup> Orang ke jai enggau orang ke bula, nyerang aku. Sida betenahka aku,",
			"3": "<sup>3</sup> bejaku jai pasal aku, lalu nyerang aku enggau nadai kebuah.",
			"4": "<sup>4</sup> Sida ngelaban aku, taja pen aku rinduka sida lalu udah besampika sida.",
			"5": "<sup>5</sup> Sida malas pemanah aku ngena penyai, lalu malas pengerindu aku ngena pemenchi.",
			"6": "<sup>6</sup> Pilih sekeda hakim ti jai ngelaban iya, lalu awakka siku orang bedawaka iya.",
			"7": "<sup>7</sup> Lebuh iya kena pechara, awakka iya ditemu udah ngereja penyalah, datai ke sampi iya dikumbai dosa!",
			"8": "<sup>8</sup> Awakka iya pandak umur; awakka orang bukai ngambi pengawa iya!",
			"9": "<sup>9</sup> Awakka bala anak iya nadai apai, lalu bini iya balu!",
			"10": "<sup>10</sup> Awakka bala anak iya bejalai segau-segau bepintaka utai; Awakka sida diburu ari tembawai ti alai sida diau!",
			"11": "<sup>11</sup> Awakka orang ke udah nyali iya utai, ngerampas semua reta tengkira iya; lalu awakka orang kampar ngenchuri semua utai ti udah ulih iya bekereja.",
			"12": "<sup>12</sup> Awakka nadai orang deka nelap enggau iya tauka kasihka bala anak iya ke tinggal nadai apai.",
			"13": "<sup>13</sup> Awakka semua peturun iya mati magang, lalu awakka nama iya enda dikingatka agi ba peturun ti nangkanka iya.",
			"14": "<sup>14</sup> Awakka TUHAN ngingatka penyai aki ini iya, lalu awakka dosa indai iya enda diampun.",
			"15": "<sup>15</sup> Awakka TUHAN seruran ingatka dosa sida, lalu awakka sida enda dikingatka agi!",
			"16": "<sup>16</sup> Laban iya nadai kala berunding siru enggau orang, tang merinsaka orang ke seranta enggau ke suntuk utai lalu merinsaka orang ke tusah datai ke sida mati.",
			"17": "<sup>17</sup> Iya rindu nyumpah; awakka iya empu ditinggang sumpah! Iya enggai merekatka orang; awakka iya empu nadai bulih berekat!",
			"18": "<sup>18</sup> Iya ngerasukka tebiat nyumpah; awakka sumpah iya tama ke dalam tubuh iya empu baka ai, baka minyak, ti dalam tulang iya!",
			"19": "<sup>19</sup> Awakka sumpah nya nyerayung iya baka kain baka belulang ti melit punggung iya!",
			"20": "<sup>20</sup> Awakka tu nyadika upah ti diberi TUHAN ngagai orang ke bedawaka aku, orang ke bejaku jai ngelaban aku!",
			"21": "<sup>21</sup> Tang Nuan, O Allah Taala ti TUHAN aku, tulung meh aku ketegal nama Nuan; laban pengerindu Nuan ti meruan belama iya endang amat manah, lepaska meh aku tu.",
			"22": "<sup>22</sup> Laban aku tu seranta sereta suntuk, lalu ati aku amat tusah.",
			"23": "<sup>23</sup> Aku nyau deka lesap baka ayuk lebuh lemai hari. Aku dikebuka baka buntak.",
			"24": "<sup>24</sup> Patung aku lemi ketegal aku bepasa; lalu tubuh aku nyau kurus.",
			"25": "<sup>25</sup> Aku tu diketawaka orang ke bedawaka aku; lebuh sida meda aku sida ngilikka pala.",
			"26": "<sup>26</sup> Tulung meh aku, O TUHAN Allah Taala aku. Selamatka aku ketegal pengerindu Nuan ti meruan belama iya!",
			"27": "<sup>27</sup> Asuh bala munsuh aku nemu Nuan udah ngelepaska aku.",
			"28": "<sup>28</sup> Sida nyangka deka nyumpah aku, tang Nuan deka merekatka aku. Awakka sida malu, lalu awakka aku ke nembiak Nuan, gaga ati.",
			"29": "<sup>29</sup> Awakka bala orang ke bedawaka aku malu. Awakka sida nyerayung diri ngena pemalu nyadika baju panjai sida.",
			"30": "<sup>30</sup> Aku deka meri terima kasih ngagai TUHAN enggau nyawa ti inggar. Aku deka muji Iya lebuh orang begempuru bebala mayuh,",
			"31": "<sup>31</sup> laban Iya ngeringka orang ke seranta lalu ngelepaska sida ari orang ke ngeletak sida diukum dibunuh."
		},
		"110": {
			"1": "<sup>1</sup> Ku TUHAN bejaku ngagai tuan aku, “Duduk ditu muak ari kanan Aku datai ke Aku udah ngaga munsuh nuan nyadika pelasar kaki nuan.”",
			"2": "<sup>2</sup> TUHAN deka ngerembaika kuasa diraja nuan ari Sion. Ku Iya bejaku, “Perintah bala munsuh nuan!”",
			"3": "<sup>3</sup> Bala nembiak nuan deka datai mantu nuan ba hari nuan deka nguluka bala nuan ba bukit ti kudus. Bala nembiak nuan deka datai ngagai nuan baka ambun pagi hari.",
			"4": "<sup>4</sup> TUHAN udah besemaya ngena sumpah lalu Iya enda matak semaya Diri: “Nuan tu imam belama iya murihka Imam Melkisedek.”",
			"5": "<sup>5</sup> TUHAN bisi ari kanan nuan. Lebuh Iya ringat, Iya deka ngalahka semua raja.",
			"6": "<sup>6</sup> Iya deka ngakim semua bansa lalu ngisi semua bansa nya enggau bangkai; Iya deka ngalahka semua raja di serata dunya.",
			"7": "<sup>7</sup> Iya deka ngirup ari sungai ti ngimbai jalai raya, lalu lebuh iya udah berasai kering agi, iya deka menang."
		},
		"111": {
			"1": "<sup>1</sup> Puji TUHAN! Aku deka meri terima kasih ngagai TUHAN enggau pengabis ati aku lebuh nembiak Iya begempuru bebala mayuh.",
			"2": "<sup>2</sup> Naka meh pemadas pengawa TUHAN, ti deka dipelajarka semua orang ke gagit ati ke utai nya.",
			"3": "<sup>3</sup> Semua utai ti dikereja Iya penuh laban basa enggau puji, lalu pengelurus Iya meruan belama iya.",
			"4": "<sup>4</sup> Iya udah ngasuh pengawa Iya ti badas dikingatka; TUHAN endang nelap sereta kasih.",
			"5": "<sup>5</sup> Iya meri pemakai ngagai orang ke nangika Iya. Iya endang seruran ingatka sempekat Iya.",
			"6": "<sup>6</sup> Iya udah mandangka kuasa pengawa Iya ngagai bala nembiak Iya lebuh Iya meri menua orang bansa bukai ngagai sida.",
			"7": "<sup>7</sup> Dalam semua utai ti dikereja Iya, Iya endang tetap ati sereta lurus. Semua pesan Iya endang tau dikarapka magang.",
			"8": "<sup>8</sup> Pesan Iya meruan belama iya; Iya ngeletak pesan nya enggau ati ti tetap sereta enggau pengelurus.",
			"9": "<sup>9</sup> Iya udah nebus bala nembiak Iya lalu udah ngaga siti sempekat ti meruan belama iya enggau sida. Iya endang kudus sereta bekuasa!",
			"10": "<sup>10</sup> Nangika TUHAN nya pun penemu-dalam. Semua orang ke ngena penemu-dalam sigi amat nemu meretika utai. Awakka TUHAN dipuji belama iya."
		},
		"112": {
			"1": "<sup>1</sup> Puji TUHAN! Beberekat meh orang ke nangika TUHAN, ke gaga ati ngasika pesan Iya.",
			"2": "<sup>2</sup> Bala peturun iya deka nyadi orang ke bekuasa dalam menua tu. Peturun iya deka bulih berekat.",
			"3": "<sup>3</sup> Ruang bilik iya deka bulih pengaya, lalu pengelurus iya deka meruan belama iya.",
			"4": "<sup>4</sup> Penampak manchar ba endur ti petang ke orang ke badas, ke orang ke nemu kasih, nelap, sereta lurus ati.",
			"5": "<sup>5</sup> Lantang meh orang ke gerah ati nyali utai ngagai orang, ke bejalaika pengawa iya enggau atur ti lurus.",
			"6": "<sup>6</sup> Laban orang ke lurus nadai kala lebu. Iya deka dikingatka belama iya.",
			"7": "<sup>7</sup> Iya enda takut nerima berita ti enda manah. Ati iya tegap, lalu iya manggaika pengarap iya ba TUHAN.",
			"8": "<sup>8</sup> Iya enda irau ati tauka takut, datai ke meda munsuh iya alah.",
			"9": "<sup>9</sup> Iya meri utai enggau ati ti gerah mudah ngagai orang ke suntuk, lalu penelap iya nadai kala puntan. Iya deka nyadi orang ke bekuasa sereta dibasa orang.",
			"10": "<sup>10</sup> Orang ke jai meda nya, lalu ringat. Ngeli sida kerik-kerik, lalu sida lesap. Pengadang sida lenyau belama iya."
		},
		"113": {
			"1": "<sup>1</sup> Puji TUHAN! Kita ke nembiak TUHAN, puji meh nama Iya!",
			"2": "<sup>2</sup> Puji meh nama TUHAN kenyau ari diatu datai ke belama iya.",
			"3": "<sup>3</sup> Ari timur nyentuk ke barat puji meh nama TUHAN!",
			"4": "<sup>4</sup> TUHAN merintah semua bansa, mulia Iya tinggi agi ari langit.",
			"5": "<sup>5</sup> Nadai orang sebaka enggau TUHAN Allah Taala kitai. Iya diau ba endur ti tinggi di atas,",
			"6": "<sup>6</sup> tang Iya nilik ke baruh deka meda langit enggau dunya.",
			"7": "<sup>7</sup> Iya ngasuh orang ke seranta diau lantang. Iya ngelepaska orang ke suntuk ari pemerinsa sida",
			"8": "<sup>8</sup> lalu ngasuh sida duduk begulai enggau bala raja, bala raja ke megai nembiak Iya.",
			"9": "<sup>9</sup> Iya meri rumah ngagai indu ke balu, lalu ngasuh iya gaga ati mayuh anak. Puji TUHAN!"
		},
		"114": {
			"1": "<sup>1</sup> Lebuh orang Israel pansut ari menua Ejip, lebuh bala peturun Jakop angkat ari menua nya,",
			"2": "<sup>2</sup> Judah nyadi nembiak TUHAN ti kudus, lalu Israel nyadi pesaka Iya.",
			"3": "<sup>3</sup> Tasik Mirah malik lalu rari. Sungai Jordan lalu ngetu rarat.",
			"4": "<sup>4</sup> Bukit engkejuk baka kambin. Munggu merejuk baka anak bedus.",
			"5": "<sup>5</sup> Nama kebuah nuan rari, Tasik? Lalu nuan, Jordan, nama kebuah nuan ngetu rarat?",
			"6": "<sup>6</sup> Bukit, nama kebuah kita engkejuk baka kambin? Munggu, nama kebuah kita merejuk baka anak bedus?",
			"7": "<sup>7</sup> Begetar meh nuan dunya, lebuh TUHAN datai, di mua Allah Taala Jakop,",
			"8": "<sup>8</sup> ke ngubah batu galang nyadi letung lalu ngubah tingkah batu nyadi mata ai."
		},
		"115": {
			"1": "<sup>1</sup> Ukai kami, O TUHAN, ukai kami, tang Nuan siku aja ke patut dipuji, laban pengerindu enggau penetap ati Nuan endang meruan belama iya!",
			"2": "<sup>2</sup> Nama kebuah semua bansa nanya, “Dini alai Allah Taala sida?”",
			"3": "<sup>3</sup> Allah Taala kami di serega. Iya ngereja sebarang utai ti dikedekaka Iya.",
			"4": "<sup>4</sup> Engkeramba sida digaga ari pirak enggau emas, ditempa jari mensia.",
			"5": "<sup>5</sup> Sida bisi lubang nyawa, tang enda nemu bejaku, bisi mata, tang enda ulih meda.",
			"6": "<sup>6</sup> Sida bisi pending, tang enda ulih ninga, bisi idung, tang enda ulih nyium utai.",
			"7": "<sup>7</sup> Sida bisi jari, tang enda ulih ngama utai, bisi kaki, tang enda ulih bejalai. Sida enda ulih bemunyi.",
			"8": "<sup>8</sup> Awakka semua orang ke nempa sereta manggaika pengarap sida ba engkeramba nya nyadi baka engkeramba ti udah ditempa sida.",
			"9": "<sup>9</sup> Panggaika pengarap kita ba TUHAN, kita orang Israel. Iya nulung sereta nyaga kita.",
			"10": "<sup>10</sup> Panggaika pengarap kita ba TUHAN, kita imam Allah Taala. Iya nulung sereta nyaga kita.",
			"11": "<sup>11</sup> Panggaika pengarap kita ba TUHAN, semua kita ke nangika Iya. Iya nulung sereta nyaga kita.",
			"12": "<sup>12</sup> TUHAN ingatka kitai, lalu deka merekatka kitai. Iya deka merekatka orang Israel enggau semua imam Allah Taala.",
			"13": "<sup>13</sup> Iya deka merekatka genap iku orang ke nangika Iya, ke besai enggau ke mit.",
			"14": "<sup>14</sup> Awakka TUHAN meri mayuh anak ngagai kita enggau ngagai peturun kita!",
			"15": "<sup>15</sup> Awakka kita diberekatka TUHAN ti udah ngaga langit enggau dunya!",
			"16": "<sup>16</sup> Serega endang enggi TUHAN siku aja, tang Iya meri dunya ngagai kitai mensia.",
			"17": "<sup>17</sup> Orang ke mati enda muji TUHAN, lalu orang ke udah nurun ngagai dunya ti sunyi. enda mega muji TUHAN.",
			"18": "<sup>18</sup> Tang kitai ke idup, deka muji TUHAN kenyau ari diatu datai ke belama iya. Puji TUHAN!"
		},
		"116": {
			"1": "<sup>1</sup> Aku rinduka TUHAN, laban Iya udah ninga aku, enggau sampi aku.",
			"2": "<sup>2</sup> Laban Iya nyingitka pending Iya ngagai aku nya alai aku deka ngangau seagi aku idup.",
			"3": "<sup>3</sup> Kuasa pemati bekelingika aku. Penakut aku ke pendam nyau malut aku. Aku takut sereta irau.",
			"4": "<sup>4</sup> Aku lalu ngangauka nama TUHAN, “O TUHAN, aku minta Nuan ngidupka nyawa aku!”",
			"5": "<sup>5</sup> TUHAN kasih sereta badas. Allah Taala kitai endang kasih.",
			"6": "<sup>6</sup> TUHAN nyaga orang ke nadai daya. Lebuh aku dituntung penusah, Iya nyelamatka aku.",
			"7": "<sup>7</sup> Pulai meh nuan, O semengat aku, ngagai endur alai nuan belelak; laban TUHAN udah manah enggau nuan.",
			"8": "<sup>8</sup> Laban TUHAN udah ngelepaska aku ari pemati. nagang ai mata aku pansut nagang aku teterap.",
			"9": "<sup>9</sup> Nya alai aku bejalai di mua TUHAN di dunya orang ke idup.",
			"10": "<sup>10</sup> Aku majak arap, taja pen aku nyebut, “Aku balat kena perinsa.”",
			"11": "<sup>11</sup> Ati aku iran, lalu aku nyebut, “Nadai siku orang tau dikarapka.”",
			"12": "<sup>12</sup> Nama utai ti tau diberi aku ngagai TUHAN kena malas semua pemanah Iya enggau aku?",
			"13": "<sup>13</sup> Aku deka mai piring wain lalu ngangauka nama TUHAN;",
			"14": "<sup>14</sup> Aku deka mayar semaya aku ngagai TUHAN di mua semua bala nembiak Iya.",
			"15": "<sup>15</sup> Naka meh penyayau TUHAN ke nembiak Iya ke mati!",
			"16": "<sup>16</sup> O TUHAN, aku tu nembiak Nuan; Aku tu nembiak Nuan; anak ulun Nuan. Nuan udah muka tali ti dikena nanchang aku.",
			"17": "<sup>17</sup> Aku deka meri ngagai Nuan piring terima kasih lalu ngangauka nama TUHAN.",
			"18": "<sup>18</sup> Aku deka mayar semaya aku ngagai TUHAN di mua semua nembiak Iya,",
			"19": "<sup>19</sup> dalam kot ti dalam Rumah Sembiang TUHAN, ba tengah-tengah nuan, O Jerusalem. Puji TUHAN!"
		},
		"117": {
			"1": "<sup>1</sup> Puji meh TUHAN, O kita semua bansa! Puji meh Iya, semua kita mensia!",
			"2": "<sup>2</sup> Laban pengerindu Iya ke kitai endang besai sereta meruan belama iya, lalu penetap ati TUHAN endang meruan belama iya. Puji TUHAN!"
		},
		"118": {
			"1": "<sup>1</sup> Beri meh terima kasih ngagai TUHAN, laban Iya endang badas; pengerindu Iya meruan belama iya.",
			"2": "<sup>2</sup> Awakka orang Israel nyebut, “Pengerindu Iya endang meruan belama iya.”",
			"3": "<sup>3</sup> Awakka ruang bilik imam Aron nyebut, “Pengerindu Iya endang meruan belama iya.”",
			"4": "<sup>4</sup> Awakka semua orang ke nangika TUHAN nyebut, “Pengerindu Iya endang meruan belama iya.”",
			"5": "<sup>5</sup> Lebuh aku tusah, aku ngangauka TUHAN; TUHAN nyaut aku lalu ngelepaska aku.",
			"6": "<sup>6</sup> Aku enda takut laban TUHAN bisi ngimbai aku. Nama utai ti ulih dikereja orang ngagai aku?",
			"7": "<sup>7</sup> TUHAN ngimbai aku lalu nulung aku; aku deka menang lalu malik ngagai bala munsuh ke begedika aku.",
			"8": "<sup>8</sup> Manah agi belindung ba TUHAN ari ke manggaika diri ba mensia.",
			"9": "<sup>9</sup> Manah agi belindung ba TUHAN ari ke manggaika diri ba bala tuai mensia.",
			"10": "<sup>10</sup> Semua bansa bekelingika aku; tang aku ngerusak sida laban TUHAN nulung aku!",
			"11": "<sup>11</sup> Sida ngelulung aku; tang aku ngerusak sida laban TUHAN nulung aku!",
			"12": "<sup>12</sup> Sida betatam ngelingi aku baka indu manyi, tang sida angus, ngenyampat babas ti diempa api. Aku ngerusak sida laban TUHAN nulung aku!",
			"13": "<sup>13</sup> Aku balat amat diserang sida lalu aku nyau deka alah tang TUHAN nulung aku.",
			"14": "<sup>14</sup> TUHAN nyadika pengering enggau lagu aku; Iya udah nyelamatka aku.",
			"15": "<sup>15</sup> Pendingka bala nembiak Allah Taala manjung gaga ati dalam langkau kain sida laban sida udah menang: “Jari kanan TUHAN ti udah menang!",
			"16": "<sup>16</sup> Jari kanan TUHAN ti patut dipuji, Jari kanan TUHAN udah menang!”",
			"17": "<sup>17</sup> Aku enda mati, tang deka idup lalu nusui pasal utai ti udah dikereja TUHAN.",
			"18": "<sup>18</sup> TUHAN udah balat amat ngukum aku, tang Iya enda nejuka aku mati.",
			"19": "<sup>19</sup> Buka meh pintu Rumah Sembiang ke aku, ngambika aku ulih tama nengah pintu nya lalu meri terima kasih ngagai TUHAN!",
			"20": "<sup>20</sup> Tu pintu TUHAN. Orang ke lurus deka tama nengah pintu nya.",
			"21": "<sup>21</sup> Aku meri terima kasih ngagai Nuan, O TUHAN, laban Nuan udah nyaut aku, lalu nyelamatka aku.",
			"22": "<sup>22</sup> Batu ti enda diguna tukang rumah udah nyadi batu ti pemadu beguna.",
			"23": "<sup>23</sup> Utai tu udah dikereja TUHAN; Utai nya manah gaya dipeda kitai!",
			"24": "<sup>24</sup> Tu meh hari ti udah digaga TUHAN; aram kitai rami gaga lalu ngelantangka diri ba hari tu.",
			"25": "<sup>25</sup> Lepaska kami, O TUHAN, Lepaska kami! Asuh kami mujur, O TUHAN!",
			"26": "<sup>26</sup> Beberekat meh iya ke datai dalam nama TUHAN! Kami muji nuan ari Rumah TUHAN.",
			"27": "<sup>27</sup> TUHAN endang Allah Taala, lalu Iya udah meri kitai penampak. Bai dan kayu ba jari kita, lalu belabuh begawai bejalai ngelingi alta.",
			"28": "<sup>28</sup> Nuan endang Allah Taala aku, lalu aku deka meri terima kasih ngagai Nuan. Nuan endang Allah Taala aku, Aku deka ngemesaika Nuan.",
			"29": "<sup>29</sup> Beri terima kasih ngagai TUHAN, laban Iya endang badas, laban pengerindu Iya endang meruan belama iya."
		},
		"119": {
			"1": "<sup>1</sup> Beberekat meh orang ke nadai ngereja penyalah, ke ngasika adat TUHAN.",
			"2": "<sup>2</sup> Beberekat meh orang ke nitihka adat TUHAN, ke ngiga Iya enggau pengabis ati sida.",
			"3": "<sup>3</sup> Sida nadai kala ngereja penyalah, tang nitihka TUHAN.",
			"4": "<sup>4</sup> Nuan, O TUHAN, udah meri ngagai kami adat Nuan lalu udah ngasuh kami tetap ati ngasika adat nya.",
			"5": "<sup>5</sup> Aku ngarapka diri ulih tetap ati nitihka semua atur Nuan!",
			"6": "<sup>6</sup> Nya baru aku enda malu, laban aku meratika semua pesan Nuan.",
			"7": "<sup>7</sup> Aku deka muji Nuan enggau ati ti tuchi, lebuh aku belajarka adat Nuan ti lurus.",
			"8": "<sup>8</sup> Aku deka ngasika adat Nuan. Anang ninggalka aku!",
			"9": "<sup>9</sup> Baka ni ku orang ke biak ulih ngintu pendiau diri tuchi? Ari sida ke ngasika pesan Nuan.",
			"10": "<sup>10</sup> Aku deka ngiga Nuan enggau pengabis ati aku; anang nejuka aku limpang ari pesan Nuan.",
			"11": "<sup>11</sup> Aku udah nyimpan adat Nuan dalam ati aku, ngambika aku enda bedosa ngelaban Nuan.",
			"12": "<sup>12</sup> Aku muji Nuan, O TUHAN. Ajar meh aku jalai Nuan.",
			"13": "<sup>13</sup> Aku deka madahka semua adat ti udah diberi Nuan.",
			"14": "<sup>14</sup> Aku gaga ati nitihka adat Nuan, lebih agi ari ke gaga ati ngembuan mayuh reta.",
			"15": "<sup>15</sup> Aku belematika atur Nuan, lalu meratika ajar Nuan.",
			"16": "<sup>16</sup> Aku gaga ati ke adat Nuan. Aku deka seruran ingatka pesan Nuan.",
			"17": "<sup>17</sup> Awakka Nuan meri aku ke nembiak Nuan mayuh utai, ngambika aku ulih idup lalu ngasika ajar Nuan.",
			"18": "<sup>18</sup> Buka meh mata aku, ngambika aku ulih meda utai ti badas dalam adat Nuan.",
			"19": "<sup>19</sup> Aku enda diau lama di dunya tu; anang ngelalaika pesan Nuan ari aku.",
			"20": "<sup>20</sup> Ati aku balat lelengau ke atur Nuan berari.",
			"21": "<sup>21</sup> Nuan nganu orang ke sumbung, ke kena sumpah, ke limpang ari pesan Nuan.",
			"22": "<sup>22</sup> Buai ari aku jaku pechat enggau jaku kelese sida, laban aku udah ngasika adat Nuan.",
			"23": "<sup>23</sup> Taja pen bala raja bepekat ngelaban aku, tang aku deka belematika ajar Nuan.",
			"24": "<sup>24</sup> Adat Nuan ngasuh ati aku gaga. Adat Nuan nyadika penyanding penemu aku.",
			"25": "<sup>25</sup> Aku alah lalu garing-garing malai amau. Idupka aku baru, munyi ti udah disemaya Nuan.",
			"26": "<sup>26</sup> Lebuh aku madahka semua utai ti udah dikereja aku, Nuan udah nyaut aku. Ajar meh aku jalai Nuan.",
			"27": "<sup>27</sup> Asuh aku meretika adat Nuan, lalu aku deka belematika pengawa Nuan ti badas.",
			"28": "<sup>28</sup> Aku enda ulih natka ati diri ti tusah. Keringka aku, munyi ti udah disemaya Nuan.",
			"29": "<sup>29</sup> Kejangka aku jauh ari pendiau ti salah, lalu ulih pengasih Nuan, ajar aku adat Nuan.",
			"30": "<sup>30</sup> Aku udah mutuska runding deka tetap ati ngagai Nuan. Aku udah mendingka atur Nuan.",
			"31": "<sup>31</sup> Aku bepegai ba adat Nuan, O TUHAN. Anang nejuka orang ngemaluka aku.",
			"32": "<sup>32</sup> Aku deka nitihka pesan Nuan lebuh Nuan ngasuh aku lebih agi mereti.",
			"33": "<sup>33</sup> Ajar meh aku, O TUHAN, reti adat Nuan, lalu aku deka ngasika adat nya datai ke ujung.",
			"34": "<sup>34</sup> Asuh aku mereti, ngambika aku ngasika adat Nuan lalu nitihka iya enggau pengabis ati aku.",
			"35": "<sup>35</sup> Iring aku nitihka pesan Nuan, laban aku rinduka iya.",
			"36": "<sup>36</sup> Asuh ati aku inginka adat Nuan, ukai inginka pengaya.",
			"37": "<sup>37</sup> Anang ngasuh mata aku merening ngagai utai ti ngasuh aku sumbung; lalu awakka aku idup nitihka jalai Nuan.",
			"38": "<sup>38</sup> Kingatka semaya Nuan enggau aku ke nembiak Nuan, semaya ti digaga Nuan enggau orang ke ngasika Nuan.",
			"39": "<sup>39</sup> Kejangka aku jauh ari jaku anu ti diketakutka aku; laban adat Nuan endang manah.",
			"40": "<sup>40</sup> Aku lelengauka pesan Nuan; laban pengelurus Nuan meri aku pengidup.",
			"41": "<sup>41</sup> pandangka pemesai pengerindu Nuan ke aku, O TUHAN, lalu selamatka aku, munyi ti udah disemaya Nuan;",
			"42": "<sup>42</sup> nya baru aku ulih nyaut orang ke ngeleseka aku laban aku manggaika diri ba jaku Nuan.",
			"43": "<sup>43</sup> Lalu anang nagang aku nyebut jaku ti bendar, laban aku andal ati ke adat Nuan.",
			"44": "<sup>44</sup> Aku deka seruran ngasika adat Nuan, belama lama iya;",
			"45": "<sup>45</sup> lalu aku deka dilepaska laban aku udah ngiga ajar Nuan.",
			"46": "<sup>46</sup> Aku deka mega bejakuka pasal adat Nuan ngagai bala raja lalu aku enda malu;",
			"47": "<sup>47</sup> laban aku gaga ati ke pesan Nuan ti dikerinduka aku.",
			"48": "<sup>48</sup> Aku bebasaka pesan Nuan ti dikerinduka aku, lalu aku deka belematika ajar Nuan.",
			"49": "<sup>49</sup> Kingatka semaya Nuan enggau aku ke nembiak Nuan, ti ngasuh ati aku andal.",
			"50": "<sup>50</sup> Tu meh utai ti ngenyamaika ati aku lebuh aku natka diri merinsa, iya nya, semaya Nuan deka meri aku pengidup.",
			"51": "<sup>51</sup> Orang ke enda nangika Allah Taala ngeleseka aku, tang aku enggai limpang ari adat Nuan.",
			"52": "<sup>52</sup> Lebuh aku berundingka adat ti diberi Nuan dulu kelia, ati aku lantang, O TUHAN.",
			"53": "<sup>53</sup> Aku balat pedis ati lebuh aku meda orang ke jai ngelengkaka adat Nuan.",
			"54": "<sup>54</sup> Adat Nuan nyadika lagu aku sekumbang aku ke idup enda lama di dunya tu.",
			"55": "<sup>55</sup> Lebuh malam hari aku ngingatka nama Nuan, O TUHAN, lalu ngasika adat Nuan.",
			"56": "<sup>56</sup> Berekat tu udah datai ba aku, ngambika aku ngasika ajar Nuan.",
			"57": "<sup>57</sup> Semina Nuan siku aja ke dikinginka aku, O TUHAN. Aku besemaya deka ngasika jaku Nuan.",
			"58": "<sup>58</sup> Enggau pengabis ati aku, aku minta Nuan kasihka aku munyi ti udah disemaya Nuan!",
			"59": "<sup>59</sup> Aku udah berundingka pasal perangai aku, lalu aku besemaya deka nitihka adat Nuan.",
			"60": "<sup>60</sup> Aku enda betangguh tang beguai-guai ngasika pesan Nuan.",
			"61": "<sup>61</sup> Orang ke jai udah nan panjuk kena nyintak aku, tang aku seruran ingatka adat Nuan.",
			"62": "<sup>62</sup> Aku dani tengah malam lalu muji Nuan ketegal pengelurus atur Nuan.",
			"63": "<sup>63</sup> Aku tu pangan semua orang ke nangika Nuan, semua orang ke ngasika adat Nuan.",
			"64": "<sup>64</sup> TUHAN, dunya tu penuh laban pengerindu Nuan ti meruan belama iya. Ajar aku pesan Nuan.",
			"65": "<sup>65</sup> Nuan udah ngamatka semaya Nuan, TUHAN, lalu Nuan udah manah enggau aku ke nembiak Nuan.",
			"66": "<sup>66</sup> Beri aku penemu enggau runding ti tajam laban aku arapka pesan Nuan.",
			"67": "<sup>67</sup> Sebedau Nuan ngukum aku suba, aku suah udah salah, tang diatu aku ngasika jaku Nuan.",
			"68": "<sup>68</sup> Nuan endang badas lalu ngereja utai ti badas; Ajar aku adat Nuan.",
			"69": "<sup>69</sup> Orang ke enda nangika Allah Taala bejaku bula pasal aku, tang aku ngasika atur Nuan enggau pengabis ati aku.",
			"70": "<sup>70</sup> Sida enda mereti, tang aku gaga ati ke adat Nuan.",
			"71": "<sup>71</sup> Aku untung natka diri kena perinsa, ngambika aku nemu belajarka adat Nuan.",
			"72": "<sup>72</sup> Adat ti udah diberi Nuan besai agi reti ba aku ari semua duit emas enggau semua duit pirak di dunya tu.",
			"73": "<sup>73</sup> Nuan udah ngaga enggau nempa aku; Asuh aku mereti, ngambika aku ulih belajarka adat Nuan.",
			"74": "<sup>74</sup> Orang ke nangika Nuan deka meda aku lalu rami gaga, laban aku andal ati ke semaya Nuan.",
			"75": "<sup>75</sup> Aku nemu atur Nuan endang lurus, O TUHAN, lalu Nuan udah ngukum aku laban Nuan endang tetap ati.",
			"76": "<sup>76</sup> Awakka pengerindu Nuan ti meruan belama iya, merindang ati aku, munyi ti udah disemaya Nuan ngagai aku ke nembiak Nuan.",
			"77": "<sup>77</sup> Kasihka aku, ngambika aku idup laban aku gaga ati ke adat Nuan.",
			"78": "<sup>78</sup> Awakka orang ke enda nangika Allah Taala malu, laban sida udah nguji nipu aku; tang aku deka belematika atur Nuan.",
			"79": "<sup>79</sup> Awakka orang ke nangika Nuan datai ngagai aku, ngambika sida nemu adat Nuan.",
			"80": "<sup>80</sup> Awakka aku bebasaka adat Nuan ngambika aku enda malu.",
			"81": "<sup>81</sup> TUHAN, aku nyau lelak nganti Nuan ngelepaska aku. Aku manggaika diri ba jaku Nuan.",
			"82": "<sup>82</sup> Mata aku nyau lelak nganti utai ti disemaya Nuan, lalu aku nanya, “Kemaya Nuan deka merindang aku?”",
			"83": "<sup>83</sup> Laban aku tu nadai guna baka kambut wain ti udah dibuai, tang aku nadai kala enda ingatka adat Nuan.",
			"84": "<sup>84</sup> Ni pengelama aku agi nganti? Kemaya Nuan deka ngakim orang ke merinsaka aku?",
			"85": "<sup>85</sup> Orang ke enda nangika Allah Taala, ti enda ngasika adat Nuan, udah numbak lubang kena sida nangkap aku.",
			"86": "<sup>86</sup> Semua pesan Nuan endang tau dikarapka magang. Orang merinsaka aku ngena pemula - tulung aku!",
			"87": "<sup>87</sup> Sida nyau deka ulih munuh aku, tang aku nadai kala ngelengkaka pesan Nuan.",
			"88": "<sup>88</sup> Ulih pengerindu Nuan ti meruan belama iya, anang munuh aku, ngambika aku ulih ngasika adat Nuan.",
			"89": "<sup>89</sup> Jaku Nuan, O TUHAN, deka meruan belama iya. Iya meruan belama iya di serega.",
			"90": "<sup>90</sup> Penetap ati Nuan endang meruan belama iya; Nuan udah ngengkahka dunya ba endur iya, lalu iya enda nginsit.",
			"91": "<sup>91</sup> Semua utai mengkang meruan magang nyentuk ngagai sehari tu laban pesan Nuan, laban semua sida nya endang nembiak Nuan magang.",
			"92": "<sup>92</sup> Enti aku enda udah gaga ati ke adat Nuan, aku tentu udah mati laban pemerinsa aku.",
			"93": "<sup>93</sup> Aku enggai sekali-kali ngelengkaka atur Nuan, laban Nuan udah ngidupka nyawa aku ngena atur nya.",
			"94": "<sup>94</sup> Aku endang enggi Nuan - selamatka aku; laban aku udah ngiga ajar Nuan.",
			"95": "<sup>95</sup> Orang ke jai ngipa aku, deka munuh aku, tang aku deka belematika adat Nuan.",
			"96": "<sup>96</sup> Aku nemu, semua utai endang bisi sekat magang, tang pesan Nuan amat luas.",
			"97": "<sup>97</sup> Aku rinduka adat Nuan! Aku belematika iya sepemanjai hari.",
			"98": "<sup>98</sup> Pesan Nuan ngasuh aku pintar agi ari bala munsuh aku, laban pesan Nuan endang seruran enggau aku.",
			"99": "<sup>99</sup> Aku mereti agi ke utai ari semua pengajar aku, laban aku belematika ajar Nuan.",
			"100": "<sup>100</sup> Aku meretika mayuh agi utai ari sida ke tuai umur, laban aku ngasika ajar Nuan.",
			"101": "<sup>101</sup> Aku udah nyeliahka semua ulah ti jai, ngambika aku ulih ngasika jaku Nuan.",
			"102": "<sup>102</sup> Aku nadai kala limpang ari adat Nuan laban Nuan Empu udah ngajar aku.",
			"103": "<sup>103</sup> Naka meh pemanis ajar Nuan, manis agi ari ai manyi dalam mulut aku!",
			"104": "<sup>104</sup> Aku bulih penemu-dalam ari ajar Nuan, nya alai aku begedika semua perangai ti jai.",
			"105": "<sup>105</sup> Leka jaku Nuan nyadika lampu tenggau aku nyadika tenggau jalai aku.",
			"106": "<sup>106</sup> Aku udah besumpah lalu udah ngamatka sumpah deka ngasika adat Nuan ti lurus.",
			"107": "<sup>107</sup> Aku balat merinsa; beri aku pengidup, O TUHAN, nitihka semaya nuan.",
			"108": "<sup>108</sup> Sambut meh sampi puji aku, O TUHAN, lalu ajar aku adat Nuan.",
			"109": "<sup>109</sup> Aku berambunka nyawa diri, tang aku nadai kala enda ingatka adat Nuan.",
			"110": "<sup>110</sup> Orang ke jai deka manjuk aku, tang aku nadai kala limpang ari ajar Nuan.",
			"111": "<sup>111</sup> Adat Nuan endang nyadika pesaka aku belama iya; sigi amat, sida nyungkak ati aku gaga.",
			"112": "<sup>112</sup> Aku mutuska runding deka ngasika adat Nuan belama iya datai ke ujung.",
			"113": "<sup>113</sup> Aku begedika orang ke aum-juak, tang aku rinduka adat Nuan.",
			"114": "<sup>114</sup> Nuan meh penyaga enggau pengelindung aku. Aku manggaika pengandal ati aku ba semaya Nuan.",
			"115": "<sup>115</sup> Rari ari aku, kita ke ngereja penyai, ngambika aku ngereja utai ti dipesanka Allah Taala aku.",
			"116": "<sup>116</sup> Beri aku pengering, munyi ti udah disemaya Nuan, ngambika aku idup, lalu anang nejuka aku asa ngapa ke pengadang aku!",
			"117": "<sup>117</sup> Pegai aku, ngambika aku likun lalu seruran meratika adat Nuan.",
			"118": "<sup>118</sup> Nuan nulak semua orang ke limpang ari adat Nuan; sigi amat, kelakar sida endang nadai guna.",
			"119": "<sup>119</sup> Nuan nulak semua orang ke jai baka uras; Nya kebuah aku rinduka adat Nuan.",
			"120": "<sup>120</sup> Tubuh aku getar-getar laban aku nangika Nuan, lalu aku takutka ukum Nuan.",
			"121": "<sup>121</sup> Aku udah ngereja utai ti lurus sereta ngena; anang ninggalka aku ba jari orang ke nekanka aku!",
			"122": "<sup>122</sup> Aku minta Nuan besemayaka nulung nembiak Nuan tu. Anang nejuka orang ke enda nangika Allah Taala nekanka aku!",
			"123": "<sup>123</sup> Mata aku nyau rapa nganti pengelepas ari Nuan, nganti Nuan ngamatka semaya Nuan.",
			"124": "<sup>124</sup> Ibun meh aku nitihka pengerindu Nuan ti meruan belama iya, lalu ajar aku adat Nuan.",
			"125": "<sup>125</sup> Aku tu nembiak Nuan; asuh aku mereti, ngambika aku nemu adat Nuan.",
			"126": "<sup>126</sup> TUHAN, tu meh maya Nuan patut ngereja utai, laban mensia udah ngelanggar adat Nuan.",
			"127": "<sup>127</sup> Aku rinduka pesan Nuan, lebih agi ari emas, lebih agi ari emas ti pemadu tulin.",
			"128": "<sup>128</sup> Nya alai aku nitihka semua ajar Nuan. Aku begedika semua jalai ti salah.",
			"129": "<sup>129</sup> Adat Nuan endang manah; nya alai aku deka ngasika adat Nuan.",
			"130": "<sup>130</sup> Lebuh jaku Nuan diterangka, iya meri penerang; iya meri penemu ngagai orang ke enda nemu utai.",
			"131": "<sup>131</sup> Mulut aku rangap-rangap lalu aku sengah-sengah lelengauka pesan Nuan.",
			"132": "<sup>132</sup> Kuingka Diri ngagai aku, lalu kasihka aku baka Nuan ke kasihka semua orang ke rinduka Nuan.",
			"133": "<sup>133</sup> Tegapka singkang aku munyi ti udah disemaya Nuan, lalu anang nejuka penyai ngalahka aku.",
			"134": "<sup>134</sup> Tebus aku ari orang ke nekanka pengidup aku, ngambika aku ulih ngasika ajar Nuan.",
			"135": "<sup>135</sup> Awakka penampak mua Nuan manchar ngagai aku ke nembiak Nuan, lalu ajar aku adat Nuan.",
			"136": "<sup>136</sup> Ai mata aku nyau bekerenjui, laban mensia enda ngasika adat Nuan.",
			"137": "<sup>137</sup> Nuan endang lurus, TUHAN, lalu ukum ti diberi Nuan endang ngena.",
			"138": "<sup>138</sup> Adat ti udah diberi Nuan endang betul sereta ngena.",
			"139": "<sup>139</sup> Aku pedis ati, laban bala munsuh aku enda ingatka ajar Nuan.",
			"140": "<sup>140</sup> Semaya Nuan endang tau dikarapka lalu aku rinduka iya!",
			"141": "<sup>141</sup> Aku tu orang ke nadai guna sereta dikeleseka orang, tang aku enda ngelengkaka ajar Nuan.",
			"142": "<sup>142</sup> Pengelurus Nuan deka tan belama iya lalu adat Nuan endang amat.",
			"143": "<sup>143</sup> Ati aku balat tusah sereta irau, tang pesan Nuan ngasuh ati aku gaga.",
			"144": "<sup>144</sup> Adat Nuan endang lurus belama iya. Asuh aku mereti ngambika aku ulih idup.",
			"145": "<sup>145</sup> Aku ngangauka Nuan enggau pengabis ati aku; saut aku, O TUHAN! aku besemaya deka ngasika adat Nuan!",
			"146": "<sup>146</sup> Aku ngangauka Nuan; selamatka aku, ngambika aku ngasika adat Nuan.",
			"147": "<sup>147</sup> Sebedau tumbuh mata panas, aku ngangauka Nuan minta tulung. Aku manggaika pengandal ati aku ba semaya Nuan.",
			"148": "<sup>148</sup> Semalam-malam aku enda tinduk, belematika semaya Nuan.",
			"149": "<sup>149</sup> Nuan endang rinduka aku belama, nya alai dingaka aku, O TUHAN. Kasihka aku, lalu selamatka aku!",
			"150": "<sup>150</sup> Orang ke jai ti deka merinsaka aku, nyau deka datai semak ngagai aku. Sida nya orang ke ngelanggar adat Nuan.",
			"151": "<sup>151</sup> Tang Nuan endang semak aku, O TUHAN, lalu semua pesan Nuan endang amat magang.",
			"152": "<sup>152</sup> Aku lama udah nemu adat Nuan lalu Nuan ngaga adat Nuan meruan belama iya.",
			"153": "<sup>153</sup> Peda Nuan pemerinsa aku, lalu lepaska aku, laban aku nadai kala limpang ari adat Nuan.",
			"154": "<sup>154</sup> Bejaku meh Nuan ngarika aku, lalu tebus aku; Selamatka aku, nitihka semaya Nuan.",
			"155": "<sup>155</sup> Orang ke jai enda dilepaska, laban sida enda ngasika adat Nuan.",
			"156": "<sup>156</sup> Naka meh pemesai pengasih Nuan, O TUHAN; selamatka aku nitihka pengelurus atur Nuan.",
			"157": "<sup>157</sup> Aku mayuh munsuh lalu orang ke nekanka pengidup aku mega mayuh, tang aku nadai kala limpang ari adat Nuan.",
			"158": "<sup>158</sup> Lebuh aku merening ngagai orang ke aum-juak, ati aku pedis, laban sida enda nitihka pesan Nuan.",
			"159": "<sup>159</sup> Peda Nuan pemalat aku rinduka ajar Nuan. Selamatka aku, laban pengerindu Nuan endang meruan belama iya!",
			"160": "<sup>160</sup> Semua adat Nuan endang amat magang, lalu semua adat Nuan endang lurus sereta meruan belama iya.",
			"161": "<sup>161</sup> Orang ke bekuasa merinsaka aku enggau nadai kebuah, tang aku nangika jaku Nuan.",
			"162": "<sup>162</sup> Aku gaga ati ke semaya Nuan, baka ke tetemuka pengaya.",
			"163": "<sup>163</sup> Aku begedi sereta kejika semua pemula, tang aku rinduka adat Nuan.",
			"164": "<sup>164</sup> Tujuh kali aku muji Nuan dalam sehari ketegal pengelurus adat Nuan.",
			"165": "<sup>165</sup> Orang ke rinduka adat Nuan diau likun belama; nadai utai ulih ngasuh sida tesangkut.",
			"166": "<sup>166</sup> Aku nganti Nuan ngelepaska aku, O TUHAN, lalu aku ngereja utai ti dipesanka Nuan.",
			"167": "<sup>167</sup> Aku ngasika adat Nuan. Aku rinduka sida enggau pengabis ati aku.",
			"168": "<sup>168</sup> Aku ngasika ajar enggau adat Nuan, laban Nuan meda semua utai ti dikereja aku.",
			"169": "<sup>169</sup> Awakka sabak aku datai ba Nuan, O TUHAN; asuh aku mereti, munyi ti udah disemaya Nuan.",
			"170": "<sup>170</sup> Pendingka sampi aku, lalu selamatka aku, munyi ti udah disemaya Nuan!",
			"171": "<sup>171</sup> Aku deka muji Nuan berari, ngambika Nuan ngajar aku adat Nuan.",
			"172": "<sup>172</sup> Aku deka belagu muji jaku Nuan, laban semua pesan Nuan endang lurus magang.",
			"173": "<sup>173</sup> Awakka Nuan seruran sedia nulung aku, laban aku nitihka pesan Nuan.",
			"174": "<sup>174</sup> Aku lelengauka pengelepas ari Nuan, O TUHAN, lalu aku gaga ati ke adat Nuan.",
			"175": "<sup>175</sup> Awakka aku idup ngambika aku ulih muji Nuan, lalu awakka adat Nuan nulung aku.",
			"176": "<sup>176</sup> Aku udah limpang baka bedus ti tesat; giga meh nembiak Nuan, laban aku nadai kala enda ingatka adat Nuan."
		},
		"120": {
			"1": "<sup>1</sup> Lebuh aku dituntung penusah, aku ngangauka TUHAN, lalu Iya nyaut aku.",
			"2": "<sup>2</sup> Selamatka aku, O TUHAN, ari orang ke bula enggau ari orang ke nipu.",
			"3": "<sup>3</sup> Nama utai ti deka diberi ngagai kita? Lalu nama utai bukai ti deka dikereja Allah Taala ngagai kita, kita orang pengelakar?",
			"4": "<sup>4</sup> Panah ti tajam ti ditimbakka soldadu, enggau bara api ti chinang-chinang!",
			"5": "<sup>5</sup> Tulah meh aku tu, diau di Mesek, begulai enggau orang Kedar.",
			"6": "<sup>6</sup> Aku udah kelalu lama diau begulai enggau orang ke begedika pemaik!",
			"7": "<sup>7</sup> Aku rinduka pemaik; tang lebuh aku bejakuka pasal pemaik, sida bejakuka pasal deka beperang."
		},
		"121": {
			"1": "<sup>1</sup> Aku engkadah ngagai bukit - Ari ni penyaup aku deka datai?",
			"2": "<sup>2</sup> Penyaup aku deka datai ari TUHAN, ti udah ngaga langit enggau dunya.",
			"3": "<sup>3</sup> Iya enggai nejuka nuan rebah. Penyaga nuan deka seruran nyaga nuan.",
			"4": "<sup>4</sup> Iya ke nyaga orang Israel enda ngantuk tauka tinduk.",
			"5": "<sup>5</sup> TUHAN endang nyaga nuan berari; Iya ngimbai nuan lalu nyaga nuan.",
			"6": "<sup>6</sup> Mata panas enda ngemediska nuan lebuh siang hari, lalu bulan enda ngemediska nuan lebuh malam hari.",
			"7": "<sup>7</sup> TUHAN deka nyaga nuan ari semua utai ti jai; Iya deka ngibun nuan likun.",
			"8": "<sup>8</sup> TUHAN deka nyaga nuan lebuh nuan bejalai enggau pulai kenyau ari diatu nyentuk ngagai belama lama iya."
		},
		"122": {
			"1": "<sup>1</sup> Aku gaga ati lebuh sida bejaku enggau aku, “Aram kitai mansang ngagai rumah TUHAN.”",
			"2": "<sup>2</sup> Lalu diatu kitai bisi ditu, bediri ba mua pintu nengeri Jerusalem!",
			"3": "<sup>3</sup> Jerusalem udah digaga nyadi nengeri ti manah gaya sereta betul atur.",
			"4": "<sup>4</sup> Tu meh endur alai raban bansa datai, raban bansa orang Israel, meri terima kasih ngagai TUHAN.",
			"5": "<sup>5</sup> Tu meh endur alai sida raja orang Israel duduk ngakim bala nembiak sida.",
			"6": "<sup>6</sup> Besampi minta pemaik ke nengeri Jerusalem: “Awakka orang ke rinduka nuan, nyadi kaya-raja.",
			"7": "<sup>7</sup> Awakka pemaik seruran meruan dalam kandang dinding kuta nuan enggau pengelikun dalam menara nuan.”",
			"8": "<sup>8</sup> Aku deka bejaku ngarika kaban belayan enggau pangan aku munyi tu ngagai Jerusalem, “Awakka pemaik meruan dalam nuan!”",
			"9": "<sup>9</sup> Ketegal rumah TUHAN Allah Taala kami aku deka besampi minta nuan nyadi kaya-raja."
		},
		"123": {
			"1": "<sup>1</sup> TUHAN, aku engkadah ngagai Nuan, engkadah ngagai serega ti alai Nuan merintah.",
			"2": "<sup>2</sup> Baka ulun ke manggaika diri ba tuan iya, baka orang gaji ti indu, ke manggaika diri ba indu ke ngaji iya, baka nya meh aku deka majak merening ngagai Nuan, O TUHAN Allah Taala kami, datai ke Nuan nyau kasihka kami.",
			"3": "<sup>3</sup> Kasihka kami, O TUHAN, kasihka kami, laban kami udah kelalu balat dipechat.",
			"4": "<sup>4</sup> Kami udah kelalu lama diketawaka orang ke kaya lalu dipechat orang ke sumbung."
		},
		"124": {
			"1": "<sup>1</sup> Kati enti TUHAN enda udah setanah enggau kitai? Awakka orang Israel nyebut:",
			"2": "<sup>2</sup> “Enti TUHAN enda udah setanah enggau kitai lebuh orang nyerang kitai,",
			"3": "<sup>3</sup> sida tentu udah nelan kitai idup-idup lebuh sida ringat pedis ati ngagai kitai;",
			"4": "<sup>4</sup> nya baru ai bah nabanka kitai, lalu ngelamun kitai;",
			"5": "<sup>5</sup> lalu kitai lemas.",
			"6": "<sup>6</sup> Puji meh TUHAN ti udah nagang munsuh kitai ngerusak kitai.",
			"7": "<sup>7</sup> Kitai udah rari baka burung ti lepas ari panjuk; panjuk putus, lalu kitai rari!",
			"8": "<sup>8</sup> Penyaup kitai datai ari TUHAN ke ngaga langit enggau dunya."
		},
		"125": {
			"1": "<sup>1</sup> Orang ke manggaika pengarap sida ba TUHAN nya baka Bukit Sion, ti enda ulih dikinsitka, tang meruan dia belama iya.",
			"2": "<sup>2</sup> Baka bukit ti ngelingi Jerusalem, baka nya meh TUHAN ngelingi bala nembiak Iya, ke diatu enggau ke belama iya.",
			"3": "<sup>3</sup> Laban orang ke jai enda merintah menua orang ke lurus, enggai ke orang ke lurus ngereja penyalah.",
			"4": "<sup>4</sup> TUHAN, kereja utai ti manah ngagai orang ke manah, ngagai orang ke lurus.",
			"5": "<sup>5</sup> Tang orang ke limpang lalu niti jalai sida ke jai deka diukum TUHAN sama enggau orang ke ngereja penyai! Awakka pemaik meruan di menua Israel!"
		},
		"126": {
			"1": "<sup>1</sup> Lebuh TUHAN mai kitai pulai baru ngagai Jerusalem, utai nya asai ti diambi mimpi!",
			"2": "<sup>2</sup> Bebendar amat kitai ketawa, bebendar amat kita manjung gaga ati! Udah nya bansa bukai lalu bejaku, “TUHAN udah ngereja utai ti besai ke sida.”",
			"3": "<sup>3</sup> TUHAN endang udah ngereja utai ti besai ke kitai. Nya kebuah kitai gaga!",
			"4": "<sup>4</sup> TUHAN, asuh kami nyadi kaya-raja baru, O TUHAN, baka sungai ti rangkai nyau penuh laban ai baru lebuh musin pengujan.",
			"5": "<sup>5</sup> Awakka orang ke nyabak lebuh sida nanam utai manjung gaga ati lebuh sida ngetau!",
			"6": "<sup>6</sup> Orang ke nyabak lebuh sida mai benih deka manjung gaga ati lebuh sida pulai ke rumah mai utai ti diketau sida."
		},
		"127": {
			"1": "<sup>1</sup> Enti TUHAN enda ngaga rumah, orang ke ngaga rumah deka lebu ngapa gawa. Enti TUHAN enda nyaga nengeri, orang ke jaga nengeri nya deka lebu ngapa enda tinduk nyaga nengeri nya.",
			"2": "<sup>2</sup> Nadai guna bekereja bebendar ngiga pengidup, angkat tumu pagi, lalu laun tinduk, laban TUHAN meri utai ngagai orang ke dikerinduka Iya, lebuh sida tinduk.",
			"3": "<sup>3</sup> Anak mit endang pesaka ti datai ari TUHAN. Sida endang berekat ti bendar.",
			"4": "<sup>4</sup> Anak lelaki ke ada lebuh apai sida agi biak nya baka laja ti dipegai soldadu.",
			"5": "<sup>5</sup> Lantang meh orang ke mayuh panah. Iya enda malu lebuh iya bejaku enggau munsuh ba endur alai ngatur pechara."
		},
		"128": {
			"1": "<sup>1</sup> Beberekat meh orang ke nangika TUHAN, ke ngasika adat Iya.",
			"2": "<sup>2</sup> Nuan deka makai utai ti ulih jari nuan empu; Nuan deka gaga sereta nyadi kaya-raja.",
			"3": "<sup>3</sup> Bini nuan baka pun anggur ti bebuah lebat, dalam rumah nuan, lalu bala anak nuan baka anak kayu kana, ngelingi mija nuan.",
			"4": "<sup>4</sup> Baka nya meh orang ke ngasika TUHAN deka diberi berekat.",
			"5": "<sup>5</sup> TUHAN deka merekatka nuan ari Sion! Awakka nuan meda Jerusalem nyadi kaya-raja sepemanjai umur nuan!",
			"6": "<sup>6</sup> Awakka nuan idup tepedaka bala uchu! Awakka Israel seruran diau dalam pemaik!"
		},
		"129": {
			"1": "<sup>1</sup> Israel, awakka nuan nyebut: “Sida udah balat merinsaka aku kenyau ari aku agi biak.”",
			"2": "<sup>2</sup> “Sida udah balat merinsaka aku kenyau ari aku agi biak, tang sida enda ulih ngalahka aku.",
			"3": "<sup>3</sup> Sida ngurik belakang aku; panjai amat abi urik sida.”",
			"4": "<sup>4</sup> TUHAN endang lurus; Iya udah ngelepaska aku ari jari orang ke jai.”",
			"5": "<sup>5</sup> Awakka genap iku orang ke begedika Sion malu magang, lalu diburu nyurutka diri.",
			"6": "<sup>6</sup> Awakka sida baka rumput ti ba perabung rumah, ti layu sebedau iya tumbuh.",
			"7": "<sup>7</sup> Orang nadai ngumpul iya tauka merekaska iya lalu muai iya.",
			"8": "<sup>8</sup> Lalu orang ke mansa nadai nyebut, “Awakka TUHAN merekatka nuan! Kami merekatka nuan dalam nama TUHAN.”"
		},
		"130": {
			"1": "<sup>1</sup> Aku ngangauka Nuan, O TUHAN, laban aku balat tusah.",
			"2": "<sup>2</sup> Dingaka sabak aku, O TUHAN. Pendingka kangau aku ke minta tulung!",
			"3": "<sup>3</sup> Enti Nuan, O TUHAN nuliska dosa kami, sapa tan?",
			"4": "<sup>4</sup> Tang Nuan ngampun, ngambika kami nangika Nuan.",
			"5": "<sup>5</sup> Aku nganti tulung ari TUHAN, lalu aku manggaika pengadang aku ba semaya Iya.",
			"6": "<sup>6</sup> Aku nganti TUHAN lebih ari jejaga ke nganti hari tawas, lebih ari jejaga ke nganti hari tawas.",
			"7": "<sup>7</sup> Israel, panggaika pengadang kita ba TUHAN, laban pengerindu Iya endang meruan belama iya lalu Iya seruran nebus.",
			"8": "<sup>8</sup> Lalu Iya deka nebus Israel ari semua penyai sida."
		},
		"131": {
			"1": "<sup>1</sup> O TUHAN, aku enda sumbung; aku enda tinggi ati; Aku enda ngiruh diri enggau utai ti kelalu besai tauka kelalu manah ke aku.",
			"2": "<sup>2</sup> Tang aku bejaku ngagai ati aku, “Duduk, lalu diau jenuh baka anak mit ti enda bemunyi tingkap ba dada indai iya lebuh iya udah kenyang nusu.” Baka nya meh ati aku diau jenuh.",
			"3": "<sup>3</sup> O Israel, panggaika pengadang kita ba TUHAN kenyau ari diatu enggau ke belama iya!"
		},
		"132": {
			"1": "<sup>1</sup> O TUHAN, anang enda ingatka David, semua pemerinsa ti ditatka iya.",
			"2": "<sup>2</sup> O TUHAN, kingatka utai ti udah disemaya iya, semaya ti udah digaga iya enggau Nuan, Allah Taala ti Pemadu Bekuasa, ke disembah Jakop:",
			"3": "<sup>3</sup> “Aku enggai pulai ke rumah, tauka tama ngagai bilik tinduk.",
			"4": "<sup>4</sup> Aku enggai tinduk tauka belelak,",
			"5": "<sup>5</sup> sebedau aku nyendiaka endur alai TUHAN, rumah alai Allah Taala ti Pemadu Bekuasa, ke disembah Jakop, diau.”",
			"6": "<sup>6</sup> Kami ninga pasal Peti Lampang Batu Adat di Betlehem, lalu kami nemu Peti nya ba padang Jearim.",
			"7": "<sup>7</sup> Ku kami bejaku, “Aram kitai mansang ngagai rumah TUHAN. Aram kitai nyembah Iya di mua kerusi diraja Iya.”",
			"8": "<sup>8</sup> Angkat meh Nuan O TUHAN, lalu mansang ngagai rumah Nuan, sama enggau Peti Lampang Batu Adat, ti ngelambangka kuasa Nuan.",
			"9": "<sup>9</sup> Awakka bala imam Nuan seruran ngerasukka pengelurus, lalu awakka bala nembiak Nuan manjung gaga ati!",
			"10": "<sup>10</sup> Ketegal nembiak Nuan David, anang nulak raja ti udah dipilih Nuan.",
			"11": "<sup>11</sup> Nuan udah besemaya ngena sumpah enggau David, semaya ti enda tau dibatak Nuan pulai: “Aku deka ngasuh siku ari peturun nuan ti lelaki duduk ba kerusi diraja nuan.”",
			"12": "<sup>12</sup> Enti bala peturun nuan ti lelaki ngasika sempekat enggau adat ti deka diajar Aku ngagai sida, peturun sida ke lelaki deka mega duduk ba kerusi diraja nuan belama iya.”",
			"13": "<sup>13</sup> Laban TUHAN udah milih Sion; Iya deka ngaga Sion nyadi endur alai Iya diau.",
			"14": "<sup>14</sup> “Tu meh endur alai Aku deka diau belama iya. Tu meh endur alai Aku diau, laban Aku deka merintah ba endur tu.",
			"15": "<sup>15</sup> Aku deka meri semua utai ti dipeguna Sion. Aku deka meri pemakai ti chukup ngagai nembiak iya ke seranta.",
			"16": "<sup>16</sup> Bala imam iya deka ngayanka pengelepas ari Aku, lalu bala nembiak iya deka belagu sereta manjung gaga ati.",
			"17": "<sup>17</sup> Ditu Aku deka ngasuh peturun David nyadi raja ti besai kuasa, baka lampu ti menyadi belama iya.",
			"18": "<sup>18</sup> Aku deka ngasuh bala munsuh iya malu, tang perintah iya deka nyadi kaya-raja sereta mansang.”"
		},
		"133": {
			"1": "<sup>1</sup> Naka meh pemanah, naka meh penyamai lebuh nembiak Allah Taala diau seati enggau pangan diri!",
			"2": "<sup>2</sup> Iya baka minyak ti berega ti dituang ba pala Aron lalu lilih nelusur ragum iya ngagai rekung baju panjai iya.",
			"3": "<sup>3</sup> Iya baka ambun Bukit Hermon ti labuh ba bukit di Sion. Nya meh endur alai TUHAN udah besemaya deka meri berekat Iya, iya nya pengidup ti meruan belama iya."
		},
		"134": {
			"1": "<sup>1</sup> Aram muji TUHAN, semua kita ke nembiak Iya, semua kita ke bekereja lebuh malam hari dalam Rumah Sembiang Iya.",
			"2": "<sup>2</sup> Angkatka jari kita lebuh kita besampi dalam Rumah Sembiang, lalu puji TUHAN!",
			"3": "<sup>3</sup> Awakka TUHAN, ke ngaga langit enggau dunya, merekatka kita ari Sion!"
		},
		"135": {
			"1": "<sup>1</sup> Puji TUHAN! Puji meh nama Iya, puji TUHAN, kita ke nembiak Iya,",
			"2": "<sup>2</sup> ke bediri dalam Rumah TUHAN, dalam Rumah Sembiang Allah Taala kitai.",
			"3": "<sup>3</sup> Puji meh TUHAN, laban Iya endang badas. Belagu pantun puji ke nama Iya, laban Iya endang nelap.",
			"4": "<sup>4</sup> Iya milih Jakop ke Iya Empu, lalu orang Israel nyadika pesaka Iya.",
			"5": "<sup>5</sup> Laban aku nemu TUHAN endang besai, ari atas semua petara.",
			"6": "<sup>6</sup> Iya ngereja sebarang utai ti dikedekaka Iya di langit enggau di dunya, di tasik enggau di tasik ti dalam.",
			"7": "<sup>7</sup> Iya ngasuh kabut betiup ari tisi langit. Iya ngaga kilat lebuh hari ujan, lalu Iya mansutka ribut ari bilik setor Iya.",
			"8": "<sup>8</sup> Iya udah munuh semua anak tuai di menua Ejip, anak tuai mensia enggau jelu.",
			"9": "<sup>9</sup> Di menua Ejip Iya ngereja kereja ajih enggau kereja ti nyelai kena Iya ngukum raja enggau semua pemesai iya.",
			"10": "<sup>10</sup> Iya ngerusak mayuh bengkah bansa lalu munuh mayuh raja ke bekuasa.",
			"11": "<sup>11</sup> Sihon, raja orang Amor, enggau Og, raja menua Basan, enggau semua raja menua Kanaan.",
			"12": "<sup>12</sup> Iya meri menua sida nyadika pesaka nembiak Iya Israel.",
			"13": "<sup>13</sup> Nama Nuan, O TUHAN, deka meruan belama iya, Semua serak deka ingatka Nuan.",
			"14": "<sup>14</sup> Laban TUHAN deka nyaga nembiak Iya, lalu siru enggau sida.",
			"15": "<sup>15</sup> Petara orang bansa bukai digaga ari pirak enggau emas, ditempa jari mensia.",
			"16": "<sup>16</sup> Sida bisi lubang nyawa, tang enda ulih bejaku, bisi mata, tang enda ulih meda,",
			"17": "<sup>17</sup> bisi pending, tang enda ulih ninga, lalu sida pen enda mega nemu enseput.",
			"18": "<sup>18</sup> Awakka semua orang ke ngaga lalu arapka engkeramba nya nyadi baka engkeramba nya empu!",
			"19": "<sup>19</sup> Puji meh TUHAN, kita orang Israel. Puji meh Iya, kita peturun Aron!",
			"20": "<sup>20</sup> Puji meh TUHAN, kita orang Lewi. Puji meh TUHAN, semua kita ke nangika Iya!",
			"21": "<sup>21</sup> Puji meh TUHAN di Sion, Iya ti diau di Jerusalem! Puji TUHAN!"
		},
		"136": {
			"1": "<sup>1</sup> Beri terima kasih ngagai TUHAN, laban Iya endang badas. laban pengerindu Iya meruan belama iya.",
			"2": "<sup>2</sup> Beri terima kasih ngagai Allah Taala ti Pemadu Tinggi, laban pengerindu Iya meruan belama iya.",
			"3": "<sup>3</sup> Beri terima kasih ngagai TUHAN ti besai agi ari semua tuhan, laban pengerindu Iya meruan belama iya.",
			"4": "<sup>4</sup> Semina Iya siku aja ti ngereja kereja ajih. Pengerindu Iya meruan belama iya.",
			"5": "<sup>5</sup> Ngena penemu Iya, Iya ngaga langit, laban pengerindu Iya meruan belama iya.",
			"6": "<sup>6</sup> Iya ngaga dunya atas ai ti dalam, laban pengerindu Iya meruan belama iya.",
			"7": "<sup>7</sup> Iya ngaga mata hari enggau bulan, laban pengerindu Iya meruan belama iya.",
			"8": "<sup>8</sup> Iya ngaga mata hari ti manchar lebuh siang hari, laban pengerindu Iya meruan belama iya.",
			"9": "<sup>9</sup> Iya ngaga bulan enggau bintang ti manchar lebuh malam hari, laban pengerindu Iya meruan belama iya.",
			"10": "<sup>10</sup> Iya udah munuh anak tuai ti lelaki orang Ejip, laban pengerindu Iya meruan belama iya.",
			"11": "<sup>11</sup> Iya udah nguluka orang Israel pansut ari menua Ejip, laban pengerindu Iya meruan belama iya.",
			"12": "<sup>12</sup> Ngena jari Iya ti kering, lengan Iya ti bekuasa, laban pengerindu Iya meruan belama iya.",
			"13": "<sup>13</sup> Iya ngasuh ai bebagi ba Tasik Mirah, laban pengerindu Iya meruan belama iya.",
			"14": "<sup>14</sup> Iya mai bala nembiak Iya bejalai nengah tasik nya, laban pengerindu Iya meruan belama iya.",
			"15": "<sup>15</sup> Tang Iya ngelemaska raja enggau bala soldadu menua Ejip, laban pengerindu Iya meruan belama iya.",
			"16": "<sup>16</sup> Iya nguluka bala nembiak Iya bejalai nengah menua puang, laban pengerindu Iya meruan belama iya.",
			"17": "<sup>17</sup> Iya munuh bala raja ti bekuasa, laban pengerindu Iya meruan belama iya.",
			"18": "<sup>18</sup> Iya munuh bala raja ti tebilang nama, laban pengerindu Iya meruan belama iya.",
			"19": "<sup>19</sup> Iya munuh Sihon, raja orang Amor, laban pengerindu Iya meruan belama iya.",
			"20": "<sup>20</sup> enggau Og, raja nengeri Basan, laban pengerindu Iya meruan belama iya.",
			"21": "<sup>21</sup> Iya meri menua sida ngagai nembiak Iya, laban pengerindu Iya meruan belama iya.",
			"22": "<sup>22</sup> Iya meri menua nya ngagai orang Israel, ti nembiak Iya, laban pengerindu Iya meruan belama iya.",
			"23": "<sup>23</sup> Iya nadai kala enda ingatka kitai lebuh kitai alah, laban pengerindu Iya meruan belama iya.",
			"24": "<sup>24</sup> Iya ngelepaska kitai ari munsuh kitai, laban pengerindu Iya meruan belama iya.",
			"25": "<sup>25</sup> Iya meri pemakai ngagai semua utai ti idup, laban pengerindu Iya meruan belama iya.",
			"26": "<sup>26</sup> Beri terima kasih ngagai Allah Taala di serega, laban pengerindu Iya meruan belama iya."
		},
		"137": {
			"1": "<sup>1</sup> Kami duduk ba tebing sungai di menua Babilon. Dia kami nyabak, lebuh kami ingatka Sion.",
			"2": "<sup>2</sup> Ba kayu willow dia kami ngantung belikan kami.",
			"3": "<sup>3</sup> Laban orang ke nangkap kami ngasuh kami belagu, lalu orang ke merinsaka kami ngasuh kami merindang sida, ku sida, “Nyanyi ke kami lagu nengeri Sion.”",
			"4": "<sup>4</sup> Baka ni ku kami ulih nyanyi lagu TUHAN di menua orang?",
			"5": "<sup>5</sup> Enti aku enda ingatka nuan, O Jerusalem, awakka jari kanan aku lupis!",
			"6": "<sup>6</sup> Awakka aku enda ulih belagu agi enti aku enda ingatka nuan, enti aku enda ngumbai nuan nengeri ti pemadu dikegagaka aku!",
			"7": "<sup>7</sup> O TUHAN, kingatka utai ti udah dikereja orang Edom lebuh Jerusalem alah. Kingatka sida ke majak nyebut, “Runtuhka nengeri nya!”",
			"8": "<sup>8</sup> Babilon, nuan deka dirusak. Beberekat meh orang ke malas nuan ketegal utai ti udah dikereja nuan ngagai kami,",
			"9": "<sup>9</sup> ngambi anak nuan, lalu mangkaka sida ngagai batu."
		},
		"138": {
			"1": "<sup>1</sup> Aku meri terima kasih ngagai Nuan, O TUHAN, enggau pengabis ati aku. Aku belagu muji Nuan di mua bala petara.",
			"2": "<sup>2</sup> Aku meremi ngadap ngagai Rumah Nuan ti kudus, lalu meri terima kasih ngagai nama Nuan ketegal pengerindu enggau penetap ati Nuan ti meruan belama iya, laban Nuan udah mandangka Diri enggau pesan Nuan endang tinggi ari semua.",
			"3": "<sup>3</sup> Nuan nyaut aku, lebuh aku ngangauka Nuan. Ngena pengering Nuan, Nuan ngeringka aku.",
			"4": "<sup>4</sup> Semua raja di dunya deka muji Nuan, O TUHAN, laban sida udah ninga semaya Nuan,",
			"5": "<sup>5</sup> lalu sida deka belagu ngenang utai ti dikereja Nuan, laban mulia Nuan amat besai.",
			"6": "<sup>6</sup> Laban taja pen Nuan diau tinggi di atas, tang Nuan ngintu orang ke baruh pendiau, lalu orang ke sumbung enda ulih belalai ari Nuan.",
			"7": "<sup>7</sup> Taja pen aku dikelingi penusah, Nuan nyaga nyawa aku; Nuan ngunjurka jari Nuan ngelaban bala munsuh aku lalu jari kanan Nuan ngelepaska aku.",
			"8": "<sup>8</sup> Nuan deka ngamatka semua utai ti udah disemaya Nuan ngagai aku; Pengerindu Nuan endang meruan belama iya, O TUHAN. Anang ninggalka nembiak Nuan Israel."
		},
		"139": {
			"1": "<sup>1</sup> O TUHAN, Nuan udah meresa lalu nemu aku.",
			"2": "<sup>2</sup> Nuan nemu semua utai ti dikereja aku; Ari jauh Nuan meda semua runding aku.",
			"3": "<sup>3</sup> Nuan meda aku lebuh aku gawa tauka belelak, lalu Nuan nemu semua utai ti dikereja aku.",
			"4": "<sup>4</sup> Indah sebedau aku bejaku deh Nuan endang udah nemu semua utai ti deka disebut aku.",
			"5": "<sup>5</sup> Nuan endang ngelingi aku, lalu Nuan nyaga aku ngena kuasa Nuan.",
			"6": "<sup>6</sup> Penemu Nuan endang amat dalam, ngelui ari ti ulih diperagam aku.",
			"7": "<sup>7</sup> Kini ke tunga aku ulih ngerarika diri ari Nuan? Ngagai ni aku ulih ngerarika diri ari Nuan?",
			"8": "<sup>8</sup> Enti aku niki ke serega, Nuan ga bisi dia. Enti aku gali di dunya pemati, Nuan ga bisi dia.",
			"9": "<sup>9</sup> Enti aku terebai lalu diau ba endur ti pemadu jauh,",
			"10": "<sup>10</sup> Nuan ga bisi dia nguluka aku, lalu jari kanan Nuan nulung aku.",
			"11": "<sup>11</sup> Enti aku nyebut, “Awakka pemetang ngelamun aku, lalu awakka penampak ti ngelingi aku bebali nyadi malam,”",
			"12": "<sup>12</sup> indah pemetang nya deh enda chukup petang ke Nuan, lalu malam terang baka penampak siang hari; laban ba Nuan pemetang enggau penampak sigi nadai bida.",
			"13": "<sup>13</sup> Laban Nuan udah nempa semua perengka tubuh aku; lalu bejantilka aku dalam kandung indai aku.",
			"14": "<sup>14</sup> Aku muji Nuan laban Nuan endang patut dikenangika, laban semua utai ti dikereja Nuan endang nyelai sereta manah magang. Nuan sigi nemu aku.",
			"15": "<sup>15</sup> Lebuh tulang aku benung ditempa, dipejantilka bejimat jimat dalam kandung indai aku, lebuh aku benung mansang besai dalam kandung enda dipeda mensia, Nuan nemu aku endang bisi dia.",
			"16": "<sup>16</sup> Nuan endang udah meda aku sebedau aku ada. Umur ti diungkup Nuan ke aku endang udah ditulis dalam bup Nuan, sebedau utai nya nyadi.",
			"17": "<sup>17</sup> O Allah Taala, naka meh pemayuh runding Nuan datai ke aku nyau enda teperagamka sida.",
			"18": "<sup>18</sup> Enti aku ngitung sida, sida mayuh agi ari igi pasir. Lebuh aku dani, aku agi mengkang enggau Nuan.",
			"19": "<sup>19</sup> O Allah Taala, naka meh pemanah enti Nuan munuh orang ke jai! Naka meh pemanah enti orang ke mangah nya jauh ari aku!",
			"20": "<sup>20</sup> Sida nyakap Nuan. Sida bejaku jai ngelaban nama Nuan.",
			"21": "<sup>21</sup> Naka meh pemalat aku begedika orang ke begedika Nuan, O TUHAN! Naka meh pemalat aku pedis ati ke orang ke angkat ngelaban Nuan!",
			"22": "<sup>22</sup> Aku begedika sida enggau pengabis ati aku. Aku ngumbai sida munsuh aku.",
			"23": "<sup>23</sup> Peresa meh aku, O Allah Taala, ngambika Nuan nemu ati aku. Uji meh ati aku, ngambika Nuan nemu runding aku.",
			"24": "<sup>24</sup> Giga meh ulah ti jai dalam aku lalu iring aku niti jalai ti meruan belama iya."
		},
		"140": {
			"1": "<sup>1</sup> Selamatka aku ari orang ke ngereja penyai, O TUHAN. Jaga aku ari orang ke mangah,",
			"2": "<sup>2</sup> ke seruran bekira deka ngereja penyai, lalu enda ngetu-ngetu mai orang belaya.",
			"3": "<sup>3</sup> Dilah sida baka ular ti bisa. Leka jaku sida baka rachun ular tedung.",
			"4": "<sup>4</sup> Jaga meh aku, TUHAN, ari kuasa orang ke jai. Jaga aku ari orang ke mangah ke bekira deka numbangka aku.",
			"5": "<sup>5</sup> Orang ke sumbung udah nan panjuk deka nyintak aku. Sida udah nan jarin, lalu ba sebelah tisi jalai, sida udah nan panjuk deka nangkap aku.",
			"6": "<sup>6</sup> Ku aku bejaku enggau TUHAN, “Nuan endang Allah Taala aku.” Dingaka aku ke ngangau minta tulung Nuan, O TUHAN!",
			"7": "<sup>7</sup> TUHAN ti Pemadu Tinggi, penyaga aku ti kering, Nuan udah nyaga aku lebuh aku beperang.",
			"8": "<sup>8</sup> TUHAN, anang meri ngagai orang ke jai utai ti dikinginka sida. Anang nejuka akal sida mujur.",
			"9": "<sup>9</sup> Anang nejuka bala munsuh aku menang. Asuh jaku emba sida, pulai ngagai sida empu.",
			"10": "<sup>10</sup> Awakka bara api ninggang sida. Awakka sida dibuai ngagai lubang kebung, lalu enda pansut agi.",
			"11": "<sup>11</sup> Awakka orang enda mujur betenahka pangan diri. Awakka penyai nitihka orang ke mangah lalu ngerusak sida.",
			"12": "<sup>12</sup> TUHAN, aku nemu Nuan endang nyaga orang ke seranta enggau nyaga hak orang ke suntuk.",
			"13": "<sup>13</sup> Orang ke lurus deka muji Nuan. Sida deka diau begulai enggau Nuan."
		},
		"141": {
			"1": "<sup>1</sup> Aku ngangauka Nuan, O TUHAN. Tulung meh aku diatu! Pendingka aku, lebuh aku ngangauka Nuan.",
			"2": "<sup>2</sup> Sambut meh sampi aku nyadika menyan, jari ti diangkatka aku, nyadika piring lemai hari.",
			"3": "<sup>3</sup> O TUHAN, jaga mulut aku, lebuh aku bejaku.",
			"4": "<sup>4</sup> Anang nejuka aku ingin ngereja utai ti salah enggau ingin deka nyereta orang ke jai ngereja pengawa sida ke jai. Awakka aku enda enggau sida ngintu pengerami makai besai.",
			"5": "<sup>5</sup> Orang ke badas nyangka bisi nganu sereta ngerara aku enggau ati ti nelap, tang aku enggai sekali-kali nerima basa ari orang ke jai, laban aku endang seruran besampi ngelaban pengawa sida ke jai.",
			"6": "<sup>6</sup> Lebuh orang ke merintah sida ditikauka ari pala tingkah, nya baru sida deka ngaku jaku aku endang amat.",
			"7": "<sup>7</sup> Baka kayu ti dibelah lalu dipepat, baka nya meh tulang sida deka ngachir ba mua kubur.",
			"8": "<sup>8</sup> Tang aku majak arapka Nuan, O TUHAN Allah Taala aku. Aku minta Nuan nyaga aku. Anang nejuka aku mati!",
			"9": "<sup>9</sup> Jaga aku ari panjuk ti udah ditan kena sida nyintak aku, ari jarin orang ke jai.",
			"10": "<sup>10</sup> Awakka orang ke jai disintak panjuk sida empu, lalu awakka aku enda bebadi."
		},
		"142": {
			"1": "<sup>1</sup> Aku ngangauka TUHAN minta tulung. Aku minta Iya nulung aku.",
			"2": "<sup>2</sup> Aku mantaika semua serebana aku ngagai Iya. Aku madahka semua penusah aku ngagai Iya.",
			"3": "<sup>3</sup> Lebuh aku nyau lemi semengat, Iya nemu utai ti patut dikereja aku. Ba jalai ti ditengah aku, bala munsuh aku udah nan panjuk kena sida nyintak aku.",
			"4": "<sup>4</sup> Lebuh aku malik kiba kanan aku nadai meda orang deka nulung aku, nadai orang deka nyaga aku. nadai orang deka ibuhka aku.",
			"5": "<sup>5</sup> O TUHAN, aku nyabak minta Nuan nulung aku. Nuan, O TUHAN, endang penyaga aku. Semina Nuan siku aja ke dikinginka aku dalam pengidup tu.",
			"6": "<sup>6</sup> Pendingka sabak aku ke minta tulung, laban ati aku balat tusah. Selamatka aku ari bala munsuh aku, laban sida kelalu kering ke aku.",
			"7": "<sup>7</sup> Selamatka aku ari penusah tu, nya baru aku deka meri terima kasih ngagai Nuan! Orang ke lurus deka begempuru ngelingi aku, laban pemadas Nuan enggau aku."
		},
		"143": {
			"1": "<sup>1</sup> O TUHAN, dingaka sampi aku! Laban Nuan endang lurus, nya alai pendingka kangau aku. Ulih penetap ati Nuan, saut meh aku!",
			"2": "<sup>2</sup> Anang nyara aku ke nembiak Nuan; laban nadai orang ke enda bepenyalah di mua Nuan.",
			"3": "<sup>3</sup> Bala munsuh aku udah begagaika aku lalu ngalahka aku. Munsuh aku udah nutup aku dalam rumah jil ti petang, lalu aku nyau baka orang ke udah mati dulu kelia.",
			"4": "<sup>4</sup> Nya alai aku lemi semengat. Ati aku balat tusah.",
			"5": "<sup>5</sup> Aku ingatka maya dulu kelia. Aku berundingka semua utai ti udah dikereja Nuan, aku ngingatka semua pengawa Nuan.",
			"6": "<sup>6</sup> Aku ngangkatka jari besampi ngagai Nuan. Baka tanah ti rangkai, baka nya meh semengat aku aus ke Nuan.",
			"7": "<sup>7</sup> Saut meh aku diatu, O TUHAN! Aku nadai pengadang agi. Anang belalai ari aku, enggai ke aku enggau orang nurun ngagai dunya pemati.",
			"8": "<sup>8</sup> Asuh aku genap pagi ngingatka pengerindu Nuan ti meruan belama iya, laban aku manggaika pengarap aku ba Nuan. Aku besampi ngagai Nuan. Tunjukka jalai ti patut ditengah aku.",
			"9": "<sup>9</sup> O TUHAN, aku datai ngagai Nuan, minta Nuan nyaga aku. Selamatka aku ari bala munsuh aku.",
			"10": "<sup>10</sup> Nuan endang Allah Taala aku. Ajar aku ngereja utai ti dikedekaka Nuan. Aku minta Nuan manah enggau aku, lalu ngiring aku nengah jalai ti likun.",
			"11": "<sup>11</sup> Selamatka aku, O TUHAN, munyi ti udah disemaya Nuan. Ulih pemadas Nuan, selamatka aku ari penusah aku!",
			"12": "<sup>12</sup> Ketegal pengerindu Nuan ke aku, bunuh meh bala munsuh aku lalu rusak meh semua orang ke nekanka pengidup aku, laban aku tu nembiak Nuan."
		},
		"144": {
			"1": "<sup>1</sup> Pujika TUHAN ti nyaga aku! Iya matihka aku ngelaban munsuh lalu nyendiaka aku beperang.",
			"2": "<sup>2</sup> Iya nyadi penyaga enggau pengelepas aku, ti dikarapka aku meri aku pengelikun. Iya ngalahka semua bansa lalu ngasuh aku merintah sida.",
			"3": "<sup>3</sup> O TUHAN, sapa mensia alai Nuan deka ibuhka sida? Sapa anak mensia, alai Nuan deka ibuhka sida?",
			"4": "<sup>4</sup> Kami tu baka kesuh seput. Umur kami semina sekejap aja, baka ayuk.",
			"5": "<sup>5</sup> O TUHAN, buka meh langit, lalu nurun. Tegu meh bukit, lalu sida deka berasap.",
			"6": "<sup>6</sup> Asuh kilat nyelar lalu mambarka bala munsuh Nuan. Timbakka laja Nuan, lalu asuh munsuh Nuan rari.",
			"7": "<sup>7</sup> Ulurka jari Nuan ari serega, tarit aku ari ai ti dalam, lalu selamatka aku. Selamatka aku ari kuasa orang bansa bukai,",
			"8": "<sup>8</sup> ti nadai kala madahka utai ti bendar tang bula, lalu ngelanggar sumpah diri.",
			"9": "<sup>9</sup> Aku deka nyanyi lagu ti baru ke Nuan, O Allah Taala. Aku deka nebah belikan, lalu belagu ke Nuan.",
			"10": "<sup>10</sup> Nuan ngasuh bala raja menang lalu ngelepaska nembiak Nuan David.",
			"11": "<sup>11</sup> Selamatka aku ari bala munsuh ke mangah. Selamatka aku ari kuasa orang bansa bukai, ti nadai kala madahka utai ti bendar tang bula lalu ngelanggar sumpah diri.",
			"12": "<sup>12</sup> Awakka bala anak lelaki kami, lebuh sida agi biak nyadi baka utai ti ditanam, ti mansang besai lalu nyadi kering. Awakka bala anak indu kami nyadi baka tiang ti manah gaya ti dikena ngias sukut istana.",
			"13": "<sup>13</sup> Awakka rumah setor kami penuh laban genap macham utai ti udah diketau. Awakka jelu bedus ba tanah kami beranak beribu iku.",
			"14": "<sup>14</sup> Awakka sapi kami mayuh anak nadai gugur anak, tauka lenyau. Awakka nadai sabak tusah ati didinga ba sebelah jalai raya kami.",
			"15": "<sup>15</sup> Lantang meh bansa ke nerima berekat nya. Lantang meh mensia ti ngaku Allah Taala nyadika TUHAN sida!"
		},
		"145": {
			"1": "<sup>1</sup> Aku deka ngemesaika Nuan, O Allah Taala enggau Raja aku. Aku deka meri terima kasih ngagai Nuan belama lama iya.",
			"2": "<sup>2</sup> Tiap hari aku deka meri terima kasih ngagai Nuan, lalu muji nama Nuan belama lama iya.",
			"3": "<sup>3</sup> TUHAN endang besai lalu endang patut dipuji. Pemesai kuasa Iya enda ulih diperagam mensia.",
			"4": "<sup>4</sup> Utai ti udah dikereja Nuan deka dipuji genap turun-menurun. Sida deka madahka pasal pemesai pengawa Nuan.",
			"5": "<sup>5</sup> Sida deka bejakuka pasal mulia enggau pemesai kuasa Nuan, lalu aku deka belematika pemadas pengawa Nuan.",
			"6": "<sup>6</sup> Orang deka ngenang pasal pemesai pengawa Nuan, lalu aku deka ngemesaika Nuan.",
			"7": "<sup>7</sup> Sida deka nusui pasal semua pemadas Nuan lalu belaguka pasal penelap Nuan.",
			"8": "<sup>8</sup> TUHAN endang rindu sereta kasih, lubah ringat sereta penuh laban pengerindu ti meruan belama iya.",
			"9": "<sup>9</sup> Iya manah enggau semua orang lalu siru enggau semua utai ti udah digaga Iya.",
			"10": "<sup>10</sup> Semua utai ti idup ti udah digaga Nuan, O TUHAN, deka meri terima kasih ngagai Nuan, lalu semua nembiak Nuan deka muji Nuan.",
			"11": "<sup>11</sup> Sida deka bejakuka pasal mulia diraja Nuan lalu madahka pasal kuasa Nuan,",
			"12": "<sup>12</sup> ngambika semua orang nemu pemesai pengawa Nuan enggau pemesai mulia perintah Nuan.",
			"13": "<sup>13</sup> Perintah Nuan endang meruan belama iya, lalu Nuan nyadi Raja belama iya. TUHAN endang deka ngamatka semaya Iya. Iya endang ngayanka pengasih dalam semua pengawa Iya.",
			"14": "<sup>14</sup> Iya nulung orang ke dituntung penusah. Iya ngangkatka orang ke udah rebah.",
			"15": "<sup>15</sup> Semua utai ti idup aja manggaika pengadang sida ba Nuan, lalu Nuan meri sida pemakai ba maya ti patut.",
			"16": "<sup>16</sup> Nuan meri sida chukup utai datai ke semua utai ti idup nadai suntuk utai.",
			"17": "<sup>17</sup> TUHAN endang lurus dalam semua utai ti dikereja Iya, ngayanka pengasih dalam semua pengawa Iya.",
			"18": "<sup>18</sup> Iya diau semak sida ke ngangauka Iya, ke ngangauka Iya enggau ati ti bendar.",
			"19": "<sup>19</sup> Iya meri utai ti dipeguna orang ke ngemesaika Iya. Iya ninga sabak sida, lalu nyelamatka sida.",
			"20": "<sup>20</sup> Iya nyaga semua orang ke rinduka Iya, tang Iya deka ngerusak orang ke jai.",
			"21": "<sup>21</sup> Aku deka seruran muji TUHAN. Awakka semua utai ti idup ti udah digaga Iya muji nama Iya ti kudus belama lama iya."
		},
		"146": {
			"1": "<sup>1</sup> Puji TUHAN! Puji meh TUHAN, O semengat aku!",
			"2": "<sup>2</sup> Aku deka muji Iya seumur idup. Aku deka belagu muji Allah Taala aku sepemanjai umur aku.",
			"3": "<sup>3</sup> Anang ngarapka tuai mensia. Nadai mensia ulih nulung kita.",
			"4": "<sup>4</sup> lebuh sida mati, sida pulai baru ngagai tanah. Nya meh maya semua pelan sida lesap.",
			"5": "<sup>5</sup> Beberekat meh orang ke ditulung Allah Taala Jakop ke manggaika diri ba TUHAN Allah Taala sida,",
			"6": "<sup>6</sup> ke ngaga langit, tanah enggau tasik enggau semua utai ti dalam sida. Iya endang seruran ngamatka semaya Iya.",
			"7": "<sup>7</sup> Lebuh Iya ngakim Iya ngeringka orang ke kena tekan, lalu meri pemakai ngagai orang ke lapar. TUHAN ngelepaska orang ke kena jil",
			"8": "<sup>8</sup> lalu ngasuh orang ke buta meda. Iya ngangkatka orang ke udah rebah. Iya rinduka bala nembiak Iya ke lurus.",
			"9": "<sup>9</sup> Iya nyaga orang kampar ti diau di menua kitai. Iya nulung indu ke balu enggau orang ke nadai apai indai, tang ngerusak orang ke jai.",
			"10": "<sup>10</sup> TUHAN endang Raja belama iya. O Sion, Allah Taala nuan deka merintah belama iya. Puji TUHAN!"
		},
		"147": {
			"1": "<sup>1</sup> Puji TUHAN! Laban kitai endang patut belagu muji Allah Taala kitai; laban Iya endang kasih, lalu kitai endang patut belagu muji Iya.",
			"2": "<sup>2</sup> TUHAN nirika Jerusalem baru. Iya mai orang ke udah dibuai pulai baru ari menua bukai.",
			"3": "<sup>3</sup> Iya ngeraika orang ke tusah ati lalu malut telih sida.",
			"4": "<sup>4</sup> Iya ngeletak pemayuh bintang, lalu meri nama ngagai tiap igi bintang nya.",
			"5": "<sup>5</sup> TUHAN kitai endang besai sereta bekuasa. Penalam penemu Iya endang enda ulih disukat.",
			"6": "<sup>6</sup> Iya ngangkatka orang ke baruh ati, tang ngempaska orang ke jai ngagai tanah.",
			"7": "<sup>7</sup> Belagu muji TUHAN. Tebah meh belikan ke Allah Taala kitai.",
			"8": "<sup>8</sup> Iya nganchau mua hari di langit. Iya meri ai ujan ngagai tanah lalu ngasuh rumput tumbuh ba sebelah bukit.",
			"9": "<sup>9</sup> Iya meri pemakai ngagai jelu lalu nupi burung kak ti agi mit lebuh sida ngangau.",
			"10": "<sup>10</sup> TUHAN ukai gaga ati ke kuda ti kering, tauka rinduka soldadu ti berani,",
			"11": "<sup>11</sup> tang rinduka orang ke ngemesaika Iya, orang ke manggaika diri ba pengerindu Iya ti meruan belama iya.",
			"12": "<sup>12</sup> Puji meh TUHAN, O Jerusalem! Puji meh Allah Taala kitai, O Sion!",
			"13": "<sup>13</sup> Iya ngasuh semua pintu nuan tegap. Iya merekatka bala nembiak Nuan.",
			"14": "<sup>14</sup> Iya nyaga garis entara menua nuan enggau likun lalu ngasuh kita kenyang makai gandum ti pemadu manah.",
			"15": "<sup>15</sup> Iya bejaku ngagai tanah, lalu utai ti disebut Iya nyadi enggau jampat.",
			"16": "<sup>16</sup> Iya nganchau seleju baka pua lalu naburka ais baka amau.",
			"17": "<sup>17</sup> Iya ngasuh ujan batu labuh baka batu kerangan. Nadai orang ulih natka penyelap ti dikirumka Iya!",
			"18": "<sup>18</sup> Udah nya Iya bejaku, lalu batu ais nya lela nyadi ai. Iya lalu ngasuh ribut datai, lalu ai nya rarat.",
			"19": "<sup>19</sup> Iya meri pesan ngagai bala nembiak Iya, atur enggau Adat Iya ngagai orang Israel.",
			"20": "<sup>20</sup> Iya nadai kala ngereja utai nya ke semua bansa bukai. Sida enda nemu Adat Iya. Puji TUHAN!"
		},
		"148": {
			"1": "<sup>1</sup> Puji TUHAN! Puji meh TUHAN ti datai ari serega, puji meh Iya ba endur ti pemadu tinggi.",
			"2": "<sup>2</sup> Puji meh Iya, semua kita ke melikat Iya, semua kita ke bala soldadu serega.",
			"3": "<sup>3</sup> Puji meh Iya, O kita mata hari enggau bulan. Puji meh Iya, O kita bintang ti manchar.",
			"4": "<sup>4</sup> Puji meh Iya, O langit ti pemadu tinggi, enggau kita ai ti ari atas langit.",
			"5": "<sup>5</sup> Awakka semua sida muji nama TUHAN! Laban lebuh Iya bejaku, sida lalu digaga.",
			"6": "<sup>6</sup> Lalu Iya ngengkahka sida ba endur alai sida ke meruan belama iya; Iya ngengkahka sida ba endur sida ngambika sida meruan dia belama iya.",
			"7": "<sup>7</sup> Puji meh TUHAN O kita ke diau di dunya, bala kita naga tasik, enggau semua tasik,",
			"8": "<sup>8</sup> kilat enggau ujan batu, seleju enggau mua hari, ribut ti deras ti ngasika pesan Iya.",
			"9": "<sup>9</sup> Puji meh Iya, O kita munggu enggau bukit, kayu buah enggau utan,",
			"10": "<sup>10</sup> semua jelu ti jinak enggau jelu babas, kita ular enggau burung.",
			"11": "<sup>11</sup> Puji meh Iya, O kita bala raja enggau semua mensia, bala anak raja enggau semua pemerintah ti bukai,",
			"12": "<sup>12</sup> indu ke biak, enggau orang bujang, orang ke tuai enggau bala anak mit.",
			"13": "<sup>13</sup> Awakka semua sida muji nama TUHAN, laban semina nama Iya aja dikemesaika; laban mulia Iya ari atas dunya enggau serega.",
			"14": "<sup>14</sup> Iya meri pengering ngagai nembiak Iya, ngambika sida muji Iya, iya nya orang Israel ke balat dikesayauka Iya. Puji TUHAN!"
		},
		"149": {
			"1": "<sup>1</sup> Puji TUHAN! Belagu ngena lagu ti baru ke TUHAN. Puji Iya lebuh bala nembiak Iya begempuru!",
			"2": "<sup>2</sup> Awakka ati kita gaga, Israel, laban Iya udah ngaga kita. Rami gaga ati meh kita orang Sion, ke Raja kita!",
			"3": "<sup>3</sup> Betanda muji nama Iya meh kita, belagu muji Iya ngena dedumbak enggau belikan.",
			"4": "<sup>4</sup> Laban TUHAN gaga ati ke nembiak Iya; Iya meri pemenang ngagai orang ke baruh ati.",
			"5": "<sup>5</sup> Awakka bala nembiak Allah Taala rami gaga ke pemenang sida, lalu belagu gaga ati lebuh sida gali ba kerusi-panjai sida.",
			"6": "<sup>6</sup> Awakka sida manjung muji Allah Taala, nyandang pedang ti bemata dua,",
			"7": "<sup>7</sup> kena sida ngalahka semua bansa lalu ngukum semua mensia,",
			"8": "<sup>8</sup> nanchang bala raja sida ngena rantai, nanchang bala tuai sida ngena rantai besi,",
			"9": "<sup>9</sup> kena ngukum semua bansa nitihka jaku Allah Taala. Tu meh pemenang ti diberi Allah Taala ngagai bala nembiak Iya. Puji TUHAN!"
		},
		"150": {
			"1": "<sup>1</sup> Puji TUHAN! Puji meh Allah Taala dalam Rumah Sembiang Iya! Puji meh pengering Iya di langit!",
			"2": "<sup>2</sup> Puji meh Iya ketegal pemesai pengawa ti udah dikereja Iya. Puji meh kuasa Iya ti pemadu besai.",
			"3": "<sup>3</sup> Puji meh Iya ngena terumpit. Puji meh Iya ngena belikan enggau engkeratung.",
			"4": "<sup>4</sup> Puji meh Iya ngena dedumbak enggau ajat; puji meh Iya ngena belikan enggau kesuling.",
			"5": "<sup>5</sup> Puji meh Iya ngena bebendai. Puji meh Iya ngena bebendai ti inggar.",
			"6": "<sup>6</sup> Awakka semua utai ti idup muji TUHAN! Puji TUHAN!"
		}
	}
};
module.exports = book;