var book = {
	"name": "1ნეშტ",
	"numChapters": 29,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ადამი, შეთი, ენოში,",
			"2": "<sup>2</sup> კენანი, მაჰალალეელი, იარედი,",
			"3": "<sup>3</sup> ხანოქი, მეთუშელახი, ლამექი,",
			"4": "<sup>4</sup> ნოე, სემი, ქამი, იაფეთი.",
			"5": "<sup>5</sup> იაფეთის ძენი: გომერი, მაგოგი, მადაი, იავანი, თუბალი, მეშექი და თირამი.",
			"6": "<sup>6</sup> გომერის ძენი: აშქენაზი, რიფათი და თოგარმა.",
			"7": "<sup>7</sup> იავანის ძენი: ელიშა, თარშიშა, ქითიმი და დოდანიმი.",
			"8": "<sup>8</sup> ქამის ძენი: ქუში, მიცრაიმი, ფუტი და ქანაანი.",
			"9": "<sup>9</sup> ქუშის ძენი: სება, ხავილა, საბთა, რაყმა, საბთექა. რაყმას ძენი: შება და დედანი.",
			"10": "<sup>10</sup> ქუშმა შვა ნიმროდი. მან დაიწყო ძალის გამოჩენა ქვეყანაზე.",
			"11": "<sup>11</sup> მიცრაიმმა შვა ლუდიმი და ყანანიმი, ლეჰაბიმი და ნაფთუხიმი,",
			"12": "<sup>12</sup> ფათრუმიმი და ქასლუხიმი, რომელთაგანაც ფილისტიმელები და ქაფთორელნი გამოვიდნენ.",
			"13": "<sup>13</sup> ქანაანმა შვა ციდონი, თავისი პირმშო, და ხეთი.",
			"14": "<sup>14</sup> იებუსელნი, ამორეველნი, გირგაშეველნი,",
			"15": "<sup>15</sup> ხიველნი, ყარკიელნი, სინაელნი,",
			"16": "<sup>16</sup> არვადიელნი, ცემარიელნი და ხამათიელნი.",
			"17": "<sup>17</sup> სემის ძენი: ელამი, აშური, არფაქშადი, ლუდი, არამი, ყუცი, ხული, გეთერი და მეშექი.",
			"18": "<sup>18</sup> არფაქშადმა შვა შელახი, შელახმა შვა ყებერი.",
			"19": "<sup>19</sup> ყებერს ორი ვაჟი შეეძინა; ერთის სახელი იყო ფელეგი, რადგან მის დროს გაიყო ქვეყანა. მისი ძმის სახელი იყო იაკტანი.",
			"20": "<sup>20</sup> იაკტანმა შვა ალმოდადი, შალეფი, ხაცარ-მავეთი, იარახი,",
			"21": "<sup>21</sup> ჰადორამი, უზალი, დიკლა,",
			"22": "<sup>22</sup> ყებალი, აბიმაელი, შება,",
			"23": "<sup>23</sup> ოფირი, ხავილა, იობაბი. ყველა ესენი იაკტანის ძენი არიან.",
			"24": "<sup>24</sup> სემი, არფაქშადი, შალახი,",
			"25": "<sup>25</sup> ყებერი, ფელეგი, რეყუ,",
			"26": "<sup>26</sup> სერუგი, ნახორი, თარახი,",
			"27": "<sup>27</sup> აბრამი, იგივე აბრაამი.",
			"28": "<sup>28</sup> აბრაამის ძენი: ისაკი და ისმაელი.",
			"29": "<sup>29</sup> ეს არის მათი მოდგმა: ისმაელის პირმშო ნებაიოთი, კედარი, ადბეელი, მიბსამი,",
			"30": "<sup>30</sup> მიშმაყი, დუმა, მასა, ხადადი, თემა,",
			"31": "<sup>31</sup> იეტური, ნაფიში და კედმა. ესენი არიან ისმაელის ძენი.",
			"32": "<sup>32</sup> კეტურას, აბრაამის ხარჭას ძენი: მან შვა ზიმრა, იაკშანი, მედანი, მიდიანი, იშბაკი და შუახი, იაკშანის ძენი: შება და დედანი.",
			"33": "<sup>33</sup> მიდიანის ძენი: ყეფა, ყეფერი, ხანოქი, აბიდაყი და ელდაყა. ყველა ესენი კეტურას ძენი არიან.",
			"34": "<sup>34</sup> აბრაამმა შვა ისაკი. ისაკის ძენი: ესავი და ისრაელი.",
			"35": "<sup>35</sup> ესავის ძენი: ელიფაზი, რეღუელი, იეყუში, იაყლამი და კორახი.",
			"36": "<sup>36</sup> ელიფაზის ძენი: თემანი, ომარი, ცეფი, გაყთამი, კენაზი, თიმნაყი, ყამალეკი.",
			"37": "<sup>37</sup> რეღუელის ძენი: ნახათი, ზერახი, შამა და მიზა.",
			"38": "<sup>38</sup> სეყირის ძენი: ლოტანი, შობალი, ციბყონი, ყანა, დიშონი, ეცერი და დიშანი.",
			"39": "<sup>39</sup> ლოტანის ძენი: ნორი, ჰომამი. ლოტანის და იყო თიმნაყი.",
			"40": "<sup>40</sup> შობალის ძენი: ყალიანი, მანახათი, ყებალი, შეფი, ონამი. ციბყონის ძენი: აია და ყანა.",
			"41": "<sup>41</sup> ყანას ძე დიშონი. დიშონის ძენი: ხამრანი, ეშბანი, ითრანი, ქერანი.",
			"42": "<sup>42</sup> ეცერის ძენი: ბილჰანი, ზაყავანი, იაყაკანი. დიშონის ძენი: ყუცი და არანი.",
			"43": "<sup>43</sup> აჰა, ის მეფენი, ედომის ქვეყნად რომ მეფობდნენ, ვიდრე ისრაელიანებში გამეფდებოდა მეფე: ბეყალი, ძე ბეორისა. მისი ქალაქის სახელი იყო დინჰაბა.",
			"44": "<sup>44</sup> მოკვდა ბეყალი და მის ნაცვლად გამეფდა იობაზ ზერახის ძე, ბოცრელი.",
			"45": "<sup>45</sup> მოკვდა იობაბი და მის ნაცვლად გამეფდა ხუშანი, თემანის ქვეყნიდან.",
			"46": "<sup>46</sup> მოკვდა ხუშანი და მის ნაცვლად გამეფდა ჰადად ბედადის ძე, რომელმაც მოაბის ველზე მიდიანელნი გაანადგურა. მისი ქალაქის სახელი იყო ყავითი.",
			"47": "<sup>47</sup> მოკვდა ჰადადი და მის ნაცვლად სამლა მასრეკელი გამეფდა.",
			"48": "<sup>48</sup> მოკვდა სამლა და მის ნაცვლად საული გამეფდა, მდინარისპირა რეხობოთიდან.",
			"49": "<sup>49</sup> მოკვდა საული და მის ნაცვლად ბაყალ-ხანან ყაქბორის ძე გამეფდა.",
			"50": "<sup>50</sup> მოკვდა ბაყალ-ხანანი და მის ნაცვლად ჰადადი გამეფდა. მისი ქალაქის სახელი იყო ფაყი. სახელი მისი ცოლისა მეჰატაბელი, ასული მატრედისა, ასული მიეზაჰაბისა.",
			"51": "<sup>51</sup> მოკვდა ჰადადი. ედომის მთავრები იყვნენ: მთავარი თიმნაყი, მთავარი ყალია, მთავარი ითეთი,",
			"52": "<sup>52</sup> მთავარი ოჰოლიბამა, მთავარი ელა, მთავარი ფინონი,",
			"53": "<sup>53</sup> მთავარი კენაზი, მთავარი თემანი, მთავარი მიბცარი,",
			"54": "<sup>54</sup> მთავარი მაგდიელი, მთავარი ყირამი. ესენი იყვნენ ედომის მთავრები.",
		},
		"2": {
			"1": "<sup>1</sup> ესენი არიან ისრაელის ძენი: რეუბენი, სიმონი, ლევი, იუდა, ისაქარი, ზებულუნი,",
			"2": "<sup>2</sup> დანი, იოსები, ბენიამინი, ნაფთალი, გადი და აშერი.",
			"3": "<sup>3</sup> იუდას ძენი: ყერი, ონანი და შელა. ქანაანელი შუაყის ასულისგან მას სამი შეეძინა. იუდას პირმშო ყერი ბოროტი იყო უფლის თვალში, ამიტომ მოაკვდინა იგი.",
			"4": "<sup>4</sup> თამარმა, მისმა რძალმა, შვა ფერეცი და ზარახი. იუდას სულ ხუთი ვაჟი ჰყავდა.",
			"5": "<sup>5</sup> ფერეცის ძენი: ხეცრონი და ხამული.",
			"6": "<sup>6</sup> ზერახის ძენი: ზიმრი, ეთანი, ჰემანი, ქალქოლი და დარაყი - ხუთნი.",
			"7": "<sup>7</sup> ქარამის ძენი: ყაქარი, ფიცი რომ გადეხა და ისრაელს უბედურება შეამთხვია.",
			"8": "<sup>8</sup> ეთანის ძე: ყაზარია.",
			"9": "<sup>9</sup> ხეცრონის ძენი, მას რომ შეეძინნენ: იერახმეელი, რამი და ქელუბაი.",
			"10": "<sup>10</sup> რამმა შვა: ყამინადაბი. ყამინადაბმა შვა ნახშონი, იუდას ძეთა წინამძღოლი.",
			"11": "<sup>11</sup> ნახშონმა შვა სალმა. სალმამ შვა ბოყაზი.",
			"12": "<sup>12</sup> ბოყაზმა შვა ყობედი. ყობედმა შვა იესე.",
			"13": "<sup>13</sup> იესემ შვა თავისი პირმშო ელიაბი, მეორე - აბინადაბი, მესამე - შიმყა,",
			"14": "<sup>14</sup> მეოთხე - ნეთანელი, მეხუთე - რადაი,",
			"15": "<sup>15</sup> მეექვსე - ოცემი, მეშვიდე - დავითი.",
			"16": "<sup>16</sup> მათი დები: ცერუია და აბიგაილი. ცერუიას ძენი: აბშაი, იოაბი და ყასაელი - სამნი.",
			"17": "<sup>17</sup> აბიგაელმა შვა ყამასა. ყამასას მამა იეთერი ისმაელიანი იყო.",
			"18": "<sup>18</sup> ქალებს, ხეცრონის ძეს, ყაზუბასაგან, თავისი ცოლისგან, და იერიყოთისაგან შვილები შეეძინნენ. აჰა, მისი ძენი: იეშერი, შობაბი და არდონი.",
			"19": "<sup>19</sup> მოკვდა ეაზუბა და ქალებმა ეფრათი შეირთო ცოლად. მასთან შეეძინა ხური.",
			"20": "<sup>20</sup> ხურმა შვა ური, ურმა შვა ბეცალიელი.",
			"21": "<sup>21</sup> მერე ხეცრონი გალაადის მამის ასულთან შევიდა და ცოლად შეირთო. მაშინ იგი სამოცი წლისა იყო. მასთან შეეძინა სეგუბი.",
			"22": "<sup>22</sup> სეგუბმა შვა იაირი. გალაადის ქვეყანაში მას ოცდასამი ქალაქი ჰქონდა.",
			"23": "<sup>23</sup> გეშურმა და არამმა წაართვა მათ იაირის დაბები, კენათი თავის ქალაქებიანად - სულ სამოცი ქალაქი. ყველა ესენი გალაადის მამის, მაქირის ძენი არიან.",
			"24": "<sup>24</sup> მას შემდეგ, რაც ხეცრონი ქალებ-ეფრათში მოკვდა, ხეცრონის ცოლს, აბიას შეეძინა აშხური, თეკოაყის მამა.",
			"25": "<sup>25</sup> ხეცრონის პირმშოს, იერახმეელის ძენი იყვნენ: პირმშო რამი, ბუნა, ორენი, ოცემი და ახია.",
			"26": "<sup>26</sup> იერახმეელის სხვა ცოლიც ჰყავდა, სახელად ყაცრა. ის იყო ონამის დედა.",
			"27": "<sup>27</sup> იერახმეელის პირმშოს, რამის ძენი იყვნენ: მაყაცი, იამინი და ყეკერი.",
			"28": "<sup>28</sup> ონამის ძენი იყვნენ: შამაი და იდაყი. შამაის ძენი: ნადაბი და აბიშური.",
			"29": "<sup>29</sup> აბიშურის ცოლს ერქვა აბიხაილი. მან შვა ახბანი და მოლიდი.",
			"30": "<sup>30</sup> ნედაბის ძენი: სელედი და აფაიმი. სელედი უშვილოდ მოკვდა.",
			"31": "<sup>31</sup> აფაიმს ყავდა ძე - იშყი. იშყის ძე იყო შეშანი. შეშანის ძე იყო ახლაი.",
			"32": "<sup>32</sup> იადაყის ძენი: ახი, შამი, იეთერი და იონათანი. იეთერი უშვილოდ მოკვდა.",
			"33": "<sup>33</sup> იონათანის ძენი: ფელეთი, ზაზა. ესენი იერახმელის ძენი იყვნენ.",
			"34": "<sup>34</sup> შეშანს ვაჟები არ ჰყოლია, მხოლოდ ასულები ჰყავდა. შეშეთს ეგვიპტელი მორჩილი ჰყავდა, სახელად იარხაყი.",
			"35": "<sup>35</sup> შეშეთმა თავისი ასული თავის მორჩილს, იარნაყს, მისცა ცოლად. მას შეეძინა ყათაი.",
			"36": "<sup>36</sup> ყათაიმ შვა ნათანი; ნათანმა შვა ზაბადი.",
			"37": "<sup>37</sup> ზაბადმა შვა ეფლალი, ეფლალმა შვა ყობედი.",
			"38": "<sup>38</sup> ყობედმა შვა იეჰუ, იეჰუმ შვა ყაზარია.",
			"39": "<sup>39</sup> ყაზარიამ შვა ხალეცი, ხალეცმა შვა ელყასა.",
			"40": "<sup>40</sup> ელყასამ შვა სისმაი, სისმაიმ შვა შალუმი.",
			"41": "<sup>41</sup> შალუმმა შვა იეკამია, იეკამიამ შვა ელიშამაყი.",
			"42": "<sup>42</sup> იერახმეელის ძმის, ხალების ძენი: მისი პირმშო მეშაყი, ეს არის ზიფის მამა; და მარეშას, ხებრონის მამის ძენი.",
			"43": "<sup>43</sup> ხებრონის ძენი: კორახი, თაფუახი, რეკემი, შემაყი.",
			"44": "<sup>44</sup> შემაყმა შვა რახამი, იარკეყამის მამა. რეკემმა შვა შამაი.",
			"45": "<sup>45</sup> შამაის ძე იყო მაყონი. მაყონი იყო მამა ბეთ-ცურისა.",
			"46": "<sup>46</sup> ქალების ხარჭას, ყეფას, შეეძინა: ხარანი, მოცავი და გაზეზი. ხარანმა შვა გაზეზი.",
			"47": "<sup>47</sup> იაჰდაის ძენი: რეგემი, იოთამი, გეშანი, ფელეტი, ყეფა და შაყაფი.",
			"48": "<sup>48</sup> ქალების ხარჭას, მაყაქას, შეეძინა: შებერი, თირხანა,",
			"49": "<sup>49</sup> მადმანას მამა შაყაფი, მაქბენასა და გიბყას მამა შევა. ქალების ასული ყაქსა.",
			"50": "<sup>50</sup> ესენი იყვნენ ქალების ძენი: ხურის ძე შობალი, ეფრათას პირმშო, მამა კირიათ- იეყარიმისა,",
			"51": "<sup>51</sup> სალმა, მამა ბეთ-ლეხემისა, და ხარეფი, მამა ბეთ-გადელისა.",
			"52": "<sup>52</sup> შობალს, კირიათ-იეყარიმის მამას, ჰყავდა ვაჟები - ჰაროე და ხაცი-ჰამენუხოთი.",
			"53": "<sup>53</sup> კირიათ-იეყარიმის საგვარეულონი: ითრიელნი, ფუთიელნი, შუმათიელნი და მიშრაყიელნი. მათგან გამოვიდნენ: ცორყელნი და ეშთაოლელნი.",
			"54": "<sup>54</sup> სალმას ძენი: ბეთ-ლეხემი, ნეტოფათიელნი, ყატრო-ბეთ-იოაბი, ხაცი-ჰამანახთი, ცორყიელნი.",
			"55": "<sup>55</sup> იაყბეცში მცხოვრებ მწიგნობართა საგვარეულონი: თირყათელნი, შიმყათიელნი, სუქათიელნი. ესენი კინიელნი არიან, ბეთ-რექაბას მამის, ხამათის, შთამომავლები.",
		},
		"3": {
			"1": "<sup>1</sup> ესენი იყვნენ დავითის ძენი, რომლებიც ხებრონში შეეძინნენ მას: პირმშო ამონი, ახინოყამ ისრაელიანისგან, მეორე - დანიელი, აბიგაილ ქარმელელისგან,",
			"2": "<sup>2</sup> მესამე - აბესალომი, გეშურის მეფე თალმაის ასულის, მაყაქას ვაჟი, მეოთხე - ადონია, ხაგითის ვაჟი.",
			"3": "<sup>3</sup> მეხუთე - შეფატია, აბიტალისგან, მეექვსე - ითარეყამი, ყეგლასგან, თავისი ცოლისაგან.",
			"4": "<sup>4</sup> ექვსნი შეეძინნენ მას ხებრონში. შვიდი წელი და ექვსი თვე იქ იმეფა, ოცდაცამეტი წელი კი - იერუსალიმში.",
			"5": "<sup>5</sup> ესენი შეეძინნენ იერუსალიმში: შიმყა, შობაბი, ნათანი, სოლომონი - ოთხნი ბათშუაყისგან, ყამიელის ასულისგან.",
			"6": "<sup>6</sup> იბხარი, ელიშამაყი, ელიფალეტი,",
			"7": "<sup>7</sup> ნოჰაგი, ნეფეგი, იაფიაყი,",
			"8": "<sup>8</sup> ელიშამაყი, ელიადაყი, ელიფელეგი - ცხრანი.",
			"9": "<sup>9</sup> სულ ესენი არიან დავითის ძენი, ხარჭების ძეთა გარდა; თამარიც მათი და იყო.",
			"10": "<sup>10</sup> სოლომონის ძე რობოამი, მისი ძე აბია, მისი ძე ასა, მისი ძე იოშაფატი,",
			"11": "<sup>11</sup> მისი ძე იორამი, მისი ძე ახაზიაჰუ, მისი ძე იოაში,",
			"12": "<sup>12</sup> მისი ძე ამაციაჰუ, მისი ძე ყაზარია, მისი ძე იოთამი,",
			"13": "<sup>13</sup> მისი ძე ახაზი, მისი ძე ხიზკიაჰუ, მისი ძე მენაშე,",
			"14": "<sup>14</sup> მისი ძე ამონი, მისი ძე იოშიაჰუ.",
			"15": "<sup>15</sup> იოშიაჰუს ძენი: პირმშო იოხაზანი, მეორე - იეჰოიაკიმი, მესამე - ციდკიაჰუ, მეოთხე - შალუმი.",
			"16": "<sup>16</sup> იეჰოიაკიმის ძენი: მისი ძე იექონია, მისი ძე ციდკია.",
			"17": "<sup>17</sup> იექონიას ძენი: ასირი, მისი ძე შელათიელი,",
			"18": "<sup>18</sup> მალქირამი, ფედაია, შენაცარი, იეკამია, ჰოშამაყი, ნედაბია.",
			"19": "<sup>19</sup> ფედაიას ძენი: ზერუბაბელი, შიმყი. ზერუბაბელის ძენი: მეშულამი, ხანანია, მათი და შელომითი;",
			"20": "<sup>20</sup> ხაშუბა, ოჰელი, ბერექია და ხასადია.",
			"21": "<sup>21</sup> ხანანიას ძენი: ფელატია და ესაია. რეფაიას ძენი და არნანის ძენი, ყობადიას ძენი და შექანიას ძენი.",
			"22": "<sup>22</sup> შექანიას ძე შემაყია. შემაყიას ძენი: ხატუში, იგალი, ბარიახი, ნეყარია, შეფატი - ექვსნი.",
			"23": "<sup>23</sup> ნეყარიას ძენი: ელიოყენაი, ხიზკია, ყაზრიკამი - სამნი.",
			"24": "<sup>24</sup> ელიოყენაის ძენი: ჰოდაგიაჰუ, ელიაშიბი, ფელაია, ყაკუბი. იოხანანი, დელაია, ყანანი - შვიდნი.",
		},
		"4": {
			"1": "<sup>1</sup> იუდას ძენი: ფერეცი, ხეცრონი, ქარმი, ნური და შობალი.",
			"2": "<sup>2</sup> შობალის ძემ, რეიამ, შვა იახათი. იახათმა შვა ახუმაი და ლაჰადი. აჰა, ცორყათელთა საგვარეულონი.",
			"3": "<sup>3</sup> აჰა, ყეტამის მამის ძენი: იზრეყელი, იშმა, იდბაში; მათ დას ჰაცლელფონი ერქვა;",
			"4": "<sup>4</sup> ფენუელი, გედორის მამა, და ყეზერი, ხუშას მამა.",
			"5": "<sup>5</sup> აშხურს, თეკოაყის მამას ორი ცოლი ჰყავდა: ხელა და ნაყარა.",
			"6": "<sup>6</sup> ნაყარასგან შეეძინა: ახუზამი, ხეფერი, თემნი და ჰაახაშთარი. ესენი იყვნენ ნაყარას ძენი.",
			"7": "<sup>7</sup> ხელას ძენი: ცერეთი, იეცოხარი და ეთნანი.",
			"8": "<sup>8</sup> კოცმა შვა ყანუბი, ჰაცობება და ახარხელ ჰარუმის ძის საგვარეულონი.",
			"9": "<sup>9</sup> იაყბეცი თავის ძმებზე უფრო პატივდებული იყო. იაყბეცი დედამისმა დაარქვა. თქვა: რადგან სიმწრით გავაჩინე.",
			"10": "<sup>10</sup> მოუხმო იაყბეცმა ისრაელის ღმერთს და უთხრა: ნეტავ კურთხევით მაკურთხევდე და საზღვრებს გამიდიდებდე, შენი ხელი ჩემთან იქნებოდეს და ბოროტებისაგან დამიცავდე, რომ არ გავიტანჯო. და ღმერთმა მისცა, რასაც ითხოვდა.",
			"11": "<sup>11</sup> შეხას ძმა ქელუბმა შვა მეხირ-ეშთონის მამა.",
			"12": "<sup>12</sup> ეშთონმა შვა ბეთ-რაფა, ფასეახი და ყირ-ნახაშის მამა თეჰინა. აჰა, რექას მცხოვრებნი.",
			"13": "<sup>13</sup> ყენაზის ძენი: ყათინელი და სერაია. ყათნიელის ძე იყო ხათათა.",
			"14": "<sup>14</sup> მეყონოთაიმ შვა ყოფრა. სერაიამ შვა იოაბი, მამა ხუროთა ველისა, რადგან ხურონი იყვნენ.",
			"15": "<sup>15</sup> იეფუნის ძის, ხალების ძენი: ყირუ, ელა და ნაყამი. ელას ძე იყო კენაზი.",
			"16": "<sup>16</sup> იეჰალელის ძენი: ზიფი, ზიფა, თირია და ასარიელი.",
			"17": "<sup>17</sup> ყეზრას ძენი: იეთერი, მერედი, ყეფერი და იალონი. იეთერმა შვა მირიამი, შამაი და იშბახი, იშთემოაყის მამა.",
			"18": "<sup>18</sup> მის იუდაელ ცოლს შეეძინა იერედი, მამა გედორისა, ხებერი, მამა სოხოსი, იეკუთიელი, მამა ზანოახისა. აჰა, ძენი ბითიასი, ფარაონის ასულისა, მარედმა რომ შეირთო.",
			"19": "<sup>19</sup> ჰოდიას ცოლის, ნახამის დის ვაჟები: კეყილას მამა გარმელი, და ეშთემოაყ მაყაქათელი.",
			"20": "<sup>20</sup> შიმონის ძენი: ამნონი, რინა, ბენ-ხანანი და თილონი. იშყის ძენი: ზონეთი და ბენ- ზოხეთი.",
			"21": "<sup>21</sup> შელა იუდას ძის ვაჟები: ყერი, ლექას მამა, ლაყდა, მარეშას მამა და სელის მკეთებელთა საგვარეულოები აშბეაყის სახლიდან.",
			"22": "<sup>22</sup> იოკიმი, ქოზებას ხალხი, იოაში და სარაფი მოაბსა და იაშუბი-ლახუმს ფლობდნენ. ეს ძველი ამბებია.",
			"23": "<sup>23</sup> ისინი მეთუნენი იყვნენ. ნეტაყიმსა და გედერაში სახლობდნენ, მეფესთან ცხოვრობდნენ და მისთვის შრომობდნენ.",
			"24": "<sup>24</sup> სიმონის ძენი: ნემუელი, იამინი, იარიბი, ზერახი და საული.",
			"25": "<sup>25</sup> მისი ძე შალუმი, მისი ძე მიბსამი, მისი ძე მიშმაყი.",
			"26": "<sup>26</sup> მიშმაყის ძენი: მისი ძე ნამუელი, მისი ძე ზაქური, მისი ძე შიმყი.",
			"27": "<sup>27</sup> შიმყის თექვსმეტი ვაჟი და ექვსი ქალი ჰყავდა. მის ძმებს ბევრი ვაჟები არ ჰყოლია და არც მათი საგვარეულო იყო იუდას ძეთა საგვარეულოსავით მრავალრიცხოვანი.",
			"28": "<sup>28</sup> ისინი ცხოვრობდნენ ბეერ-შებაყში, მოლედასა და ხაცარ შეყალში,",
			"29": "<sup>29</sup> ბილჰაში, ყეცამში, თოლადში,",
			"30": "<sup>30</sup> ბეთუელში, ხარმაში, ციკლაგში,",
			"31": "<sup>31</sup> ბეთ-მარქაბოთში, ხაცარ-სუსიმში, ბეთ-ბირსა და შაყარაიმში. აჰა, მათი ქალაქები დავითის გამეფებამდე.",
			"32": "<sup>32</sup> მათი დასახლებები: ყეტამი, ყაინი, რიმონი, თოქენი, ყაშანი - ხუთი ქალაქი.",
			"33": "<sup>33</sup> ასევე ყველა ის სოფელი, რომელიც ამ ქალაქებს ეკვროდა ვიდრე ბაყალამდე. აჰა, მათი საცხოვრებელი და მათი საგვარტომო ნუსხა.",
			"34": "<sup>34</sup> მეშობაბი, იამლექი, იოშა ამაციას ძე,",
			"35": "<sup>35</sup> იოელი და იეჰუ, ძე ოშიბიასი, სერაიას ძისა, ყასიელის ძისა.",
			"36": "<sup>36</sup> ელიოყენაი, იაყაკობა, იშოხაია, ყასია, ყადიელი, ისიმიელი, ბენაია,",
			"37": "<sup>37</sup> ზიზა ძე შიფყისა, ძე ალონისა, ძე იედაიასა, ძე შიმრისა, ძე შემაყიასი.",
			"38": "<sup>38</sup> ესენი, სახელებით რომ არიან ჩამოთვლილნი, თავთავიანთი საგვარეულოების მთავრები იყვნენ. მათი მამისსახლი ძალიან გაიზარდა.",
			"39": "<sup>39</sup> მათ გედორისაკენ გასწიეს, ველის აღმოსავლეთით, თავიანთი ცხვრისთვის საძოვრის საძებნელად.",
			"40": "<sup>40</sup> პოხიერსა და კარგ საძოვარს მიაგნეს; ვრცელი ქვეყანა იყო, თან მშვიდი და მყუდრო; რადგან უწინ აქ ქამის შთამომავლები ცხოვრობდნენ.",
			"41": "<sup>41</sup> ისინი, სახელებით რომ იყვნენ ჩამოწერილნი, იუდას მეფის, იეხიზკიაჰუს დღეებში მოვიდნენ, გაანადგურეს მათი კარვები და მაყონელნი, იქ რომ იყვნენ. იმ დღეს ერთიანად ამოწყვიტეს ისინი და მათ ნაცვლად დასახლდნენ, რადგან იქ იყო მათი ცხვრის საძოვარი.",
			"42": "<sup>42</sup> ხოლო მათგან, სიმონის ძეთაგან, ხუთასი კაცი სეყირის მთისკენ გაეშურა იშყის ძეების - ფელატიას, ნეფარიას, რეფაიასა და ყუზიელის მეთაურობით.",
			"43": "<sup>43</sup> გაანადგურეს ყამალეკთა ნატამალი და დღემდე იქ ცხოვრობენ.",
		},
		"5": {
			"1": "<sup>1</sup> რეუბენის, ისრაელის პირმშოს ძენი (იგი პირმშო იყო, მაგრამ როცა მამის სარეცელი წაბილწა, მისი პირმშოობა გადაეცა იოსებს, ისრაელის ძეს, არა იმისათვის რომ პირმშოდ ჩაწერილიყო.",
			"2": "<sup>2</sup> იუდა ძლიერი იყო თავის ძმებში და წინამძღვარიც მისგან იყო, პირმშოობა კი იოსებს ეკუთვნოდა).",
			"3": "<sup>3</sup> რეუბენის, ისრაელის პირმშოს ძენი: ხანოქი, ფალლუ, ხეცრონი და ქარმი.",
			"4": "<sup>4</sup> იოელის ძენი: მისი ძე შემაყია, მისი ძე გოგი, მისი ძე შიმაყი,",
			"5": "<sup>5</sup> მისი ძე მიქა, მისი ძე სერაია, მისი ძე ბაცალი,",
			"6": "<sup>6</sup> მისი ძე ბეერა, რომელიც ტყვედ წაიყვანა აშურის მეფემ, თიგლათ-ფილნესერმა. ის იყო რეუბენიანთა წინამძღოლი.",
			"7": "<sup>7</sup> მისი ძმები, მათი საგვარეულოებისა და საგვარტომო ნუსხის მიხედვით იყვნენ: უფროსი: იეყიელი, ზექარია,",
			"8": "<sup>8</sup> ბელაყი, ძე ყაზაზისა, ძე შემაყისა, ძე იოელისა. იგი ყაროყერში ცხოვრობდა ნებომდე და ბაყალ-მეყონამდე.",
			"9": "<sup>9</sup> აღმოსავლეთით ცხოვრობდა მდინარე ევფრატიდან მოყოლებული უდაბნოს პირამდე, რადგან დიდძალი საქონელი ჰყავდათ გალაადის ქვეყანაში.",
			"10": "<sup>10</sup> საულის დროს აგარიანებს ეომებოდნენ და მათ ხელში ჩაცვივდნენ. დასახლდნენ მათ კარვებში, გალაადის მთელ აღმოსავლეთ მხარეში.",
			"11": "<sup>11</sup> გადის ძენი მათ მეზობლად ცხოვრობდნენ, ბაშანის ქვეყანაში, სალქამდე.",
			"12": "<sup>12</sup> უფროსი იოელი, მომდევნო შაფამი, იაყნაი და შაფატი ბაშანში ცხოვრობდნენ.",
			"13": "<sup>13</sup> მათი მოძმენი, თავ-თავიანთი მამისსახლებიდან: მიქაელი, მეშულამი, შებაყი, იორაი, იაყქანი, ზიაყი, ყებედი და შიბყა.",
			"14": "<sup>14</sup> აჰა, ძენი აბიხაილისა, ხურის ძისა, იაროახის ძის, გილყადის ძისა, მიქაელის ძისა, იეშიშაის ძისა, იახდოს ძისა, ბუზის ძისა.",
			"15": "<sup>15</sup> ახი, ძე ყაბდიელისა, ძე გუნისა - მათი მამისსახლის თავკაცი.",
			"16": "<sup>16</sup> ისინი ცხოვრობდნენ გალაადში, ბაშანსა და მის კუთვნილ ქალაქებში, ასევე შარონის მთელ სანახებში, ვიდრე გამოვიდოდნენ.",
			"17": "<sup>17</sup> ყველანი აღირიცხნენ იუდას მეფის, იოთამის დროს და იარბეყამ ისრაელის მეფის დროს.",
			"18": "<sup>18</sup> რეუბენიანებში, გადიანებში და მენაშეს ტომის ნახევარში იყვნენ გულადი ვაჟკაცები, რომელნიც ფარსა და მახვილს ატარებდნენ, მშვილდოსნობდნენ და ბრძოლაში იყვნენ ნაწრთობნი. მათგან რიცხვით ორმოცდაოთხიათას შვიდას სამოცნი გამოდიოდნენ საომრად.",
			"19": "<sup>19</sup> აგარიანებთან, იატურთან, ნაფიშთან და ნოდაბთან ჰქონდათ ომი.",
			"20": "<sup>20</sup> მიეხმარნენ მათ წინააღმდეგ ბრძოლაში და მათ ხელში ჩაცვივდნენ აგარიანები მთელი მათი საბადებლითურთ. ეს იმიტომ, რომ ომში ღმერთს მოუხმეს. მანაც უსმინა მათ, რაკი მისი იმედი ჰქონდათ.",
			"21": "<sup>21</sup> წამოასხეს მათი საქონელი: ორმოცდაათიათასი აქლემი, ორასორმოცდაათიათასი ცხვარი, ორი ათასი ვირი და ასი ათასი სული ტყვე.",
			"22": "<sup>22</sup> მრავალი მოკლული დაეცა, რადგან ღვთისაგან იყო ეს ომი. მკვიდრობდნენ ისინი ამ ადგილებზე, სანამ ტყვეობაში მოხვდებოდნენ.",
			"23": "<sup>23</sup> მენაშეს ტომის ნახევარიც დასახლდა იმ ქვეყნად, დაწყებული ბაშანიდან ვიდრე ბაალ-ხერმონამდე, სენირამდე და ხერმონის მთამდე. მრავალრიცხოვანნი იყვნენ ისინი.",
			"24": "<sup>24</sup> აჰა, მათი მამისსახლის თავკაცები: ყეფერი, იშყი, ელიელი, ყაზრიელი, იერემია, ჰოდავია, იახდიელი - მამაცი ვაჟკაცები, მამისსახლთა სახელოვანი თავკაცები.",
			"25": "<sup>25</sup> მათ ზურგი აქციეს მამა-პაპის ღმერთს და გზააბნეულნი გაჰევნენ იმ ქვეყნის ხალხის ღმერთებს, მათგან რომ განდევნა ღმერთმა.",
			"26": "<sup>26</sup> ისრაელის ღმერთმა აღძრა აშურის მეფის, ფულის, სული და აშურის მეფის, თიგლათ-ფილნესერის, სული და გაასახლა რეუბენიანები, გადიანები და მენაშეს ტომის ნახევარი. ჩაასახლა ისინი ხალახში, ხაბორში, ჰარაში, მდინარე გოზანთან, სადაც დღემდე არიან.",
			"27": "<sup>27</sup> ლევის ძენი: გერშომი, კეჰათი და მერარი.",
			"28": "<sup>28</sup> კეხათის ძენი: ყამრამი, იცჰარი, ხებრონი და ყუზიელი.",
			"29": "<sup>29</sup> ყამრამის ძენი: აარონი, მოსე, მირიამი. აარონის ძენი: ნადაბი, აბიჰუ, ელყაზარი და ითამარი.",
			"30": "<sup>30</sup> ელყაზარმა შვა ფინხასი, ფინხასმა შვა აბიშუაყი.",
			"31": "<sup>31</sup> აბიშუაყმა შვა ბუკი, ბუკმა შვა ყუზი.",
			"32": "<sup>32</sup> ყუზმა შვა ზერახია, ზერახიამ შვა მერაიოთი.",
			"33": "<sup>33</sup> მერაიოთმა შვა ამარია, ამარიამ შვა ახიტუბი.",
			"34": "<sup>34</sup> ახიტუბმა შვა ცადოკი, ცადოკმა შვა ახიმაყაცი.",
			"35": "<sup>35</sup> ახიმაყაცმა შვა ყაზარია, ყაზარიამ შვა იოხანანი.",
			"36": "<sup>36</sup> იოხანანმა შვა ყაზარია (ეს იმ ტაძარში მღვდლობდა, რომელიც სოლომონმა ააშენა იერუსალიმში).",
			"37": "<sup>37</sup> ყაზარიამ შვა ამარია, ამარიამ შვა ახიტუბი.",
			"38": "<sup>38</sup> ახიტუბმა შვა ცადოკი, ცადოკმა შვა შალუმი,",
			"39": "<sup>39</sup> შალუმმა შვა ხილკია, ხილკიამ შვა ყაზარია.",
			"40": "<sup>40</sup> ყაზარიამ შვა სერაია, სერაიამ შვა იეჰოცადაკი.",
			"41": "<sup>41</sup> იეჰოცადაკი წავიდა, როცა უფალმა იუდა და იერუსალიმი ნაბუქოდონოსორის ხელით გაასახლა.",
		},
		"6": {
			"1": "<sup>1</sup> ლევის ძენი: გერშომი, კეჰათი, მერარი.",
			"2": "<sup>2</sup> აჰა, გერშომის ძეთა სახელები: ლიბნი და შიმყი.",
			"3": "<sup>3</sup> კეჰათის ძენი: ყამრამი, იცჰარი, ხებრონი და ყუზიელი.",
			"4": "<sup>4</sup> მერარის ძენი: მახლი და მუში. აჰა, ლევის საგვარეულონი, თავ-თავიანთი მამისსახლის მიხედვით.",
			"5": "<sup>5</sup> გერშომს ჰყავდა მისი ძე ლიბნი, მისი ძე იახათი, მისი ძე ზიმა,",
			"6": "<sup>6</sup> მისი ძე იოახი, მისი ძე ყიდო, მისი ძე ზერახი, მისი ძე იეათრაი.",
			"7": "<sup>7</sup> კეჰათის ძენი: მისი ძე ყამინადაბი, მისი ძე კორახი, მისი ძე ასირი,",
			"8": "<sup>8</sup> მისი ძე ელკანა, მისი ძე აბიასაფი, მისი ძე ასირი,",
			"9": "<sup>9</sup> მისი ძე თახათი, მისი ძე ურიელი, მისი ძე ყუზია, მისი ძე საული.",
			"10": "<sup>10</sup> ელკანას ძენი: ყასამაი და ახიმოთი.",
			"11": "<sup>11</sup> ელკანა. ელკანას ძენი: მისი ძე ცოფაი, მისი ძე ნახათი,",
			"12": "<sup>12</sup> მისი ძე ელიაბი, მისი ძე იეროხამი, მისი ძე ელკანა.",
			"13": "<sup>13</sup> სამუელის ძენი: პირმშო ვაშნი და აბია.",
			"14": "<sup>14</sup> მერარის ძენი: მახლი, მისი ძე ლიბნი, მისი ძე შიმყი, მისი ძე ყუზა,",
			"15": "<sup>15</sup> მისი ძე შიმყა, მისი ძე ხაგია, მისი ძე ყასაია.",
			"16": "<sup>16</sup> აჰა, ისინი, მგალობლებად რომ დააყენა დავითმა უფლის სახლში, კიდობნის დადგმის შემდეგ.",
			"17": "<sup>17</sup> სადღესასწაულო კარავთან გალობით ასრულებდნენ სამსახურს, სანამ სოლომონი იერუსალიმში უფლის სახლს ააშენებდა. ისინი თავიანთი სამსახურის წესისდამიხედვით იდგნენ.",
			"18": "<sup>18</sup> აჰა, ისინი, სამსახურს ვინც ეწეოდნენ და მათი ძენი:",
			"19": "<sup>19</sup> ელკანას ძე, იეროხამის ძე, ელილელის ძე, თირხას ძე,",
			"20": "<sup>20</sup> ცუფის ძე, ელკანას ძე, მახათის ძე, ყამასაის ძე,",
			"21": "<sup>21</sup> ელკანას ძე, იოელის ძე, ყაზარიას ძე, ცეფანიას ძე,",
			"22": "<sup>22</sup> თახათის ძე, ასირის ძე, აბიასაფის ძე, კორახის ძე,",
			"23": "<sup>23</sup> იცჰარის ძე, კეჰათის ძე, ლევის ძე, ისრაელის ძე.",
			"24": "<sup>24</sup> მისი ძმა ასაფი მის მარჯვნივ მდგომი, ასაფ ბერექიას ძე, შიმყას ძე,",
			"25": "<sup>25</sup> მიქაელის ძე, ბაყასეას ძე, მალქიას ძე,",
			"26": "<sup>26</sup> ეთნის ძე, ზერახის ძე, ყადიას ძე,",
			"27": "<sup>27</sup> ეთანის ძე, ზიმას ძე, შიმყის ძე,",
			"28": "<sup>28</sup> იახათის ძე, გერშომის ძე, ლევის ძე,",
			"29": "<sup>29</sup> მერარის ძე, მათი ძმები მარცხნივ რომ იდგნენ: ეთან კიშის ძე, ყავდის ძე, მალუქის ძე,",
			"30": "<sup>30</sup> ხაშაბიას ძე, ამაციას ძე, ხილკიას ძე,",
			"31": "<sup>31</sup> ამცის ძე, ბანის ძე, შამერის ძე,",
			"32": "<sup>32</sup> მახლის ძე, მუშის ძე, მერარის ძე, ლევის ძე.",
			"33": "<sup>33</sup> მათი ძმები, ლევიანები, ღვთის სახლის სავანის ყველანაირი სამსახურისთვის იყვნენ დაყენებულნი.",
			"34": "<sup>34</sup> აარონი და მისი ძენი აღსავლენის სამსხვერპლოზე და საკმევლის სამსხვერპლოზე აკმევდნენ. წმიდათა წმიდაში ყველა საქმეს აკეთებდნენ და ყველაფერს ისე ასრულებდნენ ისრაელის განსაწმედად, როგორც მოსემ ბრძანა, ღვთის მორჩილმა.",
			"35": "<sup>35</sup> აჰა, აარონის ძენი: მისი ძე ელყაზარი, მისი ძე ფინხასი, მისი ძე აბიშუაყი,",
			"36": "<sup>36</sup> მისი ძე ბუკი, მისი ძე ყუზი, მისი ძე ზერახია,",
			"37": "<sup>37</sup> მისი ძე მერაიოთი, მისი ძე ამარია, მისი ძე ახიტუბი,",
			"38": "<sup>38</sup> მისი ძე ცადოკი, მისი ძე ახიმაყაცი.",
			"39": "<sup>39</sup> ეს მათი საცხოვრებელი, მათი დაბებისა და საზღვრის მიხედვით, ერგოთ აარონიანებს, კეჰათელთა საგვარეულოდან, რადგან წილისყრით მათ ეკუთვნოდათ.",
			"40": "<sup>40</sup> მისცეს მათ ხებრონი, იუდას ქვეყანაში, თავისი სანახებითა და შემოგარენითურთ.",
			"41": "<sup>41</sup> ამ ქალაქის მინდორ-ველი და სოფლები ქალებს მისცეს, იეფუნეს ძეს.",
			"42": "<sup>42</sup> აარონიანებს მისცეს თავშესაფარი ქალაქები: ხებრონი, ლიბნა თავისი სანახებით, იათირი და ეშთემოაყი თავისი სანახებით,",
			"43": "<sup>43</sup> ხილენი თავისი სანახებით, დებირი თავისი სანახებით,",
			"44": "<sup>44</sup> ყაშანი თავისი სანახებით, ბეთ-შემეში თავისი სანახებით,",
			"45": "<sup>45</sup> ბენიამინის ტომიდან: გებაყი თავისი სანახებით, ყალემეთი თავისი სანახებით, ყანათოთი თავისი სანახებით. მათ საგვარეულოებში სულ ცამეტი ქალაქი იყო.",
			"46": "<sup>46</sup> ნოლო დანარჩენ კეჰათიანებს, ამ ტომის საგვარეულოდან, მენაშეს ტომის ნახევრიდან ათი ქალაქი ერგოთ წილისყრით.",
			"47": "<sup>47</sup> გერშომიანებს, თავ-თავიანთი საგვარეულოებისდა მიხედვით, ისაქარის, აშერის, ნაფთალის და მენაშეს ტომებიდან ცამეტი ქალაქი ერგოთ ბაშანში.",
			"48": "<sup>48</sup> მერარიანებს, თავ-თავიანთი საგვარეულოებისდა მიხედვით, რეუბენის, გადისა და ზებულუნის ტომებიდან თორმეტი ქალაქი შეხვდათ წილისყრით.",
			"49": "<sup>49</sup> ეს ქალაქები მისცეს თავისი სანახებითურთ ისრაელიანებმა ლევიანებს.",
			"50": "<sup>50</sup> წილისყრით მისცეს იუდას, სიმონისა და ბენიამინის ძეთა ტომებიდან ის ქალაქები, რომელთაც სახელები ერქვათ.",
			"51": "<sup>51</sup> კეჰათიანთა საგვარეულოებმა თავიანთი სასაზღვრო ქალაქები ეფრემის ტომისგან მიიღეს.",
			"52": "<sup>52</sup> მათვე მისცეს თავშესაფარი ქალაქები: შექემი, ეფრემის მთაზე, თავის სანახებიანად, გეზერი თავისი სანახებით,",
			"53": "<sup>53</sup> იაკმეყამი თავისი სანახებით, ბეთ-ხორონი თავისი სანახებით,",
			"54": "<sup>54</sup> აიალონი თავისი სანახებით და გათ-რიმონი თავისი სანახებით.",
			"55": "<sup>55</sup> მენაშეს ტომის ნახევრიდან ყანერი თავისი სანახებით და ბილყამი თავისი სანახებით მისცეს კეჰათიანთა დანრჩენ საგვარეულოს.",
			"56": "<sup>56</sup> გერშომიანებს, მენაშეს ტომის ნახევრის საგვარეულოდან, ბაშანში მისცეს: გოლანი თავის სანახებიანად და ყაშთაროთი თავის სანახებიანად.",
			"57": "<sup>57</sup> ისაქარის ტომიდან მისცეს: კედეში თავის სანახებიანად, დაბრაში თავის სანახებიანად,",
			"58": "<sup>58</sup> რამონი თავის სანახებიანად, ყანემი თავის სანახებიანად.",
			"59": "<sup>59</sup> აშერის ტომიდან მისცეს: მაშალი თავის სანახებიანად, ყაბდონი თავის სანახებიანად,",
			"60": "<sup>60</sup> ხუკოკი თავის სანახებიანად, რეხობი თავის სანახებიანად.",
			"61": "<sup>61</sup> ნაფთალის ტომიდან მისცეს კედეში, გალილეაში თავის სანახებიანად.",
			"62": "<sup>62</sup> დანარჩენ მერარიანებს კი ზებულუნის ტომიდან მისცეს: რიმონო თავის სანახებიანად და თაბორი თავის სანახებიანად.",
			"63": "<sup>63</sup> იორდანეს გადაღმა, იერიხონთან, იორდანეს აღმოსავლეთით, რეუბენის ტომიდან მისცეს: ბეცერი უდაბნოში თავის სანახებიანად, იახცარი თავის სანახებიანად,",
			"64": "<sup>64</sup> კედმოთი თავის სანახებიანად და მეფაყი თავის სანახებიანად.",
			"65": "<sup>65</sup> გადის ტომიდან მისცეს: რამოთი გალაადში თავის სანახებიანად,",
			"66": "<sup>66</sup> ხეშბონი თავის სანახებიანად და იაყზერი თავის სანახებიანად.",
		},
		"7": {
			"1": "<sup>1</sup> ისაქარის ძენი: თოლაყი, ფუა, იაშუბი, შიმრონი - ოთხნი.",
			"2": "<sup>2</sup> თოლაყის ძენი: ყუზი, რეფაია, ირიელი, იახმაი, იაბხამი, სამუელი - თოლაყის მამისსახლის თავკაცები, თავიანთი მოდგმის მაგარი ვაჟკაცები. დავითის დროს მათი რიცხვი ოცდაორიათას ექვსასი იყო.",
			"3": "<sup>3</sup> ყუზის ძე - იზრახია. იზრახიას ძენი: მიქაელი, ყობადია, იოელი, იშია - სულ ხუთი თავკაცი.",
			"4": "<sup>4</sup> თავ-თავიანთი მოდგმიდან და მამისსახლიდან ოცდათექვსმეტათასიანი საბრძოლო რაზმი გამოჰყავდათ ომში, რადგან ბევრი ცოლ-შვილი ჰყავდათ.",
			"5": "<sup>5</sup> მათი ძმები, ისაქარის ყველა საგვარეულოს მაგარი ვაჟკაცები, ოთხმოცდაშვიდი ათასნი იყვნენ. ყველანი საგვარტომო ნუსხაში იყვნენ ჩაწერილნი.",
			"6": "<sup>6</sup> ბენიამინიანები: ბელაყი, ბექერი და იდიყაელი - სამნი.",
			"7": "<sup>7</sup> ბელაყიანები: ეცბონი, ყუზი, ყუზიელი, ირიმოთი, ყირი-ხუთნი; მამისსახლის თავკაცები და მამაცი ვაჟკაცები. საგვარტომო ნუსხაში ოცდაორი ათას ოცდათორმეტნი არიან ჩაწერილნი.",
			"8": "<sup>8</sup> ბექერიანები: ზემირა, იოყამი, ელიყეზერი, ელიოყენაი, ყომრი, ირემოთი, აბია, ყანათოთი და ყალამეთი. ყველა ესენი ბექერიანები არიან.",
			"9": "<sup>9</sup> საგვარტომო ნუსხაში ისინი თავ-თავიანთი მოდგმის, მამისსახლთა თავკაცების მიხედვით არიან ჩაწერილნი - სულ ოციათას ორასი ძლიერი მებრძოლი.",
			"10": "<sup>10</sup> იედიყაელიანები: ბილჰანი, ბილჰანის ძენი: იეყუში, ბენიამინი, ეჰუდი, ქენაყანა, ზეთანი, თარშიში და ახიშახარი.",
			"11": "<sup>11</sup> ყველა ესენი იედიყაილიანები არიან, მამისსახლთა თავკაცები. მათგან ჩვიდმეტიათას ორასი მებრძოლი გამოდიოდა საბრძოლველად.",
			"12": "<sup>12</sup> შეფიმი და ხუფიმი ყირის ძენი იყვნენ, ხოლო ხუშიმი ახერის ძე იყო.",
			"13": "<sup>13</sup> ნაფთალიანები: იახაციელი, გუნი, იეცერი და შალუმი ბილჰას შვილები იყვნენ.",
			"14": "<sup>14</sup> მენაშეანები: ასერიელი, რომელიც მისმა არამელმა ხარჭამ გააჩინა; მასვე ეყოლა მაქირი, გალაადის მამა.",
			"15": "<sup>15</sup> მან ცოლად მოიყვანა ხუფიმი და შუფიმი. მის ერთ დას მაყაქა ერქვა, მეორეს ცელაფხადი. ცელაფხადს ასულები ჰყავდა.",
			"16": "<sup>16</sup> მაყაქამ, მაქრის ცოლმა, ვაჟი გააჩინა და ფერეში დაარქვა. მის ძმას შარეში ერქვა, მისი ვაჟები იყვნენ ულამი და რაკამი.",
			"17": "<sup>17</sup> ულამის ძე ბედანი. აჰა, გალაადის, მაყაქა მენაშეს ძის ვაჟის ძენი.",
			"18": "<sup>18</sup> მისმა დამ, ჰამოლეხეთმა შვა იშ-ჰოდი, აბიყეზერი და მახლა.",
			"19": "<sup>19</sup> შემიდაიანები იყვნენ: ახიანი, შექემი, ლიკხი და ანიყამი.",
			"20": "<sup>20</sup> ეფრემიანები: შუთალახი, მისი ძე ბერედი, მისი ძე თახათი, მისი ძე ელყადა, მისი ძე თახათი,",
			"21": "<sup>21</sup> მისი ძე ზაბადი, მისი ძე შუთელახი, ყეზერი და ელყადი. ისინი იმ ქვეყნის მკვიდრმა გეთელებმა ამოხოცეს, რადგან ისინი საქონლის წასართმევად დაესხნენ თავს.",
			"22": "<sup>22</sup> დიდხანს გლოვობდა ეფრემი, მათი მამა. მის სანუგეშებლად მისი ძმები მოვიდნენ.",
			"23": "<sup>23</sup> მიუდგა იგი თავის ცოლს, დაორსულდა იგი და ბიჭი გაუჩნდა; სახელად ბერიყა დაარქვეს, რადგან მის სახლში უბედურება დატრიალდა.",
			"24": "<sup>24</sup> შეერა მისი და იყო. ააშენა მან ზემო და ქვემო ბეთ-ჰორონი და ყუზენ-შეერა.",
			"25": "<sup>25</sup> მისი ძე იყო რეფახი, რეშეფი, მისი ძე თელახი და მისი ძე თახანი,",
			"26": "<sup>26</sup> მისი ძე ლადანი, მისი ძე ყამიჰუდი, მისი ძე ელიშამაყი,",
			"27": "<sup>27</sup> მისი ძე ნავე, მისი ძე იესო.",
			"28": "<sup>28</sup> მათი სამფლობელო და საცხოვრებელი იყო: ბეთ-ელი და მისი დაბა-სოფლები; აღმოსავლეთით ნაყარანი და დასავლეთით გეზერი თავისი დაბა-სოფლებით, შექემი თავისი დაბა-სოფლებით, ღაზამდე და მის დაბა-სოფლებამდე.",
			"29": "<sup>29</sup> მენაშეანთა ხელში იყო: ბეთ-შუანი თავისი დაბა-სოფლებით, თაყანი თავისი დაბა-სოფლებით, მეგიდო თავისი დაბა-სოფლებით, დორი თავისი დაბა-სოფლებით. ამ ქალაქებში ცხოვრობდნენ იოსებ ისრაელის ძის ძეები.",
			"30": "<sup>30</sup> აშერიანები: იმნა, იშვა, იშვი, ბერიყა და მათი და სერახი.",
			"31": "<sup>31</sup> ბერიყიანები: ხებერი და მალქიელი - ბირზავითის მამა.",
			"32": "<sup>32</sup> ხებერმა შვა იფლატი, შომერი, ხოთამი და მათი და შუყა.",
			"33": "<sup>33</sup> იაფლეტიანები: ფასაქი ბიმჰალი და ყაშვათი. ესენი არიან იაფლეტის ძენი.",
			"34": "<sup>34</sup> შამარიანები: ახი, როჰგა, იეხუბა და არამი.",
			"35": "<sup>35</sup> მისი ძმის, ჰელეგის ძენი: ცოფახი იმნაყი, შელეში და ყამალი.",
			"36": "<sup>36</sup> ცოფახიანები: სუახი, ხარნეფერი, შუყალი, ბერი და იმრა.",
			"37": "<sup>37</sup> ბეცერი და ჰოდი, შამა და შილმა, ითრანი და ბეერა.",
			"38": "<sup>38</sup> იეთერიანები: იეფუნა, ფისფა და არაა.",
			"39": "<sup>39</sup> ყულიანები: არახი, ხანიელი და რიცია.",
			"40": "<sup>40</sup> ყველა ესენი აშერიანები არიან, მამისსახლთა თავკაცები, რჩეული და ძლიერი მებრძოლები, წინამძღოლების თავკაცები. მათ საგვარტომო ნუსხაში რიცხვით ოცდაექვსი ათასი ომში მებრძოლი კაცი სწერია.",
		},
		"8": {
			"1": "<sup>1</sup> ბენიამინმა შვა თავისი პირმშო ბელაყი, მეორე - აშბელი, მესამე - ახრახი,",
			"2": "<sup>2</sup> მეოთხე - ნოხა, მეხუთე - რაფა.",
			"3": "<sup>3</sup> ბალაყს ჰყავდა ძეები: ადარი, გერა, აბიჰუდი,",
			"4": "<sup>4</sup> აბიშუაყი, ნაყამანი, ახოახი,",
			"5": "<sup>5</sup> გერა, შეფუფანი, ხურამი.",
			"6": "<sup>6</sup> ესენი არიან ახუდის ძენი - მამისსახლთა თავკაცები, რომელნიც გებაყში ცხოვრობდნენ და მანახათში გადაასახლეს.",
			"7": "<sup>7</sup> ნაყამანი, ახია და გერა: რომელმაც გადაასახლა ისინი. მას შეეძინა ყუზა და ახიხუდი.",
			"8": "<sup>8</sup> შახარაიმს მოაბის ველზე შეეძინა შვილები მას შემდეგ, რაც თავისი ცოლები, ხუშიმი და ბაყარა გაუშვა.",
			"9": "<sup>9</sup> ხოდეშისგან, თავისი ცოლისგან, მას შეეძინა იობაბი, ციბია, მეშა, მალქამი,",
			"10": "<sup>10</sup> იეყუცი, საქია და მირმა. ესენი არიან მამისსახლთა თავკაცები.",
			"11": "<sup>11</sup> მეხუშიბმა შეა აბიტუბი და ელფაყალი.",
			"12": "<sup>12</sup> ელფაყალიანები: ყებერი, მიშყამი, შემერი. მან ააშენა ონო და ლოდი თავისი დაბა- სოფლებით.",
			"13": "<sup>13</sup> ბერიყა და შემაყი მამისსახლთა თავკაცები იყვნენ და აიალონში ცხოვრობდნენ, მათ განდევნეს გათელები.",
			"14": "<sup>14</sup> ახიო, შაშაკი, ირემოთი.",
			"15": "<sup>15</sup> ზებადია, ყარადი, ყადერი,",
			"16": "<sup>16</sup> მიქაელი, იშფა, იოხა-ბერიყას ძენი.",
			"17": "<sup>17</sup> ზებადია, მეშულამი, ხიზკი, ხაბერი,",
			"18": "<sup>18</sup> იშმერია, იზლია, იობაბი - ელფაყალის ძენი,",
			"19": "<sup>19</sup> იაკიმი, ზიქორი, ზაბდი,",
			"20": "<sup>20</sup> ელიყენაი, ცილთაი, ელიელი,",
			"21": "<sup>21</sup> ყადაია, ბერაია - შიმრათი-შიმყის ძენი.",
			"22": "<sup>22</sup> იშფანი, ყებერი, ელიელი,",
			"23": "<sup>23</sup> ყაბდონი, ზიქრი, ხანანი.",
			"24": "<sup>24</sup> ხანანია, ყელამი, ყანთოთია,",
			"25": "<sup>25</sup> იფდეია, ფენუელი - შაშაკის ძენი.",
			"26": "<sup>26</sup> შამშერაი, შეხარია, ღოთალია,",
			"27": "<sup>27</sup> იაყარეშია, ელია - ზიქურ-იეროხამის ძენი.",
			"28": "<sup>28</sup> ესენი არიან მამისსახლთა თავკაცები, მთავარნი თავთავიანთ გვარ-ტომში. ესენი იერუსალიმში ცხოვრობდნენ.",
			"29": "<sup>29</sup> გიბყონში ცხოვრობდნენ: გიბყონის მამა, რომლის ცოლსაც მაყაქა ერქვა.",
			"30": "<sup>30</sup> მისი ძე და პირმშო ყაბდონი, ცური, კიში, ბაყალი, ნადაბი,",
			"31": "<sup>31</sup> გედორი, ახიო, ზაქერი.",
			"32": "<sup>32</sup> მიკლოთმა შვა შიმეა. ისინი იერუსალიმში თავიანთი ძმების გვერდით ცხოვრობდნენ, ძმებთან ერთად.",
			"33": "<sup>33</sup> ნერმა შვა კიში, კიშმა შვა საული, საულმა შვა იონათანი, მალქიშუაყი, აბინადაბი და ეშბაყალი.",
			"34": "<sup>34</sup> იონათანის ძე მერიბ-ბაყალი იყო. მერიბ-ბაყალმა შვა მიქა.",
			"35": "<sup>35</sup> მიქას ძენი: ფითონი, მელექი, თარეაყი, ახაზი.",
			"36": "<sup>36</sup> ახაზმა შვა იეჰოყადა, იეჰოყადამ შვა ყალემეთი, ყაზმავეთი და ზიმრი. ზიმრიმ შვა მოცა,",
			"37": "<sup>37</sup> მოცამ შვა ბინყა. მისი ძე იყო რაფა, მისი ძე იყო ელაყასა, მისი ძე იყო აცელი.",
			"38": "<sup>38</sup> აცელს ექვსი ვაჟი ჰყავდა. აჰა, მათი სახელები: ყაზრიყამი, ბოქრუ, იშმაყელი, შეყარია, ყობადია და ხანანი. ყველა ესენი აცელის ძენი არიან.",
			"39": "<sup>39</sup> მისი ძმის, ყეშეკის ძენი: მისი პირმშო ულამი, მეორე - იეყუში, მესამე - ელიფელეტი.",
			"40": "<sup>40</sup> ულამის ძენი მაგარი ვაჟკაცები, მშვილდოსნები და ასორმოცდაათი შვილისა და შვილიშვილის პატრონები იყვნენ. ყველა ესენი ბენიამინიანთაგან იყვნენ.",
		},
		"9": {
			"1": "<sup>1</sup> ყველა ისრაელიანი აღნუსხეს და ისრაელის მეფეთა წიგნში ჩაწერეს. იუდაელები კი ბაბილონს გაასახლეს მათი უკეთურების გამო.",
			"2": "<sup>2</sup> პირველი მკვიდრნი, თავიანთ სამფლობელოებსა და ქალაქებში რომ ცხოვრობდნენ, იყვნენ: ისრაელიანები, მღვდლები, ლევიანები, შეწირულნი და ტაძარში მომსახურენი.",
			"3": "<sup>3</sup> იერუსალიმში იუდას, ბენიამინის, ეფრემისა და მენაშეს ძეთაგან ცხოვრობდნენ.",
			"4": "<sup>4</sup> ყუთაი, ძე ყამიჰუდისა, ძე ყომრისა, ძე იმრისა, ძე ბანისა, ფერეც იუდას ძის შვილთაგან.",
			"5": "<sup>5</sup> შილონისგან იყვნენ: მისი პირმშო ყასაია და მისი ძენი.",
			"6": "<sup>6</sup> ზერახიანთაგან: იეყუელი და მისი ძმები - ექვსას ოთხმოცდაათნი;",
			"7": "<sup>7</sup> ბენიამინიანთაგან: სალუ, ძე მეშელამისა, ძე ჰოდავიასი, ძე ჰასენუასი.",
			"8": "<sup>8</sup> იბნა იეროხამის ძე, ელა, ძე ყუზისა, მიქრის ძისა, მეშელამი, ძე შაფატიასი, ძე რეღუელისა, ძე იბნიასი.",
			"9": "<sup>9</sup> მათი ძმები თავ-თავიანთი მოდგმის მიხედვით ცხრაას ორმოცდათექვსმეტნი იყვნენ. ყველა ეს ვაჟკაცი მამისსახლის თავკაცი იყო.",
			"10": "<sup>10</sup> მღვდელთაგან იყვნენ: იედაყია, იეჰოიარიბი, იაქინი,",
			"11": "<sup>11</sup> ყაზარია, ძე ხილკიასი, ძე მეშულამისა, ძე ცადოკისა, ძე მერაიოთისა, ძე ახიტუბისა - ღვთის სახლის წინამძღვარი;",
			"12": "<sup>12</sup> ყადაია, ძე იეროხამისა, ძე ფაშხურისა, ძე მალქიასი, და მაყასაი ძე ყადიელისა, ძე იახზერასი, ძე მეშელამისა, ძე მეშილემითისა, ძე იმრისა.",
			"13": "<sup>13</sup> მათი ძმები, მამისსახლთა თავკაცები, ათას შვიდას სამოცნი, ღვთის სახლის სამსახურის საქმეში ნაცადი ხალხი.",
			"14": "<sup>14</sup> ლევიანთაგან: შემაყია ძე ხაშუბისა, ძე ყაზრიყამისა, ძე ხაშაბიასი - მერარას ძეთაგან,",
			"15": "<sup>15</sup> ბაკებაკარი, ხერეში, გალალი, მათანია ძე მიქასი, ძე ზაქრისა, ძე ასფისა,",
			"16": "<sup>16</sup> ყობადია, შემაყია ძე გალალისა, ძე იედუთუნისა, ბერაქია ძე ასასი, ძე ელკანასი - ნეტოფათელთა დაბა-სოფლებში მცხოვრებნი.",
			"17": "<sup>17</sup> კარის მცველნი: შალუმი, ყაკუბი, ტალმონი, ახიმანი და ძმანი მისნი. შალუმი თავკაცი იყო",
			"18": "<sup>18</sup> და დღემდე ის დგას სამეფო კარიბჭეში, აღმოსავლეთით. ესენი არიან კარისმცველნი ლევიანთა ბანაკიდან.",
			"19": "<sup>19</sup> შალუმი, ძე კორესი, ძისა აბიასაფისა, ძისა კორახისა, და მისი ძმები, მისი მამისსახლიდან - კორახელნი, სამსახურს ეწეოდნენ და კარვის ზღურბლებს იცავდნენ, ხოლო მათი მამები უფლის ბანაკთან მისასვლელს დარაჯობდნენ.",
			"20": "<sup>20</sup> ფინხამ ელყაზარის ძე უწინ მათი წინამძღოლი იყო და უფალიც მასთან იყო.",
			"21": "<sup>21</sup> ზაქარია მეშელემელის ძე სადღესასწაულო კარვის კარის მცველი იყო.",
			"22": "<sup>22</sup> ზღურბლების მცველებად ამორჩეულნი მთლიანად ორასთორმეტნი იყვნენ, ისინი საგვარტომო ნუსხაში თავ-თავიანთი დაბა-სოფლების მიხედვით არიან ჩაწერილნი. ისინი დავითმა და სამუელ მისანმა დააყენეს მათი ერთგულების გამო.",
			"23": "<sup>23</sup> ისინი თავიანთ ძეებთან ერთად დარაჯობდნენ უფლის სახლის კარიბჭეებს, კარვის სახლს.",
			"24": "<sup>24</sup> კარისმცველები ოთხივ მხრივ იდგნენ: აღმოსავლეთით, დასავლეთით, ჩრდილოეთით და სამხრეთით.",
			"25": "<sup>25</sup> მათი ძმები თავიანთ დაბა-სოფლებში იყვნენ და დრო და დრო, შვიდდღე გამოშვებით უნდა მისულიყვნენ მათთან.",
			"26": "<sup>26</sup> რადგან კარისმცველთა ის ოთხი მეთაური, რომლებიც ლევიანები იყვნენ, მუდამ ოთახებთან და ღვთის სახლის საგანძურებთან იდგნენ.",
			"27": "<sup>27</sup> ისინი ღვთის სახლის გარშემო ათევდნენ ღამეს, რადგან მათ ებარათ დაცვა და ყოველ დილით კარიბჭის გაღება.",
			"28": "<sup>28</sup> ერთ მათგანს სამსახურებელი ჭურჭელი ებარა, ვინაიდან თვლით შეჰქონდათ და თვლით გაჰქონდათ.",
			"29": "<sup>29</sup> სხვებს ჩაბარდათ სხვა ჭურჭლეული, ყველა წმიდა ჭურჭელი, გამტკიცული ფქვილი, ღვინო, ზეთი, გუნდრუკი და სურნელებანი.",
			"30": "<sup>30</sup> სამღვდელონი კი მირონს ამზადებდნენ ნელსაცხებლისგან.",
			"31": "<sup>31</sup> მათითიას, ლევიანთაგან ერთ-ერთს, შალუმ კორახელის პირმშოს ტაფებზე მომზადება მიენდო.",
			"32": "<sup>32</sup> ზოგიერთ მათ ძმათაგანს, კეჰათიანთაგან, საწირი პური ჩააბარეს, რათა შაბათობით მოემზადებინათ.",
			"33": "<sup>33</sup> ხოლო მგალობელნი, ლევიანთა მამისსახლების თავკაცნი, ტაძრის ოთახებში საქმიანობისგან განთავისუფლებულნი იყვნენ, რადგან ისინი დღედაღამ თავიანთი საქმით იყვნენ დაკავებულნი.",
			"34": "<sup>34</sup> ესენი არიან ლევიანთა მამისსახლების თავკაცები, თავიანთი მოდგმის თავკაცები. ისინი იერუსალიმში ცხოვრობდნენ.",
			"35": "<sup>35</sup> გაბაონში ცხოვრობდნენ: გაბაონის მამა იეყიელი, მის ცოლს სახელად მაყაქა ერქვა.",
			"36": "<sup>36</sup> მისი ძე - პირმშო ყაბდონი, მერე ცური, კიში, ბაყალი, ნერი, ნედაბი,",
			"37": "<sup>37</sup> გედორი, ახიო, ზაქარია და მიკლოთი.",
			"38": "<sup>38</sup> მიკლოთმა შვა შიმამი. ისინი თავიანთი ძმების მეზობლად, თავიანთ ძმებთან ერთად ცხოვრობდნენ იერუსალიმში.",
			"39": "<sup>39</sup> ნერმა შვა კიში, კიშმა შვა საული, საულმა შვა იონათანი, მალქიშუაყი, აბინადაბი და ეშბაყალი.",
			"40": "<sup>40</sup> იონათანის ძე იყო მერიბ-ბაყალი. მერიბ-ბაყალმა შვა მიქა.",
			"41": "<sup>41</sup> მიქას ძენი: ფითონი, მელექი და თახრეაყი.",
			"42": "<sup>42</sup> ახაზმა შვა იაყრა, იაყრამ შვა ყალემეთი, ყაზმავეთი და ზიმრი; ზიმრიმ შვა მოცა,",
			"43": "<sup>43</sup> მოცამ შვა ბინყა, მისი ძე რეფაია, მისი ძე ელყასა, მისი ძე აცელი.",
			"44": "<sup>44</sup> აცელს ექვსი ძე ჰყავდა. აჰა, მათი სახელები: ყაზრიკამი, ბოქრუ, იშმაყელი, შეყარია, ყობადია, ხანანი. ესენი არიან აცალის ძენი.",
		},
		"10": {
			"1": "<sup>1</sup> გაუმართეს ბრძოლა ფილისტიმელებმა ისრაელს და გაექცნენ ისრაელიანები ფილისტიმელებს. გილბოაყის მთაზე ეცემოდნენ დახოცილნი.",
			"2": "<sup>2</sup> მიეწივნენ ფილისტიმელები საულს და მის ვაჟებს და დახოცეს ფილისტიმელებმა იონათანი, აბინადაბი და მალქიშუაყი - საულის შვილები.",
			"3": "<sup>3</sup> გაუძნელდა ბრძოლა საულს. ნიშანში ამოიღეს იგი მშვილდოსნებმა და მძიმედ დაჭრეს.",
			"4": "<sup>4</sup> ურხრა საულმა თავის საჭურველთმტვირთველს: იშიშვლე მახვილი და გამიყარე, იმ წინადაუცვეთელებმა რომ არ შეურაცხმყონ. ვერ გაბედა საჭურველთმტვირთველმა, რადგან ძალიან ეშინოდა. მაშინ საულმა აიღო მახვილი და ზედ დაეგო.",
			"5": "<sup>5</sup> როცა დაინახა საჭურველთმტვირთველმა, რომ მოკვდა საული, ისიც ზედ დაეგო მახვილს და მოკვდა.",
			"6": "<sup>6</sup> ერთად დაიხოცნენ საული, მისი სამი ვაჟიშვილი და მთელი მისი სახლი.",
			"7": "<sup>7</sup> როდესაც ველზე მყოფმა ისრაელიანებმა დაინახეს, რომ უკუიქცნენ ისრაელიანები და დაიხოცნენ საული და მისი ვაჟიშვილები, დადაგეს ქალები და გადაიხვეწნენ. მოვიდნენ ფილისტიმელები და ჩასახლდნენ იქ.",
			"8": "<sup>8</sup> მეორე დღეს მოვიდნენ ფილისტიმელები დახოცილთა გასაძარცვავად და, როცა ნახეს გილბოაყის მთაზე დაცემული საული და მისი სამი ვაჟიშვილი,",
			"9": "<sup>9</sup> გაძარცვეს იგი, აიღეს მისი თავი და საჭურველი და მოატარეს ფილისტიმელთა მთელი ქვეყნის ირგვლივ, რათა ეხარებინათ თავიანთი საკერპოებისთვის და ხალხისთვის.",
			"10": "<sup>10</sup> მისი საჭურველი თავიანთი ღმერთის ტაძარში დადეს, ხოლო მისი თავისქალა დაგონის სახლზე მიაჭედეს.",
			"11": "<sup>11</sup> როცა გაიგეს გალაადის იაბეშელებმა, როგორ მოექცნენ ფილისტიმელნი საულს,",
			"12": "<sup>12</sup> ადგა ყველა, ვინც ვაჟკაცი იყო, წამოიღეს საულისა და მისი ვაჟიშვილების გვამები, მოიტანეს იაბეშში და დამარხეს მათი ძვლები იაბეშში, მუხის ქვეშ. მერე შვიდი დღე მარხულობდნენ.",
			"13": "<sup>13</sup> მოკვდა საული თავისი ორგულობის გამო, რადგან ღალატობდა იგი უფალს, უფლის სიტყვას არ იცავდა და გამოსაკითხად მესულთანეს მიმართავდა.",
			"14": "<sup>14</sup> უფალს არ ეკითხებოდა. ამიტომ მოკლა იგი უფალმა და მისი სამეფო დავითს, იესეს ძეს მისცა.",
		},
		"11": {
			"1": "<sup>1</sup> მოვიდნენ ისრაელიანები დავითთან ხებრონს და უთხრეს: აჰა, შენი ძვალი და ხორცი ვართ.",
			"2": "<sup>2</sup> გუშინაც და იმის უწინაც, როცა საული მეფობდა, შენ მიგყავდა და მოგყავდა ისრაელი. უფალმა, შენმა ღმერთმა გითხრა: შენ დამწყემსავ ჩემს ერს, ისრაელს, შენ იქნები ჩემი ერის, ისრაელის მთავარი.",
			"3": "<sup>3</sup> მოვიდა ისრაელის მთელი უხუცესობა მეფესთან ხებრონს და შეჰკრა დავითმა მათთან კავშირი ხებრონში უფლის წინაშე და სცხეს ისრაელის მეფედ დავითი, უფლის სიტყვის თანახმად, სამუელის ხელით.",
			"4": "<sup>4</sup> გაილაშქრეს დავითმა და ისრაელიანებმა იერუსალიმისკენ, ანუ იებუსისკენ. იქ იყვნენ იებუსელნი - იმ ქვეყნის მკვიდრნი.",
			"5": "<sup>5</sup> უთხრეს იებუსის მკვიდრებმა დავითს, აქ ვერ შემოხვალო. მაგრამ აიღო დავითმა სიონის ციხე-სიმაგრე. ეს არის დავითის ქალაქი.",
			"6": "<sup>6</sup> თქვა დავითმა, ვინც ყველაზე უწინ შემუსრავს იებუსელთ, ის გახდებაო თავკაცი და სარდალი. ყველაზე უწინ იოაბ ცერუიას ძე ავიდა და გახდა სარდალი.",
			"7": "<sup>7</sup> დადგა დავითი ციხე-სიმაგრეში, ამიტომ უწოდეს მას დავითის ქალაქი.",
			"8": "<sup>8</sup> მან შემოაშენა ქალაქი გარშემო, მილოდან შემოგარენამდე. იოაბმა კი ძველი ქალაქი აღადგინა.",
			"9": "<sup>9</sup> სულ მაღლა და მაღლა მიდიოდა დავითი და ცაბაოთ უფალი მასთან იყო.",
			"10": "<sup>10</sup> აჰა, დავითის მოყმეთა მთავარნი, რომელნიც მხარს უმაგრებდნენ მას მის სამეფოში ისრაელთან ერთად, მის გასამეფებლად, უფლის სიტყვისამებრ.",
			"11": "<sup>11</sup> აჰა, დავითის მოყმეთა რიცხვი: იაშაბყამ ხაქმონის ძე, ოცდაათეულის თავკაცი; მან თავისი შუბის ერთი მოქნევით სამასი კაცი მოკლა.",
			"12": "<sup>12</sup> მას შემდეგ ელეზარი ძე დოდოსი, ახოხელი, ერთერთი იმ სამ მოყმეთაგანი.",
			"13": "<sup>13</sup> იგი დავითს ახლდა ფას-დამიმში, სადაც ფილისტიმელნი საომრად შეიყარნენ (იყო იქ ყანის ნაკვეთი, სადაც ქერი ეთესა) და უკუიქცა ხალხი ფილისტიმელთაგან.",
			"14": "<sup>14</sup> ისინი ჩადგნენ შუა ყანაში, დაიცვეს იგი და მუსრი გაავლეს ფილისტიმელებს. მაშინ დიდი ხსნა მოუტანა მათ უფალმა.",
			"15": "<sup>15</sup> ოცდაათი თავკაციდან ეს სამნი ავიდნენ კლდეზე დავითთან, ყადულამის გამოქვაბულში. ხოლო ფილისტიმელთა ურდო რეფაიმის ველზე იყო დაბანაკებული.",
			"16": "<sup>16</sup> მაშინ დავითი საფარში იყო, ხოლო ფილისტიმელთა ყარაული ბეთლემში იდგა.",
			"17": "<sup>17</sup> ინება დავითმა და თქვა: ვინ დამალევინებს ბეთლემის ჭის წყალს, კარიბჭესთან რომ არის?",
			"18": "<sup>18</sup> გაარღვია ამ სამმა ფილისტიმელთა ბანაკი, ამოიღეს წყალი ბეთლემის ჭიდან, კარიბჭესთან რომ არის, წამოიღეს და დავითს მოუტანეს, მაგრამ არ ინდომა დავითმა მისი დალევა და უფალს დაუღვარა.",
			"19": "<sup>19</sup> თქვა: შორს იყოს ჩემგან, უფალო, ასეთი საქმე! ნუმც დამელიოს ამ ვაჟკაცთა სისხლი, თავის გასაწირად რომ წავიდნენ! აღარ ინდომა დავითმა წყლის დალევა. ასე მოიქცა ეს სამი მოყმე.",
			"20": "<sup>20</sup> აბშაი, იაბის ძმა, ამ სამის უფროსი იყო. მან სამას კაცს მოუქნია შუბი და განგმირა. მას მეტი სახელი ჰქონდა ამ სამში.",
			"21": "<sup>21</sup> ამ სამზე მეტად იყო პატივდებული და მათ მეთაურობდა, მაგრამ იმ პირველ სამს ვერ უტოლდებოდა.",
			"22": "<sup>22</sup> ბენაია იეჰოიადაყის ძე მაგარი ვაჟკაცი იყო, დიდ საქმეთა მქმნელი, კაბცეელიდან. მან დაამარცხა ორი მოაბელი ლომკაცი; ის ჩავიდა ორმოში და მოკლა ლომი თოვლიან დღეს.",
			"23": "<sup>23</sup> მანვე მოკლა ეგვიპტელი კაცი, ხუთი წყრთის სიმაღლისა; შუბი ისე ეჭირა ეგვიპტელს ხელში, როგორც ფეიქარს საქსოვი ლილვი. ჩაუხტა კეტით, ხელიდან გამოსტაცა ეგვიპტელს შუბი და მისივე შუბით განგმირა.",
			"24": "<sup>24</sup> ეს გააკეთა ბენაია იეჰოიადაყის ძემ და სახელი მოიხვეჭა სამ მოყმეს შორის.",
			"25": "<sup>25</sup> ოცდაათისგან გამოირჩეოდა, მაგრამ სამს ვერ უტოლდებოდა. თავის მცველთა მეთაურად დანიშნა იგი დავითმა.",
			"26": "<sup>26</sup> მებრძოლთა მეთაურნი იყვნენ: ყასაელი, იოაბის ძმა, ელხანან დოდოს ძე, ბეთლემელი,",
			"27": "<sup>27</sup> შამოთ ჰაროლელი, ხალეც ფელონელი,",
			"28": "<sup>28</sup> ყირა ყიკეშის ძე, თეკოყელი, აბიყეზერ ყანათოთელი,",
			"29": "<sup>29</sup> სიბქაი ხუშათელი, ყილაი ახოხელი,",
			"30": "<sup>30</sup> მაჰარაი ნეტოფათელი, ხელედ ბაყანას ძე, ნეტოფათელი,",
			"31": "<sup>31</sup> ითაი რიბაის ძე, ბენიამინის გიბყადან, ბენაია ფარყათონელი,",
			"32": "<sup>32</sup> ხურაი ნახალე-გაყასელი, აბიელ ყარბათელი,",
			"33": "<sup>33</sup> ყაზმავეთ ბახარუმელი, ელიახბა შაყალბონელი.",
			"34": "<sup>34</sup> ჰაშემ გიზონელის ძენი: იონათან შაგეს ძე, ჰარარელი,",
			"35": "<sup>35</sup> ახიამ საქარის ძე, ჰარარელი, ელიფალ ურის ძე,",
			"36": "<sup>36</sup> ხეფერ მექერათელი, ახია ფელონელი,",
			"37": "<sup>37</sup> ხეცრო ქარმელელი, ნაყარაი ეზბაის ძე,",
			"38": "<sup>38</sup> იოელი, ნათანის ძმა, მიბხარ გერის ძე,",
			"39": "<sup>39</sup> ცელეკ ყამონელი, ნახრაი ბეროთელი, იოაბ ცერუიას ძის საჭურველთმტვირთველი,",
			"40": "<sup>40</sup> ყირა ითრელი, გარებ ითრელი,",
			"41": "<sup>41</sup> ურია ხეთელი, ზაბად ახლაის ძე.",
			"42": "<sup>42</sup> ყადინა შიზას ძე, რეუბენელი, რეუბენიანთა თავკაცი და ოცდაათი მასთან ერთად.",
			"43": "<sup>43</sup> ხანან მაყაქას ძე, იოშაფატ მითნელი,",
			"44": "<sup>44</sup> ყუზია ყაშთერათელი, შამაყი და იეთიელი - ხოთამის ძენი, ყაროყერელნი.",
			"45": "<sup>45</sup> იედიყაელ შიმრის ძე, მისი ძმა იოხა თიციელი,",
			"46": "<sup>46</sup> ელიელ მახაველი, ირიბაი, იოშავია - ელნაყარის ძენი და ითმა მოაბელი.",
			"47": "<sup>47</sup> ელიელი, ყობედ და იაყისელ მეცობაელნი.",
		},
		"12": {
			"1": "<sup>1</sup> აჰა, ისინი, ციკლაგში რომ მივიდნენ დავითთან, როცა იგი ჯერ კიდევ საულ კიშის ძეს ემალებოდა, ისინი ომში შემწე მოყმეთა შორის იყვნენ,",
			"2": "<sup>2</sup> მშვილდ-ისრით შეიარაღებულნი, მარჯვენა-მარცხენით ქვისა და მშვილდ-ისრის მსროლელნი, საულის ძმათაგანნი, ბენიამინიდან:",
			"3": "<sup>3</sup> მეთაური ახიყეზერი და იოაში - შემაყა გიბყათელის ძენი, იზიელი და ფელეტი - ყაზმავეთის ძენი, ბერაქა და იეჰუ ყანათოთელნი,",
			"4": "<sup>4</sup> იშმაყია გაბაონელი, ერთ-ერთი მოყმე ოცდაათში და ოცდაათის მეთაური, იერემია, იახაზიელი, იოხანანი, იოზაბედ გედერათელი,",
			"5": "<sup>5</sup> ელყუზაი, ირიმოთი, ბეყალია, შემარიაჰუ, შეფატიაჰუ ხარუფელი,",
			"6": "<sup>6</sup> ელკანა, იშიაჰუ, ყაზარელი, იოყეზერი, იაშაბყამი - კორახელნი,",
			"7": "<sup>7</sup> იოყელა და ზებადია - იეროხამ გედორელის ძენი.",
			"8": "<sup>8</sup> გადელებს ჩამოშორდნენ და დავითთან უდაბნოს საფარში მივიდნენ მამაცი მოყმენი, საომრად გაწვრთნილი ვაჟკაცები, ფარ-შუბით შეიარაღებულნი, სახით ლომის მსგავსნი და მთის არჩვივით სწრაფნი.",
			"9": "<sup>9</sup> მეთაური ყეზერი, მეორე - ყობადია, მესამე - ელიაბი,",
			"10": "<sup>10</sup> მეოთხე - შიშმანა, მეხუთე - იერემია,",
			"11": "<sup>11</sup> მეექვსე - ყათაი, მეშვიდე - ელიელი,",
			"12": "<sup>12</sup> მერვე - იოხანანი, მეცხრე - ელზაბადი,",
			"13": "<sup>13</sup> მეათე - იერემიაჰუ, მეთერთმეტე - მაქბანაი.",
			"14": "<sup>14</sup> ესენი იყვნენ გადის ძეთაგანნი, მხედრობის მეთაურნი. ყველაზე ნაკლები ერთი ასს უმკლავდებოდა და ყველაზე დიდი - ათასს.",
			"15": "<sup>15</sup> მათ პირველ თვეში გადალახეს იორდანე, როდესაც ადიდებული მდინარე ნაპირებიდან გადმოდიოდა. მათ აღმოსავლეთითა და დასავლეთით განდევნეს ამ ველის მკვიდრნი.",
			"16": "<sup>16</sup> ბენიამინისა და იუდას ძეთაგანაც მოვიდნენ დავითთან საფარში.",
			"17": "<sup>17</sup> გამოეგება მათ დავითი, გამოელაპარაკა და უთხრა: თუ სამშვიდობოდ და დამხმარედ მოსულხართ ჩემთან, გიერთგულებთ; თუ იმისთვის მოხვედით, რომ ჩემი მტრის ხელში მზაკვრულად მიმცეთ, აჰა, ჩემი ხელი სუფთაა, დაე, ჩვენი მამა- პაპის ღმერთმა იხილოს ეს და განსაჯოს.",
			"18": "<sup>18</sup> მაშინ სულმა მოიცვა ყამასაი, ოცდაათის მეთაური და თქვა: მშვიდობა შენდა, დავით, იესეს ძეო, მშვიდობა შენ და მშვიდობა შენს შემწეთა, რადგან ღმერთია შენი შემწე. მიიღო ისინი დავითმა და რაზმის მეთაურობა ჩააბარა.",
			"19": "<sup>19</sup> მენაშესგანაც მიემხრნენ დავითს, როდესაც იგი ფილისტიმელებით მიდიოდა საულთან საომრად, მაგრამ არ მიხმარებიან ისინი, რადგან ფილისტიმელთა მთავრები შეითქვნენ და უკან გამოაბრუნეს დავითი: თქვეს, ჩვენდა საზიანოდ თავის ბატონ საულს მიემხრობაო.",
			"20": "<sup>20</sup> როდესაც ციკლაგს მივიდა, მენაშესგან შეუერთდნენ: ყადნახი, იოზაბადი, იდიყაელი, მიქაელი, იოზაბადი, ელიაჰუ, ცილათი - მენაშელთა ასისთავები.",
			"21": "<sup>21</sup> ისინი ეხმარებოდნენ დავითს ურდოთა წინააღმდეგ ბრძოლაში, რადგან ყველანი მამაცი მოყმენი და ჯარის მეთაურები იყვნენ.",
			"22": "<sup>22</sup> დღიდან დღემდე მიედინებოდნენ დავითთან და ეხმარებოდნენ, ვიდრე მისი ლაშქარი ღვთის მხედრობასავით არ გაიზარდა.",
			"23": "<sup>23</sup> აჰა, რიცხვი საომრად შეჭურვილ მეთაურთა, რომელნიც დავითთან ხებრონში მივიდნენ, რათა უფლის სიტყვისამებრ, საულის სამეფო გადაეცათ მისთვის.",
			"24": "<sup>24</sup> იუდიანები, ფარ-შუბის მტვირთველნი, ექვსიათას რვაასნი იყვნენ, საომრად აღჭურვილნი.",
			"25": "<sup>25</sup> სიმონიანთაგან შვიდიათას ასი მამაცი მოყმე იყო.",
			"26": "<sup>26</sup> ლევიანთაგან - ოთხიათას ექვსასი.",
			"27": "<sup>27</sup> ხოლო იეჰოიადაყს, აარონელთა წინამძღოლს, თან სამიათას შვიდასი ახლდა.",
			"28": "<sup>28</sup> ცადოკს, მამაც ყმაწვილს, და მის მამისსახლს ოცდაორი მეთაური ჰყავდა.",
			"29": "<sup>29</sup> საულის ძმები ბენიამინიანთაგან სამიათასნი იყვნენ. მათგან დიდი წილი საულის სახლში დარჩა.",
			"30": "<sup>30</sup> ეფრემიანები ოციათას რვაასნი იყვნენ - მამაცი მოყმენი და მამისსახლთა სახელოვანი ვაჟკაცები.",
			"31": "<sup>31</sup> მენაშეს ტომის ნახევრიდან თვრამეტი ათასნი იყვნენ, რომელნიც სახელდებით გამოიხმეს, რათა დავითის გასამეფებლად მოსულიყვნენ.",
			"32": "<sup>32</sup> ისაქარიანთაგან ორასი მეთაური იყო, რომელთაც იცოდნენ დრო-ჟამთა გულისხმისყოფა და უწყოდნენ, თუ რა უნდა გაეკეთებინა ისრაელს. ყველა მათი ძმა მათი დარიგებით იქცეოდა.",
			"33": "<sup>33</sup> ზაბულუნიდან საბრძოლველად გამოდიოდნენ და საომრად იყვნენ დარაზმულნი ყველანაირი საბრძოლო აღჭურვილობით ორმოცდაათი ათასი, რათა უორგუღოდ დახმარებოდნენ.",
			"34": "<sup>34</sup> ნაფთალიდან ათასი სარდალი იყო, ოცდაჩვიდმეტი ათასი ფარ-შუბოსნის თანხლებით.",
			"35": "<sup>35</sup> დანიდან იყვნენ ოცდარვაათას ექვსასნი, საომრად დარაზმულნი.",
			"36": "<sup>36</sup> აშერიდან საბრძოლველად ორმოცი ათასი საომრად დარაზმული გამოდიოდა.",
			"37": "<sup>37</sup> იორდანეს გაღმა მხრიდან გამოდიოდნენ: რეუბენიდან, გადიდან და მენაშეს ტომის ნახევრიდან ყველანაირ საბრძოლო საჭურველით - სულ ასოცი ათასი.",
			"38": "<sup>38</sup> ყველა ეს მებრძოლი ვაჟკაცი მწყობრად დარაზმული, წრფელი გულით მოვიდა ხებრონში დავითის გასამეფებლად მთელს ისრაელზე. ასევე მთელი დანარჩენი ისრაელი ერთსულოვანი იყო დავითის გასამეფებღად.",
			"39": "<sup>39</sup> სამ დღეს დარჩნენ ისინი იქ. ჭამდნენ და სვამდნენ, რადგან მათ ძმებს ყველაფერი მათთვის მოემზადებინათ.",
			"40": "<sup>40</sup> ასევე მათ მეზობლებს, ისაქარის, ზებულუნისა და ნაფთალის ტომებიდანაც კი მოჰქონდათ ვირებით, აქლემებით, ჯორებითა და ხარებით სურსათ-სანოვაგე: ფქვილეული, ლეღვი, ჩირი, ღვინო, ზეთი, უამრავი ხარ-ძროხა და ცხვარი. ეს იმიტომ, რომ სიხარული იყო ისრაელში.",
		},
		"13": {
			"1": "<sup>1</sup> ეთათბირა დავითი ათასისთავებს, ასისთავებს და წინამძღოლებს.",
			"2": "<sup>2</sup> უთხრა დავითმა ისრაელის მთელ კრებულს: თუ თქვენ თანახმა იქნებით და თუ ეს უფლის, ჩვენი ღმერთის ნება იქნება, ხალხი გავუგზავნოთ ისრაელის ყველა მხარეში ჩვენს ძმებს, მღვდლებს, ლევიანებს მათ შემოგარენ ქალაქებში, რათა შემოგვიერთდნენ.",
			"3": "<sup>3</sup> და ჩვენთან გადმოვაბრძანოთ ჩვენი ღვთის კიდობანი, რადგან საულის დროს მისთვის არ მიგვიმართავს.",
			"4": "<sup>4</sup> მიუგო მთელმა კრებულმა: დაე, ასე იყოს, ვინაიდან ეს საქმე მოსაწონია ხალხის თვალში.",
			"5": "<sup>5</sup> შეყარა დავითმა მთელი ისრაელი. ეგვიპტის შიხორიდან ხამათის მისადგომამდე, კირიათ-იეყარიმიდან ღვთის კიდობნის გადმოსაბრძანებლად.",
			"6": "<sup>6</sup> ავიდა დავითი და მთელი ისრაელი ბაყალთაში, კირიათ-იეყარიმში, იუდას რომ ეკუთვნის, რათა იქიდან წამოებრძანებინათ კიდობანი ღვთისა, ქერუბიმებზე მჯდომარე უფლისა, რომელიც ამ სახელით იწოდება.",
			"7": "<sup>7</sup> დაასვენეს ღვთის კიდობანი ახალ ურემზე და წამოაბრძანეს აბინადაბის სახლიდან. ურემს ყუზა და ახიო მიჰყვებოდნენ.",
			"8": "<sup>8</sup> დავითი და მასთან ერთად მთელი ისრაელი როკავდა ღვთის წინაშე მთელი ძალ- ღონით; მღეროდნენ და უკრავდნენ ქნარებს, ბობღნებს, დაფდაფებს, ჩანგებსა და წინწილებს.",
			"9": "<sup>9</sup> როცა ქიდონის კალოსთან მივიდნენ, ყუზამ ხელი შეაშველა კიდობანს, რადგან კინაღამ გადმოაგდეს ხარებმა.",
			"10": "<sup>10</sup> აენთო უფლის რისხვა ყუზაზე და იქვე დასცა იგი ღმერთმა კიდობანზე ხელის წაშველების გამო. და იქვე მოკვდა ღვთის წინაშე.",
			"11": "<sup>11</sup> შეწუხდა დავითი, რომ დაღუპა უფალმა ყუზა, და დღემდე იმ ადგილს ეწოდება ფერეც-ყუზა (ყუზას დაღუპვა).",
			"12": "<sup>12</sup> ღმერთის შეეშინდა დავითს იმ დღეს და თქვა: როგორ მოვა ჩემამდე ღვთის კიდობანი?",
			"13": "<sup>13</sup> არ ინდომა დავითმა უფლის კიდობნის თავისთან მიტანა დავითის ქალაქში და გეთელი ყობედ-ედომის კარ-მიდამოსკენ გადაახვევინა.",
			"14": "<sup>14</sup> სამ თვეს ესვენა ღვთის კიდობანი გეთელი ყობედედომის კარმიდამოში და აკურთხა უფალმა ყობედ-ედომის კარმიდამო, და მთელი მისი საბადებელი.",
		},
		"14": {
			"1": "<sup>1</sup> გამოუგზავნა ხირამმა, ციდონის მეფემ, დავითს მოციქულები და ნაძვის ძელები, კედლის მშენებლები, ხუროები მისთვის სახლის ასაშენებლად.",
			"2": "<sup>2</sup> მიხვდა დავითი, რომ მტკიცედ ჰყავდა დადგენილი იგი უფალს ისრაელის მეფედ და რომ აღამაღლა მისი სამეფო თავისი ერის, ისრაელის გამო.",
			"3": "<sup>3</sup> კიდევ მოიყვანა დავითმა იერუსალიმში ცოლები და კიდევ შეეძინა დავითს ვაჟები და ასულები.",
			"4": "<sup>4</sup> აჰა, იმათი სახელები, რომელნიც იერუსალიმში შეეძინა: შამუაყი, შობაბი, ნათანი, სოლომონი,",
			"5": "<sup>5</sup> იბხარი, ელიშუაყი, ელფალეტი,",
			"6": "<sup>6</sup> ნოგაჰი, გეფეგი, იაფიაყი,",
			"7": "<sup>7</sup> ელიშამაყი, ბეყელიადაყი, ელიფალეტი.",
			"8": "<sup>8</sup> შეიტყვეს ფილისტიმელებმა, რომ მთელი ისრაელის მეფედ აკურთხეს დავითი და დაიძრნენ ფილისტიმელნი დავითის შესაპყრობად. შეიტყო ეს დავითმა და გამოვიდა მათ წინაშე.",
			"9": "<sup>9</sup> მოვიდნენ ფილისტიმელები და რეფაიმის ველზე გაიშალნენ.",
			"10": "<sup>10</sup> დავითი ღმერთს დაეკითხა: გავილაშქრო ფილისტიმელებზე? ჩამიგდებ მათ ხელში? მიუგო მას უფალმა: გაილაშქრე, რადგან ხელში ჩაგიგდებ ფილისტიმელებს.",
			"11": "<sup>11</sup> ავიდნენ ბაყალ-ფერაციმში და იქ სძლია დავითმა და თქვა: წალეკა უფალმა ჩემი მტრები ჩემივე ხელით, როგორც წყალი წალეკავს ხოლმე. ამიტომ ეწოდა ამ ადგილს ბაყალ-ფერაციმი.",
			"12": "<sup>12</sup> იქ მიატოვეს მათ თავიანთი კერპები; დავითმა ბრძანა და ცეცხლში დაწვეს ისინი.",
			"13": "<sup>13</sup> კვლავ გამოილაშქრეს ფილისტიმელებმა და ველზე გაიშალნენ.",
			"14": "<sup>14</sup> კვლავ დაეკითხა დავითი ღმერთს. მიუგო ღმერთმა: უკან ნუ გაეკიდები, გარს მოუარე და თუთების მხრიდან მიადექი.",
			"15": "<sup>15</sup> როგორც კი თუთების კენწეროებიდან შრიალის ხმა შემოგესმება, იმ წამსვე შეიმართე საომრად, რადგან მაშინ წაგიძღვება ღმერთი ფილისტიმელთა ბანაკის შესამუსვრელად.",
			"16": "<sup>16</sup> ისე მოიქცა დავითი, როგორც ღმერთმა დაარიგა და გაბაონიდან გაზრამდე მუსრი გაავლო ფილისტიმელთა ურდოს.",
			"17": "<sup>17</sup> დავითმა მთელ ქვეყანაზე გაითქვა სახელი და უფალმა მისი შიში მისცა ყველა ხალხს.",
		},
		"15": {
			"1": "<sup>1</sup> აიშენა მან სახლები დავითის ქალაქში, ადგილი მოამზადა ღვთის კიდობნისთვის და კარავი გაშალა.",
			"2": "<sup>2</sup> მაშინ თქვა დავითმა: ლევიანთა გარდა არავინ ზიდოს კიდობანი, რადგან ისინი ამოირჩია უფალმა ღვთის კიდობნის სატარებლად და სამარადისო მსახურებად.",
			"3": "<sup>3</sup> შეყარა დავითმა მთელი ისრაელი იერუსალიმში, რომ დაესვენებინათ უფლის კიდობანი იმ ადგილზე, რომელიც მისთვის იყო გამზადებული.",
			"4": "<sup>4</sup> შეყარა დავითმა აარონიანები და ლევიანები.",
			"5": "<sup>5</sup> კეჰათიანთაგან: სარდალი ურიელი და მისი მოძმენი - ასოცი კაცი.",
			"6": "<sup>6</sup> მერარიანთაგან: სარდალი ყასაია და მისი მოძმენი - ორასი კაცი.",
			"7": "<sup>7</sup> გერშომიანთაგან: სარდალი იოელი და მისი ძმები - ასოცდაათი კაცი.",
			"8": "<sup>8</sup> ელიცაფანიანთაგან: სარდალი შემაყია და მისი მოძმენი - ორასნი.",
			"9": "<sup>9</sup> ხებრონიანთაგან: სარდალი ელიელი და მისი მოძმე - წინწილებს ახმიანებდნენ.",
			"10": "<sup>10</sup> ყუზიელიანთაგან: სარდალი ყამინადაბი და მისი მოძმენი - თორმეტნი.",
			"11": "<sup>11</sup> მოუხმო დავითმა ცადოკსა და აბიათარს, მღვდლებსა და ლევიანებს - ურიელს, ყასაიას, იოელს, შემაყიას, ელიელს, ყამინადაბს",
			"12": "<sup>12</sup> და უთხრა: თქვენა ხართ ლევიანთა მამისსახლების თავკაცები. განიწმიდენით თქვენ და თქვენი მოძმენი და დაასვენეთ უფლის, ისრაელის ღმერთის კიდობანი მისთვის მომზადებულ ადგილას.",
			"13": "<sup>13</sup> რაკი უწინ ამას არ ასრულებდით, უფალმა, ჩვენმა ღმერთმა, შეგვმუსრა, რადგან წესისამებრ არ ვეკითხბოდით მას.",
			"14": "<sup>14</sup> განიწმიდნენ მღვდლები და ლევიანები, რათა აეწიათ უფლის, ისრაელის ღმერთის კიდობანი.",
			"15": "<sup>15</sup> ასწიეს ლევიანებმა ღვთის კიდობანი და, როგორც მოსეს ჰქონდა ნაბრძანები უფლის სიტყვისამებრ, კეტებით მხრებზე შეიდგეს.",
			"16": "<sup>16</sup> დავითმა ლევიანთა მთავრებს უბრძანა, რომ მათი ძმები დაეყენებინათ მგალობლებად საკრავი იარაღებით, ბობღნებით, ქნარებით, წინწილებით, რათა სიხარულის ხმები გამოეცათ.",
			"17": "<sup>17</sup> ლევიანებმა დააყენეს: ჰიმან იოელის ძე, მის მოძმეთაგან ასაფ ბერექიაჰუს ძე, ხოლო მათი მოძმეების, მერარის ძეთაგან ეთან კუშიაჰუს ძე.",
			"18": "<sup>18</sup> მათთან ერთად მათი მოძმენი, მეორეხარისხისანი: ზაქარიაჰუ, ბენი, იაყაზიელი, შემირამოთი, იეხიელი, ყუზი, ელიაბი, ბენიაჰუ, მაყასიაჰუ, მათითიაჰუ, ელიფელეჰუ, მიკნეჰუ, ყობედ-ედომი, იეყიელი - კარისმცველნი.",
			"19": "<sup>19</sup> მგალობელნი: ჰემანი, ასაფი და ეთანი სპილენძის წინწილებს ახმიანებდნენ.",
			"20": "<sup>20</sup> ხოლო ზაქარია, ყაზიელი, შემირამოთი, იეხიელი, ყუნი, ელიაბი, მაყასიაჰუ და ბენიაჰუ ქნარს ქალწულურ ჰანგზე.",
			"21": "<sup>21</sup> მათითიაჰუ, ელეფელეჰუ, მიკნეიაჰუ, ყობედ-ედომი, იეყიელი, ყაზაზიაჰუ ებნებს, რვა სიმიანს, სალოტბაროდ.",
			"22": "<sup>22</sup> ქენანიაჰუ, ლევიანთა მთავარი, სახიობის ხელმძღვანელად განაწესეს, ვინაიდან მცოდნე იყო.",
			"23": "<sup>23</sup> ბერექია და ელკანა კიდობნის კარისმცველები იყვნენ.",
			"24": "<sup>24</sup> მღვდლები შებანიაჰუ, იოშაფატი, ნეთანელი, ყამასა, ზაქარიაჰუ, ბენიაჰუ და ელიყაზარი საყვირს უკრავდნენ ღვთის კიდობნის წინაშე. ყობედ-ედომი და იხია კი კიდობნის კარისმცველნი იყვნენ.",
			"25": "<sup>25</sup> და გაემართნენ დავითი, ისრაელის უხუცესობა და ათასისთავები სიხარულით უფლის აღთქმის კიდობნის წამოსაბრძანებლად ყობედ-ედომის კარ-მიდამოდან.",
			"26": "<sup>26</sup> ღვთის შეწევნით, იყო, რომ გადააბრძანეს ლევიანებმა უფლის აღთქმის კიდობანი და შვიდი ხარი და შვიდი ცხვარი შესწირეს.",
			"27": "<sup>27</sup> დავითს ბისონის მოსასხამი ემოსა, ასევე ყველა ლევიანს, კიდობანს რომ მიაბრძანებდნენ, მგალობლებს და ქენანიას, მგალობელთა ლოტბარს. დავითს ამასთან ერთად სელის ეფოდიც ემოსა.",
			"28": "<sup>28</sup> მთელი ისრაელი მიაცილებდა უფლის აღთქმის კიდობანს ყიჟინით, ბუკის ცემით, საყვირების, წინწიღების, ბობღნებისა და ქნარების დაკვრით.",
			"29": "<sup>29</sup> როცა უფლის აღთქმის კიდობანმა დავითის ქალაქს მიაღწია, სარკმლიდან გამოიხედა საულის ასულმა მიქალმა და დაინახა მოხტუნავე და მროკველი მეფე დავითი და დასცინა მას თავის გულში.",
		},
		"16": {
			"1": "<sup>1</sup> მოაბრძანეს უფლის კიდობანი და დაასვენეს შუაგულ კარავში, რომელიც მისთვის ჰქონდა დავითს გაშლილი. მერე კი აღსავლენი და სამადლობელი მსხვერპლი შესწირეს ღმერთს.",
			"2": "<sup>2</sup> მოამთავრა დავითმა აღსავლენი და სამადლობელი მსხვერპლის შეწირვა და უფლის სახელით დალოცა ერი.",
			"3": "<sup>3</sup> ჩამოურიგა მთელ ერს, ისრაელის მთელ კრებულს, კაცსა და ქალს, თითო პურის კვერი, თითო ხორცის წილობი და თითო ქადა.",
			"4": "<sup>4</sup> დააყენა ლევიანები უფლის კიდობნის წინაშე სამსახურის გასაწევად, რათა მოეხსენებინათ, ედიდებინათ და ექოთ უფალი, ღმერთი ისრაელისა.",
			"5": "<sup>5</sup> ასაფი მათი მეთაური იყო, ზაქარია - მისი მომდევნო, იეყიელი, შემირამოთი, იეჰიელი, მათითია, ელიაბი, ბენიაჰუ, ყობედ-ედომ და იეყიელი ბობღნებსა და ქნარებზე იყვნენ. ხოლო ასაფი წინწილების დამკვრელი იყო.",
			"6": "<sup>6</sup> მღვდლები: ბენიაჰუ და იახაზიელი საყვირებით მუდამ უფლის აღთქმის კიდობნის წინ იდგნენ.",
			"7": "<sup>7</sup> იმ დღეს მაშინ შეასხა დავითმა პირველად ხოტბა უფალს, ასაფისა და მისი ძმების ხელით:",
			"8": "<sup>8</sup> ადიდეთ უფალი, მოუხმეთ მის სახელს, აუწყეთ ხალხებს საქმენი მისნი.",
			"9": "<sup>9</sup> უმღერეთ მას, უგალობეთ მას, ილაპარაკეთ ყველა მის სასწაულზე.",
			"10": "<sup>10</sup> დაიკვეხნეთ მისი წმიდა სახელით, გამხიარულდეს გული უფლის მაძიებელთა.",
			"11": "<sup>11</sup> ეძიეთ უფალი და ძლიერება მისი, ეძიეთ პირი მისი მარადის.",
			"12": "<sup>12</sup> გაიხსენეთ მისი ნამოქმედარი სასწაულები, საოცრებანი და მისი ბაგის სამართალნი.",
			"13": "<sup>13</sup> შთამომავალნო აბრაამისა, მისი რჩეულისა, ძენო იაკობისა, რჩეულნო მისნო.",
			"14": "<sup>14</sup> ის არის უფალი, ღმერთი ჩვენი, მთელ ქვეყნიერებაზეა სამართალნი მისნი.",
			"15": "<sup>15</sup> გახსოვდეთ საუკუნოდ აღთქმა მისი, სიტყვა, რომელიც ბრძანა ათასი თაობის მიმართ,",
			"16": "<sup>16</sup> რომელიც დაუდო აბრაამს, და მისი ფიცი ისაკთან,",
			"17": "<sup>17</sup> და რაც დაუდგინა იაკობს წესად და ისრაელს - საუკუნო აღთქმად.",
			"18": "<sup>18</sup> უთხრა: შენ მოგცემ ქანაანის ქვეყანას წილად თქვენი მემკვიდრეობისა.",
			"19": "<sup>19</sup> როცა ცოტანი იყავით, მცირერიცხოვანნი და იქ შეხიზნულნი,",
			"20": "<sup>20</sup> იარებოდნენ ერთი ხალხიდან მეორესთან, სამეფოდან - სხვა ერთან,",
			"21": "<sup>21</sup> არავის ანებებდა მათს დაჩაგვრას და აფრთხილებდა მათ გამო მეფეებს:",
			"22": "<sup>22</sup> არ შეეხოთ ჩემს ცხებულებს და ჩემს წინასწარმეგყველებს არ უბოროტოთ.",
			"23": "<sup>23</sup> უგალობე უფალს, მთელო ქვეყნიერებავ! ახარებდეთ დღითიდღე მისმიერ ხსნას.",
			"24": "<sup>24</sup> მოუთხრეთ ხალხებს მისი ღირსება, ყოველ ერს მისი სასწაულები.",
			"25": "<sup>25</sup> რადგან დიდია უფალი და ღირსსაქებარი მეტად, ყველა ღმერთზე საშიშია ის.",
			"26": "<sup>26</sup> რადგან ხალხთა ყველა ღმერთი - კერპებია, ხოლო უფალმა ცანი შეჰქმნა.",
			"27": "<sup>27</sup> მშვენიერება და ბრწყინვალება მის წინაშე, სიძლიერე და მომხიბლაობა - მის ტაძარში.",
			"28": "<sup>28</sup> შესწირეთ უფალს, ტომებო ხალხთა, შესწირეთ უფალს ქება და დიდება.",
			"29": "<sup>29</sup> შესწირეთ უფალს ქება მისი სახელისა, წაუღეთ ძღვენი და მიდით მის ეზოებში.",
			"30": "<sup>30</sup> ძრწოდე მის წინაშე, მთელო ქვეყნიერებავ! ხომ მტკიცეა სამყარო, არ შეირყევა.",
			"31": "<sup>31</sup> გამხიარულდებიან ცანი და მოილხენს ქვეყანა. უთხარით ხალხებს: უფალი მეფობს!",
			"32": "<sup>32</sup> იხმაურებს ზღვა და სავსება მისი. გაიხარებს ველი და ყოველი, რაც მასზეა.",
			"33": "<sup>33</sup> მაშინ იზეიმებენ ყოველნი ხენი ტევრისანი, რადგან მოვა ქვეყნიერების განსასჯელად.",
			"34": "<sup>34</sup> აქეთ უფალი, რადგან კეთილია იგი, რადგან უკუნისამდეა მისი წყალობა.",
			"35": "<sup>35</sup> და თქვით: გვიხსენი, ჩვენი ხსნის ღმერთო, შეგვკრიბე და დაგვიცავ ხალხებისგან, რათა ვადიდოთ შენი წმიდა სახელი, დავიქადნოთ შენი ქება-დიდებით.",
			"36": "<sup>36</sup> კურთხეულ არს უფალი, ღმერთი ისრაელისა უკუნითი უკუნისამდე. და მიუგო ერმა: ამენ! ქება უფალს!",
			"37": "<sup>37</sup> დავითმა უფლის აღთქმის კიდობნის წინაშე დატოვა ასაფი და მისი ძმები, რათა კიდობნის წინაშე სამსახური გაეწიათ მუდამ, ყოველდღიურად,",
			"38": "<sup>38</sup> და ყობედ-ედომი და მისი ძმები - სამოცდარვა. ყობედ-ედომ იედუთუნის ძე და ხოსა კი კარისმცველებად,",
			"39": "<sup>39</sup> მღვდელი ცადოკი და მისი მღვდელმსახური ძმები უფლის სავანის წინ, გაბაონში რომ ბორცვია, იქ დააყენა,",
			"40": "<sup>40</sup> რომ მუდამ, დილა-საღამოობით, აღსავლენი აღევლინათ უფლისათვის აღსავლენის სამსხვერპლოზე და რომ ყოველივე შეესრულებინათ, რაც უფლის რჯულში სწერია და რაც მან დაუწესა ისრაელს.",
			"41": "<sup>41</sup> მათთან იყვნენ: ჰემანი, იედუთუნი და სხვა რჩეულნი, რომელნიც სახელებით იყვნენ დადგენილნი, რათა ექოთ უფლის სახელი, რადგან უკუნისამდეა წყალობა მისი.",
			"42": "<sup>42</sup> მათთან ერთად ჰემანი და იედუთუნი საყვირებით, წინწილებითა და საკრავებით ღვთის მგალობლებად იყვნენ დადგენილნი, იედუთუნის ძენი კი კარიბჭესთან.",
			"43": "<sup>43</sup> ყველანი თავიანთ სახლებში წავიდ-წამოვიდნენ. დავითიც თავისი სახლის დასალოცად გაბრუნდა.",
		},
		"17": {
			"1": "<sup>1</sup> როგორც კი დავითი თავის სახლში დამკვიდრდა, უთხრა დავითმა ნათან წინასწარმეტყველს: შეხედე, მე კედარის სახლში ვცხოვრობ, უფლის აღთქმის კიდობანი კი ჩარდახქვეშ არის.",
			"2": "<sup>2</sup> უთხრა ნათანმა დავითს: გააკეთე, რაც გულში გაქვს, რადგან შენთანაა უფალი.",
			"3": "<sup>3</sup> იმ ღამეს იყო, რომ ჩაესმა ნათანს უფლის სიტყვა:",
			"4": "<sup>4</sup> წადი და უთხარი ჩემს მორჩილს დავითს, ასე ამბობს-თქო უფალი: განა შენ უნდა ამიშენო სახლი ჩემს სამყოფელად?",
			"5": "<sup>5</sup> მე ხომ არ დავმკვიდრებულვარ სახლში იმ დროიდან, რაც მე ისრაელი გამოვიყვანე და მე დღემდე კარვიდან კარავში და სავანიდან სავანეში ვიმყოფები.",
			"6": "<sup>6</sup> ნუთუ მთელ ისრაელთან მოარულს ოდესმე სიტყვა მითქვამს ისრაელის რომელიმე მსაჯულისთვის, ვისთვისაც ჩემი ერის მწყემსობა მიბრძანებია, კედარის სახლს რატომ არ მიშენებთ-მეთქი?",
			"7": "<sup>7</sup> ახლა ასე უთხარი ჩემს მორჩილს დავითს, ასე ამბობს-თქო ცაბაოთ უფალი: ფარეხიდან, ცხვრის ფარიდან, წამოგიყვანე, რომ ჩემი ერის, ისრაელის წინამძღოლი ყოფილიყავი.",
			"8": "<sup>8</sup> ყველგან შენთან ვიმყოფებოდი, სადაც არ უნდა წასულიყავი. ვანადგურებდი შენს მტრებს შენს წინაშე, განვადიდე შენი სახელი მსგავსად დიდებულთა სახელისა ამ ქვეყანაზე.",
			"9": "<sup>9</sup> ადგილი დავუდგინე ჩემს ერს, ისრაელს და დაეაფუძნე იგი. ახლა თავისთვის იცხოვრებს უშიშრად დ ბოროტმოქმედნი აღარ შეაჭირვებენ წინანდებურად.",
			"10": "<sup>10</sup> იმ დროიდან, რაც მე მსაჯულები დავუყენე ჩემს ხალხს, ისრაელს, მოსვენებული მყავხარ ყველა მტრებისგან. გამოგიცხადე, რომ სახლს აგიშენებს უფალი.",
			"11": "<sup>11</sup> როცა დრო მოვა და შენს მამა-პაპასთან განისვენებ, მე აღვადგენ შენს შემდგომად შენს თესლს, რომელიც იქნება შენი ძეთაგანი, და მის მეფობას განვამტკიცებ.",
			"12": "<sup>12</sup> ის ამიშენებს სახლს და მე გავამტკიცებ მის ტახტს სამარადისოდ.",
			"13": "<sup>13</sup> მე მამა ვიქნები მისთვის, ის კი შვილი იქნება ჩემთვის, წყალობას არ წავართმევ, როგორც შენს წინამორბედს წავართვი.",
			"14": "<sup>14</sup> დავაყენებ მას ჩემს სახლში და ჩემს სამეფოში სამარადისოდ, მყარი იქნება მისი ტახტი სამარადისოდ.",
			"15": "<sup>15</sup> თანახმად ამ სიტყვებისა და ამ ხილვისა ელაპარაკა ნათანი დავითს.",
			"16": "<sup>16</sup> მივიდა დავით მეფე და დადგა უფლის წინაშე და თქვა: ვინა ვარ მე, უფალო ღმერთო, ვინ არის ჩემი სახლი, რომ აქამდე აღმაზევე!",
			"17": "<sup>17</sup> ესეც მცირე ყოფილა შენს თვალში, ღმერთო, რომ სამომავლოდაც აღუთქვი დაპირება შენი მორჩილის სახლს. შენ მომხედე როგორც განდიდებულ ადამიანს, უფალო ღმერთო ჩემო.",
			"18": "<sup>18</sup> კიდევ რა უნდა გითხრას დავითმა შენი მორჩილის განსადიდებელი? შენ თავად იცნობ შენს მორჩილს, უფალო ღმერთო.",
			"19": "<sup>19</sup> უფალო, შენი მორჩილისათვის და შენი გულის თანახმად აკეთებ ამ დიდებულ საქმეს, რათა გამოამჟღავნო მთელი შენი სიდიადე.",
			"20": "<sup>20</sup> უფალო, არავინაა შენი მსგავსი, არ არის ღმერთი შენს გარდა, თანახმად ყველაფრისა, რაც ჩვენი ყურით მოვისმინეთ.",
			"21": "<sup>21</sup> თუ არის ამ ქვეყნად თუნდაც ერთი ხალხი შენი ერის, ისრაელის მსგავსი, რომელსაც მოვლენოდეს ღმერთი, რომ თავისთვის გამოესყიდოს იგი, რომ ექციოს თავის ერად, რომ სახელოვანი გაეხადოს და ჩაედინოს დიადი და საკვირველი ამბები, ხალხების განდევნით შენი ერის წინაშე, რომელიც გამოისყიდე ეგვიპტიდან და ხალხებისაგან.",
			"22": "<sup>22</sup> შენ შეჰქმენ შენი ერი, ისრაელი, რომ შენი ერი ყოფილიყო სამარადისოდ და შენ, უფალო, მათი ღმერთი ყოფილიყავი.",
			"23": "<sup>23</sup> ახლა კი, უფალო, ეს სიტყვა, რომელიც თქვი შენს მორჩილზე და მის სახლზე, განმტკიცდეს სამარადისოდ, და ქმენი ისე, როგორც ნაბრძანები გაქვს.",
			"24": "<sup>24</sup> იძლიეროს და იდიდოს შენმა სახელმა სამარადისოდ, თქვან: უფალი ღმერთი ისრაელისა ღმერთობს ისრაელზე! მტკიცე იყოს შენს წინაშე შენი მორჩილის დავითის სახლი,",
			"25": "<sup>25</sup> რადგან შენ გამოუცხადე ღმერთო, შენს მორჩილს, სახლს აგიშენებო. ამიტომ გაუწია გულმა შენს მორჩილს ასეთი ლოცვით ელოცა შენს მიმართ.",
			"26": "<sup>26</sup> ახლა, უფალო ჩემო, ღმერთი ხარ შენ და შენი მორჩილისათვის ეს სიკეთე გაქვს გამოცხადებული.",
			"27": "<sup>27</sup> ახლა შენ დალოცე შენი მორჩილის სახლი, რათა სამარადისოდ იყოს შენს წინაშე, რადგან შენ აკურთხე, უფალო, და კურთხეული იქნება სამარადისოდ.",
		},
		"18": {
			"1": "<sup>1</sup> ამის შემდეგ იყო, რომ დაამარცხა დავითმა ფილისტიმელები, დაიმორჩილა ისინი და ხელიდან გამოგლიჯა ფილისტიმელებს გათი თავის ქალაქებიანად.",
			"2": "<sup>2</sup> დაამარცხა მოაბიც. შეიქნა მოაბი დავითის მორჩილი და მოხარკე.",
			"3": "<sup>3</sup> დაამარცხა დავითმა ჰადარყეზერი, ცობას მეფე ხამათში, როცა ის ევფრატზე ბატონობის განსამტკიცებლად მიდიოდა.",
			"4": "<sup>4</sup> წაართვა მას დავითმა ათასი ეტლი, შვიდი ათასი ცხენოსანი და ოციათასი ქვეითი. ეტლის ცხენებს ძარღვები გადაუჭრა დავითმა, მხოლოდ ასი ეტლი დაიტოვა მათგან.",
			"5": "<sup>5</sup> მოვიდნენ დამასკოს არამელები ცობას მეფის ჰადარყეზერის დასახმარებლად და მუსრი გაავლო დავითმა არამელთაგან ოცდაორიათას კაცს.",
			"6": "<sup>6</sup> დაადგინა დავითმა ხელისუფალნი დამასკოს არამში და შეიქნენ არამელები დავითის მორჩილნი და მოხარკენი. სადაც არ უნდა წასულიყო დავითი, ყველგან უფალი შეეწეოდა.",
			"7": "<sup>7</sup> აიღო დავითმა ოქროს ფარები, ჰადარყეზერის მსახურებს რომ ჰქონდათ, და იერუსალიმში მოიტანა.",
			"8": "<sup>8</sup> დიდძალი სპილენძი წამოიღო დავითმა ტიბხათიდან და ქუნიდან, ჰადარყეზერის ქალაქებიდან, რომლისგანაც შემდეგ სოლომონმა გააკეთა სპილენძის საბანელი, ბოძები და სპილენძის ჭურჭელი.",
			"9": "<sup>9</sup> შეიტყო თოყიმ, ხათის მეფემ, რომ სძლია დავითმა ჰადარყეზერს, ცობას მეფეს.",
			"10": "<sup>10</sup> და გაგზავნა თავისი ძე ჰადორამი მეფე დავითთან მოსაკითხად და მისალოცად, რომ შეებრძოლა ჰადარყეზერს და სძლია მას, რადგან თოყის მოსისხლე იყო ჰადარყეზერი. თან გაატანა ოქრო-ვერცხლისა და სპილენძის ჭურჭელი.",
			"11": "<sup>11</sup> მეფე დავითმა ესეც უფალს შესწირა იმ შეწირულ ოქრო-ვერცხლთან ერთად, დამორჩილებულ ხალხებს: ედომს, მოაბს, ყამონიანებს, ფილისტიმელთ და ყამალეკს რომ წაართვა.",
			"12": "<sup>12</sup> აბეშაი ცერუიას ძემ თვრამეტი ათასი ედომელი ამოწყვიტა მარილოვან ველზე.",
			"13": "<sup>13</sup> დააყენა ედომში ხელისუფალნი და შეიქნა მთელი ედომი დავითის მორჩილი. ყველგან, სადაც არ უნდა წასულიყო, უფალი შეეწეოდა დავითს.",
			"14": "<sup>14</sup> მეფობდა დავითი მთელს ისრაელზე და წესსა და სამართალს აძლევდა თავის ერს.",
			"15": "<sup>15</sup> იოაბ ცერუიას ძე ჯარს სარდლობდა; ხოლო იოშაფატ ახილუდის ძე მემატიანე იყო.",
			"16": "<sup>16</sup> ცადოკ ახიტუბის ძე და აბიმელექ აბიათარის ძე მღვდლები იყვნენ, ხოლო სერაია მწერალი იყო.",
			"17": "<sup>17</sup> ბენიაჰუ იეჰოდაყის ძე ქერეთელებსა და ფერეთელებს გამგებლობდა, ხოლო დავითის ძენი პირველნი იყვნენ მეფის გვერდით.",
		},
		"19": {
			"1": "<sup>1</sup> ამის შემდეგ იყო, რომ მოკვდა ყამონიანთა მეფე ნახაშ ყამონის ძე და გამეფდა მისი ძე მის ნაცვლად.",
			"2": "<sup>2</sup> თქვა დავითმა: წყალობას ვუყოფ ხანუნ ნახაშის ძეს, როგორც მამამისი მწყალობდა. და გაგზავნა დავითმა მოციქულები სანუგეშებლად მამის გამო. მივიდნენ დავითის მორჩილნი ყამონიანთა ქვეყანაში.",
			"3": "<sup>3</sup> უთხრეს ყამონიანთა მთავრებმა ხანუნს: ნუთუ მამაშენის პატივისცემის გამო გიგზავნის დავითი ნუგეშისმცემლებს? იქნებ იმისთვის გამოგზავნა დავითმა თავისი მორჩილნი, რომ დაზვეროს ქვეყანა, მიათვალ-მოათვალიეროს და მერე დააქციოს?",
			"4": "<sup>4</sup> შეიპყრო ხანუნმა დავითის მორჩილნი და ნახევარი წვერი მოპარსა, წელამდე შემოაჭრა ნახევარი სამოსელი და გაისტუმრა.",
			"5": "<sup>5</sup> და წავიდნენ. შეატყობინეს დავითს ამ კაცების ამბავი. გაგზავნა კაცები მათ შესაგებებლად, რადგან ძალზე შეურაცხყოფილნი იყვნენ. უთხრა მეფემ: იერიქონში დარჩით, ვიდრე წვერი მოგეზრდებოდეთ და მერე დაბრუნდით.",
			"6": "<sup>6</sup> დაინახეს ყამონიანებმა, რომ თავი შეაძულეს დავითს. გაგზავნეს ხანუნმა და ყამონიანებმა ათასი ქანქარი ვერცხლი ეტლებისა და მხედრების დასაქირავებლად შუამდინარელი არამელებისგან და მაყაქასა და ცობას არამელებისგან.",
			"7": "<sup>7</sup> იქირავეს ოცდათორმეტი ათასი ეტლი და მაყაქას მეფე თავისი ხალხით. მოვიდნენ და მედიბას წინ დაბანაკდნენ. ყამონიანები კი ერთად შეიყარნენ და საომრად გამოემართნენ.",
			"8": "<sup>8</sup> შეიტყო ეს დავითმა და გაგზავნა იოაბი უმამაცესთა მთელი ლაშქრითურთ.",
			"9": "<sup>9</sup> გამოვიდნენ ყამონიანები და გაეწყვნენ საბრძოლველად ქალაქის კარიბჭესთან. ხოლო ის მეფენი, რომელნიც მოვიდნენ, ცალ-ცალკე დადგნენ მინდორში.",
			"10": "<sup>10</sup> როცა იოაბმა დაინახა, რომ მტერი წინიდან და ზურგიდან იყო მისკენ მიმართული, გამოარჩია ისრაელიანებში ურჩეულესნი და დააწყო არამელთა წინააღმდეგ.",
			"11": "<sup>11</sup> დანარჩენი ხალხი თავის ძმას, აბეშაის ჩააბარა და მანაც დააწყო იგი ყამონიანთა წინააღმდეგ.",
			"12": "<sup>12</sup> თქვა: თუ არამელები შენ გძლევენ, მე მოვალ შენს მოსაშველებლად.",
			"13": "<sup>13</sup> გამაგრდი და მტკიცედ იდექი ჩვენი სალხისათვის და ჩვენი უფლის, ჩვენი ღმერთის ქალაქებისათვის. უფალმა ქმნას, რაც ენებოს.",
			"14": "<sup>14</sup> გავიდნენ იოაბი და მისი ხალხი არამელებთან საბრძოლველად და უკუიქცნენ არამელები.",
			"15": "<sup>15</sup> როცა ყამონიანებმა დაინახეს, რომ გარბოდნენ არამელები, თავადაც გაექცნენ აბეშაის და ქალაქში შეცვივდნენ. იოაბი გაეცალა ყამონიანებს და მივიდა იერუსალიმში.",
			"16": "<sup>16</sup> დაინახეს არამელებმა, რომ მარცხდებიან ისრაელთან, კაცები გაუგზავნეს მდინარისგაღმელ არამელებს და გადმოიყვანეს. მათ წინამძღოლობდა ჰადარყეზერის სარდალი შოფეხი.",
			"17": "<sup>17</sup> შეატყობინეს ეს დავითს და მანაც შეყარა მთელი ისრაელი, გადალახა იორდანე, მივიდა მათთან და გაეწყო მათ წინააღმდეგ. ბრძოლა გაუმართა დავითმა არამელებს და ისინიც შეებნენ მას.",
			"18": "<sup>18</sup> გაექცნენ არამელები ისრაელს. დავითმა არამელებს დაუხოცა შვიდი ათასი ეტლოსანი და ორმოცი ათასი ქვეითი. მათი სარდალი შოფეხიც მოკლულ იქნა.",
			"19": "<sup>19</sup> როცა ჰადარყეზერის მორჩილთა დაინახეს, რომ მარცხდებოდნენ ისრაელთან, დაეზავნენ დავითს და დაემორჩილნენ. ვეღარ გაბედეს არამელებმა ყამონიანთა დახმარება.",
		},
		"20": {
			"1": "<sup>1</sup> წლის შემოქცევისას, მეფეთა გამოსვლის ჟამს, იოაბი წინ წაუძღვა ლაშქარს და გაანადგურა ყამონიანთა ქვეყანა, მივიდა და რაბას შემოადგა. დავითი კი იერუსალიმში დარჩა. აიღო იოაბმა რაბა და დაანგრია.",
			"2": "<sup>2</sup> დავითმა მათ მეფეს გვირგვინი მოხადა, ნახა რომ ერთი ტალანტი ოქროს წონისა იყო და ძვირფასი ქვებით იყო მოოჭვილი, და თავზე დაირქვა იგი. აურაცხელი ნადავლი წამოიღო დავითმა ქალაქიდან.",
			"3": "<sup>3</sup> გამოიყვანა იქ მყოფი ხალხი და იწყო მათი ხერხით ხერხვა და რკინის კბილებითა და რკინის ცულებით ხოცვა. ასე მოექცა დავითი ყამონიანთა ქალაქს და დაბრუნდა დავითი თავისი ხალხითურთ იერუსალიმში.",
			"4": "<sup>4</sup> ამის შემდეგ იყო, რომ ფილისტიმელებთან ომი ატყდა გეზერში. მაშინ სიბქაი ხუშათელმა მოკლა სიფაი, რაფას ერთ-ერთი ვაჟი და დაზავდნენ.",
			"5": "<sup>5</sup> კვლავ ატყდა ფილისტიმელებთან ომი. მაშინ ელხანან იაყირის ძემ დაამარცხა ლახმი, გოლიათის ძმა, რომლის შუბის ტარი მქსოველთა ლილვისოდენა იყო.",
			"6": "<sup>6</sup> კვლავ ატყდა ომი გეთში. იყო უზარმაზარი კაცი, რომელსაც ხელ-ფეხზე ექვს-ექვსი თითი ება - სულ ოცდაოთხი. ისიც რაფას ნაშიერი იყო.",
			"7": "<sup>7</sup> დასცინოდა ისრაელს, მაგრამ განგმირა იგი იონათან მიმყას ძემ - დავითის ძმისწულმა.",
			"8": "<sup>8</sup> რაფას გათში ესენი შეეძინნენ და ყველანი დავითისა და მისი მორჩილთა ხელით დაეცნენ.",
		},
		"21": {
			"1": "<sup>1</sup> სატანა აღდგა ისრაელზე და ისრაელის აღრიცხვის სურვილი აღუძრა დავითს.",
			"2": "<sup>2</sup> უთხრა დავითმა იოაბსა და ხალხის მთავრებს: წადით და აღრიცხეთ ისრაელი ბერშაბედან დანამდე და მომიტანეთ, რომ ვიცოდე მათი რიცხვი.",
			"3": "<sup>3</sup> მიუგო იოაბმა: დაე, რაც არის, ასმაგად გაამრავლოს უფალმა თავისი ერი. განა ყველანი ისინი, ჩემო ბატონო მეფევ, ჩემი ბატონის მორჩილნი არ არიან? მაშ, ამას რაღად თხოულობს ჩემი ბატონი? რატომ დაედოს ცოდვა ისრაელს?",
			"4": "<sup>4</sup> მაგრამ მეფის სიტყვა კანონი იყო იოაბისთვის. წავიდა იოაბი, მთელი ისრაელი მოიარა და იერუსალიმში დაბრუნდა.",
			"5": "<sup>5</sup> მიართვა იოაბმა დავითს აღრიცხვის წიგნი და აღმოჩნდა მთელს ისრაელში მილიონ ასი ათასი მახვილის მაშიშვლებელი კაცი, ხოლო იუდაში ოთხასსამოცდაათი ათასი მახვილის მაშიშვლებელი კაცი.",
			"6": "<sup>6</sup> ლევიანები და ბენიამინიანები მათ შორის არ აღურიცხავს, რადგან არ მოეწონა იოაბს მეფის სიტყვა.",
			"7": "<sup>7</sup> ბოროტი იყო ეს საქმე ღვთის თვალში, ამიტომ დაამარცხა ისრაელი.",
			"8": "<sup>8</sup> უთხრა დავითმა ღმერთს: დიდად შევცოდე, ეს რომ გავაკეთე. ახლა მიუტევე, უფალო, შეცოდება შენს მორჩილს, რადგან უგუნურად მოვიქეცი.",
			"9": "<sup>9</sup> დაელაპარაკა უფალი დავითის მისანს და უთხრა:",
			"10": "<sup>10</sup> წადი და დავითს უთხარი: ასე ამბობს-თქო უფალი: სასჯელს შემოგთავაზებ და ერთ-ერთი ამოირჩიე, რომ დაგსაჯო.",
			"11": "<sup>11</sup> მივიდა გადი დავითთან და უთხრა: ასე ამბობს უფალი: ამოირჩიე,",
			"12": "<sup>12</sup> სამი წლის შიმშილობა გირჩევნია, სამი თვის ლტოლვილობა შენი მტრისგან და მათ მიერ შენი დევნა, და უფლის ანგელოზი, შემმუსრველი, ისრაელის მთელ სამკვიდრებელში.",
			"13": "<sup>13</sup> უთხრა დავითმა გადს: დამწუხრებული ვარ დიდად. მაგრამ მიჯობს უფლის ხელში ჩავვარდე, რადგან დიდია მისი მოწყალება, ოღონდაც კაცის ხელში ნუ ჩავვარდები.",
			"14": "<sup>14</sup> მოავლინა უფალმა შავი ჭირი ისრაელში და სამოცდაათი ათასი კაცი გაწყდა ისრაელში.",
			"15": "<sup>15</sup> იერუსალიმში ანგელოზი გაგზავნა მის გასანადგურებლად. როდესაც მან განადგურებას მიჰყო ხელი, მოხედა უფალმა და აკმარა უბედურება, უთხრა შემმუსრველ ანგელოზს: კმარა, ახლა უკან წაიღე ხელი! უფლის ანგელოზი ორნან იებუსელის კალოსთან იდგა.",
			"16": "<sup>16</sup> გაიხედა და დაინახა დავითმა ცასა და დედამიწას შუა მდგარი უფლის ანგელოზი, ხელში თავისი გაშიშვლებული მახვილი ეჭირა, იერუსალიმზე დაღირებული. დავითი და უხუცესნი, სახეშებურვილნი, პირქვე დაემხვნენ.",
			"17": "<sup>17</sup> უთხრა დავითმა ღმერთს: ხალხის აღრიცხვა ხომ მე ვბრძანე? მე შევცოდე, მე ჩავიდინე ბოროტება, ამ ცხვრებმა რაღა დააშავეს? უფალო, ღმერთო ჩემო, ჩემზე და მამაჩემის სახლზე იყოს შენი ხელი, ოღონდაც შენს ერზე ნუ იქნება მისდა დასაღუპად.",
			"18": "<sup>18</sup> უფლის ანგელოზმა დავითისთვის სათქმელად უთხრა გადს: ადგეს და უფლის სამსხვერპლო ააგოს დავითმა ორნან იებუსელის კალოზე.",
			"19": "<sup>19</sup> წავიდა დავითი გადის სიტყვისამებრ, რომელიც უფლის სახელით ჰქონდა ნათქვამი.",
			"20": "<sup>20</sup> გამოიხედა ორნანმა, დაინახა ანგელოზი და თავის ოთხ ვაჟთან ერთად დაიმალა. ორნანი იმ დროს ხორბალს ფქვავდა.",
			"21": "<sup>21</sup> მივიდა დავითი ორნანთან. გამოიხედა ორნანმა და დაინახა დავითი, კალოდან გამოეგება და მდაბლად თაყვანი სცა დავითს.",
			"22": "<sup>22</sup> უთხრა დავითმა ორნანს: მომეცი შენი კალოს ადგილი და უფლის სამსხვერპლოს ავაშენებ მასზე, სრულ საფასად მომეცი, რათა განერიდოს ხალხს ჟამი.",
			"23": "<sup>23</sup> უთხრა ორნანმა დავითს: აიღოს და გააკეთოს მეფე ბატონმა, რაც ენებოს. აჰა, ხარები აღსავლენ მსხვერპლად მომირთმევია, კევრები შეშად, ხორბალი - საძღვნოდ. ყველაფერს ვიძლევი.",
			"24": "<sup>24</sup> უთხრა მეფე დავითმა ორნანს: არა, სრულ საფასურად უნდა ვიყიდო შენგან, რადგან უფალს ვერც შენს საკუთრებას შევწირავ და ვერც მუქთა აღსავლენს.",
			"25": "<sup>25</sup> ამ ადგილის საფასურად დავითმა ექვსასი შეკელი ოქრო მისცა ორნანს.",
			"26": "<sup>26</sup> იმ ადგილას აუგო დავითმა უფალს სამსხვერპლო, მიიტანა აღსავლენი და სამადლობელი მსხვერპლი. და მოუხმო უფალს. მან კი აღსავლენის სამსხვერპლოზე ზეციდან ცეცხლის მოცემით უპასუხა.",
			"27": "<sup>27</sup> უბრძანა უფალმა ანგელოზს და მანაც თავისი მახვილი ქარქაშში ჩააგო.",
			"28": "<sup>28</sup> იმ დროს, როცა დავითმა დაინახა, რომ უფალმა პასუხი გასცა მას ორნან იებუსელის კალოზე, იქ შესწირა მსხვერპლი.",
			"29": "<sup>29</sup> უფლის სავანე, მოსემ უდაბნოში რომ გააკეთა, და აღსავლენის სამსხვერპლო იმჟამად გაბაონის ბორცვზე იდგა.",
			"30": "<sup>30</sup> არ შეეძლო დავითს იქ მისვლა ღვთისთვის შესაკითხად, რადგან უფლის ანგელოზის მახვილით იყო დაშინებული.",
		},
		"22": {
			"1": "<sup>1</sup> თქვა დავითმა: აჰა, უფლის, ღვთის სახლი და აღსავლენის სამსხვერპლო ისრაელისათვის.",
			"2": "<sup>2</sup> ბრძანება გასცა დავითმა, რომ თავი მოეყარათ ხიზნებისათვის, რომელნიც ისრაელის ქვეყნად იყვნენ, და განაწესა ისინი ქვისმთლელებად, რომ ღვთის სახლის ასაშენებლად ქვები დაეთალათ.",
			"3": "<sup>3</sup> დავითმა დიდძალი რკინა გაიღო კარიბჭის ლურსმებისა და კაუჭების დასამზადებლად. ხოლო დახარჯულ სპილენძს ხომ წონა არ ჰქონდა.",
			"4": "<sup>4</sup> არც კედარის ძელებს ჰქონდა თვლა, რადგან ციდონელებსა და ტვიროსელებს მოჰქონდათ დავითისთვის.",
			"5": "<sup>5</sup> თქვა დავითმა: ჩემი ძე სოლომონი ყმაწვილია და სუსტი, უფლისთვის ასაშენებელი სახლი კი ყველაზე დიდებული, სახელგანთქმული და მშვენიერი უნდა იყოს მთელ ქვეყანაზე. ამიტომ ყველაფერს გავუმზადებ მას. სიკვდილის წინ დავითმა მოამარაგა იგი.",
			"6": "<sup>6</sup> დაუძახა თავის ძეს, სოლომონს, და უფლისთვის, ისრაელის ღმერთისთვის, სახლის აგება უანდერძა.",
			"7": "<sup>7</sup> უთხრა დავითმა თავის ძეს, სოლომონს: შვილო, გულით მეწადა უფლის, ღმერთის სახელისთვის სახლი ამეგო.",
			"8": "<sup>8</sup> და იყო უფლის სიტყვა ჩემს მიმართ თქმული: ბევრი სისხლი გაქვს დაღვრილი და დიდი ომები გაქვს გადახდილი, ამიტომ შენ არ ააშენებ ჩემს სახელზე სახლს, რადგან ბევრი სისხლი დაღვარე ქვეყნად ჩემს წინაშე.",
			"9": "<sup>9</sup> აჰა, ძე შეგეძინება და მშვიდი კაცი იქნება. ყველა მტრისაგან მოვასვენებ, ვინაიდან სოლომონი იქნება მისი სახელი და მის დღეებში მშვიდობას მივცემ ისრაელს.",
			"10": "<sup>10</sup> ის ააშენებს სახლს ჩემს სახელზე, ის იქნება ჩემთვის ძე და მე ვიქნები მისთვის მამა, მის სამეფო ტახტს სამარადისოდ განვამტკიცებ ისრაელში.",
			"11": "<sup>11</sup> ახლა კი, შვილო ჩემო, უფალი იყოს შენთან, რათა ხელი მოგემართოს და უფლის, ღმერთის სახლი ააშენო, როგორც თქვა შენზე.",
			"12": "<sup>12</sup> უფალმა მოგცეს აზრი და გონიერება და დაგაყენოს ისრაელზე უფლის, ღმერთის რჯულის დასაცავად.",
			"13": "<sup>13</sup> მაშინ იქნები წარმატებული, თუ იმ წესებსა და კანონებს დაიცავ, რომელნიც მოსეს უბრძანა უფალმა ისრაელისთვის. მტკიცედ დეგ და გამაგრდი, ნუ შიშობ და ნუ თრთი.",
			"14": "<sup>14</sup> აჰა, ჩემს სიღარიბეში უფლის სახლისათვის მოვიმარაგე ასიათასი ქანქარი ოქრო, ათასი ათასი ქანქარი ვერცხლი, სპილენძსა და რკინას წონა არა აქვს, რადგან უამრავია. ხე-ტყე და ქვაც მოვიმარაგე, მაგრამ შენც შეგიძლია მიუმატო.",
			"15": "<sup>15</sup> ბევრი ხელოსანი მყავს: ტყისმჭრელები, ქვისმთლელები, ხუროები და ყველანაირი ხელობის მცოდნენი.",
			"16": "<sup>16</sup> ოქროს, ვერცხლს, სპილენძს თვლა არა აქვს. ახლა ადექი და საქმეს შეუდექი და უფალი შენთან იქნება.",
			"17": "<sup>17</sup> ბრძანა დავითმა, რომ ისრაელის ყველა მთავარი მიხმარებოდა მის ძეს, სოლომონს.",
			"18": "<sup>18</sup> განა თქვენთან არ არის უფალი ღმერთი? განა მან არ მოგცათ ირგვლივ მშვიდობა? მან ჩემს ხელში აყარა ამ ქვეყნის მკვიდრნი და დამორჩილდა ეს ქვეყანა უფლისა და მისი ერის წინაშე.",
			"19": "<sup>19</sup> ახლა კი გაუხსენით თქვენი გული და სული უფალს, თქვენს ღმერთს. ადექით და დაიწყეთა უფლის, ღმერთის საწმიდრის მშენებლობა, რათა უფლის აღთქმის კიდობანი და ღვთის ჭურჭლეული უფლის სახელზე აგებულ სახლში გადააბრძანოთ.",
		},
		"23": {
			"1": "<sup>1</sup> როცა დავითი მოხუცდა და გაძღა წლებით, თავისი ვაჟი სოლომონი გაამეფა ისრაელზე.",
			"2": "<sup>2</sup> თავი მოუყარა ისრაელის მთავრებს, მღვდლებსა დ ლევიანებს.",
			"3": "<sup>3</sup> აღრიცხეს ოცდაათ წელს გადაცილებული ლევიანები. გამოვიდა სულ ოცდათვრამეტი ათასი კაცი.",
			"4": "<sup>4</sup> აქედან ოცდაოთხიათასი უფლის სახლის საქმეს უძღვებოდა, ექვსი ათასი კი მწიგნობრები და მსაჯულები იყვნენ.",
			"5": "<sup>5</sup> ოთხიათასი კარისმცველი იყო, ოთხიათასი კი იმ საკრავებით ადიდებდა უფალს, რომელნიც დავითმა გააკეთა.",
			"6": "<sup>6</sup> დავითმა ისინი ლევის, გერშონის, კეჰათის და მერარის ძეთა დასებად დაჰყო.",
			"7": "<sup>7</sup> გერშონელთაგან იყვნენ: ლაყდანი და შიმყი.",
			"8": "<sup>8</sup> ლაყდანის ძენი: უფროსი იეხიელი, ზეთამი და იოელი - სამნი.",
			"9": "<sup>9</sup> შიმყის ძენი: შელომითი, ხაზიელი და ხარაში - სამნი. ესენი არიან ლაყდანის მამისსახლთა თავკაცები.",
			"10": "<sup>10</sup> შიმყის ძენი: იხათი, ზიზა, იეყუმში და ბერიყა. ესენი არიან შიმყის ძენი - ოთხნი.",
			"11": "<sup>11</sup> იახათი უფროსი იყო, ზიზა - მომდევნო. იეყუშსა და ბერიყას ძალიან ცოტა შვილები ჰყავდათ. ამიტომ აღრიცხვისას ერთ მამისსახლს შეადგენდნენ.",
			"12": "<sup>12</sup> კეჰათის ძენი: ყამრამი, იცჰარი, ხებრონი და ყუზიელი - ოთხნი.",
			"13": "<sup>13</sup> ყამრამის ძენი: აარონი და მოსე. აარონი იმისთვის გამოირჩია, რომ წმიდათა წმიდისთვის ეკურთხებინათ იგი და მისი ძენი სამარადისოდ, რათა მათ ეკმიათ უფლის წინაშე, რათა სამსახური გაეწიათ მისთვის და მისი სახელით უკუნისამდე ეკურთხებინათ.",
			"14": "<sup>14</sup> მოსე ღვთისკაცი იყო. მისი ძენი ლევიანთა ტომს მიათვალეს.",
			"15": "<sup>15</sup> მოსეს ძენი: გერშომი, ელიყეზერი.",
			"16": "<sup>16</sup> გერშომის ძენი: შებუელი - უფროსი.",
			"17": "<sup>17</sup> ელიყეზერის ძენი იყვნენ: რეხაბია, უფროსი. ელიყეზერს მეტი შვილი არც ჰყოლია. რეხაბის ძენი კი ძალიან გამრავლდნენ.",
			"18": "<sup>18</sup> იცჰარის ძენი: შელომითი - უფროსი.",
			"19": "<sup>19</sup> ხებრონის ძენი: იერიაჰუ - უფროსი, ამარია - მეორე, იახაზიელი - მესამე, იეკამაყი - მეოთხე.",
			"20": "<sup>20</sup> ყუზიელის ძენი: მიქა - უფროსი, იშია - მეორე.",
			"21": "<sup>21</sup> მერარის ძენი: მახლი და მუში. მახლის ძენი: ელყაზარი და კიში.",
			"22": "<sup>22</sup> ელყაზარი ისე მოკვდა, რომ ვაჟი არ ჰყოლია, მხოლოდ ასულები დარჩა. ისინი კიშის ძეებმა, ბიძაშვილებმა, შეირთეს.",
			"23": "<sup>23</sup> მუშის ძენი: მახლი, ყედერი, იერემოთი - სამნი.",
			"24": "<sup>24</sup> აჰა, ლევის ძენი, თავ-თავიანთი მამისსახლების მიხედვით, მამისსახლთა თავკაცები, სახელებისა და სულადობის რიცხვის მიხედვით აღრიცხულნი, რომელნიც უფლის სახლის სამსახურის საქმეს უძღვებოდნენ, ოცი წლის ასაკს გადაცილებულნი.",
			"25": "<sup>25</sup> ვინაიდან თქვა დავითმა: უფალმა ღმერთმა მისცა მშვიდობა ისრაელს, თავის ერს, და იერუსალიმში დამკვიდრდა იგი სამარადისოდ.",
			"26": "<sup>26</sup> აღარც ლევიანებს დასჭირდებათ სამსახურის გასაწევად სავანისა და მისი ჭურჭლეულის ტარება.",
			"27": "<sup>27</sup> დავითის უკანასკნელი სიტყვების მიხედვით აღრიცხეს ოცი წლის ასაკს გადაცილებული ლევიანები.",
			"28": "<sup>28</sup> ისინი უნდა მდგარიყვნენ აარონიანთა გვერდით, ემსახურათ უფლის სახლში, ეზოებში და ოთახებში, სუფთად შეენახათ ყოველი სიწმიდე და აღესრულებინათ ღვთის სახლის სამსახური,",
			"29": "<sup>29</sup> ეზრუნათ საწირ პურზე, ძღვენზე, უფუარ კვერებზე, ტაფებზე და შესაწვავზე, ყველაფრის არწყვა-აწონაზე,",
			"30": "<sup>30</sup> დილა-საღამოობით უფლის ქება-დიდებაზე,",
			"31": "<sup>31</sup> ყოველგვარ აღსავლენებზე უფლისათვის შაბათობით, ახალმთვარობების და დღესასწაულების განსაზღვრულ რიცხვებში, როგორც მათთვისაა დადგენილი უფლის წინაშე მარად აღსასრულებლად,",
			"32": "<sup>32</sup> რომ დაიცვან დადგენილება სადღესასწაულო კარვის შესახებ, დადგენილება საწმიდარის შესახებ და დადგენილება აარონიანთა, მათი ძმების შესახებ, უფლის სახლში სამსახურის გასაწევად.",
		},
		"24": {
			"1": "<sup>1</sup> აარონიანებს თავიანთი დასები აქვთ. აარონიანები: ნადაბი, აბიჰუ, ელყეზერი და ითამარი.",
			"2": "<sup>2</sup> ნადაბი და აბიჰუ მამაზე ადრე დაიხოცნენ, ისე რომ ვაჟები არ ჰყოლიათ. ელყაზარი და ითამარი შეუდგნენ მღვდელმსახურებას.",
			"3": "<sup>3</sup> გაამწესა დავითმა ცადოკი, ელყაზარის ძეთაგან და ახიმელეხი, ითამარის ძეთაგან, თავ-თავიანთ სამსახურში, თავ-თავიანთი მოვალეობისდა მიხედვით.",
			"4": "<sup>4</sup> აღმოჩნდა, რომ ელყაზარის შვილებში კაცები უფრო მეტნი იყვნენ, ვიდრე ითამარის შვილებში. დაანაწილეს მათ ისინი: ელყაზარის ძეთა მამისსახლებს თექვსმეტი თავკაცი არგუნეს, ხოლო ითამარის ძეთა მამისსახლებს - რვა თავკაცი.",
			"5": "<sup>5</sup> ერთმანეთში წილისყრით დაინაწილეს, რადგან საწმიდრის წინამძღოლები და ღვთის სახლის წინამძღოლები ელეაზარისა და ითამარის ძეთაგან იყვნენ.",
			"6": "<sup>6</sup> ისინი აღნუსხა შემაყია ნეთანელის ძემ, ლევიანთა მწიგნობარმა, მეფისა და მთავართა წინაშე, ცადოკ მღვდლისა და ახიმელეხ აბიათარის ძის წინაშე, მღვდლების და ლევიანების მამისსახლთა თავკაცთა წინაშე. ერთი მამისსახლი ელყაზარს უყრიდა წილს და ერთი ითამარს უყრიდა წილს.",
			"7": "<sup>7</sup> პირველი წილი იეჰოარიბს შეხვდა, მეორე - იედაყიას,",
			"8": "<sup>8</sup> მესამე - ხარემს, მეოთხე - სეყორიმს,",
			"9": "<sup>9</sup> მეხუთე - მალქიას, მეექვსე - მიამიმს,",
			"10": "<sup>10</sup> მეშვიდე - ჰაკოცს, მერვე - აბიას,",
			"11": "<sup>11</sup> მეცხრე - იეშუაყს, მეათე - შექანიაჰუს,",
			"12": "<sup>12</sup> მეთერთმეტე - ელიაშიბს, მეთორმეტე - იაკიმს,",
			"13": "<sup>13</sup> მეცამეტე - ხეფას, მეთოთხმეტე - იეშებაბს,",
			"14": "<sup>14</sup> მეთხუთმეტე - ბილგას, მეთექვსმეტ იმერს,",
			"15": "<sup>15</sup> მეჩვიდმეტე - ხეზირს, მეთვრამეტე - ჰაფიცეცს,",
			"16": "<sup>16</sup> მეცხრამეტე - ფეთახიას, მეოცე - იეხეზკელს,",
			"17": "<sup>17</sup> ოცდამეერთე - იახინს, ოცდამეორე - გამულს,",
			"18": "<sup>18</sup> ოცდამესამე - დელაიაჰუს, ოცდამეოთხე - მაყაზიაჰუს.",
			"19": "<sup>19</sup> ეს არის მათი განაწესი, თუ როგორ უნდა მისულიყვნენ უფლის სახლში იმ სამსახურის გასაწევად, მათი მამის, აარონის ხელით რომ იყო დაწესებული ისე, როგორც უფალმა, ისრაელის ღმერთმა უბრძანა მას.",
			"20": "<sup>20</sup> დანარჩენი ლევიანები: ყამრამის ძეთაგან შუბაელი, შუბაელის ძეთაგან იეხიდიაჰუ.",
			"21": "<sup>21</sup> რეხაბიაჰუსგან: რეხაბიაჰუს ძეთაგან იშია იყო თავკაცი.",
			"22": "<sup>22</sup> იცჰარიელთაგან: შელომოთი. შელომოთის ძეთაგან იახათი.",
			"23": "<sup>23</sup> ხებრონის ძეთაგან: იერიაჰუ, ამარიაჰუ - მეორე, იახაზიელი - მესამე, იეკამყამი - მეოთხე.",
			"24": "<sup>24</sup> ყუზიელის ძეთაგან: მიქა. მიქას ძეთაგან: შამირი.",
			"25": "<sup>25</sup> მიქას ძმა იყო იშია. იშიას ძეთაგან: ზაქარიაჰუ.",
			"26": "<sup>26</sup> მერარის ძენი: მახლი და მუში. იაყაზიაჰუს ძეთაგან: ბენო.",
			"27": "<sup>27</sup> მერარის ძენი იაყაზიაჰუსგან: ბენო, შოხამი, ზაქური და ყიბრი.",
			"28": "<sup>28</sup> მახლისგან: ელყაზარი, რომელსაც ვაჟები არ ჰყოლია.",
			"29": "<sup>29</sup> კიშისგან: კიშის ძე იერახმეელი.",
			"30": "<sup>30</sup> მუშის ძენი: მახლი, ყედერი, იერიმოთი. აჰა, ლევიანები, თავ-თავიანთი მამისსახლებისდა მიხედვით.",
			"31": "<sup>31</sup> მამისსახლთა თავკაცებმა უმცროსი ძმების თანასწორად ყარეს წილი თავიანთი ძმების, აარონიანთა მსგავსად, მეფე დავითის, ცადოკის, ახიმელექის, მღვდლებისა და ლევიანების მამისსახლთა თავკაცების წინაშე.",
		},
		"25": {
			"1": "<sup>1</sup> დავითმა და მისმა მხედართმთავრებმა სამსახურისთვის დაანაწილეს ასაფის, ჰემანის და იედუთუნის ძენი, რომელნიც ბობღნებზე, ქნარებსა და წინწილებზე უკრავდნენ. იმათი რიცხვი, ვინც თავიანთ სამსახურის საქმეს ეწეოდნენ, იყო:",
			"2": "<sup>2</sup> ასაფის ძეთაგან: ზაქური, იოსები, ნეთანია, აშარელა - ასაფის ძენი, რომელნიც ასაფის ლოტბარობითა და მეფის ხელმძღვანელობით უკრავდნენ.",
			"3": "<sup>3</sup> იედუთუნისგან, იედუთუნის ძენი: გედალიაჰუ, ცერი, ესაია, ხაშაბიაჰუ, მათითიაჰუ - ექვსნი. თავიანთი მამის იედუთუნის ხელქვეით იყვნენ, რომელიც ბობღანზე უკრავდა უფლის საქებ-სადიდებელს.",
			"4": "<sup>4</sup> ჰემანისგან, ჰემანის ძენი: ბუკიაჰუ, მათანიაჰუ, ყუზიელი, შებუელი, იერიმოთი, ხანანია, ხანანი, ელიათა, გიდალთი, რომამთი-ყეზერი, იაშბეკაშა, მალოთი, ჰოთირი, მახაზიოთი.",
			"5": "<sup>5</sup> ყველა ესენი იმ ჰემანის ძენი არიან, რომელიც მეფის მისანი იყო, უფლის სიტყვების გამოსაცხადებლად და მისი დიდების რქის ასამაღლებლად. ღმერთმა ჰემანს თორმეტი ვაჟი და სამი ასული მისცა.",
			"6": "<sup>6</sup> ყველა ესენი მათი მამის ლოტბარობით გალობდნენ უფლის სახლში წინწილებით, ქნარებით, ბობღნებით, ღვთის სახლის სამსახურის გასაწევად, მეფის, ასაფის, იედუთუნისა და ჰემანის ხელმძღვანელობით.",
			"7": "<sup>7</sup> უფლისათვის გალობაში განსწავლულთა და ყველა მცოდნეთა რიცხვი, თავიანთ ძმებთან ერთად, იყო ორასოთხმოცდარვა.",
			"8": "<sup>8</sup> სამსახურის რიგზე წილი თანასწორად ყარეს, დიდმა და პატარამ, სწავლულმა და მოსწავლემ.",
			"9": "<sup>9</sup> პირველი წილი ერგო ასაფს იოსებისთვის, მეორე - გედალიაჰუს; იგი, მისი ძმები და შვილები - თორმეტნი.",
			"10": "<sup>10</sup> მესამე - ზაქურს, მისი ძენი და ძმანი - თორმეტნი.",
			"11": "<sup>11</sup> მეოთხე - იცრის, მისი ძენი და ძმანი - თორმეტნი.",
			"12": "<sup>12</sup> მეხუთე - ნეთანიაჰუს, მისი ძენი და ძმანი - თორმეტნი.",
			"13": "<sup>13</sup> მეექვსე ბუკიაჰუს, მისი ძენი და ძმანი - თორმეტნი.",
			"14": "<sup>14</sup> მეშვიდე - იეშარელას, მისი ძენი და ძმანი - თორმეტნი.",
			"15": "<sup>15</sup> მერვე - ესაიას, მისი ძენი და ძმანი - თორმეტნი.",
			"16": "<sup>16</sup> მეცხრე - მათანიაჰუს, მისი ძენი და ძმანი - თორმეტნი.",
			"17": "<sup>17</sup> მეათე - შიმყას, მისი ძენი და ძმანი - თორმეტნი.",
			"18": "<sup>18</sup> მეთერთმეტე - ყაზარელს, მისი ძენი და ძმანი - თორმეტნი.",
			"19": "<sup>19</sup> მეთორმეტე - ხაშაბიას, მისი ძენი და ძმანი - თორმეტნი.",
			"20": "<sup>20</sup> მეცამეტე - შუბაელს, მისი ძენი და ძმანი - თორმეტნი.",
			"21": "<sup>21</sup> მეთოთხმეტე - მათითიაჰუს, მისი ძენი და ძმანი - თორმეტნი.",
			"22": "<sup>22</sup> მეთხუთმეტე - იერემოთს, მისი ძენი და ძმანი - თორმეტნი.",
			"23": "<sup>23</sup> მეთექვსმეტე - ხანანიაჰუს, მისი ძენი და ძმანი - თორმეტნი.",
			"24": "<sup>24</sup> მეჩვიდმეტე - იოშბეკაშას, მისი ძენი და ძმანი- თორმეტნი.",
			"25": "<sup>25</sup> მეთვრამეტე - ხანანის, მისი ძენი და ძმანი - თორმეტნი.",
			"26": "<sup>26</sup> მეცხრამეტე - მალოთის, მისი ძენი და ძმანი - თორმეტნი.",
			"27": "<sup>27</sup> მეოცე - ელიათას, მისი ძენი და ძმანი - თორმეტნი.",
			"28": "<sup>28</sup> ოცდამეერთე - ჰოთირს, მისი ძენი და ძმანი -თორმეტნი.",
			"29": "<sup>29</sup> ოცდამეორე - გიდალთის, მისი ძენი და ძმანი - თორმეტნი.",
			"30": "<sup>30</sup> ოცდამესამე - მახაზიოთს, მისი ძენი და ძმანი - თორმეტნი.",
			"31": "<sup>31</sup> ოცდამეოთხე - რომამთი-ყეზერს, მისი ძენი და ძმანი - თორმეტნი.",
		},
		"26": {
			"1": "<sup>1</sup> კარისმცველთა დასები. კორხიელთაგან: მეშელემიაჰუ კორეხის ძე - ასაფის ძეთაგან.",
			"2": "<sup>2</sup> მეშელემიაჰუს ძენი: ზექარია - პირმშო, იედიყაელი - მეორე, ზებადიაჰუ - მესამე, იათნიელი - მეოთხე,",
			"3": "<sup>3</sup> ყელამი - მეხუთე, იეჰოხანანი - მეექვსე, ელიეჰოყენაი - მეშვიდე.",
			"4": "<sup>4</sup> ყობედ-ედომის ძენი: შემაყია - პირმშო, იეჰოზაბადი - მეორე, იოახი - მესამე, საქარი - მეოთხე, ნეთანელი - მეხუთე.",
			"5": "<sup>5</sup> ყამიელი - მეექვსე, ისაქარი - მეშვიდე, ფეყულთაი - მერვე, რადგან ღმერთმა აკურთხა იგი.",
			"6": "<sup>6</sup> მის ძეს, შემაყიას, ვაჟები შეეძინა, რომელნიც მამისსახლს განაგებდნენ, ვინაიდან მაგარი ვაჟკაცები იყვნენ.",
			"7": "<sup>7</sup> შემაყიას ძენი: ყათნი, რეფაელი, ყობედი, ელზაბადი, მისი ძმანი, მაგარი ხალხი, ელიჰუ და სემაქიაჰუ.",
			"8": "<sup>8</sup> ყველანი ყობედ-ედომის ძენი იყვნენ. ისინი თავიანთი ძეებითა და ძმებითურთ მაგარი ვაჟკაცვბი იყვნენ და სამსახურის გაწევის უნარი ჰქონდათ. ყობედ-ედომს ასეთები სულ სამოცდაორი ჰყავდა.",
			"9": "<sup>9</sup> მეშელემიაჰუს თვრამეტი ვაჟი და ძმა, მაგარი ვაჟკაცები, ჰყავდა.",
			"10": "<sup>10</sup> ხოსას, მერარის ერთ-ერთი ძეთაგანის, ვაჟები: უფროსი შიმრი იყო (თუმცა პირმშო არ ყოფილა - მამამისმა არგუნა მას უფროსობა);",
			"11": "<sup>11</sup> ხილკიაჰუ მომდევნო იყო, ტებალიაჰუ - მესამე, ზაქარიაჰუ - მეოთხე. ხოსას სულ ცამეტი ვაჟი და ძმა ჰყავდა.",
			"12": "<sup>12</sup> კარისმცველთა დასს, მოყმეთა თავკაცებს ჩაბარდათ დაცვა თავიანთ ძმებთან ერთად უფლის სახლში სამსახურის გასაწევად.",
			"13": "<sup>13</sup> ყველა კარიბჭეზე წილი ყარეს, როგორც დიდებმა, ისე პატარებმა, თავ-თავიანთი მამისსახლების მიხედვით.",
			"14": "<sup>14</sup> აღმოსავლეთის მხარე შელემიაჰუს ერგო წილად. მის ვაჟს ზაქარიას, გონიერ მრჩეველს, უყარეს წილი და მას წილად ჩრდილოეთის მხარე შეხვდა.",
			"15": "<sup>15</sup> ყობედ-ედომს სამხრეთის მხარე ერგო წილად, მის ძეებს - საკუჭნაო.",
			"16": "<sup>16</sup> შუფიმსა და ხოსას - დასავლეთის მხარე შეხვდა, შალექეთის ჭიშკართან, საიდანაც გზა ადის და დაცვა დაცვის პირისპირ დგას.",
			"17": "<sup>17</sup> აღმოსავლეთით ექვსი ლევიანი იდგა, ჩრდილოეთით დღეში ოთხი იდგა, სამხრეთით დღეში ოთხი იდგა, საკუჭნაოსთან კი ორ-ორი.",
			"18": "<sup>18</sup> ფარბარის დასავლეთით, გზაზე, ოთხნი იდგნენ, თავად ფარბართან კი - ორნი.",
			"19": "<sup>19</sup> აჰა, კორახისა და მერარის ძეების კარისმცველთა დასები.",
			"20": "<sup>20</sup> ლევიანთაგან: ახია ღვთის სახლის საგანძურსა და შენაწირი ნივთების საგანძურს დარაჯობდა.",
			"21": "<sup>21</sup> ლაყდანის ძენი, გერშუნელთა ძენი ლაყდანისგან, მამისსახლის თავკაცები ლაყდან გერშუნელისგან: იეხიელი.",
			"22": "<sup>22</sup> იეხიელის ძენი: ზეთამი და მისი ძმა იოელი უფლის სახლის საგანძურს დარაჯობდნენ.",
			"23": "<sup>23</sup> ყამრამელებისგან, იცჰარელებისგან, ხებრონელებისგან და ყაზიელებისგან იყო:",
			"24": "<sup>24</sup> შებუელი ძე გერშომისა, მოსეს ძისა, რომელიც საგანძურის ზედამხედველი იყო.",
			"25": "<sup>25</sup> მისი ძმები ელიყეზერისგან: მისი ძე რეხაბიაჰუ, მისი ძე იეშაყიაჰუ, მისი ძე იორამი, მისი ძე შელომითი.",
			"26": "<sup>26</sup> ის შელომითი თავისი ძმებითურთ დარაჯობდა შემოწირულობათა მთელ საგანძურს, რომელიც მეფე დავითმა, მამისსახლთა თავკაცებმა, ათასისთავებმა, ასისთავებმა და სარდლებმა შესწირეს.",
			"27": "<sup>27</sup> ომიდან და ნაალაფრიდან სწირავდნენ უფლის სახლის შესანახად.",
			"28": "<sup>28</sup> ყველაფერი, რაც სამუელ წინასწარმეტყველმა, საულ კიშის ძემ, აბნერ ნერის ძემ, იოაბ ცერუიას ძემ შესწირეს, ყველა ეს შენაწირი შელომითისა და მისი ძმების ხელში იყო.",
			"29": "<sup>29</sup> იცჰარელთაგან ქენანიაჰუ და მისი ძმები ისრაელში საგარეო სამსახურზე იყვნენ გამწესებულნი მზირებად და მსაჯულებად.",
			"30": "<sup>30</sup> ხებრონელთაგან: ხაშაბიაჰუ და მისი ძმები, მაგარი ვაჟკაცები, ათას შვიდასნი, დანიშნულნი იყვნენ ისრაელზე იორდანის გაღმიდან დასავლეთამდე, უფლის ყოველგვარი საქმისათვის და მეფის ყოველგვარი სამსახურისთვის.",
			"31": "<sup>31</sup> ხებრონელებს იერია ხებრონელი ჰყავდათ თავკაცად თავიანთ გვარ-ტომში, მამისსახლში. დავითის მეფობის მეორმოცე წელს გამოიკითხეს და მათ შორის მაგარი ვაჟკაცები აღმოაჩინეს გალაადის იაყზერში.",
			"32": "<sup>32</sup> მათი ძმანი, მაგარი ვაჟკაცები, ორიათას შვიდასნი იყვნენ - მამისსახლის თავკაცნი. დავით მეფემ ისინი რეუბენიანებში, გადელებში და მენაშეს ტომის ნახევარში გაამწესა ღვთის ყველა საქმისთვის და სამეფო საქმისთვის.",
		},
		"27": {
			"1": "<sup>1</sup> ესენი არიან ისრაელიანები, მათი რიცხაის მიხედვით, მამისსახლთა თავკაცები, ათასისთავები, ასისთავები და მათი მზირები, რომელნიც მეფეს ემსახურებოდნენ დასების ყველა საქმეში, მიდი-მოდიოდნენ ყოველთვიურად, წელიწადის ყოველ თვეში. თითოეული დასი ოცდაოთხი ათასისგან შედგებოდა.",
			"2": "<sup>2</sup> პირველ დასზე პირველი თვისთვის დადგენილი იყო იაშაბყამ ზაბდიელის ძე; მის დასში ოცდაოთხი ათასი იყო.",
			"3": "<sup>3</sup> იგი იყო ფერეცის შვილთაგანი, პირველი თვის ყველა მხედართმთავრის მეთაური.",
			"4": "<sup>4</sup> მეორე თვის დასზე იყო დოდა ახოხელი; მის დასშივე იყო მთავარი მიკლოთი. მათ დასში ოცდაოთხი ათასი იყო.",
			"5": "<sup>5</sup> მესამე თვის მესამე მხედართმთავარი იყო ბენიაჰუ, იეჰოიადაყ მღვდლის ვაჟი, მეთაური. დასში ოცდაოთხი ათასი იყო.",
			"6": "<sup>6</sup> ეს ბენიაჰუ ოცდაათ მოყმეთაგანი და მათი მეთაური იყო. მისი ძე ყამიზაბადიც მის დასში იყო.",
			"7": "<sup>7</sup> მეოთხე თვეში მეოთხე იყო ყასარიელი, იოაბის ძმა. მის შემდეგ კი მისი ძე ზებადია. მის დასში ოცდაოთხი ათასი იყო.",
			"8": "<sup>8</sup> მეხუთე თვეში მეხუთე მხედართმთავარი იყო შამუი იზრახელი. მის დასში ოცდაოთხი ათასი იყო.",
			"9": "<sup>9</sup> მეექვსე თვეში მეექვსე იყო ყირია ყიკეშის ძე, თეკოყელი. მის დასში ოცდაოთხი ათასი იყო.",
			"10": "<sup>10</sup> მეშვიდე თვეში მეშვიდე იყო ხელეც ფელონელი, ეფრემის ძეთაგან. მის დასში ოცდაოთხი ათასი იყო.",
			"11": "<sup>11</sup> მერვე თვეში მერვე იყო სიბქა ხუშათელი, ზარხელთაგან. მის დასში ოცდაოთხი ათასი იყო.",
			"12": "<sup>12</sup> მეცხრე თვეში მეცხრე იყო აბიყეზერ ყანათოთელი, ბენიამინელთაგან. მის დასში ოცდაოთხი ათასი აყო.",
			"13": "<sup>13</sup> მეათე თვეში მეათე იყო მაჰარაი ნეტოფათელი, ზარბიელთაგან. მის დასში ოცდაოთხი ათასი იყო.",
			"14": "<sup>14</sup> მეთერთმეტე თვეში მეთერთმეტე იყო ბენაია ფირყათონელი, ეფრემის ძეთაგან. მის დასში ოცდაოთხი ათასი იყო.",
			"15": "<sup>15</sup> მეთორმეტე თვეში მეთორმეტე იყო ხელდაი ნეტოფათელი, ყანთიელთაგან. მის დასში ოცდაოთხი ათასი იყო.",
			"16": "<sup>16</sup> ისრაელის შტოებზე: რეუბენიანთა წინამძღოლი იყო ელიყეზერ ზიქრის ძე, სიმონელებისა - შეფატიაჰუ მაყაქას ძე.",
			"17": "<sup>17</sup> ლევიანებისა - ხაშაბია კემუელის ძე, აარონისა - ცადოკი.",
			"18": "<sup>18</sup> იუდასი - ელიჰუ, დავითის ძმათაგანი. ისაქარისა - ყომრი მიქაელის ძე.",
			"19": "<sup>19</sup> ზებულუნისა - იშმაყიაჰუ ყობადიაჰუს ძე. ნაფთალისა - იერიმოთ ყაზარიელის ძე.",
			"20": "<sup>20</sup> ეფრემიანთა - ჰოშეაყ ყაზაზიაჰუს ძე. მენაშეს ტომის ნახევრისა - იოელ ფედიაჰუს ძე.",
			"21": "<sup>21</sup> მენაშეს ტომის ნახევრისა - იდო ზაქარიას ძე. ბენიამინისა - იაყასიელ აბნერის ძე.",
			"22": "<sup>22</sup> დანისა - ყაზარიელ იეროხამის ძე. ესენი არიან ისრაელის ტომთა მთავარნი.",
			"23": "<sup>23</sup> ოცი წლისანი და მის ქვემოთ არ აღურიცხავს დავითს, ვინაიდან უფალმა ისრაელს ცის ვარსკვლავებივით გამრავლება აღუთქვა.",
			"24": "<sup>24</sup> იოაბ ცერუიას ძემ დაიწყო აღრიცხვა, მაგრამ არ დაამთავრა. ამის გამო ისრაელს რისხვა დაატყდა. ეს აღრიცხვა დავით მეფის მატიანის აღრიცხვაშიც არ შევიდა.",
			"25": "<sup>25</sup> სამეფო საუნჯეებზე იყო ყაზმავეთ ყადიელის ძე, ხოლო იმ საუნჯეებზე, რომელიც მინდორში, ქალაქებში და ციხე-სიმაგრეებში იყო - იონათან ყუზიელის ძე.",
			"26": "<sup>26</sup> მინდვრის სამუშაოების შემსრულებლებზე, მიწას რომ ამუშავებდნენ - ყეზერ ქელუბის ძე.",
			"27": "<sup>27</sup> ვენახებზე - შიმყი რამათელი; ყურძნისა და ღვინის მარაგზე - ზაბდი შიფმელი.",
			"28": "<sup>28</sup> ზეთისხილებზე და ლეღვებზე, დაბლობში რომ არის - ბაყალ-ხანან გედერელი. ზეთის საუნჯეებზე - იოყაში.",
			"29": "<sup>29</sup> ხოლო იმ ნახირზე, შარონში რომ სძოვდა, შიტრაი შარონელი. ნახირზე ხეობებში - შაფატ ყადლის ძე.",
			"30": "<sup>30</sup> აქლემებზე - ობილ ისმაელიანი. სახედრებზე - იეხდიაჰუ მერონოთელი.",
			"31": "<sup>31</sup> ცხვარზე - იაზიზ აგარელი. ყველა ესენი დავით მეფის ქონების მოურავები იყვნენ.",
			"32": "<sup>32</sup> იონათანი, დავითის ბიძა - მრჩეველი იყო, კაცი გონიერი და მწიგნობარი; იეხიელ ხაქმონის ძე მეფისწულებთან იყო.",
			"33": "<sup>33</sup> ახითოფელი მეფის მრჩეველი იყო, ხუშაი არქიელი კი - მეფის მეგობარი.",
			"34": "<sup>34</sup> ახითოფელის შემდეგ იყვნენ იეჰოიადაყ ბენიაჰუს ძე და აბიათარი. მეფის მხედართმთავარი იყო იოაბი.",
		},
		"28": {
			"1": "<sup>1</sup> შეყარა დავითმა იერუსალიმში ისრაელის ყველა მთავარი, შტოთა მთავარნი, დასების მთავარნი, მეფის სამსახურში მყოფნი, ათასისთავები, ასისთავები, მეფისა და მისი ძეების მთელი საქონ-საყოლის მოურავები, სახლთუხუცესები და ყველა მაგარი ვაჟკაცი.",
			"2": "<sup>2</sup> ფეხზე წამოდგა დავითი და თქვა: ყური დამიგდეთ, ჩემო ძმებო და ჩემო ხალხო! გულით მეწადა ამეშენებინა განსასვენებელი სახლი უფლის აღთქმის კიდობნისთვის, ჩვენი ღმერთის ფერხთა დასადგმელი, და მოვემზადე ასაშენებლად.",
			"3": "<sup>3</sup> მაგრამ ღმერთმა მითხრა: ჩემს სახელზე სახლს ნუ ააშენებ! რადგან მეომარი კაცი ხარ და სისხლი გაქვს დაღვრილიო.",
			"4": "<sup>4</sup> უფალმა, ისრაელის ღმერთმა, მამაჩემის სახლიდან ამომირჩია, რათა მთელი ისრაელის მეფე გავმხდარიყავი სამარადისოდ. რადგან იუდა ამოირჩია მთავრად, იუდას სახლიდან მამაჩემის სახლი ამოირჩია, მამაჩემის ძეთაგან ჩემი გამეფება ინება მთელ ისრაელზე.",
			"5": "<sup>5</sup> ყველა ჩემს ვაჟს შორის კი, რადგან მრავალი შვილი მომცა უფალმა, ჩემი ძე სოლომონი ამოირჩია, რათა დამჯდარიყო უფლის სამეფოს ტახტზე, ისრაელზე.",
			"6": "<sup>6</sup> მითხრა: შენი ძე სოლომონი ააშენებს ჩემს სახლსა და ეზოებს, რადგან იგი ძედ ამოვირჩიე და მე ვიქნები მისი მამა.",
			"7": "<sup>7</sup> სამარადისოდ გავამაყარებ მის სამეფოს, თუ იგი მტკიცედ დაიცავს ჩემს მცნებებს და სჯულდებას, როგორც დღემდის იცავდა.",
			"8": "<sup>8</sup> ახლა კი მთელი ისრაელის, უფლის კრებულის, თვალწინ, ჩვენი ღმერთის გასაგონად გეუბნებით: დაიცავით და გამოიკითხეთ უფლის, თქვენი ღმერთის, ყველა მცნება, რათა დოვლათიან ქვეყანას დაეპატრონოთ და თქვენს შემდეგ თქვენს შვილებს დაუმკვიდროთ უკუნისამდე.",
			"9": "<sup>9</sup> ხოლო შენ, სოლომონ, ძეო ჩემო, იცოდე მამაშენის ღმერთი წრფელი გულითა და ხალისიანი სულით. ემსახურე, რადგან ყოველთა გულის მხილველია უფალი და ყველა ფიქრისა და ზრახვის მცოდნეა. ძებნას თუ დაუწყებ, იპოვი კიდეც. თუ შენ მიატოვებ, ისიც სამუდამოდ მიგატოვებს.",
			"10": "<sup>10</sup> ახლა უყურე, რაკი უფალმა გამოგარჩია უფლის საწმიდრის ამშენებლად, მტკიცედ დეგ და გააკეთე.",
			"11": "<sup>11</sup> მისცა დავითმა სოლომონს, თავის ძეს გეგმა კარიბჭისა, მისი სახლებისა, განძთსაცავებისა, ქორედებისა, შიდა ოთახებისა და კიდობნის სახლისა.",
			"12": "<sup>12</sup> ასევე ყველაფრის გეგმა, რისი გაკეთებაც გულში ჰქონდა: უფლის სახლის ეზოებისა, ირგვლივ განლაგებული ყველა ოთახის, ღვთის სახლის საგანძურისა და შემოწირულობათა საგანძურისა,",
			"13": "<sup>13</sup> მღვდელთა და ლევიანთა დასებისა, უფლის სახლის სამსახურის ყველა საქმისა, უფლის სახლის სამსახურისთვის საჭირო ყველა ჭურჭლისა,",
			"14": "<sup>14</sup> ოქროულობა, ოქროს წონით, ყველა სამსახურებელი ჭურჭლისთვის, ვერცხლის ყველა ჭურჭლისთვის, წონით ერთად, ყველა სამსახურებელი ჭურჭლისთვის.",
			"15": "<sup>15</sup> წონა ოქროს სასანთლეებისა და მისი ოქროს ლამპრებისა, თითოეული სასანთლისა და ლამპრის წონასთან ერთად; ასევე ვერცხლის სასანთლეები, მისი სასანთლეებისა და ლამპრების წონასთან ერთად, როგორც თითოეული სასანთლის სამსახურის წესია.",
			"16": "<sup>16</sup> ოქროს წონა საწირი ტაბლებისთვის და ყველა მაგიდისთვის. ასევე ვერცხლი ვერცხლის ტაბლებისთვის.",
			"17": "<sup>17</sup> ხალასი ოქრო ჩანგლებისთვის, სასხურებლებისთვის, ჯამებისთვის. ოქროს ფიალებისთვის, თითოეული ფიალის წონასთან ერთად, აგრეთვე ვერცხლის ფიალებისთვის, თითოეული ფიალის წონასთან ერთად.",
			"18": "<sup>18</sup> საკმევის სამსხვერპლოსთვის გასუფთავებული ოქრო წონასთან ერთად, და გეგმა ეტლისა და ოქროს ქერუბიმებისა, რომელთა ფრთებიც გადაშლილი და გადაფარებული იყო უფლის აღთქმის კიდობანზე.",
			"19": "<sup>19</sup> ეს ყველაფერი უფლის ხელით არის ჩაწერილი, მან ჩამაგონა გეგმის ყოველი წვრილმანი.",
			"20": "<sup>20</sup> უთხრა დავითმა თავის ძეს სოლომონს: მტკიცედ დეგ და გამხნევდი, ნუ შიშობ და ნუ ძრწი, რადგან უფალი ღმერთი, ჩემი ღმერთი, შენთან არის, არ განგიდგება იგი და არ მიგატოვებს, ვიდრე არ შეასრულებ უფლის სახლის სამსახურის ყველა საქმეს.",
			"21": "<sup>21</sup> აჰა, მღვდელთა და ლევიანთა დასები, ღვთის სახლის ყოველგვარი სამსახურის გასაწევად. მხარში გიდგანან ყველა საქმეში ბეჯითნი და ყველა სამუშაოს მცოდნენი. მთავრები და მთელი ხალხი კი შენს ყველა ბრძანებებს ემორჩილებიან.",
		},
		"29": {
			"1": "<sup>1</sup> უთხრა დავით მეფემ მთელ კრებულს: სოლომონი, ჩემი ძე, რომელიც უფალმა ამოირჩია, ნორჩია და სუსტი, ეს საქმე კი ძალზე დიდია, რადგან ეს შენობა კაცისთვის კი არ არის, არამედ უფლისთვის, ღვთისთვის.",
			"2": "<sup>2</sup> რაც კი შემეძლო ყველაფერი მოვამზადე უფლის სახლისთვის: ოქრო ოქროს ნივთებისთვის, ვერცხლი ვერცხლის ნაკეთობებისთვის, სპილენძი სპილენძისთვის,რკინა რკინეულისთვის, ხე-ტყე ხე-ტყის მასალისთვის, აგრეთვე ონიქსის ქვები, ბუდეებში ჩასმული ქვები, ლამაზი და ფერად-ფერადი ქვები, ყველანაირი ძვირფასი ქვები და მარმარილოს ლოდები უამრავი.",
			"3": "<sup>3</sup> ამასთან ერთად, ჩემი უფლის სახლის სიყვარულის გამო, მაქვს ოქროსა და ვერცხლის საუნჯე, რომელსაც ღვთის სახლს ვწირავ, ყოველივე იმის გარდა, რაც წმიდა სახლისთვის მაქვს მომზადებული.",
			"4": "<sup>4</sup> სამიათასი ქანქარი ოქრო, ოფირის ოქრო, შვიდი ათასი ქანქარი გადამდნარი ვერცხლი, სახლის კედლების მოსაპირკეთებლად,",
			"5": "<sup>5</sup> ოქროს ნივთებისთვის, ვერცხლის ნაკეთობებისთვის, ყველა საოქროჭმედლო საქმისთვის. კიდევ ვინ შესწირავს დღეს უფალს სავსე ხელით?",
			"6": "<sup>6</sup> გაიღეს შესაწირავი მამისსახლთა მთავრებმა, ისრაელის შტოთა მთავრებმა, ათასისთავებმა, ასისთავებმა და მეფის მოურავებმა.",
			"7": "<sup>7</sup> და გამოიღეს უფლის სახლის სამსახურისთვის ხუთი ათასი ქანქარი ოქრო, ათი ათასი დრამა, ათი ათასი ქანქარი ვერცხლი, თვრამეტი ათასი ქანქარი სპილენძი, ასი ათასი ქანქარი რკინა.",
			"8": "<sup>8</sup> ვისაც კი ქვები აღმოაჩნდა უფლის სახლის საგანძურისთვის, იეხიელ გერშუნელს ჩააბარეს.",
			"9": "<sup>9</sup> ხალხი ხარობდა შესაწირავის გამო, რადგან სრული გულით სწირავდნენ უფალს. ასევე მეფე დავითიც დიდი სიხარულით ხარობდა.",
			"10": "<sup>10</sup> აკურთხა დავითმა უფალი მთელი კრებულის თვალწინ და თქვა დავითმა: კურთხეულ ხარ, უფალო, ისრაელისა და ჩვენი მამა-პაპის ღმერთო, უკუნითი უკუნისამდე.",
			"11": "<sup>11</sup> შენია, უფალო, დიდება, ძლიერება, ქება, ძლევა და დიდებულება, ისევე როგორც ყოველივე ცაზე და ქვეყანაზე. შენია უფალო, მეფობა, ყველაზე აღმატებული ხარ მთავრად.",
			"12": "<sup>12</sup> სიმდიდრე და დიდება შენგან მოდის, შენ განაგებ ყოველივეს, შენს ხელშია ძალა და ძლიერება, შენს ხელშია ყოველივეს განდიდება და განმტკიცება.",
			"13": "<sup>13</sup> აწ გაქებთ შენ, ღმერთო ჩვენო, და ვადიდებთ შენს დიდებულ სახელს.",
			"14": "<sup>14</sup> ვინა ვარ მე, ან ვინ არის ჩემი ერი, ამდენის შემოწირვა რომ შევიძელით? რადგან ყოველივე ეს შენგან არის, ეს ყოველივე შენი ხელით მოცემული შემოგწირეთ.",
			"15": "<sup>15</sup> ვინაიდან მდგმურნი და ხიზანნი ვართ შენს წინაშე, ჩვენი მამა-პაპის მსგავსად. აჩრდილივით არის ჩვენი დღენი ამ ქვეყნად, არაფერია საიმედო.",
			"16": "<sup>16</sup> უფალო, ღმერთო ჩემო! მთელი სიმდიდრე, რაც ჩვენ მოვამზადეთ, შენს წმიდა სახელზე სახლის ასაგებად, შენი ხელით არის მოცემული და შენია ყოველი.",
			"17": "<sup>17</sup> ღმერთო ჩემო, ვიცი, რომ გულთამხილველი და სიმართლისმოყვარული ხარ. ეს აყველაფერი ჩემი წრფელი გულით შემოგწირე. ახლა კი გახარებულს ვხედავ აქ მყოფ შენს ერს, რომელიც შესაწირავს გიძღვნის.",
			"18": "<sup>18</sup> უფალო, ღმერთო აბრაამისა, ისაკისა, ისრაელისა და ჩვენი მამა-პაპისა! სამარადისოდ დაიცავი ეს, შენი ერის გულის ფიქრები და ზრახვანი და შენსკენ წარმართე მათი გულები.",
			"19": "<sup>19</sup> და სოლომონს, ჩემს ძეს, მიეცი სრული გული, რომ დაიცვას შენი მცნებები, შენი წესები და კანონები, რათა ყველაფერი გააკეთოს იმ შენობის ასაგებად, რომლისთვისაც მე მოვამზადე.",
			"20": "<sup>20</sup> უთხრა დავითმა მთელ კრებულს: აკურთხეთ უფალი, ღმერთი თქვენი! და აკურთხა მთელმა კრებულმა უფალი, თავისი მამა-პაპის ღმერთი, დაეცნენ და თაყვანი სცეს უფალს და მეფეს.",
			"21": "<sup>21</sup> მეორე დღეს შესწირეს უფალს მსხვერპლები და აღსავლენი აღუვლინეს უფალს: ათასი კურატი, ათასი ცხვარი, ათასი კრავი, თავისი საღვრელითურთ, და უამრავი მსხვერპლი მთელი ისრაელისათვის.",
			"22": "<sup>22</sup> იმ დღეს დიდი სიხარულით ჭამდნენ და სვამდნენ უფლის წინაშე. მეორე დღეს კი სოლომონ დავითის ძე გაამეფეს, უფლის წინაშე იგი წინამძღოლად სცხეს. ხოლო ცადოკი - მღვდლად.",
			"23": "<sup>23</sup> დაჯდა სოლომონი საუფლო ტახტზე მეფედ დავითის, მამამისის ნაცვლად. წარმატებული იყო და მთელი ისრაელი ემორჩილებოდა.",
			"24": "<sup>24</sup> ყველა მთავარი, მოყმე და მეფე დავითის ყველა ვაჟიშვილი ემორჩილებოდა სოლომონ მეფეს.",
			"25": "<sup>25</sup> დიდად აღამაღლა უფალმა სოლომონი მთელი ისრალოს თვალში და ისეთი მეფური დიდება უბოძა, ისრაელის არც ერთ წინამორბედ მეფეს რომ არ ჰქონია.",
			"26": "<sup>26</sup> დავით იესეს ძე მეფობდა მთელს ისრაელზე.",
			"27": "<sup>27</sup> ორმოც წელიწადს იმეფა მან ისრაელზე, აქედან შვიდი წელიწადი ხებრონში იმეფა, ოცდაცამეტი - იერუსალიმში.",
			"28": "<sup>28</sup> მოკვდა კეთილ სიბერეში, მაძღარი წლებით, სიმდიდრითა და დიდებით. მის ნაცვლად სოლომონი გამეფდა.",
			"29": "<sup>29</sup> დავით მეფის საქმენი, ადრინდელნი და უკანასკნელნი, სამუელ მისნის სიტყვებში, ნათან წინასწარმეტყველის სიტყვებში და გად მისნის სიტყვებშია აღწერილი,",
			"30": "<sup>30</sup> მთელს მის მეფობასთან, მის სიქველესთან და იმ ამბებთან ერთად, რომლებიც თავად მას, ისრაელს და სხვა ქვეყანათა სამეფოებს გადახდა.",
		}
	}
}
module.exports = book;