var book = {
	"name": "ಧರ್ಮೋಪದೇಶಕಾಂಡ",
	"numChapters": 34,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ಯೊರ್ದನ್ ನದಿಯ ಆಚೆ, ಪಾರಾನ್, ತೋಫೆಲ್, ಲಾಬಾನ್, ಹಚೇರೋತ್ ಮತ್ತು ದೀಜಾಹಾಬ್ ಎಂಬ ಸ್ಥಳಗಳ ನಡುವೆ, ಅರಣ್ಯದಲ್ಲಿ ಆರಾಬಾ ಎಂಬ ತಗ್ಗಾದ ಪ್ರದೇಶದಲ್ಲಿ ಸೂಫಿಗೆ ಎದುರಾಗಿ ಮೋಶೆಯು ಇಸ್ರಾಯೇಲರೆಲ್ಲರಿಗೆ ಮಾಡಿದ ಉಪದೇಶ ಮತ್ತು ಉಪನ್ಯಾಸಗಳು.",
			"2": "<sup>2</sup> ಹೋರೇಬಿನಿಂದ ಸೇಯೀರ್ ಬೆಟ್ಟಗಳ ಮಾರ್ಗವಾಗಿ ಕಾದೇಶ್‌ಬರ್ನೇಯದ ತನಕ ಹನ್ನೊಂದು ದಿನದ ಪ್ರಯಾಣ.",
			"3": "<sup>3</sup> ಇಸ್ರಾಯೇಲರು ಐಗುಪ್ತದೇಶವನ್ನು ಬಿಟ್ಟ ನಲ್ವತ್ತನೆಯ ವರುಷದ, ಹನ್ನೊಂದನೆಯ ತಿಂಗಳಿನ ಮೊದಲನೆಯ ದಿನದಲ್ಲಿ ಯೆಹೋವನು ತನಗೆ ಆಜ್ಞಾಪಿಸಿದ್ದನ್ನೆಲ್ಲಾ ಮೋಶೆಯು ಇಸ್ರಾಯೇಲರಿಗೆ ತಿಳಿಸಿದನು.",
			"4": "<sup>4</sup> ಯೆಹೋವನು ಹೆಷ್ಬೋನಿನಲ್ಲಿದ್ದ ಅಮೋರಿಯರ ಅರಸನಾದ ಸೀಹೋನನನ್ನೂ, ಅಷ್ಟಾರೋತ್, ಎದ್ರೈ ಎಂಬ ಪಟ್ಟಣಗಳಲ್ಲಿದ್ದ ಬಾಷಾನಿನ ಅರಸನಾದ ಓಗನನ್ನೂ ಜಯಿಸಿದ ತರುವಾಯವೇ ಮೋಶೆಯು ಇದನ್ನೆಲ್ಲಾ ತಿಳಿಸಿದನು.",
			"5": "<sup>5</sup> ಯೊರ್ದನ್ ನದಿಯ ಆಚೆ ಮೋವಾಬ್ಯರ ದೇಶದಲ್ಲಿ ಮೋಶೆಯು ಈ ಮುಂದಣ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ವಿವರಿಸುವುದಕ್ಕೆ ಪ್ರಾರಂಭಿಸಿ ಹೀಗೆ ಹೇಳಿದನು:",
			"6": "<sup>6</sup> “ಹೋರೇಬಿನಲ್ಲಿ ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಮಗೆ, ‘ನೀವು ಈ ಬೆಟ್ಟದ ಬಳಿಯಲ್ಲಿ ವಾಸಿಸಿದ್ದು ಸಾಕು.",
			"7": "<sup>7</sup> ಈಗ ಹಿಂದಿರುಗಿ ಅಮೋರಿಯರು ಇರುವ ಬೆಟ್ಟದ ಸೀಮೆಗೂ ಮತ್ತು ಅದಕ್ಕೆ ಸೇರಿದ ಪ್ರದೇಶದ ಕಡೆಗೆ ಪ್ರಯಾಣಮಾಡಿರಿ. ಅವು ಯಾವುವೆಂದರೆ: ತಗ್ಗಾದ ಪ್ರದೇಶ, ಬೆಟ್ಟದ ಮೇಲಿನ ಪ್ರದೇಶ, ಇಳಕಲಿನ ಪ್ರದೇಶ, ದಕ್ಷಿಣಸೀಮೆ ಮತ್ತು ಸಮುದ್ರತೀರ ಎಂಬ ನಾಡುಗಳನ್ನು ಒಳಗೊಂಡಿರುವ ಕಾನಾನ್ಯರ ದೇಶ, ಲೆಬನೋನ್ ಪರ್ವತ ಮತ್ತು ಯೂಫ್ರೇಟೀಸ್ ಎಂಬ ಮಹಾನದಿಯ ವರೆಗೆ ವಿಸ್ತರಿಸಿಕೊಂಡಿರುವ ಪ್ರದೇಶಗಳಿಗೆ ಹೋಗಿರಿ.",
			"8": "<sup>8</sup> ಆ ದೇಶವನ್ನು ನಿಮಗೇ ಕೊಟ್ಟಿದ್ದೇನೆ. ಯೆಹೋವನೆಂಬ ನಾನು ನಿಮ್ಮ ಪಿತೃಗಳಾದ ಅಬ್ರಹಾಮ್, ಇಸಾಕ್, ಯಾಕೋಬರಿಗೂ ಮತ್ತು ಅವರ ಸಂತತಿಯವರಿಗೂ ಆ ದೇಶವನ್ನು ಕೊಡುತ್ತೇನೆಂದು ಪ್ರಮಾಣಪೂರ್ವಕವಾಗಿ ವಾಗ್ದಾನ ಮಾಡಿದೆನಲ್ಲಾ. ಅದರಲ್ಲಿ ಪ್ರವೇಶಮಾಡಿ ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳಿರಿ’” ಎಂದು ಅಜ್ಞಾಪಿಸಿದನು.",
			"9": "<sup>9</sup> ಆ ಕಾಲದಲ್ಲಿ ನಾನು ನಿಮ್ಮ ಸಂಗಡ ಮಾತನಾಡಿ, “ನಿಮ್ಮ ಹೊಣೆಯನ್ನು ನಾನೊಬ್ಬನೇ ಹೊರುವುದು ಅಸಾಧ್ಯ.",
			"10": "<sup>10</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಅಧಿಕ ಸಂಖ್ಯೆಯಲ್ಲಿ ಹೆಚ್ಚಿಸಿದ್ದರಿಂದ ನೀವು ಈಗ ಆಕಾಶದ ನಕ್ಷತ್ರಗಳಷ್ಟು ಅಸಂಖ್ಯರಾಗಿದ್ದೀರಿ.",
			"11": "<sup>11</sup> ನೀವು ಈಗ ಇರುವುದಕ್ಕಿಂತಲೂ ಇನ್ನು ಸಾವಿರದಷ್ಟಾಗುವಂತೆ ನಿಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಹೆಚ್ಚಿಸಿ, ತಾನು ವಾಗ್ದಾನಮಾಡಿದ ಪ್ರಕಾರವೇ ನಿಮಗೆ ಶುಭಗಳನ್ನು ಅನುಗ್ರಹಿಸಲಿ.",
			"12": "<sup>12</sup> ಆದರೆ ನಿಮ್ಮಿಂದಾಗುವ ತೊಂದರೆಗಳನ್ನೂ, ಹೊಣೆಯನ್ನು ಮತ್ತು ವ್ಯಾಜ್ಯಗಳನ್ನೂ ನಾನೊಬ್ಬನೇ ನಿಬಾಯಿಸುವುದು ಹೇಗೆ?",
			"13": "<sup>13</sup> ಆದುದರಿಂದ ನೀವು ಪ್ರತಿಯೊಂದು ಕುಲದಿಂದ ಬುದ್ಧಿವಿವೇಕಗಳಿಂದ ಪ್ರಸಿದ್ಧರಾದ ಪುರುಷರನ್ನು ಆರಿಸಿಕೊಳ್ಳಿರಿ. ನಾನು ಅವರನ್ನು ನಿಮಗೆ ಅಧಿಪತಿಗಳನ್ನಾಗಿ ನೇಮಿಸುವೆನು” ಎಂದು ಹೇಳಿದೆನು.",
			"14": "<sup>14</sup> ಅದಕ್ಕೆ ನೀವು, “ನಿನ್ನ ಆಲೋಚನೆಯಂತೆ ನಡೆಯುವುದು ಹಿತವಾದದ್ದೇ” ಎಂದು ಉತ್ತರಕೊಟ್ಟಿರಿ.",
			"15": "<sup>15</sup> ಆಗ ನಾನು ನಿಮ್ಮಲ್ಲಿ ಪ್ರಸಿದ್ಧರಾದ, ಬುದ್ಧಿವಂತರನ್ನು ಆರಿಸಿಕೊಂಡು ಒಂದೊಂದು ಕುಲದಲ್ಲಿ ಸಾವಿರ ಜನರಿಗೆ ಒಬ್ಬರಂತೆ, ನೂರು ಜನರಿಗೆ ಒಬ್ಬರಂತೆ, ಐವತ್ತು ಜನರಿಗೆ ಒಬ್ಬರಂತೆ ಮತ್ತು ಹತ್ತು ಜನರಿಗೆ ಒಬ್ಬರಂತೆ ಅಧಿಕಾರಿಗಳನ್ನಾಗಿಯೂ ಮತ್ತು ಉಪ ಅಧಿಕಾರಿಗಳನ್ನಾಗಿಯೂ ನೇಮಿಸಿದೆನು.",
			"16": "<sup>16</sup> ಆ ನ್ಯಾಯಾಧಿಪತಿಗಳಿಗೆ ನಾನು ಆ ಕಾಲದಲ್ಲಿ ಅಪ್ಪಣೆಮಾಡಿ, “ನೀವು ಸ್ವಕುಲದವರ ವ್ಯಾಜ್ಯಗಳನ್ನು ವಿಚಾರಿಸಬೇಕು. ಅವರು ಇಸ್ರಾಯೇಲರೊಡನೆ ವ್ಯಾಜ್ಯವಾಡಿದರೂ, ಅನ್ಯರೊಡನೆ ವ್ಯಾಜ್ಯವಾಡಿದರೂ ನೀವು ನ್ಯಾಯದ ಪ್ರಕಾರವೇ ತೀರ್ಪುಮಾಡಬೇಕು.",
			"17": "<sup>17</sup> ನ್ಯಾಯವಿಚಾರಣೆ ಮಾಡುವಾಗ ಮುಖದಾಕ್ಷಿಣ್ಯಮಾಡದೆ, ಪ್ರಮುಖರನ್ನೂ ಹಾಗು ಅಲ್ಪರನ್ನೂ ಸಮಾನವಾಗಿ ಕಾಣಬೇಕು. ನೀವು ದೇವರ ಹೆಸರಿನಲ್ಲಿ ನ್ಯಾಯತೀರಿಸುವವರಾದ ಕಾರಣ ಮನುಷ್ಯರ ಮುಖವನ್ನು ನೋಡಿ ಹೆದರಬೇಡಿರಿ. ನೀವು ತೀರಿಸಲಿಕ್ಕೆ ಆಗದ ವ್ಯಾಜ್ಯಗಳನ್ನು ನನ್ನ ಬಳಿಗೆ ತರಬೇಕು; ನಾನೇ ಅವುಗಳನ್ನು ತೀರಿಸುವೆನು” ಎಂದು ಹೇಳಿದೆನು.",
			"18": "<sup>18</sup> ಅದಲ್ಲದೆ ನೀವು ಮಾಡಬೇಕಾದ ಎಲ್ಲಾ ಕಾರ್ಯಗಳ ವಿಷಯದಲ್ಲಿಯೂ ನಾನು ಆ ಕಾಲದಲ್ಲಿ ನಿಮಗೆ ಆಜ್ಞೆಕೊಟ್ಟೆನು.",
			"19": "<sup>19</sup> ನಾವು ಹೋರೇಬಿನಿಂದ ಹೊರಟು ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಆಜ್ಞಾಪಿಸಿದಂತೆ ಅಮೋರಿಯರ ಬೆಟ್ಟದ ಸೀಮೆಯ ಮಾರ್ಗವನ್ನು ಹಿಡಿದು ನೀವು ನೋಡಿದ ಆ ಘೋರವಾದ ಮಹಾ ಅರಣ್ಯದಲ್ಲಿ ನಡೆದು ಕಾದೇಶ್‌ಬರ್ನೇಯಕ್ಕೆ ಸೇರಿದೆವು.",
			"20": "<sup>20</sup> ಆಗ ನಾನು, “ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಮಗೆ ಕೊಡುವ ಅಮೋರಿಯರ ಬೆಟ್ಟದ ಸೀಮೆಯ ಹತ್ತಿರಕ್ಕೆ ಬಂದಿದ್ದೀರಿ.",
			"21": "<sup>21</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಆ ದೇಶವನ್ನು ನಿಮಗೇ ಕೊಟ್ಟಿದ್ದಾನೆ; ನಿಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಯೆಹೋವನು ಹೇಳಿದಂತೆ ಆ ಬೆಟ್ಟವನ್ನು ಹತ್ತಿ ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳಿರಿ; ನೀವು ಭಯಪಡದೆ ಧೈರ್ಯವಾಗಿಯೇ ಇರಬೇಕು” ಎಂದು ಹೇಳಿದೆನು.",
			"22": "<sup>22</sup> ಆಗ ನೀವೆಲ್ಲರೂ ನನ್ನ ಬಳಿಗೆ ಬಂದು, “ನಮಗಿಂತ ಮುಂದಾಗಿ ಜನರನ್ನು ಕಳುಹಿಸುವೆವು. ಅವರು ನಮಗೋಸ್ಕರ ಆ ದೇಶವನ್ನು ಪರೀಕ್ಷಿಸಿ ನೋಡಿ, ನಾವು ಹತ್ತಿ ಹೋಗಬೇಕಾದ ದಾರಿಯ ವಿಷಯದಲ್ಲೂ ಮತ್ತು ಸೇರಬೇಕಾದ ಊರುಗಳ ವಿಷಯದಲ್ಲೂ ನಮಗೆ ವರ್ತಮಾನ ತಿಳಿಸಲಿ” ಎಂದು ಹೇಳಿದಿರಿ.",
			"23": "<sup>23</sup> ಅದು ಒಳ್ಳೆಯ ಆಲೋಚನೆಯೆಂದು ನಾನು ತಿಳಿದುಕೊಂಡು ನಿಮ್ಮಲ್ಲಿ ಕುಲಕ್ಕೆ ಒಬ್ಬೊಬ್ಬನಂತೆ ಹನ್ನೆರಡು ಜನರನ್ನು ಆರಿಸಿಕೊಂಡೆನು.",
			"24": "<sup>24</sup> ಅವರು ಹೋಗಿ ಆ ಬೆಟ್ಟದ ಸೀಮೆಯನ್ನು ಹತ್ತಿ, ಎಷ್ಕೋಲ್ ಎಂಬ ತಗ್ಗಿನ ಬಳಿಗೆ ಬಂದು ಆ ಪ್ರದೇಶದಲ್ಲಿ ಸಂಚರಿಸಿ ನೋಡಿದರು.",
			"25": "<sup>25</sup> ಅವರು ಅಲ್ಲಿನ ಹಣ್ಣುಗಳಲ್ಲಿ ಕೆಲವನ್ನು ತಂದು ತೋರಿಸಿ, “ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಮಗೆ ಕೊಡುವ ಆ ದೇಶವು ಒಳ್ಳೆಯ ದೇಶ” ಎಂದು ನಮಗೆ ತಿಳಿಸಿದರು.",
			"26": "<sup>26</sup> ಆದರೂ ನೀವು ಬೆಟ್ಟವನ್ನು ಹತ್ತಿ ನೋಡಲು ಇಷ್ಟಪಡದೆ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಆಜ್ಞೆಯನ್ನು ಉಲ್ಲಂಘಿಸಿದಿರಿ.",
			"27": "<sup>27</sup> ನೀವು ನಿಮ್ಮ ಡೇರೆಗಳಲ್ಲಿ ಗುಣುಗುಟ್ಟುತ್ತಾ, “ಯೆಹೋವನು ನಮ್ಮನ್ನು ಐಗುಪ್ತದೇಶದಿಂದ ಬರಮಾಡಿದ್ದು ಹಗೆತನದಿಂದಲೇ; ಆತನು ನಮ್ಮನ್ನು ಸಂಹರಿಸಬೇಕೆಂದು ಅಮೋರಿಯರ ಕೈಗೆ ಒಪ್ಪಿಸುತ್ತಾನೆ.",
			"28": "<sup>28</sup> ನಾವು ಹೋಗಬೇಕಾದ ಆ ಸೀಮೆ ಎಂಥದೆಂದು ಆಲೋಚಿಸಿರಿ; ಅಲ್ಲಿ ಹೋಗಿದ್ದ ನಮ್ಮ ಸಹೋದರರು ನಮಗೆ, ‘ಆ ದೇಶದ ಜನರು ನಮಗಿಂತ ಬಲಿಷ್ಠರಾಗಿಯೂ ಮತ್ತು ಎತ್ತರವಾಗಿಯೂ ಇದ್ದಾರೆ. ಅವರಿರುವ ಪಟ್ಟಣಗಳು ದೊಡ್ಡದಾಗಿಯೂ ಮತ್ತು ಆಕಾಶವನ್ನು ಮುಟ್ಟುವ ಕೋಟೆಕೊತ್ತಲುಗಳಿಂದ ಇವೆ. ಅಲ್ಲಿ ನಾವು ಎತ್ತರವಾದ ಪುರುಷರನ್ನು ಅಂದರೆ ಅನಾಕೀಮ್ ವಂಶಸ್ಥರ ಮಕ್ಕಳನ್ನು ನೋಡಿದ್ದೇವೆ’ ಎಂದು ಹೇಳಿ ನಮ್ಮ ಧೈರ್ಯವನ್ನು ಕೆಡಿಸಿದ್ದಾರೆ” ಅಂದುಕೊಳ್ಳುತ್ತಿದ್ದಿರಿ.",
			"29": "<sup>29</sup> ಅದಕ್ಕೆ ನಾನು, “ಕಳವಳಪಡಬೇಡಿರಿ, ಅವರಿಗೆ ಭಯಪಡಬೇಡಿರಿ.",
			"30": "<sup>30</sup> ನಿಮ್ಮ ಮುಂದೆ ಮಾರ್ಗದರ್ಶಕನಾಗಿ ಹೋಗುವ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಐಗುಪ್ತ ದೇಶದಲ್ಲಿಯೂ, ನೀವು ನೋಡಿದ ಅರಣ್ಯದಲ್ಲಿಯೂ ಪ್ರತ್ಯಕ್ಷನಾಗಿ ನಿಮಗೋಸ್ಕರ ಯುದ್ಧಮಾಡಿದಂತೆಯೇ ಈಗಲೂ ನಿಮ್ಮವನಾಗಿ ಯುದ್ಧಮಾಡುವನು.",
			"31": "<sup>31</sup> ನೀವು ಈ ಸ್ಥಳಕ್ಕೆ ಸೇರುವ ತನಕ ನಿಮ್ಮ ಪ್ರಯಾಣದಲ್ಲೆಲ್ಲಾ ಒಬ್ಬ ತಂದೆ ತನ್ನ ಮಗನನ್ನು ಹೇಗೆ ಮುನ್ನಡೆಸುತ್ತಾನೋ ಹಾಗೆಯೇ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಹೊತ್ತನಲ್ಲವೇ?” ಎಂದು ನಿಮಗೆ ಹೇಳಿದೆನು.",
			"32": "<sup>32</sup> ಆದರೂ ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ನಂಬಲೇ ಇಲ್ಲ.",
			"33": "<sup>33</sup> ನೀವು ಹೋಗಬೇಕಾದ ದಾರಿಯನ್ನು ತೋರಿಸುವುದಕ್ಕೂ, ದಂಡು ಇಳಿಯಬೇಕಾದ ಸ್ಥಳಗಳನ್ನು ಗೊತ್ತುಮಾಡುವುದಕ್ಕೂ ರಾತ್ರಿಯಲ್ಲಿ ಬೆಂಕಿಯೋಪಾದಿಯಲ್ಲಿಯೂ ಮತ್ತು ಹಗಲಿನಲ್ಲಿ ಮೇಘದೋಪಾದಿಯಲ್ಲಿಯೂ ನಿಮ್ಮ ಮಾರ್ಗದಲ್ಲಿ ನಿಮ್ಮನ್ನು ಮುನ್ನಡೆಸಿದ ದೇವರನ್ನು ನೀವು ನಂಬಲಿಲ್ಲ.",
			"34": "<sup>34</sup> ಯೆಹೋವನು ನಿಮ್ಮ ಮಾತುಗಳನ್ನು ಕೇಳಿ ಕೋಪಗೊಂಡನು.",
			"35": "<sup>35</sup> ಆತನು, “ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ವಾಗ್ದಾನಮಾಡಿದ ಆ ಒಳ್ಳೇ ದೇಶವನ್ನು ಈ ದುಷ್ಟ ಸಂತತಿಯವರಲ್ಲಿ ಯಾರೂ ನೋಡುವುದಿಲ್ಲ” ಎಂದು ಪ್ರಮಾಣಮಾಡಿದನು.",
			"36": "<sup>36</sup> ಮತ್ತು ಯೆಹೋವನು “ಯೆಫುನ್ನೆಯ ಮಗನಾದ ಕಾಲೇಬನೊಬ್ಬನೇ ಯೆಹೋವನನ್ನು ಮನಃಪೂರ್ವಕವಾಗಿ ಅನುಸರಿಸಿದ್ದರಿಂದ, ಅವನೇ ಅದನ್ನು ನೋಡುವನು. ಅವನು ಸಂಚರಿಸಿದ ಪ್ರದೇಶವನ್ನು ಅವನಿಗೂ ಮತ್ತು ಅವನ ಸಂತತಿಯವರಿಗೂ ಕೊಡುವೆನು” ಎಂದು ಹೇಳಿದನು.",
			"37": "<sup>37</sup> ಅದು ಮಾತ್ರವಲ್ಲದೆ ಯೆಹೋವನು ನಿಮ್ಮಿಂದ ನನ್ನ ಮೇಲೆಯೂ ಕೋಪಗೊಂಡು, “ನೀನೂ ಆ ದೇಶಕ್ಕೆ ಸೇರುವುದಿಲ್ಲ;",
			"38": "<sup>38</sup> ಆದರೆ ನಿನ್ನ ಮುಂದೆ ನಿಂತಿರುವ ನಿನ್ನ ಸೇವಕನು, ನೂನನ ಮಗನೂ ಆದ ಯೆಹೋಶುವನು ಅಲ್ಲಿಗೆ ಸೇರುವನು. ಅವನೇ ಇಸ್ರಾಯೇಲರಿಗೆ ಆ ದೇಶವನ್ನು ಸ್ವಾಧೀನಪಡಿಸುವನು. ಆದುದರಿಂದ ಅವನನ್ನು ಧೈರ್ಯಗೊಳಿಸು.",
			"39": "<sup>39</sup> ಆದರೆ ಪರರ ಪಾಲಾಗುವರೆಂದು ನೀವು ಹೇಳಿದ ನಿಮ್ಮ ಮನೆಯವರೂ ಹಾಗು ಒಳ್ಳೆಯದನ್ನು ಮತ್ತು ಕೆಟ್ಟದ್ದನ್ನು ಅರಿಯದ ನಿಮ್ಮ ಮಕ್ಕಳೂ ಅಲ್ಲಿಗೆ ಸೇರುವರು. ಅವರಿಗೇ ಆ ದೇಶವನ್ನು ಕೊಡುವೆನು; ಅವರು ಅದನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವರು.",
			"40": "<sup>40</sup> ನೀವಾದರೋ ಹಿಂದಿರುಗಿ ಕೆಂಪು ಸಮುದ್ರದ ಮಾರ್ಗವನ್ನು ಹಿಡಿದು ಅರಣ್ಯಕ್ಕೆ ಹೊರಟುಹೋಗಿರಿ” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"41": "<sup>41</sup> ಅದಕ್ಕೆ ನೀವು, “ನಾವು ಯೆಹೋವನಿಗೆ ದ್ರೋಹಿಗಳಾದೆವು. ಆದರೂ ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಆಜ್ಞಾಪಿಸಿದಂತೆ ನಾವೇ ಈಗ ಆ ಬೆಟ್ಟದ ಸೀಮೆಯನ್ನು ಹತ್ತಿ ಯುದ್ಧಮಾಡುವೆವು” ಎಂದು ಉತ್ತರಕೊಟ್ಟಿರಿ. ಮತ್ತು ನೀವೆಲ್ಲರೂ ನಿಮ್ಮ ನಿಮ್ಮ ಆಯುಧಗಳನ್ನು ತೆಗೆದುಕೊಂಡು, ಆ ಬೆಟ್ಟದ ಸೀಮೆಯನ್ನು ಹತ್ತಿ ಜಯಿಸುವುದು ಅಲ್ಪಕಾರ್ಯವೆಂದು ಭಾವಿಸಿ ಹೊರಡುವುದಕ್ಕಿದ್ದಿರಿ.",
			"42": "<sup>42</sup> ಆದರೆ ಯೆಹೋವನು ನನಗೆ, “ನಾನು ಇವರ ಮಧ್ಯದಲ್ಲಿ ಇರುವುದಿಲ್ಲವಾದುದರಿಂದ ಇವರು ಸೋತುಹೋಗುವರು. ಇವರು ಯುದ್ಧಮಾಡಲೂ ಬಾರದು, ಆ ಬೆಟ್ಟದ ಸೀಮೆಗೆ ಹೋಗಲೂ ಬಾರದು ಎಂದು ಇವರಿಗೆ ಆಜ್ಞಾಪಿಸು” ಎಂದು ಹೇಳಿದನು.",
			"43": "<sup>43</sup> ನಾನು ಆ ಮಾತನ್ನು ನಿಮಗೆ ತಿಳಿಸಿದರೂ, ನೀವು ಕಿವಿಗೊಡದೆ ಯೆಹೋವನ ಆಜ್ಞೆಯನ್ನು ತಾತ್ಸಾರಮಾಡಿ ಸೊಕ್ಕಿನಿಂದ ಆ ಬೆಟ್ಟವನ್ನು ಹತ್ತಿದಿರಿ.",
			"44": "<sup>44</sup> ಆಗ ಬೆಟ್ಟದಲ್ಲಿದ್ದ ಅಮೋರಿಯರು ನಿಮಗೆ ವಿರುದ್ಧವಾಗಿ ಹೊರಟು ಜೇನುಹುಳಗಳಂತೆ ನಿಮ್ಮನ್ನು ಮುತ್ತಿ ಸೇಯೀರಿನಲ್ಲಿ ಹೊರ್ಮದ ವರೆಗೂ ಬೆನ್ನಟ್ಟಿ ಸಂಹರಿಸಿದರು.",
			"45": "<sup>45</sup> ನೀವು ಹಿಂದಿರುಗಿ ಬಂದು ಯೆಹೋವನ ಮುಂದೆ ಗೋಳಾಡಿದಾಗ ಆತನು ನಿಮ್ಮ ಮೊರೆಯನ್ನು ಕೇಳಲೂ ಇಲ್ಲ ಮತ್ತು ರಕ್ಷಿಸಲೂ ಇಲ್ಲ.",
			"46": "<sup>46</sup> ಆ ಮೇಲೆ ನೀವು ಕಾದೇಶಿನಲ್ಲಿ ಬಹುಕಾಲ ವಾಸವಾಗಿದ್ದಿರಿ ಎಂದು ಹೇಳಿದನು."
		},
		"2": {
			"1": "<sup>1</sup> ಆಗ ಯೆಹೋವನು ನನಗೆ ಆಜ್ಞಾಪಿಸಿದಂತೆ ನಾವು ಹಿಂದಿರುಗಿ ಅರಣ್ಯಕ್ಕೆ ಹೊರಟು ಕೆಂಪು ಸಮುದ್ರದ ಮಾರ್ಗದಲ್ಲಿ ನಡೆದು ಅನೇಕ ದಿನಗಳು ಸೇಯೀರ್ ಬೆಟ್ಟದ ಸೀಮೆಯನ್ನು ಸುತ್ತುತ್ತಿದ್ದೆವು.",
			"2": "<sup>2</sup> ಆ ಮೇಲೆ ಯೆಹೋವನು ನನಗೆ,",
			"3": "<sup>3</sup> “ನೀವು ಈ ಬೆಟ್ಟದ ಸೀಮೆಯನ್ನು ಸುತ್ತಿದ್ದು ಸಾಕು; ಉತ್ತರದಿಕ್ಕಿಗೆ ತಿರುಗಿಕೊಳ್ಳಿರಿ.",
			"4": "<sup>4</sup> ಮತ್ತು ನೀನು ಇಸ್ರಾಯೇಲರಿಗೆ ಹೀಗೆ ಆಜ್ಞಾಪಿಸು, ‘ಸೇಯೀರ್ ದೇಶದಲ್ಲಿ ವಾಸವಾಗಿರುವ ನಿಮ್ಮ ಬಂಧುಗಳಾದ ಏಸಾವನ ವಂಶದವರ ದೇಶವನ್ನು ದಾಟುವುದಕ್ಕಿದ್ದೀರಷ್ಟೆ. ಅವರು ನಿಮಗೆ ಭಯಪಡುವರು; ನೀವು ಅವರ ಸಂಗಡ ಯುದ್ಧಕ್ಕೆ ಹೋಗದೆ ಬಹಳ ಜಾಗರೂಕತೆಯಿಂದಿರಬೇಕು.",
			"5": "<sup>5</sup> ನಾನು ಸೇಯೀರ್ ಬೆಟ್ಟದ ಸೀಮೆಯನ್ನು ಏಸಾವ್ಯರಿಗೇ ಸ್ವದೇಶವಾಗಿ ಕೊಟ್ಟಿರುವುದರಿಂದ ಅದರಲ್ಲಿ ನಿಮಗೆ ಹೆಜ್ಜೆಯಿಡುವಷ್ಟು ನೆಲವನ್ನಾದರೂ ಕೊಡುವುದಿಲ್ಲ.",
			"6": "<sup>6</sup> ನೀವು ಅಲ್ಲಿ ಹಣವನ್ನು ಕೊಟ್ಟು ಆಹಾರಪದಾರ್ಥಗಳನ್ನು ತಿನ್ನಬೇಕು; ನೀರನ್ನು ಕೊಂಡುಕೊಂಡು ಕುಡಿಯಬೇಕು.",
			"7": "<sup>7</sup> ನೀವು ಕೈಹಾಕಿದ ಎಲ್ಲಾ ಕೆಲಸಗಳನ್ನೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಸಫಲಪಡಿಸಿದ್ದಾನಲ್ಲಾ. ಈ ದೊಡ್ಡ ಅರಣ್ಯದಲ್ಲಿ ನೀವು ಸಂಚರಿಸುತ್ತಿರುವಾಗೆಲ್ಲಾ ಆತನು ನಿಮ್ಮನ್ನು ಪರಾಂಬರಿಸುತ್ತಾ ಬಂದನು. ಈ ನಲ್ವತ್ತು ವರುಷ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮ ಸಂಗಡ ಇದ್ದುದರಿಂದ ನಿಮಗೆ ಏನೂ ಕೊರತೆಯಾಗಲಿಲ್ಲ’” ಎಂದು ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ಆದಕಾರಣ ನಾವು ಸೇಯೀರಿನಲ್ಲಿರುವ ನಮ್ಮ ಬಂಧುಗಳಾದ ಏಸಾವ್ಯರಿಗೆ ಗೊತ್ತಾಗದಂತೆ ಎಚ್ಚರಿಕೆಯಿಂದ ಅರಾಬದ ಮಾರ್ಗವನ್ನು ಬಿಟ್ಟು ಏಲತ್ ಮತ್ತು ಎಚ್ಯೋನ್ ಗೆಬೆರ್ ಎಂಬ ಪಟ್ಟಣಗಳಿಂದ ಪ್ರಯಾಣಮಾಡಿ ಮೋವಾಬ್ಯರ ಅಡವಿಯ ದಾರಿಯಲ್ಲಿ ನಡೆದೆವು.",
			"9": "<sup>9</sup> ಆಗ ಯೆಹೋವನು ನನಗೆ, “ನೀವು ಮೋವಾಬ್ಯರಿಗೆ ವೈರಿಗಳಾಗಿ ನಡೆದು ಅವರೊಡನೆ ಯುದ್ಧಮಾಡಬಾರದು. ಲೋಟನ ವಂಶದವರಾದ ಅವರಿಗೆ ನಾನು ಆರ್ ಎಂಬ ಪ್ರದೇಶವನ್ನು ಸ್ವದೇಶವಾಗುವುದಕ್ಕೆ ಕೊಟ್ಟಿರುವುದರಿಂದ ಅದರಲ್ಲಿ ನಿಮಗೆ ಸ್ವಾಸ್ಥ್ಯವನ್ನು ಕೊಡುವುದಿಲ್ಲ” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"10": "<sup>10</sup> ಏಮಿಯರು ಮೊದಲು ಆ ದೇಶದಲ್ಲಿ ವಾಸವಾಗಿದ್ದರು. ಅವರು ಬಲಿಷ್ಠರೂ, ಬಹುಜನರೂ ಮತ್ತು ಅನಾಕೀಮ್ಯರಂತೆ ಎತ್ತರವಾದ ಪುರುಷರಾಗಿದ್ದರು.",
			"11": "<sup>11</sup> ಅವರು ಅನಾಕ್ಯರಂತೆ ರೆಫಾಯರಿಗೆ ಸೇರಿದವರೆಂದು ಹೇಳುವುದುಂಟು; ಆದರೆ ಮೋವಾಬ್ಯರು ಅವರನ್ನು ಏಮಿಯರೆಂದು ಹೇಳುತ್ತಾರೆ.",
			"12": "<sup>12</sup> ಹೋರಿಯರು ಸಹ ಪೂರ್ವಕಾಲದಲ್ಲಿ ಸೇಯೀರಿನಲ್ಲಿ ವಾಸವಾಗಿದ್ದರು. ತರುವಾಯ ಇಸ್ರಾಯೇಲರು ತಮಗೆ ಯೆಹೋವನು ಕೊಟ್ಟ ದೇಶವನ್ನು ಹೇಗೆ ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡರೋ ಹಾಗೆಯೇ ಏಸಾವ್ಯರು ಬಂದು ಹೋರಿಯರನ್ನು ಸಂಹರಿಸಿ ಅವರ ದೇಶದಲ್ಲಿ ವಾಸಮಾಡಿದರು.",
			"13": "<sup>13</sup> ಆಗ ನಾನು, “ಎದ್ದು ಜೆರೆದ್ ಹಳ್ಳವನ್ನು ದಾಟಿರಿ” ಎಂದು ಹೇಳಲು ನಾವು ಜೆರೆದ್ ಹಳ್ಳವನ್ನು ದಾಟಿದೆವು.",
			"14": "<sup>14</sup> ನಾವು ಕಾದೇಶ್‌ಬರ್ನೇಯದಿಂದ ಹೊರಟು ಜೆರೆದ್ ಹಳ್ಳವನ್ನು ದಾಟಲು ಮೂವತ್ತೆಂಟು ವರುಷವಾಯಿತು. ಯೆಹೋವನು ಪ್ರಮಾಣಪೂರ್ವಕವಾಗಿ ಹೇಳಿದ್ದಂತೆ ಅಷ್ಟರಲ್ಲಿ ಆ ತಲೆಮಾರಿನ ಸೈನಿಕರೆಲ್ಲರೂ ಸತ್ತುಹೋಗಿದ್ದರು.",
			"15": "<sup>15</sup> ಯೆಹೋವನ ಹಸ್ತವು ಅವರಿಗೆ ವಿರುದ್ಧವಾಗಿದ್ದುದರಿಂದ ಪಾಳೆಯದಲ್ಲಿ ಒಬ್ಬನೂ ಉಳಿಯದಂತೆ ನಾಶವಾದರು.",
			"16": "<sup>16</sup> ಆ ಕಾಲದಲ್ಲಿ ಯುದ್ಧನಿಪುಣರಾಗಿದ್ದ ಸೈನಿಕರೆಲ್ಲರೂ ಸತ್ತುಹೋದರು.",
			"17": "<sup>17</sup> ಆಗ ಯೆಹೋವನು ನನ್ನೊಂದಿಗೆ ಮಾತನಾಡಿ ನನಗೆ,",
			"18": "<sup>18</sup> “ನೀವು ಈಗ ಮೋವಾಬ್ಯರ ಪ್ರಾಂತ್ಯವಾಗಿರುವ ಆರ್ ಎಂಬ ಪ್ರದೇಶವನ್ನು ದಾಟುವವರಾಗಿದ್ದೀರಿ.",
			"19": "<sup>19</sup> ಮುಂದೆ ನೀವು ಅಮೋನಿಯರ ಹತ್ತಿರಕ್ಕೆ ಬರುವಾಗ ಅವರಿಗೆ ತೊಂದರೆಪಡಿಸಲೂ ಬೇಡಿರಿ, ಅವರಿಗೆ ವೈರಿಗಳಾಗಿ ನಡೆದು ಯುದ್ಧಮಾಡಲೂಬೇಡಿರಿ. ನಾನು ಆ ದೇಶವನ್ನು ಲೋಟನ ವಂಶದವರಾದ ಅಮ್ಮೋನಿಯರಿಗೆ ಸ್ವಾಸ್ತ್ಯವಾಗಿ ಕೊಟ್ಟಿರುವುದರಿಂದ ನಿಮಗೆ ಅದರಲ್ಲಿ ಯಾವ ಭಾಗವನ್ನಾದರೂ ಕೊಡುವುದಿಲ್ಲ.”",
			"20": "<sup>20</sup> ಪೂರ್ವಕಾಲದಲ್ಲಿ ರೆಫಾಯರು ಆ ದೇಶದಲ್ಲಿ ವಾಸವಾಗಿದ್ದುದರಿಂದ ಅದು ರೆಫಾಯರ ದೇಶವೆನಿಸಿಕೊಳ್ಳುತ್ತದೆ. ಅಮ್ಮೋನಿಯರು ಅವರನ್ನು ಜಂಜುಮ್ಯರೆಂದು ಹೇಳುತ್ತಾರೆ.",
			"21": "<sup>21</sup> ಅವರು ಬಲಿಷ್ಠರೂ, ಬಹುಜನರೂ ಮತ್ತು ಅನಾಕ್ಯರಂತೆ ಎತ್ತರವಾದ ಪುರುಷರೂ ಆಗಿದ್ದರು.",
			"22": "<sup>22</sup> ಆದರೆ ಹೇಗೆ ಯೆಹೋವನು ಸೇಯೀರಿನಲ್ಲಿರುವ ಏಸಾವ್ಯರಿಗೋಸ್ಕರ ಹೋರಿಯರನ್ನು ಹೊರಡಿಸಿದಾಗ ಏಸಾವ್ಯರು ಆ ದೇಶವನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡು ಇಂದಿನವರೆಗೂ ಅಲ್ಲೇ ವಾಸವಾಗಿದ್ದಾರೋ,",
			"23": "<sup>23</sup> ಮತ್ತು ಹೇಗೆ ಕಫ್ತೋರಿಂದ ಬಂದ ಕಫ್ತೋರ್ಯರು ಗಾಜಾ ಪಟ್ಟಣದವರೆಗೂ ಹಳ್ಳಿಗಳಲ್ಲಿದ್ದ ಅವ್ವಿಯರನ್ನು ನಾಶಮಾಡಿ ಅವರ ಸ್ಥಳದಲ್ಲಿ ವಾಸಮಾಡಿದರೋ, ಹಾಗೆಯೇ ಯೆಹೋವನು ರೆಫಾಯರನ್ನು ಅಮ್ಮೋನಿಯರ ನಾಡಿನಿಂದ ಹೊರಗೆ ಹೋಗುವಂತೆ ಮಾಡಿದನು. ಅಮ್ಮೋನಿಯರು ಆ ದೇಶವನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡು ಅವರಲ್ಲಿ ವಾಸಮಾಡ ತೊಡಗಿದರು.",
			"24": "<sup>24</sup> “ಈಗ ನೀವು ಹೊರಟು ಅರ್ನೋನ್ ಹಳ್ಳವನ್ನು ದಾಟಿರಿ. ಅಮೋರಿಯನಾಗಿರುವ ಹೆಷ್ಬೋನಿನ ಅರಸನಾದ ಸೀಹೋನನು ನಿಮ್ಮಿಂದ ಸೋತುಹೋಗುವನು, ಅವನ ರಾಜ್ಯವು ನಿಮಗೇ ವಶವಾಗಬೇಕೆಂದು ನಾನು ಅನುಗ್ರಹಿಸಿದ್ದೇನೆ. ಅವನೊಡನೆ ಯುದ್ಧಮಾಡಿ, ಆ ದೇಶವನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವುದಕ್ಕೆ ಪ್ರಾರಂಭಿಸಿರಿ.",
			"25": "<sup>25</sup> ಲೋಕದಲ್ಲಿರುವ ಎಲ್ಲಾ ಜನಗಳಿಗೂ ನಿಮ್ಮಿಂದ ದಿಗಿಲೂ ಮತ್ತು ಹೆದರಿಕೆಯೂ ಉಂಟಾಗುವಂತೆ ಮಾಡುತ್ತೇನೆ. ಇಂದಿನಿಂದ ನಿಮ್ಮ ಸುದ್ದಿಯನ್ನು ಕೇಳಿದ ಮಾತ್ರಕ್ಕೆ ಎಲ್ಲರೂ ಗಡಗಡನೆ ನಡುಗಿ ಸಂಕಟಪಡುವರು” ಎಂದು ಹೇಳಿದನು.",
			"26": "<sup>26</sup> ಆಗ ನಾನು ಕೆದೇಮೋತಿನ ಅರಣ್ಯದಿಂದ ಹೆಷ್ಬೋನಿನ ಅರಸನಾದ ಸೀಹೋನನ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿ ಅವನಿಗೆ,",
			"27": "<sup>27</sup> “ನಿನ್ನ ದೇಶವನ್ನು ದಾಟಿಹೋಗುವುದಕ್ಕೆ ನಮಗೆ ಅಪ್ಪಣೆಯಾಗಬೇಕು. ನಾವು ಎಡಬಲಕ್ಕೆ ತಿರುಗದೆ ದಾರಿಹಿಡಿದು ನಡೆದುಹೋಗುವೆವು.",
			"28": "<sup>28</sup> ನಮ್ಮಿಂದ ಕ್ರಯತೆಗೆದುಕೊಂಡು ಆಹಾರಪದಾರ್ಥಗಳನ್ನೂ ಮತ್ತು ಕುಡಿಯಲಿಕ್ಕೆ ನೀರನ್ನೂ ಕೊಡಿರಿ.",
			"29": "<sup>29</sup> ನಾವು ಯೊರ್ದನ್ ನದಿಯನ್ನು ದಾಟಿ ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಮಗೆ ಕೊಡುವ ದೇಶವನ್ನು ಸೇರಬೇಕೆಂದಿದ್ದೇವೆ. ಸೇಯೀರಿನಲ್ಲಿರುವ ಏಸಾವ್ಯರೂ ಮತ್ತು ಆರ್ ಪ್ರದೇಶದಲ್ಲಿರುವ ಮೋವಾಬ್ಯರೂ ನಮಗೆ ದಾರಿ ಕೊಟ್ಟಂತೆ ನೀನೂ ಕೊಡಬೇಕು. ಕಾಲ್ನಡೆಯಾಗಿ ನಿನ್ನ ದೇಶವನ್ನು ದಾಟಿಹೋಗುವುದಕ್ಕೆ ನಿನ್ನ ಅಪ್ಪಣೆಯಾಗಬೇಕೇ ಹೊರತು ಬೇರೆ ಏನೂ ಬೇಕಿಲ್ಲ” ಎಂದು ಸಮಾಧಾನಕರವಾದ ಮಾತುಗಳಿಂದ ಹೇಳಿ ಕಳುಹಿಸಿದೆನು.",
			"30": "<sup>30</sup> ಆದರೆ ಯೆಹೋವನು ಅವನಿಗೆ ಮೂರ್ಖಬುದ್ಧಿಯನ್ನು ಕೊಟ್ಟು ಹಟವನ್ನು ಹುಟ್ಟಿಸಿದ್ದರಿಂದ ಅವನು ಸಮ್ಮತಿಸಲಿಲ್ಲ. ಅವನು ನಿಮ್ಮಿಂದ ಸೋತುಹೋಗಬೇಕೆಂಬುದೇ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಸಂಕಲ್ಪವಾಗಿತ್ತು; ಅದು ಈಗಾಗಲೇ ನೆರವೇರಿತಲ್ಲಾ.",
			"31": "<sup>31</sup> ತರುವಾಯ ಯೆಹೋವನು ನನಗೆ, “ಈಗ ನಾನು ಸೀಹೋನನನ್ನೂ ಮತ್ತು ಅವನ ದೇಶವನ್ನೂ ನಿಮಗೆ ವಶಪಡಿಸುವುದಕ್ಕೆ ಪ್ರಾರಂಭಿಸಿದ್ದೇನೆ; ಅದನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವುದಕ್ಕೆ ಪ್ರಾರಂಭಮಾಡಿರಿ” ಎಂದು ಹೇಳಿದನು.",
			"32": "<sup>32</sup> ಸೀಹೋನನು ತನ್ನ ಜನರೆಲ್ಲರ ಸಮೇತ ನಮ್ಮೊಡನೆ ಯುದ್ಧಮಾಡುವುದಕ್ಕೆ ಯಹಜಿಗೆ ಹೊರಟುಬಂದನು.",
			"33": "<sup>33</sup> ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಅವನನ್ನು ನಮ್ಮಿಂದ ಪರಾಜಯಪಡಿಸಿದನು. ನಾವು ಅವನನ್ನೂ, ಅವನ ಮಕ್ಕಳನ್ನೂ ಮತ್ತು ಜನರೆಲ್ಲರನ್ನೂ ಸಂಹರಿಸಿದೆವು.",
			"34": "<sup>34</sup> ಆ ಕಾಲದಲ್ಲಿ ನಾವು ಅವನ ಎಲ್ಲಾ ಪಟ್ಟಣಗಳನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡು ಅವುಗಳಲ್ಲಿದ್ದ ಗಂಡಸರನ್ನೂ, ಹೆಂಗಸರನ್ನು ಮತ್ತು ಮಕ್ಕಳನ್ನೂ ನಿಶ್ಯೇಷವಾಗಿ ಹತಮಾಡಿದೆವು; ಒಬ್ಬನನ್ನೂ ಉಳಿಸಲಿಲ್ಲ.",
			"35": "<sup>35</sup> ಪಶುಗಳನ್ನು ಮಾತ್ರ ಉಳಿಸಿ ನಮ್ಮ ಸ್ವಂತಕ್ಕಾಗಿ ತೆಗೆದುಕೊಂಡು ಊರುಗಳನ್ನು ಸೂರೆಮಾಡಿಬಿಟ್ಟೆವು.",
			"36": "<sup>36</sup> ಅರ್ನೋನ್ ತಗ್ಗಿನ ಅಂಚಿನಲ್ಲಿರುವ ಅರೋಯೇರ್ ಪಟ್ಟಣವು ಮತ್ತು ತಗ್ಗಿನಲ್ಲಿಯೇ ಇರುವ ಪಟ್ಟಣವು ಇವುಗಳು ಮೊದಲುಗೊಂಡು ಗಿಲ್ಯಾದ್ ಸೀಮೆಯವರೆಗೂ ಯಾವ ಪಟ್ಟಣವನ್ನು ಜಯಿಸಲು ನಮಗೆ ಅಸಾಧ್ಯವಾಗಲಿಲ್ಲ. ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಅವುಗಳನ್ನೆಲ್ಲಾ ನಮ್ಮ ಕೈಗೆ ಸಿಕ್ಕುವಂತೆ ಮಾಡಿದನು.",
			"37": "<sup>37</sup> ಅಮ್ಮೋನಿಯರ ದೇಶವನ್ನು ಮಾತ್ರ ಅಂದರೆ ಯಬ್ಬೋಕ್ ಹೊಳೆಯ ಬಳಿಯಲ್ಲಿರುವ ಪ್ರದೇಶವು ಮತ್ತು ಬೆಟ್ಟದ ಸೀಮೆಯ ಊರುಗಳು ಇವೇ ಮೊದಲಾದ ಸ್ಥಳಗಳನ್ನು ನೀವು ಅತಿಕ್ರಮಿಸಲಿಲ್ಲ; ಅಲ್ಲಿಗೆ ಹೋಗಲೇ ಬಾರದೆಂದು ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಆಜ್ಞಾಪಿಸಿದ್ದನು."
		},
		"3": {
			"1": "<sup>1</sup> ತರುವಾಯ ನಾವು ಹಿಂದಿರುಗಿ ಬಾಷಾನಿನ ಮಾರ್ಗವನ್ನು ಹಿಡಿದು ಮೇಲಕ್ಕೆ ಹೋದಾಗ ಬಾಷಾನಿನ ಅರಸನಾದ ಓಗನು ತನ್ನ ಜನರೆಲ್ಲರ ಸಮೇತ ನಮಗೆ ವಿರುದ್ಧವಾಗಿ ಯುದ್ಧಮಾಡುವುದಕ್ಕೆ ಎದ್ರೈ ಊರಿಗೆ ಹೊರಟುಬಂದನು.",
			"2": "<sup>2</sup> ಆಗ ಯೆಹೋವನು ನನಗೆ, “ಅವನಿಗೆ ಭಯಪಡಬೇಡ; ನಾನು ಅವನನ್ನೂ, ಅವನ ಸಮಸ್ತ ಜನರನ್ನೂ ಮತ್ತು ದೇಶವನ್ನೂ ನಿನ್ನ ಕೈವಶಮಾಡಿದ್ದೇನೆ. ನೀನು ಹೆಷ್ಬೋನಿನಲ್ಲಿದ್ದ ಅಮೋರಿಯರ ಅರಸನಾದ ಸೀಹೋನನಿಗೆ ಮಾಡಿದ ಪ್ರಕಾರವೇ ಇವನಿಗೂ ಮಾಡಬೇಕು” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"3": "<sup>3</sup> ಬಾಷಾನಿನ ಅರಸನಾದ ಓಗನೂ ಅವನ ಜನರೆಲ್ಲರೂ ನಮ್ಮಿಂದ ಸೋತುಹೋಗುವಂತೆ ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಮಾಡಲಾಗಿ ಅವರಲ್ಲಿ ಒಬ್ಬರಾದರೂ ಉಳಿಯದಂತೆ ಅವರನ್ನು ಹತಮಾಡಿದೆವು.",
			"4": "<sup>4</sup> ಆ ಕಾಲದಲ್ಲಿ ನಾವು ಅವನ ಎಲ್ಲಾ ಪಟ್ಟಣಗಳನ್ನು ಜಯಿಸಿದೆವು; ನಾವು ಜಯಿಸದೇ ಇದ್ದ ಪಟ್ಟಣವು ಒಂದೂ ಇರಲಿಲ್ಲ. ಬಾಷಾನಿನಲ್ಲಿರುವ ಓಗನ ರಾಜ್ಯದಲ್ಲಿ ಅಂದರೆ ಅರ್ಗೋಬ್ ಎಂಬ ಸಮಸ್ತ ಪ್ರದೇಶದಲ್ಲಿ ಅರವತ್ತು ಪಟ್ಟಣಗಳನ್ನು ತೆಗೆದುಕೊಂಡೆವು.",
			"5": "<sup>5</sup> ಅವುಗಳೆಲ್ಲಾ ಎತ್ತರವಾದ ಪೌಳಿಗೋಡೆಗಳಿಂದಲೂ, ಕದಗಳಿಂದಲೂ, ಅಗುಳಿಗಳಿಂದಲೂ ಭದ್ರಮಾಡಲ್ಪಟ್ಟ ಪಟ್ಟಣಗಳೇ. ಅವುಗಳಲ್ಲದೆ ಹಳ್ಳಿಪಳ್ಳಿಗಳು ಬಹಳ ಇದ್ದವು.",
			"6": "<sup>6</sup> ನಾವು ಹೆಷ್ಬೋನಿನ ಅರಸನಾದ ಸೀಹೋನನ ಪಟ್ಟಣಗಳಿಗೆ ಮಾಡಿದಂತೆ ಇವುಗಳಿಗೂ ಮಾಡಿದೆವು. ಅವುಗಳಲ್ಲಿದ್ದ ಗಂಡಸರನ್ನೂ, ಹೆಂಗಸರನ್ನೂ ಮತ್ತು ಮಕ್ಕಳನ್ನೂ ಸಂಪೂರ್ಣವಾಗಿ ನಾಶಮಾಡಿದೆವು.",
			"7": "<sup>7</sup> ಪಶುಗಳನ್ನು ಮಾತ್ರ ಉಳಿಸಿ ಸ್ವಂತಕ್ಕೆ ತೆಗೆದುಕೊಂಡು ಊರುಗಳನ್ನೆಲ್ಲಾ ಸೂರೆಮಾಡಿಬಿಟ್ಟೆವು.",
			"8": "<sup>8</sup> ಆ ಕಾಲದಲ್ಲಿ ನಾವು ಅರ್ನೋನ್ ನದಿ ಮೊದಲುಗೊಂಡು ಹೆರ್ಮೋನ್ ಪರ್ವತದವರೆಗೂ, ಯೊರ್ದನ್ ನದಿಯ ಈಚೆ ಇರುವ ದೇಶವನ್ನೆಲ್ಲಾ ಅಮೋರಿಯರ ಇಬ್ಬರು ಅರಸರಿಂದ ತೆಗೆದುಕೊಂಡೆವು.",
			"9": "<sup>9</sup> (ಹೆರ್ಮೋನ್ ಪರ್ವತಕ್ಕೆ ಚೀದೋನ್ಯರು ಸಿರ್ಯೋನ್ ಎಂದೂ ಮತ್ತು ಅಮೋರಿಯರು ಸೆನೀರ್ ಎಂದೂ ಕರೆಯುತ್ತಿದ್ದರು.)",
			"10": "<sup>10</sup> ಹೀಗೆ ಮೀಶೊರೆಂಬ ಎತ್ತರವಾದ ಬೈಲುಸೀಮೆಯ ಎಲ್ಲಾ ಪಟ್ಟಣಗಳನ್ನೂ, ಗಿಲ್ಯಾದ್ ಸೀಮೆಯನ್ನೂ, ಓಗನ ರಾಜ್ಯವಾದ ಸಲ್ಕಾ ಮತ್ತು ಎದ್ರೈ ಎಂಬ ಪಟ್ಟಣಗಳಿಂದ ಕೂಡಿರುವ ಬಾಷಾನ್ ಸೀಮೆಯನ್ನೂ ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡೆವು.",
			"11": "<sup>11</sup> (ರೆಫಾಯರೊಳಗೆ ಬಾಷಾನಿನ ಅರಸನಾದ ಓಗನೊಬ್ಬನೇ ಉಳಿದಿದ್ದನು. ಅವನ ಮಂಚ ಕಬ್ಬಿಣದ್ದು; ಅಮ್ಮೋನಿಯರ ರಬ್ಬಾ ಎಂಬ ಪಟ್ಟಣದಲ್ಲಿ ಅದನ್ನು ಈಗಲೂ ನೋಡಬಹುದು. ಪುರುಷನ ಕೈ ಅಳತೆಯ ಪ್ರಕಾರ ಅದರ ಉದ್ದ ಒಂಭತ್ತು ಮೊಳ, ಅಗಲ ನಾಲ್ಕು ಮೊಳ.)",
			"12": "<sup>12</sup> ಆ ಕಾಲದಲ್ಲಿ ನಾವು ಆ ದೇಶವನ್ನೆಲ್ಲಾ ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡೆವು. ಅರ್ನೋನ್ ಹಳ್ಳದ ಬಳಿಯಲ್ಲಿರುವ ಅರೋಯೇರ್ ಮೊದಲುಗೊಂಡು ಗಿಲ್ಯಾದ್ ಸೀಮೆಯ ಮಧ್ಯದ ವರೆಗೂ ಇರುವ ಪ್ರದೇಶವನ್ನೆಲ್ಲಾ ಅದರ ಪಟ್ಟಣಗಳ ಸಹಿತವಾಗಿ ನಾನು ರೂಬೇನ್ಯರಿಗೂ ಮತ್ತು ಗಾದ್ಯರಿಗೂ ಕೊಟ್ಟೆನು.",
			"13": "<sup>13</sup> ಗಿಲ್ಯಾದಿನ ಮಿಕ್ಕ ಭಾಗವನ್ನೂ ಓಗನ ರಾಜ್ಯವಾಗಿದ್ದ ಅರ್ಗೋಬ್ ಎನಿಸಿಕೊಳ್ಳುವ ಸಮಸ್ತ ಬಾಷಾನ್ ದೇಶವನ್ನೂ ಮನಸ್ಸೆ ಕುಲದ ಅರ್ಧ ಜನರಿಗೆ ಕೊಟ್ಟೆನು.",
			"14": "<sup>14</sup> (ಬಾಷಾನ್ ದೇಶವು ರೆಫಾಯರ ದೇಶವೆನಿಸಿಕೊಳ್ಳುತ್ತದೆ. ಮನಸ್ಸೆಯ ವಂಶಸ್ಥನಾದ ಯಾಯಿರನು ಗೆಷೂರ್ಯರ ಮತ್ತು ಮಾಕಾತ್ಯರ ಮೇರೆಯವರೆಗೆ ಅರ್ಗೋಬ್ ಎಂಬ ಪ್ರದೇಶವನ್ನೆಲ್ಲಾ ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡನು. ಅವನು ಬಾಷಾನಿಗೆ ಸೇರಿರುವ ಆ ಪ್ರಾಂತ್ಯಕ್ಕೆ ಯಾಯೀರನ ಗ್ರಾಮಗಳೆಂದು ತನ್ನ ಹೆಸರಿಟ್ಟನು. ಈ ಹೆಸರು ಇಂದಿನ ವರೆಗೂ ಉಂಟು.)",
			"15": "<sup>15</sup> ನಾನು ಗಿಲ್ಯಾದ್ ದೇಶವನ್ನು ಮಾಕೀರನಿಗೂ ಗಿಲ್ಯಾದ್ ಮೊದಲುಗೊಂಡು (ದಕ್ಷಿಣದ) ಅರ್ನೋನ್ ತಗ್ಗಿನ ವರೆಗೂ,",
			"16": "<sup>16</sup> (ಪೂರ್ವದ) ಯಬ್ಬೋಕ್ ನದಿಯ ವರೆಗೂ ಇರುವ ಪ್ರದೇಶವನ್ನು ರೂಬೇನ್ಯರಿಗೂ ಮತ್ತು ಗಾದ್ಯರಿಗೂ ಕೊಟ್ಟೆನು. ಅರ್ನೋನ್ ತಗ್ಗಿನ ಮಧ್ಯಭಾಗವೇ ಅವರ (ದಕ್ಷಿಣ) ಮೇರೆ. ಅವರಿಗೂ ಅಮ್ಮೋನಿಯರಿಗೂ ನಡುವೆ ಇರುವ ಯಬ್ಬೋಕ್ ನದಿಯು ಅವರ (ಪೂರ್ವದಿಕ್ಕಿನವರೆಗೆ) ಮೇರೆ.",
			"17": "<sup>17</sup> ಅದಲ್ಲದೆ ಆರಾಬಾ ಎಂಬ ತಗ್ಗನ್ನೂ ಅವರಿಗೆ ಕೊಟ್ಟೆನು; ಅದರಲ್ಲಿ ಕಿನ್ನೆರೆತ್ ಸಮುದ್ರದಿಂದ ಪಿಸ್ಗಾ ಬೆಟ್ಟದ ಪಶ್ಚಿಮದಲ್ಲಿರುವ ಅರಾಬದ ಸಮುದ್ರವೆನಿಸಿಕೊಳ್ಳುವ ಲವಣಸಮುದ್ರದ ವರೆಗೆ ಯೊರ್ದನ್ ನದಿಯು ಅವರ (ಪಶ್ಚಿಮದ ವರೆಗೆ) ಮೇರೆ.",
			"18": "<sup>18</sup> ಆ ಕಾಲದಲ್ಲಿ ನಾನು ನಿಮಗೆ, “ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಈ ಪ್ರದೇಶವನ್ನೇ ನಿಮಗೆ ಸ್ವಾಸ್ತ್ಯಕ್ಕಾಗಿ ಕೊಟ್ಟಿದ್ದಾನೆ. ಆದರೆ ನಿಮ್ಮ ಸೈನಿಕರೆಲ್ಲರೂ ಯುದ್ಧಸನ್ನದ್ಧರಾಗಿ ನಿಮ್ಮ ಸಹೋದರರಾದ ಇಸ್ರಾಯೇಲರ ಮುಂದೆ ಹೊರಟು ಹೊಳೆ ದಾಟಿ ಹೋಗಬೇಕು.",
			"19": "<sup>19</sup> ನಿಮಗೆ ಬಹಳ ದನಕುರಿಗಳು ಉಂಟೆಂಬುದನ್ನು ನಾನು ಬಲ್ಲೆ. ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಯೊರ್ದನ್ ನದಿಯ ಆಚೆ ನಿಮ್ಮ ಸಹೋದರರಿಗೆ ಕೊಡುವ ದೇಶವು ಅವರಿಗೆ ಸ್ವಾಧೀನವಾಗುವವರೆಗೆ ನಿಮ್ಮ ಹೆಂಡತಿ ಮಕ್ಕಳೂ, ದನಕುರಿಗಳೂ ನಾನು ನಿಮಗೆ ಕೊಟ್ಟಿರುವ ಪಟ್ಟಣಗಳಲ್ಲೇ ಇರಲಿ.",
			"20": "<sup>20</sup> ಅನಂತರ ನಿಮ್ಮಂತೆ ನಿಮ್ಮ ಸಹೋದರರಿಗೂ ಯೆಹೋವನಿಂದ ವಿಶ್ರಾಂತಿ ದೊರಕಿದಾಗ ನಿಮ್ಮಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬನೂ ನಾನು ಕೊಟ್ಟ ಸ್ವಾಸ್ತ್ಯಕ್ಕೆ ಹಿಂತಿರುಗಿ ಬರಬಹುದು” ಎಂದು ಆಜ್ಞಾಪಿಸಿದೆನು.",
			"21": "<sup>21</sup> ಆ ಕಾಲದಲ್ಲಿ ನಾನು ಯೆಹೋಶುವನಿಗೆ, “ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಆ ಇಬ್ಬರು ಅರಸರಿಗೆ ಮಾಡಿದ್ದನ್ನು ನೀನು ನೋಡೇ ಇದ್ದೀ. ನೀನು ನದಿ ದಾಟಿಹೋಗುವ ಎಲ್ಲಾ ರಾಜ್ಯಗಳನ್ನೂ ಆತನು ಹಾಗೆಯೇ ನಾಶಮಾಡುವನು.",
			"22": "<sup>22</sup> ನೀವು ಅವರಿಗೆ ಭಯಪಡಬೇಡಿರಿ; ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ತಾನೇ ನಿಮ್ಮ ಪರವಾಗಿ ಯುದ್ಧಮಾಡುವನು” ಎಂದು ಆಜ್ಞಾಪಿಸಿದೆನು.",
			"23": "<sup>23</sup> ಆ ಕಾಲದಲ್ಲಿ ನಾನು ಯೆಹೋವನಿಗೆ, “ಕರ್ತನಾದ ಯೆಹೋವನೇ, ನೀನು ನಿನ್ನ ಮಹತ್ವವನ್ನೂ, ಭುಜಬಲವನ್ನೂ ನಿನ್ನ ದಾಸನಿಗೆ ತೋರಿಸಲಾರಂಭಿಸಿದಿ.",
			"24": "<sup>24</sup> ನೀನು ನಡಿಸುವ ಈ ಮಹತ್ಕಾರ್ಯಗಳಿಗೆ ಸಮಾನವಾದ ಕಾರ್ಯಗಳನ್ನು ಆಕಾಶದಲ್ಲಾಗಲಿ, ಭೂಮಿಯಲ್ಲಾಗಲಿ ಬೇರೆ ಯಾವ ದೇವರು ನಡೆಸಲಾರನು?",
			"25": "<sup>25</sup> ಕರ್ತನೇ, ನಾನೂ ಈ ನದಿಯನ್ನು ದಾಟಿ ಆಚೆಯಿರುವ ಒಳ್ಳೆಯ ದೇಶವನ್ನು ಅಂದರೆ ಆ ಅಂದವಾದ ಬೆಟ್ಟದ ಸೀಮೆಯನ್ನೂ, ಲೆಬನೋನ್ ಪರ್ವತವನ್ನೂ ನೋಡುವುದಕ್ಕೆ ಅಪ್ಪಣೆಯಾಗಲಿ” ಎಂದು ಬಿನ್ನವಿಸಿದೆನು.",
			"26": "<sup>26</sup> ಆದರೆ ಯೆಹೋವನು ನಿಮ್ಮ ದೆಸೆಯಿಂದ ನನ್ನ ಮೇಲೆ ಕೋಪವುಳ್ಳವನಾಗಿ ನನ್ನ ಮನವಿಯನ್ನು ಕೇಳದೆ, “ಸಾಕು, ಇದರ ವಿಷಯದಲ್ಲಿ ಇನ್ನು ನನ್ನ ಸಂಗಡ ಮಾತನಾಡಬೇಡ; ನೀನು ಯೊರ್ದನ್ ನದಿಯನ್ನು ದಾಟಬಾರದು.",
			"27": "<sup>27</sup> ಅದರೆ ಪಿಸ್ಗಾ ಬೆಟ್ಟವನ್ನು ಹತ್ತಿ ತುದಿಯಲ್ಲಿ ನಿಂತು, ನಾಲ್ಕು ದಿಕ್ಕುಗಳಲ್ಲಿ ಸುತ್ತಲೂ ಇರುವ ದೇಶವನ್ನು ಕಣ್ಣು ತುಂಬಾ ನೋಡಬಹುದು.",
			"28": "<sup>28</sup> ಯೆಹೋಶುವನೇ ಈ ಜನರ ನಾಯಕನಾಗಿ ಹೋಗಿ ನೀನು ನೋಡುವ ದೇಶವನ್ನು ಅವರಿಗೆ ಸ್ವಾಧೀನಪಡಿಸುವನು. ಅವನಿಗೇ ಈ ಅಧಿಕಾರವನ್ನು ಕೊಡಬೇಕು; ಅವನು ದೃಢವಾಗುವಂತೆ ಅವನನ್ನು ಧೈರ್ಯಪಡಿಸಬೇಕು” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"29": "<sup>29</sup> ಆಗ ನಾವು ಬೇತ್‍ಪೆಗೋರಿಗೆ ಮುಂದೆ ಇದ್ದ ಕಣಿವೆಯಲ್ಲಿ ವಾಸವಾಗಿದ್ದೆವು."
		},
		"4": {
			"1": "<sup>1</sup> ಇಸ್ರಾಯೇಲರೇ ಕೇಳಿರಿ, ನೀವು ಜೀವದಿಂದ ಉಳಿದು ನಿಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಕೊಡುವ ದೇಶವನ್ನು ಸೇರಿ, ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವಂತೆ ಸಾಧ್ಯವಾಗಲು ನಾನು ಈಗ ಬೋಧಿಸುವ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ಗಮನಿಸಿ ಅನುಸರಿಸಬೇಕು.",
			"2": "<sup>2</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಕೊಟ್ಟ ಆಜ್ಞೆಗಳನ್ನೇ ನಾನು ನಿಮಗೆ ತಿಳಿಸುತ್ತಾ ಇದ್ದೇನೆ. ಈ ಮಾತುಗಳನ್ನು ನೀವು ಕೈಕೊಳ್ಳಬೇಕೇ ಹೊರತು ಅವುಗಳಿಗೆ ಯಾವುದನ್ನೂ ಕೂಡಿಸಬಾರದು, ಅವುಗಳಿಂದ ಯಾವುದನ್ನೂ ತೆಗೆಯಬಾರದು.",
			"3": "<sup>3</sup> ಪೆಗೋರದ ಬಾಳನ ಸಂಗತಿಯಲ್ಲಿ ಯೆಹೋವನು ಮಾಡಿದ ಕಾರ್ಯವನ್ನು ನೀವು ಗಮನಿಸಿದ್ದೀರಿ. ಪೆಗೋರದ ಬಾಳನನ್ನು ಅನುಸರಿಸಿದವರೆಲ್ಲರನ್ನೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮಲ್ಲಿರದಂತೆ ನಾಶಮಾಡಿದ್ದಾನೆ.",
			"4": "<sup>4</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ನೀವು ನಂಬಿ ನಡೆದುದರಿಂದ ನೀವಾದರೋ ಇಂದಿನವರೆಗೂ ಉಳಿದಿದ್ದೀರಿ.",
			"5": "<sup>5</sup> ನೀವು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವುದಕ್ಕೆ ಹೋಗುವ ದೇಶದಲ್ಲಿ ಅನುಸರಿಸಬೇಕಾದ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ನನ್ನ ದೇವರಾದ ಯೆಹೋವನು ನನಗೆ ಆಜ್ಞಾಪಿಸಿದಂತೆಯೇ ನಿಮಗೆ ಬೋಧಿಸಿದ್ದೇನೆ.",
			"6": "<sup>6</sup> ಇವುಗಳನ್ನು ಕೈಕೊಂಡು ಅನುಸರಿಸಿರಿ. ನೀವು ಅನುಸರಿಸಿ ನಡೆದರೆ ಅನ್ಯಜನಗಳು ನಿಮ್ಮನ್ನು ಜ್ಞಾನಿಗಳೂ, ವಿವೇಕಿಗಳೂ ಎಂದು ತಿಳಿಯುವರು. ಅವರು ಈ ಆಜ್ಞೆಗಳ ವಿಷಯದಲ್ಲಿ ವರ್ತಮಾನವನ್ನು ಕೇಳಿ, “ಈ ದೊಡ್ಡ ಜನಾಂಗವು ಜ್ಞಾನವಿವೇಕವುಳ್ಳ ಜನಾಂಗ” ಎಂದು ಹೇಳುವರು.",
			"7": "<sup>7</sup> ನಾವು ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಡುವಾಗೆಲ್ಲಾ ಆತನು ಸಮೀಪವಾಗಿಯೇ ಇದ್ದಾನಲ್ಲಾ; ಬೇರೆ ಎಷ್ಟು ದೊಡ್ಡ ಜನಾಂಗಕ್ಕಾದರೂ ಯಾವ ದೇವರು ಹೀಗೆ ಸಮೀಪವಾಗಿರುವನು?",
			"8": "<sup>8</sup> ನಾನು ಈ ಹೊತ್ತು ನಿಮ್ಮ ಮುಂದೆ ಇಡುವ ಇಂಥ ನ್ಯಾಯವಾದ ಆಜ್ಞಾವಿಧಿಗಳುಳ್ಳ ಈ ಧರ್ಮಶಾಸ್ತ್ರಕ್ಕೆ ಸಮಾನವಾದದ್ದು ಬೇರೆ ಯಾವ ಜನಾಂಗಕ್ಕೆ ಉಂಟು?",
			"9": "<sup>9</sup> ಹೀಗಿರುವುದರಿಂದ ನೀವು ಬಹು ಜಾಗರೂಕತೆಯಿಂದ ನಿಮ್ಮ ನಿಮ್ಮ ಮನಸ್ಸುಗಳನ್ನು ಕಾಪಾಡಿಕೊಳ್ಳುವವರಾಗಿ, ನೀವು ನೋಡಿದ ಸಂಗತಿಗಳನ್ನು ಎಷ್ಟು ಮಾತ್ರವೂ ಮರೆಯದೆ ಜೀವದಿಂದಿರುವ ವರೆಗೂ ಅವುಗಳನ್ನು ನೆನಪಿನಲ್ಲಿಟ್ಟುಕೊಂಡು ನಿಮ್ಮ ಮಕ್ಕಳಿಗೂ ಮತ್ತು ಮೊಮ್ಮಕ್ಕಳಿಗೂ ತಿಳಿಸುತ್ತಾ ಹೋಗಬೇಕು.",
			"10": "<sup>10</sup> ಆ ಸಂಗತಿಗಳು ಯಾವುವೆಂದರೆ, ನೀವು ಹೋರೇಬಿನಲ್ಲಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನಿಂತಿದ್ದಾಗ ಆತನು ನನಗೆ, “ಜನರೆಲ್ಲರನ್ನು ನನ್ನ ಹತ್ತಿರಕ್ಕೆ ಕೂಡಿಸು; ಅವರು ತಾವು ಭೂಮಿಯ ಮೇಲಿರುವ ದಿನಗಳಲ್ಲೆಲ್ಲಾ ನನಗೆ ಭಯಭಕ್ತಿಯಿಂದಿರುವುದಕ್ಕೆ ಕಲಿತುಕೊಂಡು ತಮ್ಮ ಮಕ್ಕಳಿಗೂ ಕಲಿಸಿಕೊಡುವಂತೆ ಅವರಿಗೆ ಆಜ್ಞೆಗಳನ್ನು ತಿಳಿಸುತ್ತೇನೆ” ಎಂದು ಹೇಳಿದನು.",
			"11": "<sup>11</sup> ಆ ದಿನದಲ್ಲಿ ನೀವು ಹತ್ತಿರ ಬಂದು ಬೆಟ್ಟದ ಬುಡದಲ್ಲಿ ನಿಂತುಕೊಂಡಿರಲು ಕತ್ತಲೂ, ಮೋಡವೂ, ಕಾರ್ಗತ್ತಲೂ ಕವಿದು ಆ ಬೆಟ್ಟವು ಆಕಾಶದ ತುದಿಯವರೆಗೂ ಬೆಂಕಿಯಿಂದ ಉರಿಯಿತು.",
			"12": "<sup>12</sup> ಆಗ ಯೆಹೋವನು ಆ ಬೆಂಕಿಯ ಜ್ವಾಲೆಯೊಳಗಿಂದ ನಿಮ್ಮ ಸಂಗಡ ಮಾತನಾಡಿದನು. ಆತನು ಮಾತನಾಡಿದ ಸ್ವರ ನಿಮಗೆ ಕೇಳಿಸಿತೇ ಹೊರತು ಯಾವ ಆಕಾರವೂ ಕಾಣಿಸಲ್ಲಿಲ್ಲ.",
			"13": "<sup>13</sup> ನೀವು ಅನುಸರಿಸಬೇಕೆಂದು ಆತನು ನೇಮಿಸಿದ ನಿಬಂಧನೆಯನ್ನು ಅಂದರೆ ಹತ್ತು ಆಜ್ಞೆಗಳನ್ನು ನಿಮಗೆ ವಿವರಿಸಿ, ಎರಡು ಕಲ್ಲಿನ ಹಲಗೆಗಳಲ್ಲಿ ಅದನ್ನು ಕೆತ್ತಿಸಿದನು.",
			"14": "<sup>14</sup> ನೀವು ನದಿ ದಾಟಿ ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವ ದೇಶದಲ್ಲಿ ಅನುಸರಿಸಬೇಕಾದ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ನಿಮಗೆ ಬೋಧಿಸಬೇಕೆಂದು ಯೆಹೋವನು ಆ ಕಾಲದಲ್ಲಿ ನನಗೆ ಆಜ್ಞಾಪಿಸಿದ್ದನ್ನು ನೀವು ನೆನಪಿನಲ್ಲಿಟ್ಟುಕೊಂಡು ನಿಮ್ಮ ಸಂತತಿಯವರಿಗೆ ತಿಳಿಸಬೇಕು.",
			"15": "<sup>15</sup> ಹೋರೇಬಿನಲ್ಲಿ ಯೆಹೋವನು ಬೆಂಕಿಯ ಜ್ವಾಲೆಯೊಳಗಿಂದ ನಿಮ್ಮ ಸಂಗಡ ಮಾತನಾಡಿದಾಗ ನಿಮಗೆ ಯಾವ ಆಕಾರವೂ ಕಾಣಿಸಲಿಲ್ಲ. ಆದುದರಿಂದ ನೀವು ನಿಮ್ಮ ನಿಮ್ಮ ಮನಸ್ಸುಗಳನ್ನು ಬಹು ಜಾಗರೂಕತೆಯಿಂದ ಕಾಪಾಡಿಕೊಳ್ಳಬೇಕು.",
			"16": "<sup>16</sup> ನೀವು ಸ್ತ್ರೀಪುರುಷರ ರೂಪವನ್ನಾಗಲಿ,",
			"17": "<sup>17</sup> ಭೂಮಿಯ ಮೇಲೆ ಸಂಚರಿಸುವ ಮೃಗದ ರೂಪವನ್ನಾಗಲಿ, ಆಕಾಶದಲ್ಲಿ ಹಾರಾಡುವ ಪಕ್ಷಿಯ ರೂಪವನ್ನಾಗಲಿ,",
			"18": "<sup>18</sup> ನೆಲದ ಮೇಲೆ ಹರಿದಾಡುವ ಕ್ರಿಮಿಕೀಟಗಳ ರೂಪವನ್ನಾಗಲಿ ಮತ್ತು ಭೂಮಿಯ ಕೆಳಗಣ ನೀರಿನಲ್ಲಿರುವ ಮೀನಿನ ರೂಪವನ್ನಾಗಲಿ ದೇವರ ಸ್ವರೂಪವೆಂದು ಮಾಡಿಕೊಂಡು ಭ್ರಷ್ಟರಾಗಬಾರದು.",
			"19": "<sup>19</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಭೂಲೋಕದಲ್ಲಿರುವ ಬೇರೆ ಎಲ್ಲಾ ಜನಾಂಗಗಳಿಗೋಸ್ಕರ ದಯಪಾಲಿಸಿದ ಸೂರ್ಯ, ಚಂದ್ರ ಅಥವ ನಕ್ಷತ್ರಗಳೆನಿಸಿಕೊಳ್ಳುವ ಆಕಾಶಸೈನ್ಯವನ್ನು ನೀವು ಆಕಾಶದ ಕಡೆಗೆ ಕಣ್ಣೆತ್ತಿ ನೋಡುವಾಗ, ಮರುಳುಗೊಂಡು ಅವುಗಳನ್ನು ಪೂಜಿಸಲೂ ಬಾರದು ಮತ್ತು ನಮಸ್ಕರಿಸಲೂ ಬಾರದು.",
			"20": "<sup>20</sup> ನಿಮ್ಮನ್ನಾದರೋ ಯೆಹೋವನು ತನಗೆ ಸ್ವಂತ ಜನರನ್ನಾಗಿ ಮಾಡಿಕೊಳ್ಳಬೇಕೆಂದು ಸಂಕಲ್ಪಿಸಿ, ಕಬ್ಬಿಣವನ್ನು ಕರಗಿಸುವ ಕುಲುಮೆಯೋಪಾದಿಯಲ್ಲಿದ್ದ ಐಗುಪ್ತದೇಶದಿಂದ ತಪ್ಪಿಸಿದನು; ಆ ಸಂಕಲ್ಪವು ಈಗ ನೆರವೇರಿತು.",
			"21": "<sup>21</sup> ಅದಲ್ಲದೆ ಯೆಹೋವನು ನಿಮ್ಮ ದೆಸೆಯಿಂದ ನನ್ನ ಮೇಲೆಯೂ ಕೋಪಗೊಂಡು ನಾನು ಯೊರ್ದನ್ ನದಿಯನ್ನು ದಾಟಿ, ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಸ್ವದೇಶವಾಗುವುದಕ್ಕೆ ಕೊಡುವ ಆ ಒಳ್ಳೆಯ ದೇಶವನ್ನು ಸೇರದೆ,",
			"22": "<sup>22</sup> ಇಲ್ಲಿಯೇ ಸಾಯಬೇಕೆಂದು ಖಂಡಿತವಾಗಿ ಹೇಳಿದನು. ನೀವಾದರೋ ನದಿದಾಟಿ ಆ ಒಳ್ಳೆಯ ದೇಶವನ್ನು ಸೇರಿ ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವಿರಿ.",
			"23": "<sup>23</sup> ಎಚ್ಚರಿಕೆಯಾಗಿರಿ, ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮ ಸಂಗಡ ಮಾಡಿದ ನಿಬಂಧನೆಯನ್ನು ನೀವು ಮರೆತು, ಪೂಜಿಸಬೇಡಿರೆಂದು ಆತನು ನಿಷೇಧಿಸಿದ ಯಾವ ವಸ್ತುವಿನ ವಿಗ್ರಹವನ್ನೂ ಮಾಡಿಕೊಳ್ಳಬಾರದು.",
			"24": "<sup>24</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ತನಗೆ ಸಲ್ಲಬೇಕಾದ ಗೌರವವನ್ನು ಮತ್ತೊಬ್ಬನಿಗೆ ಸಲ್ಲಗೊಡಿಸದೆ ಉರಿಯುವ ಬೆಂಕಿಯಂತೆ ದ್ರೋಹಿಗಳನ್ನು ದಹಿಸಿಬಿಡುವವನಾಗಿದ್ದಾನೆಂದು ತಿಳಿಯಿರಿ.",
			"25": "<sup>25</sup> ನೀವು ಮಕ್ಕಳನ್ನೂ ಮತ್ತು ಮೊಮ್ಮಕ್ಕಳನ್ನೂ ಪಡೆದು ಬಹುಕಾಲ ಆ ದೇಶದಲ್ಲಿದ್ದ ಮೇಲೆ ದ್ರೋಹಿಗಳಾಗಿ ಯಾವ ವಿಗ್ರಹವನ್ನಾದರೂ ಮಾಡಿಕೊಂಡು, ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ದೃಷ್ಟಿಗೆ ಕೆಟ್ಟದ್ದನ್ನು ನಡಿಸಿ,",
			"26": "<sup>26</sup> ಆತನನ್ನು ಕೋಪಗೊಳಿಸಿದರೆ, ನೀವು ಯೊರ್ದನ್ ನದಿಯನ್ನು ದಾಟಿ ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವ ಆ ದೇಶದಲ್ಲಿ ಉಳಿಯದೆ, ಬೇಗನೆ ನಾಶವಾಗಿ ಹೋಗುವಿರಿ ಎಂದು ಭೂಮ್ಯಾಕಾಶಗಳನ್ನು ಸಾಕ್ಷಿಯಾಗಿಟ್ಟು ಎಚ್ಚರಿಸುತ್ತೇನೆ. ನೀವು ಆ ದೇಶದಲ್ಲಿ ಬಹುದಿನ ಇರದೆ ನಾಶವಾಗಿಯೇ ಹೋಗುವಿರಿ.",
			"27": "<sup>27</sup> ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಅನ್ಯಜನಗಳಲ್ಲಿ ಚದರಿಸುವನು; ಆತನು ನಿಮ್ಮನ್ನು ಓಡಿಸುವ ದೇಶಗಳ ಜನರ ಮಧ್ಯದಲ್ಲಿ ನೀವು ಸ್ವಲ್ಪ ಜನ ಮಾತ್ರ ಉಳಿಯುವಿರಿ.",
			"28": "<sup>28</sup> ನೀವು ಆ ದೇಶಗಳಲ್ಲಿ ಮನುಷ್ಯರು ಮಾಡಿಕೊಂಡ ದೇವರುಗಳನ್ನು ಅಂದರೆ ಮರದಲ್ಲಾಗಲಿ, ಕಲ್ಲಿನಲ್ಲಾಗಲಿ ಕೆತ್ತಲ್ಪಟ್ಟು ನೋಡಲಾರದೆ, ಕೇಳಲಾರದೆ, ತಿನ್ನಲಾರದೆ, ವಾಸನೆ ಗ್ರಹಿಸಲಾರದೆ ಇರುವ ದೇವರುಗಳನ್ನು ಪೂಜಿಸುವವರಾಗುವಿರಿ.",
			"29": "<sup>29</sup> ಆಗ ಅಲ್ಲಿಂದಲಾದರೂ ನೀವು ಸಂಪೂರ್ಣ ಹೃದಯದಿಂದಲೂ, ಪೂರ್ಣ ಮನಸ್ಸಿನಿಂದಲೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಹುಡುಕಿದರೆ ಆತನು ನಿಮಗೆ ಸಿಕ್ಕುವನು.",
			"30": "<sup>30</sup> ಮೇಲೆ ಹೇಳಿದ ಎಲ್ಲಾ ಕಷ್ಟಗಳು ನಿಮಗೆ ಸಂಭವಿಸಿ, ನೀವು ಸಂಕಟದಲ್ಲಿರುವಾಗ ಅಂತ್ಯದಲ್ಲಿ ನೀವು ಆತನ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡು ಆತನ ಮಾತಿಗೆ ಕಿವಿಗೊಡುವಿರಿ.",
			"31": "<sup>31</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಕನಿಕರವುಳ್ಳ ದೇವರಾದುದರಿಂದ ಆತನು ನಿಮ್ಮನ್ನು ಅಲಕ್ಷ್ಯಮಾಡುವುದಿಲ್ಲ, ವಿನಾಶಕ್ಕೆ ಬಿಡುವುದಿಲ್ಲ. ಆತನು ನಿಮ್ಮ ಪಿತೃಗಳ ಸಂಗಡ ಪ್ರಮಾಣಪೂರ್ವಕವಾಗಿ ಮಾಡಿಕೊಂಡ ಒಡಂಬಡಿಕೆಯನ್ನು ಮರೆಯುವುದಿಲ್ಲ.",
			"32": "<sup>32</sup> ದೇವರು, ಮನುಷ್ಯರನ್ನು ಸೃಷ್ಟಿಸಿ ಭೂಮಿಯ ಮೇಲೆ ಇರಿಸಿದ ದಿನ ಮೊದಲುಗೊಂಡು ಇಂದಿನವರೆಗೂ ಒಂದು ದಿಕ್ಕಿನಿಂದ ಮತ್ತೊಂದು ದಿಕ್ಕಿನವರೆಗೂ ಇಂತಹ ಅದ್ಭುತ ಕಾರ್ಯವು ನಡೆದದ್ದುಂಟೋ, ಇಲ್ಲವೆ ಇಂತಹ ಸುದ್ದಿ ಕೇಳಿದ್ದುಂಟೋ ಎಂದು ವಿಚಾರಿಸಿಕೊಳ್ಳಿರಿ.",
			"33": "<sup>33</sup> ದೇವರು ಬೆಂಕಿಯ ಜ್ವಾಲೆಯೊಳಗಿಂದ ಮಾತನಾಡಿದ ಸ್ವರವು ನಿಮಗೆ ಕೇಳಿಸಿತಲ್ಲಾ; ಬೇರೆ ಯಾವ ಜನರಾದರೂ ದೇವರ ಸ್ವರವನ್ನು ಕೇಳಿ ಬದುಕಿದ್ದುಂಟೋ?",
			"34": "<sup>34</sup> ಬೇರೆ ಯಾವ ದೇವರೂ ಮನಪರಿಶೋಧನೆ, ಮಹತ್ಕಾರ್ಯ, ಉತ್ಪಾತ, ಯುದ್ಧ, ಭುಜಪರಾಕ್ರಮ, ಶಿಕ್ಷಾಹಸ್ತ, ಮಹಾಭೀತಿ ಇವುಗಳನ್ನು ಪ್ರಯೋಗಿಸಿ, ಒಂದು ಜನಾಂಗವನ್ನು ಮತ್ತೊಂದು ಜನಾಂಗದ ಕೈಯೊಳಗಿಂದ ತಪ್ಪಿಸುವುದಕ್ಕೆ ಪ್ರಯತ್ನಿಸಿದ್ದಾನೇ? ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಾದರೋ ಐಗುಪ್ತದೇಶದಲ್ಲಿ ನಿಮಗೋಸ್ಕರ ಇದನ್ನೆಲ್ಲಾ ನಿಮ್ಮ ಕಣ್ಣು ಮುಂದೆ ನಡಿಸಿದ್ದಾನಲ್ಲಾ.",
			"35": "<sup>35</sup> ಯೆಹೋವನೊಬ್ಬನೇ ದೇವರು, ಬೇರೆ ದೇವರೇ ಇಲ್ಲವೆಂದು ನೀವು ತಿಳಿದುಕೊಳ್ಳುವುದಕ್ಕಾಗಿ ಇದೆಲ್ಲಾ ನಿಮಗೆ ಮಾತ್ರ ತೋರಿಸಿದ್ದಾನೆ.",
			"36": "<sup>36</sup> ಆತನು ನಿಮ್ಮ ಶಿಕ್ಷಣಕ್ಕೋಸ್ಕರ ಆಕಾಶದಿಂದ ತನ್ನ ಸ್ವರವನ್ನು ನಿಮಗೆ ಕೇಳಿಸುವಂತೆ ಮಾಡಿದನು; ಮತ್ತು ತನ್ನ ಮಹಾ ಅಗ್ನಿಜ್ವಾಲೆಯನ್ನು ಭೂಮಿಯ ಮೇಲೆ ತೋರಿಸಿದನು. ಅದಲ್ಲದೆ ಆ ಅಗ್ನಿಜ್ವಾಲೆಯೊಳಗಿಂದ ಆತನು ಆಡಿದ ಮಾತುಗಳನ್ನು ಕೇಳಿಸುವಂತೆ ಮಾಡಿದನು.",
			"37": "<sup>37</sup> ಆತನು ನಿಮ್ಮ ಪಿತೃಗಳನ್ನು ಪ್ರೀತಿಸಿ ಅವರ ತರುವಾಯ ಅವರ ಸಂತತಿಯವರಾದ ನಿಮ್ಮನ್ನು ಆರಿಸಿಕೊಂಡು,",
			"38": "<sup>38</sup> ಈಗ ನಮ್ಮ ಅನುಭವಕ್ಕೆ ಬಂದ ಪ್ರಕಾರ ನಿಮಗಿಂತ ಮಹಾಬಲಿಷ್ಠವಾದ ಜನಾಂಗಗಳನ್ನು ಹೊರಡಿಸಲು ಅವರ ದೇಶದಲ್ಲಿ ನಿಮ್ಮನ್ನು ಸೇರಿಸಿ, ಆ ದೇಶವನ್ನು ನಿಮಗೇ ಸ್ವದೇಶವಾಗಿ ಕೊಡಬೇಕೆಂದು ಸಂಕಲ್ಪಿಸಿ, ತಾನೇ ನಿಮ್ಮೊಂದಿಗಿದ್ದು ತನ್ನ ಅಪಾರ ಸಾಮರ್ಥ್ಯದಿಂದ ನಿಮ್ಮನ್ನು ಐಗುಪ್ತದೇಶದೊಳಗಿಂದ ಬರಮಾಡಿದನಲ್ಲಾ.",
			"39": "<sup>39</sup> ನೀವು ಇದನ್ನೆಲ್ಲಾ ಆಲೋಚಿಸಿ ಆಕಾಶದಲ್ಲಿಯೂ ಮತ್ತು ಭೂಮಿಯಲ್ಲಿಯೂ ಯೆಹೋವನೊಬ್ಬನೇ ದೇವರು, ಬೇರೆ ಯಾವ ದೇವರೂ ಇಲ್ಲವೆಂಬುವುದನ್ನು ಈಗ ಗ್ರಹಿಸಿಕೊಂಡು ಜ್ಞಾಪಕದಲ್ಲಿಡಬೇಕು.",
			"40": "<sup>40</sup> ನಿಮಗೂ ಮತ್ತು ನಿಮ್ಮ ಸಂತತಿಯವರಿಗೂ ಶುಭವುಂಟಾಗುವಂತೆಯೂ, ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಕೊಡುವ ದೇಶದಲ್ಲಿ ನೀವು ಸದಾಕಾಲ ಬಾಳುವಂತೆಯೂ, ನಾನು ನಿಮಗೆ ಈಗ ತಿಳಿಸಿದ ಆತನ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ನೀವು ಅನುಸರಿಸಿ ನಡೆಯಬೇಕು.",
			"41": "<sup>41</sup> ಆ ಕಾಲದಲ್ಲಿ ಮೋಶೆ ಯೊರ್ದನ್ ನದಿಯ ಪೂರ್ವದಿಕ್ಕಿನಲ್ಲಿ ಮೂರು ಆಶ್ರಯ ಪಟ್ಟಣಗಳನ್ನು ಗೊತ್ತುಮಾಡಿದನು.",
			"42": "<sup>42</sup> ಯಾವ ದ್ವೇಷವೂ ಇಲ್ಲದೆ ಕೈತಪ್ಪಿ ಮತ್ತೊಬ್ಬನನ್ನು ಕೊಂದವನು ಆ ಪಟ್ಟಣಗಳಲ್ಲಿ ಒಂದಕ್ಕೆ ಓಡಿಹೋಗಿ ಬದುಕಿಕೊಳ್ಳಲಿ ಎಂದು ಆ ಪಟ್ಟಣಗಳನ್ನು ನೇಮಿಸಿದನು.",
			"43": "<sup>43</sup> ಅವು ಯಾವುವೆಂದರೆ - ರೂಬೇನ್ಯರಿಗೋಸ್ಕರ ಮೀಶೋರ್ ಎಂಬ ಎತ್ತರವಾದ ಬಯಲು ಸೀಮೆಯ ಅರಣ್ಯದಲ್ಲಿರುವ ಬೆಚೆರ್; ಗಾದ್ಯರಿಗೋಸ್ಕರ ಗಿಲ್ಯಾದಿನಲ್ಲಿರುವ ರಾಮೋತ್ ಮತ್ತು ಮನಸ್ಸೆಯವರಿಗೋಸ್ಕರ ಬಾಷಾನಿನಲ್ಲಿರುವ ಗೋಲಾನ್ ಪಟ್ಟಣಗಳು.",
			"44": "<sup>44</sup> ಮೋಶೆ ಇಸ್ರಾಯೇಲರಿಗೆ ಕೊಟ್ಟ ಧರ್ಮಶಾಸ್ತ್ರವು ಇದೇ.",
			"45": "<sup>45</sup> ಇಸ್ರಾಯೇಲರು ಐಗುಪ್ತ ದೇಶದಿಂದ ಬಂದಾಗ ಮೋಶೆಯು ಇಸ್ರಾಯೇಲರಿಗೆ ತಿಳಿಸಿದ ಆಜ್ಞಾವಿಧಿನಿಯಮಗಳು ಇವೇ.",
			"46": "<sup>46</sup> ಅವರು ಯೊರ್ದನ್ ನದಿಯ ಆಚೆ ಬೇತ್‍ಪೆಗೋರಿಗೆ ಎದುರಾಗಿರುವ ಕಣಿವೆಯಲ್ಲಿ ಹೆಷ್ಬೋನಿನಲ್ಲಿ ವಾಸಿಸಿದರು. ಅಮೋರಿಯರ ಅರಸನಾದ ಸೀಹೋನನ ದೇಶದಲ್ಲಿ ಮೋಶೆಯೂ ಮತ್ತು ಇಸ್ರಾಯೇಲರೂ ಐಗುಪ್ತದೇಶದಿಂದ ಬಂದಾಗ ಆ ಸೀಹೋನನನ್ನು ಜಯಿಸಿದ್ದರು.",
			"47": "<sup>47</sup> ಅವರು ಅವನ ರಾಜ್ಯವನ್ನೂ, ಬಾಷಾನ್ ದೇಶದ ಅರಸನಾದ ಓಗನ ರಾಜ್ಯವನ್ನೂ ಮತ್ತು ಯೊರ್ದನ್ ನದಿಯ ಆಚೆ ಪೂರ್ವದಿಕ್ಕಿನಲ್ಲಿದ್ದ ಅಮೋರಿಯರ ಇಬ್ಬರು ಅರಸರ ರಾಜ್ಯಗಳನ್ನು ಗೆದ್ದುಕೊಂಡಿದ್ದರು.",
			"48": "<sup>48</sup> ಅಂತು ಅರ್ನೋನ್ ತಗ್ಗಿನ ಅಂಚಿನಲ್ಲಿರುವ ಅರೋಯೇರ್ ಪಟ್ಟಣ ಮೊದಲುಗೊಂಡು ಹೆರ್ಮೋನ್ ಎಂಬ ಸೀಯೋನ್ ಪರ್ವತದವರೆಗೂ ಮತ್ತು",
			"49": "<sup>49</sup> ಅರಾಬಾ ಎಂಬ ತಗ್ಗಿನಲ್ಲಿ ಪಿಸ್ಗಾ ಬೆಟ್ಟದ ಬುಡದಲ್ಲಿರುವ ಅರಾಬ ಸಮುದ್ರದ ವರೆಗೆ ಯೊರ್ದನ್ ನದಿಯ ಆಚೆ ಇರುವ ಭಾಗವನ್ನೆಲ್ಲಾ ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡಿದ್ದರು."
		},
		"5": {
			"1": "<sup>1</sup> ಮೋಶೆ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲರನ್ನು ಕರೆದು, “ಇಸ್ರಾಯೇಲರೇ, ನಾನು ಈಗ ನಿಮಗೆ ತಿಳಿಸುವ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ಕೇಳಿರಿ; ಇವುಗಳನ್ನು ಗ್ರಹಿಸಿಕೊಂಡು ಅನುಸರಿಸಿ ನಡೆಯಬೇಕು.",
			"2": "<sup>2</sup> ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಹೋರೇಬಿನಲ್ಲಿ ನಮ್ಮ ಸಂಗಡ ನಿಬಂಧನೆಯನ್ನು ಮಾಡಿದನು.",
			"3": "<sup>3</sup> ಆತನು ನಮ್ಮ ಪೂರ್ವಿಕರ ಸಂಗಡ ಈ ನಿಬಂಧನೆಯನ್ನು ಮಾಡದೆ, ಈಗ ಇಲ್ಲಿ ಜೀವದಿಂದಿರುವ ನಮ್ಮ ಸಂಗಡಲೇ ಇದನ್ನು ಮಾಡಿದನು.",
			"4": "<sup>4</sup> ಯೆಹೋವನು ಆ ಬೆಟ್ಟದ ಮೇಲೆ ಬೆಂಕಿಯ ಜ್ಞಾಲೆಯೊಳಗಿಂದ ಮುಖಾಮುಖಿಯಾಗಿ ನಿಮ್ಮ ಸಂಗಡ ಮಾತನಾಡಿದನು.",
			"5": "<sup>5</sup> ನೀವು ಆ ಬೆಂಕಿಯ ಜ್ವಾಲೆಯ ದೆಸೆಯಿಂದ ಭಯಪಟ್ಟು, ಬೆಟ್ಟವನ್ನು ಹತ್ತದೆ ಇದ್ದುದರಿಂದ ನಾನು ಯೆಹೋವನಿಗೂ ಮತ್ತು ನಿಮಗೂ ನಡುವೆ ನಿಂತು ಆತನು ಹೇಳಿದ ಮಾತುಗಳನ್ನು ನಿಮಗೆ ತಿಳಿಸಿದೆನು. ಆತನು ಹೇಳಿದ್ದೇನೆಂದರೆ,",
			"6": "<sup>6</sup> “ನೀನು ದಾಸತ್ವದಲ್ಲಿದ್ದ ಐಗುಪ್ತದೇಶದೊಳಗಿಂದ ನಿನ್ನನ್ನು ಬಿಡುಗಡೆಮಾಡಿದ ಯೆಹೋವನೆಂಬ ನಿನ್ನ ದೇವರು ನಾನೇ.",
			"7": "<sup>7</sup> ನಾನಲ್ಲದೆ ನಿನಗೆ ಬೇರೆ ದೇವರುಗಳು ಇರಬಾರದು.",
			"8": "<sup>8</sup> “ಯಾವ ಮೂರ್ತಿಯನ್ನೂ ಮಾಡಿಕೊಳ್ಳಬಾರದು. ಆಕಾಶದಲ್ಲಾಗಲಿ, ಭೂಮಿಯಲ್ಲಾಗಲಿ, ಭೂಮಿಯ ಕೆಳಗಣ ನೀರಿನಲ್ಲಾಗಲಿ ಇರುವ ಯಾವುದರ ರೂಪವನ್ನೂ ಮಾಡಿಕೊಳ್ಳಬಾರದು.",
			"9": "<sup>9</sup> ಅವುಗಳಿಗೆ ಅಡ್ಡಬೀಳಲೂ ಬಾರದು, ಪೂಜೆಮಾಡಲೂ ಬಾರದು. ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನೆಂಬ ನಾನು ನನಗೆ ಸಲ್ಲತಕ್ಕ ಗೌರವವನ್ನು ಮತ್ತೊಬ್ಬನಿಗೆ ಸಲ್ಲಗೊಡಿಸದವನಾದ್ದರಿಂದ, ನನ್ನನ್ನು ದ್ವೇಷಿಸುವವರ ವಿಷಯದಲ್ಲಿ ಪಿತೃಗಳ ದೋಷಫಲವನ್ನು ಮಕ್ಕಳ ಮೇಲೆ ಮೂರು ನಾಲ್ಕು ತಲೆಗಳ ವರೆಗೆ ಬರಮಾಡುವವನಾಗಿದ್ದೇನೆ.",
			"10": "<sup>10</sup> ನನ್ನನ್ನು ಪ್ರೀತಿಸಿ ನನ್ನ ಆಜ್ಞೆಗಳನ್ನು ಕೈಕೊಳ್ಳುವವರಿಗೋಸ್ಕರ ಸಾವಿರ ತಲೆಗಳವರೆಗೆ ದಯೆತೋರಿಸುವವನಾಗಿಯೂ ಇದ್ದೇನೆ.",
			"11": "<sup>11</sup> “ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನ ಹೆಸರನ್ನು ಅಯೋಗ್ಯಕಾರ್ಯಕ್ಕಾಗಿ ಉಪಯೋಗಿಸಬಾರದು. ಯೆಹೋವನು ತನ್ನ ಹೆಸರನ್ನು ಅಯೋಗ್ಯ ಕಾರ್ಯಕ್ಕಾಗಿ ಉಪಯೋಗಿಸುವವನನ್ನು ಶಿಕ್ಷಿಸದೆ ಬಿಡುವುದಿಲ್ಲ.",
			"12": "<sup>12</sup> “ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನು ಆಜ್ಞಾಪಿಸಿದಂತೆ ನೀನು ಸಬ್ಬತ್‍ ದಿನವನ್ನು ದೇವರ ದಿನವೆಂದು ಆಚರಿಸಬೇಕು.",
			"13": "<sup>13</sup> ಆರು ದಿನಗಳಲ್ಲಿ ನೀನು ದುಡಿದು ನಿನ್ನ ಕೆಲಸವನ್ನೆಲ್ಲಾ ಮಾಡಬೇಕು.",
			"14": "<sup>14</sup> ಏಳನೆಯ ದಿನ ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಮೀಸಲಾದ ವಿಶ್ರಾಂತಿದಿನವಾಗಿದೆ. ಅದರಲ್ಲಿ ನೀನು ಯಾವ ಕೆಲಸವನ್ನೂ ಮಾಡಬಾರದು. ನಿನ್ನ ಗಂಡುಮಕ್ಕಳು, ಹೆಣ್ಣುಮಕ್ಕಳು, ಗಂಡಾಳು, ಹೆಣ್ಣಾಳು, ಎತ್ತು, ಕತ್ತೆ, ಪಶುಗಳು ಮತ್ತು ನಿನ್ನ ಊರಲ್ಲಿರುವ ಅನ್ಯದೇಶದವರು ಯಾವ ಕೆಲಸವನ್ನೂ ಮಾಡಬಾರದು. ನಿನ್ನಂತೆಯೇ ನಿನ್ನ ಗಂಡಾಳು ಹೆಣ್ಣಾಳುಗಳಿಗೂ ವಿಶ್ರಾಂತಿ ದೊರೆಯಬೇಕು.",
			"15": "<sup>15</sup> ಐಗುಪ್ತ ದೇಶದಲ್ಲಿ ನೀವು ದಾಸತ್ವದಲ್ಲಿದ್ದುದ್ದನ್ನೂ, ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಭುಜಪರಾಕ್ರಮದಿಂದಲೂ ಮತ್ತು ಶಿಕ್ಷಾಹಸ್ತದಿಂದಲೂ ನಿಮ್ಮನ್ನು ಬಿಡುಗಡೆಮಾಡಿದ್ದನ್ನೂ ಜ್ಞಾಪಕಮಾಡಿಕೊಳ್ಳಿರಿ. ನೀವು ಸಬ್ಬತ್‍ ದಿನವನ್ನು ಆಚರಿಸಬೇಕೆಂದು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಇದಕ್ಕಾಗಿಯೇ ಆಜ್ಞಾಪಿಸಿದ್ದಾನೆ.",
			"16": "<sup>16</sup> “ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನು ಆಜ್ಞಾಪಿಸಿದಂತೆ ನಿನ್ನ ತಂದೆತಾಯಿಗಳನ್ನು ಸನ್ಮಾನಿಸಬೇಕು; ಸನ್ಮಾನಿಸಿದರೆ ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನು ನಿನಗೆ ಅನುಗ್ರಹಿಸುವ ದೇಶದಲ್ಲಿ ನೀನು ಬಹುಕಾಲ ಕ್ಷೇಮವಾಗಿ ಇರುವಿ, ಮತ್ತು ನಿನಗೆ ಒಳ್ಳೆಯದಾಗುವುದು.",
			"17": "<sup>17</sup> “ನರಹತ್ಯ ಮಾಡಬಾರದು.",
			"18": "<sup>18</sup> “ವ್ಯಭಿಚಾರ ಮಾಡಬಾರದು.",
			"19": "<sup>19</sup> “ಕದಿಯಬಾರದು.",
			"20": "<sup>20</sup> “ಮತ್ತೊಬ್ಬನ ಮೇಲೆ ಸುಳ್ಳು ಸಾಕ್ಷಿ ಹೇಳಬಾರದು.",
			"21": "<sup>21</sup> “ಮತ್ತೊಬ್ಬನ ಹೆಂಡತಿಯನ್ನು ಅಪೇಕ್ಷಿಸಬಾರದು; ಮತ್ತೊಬ್ಬನ ಹೊಲ, ಮನೆ, ಗಂಡಾಳು, ಹೆಣ್ಣಾಳು, ಎತ್ತು ಮತ್ತು ಕತ್ತೆ ಮುಂತಾದವುಗಳನ್ನು ಆಶಿಸಬಾರದು.”",
			"22": "<sup>22</sup> ಈ ಮಾತುಗಳನ್ನು ಯೆಹೋವನು ಆ ಬೆಟ್ಟದ ಮೇಲೆ ಬೆಂಕಿ, ಮೋಡ, ಕಾರ್ಗತ್ತಲು ಇವುಗಳ ಮಧ್ಯದೊಳಗಿಂದ ನಿಮ್ಮ ಸರ್ವಸಮೂಹಕ್ಕೆ ಕೇಳಿಸುವಂತೆ ಮಹಾ ಸ್ವರದಿಂದ ನುಡಿದನು. ಇದಲ್ಲದೆ ಆತನು ಮತ್ತೇನೂ ಹೇಳಲಿಲ್ಲ. ಆತನು ಆ ಮಾತುಗಳನ್ನು ಎರಡು ಕಲ್ಲಿನ ಹಲಗೆಗಳಲ್ಲಿ ಕೆತ್ತಿ ನನಗೆ ಕೊಟ್ಟನು.",
			"23": "<sup>23</sup> ಆ ಬೆಟ್ಟವು ಬೆಂಕಿಯಿಂದ ಪ್ರಜ್ವಲಿಸುತ್ತಿರುವಾಗ ಆ ಕತ್ತಲೊಳಗಿಂದ ದೇವರ ಸ್ವರವನ್ನು ಕೇಳಿ, ನೀವು ಅಂದರೆ ನಿಮ್ಮ ಕುಲಾಧಿಪತಿಗಳೂ ಮತ್ತು ಹಿರಿಯರೂ ನನ್ನ ಬಳಿಗೆ ಬಂದು,",
			"24": "<sup>24</sup> “ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ತನ್ನ ಘನವನ್ನೂ ಮತ್ತು ಮಹಿಮೆಯನ್ನೂ ನಮಗೆ ತೋರಿಸಿ, ಅಗ್ನಿಜ್ವಾಲೆಯೊಳಗಿಂದ ತನ್ನ ಸ್ವರವನ್ನು ನಮಗೆ ಕೇಳುವಂತೆ ಮಾಡಿದನಲ್ಲಾ. ಮನುಷ್ಯನು ತನ್ನ ಸಂಗಡ ದೇವರು ಮಾತನಾಡಿದರೂ ಸಾಯದೆ ಬದುಕಿದ್ದುಂಟೆಂಬುದು ಈ ಹೊತ್ತು ನಮಗೆ ತಿಳಿಯಬಂತು.",
			"25": "<sup>25</sup> ಆದರೂ ಈ ಘೋರವಾದ ಅಗ್ನಿಜ್ವಾಲೆಯು ನಮ್ಮನ್ನು ದಹಿಸಿ ಬಿಟ್ಟೀತು, ಮತ್ತು ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಸ್ವರವನ್ನು ನಾವು ಪುನಃ ಕೇಳಿದರೆ ಸತ್ತೇವು.",
			"26": "<sup>26</sup> ಬೆಂಕಿಯ ಜ್ವಾಲೆಯೊಳಗಿಂದ ಮಾತನಾಡುವ ಚೈತನ್ಯಸ್ವರೂಪನಾದ ದೇವರ ಸ್ವರವನ್ನು ನಾವು ಕೇಳಿದಂತೆ ಎಲ್ಲಾ ಮನುಷ್ಯರೊಳಗೆ ಯಾರು ಕೇಳಿ ಬದುಕಿದರು? ಹೀಗಿರುವುದರಿಂದ ನಾವು ಯಾಕೆ ಸಾಯಬೇಕು?",
			"27": "<sup>27</sup> ನೀನೇ ಹತ್ತಿರಕ್ಕೆ ಹೋಗಿ ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಆಜ್ಞೆಗಳನ್ನೆಲ್ಲಾ ತಿಳಿದುಕೊಂಡು ಬಂದು ನಮಗೆ ತಿಳಿಸು; ತಿಳಿಸಿದಾಗ ನಾವು ನಡಿಸುವೆವು” ಎಂದು ಹೇಳಿದಿರಿ.",
			"28": "<sup>28</sup> ನಿಮ್ಮ ಮಾತುಗಳನ್ನು ಯೆಹೋವನು ಕೇಳಿ ನನಗೆ, “ಈ ಜನರು ನಿನಗೆ ಹೇಳಿದ ಮಾತುಗಳನ್ನು ಕೇಳಿದ್ದೇನೆ; ಆ ಮಾತುಗಳೆಲ್ಲಾ ಒಳ್ಳೆಯವೇ.",
			"29": "<sup>29</sup> ನನ್ನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿ, ನನ್ನ ಆಜ್ಞೆಗಳನ್ನು ಅನುಸರಿಸುವ ಮನಸ್ಸು ಯಾವಾಗಲೂ ಅವರಲ್ಲಿ ಇದ್ದರೆ ಒಳ್ಳೇಯದು. ಆಗ ಅವರಿಗೂ ಅವರ ಸಂತತಿಯವರಿಗೂ ಯಾವಾಗಲೂ ಶುಭವುಂಟಾಗುವುದು”",
			"30": "<sup>30</sup> “‘ನಿಮ್ಮ ನಿಮ್ಮ ಡೇರೆಗಳಿಗೆ ಹಿಂತಿರುಗಿ ಹೋಗಿರಿ’ ಎಂದು ಅವರಿಗೆ ಆಜ್ಞಾಪಿಸು.",
			"31": "<sup>31</sup> ನೀನಾದರೋ ಇಲ್ಲೇ ನನ್ನ ಹತ್ತಿರ ನಿಂತಿರು; ನಾನು ಅವರಿಗೆ ಬೋಧಿಸಬೇಕಾದ ಎಲ್ಲಾ ಧರ್ಮೋಪದೇಶವನ್ನು ಮತ್ತು ವಿಧಿನಿಯಮಗಳನ್ನೂ ನಿನಗೆ ತಿಳಿಸುತ್ತೇನೆ. ನಾನು ಅವರಿಗೆ ಸ್ವಂತಕ್ಕಾಗಿ ಕೊಡುವ ದೇಶದಲ್ಲಿ ಅವರು ಇವುಗಳನ್ನು ಅನುಸರಿಸಬೇಕು” ಎಂದು ಹೇಳಿದನು.",
			"32": "<sup>32</sup> ಆದುದರಿಂದ ನೀವು ಬಲಕ್ಕಾಗಲಿ ಎಡಕ್ಕಾಗಲಿ ತಿರುಗಿಕೊಳ್ಳದೆ ತಪ್ಪಿ ನಡೆಯದೆ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಆಜ್ಞಾಪಿಸಿದಂತೆಯೇ ನಡೆದುಕೊಳ್ಳಬೇಕು.",
			"33": "<sup>33</sup> ಆಗ ನೀವು ಸ್ವತಂತ್ರಿಸಿಕೊಳ್ಳುವ ದೇಶದಲ್ಲಿ ಬಹುಕಾಲ ಸುಕ್ಷೇಮದಿಂದ ಬದುಕಿಕೊಳ್ಳುವಂತೆ, ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಆಜ್ಞಾಪಿಸಿದ ಮಾರ್ಗದಲ್ಲೇ ನಡೆಯಬೇಕು."
		},
		"6": {
			"1": "<sup>1</sup> ನೀವು ಯೊರ್ದನ್ ನದಿ ದಾಟಿ ಸ್ವತಂತ್ರಿಸಿಕೊಳ್ಳುವ ದೇಶದಲ್ಲಿ ಅನುಸರಿಸುವುದಕ್ಕಾಗಿ ನಿಮಗೆ ಬೋಧಿಸಬೇಕೆಂದು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನನಗೆ ಆಜ್ಞಾಪಿಸಿದ ಧರ್ಮೋಪದೇಶವೂ ಮತ್ತು ವಿಧಿನಿಯಮಗಳೂ ಇವೇ.",
			"2": "<sup>2</sup> ನೀವೂ, ನಿಮ್ಮ ಮಕ್ಕಳು, ಅವರ ಮಕ್ಕಳು ಮತ್ತು ಅವರ ಸಂತತಿಯವರು ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿದ್ದುಕೊಂಡು, ನಾನು ಈಗ ಬೋಧಿಸುವ ಆತನ ವಿಧಿನಿಯಮಗಳನ್ನೆಲ್ಲಾ ಅನುಸರಿಸಬೇಕೆಂದೂ ಮತ್ತು ನೀವು ಬಹುಕಾಲ ಬಾಳಬೇಕೆಂದೂ ಇವುಗಳನ್ನು ಆಜ್ಞಾಪಿಸಿದ್ದಾನೆ.",
			"3": "<sup>3</sup> ಆದುದರಿಂದ ಇಸ್ರಾಯೇಲರೇ, ನಿಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಯೆಹೋವನು ವಾಗ್ದಾನಮಾಡಿದ ಪ್ರಕಾರ ಹಾಲೂ ಮತ್ತು ಜೇನೂ ಹರಿಯುವ ಆ ದೇಶದಲ್ಲಿ ನಿಮಗೆ ಶುಭವುಂಟಾಗುವುದಕ್ಕೂ, ನೀವು ಬಹಳವಾಗಿ ಹೆಚ್ಚುವುದಕ್ಕೂ ಈ ಆಜ್ಞೆಗಳನ್ನು ಲಕ್ಷ್ಯವಿಟ್ಟು ಕೇಳಿ ಅನುಸರಿಸಲೇಬೇಕು.",
			"4": "<sup>4</sup> ಇಸ್ರಾಯೇಲರೇ, ಕೇಳಿರಿ, ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಒಬ್ಬನೇ ದೇವರು;",
			"5": "<sup>5</sup> ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಪೂರ್ಣಹೃದಯದಿಂದಲೂ, ಪೂರ್ಣಪ್ರಾಣದಿಂದಲೂ ಮತ್ತು ಪೂರ್ಣಶಕ್ತಿಯಿಂದಲೂ ಪ್ರೀತಿಸಬೇಕು.",
			"6": "<sup>6</sup> ನಾನು ಈಗ ನಿಮಗೆ ತಿಳಿಸುವ ಮಾತುಗಳು ನಿಮ್ಮ ಹೃದಯದಲ್ಲಿರಬೇಕು.",
			"7": "<sup>7</sup> ಇವುಗಳನ್ನು ನಿಮ್ಮ ಮಕ್ಕಳಿಗೆ ಅಭ್ಯಾಸಮಾಡಿಸಿ, ಮನೆಯಲ್ಲಿ ಕುಳಿತಿರುವಾಗಲೂ, ದಾರಿಯಲ್ಲಿ ನಡೆಯುವಾಗಲೂ, ಮಲಗುವಾಗಲೂ ಮತ್ತು ಏಳುವಾಗಲೂ ಇವುಗಳ ವಿಷಯದಲ್ಲಿ ಮಾತನಾಡಬೇಕು.",
			"8": "<sup>8</sup> ಇವುಗಳನ್ನು ಜ್ಞಾಪಕಾರ್ಥವಾಗಿ ಕೈಗೆ ಕಟ್ಟಿಕೊಳ್ಳಬೇಕು, ಇವು ಹಣೆಗೆ ಕಟ್ಟಿಕೊಳ್ಳುವ ಜ್ಞಾಪಕದ ಪಟ್ಟಿಯಂತೆ ಇರಬೇಕು.",
			"9": "<sup>9</sup> ನಿಮ್ಮ ಮನೆಬಾಗಿಲಿನ ನಿಲುವು ಪಟ್ಟಿಗಳಲ್ಲಿಯೂ ಮತ್ತು ದ್ವಾರಗಳ ಮೇಲೆಯೂ ಇವುಗಳನ್ನು ಬರೆಯಬೇಕು.",
			"10": "<sup>10</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮ ಪಿತೃಗಳಾದ ಅಬ್ರಹಾಮ್, ಇಸಾಕ್ ಮತ್ತು ಯಾಕೋಬರಿಗೆ ಪ್ರಮಾಣಮಾಡಿಕೊಟ್ಟ ದೇಶದಲ್ಲಿ ನಿಮ್ಮನ್ನು ಸೇರಿಸಿದಾಗ ನೀವು ಕಟ್ಟದ ಒಳ್ಳೆಯ ದೊಡ್ಡ ಪಟ್ಟಣಗಳನ್ನೂ, ನೀವು ಕೂಡಿಸದ ಉತ್ತಮವಸ್ತುಗಳಿಂದ",
			"11": "<sup>11</sup> ತುಂಬಿದ ಮನೆಗಳನ್ನೂ, ನೀವು ತೋಡದ ನೀರುಗುಂಡಿಗಳನ್ನೂ, ನೀವು ಬೆಳಸದ ದ್ರಾಕ್ಷೇತೋಟಗಳನ್ನೂ ಮತ್ತು ಎಣ್ಣೆಮರಗಳನ್ನೂ ಅನುಭವಿಸುತ್ತಾ ತೃಪ್ತರಾಗಿರುವಾಗ,",
			"12": "<sup>12</sup> ಐಗುಪ್ತದೇಶದಲ್ಲಿ ದಾಸರಾಗಿದ್ದ ನಿಮ್ಮನ್ನು ಬಿಡುಗಡೆ ಮಾಡಿದ ಯೆಹೋವನನ್ನು ಮರೆಯದೇ ಎಚ್ಚರದಿಂದಿರಿ.",
			"13": "<sup>13</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಲ್ಲಿಯೇ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿರಬೇಕು, ಆತನನ್ನೇ ಸೇವಿಸಬೇಕು ಮತ್ತು ಆತನ ಹೆಸರು ಹೇಳಿ ಪ್ರಮಾಣಮಾಡಬೇಕು.",
			"14": "<sup>14</sup> ಸುತ್ತಮುತ್ತಲಿರುವ ಜನಾಂಗಗಳು ಸೇವಿಸುವ ದೇವರುಗಳನ್ನು ನೀವು ಆರಾಧಿಸಬಾರದು.",
			"15": "<sup>15</sup> ನಿಮ್ಮ ಮಧ್ಯದಲ್ಲಿರುವ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ತನಗೆ ಸಲ್ಲಬೇಕಾದ ಗೌರವವನ್ನು ಇನ್ನೊಬ್ಬನಿಗೆ ಸಲ್ಲಗೊಡಿಸುವುದಿಲ್ಲ, ಆತನು ನಿಮ್ಮ ಮೇಲೆ ಕೋಪಗೊಂಡರೆ ನಿಮ್ಮನ್ನು ಭೂಮಿಯ ಮೇಲೆ ಉಳಿಯದಂತೆ ನಾಶಮಾಡಾನು.",
			"16": "<sup>16</sup> ನೀವು ಮಸ್ಸದಲ್ಲಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಪರೀಕ್ಷಿಸಿದಂತೆ ಇನ್ನು ಮುಂದೆ ಮಾಡಬೇಡಿರಿ.",
			"17": "<sup>17</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ನೇಮಿಸಿದ ಆಜ್ಞಾವಿಧಿನಿಯಮಗಳನ್ನು ಅನುಸರಿಸಲೇಬೇಕು.",
			"18": "<sup>18</sup> ಯೆಹೋವನ ದೃಷ್ಟಿಗೆ ಯಾವುದು ನ್ಯಾಯವೂ ಮತ್ತು ಯೋಗ್ಯವೂ ಆಗಿದೆಯೋ ಅದನ್ನೇ ಮಾಡಬೇಕು. ಹೀಗೆ ನಡೆದರೆ ನಿಮಗೆ ಶುಭವುಂಟಾಗುವುದು, ಮತ್ತು ಯೆಹೋವನು ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ಪ್ರಮಾಣಪೂರ್ವಕವಾಗಿ ಕೊಟ್ಟ ಉತ್ತಮದೇಶವನ್ನು ನೀವು ಸೇರಿ ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವಿರಿ.",
			"19": "<sup>19</sup> ಆತನು ತಾನೇ ನಿಮ್ಮ ಶತ್ರುಗಳನ್ನು ನಿಮ್ಮ ಎದುರಿನಿಂದ ಹೊರಡಿಸುವೆನೆಂದು ಮಾತುಕೊಟ್ಟನಲ್ಲವೇ.",
			"20": "<sup>20</sup> ಇನ್ನು ಮುಂದೆ ನಿಮ್ಮ ಮಕ್ಕಳು, “ಈ ಆಜ್ಞಾವಿಧಿನಿಯಮಗಳನ್ನು ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಏಕೆ ನೇಮಿಸಿದನು?” ಎಂದು ವಿಚಾರಿಸುವಾಗ",
			"21": "<sup>21</sup> ನೀವು ಅವರಿಗೆ, “ನಾವು ಐಗುಪ್ತದೇಶದಲ್ಲಿ ಫರೋಹನಿಗೆ ದಾಸರಾಗಿದ್ದಾಗ ಯೆಹೋವನು ತನ್ನ ಭುಜಬಲದಿಂದ ನಮ್ಮನ್ನು ಬಿಡುಗಡೆಮಾಡಿದನು.",
			"22": "<sup>22</sup> ಆತನು ಐಗುಪ್ತ್ಯರನ್ನೂ, ಫರೋಹನನ್ನೂ ಮತ್ತು ಅವನ ಮನೆಯವರನ್ನೂ ಬಾಧಕವಾದ ದೊಡ್ಡ ಮಹತ್ಕಾರ್ಯಗಳಿಂದಲೂ, ಉತ್ಪಾತಗಳಿಂದಲೂ ನಮ್ಮ ಕಣ್ಣೆದುರಿನಲ್ಲಿ ಶಿಕ್ಷಿಸಿ,",
			"23": "<sup>23</sup> ತಾನು ನಮ್ಮ ಪಿತೃಗಳಿಗೆ ಪ್ರಮಾಣಮಾಡಿಕೊಟ್ಟ ದೇಶದಲ್ಲಿ ನಮ್ಮನ್ನು ಸೇರಿಸುವುದಕ್ಕಾಗಿ ಐಗುಪ್ತದೇಶದೊಳಗಿಂದ ಬರಮಾಡಿದನು.",
			"24": "<sup>24</sup> ಆ ಕಾರಣದಿಂದಲೇ ಈ ನಿಯಮಗಳನ್ನೆಲ್ಲಾ ಆಚರಿಸಬೇಕೆಂದು ಆತನು ನಮಗೆ ಅಪ್ಪಣೆಕೊಟ್ಟಿದ್ದಾನೆ. ಯಾವಾಗಲೂ ಶುಭವುಂಟಾಗುವಂತೆಯೂ, ಆತನು ಈಗಲೂ ನಮ್ಮ ಪ್ರಾಣಗಳನ್ನು ಕಾಪಾಡುವಂತೆಯೂ ನಾವು ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿರಬೇಕು.",
			"25": "<sup>25</sup> ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿರೂಪಿಸಿದ ಈ ಧರ್ಮೋಪದೇಶವನ್ನೆಲ್ಲಾ ನಾವು ಅನುಸರಿಸಿದರೆ ಆತನ ದೃಷ್ಟಿಯಲ್ಲಿ ನೀತಿವಂತರೆಂದು ಪರಿಗಣಿಸಲು ಯೋಗ್ಯರಾಗುವೆವು” ಎಂಬುವುದಾಗಿ ನೀವು ಉತ್ತರಕೊಡಬೇಕು."
		},
		"7": {
			"1": "<sup>1</sup> ನೀವು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವುದಕ್ಕೆ ಹೋಗುವ ದೇಶದಲ್ಲಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಸೇರಿಸಿ ಅಧಿಕ ಸಂಖ್ಯೆಯಲ್ಲಿಯೂ ಮತ್ತು ಅಧಿಕ ಬಲದಲ್ಲಿಯೂ ನಿಮಗೆ ಮೀರುವ ಹಿತ್ತಿಯರು, ಗಿರ್ಗಾಷಿಯರು, ಅಮೋರಿಯರು, ಕಾನಾನ್ಯರು, ಪೆರಿಜ್ಜೀಯರು, ಹಿವ್ವಿಯರು ಮತ್ತು ಯೆಬೂಸಿಯರು ಎಂಬ ಏಳು ಜನಾಂಗಗಳನ್ನು ನಿಮ್ಮ ಮುಂದೆಯೇ ಹೊರಡಿಸುವನು.",
			"2": "<sup>2</sup> ಯೆಹೋವನು ಅವರನ್ನು ನಿಮಗೆ ಒಪ್ಪಿಸಲು ನೀವು ಅವರನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ಸಂಹರಿಸಬೇಕು. ಅವರ ಸಂಗಡ ಒಡಂಬಡಿಕೆ ಮಾಡಿಕೊಳ್ಳಬಾರದು ಹಾಗು ಅವರಿಗೆ ಕನಿಕರ ತೋರಬಾರದು.",
			"3": "<sup>3</sup> ಅವರೊಡನೆ ಬೀಗತನಮಾಡಬಾರದು; ಅವರ ಗಂಡು ಮಕ್ಕಳಿಗೆ ನಿಮ್ಮ ಹೆಣ್ಣುಮಕ್ಕಳನ್ನು ಕೊಡಲೂಬಾರದು ಮತ್ತು ಅವರ ಹೆಣ್ಣುಮಕ್ಕಳನ್ನು ನಿಮ್ಮ ಗಂಡುಮಕ್ಕಳಿಗೆ ತರಲೂಬಾರದು.",
			"4": "<sup>4</sup> ಹಾಗೆ ಮಾಡಿದರೆ ಅವರು ನಿಮ್ಮ ಗಂಡು ಮಕ್ಕಳನ್ನು ಯೆಹೋವನ ಸೇವೆ ಮಾಡದಂತೆ ತಪ್ಪಿಸಿ, ಅನ್ಯ ದೇವರುಗಳನ್ನು ಪೂಜಿಸುವ ಹಾಗೆ ಮಾಡುವರು. ಆಗ ಯೆಹೋವನು ನಿಮ್ಮ ಮೇಲೆ ಕೋಪಗೊಂಡು ಬೇಗನೆ ನಿಮ್ಮನ್ನು ನಾಶಮಾಡುವನು.",
			"5": "<sup>5</sup> ಆದುದರಿಂದ ನೀವು ಹೀಗೆ ಮಾಡಬೇಕು, ಅವರ ಬಲಿಪೀಠಗಳನ್ನು ಕೆಡವಬೇಕು; ಅವರ ಪವಿತ್ರವಾದ ಕಲ್ಲಿನ ಕಂಬಗಳನ್ನು ಒಡೆದು, ಅಶೇರವೆಂಬ ವಿಗ್ರಹಸ್ತಂಭಗಳನ್ನು ಕಡಿದು, ಅವರ ದೇವತಾಪ್ರತಿಮೆಗಳನ್ನು ಬೆಂಕಿಯಿಂದ ಸುಟ್ಟುಬಿಡಬೇಕು.",
			"6": "<sup>6</sup> ಯಾಕೆಂದರೆ ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಮೀಸಲಾದ ಪರಿಶುದ್ಧ ಜನರಾಗಿದ್ದೀರಲ್ಲವೇ; ಆತನು ಭೂಲೋಕದಲ್ಲಿರುವ ಸಮಸ್ತ ಜನಾಂಗಗಳಲ್ಲಿ ನಿಮ್ಮನ್ನೇ ಸ್ವಕೀಯಜನವಾಗುವುದಕ್ಕೆ ಆರಿಸಿಕೊಂಡನು.",
			"7": "<sup>7</sup> ನಿಮ್ಮನ್ನು ಎಲ್ಲಾ ಜನಾಂಗಗಳಲ್ಲಿ ಹೆಚ್ಚು ಜನರು ಎಂದು ಇಷ್ಟಪಟ್ಟು ಆರಿಸಿಕೊಳ್ಳಲಿಲ್ಲ; ನೀವು ಎಲ್ಲಾ ಜನಾಂಗಗಳಿಗಿಂತಲೂ ಅಲ್ಪಸಂಖ್ಯಾತರು.",
			"8": "<sup>8</sup> ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಪ್ರೀತಿಸಿ, ತಾನು ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ಮಾಡಿದ ಪ್ರಮಾಣವನ್ನು ನೆರವೇರಿಸಬೇಕೆಂದು ಐಗುಪ್ತ್ಯರ ಅರಸನಾದ ಫರೋಹನ ಕೈಕೆಳಗೆ ದಾಸರಾಗಿದ್ದ ನಿಮ್ಮನ್ನು ಬಿಡುಗಡೆಮಾಡಿ, ತನ್ನ ಭುಜಬಲವನ್ನು ಪ್ರಯೋಗಿಸಿ ಆ ದೇಶದೊಳಗಿಂದ ಬರಮಾಡಿದನು.",
			"9": "<sup>9</sup> ಆದುದರಿಂದ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನೊಬ್ಬನೇ ದೇವರೆಂದು ತಿಳಿದುಕೊಳ್ಳಬೇಕು. ಆತನು ನಂಬಿಗಸ್ತನಾದ ದೇವರು; ತನ್ನನ್ನು ಪ್ರೀತಿಸಿ ತನ್ನ ಆಜ್ಞೆಗಳನ್ನು ಅನುಸರಿಸುವವರಿಗೆ ತಾನು ಮಾಡಿದ ವಾಗ್ದಾನವನ್ನೂ ಮತ್ತು ಕೃಪೆಯನ್ನೂ ಅವರ ಸಂತತಿಯವರಲ್ಲಿ ಸಾವಿರ ತಲೆಗಳವರೆಗೂ ನೆರವೇರಿಸುವವನಾಗಿಯೂ ಮತ್ತು",
			"10": "<sup>10</sup> ತನ್ನನ್ನು ತಿರಸ್ಕರಿಸಿದವರಿಗೆ ಪ್ರತಿಯಾಗಿ ನಾಶವನ್ನು ಉಂಟುಮಾಡುವವನಾಗಿಯೂ ಇದ್ದಾನೆಂದು ತಿಳಿದುಕೊಳ್ಳಿರಿ. ಹಗೆ ಮಾಡುವವರ ವಿಷಯದಲ್ಲಿ ತಡಮಾಡದೆ ಆಗಲೇ ಮುಯ್ಯಿತೀರಿಸುವನು.",
			"11": "<sup>11</sup> ಆದಕಾರಣ ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ಧರ್ಮೋಪದೇಶವನ್ನೂ ಆಜ್ಞಾವಿಧಿಗಳನ್ನೂ ನೀವು ಅನುಸರಿಸಿ ನಡೆಯಬೇಕು.",
			"12": "<sup>12</sup> ನೀವು ಈ ವಿಧಿಗಳನ್ನು ಲಕ್ಷ್ಯವಿಟ್ಟು ಅನುಸರಿಸಿದರೆ, ಅದಕ್ಕೆ ಪ್ರತಿಯಾಗಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ಪ್ರಮಾಣಪೂರ್ವಕವಾಗಿ ಮಾಡಿದ ಒಡಂಬಡಿಕೆಯನ್ನು ನಂಬಿಗಸ್ತಿಕೆಯಿಂದ ನೆರವೇರಿಸುವವನಾಗಿದ್ದಾನೆ.",
			"13": "<sup>13</sup> ಆತನು ನಿಮ್ಮನ್ನು ಪ್ರೀತಿಸಿ, ಅಭಿವೃದ್ಧಿಪಡಿಸಿ ಹೆಚ್ಚಿಸುವನು. ಆತನು ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ಪ್ರಮಾಣಮಾಡಿಕೊಟ್ಟ ದೇಶದಲ್ಲಿ ನಿಮ್ಮ ಸಂತಾನವನ್ನೂ, ವ್ಯವಸಾಯವನ್ನೂ, ನಿಮಗಿರುವ ದವಸ ಧಾನ್ಯ, ದ್ರಾಕ್ಷಾರಸ, ಎಣ್ಣೆ ಇವುಗಳನ್ನೂ, ನಿಮ್ಮ ದನ ಮತ್ತು ಕುರಿಗಳಲ್ಲಿ ಹುಟ್ಟಿದ್ದನ್ನೂ ಅಭಿವೃದ್ಧಿಪಡಿಸುವನು.",
			"14": "<sup>14</sup> ನೀವು ಎಲ್ಲಾ ಜನಗಳಿಗಿಂತಲೂ ಹೆಚ್ಚಾಗಿ ದೇವರ ಅನುಗ್ರಹವನ್ನು ಹೊಂದಿದವರಾಗಿರುವಿರಿ. ನಿಮ್ಮೊಳಗೆ ಸ್ತ್ರೀಪುರುಷರಲ್ಲಿಯಾಗಲಿ, ಹೆಣ್ಣುಗಂಡು ಪಶುಗಳಲ್ಲಿಯಾಗಲಿ ಬಂಜೆತನವು ಇರುವುದಿಲ್ಲ.",
			"15": "<sup>15</sup> ನಿಮ್ಮಲ್ಲಿ ಯಾವ ವ್ಯಾಧಿಯೂ ಉಂಟಾಗದಂತೆ ಯೆಹೋವನು ಮಾಡುವನು. ನಿಮ್ಮ ಅನುಭವಕ್ಕೆ ಬಂದ ಪ್ರಕಾರ ಐಗುಪ್ತದೇಶದಲ್ಲಿ ಪ್ರಬಲವಾಗಿರುವ ಕ್ರೂರವ್ಯಾಧಿಗಳನ್ನು ನಿಮಗೆ ಬರಗೊಡಿಸದೆ, ಅವುಗಳನ್ನು ನಿಮ್ಮ ಶತ್ರುಗಳ ಮೇಲೆಯೇ ಬರಮಾಡುವನು.",
			"16": "<sup>16</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮಿಂದ ಪರಾಜಯ ಹೊಂದುವ ಜನಾಂಗಗಳನ್ನೆಲ್ಲಾ ನೀವು ಕನಿಕರಿಸದೆ ನಾಶಮಾಡಬೇಕು. ಅವರ ದೇವರುಗಳನ್ನು ಪೂಜಿಸಲೇಬಾರದು; ಪೂಜಿಸಿದರೆ ಅವು ನಿಮ್ಮ ಜೀವಕ್ಕೆ ಉರುಲಾಗುವವು.",
			"17": "<sup>17</sup> ಆ ಜನಗಳು ನಮಗಿಂತ ಹೆಚ್ಚಾಗಿದ್ದಾರೆ; ಅವರನ್ನು ಹೊರಡಿಸುವುದು ನಮ್ಮಿಂದ ಹೇಗಾದೀತು ಅಂದುಕೊಳ್ಳುತ್ತೀರೋ? ಅವರಿಗೆ ಹೆದರಬೇಡಿರಿ.",
			"18": "<sup>18</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಫರೋಹನಿಗೂ ಮತ್ತು ಐಗುಪ್ತ್ಯರೆಲ್ಲರಿಗೂ ಮಾಡಿದ್ದನ್ನು ನೆನಪಿಗೆ ತಂದುಕೊಳ್ಳಿರಿ.",
			"19": "<sup>19</sup> ಆಗ ನೀವು ಕಣ್ಣಾರೆ ನೋಡಿದ ಪ್ರಕಾರ ಆತನು ವಿಶೇಷ ಪರಿಶೋಧನೆ, ಮಹತ್ಕಾರ್ಯ, ಉತ್ಪಾತ, ಭುಜಪರಾಕ್ರಮ, ಶಿಕ್ಷೆ ಇವುಗಳನ್ನು ಪ್ರಯೋಗಿಸಿ ನಿಮ್ಮನ್ನು ಬಿಡುಗಡೆಮಾಡಿದನಲ್ಲಾ. ನೀವು ಹೆದರಿಕೊಳ್ಳುವ ಆ ಎಲ್ಲಾ ಜನಾಂಗಗಳಿಗೂ ಆತನು ಹಾಗೆಯೇ ಮಾಡುವನು.",
			"20": "<sup>20</sup> ಅದು ಮಾತ್ರವಲ್ಲದೆ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಕಣಜದ ಹುಳಗಳನ್ನು ಅವರ ನಡುವೆ ಕಳುಹಿಸುವನು. ಮರೆಯಾಗಿದ್ದು ಉಳಿದವರು ಆ ಹುಳಗಳ ದೆಸೆಯಿಂದ ನಾಶವಾಗಿ ನಿಮಗೆ ಕಾಣದೆ ಹೋಗುವರು.",
			"21": "<sup>21</sup> ನೀವು ಅವರಿಗೆ ಹೆದರಿಕೊಳ್ಳಬೇಡಿರಿ; ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮ ಮಧ್ಯದಲ್ಲಿದ್ದಾನೆ; ಆತನು ನಮ್ಮ ಭಕ್ತಿಗೆ ಪ್ರತಿಕಾರವಾಗಿ ವಿಸ್ಮಯ ಹುಟ್ಟಿಸುವ ದೇವರಾಗಿದ್ದಾನೆ.",
			"22": "<sup>22</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಆ ಜನಾಂಗಗಳನ್ನು ನಿಮ್ಮ ಮುಂದೆಯೇ ಸ್ವಲ್ಪಸ್ವಲ್ಪವಾಗಿ ಹೊರಟು ಹೋಗುವಂತೆ ಮಾಡುವನು. ನೀವು ಅವರನ್ನು ಒಂದೇ ಬಾರಿಗೆ ನಿರ್ಮೂಲಮಾಡಬಾರದು. ಹಾಗೆ ಮಾಡಿದರೆ ಕಾಡುಮೃಗಗಳು ಹೆಚ್ಚಿ ಅವು ನಿಮಗೆ ತೊಂದರೆಯನ್ನು ಉಂಟುಮಾಡುತ್ತವೆ.",
			"23": "<sup>23</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಅವರನ್ನು ನಿಮ್ಮಿಂದ ಸೋಲಿಸಿ, ಬಹಳವಾಗಿ ಗಲಿಬಿಲಿಮಾಡಿ ಕಡೆಗೆ ಅವರನ್ನು ಇಲ್ಲದಂತೆ ಮಾಡುವನು.",
			"24": "<sup>24</sup> ಆತನು ಅವರ ಅರಸರನ್ನು ನಿಮ್ಮ ಕೈಗೆ ಸಿಗುವಂತೆ ಮಾಡಿ, ನೀವು ಅವರ ಹೆಸರುಗಳೇ ಭೂಮಿಯ ಮೇಲೆ ಉಳಿಯದಂತೆ ಮಾಡಬೇಕು. ಯಾರೂ ನಿಮ್ಮ ಮುಂದೆ ನಿಲ್ಲುವುದಿಲ್ಲ; ನೀವು ಎಲ್ಲರನ್ನೂ ನಾಶಮಾಡುವಿರಿ.",
			"25": "<sup>25</sup> ಅವರು ದೇವರೆಂದು ಮಾಡಿಕೊಂಡ ಮರದ ವಿಗ್ರಹಗಳನ್ನು ನೀವು ಬೆಂಕಿಯಿಂದ ಸುಟ್ಟುಬಿಡಬೇಕು. ಆ ವಿಗ್ರಹಗಳ ಮೇಲಿರುವ ಬೆಳ್ಳಿಬಂಗಾರವನ್ನು ನೀವು ಆಶಿಸಬಾರದು; ಅದನ್ನು ತೆಗೆದುಕೊಂಡರೆ ಅದು ನಿಮಗೆ ಉರುಲಾಗುವುದು. ಅದು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಅಸಹ್ಯವಾದುದು.",
			"26": "<sup>26</sup> ಅದು ಕೇವಲ ಯೆಹೋವನಿಂದ ನಾಶವಾಗತಕ್ಕದ್ದು. ಆದುದರಿಂದ ನೀವು ಅಂತಹ ವಸ್ತುವನ್ನು ಮನೆಯೊಳಕ್ಕೆ ತಂದು ಅದರಂತೆಯೇ ನಾಶಕ್ಕೆ ಗುರಿಯಾಗಬಾರದು. ನೀವು ಅದನ್ನು ಅಸಹ್ಯಪಟ್ಟು ಮುಟ್ಟಲೂ ಬಾರದು."
		},
		"8": {
			"1": "<sup>1</sup> ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ಧರ್ಮೋಪದೇಶವನ್ನೆಲ್ಲಾ ನೀವು ಅನುಸರಿಸಬೇಕು. ಅನುಸರಿಸಿದರೆ ನೀವು ಬದುಕಿ ಅಭಿವೃದ್ಧಿಹೊಂದುವಿರಿ, ಯೆಹೋವನು ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ಪ್ರಮಾಣಪೂರ್ವಕವಾಗಿ ಕೊಟ್ಟ ದೇಶವನ್ನು ಸೇರಿ ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವಿರಿ.",
			"2": "<sup>2</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಈ ನಲ್ವತ್ತು ವರುಷ ಅರಣ್ಯದಲ್ಲಿ ನಡಿಸಿದ್ದನ್ನೂ, ನೀವು ಆತನ ಆಜ್ಞೆಗಳನ್ನು ಕೈಕೊಳ್ಳುತ್ತಿರೋ ಇಲ್ಲವೋ ಎಂದು ತಿಳಿದುಕೊಳ್ಳುವುದಕ್ಕೆ ನಿಮ್ಮನ್ನು ಕಷ್ಟಕ್ಕೆ ಒಳಪಡಿಸಿ, ನಿಮ್ಮ ಮನೋಭಾವವನ್ನು ಪರೀಕ್ಷಿಸಿದ್ದನ್ನೂ ನೆನಪಿಗೆ ತಂದುಕೊಳ್ಳಿರಿ.",
			"3": "<sup>3</sup> ಮನುಷ್ಯರು ಆಹಾರದಿಂದ ಮಾತ್ರವಲ್ಲ, ಯೆಹೋವನ ಬಾಯಿಂದ ಹೊರಡುವ ಪ್ರತಿಯೊಂದು ನುಡಿಯಿಂದಲೂ ಬದುಕುತ್ತಾರೆ ಎಂಬುವುದು ನಿಮಗೆ ತಿಳಿಯುವಂತೆ ಆತನು ನಿಮ್ಮನ್ನು ಕಷ್ಟಕ್ಕೆ ಒಳಪಡಿಸಿ, ಹಸಿವೆಯಿಂದ ಬಳಲಿಸಿ, ನಿಮಗೂ, ನಿಮ್ಮ ಪಿತೃಗಳಿಗೂ ಮತ್ತು ನಿಮ್ಮ ಪೂರ್ವಿಕರಿಗೂ ಗೊತ್ತಿಲ್ಲದ ಮನ್ನವನ್ನು ಕೊಟ್ಟು ಪೋಷಿಸಿದನು.",
			"4": "<sup>4</sup> ಆ ನಲವತ್ತು ವರುಷ ನಿಮ್ಮ ಮೈಮೇಲಿದ್ದ ಉಡುಪು ಹಳೇಯದಾಗಲಿಲ್ಲ; ನಿಮ್ಮ ಕಾಲುಗಳು ಬಾತುಹೋಗಲಿಲ್ಲ.",
			"5": "<sup>5</sup> ತಂದೆಯು ಮಗನನ್ನು ಹೇಗೆ ಶಿಕ್ಷಿಸುತ್ತಾನೋ ಹಾಗೆಯೇ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಶಿಕ್ಷಿಸುತ್ತಾ ಬಂದನು ಎಂಬುವುದನ್ನು ನೀವು ತಿಳಿದುಕೊಳ್ಳಿರಿ.",
			"6": "<sup>6</sup> ನೀವು ಆತನ ಆಜ್ಞೆಗಳನ್ನು ಅನುಸರಿಸುವವರಾಗಿ, ಆತನು ಹೇಳಿದ ಮಾರ್ಗದಲ್ಲೇ ನಡೆದು ಆತನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿರಬೇಕು.",
			"7": "<sup>7</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಉತ್ತಮವಾದ ದೇಶಕ್ಕೆ ನಿಮ್ಮನ್ನು ಸೇರಿಸುತ್ತಾನೆ. ಆ ದೇಶದ ಹಳ್ಳಗಳಲ್ಲಿ ನೀರು ಯಾವಾಗಲೂ ಹರಿಯುತ್ತದೆ; ಮತ್ತು ತಗ್ಗುಗಳಲ್ಲಾಗಲಿ, ಗುಡ್ಡಗಳಲ್ಲಾಗಲಿ ಎಲ್ಲಾ ಕಡೆಯೂ ಬುಗ್ಗೆಗಳಿಂದ ನೀರು ಉಕ್ಕುತ್ತದೆ.",
			"8": "<sup>8</sup> ಆ ದೇಶದಲ್ಲಿ ಗೋದಿ, ಜವೆಗೋದಿ, ದ್ರಾಕ್ಷೇ, ಅಂಜೂರ, ದಾಳಿಂಬ ಇವುಗಳು ಬೆಳೆಯುತ್ತವೆ; ಎಣ್ಣೆ ಮರಗಳೂ ಮತ್ತು ಜೇನೂ ಸಿಕ್ಕುತ್ತವೆ.",
			"9": "<sup>9</sup> ಅಲ್ಲಿ ನೀವು ದುರ್ಭಿಕ್ಷವನ್ನು ಕಾಣದೆ ಸಮೃದ್ಧಿಯಾಗಿ ಊಟಮಾಡಿ, ಯಾವ ಕೊರತೆಯೂ ಇಲ್ಲದೆ ಇರುವಿರಿ. ಆ ದೇಶದಲ್ಲಿ ಕಬ್ಬಿಣದ ಕಲ್ಲು ಸಿಕ್ಕುತ್ತದೆ; ಅಲ್ಲಿಯ ಬೆಟ್ಟಗಳಲ್ಲಿ ತಾಮ್ರದ ಗಣಿಗಳುಂಟು.",
			"10": "<sup>10</sup> ನೀವು ಹೊಟ್ಟೆ ತುಂಬಾ ಊಟಮಾಡಿ ಸುಖದಿಂದಿರುವಾಗ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಉತ್ತಮ ದೇಶವನ್ನು ಕೊಟ್ಟಿದ್ದಕ್ಕಾಗಿ ಆತನನ್ನು ಸ್ತುತಿಸಬೇಕು.",
			"11": "<sup>11</sup> ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಆಜ್ಞಾವಿಧಿನಿಯಮಗಳನ್ನು ಕೈಕೊಳ್ಳದವರೂ, ಆತನನ್ನು ಮರೆಯುವವರೂ ಆಗಬೇಡಿರಿ.",
			"12": "<sup>12</sup> ನೀವು ಹೊಟ್ಟೆತುಂಬಾ ಊಟಮಾಡಿ, ಸುಖವಾಗಿದ್ದು ಒಳ್ಳೇ ಮನೆಗಳನ್ನು ಕಟ್ಟಿಸಿಕೊಂಡು,",
			"13": "<sup>13</sup> ಅವುಗಳಲ್ಲಿ ವಾಸವಾಗಿರುವ ಕಾಲದಲ್ಲಿ ನಿಮ್ಮ ದನಗಳೂ, ಆಡು ಕುರಿಗಳೂ, ನಿಮ್ಮ ಬೆಳ್ಳಿ ಮತ್ತು ಬಂಗಾರವೂ,",
			"14": "<sup>14</sup> ಆಸ್ತಿಯೆಲ್ಲವೂ ಹೆಚ್ಚುತ್ತಿರುವಾಗ ಒಂದು ವೇಳೆ ನೀವು ಅಹಂಕಾರದಿಂದ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಮರೆತೀರಿ.",
			"15": "<sup>15</sup> ಆತನು ಐಗುಪ್ತ ದೇಶದಲ್ಲಿ ದಾಸರಾಗಿದ್ದ ನಿಮ್ಮನ್ನು ಬಿಡುಗಡೆಮಾಡಿ, ವಿಷಸರ್ಪಗಳೂ ಮತ್ತು ಚೇಳುಗಳೂ ಇದ್ದ ಆ ಘೋರವಾದ ಮಹಾ ಅರಣ್ಯವನ್ನೂ, ನೀರು ಬತ್ತಿಹೋದ ಭೂಮಿಗಳನ್ನೂ ದಾಟಿಸಿದ್ದನ್ನು ಮತ್ತು ಗಟ್ಟಿಯಾದ ಬಂಡೆಯೊಳಗಿಂದ ನೀರು ಹೊರಡಿಸಿದ್ದನ್ನು ಮರೆಯಬೇಡಿರಿ.",
			"16": "<sup>16</sup> ಆತನು ನಿಮ್ಮನ್ನು ಕಷ್ಟಕ್ಕೆ ಒಳಪಡಿಸಿ ಪರೀಕ್ಷಿಸಿದನಂತರ, ನಿಮಗೆ ಸುಕ್ಷೇಮವನ್ನು ಉಂಟುಮಾಡಬೇಕೆಂಬ ಉದ್ದೇಶದಿಂದ ನಿಮ್ಮ ಪೂರ್ವಿಕರಿಗೆ ತಿಳಿಯದೆ ಇದ್ದ ಮನ್ನವನ್ನು ಕೊಟ್ಟು ಪೋಷಿಸಿದಾತನನ್ನು ನೀವು ಮರೆತು",
			"17": "<sup>17</sup> ನಿಮ್ಮ ಮನಸ್ಸಿನೊಳಗೆ, “ಈ ಭಾಗ್ಯವು ನಮ್ಮ ಸಾಮರ್ಥ್ಯ ಮತ್ತು ಸಾಹಸಗಳಿಂದಲೇ ನಮಗುಂಟಾಯಿತು” ಎಂದು ಅಂದುಕೊಂಡೀರಿ.",
			"18": "<sup>18</sup> ಆದುದರಿಂದ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ತಾನೇ ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ಪ್ರಮಾಣಪೂರ್ವಕವಾಗಿ ಮಾಡಿದ ವಾಗ್ದಾನವನ್ನು ಈಗ ನಿಮ್ಮ ಅನುಭವಕ್ಕೆ ಬಂದ ರೀತಿಯಲ್ಲಿ ನೆರವೇರಿಸುವವನಾಗಿ, ನಿಮಗೆ ಇಷ್ಟು ಭಾಗ್ಯವನ್ನು ಸಂಪಾದಿಸುವುದಕ್ಕೆ ಸಾಮರ್ಥ್ಯವನ್ನು ಕೊಟ್ಟವನೆಂದು ಜ್ಞಾಪಕಮಾಡಿಕೊಳ್ಳಿರಿ.",
			"19": "<sup>19</sup> ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಮರೆತು, ಇತರ ದೇವರುಗಳನ್ನು ಅವಲಂಬಿಸಿ ಪೂಜಿಸಿದರೆ ನೀವು ತಪ್ಪದೆ ನಾಶವಾಗಿ ಹೋಗುವಿರೆಂದು ನಿಮ್ಮನ್ನು ಈಗ ಖಂಡಿತವಾಗಿ ಎಚ್ಚರಿಸುತ್ತೇನೆ.",
			"20": "<sup>20</sup> ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಮಾತನ್ನು ಕೇಳದೆ ಹೋದರೆ ನಿಮ್ಮ ಎದುರಾಗಿ ನಾಶವಾಗಿ ಹೋಗುವ ಇತರ ಜನಾಂಗಗಳಂತೆಯೇ ನೀವೂ ಆತನಿಂದ ನಾಶವಾಗಿ ಹೋಗುವಿರಿ."
		},
		"9": {
			"1": "<sup>1</sup> ಇಸ್ರಾಯೇಲರೇ ಕೇಳಿರಿ, ನಿಮಗಿಂತ ಮಹಾ ಬಲಿಷ್ಠ ಜನಾಂಗಗಳನ್ನೂ ಮತ್ತು ಆಕಾಶವನ್ನು ಮುಟ್ಟುವ ಕೋಟೆಕೊತ್ತಲುಗಳುಳ್ಳ ಪಟ್ಟಣಗಳನ್ನೂ ಸ್ವಾಧೀನ ಮಾಡಿಕೊಳ್ಳುವುದಕ್ಕೆ ನೀವು ಈ ಯೊರ್ದನ್ ನದಿಯನ್ನು ಇಂದು ದಾಟಲಿದ್ದೀರಿ.",
			"2": "<sup>2</sup> ಆ ದೇಶದ ಅನಾಕ್ಯರು ಬಲಿಷ್ಠರು ಮತ್ತು ಎತ್ತರವಾದ ಪುರುಷರು ಆಗಿದ್ದಾರೆ. ಅವರ ವಿಷಯ ನಿಮಗೆ ತಿಳಿದೇ ಇದೆ, “ಅನಾಕ್ಯರ ಮುಂದೆ ನಿಲ್ಲಬಲ್ಲವರು ಯಾರಿದ್ದಾರೆ?” ಎಂಬ ಮಾತನ್ನು ಕೇಳಿದ್ದೀರಿ.",
			"3": "<sup>3</sup> ಆದುದರಿಂದ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ತಾನೇ ವಿರುದಿಗಳನ್ನು ದಹಿಸುವ ಬೆಂಕಿಯೋಪಾದಿಯಲ್ಲಿ ನಿಮ್ಮ ಮುಂದೆ ಹೋಗುತ್ತಾನೆಂದು ಈಗ ತಿಳಿದುಕೊಳ್ಳಿರಿ. ಆತನೇ ಅವರನ್ನು ನಾಶಮಾಡುವನು; ಆತನೇ ಅವರನ್ನು ನಿಮ್ಮ ಮುಂದೆ ಸೋತುಹೋಗುವಂತೆ ಮಾಡುವನು. ಯೆಹೋವನು ನಿಮಗೆ ಹೇಳಿದಂತೆ ನೀವು ಅವರನ್ನು ಹೊರಡಿಸಿ, ಬೇಗನೆ ನಾಶಮಾಡುವಿರಿ.",
			"4": "<sup>4</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಅವರನ್ನು ನಿಮ್ಮ ಎದುರಿನಿಂದ ಹೊರಡಿಸಿದ ನಂತರ ನೀವು ನಿಮ್ಮ ಮನಸ್ಸಿನಲ್ಲಿ, “ಯೆಹೋವನು ನಮ್ಮನ್ನು ಈ ದೇಶಕ್ಕೆ ಕರೆತಂದು ಅದನ್ನು ನಮಗೆ ಸ್ವಾಧೀನಪಡಿಸಿದ್ದಕ್ಕೆ ಕಾರಣ ನಮ್ಮ ಸದಾಚಾರವೇ” ಅಂದುಕೊಳ್ಳಬಾರದು. ಆ ಜನಾಂಗಗಳ ದುರ್ನಡತೆಯ ದೆಸೆಯಿಂದಲೇ ಯೆಹೋವನು ನಿಮ್ಮ ಎದುರಿನಿಂದ ಅವರನ್ನು ಹೊರಡಿಸುತ್ತಾನೆ.",
			"5": "<sup>5</sup> ನೀವು ಅವರ ದೇಶಕ್ಕೆ ಬಂದು ಅದನ್ನು ಸ್ವಾಧೀನ ಮಾಡಿಕೊಳ್ಳುವುದಕ್ಕೆ ನಿಮ್ಮ ಸದಾಚಾರವಾಗಲಿ, ನಿಮ್ಮ ಒಳ್ಳೆಯ ಸ್ವಭಾವವೇ ಆಗಲಿ ಕಾರಣವಲ್ಲ. ಆ ಜನಾಂಗಗಳ ದುರ್ನಡತೆಯ ದೆಸೆಯಿಂದಲೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮ ಪಿತೃಗಳಾದ ಅಬ್ರಹಾಮ್, ಇಸಾಕ್ ಮತ್ತು ಯಾಕೋಬರಿಗೆ ಪ್ರಮಾಣಪೂರ್ವಕವಾಗಿ ಮಾಡಿದ ವಾಗ್ದಾನವನ್ನು ನೆರವೇರಿಸಬೇಕೆಂದಿದ್ದರಿಂದಲೂ ಅವರನ್ನು ನಿಮ್ಮ ಎದುರಿನಿಂದ ಹೊರಡಿಸಿಬಿಡುತ್ತಾನೆ.",
			"6": "<sup>6</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಆ ಉತ್ತಮ ದೇಶವನ್ನು ನಿಮಗೆ ಸ್ವದೇಶವಾಗಿ ಕೊಡುವುದು ನಿಮ್ಮ ಸದಾಚಾರದ ಫಲವಲ್ಲವೆಂದು ಚೆನ್ನಾಗಿ ತಿಳಿದುಕೊಳ್ಳಿರಿ. ನೀವು ಆತನ ಆಜ್ಞೆಯನ್ನು ಪಾಲಿಸದ ಮೊಂಡ ಜನರೇ ಆಗಿದ್ದೀರಿ.",
			"7": "<sup>7</sup> ನೀವು ಅರಣ್ಯದಲ್ಲಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಕೋಪಹುಟ್ಟಿಸಿದ್ದನ್ನು ನೆನಪುಮಾಡಿಕೊಳ್ಳಿರಿ; ಅದನ್ನು ಮರೆಯಬೇಡಿರಿ. ನೀವು ಐಗುಪ್ತದೇಶವನ್ನು ಬಿಟ್ಟಂದಿನಿಂದ ಈ ಸ್ಥಳಕ್ಕೆ ಬಂದ ದಿನದವರೆಗೂ ಯೆಹೋವನ ಆಜ್ಞೆಗಳನ್ನು ಧಿಕ್ಕರಿಸುವವರಾಗಿದ್ದೀರಿ.",
			"8": "<sup>8</sup> ಉದಾಹರಣೆಗೆ, ಹೋರೇಬಿನಲ್ಲಿ ನೀವು ಯೆಹೋವನಿಗೆ ಕೋಪವನ್ನು ಹುಟ್ಟಿಸಿದಿರಿ. ಆಗ ಆತನು ಬಲು ಸಿಟ್ಟುಗೊಂಡು ನಿಮ್ಮನ್ನು ಧ್ವಂಸ ಮಾಡಬೇಕೆಂದಿದ್ದನು.",
			"9": "<sup>9</sup> ಯೆಹೋವನು ನಿಮ್ಮ ಸಂಗಡ ಮಾಡಿದ ನಿಬಂಧನೆಯನ್ನು ಬರೆದ ಆ ಕಲ್ಲಿನ ಹಲಿಗೆಗಳನ್ನು ತೆಗೆದುಕೊಳ್ಳುವುದಕ್ಕೆ ನಾನು ಬೆಟ್ಟವನ್ನು ಹತ್ತಿದ್ದಾಗ ನಾನು ಅನ್ನ ಪಾನಗಳನ್ನು ಬಿಟ್ಟು ಹಗಲಿರುಳು ನಲ್ವತ್ತು ದಿನ ಆ ಬೆಟ್ಟದಲ್ಲಿದ್ದೆನು.",
			"10": "<sup>10</sup> ಆಗ ಯೆಹೋವನು ತನ್ನ ಕೈಯಿಂದಲೇ ಬರೆದ ಆ ಎರಡು ಕಲ್ಲಿನ ಹಲಿಗೆಗಳನ್ನು ನನ್ನ ವಶಕ್ಕೆ ಕೊಟ್ಟನು. ನೀವು ಸಭೆ ಕೂಡಿದ ದಿನದಲ್ಲಿ ಯೆಹೋವನು ಆ ಬೆಟ್ಟದ ಮೇಲೆ ಅಗ್ನಿಜ್ವಾಲೆಯೊಳಗಿಂದ ನಿಮ್ಮ ಸಂಗಡ ಹೇಳಿದ ಮಾತುಗಳು ಆ ಹಲಿಗೆಗಳಲ್ಲಿದ್ದವು.",
			"11": "<sup>11</sup> ಆ ನಲ್ವತ್ತು ದಿನಗಳು ಕಳೆದ ಮೇಲೆ ಯೆಹೋವನು ತನ್ನ ಆಜ್ಞೆಗಳಿರುವ ಆ ಎರಡು ಕಲ್ಲಿನ ಹಲಿಗೆಗಳನ್ನು ನನಗೆ ಕೊಟ್ಟು, “ನೀನು ಬೇಗ ಇಲ್ಲಿಂದ ಹೋಗು;",
			"12": "<sup>12</sup> ನೀನು ಐಗುಪ್ತ ದೇಶದಿಂದ ಕರೆದುಕೊಂಡು ಬಂದ ನಿನ್ನ ಜನರು ಕೆಟ್ಟುಹೋದರು. ನಾನು ಅವರಿಗೆ ಆಜ್ಞಾಪಿಸಿದ ಮಾರ್ಗವನ್ನು ಅವರು ಬೇಗನೆ ಬಿಟ್ಟುಹೋಗಿ ತಮಗೆ ಬೇಕಾದ ಲೋಹದ ವಿಗ್ರಹವನ್ನು ಮಾಡಿಸಿಕೊಂಡಿದ್ದಾರೆ” ಎಂದು ಹೇಳಿದನು.",
			"13": "<sup>13</sup> ಅದಲ್ಲದೆ ಆತನು ನನಗೆ, “ಈ ಜನರ ಸ್ವಭಾವವನ್ನು ನಾನು ನೋಡುತ್ತಾ ಬಂದಿದ್ದೇನೆ; ಇವರು ನನ್ನ ಆಜ್ಞೆಗೆ ಅವಿಧೇಯರಾದವರು.",
			"14": "<sup>14</sup> ಬಿಡು, ನಾನು ಅವರನ್ನು ನಾಶಮಾಡಿ, ಅವರ ಹೆಸರನ್ನು ಭೂಮಿಯ ಮೇಲೆ ಉಳಿಯದಂತೆ ಮಾಡುವೆನು. ತರುವಾಯ ಅವರಿಗಿಂತಲೂ ಮಹಾ ಬಲಿಷ್ಠ ಜನಾಂಗವು ನಿನ್ನಿಂದುಂಟಾಗುವಂತೆ ಮಾಡುವೆನು” ಎಂದು ಹೇಳಿದನು.",
			"15": "<sup>15</sup> ಆಗ ನಾನು ಆ ಎರಡು ಆಜ್ಞಾಶಾಸನಗಳನ್ನು ನನ್ನ ಎರಡು ಕೈಗಳಲ್ಲಿ ಹಿಡಿದುಕೊಂಡು ಬೆಂಕಿಯಿಂದ ಉರಿಯುವ ಆ ಬೆಟ್ಟದಿಂದ ಇಳಿದು ಬಂದು ನೋಡಲಾಗಿ",
			"16": "<sup>16</sup> ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ದ್ರೋಹಿಗಳಾಗಿ ಲೋಹದ ಬಸವನನ್ನು ಮಾಡಿಸಿಕೊಂಡು, ಯೆಹೋವನು ನಿಮಗೆ ಆಜ್ಞಾಪಿಸಿದ್ದ ಮಾರ್ಗವನ್ನು ಬೇಗನೆ ಬಿಟ್ಟು ಹೋದದ್ದು ನನಗೆ ಕಂಡುಬಂತು.",
			"17": "<sup>17</sup> ಅದನ್ನು ನೋಡಿ ನಾನು ಕೈಯಲ್ಲಿದ್ದ ಆ ಎರಡು ಶಿಲಾಶಾಸನಗಳನ್ನು ನಿಮಗೆದುರಾಗಿಯೇ ನೆಲಕ್ಕೆ ಹಾಕಿ ಒಡೆದುಬಿಟ್ಟೆನು.",
			"18": "<sup>18</sup> ನೀವು ಹೀಗೆ ಅಪರಾಧಮಾಡಿ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ಕೆಟ್ಟದ್ದನ್ನು ನಡಿಸಿ ಆತನನ್ನು ಕೋಪಗೊಳಿಸಿದ ಕಾರಣ ನಾನು ಮೊದಲಿನಂತೆ ಅನ್ನ ಪಾನಗಳನ್ನು ಬಿಟ್ಟು ನಲ್ವತ್ತು ದಿನಗಳು ಹಗಲಿರುಳು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲೇ ಬಿದ್ದಿದ್ದೆನು.",
			"19": "<sup>19</sup> ಯೆಹೋವನು ನಿಮ್ಮ ಮೇಲೆ ಬಹುಕೋಪಗೊಂಡು ನಿಮ್ಮನ್ನು ನಾಶಮಾಡಬೇಕೆಂದು ಆಲೋಚಿಸಿದ್ದರಿಂದ ನಾನು ಹೆದರಿಕೊಂಡೆನು. ಆದರೆ ಆ ಸಮಯದಲ್ಲಿಯೂ ಆತನು ನನ್ನ ಮನವಿಯನ್ನು ಕೇಳಿದನು.",
			"20": "<sup>20</sup> ಅದಲ್ಲದೆ ಯೆಹೋವನು ಆರೋನನ ಮೇಲೆ ಬಹಳ ಸಿಟ್ಟುಗೊಂಡು ಅವನನ್ನೂ ನಾಶಮಾಡಬೇಕೆಂದಿದ್ದರಿಂದ ನಾನು ಅವನಿಗೋಸ್ಕರವಾಗಿಯೂ ಆ ಸಮಯದದಲ್ಲಿ ಪ್ರಾರ್ಥಿಸಿದೆನು.",
			"21": "<sup>21</sup> ಮತ್ತು ನೀವು ಯಾವುದರ ಮೂಲಕ ಪಾಪವನ್ನು ಮಾಡಿದ್ದಿರೋ ಆ ಬಸವನನ್ನು ನಾನು ತೆಗೆದುಕೊಂಡು ಬೆಂಕಿಯಿಂದ ಸುಟ್ಟು, ಒಡೆದು, ಅರೆದು ಧೂಳುಮಾಡಿ, ಬೆಟ್ಟದಿಂದ ಹರಿಯುವ ಹಳ್ಳದಲ್ಲಿ ಬೀಸಾಡಿಬಿಟ್ಟೆ.",
			"22": "<sup>22</sup> ತಬೆರಾ, ಮಸ್ಸಾ, ಕಿಬ್ರೋತ್ ಹತಾವಾ ಎಂಬ ಸ್ಥಳಗಳಲ್ಲಿಯೂ ನೀವು ಯೆಹೋವನಿಗೆ ಕೋಪವನ್ನು ಹುಟ್ಟಿಸಿದ್ದಿರಿ.",
			"23": "<sup>23</sup> ಅದಲ್ಲದೆ ಕಾದೇಶ್ ಬರ್ನೇಯದಲ್ಲಿ ಯೆಹೋವನು ನಿಮಗೆ, “ಬೆಟ್ಟದ ಸೀಮೆಯನ್ನು ಹತ್ತಿ ನಾನು ನಿಮಗೆ ಕೊಟ್ಟಿರುವ ದೇಶವನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳಿರಿ” ಎಂದು ಅಪ್ಪಣೆಕೊಟ್ಟಾಗಲೂ ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಆ ಆಜ್ಞೆಯನ್ನು ಧಿಕ್ಕರಿಸಿ, ಆತನನ್ನು ನಂಬದೆ, ಆತನ ಮಾತನ್ನು ಅಲಕ್ಷ್ಯ ಮಾಡಿದಿರಿ.",
			"24": "<sup>24</sup> ನನಗೆ ನಿಮ್ಮ ಪರಿಚಯವಾದಂದಿನಿಂದಲೂ ನೀವು ಯೆಹೋವನಿಗೆ ದ್ರೋಹಿಗಳಾಗಿ ನಡೆದುಕೊಂಡಿದ್ದೀರಿ.",
			"25": "<sup>25</sup> ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ನಾಶಮಾಡಬೇಕೆಂದಿದ್ದರಿಂದ ನಾನು ಆ ನಲ್ವತ್ತು ದಿನವೂ ಹಗಲಿರುಳು ಆತನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಬಿದ್ದಿದ್ದೆನು.",
			"26": "<sup>26</sup> ನಾನು ಆತನನ್ನು ಬೇಡಿಕೊಳ್ಳುತ್ತಾ, “ಕರ್ತನಾದ ಯೆಹೋವನೇ, ನೀನು ನಿನ್ನ ಮಹಿಮೆಯಿಂದ ರಕ್ಷಸಿ ನಿನ್ನ ಭುಜಬಲದಿಂದ ಐಗುಪ್ತ್ಯರಿಂದ ಬಿಡುಗಡೆಮಾಡಿದ ನಿನ್ನ ಸ್ವಕೀಯ ಜನರನ್ನು ನಾಶಮಾಡಬೇಡ.",
			"27": "<sup>27</sup> ಇವರ ಮೊಂಡುತನ, ದುಷ್ಟತ್ವ, ಪಾಪ ಇವುಗಳನ್ನು ಮನಸ್ಸಿಗೆ ತಗೆದುಕೊಳ್ಳದೆ ನಿನ್ನ ಸೇವಕರಾದ ಅಬ್ರಹಾಮ್, ಇಸಾಕ್ ಮತ್ತು ಯಾಕೋಬರನ್ನು ನೆನಪಿಗೆ ತಂದುಕೋ.",
			"28": "<sup>28</sup> ನೀನು ಯಾವ ದೇಶದಿಂದ ನಮ್ಮನ್ನು ಬರಮಾಡಿದಿಯೋ ಆ ದೇಶದವರು, ‘ಯೆಹೋವನು ಇಸ್ರಾಯೇಲರಿಗೆ ವಾಗ್ದಾನಮಾಡಿದ ದೇಶಕ್ಕೆ ಅವರನ್ನು ಸೇರಿಸಲಾರದೆ ಹಗೆಮಾಡಿ ಅರಣ್ಯದಲ್ಲಿ ಕೊಲ್ಲುವುದಕ್ಕೆ ಕರೆದುಕೊಂಡು ಹೋದನು’ ಎಂದು ಹೇಳುವುದಕ್ಕೆ ಆಸ್ಪದವಾದೀತು.",
			"29": "<sup>29</sup> ಇವರು ಮಹಾಶಕ್ತಿಯಿಂದಲೂ ಮತ್ತು ಭುಜಬಲದಿಂದಲೂ ನೀನು ಬಿಡಿಸಿದ ನಿನ್ನ ಸ್ವಕೀಯ ಜನರಲ್ಲವೇ” ಎಂದು ಬಿನ್ನೈಸಿದೆನು."
		},
		"10": {
			"1": "<sup>1</sup> ಆ ಕಾಲದಲ್ಲಿ ಯೆಹೋವನು ನನಗೆ, “ನೀನು ಮೊದಲಿನ ಕಲ್ಲಿನ ಹಲಿಗೆಗಳಂತೆ ಬೇರೆ ಎರಡು ಹಲಿಗೆಗಳನ್ನು ಸಿದ್ಧಪಡಿಸಿಕೊಂಡು, ಬೆಟ್ಟವನ್ನು ಹತ್ತಿ ನನ್ನ ಬಳಿಗೆ ಬಾ; ಮತ್ತು ಒಂದು ಮರದ ಮಂಜೂಷವನ್ನು ಮಾಡಿಸಿಕೊಳ್ಳಬೇಕು.",
			"2": "<sup>2</sup> ನೀನು ಒಡೆದುಬಿಟ್ಟ ಆ ಮೊದಲಿನ ಹಲಿಗೆಗಳಲ್ಲಿ ಬರೆದಿದ್ದ ಮಾತುಗಳನ್ನು ನಾನು ಈ ಹಲಿಗೆಗಳ ಮೇಲೆ ಬರೆಯುವೆನು. ತರುವಾಯ ನೀನು ಅವುಗಳನ್ನು ಆ ಮಂಜೂಷದಲ್ಲಿ ಇಡಬೇಕು” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"3": "<sup>3</sup> ಆದಕಾರಣ ನಾನು ಜಾಲೀಮರದಿಂದ ಮಂಜೂಷವನ್ನು ಮಾಡಿಸಿ ಮೊದಲಿನ ಕಲ್ಲಿನ ಹಲಿಗೆಗಳಂತೆ ಬೇರೆ ಎರಡು ಹಲಿಗೆಗಳನ್ನು ಸಿದ್ಧಪಡಿಸಿ, ಆ ಎರಡು ಹಲಿಗೆಗಳನ್ನು ಕೈಯಲ್ಲಿ ಹಿಡಿದುಕೊಂಡು ಬೆಟ್ಟವನ್ನು ಹತ್ತಿದೆನು.",
			"4": "<sup>4</sup> ಯೆಹೋವನು ಬೆಟ್ಟದ ಮೇಲೆ ಅಗ್ನಿಜ್ವಾಲೆಯೊಳಗಿಂದ ನೀವು ಸಭೆ ಕೂಡಿದ ದಿನದಲ್ಲಿ ನಿಮ್ಮ ಸಂಗಡ ಹೇಳಿದ ಮಾತುಗಳನ್ನು, ಅಂದರೆ ಆ ಹತ್ತು ಕಟ್ಟಳೆಗಳನ್ನು ಆತನು ಮೊದಲಿನಂತೆಯೇ ಆ ಹಲಿಗೆಗಳ ಮೇಲೆ ಬರೆದನು.",
			"5": "<sup>5</sup> ಯೆಹೋವನು ಆ ಹಲಿಗೆಗಳನ್ನು ನನ್ನ ವಶಕ್ಕೆ ಕೊಟ್ಟ ನಂತರ ನಾನು ಬೆಟ್ಟದಿಂದ ಇಳಿದು ಬಂದು ನನ್ನಿಂದ ಸಿದ್ಧವಾಗಿದ್ದ ಮಂಜೂಷದಲ್ಲಿ ಆತನ ಅಪ್ಪಣೆಯ ಮೇರೆಗೆ ಅವುಗಳನ್ನು ಇಟ್ಟೆನು; ಅವು ಇಂದಿನವರೆಗೂ ಅದರಲ್ಲೇ ಇವೆ.",
			"6": "<sup>6</sup> (ಇಸ್ರಾಯೇಲರು ಯಾಕಾನ್ಯರ ಬಾವಿಗಳ ಬಳಿಯಿಂದ ಪ್ರಯಾಣಮಾಡಿ ಮೋಸೇರಕ್ಕೆ ಬಂದರು. ಅಲ್ಲಿ ಆರೋನನು ಸಾಯಲಾಗಿ ಅವನನ್ನು ಅಲ್ಲಿಯೇ ಸಮಾಧಿ ಮಾಡಿದರು. ಅವನ ಮಗನಾದ ಎಲ್ಲಾಜಾರನು ಅವನಿಗೆ ಬದಲಾಗಿ ಮಹಾಯಾಜಕನಾದನು.",
			"7": "<sup>7</sup> ಅಲ್ಲಿಂದ ಅವರು ಗುದ್ಗೋದಕ್ಕೂ, ಗುದ್ಗೋದದಿಂದ ನೀರಿನ ಹಳ್ಳಗಳುಳ್ಳ ಯೊಟ್ಬಾತಕ್ಕೂ ಪ್ರಯಾಣ ಮಾಡಿದರು.)",
			"8": "<sup>8</sup> ಆ ಕಾಲದಲ್ಲಿ ಯೆಹೋವನು ಲೇವಿ ಕುಲದವರನ್ನು ಪ್ರತ್ಯೇಕಿಸಿ ತನ್ನ ಒಡಂಬಡಿಕೆಯ ಮಂಜೂಷವನ್ನು ಹೊರುವುದಕ್ಕೂ, ತನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸೇವೆ ಮಾಡುವುದಕ್ಕೂ ಮತ್ತು ತನ್ನ ಹೆಸರಿನಿಂದ ಜನರನ್ನು ಆಶೀರ್ವದಿಸುವುದಕ್ಕೂ ಅವರನ್ನು ನೇಮಿಸಿದನು. ಅವರು ಇಂದಿನ ವರೆಗೂ ಆ ಕೆಲಸವನ್ನು ನಡಿಸುತ್ತಾರೆ.",
			"9": "<sup>9</sup> ಆದುದರಿಂದ ಉಳಿದ ಇಸ್ರಾಯೇಲರಿಗೆ ದೊರಕಿದಂತೆ ಲೇವಿಯರಿಗೆ ಸ್ವಂತವಾದ ಭೂಸ್ಥಿತಿಯು ದೊರೆಯಲಿಲ್ಲ. ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಅವರಿಗೆ ಹೇಳಿದಂತೆ ಯೆಹೋವನೇ ಅವರಿಗೆ ಸ್ವಾಸ್ತ್ಯ.",
			"10": "<sup>10</sup> ನಾನು ಮೊದಲಿನಂತೆ ಹಗಲಿರುಳು ನಲ್ವತ್ತು ದಿನವೂ ಬೆಟ್ಟದ ಮೇಲೆ ಇರಲಾಗಿ ಯೆಹೋವನು ಆ ಕಾಲದಲ್ಲಿಯೂ ನನ್ನ ಪ್ರಾರ್ಥನೆಯನ್ನು ಕೇಳಿ ನಿಮ್ಮನ್ನು ನಾಶಮಾಡಬೇಕೆಂಬ ಆಲೋಚನೆಯನ್ನು ಬಿಟ್ಟುಬಿಟ್ಟನು.",
			"11": "<sup>11</sup> ತರುವಾಯ ಯೆಹೋವನು ನನಗೆ, “ನೀನು ಈ ಜನರ ಮುಂದೆ ಹೋಗು; ನಾನು ಇವರ ಪಿತೃಗಳಿಗೆ ಪ್ರಮಾಣಮಾಡಿಕೊಟ್ಟ ದೇಶವನ್ನು ಇವರು ಸೇರಿ ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳಲಿ” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"12": "<sup>12</sup> ಆದುದರಿಂದ ಇಸ್ರಾಯೇಲರೇ, ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿ, ಎಲ್ಲಾ ವಿಷಯಗಳಲ್ಲಿಯೂ ಆತನು ಹೇಳುವ ಮಾರ್ಗದಲ್ಲೇ ನಡೆಯುತ್ತಾ, ಆತನನ್ನು ಪ್ರೀತಿಸುತ್ತಾ, ಸಂಪೂರ್ಣವಾದ ಹೃದಯದಿಂದಲೂ, ಮನಸ್ಸಿನಿಂದಲೂ ಸೇವೆ ಮಾಡುತ್ತಾ,",
			"13": "<sup>13</sup> ನಾನು ನಿಮ್ಮ ಮೇಲಿಗಾಗಿ ಈಗ ಬೋಧಿಸುವ ಆತನ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ಅನುಸರಿಸುತ್ತಾ ಇರುವುದನ್ನೇ ಹೊರತು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು",
			"14": "<sup>14</sup> ನಿಮ್ಮಿಂದ ಬೇರೇನು ಕೇಳಿಕೊಳ್ಳುತ್ತಾನೆ? ಆಲೋಚಿಸಿರಿ; ಉನ್ನತವಾದ ಆಕಾಶಮಂಡಲವೂ ಮತ್ತು ಭೂಮಿಯೂ, ಅದರಲ್ಲಿರುವ ಎಲ್ಲವೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನವೇ.",
			"15": "<sup>15</sup> ಆದರೂ ಆತನು ನಿಮ್ಮ ಪಿತೃಗಳಲ್ಲಿ ಇಷ್ಟವುಳ್ಳವನಾಗಿ ಅವರನ್ನು ಪ್ರೀತಿಸಿದ್ದರಿಂದ ಈಗ ನಿಮ್ಮ ಅನುಭವಕ್ಕೆ ಬಂದಂತೆ ಅವರ ತರುವಾಯ ಅವರ ಸಂತತಿಯವರಾದ ನಿಮ್ಮನ್ನೇ ಎಲ್ಲಾ ಜನಾಂಗಗಳೊಳಗಿಂದ ಆರಿಸಿಕೊಂಡನು.",
			"16": "<sup>16</sup> ಆದುದರಿಂದ ನೀವು ಆಜ್ಞೆಗೆ ಮಣಿಯದ ನಿಮ್ಮ ದುಷ್ಟಸ್ವಭಾವವನ್ನು ಬಿಟ್ಟು ನಿಮ್ಮ ಹೃದಯದಲ್ಲೇ ಸುನ್ನತಿಮಾಡಿಕೊಳ್ಳಿರಿ.",
			"17": "<sup>17</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ದೇವಾಧಿದೇವನಾಗಿಯೂ, ಕರ್ತರ ಕರ್ತನಾಗಿಯೂ ಇದ್ದಾನೆ. ಆತನು ಪರಮದೇವರೂ, ಪರಾಕ್ರಮಿಯೂ ಮತ್ತು ಭಯಂಕರನೂ ಆಗಿದ್ದಾನೆ. ಆತನು ಪಕ್ಷಪಾತ ಮಾಡುವವನಲ್ಲ ಹಾಗೂ ಲಂಚತೆಗೆದುಕೊಳ್ಳುವವನಲ್ಲ.",
			"18": "<sup>18</sup> ಆತನು ಅನಾಥರ ಮತ್ತು ವಿಧವೆಯರ ನ್ಯಾಯವನ್ನು ಸ್ಥಾಪಿಸುತ್ತಾನೆ. ಪರದೇಶಿಗಳಾದವರಲ್ಲಿ ಪ್ರೀತಿಯಿಟ್ಟು ಅವರಿಗೆ ಅನ್ನವಸ್ತ್ರಗಳನ್ನು ಕೊಡುತ್ತಾನೆ.",
			"19": "<sup>19</sup> ಐಗುಪ್ತದೇಶದಲ್ಲಿ ನೀವೇ ಪರದೇಶಿಗಳಾಗಿದ್ದದ್ದನ್ನು ಜ್ಞಾಪಿಸಿಕೊಂಡು ಪರದೇಶದವರಲ್ಲಿ ಪ್ರೀತಿಯಿಡಿರಿ.",
			"20": "<sup>20</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿ, ಆತನನ್ನೇ ಸೇವಿಸಬೇಕು; ಆತನನ್ನು ಹೊಂದಿಕೊಂಡು, ಆತನ ಹೆಸರಿನ ಮೇಲೆಯೇ ಪ್ರಮಾಣಮಾಡಬೇಕು.",
			"21": "<sup>21</sup> ಆತನೊಬ್ಬನೇ ನಿಮ್ಮ ಸ್ತುತಿ ಸ್ತೋತ್ರಕ್ಕೆ ಪಾತ್ರನು; ಆತನು ನಿಮ್ಮ ದೇವರು; ನೀವು ನೋಡಿದ ಆ ಅದ್ಭುತವಾದ ಮಹತ್ಕಾರ್ಯಗಳನ್ನು ನಿಮಗೋಸ್ಕರ ನಡಿಸಿದವನು ಆತನೇ.",
			"22": "<sup>22</sup> ನಿಮ್ಮ ಪಿತೃಗಳಲ್ಲಿ ಎಪ್ಪತ್ತು ಜನರು ಮಾತ್ರ ಐಗುಪ್ತದೇಶಕ್ಕೆ ಹೋದರು; ಈಗ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಆಕಾಶದ ನಕ್ಷತ್ರಗಳಷ್ಟು ಅಸಂಖ್ಯರಾಗಿರುವಂತೆ ಮಾಡಿದ್ದಾನೆ."
		},
		"11": {
			"1": "<sup>1</sup> ಆದುದರಿಂದ ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಪ್ರೀತಿಸಿ, ಆತನ ನಿಯಮಗಳನ್ನು ಕೈಕೊಂಡು, ಆತನ ಆಜ್ಞಾವಿಧಿನಿಯಮಗಳನ್ನು ಯಾವಾಗಲೂ ಅನುಸರಿಸಬೇಕು.",
			"2": "<sup>2</sup> ಈ ಕಾರ್ಯಗಳನ್ನು ನೋಡದೆಯೂ ಮತ್ತು ತಿಳಿಯದೆಯೂ ಇರುವ ನಿಮ್ಮ ಮಕ್ಕಳಿಗೆ ನಾನು ಈ ಮಾತನ್ನು ಹೇಳುವುದಿಲ್ಲ. ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಶಿಕ್ಷಣ ಕ್ರಮವನ್ನೂ, ಮಹಿಮೆಯನ್ನೂ, ಭುಜಬಲವನ್ನೂ ಮತ್ತು ಶಿಕ್ಷಾಹಸ್ತದಿಂದ ನಡಿಸಿದ ಸೂಚಕಕಾರ್ಯಗಳನ್ನೂ ಮತ್ತು ಮಹತ್ಕಾರ್ಯಗಳನ್ನೂ ನೋಡಿದಿರಿ.",
			"3": "<sup>3</sup> ಆತನು ಐಗುಪ್ತದೇಶದಲ್ಲಿ ಫರೋಹನನ್ನೂ ಹಾಗೂ ಅವನ ದೇಶವನ್ನೂ ಶಿಕ್ಷಿಸುವುದಕ್ಕಾಗಿ",
			"4": "<sup>4</sup> ಐಗುಪ್ತ್ಯರ ಸೈನ್ಯ ಅಂದರೆ, ಅವರ ರಥಾಶ್ವಬಲಗಳು ನಿಮ್ಮನ್ನು ಹಿಂದಟ್ಟಿ ಬಂದಾಗ ಆತನು ಕೆಂಪು ಸಮುದ್ರದ ನೀರನ್ನು ಅವರ ಮೇಲೆ ಬರಮಾಡಿ ಸಂಪೂರ್ಣವಾಗಿ ನಾಶಮಾಡಿದ್ದನ್ನೂ,",
			"5": "<sup>5</sup> ನೀವು ಈ ಸ್ಥಳಕ್ಕೆ ಸೇರುವ ತನಕ ಆತನು ನಿಮಗೋಸ್ಕರ ಅರಣ್ಯದಲ್ಲಿ ಉಪಕಾರಮಾಡಿದ್ದನ್ನೂ,",
			"6": "<sup>6</sup> ರೂಬೇನನ ಮೊಮ್ಮಕ್ಕಳೂ, ಎಲೀಯಾಬನ ಮಕ್ಕಳೂ ಆದ ದಾತಾನ್ ಮತ್ತು ಅಬೀರಾಮರು ತಿರುಗಿ ಬಿದ್ದಾಗ ಭೂಮಿಯು ಬಾಯ್ದೆರೆದು ಅವರನ್ನೂ, ಅವರ ಮನೆಯವರನ್ನೂ, ಅವರ ಡೇರೆಗಳನ್ನೂ ಮತ್ತು ಅವರಿಗೆ ಸೇರಿದ ಎಲ್ಲಾ ಪ್ರಾಣಿಗಳನ್ನೂ ಇಸ್ರಾಯೇಲರ ನಡುವೆ ನುಂಗಿಬಿಟ್ಟದ್ದನ್ನೂ ಈಗ ಜ್ಞಾಪಕಮಾಡಿಕೊಳ್ಳಿರಿ.",
			"7": "<sup>7</sup> ಯೆಹೋವನು ನಡಿಸಿದ ಆ ವಿಶೇಷ ಕಾರ್ಯಗಳನ್ನು ಪ್ರತ್ಯಕ್ಷವಾಗಿ ನೋಡಿದವರಾದ ನಿಮಗೇ ಹೇಳುತ್ತಿದ್ದೇನೆ.",
			"8": "<sup>8</sup> ಆದುದರಿಂದಲೇ ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ಈ ಧರ್ಮೋಪದೇಶವನ್ನೆಲ್ಲಾ ನೀವು ಅನುಸರಿಸಬೇಕು. ಆಗ ನೀವು ಬಲವುಳ್ಳವರಾಗಿ ಈ ನದಿಯನ್ನು ದಾಟಿ ಆಚೆಯಿರುವ",
			"9": "<sup>9</sup> ದೇಶವನ್ನು ಸೇರಿ ಸ್ವಾಧೀನ ಮಾಡಿಕೊಳ್ಳುವಿರಿ. ಯೆಹೋವನು ನಿಮ್ಮ ಪಿತೃಗಳಿಗೂ ಅವರ ಸಂತತಿಗೂ ಕೊಡುತ್ತೇನೆಂದು ಪ್ರಮಾಣಮಾಡಿದ ಆ ದೇಶದಲ್ಲಿ ನೀವು ಬಹುಕಾಲ ಬದುಕುವಿರಿ. ಅದು ಹಾಲೂ ಮತ್ತು ಜೇನೂ ಹರಿಯುವ ದೇಶ.",
			"10": "<sup>10</sup> ನೀವು ಸ್ವತಂತ್ರಿಸಿಕೊಳ್ಳುವುದಕ್ಕೆ ಹೋಗುವ ದೇಶವು ಬಿಟ್ಟುಬಂದ ಐಗುಪ್ತದೇಶದ ಹಾಗಲ್ಲ. ಐಗುಪ್ತದೇಶದಲ್ಲಿ ನೀವು ಹೊಲಗದ್ದೆಗಳಲ್ಲಿ ಬೀಜ ಬಿತ್ತಿದ ಮೇಲೆ ಕಾಯಿಪಲ್ಯಗಳ ತೋಟವನ್ನು ವ್ಯವಸಾಯ ಮಾಡುವ ರೀತಿಯಲ್ಲಿ ಏತವನ್ನು ತುಳಿದು ನೀರು ಕಟ್ಟುತ್ತಿದ್ದಿರಿ.",
			"11": "<sup>11</sup> ಆದರೆ ನೀವು ಸ್ವಾಧೀನ ಮಾಡಿಕೊಳ್ಳುವುದಕ್ಕೆ ಹೋಗುವ ದೇಶವೋ ಹಳ್ಳದಿಣ್ಣೆಗಳ ದೇಶ; ಆಕಾಶದಿಂದ ಮಳೆಬಿದ್ದ ಪ್ರಕಾರವೇ ಅದಕ್ಕೆ ನೀರು ದೊರೆಯುವುದು.",
			"12": "<sup>12</sup> ಅದು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಪರಾಂಬರಿಸುವ ದೇಶ; ವರ್ಷದ ಪ್ರಾರಂಭ ಮೊದಲುಗೊಂಡು ಕೊನೆಯ ವರೆಗೂ ಆತನು ಅದನ್ನು ಸದಾ ಕಟಾಕ್ಷಿಸುವನು.",
			"13": "<sup>13</sup> ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ಆಜ್ಞೆಗಳಿಗೆ ನೀವು ಲಕ್ಷ್ಯಕೊಟ್ಟು, ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಸಂಪೂರ್ಣ ಹೃದಯದಿಂದಲೂ, ಸಂಪೂರ್ಣ ಮನಸ್ಸಿನಿಂದಲೂ ಪ್ರೀತಿಸಿ ಆತನನ್ನು ಸೇವಿಸಿದರೆ,",
			"14": "<sup>14</sup> ನಿಮಗೆ ಗೋಧಿ, ದ್ರಾಕ್ಷೇ, ಎಣ್ಣೇಕಾಯಿ ಇವುಗಳ ಬೆಳೆ ಚೆನ್ನಾಗಿ ಉಂಟಾಗುವಂತೆ ಆತನು ನಿಮ್ಮ ಭೂಮಿಗೆ ಬೇಕಾದ ಮುಂಗಾರು ಮತ್ತು ಹಿಂಗಾರು ಮಳೆಯನ್ನು ಸರಿಯಾಗಿ ಕೊಡುವನು.",
			"15": "<sup>15</sup> ಅಡವಿಯಲ್ಲಿ ದನಕರುಗಳಿಗೆ ಬೇಕಾದಷ್ಟು ಹುಲ್ಲನ್ನು ಕೊಡುವನು; ನೀವು ಆಹಾರದಿಂದ ತೃಪ್ತರಾಗಿರುವಿರಿ.",
			"16": "<sup>16</sup> ಆದರೆ ನೀವು ಎಚ್ಚರದಿಂದಿರಬೇಕು; ನಿಮ್ಮ ಹೃದಯವು ಮರುಳುಗೊಂಡು ಯೆಹೋವನು ಹೇಳಿದ ಮಾರ್ಗವನ್ನು ಬಿಟ್ಟು, ಇತರ ದೇವರುಗಳನ್ನು ಅವಲಂಭಿಸಿ ಪೂಜಿಸಿದರೆ,",
			"17": "<sup>17</sup> ಆತನು ನಿಮ್ಮ ಮೇಲೆ ಸಿಟ್ಟುಗೊಂಡು ಮಳೆಬಾರದಂತೆ ಆಕಾಶವನ್ನು ಮುಚ್ಚಿಬಿಟ್ಟಾನು; ಆಗ ಭೂಮಿಯಲ್ಲಿ ಬೆಳೆಯಾಗದೆ ಯೆಹೋವನು ನಿಮಗೆ ಕೊಡುವ ಆ ಉತ್ತಮ ದೇಶದಲ್ಲಿ ನೀವು ಉಳಿಯದೆ ಬೇಗ ನಾಶವಾಗಿ ಹೋಗುವಿರಿ.",
			"18": "<sup>18</sup> ಆದುದರಿಂದ ನೀವು ಈ ನನ್ನ ಮಾತುಗಳನ್ನು ನಿಮ್ಮ ಹೃದಯದಲ್ಲಿಯೂ, ಮನಸ್ಸಿನಲ್ಲಿಯೂ ಇಟ್ಟುಕೊಳ್ಳಬೇಕು. ಇವುಗಳನ್ನು ಜ್ಞಾಪಕಾರ್ಥವಾಗಿ ಕೈಗೆ ಕಟ್ಟಿಕೊಳ್ಳಬೇಕು. ಇವು ನಿಮ್ಮ ಹಣೆಗೆ ಕಟ್ಟಿಕೊಳ್ಳುವ ಜ್ಞಾಪಕದ ಪಟ್ಟಿಯಂತಿರಬೇಕು.",
			"19": "<sup>19</sup> ನೀವು ಮನೆಯಲ್ಲಿರುವಾಗಲೂ, ಪ್ರಯಾಣದಲ್ಲಿರುವಾಗಲೂ, ಮಲಗುವಾಗಲೂ ಮತ್ತು ಏಳುವಾಗಲೂ ಇವುಗಳ ವಿಷಯದಲ್ಲಿ ಮಾತನಾಡುತ್ತಾ ನಿಮ್ಮ ಮಕ್ಕಳಿಗೆ ಇವುಗಳನ್ನು ಅಭ್ಯಾಸಮಾಡಿಸಬೇಕು.",
			"20": "<sup>20</sup> ನಿಮ್ಮ ಮನೇಬಾಗಿಲಿನ ನಿಲುವುಪಟ್ಟಿಗಳಲ್ಲಿಯೂ ಮತ್ತು ಹೆಬ್ಬಾಗಿಲುಗಳ ಮೇಲೆಯೂ ಇವುಗಳನ್ನು ಬರೆಯಬೇಕು.",
			"21": "<sup>21</sup> ಹೀಗೆ ಮಾಡಿದರೆ ಭೂಮಿಯ ಮೇಲೆ ಆಕಾಶವು ಎಷ್ಟು ಕಾಲ ಇರುವುದೋ, ಅಷ್ಟು ಕಾಲದ ವರೆಗೆ ನೀವೂ ಮತ್ತು ನಿಮ್ಮ ಸಂತತಿಯವರೂ ಯೆಹೋವನು ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ಪ್ರಮಾಣಪೂರ್ವಕವಾಗಿ ವಾಗ್ದಾನಮಾಡಿದ ಆ ದೇಶದಲ್ಲಿ ಬಾಳುವಿರಿ.",
			"22": "<sup>22</sup> ನಾನು ನಿಮಗೆ ಬೋಧಿಸುವ ಈ ಧರ್ಮೋಪದೇಶವನ್ನೆಲ್ಲಾ ನೀವು ಅನುಸರಿಸುವವರಾಗಿ, ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಪ್ರೀತಿಸಿ, ಎಲ್ಲಾ ವಿಷಯಗಳಲ್ಲಿಯೂ ಆತನು ಹೇಳುವ ಮಾರ್ಗದಲ್ಲೇ ನಡೆದು, ಆತನನ್ನೇ ಹೊಂದಿಕೊಂಡಿರಬೇಕು.",
			"23": "<sup>23</sup> ಆಗ ಆತನು ಆ ಜನಾಂಗಗಳನ್ನೆಲ್ಲಾ ನಿಮ್ಮ ಎದುರಿನಿಂದ ಹೊರಡಿಸುವನು. ನಿಮಗಿಂತಲೂ ಮಹಾಬಲಿಷ್ಠ ಜನಾಂಗಗಳ ದೇಶವನ್ನು ನೀವು ಸ್ವಾಧೀನ ಮಾಡಿಕೊಳ್ಳುವಿರಿ.",
			"24": "<sup>24</sup> ನೀವು ಹೆಜ್ಜೆಯಿಡುವ ಎಲ್ಲಾ ಸ್ಥಳಗಳು ನಿಮ್ಮದಾಗುವವು. ಅರಣ್ಯ ಮೊದಲುಗೊಂಡು ಲೆಬನೋನ್ ಪರ್ವತದ ವರೆಗೂ ಮತ್ತು ಯೂಫ್ರೇಟೀಸ್ ನದಿಯಿಂದ ಪಶ್ಚಿಮ ಸಮುದ್ರದವರೆಗೂ ನಿಮ್ಮ ಸೀಮೆ ವ್ಯಾಪಿಸುವುದು.",
			"25": "<sup>25</sup> ಯಾರೂ ನಿಮ್ಮ ಮುಂದೆ ನಿಲ್ಲುವುದಿಲ್ಲ. ತಾನು ನಿಮಗೆ ಹೇಳಿದಂತೆ ನೀವು ಕಾಲಿಡುವ ಎಲ್ಲಾ ಪ್ರದೇಶಗಳ ಜನಗಳಿಗೂ ನಿಮ್ಮಿಂದ ದಿಗಿಲೂ ಮತ್ತು ಹೆದರಿಕೆಯೂ ಉಂಟಾಗುವಂತೆ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಮಾಡುವನು.",
			"26": "<sup>26</sup> ಇಗೋ ನೋಡಿರಿ, ಈ ಹೊತ್ತು ನಾನು ಆಶೀರ್ವಾದವನ್ನೂ ಮತ್ತು ಶಾಪವನ್ನೂ ನಿಮ್ಮ ಮುಂದೆ ಇಡುತ್ತಾ ಇದ್ದೇನೆ.",
			"27": "<sup>27</sup> ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಆಜ್ಞೆಗಳಿಗೆ ನೀವು ವಿಧೇಯರಾಗಿ ನಡೆದರೆ ಆಶೀರ್ವಾದವೂ,",
			"28": "<sup>28</sup> ಈ ಆಜ್ಞೆಗಳಿಗೆ ಅವಿಧೇಯರಾಗಿ, ನಾನು ಈಗ ಬೋಧಿಸುವ ಮಾರ್ಗವನ್ನು ಬಿಟ್ಟು ನಿಮಗೆ ಗೊತ್ತಿಲ್ಲದ ಇತರ ದೇವರುಗಳನ್ನು ಅವಲಂಬಿಸಿದರೆ ಶಾಪವೂ ನಿಮಗುಂಟಾಗುವವು.",
			"29": "<sup>29</sup> ನೀವು ಸ್ವಾಧೀನ ಮಾಡಿಕೊಳ್ಳುವುದಕ್ಕೆ ಹೋಗುವ ಆ ದೇಶಕ್ಕೆ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಸೇರಿಸಿದ ನಂತರ ನೀವು ಗೆರಿಜ್ಜೀಮ್ ಬೆಟ್ಟದ ಮೇಲೆ ಆಶೀರ್ವಾದವನ್ನೂ ಮತ್ತು ಏಬಾಲ್ ಬೆಟ್ಟದ ಮೇಲೆ ಶಾಪವನ್ನೂ ಪ್ರಕಟಿಸಬೇಕು.",
			"30": "<sup>30</sup> ಆ ಬೆಟ್ಟಗಳು ಯೊರ್ದನ್ ನದಿಯ ಆಚೆ, ಪಡುವಣ ದಾರಿಯ ಪಶ್ಚಿಮದಲ್ಲಿ ಕಾನಾನ್ಯರು ವಾಸಿಸುವ ಅರಾಬಾ ಪ್ರದೇಶದಲ್ಲಿ, ಗಿಲ್ಗಾಲಿಗೆ ಎದುರಾಗಿ ಮೋರೆ ಎಂಬ ವೃಕ್ಷದ ಬಳಿಯಲ್ಲಿ ಇವೆ.",
			"31": "<sup>31</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಕೊಡುವ ದೇಶವನ್ನು ಸ್ವಾಧೀನ ಮಾಡಿಕೊಳ್ಳುವುದಕ್ಕೆ ನೀವು ಯೊರ್ದನ್ ನದಿಯನ್ನು ದಾಟಬೇಕು. ನೀವು ಆ ದೇಶವನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡು, ಅದರಲ್ಲಿ ವಾಸಮಾಡುವಾಗ",
			"32": "<sup>32</sup> ನಾನು ಈಗ ನಿಮಗೆ ತಿಳಿಸುವ ಎಲ್ಲಾ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ಅನುರಿಸಿ ನಡೆಯಲೇಬೇಕು."
		},
		"12": {
			"1": "<sup>1</sup> ನಿಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಸ್ವದೇಶವಾಗುವುದಕ್ಕೆ ಕೊಡುವ ದೇಶದಲ್ಲಿ ನೀವು ಭೂಮಿಯ ಮೇಲೆ ವಾಸಿಸುವ ಎಲ್ಲಾ ದಿನಗಳಲ್ಲಿ ಅನುಸರಿಸಬೇಕಾದ ಆಜ್ಞಾವಿಧಿಗಳು ಇವೇ:",
			"2": "<sup>2</sup> ನೀವು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವ ದೇಶದ ಜನಾಂಗಗಳು ದೊಡ್ಡ ಬೆಟ್ಟಗಳ ಮೇಲೆಯೂ, ದಿಣ್ಣೆಗಳ ಮೇಲೆಯೂ ಹರಡಿಕೊಂಡು, ಮರಗಳ ಕೆಳಗೂ ತಮ್ಮ ದೇವರುಗಳನ್ನು ಆರಾಧಿಸುತ್ತಾರಷ್ಟೆ; ಆ ಸ್ಥಳಗಳನ್ನೆಲ್ಲಾ ನೀವು ತಪ್ಪದೆ ನಾಶಮಾಡಬೇಕು.",
			"3": "<sup>3</sup> ಅವರ ಬಲಿಪೀಠಗಳನ್ನು ಕೆಡವಿ, ಅದರ ಪವಿತ್ರವಾದ ಕಲ್ಲುಕಂಬಗಳನ್ನು ಒಡೆದು, ಅಶೇರವೆಂಬ ವಿಗ್ರಹಸ್ತಂಭಗಳನ್ನು ಸುಟ್ಟು, ಅವರ ದೇವತಾಪ್ರತಿಮೆಗಳನ್ನು ಕಡಿದುಬಿಟ್ಟು ಆ ದೇವರುಗಳ ಹೆಸರೇ ಉಳಿಯದಂತೆ ಮಾಡಬೇಕು.",
			"4": "<sup>4</sup> ಅವರು ಮಾಡಿದ ರೀತಿಯಲ್ಲಿ ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಪೂಜಿಸಬಾರದು.",
			"5": "<sup>5</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ತನ್ನ ಹೆಸರನ್ನು ಸ್ಥಾಪಿಸಿಕೊಳ್ಳುವುದಕ್ಕೆ ನಿಮ್ಮನ್ನು ಎಲ್ಲಾ ಕುಲಗಳೊಳಗಿಂದ ಆರಿಸಿಕೊಳ್ಳುವನು. ನೀವು ಆ ಸ್ಥಳದಿಂದ ಆತನು ಆರಿಸಿಕೊಳ್ಳುವ ಸ್ಥಳಕ್ಕೆ ಸೇರಿ ಬರಬೇಕು. ಅಲ್ಲೇ ಆತನು ವಾಸಿಸುವನು.",
			"6": "<sup>6</sup> ನೀವು ಸಮರ್ಪಿಸುವ ಸರ್ವಾಂಗಹೋಮ, ಯಜ್ಞಪಶುಗಳನ್ನೂ, ನಿಮ್ಮ ಬೆಳೆಯಲ್ಲಿ ದಶಮಾಂಶವನ್ನೂ, ಯಾಜಕರಿಗೋಸ್ಕರ ಪ್ರತ್ಯೇಕಿಸುವ ಪದಾರ್ಥಗಳನ್ನೂ, ಹರಕೆ ಕಾಣಿಕೆಗಳನ್ನೂ, ದನ ಮತ್ತು ಕುರಿಗಳ ಚೊಚ್ಚಲ ಮರಿಗಳನ್ನೂ ಆ ಸ್ಥಳಕ್ಕೆ ಮಾತ್ರ ತರಬೇಕು.",
			"7": "<sup>7</sup> ಅಲ್ಲಿಯೇ ಆತನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಊಟಮಾಡಿ, ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮ ಪ್ರಯತ್ನಗಳನ್ನು ಕೈಗೂಡುವಂತೆ ಮಾಡಿದ್ದಕ್ಕಾಗಿ ನೀವೂ ಮತ್ತು ನಿಮ್ಮ ಮನೆಗಳವರೂ ಸಂತೋಷದಿಂದಿರಬೇಕು.",
			"8": "<sup>8</sup> ಈಗ ಈ ಸ್ಥಳದಲ್ಲಿ ನಾವು ನಡೆಕೊಳ್ಳುವಂತೆ ಎಲ್ಲರೂ ತಮ್ಮ ತಮ್ಮ ಮನಸ್ಸಿಗೆ ತೋರಿದಂತೆ ನಡೆಯಬಾರದು.",
			"9": "<sup>9</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಕೊಡುವ ಸ್ವಾಸ್ತ್ಯಕ್ಕೆ ಸೇರಿ ನೀವು ಇನ್ನೂ ವಿಶ್ರಾಂತಿ ಹೊಂದಲಿಲ್ಲವಲ್ಲಾ.",
			"10": "<sup>10</sup> ಆದರೆ ನೀವು ಯೊರ್ದನ್ ನದಿಯನ್ನು ದಾಟಿ, ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಸ್ವಾಸ್ತ್ಯವಾಗಿ ಕೊಡುವ ಆ ದೇಶದಲ್ಲಿ ಮನೆಮಾಡಿಕೊಂಡಿರುವಾಗ, ನಿಮ್ಮ ಸುತ್ತಲೂ ಶತ್ರುಗಳು ಯಾರೂ ಇಲ್ಲದಂತೆ ಯೆಹೋವನು ಮಾಡಿದ್ದರಿಂದ ನೀವು ನಿರ್ಭಯವಾಗಿರುವ ಕಾಲದಲ್ಲಿ,",
			"11": "<sup>11</sup> ನಾನು ನಿಮಗೆ ಆಜ್ಞಾಪಿಸಿದ ಎಲ್ಲಾ ಕಾಣಿಕೆಗಳನ್ನು ಅಂದರೆ ಸರ್ವಾಂಗಹೋಮಗಳೇ ಮುಂತಾದ ಯಜ್ಞಪಶುಗಳನ್ನೂ, ಬೆಳೆಯ ದಶಮಾಂಶಗಳನ್ನೂ, ಯೆಹೋವನಿಗೋಸ್ಕರ ಪ್ರತ್ಯೇಕಿಸುವ ಪದಾರ್ಥಗಳನ್ನೂ, ಹರಕೆಮಾಡಿದ ವಿಶೇಷವಾದ ಕಾಣಿಕೆಗಳನ್ನೂ ಇವುಗಳನ್ನೆಲ್ಲಾ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ತನ್ನ ಹೆಸರನ್ನು ಸ್ಥಾಪಿಸುವುದಕ್ಕೆ ಆರಿಸಿಕೊಳ್ಳುವ ಸ್ಥಳಕ್ಕೇ ತರಬೇಕು.",
			"12": "<sup>12</sup> ಅಲ್ಲಿ ನೀವೂ, ನಿಮ್ಮ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳೂ, ದಾಸ ದಾಸಿಯರೂ ಮತ್ತು ನಿಮ್ಮೊಡನೆ ಸ್ವಾಸ್ತ್ಯವನ್ನು ಹೊಂದದೆ ಇರುವ ನಿಮ್ಮ ಊರಿನ ಲೇವಿಯರೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸಂತೋಷವಾಗಿರಬೇಕು.",
			"13": "<sup>13</sup> ನೀವು ಕಂಡ ಕಂಡ ಸ್ಥಳಗಳಲ್ಲಿ ಸರ್ವಾಂಗಹೋಮಗಳನ್ನು ಸಮರ್ಪಿಸಬಾರದು, ನೋಡಿಕೊಳ್ಳಿರಿ.",
			"14": "<sup>14</sup> ನಿಮ್ಮ ಕುಲಗಳಲ್ಲಿ ಯಾವುದೋ ಒಂದು ಕುಲದಲ್ಲಿ ಯೆಹೋವನು ಆರಿಸಿಕೊಳ್ಳುವ ಸ್ಥಳದಲ್ಲೇ ನಿಮ್ಮ ಸರ್ವಾಂಗಹೋಮಗಳನ್ನು ಸಮರ್ಪಿಸಬೇಕು; ಅಲ್ಲಿಯೇ ನಾನು ಆಜ್ಞಾಪಿಸಿದ ಎಲ್ಲಾ ಆಚಾರಗಳನ್ನು ನಡಿಸಬೇಕು.",
			"15": "<sup>15</sup> ಆದರೆ ನೀವು ಎಲ್ಲಾ ಊರುಗಳಲ್ಲಿಯೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಅಭಿವೃದ್ಧಿಪಡಿಸಿದ ಮೇರೆಗೆ ಪಶುಗಳನ್ನು ಇಷ್ಟಾನುಸಾರವಾಗಿ ಕೊಯಿದು ಊಟಮಾಡಬಹುದು. ಶುದ್ಧರೂ ಮತ್ತು ಅಶುದ್ಧರೂ ಜಿಂಕೆದುಪ್ಪಿಗಳ ಮಾಂಸವನ್ನು ತಿನ್ನುವ ಪ್ರಕಾರ ಇವುಗಳನ್ನು ತಿನ್ನಬಹುದು.",
			"16": "<sup>16</sup> ರಕ್ತವನ್ನು ಮಾತ್ರ ತಿನ್ನಬಾರದು; ಅದನ್ನು ನೀರಿನಂತೆ ಭೂಮಿಯ ಮೇಲೆ ಸುರಿದುಬಿಡಬೇಕು.",
			"17": "<sup>17</sup> ಧಾನ್ಯ, ದ್ರಾಕ್ಷೆ, ಎಣ್ಣೇಕಾಯಿ ಈ ಬೆಳೆಗಳ ದಶಮಾಂಶಗಳನ್ನೂ, ದನ ಮತ್ತು ಕುರಿಗಳ ಚೊಚ್ಚಲು ಮರಿಗಳನ್ನೂ, ಹರಕೆಮಾಡಿದ ಪದಾರ್ಥಗಳನ್ನೂ, ಕಾಣಿಕೆಗಳನ್ನೂ, ಯಾಜಕರಿಗೋಸ್ಕರ ಪ್ರತ್ಯೇಕಿಸಿದ ಪದಾರ್ಥಗಳನ್ನೂ ನಿಮ್ಮ ನಿಮ್ಮ ಊರುಗಳಲ್ಲಿ ತಿನ್ನಬಾರದು.",
			"18": "<sup>18</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಆಯ್ದುಕೊಳ್ಳುವ ಸ್ಥಳದಲ್ಲೇ ಆತನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಅವುಗಳನ್ನು ಊಟಮಾಡಬೇಕು. ನಿಮ್ಮ ಎಲ್ಲಾ ಪ್ರಯತ್ನಗಳು ಕೈಗೂಡಿದ್ದಕ್ಕಾಗಿ ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸಂತೋಷದಿಂದಿದ್ದು, ನೀವೂ, ನಿಮ್ಮ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳೂ, ಆಳುಗಳೂ ಮತ್ತು ನಿಮ್ಮ ಊರಿನಲ್ಲಿರುವ ಲೇವಿಯರೂ ಇಂಥವುಗಳನ್ನು ಊಟಮಾಡಬೇಕು.",
			"19": "<sup>19</sup> ನೀವು ನಿಮ್ಮ ದೇಶದಲ್ಲಿ ಇರುವ ತನಕ ಲೇವಿಯರನ್ನು ಕೈಬಿಡಬಾರದು, ಜ್ಞಾಪಕವಿರಲಿ.",
			"20": "<sup>20</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ವಾಗ್ದಾನ ಮಾಡಿದಂತೆ ನಿಮ್ಮ ರಾಜ್ಯವನ್ನು ವಿಸ್ತರಿಸಿದಾಗ ನೀವು, “ನಾವು ಮಾಂಸಾಹಾರವನ್ನು ಉಟಮಾಡಬೇಕು” ಎಂದು ನೀವು ಇಷ್ಟಪಟ್ಟರೆ ಮಾಂಸಾಹಾರವನ್ನು ಮಾಡಬಹುದು.",
			"21": "<sup>21</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ತನ್ನ ಹೆಸರನ್ನು ಸ್ಥಾಪಿಸಿಕೊಳ್ಳುವುದಕ್ಕಾಗಿ ಆರಿಸಿಕೊಂಡ ಸ್ಥಳವು ನಿಮಗೆ ದೂರವಾದರೆ, ಆತನು ನಿಮಗೆ ಅನುಗ್ರಹಿಸಿದ ದನ ಮತ್ತು ಕುರಿಗಳಲ್ಲಿ ಬೇಕಾದಷ್ಟನ್ನು ನಾನು ಅಪ್ಪಣೆಕೊಟ್ಟ ಮೇರೆಗೆ ನೀವು ಕೊಯಿದು ನಿಮ್ಮ ಊರಲ್ಲೇ ಊಟಮಾಡಬಹುದು.",
			"22": "<sup>22</sup> ಜಿಂಕೆದುಪ್ಪಿಗಳನ್ನು ಶುದ್ಧರೂ ಮತ್ತು ಅಶುದ್ಧರೂ ತಿನ್ನುವ ಪ್ರಕಾರ ದನ ಮತ್ತು ಕುರಿಗಳನ್ನೂ ತಿನ್ನಬಹುದು.",
			"23": "<sup>23</sup> ಆದರೆ ರಕ್ತವು ಜೀವಾಧಾರವಾದ್ದರಿಂದ ಮಾಂಸದೊಡನೆ ರಕ್ತವನ್ನು ತಿನ್ನಲೇ ಕೂಡದೆಂಬುದನ್ನು ಜ್ಞಾಪಕದಲ್ಲಿಡಿರಿ. ಪಶುಮಾಂಸದೊಡನೆ ಅದರ ಜೀವಾಧಾರವಾದದ್ದನ್ನೂ ತಿನ್ನಬಾರದಷ್ಟೆ.",
			"24": "<sup>24</sup> ಅದನ್ನು ತಿನ್ನದೆ, ನೀರಿನಂತೆ ಭೂಮಿಯ ಮೇಲೆ ಸುರಿದುಬಿಡಬೇಕು.",
			"25": "<sup>25</sup> ನೀವು ಅದನ್ನು ತಿನ್ನದೆ ಯೆಹೋವನಿಗೆ ಮೆಚ್ಚಿಗೆಯಾಗಿರುವುದನ್ನು ನಡಿಸಿದರೆ ನಿಮಗೂ ಮತ್ತು ನಿಮ್ಮ ಸಂತತಿಯವರಿಗೂ ಶುಭವುಂಟಾಗುವುದು.",
			"26": "<sup>26</sup> ಆದರೆ ನಿಮ್ಮ ಬಳಿಯಲ್ಲಿರುವ ದೇವರ ವಸ್ತುಗಳನ್ನೂ ಹರಕೆಮಾಡಿದ್ದನ್ನೂ ಯೆಹೋವನು ಆರಿಸಿಕೊಂಡ ಸ್ಥಳಕ್ಕೇ ತೆಗೆದುಕೊಂಡು ಹೋಗಬೇಕು.",
			"27": "<sup>27</sup> ಸರ್ವಾಂಗ ಹೋಮಗಳನ್ನು ಸಮರ್ಪಿಸುವಾಗ, ರಕ್ತ ಮಾಂಸಗಳೆರಡನ್ನೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಯಜ್ಞವೇದಿಯಲ್ಲಿ ದಹಿಸಿಬಿಡಬೇಕು. ಸಮಾಧಾನ ಯಜ್ಞಗಳನ್ನು ಮಾಡುವಾಗ ರಕ್ತವನ್ನು ಆತನ ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಸುರಿದುಬಿಟ್ಟು ಮಾಂಸವನ್ನು ಊಟಮಾಡಬೇಕು.",
			"28": "<sup>28</sup> ನಾನು ಬೋಧಿಸುವ ಈ ಎಲ್ಲಾ ಆಜ್ಞೆಗಳಿಗೆ ನೀವು ಚೆನ್ನಾಗಿ ಕಿವಿಗೊಟ್ಟು, ಅವುಗಳನ್ನು ಅನುಸರಿಸಿ, ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಒಳ್ಳೆಯದೂ ಹಾಗು ಯುಕ್ತವೂ ಆಗಿರುವುದನ್ನು ಮಾಡಿದರೆ ನಿಮಗೂ ಮತ್ತು ನಿಮ್ಮ ಸಂತತಿಯವರಿಗೂ ಸದಾಕಾಲ ಶುಭವುಂಟಾಗುವುದು.",
			"29": "<sup>29</sup> ನೀವು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವುದಕ್ಕೆ ಹೋಗುವ ದೇಶದ ಜನಾಂಗಗಳನ್ನು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮ ಮುಂದೆ ನಾಶಮಾಡಿದ ಮೇಲೆ ನೀವು ಅವರ ದೇಶವನ್ನು ವಶಮಾಡಿಕೊಂಡು ಅದರಲ್ಲಿ ವಾಸವಾಗಿರುವಾಗ",
			"30": "<sup>30</sup> ನೀವು ಭ್ರಮೆಗೊಂಡು ನಿಮ್ಮ ಎದುರಿನಿಂದ ನಾಶವಾಗಿ ಹೋದವರ ದುಷ್ಟಪದ್ಧತಿಗಳನ್ನು ಅನುಸರಿಸಬಾರದು. ನೀವು, “ಈ ದೇಶದ ಜನರು ತಮ್ಮ ದೇವರುಗಳನ್ನು ಹೇಗೆ ಸೇವಿಸುತ್ತಿದ್ದರೋ ಹಾಗೆಯೇ ನಾವೂ ಸೇವಿಸುವೆವು” ಎಂದು ಹೇಳಿಕೊಳ್ಳುವವರಾಗಿ ಅವರ ದೇವರುಗಳ ವಿಷಯದಲ್ಲಿ ವಿಚಾರಣೆಯನ್ನು ಎಷ್ಟು ಮಾತ್ರವೂ ಮಾಡಬಾರದು.",
			"31": "<sup>31</sup> ಯೆಹೋವನಿಗೆ ಅಸಹ್ಯವಾಗಿರುವ ಹಲವು ಕೆಟ್ಟಕೆಲಸಗಳನ್ನು ಅವರು ತಮ್ಮ ದೇವತೆಗಳಿಗೋಸ್ಕರ ನಡಿಸುತ್ತಾರಲ್ಲಾ. ತಮ್ಮ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ತಮ್ಮ ದೇವತೆಗಳಿಗೋಸ್ಕರ ಬೆಂಕಿಯಲ್ಲಿ ಸುಡುತ್ತಾರಲ್ಲಾ. ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಹಾಗೆ ಸೇವಿಸಲೇಬಾರದು.",
			"32": "<sup>32</sup> ನಾನು ನಿಮಗೆ ಆಜ್ಞಾಪಿಸುವದನ್ನೆಲ್ಲಾ ನೀವು ಅನುಸರಿಸಲೇಬೇಕು; ಅದಕ್ಕೆ ಏನೂ ಸೇರಿಸಬಾರದು; ಅದರಿಂದ ಏನೂ ತೆಗೆದುಬಿಡಬಾರದು."
		},
		"13": {
			"1": "<sup>1</sup> ಯಾವ ಪ್ರವಾದಿಯಾಗಲಿ ಅಥವಾ ಕನಸುಗಾರನೇ ಆಗಲಿ ನಿಮ್ಮ ಮಧ್ಯದಲ್ಲಿ ಬಂದು, ನಿಮಗೆ ಗೊತ್ತಿಲ್ಲದ ಇತರ ದೇವರುಗಳನ್ನು ತೋರಿಸಿ,",
			"2": "<sup>2</sup> “ಈ ದೇವತೆಗಳನ್ನು ಅವಲಂಬಿಸಿ ಅವುಗಳನ್ನು ಸೇವಿಸೋಣ” ಎಂದು ಬೋಧಿಸುತ್ತಾ, ಆ ಬೋಧನೆಯನ್ನು ಸ್ಥಾಪಿಸುವುದಕ್ಕೆ ಒಂದು ಅದ್ಭುತವನ್ನಾಗಲಿ ಅಥವಾ ಮಹತ್ಕಾರ್ಯವನ್ನಾಗಲಿ ತೋರಿಸಿಕೊಡುತ್ತೇನೆಂದು ಹೇಳಿ ಅವನು ಹೇಳಿದಂತೆಯೇ ನಡೆದರೂ ನೀವು ಅವನ ಮಾತಿಗೆ ಕಿವಿಗೊಡಬಾರದು.",
			"3": "<sup>3</sup> ಏಕೆಂದರೆ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು, “ಇವರು ಸಂಪೂರ್ಣ ಹೃದಯದಿಂದಲೂ ಮತ್ತು ಸಂಪೂರ್ಣ ಮನಸ್ಸಿನಿಂದಲೂ ತನ್ನನ್ನೇ ಪ್ರೀತಿಸುವವರು ಹೌದೋ ಅಲ್ಲವೋ” ಎಂಬುದನ್ನು ತಿಳಿದುಕೊಳ್ಳುವುದಕ್ಕೆ ನಿಮ್ಮನ್ನು ಪರೀಕ್ಷಿಸುತ್ತಾನೆ.",
			"4": "<sup>4</sup> ಆತನು ಹೇಳುವ ಮಾರ್ಗದಲ್ಲೇ ನೀವು ನಡೆದು, ಆತನಲ್ಲಿಯೇ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿ, ಆತನ ಆಜ್ಞೆಗಳನ್ನೇ ಅನುಸರಿಸಿ, ಆತನಿಗೇ ವಿಧೇಯರಾಗಿ, ಆತನನ್ನೇ ಸೇವಿಸುತ್ತಾ ಹೊಂದಿಕೊಂಡಿರಬೇಕು.",
			"5": "<sup>5</sup> ಆ ಪ್ರವಾದಿಗೆ ಅಥವಾ ಆ ಕನಸುಗಾರನಿಗೆ ಮರಣ ಶಿಕ್ಷೆಯಾಗಬೇಕು. ದಾಸತ್ವದಲ್ಲಿದ್ದ ನಿಮ್ಮನ್ನು ಬಿಡುಗಡೆಮಾಡಿ, ಐಗುಪ್ತದೇಶದೊಳಗಿಂದ ಕರೆದುಕೊಂಡು ಬಂದ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ವಿರುದ್ಧವಾಗಿ ಅವನು ದ್ರೋಹದ ಮಾತುಗಳನ್ನಾಡಿ, ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಹೇಳಿದ ಮಾರ್ಗದಿಂದ ನಿಮ್ಮನ್ನು ತಪ್ಪಿಸಬೇಕೆಂದಿದ್ದನಲ್ಲಾ. ಅವನನ್ನು ಕೊಲ್ಲಿಸಿ, ಆ ದುಷ್ಟತ್ವವನ್ನು ನಿಮ್ಮ ಮಧ್ಯದಿಂದ ತೆಗೆದುಹಾಕಬೇಕು.",
			"6": "<sup>6</sup> ಒಡಹುಟ್ಟಿದ ಅಣ್ಣತಮ್ಮಂದಿರಾಗಲಿ, ಮಗನಾಗಲಿ, ಮಗಳಾಗಲಿ, ಪ್ರಾಣಪ್ರಿಯಳಾದ ಹೆಂಡತಿಯಾಗಲಿ, ಆಪ್ತಮಿತ್ರನಾಗಲಿ ಇತರ ದೇವರುಗಳನ್ನು ತೋರಿಸಿ, “ನಿಮಗೂ ನಿಮ್ಮ ಪಿತೃಗಳಿಗೂ ಗೊತ್ತಿಲ್ಲದ ಆ ದೇವರುಗಳನ್ನು ಸೇವಿಸೋಣ ಬನ್ನಿರಿ” ಎಂದು ರಹಸ್ಯವಾಗಿ ಆಕರ್ಷಿಸಿ, ನಿಮಗೆ ಬೋಧಿಸಿದರೆ,",
			"7": "<sup>7</sup> ಆ ದೇವರುಗಳು ಹತ್ತಿರುವಿರುವ ಜನಾಂಗಗಳ ದೇವರುಗಳಾದರೂ, ದೂರವಾದವರ ದೇವರುಗಳಾದರೂ, ಭೂಲೋಕದ ಯಾವ ಭಾಗದವರ ದೇವರುಗಳಾದರೂ",
			"8": "<sup>8</sup> ನೀವು ಸಮ್ಮತಿಸಲೂ ಬಾರದು ಮತ್ತು ಕಿವಿಗೊಡಲೂಬಾರದು. ಅವನನ್ನು ಕನಿಕರಿಸಲೂಬಾರದು, ತಪ್ಪಿಸಲೂಬಾರದು, ಬಚ್ಚಿಡಲೂಬಾರದು ಅವನನ್ನು ಕೊಲ್ಲಿಸಲೇಬೇಕು.",
			"9": "<sup>9</sup> ಆ ಶಿಕ್ಷೆಯನ್ನು ನಡಿಸುವಾಗ ತಪ್ಪು ಕಂಡವನೇ ಮೊದಲು ಕೈಹಾಕಬೇಕು.",
			"10": "<sup>10</sup> ತರುವಾಯ ಜನರೆಲ್ಲರೂ ಕೈಹಾಕಲಿ, ದಾಸತ್ವದಲ್ಲಿದ್ದ ನಿಮ್ಮನ್ನು ಬಿಡುಗಡೆಮಾಡಿ, ಐಗುಪ್ತ ದೇಶದೊಳಗಿಂದ ಕರೆದುಕೊಂಡು ಬಂದ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಆಶ್ರಯದಿಂದ ಅವನು ನಿಮ್ಮನ್ನು ತಪ್ಪಿಸಬೇಕೆಂದಿದ್ದರಿಂದ ಅವನನ್ನು ಕಲ್ಲೆಸೆದು ಕೊಲ್ಲಲೇಬೇಕು.",
			"11": "<sup>11</sup> ಇದನ್ನು ಇಸ್ರಾಯೇಲರೆಲ್ಲರೂ ಕೇಳಿ ಭಯಪಟ್ಟು ಅಂಥ ದುಷ್ಕಾರ್ಯವನ್ನು ಇನ್ನು ಮುಂದೆ ಮಾಡದೆ ಇರುವರು.",
			"12": "<sup>12</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮ ನಿವಾಸಕ್ಕಾಗಿ ಕೊಡುವ ಯಾವುದಾದರೂ ಒಂದು ಊರಿನ ವಿಷಯದಲ್ಲಿ,",
			"13": "<sup>13</sup> ಅಲ್ಲಿ ವಾಸವಾಗಿರುವ ಇಸ್ರಾಯೇಲರಲ್ಲಿ ಕೆಲವು ಜನ ದುಷ್ಟರು ನಿಮಗೆ ಗೊತ್ತಿಲ್ಲದ ಇತರ ದೇವರುಗಳನ್ನು ತಮ್ಮ ಊರಿನವರಿಗೆ ತೋರಿಸಿ, “ಆ ದೇವರುಗಳನ್ನು ಸೇವಿಸೋಣ ಬನ್ನಿರಿ” ಎಂದು ಹೇಳಿ ಅವರನ್ನು ಸನ್ಮಾರ್ಗದಿಂದ ತಪ್ಪಿಸಿದ್ದಾರೆಂಬ ಸುದ್ದಿಯನ್ನು ನೀವು ಕೇಳಿದರೆ,",
			"14": "<sup>14</sup> ಆ ಸಂಗತಿಯನ್ನು ಚೆನ್ನಾಗಿ ವಿಚಾರಿಸಿ ತಿಳಿದುಕೊಳ್ಳಬೇಕು. ಅಂತಹ ಅಸಹ್ಯಕಾರ್ಯವು ಇಸ್ರಾಯೇಲರಲ್ಲಿ ನಡೆದದ್ದು",
			"15": "<sup>15</sup> ನಿಜವೆಂದು ತಿಳಿದುಬಂದರೆ ಆ ಊರನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ಹಾಳು ಮಾಡಿ, ಅದರಲ್ಲಿರುವ ಎಲ್ಲಾ ಜನರನ್ನೂ, ದನಗಳನ್ನೂ ಕತ್ತಿಯಿಂದ ಸಂಹರಿಸಿಡಬೇಕು.",
			"16": "<sup>16</sup> ಅದರಲ್ಲಿರುವ ಎಲ್ಲಾ ಸಾಮಾನುಗಳನ್ನು ಗ್ರಾಮಮಧ್ಯದಲ್ಲಿ ಕೂಡಿಸಿ, ಊರನ್ನೂ ಅದರಲ್ಲಿರವ ಎಲ್ಲಾ ಸಾಮಾನುಗಳನ್ನೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೋಸ್ಕರ ಸಂಪೂರ್ಣವಾಗಿ ಸುಟ್ಟುಬಿಡಬೇಕು. ಅದು ಪುನಃ ಕಟ್ಟಲ್ಪಡದೆ ಯಾವಾಗಲೂ ಹಾಳುದಿಬ್ಬವಾಗಿರಬೇಕು.",
			"17": "<sup>17</sup> “ಕೇವಲ ಯೆಹೋವನಿಗೇ ಆಗಲಿ” ಎಂದು ನೀವು ಗೊತ್ತು ಮಾಡಿದ್ದರಲ್ಲಿ ಸ್ವಲ್ಪವನ್ನಾದರೂ ತೆಗೆದುಕೊಳ್ಳಬಾರದು. ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ಆತನ ಎಲ್ಲಾ ಆಜ್ಞೆಗಳನ್ನು ಕೈಕೊಂಡು ಆತನ ದೃಷ್ಟಿಗೆ ಸರಿಯಾದದ್ದನ್ನು ಮಾಡಬೇಕು.",
			"18": "<sup>18</sup> ನೀವು ಹೀಗೆ ನಡೆದು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಮಾತಿಗೆ ಕಿವಿಗೊಟ್ಟರೆ, ಆಗ ಆತನು ತನ್ನ ರೋಷಾಗ್ನಿಯನ್ನು ಬಿಟ್ಟು ನಿಮಗೆ ದಯೆಯನ್ನು ತೋರಿಸಿ, ನಿಮ್ಮನ್ನು ಕರುಣಿಸಿ, ತಾನು ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ವಾಗ್ದಾನ ಮಾಡಿದಂತೆ ನಿಮ್ಮ ಸಂತತಿಯನ್ನು ಹೆಚ್ಚಿಸುವನು."
		},
		"14": {
			"1": "<sup>1</sup> ನೀವು ನಿಮ್ಮ ದೇವರಾಗಿರುವ ಯೆಹೋವನ ಮಕ್ಕಳಾಗಿರುವುದರಿಂದ ಸತ್ತವರಿಗೋಸ್ಕರ ದೇಹವನ್ನು ಗಾಯಮಾಡಿಕೊಳ್ಳಬಾರದು ಮತ್ತು ತಲೆಯ ಮುಂದಿನಭಾಗವನ್ನು ಬೋಳಿಸಿಕೊಳ್ಳಲೂ ಬಾರದು.",
			"2": "<sup>2</sup> ಯಾಕೆಂದರೆ ನೀವು ಕೇವಲ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಪರಿಶುದ್ಧ ಜನರೇ. ಆತನು ಭೂಲೋಕದಲ್ಲಿರುವ ಸಮಸ್ತ ಜನಾಂಗಗಳಲ್ಲಿ ನಿಮ್ಮನ್ನೇ ಸ್ವಕೀಯ ಜನರಾಗುವುದಕ್ಕೆ ಆರಿಸಿಕೊಂಡಿದ್ದಾನೆ ಎಂಬುದು ನಿಮಗೆ ತಿಳಿದಿರಬೇಕು.",
			"3": "<sup>3</sup> ನಿಷಿದ್ಧವಾಗಿರುವ ಯಾವುದನ್ನೂ ನೀವು ತಿನ್ನಬಾರದು.",
			"4": "<sup>4</sup> ನೀವು ತಿನ್ನಬಹುದಾದ ಪಶುಜಾತಿಗಳು ಯಾವುವೆಂದರೆ ದನ, ಕುರಿ, ಆಡು,",
			"5": "<sup>5</sup> ದುಪ್ಪಿ, ಜಿಂಕೆ, ಸಾರಂಗ, ಕಾಡುಮೇಕೆ, ಚಿಗರಿ, ಕಡವೆ, ಕೊಂಡಗುರಿ (ಕಾಡುಟಗರು) ಎಂಬಿವುಗಳೇ.",
			"6": "<sup>6</sup> ಯಾವ ಪ್ರಾಣಿಯ ಕಾಲ್ಗೊರಸು ಸೀಳಿದೆಯೋ ಅದು ಮೆಲಕುಹಾಕುವಂಥದಾದರೆ ಅದರ ಮಾಂಸವನ್ನು ತಿನ್ನಬಹುದು.",
			"7": "<sup>7</sup> ಆದರೆ ಯಾವ ಪ್ರಾಣಿಯು ಮೆಲಕುಹಾಕಿದರೂ ಗೊರಸು ಸೀಳಿರುವುದಿಲ್ಲವೋ ಮತ್ತು ಯಾವ ಪ್ರಾಣಿಯ ಗೊರಸು ಸೀಳಿದ್ದರೂ ಮೆಲಕು ಹಾಕುವುದಿಲ್ಲವೋ, ಅವುಗಳ ಮಾಂಸವನ್ನು ನೀವು ತಿನ್ನಬಾರದು. ಉದಾಹರಣೆ: ಒಂಟೆ, ಮೊಲ, ಬೆಟ್ಟದಮೊಲ ಇವುಗಳು ಮೆಲಕುಹಾಕುವಂಥವುಗಳಾದರೂ ಸೀಳುಗೊರಸು ಇಲ್ಲವಾದ ಕಾರಣ ಅವು ನಿಮಗೆ ಅಶುದ್ಧ.",
			"8": "<sup>8</sup> ಹಂದಿಯ ಗೊರಸು ಸೀಳಿದ್ದರೂ ಅದು ಮೆಲಕುಹಾಕುವುದಿಲ್ಲವಾದುದರಿಂದ ಅದು ನಿಮಗೆ ಅಶುದ್ಧ. ಇವುಗಳ ಮಾಂಸವನ್ನು ನೀವು ತಿನ್ನಬಾರದು; ಇವುಗಳ ಹೆಣವನ್ನು ಮುಟ್ಟಬಾರದು.",
			"9": "<sup>9</sup> ಜಲಜಂತುಗಳಲ್ಲಿ ನೀವು ತಿನ್ನಬಹುದಾದವುಗಳು ಯಾವುವೆಂದರೆ: ಯಾವ ಜಾತಿಯ ಜಲಜಂತುಗಳಿಗೆ ರೆಕ್ಕೆ ಇದ್ದು ದೇಹವೆಲ್ಲಾ ಪರೆಪರೆಯಾಗಿರುವುದೋ ಅದರ ಮಾಂಸವನ್ನು ತಿನ್ನಬಹುದು.",
			"10": "<sup>10</sup> ಯಾವ ಜಾತಿಯ ಜಲಜಂತುಗಳಿಗೆ ರೆಕ್ಕೆಯೂ ಮತ್ತು ಪರೆಪರೆಯಾದ ದೇಹವು ಇರುವುದಿಲ್ಲವೋ ಅವುಗಳ ಮಾಂಸವನ್ನು ನೀವು ತಿನ್ನಬಾರದು; ಅದು ನಿಮಗೆ ಅಶುದ್ಧ.",
			"11": "<sup>11</sup> ಎಲ್ಲಾ ಶುದ್ಧ ಪಕ್ಷಿಗಳ ಮಾಂಸವನ್ನು ನೀವು ತಿನ್ನಬಹುದು.",
			"12": "<sup>12</sup> ನೀವು ತಿನ್ನಬಾರದವುಗಳು ಯಾವುವೆಂದರೆ ಗರುಡ, ಬೆಟ್ಟದ ಹದ್ದು, ಕ್ರೌಂಚ,",
			"13": "<sup>13</sup> ಗಿಡಗ, ಹಕ್ಕಿಸಾಲೆ, ಸಕಲವಿಧವಾದ ಹದ್ದು,",
			"14": "<sup>14</sup> ಸಕಲವಿಧವಾದ ಕಾಗೆ,",
			"15": "<sup>15</sup> ಉಷ್ಟ್ರಪಕ್ಷಿ, ಉಲೂಕ, ಕಡಲಹಕ್ಕಿ, ಸಕಲವಿಧವಾದ ಡೇಗೆ,",
			"16": "<sup>16</sup> ಗೂಬೆ, ಹೆಗ್ಗೂಬೆ, ಕರೇಟು,",
			"17": "<sup>17</sup> ಚೀಲಮೂಗಿ, ರಣಹದ್ದು, ನೀರುಕಾಗೆ,",
			"18": "<sup>18</sup> ಕೊಕ್ಕರೆ, ಸಕಲವಿಧವಾದ ಬಕ, ಹೆಡೆಹಕ್ಕಿ ಮತ್ತು ಕಣ್ಣಕಪಡಿ.",
			"19": "<sup>19</sup> ರೆಕ್ಕೆಯುಳ್ಳ ಕ್ರಿಮಿಕೀಟಗಳೆಲ್ಲವೂ ನಿಮಗೆ ಅಶುದ್ಧ. ಅವುಗಳನ್ನು ನೀವು ತಿನ್ನಬಾರದು.",
			"20": "<sup>20</sup> ರೆಕ್ಕೆಯುಳ್ಳವುಗಳಲ್ಲಿ ಶುದ್ಧ ಜಾತಿಯವುಗಳನ್ನು ಮಾತ್ರ ನೀವು ತಿನ್ನಬಹುದು.",
			"21": "<sup>21</sup> ಸತ್ತುಬಿದ್ದದ್ದನ್ನು ನೀವು ತಿನ್ನಬಾರದು; ನಿಮ್ಮ ಊರಲ್ಲಿರುವ ಪರದೇಶೀಯರಿಗೆ ಅದನ್ನು ತಿನ್ನುವುದಕ್ಕೆ ಕೊಡಬಹುದು, ಅನ್ಯರಿಗೆ ಮಾರಬಹುದು. ನೀವಾದರೋ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಪ್ರತಿಷ್ಠಿತರಾದವರು. ಆಡುಮರಿಯನ್ನು ಅದರ ತಾಯಿಯ ಹಾಲಿನಲ್ಲಿ ಬೇಯಿಸಬಾರದು.",
			"22": "<sup>22</sup> ಪ್ರತಿವರುಷದ ನಿಮ್ಮ ಹೊಲದ ಬೆಳೆಯಲ್ಲಿ ದಶಮ ಭಾಗವನ್ನು ಪ್ರತ್ಯೇಕಿಸಬೇಕು.",
			"23": "<sup>23</sup> ಧಾನ್ಯ, ದ್ರಾಕ್ಷೆ, ಎಣ್ಣೆ ಇವುಗಳಲ್ಲಿ ದಶಮಭಾಗವನ್ನೂ ದನಕುರಿಗಳ ಚೊಚ್ಚಲು ಮರಿಗಳನ್ನೂ ಮತ್ತು ನಿಮ್ಮ ಹಿಂಡುಗಳನ್ನು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಆತನು ತನ್ನ ಹೆಸರನ್ನು ಸ್ಥಾಪಿಸಿಕೊಳ್ಳುವುದಕ್ಕೆ ಆರಿಸಿಕೊಳ್ಳುವ ಸ್ಥಳದಲ್ಲೇ ನೀವು ತಿನ್ನಬೇಕು. ಹೀಗೆ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಲ್ಲೇ ಯಾವಾಗಲೂ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿ ಇರುವುದನ್ನು ಅಭ್ಯಾಸ ಮಾಡಿಕೊಳ್ಳುವವರಾಗಿರಬೇಕು.",
			"24": "<sup>24</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಆರಿಸಿಕೊಳ್ಳುವ ಸ್ಥಳವು ನಿಮಗೆ ದೂರವಾಗಿದ್ದು, ಆ ಕಾಣಿಕೆಗಳನ್ನು ಅಷ್ಟು ದೂರ ಹೊತ್ತುಕೊಂಡು ಹೋಗುವುದು ಅಸಾಧ್ಯವಾದರೆ, ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಅಭಿವೃದ್ಧಿಯನ್ನು ಉಂಟುಮಾಡಿದ ಕಾಲದಲ್ಲಿ,",
			"25": "<sup>25</sup> ಅವುಗಳನ್ನು ಮಾರಿಬಿಟ್ಟು, ಆ ಹಣದ ಗಂಟನ್ನು ಆತನು ಆರಿಸಿಕೊಂಡ ಸ್ಥಳಕ್ಕೆ ತೆಗೆದುಕೊಂಡು ಹೋಗಿರಿ.",
			"26": "<sup>26</sup> ಅಲ್ಲಿ ನಿಮ್ಮ ಇಷ್ಟಾನುಸಾರ ದನ, ಕುರಿ, ದ್ರಾಕ್ಷಾರಸ, ಮದ್ಯ ಮೊದಲಾದವುಗಳನ್ನು ಆ ಹಣದಿಂದ ಕೊಂಡುಕೊಂಡು, ನೀವೂ ಮತ್ತು ನಿಮ್ಮ ಮನೆಯವರೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಊಟಮಾಡಿ ಸಂತೋಷವಾಗಿರಬೇಕು.",
			"27": "<sup>27</sup> ಲೇವಿಯರಿಗೆ ನಿಮ್ಮೊಡನೆ ಯಾವ ಸ್ವಾಸ್ತ್ಯವೂ ಇಲ್ಲವಾದ್ದರಿಂದ ನೀವು ಅವರನ್ನು ಕೈಬಿಡಬಾರದು.",
			"28": "<sup>28</sup> ಪ್ರತಿ ಮೂರನೆಯ ವರುಷದ ಬೆಳೆಯ ದಶಮಭಾಗವನ್ನು ತಂದು ನೀವು ಇರುವ ಊರಿನಲ್ಲಿಯೇ ಕೂಡಿಸಬೇಕು.",
			"29": "<sup>29</sup> ಆಗ ಊರಿನಲ್ಲಿರುವ ಅನ್ಯದೇಶದವರೂ, ಅನಾಥರು, ವಿಧವೆಯರೂ ಮತ್ತು ನಿಮ್ಮೊಡನೆ ಸ್ವಾಸ್ತ್ಯವನ್ನು ಹೊಂದದೆಹೋದ ಲೇವಿಯರೂ ಊಟಮಾಡಿ ಸಂತೋಷವಾಗಿರಲಿ. ನೀವು ಹೀಗೆ ನಡೆದುಕೊಂಡರೆ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮ ಎಲ್ಲಾ ಪ್ರಯತ್ನಗಳನ್ನು ಸಫಲಮಾಡುವನು."
		},
		"15": {
			"1": "<sup>1</sup> ಪ್ರತಿ ಏಳುನೆಯ ವರುಷದ ಕೊನೆಯಲ್ಲಿ ಸಾಲವನ್ನು ಬಿಟ್ಟುಬಿಡಬೇಕು.",
			"2": "<sup>2</sup> ಹೇಗೆಂದರೆ ಯೆಹೋವನು ನೇಮಿಸಿದ ಬಿಡುಗಡೆಯ ವರುಷವು ಬಂತೆಂದು ಪ್ರಕಟವಾದುದರಿಂದ ಸಾಲಕೊಟ್ಟವನು ತೆಗೆದುಕೊಂಡವನಿಗೆ ಆ ಸಾಲವನ್ನು ಬಿಟ್ಟುಬಿಡಬೇಕು. ಅವನು ಸ್ವದೇಶದವನಿಗೆ ಕೊಟ್ಟ ಸಾಲವನ್ನು ಕೇಳಬಾರದು.",
			"3": "<sup>3</sup> ಅನ್ಯದೇಶದವರಿಗೆ ಕೊಟ್ಟ ಸಾಲವನ್ನು ಕೇಳಿ ತೆಗೆದುಕೊಳ್ಳಬಹುದೇ ಹೊರತು ಸ್ವದೇಶದವನಿಗೆ ಕೊಟ್ಟಿರುವುನ್ನು ಕೇಳಬಾರದು.",
			"4": "<sup>4</sup> ಆದರೆ ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಮಾತಿಗೆ ಕಿವಿಗೊಟ್ಟು, ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ಈ ಧರ್ಮೋಪದೇಶವನ್ನೆಲ್ಲಾ ಅನುಸರಿಸಿದರೆ,",
			"5": "<sup>5</sup> ಯೆಹೋವನು ತಾನು ನಿಮಗೆ ಸ್ವದೇಶವಾಗಿ ಕೊಡುವ ದೇಶದಲ್ಲಿ ನೀವು ಅಭಿವೃದ್ಧಿ ಹೊಂದುವುದರಿಂದ ನಿಮ್ಮಲ್ಲಿ ಬಡವರೇ ಇರುವುದಿಲ್ಲ.",
			"6": "<sup>6</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ವಾಗ್ದಾನಮಾಡಿದಂತೆ ನಿಮ್ಮನ್ನು ಅಭಿವೃದ್ಧಿಪಡಿಸುವನು. ನೀವು ಅನೇಕ ಜನಗಳಿಗೆ ಸಾಲಕೊಡುವಿರೇ ಹೊರತು ಅವರಿಂದ ಸಾಲ ತೆಗೆದುಕೊಳ್ಳುವುದಿಲ್ಲ. ನೀವು ಅನೇಕ ಜನಾಂಗಗಳ ಮೇಲೆ ದೊರೆತನ ಮಾಡುವಿರೇ ಹೊರತು ಅವರು ನಿಮ್ಮ ಮೇಲೆ ದೊರೆತನ ಮಾಡುವುದಿಲ್ಲ.",
			"7": "<sup>7</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಕೊಡುವ ದೇಶದ ಯಾವ ಊರಿನಲ್ಲಾದರೂ ಸ್ವದೇಶದವನಾದ ಬಡವನು ಇದ್ದರೆ, ನೀವು ಆ ಬಡ ಸಹೋದರನಿಗೆ ಮನಸ್ಸನ್ನು ಕಠಿಣಮಾಡಿಕೊಂಡು ಅವನಿಗೆ ಸಹಾಯಮಾಡದೆ ಇರಬಾರದು.",
			"8": "<sup>8</sup> ನೀವು ಕೈತೆರೆದು ಅವನಿಗೆ ಅವಶ್ಯವಾಗಿ ಬೇಕಾದದ್ದನ್ನು ಕೊಟ್ಟು ಸಹಾಯ ಮಾಡಬೇಕು.",
			"9": "<sup>9</sup> ನೀವು “ಬಿಡುಗಡೆಯುಂಟಾಗುವ ಏಳನೆಯ ವರುಷವು ಸಮೀಪವಾಯಿತು” ಎಂಬ ನೀಚವಾದ ಆಲೋಚನೆಯನ್ನು ಮಾಡಿ, ಆ ಬಡ ಸಹೋದರನಿಗೆ ಏನೂ ಕೊಡದೆ ಇರಬಾರದು, ಎಚ್ಚರಿಕೆ! ಹಾಗೆ ಧಿಕ್ಕರಿಸಿದರೆ ಅವನು ಒಂದು ವೇಳೆ ನಿಮ್ಮ ವಿಷಯದಲ್ಲಿ ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಟ್ಟಾನು, ನೀವು ದೋಷಿಗಳಾಗಿ ಕಂಡುಬರುವಿರಿ.",
			"10": "<sup>10</sup> ನೀವು ಕೊಡುವಾಗ ಬೇಸರಗೊಳ್ಳದೆ ಉದಾರವಾದ ಮನಸ್ಸಿನಿಂದಲೇ ಕೊಡಬೇಕು. ಇದರಿಂದ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮ ಎಲ್ಲಾ ಕೆಲಸಗಳಲ್ಲಿಯೂ ಮತ್ತು ಪ್ರಯತ್ನಗಳಲ್ಲಿಯೂ ನಿಮ್ಮನ್ನು ಅಭಿವೃದ್ಧಿಪಡಿಸುವನು.",
			"11": "<sup>11</sup> ದೇಶದಲ್ಲಿ ಯಾವಾಗಲೂ ಬಡವರು ಇರುವರಷ್ಟೆ; ಆದುದರಿಂದ, “ನೀವು ಸ್ವದೇಶದವರಾದ ನಿಮ್ಮ ಸಹೋದರರಿಗೂ, ಬಡವರಿಗೂ ಮತ್ತು ಗತಿಯಿಲ್ಲದವರಿಗೂ ಕೈನೀಡಿ ಸಹಾಯಮಾಡಬೇಕು” ಎಂದು ನಾನು ನಿಮಗೆ ಆಜ್ಞಾಪಿಸಿದ್ದೇನೆ.",
			"12": "<sup>12</sup> ಸ್ವದೇಶದವರೊಳಗೆ ಯಾವ ಇಬ್ರಿಯ ಪುರುಷನಾಗಲಿ, ಸ್ತ್ರೀಯಾಗಲಿ ನಿಮಗೆ ದಾಸರಾಗಿರುವುದಕ್ಕಾಗಿ ಮಾರಲ್ಪಡುವ ಪಕ್ಷಕ್ಕೆ, ಅಂಥವರು ಆರು ವರುಷಗಳವರೆಗೂ ನಿಮ್ಮ ಸೇವೆ ಮಾಡಲಿ; ಏಳನೆಯ ವರುಷದಲ್ಲಿ ನೀವು ಅವರನ್ನು ಬಿಡುಗಡೆ ಮಾಡಬೇಕು.",
			"13": "<sup>13</sup> ಬಿಡುಗಡೆ ಮಾಡುವಾಗ ಬರೀಗೈಯಲ್ಲಿ ಕಳುಹಿಸಿಬಿಡಬಾರದು.",
			"14": "<sup>14</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಅನುಗ್ರಹಿಸಿರುವ ಮೇರೆಗೆ ನಿಮಗಿರುವ ಹಿಂಡು, ದವಸ, ದ್ರಾಕ್ಷೆ ಆಲೆ ಮತ್ತು ಕಣ ಇವುಗಳಲ್ಲಿ ಉದಾರವಾಗಿ ಕೊಟ್ಟು ಕಳುಹಿಸಿಬಿಡಬೇಕು.",
			"15": "<sup>15</sup> ಐಗುಪ್ತದೇಶದಲ್ಲಿ ನೀವೇ ದಾಸರಾಗಿರಲು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಬಿಡುಗಡೆಮಾಡಿದನು ಎಂಬುದನ್ನು ಜ್ಞಾಪಕಮಾಡಿಕೊಳ್ಳಿರಿ. ಇದಕ್ಕೋಸ್ಕರವಾಗಿಯೇ ನಾನು ಈ ಆಜ್ಞೆಯನ್ನು ನಿಮಗೆ ಈಗ ಕೊಟ್ಟಿದ್ದೇನೆ.",
			"16": "<sup>16</sup> ಒಂದು ವೇಳೆ ಆ ದಾಸನು ನಿಮ್ಮ ಬಳಿಯಲ್ಲಿ ಸುಖವಾಗಿದ್ದು, ನಿಮ್ಮನ್ನೂ ಮತ್ತು ನಿಮ್ಮ ಮನೆಯವರನ್ನೂ ಪ್ರೀತಿಸಿ, ತಾನು ಬಿಡುಗಡೆಯಾಗಿ ಹೋಗುವುದಿಲ್ಲ ಎಂದು ಹೇಳುವ ಪಕ್ಷಕ್ಕೆ,",
			"17": "<sup>17</sup> ನೀವು ದಬ್ಬಲದಿಂದ ಅವನ ಕಿವಿಯನ್ನು ಚುಚ್ಚಿ, ಕದಕ್ಕೆ ಸಿಕ್ಕಿಸಬೇಕು. ಆ ದಿನದಿಂದ ಅವನು ನಿಮಗೆ ಶಾಶ್ವತ ದಾಸನಾಗಿರಬೇಕು. ಅದೇ ರೀತಿಯಾಗಿ ದಾಸಿಯರ ವಿಷಯದಲ್ಲಿಯೂ ನಡೆದುಕೊಳ್ಳಬೇಕು.",
			"18": "<sup>18</sup> ದಾಸನನ್ನು ಬಿಡುಗಡೆಮಾಡುವುದು ಕಷ್ಟವೆಂದು ಹೇಳಿಕೊಳ್ಳಬಾರದು. ಅವನು ಆರು ವರುಷಗಳು ನಿಮ್ಮ ದಾಸನಾಗಿದ್ದು ಕೂಲಿಯಾಳಿಗಿಂತ ಎರಡರಷ್ಟು ಪ್ರಯೋಜನವನ್ನು ಉಂಟುಮಾಡಿದನಲ್ಲಾ. ನೀವು ಹೀಗೆ ನಡೆದುಕೊಂಡರೆ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮ ಎಲ್ಲಾ ಪ್ರಯತ್ನಗಳನ್ನು ಸಫಲಗೊಳಿಸುವನು.",
			"19": "<sup>19</sup> ದನಗಳಲ್ಲಿಯೂ ಮತ್ತು ಆಡುಕುರಿಗಳಲ್ಲಿಯೂ ಚೊಚ್ಚಲಾದ ಗಂಡನ್ನು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗಾಗಿ ಪ್ರತಿಷ್ಠಿಸಬೇಕು. ಚೊಚ್ಚಲಾದ ಹೋರಿಯಿಂದ ಕೆಲಸಮಾಡಿಸಬಾರದು; ಚೊಚ್ಚಲಾದ ಕುರಿಯ ಉಣ್ಣೆಯನ್ನು ಕತ್ತರಿಸಬಾರದು.",
			"20": "<sup>20</sup> ಪ್ರತಿ ವರುಷದಲ್ಲಿ ನೀವೂ ಮತ್ತು ನಿಮ್ಮ ಮನೆಯವರೂ ಅವುಗಳನ್ನು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಆರಿಸಿಕೊಳ್ಳುವ ಸ್ಥಳದಲ್ಲಿ ಆತನ ಸನ್ನಿಧಿಯಲ್ಲೇ ಕೊಯಿದು ತಿನ್ನಬೇಕು.",
			"21": "<sup>21</sup> ಅದು ಕುಂಟಾಗಿ, ಕುರುಡಾಗಿ ಇಲ್ಲವೆ ಬೇರೆ ವಿಧದಿಂದ ವಿರೂಪವಾಗಿದ್ದರೆ ಅದನ್ನು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಿಸಬಾರದು.",
			"22": "<sup>22</sup> ಅದನ್ನು ನಿಮ್ಮ ಊರಲ್ಲೇ ತಿನ್ನಬೇಕು. ಶುದ್ಧರೂ ಮತ್ತು ಅಶುದ್ಧರೂ ಜಿಂಕೆ ದುಪ್ಪಿಗಳ ಮಾಂಸವನ್ನು ತಿನ್ನುವ ಪ್ರಕಾರ ಇವುಗಳನ್ನೂ ತಿನ್ನಬಹುದು.",
			"23": "<sup>23</sup> ನೀವು ರಕ್ತವನ್ನು ಮಾತ್ರ ಊಟಮಾಡಬಾರದು; ಅದನ್ನು ನೀರಿನಂತೆ ಭೂಮಿಯ ಮೇಲೆ ಸುರಿದುಬಿಡಬೇಕು."
		},
		"16": {
			"1": "<sup>1</sup> ನೀವು ಚೈತ್ರ ಮಾಸದಲ್ಲಿ ಪಸ್ಕಹಬ್ಬವನ್ನು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೋಸ್ಕರ ಆಚರಿಸಬೇಕು. ಆ ಮಾಸದ ಒಂದು ರಾತ್ರಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮ ಪುರ್ವಜರನ್ನು ಐಗುಪ್ತದೇಶದಿಂದ ಬಿಡಿಸಿದ ನೆನಪಿಗಾಗಿ ಪಸ್ಕಹಬ್ಬವನ್ನು ಆಚರಿಸಬೇಕು.",
			"2": "<sup>2</sup> ನೀವು ಆಡು ಕುರಿಗಳಲ್ಲಾಗಲಿ, ದನಗಳಲ್ಲಾಗಲಿ ಪಸ್ಕದ ಪಶುವನ್ನು ಯೆಹೋವನು ತನ್ನ ಹೆಸರನ್ನು ಸ್ಥಾಪಿಸುವುದಕ್ಕೆ ಆರಿಸಿಕೊಳ್ಳುವ ಸ್ಥಳದಲ್ಲೇ ಆತನಿಗಾಗಿ ವಧಿಸಬೇಕು.",
			"3": "<sup>3</sup> ಅದರೊಂದಿಗೆ ಹುಳಿಹಾಕಿದ ರೊಟ್ಟಿಯನ್ನು ತಿನ್ನಬಾರದು; ಯಾಕೆಂದರೆ ನೀವು (ರೊಟ್ಟಿಗೆ ಹುಳಿಹಾಕುವುದಕ್ಕೆ ಅವಕಾಶವಿಲ್ಲದೆ) ಅವಸರದಿಂದ ಐಗುಪ್ತದೇಶವನ್ನು ಬಿಟ್ಟು ಬಂದಿರಿ. ನಿಮಗೆ ಬಿಡುಗಡೆಯಾದ ಆ ದಿನವು ನಿಮ್ಮ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ನೆನಪಿನಲ್ಲಿಟ್ಟುಕೊಳ್ಳುವಂತೆ ನೀವು ಕಷ್ಟವನ್ನು ಸೂಚಿಸುವ ಹುಳಿಯಿಲ್ಲದ ರೊಟ್ಟಿಗಳನ್ನು ಏಳು ದಿನಗಳ ವರೆಗೂ ತಿನ್ನಬೇಕು.",
			"4": "<sup>4</sup> ಆ ಏಳು ದಿನಗಳು ನಿಮ್ಮ ದೇಶದಲ್ಲೆಲ್ಲಾ ಹುಳಿಬೆರೆತ ಯಾವ ಪದಾರ್ಥವೂ ನಿಮ್ಮ ಬಳಿಯಲ್ಲಿ ಇರಲೇಬಾರದು. ಮೊದಲನೆಯ ದಿನದ ಸಂಜೆಯಲ್ಲಿ ನೀವು ವಧಿಸಿದ ಪಶುಮಾಂಸದಲ್ಲಿ ಸ್ವಲ್ಪವನ್ನೂ ಮರುದಿನದ ವರೆಗೆ ಉಳಿಸಬಾರದು.",
			"5": "<sup>5</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಕೊಡುವ ಯಾವ ಊರಲ್ಲಿಯಾದರೂ ಪಸ್ಕದ ಪಶುವನ್ನು ವಧಿಸದೆ,",
			"6": "<sup>6</sup> ಆತನು ತನ್ನ ಹೆಸರನ್ನು ಸ್ಥಾಪಿಸುವುದಕ್ಕೆ ಆರಿಸಿಕೊಳ್ಳುವ ಸ್ಥಳದಲ್ಲೇ, ನೀವು ಐಗುಪ್ತದೇಶದಿಂದ ಹೊರಟುಬಂದ ಸಮಯ, ಅಂದರೆ ಸಾಯಂಕಾಲದಲ್ಲಿ ಪಸ್ಕದ ಯಜ್ಞವನ್ನು ಸಮರ್ಪಿಸಬೇಕು.",
			"7": "<sup>7</sup> ಆ ಸ್ಥಳದಲ್ಲಿಯೇ ನೀವು ಅದನ್ನು ಅಡಿಗೆಮಾಡಿ ತಿನ್ನಬೇಕು. ಮರುದಿನ ನಿಮ್ಮ ನಿಮ್ಮ ಮನೆಗಳಿಗೆ ನೀವು ಹೊರಟು ಹೋಗಬಹುದು.",
			"8": "<sup>8</sup> ಆರು ದಿನ ನೀವು ಹುಳಿಯಿಲ್ಲದ ರೊಟ್ಟಿಗಳನ್ನು ತಿನ್ನಬೇಕು; ಏಳನೆಯ ದಿನದಲ್ಲಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗಾಗಿ ಸಭೆ ಸೇರಿಬರಬೇಕು; ಆ ದಿನದಲ್ಲಿ ಯಾವ ಕೆಲಸವ ಮಾನ್ನೂಡಬಾರದು.",
			"9": "<sup>9</sup> ಬೆಳೆ ಕೊಯ್ಯುವುದಕ್ಕೆ ಕುಡುಗೋಲು ಹಾಕುವ ಕಾಲ ಮೊದಲುಗೊಂಡು ಏಳು ವಾರಗಳನ್ನು ಎಣಿಸಿ,",
			"10": "<sup>10</sup> ಆ ಏಳು ವಾರಗಳಾದನಂತರ ಪಂಚಾಶತ್ತಮ ದಿನದ ಹಬ್ಬವನ್ನು ಆಚರಿಸಬೇಕು. ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಅನುಗ್ರಹಿಸಿದ ಬೆಳೆಗೆ ತಕ್ಕಷ್ಟು ಕಾಣಿಕೆಯನ್ನು ನೀವು ಆಗ ಆತನಿಗೋಸ್ಕರ ತರಬೇಕು.",
			"11": "<sup>11</sup> ಮತ್ತು ಆತನು ತನ್ನ ಹೆಸರನ್ನು ಸ್ಥಾಪಿಸುವುದಕ್ಕೆ ಆರಿಸಿಕೊಳ್ಳುವ ಸ್ಥಳದಲ್ಲಿ ನೀವೂ ನಿಮ್ಮ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳೂ, ದಾಸ ದಾಸಿಯರೂ, ನಿಮ್ಮ ಊರಲ್ಲಿರುವ ಲೇವಿಯರೂ, ಪರದೇಶದವರೂ, ಅನಾಥರು ಮತ್ತು ವಿಧವೆಯರೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸಂಭ್ರಮಿಸಬೇಕು.",
			"12": "<sup>12</sup> ಐಗುಪ್ತದೇಶದಲ್ಲಿ ದಾಸರಾಗಿದ್ದೆವೆಂಬುದನ್ನು ನೀವು ನೆನಪಿಗೆ ತಂದುಕೊಂಡು ಈ ವಿಧಿಗಳನ್ನು ಅನುಸರಿಸಿ ನಡೆಯಬೇಕು.",
			"13": "<sup>13</sup> ನೀವು ಕಣದ ಕೆಲಸವನ್ನೂ, ದ್ರಾಕ್ಷೆ ಆಲೆಯ ಕೆಲಸವನ್ನೂ ಪೂರೈಸಿ, ಬೆಳೆಯನ್ನು ಮನೆಗೆ ತಂದಾಗ ಏಳು ದಿನಗಳವರೆಗೂ ಪರ್ಣಶಾಲೆಗಳ ಹಬ್ಬವನ್ನು ಆಚರಿಸಬೇಕು.",
			"14": "<sup>14</sup> ಅದರಲ್ಲಿ ನೀವೂ, ನಿಮ್ಮ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳೂ, ದಾಸ ಮತ್ತು ದಾಸಿಯರೂ, ನಿಮ್ಮ ಊರಲ್ಲಿರುವ ಲೇವಿಯರೂ, ಪರದೇಶದವರೂ, ಅನಾಥರು ಮತ್ತು ವಿಧವೆಯರೂ ಸಂಭ್ರಮಿಸಬೇಕು.",
			"15": "<sup>15</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಆರಿಸಿಕೊಳ್ಳುವ ಸ್ಥಳದಲ್ಲಿ ನೀವು ಏಳು ದಿನವೂ ಆ ಜಾತ್ರೆಯನ್ನು ಆಚರಿಸಬೇಕು. ನಿಮ್ಮ ವ್ಯವಸಾಯವನ್ನೂ ಮತ್ತು ಬೇರೆ ಎಲ್ಲಾ ಕೆಲಸಗಳನ್ನೂ ಆತನು ಸಫಲ ಮಾಡಿದ್ದರಿಂದ ನೀವು ಬಹಳ ಆನಂದದಿಂದಿರಬೇಕು.",
			"16": "<sup>16</sup> ಹುಳಿಯಿಲ್ಲದ ರೊಟ್ಟಿಗಳ ಹಬ್ಬದಲ್ಲಿಯೂ, ಪಂಚಾಶತ್ತಮ ದಿನದ ಹಬ್ಬದಲ್ಲಿಯೂ ಮತ್ತು ಪರ್ಣಶಾಲೆಗಳ ಹಬ್ಬದಲ್ಲಿಯೂ ಅಂತು ವರುಷಕ್ಕೆ ಮೂರಾವರ್ತಿ ನಿಮ್ಮಲ್ಲಿರುವ ಗಂಡಸರೆಲ್ಲರೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಆತನು ಆರಿಸಿಕೊಳ್ಳುವ ಸ್ಥಳದಲ್ಲಿ ಕಾಣಿಸಿಕೊಳ್ಳಬೇಕು. ಒಬ್ಬರೂ ಕೈಯಲ್ಲಿ ಕಾಣಿಕೆಯಿಲ್ಲದೆ ಆತನ ಸನ್ನಿಧಿಗೆ ಬರಬಾರದು.",
			"17": "<sup>17</sup> ಪ್ರತಿಯೊಬ್ಬನು ತನಗೆ ಯೆಹೋವನು ಅನುಗ್ರಹಿಸಿದ ಆದಾಯದ ಮೇರೆಗೆ ತನ್ನ ಶಕ್ತ್ಯಾನುಸಾರ ಕೊಡಬೇಕು.",
			"18": "<sup>18</sup> ಪ್ರತಿಯೊಂದು ಕುಲವು ಇರುವ ಪ್ರದೇಶದಲ್ಲಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಕೊಡುವ ಎಲ್ಲಾ ಊರುಗಳಲ್ಲಿಯೂ, ನ್ಯಾಯಾಧಿಪತಿಗಳನ್ನೂ ಮತ್ತು ಅಧಿಕಾರಿಗಳನ್ನೂ ನೀವು ನೇಮಿಸಬೇಕು. ಅವರು ಜನರಿಗೋಸ್ಕರ ನ್ಯಾಯವನ್ನು ವಿಚಾರಿಸಿ ಸರಿಯಾದ ತೀರ್ಪುಕೊಡಬೇಕು.",
			"19": "<sup>19</sup> ನೀವು ನ್ಯಾಯವನ್ನು ಬಿಟ್ಟು ತೀರ್ಮಾನ ಮಾಡಬಾರದು, ಪಕ್ಷಪಾತಮಾಡಬಾರದು, ಲಂಚತೆಗೆದುಕೊಳ್ಳಬಾರದು. ಲಂಚವು ಬುದ್ಧಿವಂತರನ್ನೂ ಕುರುಡರಂತೆ ಮಾಡುತ್ತದೆ; ನಿರಪರಾಧಿಗಳ ನ್ಯಾಯವನ್ನು ಕೆಡಿಸುತ್ತದೆ.",
			"20": "<sup>20</sup> ನೀವು ಕೇವಲ ನ್ಯಾಯವನ್ನೇ ಅನುಸರಿಸಬೇಕು. ಹಾಗೆ ನಡೆದರೆ ನೀವು ಬದುಕಿಕೊಂಡು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಕೊಡುವ ದೇಶವನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವಿರಿ.",
			"21": "<sup>21</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೋಸ್ಕರ ನೀವು ಕಟ್ಟಿಸಿಕೊಳ್ಳುವ ಯಜ್ಞವೇದಿಯ ಬಳಿಯಲ್ಲಿ, ಅಶೇರ ವಿಗ್ರಹಕ್ಕೆ ಸಂಬಂಧಪಟ್ಟ ಯಾವ ಮರದ ಸ್ತಂಭವನ್ನೂ ನೆಡಬಾರದು.",
			"22": "<sup>22</sup> ಪವಿತ್ರ ಕಲ್ಲಿನ ಕಂಬವನ್ನೂ ನಿಲ್ಲಿಸಬಾರದು; ಇದು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ವಿರುದ್ಧವಾದದ್ದು."
		},
		"17": {
			"1": "<sup>1</sup> ಯಾವುದೇ ಕುಂದುಕೊರತೆ ಅಥವಾ ಊನ ಇರುವ ಎತ್ತು ಮತ್ತು ಕುರಿಗಳನ್ನು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಿಸಬಾರದು. ಅಂಥವುಗಳನ್ನು ಸಮರ್ಪಿಸುವುದು ಆತನಿಗೆ ಅಸಹ್ಯವಾಗಿದೆ.",
			"2": "<sup>2</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಕೊಡುವ ಯಾವುದೇ ಊರಿನಲ್ಲಿ, ಯಾವ ಪುರುಷನಾಗಲಿ ಅಥವಾ ಸ್ತ್ರೀಯಾಗಲಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ನಿಬಂಧನೆಯನ್ನು ಮೀರಿ,",
			"3": "<sup>3</sup> ಆತನಿಂದ ದೂರಹೋಗಿ ಅನ್ಯದೇವರುಗಳನ್ನು ಪೂಜಿಸಬಾರದೆಂದು ಮತ್ತು ಆತನು ನಿಷೇಧಿಸಿರುವ ಸೂರ್ಯ, ಚಂದ್ರ ಅಥವಾ ನಕ್ಷತ್ರಮಂಡಲವನ್ನಾಗಲಿ ಸೇವಿಸಿ, ಪೂಜಿಸಿ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ಅಸಹ್ಯವಾದದ್ದನ್ನು ನಡಿಸಿದರೆ,",
			"4": "<sup>4</sup> ನೀವು ಆ ಸಂಗತಿಯನ್ನು ಕೇಳಿದಾಗ ಸೂಕ್ಷ್ಮವಾಗಿ ವಿಚಾರಣೆಯನ್ನು ಮಾಡಬೇಕು. ಆಗ ಇಸ್ರಾಯೇಲರಲ್ಲಿ ಯಾರಿಂದಾದರೂ ಆ ನಿಷಿದ್ಧಕಾರ್ಯವು ನಡೆದದ್ದು ನಿಜವೆಂದು ತಿಳಿದು ಬಂದರೆ,",
			"5": "<sup>5</sup> ಆಗ ಆ ಪುರುಷನನ್ನಾಗಲಿ ಅಥವಾ ಸ್ತ್ರೀಯನ್ನಾಗಲಿ ಹಿಡಿದು ಊರ ಹೊರಗೆ ತಂದು ಕಲ್ಲೆಸೆದು ಕೊಲ್ಲಬೇಕು.",
			"6": "<sup>6</sup> ಒಬ್ಬನೇ ಒಬ್ಬನ ಸಾಕ್ಷಿಯ ಮಾತಿನ ಮೇಲೆ ಯಾರಿಗೂ ಮರಣಶಿಕ್ಷೆಯಾಗಬಾರದು. ಮರಣಶಿಕ್ಷೆಯನ್ನು ವಿಧಿಸುವುದಕ್ಕೆ ಇಬ್ಬರು ಇಲ್ಲವೆ ಮೂವರ ಸಾಕ್ಷಿಗಳು ಬೇಕು.",
			"7": "<sup>7</sup> ಅಪರಾಧಿಯನ್ನು ಕೊಲ್ಲುವುದಕ್ಕೆ ಸಾಕ್ಷಿಗಳೇ ಮೊದಲು ಕಲ್ಲನ್ನು ಹಾಕಬೇಕು; ತರುವಾಯ ಜನರೆಲ್ಲರೂ ಹಾಕಲಿ. ಹೀಗೆ ಮಾಡಿ ನೀವು ಆ ದುಷ್ಟತ್ವವನ್ನು ನಿಮ್ಮ ಮಧ್ಯದಿಂದ ತೆಗೆದುಹಾಕಬೇಕು.",
			"8": "<sup>8</sup> ವಿಧವಿಧವಾದ ಕೊಲೆ, ನ್ಯಾಯ, ಹೊಡೆದಾಟ ಇವುಗಳ ವಿಷಯದಲ್ಲಿ ಅನುಮಾನ ಅಥವಾ ಚರ್ಚೆಗಳು ನಿಮ್ಮ ಊರಿನಲ್ಲಿ ಉಂಟಾದಾಗ, ನ್ಯಾಯ ತೀರಿಸುವುದು ಕಷ್ಟವಾದರೆ, ನೀವು ಊರಿನಿಂದ ಹೊರಟು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಆರಿಸಿಕೊಳ್ಳುವ ಸ್ಥಳಕ್ಕೆ ಹೋಗಬೇಕು.",
			"9": "<sup>9</sup> ಅಲ್ಲಿ ಯಾಜಕಸೇವೆಯನ್ನು ಮಾಡುವ ಲೇವಿಯರ ಮತ್ತು ಆ ಕಾಲದ ನ್ಯಾಯಾಧಿಪತಿಯ ಬಳಿಗೆ ಹೋಗಿ ವಿಚಾರಿಸಲು ನೀವು ಕೊಡಬೇಕಾದ ತೀರ್ಪನ್ನು ಅವರು ತಿಳಿಸುವರು.",
			"10": "<sup>10</sup> ಯೆಹೋವನು ಆರಿಸಿಕೊಂಡ ಆ ಸ್ಥಳದಿಂದ ಅವರು ನಿಮಗೆ ತಿಳಿಸುವ ಮಾತಿನ ಪ್ರಕಾರ ನೀವು ಮಾಡಬೇಕು. ಅವರು ಬೋಧಿಸುವುದನ್ನೇ ನೀವು ಅನುಸರಿಸಬೇಕು.",
			"11": "<sup>11</sup> ಅವರು ಕಲಿಸಿಕೊಡುವ ಆಜ್ಞೆಗಳನ್ನು ಅನುಸರಿಸಿ, ಅವರು ಹೇಳಿಕೊಡುವ ತೀರ್ಪಿನಂತೆ ನೀವು ಮಾಡಬೇಕು; ಅವರು ತಿಳಿಸುವ ಮಾತನ್ನು ಬಿಟ್ಟು ಎಡಕ್ಕಾದರೂ ಬಲಕ್ಕಾದರೂ ತಿರುಗಬಾರದು.",
			"12": "<sup>12</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸೇವೆ ಮಾಡುವ ಯಾಜಕರ ಅಥವಾ ನ್ಯಾಯಾಧಿಪತಿಯ ಮಾತನ್ನು ಯಾರು ಅಹಂಕಾರದಿಂದ ನಿರಾಕರಿಸುತ್ತಾರೋ ಅವರಿಗೆ ಮರಣಶಿಕ್ಷೆಯಾಗಬೇಕು. ಹೀಗೆ ಮಾಡಿ ನೀವು ಆ ದುಷ್ಟತ್ವವನ್ನು ಇಸ್ರಾಯೇಲರ ಮಧ್ಯದಿಂದ ತೆಗೆದುಹಾಕಬೇಕು.",
			"13": "<sup>13</sup> ಆಗ ಜನರೆಲ್ಲರೂ ಕೇಳಿ ಭಯಪಟ್ಟು ಇನ್ನು ಅಹಂಕಾರದಿಂದ ನಡೆಯದೆ ಇರುವರು.",
			"14": "<sup>14</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಕೊಡುವ ದೇಶವನ್ನು ನೀವು ಸೇರಿ ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡು ಅದರಲ್ಲಿ ವಾಸವಾಗಿರುವಾಗ, “ಸುತ್ತಲಿರುವ ಎಲ್ಲಾ ಜನಾಂಗಗಳಂತೆ ನಾವೂ ಅರಸನನ್ನು ನೇಮಿಸಿಕೊಳ್ಳೋಣ” ಎಂದು ಹೇಳಿಕೊಳ್ಳುವ ಸಂದರ್ಭದಲ್ಲಿ,",
			"15": "<sup>15</sup> ನೀವು ಅಗತ್ಯವಾಗಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಆರಿಸಿಕೊಂಡ ವ್ಯಕ್ತಿಯನ್ನೇ ನೇಮಿಸಿಕೊಳ್ಳಬೇಕು. ನಿಮ್ಮ ಬಂಧುಗಳಾದ ಸ್ವಜನರಲ್ಲೇ ಒಬ್ಬನನ್ನು ಅರಸನನ್ನಾಗಿ ನೇಮಿಸಿಕೊಳ್ಳಬೇಕೇ ಹೊರತು ಅನ್ಯದೇಶದವನನ್ನು ನೇಮಿಸಬಾರದು.",
			"16": "<sup>16</sup> ಅವನು ಕುದುರೆಯ ದಂಡನ್ನು ಕೂಡಿಸಿಕೊಳ್ಳಬಾರದು. ಕುದುರೆಗಳನ್ನು ಕೊಂಡುಕೊಳ್ಳುವುದಕ್ಕಾಗಿ ತನ್ನ ಪ್ರಜೆಗಳನ್ನು ಐಗುಪ್ತದೇಶಕ್ಕೆ ಹೋಗಗೊಡಿಸಬಾರದು. ನೀವು ಇನ್ನು ಆ ದೇಶಕ್ಕೆ ಹೋಗಲೇಬಾರದು ಎಂದು ಯೆಹೋವನು ಆಜ್ಞಾಪಿಸಿದ್ದಾನಲ್ಲಾ.",
			"17": "<sup>17</sup> ಅವನು ಅನೇಕ ಸ್ತ್ರೀಯರನ್ನು ಮದುವೆ ಮಾಡಿಕೊಳ್ಳಬಾರದು; ಮಾಡಿಕೊಂಡರೆ ಅವನ ಮನಸ್ಸು ಯೆಹೋವನ ಕಡೆಯಿಂದ ತಿರುಗುವುದಕ್ಕೆ ಅವಕಾಶವಾಗುವುದು. ಅವನು ಹೆಚ್ಚು ಬೆಳ್ಳಿಬಂಗಾರವನ್ನು ಕೂಡಿಸಿಟ್ಟುಕೊಳ್ಳಬಾರದು.",
			"18": "<sup>18</sup> ಅವನು ರಾಜ್ಯಾಧಿಕಾರಕ್ಕೆ ಬಂದಾಗ ಯಾಜಕ ಸೇವೆಮಾಡುವ ಲೇವಿಯರ ವಶದಲ್ಲಿರುವ ಈ ಧರ್ಮಶಾಸ್ತ್ರಗ್ರಂಥದ ಒಂದು ಪ್ರತಿಯನ್ನು ತನಗೋಸ್ಕರ ಪುಸ್ತಕರೂಪವಾಗಿ ಬರೆಯಿಸಿಕೊಳ್ಳಬೇಕು.",
			"19": "<sup>19</sup> ಅವನು ತನ್ನ ದೇವರಾದ ಯೆಹೋವನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವನಾಗಿ ಆ ಧರ್ಮಶಾಸ್ತ್ರದ ಎಲ್ಲಾ ವಾಕ್ಯಗಳನ್ನೂ ಮತ್ತು ವಿಧಿಗಳನ್ನೂ ಅನುಸರಿಸುವುದಕ್ಕೆ ಅಭ್ಯಾಸಮಾಡಿಕೊಳ್ಳುವಂತೆ ಆ ಗ್ರಂಥವು ಅವನ ಬಳಿಯಲ್ಲೇ ಇರಬೇಕು. ಅವನು ತನ್ನ ಜೀವಮಾನದ ದಿನಗಳೆಲ್ಲಾ ಅದನ್ನು ಓದಿಕೊಳ್ಳುತ್ತಾ ಇರಬೇಕು.",
			"20": "<sup>20</sup> ಅವನೂ ಮತ್ತು ಅವನ ಸಂತತಿಯವರೂ ಬಹುಕಾಲ ರಾಜ್ಯಾಧಿಕಾರವನ್ನು ನಡಿಸುತ್ತಾ, ಇಸ್ರಾಯೇಲರ ನಡುವೆ ಬಾಳುವಂತೆಯೂ ಮತ್ತು ಅವನು ಮದದಿಂದ ತನ್ನ ಸ್ವದೇಶದವರನ್ನು ಹಿಯಾಳಿಸದಂತೆಯೂ ಅವನು ಯೆಹೋವನ ಆಜ್ಞೆಗಳನ್ನು ಅನುಸರಿಸಬೇಕು. ಅದನ್ನು ಮೀರಿ ಎಡಕ್ಕಾಗಲಿ ಬಲಕ್ಕಾಗಲಿ ತಿರುಗಿಕೊಳ್ಳಬಾರದು."
		},
		"18": {
			"1": "<sup>1</sup> ಯಾಜಕಸೇವೆ ಮಾಡುವ ಲೇವಿಯರಿಗಾಗಲಿ ಅಥವಾ ಲೇವಿಯ ಕುಲದವರಲ್ಲಿ ಯಾರಿಗೇ ಆಗಲಿ ಇತರ ಇಸ್ರಾಯೇಲರೊಡನೆ ಸ್ವಂತಕ್ಕಾಗಿ ಪಾಲಾಗಲಿ ಅಥವಾ ಸ್ವಾಸ್ತ್ಯವಾಗಲಿ ದೊರೆಯುವುದಿಲ್ಲ. ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಿಸಲ್ಪಡುವ ಹೋಮದ್ರವ್ಯಗಳೂ ಆತನಿಗೋಸ್ಕರ ಪ್ರತ್ಯೇಕಿಸಲ್ಪಟ್ಟದ್ದೆಲ್ಲವೂ ಅವರಿಗೆ ಜೀವನಾಧಾರ.",
			"2": "<sup>2</sup> ಸ್ವದೇಶಸ್ಥರೊಂದಿಗೂ ಅವರಿಗೆ ಸ್ವಾಸ್ತ್ಯವು ದೊರೆಯುವುದಿಲ್ಲ; ಯೆಹೋವನು ಅವರಿಗೆ ಹೇಳಿದಂತೆ ತಾನೇ ಅವರಿಗೆ ಸ್ವಾಸ್ತ್ಯವಾಗಿರುವನು.",
			"3": "<sup>3</sup> ಜನರ ಕಡೆಯಿಂದ ಯಾಜಕರು ಹೊಂದಬೇಕಾದವುಗಳು ಯಾವುವೆಂದರೆ: ದನಗಳಲ್ಲಿಯಾಗಲಿ, ಆಡು ಮತ್ತು ಕುರಿಗಳಲ್ಲಿಯಾಗಲಿ ಪಶುವನ್ನು ಕೊಯಿದು ಯಜ್ಞಮಾಡುವವರೆಲ್ಲರೂ ಅದರ ಮುಂದೊಡೆಯನ್ನೂ, ಎರಡು ದವಡೆಗಳನ್ನೂ, ಕೋಷ್ಠವನ್ನೂ (ಒಳಭಾಗವನ್ನು) ಯಾಜಕರಿಗೆ ಕೊಡಬೇಕು.",
			"4": "<sup>4</sup> ಧಾನ್ಯ, ದ್ರಾಕ್ಷಾರಸ ಮತ್ತು ಎಣ್ಣೆ ಇವುಗಳ ಪ್ರಥಮಫಲಗಳನ್ನು ಹಾಗೂ ಮೊದಲನೆಯ ಸಾರಿ ಕತ್ತರಿಸುವ ಕುರಿಗಳ ಉಣ್ಣೆಯನ್ನೂ ಅವರಿಗೆ ಕೊಡಬೇಕು.",
			"5": "<sup>5</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮ ಎಲ್ಲಾ ಕುಲಗಳಲ್ಲಿ ಇವರನ್ನೂ ಮತ್ತು ಇವರ ತರುವಾಯ ಇವರ ಸಂತತಿಯವರನ್ನೂ ತನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಶಾಶ್ವತವಾಗಿ ಸೇವೆ ಮಾಡುವುದಕ್ಕೆ ತಾನೇ ನೇಮಿಸಿಕೊಂಡನಲ್ಲಾ.",
			"6": "<sup>6</sup> ಇಸ್ರಾಯೇಲರ ಯಾವುದಾದರೂ ಒಂದು ಊರಲ್ಲಿ ಇಳಿದುಕೊಂಡಿರವ ಲೇವಿಯನು ಸ್ವಂತ ಮನಸ್ಸಿನಿಂದ ಆ ಊರನ್ನು ಬಿಟ್ಟು, ಯೆಹೋವನು ಆರಿಸಿಕೊಂಡ ಪವಿತ್ರ ಸ್ಥಳಕ್ಕೆ ಬಂದು,",
			"7": "<sup>7</sup> ಅಲ್ಲೇ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸೇವೆಮಾಡುವ ತನ್ನ ಸ್ವಕುಲದವರಂತೆ ತನ್ನ ದೇವರಾದ ಯೆಹೋವನ ಹೆಸರನ್ನು ಹೇಳಿ ತಾನೂ ಸೇವೆ ನಡಿಸಿದರೆ,",
			"8": "<sup>8</sup> ಅವನ ಕೈಯಲ್ಲಿ ಪಿತ್ರಾರ್ಜಿತ ಸೊತ್ತನ್ನು ಮಾರಿದ ಹಣವಿದ್ದರೂ, ಇತರ ಲೇವಿಯರೊಂದಿಗೆ ಸಮವಾಗಿ ಭೋಗಿಸುವುದಕ್ಕೆ ಬಾಧ್ಯನಾಗಿರುವನು.",
			"9": "<sup>9</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಕೊಡುವ ದೇಶದಲ್ಲಿ ನೀವು ಸೇರಿದಾಗ ಅಲ್ಲಿರುವ ಜನಗಳು ಮಾಡುವ ಅಸಹ್ಯವಾದ ಕೆಲಸಗಳನ್ನುನೀವು ಅನುಸರಿಸಲೇಬಾರದು.",
			"10": "<sup>10</sup> ಮಕ್ಕಳನ್ನು ಬಲಿಕೊಡುವವರು, ಕಣಿಹೇಳುವವರು, ಶಕುನನೋಡುವವರು, ಯಂತ್ರ ಮಂತ್ರಗಳನ್ನು ಮಾಡುವವರು,",
			"11": "<sup>11</sup> ಮಾಟಗಾರರು, ತಂತ್ರಗಾರರು, ಸತ್ತವರಲ್ಲಿ ವಿಚಾರಿಸುವವರು, ಬೇತಾಳ, ಪ್ರೇತ, ಭೂತ ಎಂದು ಪೂಜಿಸುವವರು ಯಾರೂ ನಿಮ್ಮಲ್ಲಿ ಇರಬಾರದು.",
			"12": "<sup>12</sup> ಇಂಥ ಕೆಲಸಗಳನ್ನು ನಡಿಸುವವರನ್ನು ಯೆಹೋವನು ಸಹಿಸುವುದಿಲ್ಲ; ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಇಂಥ ಹೇಯಕೃತ್ಯಗಳನ್ನು ಮಾಡುವ ಆ ಜನಾಂಗಗಳನ್ನು ನಿಮ್ಮಿಂದ ಹೊರ ಹೋಗುವಂತೆ ಮಾಡಿದ್ದಾನೆ.",
			"13": "<sup>13</sup> ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಮುಂದೆ ನಿರ್ದೊಷಿಗಳಾಗಿರಬೇಕು.",
			"14": "<sup>14</sup> ನೀವು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವ ದೇಶದ ಜನಾಂಗಗಳು ಶಕುನಗಳನ್ನು ನೋಡುತ್ತಾರೆ ಮತ್ತು ಯಂತ್ರ ಮಂತ್ರಗಳನ್ನು ಮಾಡುತ್ತಾರೆ. ನೀವಾದರೋ ಹಾಗೆ ಮಾಡುವುದಕ್ಕೆ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಂದ ಅಪ್ಪಣೆಹೊಂದಲಿಲ್ಲ.",
			"15": "<sup>15</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮ ಸಹೋದರರಲ್ಲಿ ನನ್ನಂಥ ಪ್ರವಾದಿಯನ್ನು ನಿಮಗೆ ಏರ್ಪಡಿಸುವನು; ಅವನಿಗೇ ನೀವು ಕಿವಿಗೊಡಬೇಕು.",
			"16": "<sup>16</sup> ಹೋರೇಬಿನಲ್ಲಿ ಸಭೆಕೂಡಿದ ದಿನದಲ್ಲಿ ನೀವು, “ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಸ್ವರವನ್ನು ಇನ್ನು ಕೇಳುವುದಕ್ಕಾಗಲಿ ಅಥವಾ ಈ ಘೋರವಾದ ಅಗ್ನಿಜ್ವಾಲೆಯನ್ನು ಇನ್ನು ನೋಡವುದಕ್ಕಾಗಲಿ ನಮಗೆ ಮನಸ್ಸಿಲ್ಲ, ಹಾಗೇನಾದರು ಕೇಳಿ ನೋಡಿದರೆ ಸತ್ತು ಹೋಗುವೆವು” ಎಂದು ನೀವು ಹೇಳಿದಿರಷ್ಟೆ.",
			"17": "<sup>17</sup> ಆಗ ಯೆಹೋವನು ನನಗೆ, “ಇವರು ಹೇಳಿದ ಮಾತು ಒಳ್ಳೆಯದೇ;",
			"18": "<sup>18</sup> ಇವರ ಸ್ವದೇಶದವರಲ್ಲಿ ನಿನ್ನಂಥ ಪ್ರವಾದಿಯನ್ನು ಅವರಿಗೋಸ್ಕರ ಏರ್ಪಡಿಸುವೆನು; ಅವನ ಬಾಯಿಂದ ನನ್ನ ಮಾತುಗಳನ್ನು ನುಡಿಸುವೆನು; ನಾನು ಅವನಿಗೆ ಆಜ್ಞಾಪಿಸಿದ್ದನ್ನೆಲ್ಲಾ ಅವನು ಅವರಿಗೆ ತಿಳಿಸುವನು.",
			"19": "<sup>19</sup> ಅವನು ನನ್ನ ಹೆಸರಿನಲ್ಲಿ ಹೇಳಿದ ಮಾತುಗಳಿಗೆ ಯಾರು ಕಿವಿಗೊಡುವದಿಲ್ಲವೋ ಅಂಥವರನ್ನು ನಾನು ಶಿಕ್ಷಿಸುವೆನು.",
			"20": "<sup>20</sup> ಆದರೆ ಯಾವ ಪ್ರವಾದಿ ನನ್ನಿಂದ ಅಧಿಕಾರಹೊಂದದೆ, ನಾನು ಪ್ರೇರಣೆಮಾಡದ ಮಾತುಗಳನ್ನು ಯೆಹೋವನ ಮಾತೆಂದು ಹೇಳಿ ಜನರಿಗೆ ತಿಳಿಸುವನೋ, ಇಲ್ಲವೆ ಇತರ ದೇವರುಗಳ ಹೆಸರಿನಲ್ಲಿ ಮಾತನಾಡುವನೋ ಅವನಿಗೆ ಮರಣಶಿಕ್ಷೆಯಾಗಬೇಕೆಂದು ಹೇಳಿದನು.",
			"21": "<sup>21</sup> ‘ಪ್ರವಾದಿಯು ಹೇಳಿದ ಮಾತು ಯೆಹೋವನ ಮಾತಲ್ಲವೆಂದು ತಿಳಿದುಕೊಳ್ಳುವುದು ಹೇಗೆ’ ಅಂದುಕೊಳ್ಳುತ್ತೀರೋ?",
			"22": "<sup>22</sup> ಪ್ರವಾದಿಯು “ಇದು ಯೆಹೋವನ ನುಡಿ” ಎಂದು ಹೇಳಿ, ಆತನು ತಿಳಿಸಿದ ಸಂಗತಿಯೂ ನಡೆಯದೆ ಹೋದರೆ, ಅವನ ಮಾತು ಯೆಹೋವನದಲ್ಲವೆಂದು ನೀವು ತಿಳಿದುಕೊಳ್ಳಬೇಕು. ಅವನು ನನ್ನ ಅಪ್ಪಣೆ ಇಲ್ಲದೆ ಮಾತನಾಡಿದವನು ಮತ್ತು ನೀವು ಅವನಿಗೆ ಹೆದರಬಾರದು” ಎಂದು ಹೇಳಿದನು."
		},
		"19": {
			"1": "<sup>1</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಕೊಡುವ ದೇಶದ ಜನಾಂಗಗಳನ್ನು ಆತನು ನಾಶಮಾಡಿ ಬಿಟ್ಟ ತರುವಾಯ, ನೀವು ಆ ದೇಶವನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡು ಅವರ ಗ್ರಾಮಗಳಲ್ಲಿಯೂ,",
			"2": "<sup>2</sup> ಮನೆಗಳಲ್ಲಿಯೂ ವಾಸವಾಗಿರುವಾಗ, ಆ ನಿಮ್ಮ ದೇಶದ ಮಧ್ಯದಲ್ಲಿ ನೀವು ಮೂರು ಆಶ್ರಯ ನಗರಗಳನ್ನು ಗೊತ್ತುಮಾಡಬೇಕು.",
			"3": "<sup>3</sup> ನರಹತ್ಯಮಾಡಿದವರು ಆ ನಗರಗಳಿಗೆ ಓಡಿಹೊಗುವುದಕ್ಕೆ ಅನುಕೂಲವಾಗುವಂತೆ ನೀವು ಅವುಗಳ ದಾರಿಗಳನ್ನು ಸರಿಪಡಿಸಬೇಕು. ಮತ್ತು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಸ್ವದೇಶವಾಗುವುದಕ್ಕೆ ಕೊಡುವ ಆ ದೇಶವನ್ನು ಮೂರು ಭಾಗ ಮಾಡಬೇಕು.",
			"4": "<sup>4</sup> ಆ ನಗರಗಳ ವಿವರ ಹೀಗಿದೆ, ಯಾವನಾದರೂ ದ್ವೇಷವಿಲ್ಲದೆ ಕೈತಪ್ಪಿ ಮತ್ತೊಬ್ಬನನ್ನು ಕೊಂದರೆ ಅವನು ಆ ಪಟ್ಟಣಗಳಲ್ಲಿ ಒಂದಕ್ಕೆ ಓಡಿಹೋಗಿ ಬದುಕಿಕೊಳ್ಳಬಹುದು.",
			"5": "<sup>5</sup> ಉದಾಹರಣೆ, ಒಬ್ಬ ಮನುಷ್ಯನು ಕಟ್ಟಿಗೆಯನ್ನು ಕಡಿಯಬೇಕೆಂದು ಮತ್ತೊಬ್ಬನ ಜೊತೆಯಲ್ಲಿ ಅಡವಿಗೆ ಹೋಗಿ, ಅಲ್ಲಿ ಒಂದು ಮರವನ್ನು ಕಡಿಯಬೇಕೆಂದು ಕೊಡಲಿಯಿಂದ ಹೊಡೆಯುತ್ತಿರುವಾಗ, ಕೊಡಲಿಯು ಕಾವಿನಿಂದ ಜಾರಿ ಆ ಮತ್ತೊಬ್ಬನಿಗೆ ತಗಲಿದ್ದರಿಂದ ಅವನು ಸತ್ತರೆ, ಹೊಡೆದವನು ಆ ಪಟ್ಟಣಗಳಲ್ಲಿ ಒಂದಕ್ಕೆ ಓಡಿಹೋಗಿ ಬದುಕಿಕೊಳ್ಳಬಹುದು.",
			"6": "<sup>6</sup> ಸತ್ತವನ ಮೇಲೆ ಅವನಿಗೆ ಮೊದಲು ದ್ವೇಷವಿಲ್ಲದ್ದರಿಂದ ಮತ್ತು ಅವನನ್ನು ಸಾಯಿಸುವ ಉದ್ದೇಶವಿಲ್ಲದ ಕಾರಣ ಮರಣಶಿಕ್ಷೆಗೆ ಪಾತ್ರನಲ್ಲ. ಆದರೂ ಹತ್ಯಮಾಡಿದವನಿಗೆ ಮುಯ್ಯಿತೀರಿಸುವ ಹಂಗುಳ್ಳ ಸಮೀಪ ಬಂಧುವು ಇನ್ನು ಕೋಪದಿಂದ ಉರಿಯುತ್ತಿರುವಾಗಲೇ ಅವನನ್ನು ಹಿಂದಟ್ಟಿ ಹೋಗಬವುದು ಮತ್ತು ಮಾರ್ಗ ದೂರವಾಗಿರುವ ಪಕ್ಷಕ್ಕೆ ಅವನನ್ನು ಹಿಡಿದು ಕೊಂದು ಹಾಕಿಬಿಡಬಹುದು.",
			"7": "<sup>7</sup> ಈ ಕಾರಣದಿಂದ ನೀವು ಮೂರು ಆಶ್ರಯ ನಗರಗಳನ್ನು ಗೊತ್ತುಮಾಡಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿದ್ದೇನೆ.",
			"8": "<sup>8</sup> ಯೆಹೋವನು ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ವಾಗ್ದಾನಮಾಡಿದಂತೆ ನಿಮ್ಮ ರಾಜ್ಯವನ್ನು ವಿಸ್ತರಿಸಿ, ತಾನು ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ಸೂಚಿಸಿದ ಪ್ರದೇಶವನ್ನೆಲ್ಲಾ ಕೊಡುವಾಗ, ಇನ್ನೂ ಮೂರು ನಗರಗಳನ್ನು ನಿಮಗಾಗಿ ಗೊತ್ತುಮಾಡಬೇಕು. ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ಈ ಧರ್ಮೋಪದೇಶವನ್ನೆಲ್ಲಾ ನೀವು ಅನುಸರಿಸುವವರಾಗಿ,",
			"9": "<sup>9</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಪ್ರೀತಿಸಿ, ಆತನು ಹೇಳುವ ಮಾರ್ಗದಲ್ಲೇ ಯಾವಾಗಲೂ ನಡೆಯುವುದಾದರೆ, ಆಗ ಯೆಹೋವನು ಈ ಮೂರು ನಗರಗಳನ್ನು ನಿಮಗೆ ಕೊಡುವನು.",
			"10": "<sup>10</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಸ್ವದೇಶವಾಗುವುದಕ್ಕೆ ಕೊಡುವ ದೇಶದಲ್ಲಿ ನಿರಪರಾಧಿಗೆ ಮರಣಶಿಕ್ಷೆಯಾಗದಂತೆ; ಆ ದೋಷವು ನಿಮ್ಮದಾಗದಂತೆ ನೀವು ಹೀಗೆ ಮಾಡಬೇಕು.",
			"11": "<sup>11</sup> ಆದರೆ ಯಾವನಾದರೂ ಒಬ್ಬನಲ್ಲಿ ದ್ವೇಷವನ್ನೇ ಇಟ್ಟು ಸಮಯನೋಡಿಕೊಂಡು, ಅವನ ಮೇಲೆ ಬಿದ್ದು ಅವನನ್ನು ಹೊಡೆದು ಕೊಂದ ತರುವಾಯ ಈ ಪಟ್ಟಣಗಳಲ್ಲಿ",
			"12": "<sup>12</sup> ಒಂದಕ್ಕೆ ಓಡಿಹೋದರೆ, ಅವನ ಊರಿನ ಹಿರಿಯರು ಅವನನ್ನು ಅಲ್ಲಿಂದ ಹಿಡಿದು ತರಿಸಿ ಅವನಿಗೆ ಮರಣಶಿಕ್ಷೆಯಾಗುವಂತೆ ಮುಯ್ಯಿತೀರಿಸುವ ಹಂಗುಳ್ಳವನ ಕೈಗೆ ಒಪ್ಪಿಸಬೇಕು.",
			"13": "<sup>13</sup> ನೀವು ಅವನನ್ನು ಕನಿಕರಿಸಬಾರದು. ನಿಮಗೆ ಶುಭವುಂಟಾಗುವಂತೆ ನಿರಪರಾಧಿಯ ಪ್ರಾಣವನ್ನು ತೆಗೆದವನನ್ನು ಇಸ್ರಾಯೇಲರಲ್ಲಿ ಉಳಿಯದಂತೆ ಮಾಡಬೇಕು.",
			"14": "<sup>14</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಸ್ವದೇಶವಾಗುವುದಕ್ಕೆ ಕೊಡುವ ದೇಶದಲ್ಲಿ ನಿಮ್ಮ ಪಾಲಿಗೆ ಬಂದ ಸ್ವಾಸ್ತ್ಯದಲ್ಲಿ ನೆರೆಯವನ ಭೂಮಿಯ ಹಿಂದಿನ ಕಾಲದ ಗಡಿಯನ್ನು ಒತ್ತಬಾರದು.",
			"15": "<sup>15</sup> ಯಾವ ದೋಷದ ಅಥವಾ ಅಪರಾಧದ ವಿಷಯದಲ್ಲೂ ಒಬ್ಬನೇ ಒಬ್ಬನ ಸಾಕ್ಷಿಯನ್ನು ನೀವು ಅಂಗೀಕರಿಸಬಾರದು. ನಡೆದ ಸಂಗತಿ ರುಜುವಾತಾಗುವುದಕ್ಕೆ ಇಬ್ಬರ ಇಲ್ಲವೆ ಮೂವರ ಸಾಕ್ಷಿಗಳು ಬೇಕು.",
			"16": "<sup>16</sup> ಯಾವನಾದರೂ ನ್ಯಾಯವಿರುದ್ಧವಾಗಿ ಬಂದು ಮತ್ತೊಬ್ಬನನ್ನು ಅಪರಾಧಿ ಎಂದು ಸಾಕ್ಷಿಹೇಳಿದರೆ,",
			"17": "<sup>17</sup> ವ್ಯಾಜ್ಯವಾಡುವ ಆ ಇಬ್ಬರೂ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಯಾಜಕರ ಮುಂದೆಯೂ, ಆಗ ಇರುವ ನ್ಯಾಯಾಧಿಪತಿಗಳ ಮುಂದೆಯೂ ನಿಲ್ಲಬೇಕು.",
			"18": "<sup>18</sup> ನ್ಯಾಯಾಧಿಪತಿಗಳು ಚೆನ್ನಾಗಿ ವಿಚಾರಣೆ ಮಾಡಿದ ಮೇಲೆ ಆ ಮನುಷ್ಯನ ಸಾಕ್ಷಿ ಸುಳ್ಳೆಂದು ತೋರಿಬಂದರೆ,",
			"19": "<sup>19</sup> ಅವನು ಆ ಮತ್ತೊಬ್ಬನಿಗೆ ಕೊಡಿಸಬೇಕೆಂದಿದ್ದ ಶಿಕ್ಷೆಯನ್ನು ಅವನಿಗೆ ವಿಧಿಸಬೇಕು. ಹೀಗೆ ನೀವು ಆ ದುಷ್ಟತ್ವವನ್ನು ನಿಮ್ಮ ಮಧ್ಯದೊಳಗಿಂದ ತೆಗೆದುಹಾಕಬೇಕು.",
			"20": "<sup>20</sup> ಉಳಿದವರು ಆ ಸಂಗತಿಯನ್ನು ಕೇಳಿ ಭಯಪಟ್ಟು ಇನ್ನು ಮುಂದೆ ಅಂಥ ದುಷ್ಟತ್ವವನ್ನು ನಡೆಸುವುದಿಲ್ಲ.",
			"21": "<sup>21</sup> ನೀವು ಅಂಥವರನ್ನು ಕನಿಕರಿಸಬಾರದು; ಪ್ರಾಣಕ್ಕೆ ಪ್ರತಿಯಾಗಿ ಪ್ರಾಣವನ್ನೂ, ಕಣ್ಣಿಗೆ ಪ್ರತಿಯಾಗಿ ಕಣ್ಣನ್ನೂ, ಹಲ್ಲಿಗೆ ಪ್ರತಿಯಾಗಿ ಹಲ್ಲನ್ನೂ, ಕೈಗೆ ಪ್ರತಿಯಾಗಿ ಕೈಯನ್ನೂ ಮತ್ತು ಕಾಲಿಗೆ ಪ್ರತಿಯಾಗಿ ಕಾಲನ್ನೂ ತೆಗೆದುಬಿಡಬೇಕು."
		},
		"20": {
			"1": "<sup>1</sup> ನೀವು ನಿಮ್ಮ ಶತ್ರುಗಳಿಗೆ ವಿರುದ್ಧವಾಗಿ ಯುದ್ಧಕ್ಕೆ ಹೊರಟಾಗ ಅವರ ಹತ್ತಿರ ಕುದುರೆಗಳೂ, ರಥಗಳೂ ಮತ್ತು ನಿಮಗಿಂತ ಹೆಚ್ಚಾದ ಕಾಲ್ಬಲವೂ ಇರುವುದನ್ನು ಕಂಡರೆ ಹೆದರಬಾರದು. ಏಕೆಂದರೆ ಐಗುಪ್ತದೇಶದಿಂದ ನಿಮ್ಮನ್ನು ಬಿಡಿಸಿದ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮ ಕಡೆ ಇದ್ದಾನಲ್ಲಾ.",
			"2": "<sup>2</sup> ನಿಮಗೆ ಯುದ್ಧ ಸಮೀಪಿಸಿದಾಗ ಯಾಜಕನು ಮುಂದೆ ಬಂದು ಭಟರ ಸಂಗಡ ಮಾತನಾಡಿ ಅವರಿಗೆ,",
			"3": "<sup>3</sup> “ಇಸ್ರಾಯೇಲರೇ, ಕೇಳಿರಿ; ನೀವು ಈಗ ಶತ್ರುಗಳೊಡನೆ ಯುದ್ಧಮಾಡುವ ಕಾಲ ಸಮೀಪಿಸಿತಷ್ಟೆ. ನೀವು ಎದೆಗುಂದಬಾರದು, ದಿಗಿಲುಪಡಬಾರದು, ನಡುಗಬಾರದೂ ಮತ್ತು ಅವರಿಗೆ ಹೆದರಲೂಬಾರದು.",
			"4": "<sup>4</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ತಾನೇ ನಿಮ್ಮ ಸಂಗಡ ಇದ್ದು ಶತ್ರುಗಳೊಡನೆ ನಿಮಗೋಸ್ಕರ ಯುದ್ಧಮಾಡಿ ನಿಮಗೆ ಜಯವನ್ನು ಉಂಟುಮಾಡುವನು” ಎಂದು ಹೇಳಬೇಕು.",
			"5": "<sup>5</sup> ಮತ್ತು ಸೈನ್ಯಾಧಿಪತಿಗಳು ಅವರಿಗೆ, “ನಿಮ್ಮಲ್ಲಿ ಯಾವನಾದರೂ ಹೊಸ ಮನೆಯನ್ನು ಕಟ್ಟಿಸಿಕೊಂಡು ಇನ್ನು ಪ್ರತಿಷ್ಠೆ ಮಾಡದಿದ್ದರೆ ಅವನು ಆ ಮನೆಗೆ ಹೋಗಲಿ, ಅವನು ಯುದ್ಧಕ್ಕೆ ಬಂದು ಸತ್ತರೆ ಮತ್ತೊಬ್ಬನು ಆ ಮನೆಯಲ್ಲಿ ಗೃಹಪ್ರವೇಶ ಮಾಡಿಕೊಳ್ಳಬಹುದು.",
			"6": "<sup>6</sup> ಯಾವನಾದರೂ ದ್ರಾಕ್ಷೇತೋಟವನ್ನು ಮಾಡಿ ಅದರ ಹಣ್ಣುಗಳನ್ನು ಇನ್ನೂ ಅನುಭೋಗಿಸದಿದ್ದರೆ ಅವನು ಮನೆಗೆ ಹೋಗಲಿ; ಅವನು ಕಾಳಗದಲ್ಲಿ ಸತ್ತರೆ ಮತ್ತೊಬ್ಬನು ಆ ತೋಟದ ಹಣ್ಣುಗಳನ್ನು ಅನುಭೋಗಿಸಬಹುದು.",
			"7": "<sup>7</sup> ಯಾವನಾದರೂ ತಾನು ಮದುವೆಗೆ ನಿಶ್ಚಯ ಮಾಡಿಕೊಂಡ ಹೆಣ್ಣನ್ನು ಇನ್ನೂ ಸೇರಿಸಿಕೊಳ್ಳದಿದ್ದರೆ ಅವನು ಮನೆಗೆ ಹೋಗಲಿ; ಅವನು ಯುದ್ಧದಲ್ಲಿ ಸತ್ತರೆ ಮತ್ತೊಬ್ಬನು ಅವಳನ್ನು ಮದುವೆ ಮಾಡಿಕೊಳ್ಳಬಹುದು” ಎಂದು ಹೇಳಬೇಕು.",
			"8": "<sup>8</sup> ಇದಲ್ಲದೆ, “ಯಾವನಾದರೂ ಅಧೈರ್ಯದಿಂದ ಯುದ್ಧ ಮಾಡಲು ಹಿಂಜರಿದರೆ ಅವನೂ ಮನೆಗೆ ಹೋಗಲಿ; ಅವನು ದಿಗಿಲುಪಡುವುದರಿಂದ ಅವನ ಜೊತೆಗಾರರೂ ದಿಗಿಲುಪಟ್ಟಾರು” ಎಂದು ಹೇಳಬೇಕು.",
			"9": "<sup>9</sup> ಸೈನ್ಯಾಧಿಪತಿಗಳು ಭಟರಿಗೆ ಈ ರೀತಿಯಾಗಿ ಹೇಳಿದನಂತರ ದಂಡಿನವರ ಮೇಲೆ ನಾಯಕರನ್ನು ನೇಮಿಸಬೇಕು.",
			"10": "<sup>10</sup> ನೀವು ಒಂದು ಪಟ್ಟಣವನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವುದಕ್ಕೆ ಅದರ ಹತ್ತಿರಕ್ಕೆ ಬಂದಾಗ ಮೊದಲು ಅದು ಯುದ್ಧವಿಲ್ಲದೆ ಸಮಾಧಾನದಿಂದಲೇ ಅಧೀನವಾಗಬಹುದೆಂದು ಪ್ರಕಟಿಸಬೇಕು.",
			"11": "<sup>11</sup> ಅದರಲ್ಲಿರುವವರು ಒಪ್ಪಿ ಬಾಗಿಲನ್ನು ನಿಮಗೆ ತೆರೆದುಕೊಟ್ಟರೆ ಅವರೆಲ್ಲರೂ ನಿಮಗೆ ಬಿಟ್ಟೀಕೆಲಸವನ್ನು ಮಾಡುವ ದಾಸರಾಗಬೇಕು.",
			"12": "<sup>12</sup> ಅವರು ನಿಮ್ಮ ಮಾತಿಗೆ ಸಮ್ಮತಿಸದೆ ಎದುರಿಸಿದರೆ ನೀವು ಆ ಪಟ್ಟಣಕ್ಕೆ ಮುತ್ತಿಗೆ ಹಾಕಬೇಕು.",
			"13": "<sup>13</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಆ ಪಟ್ಟಣವನ್ನು ನಿಮಗೆ ವಶವಾಗುವಂತೆ ಮಾಡಿದಾಗ ಅದರಲ್ಲಿರುವ ಗಂಡಸರೆಲ್ಲರನ್ನೂ ಕನಿಕರಿಸದೆ ಸಂಹಾರಮಾಡಬೇಕು.",
			"14": "<sup>14</sup> ಹೆಂಗಸರನ್ನೂ, ಮಕ್ಕಳನ್ನೂ, ದನಗಳನ್ನೂ ಮತ್ತು ಊರಿನಲ್ಲಿರುವ ಆಸ್ತಿಯನ್ನೂ ನೀವು ಸ್ವಂತಕ್ಕೆ ತೆಗೆದುಕೊಳ್ಳಬಹುದು. ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ವಶಪಡಿಸಿದ ಶತ್ರುಗಳ ಆಸ್ತಿಯನ್ನು ನೀವೇ ಅನುಭೋಗಿಸಬಹುದು.",
			"15": "<sup>15</sup> ಈ ದೇಶದ ಜನಾಂಗಗಳಿಗೆ ಸೇರದೆ ದೂರವಾಗಿರುವ ಜನಾಂಗಗಳ ಪಟ್ಟಣಗಳ ವಿಷಯದಲ್ಲಿ ನೀವು ಹಾಗೆಯೇ ನಡೆದುಕೊಳ್ಳಬೇಕು.",
			"16": "<sup>16</sup> ಆದರೆ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಸ್ವಂತಕ್ಕಾಗಿ ಕೊಡುವ ಈ ಜನಾಂಗಗಳ ಪಟ್ಟಣಗಳ ವಿಷಯದಲ್ಲಿ ಹಾಗೆ ಮಾಡದೆ ಉಸಿರಾಡುವ ಒಬ್ಬರನ್ನಾದರೂ ಉಳಿಸಬಾರದು.",
			"17": "<sup>17</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಅಪ್ಪಣೆಕೊಟ್ಟಂತೆ ಇವರನ್ನು ಅಂದರೆ ಹಿತ್ತಿಯರು, ಅಮೋರಿಯರು, ಕಾನಾನ್ಯರು, ಪೆರಿಜ್ಜೀಯರು, ಹಿವ್ವಿಯರು, ಯೆಬೂಸಿಯರು, ಇವರೆಲ್ಲರನ್ನೂ ನಾಶಮಾಡಿಬಿಡಬೇಕು.",
			"18": "<sup>18</sup> ಇಲ್ಲವಾದರೆ ಅವರು ತಮ್ಮ ದೇವರುಗಳಿಗಾಗಿ ಆಚರಿಸುವ ನಿಷಿದ್ಧವಾದ ಆಚಾರಗಳನ್ನು ನಿಮಗೆ ಕಲಿಸಿಕೊಟ್ಟಾರು; ಮತ್ತು ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ದ್ರೋಹಿಗಳಾಗಬಹುದು.",
			"19": "<sup>19</sup> ನೀವು ಒಂದು ಪಟ್ಟಣವನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವುದಕ್ಕಾಗಿ ಅದಕ್ಕೆ ಬಹುದಿನ ಮುತ್ತಿಗೆಹಾಕಿದಾಗ, ಅದಕ್ಕೆ ಸೇರಿರುವ ಮರಗಳನ್ನು ಹಾಳುಮಾಡಬಾರದು; ಅವುಗಳಿಗೆ ಕೊಡಲಿಯನ್ನು ಹಾಕಲೇಬಾರದು. ಅವುಗಳ ಹಣ್ಣನ್ನು ತಿನ್ನಬಹುದೇ ಹೊರತು ಮರಗಳನ್ನು ಕಡಿಯಬಾರದು. ಅಡವಿಯ ಮರಗಳು ಯಾರಿಗೂ ಶತ್ರುಗಳಲ್ಲ! ಅವುಗಳೊಡನೆ ಯುದ್ಧಮಾಡಬಹುದೇ?",
			"20": "<sup>20</sup> ಮರಗಳು ತಿನ್ನತಕ್ಕ ಹಣ್ಣು ಕೊಡುವುದಿಲ್ಲವೆಂದು ನೀವು ತಿಳಿದಾಗ ಮಾತ್ರ ಅದನ್ನು ಕಡಿದು ನಾಶಮಾಡಬಹುದು. ಅವುಗಳಿಂದ ಯುದ್ಧಸಲಕರಣೆಗಳನ್ನು ಮಾಡಿಕೊಂಡು ನಿಮಗೆ ವಿರುದ್ಧವಾಗಿರುವ ಆ ಪಟ್ಟಣವು ನಾಶವಾಗುವ ತನಕ ಅದಕ್ಕೆ ಮುತ್ತಿಗೆ ಹಾಕಬೇಕು."
		},
		"21": {
			"1": "<sup>1</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಸ್ವದೇಶವಾಗಿ ಕೊಡುವ ದೇಶದಲ್ಲಿ ಹತನಾದ ಮನುಷ್ಯನ ಶವವು ಅಡವಿಯಲ್ಲಿ ಬಿದ್ದಿರುವುದನ್ನು ನೀವು ಕಂಡರೆ ಮತ್ತು ಅವನನ್ನು ಕೊಂದವನು ಯಾರೆಂಬುವುದು ತಿಳಿಯದೆ ಹೋದರೆ,",
			"2": "<sup>2</sup> ನಿಮ್ಮ ಹಿರಿಯರೂ ಮತ್ತು ನ್ಯಾಯಾಧಿಪತಿಗಳೂ ಬಂದು ಹತನಾದವನ ಶವದ ಸುತ್ತಲಿರುವ ಊರುಗಳಲ್ಲಿ ಯಾವುದು ಹತ್ತಿರವೆಂದು ತಿಳಿದುಕೊಳ್ಳುವುದಕ್ಕೆ ಅಳತೆಮಾಡಬೇಕು.",
			"3": "<sup>3</sup> ಯಾವ ಊರು ಹತ್ತಿರವಾಗಿದೆಯೋ ಆ ಊರಿನ ಹಿರಿಯರು ಇನ್ನೂ ನೊಗಕ್ಕೆ ಕಟ್ಟಲ್ಪಡದೆಯೂ, ಯಾವ ಕೆಲಸವನ್ನೂ ಮಾಡದೆಯೂ ಇರುವ ಒಂದು ಮಣಕವನ್ನು ತೆಗೆದುಕೊಳ್ಳಬೇಕು.",
			"4": "<sup>4</sup> ಅವರು ಅದನ್ನು ಎಂದಿಗೂ ವ್ಯವಸಾಯಕ್ಕೆ ಉಪಯೋಗಿಸದಂಥ ಅಥವಾ ನೀರು ಯಾವಾಗಲೂ ಹರಿಯುವಂಥ ತಗ್ಗಿಗೆ ತೆಗೆದುಕೊಂಡು ಹೋಗಿ, ಅಲ್ಲಿ ಅದರ ಕುತ್ತಿಗೆಯನ್ನು ಮುರಿದು ಕೊಲ್ಲಬೇಕು.",
			"5": "<sup>5</sup> ಲೇವಿಕುಲದವರಾದ ಯಾಜಕರಲ್ಲಿ ಕೆಲವರು ಹತ್ತಿರ ಬರಬೇಕು. ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಅವರನ್ನೇ ತನ್ನ ಸಾನ್ನಿಧ್ಯಸೇವೆಯನ್ನು ಮಾಡುವುದಕ್ಕೂ ಮತ್ತು ತನ್ನ ಹೆಸರಿನಿಂದ ಜನರನ್ನು ಆಶೀರ್ವದಿಸುವುದಕ್ಕೂ ಆರಿಸಿಕೊಂಡಿದ್ದಾನಲ್ಲಾ; ಅನುಮಾನವಾದ ಎಲ್ಲಾ ವ್ಯಾಜ್ಯಗಳ ಮತ್ತು ಹೊಡೆದಾಟಗಳ ವಿಷಯದಲ್ಲಿ ಅವರೇ ತೀರ್ಮಾನಿಸುವವರು, ಅವರ ಮಾತುಗಳನ್ನು ಕೇಳಿರಿ.",
			"6": "<sup>6</sup> ಹತನಾದ ಮನುಷ್ಯನಿಗೆ ಹತ್ತಿರುವಿರುವ ಗ್ರಾಮದ ಹಿರಿಯರು, ತಗ್ಗಿನಲ್ಲಿ ಕುತ್ತಿಗೆ ಮುರಿಯಲ್ಪಟ್ಟ ಆ ಮಣಕದ ಮೇಲೆ ತಮ್ಮ ಕೈಗಳನ್ನು ತೊಳೆದುಕೊಂಡು,",
			"7": "<sup>7</sup> “ನಮ್ಮ ಕೈಗಳು ಈ ಹತ್ಯವನ್ನು ಮಾಡಲಿಲ್ಲ, ನಮ್ಮ ಕಣ್ಣುಗಳು ನೋಡಲಿಲ್ಲ.",
			"8": "<sup>8</sup> ಯೆಹೋವನೇ ನೀನು ಬಿಡುಗಡೆಮಾಡಿದ ನಿನ್ನ ಜನರನ್ನು ಕ್ಷಮಿಸಬೇಕು; ಅನ್ಯಾಯವಾದ ನರಹತ್ಯದೋಷ ಫಲವು ನಿನ್ನ ಜನರಾದ ಇಸ್ರಾಯೇಲರಿಗೆ ತಗಲದಿರಲಿ” ಎಂದು ಹೇಳಬೇಕು.",
			"9": "<sup>9</sup> ಆಗ ಆ ರಕ್ತಾಪರಾಧವು ಕ್ಷಮಿಸಲ್ಪಡುವುದು. ಹೀಗೆ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ಸರಿಯಾದುದನ್ನು ಮಾಡುವುದರಿಂದ ನೀವೇ ಅನ್ಯಾಯವಾದ ನರಹತ್ಯದೋಷವನ್ನು ನಿಮ್ಮ ಮಧ್ಯದಿಂದ ತೆಗೆದುಹಾಕುವಿರಿ.",
			"10": "<sup>10</sup> ನೀವು ಶತ್ರುಗಳೊಡನೆ ಯುದ್ಧಮಾಡಲಾಗಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಅನುಗ್ರಹದಿಂದ ಅವರನ್ನು ಸೋಲಿಸಿದಾಗ ನೀವು ಅವರನ್ನು ಸೆರೆಯಾಗಿ ಒಯ್ಯಬೇಕು.",
			"11": "<sup>11</sup> ನಿಮ್ಮಲ್ಲಿ ಯಾವನಾದರೂ ಸೆರೆಯವರಲ್ಲಿ ಸುಂದರ ಸ್ತ್ರೀಯನ್ನು ಕಂಡು ಮೋಹಿಸಿ ಮದುವೆಮಾಡಿಕೊಳ್ಳಬೇಕೆಂದು ಅಪೇಕ್ಷಿಸಿದರೆ,",
			"12": "<sup>12</sup> ಅವಳನ್ನು ಮನೆಗೆ ಕರೆದುಕೊಂಡು ಬರಲಿ, ತರುವಾಯ ಅವಳು ಕ್ಷೌರಮಾಡಿಸಿಕೊಂಡು, ಉಗುರುಗಳನ್ನು ಕತ್ತರಿಸಿಕೊಳ್ಳುವಳು.",
			"13": "<sup>13</sup> ಅವಳು ಸೆರೆಯ ಬಟ್ಟೆಗಳನ್ನು ತೆಗೆದಿಟ್ಟು, ಅವನ ಮನೆಯಲ್ಲಿ ಒಂದು ತಿಂಗಳಿನ ವರೆಗೆ ತಾಯಿ ಮತ್ತು ತಂದೆಗಳ ವಿಯೋಗದ ನಿಮಿತ್ತ ಹಂಬಲಿಸಲಿ. ಆಮೇಲೆ ಅವನು ಅವಳನ್ನು ಹೆಂಡತಿಯನ್ನಾಗಿ ಮಾಡಿಕೊಳ್ಳಬಹುದು.",
			"14": "<sup>14</sup> ತರುವಾಯ ಅವಳು ಅವನಿಗೆ ಇಷ್ಟವಾಗದೆಹೋದರೆ ಅವನು ಅವಳನ್ನು ಅವಳ ಮನಸ್ಸು ಬಂದಲ್ಲಿಗೆ ಕಳುಹಿಸಿಬಿಡಬೇಕು. ಅವಳನ್ನು ಸಂಗಮಿಸಿದ್ದರಿಂದ ಹಣ ಕೊಟ್ಟು ದಾಸತ್ವಕ್ಕೆ ಮಾರಲೂ ಬಾರದು, ದಾಸಿಯಂತೆ ನಡಿಸಲೂ ಬಾರದು.",
			"15": "<sup>15</sup> ಯಾವನಾದರು ಇಬ್ಬರು ಹೆಂಡತಿಯರನ್ನು ಮಾಡಿಕೊಂಡು ಅವರಲ್ಲಿ ಒಬ್ಬಳನ್ನು ಪ್ರೀತಿಸಿ, ಮತ್ತೊಬ್ಬಳನ್ನು ತಿರಸ್ಕರಿಸುವ ಸಂದರ್ಭ ಬಂದಾಗ ಅವರಿಬ್ಬರೂ ಅವನಿಂದ ಮಕ್ಕಳನ್ನು ಪಡೆದಿದ್ದರೆ, ಚೊಚ್ಚಲು ಮಗನು ತಿರಸ್ಕರಿಸಲ್ಪಟ್ಟವಳಲ್ಲಿಯೇ ಹುಟ್ಟಿದ್ದರೆ,",
			"16": "<sup>16</sup> ತಂದೆಯಾದವನು ತನ್ನ ಆಸ್ತಿಯನ್ನು ಮಕ್ಕಳಿಗೆ ಸ್ವಾಸ್ತ್ಯವಾಗಿ ಕೊಡುವಾಗ ಆ ತಿರಸ್ಕರಿಸಲ್ಪಟ್ಟವಳ ಮಗನನ್ನು ತಳ್ಳಿಬಿಟ್ಟು ತಾನು ಪ್ರೀತಿಸುವ ಹೆಂಡತಿಯ ಮಗನನ್ನೇ ಚೊಚ್ಚಲನೆಂದು ಭಾವಿಸಬಾರದು.",
			"17": "<sup>17</sup> ತಿರಸ್ಕರಿಸಲ್ಪಟ್ಟವಳ ಮಗನೇ ಚೊಚ್ಚಲನೆಂದು ಒಪ್ಪಿ, ಅವನಿಗೆ ಎರಡು ಭಾಗಗಳನ್ನು ಕೊಡಬೇಕು. ಅವನೇ ತಂದೆಯ ವೀರ್ಯಕ್ಕೆ ಪ್ರಥಮಫಲವೂ ಮತ್ತು ಚೊಚ್ಚಲುತನದ ಹಕ್ಕಿಗೆ ಬಾಧ್ಯನೂ ಆಗಿದ್ದಾನಲ್ಲಾ.",
			"18": "<sup>18</sup> ಒಬ್ಬ ಮಗನು ತಂದೆತಾಯಿಗಳ ಆಜ್ಞೆಗೆ ಒಳಗಾಗದೆ ಶಿಕ್ಷಿಸಲ್ಪಟ್ಟರೂ, ಮೊಂಡನೂ ಮತ್ತು ಅವಿಧೇಯನೂ ಆಗಿ,",
			"19": "<sup>19</sup> ಅವರ ಮಾತನ್ನು ಕೇಳದೆಹೋದರೆ ತಂದೆತಾಯಿಗಳು ಅವನನ್ನು ಹಿಡಿದು,",
			"20": "<sup>20</sup> ಊರು ಬಾಗಿಲಿಗೆ ಹಿರಿಯರ ಮುಂದೆ ತಂದು ಅವರಿಗೆ, “ಈ ನಮ್ಮ ಮಗನು ನಮ್ಮ ಮಾತನ್ನು ಕೇಳುವುದೇ ಇಲ್ಲ, ಆಜ್ಞೆಗೆ ಒಳಗಾಗುವುದಿಲ್ಲ; ಇವನು ಮೊಂಡ, ಕುಡುಕ ಮತ್ತು ಹೊಟ್ಟೆಬಾಕ” ಎಂದು ಸಾಕ್ಷಿಹೇಳಬೇಕು.",
			"21": "<sup>21</sup> ಆಗ ಊರಿನವರೆಲ್ಲರೂ ಅವನನ್ನು ಕಲ್ಲೆಸೆದು ಕೊಲ್ಲಬೇಕು. ಹೀಗೆ ನೀವು ಆ ದುಷ್ಟತ್ವವನ್ನು ನಿಮ್ಮ ಮಧ್ಯದೊಳಗಿಂದ ತೆಗೆದುಹಾಕಿಬಿಡಬೇಕು. ಇಸ್ರಾಯೇಲರೆಲ್ಲರೂ ಇದನ್ನು ಕೇಳಿ ಭಯಪಡುವರು.",
			"22": "<sup>22</sup> ಅಪರಾಧ ಮಾಡಿದವನು ಮರಣಶಿಕ್ಷೆಯನ್ನು ಹೊಂದಿದ ಮೇಲೆ ನೀವು ಅವನ ಶವವನ್ನು ಮರದ ಕಂಬಕ್ಕೆ ತೂಗುಹಾಕಿದರೆ,",
			"23": "<sup>23</sup> ಅದು ರಾತ್ರಿಯೆಲ್ಲಾ ಮರದ ಮೇಲೆ ಇರಬಾರದು; ಅದೇ ದಿನದಲ್ಲಿ ಅದನ್ನು ನೆಲದಲ್ಲಿ ಹೂಣಿಡಬೇಕು. ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಸ್ವದೇಶವಾಗುವುದಕ್ಕೆ ಕೊಡುವ ದೇಶವು ಅಪವಿತ್ರವಾಗಬಾರದು. ಮರಕ್ಕೆ ತೂಗಹಾಕಲ್ಪಟ್ಟವನು ದೇವರ ಶಾಪವನ್ನು ಹೊಂದಿದವನಾಗಿರುತ್ತಾನೆ."
		},
		"22": {
			"1": "<sup>1</sup> ಸ್ವದೇಶದವನ ಎತ್ತಾಗಲಿ ಅಥವಾ ಕುರಿಯಾಗಲಿ ದಾರಿ ತಪ್ಪಿ ಹೋಗಿರುವುದನ್ನು ನೀವು ನೋಡಿಯೂ ನೋಡದಂತೆ ಅದನ್ನು ಬಿಟ್ಟು ಹೋಗಬಾರದು; ಅದನ್ನು ಅಟ್ಟಿಸಿಕೊಂಡುಹೋಗಿ ಅವನಿಗೆ ಕೊಡಲೇಬೇಕು.",
			"2": "<sup>2</sup> ಅವನು ನಿಮಗೆ ದೂರವಾಗಿದ್ದರೆ ಇಲ್ಲವೆ ಅವನು ಯಾರೋ ಎಂದು ನಿಮಗೆ ತಿಳಿಯದೆ ಹೋದರೆ, ಆ ಪಶುವನ್ನು ನಿಮ್ಮ ಮನೆಗೆ ಹೊಡೆದುಕೊಂಡು ಬಂದು ಕಾಯಬೇಕು. ಅವನು ಅದನ್ನು ಹುಡುಕುತ್ತಾ ಬಂದಾಗ ನೀವು ಅದನ್ನು ಅವನಿಗೆ ಹಿಂತಿರುಗಿ ಕೊಡಬೇಕು.",
			"3": "<sup>3</sup> ಅವನ ಕತ್ತೆಯಾಗಲಿ, ಬಟ್ಟೆಯಾಗಲಿ, ಅವನು ಕಳೆದುಕೊಂಡ ಯಾವ ವಸ್ತುವಾಗಲಿ ನಿಮಗೆ ಸಿಕ್ಕಿದರೆ ಹಾಗೆಯೇ ಮಾಡಬೇಕು; ನೋಡದಂತೆ ಇರಲೇ ಬಾರದು.",
			"4": "<sup>4</sup> ಸ್ವದೇಶದವನ ಕತ್ತೆಯಾಗಲಿ ಅಥವಾ ಎತ್ತಾಗಲಿ ದಾರಿಯಲ್ಲಿ ಬಿದ್ದಿರುವುದನ್ನು ನೀವು ಕಂಡರೆ ಕಾಣದವರಂತೆ ಹೋಗದೆ ಅವನಿಗೆ ಸಹಾಯಮಾಡಿ ಎಬ್ಬಿಸಬೇಕು.",
			"5": "<sup>5</sup> ಸ್ತ್ರೀಯು ಪುರುಷವೇಷವನ್ನಾಗಲಿ ಅಥವಾ ಪುರುಷನು ಸ್ತ್ರೀವೇಷವನ್ನಾಗಲಿ ಹಾಕಿಕೊಳ್ಳಬಾರದು; ಹಾಗೆ ಮಾಡುವವರು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಅಸಹ್ಯರು.",
			"6": "<sup>6</sup> ನೀವು ದಾರಿಯಲ್ಲಿ ಹೋಗುವಾಗ ಮರದ ಮೇಲಾಗಲಿ ಅಥವಾ ನೆಲದ ಮೇಲಾಗಲಿ ಇರುವ ಒಂದು ಪಕ್ಷಿಯ ಗೂಡಿನೊಳಗೆ ಮರಿಗಳು ಅಥವಾ ಮೊಟ್ಟೆಗಳು ಇದ್ದು, ಅವುಗಳ ಮೇಲೆ ತಾಯಿ ಹೊದಗಿರುವುದನ್ನು ಕಂಡರೆ ಮರಿಗಳನ್ನು ತೆಗೆದುಕೊಳ್ಳಬಹುದೇ ಹೊರತು ತಾಯಿಯನ್ನು ಹಿಡಿದುಕೊಳ್ಳಬಾರದು.",
			"7": "<sup>7</sup> ನೀವು ಹೀಗೆ ನಡೆದುಕೊಂಡರೆ ನಿಮಗೆ ಶ್ರೇಯಸ್ಸು ಉಂಟಾಗುವುದು, ಮತ್ತು ಬಹುಕಾಲ ಬಾಳುವಿರಿ.",
			"8": "<sup>8</sup> ಹೊಸ ಮನೆಯನ್ನು ಕಟ್ಟಿಸಿಕೊಳ್ಳುವಾಗ ಯಾವನಾದರೂ ಅದರ ಮಾಳಿಗೆಯಿಂದ ಬಿದ್ದು ನಿಮಗೆ ಜೀವಹತ್ಯ ದೋಷವುಂಟಾಗದಂತೆ ಮಾಳಿಗೆಯ ಮೇಲೆ ಸುತ್ತಲೂ ಸಣ್ಣ ಗೋಡೆಯನ್ನು ಕಟ್ಟಿಸಬೇಕು.",
			"9": "<sup>9</sup> ದ್ರಾಕ್ಷೇತೋಟಗಳಲ್ಲಿ ಬೇರೆ ವಿಧವಾದ ಬೀಜವನ್ನು ಬಿತ್ತಬಾರದು; ಹಾಗೆ ಮಾಡಿದರೆ ಯಾಜಕರು ಆ ಬೆಳೆಯನ್ನು, ದ್ರಾಕ್ಷೇಯ ಬೆಳೆಯನ್ನು ಅಂತೂ ಆ ತೋಟದ ಎಲ್ಲಾ ಬೆಳೆಯನ್ನು ದೇವರಿಗೆ ಸಮರ್ಪಿಸುವರು.",
			"10": "<sup>10</sup> ಎತ್ತನ್ನೂ ಮತ್ತು ಕತ್ತೆಯನ್ನೂ ಜೊತೆಮಾಡಿ ನೇಗಿಲಿಗೆ ಕಟ್ಟಿ ಉಳಬಾರದು.",
			"11": "<sup>11</sup> ನಾರು ಹಾಗು ಉಣ್ಣೆಯೂ ಕೂಡಿರುವ ಬಟ್ಟೆಯನ್ನು ಹಾಕಿಕೊಳ್ಳಬಾರದು.",
			"12": "<sup>12</sup> ನೀವು ಹೊದ್ದುಕೊಳ್ಳುವ ಬಟ್ಟೆಯ ನಾಲ್ಕು ಮೂಲೆಗಳಲ್ಲಿ ಗೊಂಡೆಗಳನ್ನು ಕಟ್ಟಬೇಕು.",
			"13": "<sup>13</sup> ಯಾವನಾದರೂ ತಾನು ಮದುವೆಮಾಡಿಕೊಂಡ ಸ್ತ್ರೀಯನ್ನು ದ್ವೇಷಿಸಿ, ಅವಳ ವಿಷಯದಲ್ಲಿ ಇಲ್ಲಸಲ್ಲದ ಕೆಟ್ಟ ಮಾತುಗಳನ್ನು ಆಡಿ,",
			"14": "<sup>14</sup> “ನಾನು ಇವಳನ್ನು ಮದುವೆಮಾಡಿಕೊಂಡೆನು; ಆದರೆ ಇವಳೊಡನೆ ಸಂಗಮಿಸಿದಾಗ ಇವಳು ಕನ್ನಿಕೆಯಲ್ಲವೆಂದು ತಿಳಿದುಬಂತು” ಎಂದು ಹೇಳಿ ಅವಳ ಹೆಸರನ್ನು ಕೆಡಿಸಿದರೆ,",
			"15": "<sup>15</sup> ಅವಳ ತಾಯಿ ತಂದೆಗಳು ತಮ್ಮ ಮಗಳು ವಿವಾಹಕ್ಕೆ ಮುಂಚೆ ಪುರುಷಸಂಪರ್ಕವಿಲ್ಲದವಳು ಎಂಬುವುದಕ್ಕೆ ನಿದರ್ಶನವನ್ನು ಊರ ಬಾಗಿಲಿಗೆ ಹಿರಿಯರ ಮುಂದೆ ತಂದು ತೋರಿಸಬೇಕು.",
			"16": "<sup>16</sup> ಆ ಸ್ತ್ರೀಯ ತಂದೆ ಅವರಿಗೆ, “ನಾನು ನನ್ನ ಮಗಳನ್ನು ಈ ಪುರುಷನಿಗೆ ಮದುವೆಮಾಡಿಕೊಟ್ಟ ಮೇಲೆ",
			"17": "<sup>17</sup> ಇವನು ಅವಳನ್ನು ದ್ವೇಷಿಸಿ, ಪರಪುರುಷನ ಸಂಪರ್ಕ ಮಾಡಿದವಳೆಂದು ನಿರಾಧಾರವಾದ ಮಾತುಗಳನ್ನಾಡಿ ಅವಳ ಹೆಸರನ್ನು ಅವಮಾನಪಡಿಸುತ್ತಾನೆ. ಇಗೋ ನನ್ನ ಮಗಳು ವಿವಾಹಕ್ಕೆ ಮುಂಚೆ ಪುರುಷಸಂಪರ್ಕ ಮಾಡಿದವಳಲ್ಲ ಎಂಬುವುದಕ್ಕೆ ಇದೇ ಪ್ರಮಾಣ” ಎಂದು ಹೇಳಿ ಅವಳ ಹೊದಿಕೆಯನ್ನು ಆ ಊರಿನ ಹಿರಿಯರ ಮುಂದೆ ಇಡಬೇಕು.",
			"18": "<sup>18</sup> ಆ ಊರಿನ ಹಿರಿಯರು ಅವನನ್ನು ಹಿಡಿಸಿ ಅವನನ್ನು ಹೊಡಿಸಬೇಕು.",
			"19": "<sup>19</sup> ಇಸ್ರಾಯೇಲಳಾದ ಹೆಣ್ಣಿನ ವಿಷಯದಲ್ಲಿ ಆ ಪುರುಷನು ನಿರಾಧಾರವಾದ ತಪ್ಪು ಹೊರಿಸಿದ್ದರಿಂದ, ಅವನಿಗೆ ನೂರು ಶೆಕೆಲ್ ಬೆಳ್ಳಿ ದಂಡವನ್ನು ವಿಧಿಸಿ, ಆ ಹಣವನ್ನು ಅವಳ ತಂದೆಗೆ ಕೊಡಿಸಬೇಕು. ಮತ್ತು ಅವನು ಆ ಸ್ತ್ರೀಯನ್ನು ಹೆಂಡತಿಯನ್ನಾಗಿಯೇ ಇಟ್ಟುಕೊಳ್ಳಬೇಕು; ಅವನ ಜೀವಮಾನಕಾಲವೆಲ್ಲಾ ಅವಳನ್ನು ಪರಿತ್ಯಾಗಮಾಡುವುದಕ್ಕೆ ಅವನಿಗೆ ಅಧಿಕಾರವಿರುವುದಿಲ್ಲ.",
			"20": "<sup>20</sup> ಆದರೆ ಆ ಹೆಣ್ಣು ವಿವಾಹಕ್ಕೆ ಮುಂಚೆ ದೈಹಿಕವಾಗಿ ಪುರುಷಸಂಪರ್ಕ ಮಾಡಿದ್ದು ನಿಜ ಎಂದು ತಿಳಿದುಬಂದರೆ,",
			"21": "<sup>21</sup> ಅವಳು ತಂದೆಯ ಮನೆಯಲ್ಲೇ ಪರಪುರುಷನೊಡನೆ ಸಂಪರ್ಕಮಾಡಿ ಇಸ್ರಾಯೇಲರೊಳಗೆ ದುರಾಚಾರವನ್ನು ನಡಿಸಿದ್ದರಿಂದ, ಅವರು ಅವಳನ್ನು ತಂದೆಯ ಮನೆಯ ಬಾಗಿಲಿಗೆ ಕರೆಯಿಸಬೇಕು; ಊರಿನವರೆಲ್ಲರು ಅವಳನ್ನು ಕಲ್ಲೆಸೆದು ಕೊಲ್ಲಬೇಕು. ಹೀಗೆ ಆ ದುಷ್ಟತ್ವವನ್ನು ನಿಮ್ಮ ಮಧ್ಯದಿಂದ ನಿವಾರಿಸಿಕೊಳ್ಳಬೇಕು.",
			"22": "<sup>22</sup> ಯಾವನಾದರೂ ಇನ್ನೊಬ್ಬನ ಹೆಂಡತಿಯೊಡನೆ ವ್ಯಭಿಚಾರಮಾಡಿದ್ದು ತಿಳಿದುಬಂದರೆ ಆ ಸ್ತ್ರೀ ಮತ್ತು ಪುರುಷರಿಬ್ಬರಿಗೂ ಮರಣಶಿಕ್ಷೆಯಾಗಬೇಕು. ಹೀಗೆ ನೀವು ಆ ದುಷ್ಟತ್ವವನ್ನು ಇಸ್ರಾಯೇಲರ ಮಧ್ಯದಿಂದ ತೆಗೆದುಹಾಕಬೇಕು.",
			"23": "<sup>23</sup> ಒಬ್ಬನಿಗೆ ನಿಶ್ಚಿತಳಾದ ಹೆಣ್ಣನ್ನು ಯಾವನಾದರೂ ಊರೊಳಗೆ ಮರುಳುಗೊಳಿಸಿ ಸಂಗಮಿಸಿದರೆ,",
			"24": "<sup>24</sup> ನೀವು ಅವರಿಬ್ಬರನ್ನೂ ಊರುಬಾಗಲಿನ ಹೊರಕ್ಕೆ ತರಿಸಿ ಅವರನ್ನು ಕಲ್ಲೆಸೆದು ಕೊಲ್ಲಬೇಕು. ಒಬ್ಬನಿಗೆ ನಿಶ್ಚಿತಳಾದ ಸ್ತ್ರೀಯನ್ನು ಕೆಡಿಸಿದ್ದರಿಂದ ಆ ಪುರುಷನಿಗೂ, ಊರಲ್ಲಿದ್ದು ರಕ್ಷಣೆಗಾಗಿ ಕೂಗಿಕೊಳ್ಳದೆ ಹೋದುದರಿಂದ ಆ ಸ್ತ್ರೀಗೂ ಮರಣಶಿಕ್ಷೆಯಾಗಬೇಕು. ಹೀಗೆ ನೀವು ಆ ದುಷ್ಟತ್ವವನ್ನು ನಿಮ್ಮ ಮಧ್ಯದಿಂದ ತೆಗೆದುಹಾಕಬೇಕು.",
			"25": "<sup>25</sup> ಆದರೆ ಆ ಪುರುಷನು ನಿಶ್ಚಿತಳಾದ ಆ ಸ್ತ್ರೀಯನ್ನು ಅಡವಿಯಲ್ಲಿ ಬಲಾತ್ಕಾರದಿಂದ ಸಂಗಮಿಸಿದ್ದರೆ ಪುರುಷನಿಗೆ ಮಾತ್ರ ಮರಣಶಿಕ್ಷೆಯಾಗಬೇಕು.",
			"26": "<sup>26</sup> ಆ ಸ್ತ್ರೀಗೆ ಯಾವ ಶಿಕ್ಷೆಯೂ ಆಗಬಾರದು; ಅವಳಲ್ಲಿ ಮರಣ ಶಿಕ್ಷೆಗೆ ಪಾತ್ರವಾದ ಅಪರಾಧವೇನೂ ಇಲ್ಲ. ಒಬ್ಬ ಮನುಷ್ಯನು ಮತ್ತೊಬ್ಬನ ಮೇಲೆ ಬಿದ್ದು ಕೊಲ್ಲುವುದು ಹೇಗೋ ಈ ಸಂಗತಿಯೂ ಹಾಗೆಯೇ ಎಂದು ತಿಳಿಯಬೇಕು.",
			"27": "<sup>27</sup> ಆ ಸ್ತ್ರೀಯು ಅಡವಿಯಲ್ಲೇ ಅವನ ಕೈಗೆ ಸಿಕ್ಕಿದ್ದರಿಂದ ಅವಳು ಕೂಗಿಕೊಂಡರೂ ತಪ್ಪಿಸುವವರು ಇರಲಿಲ್ಲ.",
			"28": "<sup>28</sup> ಯಾರಿಗೂ ನಿಶ್ಚಯವಾಗದೆ ಇರುವ ಸ್ತ್ರೀಯನ್ನು ಒಬ್ಬನು ಹಿಡಿದು ಸಂಗಮಿಸಿದ್ದು ತಿಳಿದು ಬಂದರೆ,",
			"29": "<sup>29</sup> ಅವನು ಅವಳನ್ನು ಮಾನಭಂಗಪಡಿಸಿದ್ದರಿಂದ, ಅವಳ ತಂದೆಗೆ ಐವತ್ತು ಶೆಕೆಲ್ ಬೆಳ್ಳಿಯನ್ನು ಕೊಟ್ಟು ಅವಳನ್ನು ಹೆಂಡತಿಯನ್ನಾಗಿ ಮಾಡಿಕೊಳ್ಳಬೇಕು. ಅವನು ಬದುಕುವ ತನಕ ಅವಳನ್ನು ಪರಿತ್ಯಾಗಮಾಡುವುದಕ್ಕೆ ಅವನಿಗೆ ಅಧಿಕಾರವಿರುವುದಿಲ್ಲ.",
			"30": "<sup>30</sup> ತಂದೆಗೆ ಹೆಂಡತಿಯಾದವಳನ್ನು ಸಂಗಮಿಸಲೇ ಬಾರದು; ಅದು ತಂದೆಗೆ ಅಪಮಾನಪಡಿಸಿದ ಹಾಗಾಗುವುದು."
		},
		"23": {
			"1": "<sup>1</sup> ಬೀಜಹೊಡಿಸಿಕೊಂಡವರು [ವೀರ್ಯನಾಶ] ಅಥವಾ ರಹಸ್ಯಾಂಗವನ್ನು [ಜನನೆಂದ್ರಿಯ] ಛೇದಿಸಿಕೊಂಡವರು ಯೆಹೋವನ ಸಭೆಯಲ್ಲಿ ಸೇರಬಾರದು.",
			"2": "<sup>2</sup> ಅನೈತಿಕ ಸಂಬಂಧದಿಂದ ಹುಟ್ಟಿದ ಸಂತತಿಯವರು, ಹತ್ತನೆಯ ತಲೆಯವರಾದರೂ ಅವರು ಯೆಹೋವನ ಸಭೆಗೆ ಸೇರಬಾರದು.",
			"3": "<sup>3</sup> ಅಮ್ಮೋನಿಯರಾಗಲಿ ಅಥವಾ ಮೋವಾಬ್ಯರಾಗಲಿ ಯೆಹೋವನ ಸಭೆಗೆ ಎಂದೆಂದಿಗೂ ಸೇರಬಾರದು. ಅವರ ಸಂತತಿಯವರು ಹತ್ತನೆಯ ತಲೆಯವರಾದರೂ ಯೆಹೋವನ ಸಭೆಗೆ ಸೇರಬಾರದು.",
			"4": "<sup>4</sup> ಯಾಕೆಂದರೆ ನೀವು ಐಗುಪ್ತದೇಶದಿಂದ ಬಂದಾಗ ಅಮ್ಮೋನಿಯರು ಅನ್ನಪಾನಗಳನ್ನು ತಂದು ನಿಮ್ಮನ್ನು ಎದುರುಗೊಳ್ಳಲಿಲ್ಲ; ಮೋವಾಬ್ಯರು ನಿಮ್ಮನ್ನು ಶಪಿಸುವುದಕ್ಕಾಗಿ ಬೆಯೋರನ ಮಗನಾದ ಬಿಳಾಮನಿಗೆ ಹಣಕೊಟ್ಟು ಎರಡು ನದಿಗಳ ಮಧ್ಯದಲ್ಲಿರುವ ಅರಾಮಿನ ಪೆತೋರ್ ಊರಿನಿಂದ ಅವನನ್ನು ಕರೆಯಿಸಿದರು.",
			"5": "<sup>5</sup> ಆದರೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಪ್ರೀತಿಸಿದ್ದರಿಂದ ಬಿಳಾಮನ ಮಾತಿಗೆ ಸಮ್ಮತಿಸದೆ ಅವನಿಂದ ಶಾಪವನ್ನು ನುಡಿಸದೆ ಆಶೀರ್ವಾದವನ್ನೇ ಹೇಳಿಸಿದನು.",
			"6": "<sup>6</sup> ನಿಮ್ಮ ಜೀವಮಾನಕಾಲವೆಲ್ಲಾ ಅವರ ಯೋಗಕ್ಷೇಮವನ್ನು ಬಯಸಲೇ ಬಾರದು.",
			"7": "<sup>7</sup> ಎದೋಮ್ಯರನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ನಿಷೇಧಿಸಬಾರದು; ಅವರು ನಮ್ಮ ಹತ್ತಿರ ಸಂಭಂದಿಕರು. ಐಗುಪ್ತ್ಯರನ್ನೂ ಸಂಪೂರ್ಣವಾಗಿ ನಿಷೇಧಿಸಬಾರದು; ಅವರ ದೇಶದಲ್ಲಿ ನೀವು ಪ್ರವಾಸಿಗಳಾಗಿದ್ದಿರಲ್ಲಾ.",
			"8": "<sup>8</sup> ಅವರ ಸಂತತಿಯವರಲ್ಲಿ ಮೂರನೆಯ ತಲೆಯವರು ಯೆಹೋವನ ಸಭೆಗೆ ಸೇರಬಹುದು.",
			"9": "<sup>9</sup> ನೀವು ಶತ್ರುಗಳೊಡನೆ ಯುದ್ಧಕ್ಕೆ ಹೊರಟು ಪಾಳೆಯದಲ್ಲಿರುವಾಗ ಯಾವ ಅಶುದ್ಧ ಕಾರ್ಯಗಳು ನಡೆಯದಂತೆ ಎಚ್ಚರದಿಂದಿರಬೇಕು.",
			"10": "<sup>10</sup> ರಾತ್ರಿಕಾಲದಲ್ಲಿ ವೀರ್ಯಸ್ಖಲನದಿಂದ ಯಾವನಾದರೂ ಅಶುದ್ಧನಾದರೆ, ಅವನು ಪಾಳೆಯದೊಳಗೆ ಇರದೆ ಹೊರಗೆ ಇರಬೇಕು.",
			"11": "<sup>11</sup> ಅವನು ಸಂಜೆಯ ವೇಳೆಯಲ್ಲಿ ಸ್ನಾನಮಾಡಿ ಸೂರ್ಯನು ಮುಳುಗಿದ ಮೇಲೆ ಪಾಳೆಯದೊಳಗೆ ಬರಬಹುದು.",
			"12": "<sup>12</sup> ಪಾಳೆಯದ ಹೊರಗೆ ಪಾಯಿಖಾನೆಗಾಗಿ ಒಂದು ಸ್ಥಳವನ್ನು ಗೊತ್ತುಮಾಡಬೇಕು.",
			"13": "<sup>13</sup> ಯುದ್ಧದ ಸಾಮಾನುಗಳಲ್ಲದೆ ನಿಮ್ಮ ಬಳಿಯಲ್ಲಿ ಸಲಿಕೆ ಇರಬೇಕು; ನೀವು ಬಹಿರ್ಭೂಮಿಗೆ ಹೋದಾಗ ಆ ಸಲಿಕೆಯಿಂದ ಅಗಿದು ಕಲ್ಮಷವನ್ನು ಮುಚ್ಚಿಬಿಡಬೇಕು.",
			"14": "<sup>14</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಕಾಪಾಡುವುದಕ್ಕೂ, ಶತ್ರುಗಳನ್ನು ನಿಮ್ಮ ಕೈವಶಮಾಡುವುದಕ್ಕೂ ನಿಮ್ಮ ಪಾಳೆಯದೊಳಗೆ ಸಂಚಾರಮಾಡುತ್ತಾನಲ್ಲಾ; ಆದುದರಿಂದ ಪಾಳೆಯವು ನಿಮರ್ಲವಾಗಿರಬೇಕು; ನಿಮ್ಮಲ್ಲಿ ಅಶುದ್ಧವೇನಾದರೂ ಕಂಡುಬಂದರೆ ಆತನು ನಿಮ್ಮನ್ನು ಬಿಟ್ಟುಹೋದಾನು.",
			"15": "<sup>15</sup> ತಪ್ಪಿಸಿಕೊಂಡ ಗುಲಾಮನು ನಿಮ್ಮಲ್ಲಿರುವುದಕ್ಕೆ ಬಂದರೆ ಅವನನ್ನು ಅವನ ದಣಿಗೆ ಪುನಃ ವಶಪಡಿಸಬಾರದು.",
			"16": "<sup>16</sup> ನಿಮ್ಮ ಊರುಗಳಲ್ಲಿ ಯಾವ ಸ್ಥಳವು ಅವನಿಗೆ ಇಷ್ಟವಾಗಿದೆಯೋ ಅಲ್ಲೇ ಅವನು ವಾಸವಾಗಿರಬಹುದು; ಅವನನ್ನು ನಿರ್ಬಂಧಪಡಿಸಬಾರದು ಹಾಗೂ ನೀವು ಅವನನ್ನು ಮತ್ತೆ ಶೋಷಣೆಗೆ ಗುರಿಮಾಡಬಾರದು.",
			"17": "<sup>17</sup> ಇಸ್ರಾಯೇಲರಲ್ಲಿ ಯಾವ ಸ್ತ್ರೀಯೂ ದೇವದಾಸಿಯಾಗಬಾರದು; ಯಾವ ಪುರುಷನೂ ಅಂತಹ ವ್ಯಭಿಚಾರಕ್ಕೆ ಇಳಿಯಬಾರದು.",
			"18": "<sup>18</sup> ಇಸ್ರಾಯೇಲರ ಪುರುಷ ಅಥವಾ ಸ್ತ್ರೀಯೂ ವ್ಯಭಿಚಾರದಿಂದಾಗಿ ಸಂಪಾದಿಸಿದ ಹಣವನ್ನು ಹರಕೆಯಾಗಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಆಲಯದೊಳಗೆ ತರಲೇಬಾರದು. ಈ ಎರಡೂ ಯೆಹೋವನಿಗೆ ಅಸಹ್ಯವಾದ ವಿಷಯ.",
			"19": "<sup>19</sup> ಹಣವನ್ನಾಗಲಿ, ಆಹಾರಪದಾರ್ಥಗಳನ್ನಾಗಲಿ ಬೇರೆ ಯಾವುದನ್ನಾಗಲಿ ಪರದೇಶದವನಿಗೆ ಬಡ್ಡಿಗೆ ಕೊಡಬಹುದೇ ಹೊರತು ಸ್ವದೇಶದವನಿಗೆ ಕೊಡಬಾರದು.",
			"20": "<sup>20</sup> ಹೀಗೆ ನಡೆದರೆ ನೀವು ಸ್ವದೇಶವಾಗಿ ಪಡೆಯಲಿರುವ ದೇಶದಲ್ಲಿ ನಿಮ್ಮ ಎಲ್ಲಾ ಪ್ರಯತ್ನಗಳಲ್ಲಿಯೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಅಭಿವೃದ್ಧಿಪಡಿಸುವನು.",
			"21": "<sup>21</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಹರಕೆಮಾಡಿದ ಮೇಲೆ ಅದನ್ನು ತಡಮಾಡದೆ ತೀರಿಸಬೇಕು; ಆತನು ತಪ್ಪದೆ ಅದನ್ನು ವಿಚಾರಿಸುವನು; ತೀರಿಸದೆ ಹೋಗುವುದು ಪಾಪ.",
			"22": "<sup>22</sup> ನೀವು ಹರಕೆಮಾಡದಿದ್ದರೆ ದೋಷವೇನೂ ಇರಲಿಲ್ಲ;",
			"23": "<sup>23</sup> ಆದರೆ ಬಾಯಿಂದ ನುಡಿದದ್ದನ್ನು ನೆರವೇರಿಸಲೇಬೇಕು. ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಬಾಯಿಂದ ಹರಕೆಮಾಡಿಕೊಂಡಂತೆಯೇ ಅದನ್ನು ಒಪ್ಪಿಸಿಬಿಡಬೇಕು.",
			"24": "<sup>24</sup> ಮತ್ತೊಬ್ಬನ ದ್ರಾಕ್ಷೇತೋಟದಲ್ಲಿ ನೀವು ಹೋಗುತ್ತಿರುವಾಗ ಇಷ್ಟಾನುಸಾರವಾಗಿ ಹಣ್ಣುಗಳನ್ನು ತಿನ್ನಬಹುದೇ ಹೊರತು ಪಾತ್ರೆಯಲ್ಲಿ ತುಂಬಿಕೊಂಡು ಹೋಗಬಾರದು.",
			"25": "<sup>25</sup> ಮತ್ತೊಬ್ಬನ ಪೈರಿನಲ್ಲಿ ಹೋಗುತ್ತಿರುವಾಗ ತೆನೆಗಳನ್ನು ಕೈಯಿಂದ ಮುರಿದುಕೊಳ್ಳಬಹುದೇ ಹೊರತು ಆ ಪೈರಿಗೆ ಕುಡುಗೋಲು ಹಾಕಬಾರದು."
		},
		"24": {
			"1": "<sup>1</sup> ಯಾವನಾದರೂ ತಾನು ಮದುವೆಮಾಡಿಕೊಂಡ ಸ್ತ್ರೀಯಲ್ಲಿ ಏನೋ ಅವಲಕ್ಷಣವನ್ನು ಕಂಡು ಅವಳಲ್ಲಿ ಸಂತೋಷಪಡದೆ ಇದ್ದರೆ ಅವನು ತ್ಯಾಗಪತ್ರವನ್ನು ಬರೆದುಕೊಟ್ಟು ಅವಳನ್ನು ಮನೆಯಿಂದ ಕಳುಹಿಸಬೇಕು.",
			"2": "<sup>2</sup> ಅವಳು ಮನೆಯಿಂದ ಹೋಗಿ ಮತ್ತೊಬ್ಬನನ್ನು ಮದುವೆಯಾಗಬಹುದು.",
			"3": "<sup>3</sup> ಆಕೆ ಎರಡನೆಯ ಗಂಡನಿಂದಲೂ ತಿರಸ್ಕರಿಸಲ್ಪಟ್ಟು, ತ್ಯಾಗಪತ್ರವನ್ನು ಹೊಂದಿ ಕಳುಹಿಸಲ್ಪಟ್ಟರೆ ಅಥವಾ ಎರಡನೆಯ ಗಂಡನು ಸತ್ತರೆ,",
			"4": "<sup>4</sup> ಆಗ ಅವಳನ್ನು ಕಳುಹಿಸಿಬಿಟ್ಟ ಮೊದಲನೆಯ ಗಂಡನು ಅವಳನ್ನು ಪುನಃ ತನ್ನ ಹೆಂಡತಿಯಾಗಿ ಸ್ವೀಕರಿಸಬಾರದು; ಅವಳು ಅಶುದ್ಧಳಾದಳು; ಅವಳನ್ನು ಪುನಃ ಸ್ವೀಕರಿಸುವುದು ಯಹೋವನಿಗೆ ಅಸಹ್ಯ ಕಾರ್ಯ. ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಸ್ವದೇಶವಾಗುವುದಕ್ಕೆ ಕೊಡುವ ದೇಶಕ್ಕೆ ದೋಷವುಂಟಾಗುವಂತೆ ಅವಕಾಶಕೊಡಬಾರದು.",
			"5": "<sup>5</sup> ಹೊಸದಾಗಿ ಮದುವೆಮಾಡಿಕೊಂಡವನು ಸೈನ್ಯದವರೊಡನೆ ಯುದ್ಧಕ್ಕೆ ಹೋಗಬಾರದು; ಭಾರವಾದ ಯಾವ ಕೆಲಸವನ್ನೂ ಅವನಿಗೆ ನೇಮಿಸಬಾರದು. ಅವನು ಒಂದು ವರುಷದ ವರೆಗೂ ಬಿಡುವಾಗಿ ಮನೆಯಲ್ಲಿ ಇದ್ದುಕೊಂಡು ಪರಿಗ್ರಹಿಸಿದ ಹೆಂಡತಿಯೊಡನೆ ಸುಖವಾಗಿರಲಿ.",
			"6": "<sup>6</sup> ಬೀಸುವಕಲ್ಲನ್ನು ಪೂರ್ತಿಯಾಗಲಿ ಅಥವಾ ಅರ್ಧವಾಗಲಿ ಒತ್ತೆ ತೆಗೆದುಕೊಳ್ಳಬಾರದು; ಅದು ಜೀವನಾಧಾರವನ್ನೇ ಒತ್ತೆಯಾಗಿ ತೆಗೆದುಕೊಂಡ ಹಾಗಾಗುವುದು.",
			"7": "<sup>7</sup> ಯಾವನಾದರೂ ಸ್ವದೇಶದವನಾದ ಇಸ್ರಾಯೇಲನನ್ನು ಕದ್ದು ಅವನನ್ನು ದಾಸತ್ವದಲ್ಲಿ ನಡಿಸಿದ್ದಾಗಲಿ ಅಥವಾ ಮಾರಿಬಿಟ್ಟದ್ದು ಕಂಡುಬಂದರೆ ಕದ್ದವನಿಗೆ ಮರಣಶಿಕ್ಷೆಯಾಗಬೇಕು. ಹೀಗೆ ಅಂಥ ದುಷ್ಟತ್ವವನ್ನು ನಿಮ್ಮ ಮಧ್ಯದಿಂದ ತೆಗೆದುಹಾಕಿಬಿಡಬೇಕು.",
			"8": "<sup>8</sup> ಕುಷ್ಠರೋಗಿಗಳ ವಿಷಯದಲ್ಲಿ ಯಾಜಕರಾದ ಲೇವಿಯರು ಬೋಧಿಸುವಂತೆಯೇ ಮಾಡುವುದಕ್ಕೆ ನೀವು ಜಾಗರೂಕರಾಗಿರಬೇಕು. ನಾನು ಅವರಿಗೆ ಆಜ್ಞಾಪಿಸಿದ ನಿಯಮಗಳನ್ನೇ ನೀವು ಅನುಸರಿಸಬೇಕು.",
			"9": "<sup>9</sup> ನೀವು ಐಗುಪ್ತದೇಶದಿಂದ ಬಂದಾಗ ದಾರಿಯಲ್ಲಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಮಿರ್ಯಾಮಳಿಗೆ ಮಾಡಿದ್ದನ್ನು ನೆನಪುಮಾಡಿಕೊಳ್ಳಿರಿ.",
			"10": "<sup>10</sup> ಮತ್ತೊಬ್ಬನಿಗೆ ಸಾಲಕೊಡುವಾಗ ಒತ್ತೆಯನ್ನು ತೆಗೆದುಕೊಳ್ಳುವುದಕ್ಕೆ ಅವನ ಮನೆಯೊಳಕ್ಕೆ ಹೋಗದೆ ಹೊರಗೆ ಇರಬೇಕು.",
			"11": "<sup>11</sup> ಸಾಲತೆಗೆದುಕೊಂಡವನೇ ಒತ್ತೆಯ ಸಾಮಾನುಗಳನ್ನು ತಂದುಕೊಡಬೇಕು.",
			"12": "<sup>12</sup> ಅವನು ಕೇವಲ ಬಡತನದಿಂದ ತನ್ನ ಕಂಬಳಿಯನ್ನೇ ಒತ್ತೆಯಾಗಿ ಇಟ್ಟ ಸಂದರ್ಭದಲ್ಲಿ ಹೊತ್ತುಮುಣುಗಿದಾಗ ಅದನ್ನು ಹಿಂದಕ್ಕೆ ಕೊಡಬೇಕು.",
			"13": "<sup>13</sup> ಅದನ್ನು ಇಟ್ಟುಕೊಂಡು ರಾತ್ರಿ ಮಲಗಬಾರದು; ಅವನು ಅದನ್ನು ಹೊದ್ದುಕೊಂಡೇ ಮಲಗಿಕೊಳ್ಳಬೇಕಲ್ಲಾ; ಅದಲ್ಲದೆ ಅವನು ನಿಮ್ಮನ್ನು ಹರಸುವನು, ಮತ್ತು ನೀವು ಮಾಡಿದ್ದು ಧರ್ಮಕಾರ್ಯವೆಂದು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ತಿಳಿದುಕೊಳ್ಳುವನು.",
			"14": "<sup>14</sup> ನೀವು ಸ್ವದೇಶದವರಲ್ಲಿಯಾಗಲಿ ಅಥವಾ ನಿಮ್ಮಲ್ಲಿರುವ ಅನ್ಯದೇಶದವರಲ್ಲಿಯಾಗಲಿ ಗತಿಯಿಲ್ಲದ ಬಡ ಕೂಲಿಯವನಿಗೆ",
			"15": "<sup>15</sup> ಏನೂ ಅನ್ಯಾಯಮಾಡದೆ ದಿನದ ಕೂಲಿಯನ್ನು ಆ ದಿನದಲ್ಲೇ ಹೊತ್ತುಮುಣುಗುವುದಕ್ಕೆ ಮುಂಚಿತವಾಗಿ ಅವನಿಗೆ ಕೊಡಬೇಕು. ಅವನಿಗೆ ಬೇರೆ ಗತಿಯಿಲ್ಲದ್ದರಿಂದ ಅದನ್ನೇ ಎದುರುನೋಡುತ್ತಾನಲ್ಲಾ. ನೀವು ಕೊಡದಿದ್ದರೆ ಅವನು ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಟ್ಟಾನು; ಆಗ ನೀವು ದೋಷಿಗಳಾಗಿ ಕಂಡುಬಂದೀರಿ.",
			"16": "<sup>16</sup> ಮಕ್ಕಳ ಪಾಪಕ್ಕಾಗಿ ತಂದೆಗೂ, ತಂದೆಯ ಪಾಪಕ್ಕಾಗಿ ಮಕ್ಕಳಿಗೂ ಮರಣಶಿಕ್ಷೆಯಾಗಬಾರದು. ಪ್ರತಿಯೊಬ್ಬನೂ ತನ್ನ ಪಾಪಫಲವನ್ನು ತಾನೇ ಅನುಭವಿಸಬೇಕು.",
			"17": "<sup>17</sup> ನೀವು ಪರದೇಶಿಯ ಅಥವಾ ದಿಕ್ಕಿಲ್ಲದವನ ವ್ಯಾಜ್ಯವನ್ನು ವಿಚಾರಿಸುವಾಗ ನ್ಯಾಯವನ್ನು ಬಿಟ್ಟು ತೀರ್ಪು ಕೊಡಬಾರದು. ವಿಧವೆಯಿಂದ ಉಡುವ ಬಟ್ಟೆಯನ್ನು ಒತ್ತೆಯಿಡಿಸಿಕೊಳ್ಳಬಾರದು.",
			"18": "<sup>18</sup> ನೀವೇ ಐಗುಪ್ತದೇಶದಲ್ಲಿ ದಾಸರಾಗಿದ್ದಾಗ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಬಿಡಿಸಿದನೆಂಬುವುದನ್ನು ನೆನಪುಮಾಡಿಕೊಳ್ಳಿರಿ; ಅದಕ್ಕಾಗಿಯೇ ಇದನ್ನು ಆಜ್ಞಾಪಿಸಿದ್ದೇನೆ.",
			"19": "<sup>19</sup> ನೀವು ಪೈರುಗಳನ್ನು ಕೊಯ್ಯುವಾಗ ಒಂದು ಸಿವುಡನ್ನು ಹೊಲದಲ್ಲೇ ಮರೆತುಬಂದರೆ ಅದನ್ನು ತರುವುದಕ್ಕೆ ಹಿಂದಕ್ಕೆ ಹೋಗಬಾರದು; ಪರದೇಶಿ, ಅನಾಥ, ವಿಧವೆ ಇಂಥವರಿಗೋಸ್ಕರ ಇರಲಿ. ನೀವು ಹೀಗೆ ನಡೆದುಕೊಂಡರೆ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮ ಎಲ್ಲಾ ಕೆಲಸಗಳಲ್ಲಿಯೂ ನಿಮ್ಮನ್ನು ಅಭಿವೃದ್ಧಿಪಡಿಸುವನು.",
			"20": "<sup>20</sup> ಎಣ್ಣೆಯಮರಗಳ ರೆಂಬೆಗಳನ್ನು ಹೊಡೆದು ಕಾಯಿಗಳನ್ನು ಉದುರಿಸಿದ ಮೇಲೆ ಪುನಃ ಉದುರಿಸುವುದಕ್ಕೆ ಹೋಗಬಾರದು; ಮಿಕ್ಕ ಕಾಯಿಗಳು ಪರದೇಶಿ, ಅನಾಥ, ವಿಧವೆ ಇಂಥವರಿಗೋಸ್ಕರ ಇರಲಿ.",
			"21": "<sup>21</sup> ದ್ರಾಕ್ಷೇತೋಟದ ಬೆಳೆಯನ್ನು ಕೂಡಿಸಿಕೊಳ್ಳುವಾಗ ಹಕ್ಕಲಾಯಬಾರದು; ಅದು ಪರದೇಶಿ, ಅನಾಥ, ವಿಧವೆ ಇಂಥವರಿಗೋಸ್ಕರ ಇರಲಿ.",
			"22": "<sup>22</sup> ನೀವೇ ಐಗುಪ್ತದೇಶದಲ್ಲಿ ದಾಸರಾಗಿದ್ದದ್ದು ಜ್ಞಾಪಕದಲ್ಲಿರಬೇಕು; ಅದಕ್ಕಾಗಿಯೇ ಇದನ್ನು ನಿಮಗೆ ಆಜ್ಞಾಪಿಸಿದ್ದೇನೆ."
		},
		"25": {
			"1": "<sup>1</sup> ಇಬ್ಬರು ವ್ಯಾಜ್ಯವಾಡುತ್ತಾ ನ್ಯಾಯಾಧಿಪತಿಗಳ ಬಳಿಗೆ ಬಂದರೆ, ನ್ಯಾಯಾಧಿಪತಿಗಳು ತಪ್ಪಿಲ್ಲದವನನ್ನು ನಿರಪರಾಧಿ ಎಂದೂ ತಪ್ಪುಳ್ಳವನನ್ನು ಅಪರಾಧಿ ಎಂದೂ ತೀರ್ಮಾನಿಸಬೇಕು.",
			"2": "<sup>2</sup> ಅಪರಾಧಿಗೆ ಪೆಟ್ಟಿನ ಶಿಕ್ಷೆ ತೀರ್ಮಾನವಾದರೆ ನ್ಯಾಯಾಧಿಪತಿಯು ಅವನನ್ನು ಮಲಗಿಸಿ, ಅವನ ಅಪರಾಧಕ್ಕೆ ಅನುಸಾರವಾಗಿ ಪೆಟ್ಟುಗಳನ್ನು ತನ್ನ ಮುಂದೆಯೇ ಹೊಡಿಸಿ ಲೆಕ್ಕಿಸಬೇಕು.",
			"3": "<sup>3</sup> ಪೆಟ್ಟುಗಳ ಶಿಕ್ಷೆಯು ನಲ್ವತ್ತಕ್ಕಿಂತ ಹೆಚ್ಚಿರಬಾರದು. ಅದಕ್ಕಿಂತ ಹೆಚ್ಚು ಪೆಟ್ಟುಗಳನ್ನು ನೀವು ಹೊಡಿಸಿದರೆ ನಿಮ್ಮ ಸ್ವದೇಶದವನನ್ನು ನೀವು ಕೇವಲ ನೀಚನನ್ನಾಗಿ ಕಂಡಂತೆ ಆಗುವುದು.",
			"4": "<sup>4</sup> ಕಣತುಳಿಯುವ ಎತ್ತಿನ ಬಾಯಿಯನ್ನು ಕಟ್ಟಬಾರದು.",
			"5": "<sup>5</sup> ಅಣ್ಣತಮ್ಮಂದಿರು ಒಂದೇ ಕುಟುಂಬದಲ್ಲಿರುವಾಗ ಅಣ್ಣನು ಮಗನಿಲ್ಲದೆ ಸತ್ತರೆ, ಅವನ ಹೆಂಡತಿ ಬೇರೆ ಪುರುಷನನ್ನು ಮದುವೆಯಾಗಬಾರದು. ಅವಳ ಮೈದುನನು ಅವಳನ್ನು ಹೆಂಡತಿಯನ್ನಾಗಿ ತೆಗೆದುಕೊಂಡು ಮೈದುನಧರ್ಮವನ್ನು ನೆರವೇರಿಸಬೇಕು.",
			"6": "<sup>6</sup> ಅವಳಲ್ಲಿ ಹುಟ್ಟುವ ಚೊಚ್ಚಲುಮಗನು ಸತ್ತವನ ಮಗನೆಂದು ಎಣಿಸಲ್ಪಡಬೇಕು. ಹೀಗೆ ಮಾಡುವುದರಿಂದ ಸತ್ತವನ ಹೆಸರು ಇಸ್ರಾಯೇಲರೊಳಗಿಂದ ಅಳಿಸಿ ಹೋಗುವುದಿಲ್ಲ.",
			"7": "<sup>7</sup> ತಮ್ಮನು ಅಣ್ಣನ ಹೆಂಡತಿಯನ್ನು ಪರಿಗ್ರಹಿಸದೆ ಹೋದರೆ ಅವಳು ಊರ ಬಾಗಿಲಿಗೆ ಹೋಗಿ ಅಲ್ಲಿಯ ಹಿರಿಯರಿಗೆ, “ನನ್ನ ಗಂಡನ ತಮ್ಮನು ತನ್ನ ಅಣ್ಣನ ಹೆಸರನ್ನು ಉಳಿಸುವುದಕ್ಕೆ ಸಿದ್ಧನಿಲ್ಲ. ಅವನು ಮೈದುನಧರ್ಮವನ್ನು ನಡೆಸುವುದಿಲ್ಲ ಅನ್ನುತ್ತಾನೆ” ಎಂದು ತಿಳಿಸಬೇಕು.",
			"8": "<sup>8</sup> ಆಗ ಆ ಊರಿನ ಹಿರಿಯರು ಅವನನ್ನು ಕರೆಯಿಸಿ ವಿಚಾರಿಸಿದಲ್ಲಿ ಅವನು ಅವರ ಮುಂದೆ ನಿಂತು, “ಈಕೆಯನ್ನು ಪರಿಗ್ರಹಿಸುವುದಕ್ಕೆ ನನಗೆ ಇಷ್ಟವಿಲ್ಲ” ಎಂದು ಹೇಳುವ ಪಕ್ಷಕ್ಕೆ,",
			"9": "<sup>9</sup> ಅವನ ಅತ್ತಿಗೆ ಹಿರಿಯರ ಎದುರಿನಲ್ಲಿ ಅವನ ಕೆರವನ್ನು ಅವನ ಕಾಲಿನಿಂದ ತೆಗೆದುಬಿಟ್ಟು, ಅವನ ಮುಖದ ಮೇಲೆ ಉಗುಳಿ, “ಅಣ್ಣನಿಗೋಸ್ಕರ ಸಂತಾನವನ್ನು ವೃದ್ಧಿಗೊಳಿಸುವುದಕ್ಕೆ ಮನಸ್ಸಿಲ್ಲದವರೆಲ್ಲರಿಗೆ ಇಂಥ ಅವಮಾನವಾಗಲಿ” ಎಂದು ಹೇಳಬೇಕು.",
			"10": "<sup>10</sup> ಇಸ್ರಾಯೇಲರಲ್ಲಿ ಆ ಮನುಷ್ಯನ ಮನೆಯವರಿಗೆ, “ಕೆರವನ್ನು ಬಿಚ್ಚಿಸಿಕೊಂಡವನ ಮನೆಯವರು” ಎಂದು ಹೆಸರುಂಟಾಗುವುದು.",
			"11": "<sup>11</sup> ಇಬ್ಬರು ಜಗಳವಾಡುತ್ತಿರಲಾಗಿ ಅವರಲ್ಲಿ ಒಬ್ಬನ ಹೆಂಡತಿ ಬಂದು ತನ್ನ ಗಂಡನನ್ನು ಬಿಡಿಸಬೇಕೆಂದು ಆ ಪರಪುರುಷನ ಜನನೇಂದ್ರಿಯವನ್ನು ಹಿಡಿದುಕೊಂಡರೆ,",
			"12": "<sup>12</sup> ಅವನು ಅವಳನ್ನು ಕನಿಕರಿಸದೆ ಅವಳ ಕೈಯನ್ನು ಕಡಿದುಹಾಕಬೇಕು.",
			"13": "<sup>13</sup> ಹೆಚ್ಚು ಕಡಿಮೆಯಾಗಿರುವ ಎರಡು ವಿಧವಾದ ತೂಕದ ಕಲ್ಲುಗಳನ್ನು ನಿಮ್ಮ ಚೀಲದಲ್ಲಿ ಇಟ್ಟುಕೊಳ್ಳಬಾರದು.",
			"14": "<sup>14</sup> ಹೆಚ್ಚುಕಡಿಮೆಯಾದ ಎರಡು ವಿಧವಾದ ಸೇರುಗಳು ಹಾಗೂ ಮೋಸ ಮಾಡುವ ತೂಕದ ಕಲ್ಲುಗಳು ನಿಮ್ಮ ಮನೆಯಲ್ಲಿ ಇರಬಾರದು.",
			"15": "<sup>15</sup> ನಿಮ್ಮ ತೂಕದ ಕಲ್ಲೂ ಮತ್ತು ನಿಮ್ಮ ಅಳತೆಯ ಸೇರೂ ನ್ಯಾಯವಾಗಿಯೇ ಇರಬೇಕು. ನೀವು ಹೀಗೆ ನಡೆದುಕೊಂಡರೆ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಕೊಡುವ ದೇಶದಲ್ಲಿ ಬಹುಕಾಲ ಬಾಳುವಿರಿ.",
			"16": "<sup>16</sup> ನ್ಯಾಯವಿರುದ್ಧವಾದ ಅಳತೆತೂಕಗಳನ್ನು ಮಾಡುವವರೆಲ್ಲರೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಅಸಹ್ಯರಾಗಿದ್ದಾರೆ.",
			"17": "<sup>17</sup> ನೀವು ಐಗುಪ್ತದೇಶದಿಂದ ಬರುವಾಗ ದಾರಿಯಲ್ಲಿ ಅಮಾಲೇಕ್ಯರು ನಿಮ್ಮನ್ನು ಎದುರಿಸಿದ್ದನ್ನು ಜ್ಞಾಪಿಸಿಕೊಳ್ಳಿರಿ.",
			"18": "<sup>18</sup> ಅವರು ದೇವರಲ್ಲಿ ಸ್ವಲ್ಪವೂ ಭಯಭಕ್ತಿಯಿಲ್ಲದವರಾಗಿ, ನೀವು ದಣಿದು ಮತ್ತು ಬಳಲಿದಾಗ ನಿಮ್ಮವರಲ್ಲಿ ಹಿಂದೆಬಿದ್ದ ಬಲಹೀನರನ್ನು ಸಂಹಾರ ಮಾಡಿದರು.",
			"19": "<sup>19</sup> ಆದಕಾರಣ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಸ್ವದೇಶವಾಗುವುದಕ್ಕೆ ಕೊಡುವ ದೇಶದಲ್ಲಿ ನಿಮ್ಮನ್ನು ಸೇರಿಸಿ ಸುತ್ತಲು ಯಾವ ಶತ್ರುಗಳೂ ಇಲ್ಲದಂತೆ ಮಾಡಿದಾಗ ಭೂಮಿಯ ಮೇಲೆ ಅಮಾಲೇಕ್ಯರ ಹೆಸರೇ ಉಳಿಯದಂತೆ ನೀವು ಮಾಡಬೇಕು; ಇದನ್ನು ಮರೆಯಬಾರದು."
		},
		"26": {
			"1": "<sup>1</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಸ್ವದೇಶವಾಗುವುದಕ್ಕೆ ಕೊಡುವ ದೇಶವನ್ನು ನೀವು ಸೇರಿ, ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡು ಅದರಲ್ಲಿ ವಾಸಿಸುವಾಗ,",
			"2": "<sup>2</sup> ನಿಮ್ಮಲಿರುವ ಪ್ರತಿಯೊಬ್ಬನೂ ಆತನು ದಯಪಾಲಿಸಿದ ಹೊಲದ ಎಲ್ಲಾ ಬೆಳೆಗಳಲ್ಲಿ ಪ್ರಥಮಫಲಗಳನ್ನು ಪುಟ್ಟಿಯಲ್ಲಿಟ್ಟು, ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ತನ್ನ ಹೆಸರನ್ನು ಸ್ಥಾಪಿಸುವುದಕ್ಕೆ ಆದುಕೊಳ್ಳುವ ಸ್ಥಳಕ್ಕೆ ತರಬೇಕು.",
			"3": "<sup>3</sup> ನೀವು ಅದನ್ನು ತೆಗೆದುಕೊಂಡುಹೋಗಿ ಆ ಕಾಲದಲ್ಲಿ ಇರುವ ಮಹಾಯಾಜಕನಿಗೆ, “ಯೆಹೋವನು ನಮ್ಮ ಪಿತೃಗಳಿಗೆ ಪ್ರಮಾಣಮಾಡಿಕೊಟ್ಟ ದೇಶದಲ್ಲಿ ನಾವು ಸೇರಿದ್ದಾಯಿತೆಂದು ನೀನು ಸೇವಿಸುವ ದೇವರಾದ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನಾವು ಈಗ ಒಪ್ಪಿಕೊಂಡಿದ್ದೇವೆ” ಎಂದು ಹೇಳಬೇಕು.",
			"4": "<sup>4</sup> ಯಾಜಕನು ಆ ಪುಟ್ಟಿಯನ್ನು ನಿಮ್ಮ ಕೈಯಿಂದ ತೆಗೆದುಕೊಂಡು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಯಜ್ಞವೇದಿಯ ಮುಂದೆ ಇಡುವನು.",
			"5": "<sup>5</sup> ಆಗ ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ, “ಅರಾಮ್ಯನಾದ ನಮ್ಮ ಮೂಲಪಿತೃವು ಗತಿಯಿಲ್ಲದವನಾಗಿ ಅಲೆದಾಡುತ್ತಾ ಸ್ವಲ್ಪ ಜನರೊಡನೆ ಐಗುಪ್ತದೇಶಕ್ಕೆ ಹೋಗಿ ಅಲ್ಲಿ ಇಳಿದುಕೊಂಡನು. ಅಲ್ಲಿ ಅವನ ಸಂತಾನದವರು ಹೆಚ್ಚಿ ಅವರು ಮಹಾಬಲಿಷ್ಠ ಜನಾಂಗವಾದರು.",
			"6": "<sup>6</sup> ಐಗುಪ್ತ್ಯರು ನಮ್ಮನ್ನು ಉಪದ್ರವಪಡಿಸಿ, ಬಾಧಿಸಿ, ನಮ್ಮಿಂದ ಕಠಿಣವಾಗಿ ದುಡಿಸಿಕೊಂಡರು.",
			"7": "<sup>7</sup> ಆಗ ನಾವು ನಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಡಲಾಗಿ ಆತನು ನಮ್ಮ ಸ್ವರವನ್ನು ಕೇಳಿ, ನಮ್ಮ ದುರವಸ್ಥೆಯನ್ನೂ, ಕಷ್ಟವನ್ನೂ ಮತ್ತು ಉಪದ್ರವವನ್ನೂ ನೋಡಿದನು.",
			"8": "<sup>8</sup> ಆತನು ತನ್ನ ಭುಜಬಲವನ್ನೂ, ಶಿಕ್ಷಾಹಸ್ತವನ್ನೂ, ಮಹಾಭೀತಿಗಳನ್ನೂ, ಮಹತ್ಕಾರ್ಯಗಳನ್ನೂ ಮತ್ತು ಸೂಚಕಕಾರ್ಯಗಳನ್ನು ಪ್ರಯೋಗಿಸಿ ನಮ್ಮನ್ನು ಬಿಡಿಸಿದನು.",
			"9": "<sup>9</sup> ಅವನು ನಮ್ಮನ್ನು ಐಗುಪ್ತದೇಶದಿಂದ ಕರೆತಂದು ಹಾಲೂ ಮತ್ತು ಜೇನೂ ಹರಿಯುವ ಈ ದೇಶವನ್ನು ನಮಗೆ ಕೊಟ್ಟಿದ್ದಾನೆ.",
			"10": "<sup>10</sup> ಆದುದರಿಂದ ಯೆಹೋವನೇ, ನೀನು ಅನುಗ್ರಹಿಸಿರುವ ಭೂಮಿಯ ಬೆಳೆಯಲ್ಲಿ ಪ್ರಥಮಫಲಗಳನ್ನು ನಾವು ಕಾಣಿಕೆಯಾಗಿ ತಂದಿದ್ದೇವೆ, ಅದನ್ನು ಸ್ವೀಕರಿಸಬೇಕು” ಎಂದು ಹೇಳಿ ಆ ಪ್ರಥಮಫಲಗಳನ್ನು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿಟ್ಟು ಆತನನ್ನು ಆರಾಧಿಸಬೇಕು.",
			"11": "<sup>11</sup> ಆತನು ನಿಮಗೂ ಮತ್ತು ನಿಮ್ಮ ಮನೆಯವರಿಗೂ ಉಂಟುಮಾಡಿದ ಎಲ್ಲಾ ಸುಖಸಂತೋಷಗಳಿಗಾಗಿ ನೀವೂ, ಲೇವಿಯರೂ ಮತ್ತು ನಿಮ್ಮ ಮಧ್ಯದಲ್ಲಿರುವ ಪರದೇಶಿಗಳೂ ಸಂಭ್ರಮದಿಂದಿರಬೇಕು.",
			"12": "<sup>12</sup> ದಶಮಾಂಶವನ್ನು ಕೊಡಬೇಕಾದ ಮೂರನೆಯ ವರ್ಷದಲ್ಲಿ ನಿಮಗುಂಟಾದ ಆದಾಯದ ದಶಮ ಭಾಗವನ್ನು ನೀವು ಪ್ರತ್ಯೇಕಿಸಿದಾಗ, ನಿಮ್ಮ ನಿಮ್ಮ ಊರುಗಳಲ್ಲಿರುವ ಲೇವಿಯರೂ, ಪರದೇಶಿಗಳೂ, ಅನಾಥರು ಮತ್ತು ವಿಧವೆಯರೂ ಊಟಮಾಡಿ ಸಂತೋಷವಾಗಿರುವಂತೆ ಅದನ್ನು ಅವರಿಗೆ ಕೊಡಬೇಕಲ್ಲಾ.",
			"13": "<sup>13</sup> ಆಗ ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ, “ನಾವು ಪ್ರತ್ಯೆಕಿಸಿದ ವಸ್ತುಗಳನ್ನು ಮನೆಯಿಂದ ತೆಗೆದುಕೊಂಡು ಬಂದಿದ್ದೇವೆ. ನೀನು ಆಜ್ಞಾಪಿಸಿದ ಮೇರೆಗೆ ಅವುಗಳನ್ನು ಲೇವಿಯರು, ಪರದೇಶಿಗಳು, ಅನಾಥರು, ವಿಧವೆಯರು ಇಂಥವರಿಗೆ ಕೊಟ್ಟಿದ್ದೇವೆ ನಿನ್ನ ಆಜ್ಞೆಗಳಲ್ಲಿ ಒಂದನ್ನೂ ಮೀರಲಿಲ್ಲ ಮತ್ತು ಮರೆಯಲೂ ಇಲ್ಲ.",
			"14": "<sup>14</sup> ನಾವು ಸತ್ತವರಿಗೋಸ್ಕರ ದುಃಖಿಸುವ ಕಾಲದಲ್ಲೆಲ್ಲಾ ಅದರಲ್ಲಿ ಏನೂ ತಿನ್ನಲಿಲ್ಲ; ಅಶುದ್ಧರಾಗಿ ಅದನ್ನು ತೆಗೆದುಹಾಕಲಿಲ್ಲ; ಸತ್ತವರಿಗೋಸ್ಕರ ಅದರಲ್ಲಿ ಏನೂ ಕೊಡಲಿಲ್ಲ; ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಮಾತಿನಲ್ಲಿ ಲಕ್ಷ್ಯವಿಟ್ಟು ನೀನು ನೇಮಿಸಿದ್ದನ್ನೆಲ್ಲಾ ಅನುಸರಿಸಿದ್ದೇವೆ.",
			"15": "<sup>15</sup> ನೀನು ನಿನ್ನ ಪರಿಶುದ್ಧ ವಾಸಸ್ಥಾನವಾಗಿರುವ ಪರಲೋಕದಿಂದ ಕೆಳಗಿರುವ ನಮ್ಮನ್ನು ಕಟಾಕ್ಷಿಸಿ, ನಿನ್ನ ಜನರಾದ ಇಸ್ರಾಯೇಲರನ್ನೂ ಹಾಲೂ ಮತ್ತು ಜೇನೂ ಹರಿಯುವ ದೇಶದಲ್ಲಿ ನೀನು ನಮ್ಮ ಪಿತೃಗಳಿಗೆ ವಾಗ್ದಾನಮಾಡಿದಂತೆ ನಮ್ಮನ್ನು ನೆಲೆಸುವಂತೆ ಮಾಡಿ, ನಮ್ಮನ್ನು ಈ ಭೂಮಿಯನ್ನೂ ಆಶೀರ್ವದಿಸು ದೇವರೇ” ಎಂದು ಹೇಳಬೇಕು.",
			"16": "<sup>16</sup> ಈ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ನೀವು ಅನುಸರಿಸಬೇಕೆಂದು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಈಗ ಆಜ್ಞಾಪಿಸಿದ್ದಾನೆ. ಆದುದರಿಂದ ನೀವು ಸಂಪೂರ್ಣವಾದ ಹೃದಯದಿಂದಲೂ, ಮನಸ್ಸಿನಿಂದಲೂ ಇವುಗಳನ್ನು ಕೈಕೊಂಡು ನಡೆಯಬೇಕು.",
			"17": "<sup>17</sup> ನಮಗೆ ಯೆಹೋವನೇ ದೇವರಾಗಿರುವನೆಂದೂ, ಆತನು ಹೇಳಿದ ಮಾರ್ಗದಲ್ಲೇ ನಡೆದು ಆತನ ಆಜ್ಞಾವಿಧಿನಿಯಮಗಳನ್ನು ಕೈಕೊಂಡು, ಆತನ ಮಾತಿಗೆ ಲಕ್ಷ್ಯವಿಡುವೆವು ಎಂದು ನೀವು ಈಗ ಒಡಂಬಟ್ಟಿದ್ದೀರಿ.",
			"18": "<sup>18</sup> ಯೆಹೋವನೋ ನಿಮ್ಮ ವಿಷಯದಲ್ಲಿ, “ಇವರು ನನ್ನ ಆಜ್ಞೆಗಳನ್ನೆಲ್ಲಾ ಅನುಸರಿಸುವ ಪಕ್ಷಕ್ಕೆ ನನ್ನ ವಾಗ್ದಾನಕ್ಕೆ ಅನುಸಾರವಾಗಿ ನನಗೆ",
			"19": "<sup>19</sup> ಸ್ವಕೀಯಜನರಾಗಿ ಇರುವರೆಂದೂ, ನಾನು ನಿರ್ಮಿಸಿದ ಬೇರೆ ಎಲ್ಲಾ ಜನಾಂಗಗಳಿಗಿಂತಲೂ ಇವರಿಗೆ ಹೆಚ್ಚಾದ ಕೀರ್ತಿ ಮತ್ತು ಘನಮಾನಗಳನ್ನು ಉಂಟುಮಾಡುವೆನೆಂದೂ ಇವರು ನನ್ನ ಮಾತಿನ ಮೇರೆಗೆ ತಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೋಸ್ಕರ ಮೀಸಲಾದ ಜನರಾಗುವರು” ಎಂದು ಒಪ್ಪಿಕೊಂಡಿದ್ದಾನೆ."
		},
		"27": {
			"1": "<sup>1</sup> ಮೋಶೆ ಮತ್ತು ಇಸ್ರಾಯೇಲರ ಹಿರಿಯರ ಸಹಿತ ಜನರಿಗೆ, “ನಾನು ಈಗ ನಿಮಗೆ ಆಜ್ಞಾಪಿಸಿದ ಧರ್ಮೋಪದೇಶವನ್ನೆಲ್ಲಾ ನೀವು ಅನುಸರಿಸಬೇಕು.",
			"2": "<sup>2</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಕೊಡುವ ದೇಶದಲ್ಲಿ ಪ್ರವೇಶಿಸುವುದಕ್ಕೆ ನೀವು ಯೊರ್ದನ್ ನದಿಯನ್ನು ದಾಟುವಾಗ ನೀವು ದೊಡ್ಡ ಕಲ್ಲುಗಳನ್ನು ನಿಲ್ಲಿಸಿ, ಅವುಗಳಿಗೆ ಗಿಲಾವು ಮಾಡಿಸಬೇಕು.",
			"3": "<sup>3</sup> ನಿಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಯೆಹೋವನು ತಾನು ವಾಗ್ದಾನಮಾಡಿದಂತೆ ನಿಮಗೆ ಕೊಡುವ ಹಾಲೂ ಮತ್ತು ಜೇನೂ ಹರಿಯುವ ದೇಶವನ್ನು ಪ್ರವೇಶಿಸುವುದಕ್ಕೆ ನೀವು ಯೊರ್ದನ್ ನದಿಯನ್ನು ದಾಟುವಾಗ ಈ ಧರ್ಮಶಾಸ್ತ್ರದ ವಾಕ್ಯಗಳನ್ನೆಲ್ಲಾ ಆ ದೊಡ್ಡ ಕಲ್ಲುಗಳ ಮೇಲೆ ಬರೆಯಿಸಬೇಕು.",
			"4": "<sup>4</sup> ನೀವು ಯೊರ್ದನ್ ನದಿಯನ್ನು ದಾಟಿದ ನಂತರ ನಾನು ಈಗ ಆಜ್ಞಾಪಿಸಿದ ಕಲ್ಲುಗಳನ್ನು ಏಬಾಲ್ ಬೆಟ್ಟದ ಮೇಲೆ ನಿಲ್ಲಿಸಿ ಅವುಗಳಿಗೆ ಗಿಲಾವು ಮಾಡಿಸಬೇಕು.",
			"5": "<sup>5</sup> “ಅಲ್ಲಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೋಸ್ಕರ ನೀವು ಕಲ್ಲುಗಳಿಂದ ಯಜ್ಞವೇದಿಯನ್ನು ಕಟ್ಟಿಸಬೇಕು.",
			"6": "<sup>6</sup> ಉಳಿ ಮುಂತಾದದ್ದನ್ನು ಉಪಯೋಗಿಸದೆ ಹುಟ್ಟುಕಲ್ಲುಗಳಿಂದಲೇ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಯಜ್ಞವೇದಿಯನ್ನು ಕಟ್ಟಿಸಬೇಕು.",
			"7": "<sup>7</sup> ಅಲ್ಲಿ ಸರ್ವಾಂಗಹೋಮಗಳನ್ನೂ ಮತ್ತು ಸಮಾಧಾನಯಜ್ಞಗಳನ್ನೂ ಸಮರ್ಪಿಸಿ, ಸಹಭೋಜನಮಾಡಿ, ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸಂಭ್ರಮಿಸಬೇಕು.",
			"8": "<sup>8</sup> ನೀವು ಆ ಕಲ್ಲುಗಳ ಮೇಲೆ ಈ ಧರ್ಮಶಾಸ್ತ್ರದ ವಾಕ್ಯಗಳನ್ನೆಲ್ಲಾ ಸ್ಪಷ್ಟವಾಗಿ ಬರೆಯಬೇಕು” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"9": "<sup>9</sup> ಮೋಶೆ, ಯಾಜಕರು ಮತ್ತು ಲೇವಿಯರೊಡನೆ ಇಸ್ರಾಯೇಲರೆಲ್ಲರಿಗೆ, “ಇಸ್ರಾಯೇಲರೇ, ನಿಶ್ಯಬ್ದವಾಗಿದ್ದು ಕೇಳಿರಿ, ನೀವು ಈಗ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಜನರಾಗಿದ್ದೀರಷ್ಟೆ.",
			"10": "<sup>10</sup> ಆದುದರಿಂದ ಈಗ ನಾನು ನಿಮಗೆ ಹೇಳುವ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಮಾತುಗಳಿಗೆ ಕಿವಿಗೊಟ್ಟು ಆತನ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ಅನುಸರಿಸಬೇಕು” ಎಂದು ಹೇಳಿದನು.",
			"11": "<sup>11</sup> ಮೋಶೆ ಆ ದಿನದಲ್ಲಿ ಜನರಿಗೆ ಆಜ್ಞಾಪಿಸಿದ್ದೇನೆಂದರೆ,",
			"12": "<sup>12</sup> “ನೀವು ಯೊರ್ದನ್ ನದಿಯನ್ನು ದಾಟಿದಾಗ ಸಿಮೆಯೋನ್, ಲೇವಿ, ಯೆಹೂದ, ಇಸ್ಸಾಕಾರ್, ಯೋಸೇಫ್ ಮತ್ತು ಬೆನ್ಯಾಮೀನ್ ಎಂಬ ಈ ಕುಲಗಳವರು ಗೆರಿಜ್ಜೀಮ್ ಬೆಟ್ಟದ ಮೇಲೆ ನಿಂತುಕೊಂಡು ಆಶೀರ್ವಾದಗಳನ್ನು ಉಚ್ಚರಿಸಬೇಕು.",
			"13": "<sup>13</sup> ರೂಬೇನ್, ಗಾದ್, ಆಶೇರ್, ಜೆಬುಲೂನ್, ದಾನ್ ಮತ್ತು ನಫ್ತಾಲಿ ಎಂಬ ಈ ಕುಲಗಳವರು ಏಬಾಲ್ ಬೆಟ್ಟದ ಮೇಲೆ ನಿಂತುಕೊಂಡು ಶಾಪೋಕ್ತಿಗಳನ್ನು ಉಚ್ಚರಿಸಬೇಕು.",
			"14": "<sup>14</sup> “ಲೇವಿಯರು ಗಟ್ಟಿಯಾದ ಸ್ವರದಿಂದ ಇಸ್ರಾಯೇಲರೆಲ್ಲರಿಗೂ,",
			"15": "<sup>15</sup> ‘ಶಿಲ್ಪಿಯ ಕೆಲಸವಾಗಿರುವ ಮರದ ವಿಗ್ರಹ ಮತ್ತು ಲೋಹವಿಗ್ರಹ ಯೆಹೋವನಿಗೆ ಅಸಹ್ಯವಾದುದರಿಂದ ಅವುಗಳನ್ನು ಮಾಡಿಸಿ ಗುಪ್ತವಾಗಿ ನಿಲ್ಲಿಸಿಕೊಂಡವನು ಶಾಪಗ್ರಸ್ತನು’ ಎಂದು ಹೇಳಲು ಜನರೆಲ್ಲರೂ, ‘ಆಮೆನ್’” ಅನ್ನಬೇಕು.",
			"16": "<sup>16</sup> “ಲೇವಿಯರು, ತಂದೆತಾಯಿಗಳನ್ನು ಅವಮಾನಪಡಿಸಿದವನು ಶಾಪಗ್ರಸ್ತನು” ಎಂದು ಹೇಳಲು ಜನರೆಲ್ಲರೂ, “ಆಮೆನ್” ಅನ್ನಬೇಕು.",
			"17": "<sup>17</sup> “ಅವರು, ಮತ್ತೊಬ್ಬನ ಗಡಿಮೇರೆಯನ್ನು ಮೀರಿಬಂದವನು ಶಾಪಗ್ರಸ್ತನು” ಎಂದು ಹೇಳಲು, “ಆಮೆನ್” ಅನ್ನಬೇಕು.",
			"18": "<sup>18</sup> “ಅವರು, ಕುರುಡರಿಗೆ ದಾರಿತಪ್ಪಿಸಿದವನು ಶಾಪಗ್ರಸ್ತನು” ಎಂದು ಹೇಳಲು, “ಆಮೆನ್” ಅನ್ನಬೇಕು.",
			"19": "<sup>19</sup> “ಅವರು, ಪರದೇಶಿ, ಅನಾಥ, ಇವರ ವ್ಯಾಜ್ಯದಲ್ಲಿ ನ್ಯಾಯ ಬಿಟ್ಟು ತೀರ್ಪುಹೇಳಿದವನು ಶಾಪಗ್ರಸ್ತನು” ಎಂದು ಹೇಳಲು ಜನರೆಲ್ಲರೂ, “ಆಮೆನ್” ಅನ್ನಬೇಕು.",
			"20": "<sup>20</sup> “ಅವರು, ಮಲತಾಯಿಯನ್ನು ಸಂಗಮಿಸಿ ತಂದೆಗೆ ಅವಮಾನಪಡಿಸಿದವನು ಶಾಪಗ್ರಸ್ತನು” ಎಂದು ಹೇಳಲು ಜನರೆಲ್ಲರೂ, “ಆಮೆನ್” ಅನ್ನಬೇಕು.",
			"21": "<sup>21</sup> “ಅವರು, ಪಶುಸಂಗಮ ಮಾಡಿದವನು ಶಾಪಗ್ರಸ್ತನು” ಎಂದು ಹೇಳಲು ಜನರೆಲ್ಲರೂ, “ಆಮೆನ್” ಅನ್ನಬೇಕು.",
			"22": "<sup>22</sup> “ಅವರು, ತಂದೆಯ ಮಗಳನ್ನಾಗಲಿ ಅಥವಾ ತಾಯಿಯ ಮಗಳನ್ನಾಗಲಿ ಸಂಗಮಿಸಿದವನು ಶಾಪಗ್ರಸ್ತನು” ಎಂದು ಹೇಳಲು ಜನರೆಲ್ಲರೂ, “ಆಮೆನ್” ಅನ್ನಬೇಕು.",
			"23": "<sup>23</sup> “ಅವರು, ಅತ್ತೆಯನ್ನು ಸಂಗಮಿಸಿದವನು ಶಾಪಗ್ರಸ್ತನು” ಎಂದು ಹೇಳಲು ಜನರೆಲ್ಲರೂ, “ಆಮೆನ್” ಅನ್ನಬೇಕು.",
			"24": "<sup>24</sup> “ಅವರು, ರಹಸ್ಯವಾಗಿ ನರಹತ್ಯಮಾಡಿದವನು ಶಾಪಗ್ರಸ್ತನು” ಎಂದು ಹೇಳಲು ಜನರೆಲ್ಲರೂ, “ಆಮೆನ್” ಅನ್ನಬೇಕು.",
			"25": "<sup>25</sup> “ಅವರು, ಹಣತೆಗೆದುಕೊಂಡು ನಿರಪರಾಧಿಯನ್ನು ಕೊಂದವನು ಶಾಪಗ್ರಸ್ತನು” ಎನ್ನಲಾಗಿ ಜನರೆಲ್ಲರೂ, “ಆಮೆನ್” ಅನ್ನಬೇಕು.",
			"26": "<sup>26</sup> “ಅವರು, ಈ ಧರ್ಮಶಾಸ್ತ್ರವಾಕ್ಯಗಳಿಗೆ ಒಡಂಬಟ್ಟು ಕೈಕೊಳ್ಳದೆ ಇರುವವನು ಶಾಪಗ್ರಸ್ತನು” ಎಂದು ಹೇಳಲು ಜನರೆಲ್ಲರೂ, “ಆಮೆನ್” ಅನ್ನಬೇಕು."
		},
		"28": {
			"1": "<sup>1</sup> ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಮಾತನ್ನು ಶ್ರದ್ಧೆಯಿಂದ ಕೇಳಿ, ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ಆತನ ಆಜ್ಞೆಗಳನ್ನೆಲ್ಲಾ ಅನುಸರಿಸಿ ನಡೆದರೆ, ಆತನು ಭೂಮಿಯ ಮೇಲಿರುವ ಎಲ್ಲಾ ಜನಾಂಗಗಳಿಗಿಂತಲೂ ನಿಮ್ಮನ್ನು ಉನ್ನತಸ್ಥಿತಿಗೆ ತರುವನು.",
			"2": "<sup>2</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಮಾತಿಗೆ ಕಿವಿಗೊಟ್ಟು ನಡೆದರೆ ಈ ಶುಭಗಳೆಲ್ಲಾ ನಿಮಗೆ ಪ್ರಾಪ್ತವಾಗುವವು. ಅವು ಯಾವುವೆಂದರೆ:",
			"3": "<sup>3</sup> ನಿಮಗೆ ಊರಿನಲ್ಲಿಯೂ ಮತ್ತು ಅಡವಿಯಲ್ಲಿಯೂ ಶುಭವುಂಟಾಗುವುದು.",
			"4": "<sup>4</sup> ನಿಮ್ಮ ಸಂತಾನದವರಿಗೂ, ವ್ಯವಸಾಯಗಳಿಗೂ, ದನ, ಕುರಿ ಮುಂತಾದ ಪಶುಗಳಿಗೂ ಶುಭವುಂಟಾಗುವುದು.",
			"5": "<sup>5</sup> ನಿಮ್ಮ ಪುಟ್ಟಿಗಳಿಗೂ ಮತ್ತು ಕೊಣವಿಗೆಗಳಿಗೂ (ಹಿಟ್ಟು ನಾದುವ ಬೋಗುಣಿ) ಶುಭವುಂಟಾಗುವುದು.",
			"6": "<sup>6</sup> ನೀವು ಹೊರಗೆ ಹೋಗುವಾಗಲೂ ಒಳಗೆ ಬರುವಾಗಲೂ ಶುಭವುಂಟಾಗುವುದು.",
			"7": "<sup>7</sup> ನಿಮ್ಮ ವಿರುದ್ಧವಾಗಿ ಬರುವ ಶತ್ರುಗಳು ನಿಮ್ಮಿಂದ ಸೋತುಹೋಗುವಂತೆ ಯೆಹೋವನು ಮಾಡುವನು; ಅವರು ಒಂದೇ ದಾರಿಯಿಂದ ನಿಮ್ಮ ವಿರುದ್ಧವಾಗಿ ಬಂದರೂ ಏಳು ದಾರಿಗಳಿಂದ ಓಡಿಹೋಗುವರು.",
			"8": "<sup>8</sup> ನಿಮ್ಮ ಕಣಜಗಳು ಯಾವಾಗಲೂ ತುಂಬಿರುವಂತೆಯೂ ಮತ್ತು ನಿಮ್ಮ ಪ್ರಯತ್ನಗಳೆಲ್ಲಾ ಸಫಲವಾಗುವಂತೆಯೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಶುಭವನ್ನು ಅನುಗ್ರಹಿಸುವನು. ಆತನು ನಿಮಗೆ ಕೊಡುವ ದೇಶದಲ್ಲಿ ನಿಮಗೆ ಶುಭವನ್ನೇ ಉಂಟುಮಾಡುವನು.",
			"9": "<sup>9</sup> ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಆಜ್ಞೆಗಳನ್ನು ಅನುಸರಿಸಿ ಆತನು ಹೇಳಿದ ಮಾರ್ಗದಲ್ಲೇ ನಡೆದರೆ, ಆತನು ವಾಗ್ದಾನಮಾಡಿದಂತೆ ನಿಮ್ಮನ್ನು ತನಗೋಸ್ಕರ ಮೀಸಲಾದ ಜನರನ್ನಾಗಿ ಸ್ಥಾಪಿಸುವನು.",
			"10": "<sup>10</sup> ಭೂಮಿಯಲ್ಲಿರುವ ಎಲ್ಲಾ ಜನಗಳೂ ನಿಮ್ಮನ್ನು ಯೆಹೋವನ ಜನರೆಂದು ತಿಳಿದುಕೊಂಡು ನಿಮಗೆ ಭಯಪಡುವರು.",
			"11": "<sup>11</sup> ಯೆಹೋವನು ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ಪ್ರಮಾಣ ಮಾಡಿದ ಮೇರೆಗೆ ನಿಮಗೆ ಕೊಡುವ ದೇಶದಲ್ಲಿ ಆತನು ನಿಮ್ಮ ಸಂತಾನ, ಪಶು ಮತ್ತು ವ್ಯವಸಾಯಗಳಲ್ಲಿ ಸಮೃದ್ಧಿಯನ್ನು ಉಂಟುಮಾಡುವನು.",
			"12": "<sup>12</sup> ಯೆಹೋವನು ಆಕಾಶದಲ್ಲಿರುವ ತನ್ನ ಜಲನಿಧಿಯನ್ನು ತೆರೆದು ನಿಮ್ಮ ದೇಶದ ಮೇಲೆ ಬೆಳೆಗೆ ಬೇಕಾದ ಹಾಗೆ ಮಳೆಯನ್ನು ಸುರಿಸಿ, ನಿಮ್ಮ ಎಲ್ಲಾ ವ್ಯವಸಾಯವು ಫಲಭರಿತವಾಗುವಂತೆ ಸಾಫಲ್ಯತೆ ಉಂಟುಮಾಡುವನು. ನೀವು ಅನೇಕಜನಗಳಿಗೆ ಸಾಲಕೊಡುವಿರೇ ಹೊರತು ಸಾಲತೆಗೆದುಕೊಳ್ಳುವುದಿಲ್ಲ.",
			"13": "<sup>13</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಆಜ್ಞೆಗಳನ್ನೇ ಲಕ್ಷ್ಯವಿಟ್ಟು ಕೇಳಿದರೆ, ನೀವು ಎಲ್ಲರಿಗಿಂತಲೂ ಮೇಲಿನವರಾಗಿರುವಿರೇ ಹೊರತು ಇತರರಿಗೆ ಅಧೀನರಾಗುವುದಿಲ್ಲ.",
			"14": "<sup>14</sup> ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ಮಾತುಗಳನ್ನು ಬಿಟ್ಟು ಎಡಕ್ಕಾಗಲಿ ಅಥವಾ ಬಲಕ್ಕಾಗಲಿ ತಿರುಗದೆ, ಬೇರೆ ದೇವರುಗಳನ್ನು ಅವಲಂಬಿಸದೆ, ಅವುಗಳನ್ನೇ ಅನುಸರಿಸಿ ನಡೆದರೆ ಆತನು ಇತರರಿಗೆ ನಿಮ್ಮನ್ನು ಅಧೀನಮಾಡದೆ ಎಲ್ಲರಿಗೂ ಶಿರಸ್ಸನ್ನಾಗಿಯೇ ಮಾಡುವನು.",
			"15": "<sup>15</sup> ಆದರೆ ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಮಾತಿಗೆ ಕಿವಿಗೊಡದೆ, ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ಆತನ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ಅನುಸರಿಸದೆ ಹೋದರೆ ಈ ಶಾಪಗಳು ನಿಮಗೆ ಪ್ರಾಪ್ತವಾಗುವವು:",
			"16": "<sup>16</sup> ನಿಮಗೆ ಊರಿನಲ್ಲಿಯೂ ಮತ್ತು ಅಡವಿಯಲ್ಲಿಯೂ ಶಾಪ ಉಂಟಾಗುವುದು.",
			"17": "<sup>17</sup> ನಿಮ್ಮ ಪುಟ್ಟಿಗಳಿಗೂ ಮತ್ತು ಕೊಣವಿಗೆಗಳಿಗೂ ಶಾಪ ಉಂಟಾಗುವುದು.",
			"18": "<sup>18</sup> ನಿಮ್ಮ ಸಂತಾನ, ವ್ಯವಸಾಯ, ದನ ಮತ್ತು ಕುರಿಗಳಿಗೂ ಶಾಪ ಉಂಟಾಗುವುದು.",
			"19": "<sup>19</sup> ನೀವು ಹೊರಗೆ ಹೋಗುವಾಗಲೂ, ಒಳಗೆ ಬರುವಾಗಲೂ ಶಾಪ ಉಂಟಾಗುವುದು.",
			"20": "<sup>20</sup> ನೀವು ದುರ್ನಡತೆಯುಳ್ಳವರಾಗಿ ಯೆಹೋವನನ್ನು ಬಿಟ್ಟಿದ್ದರಿಂದ ಆತನು ನಿಮ್ಮ ಎಲ್ಲಾ ಪ್ರಯತ್ನಗಳಲ್ಲಿಯೂ ವಿಪತ್ತು, ಕಳವಳ, ಶಾಪ ಇವುಗಳನ್ನು ಉಂಟುಮಾಡುತ್ತಾ ನಿಮ್ಮನ್ನು ಬೇಗನೆ ನಾಶಮಾಡುವನು.",
			"21": "<sup>21</sup> ವ್ಯಾಧಿಯು ನಿಮಗೆ ಹತ್ತಿಕೊಂಡೇ ಇರುವಂತೆ ಯೆಹೋವನು ಮಾಡಿ, ನೀವು ಸ್ವಾಧೀನ ಮಾಡಿಕೊಳ್ಳುವುದಕ್ಕೆ ಹೋಗುವ ದೇಶದಲ್ಲಿ ನಿಮ್ಮಲ್ಲಿ ಒಬ್ಬರೂ ಉಳಿಯದಂತೆ ಮಾಡುವನು.",
			"22": "<sup>22</sup> ಆತನು ನಿಮ್ಮನ್ನು ಕ್ಷಯರೋಗ, ಚಳಿಜ್ವರ, ಉರಿತ, ಉಷ್ಣಜ್ವರಗಳಿಂದಲೂ, ದೇಶವನ್ನು ಕ್ಷಾಮದಿಂದಲೂ ಮತ್ತು ಬೆಳೆಯನ್ನು ಕಾಡಿನ ಬಿಸಿಗಾಳಿಗಳಿಂದಲೂ ನರಳುವಂತೆ ಮಾಡುವನು. ನೀವು ಸಾಯುವ ತನಕ ಇವು ನಿಮ್ಮನ್ನು ಬೆನ್ನತ್ತುವವು.",
			"23": "<sup>23</sup> ಮೇಲೆ ಆಕಾಶವು ಮಳೆಸುರಿಸದೆ ತಾಮ್ರದಂತೆಯೂ ಮತ್ತು ಕೆಳಗೆ ಭೂಮಿಯು ಬೆಳೆಕೊಡದೆ ಕಬ್ಬಿಣದಂತೆಯೂ ಇರುವವು.",
			"24": "<sup>24</sup> ನಿಮ್ಮ ದೇಶದಲ್ಲಿ ಯೆಹೋವನು ಆಕಾಶದಿಂದ ಮಳೆಗೆ ಬದಲಾಗಿ ಧೂಳನ್ನೂ ಮತ್ತು ಉಸುಬನ್ನೂ ನಿಮ್ಮ ಮೇಲೆ ಸುರಿಸುವನು; ಅದುದರಿಂದ ನೀವು ನಾಶವಾಗಿ ಹೋಗುವಿರಿ.",
			"25": "<sup>25</sup> ನೀವು ಶತ್ರುಗಳಿಂದ ಸೋಲನ್ನು ಅನುಭವಿಸುವಂತೆ ಯೆಹೋವನು ಮಾಡುವನು; ನೀವು ಒಂದೇ ದಾರಿಯಿಂದ ಅವರೆದುರಿಗೆ ಹೋಗಿ ಏಳು ದಾರಿಗಳಿಂದ ಓಡಿಹೋಗುವಿರಿ. ಲೋಕದ ಎಲ್ಲಾ ರಾಜ್ಯಗಳವರೂ ಇದನ್ನು ಕಂಡು ಬೆರಗಾಗುವರು.",
			"26": "<sup>26</sup> ನಿಮ್ಮ ಹೆಣಗಳು ಪಕ್ಷಿಗಳಿಗೂ ಮತ್ತು ಕಾಡು ಮೃಗಗಳಿಗೂ ಆಹಾರವಾಗುವವು; ಅವುಗಳನ್ನು ಬೆದರಿಸುವವರು ಯಾರೂ ಇರುವುದಿಲ್ಲ.",
			"27": "<sup>27</sup> ಐಗುಪ್ತ್ಯರ ಹುಣ್ಣು, ಬಾವು, ತುರಿಕಜ್ಜಿ ಮುಂತಾದ ವಾಸಿಯಾಗದ ರೋಗಗಳಿಂದ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಬಾಧಿಸುವನು.",
			"28": "<sup>28</sup> ಚಿತ್ತಭ್ರಮಣೆ, ಕುರುಡುತನ, ಮನೋವಿಸ್ಮಯ ಇವುಗಳಿಂದ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಬಾಧಿಸುವನು.",
			"29": "<sup>29</sup> ನೀವು ಕುರುಡರಂತೆ ಮಧ್ಯಾಹ್ನದಲ್ಲಿಯೂ ಕತ್ತಲಾಯಿತೆಂದು ತಡವರಿಸುತ್ತಾ ಇರುವಿರಿ; ನೀವು ಮಾಡುವ ಯಾವ ಕೆಲಸವೂ ಕೈಗೂಡುವುದಿಲ್ಲ. ಅನ್ಯರು ಯಾವಾಗಲೂ ನಿಮ್ಮನ್ನು ಪೀಡಿಸುತ್ತಾ ನಿಮ್ಮ ಸೊತ್ತನ್ನು ಸೂರೆಮಾಡುತ್ತಾ ಇರುವರು; ತಪ್ಪಿಸುವವರು ಯಾರೂ ಇರುವುದಿಲ್ಲ.",
			"30": "<sup>30</sup> ನೀವು ಮದುವೆಮಾಡಿಕೊಂಡ ಸ್ತ್ರೀಯನ್ನು ಮತ್ತೊಬ್ಬನು ಮಾನಭಂಗಪಡಿಸುವನು; ನೀವು ಕಟ್ಟಿಕೊಂಡ ಮನೆಯಲ್ಲಿ ಮತ್ತೊಬ್ಬನು ವಾಸಮಾಡುವನು; ನೀವು ಮಾಡಿಕೊಂಡ ದ್ರಾಕ್ಷೇತೋಟದ ಬೆಳೆಯು ನಿಮಗೆ ದೊರೆಯುವುದಿಲ್ಲ.",
			"31": "<sup>31</sup> ನಿಮ್ಮ ದನಗಳನ್ನು ನಿಮ್ಮ ಕಣ್ಣು ಮುಂದೆ ಕೊಯ್ಯುವರು; ಅವುಗಳ ಮಾಂಸವು ನಿಮಗೆ ಸಿಕ್ಕುವುದಿಲ್ಲ. ನಿಮ್ಮ ಕತ್ತೆಯನ್ನು ನಿಮ್ಮ ಮುಂದೆಯೇ ಬಲಾತ್ಕಾರದಿಂದ ಹಿಡಿದುಕೊಂಡುಹೋಗುವರು. ಕೇಳಿದರೆ ನಿಮಗೆ ಹಿಂದಕ್ಕೆ ಕೊಡುವುದಿಲ್ಲ. ನಿಮ್ಮ ಆಡು ಮತ್ತು ಕುರಿಗಳು ಶತ್ರುಗಳ ಪಾಲಾಗುವವು. ನಿಮಗೆ ರಕ್ಷಕರು ಯಾರೂ ಇರುವುದಿಲ್ಲ.",
			"32": "<sup>32</sup> ನಿಮ್ಮ ಗಂಡು ಮತ್ತು ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ನಿಮ್ಮ ಮುಂದೆಯೇ ಹಿಡಿದು ಅನ್ಯರಿಗೆ ವಶಪಡಿಸುವರು; ನೀವು ಹಗಲೆಲ್ಲಾ ಅವರನ್ನು ಕಾಣಬೇಕೆಂದು ಹಂಬಲಿಸುತ್ತಾ ಕಂಗೆಡುವಿರಿ; ಆದರೆ ನಿಮ್ಮ ಪ್ರಯತ್ನವೇನೂ ಸಾಗುವುದಿಲ್ಲ.",
			"33": "<sup>33</sup> ನಿಮಗೆ ಗುರುತೇ ಇಲ್ಲದ ಜನಾಂಗದವರು ಬಂದು ನಿಮ್ಮ ದೇಶದ ಬೆಳೆಯನ್ನೂ ಮತ್ತು ನಿಮ್ಮ ಕಷ್ಟಾರ್ಜಿತವನ್ನೂ ತಿಂದುಬಿಡುವರು. ನೀವಾದರೋ ಯಾವಾಗಲೂ ಹಿಂಸೆಗೂ ಬಲಾತ್ಕಾರಕ್ಕೂ ಗುರಿಯಾಗಿ,",
			"34": "<sup>34</sup> ನಿಮ್ಮ ಮುಂದೆ ನಡೆಯುವ ಸಂಗತಿಗಳ ದೆಸೆಯಿಂದ ಹುಚ್ಚರಾಗಿ ಹೋಗುವಿರಿ.",
			"35": "<sup>35</sup> ಯೆಹೋವನು ನಿಮ್ಮ ಮೊಣಕಾಲುಗಳಲ್ಲಿಯೂ, ತೊಡೆಗಳಲ್ಲಿಯೂ ಮತ್ತು ಅಂಗಾಲು ಮೊದಲುಗೊಂಡು ನಡುನೆತ್ತಿಯ ವರೆಗೂ ವಾಸಿಯಾಗದ ಕೆಟ್ಟ ಹುಣ್ಣುಗಳನ್ನು ಹುಟ್ಟಿಸಿ ಬಾಧಿಸುವನು.",
			"36": "<sup>36</sup> ನಿಮಗೂ ನಿಮ್ಮ ಪಿತೃಗಳಿಗೂ ಗೊತ್ತಿಲ್ಲದ ಜನಾಂಗದವರ ದೇಶಕ್ಕೆ ಯೆಹೋವನು ನಿಮ್ಮನ್ನೂ ಮತ್ತು ನೀವು ನೇಮಿಸಿಕೊಳ್ಳುವ ಅರಸರನ್ನೂ ಒಯ್ಯುವನು; ಅಲ್ಲಿ ನೀವು ಮರದ ದೇವರುಗಳನ್ನೂ ಮತ್ತು ಕಲ್ಲಿನ ದೇವರುಗಳನ್ನೂ ಸೇವಿಸುವಿರಿ.",
			"37": "<sup>37</sup> ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ನಡೆಸುವ ಜನಾಂಗಗಳಲ್ಲಿ ನೀವು ಭೀಕರತೆಗೂ, ಗಾದೆಗೂ ಮತ್ತು ಪರಿಹಾಸ್ಯಕ್ಕೂ ಗುರಿಯಾಗುವಿರಿ.",
			"38": "<sup>38</sup> ನೀವು ಹೊಲದಲ್ಲಿ ಎಷ್ಟು ಬೀಜವನ್ನು ಬಿತ್ತಿದ್ದರೂ ಮಿಡತೆಯದಂಡು ಬಂದು ಅದನ್ನು ತಿಂದು ಬಿಡುವುದರಿಂದ ನಿಮಗೆ ದೊರೆಯುವ ಬೆಳೆ ಅತ್ಯಲ್ಪವಾಗುವುದು.",
			"39": "<sup>39</sup> ನೀವು ದ್ರಾಕ್ಷೇ ವ್ಯವಸಾಯವನ್ನು ಎಷ್ಟು ಮಾಡಿದರೂ ಅದರ ಹಣ್ಣುಗಳನ್ನು ಹುಳಗಳೇ ತಿಂದುಹಾಕುವುದರಿಂದ ನೀವು ಅದರ ಸಾರವನ್ನು ರುಚಿನೋಡಲು ಆಗುವುದಿಲ್ಲ.",
			"40": "<sup>40</sup> ನಿಮ್ಮ ಎಲ್ಲಾ ಪ್ರದೇಶಗಳಲ್ಲಿ ಎಣ್ಣೇಮರಗಳು ಎಷ್ಟಿದ್ದರೂ ಅವುಗಳ ಕಾಯಿಗಳು ಉದುರಿಹೋಗುವುದರಿಂದ ನೀವು ಎಣ್ಣೆಯನ್ನು ಮೈಗೆ ಹಚ್ಚಿಕೊಳ್ಳುವುದಿಲ್ಲ.",
			"41": "<sup>41</sup> ನಿಮಗೆ ಗಂಡು ಮತ್ತು ಹೆಣ್ಣು ಮಕ್ಕಳು ಎಷ್ಟು ಹುಟ್ಟಿದರೂ ಅವರು ಸೆರೆಯವರಾಗಿ ಒಯ್ಯಲ್ಪಡುವುದರಿಂದ ನಿಮ್ಮ ಬಳಿಯಲ್ಲಿ ಇರುವುದಿಲ್ಲ.",
			"42": "<sup>42</sup> ನಿಮ್ಮ ಎಲ್ಲಾ ಮರಗಳೂ ಮತ್ತು ಪೈರುಗಳೂ ಮಿಡತೆಯ ಪಾಲಾಗುವವು.",
			"43": "<sup>43</sup> ನಿಮ್ಮ ಮಧ್ಯದಲ್ಲಿರುವ ಅನ್ಯರು ನಿಮಗಿಂತಲೂ ಹೆಚ್ಚೆಚ್ಚಾಗಿ ಅಭಿವೃದ್ಧಿಗೆ ಬರುವರು; ನೀವೋ ಕಡಿಮೆಯಾಗುತ್ತಾ ಹೀನಸ್ಥಿತಿಗೆ ಬರುವಿರಿ.",
			"44": "<sup>44</sup> ಅವರು ನಿಮಗೆ ಸಾಲಕೊಡುವರೇ ಹೊರತು ನೀವು ಅವರಿಗೆ ಕೊಡುವುದಿಲ್ಲ. ನೀವು ಅವರಿಗೆ ಅಧೀನರಾಗುವಿರಿ; ಅವರು ನಿಮಗೆ ಶಿರಸ್ಸಾಗುವರು.",
			"45": "<sup>45</sup> ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಮಾತಿಗೆ ಕಿವಿಗೊಡದೆಯೂ, ಆತನು ನೇಮಿಸಿದ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ಅನುಸರಿಸದೆಯೂ ಹೋದುದರಿಂದ ಈ ಎಲ್ಲಾ ಅಶುಭಗಳು ನಿಮಗೆ ಪ್ರಾಪ್ತವಾಗಿ ನಿಮ್ಮನ್ನು ಹಿಂದಟ್ಟಿ ಹಿಡಿದು ಕಡೆಗೆ ನಾಶಮಾಡುವವು.",
			"46": "<sup>46</sup> ಇವು ನಿಮಗೂ ಮತ್ತು ನಿಮ್ಮ ಸಂತತಿಯವರಿಗೂ ಯಾವಾಗಲೂ ಪ್ರಾಪ್ತವಾಗುತ್ತಾ ಎಲ್ಲರಿಗೂ ಎಚ್ಚರಿಕೆಯನ್ನೂ ಮತ್ತು ಬೆರಗನ್ನೂ ಉಂಟುಮಾಡುವವು.",
			"47": "<sup>47</sup> ನಿಮಗೆ ಸರ್ವಸಮೃದ್ಧಿಯುಂಟಾದ ಕಾಲದಲ್ಲಿಯೂ ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಹರ್ಷಾನಂದಗಳುಳ್ಳವರಾಗಿ ಸೇವಿಸದೆ ಹೋದುದರಿಂದ,",
			"48": "<sup>48</sup> ಯೆಹೋವನು ನಿಮ್ಮ ಮೇಲೆ ಶತ್ರುಗಳನ್ನು ಬರಮಾಡುವನು; ಆಗ ನೀವು ಹಸಿವೆ, ಬಾಯಾರಿಕೆಗಳುಳ್ಳವರಾಗಿ, ಬಟ್ಟೆಯೂ ಮತ್ತು ಏನೂ ಇಲ್ಲದವರಾಗಿ ಆ ಶತ್ರುಗಳಿಗೇ ಸೇವಕರಾಗಬೇಕಾಗುವುದು. ಆತನು ಕಬ್ಬಿಣದ ನೊಗವನ್ನು ಹೇರಿಸಿ ನಿಮ್ಮನ್ನು ನಾಶಮಾಡುವನು.",
			"49": "<sup>49</sup> ರಣಹದ್ದು ಹೇಗೆ ದೂರದಿಂದ ಹಾರಿಬರುವುದೋ ಹಾಗೆಯೇ ನಿಮಗೆ ತಿಳಿಯದ ಭಾಷೆಯನ್ನಾಡುವ ಒಂದು ಜನಾಂಗವು ನಿಮ್ಮ ಮೇಲೆ ಬರುವಂತೆ ಯೆಹೋವನು ಮಾಡುವನು.",
			"50": "<sup>50</sup> ಆ ಜನಾಂಗದವರು ಕ್ರೂರಮುಖವುಳ್ಳವರಾಗಿ ನಿಮ್ಮನ್ನು ವೃದ್ಧರೆಂದು ಮರ್ಯಾದೆ ತೋರಿಸುವುದಿಲ್ಲ ಮತ್ತು ಚಿಕ್ಕವರೆಂದು ಕನಿಕರಿಸುವುದಿಲ್ಲ.",
			"51": "<sup>51</sup> ಅವರು ನಿಮ್ಮ ದನಗಳನ್ನೂ ಮತ್ತು ಬೆಳೆಗಳನ್ನೂ ತಿಂದುಬಿಟ್ಟು ನಿಮಗೆ ಧಾನ್ಯವನ್ನಾಗಲಿ, ದ್ರಾಕ್ಷಾರಸವನ್ನಾಗಲಿ, ಎಣ್ಣೆಯನ್ನಾಗಲಿ, ದನ ಮತ್ತು ಕುರಿಗಳ ಸಂತಾನವನ್ನಾಗಲಿ ಉಳಿಸದೆ ನಿಮ್ಮನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ನಾಶಮಾಡುವರು.",
			"52": "<sup>52</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಕೊಟ್ಟಿರುವ ದೇಶದ ಎಲ್ಲಾ ಪಟ್ಟಣಗಳಿಗೂ ಅವರು ಮುತ್ತಿಗೆಹಾಕಿ, ನೀವು ನಂಬಿಕೊಂಡಿದ್ದ ಎತ್ತರವಾದ ಕೊತ್ತಲುಗಳುಳ್ಳ ಗೋಡೆಗಳನ್ನು ಕೆಡವಿಬಿಡುವರು.",
			"53": "<sup>53</sup> ಶತ್ರುಗಳು ನಿಮ್ಮ ಪಟ್ಟಣಗಳಿಗೆ ಮುತ್ತಿಗೆ ಹಾಕಿ ನಿಮ್ಮನ್ನು ಇಕ್ಕಟ್ಟಿಗೆ ಸಿಕ್ಕಿಸುವುದರಿಂದ ನೀವು ನಿಮ್ಮ ಸಂತಾನವನ್ನೇ ಅಂದರೆ, ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಕೊಟ್ಟಿರುವ ಗಂಡು ಮತ್ತು ಹೆಣ್ಣು ಮಕ್ಕಳ ಮಾಂಸವನ್ನೇ ತಿನ್ನುವಿರಿ.",
			"54": "<sup>54</sup> ನಿಮ್ಮಲ್ಲಿ ಮೃದುಸ್ವಭಾವಿಯೂ ಮತ್ತು ಸೂಕ್ಷ್ಮಗುಣವುಳ್ಳವನು ಆಗಿರುವ ಸಹೋದರನು ಆಹಾರಕ್ಕಾಗಿ ತನ್ನ ಸಹೋದರ, ಪ್ರೀತಿಯ ಹೆಂಡತಿ ಮತ್ತು ತನ್ನ ಮಕ್ಕಳನ್ನು ದ್ವೇಶಿಸುವನು.",
			"55": "<sup>55</sup> ಶತ್ರುಗಳು ನಿಮ್ಮ ಎಲ್ಲಾ ಪಟ್ಟಣಗಳಿಗೆ ಮುತ್ತಿಗೆಹಾಕಿ, ನಿಮ್ಮನ್ನು ಇಕ್ಕಟ್ಟಿಗೆ ಸಿಕ್ಕಿಸುವ ಕಾಲದಲ್ಲಿ ತನಗೆ ತಿನ್ನಲಿಕ್ಕೆ ಏನೂ ಉಳಿಯದೆ ಹೋಗುವುದರಿಂದ ಅವನು ತನ್ನ ಮಕ್ಕಳ ಮಾಂಸವನ್ನು ತಾನೇ ತಿನ್ನಬೇಕೆಂದು ಅಣ್ಣತಮ್ಮಂದಿರಿಗೂ, ಪ್ರಾಣಪ್ರಿಯಳಾದ ಹೆಂಡತಿಗೂ ಮತ್ತು ಉಳಿದ ಮಕ್ಕಳಿಗೂ ಕೋಪಗೊಂಡವನಂತೆ ನಟಿಸುತ್ತಾ ಆ ಮಾಂಸದಲ್ಲಿ ಅವರಿಗೆ ಸ್ವಲ್ಪವನ್ನಾದರೂ ಕೊಡದೆ ಹೋಗುವನು.",
			"56": "<sup>56</sup> ಶತ್ರುಗಳು ನಿಮ್ಮ ಪಟ್ಟಣಗಳಿಗೆ ಮುತ್ತಿಗೆ ಹಾಕಿ, ನಿಮ್ಮನ್ನು ಬಹಳವಾಗಿ ಹಿಂಸಿಸುವ ಕಾಲದಲ್ಲಿ ಕೋಮಲತೆಯ ಮತ್ತು ಅತಿಸೂಕ್ಷ್ಮತೆಯ ದೆಸೆಯಿಂದ ಅಂಗಾಲನ್ನೂ ನೆಲಕ್ಕೆ ಇಡದವಳಾದ ಸ್ತ್ರೀಯು ತಿನ್ನಲಿಕ್ಕೆ ಏನೂ ಇಲ್ಲದವಳಾಗಿ, ನರಳುವಾಗ",
			"57": "<sup>57</sup> ತಾನು ಆಗಲೇ ಹೆತ್ತ ಮಗುವಿನ ಮಾಂಸವನ್ನೂ ರಹಸ್ಯವಾಗಿ ತಿನ್ನಬೇಕೆಂದು ಯೋಚಿಸಿ, ಪ್ರಾಣಪ್ರಿಯನಾದ ಗಂಡನಿಗೂ, ಮಕ್ಕಳಿಗೂ ತಿಳಿಯದಂತೆ ಆ ಕಾರ್ಯವನ್ನು ಮಾಡುವಳು.",
			"58": "<sup>58</sup> ಈ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುವ ಧರ್ಮಶಾಸ್ತ್ರವಾಕ್ಯಗಳನ್ನೆಲ್ಲಾ ನೀವು ಅನುಸರಿಸದೆ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನೆಂಬ ಮಹಾಮಹಿಮೆಯುಳ್ಳ ಅದ್ಭುತ ನಾಮದಲ್ಲಿ ನೀವು ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿ ನಡೆಯದೆ ಹೋದರೆ,",
			"59": "<sup>59</sup> ಆತನು ನಿಮಗೂ, ನಿಮ್ಮ ಸಂತತಿಯವರಿಗೂ ದೀರ್ಘಕಾಲವಿರುವ ಬಹುಭಯಂಕರವಾದ ಉಪದ್ರವಗಳನ್ನು ಬರಮಾಡುವನು. ಬಹುಕಾಲದವರೆಗೂ ವಾಸಿಯಾಗದ ಘೋರ ವ್ಯಾಧಿಗಳಿಂದ ನಿಮ್ಮನ್ನು ಬಾಧಿಸುವನು.",
			"60": "<sup>60</sup> ನೀವು ಹೆದರಿಕೊಳ್ಳುತ್ತಿದ್ದ ಐಗುಪ್ತದೇಶದ ರೋಗಗಳನ್ನೆಲ್ಲಾ ತಿರುಗಿ ನಿಮ್ಮ ಮೇಲೆ ಬರಮಾಡುವನು; ಅವು ನಿಮ್ಮನ್ನು ಅಂಟಿಕೊಂಡೇ ಇರುವವು.",
			"61": "<sup>61</sup> ಇಷ್ಟು ಮಾತ್ರವಲ್ಲದೆ ಈ ಧರ್ಮಶಾಸ್ತ್ರಗ್ರಂಥದಲ್ಲಿ ಬರೆಯದೆ ಇರುವ ವಿಧವಿಧವಾದ ರೋಗಗಳನ್ನೂ ಮತ್ತು ವ್ಯಾಧಿಗಳನ್ನೂ ನಿಮ್ಮ ಮೇಲೆ ಬರಮಾಡಿ ನಿಮ್ಮನ್ನು ನಾಶಮಾಡುವನು.",
			"62": "<sup>62</sup> ನಕ್ಷತ್ರಗಳಷ್ಟು ಅಸಂಖ್ಯರಾಗಿರುವ ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಮಾತಿನಲ್ಲಿ ಲಕ್ಷ್ಯವಿಡದೆ ಹೋಗುವುದರಿಂದ ಸ್ವಲ್ಪ ಜನರಾಗಿಯೇ ಉಳಿಯುವಿರಿ.",
			"63": "<sup>63</sup> ಹೇಗೆ ಯೆಹೋವನು ಈಗ ನಿಮ್ಮನ್ನು ಅಭಿವೃದ್ಧಿಪಡಿಸುವುದರಲ್ಲಿಯೂ, ಹೆಚ್ಚಿಸುವದರಲ್ಲಿಯೂ ಸಂತೋಷಪಡುತ್ತಾನೋ ಹಾಗೆಯೇ ಆಗ ನಿಮ್ಮನ್ನು ಕೆಡಿಸಿ ನಾಶಮಾಡುವುದರಲ್ಲಿ ಸಂತೋಷಪಟ್ಟು, ನೀವು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವುದಕ್ಕೆ ಹೋಗುವ ದೇಶದೊಳಗಿಂದ ನಿಮ್ಮನ್ನು ಕಿತ್ತುಹಾಕುವನು.",
			"64": "<sup>64</sup> ಭೂಮಂಡಲದ ಚತುರ್ದಿಕ್ಕುಗಳಲ್ಲಿರುವ ಎಲ್ಲಾ ಜನಾಂಗಗಳಲ್ಲಿಯೂ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಚದರಿಸುವನು. ಅಲ್ಲಿ ನಿಮಗಾಗಲಿ ಅಥವಾ ನಿಮ್ಮ ಪಿತೃಗಳಿಗಾಗಲಿ ಗೊತ್ತಿಲ್ಲದ ಮರದ ದೇವರುಗಳನ್ನೂ ಕಲ್ಲಿನ ದೇವರುಗಳನ್ನೂ ಪೂಜಿಸುವಿರಿ.",
			"65": "<sup>65</sup> ಆ ದೇಶಗಳಲ್ಲಿ ನಿಮಗೆ ಯಾವ ವಿಶ್ರಾಂತಿಯೂ ದೊರೆಯುವುದಿಲ್ಲ; ಸ್ವಲ್ಪ ಹೊತ್ತು ಅಂಗಾಲಿಡುವುದಕ್ಕೂ ನಿಮಗೆ ಸ್ಥಳಸಿಕ್ಕುವುದಿಲ್ಲ. ನೀವು ನಡುಗುವ ಹೃದಯವುಳ್ಳವರಾಗಿಯೂ, ದಿಕ್ಕುತೋರದೆ ಕಂಗೆಟ್ಟವರಾಗಿಯೂ, ಮನಗುಂದಿದವರಾಗಿಯೂ ಇರುವಂತೆ ಯೆಹೋವನು ಮಾಡುವನು.",
			"66": "<sup>66</sup> ಬದುಕುವೆವೋ ಇಲ್ಲವೋ ಎಂಬುದಾಗಿ ಅನುಮಾನಪಟ್ಟು ನೀವು ಹಗಲಿರುಳು ಪ್ರಾಣಭಯದಲ್ಲಿರುವಿರಿ; ಪ್ರಾಣದ ಮೇಲಣ ನಂಬಿಕೆಯನ್ನು ಬಿಟ್ಟೇಬಿಡುವಿರಿ.",
			"67": "<sup>67</sup> ಮನಸ್ಸಿನಲ್ಲಿ ಯಾವಾಗಲೂ ಪ್ರಾಣಭಯವಿರುವುದರಿಂದಲೂ ಕಣ್ಣಿನಿಂದ ಭಯಂಕರವಾದ ಸಂಗತಿಗಳನ್ನು ನೋಡುವುದರಿಂದಲೂ ನೀವು ಹೊತ್ತಾರೆಯಲ್ಲಿ, “ಅಯ್ಯೋ, ಅಯ್ಯೋ ಸಾಯಂಕಾಲ ಯಾವಾಗ ಬರುವುದೋ” ಎಂದೂ, ಸಾಯಂಕಾಲದಲ್ಲಿ, “ಅಯ್ಯೋ, ಅಯ್ಯೋ! ಬೆಳಿಗ್ಗೆ ಯಾವಾಗ ಬರುವುದೋ” ಎಂದು ಕೊರಗುವಿರಿ.",
			"68": "<sup>68</sup> ನೀವು ಐಗುಪ್ತದೇಶವನ್ನು ತಿರುಗಿ ಎಂದಿಗೂ ನೋಡುವುದಿಲ್ಲವೆಂದು ಯೆಹೋವನು ಹೇಳಿದ್ದರೂ ಆತನು ನಿಮ್ಮನ್ನು ಹಡಗುಗಳನ್ನೇರಿಸಿ ಅಲ್ಲಿಗೆ ತಿರುಗಿ ಹೋಗ ಮಾಡುವನು. ಅಲ್ಲಿ ನೀವು ಶತ್ರುಗಳಿಗೆ ನಿಮ್ಮನ್ನು ದಾಸದಾಸಿಯರನ್ನಾಗಿ ಮಾರಿಕೊಳ್ಳಬೇಕೆಂದು ಅಪೇಕ್ಷಿಸಿದರೂ ಯಾರೂ ನಿಮ್ಮನ್ನು ಕೊಂಡುಕೊಳ್ಳುವುದಿಲ್ಲ."
		},
		"29": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಹೋರೇಬಿನಲ್ಲಿ ಇಸ್ರಾಯೇಲರೊಡನೆ ಮಾಡಿದ ನಿಬಂಧನೆ ಅಲ್ಲದೆ, ಆತನು ಮೋವಾಬ್ಯರ ದೇಶದಲ್ಲಿ ಅವರೊಡನೆ ಮಾಡಬೇಕೆಂದು ಮೋಶೆಗೆ ಆಜ್ಞಾಪಿಸಿದ ಒಡಂಬಡಿಕೆಯ ವಚನಗಳು.",
			"2": "<sup>2</sup> ಮೋಶೆ ಇಸ್ರಾಯೇಲರನ್ನು ಕರೆದು, “ಯೆಹೋವನು ಐಗುಪ್ತದೇಶದಲ್ಲಿ ನಿಮ್ಮ ಕಣ್ಣುಮುಂದೆ ಫರೋಹನಿಗೂ, ಅವನ ಪರಿವಾರದವರಿಗೂ ಮತ್ತು",
			"3": "<sup>3</sup> ಸಮಸ್ತ ಪ್ರಜೆಗಳಿಗೂ ಮಾಡಿದವುಗಳನ್ನು ನೋಡಿದ್ದೀರಿ. ಮಹಾಮನಶ್ಶೋಧನೆ, ಉತ್ಪಾತ ಮತ್ತು ಮಹತ್ಕಾರ್ಯಗಳನ್ನು ನೀವು ನೋಡೇ ನೋಡಿದ್ದೀರಿ.",
			"4": "<sup>4</sup> ಆದರೂ ಗ್ರಹಿಸುವ ಬುದ್ಧಿ, ನೋಡುವ ಕಣ್ಣು ಮತ್ತು ಕೇಳುವ ಕಿವಿ ಇವುಗಳನ್ನು ಯೆಹೋವನು ಇಂದಿನವರೆಗೂ ನಿಮಗೆ ಅನುಗ್ರಹಿಸಲಿಲ್ಲ.",
			"5": "<sup>5</sup> ನಲ್ವತ್ತು ವರುಷ ನಾನು ನಿಮ್ಮನ್ನು ಅರಣ್ಯದಲ್ಲಿ ನಡೆಸಿಕೊಂಡು ಬಂದಿದ್ದೇನೆ. ನಿಮ್ಮ ಮೈಮೇಲಿದ್ದ ಉಡುಪು ಹರಿಯಲ್ಲಿಲ್ಲ ಅಥವಾ ಕಾಲಿನಲ್ಲಿದ್ದ ಕೆರವಾಗಲಿ ಸವೆದು ಹೋಗಲಿಲ್ಲ;",
			"6": "<sup>6</sup> ನೀವು ರೊಟ್ಟಿಯನ್ನು ತಿನ್ನಲಿಲ್ಲ, ದ್ರಾಕ್ಷಾರಸವನ್ನಾಗಲಿ ಯಾವ ಮದ್ಯವನ್ನಾಗಲಿ ಕುಡಿಯಲೇ ಇಲ್ಲ. ಯೆಹೋವನೇ ನಿಮ್ಮ ದೇವರು ಎಂಬುವುದು ನಿಮಗೆ ತಿಳಿಯಬೇಕೆಂಬ ಉದ್ದೇಶದಿಂದ ಹೀಗಾಯಿತು.",
			"7": "<sup>7</sup> ನೀವು ಈ ಸ್ಥಳಕ್ಕೆ ಬಂದಾಗ ಹೆಷ್ಬೋನಿನ ಅರಸನಾದ ಸೀಹೋನನೂ, ಬಾಷಾನಿನ ಅರಸನಾದ ಓಗನೂ ನಮ್ಮೊಡನೆ ಯುದ್ಧಮಾಡುವುದಕ್ಕೆ ಬರಲಾಗಿ ನಾವು ಅವರನ್ನು ಸೋಲಿಸಿದೆವು.",
			"8": "<sup>8</sup> ಅವರ ರಾಜ್ಯಗಳನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡು ರೂಬೇನ್ಯರಿಗೂ, ಗಾದ್ಯರಿಗೂ ಹಾಗು ಮನಸ್ಸೆ ಕುಲದವರಲ್ಲಿ ಅರ್ಧಜನರಿಗೂ ಸ್ವಾಸ್ತ್ಯವಾಗಿ ಕೊಟ್ಟೆವಷ್ಟೆ.",
			"9": "<sup>9</sup> ಈ ಸಂಗತಿಗಳನ್ನೆಲ್ಲಾ ನೀವು ಜ್ಞಾಪಕಮಾಡಿಕೊಂಡು ಈ ಒಡಂಬಡಿಕೆಯ ವಾಕ್ಯಗಳನ್ನು ಅನುಸರಿಸಿ ನಡೆಯಬೇಕು; ಆಗ ನೀವು ನಡಿಸುವ ಎಲ್ಲಾ ಕೆಲಸಗಳಲ್ಲಿಯೂ ಜಾಣರಾಗಿ ಅಭಿವೃದ್ಧಿಹೊಂದುವಿರಿ.",
			"10": "<sup>10</sup> ಇಸ್ರಾಯೇಲ್ ಪುರುಷರೇ, ನೀವೆಲ್ಲರೂ ಈಹೊತ್ತು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನಿಂತಿರುವುದಕ್ಕೆ ಕಾರಣವೇನೆಂದರೆ, ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಹೇಳಿದಂತೆ ನಿಮ್ಮ ಪ್ರಧಾನರು, ಗೋತ್ರದವರು, ಹಿರಿಯರು, ಅಧಿಕಾರಿಗಳು,",
			"11": "<sup>11</sup> ಹೆಂಗಸರು, ಮಕ್ಕಳು, ನಿಮ್ಮ ಪಾಳೆಯದಲ್ಲಿರುವ ಅನ್ಯರಾದ ಕಟ್ಟಿಗೆ ಒಡೆಯುವವರು ಮತ್ತು ನೀರು ಸೇದುವವರು ಮುಂತಾದ ಆಳುಗಳು,",
			"12": "<sup>12</sup> ಹೀಗೆ ನಿಮ್ಮೆಲ್ಲರ ಸಂಗಡ ಈ ಹೊತ್ತು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಪ್ರಮಾಣಪೂರ್ವಕವಾಗಿ ಒಂದು ಒಡಂಬಡಿಕೆಯನ್ನು ಮಾಡಿಕೊಳ್ಳುತ್ತಾನೆ. ನೀವು ಅದರಲ್ಲಿ ಸೇರುವುದಕ್ಕಾಗಿ ಕೂಡಿಬಂದಿದ್ದೀರಿ. ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಹೇಳಿದಂತೆ,",
			"13": "<sup>13</sup> ನಿಮ್ಮ ಪಿತೃಗಳಾದ ಅಬ್ರಹಾಮ್, ಇಸಾಕ್ ಮತ್ತು ಯಾಕೋಬರಿಗೆ ವಾಗ್ದಾನ ಮಾಡಿದಂತೆಯೂ ನಿಮ್ಮನ್ನು ತನಗೆ ಸ್ವಕೀಯಜನರನ್ನಾಗಿಯೂ, ತನ್ನನ್ನು ನಿಮಗೆ ದೇವರನ್ನಾಗಿಯೂ ಸ್ಥಾಪಿಸಿಕೊಳ್ಳುತ್ತಾನೆ.",
			"14": "<sup>14</sup> “ಆತನು ಈ ಒಡಂಬಡಿಕೆಯನ್ನು ಕೇವಲ ನಮ್ಮೊಡನೆ ಮಾಡಿಕೊಳ್ಳುವುದಿಲ್ಲ.",
			"15": "<sup>15</sup> ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸಿದ್ಧರಾಗಿ ನಿಂತಿರುವ ನಮ್ಮೊಡನೆ ಮತ್ತು ಮುಂದೆ ಹುಟ್ಟುವ ನಮ್ಮ ಸಂತತಿಯವರೊಡನೆಯೂ ಪ್ರಮಾಣಪೂರ್ವಕವಾದ ಈ ಒಡಂಬಡಿಕೆಯನ್ನು ಮಾಡಿಕೊಳ್ಳುವವನಾಗಿದ್ದಾನೆ.",
			"16": "<sup>16</sup> ನಾವು ಐಗುಪ್ತದೇಶದಲ್ಲಿ ವಾಸಮಾಡಿದ್ದೂ ಹಾಗೂ ಅನೇಕ ಜನಾಂಗಗಳ ಪ್ರದೇಶಗಳನ್ನು ದಾಟಿಬಂದದ್ದೂ ನಿಮಗೆ ತಿಳಿದೇ ಇದೆ.",
			"17": "<sup>17</sup> ಆ ಜನಾಂಗಗಳಲ್ಲಿ ನಡೆಯುವ ಅಸಹ್ಯವಾದ ಆಚಾರಗಳನ್ನೂ ಹಾಗು ಅವರು ಮರ, ಕಲ್ಲು, ಬೆಳ್ಳಿ ಮತ್ತು ಬಂಗಾರ ಇವುಗಳಿಂದ ಮಾಡಿಕೊಂಡು ಪೂಜಿಸುವ ಬೊಂಬೆಗಳನ್ನೂ ನೋಡಿದ್ದೀರಿ.",
			"18": "<sup>18</sup> “ಎಚ್ಚರಿಕೆಯಿಂದಿರಿ, ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಬಿಟ್ಟು ಆ ಜನಾಂಗಗಳ ದೇವರುಗಳನ್ನು ಸೇವಿಸುವ ಯಾವ ಕುಲವಾಗಲಿ, ಕುಟುಂಬವಾಗಲಿ, ಸ್ತ್ರೀಯರಾಗಲಿ, ಪುರುಷರಾಗಲಿ ನಿಮ್ಮಲ್ಲಿ ಇರಲೇ ಬಾರದು; ನಿಮ್ಮಲ್ಲಿ ಯಾವ ವಿಷದ ಬಳ್ಳಿಯ ಬೇರೂ ಇರಬಾರದು.",
			"19": "<sup>19</sup> ಶಾಪದಿಂದ ಕೂಡಿರುವ ಈ ಪ್ರತಿಜ್ಞೆಯನ್ನು ಕೇಳಿದ ಯಾವನಾದರೂ ತನ್ನೊಳಗೆ, ‘ನಾನು ಹಟವನ್ನು ಹಿಡಿದು ಅವಿಧೇಯನಾದರೂ ನನಗೆ ಕ್ಷೇಮವಾಗಿಯೇ ಇರುವುದು’ ಅಂದುಕೊಂಡು ಹಿಗ್ಗಬಾರದು. ಹಾಗೆ ಮಾಡುವವನು ದೋಷಿಗಳಿಗೂ ಮತ್ತು ನಿರ್ದೋಷಿಗಳಿಗೂ ನಾಶನವನ್ನು ಉಂಟುಮಾಡುವವನಾಗಿರುತ್ತಾನೆ.",
			"20": "<sup>20</sup> ಯೆಹೋವನು ಅಂಥವನನ್ನು ಎಂದಿಗೂ ಕ್ಷಮಿಸದೆ ಬಹಳ ಸಿಟ್ಟುಮಾಡಿಕೊಂಡು, ತನ್ನ ಗೌರವವನ್ನು ಕಾಪಾಡಿಕೊಳ್ಳುವವನಾಗಿ ಈ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುವ ಎಲ್ಲಾ ಶಾಪೋಕ್ತಿಗಳಿಗೂ ಅವನನ್ನು ಗುರಿಪಡಿಸಿ ಅವನ ಹೆಸರನ್ನು ಭೂಮಿಯ ಮೇಲೆ ಇಲ್ಲದಂತೆ ಮಾಡುವನು.",
			"21": "<sup>21</sup> ಈ ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿ ಬರೆದಿರುವ ನಿಬಂಧನೆಯನ್ನು ಮೀರುವವರಿಗೆ ಉಂಟಾಗುವ ಶಾಪಗಳು ಅವನಲ್ಲಿ ನೆರವೇರಲೆಂದು ಯೆಹೋವನು ಅವನನ್ನು ಇಸ್ರಾಯೇಲರ ಎಲ್ಲಾ ಕುಲಗಳಿಂದ ಪ್ರತ್ಯೇಕಿಸಿ ದಂಡಿಸುವನು.",
			"22": "<sup>22</sup> “ಮುಂದೆ ಹುಟ್ಟುವ ನಿಮ್ಮ ಸಂತತಿಯವರೂ ದೇಶಾಂತರಗಳಿಂದ ಬರುವ ಅನ್ಯರೂ ನಿಮ್ಮ ದೇಶಕ್ಕೆ ಯೆಹೋವನು ಉಂಟುಮಾಡಿದ ರೋಗ ಮತ್ತು ಉಪದ್ರವಗಳನ್ನು ಕಂಡು ಆಶ್ಚರ್ಯಪಡುವರು.",
			"23": "<sup>23</sup> ಎಲ್ಲಾ ಜನಾಂಗಗಳವರೂ ನಿಮ್ಮ ದೇಶವು ಯಾವ ವ್ಯವಸಾಯವೂ ಇಲ್ಲದೆ, ಹುಲ್ಲಾದರೂ ಬೆಳೆಯದೆ, ಹಾಳುಬಿದ್ದಿರುವುದನ್ನು ಕಾಣುವರು. ಯೆಹೋವನು ಮಹಾ ಕೋಪದಿಂದ ಕೆಡವಿದ ಸೊದೋಮ್ ಮತ್ತು ಗೊಮೋರ, ಅದ್ಮಾ ಮತ್ತು ಚೆಬೋಯಿಮ್ ಎಂಬ ಪಟ್ಟಣಗಳ ಪ್ರದೇಶದಂತೆ ಸುಟ್ಟು ಹೋಗಿ, ಎಲ್ಲಾ ಕಡೆಗಳಲ್ಲಿಯೂ ಗಂಧಕ ಉಪ್ಪುಗಳಿಂದ ತುಂಬಿರುವುದನ್ನು ಕಂಡು,",
			"24": "<sup>24</sup> ‘ಯೆಹೋವನು ಈ ದೇಶಕ್ಕೆ ಹೀಗೆ ಮಾಡಿದ್ದೇಕೆ? ಇಂಥ ಕೋಪಾಗ್ನಿಗೆ ಕಾರಣವೇನಿದ್ದೀತು?’ ಎಂಬದಾಗಿ ವಿಚಾರಿಸುವರು.",
			"25": "<sup>25</sup> ಅದಕ್ಕೆ ಜನರು, ‘ಈ ದೇಶದವರ ಪಿತೃಗಳ ದೇವರಾದ ಯೆಹೋವನು ಅವರನ್ನು ಐಗುಪ್ತದೇಶದಿಂದ ಬಿಡಿಸಿದ ಮೇಲೆ ಅವರೊಡನೆ ಮಾಡಿದ ಒಡಂಬಡಿಕೆಯನ್ನು ಅವರು ಉಲ್ಲಂಘಿಸಿದರು.",
			"26": "<sup>26</sup> ಅವರು ತಮಗೆ ನೇಮಿಸಲ್ಪಡದೆಯೂ ಮತ್ತು ಗೊತ್ತಿಲ್ಲದೆಯೂ ಇದ್ದ ಇತರ ದೇವರುಗಳನ್ನು ಸೇವಿಸಿ ಪೂಜಿಸಿದ್ದರಿಂದ,",
			"27": "<sup>27</sup> ಯೆಹೋವನು ಅವರ ದೇಶದ ಮೇಲೆ ಕೋಪಮಾಡಿ, ಈ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುವ ಶಾಪಗಳನ್ನು ಅವರ ಮೇಲೆ ಬರಮಾಡಿದನು.",
			"28": "<sup>28</sup> ಅವನು ಮಹಾಕೋಪೋದ್ರೇಕದಿಂದ ಆ ಜನರನ್ನು ದೇಶದಿಂದ ಕಿತ್ತುಹಾಕಿ, ಈಗ ಅನುಭವಕ್ಕೆ ಬಂದಿರುವಂತೆ ದೇಶಾಂತರದಲ್ಲಿ ಹಾಕಿಬಿಟ್ಟನು’ ಎಂದು ಉತ್ತರ ಕೊಡುವರು.",
			"29": "<sup>29</sup> “ರಹಸ್ಯವಾದವುಗಳು ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನವು; ಆದರೆ ಆತನಿಂದ ಪ್ರಕಟವಾದವುಗಳು ಸದಾ ನಮ್ಮ ಮತ್ತು ನಮ್ಮ ಸಂತತಿಯವರ ಪಾಲಾಗಿರುವುದರಿಂದ ನಾವು ಈ ಧರ್ಮಶಾಸ್ತ್ರನಿಯಮಗಳನ್ನು ಅನುಸರಿಸಿ ನಡೆಯಬೇಕಾದ ಹಂಗಿನಲ್ಲಿದ್ದೇವೆ.”"
		},
		"30": {
			"1": "<sup>1</sup> ನಾನು ನಿಮ್ಮ ಮುಂದೆ ಇಟ್ಟಿರುವ ಆಶೀರ್ವಾದವೂ ಮತ್ತು ಶಾಪವೂ ನಿಮ್ಮ ಅನುಭವಕ್ಕೆ ಬಂದಾಗ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಚದರಿಸಿರುವ ದೇಶಗಳಲ್ಲಿ ನೀವು ಇವುಗಳನ್ನು ನೆನಪಿಗೆ ತಂದುಕೊಂಡು,",
			"2": "<sup>2</sup> ನಿಮ್ಮ ಮಕ್ಕಳ ಸಹಿತವಾಗಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಆಶ್ರಯಕ್ಕೆ ತಿರುಗಿ ಬಂದು, ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ಆತನ ಮಾತುಗಳಿಗೆ ಸಂಪೂರ್ಣವಾದ ಹೃದಯದಿಂದಲೂ, ಮನಸ್ಸಿನಿಂದಲೂ ವಿಧೇಯರಾದರೆ,",
			"3": "<sup>3</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ದುರವಸ್ಥೆಯಿಂದ ನಿಮ್ಮನ್ನು ತಪ್ಪಿಸಿ, ನಿಮ್ಮನ್ನು ಕನಿಕರಿಸಿ, ತಾನು ನಿಮ್ಮನ್ನು ಚದರಿಸಿರುವ ಎಲ್ಲಾ ದೇಶಗಳಿಂದ ಪುನಃ ಕೂಡಿಸುವನು.",
			"4": "<sup>4</sup> ನಿಮ್ಮಲ್ಲಿ ಯಾರಾದರೂ ಭೂಲೋಕದ ಕೊನೆಯ ವರೆಗೆ ಒಯ್ಯಲ್ಪಟ್ಟರೂ, ಅಲ್ಲಿಂದಲೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಅವರನ್ನೂ ಕರೆದುಕೊಂಡು ಬರುವನು.",
			"5": "<sup>5</sup> ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ಸ್ವಾಸ್ತ್ಯವಾಗಿದ್ದ ದೇಶದಲ್ಲಿ ಸೇರಿಸಿ, ಅದು ನಿಮಗೂ ಸ್ವದೇಶವಾಗುವಂತೆ ಮಾಡಿ, ನಿಮಗೆ ಮೇಲನ್ನುಂಟುಮಾಡಿ ಪಿತೃಗಳಿಗಿಂತಲೂ ಅಧಿಕವಾಗಿ ನಿಮ್ಮನ್ನು ಹೆಚ್ಚಿಸುವನು.",
			"6": "<sup>6</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮ ಮತ್ತು ನಿಮ್ಮ ಸಂತತಿಯವರ ಹೃದಯಕ್ಕೆ ಸುನ್ನತಿಮಾಡುವನು. ಆಗ ನೀವು ಆತನನ್ನು ಸಂಪೂರ್ಣವಾದ ಹೃದಯದಿಂದಲೂ ಮತ್ತು ಸಂಪೂರ್ಣವಾದ ಮನಸ್ಸಿನಿಂದಲೂ ಪ್ರೀತಿಸಿ ಬದುಕಿಕೊಳ್ಳುವಿರಿ.",
			"7": "<sup>7</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಆ ಶಾಪಗಳನ್ನೆಲ್ಲಾ, ನಿಮ್ಮನ್ನು ದ್ವೇಷಿಸಿ ಹಿಂಸಿಸಿದ ನಿಮ್ಮ ಶತ್ರುಗಳ ಮೇಲೆಯೇ ಬರಮಾಡುವನು.",
			"8": "<sup>8</sup> ನೀವಾದರೋ ಯೆಹೋವನ ಮಾತಿಗೆ ಪುನಃ ವಿಧೇಯರಾಗಿ, ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ಆತನ ಆಜ್ಞೆಗಳಂತೆ ನಡೆಯುವಿರಿ.",
			"9": "<sup>9</sup> ಮತ್ತು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮ ಸಂತಾನ, ಪಶು ಮತ್ತು ವ್ಯವಸಾಯಗಳನ್ನು ಹೆಚ್ಚಿಸಿ ನಿಮ್ಮ ಎಲ್ಲಾ ಪ್ರಯತ್ನಗಳನ್ನು ಸಫಲಪಡಿಸಿ ನಿಮಗೆ ಮೇಲನ್ನು ಉಂಟುಮಾಡುವನು.",
			"10": "<sup>10</sup> ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಮಾತಿಗೆ ವಿಧೇಯರಾಗಿ ಈ ಧರ್ಮಶಾಸ್ತ್ರದ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ಅನುಸರಿಸಿ ಸಂಪೂರ್ಣವಾದ ಹೃದಯದಿಂದಲೂ ಮತ್ತು ಸಂಪೂರ್ಣವಾದ ಮನಸ್ಸಿನಿಂದಲೂ ಆತನ ಕಡೆಗೆ ತಿರುಗಿಕೊಳ್ಳಲು, ಆತನು ನಿಮ್ಮ ಪಿತೃಗಳ ವಿಷಯದಲ್ಲಿ ಸಂತೋಷಪಟ್ಟ ಹಾಗೆಯೇ ನಿಮ್ಮ ವಿಷಯದಲ್ಲಿಯೂ ತಿರುಗಿ ಸಂತೋಷಪಟ್ಟು ನಿಮಗೆ ಮೇಲನ್ನು ಉಂಟುಮಾಡುವನು.",
			"11": "<sup>11</sup> ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸಿದ ಧರ್ಮೋಪದೇಶವು ನಿಮಗೆ ಗ್ರಹಿಸುವುದಕ್ಕೆ ಕಷ್ಟವಾದದ್ದು ಯಾವುದು ಇಲ್ಲ, ಅನುಸರಿಸುವುದಕ್ಕೆ ಅಸಾಧ್ಯವಾದದ್ದೂ ಅಲ್ಲ.",
			"12": "<sup>12</sup> ಇದು ಮೇಲಣ ಲೋಕದಲ್ಲಿರುವ ಮಾತಲ್ಲ. “ನಮಗೋಸ್ಕರ ಮೇಲಣ ಲೋಕವನ್ನು ಏರಿ ಅದನ್ನು ತಿಳಿದುಕೊಂಡುಬಂದು ನಮಗೆ ತಿಳಿಸುವಷ್ಟು ಶಕ್ತರು ಯಾರಿದ್ದಾರೆ? ಇದ್ದರೆ ನಾವು ಕೇಳಿ ಕೈಕೊಂಡೇವು” ಅಂದುಕೊಳ್ಳುವುದಕ್ಕೆ ಅವಕಾಶವೇನೂ ಇಲ್ಲ.",
			"13": "<sup>13</sup> ಮತ್ತು “ಇದು ಸಮುದ್ರದ ಆಚೆ ಇರುವ ಮಾತಲ್ಲ. ನಮಗೋಸ್ಕರ ಸಮುದ್ರವನ್ನು ದಾಟಿ ಅದನ್ನು ತೆಗೆದುಕೊಂಡುಬಂದು ನಮಗೆ ತಿಳಿಸುವಷ್ಟು ಶಕ್ತರು ಯಾರಿದ್ದಾರೆ? ಇದ್ದರೆ ನಾವು ಕೈಕೊಂಡೇವು” ಅಂದುಕೊಳ್ಳುವುದಕ್ಕೆ ಆಸ್ಪದವೇನೂ ಇಲ್ಲ.",
			"14": "<sup>14</sup> ಈ ವಾಕ್ಯವು ನಿಮ್ಮ ಸಮೀಪದಲ್ಲಿಯೇ ಇದೆ; ಇದು ನಿಮ್ಮ ಬಾಯಲ್ಲಿಯೂ ಮತ್ತು ಹೃದಯದಲ್ಲಿಯೂ ಇದೆ; ಇದನ್ನು ಅನುಸಿರಿಸುವುದಕ್ಕೆ ಅವಕಾಶವುಂಟಷ್ಟೆ.",
			"15": "<sup>15</sup> “ನೋಡಿರಿ, ಈಗ ನಾನು ಜೀವ ಮತ್ತು ಶುಭಗಳನ್ನೂ ಹಾಗೂ ಮರಣ ಮತ್ತು ಅಶುಭಗಳನ್ನೂ ನಿಮ್ಮ ಮುಂದೆ ಇಟ್ಟಿದ್ದೇನೆ.",
			"16": "<sup>16</sup> ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ಪ್ರಕಾರ ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಪ್ರೀತಿಸಿ, ಆತನು ಹೇಳಿದ ಮಾರ್ಗದಲ್ಲಿ ನಡೆದು, ಆತನ ಆಜ್ಞಾವಿಧಿ ನಿಯಮಗಳನ್ನು ಅನುಸರಿಸಿದರೆ ನೀವು ಬದುಕಿಕೊಂಡು ಹೆಚ್ಚುವಿರಿ; ಮತ್ತು ನೀವು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವುದಕ್ಕೆ ಹೋಗುವ ದೇಶದಲ್ಲಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಅಭಿವೃದ್ಧಿಪಡಿಸುವನು.",
			"17": "<sup>17</sup> ಆದರೆ ನೀವು ಆತನನ್ನು ಬಿಟ್ಟು ಆತನ ಮಾತನ್ನು ಕೇಳಲೊಲ್ಲದೆ ಮರುಳುಗೊಂಡವರಾಗಿ, ಇತರ ದೇವರುಗಳನ್ನು ಪೂಜಿಸಿ, ಸೇವಿಸಿದರೆ,",
			"18": "<sup>18</sup> ನೀವು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವುದಕ್ಕೆ ಯೊರ್ದನ್ ನದಿಯನ್ನು ದಾಟಿ ಹೋಗುವ ದೇಶದಲ್ಲಿ ಬಹುಕಾಲ ಇರದೆ ನಾಶವಾಗಿಯೇ ಹೋಗುವಿರೆಂದು ನಾನು ಈಗ ನಿಮಗೆ ಖಂಡಿತವಾಗಿ ತಿಳಿಸುತ್ತೇನೆ.",
			"19": "<sup>19</sup> ನಾನು ಜೀವ ಮತ್ತು ಮರಣಗಳನ್ನೂ ಹಾಗು ಆಶೀರ್ವಾದ ಮತ್ತು ಶಾಪಗಳನ್ನೂ ಈಗ ನಿಮ್ಮ ಮುಂದೆ ಇಟ್ಟಿದ್ದೇನೆ; ಇದಕ್ಕೆ ಭೂಮಿ ಮತ್ತು ಆಕಾಶಗಳು ಸಾಕ್ಷಿಗಳಾಗಿರಲಿ. ಆದುದರಿಂದ ನೀವೂ ನಿಮ್ಮ ಸಂತತಿಯವರೂ ಬದುಕಿಕೊಳ್ಳುವಂತೆ ಜೀವವನ್ನೇ ಆರಿಸಿಕೊಳ್ಳಿರಿ.",
			"20": "<sup>20</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಪ್ರೀತಿಸಿ, ಆತನ ಮಾತಿಗೆ ವಿಧೇಯರಾಗಿರಿ, ಆತನನ್ನು ಹೊಂದಿಕೊಂಡೇ ಇರ್ರಿ. ಯೆಹೋವನು ನಿಮ್ಮ ಪಿತೃಗಳಾದ ಅಬ್ರಹಾಮ್, ಇಸಾಕ್ ಮತ್ತು ಯಾಕೋಬರಿಗೆ ಪ್ರಮಾಣ ಮಾಡಿಕೊಟ್ಟ ದೇಶದಲ್ಲಿ ನೀವು ಬದುಕಿಕೊಳ್ಳುವುದಕ್ಕೂ ಬಹುಕಾಲ ಇರುವುದಕ್ಕೂ ಆತನೇ ಆಧಾರ” ಎಂದು ಹೇಳಿದನು."
		},
		"31": {
			"1": "<sup>1</sup> ಮೋಶೆ ಹೋಗಿ ಈ ಮಾತುಗಳನ್ನು ಇಸ್ರಾಯೇಲರೆಲ್ಲರಿಗೆ ತಿಳಿಸಿದನು.",
			"2": "<sup>2</sup> ಅವನು ಅವರಿಗೆ, “ನಾನು ಈಗ ನೂರಿಪ್ಪತ್ತು ವರುಷದವನಾಗಿದ್ದೇನೆ; ವಿಶೇಷವಾದ ಕಾರ್ಯಗಳನ್ನು ನಿರ್ವಹಿಸುವುದು ಇನ್ನು ನನ್ನಿಂದಾಗದು. ಯೆಹೋವನು ನನಗೆ, ‘ನೀನು ಈ ಯೊರ್ದನ್ ನದಿಯನ್ನು ದಾಟಬಾರದು’ ಎಂದು ಆಜ್ಞಾಪಿಸಿದ್ದಾನೆ.",
			"3": "<sup>3</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ತಾನೇ ನಿಮ್ಮ ಮುಂದೆ ಹೋಗುವನು; ಆತನು ಆ ಜನಾಂಗಗಳನ್ನು ನಿಮ್ಮ ಎದುರಿನಲ್ಲಿ ನಿಲ್ಲದಂತೆ ನಾಶಮಾಡುವುದರಿಂದ ನೀವು ಅವರ ದೇಶವನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವಿರಿ. ಯೆಹೋವನು ಆಜ್ಞಾಪಿಸಿದಂತೆ ಯೆಹೋಶುವನು ನಿಮ್ಮ ನಾಯಕನಾಗಿ ನದಿಯನ್ನು ದಾಟಿಹೋಗುವನು.",
			"4": "<sup>4</sup> ಯೆಹೋವನು ಅಮೋರಿಯರ ಅರಸನಾದ ಸೀಹೋನ್ ಮತ್ತು ಓಗರನ್ನು ನಾಶಮಾಡಿ, ಅವರ ದೇಶವನ್ನು ನಿಮಗೆ ಸ್ವಾಧೀನಪಡಿಸಿದಂತೆ ಆ ಜನಾಂಗಗಳನ್ನೂ ನಾಶಮಾಡಿ ಅವರ ದೇಶವನ್ನು ನಿಮಗೆ ವಶಪಡಿಸುವನು.",
			"5": "<sup>5</sup> ಯೆಹೋವನು ಅವರನ್ನು ನಿಮ್ಮಿಂದ ಪರಾಜಯಪಡಿಸಿದಾಗ ನೀವು ಅವರ ವಿಷಯದಲ್ಲಿ ನನ್ನ ಆಜ್ಞೆಗಳನ್ನೆಲ್ಲಾ ಅನುಸರಿಸಿ ನಡೆದುಕೊಳ್ಳಬೇಕು.",
			"6": "<sup>6</sup> ನೀವು ಶೂರರಾಗಿ ಧೈರ್ಯದಿಂದ ಇರ್ರಿ; ಅವರಿಗೆ ಅಂಜಬೇಡಿರಿ, ಕಳವಳಪಡಬೇಡಿರಿ. ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನೇ ನಿಮ್ಮ ಸಂಗಡ ಇರುತ್ತಾನಲ್ಲಾ; ಆತನು ನಿಮ್ಮನ್ನು ಕೈಬಿಡುವುದಿಲ್ಲ, ಅಪಜಯಕ್ಕೆ ಗುರಿಪಡಿಸುವುದೇ ಇಲ್ಲ” ಎಂದು ಹೇಳಿದನು.",
			"7": "<sup>7</sup> ಆಗ ಮೋಶೆ ಯೆಹೋಶುವನನ್ನು ಕರೆದು ಇಸ್ರಾಯೇಲರ ಮುಂದೆ ಅವನಿಗೆ, “ಯೆಹೋವನು ಇವರ ಪಿತೃಗಳಿಗೆ ಪ್ರಮಾಣಮಾಡಿಕೊಟ್ಟ ದೇಶಕ್ಕೆ ನೀನೇ ಇವರೊಡನೆ ಹೋಗಿ ಅದನ್ನು ಅವರಿಗೆ ಸ್ವಾಧೀನಪಡಿಸಬೇಕು. ಆದುದರಿಂದ ಶೂರನಾಗಿ ಧೈರ್ಯದಿಂದಿರು;",
			"8": "<sup>8</sup> ಯೆಹೋವನು ತಾನೇ ನಿನ್ನ ಮುಂದೆ ಹೋಗುವನು; ಆತನೇ ನಿನ್ನ ಸಂಗಡ ಇರುವನು; ನಿನ್ನನ್ನು ಅಪಜಯಕ್ಕೆ ಗುರಿಪಡಿಸುವುದೇ ಇಲ್ಲ, ಕೈಬಿಡುವುದಿಲ್ಲ; ಅಂಜಬೇಡ, ಧೈರ್ಯದಿಂದಿರು” ಎಂದು ಹೇಳಿದನು.",
			"9": "<sup>9</sup> ಮೋಶೆ ಈ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಬರೆದು ಯೆಹೋವನ ಆಜ್ಞಾಶಾಸನಗಳ ಮಂಜೂಷವನ್ನು ಹೊರುವ ಲೇವಿಯರಾದ ಯಾಜಕರ ಮತ್ತು ಇಸ್ರಾಯೇಲರ ಹಿರಿಯರ ವಶಕ್ಕೆ ಕೊಟ್ಟನು.",
			"10": "<sup>10</sup> ಆತನು ಅವರಿಗೆ, “ಪ್ರತಿ ಏಳು ವರುಷಗಳು ಪೂರ್ತಿಯಾದಾಗ ಬಿಡುಗಡೆಯ ಸಂವತ್ಸರದ ನೇಮಕ ಕಾಲದಲ್ಲಿ ಅಂದರೆ ಪರ್ಣಶಾಲೆಗಳ ಜಾತ್ರೆಯಲ್ಲಿ",
			"11": "<sup>11</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಆದುಕೊಂಡ ಸ್ಥಳದಲ್ಲಿ ಇಸ್ರಾಯೇಲರೆಲ್ಲರು ಆತನ ಸನ್ನಿಧಿಗೆ ಕೂಡಿಬಂದಾಗ ನೀವು ಅವರೆಲ್ಲರಿಗೆ ಕೇಳಿಸುವಂತೆ ಈ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಅವರ ಮುಂದೆ ಓದಬೇಕು.",
			"12": "<sup>12</sup> ಜನರೆಲ್ಲರೂ ಈ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಕೇಳಿ ತಿಳಿದುಕೊಳ್ಳುವಂತೆ ಸ್ತ್ರೀಯರನ್ನೂ, ಪುರುಷರನ್ನೂ, ಮಕ್ಕಳನ್ನೂ ಮತ್ತು ನಿಮ್ಮ ಊರುಗಳಲ್ಲಿರುವ ಅನ್ಯರನ್ನೂ ಕೂಡಿಸಬೇಕು. ಆಗ ಅವರು ತಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿ ಈ ಧರ್ಮಶಾಸ್ತ್ರದ ನಿಯಮಗಳನ್ನು ಅನುಸಿರಿಸಿ ನಡೆಯುವರು.",
			"13": "<sup>13</sup> ಮತ್ತು ಯೆಹೋವನ ಮಹತ್ಕಾರ್ಯಗಳನ್ನು ನೋಡದಿರುವ ನಿಮ್ಮ ಸಂತತಿಯವರೂ ಕೇಳಿ ನೀವು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವುದಕ್ಕೆ ಯೊರ್ದನ್ ನದಿಯನ್ನು ದಾಟಿ ಹೋಗುವ ದೇಶದಲ್ಲಿ ವಾಸವಾಗಿರುವವರೆಗೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿರುವುದಕ್ಕೆ ಕಲಿತುಕೊಳ್ಳುವರು” ಎಂದು ಹೇಳಿದನು.",
			"14": "<sup>14</sup> ಯೆಹೋವನು ಮೋಶೆಗೆ, “ನೀನು ಸಾಯಬೇಕಾದ ದಿನಗಳು ಸಮೀಪವಾದವು, ಆದಕಾರಣ ನೀನು ಯೆಹೋಶುವನನ್ನು ಕರೆದುಕೊಂಡು ಬಂದು ದೇವದರ್ಶನದ ಗುಡಾರದಲ್ಲಿ ನನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನಿಲ್ಲಬೇಕು; ಆಗ ನಾನು ಅವನಿಗೆ ಅಧಿಕಾರವನ್ನು ಕೊಡುವೆನು” ಎಂದು ಹೇಳಿದನು. ಆಗ ಮೋಶೆ ಮತ್ತು ಯೆಹೋಶುವನೂ ದೇವದರ್ಶನದ ಗುಡಾರದಲ್ಲಿ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನಿಂತುಕೊಂಡರು.",
			"15": "<sup>15</sup> ಅಲ್ಲಿ ಯೆಹೋವನು ಮೇಘಸ್ತಂಭದಲ್ಲಿ ಕಾಣಿಸಿದನು; ಆ ಮೇಘಸ್ತಂಭವು ಗುಡಾರದ ಬಾಗಿಲಿನ ಬಳಿಯಲ್ಲಿ ನಿಂತಿತು.",
			"16": "<sup>16</sup> ಯೆಹೋವನು ಮೋಶೆಗೆ, “ನೀನು ಪಿತೃಗಳಲ್ಲಿ ಸೇರಿದ ಮೇಲೆ ಈ ಜನರು ನನ್ನನ್ನು ಬಿಟ್ಟು ನಾನು ಅವರೊಡನೆ ಮಾಡಿಕೊಂಡ ಒಡಂಬಡಿಕೆಯನ್ನು ಮೀರಿ ದೇವದ್ರೋಹಿಗಳಾಗಿ ತಾವು ಹೋಗುವ ದೇಶದಲ್ಲಿರುವ ಅನ್ಯದೇವತೆಗಳನ್ನು ಪೂಜಿಸುವರು.",
			"17": "<sup>17</sup> ಆಗ ನಾನು ಅವರ ಮೇಲೆ ಬಲುಕೋಪಗೊಂಡು ಅವರನ್ನು ಬಿಟ್ಟು ಅವರಿಗೆ ವಿಮುಖನಾಗಿರುವೆನು. ಆದುದರಿಂದ ಅವರು ಸಂಹಾರಕ್ಕೆ ಗುರಿಯಾಗುವರು; ಮತ್ತು ಅನೇಕ ಕಷ್ಟಗಳೂ ಹಾಗು ವಿಪತ್ತುಗಳೂ ಅವರಿಗೆ ಒದಗುವವು. ಆಗ ಅವರು, ‘ನಮ್ಮ ದೇವರು ನಮ್ಮ ಮಧ್ಯದಲ್ಲಿ ಇಲ್ಲದೆ ಹೋದುದರಿಂದಲೇ ಈ ವಿಪತ್ತುಗಳೆಲ್ಲಾ ನಮಗೆ ಸಂಭವಿಸಿತ್ತಲ್ಲ’ ಅಂದುಕೊಳ್ಳುವರು.",
			"18": "<sup>18</sup> ಅವರು ನನ್ನನ್ನು ಬಿಟ್ಟು ಇತರ ದೇವರುಗಳನ್ನು ಆಶ್ರಯಿಸಿ ಬಹಳ ದುಷ್ಕಾರ್ಯಗಳನ್ನು ನಡೆಸುವುದರಿಂದ ನಾನು ಆ ಕಾಲದಲ್ಲಿ ಖಂಡಿತವಾಗಿ ಅವರಿಗೆ ವಿಮುಖನಾಗಿರುವೆನು.",
			"19": "<sup>19</sup> “ಆದಕಾರಣ ನೀನು ಈ ಪದ್ಯವನ್ನು ಬರೆದುಕೊಂಡು ಇಸ್ರಾಯೇಲರಿಗೆ ಕಲಿಸಿಕೊಡಬೇಕು. ಈ ಪದ್ಯವು ಇಸ್ರಾಯೇಲರಿಗೆ ವಿರುದ್ಧವಾಗಿ ನನ್ನ ಪಕ್ಷದಲ್ಲಿ ಸಾಕ್ಷಿಯಾಗಿರುವಂತೆ ಇದನ್ನು ಅವರಿಗೆ ಬಾಯಿಪಾಠಮಾಡಿಸು.",
			"20": "<sup>20</sup> ನಾನು ಪ್ರಮಾಣಮಾಡಿಕೊಟ್ಟ ದೇಶದಲ್ಲಿ ಇವರು ಸೇರುವುದಕ್ಕಿಂತ ಮುಂಚಿತವಾಗಿಯೇ ಇವರು ಈಗಲೂ ಮಾಡುವ ದುರಾಲೋಚನೆಗಳನ್ನು ನಾನು ಬಲ್ಲೆನು. ನಾನು ಇವರ ಪಿತೃಗಳಿಗೆ ಪ್ರಮಾಣಮಾಡಿದಂತೆ ಹಾಲೂ ಮತ್ತು ಜೇನೂ ಹರಿಯುವ ದೇಶಕ್ಕೆ ಇವರನ್ನು ಸೇರಿಸಿದ ಮೇಲೆ ಇವರು ಚೆನ್ನಾಗಿ ಊಟಮಾಡಿ ಕೊಬ್ಬಿದವರಾದಾಗ ಇತರ ದೇವರುಗಳನ್ನು ಅವಲಂಬಿಸಿ, ಸೇವಿಸಿ ನನ್ನನ್ನು ತಾತ್ಸಾರಮಾಡಿ ನನ್ನ ನಿಬಂಧನೆಯನ್ನು ಮೀರುವರು.",
			"21": "<sup>21</sup> ಅನಂತರ ಅನೇಕ ಕಷ್ಟಗಳೂ ಮತ್ತು ವಿಪತ್ತುಗಳೂ ಸಂಭವಿಸಿದಾಗ ಈ ಪದ್ಯವು ಇವರ ಸಂತತಿಯವರ ಬಾಯಲ್ಲಿ ಇದ್ದುಕೊಂಡು ಅವರ ಮುಂದೆ ಸಾಕ್ಷಿಕೊಡುವುದು” ಎಂದು ಹೇಳಿದನು.",
			"22": "<sup>22</sup> ಆದಕಾರಣ ಮೋಶೆ ಆ ದಿನದಲ್ಲೇ ಈ ಪದ್ಯವನ್ನು ಬರೆದು ಇಸ್ರಾಯೇಲರಿಗೆ ಕಲಿಸಿದನು.",
			"23": "<sup>23</sup> ಆಗ ಯೆಹೋವನು ನೂನನ ಮಗನಾದ ಯೆಹೋಶುವನಿಗೆ, “ನಾನು ಇಸ್ರಾಯೇಲರಿಗೆ ಪ್ರಮಾಣಮಾಡಿಕೊಟ್ಟ ದೇಶದಲ್ಲಿ ನೀನೇ ಅವರನ್ನು ಸೇರಿಸಬೇಕು; ಆದುದರಿಂದ ಶೂರನಾಗಿ ಧೈರ್ಯದಿಂದಿರು; ನಾನೇ ನಿನ್ನ ಸಂಗಡ ಇರುವೆನು” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"24": "<sup>24</sup> ಮೋಶೆ ಈ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಗ್ರಂಥರೂಪವಾಗಿ ಬರೆದು ಪೂರೈಸಿದಾಗ,",
			"25": "<sup>25</sup> ಯೆಹೋವನ ಆಜ್ಞಾಶಾಸನಗಳ ಮಂಜೂಷವನ್ನು ಹೊರುವ ಲೇವಿಯರಿಗೆ,",
			"26": "<sup>26</sup> “ನೀವು ಈ ಧರ್ಮಶಾಸ್ತ್ರ ಪುಸ್ತಕವನ್ನು ನಿಮ್ಮ ವಶಕ್ಕೆ ತೆಗೆದುಕೊಂಡು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಆಜ್ಞಾಶಾಸನಗಳ ಮಂಜೂಷದ ಬಳಿಯಲ್ಲಿ ಇಡಿರಿ. ಇಸ್ರಾಯೇಲರೇ, ಅಲ್ಲಿ ಇದು ನಿಮಗೆ ವಿರುದ್ಧವಾಗಿ ಸಾಕ್ಷಿಕೊಡುವುದಾಗಿರಲಿ.",
			"27": "<sup>27</sup> ಯಾಕೆಂದರೆ ನೀವು ಹಟಮಾರಿಗಳು, ಆಜ್ಞೆಗೆ ಒಳಗಾಗದವರೆಂಬುದು ನನಗೆ ತಿಳಿದೇ ಇದೆ. ನಾನು ಇನ್ನೂ ಜೀವದಿಂದಿರುವಾಗಲೇ ನೀವು ಯೆಹೋವನಿಗೆ ವಿರುದ್ಧವಾಗಿ ಪ್ರತಿಭಟಿಸಿದ್ದೀರಿ; ನಾನು ಹೋದ ತರುವಾಯ ನೀವು ಪುನಃ ಬೀಳುವುದು ಮತ್ತಷ್ಟು ನಿಶ್ಚಯ.",
			"28": "<sup>28</sup> ನಿಮ್ಮ ಕುಲಗಳ ಹಿರಿಯರನ್ನೂ, ಅಧಿಪತಿಗಳನ್ನೂ ನನ್ನ ಬಳಿಗೆ ಕೂಡಿಸಿರಿ; ನಾನು ಈ ಮಾತುಗಳನ್ನು ಅವರಿಗೆ ತಿಳಿಸಿ ಅವರಿಗೆ ವಿರುದ್ಧವಾಗಿ ಸಾಕ್ಷಿಗಳಾಗುವುದಕ್ಕೆ ಭೂಮಿ ಮತ್ತು ಆಕಾಶಗಳನ್ನು ಕರೆಯುವೆನು.",
			"29": "<sup>29</sup> ನಾನು ಹೋದ ಮೇಲೆ ನೀವು ದ್ರೋಹಿಗಳಾಗಿ ನಾನು ಬೋಧಿಸಿದ ಮಾರ್ಗವನ್ನು ತಪ್ಪಿಹೋಗುವಿರೆಂದೂ ತಿಳಿದಿದೆ, ಅನಂತರದಲ್ಲಿ ನೀವು ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ಕೆಟ್ಟದ್ದನ್ನು ನಡಿಸಿ ದುರಾಚಾರಿಗಳಾಗಿ ಆತನನ್ನು ಕೋಪಗೊಳಿಸುವುದರಿಂದ ನಿಮಗೆ ಆಪತ್ತುಗಳು ಉಂಟಾಗುವವೆಂದೂ ನಾನು ಬಲ್ಲೆ” ಎಂದು ಹೇಳಿದನು.",
			"30": "<sup>30</sup> ಆಗ ಮೋಶೆ ಇಸ್ರಾಯೇಲರ ಸರ್ವಸಮೂಹದವರಿಗೆ ಕೇಳಿಸುವಂತೆ ಈ ಪದ್ಯದ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ಹೇಳಿದನು."
		},
		"32": {
			"1": "<sup>1</sup> ಆಕಾಶಮಂಡಲವೇ, ನನ್ನ ಮಾತುಗಳಿಗೆ ಕಿವಿಗೊಡು. ಭೂಮಂಡಲವೇ, ನಾನು ಹೇಳುವುದನ್ನು ಕೇಳು.",
			"2": "<sup>2</sup> ನನ್ನ ಉಪದೇಶವು ಹಸಿಹುಲ್ಲಿನ ಮೇಲೆ ಮೆಲ್ಲಗೆ ಸುರಿಯುವ ಮಳೆಯ ತುಂತುರುಗಳಂತೆ ತಣ್ಣಗಿರುವುದು; ನನ್ನ ಬೋಧನೆಯು ಮಂಜಿನಂತೆಯೂ ಮತ್ತು ಕಾಯಿಪಲ್ಯಗಳ ಮೇಲೆ ಬೀಳುವ ಹದಮಳೆಯಂತೆಯೂ ಹಿತವಾಗಿರುವುದು.",
			"3": "<sup>3</sup> ನಾನು ಯೆಹೋವನ ನಾಮಮಹತ್ವವನ್ನು ಪ್ರಕಟಿಸುವೆನು; ನಮ್ಮ ದೇವರನ್ನು ಮಹಾಮಹಿಮೆಯುಳ್ಳವನೆಂದು ಕೊಂಡಾಡುವೆನು.",
			"4": "<sup>4</sup> ನಮಗೆ ಶರಣನಾದ ದೇವರು ಮಾಡುವ ಕಾರ್ಯದಲ್ಲಿ ಯಾವ ಕುಂದೂ ಇಲ್ಲ; ಆತನು ನಡೆಸುವುದೆಲ್ಲಾ ನ್ಯಾಯ; ಆತನು ನಿರ್ವಂಚಕನಾದ ನಂಬಿಗಸ್ತ ದೇವರು, ನೀತಿಯುಳ್ಳವನೂ ಮತ್ತು ಯಥಾರ್ಥನೂ ಆಗಿದ್ದಾನೆ.",
			"5": "<sup>5</sup> ಆದರೆ ಅವರು ದ್ರೋಹಿಗಳೇ, ಮಕ್ಕಳಲ್ಲ; ಇದು ಅವರ ದೋಷವು; ಅವರು ವಕ್ರಬುದ್ಧಿಯುಳ್ಳ ಮೂರ್ಖಜಾತಿಯವರು.",
			"6": "<sup>6</sup> ದುರಾಚಾರಿಗಳಿರಾ, ಅವಿವೇಕಿಗಳಿರಾ, ಯೆಹೋವನ ವಿಷಯದಲ್ಲಿ ಈ ರೀತಿಯಾಗಿ ವರ್ತಿಸಬಹುದೇ? ಆತನು ನಿಮ್ಮನ್ನು ಸೃಷ್ಟಿಸಿದ ತಂದೆಯಲ್ಲವೇ; ನಿಮ್ಮನ್ನು ಜನಾಂಗವನ್ನಾಗಿ ಮಾಡಿ ಸ್ಥಾಪಿಸಿದನಲ್ಲವೇ.",
			"7": "<sup>7</sup> ಪೂರ್ವಕಾಲವನ್ನು ನೆನಪಿಗೆ ತಂದುಕೊಳ್ಳಿರಿ; ನಿಮ್ಮ ಪೂರ್ವಜರ ಚರಿತ್ರೆಯನ್ನು ಆಲೋಚಿಸಿರಿ. ನಿಮ್ಮ ನಿಮ್ಮ ತಂದೆಗಳನ್ನು ವಿಚಾರಿಸಿದರೆ ಅವರು ತಿಳಿಸುವರು; ಹಿರಿಯರನ್ನು ಕೇಳಿದರೆ ಅವರು ವಿವರಿಸುವರು.",
			"8": "<sup>8</sup> ಹೇಗೆಂದರೆ, “ಪರಾತ್ಪರನಾದ ದೇವರು ಜನಾಂಗಗಳನ್ನು ಬೇರೆ ಬೇರೆ ಮಾಡಿ ಅವರವರಿಗೆ ಸ್ವದೇಶಗಳನ್ನು ನೇಮಿಸಿಕೊಟ್ಟಾಗ ಇಸ್ರಾಯೇಲರ ಸಂಖ್ಯೆಗೆ ತಕ್ಕಂತೆ ಆಯಾ ಜನಾಂಗಕ್ಕೆ ಒಂದೊಂದು ಪ್ರದೇಶವನ್ನು ಗೊತ್ತು ಮಾಡಿದನು.",
			"9": "<sup>9</sup> ಇಸ್ರಾಯೇಲರು ಮಾತ್ರ ಯೆಹೋವನ ಸ್ವಂತ ಜನರಾದರು. ಯಾಕೋಬನ ವಂಶಸ್ಥರು ಆತನಿಗೆ ಸ್ವಕೀಯ ಪ್ರಜೆಯಾದರು.",
			"10": "<sup>10</sup> ಆತನು ಶೂನ್ಯವೂ ಮತ್ತು ಭಯಂಕರವೂ ಆಗಿರುವ ಮರಳುಗಾಡಿನಲ್ಲಿ ಅವರನ್ನು ಕಂಡು ಗುರಾಣಿಯಂತೆ ಆವರಿಸಿಕೊಂಡನು. ಪ್ರೀತಿಯಿಂದ ಪರಾಂಬರಿಸಿದ ಹಾಗೂ ಕಣ್ಣುಗುಡ್ದೆಯಂತೆ ಕಾಪಾಡಿದನು.",
			"11": "<sup>11</sup> ಹದ್ದು ತನ್ನ ಮರಿಗಳನ್ನು ಗೂಡಿನೊಳಗಿಂದ ಹೊರಡಿಸಿ, ಅವುಗಳ ಬಳಿಯಲ್ಲಿ ಹಾರಾಡುವಂತೆ ಯೆಹೋವನು ತನ್ನ ರೆಕ್ಕೆಗಳನ್ನು ಚಾಚಿ ಇಸ್ರಾಯೇಲರನ್ನು ಹೊತ್ತುಕೊಂಡು ಅವರನ್ನು ಸಂರಕ್ಷಿಸಿದನು.",
			"12": "<sup>12</sup> ಯಾವ ಅನ್ಯದೇವರೂ ಇಲ್ಲ ಯೆಹೋವನೊಬ್ಬನೇ ಅವರನ್ನು ನಡಿಸಿಕೊಂಡು ಬಂದನು.",
			"13": "<sup>13</sup> ಆತನು ಅವರನ್ನು ಭೂಮಿಯ ಎತ್ತರವಾದ ಪ್ರದೇಶಗಳ ಮೇಲೆ ಹತ್ತಿಸಿ ಅವರಿಗೆ ವ್ಯವಸಾಯವೃದ್ಧಿಯನ್ನು ಉಂಟುಮಾಡಿ, ಬಂಡೆಯಿಂದ ಜೇನೂ ಮತ್ತು ಗಿರಿಯಿಂದ ಎಣ್ಣೆಯೂ ದೊರೆಯುವಂತೆ ಮಾಡಿದನು.",
			"14": "<sup>14</sup> ಆಹಾರಕ್ಕಾಗಿ ಆಕಳಿನ ಮೊಸರು, ಆಡು ಕುರಿಗಳ ಹಾಲು, ಕೊಬ್ಬಿದ ಕುರಿ ಟಗರುಗಳ ಮಾಂಸವು, ಬಾಷಾನ್ ಸೀಮೆಯ ಉತ್ತಮಪಶುಗಳು, ಹೋತಗಳು, ಶ್ರೇಷ್ಠವಾದ ಗೋದಿ ಇವುಗಳನ್ನೂ ಮತ್ತು ಪಾನಕ್ಕಾಗಿ ದ್ರಾಕ್ಷೇಯರಸವನ್ನೂ ಕೊಟ್ಟದ್ದೂ; ಇದನ್ನೆಲ್ಲಾ ನೆನಪಿಗೆ ತಂದುಕೊಳ್ಳಿರಿ.”",
			"15": "<sup>15</sup> ಆದರೆ ಯೆಶುರೂನು ಚೆನ್ನಾಗಿ ತಿಂದು, ಕೊಬ್ಬಿ, ತನ್ನನ್ನು ಸೃಷ್ಟಿಸಿದ ದೇವರನ್ನು ಬಿಟ್ಟು ತನ್ನ ಆಶ್ರಯದುರ್ಗವನ್ನು ತಿರಸ್ಕರಿಸಿದನು.",
			"16": "<sup>16</sup> ಅವರು ಅನ್ಯದೇವರುಗಳನ್ನು ಪೂಜಿಸಿ ಆತನನ್ನು ರೇಗಿಸಿದರು; ನಿಷಿದ್ಧಾಚಾರಗಳನ್ನು ನಡಿಸಿ ಆತನಿಗೆ ಸಿಟ್ಟೇರಿಸಿದರು.",
			"17": "<sup>17</sup> ದೇವರೇ ಅಲ್ಲದ ಕ್ಷುದ್ರದೇವತೆಗಳಿಗೂ, ಪಿತೃಗಳು ಭಜಿಸದೆ ಇದ್ದ ಮತ್ತು ತಮಗೆ ಮೊದಲಿನಿಂದಲೂ ಗೊತ್ತಿಲ್ಲದೆ ಇರುವ ನೂತನದೇವತೆಗಳಿಗೂ ಬಲಿಯನ್ನರ್ಪಿಸಿದರು.",
			"18": "<sup>18</sup> ಇಸ್ರಾಯೇಲರೇ, ನಿಮ್ಮನ್ನು ಹುಟ್ಟಿಸಿದ ತಂದೆಯಂತಿರುವ ಶರಣನನ್ನು ನೀವು ಸ್ಮರಿಸಲಿಲ್ಲ; ಹೆತ್ತ ತಾಯಿಯಂತಿರುವ ದೇವರನ್ನು ಮರೆತುಬಿಟ್ಟಿರಿ.",
			"19": "<sup>19</sup> ಯೆಹೋವನು ಇದನ್ನು ಕಂಡು ಅವರ ವಿಷಯದಲ್ಲಿ ಬೇಸರಗೊಂಡನು; ತನ್ನ ಕುಮಾರ ಕುಮಾರ್ತೆಯರ ವಿಷಯದಲ್ಲಿ ವ್ಯಥೆಪಟ್ಟನು.",
			"20": "<sup>20</sup> ಆತನು ಅವರ ವಿಷಯದಲ್ಲಿ ಹೀಗೆ ಅಂದುಕೊಂಡನು, “ನಾನು ಅವರಿಗೆ ವಿಮುಖನಾಗಿ ಅವರಿಗೆ ಪ್ರಾಪ್ತವಾಗುವ ಗತಿಯನ್ನು ನೋಡುವೆನು. ಅವರು ಸತ್ಯವನ್ನು ತಿಳಿದೂ ಅದನ್ನು ಅನುಸರಿಸದೆ, ದ್ರೋಹಿಗಳಾದ ಮಕ್ಕಳಾಗಿದ್ದಾರೆ.",
			"21": "<sup>21</sup> ಅವರು ದೇವರಲ್ಲದವುಗಳ ಮೂಲಕ ನನ್ನನ್ನು ರೇಗಿಸಿದ್ದರಿಂದ ನಾನು ಜನಾಂಗವಲ್ಲದವರ ಮೂಲಕ ಅವರಲ್ಲಿ ಕೋಪಹುಟ್ಟಿಸುವೆನು. ಅವರು ನಿಷ್ಪ್ರಯೋಜಕವಾದ ವಿಗ್ರಹಗಳ ಮೂಲಕ ನನ್ನನ್ನು ಸಿಟ್ಟಿಗೆಬ್ಬಿಸಿದ್ದರಿಂದ ನಾನು ಸದಾಚಾರವಿಲ್ಲದ ಜನರ ಮೂಲಕ ಅವರನ್ನು ಸಿಟ್ಟಿಗೆಬ್ಬಿಸುವೆನು.",
			"22": "<sup>22</sup> ನನ್ನ ಕೋಪಾಗ್ನಿ ಪ್ರಜ್ವಲಿಸುತ್ತದೆ; ಅದು ಪಾತಾಳದ ವರೆಗೂ ವ್ಯಾಪಿಸಿ ಬೆಳೆಯ ಸಹಿತವಾಗಿ ಭೂಮಿಯನ್ನೂ ಬುಡಸಹಿತವಾಗಿ ಬೆಟ್ಟಗಳನ್ನೂ ದಹಿಸಿಬಿಡುವುದು.",
			"23": "<sup>23</sup> ಅವರಿಗೆ ಒಂದರ ಮೇಲೊಂದಾಗಿ ವಿಪತ್ತುಗಳನ್ನು ಬರಮಾಡುವೆನು; ನನ್ನ ಎಲ್ಲಾ ಬಾಣಗಳನ್ನೂ ಅವರಿಗೆ ವಿರುದ್ಧವಾಗಿ ಪ್ರಯೋಗಿಸುವೆನು.",
			"24": "<sup>24</sup> ಅವರು ಬರದಿಂದ ಕ್ಷೀಣವಾಗುವರು, ತಾಪದಿಂದಲೂ ಅಂಟುರೋಗದಿಂದಲೂ ಸಾಯುವರು; ಅದಲ್ಲದೆ ನಾನು ದುಷ್ಟ ಮೃಗಗಳನ್ನೂ ಮತ್ತು ವಿಷಸರ್ಪಗಳನ್ನೂ ಅವರ ಮೇಲೆ ಬರಮಾಡುವೆನು.",
			"25": "<sup>25</sup> ಮನೆಯ ಹೊರಗೆ ಕತ್ತಿಯೂ, ಒಳಗೆ ಭಯವೂ ಇರುವುದು. ಯೌವನಸ್ಥರು, ಕನ್ಯೆಯರು, ಮೊಲೆಕೂಸುಗಳು, ನರೇತಲೆಯವರು ಇವರೆಲ್ಲರೂ ಕತ್ತಿಯಿಂದ ಕೊಲ್ಲಲ್ಪಡುವರು.",
			"26": "<sup>26</sup> ಅವರನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ನಾಶಮಾಡುತ್ತಿದ್ದೆನು, ಅವರು ಮನುಷ್ಯರ ಜ್ಞಾಪಕದಲ್ಲೂ ಇಲ್ಲದಂತೆ ಮಾಡುತ್ತಿದ್ದೆನು.",
			"27": "<sup>27</sup> ಆದರೆ ಅವರ ವಿರುಧಿಗಳ ಹಮ್ಮಿನಿಂದ ತಪ್ಪಾದ ಭಾವನೆ ಮಾಡಿಕೊಂಡು, ‘ಇದು ನಮ್ಮ ಭುಜಬಲದಿಂದಲೇ ಹೊರತು ಯೆಹೋವನಿಂದ ಆಗಲಿಲ್ಲ ಅಂದುಕೊಳ್ಳುವರು’ ಎಂದು ನಾನು ಹಿಂದೆಗೆದೆನು” ಎಂಬುದೇ.",
			"28": "<sup>28</sup> ಅವರು ವಿವೇಚನೆಯಿಲ್ಲದ ಜನರು; ಅವರಿಗೆ ಸ್ವಲ್ಪವಾದರೂ ವಿವೇಕವಿಲ್ಲ.",
			"29": "<sup>29</sup> ಅವರಿಗೆ ಜ್ಞಾನವಿದ್ದರೆ ಈ ಸಂಗತಿಗಳನ್ನೆಲ್ಲಾ ಗ್ರಹಿಸುತ್ತಿದ್ದರು; ತಮಗೆ ಅಂತ್ಯದಲ್ಲಿ ದುರವಸ್ಥೆ ಪ್ರಾಪ್ತವಾಗುವುದೆಂದು ತಿಳಿದುಕೊಳ್ಳುತ್ತಿದ್ದರು!",
			"30": "<sup>30</sup> ಅವರ ಆಶ್ರಯದುರ್ಗವಾದ ಯೆಹೋವನು ಅವರನ್ನು ಶತ್ರಗಳಿಗೆ ಒಪ್ಪಿಸಿಕೊಡದಿದ್ದರೆ ಒಬ್ಬನಿಂದ ಸಾವಿರ ಜನರು ಹೇಗೆ ಸೋತುಹೋಗುತ್ತಿದ್ದರು? ಯೆಹೋವನು ಅವರನ್ನು ಕೈಬಿಡದಿದ್ದರೆ ಇಬ್ಬರಿಗೆ ಹೆದರಿ ಹತ್ತು ಸಾವಿರ ಜನರು ಹೇಗೆ ಓಡಿಹೋಗುತ್ತಿದ್ದರು?",
			"31": "<sup>31</sup> ನಮ್ಮ ಆಶ್ರಯದುರ್ಗ ಅವರ ಆಶ್ರಯದುರ್ಗದಂತಲ್ಲಾ, ನಮ್ಮ ಅಶ್ರಯದಾತನಿಗೆ ಯಾರು ಸಮಾನರಲ್ಲವೆಂದು ನಮ್ಮ ಶತ್ರುಗಳೇ ಒಪ್ಪಿಕೊಳ್ಳುತ್ತಾರೆ.",
			"32": "<sup>32</sup> ಅವರು ಸೊದೋಮ್ಯರೆಂಬ ದ್ರಾಕ್ಷೇಲತೆಯ ಒಂದು ಬಳ್ಳಿಯಂತಿದ್ದಾರೆ; ಮತ್ತು ಅದು ಗೊಮೋರ ಪಟ್ಟಣದ ತೋಟಗಳಲ್ಲಿ ಬೆಳೆಯುವ ಜಾತಿ. ಅದರ ದ್ರಾಕ್ಷೇಹಣ್ಣು ವಿಷದ ದ್ರಾಕ್ಷೇಹಣ್ಣು; ಅದರ ಗೊಂಚಲು ಕಹಿ.",
			"33": "<sup>33</sup> ಅದರ ದ್ರಾಕ್ಷಾರಸವು ಸರ್ಪದಂತೆಯೂ ಮತ್ತು ಕ್ರೂರ ವಿಷಸರ್ಪದಂತೆ ಮರಣಕರವಾದದ್ದು.",
			"34": "<sup>34</sup> ಇದನ್ನೆಲ್ಲಾ ನಾನು ಮುದ್ರೆಹಾಕಿ ನನ್ನ ಉಗ್ರಾಣದಲ್ಲಿಟ್ಟುಕೊಂಡಿದ್ದೇನೆ ಅಲ್ಲವೇ?",
			"35": "<sup>35</sup> ಪ್ರತೀಕಾರಮಾಡಿ ಮುಯ್ಯಿಗೆ ಮುಯ್ಯಿ ತೀರಿಸುವುದು ನನ್ನ ಕೆಲಸ; ಅವರು ಜಾರಿ ಬೀಳುವ ಸಮಯ ಬರುವುದು. ಅವರಿಗೆ ಆಪತ್ಕಾಲವು ಸಮೀಪಿಸಿತು; ಅವರಿಗೋಸ್ಕರ ಸಿದ್ಧವಾಗಿರುವ ದುರ್ಗತಿ ಬೇಗ ಬರುವುದು.",
			"36": "<sup>36</sup> ಆಗ ಯೆಹೋವನು ತನ್ನ ಜನರಿಗಾಗಿ ನ್ಯಾಯತೀರಿಸುವನು. ಪರತಂತ್ರರಾಗಲಿ ಅಥವಾ ಸ್ವತಂತ್ರರಾಗಲಿ ಅವರೆಲ್ಲರೂ ನಿರಾಶ್ರಿತರಾಗಿ ನಿಶ್ಶೇಷರಾದುದ್ದನ್ನು ಆತನು ತಿಳಿದು ತನ್ನ ಸೇವಕರ ವಿಷಯದಲ್ಲಿ ಕನಿಕರ ಪಡುವನು.",
			"37": "<sup>37</sup> ಯೆಹೋವನು ಅವರ ವಿಷಯದಲ್ಲಿ ಹೇಳುವುದು ಏನೆಂದರೆ, “ಅವರು ಪೂಜಿಸುತ್ತಿದ್ದ ದೇವರುಗಳು ಎಲ್ಲಿ ಹೋದರು? ಅವರು ಆಶ್ರಯಿಸಿಕೊಂಡಿದ್ದ ಆಶ್ರಯದುರ್ಗನು ಎಲ್ಲಿದ್ದಾನೆ?",
			"38": "<sup>38</sup> ಅವರ ನೈವೇದ್ಯಗಳ ಕೊಬ್ಬನ್ನು ತಿಂದು ಮತ್ತು ಅವರು ಸಮರ್ಪಿಸಿದ ಪಾನದ್ರವ್ಯಗಳನ್ನು ಕುಡಿದ ದೇವತೆಗಳು ಎಲ್ಲಿ? ಅವರೇ ನಿಮ್ಮ ಸಹಾಯಕ್ಕೆ ಬರಲಿ; ಅವರೇ ನಿಮ್ಮನ್ನು ಕಾಯಲಿ.",
			"39": "<sup>39</sup> ನಾನೇ, ನಾನೊಬ್ಬನೇ ದೇವರಾಗಿರಲಾಗಿ ನನ್ನ ಹೊರತು ಯಾವ ದೇವರೂ ಇಲ್ಲವೆಂದು ಈಗಲಾದರೂ ತಿಳಿದುಕೊಳ್ಳಿರಿ. ಬದುಕಿಸುವವನೂ ಹಾಗೂ ಕೊಲ್ಲುವವನೂ ನಾನೇ; ಗಾಯಪಡಿಸುವವನೂ ಮತ್ತು ವಾಸಿಮಾಡುವವನೂ ನಾನೇ; ನನ್ನ ಕೈಯಿಂದ ತಪ್ಪಿಸಲು ಶಕ್ತನು ಯಾವನೂ ಇಲ್ಲ.",
			"40": "<sup>40</sup> ನಾನು ಆಕಾಶದ ಕಡೆಗೆ ಕೈಯೆತ್ತಿ ನಾನು ಸದಾಕಾಲ ಜೀವಿಸುವವನೆಂಬುದು ಎಷ್ಟು ನಿಶ್ಚಯ ಎಂದು ಖಂಡಿತವಾಗಿ ಪ್ರಮಾಣಮಾಡುವೆನು,",
			"41": "<sup>41</sup> ‘ಥಳಥಳಿಸುವ ನನ್ನ ಕತ್ತಿಯನ್ನು ನಾನು ಹದಮಾಡಿ, ನಾನು ಕೈ ಚಾಚಿ ನ್ಯಾಯವನ್ನು ಸ್ಥಾಪಿಸುವುದಕ್ಕೆ ಮುನ್ನುಗ್ಗಿ ನನ್ನ ಶತ್ರುಗಳಿಗೆ ಮುಯ್ಯಿತೀರಿಸುವೆನು, ನನ್ನನ್ನು ದ್ವೇಷಿಸಿದವರಿಗೆ ಪ್ರತಿದಂಡನೆ ಮಾಡುವೆನು;",
			"42": "<sup>42</sup> ನನ್ನ ಬಾಣಗಳು ರಕ್ತವನ್ನು ಕುಡಿದು ಮತ್ತವಾಗುವವು; ನನ್ನ ಕತ್ತಿಯು ಕೊಲ್ಲಲ್ಪಟ್ಟವರ ಮತ್ತು ಸೆರೆಹಿಡಿದವರ ರಕ್ತಮಾಂಸಗಳನ್ನು ಭಕ್ಷಿಸಿ ಶತ್ರುಗಳಲ್ಲಿರುವ ವೀರರ ತಲೆಗಳನ್ನು ಚೆಂಡಾಡುವುದು’ ಎಂಬುದೇ.",
			"43": "<sup>43</sup> ಜನಾಂಗಳಿರಾ, ದೇವರ ಜನರನ್ನು ಹೊಗಳಿರಿ. ತನ್ನ ಸೇವಕರ ರಕ್ತವನ್ನು ಚೆಲ್ಲಿದ ಶತ್ರುಗಳಿಗೆ ಯೆಹೋವನು ಪ್ರತಿದಂಡನೆ ಮಾಡುತ್ತಾನೆ; ಅವರಿಗೆ ಮುಯ್ಯಿತೀರಿಸುತ್ತಾನೆ; ತನ್ನ ಜನರಿಗೋಸ್ಕರವೂ ಮತ್ತು ದೇಶಕ್ಕೋಸ್ಕರವೂ ದೋಷ ಪರಿಹಾರಮಾಡುತ್ತಾನೆ.",
			"44": "<sup>44</sup> ಮೋಶೆ ಮತ್ತು ನೂನನ ಮಗನಾದ ಯೆಹೋಶುವನೂ ಈ ಪದ್ಯದ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ಜನರಿಗೆ ಕೇಳಿಸುವಂತೆ ಹೇಳಿದರು.”",
			"45": "<sup>45</sup> ಮೋಶೆ ಈ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ಇಸ್ರಾಯೇಲರಿಗೆ ಹೇಳಿ ಮುಗಿಸಿದನಂತರ,",
			"46": "<sup>46</sup> ಅವರಿಗೆ, “ನಾನು ಈಗ ನಿಮಗೆ ಹೇಳಿದ ಎಚ್ಚರಿಕೆಯ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ಮನಸ್ಸಿನಲ್ಲಿಟ್ಟುಕೊಳ್ಳಿರಿ, ಮತ್ತು ನಿಮ್ಮ ಸಂತತಿಯವರಿಗೆ ಈ ಧರ್ಮಶಾಸ್ತ್ರದ ನಿಯಮಗಳನ್ನು ಅನುಸಿರಿಸಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿರಿ.",
			"47": "<sup>47</sup> ಇದು ನಿರರ್ಥಕವೆಂದು ಭಾವಿಸಬಾರದು; ಇದರಿಂದ ನೀವು ಬಾಳುವಿರಿ; ನೀವು ಯೊರ್ದನ್ ನದಿಯನ್ನು ದಾಟಿ ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವುದಕ್ಕೆ ಹೋಗುವ ದೇಶದಲ್ಲಿ ಇದನ್ನು ಅನುಸರಿಸುವುದರಿಂದಲೇ ಬಹುಕಾಲ ಇರುವಿರಿ” ಎಂದು ಹೇಳಿದನು.",
			"48": "<sup>48</sup> ಆದೇ ದಿನ ಯೆಹೋವನು ಮೋಶೆಗೆ,",
			"49": "<sup>49</sup> “ನೀನು ಈ ಅಬಾರೀಮ್ ಬೆಟ್ಟಗಳನ್ನು ಅಂದರೆ ಮೋವಾಬ್ಯರ ದೇಶದಲ್ಲಿ ಯೆರಿಕೋ ಪಟ್ಟಣಕ್ಕೆ ಎದುರಾಗಿರುವ ನೆಬೋ ಬೆಟ್ಟವನ್ನು ಹತ್ತಿ, ನಾನು ಇಸ್ರಾಯೇಲರಿಗೆ ಸ್ವದೇಶವಾಗುವುದಕ್ಕೆ ಕೊಡುವ ಕಾನಾನ್ ದೇಶವನ್ನು ನೋಡು.",
			"50": "<sup>50</sup> ಅನಂತರ ನಿನ್ನ ಅಣ್ಣನಾದ ಆರೋನನು ಹೇಗೆ ಹೋರೆಬ್ ಎಂಬ ಬೆಟ್ಟದಲ್ಲಿ ದೇಹವನ್ನು ಬಿಟ್ಟು ತನ್ನ ಪಿತೃಗಳಲ್ಲಿ ಸೇರಿದನೋ ಹಾಗೆಯೇ ನೀನೂ ಈ ಬೆಟ್ಟದಲ್ಲಿ ದೇಹವನ್ನು ಬಿಟ್ಟು ಪಿತೃಗಳಲ್ಲಿ ಸೇರಬೇಕು.",
			"51": "<sup>51</sup> ನೀವಿಬ್ಬರೂ ಚಿನ್ ಅರಣ್ಯದ ಕಾದೇಶಿನ ಮೆರೀಬಾದಲ್ಲಿ ನೀರು ಹೊರಟ ಸ್ಥಳದ ಬಳಿಯಲ್ಲಿದ್ದಾಗ, ಇಸ್ರಾಯೇಲರ ಮಧ್ಯದಲ್ಲಿ ನೀನು ನನಗೆ ವಿರುದ್ಧವಾಗಿ ದ್ರೋಹಮಾಡಿ ಅವರ ಮುಂದೆ ನನ್ನ ಗೌರವವನ್ನು ಕಾಪಾಡದೆಹೋದುದರಿಂದ ಹೀಗೆ ಆಗಬೇಕು.",
			"52": "<sup>52</sup> ನಾನು ಇಸ್ರಾಯೇಲರಿಗೆ ಕೊಡುವ ದೇಶವನ್ನು ನೀನು ದೂರದಿಂದ ನೋಡಬಹುದೇ ಹೊರತು ಅದರಲ್ಲಿ ಪ್ರವೇಶಿಸಕೂಡದು” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು."
		},
		"33": {
			"1": "<sup>1</sup> ದೇವಪುರುಷನಾದ ಮೋಶೆ ದೇಹವನ್ನು ಬಿಡುವುದಕ್ಕಿಂತ ಮುಂಚೆ ಇಸ್ರಾಯೇಲರ ವಿಷಯವಾಗಿ ಹೇಳಿದ ಆಶೀರ್ವಾದವು:",
			"2": "<sup>2</sup> “ಯೆಹೋವನು ಸೀನಾಯಿಬೆಟ್ಟದಿಂದ ಬಂದು ಸೇಯೀರ್ ಎಂಬ ಬೆಟ್ಟದ ಸೀಮೆಯೊಳಗಿಂದ ಪ್ರಕಾಶಿಸಿ ಪಾರಾನ್ ಪರ್ವತದಿಂದ ಪ್ರಜ್ವಲಿಸಿ, ಲಕ್ಷಾಂತರ ಪರಿಶುದ್ಧದೂತರ ಮಧ್ಯದಿಂದ ಅವರಿಗೋಸ್ಕರ ದಯಮಾಡಿದನು; ಆತನ ಬಲಪಾರ್ಶ್ವದಲ್ಲಿ ಅಗ್ನಿಸದೃಶವಾದ ಧರ್ಮಶಾಸ್ತ್ರವಿತ್ತು.",
			"3": "<sup>3</sup> “ಆತನು ತನ್ನ ಜನರನ್ನು ಪ್ರೀತಿಸುತ್ತಾನೆ. ಯೆಹೋವನೇ, ನಿನ್ನ ಪರಿಶುದ್ಧ ಜನರು ನಿನ್ನ ಆಶ್ರಯದಲ್ಲಿಯೇ ಇದ್ದಾರೆ; ನಿನ್ನ ಚರಣಸನ್ನಿಧಾನದಲ್ಲಿ ಕುಳಿತಿರುವರು; ನೀನು ಹೇಳುವ ಆಜ್ಞೆಗಳನ್ನು ತಪ್ಪದೆ ಪಾಲಿಸಿದರು.",
			"4": "<sup>4</sup> ಮೋಶೆಯಾದ, ನಾನು ಯಾಕೋಬನ ಸಂತತಿಯವರಾದ ನಿಮಗೆ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಸ್ವಾಸ್ತ್ಯವಾಗಿ ಕೊಟ್ಟಿದ್ದೇನೆ.",
			"5": "<sup>5</sup> ಇಸ್ರಾಯೇಲರ ಕುಲಗಳು ಅವರ ಅಧಿಪತಿಗಳೊಡನೆ ಒಟ್ಟಾಗಿ ಸೇರಿಬಂದಾಗ ಯೆಹೋವನು ತಾನೇ ಯೆಶುರೂನಿನಲ್ಲಿ ಅರಸನಾದನು.",
			"6": "<sup>6</sup> “ರೂಬೇನ್ ಕುಲದ ವಿಷಯದಲ್ಲಿ, ‘ರೂಬೇನ್ ಕುಲವು ನಾಶವಾಗದೆ ಉಳಿಯಲಿ; ಆದರೆ ಅದು ಸ್ವಲ್ಪ ಜನರುಳ್ಳದ್ದಾಗಿರುವುದು’ ಎಂದು ಹೇಳಿದನು.",
			"7": "<sup>7</sup> “ಯೆಹೂದ ಕುಲದ ವಿಷಯದಲ್ಲಿ, ‘ಯೆಹೋವನೇ, ಯೆಹೂದ ಕುಲದವರ ಮೊರೆಯನ್ನು ಲಾಲಿಸಿ ಅವರನ್ನು ಅವರ ಬಂಧುಗಳೊಡನೆ ಸೇರಿಸು. ಅವರಿಗೋಸ್ಕರ ಯುದ್ಧಮಾಡಿದರಲ್ಲಾ; ಅವರ ಶತ್ರುಗಳಿಂದ ಅವರನ್ನು ರಕ್ಷಿಸು’” ಎಂದು ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ಲೇವಿಕುಲದ ವಿಷಯದಲ್ಲಿ, “ಯೆಹೋವನೇ ನಿನ್ನ ನಿರ್ಣಯವನ್ನು ತಿಳಿಸುವ ಊರೀಮ್ ಮತ್ತು ತುಮ್ಮೀಮ್ ಎಂಬ ವಸ್ತುಗಳು ನಿನ್ನ ಭಕ್ತರಾದ ಇವರ ವಶದಲ್ಲಿ ಉಂಟಷ್ಟೆ. ನೀನು ಮಸ್ಸದಲ್ಲಿ ಅವರನ್ನು ಪರೀಕ್ಷಿಸಿದಿಯಲ್ಲಾ; ಮೆರೀಬದಲ್ಲಿ ನೀರು ಹೊರಟ ಸ್ಥಳದ ಹತ್ತಿರ ಅವರೊಡನೆ ವಿವಾದಮಾಡಿದಿ.",
			"9": "<sup>9</sup> ಅವರು ನಿನ್ನ ಆಜ್ಞೆಯನ್ನು ಅನುಸರಿಸುತ್ತಾ ನಿನ್ನ ನಿಬಂಧನೆಯನ್ನು ಕೈಕೊಳ್ಳುವವರಾಗಿ ತಾಯಿತಂದೆಗಳನ್ನು ‘ಪರಿಚಯವಿಲ್ಲವೆಂದೂ, ಅಣ್ಣತಮ್ಮಂದಿರನ್ನು ಅರಿಯವೆಂದೂ ಮತ್ತು ಸ್ವಂತ ಮಕ್ಕಳನ್ನು ಗುರುತಿಲ್ಲವೆಂದೂ’ ಹೇಳಿಬಿಟ್ಟರಲ್ಲಾ.",
			"10": "<sup>10</sup> ಅವರು ಯಾಕೋಬ್ ವಂಶದವರಿಗೆ ನಿನ್ನ ನಿರ್ಣಯಗಳನ್ನು ತಿಳಿಸುವರು; ಇಸ್ರಾಯೇಲರಿಗೆ ನಿನ್ನ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಕಲಿಸುವರು. ನಿನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಧೂಪಹಾಕುವರು; ನಿನ್ನ ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಸರ್ವಾಂಗಹೋಮಗಳನ್ನು ಸಮರ್ಪಿಸುವರು.",
			"11": "<sup>11</sup> ಯೆಹೋವನೇ, ಅವರ ಆಸ್ತಿಯನ್ನು ವೃದ್ಧಿಪಡಿಸು; ಅವರ ಸೇವೆಯು ನಿನಗೆ ಸಮರ್ಪಕವಾಗಿರಲಿ; ಅವರಿಗೆ ವಿರುದ್ಧವಾಗಿ ಬರುವವರ ನಡುವನ್ನು ಮುರಿದು ಅವರ ವೈರಿಗಳು ಪುನಃ ಎದ್ದು ಬಾರದಂತೆ ಮಾಡು” ಎಂದು ಹೇಳಿದನು.",
			"12": "<sup>12</sup> ಬೆನ್ಯಾಮೀನ್ ಕುಲದವರ ವಿಷಯದಲ್ಲಿ, “ಯೆಹೋವನು ಇವರ ಬೆಟ್ಟಗಳ ತಪ್ಪಲುಗಳಲ್ಲೇ ವಾಸಿಸುತ್ತಾ ಇವರನ್ನು ಯಾವಾಗಲೂ ಆವರಿಸಿಕೊಂಡಿರುವುದರಿಂದ ಆತನ ಪ್ರಿಯರಾದ ಇವರು ನಿರ್ಭಯವಾಗಿ ವಾಸಿಸುವರು” ಎಂದು ಹೇಳಿದನು.",
			"13": "<sup>13</sup> ಯೋಸೇಫ್ ಕುಲಗಳ ವಿಷಯದಲ್ಲಿ, “ಅವರ ಸೀಮೆಗೆ ಯೆಹೋವನ ಆಶೀರ್ವಾದವಾಗಲಿ. ಮೇಲಣ ಆಕಾಶದಿಂದುಂಟಾಗುವ ಮಂಜು, ಕೆಳಗಣ ಸಾಗರದ ಸೆಲೆ,",
			"14": "<sup>14</sup> ವರುಷ ವರುಷಕ್ಕೂ ತಿಂಗಳು ತಿಂಗಳಿಗೂ ಆಗುವ ಬೆಳೆ,",
			"15": "<sup>15</sup> ಅನಾದಿಯಾದ ಪರ್ವತಗಳ ಉತ್ಪನ್ನ, ಶಾಶ್ವತವಾದ ಬೆಟ್ಟಗಳಿಂದುಂಟಾಗುವ ಮೇಲು,",
			"16": "<sup>16</sup> ಭೂಮಿಯಲ್ಲಿ ಸಮೃದ್ಧಿಯಾಗಿ ಬೆಳೆಯುವ ಫಲ ಇವೆಲ್ಲವುಗಳಲ್ಲಿ ಶ್ರೇಷ್ಠವಾದದ್ದೂ ಮತ್ತು (ಹೋರೇಬ್ ಬೆಟ್ಟದಲ್ಲಿನ) ಪೊದೆಯಲ್ಲಿ ವಾಸಿಸಿದಾತನ ದಯೆಯೂ ಯೋಸೇಫ್ ವಂಶದವರ ಪಾಲಿಗೆ ಬರಲಿ; ಅಣ್ಣತಮ್ಮಂದಿರಿಗೆ ಪ್ರಭುವಾಗಿರುವ ಮತ್ತು ಕುಲದವರ ಶಿರಸ್ಸಾಗಿರುವ ಇವನ ಮೇಲೆ ಆಶೀರ್ವಾದ ಉಂಟಾಗಲಿ.",
			"17": "<sup>17</sup> ಯೋಸೇಫನ ಜ್ಯೇಷ್ಠಸಂತತಿಯವರು ಗೂಳಿಯೋಪಾದಿಯಲ್ಲಿ ಗಾಂಭೀರ್ಯವುಳ್ಳವರು. ಅವರ ಕೊಂಬುಗಳು ಕಾಡುಕೋಣದ ಕೊಂಬುಗಳಷ್ಟು (ಬಲವುಳ್ಳವು); ಅವುಗಳಿಂದ ಭೂಮಂಡಲದ ಜನಾಂಗಗಳನ್ನೆಲ್ಲಾ ಇರಿದು ಓಡಿಸುವರು. ಎಫ್ರಾಯೀಮ್ ಕುಲದ ಕೊಟ್ಯಾಂತರ ಜನರೂ ಮನಸ್ಸೆಕುಲದ ಲಕ್ಷಾಂತರ ಮಂದಿಯೂ ಇಂಥವರೇ” ಎಂದು ಹೇಳಿದನು.",
			"18": "<sup>18</sup> ಜೆಬುಲೂನ್ಯರ ಮತ್ತು ಇಸ್ಸಾಕಾರ್ಯರ ವಿಷಯದಲ್ಲಿ, “ಜೆಬುಲೂನ್ಯರೇ, ನಿಮ್ಮ ಪ್ರಯಾಣಗಳಲ್ಲಿ ಸಂತೋಷವಾಗಿರ್ರಿ; ಇಸ್ಸಾಕಾರ್ಯರೇ, ನಿಮ್ಮ ಪಾಳೆಯಗಳಲ್ಲಿ ಆನಂದವಾಗಿರ್ರಿ.",
			"19": "<sup>19</sup> ಅವರು ಸಮುದ್ರದಿಂದ ಐಶ್ವರ್ಯವನ್ನು ಹೊಂದಿ ಮರಳಿನಲ್ಲಿ, ಭೂಮಿಯೊಳಗೆ ಅಡಗಿರುವ ಸಂಪತ್ತನ್ನು ಪಡೆಯುವರು. ಅನ್ಯಜನಗಳನ್ನು ತಮ್ಮ ಬೆಟ್ಟದ ಸೀಮೆಗೆ ಕರೆಯಿಸಿ ಅಲ್ಲಿ ನ್ಯಾಯವಾದ ಯಜ್ಞಗಳನ್ನು ಸಮರ್ಪಿಸುವರು” ಎಂದು ಹೇಳಿದನು.",
			"20": "<sup>20</sup> ಗಾದ್ ಕುಲದವರ ವಿಷಯದಲ್ಲಿ, “ಗಾದ್ಯರ ಪ್ರದೇಶವನ್ನು ವಿಸ್ತಾರಮಾಡಿದ ಯೆಹೋವನಿಗೆ ಸ್ತೋತ್ರ. ಅವರು ಸಿಂಹದಂತೆ ಹೊಂಚಿಕೊಂಡು (ಶತ್ರುಗಳ) ಭುಜವನ್ನೂ ಅಥವಾ ಶಿರಸ್ಸನ್ನೂ ಮುರಿಯುತ್ತಾರೆ.",
			"21": "<sup>21</sup> ಅವರು ದೇಶದ ಮೊದಲನೆಯ ಭಾಗವನ್ನು ತಮಗೋಸ್ಕರ ತೆಗೆದುಕೊಂಡರು; ಅಲ್ಲಿ ಪ್ರಧಾನನಿಗೆ ಯೋಗ್ಯವಾದ ಸ್ವಾಸ್ತ್ಯವು ದೊರಕಿತು. ಅವರು ಜನಾಧಿಪತಿಗಳ ಜೊತೆಯಲ್ಲಿ ಬಂದು ಇಸ್ರಾಯೇಲರೊಡನೆ ಯೆಹೋವನ ಆಜ್ಞೆಯನ್ನು ನೆರವೇರಿಸಿ ಆತನ ನ್ಯಾಯವನ್ನು ಸ್ಥಾಪಿಸಿದರು” ಎಂದು ಹೇಳಿದನು.",
			"22": "<sup>22</sup> ದಾನ್ ಕುಲದ ವಿಷಯದಲ್ಲಿ, “ದಾನ್ ಕುಲವು ಬಾಷಾನ್ ಸೀಮೆಯಿಂದ ಹೊರಟು ಹಾರಿಬರುವ ಪ್ರಾಯದ ಸಿಂಹದಂತಿದೆ” ಎಂದು ಹೇಳಿದನು.",
			"23": "<sup>23</sup> ನಫ್ತಾಲಿಕುಲದ ವಿಷಯದಲ್ಲಿ, “ನಫ್ತಾಲಿಕುಲವೇ, ನೀನು ಯೆಹೋವನ ದಯೆಯನ್ನು ಹೊಂದಿ ಸಂತೃಪ್ತನಾದೆ; ಆತನ ಆಶೀರ್ವಾದದಿಂದ ನಿನಗೆ ಸಮೃದ್ಧಿಯುಂಟು. (ಕಿನ್ನೆರೆತ್) ಸಮುದ್ರವೂ ದಕ್ಷಿಣಪ್ರದೇಶವೂ ನಿನಗೆ ಸ್ವಾಸ್ತ್ಯವಾಗಲಿ” ಎಂದು ಹೇಳಿದನು.",
			"24": "<sup>24</sup> ಅಶೇರ್ ಕುಲದ ವಿಷಯದಲ್ಲಿ, “ಪುತ್ರಶ್ರೇಷ್ಠರಾದ ಆಶೇರ್ಯರು ಭಾಗ್ಯವನ್ನು ಹೊಂದಲಿ; ಅವರು ಸಹೋದರರ ದಯೆಯನ್ನು ಪಡೆಯಲಿ; ಅವರು ಎಣ್ಣೆಯಲ್ಲೇ ಕಾಲನ್ನು ಅದ್ದಲಿ.",
			"25": "<sup>25</sup> ನಿಮ್ಮ ಕೋಟೆಯ ಬಾಗಿಲುಗಳಿಗೆ ಕಬ್ಬಿಣದ ಮತ್ತು ತಾಮ್ರದ ಅಗುಳಿಗಳು ಇರಲಿ; ನೀವು ಇರುವವರೆಗೂ ನಿಮಗೆ ಬಲವಿರಲಿ” ಎಂದು ಹೇಳಿದನು.",
			"26": "<sup>26</sup> ಯೆಶುರೂನೇ, ನಿಮ್ಮ ದೇವರಿಗೆ ಸಮಾನರು ಯಾರು ಇಲ್ಲ; ಆತನು ಆಕಾಶವನ್ನೇರಿ ಮೇಘಾರೂಢನಾಗಿ ಮಹಾಗಾಂಭೀರ್ಯದಿಂದ ನಿಮ್ಮ ಸಹಾಯಕ್ಕೆ ಬರುವನು.",
			"27": "<sup>27</sup> ಆದಿಯಿಂದಲೂ ದೇವರೇ ನಿಮಗೆ ನಿವಾಸಸ್ಥಾನವಾಗಿದ್ದಾನಲ್ಲಾ; ಸದಾ ದೇವರ ಹಸ್ತವೇ ನಿಮಗೆ ಆಧಾರ. ಆತನು “ನಿಮ್ಮೆದುರಿನಿಂದ ಶತ್ರುಗಳನ್ನು ಹೊರಡಿಸಿ ಅವರನ್ನು ಸಂಹರಿಸಿರಿ” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು;",
			"28": "<sup>28</sup> ಆದಕಾರಣ ಇಸ್ರಾಯೇಲರು ಆಕಾಶದಿಂದ ಮಂಜು ಸುರಿದು, ಧಾನ್ಯವೂ ಮತ್ತು ದ್ರಾಕ್ಷಾರಸವೂ ಸಮೃದ್ಧಿಯಾಗಿರುವ ದೇಶದಲ್ಲೇ ಸೇರಿ ನಿರ್ಭಯವಾಗಿ ವಾಸಿಸುವವರಾದರು, ಯಾಕೋಬನ ವಂಶದವರು ಸುರಕ್ಷಿತರಾದರು.",
			"29": "<sup>29</sup> “ಇಸ್ರಾಯೇಲರೇ, ನೀವು ಎಷ್ಟೋ ಧನ್ಯರು; ನಿಮ್ಮಷ್ಟು ಭಾಗ್ಯವಂತರು ಯಾರಿದ್ದಾರೆ? ನೀವು ಯೆಹೋವನ ಅನುಗ್ರಹದಿಂದ ಜಯವನ್ನು ಹೊಂದಿದವರು. ಆತನೇ ನಿಮ್ಮನ್ನು ಕಾಯುವ ಗುರಾಣಿಯು, ನಿಮ್ಮ ಗೌರವವನ್ನು ಕಾಪಾಡುವ ಕತ್ತಿಯೂ ಆಗಿದ್ದಾನೆ. ಆದದರಿಂದ ನಿಮ್ಮ ಶತ್ರುಗಳು ನಿಮ್ಮ ಮುಂದೆ ಮುದುರಿಕೊಳ್ಳುತ್ತಾರೆ; ನೀವೋ ಅವರಿಗಿದ್ದ ಎತ್ತರವಾದ ಸೀಮೆಯಲ್ಲಿ ಜಯಶಾಲಿಗಳಾಗಿ ಸಂಚರಿಸುತ್ತೀರಿ” ಎಂಬುದೇ."
		},
		"34": {
			"1": "<sup>1</sup> ಮೋಶೆಯು ಮೋವಾಬ್ಯರ ಮೈದಾನದಿಂದ ಯೆರಿಕೋ ಪಟ್ಟಣಕ್ಕೆ ಎದುರಾಗಿರುವ ನೆಬೋ ಪರ್ವತಕ್ಕೆ ಹೋಗಿ ಪಿಸ್ಗಾ ಎಂಬ ಬೆಟ್ಟದ ಶಿಖರವನ್ನು ಹತ್ತಿದನು. ಆಗ ಯೆಹೋವನು ಕಾನಾನ್ ದೇಶವನ್ನೆಲ್ಲಾ ಅಂದರೆ ದಾನ್ ಪಟ್ಟಣದ ವರೆಗೂ ಗಿಲ್ಯಾದ್ ಸೀಮೆ,",
			"2": "<sup>2</sup> ನಫ್ತಾಲಿ ಪ್ರದೇಶ, ಎಫ್ರಾಯೀಮ್ ಮತ್ತು ಮನಸ್ಸೆ ಕುಲಗಳವರ ಸೀಮೆ, ಪಶ್ಚಿಮಸಮುದ್ರದವರೆಗೂ ಯೆಹೂದ ಸೀಮೆ,",
			"3": "<sup>3</sup> ದಕ್ಷಿಣಪ್ರದೇಶ, ಚೋಗರೂರಿನ ತನಕ ಯೆರಿಕೋ ಎಂಬ ಖರ್ಜೂರಗಳ ಪಟ್ಟಣದ ಸುತ್ತಲಿರುವ ಮೈದಾನ ಇದನ್ನೆಲ್ಲಾ ಅವನಿಗೆ ತೋರಿಸಿ,",
			"4": "<sup>4</sup> “ನಾನು ಅಬ್ರಹಾಮ್, ಇಸಾಕ್ ಮತ್ತು ಯಾಕೋಬರಿಗೆ ಪ್ರಮಾಣಮಾಡಿ ಅವರ ಸಂತತಿಯವರಿಗೆ ಕೊಡುವೆನೆಂದು ವಾಗ್ದಾನಮಾಡಿದ ದೇಶವು ಇದೇ; ನಿನಗೆ ಪ್ರತ್ಯಕ್ಷವಾಗಿ ತೋರಿಸಿದ್ದೇನೆ. ಆದರೆ ನೀನು ನದಿ ದಾಟಿ ಅಲ್ಲಿಗೆ ಹೋಗಕೂಡದು” ಎಂದು ಹೇಳಿದನು.",
			"5": "<sup>5</sup> ಯೆಹೋವನ ಮಾತಿನಂತೆ ಆತನ ಸೇವಕನಾದ ಮೋಶೆ ಅಲ್ಲೇ ಮೋವಾಬ್ಯರ ದೇಶದಲ್ಲಿ ಮರಣ ಹೊಂದಿದನು.",
			"6": "<sup>6</sup> ಮೋವಾಬ್ಯರ ದೇಶದಲ್ಲಿ ಬೇತ್-ಪೆಗೋರದ ಎದುರಾಗಿರುವ ಕಣಿವೆಯಲ್ಲಿ (ಯೆಹೋವನು) ಅವನ ದೇಹವನ್ನು ಸಮಾಧಿಮಾಡಿದನು; ಅವನ ಸಮಾಧಿ ಎಲ್ಲಿದೆಯೋ ಇಂದಿನ ವರೆಗೆ ಯಾರಿಗೂ ತಿಳಿಯದು.",
			"7": "<sup>7</sup> ಮೋಶೆ ಸಾಯುವಾಗ ನೂರಿಪ್ಪತ್ತು ವರುಷದವನಾಗಿದ್ದನು. ಅವನ ಕಣ್ಣು ಮೊಬ್ಬಾಗಲಿಲ್ಲ, ಅವನ ಜೀವಕಳೆ ಕುಂದಿಹೋಗಿರಲಿಲ್ಲ.",
			"8": "<sup>8</sup> ಮೋವಾಬ್ಯರ ಮೈದಾನದಲ್ಲಿ ಇಸ್ರಾಯೇಲರು ಮೋಶೆಗೋಸ್ಕರ ಮೂವತ್ತು ದಿನಗಳ ವರೆಗೆ ದುಃಖಿಸಿದರು. ಅಲ್ಲಿಗೆ ಮೋಶೆಯ ಪ್ರಲಾಪದ ದಿನಗಳು ಮುಗಿದವು.",
			"9": "<sup>9</sup> ಮೋಶೆಯು ನೂನನ ಮಗನಾದ ಯೆಹೋಶುವನ ಮೇಲೆ ಕೈಯಿಟ್ಟಿದ್ದರಿಂದ ಅವನು ಜ್ಞಾನವರಸಂಪನ್ನನಾದನು. ಯೆಹೋವನು ಮೋಶೆಗೆ ಕೊಟ್ಟ ಆಜ್ಞೆಗೆ ಅನುಸಾರವಾಗಿ ಇಸ್ರಾಯೇಲರು ಯೆಹೋಶುವನ ಮಾತಿನ ಪ್ರಕಾರ ನಡೆದರು.",
			"10": "<sup>10</sup> ಮೋಶೆಗೆ ಸಮಾನನಾದ ಮತ್ತೊಬ್ಬ ಪ್ರವಾದಿ ಇಸ್ರಾಯೇಲರಲ್ಲಿ ಮತ್ತೆ ಹುಟ್ಟಲೇ ಇಲ್ಲ, ಯೆಹೋವನು ಮೋಶೆಯ ಸಂಗಡ ಮುಖಾಮುಖಿಯಾಗಿ ಮಾತನಾಡುತ್ತಿದ್ದನು.",
			"11": "<sup>11</sup> ಆತನು ಐಗುಪ್ತದೇಶದಲ್ಲಿ ಫರೋಹನ ಮುಂದೆಯೂ, ಅವನ ಪ್ರಜಾಪರಿವಾರದವರ ಮುಂದೆಯೂ ವಿಧವಿಧವಾದ ಅದ್ಭುತಕಾರ್ಯಗಳನ್ನು ಮತ್ತು ಮಹತ್ಕಾರ್ಯಗಳನ್ನು ನಡಿಸುವುದಕ್ಕೆ ಯೆಹೋವನು ಅವನನ್ನು ಕಳುಹಿಸಿದನು.",
			"12": "<sup>12</sup> ಅವನು ಇಸ್ರಾಯೇಲರ ಕಣ್ಣ ಮುಂದೆ ವಿಶೇಷವಾದ ಭುಜಪರಾಕ್ರಮವನ್ನೂ ಮತ್ತು ಆಶ್ಚರ್ಯಕರವಾದ ಕಾರ್ಯಗಳನ್ನೂ ನಡೆಸಿದನು."
		}
	}
};
