var book = {
	"name": "ನ್ಯಾಯಸ್ಥಾಪಕರು",
	"numChapters": 21,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ಯೆಹೋಶುವನು ಮರಣಹೊಂದಿದ ನಂತರ ಇಸ್ರಾಯೇಲ್ಯರು “ಕಾನಾನ್ಯರೊಡನೆ ಯುದ್ಧಮಾಡುವುದಕ್ಕೆ ನಮ್ಮಲ್ಲಿ ಮೊದಲು ಯಾರು ಹೋಗಬೇಕು?” ಎಂದು ಯೆಹೋವನನ್ನು ಕೇಳಲು",
			"2": "<sup>2</sup> ಆತನು ಅವರಿಗೆ “ಯೆಹೂದ್ಯರು ಹೋಗಲಿ; ಇಗೋ, ದೇಶವನ್ನು ಅವರಿಗೆ ಒಪ್ಪಿಸಿದ್ದೇನೆ” ಅಂದನು.",
			"3": "<sup>3</sup> ಆಗ ಯೆಹೂದ್ಯರು ತಮ್ಮ ಸಹೋದರರಾದ ಸಿಮೆಯೋನ್ಯರಿಗೆ, “ಕಾನಾನ್ಯರೊಡನೆ ಯುದ್ಧಮಾಡುವುದಕ್ಕೋಸ್ಕರ ನೀವು ನಮ್ಮ ಸಂಗಡ ನಮ್ಮ ಸ್ವದೀನದಲ್ಲಿರುವ ಭೂಮಿಗೆ ಬನ್ನಿರಿ; ಅನಂತರ ನಾವೂ ನಿಮ್ಮ ಸಂಗಡ ನಿಮ್ಮ ಸ್ವದೀನದಲ್ಲಿರುವ ಭೂಮಿಗೆ ಬರುವೆವು” ಎಂದು ಹೇಳಿದರು.",
			"4": "<sup>4</sup> ಅವರು ಒಪ್ಪಿ ಯೆಹೂದ್ಯರ ಜೊತೆಯಲ್ಲಿ ಯುದ್ಧಕ್ಕೆ ಹೋದರು. ಆಗ ಯೆಹೋವನು ಕಾನಾನ್ಯರನ್ನೂ, ಪೆರಿಜ್ಜೀಯರನ್ನೂ ಅವರ ಕೈಗೆ ಒಪ್ಪಿಸಿದ್ದರಿಂದ ಅವರು ಅವರಲ್ಲಿ ಹತ್ತು ಸಾವಿರ ಮಂದಿಯನ್ನು ಬೆಜೆಕ್ ಎಂಬ ಸ್ಥಳದಲ್ಲಿ ಹತ್ಯೆಮಾಡಿದರು.",
			"5": "<sup>5</sup> ಅವರು ಅಲ್ಲಿ ಅದೋನೀಬೆಜೆಕನನ್ನು ಸಂಧಿಸಿ, ಅವನೊಡನೆ ಯುದ್ಧಮಾಡಿ ಕಾನಾನ್ಯರನ್ನೂ, ಪೆರಿಜ್ಜೀಯರನ್ನೂ ಸೋಲಿಸಿದರು.",
			"6": "<sup>6</sup> ಅದೋನೀಬೆಜೆಕನು ಓಡಿಹೋಗಲು ಅವನನ್ನು ಹಿಂದಟ್ಟಿ ಹಿಡಿದು ಅವನ ಕೈಕಾಲುಗಳ ಹೆಬ್ಬೆರಳುಗಳನ್ನು ಕತ್ತರಿಸಿಬಿಟ್ಟರು.",
			"7": "<sup>7</sup> ಆಗ ಅದೋನೀಬೆಜೆಕನು, “ಕೈ ಕಾಲುಗಳ ಹೆಬ್ಬೆರಳುಗಳನ್ನು ನಾನು ಕತ್ತರಿಸಿಬಿಟ್ಟ ಎಪ್ಪತ್ತು ಮಂದಿ ಅರಸರು ನನ್ನ ಮೇಜಿನ ಕೆಳಗೆ ಬೀಳುವ ಆಹಾರದ ಚೂರುಗಳನ್ನು ಕೂಡಿಸಿಕೊಂಡು ತಿನ್ನುತ್ತಿದ್ದರು. ನಾನು ಅವರಿಗೆ ಮಾಡಿದಂತೆಯೇ ದೇವರು ನನಗೆ ಮಾಡಿದ್ದಾನೆ” ಅಂದನು. ಅವರು ಅವನನ್ನು ಯೆರೂಸಲೇಮಿಗೆ ಕರೆದುಕೊಂಡು ಬರಲು ಅವನು ಅಲ್ಲಿ ಸತ್ತನು.",
			"8": "<sup>8</sup> ಯೆಹೂದ್ಯರು ಯೆರೂಸಲೇಮಿನವರೊಡನೆ ಯುದ್ಧಮಾಡಿ, ಅಲ್ಲಿನ ಜನರನ್ನು ಹಿಡಿದು ಕತ್ತಿಯಿಂದ ಸಂಹರಿಸಿ ಪಟ್ಟಣಕ್ಕೆ ಬೆಂಕಿಯಿಟ್ಟರು.",
			"9": "<sup>9</sup> ತರುವಾಯ ಯೆಹೂದ್ಯರು ಹೋಗಿ ಪರ್ವತಪ್ರದೇಶ, ದಕ್ಷಿಣಸೀಮೆ ಹಾಗೂ ಕಣಿವೆ ಪ್ರದೇಶಗಳಲ್ಲಿದ್ದ ಕಾನಾನ್ಯರೊಡನೆ ಯುದ್ಧಮಾಡಿದರು.",
			"10": "<sup>10</sup> ಅವರು ಮೊದಲು ಕಿರ್ಯತರ್ಬ ಎಂಬ ಹೆಸರಿದ್ದ ಹೆಬ್ರೋನಿಗೆ ಹೋಗಿ ಅಲ್ಲಿದ್ದ ಕಾನಾನ್ಯರ ಮೇಲೆ ಬಿದ್ದು, ಅವರಲ್ಲಿ ಶೇಷೈ, ಅಹೀಮನ್, ತಲ್ಮೈ ಎಂಬುವರನ್ನು ಸೋಲಿಸಿದರು.",
			"11": "<sup>11</sup> ಅಲ್ಲಿಂದ ದೆಬೀರಿನವರಿಗೆ ವಿರೋಧವಾಗಿ ಹೋದರು; ದೆಬೀರಕ್ಕೆ ಮೊದಲು ಕಿರ್ಯತ್ ಸೇಫೆರ್ ಎಂಬ ಹೆಸರಿತ್ತು.",
			"12": "<sup>12</sup> ಕಿರ್ಯತ್ ಸೇಫೆರ ಎಂಬ ಪಟ್ಟಣವನ್ನು ವಶಪಡಿಸಿಕೊಳ್ಳುವವನಿಗೆ ನನ್ನ ಮಗಳಾದ ಅಕ್ಷಾಳನ್ನು ಮದುವೆಮಾಡಿಕೊಡುತ್ತೇನೆಂದು ಕಾಲೇಬನು ಹೇಳಿದನು.",
			"13": "<sup>13</sup> ಅವನ ತಮ್ಮನೂ, ಕೆನಜನ ಮಗನೂ ಆದ ಒತ್ನೀಯೇಲನು ಕಿರ್ಯತ್ ಸೇಫೆರನ್ನು ವಶಪಡಿಸಿಕೊಂಡನು. ಆಗ ಕಾಲೇಬನು ತನ್ನ ಮಗಳಾದ ಅಕ್ಷಾಳನ್ನು ಅವನಿಗೆ ಮದುವೆಮಾಡಿಕೊಟ್ಟನು.",
			"14": "<sup>14</sup> ಆಕೆಯು ಬರುತ್ತಿರುವಾಗ ತನ್ನ ತಂದೆಯ ಹತ್ತಿರ ಹೊಲವನ್ನು ಕೇಳಬೇಕೆಂದು ಗಂಡನನ್ನು ಪ್ರೇರೇಪಿಸಿ ತಾನು ಕತ್ತೆಯಿಂದ ಇಳಿದಳು. ಕಾಲೇಬನು, “ನಿನಗೇನು ಬೇಕು?” ಎಂದು ಆಕೆಯನ್ನು ಕೇಳಲು",
			"15": "<sup>15</sup> ಆಕೆಯು, “ನನಗೊಂದು ದಾನಕೊಡಬೇಕು; ನೀನು ನನ್ನನ್ನು ದಕ್ಷಿಣ ಪ್ರಾಂತ್ಯಕ್ಕೆ ಕೊಟ್ಟುಬಿಟ್ಟಿಯಲ್ಲಾ, ಬುಗ್ಗೆಗಳಿರುವ ಸ್ಥಳವನ್ನು ನನಗೆ ಕೊಡು” ಅಂದಳು. ಆಗ ಅವನು ಆಕೆಗೆ ಮೇಲಣ ಬುಗ್ಗೆ ಮತ್ತು ಕೆಳಗಣ ಪ್ರದೇಶದ ಬುಗ್ಗೆಗಳನ್ನು ಕೊಟ್ಟನು.",
			"16": "<sup>16</sup> ಮೋಶೆಯ ಮಾವನಾದ ಕೇನ್ಯನ ವಂಶದವರು ಖರ್ಜೂರ ನಗರದಿಂದ ಹೊರಟು ಯೆಹೂದ್ಯರ ಜೊತೆಯಲ್ಲಿ ಅರಾದಿನ ದಕ್ಷಿಣದಲ್ಲಿರುವ ಯೆಹೂದ ಅಡವಿಗೆ ಬಂದು ಅಲ್ಲಿನ ಜನರ ಸಂಗಡ ವಾಸಮಾಡಿದರು.",
			"17": "<sup>17</sup> ತರುವಾಯ ಯೆಹೂದ್ಯರು ತಮ್ಮ ಬಂಧುಗಳಾದ ಸಿಮೆಯೋನ್ಯರ ಸಂಗಡ ಹೋಗಿ ಚೆಫತ್ ಎಂಬ ಪಟ್ಟಣದಲ್ಲಿದ್ದ ಕಾನಾನ್ಯರನ್ನು ಸೋಲಿಸಿ, ಆ ಪಟ್ಟಣವನ್ನು ಹಾಳುಮಾಡಿ ಅದನ್ನು ಹೊರ್ಮಾ (ಹೊರ್ಮಾ ಎಂದರೆ ಹಾಳಾದದ್ದು ಎಂದು ಅರ್ಥ) ಎಂದು ಕರೆದರು.",
			"18": "<sup>18</sup> ಅನಂತರ ಅವರು ಗಾಜಾ, ಅಷ್ಕೆಲೋನ್, ಎಕ್ರೋನ್ ಎಂಬ ಪಟ್ಟಣಗಳನ್ನೂ ಅವುಗಳ ಮೇರೆಗಳನ್ನೂ ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡರು.",
			"19": "<sup>19</sup> ಯೆಹೋವನು ಯೆಹೂದ್ಯರ ಸಂಗಡ ಇದ್ದುದರಿಂದ ಅವರು ಪರ್ವತಪ್ರದೇಶಗಳನ್ನೆಲ್ಲಾ ಸ್ವಾಧೀನಪಡಿಸಿಕೊಂಡರು. ಆದರೆ ತಗ್ಗಿನ ಪ್ರದೇಶಗಳಲ್ಲಿ ವಾಸಿಸುವವರಿಗೆ ಕಬ್ಬಿಣದ ರಥಗಳಿದ್ದುದರಿಂದ ಅವರನ್ನು ಹೊರಡಿಸುವುದಕ್ಕೆ ಆಗಲಿಲ್ಲ.",
			"20": "<sup>20</sup> ಮೋಶೆ ಆಜ್ಞಾಪಿಸಿದಂತೆ ಅವರು ಕಾಲೇಬನಿಗೆ ಹೆಬ್ರೋನ್ ಪಟ್ಟಣವನ್ನು ಕೊಟ್ಟರು. ಅವನು ಅನಾಕನ ಮೂರು ಮಂದಿ ಮಕ್ಕಳನ್ನು ಅಲ್ಲಿಂದ ಓಡಿಸಿಬಿಟ್ಟನು.",
			"21": "<sup>21</sup> ಯೆರೂಸಲೇಮಿನಲ್ಲಿದ್ದ ಯೆಬೂಸಿಯರನ್ನು ಬೆನ್ಯಾಮೀನ್ಯರು ಹೊರಡಿಸಿಬಿಡಲಿಲ್ಲ. ಅವರು ಇಂದಿನವರೆಗೂ ಬೆನ್ಯಾಮೀನ್ಯರ ಸಂಗಡ ಯೆರೂಸಲೇಮಿನಲ್ಲಿಯೇ ವಾಸವಾಗಿದ್ದಾರೆ.",
			"22": "<sup>22</sup> ಇವರ ಹಾಗೆಯೇ ಯೋಸೇಫನ ವಂಶದವರೂ ಹೊರಟು ಬೇತೇಲಿಗೆ ಬಂದರು. ಯೆಹೋವನು ಅವರ ಸಂಗಡ ಇದ್ದನು.",
			"23": "<sup>23</sup> ಅವರು ಲೂಜ್ ಎಂದು ಕರೆಯುತ್ತಿದ್ದ ಬೇತೇಲ್ ಊರನ್ನು ಸಂಚರಿಸಿ ನೋಡುವುದಕ್ಕೆ ಗೂಢಚಾರರನ್ನು ಕಳುಹಿಸಿದರು.",
			"24": "<sup>24</sup> ಅವರು ಆ ಊರೊಳಗಿನಿಂದ ಬರುವ ಒಬ್ಬ ಮನುಷ್ಯನನ್ನು ಕಂಡು ಅವನಿಗೆ, “ದಯವಿಟ್ಟು ಪಟ್ಟಣದೊಳಗೆ ನುಗ್ಗಬಹುದಾದ ದಾರಿಯನ್ನು ನಮಗೆ ತೋರಿಸು; ನಾವೂ ನಿನಗೆ ದಯೆತೋರಿಸುವೆವು” ಎಂದು ಹೇಳಲು ಅವನು ಅವರಿಗೆ ಆ ದಾರಿಯನ್ನು ತೋರಿಸಿದನು.",
			"25": "<sup>25</sup> ಆಗ ಅವರು ಆ ಪಟ್ಟಣವನ್ನು ಕತ್ತಿಯಿಂದ ಸಂಹರಿಸಿದರು. ಆದರೆ ಆ ಮನುಷ್ಯನನ್ನೂ ಅವನ ಕುಟುಂಬವನ್ನೂ ಏನು ಮಾಡದೆ ಕಳುಹಿಸಿಬಿಟ್ಟರು.",
			"26": "<sup>26</sup> ಅವನು ಹಿತ್ತಿಯರ ದೇಶಕ್ಕೆ ಹೋಗಿ ಒಂದು ಪಟ್ಟಣವನ್ನು ಕಟ್ಟಿಕೊಂಡು ಅದಕ್ಕೆ ಲೂಜ್ ಎಂದು ಹೆಸರಿಟ್ಟನು; ಅದಕ್ಕೆ ಇಂದಿನವರೆಗೂ ಅದೇ ಹೆಸರಿರುತ್ತದೆ.",
			"27": "<sup>27</sup> ಮನಸ್ಸೆಯವರು ಬೇತ್ ಷೆಯಾನ್, ತಾನಾಕ್, ದೋರ್, ಇಬ್ಲೆಯಾಮ್, ಮೆಗಿದ್ದೋ ಎಂಬ ಪಟ್ಟಣಗಳಲ್ಲಿಯೂ ಅವುಗಳ ಗ್ರಾಮಗಳಲ್ಲಿಯೂ ವಾಸವಾಗಿದ್ದವರನ್ನು ಹೊರಡಿಸಿಬಿಡಲಿಲ್ಲ. ಆದುದರಿಂದ ಕಾನಾನ್ಯರು ಆ ಪ್ರಾಂತಗಳಲ್ಲೇ ವಾಸಿಸುವುದಕ್ಕೆ ದೃಢಮಾಡಿಕೊಂಡರು.",
			"28": "<sup>28</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಬಲಗೊಂಡ ಮೇಲೆ ಅವರನ್ನು ದಾಸತ್ವದಲ್ಲಿ ಇಟ್ಟುಕೊಂಡರೇ ಹೊರತು ಅಲ್ಲಿಂದ ಓಡಿಸಲಿಲ್ಲ.",
			"29": "<sup>29</sup> ಎಫ್ರಾಯಿಮ್ಯರು ಗೆಜೆರಿನಲ್ಲಿದ್ದ ಕಾನಾನ್ಯರನ್ನು ಹೊರಡಿಸಿಬಿಡಲಿಲ್ಲ. ಆದ್ದರಿಂದ ಕಾನಾನ್ಯರು ಎಫ್ರಾಯಿಮ್ಯರ ಮಧ್ಯದಲ್ಲೇ ವಾಸಿಸುವವರಾದರು.",
			"30": "<sup>30</sup> ಜೆಬುಲೂನ್ಯರು ಕಿತ್ರೋನ್, ನಹಲೋಲ್ ಎಂಬ ಪಟ್ಟಣಗಳ ನಿವಾಸಿಗಳನ್ನು ಓಡಿಸಲಿಲ್ಲ. ಇದರಿಂದ ಕಾನಾನ್ಯರು ಅವರ ಮಧ್ಯದಲ್ಲೇ ವಾಸಮಾಡುವವರಾದರು. ಆದರೆ ಜೆಬುಲೂನ್ಯರು ಕಾನಾನ್ಯರನ್ನು ಗುಲಾಮರನ್ನಾಗಿ ಮಾಡಿಕೊಂಡರು.",
			"31": "<sup>31</sup> ಆಶೇರ್ಯರು ಅಕ್ಕೋ, ಚೀದೋನ್, ಅಹ್ಲಾಬ್, ಅಕ್ಜೀಬ್, ಹೆಲ್ಬಾ, ಅಫೀಕ್, ರೆಹೋಬ್ ಎಂಬ ಪಟ್ಟಣಗಳಲ್ಲಿ ವಾಸವಾಗಿದ್ದ ಜನರನ್ನು ಹೊರಡಿಸಿಬಿಡಲಿಲ್ಲ;",
			"32": "<sup>32</sup> ಹೀಗಾಗಿ ಆಶೇರ್ಯರು ಕಾನಾನ್ಯರನ ಮಧ್ಯದಲ್ಲೇ ವಾಸಮಾಡಿದರು.",
			"33": "<sup>33</sup> ನಫ್ತಾಲಿ ಕುಲದವರು ಬೇತ್ ಷೆಮೆಷ್, ಬೇತನಾತ್ ಎಂಬ ಊರುಗಳಲ್ಲಿ ಸ್ವಾದೀನ ಮಾಡಿಕೊಳ್ಳದೆ ಅಲ್ಲಿಯ ನಿವಾಸಿಗಳಾದ ಕಾನಾನ್ಯರ ಮಧ್ಯದಲ್ಲೇ ವಾಸಮಾಡಿದರು ಮತ್ತು ಬೇತ್ ಷೆಮೆಷ್, ಬೇತನಾತ್ ಊರುಗಳ ಜನರು ಅವರಿಗೆ ಗುಲಾಮರಾದರು.",
			"34": "<sup>34</sup> ಇದಲ್ಲದೆ ಅಮೋರಿಯರು ದಾನ್ ಕುಲದವರನ್ನು ತಗ್ಗಿನ ಪ್ರದೇಶಕ್ಕೆ ಇಳಿಯಗೊಡದೆ ಹಿಂದಟ್ಟಿ ಬೆಟ್ಟಗಳಿಗೆ ಓಡಿಸಿಬಿಟ್ಟರು.",
			"35": "<sup>35</sup> ಹೀಗೆ ಅಮೋರಿಯರು ಅಯ್ಯಾಲೋನ್, ಶಲ್ಬೀಮ್ ಎಂಬ ಊರುಗಳ ಬಳಿ ಇದ್ದ ಹರ್ ಹೆರೆಸ್ ಎಂಬ ಬೆಟ್ಟದ ಹತ್ತಿರ ವಾಸಿಸಲು ನಿರ್ಧಾರಮಾಡಿಕೊಂಡರು. ಆದರೆ ಯೋಸೇಫನ ಕುಲದವರು ಅವರನ್ನು ಸೋಲಿಸಿ ಗುಲಾಮರನ್ನಾಗಿ ಮಾಡಿಕೊಂಡರು.",
			"36": "<sup>36</sup> ಅಮೋರಿಯರ ಮೇರೆಯು ಅಕ್ರಬ್ಬೀಮ್ ಮೇಲುದಿಣ್ಣೆಯಿಂದ ಸೇಲಾ ಊರಿನಿಂದ ಮೇಲಕ್ಕೆ ವಿಸ್ತರಿಸಿಕೊಂಡಿದೆ."
		},
		"2": {
			"1": "<sup>1</sup> ಯೆಹೋವನ ದೂತನು ಗಿಲ್ಗಾಲಿನಿಂದ ಬೋಕೀಮಿಗೆ ಬಂದು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ, “ನಾನು ನಿಮ್ಮನ್ನು ಐಗುಪ್ತದಿಂದ ಬರಮಾಡಿ, ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ಪ್ರಮಾಣ ಮಾಡಿದ ದೇಶಕ್ಕೆ ಕರೆತಂದೆನು. ‘ನಿಮ್ಮೊಡನೆ ಮಾಡಿಕೊಂಡ ಒಡಂಬಡಿಕೆಯನ್ನು ಎಂದೂ ಭಂಗಪಡಿಸುವುದಿಲ್ಲವೆಂದು ನಾನು ಮಾತು ಕೊಟ್ಟಾಗ,",
			"2": "<sup>2</sup> ನೀವು ಈ ದೇಶದ ನಿವಾಸಿಗಳ ಸಂಗಡ ಒಡಂಬಡಿಕೆ ಮಾಡಿಕೊಳ್ಳಬಾರದು; ಅವರ ಯಜ್ಞವೇದಿಗಳನ್ನು ಕೆಡವಿಬಿಡಬೇಕು’ ಎಂದು ಆಜ್ಞಾಪಿಸಿದೆನು. ಆದರೆ ನೀವು ನನ್ನ ಮಾತನ್ನು ಕೇಳಲಿಲ್ಲ. ಹೀಗೆ ಯಾಕೆ ಮಾಡಿದಿರಿ?",
			"3": "<sup>3</sup> ‘ನೀವು ಹೀಗೆ ಮಾಡುವುದಾದರೆ ನಾನು ಈ ದೇಶ ನಿವಾಸಿಗಳನ್ನು ನಿಮ್ಮೆದುರಿನಿಂದ ಓಡಿಸುವುದಿಲ್ಲ; ಅವರು ನಿಮ್ಮ ಪಕ್ಕೆಗಳಿಗೆ ಮುಳ್ಳುಗಳಾಗಿರುವರು; ಅವರ ದೇವತೆಗಳು ನಿಮಗೆ ಉರುಲಾಗುವವು’ ಎಂದು ನಾನು ಮೊದಲೇ ಹೇಳಿದ್ದೆನಲ್ಲ” ಅಂದನು.",
			"4": "<sup>4</sup> ಯೆಹೋವನ ದೂತನು ಈ ಮಾತುಗಳನ್ನು ಹೇಳಿದಾಗ ಇಸ್ರಾಯೇಲರೆಲ್ಲರೂ ಬಹಳವಾಗಿ ದುಖಿಸಿ ಗಟ್ಟಿಯಾಗಿ ಅತ್ತರು.",
			"5": "<sup>5</sup> ಮತ್ತು ಆ ಸ್ಥಳಕ್ಕೆ ಬೋಕೀಮೆಂಬ ಹೆಸರಿಟ್ಟು, ಅಲ್ಲಿಯೇ ಯೆಹೋವನಿಗೋಸ್ಕರ ಯಜ್ಞಮಾಡಿದರು.",
			"6": "<sup>6</sup> ಯೆಹೋಶುವನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಕಳುಹಿಸಿದ ಮೇಲೆ ಅವರು ತಮ್ಮ ತಮ್ಮ ಪಾಲಿಗೆ ಬಂದ ಪ್ರದೇಶಗಳಿಗೆ ಹೋಗಿ ಅವುಗಳನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡರು.",
			"7": "<sup>7</sup> ಯೆಹೋಶುವನ ದಿನಗಳಲ್ಲಿಯೂ ಅವನ ಕಾಲದಿಂದ ಇನ್ನೂ ಬದುಕಿದ್ದ, ಯೆಹೋವನು ಇಸ್ರಾಯೇಲರಿಗೋಸ್ಕರ ಮಾಡಿದ ಮಹತ್ಕಾರ್ಯಗಳಿಗೆ ಸಾಕ್ಷಿಗಳಾಗಿದ್ದ ಹಿರಿಯರ ದಿನಗಳಲ್ಲಿಯೂ ಇಸ್ರಾಯೇಲರು ಯೆಹೋವನನ್ನು ಸೇವಿಸುತ್ತಿದ್ದರು.",
			"8": "<sup>8</sup> ಯೆಹೋವನ ಸೇವಕನಾದ ನೂನನ ಮಗ ಯೆಹೋಶುವನು ನೂರ ಹತ್ತು ವರುಷದವನಾಗಿ ಮರಣಹೊಂದಿದನು.",
			"9": "<sup>9</sup> ಅವನನ್ನು ಅವನ ಸ್ವಾದೀನದ ಭೂಮಿಯಾದ ತಿಮ್ನತ್ ಹೆರೆಸ್ ಎಂಬಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಅದು ಎಫ್ರಾಯಿಮ್ ಪರ್ವತಪ್ರದೇಶದಲ್ಲಿರುವ ಗಾಷ್ ಬೆಟ್ಟದ ಉತ್ತರದಿಕ್ಕಿನಲ್ಲಿರುತ್ತದೆ.",
			"10": "<sup>10</sup> ಅವನ ಕಾಲದವರೆಲ್ಲರೂ ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಿದರು. ಆನಂತರ ಯೆಹೋವನನ್ನೂ, ಆತನು ಇಸ್ರಾಯೇಲರಿಗೋಸ್ಕರ ಮಾಡಿದ ಮಹತ್ಕಾರ್ಯಗಳನ್ನೂ ಅರಿಯದಿದ್ದ ಬೇರೊಂದು ಸಂತಾನವು ಹುಟ್ಟಿತು.",
			"11": "<sup>11</sup> ಈ ಇಸ್ರಾಯೇಲರು ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ಕೆಟ್ಟದನ್ನು ಮಾಡಿದರು ಹಾಗೂ ಬಾಳನನ್ನು ಸೇವಿಸಿದರು.",
			"12": "<sup>12</sup> ತಮ್ಮ ಪಿತೃಗಳನ್ನು ಐಗುಪ್ತದಿಂದ ಕರತಂದ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಬಿಟ್ಟು ಅನ್ಯದೇವತೆಗಳನ್ನು ಅಂದರೆ, ತಮ್ಮ ಸುತ್ತಣ ಜನಾಂಗಗಳ ದೇವರುಗಳನ್ನು ಅವಲಂಬಿಸಿ, ಅವುಗಳಿಗೆ ಅಡ್ಡಬಿದ್ದು ಯೆಹೋವನಿಗೆ ಸಿಟ್ಟು ಬರುವಂತೆ ಮಾಡಿದರು.",
			"13": "<sup>13</sup> ಅವರು ಯೆಹೋವನನ್ನು ಬಿಟ್ಟು ಬಾಳ್, ಅಷ್ಟೋರೆತ್ ಎಂಬ ದೇವತೆಗಳನ್ನು ಪೂಜಿಸುವವರಾದರು.",
			"14": "<sup>14</sup> ಆಗ ಯೆಹೋವನ ಕೋಪವು ಅವರ ಮೇಲೆ ಹೆಚ್ಚಾಯಿತು. ಆತನು ಅವರನ್ನು ಸೂರೆಮಾಡುವವರ ಕೈಗೆ ಒಪ್ಪಿಸಲು ಅವರು ಅವರನ್ನು ಸೂರೆಮಾಡಿದರು. ಆತನು ಅವರನ್ನು ಸುತ್ತಣ ವೈರಿಗಳಿಗೆ ಮಾರಿಬಿಟ್ಟನು. ಅವರು ಆ ಶತ್ರುಗಳ ಮುಂದೆ ನಿಲ್ಲಲಾರದವರಾದರು.",
			"15": "<sup>15</sup> ಯೆಹೋವನು ತಾನು ಮೊದಲೇ ಆಣೆಯಿಟ್ಟು ಹೇಳಿದಂತೆ ಇಸ್ರಾಯೇಲರು ಎಲ್ಲಿ ಹೋದರೂ ಆತನ ಕೈ ಅವರಿಗೆ ವಿರೋಧವಾಗಿಯೇ ಇತ್ತು. ಅವರು ಬಹು ಕಷ್ಟಗಳಿಗೆ ಗುರಿಯಾದರು.",
			"16": "<sup>16</sup> ಆಗ ಯೆಹೋವನು ಅವರನ್ನು ಸೂರೆಮಾಡುವವರ ಕೈಯಿಂದ ತಪ್ಪಿಸುವುದಕ್ಕೋಸ್ಕರ ನ್ಯಾಯಸ್ಥಾಪಕರನ್ನು ಕಳುಹಿಸಿದನು.",
			"17": "<sup>17</sup> ಇಸ್ರಾಯೇಲರು ಅವರ ಮಾತನ್ನೂ ಕೇಳದೆ ಅನ್ಯದೇವರುಗಳನ್ನು ಪೂಜಿಸಿ, ಅವುಗಳಿಗೆ ಅಡ್ಡಬಿದ್ದು ದೈವದ್ರೋಹಿಗಳಾದರು. ಯೆಹೋವನ ಆಜ್ಞೆಗಳನ್ನು ಕೈಕೊಳ್ಳುತ್ತಿದ್ದ ತಮ್ಮ ಪಿತೃಗಳ ಮಾರ್ಗವನ್ನು ಬೇಗನೆ ಬಿಟ್ಟುಬಿಟ್ಟರು. ಅವರಂತೆ ನಡೆಯಲೇ ಇಲ್ಲ.",
			"18": "<sup>18</sup> ವೈರಿಗಳ ಹಿಂಸೆಯನ್ನು ತಾಳಲಾರದ ಇಸ್ರಾಯೇಲ್ಯರ ಗೋಳಾಟವನ್ನು ಯೆಹೋವನು ಕೇಳಿ, ಕನಿಕರಪಟ್ಟು, ನ್ಯಾಯಸ್ಥಾಪಕರನ್ನು ಎಬ್ಬಿಸಿ ಜೀವಮಾನವೆಲ್ಲಾ ಅವರ ಸಂಗಡ ಇದ್ದನು. ಅವರ ಮೂಲಕವಾಗಿ ಇಸ್ರಾಯೇಲರನ್ನು ಶತ್ರುಗಳಿಂದ ಬಿಡಿಸಿದನು.",
			"19": "<sup>19</sup> ಅಂಥ ನ್ಯಾಯಸ್ಥಾಪಕರು ತೀರಿಹೋದನಂತರ ಇಸ್ರಾಯೇಲರು ಪುನಃ ತಮ್ಮ ಹಿರಿಯರಿಗಿಂತಲೂ ಭ್ರಷ್ಟರಾಗಿ ಅನ್ಯದೇವತೆಗಳನ್ನು ಅವಲಂಬಿಸಿ, ಸೇವಿಸಿ, ಅವುಗಳಿಗೆ ಅಡ್ಡಬಿದ್ದರು. ಅವರು ತಮ್ಮ ದುರ್ಮಾರ್ಗಗಳನ್ನು, ಮೊಂಡತನಗಳನ್ನು ಬಿಡಲೇ ಇಲ್ಲ.",
			"20": "<sup>20</sup> ಆದ್ದದರಿಂದ ಯೆಹೋವನ ಕೋಪವು ಇಸ್ರಾಯೇಲರ ಮೇಲೆ ಹೆಚ್ಚಾಯಿತು. ಆತನು, “ಈ ಜನರ ಪಿತೃಗಳಿಗೆ ನಾನು ಕೊಟ್ಟ ಒಡಂಬಡಿಕೆಯನ್ನು ಇವರು ಕೈಕೊಳ್ಳಲಿಲ್ಲ; ನನ್ನ ಮಾತಿಗೆ ಕಿವಿಗೊಡಲಿಲ್ಲ.",
			"21": "<sup>21</sup> ಹೀಗಿರುವುದರಿಂದ ಯೆಹೋಶುವನು ಸಾಯುವ ಮೊದಲು ಹೊರಡಿಸದೆ ಬಿಟ್ಟಿದ ಜನಾಂಗಗಳಲ್ಲಿ ನಾನು ಒಂದನ್ನು ಹೊರಡಿಸಿಬಿಡುವುದಿಲ್ಲ.",
			"22": "<sup>22</sup> ಇವರೂ ತಮ್ಮ ಹಿರಿಯರಂತೆ ಜಾಗರೂಕತೆಯಿಂದ ಯೆಹೋವನಾದ ನನ್ನ ಮಾರ್ಗದಲ್ಲಿ ನಡೆಯುವರೋ ಇಲ್ಲವೋ ಎಂದು ಈ ಜನಾಂಗಗಳ ಮೂಲಕವಾಗಿ ಪರೀಕ್ಷಿಸುವೆನು” ಅಂದನು.",
			"23": "<sup>23</sup> ಇದರಿಂದ ಯೆಹೋವನು ಯೆಹೋಶುವನ ಕೈಗೆ ಒಪ್ಪಿಸದೆ ಉಳಿಸಿದ್ದ ಜನಾಂಗಗಳನ್ನು ಬೇಗನೆ ಓಡಿಸಲಿಲ್ಲ."
		},
		"3": {
			"1": "<sup>1</sup> ಕಾನಾನ್ಯರೊಡನೆ ನಡೆದ ಯುದ್ಧಗಳಲ್ಲಿ ಒಂದನ್ನೂ ಅರಿಯದೆ ಇದ್ದ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಪರೀಕ್ಷಿಸುವುದಕ್ಕೂ,",
			"2": "<sup>2</sup> ಇಸ್ರಾಯೇಲ್‍ ಸಂತತಿಯವರಲ್ಲಿ ಯುದ್ಧ ವಿದ್ಯೆಯನ್ನು ಅರಿಯದವರಿಗೆ ಅದನ್ನು ಕಲಿಸಿಕೊಡುವುದಕ್ಕೂ ಯೆಹೋವನು ಉಳಿಸಿದ ಜನಾಂಗಗಳವರು ಯಾರಾರೆಂದರೆ,",
			"3": "<sup>3</sup> ಐದು ಮಂದಿ ಫಿಲಿಷ್ಟಿಯ ಪ್ರಭುಗಳು, ಸರ್ವ ಕಾನಾನ್ಯರು, ಚೀದೋನ್ಯರು, ಲೆಬನೋನ್ ಪರ್ವತಗಳಲ್ಲಿಯೂ ಬಾಳ್ ಹೆರ್ಮೊನ್ ಬೆಟ್ಟದಿಂದ ಹಮಾತಿನ ದಾರಿಯವರೆಗೆ ವಾಸವಾಗಿರುವ ಹಿವ್ವಿಯರು ಇವರೇ.",
			"4": "<sup>4</sup> ತಾನು ಮೋಶೆಯ ಮೂಲಕವಾಗಿ ಇವರ ಪಿತೃಗಳಿಗೆ ವಿಧಿಸಿದ ಆಜ್ಞೆಗಳನ್ನು ಇಸ್ರಾಯೇಲರು ಕೈಕೊಂಡು ನಡೆಯುವರೋ ಇಲ್ಲವೋ ಎಂಬುದನ್ನು ಪರೀಕ್ಷಿಸಿ ತಿಳಿದುಕೊಳ್ಳುವುದಕ್ಕೋಸ್ಕರ ಯೆಹೋವನು ಅವರನ್ನು ಉಳಿಸಿದ್ದನು.",
			"5": "<sup>5</sup> ಇಸ್ರಾಯೇಲರು ಕಾನಾನ್ಯರು, ಹಿತ್ತಿಯರು, ಅಮೋರಿಯರು, ಪೆರಿಜ್ಜೀಯರು, ಹಿವ್ವಿಯರು, ಯೆಬೂಸಿಯರು ಎಂಬೀ ಜನಾಂಗಗಳ ಮಧ್ಯದಲ್ಲಿ ವಾಸಮಾಡುತ್ತಾ,",
			"6": "<sup>6</sup> ಅವರ ಕನ್ಯೆಯರನ್ನು ತಾವು ತಂದು, ತಮ್ಮ ಕನ್ಯೆಯರನ್ನು ಅವರ ಕುಮಾರರಿಗೆ ಕೊಟ್ಟು, ಅವರ ದೇವತೆಗಳನ್ನು ಸೇವಿಸಿದರು.",
			"7": "<sup>7</sup> ಇಸ್ರಾಯೇಲರು ಬಾಳ್, ಆಶೇರ ಎಂಬ ದೇವತೆಗಳನ್ನು ಪೂಜಿಸಿ, ತಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಮರೆತುಬಿಟ್ಟು ಆತನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಗಳಾದರು.",
			"8": "<sup>8</sup> ಆದ್ದರಿಂದ ಆತನು ಇಸ್ರಾಯೇಲ್ಯರ ಮೇಲೆ ಕೋಪಗೊಂಡು, ಅವರನ್ನು ಎರಡು ನದಿಗಳ ಮಧ್ಯದಲ್ಲಿರುವ ಆರಾಮ್ ರಾಜ್ಯದ ಅರಸನಾದ ಕೂಷನ್ ರಿಷಾತಯಿಮ್ ಎಂಬುವವನಿಗೆ ಮಾರಿಬಿಟ್ಟನು. ಅವರು ಎಂಟು ವರುಷಗಳ ವರೆಗೆ ಅವನಿಗೆ ದಾಸರಾಗಿದ್ದರು.",
			"9": "<sup>9</sup> ಇಸ್ರಾಯೇಲರು ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಟ್ಟಾಗ ಆತನು ಅವರನ್ನು ಬಿಡಿಸುವುದಕ್ಕೋಸ್ಕರ ರಕ್ಷಕನನ್ನು ಎಬ್ಬಿಸಿದನು. ಕಾಲೇಬನ ತಮ್ಮನೂ ಕೆನಜನ ಮಗನೂ ಆದ ಒತ್ನೀಯೇಲನೇ ಆ ರಕ್ಷಕನು.",
			"10": "<sup>10</sup> ಯೆಹೋವನ ಆತ್ಮವು ಅವನ ಮೇಲೆ ಬಂದುದರಿಂದ ಅವನು ಇಸ್ರಾಯೇಲರ ನ್ಯಾಯಸ್ಥಾಪನೆಗೋಸ್ಕರ ಯುದ್ಧಕ್ಕೆ ಹೊರಟನು. ಯೆಹೋವನು ಎರಡು ನದಿಗಳ ಮಧ್ಯದಲ್ಲಿರುವ ಆರಾಮ್ ರಾಜ್ಯದ ಅರಸನಾದ ಕೂಷನ್ ರಿಷಾತಯಿಮನನ್ನು ಅವನ ಕೈಗೆ ಒಪ್ಪಿಸಿಕೊಟ್ಟದ್ದರಿಂದ ಅವನು ಅವನನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ಸೋಲಿಸಿಬಿಟ್ಟನು.",
			"11": "<sup>11</sup> ದೇಶದಲ್ಲಿ ನಾಲ್ವತ್ತು ವರುಷಗಳ ಕಾಲ ಸಮಾಧಾನವಿತ್ತು. ತರುವಾಯ ಕೆನಜನ ಮಗನಾದ ಒತ್ನೀಯೇಲನು ಮರಣಹೊಂದಿದನು.",
			"12": "<sup>12</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಪುನಃ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಗಳಾದರು; ಆತನು ಅವರ ದ್ರೋಹದ ನಿಮಿತ್ತ ಮೋವಾಬ್ಯರ ಅರಸನಾದ ಎಗ್ಲೋನನನ್ನು ಅವರಿಗೆ ವಿರೋಧವಾಗಿ ಬಲಪಡಿಸಿದನು.",
			"13": "<sup>13</sup> ಇವನು ಅಮ್ಮೋನಿಯರನ್ನೂ, ಅಮಾಲೇಕ್ಯರನ್ನೂ ಕೂಡಿಸಿಕೊಂಡು ಹೊರಟು ಬಂದು ಇಸ್ರಾಯೇಲರನ್ನು ಸೋಲಿಸಿ ಖರ್ಜೂರನಗರವನ್ನು ಹಿಡಿದನು.",
			"14": "<sup>14</sup> ಇಸ್ರಾಯೇಲರು ಅವನಿಗೆ ಹದಿನೆಂಟು ವರುಷ ದಾಸರಾಗಿದ್ದರು.",
			"15": "<sup>15</sup> ಅವರು ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಡಲು ಆತನು ಅವರನ್ನು ರಕ್ಷಿಸುವುದಕ್ಕೋಸ್ಕರ ಬೆನ್ಯಾಮೀನ್ ಕುಲದ ಗೇರನ ಮಗನಾದ ಏಹೂದನನ್ನು ಎಬ್ಬಿಸಿದನು. ಅವನು ಎಡಚನಾಗಿದ್ದನು. ಇಸ್ರಾಯೇಲರು ಅವನ ಮೂಲಕವಾಗಿ ಮೋವಾಬ್ಯರ ಅರಸನಾದ ಎಗ್ಲೋನನಿಗೆ ಕಪ್ಪ ಕಾಣಿಕೆಯನ್ನು ಕಳುಹಿಸಿದರು.",
			"16": "<sup>16</sup> ಅವನು ತನಗೋಸ್ಕರ ಒಂದು ಗುದ್ದುಮೊಳ (ಒಂದುವರೆ ಅಡಿ) ಉದ್ದವಾದ ಇಬ್ಬಾಯಿಕತ್ತಿಯನ್ನು ಮಾಡಿ ಅದನ್ನು ಬಟ್ಟೆಗಳ ಒಳಗೆ ಬಲಗಡೆಯ ಸೊಂಟಕ್ಕೆ ಕಟ್ಟಿಕೊಂಡು ಹೋಗಿ",
			"17": "<sup>17</sup> ಮೋವಾಬ್ಯರ ಅರಸನಾದ ಎಗ್ಲೋನನಿಗೆ ಕಪ್ಪ ಕಾಣಿಕೆಯನ್ನು ಒಪ್ಪಿಸಿದನು. ಎಗ್ಲೋನನು ಬಹಳ ದಪ್ಪನಾದ ವ್ಯಕ್ತಿ.",
			"18": "<sup>18</sup> ಏಹೂದನು ಕಪ್ಪಕಾಣಿಕೆಯನ್ನು ಒಪ್ಪಿಸಿದ ಮೇಲೆ ಅದನ್ನು ಹೊತ್ತುಕೊಂಡು ಬಂದಿದ್ದ ಆಳುಗಳನ್ನು ಕಳುಹಿಸಿಬಿಟ್ಟು,",
			"19": "<sup>19</sup> ತಾನು ಗಿಲ್ಗಾಲಿನಲ್ಲಿರುವ ವಿಗ್ರಹಸ್ಥಳದಿಂದ ಹಿಂದಿರುಗಿ ಎಗ್ಲೋನನ ಬಳಿಗೆ ಬಂದು, “ಅರಸನೇ, ನಿನಗೆ ತಿಳಿಸತಕ್ಕದ್ದೊಂದು ರಹಸ್ಯವಿದೆ” ಅಂದನು. ಆಗ ಅರಸನು, “ನಿಶ್ಯಬ್ದ” ಅನ್ನಲು ಅವನ ಸೇವಕರೆಲ್ಲರೂ ಹೊರಗೆ ಹೋದರು.",
			"20": "<sup>20</sup> ಅರಸನು ತನ್ನ ತಂಪಾದ ಮೇಲಂತಸ್ತಿನ ಕೋಣೆಯಲ್ಲಿ ಒಬ್ಬನೇ ಕುಳಿತುಕೊಂಡಿದ್ದಾಗ ಏಹೂದನು ಅವನ ಬಳಿಗೆ ಹೋಗಿ, “ನಿನಗೆ ಹೇಳಬೇಕಾದದ್ದೊಂದು ದೈವೋಕ್ತಿಯಿದೆ” ಅನ್ನಲು ಅವನು ತನ್ನ ಸಿಂಹಾಸನದಿಂದ ಎದ್ದನು.",
			"21": "<sup>21</sup> ಆಗ ಏಹೂದನು ಎಡಗೈ ಚಾಚಿ ಬಲಗಡೆಯ ಸೊಂಟಕ್ಕೆ ಕಟ್ಟಿದ್ದ ಕತ್ತಿಯನ್ನು ಹಿರಿದು ಅವನ ಹೊಟ್ಟೆಯಲ್ಲಿ ತಿವಿದನು.",
			"22": "<sup>22</sup> ಆ ಕತ್ತಿ ಹಿಡಿಯ ಸಮೇತ ಹೊಟ್ಟೆಯೊಳಗೆ ಹೊಕ್ಕಿತು. ಅವನು ಕತ್ತಿಯನ್ನು ಹೊರಗೆ ತೆಗೆಯದಿದ್ದುದರಿಂದ ಕೊಬ್ಬು ಕತ್ತಿಯನ್ನು ಸುತ್ತಿಕೊಂಡಿತು; ಅದರ ತುದಿಯು ಬೆನ್ನಿನಿಂದ ಹೊರಗೆ ಬಂದಿತು.",
			"23": "<sup>23</sup> ಏಹೂದನು ಪಡಸಾಲೆಗೆ ಬಂದು, ಆ ಮೇಲುಪ್ಪರಿಗೆಯ ಕದವನ್ನು ಮುಚ್ಚಿ ಬೀಗಹಾಕಿ ಹೊರಟು ಹೋದನು.",
			"24": "<sup>24</sup> ತರುವಾಯ ಸೇವಕರು ಅಲ್ಲಿ ಬಂದು ಬಾಗಲಿಗೆ ಬೀಗಹಾಕಿರುವುದನ್ನು ಕಂಡು, ಅರಸನು ತಂಪಾದ ಕೋಣೆಗೆ ಸೇರಿದ ಪಾಯಖಾನೆಗೆ ಹೋಗಿರಬೇಕು ಅಂದುಕೊಂಡು",
			"25": "<sup>25</sup> ತಮಗೆ ಬೇಸರವಾಗುವ ತನಕ ಕಾಯುತ್ತಾ ಇದ್ದರು. ಆದರೂ ಕದಗಳು ತೆರೆಯಲ್ಪಡದೆ ಇರುವುದನ್ನು ನೋಡಿ ಬೀಗದ ಕೈಯನ್ನು ತೆಗೆದುಕೊಂಡು ಬಾಗಲನ್ನು ತೆರೆಯಲು, ಇಗೋ, ಅವರ ಒಡೆಯನು ಸತ್ತುಬಿದ್ದಿದ್ದನು.",
			"26": "<sup>26</sup> ಅವರು ಅರಸನಿಗಾಗಿ ಕಾಯುತ್ತಿದ್ದ ಸಮಯದಲ್ಲೇ ಏಹೂದನು ತಪ್ಪಿಸಿಕೊಂಡು ವಿಗ್ರಹಗಳಿದ್ದ ಸ್ಥಳದಲ್ಲಿ ಹೊಳೆದಾಟಿ ಸೆಯೀರಾ ಎಂಬಲ್ಲಿಗೆ ಬಂದು,",
			"27": "<sup>27</sup> ಎಫ್ರಾಯಿಮ್ ಪರ್ವತಪ್ರದೇಶದಲ್ಲಿ ಕೊಂಬನ್ನು ಊದಿದನು. ಆಗ ಇಸ್ರಾಯೇಲರು ಅವನ ಬಳಿಗೆ ಬಂದು, ಗಟ್ಟಾ ಇಳಿದು ಅವನ ಸಂಗಡ ಹೊರಟರು.",
			"28": "<sup>28</sup> ಅವನು ಅವರ ನಾಯಕನಾಗಿ, “ನನ್ನನ್ನು ಹಿಂಬಾಲಿಸಿರಿ; ಯೆಹೋವನು ನಿಮ್ಮ ಶತ್ರುಗಳಾದ ಮೋವಾಬ್ಯರನ್ನು ನಿಮ್ಮ ಕೈಗೆ ಒಪ್ಪಿಸಿದ್ದಾನೆ” ಎಂದು ಹೇಳಲು, ಅವರು ಅವನನ್ನು ಹಿಂಬಾಲಿಸಿ ಮೋವಾಬಿಗೆ ಹೋಗುವ ಹಾಯಗಡಗಳನ್ನೆಲ್ಲಾ (ನಿದಿಯಲ್ಲಿ ಹಾದು ಹೋಗಬಹುದಾದ ಸ್ಥಳಗಳು) ಹಿಡಿದರು; ಯಾರನ್ನೂ ದಾಟಗೊಡಲಿಲ್ಲ.",
			"29": "<sup>29</sup> ಅವರು ಆ ಕಾಲದಲ್ಲಿ ಪುಷ್ಟರೂ ಪರಾಕ್ರಮಿಗಳೂ ಆದ ಸುಮಾರು ಹತ್ತು ಸಾವಿರ ಮಂದಿ ಮೋವಾಬ್ಯರಲ್ಲಿ ಒಬ್ಬನೂ ತಪ್ಪಿಸಿಕೊಳ್ಳದಂತೆ ಹತ್ಯೆಮಾಡಿದರು.",
			"30": "<sup>30</sup> ಆ ದಿವಸದಲ್ಲಿ ಮೋವಾಬ್ಯರು ಇಸ್ರಾಯೇಲ್ಯರಿಂದ ತಗ್ಗಿಸಲ್ಪಟ್ಟರು. ದೇಶದಲ್ಲಿ ಎಂಭತ್ತು ವರುಷ ಸಮಾಧಾನವಿತ್ತು.",
			"31": "<sup>31</sup> ಏಹೂದನ ತರುವಾಯ ಆನಾತನ ಮಗನಾದ ಶಮ್ಗರನು ಎದ್ದು ಎತ್ತಿನ ತಿವಿಗೋಲಿನಿಂದ ಆರುನೂರು ಮಂದಿ ಫಿಲಿಷ್ಟಿಯರನ್ನು ಕೊಂದು ಇಸ್ರಾಯೇಲರನ್ನು ರಕ್ಷಿಸಿದನು."
		},
		"4": {
			"1": "<sup>1</sup> ಏಹೂದನು ಮರಣಹೊಂದಿದ ನಂತರ ಇಸ್ರಾಯೇಲ್ಯರು ಪುನಃ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಗಳಾಗಿದ್ದರು.",
			"2": "<sup>2</sup> ಯೆಹೋವನು ಅವರನ್ನು ಹಾಚೋರಿನಲ್ಲಿ ಆಳುತ್ತಿದ್ದ ಕಾನಾನ್ಯ ರಾಜನಾದ ಯಾಬೀನನಿಗೆ ಒಪ್ಪಿಸಿಕೊಟ್ಟನು. ಅನ್ಯಜನರ ಹರೋಷೆತ್ ಎಂಬ ಪಟ್ಟಣದಲ್ಲಿ ವಾಸವಾಗಿದ್ದ ಸೀಸೆರನು ಅವನ ಸೇನಾಧಿಪತಿಯಾಗಿದ್ದನು.",
			"3": "<sup>3</sup> ಒಂಭೈನೂರು ಕಬ್ಬಿಣದ ರಥಗಳುಳ್ಳ ಇವನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಇಪ್ಪತ್ತು ವರುಷಗಳ ಕಾಲ ಕಠಿಣವಾಗಿ ಬಾಧಿಸುತ್ತಿರಲು ಅವರು ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಟ್ಟರು.",
			"4": "<sup>4</sup> ಆ ಕಾಲದಲ್ಲಿ ಲಪ್ಪೀದೋತನ ಹೆಂಡತಿ, ದೆಬೋರಳೆಂಬ ಪ್ರವಾದಿನಿಯು ಇಸ್ರಾಯೇಲರಲ್ಲಿ ನ್ಯಾಯತೀರ್ಪನ್ನು ಮಾಡುತ್ತಿದ್ದಳು.",
			"5": "<sup>5</sup> ಆಕೆ ಎಫ್ರಾಯಿಮ್ ಬೆಟ್ಟದ ಸೀಮೆಯಲ್ಲಿ ರಾಮಕ್ಕೂ ಬೇತೇಲಿಗೂ ಮಧ್ಯದಲ್ಲಿರುವ ದೆಬೋರಳ ಖರ್ಜೂರ ವೃಕ್ಷವೆಂದು ಹೆಸರುಗೊಂಡ ಮರದ ಕೆಳಗೆ ಕುಳಿತುಕೊಂಡಿರುತ್ತಿದ್ದಳು. ಇಸ್ರಾಯೇಲರು ನ್ಯಾಯತೀರ್ಪಿಗಾಗಿ ಆಕೆಯ ಬಳಿಗೆ ಬರುತ್ತಿದ್ದರು.",
			"6": "<sup>6</sup> ಆಕೆ ನಫ್ತಾಲಿ ದೇಶದ ಕೆದೆಷ್ ಊರಿನಲ್ಲಿದ್ದ ಅಬೀನೋವಮನ ಮಗನಾದ ಬಾರಾಕನನ್ನು ಬರಲು ಹೇಳಿ ಅವನಿಗೆ, “ನಿಶ್ಚಯವಾಗಿ ಇಸ್ರಾಯೇಲರ ದೇವರಾದ ಯೆಹೋವನು ನಿನಗೆ, ‘ಎದ್ದು ನಫ್ತಾಲಿ, ಜೆಬುಲೂನ್ ಕುಲಗಳಿಂದ ಹತ್ತು ಸಾವಿರ ಜನರನ್ನು ಕೂಡಿಸಿಕೊಂಡು ತಾಬೋರ್ ಬೆಟ್ಟಕ್ಕೆ ಹೋಗು.",
			"7": "<sup>7</sup> ನಾನು ಯಾಬೀನನ ಸೇನಾಧಿಪತಿಯಾದ ಸೀಸೆರನನ್ನೂ ಅವನ ಸೈನ್ಯರಥಗಳನ್ನೂ ನಿನ್ನ ಬಳಿಗೆ ಕೀಷೋನ್ ಹಳ್ಳಕ್ಕೆ ಎಳೆದುಕೊಂಡು ಬಂದು ನಿನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸುವೆನು ಎಂದು ಆಜ್ಞಾಪಿಸಿದ್ದಾನೆ’” ಅಂದಳು.",
			"8": "<sup>8</sup> ಬಾರಾಕನು ಆಕೆಗೆ, “ನೀನು ನನ್ನ ಸಂಗಡ ಬರುವುದಾದರೆ ಹೋಗುತ್ತೇನೆ; ಇಲ್ಲವಾದರೆ ಹೋಗುವುದಿಲ್ಲ” ಅನ್ನಲು",
			"9": "<sup>9</sup> ಆಕೆಯು, “ನಾನು ಹೇಗೂ ನಿನ್ನ ಸಂಗಡ ಬರುವೆನು; ಆದರೆ ಈ ಯುದ್ಧಪ್ರಯಾಣದಲ್ಲಿ ಉಂಟಾಗುವ ಗೌರವ ನಿನಗೆ ಸಲ್ಲುವುದಿಲ್ಲ. ಯಾಕೆಂದರೆ ಒಬ್ಬ ಸ್ತ್ರೀ ಸೀಸೆರನನ್ನು ಸೋಲಿಸುವಂತೆ ಯೆಹೋವನು ಮಾಡುವನು” ಎಂದು ಹೇಳಿ ದೆಬೋರಳು ಬಾರಾಕನೊಡನೆ ಕೆದೆಷಿಗೆ ಹೋದಳು.",
			"10": "<sup>10</sup> ಬಾರಾಕನು ಜೆಬುಲೂನ್ಯರನ್ನೂ, ನಫ್ತಾಲ್ಯರನ್ನೂ ಕೆದೆಷಿಗೆ ಕರೆಸಿದನು. ಅವರಲ್ಲಿ ಹತ್ತು ಸಾವಿರ ಮಂದಿ ಅವನ ಹೆಜ್ಜೆ ಹಿಡಿದು ಯುದ್ಧಕ್ಕೆ ಹೋದರು, ದೆಬೋರಳೂ ಅವರೊಂದಿಗೆ ಹೋದಳು.",
			"11": "<sup>11</sup> (ಕೇನ್ಯನಾದ) ಹೆಬೆರನು ಉಳಿದ ಕೇನ್ಯರಿಂದ ತನ್ನನ್ನು ಪ್ರತ್ಯೇಕಿಸಿಕೊಂಡನು. ಕೇನ್ಯರು ಮೋಶೆಯ ಮಾವನಾದ ಹೋಬಾಬನ ವಂಶದವರು. ಹೆಬೆರನು ಕೆದೆಷಿನ ಹತ್ತಿರ ಇರುವ ಚಾನನ್ನೀಮೆಂಬ ಊರಿನ ಏಲೋನ್ ವೃಕ್ಷದ ವರೆಗೆ ಬಂದು ಅಲ್ಲಿ ಗುಡಾರ ಹಾಕಿಕೊಂಡಿದ್ದನು.",
			"12": "<sup>12</sup> ಅಬೀನೋವಮನ ಮಗನಾದ ಬಾರಾಕನು ತಾಬೋರ್ ಬೆಟ್ಟವನ್ನೇರಿ ಬಂದಿದ್ದಾನೆಂಬ ವರ್ತಮಾನವು ಸೀಸೆರನಿಗೆ ಮುಟ್ಟಿದಾಗ",
			"13": "<sup>13</sup> ಅವನು ತನ್ನ ಒಂಭೈನೂರು ಕಬ್ಬಿಣದ ರಥಗಳನ್ನೂ, ಎಲ್ಲಾ ಸೈನ್ಯವನ್ನೂ ತೆಗೆದುಕೊಂಡು ಅನ್ಯಜನರ ಹರೋಷತಿನಿಂದ ಕೀಷೋನ್ ಹಳ್ಳಕ್ಕೆ ಬಂದನು.",
			"14": "<sup>14</sup> ಆಗ ದೆಬೋರಳು ಬಾರಾಕನಿಗೆ, “ಏಳು, ಯೆಹೋವನು ಸೀಸೆರನನ್ನು ನಿನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸುವ ದಿನವು ಇಂದೇ. ನಿಶ್ಚಯವಾಗಿ ಆತನು ತಾನೇ ನಿನಗೆ ಮುಂದಾಗಿ ಯುದ್ಧಕ್ಕೆ ಹೊರಡುವನು” ಅನ್ನಲು ಬಾರಾಕನು ಬೇಗನೆ ಹತ್ತು ಸಾವಿರ ಜನರ ಸಹಿತವಾಗಿ ತಾಬೋರ್ ಬೆಟ್ಟದಿಂದ ಇಳಿದನು.",
			"15": "<sup>15</sup> ಯೆಹೋವನು ಸೀಸೆರನನ್ನು ಅವನ ಎಲ್ಲಾ ಸೈನ್ಯರಥಗಳನ್ನೂ ಬಾರಾಕನ ಮುಂದೆ ಗಲಿಬಿಲಿಗೊಳಿಸಿ, ಕತ್ತಿಗೆ ತುತ್ತಾಗಿಸಿದನು. ಸೀಸೆರನು ರಥದಿಂದ ಇಳಿದು ಓಡಿಹೋದನು.",
			"16": "<sup>16</sup> ಬಾರಾಕನು ಅವನ ಸೈನ್ಯರಥಗಳನ್ನು ಅನ್ಯಜನರ ಹರೋಷೆತಿನ ವರೆಗೂ ಹಿಂದಟ್ಟಿದನು. ಸೀಸೆರನ ಸೈನ್ಯದವರೆಲ್ಲರೂ ಕತ್ತಿಯಿಂದ ಹತರಾದರು; ಒಬ್ಬನೂ ಉಳಿಯಲಿಲ್ಲ.",
			"17": "<sup>17</sup> ಹಾಚೋರಿನ ಅರಸನಾದ ಯಾಬೀನನಿಗೂ ಕೇನ್ಯನಾದ ಹೆಬೆರನ ಮನೆಯವರಿಗೂ ಸಮಾಧಾನವಿದ್ದುದರಿಂದ ಸೀಸೆರನು ಕಾಲುನಡಿಗೆಯಿಂದ ಕೇನ್ಯನಾದ ಹೆಬೆರನ ಹೆಂಡತಿ ಯಾಯೇಲಳ ಗುಡಾರದ ಕಡೆಗೆ ಹೋದನು.",
			"18": "<sup>18</sup> ಯಾಯೇಲಳು ಹೊರಗೆ ಹೋಗಿ ಸೀಸೆರನನ್ನು ಎದುರುಗೊಂಡು ಅವನಿಗೆ, “ಸ್ವಾಮೀ, ಒಳಗೆ ಬನ್ನಿರಿ; ಹೆದರಬೇಡಿರಿ, ನಮ್ಮಲ್ಲಿಗೆ ಬನ್ನಿರಿ” ಅನ್ನಲು ಅವನು ಗುಡಾರದೊಳಗೆ ಹೋದನು. ಆಗ ಆಕೆಯು ಅವನನ್ನು ಕಂಬಳಿಯಿಂದ ಮುಚ್ಚಿದಳು.",
			"19": "<sup>19</sup> ಅವನು ಆಕೆಗೆ, “ದಯವಿಟ್ಟು ಸ್ವಲ್ಪ ನೀರನ್ನು ಕೊಡು, ನನಗೆ ಬಹಳ ಬಾಯಾರಿಕೆಯಾಗಿದೆ” ಅಂದನು. ಆಕೆಯು ಬುದ್ದಲಿಯನ್ನು ಬಿಚ್ಚಿ ಹಾಲನ್ನು ಕುಡಿಯಲು ಕೊಟ್ಟು ಅವನನ್ನು ಪುನಃ ಮುಚ್ಚಿದಳು.",
			"20": "<sup>20</sup> ಅವನು ಆಕೆಗೆ, “ನೀನು ಗುಡಾರದ ಬಾಗಲಲ್ಲೇ ನಿಂತಿರು; ಯಾರಾದರೂ ಬಂದು ‘ಇಲ್ಲಿ ಒಬ್ಬ ಮನುಷ್ಯನಿದ್ದಾನೆಯೇ ಎಂದು ವಿಚಾರಿಸಿದರೆ’ ಇಲ್ಲ ಎಂದು ಹೇಳು” ಎಂದು ಹೇಳಿದನು.",
			"21": "<sup>21</sup> ಹೆಬೆರನ ಹೆಂಡತಿಯಾದ ಯಾಯೇಲಳು ಕೈಯಲ್ಲಿ ಗುಡಾರದ ಗೂಟವನ್ನೂ, ಒಂದು ಸುತ್ತಿಗೆಯನ್ನೂ ತೆಗೆದುಕೊಂಡು ಅವನು ಆಯಾಸದಿಂದ ಗಾಢನಿದ್ರೆಯಲ್ಲಿದ್ದಾಗ, ಮೆಲ್ಲಗೆ ಹತ್ತಿರ ಹೋಗಿ, ಅವನ ತಲೆಯಲ್ಲಿ ಆ ಗೂಟವನ್ನು ಹೊಡೆದು ನೆಲಕ್ಕೆ ನಾಟಿಸಿದಳು; ಅವನು ಸತ್ತನು.",
			"22": "<sup>22</sup> ಅದೇ ಕ್ಷಣದಲ್ಲಿ ಸೀಸೆರನನ್ನು ಹಿಂದಟ್ಟುತ್ತಿದ್ದ ಬಾರಾಕನು ಅಲ್ಲಿಗೆ ಬಂದನು. ಯಾಯೇಲಳು ಹೊರಗೆ ಹೋಗಿ ಅವನನ್ನು ಎದುರುಗೊಂಡು, “ಬಾ, ನೀನು ಹುಡುಕುವ ಮನುಷ್ಯನನ್ನು ತೋರಿಸುತ್ತೇನೆ” ಎಂದು ಹೇಳಲು ಅವನು ಒಳಗೆ ಹೋಗಿ ಸೀಸೆರನು ಸತ್ತು ಬಿದ್ದದ್ದನ್ನು ಕಂಡನು. ಅವನ ಕಣ್ತಲೆಯಲ್ಲಿ ಗೂಟವು ಜಡಿದಿತ್ತು.",
			"23": "<sup>23</sup> ಆ ದಿನದಲ್ಲಿ ದೇವರು ಕಾನಾನ್ಯ ರಾಜನಾದ ಯಾಬೀನನನ್ನು ಇಸ್ರಾಯೇಲರ ಮುಂದೆ ತಗ್ಗಿಸಿದನು.",
			"24": "<sup>24</sup> ಇಸ್ರಾಯೇಲರ ಹಸ್ತವು ಹೆಚ್ಚು ಹೆಚ್ಚು ಬಲಗೊಂಡದ್ದರಿಂದ ಕಾನಾನ್ಯರ ಅರಸನಾದ ಯಾಬೀನನು ಪೂರ್ಣವಾಗಿ ಸೋತು ನಿರ್ನಾಮವಾದನು."
		},
		"5": {
			"1": "<sup>1</sup> ಆ ದಿನದಲ್ಲಿ ದೆಬೋರಳೂ ಅಬೀನೋವಮನ ಮಗನಾದ ಬಾರಾಕನೂ ಈ ಗೀತೆಯನ್ನು ಹಾಡಿದರು:",
			"2": "<sup>2</sup> “ಇಸ್ರಾಯೇಲರಲ್ಲಿ ಸೇನಾ ನಾಯಕರು ಎದ್ದಿದ್ದಾರೆ; ಜನರು ಸ್ವಇಚ್ಛೆಯಿಂದ ಸೈನ್ಯದಲ್ಲಿ ಸೇರಿದ್ದಾರೆ, ಯೆಹೋವನನ್ನು ಕೊಂಡಾಡಿರಿ.",
			"3": "<sup>3</sup> “ಅರಸರೇ, ಕೇಳಿರಿ! ಪ್ರಭುಗಳೇ, ಕಿವಿಗೊಡಿರಿ! ನಾನು ಯೆಹೋವನನ್ನು ಕೀರ್ತಿಸುವೆನು; ನಾನು ಇಸ್ರಾಯೇಲ್ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಸ್ತುತಿ ಗೀತೆ ಹಾಡುವೆನು.",
			"4": "<sup>4</sup> “ಯೆಹೋವನೇ, ನೀನು ಹೊರಟು ಎದೋಮ್ಯರ ಪ್ರಾಂತವಾದ ಸೇಯೀರಿನ ಮಾರ್ಗವಾಗಿ ಪಯಣಮಾಡುತ್ತಾ ಬರುವಾಗ ಭೂಲೋಕವು ಕಂಪಿಸಿತು, ಆಕಾಶವು ನಡುಗಿತು; ಮೇಘಮಂಡಲವು ಮಳೆಗರೆಯಿತು.",
			"5": "<sup>5</sup> ಯೆಹೋವನ ಮುಂದೆ ಪರ್ವತಗಳು ಕರಗಿಹೋದವು, ಇಸ್ರಾಯೇಲಿನ ದೇವರಾದ ಯೆಹೋವನ ಎದುರಿಗೆ ಆ ಸೀನಾಯಿಬೆಟ್ಟವೂ ನೀರಾಯಿತು.",
			"6": "<sup>6</sup> “ಅನಾತನ ಮಗನಾದ ಶಮ್ಗರನ ಕಾಲದಲ್ಲಿಯೂ, ಯಾಯೇಲಳ ದಿನಗಳಲ್ಲಿಯೂ ರಾಜಮಾರ್ಗಗಳಲ್ಲಿ ಸಂಚಾರವು ನಿಂತುಹೋಯಿತು. ಪ್ರಯಾಣಿಕರು ಅಂಕುಡೊಂಕಾದ ದಾರಿಹಿಡಿದು ಹೋದರು.",
			"7": "<sup>7</sup> ದೆಬೋರಳಾದ ನಾನು ಇಸ್ರಾಯೇಲರಲ್ಲಿ ತಾಯಿಯಂತೆ ಎದ್ದುಬರುವವರೆಗೆ ಇಸ್ರಾಯೇಲ್ ಗ್ರಾಮಗಳು ಹಾಳುಬಿದ್ದಿದ್ದವು.",
			"8": "<sup>8</sup> ಜನರು ಅನ್ಯದೇವತೆಗಳನ್ನು ಆರಿಸಿಕೊಂಡಿದ್ದರು; ಯುದ್ಧವು ಉರುಬಾಗಿಲವರೆಗೂ ಬಂದಿತ್ತು. ಇಸ್ರಾಯೇಲರ ನಾಲ್ವತ್ತು ಸಾವಿರ ಸೈನಿಕರ ಮಧ್ಯದಲ್ಲಿ ಗುರಾಣಿ ಬರ್ಜಿಗಳು ಇರಲೇ ಇಲ್ಲ.",
			"9": "<sup>9</sup> ನನ್ನ ಹೃದಯವು ಇಸ್ರಾಯೇಲರ ಸೇನಾಧಿಪತಿಗಳಲ್ಲಿಯೂ, ಸ್ವಇಚ್ಛೆಯಿಂದ ಸೈನ್ಯದಲ್ಲಿ ಸೇರಿದ ಜನರಲ್ಲಿಯೂ ಸಂತೋಷಿಸುತ್ತದೆ; ಅವರಿಗೋಸ್ಕರ ಯೆಹೋವನನ್ನು ಕೊಂಡಾಡುತ್ತೇವೆ.",
			"10": "<sup>10</sup> “ಬಿಳೀ ಕತ್ತೆಗಳ ಮೇಲೆ ಸವಾರಿಮಾಡುವವರೇ, ರತ್ನಗಂಬಳಿಗಳ ಮೇಲೆ ಕುಳಿತುಕೊಳ್ಳುವವರೇ, ಕಾಲ್ನಡಿಗೆಯಲ್ಲಿ ಪ್ರಯಾಣಿಸುವವರೇ, ಗಾನಮಾಡಿರಿ.",
			"11": "<sup>11</sup> ಸೇದುವ ಬಾವಿಗಳ ಬಳಿಯಲ್ಲಿ ಕೊಳ್ಳೆಯನ್ನು ಹಂಚಿಕೊಳ್ಳುವವರ ಧ್ವನಿಗಿಂತ ಗಟ್ಟಿಯಾಗಿ ಯೆಹೋವನ ನೀತಿಸಾಧನೆಯ ವರ್ಣನೆಯನ್ನು ಕೇಳಿರಿ. ಆತನು ತನ್ನ ಪ್ರಜೆಗಳಾಗಿರುವ ಇಸ್ರಾಯೇಲ್ ಗ್ರಾಮಸ್ಥರ ನ್ಯಾಯವನ್ನು ಸ್ಥಾಪಿಸಿದ್ದಾನಲ್ಲಾ. ಆ ಕಾಲದಲ್ಲಿ ಯೆಹೋವನ ಜನರು ಊರುಬಾಗಿಲುಗಳಿಗೆ ಇಳಿದು ಬಂದರು.",
			"12": "<sup>12</sup> ‘ಎಚ್ಚರವಾಗು, ದೆಬೋರಳೇ! ಎಚ್ಚರವಾಗು, ಎಚ್ಚರವಾಗು, ಎಚ್ಚರವಾಗಿ ಗಾನಮಾಡು. ಬಾರಾಕನೇ, ಏಳು; ಅಬೀನೋವಮನ ಮಗನೇ, ನೀನು ಸೆರೆಹಿಡಿದವರನ್ನು ಸಾಗಿಸಿಕೊಂಡು ಹೋಗು.’",
			"13": "<sup>13</sup> “ಆಗ ಚದರಿಹೋದವರು ನಾಯಕರ ಬಳಿಗೆ ಕೂಡಿಬಂದರು. ಯೆಹೋವನ ಜನರು ವೀರರಾಗಿ ನನ್ನ ಸಹಾಯಕ್ಕೆ ನೆರೆದು ಬಂದರು.",
			"14": "<sup>14</sup> ಅಮಾಲೇಕ್ಯರ ಪ್ರದೇಶದಲ್ಲಿ ನೆಲೆಗೊಂಡಿರುವ ಎಫ್ರಾಯಿಮ್ಯರೂ ಅವರ ಜೊತೆಯಲ್ಲಿ ಬೆನ್ಯಾಮೀನ್ಯರೂ ಬಂದರು. ಮಾಕೀರನ ಗೋತ್ರದಿಂದ ಪ್ರಧಾನರೂ ಜೆಬುಲೂನ್ ಕುಲದಿಂದ ದಂಡಧಾರಿಗಳಾದ ಸೇನಾನಾಯಕರೂ",
			"15": "<sup>15</sup> ದೆಬೋರಳ ಜೊತೆಯಲ್ಲಿ ಇಸ್ಸಾಕಾರ್ ಕುಲಪ್ರಭುಗಳೂ ಇಸ್ಸಾಕಾರರೊಡನೆ ಬಾರಾಕನೂ ಆಗಮಿಸಿದರು. ಇವರೆಲ್ಲರೂ ಬಾರಾಕನ ಹೆಜ್ಜೆಹಿಡಿದು ತಟ್ಟನೆ ತಗ್ಗಿನ ಪ್ರದೇಶಕ್ಕೆ ಬಂದರು. ರೂಬೇನ್ಯರ ಗೋತ್ರದವರು ಬಹಳಷ್ಟು ತಮ್ಮ ಹೃದಯಗಳಲ್ಲಿ ವಿಶ್ಲೇಷಿಸಿದರೂ ತೀರ್ಮಾನಿಸಲಾಗಲ್ಲಿಲ್ಲ",
			"16": "<sup>16</sup> ರೂಬೇನ್ಯರೇ, ನೀವು ಕುರಿಹಟ್ಟಿಗಳಲ್ಲಿ, ಮಂದೆಗಳ ಮಧ್ಯದಲ್ಲಿ ಕೊಳಲೂದುವುದನ್ನು ಕೇಳಬೇಕೆಂದು ಅಲ್ಲಿಯೇ ಕುಳಿತಿರುವುದೇಕೆ? ರೂಬೇನ್ಯರು ಗೋತ್ರದವರು ಬಹಳಷ್ಟು ತಮ್ಮ ಹೃದಯಗಳಲ್ಲಿ ವಿಶ್ಲೇಷಿಸಿದರೂ ತೀರ್ಮಾನಿಸಲಾಗಲ್ಲಿಲ್ಲ.",
			"17": "<sup>17</sup> ಗಿಲ್ಯಾದ್ಯರು ಯೊರ್ದನಿನ ಆಚೆಯಲ್ಲೇ ಉಳಿದರು. ದಾನ್ ಕುಲದವರು ಹಡಗುಗಳಲ್ಲಿಯೇ ಉಳಿದದ್ದೇಕೆ? ಆಶೇರ್ ಕುಲದವರು ತಮ್ಮ ರೇವುಗಳನ್ನು ಬಿಡದೆ ಸಮುದ್ರ ತೀರದಲ್ಲಿ ಸುಮ್ಮನೆ ಕುಳಿತುಬಿಟ್ಟರು.",
			"18": "<sup>18</sup> ಜೆಬುಲೂನ್ಯರು ತಮ್ಮ ಜೀವವನ್ನು ಮರಣದ ಆಪತ್ತಿಗೆ ಒಪ್ಪಿಸಲು ಸಿದ್ಧರಿದರು, ನಫ್ತಾಲ್ಯರು ಸಹ ರಣರಂಗದಲ್ಲಿಯೇ ಇದ್ದರು.",
			"19": "<sup>19</sup> “ಅರಸರು ಬಂದು ಯುದ್ಧಮಾಡಿದರು; ಕಾನಾನ್ಯ ರಾಜರು ಮೆಗಿದ್ದೋ ನೀರಿನ ಕಾಲುವೆಗಳ ಬಳಿಯಲ್ಲಿರುವ ತಾನಾಕದಲ್ಲಿ ಕೂಡಿಬಂದು ಹೋರಾಡಿದರು. ಅವರಿಗೆ ದ್ರವ್ಯವೇನೂ ಸಿಕ್ಕಲಿಲ್ಲ.",
			"20": "<sup>20</sup> ನಕ್ಷತ್ರಗಳೂ ಆಕಾಶಪಥದಲ್ಲಿದ್ದು ಸೀಸೆರನೊಡನೆ ಯುದ್ಧಮಾಡಿದವು.",
			"21": "<sup>21</sup> ಪೂರ್ವಪ್ರಸಿದ್ಧವಾದ ಕೀಷೋನ್ ಹೊಳೆಯು ಶತ್ರುಗಳನ್ನು ಕೊಚ್ಚಿಕೊಂಡು ಹೋಯಿತು. ನನ್ನ ಮನವೇ ನೀನು ಧೈರ್ಯದಿಂದ ಮುಂದಕ್ಕೆ ಹೊರಡು.",
			"22": "<sup>22</sup> ಆಗ ಅತಿವೇಗವಾಗಿ ಓಡುತ್ತಿದ್ದ ಅವರ ಬಲವಾದ ಕುದುರೆಗಳ ಗೊರಸುಗಳ ಪೆಟ್ಟಿನಿಂದ ನೆಲವು ಕಂಪಿಸಿತು.",
			"23": "<sup>23</sup> ‘ಮೇರೋಜ್’ ಊರನ್ನು ಶಪಿಸಿರೆಂದು ಯೆಹೋವನ ದೂತನು ಹೇಳುತ್ತಾನೆ. ‘ಅದರ ನಿವಾಸಿಗಳು ಯೆಹೋವನ ಸಹಾಯಕ್ಕೆ ಬರಲಿಲ್ಲ. ಯುದ್ಧವೀರರ ಹೋರಾಟದ ವಿರುದ್ಧ ಯೆಹೋವನ ಸಹಾಯಕ್ಕೆ ಬರಲಿಲ್ಲವಲ್ಲಾ; ಅವರನ್ನು ಶಪಿಸೇ ಶಪಿಸಿರಿ!’",
			"24": "<sup>24</sup> “ಎಲ್ಲಾ ಸ್ತ್ರೀಯರಲ್ಲಿ ಕೇನ್ಯನಾದ ಹೆಬೆರನ ಹೆಂಡತಿ ಯಾಯೇಲಳೆ ಭಾಗ್ಯವಂತಳು. ಗುಡಾರಗಳಲ್ಲಿ ವಾಸಿಸುವ ಸ್ತ್ರೀಯರಲ್ಲಿ ಆಕೆಯೇ ಆಶೀರ್ವದಿಸಲ್ಪಟ್ಟವಳು.",
			"25": "<sup>25</sup> ನೀರು ಕೇಳಿದನು, ಹಾಲು ಕೊಟ್ಟಳು; ಉತ್ತಮ ಪಾತ್ರೆಯಲ್ಲಿ ಮಜ್ಜಿಗೆ ಕೊಟ್ಟಳು.",
			"26": "<sup>26</sup> ಕೈಚಾಚಿ ಗೂಟವನ್ನು ತೆಗೆದುಕೊಂಡಳು; ಬಲಗೈಯಲ್ಲಿ ದೊಡ್ಡ ಸುತ್ತಿಗೆಯನ್ನು ಹಿಡಿದಳು; ಆ ಸುತ್ತಿಗೆಯಿಂದ ಸೀಸೆರನ ತಲೆಯನ್ನು ಒಡೆದುಬಿಟ್ಟಳು. ಅವನ ನೆತ್ತಿಗೆ ತಿವಿದು ಜಡಿದಳು.",
			"27": "<sup>27</sup> ಅವನು ಆಕೆಯ ಕಾಲುಗಳ ಬಳಿಯಲ್ಲಿ ಉರುಳಿ ಬಿದ್ದನು; ಅವನು ಆಕೆಯ ಕಾಲುಗಳ ಬಳಿಯಲ್ಲಿ ಮುದುರಿಕೊಂಡು ಉರುಳಿದನು. ಅವನು ಬಿದ್ದಲ್ಲೇ ಸತ್ತನು.",
			"28": "<sup>28</sup> “ಸೀಸೆರನ ತಾಯಿಯು ಕಿಟಿಕಿಯಿಂದ ನೋಡಿದಳು; ಸೀಸೆರನ ತಾಯಿಯು ಕಿಟಿಕಿಯ ಜಾಲರಿಗಳಿಂದ ಕೂಗಿದಳು. ‘ಅವನ ರಥವು ಬರುವುದಕ್ಕೆ ಏಕೆ ಇಷ್ಟುಹೊತ್ತಾಯಿತು! ಅವನ ಕುದುರೆಗಳ ಗೊರಸುಗಳಿಗೆ ಇಷ್ಟು ಸಾವಕಾಶವೇಕೆ’ ಅಂದಳು.",
			"29": "<sup>29</sup> ಬುದ್ಧಿವಂತೆಯರಾದ ಸ್ತ್ರೀಯರು ಕೊಟ್ಟ ಉತ್ತರವನ್ನೇ ಈಕೆಯು ತನ್ನಲ್ಲಿ ತಾನೇ ಅಂದುಕೊಳ್ಳುತ್ತಿದ್ದದ್ದು ಏನೆಂದರೆ,",
			"30": "<sup>30</sup> ‘ನಿಶ್ಚಯವಾಗಿ ಅವರಿಗೆ ಕೊಳ್ಳೆಸಿಕ್ಕಿರುತ್ತದೆ; ಅದನ್ನು ಹಂಚಿಕೊಳ್ಳುತ್ತಿದ್ದಾರಲ್ಲವೋ?. ಪ್ರತಿಯೊಬ್ಬ ಪುರುಷನಿಗೆ ಒಬ್ಬಿಬ್ಬರು ಹೆಂಗಸರೂ, ಸೀಸೆರನಿಗೆ ನಾನಾ ವರ್ಣಗಳುಳ್ಳ ಬಟ್ಟೆಗಳೂ, ವಿಚಿತ್ರವಾಗಿ ಕಸೂತಿಹಾಕಿದ ಎರಡು ವಸ್ತ್ರಗಳೂ, ಕಂಠಮಾಲೆಯೂ ದೊರಕಿರುತ್ತವೆ.’",
			"31": "<sup>31</sup> “ಯೆಹೋವನ ಎಲ್ಲಾ ಶತ್ರುಗಳೂ ಹೀಗೆಯೇ ನಾಶವಾಗಲಿ. ಆತನನ್ನು ಪ್ರೀತಿಸುವ ಭಕ್ತರು ಪ್ರಕಾಶದಿಂದ ಉದಯಿಸುವ ಸೂರ್ಯನಂತಿರಲಿ.” ಅನಂತರ ದೇಶದಲ್ಲಿ ನಲವತ್ತು ವರುಷ ಸಮಾಧಾನವಿತ್ತು."
		},
		"6": {
			"1": "<sup>1</sup> ಇಸ್ರಾಯೇಲರು ತಿರುಗಿ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಗಳಾದ್ದರಿಂದ ಆತನು ಅವರನ್ನು ಏಳು ವರುಷಗಳ ಕಾಲ ಮಿದ್ಯಾನ್ಯರ ಕೈಗೆ ಒಪ್ಪಿಸಿದನು.",
			"2": "<sup>2</sup> ಮಿದ್ಯಾನ್ಯರ ಹಸ್ತವು ಬಲಗೊಂಡಿದ್ದರಿಂದ ಇಸ್ರಾಯೇಲರು ಅವರಿಗೆ ಹೆದರಿ ಪರ್ವತಗಳಲ್ಲಿ ಗುಹೆಗಳನ್ನು, ಕಂದರಗಳನ್ನು ಮಾಡಿಕೊಂಡು ಅದರಲ್ಲಿ ವಾಸಿಸುತ್ತಿದ್ದರು.",
			"3": "<sup>3</sup> ಇವರು ಬಿತ್ತನೆಮಾಡಿದಾಗಲೆಲ್ಲಾ ಮಿದ್ಯಾನ್ಯರೂ ಅಮಾಲೇಕ್ಯರೂ ಮೂಡಣ ದೇಶದವರೂ ಇವರಿಗೆ ವಿರೋಧವಾಗಿ ದಂಡೆತ್ತಿ ಬಂದು ಇಳಿದುಕೊಳ್ಳುತ್ತಿದ್ದರು.",
			"4": "<sup>4</sup> ಅವರು ಗಾಜಾ ಪ್ರಾಂತ್ಯದವರೆಗಿದ್ದ ಎಲ್ಲಾ ಭೂಮಿಯ ಹುಟ್ಟುವಳಿಯನ್ನು ಹಾಳುಮಾಡಿಬಿಡುತ್ತಿದ್ದುದರಿಂದ ಇವರಿಗೆ ದವಸಧಾನ್ಯವಾಗಲಿ, ಕುರಿ, ದನ, ಕತ್ತೆಗಳಾಗಲಿ ಉಳಿಯಲೇ ಇಲ್ಲ.",
			"5": "<sup>5</sup> ಆ ಶತ್ರುಗಳು ತಮ್ಮ ಕುರಿದನ ಗುಡಾರಗಳ ಸಹಿತವಾಗಿ ಮಿಡಿತೆಗಳಂತೆ ಗುಂಪುಗುಂಪಾಗಿ ಬರುತ್ತಿದ್ದರು. ಅವರೂ ಅವರ ಒಂಟೆಗಳೂ ಅಸಂಖ್ಯವಾಗಿದ್ದವು. ಆ ಗುಂಪೆಲ್ಲಾ ಬಂದು ದೇಶವನ್ನು ಹಾಳುಮಾಡುತ್ತಿತ್ತು.",
			"6": "<sup>6</sup> ಇಸ್ರಾಯೇಲರು ಮಿದ್ಯಾನ್ಯರ ದೆಸೆಯಿಂದ ಬಹಳವಾಗಿ ಕುಗ್ಗಿಹೋಗಿ ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಟ್ಟರು.",
			"7": "<sup>7</sup> ಇಸ್ರಾಯೇಲರು ಮಿದ್ಯಾನ್ಯರ ಹಿಂಸೆಯನ್ನು ಸಹಿಸಲಾರದೆ ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಟ್ಟಾಗ",
			"8": "<sup>8</sup> ಆತನು ಅವರ ಬಳಿಗೆ ಒಬ್ಬ ಪ್ರವಾದಿಯನ್ನು ಕಳುಹಿಸಿದನು. ಅವನು ಬಂದು ಅವರಿಗೆ, “ಇಸ್ರಾಯೇಲರ ದೇವರಾದ ಯೆಹೋವನ ಮಾತನ್ನು ಕೇಳಿರಿ; ಆತನು ಹೇಳುವುದೇನೆಂದರೆ ‘ನೀವು ದಾಸತ್ವದಲ್ಲಿದ್ದ ಐಗುಪ್ತದಿಂದ ನಿಮ್ಮನ್ನು ಕರೆತಂದೆನು.",
			"9": "<sup>9</sup> ಐಗುಪ್ತ್ಯರಿಂದ ನಿಮ್ಮನ್ನು ಬಿಡಿಸಿದ್ದಲ್ಲದೆ ಬೇರೆ ಎಲ್ಲಾ ಬಾಧಕರಿಂದಲೂ ನಿಮ್ಮನ್ನು ಬಿಡಿಸಿ, ಅವರನ್ನು ನಿಮ್ಮೆದುರಿನಿಂದ ಓಡಿಸಿಬಿಟ್ಟು, ಅವರ ದೇಶವನ್ನು ನಿಮಗೆ ಕೊಟ್ಟೆನು.",
			"10": "<sup>10</sup> ಯೆಹೋವನೆಂಬ ನಾನೇ ನಿಮ್ಮ ದೇವರಾಗಿದ್ದೇನೆಂದೂ, ನೀವು ಅಮೋರಿಯರ ಮಧ್ಯದಲ್ಲಿ ವಾಸಿಸುತ್ತಿದ್ದಾಗ ಅವರ ದೇವತೆಗಳನ್ನು ಪೂಜಿಸಬಾರದೆಂದೂ ಆಜ್ಞಾಪಿಸಿದ್ದೆನು; ಆದರೆ ನೀವು ನನ್ನ ಮಾತನ್ನು ಕೇಳಲಿಲ್ಲ’” ಅಂದನು.",
			"11": "<sup>11</sup> ಆ ಮೇಲೆ ಯೆಹೋವನ ದೂತನು ಬಂದು ಒಫ್ರದಲ್ಲಿದ್ದ ಏಲಾ ವೃಕ್ಷದ ಕೆಳಗೆ ಕುಳಿತುಕೊಂಡನು. ಅದು ಅಬೀಯೆಜೆರನ ಗೋತ್ರದವನಾದ ಯೋವಾಷನದಾಗಿತ್ತು. ಅವನ ಮಗನಾದ ಗಿದ್ಯೋನನು ಮಿದ್ಯಾನ್ಯರಿಗೆ ಗೊತ್ತಾಗದ ಹಾಗೆ ಅಲ್ಲಿನ ದ್ರಾಕ್ಷೆಯ ಆಲೆಯ ಮರೆಯಲ್ಲಿ ಗೋದಿಯನ್ನು ಬಡಿಯುತ್ತಿದ್ದನು.",
			"12": "<sup>12</sup> ಯೆಹೋವನ ದೂತನು ಗಿದ್ಯೋನನಿಗೆ ಪ್ರತ್ಯಕ್ಷನಾಗಿ ಅವನಿಗೆ, “ಪರಾಕ್ರಮಶಾಲಿಯೇ, ಯೆಹೋವನು ನಿನ್ನ ಸಂಗಡ ಇದ್ದಾನೆ” ಅಂದನು.",
			"13": "<sup>13</sup> ಆಗ ಗಿದ್ಯೋನನು ಅವನಿಗೆ “ಸ್ವಾಮೀ, ಯೆಹೋವನು ನಮ್ಮ ಸಂಗಡ ಇದ್ದರೆ ನಮಗೆ ಇದೆಲ್ಲಾ ಯಾಕೆ ಸಂಭವಿಸೀತು? ನಮ್ಮ ಹಿರಿಯರು, ಯೆಹೋವನು ಅದ್ಭುತಗಳನ್ನು ನಡಿಸಿ ತಮ್ಮನ್ನು ಐಗುಪ್ತದಿಂದ ಬಿಡಿಸಿದನೆಂಬುದಾಗಿ ವಿವರಿಸುತ್ತಿದ್ದರು. ಅಂಥ ಅದ್ಭುತಗಳು ಈಗೆಲ್ಲಿವೆ? ಆತನು ನಮ್ಮನ್ನು ತಿರಸ್ಕರಿಸಿ ಮಿದ್ಯಾನ್ಯರ ಕೈಗೆ ಒಪ್ಪಿಸಿಬಿಟ್ಟಿದ್ದಾನಲ್ಲಾ” ಅಂದನು.",
			"14": "<sup>14</sup> ಆಗ ಯೆಹೋವನು ಅವನನ್ನು ಚೆನ್ನಾಗಿ ನೋಡಿ, “ನಾನು ನಿನ್ನನ್ನು ಕಳುಹಿಸುತ್ತೇನೆ, ಹೋಗು; ನಿನ್ನ ಈ ಬಲದಿಂದ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಮಿದ್ಯಾನ್ಯರಿಂದ ಬಿಡಿಸು” ಎಂದು ಹೇಳಿದನು.",
			"15": "<sup>15</sup> ಆಗ ಗಿದ್ಯೋನನು ಆತನಿಗೆ, “ಕರ್ತನೇ, ನಾನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ರಕ್ಷಿಸುವುದು ಹೇಗೆ? ಮನಸ್ಸೆ ಕುಲದಲ್ಲಿ ನನ್ನ ಮನೆಯು ಕನಿಷ್ಠವಾದದ್ದು; ಮತ್ತು ನಾನು ನಮ್ಮ ಕುಟುಂಬದಲ್ಲಿ ಅಲ್ಪನು” ಅಂದನು.",
			"16": "<sup>16</sup> ಯೆಹೋವನು ಅವನಿಗೆ, “ನಾನು ನಿನ್ನ ಸಂಗಡ ಇರುವುದರಿಂದ ನೀನು ಮಿದ್ಯಾನ್ಯರನ್ನೂ ಒಬ್ಬ ಮನುಷ್ಯನನ್ನೋ ಎಂಬಂತೆ ಸಂಹರಿಸಿಬಿಡುವಿ” ಅಂದನು.",
			"17": "<sup>17</sup> ಅವನು ಆತನಿಗೆ, “ಕರ್ತನೇ, ದಯವಿರಲಿ; ನನ್ನೊಂದಿಗೆ ಮಾತನಾಡುತ್ತಿರುವವರು ತಾವೇ ಆಗಿದ್ದೀರೆಂಬುದಕ್ಕೆ ನನಗೊಂದು ಗುರುತನ್ನು ಅನುಗ್ರಹಿಸಬೇಕು.",
			"18": "<sup>18</sup> ನಾನು ಹೋಗಿ ತಮಗೆ ಕಾಣಿಕೆಯನ್ನು ತೆಗೆದುಕೊಂಡು ಬರುವ ವರೆಗೆ ತಾವು ಈ ಸ್ಥಳವನ್ನು ಬಿಡಬಾರದು” ಎಂದು ಬಿನ್ನವಿಸಲು ಆತನು, “ನೀನು ತಿರುಗಿ ಬರುವ ವರೆಗೆ ನಾನು ಇಲ್ಲೇ ಇರುವೆನು” ಅಂದನು.",
			"19": "<sup>19</sup> ಗಿದ್ಯೋನನು ಮನೆಗೆ ಹೋಗಿ ಒಂದು ಆಡಿನ ಮರಿಯನ್ನು ತೆಗೆದುಕೊಂಡು, ಒಂದು ಏಫಾ ಅಂದರೆ ಸುಮಾರು ಮುವ್ವತ್ತು ಸೇರು ಹಿಟ್ಟಿನಿಂದ ಹುಳಿಯಿಲ್ಲದ ರೊಟ್ಟಿಗಳನ್ನು ಸುಟ್ಟು, ಮಾಂಸವನ್ನು ಸಿದ್ಧಮಾಡಿ, ಪುಟ್ಟಿಯಲ್ಲಿಟ್ಟು ಅದರ ರಸವನ್ನು ಬಟ್ಟಲಿನಲ್ಲಿ ಹೊಯ್ದು, ಎಲ್ಲವನ್ನೂ ಏಲಾ ಮರದ ಕೆಳಗೆ ಆತನ ಮುಂದೆ ತಂದಿಟ್ಟನು.",
			"20": "<sup>20</sup> ಆಗ ದೇವದೂತನು ಅವನಿಗೆ, “ಮಾಂಸವನ್ನೂ, ರೊಟ್ಟಿಗಳನ್ನೂ ಆ ಬಂಡೆಯ ಮೇಲಿಟ್ಟು ರಸವನ್ನು ಅದರ ಮೇಲೆ ಹೊಯ್ಯಿ” ಅನ್ನಲು ಅವನು ಹಾಗೆಯೇ ಮಾಡಿದನು.",
			"21": "<sup>21</sup> ಅನಂತರ ಯೆಹೋವನ ದೂತನು ತನ್ನ ಕೈಕೋಲನ್ನು ಚಾಚಿ ಅದರ ತುದಿಯನ್ನು ಆ ರೊಟ್ಟಿಗಳಿಗೂ, ಮಾಂಸಕ್ಕೂ ಮುಟ್ಟಿಸಲು ಬಂಡೆಯಿಂದ ಬೆಂಕಿಯೆದ್ದು ಅವೆರಡನ್ನೂ ದಹಿಸಿಬಿಟ್ಟಿತು; ಯೆಹೋವನ ದೂತನು ಅದೃಶ್ಯನಾದನು.",
			"22": "<sup>22</sup> ಆತನು ಯೆಹೋವನ ದೂತನೆಂದು ಗಿದ್ಯೋನನು ತಿಳಿದು ಭಯಪಟ್ಟು, “ಅಯ್ಯೋ ಕರ್ತನೇ, ಯೆಹೋವನೇ, ಯೆಹೋವನ ದೂತನನ್ನು ಮುಖಾಮುಖಿಯಾಗಿ ನೋಡಿಬಿಟ್ಟೆನಲ್ಲಾ” ಎಂದು ಕೂಗಿದನು.",
			"23": "<sup>23</sup> ಆದರೆ ಯೆಹೋವನು ಅವನಿಗೆ, “ಸಮಾಧಾನದಿಂದಿರು, ಹೆದರಬೇಡ; ನೀನು ಸಾಯುವುದಿಲ್ಲ” ಅಂದನು.",
			"24": "<sup>24</sup> ಗಿದ್ಯೋನನು ಅಲ್ಲಿ ಯೆಹೋವನಿಗೋಸ್ಕರ ಒಂದು ಯಜ್ಞವೇದಿಯನ್ನು ಕಟ್ಟಿ ಅದಕ್ಕೆ ‘ಯೆಹೋವ ಷಾಲೋಮ್’ ಎಂದು ಹೆಸರಿಟ್ಟನು; ಅದು ಈ ವರೆಗೂ ಅಬೀಯೆಜೆರನ ಗೋತ್ರದವರ ಒಫ್ರದಲ್ಲಿರುತ್ತದೆ.",
			"25": "<sup>25</sup> ಅದೇ ದಿನ ರಾತ್ರಿಯಲ್ಲಿ ಯೆಹೋವನು ಅವನಿಗೆ, “ನೀನು ನಿನ್ನ ತಂದೆಯ ಹೋರಿಗಳಲ್ಲಿ ಒಂದು ಚಿಕ್ಕ ಹೋರಿಯನ್ನೂ, ಏಳು ವರುಷದ ಇನ್ನೊಂದು ಹೋರಿಯನ್ನೂ ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ನಿನ್ನ ತಂದೆಯು ಕಟ್ಟಿರುವ ಬಾಳನ ಯಜ್ಞವೇದಿಯನ್ನು ಕೆಡವಿ, ಅದರ ಬಳಿಯಲ್ಲಿರುವ ಅಶೇರವೆಂಬ ವಿಗ್ರಹಸ್ತಂಭವನ್ನು ಕಡಿದುಹಾಕು.",
			"26": "<sup>26</sup> ಈ ಗುಡ್ದದ ಶಿಖರದಲ್ಲಿ ಯೆಹೋವನಿಗೋಸ್ಕರ ನೇಮಕವಾದ ರೀತಿಯಲ್ಲಿ ಒಂದು ಯಜ್ಞವೇದಿಯನ್ನು ಕಟ್ಟಿಸು. ನೀನು ಕಡಿದು ಹಾಕಿದ ಅಶೇರ ವಿಗ್ರಹಸ್ತಂಭದಿಂದ ಬೆಂಕಿಮಾಡಿ, ಆ ಎರಡನೆಯ ಹೋರಿಯನ್ನೂ ಸರ್ವಾಂಗಹೋಮವಾಗಿ ಸಮರ್ಪಿಸು” ಎಂದು ಹೇಳಿದನು.",
			"27": "<sup>27</sup> ಆಗ ಗಿದ್ಯೋನನು ತನ್ನ ಸೇವಕರಲ್ಲಿ ಹತ್ತು ಮಂದಿಯನ್ನು ಕರೆದುಕೊಂಡು ಹೋಗಿ ಯೆಹೋವನು ಹೇಳಿದಂತೆಯೇ ಮಾಡಿದನು. ಆದರೆ ಅವನು ತನ್ನ ಮನೆಯವರಿಗೂ, ಊರಿನವರಿಗೂ ಹೆದರಿಕೊಂಡಿದ್ದರಿಂದ ಅದನ್ನು ಹಗಲಿನಲ್ಲಿ ಮಾಡದೆ ರಾತ್ರಿಯಲ್ಲಿ ಮಾಡಿದನು.",
			"28": "<sup>28</sup> ಊರಿನ ಜನರು ಹೊತ್ತಾರೆಯಲ್ಲೆದ್ದು ಬಾಳನ ಯಜ್ಞವೇದಿಯು ಕೆಡವಲ್ಪಟ್ಟಿರುವುದನ್ನೂ, ಅದರ ಸಮೀಪದಲ್ಲಿದ್ದ ವಿಗ್ರಹಸ್ತಂಭವು ಕಡಿಯಲ್ಪಟ್ಟುರುವುದನ್ನೂ, ಹೊಸದಾಗಿ ಕಟ್ಟಿದ ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಎರಡನೆಯ ಹೋರಿಯು ಯಜ್ಞವಾದದ್ದನ್ನೂ ಕಂಡು,",
			"29": "<sup>29</sup> “ಹೀಗೆ ಮಾಡಿದವರಾರು?” ಎಂದು ಒಬ್ಬರನ್ನೊಬ್ಬರು ವಿಚಾರಿಸುವಲ್ಲಿ ಯೋವಾಷನ ಮಗನಾದ ಗಿದ್ಯೋನನೇ ಎಂದು ತಿಳಿದು ಬಂದಿತು.",
			"30": "<sup>30</sup> ಆ ಜನರು ಯೋವಾಷನಿಗೆ, “ನಿನ್ನ ಮಗನನ್ನು ಹೊರಗೆ ಕರೆದುಕೊಂಡು ಬಾ; ಅವನು ಸಾಯಬೇಕು; ಯಾಕೆಂದರೆ ಬಾಳನ ಯಜ್ಞವೇದಿಯನ್ನು ಕೆಡವಿ ಅದರ ಬಳಿಯಲ್ಲಿದ್ದ ಅಶೇರ ವಿಗ್ರಹಸ್ತಂಭವನ್ನು ಕಡಿದುಹಾಕಿದ್ದಾನೆ” ಅಂದರು.",
			"31": "<sup>31</sup> ಯೋವಾಷನು ತನಗೆ ವಿರೋಧವಾಗಿ ನಿಂತವರೆಲ್ಲರಿಗೆ, “ಬಾಳನಿಗೋಸ್ಕರ ನೀವು ವ್ಯಾಜ್ಯಮಾಡಬೇಕೋ? ನೀವು ಅವನನ್ನು ರಕ್ಷಿಸಬೇಕೋ? ಅವನಿಗಾಗಿ ವ್ಯಾಜ್ಯಮಾಡುವವರು ಈ ಹೊತ್ತೇ ಕೊಲ್ಲಲ್ಪಡಲಿ. ಬಾಳನು ದೇವನಾಗಿದ್ದರೆ ತನ್ನ ಯಜ್ಞವೇದಿಯನ್ನು ಕೆಡವಿಬಿಟ್ಟದ್ದಕ್ಕಾಗಿ ತಾನೇ ವ್ಯಾಜ್ಯಮಾಡಲಿ” ಎಂದನು.",
			"32": "<sup>32</sup> ಆ ದಿನದಲ್ಲಿ ಅವನು, “ಬಾಳನು ತನ್ನ ಯಜ್ಞವೇದಿಯನ್ನು ಕೆಡವಿಬಿಟ್ಟವನೊಡನೆ ತಾನೇ ವ್ಯಾಜ್ಯಮಾಡಲಿ” ಎಂದು ಹೇಳಿದ್ದರಿಂದ ಗಿದ್ಯೋನನಿಗೆ ‘ಯೆರುಬ್ಬಾಳ’ ಎಂಬ ಹೆಸರಾಯಿತು.",
			"33": "<sup>33</sup> ಇಷ್ಟರಲ್ಲಿ ಮಿದ್ಯಾನ್ಯರೂ, ಅಮಾಲೇಕ್ಯರೂ, ಮೂಡಣದೇಶದವರೂ ಸೇರಿ ಹೊಳೆದಾಟಿ ಬಂದು ಇಜ್ರೇಲಿನ ತಗ್ಗಿನಲ್ಲಿ ಪಾಳೆಯಮಾಡಿಕೊಂಡರು.",
			"34": "<sup>34</sup> ಆಗ ಗಿದ್ಯೋನನ ಮೇಲೆ ಯೆಹೋವನ ಆತ್ಮವು ಬಂದಿತ್ತು. ಅವನು ಕೊಂಬನ್ನು ಊದಲು ಅಬೀಯೆಜೆರನ ಗೋತ್ರದವರು ಬಂದು ಅವನನ್ನು ಹಿಂಬಾಲಿಸಿದರು.",
			"35": "<sup>35</sup> ಅವನು ಉಳಿದ ಮನಸ್ಸೆಯವರ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಲಾಗಿ ಅವರೂ ಕೂಡಿಬಂದರು. ಆಶೇರ್, ಜೆಬುಲೂನ್, ನಫ್ತಾಲಿ ಕುಲಗಳವರ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಲು ಅವರೂ ಬಂದು ಯುದ್ಧಪ್ರಯಾಣಕ್ಕೋಸ್ಕರ ಸೇರಿಕೊಂಡರು.",
			"36": "<sup>36</sup> ಮತ್ತು ಗಿದ್ಯೋನನು ದೇವರಿಗೆ, “ನೀನು ಹೇಳಿದಂತೆ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ನನ್ನ ಮೂಲಕವಾಗಿ ರಕ್ಷಿಸುವಿಯೋ ಇಲ್ಲವೋ ಎಂಬುದನ್ನು ತಿಳಿಯಪಡಿಸು.",
			"37": "<sup>37</sup> ಇಗೋ, ನಾನು ಕಣದಲ್ಲಿ ಕುರಿಯ ಉಣ್ಣೆಯನ್ನು ಇಡುತ್ತೇನೆ; ಅದರಲ್ಲಿ ಮಾತ್ರ ಮಂಜುಬಿದ್ದು ನೆಲವೆಲ್ಲಾ ಒಣಗಿದ್ದರೆ ನೀನು ಹೇಳಿದಂತೆಯೇ ಇಸ್ರಾಯೇಲರನ್ನು ನನ್ನ ಮೂಲಕವಾಗಿ ರಕ್ಷಿಸುವಿಯೆಂದು ತಿಳಿಯುವೆನು” ಎಂಬದಾಗಿ ವಿಜ್ಞಾಪಿಸಿದನು.",
			"38": "<sup>38</sup> ಹಾಗೆಯೇ ಆಯಿತು; ಅವನು ಮರುದಿನ ಹೊತ್ತಾರೆಯಲ್ಲಿ ಎದ್ದು ಉಣ್ಣೆಯನ್ನು ಒತ್ತಿ ಹಿಂಡಲು ಮಂಜಿನ ನೀರು ಒಂದು ಬಟ್ಟಲು ತುಂಬಾ ಆಯಿತು.",
			"39": "<sup>39</sup> ಅವನು ತಿರುಗಿ ದೇವರಿಗೆ, “ಸ್ವಾಮೀ, ಸಿಟ್ಟಾಗಬಾರದು; ಇನ್ನೊಂದು ಸಾರಿ ಮಾತನಾಡುತ್ತೇನೆ. ಇನ್ನು ಒಂದೇ ಸಾರಿ ಈ ಉಣ್ಣೆಯಿಂದ ನಿನ್ನನ್ನು ಪರೀಕ್ಷಿಸುವುದಕ್ಕೆ ಅಪ್ಪಣೆಯಾಗಲಿ; ಈ ಉಣ್ಣೆ ಮಾತ್ರವೇ ಒಣಗಿದ್ದು ನೆಲದ ಮೇಲೆಲ್ಲಾ ಹನಿಬಿದ್ದಿರಲಿ” ಅಂದನು.",
			"40": "<sup>40</sup> ಆ ರಾತ್ರಿ ದೇವರು ಹಾಗೆಯೇ ಮಾಡಿದನು; ಉಣ್ಣೆ ಮಾತ್ರ ಒಣಗಿತ್ತು, ನೆಲದ ಮೇಲೆಲ್ಲಾ ಹನಿ ಬಿದ್ದಿತ್ತು."
		},
		"7": {
			"1": "<sup>1</sup> ಆನಂತರ ಯೆರುಬ್ಬಾಳನೆನಿಸಿಕೊಳ್ಳುವ ಗಿದ್ಯೋನನೂ, ಅವನ ಸಂಗಡ ಇದ್ದ ಜನರೂ ಬೆಳಿಗ್ಗೆ ಎದ್ದು ಹೊರಟು ಹೋಗಿ ಹರೋದಿನ ಬುಗ್ಗೆಯ ಬಳಿಯಲ್ಲಿ ಬಿಡಾರ ಹಾಕಿಕೊಂಡರು. ಇವರ ಉತ್ತರದಿಕ್ಕಿನಲ್ಲಿ ಮೋರೆಗುಡ್ಡದ ಹಿಂದಿನ ತಗ್ಗಿನಲ್ಲಿ ಮಿದ್ಯಾನ್ಯರ ದಂಡಿಳಿದಿತ್ತು.",
			"2": "<sup>2</sup> ಆಗ ಯೆಹೋವನು ಗಿದ್ಯೋನನಿಗೆ, “ನಿನ್ನ ಸಂಗಡ ಇರುವ ಜನರು ಹೆಚ್ಚಾಗಿದ್ದಾರೆ; ಇವರ ಕೈಗೆ ಮಿದ್ಯಾನ್ಯರನ್ನು ಒಪ್ಪಿಸುವುದು ನನಗೆ ಸರಿಯಾಗಿ ಕಾಣುವುದಿಲ್ಲ. ಒಪ್ಪಿಸಿಕೊಟ್ಟರೆ ಸ್ವಹಸ್ತದಿಂದಲೇ ನಮಗೆ ರಕ್ಷಣೆಯುಂಟಾಯಿತೆಂದು ಹೆಚ್ಚಳಪಟ್ಟು ನನ್ನನ್ನು ಅಲಕ್ಷ್ಯಮಾಡಾರು.",
			"3": "<sup>3</sup> ಆದುದರಿಂದ ಎಲ್ಲಾ ಜನರಿಗೆ ಕೇಳಿಸುವಂತೆ, ‘ಧೈರ್ಯವಿಲ್ಲದವರೂ, ಅಂಜುವವರೂ ಈ ಗಿಲ್ಯಾದ್ ಪರ್ವತವನ್ನು ಬಿಟ್ಟು ಮನೆಗೆ ಹೋಗಲಿ ಎಂದು ಪ್ರಕಟಿಸು’” ಅಂದನು. ಹಾಗೆ ಮಾಡಲು ಇಪ್ಪತ್ತೆರಡು ಸಾವಿರ ಮಂದಿ ಹಿಂದಿರುಗಿ ಹೋದರು; ಹತ್ತು ಸಾವಿರ ಮಂದಿ ಉಳಿದರು.",
			"4": "<sup>4</sup> ಯೆಹೋವನು ತಿರುಗಿ ಗಿದ್ಯೋನನಿಗೆ, “ನಿನ್ನ ಬಳಿಯಲ್ಲಿರುವ ಜನರು ಇನ್ನೂ ಹೆಚ್ಚಾಗಿದ್ದಾರೆ; ನೀನು ಅವರನ್ನು ಹಳ್ಳಕ್ಕೆ ಕರೆದುಕೊಂಡು ಹೋಗು; ನಾನು, ಅಲ್ಲಿ ಈ ಜನರ ಸಂಖ್ಯೆಯನ್ನು ಕಡಿಮೆಮಾಡಿ ನಿನಗೆ ಕೊಡುವೆನು. ಯಾರು ನಿನ್ನ ಸಂಗಡ ಹೋಗಬಹುದೆಂದು ಹೇಳುವೆನೋ ಅವರು ಮಾತ್ರ ಹೋಗಲಿ; ಯಾರಿಗೆ ಹೋಗಬಾರದೆಂದು ಹೇಳುವೆನೋ ಅವರು ಹೋಗದಿರಲಿ” ಅಂದನು.",
			"5": "<sup>5</sup> ಗಿದ್ಯೋನನು ಜನರನ್ನು ಹಳ್ಳಕ್ಕೆ ಕರೆದುಕೊಂಡು ಬಂದಾಗ ಯೆಹೋವನು ಅವನಿಗೆ, “ನಾಯಿಯಂತೆ ನೀರನ್ನು ನಾಲಿಗೆಯಿಂದ ನೆಕ್ಕುವವರನ್ನೂ, ಮೊಣಕಾಲೂರಿ ಕುಡಿಯುವವರನ್ನೂ ಬೇರೆ ಬೇರೆಯಾಗಿ ನಿಲ್ಲಿಸು” ಎಂದು ಹೇಳಿದನು.",
			"6": "<sup>6</sup> ಕೈಯಿಂದ ನೀರನ್ನು ಬಾಯಿಗೆ ತೆಗೆದುಕೊಂಡು ನೆಕ್ಕಿಕುಡಿದವರ ಸಂಖ್ಯೆ ಮುನ್ನೂರು. ಇತರ ಜನರು ಮೊಣಕಾಲೂರಿ ಕುಡಿದವರು.",
			"7": "<sup>7</sup> ಆಗ ಯೆಹೋವನು ಗಿದ್ಯೋನನಿಗೆ, “ನೀರನ್ನು ನೆಕ್ಕಿಕುಡಿದ ಆ ಮುನ್ನೂರು ಜನರಿಂದಲೇ ನಿಮಗೆ ಜಯವನ್ನುಂಟುಮಾಡಿ ಮಿದ್ಯಾನ್ಯರನ್ನು ನಿನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸಿಕೊಡುವೆನು; ಉಳಿದವರು ತಮ್ಮ ತಮ್ಮ ಸ್ಥಳಗಳಿಗೆ ಹೋಗಲಿ” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"8": "<sup>8</sup> ಗಿದ್ಯೋನನು ಆ ಮುನ್ನೂರು ಜನರನ್ನು ಇಟ್ಟುಕೊಂಡು ಉಳಿದ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಅವರವರ ಗುಡಾರಗಳಿಗೆ ಕಳುಹಿಸಿದನು. ಕಳುಹಿಸುವಾಗ ಅವರ ಹತ್ತಿರ ಇದ್ದ ಆಹಾರವನ್ನೂ, ಕೊಂಬುಗಳನ್ನೂ ತನ್ನ ಜನರಿಗೋಸ್ಕರ ತೆಗೆದುಕೊಂಡನು. ಮಿದ್ಯಾನ್ಯರ ದಂಡು ಕೆಳಗೆ ತಗ್ಗಿನಲ್ಲಿ ಇಳಿದುಕೊಂಡಿತ್ತು.",
			"9": "<sup>9</sup> ಅದೇ ದಿನದ ರಾತ್ರಿಯಲ್ಲಿ ಯೆಹೋವನು ಅವನಿಗೆ, “ನೀನು ಎದ್ದು ಹೋಗಿ, ಶತ್ರುಗಳ ಪಾಳೆಯದ ಮೇಲೆ ಬೀಳು. ಅದನ್ನು ನಿನಗೆ ಒಪ್ಪಿಸಿಕೊಡುತ್ತೇನೆ.",
			"10": "<sup>10</sup> ಆದರೆ ನೀನು ಅವರ ಮೇಲೆ ಬೀಳುವುದಕ್ಕೆ ಹೆದರುತ್ತೀಯಾದರೆ ಮೊದಲು ನಿನ್ನ ಸೇವಕನಾದ ಪುರನ ಸಂಗಡ ಅಲ್ಲಿಗೆ ಹೋಗಿ ಅವರು ಮಾತನಾಡಿಕೊಳ್ಳುವುದನ್ನು ಲಾಲಿಸು;",
			"11": "<sup>11</sup> ಆಗ ಅವರ ಮೇಲೆ ಬೀಳುವುದಕ್ಕೆ ನಿನಗೆ ಧೈರ್ಯಬರುವುದು” ಅಂದನು. ಗಿದ್ಯೋನನು ಅದರಂತೆಯೇ ತನ್ನ ಸೇವಕನಾದ ಪುರನ ಸಂಗಡ ಶತ್ರುಸೈನಿಕರ ಪಾಳೆಯದ ಕಡೇ ಭಾಗಕ್ಕೆ ಹೋದನು.",
			"12": "<sup>12</sup> ಮಿದ್ಯಾನ್ಯರೂ, ಅಮಾಲೇಕ್ಯರೂ, ಮೂಡಣ ದೇಶದವರೂ ಮಿಡಿತೆಗಳಂತೆ ಗುಂಪಾಗಿ ತಗ್ಗಿನಲ್ಲಿ ಇಳಿದುಕೊಂಡಿದ್ದರು. ಅವರ ಒಂಟೆಗಳು ಸಮುದ್ರತೀರದ ಮರಳಿನಂತೆ ಅಸಂಖ್ಯವಾಗಿದ್ದವು.",
			"13": "<sup>13</sup> ಗಿದ್ಯೋನನು ಬಂದಾಗ ಅವರಲ್ಲೊಬ್ಬನು ತನ್ನ ಜೊತೆಗಾರನಿಗೆ ಒಂದು ಕನಸನ್ನು ತಿಳಿಸುತ್ತಿದ್ದನು. ಅವನು, “ಇಗೋ, ನಾನು ಒಂದು ಕನಸು ಕಂಡೆನು; ಅದರಲ್ಲಿ ಒಂದು ಜವೆಗೋದಿಯ ರೊಟ್ಟಿಯು ಉರುಳುತ್ತಾ ಮಿದ್ಯಾನ್ಯರ ಪಾಳೆಯದೊಳಗೆ ಬಂದು, ಒಂದು ಡೇರೆಗೆ ತಗಲಿ, ಅದನ್ನು ಬುಡಮೇಲು ಮಾಡಿ ಬೀಳಿಸಿಬಿಟ್ಟಿತು” ಎಂದು ಹೇಳಿದನು",
			"14": "<sup>14</sup> ಆಗ ಅವನ ಜೊತೆಗಾರನು, “ಇದು ಇಸ್ರಾಯೇಲನಾದ ಯೋವಾಷನ ಮಗ ಗಿದ್ಯೋನನ ಕತ್ತಿಯೇ ಹೊರತು ಮತ್ತೊಂದಲ್ಲ; ದೇವರು ಮಿದ್ಯಾನ್ಯರನ್ನೂ ಅವರ ಪಾಳೆಯಗಳನ್ನೂ ಅವನ ಕೈಗೆ ಒಪ್ಪಿಸಿಕೊಟ್ಟಿದ್ದಾನೆ” ಅಂದನು.",
			"15": "<sup>15</sup> ಗಿದ್ಯೋನನು ಆ ಕನಸನ್ನೂ, ಅದರ ಅರ್ಥವನ್ನೂ ಕೇಳಿದಾಗ ಯೆಹೋವನಿಗೆ ಅಡ್ಡಬಿದ್ದು, ಇಸ್ರಾಯೇಲರ ಪಾಳೆಯಕ್ಕೆ ಹಿಂದಿರುಗಿ ಬಂದು ಅವರಿಗೆ, “ಏಳಿರಿ, ಯೆಹೋವನು ಮಿದ್ಯಾನ್ಯರ ಪಾಳೆಯವನ್ನು ನಿಮ್ಮ ಕೈಗೆ ಒಪ್ಪಿಸಿದ್ದಾನೆ” ಅಂದನು.",
			"16": "<sup>16</sup> ಮತ್ತು ಅವನು ಆ ಮುನ್ನೂರು ಮಂದಿಯನ್ನು ಮೂರು ಗುಂಪು ಮಾಡಿ ಪ್ರತಿಯೊಬ್ಬನ ಕೈಯಲ್ಲಿ ಕೊಂಬನ್ನೂ, ಉರಿಯುವ ಪಂಜು ಇರುವ ಬರಿಕೊಡವನ್ನೂ ಕೊಟ್ಟು ಅವರಿಗೆ,",
			"17": "<sup>17</sup> “ನನ್ನನ್ನೇ ನೋಡುತ್ತಾ ನಾನು ಮಾಡುವ ಹಾಗೆ ಮಾಡಿರಿ. ನಾನು ಪಾಳೆಯದ ಅಂಚಿಗೆ ಬಂದಾಗ ಹೇಗೆ ಮಾಡುತ್ತೇನೋ ಹಾಗೆ ನೀವೂ ಮಾಡಬೇಕು.",
			"18": "<sup>18</sup> ನಾನೂ ನನ್ನ ಸಂಗಡ ಇದ್ದವರೂ ಕೊಂಬುಗಳನ್ನು ಊದಲು ನೀವೂ ಪಾಳೆಯದ ಸುತ್ತಲೂ ಕೊಂಬನ್ನು ಊದಿ, ‘ಯೆಹೋವನಿಗೆ ಜಯ, ಗಿದ್ಯೋನನಿಗೆ ಜಯ ಎಂದು ಕೂಗಿರಿ’” ಎಂದು ಹೇಳಿದನು.",
			"19": "<sup>19</sup> ಮಧ್ಯರಾತ್ರಿಯ ಮೊದಲನೆಯ ಜಾವದಲ್ಲಿ ಕಾವಲುಗಾರರು ಬದಲಾದ ಕೂಡಲೆ ಗಿದ್ಯೋನನೂ ಅವನ ಸಂಗಡ ಇದ್ದ ನೂರು ಮಂದಿಯೂ (ಶತ್ರುಗಳ) ಪಾಳೆಯದ ಅಂಚಿಗೆ ಬಂದು, ಕೊಂಬುಗಳನ್ನು ಊದಿ, ತಮ್ಮ ಕೈಯಲ್ಲಿದ್ದ ಕೊಡಗಳನ್ನು ಒಡೆದುಬಿಟ್ಟರು.",
			"20": "<sup>20</sup> ಕೂಡಲೆ ಮೂರು ಗುಂಪಿನವರೂ ಕೊಂಬುಗಳನ್ನು ಊದಿ, ಕೊಡಗಳನ್ನು ಒಡೆದುಬಿಟ್ಟು, ಎಡಗೈಯಲ್ಲಿ ಪಂಜುಗಳನ್ನೂ ಬಲಗೈಯಲ್ಲಿ ಊದುವ ಕೊಂಬುಗಳನ್ನೂ ಹಿಡಿದು, “ಯೆಹೋವನ ಕತ್ತಿ, ಗಿದ್ಯೋನನ ಕತ್ತಿ ಆಗಿದೆ” ಎಂದು ಕೂಗಿ",
			"21": "<sup>21</sup> ಪಾಳೆಯದ ಸುತ್ತಲೂ ತಮ್ಮ ತಮ್ಮ ಸ್ಥಳದಲ್ಲಿ ನಿಂತರು. ಪಾಳೆಯದವರಾದರೋ ಕೂಗುತ್ತಾ ಓಡಿಹೋದರು.",
			"22": "<sup>22</sup> ಆ ಮುನ್ನೂರು ಮಂದಿಯು ಕೊಂಬುಗಳನ್ನು ಊದುತ್ತಿರುವಾಗ ಪಾಳೆಯದವರು ಒಬ್ಬರನ್ನೊಬ್ಬರು ಹತಮಾಡಿಕೊಳ್ಳುವಂತೆ ಯೆಹೋವನು ಮಾಡಿದನು. ಪಾಳೆಯದವರು ಚೆರೇರದ ದಾರಿಯಲ್ಲಿರುವ ಬೇತ್‍ಷಿಟ್ಟದ ವರೆಗೂ ಟಬ್ಬಾತಿನ ಬಳಿಯಲ್ಲಿರುವ ಆಬೇಲ್ಮೆ ಹೋಲಾ ಪ್ರಾಂತ್ಯದ ವರೆಗೂ ಓಡಿಹೋದರು.",
			"23": "<sup>23</sup> ಇಸ್ರಾಯೇಲ್ಯರಾದ ನಫ್ತಾಲಿ, ಆಶೇರ್ ಕುಲಗಳವರೂ, ಮನಸ್ಸೆಕುಲದ ಎಲ್ಲಾ ಜನರೂ ಕೂಡಿ ಬಂದು ಮಿದ್ಯಾನ್ಯರನ್ನು ಹಿಂದಟ್ಟಿದರು.",
			"24": "<sup>24</sup> ಇದಲ್ಲದೆ ಗಿದ್ಯೋನನು ಎಫ್ರಾಯಿಮ್ ಪರ್ವತಪ್ರದೇಶಗಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿ ಅಲ್ಲಿನವರಿಗೆ, “ನೀವು ಮಿದ್ಯಾನ್ಯರಿಗೆ ವಿರೋಧವಾಗಿ ಹೊರಟು ಬೇತ್‌ಬಾರದವರೆಗಿರುವ ಪ್ರವಾಹಗಳನ್ನೂ ಯೊರ್ದನ್ ಹೊಳೆಯನ್ನೂ ವಶಪಡಿಸಿಕೊಳ್ಳಿರಿ” ಎಂದು ಹೇಳಿಕಳುಹಿಸಿದನು. ಆಗ ಎಫ್ರಾಯಿಮ್ಯರೆಲ್ಲರೂ ಒಟ್ಟಾಗಿ ಬೇತ್‌ಬಾರದವರೆಗಿರುವ ಪ್ರವಾಹಗಳಿಗೂ ಬಂದು ಅವುಗಳ ಹಾಯಗಡಗಳನ್ನೆಲ್ಲಾ ಮತ್ತು ಯೋರ್ದನ್ ಹೊಳೆಯನ್ನೂ ಹಿಡಿದರು.",
			"25": "<sup>25</sup> ಇದಲ್ಲದೆ ಅವರು ಮಿದ್ಯಾನ್ಯರ ನಾಯಕರಾದ ಓರೇಬ್, ಜೇಬ್ ಎಂಬುವರನ್ನು ಹಿಡಿದು ಓರೇಬನನ್ನು, ಓರೇಬನ ಬಂಡೆಯ ಮೇಲೆಯೂ, ಜೇಬನನ್ನು ಜೇಬನ ದ್ರಾಕ್ಷೆಯ ಆಲೆಯಲ್ಲಿಯೂ ಕೊಂದು ಅವರ ತಲೆಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಮಿದ್ಯಾನ್ಯರನ್ನು ಹಿಂದಟ್ಟುತ್ತಾ ಯೊರ್ದನಿನ ಆಚೆಗೆ ಹೋಗಿ ಅಲ್ಲಿದ್ದ ಗಿದ್ಯೋನನಿಗೆ ಅವುಗಳನ್ನು ಒಪ್ಪಿಸಿದರು."
		},
		"8": {
			"1": "<sup>1</sup> ಅನಂತರ ಎಫ್ರಾಯಿಮ್ಯರು ಗಿದ್ಯೋನನಿಗೆ, “ನೀನು ಹೀಗೆ ಯಾಕೆ ಮಾಡಿದಿ? ಮಿದ್ಯಾನ್ಯರೊಡನೆ ಯುದ್ಧಮಾಡುವುದಕ್ಕೆ ಹೊರಟಾಗ ನಮ್ಮನ್ನೇಕೆ ಕರೆಯಲಿಲ್ಲ?” ಎಂದು ಉಗ್ರಕೋಪದಿಂದ ಜಗಳವಾಡಿದರು.",
			"2": "<sup>2</sup> ಆಗ ಅವನು ಅವರಿಗೆ, “ನೀವು ಮಾಡಿದ ಕಾರ್ಯಕ್ಕೆ ಸಮಾನವಾಗುವಂತದ್ದು ನಾನೇನು ಮಾಡಿದೆ? ಅಬೀಯೆಜೆರನವರು ಸುಗ್ಗಿಯಲ್ಲಿ ಕೂಡಿಸಿದ್ದಕ್ಕಿಂತಲೂ, ಎಫ್ರಾಯಿಮ್ಯರು ಹಕ್ಕಲಾಯ್ದು ಕೂಡಿಸಿದ್ದು ಹೆಚ್ಚಾಗಿದೆಯಲ್ಲವೇ?",
			"3": "<sup>3</sup> ದೇವರು ನಿಮ್ಮ ಕೈಯಲ್ಲಿ ಮಿದ್ಯಾನ್ಯರ ನಾಯಕರಾದ ಓರೇಬ್ ಜೇಬರನ್ನು ಒಪ್ಪಿಸಿ ಕೊಟ್ಟಿದ್ದಾನಲ್ಲಾ! ಇದಕ್ಕೆ ಸಮಾನವಾದದ್ದನ್ನು ನಾನೇನು ಮಾಡಿದ್ದೇನೆ?” ಎಂದನು. ಈ ಮಾತುಗಳನ್ನು ಕೇಳಿದಾಗ ಅವರ ಸಿಟ್ಟಿಳಿಯಿತು.",
			"4": "<sup>4</sup> ಗಿದ್ಯೋನನು ಅವನ ಸಂಗಡ ಇದ್ದ ಮುನ್ನೂರು ಜನರು ಬಹಳ ದಣಿದವರಾಗಿದ್ದರೂ, ಹಿಂದಟ್ಟುತ್ತಾ ಯೊರ್ದನನ್ನು ದಾಟಿ ಸುಖೋತಿಗೆ ಬಂದರು.",
			"5": "<sup>5</sup> ಗಿದ್ಯೋನನು ಸುಖೋತಿನವರಿಗೆ, “ದಯವಿಟ್ಟು ನನ್ನ ಜೊತೆಯಲ್ಲಿ ಬಂದಿರುವವರಿಗೆ ಕೆಲವು ರೊಟ್ಟಿಗಳನ್ನು ಕೊಡಿರಿ, ಅವರು ಬಹಳ ದಣಿದಿದ್ದಾರೆ; ನಾನು ಮಿದ್ಯಾನ್ಯರ ಅರಸರಾದ ಜೆಬಹ, ಚಲ್ಮುನ್ನರನ್ನು ಹಿಂದಟ್ಟುತ್ತಾ ಇದ್ದೇನೆ” ಅಂದನು.",
			"6": "<sup>6</sup> ಆದರೆ ಸುಖೋತಿನ ಮುಖಂಡರು ಅವನಿಗೆ, “ನಾವು ನಿನ್ನ ಸೈನಿಕರಿಗೆ ರೊಟ್ಟಿಗಳನ್ನು ಕೊಡುವುದಕ್ಕೆ ಜೆಬಹ, ಚಲ್ಮುನ್ನರನ್ನು ನೀನು ಕೈಕಟ್ಟಿ ವಶಮಾಡಿಕೊಂಡಿದ್ದೀಯೋ?” ಎಂದು ಉತ್ತರಕೊಟ್ಟರು.",
			"7": "<sup>7</sup> ಆಗ ಗಿದ್ಯೋನನು ಅವರಿಗೆ, “ಒಳ್ಳೇದು, ಯೆಹೋವನು ಜೆಬಹ, ಚಲ್ಮುನ್ನರನ್ನು ನನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸಿಕೊಟ್ಟ ತರುವಾಯ ನಾನು ನಿಮ್ಮನ್ನು ಕಾಡಿನಲ್ಲಿರುವ ಜಾಲಿಮುಳ್ಳುಗಳಿಂದಲೂ, ಕಾರೆಗಿಡಗಳಿಂದಲೂ ಹೊಡಿಸುವೆನು” ಎಂದು ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ಅವನು ಅಲ್ಲಿಂದ ಪೆನೂವೇಲಿಗೆ ಬಂದು ಅಲ್ಲಿನವರನ್ನು ಅದೇ ಪ್ರಕಾರ ಬಿನ್ನವಿಸಲು ಅವರೂ ಸುಖೋತಿನವರಂತೆಯೇ ಉತ್ತರಕೊಟ್ಟರು.",
			"9": "<sup>9</sup> ಗಿದ್ಯೋನನು ಅವರಿಗೆ, “ನಾನು ಸುರಕ್ಷಿತವಾಗಿ ಹಿಂದಿರುಗಿ ಬಂದಾಗ ಈ ಬುರುಜನ್ನು ಕೆಡವಿಬಿಡುವೆನು” ಅಂದನು.",
			"10": "<sup>10</sup> ಜೆಬಹ, ಚಲ್ಮುನ್ನರು ಸುಮಾರು ಹದಿನೈದು ಸಾವಿರ ಮಂದಿ ಸೈನಿಕರೊಡನೆ ಕರ್ಕೋರಿನಲ್ಲಿ ಇಳಿದುಕೊಂಡಿದ್ದರು. ಮೂಡಣದೇಶದವರ ಸೈನ್ಯದಲ್ಲಿ ಲಕ್ಷದ ಇಪ್ಪತ್ತು ಸಾವಿರ ಭಟರು ಹತರಾಗಿ ಉಳಿದವರು ಇಷ್ಟೇ ಮಂದಿ.",
			"11": "<sup>11</sup> ಗಿದ್ಯೋನನು ಕಾಡುಗೊಲ್ಲರ ಪ್ರದೇಶಮಾರ್ಗವಾಗಿ ನೋಬಹ, ಯೊಗ್ಬೆಹಾ ಇವುಗಳ ಪೂರ್ವದಿಕ್ಕಿಗೆ ಹೋಗಿ ನಿರ್ಭಯದಿಂದ ಇಳುಕೊಂಡಿದ್ದ ಆ ಪಾಳೆಯದ ಮೇಲೆ ಬಿದ್ದನು.",
			"12": "<sup>12</sup> ಜೆಬಹನೂ ಮತ್ತು ಚಲ್ಮುನ್ನನೂ ಓಡಿಹೋಗಲಾಗಿ, ಅವರನ್ನು ಹಿಂದಟ್ಟಿ ಹಿಡಿದು, ಅವರ ಸೈನ್ಯವನ್ನು ಚದರಿಸಿಬಿಟ್ಟನು.",
			"13": "<sup>13</sup> ಯೋವಾಷನ ಮಗನಾದ ಗಿದ್ಯೋನನು ಯುದ್ಧದಿಂದ ಹೆರೆಸಿನ ಗಟ್ಟದ ಮಾರ್ಗವಾಗಿ ಹಿಂದಿರುಗಿ ಬಂದು,",
			"14": "<sup>14</sup> ಸುಖೋತಿನ ಒಬ್ಬ ಯೌವನಸ್ಥನನ್ನು ಹಿಡಿದು, ಆ ಊರಿನ ಮುಖಂಡರೂ, ಹಿರಿಯರೂ ಯಾರಾರೆಂದು ವಿಚಾರಿಸಲು ಅವನು ಎಪ್ಪತ್ತೇಳು ಮಂದಿಯ ಹೆಸರುಗಳನ್ನು ಬರೆದುಕೊಟ್ಟನು.",
			"15": "<sup>15</sup> ಅನಂತರ ಅವನು ಸುಖೋತಿನವರ ಬಳಿಗೆ ಹೋಗಿ ಅವರಿಗೆ, “ನಾವು ದಣಿದಿರುವ ನಿನ್ನ ಸೈನಿಕರಿಗೋಸ್ಕರ ರೊಟ್ಟಿಗಳನ್ನು ಕೊಡುವುದಕ್ಕೆ ಜೆಬಹ, ಚಲ್ಮುನ್ನರನ್ನು ಕೈಕಟ್ಟಿ ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡಿದ್ದೀಯೋ? ಎಂದು ನನ್ನನ್ನು ನಿಂದಿಸಿದಿರಲ್ಲಾ;",
			"16": "<sup>16</sup> ನೋಡಿರಿ, ಅವರು ಇಲ್ಲಿರುತ್ತಾರೆ” ಎಂದು ಹೇಳಿ ಊರಿನ ಹಿರಿಯರನ್ನೂ, ಮುಖಂಡರನ್ನೂ, ಜಾಲಿಮುಳ್ಳುಗಳಿಂದಲೂ, ಕಾರೆಗಿಡಗಳಿಂದಲೂ ಹೊಡಿಸಿ ಸುಖೋತಿನವರಿಗೆ ಬುದ್ಧಿಕಲಿಸಿದನು.",
			"17": "<sup>17</sup> ಮತ್ತು ಪೆನೂವೇಲಿನ ಬುರುಜನ್ನು ಕೆಡವಿಬಿಟ್ಟು ಆ ಊರಿನ ಜನರನ್ನು ಸಂಹರಿಸಿದನು.",
			"18": "<sup>18</sup> ಅವನು ಜೆಬಹ, ಚಲ್ಮುನ್ನರನ್ನು, “ನೀವು ತಾಬೋರದಲ್ಲಿ ಕೊಂದುಹಾಕಿದ ಮನುಷ್ಯರು ಹೇಗಿದ್ದರು?” ಎಂದು ಕೇಳಲಾಗಿ ಅವರು ಅವನಿಗೆ, “ನಿನ್ನ ಹಾಗೆಯೇ ಇದ್ದರು; ಎಲ್ಲರೂ ರೂಪದಲ್ಲಿ ರಾಜಪುತ್ರರಂತಿದ್ದರು” ಎಂದು ಉತ್ತರಕೊಟ್ಟರು.",
			"19": "<sup>19</sup> ಆಗ ಅವನು, “ಅವರು ನನ್ನ ತಾಯಿಯ ಮಕ್ಕಳು; ನನ್ನ ಸಹೋದರರು; ಯೆಹೋವನಾಣೆ, ನೀವು ಅವರನ್ನು ಉಳಿಸಿದ್ದರೆ ನಾನು ನಿಮ್ಮನ್ನು ಕೊಲ್ಲುತ್ತಿರಲಿಲ್ಲ” ಎಂದು ಹೇಳಿ",
			"20": "<sup>20</sup> ತನ್ನ ಹಿರೀ ಮಗನಾದ ಎತೆರನಿಗೆ, “ನೀನೆದ್ದು ಇವರನ್ನು ಕೊಂದುಹಾಕು” ಅಂದನು. ಆದರೆ ಅವನು ಇನ್ನೂ ಹುಡುಗನಾಗಿದ್ದರಿಂದ ಭಯಪಟ್ಟು ಕತ್ತಿಯನ್ನು ಹಿರಿಯಲೇ ಇಲ್ಲ.",
			"21": "<sup>21</sup> ಆಗ ಜೆಬಹನೂ ಮತ್ತು ಚಲ್ಮುನ್ನನೂ ಗಿದ್ಯೋನನಿಗೆ, “ನೀನೇ ಎದ್ದು ಬಂದು ನಮ್ಮನ್ನು ಕೊಂದುಹಾಕು; ಪ್ರಾಯಕ್ಕೆ ತಕ್ಕಂತೆ ಪುರುಷನಿಗೆ ಬಲವಿರುತ್ತದಲ್ಲಾ” ಅನ್ನಲು ಅವನೆದ್ದು ಜೆಬಹ, ಚಲ್ಮುನ್ನರನ್ನು ಕೊಂದುಹಾಕಿ, ಅವರ ಒಂಟೆಗಳ ಕೊರಳಿನಲ್ಲಿದ್ದ ಅರ್ಧಚಂದ್ರಾಕಾರದ ಆಭರಣಗಳನ್ನು ತೆಗೆದುಕೊಂಡನು.",
			"22": "<sup>22</sup> ತರುವಾಯ ಇಸ್ರಾಯೇಲ್ಯರು ಗಿದ್ಯೋನನಿಗೆ, “ನೀನು ನಮ್ಮನ್ನು ಮಿದ್ಯಾನ್ಯರ ಕೈಯಿಂದ ಬಿಡಿಸಿದ್ದೀಯಲ್ಲಾ; ನೀನೂ, ನಿನ್ನ ಮಗನೂ, ಮೊಮ್ಮಗನೂ ವಂಶಪಾರಂಪರ್ಯವಾಗಿ ನಮ್ಮ ಮೇಲೆ ಅರಸರಾಗಿರಬೇಕು” ಎಂದರು.",
			"23": "<sup>23</sup> ಅವನು ಅವರಿಗೆ, “ನಾನಾಗಲಿ, ನನ್ನ ಮಗನಾಗಲಿ ನಿಮ್ಮನ್ನು ಆಳುವುದಿಲ್ಲ; ಯೆಹೋವನೇ ನಿಮ್ಮ ಅರಸನಾಗಿರುವನು.",
			"24": "<sup>24</sup> ಆದರೆ ನಿಮಗೆ ಒಂದು ಬಿನ್ನಹ ಮಾಡುತ್ತೇನೆ; ಪ್ರತಿಯೊಬ್ಬನು ತಾನು ಕೊಳ್ಳೆಹೊಡೆದ ಬಂಗಾರದ ಓಲೆಗಳನ್ನು ನನಗೆ ಕೊಡಲಿ” ಅಂದನು. ಮಿದ್ಯಾನ್ಯರು ಇಷ್ಮಾಯೇಲ್ಯರಾಗಿದ್ದರಿಂದ ಅವರ ಬಳಿಯಲ್ಲಿ ಬಂಗಾರದ ಓಲೆಗಳು ಇದ್ದವು.",
			"25": "<sup>25</sup> ಗಿದ್ಯೋನನ ಬಿನ್ನಹಕ್ಕೆ ಇಸ್ರಾಯೇಲ್ಯರು, “ನಾವು ಸಂತೋಷದಿಂದ ಕೊಡುವೆವು” ಎಂದು ಹೇಳಿ, ಅಲ್ಲಿ ಒಂದು ಬಟ್ಟೆಯನ್ನು ಹಾಸಿ, ಅದರ ಮೇಲೆ ತಾವು ಕೊಳ್ಳೆಯಾಗಿ ತಂದ ಓಲೆಗಳನ್ನು ಇಟ್ಟುಬಿಟ್ಟರು.",
			"26": "<sup>26</sup> ಗಿದ್ಯೋನನಿಗೆ ಮೊದಲೇ ದೊರಕಿದ್ದ ಅರ್ಧಚಂದ್ರಾಕಾರದ ಆಭರಣ, ಕುಂಡಲ, ಮಿದ್ಯಾನ್ ರಾಜರು ಹೊದ್ದುಕೊಂಡಿದ್ದ ರಕ್ತಾಂಬರ, ಅವರ ಒಂಟೆಗಳ ಕಂಠಮಾಲೆ, ಇವುಗಳ ಹೊರತಾಗಿ ಅವನು ಇಸ್ರಾಯೇಲ್ಯರಿಂದ ಕೇಳಿ ತೆಗೆದುಕೊಂಡ ಓಲೆಗಳ ಬಂಗಾರವೇ ಸಾವಿರದ ಏಳುನೂರು ತೊಲೆ ತೂಕವಾಗಿತ್ತು.",
			"27": "<sup>27</sup> ಈ ಬಂಗಾರದಿಂದ ಗಿದ್ಯೋನನು ಒಂದು ಏಫೋದನ್ನು ಮಾಡಿಸಿ, ಅದನ್ನು ತನ್ನ ಊರಾದ ಒಫ್ರದಲ್ಲಿಟ್ಟನು. ಆದ್ದರಿಂದ ಇಸ್ರಾಯೇಲರೆಲ್ಲರೂ ಅದನ್ನು ಪೂಜಿಸುತ್ತಿದ್ದುದರಿಂದ ದೈವದ್ರೋಹಿಗಳಾದರು; ಅದು ಗಿದ್ಯೋನನಿಗೂ ಅವನ ಮನೆಯವರಿಗೂ ಉರುಲಾಯಿತು.",
			"28": "<sup>28</sup> ಮಿದ್ಯಾನ್ಯರು ಇಸ್ರಾಯೇಲ್ಯರ ಮುಂದೆ ಬಹಳವಾಗಿ ತಗ್ಗಿಸಲ್ಪಟ್ಟರು; ಅವರು ಪುನಃ ತಲೆಯೆತ್ತಲಿಲ್ಲ. ಗಿದ್ಯೋನನ ಜೀವಮಾನದಲ್ಲಿ ನಾಲ್ವತ್ತು ವರುಷಗಳ ಕಾಲ ದೇಶದಲ್ಲಿ ಸಮಾಧಾನವಿತ್ತು.",
			"29": "<sup>29</sup> ಯೋವಾಷನ ಮಗ ಯೆರುಬ್ಬಾಳನು ಹೋಗಿ ತನ್ನ ಮನೆಯಲ್ಲಿ ವಾಸವಾಗಿದ್ದನು.",
			"30": "<sup>30</sup> ಗಿದ್ಯೋನನಿಗೆ ಅನೇಕ ಮಂದಿ ಹೆಂಡತಿಯರಿದ್ದುದರಿಂದ ಅವನಿಗೆ ಎಪ್ಪತ್ತು ಮಂದಿ ಗಂಡು ಮಕ್ಕಳು ಹುಟ್ಟಿದರು.",
			"31": "<sup>31</sup> ಅವನಿಗೆ ಶೆಕೆಮಿನಲ್ಲಿದ್ದ ತನ್ನ ಉಪಪತ್ನಿಯಲ್ಲಿ ಇನ್ನೊಬ್ಬ ಮಗನು ಹುಟ್ಟಲು, ಆ ಮಗನಿಗೆ ಅಬೀಮೆಲೆಕನೆಂದು ಹೆಸರಿಟ್ಟನು.",
			"32": "<sup>32</sup> ಯೋವಾಷನ ಮಗನಾದ ಗಿದ್ಯೋನನು ದಿನತುಂಬಿದ ಮುದುಕನಾಗಿ ಮರಣ ಹೊಂದಲು, ಅವನ ಶವವನ್ನು ಅಬೀಯೆಜೆರೀಯರ ಒಫ್ರದಲ್ಲಿದ್ದ ಒಂದು ಸಮಾಧಿಯಲ್ಲಿ ಹೂಣಿಟ್ಟರು. ಆ ಸಮಾಧಿಯು ಅವನ ತಂದೆಯಾದ ಯೋವಾಷನದಾಗಿತ್ತು.",
			"33": "<sup>33</sup> ಗಿದ್ಯೋನನು ಸತ್ತನಂತರ ಇಸ್ರಾಯೇಲರು ದೈವದ್ರೋಹಿಗಳಾಗಿ ಬಾಳನ ಪ್ರತಿಮೆಗಳನ್ನು ಪೂಜಿಸಿದರು; ಬಾಳ್‌ಬೆರೀತನನ್ನು ತಮ್ಮ ದೇವರನ್ನಾಗಿ ಮಾಡಿಕೊಂಡರು.",
			"34": "<sup>34</sup> ಸುತ್ತಣ ಎಲ್ಲಾ ಶತ್ರುಗಳಿಂದ ತಮ್ಮನ್ನು ಬಿಡಿಸಿದ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಮರೆತುಬಿಟ್ಟರು.",
			"35": "<sup>35</sup> ಯೆರುಬ್ಬಾಳನೆಂಬ ಗಿದ್ಯೋನನು ತಮಗೆ ಮಾಡಿದ ಉಪಕಾರಗಳನ್ನು ಅವರು ನೆನಸಲಿಲ್ಲ; ಅವನ ಮನೆಯವರಿಗೆ ದಯೆತೋರಿಸಲಿಲ್ಲ."
		},
		"9": {
			"1": "<sup>1</sup> ಯೆರುಬ್ಬಾಳನ ಮಗನಾದ ಅಬೀಮೆಲೆಕನು ಶೆಕೆಮಿಗೆ ಹೋಗಿ ತನ್ನ ಸೋದರಮಾವಂದಿರಿಗೂ, ತಾಯಿಯ ಬಂಧುಗಳೆಲ್ಲರಿಗೂ ಹೇಳಿದ್ದೇನೆಂದರೆ,",
			"2": "<sup>2</sup> “ನೀವು ದಯವಿಟ್ಟು ಶೆಕೆಮಿನ ಹಿರಿಯರನ್ನು ಮಾತನಾಡಿಸಿ, ‘ಯೆರುಬ್ಬಾಳನ ಎಪ್ಪತ್ತು ಮಂದಿ ಮಕ್ಕಳು ನಿಮ್ಮನ್ನು ಆಳ್ವಿಕೆ ಮಾಡುವುದು ಒಳ್ಳೆಯದೋ ಅಥವಾ ಒಬ್ಬನೇ ಆಳುವುದು ಮೇಲೋ?’ ಎಂದು ಅವರನ್ನು ಕೇಳಿ, ನಾನು ಅವರ ರಕ್ತಸಂಬಂಧಿಯಾಗಿದ್ದೇನೆ ಎಂಬುದನ್ನು ತಿಳಿಸಿರಿ” ಅಂದನು.",
			"3": "<sup>3</sup> ಅವರು ಶೆಕೆಮಿನ ಹಿರಿಯರ ಬಳಿಗೆ ಹೋಗಿ ಅಬೀಮೆಲೆಕನ ವಿಷಯದಲ್ಲಿ ಹಾಗೆಯೇ ಹೇಳಲು ಅವರು, “ಇವನು ನಮ್ಮ ಸಹೋದರನಾಗಿದ್ದಾನಲ್ಲಾ” ಅಂದುಕೊಂಡು ಅವನ ಪಕ್ಷವನ್ನು ಹಿಡಿಯುವುದಕ್ಕೆ ಒಪ್ಪಿಕೊಂಡರು.",
			"4": "<sup>4</sup> ಮತ್ತು ಅವರು ಬಾಳ್‍ಬೆರೀತಿನ ದೇವಸ್ಥಾನದಿಂದ ಎಪ್ಪತ್ತು ರೂಪಾಯಿಗಳನ್ನು ಅವನಿಗೆ ಕೊಟ್ಟರು. ಅವನು ಈ ಹಣದಿಂದ ಕೆಲವು ಕಾಕಪೋಕರನ್ನು ಕೂಡಿಸಿ,",
			"5": "<sup>5</sup> ಅವರ ನಾಯಕನಾಗಿ ಹೊರಟು, ಒಫ್ರದಲ್ಲಿದ್ದ ತನ್ನ ತಂದೆಯಾದ ಯೆರುಬ್ಬಾಳನ ಮನೆಗೆ ಹೋಗಿ, ಅವನ ಎಪ್ಪತ್ತು ಮಂದಿ ಮಕ್ಕಳನ್ನು ಹಿಡಿದು, ಒಂದೇ ಬಂಡೆಯ ಮೇಲೆ ಕೊಲ್ಲಿಸಿದನು. ಆದರೆ ಕಿರಿಯವನಾದ ಯೋತಾಮನೆಂಬವನು ಅಡಗಿಕೊಂಡು ಉಳಿದನು.",
			"6": "<sup>6</sup> ತರುವಾಯ ಎಲ್ಲಾ ಶೆಕೆಮಿನವರೂ ಮಿಲ್ಲೋ ಕೋಟೆಯವರೂ ಶೆಕೆಮಿನ ಬಳಿಯಲ್ಲಿ ಜ್ಞಾಪಕಸ್ತಂಭವಿರುವ ಏಲೋನ್ ವೃಕ್ಷದ ಹತ್ತಿರ ಕೂಡಿಬಂದು ಅಬೀಮೆಲೆಕನನ್ನು ತಮ್ಮ ಅರಸನನ್ನಾಗಿ ಮಾಡಿಕೊಂಡರು.",
			"7": "<sup>7</sup> ಯೋತಾಮನು ಇದನ್ನು ಕೇಳಿ ಗೆರಿಜ್ಜೀಮ್ ಬೆಟ್ಟದ ತುದಿಯಲ್ಲಿ ನಿಂತು ಗಟ್ಟಿಯಾದ ಧ್ವನಿಯಿಂದ, “ಶೆಕೆಮಿನ ಜನರೇ, ನನ್ನ ಮಾತನ್ನು ಕೇಳಿರಿ; ಆಗ ದೇವರು ನಿಮ್ಮ ಮೊರೆಗೆ ಕಿವಿಗೊಡುವನು” ಎಂದನು.",
			"8": "<sup>8</sup> “ಒಮ್ಮೆ, ಮರಗಳು ತಮಗೋಸ್ಕರ ಒಬ್ಬ ಅರಸನನ್ನು ಅಭಿಷೇಕಿಸಬೇಕೆಂದು ಹೊರಟು ಎಣ್ಣೆಯ ಮರದ ಬಳಿಗೆ ಬಂದು, ‘ನೀನು ಬಂದು ನಮಗೆ ಅರಸನಾಗು’ ಅಂದವು.",
			"9": "<sup>9</sup> ಆಗ ಅದು ಅವುಗಳಿಗೆ, ‘ನನ್ನಲ್ಲಿರುವ ಎಣ್ಣೆಗೋಸ್ಕರ ದೇವತೆಗಳೂ, ಮನುಷ್ಯರು ನನ್ನನ್ನು ಘನಪಡಿಸುತ್ತಾರೆ; ಇದನ್ನು ಬಿಟ್ಟು ಮರಗಳ ಮೇಲೆ ಓಲಾಡುವುದಕ್ಕೆ ಬರಬೇಕೋ?’ ಅಂದಿತು.",
			"10": "<sup>10</sup> ತರುವಾಯ ಅವು ಅಂಜೂರ ಗಿಡದ ಬಳಿಗೆ ಹೋಗಿ ಅದಕ್ಕೆ, ‘ನೀನು ಬಂದು ನಮಗೆ ಅರಸನಾಗು’ ಎನ್ನಲು",
			"11": "<sup>11</sup> ಅದು, ‘ನಾನು ನನ್ನ ಶ್ರೇಷ್ಠವಾದ ಮಧುರಫಲದಾನವನ್ನು ಬಿಟ್ಟು ಮರಗಳ ಮೇಲೆ ಓಲಾಡುವುದಕ್ಕೆ ಬರಬೇಕೋ?’ ಎಂದಿತು.",
			"12": "<sup>12</sup> ಅನಂತರ ಅವು ದ್ರಾಕ್ಷಾಲತೆಯ ಬಳಿಗೆ ಹೋಗಿ, ‘ನೀನು ಬಂದು ನಮಗೆ ಅರಸನಾಗು’ ಎನ್ನಲು",
			"13": "<sup>13</sup> ಅದು ಅವುಗಳಿಗೆ, ‘ನಾನು ನನ್ನ ರಸದಿಂದ ದೇವತೆಗಳಿಗೂ ಮನುಷ್ಯರಿಗೂ ಆನಂದಕೊಡುವುದನ್ನು ಬಿಟ್ಟು ಮರಗಳ ಮೇಲೆ ಓಲಾಡುವುದಕ್ಕೆ ಬರಬೇಕೋ?’ ಎಂದಿತು.",
			"14": "<sup>14</sup> ಕೊನೆಗೆ ಎಲ್ಲಾ ಮರಗಳು ಮುಳ್ಳುಗಿಡದ ಬಳಿಗೆ ಹೋಗಿ, ‘ನೀನು ಬಂದು ನಮಗೆ ಅರಸನಾಗು’ ಅನ್ನಲು",
			"15": "<sup>15</sup> ಅದು, ‘ನೀವು ಯಥಾರ್ಥಮನಸ್ಸಿನಿಂದ ನನಗೆ ರಾಜ್ಯಾಭಿಷೇಕ ಮಾಡಬೇಕೆಂದಿರುವುದಾದರೆ ಬಂದು ನನ್ನ ನೆರಳನ್ನು ಆಶ್ರಯಿಸಿಕೊಳ್ಳಿರಿ; ಇಲ್ಲವಾದರೆ ನನ್ನಿಂದ ಬೆಂಕಿಹೊರಟು ಲೆಬನೋನಿನ ದೇವದಾರು ವೃಕ್ಷಗಳನ್ನು ದಹಿಸಿಬಿಡುವುದು’ ಎಂದಿತು.",
			"16": "<sup>16</sup> “ನೀವು ಅಬೀಮೆಲೆಕನನ್ನು ಅರಸನನ್ನಾಗಿ ಮಾಡಿದ್ದು ನ್ಯಾಯವೋ? ಅದು ಧರ್ಮಕಾರ್ಯವೋ? ಯೆರುಬ್ಬಾಳನು ನಿಮಗೆ ಮಾಡಿದ ಉಪಕಾರಕ್ಕೋಸ್ಕರ ನೀವು ಅವನಿಗೂ ಅವನ ಮನೆಯವರಿಗೂ ಪ್ರತ್ಯುಪಕಾರಮಾಡಿದಿರೋ?",
			"17": "<sup>17</sup> ನನ್ನ ತಂದೆಯು ತನ್ನ ಜೀವದ ಆಶೆಯನ್ನು ತೊರೆದು, ಹೋರಾಡಿ, ನಿಮ್ಮನ್ನು ಮಿದ್ಯಾನ್ಯರ ಕೈಯಿಂದ ಬಿಡಿಸಿದನಲ್ಲಾ.",
			"18": "<sup>18</sup> ಆದರೆ ನೀವು ಈಗ ನನ್ನ ತಂದೆಯ ಮನೆಗೆ ವಿರೋಧವಾಗಿ ನಿಂತು, ಅವನ ಎಪ್ಪತ್ತು ಮಂದಿ ಮಕ್ಕಳನ್ನು ಒಂದೇ ಬಂಡೆಯ ಮೇಲೆ ವಧಿಸಿ, ಅವನ ದಾಸಿಯ ಮಗನಾದ ಅಬೀಮೆಲೆಕನನ್ನು ನಮ್ಮ ಬಂಧುವೆಂದು ಹೇಳಿ, ಶೆಕೆಮಿನ ಅರಸನನ್ನಾಗಿ ಮಾಡಿಕೊಂಡಿದ್ದೀರಿ.",
			"19": "<sup>19</sup> ನೀವು ಈಗ ಯೆರುಬ್ಬಾಳನಿಗೂ ಅವನ ಮನೆಯವರಿಗೂ ಮಾಡಿದ್ದು ನ್ಯಾಯವೂ, ಧರ್ಮವೂ ಆಗಿದ್ದರೆ ಅಬೀಮೆಲೆಕನಲ್ಲಿ ಆನಂದಿಸಿರಿ; ಅವನು ನಿಮ್ಮಲ್ಲಿ ಆನಂದಿಸಲಿ.",
			"20": "<sup>20</sup> ಇಲ್ಲವಾದರೆ ಅಬೀಮೆಲೆಕನಿಂದ ಬೆಂಕಿಹೊರಟು ಶೆಕೆಮಿನವರನ್ನೂ, ಮಿಲ್ಲೋ ಕೋಟೆಯವರನ್ನೂ ದಹಿಸಿಬಿಡಲಿ; ಶೆಕೆಮಿನವರಿಂದಲೂ, ಮಿಲ್ಲೋ ಕೋಟೆಯವರಿಂದಲೂ ಬೆಂಕಿಹೊರಟು ಅಬೀಮೆಲೆಕನನ್ನು ದಹಿಸಿಬಿಡಲಿ” ಎಂದನು.",
			"21": "<sup>21</sup> ಯೋತಾಮನು ಇಷ್ಟು ಹೇಳಿ ತನ್ನ ಸಹೋದರನಾದ ಅಬೀಮೆಲೆಕನಿಂದ ತಪ್ಪಿಸಿಕೊಂಡು ಬೇರಕ್ಕೆ ಓಡಿಹೋಗಿ ಅಲ್ಲೇ ವಾಸಮಾಡಿದನು.",
			"22": "<sup>22</sup> ಅಬೀಮೆಲೆಕನು ಇಸ್ರಾಯೇಲರನ್ನು ಮೂರು ವರುಷ ಆಳಿದನಂತರ",
			"23": "<sup>23</sup> ಯೆಹೋವನು ಅವನಿಗೂ ಶೆಕೆಮಿನ ಹಿರಿಯರಿಗೂ ವೈಮನಸ್ಸು ಹುಟ್ಟುವಂತೆ ಮಾಡಿದನು. ಅವರು ಅವನಿಗೆ ದ್ರೋಹಮಾಡಿದರು.",
			"24": "<sup>24</sup> ಇದರಿಂದ ಯೆರುಬ್ಬಾಳನ ಎಪ್ಪತ್ತು ಮಂದಿ ಮಕ್ಕಳನ್ನು ಕ್ರೂರತನದಿಂದ ಕೊಂದುಹಾಕಿದ ರಕ್ತಾಪರಾಧಫಲವು ಅವರ ಸಹೋದರನಾದ ಅಬೀಮೆಲೆಕನ ಮೇಲೆಯೂ, ಆ ಕೃತ್ಯದಲ್ಲಿ ಅವನಿಗೆ ಸಹಾಯಕರಾಗಿದ್ದ ಶೆಕೆಮಿನವರ ಮೇಲೆಯೂ ಬರುವುದಕ್ಕೆ ಕಾರಣವಾಯಿತು.",
			"25": "<sup>25</sup> ಶೆಕೆಮಿನವರು ಅಬೀಮೆಲೆಕನಿಗೆ ವಿರೋಧವಾಗಿ ಪರ್ವತಶಿಖರಗಳಲ್ಲಿ ಹೊಂಚುಗಾರರನ್ನಿಟ್ಟರು. ಇವರು ಆ ದಾರಿಗಳಲ್ಲಿ ಸಂಚರಿಸುವ ಪ್ರಯಾಣಿಕರನ್ನು ಸುಲಿಗೆ ಮಾಡಿಕೊಳ್ಳುತ್ತಿದ್ದರು. ಈ ವರ್ತಮಾನವು ಅಬೀಮೆಲೆಕನಿಗೆ ಮುಟ್ಟಿತು.",
			"26": "<sup>26</sup> ಅದೇ ಸಮಯದಲ್ಲಿ ಎಬೆದನ ಮಗನಾದ ಗಾಳನೆಂಬುವನು ತನ್ನ ಬಂಧುಗಳೊಡನೆ ಶೆಕೆಮಿಗೆ ಬಂದನು. ಶೆಕೆಮಿನ ಹಿರಿಯರು ಅವನಲ್ಲಿ ಭರವಸವಿಟ್ಟರು.",
			"27": "<sup>27</sup> ಆ ಊರಿನವರು ಒಂದಾನೊಂದು ದಿನ ತಮ್ಮ ದ್ರಾಕ್ಷಾಫಲಗಳನ್ನು ಕೊಯಿದು, ಆಲೆಗಳಲ್ಲಿ ರಸತೆಗೆದು, ತಮ್ಮ ದೇವರ ಗುಡಿಗೆ ಹೋಗಿ ಹಬ್ಬಮಾಡಿ, ಉಂಡುಕುಡಿದು ಅಬೀಮೆಲೆಕನನ್ನು ಶಪಿಸಿದರು.",
			"28": "<sup>28</sup> ಎಬೆದನ ಮಗನಾದ ಗಾಳನೂ ಅವರ ಸಂಗಡ ಕೂಡಿಕೊಂಡು, “ಅಬೀಮೆಲೆಕನು ಎಷ್ಟರವನು? ಶೆಕೆಮಿನವರಾದ ನಾವು ಎಷ್ಟರವರು? ನಾವು ಅಬೀಮೆಲೆಕನನ್ನು ಯಾಕೆ ಸೇವಿಸಬೇಕು? ಅವನು ಯೆರುಬ್ಬಾಳನ ಮಗನಲ್ಲವೋ? ಅವನ ಪುರಾಧಿಕಾರಿಯು ಜೆಬುಲನಲ್ಲವೋ? ನಾವು ಅವನನ್ನು ಯಾಕೆ ಸೇವಿಸಬೇಕು? ಶೆಕೆಮನ ತಂದೆಯಾದ ಹಮೋರನ ವಂಶದವರ ಸೇವೇಯನ್ನು ಮಾಡಲಿ.",
			"29": "<sup>29</sup> ಈ ಜನರ ಮೇಲೆ ನನಗೆ ಅಧಿಕಾರವಿದ್ದಿದ್ದರೆ ಅಬೀಮೆಲೆಕನನ್ನು ಓಡಿಸಿಬಿಡುತ್ತಿದ್ದೆನು” ಎಂದು ಹೇಳಿ, “ಎಲಾ, ಅಬೀಮೆಲೆಕನೇ, ಸೈನ್ಯವನ್ನು ತೆಗೆದುಕೊಂಡು ನನ್ನೊಡನೆ ಯುದ್ಧಕ್ಕೆ ಬಾ” ಎಂದು ಕೊಚ್ಚಿಕೊಂಡನು.",
			"30": "<sup>30</sup> ಪುರಾಧಿಕಾರಿಯಾದ ಜೆಬುಲನು ಎಬೆದನ ಮಗನಾದ ಗಾಳನ ಮಾತುಗಳನ್ನು ಕೇಳಿ, ಕೋಪಗೊಂಡು ಯಾರಿಗೂ ತಿಳಿಯದಂತೆ ಅಬೀಮೆಲೆಕನ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿ,",
			"31": "<sup>31</sup> “ಇಗೋ, ಎಬೆದನ ಮಗನಾದ ಗಾಳನೂ ಅವನ ಬಂಧುಗಳೂ ಇಲ್ಲಿಗೆ ಬಂದು ಶೆಕೆಮಿನವರೆಲ್ಲರನ್ನೂ ನಿನಗೆ ವಿರೋಧವಾಗಿ ಹುರಿದುಂಬಿಸುತ್ತಿದ್ದಾರೆ.",
			"32": "<sup>32</sup> ಆದುದರಿಂದ ನೀನು ಈ ರಾತ್ರಿಯೇ ಸೈನ್ಯವನ್ನು ತೆಗೆದುಕೊಂಡು ಬಂದು ಹೊಲಗಳಲ್ಲಿ ಹೊಂಚುಹಾಕಿಕೊಂಡಿರು. ಸೂರ್ಯನು ಉದಯಿಸುತ್ತಲೇ ಎದ್ದು ಪಟ್ಟಣದ ಮೇಲೆ ಬೀಳು. ಅವನೂ ಅವನ ಜೊತೆಯಲ್ಲಿರುವವರೂ",
			"33": "<sup>33</sup> ನಿನಗೆ ವಿರೋಧವಾಗಿ ಹೊರಟು ಬಂದಾಗ ನಿನಗೆ ಅನುಕೂಲ ತೋರಿದ ಹಾಗೆ ಮಾಡು” ಎಂದು ಹೇಳಿಸಿದನು.",
			"34": "<sup>34</sup> ಅಬೀಮೆಲೆಕನೂ, ಅವನ ಜನರೂ ರಾತ್ರಿಯಲ್ಲೇ ಹೊರಟು ಬಂದು ನಾಲ್ಕು ಗುಂಪಾಗಿ ಶೆಕೆಮಿನವರನ್ನು ಹೊಂಚಿಕಾಯುತ್ತಿದ್ದರು.",
			"35": "<sup>35</sup> ಎಬೆದನ ಮಗನಾದ ಗಾಳನು ಹೊರಗೆ ಹೋಗಿ ಊರುಬಾಗಿಲಲ್ಲಿ ನಿಂತುಕೊಂಡಾಗ ಅಬೀಮೆಲೆಕನೂ ಅವನ ಜನರೂ ತಾವು ಹೊಂಚಿನೋಡುತ್ತಿದ್ದ ಸ್ಥಳದಿಂದ ಎದ್ದು ಬಂದರು.",
			"36": "<sup>36</sup> ಗಾಳನು ಅವರನ್ನು ಕಂಡು ಜೆಬುಲನಿಗೆ, “ಇಗೋ, ಬೆಟ್ಟದ ಮೇಲಿನಿಂದ ಜನರು ಬರುತ್ತಿದ್ದಾರೆ” ಎಂದು ಹೇಳಿದನು. ಜೆಬುಲನು ಅವನಿಗೆ, “ನೀನು ಬೆಟ್ಟದ ನೆರಳನ್ನು ನೋಡಿ ಜನರೆಂದು ಭಾವಿಸುತ್ತೀ” ಅನ್ನಲು",
			"37": "<sup>37</sup> ಅವನು ತಿರುಗಿ, “ನೋಡು, ಬೆಟ್ಟದ ಮೇಲಿನಿಂದ ಜನರು ಬರುತ್ತಿದ್ದಾರೆ; ಕಣಿಹೇಳುವವರ ಮರದ ಮಾರ್ಗವಾಗಿ ಇನ್ನೊಂದು ಗುಂಪು ಬರುತ್ತಿದೆ” ಅಂದನು.",
			"38": "<sup>38</sup> ಆಗ ಜೆಬುಲನು ಅವನಿಗೆ, “ಈಗ ನಿನ್ನ ಬಾಯೆಲ್ಲಿದೆ? ಅಬೀಮೆಲೆಕನು ಎಷ್ಟರವನು? ನಾವು ಅವನನ್ನು ಯಾಕೆ ಸೇವಿಸಬೇಕು ಎಂದು ಕೊಚ್ಚಿಕೊಂಡಿಯಲ್ಲಾ; ಅವರು ನೀನು ತಿರಸ್ಕರಿಸಿದ ಜನರಲ್ಲವೋ? ಹಾಗಾದರೆ ಹೋಗಿ ಅವರೊಡನೆ ಯುದ್ಧಮಾಡು, ನೋಡೋಣ” ಎಂದು ಹೇಳಿದನು.",
			"39": "<sup>39</sup> ಗಾಳನು ಶೆಕೆಮಿನವರ ನಾಯಕನಾಗಿ ಹೊರಟು ಅಬೀಮೆಲೆಕನೊಡನೆ ಕಾದಾಡಿದನು.",
			"40": "<sup>40</sup> ಆದರೆ ಅಬೀಮೆಲೆಕನಿಗೆ ಬೆನ್ನು ತೋರಿಸಿ ಓಡಿಹೋಗಲು, ಅವನು ಇವನನ್ನು ಹಿಂದಟ್ಟಿದನು. ಊರುಬಾಗಿಲಿನವರೆಗೆ ಅನೇಕ ಮಂದಿ ಹತರಾಗಿ ಬಿದ್ದರು.",
			"41": "<sup>41</sup> ಅನಂತರ ಅಬೀಮೆಲೆಕನು ಹೋಗಿ ಅರೂಮದಲ್ಲಿ ವಾಸಮಾಡಿದನು. ಇತ್ತ ಜೆಬುಲನು ಗಾಳನನ್ನೂ ಅವನ ಬಂಧುಗಳನ್ನೂ ಶೆಕೆಮಿನಿಂದ ಓಡಿಸಿಬಿಟ್ಟನು.",
			"42": "<sup>42</sup> ಮರುದಿನ ಜನರು ಬೈಲಿಗೆ ಹೊರಟು ಬಂದರು;",
			"43": "<sup>43</sup> ಇದು ಅಬೀಮೆಲೆಕನಿಗೆ ಗೊತ್ತಿದ್ದುದರಿಂದ ಅವನು ತನ್ನ ಜನರನ್ನು ಮೂರು ಗುಂಪು ಮಾಡಿ ಅವರನ್ನು ಕರೆದುಕೊಂಡು ಬಂದು ಹೊಲಗಳಲ್ಲಿ ಹೊಂಚಿ ನೋಡುತ್ತಿದ್ದನು. ಜನರು ಪಟ್ಟಣದಿಂದ ಹೊರಟು ಬರುವುದನ್ನು ಇವನು ಕಾಣುತ್ತಲೇ, ಅಲ್ಲಿಂದ ಎದ್ದು ಅವರನ್ನು ಹೊಡೆದನು.",
			"44": "<sup>44</sup> ಅಬೀಮೆಲೆಕನೂ ಅವನ ಸಂಗಡ ಇದ್ದ ಗುಂಪಿನವರೂ ಓಡಿ ಬಂದು ಊರು ಬಾಗಿಲಲ್ಲಿ ನಿಂತರು; ಉಳಿದ ಎರಡು ಗುಂಪಿನವರು ಬೈಲಿನಲ್ಲಿದ್ದ ಶೆಕೆಮಿನವರ ಮೇಲೆ ಬಿದ್ದು ಅವರನ್ನು ಹತಮಾಡಿದರು.",
			"45": "<sup>45</sup> ಅಬೀಮೆಲೆಕನು ಆ ದಿನವೆಲ್ಲಾ ಯುದ್ಧಮಾಡಿ, ಪಟ್ಟಣವನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡು, ಅದರ ಜನರನ್ನೆಲ್ಲಾ ಸಂಹರಿಸಿ, ಊರನ್ನು ಕೆಡವಿಬಿಟ್ಟು, ಅಲ್ಲಿ ಉಪ್ಪನ್ನು ಎರಚಿಬಿಟ್ಟನು.",
			"46": "<sup>46</sup> ಶೆಕೆಮಿನ ಕೋಟೆಯವರು ಇದನ್ನು ಕೇಳಿ ಏಲ್‌ಬೆರೀತೆ ಎಂಬ ದೇವರ ಗುಡಿಯ ನೆಲಮನೆಯಲ್ಲಿ ಅಡಗಿಕೊಂಡರು.",
			"47": "<sup>47</sup> ಕೋಟೆಯವರೆಲ್ಲರೂ ಅಲ್ಲಿ ಅಡಗಿಕೊಂಡಿದ್ದಾರೆಂಬುದು ಅಬೀಮೆಲೆಕನಿಗೆ ಗೊತ್ತಾದಾಗ",
			"48": "<sup>48</sup> ಅವನು ಕೊಡಲಿಯನ್ನು ತೆಗೆದುಕೊಂಡು ತನ್ನ ಜನರೆಲ್ಲರೊಡನೆ ಚಲ್ಮೋನ್ ಗುಡ್ಡವನ್ನೇರಿ ತನ್ನವರಿಗೆ, “ನಾನು ಮಾಡುವಂತೆಯೇ ನೀವೂ ತೀವ್ರವಾಗಿ ಮಾಡಿರಿ” ಎಂದು ಹೇಳಿ ತಾನು ಒಂದು ಮರದ ಕೊಂಬೆಯನ್ನು ಕಡಿದು ಹೆಗಲ ಮೇಲೆ ಹೊತ್ತುಕೊಳ್ಳಲು",
			"49": "<sup>49</sup> ಅವರೂ ಅದರಂತೆಯೇ ಮಾಡಿ, ಅವನ ಹಿಂದೆ ಹೋಗಿ, ಆ ಕೊಂಬೆಗಳನ್ನು ಅವರಿದ್ದ ನೆಲಮನೆಯ ಸುತ್ತಲೂ ಹಾಕಿ, ಬೆಂಕಿಹೊತ್ತಿಸಿ ಅದನ್ನು ಸುಟ್ಟುಬಿಟ್ಟರು. ಹೀಗೆ ಶೆಕೆಮ್‌ ಕೋಟೆಯವರೆಲ್ಲರೂ ಸತ್ತರು. ಅವರ ಸ್ತ್ರೀಪುರುಷರೆಲ್ಲಾ ಸುಮಾರು ಸಾವಿರ ಜನರಿದ್ದರು.",
			"50": "<sup>50</sup> ಅನಂತರ ಅಬೀಮೆಲೆಕನು ತೇಬೇಚಿಗೆ ಹೋಗಿ ಮುತ್ತಿಗೆಹಾಕಿ ಅದನ್ನು ಹಿಡಿದನು.",
			"51": "<sup>51</sup> ಆ ಊರಿನ ಮಧ್ಯದಲ್ಲಿ ಒಂದು ಭದ್ರವಾದ ಬುರುಜು ಇತ್ತು; ಊರಿನ ಸ್ತ್ರೀಪುರುಷರೆಲ್ಲರೂ ಓಡಿಹೋಗಿ, ಅದರೊಳಗೆ ಹೊಕ್ಕು, ಬಾಗಿಲನ್ನು ಮುಚ್ಚಿಕೊಂಡು ಮೇಲೆ ಹತ್ತಿದರು.",
			"52": "<sup>52</sup> ಅಬೀಮೆಲೆಕನು ಅಲ್ಲಿಗೆ ಬಂದು ಯುದ್ಧಮಾಡುತ್ತಾ ಬುರುಜಿನ ಬಾಗಿಲಿಗೆ ಬೆಂಕಿ ಹೊತ್ತಿಸಬೇಕೆಂದು ಅದರ ಸಮೀಪಕ್ಕೆ ಹೋಗಲು,",
			"53": "<sup>53</sup> ಒಬ್ಬ ಸ್ತ್ರೀಯು ಮೇಲಿನಿಂದ ಒಂದು ಬೀಸುವ ಕಲ್ಲನ್ನು ಬೀಳಿಸಿ, ಅವನ ತಲೆಬುರುಡೆಯನ್ನು ಒಡೆದುಬಿಟ್ಟಳು.",
			"54": "<sup>54</sup> ಅವನು ಕೂಡಲೆ ತನ್ನ ಆಯುಧ ಹೊರುವವನನ್ನು ಕರೆದು ಅವನಿಗೆ, “ಕತ್ತಿಯನ್ನು ಹಿರಿದು ನನ್ನನ್ನು ಕೊಲ್ಲು; ಇಲ್ಲವಾದರೆ ನಾನು ಹೆಂಗಸಿನ ಕೈಯಿಂದ ಸತ್ತನೆಂದು ಜನರು ಹೇಳಾರು” ಎನ್ನಲು ಆ ಪ್ರಾಯಸ್ಥನು ಅವನನ್ನು ತಿವಿದು ಕೊಂದುಹಾಕಿದನು.",
			"55": "<sup>55</sup> ಅಬೀಮೆಲೆಕನು ಸತ್ತು ಹೋದದ್ದನ್ನು ಕಂಡು ಇಸ್ರಾಯೇಲರು ತಮ್ಮ ತಮ್ಮ ಸ್ಥಳಗಳಿಗೆ ಹಿಂದಿರುಗಿದರು.",
			"56": "<sup>56</sup> ಎಪ್ಪತ್ತು ಮಂದಿ ಸಹೋದರರನ್ನು ಕೊಂದುಹಾಕಿ, ತನ್ನ ತಂದೆಗೆ ದ್ರೋಹಿಯಾದ ಅಬೀಮೆಲೆಕನಿಗೆ ದೇವರು ಈ ರೀತಿಯಾಗಿ ಮುಯ್ಯಿತೀರಿಸಿದನು.",
			"57": "<sup>57</sup> ಶೆಕೆಮಿನವರಿಗಾದರೋ ಅವರ ದುಷ್ಟತನವನ್ನು ಅವರ ತಲೆಯ ಮೇಲೆಯೇ ಬರಮಾಡಿದನು. ಹೀಗೆ ಯೆರುಬ್ಬಾಳನ ಮಗನಾದ ಯೋತಾಮನು ಇವರ ವಿಷಯದಲ್ಲಿ ನುಡಿದ ಶಾಪವು ನೆರವೇರಿತು."
		},
		"10": {
			"1": "<sup>1</sup> ಅಬೀಮೆಲೆಕನ ತರುವಾಯ ಇಸ್ಸಾಕಾರ್ ಕುಲದ ಪೂವನ ಮಗನೂ, ದೋದೋವಿನ ಮೊಮ್ಮಗನೂ ಆಗಿದ್ದ ತೋಲನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ರಕ್ಷಿಸಿವುದಕ್ಕೋಸ್ಕರ ಎದ್ದನು. ಅವನು ಎಫ್ರಾಯಿಮ್ ಬೆಟ್ಟದ ಸೀಮೆಯ ಶಾಮೀರೆಂಬ ಊರಲ್ಲಿ ವಾಸವಾಗಿದ್ದನು.",
			"2": "<sup>2</sup> ಇಪ್ಪತ್ಮೂಮೂರು ವರುಷಗಳವರೆಗೆ ಇಸ್ರಾಯೇಲರನ್ನು ಪಾಲಿಸಿದನಂತರ, ಅವನು ಸತ್ತು ಶಾಮೀರಿನಲ್ಲಿ ಹೂಣಲ್ಪಟ್ಟನು.",
			"3": "<sup>3</sup> ಅವನ ತರುವಾಯ ಗಿಲ್ಯಾದ್ಯನಾದ ಯಾಯೀರನು ಎದ್ದು ಇಸ್ರಾಯೇಲರನ್ನು ಇಪ್ಪತ್ತೆರಡು ವರುಷ ಪಾಲಿಸಿದನು.",
			"4": "<sup>4</sup> ಇವನಿಗೆ ಮೂವತ್ತು ಮಂದಿ ಮಕ್ಕಳಿದ್ದರು. ಇವರಿಗೆ ಮೂವತ್ತು ಸವಾರಿ ಕತ್ತೆಗಳೂ, ಗಿಲ್ಯಾದ್ ದೇಶದಲ್ಲಿ ಮೂವತ್ತು ಊರುಗಳೂ ಇದ್ದವು. ಅವುಗಳಿಗೆ ಇಂದಿನ ವರೆಗೂ ಯಾಯೀರನ ಗ್ರಾಮಗಳೆಂದು ಹೆಸರಿದೆ.",
			"5": "<sup>5</sup> ಯಾಯೀರನು ಸತ್ತು ಕಾಮೋನಿನಲ್ಲಿ ಹೂಣಲ್ಪಟ್ಟನು.",
			"6": "<sup>6</sup> ಇಸ್ರಾಯೇಲರು ಪುನಃ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಗಳಾದರು. ಅವರು ಆತನನ್ನು ಬಿಟ್ಟು ಬಾಳ್, ಅಷ್ಟೋರೆತ್ ಎಂಬ ದೇವತೆಗಳನ್ನೂ, ಅರಾಮ್ಯರು, ಚೀದೋನ್ಯರು, ಮೋವಾಬ್ಯರು, ಅಮ್ಮೋನಿಯರು, ಫಿಲಿಷ್ಟಿಯರು ಇವರ ದೇವತೆಗಳನ್ನೂ ಪೂಜಿಸತೊಡಗಿದರು. ಯೆಹೋವನನ್ನು ಮರೆತು ಆತನನ್ನು ಆರಾಧಿಸುವುದನ್ನು ಬಿಟ್ಟೇಬಿಟ್ಟರು.",
			"7": "<sup>7</sup> ಆದುದರಿಂದ ಆತನು ಇಸ್ರಾಯೇಲರ ಮೇಲೆ ಕೋಪಗೊಂಡು ಅವರನ್ನು ಫಿಲಿಷ್ಟಿಯರ ಮತ್ತು ಅಮ್ಮೋನಿಯರ ಕೈಗೆ ಒಪ್ಪಿಸಿಬಿಟ್ಟನು.",
			"8": "<sup>8</sup> ಇವರು ಅಂದಿನಿಂದ ಹದಿನೆಂಟು ವರುಷಗಳ ವರೆಗೆ ಯೊರ್ದನಿನ ಆಚೆ ಗಿಲ್ಯಾದಿನಲ್ಲಿದ್ದ ಇಸ್ರಾಯೇಲರನ್ನು ಬಹಳವಾಗಿ ಪೀಡಿಸುತ್ತಾ, ಕುಗ್ಗಿಸುತ್ತಾ ಇದ್ದರು. ಈ ದೇಶವು ಮೊದಲು ಅಮೋರಿಯರದಾಗಿತ್ತು.",
			"9": "<sup>9</sup> ಇದಲ್ಲದೆ ಅಮ್ಮೋನಿಯರು ಯೊರ್ದನ್ ಹೊಳೆಯನ್ನು ದಾಟಿ, ಯೆಹೂದ, ಬೆನ್ಯಾಮೀನ್, ಎಫ್ರಾಯಿಮ್ ಕುಲಗಳೊಡನೆ ಯುದ್ಧಮಾಡಿದ್ದರಿಂದ ಇಸ್ರಾಯೇಲರಿಗೆ ಬಹಳ ಕಷ್ಟವಾಯಿತು.",
			"10": "<sup>10</sup> ಆಗ ಅವರು ಯೆಹೋವನಿಗೆ, “ನಾವು ನಮ್ಮ ದೇವರಾದ ನಿನ್ನನ್ನು ಬಿಟ್ಟು, ಬಾಳನ ಪ್ರತಿಮೆಗಳನ್ನು ಪೂಜಿಸಿ, ನಿನಗೆ ವಿರುದ್ಧವಾಗಿ ಪಾಪಮಾಡಿದ್ದೇವೆ” ಎಂದು ಮೊರೆಯಿಟ್ಟರು.",
			"11": "<sup>11</sup> ಆತನು ಅವರಿಗೆ, “ಐಗುಪ್ತ, ಅಮೋರಿಯ, ಅಮ್ಮೋನಿಯ, ಫಿಲಿಷ್ಟಿಯ, ಚೀದೋನ್ಯ, ಅಮಾಲೇಕ್ಯ, ಮಾವೋನ್ಯ ಇವೇ",
			"12": "<sup>12</sup> ಮೊದಲಾದ ಜನಾಂಗಗಳು ನಿಮ್ಮನ್ನು ಪೀಡಿಸಿದಾಗ ನೀವು ನನಗೆ ಮೊರೆಯಿಟ್ಟಿರಿ; ನಾನು ಅವರಿಂದ ನಿಮ್ಮನ್ನು ಬಿಡಿಸಿದರೂ",
			"13": "<sup>13</sup> ನೀವು ಪುನಃ ನನ್ನನ್ನು ಬಿಟ್ಟು, ಅನ್ಯದೇವತೆಗಳನ್ನು ಸೇವಿಸುತ್ತಾ ಬಂದಿರಿ. ಆದುದರಿಂದ ನಾನು ಇನ್ನು ಮುಂದೆ ನಿಮ್ಮನ್ನು ರಕ್ಷಿಸುವುದೇ ಇಲ್ಲ.",
			"14": "<sup>14</sup> ಹೋಗಿ, ನೀವು ಆರಿಸಿಕೊಂಡ ದೇವತೆಗಳಿಗೆ ಮೊರೆಯಿಡಿರಿ; ಅವು ನಿಮ್ಮನ್ನು ಈ ಇಕ್ಕಟ್ಟಿನಿಂದ ಬಿಡಿಸಲಿ” ಅಂದನು.",
			"15": "<sup>15</sup> ಅವರು ತಿರುಗಿ ಯೆಹೋವನಿಗೆ, “ನಾವು ಪಾಪ ಮಾಡಿದ್ದೇವೆ; ನಿನಗೆ ಸರಿಕಾಣುವ ಪ್ರಕಾರ ನಮಗೆ ಮಾಡು; ಆದರೆ ಈ ಸಾರಿ ಹೇಗೂ ನಮ್ಮನ್ನು ರಕ್ಷಿಸಬೇಕು” ಎಂದು ಮೊರೆಯಿಟ್ಟು ಅನ್ಯದೇವತೆಗಳನ್ನು ತಮ್ಮಲ್ಲಿಂದ ತೆಗೆದು ಹಾಕಿ ಯೆಹೋವನನ್ನು ಸೇವಿಸುವವರಾದರು.",
			"16": "<sup>16</sup> ಆಗ ಆತನ ಮನಸ್ಸು ಇಸ್ರಾಯೇಲರ ಸಂಕಟದ ನಿಮಿತ್ತ ಬಹಳವಾಗಿ ನೊಂದಿತು.",
			"17": "<sup>17</sup> ಅಮ್ಮೋನಿಯರು ದಂಡೆತ್ತಿ ಬಂದು ಗಿಲ್ಯಾದಿನಲ್ಲಿ ಇಳಿದುಕೊಳ್ಳಲು ಇಸ್ರಾಯೇಲರು ಸೇರಿ ಮಿಚ್ಪೆಯಲ್ಲಿ ಪಾಳೆಯಮಾಡಿಕೊಂಡರು.",
			"18": "<sup>18</sup> ಗಿಲ್ಯಾದಿನ ಜನರೂ ಅಧಿಪತಿಗಳೂ, “ನಮ್ಮಲ್ಲಿ ಅಮ್ಮೋನಿಯರೊಡನೆ ಯುದ್ಧಕ್ಕೆ ಕೈಹಾಕುವವನು ಯಾರು? ಅಂಥವನನ್ನು ಗಿಲ್ಯಾದಿನವರೆಲ್ಲರ ಮೇಲೆ ನಾಯಕನನ್ನಾಗಿ ಮಾಡುವೆವು” ಎಂದು ತಮ್ಮ ತಮ್ಮೊಳಗೆ ಮಾತನಾಡಿಕೊಂಡರು."
		},
		"11": {
			"1": "<sup>1</sup> ಗಿಲ್ಯಾದ್ಯನಾದ ಯೆಪ್ತಾಹನು ಮಹಾಪರಾಕ್ರಮಿಯಾಗಿದ್ದನು. ಅವನು ಗಿಲ್ಯಾದನಿಂದ ಒಬ್ಬ ವೇಶ್ಯೆಗೆ ಹುಟ್ಟಿದವನು.",
			"2": "<sup>2</sup> ಗಿಲ್ಯಾದನಿಗೆ ಹೆಂಡತಿಗೂ ಮಕ್ಕಳು ಹುಟ್ಟಿದ್ದರು. ಇವರು ದೊಡ್ಡವರಾದ ಮೇಲೆ ಯೆಪ್ತಾಹನಿಗೆ, “ನೀನು ಪರಸ್ತ್ರೀಯಿಂದ ಹುಟ್ಟಿದವನು; ಆದುದರಿಂದ ನಿನಗೆ ನಮ್ಮ ತಂದೆಯ ಮನೆಯಲ್ಲಿ ಬಾಧ್ಯತೆ ಸಿಕ್ಕಲಾರದು” ಎಂದು ಹೇಳಿ ಅವನನ್ನು ಹೊರಗೆ ಹಾಕಿದರು.",
			"3": "<sup>3</sup> ಅವನು ತನ್ನ ಸಹೋದರರನ್ನು ಬಿಟ್ಟು ಟೋಬ್ ದೇಶಕ್ಕೆ ಹೋಗಲು ಅಲ್ಲಿನ ಕಾಕಪೋಕರು ಕೂಡಿ ಅವನನ್ನು ಹಿಂಬಾಲಿಸಿದರು.",
			"4": "<sup>4</sup> ಸ್ವಲ್ಪಕಾಲದ ನಂತರ ಇಸ್ರಾಯೇಲರಿಗೆ ವಿರೋಧವಾಗಿ ಬಂದ ಅಮ್ಮೋನಿಯರು ಅವರ ಮೇಲೆ ಯುದ್ಧವನ್ನು ಪ್ರಾರಂಭಿಸಿದರು.",
			"5": "<sup>5</sup> ಆಗ ಗಿಲ್ಯಾದಿನ ಹಿರಿಯರು ಯೆಪ್ತಾಹನನ್ನು ಟೋಬ್ ದೇಶದಿಂದ ಕರೆದುಕೊಂಡು ಬರುವುದಕ್ಕಾಗಿ ಅಲ್ಲಿಗೆ ಹೋದರು.",
			"6": "<sup>6</sup> ಅವರು ಅವನಿಗೆ, “ನಾವು ಅಮ್ಮೋನಿಯರೊಡನೆ ಯುದ್ಧಮಾಡುವ ಹಾಗೆ ನೀನು ಬಂದು ನಮ್ಮ ನಾಯಕನಾಗು” ಎಂದು ಬೇಡಿಕೊಂಡರು.",
			"7": "<sup>7</sup> ಅವನು ಅವರಿಗೆ, “ನನ್ನನ್ನು ಹಗೆಮಾಡಿ, ನನ್ನ ತಂದೆಯ ಮನೆಯಿಂದ ಓಡಿಸಿಬಿಟ್ಟವರು ನೀವೇ ಅಲ್ಲವೋ? ನಿಮಗೆ ಕಷ್ಟ ಬಂದಾಗ ನನ್ನ ಬಳಿಗೆ ಯಾಕೆ ಬಂದಿರಿ?” ಎನ್ನಲು",
			"8": "<sup>8</sup> ಅವರು, “ಆದುದರಿಂದಲೇ ಈಗ ತಿರುಗಿ ನಿನ್ನ ಬಳಿಗೆ ಬಂದಿದ್ದೇವೆ; ನಮ್ಮ ಸಂಗಡ ಬಂದು ಅಮ್ಮೋನಿಯರೊಡನೆ ಯುದ್ಧಮಾಡುವುದಾದರೆ ಗಿಲ್ಯಾದಿನವರಿಗೆಲ್ಲಾ ನೀನೇ ನಾಯಕನಾಗಿರುವಿ” ಎಂದು ಹೇಳಿದರು.",
			"9": "<sup>9</sup> ಆಗ ಯೆಪ್ತಾಹನು ಅವರಿಗೆ, “ನೀವು ನನ್ನನ್ನು ಅಮ್ಮೋನಿಯರೊಡನೆ ಯುದ್ಧಮಾಡುವುದಕ್ಕೋಸ್ಕರ ಕರೆದುಕೊಂಡು ಹೋಗುವಲ್ಲಿ ಯೆಹೋವನು ಅವರನ್ನು ನನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸಿಕೊಟ್ಟರೆ ನಿಜವಾಗಿ ನನ್ನನ್ನು ಅಧಿಪತಿಯನ್ನಾಗಿ ಮಾಡುವಿರೋ?” ಎಂದು ಕೇಳಲು",
			"10": "<sup>10</sup> ಅವರು ಅವನಿಗೆ, “ಹಾಗೆಯೇ ಮಾಡುವೆವು; ನಮ್ಮಿಬ್ಬರ ಈ ಮಾತುಗಳಿಗೆ ಯೆಹೋವನೇ ಸಾಕ್ಷಿ” ಎಂದು ಉತ್ತರಕೊಟ್ಟರು.",
			"11": "<sup>11</sup> ಆಗ ಯೆಪ್ತಾಹನು ಗಿಲ್ಯಾದಿನ ಹಿರಿಯರ ಸಂಗಡ ಹೋದನು. ಜನರು ಅವನನ್ನು ಅಧಿಪತಿಯನ್ನಾಗಿಯೂ, ನಾಯಕನನ್ನಾಗಿಯೂ ನೇಮಿಸಿದರು. ಮತ್ತು ಯೆಪ್ತಾಹನು ತನ್ನ ಸಂಗತಿಗಳನ್ನೆಲ್ಲಾ ಮಿಚ್ಚೆಯಲ್ಲಿ ಯೆಹೋವನ ಮುಂದೆ ಅರಿಕೆಮಾಡಿದನು.",
			"12": "<sup>12</sup> ಯೆಪ್ತಾಹನು ಅಮ್ಮೋನಿಯರ ಅರಸನ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿ ಅವನಿಗೆ, “ನನಗೂ ನಿನಗೂ ಏನಿದೆ? ನೀನು ನನ್ನ ದೇಶಕ್ಕೆ ವಿರೋಧವಾಗಿ ಯುದ್ಧಮಾಡುವುದಕ್ಕೆ ಏನು ಕಾರಣ?” ಎಂದು ಕೇಳಿದನು.",
			"13": "<sup>13</sup> ಆ ಅರಸನು ದೂತರಿಗೆ, “ಇಸ್ರಾಯೇಲರು ಐಗುಪ್ತದಿಂದ ಬಂದಾಗ ಅರ್ನೋನಿನಿಂದ ಯಬ್ಬೋಕ್ ಮತ್ತು ಯೋರ್ದನ್ ಹೊಳೆಗಳವರೆಗೂ ಇದ್ದ ನನ್ನ ದೇಶವನ್ನು ವಶಪಡಿಸಿಕೊಂಡರಲ್ಲಾ; ನೀನು ಈಗ ಅದನ್ನು ಸಮಾಧಾನದಿಂದ ಹಿಂದಕ್ಕೆ ಕೊಡು” ಎಂದು ಹೇಳಿ ಕಳುಹಿಸಿದನು.",
			"14": "<sup>14</sup> ಯೆಪ್ತಾಹನು ಇನ್ನೊಂದು ಸಾರಿ ಅಮ್ಮೋನಿಯರ ಅರಸನ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿ ಅವನಿಗೆ,",
			"15": "<sup>15</sup> “ಯೆಫ್ತಾಹನಾದ ನನ್ನ ಮಾತನ್ನು ಕೇಳು; ಇಸ್ರಾಯೇಲರು ಮೋವಾಬ್ಯರ ಮತ್ತು ಅಮ್ಮೋನಿಯರ ದೇಶವನ್ನು ತೆಗೆದುಕೊಳ್ಳಲೇ ಇಲ್ಲ.",
			"16": "<sup>16</sup> ಅವರು ಐಗುಪ್ತವನ್ನು ಬಿಟ್ಟನಂತರ ಅರಣ್ಯದಲ್ಲಿ ಸಂಚರಿಸಿ, ಕೆಂಪು ಸಮುದ್ರಕ್ಕೂ ಅನಂತರ ಕಾದೇಶಿಗೂ ಬಂದರು.",
			"17": "<sup>17</sup> ಅವರು ಅಲ್ಲಿಂದ ಎದೋಮ್ಯರ ಅರಸನ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿ, ‘ನಿನ್ನ ದೇಶವನ್ನು ದಾಟಿಹೋಗುವುದಕ್ಕೆ ಅಪ್ಪಣೆಯಾಗಬೇಕು’ ಎಂದು ಬೇಡಿಕೊಳ್ಳಲು ಅವನು ಒಪ್ಪಿಕೊಳ್ಳಲಿಲ್ಲ. ತರುವಾಯ ಮೋವಾಬ್ಯರ ಅರಸನ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಲು ಅವನೂ ಒಪ್ಪಿಕೊಳ್ಳಲಿಲ್ಲ; ಆದುದರಿಂದ ಅವರು ಕಾದೇಶಿನಲ್ಲಿಯೇ ಉಳಿದುಕೊಂಡರು.",
			"18": "<sup>18</sup> ಅನಂತರ ಅವರು ಅರಣ್ಯದಲ್ಲಿ ಪ್ರಯಾಣಮಾಡಿ ಎದೋಮ್, ಮೋವಾಬ್ ದೇಶಗಳನ್ನು ಸುತ್ತಿಕೊಂಡು ಮೋವಾಬ್‍ದೇಶದ ಪೂರ್ವದಿಕ್ಕಿಗೆ ಬಂದು, ಅದರ ಮೇರೆಯಾಗಿರುವ ಅರ್ನೋನ್ ಹೊಳೆಯ ಆಚೆಯಲ್ಲಿ ಇಳಿದುಕೊಂಡರು. ಮೋವಾಬ್ಯರ ಮೇರೆಯೊಳಗೆ ಕಾಲಿಡಲಿಲ್ಲ.",
			"19": "<sup>19</sup> ತರುವಾಯ ಅವರು ಹೆಷ್ಬೋನನ್ನು ರಾಜಧಾನಿಮಾಡಿಕೊಂಡಿದ್ದ ಅಮೋರಿಯರ ಅರಸನಾದ ಸೀಹೋನನ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿ, ‘ನಿನ್ನ ದೇಶವನ್ನು ದಾಟಿ ನಮ್ಮ ದೇಶಕ್ಕೆ ಹೋಗುವುದಕ್ಕೆ ಅಪ್ಪಣೆಕೊಡು’ ಎಂದು ಅವನನ್ನು ಬೇಡಿಕೊಂಡರು.",
			"20": "<sup>20</sup> ಆದರೆ ಸೀಹೋನನು ಅವರನ್ನು ನಂಬದೆ ತನ್ನ ಸೀಮೆಯನ್ನು ದಾಟಿಹೋಗುವುದಕ್ಕೆ ಅಪ್ಪಣೆಕೊಡದೆ ತನ್ನ ಜನರನ್ನು ಕೂಡಿಸಿಕೊಂಡು ಯಹಚಕ್ಕೆ ಬಂದು ಅವರೊಡನೆ ಯುದ್ಧಮಾಡಿದನು.",
			"21": "<sup>21</sup> ಇಸ್ರಾಯೇಲರ ದೇವರಾದ ಯೆಹೋವನು ಸೀಹೋನನನ್ನೂ, ಅವನ ಜನರೆಲ್ಲರನ್ನೂ ಇಸ್ರಾಯೇಲರ ಕೈಗೆ ಒಪ್ಪಿಸಿದ್ದರಿಂದ ಅವರು ಈ ದೇಶದ ನಿವಾಸಿಗಳಾದ ಅಮೋರಿಯರನ್ನು ಹೊಡೆದು, ಅವರ ದೇಶವನ್ನೆಲ್ಲಾ ಸ್ವದೀನಪಡಿಸಿಕೊಂಡರು.",
			"22": "<sup>22</sup> ಹೀಗೆ ಇಸ್ರಾಯೇಲರು ಅರ್ನೋನಿನಿಂದ ಯಬ್ಬೋಕಿನವರೆಗೂ, ಅರಣ್ಯದಿಂದ ಯೊರ್ದನಿನವರೆಗೂ ಇದ್ದ ಅಮೋರಿಯರ ಪ್ರಾಂತ್ಯವನ್ನೆಲ್ಲಾ ವಶಮಾಡಿಕೊಂಡರು.",
			"23": "<sup>23</sup> ಇಸ್ರಾಯೇಲರ ದೇವರಾದ ಯೆಹೋವನು ಈ ದೇಶವನ್ನು ಅಮೋರಿಯರಿಂದ ತೆಗೆದು ತನ್ನ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಾಯೇಲರಿಗೆ ಕೊಟ್ಟ ಮೇಲೆ ನೀನು ಅದನ್ನು ತೆಗೆದುಕೊಳ್ಳುವುದು ಹೇಗೆ?",
			"24": "<sup>24</sup> ನಿನ್ನ ದೇವನಾದ ಕೆಮೋಷನು ನಿನಗೆ ಕೊಡುವ ದೇಶಗಳನ್ನು ನೀನು ಸ್ವದೀನಪಡಿಸಿಕೊಳ್ಳುವುದಿಲ್ಲವೋ? ಹಾಗೆಯೇ ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಮಗೆ ಒಪ್ಪಿಸಿಕೊಡುವುದನ್ನು ನಾವು ಸ್ವದೀನಪಡಿಸಿಕೊಳ್ಳುವುದು ನ್ಯಾಯವಾಗಿದೆ.",
			"25": "<sup>25</sup> ಚಿಪ್ಪೋರನ ಮಗನೂ ಮೋವಾಬ್ಯರ ಅರಸನೂ ಆದ ಬಾಲಾಕನಿಗಿಂತ ನೀನು ಹೆಚ್ಚಿನವನೋ? ಅವನು ಇಸ್ರಾಯೇಲರೊಡನೆ ಎಂದಾದರೂ ವಿವಾದಮಾಡಿದನೋ? ಅಥವಾ ಅವರಿಗೆ ವಿರೋಧವಾಗಿ ಯುದ್ಧಕ್ಕೆ ಬಂದನೋ?",
			"26": "<sup>26</sup> ಇಸ್ರಾಯೇಲರು ಮುನ್ನೂರು ವರುಷಗಳಿಂದ ಹೆಷ್ಬೋನ್, ಅರೋಯೇರ್ ಎಂಬ ಪಟ್ಟಣಗಳಲ್ಲಿಯೂ, ಅವುಗಳ ಗ್ರಾಮಗಳಲ್ಲಿಯೂ, ಅರ್ನೋನ್ ತೀರದ ಎಲ್ಲಾ ಪಟ್ಟಣಗಳಲ್ಲಿಯೂ ವಾಸಿಸುತ್ತಿದ್ದಾರಲ್ಲಾ. ಇಷ್ಟು ದಿನಗಳವರೆಗೆ ನೀನು ಅದನ್ನು ಬಿಡಿಸಿಕೊಳ್ಳದೆ ಇದ್ದದ್ದೇಕೆ?",
			"27": "<sup>27</sup> ನಾನು ನಿನಗೆ ಯಾವ ಅನ್ಯಾಯವನ್ನೂ ಮಾಡಲಿಲ್ಲ. ನೀನು ಈಗ ನನಗೆ ವಿರೋಧವಾಗಿ ಯುದ್ಧಕ್ಕೆ ಬಂದದ್ದರಿಂದ ನೀನೇ ಅನ್ಯಾಯ ಮಾಡಿದ ಹಾಗಾಯಿತು. ನ್ಯಾಯಸ್ಥಾಪಕನಾದ ಯೆಹೋವನೇ ಈ ಹೊತ್ತು ಇಸ್ರಾಯೇಲರ ಮತ್ತು ಅಮ್ಮೋನಿಯರ ಮಧ್ಯದಲ್ಲಿ ನ್ಯಾಯತೀರಿಸಲಿ” ಎಂದು ಹೇಳಿಸಿದನು.",
			"28": "<sup>28</sup> ಆದರೆ ಯೆಪ್ತಾಹನು ಹೇಳಿಕಳುಹಿಸಿದ ಮಾತುಗಳಿಗೆ ಅಮ್ಮೋನಿಯರ ಅರಸನು ಕಿವಿಗೊಡಲಿಲ್ಲ.",
			"29": "<sup>29</sup> ಆಗ ಯೆಹೋವನ ಆತ್ಮವು ಯೆಪ್ತಾಹನ ಮೇಲೆ ಬಂದಿತು. ಅವನು ಗಿಲ್ಯಾದ್ ಪ್ರಾಂತ್ಯ, ಮನಸ್ಸೆಯ ದೇಶ ಇವುಗಳಲ್ಲಿ ಸಂಚರಿಸಿ ತಿರುಗಿ ಗಿಲ್ಯಾದಿನ ಮಿಚ್ಚೆಗೆ ಬಂದು, ಅಲ್ಲಿಂದ ಅಮ್ಮೋನಿಯರ ಮೇಲೆ ಯುದ್ಧಕ್ಕೆ ಹೋದನು.",
			"30": "<sup>30</sup> ಇದಲ್ಲದೆ ಅವನು ಯೆಹೋವನಿಗೆ, “ನೀನು ಅಮ್ಮೋನಿಯರನ್ನು ನನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸುವುದಾದರೆ",
			"31": "<sup>31</sup> ನಾನು ಸುರಕ್ಷಿತವಾಗಿ ಮನೆಗೆ ಸೇರುವಾಗ ನನ್ನನ್ನು ಎದುರುಗೊಳ್ಳುವುದಕ್ಕಾಗಿ ನನ್ನ ಮನೆಯ ಬಾಗಿಲಿನಿಂದ ಮೊದಲು ಬರುವಂಥ ಪ್ರಾಣಿಯು ನಿನ್ನದೇ ಎಂದು ಅದನ್ನು ನಿನಗೋಸ್ಕರ ಯಜ್ಞವಾಗಿ ಅರ್ಪಿಸುವೆನು” ಎಂದು ಹರಕೆಮಾಡಿದನು.",
			"32": "<sup>32</sup> ಯೆಪ್ತಾಹನು ಅಮ್ಮೋನಿಯರಿಗೆ ವಿರೋಧವಾಗಿ ಯುದ್ಧಕ್ಕೆ ಹೋಗಲು ಯೆಹೋವನು ಅವರನ್ನು ಅವನ ಕೈಗೆ ಒಪ್ಪಿಸಿದನು.",
			"33": "<sup>33</sup> ಅವನು ಅವರನ್ನು ಸೋಲಿಸಿ ಅರೋಯೇರಿನಿಂದ ಮಿನ್ನೀತಿನ ದಾರಿಯವರೆಗೂ ಆಬೇಲ್ ಕೆರಾಮೀಮಿನವರೆಗೂ ಇಪ್ಪತ್ತು ಪಟ್ಟಣಗಳನ್ನು ಹಾಳುಮಾಡಿಬಿಟ್ಟನು. ಹೀಗೆ ದೊಡ್ಡ ಜಯವಾಗಿ ಅಮ್ಮೋನಿಯರು ಇಸ್ರಾಯೇಲರ ಮುಂದೆ ತಗ್ಗಿಸಲ್ಪಟ್ಟರು.",
			"34": "<sup>34</sup> ಯೆಪ್ತಾಹನು ಮಿಚ್ಚೆಯಲ್ಲಿದ್ದ ತನ್ನ ಮನೆಯನ್ನು ಸಮೀಪಿಸಿದಾಗ ಅವನ ಮಗಳು ದಮ್ಮಡಿಬಡಿಯುತ್ತಾ, ನೃತ್ಯಮಾಡುತ್ತಾ ಅವನನ್ನು ಎದುರುಗೊಳ್ಳುವುದಕ್ಕಾಗಿ ಬಂದಳು. ಆಕೆಯು ಅವನಿಗೆ ಒಬ್ಬಳೇ ಮಗಳಾಗಿದ್ದಳು. ಆಕೆಯ ಹೊರತು ಅವನಿಗೆ ಬೇರೆ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳೇ ಇರಲಿಲ್ಲ.",
			"35": "<sup>35</sup> ಆಕೆಯನ್ನು ಕಾಣುತ್ತಲೇ ಅವನು ತನ್ನ ಬಟ್ಟೆಯನ್ನು ಹರಿದುಕೊಂಡು, “ಅಯ್ಯೋ, ನನ್ನ ಮಗಳೇ, ನೀನು ನನ್ನನ್ನು ಕುಗ್ಗಿಸೇಬಿಟ್ಟೆಯಲ್ಲಾ; ನನಗೆ ಮಹಾಸಂಕಟವನ್ನು ಉಂಟುಮಾಡಿದಿ. ನಾನು ಬಾಯ್ದೆರೆದು ಯೆಹೋವನಿಗೆ ಹರಕೆಮಾಡಿದ್ದೇನೆ; ಅದಕ್ಕೆ ಹಿಂದೆಗೆಯಲಾರೆನು” ಎಂದು ಕೂಗಿಕೊಳ್ಳಲು",
			"36": "<sup>36</sup> ಆಕೆಯು ಅವನಿಗೆ, “ನನ್ನ ತಂದೆಯೇ, ನೀನು ಬಾಯ್ದೆರೆದು ಯೆಹೋವನಿಗೆ ಹರಕೆಮಾಡಿದ ಮೇಲೆ ಆತನು ನಿನ್ನ ಶತ್ರುಗಳಾದ ಅಮ್ಮೋನಿಯರಿಗೆ ಮುಯ್ಯಿತೀರಿಸಿದ್ದರಿಂದ ನಿನ್ನ ಬಾಯಿಂದ ಹೊರಟದ್ದನ್ನೇ ನೆರವೇರಿಸು” ಎಂದಳು.",
			"37": "<sup>37</sup> ಆಕೆಯು ತಿರುಗಿ ತನ್ನ ತಂದೆಗೆ, “ನನ್ನ ಬಿನ್ನಹವನ್ನು ಲಾಲಿಸು; ಎರಡು ತಿಂಗಳುಗಳ ಕಾಲ ನನ್ನನ್ನು ಬಿಡು. ನಾನು ನನ್ನ ಗೆಳತಿಯರೊಡನೆ ಬೆಟ್ಟದ ಪ್ರದೇಶಗಳಿಗೆ ಹೋಗಿ, ನನ್ನ ಕನ್ಯಾವಸ್ಥೆಗೋಸ್ಕರ ಗೋಳಾಡುವೆನು” ಎಂದಳು.",
			"38": "<sup>38</sup> ಅವನು, “ಎರಡು ತಿಂಗಳ ತನಕ ಹೋಗಿ ಬಾ” ಎಂದು ಕಳುಹಿಸಿದನು. ಆಕೆಯು ಸಖಿಯರ ಸಹಿತವಾಗಿ ಬೆಟ್ಟದ ಪ್ರದೇಶಗಳಿಗೆ ಹೋಗಿ ತನ್ನ ಕನ್ಯಾವಸ್ಥೆಗೋಸ್ಕರ ಗೋಳಾಡಿದಳು.",
			"39": "<sup>39</sup> ಎರಡು ತಿಂಗಳು ಕಳೆದ ನಂತರ ಆಕೆ ಪುನಃ ತಂದೆಯ ಹತ್ತಿರ ಬರಲು ಅವನು ತನ್ನ ಹರಕೆಯನ್ನು ತೀರಿಸಿದನು. ಆಕೆಯು ಪುರುಷನನ್ನರಿಯದವಳು.",
			"40": "<sup>40</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಹೆಣ್ಣುಮಕ್ಕಳು ಪ್ರತಿವರುಷದಲ್ಲಿಯೂ ನಾಲ್ಕು ದಿನ ಗಿಲ್ಯಾದ್ಯನಾದ ಯೆಪ್ತಾಹನ ಮಗಳನ್ನು ವರ್ಣಿಸುತ್ತಾರೆ. ಇದು ಅವರಲ್ಲಿ ಒಂದು ಪದ್ಧತಿ."
		},
		"12": {
			"1": "<sup>1</sup> ಎಫ್ರಾಯಿಮ್ಯರು ಒಟ್ಟಿಗೆ ಸೇರಿಕೊಂಡು ಯೊರ್ದನಿನ ಉತ್ತರದಿಕ್ಕಿನಲ್ಲಿರುವ ಚಾಫೋನನ್ನು ದಾಟಿ ಯೆಪ್ತಾಹನ ಬಳಿಗೆ ಹೋಗಿ ಅವನಿಗೆ, “ನೀನು ಅಮ್ಮೋನಿಯರ ಸಂಗಡ ಯುದ್ಧಕ್ಕೆ ಹೋಗುವಾಗ ನಮ್ಮನ್ನು ಯಾಕೆ ಕರೆಯಲಿಲ್ಲ? ಈಗ ನಾವು ನಿನ್ನನ್ನೂ ನಿನ್ನ ಮನೆಯನ್ನೂ ಸುಟ್ಟುಬಿಡುತ್ತೇವೆ” ಅಂದರು.",
			"2": "<sup>2</sup> ಆಗ ಅವನು ಅವರಿಗೆ, “ನನಗೂ ನನ್ನ ಜನರಿಗೂ ಅಮ್ಮೋನಿಯರೊಡನೆ ವ್ಯಾಜ್ಯವಿದ್ದಾಗ ನಾನು ನಿಮ್ಮನ್ನು ಕರೆದೆನು; ಆದರೆ ನೀವು ಬಂದು ನನ್ನನ್ನು ಅವರ ಕೈಯಿಂದ ಬಿಡಿಸಲಿಲ್ಲ.",
			"3": "<sup>3</sup> ನೀವು ಸಹಾಯಮಾಡುವುದಿಲ್ಲವೆಂದು ತಿಳಿದು ಜೀವವನ್ನು ಕೈಯಲ್ಲಿ ಹಿಡಿದು ಅಮ್ಮೋನಿಯರೊಡನೆ ಯುದ್ಧಕ್ಕೆ ಹೋದೆನು. ಯೆಹೋವನು ಅವರನ್ನು ನನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸಿದನು. ಹೀಗಿರಲು ನೀವು ಈಗ ಬಂದು ನನ್ನೊಡನೆ ಕಲಹಮಾಡುವುದೇಕೆ?” ಎಂದು ಉತ್ತರಿಸಿ,",
			"4": "<sup>4</sup> ಗಿಲ್ಯಾದಿನವರೆಲ್ಲರನ್ನೂ ಸೇರಿಸಿಕೊಂಡು ಎಫ್ರಾಯಿಮ್ಯರಿಗೆ ವಿರೋಧವಾಗಿ ಯುದ್ಧಕ್ಕೆ ನಿಂತನು. ಆಗ ಎಫ್ರಾಯಿಮ್ಯರು “ಗಿಲ್ಯಾದ್ಯರಾದ ನೀವು, ನಮ್ಮ ಮತ್ತು ಮನಸ್ಸೆಯವರ ಮಧ್ಯದಲ್ಲಿದ್ದು ಸ್ವಕುಲವನ್ನು ಬಿಟ್ಟು ಇಲ್ಲಿಗೆ ಓಡಿ ಬಂದಿದ್ದೀರಿ” ಎಂದು ಎಫ್ರಾಯಿಮ್ಯರು ಹೇಳಿದ್ದರಿಂದ ಗಿಲ್ಯಾದ್ಯರು ಕೋಪಗೊಂಡು ಅವರನ್ನು ಪೂರ್ಣವಾಗಿ ಸೋಲಿಸಿಬಿಟ್ಟರು.",
			"5": "<sup>5</sup> ಇದಲ್ಲದೆ ಅವರು ಎಫ್ರಾಯಿಮಿಗೆ ಹೋಗುವ ಯೊರ್ದನಿನ ಹಾಯಗಡಗಳನ್ನೆಲ್ಲಾ ಹಿಡಿದರು. ತಪ್ಪಿಸಿಕೊಂಡ ಎಫ್ರಾಯಿಮ್ಯರಲ್ಲೊಬ್ಬನು ಅಲ್ಲಿಗೆ ಬಂದು ನನ್ನನ್ನು ದಾಟಗೊಡಿಸಿರಿ ಎಂದು ಅವರನ್ನು ಕೇಳಿದರೆ ಅವರು, “ನೀನು ಎಫ್ರಾಯಿಮ್ಯನೋ” ಎಂದು ಕೇಳುವರು.",
			"6": "<sup>6</sup> ಅವನು ಅಲ್ಲವೆಂದರೆ ಅವನಿಗೆ, “ನೀನು ಷಿಬ್ಬೋಲೆತ್ ಎಂದು ಹೇಳು” ಎಂದು ಹೇಳುವರು. ಹಾಗೆ ಅನ್ನಲಿಕ್ಕೆ ಬಾರದೆ ಅವನು ಸಿಬ್ಬೋಲೆತ್ ಅನ್ನುವನು. ಕೂಡಲೆ ಅವರು ಅವನನ್ನು ಹಿಡಿದು ಯೊರ್ದನಿನ ಹಾಯಗಡಗಳ (ಕಾಲ್ನಡಿಗೆಯಿಂದ ದಾಟಬಹುದಾದ ನಿರುಳ್ಳಸ್ಥಳ) ಬಳಿಯಲ್ಲೇ ಕೊಂದುಹಾಕುವರು. ಹೀಗೆ ಆ ಕಾಲದಲ್ಲಿ ಎಫ್ರಾಯಿಮ್ಯರೊಳಗೆ ನಾಲ್ವತ್ತೆರಡು ಸಾವಿರ ಜನರು ಹತರಾದರು.",
			"7": "<sup>7</sup> ಗಿಲ್ಯಾದ್ಯನಾದ ಯೆಪ್ತಾಹನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಆರು ವರುಷ ಪಾಲಿಸಿದ ನಂತರ ಸತ್ತನು; ಅವನ ಶವವನ್ನು ಗಿಲ್ಯಾದಿನ ಒಂದು ಪಟ್ಟಣದಲ್ಲಿ ಸಮಾಧಿ ಮಾಡಿದರು.",
			"8": "<sup>8</sup> ಯೆಪ್ತಾಹನ ತರುವಾಯ ಬೇತ್ಲೆಹೇಮಿನವನಾದ ಇಬ್ಚಾನನು ಇಸ್ರಾಯೇಲರ ನ್ಯಾಯಪಾಲಕನಾದನು.",
			"9": "<sup>9</sup> ಅವನಿಗೆ ಮೂವತ್ತು ಮಂದಿ ಗಂಡುಮಕ್ಕಳೂ, ಮೂವತ್ತು ಮಂದಿ ಹೆಣ್ಣುಮಕ್ಕಳೂ ಇದ್ದರು. ತನ್ನ ಹೆಣ್ಣುಮಕ್ಕಳನ್ನು ಬೇರೆಯವರಿಗೆ ಕೊಟ್ಟು, ಗಂಡುಮಕ್ಕಳಿಗೋಸ್ಕರ ಹೊರಗಿನ ಮೂವತ್ತು ಮಂದಿ ಕನ್ಯೆಯರನ್ನು ತಂದನು.",
			"10": "<sup>10</sup> ಇವನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಏಳು ವರುಷ ಪಾಲಿಸಿದನು. ಇಬ್ಚಾನನು ಸತ್ತ ಮೇಲೆ ಅವನ ಶವವನ್ನು ಬೇತ್ಲೆಹೇಮಿನಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು.",
			"11": "<sup>11</sup> ಇವನ ತರುವಾಯ ಜೆಬುಲೂನ್ಯನಾದ ಏಲೋನನು ಇಸ್ರಾಯೇಲರ ಪಾಲಕನಾಗಿ ಹತ್ತು ವರುಷಗಳ ಕಾಲ ಅವರನ್ನು ಪಾಲಿಸಿದನಂತರ ಮರಣಹೊಂದಿದನು.",
			"12": "<sup>12</sup> ಅವನ ಶವವನ್ನು ಜೆಬುಲೂನ್ ದೇಶದ ಅಯ್ಯಾಲೋನಿನಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು.",
			"13": "<sup>13</sup> ಇವನ ತರುವಾಯ ಪಿರಾತೋನಿನವನೂ, ಹಿಲ್ಲೇಲನ ಮಗನೂ ಆದ ಅಬ್ದೋನನು ಇಸ್ರಾಯೇಲರ ಪಾಲಕನಾದನು.",
			"14": "<sup>14</sup> ಇವನಿಗೆ ನಾಲ್ವತ್ತು ಮಂದಿ ಮಕ್ಕಳೂ, ಮೂವತ್ತು ಮಂದಿ ಮೊಮ್ಮಕ್ಕಳೂ ಇದ್ದರು. ಇವರೆಲ್ಲರಿಗೆ ಸವಾರಿಮಾಡುವುದಕ್ಕೋಸ್ಕರ ಎಪ್ಪತ್ತು ಕತ್ತೆಗಳಿದ್ದವು. ಪಿರಾತೋನಿನವನೂ ಹಿಲ್ಲೇಲನ ಮಗನೂ ಆದ ಅಬ್ದೋನನು ಇಸ್ರಾಯೇಲರನ್ನು ಎಂಟು ವರುಷಗಳ ಕಾಲ ಪಾಲಿಸಿದನಂತರ ಮರಣಹೊಂದಿದನು;",
			"15": "<sup>15</sup> ಅವನ ಶವವನ್ನು ಎಫ್ರಾಯಿಮ್ ದೇಶದಲ್ಲಿ ಅಮಾಲೇಕ್ಯರ ಬೆಟ್ಟದ ಮೇಲಿರುವ ಪಿರಾತೋನಿನಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು."
		},
		"13": {
			"1": "<sup>1</sup> ಇಸ್ರಾಯೇಲರು ತಿರುಗಿ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಗಳಾದುದರಿಂದ ಆತನು ಅವರನ್ನು ನಲ್ವತ್ತು ವರುಷಗಳ ಕಾಲ ಫಿಲಿಷ್ಟಿಯರ ಕೈಗೆ ಒಪ್ಪಿಸಿದನು.",
			"2": "<sup>2</sup> ಚೊರ್ಗಾ ಎಂಬ ಊರಲ್ಲಿ ದಾನ್ ಕುಲದವನಾದ ಮಾನೋಹ ಎಂಬ ಒಬ್ಬ ಮನುಷ್ಯನಿದ್ದನು. ಅವನ ಹೆಂಡತಿಯು ಬಂಜೆಯಾಗಿದ್ದರಿಂದ ಅವನಿಗೆ ಮಕ್ಕಳಿರಲಿಲ್ಲ.",
			"3": "<sup>3</sup> ಒಂದಾನೊಂದು ದಿನ ಯೆಹೋವನ ದೂತನು ಆಕೆಗೆ ಪ್ರತ್ಯಕ್ಷನಾಗಿ, “ಇಗೋ, ಬಂಜೆಯಾಗಿ ಮಕ್ಕಳಿಲ್ಲದಿರುವ ನೀನು ಗರ್ಭವತಿಯಾಗಿ ಮಗನನ್ನು ಹೆರುವಿ.",
			"4": "<sup>4</sup> ಆದುದರಿಂದ ಜಾಗರೂಕತೆಯಿಂದಿರು; ದ್ರಾಕ್ಷಾರಸವನ್ನಾಗಲಿ, ಬೇರೆ ಯಾವ ಮದ್ಯವನ್ನಾಗಲಿ ಕುಡಿಯದಿರು; ಯಾವ ನಿಷಿದ್ಧ ಪದಾರ್ಥಗಳನ್ನು ಊಟಮಾಡದಿರು.",
			"5": "<sup>5</sup> ನೀನು ಗರ್ಭವತಿಯಾಗಿ ಹೆರುವ ಮಗನ ತಲೆಯ ಮೇಲೆ ಕ್ಷೌರದ ಕತ್ತಿಯನ್ನು ಉಪಯೋಗಿಸಲೇಬಾರದು; ಅವನು ಹುಟ್ಟಿದಂದಿನಿಂದ ದೇವರಿಗೆ ಪ್ರತಿಷ್ಠಿತನಾಗಿರುವನು; ಇಸ್ರಾಯೇಲರನ್ನು ಫಿಲಿಷ್ಟಿಯರ ಕೈಯಿಂದ ಬಿಡಿಸಲು ಪ್ರಾರಂಭಿಸುವನು” ಅಂದನು.",
			"6": "<sup>6</sup> ತರುವಾಯ ಆ ಸ್ತ್ರೀಯು ತನ್ನ ಗಂಡನ ಬಳಿಗೆ ಹೋಗಿ, “ಒಬ್ಬ ದೇವರಪುರುಷನು ನನ್ನ ಹತ್ತಿರ ಬಂದಿದ್ದನು. ಅವನ ರೂಪವು ದೇವದೂತನ ರೂಪದಂತಿದ್ದು ಭಯಂಕರನಾಗಿದ್ದನು. ನೀನು ಎಲ್ಲಿಂದ ಬಂದಿ ಎಂದು ನಾನು ಅವನನ್ನು ಕೇಳಲಿಲ್ಲ; ಅವನೂ ತನ್ನ ಹೆಸರನ್ನು ತಿಳಿಸಲಿಲ್ಲ.",
			"7": "<sup>7</sup> ಆದರೆ ನನಗೆ, ‘ನೀನು ಗರ್ಭವತಿಯಾಗಿ ಒಬ್ಬ ಮಗನನ್ನು ಹೆರುವಿ; ಆದುದರಿಂದ ನೀನು ದ್ರಾಕ್ಷಾರಸವನ್ನಾಗಲಿ, ಬೇರೆ ಯಾವ ಮದ್ಯವನ್ನಾಗಲಿ ಕುಡಿಯದಿರು, ಯಾವ ನಿಷಿದ್ಧಾಹಾರವನ್ನೂ ಮುಟ್ಟಬೇಡ; ಆ ಹುಡುಗನು ಹುಟ್ಟಿದಂದಿನಿಂದ ಸಾಯುವ ವರೆಗೆ ದೇವರಿಗೆ ಪ್ರತಿಷ್ಠಿತನಾಗಿರುವನು’ ಎಂದು ಹೇಳಿದನು” ಅಂದಳು.",
			"8": "<sup>8</sup> ಮಾನೋಹನು ಇದನ್ನು ಕೇಳಿ ಯೆಹೋವನಿಗೆ, “ಸ್ವಾಮೀ, ದಯವಿರಲಿ; ನೀನು ಕಳುಹಿಸಿದ ದೇವಪುರುಷನು ಇನ್ನೊಂದು ಸಾರಿ ನಮ್ಮ ಬಳಿಗೆ ಬಂದು ಹುಟ್ಟಲಿಕ್ಕಿರುವ ಮಗುವಿಗೋಸ್ಕರ ಮಾಡಬೇಕಾದದ್ದನ್ನು ನಮಗೆ ಬೋಧಿಸಲಿ” ಎಂದು ಬೇಡಿಕೊಳ್ಳಲು ದೇವರು ಅವನ ಮೊರೆಯನ್ನು ಕೇಳಿದನು.",
			"9": "<sup>9</sup> ಆ ಸ್ತ್ರೀಯು ಹೊಲದಲ್ಲಿ ಕುಳಿತಿರುವಾಗ ದೇವದೂತನು ಪುನಃ ಬಂದನು. ಆಕೆಯ ಗಂಡನಾದ ಮಾನೋಹನು ಅಲ್ಲಿರಲಿಲ್ಲ.",
			"10": "<sup>10</sup> ಆದ್ದರಿಂದ ಆಕೆಯು ಬೇಗನೆ ಗಂಡನ ಬಳಿಗೆ ಹೋಗಿ, “ಮೊನ್ನೆ ನನಗೆ ಪ್ರತ್ಯಕ್ಷನಾದ ಪುರುಷನು ತಿರುಗಿ ಬಂದಿದ್ದಾನೆ” ಎಂದು ತಿಳಿಸಲು",
			"11": "<sup>11</sup> ಅವನೆದ್ದು ಹೆಂಡತಿಯೊಡನೆ ಬಂದು ಆ ಪುರುಷನಿಗೆ, “ಮೊನ್ನೆ ಈಕೆಯೊಡನೆ ಮಾತನಾಡಿದವನು ನೀನೋ” ಎಂದು ಕೇಳಲು ಅವನು, “ಹೌದು, ನಾನೇ” ಅಂದನು.",
			"12": "<sup>12</sup> ಆಗ ಮಾನೋಹನು, “ನೀನು ಹೇಳಿದ್ದು ನೆರವೇರಿದಾಗ ನಾವು ಆ ಮಗುವಿಗೋಸ್ಕರ ಮಾಡತಕ್ಕದ್ದೇನು? ಅವನನ್ನು ಹೇಗೆ ಬೆಳೆಸ ಬೇಕು” ಎಂದು ಕೇಳಲು",
			"13": "<sup>13</sup> ಯೆಹೋವನ ದೂತನು ಮಾನೋಹನಿಗೆ, “ನಾನು ಹೇಳಿದ್ದನ್ನೆಲ್ಲಾ ಈಕೆಯು ಜಾಗರೂಕತೆಯಿಂದ ಕೈಕೊಳ್ಳಲಿ.",
			"14": "<sup>14</sup> ದ್ರಾಕ್ಷಾಫಲವನ್ನು ತಿನ್ನದಿರಲಿ; ದ್ರಾಕ್ಷಾರಸವನ್ನಾಗಲಿ, ಬೇರೆ ಯಾವ ಮದ್ಯವನ್ನಾಗಲಿ ಕುಡಿಯದಿರಲಿ; ನಿಷಿದ್ಧಾಹಾರವನ್ನು ಮುಟ್ಟದಿರಲಿ; ಹೀಗೆ ನಾನು ಆಜ್ಞಾಪಿಸಿದ್ದನ್ನೆಲಾ ಕೈಕೊಳ್ಳಲಿ” ಅಂದನು.",
			"15": "<sup>15</sup> ಮತ್ತು ಮಾನೋಹನು ಯೆಹೋವನ ದೂತನನ್ನು, “ನಾವು ನಿನಗೋಸ್ಕರ ಒಂದು ಹೋತಮರಿಯನ್ನು ಪಕ್ವಮಾಡಿ ತರುವವರೆಗೆ ದಯವಿಟ್ಟು ಇಲ್ಲೇ ನಿಲ್ಲಬೇಕು” ಎಂದು ಬೇಡಿಕೊಂಡನು.",
			"16": "<sup>16</sup> ಯೆಹೋವನ ದೂತನು ಮಾನೋಹನಿಗೆ, “ನೀನು ನನ್ನನ್ನು ನಿಲ್ಲಿಸಿಕೊಂಡರೂ ನಾನು ನಿನ್ನ ಆಹಾರವನ್ನು ಊಟಮಾಡುವುದಿಲ್ಲ; ಯಜ್ಞಮಾಡಬೇಕೆಂದು ನಿನಗೆ ಮನಸ್ಸಿದ್ದರೆ ಅದನ್ನು ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಿಸು” ಅಂದನು. ಅವನು ಯೆಹೋವನ ದೂತನೆಂಬುದು ಮಾನೋಹನಿಗೆ ಗೊತ್ತಿರಲಿಲ್ಲ.",
			"17": "<sup>17</sup> ಆದ್ದರಿಂದ ಅವನು ಆ ದೂತನನ್ನು, “ನೀನು ಹೇಳಿದ್ದು ನೆರವೇರಿದಾಗ ನಿನ್ನನ್ನು ಸನ್ಮಾನಿಸಬೇಕೆಂದಿರುತ್ತೇವೆ, ನಿನ್ನ ಹೆಸರೇನು” ಎಂದು ಕೇಳಿದನು.",
			"18": "<sup>18</sup> ಯೆಹೋವನ ದೂತನು, “ನನ್ನ ಹೆಸರನ್ನು ಕೇಳುವುದೇಕೆ? ಅದು ಆಶ್ಚರ್ಯಕರವಾದದ್ದು” ಅಂದನು.",
			"19": "<sup>19</sup> ಮಾನೋಹನು ಹೋತಮರಿಯನ್ನೂ ಧಾನ್ಯದ್ರವ್ಯವನ್ನೂ ತಂದು ಬಂಡೆಯ ಮೇಲಿಟ್ಟು ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಿಸಿದನು. ಮಾನೋಹನೂ ಅವನ ಹೆಂಡತಿಯೂ ನೋಡುತ್ತಿರುವಾಗಲೇ ಯೆಹೋವನ ದೂತನು ಆಶ್ಚರ್ಯವನ್ನು ನಡಿಸಿದನು.",
			"20": "<sup>20</sup> ಏನಂದರೆ ಅವನು ಯಜ್ಞವೇದಿಯಿಂದ ಆಕಾಶಕ್ಕೆ ಹೋಗುತ್ತಿರುವ ಅಗ್ನಿಜ್ವಾಲೆಯೊಳಗೆ ಮೇಲಕ್ಕೇರಿ ಹೋದನು. ಅವರು ಇದನ್ನು ನೋಡುತ್ತಲೆ ನೆಲದ ಮೇಲೆ ಬೋರಲುಬಿದ್ದರು.",
			"21": "<sup>21</sup> ಯೆಹೋವನ ದೂತನು ಮಾನೋಹನಿಗೂ ಅವನ ಹೆಂಡತಿಗೂ ಮತ್ತೆ ಕಾಣಿಸಲಿಲ್ಲ. ಆಗ ಮಾನೋಹನು ಆತನು ಯೆಹೋವನ ದೂತನೆಂದು ತಿಳಿದು",
			"22": "<sup>22</sup> ತನ್ನ ಹೆಂಡತಿಗೆ, “ನಾವು ಸಾಯುವುದು ನಿಜ, ದೇವರನ್ನು ಕಣ್ಣಾರೆ ಕಂಡೆವಲ್ಲಾ” ಅಂದನು.",
			"23": "<sup>23</sup> ಆದರೆ ಆಕೆಯು ಅವನಿಗೆ, “ಯೆಹೋವನು ನಮ್ಮನ್ನು ಕೊಲ್ಲಬೇಕೆಂದಿದ್ದರೆ ಆತನು ನಮ್ಮ ಕೈಯಿಂದ ಯಜ್ಞವನ್ನೂ, ನೈವೇದ್ಯವನ್ನೂ ಸ್ವೀಕರಿಸುತ್ತಿರಲಿಲ್ಲ; ಈಗ ಇವುಗಳನ್ನೆಲ್ಲಾ ನಮಗೆ ತೋರಿಸುತ್ತಿರಲಿಲ್ಲ, ಹೇಳುತ್ತಿರಲಿಲ್ಲ” ಅಂದಳು.",
			"24": "<sup>24</sup> ಆ ಸ್ತ್ರೀಯು ಮಗನನ್ನು ಹೆತ್ತು ಅವನಿಗೆ ಸಂಸೋನನೆಂದು ಹೆಸರಿಟ್ಟಳು. ಹುಡುಗನು ದೊಡ್ಡವನಾದನು. ಯೆಹೋವನ ಆಶೀರ್ವಾದವು ಅವನ ಮೇಲಿತ್ತು.",
			"25": "<sup>25</sup> ಇದಲ್ಲದೆ ಅವನು ಚೊರ್ಗಕ್ಕೂ, ಎಷ್ಟಾವೋಲಿಗೂ ಮಧ್ಯದಲ್ಲಿರುವ ದಾನ್‍ ಕುಲದ ಪಾಳೆಯದಲ್ಲಿದ್ದಾಗ ಯೆಹೋವನ ಆತ್ಮವು ಅವನನ್ನು ಪ್ರೇರೇಪಿಸುವುದಕ್ಕೆ ಪ್ರಾರಂಭಿಸಿತು."
		},
		"14": {
			"1": "<sup>1</sup> ಸಂಸೋನನು ಗಟ್ಟಾ ಇಳಿದು ತಿಮ್ನಾ ಊರಿಗೆ ಹೋಗಿ ಅಲ್ಲಿನ ಫಿಲಿಷ್ಟಿಯರ ಹೆಣ್ಣುಗಳಲ್ಲಿ ಒಬ್ಬಳನ್ನು ನೋಡಿ, ತಿರುಗಿ ತಂದೆತಾಯಿಗಳ ಬಳಿಗೆ ಬಂದು",
			"2": "<sup>2</sup> ಅವರಿಗೆ, “ನಾನು ತಿಮ್ನಾ ಊರಲ್ಲಿ ಒಬ್ಬ ಫಿಲಿಷ್ಟಿಯ ಹೆಣ್ಣನ್ನು ನೋಡಿದ್ದೇನೆ; ಆಕೆಯನ್ನು ನನಗೆ ಮದುವೆ ಮಾಡಿಕೊಡಿರಿ” ಎಂದು ಹೇಳಿದನು.",
			"3": "<sup>3</sup> ಆಗ ಅವರು ಅವನಿಗೆ, “ಸುನ್ನತಿಯಿಲ್ಲದ ಫಿಲಿಷ್ಟಿಯರ ಹೆಣ್ಣನ್ನು ಮದುವೆ ಮಾಡಿಕೊಳ್ಳಬೇಕೆಂದಿರುತ್ತೀಯಲ್ಲಾ; ನಿನ್ನ ಬಂಧುಗಳಲ್ಲಿಯೂ, ನಮ್ಮ ಎಲ್ಲಾ ಜನರಲ್ಲಿಯೂ ನಿನಗೆ ಹೆಣ್ಣು ಸಿಕ್ಕುವುದಿಲ್ಲವೋ?” ಎಂದು ಕೇಳಲು ಅವನು, “ನನಗೋಸ್ಕರ ಆಕೆಯನ್ನೇ ತೆಗೆದುಕೊಳ್ಳಬೇಕು; ಆಕೆಯನ್ನೇ ಮೆಚ್ಚಿಕೊಂಡಿದ್ದೇನೆ” ಅಂದನು.",
			"4": "<sup>4</sup> ಫಿಲಿಷ್ಟಿಯರ ಕೇಡಿಗೆ ಕಾರಣ ಹುಡುಕುತ್ತಿದ್ದ ಯೆಹೋವನ ಪ್ರೇರಣೆಯಿಂದಲೇ ಈ ಸಂಗತಿ ಉಂಟಾಯಿತೆಂಬುದು ಅವನ ತಂದೆತಾಯಿಗಳಿಗೆ ಗೊತ್ತಿರಲಿಲ್ಲ. ಆ ಕಾಲದಲ್ಲಿ ಫಿಲಿಷ್ಟಿಯರು ಇಸ್ರಾಯೇಲರ ಮೇಲೆ ದೊರೆತನ ನಡೆಸುತ್ತಿದ್ದರು.",
			"5": "<sup>5</sup> ಸಂಸೋನನು ತನ್ನ ತಂದೆತಾಯಿಗಳ ಸಹಿತ ತಿಮ್ನಾ ಊರಿಗೆ ಹೊರಟು ಅಲ್ಲಿನ ದ್ರಾಕ್ಷೇ ತೋಟಗಳ ಬಳಿಗೆ ಬಂದಾಗ, ಒಂದು ಪ್ರಾಯದ ಸಿಂಹವು ಗರ್ಜಿಸುತ್ತಾ ಅವನೆದುರಿಗೆ ಬಂದಿತು.",
			"6": "<sup>6</sup> ಅವನ ಕೈಯಲ್ಲಿ ಏನೂ ಇಲ್ಲದಿದ್ದರೂ, ಯೆಹೋವನ ಆತ್ಮವು ಅವನ ಮೇಲೆ ಫಕ್ಕನೆ ಬಂದದ್ದರಿಂದ ಆ ಸಿಂಹವನ್ನು ಹೋತ ಮರಿಯನ್ನೋ ಎಂಬಂತೆ ಸೀಳಿಬಿಟ್ಟನು. ಈ ಸಂಗತಿಯನ್ನು ತಂದೆತಾಯಿಗಳಿಗೆ ತಿಳಿಸಲಿಲ್ಲ.",
			"7": "<sup>7</sup> ಅನಂತರ ಅವನು ಊರೊಳಗೆ ಹೋಗಿ ಆ ಸ್ತ್ರೀಯನ್ನು ಮಾತನಾಡಿಸಿ ಆಕೆಯನ್ನು ಮೆಚ್ಚಿಕೊಂಡನು.",
			"8": "<sup>8</sup> ಕೆಲವು ದಿನಗಳಾದ ಮೇಲೆ ಅವನು ಆಕೆಯನ್ನು ವರಿಸುವುದಕ್ಕೋಸ್ಕರ ತಿರುಗಿ ಆ ಊರಿಗೆ ಹೋಗುತ್ತಿದ್ದಾಗ ದಾರಿಯ ಸಮೀಪದಲ್ಲಿ ಬಿದ್ದಿದ್ದ ಸಿಂಹದ ಹೆಣವನ್ನು ನೋಡುವುದಕ್ಕೆ ಹೋಗಲು ಅದರ ಒಡಲಲ್ಲಿ ಜೇನುಹುಳಗಳನ್ನೂ, ಜೇನನ್ನೂ ಕಂಡು,",
			"9": "<sup>9</sup> ಆ ಜೇನನ್ನು ಕೈಯಲ್ಲಿ ತೆಗೆದುಕೊಂಡು ತಿನ್ನುತ್ತಾ ಹೋದನು; ತನ್ನ ತಂದೆತಾಯಿಗಳಿಗೂ ಕೊಟ್ಟನು; ಅವರೂ ತಿಂದರು. ಆದರೆ ಸತ್ತ ಸಿಂಹದ ದೇಹದಿಂದ ತಾನು ಜೇನು ತೆಗೆದುಕೊಂಡನೆಂದು ಅವರಿಗೆ ತಿಳಿಸಲಿಲ್ಲ.",
			"10": "<sup>10</sup> ಸಂಸೋನನ ತಂದೆಯು ಹೆಣ್ಣಿನ ಮನೆಗೆ ಬಂದ ಮೇಲೆ ಸಂಸೋನನು ಯೌವನಸ್ಥರ ಪದ್ದತಿಯ ಪ್ರಕಾರ ಅಲ್ಲಿ ಔತಣವನ್ನು ಸಿದ್ಧಮಾಡಿಸಿದನು.",
			"11": "<sup>11</sup> ಅವರು ಅವನನ್ನು ನೋಡಿದಾಗ ತಮ್ಮಲ್ಲಿಂದ ಮೂವತ್ತು ಮಂದಿಯನ್ನು ಆರಿಸಿ ಅವನ ಜೊತೆಯಲ್ಲಿರಿಸಿದರು.",
			"12": "<sup>12</sup> ಸಂಸೋನನು ಇವರಿಗೆ, “ನಾನು ನಿಮಗೆ ಒಂದು ಒಗಟನ್ನು ಹೇಳುತ್ತೇನೆ; ನೀವು ಔತಣದ ಏಳು ದಿನಗಳಲ್ಲಿಯೇ ಅದನ್ನು ಬಿಚ್ಚಿ ಅದರ ಅರ್ಥವನ್ನು ಹೇಳಿದರೆ ನಿಮಗೆ ಮೂವತ್ತು ದುಪ್ಪಟಿಗಳನ್ನೂ ಮೂವತ್ತು ವಿಶೇಷವಸ್ತ್ರಗಳನ್ನೂ ಕೊಡುವೆನು.",
			"13": "<sup>13</sup> ನಿಮ್ಮಿಂದ ಆಗದೆ ಹೋದರೆ ನೀವು ನನಗೆ ಮೂವತ್ತು ನಾರು ಬಟ್ಟೆಯ ನಿಲುವಂಗಿಗಳನ್ನು (ದುಪ್ಪಟಿಗಳನ್ನೂ), ಮೂವತ್ತು ವಿಶೇಷವಸ್ತ್ರಗಳನ್ನೂ ಕೊಡಬೇಕು” ಅನ್ನಲು ಅವರು ಅವನಿಗೆ, “ನಿನ್ನ ಒಗಟನ್ನು ಹೇಳು, ಕೇಳೋಣ” ಎಂದು ಹೇಳಿದರು.",
			"14": "<sup>14</sup> ಆಗ ಅವನು, “ತಿಂದುಬಿಡುವಂಥದರಿಂದ ತಿನ್ನತಕ್ಕದ್ದು ದೊರಕಿತು; ಕ್ರೂರವಾದದ್ದರಿಂದ ಮಧುರವಾದದ್ದು ಹುಟ್ಟಿತು” ಎಂದು ಹೇಳಿದನು. ಮೂರು ದಿನಗಳಾದರೂ ಅವರು ಅದನ್ನು ಬಿಡಿಸಲಾರದೆ ಹೋದರು.",
			"15": "<sup>15</sup> ಅವರು ಏಳನೆಯ ದಿನದಲ್ಲಿ ಸಂಸೋನನ ಹೆಂಡತಿಗೆ, “ನಿನ್ನ ಗಂಡನನ್ನು ಮರುಳುಗೊಳಿಸಿ ಅವನಿಂದ ಒಗಟಿನ ಅರ್ಥವನ್ನು ತಿಳಿದುಕೋ; ಇಲ್ಲವಾದರೆ ನಾವು ನಿನ್ನನ್ನೂ, ನಿನ್ನ ತಂದೆಯ ಮನೆಯವರನ್ನೂ ಬೆಂಕಿಯಿಂದ ಸುಟ್ಟುಬಿಡುವೆವು; ನಮಗಿರುವುದೆಲ್ಲವನ್ನೂ ಕಸಿದುಕೊಳ್ಳುವುದಕ್ಕೆ ನಮ್ಮನ್ನು ಇಲ್ಲಿಗೆ ಕರೆಸಿದಿರೋ” ಅಂದರು.",
			"16": "<sup>16</sup> ಆಕೆಯು ಸಂಸೋನನ ಮುಂದೆ ಅಳುತ್ತಾ, “ನೀನು ನನ್ನನ್ನು ಪ್ರೀತಿಸುವುದಿಲ್ಲ, ದ್ವೇಷಿಸುತ್ತೀಯಷ್ಟೆ; ನನ್ನ ಜನರಿಗೆ ಒಗಟನ್ನು ಹೇಳಿದ್ದೀ, ಅದರ ಅರ್ಥವನ್ನು ನನಗೆ ತಿಳಿಸಲಿಲ್ಲವಲ್ಲಾ” ಎಂದು ತೊಂದರೆಪಡಿಸಿದಳು. ಅವನು ಆಕೆಗೆ, “ಅದನ್ನು ನನ್ನ ತಂದೆತಾಯಿಗಳಿಗೂ ಹೇಳಲಿಲ್ಲ; ನಿನಗೆ ಹೇಳುವೆನೋ?” ಎಂದು ಉತ್ತರ ಕೊಟ್ಟನು.",
			"17": "<sup>17</sup> ಆಕೆಯು ಔತಣದ ಏಳು ದಿನಗಳಲ್ಲಿಯೂ ಅವನ ಮುಂದೆ ಅಳುತ್ತಿದ್ದಳು. ಏಳನೆಯ ದಿನದಲ್ಲಿ ಆಕೆಯು ಬಹಳವಾಗಿ ಪೀಡಿಸಿದ್ದರಿಂದ ಅವನು ಹೇಳಿಬಿಟ್ಟನು. ಆಕೆಯು ಒಗಟಿನ ಅರ್ಥವನ್ನು ತನ್ನ ಜನರಿಗೆ ತಿಳಿಸಿದಳು.",
			"18": "<sup>18</sup> ಅವರು ಏಳನೆಯ ದಿನ ಸೂರ್ಯ ಮುಳುಗುವುದಕ್ಕಿಂತ ಮೊದಲು ಅವನಿಗೆ, “ಜೇನಿಗಿಂತ ಸಿಹಿಯಾದದ್ದು ಯಾವುದು? ಸಿಂಹಕ್ಕಿಂತ ಬಲಿಷ್ಠವಾದದ್ದು ಯಾವುದು?” ಎಂದು ಹೇಳಲು ಅವನು, “ನೀವು ನನ್ನ ಕಡಸಿನಿಂದ (ಮಣಕದಿಂದ) ಉಳದೇ ಹೋಗಿದ್ದರೆ ಒಗಟನ್ನು ಬಿಚ್ಚುವುದು ನಿಮ್ಮಿಂದಾಗುತ್ತಿರಲಿಲ್ಲ” ಅಂದನು.",
			"19": "<sup>19</sup> ಯೆಹೋವನ ಆತ್ಮವು ಫಕ್ಕನೆ ಅವನ ಮೇಲೆ ಬಂದಿತು; ಅವನು ಅಷ್ಕೆಲೋನಿಗೆ ಹೋಗಿ ಅಲ್ಲಿನ ಮೂವತ್ತು ಮಂದಿಯನ್ನು ಹೊಡೆದು, ಅವರ ವಸ್ತ್ರಗಳನ್ನು ಸುಲುಕೊಂಡು ಒಗಟನ್ನು ಬಿಚ್ಚಿದವರಿಗೆ ತಂದುಕೊಟ್ಟನು. ಮತ್ತು ಕೊಪಗೊಂಡವನಾಗಿ ತನ್ನ ತಂದೆಯ ಮನೆಗೆ ಹೋದನು.",
			"20": "<sup>20</sup> ಇತ್ತ ಸಂಸೋನನ ಹೆಂಡತಿಯನ್ನು ಆ ಮೂವತ್ತು ಮಂದಿಯಲ್ಲಿ ಅವನಿಗೆ ಸ್ನೇಹಿತನಾಗಿದ್ದವನಿಗೆ ಮದುವೆಮಾಡಿಕೊಟ್ಟರು."
		},
		"15": {
			"1": "<sup>1</sup> ಕೆಲವು ದಿನಗಳಾದ ಮೇಲೆ ಗೋದಿಯ ಸುಗ್ಗಿಯಲ್ಲಿ ಸಂಸೋನನು ಒಂದು ಹೋತಮರಿಯನ್ನು ತೆಗೆದುಕೊಂಡು ತನ್ನ ಹೆಂಡತಿಯನ್ನು ನೋಡುವುದಕ್ಕೋಸ್ಕರ ಹೊರಟುಹೋಗಿ ಆಕೆಯ ತಂದೆಗೆ, “ನಾನು ನನ್ನ ಹೆಂಡತಿಯ ಹತ್ತಿರ ಒಳಗಿನ ಕೋಣೆಗೆ ಹೋಗುತ್ತೇನೆ” ಅಂದನು. ಅದಕ್ಕೆ ಅವನು, “ಹೋಗಬಾರದು;",
			"2": "<sup>2</sup> ನೀನು ನಿಜವಾಗಿ ಆಕೆಯನ್ನು ಹಗೆಮಾಡುತ್ತೀಯೆಂದು ತಿಳಿದು ಅವಳನ್ನು ನಿನ್ನ ಗೆಳೆಯರಲ್ಲೊಬ್ಬನಿಗೆ ಮದುವೆಮಾಡಿಕೊಟ್ಟೆನು. ಆಕೆಯ ತಂಗಿಯು ಆಕೆಗಿಂತ ಸುಂದರಿಯಾಗಿದ್ದಾಳಲ್ಲಾ; ಈಕೆಯು ನಿನಗಿರಲಿ” ಅಂದನು.",
			"3": "<sup>3</sup> ಆಗ ಸಂಸೋನನು, “ಈ ಸಾರಿ ನಾನು ಫಿಲಿಷ್ಟಿಯರಿಗೆ ಕೇಡುಮಾಡುವುದಾದರೆ ತಪ್ಪು ನನ್ನದಲ್ಲ” ಎಂದುಕೊಂಡು ಹೋಗಿ",
			"4": "<sup>4</sup> ಮುನ್ನೂರು ನರಿಗಳನ್ನು ಹಿಡಿದು, ಬಾಲಕ್ಕೆ ಬಾಲವನ್ನು ಸೇರಿಸಿ ಕಟ್ಟಿ, ಅವುಗಳ ನಡುವೆ ಒಂದೊಂದು ಪಂಜನ್ನು ಸಿಕ್ಕಿಸಿ, ಪಂಜುಗಳಿಗೆ ಬೆಂಕಿಹೊತ್ತಿಸಿ,",
			"5": "<sup>5</sup> ಆ ನರಿಗಳನ್ನು ಫಿಲಿಷ್ಟಿಯರ ಹೊಲಗಳಲ್ಲಿ ಬಿಟ್ಟು, ಅವರ ತೆನೆಗೂಡುಗಳನ್ನೂ, ಇನ್ನೂ ಕೊಯ್ಯದೆ ಇದ್ದ ಬೆಳೆಯನ್ನೂ, ಎಣ್ಣೆಯ ಮರಗಳ ತೋಟಗಳನ್ನೂ ಸುಟ್ಟುಬಿಟ್ಟನು.",
			"6": "<sup>6</sup> ಫಿಲಿಷ್ಟಿಯರು ಇದನ್ನು ಮಾಡಿದವರು ಯಾರೆಂದು ವಿಚಾರಿಸುವಲ್ಲಿ ತಿಮ್ನಾ ಊರಿನವನ ಅಳಿಯನಾದ ಸಂಸೋನನೆಂದು ಗೊತ್ತಾಯಿತು. ಅವನ ಮಾವನು ಅವನ ಹೆಂಡತಿಯನ್ನು ಬೇರೊಬ್ಬನಿಗೆ ಕೊಟ್ಟುಬಿಟ್ಟದ್ದೇ ಇದಕ್ಕೆ ಕಾರಣವೆಂದು ಅವರು ತಿಳಿದು, ಆಕೆಯನ್ನೂ, ಆಕೆಯ ತಂದೆಯನ್ನೂ ಸುಟ್ಟುಬಿಟ್ಟರು.",
			"7": "<sup>7</sup> ಸಂಸೋನನು ಅವರಿಗೆ, “ನೀವು ಹೀಗೆ ಮಾಡಿದ್ದರಿಂದ ನಾನು ನಿಮಗೆ ಮುಯ್ಯಿತೀರಿಸದೆ ಬಿಡುವುದಿಲ್ಲ” ಎಂದು ಹೇಳಿ",
			"8": "<sup>8</sup> ಅವರ ತೊಡೆ, ಸೊಂಟಗಳನ್ನು ಮುರಿದು, ಅವರನ್ನು ಸಂಹರಿಸಿ, ತಾನು ಹೋಗಿ ಏಟಾಮ್ ಗಿರಿಯ ಗುಹೆಯಲ್ಲಿ ವಾಸಮಾಡಿದನು.",
			"9": "<sup>9</sup> ಆಗ ಫಿಲಿಷ್ಟಿಯರು ಹೊರಟು ಯೆಹೂದಾ ಪ್ರಾಂತದ ಲೇಹಿ ಎಂಬಲ್ಲಿಗೆ ಹೋಗಿ ಪಾಳೆಯಮಾಡಿಕೊಂಡರು.",
			"10": "<sup>10</sup> ನೀವು ನಮಗೆ ವಿರೋಧವಾಗಿ ಯುದ್ಧಕ್ಕೆ ಬಂದದ್ದೇಕೆ ಎಂದು ಯೆಹೂದ್ಯರು ಅವರನ್ನು ಕೇಳಲು ಅವರು, “ಸಂಸೋನನನ್ನು ಹಿಡಿದು ಕಟ್ಟಿ ಅವನು ನಮಗೆ ಮಾಡಿದಂತೆಯೇ ಅವನಿಗೂ ಮಾಡಬೇಕೆಂದು ಬಂದೆವು” ಎಂದು ಉತ್ತರಕೊಟ್ಟರು.",
			"11": "<sup>11</sup> ಮೂರು ಸಾವಿರ ಮಂದಿ ಯೆಹೂದ್ಯರು ಏಟಾಮ್ ಗಿರಿಗೆ ಹೋಗಿ ಸಂಸೋನನಿಗೆ, “ಫಿಲಿಷ್ಟಿಯರು ನಮ್ಮ ಮೇಲೆ ದೊರೆತನಮಾಡುತ್ತಿದ್ದಾರೆ ಎಂಬುದು ನಿನಗೆ ಗೊತ್ತಿಲ್ಲವೋ? ಹೀಗೆ ಯಾಕೆ ಮಾಡಿದಿ?” ಎಂದು ಕೇಳಲು ಅವನು, “ಅವರು ನನಗೆ ಮಾಡಿದಂತೆಯೇ ನಾನು ಅವರಿಗೆ ಮಾಡಿದ್ದೇನೆ” ಅಂದನು.",
			"12": "<sup>12</sup> ಅವರು, “ನಾವು ನಿನ್ನನ್ನು ಕಟ್ಟಿ ಫಿಲಿಷ್ಟಿಯರ ಕೈಗೆ ಒಪ್ಪಿಸಬೇಕೆಂದು ಬಂದಿದ್ದೇವೆ” ಅನ್ನಲು ಸಂಸೋನನು, “ಹಾಗಾದರೆ ನೀವು ನನ್ನನ್ನು ಕೊಲ್ಲುವುದಿಲ್ಲವೆಂದು ಆಣೆಮಾಡಿರಿ” ಎಂದನು.",
			"13": "<sup>13</sup> ಅದಕ್ಕೆ ಅವರು, “ನಾವು ಸತ್ಯವಾಗಿ ನಿನ್ನನ್ನು ಕೊಲ್ಲುವುದಿಲ್ಲ; ನಿನ್ನನ್ನು ಕಟ್ಟಿ ಫಿಲಿಷ್ಟಿಯರ ಕೈಗೆ ಒಪ್ಪಿಸುತ್ತೇವೆ” ಎಂದು ಹೇಳಿ ಅವನನ್ನು ಎರಡು ಹೊಸ ಹಗ್ಗಗಳಿಂದ ಕಟ್ಟಿ ಗುಡ್ಡದ ಮೇಲಿನಿಂದ ಕರತಂದರು.",
			"14": "<sup>14</sup> ಸಂಸೋನನು ಲೆಹೀಯ ಸಮೀಪಕ್ಕೆ ಬರಲು ಫಿಲಿಷ್ಟಿಯರು ಅವನನ್ನು ಕಂಡು ಆರ್ಬಟಿಸಿದರು. ಯೆಹೋವನ ಆತ್ಮವು ಅವನ ಮೇಲೆ ಬಂದದ್ದರಿಂದ ಅವನ ರಟ್ಟೆಗಳಿಗೆ ಸುತ್ತಲೂ ಕಟ್ಟಿದ್ದ ಹಗ್ಗಗಳು ಸುಟ್ಟ ಸಣಬಿನ ಹಾಗಾದವು; ಕೈಗೆ ಹಾಕಿದ ಬೇಡಿಗಳು ಕಳಚಿ ಬಿದ್ದವು.",
			"15": "<sup>15</sup> ಅಲ್ಲಿ ಒಂದು ಕತ್ತೆಯ ದವಡೇ ಎಲುಬು ಬಿದ್ದಿತ್ತು. ಅದು ಇನ್ನೂ ಹಸಿದಾಗಿತ್ತು. ಅವನು ಅದನ್ನು ತೆಗೆದುಕೊಂಡು ಅದರಿಂದ ಸಾವಿರ ಜನರನ್ನು ಸಂಹರಿಸಿಬಿಟ್ಟು,",
			"16": "<sup>16</sup> “ಕತ್ತೆಯ ದವಡೇ ಎಲುಬಿನಿಂದ ಸಾವಿರ ಜನರನ್ನು ಸಾಯಿಸಿದೆನು; ಕತ್ತೆಯ ದವಡೇ ಎಲುಬಿನಿಂದ ಹೆಣಗಳನ್ನು ರಾಶಿರಾಶಿಯಾಗಿ ಬೀಳಿಸಿದೆನು” ಎಂದು ಹೇಳಿ,",
			"17": "<sup>17</sup> ತನ್ನ ಕೈಯಲ್ಲಿದ್ದ ಎಲುಬನ್ನು ಬೀಸಾಡಿದನು. ಆ ಸ್ಥಳಕ್ಕೆ ರಾಮತ್ ಲೆಹೀ (ದವಡೆಯ ಗುಡ್ಡ ಎಂದು ಅರ್ಥ) ಎಂದು ಹೆಸರಾಯಿತು.",
			"18": "<sup>18</sup> ಅವನು ಬಹಳ ಬಾಯಾರಿದವನಾಗಿ ಯೆಹೋವನಿಗೆ, “ನೀನು ನಿನ್ನ ಸೇವಕನ ಮೂಲಕವಾಗಿ ಈ ಮಹಾಜಯವನ್ನುಂಟುಮಾಡಿದ ಮೇಲೆ ನಾನು ದಾಹದಿಂದ ಸತ್ತು ಸುನ್ನತಿಯಿಲ್ಲದವರ ಕೈಯಲ್ಲಿ ಬೀಳಬೇಕೋ” ಎಂದು ಮೊರೆಯಿಡಲು",
			"19": "<sup>19</sup> ಆತನು ಲೇಹಿಯಲ್ಲಿ ಒರಳಬಂಡೆಯನ್ನು ಸೀಳಿ ಅದರೊಳಗಿಂದ ನೀರು ಬರುವಂತೆ ಮಾಡಲು ಅವನು ಅದನ್ನು ಕುಡಿದು ಪುನಃ ಚೈತನ್ಯಹೊಂದಿದನು. ಆದ್ದರಿಂದ ಅದಕ್ಕೆ ಏನ್ ಹಕ್ಕೋರೇ (ಮೊರೆಯಿಡುವವನ ಬುಗ್ಗೆ ಎಂದು ಅರ್ಥ) ಎಂದು ಹೆಸರಾಯಿತು; ಅದು ಇಂದಿನ ವರೆಗೂ ಲೆಹೀಯಲ್ಲಿರುತ್ತದೆ.",
			"20": "<sup>20</sup> ಫಿಲಿಷ್ಟಿಯರ ಪ್ರಾಬಲ್ಯದಲ್ಲಿ ಸಂಸೋನನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಇಪ್ಪತು ವರುಷಗಳ ವರೆಗೆ ಪಾಲಿಸಿದನು."
		},
		"16": {
			"1": "<sup>1</sup> ತರುವಾಯ ಸಂಸೋನನು ಗಾಜಕ್ಕೆ ಹೋಗಿ ಅಲ್ಲಿ ಒಬ್ಬ ವೇಶ್ಯಯನ್ನು ಕಂಡು ಅವಳ ಬಳಿಗೆ ಹೋದನು.",
			"2": "<sup>2</sup> ಸಂಸೋನನು ಬಂದಿರುವುದು ಗಾಜದವರಿಗೆ ಗೊತ್ತಾಗಲು ಅವರು ಬಂದು ಅವನನ್ನು ಸುತ್ತಿಕೊಂಡರು. ಸೂರ್ಯೋದಯವಾಗುತ್ತಲೆ ಅವನನ್ನು ಕೊಂದುಹಾಕೋಣ ಎಂದುಕೊಂಡು ರಾತ್ರಿಯೆಲ್ಲಾ ಊರುಬಾಗಲಲ್ಲಿ ಹೊಂಚಿನೋಡುತ್ತಾ ಸುಮ್ಮನಿದ್ದರು.",
			"3": "<sup>3</sup> ಸಂಸೋನನು ಮಲಗಿ ಮಧ್ಯರಾತ್ರಿಯಲ್ಲೆದ್ದು ಊರು ಬಾಗಿಲಿನ ಕದಗಳನ್ನೂ, ಅದರ ಎರಡು ನಿಲುವುಪಟ್ಟಿಗಳನ್ನೂ, ಅಗುಳಿಗಳನ್ನೂ ಕಿತ್ತು, ಹೆಗಲ ಮೇಲೆ ಹೊತ್ತುಕೊಂಡು ಹೋಗಿ ಹೆಬ್ರೋನಿನ ಎದುರಿಗಿರುವ ಪರ್ವತ ಶಿಖರದ ಮೇಲೆ ಇಟ್ಟನು.",
			"4": "<sup>4</sup> ಅನಂತರ ಸಂಸೋನನು ಸೋರೇಕ್ ತಗ್ಗಿನಲ್ಲಿ ವಾಸವಾಗಿದ್ದ ದೆಲೀಲಾ ಎಂಬ ಹೆಸರುಳ್ಳ ಒಬ್ಬ ಸ್ತ್ರೀಯಲ್ಲಿ ಮೋಹಿತನಾದನು.",
			"5": "<sup>5</sup> ಫಿಲಿಷ್ಟಿಯ ಪ್ರಭುಗಳು ಅವಳ ಬಳಿಗೆ ಹೋಗಿ ಅವಳಿಗೆ, “ನೀನು ಅವನನ್ನು ಮರುಳುಗೊಳಿಸಿ, ಅವನ ಮಹಾಶಕ್ತಿಯ ಮೂಲ ಯಾವುದೆಂಬುದನ್ನೂ, ನಾವು ಅವನನ್ನೂ ಗೆದ್ದು, ಬಲವನ್ನು ಕುಂದಿಸುವುದು ಹೇಗೆಂಬುದನ್ನೂ ಗೊತ್ತುಮಾಡಿಕೊಂಡು ನಮಗೆ ತಿಳಿಸು; ನಮ್ಮಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬನೂ ನಿನಗೆ ಸಾವಿರದ ನೂರು ರೂಪಾಯಿ ಕೊಡುವೆವು” ಎಂದು ಹೇಳಿದರು.",
			"6": "<sup>6</sup> ದೆಲೀಲಳು ಸಂಸೋನನಿಗೆ, “ನಿನಗೆ ಇಂಥ ಮಹಾ ಶಕ್ತಿ ಹೇಗೆ ಬಂದಿತು? ನಿನ್ನನ್ನು ಬಲವನ್ನು ಕುಂದಿಸುವುದು ಹೇಗೆ ಎಂಬುದನ್ನು ದಯವಿಟ್ಟು ನನಗೆ ತಿಳಿಸು” ಅಂದಳು.",
			"7": "<sup>7</sup> ಅವನು, “ಹಸಿ ನಾರಿನ ಏಳು ಎಳೆಗಳಿಂದ ನನ್ನನ್ನು ಕಟ್ಟಿದರೆ ನಾನು ಬಲಹೀನನಾಗಿ ಬೇರೆ ಮನುಷ್ಯನಂತಾಗುವೆನು” ಎಂದು ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ಫಿಲಿಷ್ಟಿಯ ಪ್ರಭುಗಳು ಅಂಥ ಹಸೀ ನಾರಿನ ಏಳು ಎಳೆಗಳನ್ನು ಅವಳಿಗೆ ತಂದುಕೊಟ್ಟರು.",
			"9": "<sup>9</sup> ಅವಳು ಒಳಗಿನ ಕೋಣೆಯಲ್ಲಿ ಹೊಂಚುಗಾರರನ್ನಿಟ್ಟು ಅವುಗಳಿಂದ ಅವನನ್ನು ಕಟ್ಟಿ, “ಸಂಸೋನನೇ, ನಿನ್ನ ಮೇಲೆ ಫಿಲಿಷ್ಟಿಯರು ಬಂದರು” ಎಂದು ಕೂಗಿದಳು. ಅವನು ಬೆಂಕಿತಗುಲಿದ ಸೆಣಬಿನ ದಾರವನ್ನೋ ಎಂಬಂತೇ ಆ ಎಳೆಗಳನ್ನು ಹರಿದುಬಿಟ್ಟನು; ಹೀಗೆ ಅವನ ಶಕ್ತಿಯ ರಹಸ್ಯವು ತಿಳಿಯದೆ ಹೋಯಿತು.",
			"10": "<sup>10</sup> ದೆಲೀಲಳು ತಿರುಗಿ ಸಂಸೋನನಿಗೆ, “ನೀನು ನನ್ನನ್ನು ವಂಚಿಸಿದಿ; ಸುಳ್ಳಾಡಿದಿ; ನಿನ್ನನ್ನು ಯಾವುದರಿಂದ ಕಟ್ಟಬಹುದೆಂಬುದನ್ನು ದಯವಿಟ್ಟು ನನಗೆ ತಿಳಿಸು” ಎಂದಳು.",
			"11": "<sup>11</sup> ಅವನು ಅವಳಿಗೆ, “ಯಾವುದಕ್ಕೂ ಉಪಯೋಗಿಸದ ಹೊಸ ಹಗ್ಗಗಳಿಂದ ನನ್ನನ್ನು ಕಟ್ಟಿದರೆ ನಾನು ಬಲಹೀನನಾಗಿ ಬೇರೆ ಮನುಷ್ಯರಂತಾಗುವೆನು” ಅಂದನು.",
			"12": "<sup>12</sup> ಅವಳು ಒಳಗಿನ ಕೋಣೆಯಲ್ಲಿ ಹೊಂಚುಗಾರರನ್ನಿರಿಸಿ, ಹೊಸ ಹಗ್ಗಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಅವುಗಳಿಂದ ಅವನನ್ನು ಕಟ್ಟಿ, “ಸಂಸೋನನೇ, ಫಿಲಿಷ್ಟಿಯರು ಬಂದಿದ್ದಾರೆ” ಎಂದು ಕೂಗಲು ಅವನೆದ್ದು ತೋಳುಗಳಿಗೆ ಕಟ್ಟಿದ್ದ ಹಗ್ಗಗಳನ್ನು ದಾರವನ್ನೋ ಎಂಬಂತೆ ಹರಿದುಬಿಟ್ಟನು.",
			"13": "<sup>13</sup> ಆಗ ದೆಲೀಲಳು ಸಂಸೋನನಿಗೆ “ನೀನು ತಿರುಗಿ ನನ್ನನ್ನು ವಂಚಿಸಿದಿ; ಸುಳ್ಳಾಡಿದಿ; ನಿನ್ನನ್ನು ಯಾವುದರಿಂದ ಕಟ್ಟಬಹುದೆಂದು ತಿಳಿಸು” ಅನ್ನಲು ಅವನು, “ನನ್ನ ತಲೆಕೂದಲಿನ ಏಳು ಜಡೆಗಳನ್ನು ಮಗ್ಗದಲ್ಲಿ ನೇಯಿದರೆ ಸಾಕು” ಎಂದು ಹೇಳಿದನು.",
			"14": "<sup>14</sup> ಅವಳು ಅವನ ತಲೆಕೂದಲಿನ ಏಳು ಜಡೆಗಳನ್ನು ಗೂಟಕ್ಕೆ ಭದ್ರಮಾಡಿ, “ಸಂಸೋನನೇ, ಫಿಲಿಷ್ಟಿಯರು ಬಂದಿದ್ದಾರೆ” ಎಂದು ಕೂಗಿದಳು. ಅವನು ನಿದ್ರೆಯಿಂದ ಎಚ್ಚತ್ತು ಮಗ್ಗವನ್ನೂ ಮಗ್ಗದ ಗೂಟವನ್ನೂ ಕಿತ್ತುಕೊಂಡು ಹೋದನು.",
			"15": "<sup>15</sup> ಆಕೆಯು ತಿರುಗಿ ಅವನಿಗೆ, “ನನ್ನನ್ನು ಪ್ರೀತಿಸುತ್ತೇನೆಂದು ನೀನು ನನಗೆ ಹೇಗೆ ಹೇಳುವೇ? ನಿನ್ನ ಮನಸ್ಸು ನನ್ನ ಮೇಲೆ ಇಲ್ಲವಲ್ಲಾ; ಮೂರು ಸಾರಿ ನನ್ನನ್ನು ವಂಚಿಸಿದಿ. ನಿನಗೆ ಇಂಥ ಮಹಾ ಶಕ್ತಿ ಯಾವುದರಿಂದ ಬಂದಿತೆಂಬುದನ್ನು ನನಗೆ ತಿಳಿಸಲಿಲ್ಲ” ಅಂದಳು.",
			"16": "<sup>16</sup> ಇದಲ್ಲದೆ ಅವಳು ಅವನನ್ನು ದಿನದಿನವೂ ಮಾತಿನಿಂದ ಪೀಡಿಸಿದ್ದರಿಂದ ಅವನಿಗೆ ಸಾಯುವುದು ಒಳ್ಳೆಯದು ಎನ್ನುವಷ್ಟು ಬೇಸರವಾಯಿತು.",
			"17": "<sup>17</sup> ಆಗ ಅವನು, “ಕ್ಷೌರಕತ್ತಿಯು ನನ್ನ ತಲೆಯ ಮೇಲೆ ಮುಟ್ಟಿಸಿಲ್ಲ; ನಾನು ಮಾತೃಗರ್ಭದಿಂದಲೇ ದೇವರಿಗೆ ಪ್ರತಿಷ್ಠಿತನಾದವನು (ನಾಜೀರನು). ನನ್ನ ತಲೆಯನ್ನು ಕ್ಷೌರಮಾಡುವುದಾದರೆ ನನ್ನ ಶಕ್ತಿಯು ಹೋಗುವುದು; ಮತ್ತು ನಾನು ಬಲಹೀನನಾಗಿ ಬೇರೆ ಮನುಷ್ಯನಂತಾಗುವೆನು” ಎಂದು ತನ್ನ ಗುಟ್ಟನ್ನೆಲ್ಲಾ ಆಕೆಯ ಮುಂದೆ ಬಿಚ್ಚಿಹೇಳಿದನು.",
			"18": "<sup>18</sup> ಅವನು ಇದ್ದದ್ದನ್ನು ಇದ್ದ ಹಾಗೆಯೇ ಹೇಳಿದನೆಂದು ದೆಲೀಲಳು ತಿಳಿದು ಫಿಲಿಷ್ಟಿಯ ಪ್ರಭುಗಳ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿ ಅವರಿಗೆ, “ಇದೊಂದು ಸಾರಿ ಬನ್ನಿರಿ: ಅವನು ನನಗೆ ತನ್ನ ಹೃದಯವನ್ನೆಲ್ಲಾ ತಿಳಿಸಿದ್ದಾನೆ” ಎಂದು ಹೇಳಿ, ಕರೆಕಳುಹಿಸಿದಳು. ಅವರು ಹಣವನ್ನು ತೆಗೆದುಕೊಂಡು ಆಕೆಯ ಬಳಿಗೆ ಬಂದರು.",
			"19": "<sup>19</sup> ಆಕೆಯು ಸಂಸೋನನನ್ನು ತನ್ನ ತೊಡೆಯ ಮೇಲೆ ಮಲಗಿಸಿಕೊಂಡು ಅವನಿಗೆ ನಿದ್ರೆಹತ್ತಿದಾಗ ಒಬ್ಬ ಮನುಷ್ಯನಿಂದ ಅವನ ತಲೆಯ ಏಳು ಜಡೆಗಳನ್ನು ಬೋಳಿಸಿ, ಅವನನ್ನು ದುರ್ಬಲಪಡಿಸಿದಳು. ಅವನ ಶಕ್ತಿಯು ಅವನನ್ನು ಬಿಟ್ಟು ಹೋಯಿತು,",
			"20": "<sup>20</sup> ಆಗ ಅವಳು, “ಸಂಸೋನನೇ, ಫಿಲಿಷ್ಟಿಯರು ಬಂದಿದ್ದಾರೆ” ಎಂದು ಕೂಗಲು ಅವನು ಎಚ್ಚೆತ್ತು, ಮುಂಚಿನಂತೆ ಈಗಲೂ ಕೊಸರಿಕೊಂಡು ಹೋಗುವೆನು ಅಂದುಕೊಂಡನು; ಆದರೆ ಯೆಹೋವನು ತನ್ನನ್ನು ಬಿಟ್ಟುಹೋಗಿದ್ದಾನೆಂಬದು ಅವನಿಗೆ ತಿಳಿಯಲಿಲ್ಲ.",
			"21": "<sup>21</sup> ಫಿಲಿಷ್ಟಿಯರು ಅವನನ್ನು ಹಿಡಿದು, ಕಣ್ಣುಗಳನ್ನು ಕಿತ್ತು, ಗಾಜಕ್ಕೆ ಒಯ್ದು, ಕಬ್ಬಿಣದ ಬೇಡಿಗಳನ್ನು ಹಾಕಿ, ಸೆರೆಮನೆಯಲ್ಲಿ ಹಿಟ್ಟನ್ನು ಬೀಸುವುದಕ್ಕೆ ಇರಿಸಿದರು.",
			"22": "<sup>22</sup> ಅಷ್ಟರಲ್ಲಿ ಅವನ ತಲೆಯ ಕೂದಲುಗಳು ಬೆಳೆಯ ತೊಡಗಿದವು.",
			"23": "<sup>23</sup> ಶತ್ರುವಾದ ಸಂಸೋನನನ್ನು ತಮ್ಮ ಕೈಗೆ ಒಪ್ಪಿಸಿದ ತಮ್ಮ ದೇವರಾದ ದಾಗೋನನಿಗೆ ಮಹಾಯಜ್ಞವನ್ನು ಸಮರ್ಪಿಸಿ ಸಂತೋಷಪಡಬೇಕೆಂದು ಫಿಲಿಷ್ಟಿಯ ಪ್ರಭುಗಳು ಒಟ್ಟಾಗಿ ಸೇರಿಬಂದರು.",
			"24": "<sup>24</sup> ಜನರು ಸಂಸೋನನನ್ನು ನೋಡಿದಾಗ, “ನಮ್ಮ ದೇಶವನ್ನು ಹಾಳುಮಾಡಿ ನಮ್ಮಲ್ಲಿ ಅನೇಕರನ್ನು ಕೊಂದುಹಾಕಿದ ಈ ಶತ್ರುವನ್ನು ನಮ್ಮ ದೇವರು ನಮ್ಮ ಕೈಗೆ ಒಪ್ಪಿಸಿಕೊಟ್ಟಿದ್ದಾನೆ” ಎಂದು ಹಾಡುಹಾಡಿ ಸಂತೋಷಿಸಿದರು.",
			"25": "<sup>25</sup> ಅವರು ಸಂಭ್ರಮದಲ್ಲಿದ್ದಾಗ, “ನಮ್ಮ ವಿನೋದಕ್ಕೋಸ್ಕರ ಸಂಸೋನನನ್ನು ಕರೆದುಕೊಂಡು ಬನ್ನಿರಿ” ಅಂದರು. ಅದರಂತೆಯೇ ಸಂಸೋನನನ್ನು ಸೆರೆಮನೆಯಿಂದ ಕರೆದುಕೊಂಡು ಬಂದರು. ಅವನು ಎರಡು ಸ್ತಂಭಗಳ ನಡವೆ ನಿಂತು ವಿನೋದ ಮಾಡಬೇಕಾಯಿತು.",
			"26": "<sup>26</sup> ಆಗ ಸಂಸೋನನು ತನ್ನನ್ನು ಕೈಹಿಡಿದು ನಡೆಸುವ ಹುಡುಗನಿಗೆ, “ನನ್ನ ಕೈಗಳನ್ನು ಬಿಡು; ನಾನು ಕೈಯಾಡಿಸಿ ಮನೆಗೆ ಆಧಾರವಾಗಿರುವ ಸ್ತಂಭಗಳನ್ನು ಹುಡುಕಿ ಅವುಗಳಿಗೆ ಸ್ವಲ್ಪ ಒರಗಿಕೊಳ್ಳುತ್ತೇನೆ” ಅಂದನು.",
			"27": "<sup>27</sup> ಆ ಮನೆಯು ಸ್ತ್ರೀಪುರುಷರಿಂದ ಕಿಕ್ಕಿರಿದು ಹೋಗಿತ್ತು. ಫಿಲಿಷ್ಟಿಯರ ಅಧಿಪತಿಗಳೆಲ್ಲರೂ ಅಲ್ಲಿದ್ದರು; ಸಂಸೋನನ ವಿನೋದವನ್ನು ನೋಡುವುದಕ್ಕಾಗಿ ಬಂದವರಲ್ಲಿ ಸುಮಾರು ಮೂರು ಸಾವಿರ ಮಂದಿ ಸ್ತ್ರೀಪುರುಷರು ಮಾಳಿಗೆಯ ಮೇಲಿದ್ದರು.",
			"28": "<sup>28</sup> ಆಗ ಸಂಸೋನನು ಯೆಹೋವನಿಗೆ, “ಕರ್ತನೇ, ಯೆಹೋವನೇ, ಫಿಲಿಷ್ಟಿಯರು ಕಿತ್ತುಹಾಕಿದ ನನ್ನ ಎರಡೂ ಕಣ್ಣುಗಳಲ್ಲಿ ಒಂದಕ್ಕೋಸ್ಕರವಾದರೂ ನಾನು ಅವರಿಗೆ ಮುಯ್ಯಿತೀರಿಸುವ ಹಾಗೆ ನನ್ನನ್ನು ಈ ಸಾರಿ ಬಲಪಡಿಸು” ಎಂದು ಮೊರೆಯಿಟ್ಟು,",
			"29": "<sup>29</sup> ಮನೆಯ ಆಧಾರಸ್ತಂಭಗಳಲ್ಲಿ ಒಂದನ್ನು ಬಲಗೈಯಿಂದಲೂ, ಇನ್ನೊಂದನ್ನು ಎಡಗೈಯಿಂದಲೂ ಹಿಡಿದು, ಅವುಗಳನ್ನು ಒತ್ತಿ,",
			"30": "<sup>30</sup> “ನಾನೂ ಫಿಲಿಷ್ಟಿಯರ ಸಂಗಡ ಸಾಯುವೆನು” ಎಂದು ಹೇಳಿ ಬಲವಾಗಿ ಬಾಗಿಕೊಂಡನು. ಆಗ ಅ ಕಟ್ಟಡವು ಅಧಿಪತಿಗಳ ಮೇಲೆಯೂ ಅಲ್ಲಿ ಬಂದಿದ್ದ ಎಲ್ಲಾ ಜನರ ಮೇಲೆಯೂ ಬಿದ್ದಿತು. ಅವನು ಜೀವದಿಂದಿದ್ದಾಗ ಕೊಂದುಹಾಕಿದವರಿಗಿಂತ ಸಾಯುವಾಗ ಕೊಂದವರ ಸಂಖ್ಯೆಯೇ ಹೆಚ್ಚಾಗಿತ್ತು.",
			"31": "<sup>31</sup> ಅವನ ಬಂಧುಗಳೂ, ತಂದೆಯ ಮನೆಯವರೆಲ್ಲರೂ ಬಂದು ಅವನ ಶವವನ್ನು ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ಚೊರ್ಗಕ್ಕೂ ಎಷ್ಟಾವೋಲಿಗೂ ಮಧ್ಯದಲ್ಲಿದ್ದ ಅವನ ತಂದೆಯಾದ ಮಾನೋಹನ ಸಮಾಧಿಯಲ್ಲಿ ಹೂಣಿಟ್ಟರು. ಅವನು ಇಸ್ರಾಯೇಲರನ್ನು ಇಪ್ಪತ್ತು ವರುಷ ಪಾಲಿಸಿದ್ದನು."
		},
		"17": {
			"1": "<sup>1</sup> ಎಫ್ರಾಯಿಮ್ ಬೆಟ್ಟದ ಸೀಮೆಯಲ್ಲಿ ಮೀಕನೆಂಬ ಒಬ್ಬ ಮನುಷ್ಯನಿದ್ದನು. ಅವನು ಒಂದು ದಿನ ತನ್ನ ತಾಯಿಗೆ, “ಅಮ್ಮಾ, ಕಳುವಾಗಿದ್ದ ನಿನ್ನ ಸಾವಿರದ ನೂರು ರೂಪಾಯಿಗಳಿಗೋಸ್ಕರ ನೀನು ನನಗೆ ಕೇಳಿಸುವಂತೆ ಶಪಿಸಿದಿಯಲ್ಲಾ;",
			"2": "<sup>2</sup> ಇಗೋ, ಆ ರೂಪಾಯಿಗಳು ನನ್ನ ಹತ್ತಿರ ಇವೆ; ನಾನು ತೆಗೆದುಕೊಂಡಿದ್ದೆನು” ಎಂದು ಹೇಳಿದನು. ಆಕೆಯು, “ನನ್ನ ಮಗನೇ, ಯೆಹೋವನು ನಿನ್ನನ್ನು ಆಶೀರ್ವದಿಸಲಿ” ಅಂದಳು.",
			"3": "<sup>3</sup> ಅವನು ಆ ಸಾವಿರದ ನೂರು ರೂಪಾಯಿಗಳನ್ನು ತನ್ನ ತಾಯಿಗೆ ಹಿಂದಕ್ಕೆ ಕೊಡಲು ಆಕೆಯು, “ನನ್ನ ಮಗನಿಗೆ ಹಿತವಾಗಲೆಂದು ಈ ಹಣವನ್ನು ಯೆಹೋವನಿಗೆ ಹರಕೆಮಾಡಿದ್ದೇನೆ; ಇದರಿಂದ ಒಂದು ಎರಕದ ವಿಗ್ರಹವನ್ನು ಮಾಡಿಸಿ ನಿನ್ನ ವಶಕ್ಕೆ ಕೊಡುವೆನು” ಎಂದು ಹೇಳಿ,",
			"4": "<sup>4</sup> ಮಗನು ಹಿಂದಕ್ಕೆ ಕೊಟ್ಟ ರೂಪಾಯಿಗಳಲ್ಲಿ ಇನ್ನೂರು ರೂಪಾಯಿಗಳನ್ನು ತೆಗೆದು ಅಕ್ಕಸಾಲಿಗನಿಗೆ ಕೊಟ್ಟಳು. ಅವನು ಅವುಗಳಿಂದ ಕೆತ್ತನೆಯ ಮತ್ತು ಎರಕದ ವಿಗ್ರಹಗಳನ್ನು ಮಾಡಿಕೊಟ್ಟನು. ಮೀಕನು ಅದನ್ನು ತನ್ನ ಮನೆಯಲ್ಲಿಟ್ಟುಕೊಂಡನು.",
			"5": "<sup>5</sup> ಈ ಮೀಕನು ಏಫೋದನ್ನೂ ವಿಗ್ರಹಗಳನ್ನೂ ಮಾಡಿಸಿ, ಅವುಗಳನ್ನು ತಾನು ಕಟ್ಟಿಸಿದ ದೇವಸ್ಥಾನದಲ್ಲಿಟ್ಟು, ತನ್ನ ಮಕ್ಕಳಲ್ಲಿ ಒಬ್ಬನನ್ನು ಅರ್ಚಕ ಸೇವೆಗಾಗಿ ಪ್ರತಿಷ್ಠಿಸಿದನು.",
			"6": "<sup>6</sup> ಆ ಕಾಲದಲ್ಲಿ ಇಸ್ರಾಯೇಲರೊಳಗೆ ಅರಸನಿರಲಿಲ್ಲ. ಪ್ರತಿಯೊಬ್ಬನೂ ಮನಸ್ಸಿಗೆ ಬಂದ ಹಾಗೆಯೇ ನಡೆದುಕೊಳ್ಳುತ್ತಿದ್ದನು.",
			"7": "<sup>7</sup> ಯೆಹೂದದ ಬೇತ್ಲೆಹೇಮಿನವನೂ, ಯೆಹೂದ ಕುಲದವನೂ ಆಗಿದ್ದ ಒಬ್ಬ ಯೌವನಸ್ಥನಾದ ಲೇವಿಯು ಉಪಜೀವನಕ್ಕೆ ಅನುಕೂಲವಾಗುವಂತೆ",
			"8": "<sup>8</sup> ವಾಸಮಾಡಲು ತನ್ನ ಸ್ವಂತ ಊರನ್ನು ಬಿಟ್ಟು, ಪ್ರಯಾಣಮಾಡುತ್ತಾ ಎಫ್ರಾಯಿಮ್ ಬೆಟ್ಟದ ಸೀಮೆಯಲ್ಲಿದ್ದ ಮೀಕನ ಮನೆಗೆ ಬಂದನು.",
			"9": "<sup>9</sup> “ನೀನು ಎಲ್ಲಿಂದ ಬಂದಿ” ಎಂದು ಮೀಕನು ಅವನನ್ನು ಕೇಳಲು ಅವನು, “ನಾನು ಯೆಹೂದದ ಬೇತ್ಲೆಹೇಮಿನವನಾದ ಲೇವಿಯನು; ಉಪಜೀವನಕ್ಕೆ ಅನುಕೂಲವಾದ ವಾಸಸ್ಥಳವನ್ನು ಹುಡುಕಿಕೊಂಡು ಹೊರಟಿದ್ದೇನೆ” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"10": "<sup>10</sup> ಮೀಕನು ಅವನಿಗೆ, “ನೀನು ನಮ್ಮಲ್ಲಿರು; ನಮಗೆ ತಂದೆಯೂ, ಯಾಜಕನೂ ಆಗು. ನಿನಗೆ ವರುಷಕ್ಕೆ ಹತ್ತು ಬೆಳ್ಳಿ ನಾಣ್ಯಗಳನ್ನೂ, ಬಟ್ಟೆಯನ್ನೂ, ಆಹಾರವನ್ನೂ ಕೊಡುತ್ತೇನೆ” ಅನ್ನಲು",
			"11": "<sup>11</sup> ಆ ಲೇವಿಯನು ಒಪ್ಪಿಕೊಂಡು ಅಲ್ಲೇ ವಾಸಮಾಡಿದನು; ಹೀಗೆ ಆ ಯೌವನಸ್ಥನು ಅವನ ಮಕ್ಕಳಲ್ಲೊಬ್ಬನಂತಾದನು.",
			"12": "<sup>12</sup> ಮೀಕನು ಯೌವನಸ್ಥನಾದ ಆ ಲೇವಿಯನನ್ನು ಯಾಜಕಸೇವೆಗೋಸ್ಕರ ಪ್ರತಿಷ್ಠಿಸಿ ತನ್ನ ಮನೆಯಲ್ಲಿಟ್ಟುಕೊಂಡು,",
			"13": "<sup>13</sup> “ಒಬ್ಬ ಲೇವಿಯು ನನ್ನ ಮನೆಯಲ್ಲಿ ಯಾಜಕನಾಗಿರುವುದರಿಂದ ಯೆಹೋವನು ನನ್ನನ್ನು ಆಶೀರ್ವದಿಸುವನೆಂದು ಬಲ್ಲೆನು” ಅಂದುಕೊಂಡನು."
		},
		"18": {
			"1": "<sup>1</sup> ಆ ಕಾಲದಲ್ಲಿ ಇಸ್ರಾಯೇಲರೊಳಗೆ ಅರಸನಿರಲಿಲ್ಲ. ದಾನ್‌ ಕುಲದವರಿಗೆ ಆ ವರೆಗೂ ಇಸ್ರಾಯೇಲರಲ್ಲಿ ಸ್ವಾಸ್ತ್ಯವು ದೊರಕಿರಲಿಲ್ಲ, ಅವರು ತಮಗೋಸ್ಕರ ವಾಸಸ್ಥಳವನ್ನು ಹುಡುಕುತ್ತಿದ್ದರು.",
			"2": "<sup>2</sup> ಅವರು ಚೊರ್ಗಾ, ಎಷ್ಟಾವೋಲ್ ಎಂಬ ಊರುಗಳಿಂದ ತಮ್ಮ ಕುಲದಲ್ಲಿ ಐದು ಮಂದಿ ಪರಾಕ್ರಮಶಾಲಿಗಳಾದ ಗೂಢಚಾರರನ್ನು ಆರಿಸಿ ಅವರಿಗೆ, “ನೀವು ಹೋಗಿ ಲಯಿಷ್ ದೇಶವನ್ನು ಸಂಚರಿಸಿ ನೋಡಿರಿ” ಎಂದು ಆಜ್ಞಾಪಿಸಿ ಕಳುಹಿಸಿದರು. ಇವರು ಪ್ರಯಾಣಮಾಡುತ್ತಾ ಎಫ್ರಾಯಿಮ್ ಬೆಟ್ಟದ ಸೀಮೆಯಲ್ಲಿದ್ದ ಮೀಕನ ಮನೆಗೆ ಬಂದು ಇಳಿದುಕೊಂಡರು.",
			"3": "<sup>3</sup> ಇವರು ಮೀಕನ ಮನೆಯ ಹತ್ತಿರದಲ್ಲಿ ಆ ಯೌವನಸ್ಥನಾದ ಲೇವಿಯನು ಮಾತನಾಡುವುದನ್ನು ಕೇಳಿ, ಗುರುತುಹಿಡಿದು, ಅವನ ಬಳಿಗೆ ಹೋಗಿ, “ನಿನ್ನನ್ನು ಇಲ್ಲಿಗೆ ಕರೆತಂದವರಾರು? ಇಲ್ಲಿ ಏನು ಕೆಲಸ ಮಾಡುತ್ತೀ? ಏನು ಸಿಕ್ಕುತ್ತದೆ” ಎಂದು ಅವನನ್ನು ಕೇಳಲು ಅವನು,",
			"4": "<sup>4</sup> ಮೀಕನು ತನಗೆ ಇಂಥಿಂಥವುಗಳನ್ನು ಸಂಬಳವಾಗಿ ಕೊಡುತ್ತಾನೆಂದೂ, ತಾನು ಅವನ ಯಾಜಕನಾಗಿದ್ದೇನೆಂದೂ ಹೇಳಿದನು.",
			"5": "<sup>5</sup> ಆಗ ಅವರು ಅವನಿಗೆ, “ದಯವಿಟ್ಟು ನಮ್ಮ ಪ್ರಯಾಣವು ಸಫಲವಾಗುವುದೋ ಇಲ್ಲವೋ ಎಂಬುದನ್ನು ದೇವರ ಸನ್ನಿಧಿಯಲ್ಲಿ ವಿಚಾರಿಸು” ಎಂದು ಬೇಡಿಕೊಳ್ಳಲು",
			"6": "<sup>6</sup> ಅವನು, “ಸಮಾಧಾನದಿಂದ ಹೋಗಿರಿ; ಯೆಹೋವನು ನಿಮ್ಮ ಪ್ರಯಾಣವನ್ನು ಕಟಾಕ್ಷಿಸುವನು” ಅಂದನು.",
			"7": "<sup>7</sup> ಆ ಐದು ಮಂದಿ ಹೊರಟು ಲಯಿಷಿಗೆ ಬಂದು, ಅಲ್ಲಿನ ಜನರು ನಿರ್ಭೀತರಾಗಿ ಚೀದೋನ್ಯರಂತೆ ಸುಖ ಸಮಾಧಾನಗಳಿಂದ ಜೀವಿಸುತ್ತಾರೆ; ಅವರನ್ನು ತಗ್ಗಿಸುವ ಅಧಿಕಾರಿಗಳು ದೇಶದಲ್ಲಿಲ್ಲ; ಅವರು ಚೀದೋನ್ಯರಿಗೆ ದೂರವಾಗಿದ್ದು ಯಾರೊಡನೆಯೂ ಒಡನಾಟವಿಲ್ಲದವರು ಎಂಬುದನ್ನು ತಿಳಿದುಕೊಂಡು",
			"8": "<sup>8</sup> ಹಿಂದಿರುಗಿ ಚೊರ್ಗಾ, ಎಷ್ಟಾವೋಲ್ ಎಂಬ ಊರುಗಳಲ್ಲಿರುವ ತಮ್ಮ ಬಂಧುಗಳ ಬಳಿಗೆ ಬಂದರು. ಅವರ ಬಂಧುಗಳು, “ನೀವು ಯಾವ ವರ್ತಮಾನ ತಂದಿದ್ದೀರಿ” ಎಂದು ಅವರನ್ನು ಕೇಳಲು",
			"9": "<sup>9</sup> ಅವರು, “ಏಳಿರಿ, ಅವರಿಗೆ ವಿರೋಧವಾಗಿ ಯುದ್ಧಕ್ಕೆ ಹೋಗೋಣ. ನಾವು ಆ ದೇಶವನ್ನು ನೋಡಿದೆವು; ಅದು ಬಹು ಉತ್ತಮದೇಶ. ನೀವು ಸುಮ್ಮನೆ ಕುಳಿತುಕೊಳ್ಳುವುದೇಕೆ? ತಡಮಾಡದೆ ಹೊರಡಿರಿ; ಆ ದೇಶವನ್ನು ಸ್ವಾದಿನಪಡಿಸಿಕೊಳ್ಳೋಣ.",
			"10": "<sup>10</sup> ನೀವು ಅಲ್ಲಿಗೆ ಹೋಗುವುದಾದರೆ ಅಲ್ಲಿನ ಜನರು ನಿರ್ಭಯದಿಂದ ಜೀವಿಸುವವರೆಂದೂ, ಅವರ ದೇಶವು ಬಹು ವಿಸ್ತಾರವಾಗಿದೆ ಎಂದೂ ತಿಳಿದುಕೊಳ್ಳುವಿರಿ. ದೇವರು ಅದನ್ನು ನಿಮ್ಮ ಕೈಗೆ ಒಪ್ಪಿಸಿದ್ದಾನೆ; ಅದರಲ್ಲಿ ದೊರಕದಿರುವ ಪದಾರ್ಥಗಳು ಲೋಕದಲ್ಲಿ ಸಿಕ್ಕುವುದಿಲ್ಲ” ಅಂದರು.",
			"11": "<sup>11</sup> ಆಗ ಚೊರ್ಗಾ ಎಷ್ಟಾವೋಲ್ ಊರುಗಳಿಂದ ಆರುನೂರು ಮಂದಿ ದಾನ್‌ಕುಲದವರು ಯುದ್ಧ ಸನ್ನದ್ಧರಾಗಿ ಹೊರಟು,",
			"12": "<sup>12</sup> ಗಟ್ಟಾಹತ್ತಿ ಯೆಹೂದದ ಕಿರ್ಯತ್ಯಾರೀಮಿನ ಬಳಿಯಲ್ಲಿ ಇಳಿದುಕೊಂಡರು. ಆದುದರಿಂದ ಆ ಸ್ಥಳಕ್ಕೆ ಇಂದಿನವರೆಗೂ ದಾನ್ಯರ ಪಾಳೆಯವೆಂಬ ಹೆಸರಿದೆ; ಅದು ಕಿರ್ಯತ್ಯಾರೀಮಿನ ಪಶ್ಚಿಮದಲ್ಲಿರುತ್ತದೆ.",
			"13": "<sup>13</sup> ಅವರು ಅಲ್ಲಿಂದ ಮುಂದೆ ಪ್ರಯಾಣಮಾಡಿ ಎಫ್ರಾಯಿಮ್ ಪರ್ವತಪ್ರದೇಶದಲ್ಲಿದ್ದ ಮೀಕನ ಮನೆಯ ಹತ್ತಿರ ಬಂದರು.",
			"14": "<sup>14</sup> ಆಗ ಲಯಿಷ್ ದೇಶವನ್ನು ಸಂಚರಿಸಿ ನೋಡುವುದಕ್ಕೆ ಹೋಗಿದ್ದ ಐದು ಮಂದಿ ತಮ್ಮ ಬಂಧುಗಳಿಗೆ, “ಈ ಗ್ರಾಮದಲ್ಲಿ ಒಂದು ಏಫೋದೂ, ದೇವತಾಪ್ರತಿಮೆಗಳೂ, ಕೆತ್ತನೆಯ ಮತ್ತು ಎರಕದ ವಿಗ್ರಹಗಳೂ ಇರುತ್ತವೆಂಬುದು ನಿಮಗೆ ಗೊತ್ತುಂಟೋ?",
			"15": "<sup>15</sup> ಈ ವಿಷಯದಲ್ಲಿ ಏನು ಮಾಡಬೇಕೆಂಬುದನ್ನು ಆಲೋಚಿಸಿರಿ” ಎಂದು ಹೇಳಿ ತಾವು ಅಲ್ಲಿಂದ ಮೀಕನ ಮನೆಯಲ್ಲಿದ್ದ ಆ ಯೌವನಸ್ಥನಾದ ಲೇವಿಯನ ಬಳಿಗೆ ಬಂದು ಅವನ ಕ್ಷೇಮಸಮಾಚಾರವನ್ನು ವಿಚಾರಿಸ ತೊಡಗಿದರು.",
			"16": "<sup>16</sup> ಇಷ್ಟರಲ್ಲಿ ಯುದ್ಧಸನ್ನದ್ಧರಾದ ಆರುನೂರು ಮಂದಿ ದಾನ್ಯರು ಬಂದು ಬಾಗಿಲಲ್ಲಿ ನಿಂತರು.",
			"17": "<sup>17</sup> ಯಾಜಕನೂ ಬಂದು ಇವರೊಡನೆ ಬಾಗಿಲಲ್ಲಿ ನಿಂತಿದ್ದಾಗ, ಆ ಐದು ಮಂದಿ ಮನೆಯನ್ನು ಹೊಕ್ಕು, ಕೆತ್ತನೆಯ ಮತ್ತು ಎರಕದ ವಿಗ್ರಹಗಳನ್ನೂ, ಏಫೋದನ್ನೂ, ದೇವತಾಪ್ರತಿಮೆಗಳನ್ನೂ ತೆಗೆದುಕೊಂಡರು.",
			"18": "<sup>18</sup> ಅವರು ಮೀಕನ ಮನೆಯನ್ನು ಹೊಕ್ಕು ಅವುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಬರುವಾಗ ಯಾಜಕನು ಅದನ್ನು ಕಂಡು ಅವರಿಗೆ, “ನೀವು ಮಾಡಿದ್ದೇನು” ಅಂದನು.",
			"19": "<sup>19</sup> ಅವರು ಅವನಿಗೆ, “ಸುಮ್ಮನಿರು; ಬಾಯಿ ಮುಚ್ಚಿಕೊಂಡು; ನಮ್ಮ ಜೊತೆಯಲ್ಲಿ ಬಂದು ನಮಗೆ ತಂದೆಯೂ, ಯಾಜಕನೂ ಆಗಿರು. ಒಂದು ಮನೆಗೆ ಯಾಜಕನಾಗಿರುವುದು ಉತ್ತಮವೋ ಅಥವಾ ಇಸ್ರಾಯೇಲ್ಯರ ಒಂದು ಕುಲಕ್ಕೂ, ಗೋತ್ರಕ್ಕೂ ಯಾಜಕನಾಗಿರುವುದು ಉತ್ತಮವೋ?” ಎಂದು ಕೇಳಿದರು.",
			"20": "<sup>20</sup> ಯಾಜಕನು ಈ ಮಾತನ್ನು ಕೇಳಿ ಸಂತೋಷಪಟ್ಟು ಏಫೋದನ್ನೂ ದೇವತಾಪ್ರತಿಮೆಗಳನ್ನೂ, ಕೆತ್ತನೆಯ ವಿಗ್ರಹವನ್ನೂ, ತೆಗೆದುಕೊಂಡು ಅವರ ಬಳಿಗೆ ಬಂದನು.",
			"21": "<sup>21</sup> ತರುವಾಯ ಅವರು ತಮ್ಮ ಹೆಂಡತಿ ಮಕ್ಕಳನ್ನೂ, ಕುರಿದನ ಮೊದಲಾದ ಸೊತ್ತನ್ನೂ ಮುಂದಾಗಿ ಕಳುಹಿಸಿ ತಾವು ಹಿಂದಿನಿಂದ ಹೊರಟರು.",
			"22": "<sup>22</sup> ಅವರು ಮೀಕನ ಮನೆಯಿಂದ ದೂರವಾದ ಮೇಲೆ ಮೀಕನೂ ಅವನ ನೆರೆಹೊರೆಯವರೆಲ್ಲರೂ ದಾನ್ಯರನ್ನು ಹಿಂದಟ್ಟಿ",
			"23": "<sup>23</sup> ಅವರ ಸಮೀಪಕ್ಕೆ ಹೋಗಿ ಅವರನ್ನು ಕೂಗಿದರು. ದಾನ್ಯರು ಹಿಂದಿರುಗಿ ನೋಡಿ ಮೀಕನಿಗೆ, “ನಿನಗೇನಾಯಿತು? ಗುಂಪನ್ನು ಕೂಡಿಸಿಕೊಂಡು ಬಂದದ್ದೇಕೆ” ಎಂದು ಕೇಳಲು",
			"24": "<sup>24</sup> ಅವನು, “ನೀವು ಯಾಜಕನನ್ನೂ ನಾವು ಮಾಡಿಸಿಕೊಂಡ ದೇವರುಗಳನ್ನೂ ಅಪಹರಿಸಿಕೊಂಡಿದ್ದೀರಿ, ನನಗೆ ಇನ್ನೇನಿದೆ; ಹೀಗಿರಲಾಗಿ ನಿನಗೇನಾಯಿತೆಂದು ನೀವು ನನ್ನನ್ನು ಕೇಳುವುದು ಹೇಗೆ?” ಅಂದನು.",
			"25": "<sup>25</sup> ದಾನ್ಯರು ಅವನಿಗೆ, “ನಮ್ಮಲ್ಲಿ ಕೆಲವರು ತುಂಬಾ ಕೊಪಿಸಿಕೊಳ್ಳುವವರಿದ್ದಾರೆ; ಅವರು ನಿಮ್ಮ ಮೇಲೆ ಬಿದ್ದರೆ ನೀನೂ, ನಿನ್ನ ಮನೆಯವರೂ ಪ್ರಾಣವನ್ನು ಕಳೆದುಕೊಳ್ಳಬೇಕಾಗುತ್ತದೆ. ಆದ್ದರಿಂದ ಬಾಯಿಮುಚ್ಚಿಕೊಂಡು ಹೋಗು” ಎಂದು ಹೇಳಿ ಮುಂದೆ ನಡೆದರು.",
			"26": "<sup>26</sup> ಅವರು ತನಗಿಂತ ಬಲಿಷ್ಠರೆಂದು ತಿಳಿದುಕೊಂಡು ಮೀಕನು ಹಿಂದಿರುಗಿ ಮನೆಗೆ ಹೋದನು.",
			"27": "<sup>27</sup> ದಾನ್ಯರು ಮೀಕನು ಮಾಡಿಸಿದ್ದ ವಿಗ್ರಹಗಳನ್ನೂ ಅವನ ಯಾಜಕನನ್ನೂ ಅಪಹರಿಸಿಕೊಂಡು ಲಯಿಷಿಗೆ ಹೋಗಿ ಸುಖದಿಂದಲೂ, ನಿರ್ಭಯದಿಂದಲೂ ವಾಸವಾಗಿದ್ದ ಅಲ್ಲಿನ ಜನರ ಮೇಲೆ ಬಿದ್ದು, ಅವರನ್ನು ಕತ್ತಿಯಿಂದ ಸಂಹರಿಸಿ, ಅವರ ಪಟ್ಟಣವನ್ನು ಸುಟ್ಟುಬಿಟ್ಟರು.",
			"28": "<sup>28</sup> ಆ ಪಟ್ಟಣವು ಚೀದೋನಿಗೆ ದೂರವಾಗಿದ್ದುದರಿಂದಲೂ, ಸಮೀಪದಲ್ಲಿ ಪರಿಚಯಸ್ಥರು ಯಾರು ಇಲ್ಲದೆ ಇದ್ದುದರಿಂದಲೂ, ಅವರಿಗೆ ಸಹಾಯದೊರಕಲಿಲ್ಲ. ಬೇತ್‌ರೆಹೋಬಿನ ತಗ್ಗಿನಲ್ಲಿದ್ದ ಈ ಪಟ್ಟಣವನ್ನು ದಾನ್ಯರು ಪುನಃ ಕಟ್ಟಿಸಿ ಅದರಲ್ಲಿ ವಾಸಿಸಿದರು.",
			"29": "<sup>29</sup> ಮೊದಲು ಲಯಿಷ್ ಎಂಬ ಹೆಸರಿದ್ದ ಆ ಪಟ್ಟಣಕ್ಕೆ ಈಗ ದಾನ್ ಎಂದು ಹೆಸರಿಟ್ಟರು. ದಾನ್ ಎಂಬುದು ಇವರ ಮೂಲಪುರುಷನೂ ಇಸ್ರಾಯೇಲನ ಮಗನೂ ಆದ ದಾನನ ಹೆಸರು.",
			"30": "<sup>30</sup> ಇದಲ್ಲದೆ ದಾನ್ಯರು ಆ ವಿಗ್ರಹವನ್ನು ತಮ್ಮ ಪಟ್ಟಣದಲ್ಲಿಟ್ಟರು. ಇಸ್ರಾಯೇಲರು ಸೆರೆಗೆ ಹೋಗುವವರೆಗೆ ಮೋಶೆಯ ಮೊಮ್ಮಗನೂ, ಗೇರ್ಷೋಮನ ಮಗನೂ ಆದ ಯೋನಾತಾನನೂ ಅವನ ವಂಶದವರೂ ದಾನ್ಯರಿಗೆ ಯಾಜಕರಾಗಿದ್ದರು.",
			"31": "<sup>31</sup> ಶೀಲೋವಿನಲ್ಲಿ ದೇವಾಲಯವಿದ್ದ ಕಾಲದಲ್ಲೆಲ್ಲಾ ದಾನ್ಯರಲ್ಲಿ ಮೀಕನು ಮಾಡಿಸಿದ ವಿಗ್ರಹವಿತ್ತು."
		},
		"19": {
			"1": "<sup>1</sup> ಆ ಕಾಲದಲ್ಲಿ ಇಸ್ರಾಯೇಲ್ಯರೊಳಗೆ ಅರಸನಿರಲಿಲ್ಲ. ಎಫ್ರಾಯಿಮ್ ಪರ್ವತಪ್ರದೇಶದ ಒಂದು ದೂರ ಸ್ಥಳದಲ್ಲಿ ಒಬ್ಬ ಲೇವಿಯನು ಪ್ರವಾಸಿಯಾಗಿದ್ದನು. ಯೆಹೂದದ ಬೇತ್ಲೆಹೇಮಿನವಳಾದ ಒಬ್ಬ ಸ್ತ್ರೀಯು ಅವನಿಗೆ ಉಪಪತ್ನಿಯಾಗಿದ್ದಳು.",
			"2": "<sup>2</sup> ಆಕೆಯು ಇವನನ್ನು ಬಿಟ್ಟು ಜಾರತ್ವಮಾಡಿ, ಯೆಹೂದದ ಬೇತ್ಲೆಹೇಮಿನಲ್ಲಿದ್ದ ತನ್ನ ತವರುಮನೆಗೆ ಹೋಗಿ ಅಲ್ಲಿ ನಾಲ್ಕು ತಿಂಗಳು ಇದ್ದಳು.",
			"3": "<sup>3</sup> ಅನಂತರ ಆಕೆಯ ಗಂಡನಾದ ಆ ಲೇವಿಯನು ಆಕೆಯನ್ನು ಪ್ರೀತಿಯಿಂದ ಮಾತನಾಡಿಸಿ ತಿರುಗಿ ಮನೆಗೆ ಕರೆದುಕೊಂಡು ಹೋಗಬೇಕೆಂದು ಒಬ್ಬ ಸೇವಕನನ್ನೂ, ಎರಡು ಕತ್ತೆಗಳನ್ನೂ ತೆಗೆದುಕೊಂಡು ಅಲ್ಲಿಗೆ ಹೋದನು. ಆಕೆಯು ಅವನನ್ನು ತನ್ನ ತವರುಮನೆಯಲ್ಲಿ ಸೇರಿಸಿಕೊಂಡಳು; ಆಕೆಯ ತಂದೆಯೂ ಅವನನ್ನು ನೋಡಿ ಸಂತೋಷಪಟ್ಟನು.",
			"4": "<sup>4</sup> ಆ ಸ್ತ್ರೀಯ ತಂದೆಯಾದ ಅವನ ಮಾವನು ಅವನನ್ನು ಮೂರು ದಿವಸ ಅಲ್ಲೇ ಉಳಿಸಿಕೊಂಡನು; ಅವನೂ ಅವನ ಸೇವಕನೂ ಅನ್ನಪಾನಗಳನ್ನು ತೆಗೆದುಕೊಳ್ಳುತ್ತಾ ಅಲ್ಲಿದ್ದರು.",
			"5": "<sup>5</sup> ಅವರು ನಾಲ್ಕನೆಯ ದಿನ ಬೆಳಿಗ್ಗೆ ಎದ್ದು ಹೋಗುವುದಕ್ಕೆ ಸಿದ್ಧರಾಗಲು ಆ ಸ್ತ್ರೀಯ ತಂದೆಯು ತನ್ನ ಅಳಿಯನಿಗೆ, “ಮೊದಲು ಸ್ವಲ್ಪ ಊಟಮಾಡಿ ಬಲಹೊಂದು; ಆಮೇಲೆ ಹೋಗಬಹುದು” ಎಂದು ಹೇಳಲು",
			"6": "<sup>6</sup> ಅವರಿಬ್ಬರೂ ಕುಳಿತುಕೊಂಡು ಅನ್ನಪಾನಗಳನ್ನು ತೆಗೆದುಕೊಂಡರು. ಊಟವಾದ ಮೇಲೆ ಆ ಸ್ತ್ರೀಯ ತಂದೆಯು ಪುನಃ ಅವನಿಗೆ, “ದಯವಿಟ್ಟು ಈ ಹೊತ್ತಿನ ರಾತ್ರಿ ಇಲ್ಲೇ ಇದ್ದು ಸಂತೋಷಪಡು” ಎಂದು ಹೇಳಿದರೂ ಆ ಲೇವಿಯನು ಹೊರಡುವುದಕ್ಕಿದ್ದನು;",
			"7": "<sup>7</sup> ಆದರೆ ಅವನ ಮಾವನು ಬಹಳ ಒತ್ತಾಯ ಮಾಡಿದ್ದರಿಂದ ಆ ರಾತ್ರಿ ಅಲ್ಲಿಯೇ ಇಳಿದುಕೊಂಡನು.",
			"8": "<sup>8</sup> ಅವರು ಐದನೆಯ ದಿನ ಬೆಳಿಗ್ಗೆ ಎದ್ದು ಹೋಗುವುದಕ್ಕೆ ಸಿದ್ದರಾಗಲು ಆ ಸ್ತ್ರೀಯ ತಂದೆಯು, “ದಯವಿಟ್ಟು ಮಧ್ಯಾಹ್ನದವರೆಗೆ ಇಲ್ಲೇ ಇದ್ದು ಬಲಹೊಂದು” ಎಂದು ಅವನನ್ನು ಬೇಡಿಕೊಳ್ಳಲು ಅವರು ತಿರುಗಿ ಊಟಕ್ಕೆ ನಿಂತನು.",
			"9": "<sup>9</sup> ಅನಂತರ ಅವನೂ, ಅವನ ಉಪಪತ್ನಿಯೂ, ಸೇವಕನೂ ಹೋಗುವುದಕ್ಕೆ ಸಿದ್ಧರಾಗಲು ಆ ಸ್ತ್ರೀಯ ತಂದೆಯು ತನ್ನ ಅಳಿಯನಿಗೆ, “ಹೊತ್ತು ಹೋಯಿತು; ಸಾಯಂಕಾಲವಾಗುತ್ತಿದೆ, ದಯವಿಟ್ಟು ಈ ಹೊತ್ತಿನ ರಾತ್ರಿ ಇಲ್ಲೇ ಇರು; ನೋಡು, ಈ ರಾತ್ರಿ ಇಲ್ಲಿದ್ದು ನಮ್ಮೊಡನೆ ಸಂತೋಷಪಡು; ನಾಳೆ ಬೆಳಿಗ್ಗೆ ಎದ್ದು ನಿನ್ನ ಮನೆಗೆ ಹೋಗಬಹುದು” ಎಂದು ಬೇಡಿಕೊಂಡನು.",
			"10": "<sup>10</sup> ಆದರೆ ಆ ಲೇವಿಯನು ಒಪ್ಪದೆ ತನ್ನ ಎರಡು ಕತ್ತೆಗಳಿಗೆ ತಡಿಹಾಕಿಸಿ, ಉಪಪತ್ನಿಯನ್ನು ಕರೆದುಕೊಂಡು ಹೊರಟನು.",
			"11": "<sup>11</sup> ಅವರು ಯೆಬೂಸಿಯರ ಊರಾದ ಯೆರೂಸಲೇಮಿನ ಸಮೀಪಕ್ಕೆ ಬಂದಾಗ ಹೊತ್ತುಮುಳುಗುವ ಸಮಯವಾಗಿದ್ದರಿಂದ ಸೇವಕನು ದಣಿಗೆ, “ದಯವಿಟ್ಟು ಬಾ; ನಾವು ಯೆಬೂಸಿಯರ ಈ ಪಟ್ಟಣಕ್ಕೆ ಹೋಗಿ, ಅಲ್ಲಿ ರಾತ್ರಿಯನ್ನು ಕಳೆಯೋಣ” ಅಂದನು.",
			"12": "<sup>12</sup> ಆಗ ದಣಿಯು, “ನಾವು ಇಸ್ರಾಯೇಲ್ಯರಿಲ್ಲದ ಅನ್ಯರ ಪಟ್ಟಣದಲ್ಲಿ ಇಳಿಯಬಾರದು; ಗಿಬೆಯಕ್ಕೆ ಹೋಗೋಣ” ಅಂದನು.",
			"13": "<sup>13</sup> ಅವನು ತಿರುಗಿ ತನ್ನ ಆಳಿಗೆ, “ನಾವು ಬೇಗನೆ ಹೋಗಿ ಮುಂದಿರುವ ಗಿಬೆಯದಲ್ಲಾಗಲಿ, ರಾಮದಲ್ಲಾಗಲಿ ಇಳಿದುಕೊಳ್ಳೋಣ ಬಾ” ಅಂದನು.",
			"14": "<sup>14</sup> ಹೀಗೆ ಅವರು ಮುಂದೆ ಸಾಗಿ ಬೆನ್ಯಾಮೀನ್ಯರ ಗಿಬೆಯ ಊರಿನ ಸಮೀಪಕ್ಕೆ ಬಂದಾಗ ಸೂರ್ಯನು ಮುಳುಗಿದ್ದನು.",
			"15": "<sup>15</sup> ಅವರು ಇಳಿದುಕೊಳ್ಳುವುದಕ್ಕೋಸ್ಕರ ಗಿಬೆಯಕ್ಕೆ ಹೋದರು. ಆದರೆ ಯಾರೂ ಅವರನ್ನು ಮನೆಯಲ್ಲಿ ಉಳಿದುಕೊಳ್ಳಲು ಅವಕಾಶಕೊಡದ ಕಾರಣ ಅವರು ಪಟ್ಟಣದ ಮಧ್ಯದಲ್ಲಿ ಕುಳಿತುಕೊಂಡರು.",
			"16": "<sup>16</sup> ಅಷ್ಟರಲ್ಲಿ ಎಫ್ರಾಯಿಮಿನ ಪರ್ವತಪ್ರದೇಶಕ್ಕೆ ಸೇರಿದ ಒಬ್ಬ ಮುದುಕನು, ಬೆನ್ಯಾಮೀನ್ಯರ ಊರಾದ ಗಿಬೆಯದಲ್ಲಿ ವಾಸವಾಗಿದ್ದ ಆ ಮುದುಕನು ಸಾಯಂಕಾಲವಾದ್ದರಿಂದ ಕೆಲಸವನ್ನು ಮುಗಿಸಿ ಹೊಲದಿಂದ ಮನೆಗೆ ಬರುತ್ತಿದ್ದನು.",
			"17": "<sup>17</sup> ಈ ಮುದುಕನು ಬೀದಿಯಲ್ಲಿ ಕುಳಿತುಕೊಂಡಿದ್ದ ಆ ಪ್ರಯಾಣಿಕನನ್ನು ಕಂಡು, “ನೀನು ಎಲ್ಲಿಂದ ಬಂದೆ? ಎಲ್ಲಿಗೆ ಹೋಗುತ್ತೀ?” ಎಂದು ಕೇಳಲು",
			"18": "<sup>18</sup> ಆ ಲೇವಿಯನು ಅವನಿಗೆ, “ನಾವು ಯೆಹೂದದ ಬೆತ್ಲೆಹೇಮಿನಿಂದ ಎಫ್ರಾಯಿಮಿನ ಪರ್ವತಪ್ರದೇಶದ ಒಂದು ದೂರಸ್ಥಳದಲ್ಲಿರುವ ನಮ್ಮ ವಾಸಸ್ಥಳಕ್ಕೆ ಹೋಗುತ್ತಿದ್ದೇವೆ. ನಾನು ಯೆಹೂದದ ಬೇತ್ಲೆಹೇಮಿಗೆ ಹೋಗಿದ್ದೆನು; ಈಗ ತಿರುಗಿ ಯೆಹೋವನ ಮಂದಿರಕ್ಕೆ ಹೋಗುತ್ತೇನೆ. ಇಲ್ಲಿ ಯಾರೂ ನಮ್ಮನ್ನು ತಮ್ಮ ಮನೆಗೆ ಸೇರಿಸಿಕೊಳ್ಳಲಿಲ್ಲ.",
			"19": "<sup>19</sup> ನಮ್ಮ ಕತ್ತೆಗಳಿಗೆ ಹುಲ್ಲೂ ಕಾಳೂ ಇವೆ; ನನಗೂ ನಿನ್ನ ಸೇವಕಿಗೂ, ನಿನ್ನ ಸೇವಕನ ಸಂಗಡ ಬಂದಿರುವ ಈ ಪ್ರಾಯಸ್ಥನಿಗೂ ಸಾಕಾಗುವಷ್ಟು ರೊಟ್ಟಿಯೂ, ದ್ರಾಕ್ಷಾರಸವೂ ಇರುತ್ತವೆ. ನಮಗೆ ಹೆಚ್ಚು ಏನೂ ಬೇಕಾಗಿರುವುದಿಲ್ಲ” ಎಂದು ಹೇಳಿದನು.",
			"20": "<sup>20</sup> ಆಗ ಆ ಮುದುಕನು, “ನಿನಗೆ ಶುಭವಾಗಲಿ; ನಿನಗೆ ಏನಾದರೂ ಕಡಿಮೆಯಾದರೆ ಅದನ್ನು ನೋಡಿಕೊಳ್ಳುತ್ತೇನೆ;",
			"21": "<sup>21</sup> ಈ ರಾತ್ರಿಯನ್ನು ಬೀದಿಯಲ್ಲಿ ಕಳೆಯಬೇಡ, ನನ್ನ ಮನೆಗೆ ಬಾ” ಎಂದು ಹೇಳಿ ಅವನನ್ನು ಕರೆದುಕೊಂಡು ಹೋಗಿ ಅವನ ಕತ್ತೆಗಳಿಗೆ ಮೇವುಕೊಟ್ಟನು. ಅವರು ತಮ್ಮ ಕೈಕಾಲುಗಳನ್ನು ತೊಳೆದುಕೊಂಡು ಅಲ್ಲಿ ಅನ್ನಪಾನಗಳನ್ನು ತೆಗೆದುಕೊಂಡರು.",
			"22": "<sup>22</sup> ಅವರು ಭೋಜನದಿಂದ ತೃಪ್ತಿಪಡುವಷ್ಟರಲ್ಲಿ ಆ ಊರಲ್ಲಿದ್ದ ನೀಚಜನರು ಬಂದು ಆ ಮನೆಯನ್ನು ಸುತ್ತಿಕೊಂಡು, ಬಾಗಿಲನ್ನು ಬಡಿದು, ಮನೆಯ ಯಜಮಾನನಾದ ಮುದುಕನಿಗೆ, “ನಿನ್ನ ಮನೆಗೆ ಬಂದಿರುವಂಥ ಮನುಷ್ಯನನ್ನು ಹೊರಗೆ ಕರೆದುಕೊಂಡು ಬಾ; ಅವನೊಡನೆ ನಾವು ಸಂಗಿಸಬೇಕು” ಎಂದು ಕೂಗಿದರು.",
			"23": "<sup>23</sup> ಆಗ ಆ ಮುದುಕನು ಹೊರಗೆ ಹೋಗಿ ಅವರಿಗೆ, “ಸಹೋದರರೇ, ಇಂಥ ದುಷ್ಟತನವನ್ನು ನಡೆಸಬೇಡಿರಿ; ಅವನು ಆಶ್ರಯಕ್ಕಾಗಿ ನನ್ನ ಮನೆಗೆ ಬಂದಿದ್ದಾನೆ; ನೀವು ಇಂಥ ಹುಚ್ಚುತನವನ್ನು ಮಾಡಬೇಡಿರಿ.",
			"24": "<sup>24</sup> ಇನ್ನೂ ಮದುವೆಯಾಗದೆ ಇರುವ ನನ್ನ ಮಗಳನ್ನೂ, ಅವನ ಉಪಪತ್ನಿಯನ್ನೂ ಹೊರಗೆ ತರುತ್ತೇನೆ; ಅವರನ್ನು ಭಂಗಪಡಿಸಿರಿ; ನಿಮ್ಮ ಮನಸ್ಸಿಗೆ ಬಂದ ಹಾಗೆ ಅವರಿಗೆ ಮಾಡಿರಿ. ಈ ಮನುಷ್ಯನಿಗೆ ಮಾತ್ರ ಅಂಥ ದುಷ್ಕೃತ್ಯವನ್ನು ಮಾಡಬೇಡಿರಿ” ಅಂದನು.",
			"25": "<sup>25</sup> ಆದರೆ ಅವರು ಆ ಮುದುಕನಿಗೆ ಕಿವಿಗೊಡದೆ ಹೋದುದರಿಂದ ಆ ಮನುಷ್ಯನು ತನ್ನ ಉಪಪತ್ನಿಯನ್ನು ಹೊರಗೆ ಅವರ ಬಳಿಗೆ ಕಳುಹಿಸಿಬಿಟ್ಟನು. ಅವರು ಆಕೆಯನ್ನು ಕೆಡಿಸಿ, ರಾತ್ರಿಯೆಲ್ಲಾ ಬಹುಕ್ರೂರತನದಿಂದ ವರ್ತಿಸಿ, ಬೆಳಗಾಗುವಾಗ ಆಕೆಯನ್ನು ಬಿಟ್ಟುಹೋದರು.",
			"26": "<sup>26</sup> ಆ ಸ್ತ್ರೀಯು ಸೂರ್ಯೋದಯಕ್ಕೆ ಮುಂಚೆಯೇ ಬಂದು ತನ್ನ ಯಜಮಾನನು ಇಳಿದುಕೊಂಡಿದ್ದ ಮನೆಯ ಬಾಗಿಲಲ್ಲಿ ಬಂದು ಬಿದ್ದವಳು ಬೆಳಗಾದರೂ ಏಳಲೇ ಇಲ್ಲ.",
			"27": "<sup>27</sup> ಆಕೆಯ ಪತಿಯು ಬೆಳಿಗ್ಗೆ ಎದ್ದು ಹೊರಡುವುದಕ್ಕೆ ಸಿದ್ಧನಾಗಿ ಬಾಗಿಲನ್ನು ತೆರೆಯಲು, ತನ್ನ ಉಪಪತ್ನಿಯು ಬಾಗಿಲ ಮುಂದೆ ಬಿದ್ದಿರುವುದನ್ನೂ, ಆಕೆಯ ಕೈಗಳು ಹೊಸ್ತಲಿನ ಮೇಲಿರುವುದನ್ನೂ ಕಂಡನು.",
			"28": "<sup>28</sup> ಅವನು, “ಏಳು, ಹೋಗೋಣ” ಎಂದು ಆಕೆಯನ್ನು ಕರೆಯಲು ಪ್ರತ್ಯುತ್ತರವೇ ಇಲ್ಲ. ಆಗ ಅವನು ಆಕೆಯ ಶವವನ್ನು ಕತ್ತೆಯ ಮೇಲೆ ಹಾಕಿಕೊಂಡು ತನ್ನ ಮನೆಗೆ ಹೋದನು.",
			"29": "<sup>29</sup> ಅವನು ತನ್ನ ಮನೆಗೆ ಸೇರಿದ ಮೇಲೆ ಒಂದು ಕತ್ತಿಯನ್ನು ತೆಗೆದುಕೊಂಡು, ತನ್ನ ಉಪಪತ್ನಿಯ ಶವವನ್ನು ಎಲುಬುಗಳ ಸಹಿತವಾಗಿ ಹನ್ನೆರಡು ತುಂಡುಮಾಡಿ, ಅವುಗಳನ್ನು ಇಸ್ರಾಯೇಲರ ಎಲ್ಲಾ ಪ್ರಾಂತ್ಯಗಳಿಗೆ ಕಳುಹಿಸಿದನು.",
			"30": "<sup>30</sup> ಅವುಗಳನ್ನು ಕಂಡವರೆಲ್ಲರೂ, “ಇಸ್ರಾಯೇಲರು ಐಗುಪ್ತವನ್ನು ಬಿಟ್ಟು ಬಂದ ದಿನದಿಂದ ಈ ದಿನದವರೆಗೆ ಇಂಥ ಕೃತ್ಯವು ನಡೆಯಲೇ ಇಲ್ಲ, ನಾವು ಅದನ್ನು ನೋಡಿಯೂ ಇಲ್ಲ. ಈ ಸಂಗತಿಯನ್ನು ಮನಸ್ಸಿಗೆ ತಂದು ವಿಚಾರಿಸತಕ್ಕದ್ದು” ಅಂದುಕೊಂಡರು."
		},
		"20": {
			"1": "<sup>1</sup> ದಾನ್ ಪಟ್ಟಣದಿಂದ ಬೇರ್ಷೆಬದವರೆಗಿರುವ ಪ್ರಾಂತ್ಯಗಳಲ್ಲಿಯೂ, ಗಿಲ್ಯಾದಿನಲ್ಲಿಯೂ ಇರುವ ಇಸ್ರಾಯೇಲರೆಲ್ಲರೂ ಏಕಮನಸ್ಸಿನಿಂದ ಹೊರಟು ಮಿಚ್ಪೆಗೆ ಬಂದು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸಭೆ ಸೇರಿದರು.",
			"2": "<sup>2</sup> ಆ ದೇವಜನರ ಸಭೆಯಲ್ಲಿ ಎಲ್ಲಾ ಕುಲಾಧಿಪತಿಗಳೂ, ಯುದ್ಧಸನ್ನದ್ಧರಾದ ನಾಲ್ಕು ಲಕ್ಷ ಕಾಲಾಳುಗಳೂ ಇದ್ದರು.",
			"3": "<sup>3</sup> ಇಸ್ರಾಯೇಲರು ಕೂಡಿಕೊಂಡು ಮಿಚ್ಪೆಗೆ ಬಂದಿದ್ದಾರೆಂಬ ವರ್ತಮಾನವು ಬೆನ್ಯಾಮೀನ್ಯರಿಗೆ ಮುಟ್ಟಿತು. ಇಸ್ರಾಯೇಲರು, “ಈ ದುಷ್ಟತನವು ಹೇಗೆ ನಡೆಯಿತೆಂದು ತಿಳಿಸಿರಿ” ಎಂದು ಕೇಳಲು",
			"4": "<sup>4</sup> ಹತಳಾದ ಸ್ತ್ರೀಯ ಗಂಡನಾದ ಆ ಲೇವಿಯನು, “ನಾನು ನನ್ನ ಉಪಪತ್ನಿಯ ಸಹಿತವಾಗಿ ಬೆನ್ಯಾಮೀನ್ಯರ ಊರಾದ ಗಿಬೆಯದಲ್ಲಿ ಒಂದು ರಾತ್ರಿ ಉಳಿದುಕೊಂಡೆನು.",
			"5": "<sup>5</sup> ಆ ಊರಿನ ಜನರು ನನ್ನನ್ನು ಕೊಲ್ಲಬೇಕೆಂದು ಅದೇ ರಾತ್ರಿ ನಾನು ಇಳಿದುಕೊಂಡಿದ್ದ ಮನೆಯನ್ನು ಸುತ್ತಿಕೊಂಡರು, ಮತ್ತು ನನ್ನ ಉಪಪತ್ನಿಯನ್ನು ಭಂಗಪಡಿಸಿದರು, ಆಕೆಯು ಸತ್ತುಹೋದಳು.",
			"6": "<sup>6</sup> ಅವರು ಇಸ್ರಾಯೇಲರಲ್ಲಿ ನಡೆಸಿದ ಇಂಥ ಕೆಟ್ಟ ಮತ್ತು ಕ್ರೂರ ಕೆಲಸವು ಎಲ್ಲರಿಗೂ ಗೊತ್ತಾಗಲೆಂದು ನಾನು ಆಕೆಯ ಶವವನ್ನು ತುಂಡುಮಾಡಿ ಇಸ್ರಾಯೇಲರ ಎಲ್ಲಾ ಪ್ರಾಂತ್ಯಗಳಿಗೆ ಕಳುಹಿಸಿದೆನು.",
			"7": "<sup>7</sup> ಇಲ್ಲಿ ಕೂಡಿರುವ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲರೆ, ಈಗ ನಿಮ್ಮ ಆಲೋಚನೆ ಮತ್ತು ಅಭಿಪ್ರಾಯಗಳನ್ನು ಹೇಳಿರಿ” ಅಂದನು.",
			"8": "<sup>8</sup> ಇದನ್ನು ಕೇಳಿ ಜನರು ಏಕಮನಸ್ಸಿನಿಂದ, “ನಮ್ಮಲ್ಲಿ ಯಾವನೂ ತನ್ನ ಗುಡಾರಕ್ಕೆ ಹೋಗಬಾರದು; ತನ್ನ ಮನೆಗೆ ಹಿಂದಿರುಗದಿರಲಿ.",
			"9": "<sup>9</sup> ನಾವು ಚೀಟು ಹಾಕೋಣ; ಅದು ಬಿದ್ದ ಪ್ರಕಾರವೇ ಗಿಬೆಯದವರಿಗೆ ವಿರೋಧವಾಗಿ ಯುದ್ಧಕ್ಕೆ ಹೋಗೋಣ.",
			"10": "<sup>10</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಕುಲಗಳಲ್ಲಿ ನೂರಕ್ಕೆ ಹತ್ತು, ಸಾವಿರಕ್ಕೆ ನೂರು, ಹತ್ತು ಸಾವಿರಕ್ಕೆ ಸಾವಿರ ಈ ಪ್ರಕಾರ ಜನರನ್ನು ಆರಿಸಿಕೊಂಡು ಅವರನ್ನು ಆಹಾರ ತರುವುದಕ್ಕಾಗಿ ಕಳುಹಿಸೋಣ. ಅವರು ಬಂದ ಮೇಲೆ ಬೆನ್ಯಾಮೀನ್ಯರಾದ ಗಿಬೆಯದವರು ಇಸ್ರಾಯೇಲರಲ್ಲಿ ನಡೆಸಿದ ದುಷ್ಟ ಕಾರ್ಯಕ್ಕಾಗಿ ಅವರಿಗೆ ದಂಡನೆಮಾಡೋಣ” ಅಂದುಕೊಂಡರು.",
			"11": "<sup>11</sup> ಹೀಗೆ ಅವರೆಲ್ಲರೂ ಏಕಮನಸ್ಸಿನಿಂದ ಆ ಪಟ್ಟಣಕ್ಕೆ ವಿರೋಧವಾಗಿ ಕೂಡಿಕೊಂಡರು.",
			"12": "<sup>12</sup> ಅವರು ಬೆನ್ಯಾಮೀನ್ಯರ ಸಮಸ್ತ ಕುಲದವರ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿ ಅವರಿಗೆ, “ನಿಮ್ಮಲ್ಲಿ ನಡೆದ ಈ ದುಷ್ಕೃತ್ಯ ಎಂಥದು?",
			"13": "<sup>13</sup> ಈಗ ಗಿಬೆಯ ಊರಲ್ಲಿರುವ ಆ ನೀಚರನ್ನು ನಮಗೆ ಒಪ್ಪಿಸಿರಿ; ನಾವು ಅವರನ್ನು ಕೊಂದು ಇಸ್ರಾಯೇಲರ ಮಧ್ಯದಿಂದ ಇಂಥ ದುಷ್ಟತನವನ್ನು ತೆಗೆದುಹಾಕುತ್ತೇವೆ” ಎಂದು ಹೇಳಿದರು.",
			"14": "<sup>14</sup> ಆದರೆ ಬೆನ್ಯಾಮೀನ್ಯರು ತಮ್ಮ ಬಂಧುಗಳ ಮಾತಿಗೆ ಕಿವಿಗೊಡದೆ ತಮ್ಮ ಎಲ್ಲಾ ಊರುಗಳಿಂದ ಗಿಬೆಯಕ್ಕೆ ಬಂದು ಇಸ್ರಾಯೇಲ್ಯರ ಸಂಗಡ ಯುದ್ಧಕ್ಕೆ ಸನ್ನದ್ಧರಾದರು.",
			"15": "<sup>15</sup> ಆ ದಿನದಲ್ಲಿ ಆಯಾ ಊರುಗಳಿಂದ ಯುದ್ಧಸನ್ನದ್ಧರಾಗಿ ಸೇರಿಬಂದ ಬೆನ್ಯಾಮೀನ್ಯರ ಸಂಖ್ಯೆಯು ಇಪ್ಪತ್ತಾರು ಸಾವಿರವಾಗಿತ್ತು. ಇವರಲ್ಲದೆ ಗಿಬೆಯದಲ್ಲಿಯೇ ಏಳು ನೂರು ಮಂದಿ ಯುದ್ಧವೀರರಿದ್ದರು.",
			"16": "<sup>16</sup> ಈ ಎಲ್ಲಾ ಜನರಲ್ಲಿ ಏಳುನೂರು ಜನರು ಎಡಚರಾದ ಯುದ್ಧವೀರರಿದ್ದರು. ಅವರಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬರು ಕೂದಲೆಳೆಯಷ್ಟು ಗುರಿತಪ್ಪದ ಹಾಗೆ ಕವಣೆಯನ್ನು ಹೊಡೆಯುವುದರಲ್ಲಿ ನಿಪುಣರಾಗಿದ್ದರು.",
			"17": "<sup>17</sup> ಬೆನ್ಯಾಮೀನ್ಯರಲ್ಲದ ಇಸ್ರಾಯೇಲರಲ್ಲಿ ನಾಲ್ಕು ಲಕ್ಷ ಯೋಧರಿದ್ದರು; ಇವರೆಲ್ಲರೂ ವೀರರಾಗಿದ್ದರು.",
			"18": "<sup>18</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಬೇತೇಲಿಗೆ ಹೋಗಿ, “ಬೆನ್ಯಾಮೀನ್ಯರ ಮೇಲೆ ಯುದ್ಧಕ್ಕೆ ನಮ್ಮಲ್ಲಿ ಮೊದಲು ಯಾರು ಹೋಗಬೇಕು?” ಎಂದು ದೇವರಾದ ಯೆಹೋವನನ್ನು ಕೇಳಲು ಆತನು, “ಮೊದಲು ಯೆಹೂದಕುಲದವರು ಹೋಗಲಿ” ಎಂದು ಹೇಳಿದನು.",
			"19": "<sup>19</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಬೆಳಗ್ಗೆ ಹೊರಟುಹೋಗಿ ಗಿಬೆಯದ ಎದುರಿನಲ್ಲಿ ಪಾಳೆಯಮಾಡಿಕೊಂಡು,",
			"20": "<sup>20</sup> ಬೆನ್ಯಾಮೀನ್ಯರೊಡನೆ ಯುದ್ಧಮಾಡುವುದಕ್ಕೋಸ್ಕರ ಸಿದ್ಧರಾಗಿ, ಗಿಬೆಯದ ಎದುರಿನಲ್ಲಿ ವ್ಯೂಹಕಟ್ಟಿ ನಿಂತರು.",
			"21": "<sup>21</sup> ಆ ದಿನದಲ್ಲಿ ಬೆನ್ಯಾಮೀನ್ಯರು ಗಿಬೆಯದಿಂದ ಹೊರಗೆ ಬಂದು ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಇಪ್ಪತ್ತೆರಡು ಸಾವಿರ ಜನರನ್ನು ನೆಲಕ್ಕುರುಳಿಸಿದರು.",
			"22": "<sup>22</sup> ಇಸ್ರಾಯೇಲ್ಯರು ತಿರುಗಿ ಧೈರ್ಯ ತಂದುಕೊಂಡು ಮೊದಲನೆಯ ದಿನದಲ್ಲಿ ಪ್ಯೂಹಕಟ್ಟಿದ ಸ್ಥಳದಲ್ಲೇ ತಿರುಗಿ ವ್ಯೂಹಕಟ್ಟಿ ಯುದ್ಧಕ್ಕೆ ನಿಂತರು.",
			"23": "<sup>23</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಹೋಗಿ ಯೆಹೋವನ ಮುಂದೆ ಅಳುತ್ತಾ, “ನಾವು ತಿರುಗಿ ನಮ್ಮ ಬಂಧುಗಳಾದ ಬೆನ್ಯಾಮೀನ್ಯರೊಡನೆ ಯುದ್ಧಮಾಡಬೇಕೋ?” ಎಂದು ಕೇಳಲು, ಆತನು, “ಹೋಗಿ ಯುದ್ಧಮಾಡಿರಿ” ಅಂದನು.",
			"24": "<sup>24</sup> ಆದ್ದರಿಂದ ಅವರು ಎರಡನೆಯ ದಿನದಲ್ಲಿಯೂ ಯುದ್ಧಕ್ಕೆ ಸಿದ್ಧರಾಗಿ ಬೆನ್ಯಾಮೀನ್ಯರ ಸಮೀಪಕ್ಕೆ ಬಂದರು.",
			"25": "<sup>25</sup> ಬೆನ್ಯಾಮೀನ್ಯರು ಎರಡನೆಯ ದಿನದಲ್ಲಿಯೂ ಗಿಬೆಯದಿಂದ ಹೊರಗೆ ಬಂದು ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಹದಿನೆಂಟು ಸಾವಿರ ಮಂದಿ ಯುದ್ಧವೀರರನ್ನು ನೆಲಕ್ಕೆ ಉರುಳಿಸಿದರು.",
			"26": "<sup>26</sup> ಆಗ ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರೂ, ಎಲ್ಲಾ ಜನರೂ ಬೇತೇಲಿಗೆ ಹೋಗಿ ಅಲ್ಲಿ ಯೆಹೋವನ ಮುಂದೆ ಅಳುತ್ತಾ ಬಿದ್ದು ಸಾಯಂಕಾಲದ ವರೆಗೆ ಉಪವಾಸಮಾಡಿದರು. ಅಲ್ಲಿ ಅವರು ಆತನಿಗೆ ಸರ್ವಾಂಗಹೋಮಗಳನ್ನೂ, ಸಮಾಧಾನ ಯಜ್ಞಗಳನ್ನೂ ಸಮರ್ಪಿಸಿದರು.",
			"27": "<sup>27</sup> ಆ ಕಾಲದಲ್ಲಿ ಯೆಹೋವನ ಒಡಂಬಡಿಕೆಯ ಮಂಜೂಷವು ಆ ಊರೊಳಗಿತ್ತು;",
			"28": "<sup>28</sup> ಆರೋನನ ಮೊಮ್ಮಗನೂ, ಎಲ್ಲಾಜಾರನ ಮಗನೂ ಆದ ಫೀನೆಹಾಸನು ಯಾಜಕಸೇವೆಮಾಡುತ್ತಿದ್ದನು. ಹೀಗಿರುವುದರಿಂದ ಇಸ್ರಾಯೇಲ್ಯರು, “ನಾವು ನಮ್ಮ ಬಂಧುಗಳಾದ ಬೆನ್ಯಾಮೀನ್ಯರೊಡನೆ ಯುದ್ಧಕ್ಕೆ ಹೋಗಬೇಕೋ, ಬೇಡವೋ?” ಎಂದು ಯೆಹೋವನನ್ನು ಕೇಳಿದರು. ಆತನು ಅವರಿಗೆ, “ಹೋಗಿರಿ, ನಾಳೆ ಅವರನ್ನು ನಿಮ್ಮ ಕೈಗೆ ಒಪ್ಪಿಸುವೆನು” ಅಂದನು.",
			"29": "<sup>29</sup> ಆಗ ಇಸ್ರಾಯೇಲ್ಯರು ಗಿಬೆಯದ ಸುತ್ತಲೂ ಕೆಲವರನ್ನು ಹೊಂಚು ಹಾಕುವುದಕ್ಕೆ ಇರಿಸಿ,",
			"30": "<sup>30</sup> ಉಳಿದವರು ಬೆನ್ಯಾಮೀನ್ಯರ ಸಂಗಡ ಕಾದಾಡುವುದಕ್ಕೋಸ್ಕರ ಮುಂಚಿನಂತೆ ಮೂರನೆಯ ದಿನದಲ್ಲಿಯೂ ಗಿಬೆಯದ ಸಮೀಪದಲ್ಲಿ ವ್ಯೂಹಕಟ್ಟಿದರು.",
			"31": "<sup>31</sup> ಬೆನ್ಯಾಮೀನ್ಯರು ಇಸ್ರಾಯೇಲ್ಯರೊಡನೆ ಯುದ್ಧಮಾಡವುದಕ್ಕಾಗಿ ಪಟ್ಟಣವನ್ನು ಬಿಟ್ಟು ಊರೊಳಕ್ಕೆ ಬಂದರು. ಅವರು ಮೊದಲಿನಂತೆ ಜನರನ್ನು ಕೊಲ್ಲುವುದಕ್ಕೆ ಪ್ರಾರಂಭಿಸಿ ಬೇತೇಲಿಗೂ, ಬೈಲಿನಲ್ಲಿರುವ ಗಿಬೆಯಕ್ಕೂ ಹೋಗುವ ರಾಜಮಾರ್ಗಗಳಲ್ಲಿ ಸುಮಾರು ಮೂವತ್ತು ಮಂದಿಯನ್ನು ಹತಮಾಡಿದರು.",
			"32": "<sup>32</sup> ಬೆನ್ಯಾಮೀನ್ಯರು, “ಅವರು ಮುಂಚಿನಂತೆ ಈಗಲೂ ಸೋತುಹೋಗುತ್ತಾರೆ” ಎಂದು ಅಂದುಕೊಂಡರು. ಇಸ್ರಾಯೇಲ್ಯರಾದರೋ, “ಅವರು ತಮ್ಮ ಪಟ್ಟಣಕ್ಕೆ ದೂರವಾಗುವಂತೆ ನಾವು ಸ್ವಲ್ಪ ದೂರ ಓಡಿಹೋಗೋಣ” ಎಂದು ಮಾತನಾಡಿಕೊಂಡು,",
			"33": "<sup>33</sup> ತಾವು ಮೊದಲು ನಿಂತ ಸ್ಥಳದಿಂದ ಓಡತೊಡಗಿದರು. ಬಾಳ್‌ತಾಮರಿಗೆ ಮುಟ್ಟಿದ ಕೂಡಲೆ ಅಲ್ಲಿ ನಿಂತು ತಿರುಗಿ ಯುದ್ಧವನ್ನು ಪ್ರಾರಂಭಿಸಿದರು. ಅಷ್ಟರಲ್ಲಿ ಹೊಂಚಿ ನೋಡುತ್ತಿದ್ದವರು ತಾವು ಅಡಗಿಕೊಂಡಿದ್ದ ಗಿಬೆಯದ ಮೈದಾನದಿಂದ ಎದ್ದು",
			"34": "<sup>34</sup> ಪಟ್ಟಣದ ಮುಂದೆ ಬಂದರು; ಅವರು ಸುಮಾರು ಹತ್ತು ಸಾವಿರ ಮಂದಿ ಯುದ್ಧವೀರರು. ಯುದ್ಧವು ಬಹುಘೋರವಾಯಿತು. ತಮಗೆ ಅಪಾಯವು ಒದಗಿ ಬಂದಿದೆ ಎಂಬುದು ಬೆನ್ಯಾಮೀನ್ಯರಿಗೆ ಇನ್ನೂ ಗೊತ್ತಾಗಿರಲಿಲ್ಲ.",
			"35": "<sup>35</sup> ಯೆಹೋವನು ಇಸ್ರಾಯೇಲರಿಗೆ ಜಯವನ್ನು ಕೊಟ್ಟಿದ್ದರಿಂದ ಅವರು ಆ ದಿನದಲ್ಲಿ ಇಪ್ಪತ್ತೈದು ಸಾವಿರದ ನೂರು ಮಂದಿ ಬೆನ್ಯಾಮೀನ್ಯರ ಯೋಧರನ್ನು ಹತಮಾಡಿದರು.",
			"36": "<sup>36</sup> ತಾವು ಸೋತುಹೋದೆವೆಂಬುದು ಬೆನ್ಯಾಮೀನ್ಯರಿಗೆ ಆಗ ತಿಳಿಯಿತು. ಇಸ್ರಾಯೇಲ್ಯರು ತಾವು ಗಿಬೆಯದ ಸಮೀಪದಲ್ಲಿ ಹೊಂಚು ಹಾಕುವುದಕ್ಕಾಗಿ ಇಟ್ಟ ಜನರಲ್ಲಿ ಭರವಸವುಳ್ಳವರಾಗಿದ್ದರಿಂದ, ತಮ್ಮ ಸ್ಥಳವನ್ನು ಬಿಟ್ಟು ಬೆನ್ಯಾಮೀನ್ಯರ ಮುಂದೆ ಓಡಿಹೋದರು.",
			"37": "<sup>37</sup> ಅಷ್ಟರಲ್ಲಿ ಅಡಗಿಕೊಂಡಿದ್ದವರು ಎದ್ದು ಗಿಬೆಯಕ್ಕೆ ಶೀಘ್ರವಾಗಿ ಹೋಗಿ, ಅದರೊಳಗಿದ್ದವರೆಲ್ಲರನ್ನೂ ಕತ್ತಿಯಿಂದ ಸಂಹರಿಸಿದರು.",
			"38": "<sup>38</sup> ಅಡಗಿದ್ದವರು ಊರಿಗೆ ಬೆಂಕಿಹೊತ್ತಿಸಿ ಹೊಗೆ ಏರಿಹೋಗುವಂತೆ ಮಾಡಿದ ಮೇಲೆ, ಓಡಿಹೋಗುತ್ತಿದ್ದವರು ಪುನಃ ಯುದ್ಧಕ್ಕೆ ನಿಲ್ಲಬೇಕೆಂದು ಹೊಂಚು ಹಾಕುವವರೂ ಹಾಗು ಉಳಿದ ಇಸ್ರಾಯೇಲರೂ ತಮ್ಮ ತಮ್ಮೊಳಗೆ ಮೊದಲೇ ಗೊತ್ತುಮಾಡಿಕೊಂಡಿದ್ದರು.",
			"39": "<sup>39</sup> ಬೆನ್ಯಾಮೀನ್ಯರಾದರೋ, “ಇಸ್ರಾಯೇಲ್ಯರು ಮುಂಚಿನಂತೆ ನಮ್ಮ ಮುಂದೆ ಸೋತುಹೋದರು” ಎಂದು ತಿಳಿದು ಅವರನ್ನು ಹತಮಾಡಲು ಪ್ರಾರಂಭಿಸಿ, ಸುಮಾರು ಮೂವತ್ತು ಮಂದಿಯನ್ನು ಕೊಂದುಹಾಕಿದರು.",
			"40": "<sup>40</sup> ಆದರೆ ಪಟ್ಟಣದಿಂದ ಹೊಗೆ ಎದ್ದು ಸ್ತಂಭದೋಪಾದಿಯಲ್ಲಿ ಕಾಣಿಸಿಕೊಳ್ಳಲು ಇಸ್ರಾಯೇಲರು ತಿರುಗಿ ನಿಂತರು.",
			"41": "<sup>41</sup> ಬೆನ್ಯಾಮೀನ್ಯರು ಹಿಂದಿರುಗಿ ನೋಡಿ ತಮ್ಮ ಪಟ್ಟಣವು ಬೆಂಕಿಯಿಂದ ಸುಟ್ಟುಹೋಗುವುದನ್ನು ಕಂಡು ತಮಗೆ ಅಪಾಯ ಉಂಟಾಗುವುದೆಂದು ಕಳವಳಗೊಂಡರು.",
			"42": "<sup>42</sup> ಅವರು ಇಸ್ರಾಯೇಲರಿಗೆ ಬೆನ್ನು ತೋರಿಸಿ ಅರಣ್ಯಮಾರ್ಗವಾಗಿ ಓಡಿಹೋದರು. ಆದರೆ ಯುದ್ಧವೀರರು ಅವರನ್ನು ಹಿಂದಟ್ಟಿದ್ದರಿಂದಲೂ, ಪಟ್ಟಣಕ್ಕೆ ಬೆಂಕಿ ಹೊತ್ತಿಸಿದವರು ಹೊರಗೆ ಬಂದುಬಿಟ್ಟಿದ್ದರಿಂದಲೂ, ಅವರು ಇಬ್ಬರ ಮಧ್ಯದಲ್ಲಿ ಸಿಕ್ಕಿಹಾಕಿಕೊಂಡರು.",
			"43": "<sup>43</sup> ಇಸ್ರಾಯೇಲರು ಅವರನ್ನು ಸುತ್ತಿಕೊಂಡು, ಎಲ್ಲಿಯೂ ಓಡಿಹೋಗದಂತೆ, ಗಿಬೆಯ ಊರಿನ ಪೂರ್ವದಿಕ್ಕಿನಲ್ಲಿರುವ ಮೆನೂಹದಲ್ಲಿ ತುಳಿದುಹಾಕಿಬಿಟ್ಟರು.",
			"44": "<sup>44</sup> ಈ ಪ್ರಕಾರ ಬೆನ್ಯಾಮೀನ್ಯರಲ್ಲಿ ಹದಿನೆಂಟು ಸಾವಿರ ಮಂದಿ ಯುದ್ಧವೀರರು ಹತರಾದರು.",
			"45": "<sup>45</sup> ಇದಲ್ಲದೆ ಅವರು ಅರಣ್ಯದಲ್ಲಿರುವ ರಿಮ್ಮೋನ್ ಗಿರಿಗೆ ಓಡಿಹೋಗುವಾಗ ಹಕ್ಕಲು ತೆನೆಗಳನ್ನೋ ಎಂಬಂತೆ ಐದು ಸಾವಿರ ಜನರನ್ನು ರಾಜಮಾರ್ಗಗಳಲ್ಲಿ ಕೊಂದು ಹಾಕಿದರು. ಅಲ್ಲಿಂದ ಗಿದೋಮಿನವರೆಗೆ ಹಿಂದಟ್ಟಿ ತಿರುಗಿ ಎರಡು ಸಾವಿರ ಜನರನ್ನು ಹತಮಾಡಿದರು.",
			"46": "<sup>46</sup> ಹೀಗೆ ಆ ದಿನ ಬೆನ್ಯಾಮೀನ್ಯರಲ್ಲಿ ಇಪ್ಪತ್ತೈದು ಸಾವಿರ ಯುದ್ಧಸನ್ನದ್ಧರಾದ ಸೈನಿಕರು ಸಂಹರಿಸಲ್ಪಟ್ಟರು.",
			"47": "<sup>47</sup> ಆದರೆ ಆರು ನೂರು ಮಂದಿ ಅರಣ್ಯಮಾರ್ಗವಾಗಿ ಓಡಿಹೋಗಿ, ರಿಮ್ಮೋನ್ ಗಿರಿಯನ್ನು ಸೇರಿ ಅಲ್ಲಿ ನಾಲ್ಕು ತಿಂಗಳಿದ್ದರು.",
			"48": "<sup>48</sup> ಇಸ್ರಾಯೇಲರು ಪುನಃ ಹಿಂತಿರುಗಿ ಬಂದು ಬೆನ್ಯಾಮೀನ್ ದೇಶದ ಉಳಿದ ಗ್ರಾಮನಗರಗಳಿಗೆ ಹೋಗಿ ಅವುಗಳ ನಿವಾಸಿಗಳನ್ನೂ, ದನಕುರಿಗಳನ್ನೂ, ಸಿಕ್ಕಿದ್ದೆಲ್ಲವನ್ನು ಸಂಹರಿಸಿ ಎಲ್ಲಾ ಊರುಗಳನ್ನು ಬೆಂಕಿಯಿಂದ ಸುಟ್ಟುಬಿಟ್ಟರು."
		},
		"21": {
			"1": "<sup>1</sup> ಇಸ್ರಾಯೇಲರೆಲ್ಲರೂ ಮಿಚ್ಪೆಯಲ್ಲಿದ್ದಾಗ ತಾವು ಬೆನ್ಯಾಮೀನ್ಯರಿಗೆ ತಮ್ಮ ಹೆಣ್ಣುಗಳನ್ನು ಕೊಡುವುದಿಲ್ಲವೆಂದು ಆಣೆಯಿಟ್ಟಿದ್ದರು.",
			"2": "<sup>2</sup> ಅವರು ಬೇತೇಲಿಗೆ ಬಂದು ಸಾಯಂಕಾಲದವರೆಗೆ ದೇವರ ಮುಂದೆ ಕುಳಿತು, ಗಟ್ಟಿಯಾಗಿ ಅಳುತ್ತಾ,",
			"3": "<sup>3</sup> “ಯೆಹೋವನೇ, ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರೇ, ನಮ್ಮಲ್ಲಿ ಹೀಗೆ ಯಾಕೆ ಆಯಿತು? ಇಸ್ರಾಯೇಲರ ಒಂದು ಕುಲವು ಹಾಳಾಗಿ ಹೋಯಿತಲ್ಲಾ” ಎಂದು ಕೂಗಿದರು.",
			"4": "<sup>4</sup> ಮರುದಿನ ಅವರು ಬೆಳಿಗ್ಗೆ ಎದ್ದು ಯಜ್ಞವೇದಿಯನ್ನು ಕಟ್ಟಿ ಸರ್ವಾಂಗಹೋಮಗಳನ್ನೂ, ಸಮಾಧಾನಯಜ್ಞಗಳನ್ನೂ ಸಮರ್ಪಿಸಿದರು.",
			"5": "<sup>5</sup> ಮತ್ತು ಯೆಹೋವನ ಮುಂದೆ ಸಭೆ ಸೇರಿದಾಗ, ಬಾರದೆ ಇದ್ದಂಥ ಇಸ್ರಾಯೇಲರು ಯಾರಾರೆಂದು ವಿಚಾರಮಾಡಿದರು. ಯಾಕೆಂದರೆ ಮಿಚ್ಬೆಯಲ್ಲಿ, “ಯೆಹೋವನ ಸನ್ನಿಧಿಗೆ ಬಾರದವರನ್ನು ಕೊಂದು ಹಾಕುವೆವು” ಎಂದು ಆಣೆಯಿಟ್ಟು ಪ್ರಮಾಣಮಾಡಿದ್ದರು.",
			"6": "<sup>6</sup> ಇಸ್ರಾಯೇಲರು ತಮ್ಮ ಬಂಧುಗಳಾದ ಬೆನ್ಯಾಮೀನ್ಯರ ಅವಸ್ಥೆಯನ್ನು ಕಂಡು ದುಃಖದಿಂದ, “ಅಯ್ಯೋ, ಇಸ್ರಾಯೇಲರಾದ ನಮ್ಮಲ್ಲಿ ಒಂದು ಕುಲವು ಕಡಿಮೆಯಾಯಿತಲ್ಲಾ;",
			"7": "<sup>7</sup> ಜೀವದಿಂದುಳಿದವರಿಗೆ ಹೆಂಡತಿಯರನ್ನು ದೊರಕಿಸಿಕೊಡುವುದು ಹೇಗೆ? ನಮ್ಮ ಹೆಣ್ಣುಗಳನ್ನು ಅವರಿಗೆ ಕೊಡುವುದಿಲ್ಲವೆಂದು ಯೆಹೋವನ ಹೆಸರಿನಲ್ಲಿ ಆಣೆಯಿಟ್ಟಿದ್ದೇವಲ್ಲಾ” ಎಂದು ಗೋಳಾಡಿದರು.",
			"8": "<sup>8</sup> ಮಿಚ್ಬೆಯಲ್ಲಿ ಯೆಹೋವನ ಮುಂದೆ ಸಭೆ ಸೇರಿದಾಗ, ಅದಕ್ಕೆ ಬಾರದಿದ್ದ ಇಸ್ರಾಯೇಲರು ಯಾರಾದರೂ ಇದ್ದಾರೋ ಎಂದು ವಿಚಾರಿಸಿದಾಗ ಯಾಬೇಷ್‍ಗಿಲ್ಯಾದಿನಿಂದ ಒಬ್ಬನೂ ಬರಲಿಲ್ಲವೆಂದು ಗೊತ್ತಾಯಿತು.",
			"9": "<sup>9</sup> ಜನರನ್ನು ಲೆಕ್ಕಿಸಿದಾಗ ಯಾಬೇಷ್ ಗಿಲ್ಯಾದಿನಿಂದ ಯಾರೂ ಬರಲಿಲ್ಲವೆಂಬದು ದೃಢವಾಯಿತು.",
			"10": "<sup>10</sup> ಆಗ ಅವರು ತಮ್ಮಲ್ಲಿಂದ ಹನ್ನೆರಡು ಸಾವಿರ ಮಂದಿ ಪರಾಕ್ರಮಶಾಲಿಗಳನ್ನು ಆರಿಸಿಕೊಂಡು ಅವರಿಗೆ, “ನೀವು ಯಾಬೇಷ್ ಗಿಲ್ಯಾದಿಗೆ ಹೋಗಿ ಎಲ್ಲಾ ನಿವಾಸಿಗಳನ್ನೂ, ಸ್ತ್ರೀಯರನ್ನೂ, ಚಿಕ್ಕ ಮಕ್ಕಳನ್ನೂ ಬಿಡದೆ ಸಂಹರಿಸಬೇಕು;",
			"11": "<sup>11</sup> ಹೇಗೂ ಪುರುಷರನ್ನು, ಮದುವೆಯಾದ ಸ್ತ್ರೀಯರನ್ನೂ ನಿರ್ಮೂಲಮಾಡುವುದು ನಿಮ್ಮ ಕೆಲಸ” ಎಂದು ಆಜ್ಞಾಪಿಸಿ ಕಳುಹಿಸಿದರು.",
			"12": "<sup>12</sup> ಯಾಬೇಷ್ ಗಿಲ್ಯಾದಿನಲ್ಲಿ ಇನ್ನೂ ಮದುವೆಯಾಗದಿದ್ದ ನಾನೂರು ಮಂದಿ ಯುವತಿಯರಿದ್ದರು. ಪಟ್ಟಣವನ್ನು ಸಂಹರಿಸುವುದಕ್ಕೆ ಹೋದವರು ಇವರನ್ನು ಉಳಿಸಿ, ಕಾನಾನ್ ದೇಶದ ಶೀಲೋವಿನಲ್ಲಿದ್ದ ಪಾಳೆಯಕ್ಕೆ ತೆಗೆದುಕೊಂಡು ಬಂದರು.",
			"13": "<sup>13</sup> ಅನಂತರ ಸರ್ವಸಭೆಯವರು ರಿಮ್ಮೋನ್ ಗಿರಿಯಲ್ಲಿದ್ದ ಬೆನ್ಯಾಮೀನ್ಯರ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿ ಅವರಲ್ಲಿ ಸಮಾಧಾನವಾಕ್ಯವನ್ನು ಪ್ರಕಟಿಸಲು ಅವರು ತಿರುಗಿ ಬಂದರು.",
			"14": "<sup>14</sup> ಇಸ್ರಾಯೇಲರು ತಾವು ಯಾಬೇಷ್ ಗಿಲ್ಯಾದಿನಿಂದ ಉಳಿಸಿ ತಂದ ಕನ್ಯೆಯರನ್ನು ಅವರಿಗೆ ಮದುವೆಮಾಡಿಕೊಟ್ಟರು; ಆದರೂ ಅನೇಕರಿಗೆ ಕನ್ಯೆಯರು ದೊರೆಯಲಿಲ್ಲ.",
			"15": "<sup>15</sup> ಯೆಹೋವನು ಇಸ್ರಾಯೇಲಿನ ಕುಲಗಳಲ್ಲಿ ಬೆನ್ಯಾಮೀನರನ್ನು ಬೇರ್ಪಡಿಸಿದ್ದರಿಂದ ಜನರು ಬೆನ್ಯಾಮೀನ್ಯರ ವಿಷಯದಲ್ಲಿ ದುಃಖಪಟ್ಟರು.",
			"16": "<sup>16</sup> ಸಭೆಯ ಹಿರಿಯರು, “ಉಳಿದಿರುವ ಬೆನ್ಯಾಮೀನ್ಯರಿಗೋಸ್ಕರ ಹೆಂಡತಿಯರನ್ನು ದೊರಕಿಸಿ ಕೊಡುವುದು ಹೇಗೆ? ಅವರ ಸ್ತ್ರೀಯರೆಲ್ಲಾ ಸಂಹಾರವಾದರಲ್ಲಾ.",
			"17": "<sup>17</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಒಂದು ಪೂರ್ಣ ಕುಲವು ಅಳಿದುಹೋಗಬಾರದು; ತಪ್ಪಿಸಿಕೊಂಡು ಉಳಿದಿರುವ ಬೆನ್ಯಾಮೀನರಿಗೆ ಬಾಧ್ಯಸ್ಥರು ಹುಟ್ಟುಬೇಕಲ್ಲಾ.",
			"18": "<sup>18</sup> ಬೆನ್ಯಾಮೀನ್ಯರಿಗೆ ತಮ್ಮ ಹೆಣ್ಣುಗಳನ್ನು ಕೊಡುವ ಇಸ್ರಾಯೇಲರು ಶಾಪಗ್ರಸ್ತರಾಗಲಿ ಎಂದು ಆಣೆಯಿಟ್ಟುಕೊಂಡಿದ್ದ ಕಾರಣ ನಾವು ಅವರಿಗೆ ನಮ್ಮ ಹೆಣ್ಣುಗಳನ್ನು ಕೊಡುವುದಕ್ಕಾಗುವುದಿಲ್ಲ” ಎಂದು ಮಾತಾಡಿಕೊಳ್ಳುತ್ತಿದ್ದರು.",
			"19": "<sup>19</sup> ಆಗ ಶೀಲೋವಿನಲ್ಲಿ ಪ್ರತೀ ವರ್ಷ ಯೆಹೋವನ ಉತ್ಸವ ನಡೆಯುತ್ತದೆಂಬುದು ಅವರ ನೆನಪಿಗೆ ಬಂದಿತು. (ಶೀಲೋ ಎಂಬುದು ಬೇತೇಲಿನ ಉತ್ತರಕ್ಕೂ ಬೇತೇಲಿನಿಂದ ಶೆಕೆಮಿಗೆ ಹೋಗುವ ರಾಜಮಾರ್ಗದ ಪೂರ್ವಕ್ಕೂ, ಲೆಬೋನದ ದಕ್ಷಿಣಕ್ಕೂ ಇರುತ್ತದೆ.)",
			"20": "<sup>20</sup> ಆಗ ಅವರು ಬೆನ್ಯಾಮೀನ್ಯರನ್ನು ಕರೆದು ಅವರಿಗೆ, “ಇಗೋ, ನೀವು ದ್ರಾಕ್ಷೇತೋಟಗಳಲ್ಲಿ ಅಡಗಿಕೊಳ್ಳಿರಿ;",
			"21": "<sup>21</sup> ಶೀಲೋವಿನ ಕನ್ಯೆಯರು ಹೊರಗೆ ಬಂದು ನಾಟ್ಯವಾಡುವಾಗ, ನೀವು ತೋಟಗಳಿಂದ ಹೊರಗೆ ಬಂದು ಪ್ರತಿಯೊಬ್ಬನು ತನತನಗೆ ಶೀಲೋವಿನ ಕನ್ಯೆಯರಿಂದ ಒಬ್ಬಳನ್ನು ಹೆಂಡತಿಯಾಗಿ ತೆಗೆದುಕೊಂಡು ನಿಮ್ಮ ಸೀಮೆಗೆ ಓಡಿಹೋಗಿರಿ.",
			"22": "<sup>22</sup> ಅವರ ತಂದೆಗಳೂ, ಅಣ್ಣತಮ್ಮಂದಿರೂ ನಮ್ಮ ಹತ್ತಿರ ದೂರು ತಂದರೆ ನಾವು ಅವರಿಗೆ, ‘ನಮ್ಮನ್ನು ನೋಡಿ ಅವರಿಗೆ ಕೃಪೆತೋರಿಸಿರಿ; ಯುದ್ಧದಿಂದ ಪ್ರತಿಯೊಬ್ಬನಿಗೆ ಹೆಣ್ಣನ್ನು ದೊರಕಿಸುವುದು ನಮ್ಮಿಂದಾಗಲಿಲ್ಲವಲ್ಲಾ; ಮತ್ತು ನಿಮ್ಮ ಹೆಣ್ಣುಮಕ್ಕಳನ್ನು ನೀವೇ ಕೊಡಲಿಲ್ಲವಾದ್ದರಿಂದ ನೀವು ನಿರಪರಾಧಿಗಳು’ ಎಂದು ಹೇಳಿ ಸಮಾಧಾನಪಡಿಸುವೆವು” ಅಂದರು.",
			"23": "<sup>23</sup> ಅದರಂತೆಯೇ ಬೆನ್ಯಾಮೀನ್ಯರು ನೃತ್ಯಮಾಡುತ್ತ ಹೊರಗೆ ಬಂದಿದ್ದ ಕನ್ಯೆಯರಲ್ಲಿ ತಮ್ಮ ಸಂಖ್ಯೆಗೆ ಸರಿಯಾಗುವಷ್ಟು ಮಂದಿಯನ್ನು ಹೆಂಡತಿಯರನ್ನಾಗಿ ಹಿಡಿದುಕೊಂಡು ತಮ್ಮ ಸ್ವಾದೀನವಾದ ಭೂಮಿಗೆ ಹೋಗಿ ಅಲ್ಲಿ ಪಟ್ಟಣಗಳನ್ನು ಕಟ್ಟಿ ವಾಸಮಾಡಿದರು.",
			"24": "<sup>24</sup> ಅನಂತರ ಇಸ್ರಾಯೇಲರು ತಮ್ಮ ತಮ್ಮ ಕುಲಗೋತ್ರಗಳಿಗೆ ಸ್ವಾಸ್ತ್ಯವಾಗಿ ಸಿಕ್ಕಿದ ಪ್ರದೇಶಗಳಿಗೆ ಹಿಂದಿರುಗಿ ಹೋದರು.",
			"25": "<sup>25</sup> ಆ ಕಾಲದಲ್ಲಿ ಇಸ್ರಾಯೇಲರೊಳಗೆ ಅರಸನಿರಲಿಲ್ಲ; ಪ್ರತಿಯೊಬ್ಬನೂ ತನ್ನ ಮನಸ್ಸಿಗೆ ಬಂದಂತೆ ನಡೆಯುತ್ತಿದ್ದನು."
		}
	}
};
module.exports = book;