var book = {
	"name": "ಸಮುವೇಲನು - ಪ್ರಥಮ ಭಾಗ",
	"numChapters": 31,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ಎಫ್ರಾಯಿಮ್ ಬೆಟ್ಟದ ಸೀಮೆಯಲ್ಲಿನ ರಾಮಾತಯಿಮ್ ಚೋಫೀಮ್ ಊರಿನಲ್ಲಿ ಎಲ್ಕಾನನೆಂಬ ಒಬ್ಬ ಮನುಷ್ಯನಿದ್ದನು. ಇವನು ಯೆರೋಹಾಮನ ಮಗನೂ ಎಲೀಹುವಿನ ಮೊಮ್ಮಗನೂ ತೋಹುವಿನ ಮರಿಮಗನೂ ಆಗಿದ್ದನು. ತೋಹು ಎಂಬವನು ಎಫ್ರಾಯೀಮ್ಯನಾದ ಚೂಫನ ಮಗನು.",
			"2": "<sup>2</sup> ಎಲ್ಕಾನನಿಗೆ ಇಬ್ಬರು ಹೆಂಡತಿಯರಿದ್ದರು. ಮೊದಲನೆಯವಳು ಹನ್ನಳು, ಎರಡನೆಯವಳು ಪೆನಿನ್ನಳು. ಪೆನಿನ್ನಳಿಗೆ ಮಕ್ಕಳಿದ್ದರು; ಹನ್ನಳಿಗೆ ಮಕ್ಕಳಿರಲಿಲ್ಲ.",
			"3": "<sup>3</sup> ಅವನು ಪ್ರತಿವರ್ಷವೂ ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನಿಗೆ ಯಜ್ಞವನ್ನು ಸಮರ್ಪಿಸಿ ಆತನನ್ನು ಆರಾಧಿಸುವುದಕ್ಕೋಸ್ಕರ ತನ್ನ ಊರಿನಿಂದ ಶೀಲೋವಿಗೆ ಹೋಗುತ್ತಿದ್ದನು. ಅಲ್ಲಿ ಏಲಿಯನ ಮಕ್ಕಳಾದ ಹೊಫ್ನಿ ಮತ್ತು ಫೀನೆಹಾಸ್ ಎಂಬವರು ಯೆಹೋವನ ಯಾಜಕರಾಗಿದ್ದರು.",
			"4": "<sup>4</sup> ಎಲ್ಕಾನನು ಯಜ್ಞವನ್ನರ್ಪಿಸಿದಾಗೆಲ್ಲಾ ತನ್ನ ಹೆಂಡತಿಯಾದ ಪೆನಿನ್ನಳಿಗೂ ಆಕೆಯ ಎಲ್ಲಾ ಗಂಡು ಹೆಣ್ಣುಮಕ್ಕಳಿಗೂ ಭಾಗಗಳನ್ನು ಕೊಡುತ್ತಿದ್ದನು.",
			"5": "<sup>5</sup> ಅವನು ಹನ್ನಳನ್ನು ಬಹಳವಾಗಿ ಪ್ರೀತಿಸಿದರೂ, ಯೆಹೋವನು ಆಕೆಯ ಗರ್ಭವನ್ನು ತಡೆದಿದ್ದರಿಂದ, ಅವನು ಆಕೆಗೆ ಎರಡು ಭಾಗವನ್ನು ಕೊಡುತ್ತಿದ್ದನು.",
			"6": "<sup>6</sup> ಇದಲ್ಲದೆ ಆಕೆಯ ಸವತಿಯಾದ ಪೆನಿನ್ನಳು, “ಯೆಹೋವನು ನಿನ್ನನ್ನು ಬಂಜೆಯನ್ನಾಗಿ ಮಾಡಿದ್ದಾನೆ” ಎಂದು ಹೇಳಿ ಆಕೆಯನ್ನು ಕೆಣಕಿ ನೋಯಿಸುತ್ತಿದ್ದಳು.",
			"7": "<sup>7</sup> ಅವರು ಪ್ರತಿವರ್ಷವೂ ಕುಟುಂಬವಾಗಿ ಯೆಹೋವನ ಮಂದಿರಕ್ಕೆ ಹೋದಾಗೆಲ್ಲಾ ಪೆನಿನ್ನಳು ಆಕೆಯನ್ನು ಕೆಣಕುತ್ತಿದ್ದುದರಿಂದ ಹನ್ನಳು ಊಟ ಮಾಡದೆ ಅಳುತ್ತಾ ಇದ್ದಳು.",
			"8": "<sup>8</sup> ಆಗ ಆಕೆಯ ಗಂಡನಾದ ಎಲ್ಕಾನನು ಆಕೆಗೆ “ಹನ್ನಾ, ಏಕೆ ಅಳುತ್ತೀ? ಊಟ ಮಾಡದಿರುವುದಕ್ಕೆ ಕಾರಣವೇನು? ನೀನು ವ್ಯಸನಪಡುವುದೇಕೆ? ನಾನು ನಿನಗೆ ಹತ್ತು ಮಂದಿ ಗಂಡು ಮಕ್ಕಳಿಗಿಂತ ಹೆಚ್ಚಾಗಿದ್ದೇನಲ್ಲಾ” ಅಂದನು.",
			"9": "<sup>9</sup> ಅವರು ಶೀಲೋವಿನಲ್ಲಿದ್ದ ಒಂದು ಸಂದರ್ಭದಲ್ಲಿ, ಅನ್ನಪಾನಗಳನ್ನು ತೆಗೆದುಕೊಂಡ ಮೇಲೆ, ಹನ್ನಳು ಎದ್ದು ಯೆಹೋವನ ಮಂದಿರಕ್ಕೆ ಹೋದಳು. ಯಾಜಕನಾದ ಏಲಿಯು ಮಂದಿರದ್ವಾರದ ನಿಲುವುಗಳ ಬಳಿಯಲ್ಲಿದ್ದ ತನ್ನ ಪೀಠದ ಮೇಲೆ ಕುಳಿತ್ತಿದ್ದನು.",
			"10": "<sup>10</sup> ಹನ್ನಳು ಬಹುದುಃಖದಿಂದ ಕಣ್ಣೀರುಸುರಿಸುತ್ತಾ ಯೆಹೋವನಿಗೆ,",
			"11": "<sup>11</sup> “ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೇ, ನಿನ್ನ ದಾಸಿಯ ದುಃಖವನ್ನು ಪರಾಂಬರಿಸು; ನಿನ್ನ ದಾಸಿಯನ್ನು ಮರೆಯದೆ ನನ್ನನ್ನು ನೆನಪಿಸಿಕೊಂಡು ನನಗೊಬ್ಬ ಮಗನನ್ನು ಕೊಡಬೇಕು; ಅವನು ಜೀವದಿಂದಿರುವ ತನಕ ನಿನ್ನವನಾಗಿಯೇ ಇರುವ ಹಾಗೆ ಅವನನ್ನು ನಿನಗೆ ಪ್ರತಿಷ್ಠಿಸುವೆನು; ಅವನ ತಲೆಯ ಮೇಲೆ ಕ್ಷೌರ ಕತ್ತಿಯನ್ನು ಮುಟ್ಟಗೊಡುವುದಿಲ್ಲ” ಎಂದು ಪ್ರಾರ್ಥಿಸಿ ಹರಕೆಮಾಡಿದಳು.",
			"12": "<sup>12</sup> ಆಕೆಯು ಬಹಳ ಹೊತ್ತಿನ ವರೆಗೆ ಯೆಹೋವನ ಮುಂದೆ ಪ್ರಾರ್ಥನೆಮಾಡುತ್ತಿರುವಾಗ ಏಲಿಯು ಆಕೆಯ ಬಾಯನ್ನೇ ನೋಡುತ್ತಿದ್ದನು.",
			"13": "<sup>13</sup> ಆಕೆ ತನ್ನ ಹೃದಯದಲ್ಲಿ ಪ್ರಾರ್ಥಿಸುತ್ತಿದ್ದರಿಂದ ತುಟಿಗಳು ಮಾತ್ರ ಅಲ್ಲಾಡುತ್ತಿದ್ದವು; ಶಬ್ದ ಕೇಳಿಸುತ್ತಿರಲಿಲ್ಲ. ಆದ್ದರಿಂದ ಆಕೆಯು ಮದ್ಯಪಾನಮಾಡಿದ್ದಾಳೆಂದು ತಿಳಿದು",
			"14": "<sup>14</sup> ಏಲಿಯು, “ನಿನ್ನ ಕುಡಿತದ ಅಮಲು ಇನ್ನೂ ಇಳಿಯಲಿಲ್ಲವೇ? ದ್ರಾಕ್ಷಾರಸದ ನಿಶೆ ನಿನ್ನನ್ನು ಬಿಟ್ಟು ಹೋಗಲಿ” ಅಂದನು.",
			"15": "<sup>15</sup> ಅದಕ್ಕೆ ಹನ್ನಳು “ಸ್ವಾಮೀ, ಹಾಗಲ್ಲ; ನಾನು ಬಹು ದುಃಖಪೀಡಿತಳು; ದ್ರಾಕ್ಷಾರಸವನ್ನಾದರೂ ಬೇರೆ ಯಾವ ಮದ್ಯವನ್ನಾದರೂ ಕುಡಿದವಳಲ್ಲ. ನನ್ನ ಮನೋವೇದನೆಯನ್ನು ಯೆಹೋವನ ಮುಂದೆ ತೋಡಿಕೊಳ್ಳುತ್ತಾ ಇದ್ದೇನೆ.",
			"16": "<sup>16</sup> ನಿನ್ನ ದಾಸಿಯಾದ ನನ್ನನ್ನು ಅಯೋಗ್ಯಳೆಂದು ನೆನಸಬೇಡ; ಈವರೆಗೆ ನನ್ನ ಹೆಚ್ಚಾದ ಚಿಂತೆಯನ್ನೂ, ವ್ಯಥೆಯನ್ನೂ ಅರಿಕೆಮಾಡಿಕೊಳ್ಳುತ್ತಿದ್ದೆನು” ಎಂದು ಹೇಳಿದಳು.",
			"17": "<sup>17</sup> ಆಗ ಏಲಿಯು ಆಕೆಗೆ, “ಸಮಾಧಾನದಿಂದ ಹೋಗು; ಇಸ್ರಾಯೇಲಿನ ದೇವರು ನಿನ್ನ ಪ್ರಾರ್ಥನೆಯನ್ನು ನೆರವೇರಿಸಲಿ” ಎಂದನು.",
			"18": "<sup>18</sup> ಹನ್ನಳು ಏಲಿಗೆ, “ನಿನ್ನ ದಾಸಿಯ ಮೇಲೆ ಕಟಾಕ್ಷವಿರಲಿ” ಎಂದು ಹೇಳಿ ಹೊರಟುಹೋಗಿ ಊಟಮಾಡಿದಳು. ಆ ಮೇಲೆ ಆಕೆಯ ಮುಖದಲ್ಲಿ ದುಃಖವು ಕಾಣಲಿಲ್ಲ.",
			"19": "<sup>19</sup> ಅವರು ಮರುದಿನ ಬೆಳಿಗ್ಗೆ ಎದ್ದು ಯೆಹೋವನನ್ನು ಆರಾಧಿಸಿ ರಾಮದಲ್ಲಿದ್ದ ತಮ್ಮ ಮನೆಗೆ ಬಂದರು. ಎಲ್ಕಾನನು ತನ್ನ ಹೆಂಡತಿಯಾದ ಹನ್ನಳೊಂದಿಗೆ ಸಂಗಮಿಸಲು, ಯೆಹೋವನು ಆಕೆಯ ಪ್ರಾರ್ಥನೆಯನ್ನು ಪರಿಗಣಿಸಿದ್ದರಿಂದ ಆಕೆಯು ಗರ್ಭಿಣಿಯಾದಳು.",
			"20": "<sup>20</sup> ಹನ್ನಳು ದಿನತುಂಬಿದ ಮೇಲೆ ಒಬ್ಬ ಮಗನನ್ನು ಹೆತ್ತಳು. ಯೆಹೋವನನ್ನು ಬೇಡಿ ಪಡೆದುಕೊಂಡೆನೆಂದು ಹೇಳಿ ಆ ಮಗನಿಗೆ ಸಮುವೇಲನೆಂದು ಹೆಸರಿಟ್ಟಳು.",
			"21": "<sup>21</sup> ಎಲ್ಕಾನನು ಎಂದಿನಂತೆ ಕುಟುಂಬ ಸಹಿತವಾಗಿ ಯೆಹೋವನಿಗೆ ವಾರ್ಷಿಕಯಜ್ಞವನ್ನು ಅರ್ಪಿಸಿ ಹರಕೆಸಲ್ಲಿಸಬೇಕೆಂದು ಶೀಲೋವಿಗೆ ಹೋಗುವುದಕ್ಕೆ ಸಿದ್ಧನಾಗಲು ಹನ್ನಳು",
			"22": "<sup>22</sup> “ಮಗನು ಮೊಲೆಬಿಡುವ ವರೆಗೂ ನಾನು ಬರುವುದಿಲ್ಲ; ಅವನು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಕಾಣಿಸಿಕೊಂಡು ಯಾವಾಗಲೂ ಅಲ್ಲಿಯೇ ಇರುವ ಹಾಗೆ ಅವನನ್ನು ಕರೆದುಕೊಂಡು ಬರುವೆನು” ಎಂದು ಹೇಳಿದಳು.",
			"23": "<sup>23</sup> ಆಕೆಯ ಗಂಡನಾದ ಎಲ್ಕಾನನು ಆಕೆಗೆ “ನಿನಗೆ ಸರಿತೋರುವ ಹಾಗೆ ಆಗಲಿ; ಮಗನು ಮೊಲೆಬಿಡುವವರೆಗೆ ಇರು. ಯೆಹೋವನು ತನ್ನ ವಾಕ್ಯವನ್ನು ದೃಢಪಡಿಸಲಿ” ಅಂದನು. ಮಗನು ಮೊಲೆಬಿಡುವ ತನಕ ಹನ್ನಳು ಮನೆಯಲ್ಲೇ ಇದ್ದು ಅವನನ್ನು ಸಾಕಿದಳು.",
			"24": "<sup>24</sup> ಅನಂತರ ಆಕೆಯು ಮೂರು ವರ್ಷದ ಒಂದು ಹೋರಿಯನ್ನೂ, ಒಂದು ಏಫಾ (ಐದು ಸೇರು) ಹಿಟ್ಟನ್ನೂ, ಒಂದು ತಿತ್ತಿ ದ್ರಾಕ್ಷಾರಸವನ್ನೂ ತೆಗೆದುಕೊಂಡು ಪುತ್ರನೊಂದಿಗೆ ಶೀಲೋವಿನಲ್ಲಿದ್ದ ಯೆಹೋವನ ಮಂದಿರಕ್ಕೆ ಬಂದಳು. ಮಗನು ಇನ್ನೂ ಚಿಕ್ಕವನಾಗಿದ್ದನು.",
			"25": "<sup>25</sup> ಅವರು ಹೋರಿಯನ್ನು ಯಜ್ಞಮಾಡಿದ ಮೇಲೆ ಮಗನನ್ನು ಏಲಿಯ ಬಳಿಗೆ ಕರೆದುಕೊಂಡು ಬಂದರು.",
			"26": "<sup>26</sup> ಹನ್ನಳು ಏಲಿಗೆ “ಸ್ವಾಮೀ, ನಿನ್ನ ಜೀವದಾಣೆ; ಮೊದಲು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಪ್ರಾರ್ಥನೆಮಾಡುತ್ತಾ, ಇಲ್ಲಿ ನಿನ್ನ ಹತ್ತಿರ ನಿಂತಿದ್ದ ಸ್ತ್ರೀಯು ನಾನೇ.",
			"27": "<sup>27</sup> ಆತನು ನನ್ನ ಪ್ರಾರ್ಥನೆಯ ಫಲವಾಗಿ ಅನುಗ್ರಹಿಸಿದ ಮಗನು ಇವನೇ;",
			"28": "<sup>28</sup> ನಾನು ಇವನನ್ನು ಯೆಹೋವನಿಗೇ ಒಪ್ಪಿಸಿಬಿಟ್ಟಿದ್ದೇನೆ. ಇವನು ಜೀವದಿಂದಿರುವ ತನಕ ಆತನಿಗೇ ಪ್ರತಿಷ್ಠಿತನಾಗಿರುವನು” ಎಂದು ಹೇಳಿದಳು. ಆ ಮೇಲೆ ಅವರು ಶಿಲೋವಿನಲ್ಲಿ ಯೆಹೋವನನ್ನು ಆರಾಧಿಸಿದರು."
		},
		"2": {
			"1": "<sup>1</sup> ಹನ್ನಳು ದೇವರ ಬಳಿ ಹೀಗೆ ವಿಜ್ಞಾಪಿಸಿದಳು, “ನನ್ನ ಹೃದಯವು ಯೆಹೋವನಲ್ಲಿ ಉಲ್ಲಾಸಿಸುತ್ತದೆ; ನನ್ನ ಕೊಂಬು ಯೆಹೋವನಿಂದ ಎತ್ತಲ್ಪಟ್ಟಿದೆ. ಶತ್ರುಗಳ ಮುಂದೆ ನನ್ನ ಬಾಯಿ ಧೈರ್ಯವಾಗಿ ಮಾತನಾಡುತ್ತದೆ, ಯಾಕೆಂದರೆ ನಾನು ನಿನ್ನ ರಕ್ಷಣೆಯಲ್ಲಿ ಆನಂದಿಸುತ್ತೇನೆ.",
			"2": "<sup>2</sup> “ಯೆಹೋವನಂಥ ಪರಿಶುದ್ಧನು ಇಲ್ಲವೇ ಇಲ್ಲ; ನಿನ್ನ ಹೊರತು ದೇವರು ಯಾರೂ ಇಲ್ಲ ನಮ್ಮ ದೇವರಂತಹ ಸಮಾನವಾದ ಆಶ್ರಯವಿಲ್ಲ.",
			"3": "<sup>3</sup> “ಇನ್ನು ಮುಂದೆ ಗರ್ವದಿಂದ ಮಾತನಾಡಬೇಡಿರಿ; ಸೊಕ್ಕಿನ ಮಾತು ನಿಮ್ಮ ಬಾಯಿಂದ ಹೊರಡದಿರಲಿ. ದೇವರಾದ ಯೆಹೋವನು ಸರ್ವಜ್ಞನು; ಆತನು ಮನುಷ್ಯರ ಕ್ರಿಯೆಗಳನ್ನು ತೂಗಿನೋಡುವನು.",
			"4": "<sup>4</sup> “ಶೂರರ ಬಿಲ್ಲುಗಳು ಮುರಿಯಲ್ಪಟ್ಟಿವೆ, ಎಡವಿದವರು ಶೌರ್ಯವೆಂಬ ನಡುಕಟ್ಟನ್ನು ಬಿಗಿದುಕೊಂಡಿದ್ದಾರೆ.",
			"5": "<sup>5</sup> ಹೊಟ್ಟೆತುಂಬಾ ಊಟಮಾಡುತ್ತಿದ್ದವರು ಅನ್ನಕ್ಕೋಸ್ಕರ ಕೂಲಿಮಾಡುತ್ತಾರೆ; ಹಸಿದವರು ಉಂಡು ಸುಖದಿಂದಿರುತ್ತಾರೆ. ಬಂಜೆಯು ಏಳು ಮಂದಿ ಮಕ್ಕಳನ್ನು ಹೆರುವಳು; ಮಕ್ಕಳುಳ್ಳವಳು ದಿಕ್ಕಿಲ್ಲದೆ ಹೋಗುವಳು.",
			"6": "<sup>6</sup> “ಯೆಹೋವನೇ ಸಾಯಿಸುವವನೂ ಬದುಕಿಸುವವನೂ; ಪಾತಾಳದಲ್ಲಿ ದೊಬ್ಬುವವನೂ ಮೇಲಕ್ಕೆ ಬರಮಾಡುವವನೂ ಆತನೇ.",
			"7": "<sup>7</sup> ಬಡತನ, ಸಿರಿತನಗಳನ್ನು ಕೊಡುವವನೂ ಯೆಹೋವನೇ, ತಗ್ಗಿಸುವವನೂ, ಹೆಚ್ಚಿಸುವವನೂ ಆತನೇ.",
			"8": "<sup>8</sup> ಆತನು ದೀನರನ್ನು ಧೂಳಿನಿಂದ ಎತ್ತಿ; ದರಿದ್ರರನ್ನು ತಿಪ್ಪೆಯಿಂದ ಎಬ್ಬಿಸಿ ಅಧಿಪತಿಗಳೊಂದಿಗೆ ಕುಳ್ಳಿರಿಸಿ ಅವನಿಗೆ ಮಹಿಮಾಸನವನ್ನು ಬಾಧ್ಯತೆಯಾಗಿ ಅನುಗ್ರಹಿಸುವವನಾಗಿದ್ದಾನೆ. ಭೂಮಿಯ ಆಧಾರಸ್ತಂಭಗಳು ಯೆಹೋವನವೇ; ಆತನೇ ಅವುಗಳ ಮೇಲೆ ಭೂಮಂಡಲವನ್ನು ಸ್ಥಾಪಿಸಿದ್ದಾನೆ.",
			"9": "<sup>9</sup> “ಆತನು ತನ್ನ ನಂಬಿಗಸ್ತ ಭಕ್ತರ ಹೆಜ್ಜೆಗಳನ್ನು ಕಾಯುವನು; ಆದರೆ ದುಷ್ಟರನ್ನು ಅಂಧಕಾರದ ಮೌನದಲ್ಲಿ ಮುಳುಗಿಸುವನು, ಭುಜಬಲದಿಂದಲೇ ಯಾವನೂ ಜಯಹೊಂದಲಾರನು.",
			"10": "<sup>10</sup> “ಯೆಹೋವನನ್ನು ವಿರೋಧಿಸುವವರು ಮುರಿಯಲ್ಪಡುತ್ತಾರೆ; ಆತನು ಆಕಾಶದಿಂದ ಅವರ ಮೇಲೆ ಗರ್ಜಿಸುತ್ತಾನೆ. ಯೆಹೋವನು ಭೂಮಿಯ ಕಟ್ಟಕಡೆಯಲ್ಲಿರುವವರಿಗೂ ನ್ಯಾಯತೀರಿಸುವನು. ತಾನು ನೇಮಿಸಿದ ಅರಸನಿಗೆ ಬಲವನ್ನು ಅನುಗ್ರಹಿಸುವನು; ತನ್ನ ಅಭಿಷಿಕ್ತನ ಕೊಂಬನ್ನು ಉನ್ನತಮಾಡುವನು.”",
			"11": "<sup>11</sup> ಅನಂತರ ಎಲ್ಕಾನನು ರಾಮದಲ್ಲಿದ್ದ ತನ್ನ ಮನೆಗೆ ಹೋದನು; ಹುಡುಗನು ಯಾಜಕನಾದ ಏಲಿಯ ಕೈಕೆಳಗಿದ್ದುಕೊಂಡು ಯೆಹೋವನ ಸೇವೆ ಮಾಡುತ್ತಿದ್ದನು.",
			"12": "<sup>12</sup> ಏಲಿಯ ಮಕ್ಕಳು ಬಹುದುಷ್ಟರಾಗಿದ್ದರು; ಅವರು ಯೆಹೋವನನ್ನು ಲಕ್ಷಿಸುತ್ತಿರಲಿಲ್ಲ.",
			"13": "<sup>13</sup> ಯಜ್ಞವನ್ನರ್ಪಿಸುವುದಕ್ಕೆ ಬಂದ ಜನರಲ್ಲಿ ಈ ಯಾಜಕರು ನಡಿಸಿದ್ದೇನಂದರೆ, ಯಜ್ಞಮಾಂಸವು ಬೇಯುತ್ತಿರುವಾಗಲೇ ಯಾಜಕನ ಆಳು ಕೈಯಲ್ಲಿ ತ್ರಿಶೂಲವನ್ನು ಹಿಡಿದುಕೊಂಡು ಬರುತ್ತಿದ್ದನು.",
			"14": "<sup>14</sup> ಆ ಮಾಂಸ ಬೇಯುತ್ತಿರುವ ಕೊಪ್ಪರಿಗೆಯಲ್ಲಾಗಲಿ, ಭಾಂಡಲ್ಲಾಗಲಿ (ದೊಡ್ಡ ಹಂಡೆ), ತಪ್ಪಲೆಯಲ್ಲಾಗಲಿ, ಗಡಿಗೆಯಲ್ಲಾಗಲಿ ಚುಚ್ಚಿ ಅದಕ್ಕೆ ಸಿಕ್ಕಿದ್ದೆಲ್ಲವನ್ನೂ ಯಾಜಕನಿಗೆಂದು ತೆಗೆದುಕೊಳ್ಳುತ್ತಿದ್ದನು. ಶೀಲೋವಿಗೆ ಬರುತ್ತಿದ ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರಿಗೂ ಹೀಗೆಯೇ ಮಾಡುತ್ತಿದ್ದರು.",
			"15": "<sup>15</sup> ಇದಲ್ಲದೆ ಯಾಜಕನ ಆಳು ಕೊಬ್ಬನ್ನು ಹೋಮಮಾಡುವುದಕ್ಕಿಂತ ಮುಂಚೆಯೇ ಬಂದು ಯಜ್ಞವನ್ನರ್ಪಿಸುವವನಿಗೆ, “ಯಾಜಕನಿಗೋಸ್ಕರ ಸುಡತಕ್ಕ ಮಾಂಸವನ್ನು ಕೊಡು; ನೀನು ಬೇಯಿಸಿದ ಮಾಂಸವನ್ನು ಅವನು ತೆಗೆದುಕೊಳ್ಳುವುದಿಲ್ಲ; ಅವನಿಗೆ ಹಸಿಮಾಂಸವೇ ಬೇಕು” ಅನ್ನುವನು.",
			"16": "<sup>16</sup> ಅರ್ಪಿಸುವವನು, ಮೊದಲು ಕೊಬ್ಬನ್ನು ಹೋಮಮಾಡಲಿ, ಅನಂತರ ನಿನಗೆ ಬೇಕಾದದ್ದನ್ನು ತೆಗೆದುಕೋ ಎಂದು ಹೇಳಿದರೆ ಅವನು, “ಈಗಲೇ ಕೊಡಬೇಕು; ಇಲ್ಲವಾದರೆ ಬಲಾತ್ಕಾರದಿಂದ ತೆಗೆದುಕೊಳ್ಳುವೆನು” ಎನ್ನುವನು.",
			"17": "<sup>17</sup> ಹೀಗೆ ಆ ಯೌವನಸ್ಥರು ಯೆಹೋವನ ನೈವೇದ್ಯವನ್ನು ತುಚ್ಛೀಕರಿಸಿದ್ದರಿಂದ ಅವರ ಪಾಪವು ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ಆಧಿಕವಾಯಿತು.",
			"18": "<sup>18</sup> ಆದರೆ ಬಾಲಕನಾದ ಸಮುವೇಲನು ಏಫೋದೆಂಬ ನಾರು ಮಡಿಯಂಗಿಯನ್ನು ತೊಟ್ಟುಕೊಂಡು ಯೆಹೋವನ ಸೇವೆ ಮಾಡುತ್ತಿದ್ದನು.",
			"19": "<sup>19</sup> ಅವನ ತಾಯಿಯು ಪ್ರತಿವರ್ಷವೂ ತನ್ನ ಗಂಡನ ಜೊತೆಯಲ್ಲಿ ಯಜ್ಞವನ್ನರ್ಪಿಸುವುದಕ್ಕೆ ಬರುವಾಗ ಒಂದು ಚಿಕ್ಕ ಅಂಗಿಯನ್ನು ಹೊಲಿದು ಅವನಿಗೆ ತಂದುಕೊಡುತ್ತಿದ್ದಳು.",
			"20": "<sup>20</sup> ಏಲಿಯು ಎಲ್ಕಾನನಿಗೆ, “ನೀನು ಯೆಹೋವನಿಗೆ ಒಪ್ಪಿಸಿಬಿಟ್ಟ ಮಗನಿಗೆ ಬದಲಾಗಿ ಆತನು ಈ ಸ್ತ್ರೀಯಿಂದ ನಿನಗೆ ಬೇರೆ ಮಕ್ಕಳನ್ನು ಕೊಡಲಿ” ಎಂದು ಹೇಳಿ ಅವನನ್ನೂ ಅವನ ಹೆಂಡತಿಯನ್ನೂ ಆಶೀರ್ವದಿಸುತ್ತಿದ್ದನು. ಅನಂತರ ಅವರು ಸ್ವಗ್ರಾಮಕ್ಕೆ ಹಿಂದಿರುಗುತ್ತಿದ್ದರು.",
			"21": "<sup>21</sup> ಯೆಹೋವನು ಕಟಾಕ್ಷಿಸಿದ್ದರಿಂದ ಹನ್ನಳಿಗೆ ಮೂರು ಮಂದಿ ಗಂಡುಮಕ್ಕಳೂ, ಇಬ್ಬರು ಹೆಣ್ಣುಮಕ್ಕಳಾದರು. ಬಾಲಕನಾಗಿದ್ದ ಸಮುವೇಲನು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲೇ ಬೆಳೆದು ದೊಡ್ಡವನಾದನು.",
			"22": "<sup>22</sup> ಬಹುವೃದ್ಧನಾದ ಏಲಿಯು ತನ್ನ ಮಕ್ಕಳು ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲರಲ್ಲಿ ನಡೆಸುತ್ತಿರುವುದನ್ನೂ, ಅವರು ದೇವದರ್ಶನ ಗುಡಾರದ ಬಾಗಿಲಿನಲ್ಲಿ ಸೇವೆ ಮಾಡುತ್ತಿದ್ದ ಸ್ತ್ರೀಯರೊಡನೆ ಸಂಗಮಿಸುತ್ತಿರುವುದನ್ನೂ ಕೇಳಿ",
			"23": "<sup>23</sup> ಅವರಿಗೆ, “ಎಲ್ಲಾ ಜನರಿಂದಲೂ ನಿಮ್ಮ ದುಷ್ಕೃತ್ಯಗಳನ್ನು ಕೇಳುತ್ತೇನೆ. ಹೀಗೇಕೆ ಮಾಡುತ್ತೀರಿ?”",
			"24": "<sup>24</sup> ನನ್ನ ಮಕ್ಕಳೇ, ಹೀಗೆ ಮಾಡಬಾರದು, ನೀವು ಯೆಹೋವನ ಜನರನ್ನು ದುರ್ಮಾರ್ಗಕ್ಕೆ ಎಳೆಯುವವರಾಗಿದ್ದೀರೆಂದು ಕೇಳಿದ್ದೇನೆ; ಇದು ಒಳ್ಳೆಯದಲ್ಲ.",
			"25": "<sup>25</sup> “ಮನುಷ್ಯನು ಮನುಷ್ಯರಿಗೆ ಅಪರಾಧಮಾಡಿದರೆ ದೇವರು ಮಧ್ಯಸ್ಥಿಕೆಯನ್ನು ವಹಿಸುವನು; ಆದರೆ ಮನುಷ್ಯನು ಯೆಹೋವನಿಗೆ ಅಪರಾಧಮಾಡಿದರೆ ಮಧ್ಯಸ್ಥಿಕೆ ವಹಿಸುವವರಾರು?” ಎಂದು ಎಚ್ಚರಿಸಿದರೂ ಅವರು ತಮ್ಮ ತಂದೆಯ ಮಾತಿಗೆ ಕಿವಿಗೊಡಲಿಲ್ಲ, ಯಾಕೆಂದರೆ ಯೆಹೋವನು ಅವರನ್ನು ನಾಶಮಾಡಲು ನಿರ್ಧರಿಸಿದ್ದನು.",
			"26": "<sup>26</sup> ಬಾಲಕನಾದ ಸಮುವೇಲನಾದರೋ ಬೆಳೆಯುತ್ತಾ ಬಂದ ಹಾಗೆಲ್ಲಾ ಯೆಹೋವನ ಮತ್ತು ಮನುಷ್ಯರ ದಯೆಗೆ ಪಾತ್ರನಾದನು.",
			"27": "<sup>27</sup> ದೇವರ ಮನುಷ್ಯನೊಬ್ಬನು ಏಲಿಯ ಬಳಿಗೆ ಬಂದು ಅವನಿಗೆ, “ಯೆಹೋವನು ನಿನಗೆ ಹೇಳುವುದೇನಂದರೆ, ‘ಇಸ್ರಾಯೇಲರು ಐಗುಪ್ತದಲ್ಲಿ ಫರೋಹನ ದಾಸತ್ವದಲ್ಲಿದ್ದಾಗ ನಾನು ನಿನ್ನ ಗೋತ್ರದವರಿಗೆ ಪ್ರತ್ಯಕ್ಷನಾಗಿ",
			"28": "<sup>28</sup> ಇಸ್ರಾಯೇಲರ ಎಲ್ಲಾ ಕುಟುಂಬಗಳಿಂದ ಅವರನ್ನೇ ಯಾಜಕೋದ್ಯೋಗಕ್ಕೆ ಅಂದರೆ ಯಜ್ಞವನ್ನರ್ಪಿಸುವುದಕ್ಕೂ, ಧೂಪಹಾಕುವುದಕ್ಕೂ, ನನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಏಫೋದನ್ನು (ಯಾಜಕರ ವಸ್ತ್ರ) ಧರಿಸಿಕೊಳ್ಳುವುದಕ್ಕೂ ಆರಿಸಿಕೊಂಡೆನು; ಅವರಿಗೆ ಇಸ್ರಾಯೇಲರ ಯಜ್ಞಶೇಷದ ಹಕ್ಕನ್ನು ಅನುಗ್ರಹಿಸಿದೆನು.",
			"29": "<sup>29</sup> ನನ್ನ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಾಯೇಲರು ನನ್ನ ಆಜ್ಞಾನುಸಾರವಾಗಿ ನನ್ನ ಮಂದಿರಕ್ಕೆ ತರುವ ಯಜ್ಞನೈವೇದ್ಯಗಳ ಘನತೆಯನ್ನು ನೀವು ಭಂಗಪಡಿಸಿ, ಅವುಗಳ ಶ್ರೇಷ್ಟ ಭಾಗಗಳಿಂದ ನಿಮ್ಮನ್ನು ಕೊಬ್ಬಿಸಿಕೊಳ್ಳುವುದೇಕೆ? ನೀನು ನನ್ನನ್ನು ಗೌರವಿಸುವುದಕ್ಕಿಂತ ಹೆಚ್ಚಾಗಿ ನಿನ್ನ ಮಕ್ಕಳನ್ನೇ ಗೌರವಿಸುವುದು ಸರಿಯೋ?’",
			"30": "<sup>30</sup> ಇಸ್ರಾಯೇಲಿನ ದೇವರಾದ ಯೆಹೋವನು ಹೇಳುವುದೇನೆಂದರೆ, ‘ನಿನ್ನ ಗೋತ್ರದವರೂ, ಸಂತಾನದವರೂ ನಿರಂತರವೂ ನನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸೇವೆಮಾಡಬೇಕೆಂದು ವಾಗ್ದಾನಮಾಡಿದ್ದೆನು. ಆದರೆ ಈಗ ನಾನು ತಿಳಿಸುವುದೇನಂದರೆ, ಅದು ನನಗೆ ದೂರವಾಗಿರಲಿ; ನನ್ನನ್ನು ಸನ್ಮಾನಿಸುವವರನ್ನು ನಾನು ಸನ್ಮಾನಿಸುವೆನು; ನನ್ನನ್ನು ತಿರಸ್ಕರಿಸುವವರನ್ನು ನಾನು ತಿರಸ್ಕರಿಸುವೆನು.",
			"31": "<sup>31</sup> ಇಗೋ, ನಾನು ನಿನ್ನ ಮತ್ತು ನಿನ್ನ ಮನೆಯವರ ಭುಜಬಲವನ್ನು ಮುರಿಯುವ ದಿನಗಳು ಬರುವವು. ನಿನ್ನ ಮನೆಯಲ್ಲಿ ಯಾರೂ ಪೂರ್ಣಾಯುಷ್ಯವನ್ನು ಮುಟ್ಟುವುದಿಲ್ಲ.",
			"32": "<sup>32</sup> ನಿನ್ನ ವೈರಿಯು ನನ್ನ ಮಂದಿರದಲ್ಲಿ ಸೇವೆಮಾಡುವುದನ್ನು ನೋಡುವಿ. ಇಸ್ರಾಯೇಲರಿಗೆ ಸರ್ವಶುಭವನ್ನು ದಯಪಾಲಿಸುವೆನು; ಆದರೆ ನಿನ್ನ ಮನೆಯಲ್ಲಿ ಪೂರ್ಣಾಯುಷ್ಯವನ್ನು ಹೊಂದಿ ಜೀವಿಸುವವರು ಇನ್ನು ಮುಂದೆ ಒಬ್ಬರಾದರು ಇರುವುದಿಲ್ಲ.",
			"33": "<sup>33</sup> ನಾನು ನಿನ್ನ ಸಂತಾನವನ್ನು ನನ್ನ ಸಾನ್ನಿಧ್ಯ ಸೇವೆಯಿಂದ ತೆಗೆದುಹಾಕದೆ, ಒಬ್ಬನನ್ನಾದರೂ ಉಳಿಸಿದರೆ ಅವನು ನಿನ್ನ ಕಣ್ಣೀರಿಗೂ ಮತ್ತು ಮನೋವ್ಯಥೆಗೂ ಕಾರಣನಾಗುವನು. ನಿನ್ನ ಸಂತಾನದವರೆಲ್ಲರೂ ಕತ್ತಿಯಿಂದ ಸಂಹಾರವಾಗುವರು.",
			"34": "<sup>34</sup> ಇದೆಲ್ಲಾ ನೆರವೇರುವುದೆಂಬುದಕ್ಕೆ ನಿನ್ನ ಮಕ್ಕಳಾದ ಹೊಫ್ನಿ ಹಾಗೂ ಫೀನೆಹಾಸರು ಒಂದೇ ದಿನದಲ್ಲಿ ಸಾಯುವುದೇ ಗುರುತಾಗಿರುವುದು.",
			"35": "<sup>35</sup> ನನ್ನ ಹೃದಯದ ಹಾಗೂ ಮನಸ್ಸಿನ ಆಲೋಚನೆಗಳನ್ನು ನೆರವೇರಿಸುವ ಒಬ್ಬ ನಂಬಿಗಸ್ತನಾದ ಯಾಜಕನನ್ನು ಎಬ್ಬಿಸುವೆನು; ಅವನಿಗೆ ಶಾಶ್ವತ ಗೃಹವನ್ನು ಅನುಗ್ರಹಿಸುವೆನು. ಅವನು ನನ್ನ ಅಭಿಷಿಕ್ತನ ಬಳಿಯಲ್ಲಿ ಸದಾಕಾಲ ಸೇವೆಮಾಡುವನು.",
			"36": "<sup>36</sup> ನಿನ್ನ ಮನೆಯಲ್ಲಿ ಉಳಿದವರು ಬಂದು ಅವನ ಮುಂದೆ ಅಡ್ಡ ಬಿದ್ದು, “ನಮಗೆ ಒಂದು ಬೆಳ್ಳಿಕಾಸನ್ನು ರೊಟ್ಟಿಯ ಚೂರನ್ನು ಕೊಡು; ಒಂದು ತುತ್ತು ಅನ್ನಕ್ಕೆ ಮಾರ್ಗವಾಗುವ ಹಾಗೆ ಯಾಜಕನ ಸೇವೆಯಲ್ಲಿ ಯಾವುದಕ್ಕಾದರೂ ನಮ್ಮನ್ನು ಸೇರಿಸಿಕೋ” ಎಂದು ಅವನನ್ನು ಬೇಡಿಕೊಳ್ಳುವರು, ಎಂದು ಹೇಳಿದನು."
		},
		"3": {
			"1": "<sup>1</sup> ಬಾಲಕನಾದ ಸಮುವೇಲನು ಏಲಿಯ ಕೈಕೆಳಗಿದ್ದುಕೊಂಡು ಯೆಹೋವನ ಸೇವೆಮಾಡುತ್ತಿದ್ದನು. ಆ ಕಾಲದಲ್ಲಿ ದೈವೋಕ್ತಿಗಳು ವಿರಳವಾಗಿದ್ದವು; ದೇವದರ್ಶನಗಳು ಅಪರೂಪವಾಗಿದ್ದವು.",
			"2": "<sup>2</sup> ಏಲಿಯ ಕಣ್ಣುಗಳು ದಿನದಿನಕ್ಕೆ ಮೊಬ್ಬಾಗುತ್ತಾ ಬಂದು ಸರಿಯಾಗಿ ಕಾಣಿಸದೆಹೋಗಿದ್ದವು. ಅವನು ಒಂದು ರಾತ್ರಿ ತನ್ನ ಸ್ಥಳದಲ್ಲಿ ಮಲಗಿದ್ದನು;",
			"3": "<sup>3</sup> ಸಮುವೇಲನು ಯೆಹೋವನ ಮಂದಿರದಲ್ಲಿ ದೇವರ ಮಂಜೂಷವಿದ್ದ ಸ್ಥಳದಲ್ಲಿ ಮಲಗಿದ್ದನು. ಆಗ ದೇವದೀಪವು ಇನ್ನೂ ಉರಿಯುತ್ತಿತ್ತು.",
			"4": "<sup>4</sup> ಯೆಹೋವನು ಸಮುವೇಲನನ್ನು ಕರೆದನು; ಸಮುವೇಲನು “ಇಗೋ ಬಂದೆ” ಎಂದು ಉತ್ತರಕೊಟ್ಟು ಒಡನೆ ಏಲಿಯ ಬಳಿಗೆ ಹೋಗಿ,",
			"5": "<sup>5</sup> “ಇಗೋ ಬಂದಿದ್ದೇನೆ; ನೀನು ನನ್ನನ್ನು ಕರೆದೆಯಲ್ಲವೇ?” ಅಂದನು. ಆಗ ಏಲಿಯು, “ನಾನು ನಿನ್ನನ್ನು ಕರೆಯಲಿಲ್ಲ, ಹೋಗಿ ಮಲಗಿಕೋ” ಅಂದನು. ಸಮುವೇಲನು ಹೋಗಿ ಮಲಗಿದನು.",
			"6": "<sup>6</sup> ಯೆಹೋವನು ತಿರುಗಿ, “ಸಮುವೇಲನೇ” ಎಂದು ಕರೆದನು. ಸಮುವೇಲನು ಕೂಡಲೆ ಎದ್ದು ಏಲಿಯ ಬಳಿಗೆ ಹೋಗಿ, “ಇಗೋ, ಬಂದಿದ್ದೇನೆ; ನೀನು ನನ್ನನ್ನು ಕರೆದೆಯಲ್ಲಾ” ಅನ್ನಲು ಅವನು, “ನನ್ನ ಮಗನೇ, ನಾನು ನಿನ್ನನ್ನು ಕರೆಯಲಿಲ್ಲಾ, ಹೋಗಿ ಮಲಗಿಕೋ” ಎಂದನು.",
			"7": "<sup>7</sup> ಆವರೆಗೂ ಸಮುವೇಲನಿಗೆ ಯೆಹೋವನ ಅನುಭವವಿರಲಿಲ್ಲ; ಅವನು ದೈವೋಕ್ತಿಗಳನ್ನು ಹೊಂದಿರಲಿಲ್ಲ.",
			"8": "<sup>8</sup> ಯೆಹೋವನು ಸಮುವೇಲನನ್ನು ಮೂರನೆಯ ಸಾರಿ ಕರೆದನು. ಅವನು ತಟ್ಟನೆ ಏಲಿಯ ಹತ್ತಿರ ಹೋಗಿ, “ಇಗೋ, ಬಂದಿದ್ದೇನೆ, ನನ್ನನ್ನು ಕರೆದಿಯಲ್ಲಾ” ಅನ್ನಲು, ಹುಡುಗನನ್ನು ಕರೆದವನು ಯೆಹೋವನೇ ಎಂದು ಏಲಿಯು ತಿಳಿದು ಅವನಿಗೆ, “ಹೋಗಿ ಮಲಗಿಕೋ;",
			"9": "<sup>9</sup> ಪುನಃ ಆತನು ನಿನ್ನನ್ನು ಕರೆದರೆ ಯೆಹೋವನೇ, ಅಪ್ಪಣೆಯಾಗಲಿ, ನಿನ್ನ ದಾಸನು ಕೇಳಿಸಿಕೊಳ್ಳುತ್ತಿದ್ದಾನೆ” ಎಂದು ಹೇಳು ಅಂದನು. ಸಮುವೇಲನು ತಿರುಗಿ ಹೋಗಿ ತನ್ನ ಸ್ಥಳದಲ್ಲಿ ಮಲಗಿಕೊಂಡನು.",
			"10": "<sup>10</sup> ಯೆಹೋವನು ಪ್ರತ್ಯಕ್ಷನಾಗಿ ಮುಂಚಿನಂತೆಯೇ “ಸಮುವೇಲನೇ, ಸಮುವೇಲನೇ” ಅಂದನು. ಸಮುವೇಲನು, “ಅಪ್ಪಣೆಯಾಗಲಿ, ನಿನ್ನ ದಾಸನು ಕೇಳಿಸಿಕೊಳ್ಳುತ್ತಿದ್ದಾನೆ” ಎಂದನು.",
			"11": "<sup>11</sup> ಯೆಹೋವನು ಸಮುವೇಲನಿಗೆ “ನಾನು ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಒಂದು ವಿಶೇಷಕಾರ್ಯವನ್ನು ನಡೆಸುವೆನು; ಅದರ ವಿಷಯ ಕೇಳುವವರ ಎರಡು ಕಿವಿಗಳೂ ನಿಮಿರುವುವು.",
			"12": "<sup>12</sup> ಏಲಿಯ ಮನೆಯನ್ನು ಕುರಿತು ತಿಳಿಸಿದ್ದೆಲ್ಲವನ್ನೂ ಆ ದಿನದಲ್ಲಿ ತಪ್ಪದೆ ನೆರವೇರಿಸುವೆನು.",
			"13": "<sup>13</sup> ತನ್ನ ಮಕ್ಕಳು ದೇವದೂಷಕರೆಂದು ಅವನಿಗೆ ತಿಳಿದು ಬಂದರೂ ಅವನು ಅವರನ್ನು ಗದರಿಸಲಿಲ್ಲ. ಈ ಪಾಪದ ನಿಮಿತ್ತವಾಗಿ ಅವನ ಮನೆಯನ್ನು ನಿತ್ಯದಂಡನೆಗೆ ಗುರಿಮಾಡಿದ್ದೇನೆಂದು ಅವನಿಗೆ ಮೊದಲೇ ತಿಳಿಸಿದೆನು.",
			"14": "<sup>14</sup> ಏಲಿಯ ಮನೆಯವರ ಅಪರಾಧಕ್ಕೆ ಯಜ್ಞ ಅಥವಾ ನೈವೇದ್ಯಗಳಿಂದ ಎಂದಿಗೂ ಪ್ರಾಯಶ್ಚಿತ್ತವಾಗುವುದಿಲ್ಲ ಎಂಬುದಾಗಿ ಆಣೆಯಿಟ್ಟಿದ್ದೇನೆ” ಎಂದು ಹೇಳಿದನು.",
			"15": "<sup>15</sup> ಅನಂತರ ಸಮುವೇಲನು ಮಲಗಿ ಮುಂಜಾನೆಯಲ್ಲೇ ಎದ್ದು ಯೆಹೋವನ ಮಂದಿರದ ಬಾಗಿಲುಗಳನ್ನು ತೆರೆದನು. ಅವನು ತಾನು ಕಂಡದ್ದನ್ನು ಏಲಿಗೆ ತಿಳಿಸುವುದಕ್ಕೆ ಭಯಪಟ್ಟನು.",
			"16": "<sup>16</sup> ಆದರೆ ಏಲಿಯು, “ನನ್ನ ಮಗನೇ, ಸಮುವೇಲನೇ” ಎಂದು ಕರೆಯಲು ಅವನು, “ಇಗೋ, ಇದ್ದೇನೆ” ಎಂದನು.",
			"17": "<sup>17</sup> ಆಗ ಏಲಿಯು ಅವನಿಗೆ, “ಯೆಹೋವನು ನಿನಗೆ ತಿಳಿಸಿದ್ದೇನು? ಆತನ ಮಾತುಗಳಲ್ಲಿ ಒಂದನ್ನಾದರೂ ಮುಚ್ಚಿಡಬೇಡ; ಮುಚ್ಚಿಟ್ಟರೆ ಆತನು ಬೇಕಾದ ಹಾಗೆ ನಿನ್ನನ್ನು ದಂಡಿಸಲಿ” ಎಂದು ಹೇಳಲು",
			"18": "<sup>18</sup> ಸಮುವೇಲನು ಮುಚ್ಚುಮರೆಯಿಲ್ಲದೆ ಎಲ್ಲವನ್ನು ತಿಳಿಸಿದನು. ಏಲಿಯು ಅದನ್ನು ಕೇಳಿ, “ಆತನು ಯೆಹೋವನು; ತನಗೆ ಸರಿಕಾಣುವುದನ್ನು ಮಾಡಲಿ” ಎಂದನು.",
			"19": "<sup>19</sup> ಸಮುವೇಲನು ದೊಡ್ಡವನಾಗುತ್ತಾ ಬಂದನು. ಯೆಹೋವನು ಅವನೊಡನೆ ಇದ್ದುದ್ದರಿಂದ ಅವನ ಪ್ರವಾದನೆಗಳಲ್ಲಿ ಒಂದೂ ಬಿದ್ದುಹೋಗಲಿಲ್ಲ.",
			"20": "<sup>20</sup> ಯೆಹೋವನು ಅವನನ್ನು ತನ್ನ ಪ್ರವಾದಿಯನ್ನಾಗಿ ನೇಮಿಸಿಕೊಂಡದ್ದು ದಾನ್ ಊರಿನಿಂದ ಬೇರ್ಷೆಬದವರೆಗಿದ್ದ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲರಿಗೆ ಗೊತ್ತಾಯಿತು.",
			"21": "<sup>21</sup> ಯೆಹೋವನು ಶೀಲೋವಿನಲ್ಲಿ ದರ್ಶನಕೊಡುವುದಕ್ಕೆ ಪ್ರಾರಂಭಿಸಿ ತನ್ನ ವಾಕ್ಯದಿಂದ ಸಮುವೇಲನಿಗೆ ತನ್ನನ್ನು ಪ್ರಕಟಿಸಿಕೊಳ್ಳುತ್ತಾ ಬಂದನು. ಸಮುವೇಲನು ಇಸ್ರಾಯೇಲರೆಲ್ಲರಿಗೆ ದೈವೋತ್ತರಗಳನ್ನು ತಿಳಿಸುತ್ತಿದ್ದನು."
		},
		"4": {
			"1": "<sup>1</sup> ಇಸ್ರಾಯೇಲರು ಫಿಲಿಷ್ಟಿಯರಿಗೆ ವಿರೋಧವಾಗಿ ಯುದ್ಧಕ್ಕೆ ಹೊರಟು ಎಬೆನೆಜೆರಿನ ಸಮೀಪದಲ್ಲಿ ಪಾಳೆಯಮಾಡಿಕೊಂಡರು; ಫಿಲಿಷ್ಟಿಯರು ಬಂದು ಅಫೇಕಿನಲ್ಲಿ ಇಳಿದುಕೊಂಡರು.",
			"2": "<sup>2</sup> ಇವರು ಇಸ್ರಾಯೇಲರಿಗೆ ಎದುರಾಗಿ ವ್ಯೂಹಕಟ್ಟಿ ಯುದ್ಧಮಾಡಲು ಇಸ್ರಾಯೇಲರು ಸೋತರು; ಫಿಲಿಷ್ಟಿಯರು ರಣರಂಗದಲ್ಲಿ ಸುಮಾರು ನಾಲ್ಕು ಸಾವಿರ ಮಂದಿ ಇಸ್ರಾಯೇಲರನ್ನು ಸಂಹರಿಸಿದರು.",
			"3": "<sup>3</sup> ಇಸ್ರಾಯೇಲರು ತಮ್ಮ ಪಾಳೆಯಕ್ಕೆ ಹಿಂದಿರುಗಿದ ಮೇಲೆ ಅವರ ಹಿರಿಯರು ಅವರಿಗೆ, “ಯೆಹೋವನು ಈ ಹೊತ್ತು ನಮ್ಮನ್ನು ಫಿಲಿಷ್ಟಿಯರಿಂದ ಅಪಜಯಪಡಿಸಿದ್ದೇಕೆ? ಶೀಲೋವಿನಿಂದ ಯೆಹೋವನ ಒಡಂಬಡಿಕೆಯ ಮಂಜೂಷವನ್ನು ತರಿಸೋಣ; ಆತನು ನಮ್ಮ ಮಧ್ಯದಲ್ಲಿ ಬಂದು ಶತ್ರುಗಳ ಕೈಯಿಂದ ನಮ್ಮನ್ನು ತಪ್ಪಿಸಲಿ” ಎಂದು ಆಲೋಚನೆ ಹೇಳಿದ್ದರಿಂದ ಅವರು ಜನರನ್ನು ಕಳುಹಿಸಿ",
			"4": "<sup>4</sup> ಕೆರೂಬಿಗಳ ಮಧ್ಯದಲ್ಲಿ ಆಸೀನನಾಗಿರುವ ಸೈನ್ಯಾಧೀಶ್ವರ ಯೆಹೋವನ ಒಡಂಬಡಿಕೆಯ ಮಂಜೂಷವನ್ನು ತರಿಸಿದರು. ಏಲಿಯ ಇಬ್ಬರು ಮಕ್ಕಳಾದ ಹೊಫ್ನಿ, ಫೀನೆಹಾಸ್ ಎಂಬವರು ದೇವರ ಒಡಂಬಡಿಕೆಯ ಮಂಜೂಷದ ಸಂಗಡ ಇದ್ದರು.",
			"5": "<sup>5</sup> ಯೆಹೋವನ ಒಡಂಬಡಿಕೆಯ ಮಂಜೂಷವು ಪಾಳೆಯಕ್ಕೆ ಬಂದಾಗ ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲಾ ಭೂಮಿ ಕಂಪಿಸುವ ಹಾಗೆ ಆರ್ಭಟಿಸಿದರು.",
			"6": "<sup>6</sup> ಫಿಲಿಷ್ಟಿಯರು ಇದನ್ನು ಕೇಳಿ, “ಇಬ್ರಿಯರ ಪಾಳೆಯದಲ್ಲಿ ಇಂಥ ಆರ್ಭಟಕ್ಕೇನು ಕಾರಣ” ಎಂದು ವಿಚಾರಿಸುವಲ್ಲಿ ಯೆಹೋವನ ಮಂಜೂಷವು ಪಾಳೆಯದಲ್ಲಿ ಬಂದದ್ದೇ ಕಾರಣವೆಂದು ತಿಳಿದುಕೊಂಡರು.",
			"7": "<sup>7</sup> ದೇವರು ಇವರ ಪಾಳೆಯಕ್ಕೆ ಬಂದಿದ್ದಾನಲ್ಲಾ ಎಂದು ಫಿಲಿಷ್ಟಿಯರು ಬಹಳವಾಗಿ ಭಯಪಟ್ಟು, “ಅಯ್ಯೋ! ಮುಂಚೆ ಹೀಗಾಗಿರಲ್ಲಿಲ್ಲ.",
			"8": "<sup>8</sup> ಅಯ್ಯೋ! ಪರಾಕ್ರಮವುಳ್ಳ ಈ ದೇವರ ಕೈಯಿಂದ ನಮ್ಮನ್ನು ಬಿಡಿಸುವವರು ಯಾರು? ಐಗುಪ್ತ್ಯರನ್ನು ಅರಣ್ಯದಲ್ಲಿ ಸಂಪೂರ್ಣವಾಗಿ ನಾಶಮಾಡಿಬಿಟ್ಟ ದೇವರುಗಳು ಇವರೇ ಅಲ್ಲವೋ!",
			"9": "<sup>9</sup> ಫಿಲಿಷ್ಟಿಯರೇ, ಬಲಗೊಳ್ಳಿರಿ, ಶೂರರಾಗಿರ್ರಿ; ನಿಮಗೆ ದಾಸರಾಗಿದ್ದ ಇಬ್ರಿಯರಿಗೆ ನೀವೇ ದಾಸರಾದೀರಿ. ಆದಕಾರಣ ಶೌರ್ಯದಿಂದ ಯುದ್ಧಮಾಡಬೇಕು” ಎಂದು ಹೇಳಿಕೊಂಡು",
			"10": "<sup>10</sup> ಫಿಲಿಷ್ಟಿಯರು ಯುದ್ಧಮಾಡಲು ಇಸ್ರಾಯೇಲರು ಸೋತು ತಮ್ಮ ತಮ್ಮ ಮನೆಗಳಿಗೆ ಓಡಿಹೋದರು. ಮಹಾ ಸಂಹಾರವಾಯಿತು; ಇಸ್ರಾಯೇಲರಲ್ಲಿ ಮೂವತ್ತು ಸಾವಿರ ಕಾಲಾಳುಗಳು ಮಡಿದರು.",
			"11": "<sup>11</sup> ದೇವರ ಮಂಜೂಷವು ಶತ್ರುವಶವಾಯಿತು; ಏಲಿಯ ಮಕ್ಕಳಾದ ಹೊಫ್ನಿಯೂ ಮತ್ತು ಫೀನೆಹಾಸನೂ ಹತರಾದರು.",
			"12": "<sup>12</sup> ಅದೇ ದಿನ ಒಬ್ಬ ಬೆನ್ಯಾಮೀನನು ರಣರಂಗದಿಂದ ತಪ್ಪಿಸಿಕೊಂಡು ತನ್ನ ಬಟ್ಟೆಗಳನ್ನು ಹರಿದುಕೊಂಡು ತಲೆಯ ಮೇಲೆ ಮಣ್ಣುಹಾಕಿಕೊಂಡು ಶೀಲೋವಿಗೆ ಬಂದು",
			"13": "<sup>13</sup> ನಡೆದದ್ದನ್ನು ತಿಳಿಸಲಾಗಿ ಜನರೆಲ್ಲಾ ಗೋಳಾಡತೊಡಗಿದರು. ದೇವರ ಮಂಜೂಷವೇನಾಯಿತೋ ಎಂಬ ಚಿಂತೆಯಿಂದ ಮಂದಿರದ್ವಾರದ ಬಳಿಯಲ್ಲಿ ಆಸೀನನಾಗಿ ದಾರಿಯನ್ನು ನೋಡುತ್ತಿದ್ದ ಏಲಿಯು ಆ ದುಃಖದ ಧ್ವನಿಯನ್ನು ಕೇಳಿ",
			"14": "<sup>14</sup> ಈ ಗದ್ದಲವೇನೆಂದು ವಿಚಾರಿಸಲು ಒಡನೆ ಆ ಮನುಷ್ಯನು ಏಲಿಯ ಬಳಿಗೆ ಬಂದು ನಡೆದದ್ದೆಲ್ಲವನ್ನೂ ತಿಳಿಸಿದನು. ಏಲಿಯು ತೊಂಭತ್ತೆಂಟು ವರ್ಷದವನು;",
			"15": "<sup>15</sup> ಕಣ್ಣುಗಳು ಇಂಗಿಹೋಗಿ ಕುರುಡನಾಗಿದ್ದನು.",
			"16": "<sup>16</sup> ಆ ಮನುಷ್ಯನು ಏಲಿಗೆ, “ನಾನು ರಣರಂಗದಲ್ಲಿದ್ದವನು; ಈ ಹೊತ್ತೇ ಅಲ್ಲಿಂದ ಓಡಿಬಂದೆನು” ಅನ್ನಲು ಏಲಿಯು ಅವನನ್ನು, “ನನ್ನ ಮಗನೇ, ವರ್ತಮಾನವೇನು?” ಎಂದು ಕೇಳಿದನು.",
			"17": "<sup>17</sup> ಅವನು ಏಲಿಗೆ, “ಇಸ್ರಾಯೇಲರು ಫಿಲಿಷ್ಟಿಯರಿಗೆ ಬೆನ್ನುತೋರಿಸಿದರು; ಮಹಾಸಂಹಾರವಾಯಿತು. ನಿನ್ನ ಇಬ್ಬರು ಮಕ್ಕಳಾದ ಹೊಫ್ನಿ, ಫೀನೆಹಾಸರು ಸತ್ತರು; ದೇವರ ಮಂಜೂಷವು ಶತ್ರುವಶವಾಯಿತು” ಎಂದು ತಿಳಿಸಿದನು.",
			"18": "<sup>18</sup> ಮುದುಕನೂ, ಸ್ಥೂಲಕಾಯನೂ ಆದ ಏಲಿಯು ದೇವರ ಒಡಂಬಡಿಕೆಯ ಮಂಜೂಷದ ವರ್ತಮಾನವನ್ನು ಕೇಳಿದೊಡನೆಯೇ ಪೀಠದಿಂದ ಹಿಂದಕ್ಕೆ ಬಾಗಿಲಿನ ಕಡೆಗೆ ಬಿದ್ದು ಕುತ್ತಿಗೆ ಮುರಿದು ಸತ್ತನು. ಅವನು ಇಸ್ರಾಯೇಲರನ್ನು ನಲ್ವತು ವರ್ಷ ಪಾಲಿಸಿದ್ದನು.",
			"19": "<sup>19</sup> ಅವನ ಸೊಸೆಯಾದ ಫೀನೆಹಾಸನ ಹೆಂಡತಿ ದಿನತುಂಬಿದ ಗರ್ಭಿಣಿಯಾಗಿದ್ದಳು. ಅವಳು ದೇವರ ಮಂಜೂಷವು ಶತ್ರು ವಶವಾಯಿತೆಂಬ ವರ್ತಮಾನವನ್ನೂ, ತನ್ನ ಮಾವನೂ, ತನ್ನ ಗಂಡನೂ ಹತರಾದದ್ದನ್ನೂ ಕೇಳಿದೊಡನೆ ಪ್ರಸವವೇದನೆಯುಂಟಾಗಿ ಮಗುವನ್ನು ಹೆತ್ತಳು.",
			"20": "<sup>20</sup> ಆಕೆಯು ಸಾಯುವ ಸಮಯದಲ್ಲಿ ಬಳಿಯಲ್ಲಿ ನಿಂತಿದ್ದ ಸ್ತ್ರೀಯರು, “ಭಯಪಡಬೇಡ, ಮಗನನ್ನು ಹಡೆದಿರುವೆ” ಎಂದು ಹೇಳಿದರು. ಆಕೆಯು ಅವರ ಮಾತಿಗೆ ಲಕ್ಷ್ಯಕೊಡಲಿಲ್ಲ, ಪ್ರತ್ಯುತ್ತರಕೊಡಲಿಲ್ಲ.",
			"21": "<sup>21</sup> ದೇವರ ಮಂಜೂಷವು ಶತ್ರುವಶವಾದ್ದರಿಂದಲೂ, ಮಾವನೂ, ಗಂಡನೂ ಮೃತರಾದ್ದರಿಂದಲೂ, “ಮಹಿಮೆಯು ಇಸ್ರಾಯೇಲರನ್ನು ಬಿಟ್ಟು ಹೋಯಿತೆಂದು ಹೇಳಿ ಆ ಕೂಸಿಗೆ ಈಕಾಬೋದ್” ಎಂದು ಹೆಸರಿಟ್ಟಳು.",
			"22": "<sup>22</sup> “ದೇವರ ಮಂಜೂಷವು ಶತ್ರುವಶವಾದ್ದರಿಂದಲೇ ಇಸ್ರಾಯೇಲರ ಮಹಿಮೆಯು ಹೊರಟುಹೋಯಿತೆಂದು” ಹೇಳಿದಳು."
		},
		"5": {
			"1": "<sup>1</sup> ಫಿಲಿಷ್ಟಿಯರು ದೇವರ ಮಂಜೂಷವನ್ನು ಎಬೆನೆಜೆರಿನಿಂದ ಅಷ್ಡೋದಿನಲ್ಲಿರುವ",
			"2": "<sup>2</sup> ದಾಗೋನನ ಗುಡಿಗೆ ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ಅಲ್ಲಿ ದಾಗೋನನ ಮಗ್ಗುಲಲ್ಲೇ ಇಟ್ಟರು.",
			"3": "<sup>3</sup> ಅಷ್ಡೋದಿನವರು ಮರುದಿನ ಬೆಳಿಗ್ಗೆ ಎದ್ದು ನೋಡಲಾಗಿ ದಾಗೋನ್ ವಿಗ್ರಹವು ಯೆಹೋವನ ಮಂಜೂಷದ ಮುಂದೆ ಬೋರಲಬಿದ್ದಿರುವುದನ್ನು ಕಂಡು ಅದನ್ನು ಎತ್ತಿ ತಿರುಗಿ ಅದರ ಸ್ಥಳದಲ್ಲಿಟ್ಟರು.",
			"4": "<sup>4</sup> ಮಾರನೆಯ ದಿನ ಬೆಳಿಗ್ಗೆ ನೋಡುವಾಗ ದಾಗೋನನ ತಲೆಯೂ, ಕೈಗಳೂ ಕಡಿಯಲ್ಪಟ್ಟು ಹೊಸ್ತಿಲಿನ ಮೇಲೆ ಬಿದ್ದಿದ್ದವು; ಮುಂಡ ಮಾತ್ರ ಯೆಹೋವನ ಮಂಜೂಷದ ಮುಂದೆ ಬಿದ್ದಿತ್ತು;",
			"5": "<sup>5</sup> ಆದ್ದರಿಂದ ಈ ದಿನದ ವರೆಗೂ ದಾಗೋನನ ಯಾಜಕರೂ ಮತ್ತು ದಾಗೋನನ ಮನೆಗೆ ಬರುವವರೆಲ್ಲರೂ ಅಷ್ಡೋದಿನ ಗುಡಿಯ ಹೊಸ್ತಿಲನ್ನು ಇಂದಿಗೂ ತುಳಿಯದಿರುವುದಕ್ಕೆ ಇದೇ ಕಾರಣ.",
			"6": "<sup>6</sup> ಯೆಹೋವನ ಹಸ್ತವು ಅಷ್ಡೋದಿನವರಿಗೆ ಬಾಧಕವಾಗಿತ್ತು; ಆತನು ಆ ನಗರದಲ್ಲಿಯೂ, ಅದರ ಗ್ರಾಮಗಳಲ್ಲಿಯೂ ಗಡ್ಡೆ ರೋಗವನ್ನು ಬರಮಾಡಿ ಅವರನ್ನು ನಾಶಮಾಡಿದನು.",
			"7": "<sup>7</sup> ಅಷ್ಡೋದಿನ ಜನರು ಅದನ್ನು ನೋಡಿ, “ಇಸ್ರಾಯೇಲಿನ ದೇವರ ಮಂಜೂಷವು ನಮ್ಮ ಬಳಿಯಲ್ಲಿ ಇರಬಾರದು; ಏಕೆಂದರೆ ಆತನ ಹಸ್ತವು ನಮಗೂ, ನಮ್ಮ ದೇವರಾದ ದಾಗೋನನಿಗೂ ಬಾಧಕವಾಗಿದೆ” ಎಂದು ಹೇಳಿ",
			"8": "<sup>8</sup> ಫಿಲಿಷ್ಟಿಯ ಪ್ರಭುಗಳೆಲ್ಲರನ್ನೂ ಕೂಡಿಸಿ, “ಇಸ್ರಾಯೇಲಿನ ದೇವರ ಮಂಜೂಷವನ್ನು ಏನು ಮಾಡೋಣ?” ಎಂದು ಕೇಳಲು ಅವರು, “ಅದನ್ನು ಗತ್ ಊರಿಗೆ ಕಳುಹಿಸಿ ಕೊಡಬೇಕೆಂದು ನಿರ್ಣಯಿಸಿದರು.” ಅವರು ಹಾಗೆಯೇ ಇಸ್ರಾಯೇಲಿನ ದೇವರ ಮಂಜೂಷವನ್ನು ಅಲ್ಲಿಗೆ ಕಳುಹಿಸಿದರು.",
			"9": "<sup>9</sup> ಮಂಜೂಷವು ಅಲ್ಲಿ ಹೋದ ಮೇಲೆ ಯೆಹೋವನ ಹಸ್ತವು ಆ ಊರಿನವರಿಗೂ ಬಾಧಕವಾದುದರಿಂದ ಅಲ್ಲಿ ದೊಡ್ಡ ಗದ್ದಲವಾಯಿತು. ಯೆಹೋವನು ಊರಿನ ಚಿಕ್ಕವರಲ್ಲಿಯೂ, ದೊಡ್ಡವರಲ್ಲಿಯೂ ಗಡ್ಡೆಗಳನ್ನು ಬರಮಾಡಿದನು.",
			"10": "<sup>10</sup> ಆದ್ದರಿಂದ ಅವರು ದೇವರ ಮಂಜೂಷವನ್ನು ಎಕ್ರೋನಿಗೆ ಕಳುಹಿಸಿದರು. ಅದು ಅಲ್ಲಿಗೆ ಬಂದಾಗ ಆ ಊರಿನವರು, “ನೋಡಿರಿ, ನಮ್ಮನ್ನೂ, ನಮಗೆ ಸೇರಿದವರನ್ನೂ ಕೊಲ್ಲುವುದಕ್ಕೋಸ್ಕರ ಇಸ್ರಾಯೇಲಿನ ದೇವರ ಮಂಜೂಷವನ್ನು ಇಲ್ಲಿಗೆ ಕಳುಹಿಸಿದ್ದಾರೆ” ಎಂದು ಕೂಗಿಕೊಂಡರು.",
			"11": "<sup>11</sup> ದೇವರ ಹಸ್ತವು ಅವರಿಗೂ ಬಾಧಕವಾಗಿದ್ದುದರಿಂದ ಅವರಿಗೆ ಮರಣಭಯವುಂಟಾಯಿತು. ಅವರು ಫಿಲಿಷ್ಟಿಯ ಪ್ರಭುಗಳೆಲ್ಲರನ್ನೂ ಕೂಡಿಸಿ ಅವರಿಗೆ, “ಇಸ್ರಾಯೇಲಿನ ದೇವರ ಮಂಜೂಷವನ್ನು ಹಾಗೆಯೇ ಅದರ ಸ್ಥಳಕ್ಕೆ ಕಳುಹಿಸಿಬಿಡಿರಿ” ಅಂದರು.",
			"12": "<sup>12</sup> ಸಾಯದೆ ಉಳಿದ ಜನರಿಗೂ ಗಡ್ಡೆರೋಗ ಬಂದಿತ್ತು. ಪಟ್ಟಣದ ಗೋಳಾಟವು ಆಕಾಶವನ್ನು ಮುಟ್ಟಿತು."
		},
		"6": {
			"1": "<sup>1</sup> ಯೆಹೋವನ ಮಂಜೂಷವು ಫಿಲಿಷ್ಟಿಯರ ದೇಶದಲ್ಲಿ ಏಳು ತಿಂಗಳ ಕಾಲ ಇತ್ತು.",
			"2": "<sup>2</sup> ನಂತರ ಫಿಲಿಷ್ಟಿಯರು ಪೂಜಾರಿಗಳನ್ನೂ, ಕಣಿಹೇಳುವವರನ್ನೂ ಕರೆದು ಅವರಿಗೆ, “ಯೆಹೋವನ ಮಂಜೂಷವನ್ನು ಏನು ಮಾಡಬೇಕು? ಅದನ್ನು ಪುನಃ ಅದರ ಸ್ಥಳಕ್ಕೆ ಹೇಗೆ ಕಳುಹಿಸಬೇಕು ತಿಳಿಸಿರಿ” ಅನ್ನಲು",
			"3": "<sup>3</sup> ಅವರು, “ನೀವು ಇಸ್ರಾಯೇಲಿನ ದೇವರ ಮಂಜೂಷವನ್ನು ಸುಮ್ಮನೆ ಕಳುಹಿಸಬಾರದು. ಪ್ರಾಯಶ್ಚಿತ್ತದ್ರವ್ಯದೊಡನೆ ಕಳುಹಿಸಿದರೆ ಮಾತ್ರ ಗುಣಹೊಂದುವಿರಿ. ಆತನ ಶಿಕ್ಷಾಹಸ್ತವು ನಿಮ್ಮನ್ನು ಬಾಧಿಸುತ್ತಿರುವುದಕ್ಕೆ ಕಾರಣವು ಗೊತ್ತಾಗುವುದು” ಎಂದು ಹೇಳಿದರು.",
			"4": "<sup>4</sup> ಜನರು, “ನಾವು ಪ್ರಾಯಶ್ಚಿತ್ತಾರ್ಥವಾಗಿ ಏನು ಕಳುಹಿಸಬೇಕು?” ಎಂದು ಕೇಳಲು ಅವರು, “ನಿಮಗೂ ನಿಮ್ಮ ಪ್ರಭುಗಳಿಗೂ ಒಂದೇ ವಿಧವಾದ ವ್ಯಾಧಿ ಇರುವುದರಿಂದ ಪ್ರಭುಗಳ ಸಂಖ್ಯೆಗೆ ಸರಿಯಾಗಿ ಬಂಗಾರದ ಐದು ಗಡ್ಡೆಗಳನ್ನೂ, ಬಂಗಾರದ ಐದು ಇಲಿಗಳನ್ನು ಮಾಡಿಸಬೇಕು.",
			"5": "<sup>5</sup> ನೀವು ನಿಮ್ಮ ದೇಶನಿವಾಸಿಗಳ ವಿನಾಶಕ್ಕೆ ಕಾರಣವಾಗಿರುವ ಗಡ್ಡೆ ಮತ್ತು ಇಲಿ ಇವುಗಳ ಸ್ವರೂಪಗಳನ್ನು ಚಿನ್ನದಿಂದ ಮಾಡಿಸಿ, ಇಸ್ರಾಯೇಲಿನ ದೇವರಿಗೆ ಅದನ್ನು ಅರ್ಪಿಸಿ ಮಹಿಮೆಯನ್ನು ಸಲ್ಲಿಸಿದರೆ, ಆತನ ಶಿಕ್ಷಾಹಸ್ತವು ನಿಮ್ಮನ್ನೂ, ನಿಮ್ಮ ದೇವತೆಗಳನ್ನೂ, ಪ್ರಾಂತಗಳನ್ನೂ ಬಿಟ್ಟುಹೋದೀತು.",
			"6": "<sup>6</sup> ಐಗುಪ್ತರಂತೆಯೂ ಫರೋಹನಂತೆಯೂ ನಿಮ್ಮ ಹೃದಯಗಳನ್ನು ಕಠಿಣಪಡಿಸಿಕೊಳ್ಳುವುದೇಕೆ? ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಕಳುಹಿಸಲೊಲ್ಲದ ಇವರನ್ನು ಯೆಹೋವನು ಎಷ್ಟೋ ವಿಧವಾಗಿ ಬಾಧಿಸಬೇಕಾಯಿತಲ್ಲಾ.",
			"7": "<sup>7</sup> ಈಗ ನೀವು ಒಂದು ಹೊಸ ಬಂಡಿಯನ್ನು ಮಾಡಿಸಿರಿ. ಅದಕ್ಕೆ ಹಾಲು ಕರೆಯುವ ಮತ್ತು ಎಂದೂ ನೊಗಹೊರದ ಎರಡು ಹಸುಗಳನ್ನು ಹೂಡಿಸಿರಿ. ಆ ಹಸುಗಳ ಕರುಗಳನ್ನು ಮನೆಗೆ ತೆಗೆದುಕೊಂಡು ಹೋಗಿರಿ",
			"8": "<sup>8</sup> ಆ ಬಂಡಿಯ ಮೇಲೆ ಯೆಹೋವನ ಮಂಜೂಷವನ್ನೂ, ನೀವು ಪ್ರಾಯಶ್ಚಿತ್ತಾರ್ಥವಾಗಿ ಕೊಡುವ ಬಂಗಾರದ ವಸ್ತುಗಳುಳ್ಳ ಚಿಕ್ಕ ಪೆಟ್ಟಿಗೆಯನ್ನೂ ಇಟ್ಟು ಕಳುಹಿಸಿಬಿಡಿರಿ.",
			"9": "<sup>9</sup> ಅ ಹಸುಗಳು ತಾವಾಗಿ ಸ್ವದೇಶದ ದಾರಿ ಹಿಡಿದು ಬೇತ್ ಷೆಮೆಷಿನ ಕಡೆಗೆ ಹೋದರೆ ಈ ದೊಡ್ಡ ಕೇಡನ್ನು ಬರಮಾಡಿದವನು ಯೆಹೋವನೇ ಎಂದೂ, ಆ ಮಾರ್ಗವನ್ನು ಹಿಡಿಯದಿದ್ದರೆ ಆತನ ಹಸ್ತವು ನಮ್ಮನ್ನು ಬಾಧಿಸಲ್ಲಿಲ್ಲ ಇದು ಆಕಸ್ಮಿಕವಾಗಿ ಬಂದಿದೆ ಎಂದು, ತಿಳಿದುಕೊಳ್ಳಿರಿ” ಅಂದರು.",
			"10": "<sup>10</sup> ಅದೇ ಪ್ರಕಾರ ಫಿಲಿಷ್ಟಿಯರು ಹಾಲುಕರೆಯುವ ಎರಡು ಹಸುಗಳನ್ನು ಹಿಡಿದುಕೊಂಡು ಬಂಡಿಗೆ ಹೂಡಿ ಅವುಗಳ ಕರುಗಳನ್ನು ಮನೆಯಲ್ಲಿ ಕಟ್ಟಿಹಾಕಿದರು.",
			"11": "<sup>11</sup> ಯೆಹೋವನ ಮಂಜೂಷವನ್ನೂ, ಚಿನ್ನದ ಇಲಿಗಳೂ, ಗಡ್ಡೆಗಳೂ ಇದ್ದ ಚಿಕ್ಕ ಪೆಟ್ಟಿಗೆಯನ್ನೂ ಬಂಡಿಯ ಮೇಲಿಟ್ಟರು.",
			"12": "<sup>12</sup> ಕೂಡಲೆ ಆ ಹಸುಗಳು ಬೇತ್ ಷೆಮೆಷಿನ ರಾಜಮಾರ್ಗವನ್ನು ಹಿಡಿದು, ಕೂಗುತ್ತಾ ನೆಟ್ಟಗೆ ಮುಂದೆ ನಡೆದವು; ಎಡಬಲಕ್ಕೆ ತಿರುಗಲಿಲ್ಲ. ಫಿಲಿಷ್ಟಿಯ ಪ್ರಭುಗಳು ಬೇತ್ ಷೆಮೆಷಿನ ಎಲ್ಲೆಯವರೆಗೂ ಅವುಗಳ ಹಿಂದೆ ಹೋದರು.",
			"13": "<sup>13</sup> ಬೇತ್ ಷೆಮೆಷಿನವರು ಅಲ್ಲಿನ ತಗ್ಗಿನಲ್ಲಿ ಗೋದಿಯ ಬೆಳೆಯನ್ನು ಕೊಯ್ಯುತ್ತಿದ್ದರು. ಅವರು ಕಣ್ಣೆತ್ತಿ ಯೆಹೋವನ ಮಂಜೂಷವನ್ನು ನೋಡಿ ಬಹು ಸಂತೋಷಪಟ್ಟರು.",
			"14": "<sup>14</sup> ಬಂಡಿಯು ಬೇತ್ ಷೆಮೆಷಿನವನಾದ ಯೆಹೋಶುವನ ಹೊಲದಲ್ಲಿದ್ದ ದೊಡ್ಡ ಕಲ್ಲಿನ ಬಳಿಯಲ್ಲಿ ಬಂದು ನಿಂತಿತು. ಆಗ ಅವರು ಹೋಗಿ ಬಂಡಿಯ ಕಟ್ಟಿಗೆಗಳನ್ನು ಸೀಳಿ, ಆ ಹಸುಗಳನ್ನು ಯೆಹೋವನಿಗೆ ಯಜ್ಞಮಾಡಿದರು.",
			"15": "<sup>15</sup> ಲೇವಿಯರು ಯೆಹೋವನ ಮಂಜೂಷವನ್ನೂ, ಚಿನ್ನದ ವಸ್ತುಗಳುಳ್ಳ ಚಿಕ್ಕ ಪೆಟ್ಟಿಗೆಯನ್ನೂ ಇಳಿಸಿ ಆ ದೊಡ್ಡ ಕಲ್ಲಿನ ಮೇಲಿಟ್ಟರು. ಬೇತ್ ಷೆಮೆಷಿನವರು ಅದೇ ದಿನ ಯೆಹೋವನಿಗೆ ಸರ್ವಾಂಗಹೋಮಗಳನ್ನೂ ಮತ್ತು ಯಜ್ಞಗಳನ್ನೂ ಸಮರ್ಪಿಸಿದರು.",
			"16": "<sup>16</sup> ಇದನ್ನೆಲ್ಲಾ ನೋಡಿದ ಮೇಲೆ ಫಿಲಿಷ್ಟಿಯರ ಐದು ಮಂದಿ ಪ್ರಭುಗಳು ಅದೇ ದಿನ ಎಕ್ರೋನಿಗೆ ಹಿಂದಿರುಗಿದರು.",
			"17": "<sup>17</sup> ಫಿಲಿಷ್ಟಿಯರು ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಿಸಿದ ಚಿನ್ನದ ಗಡ್ಡೆಗಳು ಅಷ್ಡೋದ್, ಗಾಜಾ, ಅಷ್ಕೆಲೋನ್, ಗತ್ ಊರು, ಎಕ್ರೋನ್ ಎಂಬ ಅವರ ಸಂಸ್ಥಾನಗಳ ಸಂಖ್ಯೆಗಳಿಗೂ",
			"18": "<sup>18</sup> ಅವರು ಕಳುಹಿಸಿದ ಬಂಗಾರದ ಇಲಿಗಳು ಫಿಲಿಷ್ಟಿಯರ ಐದು ಮಂದಿ ಅರಸುಗಳ ಅಧೀನದಲ್ಲಿದ್ದ ಎಲ್ಲಾ ಗ್ರಾಮ ನಗರಗಳ ಸಂಖ್ಯೆಗಳಿಗೂ ಸರಿಯಾಗಿದ್ದವು. ಬೇತ್ ಷೆಮೆಷಿನವನಾದ ಯೆಹೋಶುವನ ಹೊಲದಲ್ಲಿ ಇರುವ ಯೆಹೋವನ ಮಂಜೂಷವೂ ಇಡಲ್ಪಟ್ಟ ಆ ದೊಡ್ಡ ಕಲ್ಲೇ ಇದಕ್ಕೆ ಸಾಕ್ಷಿಯಾಗಿ ಇಂದಿಗೂ ಇದೆ.",
			"19": "<sup>19</sup> ಬೇತ್ ಷೆಮೆಷಿನವರು ಯೆಹೋವನ ಮಂಜೂಷದಲ್ಲಿ ಇಣಕಿ ನೋಡಿದ್ದರಿಂದ ಯೆಹೋವನು ಅವರಲ್ಲಿ ಎಪ್ಪತ್ತು ಮಂದಿಯನ್ನು ಸಾಯಿಸಿದನು. ಇದಲ್ಲದೆ ಐವತ್ತು ಸಾವಿರ ಜನರು ಮರಣ ಹೊಂದಿದರು. ಯೆಹೋವನು ತಮ್ಮಲ್ಲಿ ಮಹಾನಾಶನವನ್ನು ಉಂಟುಮಾಡಿದ್ದರಿಂದ ಬೇತ್ ಷೆಮೆಷಿನವರು ಗೋಳಾಡಿದರು.",
			"20": "<sup>20</sup> ಪರಿಶುದ್ಧ ದೇವರಾದ ಯೆಹೋವನ ಮುಂದೆ ಯಾರು ನಿಂತಾರು? ನಮ್ಮನ್ನು ಬಿಟ್ಟು ಈತನು ಹೋಗತಕ್ಕ ಸ್ಥಳ ಯಾವುದು ಎಂದು ಮಾತನಾಡಿಕೊಂಡು",
			"21": "<sup>21</sup> ಕಿರ್ಯತ್ಯಾರೀಮಿನವರ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿ ಅವರಿಗೆ, “ಫಿಲಿಷ್ಟಿಯರು ಯೆಹೋವನ ಮಂಜೂಷವನ್ನು ಕಳುಹಿಸಿದ್ದಾರೆ; ನೀವು ಬಂದು ಅದನ್ನು ತೆಗೆದುಕೊಂಡು ಹೋಗಿರಿ” ಎಂದು ಹೇಳಿದರು."
		},
		"7": {
			"1": "<sup>1</sup> ಆಗ ಕಿರ್ಯತ್ಯಾರೀಮಿನವರು ಬಂದು ಯೆಹೋವನ ಮಂಜೂಷವನ್ನು ತೆಗೆದುಕೊಂಡು ಹೋಗಿ, ಗುಡ್ಡದ ಮೇಲೆ ವಾಸವಾಗಿದ್ದ ಅಬೀನಾದಾಬನ ಮನೆಯಲ್ಲಿಟ್ಟು, ಅವನ ಮಗನಾದ ಎಲ್ಲಾಜಾರನನ್ನು ಅದರ ಸೇವೆಗೋಸ್ಕರ ಪ್ರತಿಷ್ಠಿಸಿದರು.",
			"2": "<sup>2</sup> ಮಂಜೂಷವು ಕಿರ್ಯಾತ್ಯಾರೀಮಿಗೆ ಬಂದು ಬಹಳ ದಿನಗಳು ಅಂದರೆ, ಇಪ್ಪತ್ತು ವರ್ಷಗಳು ಕಳೆದು ಹೋದವು. ಈ ಕಾಲದಲ್ಲಿ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರು ದುಃಖಿಸುತ್ತಾ ಯೆಹೋವನ ಕಡೆಗೆ ತಿರುಗಿಕೊಳ್ಳಲು ಹಂಬಲಿಸುತ್ತಿದ್ದರು.",
			"3": "<sup>3</sup> ಆಗ ಸಮುವೇಲನು ಅವರಿಗೆ, “ನೀವು ಪೂರ್ಣಮನಸ್ಸಿನಿಂದ ಯೆಹೋವನ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡಿರುವುದಾದರೆ ನಿಮ್ಮ ಮಧ್ಯದಲ್ಲಿರುವ ಅಷ್ಟೋರೆತ್ ಮೊದಲಾದ ಅನ್ಯದೇವತೆಗಳನ್ನು ತೆಗೆದುಹಾಕಿ ಯೆಹೋವನ ಮೇಲೆಯೇ ಮನಸ್ಸಿಟ್ಟು ಆತನೊಬ್ಬನನ್ನೇ ಸೇವಿಸಿರಿ. ಆಗ ಆತನು ನಿಮ್ಮನ್ನು ಫಿಲಿಷ್ಟಿಯರ ಕೈಯಿಂದ ಬಿಡಿಸಿ ಕಾಪಾಡುವನು” ಅಂದನು.",
			"4": "<sup>4</sup> ಹಾಗೆಯೇ ಇಸ್ರಾಯೇಲ್ಯರು ಬಾಳ್ ಅಷ್ಟೋರೆತ್ ದೇವತೆಗಳನ್ನು ತೆಗೆದುಹಾಕಿ ಯೆಹೋವನೊಬ್ಬನನ್ನೇ ಆರಾಧಿಸತೊಡಗಿದರು.",
			"5": "<sup>5</sup> ಸಮುವೇಲನು ತಿರುಗಿ ಅವರಿಗೆ, “ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರೂ ಮಿಚ್ಪೆಯಲ್ಲಿ ಕೂಡಿಬರಲಿ; ನಾನು ನಿಮಗೋಸ್ಕರ ಯೆಹೋವನನ್ನು ಪ್ರಾರ್ಥಿಸುವೆನು” ಅಂದನು.",
			"6": "<sup>6</sup> ಅವರು ಅಲ್ಲಿ ಕೂಡಿಬಂದು, ನೀರು ಸೇದಿ, ಯೆಹೋವನ ಮುಂದೆ ಹೊಯ್ದು, ಆ ದಿನ ಉಪವಾಸವಿದ್ದು, “ನಾವು ನಿನಗೆ ದ್ರೋಹಿಗಳಾಗಿದ್ದೇವೆ” ಎಂದು ಯೆಹೋವನಿಗೆ ಅರಿಕೆಮಾಡಿದರು. ಆನಂತರ ಸಮುವೇಲನು ನ್ಯಾಯಪಾಲಕನಾಗಿದ್ದು, ಮಿಚ್ಪೆಯಲ್ಲಿ ಇಸ್ರಾಯೇಲ್ಯರ ವ್ಯಾಜ್ಯಗಳನ್ನು ತೀರಿಸಿದನು.",
			"7": "<sup>7</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಮಿಚ್ಪೆಯಲ್ಲಿ ಕೂಡಿಬಂದಿದ್ದಾರೆ ಎಂದು ಫಿಲಿಷ್ಟಿಯರಿಗೆ ಗೊತ್ತಾದಾಗ ಅವರ ರಾಜರು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ವಿರೋಧವಾಗಿ ಹೊರಟರು. ಇದನ್ನು ತಿಳಿದ ಇಸ್ರಾಯೇಲರು ಬಹಳವಾಗಿ ಭಯಪಟ್ಟು ಸಮುವೇಲನಿಗೆ,",
			"8": "<sup>8</sup> “ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಮ್ಮನ್ನು ಫಿಲಿಷ್ಟಿಯರ ಕೈಯಿಂದ ತಪ್ಪಿಸಿ ರಕ್ಷಿಸುವ ಹಾಗೆ ನೀನು ನಮಗೋಸ್ಕರ ಆತನಿಗೆ ಮೊರೆಯಿಡು, ಸುಮ್ಮನಿರಬೇಡ” ಅಂದರು.",
			"9": "<sup>9</sup> ಆಗ ಸಮುವೇಲನು ಹಾಲುಕುಡಿಯುವ ಕುರಿಮರಿಯನ್ನು ತಂದು ಯೆಹೋವನಿಗೆ ಸರ್ವಾಂಗಹೋಮವಾಗಿ ಸಮರ್ಪಿಸಿ ಇಸ್ರಾಯೇಲ್ಯರಿಗೋಸ್ಕರ ಮೊರೆಯಿಡಲು ಆತನು ಸದುತ್ತರವನ್ನು ದಯಪಾಲಿಸಿದನು.",
			"10": "<sup>10</sup> ಹೇಗೆಂದರೆ, ಸಮುವೇಲನು ಯಜ್ಞವನ್ನು ಅರ್ಪಿಸುವ ವೇಳೆಯಲ್ಲಿ ಫಿಲಿಷ್ಟಿಯರು ಇಸ್ರಾಯೇಲರ ವಿರುದ್ಧ ಯುದ್ಧಕ್ಕೋಸ್ಕರ ಸಮೀಪಿಸಲಾಗಿ, ಯೆಹೋವನು ದೊಡ್ಡ ಗುಡುಗಿನಿಂದ ಅವರನ್ನು ಕಳವಳಗೊಳಿಸಿ ಇಸ್ರಾಯೇಲರಿಗೆ ಸೋತು ಓಡಿ ಹೋಗುವಂತೆ ಮಾಡಿದನು.",
			"11": "<sup>11</sup> ಇಸ್ರಾಯೇಲ್ಯರಾದರೋ ಮಿಚ್ಪೆಯಿಂದ ಹೊರಟು ಫಿಲಿಷ್ಟಿಯರನ್ನು ಹಿಂದಟ್ಟಿ ಬೇತ್ಕರಿನ ತಗ್ಗಿನವರೆಗೆ ಅವರನ್ನು ಹತಮಾಡಿದರು.",
			"12": "<sup>12</sup> ಅನಂತರ ಸಮುವೇಲನು ಮಿಚ್ಪೆಗೂ ಶೇನಿಗೂ ಮಧ್ಯದಲ್ಲಿ ಒಂದು ಕಲ್ಲನ್ನು ನಿಲ್ಲಿಸಿ, “ಯೆಹೋವನು ಇಲ್ಲಿಯವರೆಗೆ ನಮಗೆ ಸಹಾಯಮಾಡಿದ್ದಾನೆ” ಎಂದು ಹೇಳಿ ಅದಕ್ಕೆ ಎಬೆನೆಜೆರ್ ಎಂದು ಹೆಸರಿಟ್ಟನು.",
			"13": "<sup>13</sup> ಫಿಲಿಷ್ಟಿಯರು ಬಹಳವಾಗಿ ತಗ್ಗಿಸಲ್ಪಟ್ಟದರಿಂದ ತಿರುಗಿ ಇಸ್ರಾಯೇಲ್ಯರ ಪ್ರಾಂತದೊಳಗೆ ಬರಲೇ ಇಲ್ಲ. ಸಮುವೇಲನ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ಯೆಹೋವನ ಹಸ್ತವು ಫಿಲಿಷ್ಟಿಯರಿಗೆ ವಿರೋಧವಾಗಿಯೇ ಇತ್ತು.",
			"14": "<sup>14</sup> ಅವರು ಎಕ್ರೋನ್ ಮೊದಲುಗೊಂಡು ಗತ್ ಊರಿನ ವರೆಗೆ ಇಸ್ರಾಯೇಲರಿಂದ ಕಿತ್ತುಕೊಂಡಿದ್ದ ಎಲ್ಲಾ ಪಟ್ಟಣಗಳು ಇಸ್ರಾಯೇಲರಿಗೇ ದೊರಕಿದವು. ಅವುಗಳಿಗೆ ಸೇರಿದ ಗ್ರಾಮಗಳನ್ನೂ ಇಸ್ರಾಯೇಲರು ಫಿಲಿಷ್ಟಿಯರಿಂದ ತೆಗೆದುಕೊಂಡರು. ಇಸ್ರಾಯೇಲರಿಗೂ ಅಮೋರಿಯರಿಗೂ ಸಮಾಧಾನವಿತ್ತು.",
			"15": "<sup>15</sup> ಸಮುವೇಲನು ಜೀವದಿಂದ ಇರುವವರೆಗೆ ಇಸ್ರಾಯೇಲರಿಗೆ ನ್ಯಾಯತೀರಿಸುತ್ತಿದ್ದನು.",
			"16": "<sup>16</sup> ಅವನು ಇಸ್ರಾಯೇಲರ ನ್ಯಾಯಗಳನ್ನು ತೀರಿಸುವುದಕ್ಕೋಸ್ಕರ ಪ್ರತಿವರ್ಷವೂ ಬೇತೇಲ್, ಗಿಲ್ಗಾಲ್, ಮಿಚ್ಪೆ ಎಂಬ ಪಟ್ಟಣಗಳನ್ನು ಸುತ್ತಿ ರಾಮಕ್ಕೆ ತಿರುಗಿ ಬರುತ್ತಿದ್ದನು.",
			"17": "<sup>17</sup> ಅಲ್ಲಿ ಅವನ ಸ್ವಂತ ಮನೆಯಿದ್ದುದರಿಂದ ಅಲ್ಲಿಯೇ ಯೆಹೋವನಿಗೆ ಒಂದು ಯಜ್ಞವೇದಿಯನ್ನು ಕಟ್ಟಿಸಿ ಇಸ್ರಾಯೇಲರ ವಿವಾದಗಳನ್ನು ತೀರಿಸುತ್ತಿದ್ದನು."
		},
		"8": {
			"1": "<sup>1</sup> ಸಮುವೇಲನು ಮುದುಕನಾದ ಮೇಲೆ ತನ್ನ ಮಕ್ಕಳನ್ನು ಇಸ್ರಾಯೇಲರಿಗೆ ನ್ಯಾಯಸ್ಥಾಪಕರನ್ನಾಗಿ ಮಾಡಿದನು.",
			"2": "<sup>2</sup> ಅವನ ಮೊದಲನೆಯ ಮಗನ ಹೆಸರು ಯೋವೇಲ್, ಎರಡನೆಯ ಮಗನ ಹೆಸರು ಅಬೀಯ. ಇವರು ಬೇರ್ಷೆಬದಲ್ಲಿ ನ್ಯಾಯಸ್ಥಾಪಕರಾಗಿದ್ದರು.",
			"3": "<sup>3</sup> ಇವರು ತಂದೆಯ ಮಾರ್ಗದಲ್ಲಿ ನಡೆಯದೆ ದ್ರವ್ಯಾಶೆಯಿಂದ ಲಂಚತೆಗೆದುಕೊಂಡು, ನ್ಯಾಯವಿರುದ್ಧವಾದ ತೀರ್ಪು ಕೊಡುತ್ತಿದ್ದರು.",
			"4": "<sup>4</sup> ಆದ್ದರಿಂದ ಇಸ್ರಾಯೇಲರ ಹಿರಿಯರೆಲ್ಲರೂ ಸೇರಿಕೊಂಡು ರಾಮದಲ್ಲಿದ್ದ ಸಮುವೇಲನ ಬಳಿಗೆ ಬಂದು",
			"5": "<sup>5</sup> ಅವನಿಗೆ, “ನೀನಂತೂ ಮುದುಕನಾದಿ; ನಿನ್ನ ಮಕ್ಕಳು ನಿನ್ನ ಮಾರ್ಗದಲ್ಲಿ ನಡೆಯುತ್ತಿಲ್ಲ. ಆದ್ದರಿಂದ ಬೇರೆ ಎಲ್ಲಾ ಜನಾಂಗಗಳಿಗೆ ಇರುವಂತೆ ನಮಗೂ ಒಬ್ಬ ಅರಸನನ್ನು ನೇಮಿಸಿಕೊಡು. ಅವನೇ ನಮ್ಮ ನ್ಯಾಯಸ್ಥಾಪಕನಾಗಿರಲಿ” ಅಂದರು.",
			"6": "<sup>6</sup> ನ್ಯಾಯಸ್ಥಾಪನೆಗೋಸ್ಕರ ನಮಗೊಬ್ಬ ಅರಸನನ್ನು ನೇಮಿಸಿಕೊಡು ಎಂಬ ಅವರ ಮಾತಿಗೆ ಸಮುವೇಲನು ದುಃಖಪಟ್ಟು ಯೆಹೋವನನ್ನು ಪ್ರಾರ್ಥಿಸಿದನು.",
			"7": "<sup>7</sup> ಆಗ ಯೆಹೋವನು ಸಮುವೇಲನಿಗೆ, “ಜನರು ಹೇಳಿದಂತೆಯೇ ಮಾಡು; ಅವರು ನಿನ್ನನ್ನಲ್ಲ, ನನ್ನನ್ನು ತಿರಸ್ಕರಿಸಿದ್ದಾರೆ. ನನ್ನ ಆಳ್ವಿಕೆಯನ್ನು ಬೇಡವೆನ್ನುತ್ತಿದ್ದಾರೆ.",
			"8": "<sup>8</sup> ನಾನು ಅವರನ್ನು ಐಗುಪ್ತದಿಂದ ಬಿಡಿಸಿದಂದಿನಿಂದ ಇಂದಿನ ವರೆಗೂ ಅವರು ನನ್ನನ್ನು ಬಿಟ್ಟು ಅನ್ಯದೇವತೆಗಳನ್ನು ಸೇವಿಸಿದರು. ನನಗೆ ಹೇಗೆ ಮಾಡಿದರೋ, ಹಾಗೆಯೇ ನಿನಗೂ ಮಾಡುತ್ತಾರೆ.",
			"9": "<sup>9</sup> ನೀನು ಈಗ ಅವರು ಕೇಳಿಕೊಂಡಂತೆ ಮಾಡು; ಆದರೆ ಅವರ ಮೇಲೆ ರಾಜರಿಗಿರುವ ಅಧಿಕಾರ ಎಷ್ಟೆಂಬುದನ್ನು ಅವರಿಗೆ ತಿಳಿಸಿ, ಎಚ್ಚರಿಸು” ಎಂದು ಹೇಳಿದನು.",
			"10": "<sup>10</sup> ಸಮುವೇಲನು ಅರಸನನ್ನು ನೇಮಿಸಿಕೊಡು ಎಂದು ಬೇಡಿಕೊಂಡ ಜನರಿಗೆ ಯೆಹೋವನ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ತಿಳಿಸಿ ಅವರಿಗೆ,",
			"11": "<sup>11</sup> “ಅರಸನಿಗಿರುವ ಅಧಿಕಾರವೆಷ್ಟೆಂದು ಕೇಳಿರಿ, ಅವನು ನಿಮ್ಮ ಮಕ್ಕಳನ್ನು ತೆಗೆದುಕೊಂಡು ತನ್ನ ಸಾರಥಿಗಳನ್ನಾಗಿಯೂ ರಾಹುತರನ್ನಾಗಿಯೂ ಮಾಡಿಕೊಳ್ಳುವನು. ಅವರು ಅವನ ರಥಗಳ ಮುಂದೆ ಓಡುವವರಾಗಬೇಕು.",
			"12": "<sup>12</sup> ಕೆಲವರನ್ನು ಸಹಸ್ರಾಧಿಪತಿಗಳನ್ನಾಗಿಯೂ, ಕೆಲವರನ್ನು ಪಂಚಶತಾಧಿಪತಿಗಳನ್ನಾಗಿಯೂ ನೇಮಿಸುವನು. ಇನ್ನು ಕೆಲವರು ಅವನ ಭೂಮಿಯನ್ನು ಉಳುವವರೂ, ಪೈರನ್ನು ಕೊಯ್ಯುವವರೂ, ಯುದ್ಧಕ್ಕೆ ಬೇಕಾದ ಆಯುಧ, ರಥಸಾಮಾಗ್ರಿಗಳನ್ನು ಮಾಡುವವರೂ ಆಗಬೇಕು.",
			"13": "<sup>13</sup> ಇದಲ್ಲದೆ ಅವನು ನಿಮ್ಮ ಹೆಣ್ಣುಮಕ್ಕಳನ್ನು ಸುಗಂಧದ್ರವ್ಯ ಮಾಡುವುದಕ್ಕೂ, ಅಡಿಗೆಮಾಡುವುದಕ್ಕೂ, ರೊಟ್ಟಿಸುಡುವುದಕ್ಕೂ ನೇಮಿಸಿಕೊಳ್ಳುವನು.",
			"14": "<sup>14</sup> ನಿಮ್ಮ ಉತ್ತಮವಾದ ಹೊಲಗಳನ್ನೂ, ದ್ರಾಕ್ಷೇತೋಟಗಳನ್ನೂ ಎಣ್ಣೇ ಮರದ ತೋಪುಗಳನ್ನೂ ಕಿತ್ತುಕೊಂಡು ತನ್ನ ಸೇವಕರಿಗೆ ಕೊಡುವನು.",
			"15": "<sup>15</sup> ನಿಮ್ಮ ಧಾನ್ಯ, ದ್ರಾಕ್ಷೆಗಳಲ್ಲಿ ಹತ್ತರಷ್ಟನ್ನು ತೆಗೆದುಕೊಂಡು ತನ್ನ ಪ್ರಧಾನರಿಗೂ, ಪರಿವಾರದವರಿಗೂ ಕೊಡುವನು.",
			"16": "<sup>16</sup> ನಿಮ್ಮ ದಾಸದಾಸಿಯರನ್ನೂ, ಉತ್ತಮವಾದ ಎತ್ತು ಕತ್ತೆಗಳನ್ನೂ ತನ್ನ ಕೆಲಸ ಮಾಡುವುದಕ್ಕಾಗಿ ತೆಗೆದುಕೊಳ್ಳುವನು.",
			"17": "<sup>17</sup> ಕುರಿಹಿಂಡುಗಳಲ್ಲಿ ಹತ್ತರಲ್ಲೊಂದು ಭಾಗವನ್ನು ತೆಗೆದುಕೊಳ್ಳುವನು. ನೀವು ಅವನಿಗೆ ದಾಸರಾಗಿರಬೇಕು.",
			"18": "<sup>18</sup> ಆಗ ನೀವು ಆರಿಸಿಕೊಂಡ ಅರಸನ ದೆಸೆಯಿಂದ ಆ ದಿನದಲ್ಲಿ ಬೇಸತ್ತು ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಡುವಿರಿ. ಆಗ ಆತನು ನಿಮ್ಮನ್ನು ಲಕ್ಷಿಸುವುದಿಲ್ಲ” ಅಂದನು.",
			"19": "<sup>19</sup> ಸಮುವೇಲನು ಎಷ್ಟು ಹೇಳಿದರೂ ಜನರು ಅವನ ಮಾತುಗಳನ್ನು ಕೇಳಲೊಲ್ಲದೆ, “ಇಲ್ಲ; ನಮಗೆ ಅರಸನನ್ನು ನೇಮಿಸಿಕೊಡು;",
			"20": "<sup>20</sup> ನಾವು ಇತರ ಜನಾಂಗಗಳಂತೆ ಆಗಬೇಕು. ನಮ್ಮ ನ್ಯಾಯಗಳನ್ನು ತೀರಿಸಿ ನಮಗೆ ಮುಂದಾಗಿ ಹೊರಟು ನಮಗೋಸ್ಕರ ಯುದ್ಧಮಾಡುವ ಒಬ್ಬ ಅರಸನು ಬೇಕು” ಎಂದು ಹೇಳಿದರು.",
			"21": "<sup>21</sup> ಸಮುವೇಲನು ಜನರ ಈ ಎಲ್ಲಾ ಮಾತುಗಳನ್ನು ಯೆಹೋವನಿಗೆ ತಿಳಿಸಲಾಗಿ",
			"22": "<sup>22</sup> ಆತನು ಅವನಿಗೆ, “ಅವರ ಇಷ್ಟದಂತೆ ಅವರಿಗೊಬ್ಬ ಅರಸನನ್ನು ನೇಮಿಸು” ಎಂದನು. ಆಗ ಸಮುವೇಲನು ಇಸ್ರಾಯೇಲರಿಗೆ, “ನಿಮ್ಮ ನಿಮ್ಮ ಊರುಗಳಿಗೆ ಹೋಗಿರಿ” ಎಂದು ಅಪ್ಪಣೆಕೊಟ್ಟನು."
		},
		"9": {
			"1": "<sup>1</sup> ಬೆನ್ಯಾಮೀನ್ ಕುಲದಲ್ಲಿ ಕೀಷನೆಂಬ ಒಬ್ಬ ಐಶ್ವರ್ಯವಂತ ಮತ್ತು ಪ್ರಭಾವಶಾಲಿಯಾದ ಮನುಷ್ಯನಿದ್ದನು. ಕೀಷನು ಅಬೀಯೇಲನ ಮಗ, ಅಬೀಯೇಲನು ಚೆರೋರನ ಮಗ, ಬೆಕೋರತನ ಮೊಮ್ಮಗ ಹಾಗೂ ಅಫೀಹನ ಮರಿಮಗನು.",
			"2": "<sup>2</sup> ಕೀಷನಿಗೆ ಸೌಲನೆಂಬ ಹೆಸರುಳ್ಳ ಒಬ್ಬ ಮಗನಿದ್ದನು. ಈ ಸೌಲನು ಯೌವನಸ್ಥನೂ, ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಅತಿಸುಂದರನೂ ಆಗಿದ್ದನು. ಜನರ ಗುಂಪಿನಲ್ಲಿ ಅವನ ಹೆಗಲೂ ತಲೆಯೂ ಕಾಣುವಷ್ಟು ಎತ್ತರವಾಗಿದ್ದನು.",
			"3": "<sup>3</sup> ಒಂದು ದಿನ ಸೌಲನ ತಂದೆಯಾದ ಕೀಷನ ಕತ್ತೆಗಳು ತಪ್ಪಿಸಿಕೊಂಡು ಹೋದುದರಿಂದ ಅವನು ತನ್ನ ಮಗನಾದ ಸೌಲನಿಗೆ, “ನೀನೆದ್ದು ಆಳುಗಳಲ್ಲಿ ಒಬ್ಬನನ್ನು ಕರೆದುಕೊಂಡು ಕತ್ತೆಗಳನ್ನು ಹುಡುಕುವುದಕ್ಕೆ ಹೋಗು” ಎಂದು ಹೇಳಿದನು.",
			"4": "<sup>4</sup> ಅದರಂತೆಯೇ ಅವನು ಹೊರಟು ಎಫ್ರಾಯೀಮ್ ಪರ್ವತಪ್ರಾಂತ್ಯ, ಶಾಲಿಷಾ ದೇಶ ಇವುಗಳಲ್ಲಿ ಸಂಚರಿಸಿ ಹುಡುಕಿದರೂ ಅವು ಸಿಕ್ಕಲಿಲ್ಲ. ಶಾಲೀಮ್, ಬೆನ್ಯಾಮೀನ್ ಈ ಪ್ರಾಂತಗಳಲ್ಲಿಯೂ ಸಿಕ್ಕಲಿಲ್ಲ.",
			"5": "<sup>5</sup> ಕಡೆಗೆ ಅವರು ಚೂಫ್ ದೇಶಕ್ಕೆ ಬಂದಾಗ ಸೌಲನು ತನ್ನ ಆಳಿಗೆ, “ಹಿಂದಿರುಗಿ ಹೋಗೋಣ ನಡಿ; ನಮ್ಮ ತಂದೆಯು ಈಗ ಕತ್ತೆಗಳ ಚಿಂತೆ ಬಿಟ್ಟು ನಮ್ಮ ಚಿಂತೆಯಲ್ಲಿರಬಹುದು” ಅಂದನು",
			"6": "<sup>6</sup> ಆದರೆ ಆ ಆಳು, “ಇಗೋ, ಈ ಪಟ್ಟಣದಲ್ಲಿ ಗೌರವಸ್ಥನಾದ ಒಬ್ಬ ದೇವರ ಮನುಷ್ಯನಿರುತ್ತಾನೆ. ಅವನು ನುಡಿಯುವುದೆಲ್ಲಾ ನೆರವೇರುತ್ತದೆ; ಅಲ್ಲಿಗೆ ಹೋಗೋಣ; ಒಂದು ವೇಳೆ ಅವನು ನಾವು ಹೋಗತಕ್ಕ ಮಾರ್ಗವನ್ನು ತಿಳಿಸುವನು” ಎಂದನು.",
			"7": "<sup>7</sup> ಅದಕ್ಕೆ ಸೌಲನು, “ನಾವು ಅವನ ಬಳಿಗೆ ಹೋಗಬೇಕಾದರೆ ಏನಾದರೂ ತೆಗೆದುಕೊಂಡು ಹೋಗಬೇಕು; ನಾವು ತಂದಿದ್ದ ಆಹಾರಸಾಮಗ್ರಿಯು ಮುಗಿದುಹೋಯಿತು. ಆ ದೇವರ ಮನುಷ್ಯನಿಗೆ ಕೊಡತಕ್ಕ ಕಾಣಿಕೆ ನಮ್ಮಲ್ಲಿಲ್ಲವಲ್ಲಾ; ಏನು ಕೊಡಬೇಕು?” ಎನ್ನಲು",
			"8": "<sup>8</sup> ಆಳು, “ಇಗೋ, ನನ್ನ ಕೈಯಲ್ಲಿ ಒಂದು ಪಾವಲಿಯಿದೆ (ಪಾವಲಿ ಎಂದರೆ ಒಂದು ಬೆಳ್ಳಿ ನಾಣ್ಯದ ಕಾಲು ಭಾಗ); ಇದನ್ನು ಅವನಿಗೆ ಕೊಡೋಣ; ಅವನು ನಮಗೆ ಮಾರ್ಗ ತೋರಿಸುವನು” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"9": "<sup>9</sup> (ಪೂರ್ವ ಕಾಲದಲ್ಲಿ ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಯಾವನಾದರೂ ದೈವೋತ್ತರ ಕೇಳಬೇಕಾದರೆ “ದೇವ ದರ್ಶಿಯ ಬಳಿಗೆ ಹೋಗೋಣ ಬನ್ನಿರಿ” ಎನ್ನುವರು. ಈ ಕಾಲದಲ್ಲಿ ಪ್ರವಾದಿಗಳೆನಿಸಿಕೊಳ್ಳುವವರನ್ನು, ಆ ಕಾಲದಲ್ಲಿ ದೇವದರ್ಶಿಗಳೆಂದು ಕರೆಯುತ್ತಿದ್ದರು.)",
			"10": "<sup>10</sup> ಆಗ ಸೌಲನು ಆಳಿಗೆ, “ನೀನು ಹೇಳುವುದು ಒಳ್ಳೆಯದು; ಹೋಗೋಣ ನಡಿ” ಅಂದನು.",
			"11": "<sup>11</sup> ಅವರಿಬ್ಬರೂ ಗುಡ್ಡವನ್ನು ಹತ್ತಿ ದೇವರ ಮನುಷ್ಯನಿದ್ದ ಪಟ್ಟಣವನ್ನು ಸಮೀಪಿಸಲು ನೀರು ಸೇದುವುದಕ್ಕೋಸ್ಕರ ಊರ ಹೊರಗೆ ಬಂದಿದ್ದ ಸ್ತ್ರೀಯರನ್ನು ಕಂಡು, “ದರ್ಶಿಯು ಊರಲ್ಲಿದ್ದಾನೋ” ಎಂದು ಕೇಳಿದರು. ಅವರು, “ಇದ್ದಾನೆ;",
			"12": "<sup>12</sup> ಈ ಹೊತ್ತು ನಮ್ಮ ಊರಿನವರು ಗುಡ್ಡದ ಮೇಲೆ ಯಜ್ಞವನ್ನು ಅರ್ಪಿಸುತ್ತಿರುವುದರಿಂದ ಅವನು ಈಗಲೇ ಇಲ್ಲಿಗೆ ಬಂದಿರುವನು; ಬೇಗನೆ ಹೋಗಿರಿ.",
			"13": "<sup>13</sup> ಅವನು ಯಜ್ಞಭೋಜನಕ್ಕೋಸ್ಕರ ಗುಡ್ಡಕ್ಕೆ ಹೋಗುವ ಮೊದಲು ನೀವು ಊರೊಳಕ್ಕೆ ಹೋದರೆ ಸಿಕ್ಕುವನು. ಅವನು ಅಲ್ಲಿಗೆ ಹೋಗುವ ವರೆಗೆ ಜನರು ಊಟಮಾಡುವುದಿಲ್ಲ. ಅವನು ಹೋಗಿ ಯಜ್ಞಾಹಾರವನ್ನು ಆಶೀರ್ವದಿಸುತ್ತಾನೆ. ಅನಂತರ ಕರೆಯಲ್ಪಟ್ಟವರು ಊಟಮಾಡುತ್ತಾರೆ. ಈಗಲೇ ಹೋಗಿರಿ. ಅವನು ಸಿಕ್ಕುವ ಸಮಯ ಇದೇ” ಅಂದರು.",
			"14": "<sup>14</sup> ಅವರು ಮೇಲೆ ಹತ್ತಿ ಊರಿಗೆ ಬಂದಾಗ ಸಮುವೇಲನು ಗುಡ್ಡಕ್ಕೆ ಹೋಗುವುದಕ್ಕೋಸ್ಕರ ಹೊರಟು ಅವರೆದುರಿಗೆ ಬಂದನು.",
			"15": "<sup>15</sup> ಸೌಲನು ಬರುವುದಕ್ಕೆ ಒಂದು ದಿನ ಮುಂದಾಗಿ ಯೆಹೋವನು ಸಮುವೇಲನಿಗೆ,",
			"16": "<sup>16</sup> “ನಾಳೆ ಇಷ್ಟು ಹೊತ್ತಿಗೆ ಬೆನ್ಯಾಮೀನ್ ದೇಶದ ಒಬ್ಬನನ್ನು ನಿನ್ನ ಬಳಿಗೆ ಬರಮಾಡುವೆನು. ನೀನು ಅವನನ್ನು ಇಸ್ರಾಯೇಲರ ನಾಯಕನನ್ನಾಗಿ ಅಭಿಷೇಕಿಸಬೇಕು. ಅವನು ನನ್ನ ಜನರನ್ನು ಫಿಲಿಷ್ಟಿಯರ ಕೈಯಿಂದ ಬಿಡಿಸಿ ಕಾಪಾಡುವನು. ಅವರ ಕೂಗು ನನಗೆ ಮುಟ್ಟಿತು; ಅವರನ್ನು ಕಟಾಕ್ಷಿಸಿದ್ದೇನೆ” ಎಂದು ತಿಳಿಸಿದ್ದನು.",
			"17": "<sup>17</sup> ಸಮುವೇಲನು ಸೌಲನನ್ನು ಕಂಡಾಗ ಯೆಹೋವನು ಅವನಿಗೆ, “ನಾನು ತಿಳಿಸಿದ್ದ ಮನುಷ್ಯನು ಇವನೇ; ಇವನೇ ನನ್ನ ಪ್ರಜೆಯನ್ನು ಆಳತಕ್ಕವನು” ಎಂದು ಸೂಚಿಸಿದನು.",
			"18": "<sup>18</sup> ಊರಬಾಗಿಲಿಗೆ ಬಂದ ಸೌಲನು ತನ್ನ ಎದುರಿನಲ್ಲಿ ಸಮುವೇಲನನ್ನು ನೋಡಿ, “ದರ್ಶಿಯ ಮನೆ ಎಲ್ಲಿದೆ ಎಂಬುದನ್ನು ದಯವಿಟ್ಟು ತಿಳಿಸುವಿಯೋ?” ಎನ್ನಲು ಸಮುವೇಲನು, “ನಾನೇ ಆ ದರ್ಶಿ.",
			"19": "<sup>19</sup> ನೀನು ನನ್ನ ಜೊತೆಯಲ್ಲಿ ಗುಡ್ಡಕ್ಕೆ ಬಾ; ನೀವು ಈ ಹೊತ್ತು ನನ್ನ ಸಂಗಡ ಊಟಮಾಡಬೇಕು. ನಿನ್ನ ಚಿಂತೆಯನ್ನೆಲ್ಲಾ ಪರಿಹರಿಸಿ ನಾಳೆ ಬೆಳಿಗ್ಗೆ ನಿನ್ನನ್ನು ಕಳುಹಿಸಿಕೊಡುವೆನು.",
			"20": "<sup>20</sup> ಮೂರು ದಿನಗಳ ಹಿಂದೆ ತಪ್ಪಿಸಿಕೊಂಡ ನಿನ್ನ ಕತ್ತೆಗಳಿಗೋಸ್ಕರ ಚಿಂತೆಮಾಡಬೇಡ; ಅವು ಸಿಕ್ಕಿವೆ. ಇಸ್ರಾಯೇಲರ ಅಭಿಲಾಷೆಯೆಲ್ಲಾ ಯಾರ ಮೇಲೆ ಇರುವುದು, ನಿನ್ನ ಮತ್ತು ನಿನ್ನ ತಂದೆಯ ಕುಟುಂಬದವರಿಗೂ ಸಲ್ಲುವುದು” ಎಂದು ಹೇಳಿದನು.",
			"21": "<sup>21</sup> ಅದಕ್ಕೆ ಸೌಲನು, “ನಾನು ಇಸ್ರಾಯೇಲ್ ಕುಲಗಳಲ್ಲಿ ಅಲ್ಪವಾಗಿರುವ ಬೆನ್ಯಾಮೀನ್ ಕುಲದವನಲ್ಲವೋ? ಬೆನ್ಯಾಮೀನ್ಯರ ಎಲ್ಲಾ ಕುಟುಂಬಗಳಲ್ಲಿ ನನ್ನದು ಕನಿಷ್ಠವಾದದ್ದು; ನನಗೆ ಇಂಥ ದೊಡ್ಡ ಮಾತನ್ನು ಯಾಕೆ ಹೇಳುತ್ತೀ?” ಎಂದನು.",
			"22": "<sup>22</sup> ಸಮುವೇಲನು ಸೌಲನನ್ನೂ ಅವನ ಸೇವಕನನ್ನೂ ಮಂಟಪಕ್ಕೆ ಕರೆದುಕೊಂಡು ಹೋದನು; ಭೋಜನಕ್ಕೆ ಬಂದಿದ್ದ ಸುಮಾರು ಮೂವತ್ತು ಜನರಲ್ಲಿ ಅವರಿಬ್ಬರಿಗೂ ಮುಖ್ಯಸ್ಥಾನವನ್ನು ಕೊಟ್ಟನು. ಅನಂತರ ಸಮುವೇಲನು ಅಡಿಗೆಯವನಿಗೆ,",
			"23": "<sup>23</sup> “ನಾನು ನಿನಗೆ ಹೇಳಿ ಪ್ರತ್ಯೇಕವಾಗಿ ಇಡಿಸಿದ್ದ ಮಾಂಸಭಾಗವನ್ನು ತಂದಿಡು” ಅಂದನು.",
			"24": "<sup>24</sup> ಅವನು ಒಂದು ತೊಡೆಯ ಮಾಂಸವನ್ನೂ ಅದರ ಸಂಗಡ ಇದ್ದದ್ದನ್ನೂ ತಂದು ಸೌಲನ ಮುಂದಿಟ್ಟನು. ಆಗ ಸಮುವೇಲನು, “ಇಗೋ, ಇದು ನಿನಗೋಸ್ಕರ ಪ್ರತ್ಯೇಕಿಸಿದ್ದು; ಇದನ್ನು ತಿನ್ನು, ಯಾಕೆಂದರೆ ಜನರನ್ನು ಭೋಜನಕ್ಕೆ ಕರೆದಾಗಿನಿಂದ ಇದು ನಿನಗೋಸ್ಕರವಾಗಿಯೇ ಇಡಲ್ಪಟ್ಟಿತ್ತು” ಎಂದು ಸೌಲನಿಗೆ ಹೇಳಿದನು. ಸೌಲನು ಆ ದಿನ ಸಮುವೇಲನ ಸಂಗಡ ಊಟಮಾಡಿದನು.",
			"25": "<sup>25</sup> ಅವರು ಗುಡ್ಡದಿಂದಿಳಿದು ಪಟ್ಟಣಕ್ಕೆ ಬಂದನಂತರ ಸಮುವೇಲನು ಸೌಲನನ್ನು ತನ್ನ ಮನೇ ಮಾಳಿಗೆಯ ಮೇಲೆ ಕರೆದುಕೊಂಡು ಹೋಗಿ ಅವನೊಡನೆ ಮಾತನಾಡಿದನು.",
			"26": "<sup>26</sup> ಮರುದಿನ ಬೆಳಿಗ್ಗೆ ಸಮುವೇಲನು ಮಾಳಿಗೆಯ ಮೇಲೆ ಮಲಗಿದ್ದ ಸೌಲನನ್ನು, “ಏಳು, ನಿನ್ನನ್ನು ಕಳುಹಿಸಿ ಕೊಡುತ್ತೇನೆ” ಎಂದು ಎಬ್ಬಿಸಲು ಅವನು ಎದ್ದನು. ತರುವಾಯ ಅವರಿಬ್ಬರೂ ಹೊರಟು ಊರ ಹೊರಗೆ ಬಂದಾಗ",
			"27": "<sup>27</sup> ಸಮುವೇಲನು ಸೌಲನಿಗೆ, “ನಿನ್ನ ಸೇವಕನನ್ನು ಮುಂದೆ ಕಳುಹಿಸಿ ನೀನು ಇಲ್ಲಿ ಸ್ವಲ್ಪ ನಿಲ್ಲು; ನಿನಗೆ ತಿಳಿಸಬೇಕಾದ ದೈವೋಕ್ತಿ ಒಂದುಂಟು” ಎಂದನು."
		},
		"10": {
			"1": "<sup>1</sup> ಆ ಆಳು ಮುಂದೆ ಹೋದಾಗ ಸಮುವೇಲನು ತೈಲದ ಕೊಂಬಿನಿಂದ ಅವನ ತಲೆಯ ಮೇಲೆ ತೈಲವನ್ನು ಹೊಯ್ದು ಅವನನ್ನು ಮುದ್ದಿಟ್ಟು ಅವನಿಗೆ, “ಯೆಹೋವನು ತನ್ನ ಸ್ವಾಸ್ತ್ಯವನ್ನು ಅಳುವುದಕ್ಕೋಸ್ಕರ ನಿಜವಾಗಿ ನಿನ್ನನ್ನು ಅಭಿಷೇಕಿಸಿದ್ದಾನೆ.",
			"2": "<sup>2</sup> ಈ ಹೊತ್ತು ನೀನು ನನ್ನನ್ನು ಬಿಟ್ಟುಹೋದ ನಂತರ ಬೆನ್ಯಾಮೀನ್ಯರ ಮೇರೆಯೊಳಗಿರುವ ರಾಹೇಲಳ ಸಮಾಧಿಯ ಬಳಿಯಲ್ಲಿರುವಂಥ ಚೆಲ್ಚಹಿನಲ್ಲಿ ಇಬ್ಬರು ಮನುಷ್ಯರನ್ನು ಕಾಣುವಿ. ಅವರು ನಿನಗೆ, ‘ನೀನು ಹುಡುಕುತ್ತಿರುವ ಕತ್ತೆಗಳು ಸಿಕ್ಕಿದವು; ನಿನ್ನ ತಂದೆಯು ಕತ್ತೆಗಳ ಚಿಂತೆಬಿಟ್ಟು ನನ್ನ ಮಗನು ಎಲ್ಲಿ ಹೋದನು ಎಂಬುದಾಗಿ ನಿನಗೋಸ್ಕರ ಹಂಬಲಿಸುತ್ತಿರುತ್ತಾನೆ’ ಎಂದು ಹೇಳುವರು.",
			"3": "<sup>3</sup> ನೀನು ಅಲ್ಲಿಂದ ಮುಂದೆ ನಡೆದು ತಾಬೋರಿನ ಹತ್ತಿರವಿರುವ ಏಲೋನ್‌ ವೃಕ್ಷದ ಬಳಿಗೆ ಬಂದಾಗ ದೇವದರ್ಶನಕ್ಕಾಗಿ ಬೇತೇಲಿಗೆ ಹೋಗುವ ಮೂವರು ಜನರು ನಿನ್ನನ್ನು ಎದುರುಗೊಳ್ಳುವರು. ಅವರಲ್ಲೊಬ್ಬನು ಮೂರು ಹೋತಮರಿಗಳನ್ನೂ, ಮತ್ತೊಬ್ಬನು ಮೂರು ರೊಟ್ಟಿಗಳನ್ನೂ, ಇನ್ನೊಬ್ಬನು ದ್ರಾಕ್ಷಾರಸದ ಒಂದು ಬುದ್ದಲಿಯನ್ನೂ ಹೊತ್ತುಕೊಂಡಿರುವರು.",
			"4": "<sup>4</sup> ಅವರು ನಿನ್ನ ಕ್ಷೇಮಸಮಾಚಾರವನ್ನು ವಿಚಾರಿಸಿ ಎರಡು ರೊಟ್ಟಿಗಳನ್ನು ನಿನಗೆ ಕೊಡುವರು; ನೀನು ಅವುಗಳನ್ನು ತೆಗೆದುಕೊಳ್ಳಬೇಕು.",
			"5": "<sup>5</sup> ಅಲ್ಲಿಂದ ಫಿಲಿಷ್ಟಿಯರ ದಂಡು ಇರುವ ದೇವಗಿರಿಯನ್ನು ಮುಟ್ಟಿದಾಗ ಮುಂಭಾಗದಲ್ಲಿ ಸ್ವರಮಂಡಲ, ದಮ್ಮಡಿ, ಕೊಳಲು, ಕಿನ್ನರಿ ಈ ವಾದ್ಯಗಾರರೊಡನೆ ಗುಡ್ಡದಿಂದ ಇಳಿದು ಬರುತ್ತಿರುವ ಪ್ರವಾದಿಗಳ ಸಮೂಹವನ್ನು ಕಾಣುವಿ. ಅವರು ಪರವಶರಾಗಿ ಪ್ರವಾದಿಸುವರು.",
			"6": "<sup>6</sup> ಆಗ ಯೆಹೋವನ ಆತ್ಮವು ನಿನ್ನ ಮೇಲೆಯೂ ಬರುವುದರಿಂದ ನೀನೂ ಅವರೊಂದಿಗೆ ಪ್ರವಾದಿಸುವಿ. ನೀನು ಮಾರ್ಪಟ್ಟ ಮನುಷ್ಯನಾಗುವಿ.",
			"7": "<sup>7</sup> ಈ ಗುರುತುಗಳೆಲ್ಲಾ ಸಂಭವಿಸಿದಾಗ ನಿನಗೆ ಅನುಕೂಲವಿದ್ದ ಹಾಗೆ ಮಾಡು; ದೇವರು ನಿನ್ನ ಸಂಗಡ ಇದ್ದಾನೆ.",
			"8": "<sup>8</sup> ನೀನು ಮುಂದಾಗಿ ಗಿಲ್ಗಾಲಿಗೆ ಹೋಗು; ಏಳು ದಿನಗಳಾದ ನಂತರ ನಾನು ಸರ್ವಾಂಗಹೋಮಗಳನ್ನೂ ಸಮಾಧಾನಯಜ್ಞಗಳನ್ನೂ ಸಮರ್ಪಿಸುವುದಕ್ಕೋಸ್ಕರ ಅಲ್ಲಿಗೆ ಬಂದು ನೀನು ಮಾಡಬೇಕಾದದ್ದನ್ನು ನಿನಗೆ ತಿಳಿಸುವೆನು; ಅಲ್ಲಿಯ ವರೆಗೆ ಕಾದುಕೊಂಡಿರು” ಎಂದು ಹೇಳಿದನು.",
			"9": "<sup>9</sup> ಸೌಲನು ಸಮುವೇಲನನ್ನು ಬಿಟ್ಟು ಹೊರಟ ಕೂಡಲೆ ದೇವರು ಅವನಿಗೆ ನೂತನ ಹೃದಯವನ್ನು ಕೊಟ್ಟನು. ಅದೇ ದಿನದಲ್ಲಿ ಆ ಎಲ್ಲಾ ಗುರುತುಗಳು ಸಂಭವಿಸಿದವು.",
			"10": "<sup>10</sup> ಅವನು ದೇವಗಿರಿಗೆ ಬಂದ ಕೂಡಲೆ ಪರವಶವಾದ ಪ್ರವಾದಿಸಮೂಹವು ತನ್ನೆದುರಿಗೆ ಬರುವುದನ್ನು ಕಂಡನು. ದೇವರ ಆತ್ಮವು ಸೌಲನ ಮೇಲೆ ಬಂದ್ದದರಿಂದ ಅವನೂ ಪರವಶನಾಗಿ ಅವರೊಂದಿಗೆ ಸೇರಿ ಪ್ರವಾದಿಸಿದನು.",
			"11": "<sup>11</sup> ಅವನು ಪರವಶನಾಗಿ ಮಾತನಾಡುವುದನ್ನು ಅವನ ಪರಿಚಿತರು ಕಂಡು ತಮ್ಮ ತಮ್ಮೊಳಗೆ, “ಕೀಷನ ಮಗನಿಗೆ ಏನಾಯಿತು? ಸೌಲನೂ ಪ್ರವಾದಿಗಳಲ್ಲಿ ಒಬ್ಬನೋ?” ಎಂದು ಮಾತನಾಡಿಕೊಳ್ಳುತ್ತಿದ್ದರು.",
			"12": "<sup>12</sup> ಆಗ ಆ ಸ್ಥಳದವನೊಬ್ಬನು, “ಹಾಗಾದರೆ ಇವರ ತಂದೆ ಯಾರು?” ಎಂದು ಕೇಳಿದನು. ಈ ಸಂಗತಿಯಿಂದ “ಸೌಲನು ಪ್ರವಾದಿಗಳಲ್ಲಿದ್ದಾನೆ” ಎಂಬ ವಿಷಯ ಹುಟ್ಟಿತು.",
			"13": "<sup>13</sup> ಅವನು ಪ್ರವಾದಿಸಿದ ನಂತರ ಬೆಟ್ಟದ ಮೇಲಿದ್ದ ಯಜ್ಞವೇದಿಯ ಬಳಿಗೆ ಬಂದನು.",
			"14": "<sup>14</sup> ಸೌಲನ ಚಿಕ್ಕಪ್ಪನು ಅವನನ್ನೂ ಅವನ ಸೇವಕನನ್ನೂ, “ನೀವು ಎಲ್ಲಿಗೆ ಹೋಗಿದ್ದಿರಿ?” ಎಂದು ಕೇಳಲು ಅವನು, “ಕತ್ತೆಗಳನ್ನು ಹುಡುಕುವುದಕ್ಕೆ ಹೋಗಿದ್ದೆವು. ಅವು ಸಿಕ್ಕಲಿಲ್ಲವಾದ್ದರಿಂದ ಸಮುವೇಲನ ಬಳಿಗೆ ಹೋದೆವು” ಅಂದನು.",
			"15": "<sup>15</sup> “ಸಮುವೇಲನು ಏನು ಹೇಳಿದನು?” ಎಂದು ಚಿಕ್ಕಪ್ಪನು ಕೇಳಲು",
			"16": "<sup>16</sup> ಸೌಲನು, “ಕತ್ತೆಗಳು ಸಿಕ್ಕಿವೇ ಎಂದು ತಿಳಿಸಿದನು” ಎಂಬುದಾಗಿ ಹೇಳಿದನೇ ಹೊರತು ಸಮುವೇಲನು ಹೇಳಿದ ರಾಜ್ಯಭಾರದ ಮಾತನ್ನು ತಿಳಿಸಲಿಲ್ಲ.",
			"17": "<sup>17</sup> ಸಮುವೇಲನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಮಿಚ್ಪೆಗೆ ಕರೆಯಿಸಿ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸೇರಿಸಿಕೊಂಡನು.",
			"18": "<sup>18</sup> ಆತನು ಅವರಿಗೆ, ಇಸ್ರಾಯೇಲ್‌ ದೇವರಾದ ಯೆಹೋವನು ಹೇಳುವುದನ್ನು ಕೇಳಿರಿ, “ನೀವು ಐಗುಪ್ತರ ಕೈಗೆ ತಪ್ಪಿಸಿಕೊಳ್ಳುವಂತೆ ನಿಮ್ಮನ್ನು ಅವರ ದೇಶದಿಂದ ಬಿಡಿಸಿದವನೂ, ಬಾಧಿಸುತ್ತಿದ್ದ ಬೇರೆ ಎಲ್ಲಾ ಜನಾಂಗಗಳಿಂದ ನಿಮ್ಮನ್ನು ರಕ್ಷಿಸಿದವನೂ ನಾನೇ.",
			"19": "<sup>19</sup> ನೀವಾದರೋ ನಿಮ್ಮನ್ನು ಕಷ್ಟಸಂಕಟಗಳಿಂದ ಬಿಡಿಸಿದ ನಿಮ್ಮ ದೇವರಾದ ನನ್ನನ್ನು ಈಗ ತಿರಸ್ಕರಿಸಿ, ನಮಗೊಬ್ಬ ಅರಸನನ್ನು ನೇಮಿಸೆಂದು ಹೇಳುತ್ತೀರಿ ಅನ್ನುತ್ತಾನೆ. ಆದುದರಿಂದ ನೀವು ಕುಲ ಕುಲವಾಗಿಯೂ ಗೋತ್ರಗೋತ್ರವಾಗಿಯೂ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಕಾಣಿಸಿಕೊಳ್ಳಿರಿ” ಎಂದು ಅಪ್ಪಣೆ ಮಾಡಿದನು.",
			"20": "<sup>20</sup> ಸಮುವೇಲನು ಇಸ್ರಾಯೇಲ್ಯರ ಎಲ್ಲಾ ಕುಲಗಳನ್ನು ಸಮೀಪಕ್ಕೆ ಕರೆದಾಗ ಬೆನ್ಯಾಮೀನ್ ಕುಲಕ್ಕೆ ಚೀಟುಬಿದ್ದಿತು.",
			"21": "<sup>21</sup> ಆ ಕುಲದ ಗೋತ್ರಗಳನ್ನು ಬರಮಾಡಿದಾಗ ಮಟ್ರಿಯ ಗೋತ್ರದಲ್ಲಿ ಕೀಷನ ಮಗನಾದ ಸೌಲನಿಗೆ ಚೀಟುಬಿದ್ದಿತು. ಆದರೆ ಅವನನ್ನು ಹುಡುಕಲು ಅವನು ಸಿಕ್ಕಲಿಲ್ಲ.",
			"22": "<sup>22</sup> ಅವರು, “ಆ ಮನುಷ್ಯನು ಇಲ್ಲಿಗೆ ಬರುವನೋ” ಎಂದು ಯೆಹೋವನನ್ನು ವಿಚಾರಿಸಿದಾಗ ಆತನು, “ಬಂದಿದ್ದಾನೆ; ಇಗೋ, ಸಾಮಾನುಗಳ ಮಧ್ಯದಲ್ಲಿ ಅಡಗಿಕೊಂಡಿದ್ದಾನೆ” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"23": "<sup>23</sup> ಅವರು ತಟ್ಟನೆ ಹೋಗಿ ಅವನನ್ನು ಹಿಡಿದುಕೊಂಡು ಬಂದು ಜನರ ಮಧ್ಯದಲ್ಲಿ ನಿಲ್ಲಿಸಿದರು. ಗುಂಪಿನಲ್ಲಿದ್ದ ಎಲ್ಲಾ ಜನರಿಗಿಂತ ಅವನ ಹೆಗಲೂ ತಲೆಯೂ ಕಾಣುವಷ್ಟು ಎತ್ತರವಾದ ಪುರುಷನಾಗಿದ್ದನು.",
			"24": "<sup>24</sup> ಆಗ ಸಮುವೇಲನು ಎಲ್ಲಾ ಜನರಿಗೆ, “ನೋಡಿದಿರಾ, ಯೆಹೋವನಿಂದ ಆರಿಸಲ್ಪಟ್ಟವನು ಇವನೇ; ಸರ್ವಜನರಲ್ಲಿ ಇವನಿಗೆ ಸಮಾನರು ಇಲ್ಲವೇ ಇಲ್ಲ” ಎನ್ನಲು ಜನರೆಲ್ಲರೂ, “ಅರಸನು ಚಿರಂಜೀವಿಯಾಗಿರಲಿ” ಎಂದು ಘೋಷಣೆಗಳನ್ನು ಕೂಗಿದರು.",
			"25": "<sup>25</sup> ತರುವಾಯ ಸಮುವೇಲನು ಜನರಿಗೆ ರಾಜನೀತಿಯನ್ನು ಬೋಧಿಸಿ, ಅದನ್ನು ಒಂದು ಪುಸ್ತಕದಲ್ಲಿ ಬರೆದು, ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿಟ್ಟನು. ಆ ಮೇಲೆ ಜನರನ್ನು ಅವರವರ ಮನೆಗಳಿಗೆ ಕಳುಹಿಸಿಬಿಟ್ಟನು.",
			"26": "<sup>26</sup> ಸೌಲನೂ ಗಿಬೆಯದಲ್ಲಿರುವ ತನ್ನ ಮನೆಗೆ ಹೋದನು; ಕೆಲವು ಮಂದಿ ಶೂರರು ದೇವರಿಂದ ಪ್ರೇರಿತರಾಗಿ ಅವನ ಜೊತೆಯಲ್ಲಿ ಹೋದರು.",
			"27": "<sup>27</sup> ಕೆಲವು ಮಂದಿ ಅಯೋಗ್ಯರು, “ಇವನು ನಮ್ಮನ್ನು ರಕ್ಷಿಸುವನೋ?” ಎಂದು ತಿರಸ್ಕರಿಸಿ ಕಾಣಿಕೆಗಳನ್ನು ತರಲಿಲ್ಲ. ಆದರೂ ಸೌಲನು ಸುಮ್ಮನಿದ್ದನು."
		},
		"11": {
			"1": "<sup>1</sup> ಅಮ್ಮೋನಿಯನಾದ ನಾಹಾಷನು ಹೊರಟು ಬಂದು ಯಾಬೇಷ್ ಗಿಲ್ಯಾದಿಗೆ ಮುತ್ತಿಗೆ ಹಾಕಲು, ಯಾಬೇಷಿನವರು ಅವನನ್ನು, “ನೀನು ನಮ್ಮ ಸಂಗಡ ಒಡಂಬಡಿಕೆ ಮಾಡಿಕೋ; ನಾವು ನಿನ್ನನ್ನು ಸೇವಿಸುವೆವು” ಎಂದು ಬೇಡಿಕೊಂಡರು.",
			"2": "<sup>2</sup> ಅದಕ್ಕೆ ಅವನು, “ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲರನ್ನು ಅವಮಾನಪಡಿಸುವುದಕ್ಕಾಗಿ ನಾನು ಮೊದಲು ನಿಮ್ಮಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬರ ಬಲಗಣ್ಣನ್ನು ಕಿತ್ತುಹಾಕುತ್ತೇನೆ. ಇದಕ್ಕೆ ನೀವು ಒಪ್ಪುವುದಾದರೆ ನಿಮ್ಮ ಸಂಗಡ ಒಡಂಬಡಿಕೆ ಮಾಡಿಕೊಳ್ಳುತ್ತೇನೆ” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"3": "<sup>3</sup> ಆಗ ಯಾಬೇಷಿನ ಹಿರಿಯರು ಅವನಿಗೆ, “ಏಳು ದಿನಗಳವರೆಗೆ ಸಮಯ ಕೊಡು; ಅಷ್ಟರೊಳಗೆ ನಾವು ಇಸ್ರಾಯೇಲರ ಎಲ್ಲಾ ಪ್ರಾಂತ್ಯಗಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸುತ್ತೇವೆ. ನಮ್ಮನ್ನು ಯಾರೂ ರಕ್ಷಿಸದಿದ್ದರೆ, ನಾವು ನಿನ್ನ ಬಳಿಗೆ ಹೊರಟು ಬಂದು ನಿನಗೆ ಅಧೀನರಾಗುತ್ತೇವೆ” ಎಂದು ತಿಳಿಸಿದರು.",
			"4": "<sup>4</sup> ದೂತರು ಸೌಲನು ವಾಸವಾಗಿದ್ದ ಗಿಬೆಯಕ್ಕೆ ಬಂದು ಅಲ್ಲಿನವರಿಗೆ ವರ್ತಮಾನವನ್ನು ತಿಳಿಸಲು ಎಲ್ಲರೂ ಅಳತೊಡಗಿದರು.",
			"5": "<sup>5</sup> ಅಷ್ಟರಲ್ಲಿ ಸೌಲನು ಹೊಲದಿಂದ ದನಗಳನ್ನು ಹೊಡೆದುಕೊಂಡು ಬಂದನು. ಅವನು, “ಜನರು ಗೋಳಾಡುವುದಕ್ಕೆ ಕಾರಣವೇನು?” ಎಂದು ಕೇಳಲು ಅವರು ಅವನಿಗೆ ಯಾಬೇಷಿನವರ ವರ್ತಮಾನವನ್ನು ತಿಳಿಸಿದರು.",
			"6": "<sup>6</sup> ಆಗ ಯೆಹೋವನ ಆತ್ಮವು ಅವನ ಮೇಲೆ ಬಂದಿತು. ಅವನು ಬಹಳ ಕೋಪಗೊಂಡು, ಒಂದು ಜೋಡಿ ಎತ್ತುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ತುಂಡು ತುಂಡು ಮಾಡಿದನು.",
			"7": "<sup>7</sup> ಆ ತುಂಡುಗಳನ್ನು ದೂತರ ಮುಖಾಂತರ ಇಸ್ರಾಯೇಲರ ಎಲ್ಲಾ ಪ್ರಾಂತ್ಯಗಳಿಗೆ ಕಳುಹಿಸಿ, “ಯಾರು ಸೌಲ, ಸಮುವೇಲರನ್ನು ಹಿಂಬಾಲಿಸುವುದಿಲ್ಲವೋ ಅವರ ಎತ್ತುಗಳು ಹೀಗೆಯೇ ಕಡಿಯಲ್ಪಡುವವು” ಎಂದು ಹೇಳಿ ಕಳುಹಿಸಿದನು. ಯೆಹೋವನಿಗೆ ಭಯಪಟ್ಟು ಜನರೆಲ್ಲರೂ ಏಕಮನಸ್ಸಿನಿಂದ ಬೆಜೆಕಿನಲ್ಲಿ ಕೂಡಿಬಂದರು.",
			"8": "<sup>8</sup> ಅವರನ್ನು ಬೆಜೆಕಿನಲ್ಲಿ ಕ್ರಮಪಡಿಸಿ ಲೆಕ್ಕಿಸಿದಾಗ ಇಸ್ರಾಯೇಲರಲ್ಲಿ ಮೂರು ಲಕ್ಷ ಸೈನಿಕರೂ ಯೆಹೂದ್ಯರಲ್ಲಿ ಮೂವತ್ತು ಸಾವಿರ ಸೈನಿಕರೂ ಇದ್ದರು.",
			"9": "<sup>9</sup> ಅವರು ಬಂದಿದ್ದ ದೂತರಿಗೆ, “ನಾಳೆ ಮಧ್ಯಾಹ್ನದಲ್ಲಿ ನಿಮಗೆ ಸಹಾಯ ಸಿಕ್ಕುವುದೆಂದು ಯಾಬೇಷ್ ಗಿಲ್ಯಾದಿನವರಿಗೆ ತಿಳಿಸಿರಿ” ಎಂದು ಹೇಳಿ ಅವರನ್ನು ಕಳುಹಿಸಿದರು.",
			"10": "<sup>10</sup> ದೂತರು ಹೋಗಿ ಈ ವರ್ತಮಾನವನ್ನು ತಿಳಿಸಲು ಯಾಬೇಷಿನವರು ಬಹಳವಾಗಿ ಸಂತೋಷಪಟ್ಟು ಅಮ್ಮೋನಿಯರಿಗೆ, “ನಾವು ನಾಳೆ ನಿಮ್ಮ ಬಳಿಗೆ ಬರುವೆವು; ನಿಮಗೆ ಸರಿ ಕಾಣುವ ಪ್ರಕಾರ ಮಾಡಿರಿ” ಎಂದು ಹೇಳಿದರು.",
			"11": "<sup>11</sup> ಸೌಲನು ಬೆಳಗಿನ ಜಾವದಲ್ಲಿ ತನ್ನ ಜನರನ್ನು ಮೂರು ಗುಂಪನ್ನಾಗಿ ಮಾಡಿ ಅಮ್ಮೋನಿಯರ ಪಾಳೆಯದೊಳಗೆ ನುಗ್ಗಿ ಅವರನ್ನು ಮಧ್ಯಾಹ್ನದ ವರೆಗೆ ಹತ್ಯೆಮಾಡಿದನು. ಉಳಿದವರನ್ನು ಒಟ್ಟಾಗಿ ಸೇರಿಸಿಕೊಳ್ಳದ ಹಾಗೆ ಚದರಿಸಿಬಿಟ್ಟನು.",
			"12": "<sup>12</sup> ತರುವಾಯ ಜನರು ಸಮುವೇಲನಿಗೆ, “ಸೌಲನು ನಮಗೆ ಅರಸನಾಗಬಾರದು ಎಂದು ಹೇಳಿದವರು ಯಾರು? ಅವರನ್ನು ನಮಗೆ ಒಪ್ಪಿಸಿರಿ; ನಾವು ಕೊಂದುಹಾಕುತ್ತೇವೆ” ಎಂದರು.",
			"13": "<sup>13</sup> ಸೌಲನು ಅವರಿಗೆ, “ಯೆಹೋವನು ಈ ದಿನ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಜಯವನ್ನು ಉಂಟುಮಾಡಿರುವುದರಿಂದ ಯಾರನ್ನೂ ಕೊಲ್ಲಬಾರದು” ಎಂದು ಹೇಳಿದನು.",
			"14": "<sup>14</sup> ಸಮುವೇಲನು ಜನರಿಗೆ, “ಬನ್ನಿರಿ; ಗಿಲ್ಗಾಲಿಗೆ ಹೋಗಿ ಸೌಲನ ಅರಸುತನವನ್ನು ಸ್ಥಿರಪಡಿಸೋಣ” ಅನ್ನಲು",
			"15": "<sup>15</sup> ಅವರೆಲ್ಲರೂ ಅಲ್ಲಿಗೆ ಹೋಗಿ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸೌಲನ ಅರಸುತನವನ್ನು ಸ್ಥಿರಪಡಿಸಿ, ಯೆಹೋವನಿಗೆ ಸಮಾಧಾನ ಯಜ್ಞಗಳನ್ನು ಸಮರ್ಪಿಸಿ, ಸೌಲನೊಡನೆ ಬಹಳವಾಗಿ ಆನಂದಿಸಿದರು."
		},
		"12": {
			"1": "<sup>1</sup> ಸಮುವೇಲನು ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ, “ನೋಡಿರಿ, ನಾನು ನಿಮ್ಮ ಬಿನ್ನಹಗಳನ್ನು ಆಲಿಸಿ ನಿಮಗೊಬ್ಬ ಅರಸನನ್ನು ನೇಮಿಸಿದೆನು.",
			"2": "<sup>2</sup> ಇನ್ನು ಮುಂದೆ ಇವನೇ ನಿಮ್ಮ ಅರಸನಾಗಿರುವನು. ಚಿಕ್ಕಂದಿನಿಂದ ನಿಮ್ಮ ನಾಯಕನಾಗಿದ್ದ ನಾನು ಈಗ ತಲೆನರೆತ ಮುದುಕನಾಗಿದ್ದೇನೆ; ನನ್ನ ಮಕ್ಕಳು ನಿಮ್ಮೊಂದಿಗಿದ್ದಾರೆ.",
			"3": "<sup>3</sup> ಇಲ್ಲಿ ನಿಂತುಕೊಂಡಿರುವ ನಾನು ಯಾರ ಎತ್ತನ್ನಾಗಲಿ, ಕತ್ತೆಯನ್ನಾಗಲಿ ತೆಗೆದುಕೊಂಡು ಯಾರನ್ನಾದರೂ ವಂಚಿಸಿ, ಪೀಡಿಸಿದ್ದೂ, ಲಂಚ ತೆಗೆದುಕೊಂಡು ಕುರುಡನಂತೆ ತೀರ್ಪು ಮಾಡಿದ್ದೂ ಉಂಟೋ? ಇದ್ದರೆ ಯೆಹೋವನ ಮುಂದೆಯೂ ಆತನ ಅಭಿಷಿಕ್ತನ ಮುಂದೆಯೂ ಹೇಳಿರಿ; ನಾನು ಅದನ್ನು ಹಿಂದಕ್ಕೆ ಕೊಡುತ್ತೇನೆ” ಅಂದನು.",
			"4": "<sup>4</sup> ಅವರು, “ನೀನು ನಮ್ಮನ್ನು ವಂಚಿಸಿ ಪೀಡಿಸಿದ್ದಾಗಲಿ, ನಮ್ಮಿಂದ ಏನಾದರೂ ಕಸಿದುಕೊಂಡದ್ದಾಗಲಿ ಇರುವುದಿಲ್ಲ” ಎಂದು ಉತ್ತರ ಕೊಟ್ಟರು.",
			"5": "<sup>5</sup> ಸಮುವೇಲನು ಪುನಃ “ನೀವು ನನ್ನಲ್ಲಿ ಇಂಥದನ್ನೇನೂ ಕಾಣಲಿಲ್ಲ ಎಂಬುದಕ್ಕೆ ಯೆಹೋವನೂ ಆತನ ಅಭಿಷಿಕ್ತನೂ ಸಾಕ್ಷಿಯಾಗಿದ್ದಾರೆ” ಎಂದನು. ಅವರು, “ಹೌದು, ಯೆಹೋವನೇ ಸಾಕ್ಷಿಯಾಗಿದ್ದಾನೆ” ಎಂದು ಹೇಳಿದರು.",
			"6": "<sup>6</sup> ಆಗ ಸಮುವೇಲನು, “ನಿಮ್ಮ ಪಿತೃಗಳನ್ನು ಐಗುಪ್ತದಿಂದ ಕರೆತರುವುದಕ್ಕಾಗಿ ಮೋಶೆ ಹಾಗೂ ಆರೋನರನ್ನು ನೇಮಿಸಿದ ಯೆಹೋವನೇ ಇದಕ್ಕೆ ಸಾಕ್ಷಿ.",
			"7": "<sup>7</sup> ಇಲ್ಲಿ ನಿಂತುಕೊಂಡು ಕೇಳಿರಿ; ಯೆಹೋವನು ನಿಮಗೋಸ್ಕರವೂ, ನಿಮ್ಮ ಪಿತೃಗಳಿಗೋಸ್ಕರವೂ ನಡೆಸಿದ ನೀತಿಕಾರ್ಯಗಳನ್ನು ಕುರಿತು ಆತನ ಎದುರಿನಲ್ಲಿ ನಿಮ್ಮ ನೆನಪಿಗೆ ತಂದು, ನಿಮ್ಮನ್ನು ಎಚ್ಚರಿಸುತ್ತೇನೆ.",
			"8": "<sup>8</sup> ಐಗುಪ್ತಕ್ಕೆ ಬಂದ ಯಾಕೋಬನ ವಂಶದವರಾದ ನಿಮ್ಮ ಹಿರಿಯರು ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಟ್ಟಾಗ, ಆತನು ಮೋಶೆ ಆರೋನರ ಮುಖಾಂತರ ಅವರನ್ನು ಐಗುಪ್ತದಿಂದ ಬಿಡಿಸಿ ಈ ದೇಶದಲ್ಲಿ ನೆಲೆಗೊಳಿಸಿದನು.",
			"9": "<sup>9</sup> ಅವರು ತಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಮರೆತುಬಿಡಲು ಆತನು ಅವರನ್ನು ಹಾಚೋರಿನ ಸೇನಾಧಿಪತಿಯಾದ ಸೀಸೆರನಿಗೂ, ಫಿಲಿಷ್ಟಿಯರಿಗೂ, ಮೋವಾಬ್‌ ರಾಜನಿಗೂ ಮಾರಿಬಿಟ್ಟನು. ಇವರು ಬಂದು ಅವರೊಡನೆ ಯುದ್ಧಮಾಡಿದರು.",
			"10": "<sup>10</sup> ಆಗ ಅವರು ಯೆಹೋವನಿಗೆ, ‘ನಾವು ಯೆಹೋವನಾದ ನಿನ್ನನ್ನು ಬಿಟ್ಟು ಬಾಳ್, ಅಷ್ಟೋರೆತ್ ಎಂಬ ದೇವತೆಗಳನ್ನು ಪೂಜಿಸಿ ಪಾಪಮಾಡಿದ್ದೇವೆ. ಈಗ ಕೃಪೆಮಾಡಿ ನಮ್ಮನ್ನು ಶತ್ರುಗಳ ಕೈಯಿಂದ ಬಿಡಿಸು; ನಾವು ಇನ್ನು ಮುಂದೆ ನಿನ್ನನ್ನೇ ಸೇವಿಸುವೆವು’ ಎಂದು ಮೊರೆಯಿಟ್ಟರು.",
			"11": "<sup>11</sup> ಯೆಹೋವನು ಯೆರುಬ್ಬಾಳ್, ಬಾರಾಕ್, ಯೆಫ್ತಾಹ, ಸಮುವೇಲ್ ಇವರನ್ನು ಕಳುಹಿಸಿ ನಿಮ್ಮನ್ನು ಎಲ್ಲಾ ಶತ್ರುಗಳ ಕೈಯಿಂದ ಬಿಡಿಸಿ ಸುರಕ್ಷಿತವಾಗಿ ಜೀವಿಸುವಂತೆ ಮಾಡಿದನು.",
			"12": "<sup>12</sup> ಅಮ್ಮೋನಿಯರ ಅರಸನಾದ ನಾಹಾಷನು ನಿಮ್ಮ ಮೇಲೆ ಯುದ್ಧಕ್ಕೆ ಬರುತ್ತಾನೆಂದು ನಿಮಗೆ ಗೊತ್ತಾಗಲು, ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮ ಅರಸನಾಗಿದ್ದರೂ ನೀವು, ‘ನಮಗೊಬ್ಬ ಅರಸನನ್ನು ನೇಮಿಸು’ ಎಂದು ನನ್ನನ್ನು ಬೇಡಿಕೊಂಡಿರಿ.",
			"13": "<sup>13</sup> ಇಗೋ, ನೀವು ಅಪೇಕ್ಷಿಸಿ ಆರಿಸಿಕೊಂಡ ಅರಸನು ಇವನೇ; ಯೆಹೋವನು ಇವನನ್ನು ನಿಮ್ಮ ಮೇಲೆ ಅರಸನನ್ನಾಗಿ ನೇಮಿಸಿದ್ದಾನೆ.",
			"14": "<sup>14</sup> ನೀವೂ, ನಿಮ್ಮ ಅರಸನೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿದ್ದುಕೊಂಡು, ಆತನಿಗೆ ವಿಧೇಯರಾಗಿ, ಆತನನ್ನು ಸೇವಿಸುತ್ತಾ, ತಪ್ಪದೆ ಆತನ ಮಾತನ್ನು ಪಾಲಿಸುವವರಾಗಿ, ಆತನನ್ನೇ ಹೊಂದಿಕೊಂಡಿರುವುದಾದರೆ ಎಷ್ಟೋ ಒಳ್ಳೆಯದು.",
			"15": "<sup>15</sup> ನೀವು ಯೆಹೋವನ ಮಾತನ್ನು ಕೇಳದೆಯೂ, ಆತನ ಆಜ್ಞೆಗಳನ್ನು ಕೈಕೊಳ್ಳದೆಯೂ ಹೋದರೆ, ಆತನ ಹಸ್ತವು ನಿಮ್ಮ ಹಿರಿಯರಿಗೆ ವಿರೋಧವಾಗಿದ್ದಂತೆ ನಿಮಗೂ ವಿರೋಧವಾಗಿಯೇ ಇರುವುದು.",
			"16": "<sup>16</sup> ಈಗ ಯೆಹೋವನು ನಿಮ್ಮ ಕಣ್ಣುಮುಂದೆ ಮಾಡುವ ಮಹತ್ಕಾರ್ಯವನ್ನು ಹತ್ತಿರ ಬಂದು ನೋಡಿರಿ.",
			"17": "<sup>17</sup> ನಾನು ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಡುವೆನು; ಈಗ ಗೋದಿಯ ಸುಗ್ಗಿಯಿದ್ದರೂ ಆತನು ಗುಡುಗನ್ನೂ, ಮಳೆಯನ್ನೂ ಕಳುಹಿಸುವುದರ ಮೂಲಕ ನೀವು ಅರಸನನ್ನು ಕೇಳಿಕೊಂಡದ್ದು ತನ್ನ ದೃಷ್ಟಿಯಲ್ಲಿ ತಪ್ಪು ಎಂಬುದನ್ನು ತೋರಿಸಿಕೊಡುವನು” ಎಂದು ಅವರಿಗೆ ಹೇಳಿ ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಟ್ಟನು.",
			"18": "<sup>18</sup> ಆಗ ಯೆಹೋವನು ಗುಡುಗು ಮಳೆಗಳನ್ನು ಕಳುಹಿಸಿದನು. ಎಲ್ಲಾ ಜನರು ಯೆಹೋವನಿಗೂ ಸಮುವೇಲನಿಗೂ ಬಹಳವಾಗಿ ಭಯಪಟ್ಟು",
			"19": "<sup>19</sup> ಸಮುವೇಲನನ್ನು, “ನಮಗೊಬ್ಬ ಅರಸನು ಬೇಕೆಂದು ನಾವು ಬೇಡಿಕೊಂಡದ್ದರಿಂದ ನಮ್ಮ ಪಾಪಗಳಿಗೆ ಮತ್ತೊಂದು ಪಾಪ ಸೇರಿಕೊಂಡಿತು; ಆದ್ದರಿಂದ ನಿನ್ನ ಸೇವಕರಾದ ನಾವು ಸಾಯದಂತೆ ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಬೇಡಿಕೋ” ಎಂದು ವಿಜ್ಞಾಪಿಸಲು",
			"20": "<sup>20</sup> ಅವನು, “ಭಯಪಡಬೇಡಿರಿ; ಇಷ್ಟು ಪಾಪಮಾಡಿದ ನೀವು ಇನ್ನು ಮುಂದೆಯಾದರೂ ಅದನ್ನು ಬಿಟ್ಟು, ಯೆಹೋವನನ್ನು ಅಂಟಿಕೊಂಡು, ಪೂರ್ಣಮನಸ್ಸಿನಿಂದ ಆತನೊಬ್ಬನನ್ನೇ ಸೇವಿಸಿರಿ.",
			"21": "<sup>21</sup> ದೇವರಿಗೆ ಇಷ್ಟವಿಲ್ಲದ ವಿಷಯಗಳನ್ನು ಹಿಂಬಾಲಿಸಬೇಡಿರಿ; ಅವುಗಳಿಂದ ನಿಮಗೆ ಲಾಭವೂ, ರಕ್ಷಣೆಯೂ ಸಿಕ್ಕುವುದಿಲ್ಲ. ಅವು ವ್ಯರ್ಥವಾದವುಗಳೇ.",
			"22": "<sup>22</sup> ಯೆಹೋವನು ದಯದಿಂದ ನಿಮ್ಮನ್ನು ಸ್ವಕೀಯಜನವನ್ನಾಗಿ ಆರಿಸಿಕೊಂಡ ಮೇಲೆ ಆತನು ತನ್ನ ಮಹೋನ್ನತ ನಾಮದ ನಿಮಿತ್ತ ನಿಮ್ಮನ್ನು ಕೈಬಿಡುವುದೇ ಇಲ್ಲ.",
			"23": "<sup>23</sup> ನಾನಾದರೋ ನಿಮಗೋಸ್ಕರವಾಗಿ ಯೆಹೋವನನ್ನು ಪ್ರಾರ್ಥಿಸುತ್ತಾ, ಆತನ ಉತ್ತಮ ನೀತಿಮಾರ್ಗವನ್ನು ನಿಮಗೆ ತೋರಿಸಿಕೊಡುವುದನ್ನು ಬಿಡುವುದೇ ಇಲ್ಲ; ಬಿಟ್ಟರೆ ಆತನ ದೃಷ್ಟಿಯಲ್ಲಿ ಪಾಪಿಯಾಗಿರುವೆನು.",
			"24": "<sup>24</sup> ನೀವಾದರೋ ಯೆಹೋವನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿದ್ದು, ಆತನು ನಿಮಗೋಸ್ಕರವಾಗಿ ಮಾಡಿದ ಮಹತ್ಕಾರ್ಯಗಳನ್ನು ಸ್ಮರಿಸಿಕೊಂಡು, ಆತನನ್ನು ಸತ್ಯದಿಂದಲೂ, ಪೂರ್ಣಮನಸ್ಸಿನಿಂದಲೂ ಸೇವಿಸುತ್ತಾ ಬರಬೇಕು;",
			"25": "<sup>25</sup> ನೀವು ದ್ರೋಹಿಗಳಾಗಿಯೇ ಇದ್ದರೆ ನೀವು ಮತ್ತು ನಿಮ್ಮ ಅರಸನು ನಾಶವಾಗುವಿರಿ” ಎಂದು ಹೇಳಿದನು."
		},
		"13": {
			"1": "<sup>1</sup> ಸೌಲನು ಅರಸನಾದಾಗ ಮೂವತ್ತು ವರ್ಷದವನಾಗಿದ್ದನು. ಅವನು ಇಸ್ರಾಯೇಲರನ್ನು ಎರಡು ವರ್ಷಗಳ ಕಾಲ ಆಳಿದನು.",
			"2": "<sup>2</sup> ಅವನು ಇಸ್ರಾಯೇಲರಿಂದ ಮೂರು ಸಾವಿರ ಮಂದಿ ಸೈನಿಕರನ್ನು ಆರಿಸಿಕೊಂಡನು, ಅವರಲ್ಲಿ ಎರಡು ಸಾವಿರ ಜನರು ಅವನೊಂದಿಗೆ ಮಿಕ್ಮಾಷಿನಲ್ಲಿಯೂ, ಬೇತೇಲಿನ ಗುಡ್ಡದಲ್ಲಿಯೂ ಇದ್ದರು. ಒಂದು ಸಾವಿರ ಜನರನ್ನು ಬೆನ್ಯಾಮೀನ್ಯರ ಗಿಬೆಯದಲ್ಲಿದ್ದ ಯೋನಾತಾನನ ವಶಕ್ಕೆ ಕೊಟ್ಟನು. ಉಳಿದ ಇಸ್ರಾಯೇಲರನ್ನು ಅವರವರ ಮನೆಗಳಿಗೆ ಕಳುಹಿಸಿದನು.",
			"3": "<sup>3</sup> ಯೋನಾತಾನನು ಗೆಬದಲ್ಲಿದ್ದ ಫಿಲಿಷ್ಟಿಯರ ಕಾವಲುಪಡೆಯ ಪಾಳೆಯವನ್ನು ನಾಶಮಾಡಿದನು. ಈ ವರ್ತಮಾನವು ಫಿಲಿಷ್ಟಿಯರಿಗೆ ಮುಟ್ಟಿತು. ಸೌಲನು “ಎಲ್ಲಾ ಇಬ್ರಿಯರು ಕೇಳಲಿ” ಎಂದು ದೇಶದಲ್ಲೆಲ್ಲಾ ತುತ್ತೂರಿ ಊದಿಸಿದನು.",
			"4": "<sup>4</sup> ಸೌಲನು ಫಿಲಿಷ್ಟಿಯರ ಕಾವಲುಪಡೆಯ ಪಾಳೆಯವನ್ನು ನಾಶಮಾಡಿದ್ದರಿಂದ ತಾವು ಅವರ ದ್ವೇಷಕ್ಕೆ ಗುರಿಯಾದೆವೆಂದು ಇಸ್ರಾಯೇಲರು ತಿಳಿದು ಗಿಲ್ಗಾಲಿಗೆ ಬಂದು ಸೌಲನನ್ನು ಕೂಡಿಕೊಂಡರು.",
			"5": "<sup>5</sup> ಆಗ ಫಿಲಿಷ್ಟಿಯರು ಇಸ್ರಾಯೇಲರೊಡನೆ ಯುದ್ಧಮಾಡುವುದಕ್ಕೋಸ್ಕರ ಮೂವತ್ತು ಸಾವಿರ ರಥಬಲವನ್ನೂ, ಆರು ಸಾವಿರ ಅಶ್ವಬಲವನ್ನೂ, ಸಮುದ್ರದ ಮರಳಿನಷ್ಟು ಅಸಂಖ್ಯವಾದ ಕಾಲಾಳುಗಳನ್ನೂ ತೆಗೆದುಕೊಂಡು ಬಂದು ಬೇತಾವೆನಿನ ಪೂರ್ವದಿಕ್ಕಿನಲ್ಲಿರುವ ಮಿಕ್ಮಾಷಿನಲ್ಲಿ ಪಾಳೆಯಮಾಡಿಕೊಂಡರು.",
			"6": "<sup>6</sup> ಇಸ್ರಾಯೇಲರಿಗೆ ಕೇಡು ಬಂದಿತು. ತಾವು ಇಕ್ಕಟ್ಟಿನಲ್ಲಿದ್ದೇವೆಂದು ಅವರು ತಿಳಿದು ಗವಿ, ಕಾಡು, ಬಂಡೆ, ನೆಲಮನೆ, ಗುಂಡಿ ಇವುಗಳಲ್ಲಿ ಅಡಗಿಕೊಂಡರು.",
			"7": "<sup>7</sup> ಕೆಲವರು ಯೊರ್ದನ್ ಹೊಳೆದಾಟಿ ಗಾದ್ಯರ ದೇಶಕ್ಕೂ, ಗಿಲ್ಯಾದ್ ಪ್ರಾಂತ್ಯಕ್ಕೂ ಹೊರಟುಹೋದರು. ಸೌಲನು ಇನ್ನೂ ಗಿಲ್ಗಾಲಿನಲ್ಲೇ ಇದ್ದನು. ಅವನ ಸಂಗಡ ಇದ್ದವರು ಭಯದಿಂದ ನಡುಗುತ್ತ ಅವನನ್ನು ಹಿಂಬಾಲಿಸಿದರು.",
			"8": "<sup>8</sup> ಸೌಲನು ಸಮುವೇಲನ ಆಜ್ಞೆಯಂತೆ ಗಿಲ್ಗಾಲಿನಲ್ಲೇ ಏಳು ದಿನ ಇದ್ದರೂ ಸಮುವೇಲನು ಬರಲಿಲ್ಲ; ಅಷ್ಟರಲ್ಲಿ ಜನರು ಚದರಿಹೋದರು.",
			"9": "<sup>9</sup> ಆಗ ಸೌಲನು ಜನರಿಂದ ಸರ್ವಾಂಗಹೋಮಕ್ಕೂ, ಸಮಾಧಾನ ಯಜ್ಞಕ್ಕೂ ಬೇಕಾದದ್ದನ್ನು ತರಿಸಿ ಸರ್ವಾಂಗಹೋಮವನ್ನು ಅರ್ಪಿಸಿದನು.",
			"10": "<sup>10</sup> ಅದು ಮುಗಿಯುವಷ್ಟರಲ್ಲೇ ಸಮುವೇಲನು ಬಂದನು. ಸೌಲನು ಅವನನ್ನು ವಂದಿಸುವುದಕ್ಕೋಸ್ಕರ ಎದುರಿಗೆ ಹೋಗಲು",
			"11": "<sup>11</sup> ಸಮುವೇಲನು ಅವನನ್ನು, “ಇದೇನು ನೀನು ಮಾಡಿದ್ದು?” ಎಂದು ಕೇಳಲು ಅವನು, “ಜನರು ಚದರಿಹೋಗುವುದನ್ನೂ, ನೀನು ನಿಯಮಿತಕಾಲದಲ್ಲಿ ಬಾರದಿರುವುದನ್ನೂ, ಫಿಲಿಷ್ಟಿಯರು ಮಿಕ್ಮಾಷಿನಲ್ಲಿ ನಮಗೆ ವಿರೋಧವಾಗಿ ಕೂಡಿಕೊಂಡಿರುವುದನ್ನೂ ನೋಡಿದೆನು.",
			"12": "<sup>12</sup> ಆಗ ನಾನು ‘ಫಿಲಿಷ್ಟಿಯರು ಗಿಲ್ಗಾಲಿನಲ್ಲಿರುವ ನನ್ನ ಬಳಿಗೆ ಬಂದು ನನ್ನ ಮೇಲೆ ಬಿದ್ದಾರು, ನಾನು ಯೆಹೋವನ ದಯೆಯನ್ನು ಬೇಡಿಕೊಳ್ಳಲಿಲ್ಲ’ ಎಂದು, ಭಯಪಟ್ಟು ಸರ್ವಾಂಗಹೋಮವನ್ನು ಸಮರ್ಪಿಸುವುದಕ್ಕೆ ಮುಂದಾದೆನು” ಅಂದನು.",
			"13": "<sup>13</sup> ಆಗ ಸಮುವೇಲನು, “ನೀನು ಬುದ್ಧಿಹೀನಕಾರ್ಯವನ್ನು ಮಾಡಿದೆ; ನೀನು ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನ ಆಜ್ಞೆಯನ್ನು ಕೈಕೊಳ್ಳಲಿಲ್ಲ. ಕೈಕೊಂಡಿದ್ದರೆ ಆತನು ನಿನ್ನ ರಾಜ್ಯವನ್ನು ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಶಾಶ್ವತವಾಗಿ ಸ್ಥಿರಪಡಿಸುತ್ತಿದ್ದನು.",
			"14": "<sup>14</sup> ಈಗಲಾದರೋ ನಿನ್ನ ಅರಸುತನವು ನಿಲ್ಲುವುದಿಲ್ಲ; ನೀನು ಯೆಹೋವನ ಆಜ್ಞೆಯನ್ನು ಕೈಕೊಳ್ಳದೆ ಹೋದುದರಿಂದ ಆತನು ತನಗೆ ಒಪ್ಪುವ ಬೇರೊಬ್ಬ ಪುರುಷನನ್ನು ತನ್ನ ಪ್ರಜೆಯ ಮೇಲೆ ಪ್ರಭುವಾಗಿ ನೇಮಿಸಿದ್ದಾನೆ” ಎಂದು ಹೇಳಿದನು.",
			"15": "<sup>15</sup> ಅನಂತರ ಸಮುವೇಲನು ಗಿಲ್ಗಾಲನ್ನು ಬಿಟ್ಟು ಬೆನ್ಯಾಮೀನ್ಯರ ಗಿಬೆಯಕ್ಕೆ ಹೋದನು.",
			"16": "<sup>16</sup> ಸೌಲನು ತನ್ನ ಜೊತೆಯಲ್ಲಿದ್ದ ಸೈನಿಕರನ್ನು ಲೆಕ್ಕಿಸಿದಾಗ ಆರು ನೂರು ಪುರುಷರಿದ್ದರು. ಅವರೊಡನೆ ಅವನೂ ಅವನ ಮಗನಾದ ಯೋನಾತಾನನೂ ಬೆನ್ಯಾಮೀನ್ಯರ ಗೆಬದಲ್ಲಿ ಇಳಿದುಕೊಂಡರು. ಫಿಲಿಷ್ಟಿಯರು ಮಿಕ್ಮಾಷಿನಲ್ಲಿ ಪಾಳೆಯಮಾಡಿಕೊಂಡಿದ್ದರು.",
			"17": "<sup>17</sup> ಸುಲಿಗೆ ಮಾಡುವುದಕ್ಕೋಸ್ಕರ ಫಿಲಿಷ್ಟಿಯರ ಪಾಳೆಯದಿಂದ ಮೂರು ಗುಂಪುಗಳು ಹೊರಟು, ಒಂದು ಗುಂಪು ಒಫ್ರದ ಮಾರ್ಗವಾಗಿ ಶುವಲ್‌ ನಾಡಿಗೂ,",
			"18": "<sup>18</sup> ಇನ್ನೊಂದು ಗುಂಪು ಬೇತ್‌ಹೋರೋನಿನ ಕಡೆಗೂ, ಮತ್ತೊಂದು ಜೆಬೋಯೀಮಿನ ಕಣಿವೆಯ ಕೆಳಗಿರುವ ಅರಣ್ಯವನ್ನು ನೋಡಬಹುದಾದ ಸ್ಥಳಕ್ಕೂ ಹೋದವು.",
			"19": "<sup>19</sup> ಇಬ್ರಿಯರು ಈಟಿಕತ್ತಿಗಳನ್ನು ಮಾಡಿಕೊಳ್ಳಬಾರದೆಂದು ಫಿಲಿಷ್ಟಿಯರ ಆಜ್ಞೆಯಿದ್ದದರಿಂದ ಇಸ್ರಾಯೇಲ್‌ ದೇಶದಲ್ಲಿ ಅವುಗಳನ್ನು ಮಾಡುವ ಕಮ್ಮಾರರೇ ಇರಲಿಲ್ಲ.",
			"20": "<sup>20</sup> (ಅವರು ತಮ್ಮ ನೇಗಿಲಿನ ಗುಳ, ಸಲಿಕೆ, ಕೊಡಲಿ, ಗುದ್ದಲಿ ಇವುಗಳನ್ನು ಹರಿತ ಮಾಡಿಸುವುದಕ್ಕೆ ಫಿಲಿಷ್ಟಿಯರ ಹತ್ತಿರವೇ ಹೋಗಬೇಕಿತ್ತು.",
			"21": "<sup>21</sup> ಗುದ್ದಲಿ, ಸಲಿಕೆ, ತ್ರಿಶೂಲ, ಕೊಡಲಿ, ಮುಳ್ಳುಗೋಲು ಇವುಗಳನ್ನು ಹದಮಾಡುವುದಕ್ಕೋಸ್ಕರ ಅವರಲ್ಲಿ ಹರಿತ ಮಾಡುವ ಕಲ್ಲು (ಅರ) ಮಾತ್ರ ಇತ್ತು.",
			"22": "<sup>22</sup> ಹೀಗಿರುವುದರಿಂದ ಯುದ್ಧ ಪ್ರಾರಂಭಿಸಿದಾಗ ಸೌಲ ಮತ್ತು ಯೋನಾತಾನರ ಹೊರತು ಅವರ ಜೊತೆಯಲ್ಲಿದ್ದ ಜನರಲ್ಲಿ ಒಬ್ಬನ ಕೈಯಲ್ಲಾದರೂ, ಕತ್ತಿಯಾಗಲಿ ಬರ್ಜಿಯಾಗಲಿ ಇರಲಿಲ್ಲ.",
			"23": "<sup>23</sup> ಫಿಲಿಷ್ಟಿಯರ ಕಾವಲುದಂಡು ಮಿಕ್ಮಾಷಿನ ಕಣಿವೆಯ ಕಡೆಗೆ ಹೊರಟಿತು."
		},
		"14": {
			"1": "<sup>1</sup> ಒಂದು ದಿನ ಸೌಲನ ಮಗನಾದ ಯೋನಾತಾನನು ತನ್ನ ಆಯುಧಗಳನ್ನು ಹೊರುವ ಸೇವಕನಿಗೆ, “ಆಚೆ ಇರುವ ಫಿಲಿಷ್ಟಿಯರ ಕಾವಲುದಂಡಿಗೆ ವಿರೋಧವಾಗಿ ಹೋಗೋಣ ಬಾ” ಎಂದು ಹೇಳಿದನು. ಆದರೆ ಈ ಸಂಗತಿಯನ್ನು ತನ್ನ ತಂದೆಗೆ ತಿಳಿಸಲಿಲ್ಲ.",
			"2": "<sup>2</sup> ಸೌಲನಾದರೋ ಗಿಬೆಯ ಪ್ರಾಂತ್ಯದ ಅಂತ್ಯಭಾಗವಾಗಿರುವ ಮಿಗ್ರೋನಿನಲ್ಲಿನ ಒಂದು ದಾಳಿಂಬೆ ವೃಕ್ಷದ ಅಡಿಯಲ್ಲಿ ತಂಗಿದ್ದನು. ಅವನ ಜೊತೆಯಲ್ಲಿ ಸುಮಾರು ಆರು ನೂರು ಮಂದಿ ಸೈನಿಕರೂ,",
			"3": "<sup>3</sup> ಏಫೋದನ್ನು ಧರಿಸಿಕೊಂಡಿದ್ದ ಅಹೀಯನೂ ಇದ್ದರು. ಆ ಅಹೀಯನು ಶೀಲೋವಿನಲ್ಲಿ ಯೆಹೋವನ ಯಾಜಕನಾಗಿದ್ದ ಏಲಿಯ ಮರಿಮಗನೂ, ಫೀನೆಹಾಸನ ಮೊಮ್ಮಗನೂ, ಈಕಾಬೋದನ ಅಣ್ಣನಾದ ಅಹೀಟೂಬನ ಮಗನೂ ಆಗಿದ್ದನು. ಯೋನಾತಾನನು ಹೋದದ್ದು ಜನರಿಗೂ ಗೊತ್ತಿರಲಿಲ್ಲ.",
			"4": "<sup>4</sup> ಅವನು ಫಿಲಿಷ್ಟಿಯರ ಕಾವಲುದಂಡಿಗೆ ವಿರೋಧವಾಗಿ ಹೊರಟಾಗ, ಕಣಿವೆಯ ಮಾರ್ಗದ ಎರಡು ಕಡೆಗಳಲ್ಲಿ ಬೋಚೇಚ್, ಸೆನೆ ಎಂಬ ಎರಡು ಕಡಿದಾದ ಬಂಡೆಗಳು ಇದ್ದವು.",
			"5": "<sup>5</sup> ಅದರಲ್ಲಿ ಒಂದು ಬಂಡೆ ಉತ್ತರದಿಕ್ಕಿಗೆ ಎತ್ತರವಾಗಿ ಮಿಕ್ಮಾಷಿಗೂ, ಮತ್ತೊಂದು ದಕ್ಷಿಣದಿಕ್ಕಿನ ಗೆಬಕ್ಕೆ ಎದುರಾಗಿದ್ದವು.",
			"6": "<sup>6</sup> ಯೋನಾತಾನನು ತನ್ನ ಆಯುಧಗಳನ್ನು ಹೊರುವವನಿಗೆ, “ಸುನ್ನತಿಯಿಲ್ಲದ ಈ ಕಾವಲುದಂಡಿನವರಿಗೆ ವಿರೋಧವಾಗಿ ಹೋಗೋಣ ಬಾ. ಒಂದು ವೇಳೆ ಯೆಹೋವನು ತಾನೇ ನಮಗೋಸ್ಕರ ಕಾರ್ಯನಡಿಸುವನು. ಬಹು ಜನರಿದ್ದರೂ, ಸ್ವಲ್ಪ ಜನರಿದ್ದರೂ ರಕ್ಷಿಸುವುದು ಯೆಹೋವನಿಗೆ ಅಸಾಧ್ಯವಲ್ಲ” ಎಂದು ಹೇಳಿದನು.",
			"7": "<sup>7</sup> ಆಯುಧಗಳನ್ನು ಹೊರುವ ಸೇವಕನು, “ನಿನ್ನ ಮನಸ್ಸಿನಂತೆ ಮಾಡು, ನಿನ್ನ ಕೋರಿಕೆಯ ಹಾಗೆ ನಾನೂ ನಿನ್ನನ್ನು ಹಿಂಬಾಲಿಸುತ್ತೇನೆ” ಅಂದನು.",
			"8": "<sup>8</sup> ಆಗ ಯೋನಾತಾನನು ಅವನಿಗೆ, “ಅವರು ನಮ್ಮನ್ನು ಕಾಣುವಂತೆ ಸಮೀಪಕ್ಕೆ ಹೋಗೋಣ.",
			"9": "<sup>9</sup> ‘ನಾವು ನಿಮ್ಮ ಬಳಿಗೆ ಬರುವ ತನಕ ಅಲ್ಲೇ ನಿಲ್ಲಿರಿ’ ಎಂದು ಅವರು ನಮಗೆ ಹೇಳಿದರೆ ಮೇಲೆ ಹತ್ತದೆ ಇದ್ದ ಸ್ಥಳದಲ್ಲೇ ಇರೋಣ.",
			"10": "<sup>10</sup> ಆದರೆ ಅವರು, ‘ಮೇಲೆ ಹತ್ತಿ ನಮ್ಮ ಬಳಿಗೆ ಬನ್ನಿರಿ’ ಎಂದು ನಮ್ಮನ್ನು ಕರೆದರೆ ಯೆಹೋವನು ಅವರನ್ನು ನಮ್ಮ ಕೈಗೆ ಒಪ್ಪಿಸಿದ್ದಾನೆ ಎಂಬುದಕ್ಕೆ ಇದೇ ಗುರುತು ಎಂದು ತಿಳಿದು ಮೇಲೆ ಹೋಗೋಣ” ಎಂದನು.",
			"11": "<sup>11</sup> ಇವರಿಬ್ಬರೂ ಫಿಲಿಷ್ಟಿಯರ ಕಾವಲು ದಂಡಿನವರಿಗೆ ಕಾಣಿಸಿಕೊಂಡಾಗ ಅವರು, “ಇಗೋ, ಇಬ್ರಿಯರು ತಾವು ಅಡಗಿಕೊಂಡಿದ್ದ ಗುಹೆಗಳಿಂದ ಹೊರಗೆ ಬರುತ್ತಲಿದ್ದಾರೆ” ಎಂದು ತಮ್ಮೊಳಗೆ ಮಾತನಾಡಿಕೊಂಡರು.",
			"12": "<sup>12</sup> ಅವರು ಯೋನಾತಾನನಿಗೂ ಅವನ ಆಯುಧಗಳನ್ನು ಹೊರುವವನಿಗೂ, “ಹತ್ತಿ ನಮ್ಮ ಬಳಿಗೆ ಬನ್ನಿರಿ; ನಿಮಗೆ ತೋರಿಸಿಬೇಕಾದ ಕಾರ್ಯ ಬಂದಿದೆ” ಎಂದರು. ಯೋನಾತಾನನು ತನ್ನ ಆಯುಧಗಳನ್ನು ಹೊರುವ ಸೇವಕನಿಗೆ, “ನೀನೂ ನನ್ನ ಸಂಗಡ ಹತ್ತಿ ಬಾ; ಯೆಹೋವನು ಅವರನ್ನು ಇಸ್ರಾಯೇಲ್ಯರ ಕೈಗೆ ಒಪ್ಪಿಸಿದ್ದಾನೆ” ಎಂದು ಹೇಳಿದನು.",
			"13": "<sup>13</sup> ಯೋನಾತಾನನೂ ಅವನ ಆಯುಧಗಳನ್ನು ಹೊರುವ ಸೇವಕನೂ ತಮ್ಮ ಕೈ ಮತ್ತು ಮೊಣಕಾಲುಗಳ ಸಹಾಯದಿಂದ ಗಟ್ಟಾ ಹತ್ತಿದರು; ಯೋನಾತಾನನು ಫಿಲಿಷ್ಟಿಯರನ್ನು ನೆಲಕ್ಕುರುಳಿಸುತ್ತಾ ಹೋದನು. ಅವನ ಸೇವಕನು ಹಿಂದಿನಿಂದ ಅವರನ್ನು ಕೊಲ್ಲುತ್ತಾ ಹೋದನು.",
			"14": "<sup>14</sup> ಯೋನಾತಾನನು ಅವನ ಸೇವಕನು ಮಾಡಿದ ಮೊದಲ ದಾಳಿಯಲ್ಲಿ ಸುಮಾರು ಇಪ್ಪತ್ತು ಜನರು ಅರ್ಧ ಎಕರೆ ಭೂಮಿಯಲ್ಲಿ ಸತ್ತುಹೋದರು.",
			"15": "<sup>15</sup> ಆಗ ಪಾಳೆಯದಲ್ಲಿದ್ದವರೂ, ಕಾವಲುಗಾರರಾಗಿ ಠಾಣದಲ್ಲಿದ್ದವರೂ, ಸುಲಿಗೆಗೋಸ್ಕರ ಹೊರಗೆ ಹೋಗಿದ್ದವರೂ, ಉಳಿದ ಎಲ್ಲಾ ಜನರೂ ಭಯದಿಂದ ಕಳವಳಗೊಂಡರು. ಇದಲ್ಲದೆ ದೇವರು ಭೂಕಂಪವನ್ನು ಉಂಟುಮಾಡಿದ್ದರಿಂದ ಜನರಲ್ಲಿ ಮಹಾಭೀತಿಯುಂಟಾಯಿತು.",
			"16": "<sup>16</sup> ಬೆನ್ಯಾಮೀನ್ಯರ ಗಿಬೆಯದಲ್ಲಿ ಕಾವಲಿದ್ದ ಸೌಲನ ಸೈನಿಕರು ಫಿಲಿಷ್ಟಿಯರ ದಂಡು ಕಳವಳಗೊಂಡು ಚದರಿಹೋಗುತ್ತಿರುವುದನ್ನು ಕಂಡರು.",
			"17": "<sup>17</sup> ಸೌಲನು ತನ್ನ ಜೊತೆಯಲ್ಲಿದ್ದವರಿಗೆ, “ನಮ್ಮನ್ನು ಬಿಟ್ಟುಹೋದವರು ಯಾರೆಂಬುದನ್ನು ಲೆಕ್ಕಮಾಡಿ ನೋಡಿರಿ” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು. ಅವರು ಲೆಕ್ಕಮಾಡಿ ನೋಡಿದಾಗ ಯೋನಾತಾನನೂ, ಅವನ ಆಯುಧಗಳನ್ನು ಹೊರುವ ಸೇವಕನೂ ಇಲ್ಲವೆಂದು ತಿಳಿದುಬಂದಿತು.",
			"18": "<sup>18</sup> ಅನಂತರ ಸೌಲನು ಅಹೀಯನಿಗೆ, “ಏಫೋದನ್ನು ತೆಗೆದುಕೊಂಡು ಬಾ” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು. ಆ ಕಾಲದಲ್ಲಿ ಇವನು ಇಸ್ರಾಯೇಲರೊಳಗೆ ಏಫೋದನ್ನು ಧರಿಸುತ್ತಿದ್ದನಷ್ಟೆ.",
			"19": "<sup>19</sup> ಸೌಲನು ಯಾಜಕನೊಡನೆ ಮಾತನಾಡುತ್ತಿರುವಷ್ಟರಲ್ಲಿ ಫಿಲಿಷ್ಟಿಯರ ಸೈನ್ಯದ ಗದ್ದಲವು ಬಲು ಹೆಚ್ಚಾಯಿತು. ಆಗ ಸೌಲನು ಯಾಜಕನಿಗೆ, “ನಿನ್ನ ಕೈಯನ್ನು ತೆಗೆ” ಎಂದು ಹೇಳಿ",
			"20": "<sup>20</sup> ತನ್ನ ಜೊತೆಯಲ್ಲಿದ್ದವರನ್ನು ಕೂಡಿಕೊಂಡು ಯುದ್ಧಕ್ಕೆ ಹೋದನು. ಅಷ್ಟರಲ್ಲಿ ಫಿಲಿಷ್ಟಿಯರ ಸೈನ್ಯದಲ್ಲಿ ಗಲಿಬಿಲಿಯುಂಟಾಗಿ ಒಬ್ಬನನ್ನೊಬ್ಬನು ಕೊಂದುಹಾಕುತ್ತಿದ್ದರು.",
			"21": "<sup>21</sup> ಇದಲ್ಲದೆ ಮೊದಲಿನಿಂದಲೇ ಫಿಲಿಷ್ಟಿಯರನ್ನು ಸೇರಿಕೊಂಡು ಅವರೊಡನೆ ಪಾಳೆಯದಲ್ಲಿದ್ದ ಇಬ್ರಿಯರು, ಸೌಲ ಮತ್ತು ಯೋನಾತಾನರ ಜೊತೆಯಲ್ಲಿ ಬಂದ ಇಸ್ರಾಯೇಲರನ್ನು ಕೂಡಿಕೊಂಡರು.",
			"22": "<sup>22</sup> ಎಫ್ರಾಯೀಮಿನ ಪರ್ವತ ಪ್ರಾಂತ್ಯದಲ್ಲಿ ಅಡಗಿಕೊಂಡಿದ್ದ ಇಸ್ರಾಯೇಲರಿಗೆ ಫಿಲಿಷ್ಟಿಯರು ಓಡಿಹೋದರೆಂಬ ವರ್ತಮಾನವು ಮುಟ್ಟಿದಾಗ ಅವರೂ ಯುದ್ಧಕ್ಕೆ ಬಂದು ಅವರನ್ನು ಬೆನ್ನಟ್ಟಿದರು.",
			"23": "<sup>23</sup> ಹೀಗೆ ಯೆಹೋವನು ಆ ದಿನದಲ್ಲಿ ಇಸ್ರಾಯೇಲರಿಗೆ ಜಯವನ್ನುಂಟುಮಾಡಿದನು. ಯುದ್ಧವು ಬೇತಾವೆನಿನ ಆಚೆಯವರೆಗೂ ನಡೆಯಿತು.",
			"24": "<sup>24</sup> ಆ ದಿನ ಸೌಲನು ಇಸ್ರಾಯೇಲರನ್ನು ಕುರಿತು, “ಶತ್ರುಗಳಿಗೆ ಮುಯ್ಯಿತೀರಿಸುವುದಕ್ಕೋಸ್ಕರ ನಿಮ್ಮಲ್ಲಿ ಒಬ್ಬನಾದರೂ ಸಾಯಂಕಾಲದವರೆಗೆ ಊಟಮಾಡಬಾರದು; ಮಾಡಿದವನು ಶಾಪಗ್ರಸ್ತನಾಗಲಿ” ಎಂದು ಆಣೆಯಿಟ್ಟಿದ್ದರಿಂದ ಅಂದು ಅವರೆಲ್ಲರೂ ಬಹುವಾಗಿ ಬಳಲಿಹೋಗಿದ್ದರೂ ಒಬ್ಬನಾದರೂ ಆಹಾರ ಪಧಾರ್ಥವನ್ನು ರುಚಿಸಿ ನೋಡಿರಲಿಲ್ಲ.",
			"25": "<sup>25</sup> ಜನರೆಲ್ಲರೂ ನೆಲಜೇನು ಇರುವ ಕಾಡಿಗೆ ಬಂದರು.",
			"26": "<sup>26</sup> ಅವರು ಅಲ್ಲಿ ಜೇನುತುಪ್ಪ ಹರಿಯುವುದನ್ನು ಕಂಡರೂ ಶಾಪಕ್ಕೆ ಭಯಪಟ್ಟದರಿಂದ ಯಾವನೂ ಅದನ್ನು ಬಾಯೊಳಗೆ ಹಾಕಲಿಲ್ಲ.",
			"27": "<sup>27</sup> ಯೋನಾತಾನನಿಗೆ ತನ್ನ ತಂದೆಯು ಜನರಿಗೆ ಆಣೆಯಿಟ್ಟು ಹೇಳಿದ್ದು ಗೊತ್ತಿರಲಿಲ್ಲವಾದ್ದರಿಂದ ಅವನು ತನ್ನ ಕೋಲನ್ನು ಜೇನುಹುಟ್ಟಿನಲ್ಲಿ ಚುಚ್ಚಿ, ಕೈಯಿಂದ ತೆಗೆದು ತಿನ್ನಲು ಅವನ ಕಣ್ಣುಗಳು ಕಳೆಗೊಂಡವು.",
			"28": "<sup>28</sup> ಕೂಡಲೆ ಒಬ್ಬನು ಅವನಿಗೆ, “ಈ ಹೊತ್ತು ಊಟಮಾಡುವಂಥವರು ಶಾಪಗ್ರಸ್ತರಾಗುವರೆಂದು ನಿನ್ನ ತಂದೆಯು ಆಣೆಯಿಟ್ಟು ಹೇಳಿದ್ದಾನೆ” ಎಂದು ತಿಳಿಸಿದನು.",
			"29": "<sup>29</sup> ಜನರು ಬಹಳವಾಗಿ ಬಳಲಿ ಹೋದದ್ದನ್ನು ಕಂಡು ಯೋನಾತಾನನು ಆ ಮನುಷ್ಯನಿಗೆ, “ನನ್ನ ತಂದೆಯು ದೇಶದಲ್ಲಿ ಇಕ್ಕಟ್ಟನ್ನು ಉಂಟುಮಾಡಿದ್ದಾನೆ; ನಾನು ಸ್ವಲ್ಪ ಜೇನುತುಪ್ಪವನ್ನು ತಿಂದದ್ದರಿಂದ ನನ್ನ ಕಣ್ಣುಗಳು ಹೇಗೆ ಕಳೆಗೊಂಡಿರುತ್ತವೆ ನೋಡು.",
			"30": "<sup>30</sup> ಜನರು ತಾವು ಇಂದು ಶತ್ರುಗಳಿಂದ ಸುಲಿದುಕೊಂಡ ಆಹಾರವನ್ನು ಯಥೇಚ್ಛವಾಗಿ ಊಟಮಾಡಿದ್ದರೆ ಅವರು ಇನ್ನಷ್ಟು ಬಲಗೊಳ್ಳುತ್ತಿದ್ದರು. ಫಿಲಿಷ್ಟಿಯರಲ್ಲಿ ಹತರಾಗದವರು ಇನ್ನೂ ಹೆಚ್ಚು ಮಂದಿ ಇದ್ದಾರೆ” ಅಂದನು.",
			"31": "<sup>31</sup> ಆ ದಿನ ಜನರು ಮಿಕ್ಮಾಷಿನಿಂದ ಅಯ್ಯಾಲೋನಿನ ವರೆಗೆ ಫಿಲಿಷ್ಟಿಯರನ್ನು ಹೊಡೆದು ಬಹಳವಾಗಿ ದಣಿದಿದ್ದರು.",
			"32": "<sup>32</sup> ಅವರು ತಾವು ಕೊಳ್ಳೆಮಾಡಿದ ಎತ್ತು, ಕುರಿ, ಕರು ಇವುಗಳನ್ನು ಹಿಡಿದು ನೆಲದ ಮೇಲೆಯೇ ಕೊಂದು ಮಾಂಸವನ್ನು ರಕ್ತದೊಡನೆ ತಿಂದರು.",
			"33": "<sup>33</sup> ಆಗ ಕೆಲವರು ಸೌಲನಿಗೆ, “ನೋಡು, ಜನರು ರಕ್ತದೊಂದಿಗೆ ಊಟಮಾಡಿ ಯೆಹೋವನಿಗೆ ವಿರುದ್ಧವಾಗಿ ಪಾಪ ಮಾಡುತ್ತಾರೆ” ಎಂದು ತಿಳಿಸಿದರು. ಸೌಲನು ಅವರಿಗೆ, “ಇದು ಮಹಾಪಾತಕವೇ ಸರಿ; ನೀವು ಬೇಗನೆ ನನ್ನ ಬಳಿಗೆ ದೊಡ್ಡ ಕಲ್ಲೊಂದನ್ನು ಹೊರಳಿಸಿ ತಂದಿಟ್ಟು;",
			"34": "<sup>34</sup> ಜನರ ಬಳಿಗೆ ಹೋಗಿ ಅವರಿಗೆ, ‘ರಕ್ತದೊಂದಿಗೆ ಭೋಜನ ಮಾಡಿ ಯೆಹೋವನಿಗೆ ವಿರುದ್ಧವಾಗಿ ಪಾಪಮಾಡಬೇಡಿರಿ; ನಿಮ್ಮ ನಿಮ್ಮ ಎತ್ತು ಕುರಿಗಳನ್ನು ಇಲ್ಲಿ ತಂದು, ಕೊಂದು ತಿನ್ನಬೇಕೆಂದು ಹೇಳಿರಿ’” ಅಂದನು. ಹಾಗೆಯೇ ಜನರೆಲ್ಲರೂ ತಮ್ಮ ತಮ್ಮ ಎತ್ತು ಕುರಿಗಳನ್ನು ಅದೇ ರಾತ್ರಿ ಅಲ್ಲಿಗೆ ತಂದು ಕೊಂದು ತಿಂದರು.",
			"35": "<sup>35</sup> ಸೌಲನು ಯೆಹೋವನಿಗೋಸ್ಕರ ಒಂದು ಯಜ್ಞವೇದಿಯನ್ನು ಕಟ್ಟಿಸಿದನು; ಅವನು ಕಟ್ಟಿಸಿದ ಯಜ್ಞವೇದಿಗಳಲ್ಲಿ ಇದೇ ಮೊದಲನೆಯದು.",
			"36": "<sup>36</sup> ಸೌಲನು ತನ್ನ ಸೈನಿಕರನ್ನು, “ಬನ್ನಿರಿ; ನಾವು ಈ ರಾತ್ರಿಯಲ್ಲೇ ಫಿಲಿಷ್ಟಿಯರನ್ನು ಬೆನ್ನಟ್ಟಿ, ಬೆಳಗಾಗುವವರೆಗೆ ಅವರನ್ನು ಸುಲಿದುಕೊಂಡು, ಒಬ್ಬನನ್ನೂ ಉಳಿಸದೆ ಕೊಂದುಹಾಕೋಣ” ಎಂದು ಕರೆದನು. ಅವರು, “ನಿನ್ನ ಇಷ್ಟದಂತೆ ಆಗಲಿ” ಎಂದು ಹೇಳಿದರು. ಆದರೆ ಯಾಜಕನು, “ಇತ್ತ ಬನ್ನಿರಿ; ನಾವು ದೇವರನ್ನು ವಿಚಾರಿಸೋಣ” ಎಂದನು.",
			"37": "<sup>37</sup> ಆಗ ಸೌಲನು, “ದೇವಾ, ನಾವು ಫಿಲಿಷ್ಟಿಯರನ್ನು ಬೆನ್ನಟ್ಟಬಹುದೋ? ನೀನು ಅವರನ್ನು ಇಸ್ರಾಯೇಲ್ಯರ ಕೈಗೆ ಒಪ್ಪಿಸಿಕೊಡುವಿಯೋ?” ಎಂದು ಕೇಳಿಕೊಂಡರೂ ದೇವರು ಅವನಿಗೆ ಆ ದಿನ ಉತ್ತರಕೊಡಲೇ ಇಲ್ಲ.",
			"38": "<sup>38</sup> ಆಗ ಸೌಲನು ಪ್ರಜೆಗಳ ಮುಖಂಡರಿಗೆ, “ನೀವೆಲ್ಲರೂ ಇಲ್ಲಿ ಬಂದು, ಈ ಹೊತ್ತು ಪಾಪ ಯಾವುದರಿಂದ ಉಂಟಾಯಿತು ಎಂದು ವಿಚಾರಿಸಿ ಗೊತ್ತುಮಾಡಿರಿ.",
			"39": "<sup>39</sup> ಇಸ್ರಾಯೇಲ್ಯರ ರಕ್ಷಕನಾದ ಯೆಹೋವನ ಆಣೆ, ಪಾಪಮಾಡಿದವನು ನನ್ನ ಮಗನಾದ ಯೋನಾತಾನನಾಗಿದ್ದರೂ ಸರಿಯೇ, ಅವನು ಸಾಯಲೇಬೇಕು” ಎಂದು ಹೇಳಿದನು; ಜನರು ಏನೂ ಮಾತನಾಡದೆ ಮೌನವಾಗಿದ್ದರು.",
			"40": "<sup>40</sup> ಆಗ ಸೌಲನು ಅವರಿಗೆ, “ನೀವೆಲ್ಲರೂ ಒಂದು ಕಡೆ ನಿಲ್ಲಿರಿ; ನಾನೂ ನನ್ನ ಮಗನಾದ ಯೋನಾತಾನನೂ ಮತ್ತೊಂದು ಕಡೆಯಲ್ಲಿರುವೆವು” ಅನ್ನಲು ಅವರು, “ನಿನ್ನ ಇಷ್ಟದಂತೆ ಆಗಲಿ” ಅಂದರು.",
			"41": "<sup>41</sup> ಅನಂತರ ಸೌಲನು, “ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರೇ, ಸತ್ಯವನ್ನು ತಿಳಿಸು” ಎಂದು ಯೆಹೋವನನ್ನು ಪ್ರಾರ್ಥಿಸಿ ಚೀಟು ಹಾಕಿದಾಗ ಅದು ಅವನಿಗೂ ಯೋನಾತಾನನಿಗೂ ಬಂದಿತು, ಜನರು ಪಾರಾದರು.",
			"42": "<sup>42</sup> ಸೌಲನು ಪುನಃ, “ನಮ್ಮಿಬ್ಬರೊಳಗೆ ಚೀಟು ಹಾಕಿರಿ” ಎಂದು ಹೇಳಿದನು. ಹಾಗೆಯೇ ಮಾಡಿದಾಗ ಚೀಟು ಯೋನಾತಾನನಿಗೆ ಬಿದ್ದಿತು.",
			"43": "<sup>43</sup> ಆಗ ಸೌಲನು ಯೋನಾತಾನನನ್ನು, “ನೀನು ಏನು ಮಾಡಿದಿ, ಹೇಳು” ಎಂದು ಕೇಳಲು ಅವನು, “ನಾನು ಕೋಲಿನಿಂದ ಸ್ವಲ್ಪ ಜೇನುತುಪ್ಪವನ್ನು ತೆಗೆದುಕೊಂಡು ತಿಂದೆನು; ನಾನು ಸಾಯುವುದಕ್ಕೆ ಸಿದ್ಧನಾಗಿದ್ದೇನೆ” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"44": "<sup>44</sup> ಅದಕ್ಕೆ ಸೌಲನು, “ಯೋನಾತಾನನೇ, ನಾನು ನಿನ್ನನ್ನು ಕೊಲ್ಲದೆ ಬಿಟ್ಟರೆ ದೇವರು ನನಗೆ ಬೇಕಾದದ್ದನ್ನು ಮಾಡಲಿ” ಎಂದನು.",
			"45": "<sup>45</sup> ಜನರು ಅವನಿಗೆ, “ದೇವರ ಸಹಾಯದಿಂದ ಈ ಹೊತ್ತು ಇಸ್ರಾಯೇಲರಿಗೆ ಮಹಾ ಜಯವನ್ನುಂಟುಮಾಡಿದ ಯೋನಾತಾನನು ಸಾಯಬೇಕೋ? ಕೂಡದು; ಯೆಹೋವನಾಣೆ, ಅವನ ತಲೆಗೂದಲುಗಳಲ್ಲಿ ಒಂದನ್ನೂ ನೆಲಕ್ಕೆ ಬೀಳಿಸಬಾರದು” ಎಂದು ಹೇಳಿ, ಪ್ರಾಯಶ್ಚಿತ್ತವನ್ನು ಕೊಟ್ಟು, ಅವನನ್ನು ಬಿಡಿಸಿದರು.",
			"46": "<sup>46</sup> ಸೌಲನು ಆ ಮೇಲೆ ಫಿಲಿಷ್ಟಿಯರನ್ನು ಹಿಂದಟ್ಟದೆ ತನ್ನ ಮನೆಗೆ ಹೋದನು. ಫಿಲಿಷ್ಟಿಯರೂ ತಮ್ಮ ಪ್ರಾಂತ್ಯಕ್ಕೆ ಹೋದರು.",
			"47": "<sup>47</sup> ಸೌಲನು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಅರಸನಾದ ಮೇಲೆ ಅವನು ಸುತ್ತಣ ವೈರಿಗಳಾದ ಮೋವಾಬ್ಯರು, ಅಮ್ಮೋನಿಯರು, ಎದೋಮ್ಯರು, ಚೋಬದ ಅರಸರು, ಫಿಲಿಷ್ಟಿಯರು ಇವರೊಡನೆ ಯುದ್ಧಮಾಡಿದನು. ಅವನು ಹೋದ ಕಡೆಯಲ್ಲೆಲ್ಲಾ ಜಯವನ್ನೇ ಹೊಂದಿದನು.",
			"48": "<sup>48</sup> ಇದಲ್ಲದೆ ಅವನು ಅಮಾಲೇಕ್ಯರಿಗೆ ವಿರೋಧವಾಗಿ ದಂಡೆತ್ತಿ ಹೋಗಿ ಪರಾಕ್ರಮದಿಂದ ಅವರನ್ನು ಸದೆಬಡಿದನು. ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಸುಲಿಗೆಮಾಡುವವರ ಕೈಗೆ ಸಿಕ್ಕದಂತೆ ತಪ್ಪಿಸಿದನು.",
			"49": "<sup>49</sup> ಸೌಲನ ಗಂಡು ಮಕ್ಕಳು - ಯೋನಾತಾನ, ಇಷ್ವಿ ಮತ್ತು ಮಲ್ಕೀಷೂವ. ಅವನ ಹೆಣ್ಣು ಮಕ್ಕಳು - ಮೇರಬ್ ಮತ್ತು ಮೀಕಲ್.",
			"50": "<sup>50</sup> ಅಹೀಮಾಚನ ಮಗಳಾದ ಅಹೀನೋವಮಳು ಸೌಲನ ಹೆಂಡತಿ. ನೇರನ ಮಗನಾದ ಅಬ್ನೇರನು ಅವನ ಸೇನಾಪತಿ. ನೇರನು ಸೌಲನು ಚಿಕ್ಕಪ್ಪನು.",
			"51": "<sup>51</sup> ಸೌಲನ ತಂದೆಯಾದ ಕೀಷನೂ, ಅಬ್ನೇರನ ತಂದೆಯಾದ ನೇರನೂ ಅಬೀಯೇಲನ ಮಕ್ಕಳು.",
			"52": "<sup>52</sup> ಸೌಲನ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ಫಿಲಿಷ್ಟಿಯರಿಗೂ ಇಸ್ರಾಯೇಲರಿಗೂ ಘೋರ ಯುದ್ಧವಾಗುತ್ತಿದ್ದುದ್ದರಿಂದ ಅವನು ಎಲ್ಲಿಯಾದರೂ ಒಬ್ಬ ಬಲಿಷ್ಠನೂ ಪರಾಕ್ರಮಶಾಲಿಯೂ ಆದ ಮನುಷ್ಯನನ್ನು ಕಂಡರೆ, ಕೂಡಲೆ ಅವನನ್ನು ತನ್ನ ಹತ್ತಿರ ಇಟ್ಟುಕೊಳ್ಳುತ್ತಿದ್ದನು."
		},
		"15": {
			"1": "<sup>1</sup> ಸಮುವೇಲನು ಸೌಲನಿಗೆ, “ಯೆಹೋವನು ನಿನ್ನನ್ನು ತನ್ನ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಾಯೇಲರ ಮೇಲೆ ಅರಸನನ್ನಾಗಿ ಅಭಿಷೇಕಿಸುವುದಕ್ಕೋಸ್ಕರ ನನ್ನನ್ನೇ ಕಳುಹಿಸಿದನಲ್ಲಾ; ಆತನು ಈಗ ಹೇಳುವುದನ್ನು ಕೇಳು.",
			"2": "<sup>2</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ನಿನಗೆ, ‘ಇಸ್ರಾಯೇಲರು ಐಗುಪ್ತದಿಂದ ಬರುತ್ತಿರುವಾಗ ದಾರಿಯಲ್ಲಿ ಅಮಾಲೇಕ್ಯರು ಅವರಿಗೆ ವಿರೋಧವಾಗಿ ನಿಂತು ತೊಂದರೆಪಡಿಸಿದ್ದನ್ನು ನೆನಪುಮಾಡಿಕೊಂಡು ನಾನು ಅವರಿಗೆ ಮುಯ್ಯಿತೀರಿಸುವೆನು.",
			"3": "<sup>3</sup> ಈಗ ನೀನು ಹೋಗಿ ಅವರನ್ನು ಸೋಲಿಸಿ ಅವರಿಗಿರುವುದನ್ನೆಲ್ಲಾ ಸಂಪೂರ್ಣವಾಗಿ ಹಾಳುಮಾಡು. ಗಂಡಸರನ್ನೂ, ಹೆಂಗಸರನ್ನೂ, ಮಕ್ಕಳನ್ನೂ, ಶಿಶುಗಳನ್ನೂ, ಎತ್ತು, ಕುರಿ, ಕತ್ತೆ, ಒಂಟೆ, ಇವುಗಳನ್ನೂ ಕನಿಕರವಿಲ್ಲದೆ ಕೊಂದುಹಾಕು ಎಂದು ಹೇಳುತ್ತಾನೆ’” ಅಂದನು.",
			"4": "<sup>4</sup> ಸೌಲನು ತೆಲಾಯೀಮಿನಲ್ಲಿ ಸೈನ್ಯವನ್ನು ಕೂಡಿಸಿ ಲೆಕ್ಕಿಸಲು ಎರಡು ಲಕ್ಷ ಕಾಲಾಳುಗಳು ಇದ್ದರು; ಯೆಹೂದ್ಯರು ಹತ್ತು ಸಾವಿರ ಜನರು ಇದ್ದರು.",
			"5": "<sup>5</sup> ಅವನು ಇವರೊಡನೆ ಅಮಾಲೇಕ್ಯರ ರಾಜಧಾನಿಯ ಬಳಿಗೆ ಹೋಗಿ ಅಲ್ಲಿನ ಒಂದು ತಗ್ಗಿನಲ್ಲಿ ಹೊಂಚುಹಾಕುತ್ತಿದ್ದನು.",
			"6": "<sup>6</sup> ಆದರೆ ಅವನು ಕೇನ್ಯರಿಗೆ, “ನೀವು ಅಮಾಲೇಕ್ಯರನ್ನು ಬಿಟ್ಟುಹೋಗಿರಿ; ಇಲ್ಲವಾದರೆ ನೀವೂ ಅವರೊಡನೆ ನಾಶವಾದೀರಿ. ಇಸ್ರಾಯೇಲರು ಐಗುಪ್ತದಿಂದ ಬರುತ್ತಿದ್ದಾಗ ನೀವು ಅವರಿಗೆ ದಯೆತೋರಿಸಿದಿರಲ್ಲಾ” ಎಂದು ಹೇಳಿಕಳುಹಿಸಿದನು. ಅದರಂತೆಯೇ ಅವರು ಅಮಾಲೇಕ್ಯರನ್ನು ಬಿಟ್ಟುಹೋದರು.",
			"7": "<sup>7</sup> ಆಗ ಸೌಲನು ಅಮಾಲೇಕ್ಯರನ್ನು ಹವೀಲಾ ಪ್ರಾಂತ್ಯದಿಂದ ಐಗುಪ್ತದ ಪೂರ್ವದಿಕ್ಕಿನಲ್ಲಿರುವ ಶೂರಿನವರೆಗೆ ಅವರನ್ನು ಸಂಹರಿಸುತ್ತಾ ಹೋದನು.",
			"8": "<sup>8</sup> ಅವರ ಅರಸನಾದ ಅಗಾಗನನ್ನು ಜೀವಂತವಾಗಿ ಹಿಡಿದು, ಎಲ್ಲಾ ಜನರನ್ನು ಕತ್ತಿಯಿಂದ ಸಂಹರಿಸಿದನು.",
			"9": "<sup>9</sup> ಸೌಲನೂ ಮತ್ತು ಇಸ್ರಾಯೇಲರೂ ಅಗಾಗನನ್ನೂ, ಉತ್ತಮವಾದ ಕುರಿದನಗಳನ್ನೂ, ಕುರಿಮರಿಗಳನ್ನೂ, ಕೊಬ್ಬಿದ ಪಶುಗಳನ್ನೂ, ಶ್ರೇಷ್ಟವಾದ ಎಲ್ಲಾ ಪದಾರ್ಥಗಳನ್ನೂ ನಾಶಮಾಡಲು ಮನಸ್ಸಿಲ್ಲದೆ ಉಳಿಸಿ ಪ್ರಯೋಜನವಿಲ್ಲದಂಥ ಹೀನವಾದವುಗಳನ್ನೆಲ್ಲಾ ನಾಶಮಾಡಿಬಿಟ್ಟರು.",
			"10": "<sup>10</sup> ಯೆಹೋವನು ಸಮುವೇಲನಿಗೆ, “ಸೌಲನನ್ನು ಅರಸನನ್ನಾಗಿ ಮಾಡಿದ್ದಕ್ಕೆ ಈಗ ವಿಷಾದಿಸುತ್ತೇನೆ. ಅವನು ನನ್ನನ್ನು ಬಿಟ್ಟುಬಿಟ್ಟನು; ನನ್ನ ಆಜ್ಞೆಗಳನ್ನು ನೆರವೇರಿಸಲಿಲ್ಲ” ಎಂದು ಹೇಳಿದನು.",
			"11": "<sup>11</sup> ಸಮುವೇಲನು ಕೋಪಗೊಂಡು ರಾತ್ರಿಯೆಲ್ಲಾ ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಟ್ಟನು.",
			"12": "<sup>12</sup> ಸಮುವೇಲನು ಬೆಳಿಗ್ಗೆ ಎದ್ದು ಸೌಲನನ್ನು ನೋಡುವುದಕ್ಕೆ ಹೊರಟಾಗ ಅವನಿಗೆ, “ಸೌಲನು ಕರ್ಮೆಲಿಗೆ ಹೋಗಿ ಅಲ್ಲಿ ತನಗೋಸ್ಕರ ಒಂದು ಜ್ಞಾಪಕಸ್ತಂಭವನ್ನು ನಿಲ್ಲಿಸಿ, ಅಲ್ಲಿಂದ ಗಟ್ಟಾ ಇಳಿದು ಗಿಲ್ಗಾಲಿಗೆ ಹೋದನು” ಎಂಬ ವರ್ತಮಾನವು ಬಂದದ್ದರಿಂದ",
			"13": "<sup>13</sup> ಸಮುವೇಲನು ಅಲ್ಲಿಗೆ ಹೋದನು. ಸೌಲನು ಅವನನ್ನು ಕಂಡು, “ಯೆಹೋವನು ನಿನ್ನನ್ನು ಆಶೀರ್ವದಿಸಲಿ; ನಾನು ಆತನ ಅಪ್ಪಣೆಯನ್ನು ನೆರವೇರಿಸಿದೆನು” ಅಂದನು.",
			"14": "<sup>14</sup> ಅದಕ್ಕೆ ಸಮುವೇಲನು, “ಹಾಗಾದರೆ ಇದೇನು? ಕುರಿಗಳ ಕೂಗು ನನ್ನ ಕಿವಿಗೆ ಬೀಳುತ್ತದೆ, ದನಗಳ ಶಬ್ದವು ನನಗೆ ಕೇಳಿಸುತ್ತದೆ” ಎನ್ನಲು",
			"15": "<sup>15</sup> ಸೌಲನು, “ಜನರು ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಯಜ್ಞವನ್ನು ಅರ್ಪಿಸುವುದಕ್ಕಾಗಿ ಅಮಾಲೇಕ್ಯರ ಕುರಿದನಗಳಲ್ಲಿ ಉತ್ತಮವಾದವುಗಳನ್ನು ಉಳಿಸಿ ತಂದಿದ್ದಾರೆ; ಮಿಕ್ಕಾದವುಗಳನ್ನೆಲ್ಲಾ ಸಂಪೂರ್ಣವಾಗಿ ನಾಶಮಾಡಿದೆವು” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"16": "<sup>16</sup> ಆಗ ಸಮುವೇಲನು ಅವನಿಗೆ, “ಅದಿರಲಿ; ಯೆಹೋವನು ಕಳೆದ ರಾತ್ರಿಯಲ್ಲಿ ನನಗೆ ಹೇಳಿದ್ದನ್ನು ತಿಳಿಸುತ್ತೇನೆ ಕೇಳು” ಎನ್ನಲು ಅವನು, “ಹೇಳು” ಅಂದನು.",
			"17": "<sup>17</sup> ಸಮುವೇಲನು, “ನೀನು ನಿನ್ನ ದೃಷ್ಟಿಯಲ್ಲಿ ಅಲ್ಪನಾಗಿದ್ದರೂ ಯೆಹೋವನು ನಿನಗೆ ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ರಾಜ್ಯಾಭಿಷೇಕ ಮಾಡಿದ್ದರಿಂದ ನೀನು ಎಲ್ಲಾ ಕುಲಗಳಿಗೆ ಶಿರಸ್ಸನ್ನಾಗಿ ಮಾಡಿದನು.",
			"18": "<sup>18</sup> ಆತನು ನಿನಗೆ, ‘ಹೊರಟು ಹೋಗಿ ದುಷ್ಟರಾದ ಅಮಾಲೇಕ್ಯರೊಡನೆ ಯುದ್ಧಮಾಡಿ ಅವರೆಲ್ಲರನ್ನೂ ಸಂಪೂರ್ಣವಾಗಿ ನಾಶಮಾಡು’ ಎಂದು ಹೇಳಿದನು.",
			"19": "<sup>19</sup> ಆದರೆ ನೀನು ಯೆಹೋವನ ಮಾತನ್ನು ಕೇಳದೆ ಕೊಳ್ಳೆಗಾಗಿ ಮನಸೋತು ಆತನಿಗೆ ದ್ರೋಹಮಾಡಿದ್ದೇಕೆ?” ಅಂದನು.",
			"20": "<sup>20</sup> ಆಗ ಸೌಲನು ಸಮುವೇಲನಿಗೆ, “ಏನು? ನಾನು ಯೆಹೋವನ ಮಾತನ್ನು ಕೇಳಲಿಲ್ಲವೋ? ಆತನು ಕಳುಹಿಸಿದಲ್ಲಿಗೆ ಹೋಗಿ ಅಮಾಲೇಕ್ಯರನ್ನೆಲ್ಲಾ ಸಂಹರಿಸಿ ಅವರ ಅರಸನಾದ ಅಗಾಗನನ್ನು ಹಿಡಿದು ತಂದೆನು.",
			"21": "<sup>21</sup> ಆದರೆ ಜನರು ಸಂಪೂರ್ಣವಾಗಿ ಸಂಹರಿಸಬೇಕಾದ ಕೊಳ್ಳೆಯಲ್ಲಿ ಉತ್ತಮವಾದ ಕುರಿದನಗಳನ್ನು ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಗಿಲ್ಗಾಲಿನಲ್ಲಿ ಯಜ್ಞಾರ್ಪಣೆಮಾಡುವುದಕ್ಕಾಗಿ ಉಳಿಸಿ ತಂದರು” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"22": "<sup>22</sup> ಅದಕ್ಕೆ ಸಮುವೇಲನು, “ಯೆಹೋವನು ವಿಧೇಯತ್ವವನ್ನು ಮೆಚ್ಚುವಷ್ಟು ಯಜ್ಞಹೋಮಗಳಿಗೆ ಮೆಚ್ಚುತ್ತಾನೋ? ಯಜ್ಞವನ್ನರ್ಪಿಸುವುದಕ್ಕಿಂತ ಆತನ ಆಜ್ಞಾಪಾಲನೆ ಉತ್ತಮವಾಗಿದೆ; ಟಗರುಗಳ ಕೊಬ್ಬಿಗಿಂತ ವಿಧೇಯತೆ ವಿಶಿಷ್ಟವಾದುದು.",
			"23": "<sup>23</sup> ಅವಿಧೇಯತ್ವವು ಮಂತ್ರತಂತ್ರಗಳಷ್ಟೇ ಕೆಟ್ಟದ್ದಾಗಿರುವುದು. ಹಟವು ಮಿಥ್ಯಾಭಕ್ತಿಗೂ, ವಿಗ್ರಹಾರಾಧನೆಗೂ ಸಮಾನವಾಗಿರುವುದು. ನೀನು ಯೆಹೋವನ ಮಾತಿನಂತೆ ಅನುಸರಿಸಿ ನಡೆಯದೆ ಇದ್ದುದರಿಂದ ಆತನು ನಿನ್ನನ್ನು ಅರಸುತನದಿಂದ ನಿವಾರಿಸಿಬಿಟ್ಟಿದ್ದಾನೆ” ಎಂದು ಹೇಳಿದನು.",
			"24": "<sup>24</sup> ಸೌಲನು ಸಮುವೇಲನಿಗೆ, “ನಾನು ಯೆಹೋವನ ಮತ್ತು ನಿನ್ನ ಆಜ್ಞೆಗಳನ್ನು ಮೀರಿ ಪಾಪಮಾಡಿದ್ದೇನೆ; ಜನರಿಗೆ ಹೆದರಿ ಅವರ ಮಾತನ್ನು ಕೇಳಿದೆನು.",
			"25": "<sup>25</sup> ನೀನು ದಯವಿಟ್ಟು ನನ್ನ ಪಾಪವನ್ನು ಕ್ಷಮಿಸು; ನಾನು ಯೆಹೋವನನ್ನು ಆರಾಧಿಸುವಂತೆ ಹಿಂದಿರುಗಿ ನನ್ನ ಜೊತೆಯಲ್ಲಿ ಬಾ” ಎಂದು ಅವನನ್ನು ಬೇಡಿಕೊಂಡನು.",
			"26": "<sup>26</sup> ಅದಕ್ಕೆ ಸಮುವೇಲನು, “ನಾನು ಹಿಂದಿರುಗಿ ನಿನ್ನ ಜೊತೆಯಲ್ಲಿ ಬರುವುದಿಲ್ಲ; ನೀನು ಯೆಹೋವನ ಮಾತನ್ನು ಅನುಸರಿಸಿ ನಡೆಯದೆ ಇದ್ದುದರಿಂದ ಆತನು ನಿನ್ನನ್ನು ಇಸ್ರಾಯೇಲರ ಅರಸುತನದಿಂದ ನಿವಾರಿಸಿದ್ದಾನೆ” ಎಂದು ಹೇಳಿ ತಿರುಗಿಕೊಂಡು ಹೊರಡಲು",
			"27": "<sup>27</sup> ಸೌಲನು ಅವನ ಮೇಲಂಗಿಯ ಅಂಚನ್ನು ಹಿಡಿದು ಎಳೆದನು; ಅದು ಹರಿದುಹೋಯಿತು.",
			"28": "<sup>28</sup> ಆಗ ಸಮುವೇಲನು ಅವನಿಗೆ, “ಯೆಹೋವನು ಈ ಹೊತ್ತು ಇಸ್ರಾಯೇಲಿನ ರಾಜ್ಯವನ್ನು ನಿನ್ನಿಂದ ಕಿತ್ತುಕೊಂಡು ನಿನಗಿಂತ ಉತ್ತಮನಾದ ಇನ್ನೊಬ್ಬನಿಗೆ ಕೊಟ್ಟಿದ್ದಾನೆ.",
			"29": "<sup>29</sup> ಇಸ್ರಾಯೇಲರ ನಿರಂತರ ಆಧಾರವಾಗಿ ಇರುವವನು ಸುಳ್ಳಾಡುವವನಲ್ಲ; ಪಶ್ಚಾತ್ತಾಪಪಡುವವನಲ್ಲ; ಏಕೆಂದರೆ ವಾಗ್ದಾನ ಮಾಡಿದ ಮಾತನ್ನು ನಡೆಸದೆ ಬಿಡುವವನಲ್ಲ” ಎಂದು ಹೇಳಿದನು.",
			"30": "<sup>30</sup> ಅದಕ್ಕೆ ಸೌಲನು, “ನಾನು ಪಾಪಮಾಡಿದ್ದೇನೆ; ದಯವಿಟ್ಟು ಇಸ್ರಾಯೇಲರ ಮುಂದೆಯೂ, ಜನರ ಮುಂದೆಯೂ ಹಿರಿಯರ ಮುಂದೆಯೂ ನನ್ನ ಮಾನವನ್ನುಳಿಸು. ನಾನು ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಆರಾಧಿಸುವಂತೆ ಹಿಂದಿರುಗಿ ನನ್ನ ಜೊತೆಯಲ್ಲಿ ಬಾ” ಎಂದು ಬೇಡಿಕೊಂಡನು.",
			"31": "<sup>31</sup> ಸಮುವೇಲನು ಹಿಂದಿರುಗಿ ಸೌಲನ ಜೊತೆಯಲ್ಲಿ ಹೋದನು. ಆಗ ಸೌಲನು ದೇವರನ್ನು ಆರಾಧಿಸಿದನು.",
			"32": "<sup>32</sup> ತರುವಾಯ ಸಮುವೇಲನು, “ಅಮಾಲೇಕ್ಯರ ಅರಸನಾದ ಅಗಾಗನನ್ನು ನನ್ನ ಬಳಿಗೆ ಕರೆತನ್ನಿರಿ” ಎಂದನು. ಅಗಾಗನು ಮರಣಭಯ ತಪ್ಪಿತೆಂದು ನೆನಪಿಸಿಕೊಂಡು ಸಂತೋಷದಿಂದ ಅವನ ಹತ್ತಿರ ಬಂದನು.",
			"33": "<sup>33</sup> ಆದರೆ ಸಮುವೇಲನು ಅವನಿಗೆ, “ನಿನ್ನ ಕತ್ತಿಯು ಅನೇಕ ಸ್ತ್ರೀಯರಿಗೆ ಪುತ್ರಶೋಕವನ್ನುಂಟುಮಾಡಿದೆ; ಅದರಂತೆ ನಿನ್ನ ತಾಯಿಯು ಎಲ್ಲರಿಗಿಂತಲೂ ಹೆಚ್ಚಾಗಿ ಪುತ್ರಶೋಕವನ್ನು ಅನುಭವಿಸಲಿ” ಎಂದು ಹೇಳಿ ಅವನನ್ನು ಗಿಲ್ಗಾಲಿನಲ್ಲಿ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲೇ ಕಡಿದುಹಾಕಿದನು.",
			"34": "<sup>34</sup> ಅನಂತರ ಸಮುವೇಲನು ರಾಮಕ್ಕೆ ಹೋದನು; ಸೌಲನು ತನ್ನ ಊರಾದ ಗಿಬೆಯದಲ್ಲಿರುವ ಮನೆಗೆ ಹೋದನು.",
			"35": "<sup>35</sup> ಸಮುವೇಲನು ಜೀವದಿಂದ ಇರುವವರೆಗೂ ಸೌಲನನ್ನು ನೋಡಲಿಲ್ಲ; ಆದರೆ ಯೆಹೋವನು ಸೌಲನನ್ನು ಇಸ್ರಾಯೇಲರ ಅರಸನನ್ನಾಗಿ ಮಾಡಿದ್ದಕ್ಕಾಗಿ ವಿಷಾದಪಟ್ಟನು. ಸಮುವೇಲನು ಅವನ ಕುರಿತು ದುಃಖಪಡುತ್ತಿದ್ದನು."
		},
		"16": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಸಮುವೇಲನಿಗೆ, “ನಾನು ಸೌಲನನ್ನು ಇಸ್ರಾಯೇಲರ ಅರಸನಾಗಿರುವುದಕ್ಕೆ ಯೋಗ್ಯನಲ್ಲವೆಂದು ತಳ್ಳಿಬಿಟ್ಟಿದ್ದೇನಲ್ಲಾ; ನೀನು ಅವನಿಗೋಸ್ಕರ ಎಷ್ಟರವರೆಗೆ ದುಃಖಿಸುತ್ತಿರುವಿ? ಕೊಂಬಿನಲ್ಲಿ ಎಣ್ಣೆಯಿಂದ ತುಂಬಿಸಿಕೊಂಡು ಬಾ; ನಾನು ನಿನ್ನನ್ನು ಬೇತ್ಲೆಹೇಮಿನವನಾದ ಇಷಯನ ಬಳಿಗೆ ಕಳುಹಿಸುತ್ತೇನೆ; ಅವನ ಮಕ್ಕಳಲ್ಲೊಬ್ಬನನ್ನು ಅರಸನನ್ನಾಗಿ ಆರಿಸಿಕೊಂಡಿದ್ದೇನೆ” ಎಂದು ಹೇಳಿದನು.",
			"2": "<sup>2</sup> ಅದಕ್ಕೆ ಸಮುವೇಲನು, “ನಾನು ಹೋಗುವುದು ಹೇಗೆ? ಇದು ಸೌಲನಿಗೆ ಗೊತ್ತಾದರೆ ಅವನು ನನ್ನನ್ನು ಕೊಂದುಹಾಕುವನಲ್ಲವೇ” ಎಂದನು. ಅದಕ್ಕೆ ಯೆಹೋವನು, “ನೀನು ಒಂದು ಕರುವನ್ನು ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ಯೆಹೋವನಿಗೋಸ್ಕರ ಯಜ್ಞಮಾಡುವುದಕ್ಕೆ ಬಂದಿದ್ದೇನೆಂದು ಹೇಳಿ ಇಷಯನನ್ನು ಅದಕ್ಕಾಗಿ ಕರೆ.",
			"3": "<sup>3</sup> ಆ ನಂತರ ನೀನು ಮಾಡಬೇಕಾದದ್ದನ್ನು ನಾನೇ ನಿನಗೆ ತಿಳಿಸುತ್ತೇನೆ. ನಾನು ಯಾರನ್ನು ತೋರಿಸುತ್ತೇನೋ, ಅವನನ್ನು ನೀನು ಅಭಿಷೇಕಿಸಬೇಕು” ಎಂದನು.",
			"4": "<sup>4</sup> ಯೆಹೋವನು ಹೇಳಿದಂತೆಯೇ ಸಮುವೇಲನು ಬೇತ್ಲೆಹೇಮಿಗೆ ಹೋದನು. ಆ ಊರಿನ ಹಿರಿಯರು ನಡುಗುತ್ತಾ ಬಂದು ಅವನನ್ನು ಎದುರುಗೊಂಡು “ನಿನ್ನ ಆಗಮನವು ನಮಗೆ ಶುಭಕರವಾಗಿದೆಯೇ?” ಎಂದು ಕೇಳಲು",
			"5": "<sup>5</sup> ಅವನು, “ಹೌದು, ಶುಭಕರವಾಗಿದೆ; ಯೆಹೋವನಿಗೆ ಯಜ್ಞವನ್ನರ್ಪಿಸುವುದಕ್ಕೆ ಬಂದಿದ್ದೇನೆ. ನೀವು ನಿಮ್ಮನ್ನು ಶುದ್ಧಪಡಿಸಿಕೊಂಡು ನನ್ನ ಜೊತೆಯಲ್ಲಿ ಯಜ್ಞಕ್ಕೆ ಬನ್ನಿರಿ” ಎಂದು ಹೇಳಿ ಇಷಯನನ್ನೂ, ಅವನ ಮಕ್ಕಳನ್ನೂ ಶುದ್ಧೀಕರಿಸಿ ಅವರನ್ನೂ ಯಜ್ಞಕ್ಕೆ ಕರೆದನು.",
			"6": "<sup>6</sup> ಅವರು ಬರಲು ಅವನು ಎಲೀಯಾಬನನ್ನು ನೋಡಿ, ನಿಶ್ಚಯವಾಗಿ ಯೆಹೋವನು ಅಭಿಷೇಕಕ್ಕೆ ಆರಿಸಿಕೊಂಡವನು ಆತನ ಮುಂದೆ ಇದ್ದಾನೆ ಎಂದುಕೊಂಡನು.",
			"7": "<sup>7</sup> ಆದರೆ ಯೆಹೋವನು ಸಮುವೇಲನಿಗೆ, “ನೀನು ಅವನ ಸೌಂದರ್ಯವನ್ನೂ, ಎತ್ತರವನ್ನೂ ನೋಡಬೇಡ; ನಾನು ಅವನನ್ನು ನಿರಾಕರಿಸಿದ್ದೇನೆ. ಯೆಹೋವನು ಮನುಷ್ಯರಂತೆ ಹೊರಗಿನ ತೋರಿಕೆಯನ್ನು ನೋಡದೆ ಹೃದಯದ ಮತ್ತು ಅಂತರಂಗದ ಸೌಂದರ್ಯ ನೋಡುವವನಾಗಿದ್ದಾನೆ” ಅಂದನು.",
			"8": "<sup>8</sup> ಇಷಯನು ಅಬೀನಾದಾಬನನ್ನು ಸಮುವೇಲನ ಬಳಿಗೆ ಬರಮಾಡಲು ಸಮುವೇಲನು, “ಯೆಹೋವನು ಇವನನ್ನು ಆರಿಸಿಕೊಳ್ಳಲಿಲ್ಲ” ಎಂದನು.",
			"9": "<sup>9</sup> ತರುವಾಯ ಇಷಯನು ಶಮ್ಮನನ್ನು ಬರಮಾಡಲು ಸಮುವೇಲನು, “ಯೆಹೋವನು ಇವನನ್ನೂ ಆರಿಸಿಕೊಳ್ಳಲಿಲ್ಲ” ಎಂದು ಹೇಳಿದನು.",
			"10": "<sup>10</sup> ಹೀಗೆ ಇಷಯನು ತನ್ನ ಮಕ್ಕಳಲ್ಲಿ ಏಳು ಮಂದಿಯನ್ನು ಸಮುವೇಲನ ಬಳಿಗೆ ಬರಮಾಡಿದರೂ ಅವನು, “ಯೆಹೋವನು ಇವರನ್ನು ಆರಿಸಿಕೊಳ್ಳಲಿಲ್ಲ” ಎಂದು ನುಡಿದನು.",
			"11": "<sup>11</sup> ಅನಂತರ ಸಮುವೇಲನು ಇಷಯನನ್ನು, “ನಿನ್ನ ಮಕ್ಕಳೆಲ್ಲಾ ಇಷ್ಟೇ ಮಂದಿಯೋ” ಎಂದು ಕೇಳಲು ಅವನು, “ಇವರೆಲ್ಲರಿಗಿಂತಲೂ ಚಿಕ್ಕವನು ಒಬ್ಬನಿರುತ್ತಾನೆ; ಅವನು ಕುರಿಮೇಯಿಸುವುದಕ್ಕೆ ಹೋಗಿದ್ದಾನೆ” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು. ಆಗ ಸಮುವೇಲನು, “ಅವನನ್ನು ಕರೆದುಕೊಂಡು ಬರಲು ತಿಳಿಸು; ಅವನು ಬರುವವರೆಗೂ ನಾವು ಊಟಕ್ಕೆ ಕುಳಿತುಕೊಳ್ಳಬಾರದು” ಎಂದು ಹೇಳಲು ಇಷಯನು ಅವನನ್ನು ಕರೆದು ಕರೆದುಕೊಂಡು ಬರಲು ತಿಳಿಸಿದನು.",
			"12": "<sup>12</sup> ಅವನು ಕೆಂಪುಬಣ್ಣದವನೂ, ಸುಂದರನೇತ್ರನೂ, ನೋಡುವುದಕ್ಕೆ ರಮಣೀಯನೂ ಆಗಿದ್ದನು. ಯೆಹೋವನು ಸಮುವೇಲನಿಗೆ, “ಎದ್ದು ಇವನನ್ನು ಅಭಿಷೇಕಿಸು; ನಾನು ಆರಿಸಿಕೊಂಡವನು ಇವನೇ” ಎಂದು ಆಜ್ಞಾಪಿಸಲು",
			"13": "<sup>13</sup> ಸಮುವೇಲನು ಎಣ್ಣೆಯ ಕೊಂಬನ್ನು ತೆಗೆದುಕೊಂಡು ಅವನನ್ನು, ಅವನ ಸಹೋದರರ ಮಧ್ಯದಲ್ಲೇ ಅಭಿಷೇಕಿಸಿದನು. ಕೂಡಲೆ ಯೆಹೋವನ ಆತ್ಮವು ದಾವೀದನ ಮೇಲೆ ಬಂದು ನೆಲೆಗೊಂಡಿತು. ಅನಂತರ ಸಮುವೇಲನು ರಾಮಕ್ಕೆ ಹೊರಟುಹೋದನು.",
			"14": "<sup>14</sup> ಯೆಹೋವನ ಆತ್ಮವು ಸೌಲನನ್ನು ಬಿಟ್ಟು ಹೋಯಿತು; ಯೆಹೋವನು ಕಳುಹಿಸಿದ ದುರಾತ್ಮವು ಬಂದು ಅವನನ್ನು ಪೀಡಿಸುತ್ತಿದ್ದರಿಂದ",
			"15": "<sup>15</sup> ಅವನ ಸೇವಕರು ಅವನಿಗೆ, “ಇಗೋ, ದೇವರಿಂದ ಬಂದ ದುರಾತ್ಮವು ನಿನ್ನನ್ನು ಪೀಡಿಸುತ್ತಾ ಇದೆ.",
			"16": "<sup>16</sup> ಸ್ವಾಮೀ, ಅಪ್ಪಣೆಯಾಗಲಿ; ನಿನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನಿಂತಿರುವ ನಿನ್ನ ಸೇವಕರಾದ ನಾವು ಹೋಗಿ ಕಿನ್ನರಿ ನುಡಿಸುವುದರಲ್ಲಿ ನಿಪುಣನಾದ ಒಬ್ಬನನ್ನು ಹುಡುಕಿ ತರುತ್ತೇನೆ. ಯೆಹೋವನಿಂದ ಕಳುಹಿಸಲ್ಪಟ್ಟ ದುರಾತ್ಮವು ನಿನ್ನ ಮೇಲೆ ಬರುವಾಗ ಅವನು ಕಿನ್ನರಿ ನುಡಿಸಿದರೆ ನಿನಗೆ ಉಪಶಮನವಾಗುವುದು” ಎಂದು ಹೇಳಿದರು.",
			"17": "<sup>17</sup> ಸೌಲನು ಅವರಿಗೆ, “ಚೆನ್ನಾಗಿ ನುಡಿಸಬಲ್ಲವನಾದ ಒಬ್ಬನನ್ನು ನನಗೋಸ್ಕರ ಹುಡುಕಿ ಕರೆದುಕೊಂಡು ಬನ್ನಿರಿ” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"18": "<sup>18</sup> ಸೇವಕರಲ್ಲೊಬ್ಬನು ಅವನಿಗೆ, “ಬೇತ್ಲೆಹೇಮಿನವನಾದ ಇಷಯನ ಮಗನನ್ನು ನೋಡಿದ್ದೇನೆ; ಅವನು ಚೆನ್ನಾಗಿ ಕಿನ್ನರಿ ನುಡಿಸಬಲ್ಲವನೂ, ಪರಾಕ್ರಮಶಾಲಿಯೂ, ಯುದ್ಧ ನಿಪುಣನೂ, ವಾಕ್ಚತುರನೂ, ಸುಂದರನೂ, ಯೆಹೋವನ ಅನುಗ್ರಹವನ್ನು ಹೊಂದಿದವನೂ ಆಗಿದ್ದಾನೆ” ಎಂದು ತಿಳಿಸಿದನು.",
			"19": "<sup>19</sup> ಕೂಡಲೆ ಸೌಲನು ಇಷಯನ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿ ಅವನಿಗೆ, “ಕುರಿಗಳನ್ನು ಮೇಯಿಸುತ್ತಿರುವ ನಿನ್ನ ಮಗನಾದ ದಾವೀದನನ್ನು ನನ್ನ ಬಳಿಗೆ ಕಳುಹಿಸು” ಎಂದು ಹೇಳಿಸಿದನು.",
			"20": "<sup>20</sup> ಇಷಯನು ರೊಟ್ಟಿಯನ್ನೂ, ಒಂದು ಬುದ್ದಲಿ ದ್ರಾಕ್ಷಾರಸವನ್ನೂ, ಒಂದು ಹೋತಮರಿಯನ್ನೂ, ಕತ್ತೆಯ ಮೇಲೆ ಹೇರಿಸಿ ತನ್ನ ಮಗನಾದ ದಾವೀದನ ಮುಖಾಂತರ ಸೌಲನಿಗೆ ಕಳುಹಿಸಿದನು.",
			"21": "<sup>21</sup> ಹೀಗೆ ದಾವೀದನು ಸೌಲನ ಬಳಿಗೆ ಬಂದು ಅವನ ಸೇವಕನಾದನು. ಸೌಲನು ಅವನನ್ನು ಬಹಳವಾಗಿ ಪ್ರೀತಿಸಿ ತನ್ನ ಆಯುಧಗಳನ್ನು ಹೊರುವುದಕ್ಕೆ ನೇಮಿಸಿಕೊಂಡನು.",
			"22": "<sup>22</sup> ತರುವಾಯ ಸೌಲನು ಇಷಯನ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿ ಅವನಿಗೆ, “ನಾನು ದಾವೀದನನ್ನು ಮೆಚ್ಚಿದ್ದೇನೆ; ಅವನು ನನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸೇವೆಮಾಡಲಿ” ಎಂದು ತಿಳಿಸಿದನು.",
			"23": "<sup>23</sup> ದೇವರಿಂದ ಕಳುಹಿಸಲ್ಪಟ್ಟ ದುರಾತ್ಮವು ಸೌಲನ ಮೇಲೆ ಬಂದಾಗ ದಾವೀದನು ಕಿನ್ನರಿಯನ್ನು ನುಡಿಸುವನು; ಅದರಿಂದ ದುರಾತ್ಮವು ಸೌಲನನ್ನು ಬಿಟ್ಟುಹೋಗುವುದು; ಅವನು ಉಪಶಮನಹೊಂದಿ ಚೆನ್ನಾಗಿರುತ್ತಿದ್ದನು."
		},
		"17": {
			"1": "<sup>1</sup> ಫಿಲಿಷ್ಟಿಯರು ಯುದ್ಧ ಮಾಡುವುದಕ್ಕೋಸ್ಕರ ಯೆಹೂದದೇಶದ ಸೋಕೋವಿನಲ್ಲಿ ಸೈನ್ಯವನ್ನು ಕೂಡಿಸಿಕೊಂಡು ಬಂದು ಸೋಕೋವಿಗೂ ಆಜೇಕಕ್ಕೂ ಮಧ್ಯದಲ್ಲಿರುವ ಎಫೆಸ್ದಮ್ಮೀಮಿನಲ್ಲಿ ಪಾಳೆಯಮಾಡಿಕೊಂಡರು.",
			"2": "<sup>2</sup> ಸೌಲನೂ, ಇಸ್ರಾಯೇಲ್ಯರೂ ಫಿಲಿಷ್ಟಿಯರೊಡನೆ ಯುದ್ಧಮಾಡುವುದಕ್ಕಾಗಿ ಸೈನ್ಯವನ್ನು ಕೂಡಿಸಿ ಏಲಾ ತಗ್ಗಿನಲ್ಲಿ ಪಾಳೆಯಮಾಡಿಕೊಂಡು ವ್ಯೂಹಕಟ್ಟಿದರು.",
			"3": "<sup>3</sup> ಫಿಲಿಷ್ಟಿಯರು ಒಂದು ಗುಡ್ಡದ ಪಕ್ಕದಲ್ಲಿ, ಇಸ್ರಾಯೇಲರು ಇನ್ನೊಂದು ಗುಡ್ಡದ ಪಕ್ಕದಲ್ಲಿಯೂ ನಿಂತರು. ಅವರ ಮಧ್ಯದಲ್ಲಿ ಒಂದು ಕಣಿವೆ ಇತ್ತು.",
			"4": "<sup>4</sup> ಫಿಲಿಷ್ಟಿಯರ ಪಾಳೆಯದಿಂದ ಗತ್ ಊರಿನವನಾದ ಗೊಲ್ಯಾತನೆಂಬ ಒಬ್ಬ ರಣಶೂರನು ಹೊರಟುಬಂದನು. ಅವನು ಆರುವರೆ ಮೊಳ ಎತ್ತರವಾಗಿದ್ದನು.",
			"5": "<sup>5</sup> ಅವನ ಶಿರಸ್ತ್ರಾಣವು ತಾಮ್ರದ್ದು. ತಾಮ್ರ ಲೋಹದ ಬಿಲ್ಲೆಗಳಿಂದ ಪರೆಪರೆಯಾಗಿ ಜೋಡಿಸಲ್ಪಟ್ಟ ಅವನ ಕವಚವು (ಐದು ಸಾವಿರ ಶೆಕೆಲಿನ ತಾಮ್ರದ ನಾಣ್ಯಗಳಷ್ಟು ತೂಕವಾಗಿತ್ತು.)",
			"6": "<sup>6</sup> ಅವನ ಪಾದರಕ್ಷೆಗಳು, ಹೆಗಲ ಮೇಲಣ ಈಟಿಯೂ ತಾಮ್ರದವು.",
			"7": "<sup>7</sup> ಅವನ ಬರ್ಜಿಯ ಹಿಡಿಕೆಯು ನೇಯಿಗಾರ ಕುಂಟೆಯಷ್ಟು ಗಾತ್ರವಾಗಿತ್ತು. ಅದರ ಅಲಗು ಆರುನೂರು ತೊಲೆ ಕಬ್ಬಿಣದಿಂದ ಮಾಡಿದ್ದು. ಅವನ ಗುರಾಣಿಯನ್ನು ಹೊರುವವನು ಅವನ ಮುಂದೆ ಹೋಗುತ್ತಿದ್ದನು.",
			"8": "<sup>8</sup> ಅವನು ನಿಂತು ಇಸ್ರಾಯೇಲ ಸೈನ್ಯದವರನ್ನು ಕೂಗಿ ಅವರಿಗೆ, “ನೀವು ಯುದ್ಧಮಾಡಲು ಬಂದದ್ದೇಕೆ? ನಾನು ಫಿಲಿಷ್ಟಿಯನು. ನೀವು ಸೌಲನ ಸೇವಕರು. ನಿಮ್ಮಲ್ಲಿ ಒಬ್ಬನನ್ನು ಆರಿಸಿಕೊಂಡು ನನ್ನ ಬಳಿಗೆ ಕಳುಹಿಸಿರಿ.",
			"9": "<sup>9</sup> ಅವನು ನನ್ನೊಡನೆ ಯುದ್ಧಮಾಡಿ ನನ್ನನ್ನು ಕೊಂದರೆ ನಾವು ನಿಮಗೇ ದಾಸರಾಗಿರುವೆವು. ಆದರೆ ನಾನು ಅವನನ್ನು ಸೋಲಿಸಿ ಕೊಂದರೆ ಆಗ ನೀವು ನಮ್ಮ ಗುಲಾಮರಾಗಿ ನಮ್ಮನ್ನು ಸೇವೆಮಾಡುವವರಾಗುವಿರಿ” ಎಂದು ಹೇಳಿದನು.",
			"10": "<sup>10</sup> ಮತ್ತು ಅವನು, “ಈಹೊತ್ತು ಇಸ್ರಾಯೇಲ ಸೈನ್ಯದವರನ್ನು ಕುರಿತು. ನನ್ನೊಡನೆ ಕಾಳಗಕ್ಕೆ ನಿಮ್ಮಲ್ಲಿಂದ ಒಬ್ಬ ಮನುಷ್ಯನನ್ನು ಕಳುಹಿಸಿರಿ ನೋಡೋಣ” ಎಂದು ಸವಾಲು ಹಾಕಿದನು.",
			"11": "<sup>11</sup> ಸೌಲನೂ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲರೂ ಆ ಫಿಲಿಷ್ಟಿಯನ ಮಾತುಗಳನ್ನು ಕೇಳಿ ಎದೆಗುಂದಿದವರಾಗಿ ಬಹಳ ಭಯಪಟ್ಟರು.",
			"12": "<sup>12</sup> ಸೌಲನ ಕಾಲದಲ್ಲಿ ಯೆಹೂದ ಬೇತ್ಲೆಹೇಮ್ ಎಂಬ ಊರಿನ ಎಫ್ರಾತ್ಯನಾದ ಇಷಯನೆಂಬ ಒಬ್ಬ ವೃದ್ಧನಿದ್ದನು. ಇವನ ಎಂಟು ಮಂದಿ ಮಕ್ಕಳಲ್ಲಿ ದಾವೀದನು ಒಬ್ಬನು.",
			"13": "<sup>13</sup> ಇವನ ಮೂರು ಹಿರಿಯ ಮಕ್ಕಳು ಸೌಲನ ಜೊತೆಯಲ್ಲಿ ಯುದ್ಧಕ್ಕೆ ಹೋಗಿದ್ದರು. ಅವರಲ್ಲಿ ಚೊಚ್ಚಲ ಮಗನ ಹೆಸರು ಎಲೀಯಾಬ, ಎರಡನೆಯ ಮಗನ ಹೆಸರು ಅಬೀನಾದಾಬ, ಮೂರನೆಯವನ ಹೆಸರು ಶಮ್ಮ,",
			"14": "<sup>14</sup> ದಾವೀದನೇ ಎಲ್ಲರಿಗಿಂತಲೂ ಕಿರಿಯನು. ಮೂರು ಮಂದಿ ಹಿರಿ ಮಕ್ಕಳು ಸೌಲನ ಜೊತೆಯಲ್ಲಿ ಯುದ್ಧಕ್ಕೆ ಹೋದ್ದರಿಂದ",
			"15": "<sup>15</sup> ದಾವೀದನು ಸೌಲನನ್ನು ಬಿಟ್ಟು ತನ್ನ ತಂದೆಯ ಕುರಿಗಳನ್ನು ಮೇಯಿಸುವುದಕ್ಕೋಸ್ಕರ ಬೇತ್ಲೆಹೇಮಿಗೆ ಹೋದನು.",
			"16": "<sup>16</sup> ಆ ಫಿಲಿಷ್ಟಿಯನು ನಲ್ವತ್ತು ದಿನ ಉದಯದಲ್ಲಿಯೂ, ಸಾಯಂಕಾಲದಲ್ಲಿಯೂ, ಬಂದು ಅದೇ ಪ್ರಕಾರ ಕೂಗುತ್ತಾ ಸವಾಲು ಹಾಕುತ್ತಾ ಯುದ್ಧಕ್ಕೆ ನಿಲ್ಲುತ್ತಿದ್ದನು.",
			"17": "<sup>17</sup> ಒಂದು ದಿನ ಇಷಯನು ತನ್ನ ಮಗನಾದ ದಾವೀದನಿಗೆ, “ಇಗೋ ಇಲ್ಲಿ ಮೂವತ್ತು ಸೇರು ಹುರಿದ ಧಾನ್ಯ, ಹತ್ತು ರೊಟ್ಟಿಗಳೂ ಇರುತ್ತವೆ. ಇವುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಶೀಘ್ರವಾಗಿ ಪಾಳೆಯಕ್ಕೆ ಹೋಗಿ ನಿನ್ನ ಅಣ್ಣಂದಿರಿಗೆ ಕೊಡು.",
			"18": "<sup>18</sup> ಇದಲ್ಲದೆ ಇಲ್ಲಿ ಹತ್ತು ಗಿಣ್ಣದ ಉಂಡೆಗಳಿವೆ, ಇವುಗಳನ್ನು ಅವರ ಸಹಸ್ರಾಧಿಪತಿಗೆ ಕೊಡು. ಬರುವಾಗ ನಿನ್ನ ಅಣ್ಣಂದಿರ ಕ್ಷೇಮಸಮಾಚಾರವನ್ನು ವಿಚಾರಿಸಿಕೊಂಡು ಅವರಿಂದ ಒಂದು ಗುರುತನ್ನು ತೆಗೆದುಕೊಂಡು ಬಾ.",
			"19": "<sup>19</sup> ಸೌಲನೂ, ಇಸ್ರಾಯೇಲರೂ ಏಲಾ ತಗ್ಗಿನಲ್ಲಿ ಫಿಲಿಷ್ಟಿಯರೊಡನೆ ಯುದ್ಧಮಾಡುತ್ತಿದ್ದಾರಲ್ಲಾ” ಎಂದು ಹೇಳಿದನು.",
			"20": "<sup>20</sup> ದಾವೀದನು ಮರುದಿನ ಬೆಳಿಗ್ಗೆ ಎದ್ದು ಕುರಿ ಕಾಯುವವನಿಗೆ ಕುರಿಗಳನ್ನು ಒಪ್ಪಿಸಿ, ತನ್ನ ತಂದೆಯಾದ ಇಷಯನು ಹೇಳಿದವುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಸೈನ್ಯವು ಹೊರಟು ಯುದ್ಧಕ್ಕಾಗಿ ಅರ್ಭಟಿಸುವ ಹೊತ್ತಿನಲ್ಲಿ ಪಾಳೆಯದ ರಥಗಳು ನಿಂತಿದ್ದ ಸ್ಥಳಕ್ಕೆ ಬಂದನು.",
			"21": "<sup>21</sup> ಇಸ್ರಾಯೇಲರು, ಫಿಲಿಷ್ಟಿಯರೂ ಎದುರೆದುರು ವ್ಯೂಹಕಟ್ಟುವಷ್ಟರಲ್ಲಿ",
			"22": "<sup>22</sup> ದಾವೀದನು ತಾನು ತಂದವುಗಳನ್ನು ಸಾಮಾನು ಕಾಯುವವನ ಬಳಿಯಲ್ಲಿಟ್ಟು ರಣರಂಗಕ್ಕೆ ಓಡಿಹೋಗಿ ತನ್ನ ಸಹೋದರರ ಕ್ಷೇಮಸಮಾಚಾರವನ್ನು ವಿಚಾರಿಸುತ್ತಾ ಅವರೊಡನೆ ಮಾತನಾಡುತ್ತಿದ್ದನು.",
			"23": "<sup>23</sup> ಅಷ್ಟರಲ್ಲಿ ಗತ್ ಊರಿನ ಯುದ್ಧವೀರನಾದ ಗೊಲ್ಯಾತನೆಂಬ ಆ ಫಿಲಿಷ್ಟಿಯನು ತನ್ನ ಸೈನ್ಯದಿಂದ ಹೊರಗೆ ಬಂದು ಮುಂಚಿನಂತೆಯೇ ಕೂಗಿದನು. ದಾವೀದನು ಅವನ ಮಾತುಗಳನ್ನು ಕೇಳಿದನು.",
			"24": "<sup>24</sup> ಇಸ್ರಾಯೇಲರು ಅವನನ್ನು ನೋಡಿ ಬಹಳವಾಗಿ ಭಯಪಟ್ಟು ಅಲ್ಲಿಂದ ಓಡಿಹೋದರು.",
			"25": "<sup>25</sup> ಅವರು, “ಇಸ್ರಾಯೇಲ್ಯರಾದ ನಮ್ಮನ್ನು ಹೀಯಾಳಿಸುವುದಕ್ಕೋಸ್ಕರ ಬಂದಿರುವ ಈ ಮನುಷ್ಯನನ್ನು ಕಂಡಿರಾ? ಯಾವನು ಇವನನ್ನು ಕೊಲ್ಲುವನೋ ಅಂಥವನಿಗೆ ಅರಸನು ಅಪಾರದ್ರವ್ಯದೊಡನೆ ತನ್ನ ಮಗಳನ್ನು ಕೊಟ್ಟು ಮದುವೆ ಮಾಡುವನು. ಇದಲ್ಲದೆ ಅವನ ತಂದೆಯ ಕುಟುಂಬವನ್ನು ಎಲ್ಲಾ ತೆರಿಗೆಯಿಂದ ವಿಮೋಚಿಸುವನು” ಎಂದು ಮಾತನಾಡಿಕೊಳ್ಳುತ್ತಿದ್ದರು.",
			"26": "<sup>26</sup> ದಾವೀದನು, “ಜೀವಸ್ವರೂಪನಾದ ದೇವರ ಸೈನ್ಯವನ್ನು ಹೀಯಾಳಿಸುವುದಕ್ಕೆ ಸುನ್ನತಿಯಿಲ್ಲದ ಈ ಫಿಲಿಷ್ಟಿಯನು ಎಷ್ಟರವನು? ಇವನನ್ನು ಕೊಂದು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಬಂದಿರುವ ನಿಂದೆಯನ್ನು ತೆಗೆದುಹಾಕುವವನಿಗೆ ಏನು ಸಿಕ್ಕುವುದು ಹೇಳಿರಿ” ಎಂದು ತನ್ನ ಬಳಿಯಲ್ಲಿ ನಿಂತಿದ್ದ ಮನುಷ್ಯರನ್ನು ಕೇಳಿದನು,",
			"27": "<sup>27</sup> ಅದಕ್ಕೆ ಅವರು ಮುಂಚಿನಂತೆ ಇಂಥಿಂಥದು ದೊರಕುವುದೆಂದು ಉತ್ತರಕೊಟ್ಟರು.",
			"28": "<sup>28</sup> ದಾವೀದನು ಜನರ ಸಂಗಡ ಹೀಗೆ ಮಾತನಾಡುತ್ತಿರುವುದನ್ನು ಅವನ ಹಿರಿಯ ಅಣ್ಣನಾದ ಎಲೀಯಾಬನು ಕೇಳಿ ಕೋಪಗೊಂಡು, “ನೀನು ಇಲ್ಲಿಗೆ ಬಂದದ್ದೇಕೆ? ಅಡವಿಯಲ್ಲಿರುವ ನಾಲ್ಕಾರು ಕುರಿಗಳನ್ನು ಯಾರಿಗೊಪ್ಪಿಸಿ ಬಂದಿರುವೆ. ನಿನ್ನ ಸೊಕ್ಕು, ತುಂಟತನವು ನನಗೆ ಗೊತ್ತಿದೆ. ನೀನು ಯುದ್ಧವನ್ನು ನೋಡುವುದಕ್ಕೆ ಬಂದಿದ್ದೀ” ಎಂದು ಅವನನ್ನು ಗದರಿಸಿದನು.",
			"29": "<sup>29</sup> ಆಗ ದಾವೀದನು, “ನಾನೇನು ತಪ್ಪು ಮಾಡಿದೆನು? ಸುಮ್ಮನೆ ಮಾತನಾಡಿದೆನಷ್ಟೆ” ಎಂದು ಹೇಳಿ",
			"30": "<sup>30</sup> ಅವನನ್ನು ಬಿಟ್ಟು ಬೇರೆ ಕಡೆಗೆ ಹೋಗಿ ಹಾಗೆಯೇ ವಿಚಾರಿಸಲು ಜನರು ಮುಂಚಿನಂತೆಯೇ ಉತ್ತರಕೊಟ್ಟರು.",
			"31": "<sup>31</sup> ದಾವೀದನು ಈ ಪ್ರಕಾರ ವಿಚಾರಿಸುತ್ತಿರುವುದನ್ನು ಕೇಳಿದವರು ಸೌಲನಿಗೆ ತಿಳಿಸಿದರು. ಅವನು ದಾವೀದನನ್ನು ಕರೆದುಕೊಂಡು ಬರಲು ಹೇಳಿದನು.",
			"32": "<sup>32</sup> ದಾವೀದನು ಸೌಲನಿಗೆ, “ಆ ಫಿಲಿಷ್ಟಿಯನ ನಿಮಿತ್ತವಾಗಿ ಯಾವನೂ ಧೈರ್ಯಗೆಡಬಾರದು ನಿನ್ನ ಸೇವಕನಾದ ನಾನು ಹೋಗಿ ಅವನೊಡನೆ ಯುದ್ಧಮಾಡುವೆನು” ಅನ್ನಲು,",
			"33": "<sup>33</sup> ಸೌಲನು ಅವನಿಗೆ, “ಅವನೊಡನೆ ಕಾದಾಡಲಾರೆ, ನೀನು ಇನ್ನೂ ಹುಡುಗನು ಅವನಾದರೋ ಚಿಕ್ಕಂದಿನಿಂದಲೇ ಯುದ್ಧವೀರನು” ಎಂದು ಹೇಳಿದನು.",
			"34": "<sup>34</sup> ಆಗ ದಾವೀದನು ಅವನಿಗೆ, “ನಿನ್ನ ಸೇವಕನಾದ ನಾನು ನನ್ನ ತಂದೆಯ ಕುರಿಗಳನ್ನು ಕಾಯುತ್ತಿರುವಾಗ ಸಿಂಹವಾಗಲಿ, ಕರಡಿಯಾಗಲಿ ಹಿಂಡಿನಲ್ಲಿನ ಕುರಿಮರಿಯನ್ನು ಹಿಡಿದುಕೊಂಡು ಹೋಗಲು ಬಂದರೆ",
			"35": "<sup>35</sup> ನಾನು ಒಡನೇ ಬೆನ್ನಟ್ಟಿ, ಅದನ್ನು ಹೊಡೆದು, ಕುರಿಮರಿಯನ್ನು ಕಿತ್ತುಕೊಳ್ಳುತ್ತಿದ್ದೆನು. ಅದು ಹಿಂದಿರುಗಿ ನನ್ನ ಮೇಲೆ ಬೀಳಲು ಬಂದಾಗ ಅದರ ಗದ್ದಹಿಡಿದು ಬಡಿದು ಕೊಂದುಹಾಕುತ್ತಿದ್ದೆನು.",
			"36": "<sup>36</sup> ನಿನ್ನ ಸೇವಕನಿಂದ ಕೊಲ್ಲಲ್ಪಟ್ಟ ಸಿಂಹಕ್ಕೂ, ಕರಡಿಗೂ ಆದ ಗತಿಯೇ ಜೀವಸ್ವರೂಪನಾದ ದೇವರ ಸೈನ್ಯವನ್ನು ನಿಂದಿಸುವಂಥ ಈ ಸುನ್ನತಿಯಿಲ್ಲದ ಫಿಲಿಷ್ಟಿಯನಿಗೂ ಆಗಬೇಕು.",
			"37": "<sup>37</sup> ನನ್ನನ್ನು ಅಂಥ ಸಿಂಹದ ಮತ್ತು ಕರಡಿಯ ಉಗುರುಗಳಿಗೆ ತಪ್ಪಿಸಿದ ಯೆಹೋವನು ಈ ಫಿಲಿಷ್ಟಿಯನ ಕೈಯಿಂದಲೂ ತಪ್ಪಿಸುವನು” ಎಂದನು. ಆಗ ಸೌಲನು ದಾವೀದನಿಗೆ “ಹೋಗು ಯೆಹೋವನು ನಿನ್ನ ಸಂಗಡ ಇರಲಿ” ಎಂದು ಹೇಳಿ,",
			"38": "<sup>38</sup> ಅವನಿಗೆ ತನ್ನ ಯುದ್ಧ ವಸ್ತ್ರಗಳನ್ನು, ಕವಚವನ್ನೂ ತೊಣಿಸಿ, ತಲೆಗೊಂದು ತಾಮ್ರದ ಶಿರಸ್ತ್ರಾಣವನ್ನಿಟ್ಟನು.",
			"39": "<sup>39</sup> ದಾವೀದನು ಯುದ್ಧ ವಸ್ತ್ರದ ಮೇಲೆ ಕತ್ತಿಯನ್ನೂ ಬಿಗಿದುಕೊಂಡ ನಂತರ ನಡೆಯಲು ಸಾಧ್ಯವಾಗುತ್ತದೆಯೇ ಎಂದು ಪರೀಕ್ಷಿಸಿದ ಮೇಲೆ ಸೌಲನಿಗೆ, “ನನಗೆ ಅಭ್ಯಾಸವಿಲ್ಲದೆ ಇರುವುದರಿಂದ ಇವುಗಳನ್ನು ಧರಿಸಿಕೊಂಡು ನಡೆಯಲಾರೆ” ಎಂದು ಹೇಳಿ ಅವುಗಳನ್ನು ತೆಗೆದಿಟ್ಟು",
			"40": "<sup>40</sup> ತನ್ನ ಕೋಲನ್ನು ತೆಗೆದುಕೊಂಡು ಹೊರಟು, ಹಳ್ಳದಲ್ಲಿನ ಐದು ನುಣುಪುಕಲ್ಲುಗಳನ್ನು ಆರಿಸಿಕೊಂಡು, ಅವುಗಳನ್ನು ಕುರುಬರ ಪದ್ಧತಿಯಂತೆ ತನಗಿರುವ ಸೊಂಟದ ಚೀಲದಲ್ಲಿ ಹಾಕಿ ಕೈಯಲ್ಲಿ ಕವಣೆಯನ್ನು ಹಿಡಿದು ಫಿಲಿಷ್ಟಿಯನ ಬಳಿಗೆ ಹೋದನು.",
			"41": "<sup>41</sup> ಇತ್ತ ಫಿಲಿಷ್ಟಿಯನೂ ದಾವೀದನ ಸಮೀಪಕ್ಕೆ ಬಂದನು. ಗುರಾಣಿ ಹೊರುವವರು ಅವನ ಮುಂದೆ ಇದ್ದರು.",
			"42": "<sup>42</sup> ಫಿಲಿಷ್ಟಿಯನು ಕೆಂಬಣ್ಣದವನೂ, ಸುಂದರನೂ, ಯೌವನಸ್ಥನೂ ಆದ ದಾವೀದನನ್ನು ನೋಡಿ ತಿರಸ್ಕಾರದಿಂದ ಅವನಿಗೆ,",
			"43": "<sup>43</sup> “ನೀನು ಕೋಲುಹಿಡಿದು ನನ್ನ ಬಳಿಗೆ ಬರುವುದೇನು? ನಾನು ನಾಯಿಯೋ” ಎಂದು ಹೇಳಿ ಅವನನ್ನು ತನ್ನ ದೇವರ ಹೆಸರಿನಲ್ಲಿ ಶಪಿಸಿ,",
			"44": "<sup>44</sup> “ಇಲ್ಲಿ ಬಾ, ನಿನ್ನ ಮಾಂಸವನ್ನು ಮೃಗ ಪಕ್ಷಿಗಳಿಗೆ ಹಂಚಿಕೊಡುತ್ತೇನೆ” ಅಂದನು.",
			"45": "<sup>45</sup> ಆಗ ದಾವೀದನು ಅವನಿಗೆ, “ನೀನು ಈಟಿ, ಕತ್ತಿ, ಬರ್ಜಿಗಳೊಡನೆ ನನ್ನ ಬಳಿಗೆ ಬರುತ್ತೀ. ನಾನಾದರೋ ನೀನು ಹೀಯಾಳಿಸಿದಂಥ ಸೇನಾಧೀಶ್ವರನೂ, ಇಸ್ರಾಯೇಲ್ಯರ ಯುದ್ಧಭಟರ ದೇವರೂ ಆಗಿರುವ ಯೆಹೋವನ ನಾಮದೊಡನೆ ನಿನ್ನ ಬಳಿಗೆ ಬಂದಿದ್ದೇನೆ.",
			"46": "<sup>46</sup> ಆತನು ಈ ಹೊತ್ತು ನಿನ್ನನ್ನು ನನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸಿಕೊಡುವನು. ನಾನು ನಿನ್ನನ್ನು ಕೊಂದು ನಿನ್ನ ತಲೆಯನ್ನು ಕಡಿದು ಹಾಕಿ ಫಿಲಿಷ್ಟಿಯ ಸೈನ್ಯದ ಶವಗಳನ್ನು ಮೃಗಪಕ್ಷಿಗಳಿಗೆ ಹಂಚಿಕೊಡುವೆನು. ಇದರಿಂದ ಇಸ್ರಾಯೇಲರೊಳಗೆ ದೇವರಿದ್ದಾನೆಂಬುದು ಭೂಲೋಕದವರಿಗೆಲ್ಲಾ ತಿಳಿದುಬರುವುದು.",
			"47": "<sup>47</sup> ಯೆಹೋವನು ಈಟಿಕತ್ತಿಗಳಿಲ್ಲದೇ ರಕ್ಷಿಸಬಲ್ಲನೆಂಬುದು ಇಲ್ಲಿ ಕೂಡಿರುವವರಿಗೆಲ್ಲಾ ತಿಳಿದುಬರುವುದು. ಯಾಕೆಂದರೆ ಯುದ್ಧಫಲವು ಯೆಹೋವನ ಕೈಯಲ್ಲಿದೆ. ಆತನು ನಿಮ್ಮನ್ನು ನಮ್ಮ ಕೈಗೆ ಒಪ್ಪಿಸಿಕೊಡುವನು” ಅಂದನು.",
			"48": "<sup>48</sup> ಕೂಡಲೇ ಆ ಫಿಲಿಷ್ಟಿಯನು ಹೊರಟು ದಾವೀದನೊಡನೆ ಯುದ್ಧಮಾಡುವುದಕ್ಕೆ ಸಮೀಪಿಸಿದಾಗ ದಾವೀದನು ಫಿಲಿಷ್ಟಿಯರ ಸೈನ್ಯದ ಕಡೆಗೆ ಓಡಿಹೋಗಿ ಆ ಫಿಲಿಷ್ಟಿಯನನ್ನು ಸಂಧಿಸಿ,",
			"49": "<sup>49</sup> ಚೀಲದಲ್ಲಿ ಕೈ ಹಾಕಿ, ಒಂದು ಕಲ್ಲನ್ನು ತೆಗೆದು ಅವನ ಹಣೆಗೆ ಗುರಿಯಿಟ್ಟು ಕವಣೆಯನ್ನು ಬೀಸಿ ಹೊಡೆಯಲು ಕಲ್ಲು ಅವನ ಹಣೆಯೊಳಗೆ ಹೊಕ್ಕಿತು. ಅವನು ಬೋರಲುಬಿದ್ದನು",
			"50": "<sup>50</sup> ಕೂಡಲೇ ಅವನು ಓಡಿಹೋಗಿ ಆ ಫಿಲಿಷ್ಟಿಯನ ಮೇಲೆ ನಿಂತು ಅವನ ಕತ್ತಿಯನ್ನು ತೆಗೆದುಕೊಂಡು, ಅದರಿಂದಲೇ ಅವನ ತಲೆಯನ್ನು ಕತ್ತರಿಸಿ, ಅವನನ್ನು ಸಾಯಿಸಿದನು.",
			"51": "<sup>51</sup> ಈ ಪ್ರಕಾರ ದಾವೀದನು ಕತ್ತಿಯಿಂದಲ್ಲ ಬರೀ ಒಂದು ಕವಣೆಯ ಕಲ್ಲಿನಿಂದ ಫಿಲಿಷ್ಟಿಯನನ್ನು ಸೋಲಿಸಿ, ಕೊಂದು ಹಾಕಿದನು ಫಿಲಿಷ್ಟಿಯರು ತಮ್ಮ ರಣವೀರನು ಸತ್ತುಹೋದದ್ದನ್ನು ಕಂಡು ಓಡಿಹೋದರು.",
			"52": "<sup>52</sup> ಇಸ್ರಾಯೇಲರು ಯೆಹೂದ್ಯರು ಎದ್ದು ಆರ್ಭಟಿಸಿ ಫಿಲಿಷ್ಟಿಯರನ್ನು ತಗ್ಗಿನ ದಾರಿಯವರೆಗೂ ಎಕ್ರೋನಿನ ಬಾಗಿಲುಗಳವರೆಗೂ ಹಿಂದಟ್ಟಿದರು. ಫಿಲಿಷ್ಟಿಯರ ಹೆಣಗಳು ಶಾರಯಿಮಿನಿಂದ ಗತ್ ಎಕ್ರೋನ್ ಪಟ್ಟಣಗಳವರೆಗೂ ಬಿದ್ದಿದ್ದವು.",
			"53": "<sup>53</sup> ಅನಂತರ ಇಸ್ರಾಯೇಲರು ಹಿಂತಿರುಗಿ ಬಂದು ಫಿಲಿಷ್ಟಿಯರ ಪಾಳೆಯವನ್ನು ಸೂರೆಮಾಡಿದರು.",
			"54": "<sup>54</sup> ದಾವೀದನು ಆ ಫಿಲಿಷ್ಟಿಯನ ತಲೆಯನ್ನು ಯೆರೂಸಲೇಮಿಗೆ ಒಯ್ದನು. ಅವನ ಆಯುಧಗಳನ್ನು ತನ್ನ ಗುಡಾರದಲ್ಲಿಟ್ಟುಕೊಂಡನು.",
			"55": "<sup>55</sup> ದಾವೀದನು ಫಿಲಿಷ್ಟಿಯರೊಡನೆ ಯುದ್ಧಮಾಡುವುದಕ್ಕೆ ಹೊರಟದ್ದನ್ನು ಸೌಲನು ಕಂಡು ತನ್ನ ಸೇನಾಪತಿಯಾದ ಅಬ್ನೇರನನ್ನು, “ಅಬ್ನೇರನೇ ಈ ಹುಡುಗನು ಯಾರ ಮಗನು?” ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ ಅವನು ಸೌಲನಿಗೆ, “ಅರಸನೇ ನಿನ್ನ ಜೀವದಾಣೆ ನನಗೆ ಗೊತ್ತಿಲ್ಲ” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"56": "<sup>56</sup> ಆಗ ಅರಸನು ಅವನಿಗೆ, “ಆ ಪ್ರಾಯಸ್ಥನು ಯಾರ ಮಗನೆಂದು ವಿಚಾರಿಸು” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"57": "<sup>57</sup> ದಾವೀದನು ಫಿಲಿಷ್ಟಿಯನನ್ನು ಕೊಂದು ಅವನ ತಲೆಯನ್ನು ಹಿಡಿದುಕೊಂಡು ಹಿಂದಿರುಗಿ ಬರುವಾಗ ಅಬ್ನೇರನು ಅವನನ್ನು ಕರೆದು ಸೌಲನ ಮುಂದೆ ನಿಲ್ಲಿಸಿದನು.",
			"58": "<sup>58</sup> ಸೌಲನು ಅವನನ್ನು, “ಹುಡುಗನೇ ನೀನು ಯಾರ ಮಗನು?” ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ ಅವನು, “ನಾನು ಬೇತ್ಲೆಹೇಮಿನವನೂ ನಿನ್ನ ಸೇವಕನೂ ಆದ ಇಷಯನ ಮಗನು” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು."
		},
		"18": {
			"1": "<sup>1</sup> ದಾವೀದನು ಸೌಲನೊಡನೆ ಮಾತನಾಡಿ ಮುಗಿಸಿದ ಮೇಲೆ ಯೋನಾತಾನನ ಪ್ರಾಣವು ದಾವೀದನ ಪ್ರಾಣದೊಡನೆ ಒಂದಾಯಿತು. ಅವನು ದಾವೀದನನ್ನು ತನ್ನ ಪ್ರಾಣದಂತೆಯೇ ಪ್ರೀತಿಸತೊಡಗಿದನು.",
			"2": "<sup>2</sup> ಆ ದಿನದಿಂದ ಸೌಲನು ದಾವೀದನನ್ನು ಅವನ ತಂದೆಯ ಮನೆಗೆ ಹೋಗಗೊಡದೇ, ತನ್ನ ಬಳಿಯಲ್ಲೇ ಇಟ್ಟುಕೊಂಡನು.",
			"3": "<sup>3</sup> ಯೋನಾತಾನನು ದಾವೀದನನ್ನು ತನ್ನ ಪ್ರಾಣದಂತೆಯೇ ಪ್ರೀತಿಸಿದ್ದರಿಂದ ಅವನೊಡನೆ ಸ್ನೇಹದ ಒಪ್ಪಂದ ಮಾಡಿಕೊಂಡನು.",
			"4": "<sup>4</sup> ಇದಲ್ಲದೆ ಅವನು ತನ್ನ ಮೈಮೇಲಿದ್ದ ನಿಲುವಂಗಿಯನ್ನು ತನ್ನ ಯುದ್ಧವಸ್ತ್ರಗಳನ್ನು, ಕತ್ತಿ, ಬಿಲ್ಲು, ನಡುಕಟ್ಟುಗಳನ್ನೂ ದಾವೀದನಿಗೆ ಕೊಟ್ಟನು.",
			"5": "<sup>5</sup> ದಾವೀದನಾದರೋ ಸೌಲನು ಎಲ್ಲಿಗೆ ಕಳುಹಿಸಿದರೂ ಹೋಗಿ ಎಲ್ಲವನ್ನು ವಿವೇಕದಿಂದ ನಡೆಸುತ್ತಿದ್ದನು. ಸೌಲನು ಇವನನ್ನು ಸೇನಾಧಿಪತಿಯನ್ನಾಗಿ ನೇಮಿಸಿದನು. ಇದು ಅವನ ಸೇವಕರಿಗೂ, ಎಲ್ಲಾ ಜನರಿಗೂ ಮೆಚ್ಚಿಗೆಯಾಯಿತು.",
			"6": "<sup>6</sup> ದಾವೀದನು ಫಿಲಿಷ್ಟಿಯನನ್ನು ಸಂಹರಿಸಿ, ಸರ್ವಜನರೊಡನೆ ಹಿಂದಿರುಗಿ ಬರುವಾಗ ಇಸ್ರಾಯೇಲ್ಯರ ಎಲ್ಲಾ ಪಟ್ಟಣಗಳಿಂದ ಸ್ತ್ರೀಯರೂ ಹೊರಗೆ ಬಂದು ದಮ್ಮಡಿತಾಳಗಳನ್ನು ಹಿಡಿದು, ಸಂತೋಷದಿಂದ ಹಾಡುತ್ತಾ, ಕುಣಿಯುತ್ತಾ, ಅರಸನಾದ ಸೌಲನನ್ನು ಎದುರುಗೊಂಡರು.",
			"7": "<sup>7</sup> ಅವರು ಹಾಡುತ್ತಾ, ಕೊಂಡಾಡುತ್ತಾ, “ಸೌಲನು ಸಾವಿರ ಶತ್ರುಗಳನ್ನು ಕೊಂದನು. ದಾವೀದನು ಹತ್ತು ಸಾವಿರಾರು ಶತ್ರುಗಳನ್ನು ಕೊಂದನು” ಎಂದು ಹಾಡಿದರು.",
			"8": "<sup>8</sup> ಈ ಮಾತುಗಳ ದೆಸೆಯಿಂದ ಸೌಲನಿಗೆ ಬಹು ಅಸೂಯೆ ಉಂಟಾಗಿ ದಾವೀದನ ಮೇಲೆ ಕೋಪಗೊಂಡನು. ಅವನು “ದಾವೀದನು ಹತ್ತು ಸಾವಿರಾರು ಶತ್ರುಗಳನ್ನು ಕೊಂದನೆಂದೂ, ನಾನು ಸಾವಿರಾರು ಶತ್ರುಗಳನ್ನು ಕೊಂದೆನೆಂದೂ ಹಾಡುತ್ತಾರಲ್ಲಾ, ರಾಜತ್ವದ ಹೊರತು ಅವನಿಗೆ ಇನ್ನೇನು ಕಡಿಮೆಯಾಯಿತು” ಅಂದುಕೊಂಡು",
			"9": "<sup>9</sup> ಅಂದಿನಿಂದ ಅವನು ದಾವೀದನನ್ನು ಸಂಶಯದಿಂದ ನೋಡುತ್ತಿದ್ದನು.",
			"10": "<sup>10</sup> ಮರುದಿನ ದೇವರಿಂದ ಕಳುಹಿಸಲ್ಪಟ್ಟ ದುರಾತ್ಮವು ಸೌಲನ ಮೇಲೆ ಬಂದದ್ದರಿಂದ ಅವನು ಬುದ್ಧಿಗೆಟ್ಟು ಮನೆಯೊಳಗೆ ಕೂಗುತ್ತಿದ್ದನು. ಅವನ ಕೈಯಲ್ಲಿ ಒಂದು ಈಟಿ ಇತ್ತು. ದಾವೀದನು ವಾಡಿಕೆಯ ಪ್ರಕಾರ ಕಿನ್ನರಿಯನ್ನು ನುಡಿಸುತ್ತಾ ಇದ್ದನು.",
			"11": "<sup>11</sup> ಆಗ ಸೌಲನು ದಾವೀದನನ್ನು ಗೋಡೆಗೆ ಸೇರಿಕೊಳ್ಳುವ ಹಾಗೆ ತಿವಿಯುವೆನು ಎಂದುಕೊಂಡು ಈಟಿಯನ್ನು ಎಸೆದನು. ದಾವೀದನು ಎರಡು ಸಾರಿ ತಪ್ಪಿಸಿಕೊಂಡನು.",
			"12": "<sup>12</sup> ಯೆಹೋವನು ಸೌಲನನ್ನು ಬಿಟ್ಟು ದಾವೀದನ ಸಂಗಡ ಇದ್ದುದರಿಂದ ಸೌಲನು ದಾವೀದನಿಗೆ ಭಯಪಟ್ಟು,",
			"13": "<sup>13</sup> ಅವನನ್ನು ತನ್ನ ಸಾನಿಧ್ಯಸೇವೆಯಿಂದ ತಪ್ಪಿಸಿ, ಸೈನ್ಯದಲ್ಲಿ ಸಹಸ್ರಾಧಿಪತಿಯನ್ನಾಗಿ ನೇಮಿಸಿದನು. ಹೀಗೆ ದಾವೀದನು ಜನರ ಸಂಗಡ ಹೋಗುತ್ತಾ ಬರುತ್ತಾ ಇದ್ದನು.",
			"14": "<sup>14</sup> ಯೆಹೋವನು ಅವನ ಸಂಗಡ ಇದ್ದದರಿಂದ ದಾವೀದನು ಎಲ್ಲಾ ಕಾರ್ಯಗಳನ್ನು ವಿವೇಕದಿಂದ ನಡೆಸುತ್ತಿದ್ದನು.",
			"15": "<sup>15</sup> ಇದನ್ನು ನೋಡಿ ಸೌಲನು ದಾವೀದನಿಗೆ ಬಹಳವಾಗಿ ಹೆದರಿಕೊಂಡನು.",
			"16": "<sup>16</sup> ಆದರೆ ಇಸ್ರಾಯೇಲರೂ, ಯೆಹೂದ್ಯರೂ ತಮ್ಮ ಸಂಗಡ ಹೋಗುತ್ತಾ ಬರುತ್ತಾ ಇದ್ದ ದಾವೀದನನ್ನು ಬಹಳವಾಗಿ ಪ್ರೀತಿಸುತ್ತಿದ್ದರು.",
			"17": "<sup>17</sup> ಒಂದು ದಿನ ಸೌಲನು, ಇವನು ನನ್ನ ಕೈಯಿಂದಲ್ಲ ಫಿಲಿಷ್ಟಿಯರ ಕೈಯಿಂದ ಸಾಯಲಿ ಎಂದು ಆಲೋಚಿಸಿಕೊಂಡು ದಾವೀದನಿಗೆ, “ನನ್ನ ವೀರನಾಗಿ ಹೋಗಿ ಯೆಹೋವನ ಯುದ್ಧಗಳನ್ನು ನಡಿಸು. ನಾನು ನನ್ನ ಹಿರೀ ಮಗಳಾದ ಮೇರಬಳನ್ನು ನಿನಗೆ ಮದುವೆ ಮಾಡಿಕೊಡುತ್ತೇನೆ” ಅಂದನು.",
			"18": "<sup>18</sup> ಅದಕ್ಕೆ ದಾವೀದನು “ಅರಸನ ಅಳಿಯನಾಗುವುದಕ್ಕೆ ನಾನು ಎಷ್ಟರವನು? ಇಸ್ರಾಯೇಲರಲ್ಲಿ ನನ್ನ ತಂದೆಯ ಕುಟುಂಬವೂ ಸಮಾನವೇ?” ಎಂದನು.",
			"19": "<sup>19</sup> ಸೌಲನು ತನ್ನ ಮಗಳಾದ ಮೇರಬಳನ್ನು ದಾವೀದನಿಗೆ ಕೊಡತಕ್ಕ ಕಾಲ ಬಂದಾಗ ಅವನು ಅವಳನ್ನು ಮೆಹೋಲದ ಅದ್ರಿಯೇಲ ಎಂಬವನಿಗೆ ಮದುವೆಮಾಡಿ ಕೊಟ್ಟನು.",
			"20": "<sup>20</sup> ಸೌಲನ ಮಗಳಾದ ಮೀಕಲಳು ದಾವೀದನನ್ನು ಪ್ರೀತಿಸಿದಳು. ಇದು ಸೌಲನಿಗೆ ತಿಳಿದಾಗ ಅವನಿಗೆ ಸಂತೋಷವಾಯಿತು.",
			"21": "<sup>21</sup> ಅವಳು ದಾವೀದನಿಗೆ ಉರಲಾಗಿರುವಂತೆಯೂ, ಅವನು ಫಿಲಿಷ್ಟಿಯರ ಕೈಗೆ ಸಿಕ್ಕಿಬೀಳುವಂತೆಯೂ ಇವಳನ್ನು ಅವನಿಗೆ ಕೊಡುವೆನು ಅಂದುಕೊಂಡು ಅವನಿಗೆ, “ನೀನು ನನ್ನ ಅಳಿಯನಾಗುವುದಕ್ಕೆ ಈಗ ಎರಡನೆಯ ಸಂದರ್ಭವುಂಟಾಯಿತು” ಎಂದು ಹೇಳಿದನು.",
			"22": "<sup>22</sup> ಅವನು ತನ್ನ ಸೇವಕರಿಗೆ, “ನೀವು ದಾವೀದನಿಗೆ ಗುಪ್ತವಾಗಿ, ‘ಅರಸನಿಗೆ ನಿನ್ನನ್ನು ಒಲಿಯುತ್ತಾನೆ, ಅವನ ಸೇವಕರು ನಿನ್ನನ್ನು ಪ್ರೀತಿಸುತ್ತಾರೆ. ನೀನು ಅರಸನ ಅಳಿಯನಾಗು’ ಎಂದು ಹೇಳಿರಿ” ಅಂದನು.",
			"23": "<sup>23</sup> ಆ ಸೇವಕರು ದಾವೀದನಿಗೆ ಹಾಗೆಯೇ ಹೇಳಿದಾಗ ಅವನು, “ಅರಸನ ಅಳಿಯನಾಗುವುದು ಸಾಧಾರಣವಾದ ಸಂಗತಿಯೆಂದು ನೆನಸುತ್ತೀರೋ? ನಾನು ದರಿದ್ರನೂ, ಅಲ್ಪನೂ ಆಗಿರುತ್ತೆನಲ್ಲಾ” ಎಂದು ಉತ್ತರ ಕೊಟ್ಟನು.",
			"24": "<sup>24</sup> ಸೇವಕರು ದಾವೀದನ ಮಾತುಗಳನ್ನು ಸೌಲನಿಗೆ ತಿಳಿಸಿದರು.",
			"25": "<sup>25</sup> ಆಗ ಅವನು ಅವರಿಗೆ, “ನೀವು ಹೋಗಿ ದಾವೀದನಿಗೆ, ‘ಅರಸನು ತೆರವನ್ನು ಅಪೇಕ್ಷಿಸುವುದಿಲ್ಲ. ತನ್ನ ಶತ್ರುಗಳಾದ ಫಿಲಿಷ್ಟಿಯರಿಗೆ ಮುಯ್ಯಿತೀರಿಸಿ ಅವರಲ್ಲಿನ ನೂರು ಮಂದಿಯ ಮುಂದೊಗಲನ್ನು ತಂದುಕೊಡಬೇಕು ಅನ್ನುತ್ತಾನೆ’ ಎಂದು ಹೇಳಿರಿ” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು. ದಾವೀದನನ್ನು ಫಿಲಿಷ್ಟಿಯರಿಂದ ಕೊಲ್ಲಿಸಬೇಕೆಂಬುದೇ ಅವನ ಉದ್ದೇಶವಾಗಿತ್ತು.",
			"26": "<sup>26</sup> ಸೇವಕರು ದಾವೀದನಿಗೆ ಈ ಮಾತುಗಳನ್ನು ತಿಳಿಸಿದಾಗ ಅವನು ಅರಸನ ಅಳಿಯನಾಗುವುದಕ್ಕೆ ಒಪ್ಪಿದನು. ನೇಮಕವಾದ ದಿನಗಳು ಮುಗಿಯುವ ಮೊದಲೇ,",
			"27": "<sup>27</sup> ದಾವೀದನು ತನ್ನ ಸೇವಕರೊಡನೆ ಹೊರಟು, ಫಿಲಿಷ್ಟಿಯರ ದೇಶಕ್ಕೆ ಹೋಗಿ, ಅಲ್ಲಿನ ಇನ್ನೂರು ಜನರನ್ನು ಕೊಂದು, ಮುಂದೊಗಲುಗಳನ್ನು ತಂದು, ಅರಸನ ಅಳಿಯನಾಗುವುದಕ್ಕೋಸ್ಕರ ಅವುಗಳನ್ನು ಪೂರ್ಣವಾಗಿ ಅವನಿಗೆ ಒಪ್ಪಿಸಿದನು. ಆಗ ಸೌಲನು ತನ್ನ ಮಗಳಾದ ಮೀಕಲಳನ್ನು ಅವನಿಗೆ ಮದುವೆಮಾಡಿಕೊಟ್ಟನು.",
			"28": "<sup>28</sup> ಆಕೆಯು ಅವನನ್ನು ಬಹಳವಾಗಿ ಪ್ರೀತಿಸುವವಳಾದಳು. ಯೆಹೋವನು ದಾವೀದನ ಸಂಗಡ ಇದ್ದಾನೆಂದು,",
			"29": "<sup>29</sup> ಸೌಲನು ತಿಳಿದು ಅವನಿಗೆ ಮತ್ತಷ್ಟು ಹೆದರುವವನಾಗಿ ಅವನ ನಿತ್ಯವೈರಿಯಾದನು.",
			"30": "<sup>30</sup> ಫಿಲಿಷ್ಟಿಯ ಪ್ರಭುಗಳು ಯುದ್ಧಕ್ಕೆ ಬಂದಾಗೆಲ್ಲಾ ಸೌಲನ ಸೇನಾಧಿಪತಿಗಳಲ್ಲಿ ದಾವೀದನೇ ಹೆಚ್ಚು ಪ್ರಭಾವಶಾಲಿಯಾಗಿ ಬರುತ್ತಿದ್ದುದರಿಂದ ಅವನ ಹೆಸರು ಬಹು ಸುಪ್ರಸಿದ್ಧವಾಯಿತು."
		},
		"19": {
			"1": "<sup>1</sup> ಸೌಲನು ದಾವೀದನನ್ನು ಕೊಲ್ಲಬೇಕೆಂದು ತನ್ನ ಮಗನಾದ ಯೋನಾತಾನನಿಗೂ, ಎಲ್ಲಾ ಪರಿವಾರದವರಿಗೂ ಆಜ್ಞಾಪಿಸಿದನು. ಆದರೆ ಯೋನಾತಾನನು ದಾವೀದನನ್ನು ಬಹಳವಾಗಿ ಪ್ರೀತಿಸುತ್ತಿದ್ದನು.",
			"2": "<sup>2</sup> ಆದ್ದರಿಂದ ಅವನು ದಾವೀದನಿಗೆ, “ನನ್ನ ತಂದೆಯಾದ ಸೌಲನು ನಿನ್ನನ್ನು ಕೊಲ್ಲಬೇಕೆಂದಿರುತ್ತಾನೆ. ನೀನು ಜಾಗರೂಕತೆಯಿಂದಿರು. ನಾಳೆ ಬೆಳಿಗ್ಗೆ ಒಂದು ಗುಪ್ತವಾದ ಸ್ಥಳದಲ್ಲಿ ಅಡಗಿಕೊಂಡಿರು.",
			"3": "<sup>3</sup> ನೀನು ಅಡಗಿಕೊಂಡಿರುವ ಹೊಲಕ್ಕೆ ನಾನು ಬಂದು, ನನ್ನ ತಂದೆಯ ಬಳಿಯಲ್ಲಿ ನಿಂತು, ನಿನ್ನ ವಿಷಯವನ್ನು ಮಾತನಾಡಿ, ಅವನಿಂದ ತಿಳಿದು ಬರುವ ವಿಚಾರವನ್ನು ನಿನಗೆ ತಿಳಿಸುವೆನು” ಎಂದು ಹೇಳಿದನು.",
			"4": "<sup>4</sup> ತರುವಾಯ ಅವನು ತನ್ನ ತಂದೆಯ ಮುಂದೆ ದಾವೀದನನ್ನು ಹೊಗಳಿ, “ಒಡೆಯನು ತನ್ನ ಸೇವಕನಾದ ದಾವೀದನಿಗೆ ಅನ್ಯಾಯ ಮಾಡದಿರಲಿ. ಅವನು ನಿನಗೆ ದ್ರೋಹ ಮಾಡಲಿಲ್ಲ. ಅವನು ಮಾಡಿದ್ದೆಲ್ಲವೂ ನಿನ್ನ ಹಿತಕ್ಕಾಗಿಯೇ.",
			"5": "<sup>5</sup> ತನ್ನ ಜೀವವನ್ನು ಕೈಯಲ್ಲಿ ಹಿಡಿದುಕೊಂಡು ಹೋಗಿ ಫಿಲಿಷ್ಟಿಯನನ್ನು ಕೊಂದನು. ಯೆಹೋವನು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಮಹಾ ಜಯವನ್ನುಂಟುಮಾಡಿದನು. ನೀನು ಅದನ್ನು ನೋಡಿ ಸಂತೋಷಪಟ್ಟಿಯಲ್ಲಾ. ದಾವೀದನನ್ನು ನಿಷ್ಕರುಣೆಯಾಗಿ ಕೊಂದು, ನಿರಪರಾಧಿಯ ರಕ್ತವನ್ನು ಸುರಿಸಿ, ಅಪರಾಧಕ್ಕೆ ನೀನೇಕೆ ಗುರಿಯಾಗುತ್ತೀ?” ಅಂದನು.",
			"6": "<sup>6</sup> ಸೌಲನು ಯೋನಾತಾನನ ಮಾತುಗಳನ್ನು ಲಾಲಿಸಿ, “ಯೆಹೋವನಾಣೆ, ಅವನನ್ನು ಕೊಲ್ಲುವುದಿಲ್ಲ” ಎಂದು ಪ್ರಮಾಣಮಾಡಿದನು.",
			"7": "<sup>7</sup> ಆಗ ಯೋನಾತಾನನು ದಾವೀದನನ್ನು ಕರೆದು, ಅವನಿಗೆ ಈ ಮಾತುಗಳನ್ನು ತಿಳಿಸಿ, ಸೌಲನ ಬಳಿಗೆ ಕರೆದುಕೊಂಡು ಬಂದನು. ದಾವೀದನು ಮುಂಚಿನಂತೆ ಸೌಲನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಇರುವವನಾದನು.",
			"8": "<sup>8</sup> ಫಿಲಿಷ್ಟಿಯರು ತಿರುಗಿ ಯುದ್ಧಕ್ಕೆ ಬಂದಾಗ ದಾವೀದನು ಹೋಗಿ ಅವರೊಡನೆ ಯುದ್ಧಮಾಡಿ ಅವರನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ಸೋಲಿಸಿ, ಓಡಿಸಿಬಿಟ್ಟನು.",
			"9": "<sup>9</sup> ಒಂದು ದಿನ ಸೌಲನು ಕೈಯಲ್ಲಿ ಈಟಿಯನ್ನು ಹಿಡಿದುಕೊಂಡು ತನ್ನ ಮನೆಯಲ್ಲಿ ಕುಳಿತುಕೊಂಡಿದ್ದಾಗ ಯೆಹೋವನಿಂದ ಕಳುಹಿಸಲ್ಪಟ್ಟ ದುರಾತ್ಮವು ಅವನ ಮೇಲೆ ಬಂದಿತು.",
			"10": "<sup>10</sup> ಅವನು ಒಡನೆ ಕಿನ್ನರಿಯನ್ನು ನುಡಿಸುತ್ತಿದ್ದ ದಾವೀದನನ್ನು ಗೋಡೆಗೆ ಹತ್ತಿಕೊಳ್ಳುವಂತೆ ತಿವಿಯಬೇಕೆಂದು ತನ್ನ ಈಟಿಯನ್ನು ಎಸೆದನು. ಆದರೆ ದಾವೀದನು ಪಕ್ಕನೆ ಸೌಲನೆದುರಿನಿಂದ ಸರಿದುಕೊಂಡ ಕಾರಣ, ಅದು ಗೋಡೆಯೊಳಗೆ ನಾಟಿತು. ದಾವೀದನು ಆ ರಾತ್ರಿಯಲ್ಲೇ ತಪ್ಪಿಸಿಕೊಂಡು ಓಡಿಹೋದನು.",
			"11": "<sup>11</sup> ಸೌಲನು ಕೂಡಲೇ ದೂತರನ್ನು ಕರಿಸಿ ದಾವೀದನ ಮನೆಯ ಸುತ್ತಲೂ ಹೊಂಚುಹಾಕಿ ಅವನನ್ನು ಬೆಳಗಾಗುವಷ್ಟರಲ್ಲಿ ಕೊಂದುಹಾಕಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿ ಕಳುಹಿಸಿದನು. ಮೀಕಲಳು ತನ್ನ ಗಂಡನಾದ ದಾವೀದನಿಗೆ, “ನೀನು ಈ ರಾತ್ರಿಯೇ ತಪ್ಪಿಸಿಕೊಳ್ಳದಿದ್ದರೆ ನಾಳೆ ಬೆಳಿಗ್ಗೆ ಹತನಾಗುವಿ” ಎಂದು ಹೇಳಿ",
			"12": "<sup>12</sup> ಅವನನ್ನು ಕಿಟಕಿಯಿಂದ ಕೆಳಕ್ಕಿಳಿಸಲು ಅವನು ಓಡಿಹೋಗಿ ತಪ್ಪಿಸಿಕೊಂಡನು.",
			"13": "<sup>13</sup> ಅನಂತರ ಆಕೆಯು ಮನೆಯಲ್ಲಿದ್ದ ಒಂದು ವಿಗ್ರಹವನ್ನು ತಂದು, ಹಾಸಿಗೆಯ ಮೇಲೆ ಮಲಗಿಸಿ, ಅದರ ಮುಖದ ಮೇಲೆ ಮೇಕೆಕೂದಲಿನಿಂದ ಹೆಣೆದ ಜಾಲರಿಯನ್ನು ತಲೆದಿಂಬಿನ ಜಾಗದಲ್ಲಿ ಹಾಕಿ ಕಂಬಳಿಯನ್ನು ಹೊದಿಸಿದಳು.",
			"14": "<sup>14</sup> ಸೌಲನ ದೂತರು ದಾವೀದನನ್ನು ಹಿಡಿಯುವುದಕ್ಕೆ ಬಂದಾಗ ಅವಳು ಅವನಿಗೆ ಸ್ವಸ್ಥವಿಲ್ಲವೆಂದು ಹೇಳಿಕಳುಹಿಸಿದಳು.",
			"15": "<sup>15</sup> ಸೌಲನು ತಿರುಗಿ ತನ್ನ ಆಳುಗಳನ್ನು, “ನೀವು ಹೋಗಿ ನೋಡಿ ದಾವೀದನನ್ನು ಹಾಸಿಗೆಯೊಡನೆ ನನ್ನ ಬಳಿಗೆ ತೆಗೆದುಕೊಂಡು ಬನ್ನಿರಿ, ಕೊಂದುಹಾಕುತ್ತೇನೆ” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"16": "<sup>16</sup> ಅವರು ಹೋಗಿ ನೋಡಲು ಹಾಸಿಗೆಯ ಮೇಲಿರುವುದು ಮೇಕೆಕೂದಲಿನಿಂದ ಹೆಣೆದ ಜಾಲರಿಯ ಒಂದು ವಿಗ್ರಹ ಎಂದು ತಿಳಿದುಬಂತು.",
			"17": "<sup>17</sup> ಆಗ ಸೌಲನು ಮೀಕಲಳಿಗೆ, “ನೀನು ನನ್ನನ್ನು ವಂಚಿಸಿದ್ದೇಕೆ? ನನ್ನ ಶತ್ರುವನ್ನು ಕಳುಹಿಸಿ ಅವನು ತಪ್ಪಿಸಿಕೊಳ್ಳುವಂತೆ ಮಾಡಿದಿಯಲ್ಲಾ” ಎನ್ನಲು ಆಕೆಯು ಅವನಿಗೆ, “ನನ್ನನ್ನು ಕಳುಹಿಸದಿದ್ದರೆ ನಿನ್ನನ್ನು ಕೊಂದುಬಿಡುತ್ತೇನೆ ಎಂದು ಅವನು ನನಗೆ ಹೇಳಿದನು” ಅಂದಳು.",
			"18": "<sup>18</sup> ದಾವೀದನು ತಪ್ಪಿಸಿಕೊಂಡು ರಾಮದಲ್ಲಿದ್ದ ಸಮುವೇಲನ ಬಳಿಗೆ ಬಂದು ಸೌಲನು ತನಗೆ ಮಾಡಿದ್ದೆಲ್ಲವನ್ನು ತಿಳಿಸಿದನು. ಅವರಿಬ್ಬರೂ ಹೋಗಿ ಅಲ್ಲಿನ ನಯೋತ್ ಎಂಬ ಸ್ಥಳದಲ್ಲಿ ವಾಸವಾಗಿದ್ದರು.",
			"19": "<sup>19</sup> ದಾವೀದನು ರಾಮದ ನಯೋತಿನಲ್ಲಿರುವುದು ಸೌಲನಿಗೆ ಗೊತ್ತಾಗಲು,",
			"20": "<sup>20</sup> ಅವನು ದಾವೀದನನ್ನು ಹಿಡಿದು ತರುವುದಕ್ಕಾಗಿ ಆಳುಗಳನ್ನು ಕಳುಹಿಸಿದನು. ಅವರು ಹೋಗಿ ಅಲ್ಲಿನ ಪ್ರವಾದಿ ಸಮೂಹವು ಪ್ರವಾದಿಸುತ್ತಿರುವುದನ್ನೂ, ಸಮುವೇಲನು ಅವರ ನಾಯಕನಾಗಿ ನಿಂತಿರುವುದನ್ನು ಕಂಡಾಗ, ದೇವರ ಆತ್ಮವು ಅವರ ಮೇಲೆಯೂ ಬಂದಿತು. ಅವರೂ ಪ್ರವಾದಿಸಿದರು.",
			"21": "<sup>21</sup> ಸೌಲನಿಗೆ ಈ ವರ್ತಮಾನವು ಮುಟ್ಟಿದಾಗ ಅವನು ಬೇರೆ ಆಳುಗಳನ್ನು ಕಳುಹಿಸಿದನು. ಇವರೂ ಪ್ರವಾದಿಸಿದರು. ಅವನು ಮೂರನೆಯ ಸಾರಿ ಇನ್ನೂ ಕೆಲವು ಮಂದಿ ಆಳುಗಳನ್ನು ಕಳುಹಿಸಿದನು. ಅವರೂ ಪ್ರವಾದಿಸಿದರು.",
			"22": "<sup>22</sup> ಆಗ ಸೌಲನು ತಾನೇ ರಾಮಕ್ಕೆ ಹೋಗಬೇಕೆಂದು ಹೊರಟು, ಸೇಕೂವಿನಲ್ಲಿರುವ ದೊಡ್ಡ ಬಾವಿಯ ಬಳಿಗೆ ಬಂದು, “ಸಮುವೇಲ ದಾವೀದರು ಎಲ್ಲಿರುತ್ತಾರೆ?” ಎಂದು ವಿಚಾರಿಸಿದನು. ಆಗ ಜನರು, “ರಾಮದ ನಯೋತಿನಲ್ಲಿ ಇರುತ್ತಾರೆ” ಎಂದು ಉತ್ತರಕೊಟ್ಟರು.",
			"23": "<sup>23</sup> ಅವನು ಅಲ್ಲಿಂದ ರಾಮದ ನಯೋತಿಗೆ ಹೋಗುವಾಗ ದೇವರ ಆತ್ಮವು ಅವನ ಮೇಲೆಯೂ ಬಂದಿತು. ಅವನು ಆ ಊರನ್ನು ಮುಟ್ಟುವ ತನಕ ಪ್ರವಾದಿಸುತ್ತಾ ಹೋದನು.",
			"24": "<sup>24</sup> ಅಲ್ಲಿಗೆ ಸೇರಿದ ಮೇಲೆ ಅವನು ತನ್ನ ಬಟ್ಟೆಗಳನ್ನು ತೆಗೆದುಹಾಕಿ, ಸಮುವೇಲನ ಮುಂದೆ ಪ್ರವಾದಿಸುತ್ತಾ, ಆ ದಿನ ಹಗಲಿರುಳು ಬೆತ್ತಲೆಯಾಗಿ ಬಿದ್ದುಕೊಂಡಿದ್ದನು. ಇದರಿಂದ “ಸೌಲನೂ ಕೂಡ ಪ್ರವಾದಿಗಳಲ್ಲಿ ಒಬ್ಬನಾಗಿದ್ದಾನೆಯೇ” ಎಂಬ ಮಾತು ಪ್ರಚಲಿತವಾಯಿತು."
		},
		"20": {
			"1": "<sup>1</sup> ದಾವೀದನು ರಾಮದ ನಯೋತಿನಿಂದ ತಪ್ಪಿಸಿಕೊಂಡು ಯೋನಾತಾನನ ಬಳಿಗೆ ಬಂದು ಅವನಿಗೆ, “ನಿನ್ನ ತಂದೆಯು ನನ್ನ ಪ್ರಾಣವನ್ನು ತೆಗೆಯಬೇಕೆಂದಿರುತ್ತಾನಲ್ಲ, ನಾನೇನು ಮಾಡಿದೆನು? ನನ್ನ ಅಪರಾಧವೇನು? ನಾನು ಅವನಿಗೆ ಮಾಡಿದ ದ್ರೋಹವು ಯಾವುದು?” ಎನ್ನಲು,",
			"2": "<sup>2</sup> ಯೋನಾತಾನನು ಅವನಿಗೆ, “ಇದು ಆಗಲೇಬಾರದು, ನಿನಗೆ ಮರಣವಾಗಕೂಡದು. ನನ್ನ ತಂದೆಯು ಅಲ್ಪಕಾರ್ಯವನ್ನಾಗಲಿ, ಮಹಾಕಾರ್ಯವನ್ನಾಗಲಿ ನನಗೆ ತಿಳಿಸದೇ ಮಾಡುವುದಿಲ್ಲ. ಈ ಕಾರ್ಯವನ್ನು ನನಗೆ ಹೇಗೆ ಮರೆಮಾಡಲು ಸಾಧ್ಯ?” ಎಂದು ಹೇಳಿದನು.",
			"3": "<sup>3</sup> ಅದಕ್ಕೆ ದಾವೀದನು, “ನಿನ್ನ ಕಣ್ಣುಮುಂದೆ ನನಗೆ ದಯೆ ದೊರಕುತ್ತದೆಂಬುದನ್ನು ನಿನ್ನ ತಂದೆಯು ಬಲ್ಲನು. ಆದ್ದರಿಂದ ಇದನ್ನು ಯೋನಾತಾನನಿಗೆ ತಿಳಿಸಿದರೆ ಅವನಿಗೆ ದುಃಖವಾಗುವುದೆಂದು ಮರೆಮಾಡುತ್ತಾನೆ. ನಿನ್ನ ಜೀವದಾಣೆ, ಯೆಹೋವನ ಆಣೆ, ನನಗೂ ಮರಣಕ್ಕೂ ಒಂದು ಗೇಣು ಅಂತರವಿದೆ” ಎಂದು ಖಂಡಿತವಾಗಿ ಹೇಳಿದನು.",
			"4": "<sup>4</sup> ಆಗ ಯೋನಾತಾನನು ದಾವೀದನಿಗೆ, “ನಾನು ನಿನಗೋಸ್ಕರ ಮಾಡಬೇಕಾದದ್ದನ್ನು ಹೇಳು ಮಾಡುತ್ತೇನೆ” ಅಂದನು.",
			"5": "<sup>5</sup> ಅದಕ್ಕೆ ದಾವೀದನು, “ನಾಳೆ ಅಮಾವಾಸ್ಯೆ. ನಾನು ಅರಸನ ಪಂಕ್ತಿಯಲ್ಲಿ ಊಟಮಾಡಬೇಕಾಗಿರುವುದು. ನೀನು ಅಪ್ಪಣೆಕೊಟ್ಟರೆ ನಾನು ಈಗಲೇ ಹೋಗಿ ಮೂರನೆಯ ದಿನ ಸಾಯಂಕಾಲದವರೆಗೆ ಹೊಲದಲ್ಲಿ ಅಡಗಿಕೊಂಡಿರುವೆನು.",
			"6": "<sup>6</sup> ನಿನ್ನ ತಂದೆಯು ನನ್ನನ್ನು ಕುರಿತು ವಿಚಾರ ಮಾಡಿದರೆ, ನೀನು, ‘ದಾವೀದನು ತನ್ನ ಊರಾದ ಬೇತ್ಲೆಹೇಮಿಗೆ ಹೋಗಿ ಬರುವುದಕ್ಕೆ ಅಪ್ಪಣೆಯಾಗಬೇಕೆಂದು ನನ್ನನ್ನು ಬಹಳವಾಗಿ ಬೇಡಿಕೊಂಡನು. ಅವನ ಗೋತ್ರದವರು ಅಲ್ಲಿ ವಾರ್ಷಿಕಯಜ್ಞಮಾಡುತ್ತಾರೆ’ ಎಂದು ಹೇಳು.",
			"7": "<sup>7</sup> ಅವನು ಆಗಲಿ ಅಂದರೆ ನಿನ್ನ ಸೇವಕನಾದ ನಾನು ಸುರಕ್ಷಿತನಾಗಿರುವೆನು. ಸಿಟ್ಟುಮಾಡಿದರೆ ಅವನಿಂದ ನನಗೆ ಕೇಡು ಸಿದ್ಧವಾಗಿದೆ ಎಂದು ತಿಳಿದುಕೋ, ಸೇವಕನ ಮೇಲೆ ದಯೆಯಿರಲಿ.",
			"8": "<sup>8</sup> ನೀನು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನನ್ನೊಡನೆ ಒಡಂಬಡಿಕೆ ಮಾಡಿಕೊಂಡಿಯಲ್ಲಾ, ನಾನು ಅಪರಾಧಿಯಾಗಿದ್ದರೆ ನನ್ನನ್ನು ನಿನ್ನ ತಂದೆಗೆ ಒಪ್ಪಿಸುವುದೇಕೆ? ನೀನೇ ನನ್ನನ್ನು ಕೊಂದುಹಾಕು” ಎಂದು ಹೇಳಿದನು.",
			"9": "<sup>9</sup> ಯೋನಾತಾನನು ಅವನಿಗೆ, “ಇದು ಆಗಲೇ ಬಾರದು, ನಿನಗೆ ನನ್ನ ತಂದೆಯಿಂದ ಕೇಡು ಸಿದ್ಧವಾಗಿದ್ದರೆ ಅದನ್ನು ನಿನಗೆ ತಿಳಿಸದಿರುತ್ತೇನೆಯೇ?” ಅಂದನು.",
			"10": "<sup>10</sup> ದಾವೀದನು, “ನಿನ್ನ ತಂದೆಯು ಬಿರುನುಡಿಯಿಂದ ಉತ್ತರಕೊಟ್ಟರೆ ಅದನ್ನು ಯಾರ ಮುಖಾಂತರವಾಗಿ ತಿಳಿಸುವಿ?” ಎಂದು ಅವನನ್ನು ಕೇಳಲು",
			"11": "<sup>11</sup> ಯೋನಾತಾನನು ಅವನಿಗೆ, “ನಾವು ಹೊಲಕ್ಕೆ ಹೋಗೋಣ ಬಾ” ಎಂದು ಹೇಳಿದನು.",
			"12": "<sup>12</sup> ಅವರಿಬ್ಬರೂ ಹೊಲಕ್ಕೆ ಹೋದ ಮೇಲೆ ಯೋನಾತಾನನು ದಾವೀದನಿಗೆ, “ಇಸ್ರಾಯೇಲರ ದೇವರಾದ ಯೆಹೋವನೇ ಸಾಕ್ಷಿ, ನಾನು ನಾಳೆ ಇಲ್ಲವೆ ನಾಡಿದ್ದು ನಿನ್ನ ವಿಷಯದಲ್ಲಿ ನನ್ನ ತಂದೆಯೊಡನೆ ಮಾತನಾಡಿ, ನಿನ್ನ ಮೇಲೆ ಅವನಿಗೆ ದಯೆ ಉಂಟೆಂದು ಗೊತ್ತಾದರೆ ಕೂಡಲೆ ನಿನಗೆ ತಿಳಿಸುವೆನು.",
			"13": "<sup>13</sup> ನನ್ನ ತಂದೆಯು ನಿನಗೆ ಕೇಡುಮಾಡುವ ಮನಸ್ಸುಳ್ಳವನಾಗಿದ್ದಾನೆಂದು ಗೊತ್ತಾದರೆ, ಅದನ್ನೂ ನಿನಗೆ ತಿಳಿಸಿ, ನೀನು ತಪ್ಪಿಸಿಕೊಂಡು ಸುರಕ್ಷಿತವಾಗುವ ಹಾಗೆ ನಿನ್ನನ್ನು ಕಳುಹಿಸಿಬಿಡುವೆನು. ಹಾಗೆ ಮಾಡದಿದ್ದರೆ ಯೆಹೋವನು ನನಗೆ ಬೇಕಾದದ್ದನ್ನು ಮಾಡಲಿ, ಆತನು ನನ್ನ ತಂದೆಯ ಸಂಗಡ ಇದ್ದ ಹಾಗೆ ನಿನ್ನ ಸಂಗಡಲೂ ಇರಲಿ.",
			"14": "<sup>14</sup> ನೀನು ಯೆಹೋವನನ್ನು ನೆನಸಿ ನನ್ನ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ನನಗೆ ಕೃಪೆತೋರಿಸು.",
			"15": "<sup>15</sup> ನಾನು ಸತ್ತನಂತರ ನನ್ನ ಮನೆಯ ಮೇಲೆಯೂ ಶಾಶ್ವತವಾದ ನಿನ್ನ ದಯೆಯಿರಲಿ. ಯೆಹೋವನು ನಿನ್ನ ಎಲ್ಲಾ ಶತ್ರುಗಳನ್ನು ಭೂಮಿಯಿಂದ ತೆಗೆದು ಹಾಕಿದರೂ ಅದು ನನ್ನ ಮನೆಯಿಂದ ಅಗಲದಿರಲಿ.",
			"16": "<sup>16</sup> ಅಗಲಿದರೆ ಯೆಹೋವನೇ ಶತ್ರುಗಳ ಕೈಯಿಂದ ದಾವೀದನಿಗೆ ಮುಯ್ಯಿ ತೀರಿಸಲಿ” ಎಂದು ಹೇಳಿ ಅವರ ಮನೆಯವರೊಡನೆ ಒಡಂಬಡಿಕೆ ಮಾಡಿಕೊಂಡು,",
			"17": "<sup>17</sup> ತಾನು ದಾವೀದನನ್ನು ಸ್ವಂತ ಪ್ರಾಣದಂತೆ ಪ್ರೀತಿಸುತ್ತಿದ್ದುದರಿಂದ ಆ ಪ್ರೀತಿಸಾಕ್ಷಿಯಾಗಿ ಅವನಿಂದ ಪ್ರಮಾಣಮಾಡಿಸಿದನು.",
			"18": "<sup>18</sup> ಯೋನಾತಾನನು ದಾವೀದನಿಗೆ, “ನಾಳೆ ಅಮಾವಾಸ್ಯೆ, ನಿನ್ನ ಸ್ಥಳವು ಬರಿದಾಗಿರುವುದನ್ನು ಅರಸನು ಕಂಡು ನಿನ್ನ ವಿಷಯವಾಗಿ ವಿಚಾರಿಸುವನು.",
			"19": "<sup>19</sup> ನೀನು ಮೂರನೆಯ ದಿನದಲ್ಲಿ ಮೊದಲು ಅಡಗಿಕೊಂಡಿದ್ದ ಸ್ಥಳಕ್ಕೆ ಬೇಗ ಬಂದು ಅಲ್ಲಿನ ಕಲ್ಲುಕುಪ್ಪೆಯ ಬಳಿಯಲ್ಲಿ ಕುಳಿತುಕೊಂಡಿರು.",
			"20": "<sup>20</sup> ನಾನು ಗುರಿಯಿಟ್ಟವನೋ ಎಂಬಂತೆ ಅದರ ಕಡೆಗೆ ಮೂರು ಬಾಣಗಳನ್ನು ಎಸೆದು ಕೂಡಲೇ ಅವುಗಳನ್ನು",
			"21": "<sup>21</sup> ತರುವುದಕ್ಕಾಗಿ ನನ್ನ ಆಳುಗಳನ್ನು ಕಳುಹಿಸುವೆನು. ನಾನು ಅವನಿಗೆ, ‘ಬಾಣಗಳು ಈಚೆ ಬಿದ್ದಿರುತ್ತವೆ ಅವುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಬಾ’ ಎಂದು ಹೇಳಿದರೆ ನೀನು ಬಾ ಯೆಹೋವನ ಆಣೆ ಯಾವ ಕೇಡು ಸಂಭವಿಸದೆ ಸುರಕ್ಷಿತನಾಗಿರುವಿ.",
			"22": "<sup>22</sup> ಆದರೆ, ‘ಬಾಣಗಳು ಆಚೆ ಬಿದ್ದಿರುತ್ತವೆ’ ಎಂದು ಹೇಳಿದರೆ ಹೊರಟುಹೋಗು, ನೀನು ಹೋಗುವುದೇ ಯೆಹೋವನ ಚಿತ್ತವಾಗಿದೆ.",
			"23": "<sup>23</sup> ನಮ್ಮಿಬ್ಬರ ಒಡಂಬಡಿಕೆಗೆ ಯೆಹೋವನೇ ನಿತ್ಯ ಸಾಕ್ಷಿಯಾಗಿರುವನು” ಅಂದನು.",
			"24": "<sup>24</sup> ದಾವೀದನು ಹೊಲದಲ್ಲಿ ಅಡಗಿಕೊಂಡನು.",
			"25": "<sup>25</sup> ಅರಸನು ಅಮಾವಾಸ್ಯೆ ದಿನ, ಪದ್ದತಿಯ ಪ್ರಕಾರ ಗೋಡೆಯ ಬಳಿಯಲ್ಲಿರುವ ತನ್ನ ಆಸನದ ಮೇಲೆ ಭೋಜನಕ್ಕೆ ಕುಳಿತುಕೊಂಡನು. ಅವನ ಎದುರಾಗಿ ಯೋನಾತಾನನೂ, ಅವನ ಪಕ್ಕದಲ್ಲಿ ಅಬ್ನೇರನೂ ಕುಳಿತುಕೊಂಡನು. ಸೌಲನು ದಾವೀದನ ಸ್ಥಳವು ಬರಿದಾಗಿರುವುದನ್ನು ಕಂಡರೂ,",
			"26": "<sup>26</sup> ಆ ದಿನ ಅವನ ವಿಷಯ ಏನೂ ಹೇಳಲಿಲ್ಲ. ಅವನಿಗೆ ಏನಾದರೂ ಸಂಭವಿಸಿರಬೇಕು. ಅವನು ಹೊಲೆಯಾಗಿದ್ದಾನು, ತನ್ನನ್ನು ಇನ್ನು ಶುದ್ಧಪಡಿಸಿಕೊಳ್ಳದೆ ಇರಬಹುದು ಅಂದುಕೊಂಡನು.",
			"27": "<sup>27</sup> ಮರುದಿನದಲ್ಲಿಯೂ, ಅಂದರೆ ತಿಂಗಳಿನ ಎರಡನೆಯ ದಿನದಲ್ಲಿಯೂ ದಾವೀದನ ಸ್ಥಾನ ಬರಿದಾಗಿದ್ದರಿಂದ ಸೌಲನು, “ಇಷಯನ ಮಗನು ನಿನ್ನೆಯೂ, ಈ ಹೊತ್ತೂ ಭೋಜನಕ್ಕೆ ಯಾಕೆ ಬರಲಿಲ್ಲ?” ಎಂದು ತನ್ನ ಮಗನಾದ ಯೋನಾತಾನನನ್ನು ಕೇಳಿದನು.",
			"28": "<sup>28</sup> ಅದಕ್ಕೆ ಯೋನಾತಾನನು, “ದಾವೀದನು ‘ಬೇತ್ಲೆಹೇಮಿನಲ್ಲಿ ನಮ್ಮ ಗೋತ್ರದವರು ಯಜ್ಞಮಾಡುತ್ತಾರೆ",
			"29": "<sup>29</sup> ನನ್ನ ಅಣ್ಣನು ಅದಕ್ಕಾಗಿ ನನ್ನನ್ನು ಕರೆದಿದ್ದಾನೆ. ದಯವಿಟ್ಟು ನನಗೆ ಅಪ್ಪಣೆಕೊಡು ನಾನು ಹೋಗಿ ನನ್ನ ಅಣ್ಣಂದಿರನ್ನು ನೋಡಿ ಬರುತ್ತೇನೆ’ ಎಂದು ನನ್ನನ್ನು ಬಹಳವಾಗಿ ಬೇಡಿಕೊಂಡು ಹೋದನು. ಆದ್ದರಿಂದ ಅವನು ಅರಸನ ಪಂಕ್ತಿಗೆ ಬರಲಿಲ್ಲ” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"30": "<sup>30</sup> ಆಗ ಸೌಲನು ಬಹಳವಾಗಿ ಕೋಪಗೊಂಡು ಯೋನಾತಾನನಿಗೆ, “ಎಲೈ ದುಷ್ಟ ದಾಸಿಯ ಮಗನೇ, ನೀನು ಇಷಯನ ಮಗನೊಡನೆ ಸ್ನೇಹದಿಂದಿರುವುದು ನನಗೆ ಗೊತ್ತಿಲ್ಲವೋ? ಇದರಿಂದ ನಿನಗೂ, ನಿನ್ನನ್ನು ಹೆತ್ತವಳಿಗೂ ಮಾನಭಂಗವಾಗುವುದು.",
			"31": "<sup>31</sup> ಆ ಇಷಯನ ಮಗನು ಭೂಲೋಕದಲ್ಲಿರುವವರೆಗೆ ನಿನಗಾದರೂ, ನಿನ್ನ ರಾಜ್ಯಕ್ಕಾದರೂ ನೆಲೆಯಿಲ್ಲ. ಆದುದರಿಂದ ಅವನನ್ನು ಕರಿಸಿ ನನ್ನ ಬಳಿಗೆ ತೆಗೆದುಕೊಂಡು ಬಾ. ಅವನು ಸಾಯಬೇಕು” ಅಂದನು.",
			"32": "<sup>32</sup> ಅದಕ್ಕೆ ಯೋನಾತಾನನು, “ಅವನು ಯಾಕೆ ಸಾಯಬೇಕು? ಅವನು ಏನು ಮಾಡಿದನು?” ಎಂದು ತನ್ನ ತಂದೆಯನ್ನು ಕೇಳಿದನು.",
			"33": "<sup>33</sup> ಕೂಡಲೆ ಸೌಲನು ಅವನನ್ನು ತಿವಿಯಬೇಕೆಂದು ಈಟಿಯನ್ನೆಸೆದನು. ತನ್ನ ತಂದೆಯು ದಾವೀದನನ್ನು ಕೊಲ್ಲುವುದಕ್ಕೆ ನಿಶ್ಚಯಿಸಿದ್ದಾನೆಂಬುದು ಯೋನಾತಾನಾನಿಗೆ ಗೊತ್ತಾಗಲು",
			"34": "<sup>34</sup> ಅವನು ಬಲು ಸಿಟ್ಟುಗೊಂಡು ಪಂಕ್ತಿಯಿಂದೆದ್ದು ಹೋದನು. ತನ್ನ ತಂದೆಯು ದಾವೀದನನ್ನು ಅಪಮಾನಪಡಿಸಿದ್ದರಿಂದ ಯೋನಾತಾನನಿಗೆ ಬಹಳ ದುಃಖವುಂಟಾಗಿ ಅವನು ಆ ದಿನ ಊಟಮಾಡಲೇ ಇಲ್ಲ.",
			"35": "<sup>35</sup> ಅವನು ಮರುದಿನ ಬೆಳಿಗ್ಗೆ ಒಬ್ಬ ಹುಡುಗನನ್ನು ಕರೆದುಕೊಂಡು ದಾವೀದನನ್ನು ನೋಡುವುದಕ್ಕಾಗಿ ಹೊಲಕ್ಕೆ ಹೋಗಿ,",
			"36": "<sup>36</sup> ಹುಡುಗನಿಗೆ, “ಬೇಗ ಹೋಗಿ ನಾನು ಎಸೆದ ಬಾಣಗಳನ್ನು ಕೂಡಿಸಿಕೊಂಡು ಬಾ” ಎಂದು ಹೇಳಿದನು. ಆ ಹುಡುಗನು ಹೋಗುತ್ತಿರುವಾಗ ಯೋನಾತಾನನು ಬಾಣವನ್ನು ಅವನ ಆಚೆಗೆ ಎಸೆದನು.",
			"37": "<sup>37</sup> ಹುಡುಗನು ಬಾಣ ಬಿದ್ದ ಸ್ಥಳಕ್ಕೆ ಬಂದಾಗ ಯೋನಾತಾನನು ಅವನಿಗೆ, “ಬಾಣವು ನಿನ್ನ ಆಚೆ ಬಿದ್ದಿತಲ್ಲಾ",
			"38": "<sup>38</sup> ಬೇಗ ಹೋಗು ನಿಲ್ಲಬೇಡ” ಎಂದು ಕೂಗಿ ಹೇಳಿದನು. ಹುಡುಗನು ಬಾಣಗಳನ್ನು ಕೂಡಿಸಿಕೊಂಡು ಯಜಮಾನನ ಬಳಿಗೆ ಬಂದನು.",
			"39": "<sup>39</sup> ಇದರ ರಹಸ್ಯವು ಯೋನಾತಾನ ದಾವೀದರಿಗೆ ತಿಳಿದಿತ್ತೇ ಹೊರತು ಆ ಹುಡುಗನಿಗೆ ಗೊತ್ತಿರಲಿಲ್ಲ.",
			"40": "<sup>40</sup> ಯೋನಾತಾನನು ತನ್ನ ಆಯುಧಗಳನ್ನು ಹುಡುಗನ ಕೈಯಲ್ಲಿ ಕೊಟ್ಟು ಅವನಿಗೆ, “ಅವುಗಳನ್ನು ಊರಿಗೆ ತೆಗೆದುಕೊಂಡು ಹೋಗು” ಎಂದು ಹೇಳಿದನು.",
			"41": "<sup>41</sup> ಹುಡುಗನು ಹೋಗುತ್ತಲೇ ದಾವೀದನು ಕಲ್ಲುಕುಪ್ಪೆಯ ಬಳಿಯಿಂದ ಎದ್ದುಬಂದು ಮೂರು ಸಾರಿ ಸಾಷ್ಟಾಂಗವಾಗಿ ಯೋನಾತಾನನನ್ನು ವಂದಿಸಿದನು. ಅವರು ಒಬ್ಬರಿಗೊಬ್ಬರು ಮುದ್ದಿಟ್ಟುಕೊಂಡು ಅತ್ತರು. ದಾವೀದನು ಬಹಳವಾಗಿ ಅತ್ತನು.",
			"42": "<sup>42</sup> ಅನಂತರ ಯೋನಾತಾನನು ದಾವೀದನಿಗೆ, “ಸಮಾಧಾನದಿಂದ ಹೋಗು. ನಾವು ಯೆಹೋವನ ಹೆಸರಿನಲ್ಲಿ ಆಣೆಯಿಟ್ಟು ಒಡಂಬಡಿಕೆಯನ್ನು ಮಾಡಿಕೊಂಡಿದ್ದೇವಲ್ಲಾ. ಆತನೇ ನನಗೂ ನಿನಗೂ, ನನ್ನ ಸಂತಾನಕ್ಕೂ ನಿನ್ನ ಸಂತಾನಕ್ಕೂ ಸದಾ ಸಾಕ್ಷಿಯಾಗಿರಲಿ” ಎಂದು ಹೇಳಿದನು. ತರುವಾಯ ದಾವೀದನು ಹೊರಟುಹೋದನು. ಯೋನಾತಾನನು ಊರೊಳಕ್ಕೆ ಹೋದನು."
		},
		"21": {
			"1": "<sup>1</sup> ದಾವೀದನು ನೋಬ್ ಊರಲ್ಲಿದ್ದ ಯಾಜಕನಾದ ಅಹೀಮೆಲೆಕನ ಬಳಿಗೆ ಬಂದನು. ಅಹೀಮೆಲೆಕನು ದಾವೀದನನ್ನು ಭಯಭಕ್ತಿಯಿಂದ ಎದುರುಗೊಂಡು, “ನಿನ್ನ ಜೊತೆಯಲ್ಲಿ ಒಬ್ಬನಾದರೂ ಇಲ್ಲವಲ್ಲಾ, ನೀನು ಒಬ್ಬನೇ ಬಂದದ್ದೇಕೆ?” ಎಂದು ಅವನನ್ನು ಕೇಳಿದನು.",
			"2": "<sup>2</sup> ಅವನು, “ಅರಸನು ಒಂದು ಕೆಲಸವನ್ನು ಆಜ್ಞಾಪಿಸಿ, ಇದನ್ನು ಯಾರಿಗೂ ತಿಳಿಸಬಾರದೆಂದು ನನ್ನನ್ನು ಕಳುಹಿಸಿದ್ದಾನೆ. ನನ್ನ ಆಳುಗಳು ಇಂಥಿಂಥ ಸ್ಥಳದಲ್ಲಿ ನನ್ನನ್ನು ಸಂಧಿಸಬೇಕೆಂದು ಗೊತ್ತುಮಾಡಿದ್ದಾನೆ.",
			"3": "<sup>3</sup> ಈಗ ನಿನ್ನ ಕೈಯಲ್ಲಿ ಏನಾದರೂ ಉಂಟೋ? ನಾಲ್ಕೈದು ರೊಟ್ಟಿಗಳಾದರೂ, ಬೇರೆ ಯಾವ ಆಹಾರಪದಾರ್ಥವಾದರೂ ಇದ್ದರೆ ನನಗೆ ಕೊಟ್ಟುಬಿಡು” ಎಂದನು.",
			"4": "<sup>4</sup> ಯಾಜಕನು ದಾವೀದನಿಗೆ, “ನನ್ನ ಬಳಿಯಲ್ಲಿ ಮೀಸಲು ರೊಟ್ಟಿಗಳು ಹೊರತು ಬೇರೆ ರೊಟ್ಟಿಗಳಿಲ್ಲ. ನಿನ್ನ ಆಳುಗಳು ಸ್ತ್ರೀಸಂಗ ಬಿಟ್ಟವರಾಗಿದ್ದರೆ ಅವುಗಳನ್ನು ಕೊಡಬಹುದು” ಎಂದನು.",
			"5": "<sup>5</sup> ದಾವೀದನು, “ನಮ್ಮ ಎಲ್ಲಾ ಯುದ್ಧ ಪ್ರಯಾಣಗಳಲ್ಲಿ ಸ್ತ್ರೀಸಂಗವನ್ನು ನಿಷೇಧಮಾಡುವಂತೆ ಈಗಲೂ ಮಾಡಿದ್ದೇವೆ. ನಾವು ಸಾಧಾರಣ ಕಾರ್ಯಕ್ಕೆ ಹೊರಟಾಗಲೂ ಆಳುಗಳು, ಸಾಮಾನುಗಳು ಪರಿಶುದ್ಧವಾಗಿರುತ್ತವೆ. ಈ ದಿನದಲ್ಲಿ ಅವು ಎಷ್ಟೋ ಹೆಚ್ಚಾಗಿ ಪರಿಶುದ್ಧವಾಗಿರುತ್ತದಲ್ಲವೇ” ಎಂದು ಉತ್ತರ ಕೊಟ್ಟನು",
			"6": "<sup>6</sup> ಬಿಸಿರೊಟ್ಟಿಗಳನ್ನು ಅರ್ಪಿಸಿದ ಆ ದಿನಗಳಲ್ಲಿ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಿಂದ ತೆಗೆಯಲ್ಪಟ್ಟ ನೈವೇದ್ಯವಾದ ಮೊದಲ ರೊಟ್ಟಿಗಳ ಹೊರತು ಅಲ್ಲಿ ಬೇರೆ ರೊಟ್ಟಿಗಳು ಇರಲಿಲ್ಲವಾದ್ದರಿಂದ, ಯಾಜಕನು ಆ ಪರಿಶುದ್ಧವಾದ ರೊಟ್ಟಿಗಳನ್ನೇ ಕೊಟ್ಟುಬಿಟ್ಟನು.",
			"7": "<sup>7</sup> ಅದೇ ದಿನ ಸೌಲನ ಪಶುಪಾಲರಲ್ಲಿ ಪ್ರಧಾನನಾದ ದೋಯೇಗನೆಂಬ ಎದೋಮ್ಯನು ಯೆಹೋವನ ಆಲಯದಲ್ಲಿ ಉಳಿದುಕೊಂಡಿದ್ದನು.",
			"8": "<sup>8</sup> ದಾವೀದನು ಅಹೀಮೆಲೆಕನನ್ನು, “ಅರಸನ ಕಾರ್ಯವು ತುರ್ತಾದ್ದರಿಂದ ಕತ್ತಿಯನ್ನಾಗಲಿ, ಬೇರೆ ಆಯುಧವನ್ನಾಗಲಿ ತೆಗೆದುಕೊಂಡು ಬರಲಿಲ್ಲ, ನಿನ್ನ ಬಳಿಯಲ್ಲಿ ಬರ್ಜಿಯಾಗಲಿ ಕತ್ತಿಯಾಗಲಿ ಇಲ್ಲವೋ?” ಎಂದು ಕೇಳಿದನು.",
			"9": "<sup>9</sup> ಅವನು, “ನೀನು ಏಲಾ ತಗ್ಗಿನಲ್ಲಿ ಕೊಂದು ಹಾಕಿದ ಫಿಲಿಷ್ಟಿಯನಾದ ಗೊಲ್ಯಾತನ ಕತ್ತಿಯು ಒಂದು ಬಟ್ಟೆಯಲ್ಲಿ ಸುತ್ತಲ್ಪಟ್ಟು, ಏಫೋದಿನ ಹಿಂದೆ ಇಡಲ್ಪಟ್ಟಿದೆ. ಬೇಕಾದರೆ ಅದನ್ನು ತೆಗೆದುಕೊ. ಅದರ ಹೊರತು ನನ್ನ ಬಳಿಯಲ್ಲಿ ಬೇರೊಂದಿಲ್ಲ” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು. ದಾವೀದನು, “ಅದಕ್ಕೆ ಸಮಾನವಾದದ್ದು ಮತ್ತೊಂದಿಲ್ಲ. ಅದನ್ನೇ ಕೊಡು” ಎಂದು ಹೇಳಿ ತೆಗೆದುಕೊಂಡನು.",
			"10": "<sup>10</sup> ದಾವೀದನನು ಸೌಲನ ಭಯದಿಂದ ಅದೇ ದಿನ ಗತ್ ಊರಿನ ಅರಸನಾದ ಆಕೀಷನ ಬಳಿಗೆ ಓಡಿಹೋದನು.",
			"11": "<sup>11</sup> ಆಕೀಷನ ಸೇವಕರು, “ಸೌಲನು ಸಾವಿರಾರು ಶತ್ರುಗಳನ್ನು ಕೊಂದನು, ದಾವೀದನು ಹತ್ತು ಸಾವಿರಾರು ಶತ್ರುಗಳನ್ನು ಕೊಂದನು ಎಂಬುದಾಗಿ ಸ್ತ್ರೀಯರು ಕುಣಿಯುತ್ತಾ ಹಾಡಿದ್ದು ಇವನ ಕುರಿತಲ್ಲವೋ?” ಎಂದು ಮಾತನಾಡಿಕೊಂಡರು.",
			"12": "<sup>12</sup> ದಾವೀದನು ಈ ಮಾತುಗಳನ್ನು ಕೇಳುತ್ತಲೇ ಗತ್ ಊರಿನ ಅರಸನಾದ ಆಕೀಷನಿಗೆ ಬಹಳವಾಗಿ ಹೆದರಿ,",
			"13": "<sup>13</sup> ಅವನ ಸೇವಕರ ಮುಂದೆ ತನ್ನ ಬುದ್ಧಿಯನ್ನು ಮಾರ್ಪಡಿಸಿಕೊಂಡು, ಗಡ್ಡದ ಮೇಲೆಲ್ಲಾ ಜೊಲ್ಲು ಸುರಿಸುತ್ತಾ, ಬಾಗಿಲುಗಳನ್ನು ಕೆರೆಯುತ್ತಾ, ತನ್ನನ್ನು ಹುಚ್ಚನಂತೆ ನಟಿಸಿದನು.",
			"14": "<sup>14</sup> ಆಗ ಆಕೀಷನು ತನ್ನ ಸೇವಕರಿಗೆ, “ಈ ಮನುಷ್ಯನು ಹುಚ್ಚನೆಂದು ನಿಮಗೆ ಕಾಣುವುದಿಲ್ಲವೋ, ಇವನನ್ನು ನನ್ನ ಹತ್ತಿರ ಯಾಕೆ ಕರೆತಂದಿರಿ?",
			"15": "<sup>15</sup> ನನ್ನ ಬಳಿಯಲ್ಲಿ ಹುಚ್ಚರು ಕಡಿಮೆಯೆಂದು ನೆನಸಿ ನನ್ನನ್ನು ಬೇಸರಗೊಳಿಸುವುದಕ್ಕಾಗಿ ಇವನನ್ನು ಕರೆತಂದಿರೋ? ಇಂಥವನೂ ನನ್ನ ಮನೆಗೆ ಬರಬೇಕೋ?” ಅಂದನು."
		},
		"22": {
			"1": "<sup>1</sup> ದಾವೀದನು ಅಕೀಷ ರಾಜನಿಂದ ತಪ್ಪಿಸಿಕೊಂಡು ಅದುಲ್ಲಾಮ್ ಎಂಬ ಗವಿಗೆ ಹೋದನು. ಈ ವರ್ತಮಾನವು ಅವನ ಅಣ್ಣಂದಿರಿಗೂ, ಬೇರೆ ಎಲ್ಲಾ ಸಂಬಂಧಿಕರಿಗೂ ಮುಟ್ಟಲಾಗಿ, ಅವರೂ ಬಂದು ಅವನನ್ನು ಸೇರಿಕೊಂಡರು.",
			"2": "<sup>2</sup> ಇದಲ್ಲದೆ ಕುಗ್ಗಿದವರೂ, ಸಾಲಗಾರರೂ, ಮನನೊಂದವರೂ, ಆಗಿರುವ ಸರ್ವಜನರೂ ಬಂದು ಅವನನ್ನು ಆಶ್ರಯಿಸಿಕೊಳ್ಳಲು, ಅವನು ಸುಮಾರು ನಾನೂರು ಜನರಿಗೆ ನಾಯಕನಾದನು.",
			"3": "<sup>3</sup> ಅನಂತರ ದಾವೀದನು ತನ್ನ ತಂದೆತಾಯಿಗಳನ್ನು ಮೋವಾಬ್ ದೇಶದ ಮಿಚ್ಪೆ ಎಂಬಲ್ಲಿಗೆ ಕರೆದುಕೊಂಡು ಹೋಗಿ ಅಲ್ಲಿಯ ಅರಸನಿಗೆ, “ದೇವರು ನನಗೆ ಮಾರ್ಗತೋರಿಸುವವರೆಗೆ ನನ್ನ ತಂದೆತಾಯಿಗಳು ನಿನ್ನ ಬಳಿಯಲ್ಲಿರುವುದಕ್ಕೆ ಅಪ್ಪಣೆಯಾಗಬೇಕು” ಎಂದು ಬೇಡಿಕೊಂಡು, ಅವರನ್ನು ಅವನ ಬಳಿಯಲ್ಲಿ ಬಿಟ್ಟನು.",
			"4": "<sup>4</sup> ದಾವೀದನು ದುರ್ಗದಲ್ಲಿದ್ದ ಕಾಲವೆಲ್ಲಾ ಅವನ ತಂದೆತಾಯಿಗಳು ಈ ಅರಸನ ಬಳಿಯಲ್ಲಿ ವಾಸವಾಗಿದ್ದರು.",
			"5": "<sup>5</sup> ಗಾದ್ ಪ್ರವಾದಿಯು ದಾವೀದನಿಗೆ, “ನೀನು ಈ ದುರ್ಗದಲ್ಲಿರಬಾರದು, ಇದನ್ನು ಬಿಟ್ಟು ಯೆಹೂದ ದೇಶಕ್ಕೆ ಹೋಗು” ಎಂದು ಹೇಳಿದ್ದರಿಂದ ಅವನು ಹೆರೆತ್ ಅರಣ್ಯಕ್ಕೆ ಹೋದನು.",
			"6": "<sup>6</sup> ಒಂದು ದಿನ ಸೌಲನು ತನ್ನ ಎಲ್ಲಾ ಪರಿವಾರದವರ ನಡುವೆ ಕೈಯಲ್ಲಿ ಬರ್ಜಿಯನ್ನು ಹಿಡಿದುಕೊಂಡು, ಗಿಬೆಯ ಗುಡ್ಡದ ಪಿಚುಲ ವೃಕ್ಷದ ಕೆಳಗೆ ಕುಳಿತುಕೊಂಡಿರಲು ದಾವೀದನೂ ಅವನ ಜನರೂ ಅಡಗಿಕೊಂಡಿರುವ ಸ್ಥಳವು ತಿಳಿದುಬಂದಿದೆ ಎಂಬ ಸುದ್ದಿಯು ಅವನಿಗೆ ಮುಟ್ಟಿತು.",
			"7": "<sup>7</sup> ಆಗ ಅವನು ತನ್ನ ಸುತ್ತಲೂ ನಿಂತಿದ್ದ ಪರಿವಾರದವರಿಗೆ, “ಬೆನ್ಯಾಮೀನನ ಮಕ್ಕಳೇ ಕೇಳಿರಿ. ನೀವೆಲ್ಲರೂ ನನಗೆ ಒಳಸಂಚು ಮಾಡುವುದೇಕೆ? ಇಷಯನ ಮಗನು ನಿಮ್ಮೆಲ್ಲರಿಗೂ ಹೊಲಗಳನ್ನು, ದ್ರಾಕ್ಷೇತೋಟಗಳನ್ನು ಕೊಡುವನೋ? ಎಲ್ಲರನ್ನು ಸಹಸ್ರಾಧಿಪತಿಗಳನ್ನಾಗಲಿ, ಶತಾಧಿಪತಿಗಳನ್ನಾಗಲಿ ಮಾಡುವನೋ?",
			"8": "<sup>8</sup> ನನ್ನ ಮಗನು ಇಷಯನ ಮಗನೊಡನೆ ಒಡಂಬಡಿಕೆ ಮಾಡಿಕೊಂಡದ್ದನ್ನು ನಿಮ್ಮಲ್ಲಿ ಒಬ್ಬನಾದರೂ ನನಗೆ ತಿಳಿಸಲಿಲ್ಲವಲ್ಲಾ. ನನ್ನ ಸೇವಕನು ನನಗೆ ವಿರೋಧವಾಗಿ ಒಳಸಂಚುಮಾಡುವ ಹಾಗೆ ನನ್ನ ಮಗನೇ, ಅವನನ್ನು ಎಬ್ಬಿಸಿದ್ದಾನೆಂಬುದನ್ನು ಒಬ್ಬನಾದರೂ ನನಗೆ ಯಾಕೆ ತಿಳಿಸಲಿಲ್ಲ? ನೀವು ನನ್ನ ಚಿಂತೆಯನ್ನೇ ಬಿಟ್ಟಿದ್ದೀರಿ” ಅಂದನು",
			"9": "<sup>9</sup> ಆಗ ಸೌಲನ ಸೇವಕರ ಬಳಿಯಲ್ಲಿ ನಿಂತಿದ್ದ ಎದೋಮ್ಯನಾದ ದೋಯೇಗನು, “ಇಷಯನ ಮಗನು ನೋಬದಲ್ಲಿರುವ ಅಹೀಟೂಬನ ಮಗನಾದ ಅಹೀಮೆಲೆಕನ ಬಳಿಗೆ ಬಂದದ್ದನ್ನು ಕಂಡೆನು.",
			"10": "<sup>10</sup> ಅವನು ಇವನಿಗೆ ಯೆಹೋವನ ಚಿತ್ತವನ್ನು ತಿಳಿಸಿ, ಆಹಾರವನ್ನೂ, ಫಿಲಿಷ್ಟಿಯನಾದ ಗೊಲ್ಯಾತನ ಕತ್ತಿಯನ್ನೂ ಕೊಟ್ಟನು” ಎಂದು ಹೇಳಿದನು.",
			"11": "<sup>11</sup> ಕೂಡಲೆ ಸೌಲನು ಅಹೀಟೂಬನ ಮಗನೂ ಯಾಜಕನೂ ಆಗಿದ್ದ ಅಹೀಮೆಲೆಕನನ್ನೂ, ಅವನ ಕುಟುಂಬಕ್ಕೆ ಸೇರಿದ ನೋಬದ ಬೇರೆ ಎಲ್ಲಾ ಯಾಜಕರೊಡನೆ ಕರೆದುಕೊಂಡು ಬರುವಂತೆ ಕಳುಹಿಸಿದನು. ಅವರೆಲ್ಲರೂ ಅರಸನ ಸನ್ನಿಧಿಗೆ ಬರಲು,",
			"12": "<sup>12</sup> ಸೌಲನು, “ಅಹೀಟೂಬನ ಮಗನೇ ಕೇಳು” ಅಂದನು. ಅದಕ್ಕೆ ಅವನು, “ಒಡೆಯಾ ಅಪ್ಪಣೆಯಾಗಲಿ” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"13": "<sup>13</sup> ಆಗ ಸೌಲನು ಅವನಿಗೆ, “ನೀನು ನನಗೆ ವಿರೋಧವಾಗಿ ಇಷಯನ ಮಗನೊಡನೆ ಒಳಸಂಚುಮಾಡಿದ್ದೇಕೆ? ಅವನು ನನಗೆ ವಿರೋಧವಾಗಿ ಎದ್ದು ನನ್ನ ಜೀವಕ್ಕಾಗಿ ಹೊಂಚುಹಾಕುವಂತೆ ನೀನು ಅವನಿಗೆ ರೊಟ್ಟಿಕತ್ತಿಗಳನ್ನು ಕೊಟ್ಟು, ದೈವೋತ್ತರವನ್ನು ತಿಳಿಸಿದ್ದೇಕೆ?” ಎಂದು ಕೇಳಿದನು",
			"14": "<sup>14</sup> ಅವನು, “ನಿನ್ನ ಎಲ್ಲಾ ಸೇವಕರಲ್ಲಿ ಅರಸನ ಅಳಿಯನಾಗಿರುವ ದಾವೀದನಂತೆ ನಂಬಿಗಸ್ತನಾದವನು ಯಾವನಿದ್ದಾನೆ? ಅವನು ನಿನ್ನ ಆಲೋಚಕರಲ್ಲೋಬ್ಬನು, ನಿನ್ನ ಮನೆಯಲ್ಲಿ ಗೌರವಸ್ಥನೂ ಆಗಿದ್ದನಲ್ಲವೇ?",
			"15": "<sup>15</sup> ನಾನು ಅವನಿಗೋಸ್ಕರ ದೇವರ ಸನ್ನಿಧಿಯಲ್ಲಿ ವಿಚಾರಮಾಡಿದ್ದು ಇದೇ ಮೊದಲನೆಯ ಸಾರಿಯೋ? ಇದು ನನಗೆ ದೂರವಾಗಿರಲಿ. ಅರಸನು ತನ್ನ ಸೇವಕರ ಮೇಲೆ ಅವನ ಮನೆಯವರ ಮೇಲೆ ಇಂಥ ಮಾತನ್ನು ಹೊರಿಸಬಾರದು. ಈ ವಿಷಯದಲ್ಲಿ ನಿನ್ನ ಸೇವಕನಿಗೆ ಸ್ವಲ್ಪವೂ ಗೊತ್ತಿರಲಿಲ್ಲ” ಎಂದು ಉತ್ತರ ಕೊಟ್ಟನು.",
			"16": "<sup>16</sup> ಆಗ ಅರಸನು ಅವನಿಗೆ, “ಅಹೀಮೆಲೆಕನೇ ನೀನೂ ನಿನ್ನ ಮನೆಯವರೂ ಸಾಯಲೇ ಬೇಕು” ಎಂದು ಹೇಳಿ ತನ್ನ ಬಳಿಯಲ್ಲಿದ್ದ",
			"17": "<sup>17</sup> ಸಿಪಾಯಿಗಳಿಗೆ, “ನೀವು ಯೆಹೋವನ ಈ ಯಾಜಕರನ್ನು ಕೊಲ್ಲಿರಿ. ದಾವೀದನು ಓಡಿಹೋದದ್ದು ಗೊತ್ತಿದ್ದರೂ ನನಗೆ ತಿಳಿಸದೇ ಅವನಿಗೆ ಸಹಾಯ ಮಾಡಿದ್ದಾರೆ” ಎಂದು ಹೇಳಿ ಅವರನ್ನು ಕೊಲ್ಲಲು ಆಜ್ಞಾಪಿಸಿದನು. ಆದರೆ ಅರಸನ ಸಿಪಾಯಿಗಳು ಯಾಜಕರ ಮೇಲೆ ಕೈಹಾಕುವುದಕ್ಕೆ ಮನಸ್ಸು ಮಾಡಲಿಲ್ಲ.",
			"18": "<sup>18</sup> ಆದ್ದರಿಂದ ಸೌಲನು ಎದೋಮ್ಯನಾದ ದೋಯೇಗನಿಗೆ, “ನೀನು ಹೋಗಿ ಅವರನ್ನು ಕೊಲ್ಲು” ಎಂದು ಆಜ್ಞಾಪಿಸಲು, ಅವನು ನಾರು ಬಟ್ಟೆಯ ಎಪೋದನ್ನು ಧರಿಸಿಕೊಂಡಿದ್ದ ಎಂಭತ್ತೈದು ಯಾಜಕರನ್ನು ಆ ದಿನ ಕೊಂದುಹಾಕಿದನು.",
			"19": "<sup>19</sup> ಅನಂತರ ಅವನು ಯಾಜಕರ ಪಟ್ಟಣವಾದ ನೋಬಕ್ಕೆ ಹೋಗಿ ಅಲ್ಲಿನ ಸ್ತ್ರೀಪುರುಷರನ್ನೂ, ಶಿಶುಬಾಲಕರನ್ನೂ ದನಕುರಿಕತ್ತೆಗಳನ್ನು, ಕತ್ತಿಯಿಂದ ಸಂಹರಿಸಿದನು.",
			"20": "<sup>20</sup> ಅಹೀಟೂಬನ ಮಗನಾದ ಅಹೀಮೆಲೆಕನ ಮಕ್ಕಳಲ್ಲೊಬ್ಬನಾದ ಎಬ್ಯಾತಾರನೆಂಬುವನು ತಪ್ಪಿಸಿಕೊಂಡು ದಾವೀದನ ಬಳಿಗೆ ಓಡಿಬಂದು,",
			"21": "<sup>21</sup> ಸೌಲನು ಯೆಹೋವನ ಯಾಜಕರನ್ನು ಕೊಲ್ಲಿಸಿದ ಸಂಗತಿಯನ್ನು ಅವನಿಗೆ ತಿಳಿಸಿದನು.",
			"22": "<sup>22</sup> ದಾವೀದನು ಎದೋಮ್ಯನಾದ ದೋಯೇಗನನ್ನು, “ನಾನು ಅಲ್ಲಿ ಕಂಡಾಗಲೇ ಇವನು ಸೌಲನಿಗೆ ಹೇಗೂ ಈ ಸಂಗತಿಯನ್ನು ತಿಳಿಸುವನೆಂದು ತಿಳಿದುಕೊಂಡೆನು. ನಿನ್ನ ಸಂಬಂಧಿಕರ ಸಾವಿಗೆ ನಾನೇ ಕಾರಣನಾದೆನಲ್ಲಾ.",
			"23": "<sup>23</sup> ನೀನಾದರೋ ಹೆದರದೆ ನನ್ನ ಸಂಗಡ ಇರು. ನನ್ನ ಪ್ರಾಣವನ್ನು ತೆಗೆಯಬೇಕೆಂದಿರುವವನೇ ನಿನ್ನ ಪ್ರಾಣವನ್ನೂ ತೆಗೆಯಬೇಕೆಂದಿರುತ್ತಾನೆ. ನೀನು ನನ್ನ ಜೊತೆಯಲ್ಲಿರುವುದಾದರೆ ಸುರಕ್ಷಿತನಾಗಿರುವಿ” ಎಂದನು."
		},
		"23": {
			"1": "<sup>1</sup> ಫಿಲಿಷ್ಟಿಯರು ಕೆಯೀಲಕ್ಕೆ ಮುತ್ತಿಗೆ ಹಾಕಿ ಅಲ್ಲಿನ ಕಣಗಳನ್ನು ಸೂರೆಮಾಡುತ್ತಿದ್ದಾರೆ ಎಂಬ ವರ್ತಮಾನವು ದಾವೀದನಿಗೆ ಮುಟ್ಟಲು",
			"2": "<sup>2</sup> ಅವನು, “ನಾನು ಹೋಗಿ ಆ ಫಿಲಿಷ್ಟಿಯರನ್ನು ಹೊಡೆಯಬಹುದೋ” ಎಂದು ಯೆಹೋವನನ್ನು ಕೇಳಿದನು. ಆತನು ಅವನಿಗೆ, “ಹೋಗಿ ಫಿಲಿಷ್ಟಿಯರನ್ನು ಹೊಡೆದು ಕೆಯೀಲಾ ಊರನ್ನು ರಕ್ಷಿಸು” ಎಂದು ಉತ್ತರ ಕೊಟ್ಟನು.",
			"3": "<sup>3</sup> ಆದರೆ ದಾವೀದನ ಸೈನಿಕರು ಅವನಿಗೆ, “ನಮಗೆ ಯೆಹೂದ ಸೀಮೆಯಲ್ಲಿಯೇ ಇಷ್ಟು ಭಯವಿರುವಾಗ ಫಿಲಿಷ್ಟಿಯ ಸೈನ್ಯದೊಡನೆ ಯುದ್ಧಮಾಡುವುದಕ್ಕೆ ಕೆಯೀಲಕ್ಕೆ ಹೋದರೆ ಅಲ್ಲಿ ಇನ್ನೂ ಹೆಚ್ಚು ಭಯ ಉಂಟಾಗುತ್ತದೆ” ಅಂದರು.",
			"4": "<sup>4</sup> ಆದುದರಿಂದ ಅವನು ಪುನಃ ಯೆಹೋವನನ್ನು ಕೇಳಿದನು. ಆತನು, “ನೀನೆದ್ದು ಕೆಯೀಲಕ್ಕೆ ಹೋಗು. ನಾನು ಫಿಲಿಷ್ಟಿಯರನ್ನು ನಿನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸಿಕೊಡುವೆನು” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"5": "<sup>5</sup> ಆಗ ದಾವೀದನು ತನ್ನ ಜನರೊಡನೆ ಕೆಯೀಲಕ್ಕೆ ಹೋಗಿ, ಫಿಲಿಷ್ಟಿಯರೊಡನೆ ಯುದ್ಧಮಾಡಿ, ಅವರನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ಸೋಲಿಸಿ, ಅವರ ಪಶುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಕೆಯೀಲದವರನ್ನು ರಕ್ಷಿಸಿದನು.",
			"6": "<sup>6</sup> ದಾವೀದನ ಬಳಿಗೆ ಓಡಿಬಂದಿದ್ದ ಅಹೀಮೆಲೆಕನ ಮಗನಾದ ಎಬ್ಯಾತಾರನು ಕೆಯೀಲಕ್ಕೆ ಬರುವಾಗ ಏಫೋದನ್ನು ತಂದಿದ್ದನು,",
			"7": "<sup>7</sup> ದಾವೀದನು ಕೆಯೀಲದಲ್ಲಿದ್ದಾನೆಂಬ ವರ್ತಮಾನವು ಸೌಲನಿಗೆ ಮುಟ್ಟಿತು. ಆಗ ಅವನು, ದೇವರು ದಾವೀದನನ್ನು ನನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸಿದನು. ಅವನು ಅಗುಳಿ ಬಾಗಿಲುಗಳಿಂದ ಭದ್ರವಾಗಿರುವ ಪಟ್ಟಣವನ್ನು ಪ್ರವೇಶಿಸಿದ್ದಾನಲ್ಲಾ ಅವನು ಅಲ್ಲಿಂದ ತಪ್ಪಿಸಿಕೊಳ್ಳಲಾರನು ಅಂದುಕೊಂಡು,",
			"8": "<sup>8</sup> ಕೆಯೀಲದಲ್ಲಿದ್ದ ದಾವೀದನನ್ನೂ, ಅವನ ಜನರನ್ನೂ ಹಿಡಿಯಲು ಹೋಗುವುದಕ್ಕೋಸ್ಕರ ಸೈನ್ಯವನ್ನು ಕೂಡಿಸಿದನು.",
			"9": "<sup>9</sup> ಸೌಲನು ತನಗೆ ಕೇಡುಮಾಡಬೇಕೆಂದಿದ್ದಾನೆ ಎಂಬುದನ್ನು ದಾವೀದನು ತಿಳಿದು ಯಾಜಕನಾದ ಎಬ್ಯಾತಾರನಿಗೆ, “ಏಫೋದನ್ನು ಇಲ್ಲಿಗೆ ತೆಗೆದುಕೊಂಡು ಬಾ” ಎಂದನು.",
			"10": "<sup>10</sup> ಆಗ ದಾವೀದನು, “ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರಾದ ಯೆಹೋವನೇ ಸೌಲನು ಕೆಯೀಲಕ್ಕೆ ಬಂದು ನನ್ನ ನಿಮಿತ್ತವಾಗಿ ಪಟ್ಟಣವನ್ನು ಹಾಳು ಮಾಡಬೇಕೆಂದಿದ್ದಾನೆಂದು ನಿನ್ನ ಸೇವಕನಾದ ನನಗೆ ಸುದ್ದಿ ಬಂದಿತು.",
			"11": "<sup>11</sup> ನಾನು ಕೇಳಿದ ಸುದ್ದಿಯಂತೆ ಅವನು ನಿಜವಾಗಿ ಬರುವನೋ ಕೆಯೀಲಾ ಊರಿನವರು ನನ್ನನ್ನು ಅವನ ಕೈಗೆ ಒಪ್ಪಿಸುವರೋ? ಇಸ್ರಾಯೇಲರ ದೇವರಾದ ಯೆಹೋವನೇ ಈ ವಿಷಯದಲ್ಲಿ ಉತ್ತರ ದಯಪಾಲಿಸು” ಎಂದು ಪ್ರಾರ್ಥಿಸಲು ಅವನು ಬರುವನೆಂಬುದಾಗಿ ಯೆಹೋವನಿಂದ ಉತ್ತರ ಸಿಕ್ಕಿತು.",
			"12": "<sup>12</sup> ದಾವೀದನು ತಿರುಗಿ ಯೆಹೋವನನ್ನು, “ಕೆಯೀಲದವರು ನನ್ನನ್ನೂ, ನನ್ನ ಜನರನ್ನೂ ಸೌಲನ ಕೈಗೆ ಒಪ್ಪಿಸುವರೋ” ಎಂದು ಕೇಳಿದಾಗ ಆತನು, “ಒಪ್ಪಿಸಿಕೊಡುವರು” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"13": "<sup>13</sup> ಆಗ ದಾವೀದನು ತನ್ನ ಸಂಗಡ ಇದ್ದ ಆರುನೂರು ಜನರ ಸಹಿತವಾಗಿ ಕೆಯೀಲಾ ಊರನ್ನು ಬಿಟ್ಟು ಮನಬಂದ ಕಡೆಗೆ ಅಲೆದಾಡಿದರು. ದಾವೀದನು ಕೆಯೀಲದಿಂದ ತಪ್ಪಿಸಿಕೊಂಡನೆಂದು ಸೌಲನಿಗೆ ಗೊತ್ತಾಗಲು ಅವನು ಹೊರಡುವುದನ್ನು ನಿಲ್ಲಿಸಿಬಿಟ್ಟನು.",
			"14": "<sup>14</sup> ಅನಂತರ ದಾವೀದನು ಅರಣ್ಯದಲ್ಲಿರುವ ಆಯಾ ಆಶ್ರಯಗಿರಿಗಳಲ್ಲಿ ಇರುತ್ತಿದ್ದನು. ಅನಂತರ ಜೀಫ್ ಅರಣ್ಯದ ಪರ್ವತಪ್ರಾಂತ್ಯಕ್ಕೆ ಬಂದು ಅಲ್ಲಿ ವಾಸಮಾಡಿದನು. ಸೌಲನು ಪ್ರತಿದಿನ ಅವನನ್ನು ಹುಡುಕುತ್ತಿದ್ದರೂ ದೇವರು ಅವನನ್ನು ಸೌಲನ ಕೈಗೆ ಒಪ್ಪಿಸಲಿಲ್ಲ.",
			"15": "<sup>15</sup> ಸೌಲನು ತನ್ನನ್ನು ಕೊಲ್ಲಲು ಹೊರಟಿದ್ದಾನೆಂದು ದಾವೀದನು ತಿಳಿದು ಜೀಫ್ ಅರಣ್ಯದ ಹೋರೆಷದಲ್ಲಿ ಅಡಗಿಕೊಂಡಿದ್ದನು.",
			"16": "<sup>16</sup> ಆಗ ಸೌಲನ ಮಗನಾದ ಯೋನಾತಾನನು ಅಲ್ಲಿಗೆ ಹೋಗಿ,",
			"17": "<sup>17</sup> ದಾವೀದನಿಗೆ, “ಭಯಪಡಬೇಡ; ನೀನು ನನ್ನ ತಂದೆಯಾದ ಸೌಲನ ಕೈಗೆ ಸಿಕ್ಕಿಬೀಳುವುದಿಲ್ಲ. ನೀನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾಗುವಿ; ನಾನು ನಿನಗೆ ಎರಡನೆಯವನಾಗಿರುವೆನು. ಹೀಗಾಗುವುದೆಂದು ನನ್ನ ತಂದೆಯಾದ ಸೌಲನೂ ತಿಳಿದುಕೊಂಡಿದ್ದಾನೆ” ಎಂದು ಹೇಳಿ, ದೇವರಲ್ಲಿ ಅವನನ್ನು ಬಲಪಡಿಸಿದನು.",
			"18": "<sup>18</sup> ಅವರಿಬ್ಬರೂ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಒಡಂಬಡಿಕೆ ಮಾಡಿಕೊಂಡರು. ತರುವಾಯ ಯೋನಾತಾನನು ತನ್ನ ಮನೆಗೆ ಹೋದನು. ದಾವೀದನು ಹೋರೆಷದಲ್ಲಿಯೇ ಇದ್ದನು.",
			"19": "<sup>19</sup> ಜೀಫ್ಯರು ಗಿಬೆಯಲ್ಲಿದ್ದ ಸೌಲನ ಬಳಿಗೆ ಬಂದು ಅವನಿಗೆ, “ಒಡೆಯಾ, ಕೇಳು ದಾವೀದನು ನಮ್ಮ ಪ್ರಾಂತ್ಯಕ್ಕೆ ಸೇರಿದ ಹೋರೆಷದ ಗಿರಿಗಳಲ್ಲಿ, ಅಂದರೆ ಅರಣ್ಯದ ದಕ್ಷಿಣದಲ್ಲಿರುವ ಹಕೀಲಾ ಬೆಟ್ಟದಲ್ಲಿ ಅಡಗಿಕೊಂಡಿದ್ದಾನೆ.",
			"20": "<sup>20</sup> ಅರಸನು ತನ್ನ ಇಚ್ಛೆಯಂತೆ ನಮ್ಮ ಜೊತೆಯಲ್ಲಿ ಬರೋಣವಾಗಲಿ. ಅವನನ್ನು ಅರಸನ ಕೈಗೆ ಒಪ್ಪಿಸುವ ಕೆಲಸ ನಮ್ಮದು” ಎಂದು ಹೇಳಿದರು.",
			"21": "<sup>21</sup> ಆಗ ಸೌಲನು ಅವರಿಗೆ, “ನೀವು ನನ್ನ ಮೇಲೆ ಕನಿಕರ ತೋರಿಸಿ ಸಹಾಯ ಮಾಡುತ್ತಿರುವುದರಿಂದ ಯೆಹೋವನ ಆಶೀರ್ವಾದವು ನಿಮ್ಮ ಮೇಲಿರಲಿ.",
			"22": "<sup>22</sup> ನೀವು ದಯವಿಟ್ಟು ಹೋಗಿ ಅವನು ಅಡಗಿಕೊಂಡಿರುವ ಸ್ಥಳವನ್ನು, ಅಲ್ಲಿ ಅವನನ್ನು ಕಂಡವರನ್ನೂ ಸೂಕ್ಷ್ಮವಾಗಿ ವಿಚಾರಿಸಿ, ತಿಳಿದುಕೊಳ್ಳಿರಿ. ಅವನು ಬಹು ಯುಕ್ತಿವಂತನೆಂದು ಕೇಳಿದ್ದೇನೆ.",
			"23": "<sup>23</sup> ಆದುದರಿಂದ ಅವನು ಅಡಗಿಕೊಳ್ಳತಕ್ಕ ಎಲ್ಲಾ ಗುಪ್ತಸ್ಥಳಗಳನ್ನು ಗೊತ್ತುಮಾಡಿಕೊಂಡು ಸರಿಯಾದ ವರ್ತಮಾನವನ್ನು ನನಗೆ ತಲುಪಿಸಿರಿ. ನಾನು ನಿಮ್ಮ ಸಂಗಡ ಬರುವೆನು. ಅವನು ದೇಶದಲ್ಲಿರುವುದಾದರೆ ಎಲ್ಲಾ ಯೆಹೂದ ಪ್ರಜೆಗಳ ಮದ್ಯದಲ್ಲಾಗಲಿ ಹುಡುಕಿ ಅವನನ್ನು ಕಂಡುಹಿಡಿಯುವೆನು” ಅಂದನು.",
			"24": "<sup>24</sup> ಅವರು ಹೊರಟು ಸೌಲನ ಮುಂದಾಗಿ ಜೀಫಿಗೆ ಬಂದರು. ದಾವೀದನೂ ಅವನ ಜನರೂ ಮಾವೋನ ಅರಣ್ಯದ ದಕ್ಷಿಣ ದಿಕ್ಕಿನಲ್ಲಿರುವ ಅರಾಬ್ ಎಂಬ ತಗ್ಗಾದ ಪ್ರದೇಶದಲ್ಲಿದ್ದನು.",
			"25": "<sup>25</sup> ಸೌಲನು ತನ್ನ ಜನರೊಡನೆ ದಾವೀದನನ್ನು ಹಿಡಿಯುವುದಕ್ಕೆ ಬಂದನು. ಈ ವರ್ತಮಾನವು ದಾವೀದನಿಗೆ ತಲುಪಲು ಅವನು ಮಾವೋನಿನಲ್ಲಿರುವ ಒಂದು ಕಡಿದಾದ ಬೆಟ್ಟಕ್ಕೆ ಹೋಗಿ ಅಡಗಿಕೊಂಡನು. ಸೌಲನು ಇದನ್ನು ಕೇಳಿ ಮಾವೋನ ಅರಣ್ಯಕ್ಕೆ ಬಂದು ಆ ಬೆಟ್ಟದ ಒಂದು ಕಡೆಯಲ್ಲಿ ಹೋಗುತ್ತಿದ್ದನು.",
			"26": "<sup>26</sup> ಅದರ ಇನ್ನೊಂದು ಕಡೆಯಲ್ಲಿ ದಾವೀದನೂ ಅವನ ಜನರೂ ಇದ್ದರು. ದಾವೀದನೂ ಅವನ ಜನರೂ ತಪ್ಪಿಸಿಕೊಳ್ಳುವುದಕ್ಕೆ ತ್ವರೆಪಡುವಷ್ಟರಲ್ಲಿ ಸೌಲನೂ, ಅವನ ಜನರೂ ಬಂದು ಅವರನ್ನು ಮುತ್ತಿಗೆ ಹಾಕುವುದಕ್ಕಿದ್ದರು.",
			"27": "<sup>27</sup> ಆಗಲೇ ಒಬ್ಬ ದೂತನು ಸೌಲನ ಬಳಿಗೆ ಬಂದು ಅವನಿಗೆ, “ಬೇಗ ಬಾ, ಫಿಲಿಷ್ಟಿಯರು ದೇಶದೊಳಗೆ ನುಗ್ಗಿದ್ದಾರೆ” ಎಂದು ತಿಳಿಸಿದನು.",
			"28": "<sup>28</sup> ಅವನು ದಾವೀದನನ್ನು ಹಿಡಿಯುವ ಕೆಲಸವನ್ನು ಬಿಟ್ಟು ಫಿಲಿಷ್ಟಿಯರ ವಿರುದ್ಧ ಯುದ್ಧಮಾಡಲು ಹೊರಟನು. ಆದುದರಿಂದ ಆ ಬೆಟ್ಟಕ್ಕೆ “ಮಕ್ಹೆಲೋಕೆತ್” ಎಂದರೆ “ಪಾರದ ಬಂಡೆ” ಎಂದು ಹೆಸರಾಯಿತು.",
			"29": "<sup>29</sup> ದಾವೀದನು ಅಲ್ಲಿಂದ ಗಟ್ಟಾಹತ್ತಿ ಹೋಗಿ ಏಂಗೆದಿಯ ಗಿರಿಗಳಲ್ಲಿ ವಾಸಿಸಿದನು."
		},
		"24": {
			"1": "<sup>1</sup> ಸೌಲನು ಫಿಲಿಷ್ಟಿಯರನ್ನು ಓಡಿಸಿ ಹಿಂದಿರುಗಿದಾಗ ದಾವೀದನು ಏಂಗೆದಿಯ ಅರಣ್ಯದಲ್ಲಿ ಇದ್ದಾನೆಂದು ಅವನಿಗೆ ತಿಳಿದುಬಂದಿತು.",
			"2": "<sup>2</sup> ಆಗ ಅವನು ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲರಲ್ಲಿ ಶ್ರೇಷ್ಟರಾದ ಮೂರು ಸಾವಿರ ಮಂದಿ ಸೈನಿಕರನ್ನು ಆರಿಸಿಕೊಂಡು ಕಾಡು, ಗವಿ ಬಂಡೆಗಳಲ್ಲಿದ್ದ ದಾವೀದನನ್ನೂ, ಅವನ ಜನರನ್ನೂ ಹುಡುಕುವುದಕ್ಕೋಸ್ಕರ ಹೊರಟನು.",
			"3": "<sup>3</sup> ಅವನು ಮಾರ್ಗದಲ್ಲಿ ಕುರಿಹಟ್ಟಿಗಳ ಬಳಿಯಲ್ಲಿ ಒಂದು ಗವಿಯನ್ನು ಕಂಡು ಶೌಚಕ್ಕೋಸ್ಕರ ಅದರೊಳಗೆ ಪ್ರವೇಶಿಸಿದನು. ದಾವೀದನೂ ಅವನ ಜನರೂ ಅದೇ ಗವಿಯ ಹಿಂಭಾಗದಲ್ಲಿ ಅಡಗಿಕೊಂಡಿದ್ದರು.",
			"4": "<sup>4</sup> ಜನರು ದಾವೀದನಿಗೆ “ನಿನ್ನ ಶತ್ರುವನ್ನು ನಿನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸಿಕೊಡುವೆನು, ಆಗ ನೀನು ಅವನನ್ನು ಮನಸ್ಸಿಗೆ ಬಂದಂತೆ ನಡಿಸಬಹುದು ಎಂದು ಯೆಹೋವನು ನಿನಗೆ ಹೇಳಿದ ಮಾತು ನೆರವೇರುವ ದಿನ ಇದೇ” ಅಂದಾಗ ಅವನೆದ್ದು ಮೆಲ್ಲನೆ ಹೋಗಿ ಸೌಲನ ನಿಲುವಂಗಿಯ ತುದಿಯನ್ನು ಕತ್ತರಿಸಿಕೊಂಡನು.",
			"5": "<sup>5</sup> ಅನಂತರ ಸೌಲನ ನಿಲುವಂಗಿಯ ತುದಿಯನ್ನು ಕತ್ತರಿಸಿದ್ದಕ್ಕಾಗಿ ಅವನ ಮನಸ್ಸಾಕ್ಷಿಯು ಅವನನ್ನು ಚುಚ್ಚತೊಡಗಿತು.",
			"6": "<sup>6</sup> ಅವನು ತನ್ನ ಜನರಿಗೆ, “ಅವನು ಯೆಹೋವನಿಂದ ಅಭಿಷೇಕಿಸಲ್ಪಟ್ಟವನೂ, ನನ್ನ ಒಡೆಯನೂ ಆಗಿದ್ದಾನೆ. ನಾನು ನಿಮ್ಮ ಮಾತು ಕೇಳಿ ಯೆಹೋವನ ಅಭಿಷಿಕ್ತನಿಗೆ ವಿರೋಧವಾಗಿ ಕೈಯೆತ್ತದಂತೆ ಯೆಹೋವನೇ ನನಗೆ ಅಡ್ಡಿ ಮಾಡಲಿ” ಎಂದು ಹೇಳಿದನು",
			"7": "<sup>7</sup> ಈ ಮಾತುಗಳಿಂದ ಅವನು ಸೌಲನಿಗೆ ವಿರೋಧವಾಗಿ ಏಳದಂತೆ ತನ್ನ ಜನರನ್ನು ತಡೆದನು. ಸೌಲನು ಗವಿಯಿಂದ ಹೊರಗೆ ಬಂದು ಸ್ವಲ್ಪ ಮುಂದೆ ಹೋದ ಮೇಲೆ",
			"8": "<sup>8</sup> ದಾವೀದನೂ ಹೊರಗೆ ಬಂದು, “ಅರಸನೇ ನನ್ನ ಒಡೆಯನೇ” ಎಂದು ಅವನನ್ನು ಕೂಗಿದನು. ಸೌಲನು ಹಿಂದಿರುಗಿ ನೋಡಲು ಅವನಿಗೆ ಸಾಷ್ಟಾಂಗ ನಮಸ್ಕಾರ ಮಾಡಿ,",
			"9": "<sup>9</sup> “ದಾವೀದನು ನಿನಗೆ ಕೇಡುಮಾಡಬೇಕೆಂದಿರುತ್ತಾನೆಂದು ಹೇಳುವವರ ಮಾತಿಗೆ ನೀನೇಕೆ ಕಿವಿಗೊಡುತ್ತೀ?",
			"10": "<sup>10</sup> ಈ ಹೊತ್ತು ಯೆಹೋವನು ಗವಿಯಲ್ಲಿ ನಿನ್ನನ್ನು ನನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸಿಕೊಟ್ಟಿದ್ದಾನೆಂಬುದು ಈಗ ನಿನಗೆ ಗೊತ್ತಾಯಿತಲ್ಲವೋ? ನಿನ್ನನ್ನು ಕೊಂದುಬಿಡಬೇಕೆಂದು ಕೆಲವರು ನನಗೆ ಹೇಳಿದರು. ಆದರೆ ನಾನು ಅವರಿಗೆ, ‘ಯೆಹೋವನ ಅಭಿಷಿಕ್ತನಾದ ನನ್ನ ಒಡೆಯನ ಮೇಲೆ ಕೈಹಾಕುವುದಿಲ್ಲ’ ಎಂದು ಹೇಳಿ ನಿನ್ನನ್ನು ಉಳಿಸಿದೆನು.",
			"11": "<sup>11</sup> ನನ್ನ ತಂದೆಯೇ, ಇಗೋ ನೋಡು, ನನ್ನ ಕೈಯಲ್ಲಿ ನಿನ್ನ ನಿಲುವಂಗಿಯ ತುಂಡು ಇದೆ. ನಾನು ನಿನ್ನನ್ನು ಕೊಲ್ಲದೆ, ನಿನ್ನ ನಿಲುವಂಗಿಯ ತುದಿಯನ್ನು ಕತ್ತರಿಸಿಕೊಂಡೆನಷ್ಟೇ ಇದರಿಂದ ನನ್ನಲ್ಲಿ ಯಾವ ದೋಷವೂ ಅಪರಾಧವೂ ಇರುವುದಿಲ್ಲ. ನಾನು ನಿನಗೆ ವಿರೋಧವಾಗಿ ದ್ರೋಹಮಾಡಲಿಲ್ಲ ಎಂದು ತಿಳಿದುಕೋ. ಆದರೂ ನೀನು ನನ್ನ ಪ್ರಾಣಕ್ಕೆ ಹೊಂಚು ಹಾಕುತ್ತಿರುವಿಯಲ್ಲಾ.",
			"12": "<sup>12</sup> ಯೆಹೋವನೇ ನಮ್ಮಿಬ್ಬರ ಮಧ್ಯೆ ವ್ಯಾಜ್ಯವನ್ನು ತೀರಿಸಲಿ. ಆತನೇ ನನಗೋಸ್ಕರ ನಿನಗೆ ಮುಯ್ಯಿ ತೀರಿಸಲಿ. ನಾನಂತೂ ನಿನಗೆ ವಿರೋಧವಾಗಿ ಕೈಯೆತ್ತುವುದಿಲ್ಲ.",
			"13": "<sup>13</sup> ‘ಕೆಟ್ಟವರಿಂದಲೇ ಕೆಡುಕು’ ಎಂಬುದಾಗಿ ಹಿರಿಯರ ಗಾದೆಯುಂಟಷ್ಟೆ. ನಾನು ನಿನಗೆ ವಿರೋಧವಾಗಿ ಕೈಯೆತ್ತುವುದಿಲ್ಲ.",
			"14": "<sup>14</sup> ಇಸ್ರಾಯೇಲರ ಅರಸನೇ ಯಾರನ್ನು ಹಿಂಬಾಲಿಸಿ ಹೊರಟಿರುವೇ? ಯಾರನ್ನು ಹಿಡಿಯಬೇಕೆಂದಿರುತ್ತೀ? ಸತ್ತ ನಾಯಿಯನ್ನೋ? ಒಂದು ಬಡ ನೊಣವನ್ನೋ?.",
			"15": "<sup>15</sup> ಯೆಹೋವನು ನ್ಯಾಯಾಧಿಪತಿಯಾಗಿ ನಮ್ಮಿಬ್ಬರ ವ್ಯಾಜ್ಯವನ್ನು ತೀರಿಸಲಿ. ಆತನೇ ನೋಡಿ ನನಗೋಸ್ಕರ ವಾದಿಸಿ ನನ್ನನ್ನು ನಿನ್ನ ಕೈಯಿಂದ ತಪ್ಪಿಸಲಿ” ಎಂದು ಹೇಳಿದನು.",
			"16": "<sup>16</sup> ದಾವೀದನ ಮಾತುಗಳು ಮುಗಿದ ನಂತರ ಸೌಲನು, “ದಾವೀದನೇ, ನನ್ನ ಮಗನೇ, ಇದು ನಿನ್ನ ಸ್ವರವೋ?” ಅಂದು ಗಟ್ಟಿಯಾಗಿ ಅತ್ತನು.",
			"17": "<sup>17</sup> ಇದಲ್ಲದೆ ಅವನು ದಾವೀದನಿಗೆ, “ನೀನು ನನಗಿಂತ ನೀತಿವಂತನೂ. ನಾನು ನಿನಗೆ ಕೇಡುಮಾಡಿದರೂ ನೀನು ನನಗೆ ಒಳ್ಳೆಯದನ್ನೇ ಮಾಡಿದಿ.",
			"18": "<sup>18</sup> ಯೆಹೋವನು ನನ್ನನ್ನು ನಿನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸಿಕೊಟ್ಟರೂ ನೀನು ನನ್ನನ್ನು ಕೊಲ್ಲಲಿಲ್ಲ. ಇದರಿಂದ ನೀನು ನನಗೆ ಹಿತವನ್ನೇ ಮಾಡುವಂಥವನೆಂಬುದು ಈ ಹೊತ್ತು ಪ್ರಕಟವಾಯಿತು.",
			"19": "<sup>19</sup> ಯಾವನಾದರೂ ತನ್ನ ಕೈಗೆ ಸಿಕ್ಕಿದ ವೈರಿಯನ್ನು ಸುಕ್ಷೇಮದಿಂದ ಕಳುಹಿಸಿಬಿಡುವನೋ? ನೀನು ಈ ಹೊತ್ತು ನನಗೆ ಉಪಕಾರಮಾಡಿದ್ದಕ್ಕಾಗಿ ಯೆಹೋವನು ನಿನಗೆ ಉಪಕಾರ ಮಾಡಲಿ.",
			"20": "<sup>20</sup> ಕೇಳು, ನೀನು ಹೇಗೂ ಅರಸನಾಗುವಿ ಎಂದೂ ಇಸ್ರಾಯೇಲ್ ರಾಜ್ಯವು ನಿನ್ನಿಂದ ಸ್ಥಿರವಾಗುವುದೆಂದು ಬಲ್ಲೆನು.",
			"21": "<sup>21</sup> ಹೀಗಿರುವುದರಿಂದ ನೀನು ನನ್ನ ಸಂತಾನವನ್ನು ನಿರ್ಮೂಲಮಾಡುವುದಿಲ್ಲವೆಂದೂ, ನಮ್ಮ ಕುಲದಿಂದ ನನ್ನ ಹೆಸರನ್ನು ತೆಗೆದುಹಾಕುವುದಿಲ್ಲವೆಂದೂ ಯೆಹೋವನ ಹೆಸರಿನಲ್ಲಿ ಪ್ರಮಾಣ ಮಾಡು” ಎಂದು ಬೇಡಿಕೊಂಡ ಹಾಗೆ ದಾವೀದನು ಪ್ರಮಾಣಮಾಡಿದನು.",
			"22": "<sup>22</sup> ಅನಂತರ ಸೌಲನು ತನ್ನ ಮನೆಗೆ ಹೊರಟನು. ದಾವೀದನು ತನ್ನ ಜನರೊಡನೆ ಆಶ್ರಯಗಿರಿಗೆ ಹೋದನು."
		},
		"25": {
			"1": "<sup>1</sup> ಸಮುವೇಲನು ಮರಣಹೊಂದಿದನು. ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರೂ ಒಟ್ಟಾಗಿ ಬಂದು ಅವನಿಗಾಗಿ ಗೋಳಾಡುತ್ತಾ ಅವನ ಶವವನ್ನು ರಾಮದಲ್ಲಿದ್ದ ಅವನ ಮನೆಯ ನಿವೇಶನದಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ದಾವೀದನು ಹೊರಟು ಪಾರಾನ್ ಮರುಭೂಮಿಗೆ ಹೋದನು.",
			"2": "<sup>2</sup> ಕರ್ಮೆಲಿನಲ್ಲಿ ಸ್ವಂತ ಸೊತ್ತು ಹೊಂದಿದ್ದ ಒಬ್ಬ ಮನುಷ್ಯನಿದ್ದನು. ಅವನು ಮಾವೋನಿನಲ್ಲಿ ವಾಸಿಸುತ್ತಿದ್ದನು. ಅವನ ಹೆಸರು ನಾಬಾಲ್.",
			"3": "<sup>3</sup> ಅವನ ಹೆಂಡತಿಯ ಹೆಸರು ಅಬೀಗೈಲ್. ಆಕೆಯು ಬಹು ಬುದ್ಧಿವಂತೆಯೂ, ಸುಂದರಿಯೂ ಆಗಿದ್ದಳು. ಕಾಲೇಬನ ವಂಶದವನಾದ ಆ ಮನುಷ್ಯನು ನಿಷ್ಠುರನೂ, ದುಷ್ಕರ್ಮಿಯೂ ಆಗಿದ್ದನು. ಅವನು ಬಹು ಶ್ರೀಮಂತನಾಗಿದ್ದನು. ಅವನಿಗೆ ಮೂರು ಸಾವಿರ ಕುರಿಗಳೂ, ಸಾವಿರ ಆಡುಗಳೂ ಇದ್ದವು. ಅವನು ಒಂದು ಸಾರಿ ಕರ್ಮೆಲಿನಲ್ಲಿ ಕುರಿಗಳ ಉಣ್ಣೆಯನ್ನು ಕತ್ತರಿಸುತ್ತಿದ್ದನು.",
			"4": "<sup>4</sup> ನಾಬಾಲನು ಉಣ್ಣೆ ಕತ್ತರಿಸುತ್ತಿದ್ದಾನೆಂಬ ವರ್ತಮಾನವು ಅಲ್ಲಿ ಮರುಭೂಮಿಯಲ್ಲಿದ್ದ ದಾವೀದನಿಗೆ ಮುಟ್ಟಿತು.",
			"5": "<sup>5</sup> ಅವನು ಹತ್ತು ಮಂದಿ ಸೇವಕರನ್ನು ಕರೆದು ಅವರಿಗೆ, “ನೀವು ಕರ್ಮೆಲಿನಲ್ಲಿರುವ ನಮ್ಮ ಸಂಬಂಧಿಕನಾದ ನಾಬಾಲನ ಬಳಿಗೆ ಹೋಗಿ ಅವನ ಕ್ಷೇಮ ಸಮಾಚಾರವನ್ನು ವಿಚಾರಿಸಿರಿ.",
			"6": "<sup>6</sup> ಅವನಿಗೆ ನನ್ನ ಹೆಸರಿನಲ್ಲಿ ‘ನಿನಗೂ, ನಿನ್ನ ಕುಟುಂಬಕ್ಕೂ, ನಿನ್ನ ಸರ್ವಸಂಪತ್ತಿಗೂ ಶುಭವಾಗಲಿ.",
			"7": "<sup>7</sup> ನಿನ್ನ ಕುರಿಗಳ ಉಣ್ಣೆಯನ್ನು ಕತ್ತರಿಸುವ ಕೆಲಸವು ನಡೆಯುತ್ತದೆಂಬ ವರ್ತಮಾನವನ್ನು ಕೇಳಿದ್ದೇನೆ. ನಿನ್ನ ಕುರುಬರು ನಮ್ಮೊಡನೆ ಕರ್ಮೆಲಿನಲ್ಲಿದ್ದ ಕಾಲವೆಲ್ಲಾ ನಾವು ಅವರನ್ನು ತೊಂದರೆಪಡಿಸಲಿಲ್ಲ. ಅವರಿಗೇನೂ ನಷ್ಟವಾಗಲಿಲ್ಲ.",
			"8": "<sup>8</sup> ನಿನ್ನ ಆಳುಗಳನ್ನು ಕೇಳು, ಅವರೇ ಹೇಳುವರು. ಹೀಗಿರುವುದರಿಂದ ಶುಭಕಾಲದಲ್ಲಿ ನಿನ್ನ ಬಳಿಗೆ ಬಂದಿರುವ ನನ್ನ ಸೇವಕರಿಗೆ ನಿನ್ನ ದೃಷ್ಟಿಯಲ್ಲಿ ದಯೆದೊರಕಲಿ. ಕೃಪೆಮಾಡಿ ನಿನ್ನ ಸೇವಕರಿಗೂ ಮಗನಾದ ದಾವೀದನಿಗೂ ನಿನಗಿರುವುದರಲ್ಲಿ ಕೊಡು’ ಎಂದು ಹೇಳಿರಿ” ಎಂದು ಅವರನ್ನು ಕಳುಹಿಸಿದನು.",
			"9": "<sup>9</sup> ದಾವೀದನ ಸೇವಕರು ಹೋಗಿ ಅವನ ಹೆಸರಿನಲ್ಲಿ ಈ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ನಾಬಾಲನಿಗೆ ತಿಳಿಸಿ ಉತ್ತರಕ್ಕಾಗಿ ಕಾಯುತ್ತಿದ್ದರು.",
			"10": "<sup>10</sup> ನಾಬಾಲನು ಅವರಿಗೆ, “ದಾವೀದನಾರು? ಇಷಯನ ಮಗನಾರು? ಯಜಮಾನರನ್ನು ಬಿಟ್ಟು ಹೋದ ಸೇವಕರು ಈಗಿನ ಕಾಲದಲ್ಲಿ ಎಷ್ಟು ಮಂದಿಯಿಲ್ಲ.",
			"11": "<sup>11</sup> ಉಣ್ಣೆಕತ್ತರಿಸುವವರಿಗೋಸ್ಕರ ನಾನು ಸಿದ್ಧ ಮಾಡಿಸಿರುವ ಮಾಂಸವನ್ನು ಆಹಾರಪಾನಗಳನ್ನೂ, ಎಲ್ಲಿಂದಲೋ ಬಂದವರಿಗೆ ಕೊಟ್ಟುಬಿಡಬೇಕೋ?” ಎಂದು ಉತ್ತರ ಕೊಟ್ಟನು.",
			"12": "<sup>12</sup> ಆ ಸೇವಕರು ಹಿಂದಿರುಗಿ ತಮ್ಮ ದಾರಿ ಹಿಡಿದು ದಾವೀದನ ಬಳಿಗೆ ಬಂದು ಅವನಿಗೆ ಎಲ್ಲವನ್ನೂ ತಿಳಿಸಲಾಗಿ,",
			"13": "<sup>13</sup> ಅವನು ತನ್ನ ಜನರಿಗೆ “ಕತ್ತಿಯನ್ನು ಸೊಂಟಕ್ಕೆ ಕಟ್ಟಿಕೊಳ್ಳಿರಿ” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು. ಅವರೆಲ್ಲರು ಕಟ್ಟಿಕೊಂಡರು. ದಾವೀದನೂ ಸೊಂಟಕ್ಕೆ ಕತ್ತಿಯನ್ನು ಕಟ್ಟಿಕೊಂಡನು. ಹೆಚ್ಚುಕಡಿಮೆ ನಾನೂರು ಮಂದಿ ದಾವೀದನ ಜೊತೆಯಲ್ಲಿ ಹೋದರು. ಇನ್ನೂರು ಮಂದಿ ಸಾಮಾನುಗಳ ಬಳಿಯಲ್ಲಿ ಇದ್ದರು.",
			"14": "<sup>14</sup> ನಾಬಾಲನ ಸೇವಕರಲ್ಲಿ ಒಬ್ಬನು ಅವನ ಹೆಂಡತಿಯಾದ ಅಬೀಗೈಲಳಿಗೆ, “ಮರುಭೂಮಿಯಲ್ಲಿರುವ ದಾವೀದನು ನಮ್ಮ ಧಣಿಯ ಕ್ಷೇಮವಿಚಾರಕ್ಕಾಗಿ ದೂತರನ್ನು ಕಳುಹಿಸಲು, ಧಣಿಯು ಬಂದವರನ್ನು ಬೈದನು ಕಳುಹಿಸಿದನು.",
			"15": "<sup>15</sup> ಆ ಜನರು ನಮಗೆ ಬಹಳ ಉಪಕಾರ ಮಾಡಿದವರು. ನಾವು ಅವರೊಡನೆ ಮರುಭೂಮಿಯಲ್ಲಿದ್ದ ಕಾಲವೆಲ್ಲಾ ಅವರು ನಮಗೆ ಏನೂ ತೊಂದರೆಕೊಡಲಿಲ್ಲ, ನಾವು ನಷ್ಟಹೊಂದಲಿಲ್ಲ.",
			"16": "<sup>16</sup> ನಾವು ಕುರಿಗಳನ್ನು ಮೇಯಿಸುವವರಾಗಿ ಅವರೊಡನೆ ಇದ್ದಾಗ ಅವರು ಹಗಲಿರುಳು ನಮಗೆ ಕಾವಲು ಗೋಡೆಯಂತಿದ್ದರು.",
			"17": "<sup>17</sup> ಈಗ ಮಾಡಬೇಕಾದದ್ದನ್ನು ನೀನೇ ಆಲೋಚಿಸಿ ಗೊತ್ತುಮಾಡು. ನಮ್ಮ ಯಜಮಾನನಿಗೂ, ಅವನ ಕುಟುಂಬದವರೆಲ್ಲರಿಗೂ ಕೇಡು ಹತ್ತಿರವಾಗಿದೆ. ಮೂರ್ಖನಾದ ಅವನೊಡನೆ ಮಾತನಾಡುವುದು ಅಸಾಧ್ಯ” ಎಂದು ಹೇಳಿದನು.",
			"18": "<sup>18</sup> ಆಗ ಅಬೀಗೈಲಳು ಶೀಘ್ರವಾಗಿ ಇನ್ನೂರು ರೊಟ್ಟಿ, ಎರಡು ಬುದ್ದಲಿ ದ್ರಾಕ್ಷಾರಸ, ಸಿದ್ಧಮಾಡಿದ ಐದು ಕುರಿಗಳ ಮಾಂಸ, ಐವತ್ತು ಸೇರು ಹುರಿಗಾಳು, ಒಣಗಿದ ನೂರು ದ್ರಾಕ್ಷೇ ಗೊಂಚಲುಗಳು, ಅಂಜೂರ ಹಣ್ಣುಗಳು, ಇನ್ನೂರು ಉಂಡೆಗಳು ಇವುಗಳನ್ನು ಕತ್ತೆಗಳ ಮೇಲೆ ಹೇರಿಸಿ,",
			"19": "<sup>19</sup> ತನ್ನ ಸೇವಕರಿಗೆ, “ನೀವು ಮುಂದೆ ನಡೆಯಿರಿ, ನಾನು ಹಿಂದೆ ಬರುತ್ತೇನೆ” ಎಂದು ಹೇಳಿ ಅವರನ್ನು ಕಳುಹಿಸಿದಳು. ಗಂಡನಾದ ನಾಬಾಲನಿಗೆ ಏನೂ ತಿಳಿಸಲಿಲ್ಲ.",
			"20": "<sup>20</sup> ಆಕೆ ಕತ್ತೆಯ ಮೇಲೆ ಕುಳಿತುಕೊಂಡು ಗುಡ್ಡದ ಬದಿಯಲ್ಲಿ ಹೋಗುತ್ತಿರುವಾಗ, ದಾವೀದನೂ ಅವನ ಜನರೂ ಗುಡ್ಡ ಇಳಿದು ಆ ಕಡೆಯಿಂದ ಬರಲು ಆಕೆಯು ಅವರನ್ನು ಎದುರುಗೊಂಡಳು.",
			"21": "<sup>21</sup> ಅಷ್ಟರಲ್ಲಿ ದಾವೀದನು, “ನಾನು ಮರುಭೂಮಿಯಲ್ಲಿ ಈ ಮನುಷ್ಯನ ಆಸ್ತಿಯಲ್ಲಿ ಏನೂ ನಷ್ಟವಾಗದಂತೆ ಕಾಪಾಡಿದ್ದು ವ್ಯರ್ಥವಾಯಿತು. ಅವನು ಉಪಕಾರಕ್ಕೆ ಅಪಕಾರ ಮಾಡಿದನು.",
			"22": "<sup>22</sup> ಬೆಳಗಾಗುವಷ್ಟರಲ್ಲಿ ಅವನ ಜನರೊಳಗೆ ಒಬ್ಬ ಗಂಡಸಾದರೂ ಉಳಿದರೆ ದೇವರು ದಾವೀದನಿಗೆ ಬೇಕಾದದ್ದನ್ನು ಮಾಡಲಿ” ಎಂದು ಪ್ರತಿಜ್ಞೆ ಮಾಡಿದನು.",
			"23": "<sup>23</sup> ಅಬೀಗೈಲಳು ದಾವೀದನನ್ನು ಕಂಡ ಕೂಡಲೇ ಕತ್ತೆಯಿಂದಿಳಿದು, ಅವನಿಗೆ ಸಾಷ್ಟಾಂಗವಾಗಿ ನಮಸ್ಕರಿಸಿದಳು.",
			"24": "<sup>24</sup> ಆ ಮೇಲೆ ಅವನ ಪಾದಗಳ ಮೇಲೆ ಬಿದ್ದು ಅವನಿಗೆ, “ಸ್ವಾಮೀ ಅಪರಾಧವು ನನ್ನ ಮೇಲೆ ಇರಲಿ. ನಿನ್ನ ದಾಸಿಯು ಮಾತನಾಡುವುದಕ್ಕೆ ಅಪ್ಪಣೆಯಾಗಲಿ. ನನ್ನ ಮಾತನ್ನು ಲಾಲಿಸಬೇಕು.",
			"25": "<sup>25</sup> ಸ್ವಾಮಿಯು ಆ ಮೂರ್ಖನಾದ ನಾಬಾಲನನ್ನು ಲಕ್ಷಿಸದಿರಲಿ. ಅವನ ಹೆಸರು ನಾಬಾಲ. ಹೆಸರಿಗೆ ತಕ್ಕಂತೆ ಅವನು ಮೂರ್ಖನೇ ಆಗಿದ್ದಾನೆ. ನಿನ್ನ ದಾಸಿಯಾದ ನಾನು ಸ್ವಾಮಿಯು ಕಳುಹಿಸಿದ ಸೇವಕರನ್ನು ನೋಡಲಿಲ್ಲ.",
			"26": "<sup>26</sup> ಸ್ವಾಮೀ, ನೀನು ಸ್ವಹಸ್ತದಿಂದ ಮುಯ್ಯಿ ತೀರಿಸಿ ರಕ್ತಾಪರಾಧಕ್ಕೆ ಗುರಿಯಾಗದಂತೆ ಯೆಹೋವನು ನಿನ್ನನ್ನು ಕಾಪಾಡಿದ್ದಾನೆ. ಯೆಹೋವನಾಣೆ, ನಿನ್ನ ಜೀವದಾಣೆ ನಿನ್ನ ವಿರೋಧಿಗಳೂ, ನಿನಗೆ ಕೇಡು ಬಗೆಯುವವರೂ ನಾಬಾಲನ ಗತಿಯನ್ನು ಹೊಂದಲಿ.",
			"27": "<sup>27</sup> ನಿನ್ನ ದಾಸಿಯು ತಂದ ಈ ಕಾಣಿಕೆಯನ್ನು ಸ್ವಾಮಿಯು ತಮ್ಮ ಸೇವಕರಿಗೆ ಸಲ್ಲಿಸಲಿ.",
			"28": "<sup>28</sup> ನಿನ್ನ ದಾಸಿಯ ಅಪರಾಧಕ್ಕೆ ಕ್ಷಮಾಪಣೆಯಾಗಲಿ. ಸ್ವಾಮಿಯೇ, ನೀನು ಯೆಹೋವನ ಶತ್ರುಗಳೊಡನೆ ಯುದ್ಧಮಾಡುವುದರಿಂದ ಆತನು ನಿನ್ನ ಮನೆಯನ್ನು ಶಾಶ್ವತವಾಗಿ ಸ್ಥಿರಪಡಿಸುವನು. ಆದ್ದರಿಂದ ನಿನ್ನ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ನಿನ್ನಲ್ಲಿ ಕೆಟ್ಟತನವು ಕಾಣದಿರಲಿ.",
			"29": "<sup>29</sup> ಯಾವನಾದರೂ ನನ್ನ ಒಡೆಯನಾದ ನಿನ್ನನ್ನು ಹಿಂಸಿಸಿ ಜೀವತೆಗೆಯಬೇಕೆಂದಿದ್ದರೂ, ಆ ನಿನ್ನ ಜೀವವು ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನ ರಕ್ಷಣೆಯಲ್ಲಿರುವ ಜೀವನಿಕ್ಷೇಪದಲ್ಲಿ ಭದ್ರವಾಗಿರಲಿ. ಆದರೆ ಆತನು ನಿನ್ನ ಶತ್ರುಗಳ ಜೀವವನ್ನು ಕವಣೆಯ ಕಲ್ಲನ್ನೋ ಎಂಬಂತೆ ಎಸೆದು ಬಿಡಲಿ.",
			"30": "<sup>30</sup> ಯೆಹೋವನು ನನ್ನ ಸ್ವಾಮಿಯಾದ ನಿನಗೆ ವಾಗ್ದಾನಮಾಡಿರುವ ಎಲ್ಲಾ ಸೌಭಾಗ್ಯವನ್ನು ದಯಪಾಲಿಸಲಿ. ನಿನ್ನನ್ನು ಇಸ್ರಾಯೇಲ್ಯರ ಪ್ರಭುವನ್ನಾಗಿ ಮಾಡಿದ ಮೇಲೆ,",
			"31": "<sup>31</sup> ನಿರಪರಾಧಿಯ ರಕ್ತವನ್ನು ಸುರಿಸಿ ಮುಯ್ಯಿ ತೀರಿಸಿದ್ದರಿಂದ ಉಂಟಾಗುವ ಪಶ್ಚಾತ್ತಾಪ ಮನೋವ್ಯಥೆಗಳಿಗೆ ಕಾರಣವಿರುವುದಿಲ್ಲ. ಆತನೇ ನಿನ್ನನ್ನು ಮಹಾ ಪದವಿಗೆ ತರುವಾಗ ನಿನ್ನ ದಾಸಿಯಾದ ನನ್ನನ್ನು ಮರೆತುಬಿಡಬೇಡ” ಅಂದಳು.",
			"32": "<sup>32</sup> ಆಗ ದಾವೀದನು ಅಬೀಗೈಲಳಿಗೆ, “ಈ ದಿನ ನನ್ನನ್ನು ಎದುರುಗೊಳ್ಳುವುದಕ್ಕಾಗಿ ನಿನ್ನನ್ನು ಕಳುಹಿಸಿದ ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಸ್ತೋತ್ರವಾಗಲಿ.",
			"33": "<sup>33</sup> ಸ್ವಹಸ್ತದಿಂದ ಮುಯ್ಯಿ ತೀರಿಸಿ ರಕ್ತಾಪರಾಧಕ್ಕೆ ಗುರಿಯಾಗದಂತೆ ನನ್ನನ್ನು ತಡೆದ ನೀನೂ, ನಿನ್ನ ಬುದ್ಧಿಯೂ ಸ್ತೋತ್ರಾರ್ಹವೆ. ಸರಿ.",
			"34": "<sup>34</sup> ನಿನಗೆ ಕೇಡು ಮಾಡದಂತೆ ನನ್ನನ್ನು ತಡೆದ ಇಸ್ರಾಯೇಲರ ದೇವರಾದ ಯೆಹೋವನ ಆಣೆ, ನೀನು ಬೇಗನೇ ಬಂದು ನನ್ನನ್ನು ಎದುರುಗೊಳ್ಳದಿದ್ದರೆ ನಿಜವಾಗಿ ನಾಳೆ ಬೆಳಗಾಗುವಷ್ಟರಲ್ಲಿ ನಾಬಾಲನವರಲ್ಲಿ ಒಬ್ಬ ಗಂಡಸಾದರೂ ಉಳಿಯುತ್ತಿರಲಿಲ್ಲ” ಎಂದು ಹೇಳಿದನು.",
			"35": "<sup>35</sup> ಅವನು ಆಕೆ ತಂದ ಪದಾರ್ಥಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಆಕೆಗೆ “ಸಮಾಧಾನದಿಂದ ಮನೆಗೆ ಹೋಗು, ನಿನ್ನನ್ನು ನೋಡಿ ನಿನ್ನ ವಿಜ್ಞಾಪನೆಯನ್ನು ಲಾಲಿಸಿದ್ದೇನೆ” ಅಂದನು.",
			"36": "<sup>36</sup> ಅಬೀಗೈಲಳು ನಾಬಾಲನ ಬಳಿಗೆ ಬಂದಳು. ಅವನು ತನ್ನ ಮನೆಯಲ್ಲಿ ರಾಜರಂತೆ ಒಂದು ದೊಡ್ಡ ಔತಣವನ್ನು ಮಾಡಿಸಿ ಬಹಳವಾಗಿ ಕುಡಿದು ಬಹು ಸಂಭ್ರಮದಿಂದ ಕಾರಣ ಆಕೆಯು ಮರುದಿನದ ವರೆಗೆ ಇದರ ವಿಷಯವಾಗಿ ಏನೂ ಹೇಳಲಿಲ್ಲ.",
			"37": "<sup>37</sup> ಮರುದಿನ ಬೆಳಿಗ್ಗೆ ಅಮಲಿಳಿದ ನಂತರ ಈಕೆಯು ಈ ವರ್ತಮಾನವನ್ನು ಅವನಿಗೆ ತಿಳಿಸಲಾಗಿ, ಅವನ ಹೃದಯವೇ ನಿಂತಂತೆ ಆಯಿತು. ಅವನು ಸ್ತಬ್ಧನಾದನು.",
			"38": "<sup>38</sup> ಸುಮಾರು ಹತ್ತು ದಿನಗಳಾದನಂತರ ನಾಬಾಲನು ಯೆಹೋವನಿಂದ ಹತನಾಗಿ ಸತ್ತನು.",
			"39": "<sup>39</sup> ನಾಬಾಲನು ಸತ್ತನೆಂಬ ವರ್ತಮಾನವು ದಾವೀದನು ಕೇಳಿ, “ನನಗೆ ಅಪಮಾನ ಮಾಡಿದ ನಾಬಾಲನಿಗೆ ಮುಯ್ಯಿತೀರಿಸಿದಂತ ಯೆಹೋವನಿಗೆ ಸ್ತೋತ್ರವಾಗಲಿ. ಆತನು ನನ್ನನ್ನು ಕೆಟ್ಟತನಕ್ಕೆ ದೂರಮಾಡಿ ನಾಬಾಲನ ಕೆಟ್ಟತನವನ್ನು ಅವನ ತಲೆಯ ಮೇಲೆಯೇ ಹೊರಿಸಿದನಲ್ಲಾ” ಎಂದು ಹೇಳಿದನು. ಅನಂತರ ಅವನು ಅಬೀಗೈಲಳನ್ನು ತನಗೆ ಹೆಂಡತಿಯಾಗಿರಬೇಕೆಂದು ದೂತರ ಮೂಲಕ ಹೇಳಿ ಕಳುಹಿಸಿದನು.",
			"40": "<sup>40</sup> ಅವರು ಕರ್ಮೆಲಿನಲ್ಲಿದ್ದ ಅಬೀಗೈಲಳ ಬಳಿಗೆ ಹೋಗಿ ಆಕೆಗೆ, “ದಾವೀದನು ನಿನ್ನನ್ನು ತನಗೆ ಹೆಂಡತಿಯಾಗಿರುವುದಕ್ಕೆ ಕರೆದುಕೊಂಡು ಬರಬೇಕೆಂದು ನಮ್ಮನ್ನು ಕಳುಹಿಸಿದ್ದಾನೆ” ಅಂದರು.",
			"41": "<sup>41</sup> ಆಕೆ ಎದ್ದು ನಮಸ್ಕಾರಮಾಡಿ ಸೇವಕರಿಗೆ, “ನಿನ್ನ ದಾಸಿಯಾದ ನಾನು ನನ್ನ ಸ್ವಾಮಿಯ ಸೇವಕರ ಪಾದಗಳನ್ನು ತೊಳೆಯುವ ಸೇವಕಿಯಾಗುವುದಕ್ಕೆ ಸಿದ್ಧಳಾಗಿದ್ದೇನೆ” ಎಂದು ಉತ್ತರಕೊಟ್ಟು,",
			"42": "<sup>42</sup> ಬೇಗನೇ ಒಂದು ಕತ್ತೆಯನ್ನು ಹತ್ತಿ, ತನ್ನ ಐದು ಮಂದಿ ದಾಸಿಯರ ಸಹಿತವಾಗಿ ದಾವೀದನ ಸೇವಕರ ಜೊತೆಯಲ್ಲಿ ಹೊರಟು ಹೋಗಿ ಅವನ ಹೆಂಡತಿಯಾದಳು.",
			"43": "<sup>43</sup> ಇದಲ್ಲದೆ ದಾವೀದನು ಇಜ್ರೇಲಿನವಳಾದ (ಜೆಸ್ರೀಲ್) ಅಹೀನೋಮವಳನ್ನು ಮದುವೆಮಾಡಿಕೊಂಡಿದ್ದನು. ಅವರಿಬ್ಬರೂ ಅವನ ಹೆಂಡತಿಯರಾದರು.",
			"44": "<sup>44</sup> ಸೌಲನು ದಾವೀದನ ಹೆಂಡತಿಯಾಗಿದ್ದ ಮೀಕಲಳೆಂಬ ತನ್ನ ಮಗಳನ್ನು ಪುನಃ ಗಲ್ಲೀಮ್ ಊರಿನ ಲಯಿಷನ ಮಗನಾದ ಪಲ್ಟೀ ಎಂಬವನಿಗೆ ಮದುವೆ ಮಾಡಿಕೊಟ್ಟನು."
		},
		"26": {
			"1": "<sup>1</sup> ಜೀಫ್ಯರು ಗಿಬೆಯದಲ್ಲಿದ್ದ ಸೌಲನ ಬಳಿಗೆ ಬಂದು ಅವನಿಗೆ, “ದಾವೀದನು ಅರಣ್ಯದ ಮೂಡಣ ದಿಕ್ಕಿನಲ್ಲಿರುವ ಹಕೀಲಾ ಬೆಟ್ಟದಲ್ಲಿ ಅಡಗಿಕೊಂಡಿರುವುದು ನಿನಗೆ ಗೊತ್ತಿಲ್ಲವೋ?” ಅಂದರು.",
			"2": "<sup>2</sup> ಆಗ ಸೌಲನು ಇಸ್ರಾಯೇಲರಲ್ಲಿ ಶ್ರೇಷ್ಟರಾದ ಮೂರು ಸಾವಿರ ಮಂದಿ ಸೈನಿಕರನ್ನು ಆರಿಸಿಕೊಂಡು ದಾವೀದನನ್ನು ಹುಡುಕುವುದಕ್ಕೋಸ್ಕರ ಜೀಫ್ ಅರಣ್ಯಕ್ಕೆ ಹೋಗಿ ಅದರ ಮೂಡಣದಿಕ್ಕಿನಲ್ಲಿ,",
			"3": "<sup>3</sup> ದಾರಿಯ ಬಳಿಯಲ್ಲಿರುವ ಹಕೀಲಾ ಬೆಟ್ಟದಲ್ಲಿ ಪಾಳೆಯಮಾಡಿಕೊಂಡರು. ಸೌಲನು ತನ್ನನ್ನು ಹಿಡಿಯುವುದಕ್ಕೋಸ್ಕರ ಅರಣ್ಯಕ್ಕೆ ಬಂದಿದ್ದಾನೆಂಬ ವರ್ತಮಾನವು ಅಲ್ಲಿಯೇ ಇದ್ದ ದಾವೀದನಿಗೆ ತಲುಪಲು",
			"4": "<sup>4</sup> ಅವನು ಕೂಡಲೇ ಗೂಢಚಾರರನ್ನು ಕಳುಹಿಸಿ ಸೌಲನು ಇಳಿದುಕೊಂಡಿದ್ದ ಸ್ಥಳವನ್ನು ಗೊತ್ತುಮಾಡಿಕೊಂಡನು.",
			"5": "<sup>5</sup> ಅನಂತರ ತಾನಾಗಿಯೇ ಸೌಲನ ಪಾಳೆಯದ ಹತ್ತಿರ ಹೋಗಿ, ಸೌಲನೂ, ನೇರನ ಮಗನಾದ ಅವನ ಸೇನಾಪತಿ ಅಬ್ನೇರನೂ ಮಲಗಿದ್ದ ಸ್ಥಳಗಳನ್ನು ಕಂಡುಹಿಡಿದನು. ಸೌಲನು ರಥಗಳನ್ನು ಬಿಟ್ಟಿರುವ ಸ್ಥಳಮಧ್ಯದಲ್ಲಿ ಮಲಗಿದ್ದನು. ಅವನ ಸುತ್ತಲೂ ಸೈನಿಕರು ಇದ್ದರು.",
			"6": "<sup>6</sup> ಆಗ ದಾವೀದನು ಹಿತ್ತಿಯನ ಮಗನಾದ ಅಹೀಮೆಲೆಕನಿಗೂ, ಚೆರೂಯಳ ಮಗನೂ ಯೋವಾಬನ ತಮ್ಮನೂ ಆದ ಅಬೀಷೈಗೂ, “ನಿಮ್ಮಲ್ಲಿ ನನ್ನ ಸಂಗಡ ಸೌಲನ ಪಾಳೆಯಕ್ಕೆ ಯಾರು ಬರುವಿರಿ?” ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ ಅಬೀಷೈಯು, “ನಾನು ಬರುತ್ತೇನೆ” ಎಂದು ಉತ್ತರ ಕೊಟ್ಟನು.",
			"7": "<sup>7</sup> ದಾವೀದನೂ ಮತ್ತು ಅಬೀಷೈಯೂ ರಾತ್ರಿಯಲ್ಲಿ ಆ ಪಾಳೆಯಕ್ಕೆ ಹೋದಾಗ ಸೌಲನು ಬಂಡಿಗಳ ಗುಂಪಿನ ಮಧ್ಯದಲ್ಲಿ ಮಲಗಿಕೊಂಡು ನಿದ್ರೆಮಾಡುತ್ತಿದ್ದನು. ಅವನ ಬರ್ಜಿಯು ಅವನ ತಲೆಯ ಹತ್ತಿರ ನೆಲದಲ್ಲಿ ನೆಡಲ್ಪಟ್ಟಿತ್ತು. ಅಬ್ನೇರನೂ, ಸೈನಿಕರೂ ಅವನ ಸುತ್ತಲೂ ಮಲಗಿದ್ದರು.",
			"8": "<sup>8</sup> ಅಬೀಷೈಯು ದಾವೀದನಿಗೆ, “ದೇವರು ಈ ಹೊತ್ತು ನಿನ್ನ ವೈರಿಯನ್ನು ನಿನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸಿದ್ದಾನೆ, ಅಪ್ಪಣೆಯಾಗಲಿ ನಾನು ಬರ್ಜಿಯಿಂದ ಒಂದೇ ಪೆಟ್ಟಿನಲ್ಲಿ ಅವನನ್ನು ನೆಲಕ್ಕೆ ಹತ್ತಿಕೊಳ್ಳುವಂತೆ ತಿವಿಯುವೆನು. ಎರಡನೆಯ ಸಾರಿ ಹೊಡೆಯುವಂತ ಅವಕಾಶವಿರುವುದಿಲ್ಲ” ಎಂದು ಹೇಳಿದನು.",
			"9": "<sup>9</sup> ಆದರೆ ದಾವೀದನು, “ಅವನನ್ನು ಕೊಲ್ಲಬೇಡ, ಯೆಹೋವನ ಅಭಿಷಿಕ್ತನಿಗೆ ವಿರೋಧವಾಗಿ ಕೈಯೆತ್ತುವ ಯಾವನಾದರೂ ನಿರಪರಾಧಿಯೆಂದು ಪರಿಗಣಿಸಲ್ಪಡುವುದಿಲ್ಲ?” ಅಂದನು.",
			"10": "<sup>10</sup> ಇದಲ್ಲದೆ ದಾವೀದನು, “ಯೆಹೋವನ ಆಣೆ, ಅವನು ಯೆಹೋವನಿಂದ ಸಾಯುವನು, ಇಲ್ಲವೆ ಕಾಲ ತುಂಬಿದ ಮೇಲೆ ಮರಣ ಹೊಂದುವನು ಅಥವಾ ಯುದ್ಧದಲ್ಲಿ ಸಾಯುವನು.",
			"11": "<sup>11</sup> ತನ್ನ ಅಭಿಷಿಕ್ತನಿಗೆ ಕೈಯೆತ್ತದಂತೆ ಯೆಹೋವನು ನನ್ನನ್ನು ತಡೆಯಲಿ. ಈಗ ಅವನ ತಲೆಯ ಬಳಿಯಲ್ಲಿರುವ ಬರ್ಜಿಯನ್ನು, ನೀರಿನ ತಂಬಿಗೆಯನ್ನೂ ತೆಗೆದುಕೊಂಡು ಹೋಗೋಣ” ಎಂದು ಹೇಳಿ",
			"12": "<sup>12</sup> ಸೌಲನ ತಲೆದಿಂಬಿನ ಬಳಿಯಲ್ಲಿದ್ದ ಬರ್ಜಿ ಮತ್ತು ನೀರಿನ ತಂಬಿಗೆಯನ್ನು ತೆಗೆದುಕೊಂಡು ಹೋದರು. ಯಾರೂ ನೋಡಲಿಲ್ಲ, ಯಾರಿಗೂ ಗೊತ್ತಾಗಲಿಲ್ಲ, ಯಾರೂ ಎಚ್ಚರವಾಗಲಿಲ್ಲ. ಏಕೆಂದರೆ ಯೆಹೋವನು ಅವರಿಗೆ ಗಾಢನಿದ್ರೆಯನ್ನು ಬರಮಾಡಿದ್ದನು. ಒಬ್ಬರಿಗೂ ಎಚ್ಚರವಿಲ್ಲದೆ ಎಲ್ಲರೂ ನಿದ್ರೆಮಾಡುತ್ತಿದ್ದರು.",
			"13": "<sup>13</sup> ದಾವೀದನು ತನಗೂ ಪಾಳೆಯಕ್ಕೂ ತಕ್ಕಷ್ಟು ಅಂತರವಿರುವ ಹಾಗೆ ಸ್ವಲ್ಪ ದೂರಕ್ಕೆ ಹೋಗಿ ಗುಡ್ಡವನ್ನೇರಿ ತುದಿಯಲ್ಲಿ ನಿಂತು,",
			"14": "<sup>14</sup> “ಅಬ್ನೇರನೇ ಕಿವಿಗೊಡುವುದಿಲ್ಲವೋ?” ಎಂದು ನೇರನ ಮಗನಾದ ಅವನಿಗೂ, ಸೈನಿಕರಿಗೂ ಕೇಳಿಸುವಂತೆ ಕೂಗಿದನು. ಅಬ್ನೇರನು, “ಅರಸನನ್ನು ಕೂಗುವ ನೀನಾರು?” ಎಂದು ಕೇಳಿದನು.",
			"15": "<sup>15</sup> ದಾವೀದನು ಅಬ್ನೇರನಿಗೆ, “ನೀನು ಶೂರನಲ್ಲವೋ? ಇಸ್ರಾಯೇಲರಲ್ಲಿ ನಿನಗೆ ಸಮಾನನಾದವನು ಯಾರು? ನೀನು ನಿನ್ನ ಒಡೆಯನಾದ ಅರಸನನ್ನು ಯಾಕೆ ಕಾಯಲಿಲ್ಲ. ಜನರಲ್ಲೊಬ್ಬನು ಒಳಕ್ಕೆ ಹೊಕ್ಕು ನಿನ್ನ ಒಡೆಯನಾದ ಅರಸನನ್ನು ಕೊಲ್ಲಬೇಕೆಂದಿದ್ದನು.",
			"16": "<sup>16</sup> ನೀನು ಹೀಗೆ ಮಾಡುವುದು ಸರಿಯಲ್ಲ. ಯೆಹೋವನಾಣೆ, ಆತನ ಅಭಿಷಿಕ್ತನಾಗಿರುವ ನಿಮ್ಮ ಒಡೆಯನನ್ನು ಕಾಯದಿರುವ ನೀವು ಮರಣಕ್ಕೆ ಪಾತ್ರರೇ ಹೌದು. ಅರಸನ ತಲೆಯ ಬಳಿಯಲ್ಲಿದ್ದ ಬರ್ಜಿಯೂ ತಂಬಿಗೆಯೂ ಏನಾದವೋ ನೋಡು” ಎಂದು ಕೂಗಿ ಹೇಳಿದನು.",
			"17": "<sup>17</sup> ಸೌಲನು ದಾವೀದನ ಸ್ವರದ ಗುರುತು ಹಿಡಿದು ಅವನನ್ನು, “ದಾವೀದನೇ ನನ್ನ ಮಗನೇ, ಇದು ನಿನ್ನ ಸ್ವರವೋ” ಎಂದು ಕೇಳಲು ಅವನು, “ಅರಸನೇ, ನನ್ನ ಒಡೆಯನೇ ಹೌದು, ನನ್ನ ಸ್ವರವೇ,",
			"18": "<sup>18</sup> ನನ್ನ ಸ್ವಾಮಿಯು ತನ್ನ ಸೇವಕನನ್ನು ಈ ಪ್ರಕಾರ ಹಿಂಸಿಸುವುದೇಕೇ? ನಾನೇನು ಮಾಡಿದೆನು?",
			"19": "<sup>19</sup> ಯಾವ ಪಾಪಕ್ಕೆ ಕೈಹಾಕಿದೆನು? ನನ್ನ ಅರಸನಾದ ಒಡೆಯನು ದಯವಿಟ್ಟು ತನ್ನ ಸೇವಕನ ಮಾತುಗಳನ್ನು ಲಾಲಿಸಬೇಕು. ನಿನ್ನನ್ನು ನನಗೆ ವಿರೋಧವಾಗಿ ಎಬ್ಬಿಸಿದವನು ಯೆಹೋವನೇ ಆಗಿರುವ ಪಕ್ಷದಲ್ಲಿ, ಆತನಿಗೆ ಘಮಘಮಿಸುವ ನೈವೇದ್ಯವನ್ನು ಅಂಗೀಕರಿಸಬೇಕು. ಮನುಷ್ಯರಾಗಿದ್ದರೆ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ಅವರು ಶಾಪಗ್ರಸ್ತರಾಗಿರಲಿ ಯಾಕೆಂದರೆ ಯೆಹೋವನ ಸ್ವಾಸ್ಥ್ಯದಲ್ಲಿ ನನಗೆ ಪಾಲು ಸಿಕ್ಕದಂತೆ ‘ಹೋಗಿ ಅನ್ಯದೇವತೆಗಳನ್ನು ಸೇವಿಸು’ ಎಂದು ನನ್ನನ್ನು ತಳ್ಳಿಬಿಟ್ಟರು.",
			"20": "<sup>20</sup> ಯೆಹೋವನ ಸಾನಿಧ್ಯವಿಲ್ಲದಿರುವ ದೇಶದಲ್ಲಿ ನನ್ನ ರಕ್ತವು ಸುರಿಸಲ್ಪಡದಿರಲಿ. ಅಯ್ಯೋ ಗುಡ್ಡಗಳಲ್ಲಿ ಕೌಜುಗವನ್ನು ಹಿಡಿಯಲು ಹೊರಟ ಬೇಟೆಗಾರನೋ ಎಂಬಂತೆ ಇಸ್ರಾಯೇಲರ ಅರಸನು ಹೊರಟು ಬಂದು ನನ್ನ ಪ್ರಾಣವನ್ನು ತೆಗೆಯಬೇಕೆಂದಿರುತ್ತಾನಲ್ಲ” ಅಂದನು.",
			"21": "<sup>21</sup> ಆಗ ಸೌಲನು ಅವನಿಗೆ, “ನಾನು ಪಾಪಮಾಡಿದೆನು, ದಾವೀದನೇ ನನ್ನ ಮಗನೇ ಹಿಂದಿರುಗಿ ಬಾ. ಈ ಹೊತ್ತು ನನ್ನ ಜೀವವು ನಿನ್ನ ದೃಷ್ಟಿಯಲ್ಲಿ ಬಹು ಬೆಲೆಯುಳ್ಳದೆಂದು ಎಣಿಸಲ್ಪಟ್ಟಿತು. ಆದ್ದರಿಂದ ನಾನು ಇನ್ನು ಮುಂದೆ ನಿನಗೆ ಕೇಡುಮಾಡುವುದಿಲ್ಲ. ಈ ವರೆಗೆ ನಾನು ಮಾಡಿದ್ದು ಹುಚ್ಚುತನವೂ ದೊಡ್ಡ ತಪ್ಪೂ ಆಗಿದೆ” ಎಂದು ಹೇಳಿದನು",
			"22": "<sup>22</sup> ದಾವೀದನು ಅರಸನಿಗೆ, “ಅರಸನೇ, ಇಗೋ ನಿನ್ನ ಬರ್ಜಿ ಇಲ್ಲಿದೆ, ಸೇವಕರಲ್ಲೊಬ್ಬನು ಬಂದು ಅದನ್ನು ತೆಗೆದುಕೊಂಡು ಹೋಗಲಿ.",
			"23": "<sup>23</sup> ಯೆಹೋವನು ಪ್ರತಿಯೊಬ್ಬನಿಗೂ ಅವನವನ ನೀತಿಸತ್ಯತೆಗಳಿಗೆ ಅನುಸಾರವಾಗಿ ಪ್ರತಿಫಲವನ್ನು ಕೊಡುವನು. ಆತನು ಈ ಹೊತ್ತು ನಿನ್ನನ್ನು ನನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸಿಕೊಟ್ಟಿದ್ದರೂ, ನೀನು ಯೆಹೋವನ ಅಭಿಷಿಕ್ತನೆಂದು ನಾನು ನಿನ್ನ ಮೇಲೆ ಕೈಹಾಕಲಿಲ್ಲ.",
			"24": "<sup>24</sup> ನಾನು ಈ ಹೊತ್ತು ನಿನ್ನ ಜೀವವನ್ನು ಮಾನ್ಯವಾದದ್ದೆಂದು ಎಣಿಸಿದಂತೆ ಯೆಹೋವನು ನನ್ನ ಜೀವವನ್ನು ಮಾನ್ಯವಾದದ್ದೆಂದು ಎಣಿಸಿ, ಎಲ್ಲಾ ಇಕ್ಕಟ್ಟಿನಿಂದ ಬಿಡಿಸಲಿ” ಎಂದನು.",
			"25": "<sup>25</sup> ಆಗ ಸೌಲನು ದಾವೀದನಿಗೆ, “ನನ್ನ ಮಗನೇ ದಾವೀದನೇ ನೀನು ಆಶೀರ್ವಾದ ಹೊಂದು. ನೀನು ಹೇಗೂ ಮಹಾಕಾರ್ಯಗಳನ್ನು ನಡಿಸಿ ಸಫಲನಾಗುವಿ” ಎಂದು ಹೇಳಿ ತನ್ನ ಊರಿಗೆ ಹೊರಟನು. ದಾವೀದನು ತನ್ನ ದಾರಿಯನ್ನು ಹಿಡಿದನು."
		},
		"27": {
			"1": "<sup>1</sup> ದಾವೀದನು ತನ್ನ ಮನಸ್ಸಿನಲ್ಲಿ, “ನಾನು ಇಲ್ಲೇ ಇದ್ದರೆ ಹೇಗೂ ಒಂದು ದಿನ ಸೌಲನ ಕೈಯಿಂದ ಸಾಯಲೇಬೇಕು ಆದ್ದರಿಂದ ಫಿಲಿಷ್ಟಿಯರ ದೇಶಕ್ಕೆ ಹೋಗಿ ತಪ್ಪಿಸಿಕೊಳ್ಳುವುದೇ ಉತ್ತಮ. ಆ ಮೇಲೆ ಸೌಲನು ಇಸ್ರಾಯೇಲ್ಯರ ಪ್ರಾಂತ್ಯಗಳಲ್ಲಿ ನನ್ನನ್ನು ಹುಡುಕದೆ ಬಿಟ್ಟುಬಿಡುವನು. ನಾನು ಅವನ ಕೈಗೆ ತಪ್ಪಿಸಿಕೊಂಡವನಾಗಿರುವೆನು” ಅಂದುಕೊಂಡನು.",
			"2": "<sup>2</sup> ಅವನು ತನ್ನ ಆರುನೂರು ಜನರೊಡನೆ ಗತ್ ಊರಿನ ಅರಸನೂ ಮಾವೋಕನ ಮಗನೂ ಆದ ಆಕೀಷನ ಬಳಿಗೆ ಹೋದನು.",
			"3": "<sup>3</sup> ಅವನೂ, ಅವನ ಜನರೂ ತಮ್ಮ ತಮ್ಮ ಕುಟುಂಬ ಸಹಿತವಾಗಿ ಆಕೀಷನ ಊರಾದ ಗತ್ ಊರಿನಲ್ಲಿ ವಾಸಮಾಡಿದರು. ದಾವೀದನ ಹೆಂಡತಿಯರಾಗಿರುವ ಇಜ್ರೇಲ್ಯಳಾದ ಅಹೀನೋಮವಳೂ, ಕರ್ಮೇಲ್ಯನಾದ ನಾಬಾಲನ ಹೆಂಡತಿಯಾಗಿದ್ದ ಅಬೀಗೈಲಳೂ ಅವನ ಸಂಗಡ ಇದ್ದರು.",
			"4": "<sup>4</sup> ದಾವೀದನು ತಪ್ಪಿಸಿಕೊಂಡು ಗತ್ ಊರಿಗೆ ಹೋದನೆಂಬ ವರ್ತಮಾನವನ್ನು ಸೌಲನು ಕೇಳಿದ ಮೇಲೆ ಅವನನ್ನು ಹುಡುಕಲೇ ಇಲ್ಲ.",
			"5": "<sup>5</sup> ಒಂದು ದಿನ ದಾವೀದನು ಆಕೀಷನಿಗೆ, “ನಿನ್ನ ಸೇವಕನಾದ ನಾನು ನಿನ್ನ ಸಂಗಡ ರಾಜಧಾನಿಯಲ್ಲೇಕೆ ವಾಸಿಸಬೇಕು? ನಿನ್ನ ದೃಷ್ಟಿಯಲ್ಲಿ ನನಗೆ ದಯೆ ದೊರಕಿರುವುದಾದರೆ ಯಾವುದಾದರು ಒಂದು ಪಟ್ಟಣದಲ್ಲಿ ನನಗೆ ಸ್ಥಳಕೊಡು. ನಾನು ಅಲ್ಲಿ ವಾಸಮಾಡುವೆನು” ಅಂದನು.",
			"6": "<sup>6</sup> ಆಕೀಷನು ಕೂಡಲೇ ಅವನಿಗೆ ಚಿಕ್ಲಗ್ ಊರನ್ನು ಕೊಟ್ಟುಬಿಟ್ಟನು. ಆದ್ದರಿಂದ ಚಿಕ್ಲಗ್ ಎಂಬುದು ಇಂದಿನವರೆಗೂ ಯೆಹೂದ ರಾಜರಿಗೆ ಸೇರಿದೆ.",
			"7": "<sup>7</sup> ದಾವೀದನು ಫಿಲಿಷ್ಟಿಯರ ದೇಶದಲ್ಲಿ ಒಂದು ವರ್ಷ ನಾಲ್ಕು ತಿಂಗಳೂ ವಾಸವಾಗಿದ್ದನು.",
			"8": "<sup>8</sup> ಆ ಕಾಲದಲ್ಲಿ ಅವನೂ, ಅವನ ಜನರೂ ತೇಲಾಮಿನಿಂದ ಶೂರಿನವರೆಗೂ, ಐಗುಪ್ತ ದೇಶದವರೆಗೂ ವಿಸ್ತರಿಸಿಕೊಂಡಿರುವ ಪ್ರಾಂತ್ಯಗಳಲ್ಲಿ ವಾಸಿಸುತ್ತಿರುವ ಗೆಷೂರ್ಯರು, ಗಿಜ್ರೀಯರು, ಅಮಾಲೇಕ್ಯರು ಇವರ ಮೇಲೆ ಆಕ್ರಮಣ ಮಾಡಿ ಸುಲಿದುಕೊಳ್ಳುತ್ತಿದ್ದರು.",
			"9": "<sup>9</sup> ದಾವೀದನು ಸ್ತ್ರೀಪುರುಷರಲ್ಲಿ ಒಬ್ಬರನ್ನೂ ಉಳಿಸದೆ ದೇಶದ ಎಲ್ಲಾ ನಿವಾಸಿಗಳನ್ನು ಕೊಂದು, ಕುರಿ, ದನ, ಕತ್ತೆ, ಒಂಟೆ, ಉಡುಗೊರೆ ಇವುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಆಕೀಷನ ಬಳಿಗೆ ಹೋಗುತ್ತಿದ್ದನು.",
			"10": "<sup>10</sup> ಆಕೀಷನು ಅವನನ್ನು “ಈ ಹೊತ್ತು ಯಾವ ಪ್ರಾಂತ್ಯದವರನ್ನು ಸೂರೆಮಾಡಿಕೊಂಡು ಬಂದಿರಿ” ಎಂದು ಕೇಳುವನು. ದಾವೀದನು ಅವನಿಗೆ “ದಕ್ಷಿಣ ಪ್ರಾಂತ್ಯದಲ್ಲಿರುವ ಯೆಹೂದ್ಯರು, ಎರಹ್ಮೇಲ್ಯರು ಕೇನ್ಯರು ಇವರಲ್ಲಿ ಯಾರ ಹೆಸರನ್ನಾದರೂ” ಹೇಳುವರು.",
			"11": "<sup>11</sup> ದಾವೀದನು ತನ್ನ ಕೃತ್ಯವನ್ನು ಜನರು ಹೇಳಾರೆಂದು ನೆನಸಿ, ಅವನು ಯಾರನ್ನೂ ಗತ್ ಊರಿಗೆ ತಾರದೆ, ಎಲ್ಲಾ ಸ್ತ್ರೀಪುರುಷರನ್ನು ಅಲ್ಲೇ ಕೊಂದುಹಾಕುತ್ತಿದ್ದನು. ಅವನು ಫಿಲಿಷ್ಟಿಯರ ದೇಶದಲ್ಲಿ ವಾಸವಾಗಿದ್ದ ಕಾಲದಲ್ಲೆಲ್ಲಾ ಇದೇ ರೀತಿ ಮಾಡುತ್ತಿದ್ದನು.",
			"12": "<sup>12</sup> ದಾವೀದನ ಹೆಸರು ಸ್ವಜನರಾದ ಇಸ್ರಾಯೇಲರಲ್ಲಿ ತನ್ನ ಹೆಸರನ್ನು ಕೆಡಿಸಿಕೊಂಡಿದ್ದರಿಂದ ಇವನು ತನ್ನ ನಿತ್ಯ ದಾಸನಾಗಿರುವನೆಂದು ಆಕೀಷನು ನಂಬಿಕೊಂಡನು."
		},
		"28": {
			"1": "<sup>1</sup> ಆ ಕಾಲದಲ್ಲಿ ಫಿಲಿಷ್ಟಿಯರು ಇಸ್ರಾಯೇಲರೊಡನೆ ಯುದ್ಧಮಾಡುವುದಕ್ಕಾಗಿ ಸೈನ್ಯವನ್ನು ಕೂಡಿಸತೊಡಗಿದರು. ಆಗ ಆಕೀಷನು ದಾವೀದನಿಗೆ, “ನೀನೂ ನಿನ್ನ ಜನರೂ ಯುದ್ಧಕ್ಕೆ ನನ್ನ ಜೊತೆಯಲ್ಲಿ ಬರಬೇಕೆಂಬುದು ನಿನಗೆ ಗೊತ್ತಿಲ್ಲವೋ” ಎನ್ನಲು ದಾವೀದನು,",
			"2": "<sup>2</sup> “ಹೌದು ನಿನ್ನ ದಾಸನ ಸಾಹಸವು ಗೊತ್ತಾಗುವುದು” ಅಂದನು. ಆಕೀಷನು ಅವನಿಗೆ, “ಹಾಗಾದರೆ ಯುದ್ಧಕಾಲದಲ್ಲೆಲ್ಲಾ ನಿನ್ನನ್ನೇ ನನ್ನ ಮೈಗಾವಲಿಗೆ ನೇಮಿಸಿಕೊಳ್ಳುವೆನು” ಅಂದನು.",
			"3": "<sup>3</sup> ಸಮುವೇಲನು ಮರಣ ಹೊಂದಿದನು. ಇಸ್ರಾಯೇಲರು ಅವನಿಗೋಸ್ಕರ ಗೋಳಾಡಿ, ಅವನ ಶವವನ್ನು ಅವನ ಸ್ವಂತ ಊರಾದ ರಾಮದಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದ್ದರು. ಸೌಲನು ಸತ್ತವರಲ್ಲಿ ವಿಚಾರಿಸುವವರನ್ನೂ, ಬೇತಾಳಿಕರನ್ನೂ ದೇಶದಿಂದ ಹೊರಡಿಸಿಬಿಟ್ಟನು.",
			"4": "<sup>4</sup> ಫಿಲಿಷ್ಟಿಯರು ಸೈನ್ಯಕೂಡಿಸಿಕೊಂಡು ಬಂದು ಶೂನೇಮಿನಲ್ಲಿ ಪಾಳೆಯಮಾಡಿಕೊಂಡರು. ಸೌಲನು ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲರೊಡನೆ ಬಂದು, ಗಿಲ್ಬೋವದಲ್ಲಿ ಇಳಿದುಕೊಂಡರು.",
			"5": "<sup>5</sup> ಅವನು ಫಿಲಿಷ್ಟಿಯರ ಪಾಳೆಯವನ್ನು ಕಂಡು ಬಹಳ ಭಯದಿಂದ ಎದೆಯೊಡೆದವನಂತಾದನು.",
			"6": "<sup>6</sup> ಅವನು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ವಿಚಾರಿಸಿದರೂ, ಆತನು ಅವನಿಗೆ ಕನಸುಗಳಿಂದಾಗಲಿ ಊರೀಮಿನಿಂದಾಗಲಿ, ಪ್ರವಾದಿಗಳಿಂದಾಗಲಿ ಉತ್ತರ ಕೊಡಲೇ ಇಲ್ಲ.",
			"7": "<sup>7</sup> ಆದುದರಿಂದ ಅವನು ತನ್ನ ಸೇವಕರಿಗೆ, “ಸತ್ತವರಲ್ಲಿ ವಿಚಾರಿಸಬಲ್ಲ ಒಬ್ಬ ಸ್ತ್ರೀಯನ್ನು ಹುಡುಕಿರಿ. ನಾನು ಆಕೆಯ ಹತ್ತಿರ ಹೋಗಿ ವಿಚಾರಿಸುವೆನು” ಎಂದು ಹೇಳಿದನು. ಆಗ ಅವರು, “ಅಂಥವಳೊಬ್ಬಳು ಏಂದೋರಿನಲ್ಲಿದ್ದಾಳೆ” ಅಂದರು.",
			"8": "<sup>8</sup> ಆಗ ಸೌಲನು ವಸ್ತ್ರಗಳಿಂದ ತನ್ನನ್ನು ಮಾರ್ಪಡಿಸಿಕೊಂಡು, ಇಬ್ಬರು ಸೇವಕರೊಡನೆ ಹೊರಟು, ರಾತ್ರಿಯಲ್ಲಿ ಆಕೆಯ ಮನೆ ಸೇರಿ, “ದಯವಿಟ್ಟು ನನಗೋಸ್ಕರ ಸತ್ತವರಲ್ಲಿ ವಿಚಾರಿಸಿ ಕಣಿಹೇಳು. ನಾನು ಯಾವನ ಹೆಸರು ಹೇಳುತ್ತೇನೋ ಅವನನ್ನು ಇಲ್ಲಿಗೆ ಬರಮಾಡು” ಎಂದು ಆಕೆಯನ್ನು ಬೇಡಿಕೊಂಡನು",
			"9": "<sup>9</sup> ಆಗ ಆಕೆಯು ಅವನಿಗೆ, “ಸೌಲನು ಮಾಡಿದ್ದು ನಿನಗೆ ಗೊತ್ತುಂಟು. ಅವನು ಸತ್ತವರಲ್ಲಿ ವಿಚಾರಿಸುವವರನ್ನು ಬೇತಾಳಿಕರನ್ನೂ ದೇಶದಿಂದ ಹೊರಡಿಸಿಬಿಟ್ಟನಲ್ಲಾ, ಹೀಗಿದ್ದರೂ ನಾನೂ ಸಾಯುವಂತೆ ನೀನು ನನ್ನ ಪ್ರಾಣಕ್ಕೆ ಉರುಲೊಡ್ಡುವುದೇಕೆ?” ಅಂದಳು.",
			"10": "<sup>10</sup> ಅದಕ್ಕೆ ಸೌಲನು, “ಯೆಹೋವನಾಣೆ, ಈ ವಿಷಯದಲ್ಲಿ ನೀನು ದಂಡನೆಗೆ ಗುರಿಯಾಗುವುದಿಲ್ಲ” ಎಂದು ಆಕೆಗೆ ಯೆಹೋವನ ಹೆಸರಿನಲ್ಲಿ ಪ್ರಮಾಣಮಾಡಿದನು.",
			"11": "<sup>11</sup> ಆಗ ಸ್ತ್ರೀಯು, “ನಿನಗೆ ಯಾರನ್ನು ಬರಮಾಡಬೇಕು?” ಎನ್ನಲು, “ಸಮುವೇಲನನ್ನು” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"12": "<sup>12</sup> ಆ ಸ್ತ್ರೀಯು ಸಮುವೇಲನನ್ನು ಕಂಡಕೂಡಲೇ ಗಟ್ಟಿಯಾಗಿ ಕೂಗಿ ಸೌಲನಿಗೆ, “ನೀನು ನನ್ನನ್ನು ವಂಚಿಸಿದ್ದೇಕೆ, ನೀನು ಸೌಲನಲ್ಲವೋ?” ಅಂದಳು.",
			"13": "<sup>13</sup> ಅರಸನು, “ಹೆದರಬೇಡ, ನಿನಗೇನು ಕಾಣಿಸುತ್ತದೆ?” ಎಂದು ಆಕೆಯನ್ನು ಕೇಳಲು, “ಭೂಮಿಯೊಳಗಿಂದ ಒಬ್ಬ ದೇವನು ಬರುತ್ತಿರುವುದನ್ನು ನೋಡುತ್ತೇನೆ” ಎಂದು ಉತ್ತರ ಕೊಟ್ಟಳು.",
			"14": "<sup>14</sup> ಅವನು ತಿರುಗಿ, “ಅವನ ರೂಪ ಹೇಗಿದೆ?” ಎಂದು ಕೇಳಲು ಆಕೆಯು, “ನಿಲುವಂಗಿಯನ್ನು ಉಟ್ಟುಕೊಂಡ ಒಬ್ಬ ಮುದುಕನು ಬರುತ್ತಿದ್ದಾನೆ” ಎಂದು ಹೇಳಿದಳು. ಅವನು ಸಮುವೇಲನೆ ಎಂದು ಸೌಲನು ತಿಳಿದು, ನೆಲದ ಮಟ್ಟಿಗೂ ಬೊಗ್ಗಿ ನಮಸ್ಕರಿಸಿದನು.",
			"15": "<sup>15</sup> ಆಗ ಸಮುವೇಲನು ಸೌಲನನ್ನು, “ನೀನು ನನ್ನ ವಿಶ್ರಾಂತಿಯನ್ನು ಕೇಡಿಸಿದ್ದೇಕೆ? ನನ್ನನ್ನು ಇಲ್ಲಿಗೆ ಯಾಕೆ ಬರಮಾಡಿದೆ?” ಎಂದು ಕೇಳಿದನು. ಅದಕ್ಕೆ ಸೌಲನು, “ನಾನು ಬಲು ಇಕ್ಕಟ್ಟಿನಲ್ಲಿದ್ದೇನೆ. ಫಿಲಿಷ್ಟಿಯರು ನನಗೆ ವಿರೋಧವಾಗಿ ಯುದ್ಧಕ್ಕೆ ಬಂದಿದ್ದಾರೆ. ದೇವರು ನನ್ನನ್ನು ಬಿಟ್ಟು ದೂರ ಹೋಗಿದ್ದಾನೆ. ಆತನು ನನಗೆ ಪ್ರವಾದಿಗಳಿಂದಾಗಲಿ, ಕನಸುಗಳಿಂದಾಗಲಿ ಉತ್ತರಕೊಡಲೊಲ್ಲನು. ಆದುದರಿಂದ ನಾನು ಮಾಡಬೇಕಾದದ್ದನ್ನು ನೀನು ತಿಳಿಸುವಿಯೆಂದು ನಿನ್ನನ್ನು ಇಲ್ಲಿಗೆ ಬರಮಾಡಿದೆನು” ಅಂದನು.",
			"16": "<sup>16</sup> ಆಗ ಸಮುವೇಲನು, “ಯೆಹೋವನು ನಿನ್ನನ್ನು ಬಿಟ್ಟು ನಿನಗೆ ವಿರೋಧಿಯಾದ ಮೇಲೆ, ನೀನು ನನ್ನನ್ನು ವಿಚಾರಿಸುವುದೇಕೆ?",
			"17": "<sup>17</sup> ಆತನು ನನ್ನ ಮುಖಾಂತರ ನಿನಗೆ ಹೇಳಿದ್ದನ್ನು ನೆರವೇರಿಸಿದ್ದಾನೆ. ರಾಜ್ಯವನ್ನು ನಿನ್ನಿಂದ ಕಿತ್ತುಕೊಂಡು ನಿನ್ನ ನೆರೆಯವನಾದ ದಾವೀದನಿಗೆ ಕೊಟ್ಟಿದ್ದಾನೆ.",
			"18": "<sup>18</sup> ನೀನು ಯೆಹೋವನ ಮಾತನ್ನು ಕೇಳಲಿಲ್ಲ. ಆತನು ಅಮಾಲೇಕ್ಯರ ಮೇಲೆ ಉಗ್ರಕೊಪಗೊಂಡು ಮಾಡಿದ ಕೋಪಾಜ್ಞೆಯನ್ನು ನೀನು ನೆರವೇರಿಸಲಿಲ್ಲ. ಆದ್ದರಿಂದ ಯೆಹೋವನು ಈಗ ನಿನಗೆ ಹೀಗೆ ಮಾಡಿದ್ದಾನೆ.",
			"19": "<sup>19</sup> ಆತನು ನಿನ್ನ ಕೂಡ ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರನ್ನೂ ಫಿಲಿಷ್ಟಿಯರ ಕೈಗೆ ಒಪ್ಪಿಸುವನು. ನಾಳೆ ನೀನೂ ನಿನ್ನ ಮಕ್ಕಳೂ ನಾನಿರುವಲ್ಲಿಗೆ ಬರುವಿರಿ. ಆತನು ಇಸ್ರಾಯೇಲ್ಯರ ಸೈನ್ಯವನ್ನು ಫಿಲಿಷ್ಟಿಯರ ಕೈಗೆ ಒಪ್ಪಿಸುವನು” ಎಂದು ಹೇಳಿದನು.",
			"20": "<sup>20</sup> ಸೌಲನು ಸಮುವೇಲನ ಮಾತುಗಳನ್ನು ಕೇಳಿದ ಕೂಡಲೇ ಭಯಭ್ರಾಂತನಾಗಿ ನೆಲದ ಮೇಲೆ ಉದ್ದಕ್ಕೆ ಬಿದ್ದನು. ಅವನು ಇಡೀ ದಿನ ಊಟಮಾಡಲಿಲ್ಲವಾದ್ದರಿಂದ ಅವನಲ್ಲಿ ಬಲವೇ ಇರಲಿಲ್ಲ.",
			"21": "<sup>21</sup> ಆ ಸ್ತ್ರೀಯು ಸೌಲನ ಹತ್ತಿರ ಬಂದು ಅವನು ಬಹು ಭೀತನಾಗಿದ್ದಾನೆಂದು ಕಂಡು ಅವನಿಗೆ, “ನಿನ್ನ ದಾಸಿಯಾದ ನಾನು ನಿನ್ನ ಮಾತನ್ನು ಕೇಳಿದೆನು. ಕೈಯಲ್ಲಿ ಜೀವಹಿಡಿದವಳಾಗಿ ನಿನ್ನ ಅಪ್ಪಣೆಯನ್ನು ನೆರವೇರಿಸಿದೆನು.",
			"22": "<sup>22</sup> ಆದುದರಿಂದ ನೀನು ದಯವಿಟ್ಟು ನಿನ್ನ ದಾಸಿಯ ಮಾತನ್ನು ಕೇಳಬೇಕು. ಸ್ವಲ್ಪ ಆಹಾರ ತರುತ್ತೇನೆ, ಪ್ರಯಾಣಕ್ಕೆ ಬಲಬರುವ ಹಾಗೆ ಅದನ್ನು ಊಟಮಾಡು” ಎಂದು ಹೇಳಿದಳು.",
			"23": "<sup>23</sup> ಅವನು, “ಒಲ್ಲೆನು, ಊಟಮಾಡುವುದಿಲ್ಲ” ಅಂದನು. ಆದರೆ ಅವನ ಸೇವಕರೂ ಆ ಸ್ತ್ರೀಯೂ ಬಹಳವಾಗಿ ಬೇಡಿಕೊಂಡದ್ದರಿಂದ ಅವನು ಅವರ ಮಾತಿಗೆ ಒಪ್ಪಿಕೊಂಡು ನೆಲದಿಂದೆದ್ದು ಮಂಚದ ಮೇಲೆ ಕುಳಿತುಕೊಂಡನು.",
			"24": "<sup>24</sup> ಆ ಸ್ತ್ರೀಗೆ ಮನೆಯಲ್ಲಿ ಒಂದು ಕೊಬ್ಬಿದ ಕರುವಿತ್ತು. ಆಕೆಯು ಶೀಘ್ರವಾಗಿ ಅದನ್ನು ಕೊಯ್ದು, ಸಿದ್ಧಮಾಡಿ, ಹಿಟ್ಟನ್ನು ತೆಗೆದುಕೊಂಡು ನಾದಿ, ಹುಳಿಯಿಲ್ಲದ ರೊಟ್ಟಿಗಳನ್ನು ಸುಟ್ಟು ಸೌಲನಿಗೂ ಅವನ ಸೇವಕರಿಗೂ ಬಡಿಸಿದಳು.",
			"25": "<sup>25</sup> ಅವರು ಊಟಮಾಡಿ ಅದೇ ರಾತ್ರಿ ಹೊರಟುಹೋದರು."
		},
		"29": {
			"1": "<sup>1</sup> ಫಿಲಿಷ್ಟಿಯರು ತಮ್ಮ ಸೈನ್ಯವನ್ನು ಕೂಡಿಸಿಕೊಂಡು ಬಂದು ಅಫೇಕಿನಲ್ಲಿ ಪಾಳೆಯಮಾಡಿಕೊಂಡರು. ಇಸ್ರಾಯೇಲರು ಇಜ್ರೇಲಿನ ಬೈಲಿನಲ್ಲಿರುವ ಬುಗ್ಗೆಯ ಬಳಿಯಲ್ಲಿ ಇಳಿದುಕೊಂಡರು.",
			"2": "<sup>2</sup> ಫಿಲಿಷ್ಟಿಯ ಪ್ರಭುಗಳ ಸೈನಿಕರು ನೂರು ನೂರು ಮಂದಿಯಾಗಿಯೂ, ಸಾವಿರ ಸಾವಿರ ಮಂದಿಯಾಗಿ, ಬರುತ್ತಿರುವಾಗ ದಾವೀದನು ಅವನ ಜನರ ಸಹಿತವಾಗಿ ಆಕೀಷನ ಸಂಗಡ ಹಿಂಬಾಲಿಸುತ್ತಿದ್ದನು.",
			"3": "<sup>3</sup> ಆಗ ಫಿಲಿಷ್ಟಿಯ ಪ್ರಭುಗಳು, “ಈ ಇಬ್ರಿಯರು ಯಾಕೆ?” ಎಂದು ಆಕೀಷನನ್ನು ಕೇಳಿದರು. ಅವನು ಅವರಿಗೆ, “ಇವನು ಇಸ್ರಾಯೇಲರ ಅರಸನಾದ ಸೌಲನ ಸೇವಕ ದಾವೀದನಲ್ಲವೋ, ಇವನು ಇಷ್ಟು ವರ್ಷ, ಇಷ್ಟು ದಿನಗಳಿಂದ ನನ್ನ ಬಳಿಯಲ್ಲಿದ್ದಾನೆ. ಸೌಲನನ್ನು ಬಿಟ್ಟು ನನ್ನ ಹತ್ತಿರ ಬಂದ ದಿನ ಮೊದಲುಗೊಂಡು ಇಂದಿನವರೆಗೂ ನಾನು ಇವನಲ್ಲಿ ಯಾವ ಅಪರಾಧವನ್ನೂ ಕಾಣಲಿಲ್ಲ” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"4": "<sup>4</sup> ಆದರೆ ಫಿಲಿಷ್ಟಿಯ ಪ್ರಭುಗಳು ಅವನ ಮೇಲೆ ಕೋಪಗೊಂಡು ಅವನಿಗೆ, “ಈ ಮನುಷ್ಯನನ್ನು ಕಳುಹಿಸಿಬಿಡು, ಇವನು ಹಿಂದಿರುಗಿ ಹೋಗಿ ನೀನು ನೇಮಿಸಿದ ಸ್ಥಳದಲ್ಲೇ ವಾಸಿಸಲಿ. ನಮ್ಮ ಜೊತೆಯಲ್ಲಿ ಯುದ್ಧಕ್ಕೆ ಬರಬಾರದು. ಬಂದರೆ ನಮಗೆ ಶತ್ರುವಾಗಿ ನಿಂತಾನು, ಫಿಲಿಷ್ಟಿಯರ ತಲೆಗಳನ್ನು ಕಡಿದಿದ್ದರಿಂದಲೇ ಇವನು ತನ್ನ ಯಜಮಾನನ ಮೆಚ್ಚಿಕೆಯನ್ನು ಪಡೆಯಬಹುದಲ್ಲವೇ?",
			"5": "<sup>5</sup> ‘ಸೌಲನು ಸಾವಿರಾರು ಶತ್ರುಗಳನ್ನು ಕೊಂದನು. ದಾವೀದನು ಹತ್ತು ಸಾವಿರಾರು ಶತ್ರುಗಳನ್ನು ಕೊಂದನು’ ಎಂದು ಸ್ತ್ರೀಯರು ಕುಣಿಯುತ್ತಾ ಹಾಡಿದ್ದು ಈ ದಾವೀದನನ್ನು ಕುರಿತೇ ಅಲ್ಲವೇ?” ಅಂದರು.",
			"6": "<sup>6</sup> ಆಗ ಆಕೀಷನು ದಾವೀದನನ್ನು ಕರೆದು ಅವನಿಗೆ, “ಯೆಹೋವನಾಣೆ ನೀನು ಯಥಾರ್ಥನು. ನೀನು ನನ್ನ ಸಂಗಡ ಪಾಳೆಯದಲ್ಲಿದ್ದುಕೊಂಡು ಯುದ್ಧಕ್ಕೆ ಹೋಗುತ್ತಾ ಬರುತ್ತಾ ಇರುವುದು ಯುಕ್ತವೆಂದು ನನಗೆ ತೋರಿತು. ನೀನು ಬಂದಂದಿನಿಂದ ಇಂದಿನವರೆಗೂ ನಾನು ನಿನ್ನಲ್ಲಿ ಯಾವ ದೋಷವನ್ನೂ ಕಾಣಲಿಲ್ಲ.",
			"7": "<sup>7</sup> ಆದರೆ ಪ್ರಭುಗಳು ನಿನ್ನನ್ನು ಮೆಚ್ಚುವುದಿಲ್ಲ. ಆದ್ದರಿಂದ ನೀನು ಸಮಾಧಾನದಿಂದ ಹಿಂದಿರುಗಿ ಹೋಗು ಅವರನ್ನು ಸಿಟ್ಟುಗೊಳಿಸಬಾರದು” ಎಂದು ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ದಾವೀದನು ಆಕೀಷನಿಗೆ, “ನಾನೇನು ಮಾಡಿದೆನು? ನಿನ್ನ ದಾಸನಾದ ನಾನು ನಿನ್ನ ಬಳಿಗೆ ಬಂದ ದಿನದಿಂದ ಇಂದಿನವರೆಗೂ ನನ್ನಲ್ಲಿ ಯಾವ ಅಪರಾಧವನ್ನು ಕಂಡಿ? ಅರಸನಾದ ನನ್ನ ಒಡೆಯನ ಜೊತೆಯಲ್ಲಿ ಹೋಗಿ, ಅವನ ಶತ್ರುಗಳೊಡನೆ ನಾನೇಕೆ ಯುದ್ಧ ಮಾಡಬಾರದು?” ಅಂದನು.",
			"9": "<sup>9</sup> ಆಗ ಆಕೀಷನು ದಾವೀದನಿಗೆ, “ನೀನು ಒಳ್ಳೆಯವನೆಂದು ನನಗೆ ಗೊತ್ತದೆ, ನನ್ನ ದೃಷ್ಟಿಯಲ್ಲಿ ನೀನು ದೇವದೂತನಂತಿರುತ್ತೀ. ಆದರೆ ಫಿಲಿಷ್ಟಿಯ ಪ್ರಭುಗಳು, ‘ಇವನು ನಮ್ಮ ಜೊತೆಯಲ್ಲಿ ಯುದ್ಧಕ್ಕೆ ಹೊರಡಬಾರದು’ ಎಂದು ಹೇಳುತ್ತಾರಲ್ಲಾ.",
			"10": "<sup>10</sup> ಆದುದ್ದರಿಂದ ನೀನೂ, ನಿನ್ನ ಸಂಗಡ ಬಂದ ನಿನ್ನ ಒಡೆಯನ ಸೇವಕರೂ, ನಾಳೆ ಬೆಳಿಗ್ಗೆ ಎದ್ದು ಹೊತ್ತು ಮೂಡಿದ ಕೂಡಲೆ ಹಿಂದಿರುಗಿ ಹೋಗಿರಿ” ಎಂದು ಹೇಳಿದನು.",
			"11": "<sup>11</sup> ದಾವೀದನು ತನ್ನ ಜನರೊಡನೆ ಬೆಳಿಗ್ಗೆ ಎದ್ದು ಹಿಂದಿರುಗಿ ಫಿಲಿಷ್ಟಿಯರ ದೇಶಕ್ಕೆ ಹೊರಟನು. ಫಿಲಿಷ್ಟಿಯರು ಇಜ್ರೇಲಿಗೆ ಹೋದರು."
		},
		"30": {
			"1": "<sup>1</sup> ದಾವೀದನೂ ಅವನ ಜನರೂ ಮೂರನೆಯ ದಿನ ಚಿಕ್ಲಗ್ ಊರನ್ನು ಮುಟ್ಟಿದರು. ಅಷ್ಟರೊಳಗೆ ಅಮಾಲೇಕ್ಯರು ದಂಡೆತ್ತಿ ದಕ್ಷಿಣ ಪ್ರಾಂತ್ಯಕ್ಕೂ ಚಿಕ್ಲಗ್ ಊರಿಗೂ ಬಂದು ಚಿಕ್ಲಗ್ ಊರನ್ನು ಸುಟ್ಟು ಹಾಳುಮಾಡಿ,",
			"2": "<sup>2</sup> ಆ ಊರಿನಲ್ಲಿದ್ದ ಎಲ್ಲಾ ಹೆಂಗಸರನ್ನೂ ಚಿಕ್ಕವರನ್ನೂ ದೊಡ್ಡವರನ್ನೂ ಸೆರೆಹಿಡಿದುಕೊಂಡು ಹೋಗಿದ್ದರು.",
			"3": "<sup>3</sup> ಆದರೆ ಯಾರನ್ನೂ ಕೊಂದಿರಲಿಲ್ಲ. ದಾವೀದನೂ ಅವನ ಜನರೂ ತಮ್ಮ ಊರಿಗೆ ಬಂದಾಗ ಅದು ಸುಡಲ್ಪಟ್ಟಿರುವುದನ್ನೂ, ತಮ್ಮ ಹೆಂಡತಿಯರೂ ಗಂಡುಮಕ್ಕಳೂ ಸೆರೆಯಾಗಿ ಒಯ್ಯಲ್ಪಟ್ಟಿರುವುದನ್ನೂ ಕಂಡು",
			"4": "<sup>4</sup> ಅವರು ತಮ್ಮಿಂದಾಗುವಷ್ಟು ಕಾಲ ಅತ್ತರು.",
			"5": "<sup>5</sup> ದಾವೀದನ ಇಬ್ಬರು ಹೆಂಡತಿಯರಾದ ಇಜ್ರೇಲಿನವಳಾದ ಅಹೀನೋವಮಳೂ, ಕರ್ಮೆಲ್ಯನಾದ ನಾಬಾಲನ ಹೆಂಡತಿಯಾಗಿದ್ದ ಅಬೀಗೈಲಳೂ ಸೆರೆಯಾಗಿ ಹೋಗಿದ್ದರು.",
			"6": "<sup>6</sup> ಇದಲ್ಲದೆ ಜನರು ತಮ್ಮ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳಿಗಾಗಿ ಮನಸ್ಸಿನಲ್ಲಿ ಬಹಳವಾಗಿ ನೊಂದುಕೊಂಡು, ದಾವೀದನನ್ನು ಕಲ್ಲೆಸೆದು ಕೊಲ್ಲಬೇಕೆಂದಿದ್ದುದರಿಂದ ಅವನು ಬಲು ಇಕ್ಕಟ್ಟಿನಲ್ಲಿ ಬಿದ್ದನು. ಆದರೂ ಅವನು ತನ್ನ ದೇವರಾದ ಯೆಹೋವನಲ್ಲಿ ತನ್ನನ್ನು ಬಲಪಡಿಸಿಕೊಂಡು",
			"7": "<sup>7</sup> ಅಹೀಮೆಲೆಕನ ಮಗನಾದ ಎಬ್ಯಾತಾರನಿಗೆ, “ದಯವಿಟ್ಟು ಏಫೋದನ್ನು ಇಲ್ಲಿಗೆ ತೆಗೆದುಕೊಂಡು ಬಾ” ಎಂದು ಹೇಳಲು ಅವನು ತಂದನು.",
			"8": "<sup>8</sup> ಆಗ ದಾವೀದನು ಯೆಹೋವನನ್ನು, “ನಾನು ಆ ಗುಂಪನ್ನು ಹಿಂದಟ್ಟಬಹುದೋ, ಅದು ನನಗೆ ಸಿಕ್ಕುವುದೋ?” ಎಂದು ಕೇಳಲು ಆತನು, “ಹಿಂದಟ್ಟು, ಅದು ನಿನಗೆ ಸಿಕ್ಕುವುದು, ನೀನು ಎಲ್ಲರನ್ನೂ ಬಿಡಿಸಿಕೊಂಡು ಬರುವಿ” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"9": "<sup>9</sup> ದಾವೀದನೂ ಅವನ ಸಂಗಡ ಇದ್ದ ಆರುನೂರು ಮಂದಿ ಸೈನಿಕರೂ ಹೊರಟು ಬೆಸೋರ್ ಹಳ್ಳಕ್ಕೆ ಬಂದ ಮೇಲೆ ಆ ಹಳ್ಳದಿಂದ ಮೇಲೆ ಹತ್ತಲಾರದಷ್ಟು",
			"10": "<sup>10</sup> ಆಯಾಸಗೊಂಡ ಇನ್ನೂರು ಜನರು ಅಲ್ಲೇ ನಿಂತುಬಿಟ್ಟದ್ದರಿಂದ, ದಾವೀದನು ಉಳಿದ ನಾನೂರು ಜನರನ್ನೇ ಕರೆದುಕೊಂಡು ಮುಂದೆ ನಡೆದನು.",
			"11": "<sup>11</sup> ಅವನ ಜನರು ಅಡವಿಯಲ್ಲಿ ಒಬ್ಬ ಐಗುಪ್ತನು ಬಿದ್ದಿರುವುದನ್ನು ಕಂಡು, ಅವನನ್ನು ದಾವೀದನ ಬಳಿಗೆ ತಂದು, ಅವನಿಗೆ ರೊಟ್ಟಿ ತಿನ್ನಿಸಿ, ನೀರು ಕುಡಿಸಿ,",
			"12": "<sup>12</sup> ಅಂಜೂರ ಹಣ್ಣಿನ ಉಂಡೆಯನ್ನೂ ಎರಡು ದ್ರಾಕ್ಷೇ ಗೊಂಚಲುಗಳನ್ನು ಕೊಟ್ಟರು. ಮೂರು ದಿನ ಹಗಲಿರುಳು ಅನ್ನಪಾನವಿಲ್ಲದೆ ಬಹಳವಾಗಿ ಬಳಲಿಹೋಗಿದ್ದ ಇವನು ಅವುಗಳನ್ನು ತಿನ್ನುತ್ತಲೇ ಚೇತರಿಸಿಕೊಂಡನು.",
			"13": "<sup>13</sup> ಆಗ ದಾವೀದನು ಅವನನ್ನು, “ನೀನು ಯಾರ ಕಡೆಯವನು, ಎಲ್ಲಿಯವನು?” ಎಂದು ಕೇಳಿದನು. ಅವನು “ನಾನು ಐಗುಪ್ತನು, ಒಬ್ಬ ಅಮಾಲೇಕ್ಯನ ದಾಸನು. ನಾನು ಮೂರು ದಿನಗಳ ಹಿಂದೆ ಅಸ್ವಸ್ಥನಾಗಿ ಮಲಗಿಕೊಂಡಿದ್ದರಿಂದ ನನ್ನ ಯಜಮಾನನು ನನ್ನನ್ನು ಬಿಟ್ಟುಹೋದನು.",
			"14": "<sup>14</sup> ನಾವು ಕೆರೇತ್ಯರೂ ಯೆಹೂದ್ಯರೂ ಕಾಲೇಬ್ಯರೂ ಇರುವ ದಕ್ಷಿಣ ಪ್ರಾಂತ್ಯವನ್ನು ಸೂರೆಮಾಡಿ ಚಿಕ್ಲಗ್ ಊರನ್ನು ಸುಟ್ಟುಬಿಟ್ಟು ಬಂದೆವು” ಎಂದು ಉತ್ತರ ಕೊಟ್ಟನು.",
			"15": "<sup>15</sup> ಅನಂತರ ದಾವೀದನು, “ನೀನು ನನ್ನನ್ನು ಆ ಗುಂಪು ಇರುವ ಸ್ಥಳಕ್ಕೆ ಕರೆದುಕೊಂಡು ಹೋಗುವಿಯೋ?” ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ ಅವನು, “ನೀನು ನನ್ನನ್ನು ಕೊಲ್ಲುವುದಿಲ್ಲವೆಂದೂ ದೇವರ ಹೆಸರಿನಲ್ಲಿ ಪ್ರಮಾಣ ಮಾಡಿದರೆ ನಿನ್ನನ್ನು ಆ ಗುಂಪಿನವರ ಬಳಿಗೆ ಕರಕೊಂಡು ಹೋಗುವೆನು” ಅಂದನು.",
			"16": "<sup>16</sup> ಅವರು ಅಲ್ಲಿಗೆ ಹೋದಾಗ ಆ ಗುಂಪಿನವರು ತಮಗೆ ಫಿಲಿಷ್ಟಿಯ ಮತ್ತು ಯೆಹೂದ ಪ್ರಾಂತ್ಯಗಳಲ್ಲಿ ದೊರಕಿದ ದೊಡ್ಡ ಕೊಳ್ಳೆಯ ನಿಮಿತ್ತವಾಗಿ ಸಂತೋಷಿಸಿ, ಉಂಡು ಕುಡಿದು, ಕುಣಿದಾಡುತ್ತಾ ಅಲ್ಲಿನ ಬೈಲಿನಲ್ಲಿ ವ್ಯಾಪಿಸಿಕೊಂಡಿದ್ದರು.",
			"17": "<sup>17</sup> ದಾವೀದನು ಮರುದಿನ ಹೊತ್ತಾರೆಯಿಂದ ಸೂರ್ಯಸ್ತಮಾನದವರೆಗೂ ಅವರನ್ನು ಸಂಹರಿಸಿದನು. ಒಂಟೆಗಳನ್ನು ಹತ್ತಿ ಓಡಿಹೋದ ನಾನೂರು ಮಂದಿ ಪ್ರಾಯಸ್ಥರ ಹೊರತು ಬೇರೆ ಯಾರೂ ತಪ್ಪಿಸಿಕೊಳ್ಳಲಿಲ್ಲ.",
			"18": "<sup>18</sup> ದಾವೀದನು ತನ್ನ ಇಬ್ಬರು ಹೆಂಡತಿಯರನ್ನೂ ಅಮಾಲೇಕ್ಯರು ಒಯ್ದಿದ್ದ ಎಲ್ಲಾ ಕೊಳ್ಳೆಯನ್ನೂ ಬಿಡಿಸಿಕೊಂಡನು.",
			"19": "<sup>19</sup> ಅಮಾಲೇಕ್ಯರು ಒಯ್ದಿದ್ದ ಚಿಕ್ಕ ದೊಡ್ಡ ಒಡವೆಗಳಾಗಲಿ, ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳಾಗಲಿ ಒಂದನ್ನೂ ಬಿಡದೆ ಎಲ್ಲವನ್ನೂ ತಿರುಗಿ ತೆಗೆದುಕೊಂಡು ಬಂದನು.",
			"20": "<sup>20</sup> ಇದಲ್ಲದೆ ದಾವೀದನು ಶತ್ರುಗಳ ದನಕುರಿಗಳನ್ನೂ ತೆಗೆದುಕೊಂಡನು. ಅವನ ಜನರು, “ಇವು ದಾವೀದನ ಕೊಳ್ಳೆ” ಎಂದು ಹೇಳಿ ಅವುಗಳನ್ನು ತಮ್ಮ ದನಗಳ ಮುಂದಾಗಿ ಹೊಡೆದುಕೊಂಡು ಬಂದರು.",
			"21": "<sup>21</sup> ಅನಂತರ ದಾವೀದನು ಆಯಾಸದಿಂದ ತನ್ನ ಹಿಂದೆ ಬರಲಾರದೆ ಬೆಸೋರ್ ಹಳ್ಳದ ಹತ್ತಿರ ಉಳಿದಿದ್ದ ಇನ್ನೂರು ಜನರ ಬಳಿಗೆ ಹೋಗುತ್ತಿರುವಾಗ, ಅವರು ಅವನನ್ನೂ ಅವನೊಂದಿಗಿದ್ದ ಜನರನ್ನೂ ಸಂಧಿಸಿ, ಅವರ ಕ್ಷೇಮಸಮಾಚಾರವನ್ನು ವಿಚಾರಿಸಿದರು.",
			"22": "<sup>22</sup> ಆಗ ದಾವೀದನ ಜೊತೆಯಲ್ಲಿ ಹೋಗಿದ್ದ ಜನರಲ್ಲಿ ದುಷ್ಟರೂ ಮೂರ್ಖರೂ ಆದ ಕೆಲವು ಮಂದಿ, “ಇವರು ನಮ್ಮ ಸಂಗಡ ಬಾರದ್ದರಿಂದ, ನಾವು ತೆಗೆದುಕೊಂಡು ಬಂದಿರುವ ಕೊಳ್ಳೆಯಲ್ಲಿ ಇವರಿಗೆ ಏನೂ ಸಿಕ್ಕುವುದಿಲ್ಲ. ತಮ್ಮ ತಮ್ಮ ಹೆಂಡತಿ ಮಕ್ಕಳನ್ನು ಕರೆದುಕೊಂಡು ಹೋಗಿಬಿಡಲಿ” ಎಂದರು.",
			"23": "<sup>23</sup> ಆಗ ದಾವೀದನು ಅವರಿಗೆ, “ಸಹೋದರರೇ, ಯೆಹೋವನು ನಮ್ಮನ್ನು ಕಾಪಾಡಿ ನಮಗೆ ವಿರೋಧವಾಗಿ ಬಂದಿದ್ದ ಶತ್ರುಗಳ ಗುಂಪನ್ನು ನಮ್ಮ ಕೈಗೆ ಒಪ್ಪಿಸಿ ನಮಗೆ ಇಷ್ಟನ್ನೆಲ್ಲಾ ಕೊಟ್ಟ ಮೇಲೆ ನೀವು ಹೀಗೆ ಮಾಡಬಾರದು.",
			"24": "<sup>24</sup> ಈ ವಿಷಯದಲ್ಲಿ ನಿಮ್ಮ ಮಾತನ್ನು ಯಾರು ಕೇಳುವರು? ಯುದ್ಧಮಾಡಿದವನಿಗೆ ಸಿಕ್ಕುವಷ್ಟು ಪಾಲು ಸಾಮಾನು ಕಾಯುವವನಿಗೂ ಸಿಕ್ಕಬೇಕು. ಉಭಯರಿಗೂ ಸಮಭಾಗವಾಗಬೇಕು” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"25": "<sup>25</sup> ದಾವೀದನು ಇಸ್ರಾಯೇಲರಲ್ಲಿ ಇದನ್ನು ಕಟ್ಟಳೆಯಾಗಿ ನೇಮಿಸಿದ್ದರಿಂದ ಇಂದಿನವರೆಗೂ ಹಾಗೆಯೇ ನಡೆಯುತ್ತಾ ಬಂದಿದೆ.",
			"26": "<sup>26</sup> ದಾವೀದನು ಚಿಕ್ಲಗ್ ಊರಿಗೆ ಬಂದನಂತರ ತನ್ನ ಸ್ನೇಹಿತರಾದ ಯೆಹೂದದ ಹಿರಿಯರಿಗೆ ಕೊಳ್ಳೆಯ ಒಂದು ಭಾಗವನ್ನು ಕಳುಹಿಸಿ, “ಇಗೋ ಯೆಹೋವನ ಶತ್ರುಗಳನ್ನು ಸೂರೆಮಾಡಿ ತಂದವುಗಳಲ್ಲಿ ಇದು ನಿಮಗೆ ಬಹುಮಾನವಾಗಿರಲಿ” ಎಂದು ಹೇಳಿದನು.",
			"27": "<sup>27</sup> ಬಹುಮಾನ ಹೊಂದಿದವರು ಯಾರೆಂದರೆ: ಬೇತೇಲಿನವರು, ದಕ್ಷಿಣ ಪ್ರಾಂತ್ಯದ ರಾಮೋತಿನವರು, ಯತ್ತೀರಿನವರು,",
			"28": "<sup>28</sup> ಅರೋಯೇರಿನವರು ಸಿಪ್ಮೋತಿನವರು ಎಷ್ಟೆಮೋವದವರು,",
			"29": "<sup>29</sup> ರಾಕಾಲಿನವರು, ಎರಹ್ಮೇಲ್ಯರು, ಕೇನ್ಯರು,",
			"30": "<sup>30</sup> ಹೊರ್ಮದವರು, ಬೋರಾಷಾನಿನವರು, ಅತಾಕಿನವರು,",
			"31": "<sup>31</sup> ಹೆಬ್ರೋನಿನವರು ಮತ್ತು ದಾವೀದನೂ ಅವನ ಜನರೂ ಸಂಚರಿಸುತ್ತಿದ್ದ ಇತರ ಎಲ್ಲಾ ಸ್ಥಳಗಳವರು ಇವರೇ."
		},
		"31": {
			"1": "<sup>1</sup> ಫಿಲಿಷ್ಟಿಯರು ಇಸ್ರಾಯೇಲರೊಡನೆ ಯುದ್ಧಮಾಡಿದರು. ಇಸ್ರಾಯೇಲರು ಅವರಿಂದ ಅಪಜಯಹೊಂದಿ ಗಿಲ್ಬೋವ ಬೆಟ್ಟದಲ್ಲಿ ಹತರಾದರು.",
			"2": "<sup>2</sup> ಫಿಲಿಷ್ಟಿಯರು ಸೌಲನನ್ನೂ ಅವನ ಮಕ್ಕಳನ್ನೂ ಬಿಡದೆ ಹಿಂದಟ್ಟಿ, ಅವನ ಮಕ್ಕಳಾದ ಯೋನಾತಾನನನ್ನೂ ಅಬೀನಾದಾಬನನ್ನೂ ಮಲ್ಕೀಷೂವನನ್ನೂ ಕೊಂದರು.",
			"3": "<sup>3</sup> ಸೌಲನಿದ್ದ ಕಡೆಯಲ್ಲಿ ಯುದ್ಧವು ಬಹು ಘೋರವಾಗಿತ್ತು. ಬಿಲ್ಲುಗಾರನು ಅವನಿಗೆ ಗುರಿಯಿಟ್ಟನು.",
			"4": "<sup>4</sup> ಆದುದರಿಂದ ಸೌಲನು ಬಹು ಭೀತನಾಗಿ ತನ್ನ ಆಯುಧವಾಹಕನಿಗೆ “ನಿನ್ನ ಕತ್ತಿಯನ್ನು ಹಿರಿದು ನನ್ನನ್ನು ತಿವಿದು ಕೊಲ್ಲು. ಇಲ್ಲವಾದರೆ ಸುನ್ನತಿಯಿಲ್ಲದ ಈ ಜನರು ಬಂದು ನನ್ನನ್ನು ತಿವಿದು, ಅಪಕೀರ್ತಿಯನ್ನು ಉಂಟುಮಾಡಾರು” ಎಂದು ಹೇಳಲು ಅವನು ಹೆದರಿ, “ನಾನು ಕೊಲ್ಲಲಾರೆ” ಎಂದನು. ಆದುದರಿಂದ ಸೌಲನು ತಾನೇ ಕತ್ತಿಯನ್ನು ಹಿರಿದು ಅದರ ಮೇಲೆ ಬಿದ್ದನು.",
			"5": "<sup>5</sup> ಸೌಲನು ಮರಣ ಹೊಂದಿರುವುದನ್ನು ಅವನ ಆಯುಧವಾಹಕನು ಕಂಡು ತಾನೂ ತನ್ನ ಕತ್ತಿಯ ಮೇಲೆ ಬಿದ್ದು ಅಲ್ಲಿಯೇ ಸತ್ತನು.",
			"6": "<sup>6</sup> ಹೀಗೆ ಸೌಲನೂ ಅವನ ಮೂರು ಮಂದಿ ಮಕ್ಕಳೂ, ಅವನ ಆಯುಧವಾಹಕನು, ಎಲ್ಲಾ ಆಳುಗಳೂ ಅದೇ ದಿನದಲ್ಲಿ ಸತ್ತರು.",
			"7": "<sup>7</sup> ಇಸ್ರಾಯೇಲರ ಭಟರು ಸೋತುಹೋದರು. ಸೌಲನೂ ಅವನ ಮಕ್ಕಳೂ ಸತ್ತರು ಎಂಬ ವರ್ತಮಾನವನ್ನು ತಗ್ಗಿನ ಆಚೆಯಲ್ಲಿಯೂ ಯೊರ್ದನಿನ ಆಚೆಯಲ್ಲಿಯೂ ವಾಸವಾಗಿದ್ದ ಇಸ್ರಾಯೇಲರು ಕೇಳಿ ತಮ್ಮ ಪಟ್ಟಣಗಳನ್ನು ಬಿಟ್ಟು ಓಡಿಹೋದರು. ಫಿಲಿಷ್ಟಿಯರು ಬಂದು ಅವುಗಳಲ್ಲಿ ವಾಸಮಾಡಿದರು.",
			"8": "<sup>8</sup> ಮರುದಿನ ಬೆಳಿಗ್ಗೆ ಫಿಲಿಷ್ಟಿಯರು ಸತ್ತವರ ಒಡವೆಗಳನ್ನು ಸುಲಿದುಕೊಳ್ಳುವುದಕ್ಕೆ ಬಂದಾಗ, ಸೌಲನೂ ಅವನ ಮೂರು ಮಂದಿ ಮಕ್ಕಳೂ ಗಿಲ್ಬೋವ ಬೆಟ್ಟದಲ್ಲಿ ಸತ್ತುಬಿದ್ದಿರುವುದನ್ನು ಕಂಡು",
			"9": "<sup>9</sup> ಸೌಲನ ತಲೆಯನ್ನು ಕಡಿದು ಅವನ ಆಯುಧಗಳನ್ನು ತೆಗೆದುಕೊಂಡನು. ಅವುಗಳನ್ನು ತಮ್ಮ ದೇಶದ ಎಲ್ಲಾ ಕಡೆಗೂ ಕಳುಹಿಸಿ, ತಮ್ಮ ಎಲ್ಲ ಜನರಿಗೂ ದೇವಸ್ಥಾನಗಳಿಗೂ ಜಯವಾರ್ತೆಯನ್ನು ಮುಟ್ಟಿಸಿದರು.",
			"10": "<sup>10</sup> ಅವನ ಆಯುಧಗಳನ್ನು ಅಷ್ಟೋರೆತ್ ದೇವತೆಯ ಗುಡಿಯಲ್ಲಿಟ್ಟರು. ಅವನ ಶವವನ್ನು ಬೇತ್ಷೆಯಾನಿನ ಕೋಟೆ ಗೋಡೆಗೆ ನೇತುಹಾಕಿದರು.",
			"11": "<sup>11</sup> ಫಿಲಿಷ್ಟಿಯರು ಸೌಲನಿಗೆ ಏನೇನು ಮಾಡಿದರೆಂಬ ವರ್ತಮಾನ ಯಾಬೆಷ್ ಗಿಲ್ಯಾದಿನವರಿಗೆ ಮುಟ್ಟಿದಾಗ",
			"12": "<sup>12</sup> ಅವರಲ್ಲಿದ್ದ ಶೂರರೆಲ್ಲರೂ ಹೊರಟು, ರಾತ್ರಿಯೆಲ್ಲಾ ನಡೆದುಹೋಗಿ, ಸೌಲನ ಮತ್ತು ಅವನ ಮಕ್ಕಳ ಶವಗಳನ್ನು ಬೇತ್ಷೆಯಾನಿನ ಗೋಡೆಯಿಂದ ಇಳಿಸಿ, ಯಾಬೇಷಿಗೆ ತೆಗೆದುಕೊಂಡು ಬಂದು ಅಲ್ಲಿ ಅವುಗಳನ್ನು ಸುಟ್ಟುಬಿಟ್ಟರು.",
			"13": "<sup>13</sup> ಅವರ ಎಲುಬುಗಳನ್ನು ಯಾಬೇಷಿನಲ್ಲಿದ್ದ ಪಿಚುಲ ವೃಕ್ಷದ ಕೆಳಗೆ ಸಮಾಧಿಮಾಡಿ, ಏಳು ದಿನ ಉಪವಾಸ ಮಾಡಿದರು."
		}
	}
};
module.exports = book;