var book = {
	"name": "ಜೆಕರ್ಯನು",
	"numChapters": 14,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ದಾರ್ಯಾವೆಷನ ಆಳ್ವಿಕೆಯ ಎರಡನೆಯ ವರುಷದ ಎಂಟನೆಯ ತಿಂಗಳಲ್ಲಿ ಯೆಹೋವನು ಇದ್ದೋವಿನ ಮೊಮ್ಮಗನೂ ಬೆರೆಕ್ಯನ ಮಗನೂ ಅದ ಜೆಕರ್ಯನೆಂಬ ಪ್ರವಾದಿಗೆ ಈ ವಾಕ್ಯವನ್ನು ದಯಪಾಲಿಸಿದನು. ಅದೇನೆಂದರೆ,",
			"2": "<sup>2</sup> “ಯೆಹೋವನು ನಿಮ್ಮ ಪಿತೃಗಳ ಮೇಲೆ ಬಹು ಕೋಪಗೊಂಡಿದ್ದಾನೆ.",
			"3": "<sup>3</sup> ಆದಕಾರಣ ನೀನು ನಿನ್ನ ಜನರಿಗೆ ಹೀಗೆ ಹೇಳು, ‘ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ, ನನ್ನ ಕಡೆಗೆ ಪುನಃ ತಿರುಗಿಕೊಳ್ಳಿರಿ’” ಇದು ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನ ನುಡಿ, “ನಾನು ನಿಮ್ಮ ಕಡೆಗೆ ಪುನಃ ತಿರುಗುವೆನು” ಇದು ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನ ನುಡಿ.",
			"4": "<sup>4</sup> ನಿಮ್ಮ ಪಿತೃಗಳಂತಿರಬೇಡಿರಿ; ಪೂರ್ವಕಾಲದ ಪ್ರವಾದಿಗಳು ಅವರಿಗೆ, “ಸೇನಾಧೀಶ್ವರ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ, ‘ನಿಮ್ಮ ದುರ್ಮಾರ್ಗ, ದುಷ್ಕೃತ್ಯಗಳಿಂದ ಹಿಂದಿರುಗಿರಿ’ ಎಂದು ಸಾರಿದರೂ ಅವರು ನನ್ನ ಮಾತನ್ನು ಕಿವಿಗೊಟ್ಟು ಕೇಳಲಿಲ್ಲ” ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"5": "<sup>5</sup> “ನಿಮ್ಮ ಪಿತೃಗಳು ಎಲ್ಲಿ? ಪ್ರವಾದಿಗಳು ಸದಾಕಾಲ ನಿತ್ಯನಿರಂತರವಾಗಿ ಇರುವರೇ?",
			"6": "<sup>6</sup> ಆದರೆ ನನ್ನ ಸೇವಕರಾದ ಪ್ರವಾದಿಗಳಿಗೆ ಆಜ್ಞಾಪಿಸಿದ ನನ್ನ ವಾಕ್ಯಗಳೂ ಮತ್ತು ವಿಧಿಗಳೂ ನಿಮ್ಮ ಪಿತೃಗಳನ್ನು ಹಿಂದಟ್ಟಿ ಹಿಡಿದು ಶಾಶ್ವತವಾಗಿ ಉಳಿದಿದೆ. ಅವರು ತಿರುಗಿಕೊಂಡು, ಸೇನಾಧೀಶ್ವರ ಯೆಹೋವನಿಗೆ ‘ನಮ್ಮ ದುಷ್ಕೃತ್ಯಗಳಿಗೆ ತಕ್ಕ ಹಾಗೆ ನಮಗೆ ಏನು ಮಾಡಬೇಕೆಂದು ಸಂಕಲ್ಪಿಸಿದನೋ ಅದನ್ನು ನಮಗೆ ಮಾಡಿದ್ದಾನಲ್ಲಾ’” ಅಂದುಕೊಂಡರು.",
			"7": "<sup>7</sup> ದಾರ್ಯಾವೆಷನ ಆಳ್ವಿಕೆಯ ಎರಡನೆಯ ವರುಷದ ಹನ್ನೊಂದನೆಯ ತಿಂಗಳಿನ ಅಂದರೆ ಶೆಬಾಟ್ ತಿಂಗಳಿನ ಇಪ್ಪತ್ತನಾಲ್ಕನೆಯ ದಿನದಲ್ಲಿ ಯೆಹೋವನ ವಾಕ್ಯವು ಇದ್ದೋವಿನ ಮೊಮ್ಮಗನೂ ಬೆರೆಕ್ಯನ ಮಗನಾದ ಜೆಕರ್ಯನೆಂಬ ಪ್ರವಾದಿಗೆ ಕೇಳಿಬಂತು,",
			"8": "<sup>8</sup> “ರಾತ್ರಿಯಲ್ಲಿ ನನಗೆ ದರ್ಶನವಾಯಿತು. ಇಗೋ, ಕೆಂಪು ಕುದುರೆಯನ್ನು ಹತ್ತಿದ ಒಬ್ಬ ಪುರುಷನು ತಗ್ಗಿನಲ್ಲಿರುವ ಸುಗಂಧವೃಕ್ಷಗಳ ನಡುವೆ ನಿಂತಿದ್ದನು; ಅವನ ಹಿಂದೆ ಕೆಂಪು, ಕಂದು, ಬಿಳಿ ಕುದುರೆಗಳು ಮತ್ತು ಅವರ ಸವಾರರಿದ್ದರು.",
			"9": "<sup>9</sup> ನನಗೆ ಕನಸಿನ ಅರ್ಥವನ್ನು ವಿವರಿಸುವ ದೇವದೂತನನ್ನು, ‘ಸ್ವಾಮಿ ಇವರು ಯಾರು?’” ಎಂದು ನಾನು ಕೇಳಲು ಅತನು ನನಗೆ, “ಇವರು ಇಂಥವರೆಂದು ನಿನಗೆ ತೋರಿಸುವೆನು” ಎಂಬುದಾಗಿ ಹೇಳಿದನು.",
			"10": "<sup>10</sup> ಕೂಡಲೆ ಸುಗಂಧವೃಕ್ಷಗಳ ನಡುವೆ ನಿಂತಿದ್ದವನು, “ಇವರು ಲೋಕಸಂಚಾರಾರ್ಥವಾಗಿ ಯೆಹೋವನಿಂದ ಕಳುಹಿಸಲ್ಪಟ್ಟವರು” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"11": "<sup>11</sup> ಆಗ ಅವರು ಸುಗಂಧವೃಕ್ಷಗಳ ನಡುವೆ ನಿಂತಿದ್ದ ಆ ಯೆಹೋವನ ದೂತನಿಗೆ, “ನಾವು ಲೋಕದಲ್ಲಿ ಸಂಚರಿಸಿ ಬಂದಿದ್ದೇವೆ. ಇಗೋ ಲೋಕವೆಲ್ಲಾ ನೆಮ್ಮದಿಯಾಗಿ ಸುಮ್ಮನಿದೆ” ಎಂದು ಅರಿಕೆಮಾಡಿದರು.",
			"12": "<sup>12</sup> ಇದನ್ನು ಕೇಳಿ ಯೆಹೋವನ ದೂತನು, “ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೇ, ನೀನು ಎಪ್ಪತ್ತು ವರುಷಗಳಿಂದ ರೋಷಗೊಂಡಿರುವ ಯೆರೂಸಲೇಮ್ ಮೊದಲಾದ ಯೆಹೂದದ ಪಟ್ಟಣಗಳನ್ನು ಎಷ್ಟು ಕಾಲ ಕರುಣಿಸದೆ ಇರುವಿ” ಎಂದು ಬಿನ್ನವಿಸಲು,",
			"13": "<sup>13</sup> ನನ್ನೊಂದಿಗೆ ಮಾತನಾಡಿದ ದೂತನಿಗೆ, ಯೆಹೋವನು ಕರುಣೆ ಹಾಗೂ ಒಳ್ಳೆಯ ಮಾತುಗಳಿಂದ ಉತ್ತರಕೊಟ್ಟನು.",
			"14": "<sup>14</sup> ಆಗ ಆ ದೂತನು ನನಗೆ ಈ ಅಪ್ಪಣೆ ಮಾಡಿದನು, “ನೀನು ಹೀಗೆ ಸಾರಿ ಹೇಳಬೇಕು, ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ, ‘ಯೆರೂಸಲೇಮಿಗೂ, ಚೀಯೋನಿಗೂ ಅವಮಾನವಾಯಿತಲ್ಲಾ’ ಎಂದು ಬಹಳವಾಗಿ ಅಸಮಾಧಾನಗೊಂಡಿದ್ದೇನೆ.",
			"15": "<sup>15</sup> ನೆಮ್ಮದಿಯಾಗಿರುವ ಜನಾಂಗಗಳ ಮೇಲೆ ನಾನು ಬಹಳ ಕೋಪಗೊಂಡಿದ್ದೇನೆ; ನಾನು ಯೆರೂಸಲೇಮಿನ ಮೇಲೆ ಸ್ವಲ್ಪ ಮಾತ್ರ ಸಿಟ್ಟುಗೊಂಡು ಮಾಡಬೇಕೆಂದಿದ್ದ ಕೇಡಿಗಿಂತ ಅವರೇ ಹೆಚ್ಚಾಗಿ ಕೇಡಿಗೆ ಕೇಡು ಸೇರಿಸಿಕೊಂಡರು.”",
			"16": "<sup>16</sup> ಹೀಗಿರಲು ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ, “ನಾನು ಕನಿಕರವುಳ್ಳವನಾಗಿ ಯೆರೂಸಲೇಮಿಗೆ ಹಿಂದಿರುಗಿದ್ದೇನೆ; ನನ್ನ ಆಲಯವು ಪುನಃ ಅಲ್ಲಿ ಕಟ್ಟಲ್ಪಡುವುದು; ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ನೂಲು ಎಳೆಯಲ್ಪಡುವುದು” ಇದು ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನ ನುಡಿ.",
			"17": "<sup>17</sup> ಮತ್ತೊಮ್ಮೆ ಹೀಗೆ ಸಾರಿ ಹೇಳು, ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ, “ಇನ್ನು ನನ್ನ ಪಟ್ಟಣಗಳಲ್ಲಿ ಶುಭವು ತುಂಬಿ ತುಳುಕುವುದು; ಯೆಹೋವನು ಇನ್ನು ಚೀಯೋನನ್ನು ಸಂತೈಸುವನು, ಯೆರೂಸಲೇಮನ್ನು ಮತ್ತೆ ತನಗಾಗಿ ಆರಿಸಿಕೊಳ್ಳುವನು.”",
			"18": "<sup>18</sup> ನಾನು ಕಣ್ಣೆತ್ತಿ ನೋಡಲು ಇಗೋ, ನಾಲ್ಕು ಕೊಂಬುಗಳು ಕಾಣಿಸಿದವು.",
			"19": "<sup>19</sup> ವಿವರಿಸುವ ದೂತನನ್ನು ಕುರಿತು, “ಇವು ಏನು?” ಎಂದು ನಾನು ಕೇಳಿದ್ದಕ್ಕೆ ಅವನು, “ಇವು ಯೆಹೂದ, ಇಸ್ರಾಯೇಲ್, ಯೆರೂಸಲೇಮನ್ನು ಚದುರಿಸುವ ಕೊಂಬುಗಳು” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"20": "<sup>20</sup> ಅನಂತರ ಯೆಹೋವನು ನನಗೆ ನಾಲ್ಕು ಮಂದಿ ಕಮ್ಮಾರರನ್ನು ತೋರಿಸಿದನು.",
			"21": "<sup>21</sup> ಆಗ ನಾನು, “ಇವರು ಏನು ಮಾಡುವುದಕ್ಕೆ ಬಂದಿದ್ದಾರೆ?” ಎಂದು ಕೇಳಲು ಅವನು, “ಈ ಕೊಂಬುಗಳು ಯೆಹೂದದವರಲ್ಲಿ ಯಾರೂ ತಲೆಯೆತ್ತದಂತೆ ಅವರನ್ನು ಚದುರಿಸಿವೆಯಷ್ಟೆ; ಇವರಾದರೋ ಯೆಹೂದ ದೇಶದವರನ್ನು ಚದುರಿಸಬೇಕೆಂದು ತಲೆಯೆತ್ತಿದ ಜನಾಂಗಗಳ ಕೊಂಬುಗಳನ್ನು ಹೆದರಿಸಿ ಕೆಡವುವುದಕ್ಕೆ ಬಂದಿದ್ದಾರೆ” ಎಂದು ಹೇಳಿದನು."
		},
		"2": {
			"1": "<sup>1</sup> ನಾನು ಕಣ್ಣಿತ್ತಿ ನೋಡಲು ಇಗೋ, ಕೈಯಲ್ಲಿ ಅಳತೆಯ ನೂಲನ್ನು ಹಿಡಿದುಕೊಂಡಿದ್ದವನು ಕಾಣಿಸಿದನು.",
			"2": "<sup>2</sup> ನಾನು ಅವನನ್ನು, “ಎಲ್ಲಿಗೆ ಹೋಗುತ್ತಿ?” ಎಂದು ಕೇಳಲು ಅವನು ನನಗೆ, “ಯೆರೂಸಲೇಮಿನ ಅಗಲ, ಉದ್ದ ಎಷ್ಟಿರಬೇಕೆಂದು ಅಳೆದು ನೋಡುವುದಕ್ಕೆ ಹೋಗುತ್ತಿದ್ದೇನೆ” ಎಂದು ಹೇಳಿದನು.",
			"3": "<sup>3</sup> ನನ್ನ ಸಂಗಡ ಮಾತನಾಡಿದ ದೇವದೂತನು ಬರುತ್ತಿರಲು ಇನ್ನೊಬ್ಬ ದೂತನು ಅವನನ್ನು ಎದುರುಗೊಂಡು ಹೀಗೆ ಹೇಳಿದನು,",
			"4": "<sup>4</sup> “ನೀನು ಓಡು, ಆ ಯುವಕನಿಗೆ ಈ ಮಾತನ್ನು ತಿಳಿಸು, ‘ಯೆರೂಸಲೇಮಿನಲ್ಲಿರುವ ಜನ ಮತ್ತು ಪಶುಗಳ ಸಂಖ್ಯೆಯು ಅಪಾರವಾಗಿರುವುದರಿಂದ ಅದು ಪೌಳಿಗೋಡೆಯಿಲ್ಲದ ಹಳ್ಳಿಪಳ್ಳಿಗಳಂತೆ ಹರಡಿಕೊಂಡು ನೆಲೆಯಾಗಿರುವುದು.",
			"5": "<sup>5</sup> ನಾನೇ, ಅದರ ಸುತ್ತಮುತ್ತಲೂ ಅಗ್ನಿಪ್ರಾಕಾರವಾಗಿ, ಅದರೊಳಗೆ ವೈಭವವೂ ಆಗಿರುವೆನು’” ಎಂಬುದು ಯೆಹೋವನ ನುಡಿ.",
			"6": "<sup>6</sup> ಎಲೈ, ಎಲೈ ಉತ್ತರದೇಶದಿಂದ ಓಡಿ ಬನ್ನಿರಿ; ಇದು ಯೆಹೋವನ ನುಡಿ; ಚತುರ್ದಿಕ್ಕಿನ ಗಾಳಿಗಳಂತೆ ನಿಮ್ಮನ್ನು ಚದುರಿಸಿದ್ದೆನಲ್ಲಾ; ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"7": "<sup>7</sup> ಎಲೈ, ಬಾಬೆಲ್ ಪುರಿಯಲ್ಲಿ ವಾಸಿಸುವ ಚೀಯೋನಿನವರೇ, ತಪ್ಪಿಸಿಕೊಂಡು ಬನ್ನಿರಿ.",
			"8": "<sup>8</sup> ನಿಮ್ಮನ್ನು ತಾಕುವವನು ಯೆಹೋವನ ಕಣ್ಣು ಗುಡ್ಡೆಯನ್ನು ತಾಕುವವನಾಗಿದ್ದಾನೆ. ಆದಕಾರಣ ತನ್ನ ಪ್ರಸಿದ್ಧಿಗಾಗಿ ನನ್ನನ್ನು ಕಳುಹಿಸಿದ ಸೇನಾಧೀಶ್ವರ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಸೂರೆಮಾಡಿದ ಜನಾಂಗಗಳ ವಿಷಯವಾಗಿ,",
			"9": "<sup>9</sup> “ಆಹಾ, ನಾನು ಇವರ ಮೇಲೆ ಕೈಬೀಸುವೆನು; ಇವರು ತಮಗೆ ಅಡಿಯಾಳಾದವರಿಗೆ ಸೂರೆಯಾಗುವರು” ಎಂದು ಹೇಳುತ್ತಾನೆ. ಆಗ ಸೇನಾಧೀಶ್ವರ ಯೆಹೋವನು ನನ್ನನ್ನು ಕಳುಹಿಸಿದ್ದಾನೆಂಬುದು ನಿಮಗೆ ಗೊತ್ತಾಗುವುದು.",
			"10": "<sup>10</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ, “ಚೀಯೋನ್ ನಗರಿಯೇ, ಉಲ್ಲಾಸಗೊಳ್ಳು, ಹರ್ಷಧ್ವನಿಗೈ! ಇಗೋ, ನಾನು ಬಂದು ನಿನ್ನ ಮಧ್ಯದಲ್ಲಿ ವಾಸಿಸುವೆನು.",
			"11": "<sup>11</sup> ಆ ಕಾಲದಲ್ಲಿ ಬಹು ಜನಾಂಗಗಳು ಯೆಹೋವನಾದ ನನ್ನನ್ನು ಆಶ್ರಯಿಸಿಕೊಂಡು ನನ್ನ ಜನವಾಗುವರು; ನಾನು ನಿಮ್ಮ ಮಧ್ಯದಲ್ಲಿ ವಾಸಿಸುವೆನು” ಆಗ, ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೇ ನನ್ನನ್ನು ನಿನ್ನ ಬಳಿಗೆ ಕಳುಹಿಸಿದ್ದಾನೆಂಬುದು ನಿನಗೆ ಗೊತ್ತಾಗುವುದು.",
			"12": "<sup>12</sup> ಯೆಹೋವನು ಮೀಸಲಾದ ದೇಶದಲ್ಲಿ ಯೆಹೂದವನ್ನು ತನ್ನ ಸ್ವಾಸ್ತ್ಯವನ್ನಾಗಿ ಅನುಭವಿಸುವನು, ಯೆರೂಸಲೇಮನ್ನು ಮತ್ತೆ ತನಗಾಗಿ ಆರಿಸಿಕೊಳ್ಳುವನು.",
			"13": "<sup>13</sup> ಯೆಹೋವನು ತನ್ನ ಪರಿಶುದ್ಧ ನಿವಾಸದಿಂದ ಎದ್ದು ಹೊರಟಿದ್ದಾನೆ; ನರಮಾನವರೇ, ನೀವೆಲ್ಲಾ ಆತನ ಮುಂದೆ ಮೌನವಾಗಿರಿ."
		},
		"3": {
			"1": "<sup>1</sup> ಅನಂತರ ಮಹಾಯಾಜಕನಾದ ಯೆಹೋಶುವನು ಯೆಹೋವನ ದೂತನ ಮುಂದೆ ನಿಂತಿರುವುದನ್ನು ಯೆಹೋವನು ನನಗೆ ತೋರಿಸಿದನು. ಸೈತಾನನು ಯೆಹೋಶುವನಿಗೆ ಪ್ರತಿವಾದಿಯಾಗಿ ಅವನ ಬಲಗಡೆಯಲ್ಲಿ ನಿಂತಿದ್ದನು.",
			"2": "<sup>2</sup> ಆಗ ಯೆಹೋವನ ದೂತನು ಸೈತಾನನಿಗೆ, “ಯೆಹೋವನು ನಿನ್ನನ್ನು ಖಂಡಿಸಲಿ! ಹೌದು, ಯೆರೂಸಲೇಮನ್ನು ಆರಿಸಿಕೊಂಡಿರುವ ಯೆಹೋವನು ನಿನ್ನನ್ನು ಖಂಡಿಸಲಿ! ಅದು ಉರಿಯುವ ಬೆಂಕಿಯಿಂದ ಎಳೆದ ಕೊಳ್ಳಿಯಾಗಿದೆಯಲ್ಲಾ?” ಎಂದು ಹೇಳಿದನು.",
			"3": "<sup>3</sup> ಯೆಹೋಶುವನು ದೇವದೂತನ ಮುಂದೆ ಕೊಳೆಯಾದ ಬಟ್ಟೆಯನ್ನು ಧರಿಸಿ ನಿಂತಿರಲು,",
			"4": "<sup>4</sup> ದೇವದೂತನು ತನ್ನ ಸೇವಕರಿಗೆ, “ಇವನ ಕೊಳೆಬಟ್ಟೆಗಳನ್ನು ತೆಗೆದುಬಿಡಿರಿ” ಎಂದು ಅಪ್ಪಣೆ ಕೊಟ್ಟು ಅವನಿಗೆ, “ಇಗೋ, ನಿನ್ನ ದೋಷವನ್ನು ನಿನ್ನಿಂದ ತೊಲಗಿಸಿದ್ದೇನೆ, ನಿನಗೆ ಶ್ರೇಷ್ಠವಸ್ತ್ರವನ್ನು ತೊಡಿಸುವೆನು” ಎಂದು ಹೇಳಿದನು.",
			"5": "<sup>5</sup> “ಇವನ ತಲೆಗೆ ಶುಭ್ರವಾದ ಮುಂಡಾಸವನ್ನು ಸುತ್ತಿರಿ” ಎಂದು ಅವನು ಅಪ್ಪಣೆಕೊಡಲು ಅವರು ಅವನ ತಲೆಗೆ ಶುಭ್ರವಾದ ಮುಂಡಾಸವನ್ನು ಸುತ್ತಿ ಅವನಿಗೆ ವಸ್ತ್ರಗಳನ್ನು ತೊಡಿಸಿದರು, ಆಗ ಯೆಹೋವನ ದೂತನು ಎದ್ದು ನಿಂತನು.",
			"6": "<sup>6</sup> ಆಗ ಯೆಹೋವನ ದೂತನು ಯೆಹೋಶುವನಿಗೆ ಈ ಮಾತನ್ನು ಕೊಟ್ಟನು.",
			"7": "<sup>7</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ, “ನೀನು ನನ್ನ ಮಾರ್ಗಗಳಲ್ಲಿ ನಡೆದು, ನಾನು ನಿನಗೆ ವಹಿಸಿದ ಪಾರುಪತ್ಯವನ್ನು ನೆರವೇರಿಸಿದರೆ ನನ್ನ ಆಲಯದ ಮುಖ್ಯಾಧಿಕಾರಿಯಾಗಿ ನನ್ನ ಪ್ರಾಕಾರಗಳನ್ನು ನೋಡಿಕೊಳ್ಳುವ ಜವಾಬ್ದಾರಿಯನ್ನು ನೀಡುವೆ; ನನ್ನ ಸನ್ನಿಧಾನದೂತರ ನಡುವೆ ಪ್ರವೇಶಿಸುವ ಹಕ್ಕನ್ನು ನಿನಗೆ ಕೊಡುವೆನು.",
			"8": "<sup>8</sup> ಮಹಾಯಾಜಕನಾದ ಯೆಹೋಶುವನೇ, ನೀನೂ, ನಿನ್ನ ಮುಂದೆ ಕುಳಿತುಕೊಳ್ಳುವ ನಿನ್ನ ಸಂಗಡಿಗರೂ ಕೇಳಿರಿ. ಇಗೋ, ಮೊಳಕೆ ಎಂಬ ನನ್ನ ಸೇವಕನನ್ನು ಹೊರಡಿಸುತ್ತೆನೆಂಬುದಕ್ಕೆ ಇವರೇ ಮುಂಗುರುತು.",
			"9": "<sup>9</sup> ನಾನು ಯೆಹೋಶುವನ ಮುಂದೆ ಇಟ್ಟಿರುವ ಕಲ್ಲನ್ನು ನೋಡಿರಿ; ಆ ಒಂದೇ ಕಲ್ಲಿನಲ್ಲಿ ಏಳು ಕಣ್ಣುಳ್ಳಾತನ ದೃಷ್ಟಿಯು ಬಿದ್ದಿದೆ; ಇಗೋ ನಾನೇ ಅದರಲ್ಲಿ ಕೆತ್ತನೆ ಕೆತ್ತುವೆನು. ಇದು ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನ ನುಡಿ. ‘ಒಂದೇ ದಿನದೊಳಗೆ ಈ ದೇಶದ ಪಾಪವನ್ನು ಪರಿಹರಿಸುವೆನು.’",
			"10": "<sup>10</sup> “ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ, ‘ಆ ದಿನದಲ್ಲಿ ನೀವೆಲ್ಲರೂ ನಿಮ್ಮ ನಿಮ್ಮ ನೆರೆಹೊರೆಯವರನ್ನು ದ್ರಾಕ್ಷಾಲತೆ, ಅಂಜೂರಗಿಡ ಇವುಗಳ ನೆರಳಿನಲ್ಲಿ ನೆಮ್ಮದಿಯಿಂದ ವಿಶ್ರಮಿಸಲು ಕರೆಯುವಿರಿ.’”"
		},
		"4": {
			"1": "<sup>1</sup> ಅನಂತರ ವಿವರಿಸುವ ದೇವದೂತನು ಮತ್ತೆ ಬಂದು ನಿದ್ರೆಹತ್ತಿದವನ ಹಾಗಿದ್ದ ನನ್ನನ್ನು ಎಚ್ಚರಗೊಳಿಸಿ “ಏನು ನೋಡುತ್ತೀ?” ಎಂದು ಕೇಳಲು ನಾನು,",
			"2": "<sup>2</sup> “ಇಗೋ, ಏಳು ದೀಪಗಳುಳ್ಳ ಸುವರ್ಣಮಯವಾದ ಒಂದು ದೀಪಸ್ತಂಭವನ್ನು ನೋಡುತ್ತೇನೆ; ಅದರ ಮೇಲಿನ ದೀಪಗಳಿಗೆ ಏಳು ನಾಳಗಳಿವೆ; ಅದರ ಮೇಲ್ಗಡೆ ಎಣ್ಣೆಯ ಪಾತ್ರೆಯಿದೆ;",
			"3": "<sup>3</sup> ಆ ಪಾತ್ರೆಯ ಬಲಗಡೆಯಲ್ಲಿ ಒಂದು ಎಣ್ಣೆಯ ಮರ, ಎಡಗಡೆಯಲ್ಲಿ ಒಂದು ಎಣ್ಣೆಯ ಮರ, ಮತ್ತು ಎರಡು ಮರಗಳು ಅದರ ಪಕ್ಕಗಳಲ್ಲಿ ಇದೆ” ಎಂದು ಹೇಳಿದೆನು.",
			"4": "<sup>4</sup> ಪುನಃ ನಾನು ಪ್ರಸ್ತಾಪವೆತ್ತಿ ವಿವರಿಸುವ ದೇವದೂತನನ್ನು, “ಸ್ವಾಮೀ, ಇವು ಏನನ್ನು ಸೂಚಿಸುತದೆ?” ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ,",
			"5": "<sup>5</sup> ಆ ದೇವದೂತನು “ಇವು ಏನೆಂದು ನಿನಗೆ ತಿಳಿಯುವುದಿಲ್ಲವೋ?” ಎಂಬುದಾಗಿ ನನ್ನನ್ನು ಪ್ರಶ್ನೆಮಾಡಲು, “ನಾನು ಇಲ್ಲ ಸ್ವಾಮಿ” ಎಂದು ಅರಿಕೆ ಮಾಡಿದೆನು.",
			"6": "<sup>6</sup> ಆಗ ಅವನು ನನಗೆ ಪ್ರತ್ಯುತ್ತರವಾಗಿ ಹೀಗೆ ಹೇಳಿದನು, “ಪರಾಕ್ರಮದಿಂದಲ್ಲ, ಬಲದಿಂದಲ್ಲ. ನನ್ನ ಆತ್ಮದಿಂದಲೇ ಎಂಬುದು ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನ ನುಡಿ” ಎಂಬ ಈ ಮಾತುಗಳನ್ನು ಯೆಹೋವನು ಜೆರುಬ್ಬಾಬೆಲನಿಗೆ ದಯಪಾಲಿಸಿದ್ದಾನೆ.",
			"7": "<sup>7</sup> ದೊಡ್ಡ ಬೆಟ್ಟವೇ, “ನೀನು ಯಾರು? ಜೆರುಬ್ಬಾಬೆಲನ ಮುಂದೆ ನೆಲಸಮವಾಗುವಿ; ಅವನು ಕಲಶದ ಕಲ್ಲನ್ನು ಕೋಲಾಹಲದೊಡನೆ ಮೆರವಣಿಗೆ ಮಾಡುವನು; ಇದರ ಮೇಲೆ ದೇವರ ದಯೆಯಿರಲಿ” ಎಂಬ ಜನಘೋಷವಾಗುವುದು.",
			"8": "<sup>8</sup> ಅಲ್ಲದೆ ಯೆಹೋವನು ಈ ವಾಕ್ಯವನ್ನು ನನಗೆ ದಯಪಾಲಿಸಿದ್ದಾನೆ,",
			"9": "<sup>9</sup> “ಜೆರುಬ್ಬಾಬೆಲನ ಕೈಗಳು ಈ ಆಲಯದ ಅಸ್ತಿವಾರವನ್ನು ಹಾಕಿವೆ. ಆ ಕೈಗಳೇ ಇದನ್ನು ಪೂರೈಸುವವು. ಆಗ ನನ್ನನ್ನು ನಿಮ್ಮ ಬಳಿಗೆ ಕಳುಹಿಸಿದವನು ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೇ” ಎಂದು ನನಗೆ ಗೊತ್ತಾಗುವುದು.",
			"10": "<sup>10</sup> ಆಗ ದೂತನು “ಈ ದೀಪಗಳಿಂದ ಸೂಚಿತವಾದ ಯೆಹೋವನ ಏಳು ಕಣ್ಣುಗಳು ಭೂಲೋಕದ ಎಲ್ಲಾ ಕಡೆಗಳಲ್ಲಿಯೂ ಪ್ರಸರಿಸುತ್ತಾ ತೂಕದ ಗುಂಡು ಜೆರುಬ್ಬಾಬೆಲನ ಕೈಯಲ್ಲಿರುವುದನ್ನು ಸಂತೋಷದಿಂದ ನೋಡುತ್ತೇವೆ. ಹೀಗಿರುವಲ್ಲಿ ಅಲ್ಪ ಕಾರ್ಯಗಳ ದಿನವನ್ನು ಯಾರು ತಿರಸ್ಕರಿಸಿದವರು ಯಾರು?",
			"11": "<sup>11</sup> ಆಗ ನಾನು ದೇವದೂತನಿಗೆ, ದೀಪಸ್ತಂಭದ ಎಡಬಲದಲ್ಲಿರುವ ಈ ಎಣ್ಣೆಯ ಮರಗಳು ಏನು?” ಎಂದೆನು.",
			"12": "<sup>12</sup> ಮತ್ತೆ ನಾನು ಅವನಿಗೆ, ತಮ್ಮೊಳಗಿಂದ ಚಿನ್ನದಂಥ ಎಣ್ಣೆಯನ್ನು ಪಾತ್ರೆಯಲ್ಲಿ ತುಂಬಿಸುವ ಎರಡು ಬಂಗಾರದ ನಾಳಗಳಿವೆಯಲ್ಲಾ. ಆ ನಾಳಗಳ ಬಾಯಿಗೆ ಸೇರಿರುವ ಎರಡು ಎಣ್ಣೆಕಾಯಿಗುತ್ತಿಗಳು ಏನು? ಎಂದು ಕೇಳಿದೆ.",
			"13": "<sup>13</sup> ಅದಕ್ಕೆ ಆ ದೂತನು ನನಗೆ, “ಇವು ಏನೆಂದು ನಿನಗೆ ತಿಳಿಯುವುದಿಲ್ಲವೋ?” ಎಂದು ಕೇಳಿದಾಗ ನಾನು, “ಇಲ್ಲ ಸ್ವಾಮೀ” ಎಂದೆ.",
			"14": "<sup>14</sup> ಅದಕ್ಕೆ ಅವನು, “ಇವು ದೇವರಿಂದ ಆಯ್ಕೆಯಾಗಿ ಎಣ್ಣೆಯಿಂದ ಅಭಿಷೇಕಿಸಲ್ಪಟ್ಟ ಆ ಇಬ್ಬರು ವ್ಯಕ್ತಿಗಳನ್ನು ಸೂಚಿಸುತ್ತವೆ. ಸರ್ವ ಲೋಕದ ಒಡೆಯನ ಸನ್ನಿಧಿಯಲ್ಲಿರುವ ಸೇವಕರನ್ನು ಸೂಚಿಸುತ್ತವೆ” ಎಂದು ಹೇಳಿದನು."
		},
		"5": {
			"1": "<sup>1</sup> ಆಗ ನಾನು ಕಣ್ಣೆತ್ತಿ ನೋಡಲು ಇಗೋ, ಹಾರುತ್ತಿರುವ ಒಂದು ಸುರುಳಿಯು ಕಾಣಿಸಿತು.",
			"2": "<sup>2</sup> ಆಗ ದೂತನು ನನಗೆ, “ನಿನಗೆ ಏನು ಕಾಣಿಸುತ್ತದೆ?” ಎಂದಾಗ ನಾನು, ಹಾರುತ್ತಿರುವ ಒಂದು ಸುರುಳಿಯು ಕಾಣಿಸುತ್ತದೆ ಅದರ ಉದ್ದ ಇಪ್ಪತ್ತು ಮೊಳ, ಅಗಲ ಹತ್ತು ಮೊಳ ಎಂದು ಉತ್ತರಕೊಟ್ಟೆನು.",
			"3": "<sup>3</sup> ಆಗ ದೂತನು ನನಗೆ, “ದೇಶದ ಮೇಲೆಲ್ಲಾ ಹೊರಟು ಬಂದಿರುವ ಶಾಪವು ಇದೇ. ಕಳ್ಳತನ ಮಾಡುವ ಪ್ರತಿಯೊಬ್ಬರೂ ದೇಶದಿಂದ ಹೊರಗೆ ತೆಗೆದುಹಾಕಲ್ಪಡುವರು. ಇದಕ್ಕೆ ತಕ್ಕ ಹಾಗೆ ಪ್ರತಿಯೊಬ್ಬ ಸುಳ್ಳು ಸಾಕ್ಷಿಯೂ ದೇಶದಿಂದ ಹೊರಗೆ ತೆಗೆದುಹಾಕಲ್ಪಡುವನು.",
			"4": "<sup>4</sup> ನಾನು ಶಾಪವನ್ನು ಕಳುಹಿಸುತ್ತೇನೆ. ಸೇನಾಧೀಶ್ವರ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ, ‘ನನ್ನ ಅಪ್ಪಣೆಯಂತೆ ಅದು ಕಳ್ಳನ ಮನೆಯಲ್ಲಿಯೂ, ನನ್ನ ಹೆಸರೆತ್ತಿ ಸುಳ್ಳು ಸಾಕ್ಷಿಹೇಳುವವನ ಮನೆಯೊಳಗೆ ನುಗ್ಗಿ ಮತ್ತು ಅವನ ಮನೆಯಲ್ಲಿನ ಮರಗಳನ್ನೂ, ಅದರ ಕಲ್ಲುಗಳನ್ನೂ ನಾಶಮಾಡುವುದು’” ಎಂದು ಹೇಳಿದನು.",
			"5": "<sup>5</sup> ಆಗ ನನ್ನ ಸಂಗಡ ಮಾತನಾಡುತ್ತಿದ್ದ ದೂತನು ಬಂದು, “ಕಂಡು ಬರುತ್ತಿರುವ ಆ ವಸ್ತು ಏನೆಂದು ನೋಡು” ಎಂಬುದಾಗಿ ನನಗೆ ಹೇಳಿದನು.",
			"6": "<sup>6</sup> ನಾನು, “ಏನದು?” ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ ಅವನು, “ಇದು ಅಳೆಯುವ ಬುಟ್ಟಿ” ಎಂದನು. ಇದಲ್ಲದೆ ಅವನು, “ಇದೇ ಸಮಸ್ತ ಭೂಮಿಯಲ್ಲಿಯ ಜನರ ಅಧರ್ಮವು” ಎಂದನು.",
			"7": "<sup>7</sup> ಇಗೋ, ತಟ್ಟೆಯಾಕಾರದ ಸೀಸದ ಮುಚ್ಚಳವು ತೆರೆಯಲ್ಪಡಲಾಗಿ ಆಹಾ, ಒಬ್ಬ ಸ್ತ್ರಿ ಅದರಲ್ಲಿ ಕುಳಿತುಕೊಂಡಿದ್ದಳು.",
			"8": "<sup>8</sup> ಆಗ ದೂತನು ಹೇಳಿದ್ದೇನೆಂದರೆ, “ಇದು ದುಷ್ಟತ್ವವು” ಎಂದು ಹೇಳಿ ಅವಳನ್ನು ಬುಟ್ಟಿಯೊಳಗೆ ಪುನಃ ಅದುಮಿ ಆ ಬುಟ್ಟಿಯ ಮುಚ್ಚಳವನ್ನು ಮುಚ್ಚಿದನು.",
			"9": "<sup>9</sup> ನಾನು ಕಣ್ಣೆತ್ತಿ ನೋಡಲು, ಆಹಾ ಕೊಕ್ಕರೆಯ ರೆಕ್ಕೆಯಂತಿರುವ ರೆಕ್ಕೆಯುಳ್ಳ ಇಬ್ಬರು ಹೆಂಗಸರು ಕಂಡುಬಂದರು. ಅವರ ರೆಕ್ಕೆಗಳೊಳಗೆ ಗಾಳಿಯು ತುಂಬಿಕೊಂಡಿತ್ತು. ಅವರು ಅಳತೆಯ ಬುಟ್ಟಿಯನ್ನು ಎತ್ತಿಕೊಂಡು ಹಾರಿ ಹೋದರು.",
			"10": "<sup>10</sup> ಆಗ ನಾನು ನನ್ನ ಸಂಗಡ ಮಾತನಾಡಿದ ದೂತನಿಗೆ, “ಇವರು ಅಳತೆಯ ಬುಟ್ಟಿಯನ್ನು ಎಲ್ಲಿಗೆ ಹೊತ್ತುಕೊಂಡು ಹೋಗುತ್ತಾರೆ?” ಎಂದು ಹೇಳಿದೆನು.",
			"11": "<sup>11</sup> ದೂತನು ನನಗೆ, “ಶಿನಾರ್ ದೇಶದಲ್ಲಿ ಅವಳಿಗೆ ಮನೆಕಟ್ಟುವುದಕ್ಕಾಗಿ ಹೋಗುತ್ತಾರೆ. ಅಲ್ಲಿ ಅದು ಸ್ಥಾಪಿಸಲ್ಪಟ್ಟು ತನ್ನ ಸ್ವಂತ ಸ್ಥಾನದಲ್ಲಿ ಇರಿಸಲ್ಪಡುವುದು” ಎಂದನು."
		},
		"6": {
			"1": "<sup>1</sup> ನಂತರ ನಾನು ಕಣ್ಣೆತ್ತಿ ನೋಡಲು, ಇಗೋ, ಎರಡು ಬೆಟ್ಟಗಳ ನಡುವೆಯಿಂದ ಬರುತ್ತಿರುವ ನಾಲ್ಕು ರಥಗಳು ಕಾಣಿಸಿದವು. ಅ ಬೆಟ್ಟಗಳು ತಾಮ್ರದವುಗಳಾಗಿದ್ದವು.",
			"2": "<sup>2</sup> ಮೊದಲನೆಯ ರಥಕ್ಕೆ ಕೆಂಪು ಕುದುರೆಗಳು; ಎರಡನೆಯ ರಥಕ್ಕೆ ಕಪ್ಪು ಕುದುರೆಗಳು;",
			"3": "<sup>3</sup> ಮೂರನೆಯ ರಥಕ್ಕೆ ಬಿಳಿ ಕುದುರೆಗಳು; ನಾಲ್ಕನೆಯ ರಥಕ್ಕೆ ಮಚ್ಚೆ ಮಚ್ಚೆಯ ಬಲವಾದ ಕುದುರೆಗಳು ಕಟ್ಟಿದ್ದವು.",
			"4": "<sup>4</sup> ಆಗ ನನ್ನ ಸಂಗಡ ಮಾತನಾಡಿದ ದೇವದೂತನಿಗೆ, “ನನ್ನ ಸ್ವಾಮೀ ಇದೇನು?” ಎಂದೆನು.",
			"5": "<sup>5</sup> ಆ ದೇವದೂತನು ನನಗೆ, “ಇವು ಆಕಾಶದ ನಾಲ್ಕುಗಾಳಿಗಳು (ಆತ್ಮ); ಭೂಲೋಕದ ಒಡೆಯನ ಸನ್ನಿಧಾನದಲ್ಲಿ ನಿಂತಿದ್ದು ಅಲ್ಲಿಂದ ಹೊರಟು ಬರುತ್ತಾ ಇವೆ” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"6": "<sup>6</sup> ಅನಂತರ ಕಪ್ಪು ಕುದುರೆಗಳ ರಥ ಉತ್ತರದೇಶಕ್ಕೆ ಹೊರಟವು. ಬಿಳಿ ಕುದುರೆಗಳು ಅವುಗಳನ್ನು ಪಶ್ಚಿಮಕ್ಕೆ ಹಿಂಬಾಲಿಸಿದವು. ಮಚ್ಚೆ ಮಚ್ಚೆಯ ಕುದುರೆಗಳ ರಥ ದಕ್ಷಿಣ ದೇಶಕ್ಕೆ ಹೋದವು.",
			"7": "<sup>7</sup> ಕೆಂಪು ಕುದುರೆಗಳು ಬಂದು ಲೋಕದಲ್ಲಿ ಸಂಚರಿಸುವುದಕ್ಕೆ ಹೊರಡಬೇಕೆಂದು ತ್ವರೆಪಟ್ಟವು; ಆಗ ಅವನು, “ಹೊರಡಿರಿ, ಲೋಕದಲ್ಲಿ ಸಂಚರಿಸಿರಿ” ಎಂದು ಅಪ್ಪಣೆ ಮಾಡಲು ಅವು ಲೋಕದಲ್ಲಿ ಸಂಚರಿಸಿದವು.",
			"8": "<sup>8</sup> ಆ ಮೇಲೆ ಅವನು ನನಗೆ, “ಆಹಾ, ಉತ್ತರ ದೇಶಕ್ಕೆ ಹೋದವುಗಳು ಅಲ್ಲಿ ನನ್ನ ಕೋಪವನ್ನು ಶಾಂತಿಗೊಳಿಸಿವೆ” ಎಂದು ಕೂಗಿ ಹೇಳಿದನು.",
			"9": "<sup>9</sup> ಯೆಹೋವನು ಈ ವಾಕ್ಯವನ್ನು ನನಗೆ ದಯಪಾಲಿಸಿದನು,",
			"10": "<sup>10</sup> ಇನ್ನು ಸೆರೆಯಲ್ಲಿರುವವರೊಳಗೆ ಸೇರಿದವರಾದ ಹೆಲ್ದಾಯ, ತೋಬೀಯ ಮತ್ತು ಯೆದಾಯ ಎಂಬುವರ ಕೈಯಿಂದ ಬೆಳ್ಳಿ ಬಂಗಾರಗಳನ್ನು ತೆಗೆದುಕೋ; ಈ ದಿನವೇ ನೀನು ಹೋಗಿ ಬಾಬೆಲಿನಿಂದ ಬಂದು, ಇವರು ಇಳಿದುಕೊಂಡಿರುವ ಚೆಫನ್ಯನ ಮಗನಾದ ಯೋಷಿಯನ ಮನೆಗೆ ಹೋಗಿ,",
			"11": "<sup>11</sup> ಇವರಿಂದ ಬೆಳ್ಳಿಬಂಗಾರವನ್ನು ತೆಗೆದುಕೊಂಡು ಕಿರೀಟವನ್ನು ಮಾಡಿ ಯೆಹೋಚಾದಾಕನ ಮಗನೂ ಹಾಗೂ ಮಹಾಯಾಜಕನು ಆದ ಯೆಹೋಶುವನ ತಲೆಯ ಮೇಲೆ ಇಟ್ಟು,",
			"12": "<sup>12</sup> ಅವನಿಗೆ ಹೀಗೆ ಹೇಳು, “ಸೇನಾಧೀಶ್ವರ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ, ‘ಇಗೋ, ಮೊಳಿಕೆಯೆಂಬ ಪುರುಷನು! ಅವನು ಇದ್ದಲ್ಲಿಯೇ ವೃದ್ಧಿಯಾಗಿ, ಯೆಹೋವನ ಆಲಯವನ್ನು ಕಟ್ಟಿಸುವನು.",
			"13": "<sup>13</sup> ಹೌದು, ಅವನೇ ಯೆಹೋವನ ಆಲಯವನ್ನು ಕಟ್ಟಿಸಿ ರಾಜವೈಭವವನ್ನು ಹೊಂದಿಕೊಂಡು ತನ್ನ ಸಿಂಹಾಸನದಲ್ಲಿ ಆಸೀನನಾಗಿ ಆಳುವನು. ಮತ್ತು ಯಾಜಕರು ತನ್ನ ಸಿಂಹಾಸನದಲ್ಲಿ ಕುಳಿತಿರುವರು. ಅವರಿಬ್ಬರೂ ಸಮ್ಮತಿಸಮಾಧಾನದಿಂದ ಇರುವರು.",
			"14": "<sup>14</sup> “‘ಆ ಕಿರೀಟವು ಹೇಲೆಮ್, ತೋಬೀಯ, ಯೆದಾಯ, ಚೆಫನ್ಯನ ಮಗನಾದ ಹೇನ್ ಇವರ ಜ್ಞಾಪಕಾರ್ಥವಾಗಿ ಯೆಹೋವನ ಆಲಯದಲ್ಲಿ ಇಡಲಾಗಿದೆ.",
			"15": "<sup>15</sup> ದೂರದಲ್ಲಿರುವವರು ಬಂದು ಯೆಹೋವನ ಆಲಯವನ್ನು ಕಟ್ಟುವುದಕ್ಕೆ ಕೈಹಾಕುವರು; ಆಗ ನನ್ನನ್ನು ನಿಮ್ಮ ಬಳಿಗೆ ಕಳುಹಿಸಿದಾತನು ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೇ ಎಂದು ನಿಮಗೆ ದೃಡವಾಗುವುದು. ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಮಾತನ್ನು ಮನಃಪೂರ್ವಕವಾಗಿ ಕೇಳಿದರೆ ಇದೆಲ್ಲಾ ನೆರವೇರುವುದು.’”"
		},
		"7": {
			"1": "<sup>1</sup> ಅರಸನಾದ ದಾರ್ಯಾವೆಷನ ಆಳ್ವಿಕೆಯ ನಾಲ್ಕನೆಯ ವರುಷದ ಕಿಸ್ಲೇವ್ ಎಂಬ ಒಂಭತ್ತನೆಯ ತಿಂಗಳಿನ ನಾಲ್ಕನೆಯ ದಿನದಲ್ಲಿ ಯೆಹೋವನು ಜೆಕರ್ಯನಿಗೆ ಒಂದು ವಾಕ್ಯವನ್ನು ದಯಪಾಲಿಸಿದನು.",
			"2": "<sup>2</sup> ಅಷ್ಟರೊಳಗೆ ಬೇತೇಲಿನವರು ಸೆರೆಚರನನ್ನೂ, ರೆಗೆಮ್ ಮೆಲೆಕನನ್ನೂ, ಅವನ ಪರಿಜನರನ್ನೂ ಯೆಹೋವನ ಪ್ರಸನ್ನತೆಯ ಪ್ರಾರ್ಥನೆಗಾಗಿ ಕಳುಹಿಸಿದ್ದರು.",
			"3": "<sup>3</sup> ಆಗ, “ನಾವು ಬಹು ವರುಷಗಳಿಂದ ಮಾಡಿಕೊಂಡು ಬಂದಂತೆ ಮುಂದೆಯೂ ಐದನೆಯ ತಿಂಗಳಿನಲ್ಲಿ ಉಪವಾಸ ಮಾಡಿ ಅಳಬೇಕೋ? ಎಂದು ಪ್ರವಾದಿಗಳ ಮತ್ತು ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನ ಆಲಯದ ಯಾಜಕರ ಹತ್ತಿರ ವಿಚಾರಿಸಿಕೊಂಡು ಬನ್ನಿರಿ” ಎಂಬುದಾಗಿ ಅವರಿಗೆ ಅಪ್ಪಣೆಕೊಟ್ಟು ಕಳುಹಿಸಿದರು.",
			"4": "<sup>4</sup> ಹೀಗಿರಲು ಸೇನಾಧೀಶ್ವರ ಯೆಹೋವನು ಈ ವಾಕ್ಯವನ್ನು ನನಗೆ ದಯಪಾಲಿಸಿದನು.",
			"5": "<sup>5</sup> “ನೀನು ಯಾಜಕರಿಗೂ ದೇಶದ ಸಕಲ ಜನರಿಗೂ ಹೀಗೆ ನುಡಿ, ‘ನೀವು ಈ ಎಪ್ಪತ್ತು ವರುಷಗಳಿಂದಲೂ ಐದನೆಯ ಮತ್ತು ಏಳನೆಯ ತಿಂಗಳುಗಳಲ್ಲಿ ಉಪವಾಸಮಾಡಿ ಗೋಳಾಡುತ್ತಿದ್ದಾಗ, ಆ ನಿಮ್ಮ ಉಪವಾಸವು ನನಗಾಗಿಯೇ ಮಾಡಿದ್ದೋ?",
			"6": "<sup>6</sup> ನೀವು ಭೋಜನ ಮಾಡುವಾಗಲೂ, ಪಾನಮಾಡುವಾಗಲೂ ಆ ನಿಮ್ಮ ಭೋಜನಪಾನಗಳು ನಿಮ್ಮವುಗಳಾಗಿಯೇ ಇರುವುದಲ್ಲವೇ.",
			"7": "<sup>7</sup> ಯೆರೂಸಲೇಮು ಸುತ್ತಣ ಪಟ್ಟಣಗಳ ಸಮೇತ ಜನಭರಿತವಾಗಿಯೂ, ನೆಮ್ಮದಿಯಾಗಿಯೂ ಇದ್ದು, ದಕ್ಷಿಣ ಪ್ರಾಂತ್ಯದಲ್ಲಿಯೂ, ಇಳಕಲಿನ ಪ್ರದೇಶದಲ್ಲಿಯೂ ಜನರು ತುಂಬಿದ್ದಾಗ ಆ ಪೂರ್ವಕಾಲದ ಪ್ರವಾದಿಗಳ ಮೂಲಕ ಯೆಹೋವನು ಪ್ರಕಟಿಸಿದ ಮಾತುಗಳನ್ನು ನೀವು ಕೇಳಿಲ್ಲವೇ?’”",
			"8": "<sup>8</sup> ಅಲ್ಲದೆ ಯೆಹೋವನು ಜೆಕರ್ಯನಿಗೆ ಈ ವಾಕ್ಯವನ್ನು ದಯಪಾಲಿಸಿ,",
			"9": "<sup>9</sup> “ಸೇನಾಧೀಶ್ವರ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ, ‘ನಿಷ್ಠೆಯಿಂದ ನ್ಯಾಯತೀರಿಸಿರಿ,",
			"10": "<sup>10</sup> ಒಬ್ಬರಿಗೊಬ್ಬರು ಪ್ರೀತಿ, ಕರುಣೆಗಳನ್ನು ತೋರಿಸಿರಿ; ವಿಧವೆ, ಅನಾಥ, ವಿದೇಶಿ, ದರಿದ್ರರಿಗೂ ಅನ್ಯಾಯಮಾಡಬೇಡಿರಿ; ನಿಮ್ಮಲ್ಲಿ ಯಾವನೂ ತನ್ನ ಸಹೋದರನಿಗೆ ಕೇಡನ್ನು ಬಗೆಯದಿರಲಿ.’",
			"11": "<sup>11</sup> “ನಿಮ್ಮ ಪಿತೃಗಳಾದರೋ ಗಮನಿಸದೆ ಹೆಗಲುಕೊಡದೇ ಹೋದರು, ಕೇಳಬಾರದೆಂದು ಕಿವಿಮಂದಮಾಡಿಕೊಂಡರು.",
			"12": "<sup>12</sup> ಧರ್ಮೋಪದೇಶವನ್ನೂ, ಸೇನಾಧೀಶ್ವರ ಯೆಹೋವನು ಪೂರ್ವಕಾಲದ ಪ್ರವಾದಿಗಳ ಮೂಲಕ ತನ್ನ ಆತ್ಮನಿಂದ ಹೇಳಿಸಿದ ಮಾತುಗಳನ್ನೂ ಕೇಳಬಾರದೆಂದು ತಮ್ಮ ಹೃದಯಗಳನ್ನು ವಜ್ರದಷ್ಟು ಕಠಿಣಪಡಿಸಿಕೊಂಡರು. ಆದಕಾರಣ ಸೇನಾಧೀಶ್ವರ ಯೆಹೋವನ ಕೋಪಕ್ಕೆ ಗುರಿಯಾದರು.",
			"13": "<sup>13</sup> “ಆಗ ಸೇನಾಧೀಶ್ವರ ಯೆಹೋವನು ಇಂತೆಂದನು, ‘ನಾನು ಕೂಗಿದರೂ ಅವರು ಹೇಗೆ ಕೇಳಲಿಲ್ಲವೋ ಹಾಗೆ ಅವರು ಕೂಗಿದರೂ ನಾನು ಕೇಳುವುದಿಲ್ಲ.",
			"14": "<sup>14</sup> ಅವರು ನೋಡದ ಎಲ್ಲಾ ಜನಾಂಗಗಳ ಮಧ್ಯಕ್ಕೆ ಅವರನ್ನು ತೂರಿಬಿಡುವೆನು. ಯೆಹೋವನ ಈ ಮಾತಿನಂತೆ ಅವರು ಚದರಿದ ಮೇಲೆ ದೇಶವು ಹಾಳಾಯಿತು. ಅದರಲ್ಲಿ ಯಾರೂ ಹೋಗುತ್ತಿರಲಿಲ್ಲ, ಬರುತ್ತಿರಲಿಲ್ಲ; ಆ ರಮ್ಯ ದೇಶವನ್ನು ಹಾಳುಮಾಡಿದರು.’”"
		},
		"8": {
			"1": "<sup>1</sup> ಸೇನಾಧೀಶ್ವರ ಯೆಹೋವನು ಈ ವಾಕ್ಯವನ್ನು ನನಗೆ ದಯಪಾಲಿಸಿದನು.",
			"2": "<sup>2</sup> ಸೇನಾಧೀಶ್ವರ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ, “ಚೀಯೋನಿಗೆ ಅವಮಾನವಾಯಿತಲ್ಲಾ ಎಂದು ಬಹಳವಾಗಿ ಕುದಿಯುತ್ತೇನೆ. ಅತಿರೋಷಗೊಂಡು ಕುದಿಯುತ್ತೇನೆ.”",
			"3": "<sup>3</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ, “ನಾನು ಚೀಯೋನಿಗೆ ಹಿಂದಿರುಗಿದ್ದೇನೆ, ಯೆರೂಸಲೇಮಿನ ಮಧ್ಯದಲ್ಲಿ ವಾಸಿಸುವೆನು; ಆಗ ಯೆರೂಸಲೇಮ್ ಸತ್ಯದ ಪಟ್ಟಣವೆಂದು ಅನ್ನಿಸಿಕೊಳ್ಳುವುದು, ಸೇನಾಧೀಶ್ವರ ಯೆಹೋವನ ಪರ್ವತಕ್ಕೆ ಪರಿಶುದ್ಧ ಪರ್ವತವೆಂಬ ಹೆಸರು ಬರುವುದು.”",
			"4": "<sup>4</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ, “ಇನ್ನು ಮುಂದೆ ಯೆರೂಸಲೇಮಿನ ಚೌಕಗಳಲ್ಲಿ ಮುದುಕ ಮತ್ತು ಮುದುಕಿಯರು ಸೇರುವರು. ಅತಿ ವೃದ್ಧಾಪ್ಯದ ನಿಮಿತ್ತ ಪ್ರತಿಯೊಬ್ಬ ಮುದುಕನ ಕೈಯಲ್ಲಿ ಊರುಗೋಲಿರುವುದು.",
			"5": "<sup>5</sup> ಆ ಪಟ್ಟಣದ ಚೌಕಗಳಲ್ಲಿ ಆಟವಾಡುವ ಬಾಲಕ ಮತ್ತು ಬಾಲಕಿಯರು ತುಂಬಿಕೊಂಡಿರುವರು.”",
			"6": "<sup>6</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ, “ಆ ಕಾಲದ ಸ್ಥಿತಿಯು ಈ ಜನಶೇಷದವರ ದೃಷ್ಟಿಗೆ ಅತ್ಯಾಶ್ಚರ್ಯವಾದರೂ ನನ್ನ ದೃಷ್ಟಿಗೆ ಅತ್ಯಾಶ್ಚರ್ಯವೋ?” ಇದು ಸೇನಾಧೀಶ್ವರ ಯೆಹೋವನ ನುಡಿ.",
			"7": "<sup>7</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ, “ಇಗೋ, ನಾನು ನನ್ನ ಜನರನ್ನು ಪೂರ್ವದಿಕ್ಕಿನ ದೇಶದಿಂದಲೂ, ಪಶ್ಚಿಮದಿಕ್ಕಿನ ದೇಶದಿಂದಲೂ ಪಾರುಮಾಡಿ ಬರಗೊಳಿಸುವೆನು,",
			"8": "<sup>8</sup> ಅವರು ಯೆರೂಸಲೇಮಿನೊಳಗೆ ವಾಸಿಸುವರು; ಸತ್ಯಸಂಧತೆಯಿಂದಲೂ ಮತ್ತು ಸದ್ಧರ್ಮದಿಂದಲೂ ಅವರು ನನಗೆ ಪ್ರಜೆಯಾಗಿರುವರು. ಹಾಗೆಯೇ ನಾನು ಅವರಿಗೆ ದೇವರಾಗಿರುವೆನು.”",
			"9": "<sup>9</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ, “ದೇವಾಲಯವನ್ನು ಕಟ್ಟುವ ಉದ್ದೇಶದಿಂದ ಸೇನಾಧೀಶ್ವರ ಯೆಹೋವನ ಆ ಮಂದಿರದ ಅಸ್ತಿವಾರವನ್ನು ಹಾಕಿದ ಕಾಲದಲ್ಲಿಯೂ ಇದ್ದ ಪ್ರವಾದಿಗಳ ಬಾಯಿಂದ ಈ ಮಾತುಗಳನ್ನು ಈಗ ಕೇಳುವವರೇ, ನಿಮ್ಮ ಕೈಗಳು ಬಲಗೊಳ್ಳಲಿ!",
			"10": "<sup>10</sup> ಆ ಕಾಲಕ್ಕೆ ಮುಂಚೆ ಮನುಷ್ಯನಿಗಾಗಲಿ, ಪಶುವಿಗಾಗಲಿ ದುಡಿತದಿಂದ ಏನೂ ಜೀವನವಾಗುತ್ತಿರಲಿಲ್ಲ; ಹೋಗಿ ಬರುವವರಿಗೆ ಶತ್ರುಗಳ ದೆಸೆಯಿಂದ ಭಯವಿಲ್ಲದೆ ಇರಲಿಲ್ಲ; ಒಬ್ಬರನೊಬ್ಬರು ಎದುರಿಸುವಂತೆ ಎಲ್ಲರನ್ನೂ ಪ್ರೇರೇಪಿಸುತ್ತಿದ್ದರು.",
			"11": "<sup>11</sup> ಇಂದಿನಿಂದಲೋ ಈ ಜನಶೇಷದವರ ವಿಷಯದಲ್ಲಿ ನಾನು ಪೂರ್ವಕಾಲದಲ್ಲಿ ಇದ್ದಂತೆ ಇರುವುದಿಲ್ಲ” ಇದು ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನ ನುಡಿ.",
			"12": "<sup>12</sup> “ನೆಮ್ಮದಿಯ ಬೆಳೆಯಾಗುವುದು; ದ್ರಾಕ್ಷಾಲತೆಯು ಹಣ್ಣುಬಿಡುವುದು, ಭೂಮಿಯು ಧಾನ್ಯವನ್ನೀಯುವುದು, ಆಕಾಶವು ಇಬ್ಬನಿಯನ್ನು ಸುರಿಸುವುದು; ಈ ಜನಶೇಷದವರು ಇವುಗಳನ್ನೆಲ್ಲಾ ಅನುಭವಿಸುವಂತೆ ಅನುಗ್ರಹಿಸುವೆನು.",
			"13": "<sup>13</sup> ಯೆಹೂದ ಕುಲವೇ, ಇಸ್ರಾಯೇಲ್ ವಂಶವೇ, ನಿಮ್ಮ ಹೆಸರು ಹೇಗೆ ಜನಾಂಗಗಳಲ್ಲಿ ಶಾಪದ ಮಾತಾಗಿ ಸಲ್ಲುತ್ತಿತ್ತೋ, ಹಾಗೆಯೇ ನಾನು ನಿಮ್ಮನ್ನು ರಕ್ಷಿಸಿ ಆಶೀರ್ವದಿಸುವೆನು; ಹೆದರಬೇಡಿರಿ, ನಿಮ್ಮ ಕೈಗಳು ಬಲಗೊಳ್ಳಲಿ!”",
			"14": "<sup>14</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ, “ನಿಮ್ಮ ಪಿತೃಗಳು ನನಗೆ ಸಿಟ್ಟೆಬ್ಬಿಸಿದಾಗ ನಿಮಗೆ ಕೇಡುಮಾಡಬೇಕೆಂದು ನಾನು ಕರುಣೆಯಿಲ್ಲದೆ ಸಂಕಲ್ಪಸಿದಂತೆ",
			"15": "<sup>15</sup> ಈ ಕಾಲದಲ್ಲಿಯೂ ಯೆರೂಸಲೇಮಿಗೂ, ಯೆಹೂದ ಕುಲಕ್ಕೂ ಮೇಲುಮಾಡಬೇಕೆಂದು ಹೊಸ ಸಂಕಲ್ಪ ಮಾಡಿಕೊಂಡಿದ್ದೇನೆ; ಹೆದರಬೇಡಿರಿ. ಇದು ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ನುಡಿ.",
			"16": "<sup>16</sup> “ನೀವು ಮಾಡಬೇಕಾದ ಕಾರ್ಯಗಳು ಇವೇ. ಪ್ರತಿಯೊಬ್ಬನೂ ತನ್ನ ನೆರೆಯವನ ಸಂಗಡ ನಿಜವನ್ನೇ ಆಡಲಿ; ನಿಮ್ಮ ಚಾವಡಿಗಳಲ್ಲಿ ಸತ್ಯವನ್ನೂ, ಸಮಾಧಾನವಾದ ನ್ಯಾಯವನ್ನೂ ಸ್ಥಾಪಿಸಿರಿ;",
			"17": "<sup>17</sup> ನಿಮ್ಮಲ್ಲಿ ಯಾವನೂ ತನ್ನ ಹೃದಯದಲ್ಲಿ ನೆರೆಯವನಿಗೆ ಕೇಡನ್ನು ಬಗೆಯದಿರಲಿ; ಸುಳ್ಳು ಸಾಕ್ಷಿಗೆ ಎಂದೂ ಸಂತೋಷಪಡಬೇಡಿರಿ; ನಾನು ಇವುಗಳನ್ನೆಲ್ಲಾ ದ್ವೇಷಿಸುವವನಾಗಿದ್ದೇನೆ; ಇದು ಯೆಹೋವನ ನುಡಿ.”",
			"18": "<sup>18</sup> “ಸೇನಾಧೀಶ್ವರ ಯೆಹೋವನು ಈ ವಾಕ್ಯವನ್ನು ನನಗೆ ದಯಪಾಲಿಸಿದನು.",
			"19": "<sup>19</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ, ‘ನಾಲ್ಕನೆಯ ತಿಂಗಳಿನ ಉಪವಾಸ, ಐದನೆಯ ತಿಂಗಳಿನ ಉಪವಾಸ, ಏಳನೆಯ ತಿಂಗಳಿನ ಉಪವಾಸ, ಹತ್ತನೆಯ ತಿಂಗಳಿನ ಉಪವಾಸ ಇವು ಯೆಹೂದ ವಂಶಕ್ಕೆ ವಿಶೇಷವಾದ ಹಬ್ಬಗಳಾಗಿ ಹರ್ಷೋಲ್ಲಾಸಗಳನ್ನು ಉಂಟುಮಾಡುವವು; ಹೀಗಿರಲು ಸತ್ಯವನ್ನೂ, ಸಮಾಧಾನವನ್ನೂ ಪ್ರೀತಿಸಿರಿ.’”",
			"20": "<sup>20</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ, “ಇನ್ನು ಮುಂದೆ ಜನಾಂಗಗಳೂ, ದೊಡ್ಡ ಪಟ್ಟಣಗಳ ನಿವಾಸಿಗಳೂ ಬರುವರು;",
			"21": "<sup>21</sup> ಒಂದು ಊರಿನವರು ಇನ್ನೊಂದು ಊರಿಗೆ ಹೋಗಿ, ‘ಯೆಹೋವನ ಪ್ರಸನ್ನತೆಯನ್ನು ಬೇಡುವುದಕ್ಕೂ, ಸೇನಾಧೀಶ್ವರ ಯೆಹೋವನನ್ನು ಆಶ್ರಯಿಸುವುದಕ್ಕೂ ಹೋಗೋಣ ಬನ್ನಿರಿ, ನಾವೂ ಹೋಗುತ್ತೇವೆ’ ಎಂದು ಹೇಳುವರು.",
			"22": "<sup>22</sup> ಹೀಗೆ ಬಹು ದೇಶಗಳವರೂ, ಬಲವಾದ ಜನಾಂಗಗಳವರೂ ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಸೇನಾಧೀಶ್ವರ ಯೆಹೋವನನ್ನು ಆಶ್ರಯಿಸುವುದಕ್ಕೂ ಯೆಹೋವನ ಪ್ರಸನ್ನತೆಯನ್ನು ಬೇಡುವುದಕ್ಕೂ ಬರುವರು.”",
			"23": "<sup>23</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ, “ಆ ಕಾಲದಲ್ಲಿ ಜನಾಂಗಗಳ ವಿವಿಧ ಬಾಷೆಗಳವರಾದ ಹತ್ತು ಜನರು ಯೆಹೂದ್ಯನೊಬ್ಬನ ಸೆರಗನ್ನು ಹಿಡಿದುಕೊಂಡು, ‘ನಾವು ನಿಮ್ಮೊಂದಿಗೆ ಬರುವೆವು, ಏಕೆಂದರೆ ದೇವರು ನಿಮ್ಮ ಸಂಗಡ ಇದ್ದಾನೆಂಬ ಸುದ್ದಿಯು ನಮ್ಮ ಕಿವಿಗೆ ಬಿದ್ದಿದೆ’” ಎಂದು ಹೇಳುವರು."
		},
		"9": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ನುಡಿದ ದೈವೋಕ್ತಿಯು ಹದ್ರಾಕ್ ದೇಶದ ಮೇಲೆ ಬಿದ್ದಿದೆ. ದಮಸ್ಕವೇ ಅದಕ್ಕೆ ಈಡು; ಯೆಹೋವನು ನರವಂಶದ ಮೇಲೆ ಕಣ್ಣಿಟ್ಟಿದ್ದಾನೆ; ಹೌದು, ಇಸ್ರಾಯೇಲಿನ ಸಕಲ ಕುಲಗಳಲ್ಲಿಯೂ,",
			"2": "<sup>2</sup> ದಮಸ್ಕದ ಪಕ್ಕದಲ್ಲಿನ ಹಮಾತಿನಲ್ಲಿಯೂ ಬಹಳ ಜಾಣರು ಎಂದು ಕೊಚ್ಚಿಕೊಂಡಿರುವ ತೂರ್, ಚೀದೋನ್ ಪಟ್ಟಣಗಳಲ್ಲಿಯೂ ಕಣ್ಣಿಟ್ಟಿದ್ದಾನೆ.",
			"3": "<sup>3</sup> ತೂರ್ ಪಟ್ಟಣವು ಕೋಟೆಯನ್ನು ಕಟ್ಟಿಕೊಂಡು ಬೆಳ್ಳಿಯನ್ನು ದೂಳಿನಂತೆಯೂ, ಬಂಗಾರವನ್ನು ಬೀದಿಯ ಬದಿಯ ಕಸದ ರಾಶಿಯಂತೆ ಮಾಡಿಕೊಂಡಿದೆ.",
			"4": "<sup>4</sup> ಆಹಾ, ಕರ್ತನು ಅದರ ಆಸ್ತಿಯನ್ನು ಆಕ್ರಮಿಸಿ ಪೌಳಿಗೋಡೆಯನ್ನು ಸಮುದ್ರದೊಳಗೆ ಹೊಡೆದುಹಾಕುವನು; ಪಟ್ಟಣವು ಬೆಂಕಿಗೆ ತುತ್ತಾಗುವುದು.",
			"5": "<sup>5</sup> ಅಷ್ಕೆಲೋನು ಇದನ್ನು ನೋಡಿ ಹೆದರುವುದು; ಗಾಜವು ಸಹ ಅತೀ ಸಂಕಟಪಡುವುದು; ಎಕ್ರೋನು ನಿರೀಕ್ಷೆಗೆಟ್ಟು ಬಹು ವ್ಯಥೆಗೆ ಒಳಗಾಗುವುದು; ಗಾಜಕ್ಕೆ ರಾಜನೇ ಇಲ್ಲವಾಗುವನು; ಅಷ್ಕೆಲೋನು ನಿರ್ಜನವಾಗುವುದು;",
			"6": "<sup>6</sup> ಅಷ್ಡೋದಿನಲ್ಲಿ ಮಿಶ್ರಜಾತಿಯವರು ವಾಸಮಾಡುವರು; ಹೀಗೆ ಫಿಲಿಷ್ಟಿಯದ ಗರ್ವವನ್ನು ಭಂಗಪಡಿಸುವೆನು.",
			"7": "<sup>7</sup> ಅವರು ಸವಿಯುವ ರಕ್ತವನ್ನು ಬಾಯೊಳಗಿಂದ ತೆಗೆದುಬಿಡುವೆನು; ಅವರು ಕಚ್ಚುವ ಅಸಹ್ಯಪದಾರ್ಥಗಳನ್ನು ಹಲ್ಲುಗಳ ಮಧ್ಯದಿಂದ ಕಿತ್ತುಹಾಕುವೆನು; ಅವರೂ ಇಸ್ರಾಯೇಲಿನ ದೇವರಿಗೆ ಮೀಸಲಾದ ಜನರಾಗಿ ಉಳಿಯುವರು; ಯೆಹೂದದಲ್ಲಿ ಕುಲಪಾಲಕನಂತಿರುವರು; ಎಕ್ರೋನಿನವರು ಯೆಬೂಸಿಯರ ಹಾಗಿರುವರು.",
			"8": "<sup>8</sup> ಯಾರೂ ಹಾದುಹೋಗದಂತೆ ನನ್ನ ಆಲಯದ ಸುತ್ತಲು ಪಾಳೆಯನ್ನು ಹಾಕಿ ಕಾವಲಾಗಿರುವೆನು; ಇನ್ನು ಮುಂದೆ ಯಾವ ಬಾಧಕನೂ ನನ್ನ ಜನರ ಮೇಲೆ ದಾಳಿಮಾಡುವುದಿಲ್ಲ; ಏಕೆಂದರೆ ಈಗ ನಾನು ಕಣ್ಣಿಟ್ಟು ನೋಡುತ್ತಿದ್ದೇನೆ.",
			"9": "<sup>9</sup> ಚೀಯೋನ್ ನಗರಿಯೇ, ಬಹು ಸಂತೋಷಪಡು; ಯೆರೂಸಲೇಮ್ ಪುರಿಯೇ, ಹರ್ಷಧ್ವನಿಗೈ! ನೋಡು, ನಿನ್ನ ಅರಸನು ನಿನ್ನ ಬಳಿಗೆ ಬರುತ್ತಾನೆ; ಆತನು ನ್ಯಾಯವಂತನೂ, ರಕ್ಷಿಸುವಾತನೂ, ಶಾಂತಗುಣವುಳ್ಳವನಾಗಿಯೂ ಕತ್ತೆಯನ್ನು, ಹೌದು, ಪ್ರಾಯದ ಕತ್ತೆ ಮರಿಯನ್ನು ಹತ್ತಿದವನಾಗಿಯೂ ಬರುತ್ತಾನೆ.",
			"10": "<sup>10</sup> ನಾನು ಎಫ್ರಾಯೀಮಿನ ರಥಬಲವನ್ನೂ, ಯೆರೂಸಲೇಮಿನ ಅಶ್ವಬಲವನ್ನೂ ನಿಶ್ಯೇಷಮಾಡುವೆನು; ಯುದ್ಧದ ಬಿಲ್ಲು ಇಲ್ಲವಾಗುವುದು; ಆತನು ಕೊಡುವ ಅಪ್ಪಣೆಯು ಜನಾಂಗಗಳಿಗೆ ಸಮಾಧಾನಕರವಾಗಿರುವುದು; ಆತನ ಆಳ್ವಿಕೆಯು ಸಮುದ್ರದಿಂದ ಸಮುದ್ರದವರೆಗೂ ಯೂಫ್ರೇಟೀಸ್ ನದಿಯಿಂದ ಭೂಮಿಯ ಕಟ್ಟಕಡೆಯವರೆಗೂ ಹರಡಿಕೊಂಡಿರುವುದು.",
			"11": "<sup>11</sup> ನನ್ನ ಜನರೇ, ನೀವು ಒಡಂಬಡಿಕೆ ಮಾಡಿಕೊಂಡಾಗ ಸುರಿಸಿದ ರಕ್ತವನ್ನು ನಾನು ಸ್ಮರಿಸಿ ಸೆರೆಯಾಗಿರುವ ನಿನ್ನವರನ್ನು ನೀರಿಲ್ಲದ ಆ ಬಾವಿಯೊಳಗಿಂದ ಬರಮಾಡುವೆನು.",
			"12": "<sup>12</sup> ನಿರೀಕ್ಷೆ ಹೊಂದಿದ ಸೆರೆಯವರೇ, ನಿಮ್ಮ ದುರ್ಗಕ್ಕೆ ಹಿಂದಿರುಗಿರಿ; ನಿಮಗೆ ಎರಡರಷ್ಟು ಸುಖವನ್ನು ದಯಪಾಲಿಸುವೆನೆಂದು ಈಗಲೂ ಪ್ರಕಟಿಸುತ್ತೇನೆ.",
			"13": "<sup>13</sup> ಯೆಹೂದವೆಂಬ ಬಿಲ್ಲನ್ನು ಬೊಗ್ಗಿಸಿಕೊಂಡಿದ್ದೇನೆ. ಅದರಲ್ಲಿ ಎಫ್ರಾಯೀಮ್ ಎಂಬ ಬಾಣವನ್ನು ಹೂಡಿದ್ದೇನೆ; ಚೀಯೋನೇ, ನಾನು ನಿನ್ನ ಸಂತಾನವನ್ನು ಗ್ರೀಕ್ ಸಂತಾನಕ್ಕೆ ವಿರುದ್ಧವಾಗಿ ಎಬ್ಬಿಸಿ ನಿನ್ನನ್ನು ಶೂರನ ಕತ್ತಿಯನ್ನಾಗಿ ಮಾಡುವೆನು.",
			"14": "<sup>14</sup> ಯೆಹೋವನು ಸ್ವಜನರಿಗಾಗಿ ಅವರ ಮೇಲೆ ಕಾಣಿಸಿಕೊಳ್ಳುವನು; ಆತನ ಬಾಣವು ಸಿಡಿಲಿನಂತೆ ಹಾರುವುದು; ಕರ್ತನಾದ ಯೆಹೋವನು ತುತೂರಿಯನ್ನು ಊದಿ ದಕ್ಷಿಣ ಪ್ರಾಂತ್ಯದ ಬಿರುಗಾಳಿಗಳೊಡನೆ ನುಗ್ಗುವನು.",
			"15": "<sup>15</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ತನ್ನ ಜನರನ್ನು ಕಾಪಾಡುವನು; ಅವರು ಶತ್ರುಗಳನ್ನು ನುಂಗಿಬಿಡುವರು, ಕವಣೆಯ ಕಲ್ಲುಗಳನ್ನು ತುಳಿದುಹಾಕುವರು; ರಕ್ತವನ್ನು ಕುಡಿದು ಅಮಲೇರಿದವರಾಗಿ ಭೋರ್ಗರೆಯುವರು; ಬೋಗುಣಿಗಳಂತೆಯೂ, ಯಜ್ಞವೇದಿಯ ಮೂಲೆಗಳ ಹಾಗೂ ರಕ್ತಪೂರ್ಣರಾಗಿರುವರು.",
			"16": "<sup>16</sup> ಆ ದಿನದಲ್ಲಿ ಅವರ ದೇವರಾದ ಯೆಹೋವನು ಅವರನ್ನು ತನ್ನ ಹಿಂಡಾಗಿರುವ ಜನರೆಂದು ರಕ್ಷಿಸುವನು; ಅವರು ಕಿರೀಟದಲ್ಲಿನ ರತ್ನಗಳಂತೆ ತಮ್ಮ ದೇಶದಲ್ಲಿ ಥಳಥಳಿಸುವರು.",
			"17": "<sup>17</sup> ಆಹಾ, ಅವರ ಸೌಖ್ಯವೆಷ್ಟು, ಅವರ ಸೌಂದರ್ಯವೆಷ್ಟು! ಧಾನ್ಯವು ಯುವಕರನ್ನು, ದ್ರಾಕ್ಷಾರಸವು ಯುವತಿಯರನ್ನು ಪುಷ್ಟಿಗೊಳಿಸುವುದು."
		},
		"10": {
			"1": "<sup>1</sup> ಮಳೆಯಾಗುವಂತೆ ಹಿಂಗಾರಿನಲ್ಲಿ ಯೆಹೋವನನ್ನು ಬೇಡಿಕೊಳ್ಳಿರಿ; ಯೆಹೋವನೇ ಮಿಂಚುಗಳನ್ನು ಉಂಟುಮಾಡುತ್ತಾನೆ, ಮನುಷ್ಯರಿಗೆ ಸಮೃದ್ಧಿಯಾದ ಮಳೆಯನ್ನು ದಯಪಾಲಿಸುತ್ತಾನೆ, ಎಲ್ಲರಿಗೂ ಹೊಲದ ಪೈರನ್ನು ಅನುಗ್ರಹಿಸುತ್ತಾನೆ.",
			"2": "<sup>2</sup> ವಿಗ್ರಹಗಳು ನುಡಿಯುವುದು ಸುಳ್ಳು; ಕಣಿಹೇಳುವವರು ದರ್ಶನವನ್ನು ಕಾಣುವುದು ಸುಳ್ಳು, ತಿಳಿಸುವ ಕನಸುಗಳು ಮೋಸವಾದವುಗಳು, ಹೇಳುವ ಸಮಾಧಾನವು ವ್ಯರ್ಥ. ಆದುದರಿಂದ ನನ್ನ ಜನರು ದಿಕ್ಕಾಪಾಲಾಗಿದ್ದಾರೆ; ಕುರುಬನು ಇಲ್ಲದ ಕಾರಣ ಬಾಧೆಗೆ ಒಳಗಾಗಿದ್ದಾರೆ.",
			"3": "<sup>3</sup> ನನ್ನ ಕೋಪವು ಕುರುಬರ ಮೇಲೆ ಧಗಧಗಿಸುತ್ತದೆ. ನಾನು ಹೋತಗಳನ್ನು ದಂಡಿಸುವೆನು; ಸೇನಾಧೀಶ್ವರ ಯೆಹೋವನು ತನ್ನ ಮಂದೆಯಾದ ಯೆಹೂದ ವಂಶವನ್ನು ಪರಾಂಬರಿಸಿ ಘನವಾದ ಯುದ್ಧಾಶ್ವವನ್ನಾಗಿ ಮಾಡಿಕೊಳ್ಳುವನು.",
			"4": "<sup>4</sup> ಆ ವಂಶದಿಂದ ಮೂಲೆಗಲ್ಲು, ಆ ವಂಶದಿಂದ ಮೊಳೆ, ಆ ವಂಶದಿಂದ ಯುದ್ಧದ ಬಿಲ್ಲು, ಅಂತು ಆ ವಂಶದೊಳಗಿಂದ ಸಕಲ ಅಧಿಕಾರಿಗಳು ಉಂಟಾಗುವರು.",
			"5": "<sup>5</sup> ಇವರು ವೀರರಾಗಿ ರಣರಂಗದೊಳಗೆ ಶತ್ರುಗಳನ್ನು ಬೀದಿಗಳ ಕೆಸರಿನಲ್ಲಿ ತುಳಿದುಬಿಡುವರು; ಯೆಹೋವನು ನಮ್ಮ ಸಂಗಡ ಇದ್ದಾನೆಂದು ಯುದ್ಧಮಾಡುವರು; ಎದುರಿಸುವ ಸವಾರರು ಭಂಗಪಡುವರು.",
			"6": "<sup>6</sup> ನಾನು ಯೆಹೂದ ವಂಶವನ್ನು ಬಲಗೊಳಿಸಿ ಯೋಸೇಫ ವಂಶವನ್ನು ಉದ್ಧರಿಸುವೆನು; ನನ್ನ ಕನಿಕರವು ಅವರ ಮೇಲೆ ಇರುವುದರಿಂದ ಅವರನ್ನು ಹಿಂದಕ್ಕೆ ಬರಮಾಡುವೆನು; ಆಗ ನಾನು ಅವರನ್ನು ತಳ್ಳಿಬಿಟ್ಟಿದ್ದು ಇಲ್ಲದಂತಾಗುವುದು; ನಾನು ಅವರ ದೇವರಾದ ಯೆಹೋವನಾಗಿದ್ದೇನಲ್ಲಾ, ಅವರ ಮೊರೆಯನ್ನು ಲಾಲಿಸಿ ಸದುತ್ತರವನ್ನು ನೀಡುವೆನು.",
			"7": "<sup>7</sup> ಎಫ್ರಾಯೀಮ್ಯರು ಶೂರರಂತಿರುವರು, ದ್ರಾಕ್ಷಾರಸಕುಡಿದಂತೆ ಅವರ ಮನಸ್ಸು ಉತ್ಸಾಹಗೊಳ್ಳುವುದು; ಅವರ ಸಂತಾನದವರು ಇದನ್ನು ನೋಡಿ ಸಂತೋಷಪಡುವರು, ಅವರ ಹೃದಯವು ಯೆಹೋವನಲ್ಲಿ ಆನಂದಿಸುವುದು.",
			"8": "<sup>8</sup> ನಾನು ಅವರನ್ನು ಸಿಳ್ಳುಹಾಕಿ ಕರೆದು ಕೂಡಿಸುವೆನು. ಅವರನ್ನು ವಿಮೋಚಿಸಿದೆನಲ್ಲಾ; ಅವರು ಹಿಂದೆ ವೃದ್ಧಿಯಾದಂತೆ ಮುಂದೆಯೂ ವೃದ್ಧಿಯಾಗುವರು.",
			"9": "<sup>9</sup> ನಾನು ಅವರನ್ನು ಜನಾಂಗಗಳೊಳಗೆ ಚೆಲ್ಲಿದರೂ ಅವರು ದೂರದೇಶಗಳಲ್ಲಿ ನನ್ನನ್ನು ಸ್ಮರಿಸಿಕೊಂಡು ಸಂತಾನಸಮೇತವಾಗಿ ಬದುಕಿ ಬಾಳಿ ಹಿಂದಿರುಗಿ ಬರುವರು.",
			"10": "<sup>10</sup> ನಾನು ಅವರನ್ನು ಐಗುಪ್ತ ದೇಶದೊಳಗಿಂದ ಹಿಂದಕ್ಕೆ ಕರೆದುತರುವೆನು; ಅಶ್ಯೂರದಿಂದ ಕೂಡಿಸುವೆನು; ಗಿಲ್ಯಾದ್, ಲೆಬನೋನುಗಳ ಪ್ರಾಂತ್ಯಕ್ಕೆ ಬರಮಾಡುವೆನು; ಅವರಿಗೆ ಸಾಕಾಗುವಷ್ಟು ಸ್ಥಳ ಸಿಕ್ಕದು.",
			"11": "<sup>11</sup> ಅವರು ಕಷ್ಟವೆಂಬ ಕಡಲನ್ನು ದಾಟಿ ಬರುವರು, ಅಲ್ಲಕಲ್ಲೋಲವಾದ ಸಮುದ್ರವನ್ನು ಭೇದಿಸಿಬಿಡುವರು, ನೈಲ್ ನದಿಯೆಲ್ಲಾ ತಳದ ತನಕ ಒಣಗುವುದು. ಅಶ್ಯೂರದ ಗರ್ವವು ತಗ್ಗಿಸಲ್ಪಡುವುದು, ಐಗುಪ್ತದ ರಾಜದಂಡವು ತಪ್ಪಿಹೋಗುವುದು.",
			"12": "<sup>12</sup> ಅವರು ಯೆಹೋವನಲ್ಲಿ ಬಲಗೊಳ್ಳುವರು, ಆತನ ಹೆಸರಿನಲ್ಲಿ ನಡೆದುಕೊಳ್ಳುವರು; ಇದು ಯೆಹೋವನ ನುಡಿ."
		},
		"11": {
			"1": "<sup>1</sup> ಲೆಬನೋನೇ, ನಿನ್ನ ಬಾಗಿಲುಗಳನ್ನು ತೆರೆ, ಬೆಂಕಿಯು ನಿನ್ನ ದೇವದಾರುಗಳನ್ನು ನುಂಗಲಿ!",
			"2": "<sup>2</sup> ತುರಾಯಿ ಮರವೇ, ಗೋಳಾಡು! ದೇವದಾರು ಬಿದ್ದಿದೆ, ಶ್ರೇಷ್ಠವೃಕ್ಷಗಳು ನಾಶವಾಗಿವೆ. ಬಾಷಾನಿನ ಅಲ್ಲೋನ್ ಮರಗಳೇ ಕಿರುಚಿರಿ! ನುಗ್ಗಲಾಗದ ದಟ್ಟವಾದ ವನವು ಉರುಳಿದೆ.",
			"3": "<sup>3</sup> ಆಹಾ, ಕುರುಬರು ಗೋಳಾಡುತ್ತಾರೆ! ಅವರ ಅತಿಶಯದ ಕಾವಲು ಹಾಳಾಗಿದೆ. ಇಗೋ, ಪ್ರಾಯದ ಸಿಂಹಗಳು ಗರ್ಜಿಸುತ್ತವೆ! ಯೊರ್ದನಿನ ದಟ್ಟ ಅಡವಿಯು ಹಾಳಾಗಿದೆ.",
			"4": "<sup>4</sup> ನನ್ನ ದೇವರಾದ ಯೆಹೋವನು ಹೀಗೆ ಅಪ್ಪಣೆಕೊಟ್ಟನು, “ಕೊಯ್ಗುರಿಗಳ ಮಂದೆಯನ್ನು ಕಾಯಿ;",
			"5": "<sup>5</sup> ಕೊಂಡುಕೊಳ್ಳುವವರು ಅವುಗಳನ್ನು ಕೊಯ್ದರೂ ದೋಷಿಗಳೆಂದು ಎಣಿಸುವುದಿಲ್ಲ; ಮಾರುವವರು ಯೆಹೋವನಿಗೆ ಸ್ತೋತ್ರವಾಗಲಿ, ಧನವಂತರಾದೆವು ಅಂದುಕೊಳ್ಳುವರು. ಆ ಮಂದೆಯ ಕುರುಬರಾದರೂ ಅವುಗಳನ್ನು ಕರುಣಿಸುವುದಿಲ್ಲ.”",
			"6": "<sup>6</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ, “ನಾನು ಲೋಕನಿವಾಸಿಗಳನ್ನು ಇನ್ನು ಕರುಣಿಸೆನು; ಇಗೋ, ಪ್ರತಿಯೊಬ್ಬನನ್ನು ಅವನವನ ನೆರೆಯವನ ಕೈಗೂ, ಅರಸನ ಕೈಗೂ ಒಪ್ಪಿಸುವೆನು; ಆ ಬಲಿಷ್ಟರು ಲೋಕವನ್ನು ಪುಡಿಪುಡಿಮಾಡುವರು; ಅವರ ಕೈಗೆ ಸಿಕ್ಕಿದವರನ್ನು ಉದ್ಧರಿಸೆನು.”",
			"7": "<sup>7</sup> ಆಗ ನಾನು ದೀನಾವಸ್ಥೆಯಲ್ಲಿದ್ದ ಆ ಕೊಯ್ಗುರಿಗಳ ಮಂದೆಯನ್ನು ಮೇಯಿಸಿದೆನು. ಎರಡು ಕೋಲುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಒಂದಕ್ಕೆ “ಕೃಪೆ” ಎಂತಲೂ ಇನ್ನೊಂದಕ್ಕೆ, “ಒಗ್ಗಟ್ಟು” ಎಂತಲೂ ಹೆಸರಿಟ್ಟು ಅವುಗಳಿಂದ ಮಂದೆಯನ್ನು ಮೇಯಿಸಿದೆನು.",
			"8": "<sup>8</sup> ಒಂದೇ ತಿಂಗಳೊಳಗೆ ಮೂವರು ಕುರುಬರನ್ನು ಅಡಗಿಸಿಬಿಟ್ಟೆನು; ಅನಂತರ ನನಗೆ ಕುರಿಗಳ ವಿಷಯವಾಗಿ ತಾಳ್ಮೆ ತಪ್ಪಿತು; ಅವುಗಳಿಂದ ನನಗೆ ಬೇಸರವಾಯಿತು.",
			"9": "<sup>9</sup> ಆಗ, “ನಾನು ನಿಮ್ಮನ್ನು ಮೇಯಿಸೆನು; ಸಾಯುವುದು ಸಾಯಲಿ, ಹಾಳಾಗುವುದು ಹಾಳಾಗಲಿ, ಉಳಿದವುಗಳು ಒಂದರ ಮಾಂಸವೊಂದನ್ನು ತಿನ್ನಲಿ ಅಂದುಕೊಂಡೆನು.”",
			"10": "<sup>10</sup> ಆಗ ನಾನು ಎಲ್ಲಾ ಜನಾಂಗಗಳೊಂದಿಗೆ ಮಾಡಿಕೊಂಡಿದ್ದ ಒಡಂಬಡಿಕೆಯನ್ನು ಭಂಗಪಡಿಸಬೇಕೆಂದು “ಕೃಪೆ” ಎಂಬ ನನ್ನ ಕೋಲನ್ನು ತೆಗೆದು ಕತ್ತರಿಸಿಬಿಟ್ಟೆನು. ಆಗಲೇ ಮುರಿದುಹೋಯಿತು;",
			"11": "<sup>11</sup> ಇದರಿಂದ ಆ ದೀನವಾದ ಮಂದೆಯಲ್ಲಿ ನನ್ನನ್ನು ಲಕ್ಷಿಸುತ್ತಿದ್ದ ಕುರಿಗಳು, ಇದು ಯೆಹೋವನ ನುಡಿ ಎಂದು ತಿಳಿದುಕೊಂಡವು.",
			"12": "<sup>12</sup> ಅನಂತರ ನಾನು ನಿಮಗೆ, “ಸರಿಯಾಗಿ ತೋರಿದರೆ ನನಗೆ ಸಂಬಳವನ್ನು ಕೊಡಿರಿ ಇಲ್ಲವಾದರೆ ಬಿಡಿರಿ” ಅನ್ನಲು ಅವರು ಮೂವತ್ತು ತೊಲೆ ಬೆಳ್ಳಿಯನ್ನು ತೂಗಿ ನನ್ನ ಸಂಬಳಕ್ಕಾಗಿ ಕೊಟ್ಟರು.",
			"13": "<sup>13</sup> ಅವರು ಇವನ ಯೋಗ್ಯತೆ ಇಷ್ಟೇ ಎಂದುಕೊಂಡು ನನಗೆ ಕೊಟ್ಟ ಆ ಬಾರೀ ಸಂಬಳವನ್ನು ಯೆಹೋವನು ನೋಡಿ, “ಅದನ್ನು ಡಬ್ಬಿಗೆ ಹಾಕು” ಎಂದು ಅಪ್ಪಣೆ ಮಾಡಿದಾಗ ನಾನು ಆ ಮೂವತ್ತು ತೊಲ ಬೆಳ್ಳಿಯನ್ನು ತೆಗೆದು ಯೆಹೋವನ ಆಲಯದ ಡಬ್ಬಿಗೆ ಹಾಕಿದೆನು.",
			"14": "<sup>14</sup> ಕೂಡಲೆ ಯೆಹೂದಕ್ಕೂ, ಇಸ್ರಾಯೇಲಿಗೂ ಇದ್ದ ಸಹೋದರಭಾವವನ್ನು ಭಂಗಪಡಿಸಬೇಕೆಂದು “ಒಗ್ಗಟ್ಟು” ಎಂಬ ನನ್ನ ಎರಡನೆಯ ಕೋಲನ್ನು ಮುರಿದುಬಿಟ್ಟೆನು.",
			"15": "<sup>15</sup> ಆ ಮೇಲೆ ಯೆಹೋವನು ನನಗೆ ಹೀಗೆ ಅಪ್ಪಣೆ ಮಾಡಿದನು, “ನೀನು ಮತ್ತೊಮ್ಮೆ ತಕ್ಕ ಸಲಕರಣೆಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಮೂರ್ಖಕುರುಬನಂತೆ ನಟಿಸು.",
			"16": "<sup>16</sup> ಇಗೋ, ದೇಶದಲ್ಲಿ ನಾನು ಒಬ್ಬ ಕುರುಬನನ್ನು ನೇಮಿಸುವೆನು; ಅವನು ಹಾಳಾಗುತ್ತಿರುವ ಕುರಿಗಳನ್ನು ನೋಡಿಕೊಳ್ಳನು, ಚದುರಿದವುಗಳನ್ನು ಹುಡುಕನು, ಊನವಾದವುಗಳನ್ನು ಗುಣಪಡಿಸನು, ನೆಟ್ಟಗಿರುವವುಗಳನ್ನು ಸಾಕನು; ಕೊಬ್ಬಿದವುಗಳ ಮಾಂಸವನ್ನು ತಿನ್ನುವನು, ಕುರಿಗಳ ಗೊರಸುಗಳನ್ನು ಚೂರುಚೂರು ಮಾಡುವನು.",
			"17": "<sup>17</sup> ಮಂದೆಯನ್ನು ಕಾಯದೆ ಬಿಟ್ಟು ಬಿಟ್ಟ ಕುರುಬನ ಗತಿಯನ್ನು ಏನು ಹೇಳಲಿ? ಖಡ್ಗವು ಅವನ ತೋಳಿಗೂ, ಬಲಗಣ್ಣಿಗೂ ತಾಗುವುದು; ಅವನ ತೋಳು ತೀರಾ ಒಣಗಿಹೋಗುವುದು. ಅವನ ಬಲಗಣ್ಣು ಪೂರಾ ಮೊಬ್ಬಾಗುವುದು.”"
		},
		"12": {
			"1": "<sup>1</sup> ಇಸ್ರಾಯೇಲಿನ ವಿಷಯವಾಗಿ ಯೆಹೋವನು ನುಡಿದ ದೈವೋಕ್ತಿ. ಆಕಾಶ ಮಂಡಲವನ್ನು ಹರಡಿ, ಭೂಲೋಕಕ್ಕೆ ಅಸ್ತಿವಾರವನ್ನು ಹಾಕಿ, ಮನುಷ್ಯರೊಳಗೆ ಜೀವಾತ್ಮವನ್ನು ಸೃಷ್ಟಿಸುವ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ,",
			"2": "<sup>2</sup> “ಆಹಾ! ನಾನು ಯೆರೂಸಲೇಮನ್ನು ಸುತ್ತಣ ಸಕಲ ಜನಾಂಗಗಳಿಗೆ ಅಮಲೇರಿಸಿ ಓಲಾಡಿಸುವ ಬೋಗುಣಿಯನ್ನಾಗಿ ಮಾಡುವೆನು; ಯೆರೂಸಲೇಮಿಗೆ ಮುತ್ತಿಗೆಹಾಕುವಾಗ ಯೆಹೂದಕ್ಕೂ ಇಕ್ಕಟ್ಟಾಗುವುದು.",
			"3": "<sup>3</sup> ಆ ದಿನದಲ್ಲಿ ನಾನು ಯೆರೂಸಲೇಮನ್ನು ಸಮಸ್ತ ಜನಗಳಿಗೂ, ಭಾರೀ ಬಂಡೆಯನ್ನಾಗಿ ಮಾಡುವೆನು; ಅದನ್ನು ಎತ್ತುವವರೆಲ್ಲರು ಜಜ್ಜಲ್ಪಡುವರು; ಲೋಕದ ಸಕಲ ರಾಜ್ಯಗಳು ಅದನ್ನೆತ್ತಿ ಹಾಕಲು ಕೂಡಿಬರುವವು.”",
			"4": "<sup>4</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ, “ಆ ದಿನದಲ್ಲಿ ನಾನು ಎಲ್ಲಾ ಕುದುರೆಗಳು ಭಯದಿಂದ ತಬ್ಬಿಬ್ಬಾಗುವಂತೆ ಮಾಡುವೆನು, ಸವಾರರನ್ನು ಭ್ರಮೆಗೊಳಿಸುವೆನು; ಯೆಹೂದ ವಂಶವನ್ನು ಕಟಾಕ್ಷಿಸಿ ಜನಾಂಗಗಳ ಅಶ್ವಗಳನ್ನೆಲ್ಲಾ ಕುರುಡು ಮಾಡುವೆನು.",
			"5": "<sup>5</sup> ಆಗ ಯೆಹೂದದ ಕುಲಪತಿಗಳು ತಮ್ಮ ಮನಸ್ಸಿನೊಳಗೆ, ‘ಯೆರೂಸಲೇಮಿನವರು ತಮ್ಮ ದೇವರೂ, ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನಲ್ಲಿ ಬಲಗೊಂಡು ನಮಗೆ ತ್ರಾಣವಾಗಿದ್ದಾರೆ’ ಅಂದುಕೊಳ್ಳುವರು.",
			"6": "<sup>6</sup> “ಆ ದಿನದಲ್ಲಿ ನಾನು ಯೆಹೂದದ ಕುಲಪತಿಗಳನ್ನು ಸೌದೆಯ ಮಧ್ಯದಲ್ಲಿನ ಅಗ್ಗಿಷ್ಟಿಕೆಯನ್ನಾಗಿಯೂ, ಸಿವುಡುಗಳ ನಡುವಣ ಪಂಜನ್ನಾಗಿಯೂ ಮಾಡುವೆನು; ಅವರು ಸುತ್ತಣ ಜನಾಂಗಗಳನ್ನೆಲ್ಲಾ ಎಡಬಲಗಳಲ್ಲಿ ನುಂಗಿಬಿಡುವರು; ಯೆರೂಸಲೇಮಿನವರು ತಮ್ಮ ಸ್ಥಳವಾದ ಯೆರೂಸಲೇಮಿನಲ್ಲೇ ಇನ್ನು ವಾಸಿಸುವರು;",
			"7": "<sup>7</sup> ದಾವೀದ ವಂಶದವರ ಮಹಿಮೆಯೂ, ಯೆರೂಸಲೇಮಿನವರ ಮಹಿಮೆಯೂ, ಯೆಹೂದದ ಮಹಿಮೆಯನ್ನು ಮೀರದಂತೆ ಯೆಹೋವನು ಯೆಹೂದದ ಪಾಳೆಯಗಳಿಗೆ ಮೊದಲು ಜಯವನ್ನುಂಟುಮಾಡುವನು;",
			"8": "<sup>8</sup> ಆ ದಿನದಲ್ಲಿ ಯೆಹೋವನು ಯೆರೂಸಲೇಮಿನವರನ್ನು ಸುತ್ತಲು ಕಾಪಾಡುವನು; ಅವರೊಳಗೆ ಈಗಿನ ಕುಂಟನು ಆ ದಿನದಲ್ಲಿ ದಾವೀದನಂತಿರುವನು; ದಾವೀದ ವಂಶವು ದೇವರಂತೆ, ಯೆಹೋವನ ದೂತನ ಹಾಗೆ ಅವರಿಗೆ ಮುಂದಾಳಾಗುವುದು.",
			"9": "<sup>9</sup> ಯೆರೂಸಲೇಮಿನ ಮೇಲೆ ಬೀಳುವ ಎಲ್ಲಾ ಜನಾಂಗಗಳ ಧ್ವಂಸಕ್ಕೆ ಆ ದಿನದಲ್ಲಿ ಕೈಹಾಕುವೆನು.”",
			"10": "<sup>10</sup> ದಾವೀದ ವಂಶದವರಲ್ಲಿಯೂ, ಯೆರೂಸಲೇಮಿನವರಲ್ಲಿಯೂ ದೇವರ ದಯೆಯನ್ನು ಹಂಬಲಿಸಿ ಬೇಡುವ ಭಾವವನ್ನು ಸುರಿಸುವೆನು; ತಾವು ಇರಿದವನನ್ನು ದಿಟ್ಟಿಸಿ ನೋಡುವರು; ಇದ್ದ ಒಬ್ಬ ಮಗನನ್ನು ಕಳೆದುಕೊಂಡಂತೆ ಅವನಿಗಾಗಿ ಗೋಳಾಡುವರು. ಚೊಚ್ಚಲ ಮಗನ ವಿಯೋಗಕ್ಕೋಸ್ಕರ ದುಃಖಪಟ್ಟಂತೆ ಅವನಿಗಾಗಿ ದುಃಖಿಸುವರು.",
			"11": "<sup>11</sup> ಮೆಗಿದ್ದೋವಿನ ತಗ್ಗಿನೊಳಗೆ ಹದದ್ ರಿಮ್ಮೋನಿನಲ್ಲಿ ಗೋಳಾಟವಾಗುವಂತೆ ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಆ ದಿನ ದೊಡ್ಡ ಗೋಳಾಟವಾಗುವುದು.",
			"12": "<sup>12</sup> ದೇಶವೆಲ್ಲಾ ಗೋಳಾಡುವುದು, ಒಂದೊಂದು ಕುಟುಂಬವು ಬೇರೆ ಬೇರೆಯಾಗಿ ಗೋಳಾಡುವುದು; ದಾವೀದ ವಂಶದ ಕುಟುಂಬವು ಬೇರೆ, ಅದರಲ್ಲಿ ಹೆಂಗಸರು, ಗಂಡಸರು ಬೇರೆ ಬೇರೆ; ನಾತಾನ ವಂಶದ ಕುಟುಂಬವು ಬೇರೆ, ಅದರಲ್ಲಿ ಹೆಂಗಸರು ಗಂಡಸರು ಬೇರೆ ಬೇರೆ;",
			"13": "<sup>13</sup> ಲೇವಿ ವಂಶದ ಕುಟುಂಬವು ಬೇರೆ, ಅದರಲ್ಲಿ ಹೆಂಗಸರು, ಗಂಡಸರು ಬೇರೆ ಬೇರೆ; ಶಿಮ್ಮಿಯ ಸಂತಾನದ ಕುಟುಂಬವು ಬೇರೆ, ಅದರಲ್ಲಿ ಹೆಂಗಸರು, ಗಂಡಸರು ಬೇರೆ ಬೇರೆ,",
			"14": "<sup>14</sup> ಉಳಿದ ಕುಟುಂಬಗಳೆಲ್ಲಾ ಬೇರೆ ಬೇರೆ, ಒಂದೊಂದರಲ್ಲಿಯೂ ಹೆಂಗಸರು, ಗಂಡಸರು ಬೇರೆ ಬೇರೆ, ಹೀಗೆ ಬೇರೆ ಬೇರೆಯಾಗಿಯೇ ಗೋಳಾಡುವರು."
		},
		"13": {
			"1": "<sup>1</sup> ಆ ದಿನದಲ್ಲಿ ಪಾಪವನ್ನೂ, ಅಶುಚಿಯನ್ನೂ ಪರಿಹರಿಸತಕ್ಕ ಒಂದು ಬುಗ್ಗೆಯು ದಾವೀದ ವಂಶದವರಿಗೂ, ಯೆರೂಸಲೇಮಿನವರಿಗೂ ತೆರೆದಿರುವುದು.",
			"2": "<sup>2</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ, “ಆ ದಿನದಲ್ಲಿ ನಾನು ವಿಗ್ರಹಗಳನ್ನು ದೇಶದೊಳಗಿಂದ ನಿರ್ನಾಮಮಾಡುವೆನು, ಅವು ಇನ್ನು ಯಾರ ನೆನಪಿಗೂ ಬರುವುದಿಲ್ಲ; ಅಲ್ಲದೆ ಸುಳ್ಳು ಪ್ರವಾದಿಗಳನ್ನೂ, ದುರಾತ್ಮವನ್ನೂ ದೇಶದೊಳಗಿಂದ ತೊಲಗಿಸಿಬಿಡುವೆನು.",
			"3": "<sup>3</sup> ಆಗ ಯಾವನಾದರೂ ಮತ್ತೆ ಪ್ರವಾದನೆಮಾಡಿದರೆ ಅವನ ಹೆತ್ತ ತಾಯಿತಂದೆಗಳು ಅವನಿಗೆ, ‘ನೀನು ಉಳಿಯಬೇಡ; ಯೆಹೋವನ ಹೆಸರೆತ್ತಿ ಸುಳ್ಳಾಡುತ್ತಿ’ ಎಂದು ಹೇಳಿ ಆ ಹೆತ್ತ ತಾಯಿತಂದೆಗಳೇ ಅವನ ಪ್ರವಾದನೆಯ ನಿಮಿತ್ತ ಅವನನ್ನು ಇರಿದುಬಿಡುವರು.",
			"4": "<sup>4</sup> “ಆ ದಿನದಲ್ಲಿ ಯಾವ ಪ್ರವಾದಿಯೇ ಆಗಲಿ ಪ್ರವಾದನೆ ಮಾಡಬೇಕೆಂದಿರುವಾಗ ತನಗಾದ ದರ್ಶನವನ್ನು ಪ್ರಕಟಿಸಲು ನಾಚಿಕೆ ಪಡುವನು; ಯಾವನೂ ಮೋಸಕ್ಕಾಗಿ ಕಂಬಳಿಯನ್ನು ಹೊದ್ದುಕೊಳ್ಳನು;",
			"5": "<sup>5</sup> ಒಬ್ಬನು, ‘ನಾನು ಪ್ರವಾದಿಯಲ್ಲ, ಹೊಲದ ಕೆಲಸದವನು; ಚಿಕ್ಕ ವಯಸ್ಸಿನಿಂದಲೂ ಗುಲಾಮನಾಗಿದ್ದೇನೆ’ ಎಂದು ಹೇಳಲು,",
			"6": "<sup>6</sup> ಮತ್ತೊಬ್ಬನು ಅವನನ್ನು, ‘ನಿನ್ನ ಎದೆಯ ಮೇಲಿರುವ ಈ ಗಾಯಗಳು ಏನು?’ ಎಂದು ಕೇಳಿದರೆ ಅವನು, ‘ಮಿತ್ರರ ಮನೆಯಲ್ಲಿ ನನಗಾದ ಗಾಯಗಳು’” ಎಂದು ಉತ್ತರ ಕೊಡುವನು.",
			"7": "<sup>7</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ, “ಖಡ್ಗವೇ, ನಾನು ನೇಮಿಸಿದ ಕುರುಬನೂ, ನನ್ನ ಸಂಗಡಿಗನೂ ಆಗಿರುವವನ ವಿರುದ್ಧ ಎಚ್ಚರಗೊಳ್ಳು; ಕುರುಬನನ್ನು ಹೊಡೆ, ಕುರಿಗಳು ಚದುರಿಹೋಗುವವು; ಮರಿಗಳ ಮೇಲೂ ಕೈಮಾಡಬೇಕೆಂದಿದ್ದೇನೆ.",
			"8": "<sup>8</sup> “ದೇಶದೊಳಗೆಲ್ಲಾ ಮೂರರಲ್ಲಿ ಎರಡು ಭಾಗದವರು ಹತರಾಗಿ ಸಾಯುವರು, ಮೂರನೆಯ ಭಾಗದವರು ಅಲ್ಲಿ ಉಳಿಯುವರು” ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"9": "<sup>9</sup> “ಆ ಮೂರನೆಯ ಭಾಗದವರನ್ನು ನಾನು ಬೆಂಕಿಗೆ ಹಾಕಿ ಬೆಳ್ಳಿಯಂತೆ ಶೋಧಿಸುವೆನು, ಬಂಗಾರದ ಹಾಗೆ ಶುದ್ಧಿ ಮಾಡುವೆನು; ಅವರು ನನ್ನ ಹೆಸರೆತ್ತಿ ಪ್ರಾರ್ಥಿಸುವರು, ನಾನು ಆಲಿಸುವೆನು; ನಾನು, ‘ಇವರು ನನ್ನ ಜನರು’ ಅಂದುಕೊಳ್ಳುವೆನು. ಅವರು, ‘ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನೇ’ ಅನ್ನುವರು.”"
		},
		"14": {
			"1": "<sup>1</sup> ಆಹಾ, ಚೀಯೋನೇ, ಯೆಹೋವನು ನೇಮಿಸಿದ ದಿನವು ಬರುತ್ತಿದೆ; ಸೂರೆಯಾದ ನಿನ್ನ ಆಸ್ತಿಯು ಆಗ ನಿನ್ನ ಮಧ್ಯದಲ್ಲಿ ಹಂಚಿಕೆಯಾಗುವುದು.",
			"2": "<sup>2</sup> ನಾನು ಸಕಲ ಜನಾಂಗಗಳನ್ನು ಯೆರೂಸಲೇಮಿಗೆ ವಿರುದ್ಧವಾಗಿ ಕೂಡಿಸುವೆನು; ಅವು ಪಟ್ಟಣವನ್ನು ಆಕ್ರಮಿಸಿಕೊಂಡು ಮನೆಗಳನ್ನು ಸೂರೆಮಾಡಿ ಹೆಂಗಸರನ್ನು ಕೆಡಿಸುವವು; ಪಟ್ಟಣದ ಅರ್ಧ ಜನರು ಸೆರೆಗೆ ಹೋಗುವರು, ಉಳಿದ ಜನರೋ ಪಟ್ಟಣದಲ್ಲೇ ಉಳಿಯುವರು.",
			"3": "<sup>3</sup> ಆಗ ಯೆಹೋವನು ಹೊರಟು, ಯುದ್ಧದ ದಿನದಲ್ಲಿ ಹೇಗೋ ಹಾಗೆಯೇ ಆ ಜನಾಂಗಗಳಿಗೆ ಪ್ರತಿಭಟಿಸುವನು.",
			"4": "<sup>4</sup> ಯೆರೂಸಲೇಮಿನ ಪೂರ್ವದಿಕ್ಕಿಗೆ ಎದುರಾಗಿರುವ ಎಣ್ಣೆಯ ಮರಗಳ ಗುಡ್ಡದ ಮೇಲೆ ಆತನ ಪಾದಗಳು ನಿಂತಿರಲು, ಆ ಗುಡ್ಡವು ಪೂರ್ವದಿಂದ ಪಶ್ಚಿಮದ ಉದ್ದಕ್ಕೂ ಸೀಳಿಹೋಗಿ ಮಧ್ಯದಲ್ಲಿ ದೊಡ್ಡ ಡೊಂಗರವಾಗುವುದು. ಗುಡ್ಡದ ಅರ್ಧಭಾಗವು ಉತ್ತರಕ್ಕೂ, ಅರ್ಧಭಾಗವು ದಕ್ಷಿಣಕ್ಕೂ ಸರಿದುಕೊಳ್ಳುವುದು.",
			"5": "<sup>5</sup> ನೀವು ನನ್ನ ಗುಡ್ಡಗಳ ನಡುವಣ ಆ ಡೊಂಗರದೊಳಗೆ ಓಡಿಹೋಗುವಿರಿ; ಏಕೆಂದರೆ ಆ ಡೊಂಗರವು ಆಚೆಲಿನವರೆಗೂ ಮುಟ್ಟಿರುವುದು; ಯೆಹೂದದ ಅರಸನಾದ ಉಜ್ಜೀಯನ ಕಾಲದಲ್ಲಿ ಭೂಕಂಪದ ಕಡೆಯಿಂದ ನೀವು ಓಡಿಹೋದಂತೆಯೇ ಓಡಿಹೋಗುವಿರಿ; ಆಗ ಪರಿಶುದ್ಧ ದೇವರಾದ ಯೆಹೋವನು ಸಮಸ್ತ ದೇವದೂತರ ಸಮೇತ ಬರುವನು.",
			"6": "<sup>6</sup> ಆ ದಿನದಲ್ಲಿ ಬೆಳಕಿರದು, ಜ್ಯೋತಿಗಳು ಅಡಗಿಹೋಗುವವು, ಇಂಥಾ ದಿನವು ಒಂದೇ,",
			"7": "<sup>7</sup> ಅದು ಯೆಹೋವನಿಗೆ ತಿಳಿದಿರುವುದು, ಅದು ಹಗಲೂ ಅಲ್ಲ, ಇರುಳೂ ಅಲ್ಲ, ಆದರೆ ಸಂಜೆಯ ವೇಳೆಯಲ್ಲಿ ಬೆಳಕಾಗುವುದು.",
			"8": "<sup>8</sup> ಆ ದಿನದಲ್ಲಿ ಜೀವಕರವಾದ ಜಲಪ್ರವಾಹವು ಯೆರೂಸಲೇಮಿನೊಳಗಿಂದ ಹೊರಡುವುದು; ಅರ್ಧಭಾಗವು ಪೂರ್ವಸಮುದ್ರಕ್ಕೂ, ಅರ್ಧಬಾಗವು ಪಶ್ಚಿಮ ಸಮುದ್ರಕ್ಕೂ ಹರಿಯುವುದು; ಬೇಸಿಗೆಕಾಲದಲ್ಲಿಯೂ, ಮಳೆಗಾಲದಲ್ಲಿಯೂ ಹರಿಯುತ್ತಲೇ ಇರುವುದು.",
			"9": "<sup>9</sup> ಯೆಹೋವನು ಭೂಲೋಕಕ್ಕೆಲ್ಲಾ ರಾಜನಾಗಿರುವನು; ಆ ದಿನದಲ್ಲಿ ಯೆಹೋವನೊಬ್ಬನೇ ದೇವರೆಂದೂ ಆತನ ಹೆಸರೊಂದೇ ಸ್ತುತ್ಯವೆಂದೂ ಎಲ್ಲರಿಗೂ ತಿಳಿದಿರುವುದು.",
			"10": "<sup>10</sup> ಆಗ ದೇಶವು ಗೆಬದಿಂದ ಯೆರೂಸಲೇಮಿನ ದಕ್ಷಿಣದಲ್ಲಿರುವ ರಿಮ್ಮೋನಿನವರೆಗೆ ತಗ್ಗಾಗಿ ಮಾರ್ಪಡುವುದು; ಯೆರೂಸಲೇಮೋ ಬೆನ್ಯಾಮೀನಿನ ಬಾಗಿಲಿನಿಂದ ಪೂರ್ವಕಾಲದ ಬಾಗಿಲಿನ ಸ್ಥಳದವರೆಗೆ, ಮೂಲೆಯ ಬಾಗಿಲಿನ ತನಕ, ಹನನೇಲನ ಬುರುಜಿನಿಂದ ಅರಸನ ದ್ರಾಕ್ಷೆಯ ಆಲೆಗಳವರೆಗೂ ಎತ್ತರದಲ್ಲಿ ನಿಂತಿರುವುದು.",
			"11": "<sup>11</sup> ಅದರಲ್ಲಿ ಜನರು ಸುರಕ್ಷಿತವಾಗಿ ವಾಸಿಸುವರು. ಇನ್ನು ಶಾಪವಿರದು; ಯೆರೂಸಲೇಮ್ ನೆಮ್ಮದಿಯಾಗಿ ನೆಲೆಗೊಂಡಿರುವುದು.",
			"12": "<sup>12</sup> ಯೆರೂಸಲೇಮಿನ ಮೇಲೆ ಯುದ್ಧ ಮಾಡಿದ ಸಕಲ ಜನಾಂಗಗಳಿಗೂ ಯೆಹೋವನು ಈ ವ್ಯಾಧಿಯು ಬರುವಂತೆ ಮಾಡುವನು, ಹೆಜ್ಜೆಯ ಮೇಲೆ ನಿಂತ ಹಾಗೆಯೇ ಅವರ ಮಾಂಸವು ಕೊಳೆತು ಹೋಗುವುದು, ಕಣ್ಣು ಕುಣಿಯಲ್ಲೇ ಇಂಗುವುದು, ನಾಲಿಗೆಯು ಬಾಯಲ್ಲೇ ಕ್ಷಯಿಸುವುದು.",
			"13": "<sup>13</sup> ಆ ದಿನದಲ್ಲಿ ಯೆಹೋವನು ಉಂಟುಮಾಡುವ ದೊಡ್ಡ ಗಲಿಬಿಲಿಯು ಅವರಲ್ಲಿ ಹರಡುವುದು; ಒಬ್ಬರ ಕೈಯನೊಬ್ಬರು ತಡೆಹಿಡಿಯುವರು, ಒಬ್ಬರ ಮೇಲೊಬ್ಬರು ಕೈಯೆತ್ತುವರು.",
			"14": "<sup>14</sup> ಯೆಹೂದವೂ ಯೆರೂಸಲೇಮಿನ ಪರವಾಗಿ ಯುದ್ಧಮಾಡುವುದು; ಸುತ್ತಣ ಸಕಲ ಜನಾಂಗಗಳ ಆಸ್ತಿಯು ಅಂದರೆ ಬೆಳ್ಳಿ, ಬಂಗಾರ ಮತ್ತು ಬಟ್ಟೆಗಳು ರಾಶಿರಾಶಿಯಾಗಿ ಕೂಡಿಸಲ್ಪಡುವವು.",
			"15": "<sup>15</sup> ಜನಾಂಗಗಳಿಗೆ ತಗಲುವಂಥ ವ್ಯಾಧಿಯೇ ಕುದುರೆ, ಹೇಸರಗತ್ತೆ, ಒಂಟೆ, ಕತ್ತೆ ಅಂತು ಆ ಪಾಳೆಯಗಳಲ್ಲಿನ ಸಮಸ್ತ ಪಶುಗಳಿಗೂ ತಗಲುವುದು.",
			"16": "<sup>16</sup> ಅನಂತರ ಯೆರೂಸಲೇಮಿನ ಮೇಲೆ ಬಿದ್ದ ಸಕಲ ಜನಾಂಗಗಳಲ್ಲಿ ಉಳಿದವರೆಲ್ಲರೂ ಸೇನಾಧೀಶ್ವರ ಯೆಹೋವನೆಂಬ ರಾಜಾಧಿರಾಜನನ್ನು ಆರಾಧಿಸುವುದಕ್ಕೂ, ಪರ್ಣಶಾಲೆಗಳ ಹಬ್ಬವನ್ನು ಆಚರಿಸುವುದಕ್ಕೂ ಪ್ರತಿವರುಷವೂ ಹೊರಟುಬರುವರು.",
			"17": "<sup>17</sup> ಲೋಕದ ಸಮಸ್ತ ಕುಲಗಳಲ್ಲಿ ಯಾವ ಕುಲದವರು ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೆಂಬ ರಾಜಾಧಿರಾಜನನ್ನು ಆರಾಧಿಸುವುದಕ್ಕೆ ಯೆರೂಸಲೇಮಿಗೆ ಬರುವುದಿಲ್ಲವೋ ಅವರಿಗೆ ಮಳೆಬರುವುದಿಲ್ಲ.",
			"18": "<sup>18</sup> ಐಗುಪ್ತ ಕುಲದವರು ಹೊರಟು ಬರದಿದ್ದರೆ, ಪರ್ಣಶಾಲೆಗಳ ಹಬ್ಬವನ್ನು ಆಚರಿಸುವುದಕ್ಕೆ ಬರದಿದ್ದರೆ ಜನಾಂಗಗಳಿಗೆ ಯೆಹೋವನು ತಗಲಿಸುವ ಬಾಧೆಯು ಅವರಿಗೂ ತಗಲುವುದು.",
			"19": "<sup>19</sup> ಐಗುಪ್ತಕ್ಕೂ ಪರ್ಣಶಾಲೆಗಳ ಹಬ್ಬವನ್ನು ಆಚರಿಸುವುದಕ್ಕೆ ಬರದಿರುವ ಸಕಲ ಜನಾಂಗಗಳಿಗೂ ಸಂಭವಿಸುವ ದಂಡನೆಯು ಇದೇ.",
			"20": "<sup>20</sup> ಆ ದಿನದಲ್ಲಿ, “ಯೆಹೋವನಿಗೆ ಮೀಸಲು” ಎಂಬ ಲಿಪಿಯು ಕುದುರೆಗಳ ಘಂಟೆಗಳ ಮೇಲೂ ಕೆತ್ತಿರುವುದು; ಯೆಹೋವನ ಆಲಯದ ಪಾತ್ರೆಗಳೆಲ್ಲವೂ ಯಜ್ಞವೇದಿಯ ಪಕ್ಕದ ಬೋಗುಣಿಗಳಂತೆ ಪರಿಶುದ್ಧವಾಗಿರುವವು.",
			"21": "<sup>21</sup> ಅಲ್ಲದೆ ಯೆರೂಸಲೇಮಿನಲ್ಲಿಯೂ, ಯೆಹೂದದಲ್ಲಿಯೂ ಇರುವ ಸಕಲ ಪಾತ್ರೆಗಳು ಸೇನಾಧೀಶ್ವರ ಯೆಹೋವನಿಗೆ ಮೀಸಲಾಗಿರುವವು; ಯಜ್ಞಮಾಡುವವರೆಲ್ಲರು ಬಂದು ಅವುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಆ ಪಾತ್ರೆಗಳಲ್ಲಿ ಬೇಯಿಸುವರು; ಆ ದಿನದಲ್ಲಿ ಸೇನಾಧೀಶ್ವರ ಯೆಹೋವನ ಆಲಯದೊಳಗೆ ಯಾವ ವ್ಯಾಪಾರಿಯೂ ಇರುವುದಿಲ್ಲ."
		}
	}
};
module.exports = book;