var book = {
	"name": "ಲೂಕನು",
	"numChapters": 24,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ಅತೀ ಶ್ರೇಷ್ಠನಾದ ಥೆಯೊಫಿಲನೇ, ನಮ್ಮಲ್ಲಿ ನಿಶ್ಚಯಕ್ಕೆ ಬಂದಿರುವ ಕಾರ್ಯಗಳನ್ನು ಮೊದಲಿಂದ ಕಣ್ಣಾರೆ ಕಂಡು ಸುವಾರ್ತಾವಾಕ್ಯವನ್ನು ಸಾರುತ್ತಿದ್ದವರು ನಮಗೆ ತಿಳಿಸಿದ ಪ್ರಕಾರ ಆ ಕಾರ್ಯಗಳನ್ನು ಚರಿತ್ರರೂಪವಾಗಿ ಬರೆಯುವುದಕ್ಕೆ ಅನೇಕರು ಪ್ರಯತ್ನಿಸಿರಲಾಗಿ",
			"3": "<sup>3</sup> ನಾನು ಬುಡದಿಂದ ಎಲ್ಲವನ್ನೂ ಚೆನ್ನಾಗಿ ವಿಚಾರಿಸಿದವನಾದ ಕಾರಣ ಅವುಗಳನ್ನು ನಿಮಗೆ ಕ್ರಮವಾಗಿ ಬರೆಯುವುದು ಒಳ್ಳೆಯದೆಂದು ನನಗೂ ತೋಚಿತು.",
			"4": "<sup>4</sup> ನಿಮಗೆ ಉಪದೇಶಿಸಿರುವ ವಿಷಯಗಳು ಸತ್ಯವಾದವುಗಳೆಂದು ಇದರಿಂದ ನೀವು ತಿಳಿದುಕೊಳ್ಳಬಹುದು.",
			"5": "<sup>5</sup> ಯೂದಾಯದ ಅರಸನಾದ ಹೆರೋದನ ಕಾಲದಲ್ಲಿ ಅಬೀಯನ ವರ್ಗಕ್ಕೆ ಸೇರಿದ ಜಕರೀಯನೆಂಬ ಒಬ್ಬ ಯಾಜಕನಿದ್ದನು. ಅವನ ಹೆಂಡತಿಯು ಆರೋನನ ವಂಶದವಳಾಗಿದ್ದಳು. ಆಕೆಯ ಹೆಸರು ಎಲಿಸಬೇತ್.",
			"6": "<sup>6</sup> ಅವರಿಬ್ಬರೂ ಕರ್ತನ ಎಲ್ಲಾ ಆಜ್ಞೆಗಳನ್ನೂ ನೇಮ ನಿಷ್ಠೆಗಳನ್ನೂ ಕೈಕೊಂಡು ತಪ್ಪಿಲ್ಲದೆ ನಡಕೊಳ್ಳುತ್ತಾ ದೇವರ ದೃಷ್ಟಿಯಲ್ಲಿ ನೀತಿವಂತರಾಗಿದ್ದರು.",
			"7": "<sup>7</sup> ಆದರೆ ಎಲಿಸಬೇತಳು ಬಂಜೆಯಾದುದರಿಂದ ಅವರಿಗೆ ಮಕ್ಕಳಿರಲಿಲ್ಲ; ಇದಲ್ಲದೆ ಅವರಿಬ್ಬರೂ ಮುಪ್ಪಿನವರಾಗಿದ್ದರು.",
			"8": "<sup>8</sup> ಹೀಗಿರಲಾಗಿ ಜಕರೀಯನ ವರ್ಗದ ಸರದಿ ಬಂದಾಗ ಅವನು ದೇವರ ಸನ್ನಿಧಿಯಲ್ಲಿ ಯಾಜಕಧರ್ಮವನ್ನು ನಡೆಸುತ್ತಿರಲು",
			"9": "<sup>9</sup> ದೇವಾಲಯದೊಳಕ್ಕೆ ಹೋಗಿ ಧೂಪವನ್ನರ್ಪಿಸುವುದು ಯಾಜಕರ ಪದ್ಧತಿಯ ಪ್ರಕಾರ ಅವನ ಪಾಲಿಗೆ ಬಂತು.",
			"10": "<sup>10</sup> ಧೂಪವನ್ನು ಅರ್ಪಿಸುವ ಹೊತ್ತಿನಲ್ಲಿ ಜನರ ಗುಂಪೆಲ್ಲಾ ಹೊರಗೆ ನಿಂತು ದೇವರನ್ನು ಪ್ರಾರ್ಥಿಸುತ್ತಿರಲಾಗಿ",
			"11": "<sup>11</sup> ಕರ್ತನ ದೂತನು ಧೂಪಪೀಠದ ಬಲಗಡೆಯಲ್ಲಿ ನಿಂತುಕೊಂಡವನಾಗಿ ಅವನಿಗೆ ಕಾಣಿಸಿಕೊಂಡನು.",
			"12": "<sup>12</sup> ಜಕರೀಯನು ಅವನನ್ನು ಕಂಡು ನಡುಗುತ್ತಾ ಭಯಹಿಡಿದವನಾದನು.",
			"13": "<sup>13</sup> ಆದರೆ ಆ ದೂತನು ಅವನಿಗೆ - ಜಕರೀಯಾ, ಭಯಪಡಬೇಡ; ನಿನ್ನ ವಿಜ್ಞಾಪನೆ ದೇವರಿಗೆ ಮುಟ್ಟಿತು; ನಿನ್ನ ಹೆಂಡತಿಯಾದ ಎಲಿಸಬೇತಳು ನಿನಗೆ ಒಬ್ಬ ಮಗನನ್ನು ಹೆರುವಳು.",
			"14": "<sup>14</sup> ನೀನು ಅವನಿಗೆ ಯೋಹಾನನೆಂದು ಹೆಸರಿಡಬೇಕು. ನಿನಗೆ ಸಂತೋಷವೂ ಉಲ್ಲಾಸವೂ ಉಂಟಾಗುವುದು; ಮತ್ತು ಅವನು ಹುಟ್ಟಿದ್ದಕ್ಕೆ ಬಹುಜನರು ಸಂತೋಷಪಡುವರು.",
			"15": "<sup>15</sup> ಅವನು ಕರ್ತನ ದೃಷ್ಟಿಯಲ್ಲಿ ಮಹಾಪುರುಷನಾಗಿರುವನು; ದ್ರಾಕ್ಷಾರಸವನ್ನಾಗಲಿ ಯಾವ ಮಧ್ಯವನ್ನಾಗಲಿ ಕುಡಿಯದವನಾಗಿರುವನು; ಅವನು ಹುಟ್ಟಿದಂದಿನಿಂದಲೇ ಪವಿತ್ರಾತ್ಮ ಭರಿತನಾಗಿರುವನು.",
			"16": "<sup>16</sup> ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಅನೇಕರನ್ನು ಅವರ ದೇವರಾಗಿರುವ ಕರ್ತನ ಕಡೆಗೆ ತಿರುಗಿಸುವನು. ಅವನು ಆತನ ಮುಂದೆ ಹೋಗಿ ಎಲೀಯನ ಗುಣಶಕ್ತಿಗಳಿಂದ ಕೂಡಿದವನಾಗಿ",
			"17": "<sup>17</sup> ತಂದೆಗಳ ಹೃದಯವನ್ನು ಮಕ್ಕಳ ಕಡೆಗೂ ಅವಿಧೇಯರನ್ನು ನೀತಿವಂತರ ಜ್ಞಾನದ ಕಡೆಗೂ ತಿರುಗಿಸಿ, ಕರ್ತನಿಗಾಗಿ ಜನರನ್ನು ಸಿದ್ಧಪಡಿಸುವನು ಎಂದು ಹೇಳಿದನು.",
			"18": "<sup>18</sup> ಜಕರೀಯನು ಆ ದೂತನಿಗೆ - ಇದನ್ನು ನಾನು ಹೇಗೆ ತಿಳುಕೊಳ್ಳಲಿ? ನಾನು ಮುದುಕನು; ನನ್ನ ಹೆಂಡತಿಯೂ ದಿನಹೋದವಳು ಎಂದು ಹೇಳಿದ್ದಕ್ಕೆ",
			"19": "<sup>19</sup> ಆ ದೂತನು - ನಾನು ದೇವರ ಸನ್ನಿಧಿಯಲ್ಲಿ ನಿಲ್ಲುವ ಗಬ್ರಿಯೇಲನು; ನಿನ್ನ ಕೂಡ ಮಾತಾಡಿ ಈ ಶುಭವಾರ್ತೆಯನ್ನು ನಿನಗೆ ತಿಳಿಸುವುದಕ್ಕಾಗಿ ನಾನು ಕಳುಹಿಸಲ್ಪಟ್ಟಿದ್ದೇನೆ.",
			"20": "<sup>20</sup> ಈ ನನ್ನ ಮಾತು ತಕ್ಕ ಸಮಯದಲ್ಲಿ ನೆರವೇರುವುದು; ಆದರೆ ನೀನು ಅದನ್ನು ನಂಬದೆಹೋದದ್ದರಿಂದ ಅದೆಲ್ಲಾ ಸಂಭವಿಸುವ ದಿನದ ವರೆಗೂ ಮಾತನಾಡಲಾರದೆ ಮೂಕನಾಗಿರುವಿ ಎಂದು ಅವನಿಗೆ ಉತ್ತರಕೊಟ್ಟನು.",
			"21": "<sup>21</sup> ಇಷ್ಟರೊಳಗೆ ಜನರು ಜಕರೀಯನನ್ನು ಕಾದುಕೊಂಡಿದ್ದು ಅವನು ದೇವಾಲಯದೊಳಗೆ ತಡಮಾಡಿದ್ದಕ್ಕೆ ಆಶ್ಚರ್ಯಪಟ್ಟರು.",
			"22": "<sup>22</sup> ಅವನು ಹೊರಗೆ ಬಂದಾಗ ಅವರ ಕೂಡ ಏನೂ ಮಾತಾಡಲಾರದೆ ಇರಲು ಅವರು - ಇವನಿಗೆ ದೇವಾಲಯದಲ್ಲಿ ಏನೋ ದಿವ್ಯ ದರ್ಶನವಾಗಿರಬೇಕು ಎಂದು ತಿಳುಕೊಂಡರು. ಜಕರೀಯನು ಅವರಿಗೆ ಕೈಸನ್ನೆ ಮಾಡುತ್ತಾ ಮೂಕನಾಗಿಯೇ ಇದ್ದನು.",
			"23": "<sup>23</sup> ಆತನ ಯಾಜಕವರ್ಗದ ದಿನಗಳು ತೀರಿದನಂತರ ಜಕರೀಯನು ತನ್ನ ಮನೆಗೆ ಹೋದನು.",
			"24": "<sup>24</sup> ಆ ದಿವಸಗಳಾದ ಮೇಲೆ ಅವನ ಹೆಂಡತಿಯಾದ ಎಲಿಸಬೇತಳು ಬಸುರಾಗಿ ಐದು ತಿಂಗಳು ಮನೆಯಲ್ಲೇ ಮರೆಯಾಗಿದ್ದಳು.",
			"25": "<sup>25</sup> ಕರ್ತನು ಈ ಕಾಲದಲ್ಲಿ ನನ್ನನ್ನು ಕಟಾಕ್ಷಿಸಿ ನನಗೆ ಈ ರೀತಿಯಾಗಿ ಮಾಡಿ ಜನರಲ್ಲಿ ನನಗಿದ್ದ ಅವಮಾನವನ್ನು ಪರಿಹರಿಸಿದ್ದಾನಲ್ಲಾ ಎಂದು ಹೇಳಿದಳು.",
			"26": "<sup>26</sup> ಆರನೆಯ ತಿಂಗಳಲ್ಲಿ ದೇವರು ಗಬ್ರಿಯೇಲನೆಂಬ ತನ್ನ ದೂತನನ್ನು ಗಲಿಲಾಯ ಸೀಮೆಯ ನಜರೇತೆಂಬ ಊರಿಗೆ ಒಬ್ಬ ಕನ್ಯೆಯ ಬಳಿಗೆ ಕಳುಹಿಸಿದನು;",
			"27": "<sup>27</sup> ಆ ಕನ್ಯೆಯ ಹೆಸರು ಮರಿಯಳು; ಆಕೆಯು ದಾವೀದನ ಮನೆತನದ ಯೋಸೇಫನೆಂಬ ಪುರುಷನಿಗೆ ವಿವಾಹ ನಿಶ್ಚಿತಾರ್ಥವಾಗಿದ್ದಳು.",
			"28": "<sup>28</sup> ಆ ದೂತನು ಆಕೆಯ ಬಳಿಗೆ ಬಂದು ‘ದೇವರ ದಯೆ ಹೊಂದಿದವಳೇ, ನಿನಗೆ ಶುಭವಾಗಲಿ’; ‘ಕರ್ತನು ನಿನ್ನ ಸಂಗಡ ಇದ್ದಾನೆ’ ಅಂದನು.",
			"29": "<sup>29</sup> ಆಕೆಯು ಆ ಮಾತಿಗೆ ತತ್ತರಿಸಿ - ಇದೆಂಥ ಆಶೀರ್ವಾದ ಎಂದು ಯೋಚನೆಮಾಡುತ್ತಿರಲಾಗಿ",
			"30": "<sup>30</sup> ಆ ದೂತನು ಆಕೆಗೆ - ಮರಿಯಳೇ, ಹೆದರಬೇಡ; ನಿನಗೆ ದೇವರ ದಯೆ ದೊರಕಿತು.",
			"31": "<sup>31</sup> ಇಗೋ, ನೀನು ಗರ್ಭಿಣಿಯಾಗಿ ಒಬ್ಬ ಮಗನನ್ನು ಹೆರುವಿ; ಆತನಿಗೆ ಯೇಸುವೆಂದು ಹೆಸರಿಡಬೇಕು",
			"32": "<sup>32</sup> ಆತನು ಮಹಾಪುರುಷನಾಗಿ ಪರಾತ್ಪರನ ಕುಮಾರನೆನಿಸಿಕೊಳ್ಳುವನು; ಇದಲ್ಲದೆ ದೇವರಾಗಿರುವ ಕರ್ತನು ಆತನ ಮೂಲಪಿತನಾದ ದಾವೀದನ ಸಿಂಹಾಸನವನ್ನು ಆತನಿಗೆ ಕೊಡುವನು.",
			"33": "<sup>33</sup> ಆತನು ಯಾಕೋಬನ ವಂಶವನ್ನು ಸದಾಕಾಲ ಆಳುವನು; ಆತನ ರಾಜ್ಯಕ್ಕೆ ಅಂತ್ಯವೇ ಇರುವುದಿಲ್ಲ ಎಂದು ಹೇಳಿದನು.",
			"34": "<sup>34</sup> ಆಗ ಮರಿಯಳು ಆ ದೂತನಿಗೆ - ಇದು ಹೇಗಾದೀತು? ನಾನು ಇನ್ನೂ ಪುರುಷನನ್ನು ಅರಿತವಳಲ್ಲವಲ್ಲಾ ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ ದೂತನು -",
			"35": "<sup>35</sup> ನಿನ್ನ ಮೇಲೆ ಪವಿತ್ರಾತ್ಮ ಬರುವದು; ಪರಾತ್ಪರನ ಶಕ್ತಿಯ ನೆರಳು ನಿನ್ನ ಮೇಲೆ ಬೀಳುವುದು; ಆದದರಿಂದ ಹುಟ್ಟುವ ಆ ಪವಿತ್ರ ಶಿಶು ದೇವರ ಮಗನೆನಿಸಿಕೊಳ್ಳುವದು.",
			"36": "<sup>36</sup> ಮತ್ತು ನಿನ್ನ ಬಂಧುವಾದ ಎಲಿಸಬೇತಳಿದ್ದಾಳಲ್ಲಾ, ಆಕೆ ಸಹ ಮುಪ್ಪಿನವಳಾದರೂ ಗರ್ಭಿಣಿಯಾಗಿದ್ದಾಳೆ; ಆಕೆಯ ಗರ್ಭದಲ್ಲಿ ಗಂಡು ಮಗುವಿದೆ; ಬಂಜೆಯೆನಿಸಿಕೊಂಡಿದ್ದ ಆಕೆಗೆ ಈಗ ಆರನೆಯ ತಿಂಗಳು.",
			"37": "<sup>37</sup> ದೇವರಿಂದ ಅಸಾದ್ಯವಾದದ್ದು ಯಾವುದೂ ಇಲ್ಲ ಮತ್ತು ದೇವರಿಂದ ಬರುವ ಯಾವ ಮಾತಾದರೂ ನಿಷ್ಫಲವಾಗುವದಿಲ್ಲ ಅಂದನು.",
			"38": "<sup>38</sup> ಆಗ ಮರಿಯಳು ಇಗೋ, ನಾನು ಕರ್ತನ ದಾಸಿ; ನಿನ್ನ ಮಾತಿನಂತೆ ನನಗಾಗಲಿ ಅಂದಳು. ಆ ಮೇಲೆ ಆ ದೂತನು ಆಕೆಯ ಬಳಿಯಿಂದ ಹೊರಟುಹೋದನು.",
			"39": "<sup>39</sup> ಆ ಕಾಲದಲ್ಲಿ ಮರಿಯಳು ಎದ್ದು ಅವಸರದಿಂದ ಮಲೆನಾಡಿಗೆ ಹೊರಟು ಯೆಹೂದದಲ್ಲಿರುವ ಒಂದೂರಿಗೆ ಹೋಗಿ",
			"40": "<sup>40</sup> ಜಕರೀಯನ ಮನೆಯನ್ನು ಸೇರಿ ಎಲಿಸಬೇತಳಿಗೆ ವಂದಿಸಿದಳು.",
			"41": "<sup>41</sup> ಎಲಿಸಬೇತಳು ಮರಿಯಳ ವಂದನೆಯನ್ನು ಕೇಳುತ್ತಲೇ ಆಕೆಯ ಗರ್ಭದಲ್ಲಿದ್ದ ಕೂಸು ಪಕ್ಕನೆ ಕುಣಿದಾಡಿತು.",
			"42": "<sup>42</sup> ಮತ್ತು ಎಲಿಸಬೇತಳು ಪವಿತ್ರಾತ್ಮಭರಿತಳಾಗಿ ಮಹಾಧ್ವನಿಯಿಂದ ಕೂಗಿ - ನೀನು ಸ್ತ್ರೀಯರೊಳಗೆ ಆಶೀರ್ವಾದ ಹೊಂದಿದವಳು, ಮತ್ತು ನಿನ್ನಲ್ಲಿ ಹುಟ್ಟುವ ಕೂಸು ಆಶೀರ್ವಾದ ಹೊಂದಿದ್ದು.",
			"43": "<sup>43</sup> ನನ್ನ ಸ್ವಾಮಿಯ ತಾಯಿಯು ನನ್ನ ಬಳಿಗೆ ಬರುವಷ್ಟು ಭಾಗ್ಯ ನನಗೆ ಎಲ್ಲಿಂದಾಯಿತು?",
			"44": "<sup>44</sup> ಇಗೋ, ನಿನ್ನ ವಂದನೆಯು ನನ್ನ ಕಿವಿಗೆ ಬೀಳುತ್ತಲೇ ಕೂಸು ನನ್ನ ಗರ್ಭದಲ್ಲಿ ಉಲ್ಲಾಸದಿಂದ ನಲಿದಾಡಿತು. ಇದನ್ನು ನಂಬಿದವಳಾದ ನೀನು ಧನ್ಯಳೇ;",
			"45": "<sup>45</sup> ಕರ್ತನು ನಿನಗೆ ಹೇಳಿದ ಮಾತುಗಳು ನೆರವೇರುವವು ಅಂದಳು.",
			"46": "<sup>46</sup> ಆಗ ಮರಿಯಳು ಹೇಳಿದ್ದೇನಂದರೆ - ನನ್ನ ಪ್ರಾಣವು ಕರ್ತನನ್ನು ಕೊಂಡಾಡುತ್ತದೆ,",
			"47": "<sup>47</sup> ನನ್ನ ಆತ್ಮವು ನನ್ನ ರಕ್ಷಕನಾದ ದೇವರ ವಿಷಯದಲ್ಲಿ ಉಲ್ಲಾಸಗೊಂಡದೆ.",
			"48": "<sup>48</sup> ಆತನು ತನ್ನ ದಾಸಿಯ ದೀನಸ್ಥಿತಿಯ ಮೇಲೆ ಲಕ್ಷ್ಯವಿಟ್ಟಿದ್ದಾನೆ. ಇಗೋ, ಇಂದಿನಿಂದ ತಲತಲಾಂತರದವರೆಲ್ಲರೂ ನನ್ನನ್ನು ಧನ್ಯಳೆಂದು ಹೊಗಳುವರು.",
			"49": "<sup>49</sup> ಶಕ್ತನಾಗಿರುವಾತನು ನನಗೆ ದೊಡ್ಡ ಉಪಕಾರಗಳನ್ನು ಮಾಡಿದ್ದಾನೆ; ಆತನ ನಾಮವು ಪರಿಶುದ್ಧವಾದದ್ದು.",
			"50": "<sup>50</sup> ಆತನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರ ಮೇಲೆ ಆತನ ದಯೆಯು ತಲತಲಾಂತರದ ವರೆಗೂ ಇರುವದು.",
			"51": "<sup>51</sup> ಆತನು ತನ್ನ ಹೃದಯ ವಿಚಾರಗಳಲ್ಲಿ ಅಹಂಕರಿಸಿದವರನ್ನು ಚದರಿಸಿಬಿಟ್ಟಿದ್ದಾನೆ.",
			"52": "<sup>52</sup> ಪ್ರಭುಗಳನ್ನು ಸಿಂಹಾಸನಗಳಿಂದ ಕೆಳಗೆ ದೊಬ್ಬಿ ದೀನಸ್ಥಿತಿಯವರನ್ನು ಉನ್ನತಸ್ಥಿತಿಗೆ ತಂದಿದ್ದಾನೆ.",
			"53": "<sup>53</sup> ಹಸಿದವರನ್ನು ಮೃಷ್ಟಾನ್ನದಿಂದ ತೃಪ್ತಿಪಡಿಸಿ ಸ್ಥಿತಿವಂತರನ್ನು ಬರಿಗೈಯಲ್ಲಿ ಕಳುಹಿಸಿಬಿಟ್ಟಿದ್ದಾನೆ.",
			"54": "<sup>54</sup> ಆತನು ನಮ್ಮ ಪೂರ್ವಿಕರಿಗೆ ಹೇಳಿದ್ದಕ್ಕೆ ಸರಿಯಾಗಿ",
			"55": "<sup>55</sup> ಅಬ್ರಹಾಮನಿಗೂ ಅವನ ವಂಶದವರಿಗೂ ಯಾವಾಗಲೂ ದಯೆ ತೋರಿಸಬೇಕೆಂಬದನ್ನು ನೆನಸಿಕೊಂಡು ತನ್ನ ಸೇವಕನಾದ ಇಸ್ರಾಯೇಲನನ್ನು ಕೈಹಿಡಿದಿದ್ದಾನೆ",
			"56": "<sup>56</sup> ಅಂದಳು. ಮರಿಯಳು ಹೆಚ್ಚು ಕಡಿಮೆ ಮೂರು ತಿಂಗಳು ಎಲಿಸಬೇತಳ ಬಳಿಯಲ್ಲಿದ್ದು ತನ್ನ ಮನೆಗೆ ಹಿಂತಿರುಗಿ ಹೋದಳು.",
			"57": "<sup>57</sup> ಎಲಿಸಬೇತಳು ದಿನತುಂಬಿ ಗಂಡುಮಗುವನ್ನು ಹೆತ್ತಳು.",
			"58": "<sup>58</sup> ಆಕೆಯ ನೆರೆಹೊರೆಯವರೂ ಬಂಧುಬಾಂಧವರೂ ಆಕೆಯ ಮೇಲೆ ಕರ್ತನು ತನ್ನ ದಯೆಯನ್ನು ವಿಶೇಷವಾಗಿ ತೋರಿಸಿದ್ದಾನೆಂಬದನ್ನು ಕೇಳಿ ಆಕೆಯ ಸಂಗಡ ಸಂತೋಷಪಟ್ಟರು.",
			"59": "<sup>59</sup> ಎಂಟನೆಯ ದಿನದಲ್ಲಿ ಅವರು ಆ ಮಗುವಿಗೆ ಸುನ್ನತಿಮಾಡುವದಕ್ಕಾಗಿ ಬಂದು ಅದಕ್ಕೆ ಜಕರೀಯನೆಂದು ತಂದೆಯ ಹೆಸರನ್ನು ಇಡಬೇಕೆಂದಿದ್ದರು.",
			"60": "<sup>60</sup> ಈ ವಿಷಯದಲ್ಲಿ ಎಲಿಸಬೇತಳು ಅದು ಬೇಡ, ಯೋಹಾನನೆಂದು ಹೆಸರಿಡಬೇಕು ಅನ್ನಲು",
			"61": "<sup>61</sup> ಅವರು - ನಿನ್ನ ಬಂಧುಬಾಂಧವರಲ್ಲಿ ಈ ಹೆಸರಿನವರು ಒಬ್ಬರಾದರೂ ಇಲ್ಲವಲ್ಲಾ ಎಂದು ಆಕೆಗೆ ಹೇಳಿ",
			"62": "<sup>62</sup> ಇದಕ್ಕೆ ಏನು ಹೆಸರಿಡಬೇಕೆಂದಿರುತ್ತೀ ಎಂಬದಾಗಿ ಜಕರೀಯನಿಗೆ ಸನ್ನೆಮಾಡಿದರು.",
			"63": "<sup>63</sup> ಅವನು ಒಂದು ಹಲಗೆಯನ್ನು ತರಿಸಿಕೊಂಡು - ಆತನ ಹೆಸರು ಯೋಹಾನನು ಎಂದು ಬರೆದನು. ಅದಕ್ಕೆ ಎಲ್ಲರೂ ಆಶ್ಚರ್ಯಪಟ್ಟರು.",
			"64": "<sup>64</sup> ಕೂಡಲೆ ಅವನಿಗೆ ಬಾಯಿ ಬಂತು, ನಾಲಿಗೆ ಸಡಿಲವಾಯಿತು, ಅವನು ಮಾತಾಡುವ ಶಕ್ತಿಯುಳ್ಳವನಾಗಿ ದೇವರನ್ನು ಕೊಂಡಾಡಿದನು.",
			"65": "<sup>65</sup> ಇದನ್ನು ಕೇಳಿದ ನೆರೆಹೊರೆಯವರಿಗೆಲ್ಲಾ ಹೆದರಿಕೆಯುಂಟಾಯಿತು; ಮತ್ತು ಈ ಎಲ್ಲಾ ಸಂಗತಿಗಳನ್ನು ಕುರಿತು ಯೂದಾಯದ ಮಲೆನಾಡಿನಲ್ಲೆಲ್ಲಾ ಮಾತಾಡಿಕೊಳ್ಳುತ್ತಿದ್ದರು.",
			"66": "<sup>66</sup> ಕೇಳಿದವರೆಲ್ಲರೂ - ಆಹಾ, ಈ ಕೂಸು ಎಂಥವನಾಗುವನೋ? ಎಂದು ಈ ಸಂಗತಿಗಳನ್ನು ಮನಸ್ಸಿನಲ್ಲಿಟ್ಟುಕೊಂಡರು ಮತ್ತು ಕರ್ತನ ಅಭಯ ಹಸ್ತವು ಅವನ ಮೇಲೆ ಇತ್ತು.",
			"67": "<sup>67</sup> ಇದಲ್ಲದೆ ಆ ಮಗುವಿನ ತಂದೆಯಾದ ಜಕರೀಯನು ಪವಿತ್ರಾತ್ಮಭರಿತನಾಗಿ ಪ್ರವಾದನವಚನವನ್ನು ನುಡಿದದ್ದೇನಂದರೆ -",
			"68": "<sup>68</sup> ಇಸ್ರಾಯೇಲ್ ಜನರ ದೇವರಾಗಿರುವ ಕರ್ತನಿಗೆ ಸ್ತೋತ್ರವು; ಆತನು ಪೂರ್ವದಲ್ಲಿದ್ದ ತನ್ನ ಪರಿಶುದ್ಧ ಪ್ರವಾದಿಗಳ ಬಾಯಿಂದ ತಾನು ಹೇಳಿದಂತೆಯೇ ತನ್ನ ಪ್ರಜೆಯನ್ನು ಸಂಧಿಸಿ ಅವರಿಗೆ ಬಿಡುಗಡೆಯನ್ನುಂಟುಮಾಡಿದ್ದಾನೆ; ತನ್ನ ಸೇವಕನಾದ ದಾವೀದನ ಮನೆತನದಲ್ಲಿ ನಮಗೋಸ್ಕರ ಒಬ್ಬ ರಕ್ಷಣಾವೀರನನ್ನು ಎಬ್ಬಿಸಿದ್ದಾನೆ.",
			"71": "<sup>71</sup> ಆತನು ನಮ್ಮ ಪಿತೃಗಳಿಗೆ ದಯೆಯನ್ನು ತೋರಿಸುವವನಾಗಿಯೂ ನಮ್ಮ ಮೂಲಪಿತೃವಾದ ಅಬ್ರಹಾಮನಿಗೆ ಪ್ರಮಾಣಮಾಡಿ ಕೊಟ್ಟ ತನ್ನ ಪರಿಶುದ್ಧವಾದ ಒಡಂಬಡಿಕೆಯನ್ನು ನೆನಪಿಸಿಕೊಳ್ಳುವವನಾಗಿಯೂ ಇದ್ದು ನಮ್ಮ ವೈರಿಗಳಿಂದಲೂ ನಮ್ಮನ್ನು ದ್ವೇಷಿಸುವವರೆಲ್ಲರ ಕೈಯಿಂದಲೂ ನಮ್ಮನ್ನು ತಪ್ಪಿಸಿ ರಕ್ಷಿಸಿದ್ದಾನೆ.",
			"74": "<sup>74</sup> ಹೀಗೆ ನಾವು ನಮ್ಮ ವೈರಿಗಳ ಕೈಯಿಂದ ಬಿಡುಗಡೆಹೊಂದಿ ಭಯವಿಲ್ಲದವರಾಗಿದು ನಮ್ಮ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ನಿರ್ಮಲಚಿತ್ತದಿಂದಲೂ ನೀತಿಯಿಂದಲೂ ತನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸೇವೆಮಾಡುವಂತೆ ನಮಗೆ ಅನುಕೂಲಮಾಡಿದ್ದಾನೆ.",
			"76": "<sup>76</sup> ಮಗುವೇ, ನೀನಾದರೋ ಪರಾತ್ಪರನ ಪ್ರವಾದಿಯೆನಿಸಿಕೊಳ್ಳುವಿ. ನೀನು ಕರ್ತನ ಮುಂದೆ ಹೋಗಿ ಆತನ ಹಾದಿಗಳನ್ನು ಸಿದ್ಧಮಾಡುವವನಾಗಿಯೂ",
			"77": "<sup>77</sup> ನಮ್ಮ ದೇವರು ಅತ್ಯಂತಕರುಣೆಯಿಂದ ದಯಪಾಲಿಸುವ ಪಾಪಪರಿಹಾರವೆಂಬ ರಕ್ಷಣೆಯ ತಿಳುವಳಿಕೆಯನ್ನು ಆತನ ಪ್ರಜೆಗೆ ತೋರಿಸಿ ಕೊಡುವವನಾಗಿಯೂ ಇರುವಿ. ಆ ಕರುಣೆಯಿಂದಲೇ ನಮಗೆ ಮೇಲಿನಿಂದ ಅರುಣೋದಯವು ಉಂಟಾಗಿ ಅದು",
			"79": "<sup>79</sup> ಕತ್ತಲಲ್ಲಿಯೂ ಮರಣಾಂಧಕಾರದಲ್ಲಿಯೂ ವಾಸಿಸುತ್ತಿದ್ದರುವವರಾದ ನಮಗೆ ಬೆಳಕನ್ನುಕೊಟ್ಟು ನಮ್ಮ ಕಾಲುಗಳನ್ನು ಸಮಾಧಾನದ ಮಾರ್ಗದಲ್ಲಿ ಸೇರಿಸಿ ನಡಿಸುವದು ಅಂದನು.",
			"80": "<sup>80</sup> ಆ ಬಾಲಕನು ಬೆಳೆದು ಆತ್ಮದಲ್ಲಿ ಬಲವುಳ್ಳವನಾದನು. ಮತ್ತು ಇಸ್ರಾಯೇಲ್ ಜನರಿಗೆ ತನ್ನನ್ನು ತೋರ್ಪಡಿಸಿಕೊಳ್ಳುವ ದಿನದವರೆಗೂ ಅರಣ್ಯ ಪ್ರದೇಶಗಳಲ್ಲಿ ಇದ್ದನು."
		},
		"2": {
			"1": "<sup>1</sup> ಆ ಕಾಲದಲ್ಲಿ ರಾಜ್ಯವೆಲ್ಲಾ ಜನಗಣತಿಯನ್ನು ಬರಸಿಕೊಳ್ಳಬೇಕೆಂಬ ಆಜ್ಞೆಯು ಚಕ್ರವರ್ತಿಯಾದ ಅಗಸ್ತನಿಂದ ಹೊರಟಿತು.",
			"2": "<sup>2</sup> ಕುರೇನ್ಯನು ಸಿರಿಯಕ್ಕೆ ಅಧಿಪತಿಯಾಗಿದ್ದಾಗ ಈ ಮೊದಲನೆಯ ಜನಗಣತಿಯು ನಡೆಯಿತು.",
			"3": "<sup>3</sup> ಆಗ ಎಲ್ಲರೂ ಜನಗಣತಿಯನ್ನು ಬರೆಸಿಕೊಳ್ಳುವುದಕ್ಕಾಗಿ ತಮ್ಮತಮ್ಮ ಊರುಗಳಿಗೆ ಹೊರಟರು.",
			"4": "<sup>4</sup> ಯೋಸೇಫನು ಸಹ ತಾನು ದಾವೀದನ ಮನೆತನದವನೂ, ಗೋತ್ರದವನೂ ಆಗಿದ್ದದರಿಂದ ಹೆಸರು ಬರೆಸಿಕೊಳ್ಳುವುದಕ್ಕಾಗಿ ತನಗೆ ನಿಶ್ಚಿತಾರ್ಥವಾಗಿದ್ದ ಮರಿಯಳ ಸಂಗಡ ಗಲಿಲಾಯಸೀಮೆಯ ನಜರೇತ್ ಎಂಬ ಊರಿನಿಂದ ಹೊರಟು ಯೂದಾಯದಲ್ಲಿರುವ ಬೇತ್ಲೆಹೇಮೆಂಬ ದಾವೀದನೂರಿಗೆ ಹೋದರು.",
			"6": "<sup>6</sup> ಅವರು ಅಲ್ಲಿದ್ದಾಗ ಆಕೆಗೆ ಪ್ರಸವಕ್ಕೆ ದಿನತುಂಬಿತು.",
			"7": "<sup>7</sup> ಆಕೆಯು ತನ್ನ ಚೊಚ್ಚಲು ಮಗನನ್ನು ಹೆತ್ತು ಬಟ್ಟೆಯಲ್ಲಿ ಸುತ್ತಿ ಛತ್ರದಲ್ಲಿ ಅವರಿಗೆ ಸ್ಥಳವಿಲ್ಲದ ಕಾರಣ ಅವನನ್ನು ಗೋದಲಿಯಲ್ಲಿ ಮಲಗಿಸಿದಳು.",
			"8": "<sup>8</sup> ಆ ಸೀಮೆಯಲ್ಲಿ ಕುರುಬರು ಹೊಲದಲ್ಲಿದ್ದುಕೊಂಡು ರಾತ್ರಿಯಲ್ಲಿ ತಮ್ಮ ಕುರೀಹಿಂಡನ್ನು ಕಾಯುತ್ತಿರಲಾಗಿ",
			"9": "<sup>9</sup> ಕರ್ತನ ದೂತನೊಬ್ಬನು ಬಂದು ಅವರ ಎದುರಿನಲ್ಲಿ ನಿಂತನು. ಕರ್ತನ ಪ್ರಭೆಯು ಅವರ ಸುತ್ತಲು ಪ್ರಕಾಶಿಸಿತು; ಅವರು ಬಹಳವಾಗಿ ಹೆದರಿದರು.",
			"10": "<sup>10</sup> ಆ ದೂತನು ಅವರಿಗೆ - ಹೆದರಬೇಡಿರಿ, ಕೇಳಿರಿ; ಜನರಿಗೆಲ್ಲಾ ಮಹಾ ಸಂತೋಷವನ್ನುಂಟುಮಾಡುವ ಶುಭಸಮಾಚಾರವನ್ನು ನಿಮಗೆ ತಿಳಿಸುತ್ತೇನೆ.",
			"11": "<sup>11</sup> ಅದೇನಂದರೆ, ಈ ಹೊತ್ತು ನಿಮಗೋಸ್ಕರ ದಾವೀದನೂರಲ್ಲಿ ಒಬ್ಬ ರಕ್ಷಕನು ಹುಟ್ಟಿದ್ದಾನೆ. ಆತನು ಕರ್ತನಾಗಿರುವ ಕ್ರಿಸ್ತನೇ.",
			"12": "<sup>12</sup> ಅದರ ಗುರುತೇನಂದರೆ - ಬಟ್ಟೆಯಿಂದ ಸುತ್ತಿರುವ ಒಂದು ಕೂಸು ಗೋದಲಿಯಲ್ಲಿ ಮಲಗಿರುವದನ್ನು ನೀವು ಕಾಣುವಿರಿ ಎಂದು ಹೇಳಿದನು.",
			"13": "<sup>13</sup> ಫಕ್ಕನೆ ಆ ದೂತನ ಸಂಗಡ ಪರಲೋಕಸೈನ್ಯದವರ ಒಂದು ದೊಡ್ಡ ಗುಂಪು ಕಾಣಿಸಿ ಕೊಂಡು -",
			"14": "<sup>14</sup> ಮೇಲಣಲೋಕಗಳಲ್ಲಿ ದೇವರಿಗೆ ಮಹಿಮೆ, ಭೂಲೋಕದಲ್ಲಿ ಮನುಷ್ಯರೊಳಗೆ ಸಮಾಧಾನ ದೇವರು ಅವರಿಗೆ ಒಲಿಯುತ್ತಾನೆ ಎಂದು ದೇವರನ್ನು ಕೊಂಡಾಡುತ್ತಾ ಹೇಳಿದರು.",
			"15": "<sup>15</sup> ದೇವದೂತರು ಅವರ ಬಳಿಯಿಂದ ಪರಲೋಕಕ್ಕೆ ಹೊರಟುಹೋದ ಮೇಲೆ ಆ ಕುರುಬರು - ನಾವು ಈಗಲೇ ಬೇತ್ಲೆಹೇಮಿಗೆ ಹೋಗಿ ಕರ್ತನು ನಮಗೆ ತಿಳಿಯಪಡಿಸಿದ ಈ ಸಂಗತಿಯನ್ನು ನೋಡೋಣ ನಡೆಯಿರಿ ಎಂದು ತಮ್ಮತಮ್ಮೊಳಗೆ ಮಾತನಾಡಿಕೊಂಡು ತಟ್ಟನೆ ಹೋಗಿ",
			"16": "<sup>16</sup> ಮರಿಯಳನ್ನೂ ಯೋಸೇಫನನ್ನೂ ಗೋದಲಿಯಲ್ಲಿ ಮಲಗಿರುವ ಆ ಕೂಸನ್ನೂ ಕಂಡುಕೊಂಡರು.",
			"17": "<sup>17</sup> ಅದನ್ನು ನೋಡಿದಾಗ ಅವರು ಆ ಮಗುವಿನ ವಿಷಯದಲ್ಲಿ ದೇವದೂತನು ತಮಗೆ ಹೇಳಿದ್ದ ಮಾತನ್ನು ಅವರಿಗೆ ತಿಳಿಸಿದರು.",
			"18": "<sup>18</sup> ಅದನ್ನು ಕೇಳಿದವರೆಲ್ಲರೂ ತಮಗೆ ಕುರುಬರು ಹೇಳಿದ ಮಾತುಗಳಿಗೆ ಆಶ್ಚರ್ಯಪಟ್ಟರು.",
			"19": "<sup>19</sup> ಆದರೆ ಮರಿಯಳು ಆ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ತನ್ನ ಮನಸ್ಸಿನಲ್ಲಿಟ್ಟುಕೊಂಡು ಯೋಚಿಸುತ್ತಿದ್ದಳು.",
			"20": "<sup>20</sup> ಕುರುಬರು ತಮಗೆ ದೂತರಿಂದ ಹೇಳಲ್ಪಟ್ಟತಂತೆಯೇ ಎಲ್ಲಾ ಸಂಗತಿಗಳನ್ನು ಕೇಳಿ ನೋಡಿ ಅವುಗಳಿಗಾಗಿ ದೇವರನ್ನು ಕೊಂಡಾಡುತ್ತಾ ಸ್ತುತಿಸುತ್ತಾ ಹಿಂತಿರುಗಿ ಹೋದರು.",
			"21": "<sup>21</sup> ಆ ಕೂಸಿಗೆ ಸುನ್ನತಿಮಾಡುವುದಕ್ಕೆ ಕಳೆಯಬೇಕಾದ ಎಂಟು ದಿವಸಗಳು ತುಂಬಿದ ಮೇಲೆ ಸೂಚಿಸಿದ ಪ್ರಕಾರ ಯೇಸು ಎಂದು ಹೆಸರಿಟ್ಟನು. ಮಗುವು ಗರ್ಭದಲ್ಲಿ ರೂಪಿಸಲ್ಪಡುವದಕ್ಕಿಂತ ಮೊದಲೇ ದೇವದೂತನು ಅದೇ ಹೆಸರನ್ನು ಸೂಚಿಸಿದ್ದನು.",
			"22": "<sup>22</sup> ಬಳಿಕ ಮೋಶೆಯ ನಿಯಮದ ಪ್ರಕಾರ ಆಕೆಯ ಶುದ್ಧೀಕರಣದ ದಿವಸಗಳು ತುಂಬಲು ಅವರು ಆ ಕೂಸನ್ನು ಯೆರೂಸಲೇಮಿಗೆ ತೆಗೆದುಕೊಂಡು ಹೋದರು.",
			"23": "<sup>23</sup> ಯಾಕೆಂದರೆ ಚೊಚ್ಚಲು ಗಂಡೆಲ್ಲಾ ಕರ್ತನಿಗೆ ಮೀಸಲೆನಿಸಿಕೊಳ್ಳುವುದು ಎಂಬುದಾಗಿ ಕರ್ತನ ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿ ಬರೆದಿರುವಂತೆ ಅದನ್ನು ಕರ್ತನಿಗೆ ಸಮರ್ಪಿಸಬೇಕಾಗಿತ್ತು.",
			"24": "<sup>24</sup> ಇದಲ್ಲದೆ ಕರ್ತನ ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿ ಹೇಳಿರುವಂತೆ ಅವರು ಒಂದು ಜೋಡಿ ಬೆಳವಕ್ಕಿಯನ್ನಾಗಲಿ ಎರಡು ಪಾರಿವಾಳದ ಮರಿಗಳನ್ನಾಗಲಿ ಬಲಿಕೊಡಬೇಕಾಗಿತ್ತು.",
			"25": "<sup>25</sup> ಆ ಕಾಲದಲ್ಲಿ ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಸಿಮೆಯೋನನೆಂಬ ಒಬ್ಬ ಮನುಷ್ಯನಿದ್ದನು. ಈ ಮನುಷ್ಯನು ನೀತಿವಂತನೂ ದೇವಭಕ್ತನೂ ಆಗಿದ್ದು ಇಸ್ರಾಯೇಲ್ ಜನರನ್ನು ಸಂತೈಸುವವನು ಯಾವಾಗ ಬರುವನೊ ಎಂದು ನಿರೀಕ್ಷಿಸುತ್ತಾ ಇದ್ದನು; ಮತ್ತು ಆತನು ಪವಿತ್ರಾತ್ಮ ಪ್ರೇರಿತನಾಗಿದ್ದನು.",
			"26": "<sup>26</sup> ಇದಲ್ಲದೆ, ಕರ್ತನು ಕಳುಹಿಸಬೇಕಾದ ಕ್ರಿಸ್ತನನ್ನು ಕಾಣುವುದಕ್ಕಿಂತ ಮುಂಚೆ ನೀನು ಸಾಯುವುದಿಲ್ಲವೆಂದು ಪವಿತ್ರಾತ್ಮನ ಮೂಲಕ ಅವನಿಗೆ ದೈವೋಕ್ತಿ ಉಂಟಾಗಿತ್ತು.",
			"27": "<sup>27</sup> ಇವನು ಪವಿತ್ರಾತ್ಮನ ಪ್ರೇರಣೆಯಿಂದ ದೇವಾಲಯಕ್ಕೆ ಬಂದನು; ಆಗ ತಂದೆತಾಯಿಗಳು ಯೇಸುವೆಂಬ ಮಗುವಿಗೆ ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿ ಹೇಳಿರುವ ಪದ್ಧತಿಯ ಪ್ರಕಾರ ನಡಿಸಬೇಕೆಂದು ಮಗುವನ್ನು ಒಳಕ್ಕೆ ತರಲು",
			"28": "<sup>28</sup> ಅವನು ಆ ಮಗುವನ್ನು ತನ್ನ ಕೈಯಲ್ಲಿ ಎತ್ತಿಕೊಂಡು ದೇವರನ್ನು ಕೊಂಡಾಡುತ್ತಾ -",
			"29": "<sup>29</sup> ಕರ್ತನೇ, ನಿನ್ನ ಮಾತು ನೆರವೇರಿತು; ಈಗ ಸಮಾಧಾನದಿಂದ ಹೋಗುವದಕ್ಕೆ ನಿನ್ನ ದಾಸನಿಗೆ ಅಪ್ಪಣೆಯಾಯಿತು.",
			"30": "<sup>30</sup> ನೀನು ನೇಮಿಸಿರುವ ರಕ್ಷಕನನ್ನು ನಾನು ಕಣ್ಣಾರೆ ಕಂಡೆನು.",
			"31": "<sup>31</sup> ಎಲ್ಲಾ ಜನಗಳಿಗೆ ಅವನು ಪ್ರತ್ಯಕ್ಷನಾಗಲಿ ಎಂದು ಆತನನ್ನು ಒದಗಿಸಿಕೊಟ್ಟಿದ್ದೀ.",
			"32": "<sup>32</sup> ಆತನು ಅನ್ಯದೇಶದವರಿಗೆ ಜ್ಞಾನೋದಯದ ಬೆಳಕು, ನಿನ್ನ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಕೀರ್ತಿ ಅಂದನು.",
			"33": "<sup>33</sup> ಆ ಮಗುವಿನ ವಿಷಯವಾಗಿ ಹೇಳಿದ ಈ ಮಾತುಗಳಿಗೆ ಅದರ ತಂದೆತಾಯಿಗಳು ಆಶ್ಚರ್ಯಪಡುತ್ತಿರಲು",
			"34": "<sup>34</sup> ಸಿಮೆಯೋನನು ಅವರನ್ನು ಆಶೀರ್ವದಿಸಿ ತಾಯಿಯಾದ ಮರಿಯಳಿಗೆ - ಇಗೋ, ಈತನು ಇಸ್ರಾಯೇಲ್ ಜನರಲ್ಲಿ ಅನೇಕರು ಬೀಳುವುದಕ್ಕೂ, ಅನೇಕರು ಏಳುವುದಕ್ಕೂ ಕಾರಣನಾಗಿರುವನು; ಮತ್ತು ಜನರು ವಿರೋಧಿಸಿ ಮಾತಾಡುವದಕ್ಕೂ ಗುರುತಾಗಿರುವನು;",
			"35": "<sup>35</sup> ಹೀಗೆ ಬಹುಜನರ ಹೃದಯದ ಆಲೋಚನೆಗಳು ಬಹಿರಂಗವಾಗುವವು; ಈ ಕಾರಣಕ್ಕಾಗಿ ಈತನು ಹುಟ್ಟಿದ್ದು. ಇದಲ್ಲದೆ ನಿನ್ನ ಸ್ವಂತ ಮನಸ್ಸಿಗಂತೂ ವೇದನೆ ಉಂಟಾಗುತ್ತದೆ ಎಂದು ಹೇಳಿದನು.",
			"36": "<sup>36</sup> ಇದಲ್ಲದೆ ಅಸೇರನ ಕುಲದ ಫನುವೇಲನ ಮಗಳಾದ ಅನ್ನಳೆಂಬ ಒಬ್ಬ ಪ್ರವಾದಿನಿ ಇದ್ದಳು. ಆಕೆ ಬಹಳ ಮುಪ್ಪಿನವಳು; ಆಕೆ ತನ್ನ ಕನ್ಯಾವಸ್ಥೆ ಕಳೆದ ತರುವಾಯ ಗಂಡನ ಕೂಡ ಏಳು ವರುಷ ಬಾಳುವೆ ಮಾಡಿ",
			"37": "<sup>37</sup> ಎಂಭತ್ತುನಾಲ್ಕು ವರುಷ ವಿಧವೆಯಾಗಿದ್ದಳು. ಆಕೆ ದೇವಾಲಯವನ್ನು ಬಿಟ್ಟುಹೋಗದೆ ಉಪವಾಸ ವಿಜ್ಞಾಪನೆಗಳಿಂದ ರಾತ್ರಿ ಹಗಲೂ ದೇವರ ಸೇವೆಯನ್ನು ಮಾಡುತ್ತಿದ್ದಳು.",
			"38": "<sup>38</sup> ಆಕೆ ಅದೇ ಗಳಿಗೆಯಲ್ಲಿ ಹತ್ತಿರಕ್ಕೆ ಬಂದು ದೇವರಿಂದಾದ ಉಪಕಾರವನ್ನು ನೆನಸಿ ಕೊಂಡಾಡಿದ್ದಲ್ಲದೆ ಯೆರೂಸಲೇಮಿನ ಬಿಡುಗಡೆಯನ್ನು ಹಾರೈಸುತ್ತಿದ್ದವರೆಲ್ಲರ ಸಂಗಡ ಆತನ ವಿಷಯವಾಗಿ ಮಾತಾಡುವವಳಾದಳು.",
			"39": "<sup>39</sup> ಇತ್ತಲಾಗಿ ಅವರು ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿ ಹೇಳಿರುವ ಕಾರ್ಯಗಳನ್ನೆಲ್ಲಾ ನೆರವೇರಿಸಿದ ಮೇಲೆ ಗಲಿಲಾಯಕ್ಕೆ ಸೇರಿದ ನಜರೇತೆಂಬ ತಮ್ಮ ಊರಿಗೆ ಹಿಂತಿರುಗಿ ಹೋದರು.",
			"40": "<sup>40</sup> ಆ ಬಾಲಕನು ಬೆಳೆದು ಬಲಗೊಂಡು ಜ್ಞಾನದಿಂದ ತುಂಬಿದವನಾದನು; ಮತ್ತು ಆತನ ಮೇಲೆ ದೇವರ ದಯೆ ಇತ್ತು.",
			"41": "<sup>41</sup> ಯೇಸುವಿನ ತಂದೆತಾಯಿಗಳು ಪ್ರತಿವರುಷವೂ ಪಸ್ಕ ಹಬ್ಬದ ಜಾತ್ರೆಗೆ ಯೆರೂಸಲೇಮಿಗೆ ಹೋಗುತ್ತಿದ್ದರು.",
			"42": "<sup>42</sup> ಹೀಗಿರಲಾಗಿ ಆತನು ಹನ್ನೆರಡು ವರುಷದವನಾದಾಗ ಅವರು ಪದ್ಧತಿಯ ಪ್ರಕಾರ ಅಲ್ಲಿಗೆ ಹೋದರು.",
			"43": "<sup>43</sup> ಹಬ್ಬದ ದಿನಗಳನ್ನು ಮುಗಿಸಿಕೊಂಡು ಹಿಂತಿರುಗಿ ಬರುವಾಗ ಬಾಲಕನಾದ ಯೇಸು ಯೆರೂಸಲೇಮಿನಲ್ಲಿಯೇ ಉಳಿದುಕೊಂಡನು. ಅವನ ತಂದೆತಾಯಿಗಳು ಅದನ್ನು ತಿಳಿಯದೆ",
			"44": "<sup>44</sup> ಆತನು ಯಾತ್ರಿಕರ ಗುಂಪಿನಲ್ಲಿ ಸೇರಿರಬಹುದೆಂದು ಊಹಿಸಿ ಒಂದು ದಿನದ ಪ್ರಯಾಣ ಮಾಡಿ ಬಂಧುಬಾಂಧವರಲ್ಲಿಯೂ ಪರಿಚಿತರಲ್ಲಿಯೂ",
			"45": "<sup>45</sup> ಆತನನ್ನು ಹುಡುಕಿ ಕಾಣದೆ ತಿರಿಗಿ ಯೆರೂಸಲೇಮಿಗೆ ಹುಡುಕಿಕೊಂಡು ಹೋದರು.",
			"46": "<sup>46</sup> ಮೂರು ದಿನದ ಮೇಲೆ ಆತನನ್ನು ದೇವಾಲಯದಲ್ಲಿ ಕಂಡರು. ಆತನು ಬೋಧಕರ ನಡುವೆ ಕುಳಿತುಕೊಂಡು ಅವರ ಉಪದೇಶವನ್ನು ಕೇಳುತ್ತಾ ಪ್ರಶ್ನೆಮಾಡುತ್ತಾ ಇದ್ದನು.",
			"47": "<sup>47</sup> ಆತನು ಆಡಿದ ಮಾತುಗಳನ್ನು ಕೇಳಿದವರೆಲ್ಲರು ಆತನ ಬುದ್ಧಿಗೂ ಉತ್ತರಗಳಿಗೂ ಆಶ್ಚರ್ಯಪಟ್ಟರು.",
			"48": "<sup>48</sup> ತಂದೆತಾಯಿಗಳು ಆತನನ್ನು ಕಂಡು ಬೆರಗಾದರು; ಮತ್ತು ಆತನ ತಾಯಿಯು - ಕಂದಾ, ನೀನು ನಮಗೆ ಯಾಕೆ ಹೀಗೆ ಮಾಡಿದಿ? ನಿನ್ನ ತಂದೆಯೂ ನಾನೂ ಎಷ್ಟೋ ತಳಮಳಗೊಂಡು ನಿನ್ನನ್ನು ಹುಡುಕಿ ಬಂದೆವಲ್ಲಾ ಎಂದು ಹೇಳಲು",
			"49": "<sup>49</sup> ಆತನು ಅವರಿಗೆ - ನೀವು ನನ್ನನ್ನು ಹುಡುಕಿದ್ದೇನು? ನಾನು ನನ್ನ ತಂದೆಯ ಮನೆಯಲ್ಲಿ ಇರಬೇಕಾದವನು ಎಂದು ನಿಮಗೆ ತಿಳಿಯಲಿಲ್ಲವೇ? ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"50": "<sup>50</sup> ಆದರೆ ಆತನು ಹೇಳಿದ ಮಾತಿನ ಅರ್ಥವನ್ನು ಅವರು ಗ್ರಹಿಸಲಿಲ್ಲ.",
			"51": "<sup>51</sup> ಬಳಿಕ ಆತನು ಅವರ ಜೊತೆಯಲ್ಲಿ ನಜರೇತಿಗೆ ಬಂದು ಅವರಿಗೆ ವಿಧೇಯನಾಗಿದ್ದನು. ಆತನ ತಾಯಿಯು ಈ ಸಂಗತಿಗಳನ್ನೆಲ್ಲಾ ತನ್ನ ಮನಸ್ಸಿನಲ್ಲಿ ಇಟ್ಟುಕೊಂಡಳು.",
			"52": "<sup>52</sup> ಯೇಸು ಜ್ಞಾನದಲ್ಲಿಯೂ ದೇಹಬಲದಲ್ಲಿಯೂ ಬೆಳೆಯುತ್ತಾ ಬಂದನು; ಇದಲ್ಲದೆ ದೇವರ ಮತ್ತು ಮನುಷ್ಯರ ದಯೆಯು ಆತನ ಮೇಲೆ ಹೆಚ್ಚಾಗುತ್ತಾ ಬಂದಿತು."
		},
		"3": {
			"1": "<sup>1</sup> ಚಕ್ರವರ್ತಿಯಾದ ತಿಬೇರಿಯನು ಆಳ್ವಿಕೆಗೆ ಬಂದ ಹದಿನೈದನೆಯ ವರುಷದಲ್ಲಿ, ಪೊಂತ್ಯಪಿಲಾತನು ಯೂದಾಯಕ್ಕೆ ಅಧಿಪತಿಯೂ ಹೆರೋದನು ಗಲಿಲಾಯಕ್ಕೆ ಉಪರಾಜನೂ ಅವನ ತಮ್ಮನಾದ ಫಿಲಿಪ್ಪನು ಇತುರಾಯ ತ್ರಕೋನೀತಿ ಸೀಮೆಗಳಿಗೆ ಉಪರಾಜನೂ ಲುಸನ್ಯನು ಅಬಿಲೇನೆಗೆ ಉಪರಾಜನೂ ಆಗಿರುವಲ್ಲಿ",
			"2": "<sup>2</sup> ಹನ್ನನು ಮತ್ತು ಕಾಯಫನೂ ಮಹಾಯಾಜಕರಾಗಿದ್ದ ಕಾಲದಲ್ಲಿ ಜಕರೀಯನ ಮಗನಾದ ಯೋಹಾನನಿಗೆ ಅಡವಿಯಲ್ಲಿ ದೇವರ ವಾಕ್ಯವು ಪ್ರಕಟವಾಯಿತು.",
			"3": "<sup>3</sup> ಆತನು ಯೊರ್ದಾನ್ ಹೊಳೆಯ ಸುತ್ತಲಿರುವ ಪ್ರಾಂತ್ಯದಲ್ಲೆಲ್ಲಾ ಸಂಚರಿಸಿ ಜನರಿಗೆ - ನೀವು ಪಾಪ ಪರಿಹಾರಕ್ಕಾಗಿರುವ ಪಶ್ಚಾತ್ತಾಪದ ದೀಕ್ಷಾಸ್ನಾನ ಮಾಡಿಸಿಕೊಳ್ಳಬೇಕೆಂದು ಸಾರಿ ಹೇಳುತ್ತಿದ್ದನು.",
			"4": "<sup>4</sup> ಇದು ಯೆಶಾಯನೆಂಬ ಪ್ರವಾದಿಯ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುವ ಮಾತಿಗೆ ಅನುಸಾರವಾಗಿ ನಡೆಯಿತು. ಆ ಮಾತು ಏನಂದರೆ - “‘ಕರ್ತನ ದಾರಿಯನ್ನು ಸಿದ್ಧಮಾಡಿರಿ, ಆತನ ಹಾದಿಗಳನ್ನು ನೆಟ್ಟಗೆ ಮಾಡಿರಿ. ಎಲ್ಲಾ ಹಳ್ಳಕೊಳ್ಳಗಳು ಮುಚ್ಚಲ್ಪಡುವದು, ಎಲ್ಲಾ ಬೆಟ್ಟಗುಡ್ಡಗಳು ತಗ್ಗಿಸಲ್ಪಡುವದು, ಡೊಂಕಾಗಿರುವಂಥದು ನೆಟ್ಟಗಾಗುವದು, ಕೊರಕಲಾದ ದಾರಿಗಳು ಸಮವಾಗುವದು. ಎಲ್ಲಾ ಮನುಷ್ಯರೂ ದೇವರಿಂದ ಸಿದ್ಧವಾಗುವ ರಕ್ಷಣೆಯನ್ನು ಕಾಣುವರು ಎಂಬುದಾಗಿ ಅಡವಿಯಲ್ಲಿ ಕೂಗುವವನ ಶಬ್ದವದೆ’ ಎಂಬುದೇ.",
			"7": "<sup>7</sup> ಹೀಗಿರಲಾಗಿ ಯೋಹಾನನು ತನ್ನಿಂದ ದೀಕ್ಷಾಸ್ನಾನ ಮಾಡಿಸಿಕೊಳ್ಳುವುದಕ್ಕಾಗಿ ಹೊರಟುಬಂದ ಜನರ ಗುಂಪಿಗೆ - ಎಲೈ ಸರ್ಪಜಾತಿಯವರೇ, ಮುಂದೆ ಕಾಣಬರುವ ದೇವರ ಕೋಪದಿಂದ ತಪ್ಪಿಸಿಕೊಳ್ಳುವುದಕ್ಕೆ ನಿಮಗೆ ಉಪದೇಶ ಮಾಡಿದವರಾರು?",
			"8": "<sup>8</sup> ಹಾಗಾದರೆ ಮಾನಸಾಂತರಕ್ಕೆ ಯೋಗ್ಯವಾದ ಫಲಗಳನ್ನು ತೋರಿಸಿರಿ. ‘ಅಬ್ರಹಾಮನು ನಮಗೆ ತಂದೆಯಲ್ಲವೇ’ ಎಂದು ನಿಮ್ಮೊಳಗೆ ಅಂದುಕೊಳ್ಳಬೇಡಿರಿ. ದೇವರು ಅಬ್ರಹಾಮನಿಗೆ ಈ ಕಲ್ಲುಗಳಿಂದಲೂ ಮಕ್ಕಳನ್ನು ಹುಟ್ಟಿಸಬಲ್ಲನೆಂದು ನಿಮಗೆ ಹೇಳುತ್ತೇನೆ.",
			"9": "<sup>9</sup> ಈಗಲೇ ಮರಗಳ ಬೇರಿಗೆ ಕೊಡಲಿ ಹಾಕಿಯದೆ; ಒಳ್ಳೇ ಫಲವನ್ನು ಕೊಡದ ಪ್ರತಿಯೊಂದು ಮರವನ್ನು ಕಡಿದು ಬೆಂಕಿಯಲ್ಲಿ ಹಾಕುವರು ಎಂದು ಹೇಳಿದನು.",
			"10": "<sup>10</sup> ಆಗ ಆ ಗುಂಪಿನವರು - ಹಾಗಾದರೆ ನಾವೇನು ಮಾಡಬೇಕು? ಎಂದು ಆತನನ್ನು ಕೇಳಿದ್ದಕ್ಕೆ",
			"11": "<sup>11</sup> ಆತನು - ಎರಡು ಅಂಗಿಗಳುಳ್ಳವನು ಇಲ್ಲದವನಿಗೆ ಒಂದು ಕೊಡಲಿ; ಆಹಾರವುಳ್ಳವನು ಇಲ್ಲದವನಿಗೆ ಕೊಡಲಿ ಎಂದು ಅವರಿಗೆ ಹೇಳಿದನು.",
			"12": "<sup>12</sup> ತೆರಿಗೆವಸೂಲಿ ಮಾಡುವವರು ಸಹ ದೀಕ್ಷಾಸ್ನಾನ ಮಾಡಿಸಿಕೊಳ್ಳಬೇಕೆಂದು ಬಂದು - ಗುರುವೇ, ನಾವೇನು ಮಾಡಬೇಕು ಎಂದು ಆತನನ್ನು ಕೇಳಲು",
			"13": "<sup>13</sup> ಆತನು - ನೇಮಿಸಲ್ಪಟ್ಟಿದ್ದಕ್ಕಿಂತ ಹೆಚ್ಚಾಗಿ ಏನೂ ತೆಗೆದುಕೊಳ್ಳಬೇಡಿರಿ ಎಂದು ಅವರಿಗೆ ಹೇಳಿದನು.",
			"14": "<sup>14</sup> ಸಿಪಾಯಿಗಳು ಸಹ ಬಂದು - ನಾವು ಏನು ಮಾಡಬೇಕು ಎಂದು ಆತನನ್ನು ಕೇಳಿದಾಗ ಆತನು ಅವರಿಗೆ - ಯಾರನ್ನಾದರೂ ಬೆದರಿಸಿ ದುಡ್ಡು ಕಸಕೊಳ್ಳಬೇಡಿರಿ; ಯಾರ ಮೇಲೆಯೂ ಸುಳ್ಳುದೂರು ಹೇಳಬೇಡಿರಿ. ನಿಮ್ಮ ಸಂಬಳದಲ್ಲಿ ತೃಪ್ತರಾಗಿರಿ ಎಂದು ಹೇಳಿದನು.",
			"15": "<sup>15</sup> ಹೀಗಿರುವಲ್ಲಿ ಇಸ್ರಾಯೇಲ್ ಜನರು ಬರಬೇಕಾದ ಕ್ರಿಸ್ತನನ್ನು ಎದುರು ನೋಡುವವರಾಗಿದ್ದದರಿಂದ ಅವರೆಲ್ಲರೂ ಯೋಹಾನನ ವಿಷಯವಾಗಿ ಇವನೇ ಆ ಕ್ರಿಸ್ತನಾಗಿರಬಹುದೋ ಏನೋ ಎಂದು ತಮ್ಮ ಮನಸ್ಸಿನಲ್ಲಿ ವಿಚಾರಮಾಡಿಕೊಳ್ಳುತ್ತಿರಲು",
			"16": "<sup>16</sup> ಯೋಹಾನನು ಅವರೆಲ್ಲರಿಗೆ - ನಾನಂತೂ ನಿಮಗೆ ನೀರಿನ ಸ್ನಾನಮಾಡಿಸುವವನು; ಆದರೆ ನನಗಿಂತ ಶಕ್ತನು ಬರುತ್ತಾನೆ. ಆತನ ಕೆರಗಳ ಬಾರನ್ನು ಬಿಚ್ಚುವದಕ್ಕೂ ನಾನು ಯೋಗ್ಯನಲ್ಲ. ಆತನು ಪವಿತ್ರಾತ್ಮನಲ್ಲಿಯೂ ಮತ್ತು ಬೆಂಕಿಯಲ್ಲಿಯೂ ನಿಮಗೆ ಸ್ನಾನಮಾಡಿಸುವನು.",
			"17": "<sup>17</sup> ಆತನು ಮೊರವನ್ನು ಕೈಯಲ್ಲಿ ಹಿಡಿದಿದ್ದಾನೆ; ತನ್ನ ಕಣದಲ್ಲಿಯ ರಾಶಿಯನ್ನು ತೂರಿ ಹಸನುಮಾಡಿ ಗೋಧಿಯನ್ನು ತನ್ನ ಕಣಜದಲ್ಲಿ ತುಂಬಿಕೊಳ್ಳುವುದಕ್ಕಿದ್ದಾನೆ, ಹೊಟ್ಟನ್ನು ಆರದ ಬೆಂಕಿಯಲ್ಲಿ ಸುಟ್ಟುಬಿಡುವನು ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"18": "<sup>18</sup> ಇನ್ನು ಬೇರೆ ಅನೇಕ ಮಾತುಗಳಿಂದ ಆತನು ಇಸ್ರಾಯೇಲ್ ಜನರಿಗೆ ಬುದ್ಧಿಹೇಳಿ ಸುವಾರ್ತೆಯನ್ನು ಸಾರುತ್ತಿದ್ದನು.",
			"19": "<sup>19</sup> ಆದರೆ ಉಪರಾಜನಾದ ಹೆರೋದನು ತನ್ನ ಅಣ್ಣನ ಹೆಂಡತಿಯಾದ ಹೆರೋದ್ಯಳ ನಿಮಿತ್ತವಾಗಿಯೂ, ತಾನು ಮಾಡಿದ್ದ ಎಲ್ಲಾ ದುಷ್ಕೃತ್ಯಗಳ ನಿಮಿತ್ತವಾಗಿಯೂ",
			"20": "<sup>20</sup> ಯೋಹಾನನಿಂದ ಗದರಿಸಲ್ಪಟ್ಟಿದರಿಂದ ಆತನನ್ನು ಸೆರೆಮನೆಯಲ್ಲಿ ಹಾಕಿಸಿ ತನ್ನ ದುಷ್ಕೃತ್ಯಗಳ ಪಟ್ಟಿಯನ್ನು ಇನ್ನೂ ಹೆಚ್ಚಿಸಿಕೊಂಡನು.",
			"21": "<sup>21</sup> ಜನರೆಲ್ಲಾ ದೀಕ್ಷಾಸ್ನಾನ ಮಾಡಿಸಿಕೊಂಡಾಗ ಯೇಸು ಸಹ ದೀಕ್ಷಾಸ್ನಾನ ಮಾಡಿಸಿಕೊಂಡು ಪ್ರಾರ್ಥನೆ ಮಾಡುತ್ತಿರುವಾಗ ಆಕಾಶವು ತೆರೆಯಲ್ಪಟ್ಟಿತು;",
			"22": "<sup>22</sup> ಮತ್ತು ಪವಿತ್ರಾತ್ಮನು ದೇಹಾಕಾರನಾಗಿ ಪಾರಿವಾಳದ ಹಾಗೆ ಆತನ ಮೇಲೆ ಇಳಿದನು. ಆಗ - ನೀನು ಪ್ರಿಯನಾಗಿರುವ ನನ್ನ ಮಗನು, ನಿನ್ನನ್ನು ನಾನು ಮೆಚ್ಚಿದ್ದೇನೆ ಎಂಬ ವಾಣಿಯು ಪರಲೋಕದಿಂದ ಬಂತು.",
			"23": "<sup>23</sup> ಯೇಸು ಉಪದೇಶಮಾಡುವದಕ್ಕೆ ಪ್ರಾರಂಭಿಸಿದಾಗ ಹೆಚ್ಚುಕಡಿಮೆ ಮೂವತ್ತು ವರುಷದವನಾಗಿದ್ದನು. ಆತನು ಜನರ ಎಣಿಕೆಯಲ್ಲಿ ಯೋಸೇಫನ ಮಗನು.",
			"24": "<sup>24</sup> ಯೋಸೆಫನು ಹೇಲಿಯನ ಮಗನು; ಇವನು ಮತ್ಥಾತನು ಮಗನು; ಇವನು ಲೇವಿಯ ಮಗನು; ಇವನು ಮೆಲ್ಖಿಯ ಮಗನು; ಇವನು ಯನ್ನಾಯನ ಮಗನು;",
			"25": "<sup>25</sup> ಇವನು ಯೋಸೇಫನ ಮಗನು; ಇವನು ಮತ್ತಥೀಯನ ಮಗನು; ಇವನು ಆಮೋಸನ ಮಗನು; ಇವನು ನಹೂಮನ ಮಗನು; ಇವನು ಎಸ್ಲಿಯ ಮಗನು;",
			"26": "<sup>26</sup> ಇವನು ನಗ್ಗಾಯನ ಮಗನು; ಇವನು ಮಹಾಥನ ಮಗನು; ಇವನು ಮತ್ತಥೀಯನ ಮಗನು; ಇವನು ಶಿಮಿಯನ ಮಗನು; ಇವನು ಯೋಸೇಖನ ಮಗನು;",
			"27": "<sup>27</sup> ಇವನು ಯೂದನ ಮಗನು; ಇವನು ಯೋಹಾನನ ಮಗನು; ಇವನು ರೇಸನ ಮಗನು; ಇವನು ಜೆರುಬಾಬೆಲನ ಮಗನು, ಇವನು ಸಲಥಿಯೇಲನ ಮಗನು; ಇವನು ನೇರಿಯನ ಮಗನು;",
			"28": "<sup>28</sup> ಇವನು ಮೆಲ್ಖಿಯನ ಮಗನು; ಇವನು ಅದ್ದಿಯನ ಮಗನು; ಇವನು ಕೋಸಾಮನ ಮಗನು; ಇವನು ಎಲ್ಮದಾಮನ ಮಗನು; ಇವನು ಏರನ ಮಗನು;",
			"29": "<sup>29</sup> ಇವನು ಯೆಹೋಷುವನ ಮಗನು; ಇವನು ಎಲಿಯೇಜರನ ಮಗನು; ಇವನು ಯೋರೈಮನ ಮಗನು; ಇವನು ಮತ್ಥಾತನ ಮಗನು; ಇವನು ಲೇವಿಯನ ಮಗನು;",
			"30": "<sup>30</sup> ಇವನು ಸಿಮಿಯೋನನ ಮಗನು; ಇವನು ಯೂದನ ಮಗನು; ಇವನು ಯೋಸೇಫನ ಮಗನು; ಇವನು ಯೋನಾಮನ ಮಗನು; ಇವನು ಎಲಿಯಕೀಮನ ಮಗನು;",
			"31": "<sup>31</sup> ಇವನು ಮೆಲೆಯಾನ ಮಗನು; ಇವನು ಮೆನ್ನನ ಮಗನು; ಇವನು ಮತ್ತಾಥನ ಮಗನು; ಇವನು ನಾಥಾನನ ಮಗನು; ಇವನು ದಾವೀದನ ಮಗನು;",
			"32": "<sup>32</sup> ಇವನು ಇಶಾಯನ ಮಗನು; ಇವನು ಓಬೇದನ ಮಗನು; ಇವನು ಬೋವಜನ ಮಗನು; ಇವನು ಸಲ್ಮೋನನ ಮಗನು; ಇವನು ನಹಸ್ಸೋನನ ಮಗನು;",
			"33": "<sup>33</sup> ಇವನು ಅಮ್ಮಿನಾದಾಬನ ಮಗನು; ಇವನು ಆರ್ನೈಯನ ಮಗನು; ಇವನು ಎಸ್ರೋನನ ಮಗನು; ಇವನು ಪೆರೆಸನ ಮಗನು;",
			"34": "<sup>34</sup> ಇವನು ಯೂದನ ಮಗನು; ಇವನು ಯಾಕೋಬನ ಮಗನು; ಇವನು ಇಸಾಕನ ಮಗನು; ಇವನು ಅಬ್ರಹಾಮನ ಮಗನು; ಇವನು ತೇರನ ಮಗನು; ಇವನು ನಾಹೋರನ ಮಗನು;",
			"35": "<sup>35</sup> ಇವನು ಸೆರೂಗನ ಮಗನು; ಇವನು ರೆಗೂವನ ಮಗನು; ಇವನು ಪೆಲೆಗನ ಮಗನು; ಇವನು ಹೇಬೆರನ ಮಗನು; ಇವನು ಸಾಲನ ಮಗನು;",
			"36": "<sup>36</sup> ಇವನು ಕಯಿನಾನನ ಮಗನು; ಇವನು ಅರ್ಫಕ್ಷಾದನ ಮಗನು; ಇವನು ಶೇಮನ ಮಗನು; ಇವನು ನೋಹನ ಮಗನು; ಇವನು ಲಾಮೆಕನ ಮಗನು;",
			"37": "<sup>37</sup> ಇವನು ಮತೂಷಲನ ಮಗನು; ಇವನು ಹನೋಕನ ಮಗನು; ಇವನು ಯೆರೆದನ ಮಗನು; ಇವನು ಮಹಲಲೇಲನ ಮಗನು; ಇವನು ಕಯಿನಾನನ ಮಗನು;",
			"38": "<sup>38</sup> ಇವನು ಎನೋಷನ ಮಗನು; ಇವನು ಸೇಥನ ಮಗನು; ಇವನು ಆದಾಮನ ಮಗನು ಇವನು ದೇವರ ಮಗನು."
		},
		"4": {
			"1": "<sup>1</sup> ಯೇಸು ಪವಿತ್ರಾತ್ಮಭರಿತನಾಗಿ ಯೊರ್ದನ್ ಹೊಳೆಯಿಂದ ಹಿಂತಿರುಗಿ ಬಂದು ದೇವರಾತ್ಮನಿಂದ ನಾಲ್ವತ್ತು ದಿವಸ",
			"2": "<sup>2</sup> ಅಡವಿಯಲ್ಲಿ ನಡಿಸಲ್ಪಡುತ್ತಾ ಸೈತಾನನಿಂದ ಶೋಧಿಸಲ್ಪಟ್ಟನು. ಆ ದಿವಸಗಳಲ್ಲಿ ಆತನು ಏನನ್ನೂ ತಿನ್ನಲಿಲ್ಲ. ಅದು ಮುಗಿದ ಮೇಲೆ ಆತನಿಗೆ ಹಸಿವಾಯಿತು.",
			"3": "<sup>3</sup> ಆಗ ಸೈತಾನನು ಆತನಿಗೆ - ನೀನು ದೇವರ ಮಗನಾಗಿದ್ದರೆ ಈ ಕಲ್ಲನ್ನು ರೊಟ್ಟಿಯಾಗುವಂತೆ ಅಪ್ಪಣೆ ಕೊಡು ಎಂದು ಹೇಳಲು",
			"4": "<sup>4</sup> ಯೇಸು ಅವನಿಗೆ, ಮನುಷ್ಯನು ರೊಟ್ಟಿ ತಿಂದ ಮಾತ್ರದಿಂದ ಬದುಕುವುದಿಲ್ಲ ಎಂದು ಬರೆದದೆ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"5": "<sup>5</sup> ಬಳಿಕ ಸೈತಾನನು ಆತನನ್ನು ಮೇಲಕ್ಕೆ ಕರೆದು ಕೊಂಡು ಹೋಗಿ, ಕ್ಷಣಮಾತ್ರದಲ್ಲಿ ಲೋಕದ ಎಲ್ಲಾ ರಾಜ್ಯಗಳನ್ನು ಆತನಿಗೆ ತೋರಿಸಿ,",
			"6": "<sup>6</sup> ಇವೆಲ್ಲವುಗಳ ಅಧಿಕಾರವನ್ನೂ ಇವುಗಳ ವೈಭವವನ್ನೂ ನಿನಗೆ ಕೊಡುವೆನು; ಇದೆಲ್ಲಾ ನನಗೆ ಕೊಡಲ್ಪಟ್ಟಿದೆ, ಇದನ್ನು ನನ್ನ ಮನಸ್ಸು ಬಂದವನಿಗೆ ಕೊಡುತ್ತೇನೆ;",
			"7": "<sup>7</sup> ನೀನು ನನ್ನ ಮುಂದೆ ಅಡ್ಡಬಿದ್ದರೆ ಇದೆಲ್ಲಾ ನಿನ್ನದಾಗುವುದು ಎಂದು ಆತನಿಗೆ ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ಅದಕ್ಕೆ ಯೇಸು - ‘ನಿನ್ನ ದೇವರಾಗಿರುವ ಕರ್ತನನ್ನು ಆರಾಧಿಸಿ ಆತನೊಬ್ಬನನ್ನೇ ಸೇವಿಸಬೇಕು’ ಎಂಬುದಾಗಿ ಬರೆದದೆ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"9": "<sup>9</sup> ಇದಲ್ಲದೆ ಸೈತಾನನು ಆತನನ್ನು ಯೆರೂಸಲೇಮಿಗೆ ಕರೆದುಕೊಂಡು ಹೋಗಿ ದೇವಾಲಯದ ಗೋಪುರದ ತುದಿಯಲ್ಲಿ ನಿಲ್ಲಿಸಿ ಆತನಿಗೆ - ನೀನು ದೇವರ ಮಗನಾಗಿದ್ದರೆ ಇಲ್ಲಿಂದ ಕೆಳಕ್ಕೆ ಧುಮುಕು.",
			"10": "<sup>10</sup> ‘ನಿನ್ನನ್ನು ಕಾಯುವದಕ್ಕೆ ಕರ್ತನು ತನ್ನ ದೂತರಿಗೆ ಅಪ್ಪಣೆ ಕೊಡುವನು;",
			"11": "<sup>11</sup> ಮತ್ತು ನಿನ್ನ ಕಾಲು ಕಲ್ಲಿಗೆ ತಗಲದಂತೆ ಅವರು ನಿನ್ನನ್ನು ಕೈಗಳಲ್ಲಿ ಎತ್ತಿಕೊಳ್ಳುವರು’ ಎಂಬದಾಗಿ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದದೆಯಲ್ಲಾ ಎಂದು ಹೇಳಿದನು.",
			"12": "<sup>12</sup> ಅದಕ್ಕೆ ಆತನು - ನಿನ್ನ ದೇವರಾಗಿರುವ ಕರ್ತನನ್ನು ಪರೀಕ್ಷಿಸಬಾರದೆಂದು ಹೇಳಿದೆ ಎಂದು ಉತ್ತರ ಕೊಟ್ಟನು.",
			"13": "<sup>13</sup> ಹೀಗೆ ಸೈತಾನನು ನಾನಾ ವಿಧದಲ್ಲಿ ಶೋಧಿಸಿದ ನಂತರ, ತಕ್ಕ ಸಮಯ ಬರುವ ತನಕ ಆತನನ್ನು ಬಿಟ್ಟು ಹೊರಟುಹೋದನು.",
			"14": "<sup>14</sup> ತರುವಾಯ ಯೇಸು ಪವಿತ್ರಾತ್ಮನ ಶಕ್ತಿಯಿಂದ ಕೂಡಿದವನಾಗಿ ಗಲಿಲಾಯಕ್ಕೆ ಹಿಂತಿರುಗಿದನು; ಆತನ ಸುದ್ದಿಯು ಸುತ್ತಲಿರುವ ಪ್ರಾಂತ್ಯದಲ್ಲೆಲ್ಲಾ ಹರಡಿತು.",
			"15": "<sup>15</sup> ಆತನು ಅವರ ಸಭಾಮಂದಿರಗಳಲ್ಲಿ ಬೋಧಿಸುತ್ತಿದ್ದನು; ಎಲ್ಲರೂ ಆತನನ್ನು ಹೊಗಳಿದರು.",
			"16": "<sup>16</sup> ಹೀಗಿರಲಾಗಿ ಆತನು ತಾನು ಬೆಳೆದ ನಜರೇತಿಗೆ ಬಂದು ತನ್ನ ವಾಡಿಕೆಯ ಪ್ರಕಾರ ಸಬ್ಬತ್‍ ದಿನದಲ್ಲಿ ಸಭಾಮಂದಿರದೊಳಗೆ ಹೋಗಿ ಪವಿತ್ರಗ್ರಂಥ ಓದುವುದಕ್ಕಾಗಿ ಎದ್ದು ನಿಂತನು.",
			"17": "<sup>17</sup> ಆಗ ಯೆಶಾಯನೆಂಬ ಪ್ರವಾದಿಯು ಬರೆದ ಗ್ರಂಥದ ಸುರಳಿಯನ್ನು ಆತನ ಕೈಗೆ ಕೊಡಲಾಗಿ ಆತನು ಆ ಸುರಳಿಯನ್ನು ಬಿಚ್ಚಿ ಓದಿದನು; ಆ ಭಾಗದಲ್ಲಿ ಬರೆದಿರುವ ಸಂಗತಿಯೇನೆಂದರೆ -",
			"18": "<sup>18</sup> ಕರ್ತನ ಆತ್ಮವು ನನ್ನ ಮೇಲೆ ಅದೆ, ಆತನು ನನ್ನನ್ನು ಬಡವರಿಗೆ ಶುಭವಾರ್ತೆಯನ್ನು ಸಾರುವುದಕ್ಕಾಗಿ ಅಭಿಷೇಕಿಸಿರುವನು. ಸೆರೆಯಲ್ಲಿರುವವರನ್ನು ಬಿಡುಗಡೆಮಾಡುವುದಕ್ಕು, ಮತ್ತು ಕುರುಡರಿಗೆ ಕಣ್ಣು ಬರುವುದಕ್ಕೂ ಹಿಂಸಿಸಲ್ಪಟ್ಟವರನ್ನು ಬಿಡಿಸಿ ಕಳುಹಿಸುವದಕ್ಕೂ",
			"19": "<sup>19</sup> ಕರ್ತನು ನೇಮಿಸಿರುವ ಶುಭವರ್ಷವನ್ನು ಪ್ರಚಾರಪಡಿಸುವದಕ್ಕೂ ಆತನು ನನ್ನನ್ನು ಕಳುಹಿಸಿದ್ದಾನೆ ಎಂಬುದೇ.",
			"20": "<sup>20</sup> ಅದನ್ನು ಓದಿದ ಮೇಲೆ ಆತನು ಆ ಸುರಳಿಯನ್ನು ಸುತ್ತಿ ಸಭಾಮಂದಿರದ ಸೇವಕನ ಕೈಗೆ ಕೊಟ್ಟು ಕೂಳಿತುಕೊಂಡನು. ಅಲ್ಲಿ ನೆರೆದ್ದಿದವರೆಲ್ಲರ ಕಣ್ಣುಗಳೂ ಆತನ ಮೇಲಿರಲಾಗಿ",
			"21": "<sup>21</sup> ಆತನು ಅವರಿಗೆ - ಈ ಹೊತ್ತು ನೀವು ನನ್ನ ಮಾತನ್ನು ಕೇಳುವಲ್ಲಿ ಈ ವೇದೋಕ್ತಿ ನೆರವೇರಿದೆ ಎಂದು ಹೇಳುತ್ತಿರುವಾಗ",
			"22": "<sup>22</sup> ಎಲ್ಲರು ಆತನನ್ನು ಹೊಗಳಿ ಆತನ ಬಾಯಿಂದ ಹೊರಡುವ ಇಂಪಾದ ಮಾತುಗಳಿಗೆ ಆಶ್ಚರ್ಯಪಟ್ಟು, ಇವನು ಯೋಸೇಫನ ಮಗನಲ್ಲವೇ? ಎಂದು ಮಾತನಾಡಿಕೊಂಡರು.",
			"23": "<sup>23</sup> ಅದಕ್ಕೆ ಯೇಸು ಅವರಿಗೆ - ವೈದ್ಯನೇ, ನಿನ್ನನ್ನು ನೀನೇ ವಾಸಿಮಾಡಿಕೋ ಎಂಬ ಗಾದೆಯನ್ನು ನನಗೆ ನಿಸ್ಸಂದೇಹವಾಗಿ ಹೇಳಿ; ಕಪೆರ್ನೌಮಿನಲ್ಲಿ ನೀನು ಮಾಡಿದ ಕಾರ್ಯಗಳನ್ನು ನಾವು ಕೇಳಿದ್ದೇವೆ ಅಂಥವುಗಳನ್ನು ಈ ನಿನ್ನ ಸ್ವಂತ ಸ್ಥಳದಲ್ಲಿಯೂ ಮಾಡು ಎಂದು ಹೇಳುವಿರಿ.",
			"24": "<sup>24</sup> ಯಾವ ಪ್ರವಾದಿಯೂ ತನ್ನ ಸ್ವಂತ ದೇಶದಲ್ಲಿ ಅಂಗಿಕರಿಸಲ್ಪಡುವುದಿಲ್ಲವೆಂದು ನಿಮಗೆ ಸತ್ಯವಾಗಿ ಹೇಳುತ್ತೇನೆ.",
			"25": "<sup>25</sup> ಕೇಳಿರಿ, ಎಲೀಯನ ಕಾಲದಲ್ಲಿ ಮೂರು ವರುಷ ಆರು ತಿಂಗಳು ಮಳೆಬಾರದೆ ದೇಶದಲ್ಲೆಲ್ಲಾ ದೊಡ್ಡ ಬರಗಾಲ ಉಂಟಾದಾಗ ಇಸ್ರಾಯೇಲಿನಲ್ಲೇ ಅನೇಕ ವಿಧವೆಯರು ಇದ್ದದ್ದು ನಿಜ;",
			"26": "<sup>26</sup> ಆದರೆ ಅವರಲ್ಲಿ ಒಬ್ಬರ ಬಳಿಗೂ ದೇವರು ಎಲೀಯನನ್ನು ಕಳುಹಿಸದೆ ಸೀದೋನ್‍ ದೇಶಕ್ಕೆ ಸೇರಿದ ಸರೆಪ್ತ ಊರಿನಲ್ಲಿದ್ದ ಒಬ್ಬ ವಿಧವೆಯ ಬಳಿಗೆ ಮಾತ್ರ ಅವನನ್ನು ಕಳುಹಿಸಿದನು.",
			"27": "<sup>27</sup> ಮತ್ತು ಎಲೀಷನೆಂಬ ಪ್ರವಾದಿಯ ಕಾಲದಲ್ಲಿ ಇಸ್ರಾಯೇಲ್ ಜನರೊಳಗೆ ಅನೇಕ ಕುಷ್ಠರೋಗಿಗಳು ಇದ್ದಾಗ್ಯೂ, ಅವರಲ್ಲಿ ಒಬ್ಬನಾದರೂ ಶುದ್ಧನಾಗದೆ ಸಿರಿಯ ದೇಶದವನಾದ ನಾಮಾನನು ಮಾತ್ರ ಶುದ್ಧನಾದನು ಎಂದು ಹೇಳಿದನು.",
			"28": "<sup>28</sup> ಸಭಾಮಂದಿರದಲ್ಲಿರುವವರೆಲ್ಲರೂ ಈ ಮಾತುಗಳನ್ನು ಕೇಳಿ ಬಹಳವಾಗಿ ಸಿಟ್ಟುಗೊಂಡು ಎದ್ದು ಆತನನ್ನು ಊರಹೊರಕ್ಕೆ ಅಟ್ಟಿ",
			"29": "<sup>29</sup> ತಮ್ಮ ಊರು ಕಟ್ಟಲ್ಪಟ್ಟಿದ್ದ ಗುಡ್ಡದ ಕಡಿದಾದ ಸ್ಥಳಕ್ಕೆ ನಡಿಸಿಕೊಂಡು ಹೋಗಿ ಅಲ್ಲಿಂದ ಕೆಳಕ್ಕೆ ದೊಬ್ಬಿಬಿಡಬೇಕೆಂದಿದ್ದರು",
			"30": "<sup>30</sup> ಆದರೆ ಆತನು ಅವರ ಮಧ್ಯದಿಂದ ನಡೆದು ತನ್ನ ಪಾಡಿಗೆ ತಾನು ಹೊರಟು ಹೋದನು.",
			"31": "<sup>31</sup> ಬಳಿಕ ಆತನು ಘಟ್ಟಾ ಇಳಿದು ಗಲಿಲಾಯಕ್ಕೆ ಸೇರಿದ ಕಪೆರ್ನೌಮೆಂಬ ಊರಿಗೆ ಬಂದನು.",
			"32": "<sup>32</sup> ಅಲ್ಲಿ ಸಬ್ಬತ್‍ದಿನದಲ್ಲಿ ಅವರಿಗೆ ಉಪದೇಶಮಾಡುತ್ತಿರಲು ಆತನ ಮಾತು ಅಧಿಕಾರವುಳ್ಳದ್ದಾಗಿದ್ದದರಿಂದ ಅವರು ಆತನ ಉಪದೇಶಕ್ಕೆ ಅತ್ಯಾಶ್ಚರ್ಯಪಟ್ಟರು.",
			"33": "<sup>33</sup> ಆ ಸಭಾಮಂದಿರದಲ್ಲಿ ದೆವ್ವಹಿಡಿದ ಒಬ್ಬ ಮನುಷ್ಯನಿದ್ದನು.",
			"34": "<sup>34</sup> ಆ ದೆವ್ವವು - ನಜರೇತಿನ ಯೇಸುವೇ, ನಮ್ಮ ಗೊಡವೆ ನಿನಗೇಕೆ? ನಮ್ಮನ್ನು ನಾಶಮಾಡುವದಕ್ಕೆ ಬಂದೆಯಾ? ನಿನ್ನನ್ನು ಬಲ್ಲೆವು. ನೀನು ದೇವರ ಪರಿಶುದ್ದನೇ ಎಂದು ಕೂಗಿ ಹೇಳಿತು.",
			"35": "<sup>35</sup> ಯೇಸು ಅದನ್ನು ಗದರಿಸಿ - ಸುಮ್ಮನಿರು, ಇವನನ್ನು ಬಿಟ್ಟು ಹೋಗು ಎನ್ನಲಾಗಿ ಆ ದೆವ್ವವು ಅವನನ್ನು ಎಲ್ಲರ ನಡುವೆ ಬೀಳಿಸಿ ಅವನಿಗೆ ಯಾವ ಕೇಡನ್ನೂ ಮಾಡದೆ ಬಿಟ್ಟುಹೋಯಿತು.",
			"36": "<sup>36</sup> ಅದಕ್ಕೆ ಎಲ್ಲರು ಬೆರಗಾಗಿ - ಇದೆಂಥ ಮಾತಾಗಿರಬಹುದು? ಈತನು ಅಧಿಕಾರದಿಂದಲೂ, ಬಲದಿಂದಲೂ ದೆವ್ವಗಳಿಗೆ ಅಪ್ಪಣೆಕೊಡುತ್ತಾನೆ, ಅವು ಬಿಟ್ಟು ಹೋಗುತ್ತವೆ ಎಂದು ತಮ್ಮ ತಮ್ಮೊಳಗೆ ಮಾತಾಡಿಕೊಂಡರು.",
			"37": "<sup>37</sup> ಮತ್ತು ಆತನ ಸುದ್ದಿಯು ಸುತ್ತಲಿರುವ ಪ್ರಾಂತ್ಯದ ಊರುಗಳಲ್ಲೆಲ್ಲಾ ಹರಡಿತು.",
			"38": "<sup>38</sup> ಆತನು ಸಭಾಮಂದಿರದಿಂದೆದ್ದು ಸೀಮೋನನ ಮನೆಗೆ ಬಂದನು. ಅಲ್ಲಿ ಸೀಮೋನನ ಅತ್ತೆಯು ಕಠಿಣ ಜ್ವರದಿಂದ ಕಷ್ಟಪಡುತ್ತಿರಲಾಗಿ; ಆಕೆಯ ವಿಷಯದಲ್ಲಿ ಅಲಿದ್ದವರು ಆತನನ್ನು ಬೇಡಿಕೊಂಡರು.",
			"39": "<sup>39</sup> ಆತನು ಆಕೆಯ ಬಳಿ ನಿಂತು ಬಾಗಿ, ಜ್ವರವನ್ನು ಬಿಟ್ಟುಹೋಗೆಂದು ಗದರಿಸಲು; ಅದು ಆಕೆಯನ್ನು ಬಿಟ್ಟುಹೋಯಿತು. ಕೂಡಲೆ ಆಕೆಯು ಎದ್ದು ಅವರೆಲ್ಲರಿಗೆ ಉಪಚಾರ ಮಾಡಿದಳು.",
			"40": "<sup>40</sup> ಸಂಜೆಯಾದಂತೆ ಜನರು ತಮ್ಮ ತಮ್ಮ ಮನೆಯಲ್ಲಿ ವಿವಿಧ ರೋಗಗಳಿಂದ ಅಸ್ವಸ್ಥವಾದವರನ್ನೆಲ್ಲಾ ಆತನ ಹತ್ತಿರಕ್ಕೆ ಕರೆತರಲು; ಆತನು ಪ್ರತಿಯೊಬ್ಬರ ಮೇಲೆಯೂ ಕೈಯಿಟ್ಟು ಅವರನ್ನು ವಾಸಿಮಾಡಿದನು.",
			"41": "<sup>41</sup> ದೆವ್ವಗಳು ಸಹ - ನೀನು ದೇವ ಕುಮಾರನು ಎಂದು ಅಬ್ಬರಿಸಿ ಅನೇಕರನ್ನು ಬಿಟ್ಟುಹೋದವು. ಆತನು ಕ್ರಿಸ್ತನೆಂದು ದೆವ್ವಗಳಿಗೆ ಸ್ಪಷ್ಟವಾಗಿ ತಿಳಿದಿದ್ದರಿಂದ ಆತನು ಅವುಗಳನ್ನು ಮಾತನಾಡದಂತೆ ಗದರಿಸಿದನು.",
			"42": "<sup>42</sup> ಬೆಳಗಾದ ಮೇಲೆ ಆತನು ಹೊರಟು ಅಡವಿಯ ಸ್ಥಳಕ್ಕೆ ಹೋದನು; ಜನರು ಗುಂಪುಗುಂಪಾಗಿ ಆತನನ್ನು ಹುಡುಕಿಕೊಂಡು ಆತನಿದ್ದಲ್ಲಿಗೆ ಬಂದು - ನೀನು, ನಮ್ಮನ್ನು ಬಿಟ್ಟುಹೋಗಬೇಡ ಎಂದು ಆತನನ್ನು ತಡೆದರು.",
			"43": "<sup>43</sup> ಆದರೆ ಆತನು ಅವರಿಗೆ - ನಾನು ದೇವರ ರಾಜ್ಯದ ಸುವಾರ್ತೆಯನ್ನು ಬೇರೆ ಊರುಗಳಿಗೂ ಸಾರಿ ಹೇಳಬೇಕಾಗಿದೆ; ಇದಕ್ಕಾಗಿಯೇ ಕಳುಹಿಸಲ್ಪಟ್ಟಿದ್ದೇನೆ ಎಂದು ಹೇಳಿದನು.",
			"44": "<sup>44</sup> ಬಳಿಕ ಆತನು ಯೂದಾಯ ದೇಶದ ಸಭಾಮಂದಿರಗಳಲ್ಲಿ ಸುವಾರ್ತೆಯನ್ನು ಸಾರುತ್ತಾ ಇದ್ದನು."
		},
		"5": {
			"1": "<sup>1</sup> ಒಂದಾನೊಂದು ದಿವಸ ಜನರು ಗುಂಪಾಗಿ ಬಂದು ಯೇಸುವನ್ನು ಸುತ್ತುವರೆದು ದೇವರ ವಾಕ್ಯವನ್ನು ಕೇಳುತ್ತಿರಲು",
			"2": "<sup>2</sup> ಗೆನೇಜರೆತ್ ಕೆರೆಯ ದಡದಲ್ಲಿ ನಿಂತಿದ್ದ ಆತನು ಆ ಕೆರೆಯ ದಡದಲ್ಲಿ ಎರಡು ದೋಣಿಗಳನ್ನು ಕಂಡನು. ಬೆಸ್ತರು ಅವುಗಳೊಳಗಿಂದ ಹೊರಗೆ ಬಂದು ತಮ್ಮ ಬಲೆಗಳನ್ನು ತೊಳೆಯುತ್ತಿದ್ದರು.",
			"3": "<sup>3</sup> ಆ ದೋಣಿಗಳಲ್ಲಿ ಸೀಮೋನನ ಒಂದು ದೋಣಿಯನ್ನು ಯೇಸು ಹತ್ತಿ ದಡದಿಂದ ಸ್ವಲ್ಪ ದೂರಕ್ಕೆ ನೂಕಬೇಕೆಂದು ಅವನನ್ನು ಕೇಳಿಕೊಂಡನು. ತರುವಾಯ ಯೇಸು ಕುಳಿತುಕೊಂಡು ದೋಣಿಯೊಳಗಿಂದಲೇ ಆ ಜನರ ಗುಂಪಿಗೆ ಉಪದೇಶಮಾಡಿದನು.",
			"4": "<sup>4</sup> ಮಾತನಾಡುವುದನ್ನು ಮುಗಿಸಿದ ಮೇಲೆ ಸೀಮೋನನಿಗೆ - ಆಳವಾದ ಸ್ಥಳಕ್ಕೆ ದೋಣಿಯನ್ನು ನಡೆಸಿ ಮೀನುಹಿಡಿಯಲು ನಿಮ್ಮ ಬಲೆಗಳನ್ನು ಬೀಸಿರಿ ಎಂದು ಹೇಳಿದನು.",
			"5": "<sup>5</sup> ಅದಕ್ಕೆ ಸೀಮೋನನು - ಗುರುವೇ, ನಾವು ರಾತ್ರಿಯೆಲ್ಲಾ ಪ್ರಯತ್ನಪಟ್ಟರೂ ಏನೂ ಸಿಕ್ಕಲಿಲ್ಲ; ಆದರೆ ನಿನ್ನ ಮಾತಿನ ಮೇಲೆ ಬಲೆಗಳನ್ನು ಬೀಸುತ್ತೇವೆ ಅಂದನು.",
			"6": "<sup>6</sup> ಅವರು ಬಲೆಗಳನ್ನು ಬೀಸಿದ ಮೇಲೆ ಮೀನುಗಳು ರಾಶಿರಾಶಿಯಾಗಿ ಬಲೆಗೆ ಸಿಕ್ಕಿಕೊಂಡಿದ್ದರ ಪರಿಣಾಮ ಬಲೆಗಳು ಹರಿದುಹೋಗುವಂತ್ತಿದವು.",
			"7": "<sup>7</sup> ಆಗ ಅವರು ಮತ್ತೊಂದು ದೋಣಿಯಲ್ಲಿದ್ದ ತಮ್ಮ ಸಂಗಡಿಗರನ್ನೂ ಕರೆದು - ನೀವು ಬಂದು ನಮಗೆ ನೆರವಾಗಬೇಕೆಂದು ಸನ್ನೆಮಾಡಿದರು. ಅವರು ಬಂದು ಆ ಎರಡು ದೋಣಿಗಳಲ್ಲಿ ಮೀನುಗಳನ್ನು ತುಂಬಿಸಲು ಅವು ಮುಳುಗುವ ಹಾಗಾದವು.",
			"8": "<sup>8</sup> ಸೀಮೋನ್ ಪೇತ್ರನು ಇದನ್ನು ಕಂಡು ಯೇಸುವಿನ ಮೊಣಕಾಲಿಗೆ ಬಿದ್ದು ಕರ್ತನೇ, ನಾನು ಪಾಪಾತ್ಮನು; ನನ್ನನ್ನು ಬಿಟ್ಟುಹೋಗಬೇಕು ಅಂದನು.",
			"9": "<sup>9</sup> ಯಾಕೆಂದರೆ ಅವನಿಗೂ ಅವನ ಸಂಗಡ ಇದ್ದವರೆಲ್ಲರಿಗೂ ತಾವು ಹಿಡಿದ ಮೀನುಗಳ ನಿಮಿತ್ತ ಆಶ್ಚರ್ಯದಿಂದ ಬೆರಗಾದರು;",
			"10": "<sup>10</sup> ಸೀಮೋನನ ಸಂಗಡಿಗರಾಗಿದ್ದ ಜೆಬೆದಾಯನ ಮಕ್ಕಳಾದ ಯಾಕೋಬ ಮತ್ತು ಯೋಹಾನರೂ ಹಾಗೆಯೇ ಆಶ್ಚರ್ಯಪಟ್ಟರು. ಯೇಸು ಸೀಮೋನನಿಗೆ - ಅಂಜಬೇಡ, ಇಂದಿನಿಂದ ನೀನು ಮನುಷ್ಯರನ್ನು ಹಿಡಿಯುವವನಾಗಿರುವಿ ಎಂದು ಹೇಳಿದನು.",
			"11": "<sup>11</sup> ಅವರು ತಮ್ಮ ದೋಣಿಗಳನ್ನು ತಂದು ದಡಕ್ಕೆ ಬಿಟ್ಟು ಮತ್ತು ಎಲ್ಲವನ್ನು ಬಿಟ್ಟು ಅಂದಿನಿಂದ ಯೇಸುವನ್ನು ಹಿಂಬಾಲಿಸಿದರು.",
			"12": "<sup>12</sup> ಆತನು ಒಂದಾನೊಂದು ಊರಿನಲ್ಲಿದ್ದಾಗ ಮೈಯೆಲ್ಲಾ ಕುಷ್ಠರೋಗ ತುಂಬಿದ್ದ ಒಬ್ಬ ಮನುಷ್ಯನು ಯೇಸುವನ್ನು ಕಂಡು ಅಡ್ಡ ಬಿದ್ದು - ಸ್ವಾಮಿ, ನಿನಗೆ ಮನಸ್ಸಿದ್ದರೆ ನನ್ನನ್ನು ಶುದ್ಧಮಾಡಬಲ್ಲೆ ಎಂದು ಬೇಡಿಕೊಂಡನು.",
			"13": "<sup>13</sup> ಆತನು ಕೈನೀಡಿ ಅವನನ್ನು ಮುಟ್ಟಿ - ನನಗೆ ಮನಸ್ಸುಂಟು; ಶುದ್ಧನಾಗು ಅಂದನು. ಕೂಡಲೆ ಅವನ ಕುಷ್ಠವು ವಾಸಿಯಾಯಿತು.",
			"14": "<sup>14</sup> ಆಗ ಆತನು ಅವನಿಗೆ - ನೀನು ಯಾರಿಗೂ ಹೇಳಬೇಡ; ಆದರೆ ಹೋಗಿ ಯಾಜಕನಿಗೆ ನಿನ್ನ ಮೈ ತೋರಿಸಿ ಮೋಶೆಯು ಆಜ್ಞೆ ಮಾಡಿರಿವ ಕಾಣಿಕೆಯನ್ನು ಅರ್ಪಿಸಿ ನಿನ್ನ ಶುದ್ಧಾಚಾರವನ್ನು ನೆರವೇರಿಸು, ಅದು ಜನರಿಗೆ ಸಾಕ್ಷಿಯಾಗಲಿ ಎಂದು ಅಪ್ಪಣೆಕೊಟ್ಟನು.",
			"15": "<sup>15</sup> ಆದರೂ ಆತನ ಸುದ್ದಿಯು ಮತ್ತಷ್ಟು ಹಬ್ಬಿತು; ಮತ್ತು ಜನರು ಯೇಸುವಿನ ಉಪದೇಶವನ್ನು ಕೇಳುವದಕ್ಕೂ ತಮ್ಮ ತಮ್ಮ ಜಾಡ್ಯಗಳನ್ನು ವಾಸಿಮಾಡಿಸಿಕೊಳ್ಳುವುದಕ್ಕೂ ಗುಂಪುಗುಂಪಾಗಿ ಬಂದು ಸೇರಿದರು.",
			"16": "<sup>16</sup> ಆದರೆ ಯೇಸು ಜನರಿಂದ ಪ್ರತ್ಯೇಕಿಸಿಕೊಂಡು ಅರಣ್ಯ ಪ್ರದೇಶಗಳಿಗೆ ಹೋಗಿ ಪ್ರಾರ್ಥನೆಯನ್ನು ಮಾಡುತ್ತಿದ್ದನು.",
			"17": "<sup>17</sup> ಒಂದಾನೊಂದು ದಿನದಲ್ಲಿ ಆತನು ಉಪದೇಶಮಾಡುತ್ತಿರಲು ಗಲಿಲಾಯ ಮತ್ತು ಯೂದಾಯದ ಎಲ್ಲಾ ಗ್ರಾಮಗಳಿಂದಲೂ ಯೆರೂಸಲೇಮಿನಿಂದಲೂ ಬಂದಿದ್ದ ಫರಿಸಾಯರೂ ನ್ಯಾಯಶಾಸ್ತ್ರಿಗಳು ಆತನ ಹತ್ತಿರ ಕೂತುಕೊಂಡಿದ್ದರು. ಮತ್ತು ಅವರನ್ನು ಗುಣಮಾಡುವ ದೇವರ ಶಕ್ತಿಯು ಆತನಲ್ಲಿತ್ತು.",
			"18": "<sup>18</sup> ಹೀಗಿರುವಲ್ಲಿ ಕೆಲವು ಮಂದಿ ಗಂಡಸರು ಒಬ್ಬ ಪಾರ್ಶ್ವವಾಯುರೋಗದವನನ್ನು ಮಂಚದ ಮೇಲೆ ಹೊತ್ತುಕೊಂಡು ಬಂದರು; ಅವನನ್ನು ಒಳಗೆ ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ಆತನ ಮುಂದೆ ಇರಿಸಬೇಕೆಂದು ಪ್ರಯತ್ನಿಸಿದರೂ",
			"19": "<sup>19</sup> ಜನರ ಗುಂಪು ದಟ್ಟವಾಗಿದ್ದ ಕಾರಣ ಅವನನ್ನು ಒಳಗೆ ತೆಗೆದುಕೊಂಡುಹೋಗುವ ಮಾರ್ಗವನ್ನು ಕಾಣದೆ ಮನೆಯ ಮೇಲೆ ಹತ್ತಿ ಹಂಚುಗಳನ್ನು ತೆಗೆದು ಅವನನ್ನು ಹಾಸಿಗೆಯ ಸಹಿತವಾಗಿ ಜನರ ನಡುವೆ ಯೇಸುವಿನ ಮುಂದೆ ಇಳಿಸಿದರು.",
			"20": "<sup>20</sup> ಯೇಸು ಅವರ ನಂಬಿಕೆಯನ್ನು ನೋಡಿ - ಅವನಿಗೆ ನಿನ್ನ ಪಾಪಗಳು ಕ್ಷಮಿಸಲ್ಪಟ್ಟಿವೆ ಅಂದನು.",
			"21": "<sup>21</sup> ಅದಕ್ಕೆ ಆ ಶಾಸ್ತ್ರಿಗಳೂ ಮತ್ತು ಫರಿಸಾಯರೂ - ಧರ್ಮನಿಂದೆಯ ಮಾತನ್ನಾಡುವ ಇವನು ಎಷ್ಟರವನು? ಪಾಪಗಳನ್ನು ಕ್ಷಮಿಸಲು ದೇವರೊಬ್ಬನಿಂದ ಹೊರತು ಮತ್ತಾರಿಂದಾದೀತು ಎಂದು ಅಂದುಕೊಳ್ಳುತ್ತಿದ್ದರು.",
			"22": "<sup>22</sup> ಆದರೆ ಅವರು ಹಾಗಂದುಕೊಳ್ಳುವುದನ್ನು ಯೇಸುವು ತಿಳಿದುಕೊಂಡು ಅವರಿಗೆ - ನೀವು ನಿಮ್ಮ ಮನಸ್ಸಿನಲ್ಲಿ ಅಂದುಕೊಳ್ಳುವದೇನು?",
			"23": "<sup>23</sup> ಯಾವದು ಸುಲಭ? ನಿನ್ನ ಪಾಪಗಳು ಕ್ಷಮಿಸಲ್ಪಟ್ಟಿವೆ ಅನ್ನುವದೋ? ಎದ್ದು ನಡೆ ಅನ್ನುವದೋ?",
			"24": "<sup>24</sup> ಆದರೆ ಪಾಪಗಳನ್ನು ಕ್ಷಮಿಸುವುದಕ್ಕೆ ಮನುಷ್ಯ ಕುಮಾರನಿಗೆ ಭೂಲೋಕದಲ್ಲಿ ಅಧಿಕಾರ ಉಂಟೆಂಬದು ನಿಮಗೆ ತಿಳಿಯಬೇಕು ಎಂದು ಹೇಳಿ ಆ ಪಾರ್ಶ್ವವಾಯು ರೋಗದವನನ್ನು ನೋಡಿ - ಎದ್ದು ನಿನ್ನ ಹಾಸಿಗೆಯನ್ನು ಎತ್ತಿಕೊಂಡು ಮನೆಗೆ ಹೋಗೆಂದು ಹೇಳಿದನು.",
			"25": "<sup>25</sup> ಕೂಡಲೆ ಅವನು ಅವರ ಮುಂದೆ ಎದ್ದು ತಾನು ಮಲಗಿದ್ದ ಹಾಸಿಗೆಯನ್ನು ಎತ್ತಿಕೊಂಡು ದೇವರನ್ನು ಕೊಂಡಾಡುತ್ತಾ ಮನೆಗೆ ಹೋದನು.",
			"26": "<sup>26</sup> ನೋಡಿದವರೆಲ್ಲರೂ ಬೆರಗಾಗಿ ದೇವರನ್ನು ಕೊಂಡಾಡಿದರು; ಮತ್ತು ಅವರು ಭಯ ಹಿಡಿದವರಾಗಿ - ನಾವು ಈ ಹೊತ್ತು ಅಪೂರ್ವ ಸಂಗತಿಯನ್ನು ಕಂಡೆವು ಅಂದರು.",
			"27": "<sup>27</sup> ನಂತರ ಆತನು ಹೊರಟು ಸುಂಕಕ್ಕೆ ಕುಳಿತಿದ್ದ ಲೇವಿಯೆಂಬ ಒಬ್ಬ ಸುಂಕದವನನ್ನು ಕಂಡು - ನನ್ನನ್ನು ಹಿಂಬಾಲಿಸು ಎಂದು ಅವನನ್ನು ಕರೆಯಲು",
			"28": "<sup>28</sup> ಅವನು ಎಲ್ಲವನ್ನೂ ಬಿಟ್ಟು ಎದ್ದು ಆತನ ಹಿಂದೆ ಹೋದನು.",
			"29": "<sup>29</sup> ತರುವಾಯ ಲೇವಿಯು ತನ್ನ ಮನೆಯಲ್ಲಿ ಆತನಿಗೆ ಒಂದು ದೊಡ್ಡ ಔತಣವನ್ನು ಮಾಡಿರಲು ಸುಂಕದವರೂ ಇತರರೂ ಗುಂಪಾಗಿ ಕೂಡಿ ಬಂದು ಅವರ ಸಂಗಡ ಊಟಕ್ಕೆ ಕುಳಿತುಕೊಂಡಿದ್ದರು.",
			"30": "<sup>30</sup> ಅದನ್ನು ಕಂಡು ಫರಿಸಾಯರೂ ಅವರಲ್ಲಿದ್ದ ಶಾಸ್ತ್ರಿಗಳೂ ಆತನ ಶಿಷ್ಯರ ಮೇಲೆ ಗುಣುಗುಟ್ಟುತ್ತಾ - ನೀವು ಸಂಕದವರ ಮತ್ತು ಪಾಪಿಗಳ ಸಂಗಡ ಊಟಮಾಡುವದೇಕೆ ಎಂದು ಕೇಳಿದರು.",
			"31": "<sup>31</sup> ಅದಕ್ಕೆ ಯೇಸು - ಕ್ಷೇಮದಿಂದಿರುವವರಿಗೆ ವೈದ್ಯನು ಬೇಕಾಗಿಲ್ಲ, ಕ್ಷೇಮವಿಲ್ಲದವರಿಗೆ ಬೇಕು;",
			"32": "<sup>32</sup> ದೇವರ ಕಡೆಗೆ ತಿರುಗಿಕೊಳ್ಳಿರಿ, ನೀತಿವಂತರನ್ನು ಕರೆಯುವುದಕ್ಕೆ ನಾನು ಬಂದವನಲ್ಲ, ಪಾಪಿಗಳನ್ನು ಕರೆಯುವುದಕ್ಕೆ ಬಂದವನು ಎಂದು ಅವರಿಗೆ ಉತ್ತರಕೊಟ್ಟನು.",
			"33": "<sup>33</sup> ಅವರು ಆತನಿಗೆ - ಯೋಹಾನನ ಶಿಷ್ಯರು ಪದೇಪದೇ ಉಪವಾಸದಿಂದ ವಿಜ್ಞಾಪನೆಗಳನ್ನು ಮಾಡುತ್ತಾ ಇರುತ್ತಾರೆ; ಅದರಂತೆ ಫರಿಸಾಯರ ಶಿಷ್ಯರೂ ಮಾಡುತ್ತಾರೆ; ಆದರೆ ನಿನ್ನ ಶಿಷ್ಯರು ಊಟಮಾಡುತ್ತಾರೆ",
			"34": "<sup>34</sup> ಕುಡಿಯುತ್ತಾರೆ ಎಂದು ಹೇಳಲು, ಯೇಸು ಅವರಿಗೆ - ಮದಲಿಂಗನು ಮದುವೆ ಜನರ ಸಂಗಡ ಇರುವಲ್ಲಿ ಅವರಿಗೆ ಉಪವಾಸಮಾಡಿಸುವದಕ್ಕೆ ನಿಮ್ಮಿಂದಾದೀತೇ?",
			"35": "<sup>35</sup> ಆದರೆ ಮದಲಿಂಗನನ್ನು ಅವರ ಬಳಿಯಿಂದ ತೆಗೆಯಲ್ಪಡುವ ಕಾಲ ಬರುತ್ತದೆ. ಆ ಕಾಲದಲ್ಲೆ ಅವರು ಉಪವಾಸಮಾಡುವರು ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"36": "<sup>36</sup> ಅವರಿಗೆ ಒಂದು ಸಾಮ್ಯವನ್ನು ಸಹ ಹೇಳಿದನು, ಅದೇನಂದರೆ - ಯಾರೂ ಹೊಸ ವಸ್ತ್ರದಿಂದ ಒಂದು ತುಂಡನ್ನು ತೆಗೆದುಕೊಂಡು ಹಳೇ ವಸ್ತ್ರಕ್ಕೆ ತ್ಯಾಪೆ ಹಚ್ಚುವುದಿಲ್ಲ; ಹಚ್ಚಿದರೆ ಆ ಹೊಸದನ್ನು ಹರಿದು ಕೆಡಿಸಿದ ಹಾಗಾಗುವದಲ್ಲದೆ ಹೊಸದರಿಂದ ಹರಿದು ಹಚ್ಚಿದ ತ್ಯಾಪೆಯು ಹಳೆಯ ವಸ್ತ್ರಕ್ಕೆ ಒಪ್ಪುವದೂ ಇಲ್ಲ.",
			"37": "<sup>37</sup> ಮತ್ತು ಹಳೇ ಬುದ್ದಲಿಗಳಲ್ಲಿ ಹೊಸ ದ್ರಾಕ್ಷಾರಸವನ್ನು ಯಾರೂ ಹಾಕಿಡುವದಿಲ್ಲ; ಹಾಗೆ ಇಟ್ಟರೆ ಆ ಹೊಸ ದ್ರಾಕ್ಷಾರಸವು ಬುದ್ದಲಿಗಳನ್ನು ಒಡೆದು ಚೆಲ್ಲಿಹೋಗುವದಲ್ಲದೆ ಬುದ್ದಲಿಗಳೂ ಒಡೆದು ಹೋಗುವವು.",
			"38": "<sup>38</sup> ಆದರೆ ಹೊಸ ದ್ರಾಕ್ಷಾರಸವನ್ನು ಹೊಸ ಬುದ್ದಲಿಗಳಲ್ಲಿ ಹಾಕಿಡತಕ್ಕದ್ದು.",
			"39": "<sup>39</sup> ಇದಲ್ಲದೆ ಹಳೇ ದ್ರಾಕ್ಷಾರಸವನ್ನು ಕುಡಿದವನು ಹೊಸದು ಬೇಕು ಅನ್ನುವದಿಲ್ಲ; ಹಳೇದೇ ಉತ್ತಮ ಅನ್ನುವನು."
		},
		"6": {
			"1": "<sup>1</sup> ಒಂದಾನೊಂದು ಸಬ್ಬತ್‍ದಿನದಲ್ಲಿ ಯೇಸು ಪೈರಿನ ಹೊಲಗಳನ್ನು ಹಾದುಹೋಗುತ್ತಿರುವಾಗ ಆತನ ಶಿಷ್ಯರು ತೆನೆಗಳನ್ನು ಮುರಿದು ಕೈಗಳಲ್ಲಿ ಹೊಸಗಿ ಕೊಂಡು ತಿನ್ನುತ್ತಿದ್ದರು.",
			"2": "<sup>2</sup> ಆದರೆ ಫರಿಸಾಯರಲ್ಲಿ ಕೆಲವರು - ಸಬ್ಬತ್‍ದಿನದಲ್ಲಿ ನೀವು ಮಾಡಬಾರದ ಕೆಲಸವನ್ನು ಯಾಕೆ ಮಾಡುತ್ತೀರಿ ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ",
			"3": "<sup>3</sup> ಯೇಸು ಅವರಿಗೆ - ದಾವೀದನು ತಾನೂ ತನ್ನ ಸಂಗಡ ಇದ್ದವರೂ ಹಸಿವಾದಾಗ ಏನು ಮಾಡಿದನೆಂಬದನ್ನು ನೀವು ಓದಲಿಲ್ಲವೋ?",
			"4": "<sup>4</sup> ಅವನು ದೇವರ ಮಂದಿರದೊಳಕ್ಕೆ ಹೋಗಿ ಯಾಜಕರು ಮಾತ್ರವೇ ಹೊರತು ಮತ್ತಾರೂ ತಿನ್ನಬಾರದ ನೈವೇದ್ಯದ ರೊಟ್ಟಿಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ತಾನೂ ತಿಂದು ತನ್ನ ಸಂಗಡ ಇದ್ದವರಿಗೂ ಕೊಟ್ಟನಲ್ಲಾ? ಎಂದು ಉತ್ತರಿಸಿ,",
			"5": "<sup>5</sup> ಮನುಷ್ಯಕುಮಾರನು ಸಬ್ಬತ್‍ದಿನಕ್ಕೆ ಒಡೆಯನಾಗಿದ್ದಾನೆ ಎಂದು ಅವರಿಗೆ ಹೇಳಿದನು.",
			"6": "<sup>6</sup> ಮತ್ತೊಂದು ಸಬ್ಬತ್‍ದಿನದಲ್ಲಿ ಆತನು ಸಭಾಮಂದಿರಕ್ಕೆ ಹೋಗಿ ಉಪದೇಶಮಾಡುತ್ತಿರುವಾಗ ಅಲ್ಲಿ ಬಲಗೈ ಬತ್ತಿದವನೊಬ್ಬನು ಇದ್ದನು.",
			"7": "<sup>7</sup> ಶಾಸ್ತ್ರಿಗಳೂ ಫರಿಸಾಯರೂ ಆತನ ಮೇಲೆ ತಪ್ಪುಹೊರಿಸುವದಕ್ಕೆ ಕಾರಣ ಸಿಕ್ಕಬೇಕೆಂದು ಸಬ್ಬತ್‍ದಿನದಲ್ಲಿ ಸ್ವಸ್ಥಮಾಡುವನೋ ಇಲ್ಲವೋ ಎಂದು ಆತನನ್ನು ಹೊಂಚಿನೋಡುತ್ತಿದ್ದರು.",
			"8": "<sup>8</sup> ಆದರೆ ಆತನು ಅವರ ಯೋಚನೆಗಳನ್ನು ತಿಳಿದು ಕೈ ಬತ್ತಿಹೋಗಿದ್ದ ಆ ಮನುಷ್ಯನಿಗೆ - ಎದ್ದು ನಡುವೆ ಬಂದು ನಿಂತುಕೋ ಎಂದು ಹೇಳಿದನು.",
			"9": "<sup>9</sup> ಅವನು ಎದ್ದು ನಿಂತುಕೊಂಡನು. ಆಗ ಯೇಸು ಅವರಿಗೆ - ನಿಮ್ಮನ್ನು ಒಂದು ಮಾತು ಕೇಳುತ್ತೇನೆ; ಸಬ್ಬತ್‍ದಿನದಲ್ಲಿ ಯಾವುದನ್ನು ಮಾಡುವದು ನ್ಯಾಯ? ಮೇಲನ್ನು ಮಾಡುವುದೋ ಕೇಡನ್ನು ಮಾಡುವುದೋ? ಪ್ರಾಣವನ್ನು ಉಳಿಸುವುದೋ ಹಾಳುಮಡುವುದೋ ಎಂದು ಕೇಳಿ ಸುತ್ತಲೂ ಇದ್ದವರೆಲ್ಲರನ್ನೂ ನೋಡಿ",
			"10": "<sup>10</sup> ಆ ಮನುಷ್ಯನಿಗೆ - ‘ನಿನ್ನ ಕೈಚಾಚು’ ಎಂದು ಹೇಳಿದನು. ಅವನು ಹಾಗೆಯೇ ಮಾಡಿದನು; ಕೈವಾಸಿಯಾಯಿತು",
			"11": "<sup>11</sup> ಆದರೆ ಅವರಲ್ಲಿ ದುರ್ಬುದ್ಧಿ ತುಂಬಿದ್ದದರಿಂದ - ಈ ಯೇಸುವಿಗೆ ಏನು ಮಾಡೋಣ ಎಂದು ತಮ್ಮತಮ್ಮೊಳಗೆ ಮಾತನಾಡಿಕೊಂಡರು.",
			"12": "<sup>12</sup> ಆ ದಿನಗಳಲ್ಲಿ ಯೇಸುವು ಪ್ರಾರ್ಥನೆ ಮಾಡಲು ಬೆಟ್ಟಕ್ಕೆ ಹೋಗಿ ರಾತ್ರಿಯೆಲ್ಲಾ ಪ್ರಾರ್ಥನೆಯಲ್ಲೇ ಕಳೆದನು.",
			"13": "<sup>13</sup> ಬೆಳಗಾದ ಮೇಲೆ ಆತನು ತನ್ನ ಶಿಷ್ಯರನ್ನು ಹತ್ತಿರಕ್ಕೆ ಕರೆದು ಅವರಲ್ಲಿ ಹನ್ನೆರಡು ಮಂದಿಯನ್ನು ಆರಿಸಿಕೊಂಡು ಅವರಿಗೆ ಅಪೊಸ್ತಲರೆಂದು ಹೆಸರಿಟ್ಟನು.",
			"14": "<sup>14</sup> ಅವರು ಯಾರಾರೆಂದರೆ - ಆತನಿಂದ ಪೇತ್ರನೆಂಬ ಹೆಸರನ್ನು ಹೊಂದಿದ ಸೀಮೋನ, ಅವನ ತಮ್ಮನಾದ ಅಂದ್ರೇಯ, ಯಾಕೋಬ, ಯೋಹಾನ, ಫಿಲಿಪ್ಪ, ಬಾರ್ತೊಲೊಮಾಯ,",
			"15": "<sup>15</sup> ಮತ್ತಾಯ, ತೋಮ, ಅಲ್ಫಾಯನ ಮಗನಾದ ಯಾಕೋಬ, ಮತಾಭಿಮಾನಿ ಅನ್ನಿಸಿಕೊಂಡ ಸೀಮೋನ,",
			"16": "<sup>16</sup> ಯಾಕೋಬನ ಮಗನಾದ ಯೂದ, ಆತನನ್ನು ಹಿಡಿದುಕೊಡುವ ದ್ರೋಹಿಯಾದ ಇಸ್ಕರಿಯೋತ ಯೂದ ಇವರೇ.",
			"17": "<sup>17</sup> ಆ ಮೇಲೆ ಯೇಸು ಅವರೊಂದಿಗೆ ಬೆಟ್ಟದಿಂದ ಇಳಿದು ಸಮಭೂಮಿಯಲ್ಲಿ ಬಂದು ನಿಂತನು. ಆತನ ಶಿಷ್ಯರ ದೊಡ್ಡಗುಂಪು ಮತ್ತು ಎಲ್ಲಾ ಯೂದಾಯದಿಂದಲೂ ಯೆರೂಸಲೇಮಿನಿಂದಲೂ ತೂರ್‍, ಸೀದೋನ್ ಪಟ್ಟಣಗಳ ಕರಾವಳಿತೀರದಿಂದಲೂ ಬಂದಿದ್ದ ಜನರ ಮಹಾಸಮೂಹವು ಆತನ ಸಂಗಡ ಇದ್ದಿತು. ಆ ಜನರು ಆತನ ಉಪದೇಶವನ್ನು ಕೇಳುವುದಕ್ಕೂ ತಮ್ಮ ರೋಗಗಳನ್ನು ವಾಡಿಸಿಮಾಡಿಸಿಕೊಳ್ಳುವದಕ್ಕೂ ಬಂದಿದ್ದರು.",
			"18": "<sup>18</sup> ಅವರಲ್ಲಿ ದೆವ್ವಗಳ ಕಾಟವಿದ್ದವರು ಸ್ವಸ್ಥವಾದರು.",
			"19": "<sup>19</sup> ಮತ್ತು ಆತನಿಂದ ಶಕ್ತಿ ಹೊರಟು ಎಲ್ಲರನ್ನೂ ವಾಸಿಮಾಡುತ್ತಿದ್ದುದರಿಂದ ಆ ಗುಂಪಿನ ಜನರೆಲ್ಲಾ ಆತನನ್ನು ಮುಟ್ಟುವುದಕ್ಕೆ ಪ್ರಯತ್ನಪಟ್ಟರು.",
			"20": "<sup>20</sup> ತರುವಾಯ ಆತನು ತನ್ನ ಶಿಷ್ಯರ ಕಡೆಗೆ ಕಣ್ಣೆತ್ತಿನೋಡಿ ಹೇಳಿದ್ದೇನಂದರೆ -",
			"21": "<sup>21</sup> ಬಡವರಾದ ನೀವು ಧನ್ಯರು; ದೇವರ ರಾಜ್ಯವು ನಿಮ್ಮದೇ. ಈಗ ಹಸಿದಿರುವವರಾದ ನೀವು ಧನ್ಯರು; ನಿಮಗೆ ತೃಪ್ತಿಯಾಗುವದು. ಈಗ ಅಳುವವರಾದ ನೀವು ಧನ್ಯರು; ನೀವು ನಗುವಿರಿ.",
			"22": "<sup>22</sup> ಮನುಷ್ಯಕುಮಾರನ ನಿಮಿತ್ತವಾಗಿ ಜನರು ನಿಮ್ಮನ್ನು ಹಗೆಮಾಡಿ ನಿಮಗೆ ಬಹಿಷ್ಕಾರ ಹಾಕಿ ನಿಂದಿಸಿ ನಿಮ್ಮ ಹೆಸರನ್ನು ಕೆಟ್ಟದೆಂದು ತೆಗೆದುಹಾಕಿದರೆ ನೀವು ಧನ್ಯರು.",
			"23": "<sup>23</sup> ಆ ದಿನದಲ್ಲಿ ಸಂತೋಷಿಸಿ, ಕುಣಿದಾಡುವಿರಿ; ಪರಲೋಕದಲ್ಲಿ ನಿಮಗೆ ಬಹಳ ಫಲ ಸಿಕ್ಕುವುದು ಎಂದು ತಿಳಿದುಕೊಳ್ಳಿರಿ. ಈ ಜನರ ಪಿತೃಗಳು ಪ್ರವಾದಿಗಳಿಗೂ ಹಾಗೆಯೇ ಮಾಡಿದರು.",
			"24": "<sup>24</sup> ಆದರೆ ಐಶ್ವರ್ಯವಂತರೇ, ನಿಮ್ಮ ಗತಿಯನ್ನು ಏನು ಹೇಳಲಿ! ನಿಮ್ಮ ಸುಖವು ಈಗಾಗಲೇ ನಿಮಗೆ ಬಂದದೆ.",
			"25": "<sup>25</sup> ಈಗ ಹೊಟ್ಟೆತುಂಬಿದವರೇ, ನಿಮ್ಮ ಗತಿಯನ್ನು ಏನು ಹೇಳಲಿ! ನೀವು ಹಸಿಯುವಿರಿ. ಈಗ ನಗುವವರೇ, ನಿಮ್ಮ ಗತಿಯನ್ನು ಏನು ಹೇಳಲಿ! ನೀವು ದುಃಖಿಪಟ್ಟು ಅಳುವಿರಿ.",
			"26": "<sup>26</sup> ಜನರೆಲ್ಲಾ ನಿಮ್ಮನ್ನು ಹೊಗಳಿದರೆ ನಿಮ್ಮ ಗತಿಯನ್ನು ಏನು ಹೇಳಲಿ! ಅವರ ಪಿತೃಗಳು ಸುಳ್ಳುಪ್ರವಾದಿಗಳನ್ನು ಹಾಗೆಯೇ ಹೊಗಳಿದ್ದರು.",
			"27": "<sup>27</sup> ಕೇಳುವವರಾದ ನಿಮಗೆ ನಾನು ಹೇಳುವದೇನಂದರೆ - ನಿಮ್ಮ ವೈರಿಗಳನ್ನು ಪ್ರೀತಿಸಿರಿ; ನಿಮ್ಮನ್ನು ಹಗೆಮಾಡುವವರಿಗೆ ಉಪಕಾರಮಾಡಿರಿ;",
			"28": "<sup>28</sup> ನಿಮ್ಮನ್ನು ಶಪಿಸುವವರಿಗೆ ಆಶೀರ್ವಾದಮಾಡಿರಿ; ನಿಮ್ಮನ್ನು ಬಯ್ಯುವವರಿಗೋಸ್ಕರ ದೇವರಲ್ಲಿ ಪ್ರಾರ್ಥಿಸಿರಿ.",
			"29": "<sup>29</sup> ನಿನ್ನ ಒಂದು ಕೆನ್ನೆಯ ಮೇಲೆ ಹೊಡದವನಿಗೆ ಮತ್ತೊಂದು ಕೆನ್ನೆಯನ್ನೂ ತೋರಿಸಿರಿ; ನಿನ್ನ ಮೇಲಂಗಿಯನ್ನು ಕಿತ್ತುಕೊಳ್ಳುವವನಿಗೆ ಒಳಂಗಿಯನ್ನೂ ತೆಗೆದುಕೊಳ್ಳಲು ಅಡ್ಡಿಮಾಡಬೇಡಿರಿ.",
			"30": "<sup>30</sup> ನಿನ್ನನ್ನು ಬೇಡುವವರೆಲ್ಲರಿಗೂ ಕೊಡು; ನಿನ್ನ ಆಸ್ತಿಯನ್ನು ಕಸಿದುಕೊಳ್ಳುವವನನ್ನು ಹಿಂದಕ್ಕೆ ಕೊಡೆಂದು ಕೇಳಬೇಡ.",
			"31": "<sup>31</sup> ಜನರು ನಿಮಗೆ ಏನೇನು ಮಾಡಬೇಕೆಂದು ಅಪೇಕ್ಷಿಸುತ್ತೀರೋ, ಅಂಥದನ್ನೇ ನೀವು ಅವರಿಗೆ ಮಾಡಿರಿ.",
			"32": "<sup>32</sup> ನಿಮಗೆ ಪ್ರೀತಿ ತೋರಿಸುವವರನ್ನೇ ನೀವು ಪ್ರೀತಿಸಿದರೆ ನಿಮಗೆ ಏನು ಹೊಗಳಿಕೆ ಬಂದೀತು? ಪಾಪಿಷ್ಠರು ಸಹ ತಮಗೆ ಪ್ರೀತಿ ತೋರಿಸುವವರನ್ನು ಪ್ರೀತಿಸುತ್ತಾರಲ್ಲಾ.",
			"33": "<sup>33</sup> ನಿಮಗೆ ಉಪಕಾರ ಮಾಡುವವರಿಗೇ ನೀವು ಉಪಕಾರ ಮಾಡಿದರೆ ನಿಮಗೆ ಏನು ಹೊಗಳಿಕೆ ಬಂದೀತು? ಪಾಪಿಷ್ಠರು ಸಹ ಹಾಗೆ ಮಾಡುತ್ತಾರಲ್ಲಾ.",
			"34": "<sup>34</sup> ಅವರು ನಮಗೂ ಸಾಲಕೊಟ್ಟಾರೆಂದು ನೀವು ಯಾರಿಗಾದರೂ ಸಾಲಕೊಟ್ಟರೆ, ನಿಮಗೆ ಏನು ಹೊಗಳಿಕೆ ಬಂದೀತು? ಪಾಪಿಷ್ಠರೂ ಸಹ ತಾವು ಕೊಟ್ಟಷ್ಟು ತಮಗೆ ತಿರಿಗಿ ಸಿಕ್ಕೀತೆಂದು ಸಾಲ ಕೊಡುತ್ತಾರಲ್ಲಾ.",
			"35": "<sup>35</sup> ನೀವಾದರೋ ನಿಮ್ಮ ವೈರಿಗಳನ್ನು ಪ್ರೀತಿಸಿ ಅವರಿಗೆ ಉಪಕಾರಮಾಡಿರಿ. ಏನನ್ನೂನಿರೀಕ್ಷಿಸದೆ ಸಾಲಕೊಡಿರಿ; ಹೀಗೇ ಮಾಡಿದರೆ, ನಿಮಗೆ ಬಹಳ ಫಲಸಿಕ್ಕುವದು, ಮತ್ತು ನೀವು ಪರಾತ್ಪರನ ಮಕ್ಕಳಾಗುವಿರಿ. ದೇವರು ಉಪಕಾರನೆನಸದವರಿಗೂ ಕೆಟ್ಟವರಿಗೂ ಉಪಕಾರಿಯಾಗಿದ್ದಾನೆ.",
			"36": "<sup>36</sup> ನಿಮ್ಮ ತಂದೆಯು ಕರುಣೆಯುಳ್ಳವನಾಗಿರುವ ಪ್ರಕಾರವೇ ನೀವೂ ಕರುಣೆಯುಳ್ಳವರಾಗಿರಿ.",
			"37": "<sup>37</sup> ಇದಲ್ಲದೆ ತೀರ್ಪುಮಾಡಬೇಡಿರಿ, ಆಗ ನಿಮಗೂ ತೀರ್ಪಾಗುವದಿಲ್ಲ; ಅಪರಾಧಿಯೆಂದು ನಿರ್ಣಯಿಸಬೇಡಿರಿ, ಆಗ ನಿಮ್ಮನ್ನೂ ಅಪರಾಧಿಗಳೆಂದು ನಿರ್ಣಯಿಸುವುದಿಲ್ಲ; ಬಿಡಿಸಿರಿ ಆಗ ನಿಮ್ಮನ್ನೂ ಬಿಡಿಸುವರು;",
			"38": "<sup>38</sup> ಕೊಡಿರಿ, ಆಗ ನಿಮಗೂ ಕೊಡುವರು; ಜಡಿದು ಅಲ್ಲಾಡಿಸಿ ಹೊರ ಚೆಲ್ಲುವ ಹಾಗೆ ತುಂಬಿ ಅಳೆದು ನಿಮ್ಮ ಸೆರಿಗೆ ಹಾಕುವರು. ನೀವು ಅಳೆಯುವ ಅಳತೆಯಿಂದಲೇ ದೇವರು ನಿಮಗೂ ಅಳೆದುಕೊಡುವರು ಅಂದನು.",
			"39": "<sup>39</sup> ಇದಲ್ಲದೆ ಆತನು ಅವರಿಗೆ ಒಂದು ಸಾಮ್ಯವನ್ನು ಹೇಳಿದನು. ಅದೇನಂದರೆ - ಕುರುಡನು ಕುರುಡನಿಗೆ ದಾರೀ ತೋರುಸುವದಕ್ಕಾದೀತೇ? ಅವರಿಬ್ಬರೂ ಹಳ್ಳದಲ್ಲಿ ಬೀಳುವರಲ್ಲವೆ.",
			"40": "<sup>40</sup> ಗುರುವಿಗಿಂತ ಶಿಷ್ಯನು ದೊಡ್ಡವನಲ್ಲ; ಆದರೆ ಹೆಚ್ಚು ಕಲಿತವನಾದ ಪ್ರತಿಯೊಬ್ಬನು ತನ್ನ ಗುರುವಿನಂತಾಗಿರುವನು.",
			"41": "<sup>41</sup> ಇದಲ್ಲದೆ ನೀನು ನಿನ್ನ ಕಣ್ಣಿನಲ್ಲಿರುವ ತೊಲೆಯನ್ನು ಯೋಚಿಸದೆ ನಿನ್ನ ಸಹೋದರನ ಕಣ್ಣಿನಲ್ಲಿರುವ ರವೆಯನ್ನು ಗಮನಿಸುವುದೇಕೆ?",
			"42": "<sup>42</sup> ಇಲ್ಲವೆ ನಿನ್ನ ಕಣ್ಣಿನಲ್ಲಿರುವ ತೊಲೆಯನ್ನೇ ನೀನು ನೋಡದೆ ನಿನ್ನ ಸಹೋದರನಿಗೆ - ಅಣ್ಣಾ, ನಿನ್ನ ಕಣ್ಣಿನೊಳಗಿನ ರವೆಯನ್ನು ತೆಗೆಯುತ್ತೇನೆ ಬಾ ಎಂದು ಹೇಳುವದಕ್ಕೆ ಹೇಗಾದೀತು? ಮೋಸಗಾರನೇ, ಮೊದಲು ನಿನ್ನ ಕಣ್ಣಿನೊಳಗಿಂದ ತೊಲೆಯನ್ನು ತೆಗೆದುಹಾಕಿಕೋ, ಆ ಮೇಲೆ ನಿನ್ನ ಸಹೋದರನ ಕಣ್ಣಿನೊಳಗಿನ ರವೆಯನ್ನು ತೆಗೆಯುವುದಕ್ಕೆ ಚೆನ್ನಾಗಿ ಕಾಣಿಸುವುದು.",
			"43": "<sup>43</sup> ಒಳ್ಳೆಯ ಮರವು ಹುಳುಕು ಫಲವನ್ನು ಕೊಡುವದಿಲ್ಲ; ಹಾಗೆಯೇ ಹುಳುಕುಮರವು ಒಳ್ಳೆಯ ಫಲವನ್ನು ಕೊಡುವದಿಲ್ಲ.",
			"44": "<sup>44</sup> ಪ್ರತಿಯೊಂದು ಮರದ ಗುಣವು ಅದರ ಫಲದಿಂದಲೇ ಗೊತ್ತಾಗುವದು. ಮುಳ್ಳುಗಿಡಗಳಲ್ಲಿ ಅಂಜೂರದ ಹಣ್ಣುಗಳನ್ನು ಕೀಳುವುದಿಲ್ಲ; ಗಜ್ಜುಗದ ಪೊದೆಗಳಲ್ಲಿ ದ್ರಾಕ್ಷೇಹಣ್ಣುಗಳನ್ನು ಕೊಯ್ಯುಲುಸಾಧ್ಯವಿಲ್ಲ.",
			"45": "<sup>45</sup> ಒಳ್ಳೆಯವನು ತನ್ನ ಹೃದಯವೆಂಬ ಒಳ್ಳೆಯ ಬೊಕ್ಕಸದೊಳಗಿನಿಂದ ಒಳ್ಳೆಯದನ್ನು ತರುತ್ತಾನೆ; ಕೆಟ್ಟವನು ಕೆಟ್ಟಬೊಕ್ಕಸದೊಳಗಿನಿಂದ ಕೆಟ್ಟದ್ದನ್ನು ತರುತ್ತಾನೆ; ಹೃದಯದಲ್ಲಿ ತುಂಬಿರುವುದೇ ಬಾಯಿಂದ ಹೊರಡುವುದು.",
			"46": "<sup>46</sup> ಇದಲ್ಲದೆ ನೀವು ನನ್ನನ್ನು ಕರ್ತನೇ ಕರ್ತನೇ ಎಂದು ಕರೆದು ನಾನು ಹೇಳುವದನ್ನು ಮಾಡದೆ ಇರುವುದೇಕೆ?",
			"47": "<sup>47</sup> ನನ್ನ ಬಳಿಗೆ ಬಂದು ನನ್ನ ಮಾತುಗಳನ್ನು ಕೇಳಿ ಅವುಗಳಂತೆ ನಡೆಯುವವನು ಯಾರಿಗೆ ಸಮಾನನೆಂಬದನ್ನು ನಿಮಗೆ ತೋರಿಸುತ್ತೇನೆ.",
			"48": "<sup>48</sup> ಅವನು ಆಳವಾಗಿ ಅಗಿದು ಬಂಡೆಯ ಮೇಲೆ ಅಸ್ತಿವಾರವನ್ನು ಹಾಕಿ ಮನೇ ಕಟ್ಟಿದವನಿಗೆ ಸಮಾನನು; ಹೊಳೆಬಂದು ಪ್ರವಾಹವು ಆ ಮನೆಗೆ ಅಪ್ಪಳಿಸಿದರೂ ಅದು ಚೆನ್ನಾಗಿ ಅಡಿಪಾಯ ಹಾಕಿ ಕಟ್ಟಿರುವುದರಿಂದ ಆ ಪ್ರವಾಹವು ಅದನ್ನು ಅಲ್ಲಾಡಿಸಲಾರದೆ ಹೋಯಿತು.",
			"49": "<sup>49</sup> ಆದರೆ ನನ್ನ ಮಾತುಗಳನ್ನು ಕೇಳಿಯೂ ಅವುಗಳಂತೆ ನಡೆಯದಿರುವವನು ಅಸ್ತಿವಾರವಿಲ್ಲದೆ ಮರಳಿನ ಮೇಲೆ ಮನೇ ಕಟ್ಟಿದವನಿಗೆ ಸಮಾನನು. ಪ್ರವಾಹವು ಅದಕ್ಕೆ ಅಪ್ಪಳಿಸಿ ಬಡಿದ ಕೂಡಲೇ ಅದು ಕುಸಿದುಬಿದ್ದು ನಾಶವಾಗಿ ಹೋಯಿತು."
		},
		"7": {
			"1": "<sup>1</sup> ಆತನು ತನ್ನ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ಜನರು ಕೇಳುವಂತೆ ಹೇಳಿ ಮುಗಿಸಿದ ಮೇಲೆ ಕಪೆರ್ನೌಮಿಗೆ ಬಂದನು.",
			"2": "<sup>2</sup> ಅಲ್ಲಿ ದಂಡಿನ ಶತಾಧಿಪತಿಗೆ ಇಷ್ಟನಾದ ಒಬ್ಬ ಆಳು ಕ್ಷೇಮವಿಲ್ಲದೆ ಸಾಯುವ ಹಾಗಿದ್ದನು.",
			"3": "<sup>3</sup> ಅವನು ಯೇಸುವಿನ ಸುದ್ಧಿಯನ್ನು ಕೇಳಿ ಆತನ ಬಳಿಗೆ ಬಂದು ನನ್ನ ಆಳನ್ನು ಸ್ವಸ್ಥಮಾಡಬೇಕೆಂದು ಆತನನ್ನು ಬೇಡಿಕೊಳ್ಳುವ ಹಾಗೆ ಯೆಹೂದ್ಯರ ಹಿರಿಯರನ್ನು ಕಳುಹಿಸಿದನು.",
			"4": "<sup>4</sup> ಅವರು ಯೇಸುವಿನ ಬಳಿಗೆ ಬಂದು - ನಿನ್ನಿಂದ ಇಂಥ ಉಪಕಾರ ಹೊಂದುವದಕ್ಕೆ ಅವನು ಯೋಗ್ಯನು;",
			"5": "<sup>5</sup> ಅವನು ನಮ್ಮ ಜನರನ್ನು ಪ್ರೀತಿಸಿ ಪ್ರಾರ್ಥನೆಗಾಗಿ ನಮಗೆ ಸಭಾಮಂದಿರವನ್ನು ಕಟ್ಟಿಸಿಕೊಟ್ಟಿರುವನು ಎಂದು ಆತನನ್ನು ಬಹಳವಾಗಿ ಬೇಡಿಕೊಳ್ಳಲು",
			"6": "<sup>6</sup> ಯೇಸು ಅವರ ಸಂಗಡ ಹೋದನು. ಆತನು ಇನ್ನೂ ಮನೆಗೆ ಮುಟ್ಟುವುದಕ್ಕೆ ಸ್ವಲ್ಪ ದೂರವಿರುವಾಗಲೇ ಶತಾಥಿಪತಿಯು ಅವನ ಸ್ನೇಹಿತರನ್ನು ಆತನ ಬಳಿಗೆ ಕಳುಹಿಸಿ - ಕರ್ತನೇ, ತೊಂದರೆ ತೆಗೆದುಕೊಳ್ಳಬೇಡ; ನೀವು ನನ್ನ ಮನೆಗೆ ಬರತಕ್ಕಷ್ಟು ಯೋಗ್ಯತೆ ನನ್ನಲ್ಲಿಲ್ಲ.",
			"7": "<sup>7</sup> ಈ ಕಾರಣದಿಂದ ನಾನು ನನ್ನನ್ನು ನಿಮ್ಮ ಬಳಿಗೆ ಬರುವುದಕ್ಕೆ ತಕ್ಕವನೆಂದು ಎಣಿಸಿಕೊಳ್ಳಲಿಲ್ಲ. ಆದರೆ ನೀವು ಒಂದು ಮಾತು ಹೇಳಿದರೆ ಸಾಕು, ನನ್ನ ಆಳಿಗೆ ಗುಣವಾಗುವುದು.",
			"8": "<sup>8</sup> ನಾನು ಸಹ ಮತ್ತೊಬ್ಬರ ಕೈ ಕೆಳಗಿರುವವನು; ನನ್ನ ಕೈ ಕೆಳಗೆ ಸಿಪಾಯಿಗಳಿದ್ದಾರೆ; ನಾನು ಅವರಲ್ಲಿ ಒಬ್ಬನಿಗೆ ಹೋಗು ಎಂದು ಹೇಳಿದರೆ ಅವನು ಹೋಗುತ್ತಾನೆ; ಮತ್ತೊಬ್ಬನಿಗೆ ಬಾ ಎಂದು ಹೇಳಿದರೆ ಅವನು ಬರುತ್ತಾನೆ; ನನ್ನ ಆಳಿಗೆ ಇಂಥಿಂಥದನ್ನು ಮಾಡು ಎಂದು ಹೇಳಿದರೆ ಮಾಡುತ್ತಾನೆ ಎಂಬದಾಗಿ ಹೇಳಿಸಿದನು.",
			"9": "<sup>9</sup> ಯೇಸು ಈ ಮಾತನ್ನು ಕೇಳಿ ಅವನ ವಿಷಯದಲ್ಲಿ ಆಶ್ಚರ್ಯಪಟ್ಟು ತನ್ನ ಹಿಂದೆ ಬರುತ್ತಿರುವ ಗುಂಪನ್ನು ನೋಡಿ ನಾನು ಇಂಥ ದೊಡ್ಡ ನಂಬಿಕೆಯನ್ನು ಇಸ್ರಾಯೇಲ್ ಜನರಲ್ಲಿಯೂ ಕಾಣಲಿಲ್ಲವೆಂದು ನಿಮಗೆ ಹೇಳುತ್ತೇನೆ ಅಂದನು.",
			"10": "<sup>10</sup> ತರುವಾಯ ಆ ಶತಾಥಿಪತಿಯ ಕಡೆಯಿಂದ ಬಂದವರು ತಿರಿಗಿ ಮನೆಗೆ ಹೋಗಿ ಆಳು ಆರೋಗ್ಯವಾಗಿರುವದನ್ನು ಕಂಡರು.",
			"11": "<sup>11</sup> ಸ್ವಲ್ಪಕಾಲದ ನಂತರ ಆತನು ನಾಯಿನೆಂಬ ಊರಿಗೆ ಹೋದನು. ಆತನ ಜೊತೆಯಲ್ಲಿ ಆತನ ಶಿಷ್ಯರೂ, ಇನ್ನೂ ಅನೇಕರೂ ಹೋಗುತ್ತಿದ್ದರು.",
			"12": "<sup>12</sup> ಆತನು ಊರು ಬಾಗಲಿನ ಹತ್ತಿರಕ್ಕೆ ಬಂದಾಗ ಸತ್ತುಹೋಗಿದ್ದ ಒಬ್ಬನನ್ನು ಹೊರಗೆ ತರುತ್ತಿದ್ದರು. ಅವನು ತನ್ನ ತಾಯಿಗೆ ಒಬ್ಬನೇ ಮಗನಾಗಿದ್ದನು; ಆಕೆಯು ವಿಧವೆಯಾಗಿದ್ದಳು. ಆಕೆಯ ಸಂಗಡ ಗ್ರಾಮಸ್ಥರು ಅನೇಕರಿದ್ದರು.",
			"13": "<sup>13</sup> ಸ್ವಾಮಿಯು ಆಕೆಯನ್ನು ಕಂಡು ಕನಿಕರಿಸಿ -",
			"14": "<sup>14</sup> ಅಳಬೇಡ ಎಂದು ಆಕೆಗೆ ಹೇಳಿ ಚಟ್ಟದ ಹತ್ತಿರಕ್ಕೆ ಹೋಗಿ ಅದನ್ನು ಮುಟ್ಟಲು ಹೊತ್ತುಕೊಂಡವರು ನಿಂತರು. ಆಗ ಆತನು - ಯೌವನಸ್ಥನೇ, ಏಳು ಎಂದು ನಿನಗೆ ಹೇಳುತ್ತೇನೆ ಅಂದನು.",
			"15": "<sup>15</sup> ಅನ್ನುತ್ತಲೇ ಸತ್ತಿದ್ದವನು ಎದ್ದು ಕುಳಿತುಕೊಂಡು ಮಾತನಾಡುವದಕ್ಕೆ ತೊಡಗಿದನು. ಯೇಸು ಅವನನ್ನು ಅವನ ತಾಯಿಗೆ ಒಪ್ಪಿಸಿದನು.",
			"16": "<sup>16</sup> ಎಲ್ಲರು ಭಯಹಿಡಿದವರಾಗಿ - ಮಹಾಪ್ರವಾದಿಯು ನಮ್ಮಲ್ಲಿ ಎದ್ದಿದ್ದಾನೆ, ದೇವರು ತನ್ನ ಜನರನ್ನು ಸಂದರ್ಶಿಸಲು ಬಂದಿದ್ದಾನೆ ಎಂದು ದೇವರನ್ನು ಕೊಂಡಾಡಿದರು.",
			"17": "<sup>17</sup> ಈ ವಾರ್ತೆಯು ಯೂದಾಯದಲ್ಲಿಯೂ ಸುತ್ತಲಿರುವ ಎಲ್ಲಾ ಪ್ರಾಂತ್ಯದಲ್ಲಿಯೂ ಹಬ್ಬಿತು.",
			"18": "<sup>18</sup> ಯೋಹಾನನ ಶಿಷ್ಯರು ನಡೆದ ಸಂಗತಿಗಳನ್ನೆಲ್ಲಾ ಆತನಿಗೆ ತಿಳಿಸಲು",
			"19": "<sup>19</sup> ಆತನು ತನ್ನ ಶಿಷ್ಯರಲ್ಲಿ ಇಬ್ಬರನ್ನು ಕರೆದು - ಬರಬೇಕಾದವನು ನೀನೋ ಅಥವಾ ನಾವು ಬೇರೊಬ್ಬನಿಗಾಗಿ ಕಾಯಬೇಕೋ? ಎಂದು ಸ್ವಾಮಿಯನ್ನು ಕೇಳಲು ಹೇಳಿಕಳುಹಿಸಿದನು.",
			"20": "<sup>20</sup> ಅವರು ಯೇಸುವಿನ ಬಳಿಗೆ ಬಂದು - ಬರಬೇಕಾದವನು ನೀನೋ ಅಥವಾ ನಾವು ಮತ್ತೊಬ್ಬನ ಬರುವಿಕೆಗಾಗಿ ಕಾಯಬೇಕೋ ಎಂದು ಕೇಳುವುದಕ್ಕಾಗಿ ಸ್ನಾನಿಕನಾದ ಯೋಹಾನನು ನಮ್ಮನ್ನು ನಿನ್ನ ಬಳಿಗೆ ಕಳುಹಿಸಿದ್ದಾರೆ ಅಂದರು.",
			"21": "<sup>21</sup> ಅದೇ ಗಳಿಗೆಯಲ್ಲಿ ಆತನು ಅನೇಕರನ್ನು ರೋಗ, ಉಪದ್ರವ, ದೆವ್ವ ಇಂಥವುಗಳಿಂದ ಪೀಡಿತರಾದವರನ್ನು ವಾಸಿಮಾಡಿದನು; ಮತ್ತು ಅನೇಕ ಕುರುಡರಿಗೆ ಕಣ್ಣಿನ ದೃಷ್ಟಿ ದಯಪಾಲಿಸಿದನು.",
			"22": "<sup>22</sup> ಹೀಗಿರಲು ಆತನು ಅವರಿಗೆ ಪ್ರತ್ಯುತ್ತರವಾಗಿ - ನೀವು ಹೋಗಿ ಕಂಡು ಕೇಳಿದವುಗಳನ್ನು ಯೋಹಾನನಿಗೆ ತಿಳಿಸಿರಿ; ಕುರುಡರಿಗೆ ದೃಷ್ಟಿ ಬರುತ್ತವೆ, ಕುಂಟರಿಗೆ ಕಾಲುಬರುತ್ತವೆ, ಕುಷ್ಠರೋಗಿಗಳು ಶುದ್ಧರಾಗುತ್ತಾರೆ, ಕಿವುಡರಿಗೆ ಕಿವಿಕೇಳುತ್ತವೆ, ಸತ್ತವರು ಜೀವವನ್ನು ಹೊಂದುತ್ತಾರೆ, ಬಡವರಿಗೆ ಸುವಾರ್ತೆಯು ಸಾರಲ್ಪಡುತ್ತಿದೆ.",
			"23": "<sup>23</sup> ನನ್ನ ವಿಷಯದಲ್ಲಿ ಸಂಶಯಪಡದವನೇ ಧನ್ಯನು ಎಂದು ಹೇಳಿದನು.",
			"24": "<sup>24</sup> ಯೋಹಾನನ ಶಿಷ್ಯರು ಹಿಂತಿರುಗಿ ಹೋಗಲು ಆತನು ಯೋಹಾನನ ವಿಷಯವಾಗಿ ಆ ಜನರ ಗುಂಪುಗಳಿಗೆ ಹೇಳತೊಡಗಿದ್ದೇನಂದರೆ - ಏನು ನೋಡಬೇಕೆಂದು ಅಡವಿಗೆ ಹೋಗಿದ್ದಿರಿ? ಗಾಳಿಯಿಂದ ಅಲ್ಲಾಡುವ ದಂಟನ್ನೋ?",
			"25": "<sup>25</sup> ಅಲ್ಲವಾದರೆ ಏನು ನೋಡಬೇಕೆಂದು ಹೋಗಿದ್ದಿರಿ? ನಯವಾದ ಉಡುಪನ್ನು ಹಾಕಿಕೊಂಡ ಮನುಷ್ಯನನ್ನೋ? ಶೋಭಾಯಮಾನವಾದ ಉಡುಪನ್ನು ಧರಿಸಿ ಭೋಗದಲ್ಲಿ ಬಾಳುವವರು ಅರಮನೆಗಳಲ್ಲಿ ಇರುತ್ತಾರಷ್ಟೆ.",
			"26": "<sup>26</sup> ಹಾಗಾದರೆ ನೀವು ಏನು ನೋಡಬೇಕೆಂದು ಹೋಗಿದ್ದಿರಿ? ಪ್ರವಾದಿಯನ್ನೋ? ಹೌದು, ಪ್ರವಾದಿಗಿಂತಲೂ ಹೆಚ್ಚಿನವನನ್ನು ನೋಡಿದ್ದಿರಿ ಎಂದು ನಿಮಗೆ ಹೇಳುತ್ತೇನೆ.",
			"27": "<sup>27</sup> ಇಗೋ, ನನ್ನ ದೂತನನ್ನು ನಿನ್ನ ಮುಂದೆ ಕಳುಹಿಸುತ್ತೇನೆ. ನೀನು ಹೋಗುವ ದಾರಿಯನ್ನು ಅವನು ನಿನ್ನ ಮುಂದೆ ಸರಾಗಮಾಡುವನು, ಎಂದು ಯಾರ ವಿಷಯವಾಗಿ ಬರೆದದೆಯೋ ಆ ಪುರುಷನು ಅವನೇ.",
			"28": "<sup>28</sup> ಸ್ತ್ರೀಯರಲ್ಲಿ ಹುಟ್ಟಿದವರೊಳಗೆ ಯೋಹಾನನಿಗಿಂತ ಮಹಾಪುರುಷನು ಒಬ್ಬನೂ ಹುಟ್ಟಿಲ್ಲ; ಅದರೂ ದೇವರ ರಾಜ್ಯದಲ್ಲಿ ಚಿಕ್ಕವನು ಅವನಿಗಿಂತಲೂ ದೊಡ್ಡವನೆಂದು ನಿಮಗೆ ಹೇಳುತ್ತೇನೆ.",
			"29": "<sup>29</sup> ಇದಲ್ಲದೆ ಜನರೆಲ್ಲರೂ ಸುಂಕದವರೂ ಯೋಹಾನನ ಉಪದೇಶವನ್ನು ಕೇಳಿದಾಗ ಅವನಿಂದ ದೀಕ್ಷಾಸ್ನಾನ ಮಾಡಿಸಿಕೊಂಡದ್ದರಲ್ಲಿ ದೇವರು ನೀತಿವಂತನೆಂದು ಒಪ್ಪಿಕೊಂಡರು.",
			"30": "<sup>30</sup> ಆದರೆ ಫರಿಸಾಯರೂ ಧರ್ಮೋಪದೇಶಕರೂ ಅವನಿಂದ ದೀಕ್ಷಾಸ್ನಾನ ಮಾಡಿಸಿಕೊಳ್ಳದೆ ಹೋದದ್ದರಿಂದ ಅವರು ದೇವರ ಸಂಕಲ್ಪವನ್ನು ನಿರಾಕರಿಸಿದರು.",
			"31": "<sup>31</sup> ಹೀಗಿರುವಲ್ಲಿ ಈ ಕಾಲದ ಜನರನ್ನು ಯಾರಿಗೆ ಹೋಲಿಸಲಿ? ಅವರು ಯಾರನ್ನು ಹೋಲುತ್ತಾರೆ?",
			"32": "<sup>32</sup> ಪೇಟೆಯಲ್ಲಿ ಕೂಳಿತುಕೊಂಡು - ನಾವು ನಿಮಗೋಸ್ಕರ ಕೊಳಲೂದಿದೆವು, ನೀವು ಕುಣಿಯಲಿಲ್ಲ; ಗೋಳಾಡಿದೆವು, ನೀವು ಅಳಲಿಲ್ಲ ಎಂದು ಒಬ್ಬರಿಗೊಬ್ಬರು ಕೂಗಿ ಹೇಳುವಂಥ ಮಕ್ಕಳನ್ನು ಹೋಲುತ್ತಾರೆ.",
			"33": "<sup>33</sup> ಹೇಗಂದರೆ ಸ್ನಾನಿಕನಾದ ಯೋಹಾನನು ಬಂದಿದ್ದಾನೆ; ಅವನು ರೊಟ್ಟಿ ತಿನ್ನದವನು, ದ್ರಾಕ್ಷಾರಸ ಕುಡಿಯದವನು; ನೀವು - ಅವನಿಗೆ ದೆವ್ವ ಹಿಡಿದದೆ ಅನ್ನುತ್ತೀರಿ.",
			"34": "<sup>34</sup> ಮನುಷ್ಯಕುಮಾರನು ಬಂದಿದ್ದಾನೆ, ಆತನು ಅನ್ನ ಪಾನಗಳನ್ನು ತೆಗೆದುಕೊಳ್ಳುವವನಾಗಿದ್ದಾನೆ; ನೀವು - ಇಗೋ, ಈತನು ಹೊಟ್ಟೆಬಾಕನು, ಕುಡುಕನು, ಭ್ರಷ್ಟರ ಮತ್ತು ಪಾಪಿಷ್ಠರ ಗೆಳೆಯನು ಅನ್ನುತ್ತೀರಿ.",
			"35": "<sup>35</sup> ಆದರೆ ಜ್ಞಾನವಾದರೋ ತನ್ನ ಎಲ್ಲಾ ಮಕ್ಕಳಿಂದ ಸಮರ್ಥನೆ ಪಡೆದದೆ ಅಂದನು.",
			"36": "<sup>36</sup> ಫರಿಸಾಯರಲ್ಲಿ ಒಬ್ಬನು ಆತನನ್ನು ತನ್ನ ಜೊತೆಯಲ್ಲಿ ಊಟಮಾಡಬೇಕೆಂದು ಬೇಡಿಕೊಂಡಾಗ ಆತನು ಆ ಫರಿಸಾಯನ ಮನೆಗೆ ಹೋಗಿ ಆತನ ಸಂಗಡ ಊಟಕ್ಕೆ ಕುಳಿತುಕೊಂಡನು.",
			"37": "<sup>37</sup> ಆಗ ಆ ಊರಿನಲ್ಲಿದ್ದ ದುರಾಚಾರಿಯಾದ ಒಬ್ಬ ಹೆಂಗಸು ಫರಿಸಾಯನ ಮನೆಯಲ್ಲಿ ಆತನು ಊಟಕ್ಕೆ ಬಂದಿದ್ದಾನೆಂದು ತಿಳಿದು ಸುಗಂಧತೈಲದ ಭರಣಿಯನ್ನು ತೆಗೆದುಕೊಂಡು ಬಂದು",
			"38": "<sup>38</sup> ಆತನ ಹಿಂದೆ ಪಾದಗಳ ಬಳಿಯಲ್ಲಿ ನಿಂತುಕೊಂಡು ಅಳುತ್ತಾ ತನ್ನ ಕಣ್ಣೀರಿನಿಂದ ಆತನ ಪಾದಗಳನ್ನು ನೆನಸುತ್ತಾ ತನ್ನ ತಲೆ ಕೂದಲಿನಿಂದ ಒರಸಿ ಮುದ್ದಿಟ್ಟು ಆ ತೈಲವನ್ನು ಹಚ್ಚಿದಳು.",
			"39": "<sup>39</sup> ಆದರೆ ಆತನನ್ನು ಊಟಕ್ಕೆ ಕರೆದ ಫರಿಸಾಯನು ಇದನ್ನು ಕಂಡು - ಇವಳು ದುರಾಚಾರಿ; ಈತನು ಪ್ರವಾದಿಯಾಗಿದ್ದರೆ ತನ್ನನ್ನು ಮುಟ್ಟಿದ ಈ ಹೆಂಗಸು ಇಂಥವಳೆಂದು ತಿಳುಕೊಳ್ಳುತ್ತಿದ್ದನು ಅಂದುಕೊಂಡನು.",
			"40": "<sup>40</sup> ಅದಕ್ಕೆ ಯೇಸು - ಸೀಮೋನನೇ, ನಾನು ನಿನಗೆ ಹೇಳಬೇಕಾದ ಒಂದು ಮಾತದೆ ಅಂದಾಗ ಅವನು - ಬೋಧಕನೇ, ಹೇಳು ಅಂದನು.",
			"41": "<sup>41</sup> ಆಗ ಯೇಸು - ಒಬ್ಬ ಸಾಹುಕಾರನಿಗೆ ಇಬ್ಬರು ಸಾಲಗಾರರಿದ್ದರು; ಒಬ್ಬನು ಐನೂರು ಹಣ ಕೊಡಬೇಕಾಗಿತ್ತು. ಮತ್ತೊಬ್ಬನು ಐವತ್ತು ಹಣ ಕೊಡಬೇಕಾಗಿತ್ತು",
			"42": "<sup>42</sup> ತೀರಿಸುವುದಕ್ಕೆ ಅವರಿಗೆ ಗತಿಯಿಲ್ಲದ್ದರಿಂದ ಆ ಇಬ್ಬರಿಗೂ ಸಾಲವನ್ನು ಮನ್ನಾಮಾಡಿ ಬಿಟ್ಟನು. ಹಾಗಾದರೆ ಅವರಿಬ್ಬರಲ್ಲಿ ಯಾವನು ಆ ಸಾಹುಕಾರನನ್ನು ಹೆಚ್ಚಾಗಿ ಪ್ರೀತಿಸುವನು ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ",
			"43": "<sup>43</sup> ಸೀಮೋನನು - ಯಾವನಿಗೆ ಹೆಚ್ಚಾಗಿ ಬಿಟ್ಟನೋ ಅವನೇ ಎಂದು ಭಾವಿಸುತ್ತೇನೆ ಅಂದನು. ಯೇಸು ಅವನಿಗೆ - ನೀನು ಸರಿಯಾಗಿ ತೀರ್ಪುಮಾಡಿದಿ ಎಂದು ಹೇಳಿ",
			"44": "<sup>44</sup> ಆ ಹೆಂಗಸಿನ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡು ಸೀಮೋನನಿಗೆ ಹೇಳಿದ್ದೇನಂದರೆ - ಈ ಹೆಂಗಸನ್ನು ನೋಡಿದ್ದಿಯಾ? ನಾನು ನಿನ್ನ ಮನೆಗೆ ಬಂದಾಗ ನೀನು ನನ್ನ ಕಾಲಿಗೆ ನೀರು ಕೊಡಲಿಲ್ಲ; ಇವಳಾದರೋ ನನ್ನ ಕಾಲುಗಳನ್ನು ಕಣ್ಣೀರಿನಿಂದ ನೆನಸಿ ತನ್ನ ತಲೆಯಕೂದಲಿನಿಂದ ಒರಸಿದಳು.",
			"45": "<sup>45</sup> ನೀನು ನನಗೆ ಮುದ್ದಿಡಲಿಲ್ಲ; ಇವಳಾದರೋ ನಾನು ಒಳಗೆ ಬಂದಾಗಿನಿಂದ ನನ್ನ ಕಾಲಿಗೆ ಮುದ್ದಿಡುವುದನ್ನು ಬಿಟ್ಟಿಲ್ಲ.",
			"46": "<sup>46</sup> ನೀನು ನನ್ನ ತಲೆಗೆ ಎಣ್ಣೆ ಹಚ್ಚಲಿಲ್ಲ; ಇವಳಾದರೋ ಸುಗಂಧ ತೈಲವನ್ನು ಕಾಲಿಗೆ ಹಚ್ಚಿದಳು.",
			"47": "<sup>47</sup> ಹೀಗಿರಲು ನಾನು ಹೇಳುವ ಮಾತೇನಂದರೆ - ಇವಳ ಪಾಪಗಳು ಬಹಳವಾಗಿದ್ದರೂ ಅವೆಲ್ಲಾವು ಕ್ಷಮಿಸಲ್ಪಟ್ಟವೆ. ಇದಕ್ಕೆ ಪ್ರಮಾಣವೇನಂದರೆ ಇವಳು ತೋರಿಸಿದ ಪ್ರೀತಿ ಬಹಳ. ಆದರೆ ಯಾವನಿಗೆ ಸ್ವಲ್ಪ ಮಾತ್ರ ಕ್ಷಮಿಸಲ್ಪಟ್ಟಿದೆಯೋ ಅವನು ತೋರಿಸುವ ಪ್ರೀತಿಯು ಸ್ವಲ್ಪವೇ ಅಂದನು",
			"48": "<sup>48</sup> ಆ ಮೇಲೆ ಆತನು ಅವಳಿಗೆ - ನಿನ್ನ ಪಾಪಗಳು ಕ್ಷಮಿಸಲ್ಪಟ್ಟವೆ ಎಂದು ಹೇಳಿದನು.",
			"49": "<sup>49</sup> ಆತನ ಸಂಗಡ ಊಟಕ್ಕೆ ಕುಳಿತಿದ್ದವರೆಲ್ಲರೂ, ಪಾಪಗಳನ್ನು ಸಹ ಕ್ಷಮಿಸಲು ಇವನಾರು ಎಂದು ತಮ್ಮ ತಮ್ಮೊಳಗೆ ಅಂದುಕೊಂಡರು.",
			"50": "<sup>50</sup> ಆದರೆ ಆತನು ಆ ಹೆಂಗಸಿಗೆ - ನಿನ್ನ ನಂಬಿಕೆಯೇ ನಿನ್ನನ್ನು ರಕ್ಷಿಸಿದೆ; ಸಮಾಧಾನದಿಂದ ಹೋಗು ಎಂದು ಹೇಳಿದನು."
		},
		"8": {
			"1": "<sup>1</sup> ತರುವಾಯ ಯೇಸು ದೇವರ ರಾಜ್ಯದ ಸುವಾರ್ತೆಯನ್ನು ಸಾರುತ್ತಾ ಊರುಗಳಲ್ಲಿಯೂ ಗ್ರಾಮಗಳಲ್ಲಿಯೂ ಸಂಚರಿಸಿದನು. ಆತನ ಸಂಗಡ ಹನ್ನೆರಡು ಮಂದಿ ಶಿಷ್ಯರೂ",
			"2": "<sup>2</sup> ದೆವ್ವಗಳ ಕಾಟದಿಂದಲೂ ರೋಗಗಳಿಂದಲೂ ಗುಣಹೊಂದಿದ ಕೆಲವು ಮಂದಿ ಹೆಂಗಸರು ಸಹ ಇದ್ದರು. ಈ ಹೆಂಗಸರು ಯಾರಾರೆಂದರೆ - ಏಳು ದುರಾತ್ಮಗಳಿಂದ ಬಿಡುಗಡೆ ಹೊಂದಿದ್ದ ಮಗ್ದಲದ ಮರಿಯಳು,",
			"3": "<sup>3</sup> ಹೆರೋದನ ಮನೆಯ ಮೇಲ್ವಿಚಾರಕನಾಗಿದ್ದ ಕೂಜನ ಹೆಂಡತಿ ಯೋಹಾನಳು, ಸುಸನ್ನಳು, ಇನ್ನು ಬೇರೆ ಅನೇಕರು. ಇವರು ತಮ್ಮ ಆಸ್ತಿಯಿಂದ ಅವರಿಗೆ ಉಪಚಾರಮಾಡುತ್ತಿದರು.",
			"4": "<sup>4</sup> ಅನೇಕರು ಗುಂಪುಗುಂಪಾಗಿ ಬೇರೆ ಬೇರೆ ಪಟ್ಟಣಗಳಿಂದ ಆತನ ಬಳಿಗೆ ಬರುತ್ತಿರಲು ಯೇಸು ಅವರಿಗೆ ಸಾಮ್ಯರೂಪವಾಗಿ ಹೇಳಿದ್ದೇನೆಂದರೆ -",
			"5": "<sup>5</sup> ಬಿತ್ತುವವನು ಬೀಜವನ್ನು ಬಿತ್ತುವುದಕ್ಕೆ ಹೊರಟನು. ಅವನು ಬಿತ್ತುವಾಗ ಕೆಲವು ಬೀಜಗಳು ದಾರಿಯ ಮಗ್ಗುಲಲ್ಲಿ ಬಿದ್ದು ತುಳಿಯಲ್ಪಟ್ಟವು; ಆಕಾಶದಲ್ಲಿ ಹಾರಾಡುವ ಹಕ್ಕಿಗಳು ಅವುಗಳನ್ನು ತಿಂದುಬಿಟ್ಟವು.",
			"6": "<sup>6</sup> ಬೇರೆ ಕೆಲವು ಬೀಜಗಳು ಬಂಡೆಯ ಮೇಲೆ ಬಿದ್ದವು; ಅವು ಮೊಳೆತು ಬಂದರೂ ಬೇರೂರದ ಕಾರಣ ಒಣಗಿಹೋದವು.",
			"7": "<sup>7</sup> ಮತ್ತೆ ಕೆಲವು ಬೀಜಗಳು ಮುಳ್ಳುಪೊದೆಗಳೊಳಗೆ ಬಿದ್ದವು. ಮುಳ್ಳುಗಳು ಅವುಗಳೊಂದಿಗೆ ಬೆಳೆದು ಅವುಗಳನ್ನು ಅದುಮಿಬಿಟ್ಟವು.",
			"8": "<sup>8</sup> ಇನ್ನು ಕೆಲವು ಬೀಜಗಳು ಒಳ್ಳೆಯ ನೆಲದಲ್ಲಿ ಬಿದ್ದು ಮೊಳೆತು ನೂರರಷ್ಟು ಫಲವನ್ನು ಕೊಟ್ಟವು. ಈ ಮಾತುಗಳನ್ನು ಹೇಳಿ ಆತನು - ಕೇಳುವುದಕ್ಕೆ ಕಿವಿಯುಳ್ಳವನು ಕೇಳಲಿ ಎಂದು ಹೇಳಿದನು.",
			"9": "<sup>9</sup> ಯೇಸುವಿನ ಶಿಷ್ಯರು - ಈ ಸಾಮ್ಯದ ಅರ್ಥವೇನೆಂದು ಆತನನ್ನು ಕೇಳಿದಾಗ",
			"10": "<sup>10</sup> ಆತನು - ದೇವರ ರಾಜ್ಯದ ಗುಟ್ಟುಗಳನ್ನು ತಿಳಿಯುವ ವರವು ನಿಮಗೆ ಕೊಡಲಾಗಿದೆ, ಉಳಿದವರಿಗೆ ಕಣ್ಣಿದ್ದರೂ ನೋಡದಂತೆಯೂ, ಕೇಳಿಸಿಕೊಂಡರೂ ಗ್ರಹಿಸದಂತೆಯೂ ಸಾಮ್ಯರೂಪವಾಗಿ ಹೇಳಲಾಗುತ್ತದೆ.",
			"11": "<sup>11</sup> ಈ ಸಾಮ್ಯದ ಅರ್ಥವೇನಂದರೆ - ಆ ಬೀಜವಂದರೆ ದೇವರ ವಾಕ್ಯ.",
			"12": "<sup>12</sup> ಕೆಲವರು ವಾಕ್ಯವನ್ನು ಕೇಳಿದ ಕೂಡಲೆ ಸೈತಾನನು ಬಂದು ಅವರು ನಂಬಿ ರಕ್ಷಣೆ ಹೊಂದಬಾರದೆಂದು ವಾಕ್ಯವನ್ನು ಅವರ ಹೃದಯದಿಂದ ತೆಗೆದುಬಿಡುತ್ತಾನೆ; ಇವರೇ ದಾರಿಯ ಮಗ್ಗುಲಲ್ಲಿ ಬಿದ್ದಿರುವ ಬೀಜಗಳು.",
			"13": "<sup>13</sup> ಕೆಲವರು ವಾಕ್ಯವನ್ನು ಕೇಳಿದಾಗಲೇ ಅದನ್ನು ಸಂತೋಷದಿಂದ ಅಂಗೀಕರಿಸುತ್ತಾರೆ; ಇವರಿಗೆ ಬೇರಿಲ್ಲದ ಕಾರಣ ಸ್ವಲ್ಪಕಾಲ ಮಾತ್ರವೇ ನಂಬಿಕೊಂಡಿದ್ದು ಶೋಧನೆಯ ಕಾಲದಲ್ಲಿ ಬಿದ್ದು ಹೋಗುತ್ತಾರೆ; ಇವರೇ ಬಂಡೆಯ ಮೇಲೆ ಬೀಳಲ್ಪಟ್ಟ ಬೀಜಗಳು.",
			"14": "<sup>14</sup> ಇನ್ನು ಕೆಲವರು ವಾಕ್ಯವನ್ನು ಕೇಳಿದ ಮೇಲೆ ಬರಬರುತ್ತಾ ಈ ಜೀವನದಲ್ಲಿ ಆಗುವ ಚಿಂತೆ, ಐಶ್ವರ್ಯ ಭೋಗಗಳಿಂದ ಅಡಗಿಸಲ್ಪಟ್ಟು ಫಲವನ್ನು ಕೊಡಲಾರದವರು; ಇವರೇ ಮುಳ್ಳುಗಿಡಗಳಲ್ಲಿ ಬಿದ್ದ ಬೀಜವಾಗಿರುವವರು.",
			"15": "<sup>15</sup> ಮತ್ತೆ ಕೆಲವರು ವಾಕ್ಯವನ್ನು ಕೇಳಿ ಸುಗುಣವುಳ್ಳ ತಮ್ಮ ಒಳ್ಳೆಯ ಹೃದಯದಲ್ಲಿ ಅದನ್ನು ಸಂಗ್ರಹಿಸಿ ಇಟ್ಟುಕೊಂಡು ತಾಳ್ಮೆಯಿಂದ ಫಲವನ್ನು ಕೊಡುತ್ತಾರೆ; ಇವರೇ ಒಳ್ಳೆಯ ನೆಲದಲ್ಲಿ ಬಿದ್ದ ಬೀಜಗಳು.",
			"16": "<sup>16</sup> ಇದಲ್ಲದೆ ಯಾರೂ ದೀಪವನ್ನು ಹಚ್ಚಿ ಪಾತ್ರೆಯಿಂದ ಮುಚ್ಚುವುದಿಲ್ಲ, ಮಂಚದ ಕೆಳಗೂ ಇಡುವುದಿಲ್ಲ; ಮನೆಯೊಳಗೆ ಬರುವವರಿಗೆ ಬೆಳಕು ಕಾಣಿಸುವಂತೆ ಅದನ್ನು ದೀಪಸ್ತಂಭದ ಮೇಲೆ ಇಡುತ್ತಾರಷ್ಟೆ.",
			"17": "<sup>17</sup> ಬೆಳಕಿಗೆ ಬಾರದ ಯಾವ ರಹಸ್ಯವೂ ಇಲ್ಲ. ಮತ್ತು ಪ್ರಕಟವಾಗದೆ ಇರುವಂಥ ಒಂದು ಗುಟ್ಟೂ ಇರುವುದಿಲ್ಲ.",
			"18": "<sup>18</sup> ಆದ್ದರಿಂದ ನೀವು ಹೇಗೆ ಕಿವಿಗೊಡಬೇಕೋ ನೋಡಿಕೊಳ್ಳಿರಿ; ಯಾಕೆಂದರೆ ಇದ್ದವನಿಗೆ ಕೊಡಲ್ಪಡುವದು; ಇಲ್ಲದವನ ಕಡೆಯಿಂದ ಅವನು ತನ್ನದೆಂದು ಎಣಿಸುವಂಥದೂ ತೆಗೆಯಲ್ಪಡುವದು ಅಂದನು.",
			"19": "<sup>19</sup> ಯೇಸುವಿನ ತಾಯಿಯೂ ತಮ್ಮಂದಿರೂ ಆತನಿದ್ದಲ್ಲಿಗೆ ಬಂದು ಜನರ ಗುಂಪಿನ ನಿಮಿತ್ತ ಆತನ ಬಳಿಗೆ ಹೋಗಲಾರದೆ ಇದ್ದರು.",
			"20": "<sup>20</sup> ಜನರು ಆತನಿಗೆ - ನಿನ್ನ ತಾಯಿಯು ನಿನ್ನ ಸಹೋದರರು ನಿನ್ನನ್ನು ಕಾಣಬೇಕೆಂದು ಹೊರಗೆ ನಿಂತಿದ್ದಾರೆ ಎಂದು ಹೇಳಿದಾಗ",
			"21": "<sup>21</sup> ಆತನು ಅವರಿಗೆ - ಯಾರು ದೇವರ ವಾಕ್ಯವನ್ನು ಕೇಳಿ ಅದರಂತೆ ನಡೆಯುತ್ತಾರೋ ಅವರೇ ನನಗೆ ತಾಯಿಯು ಸಹೋದರರು ಆಗಿದ್ದಾರೆ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"22": "<sup>22</sup> ಒಂದು ದಿನ ಯೇಸು ತನ್ನ ಶಿಷ್ಯರೊಂದಿಗೆ ದೋಣಿಯನ್ನು ಹತ್ತಿ ಸಮುದ್ರದ ಆಚೇ ದಡಕ್ಕೆ ಹೋಗೋಣ ಎಂದು ಹೇಳಲು ಅವರು ದೋಣಿಯನ್ನು ಸಾಗಿಸಿ ಹೊರಟರು.",
			"23": "<sup>23</sup> ಹೋಗುತ್ತಿರುವಾಗ ಆತನಿಗೆ ನಿದ್ರೆ ಹತ್ತಿತು; ಅಷ್ಟರಲ್ಲಿ ಬಿರುಗಾಳಿ ಸಮುದ್ರದಲ್ಲಿ ಬೀಸಿ ದೋಣಿಯೊಳಗೆ ನೀರು ತುಂಬಿಕೊಂಡದ್ದರಿಂದ ಅವರೆಲ್ಲರು ಅಪಾಯಕ್ಕೆ ಗುರಿಯಾದರು.",
			"24": "<sup>24</sup> ಹೀಗಿರಲಾಗಿ ಅವರು ಆತನ ಹತ್ತಿರ ಬಂದು - ಗುರುವೇ, ಗುರುವೇ, ನಾವು ಸಾಯುತ್ತಿದ್ದೇವೆ ಎಂದು ಹೇಳಿ ಆತನನ್ನು ಎಬ್ಬಿಸಿದರು. ಆಗ ಆತನು ಎದ್ದು ಗಾಳಿಯನ್ನೂ ಏರಿ ಬರುತ್ತಿದ್ದ ನೀರನ ಅಲೆಗಳನ್ನು ಗದರಿಸಿದನು. ಆಗ ಅವು ನಿಂತು, ಶಾಂತವಾಯಿತು.",
			"25": "<sup>25</sup> ತರುವಾಯ ಆತನು - ನಿಮ್ಮ ನಂಬಿಕೆ ಎಲ್ಲಿ? ಎಂದು ಅವರನ್ನು ಕೇಳಿದನು. ಅವರು ಭಯಪಟ್ಟು - ಈತನು ಯಾರಿರಬಹುದು? ಗಾಳಿಗೂ ನೀರಿಗೂ ಅಪ್ಪಣೆ ಕೊಡುತ್ತಾನೆ; ಅವು ಕೂಡ ಈತನು ಹೇಳಿದ ಹಾಗೆ ಕೇಳುತ್ತವಲ್ಲಾ ಎಂದು ಒಬ್ಬರಿಗೊಬ್ಬರು ಮಾತಾಡಿಕೊಂಡು ಆಶ್ಚರ್ಯಪಟ್ಟರು.",
			"26": "<sup>26</sup> ಆ ಮೇಲೆ ಅವರು ಗಲಿಲಾಯಕ್ಕೆ ಎದುರಾಗಿರುವ ಗೆರಸೇನರ ಸೀಮೆಗಯನ್ನು ತಲುಪಿದರು.",
			"27": "<sup>27</sup> ಯೇಸು ದಡಕ್ಕೆ ಇಳಿಯುತ್ತಲೇ ಆ ಊರಿನವನಾದ ಒಬ್ಬ ಮನುಷ್ಯನು ಆತನೆದುರಿಗೆ ಬಂದನು. ಅವನಿಗೆ ದೆವ್ವಗಳು ಹಿಡಿದಿದ್ದವು. ಅವನು ಬಹುಕಾಲದಿಂದ ವಸ್ತ್ರವನ್ನೇ ಧರಿಸದೆ ಮನೆಯಲ್ಲಿ ವಾಸಿಸದೆ ಸಮಾಧಿಯ ಗವಿಗಳಲ್ಲಿಯೇ ಇರುತ್ತಿದ್ದನು.",
			"28": "<sup>28</sup> ಇವನು ಯೇಸುವನ್ನು ಕಂಡು ಆರ್ಭಟಿಸಿ ಆತನ ಮುಂದೆ ಅಡ್ಡಬಿದ್ದು ಮಹಾಶಬ್ದದಿಂದ - ಯೇಸುವೇ, ಪರಾತ್ಪರನಾದ ದೇವರ ಮಗನೇ, ನನ್ನ ಗೊಡವೆ ನಿನಗೇಕೆ? ನನ್ನನ್ನು ಕಾಡಬೇಡವೆಂದು ನಿನ್ನನ್ನು ಬೇಡಿಕೊಳ್ಳುತ್ತೇನೆ ಅಂದನು.",
			"29": "<sup>29</sup> ಯಾಕೆಂದರೆ - ಈ ಮನುಷ್ಯನನ್ನು ಬಿಟ್ಟುಹೋಗಬೇಕೆಂದು ಆತನು ಆ ದೆವ್ವಕ್ಕೆ ಅಪ್ಪಣೆ ಕೊಟ್ಟಿದ್ದನು. ಅದು ಬಹು ಕಾಲದಿಂದ ಅವನನ್ನು ಹಿಡಿದಿತ್ತು; ಇದಲ್ಲದೆ ಅವನನ್ನು ಕಾವಲಲ್ಲಿಟ್ಟು ಸರಪಣಿಗಳಿಂದಲೂ ಬೇಡಿಗಳಿಂದಲೂ ಬಂಧಿಸಿದ್ದರೂ ಅವನು ಅವುಗಳನ್ನು ಮುರಿದುಹಾಕುತ್ತಿದ್ದನು; ಮತ್ತು ಆ ದೆವ್ವವು ಅವನನ್ನು ಅರಣ್ಯಪ್ರದೇಶಗಳಿಗೆ ಓಡಿಸುತ್ತಿತ್ತು.",
			"30": "<sup>30</sup> ಯೇಸುವು ಅವನಿಗೆ ನಿನ್ನ ಹೆಸರೇನೆಂದು ಕೇಳಲು ಅವನು - ನನ್ನ ಹೆಸರು ದಂಡು ಅಂದನು; ಯಾಕೆಂದರೆ ಬಹಳ ದೆವ್ವಗಳು ಅವನೊಳಗೆ ಹೊಕ್ಕಿದವು.",
			"31": "<sup>31</sup> ಪಾತಾಳಕ್ಕೆ ಹೋಗಿಬಿಡುವಂತೆ ನಮಗೆ ಆಜ್ಞೆಮಾಡಬೇಡವೆಂದು ಅವು ಆತನನ್ನು ಬೇಡಿಕೊಂಡವು.",
			"32": "<sup>32</sup> ಅಲ್ಲಿಯ ಗುಡ್ಡದ ಮೇಲೆ ಹಂದಿಗಳ ಹಿಂಡು ಮೇಯುತ್ತಿತ್ತು. ಆ ದೆವ್ವಗಳು - ಆ ಹಂದಿಗಳೊಳಗೆ ಸೇರಿ ಕೊಳ್ಳುವುದಕ್ಕೆ ನಮಗೆ ಅಪ್ಪಣೆಕೊಡಬೇಕೆಂದು ಆತನನ್ನು ಬೇಡಿಕೊಂಡವು.",
			"33": "<sup>33</sup> ಆತನು ಆಗಲಿ ಎಂದು ಅವುಗಳಿಗೆ ಅಪ್ಪಣೆಕೊಡಲು ದೆವ್ವಗಳು ಆ ಮನುಷ್ಯನೊಳಗಿಂದ ಹೊರಗೆ ಬಂದು ಹಂದಿಗಳೊಳಗೆ ಹೊಕ್ಕವು; ಆ ಹಂದಿಗಳು ಓಡಿ ಕಡಿದಾದ ಸ್ಥಳದಿಂದ ಕೆರೆಯೊಳಗೆ ಬಿದ್ದು ಉಸುರುಕಟ್ಟಿ ಸತ್ತು ಹೋದವು.",
			"34": "<sup>34</sup> ಮೇಯಿಸುವವರು ನಡೆದದ್ದನ್ನು ಕಂಡು ಓಡಿ ಹೋಗಿ ಆ ಊರಲ್ಲಿಯೂ ಹಳ್ಳಿಗಳಲ್ಲಿಯೂ ಅದನ್ನು ತಿಳಿಸಲು",
			"35": "<sup>35</sup> ಜನರು ನಡೆದ ಸಂಗತಿಯನ್ನು ನೋಡುವುದಕ್ಕೆ ಹೊರಟು ಯೇಸುವಿದ್ದಲ್ಲಿಗೆ ಬಂದಾಗ ದೆವ್ವಗಳು ಬಿಟ್ಟುಹೋಗಿದ್ದ ಆ ಮನುಷ್ಯನು ಬಟ್ಟೆಯನ್ನು ಧರಿಸಿಕೊಂಡು ಸ್ವಸ್ಥಬುದ್ಧಿಯಿಂದ ಯೇಸುವಿನ ಪಾದಗಳ ಬಳಿಯಲ್ಲಿ ಕುಳಿತಿರುವುದನ್ನು ಕಂಡು ಹೆದರಿದರು.",
			"36": "<sup>36</sup> ನಡೆದ ಸಂಗತಿಯನ್ನು ನೋಡಿದವರು ಆ ದೆವ್ವಹಿಡಿದಿದ್ದವನಿಗೆ ಬಿಡುಗಡೆಯಾದ ರೀತಿಯನ್ನು ಅವರಿಗೆ ತಿಳಿಸಲು",
			"37": "<sup>37</sup> ಗೆರಸೇನರ ಸುತ್ತಲಿರುವ ಸೀಮೆಯವರಿಗೆಲ್ಲಾ ಮಹಾ ಭಯಉಂಟಾಗಿ ಅವರು ಆತನನ್ನು - ನೀನು ನಮ್ಮನ್ನು ಬಿಟ್ಟುಹೋಗಬೇಕೆಂದು ಬೇಡಿಕೊಂಡರು.",
			"38": "<sup>38</sup> ಆತನು ದೋಣಿಯನ್ನು ಹತ್ತಿ ಹಿಂತಿರುಗಿ ಹೋದನು. ಹೊರಡುವಾಗ ದೆವ್ವಗಳಿಂದ ಬಿಡುಗಡೆಯಾದ ಆ ಮನುಷ್ಯನು - ನಾನು ನಿನ್ನ ಜೊತೆಯಲ್ಲಿಯೇ ಇರುತ್ತೇನೆ ಎಂದು ಆತನನ್ನು ಬೇಡಿಕೊಂಡಾಗ",
			"39": "<sup>39</sup> ಆತನು - ನೀನು ನಿನ್ನ ಮನೆಗೆ ಹಿಂತಿರುಗಿ ಹೋಗಿ ದೇವರು ನಿನಗೆ ಏನೇನು ಉಪಕಾರಗಳನ್ನು ಮಾಡಿದನೋ ಅದನ್ನೆಲ್ಲಾ ನಿನ್ನವರಿಗೆ ವಿವರವಾಗಿ ಹೇಳು ಎಂದು ಅವನನ್ನು ಕಳುಹಿಸಿಬಿಟ್ಟನು. ಅವನು ಹೊರಟುಹೋಗಿ ಯೇಸು ತನಗೆ ಮಾಡಿದ ಉಪಕಾರಗಳನ್ನು ಆ ಊರಲ್ಲೆಲ್ಲಾ ಸಾರಿದನು.",
			"40": "<sup>40</sup> ಯೇಸು ಹಿಂತಿರುಗಿ ಬಂದಾಗ ಜನರೆಲ್ಲರೂ ಆತನಿಗಾಗಿ ಕಾಯುತ್ತಿದ್ದು ಅವನನ್ನು ಸಂತೋಷದಿಂದ ಸ್ವೀಕರಿಸಿದರು",
			"41": "<sup>41</sup> ಆಗ ಸಭಾಮಂದಿರದ ಅಧಿಕಾರಿಯಾದ ಯಾಯಿರನೆಂಬ ಒಬ್ಬ ಮನುಷ್ಯನು ಬಂದು ಯೇಸುವಿನ ಪಾದಗಳಿಗೆ ಅಡ್ಡಬಿದ್ದನು. ಏಕೆಂದರೆ ಹೆಚ್ಚುಕಡಿಮೆ ಹನ್ನೆರಡು ವರುಷದವಳಾದ ಅವನ ಒಬ್ಬಳೇ ಮಗಳು ದೀನಾವಸ್ಥೆಯಲ್ಲಿದ್ದು ಸಾಯುವಸ್ಥಿತಿಯಲ್ಲಿದ್ದ ಕಾರಣ ಆತನನ್ನು ತನ್ನ ಮನೆಗೆ ಬರಬೇಕೆಂದು ಬೇಡಿಕೊಂಡನು. ಯೇಸು ಹೋಗುತ್ತಿರುವಲ್ಲಿ ಜನರು ಗುಂಪುಗುಂಪಾಗಿ ಸೇರಿ ಆತನನ್ನು ನೂಕುತ್ತಿದ್ದರು.",
			"43": "<sup>43</sup> ಆಗ ಹನ್ನೆರಡು ವರುಷದಿಂದ ರಕ್ತಸ್ರಾವ ರೋಗವಿದ್ದ ಒಬ್ಬ ಹೆಂಗಸು ಅಲ್ಲಿಗೆ ಬಂದಿದ್ದಳು.",
			"44": "<sup>44</sup> ಆಕೆಯು ತನ್ನ ಬಳಿ ಇದ್ದ ಹಣವನ್ನೆಲ್ಲ ವ್ಯಚ್ಚಮಾಡಿದರೂ ಒಬ್ಬ ವೈದ್ಯರಿಂದಲೂ ಗುಣಹೊಂದಲಾರದೆ ಇದ್ದಳು. ಆಕೆಯು ಹಿಂದಿನಿಂದ ಬಂದು ಆತನ ಅಂಗಿಯ ಅಂಚನ್ನು ಮುಟ್ಟಿದಳು; ಮುಟ್ಟುತ್ತಲೇ ಆಕೆಗೆ ರಕ್ತಸ್ತ್ರವವಾಗುವುದು ನಿಂತಿತು.",
			"45": "<sup>45</sup> ಆಗ ಯೇಸುವು - ನನ್ನನ್ನು ಮುಟ್ಟಿದವರಾರು ಎಂದು ಕೇಳಲು ಎಲ್ಲರೂ - ನಾನಲ್ಲ, ನಾನಲ್ಲ ಅನ್ನಲು ಪೇತ್ರನು - ಗುರುವೇ, ಎಷ್ಟೋ ಜನರು ನಿನ್ನನ್ನು ಒತ್ತಿಕೊಂಡು ಮೈಮೇಲೆ ನೂಕುತ್ತಿದ್ದಾರಲ್ಲಾ ಅಂದನು.",
			"46": "<sup>46</sup> ಆದರೆ ಯೇಸುವು - ಯಾರೋ ಒಬ್ಬರು ನನ್ನನ್ನು ಮುಟ್ಟಿದರು; ನನ್ನಿಂದ ಶಕ್ತಿಯು ಹೊರಟಿತೆಂಬದು ನನಗೆ ಗೊತ್ತಾಯಿತು ಅಂದಾಗ",
			"47": "<sup>47</sup> ಆ ಹೆಂಗಸು ತಾನು ಮರೆಯಾಗಿಲ್ಲವೆಂದು ತಿಳಿದು ನಡುಗುತ್ತಾ ಬಂದು ಯೇಸುವಿಗೆ ಅಡ್ಡಬಿದ್ದು ತಾನು ಇಂಥ ಕಾರಣದಿಂದ ಮುಟ್ಟಿದೆನೆಂತಲೂ ಮುಟ್ಟಿದ ಕೂಡಲೆ ತನಗೆ ವಾಸಿಯಾಯಿತೆಂದೂ ಎಲ್ಲರ ಮುಂದೆ ತಿಳಿಸಿದಳು.",
			"48": "<sup>48</sup> ಆತನು ಆಕೆಗೆ - ಮಗಳೇ, ನಿನ್ನ ನಂಬಿಕೆಯೇ ನಿನ್ನನ್ನು ಸ್ವಸ್ಥಮಾಡಿತು; ಸಮಾಧಾನದಿಂದ ಹೋಗು ಎಂದು ಹೇಳಿದನು.",
			"49": "<sup>49</sup> ಆತನು ಇನ್ನೂ ಮಾತಾಡುತ್ತಿರುವಲ್ಲಿ ಸಭಾಮಂದಿರದ ಅಧಿಕಾರಿಯ ಕಡೆಯವನೊಬ್ಬನು ಬಂದು - ನಿನ್ನ ಮಗಳು ಸತ್ತುಹೋದಳು; ಗುರುವಿಗೆ ತೊಂದರೆ ಕೊಡಬೇಡ ಅಂದನು.",
			"50": "<sup>50</sup> ಆದರೆ ಯೇಸು ಅದನ್ನು ಕೇಳಿ ಅವನಿಗೆ - ಅಂಜಬೇಡ, ನಂಬಿಕೆ ಮಾತ್ರ ಇರಲಿ, ಆಕೆ ಬದುಕುವಳು ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"51": "<sup>51</sup> ತರುವಾಯ ಆತನು ಆ ಮನೆಗೆ ಹೋಗಿ ಪೇತ್ರ ಯೋಹಾನ ಯಾಕೋಬನು ಮತ್ತು ಆ ಹುಡುಗಿಯ ತಂದೆತಾಯಿಯರು ಇಷ್ಟು ಮಂದಿಯನ್ನು ಹೊರತು ಬೇರೆ ಯಾರನ್ನೂ ತನ್ನ ಸಂಗಡ ಒಳಕ್ಕೆ ಬರಗೊಡಿಸಲಿಲ್ಲ.",
			"52": "<sup>52</sup> ಎಲ್ಲರು ಅಳುತ್ತಾ ಆಕೆಗೋಸ್ಕರ ಎದೆಬಡಿದುಕೊಳ್ಳುತ್ತಾ ಇದ್ದರು. ಯೇಸು - ಅಳಬೇಡಿರಿ, ಆಕೆ ಸತ್ತಿಲ್ಲ, ನಿದ್ರಿಸುತ್ತಿದ್ದಾಳೆ ಅನ್ನಲೂ",
			"53": "<sup>53</sup> ಜನರು ಆಕೆ ಸತ್ತಳೆಂದು ತಿಳಿದುಕೊಂಡಿದ್ದರಿಂದ ಆತನನ್ನು ಹಾಸ್ಯಮಾಡಿದರು.",
			"54": "<sup>54</sup> ಆದರೆ ಆತನು ಆಕೆಯ ಕೈ ಹಿಡಿದು - ಮಗಳೇ, ಎದ್ದೇಳು ಎಂದು ಕೂಗಿ ಹೇಳಿದನು.",
			"55": "<sup>55</sup> ಆಕೆಯ ಪ್ರಾಣ ತಿರಿಗಿ ಬಂದು ತಕ್ಷಣವೇ ಆಕೆ ಎದ್ದಳು. ತರುವಾಯ ಆತನು - ಈಕೆಗೆ ಊಟಮಾಡಿಸಿರಿ ಎಂದು ಅಪ್ಪಣೆಕೊಟ್ಟನು.",
			"56": "<sup>56</sup> ಆಕೆಯ ತಂದೆತಾಯಿಗಳು ಬೆರಗಾದರು. ಆತನು - ಈ ನಡೆದ ಸಂಗತಿಯನ್ನು ಯಾರಿಗೂ ತಿಳಿಸಬೇಡಿರೆಂದು ಖಂಡಿತವಾಗಿ ಹೇಳಿದನು."
		},
		"9": {
			"1": "<sup>1</sup> ಆತನು ತನ್ನ ಹನ್ನೆರಡು ಮಂದಿ ಶಿಷ್ಯರನ್ನು ಒಟ್ಟಿಗೆ ಕರೆದು ಅವರಿಗೆ ಎಲ್ಲಾ ದೆವ್ವಗಳನ್ನು ಬಿಡಿಸುವ ಮತ್ತು ರೋಗಗಳನ್ನು ವಾಸಿಮಾಡುವ ಶಕ್ತಿ ಮತ್ತು ಅಧಿಕಾರಗಳನ್ನು ಕೊಟ್ಟು",
			"2": "<sup>2</sup> ದೇವರ ರಾಜ್ಯದ ವಿಷಯವನ್ನು ಸಾರುವುದಕ್ಕೂ ಅಸ್ವಸ್ಥರಾದವರನ್ನು ಸ್ವಸ್ಥಮಾಡುವದಕ್ಕೂ ಅವರನ್ನು ಕಳುಹಿಸಿದನು.",
			"3": "<sup>3</sup> ಕಳುಹಿಸುವಾಗ ಅವರಿಗೆ ಹೇಳಿದ್ದೇನಂದರೆ - ದಾರಿಗೆ ಏನನ್ನೂ ತೆಗೆದುಕೊಂಡುಹೋಗಬೇಡಿರಿ, ಕೋಲು, ಚೀಲ, ಬುತ್ತಿ, ಹಣ ಬೇಡ; ಎರಡಂಗಿಗಳಿರಬಾರದು.",
			"4": "<sup>4</sup> ಇದಲ್ಲದೆ ನೀವು ಯಾವ ಮನೆಯಲ್ಲಿ ಇಳುಕೊಂಡಿರುವಿರೋ ಅಲ್ಲೇ ಉಳಿಯಿರಿ, ಅಲ್ಲಿಂದಲೇ ಹೊರಡಿರಿ.",
			"5": "<sup>5</sup> ಮತ್ತು ಯಾವ ಊರಿನವರಾದರೂ ನಿಮ್ಮನ್ನು ಸ್ವಗತಿಸದೆ ಹೋದಲ್ಲಿ, ನೀವು ಆ ಊರನ್ನು ಬಿಟ್ಟುಹೊರಡುವಾಗ ನಿಮ್ಮ ಕಾಲಿಗೆ ಹತ್ತಿದ ಧೂಳನ್ನು ಝಾಡಿಸಿಬಿಡಿ; ಅದು ಅವರಿಗೆ ವಿರೋಧವಾಗಿ ಸಾಕ್ಷಿಯಾಗಿರಲಿ ಅಂದನು.",
			"6": "<sup>6</sup> ಆಗ ಅವರು ಹೊರಟುಹೋಗಿ ಎಲ್ಲೆಲ್ಲಿಯೂ ಸುವಾರ್ತೆಯನ್ನು ಸಾರುತ್ತಾ ರೋಗಿಗಳನ್ನು ವಾಸಿಮಾಡುತ್ತಾ ಗ್ರಾಮಗಳಲ್ಲೆಲ್ಲಾ ಸಂಚರಿಸಿದರು.",
			"7": "<sup>7</sup> ಹೀಗಿರಲಾಗಿ ಉಪರಾಜನಾದ ಹೆರೋದನು ನಡೆದ ಸಂಗತಿಗಳನ್ನೆಲ್ಲಾ ಕೇಳಿ ಬಹಳವಾಗಿ ಕಳವಳಪಡುವವನಾದನು. ಯಾಕೆಂದರೆ ಸತ್ತಿದ್ದ ಯೋಹಾನನು ತಿರಿಗಿ ಬದುಕಿಬಂದಿದ್ದಾನೆಂದು ಕೆಲವರೂ, ಎಲೀಯನು ಕಾಣಿಸಿಕೊಂಡನೆಂದು ಕೆಲವರೂ,",
			"8": "<sup>8</sup> ಪೂರ್ವದ ಪ್ರವಾದಿಗಳಲ್ಲಿ ಯಾರೋ ಒಬ್ಬನು ಜೀವದಿಂದ ಎದ್ದಿದ್ದಾನೆಂದು ಇನ್ನು ಕೆಲವರೂ ಹೇಳಿಕೊಳ್ಳುತ್ತಿದ್ದರು.",
			"9": "<sup>9</sup> ಆದರೆ ಹೆರೋದನು - ಯೋಹಾನನನ್ನು ನಾನೇ ಶಿರಚ್ಛೇಧನಮಾಡಿಸಿದೆನಷ್ಟೆ. ಹಾಗಿದ್ದಲಿ ಇವನಾರು? ಇವನ ವಿಷಯವಾಗಿ ಆಶ್ಚರ್ಯಕರವಾದ ಸಂಗತಿಗಳನ್ನು ಕೇಳುತ್ತೇನಲ್ಲಾ ಎಂದು ಹೇಳಿ ಅವನನ್ನು ನೋಡಬೇಕೆಂದು ಪ್ರಯತ್ನಿಸಿದನು.",
			"10": "<sup>10</sup> ಇತ್ತಲಾಗಿ ಶಿಷ್ಯರಾದ ಅಪೊಸ್ತಲರು ಹಿಂತಿರುಗಿ ಬಂದು ತಾವು ಮಾಡಿದ್ದನ್ನೆಲ್ಲಾ ಯೇಸುವಿಗೆ ವಿವರವಾಗಿ ಹೇಳಲು, ಆತನು ಅವರನ್ನು ಬೇತ್ಸಾಯಿದವೆಂಬ ಊರಿಗೆ ಕರೆದುಕೊಂಡುಹೋಗಿ ಅಲ್ಲಿಂದ ಅವರೊಂದಿಗೆ ಅಡವಿಗೆ ಹೋಗಲು,",
			"11": "<sup>11</sup> ಜನರ ಗುಂಪು ಇದನ್ನು ತಿಳಿದು ಯೇಸುವಿನ ಹಿಂದೆ ಬರಲು ಆತನು ಅವರನ್ನು ಪ್ರೀತಿಯಿಂದ ಸೇರಿಸಿಕೊಂಡು ದೇವರ ರಾಜ್ಯದ ವಿಷಯವಾಗಿ ಅವರ ಸಂಗಡ ಮಾತಾನಾಡಿ ಅವರಲ್ಲಿದ್ದ ರೋಗಿಗಳನ್ನು ಗುಣಪಡಿಸಿದನು.",
			"12": "<sup>12</sup> ಹೊತ್ತು ಕಳೆಯೂತ್ತಾ ಬರಲು. ಆ ಹನ್ನೆರಡು ಮಂದಿ ಶಿಷ್ಯರು ಯೇಸುವಿನ ಬಳಿಗೆ ಬಂದು - ಈ ಗುಂಪಿಗೆ ಅಪ್ಪಣೆಕೊಡು; ಇವರು ಸುತ್ತಲಿರುವ ಹಳ್ಳಿಗಳಿಗೆ ಹೋಗಿ ಇಳಕೊಂಡು ಊಟಕ್ಕೆ ಬೇಕಾದ ಪದಾರ್ಥಗಳನ್ನು ವ್ಯವಸ್ಥೆ ಮಾಡಿಕೊಳ್ಳಲಿ; ನಾವು ಇಲ್ಲಿ ಅಡವಿಯಲ್ಲಿ ಇದ್ದೇವಲ್ಲಾ ಎಂದು ಆತನಿಗೆ ಹೇಳಲಾಗಿ",
			"13": "<sup>13</sup> ಆತನು - ನೀವೇ ಅವರಿಗೆ ಊಟಕ್ಕೆ ಕೊಡಿರಿ ಅಂದನು. ಅದಕ್ಕವರು - ನಮ್ಮಲ್ಲಿ ಐದು ರೊಟ್ಟಿ ಎರಡು ಮೀನು ಹೊರತು ಹೆಚ್ಚೇನೂ ಇಲ್ಲ; ನಾವು ಹೋಗಿ ಈ ಜನರಿಗೆಲ್ಲಾ ಆಹಾರವನ್ನು ಕೊಂಡು ತರಬೇಕೋ? ಅಂದರು.",
			"14": "<sup>14</sup> ಅಲ್ಲಿ ಗಂಡಸರೇ ಹೆಚ್ಚು ಕಡಿಮೆ ಐದು ಸಾವಿರ ಮಂದಿಯಿದ್ದರು. ಆಗ ಯೇಸುವು ತನ್ನ ಶಿಷ್ಯರಿಗೆ - ಇವರನ್ನು ಒಂದೊಂದು ಪಂಕ್ತಿಗೆ ಹೆಚ್ಚುಕಡಿಮೆ ಐವತ್ತೈವತ್ತರಂತೆ ಕೂಡ್ರಿಸಿರಿ ಎಂದು ಹೇಳಲು",
			"15": "<sup>15</sup> ಅವರು ಹಾಗೆಯೇ ಎಲ್ಲರನ್ನೂ ಕೂಡ್ರಿಸಿದರು.",
			"16": "<sup>16</sup> ಆ ಮೇಲೆ ಯೇಸುವು ಆ ಐದು ರೊಟ್ಟಿ ಎರಡು ಮೀನುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಆಕಾಶದ ಕಡೆಗೆ ನೋಡಿ ಅವುಗಳನ್ನು ಆಶೀರ್ವದಿಸಿ ಮುರಿದು ಶಿಷ್ಯರ ಕೈಗೆ ಕೊಟ್ಟು - ಇದನ್ನು ಜನರ ಗುಂಪಿಗೆ ಹಂಚಿರಿ ಅಂದನು.",
			"17": "<sup>17</sup> ಅವರೆಲ್ಲರು ಊಟಮಾಡಿ ತೃಪ್ತರಾದರು; ನಂತರ ಉಳಿದ ತುಂಡುಗಳನ್ನು ಕೂಡಿಸಲಾಗಿ ಅವು ಹನ್ನೆರಡು ಪುಟ್ಟಿ ಆದವು.",
			"18": "<sup>18</sup> ಒಂದು ದಿನ ಯೇಸುವು ಏಕಾಂತವಾಗಿ ಪ್ರಾರ್ಥನೆಮಾಡುತ್ತಿರುವಾಗ ಆತನ ಶಿಷ್ಯರು ಆತನ ಸಂಗಡ ಇದ್ದರು. ಅನಂತರ ಆತನು ಅವರಿಗೆ ಜನರು ನನ್ನನ್ನು ಕುರಿತು ಏನು ಹೇಳುತ್ತಾರೆ ಎಂದು ಶಿಷ್ಯರನ್ನು ಕೇಳಲು ಅವರು -",
			"19": "<sup>19</sup> ನಿನ್ನನ್ನು ಸ್ನಾನಿಕನಾದ ಯೋಹಾನನು ಎಂದೂ; ಕೆಲವರು ಎಲೀಯನು ಎಂದೂ; ಇನ್ನು ಕೆಲವರು ಪೂರ್ವದ ಪ್ರವಾದಿಗಳಲ್ಲಿ ಯಾರೋ ಒಬ್ಬನು ಜೀವದಿಂದ ಎದ್ದಿದ್ದಾನೆ ಎಂದು ಅನ್ನುತ್ತಿದ್ದಾರೆ ಎಂದು ಹೇಳಿದರು.",
			"20": "<sup>20</sup> ಯೇಸು ಅವರನ್ನು - ಆದರೆ ನೀವು ನನ್ನನ್ನು ಯಾರು ಅನ್ನುತ್ತೀರಿ ಎಂದು ಕೇಳಲಾಗಿ ಪೇತ್ರನು - ನೀನು ದೇವರಿಂದ ಬರಬೇಕಾಗಿರುವ ಕ್ರಿಸ್ತನು ಎಂದು ಉತ್ತರ ಕೊಟ್ಟನು.",
			"21": "<sup>21</sup> ಆಗ ಯೇಸುವು - ಇದನ್ನು ಯಾರಿಗೂ ಹೇಳಬೇಡಿರಿ ಎಂದು ಬಹು ಖಂಡಿತವಾಗಿ ಅವರಿಗೆ ಅಪ್ಪಣೆ ಕೊಟ್ಟು -",
			"22": "<sup>22</sup> ಮನುಷ್ಯಕುಮಾರನು ಬಹು ಕಷ್ಟಗಳನ್ನನುಭವಿಸಿ ಹಿರಿಯರಿಂದಲೂ ಮಹಾಯಾಜಕರಿಂದಲೂ ಶಾಸ್ತ್ರಿಗಳಿಂದಲೂ ನಿರಾಕರಿಸಲ್ಪಟ್ಟು ಕೊಲ್ಲಲ್ಪಟ್ಟು ಮೂರನೆಯ ದಿನದಲ್ಲಿ ಜೀವಿತನಾಗಿ ಎಬ್ಬಿಸಲ್ಪಡಬೇಕಾಗಿದೆ ಎಂದು ಹೇಳಿದನು.",
			"23": "<sup>23</sup> ಇದಲ್ಲದೆ ಅತನು ಎಲ್ಲರಿಗೂ ಹೇಳಿದ್ದೇನಂದರೆ - ಯಾವನಿಗಾದರೂ ನನ್ನ ಹಿಂದೆ ಬರುವುದಕ್ಕೆ ಮನಸ್ಸಿದ್ದರೆ ಅವನು ತನ್ನನ್ನು ತಾನು ನಿರಾಕರಿಸಿ ತನ್ನ ಶಿಲುಬೆಯನ್ನು ಪ್ರತಿದಿನ ಹೊತ್ತು ಕೊಂಡು ನನ್ನ ಹಿಂದೆ ಪ್ರತಿದಿನ ಬರಲಿ.",
			"24": "<sup>24</sup> ತನ್ನ ಪ್ರಾಣವನ್ನು ಉಳಿಸಿಕೊಳ್ಳಬೇಕೆಂದಿರುವವನು ಅದನ್ನು ಕಳಕೊಳ್ಳುವನು; ಆದರೆ ನನ್ನ ನಿಮಿತ್ತವಾಗಿ ತನ್ನ ಪ್ರಾಣವನ್ನು ಕಳಕೊಳ್ಳುವವನು ಅದನ್ನು ಉಳಿಸಿಕೊಳ್ಳುವನು.",
			"25": "<sup>25</sup> ಒಬ್ಬ ಮನುಷ್ಯನು ಲೋಕವನ್ನೆಲ್ಲಾ ಸಂಪಾದಿಸಿಕೊಂಡರೂ ತನ್ನನ್ನೇ ತಾನು ಹಾಳುಮಾಡಿಕೊಂಡರೆ ಅಥವಾ ನಷ್ಟಪಡಿಸಿಕೊಂಡರೆ ಅವನಿಗೆ ಪ್ರಯೋಜನವೇನು?",
			"26": "<sup>26</sup> ಯಾವನು ನನಗೂ ನನ್ನ ಮಾತುಗಳಿಗೂ ನಾಚಿಕೊಳ್ಳುತ್ತಾನೋ ಅವನಿಗೆ ಮನುಷ್ಯಕುಮಾರನು ತನಗೂ ತನ್ನ ತಂದೆಗೂ ಪರಿಶುದ್ಧ ದೂತರಿಗೂ ಇರುವ ಮಹಾಪ್ರಭಾವದೊಡನೆ ಬರುವಾಗ ಅವನ ಕುರಿತು ನಾಚಿಕೊಳ್ಳುವನು.",
			"27": "<sup>27</sup> ನಿಮಗೆ ಸತ್ಯವಾಗಿ ಹೇಳುತ್ತೇನೆ, ಇಲ್ಲಿ ನಿಂತಿರುವವರೊಳಗೆ ಕೆಲವರು ದೇವರ ರಾಜ್ಯವನ್ನು ನೋಡುವ ತನಕ ಮರಣಹೊಂದುವುದಿಲ್ಲವೆಂದು ಹೇಳಿದನು.",
			"28": "<sup>28</sup> ಯೇಸು ಈ ಮಾತುಗಳನ್ನು ಹೇಳಿ ಸುಮಾರು ಎಂಟು ದಿವಸಗಳಾದ ಮೇಲೆ ಪೇತ್ರ ಯೋಹಾನ ಯಾಕೋಬರನ್ನು ಕರಕೊಂಡು ಪ್ರಾರ್ಥನೆಮಾಡುವುದಕ್ಕೆ ಬೆಟ್ಟವನ್ನು ಹತ್ತಿದನು.",
			"29": "<sup>29</sup> ಆತನು ಪ್ರಾರ್ಥನೆಮಾಡುತ್ತಿರಲಾಗಿ ಆತನ ಮುಖಭಾವವು ಬದಲಾಯಿತು.",
			"30": "<sup>30</sup> ಆತನ ಉಡುಪು ಬೆಳ್ಳಗಾಗಿ ಮಿಂಚುತ್ತಾ ಬಂತು. ಇದಲ್ಲದೆ ಇಬ್ಬರು ಪುರುಷರು ಆತನ ಸಂಗಡ ಮಾತನಾಡುತ್ತಿದ್ದರು! ಅವರು ಯಾರೆಂದರೆ ಮೋಶೆಯೂ ಎಲೀಯನೂ.",
			"31": "<sup>31</sup> ಅವರು ವೈಭವದೊಡನೆ ಕಾಣಿಸಿಕೊಂಡು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಆತನು ಹೊಂದಬೇಕಾಗಿದ್ದ ಮರಣದ ವಿಷಯವಾಗಿ ಮಾತನಾಡುತ್ತಿದ್ದರು.",
			"32": "<sup>32</sup> ಇಷ್ಟರಲ್ಲಿ ಪೇತ್ರನಿಗೂ ಅವನ ಸಂಗಡ ಇದ್ದವರಿಗೂ ನಿದ್ರೆಯಿಂದ ಮೈಭಾರವಾಗಿತ್ತು. ಆದರೆ ಎಚ್ಚರವಾದ ಮೇಲೆ ಅವರು ಆತನ ಮಹಿಮೆಯನ್ನೂ ಆತನ ಸಂಗಡ ನಿಂತಿದ್ದ ಆ ಇಬ್ಬರು ಪುರುಷರನ್ನೂ ಕಂಡರು.",
			"33": "<sup>33</sup> ಅವರು ಯೇಸುವನ್ನು ಬಿಟ್ಟುಹೋಗುತ್ತಿರುವಾಗ ಪೇತ್ರನು ಯೇಸುವಿಗೆ - ಗುರುವೇ ನಾವು ಇಲ್ಲೇ ಇರುವುದು ಒಳ್ಳೆಯದು, ಮೂರು ಗುಡಾರಗಳನ್ನು ಕಟ್ಟುವೆವು; ನಿನಗೊಂದು ಎಲೀಯನಿಗೊಂದು ಹಾಗೂ ಮೋಶೆಗೊಂದು ಎಂದು ಹೇಳಿದನು. ತಾನು ಏನು ಹೇಳುತ್ತಿದ್ದೇನೆನ್ನುವುದು ತನಗೇ ಗೊತ್ತಾಗಲಿಲ್ಲ.",
			"34": "<sup>34</sup> ಅವನು ಇದನ್ನು ಹೇಳುತ್ತಿರುವಲ್ಲಿ ಮೋಡವು ಬಂದು ಅವರ ಮೇಲೆ ಕವಿಯಿತು. ಮೋಡವು ಅವರನ್ನು ಆವರಿಸಿಕೊಂಡಾಗ ಅವರು ಹೆದರಿದರು.",
			"35": "<sup>35</sup> ಆಗ ಆ ಮೋಡದೊಳಗಿಂದ; “ಈತನು ನನ್ನ ಮಗನು. ನಾನು ಆರಿಸಿಕೊಂಡವನು; ಈತನ ಮಾತನ್ನು ಕೇಳಿರಿ” ಎಂಬ ವಾಣಿ ಆಯಿತು.",
			"36": "<sup>36</sup> ಆ ವಾಣಿ ಆದ ಮೇಲೆ ಯೇಸುವನ್ನು ಮಾತ್ರ ಕಂಡರು. ತಾವು ಕಂಡ ಈ ಸಂಗತಿಗಳಲ್ಲಿ ಒಂದನ್ನಾದರೂ ಆ ದಿವಸಗಳಲ್ಲಿ ಯಾರಿಗೂ ತಿಳಿಸದೆ ಸುಮ್ಮನಿದ್ದರು.",
			"37": "<sup>37</sup> ಮರುದಿವಸ ಅವರು ಬೆಟ್ಟದಿಂದ ಇಳಿದು ಬಂದಾಗ ಜನರ ದೊಡ್ಡ ಗುಂಪೊಂದು ಆತನನ್ನು ಎದುರುಗೊಂಡರು.",
			"38": "<sup>38</sup> ಆಗ ಆ ಗುಂಪಿನಲ್ಲಿದ್ದ ಒಬ್ಬನು - ಬೋಧಕನೇ, ನನ್ನ ಮಗನನ್ನು ಕಟಾಕ್ಷಿಸು ಎಂದು ನಿನ್ನನ್ನು ಬೇಡಿಕೊಳ್ಳುತ್ತೇನೆ. ನನಗೆ ಇವನೊಬ್ಬನೇ ಮಗನು. ಇವನನ್ನು ದೆವ್ವಹಿಡಿಯುತ್ತದೆ, ಹಿಡಿಯುತ್ತಲೇ ಕೂಗಿಕೊಳ್ಳುತ್ತಾನೆ.",
			"39": "<sup>39</sup> ಅದು ಬಾಯಲ್ಲಿ ನೊರೆ ಬರುವಷ್ಟು ಇವನನ್ನು ಒದ್ದಾಡಿಸುತ್ತದೆ; ಮತ್ತು ಬಹು ಕಷ್ಟಕೊಟ್ಟು ತೊಂದರೆಪಡಿಸಿದ ಹೊರತು ಬಿಡುವುದಿಲ್ಲ.",
			"40": "<sup>40</sup> ಅದನ್ನು ಬಿಡಿಸಬೇಕೆಂದು ನಿನ್ನ ಶಿಷ್ಯರನ್ನು ಬೇಡಿಕೊಂಡೆನು. ಆದರೆ ಅವರಿಂದ ಆಗದೆಹೋಯಿತು ಎಂದು ಹೇಳಿದನು.",
			"41": "<sup>41</sup> ಅದಕ್ಕೆ ಯೇಸು - ಎಲಾ ನಂಬಿಕೆಯಿಲ್ಲದಂಥ ಮೂರ್ಖಸಂತಾನವೇ, ನಾನು ಇನ್ನೆಷ್ಟು ದಿನ ನಿಮ್ಮ ಸಂಗಡ ಇದ್ದು ನಿಮ್ಮನ್ನು ಸಹಿಸಿಕೊಳ್ಳಲಿ? ನಿನ್ನ ಮಗನನ್ನು ಇಲ್ಲಿಗೆ ಕರಕೊಂಡು ಬಾ ಅಂದನು.",
			"42": "<sup>42</sup> ಆ ಹುಡುಗನು ಇನ್ನೂ ಬರುತ್ತಿರುವಾಗಲ್ಲೇ ಆ ದೆವ್ವವು ಅವನನ್ನು ನೆಲಕ್ಕೆ ಅಪ್ಪಳಿಸಿ ಬಹಳವಾಗಿ ಒದ್ದಾಡಿಸಿತು. ಆದರೆ ಯೇಸು ಆ ದೆವ್ವವನ್ನು ಗದರಿಸಿ ಆ ಹುಡುಗನಿಗೆ ವಾಸಿಮಾಡಿ ಅವನ ತಂದೆಯ ಕೈಗೆ ಕೊಟ್ಟನು.",
			"43": "<sup>43</sup> ಅದನ್ನು ನೋಡಿ ಎಲ್ಲರು ದೇವರ ಮಹತ್ಕಾರ್ಯಕ್ಕೆ ಬೆರಗಾದರು. ಯೇಸು ಮಾಡಿದ ಎಲ್ಲಾ ಮಹತ್ಕಾರ್ಯಗಳಿಗೆ ಎಲ್ಲರೂ ಆಶ್ಚರ್ಯಪಡುತ್ತಿರಲಾಗಿ ಆತನು ತನ್ನ ಶಿಷ್ಯರಿಗೆ -",
			"44": "<sup>44</sup> ನೀವಂತೂ ಈ ಮಾತುಗಳನ್ನು ನಿಮ್ಮ ಮನಸ್ಸಿನಲ್ಲಿ ಇಟ್ಟುಕೊಳ್ಳಿರಿ; ಯಾಕೆಂದರೆ ಮನುಷ್ಯಕುಮಾರನು ಮನುಷ್ಯರ ಕೈಗೆ ಒಪ್ಪಿಸಲ್ಪಡುವನು ಎಂದು ಹೇಳಿದನು.",
			"45": "<sup>45</sup> ಅವರು ಈ ಮಾತನ್ನು ಗ್ರಹಿಸಲಿಲ್ಲ; ಅದು ಅವರಿಗೆ ಮರೆಯಾಗಿದ್ದುದರಿಂದ ಅದರ ಅರ್ಥವನ್ನು ಅವರು ತಿಳಿಯದೆಹೋದರು. ಆ ಮಾತಿನ ವಿಷಯದಲ್ಲಿ ಅವರು ಆತನನ್ನು ಕೇಳುವುದಕ್ಕೆ ಅಂಜಿದರು.",
			"46": "<sup>46</sup> ತರುವಾಯ ಶಿಷ್ಯರೊಳಗೆ - ತಮ್ಮಲ್ಲಿ ಯಾವನು ಹೆಚ್ಚಿನವನು ಎಂಬ ವಿಚಾರಹುಟ್ಟಿತು.",
			"47": "<sup>47</sup> ಯೇಸು ಅವರ ಮನಸ್ಸಿನ ವಿಚಾರವನ್ನು ತಿಳಿದವನಾಗಿ, ಒಂದು ಚಿಕ್ಕಮಗುವನ್ನು ತನ್ನ ಪಕ್ಕದಲ್ಲಿ ನಿಲ್ಲಿಸಿ ಅವರಿಗೆ,",
			"48": "<sup>48</sup> ಯಾವನಾದರೂ ನನ್ನ ಹೆಸರಿನಲ್ಲಿ ಈ ಚಿಕ್ಕ ಮಗುವನ್ನು ಸೇರಿಸಿಕೊಂಡರೆ ನನ್ನನ್ನು ಸೇರಿಸಿಕೊಂಡ ಹಾಗಾಯಿತು; ಮತ್ತು ಯಾವನಾದರೂ ನನ್ನನ್ನು ಸೇರಿಸಿಕೊಂಡರೆ ನನ್ನನ್ನು ಕಳುಹಿಸಿಕೊಟ್ಟಾತನನ್ನೇ ಸೇರಿಸಿಕೊಂಡ ಹಾಗಾಯಿತು; ಯಾಕೆಂದರೆ ನಿಮ್ಮೆಲ್ಲರಲ್ಲಿ ಯಾವನು ಚಿಕ್ಕವನೋ ಅವನೇ ದೊಡ್ಡವನು ಎಂದು ಹೇಳಿದನು.",
			"49": "<sup>49</sup> ಅದಕ್ಕೆ ಯೋಹಾನನು - ಗುರುವೇ, ಯಾವನೋ ಒಬ್ಬನು ನಿನ್ನ ಹೆಸರನ್ನು ಹೇಳಿ ದೆವ್ವಬಿಡಿಸುವುದನ್ನು ನಾವು ಕಂಡು ಅವನು ನಮಗೆ ಸೇರಿದವನಲ್ಲವಾದ್ದರಿಂದ ಅವನಿಗೆ ಅಡ್ಡಿಮಾಡಿದೆವು ಅನ್ನಲು",
			"50": "<sup>50</sup> ಯೇಸು ಅವನಿಗೆ - ಅಡ್ಡಿಮಾಡಬೇಡಿರಿ; ನಿಮಗೆ ವಿರುದ್ಧವಲ್ಲದವನು ನಿಮ್ಮ ಪಕ್ಷದವನೇ ಎಂದು ಹೇಳಿದನು.",
			"51": "<sup>51</sup> ಯೇಸು ತಾನು ಪರಲೋಕಕ್ಕೆ ಏರಿಹೋಗುವ ದಿವಸಗಳು ಸಮೀಪಿಸುತ್ತಿರುವಾಗ ಯೆರೂಸಲೇಮಿಗೆ ಹೋಗುವುದಕ್ಕೆ ಮನಸ್ಸನ್ನು ದೃಢಮಾಡಿಕೊಂಡು",
			"52": "<sup>52</sup> ತನ್ನ ಮುಂದಾಗಿ ದೂತರನ್ನು ಕಳುಹಿಸಿದನು. ಇವರು ಹೊರಟು ಆತನಿಗೆ ಬೇಕಾದದ್ದನ್ನು ಸಿದ್ಧಮಾಡುವುದಕ್ಕಾಗಿ ಸಮಾರ್ಯದವರ ಒಂದು ಹಳ್ಳಿಗೆ ಬಂದರು.",
			"53": "<sup>53</sup> ಆದರೆ ಯೇಸುವು ಯೆರೂಸಲೇಮಿಗೆ ಹೋಗುವವನಾದ್ದರಿಂದ ಆ ಸಮಾರ್ಯದವರು ಆತನನ್ನು ಸೇರಿಸಿಕೊಳ್ಳಲಿಲ್ಲ.",
			"54": "<sup>54</sup> ಆತನ ಶಿಷ್ಯರಾದ ಯಾಕೋಬ, ಯೋಹಾನರು ಇದನ್ನು ಕಂಡು - ಕರ್ತನೇ, ಆಕಾಶದಿಂದ ಬೆಂಕಿಬಿದ್ದು ಇವರನ್ನು ನಾಶ ಮಾಡಲಿ ಎಂದು ನಾವು ಆಜ್ಞೆಮಾಡ ಬಹುದಲ್ಲವೇ? ಎಂದು ಕೇಳಲು",
			"55": "<sup>55</sup> ಯೇಸುವು ತಿರಿಗಿಕೊಂಡು ಅವರನ್ನು ಗದರಿಸಿ, (“ನೀವು ಎಂಥ ಆತ್ಮದವರಾಗಿದ್ದೀರೆಂದು ನಿಮಗೆ ತಿಳಿಯದು. ನರಪುತ್ರನು ಬಂದಿರುವುದು ಮಾನವ ಪ್ರಾಣಗಳನ್ನು ನಾಶಮಾದುವುದಕ್ಕಾಗಿ ಅಲ್ಲ ಅವರನ್ನು ರಕ್ಷಿಸುವುದಕ್ಕಾಗಿ ಬಂದನು”)",
			"56": "<sup>56</sup> ಅನಂತರ ಯೇಸು ಮತ್ತು ಶಿಷ್ಯರು ಹೊರಟು ಬೇರೆ ಹಳ್ಳಿಗೆ ಹೋದರು.",
			"57": "<sup>57</sup> ಅವರು ದಾರಿಯಲ್ಲಿ ಹೋಗುತ್ತಿರುವಾಗ ಒಬ್ಬನು ಯೇಸುವಿಗೆ - ನೀನು ಎಲ್ಲಿಗೇ ಹೋದರೂ ನಾನು ನಿನ್ನ ಹಿಂದೆ ಬರುತ್ತೇನೆ ಎಂದು ಹೇಳಲು ಆತನು ಅವನಿಗೆ - ನರಿಗಳಿಗೆ ಗುದ್ದುಗಳಿವೆ,",
			"58": "<sup>58</sup> ಆಕಾಶದಲ್ಲಿ ಹಾರಾಡುವ ಹಕ್ಕಿಗಳಿಗೆ ಗೂಡುಗಳಿವೆ; ಆದರೆ ಮನುಷ್ಯಕುಮಾರನಿಗೆ ತಲೆಯಿಡುವಷ್ಟು ಸ್ಥಳವೂ ಇಲ್ಲ ಎಂದು ಹೇಳಿದನು.",
			"59": "<sup>59</sup> ಯೇಸುವು ಮತ್ತೊಬ್ಬನಿಗೆ - ನನ್ನನ್ನು ಹಿಂಬಾಲಿಸು ಎಂದು ಹೇಳಲು ಅವನು - ಕರ್ತನೇ, ನಾನು ಮೊದಲು ಹೋಗಿ ನನ್ನ ತಂದೆಯ ಉತ್ತರಕ್ರಿಯೆಗಳನ್ನು ಮಾಡಿ ಬರುವಂತೆ ನನಗೆ ಅಪ್ಪಣೆಯಾಗಬೇಕು ಅಂದನು.",
			"60": "<sup>60</sup> ಆದರೆ ಯೇಸು ಅವನಿಗೆ - ಸತ್ತವರೇ ತಮ್ಮವರಲ್ಲಿ ಸತ್ತುಹೋದವರ ಉತ್ತರಕ್ರಿಯೆಗಳನ್ನು ಮಾಡಲಿ, ನೀನಂತೂ ಹೋಗಿ ದೇವರ ರಾಜ್ಯವನ್ನು ಪ್ರಸಿದ್ಧಿಪಡಿಸು ಎಂದು ಹೇಳಿದನು.",
			"61": "<sup>61</sup> ಇನ್ನೊಬ್ಬನು ಸಹ - ಕರ್ತನೇ, ನಾನು ನಿನ್ನನ್ನು ಹಿಂಬಾಲಿಸುತ್ತೇನೆ; ಆದರೆ ಮೊದಲು ನನ್ನ ಮನೆಯವರಿಗೆ ಹೇಳಿ ಬರುವದಕ್ಕೆ ನನಗೆ ಅಪ್ಪಣೆಯಾಗಬೇಕು ಅಂದನು.",
			"62": "<sup>62</sup> ಯೇಸು ಅವನಿಗೆ - ಯಾವನಾದರೂ ನೇಗಿಲಿನ ಮೇಲೆ ತನ್ನ ಕೈಯನ್ನಿಟ್ಟು ಹಿಂದಕ್ಕೆ ನೋಡಿದರೆ ಅವನು ದೇವರ ರಾಜ್ಯಕ್ಕೆ ತಕ್ಕವನಲ್ಲವೆಂದು ಹೇಳಿದನು."
		},
		"10": {
			"1": "<sup>1</sup> ಇದಾದ ಮೇಲೆ ಯೇಸು ಕರ್ತನು ಇನ್ನೂ ಎಪ್ಪತ್ತು ಮಂದಿಯನ್ನು ತನ್ನ ಶಿಷ್ಯಯರನ್ನಾಗಿ ನೇಮಿಸಿ ಅವರನ್ನು ಇಬ್ಬಿಬ್ಬರಾಗಿ ತಾನು ಹೋಗಬೇಕೆಂದಿದ್ದ ಪ್ರತಿಯೊಂದು ಊರಿಗೂ ಪ್ರತಿಯೊಂದು ಸ್ಥಳಕ್ಕೂ ಮುಂದಾಗಿ ಕಳುಹಿಸಿದನು.",
			"2": "<sup>2</sup> ಕಳುಹಿಸುವಾಗ ಅವರಿಗೆ ಹೇಳಿದ್ದೇನಂದರೆ - ಬೆಳೆಯು ಹೇರಳವಾಗಿದೆ, ಕೊಯ್ಲುಗಾರರು ಕೆಲವರೂ ಮಾತ್ರ ಇದ್ದಾರೆ; ಆದದರಿಂದ ಕೊಯ್ಲಿಗೆ ಆಳುಗಳನ್ನು ಕಳುಹಿಸಬೇಕೆಂದು ಯಜಮಾನನನ್ನು ಬೇಡಿಕೊಳ್ಳಿರಿ.",
			"3": "<sup>3</sup> ಹೋಗಿರಿ; ತೋಳಗಳ ನಡುವೆ ಕುರಿಮರಿಗಳಂತೆ ನಾನು ನಿಮ್ಮನ್ನು ಕಳುಹಿಸಿಕೊಡುತ್ತಿದ್ದೇನೆ.",
			"4": "<sup>4</sup> ನೋಡಿರಿ, ನೀವು ಹೋಗುವಾಗ ಹಣದ ಚೀಲವನ್ನಾಗಲಿ ಪ್ರವಾಸಕ್ಕೆ ಬೇಕಾಗುವದನ್ನಾಗಲಿ ಕಾಲಿಗೆ ಕೆರಗಳನ್ನಾಗಲಿ ತೆಗೆದುಕೊಂಡು ಹೋಗಬೇಡಿರಿ; ದಾರಿಯಲ್ಲಿ ಯಾರಿಗೂ ವಂದಿಸಬೇಡಿರಿ.",
			"5": "<sup>5</sup> ಇದಲ್ಲದೆ ನೀವು ಯಾವ ಮನೆಯೊಳಗೆ ಹೋದರೂ - “ಈ ಮನೆಗೆ ಶುಭವಾಗಲಿ” ಎಂದು ಮೊದಲು ಹೇಳಿರಿ.",
			"6": "<sup>6</sup> ಆಶೀರ್ವಾದ ಹೊಂದಲು ಯೋಗ್ಯನು ಆ ಮನೆಯಲ್ಲಿ ಇದ್ದರೆ ನಿಮ್ಮ ಆಶಿರ್ವಾದವು ಅವನ ಮೇಲೆ ನಿಲ್ಲುವದು; ಇಲ್ಲದಿದ್ದರೆ ಅದು ನಿಮಗೆ ಹಿಂತಿರುಗುವದು.",
			"7": "<sup>7</sup> ಆ ಮನೆಯಲ್ಲಿಯೇ ಇದ್ದುಕೊಂಡು ಅವರು ಕೊಡುವಂಥದನ್ನು ಊಟಮಾಡಿರಿ, ಕುಡಿಯಿರಿ. ಆಳು ತನ್ನ ಕೂಲಿಗೆ ಯೋಗ್ಯನಷ್ಟೆ. ಒಂದು ಮನೆಯನ್ನು ಬಿಟ್ಟು ಮತ್ತೊಂದು ಮನೆಗೆ ಹೋಗಿ ಇಳುಕೊಳ್ಳಬೇಡಿರಿ.",
			"8": "<sup>8</sup> ಮತ್ತು ನೀವು ಯಾವ ಊರಿಗಾದರೂ ಹೋದ ಮೇಲೆ ಆ ಊರಿನ ಜನರು ನಿಮ್ಮನ್ನು ಸೇರಿಸಿಕೊಂಡರೆ ಅವರು ನಿಮಗೆ ಬಡಿಸಿದ್ದನ್ನು ಊಟಮಾಡಿರಿ.",
			"9": "<sup>9</sup> ಅಲ್ಲಿರುವ ರೋಗಿಗಳನ್ನು ವಾಸಿಮಾಡಿ ಅವರಿಗೆ - ದೇವರ ರಾಜ್ಯವು ನಿಮ್ಮ ಸಮೀಪಕ್ಕೆ ಬಂದಿದೆ ಎಂದು ಹೇಳಿರಿ.",
			"10": "<sup>10</sup> ಆದರೆ ನೀವು ಯಾವ ಊರಿಗಾದರೂ ಹೋದ ಮೇಲೆ ಆ ಜನರು ನಿಮ್ಮನ್ನು ಸ್ವೀಕರಿಸದ್ದಿದ್ದರೆ, ನೀವು ಆ ಊರ ಬೀದಿಗಳಿಗೆ ಬಂದು -",
			"11": "<sup>11</sup> “ನಮ್ಮ ಪಾದಗಳಿಗೆ ಅಂಟಿರುವ ನಿಮ್ಮ ಊರಿನ ಧೂಳನ್ನು ಕೂಡ ನಿಮ್ಮ ವಿರುದ್ಧವಾಗಿ ಝಾಡಿಸಿಬಿಡುತ್ತೇವೆ; ಆದಾಗ್ಯೂ ದೇವರ ರಾಜ್ಯವು ಸಮಿಾಪಿಸಿದೆ ಎಂಬುದನ್ನು ಮಾತ್ರ ನೀವು ಗಮನದಲ್ಲಿಡಿ, ಎಂಬುದಾಗಿ ಹೇಳಿರಿ.",
			"12": "<sup>12</sup> ಆ ದಿನದಲ್ಲಿ ಅಂತಹ ಊರಿನ ಗತಿಯು ಸೊದೋಮ್ ಊರಿನ ಗತಿಗಿಂತಲೂ ಕಠಿಣವಾಗಿರುವದು ಎಂದು ನಿಮಗೆ ಹೇಳುತ್ತೇನೆ.",
			"13": "<sup>13</sup> ಅಯ್ಯೋ ಖೊರಾಜಿನೇ, ಅಯ್ಯೋ ಬೇತ್ಸಾಯಿದವೇ, ನಿಮ್ಮಲ್ಲಿ ನಡೆದ ಮಹತ್ಕಾರ್ಯಗಳು ತೂರ್ ಮತ್ತು ಸೀದೋನ್ ಪಟ್ಟಣಗಳಲ್ಲಿ ನಡೆದಿದ್ದರೆ ಅಲ್ಲಿಯವರು ಆಗಲೇ ಗೋಣೀತಟ್ಟು ಹೊದ್ದುಕೊಂಡು, ಬೂದಿಯಲ್ಲಿ ಕೂಳಿತುಕೊಂಡು ದೇವರ ಕಡೆಗೆ ತಿರುಗಿಕೊಳ್ಳುತ್ತಿದ್ದರು.",
			"14": "<sup>14</sup> ಆದರೆ ನ್ಯಾಯವಿಚಾರಣೆಯಲ್ಲಿ ನಿಮ್ಮ ಗತಿಗಿಂತಲೂ ತೂರ್ ಸೀದೋನ್ ಪಟ್ಟಣಗಳ ಗತಿಯು ಮೇಲಾಗಿರುವದು.",
			"15": "<sup>15</sup> ಎಲಾ ಕಪೆರ್ನೌಮೇ, ನೀನು ಪರಲೋಕಕ್ಕೆ ಏರಿಸಲ್ಪಡುವಿಯಾ? ಪಾತಾಳಕ್ಕೇ ಇಳಿಯುವಿ.",
			"16": "<sup>16</sup> ನಿಮ್ಮ ಮಾತನ್ನು ಕೇಳುವವನು ನನ್ನ ಮಾತನ್ನು ಕೇಳುವವನಾಗಿದ್ದಾನೆ; ನಿಮ್ಮನ್ನು ತಿರಸ್ಕರಿಸುವವನು ನನ್ನನ್ನು ತಿರಸ್ಕರಿಸುವವನಾಗಿದ್ದಾನೆ; ನನ್ನನ್ನು ತಿರಸ್ಕರಮಾಡುವವನು ನನ್ನನ್ನು ಕಳುಹಿಸಿಕೊಟ್ಟಾತನನ್ನೇ ತಿರಸ್ಕರಮಾಡುವವನಾಗಿದ್ದಾನೆ ಅಂದನು.",
			"17": "<sup>17</sup> ತರುವಾಯ ಆ ಎಪ್ಪತ್ತು ಮಂದಿಯು ಸಂತೋಷವುಳ್ಳವರಾಗಿ ಹಿಂತಿರುಗಿ ಬಂದು - ಕರ್ತನೇ, ದೆವ್ವಗಳು ಕೂಡಾ ನಿನ್ನ ಹೆಸರಿನಲ್ಲಿ ನಮಗೆ ಅಧೀನವಾಗುತ್ತವೆ ಅಂದರು.",
			"18": "<sup>18</sup> ಯೇಸು ಅವರಿಗೆ - ಸೈತಾನನು ಸಿಡಿಲಿನಂತೆ ಆಕಾಶದಿಂದ ಬೀಳುವದನ್ನು ಕಂಡೆನು.",
			"19": "<sup>19</sup> ನೋಡಿರಿ, ಹಾವುಗಳನ್ನೂ ಚೇಳುಗಳನ್ನೂ ವೈರಿಯ ಸಮಸ್ತ ಬಲವನ್ನೂ ತುಳಿಯುವುದಕ್ಕೆ ನಿಮಗೆ ಅಧಿಕಾರ ಕೊಟ್ಟಿದ್ದೇನೆ. ಅವು ಯಾವದೂ ನಿಮಗೆ ಕೇಡು ಮಾಡುವದೇ ಇಲ್ಲ.",
			"20": "<sup>20</sup> ಆದರೂ ದೆವ್ವಗಳು ನಮಗೆ ಅಧೀನವಾಗಿವೆ ಎಂದು ಸಂತೋಷಪಡದೆ ನಿಮ್ಮ ಹೆಸರುಗಳು ಪರಲೋಕದಲ್ಲಿ ಬರೆಯಲ್ಪಟ್ಟಿವೆ ಎಂದು ಸಂತೋಷಪಡಿರಿ ಎಂದು ಹೇಳಿದನು.",
			"21": "<sup>21</sup> ಅದೇ ಗಳಿಗೆಯಲ್ಲಿ ಆತನು ಪವಿತ್ರಾತ್ಮನನಿಂದ ಉಲ್ಲಾಸಗೊಂಡು ಹೇಳಿದ್ದೇನಂದರೆ - ತಂದೆಯೇ, ಪರಲೋಕ ಮತ್ತು ಭೂಲೋಕಗಳ ಒಡೆಯನೇ, ನೀನು ಜ್ಞಾನಿಗಳಿಗೂ ಮೇಧಾವಿಗಳಿಗೂ ಈ ವಿಷಯಗಳನ್ನು ಮರೆಮಾಡಿ, ಮಕ್ಕಳಿಗೆ ಪ್ರಕಟಮಾಡಿರುವುದರಿಂದ ನಿನ್ನನ್ನು ಕೊಂಡಾಡುತ್ತೇನೆ. ಹೌದು, ತಂದೆಯೇ, ಹೀಗೆ ಮಾಡುವದೇ ಒಳ್ಳೇದೆಂದು ನಿನ್ನ ದೃಷ್ಟಿಗೆ ತೋರಿದ್ದರಿಂದ ನಿನ್ನನ್ನು ಕೊಂಡಾಡುತ್ತೇನೆ.",
			"22": "<sup>22</sup> ನನ್ನ ತಂದೆಯು ಎಲ್ಲವನ್ನೂ ನನಗೆ ಒಪ್ಪಿಸಿದ್ದಾನೆ. ಮಗನು ಎಂಥವನೆಂದು ತಂದೆಯ ಹೊರತು ಮತ್ತಾರೂ ತಿಳಿದಿರುವುದಿಲ್ಲ; ತಂದೆ ಎಂಥವನೆಂದು ಮಗನ ಹೊರತು ಇನ್ಯಾರು ತಿಳಿದಿರುವುದಿಲ್ಲ; ಮತ್ತು ಮಗನು ತಂದೆಯನ್ನು ಯಾರಿಗೆ ಪ್ರಕಟಪಡಿಸುವದಕ್ಕೆ ಮನಸ್ಸುಳ್ಳವನಾಗಿದ್ದಾನೋ ಅವನೂ ಆತನನ್ನು ತಿಳಿದವನಾಗಿದ್ದಾನೆ ಅಂದನು.",
			"23": "<sup>23</sup> ಆ ಮೇಲೆ ಆತನು ಶಿಷ್ಯರ ಕಡೆಗೆ ತಿರುಗಿ ಕೊಂಡು ಹೇಳಿದ್ದೇನೆಂದರೆ-ನೀವು ನೋಡುತ್ತಿರುವ ಸಂಗತಿಗಳನ್ನು ನೋಡುವವರು ಧನ್ಯರು.",
			"24": "<sup>24</sup> ಬಹು ಮಂದಿ ಪ್ರವಾದಿಗಳೂ ಅರಸರೂ ನೀವು ನೋಡುತ್ತಿರುವ ಸಂಗತಿಗಳನ್ನು ನೋಡಬೇಕೆಂದು ಅಪೇಕ್ಷಿಸಿದರೂ ಸಾಧ್ಯವಾಗಲಿಲ್ಲ, ನೀವು ಕೇಳುತ್ತಿರುವ ಸಂಗತಿಗಳನ್ನು ಕೇಳಬೇಕೆಂದು ಅಪೇಕ್ಷಿಸಿದರೂ ಕೇಳಲಾಗಲಿಲ್ಲ ಎಂಬದಾಗಿ ನಿಮಗೆ ಹೇಳುತ್ತೇನೆ ಅಂದನು.",
			"25": "<sup>25</sup> ಆಗ ಒಬ್ಬ ಧರ್ಮೋಪದೇಶಕನು ಎದ್ದು ಆತನನ್ನು ಪರೀಕ್ಷಿಸುವುದಕ್ಕಾಗಿ - ಬೋಧಕನೇ, ನಾನು ನಿತ್ಯಜೀವವನ್ನು ಬಧ್ಯಾನಾಗಲು ಏನು ಮಾಡಬೇಕು? ಎಂದು ಕೇಳಲು ಆತನು ಅವನಿಗೆ -",
			"26": "<sup>26</sup> ಶಾಸ್ತ್ರದಲ್ಲಿ ಏನು ಬರೆದದೆ? ಹೇಗೆ ಓದಿದ್ದೀ? ಎಂದು ಹೇಳಿದನು.",
			"27": "<sup>27</sup> ಅದಕ್ಕೆ ಅವನು - ನಿನ್ನ ದೇವರಾಗಿರುವ ಕರ್ತನನ್ನು ನಿನ್ನ ಪೂರ್ಣಹೃದಯದಿಂದಲೂ ನಿನ್ನ ಪೂರ್ಣಪ್ರಾಣದಿಂದಲೂ ನಿನ್ನ ಪೂರ್ಣಶಕ್ತಿಯಿಂದಲೂ ನಿನ್ನ ಪೂರ್ಣ ಬುದ್ದಿಯಿಂದಲೂ ಪ್ರೀತಿಸಬೇಕು. ಮತ್ತು ನಿನ್ನ ನೆರೆಯವನನ್ನು ನಿನ್ನಂತೆಯೇ ಪ್ರೀತಿಸಬೇಕು ಎಂದು ಬರೆದದೆ ಅಂದನು.",
			"28": "<sup>28</sup> ಆತನು ಅವನಿಗೆ - ನೀನು ಸರಿಯಾಗಿ ಉತ್ತರಕೊಟ್ಟಿರುವಿ; ಅದರಂತೆ ಮಾಡು, ಮಾಡಿದರೆ ನಿತ್ಯಜೀವಕ್ಕೆ ಬಾಧ್ಯನಾಗುವಿ, ಎಂದು ಹೇಳಿದನು.",
			"29": "<sup>29</sup> ಆದರೆ ಅವನು ತನ್ನನ್ನು ನೀತಿವಂತನೆಂದು ತೋರಿಸಿಕೊಳ್ಳುವದಕ್ಕೆ ಅಪೇಕ್ಷಿಸಿ - ನನ್ನ ನೆರೆಯವನು ಯಾರು? ಎಂದು ಯೇಸುವನ್ನು ಕೇಳಲು",
			"30": "<sup>30</sup> ಯೇಸು ಅವನಿಗೆ ಪ್ರತ್ಯುತ್ತರವಾಗಿ ಹೇಳಿದ್ದೇನಂದರೆ - ಒಬ್ಬಾನೊಬ್ಬ ಮನುಷ್ಯನು ಯೆರೂಸಲೇಮಿನ ಬೆಟ್ಟದ ಪ್ರದೇಶದಿಂದ ಇಳಿದು ಯೆರಿಕೋ ಎಂಬ ಊರಿಗೆ ಹೋಗುತ್ತಿರುವಾಗ ಕಳ್ಳರ ಕೈಗೆ ಸಿಕ್ಕಿಬಿದ್ದನು. ಅವರು ಅವನನ್ನು ಸುಲಿಗೆ ಮಾಡಿಕೊಂಡು ಗಾಯವಾಗುವಷ್ಟು ಹೊಡೆದು ಅವನನ್ನು ಅರ್ಧಜೀವಮಾಡಿ ಬಿಟ್ಟುಹೋದರು.",
			"31": "<sup>31</sup> ಆಗ ಒಬ್ಬ ಯಾಜಕನು ಆ ದಾರಿಯಲ್ಲಿ ಇಳಿದುಬರುತ್ತಾ ಅವನನ್ನು ಕಂಡು ಓರೆಯಾಗಿ ಹೋದನು.",
			"32": "<sup>32</sup> ಅದೇ ರೀತಿಯಲ್ಲಿ ಒಬ್ಬ ಲೇವಿಯು ಆ ಸ್ಥಳಕ್ಕೆ ಬಂದು ಅವನನ್ನು ಕಂಡು ಓರೆಯಾಗಿ ಹೋದನು.",
			"33": "<sup>33</sup> ಆದರೆ ಒಬ್ಬ ಸಮಾರ್ಯದವನು ಪ್ರಯಾಣಮಾಡುತ್ತಾ ಅವನು ಬಿದಿದ್ದ ಸ್ಥಳಕ್ಕೆ ಬಂದಾಗ ಅವನನ್ನು ಕಂಡು ಕನಿಕರಿಸಿ",
			"34": "<sup>34</sup> ಅವನ ಹತ್ತಿರಕ್ಕೆ ಹೋಗಿ ಅವನ ಗಾಯಗಳಿಗೆ ಎಣ್ಣೆಯನ್ನೂ ದ್ರಾಕ್ಷಾರಸವನ್ನೂ ಹೊಯ್ದು ಗಾಯವನು ಕಟ್ಟಿ ತನ್ನ ಸ್ವಂತ ವಾಹಕಪಶುವಿನ ಮೇಲೆ ಹತ್ತಿಸಿಕೊಂಡು ಛತ್ರಕ್ಕೆ ಕರದುಕೊಂಡು ಹೋಗಿ ಅವನನ್ನು ಆರೈಕೆಮಾಡಿದನು.",
			"35": "<sup>35</sup> ಮರುದಿನ ಅವನು ಎರಡು ದಿನಾರಿಯಗಳನ್ನು ತೆಗೆದು ಛತ್ರದವನಿಗೆ ಕೊಟ್ಟು - ಇವನನ್ನು ಆರೈಕೆಮಾಡು; ಇದಕ್ಕಿಂತ ಹೆಚ್ಚಾಗಿ ಏನಾದರೂ ವೆಚ್ಚಮಾಡಿದರೆ ನಾನು ಹಿಂತಿರುಗಿ ಬಂದಾಗ ನಿನಗೆ ಕೊಡುವೆನು ಅಂದನು.",
			"36": "<sup>36</sup> ಈ ಮೂವರಲ್ಲಿ ಯಾವನು ಕಳ್ಳರ ಕೈಗೆ ಸಿಕ್ಕಿದವನಿಗೆ ನೆರೆಯವನಾದನೆಂದು ನಿನಗೆ ತೋರುತ್ತದೆ ಹೇಳು ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ",
			"37": "<sup>37</sup> ಆ ಧರ್ಮೋಪದೇಶಕನು - ಅವನಿಗೆ ದಯೆ ತೋರಿಸಿದವನೇ ಅಂದನು. ಆಗ ಯೇಸು ಅವನಿಗೆ - ಹೋಗು, ನೀನೂ ಅದರಂತೆ ಮಾಡು ಎಂದು ಹೇಳಿದನು.",
			"38": "<sup>38</sup> ಅವರು ಸಂಚಾರಮಾಡುತ್ತಿರುವಾಗ ಆತನು ಒಂದು ಹಳ್ಳಿಗೆ ಬಂದನು; ಅಲ್ಲಿ ಮಾರ್ಥಳೆಂಬ ಒಬ್ಬ ಸ್ತ್ರೀಯು ಆತನನ್ನು ತನ್ನ ಮನೆಯಲ್ಲಿ ಉಳಿಸಿಕೊಂಡಳು.",
			"39": "<sup>39</sup> ಅಕ್ಕೆಗೆ ಮರಿಯಳೆಂಬ ಒಬ್ಬ ತಂಗಿ ಇದ್ದಳು. ಈಕೆಯು ಯೇಸುವಿನ ಪಾದಗಳ ಬಳಿಯಲ್ಲಿ ಕೂತುಕೊಂಡು ಆತನ ವಾಕ್ಯವನ್ನು ಕೇಳುತ್ತಿದ್ದಳು.",
			"40": "<sup>40</sup> ಅದರೆ ಮಾರ್ಥಳು ಉಪಚಾರ ಸೇವೆಯ ವಿಷಯವಾಗಿ ಬಹಳ ಬೇಸತ್ತು ಯೇಸುವಿನ ಬಳಿಗೆ ಬಂದು - ಕರ್ತನೇ, ನನ್ನ ತಂಗಿಯು ಸೇವೆಗೆ ನನ್ನೊಬ್ಬಳನ್ನೇ ಬಿಟ್ಟಿದ್ದಾಳೆ, ಇದಕ್ಕೆ ನಿನಗೆ ಚಿಂತೆಯಿಲ್ಲವೋ? ನನಗೆ ನೆರವಾಗಬೇಕೆಂದು ಆಕೆಗೆ ಹೇಳು ಅಂದಳು.",
			"41": "<sup>41</sup> ಆದರೆ, ಯೇಸು ಸ್ವಾಮಿಯು ಆಕೆಗೆ - ಮಾರ್ಥಳೇ, ಮಾರ್ಥಳೇ, ನೀನು ಅನೇಕ ವಿಷಯಗಳಿಗಾಗಿ ಚಿಂತೆಯಲ್ಲಿಯೂ ಗಡಿಬಿಡಿಯಲ್ಲಿಯೂ ಸಿಕ್ಕಿಕೊಂಡಿದ್ದೀ.",
			"42": "<sup>42</sup> ಆದರೆ ಕೆಲವು ಮಾತ್ರ ಬೇಕಾದದ್ದು, ಅಥವಾ ಒಂದೇ, ಮರಿಯಳು ಆ ಉತ್ತಮ ಭಾಗವನ್ನು ಆರಿಸಿಕೊಂಡಿದ್ದಾಳೆ; ಅದು ಆಕೆಯಿಂದ ತೆಗೆಯಲ್ಪಡುವದಿಲ್ಲ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು."
		},
		"11": {
			"1": "<sup>1</sup> ಒಮ್ಮೆ ಯೇಸುವು ಒಂದು ಸ್ಥಳದಲ್ಲಿ ಪ್ರಾರ್ಥನೆ ಮಾಡಿ ಮುಗಿಸಿದಾಗ ಆತನ ಶಿಷ್ಯರಲ್ಲಿ ಒಬ್ಬನು ಆತನಿಗೆ - ಕರ್ತನೇ, ಯೋಹಾನನು ತನ್ನ ಶಿಷ್ಯರಿಗೆ ಕಲಿಸಿದ ಹಾಗೆ ನಮಗೂ ಪ್ರಾರ್ಥನೆ ಮಾಡುವುದನ್ನು ಕಲಿಸು ಎಂದು ಕೇಳಿದನು.",
			"2": "<sup>2</sup> ಅದಕ್ಕಾತನು - ನೀವು ಪ್ರಾರ್ಥಿಸುವಾಗ ಈ ರೀತಿಯಾಗಿ ಪ್ರಾರ್ಥಿಸಿರಿ ತಂದೆಯೇ, ನಿನ್ನ ನಾಮವು ಪರಿಶುದ್ಧವೆಂದು ಎಣಿಸಲ್ಪಡಲಿ. ನಿನ್ನ ರಾಜ್ಯವು ಬರಲಿ.",
			"3": "<sup>3</sup> ನಮ್ಮ ಅನುದಿನದ ಆಹಾರವನ್ನು ಪ್ರತಿದಿನವೂ ದಯಪಾಲಿಸು.",
			"4": "<sup>4</sup> ನಮಗೆ ತಪ್ಪುಮಾಡಿರುವ ಪ್ರತಿಯೊಬ್ಬನನ್ನು ನಾವು ಕ್ಷಮಿಸುವುದರಿಂದ ನಮ್ಮ ಪಾಪಗಳನ್ನು ಕ್ಷಮಿಸು. ನಮ್ಮನ್ನು ಶೋಧನೆಯೊಳಗೆ ಸೇರದಂತೆ ಪಾರುಮಾಡು ಎಂದು ಹೇಳಿರಿ ಅಂದನು.",
			"5": "<sup>5</sup> ಪುನಃ ಅವರಿಗೆ ಹೇಳಿದ್ದೇನಂದರೆ - ನಿಮ್ಮಲ್ಲಿ ಒಬ್ಬನು ತನ್ನ ಸ್ನೇಹಿತನ ಮನೆಗೆ ನಡುರಾತ್ರಿಯಲ್ಲಿ ಹೋಗುತ್ತಾನೆಂದು ಭಾವಿಸೋಣ: ಅವನು, ‘ಸ್ನೇಹಿತನೆ, ನನಗೆ ಮೂರು ರೊಟ್ಟಿಗಳನ್ನು ಸಾಲವಾಗಿ ಕೊಡು.",
			"6": "<sup>6</sup> ಪ್ರಯಾಣದಲ್ಲಿದ್ದ ನನ್ನ ಸ್ನೇಹಿತನೊಬ್ಬನು ಅನಿರೀಕ್ಷಿತವಾಗಿ ನನ್ನ ಮನೆಗೆ ಬಂದಿದ್ದಾನೆ. ಅವನಿಗೆ ಊಟಕ್ಕೆ ಕೊಡಲು ನನ್ನಲ್ಲಿ ಏನೂ ಇಲ್ಲ’ ಎಂದು ಕೇಳಲು,",
			"7": "<sup>7</sup> ಆ ಸ್ನೇಹಿತನು - ನನಗೆ ತೊಂದರೆ ಕೊಡಬೇಡ; ಈಗ ಬಾಗಿಲುಹಾಕಿದೆ; ನನ್ನ ಮಕ್ಕಳು ನನ್ನೊಂದಿಗೆ ಮಲಗಿದ್ದಾರೆ; ನಾನು ಎದ್ದು ನಿನಗೆ ಕೊಡುವುದಕ್ಕಾಗುವದಿಲ್ಲ, ಎಂದು ಒಳಗಿನಿಂದ ಉತ್ತರಕೊಟ್ಟರೂ ಕೊಡಬಹುದು, ಅದು ಸಹಜ.",
			"8": "<sup>8</sup> ಆದರೂ, ಸ್ನೇಹದ ನಿಮಿತ್ತವಾಗಿ ಎದ್ದು ಬಂದು ಕೊಡದೆ ಇದ್ದರೂ, ನಾಚಿಕೆಪಡದೆ ಕೇಳುತ್ತಲೇ ಇದ್ದಾನಲ್ಲಾ ಎಂಬ ಕಾರಣದಿಂದಾದರೂ ಅವನು ಎದ್ದು ಬಂದು ಕೇಳಿದಷ್ಟು ರೊಟ್ಟಿಗಳನ್ನು ಕೊಡುತಾನೆಂಬುದು ನಿಜ.",
			"9": "<sup>9</sup> ಹಾಗೆಯೇ ನಾನು ನಿಮಗೆ ಹೇಳುವದೇನಂದರೆ - ಬೇಡಿಕೊಳ್ಳಿರಿ, ನಿಮಗೆ ದೊರೆಯುವುದು; ಹುಡುಕಿರಿ, ನಿಮಗೆ ಸಿಕ್ಕುವದು;",
			"10": "<sup>10</sup> ತಟ್ಟಿರಿ, ನಿಮಗೆ ತೆರೆಯುವುದು. ಯಾಕೆಂದರೆ ಬೇಡಿಕೊಳ್ಳುವ ಪ್ರತಿಯೊಬ್ಬನು ಹೊಂದುವನು, ಹುಡುಕುವವನಿಗೆ ಸಿಕ್ಕುವದು, ತಟ್ಟುವವನಿಗೆ ತೆರೆಯುವುದು.",
			"11": "<sup>11</sup> ನಿಮ್ಮಲ್ಲಿ ತಂದೆಯಾದವನು ಮೀನನ್ನು ಕೇಳುವ ಮಗನಿಗೆ ಮೀನು ಕೊಡದೆ ಹಾವನ್ನು ಕೊಡುವನೇ?",
			"12": "<sup>12</sup> ಅಥವಾ ಮೊಟ್ಟೆಯನ್ನು ಕೇಳಿದರೆ ಚೇಳನ್ನು ಕೊಡುವನೇ?",
			"13": "<sup>13</sup> ಕೆಟ್ಟವರಾದ ನೀವು ನಿಮ್ಮ ಮಕ್ಕಳಿಗೆ ಒಳ್ಳೇ ಪದಾರ್ಥಗಳನ್ನು ಕೊಡಬಲ್ಲವರಾದರೆ ಪರಲೋಕದಲ್ಲಿರುವ ನಿಮ್ಮ ತಂದೆಯು ತನ್ನನ್ನು ಬೇಡಿಕೊಳ್ಳುವವರಿಗೆ ಎಷ್ಟೋ ಹೆಚ್ಚಾಗಿ ಪವಿತ್ರಾತ್ಮವರವನ್ನು ಕೊಡುವನಲ್ಲವೇ ಅಂದನು.",
			"14": "<sup>14</sup> ಒಮ್ಮೆ ಯೇಸುವು ಮೂಕದೆವ್ವವನ್ನು ಬಿಡಿಸುತ್ತಿದ್ದನು. ಆ ದೆವ್ವವು ಬಿಟ್ಟು ಹೋದ ಮೇಲೆ ಮೂಕನು ಮಾತಾಡಿದನು; ಅದನ್ನು ನೋಡಿದ ಜನರು ಆಶ್ಚರ್ಯಪಟ್ಟರು.",
			"15": "<sup>15</sup> ಆದರೆ ಅವರಲ್ಲಿ ಕೆಲವರು - ಇವನು ದೆವ್ವಗಳ ಒಡೆಯನಾದ ಬೆಲ್ಜಬೂಲನ ಶಕ್ತಿಯಿಂದ ದೆವ್ವಗಳನ್ನು ಬಿಡಿಸುತ್ತಾನೆ ಅಂದರು.",
			"16": "<sup>16</sup> ಬೇರೆ ಕೆಲವರು ಆತನನ್ನು ಪರೀಕ್ಷಿಸುವದಕ್ಕಾಗಿ - ನೀನು ಪರಲೋಕದಿಂದ ಒಂದು ಸೂಚಕಕಾರ್ಯವನ್ನು ಮಾಡಿತೋರಿಸುವಂತೆ ಕೇಳಿದರು.",
			"17": "<sup>17</sup> ಯೇಸು ಅವರ ಆಲೋಚನೆಗಳನ್ನು ಅರಿತುಕೊಂಡು ಅವರಿಗೆ ಹೇಳಿದ್ದೇನಂದರೆ - ತಮ್ಮತಮ್ಮೊಳಗೆ ಭೇದ ಹುಟ್ಟಿದ ರಾಜ್ಯವು ನಾಶವಾಗುವುದು; ಮತ್ತು ತನ್ನಲ್ಲಿ ಕಚ್ಚಾಡಿ ಭೇದಹುಟ್ಟಿಸುವ ಕುಟುಂಬ ಬಿದ್ದುಹೋಗುವುದು”",
			"18": "<sup>18</sup> ಅದರಂತೆ ಸೈತಾನ ಪಕ್ಷದವರು ಒಬ್ಬರ ವಿರುದ್ಧ ಒಬ್ಬರು ಜಗಳ ಆಡಿದರೆ ಅವನ ರಾಜ್ಯವು ಹೇಗೆ ಉಳಿದೀತು? ಬೆಲ್ಜೆಬೂಲನ ಬಲದಿಂದ ದೆವ್ವಗಳನ್ನು ಬಿಡಿಸುತ್ತೀ ಎಂದು ನನಗೆ ಹೇಳುತ್ತೀರಲ್ಲಾ,",
			"19": "<sup>19</sup> ನಾನು ಬೆಲ್ಜೆಬೂಲನ ಬಲದಿಂದ ದೆವ್ವಗಳನ್ನು ಬಿಡಿಸುವದಾದರೆ ನಿಮ್ಮವರು ಯಾರ ಬಲದಿಂದ ಬಿಡಿಸುತ್ತಾರೆ? ಆದುದರಿಂದ ನೀವು ಹೇಳುವುದು ತಪ್ಪೆಂದು ನಿಮ್ಮವರೇ ತೀರ್ಪುಕೊಡುವರು.",
			"20": "<sup>20</sup> ನಾನು ದೇವರ ಬಲದಿಂದಲೇ ದೆವ್ವಗಳನ್ನು ಬಿಡಿಸುವದಾದರೆ ದೇವರ ರಾಜ್ಯವು ನಿಮ್ಮಲ್ಲಿಗೆ ಈಗಾಗಲೇ ಬಂದಿದೆ, ಎಂಬುದು ಸ್ಪಷ್ಟ.",
			"21": "<sup>21</sup> ಒಬ್ಬ ಬಲಿಷ್ಠನು ಸರ್ವಾಯುಧಗಳನ್ನು ಧರಿಸಿಕೊಂಡು ತನ್ನ ಮನೆಯನ್ನು ಕಾಪಾಡಿಕೊಂಡಿರುವಾಗ ಅವನ ವಸ್ತುಗಳು ಭದ್ರವಾಗಿಯೇ ಇರುವವು;",
			"22": "<sup>22</sup> ಆದರೆ ಅವನಿಗಿಂತ ಬಲಿಷ್ಠನು ಬಂದು ಅವನನ್ನು ಗೆದ್ದು ಅವನು ನೆಚ್ಚಿಕೊಂಡಿದ್ದ ಆಯುಧಗಳನ್ನೆಲ್ಲಾ ಅವನು ಕಿತ್ತುಕೊಂಡು, ಸುಲಿಗೆಮಾಡಿದ್ದನ್ನು ಹಂಚಿಕೊಡುತ್ತಾನೆ.",
			"23": "<sup>23</sup> “ನನ್ನ ಪರವಾಗಿ ಇರದವನು ನನಗೆ ವಿರೋಧಿಯೇ ಸರಿ; ನನ್ನ ಜೊತೆಯಲ್ಲಿ ಸೇರಿಸ ಪಡದವನು ಚದರಿಸುವವನಾಗುತ್ತಾನೆ”.",
			"24": "<sup>24</sup> ಬೋದನೆಯನ್ನು ಮುಂದುವರಿಸುತ್ತಾ ಯೇಸುವು - ದೆವ್ವವು ಮನುಷ್ಯನನ್ನು ಬಿಟ್ಟುಹೋದ ಮೇಲೆ ವಿಶ್ರಾಂತಿಯನ್ನು ಹುಡುಕುತ್ತಾ ನೀರಿಲ್ಲದ ಸ್ಥಳಗಳಲ್ಲಿ ತಿರುಗಾಡುತ್ತದೆ; ವಿಶ್ರಾಂತಿ ಸಿಕ್ಕದ ಕಾರಣ ಅದು - ನಾನು ಬಿಟ್ಟು ಬಂದ ನನ್ನ ಮನೆಗೆ ಹಿಂತಿರುಗಿ ಹೋಗುತ್ತೇನೆ ಅಂದುಕೊಂಡು ಬಂದು",
			"25": "<sup>25</sup> ಆ ಮನೆ ಗುಡಿಸಿ ಅಲಂಕರಿಸಿದ್ದಾಗಿರುವದನ್ನು ಕಂಡು ಹೊರಟುಹೋಗಿ",
			"26": "<sup>26</sup> ತನಗಿಂತ ಕೆಟ್ಟವುಗಳಾದ ಬೇರೆ ಏಳು ದೆವ್ವಗಳನ್ನು ಕರಕೊಂಡು ಬರುವದು; ಅವು ಒಳಹೊಕ್ಕು ಅಲ್ಲಿ ವಾಸಮಾಡುವವು; ಆಗ ಆ ಮನುಷ್ಯನ ಅಂತ್ಯಸ್ಥಿತಿಯು ಮೊದಲಿಗಿಂತ ಕೆಟ್ಟದ್ದಾಗುವದು ಅಂದನು.",
			"27": "<sup>27</sup> ಯೇಸುವು ಈ ಮಾತುಗಳನ್ನು ಹೇಳುತ್ತಿರುವಾಗ ಜನರಲ್ಲಿ ಒಬ್ಬ ಸ್ತ್ರೀಯು - ನಿನ್ನನ್ನು ಹೊತ್ತ ಗರ್ಭವೂ, ನೀನು ಕುಡಿದ ಮೊಲೆಗಳೂ ಧನ್ಯವಾದವುಗಳು ಎಂದು ಕೂಗಿದಳು.",
			"28": "<sup>28</sup> ಅದಕ್ಕೆ ಯೇಸು, ಹಾಗನ್ನ ಬೇಡ, “ದೇವರ ವಾಕ್ಯವನ್ನು ಕೇಳಿ ಅದನ್ನು ಅನುಸರಿಸುವವನು ಹೆಚ್ಚು ಭಾಗ್ಯವಂತನು,” ಅಂದನು.",
			"29": "<sup>29</sup> ಜನರು ಗುಂಪುಗುಂಪಾಗಿ ಸೇರಿಬರುತ್ತಿರುವಾಗ ಯೇಸುವು ಹೇಳುವದಕ್ಕೆ ತೊಡಗಿದ್ದೇನಂದರೆ - ಈ ಸಂತತಿ ಕೆಟ್ಟ ಸಂತತಿಯೇ; ಇದು ಸೂಚಕಕಾರ್ಯವನ್ನು ಸಂಕೇತವಾಗಿ ನೋಡಬೇಕೆಂದು ಅಪೇಕ್ಷಿಸುತ್ತದೆ. ಆದರೆ ಪ್ರವಾದಿ ಯೋನನಲ್ಲಿ ಆದ ಸೂಚಕಕಾರ್ಯವೇ ಹೊರತು ಬೇರೆ ಯಾವದೂ ಈ ಸಂತತಿ ಸಿಕ್ಕುವುದಿಲ್ಲ.",
			"30": "<sup>30</sup> ಹೇಗೆಂದರೆ ಯೋನನು ನಿನೆವೆ ಪಟ್ಟಣದವರಿಗೆ ಗುರುತಾದ ಹಾಗೆಯೇ ಮನುಷ್ಯಕುಮಾರನು ಈ ಸಂತತಿಗೆ ಗುರುತಾಗಿರುವನು.",
			"31": "<sup>31</sup> ದಕ್ಷಿಣ ದೇಶದ ರಾಣಿಯು ನ್ಯಾಯವಿಚಾರಣೆ ದಿನದಲ್ಲಿ ಈ ಸಂತತಿಯವರೊಂದಿಗೆ ಎದ್ದುನಿಂತು ಇವರನ್ನು ಅಪರಾಧಿಗಳೆಂದು ಹೇಳುವಳು; ಆಕೆಯು ಸೊಲೊಮೋನನ ಜ್ಞಾನವನ್ನು ತಿಳುಕೊಳ್ಳುವದಕ್ಕೆ ಭೂಮಿಯ ಕಟ್ಟಕಡೆಯಿಂದ ಬಂದಳು; ಆದರೆ ಇಲ್ಲಿ ಸೊಲೊಮೋನನಿಗಿಂತಲೂ ಮೆಲಾದವನಿದ್ದಾನೆ.",
			"32": "<sup>32</sup> ನ್ಯಾಯ ವಿಚಾರಣೆಯಲ್ಲಿ ನಿನೆವೆ ಪಟ್ಟಣದವರು ಈ ಸಂತತಿಯವರೊಂದಿಗೆ ನಿಂತು ಇವರನ್ನು ಅಪರಾಧಿಗಳೆಂದು ಹೇಳುವರು. ಅವರು ಯೋನನು ಸಾರಿದ ವಾಕ್ಯವನ್ನು ಕೇಳಿ ದೇವರ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡರು; ಆದರೆ ಇಲ್ಲಿ ಯೋನನಿಗಿಂತಲೂ ಮೆಲಾದವನು ಇಲ್ಲಿದ್ದಾನೆ” ಎಂದು ಹೇಳಿದನು.",
			"33": "<sup>33</sup> ಯಾರೂ ದೀಪವನ್ನು ಹಚ್ಚಿ ಮರೆಯಲ್ಲಾಗಲಿ ಕೊಳಗದೊಳಗಾಗಲಿ ಇಡುವದಿಲ್ಲ. ಮನೆಯೊಳಗೆ ಬರುವವರಿಗೆ ಬೆಳಕು ಕಾಣಿಸುವಂತೆ ಆ ದೀಪವನ್ನು ದೀಪಸ್ತಂಭದ ಮೇಲೆ ಇಡುತ್ತಾರಷ್ಟೆ.",
			"34": "<sup>34</sup> ನಿನ್ನ ಕಣ್ಣು ದೇಹಕ್ಕೆ ದೀಪವಾಗಿದೆ; ನಿನ್ನ ಕಣ್ಣು ನೆಟ್ಟಗಿರುವಾಗ ಅದರಂತೆ ನಿನ್ನ ದೇಹವೆಲ್ಲಾ ಬೆಳಕಾಗಿರುವದು; ಅದು ಕೆಟ್ಟದ್ದಾಗಿರುವಾಗ ಅದರಂತೆ ನಿನ್ನ ದೇಹವು ಕತ್ತಲಾಗಿರುವದು.",
			"35": "<sup>35</sup> ಆದದರಿಂದ ನಿನ್ನೊಳಗಿರುವ ಬೆಳಕೇ ಕತ್ತಲಾಗಿದೆಯೋ ಏನೋ ನೋಡು.",
			"36": "<sup>36</sup> ನಿನ್ನ ದೇಹವೆಲ್ಲಾ ಬೆಳಕಾಗಿದ್ದು ಒಂದು ಭಾಗದಲ್ಲಿಯೂ ಕತ್ತಲಿಲ್ಲದ್ದಾಗಿದ್ದರೆ ದೀಪವು ಹೊಳೆದು ನಿನಗೆ ಬೆಳಕನ್ನು ಕೊಡುವ ಕಾಲದಲ್ಲಿ ಹೇಗೋ ಹಾಗೆಯೇ ಅದು ಪೂರ್ಣವಾಗಿ ಬೆಳಕಾಗಿರುವದು ಅಂದನು.",
			"37": "<sup>37</sup> ಯೇಸು ಮಾತಾಡುತ್ತಿರುವಾಗ ಒಬ್ಬ ಫರಿಸಾಯನು ಆತನನ್ನು ಊಟಕ್ಕೆ ಬರಬೇಕೆಂದು ಕೇಳಿಕೊಳ್ಳಲು ಆತನು ಒಳಕ್ಕೆ ಹೋಗಿ ಊಟಕ್ಕೆ ಕೂಳಿತುಕೊಂಡನು.",
			"38": "<sup>38</sup> ಆತನು ಕೈತೊಳೆಯದೆ ಊಟಕ್ಕೆ ಕೂಳಿತದ್ದನ್ನು ಆ ಫರಿಸಾಯನು ಕಂಡು ಆಶ್ಚರ್ಯಪಟ್ಟನು.",
			"39": "<sup>39</sup> ಆದರೆ ಸ್ವಾಮಿಯು ಅವನಿಗೆ - ಫರಿಸಾಯರಾದ ನೀವು ಪಂಚಪಾತ್ರೆ ತಟ್ಟೆ ಇವುಗಳ ಹೊರಭಾಗವನ್ನು ಶುಚಿಮಾಡುತ್ತೀರಿ ಸರಿ; ಆದರೆ ನಿಮ್ಮ ಒಳಭಾಗವು ಸುಲಿಗೆಯಿಂದಲೂ ಕೆಟ್ಟತನದಿಂದಲೂ ತುಂಬಿರುತ್ತದೆ.",
			"40": "<sup>40</sup> ಬುದ್ಧಿಯಿಲ್ಲದವರೇ, ಹೊರಭಾಗವನ್ನು ಮಾಡಿದಾತನು ಒಳಭಾಗವನ್ನೂ ಮಾಡಿದನಲ್ಲವೇ.",
			"41": "<sup>41</sup> ಹೇಗೂ ಒಳಗಿರುವಂಥದನ್ನು ದಾನಕೊಡಿರಿ; ಆಗ ಸಕಲವೂ ನಿಮಗೆ ಶುದ್ಧವಾಗಿರುವದು.",
			"42": "<sup>42</sup> ಅಯ್ಯೋ ಫರಿಸಾಯರೇ, ನೀವು ಮರುಗಪತ್ರೆ ಸದಾಪು ಮುಂತಾದ ಸಕಲ ವಿಧವಾದ ಸೋಪ್ಪುಗಳಲ್ಲಿ ಹತ್ತರಲ್ಲೊಂದು ಪಾಲು ಕೊಡುತ್ತೀರಿ ಸರಿ; ಆದರೆ ನ್ಯಾಯವನ್ನೂ ದೇವರ ಪ್ರೀತಿಯನ್ನೂ ಲಕ್ಷಿಸದೆ ಬಿಟ್ಟಿದ್ದೀರಿ; ನೀವು ಅವುಗಳನ್ನು ಅಲಕ್ಷ್ಯ ಮಾಡದೆ, ಇವುಗಳನ್ನು ಅನುಷ್ಠಾನಕ್ಕೆ ತರಬೇಕಾಗಿತ್ತು.",
			"43": "<sup>43</sup> ಅಯ್ಯೋ ಫರಿಸಾಯರೇ, ಸಭಾಮಂದಿರಗಳಲ್ಲಿ ಉನ್ನತ ಸ್ಥಾನಗಳನ್ನೂ ಅಂಗಡಿ ಬೀದಿಗಳಲ್ಲಿ ವಂದನೆಗಳನ್ನೂ ನೀವು ಬಯಸುತ್ತೀರಿ.",
			"44": "<sup>44</sup> ನಿಮ್ಮ ಗತಿಯನ್ನು ಏನು ಹೇಳಲಿ; ನೆಲಸಮವಾದ ಸಮಾಧಿಗಳ ಹಾಗಿದ್ದೀರಿ; ಅವುಗಳ ಮೇಲೆ ತಿರುಗಾಡುವ ಜನರಿಗೆ ಇವು ಸಮಾಧಿಗಳೆಂದು ತಿಳಿಯದು ಎಂದು ಹೇಳಿದನು.",
			"45": "<sup>45</sup> ಇದನ್ನು ಕೇಳಿ ಒಬ್ಬ ಧರ್ಮೋಪದೇಶಕನು ಯೇಸುವಿಗೆ - ಬೋಧಕನೇ, ಈ ಮಾತುಗಳನ್ನು ಹೇಳುವದರಿಂದ ನಮ್ಮನ್ನೂ ನಿಂದಿಸಿದ ಹಾಗಾಯಿತು ಎಂದು ಹೇಳಲು",
			"46": "<sup>46</sup> ಆತನು - ಧರ್ಮೋಪದೇಶಕರೇ, ನಿಮ್ಮ ಗತಿಯನ್ನೂ ಏನು ಹೇಳಲಿ? ನೀವು ಜನರ ಮೇಲೆ ಹೊರಲಾರದ ಹೊರೆಗಳನ್ನು ಹೊರಿಸುತ್ತೀರಿ; ನೀವಾದರೋ ಆ ಹೊರೆಗಳನ್ನು ಒಂದು ಬೆರಳಿನಿಂದಲೂ ಮುಟ್ಟುವುದಿಲ್ಲ.",
			"47": "<sup>47</sup> ನಿಮ್ಮ ಗತಿಯನ್ನು ಏನು ಹೇಳಲಿ? ನೀವು ಪ್ರವಾಧಿಗಳ ಗೋರಿಗಳನ್ನು ಕಟ್ಟುತ್ತೀರಿ; ಅವರನ್ನು ಕೊಂದವರು ನಿಮ್ಮ ಪಿತೃಗಳೇ.",
			"48": "<sup>48</sup> ಹೀಗಿರುವದರಿಂದ ನಿಮ್ಮ ಪಿತೃಗಳು ಮಾಡಿದ್ದಕ್ಕೆ ನೀವೇ ಸಾಕ್ಷಿಗಳಾಗಿರುವರಲ್ಲದೆ ಅವರ ಕೃತ್ಯಗಳಿಗೆ ಒಪ್ಪಿಕೊಂಡ ಹಾಗಾಯಿತು. ಹೇಗಂದರೆ ಅವರು ಪ್ರವಾದಿಗಳನ್ನು ಕೊಂದರು, ನೀವು ಗೋರಿಗಳನ್ನು ಕಟ್ಟುತ್ತೀರಿ.",
			"49": "<sup>49</sup> ಆದ್ದರಿಂದ ದೇವರ ಜ್ಞಾನವು ಹೇಳಿದ್ದೇನಂದರೆ - ನಾನು ಅವರ ಬಳಿಗೆ ಪ್ರವಾದಿಗಳನ್ನೂ ಅಪೊಸ್ತಲರನ್ನೂ ಕಳುಹಿಸುವೆನು; ಅವರಲ್ಲಿ ಕೆಲವರನ್ನು ಕೊಲ್ಲುವರು; ಕೆಲವರನ್ನು ಹಿಂಸೆಪಡಿಸುವರು.",
			"50": "<sup>50</sup> ಹೀಗೆ ಲೋಕದ ಆದಿಯಿಂದ ಸುರಿಸಲಾದ ಎಲ್ಲಾ ಪ್ರವಾದಿಗಳ ರಕ್ತಕ್ಕೂ ಅಂದರೆ, ಹೇಬೆಲನ ರಕ್ತ ಮೊದಲುಗೊಂಡು, ಯಜ್ಞವೇದಿಗೂ ದೇವಾಲಯಕ್ಕೂ ನಡುವೆ ಕೊಲ್ಲಲ್ಪಟ್ಟ ಜಕರೀಯನ ರಕ್ತದವರೆವಿಗೂ ಈ ಸಂತತಿಯವರು ಲೆಕ್ಕಕೊಡಬೇಕಾಗುವುದು ಎಂಬುದೇ; ಹೌದು, ಈ ಸಂತತಿಯವರೇ ಉತ್ತರಕೊಡಬೇಕೆಂದು ನಿಮಗೆ ಸ್ಪಷ್ಟವಾಗಿ ಹೇಳುತ್ತೇನೆ.",
			"52": "<sup>52</sup> ಅಯ್ಯೋ ಧರ್ಮೋಪದೇಶಕರೇ, ದೈವಿಕ ಜ್ಞಾನ ಮಂದಿರದ ಬೀಗದ ಕೈಯನ್ನು ನಿಮ್ಮಲೇ ಇಟ್ಟುಕೊಂಡಿದ್ದಿರಿ. ನೀವೂ ಒಳಕ್ಕೆ ಪ್ರವೇಶಿಸಲಿಲ್ಲ; ಒಳಕ್ಕೆ ಹೋಗ ಬಯಸುವವರಿಗೂ ಅಡ್ಡಿಮಾಡಿದೀರಿ ಅಂದನು.",
			"53": "<sup>53</sup> ಬಳಿಕ ಯೇಸು ಅಲ್ಲಿಂದ ಹೊರಗೆ ಬಂದಾಗ ಶಾಸ್ತ್ರಿಗಳೂ ಫರಿಸಾಯರೂ ಆತನನ್ನು ಕಠಿಣವಾಗಿ ಕೆಣಕಿ, ವಿರೋಧಿಸಿ ಅನೇಕ ವಿಷಯಗಳನ್ನು ಕುರಿತು ಆತನನ್ನು ಅಡ್ಡಾದಿಡ್ಡೀ ಪ್ರಶ್ನೆಮಾಡುತ್ತಾ",
			"54": "<sup>54</sup> ಆತನ ಬಾಯಿಮಾತಿನಲ್ಲಿ ಏನಾದರೂ ತಪ್ಪು ಹಿಡಿಯಲೇಬೇಕೆಂದು ಹೊಂಚುಹಾಕುತ್ತಿದ್ದರು."
		},
		"12": {
			"1": "<sup>1</sup> ಇಷ್ಟರಲ್ಲಿ ಸಾವಿರಾರು ಜನರು ಒಬ್ಬರನ್ನೊಬ್ಬರು ತುಳಿಯುವಷ್ಟು ಕೂಡಿಬಂದಿರಲಾಗಿ ಯೇಸುವು ಮೊದಲು ತನ್ನ ಶಿಷ್ಯರಿಗೆ ಉಪದೇಶಿಸಿ ಹೇಳಿದ್ದೇನೆಂದರೆ, “ಫರಿಸಾಯರ ‘ಹುಳಿಹಿಟ್ಟಿನ’ ಬಗ್ಗೆ, ಅಂದರೆ ಅವರ ಕಪಟತನದ ವಿಷಯದಲ್ಲಿ ಜಾಗರೂಕರಾಗಿರಿ.”",
			"2": "<sup>2</sup> ಮರೆಯಾಗಿರುವ ಯಾವದೂ ವ್ಯಕ್ತವಾಗದೆ ಇರುವುದಿಲ್ಲ. ಪ್ರಕಟಗೊಳ್ಳದ ರಹಸ್ಯವೂ ಇಲ್ಲ.",
			"3": "<sup>3</sup> ಹೀಗಿರುವದರಿಂದ ನೀವು ಕತ್ತಲಲ್ಲಿ ಹೇಳಿದಂಥದು ಬೆಳಕಿನಲ್ಲಿ ಕೇಳಲಾಗುವದು. ಮತ್ತು ನೀವು ಕೊಣೆಗಳೊಳಗೆ ಪಿಸುಗುಟ್ಟಿದ್ದು ಮಾಳಿಗೆಗಳ ಮೇಲೆ ಸಾರಲಾಗುವದು.",
			"4": "<sup>4</sup> ನನ್ನ ಸ್ನೇಹಿತರಾದ ನಿಮಗೆ ಹೇಳುತ್ತೇನೆ - ದೇಹವನ್ನು ಕೊಂದುಹಾಕುವವರಿಗೆ ಭಯಪಡಬೇಡಿ. ಕೊಂದುಹಾಕಿದ ಮೇಲೆ ಹೆಚ್ಚೇನು ಮಾಡಲು ಅವರಿಂದಾಗದು.",
			"5": "<sup>5</sup> ಆದರೆ ನೀವು ಯಾರಿಗೆ ಭಯಪಡಬೇಕೆಂದು ಹೇಳುತ್ತೇನೆ, ಕೇಳಿ. ಸತ್ತ ಮೇಲೆ ನರಕದೊಳಗೆ ದಬ್ಬಲು ಅಧಿಕಾರವುಳ್ಳಾತನಿಗೆ ಹೆದರಬೇಕು. ಹೌದು ಆತನಿಗೇ ಭಯಪಡಬೇಕೆಂದು ನಿಮಗೆ ಒತ್ತಿಹೇಳುತ್ತೇನೆ.",
			"6": "<sup>6</sup> ಐದು ಗುಬ್ಬಿಗಳನ್ನು ಎರಡು ದುಡ್ಡಿಗೆ ಮಾರುತ್ತಾರಲ್ಲ? ಆದಾಗ್ಯೂ ಅವುಗಳಲ್ಲಿ ಒಂದನ್ನೂ ದೇವರು ಮರೆತುಹೋಗುವುದಿಲ್ಲ.",
			"7": "<sup>7</sup> ಅಷ್ಟುಮಾತ್ರವಲ್ಲ, ನಿಮ್ಮ ತಲೆ ಕೂದಲುಗಳು ಸಹ ಎಲ್ಲವೂ ಎಣಿಕೆಯಾಗಿವೆ. ಹೆದರಬೇಡಿರಿ; ಬಹಳ ಗುಬ್ಬಿಗಳಿಗಿಂತ ನೀವು ಹೆಚ್ಚಿನವರು.",
			"8": "<sup>8</sup> ನಾನು ನಿಮಗೆ ಹೇಳುತ್ತೇನೆ, ಯಾವನು ಮನುಷ್ಯರ ಮುಂದೆ ತಾನು ನನ್ನವನೆಂದು ಒಪ್ಪಿಕೊಳ್ಳುತ್ತಾನೋ, ಮನುಷ್ಯಕುಮಾರನು ಸಹ ಆ ವ್ಯಕ್ತಿಯನ್ನು ದೇವದೂತರ ಮುಂದೆ ತನ್ನವನೆಂದು ಒಪ್ಪಿಕೊಳ್ಳುವನು,",
			"9": "<sup>9</sup> ಆದರೆ ಯಾವನು ಮನುಷ್ಯರ ಮುಂದೆ ತಾನು ಯೇಸುವಿನವನಲ್ಲವೆಂದು ಹೇಳುವನೋ ಅವನನ್ನು ನಾನು ದೇವದೂತರ ಮುಂದೆ ನನ್ನವನಲ್ಲವೆಂದು ಹೇಳುವೆನು.",
			"10": "<sup>10</sup> ಮನುಷ್ಯಕುಮಾರನ ವಿರುದ್ಧ ಮಾತಾಡುವ ಪ್ರತಿಯೊಬ್ಬನಿಗೆ ಕ್ಷಮಾಪಣೆಯಾಗುವದು. ಆದರೆ ಪವಿತ್ರಾತ್ಮನನ್ನು ದೂಷಿಸಿದವನಿಗೆ ಕ್ಷಮೆಯೇ ದೊರಕದು.",
			"11": "<sup>11</sup> ಅವರು ನಿಮ್ಮನ್ನು ಪ್ರಾರ್ಥನ ಮಂದಿರಗಳಿಗೂ ಸಭಾಮಂದಿರಗಳ ಅಧಿಕಾರಿಗಳ ಎದುರಿಗೂ ಅಧಿಪತಿಗಳ ಎದುರಿಗೂ ಹಿಡುಕೊಂಡು ಹೋಗುವಾಗ ಹೇಗೆ ಉತ್ತರ ಕೊಡಬೇಕು? ಏನು ಉತ್ತರ ಕೊಡಬೇಕು? ಏನು ಹೇಳಬೇಕು ಎಂದು ಚಿಂತೆಮಾಡಬೇಡಿರಿ.",
			"12": "<sup>12</sup> ಯಾಕೆಂದರೆ ನೀವು ಹೇಳತಕ್ಕದ್ದನ್ನು ಪವಿತ್ರಾತ್ಮನು ಆ ಗಳಿಗೆಯಲ್ಲಿಯೇ ನಿಮಗೆ ಕಲಿಸಿಕೊಡುವನು ಅಂದನು.",
			"13": "<sup>13</sup> ಆಗ ಜನರಲ್ಲಿ ಒಬ್ಬನು - ಭೋಧಕನೇ, ತಂದೆಯ ಆಸ್ತಿಯನ್ನು ನನಗೆ ಪಾಲುಮಾಡುವಂತೆ ನನ್ನ ಅಣ್ಣನಿಗೆ ಹೇಳು ಎಂದು ಯೇಸುವನ್ನು ಕೇಳಿಕೊಳ್ಳಲು",
			"14": "<sup>14</sup> ಆತನು - ಮನುಷ್ಯನೇ, ನನ್ನನ್ನು ನಿಮಗೆ ನ್ಯಾಯಾಧಿಪತಿಯನ್ನಾಗಿ ಅಥವಾ ಪಾಲುಮಾಡುವವನನ್ನಾಗಿ ನೇಮಿಸಿದವರಾರು ಎಂದು ಅವನನ್ನು ಕೇಳಿ,",
			"15": "<sup>15</sup> ಜನರಿಗೆ – ಎಲ್ಲಾ ದುರಾಸೆಗಳಿಂದಲು ಎಚ್ಚರಿಕೆಯಾಗಿದ್ದು ನಿಮ್ಮನ್ನು ಕಾಪಾಡಿಕೊಳ್ಳಿರಿ. ಒಬ್ಬನಿಗೆ ಎಷ್ಟು ಆಸ್ತಿಯಿದ್ದರೂ ಅದು ಅವನಿಗೆ ಜೀವಾಧಾರವಾಗುವದಿಲ್ಲ ಎಂದು ಹೇಳಿ ಒಂದು ಸಾಮ್ಯವನ್ನು ಹೇಳಿದನು.",
			"16": "<sup>16</sup> ಅದೇನಂದರೆ ಒಬ್ಬಾನೊಬ್ಬ ಐಶ್ವರ್ಯವಂತನ ಭೂಮಿಯಲ್ಲಿ ಸಮೃದ್ಧಿಯಾಗಿ ಬೆಳೆ ಬೆಳೆಯಿತು.",
			"17": "<sup>17</sup> ಆಗ ಅವನು ತನ್ನೊಳಗೆ - ನಾನೇನು ಮಾಡಲಿ? ನನ್ನ ಬೆಳೆಯನ್ನು ತುಂಬಿಡುವದಕ್ಕೆ ನನಗೆ ಸ್ಥಳವಿಲ್ಲವಲ್ಲಾ ಎಂದು ಆಲೋಚಿಸಿ - ಒಂದು ಕೆಲಸ ಮಾಡುತ್ತೇನೆ.",
			"18": "<sup>18</sup> ನನ್ನ ಕಣಜಗಳನ್ನು ಕೆಡವಿ ಅವುಗಳಿಗಿಂತ ದೊಡ್ಡ ಕಣಜಗಳನ್ನುಕಟ್ಟಿಸುವೆನು. ಅಲ್ಲಿ ನನ್ನ ಎಲ್ಲಾ ಧವಸಧಾನ್ಯಗಳನ್ನೂ ಸರಕುಗಳನ್ನು ತುಂಬಿಟ್ಟು.",
			"19": "<sup>19</sup> ನನ್ನ ಜೀವಾತ್ಮಕ್ಕೆ - ಜೀವವೇ, ಅನೇಕ ವರುಷಗಳಿಗೆ ಬೇಕಾದಷ್ಟು ಸರಕು ನಿನಗೆ ಬಂದಿದೆ; ವಿಶ್ರಮಿಸಿಕೋ, ಊಟಮಾಡು, ಕುಡಿ, ಸುಖಪಡು ಎಂದು ಹೇಳುವೆನು ಅಂದುಕೊಂಡನು.",
			"20": "<sup>20</sup> ಆದರೆ ದೇವರು ಅವನಿಗೆ - ಬುದ್ಧಿಹೀನನೇ, ಈ ರಾತ್ರಿಯೇ ನಿನ್ನ ಪ್ರಾಣವನ್ನು ನಿನ್ನಿಂದ ಕೇಳಲ್ಪಡುವುದು. ಆಗ ನೀನು ಕೂಡಿಸಿಟ್ಟಿರುವದು ಯಾರಿಗಾಗುವದು ಎಂದು ಹೇಳಿದನು.",
			"21": "<sup>21</sup> ತನಗೋಸ್ಕರ ಹಣವನ್ನು ಸಂಗ್ರಹಿಸಿಟ್ಟುಕೊಂಡು ದೇವರ ವಿಷಯಗಳಲ್ಲಿ ಐಶ್ವಯ೯ವಂತನಾಗದೆ ಇರುವವನು ಅವನಂತಯೇ ಇದ್ದಾನೆ ಅಂದನು.",
			"22": "<sup>22</sup> ಇದಲ್ಲದೆ ಯೇಸುವು ತನ್ನ ಶಿಷ್ಯರಿಗೆ ಹೇಳಿದ್ದೇನಂದರೆ - ಈ ಕಾರಣದಿಂದ ಪ್ರಾಣಧಾರಣೆಗೆ ಏನು ಊಟ ಮಾಡಬೇಕು? ದೇಹ ರಕ್ಷಣೆಗೆ ಏನು ಹೊದ್ದುಕೊಳ್ಳಬೇಕು ಎಂದು ಚಿಂತೆಮಾಡಬೇಡಿರಿ ಎಂಬದಾಗಿ ನಿಮಗೆ ಹೇಳುತ್ತೇನೆ.",
			"23": "<sup>23</sup> ಊಟಕ್ಕಿಂತ ಪ್ರಾಣವೂ ಉಡುಪಿಗಿಂತ ದೇಹವು ಮೇಲಾದದು.",
			"24": "<sup>24</sup> ಕಾಗೆಗಳನ್ನು ಗಮನಿಸಿರಿ. ಅವು ಬಿತ್ತುವದಿಲ್ಲ, ಕೊಯ್ಯುವದಿಲ್ಲ, ಅವುಗಳಿಗೆ ಉಗ್ರಾಣವೂ ಇಲ್ಲ, ಕಣಜವೂ ಇಲ್ಲ; ಅದಾಗ್ಯೂ ದೇವರು ಅವುಗಳನ್ನು ಸಾಕಿಸಲಹುತ್ತಾನೆ. ಹಕ್ಕಿಗಳಿಗಿಂತ ನೀವು ಎಷ್ಟೋ ಹೆಚ್ಚಿನವರಲ್ಲವೇ.",
			"25": "<sup>25</sup> ಚಿಂತಿಸಿ, ಚಿಂತಿಸಿ, ನಿಮ್ಮ ಜೀವನಾವಧಿಯನ್ನು ಒಂದು ಮೊಳದಷ್ಟು ಉದ್ದವಾದರೂ ಎಳೆದು ದೀರ್ಘಮಾಡಲು ನಿಮ್ಮಲ್ಲಿ ಯಾರಿಂದಾದೀತು?",
			"26": "<sup>26</sup> ಇಂಥ ಅಲ್ಪ ಕಾರ್ಯಗಳನ್ನು ಮಾಡಲು ನೀವು ಆಸಕ್ತರಾಗಿರಲಾಗಿ ಉಳಿದ ವಿಷಯದಲ್ಲಿ ಚಿಂತೆಮಾಡುವದೇಕೆ?",
			"27": "<sup>27</sup> ಹೂವುಗಳು ಬೆಳೆಯುವ ರೀತಿಯನ್ನು ಲಕ್ಷ್ಯವಿಟ್ಟು ಗಮನಿಸಿ ನೋಡಿರಿ; ಅವು ದುಡಿಯುವುದಿಲ್ಲ, ನೂಲುವದಿಲ್ಲ, ಆದರೂ ಅರಸನಾದ ಸೊಲೊಮೋನನು ತನ್ನ ಸರ್ವ ವೈಭವದಲ್ಲಿ ಇದ್ದಾಗಲೂ, ಈ ಹೂವುಗಳಲ್ಲಿ ಒಂದರಷ್ಟೂ ಸುಂದರವಾದ ಉಡುಪುಗಳನ್ನು ಧರಿಸಲಿಲ್ಲವೆಂದು ನಿಮಗೆ ಹೇಳುತ್ತೇನೆ.",
			"28": "<sup>28</sup> ಎಲೈ ಅಲ್ಪ ವಿಶ್ವಾಸಿಗಳೇ, ಈ ಹೊತ್ತು ಇದ್ದು ನಾಳೆ ಒಲೆಯ ಪಾಲಾಗುವ ಅಡವಿಯ ಹುಲ್ಲಿಗೆ ದೇವರು ಈ ಪ್ರಕಾರ ಉಡಿಸಿದರೆ ಅದಕ್ಕಿಂತ ಎಷ್ಟೋ ಹೆಚ್ಚಾಗಿ ನಿಮಗೆ ಉಡಿಸಿ ತೊಡಿಸುವನು.",
			"29": "<sup>29</sup> ಏನು ಊಟ ಮಾಡಬೇಕು? ಏನು ಕುಡಿಯಬೇಕು? ಎಂದು ತವಕಪಟ್ಟು ಚಿಂತಿಸಬೇಡಿರಿ.",
			"30": "<sup>30</sup> ಇವೆಲ್ಲವುಗಳ ಕುರಿತು ಇಹಲೋಕದ ಜನಗಳು ಅಲೆದಾಡುತ್ತಾರೆ. ಇವು ನಿಮಗೆ ಅವಶ್ಯವೆಂದು ನಿಮ್ಮ ತಂದೆಗೆ ತಿಳಿದಿದೆ.",
			"31": "<sup>31</sup> ನೀವಾದರೋ ದೇವರ ರಾಜ್ಯಕ್ಕಾಗಿ ತವಕಪಡಿರಿ. ಇದರ ಕೂಡ ಅವೂ ನಿಮಗೆ ದೊರಕುವದು.",
			"32": "<sup>32</sup> ಚಿಕ್ಕ ಹಿಂಡೇ, ಹೇದರಬೇಡ; ಆ ರಾಜ್ಯವನ್ನು ನಿಮಗೆ ದಯಪಾಲಿಸುವುದಕ್ಕೆ ನಿಮ್ಮ ತಂದೆಯು ಸಂತೋಷವುಳ್ಳವನಾಗಿದ್ದಾನೆ.",
			"33": "<sup>33</sup> ನಿಮಗಿರುವದನ್ನು ಮಾರಿಬಿಟ್ಟು ದಾನಾಧರ್ಮ ಮಾಡಿರಿ. ನಶಿಸದ ಹಣಚೀಲಗಳನ್ನು ಗಳಿಸಿಕೊಳ್ಳಿರಿ; ಪರಲೋಕದಲ್ಲಿ ಲಯವಾಗದ ಸಂಪತ್ತನ್ನು ಇಟ್ಟುಕೊಳ್ಳಿರಿ. ಅದನ್ನು ಕದಿಯುವುದಕ್ಕೆ ಕಳ್ಳನು ಸಮೀಪಕ್ಕೆ ಬರಲಾರನು. ಅವು ನುಸಿ ಹಿಡಿದು ನಾಶವಾಗುವುದಿಲ್ಲ.",
			"34": "<sup>34</sup> ನಿಮ್ಮ ಗಂಟು ಇದ್ದಲ್ಲಿಯೇ ನಿಮ್ಮ ಮನಸ್ಸು ಇರುವದಷ್ಟೇ.",
			"35": "<sup>35</sup> ನಿಮ್ಮ ನಡುಗಳು ಕಟ್ಟಿರಲಿ. ನಿಮ್ಮ ದೀಪಗಳು ಆರದಂತೆ ಉರಿಯುತ್ತಾ ಇರಲಿ.",
			"36": "<sup>36</sup> ನೀವಂತೂ ತಟ್ಟಿದ ಕೂಡಲೆ ಬಾಗಿಲನ್ನು ತೆರೆಯಲು ಸಿದ್ಧರಾಗಿರುವ ಸೇವಕರಂತೆ ಇರಿ. ತಮ್ಮ ಯಜಮಾನನು ಯಾವಾಗ ಮದುವೆ ಔತಣ ಮುಗಿಸಿಕೊಂಡು ಹಿಂತಿರುಗುವನೋ ಎಂದು ಕಾಯುವ ದಾಸರಂತೆ ಇರಿ.",
			"37": "<sup>37</sup> ಯಜಮಾನನು ಬಂದು ಯಾವ ಆಳು ಎಚ್ಚರವಾಗಿದ್ದಾನೆಂದು ಕಂಡುಕೊಳ್ಳುವನೋ ಆ ಆಳುಗಳೇ ಧನ್ಯರು. ಏಕೆಂದರೆ, ಯಜಮಾನನೇ ನಡುಕಟ್ಟಿಕೊಂಡು ಆ ಆಳುಗಳನ್ನು ಊಟಕ್ಕೆ ಕೂರಿಸಿ, ಹತ್ತಿರಕ್ಕೆ ಬಂದು ಅವರಿಗೆ ಊಟವನ್ನು ಬಡಿಸುವನು ಎಂದು ನಿಮಗೆ ಸತ್ಯವಾಗಿ ಹೇಳುತ್ತೇನೆ.",
			"38": "<sup>38</sup> ಯಜಮಾನನು ಬರುವಾಗ ನಡು ರಾತ್ರಿಆಗಿರಲಿ, ಮುಂಜಾವವಾಗಿರಲಿ, ಆತನು ಬರುವಾಗ ಎಚ್ಚರವಾಗಿ ಕಾಣಿಸಿಕೊಳ್ಳುವ ಸೇವಕರು ಧನ್ಯರು.",
			"39": "<sup>39</sup> ಕಳ್ಳನು ಬರುವ ಗಳಿಗೆಯು ಮನೆ ಯಜಮಾನನಿಗೆ ತಿಳಿದಿದ್ದರೆ ಅವನು ಎಚ್ಚರವಾಗಿದ್ದು ತನ್ನ ಮನೆಗೆ ಕನ್ನಾ ಹಾಕಗೊಡಿಸುತ್ತಿರಲಿಲ್ಲವೆಂದು ತಿಳುಕೊಳ್ಳಿರಿ.",
			"40": "<sup>40</sup> ನೀವು ಇದನ್ನು ಚೆನ್ನಾಗಿ ತಿಳಿದುಕೊಂಡು ನೀವು ಸಹ ಸಿದ್ಧವಾಗಿರಿ. ನೀವು ನೆನಸದ ಗಳಿಗೆಯಲ್ಲಿ ಮನುಷ್ಯಕುಮಾರನು ಬರುತ್ತಾನೆ ಅಂದನು.",
			"41": "<sup>41</sup> ಆಗ ಪೇತ್ರನು - ಸ್ವಾಮಿ, ನೀನು ಈ ಸಾಮ್ಯವು ನಮಗೆ ಮಾತ್ರ ಅನ್ವಯಿಸುತ್ತದೋ ಅಥವಾ ಎಲ್ಲರಿಗೋ? ಎಂದು ಕೇಳಲು.",
			"42": "<sup>42</sup> ಯೇಸು ಸ್ವಾಮಿಯು ಹೇಳಿದ್ದೇನಂದರೆ - ಹೊತ್ತು ಹೊತ್ತಿಗೆ ಅವಶ್ಯಕತೆಗೆ ಬೇಕಾದದ್ದನ್ನು ಅಳೆದು ಕೊಡುವುದಕ್ಕಾಗಿ ಯಜಮಾನನು ತನ್ನ ಮನೆಯವರ ಮೇಲೆ ನೇಮಿಸಿದ ನಂಬಿಗಸ್ತನೂ ವಿವೇಕಿಯೂ ಆದ ಮನೆವಾರ್ತೆಯವನು ಯಾರು?",
			"43": "<sup>43</sup> ಯಜಮಾನನು ಬಂದು ಯಾವ ಆಳು ಹೀಗೆ ಮಾಡುವದನ್ನು ಕಾಣುವನೋ ಆ ಆಳು ಧನ್ಯನು.",
			"44": "<sup>44</sup> ಅಂಥವನನ್ನು ಅವನು ತನ್ನ ಎಲ್ಲಾ ಆಸ್ತಿಯ ಮೇಲೆ ಅಧಿಕಾರಿಯಾಗಿ ನೇಮಿಸುವನು ಎಂದು ನಿಮಗೆ ಸತ್ಯವಾಗಿ ಹೇಳುತ್ತೇನೆ.",
			"45": "<sup>45</sup> ಆದರೆ ಆ ಆಳು - ನನ್ನ ಯಜಮಾನನು ಬರುವುದಕ್ಕೆ ತಡವಾಗುತ್ತದೆಯೆಂದು ತನ್ನ ಮನಸ್ಸಿನಲ್ಲಿ ಅಂದುಕೊಂಡು ಗಂಡಾಳು ಹೆಣ್ಣಾಳುಗಳನ್ನು ಹೊಡೆಯುವದಕ್ಕೂ ತೊಂದರೆಕೊಡುವುದಕ್ಕೂ ಮತ್ತು ಅಮಲೇರುವಷ್ಟು ತಿಂದು ಕುಡಿಯುವುದಕ್ಕೂ ತೊಡಗಿದರೆ,",
			"46": "<sup>46</sup> ಅವನು ನೆನಸದ ದಿನದಲ್ಲಿಯೂ ತಿಳಿಯದ ಗಳಿಗೆಯಲ್ಲಿಯೂ ಆ ಆಳಿನ ಯಜಮಾನನು ಬಂದು ಅವನನ್ನು ಕಠಿಣವಾದ ಚಿತ್ರಹಿಂಸೆಗೂ ಅಪನಂಬಿಗಸ್ತರಿಗೆ ಆಗತಕ್ಕ ಗತಿಯನ್ನು ಅವನಿಗೆ ನೇಮಿಸುವನು.",
			"47": "<sup>47</sup> ತನ್ನ ಯಜಮಾನನ ಇಷ್ಟಾರ್ಥವನ್ನು ಆಳು ಅರಿತುಕೊಂಡಿದ್ದರೂ ಅಜಾಗರೂಕನಾಗಿ ಅವನ ಚಿತ್ತಕ್ಕನುಸಾರ ನಡೆಯದಿದ್ದರೆ ಕಠಿಣವಾದ ಶಿಕ್ಷೆಗೆ ಗುರಿಯಾಗುವನು.",
			"48": "<sup>48</sup> ತಿಳಿಯದೆ ಅಜಾಗರೂಕನಾಗಿದ್ದರೆ ಕಡಿಮೆ ಶಿಕ್ಷೆಗೆ ಗುರಿಯಾಗುತ್ತಾನೆ. ಯಾವನಿಗೆ ಬಹಳವಾಗಿ ಕೊಡಲ್ಪಟ್ಟಿದೆಯೋ ಅವನ ಕಡೆಯಿಂದ ಬಹಳವಾಗಿ ನಿರೀಕ್ಷಿಸಲ್ಪಡುವದು. ಇದಲ್ಲದೆ ಯಾವನ ವಶಕ್ಕೆ ಬಹಳವಾಗಿ ಒಪ್ಪಿಸಿದೆಯೋ ಅವನ ಕಡೆಯಿಂದ ಇನ್ನೂ ಹೆಚ್ಚಾಗಿ ಕೇಳಲ್ಪಡುವುದು.",
			"49": "<sup>49</sup> ನಾನು ಭೂಮಿಯ ಮೇಲೆ ಬೆಂಕಿಯನ್ನು ಹಾಕಬೇಕೆಂದು ಬಂದೆನು. ಅದು ಇಷ್ಟರೊಳಗೆ ಹತ್ತಿಕೊಂಡಿದ್ದರೆ ನನಗೆ ಎಷ್ಟೋ ಸಂತೋಷ.",
			"50": "<sup>50</sup> ಆದರೆ ನಾನು ಹೊಂದತಕ್ಕ ದೀಕ್ಷಾಸ್ನಾನ ಒಂದುಂಟು, ಅದು ನೆರವೇರುವ ತನಕ ನನಗೆ ನೆಮ್ಮದಿಯಿಲ್ಲ.",
			"51": "<sup>51</sup> ನಾನು ಭೂಮಿಯಲ್ಲಿ ಸಮಾಧಾನವನ್ನುಂಟು ಮಾಡುವದಕ್ಕೆ ಬಂದೆನೆಂದು ಭಾವಿಸುತ್ತೀರೋ? ಅದಕ್ಕೆ ಬರಲಿಲ್ಲ, ಭಿನ್ನಭೇಧಗಳನ್ನು ಉಂಟುಮಾಡುವದಕ್ಕೆ ಬಂದೆನೆಂದು ನಿಮಗೆ ಒತ್ತಿ ಹೇಳುತ್ತೇನೆ.",
			"52": "<sup>52</sup> ಹೇಗೆಂದರೆ - ಒಂದೇ ಮನೆಯಲ್ಲಿರುವ ಐದು ಮಂದಿಯೊಳಗೆ ಇಬ್ಬರಿಗೆ ವಿರೋಧವಾಗಿ ಮೂವರು, ಮೂವರಿಗೆ ವಿರೋಧವಾಗಿ ಇಬ್ಬರೂ ಏಳುವರು.",
			"53": "<sup>53</sup> ಮಗನಿಗೆ ವಿರೋಧವಾಗಿ ತಂದೆಯು, ತಂದೆಗೆ ವಿರೋಧವಾಗಿ ಮಗನು, ಮಗಳಿಗೆ ವಿರೋಧವಾಗಿ ತಾಯಿಯು, ತಾಯಿಗೆ ವಿರೋಧವಾಗಿ ಮಗಳು, ಸೊಸೆಗೆ ವಿರೋಧವಾಗಿ ಅತ್ತೆಯು. ಅತ್ತೆಗೆ ವಿರೋಧವಾಗಿ ಸೊಸೆಯು ಪರಸ್ಪರ ಭಿನ್ನಭೇಧವುಳ್ಳವರಾಗಿ ವಿಂಗಡಿಸಿ ಹೋಗುವರು, ಅಂದನು.",
			"54": "<sup>54</sup> ಇದಲ್ಲದೆ ಯೇಸುವು ಎಲ್ಲಜನರಿಗೂ ಹೇಳಿದ್ದೇನಂದರೆ - ಪಶ್ಚಿಮ ದಿಕ್ಕಿನಲ್ಲಿ ಮೋಡ ಏಳುವುದನ್ನು ನೀವು ನೋಡಿದ ಕೂಡಲೆ ಮಳೆ ಬರುತ್ತದೆ ಅನ್ನುತ್ತೀರಿ; ಹಾಗೆಯೇ ಆಗುತ್ತದೆ.",
			"55": "<sup>55</sup> ದಕ್ಷಿಣ ದಿಕ್ಕಿನಿಂದ ಗಾಳಿ ಬೀಸಲಾಗಿ ಸೆಕೆ ಹುಟ್ಟುತ್ತದೆ ಅನ್ನುತ್ತೀರಿ. ಅದೂ ಹಾಗೆಯೇ ಆಗುತ್ತದೆ.",
			"56": "<sup>56</sup> ಕಪಟಿಗಳೆ, ನೀವು ಭೂಮ್ಯಾಕಾಶಗಳ ಲಕ್ಷಣಗಳನ್ನು ಸರಿಯಾಗಿ ಅರಿತುಕೊಳ್ಳಬಲ್ಲಿರಿ; ಆದರೆ ಪ್ರಸ್ತುತ ಕಾಲವನ್ನು ವಿವರಿಸುವ ಸಾಮರ್ಥ್ಯವನ್ನು ಹೊಂದದೆಹೊಗಿದ್ದಿರಾ?",
			"57": "<sup>57</sup> ಇದಲ್ಲದೆ ನ್ಯಾಯವಾದದ್ದು ಯಾವುದೆಂದು ನೀವೇ ಯಾಕೆ ತೀರ್ಮಾನ ಮಾಡಿಕೊಳ್ಳುವದಿಲ್ಲ?",
			"58": "<sup>58</sup> ನೀನು ವಾದಿಯ ಸಂಗಡ ನ್ಯಾಯಾಧಿಪತಿಯ ಬಳಿಗೆ ಹೋಗುತ್ತಿರುವಾಗ ದಾರಿಯಲ್ಲಿಯೇ ಜಗಳವನ್ನು ಪರಿಹರಿಸಿಕೊಳ್ಳಲು ಪ್ರಯತ್ನಿಸು. ಇಲ್ಲದಿದ್ದರೆ ಅವನು ನಿನ್ನನ್ನು ನ್ಯಾಯಾಧಿಪತಿಯ ಬಳಿಗೆ ಎಳಕೊಂಡು ಹೋದಾನು. ನ್ಯಾಯಾಧಿಪತಿಯು ನಿನ್ನನ್ನು ಸೆರೆ ಯಜಮಾನನ ವಶಕ್ಕೆ ಒಪ್ಪಿಸಾನು. ಸೆರೆಯಜಮಾನನು ನಿನ್ನನ್ನು ಸೆರೆಮನೆಯಲ್ಲಿ ಹಾಕಿಯಾನು.",
			"59": "<sup>59</sup> ನೀನು ಕೊನೆಯ ಕಾಸನ್ನು ಕೊಟ್ಟು ತೀರಿಸುವವರೆಗೂ ಅಲ್ಲಿಂದ ಹೊರ ಬರುವುದಕ್ಕೆ ಸಾಧ್ಯವಿಲ್ಲವೆಂದು ನಾನು ನಿನಗೆ ಒತ್ತಿ ಹೇಳುತ್ತೇನೆ, ಅಂದನು."
		},
		"13": {
			"1": "<sup>1</sup> ಅದೇ ಸಮಯದಲ್ಲಿ ಕೆಲವರು ಯೇಸುವಿನ ಹತ್ತಿರದಲ್ಲಿದ್ದು - ಪಿಲಾತನು ಗಲಿಲಾಯದವರ ರಕ್ತವನ್ನು ಅವರು ಕೊಟ್ಟ ಬಲಿಗಳ ಸಂಗಡ ಬೆರಸಿದನೆಂದು ಆತನಿಗೆ ತಿಳಿಸಿದರು.",
			"2": "<sup>2</sup> ಅದಕ್ಕೆ ಆತನು ಅವರಿಗೆ ಆ ಗಲಿಲಾಯದವರು ಇಂಥಾ ಕೊಲೆಯನ್ನು ಅನುಭವಿಸಿದ್ದರಿಂದ ಅವರನ್ನು ಎಲ್ಲಾ ಗಲಿಲಾಯದವರಿಗಿಂತ ಪಾಪಿಷ್ಠರೆಂದು ಭಾವಿಸುತ್ತೀರೋ?",
			"3": "<sup>3</sup> ಹಾಗೆ ಭಾವಿಸಕೂಡದೆಂದು ನಿಮಗೆ ಹೇಳುತ್ತೇನೆ. ದೇವರ ಕಡೆಗೆ ತಿರುಗಿಕೊಳ್ಳದಿದ್ದರೆ ನೀವೆಲ್ಲರೂ ಹಾಗೆಯೇ ಹಾಳಾಗಿ ಹೋಗುವಿರಿ.",
			"4": "<sup>4</sup> ಇಲ್ಲವೆ ಸಿಲೊವಾಮಿನಲ್ಲಿ ಗೋಪುರ ಬಿದ್ದು ಸತ್ತ ಆ ಹದಿನೆಂಟು ಮಂದಿಯು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ವಾಸವಾಗಿರುವ ಎಲ್ಲಾ ಮನುಷ್ಯರಿಗಿಂತಲೂ ಕೆಟ್ಟವರೆಂದು ನೀವು ಭಾವಿಸುತ್ತೀರೋ?",
			"5": "<sup>5</sup> ಹಾಗಲ್ಲವೆಂದು ನಿಮಗೆ ಹೇಳುತ್ತೇನೆ. ದೇವರ ಕಡೆಗೆ ತಿರುಗಿಕೊಳ್ಳದಿದ್ದರೆ ನೀವೆಲ್ಲರೂ ಅವರಂತೆ ಹಾಳಾಗಿ ಹೋಗುವಿರಿ ಎಂದು ಹೇಳಿದನು.",
			"6": "<sup>6</sup> ಆ ಮೇಲೆ ಯೇಸು ಒಂದು ಸಾಮ್ಯವನ್ನು ಹೇಳಿದನು. ಅದೇನಂದರೆ - ಒಬ್ಬನು ತನ್ನ ದ್ರಾಕ್ಷಿಯ ತೋಟದಲ್ಲಿ ಒಂದು ಅಂಜೂರದ ಗಿಡವನ್ನು ನೆಡಿಸಿದನು. ಸ್ವಲ್ಪಕಾಲದ ನಂತರ ಅವನು ಆ ಮರದಲ್ಲಿ ಹಣ್ಣನ್ನು ಹುಡುಕುತ್ತಾ ಬಂದನು. ಸಿಕ್ಕಲಿಲ್ಲ.",
			"7": "<sup>7</sup> ಬಳಿಕ ಅವನು ತೋಟಗಾರನಿಗೆ - ನೋಡು, ನಾನು ಮೂರು ವರುಷದಿಂದಲೂ ಈ ಅಂಜೂರದ ಮರದಲ್ಲಿ ಹಣ್ಣನ್ನು ಹುಡುಕುತ್ತಾ ಬಂದಿದ್ದೇನೆ; ಆದರೂ ನನಗೆ ಸಿಕ್ಕಲಿಲ್ಲ. ಇದನ್ನು ಕಡಿದು ಹಾಕು. ಇದರಿಂದ ಭೂಮಿಯು ಯಾಕೆ ವ್ಯರ್ಥವಾಗಬೇಕು ಎಂದು ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ಆದಕ್ಕೆ ಆ ತೋಟಗಾರನು - ಯಜಮಾನನೇ ಈ ವರುಷವೂ ಇದನ್ನು ಬಿಡು, ಅಷ್ಟರಲ್ಲಿ ನಾನು ಇದರ ಸುತ್ತಲು ಅಗಿದು ಗೊಬ್ಬರ ಹಾಕುತ್ತೇನೆ.",
			"9": "<sup>9</sup> ಮುಂದೆ ಅದರಲ್ಲಿ ಹಣ್ಣು ಬಿಟ್ಟರೆ ಸರಿ, ಇಲ್ಲದಿದ್ದರೆ ಇದನ್ನು ಕಡಿದುಹಾಕಬಹುದು ಎಂದು ಉತ್ತರ ಕೊಟ್ಟನು.",
			"10": "<sup>10</sup> ಒಂದು ಸಬ್ಬತ್ ದಿನದಲ್ಲಿ ಯೇಸು ಒಂದು ಸಭಾಮಂದಿರದೊಳಗೆ ಉಪದೇಶಮಾಡುತ್ತಾ ಇದ್ದನು.",
			"11": "<sup>11</sup> ಅಲ್ಲಿ ಹದಿನೆಂಟು ವರುಷಗಳಿಂದ ದುರಾತ್ಮನಿಂದ ಭಾದಿತಳಾಗಿ ಬೆನ್ನು ಬಗ್ಗಿ ಹೋಗಿ ಗೂನಿಯಾಗಿದ್ದು ಸ್ವಲ್ಪವಾದರೂ ಮೈಯನ್ನು ಮೇಲಕ್ಕೆ ಎತ್ತಲಾರದೆ ಇದ್ದ ಒಬ್ಬ ಹೆಣ್ಣುಮಗಳು ಇದ್ದಳು.",
			"12": "<sup>12</sup> ಯೇಸು ಆಕೆಯನ್ನು ನೋಡಿ ಹತ್ತಿರಕ್ಕೆ ಕರೆದು ಆಕೆಗೆ - ನಿನ್ನ ರೋಗವು ಬಿಡುಗಡೆಯಾಯಿತು ಎಂದು ಹೇಳಿ ಆಕೆಯ ಮೇಲೆ ತನ್ನ ಕೈಗಳನ್ನಿಟ್ಟನು;",
			"13": "<sup>13</sup> ಇಟ್ಟಕೂಡಲೆ ಆಕೆ ನೆಟ್ಟಗಾದಳು, ದೇವರನ್ನು ಕೊಂಡಾಡಿದಳು",
			"14": "<sup>14</sup> ಆದರೆ ಆ ಸಭಾಮಂದಿರದ ಅಧಿಕಾರಿಯು ನಡೆದ ಸಂಗತಿಯನ್ನು ನೋಡಿ, ಸಬ್ಬತ್ ದಿನದಲ್ಲಿ ಯೇಸು ಸ್ವಸ್ಥ ಮಾಡಿದನಲ್ಲಾ ಎಂದು ಸಿಟ್ಟುಗೊಂಡು ಜನರಿಗೆ - ಕೆಲಸ ಮಾಡುವುದಕ್ಕೆ ಆರು ದಿನಗಳು ಅವೆಯಷ್ಟೆ. ಆ ದಿವಸಗಳಲ್ಲಿ ಬಂದು ವಾಸಿಮಾಡಿಸಿಕೊಳ್ಳಿರಿ, ಸಬ್ಬತ್ ದಿನದಲ್ಲಿ ಮಾತ್ರ ಬೇಡ ಎಂದು ಹೇಳಿದನು.",
			"15": "<sup>15</sup> ಆ ಮಾತನ್ನು ಕೇಳಿ ಸ್ವಾಮಿಯು ಅವನಿಗೆ - ಕಪಟಿಗಳು ನೀವು, ನಿಮ್ಮಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬನು ಸಬ್ಬತ್ ದಿನದಲ್ಲಿ ತನ್ನ ಎತ್ತನ್ನಾಗಲಿ ಕತ್ತೆಯನ್ನಾಗಲಿ ಗೋದಲಿಯಿಂದ ಬಿಚ್ಚಿ ನೀರು ಕುಡಿಸುವದಕ್ಕಾಗಿ ಹಿಡುಕೊಂಡು ಹೋಗುತ್ತಾನಲ್ಲವೇ.",
			"16": "<sup>16</sup> ಹಾಗಾದರೆ ಹದಿನ್ನೆಂಟು ವರುಷಗಳ ತನಕ ಸೈತಾನನು ಕಟ್ಟಿ ಹಾಕಿದ್ದವಳೂ ಅಬ್ರಹಾಮನ ವಂಶದವಳೂ ಆಗಿರುವ ಈಕೆಯನ್ನು ಸಬ್ಬತ್ ದಿನದಲ್ಲಿ ಈ ಬಂಧನದಿಂದ ಬಿಡಿಸಬಾರದೋ ಎಂದು ಉತ್ತರ ಕೊಟ್ಟನು.",
			"17": "<sup>17</sup> ಈ ಮಾತುಗಳನ್ನು ಆತನು ಹೇಳುತ್ತಿರಲಾಗಿ ಆತನ ವಿರೋಧಿಗಳೆಲ್ಲರೂ ಅವಮಾನಿತರಾದರು. ಗುಂಪು ಕೂಡಿದ್ದ ಜನರೆಲ್ಲರೂ ಆತನಿಂದಾಗುತ್ತಿದ್ದ ಎಲ್ಲಾ ಮಹತ್ತಾದ ಕಾರ್ಯಗಳಿಗೆ ಸಂತೋಷಪಟ್ಟರು.",
			"18": "<sup>18</sup> ಯೇಸು ಹೇಳಿದ್ದೇನಂದರೆ - ದೇವರ ರಾಜ್ಯವು ಯಾವುದಕ್ಕೆ ಹೋಲಿಕೆಯಾಗಿದೆ?",
			"19": "<sup>19</sup> ಅದನ್ನು ನಾನು ಯಾವುದಕ್ಕೆ ಹೋಲಿಸಲಿ? ಅದು ಸಾಸಿವೆ ಕಾಳಿಗೆ ಹೋಲಿಕೆಯಾಗಿದೆ. ಒಬ್ಬ ಮನುಷ್ಯನು ಅದನ್ನು ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ತನ್ನ ತೋಟದಲ್ಲಿ ಹಾಕಿದನು. ಅದು ಬೆಳೆದು ಮರವಾಯಿತು. ಮತ್ತು ಆಕಾಶದಲ್ಲಿ ಹಾರಾಡುವ ಪಕ್ಷಿಗಳು ಅದರ ಕೊಂಬೆಗಳಲ್ಲಿ ವಾಸಮಾಡಿದವು ಅಂದನು.",
			"20": "<sup>20</sup> ಆತನು ಇನ್ನೂ ಹೇಳಿದ್ದೇನಂದರೆ - ದೇವರ ರಾಜ್ಯವನ್ನು ನಾನು ಯಾವುದಕ್ಕೆ ಹೋಲಿಸಲಿ? ಅದು ಹುಳಿಹಿಟ್ಟಿಗೆ ಹೋಲಿಕೆಯಾಗಿದೆ.",
			"21": "<sup>21</sup> ಅದನ್ನು ಒಬ್ಬಳು ಹೆಂಗಸು ತೆಗೆದು ಮೂರು ಸೇರು ಹಿಟ್ಟಿನಲ್ಲಿ ಕಲಸಿಡಲು ಆ ಹಿಟ್ಟೆಲ್ಲಾ ಹುಳಿಯಾಯಿತು ಅಂದನು.",
			"22": "<sup>22</sup> ಯೇಸು ಊರೂರಿಗೂ ಗ್ರಾಮ ಗ್ರಾಮಕ್ಕೂ ಹೋಗಿ ಉಪದೇಶ ಮಾಡುತ್ತಾ ಯೆರೂಸಲೇಮಿಗೆ ಪ್ರಯಾಣ ಮಾಡುತ್ತಿದ್ದನು.",
			"23": "<sup>23</sup> ಆಗ ಒಬ್ಬನು - ಸ್ವಾಮಿ, ರಕ್ಷಣೆ ಹೊಂದುವವರು ಸ್ವಲ್ಪ ಜನರೋ ಎಂದು ಕೇಳಲು,",
			"24": "<sup>24</sup> ಆತನು ಅವರಿಗೆ - ಇಕ್ಕಟ್ಟಾದ ಬಾಗಿಲಿನಿಂದ ಒಳಗೆ ಹೋಗುವುದಕ್ಕೆ ಪ್ರಯಾಸಪಡಿರಿ. ಏಕೆಂದರೆ ಬಹು ಜನರು ಒಳಗೆ ಹೋಗುವುದಕ್ಕೆ ನೋಡುವರು. ಆದರೆ ಅವರಿಂದಾಗುವುದಿಲ್ಲ ಎಂದು ನಿಮಗೆ ಹೇಳುತ್ತೇನೆ.",
			"25": "<sup>25</sup> ಮನೆ ಯಜಮಾನನು ಎದ್ದು ಬಾಗಿಲು ಹಾಕಿಕೊಂಡ ಮೇಲೆ ನೀವು ಹೊರಗೆ ನಿಂತುಕೊಂಡು ಬಾಗಿಲು ತಟ್ಟಿ - ಕರ್ತನೇ ನಮಗೆ ಬಾಗಿಲು ತೆರೆಯಿರಿ ಎಂದು ಬಾಗಿಲು ಬಡಿಯುವುದಕ್ಕೆ ತೊಡಗುವಾಗ ಅವನು - ನೀವು ಎಲ್ಲಿಯವರೋ? ನಿಮ್ಮ ಗುರುತು ನನಗಿಲ್ಲ ಅಂದಾನು",
			"26": "<sup>26</sup> ಅದಕ್ಕೆ ನೀವು - ನಿನ್ನ ಸಂಗಡ ನಾವು ಊಟ ಮಾಡಿದೆವು, ಕುಡಿದೆವು, ನಮ್ಮ ಬೀದಿಗಳಲ್ಲಿ ನೀನು ಉಪದೇಶ ಮಾಡಿದ್ದಿ ಎಂದು ಹೇಳುವದಕ್ಕೆ ತೊಡಗುವಿರಿ.",
			"27": "<sup>27</sup> ಆದರೆ ಅವನು - ನೀವು ಎಲ್ಲಿಯವರೋ? ನಿಮ್ಮ ಗುರುತು ನನಗಿಲ್ಲ. ಅಧರ್ಮಮಾಡುವ ನೀವೆಲ್ಲರೂ ನನ್ನ ಕಡೆಯಿಂದ ಹೊರಟು ಹೋಗಿರಿ ಎಂದು ನಿಮಗೆ ಹೇಳುತ್ತೇನೆ ಅನ್ನುವನು.",
			"28": "<sup>28</sup> ಅಬ್ರಹಾಮ, ಇಸಾಕ, ಯಾಕೋಬ ಇವರು ಮತ್ತು ಎಲ್ಲಾ ಪ್ರವಾದಿಗಳು ದೇವರ ರಾಜ್ಯದಲ್ಲಿರುವದನ್ನೂ; ನಿಮ್ಮನ್ನು ಮಾತ್ರ ಹೊರಗೆ ಹಾಕುವುದನ್ನು ನೀವು ನೋಡುವಾಗ ನಿಮಗೆ ಗೋಳಾಟವೂ ಕಟಕಟನೆ ಹಲ್ಲು ಕಡಿಯೋಣವೂ ಉಂಟಾಗುವವು.",
			"29": "<sup>29</sup> ಇದಲ್ಲದೆ ಪೂರ್ವ ಪಶ್ಛಿಮ ಉತ್ತರ ದಕ್ಷಿಣಗಳಿಂದಲೂ ಜನರು ಬಂದು ದೇವರ ರಾಜ್ಯದಲ್ಲಿ ಊಟಕ್ಕೆ ಕುಳಿತುಕೊಳ್ಳುವರು.",
			"30": "<sup>30</sup> ಇಗೋ ಕಡೆಯವರಾಗಿರುವ ಕೆಲವರು ಮೊದಲಿನವರಾಗುವರು. ಮೊದಲಿನವರಾಗಿರುವ ಕೆಲವರು ಕಡೆಯವರಾಗುವರು ಅಂದನು.",
			"31": "<sup>31</sup> ಅದೇ ಗಳಿಗೆಯಲ್ಲಿ ಕೆಲವು ಮಂದಿ ಪರಿಸಾಯರು ಹತ್ತಿರ ಬಂದು ಯೇಸುವಿಗೆ - ನೀನು ಇಲ್ಲಿಂದ ಹೊರಟು ಹೋಗು, ಹೆರೋದನು ನಿನ್ನನ್ನು ಕೊಲ್ಲಬೇಕೆಂದಿದ್ದಾನೆ ಎಂದು ಹೇಳಲು,",
			"32": "<sup>32</sup> ಆತನು ಅವರಿಗೆ - ನೀವು ಹೋಗಿ ಇಗೋ, ನಾನು ಈಹೊತ್ತು ನಾಳೆ ದೆವ್ವಗಳನ್ನು ಬಿಡಿಸುತ್ತಾ ರೋಗಗಳನ್ನು ವಾಸಿಮಾಡುತ್ತಾ ಇದ್ದು ಮೂರನೆಯ ದಿನದಲ್ಲಿ ಸಿದ್ಧಿಗೆ ಬರುತ್ತೇನೆ ಎಂದು ಆ ನರಿಗೆ ಹೇಳಿರಿ ಅಂದನು.",
			"33": "<sup>33</sup> ಹೇಗೂ ನಾನು ಈ ಹೊತ್ತು ನಾಳೆ ನಾಡಿದ್ದು ಸಂಚಾರ ಮಾಡಬೇಕು. ಪ್ರವಾದಿಯಾದವನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿಯೇ ಹೊರತು ಬೇರೆ ಪಟ್ಟಣದಲ್ಲಿ ಕೊಲ್ಲಲ್ಪಡಕೂಡದಷ್ಟೆ.",
			"34": "<sup>34</sup> ಯೆರೂಸಲೇಮೇ; ಯೆರೂಸಲೇಮೇ ಪ್ರವಾದಿಗಳ ಪ್ರಾಣ ತೆಗೆಯುವವಳೇ, ದೇವರು ನಿನ್ನ ಬಳಿಗೆ ಕಳುಹಿಸಿ ಕೊಟ್ಟವರನ್ನು ಕಲ್ಲೆಸೆದು ಕೊಲ್ಲುವವಳೇ, ಕೋಳಿ ತನ್ನ ಮರಿಗಳನ್ನು ರೆಕ್ಕೆಗಳ ಕೆಳಗೆ ಸೇರಿಸಿಕೊಳ್ಳುವಂತೆ ನಿನ್ನ ಮಕ್ಕಳನ್ನು ಸೇರಿಸಿಕೊಳ್ಳುವದಕ್ಕೆ ನನಗೆ ಎಷ್ಟೋ ಸಾರಿ ಮನಸ್ಸಿತ್ತು ಆದರೆ ನಿನಗೆ ಮನಸ್ಸಿಲ್ಲದೆ ಹೋಯಿತು.",
			"35": "<sup>35</sup> ನೋಡಿರಿ, ನಿಮ್ಮ ದೇವಾಲಯ ಪಾಳುಬೀಳುವುದು. ‘ಕರ್ತನ ಹೆಸರಿನಲ್ಲಿ ಬರುವವನಿಗೆ ಆಶೀರ್ವಾದ’ ಎಂದು ನೀವು ಹೇಳುವ ವರೆಗೂ ನೀವು ನನ್ನನ್ನು ನೋಡುವದೇ ಇಲ್ಲವೆಂದು ನಿಮಗೆ ಹೇಳುತ್ತೇನೆ."
		},
		"14": {
			"1": "<sup>1</sup> ಆತನು ಸಬ್ಬತ್‍ದಿನದಲ್ಲಿ ಫರಿಸಾಯರ ಮುಖ್ಯಸ್ಥರಲ್ಲಿ ಒಬ್ಬನ ಮನೆಗೆ ಊಟಕ್ಕೆ ಹೋದಾಗ ಅವರು ಆತನನ್ನು ಸೂಕ್ಷ್ಮವಾಗಿ ಗಮನಿಸುತ್ತಿದ್ದರು.",
			"2": "<sup>2</sup> ಆಗ ಯೇಸು ತನ್ನೆದುರಿಗೆ ಜಲೋದರವುಳ್ಳ ಒಬ್ಬ ಮನುಷ್ಯನಿರುವದನ್ನು ನೋಡಿ -",
			"3": "<sup>3</sup> ಸಬ್ಬತ್‍ದಿನದಲ್ಲಿ ಸ್ವಸ್ಥಮಾಡುವದು ಸರಿಯೋ? ಸರಿಯಲ್ಲವೋ ಎಂದು ಧರ್ಮೋಪದೇಶಕರನ್ನೂ ಫರಿಸಾಯರನ್ನೂ ಕೇಳಿದನು.",
			"4": "<sup>4</sup> ಅವರು ಸುಮ್ಮನೇ ಇರಲು ಆತನು ಅವನ ಕೈ ಹಿಡಿದು ಅವನನ್ನು ವಾಸಿಮಾಡಿ ಕಳುಹಿಸಿ -",
			"5": "<sup>5</sup> ನಿಮ್ಮಲ್ಲಿ ಯಾವನೊಬ್ಬನ ಮಗನಾಗಲಿ ಎತ್ತಾಗಲಿ ಬಾವಿಯಲ್ಲಿ ಬಿದ್ದರೆ ಅವನು ತಡಮಾಡದೆ ಸಬ್ಬತ್‍ದಿನದಲ್ಲಾದರೂ ಮೇಲಕ್ಕೆ ತೆಗೆಯುವುದಿಲ್ಲವೇ ಎಂದು ಅವರನ್ನು ಕೇಳಿದನು.",
			"6": "<sup>6</sup> ಅವರು ಅದಕ್ಕೆ ಉತ್ತರಕೊಡಲಾರದೆ ಇದ್ದರು.",
			"7": "<sup>7</sup> ಊಟಕ್ಕೆ ಕರಿಸಿಕೊಂಡವರು ಪಂಕ್ತಿಯ ಮೊದಲನೆಯ ಸ್ಥಾನವನ್ನು ಆರಿಸಿಕೊಳ್ಳುವದನ್ನು ನೋಡಿ ಯೇಸು ಒಂದು ಸಾಮ್ಯವನ್ನು ಹೇಳಿದನು, ಏನಂದರೆ -",
			"8": "<sup>8</sup> ಯಾವನಾದರೂ ನಿಮ್ಮನ್ನು ಮದುವೆ ಊಟಕ್ಕೆ ಕರೆದರೆ ಪಂಕ್ತಿಯೊಳಗೆ ಮೊದಲನೆಯ ಸ್ಥಾನದಲ್ಲಿ ಕೂತುಕೊಳ್ಳಬೇಡ; ಒಂದು ವೇಳೆ ನಿನಗಿಂತ ಗೌರವವುಳ್ಳವನನ್ನು ಅವನು ಕರೆದಿರಬಹುದು,",
			"9": "<sup>9</sup> ಮತ್ತು ನಿನ್ನನ್ನೂ ಅವನನ್ನೂ ಕರೆದವನು ಬಂದು - ಇವನಿಗೆ ಸ್ಥಳ ಬಿಡು ಎಂದು ನಿನಗೆ ಹೇಳುವಾಗ ನೀನು ನಾಚಿಕೊಂಡು ಕಡೆಯಸ್ಥಾನದಲ್ಲಿ ಕೂತುಕೊಳ್ಳುವದಕ್ಕೆ ಹೋಗುವಿ.",
			"10": "<sup>10</sup> ಆದರೆ ಯಾವನಾದರೂ ನಿನ್ನನ್ನು ಕರೆದಾಗ ನೀನು ಹೋಗಿ ಕಡೆಯ ಸ್ಥಾನದಲ್ಲಿ ಕೂತುಕೋ; ಹೀಗೆ ಮಾಡಿದರೆ ನಿನ್ನನ್ನು ಕರೆದವನು ಬಂದು ನಿನ್ನನ್ನು ನೋಡಿ - ಸ್ನೇಹಿತನೇ, ಇನ್ನೂ ಮೇಲಕ್ಕೆ ಬಾ ಅನ್ನುವನು. ಆಗ ನಿನ್ನ ಸಂಗಡ ಕುಳಿತಿರುವವರೆಲ್ಲರ ಮುಂದೆ ನಿನಗೆ ಗೌರವ ದೂರಕುವುದು.",
			"11": "<sup>11</sup> ತನ್ನನ್ನು ಹೆಚ್ಚಿಸಿಕೊಳ್ಳುವ ಪ್ರತಿಯೊಬ್ಬನು ತಗ್ಗಿಸಲ್ಪಡುವನು; ತನ್ನನ್ನು ತಗ್ಗಿಸಿಕೊಳ್ಳುವವನು ಹೆಚ್ಚಿಸಲ್ಪಡುವನು ಅಂದನು.",
			"12": "<sup>12</sup> ಆತನು ತನ್ನನ್ನು ಊಟಕ್ಕೆ ಕರೆದವನಿಗೆ ಸಹ ಒಂದು ಮಾತು ಹೇಳಿದನು; ಅದೇನಂದರೆ - ನೀನು ಮಧ್ಯಾಹ್ನದ ಊಟಕ್ಕೆ ಅಥವಾ ಸಾಯಂಕಾಲದ ಊಟಕ್ಕೆ ನಿನ್ನ ಸ್ನೇಹಿತರನ್ನಾಗಲಿ ನಿನ್ನ ಅಣ್ಣತಮ್ಮಂದಿರನ್ನಾಗಲಿ ನಿನ್ನ ಬಂಧುಬಾಂಧವರನ್ನಾಗಲಿ ಐಶ್ವರ್ಯವಂತರಾದ ನೆರೆಯವರನ್ನಾಗಲಿ ಕರೆಯಬೇಡ. ಒಂದು ವೇಳೆ ಅವರು ಸಹ ಪ್ರತಿಯಾಗಿ ನಿನ್ನನ್ನು ಕರೆದಾರು, ಮತ್ತು ನಿನಗೆ ಮುಯ್ಯಿಗೆಮುಯ್ಯಾಗುವದು.",
			"13": "<sup>13</sup> ಅದರೆ ನೀನು ಔತಣ ಮಾಡಿಸುವಾಗ ಬಡವರು, ಅಂಗಹೀನವಾದವರು, ಕುಂಟರು, ಕುರುಡರು ಇಂಥವರನ್ನು ಕರೆ;",
			"14": "<sup>14</sup> ಆಗ ನೀನು ಧನ್ಯನಾಗುವೆ. ಏಕೆಂದರೆ, ಅವರು ನಿನಗೆ ಪ್ರತಿಯಾಗಿ ಏನು ಮಾಡಲೂ ಇಲ್ಲದವರು. ನೀತಿವಂತರು ಜೀವಿತರಾಗಿ ಪುನರುತ್ತಾನ ಹೊಂದುವಾಗ ದೇವರೆ ನಿನಗೆ ಸಲ್ಲಬೇಕಾದುದನ್ನು ಸಲ್ಲಿಸುವರು.",
			"15": "<sup>15</sup> ಊಟಕ್ಕೆ ಕೂಳಿತವರಲ್ಲಿ ಒಬ್ಬನು ಈ ಮಾತುಗಳನ್ನು ಕೇಳಿ - ದೇವರ ರಾಜ್ಯದಲ್ಲಿ ಊಟಮಾಡುವವನೇ ಧನ್ಯನು ಎಂದು ಹೇಳಲು",
			"16": "<sup>16</sup> ಯೇಸು ಅವನಿಗೆ ಹೇಳಿದ್ದೇನಂದರೆ - ಒಬ್ಬ ಮನುಷ್ಯನು ಬಹಳವಾಗಿ ಅಡಿಗೆಮಾಡಿಸಿ ಅನೇಕರಿಗೆ ಊಟಕ್ಕೆ ಆಮಂತ್ರಿಸಿದನು.",
			"17": "<sup>17</sup> ಆ ಮೇಲೆ ಊಟದ ಹೊತ್ತಿಗೆ ಅವನು ಊಟಕ್ಕೆ ಆಹ್ವಾನಿಸಲ್ಪಟ್ಟವರ ಬಳಿಗೆ ಬಂದು - ಈಗ ಎಲ್ಲವೂ ಸಿದ್ಧವಾಗಿದೆ ಉಟಕ್ಕೆ ಬರಬೇಕು ಎಂದು ಕರೆಯುವದಕ್ಕೆ ತನ್ನ ಆಳನ್ನು ಕಳುಹಿಸಿದನು.",
			"18": "<sup>18</sup> ಆದರೆ ಅವರೆಲ್ಲರೂ ಕ್ಷಮಿಸಬೇಕೆಂದು ಒಂದೇ ಮನಸ್ಸಿನಿಂದ ಹೇಳುವುದಕ್ಕೆ ತೊಡಗಿದರು. ಮೊದಲನೆಯವನು ಆ ಆಳನ್ನು ನೋಡಿ ನಾನು ಒಂದು ಹೊಲವನ್ನು ಕ್ರಯಕ್ಕೆ ತೆಗೆದುಕೊಂಡಿದ್ದೇನೆ, ಅದನ್ನು ಹೋಗಿ ನೋಡುವ ಅಗತ್ಯವಿದೆ, ನನ್ನನ್ನು ಕ್ಷಮಿಸಬೇಕೆಂದು ಕೇಳಿಕೊಂಡೆನೆಂಬದಾಗಿ ಹೇಳು ಅಂದನು.",
			"19": "<sup>19</sup> ಮತ್ತೊಬ್ಬನು - ನಾನು ಐದು ಜೋಡಿ ಎತ್ತುಗಳನ್ನು ಕೊಂಡಿದ್ದೇನೆ, ಅವುಗಳನ್ನು ಪರೀಕ್ಷಿಸುವದಕ್ಕೆ ಹೋಗುತ್ತಿದ್ದೇನೆ, ನನ್ನನ್ನು ಕ್ಷಮಿಸಬೇಕೆಂದು ಕೇಳಿಕೊಂಡೆನೆಂಬದಾಗಿ ಹೇಳು ಅಂದನು.",
			"20": "<sup>20</sup> ಇನ್ನೊಬ್ಬನು - ನಾನು ಮದುವೆಮಾಡಿಕೊಂಡಿದ್ದೇನೆ, ಆದ್ದರಿಂದ ನಾನು ಬರುವುದಕ್ಕಾಗುವುದಿಲ್ಲ ಅಂದನು.",
			"21": "<sup>21</sup> ಆ ಆಳು ಬಂದು ತನ್ನ ಯಜಮಾನನಿಗೆ ಈ ಸಂಗತಿಗಳನ್ನು ತಿಳಿಸಲು ಮನೆಯ ಯಜಮಾನನು ಸಿಟ್ಟುಗೊಂಡು ಆಳಿಗೆ - ನೀನು ತಟ್ಟನೆ ಈ ಊರಿನ ಬೀದಿಬೀದಿಗೂ ಸಂದುಸಂದಿಗೂ ಹೋಗಿ ಬಡವರು, ಅಂಗಹೀನರು, ಕುರುಡರು, ಕುಂಟರು, ಇಂಥವರನ್ನು ಇಲ್ಲಿಗೆ ಕರಕೊಂಡು ಬಾ ಎಂದು ಹೇಳಿದನು.",
			"22": "<sup>22</sup> ಬಳಿಕ ಆ ಆಳು - ಅಯ್ಯಾ, ನೀನು ಅಪ್ಪಣೆಕೊಟ್ಟಂತೆ ಮಾಡಿದ್ದಾಯಿತು; ಆದರೂ ಇನ್ನೂ ಸ್ಥಳ ಉಳಿದಿದೆ ಅಂದನು.",
			"23": "<sup>23</sup> ಆಗ ಆ ಯಜಮಾನನು ತನ್ನ ಆಳಿಗೆ ಹೇಳಿದ್ದೆನೆಂದರೆ - ನೀನು ಹಾದಿಗಳಿಗೂ ಬೇಲಿಗಳ ಬಳಿಗೂ ಹೋಗಿ ಅಲ್ಲಿ ಸಿಕ್ಕಿದವರನ್ನು ಬಲವಂತಮಾಡಿ ಒಳಕ್ಕೆ ಕರಕೊಂಡು ಬಾ, ನನ್ನ ಮನೆ ತುಂಬಲಿ.",
			"24": "<sup>24</sup> ಆದರೆ ಊಟಕ್ಕೆ ಹೇಳಿಸಿಕೊಂಡವರಲ್ಲಿ ಒಬ್ಬನಾದರೂ ನಾನು ಮಾಡಿಸಿದ ಅಡಿಗೆಯ ಸವಿಯನ್ನು ನೋಡಬಾರದು ಎಂದು ನಿಮಗೆ ಹೇಳುತ್ತೇನೆ ಅಂದನು.",
			"25": "<sup>25</sup> ಬಹು ಜನರು ಗುಂಪುಗುಂಪಾಗಿ ಯೇಸುವಿನ ಸಂಗಡ ಹೋಗುತ್ತಾ ಇರಲು ಆತನು ತಿರುಗಿಕೊಂಡು ಅವರಿಗೆ ಹೇಳಿದ್ದೇನಂದರೆ -",
			"26": "<sup>26</sup> ಯಾವನಾದರೂ ನನ್ನ ಬಳಿಗೆ ಬಂದು ತನ್ನ ತಂದೆ-ತಾಯಿ, ಹೆಂಡತಿ, ಮಕ್ಕಳು, ಅಣ್ಣ ತಮ್ಮಂದಿರು, ಅಕ್ಕ ತಂಗಿಯರು ಇವರನ್ನೂ ತನ್ನ ಪ್ರಾಣವನ್ನು ಸಹ ಹಗೆಮಾಡದಿದ್ದರೆ ಅವನು ನನ್ನ ಶಿಷ್ಯನಾಗಿರಲಾರನು.",
			"27": "<sup>27</sup> ಯಾವನಾದರೂ ತನ್ನ ಶಿಲುಬೆಯನ್ನು ಹೊತ್ತುಕೊಂಡು ನನ್ನ ಹಿಂದೆ ಬಂದ ಹೊರತು ಅವನು ನನ್ನ ಶಿಷ್ಯನಾಗಿರಲಾರನು.",
			"28": "<sup>28</sup> ನಿಮ್ಮಲ್ಲಿ ಯಾವನಾದರೂ ಒಂದು ಗೋಪುರವನ್ನು ಕಟ್ಟಿಸಬೇಕೆಂದು ಯೋಚಿಸಿದರೆ ಅವನು ಮೊದಲು ಕೂಳಿತುಕೊಂಡು - ಅದಕ್ಕೆ ಎಷ್ಟು ಖರ್ಚು ಆದೀತು, ಅದನ್ನು ತೀರಿಸುವದಕ್ಕೆ ಸಾಕಾಗುವಷ್ಟು ಹಣ ನನ್ನಲ್ಲಿ ಇದೆಯೋ ಎಂದು ಲೆಕ್ಕಮಾಡುವದಿಲ್ಲವೇ?",
			"29": "<sup>29</sup> ಹೀಗೆ ಲೆಕ್ಕಮಾಡದೆ ಅದಕ್ಕೆ ಅಸ್ತಿವಾರ ಹಾಕಿದ ಮೇಲೆ ಆ ಕೆಲಸವನ್ನು ಪೂರೈಸಲಾರದೆ ಹೋದರೆ ನೋಡುವವರೆಲ್ಲರು -",
			"30": "<sup>30</sup> ಈ ಮನುಷ್ಯನು ಕಟ್ಟಿಸುವದಕ್ಕಂತೂ ತೊಡಗಿದನು, ಕೆಲಸಪೂರೈಸಲಾರದೆ ಹೋದನು ಎಂದು ಅವನನ್ನು ಹಾಸ್ಯಮಾಡಾರು.",
			"31": "<sup>31</sup> ಇಲ್ಲವೆ ಯಾವ ಅರಸನಾದರೂ ಮತ್ತೊಬ್ಬ ಅರಸನ ಸಂಗಡ ಯುದ್ಧಮಾಡುವದಕ್ಕೆ ಹೋಗುವಾಗ ತನ್ನ ಮೇಲೆ ಇಪ್ಪತ್ತು ಸಾವಿರ ದಂಡು ತೆಗೆದುಕೊಂಡು ಬರುವವನನ್ನು ತಾನು ಹತ್ತು ಸಾವಿರ ದಂಡಿನಿಂದ ಎದುರಿಸುವದಕ್ಕೆ ಶಕ್ತನಾಗುವೆನೋ ಇಲ್ಲವೋ ಎಂದು ಕೂಳಿತುಕೊಂಡು ಆಲೋಚಿಸುವದಿಲ್ಲವೇ?",
			"32": "<sup>32</sup> ತಾನು ಶಕ್ತನಲ್ಲದಿದ್ದರೆ ಬರುವ ಅರಸನು ಇನ್ನೂ ದೂರದಲ್ಲಿರುವಾಗಲೇ ರಾಯಭಾರಿಗಳನ್ನು ಕಳುಹಿಸಿ ಸಂಧಾನಕ್ಕೆ ಒಪ್ಪಂದವನ್ನು ಮಾಡಿಕೊಳ್ಳುವನು.",
			"33": "<sup>33</sup> ಹಾಗೆಯೇ ನಿಮ್ಮಲ್ಲಿ ಯಾವನೇ ಆಗಲಿ ತನಗಿರುವದನ್ನೆಲ್ಲಾ ಬಿಟ್ಟುಬಿಡದೆ ಹೋದರೆ ಅವನು ನನ್ನ ಶಿಷ್ಯನಾಗಿರಲಾರನು.",
			"34": "<sup>34</sup> ಉಪ್ಪಂತೂ ಒಳ್ಳೆಯ ಪದಾರ್ಥವೇ; ಉಪ್ಪು ಸಪ್ಪಗಾದರೆ ಅದಕ್ಕೆ ಇನ್ನಾತರಿಂದ ರುಚಿಬರಿಸಲು ಸಾಧ್ಯ?",
			"35": "<sup>35</sup> ಅದು ಭೂಮಿಗಾದರೂ ತಿಪ್ಪೆಗಾದರೂ ಉಪಯೋಗಕ್ಕೆ ಬರುವುದಿಲ್ಲ; ಅದನ್ನು ಹೊರಗೆ ಬಿಸಾಡುತ್ತಾರೆ. ಕೇಳುವದಕ್ಕೆ ಕಿವಿಯುಳ್ಳವನು ಕೇಳಲಿ ಅಂದನು."
		},
		"15": {
			"1": "<sup>1</sup> ಯೇಸುವಿನ ಉಪದೇಶವನ್ನು ಕೇಳಬೇಕೆಂದು ಎಲ್ಲಾ ಸುಂಕದವರೂ ಪಾಪಿಗಳೂ ಆತನ ಬಳಿಗೆ ಬರುತ್ತಾ ಇರಲು",
			"2": "<sup>2</sup> ಫರಿಸಾಯರೂ ಶಾಸ್ತ್ರಿಗಳೂ - ಇವನು ಪಾಪಿಗಳನ್ನು ಸೇರಿಸಿಕೊಂಡು ಅವರ ಜೊತೆಯಲ್ಲಿ ಊಟಮಾಡುತ್ತಾನೆ ಎಂದು ಹೇಳಿಕೊಂಡು ಗುಣುಗುಟ್ಟುತ್ತಿದ್ದರು.",
			"3": "<sup>3</sup> ಆಗ ಆತನು ಅವರಿಗೆ ಒಂದು ಸಾಮ್ಯವನ್ನು ಹೇಳಿದನು.",
			"4": "<sup>4</sup> ಅದೇನಂದರೆ - ನಿಮ್ಮೊಳಗೆ ಯಾವ ಮನುಷ್ಯನಾದರೂ ತನಗೆ ನೂರು ಕುರಿಗಳಿರಲಾಗಿ ಅವುಗಳಲ್ಲಿ ಒಂದು ಕಳೆದುಹೋದರೆ ಅವನು ತೊಂಭತ್ತೊಂಭತ್ತನ್ನೂ ಅಡವಿಯಲ್ಲಿ ಬಿಟ್ಟು ಕಳೆದುಹೋದದ್ದು ಸಿಕ್ಕುವ ತನಕ ಅದನ್ನು ಹುಡುಕಿಕೊಂಡು ಹೋಗದೆ ಇದ್ದಾನೆಯೇ?",
			"5": "<sup>5</sup> ಸಿಕ್ಕಿದ ಮೇಲೆ ಅವನು ಸಂತೋಷದಿಂದ ಅದನ್ನು ತನ್ನ ಹೆಗಲಿನ ಮೇಲೆ ಹೊತ್ತುಕೊಂಡು",
			"6": "<sup>6</sup> ಮನೆಗೆ ಬಂದು ಸ್ನೇಹಿತರನ್ನೂ ನೆರೆಹೊರೆಯವರನ್ನೂ ಕೂಡಿಸಿ - ಕಳೆದುಹೋಗಿದ್ದ ಕುರಿ ಸಿಕ್ಕಿತು, ನನ್ನ ಸಂಗಡ ಸಂತೋಷಿಸಿರಿ ಎಂದು ಅವರಿಗೆ ಹೇಳುವನಲ್ಲವೇ.",
			"7": "<sup>7</sup> ಅದರಂತೆ ದೇವರ ಕಡೆಗೆ ತಿರುಗಿಕೊಳ್ಳುವದಕ್ಕೆ ಅವಶ್ಯವಿಲ್ಲದ ತೊಂಭತ್ತೊಂಭತ್ತು ಮಂದಿ ನೀತಿವಂತರಿಗಿಂತ ದೇವರ ಕಡೆಗೆ ತಿರುಗಿಕೊಳ್ಳುವ ಒಬ್ಬ ಪಾಪಿಯ ವಿಷಯವಾಗಿ ಪರಲೋಕದಲ್ಲಿ ಸಂತೋಷವುಂಟಾಗುವದೆಂದು ನಿಮಗೆ ಹೇಳುತ್ತೇನೆ.",
			"8": "<sup>8</sup> ಯಾವ ಹೆಂಗಸು ತನ್ನಲ್ಲಿ ಹತ್ತು ಪಾವಲಿಗಳಿರಲಾಗಿ ಒಂದು ಪಾವಲಿ ಕಳೆದು ಹೋದರೆ ದೀಪಹಚ್ಚಿ ಮನೆಯನ್ನು ಗುಡಿಸಿ ಸಿಕ್ಕುವ ತನಕ ಅದನ್ನು ಎಚ್ಚರದಿಂದ ಹುಡುಕದೆ ಇದ್ದಾಳೂ?",
			"9": "<sup>9</sup> ಸಿಕ್ಕಿದ ಮೇಲೆ ಆಕೆಯು ತನ್ನ ಗೆಳತಿಯರನ್ನೂ ನೆರೆಹೊರೆಯವರನ್ನೂ ಸೇರಿಸಿಕೊಂಡು - ಕಳೆದು ಹೋಗಿದ್ದ ಪಾವಲಿ ಸಿಕ್ಕಿತು, ನನ್ನ ಸಂಗಡ ಸಂತೋಷಿಸಿರಿ ಎಂದು ಹೇಳುವಳಲ್ಲವೇ.",
			"10": "<sup>10</sup> ಅದರಂತೆ ಒಬ್ಬ ಪಾಪಿಯು ದೇವರ ಕಡೆಗೆ ತಿರುಗಿಕೊಳ್ಳುವ ವಿಷಯದಲ್ಲಿ ದೇವದೂತರ ಮುಂದೆ ಸಂತೋಷವಾಗುವದೆಂದು ನಿಮಗೆ ಹೇಳುತ್ತೇನೆ ಅಂದನು.",
			"11": "<sup>11</sup> ಇನ್ನೂ ಆತನು ಹೇಳಿದ್ದೇನಂದರೆ - ಒಬ್ಬ ಮನುಷ್ಯನಿಗೆ ಇಬ್ಬರು ಮಕ್ಕಳಿದ್ದರು.",
			"12": "<sup>12</sup> ಅವರಲ್ಲಿ ಕಿರಿಯವನು ತಂದೆಗೆ - ಅಪ್ಪಾ, ಆಸ್ತಿಯಲ್ಲಿ ನನಗೆ ಬರತಕ್ಕ ಪಾಲನ್ನು ಕೊಡು ಎಂದು ಕೇಳಿಕೊಳ್ಳಲು ತಂದೆಯು ಆಸ್ತಿಯನ್ನು ಅವರಿಗೆ ಹಂಚಿಕೊಟ್ಟನು.",
			"13": "<sup>13</sup> ಸ್ವಲ್ಪ ದಿವಸದ ಮೇಲೆ ಆ ಕಿರೀಮಗನು ತನ್ನ ಪಾಲನ್ನೆಲ್ಲಾ ಕೂಡಿಸಿಕೊಂಡು ದೂರದೇಶಕ್ಕೆ ಹೊರಟುಹೋಗಿ ಅಲ್ಲಿ ಪಟಿಂಗನಾಗಿ ಬದುಕಿ ತನ್ನ ಆಸ್ತಿಯನ್ನು ಹಾಳುಮಾಡಿಕೊಂಡನು.",
			"14": "<sup>14</sup> ಹೀಗೆ ಅವನು ಎಲ್ಲಾ ಹಾಳುಮಾಡಿಕೊಂಡ ಮೇಲೆ ಆ ದೇಶದಲ್ಲೆಲ್ಲಾ ಘೋರವಾದ ಬರ ಬಂದು ಏನೂ ಗತಿಯಿಲ್ಲದವನಾದನು.",
			"15": "<sup>15</sup> ಆಗ ಅವನು ಹೋಗಿ ಆ ದೇಶದ ನಿವಾಸಿಗಳೊಳಗೆ ಒಬ್ಬನಲ್ಲಿ ಸೇರಿಕೊಂಡನು; ಆ ಮನುಷ್ಯನು ಹಂದಿಗಳನ್ನು ಮೇಯಿಸುವದಕ್ಕೆ ಅವನನ್ನು ತನ್ನ ಹೊಲಗಳಿಗೆ ಕಳುಹಿಸಿದನು.",
			"16": "<sup>16</sup> ಹೀಗಿರಲಾಗಿ ಅವನು ಹಂದಿ ತಿನ್ನುತ್ತಿದ್ದ ಕಾಯಿಗಳನ್ನಾದರೂ ತಿಂದು ಹಸಿವನ್ನು ತೀರಿಸಿಕೊಳ್ಳಬೇಕೆಂದು ಆಶೆಪಟ್ಟನು; ಆದರೂ ಯಾರೂ ಅವನಿಗೆ ಕೊಡಲಿಲ್ಲ.",
			"17": "<sup>17</sup> ಆಗ ಅವನಿಗೆ ಬುದ್ಧಿಬಂದು ಅವನು - ನನ್ನ ತಂದೆಯ ಬಳಿಯಲ್ಲಿ ಎಷ್ಟೋ ಮಂದಿ ಕೂಲಿಯಾಳುಗಳಿಗೆ ಬೇಕಾದಷ್ಟು ಆಹಾರವಿದೆ; ನಾನಾದರೋ ಇಲ್ಲಿ ಹಸಿವಿನಿಂದ ಸಾಯುತ್ತಿದ್ದೇನೆ.",
			"18": "<sup>18</sup> ನಾನು ಎದ್ದು ನನ್ನ ತಂದೆಯ ಬಳಿಗೆ ಹೋಗಿ ಅವನಿಗೆ - ಅಪ್ಪಾ, ಪರಲೋಕಕ್ಕೆ ವಿರೋಧವಾಗಿಯೂ ನಿನ್ನ ಮುಂದೆಯೂ ನಾನು ಪಾಪ ಮಾಡಿದ್ದೇನೆ; ಇನ್ನು ನಾನು ನಿನ್ನ ಮಗನೆನಿಸಿಕೊಳ್ಳುವದಕ್ಕೆ ಯೋಗ್ಯನಲ್ಲ;",
			"19": "<sup>19</sup> ನನ್ನನ್ನು ನಿನ್ನ ಕೂಲಿಯಾಳುಗಳಲ್ಲಿ ಒಬ್ಬನಂತೆ ಮಾಡು ಎಂದು ಹೇಳುವೆನು ಅಂದುಕೊಂಡು ಎದ್ದು ತನ್ನ ತಂದೆಯ ಕಡೆಗೆ ಬಂದನು.",
			"20": "<sup>20</sup> ಅವನು ಇನ್ನೂ ದೂರದಲ್ಲಿರುವಾಗ ಅವನ ತಂದೆಯು ಅವನನ್ನು ಕಂಡು ಕನಿಕರಪಟ್ಟು ಓಡಿಬಂದು ಅವನ ಕೊರಳನ್ನು ಅಪ್ಪಿಕೊಂಡು ಅವನಿಗೆ ಬಹಳವಾಗಿ ಮುದ್ದಿಟ್ಟನು.",
			"21": "<sup>21</sup> ಆದರೂ ಮಗನು ಅವನಿಗೆ - ಅಪ್ಪಾ, ಪರಲೋಕಕ್ಕೆ ವಿರೋಧವಾಗಿಯೂ ನಿನ್ನ ಮುಂದೆಯೂ ಪಾಪಮಾಡಿದ್ದೇನೆ; ಇನ್ನು ನಾನು ನಿನ್ನ ಮಗನೆನಿಸಿ ಕೊಳ್ಳುವದಕ್ಕೆ ಯೋಗ್ಯನಲ್ಲ - ಎಂದು ಹೇಳಲು",
			"22": "<sup>22</sup> ತಂದೆಯು ತನ್ನ ಆಳುಗಳಿಗೆ - ಶ್ರೇಷ್ಠವಾದ ನಿಲುವಂಗಿಯನ್ನು ತಟ್ಟನೆ ತಂದು ಇವನಿಗೆ ತೊಡಿಸಿ; ಇವನ ಕೈಗೆ ಉಂಗುರವನ್ನು ಹಾಕಿರಿ; ಕಾಲಿಗೆ ಜೋಡು ಹಾಕಿರಿ;",
			"23": "<sup>23</sup> ಕೊಬ್ಬಿಸಿದ ಆ ಕರುವನ್ನು ತಂದು ಕೊಯ್ಯಿರಿ; ಹಬ್ಬಮಾಡೋಣ, ಉಲ್ಲಾಸಪಡೋಣ.",
			"24": "<sup>24</sup> ಈ ನನ್ನ ಮಗನು ಸತ್ತವನಾಗಿದ್ದನು, ತಿರಿಗಿ ಬದುಕಿ ಬಂದನು; ಪೋಲಿಹೋಗಿದ್ದನು, ಸಿಕ್ಕಿದನು ಎಂದು ಹೇಳಿ ಸಂತೋಷಿಸುವುದಕ್ಕೂ ಉಲ್ಲಾಸಪಡುವದಕ್ಕೂ ತೊಡಗಿದರು.",
			"25": "<sup>25</sup> ಆದರೆ ಅವನ ಹಿರೀಮಗನು ಹೊಲದಲ್ಲಿದ್ದನು. ಅವನು ಮನೆಯ ಸಮೀಪಕ್ಕೆ ಬರುತ್ತಿರುವಾಗ ವಾದ್ಯಘೋಷಗಳನ್ನೂ ನರ್ತನಗಳನ್ನು ಕೇಳಿ",
			"26": "<sup>26</sup> ಆಳುಗಳಲ್ಲಿ ಒಬ್ಬನನ್ನು ತನ್ನ ಬಳಿಗೆ ಕರೆದು - ಇದೇನು ಎಂದು ವಿಚಾರಿಸಿದನು.",
			"27": "<sup>27</sup> ಆಳು ಅವನಿಗೆ - ನಿನ್ನ ತಮ್ಮ ಬಂದಿದ್ದಾನೆ; ಇವನು ಸುರಕ್ಷಿತನಾಗಿ ಬಂದದ್ದರಿಂದ ನಿನ್ನ ತಂದೆಯು ಆ ಕೊಬ್ಬಿಸಿದ ಕರುವನ್ನು ಕೊಯ್ಸಿದ್ದಾನೆ ಎಂದು ಹೇಳಿದನು.",
			"28": "<sup>28</sup> ಇದನ್ನು ಕೇಳಿ ಕೋಪಗೊಂಡು ಮನೆ ಒಳಕ್ಕೆ ಹೋಗಲು ಇಷ್ಟಪಡಲಿಲ್ಲ. ಆಗ ಅವನ ತಂದೆಯು ಹೊರಗೆ ಬಂದು ಅವನನ್ನು ಬೇಡಿಕೊಂಡನು.",
			"29": "<sup>29</sup> ಆದರೆ ಅವನು ತನ್ನ ತಂದೆಗೆ - ನೋಡು, ಇಷ್ಟು ವರುಷ ನಿನಗೆ ಸೇವೆ ಮಾಡಿದ್ದೇನೆ, ಮತ್ತು ನಾನು ನಿನ್ನ ಒಂದಪ್ಪಣೆಯನ್ನಾದರೂ ಎಂದೂ ಮಿಾರಲಿಲ್ಲ; ಆದರೂ ನಾನು ನನ್ನ ಸ್ನೇಹಿತರ ಸಂಗಡ ಉಲ್ಲಾಸಪಡುವದಕ್ಕಾಗಿ ನೀನು ಎಂದೂ ನನಗೆ ಒಂದು ಆಡನ್ನಾದರೂ ಕೊಡಲಿಲ್ಲ.",
			"30": "<sup>30</sup> ಆದರೆ ಸೂಳೆಯರನ್ನು ಕಟ್ಟಿಕೊಂಡು ನಿನ್ನ ಬದುಕನ್ನು ಹಾಳುಮಾಡಿದ ಈ ನಿನ್ನ ಮಗನು ಬಂದಾಗ ಕೊಬ್ಬಿಸಿದ ಕರುವನ್ನು ಇವನಿಗೆ ಕೊಯ್ಸಿದಿಯಲ್ಲಾ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"31": "<sup>31</sup> ಅದಕ್ಕೆ ತಂದೆಯು - ಕಂದಾ, ನೀನು ಯಾವಾಗಲೂ ನನ್ನ ಸಂಗಡ ಇದ್ದೀ, ಮತ್ತು ನನ್ನದೆಲ್ಲಾ ನಿನ್ನದೇ.",
			"32": "<sup>32</sup> ಆದರೆ ಉಲ್ಲಾಸಪಡುವದೂ ಸಂತೋಷಗೊಳ್ಳುವದೂ ನ್ಯಾಯವಾದದ್ದೇ; ಯಾಕೆಂದರೆ ಈ ನಿನ್ನ ತಮ್ಮ ಸತ್ತವನಾಗಿದ್ದನು, ತಿರಿಗಿ ಬದುಕಿ ಬಂದನು; ಪೋಲಿಹೋಗಿದ್ದನು, ಸಿಕ್ಕಿದನು ಎಂದು ಹೇಳಿದನು."
		},
		"16": {
			"1": "<sup>1</sup> ಯೇಸು ತನ್ನ ಶಿಷ್ಯರಿಗೂ ಹೇಳಿದ್ದೇನಂದರೆ - ಐಶ್ವರ್ಯವಂತನಾದ ಒಬ್ಬ ಮನುಷ್ಯನಿಗೆ ಒಬ್ಬ ಮನೆವಾರ್ತೆಯವನಿದ್ದನು. ಅವನ ಕುರಿತು ಯಜಮಾನನ ಬಳಿಯಲ್ಲಿ - ಇವನು ನಿನ್ನ ಆಸ್ತಿಯನ್ನು ಹಾಳುಮಾಡುತ್ತಾ ಇದ್ದಾನೆ ಎಂದು ಯಾರೋ ದೂರು ಹೇಳಿರಲಾಗಿ,",
			"2": "<sup>2</sup> ಯಜಮಾನನು ಅವನನ್ನು ಕರೆದು - ಇದೇನು ನಾನು ನಿನ್ನ ವಿಷಯದಲ್ಲಿ ಕೇಳುವ ಸಂಗತಿ? ನಿನ್ನ ಮನೆವಾರ್ತೆಯ ಲೆಕ್ಕವನ್ನು ಒಪ್ಪಿಸು; ನೀನು ಇನ್ನು ಮನೆವಾರ್ತೆಯವನಾಗಿರುವದಕ್ಕೆ ಯೋಗ್ಯನಲ್ಲ ಎಂದು ಹೇಳಿದನು.",
			"3": "<sup>3</sup> ಹೀಗಿರಲಾಗಿ ಆ ಮನೆವಾರ್ತೆಯವನು - ನಾನೇನು ಮಾಡಲಿ? ನನ್ನ ಯಜಮಾನನು ನನ್ನಿಂದ ಈ ಮನೆವಾರ್ತೆಕೆಲಸವನ್ನು ತೆಗೆದುಬಿಡುತ್ತಾನಲ್ಲಾ. ಅಗೆಯುವದಕ್ಕೆ ನನಗೆ ಬಲವಿಲ್ಲ; ಭಿಕ್ಷೆ ಬೇಡುವದಕ್ಕೆ ನನಗೆ ನಾಚಿಕೆಯಾಗುತ್ತದೆ.",
			"4": "<sup>4</sup> ಈ ಮನೆವಾರ್ತೆಕೆಲಸದಿಂದ ನನ್ನನ್ನು ತೆಗೆದ ಮೇಲೆ ಜನರು ನನ್ನನ್ನು ತಮ್ಮ ಮನೆಗಳೊಳಗೆ ಸೇರಿಸಿಕೊಳ್ಳುವಂತೆ ನಾನು ಮಾಡಬೇಕಾದದ್ದು ಗೊತ್ತಾಯಿತು ಎಂದು ತನ್ನೊಳಗೆ ಅಂದುಕೊಂಡು,",
			"5": "<sup>5</sup> ತನ್ನ ಯಜಮಾನನ ಸಾಲಗಾರರಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬನನ್ನೂ ಕರೆದು ಮೊದಲನೆಯವನನ್ನು - ನೀನು ನನ್ನ ಯಜಮಾನನಿಗೆ ಏನು ಕೊಡಬೇಕು ಎಂದು ಕೇಳಲು",
			"6": "<sup>6</sup> ಅವನು ನೂರು ಬುದ್ದಲಿ ಎಣ್ಣೆ ಎಂದಾಗ ಅವನಿಗೆ - ಈ ನಿನ್ನ ಪತ್ರವನ್ನು ತೆಗೆದುಕೊಂಡು ಬೇಗ ಐವತ್ತು ಬುದ್ದಲಿ ಎಂದು ಬರಿ ಎಂದು ಹೇಳಿದನು.",
			"7": "<sup>7</sup> ಬಳಿಕ ಮತ್ತೊಬ್ಬನನ್ನು - ನೀನು ಏನು ಕೊಡಬೇಕು ಎಂದು ಕೇಳಲು ಅವನು - ಒಂದು ನೂರು ಖಂಡಗ ಗೋಧಿ ಅಂದಾಗ ಅವನಿಗೆ - ಈ ನಿನ್ನ ಪತ್ರವನ್ನು ತೆಗೆದುಕೊಂಡು ಎಂಭತ್ತು ಖಂಡಗ ಎಂದು ಬರಿ ಎಂದು ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ಯಜಮಾನನು ಇದನ್ನು ಕೇಳಿ ಅನ್ಯಾಯಗಾರನಾದ ಆ ಮನೆವಾರ್ತೆಯವನನ್ನು ಕುರಿತು - ಇವನು ಜಾಣತನ ಮಾಡಿದನು ಎಂದು ಹೊಗಳಿದನು. ಯಾಕೆಂದರೆ ಈ ಲೋಕದ ಜನರು ತಮ್ಮ ವಿಷಯಗಳ ಪರಲೋಕ ರಾಜ್ಯದವರಿಗಿಂತಲೂ ಜಾಣರಾಗಿದ್ದಾರೆ.",
			"9": "<sup>9</sup> ಅನ್ಯಾಯದ ಧನದ ಮೂಲಕವಾಗಿ ನಿಮಗೆ ಸ್ನೇಹಿತರನ್ನು ಮಾಡಿಕೊಳ್ಳಿರಿ; ಹೀಗೆ ಮಾಡಿದರೆ ಅದು ನಿಮ್ಮ ಕೈಬಿಟ್ಟುಹೋದಾಗ ಅವರು ನಿಮ್ಮನ್ನು ಶಾಶ್ವತವಾದ ವಾಸಸ್ಥಾನಗಳಲ್ಲಿ ಸೇರಿಸಿಕೊಳ್ಳುವರು ಎಂದು ನಾನು ನಿಮಗೆ ಹೇಳುತ್ತೇನೆ.",
			"10": "<sup>10</sup> ಸ್ವಲ್ಪವಾದದ್ದರಲ್ಲಿ ನಂಬಿಗಸ್ತನಾದವನು ಬಹಳವಾದದ್ದರಲ್ಲಿಯೂ ನಂಬಿಗಸ್ತನಾಗುವನು. ಸಣ್ಣ ವಿಷಯಗಳಲ್ಲಿ ಅನ್ಯಾಯಗಾರನಾಗಿರುವವನು ಬಹಳವಾದದ್ದರಲ್ಲಿಯೂ ಅನ್ಯಾಯಗಾರನಾಗಿರುವನು.",
			"11": "<sup>11</sup> ಹೀಗಿರುವದರಿಂದ ಅನ್ಯಾಯದ ಧನದ ವಿಷಯದಲ್ಲಿ ನೀವು ನಂಬಿಗಸ್ತರಲ್ಲದವರಾದರೆ ನಿಜವಾದ ಧನವನ್ನು ನಿಮ್ಮ ವಶಕ್ಕೆ ಯಾರು ಕೊಟ್ಟಾರು?",
			"12": "<sup>12</sup> ಮತ್ತೊಬ್ಬನ ಸೊತ್ತಿನ ವಿಷಯದಲ್ಲಿ ನೀವು ನಂಬಿಗಸ್ತರಲ್ಲದವರಾದರೆ ನಿಮ್ಮದನ್ನು ನಿಮಗೆ ಯಾರು ಕೊಟ್ಟಾರು?",
			"13": "<sup>13</sup> ಯಾವ ಆಳಾದರೂ ಇಬ್ಬರು ಯಜಮಾನರಿಗೆ ಸೇವೆ ಮಾಡಲಾರನು; ಅವನು ಒಬ್ಬನನ್ನು ದ್ವೇಷಿಸಿ ಮತ್ತೊಬ್ಬನನ್ನು ಪ್ರೀತಿಸುವನು, ಇಲ್ಲವೆ ಒಬ್ಬನನ್ನು ಹೊಂದಿಕೊಂಡು ಮತ್ತೊಬ್ಬನನ್ನು ತಾತ್ಸಾರಮಾಡುವನು. ನೀವು ದೇವರನ್ನೂ ಧನವನ್ನೂ ಒಟ್ಟಿಗೆ ಸೇವಿಸಲಾರಿರಿ ಅಂದನು.",
			"14": "<sup>14</sup> ಫರಿಸಾಯರು ಹಣದಾಸೆಯುಳ್ಳವರಾಗಿದ್ದರಿಂದ ಈ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ಕೇಳಿ ಆತನನ್ನು ಹಾಸ್ಯ ಮಾಡಿದರು.",
			"15": "<sup>15</sup> ಆಗ ಆತನು ಅವರಿಗೆ ಹೇಳಿದ್ದೇನಂದರೆ - ಮನುಷ್ಯರ ಮುಂದೆ ನೀತಿವಂತರೆಂದು ತೋರಿಸಿಕೊಳ್ಳುವವರು ನೀವು; ಆದರೆ ದೇವರು ನಿಮ್ಮ ಹೃದಯಗಳನ್ನು ಬಲ್ಲವನಾಗಿದ್ದಾನೆ. ಮನುಷ್ಯರೆದುರು ಶ್ರೇಷ್ಠವೆನಿಸಿಕೊಳ್ಳುವಂಥದು ದೇವರ ದೃಷ್ಠಿಯಲ್ಲಿ ಅಸಹ್ಯವಾಗಿದೆ.",
			"16": "<sup>16</sup> ಧರ್ಮಶಾಸ್ತ್ರವೂ ಪ್ರವಾದಿಗಳೂ ಯೋಹಾನನ ತನಕವೇ. ಆ ನಂತರದ ದಿನಗಳಿಂದ ದೇವರ ರಾಜ್ಯವು ಸಾರಲ್ಪಡುತ್ತಲಿದೆ. ಅದರಲ್ಲಿ ಎಲ್ಲರೂ ಬಲಾತ್ಕಾರವಾಗಿ ನುಗ್ಗಲು ಯತ್ನಿಸುತ್ತಿದ್ದಾರೆ.",
			"17": "<sup>17</sup> ಧರ್ಮಶಾಸ್ತ್ರದೊಳಗಿನ ಒಂದು ಗುಡಸಾದರೂ ಬಿದ್ದು ಹೋಗುವದಕ್ಕಿಂತಲೂ ಆಕಾಶವೂ ಭೂಮಿಯೂ ಅಳಿದುಹೋಗುವದು ಸುಲಭ.",
			"18": "<sup>18</sup> ತನ್ನ ಹೆಂಡತಿಯನ್ನು ಬಿಟ್ಟು ಬೇರೊಬ್ಬಳನ್ನು ಮದುವೆಮಾಡಿಕೊಳ್ಳುವ ಪ್ರತಿಯೊಬ್ಬನು ವ್ಯಭಿಚಾರ ಮಾಡುವವನಾಗಿದ್ದಾನೆ. ಮತ್ತು ಗಂಡಬಿಟ್ಟವಳನ್ನು ಮದುವೆಮಾಡಿಕೊಳ್ಳುವವನು ವ್ಯಭಿಚಾರ ಮಾಡುವವನಾಗಿದ್ದಾನೆ.",
			"19": "<sup>19</sup> ಐಶ್ವರ್ಯವಂತನಾದ ಒಬ್ಬ ಮನುಷ್ಯನಿದ್ದನು. ಅವನು ಸಕಲ ವಿಧವಾದ ಐಶ್ವರ್ಯಗಳಿಂದ ಕೂಡಿದವನಾಗಿ ಬೆಲೆಬಾಳುವ ನೇರಳೆ ಬಣದ ರೇಶಿಮೆ ಮುಂತಾದ ವಸ್ತ್ರಗಳನ್ನು ಧರಿಸಿಕೊಂಡು ಪ್ರತಿದಿನವು ವೈಭವದೊಡನೆ ಸುಖವಾಗಿದ್ದು ಸಂತೋಷಪಡುತ್ತಿದ್ದನು.",
			"20": "<sup>20</sup> ಅವನ ಮನೇ ಬಾಗಲಿನಲ್ಲಿ ಲಾಜರನೆಂಬ ಒಬ್ಬ ಭಿಕ್ಷುಕನು ಬಿದ್ದು ಕೊಂಡಿದ್ದನು.",
			"21": "<sup>21</sup> ಇವನು ಮೈತುಂಬಾ ಹುಣ್ಣೆದ್ದವನು. ಐಶ್ವರ್ಯವಂತನ ಮೇಜಿನಿಂದ ಬಿದ್ದ ಎಂಜಲನ್ನವನ್ನು ತಿಂದು ಹಸಿವು ತೀರಿಸಿಕೊಳ್ಳಬೇಕೆಂದಿದ್ದನು; ಇಷ್ಟು ಮಾತ್ರವಲ್ಲದೆ ನಾಯಿಗಳು ಸಹ ಬಂದು ಅವನ ಹುಣ್ಣುಗಳನ್ನು ನೆಕ್ಕುತ್ತಿದ್ದವು.",
			"22": "<sup>22</sup> ಹೀಗಿರುವಲ್ಲಿ ಸ್ವಲ್ಪ ಕಾಲವಾದ ಮೇಲೆ ಆ ಭಿಕ್ಷುಕನು ಸತ್ತನು; ದೇವದೂತರು ಅವನನ್ನು ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ಅಬ್ರಹಾಮನ ಎದೆಗೆ ಒರಗಿಸಿದರು. ಆ ಐಶ್ವರ್ಯವಂತನು ಸಹ ಸತ್ತನು; ಅವನಿಗೆ ಉತ್ತರ ಕ್ರಿಯಾದಿಗಳು ನಡೆದವು.",
			"23": "<sup>23</sup> ಅವನು ಪಾತಾಳದೊಳಗೆ ಯಾತನೆಪಡುತ್ತಾ ಇರುವಲ್ಲಿ ಕಣ್ಣೆತ್ತಿ ದೂರದಿಂದ ಅಬ್ರಹಾಮನನ್ನೂ ಅವನ ಎದೆಗೆ ಒರಗಿಕೊಂಡಿದ್ದ ಲಾಜರನನ್ನೂ ನೋಡಿ -",
			"24": "<sup>24</sup> ತಂದೆಯೇ, ಅಬ್ರಹಾಮನೇ ನನ್ನ ಮೇಲೆ ಕರುಣೆ ಇಟ್ಟು ಲಾಜರನನ್ನು ಕಳುಹಿಸು ಅವನು ತನ್ನ ತುದಿ ಬೆರಳನ್ನು ನೀರಿನಲ್ಲಿ ಅದ್ದಿ ನನ್ನ ನಾಲಿಗೆಯನ್ನು ತಣ್ಣಗೆ ಮಾಡಲಿ; ಈ ಉರಿಯಲ್ಲಿ ಸಂಕಟಪಡುತ್ತಿದ್ದೇನೆ. ಎಂದು ಕೂಗಿ ಹೇಳಿದನು.",
			"25": "<sup>25</sup> ಆದರೆ ಅಬ್ರಾಹಾಮನು - ಕಂದಾ ನೀನು ಆಶಿಸಿದ ಸುಖವನ್ನು ನಿನ್ನ ಜೀವಮಾನದಲ್ಲಿ ಹೊಂದಿದ್ದಿ; ಹಾಗೆಯೇ ಲಾಜರನು ಕಷ್ಟವನ್ನು ಹೊಂದಿದನು ಎಂಬದನ್ನು ನೆನಪಿಗೆ ತಂದುಕೋ. ಈಗಲಾದರೋ ಇಲ್ಲಿ ಇವನಿಗೆ ಸಮಾಧಾನ, ಆದರೆ ನಿನಗೆ ಸಂಕಟ.",
			"26": "<sup>26</sup> ಇದು ಮಾತ್ರವಲ್ಲದೆ ನಮಗೂ ನಿಮಗೂ ನಡುವೆ ದೊಡ್ಡದೊಂದು ಆಳವಾದ ಕಂದಕ ಇದೆ. ಆದಕಾರಣ ಈ ಕಡೆಯಿಂದ ನಿಮ್ಮ ಬಳಿಗೆ ಹೋಗಬೇಕೆಂದಿರುವವರು ಹೋಗಲಾರರು. ಮತ್ತು ಆ ಕಡೆಯಿಂದ ನಮ್ಮ ಬಳಿಗೆ ಯಾರಿಂದಲೂ ಬರಲಿಕ್ಕಾಗದು ಅಂದನು.",
			"27": "<sup>27</sup> ಆಗ ಅವನು - ಅಪ್ಪಾ, ಹಾಗಾದರೆ ಲಾಜರನನ್ನು ನನ್ನ ತಂದೆಯ ಮನೆಗೆ ಕಳುಹಿಸಬೇಕೆಂದು ನಿನ್ನನ್ನು ಬೇಡಿಕೊಳ್ಳುತ್ತೇನೆ.",
			"28": "<sup>28</sup> ನನಗೆ ಐದು ಮಂದಿ ಅಣ್ಣತಮ್ಮಂದಿರಿದ್ದಾರೆ. ಅವರು ಸಹ ಈ ಯಾತನೆಯ ಸ್ಥಳಕ್ಕೆ ಬಂದಾರು. ಬಾರದಂತೆ ಅವನು ತಾನು ಕಂಡದ್ದನ್ನು ಅವರಿಗೆ ಚೆನ್ನಾಗಿ ತಿಳಿಸಿ ಹೇಳಲಿ ಅಂದನು.",
			"29": "<sup>29</sup> ಆದಕ್ಕೆ ಅಬ್ರಹಾಮನು - ಮೋಶೆಯ ಧರ್ಮಶಾಸ್ತ್ರವೂ ಪ್ರವಾದಿಗಳ ಗ್ರಂಥಗಳೂ ಅವರಲ್ಲಿ ಅವೆ; ಅವುಗಳನ್ನು ಕೇಳಲಿ ಅನ್ನಲು -",
			"30": "<sup>30</sup> ಅವನು ತಂದೆಯೇ ಅಬ್ರಹಾಮನೇ ಹಾಗಲ್ಲ. ಸತ್ತವರ ಕಡೆಯಿಂದ ಒಬ್ಬನು ಅವರ ಬಳಿಗೆ ಹೋದರೆ ಅವರು ದೇವರ ಕಡೆಗೆ ತಿರುಗಿಕೊಳ್ಳುವರು ಅಂದನು.",
			"31": "<sup>31</sup> ಅಬ್ರಹಾಮನು ಅವನಿಗೆ - ಅವರು ಮೋಶೆಯ ಮಾತನ್ನೂ ಪ್ರವಾಧಿಗಳ ಮಾತನ್ನೂ ಕೇಳದಿದ್ದರೆ ಸತ್ತುಹೋಗಿದ್ದವನೊಬ್ಬನು ಜೀವಿತನಾಗಿ ಎದ್ದರೂ ಅವರು ಒಪ್ಪುವದಿಲ್ಲ ಎಂದು ಹೇಳಿದನು."
		},
		"17": {
			"1": "<sup>1</sup> ಯೇಸು ತನ್ನ ಶಿಷ್ಯರಿಗೆ - ಅಡ್ಡಿ ಆತಂಕಗಳು ಬಾರದೆ ಇರಲಾರವು; ಆದರೂ ಅವು ಯಾವನಿಂದ ಬರುತ್ತವೆಯೋ ಅವನ ಗತಿಯನ್ನು ಏನು ಹೇಳಲಿ?",
			"2": "<sup>2</sup> ಅವನು ಈ ಚಿಕ್ಕವರಲ್ಲಿ ಒಬ್ಬನಿಗೆ ತೊಂದರೆಗಳನ್ನು ಓಡ್ಡುವದಕ್ಕಿಂತ ತನ್ನ ಕುತ್ತಿಗೆಗೆ ಬೀಸುವ ಕಲ್ಲನ್ನು ಕಟ್ಟಿಸಿಕೊಂಡು ಸಮುದ್ರದಲ್ಲಿ ಹಾಕಿಸಿಕೊಳ್ಳುವದೇ ಲೇಸು.",
			"3": "<sup>3</sup> ನಿಮ್ಮ ವಿಷಯದಲ್ಲಿ ಎಚ್ಚರಿಕೆಯಾಗಿರಿ. ನಿನ್ನ ಸಹೋದರನು ತಪ್ಪುಮಾಡಿದರೆ ಅವನನ್ನು ಗದರಿಸು; ಅವನು ಪಶ್ಚಾತ್ತಾಪಪಟ್ಟರೆ ಅವನ ತಪ್ಪನ್ನು ಕ್ಷಮಿಸಿಬಿಡು.",
			"4": "<sup>4</sup> ಅವನು ದಿನಕ್ಕೆ ಏಳು ಸಾರಿ ನಿನಗೆ ತಪ್ಪುಮಾಡಿ ಏಳು ಸಾರಿಯೂ ನಿನ್ನ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡು, ‘ನಾನು ಪಶ್ಚಾತ್ತಾಪದುತ್ತೇನೆ, ನನ್ನನ್ನು ಕ್ಷಮಿಸು’ ಎಂದು ಕೇಳಿಕೊಂಡರೆ ಅವನನ್ನು ಕ್ಷಮಿಸು ಎಂದು ಹೇಳಿದನು.",
			"5": "<sup>5</sup> ಅಪೊಸ್ತಲರು ಸ್ವಾಮಿಗೆ - ನಮ್ಮ ನಂಬಿಕೆಯನ್ನು ಹೆಚ್ಚಿಸು ಎಂದು ಹೇಳಲು",
			"6": "<sup>6</sup> ಸ್ವಾಮಿಯು - ಸಾಸಿವೇ ಕಾಳಷ್ಟು ನಂಬಿಕೆ ನಿಮಗಿರುವುದಾದರೆ ನೀವು ಈ ಅಂಜೂರದಮರಕ್ಕೆ- ನೀನು ಬೇರುಸಹಿತ ಕಿತ್ತುಕೊಂಡುಹೋಗಿ ಸಮುದ್ರದಲ್ಲಿ ನಾಟಿಕೊ ಎಂದು ಹೇಳಿದರೂ ಅದು ನಿಮ್ಮ ಮಾತನ್ನು ಕೇಳುವದು ಅಂದನು.",
			"7": "<sup>7</sup> ನಿಮ್ಮಲ್ಲಿ ಯಾವನಿಗಾದರೂ ವ್ಯವಸಾಯ ಮಾಡುವ ಅಥವಾ ಕುರಿಮೇಯಿಸುವ ಆಳಿರಲಾಗಿ ಅವನು ಹೊಲದಿಂದ ಮನೆಗೆ ಬಂದ ಆ ಆಳಿಗೆ - ನೀನು ತಟ್ಟನೆ ಬಂದು ಊಟಕ್ಕೆ ಕೂತುಕೋ ಎಂದು ಹೇಳುವನೇ?",
			"8": "<sup>8</sup> ಹಾಗೆ ಹೇಳದೆ - ನೀನು ನನ್ನ ಊಟಕ್ಕೇನಾದರೂ ಸಿದ್ಧಮಾಡು, ನಾನು ಊಟಮಾಡುವ ತನಕ ನಡುಕಟ್ಟಿಕೊಂಡು ನನಗೆ ಸೇವೆಮಾಡು; ಆ ಮೇಲೆ ನೀನು ಊಟಮಾಡು, ಕೂಡಿ ಎಂದು ಹೇಳುವನಲ್ಲವೇ.",
			"9": "<sup>9</sup> ತನ್ನ ಅಪ್ಪಣೆಯಂತೆ ನಡೆದ ಆ ಆಳಿಗೆ - ನಿನ್ನಿಂದ ಉಪಕಾರವಾಯಿತೆಂದು ಅವನಿಗೆ ಹೇಳುವನೇ? ಇದರಂತೆಯೇ ನಿಮ್ಮ ಸಂಗತಿ.",
			"10": "<sup>10</sup> ನೀವು ನಿಮಗೆ ಅಪ್ಪಣೆಯಾಗಿರುವದನ್ನೆಲ್ಲಾ ಮಾಡಿದ ಮೇಲೆ ನಾವು ಆಳುಗಳು, ಪ್ರಯೋಜನವಿಲ್ಲದವರು, ಮಾಡಬೇಕಾದದ್ದನ್ನೇ ಮಾಡಿದ್ದೇವೆ ಅನ್ನಿರಿ ಎಂದು ಹೇಳಿದನು.",
			"11": "<sup>11</sup> ಯೇಸು ಯೆರೂಸಲೇಮಿಗೆ ಪ್ರಯಾಣಮಾಡುವಾಗ ಸಮಾರ್ಯ, ಗಲಿಲಾಯ ಸೀಮೆಗಳ ನಡುವೆ ಹಾದುಹೋದನು.",
			"12": "<sup>12</sup> ಒಂದು ಹಳ್ಳಿಗೆ ಬಂದಾಗ ಹತ್ತುಮಂದಿ ಕುಷ್ಠರೋಗಿಗಳು ಆತನ ಎದುರಿಗೆ ಬಂದು ದೂರದಲ್ಲಿ ನಿಂತು ಯೇಸುವೇ,",
			"13": "<sup>13</sup> ಗುರುವೇ, ನಮ್ಮ ಮೇಲೆ ದಯವಿಡು ಎಂದು ಕೂಗಿಹೇಳಿದರು.",
			"14": "<sup>14</sup> ಆತನು ಅವರನ್ನು ನೋಡಿ - ನೀವು ಹೋಗಿ ಯಾಜಕರಿಗೆ ನಿಮ್ಮ ಮೈ ತೋರಿಸಿಕೊಳ್ಳಿರಿ ಎಂದು ಹೇಳಿದನು. ಅವರು ಹೋಗುತ್ತಿರುವಾಗಲೇ ಶುದ್ಧರಾದರು.",
			"15": "<sup>15</sup> ಅವರಲ್ಲಿ ಒಬ್ಬನು ತನಗೆ ಗುಣವಾದದ್ದನ್ನು ನೋಡಿ ಮಹಾಶಬ್ದದಿಂದ ದೇವರನ್ನು ಕೊಂಡಾಡುತ್ತಾ ಹಿಂತಿರುಗಿ ಬಂದು ಯೇಸುವಿನ ಪಾದಗಳಿಗೆ ಅಡ್ಡಬಿದ್ದು",
			"16": "<sup>16</sup> ಆತನಿಗೆ ಉಪಕಾರಸ್ತುತಿಮಾಡಿ ಕೃತಜ್ಞೆತೆಯನ್ನು ಸೂಚಿಸಿದನು. ಅವನು ಸಮಾರ್ಯದವನು.",
			"17": "<sup>17</sup> ಯೇಸು ಇದನ್ನು ನೋಡಿ - ಹತ್ತು ಮಂದಿ ಶುದ್ಧರಾದರಲ್ಲವೇ. ಮಿಕ್ಕ ಒಂಭತ್ತು ಮಂದಿ ಎಲ್ಲಿ?",
			"18": "<sup>18</sup> ದೇವರನ್ನು ಸ್ತುತಿಸುವದಕ್ಕೆ ಈ ಅನ್ಯದೇಶದವನೇ ಹೊರತು ಇನ್ನಾರೂ ಹಿಂತಿರುಗಿ ಬರಲಿಲ್ಲವೇ ಎಂದು ಹೇಳಿ ಅವನಿಗೆ ಎದ್ದು ಹೋಗು,",
			"19": "<sup>19</sup> ನಿನ್ನ ನಂಬಿಕೆಯು ನಿನ್ನನ್ನು ಸ್ವಸ್ಥಮಾಡಿಯದೆ ಎಂದು ಹೇಳಿದನು.",
			"20": "<sup>20</sup> ದೇವರ ರಾಜ್ಯವು ಯಾವಾಗ ಬರುವದೆಂದು ಫರಿಸಾಯರು ಆತನನ್ನು ಕೇಳಿದಾಗ ಆತನು ಅವರಿಗೆ - ದೇವರ ರಾಜ್ಯವು ಸೂಚನೆಗಳನ್ನು ಗಮನಿಸುತ್ತಾ ಕಾಯುತ್ತಿರುವಾಗ ಪ್ರತ್ಯಕ್ಷವಾಗಿ ಬರುವಂಥದಲ್ಲ.",
			"21": "<sup>21</sup> ಇಗೋ ಇಲ್ಲಿ ಇದೆ, ಅಗೋ ಅಲ್ಲಿ ಅದೆ ಎಂದು ಹೇಳುವದಕ್ಕಾಗದು; ದೇವರ ರಾಜ್ಯವು ನಿಮ್ಮಲ್ಲಿಯೇ ಅದೆ ಎಂದು ತಿಳಿದುಕೊಳ್ಳಿರಿ ಎಂದು ಹೇಳಿದನು.",
			"22": "<sup>22</sup> ಮತ್ತು ಆತನು ತನ್ನ ಶಿಷ್ಯರಿಗೆ ಹೇಳಿದ್ದೇನಂದರೆ - ಒಂದು ಕಾಲ ಬರುತ್ತದೆ, ಆ ಕಾಲದಲ್ಲಿ ನೀವು ಮನುಷ್ಯಕುಮಾರನ ದಿವಸಗಳೊಳಗೆ ಒಂದು ದಿನವನ್ನು ನೋಡಬೇಕೆಂದು ಅಪೇಕ್ಷಿಸಿದರೂ ಅದನ್ನು ನೋಡದೆ ಇರುವಿರಿ.",
			"23": "<sup>23</sup> ಜನರು ನಿಮಗೆ - ಅಗೋ ಅಲ್ಲಿದ್ದಾನೆ, ಇಗೋ ಇಲ್ಲಿದ್ದಾನೆ ಎಂದು ಹೇಳಿದರೆ ನೀವು ಅವರು ಹೇಳುವಲ್ಲಿಗೆ ಹೊರಟುಹೋಗಬೇಡಿರಿ, ಅವರನ್ನು ಹಿಂಬಾಲಿಸಲೂ ಬೇಡಿರಿ.",
			"24": "<sup>24</sup> ಯಾಕಂದರೆ ಮಿಂಚು ಮಿಂಚುತ್ತಾ ಆಕಾಶದಲ್ಲಿ ಒಂದು ಕಡೆಯಿಂದ ಮತ್ತೊಂದು ಕಡೆಯವರೆಗೆ ಹೇಗೆ ಹೊಳೆಯುವದೋ ಹಾಗೆಯೇ ಮನುಷ್ಯಕುಮಾರನು ತನ್ನ ದಿನದಲ್ಲಿರುವನು.",
			"25": "<sup>25</sup> ಆದರೆ ಮೊದಲು ಆತನು ಬಹು ಕಷ್ಟಗಳನ್ನನುಭವಿಸಿ ಈಗಿನ ಕಾಲದ ಜನರಿಂದ ತಿರಸ್ಕರಿಸಲ್ಪಡಬೇಕು.",
			"26": "<sup>26</sup> ನೋಹನ ದಿವಸಗಳಲ್ಲಿ ನಡೆದ ಹಾಗೆಯೇ ಮನುಷ್ಯಕುಮಾರನ ದಿವಸಗಳಲ್ಲಿಯೂ ನಡೆಯುವದು.",
			"27": "<sup>27</sup> ನೋಹನು ನಾವೆಯಲ್ಲಿ ಸೇರಿದ ದಿನದ ತನಕ ಜನರು ಊಟಮಾಡುತ್ತಿದ್ದರು, ಕುಡಿಯುತ್ತಿದ್ದರು, ಮದುವೆಮಾಡಿಕೊಳ್ಳುತ್ತಿದ್ದರು, ಮದುವೆಮಾಡಿಕೊಡುತ್ತಿದ್ದರು. ಆಗ ಜಲಪ್ರಳಯವು ಬಂದು ಎಲ್ಲರನ್ನು ನಾಶಮಾಡಿತು.",
			"28": "<sup>28</sup> ಮತ್ತು ಲೋಟನ ದಿವಸಗಳಲ್ಲಿ ನಡೆದ ಪ್ರಕಾರ ನಡೆಯುವುದು. ಅವರು ಊಟಮಾಡುತ್ತಿದ್ದರು, ಕುಡಿಯುತ್ತಿದ್ದರು, ಕೊಂಡುಕೊಳ್ಳುತ್ತಿದ್ದರು, ಮಾರಾಟಮಾಡುತ್ತಿದ್ದರು, ನೆಡುತ್ತಿದ್ದರು ಮತ್ತು ಕಟ್ಟುತ್ತಿದ್ದರು.",
			"29": "<sup>29</sup> ಆದರೆ ಲೋಟನು ಸೊದೊಮ್ ಊರನ್ನು ಬಿಟ್ಟುಹೋದ ದಿವಸದಲ್ಲಿ ಆಕಾಶದಿಂದ ಬೆಂಕಿ ಗಂಧಕಗಳು ಸುರಿದು ಅವರೆಲ್ಲರನ್ನು ನಾಶಮಾಡಿದವು.",
			"30": "<sup>30</sup> ಮನುಷ್ಯಕುಮಾರನು ಪ್ರತ್ಯಕ್ಷವಾಗುವ ದಿವಸದಲ್ಲಿ ಅದೇ ರೀತಿಯಾಗಿ ಇರುವುದು.",
			"31": "<sup>31</sup> ಆ ದಿನದಲ್ಲಿ ಮಾಳಿಗೆಯ ಮೇಲೆ ಇರುವವನು ಮನೆಯೊಳಗಿರುವ ತನ್ನ ವಸ್ತುಗಳನ್ನು ತೆಗೆದುಕೊಳ್ಳುವದಕ್ಕೆ ಇಳಿದು ಬರಬಾರದು; ಅದೇ ಪ್ರಕಾರ ಹೊಲದಲ್ಲಿರುವವನು ಸಹ ತಿರಿಗಿ ಹಿಂದಕ್ಕೆ ಹೋಗಬಾರದು.",
			"32": "<sup>32</sup> ಲೋಟನ ಹೆಂಡತಿಯನ್ನು ನೆನಪಿಗೆ ತಂದುಕೊಳ್ಳಿರಿ.",
			"33": "<sup>33</sup> ತನ್ನ ಪ್ರಾಣವನ್ನು ಉಳಿಸಿ ಕೊಳ್ಳಬೇಕೆಂದಿರುವವನು ಅದನ್ನು ಕಳದುಕೊಳ್ಳುವನು; ಪ್ರಾಣವನ್ನು ಕಳದುಕೊಂಡವನು ಅದನ್ನು ರಕ್ಷಿಸಿಕೊಳ್ಳುವನು.",
			"34": "<sup>34</sup> ಆ ರಾತ್ರಿಯಲ್ಲಿ ಇಬ್ಬರು ಒಂದೇ ಹಾಸಿಗೆಯಲ್ಲಿ ಇರುವರು; ಒಬ್ಬನು ತೆಗೆದುಕೊಳ್ಳಲ್ಪಡುವನು, ಮತ್ತೊಬ್ಬನು ಕೈ ಬಿಡಲ್ಪಡುವನು.",
			"35": "<sup>35</sup> ಇಬ್ಬರು ಹೆಂಗಸರು ಒಂದೇ ಕಲ್ಲಲ್ಲಿ ಬೀಸುತ್ತಿರುವರು; ಒಬ್ಬಳು ತೆಗೆದುಕೊಳ್ಳಲ್ಪಡುವಳು, ಮತ್ತೊಬ್ಬಳು ಬಿಡಲ್ಪಡುವಳು ಅಂದನು.",
			"37": "<sup>37</sup> ಈ ಮಾತಿಗೆ ಶಿಷ್ಯರು - ಕರ್ತನೇ, ಅದು ಎಲ್ಲಿ ಆಗುವದು ಎಂದು ಕೇಳಲು ಆತನು ಅವರಿಗೆ - ಹೆಣ ಎಲ್ಲಿಯೋ ಅಲ್ಲಿಯೇ ಹದ್ದುಗಳು ಬಂದು ಸೇರುವವು ಎಂದು ಉತ್ತರಕೊಟ್ಟನು."
		},
		"18": {
			"1": "<sup>1</sup> ಬೇಸರಗೊಳ್ಳದೆ ಯಾವಾಗಲೂ ಪ್ರಾರ್ಥನೆ ಮಾಡುತ್ತಿರಬೇಕೆಂಬುದಕ್ಕೆ ಆತನು ಅವರಿಗೆ ಒಂದು ಸಾಮ್ಯನ್ನು ಹೇಳಿದನು.",
			"2": "<sup>2</sup> ಅದೇನಂದರೆ - ಒಂದು ಊರಿನಲ್ಲಿ ಒಬ್ಬ ನ್ಯಾಯಾಧಿಪತಿ ಇದ್ದನು; ಅವನು ದೇವರಿಗೆ ಹೆದರದೆ ಮನುಷ್ಯರನ್ನು ಲಕ್ಷ್ಯಮಾಡದೆ ಇದ್ದವನು.",
			"3": "<sup>3</sup> ಅದೇ ಊರಿನಲ್ಲಿ ಒಬ್ಬ ವಿಧವೆ ಇದ್ದಳು. ಆಕೆಯು ಅವನ ಬಳಿಗೆ ಬಂದು - ನ್ಯಾಯವಿಚಾರಣೆಮಾಡಿ ನನ್ನ ವಿರೋಧಿಯಿಂದ ನನ್ನನ್ನು ಬಿಡಿಸು ಎಂದು ಕೇಳಿಕೊಳ್ಳುತ್ತಿದ್ದಳು.",
			"4": "<sup>4</sup> ಅವನು ಸ್ವಲ್ಪ ಕಾಲ ಮನಸ್ಸುಕೊಡಲಿಲ್ಲ. ಆ ಮೇಲೆ ಅವನು-ನಾನು ದೇವರಿಗೆ ಹೆದರುವವನಲ್ಲ, ಮನುಷ್ಯರನ್ನು ಲಕ್ಷ್ಯ ಮಾಡುವವನಲ್ಲ;",
			"5": "<sup>5</sup> ಆದರೂ ಈ ವಿಧವೆ ನನ್ನನ್ನು ಕಾಡುತ್ತಾ ಬರುತ್ತಿರುವುದರಿಂದ ಈಕೆಯ ನ್ಯಾಯವನ್ನು ವಿಚಾರಿಸಿ ತೀರ್ಪುಮಾಡುವೆನು; ಇಲ್ಲವಾದರೆ ಈಕೆ ಪದೇಪದೇ ಬಂದು ನನ್ನ ತಲೆ ಕೆಡಿಸಿಬಿಟ್ಟಾಳು, ಎಂದು ತನ್ನೊಳಗೆ ಅಂದುಕೊಂಡನು.",
			"6": "<sup>6</sup> ಸ್ವಾಮಿಯು ಈ ಸಾಮ್ಯವನ್ನು ಹೇಳಿದ ಮೇಲೆ - ಅನ್ಯಾಯಗಾರನಾದ ಈ ನ್ಯಾಯಾಧಿಪತಿ ಅಂದುಕೊಂಡದ್ದನ್ನು ಆಲೋಚಿಸಿಕೊಳ್ಳಿರಿ.",
			"7": "<sup>7</sup> ದೇವರಾದುಕೊಂಡವರು ಆತನಿಗೆ ಹಗಲುರಾತ್ರಿ ಮೊರೆಯಿಡುವಲ್ಲಿ ಆತನು ಅವರ ವಿಷಯದಲ್ಲಿ ತಡಮಾಡಿದರೂ ಅವರ ನ್ಯಾಯವನ್ನು ತೀರಿಸದೆ ಇರುವನೇ? ಅವರಿಗೆ ಬೇಗ ನ್ಯಾಯವನ್ನು ತೀರಿಸದೆ ಇರುವನೇ? ಅವರಿಗೆ ಬೇಗ ನ್ಯಾಯತೀರಿಸುವನೆಂದು ನಿಮಗೆ ಹೇಳುತ್ತೇನೆ.",
			"8": "<sup>8</sup> ಹೀಗಿದ್ದರೂ ಮನುಷ್ಯಕುಮಾರನು ಬರುವಾಗ ಭೂಮಿಯ ಮೇಲೆ ನಂಬಿಕೆಯನ್ನು ಕಾಣುಲು ಸಾಧ್ಯವೋ? ಅಂದನು.",
			"9": "<sup>9</sup> ಇದಲ್ಲದೇ ತಾವೇ ನೀತಿವಂತರೆಂದು ತಮ್ಮಲ್ಲಿ ಭರವಸವಿಟ್ಟುಕೊಂಡು ಉಳಿದವರನ್ನು ತಿರಸ್ಕಾರಮಾಡುವಂತಹ ಕೆಲವರಿಗೆ ಒಂದು ಸಾಮ್ಯವನ್ನು ಹೇಳಿದನು.",
			"10": "<sup>10</sup> ಅದೇನಂದರೆ - ಪ್ರಾರ್ಥನೆಮಾಡಬೇಕೆಂದು ಇಬ್ಬರು ಮನುಷ್ಯರು ದೇವಾಲಯಕ್ಕೆ ಹೋದರು; ಒಬ್ಬನು ಫರಿಸಾಯನು, ಒಬ್ಬನು ಸುಂಕದವನು.",
			"11": "<sup>11</sup> ಫರಿಸಾಯನು ನಿಂತುಕೊಂಡು ಪ್ರಾರ್ಥಿಸುವಾಗ ತನ್ನೊಳಗೆ - ದೇವರೇ, ದರೋಡೆಕೋರರು, ಅನ್ಯಾಯಗಾರರೂ, ಹಾದರಮಾಡುವವರೂ ಆಗಿರುವ ಉಳಿದ ಜನರಂತೆ ನಾನಲ್ಲ, ಈ ಸುಂಕದವನಂತೆಯೂ ಅಲ್ಲ; ಆದದರಿಂದ ನಿನಗೆ ಸ್ತೋತ್ರಮಾಡುತ್ತೇನೆ.",
			"12": "<sup>12</sup> ವಾರಕ್ಕೆ ಎರಡಾವರ್ತಿ ಉಪವಾಸಮಾಡುತ್ತೇನೆ; ನಾನು ಸಂಪಾದಿಸಿದ ಎಲ್ಲಾದರಲ್ಲಿಯೂ ಹತ್ತರಲ್ಲೊಂದು ಪಾಲು ಕೊಡುತ್ತೇನೆ ಎಂದು ಹೇಳಿಕೊಂಡು ಪ್ರಾರ್ಥಿಸಿದನು.",
			"13": "<sup>13</sup> ಆದರೆ ಆ ಸುಂಕದವನು ದೂರದಲ್ಲಿ ನಿಂತು ಆಕಾಶದ ಕಡೆಗೆ ಕಣ್ಣೆತ್ತಿ ನೋಡುವದಕ್ಕೂ ಮನಸ್ಸಿಲ್ಲದೆ ಎದೆಯನ್ನು ಬಡಿದುಕೊಳ್ಳುತ್ತಾ - ದೇವರೇ, ಪಾಪಿಯಾದ ನನ್ನನ್ನು ಕರುಣಿಸು ಅಂದನು.",
			"14": "<sup>14</sup> ಇವನು ನೀತಿವಂತನೆಂದು ನಿರ್ಣಯಿಸಲ್ಪಟ್ಟವನಾಗಿ ತನ್ನ ಮನೆಗೆ ಹೋದನು, ಆ ಫರಿಸಾಯನು ಅಂಥವನಾಗಿ ಹೋಗಲಿಲ್ಲ ಎಂದು ನಿಮಗೆ ಹೇಳುತ್ತೇನೆ. ತನ್ನನ್ನು ಹೆಚ್ಚಿಸಿಕೊಳ್ಳುವ ಪ್ರತಿಯೊಬ್ಬನು ತಗ್ಗಿಸಲ್ಪಡುವನು; ತನ್ನನ್ನು ತಗ್ಗಿಸಿಕೊಳ್ಳುವವನು ಹೆಚ್ಚಿಸಲ್ಪಡುವನು ಅಂದನು.",
			"15": "<sup>15</sup> ಕೆಲವರು ತಮ್ಮ ಕೂಸುಗಳನ್ನು ಯೇಸುವಿನಿಂದ ಮುಟ್ಟಿಸಬೇಕೆಂದು ಆತನ ಬಳಿಗೆ ತರಲು ಶಿಷ್ಯರು ಕಂಡು ಅವರನ್ನು ಗದರಿಸಿದರು.",
			"16": "<sup>16</sup> ಆದರೆ ಯೇಸು ಆ ಕೂಸುಗಳನ್ನು ತನ್ನ ಬಳಿಗೆ ಕರತರಿಸಿ-ಮಕ್ಕಳನ್ನು ನನ್ನ ಹತ್ತಿರಕ್ಕೆ ಬರಗೊಡಿಸಿರಿ; ಅವುಗಳಿಗೆ ಅಡ್ಡಿಮಾಡಬೇಡಿರಿ; ದೇವರ ರಾಜ್ಯವು ಇಂಥವರದೇ.",
			"17": "<sup>17</sup> ನಿಮಗೆ ಸತ್ಯವಾಗಿ ಹೇಳುತ್ತೇನೆ, ಯಾರು ಶಿಶುಭಾವದಿಂದ ದೇವರ ರಾಜ್ಯವನ್ನು ಅಂಗೀಕರಿಸುವುದಿಲ್ಲವೋ ಅವನು ಅದರಲ್ಲಿ ಸೇರುವದೇ ಇಲ್ಲ ಅಂದನು.",
			"18": "<sup>18</sup> ಒಬ್ಬ ಅಧಿಕಾರಿಯು ಬಂದು - ಒಳ್ಳೇ ಬೋಧಕನೇ, ನಾನು ನಿತ್ಯಜೀವಕ್ಕೆ ಬಾಧ್ಯನಾಗುವಂತೆ ಏನು ಮಾಡಬೇಕು ಎಂದು ಆತನನ್ನು ಕೇಳಲಾಗಿ",
			"19": "<sup>19</sup> ಯೇಸು ಅವನಿಗೆ - ನನ್ನನ್ನು ಒಳ್ಳೆಯವನೆಂದು ಯಾಕೆ ಹೇಳುತ್ತೀ? ದೇವರೊಬ್ಬನೇ ಹೊರತು ಮತ್ತಾವನೂ ಒಳ್ಳೆಯವನಲ್ಲ.",
			"20": "<sup>20</sup> ವ್ಯಭಿಚಾರ ಮಾಡಬಾರದು; ನರಹತ್ಯ ಮಾಡಬಾರದು; ಕದಿಯ ಬಾರದು; ಸುಳ್ಳುಸಾಕ್ಷಿ ಹೇಳಬಾರದು; ನಿನ್ನ ತಂದೆತಾಯಿಗಳನ್ನು ಗೌರವಿಸಬೇಕು ಎಂಬ ದೇವರಾಜ್ಞೆಗಳು ನಿನಗೆ ಗೊತ್ತಿವೆಯಷ್ಟೆ, ಎಂದು ಹೇಳಿದನು.",
			"21": "<sup>21</sup> ಅದಕ್ಕವನು - ನಾನು ಚಿಕ್ಕಂದಿನಿಂದಲೂ ಇವೆಲ್ಲಕ್ಕೂ ಸರಿಯಾಗಿ ನಡಕೊಂಡು ಬಂದಿದ್ದೇನೆ ಎಂದು ಹೇಳಲು",
			"22": "<sup>22</sup> ಯೇಸು ಅದನ್ನು ಕೇಳಿ ಅವನಿಗೆ - ಇನ್ನೂ ನಿನಗೆ ಒಂದು ಕೊರತೆಯಿದೆ; ನಿನಗಿರುವದೆಲ್ಲ ಮಾರಿ ಬಡವರಿಗೆ ಹಂಚಿಕೊಡು; ಪರಲೋಕದಲ್ಲಿ ನಿನಗೆ ಸಂಪತ್ತುಂಟಾಗುವುದು; ನೀನು ಬಂದು ನನ್ನನ್ನು ಹಿಂಬಾಲಿಸು ಎಂದು ಹೇಳಿದನು.",
			"23": "<sup>23</sup> ಅವನು ಬಹಳ ಐಶ್ವರ್ಯವಂತನಾಗಿದ್ದದರಿಂದ ಇದನ್ನು ಕೇಳಿ ಬಹಳ ದುಃಖಪಟ್ಟನು.",
			"24": "<sup>24</sup> ಆಗ ಯೇಸು ಅವನನ್ನು ನೋಡಿ - ಹಣವಂತರು ದೇವರ ರಾಜ್ಯದಲ್ಲಿ ಸೇರುವದು ಎಷ್ಟೋ ಕಷ್ಟ.",
			"25": "<sup>25</sup> ಐಶ್ವರ್ಯವಂತನು ದೇವರ ರಾಜ್ಯದಲ್ಲಿ ಸೇರುವುದಕ್ಕಿಂತ ಒಂಟೆಯು ಸೂಜಿಯಕಣ್ಣಿನಲ್ಲಿ ನುಗ್ಗುವುದು ಸುಲಭ ಅಂದನು.",
			"26": "<sup>26</sup> ಈ ಮಾತನ್ನು ಕೇಳಿದವರು - (ಹೀಗಿದ್ದರೆ) ಯಾರಿಗೆ ರಕ್ಷಣೆಯಾದೀತು ಎಂದು ಕೇಳಲು",
			"27": "<sup>27</sup> ಆತನು - ಮನುಷ್ಯರಿಗೆ ಅಸಾಧ್ಯವಾದ ಕಾರ್ಯಗಳು ದೇವರಿಗೆ ಸಾಧ್ಯವಾಗಿವೆ ಅಂದನು.",
			"28": "<sup>28</sup> ಆಗ ಪೇತ್ರನು - ಇಗೋ, ನಾವು ನಮ್ಮ ನಮ್ಮ ಮನೆ ಮಾರುಗಳನ್ನು ಬಿಟ್ಟು ನಿನ್ನ ಹಿಂದೆ ಬಂದೆವು ಎಂದು ಹೇಳಲು",
			"29": "<sup>29</sup> ಆತನು ಅವರಿಗೆ - ನಿಮಗೆ ಸತ್ಯವಾಗಿ ಹೇಳುತ್ತೇನೆ, ಯಾವನು ದೇವರ ರಾಜ್ಯಕ್ಕಾಗಿ ಮನೆಯನ್ನಾಗಲಿ, ಹೆಂಡತಿಯನ್ನಾಗಲಿ, ಅಣ್ಣತಮ್ಮಂದಿರನ್ನಾಗಲಿ, ತಂದೆತಾಯಿಗಳನ್ನಾಗಲಿ, ಮಕ್ಕಳನ್ನಾಗಲಿ ತ್ಯಜಿಸುತ್ತಾನೋ,",
			"30": "<sup>30</sup> ಅವನಿಗೆ ಈ ಲೋಕದಲ್ಲಿ ಅನೇಕ ಪಾಲು ಹೆಚ್ಚಾಗಿ ಸಿಕ್ಕೇ ಸಿಕ್ಕುತ್ತವೇ; ಮತ್ತು ಮುಂದಣ ಲೋಕದಲ್ಲಿ ನಿತ್ಯಜೀವವು ದೊರೆಯುವದು ಎಂದು ಹೇಳಿದನು.",
			"31": "<sup>31</sup> ಆತನು ತನ್ನ ಹನ್ನೆರಡು ಮಂದಿ ಶಿಷ್ಯರನ್ನು ಕರೆದುಕೊಂಡು ಅವರಿಗೆ - ನೋಡಿರಿ, ನಾವು ಯೆರುಸಲೆಮಿಗೆ ಹೋಗುತ್ತಾ ಇದ್ದೇವೆ; ಮತ್ತು ಮನುಷ್ಯಕುಮಾರನ ವಿಷಯದಲ್ಲಿ ಪ್ರವಾದಿಗಳು ಬರೆದದ್ದೆಲ್ಲಾ ಅವನಲ್ಲಿ ನೆರವೇರುವದು.",
			"32": "<sup>32</sup> ಹೇಗಂದರೆ - ಅವನನ್ನು ಅನ್ಯರ ಕೈಗೆ ಒಪ್ಪಿಸುವರು, ಅವರು ಅವನನ್ನು ಅಪಹಾಸ್ಯ ಮಾಡುವರು, ಬೈಯುವರು, ಉಗುಳುವರು, ಕೊರಡೆಗಳಿಂದ ಹೊಡೆಯುವರು, ಕೊಂದುಹಾಕುವರು;",
			"33": "<sup>33</sup> ಮತ್ತು ಮೂರನೆಯ ದಿನದಲ್ಲಿ ಅವನು ಜೀವಿತನಾಗಿ ಎದ್ದು ಬರುವನು ಎಂದು ಹೇಳಿದನು.",
			"34": "<sup>34</sup> ಅವರು ಈ ಸಂಗತಿಗಳಲ್ಲಿ ಒಂದನ್ನಾದರೂ ಗ್ರಹಿಸಲಿಲ್ಲ, ಈ ಮಾತುಗಳು ಅವರಿಗೆ ಮರೆಯಾಗಿದ್ದವು, ಅವುಗಳ ಅರ್ಥ ಅವರಿಗೆ ತಿಳಿಯಲಿಲ್ಲ.",
			"35": "<sup>35</sup> ಆತನು ಯೆರಿಕೋವಿನ ಸಮೀಪಕ್ಕೆ ಬಂದಾಗ ದಾರಿಯ ಬಳಿಯಲ್ಲಿ ಕೂತು ಭಿಕ್ಷೆಬೇಡುತ್ತಿದ್ದ ಒಬ್ಬ ಕುರುಡನು",
			"36": "<sup>36</sup> ಗುಂಪಾಗಿ ಹೋಗುತ್ತಿದ್ದ ಜನರ ಶಬ್ದವನ್ನು ಕೇಳಿ ಇದೇನು ಎಂದು ವಿಚಾರಿಸಿದನು.",
			"37": "<sup>37</sup> ಅವರು ಅವನಿಗೆ - ನಜರೇತಿನ ಯೇಸು ಈ ಮಾರ್ಗವಾಗಿ ಹೋಗುತ್ತಿದ್ದಾನೆಂದು ತಿಳಿಸಿದಾಗ",
			"38": "<sup>38</sup> ಅವನು - ಯೇಸುವೇ, ದಾವೀದನ ಕುಮಾರನೇ, ನನ್ನನ್ನು ಕರುಣಿಸು ಎಂದು ಕೂಗಿಕೊಂಡನು.",
			"39": "<sup>39</sup> ಮುಂದೆ ಹೋಗುತ್ತಿದ್ದವರು ಸುಮ್ಮನಿರು ಎಂದು ಅವನನ್ನು ಗದರಿಸಲು ಅವನು - ದಾವೀದನ ಕುಮಾರನೇ, ನನ್ನನ್ನು ಕರುಣಿಸು ಎಂದು ಮತ್ತಷ್ಟು ಬೇಡಿಕೊಂಡನು.",
			"40": "<sup>40</sup> ಆಗ ಯೇಸು ನಿಂತು ಅವನನ್ನು ತನ್ನ ಬಳಿಗೆ ಕರೆದುಕೊಂಡು ಬರುವುದಕ್ಕೆ ಅಪ್ಪಣೆಕೊಟ್ಟನು.",
			"41": "<sup>41</sup> ಅವನು ಹತ್ತಿರಕ್ಕೆ ಬಂದನು. ಆತನು ಅವನಿಗೆ - ನಾನು ನಿನಗೆ ಏನು ಮಾಡಬೇಕೆಂದು ಕೋರುತ್ತೀ? ಎಂದು ಕೇಳಲು ಅವನು - ನನಗೆ ಕಣ್ಣುಕಾಣುವಂತೆ ಮಾಡಬೇಕು ಕರ್ತನೇ ಅಂದನು.",
			"42": "<sup>42</sup> ಯೇಸು ಅವನಿಗೆ - ನಿನಗೆ ಕಣ್ಣುಕಾಣುವಂತಾಗಲಿ ಎಂದೋ ಹೇಳಿ; ನಿನ್ನ ನಂಬಿಕೆಯೇ ನಿನ್ನನ್ನು ಸ್ವಸ್ಥಮಾಡಿದೆ ಎಂದು ಹೇಳಿದನು.",
			"43": "<sup>43</sup> ತಕ್ಷಣವೇ ಅವನಿಗೆ ಕಣ್ಣು ಕಾಣಿಸಿದವು; ಅವನು ದೇವರನ್ನು ಕೊಂಡಾಡುತ್ತಾ ಆತನ ಹಿಂದೆ ಹೋದನು. ಜನರೆಲ್ಲರು ನಡೆದ ಸಂಗತಿಯನ್ನು ನೋಡಿ ದೇವರನ್ನು ಸ್ತುತಿಸಿದರು."
		},
		"19": {
			"1": "<sup>1</sup> ಯೇಸು ಯೆರಿಕೋ ಊರನ್ನು ಹಾದುಹೋಗುವಾಗ.",
			"2": "<sup>2</sup> ಅಲ್ಲಿ ಜಕ್ಕಾಯನೆಂಬ ಹೆಸರುಳ್ಳ ಒಬ್ಬ ಮನುಷ್ಯನಿದ್ದನು; ಅವನು ಸುಂಕದ ಗುತ್ತಿಗೆದಾರನು ಮತ್ತು ಐಶ್ವರ್ಯವಂತನಾಗಿದ್ದನು.",
			"3": "<sup>3</sup> ಅವನು ಯೇಸು ಹೇಗಿದ್ದಾನೆಂದು ನೋಡಲು ಅಪೇಕ್ಷಿಸಿದರೂ ತಾನು ಕುಳ್ಳನಾಗಿದ್ದುದರಿಂದ, ಜನರ ಗುಂಪಿನ ನಿಮಿತ್ತ ನೋಡಲಾರದೆ ಹೋದನು.",
			"4": "<sup>4</sup> ಆದಕಾರಣ ಆತನನ್ನು ನೋಡುವುದಕ್ಕಾಗಿ ಮುಂದೆ ಓಡಿಹೋಗಿ ಆತನು ಬರುತ್ತಿರುವ ಮಾರ್ಗದಲ್ಲಿದ್ದ ಆಲದ ಮರವನ್ನು ಹತ್ತಿದನು.",
			"5": "<sup>5</sup> ಯೇಸು ಅಲ್ಲಿಗೆ ಬಂದು ಮೇಲಕ್ಕೆ ನೋಡಿ - ಜಕ್ಕಾಯನೇ, ತಟ್ಟನೆ ಇಳಿದು ಬಾ, ನಾನು ಈಹೊತ್ತು ನಿನ್ನ ಮನೆಯಲ್ಲಿ ಉಳಿದುಕೊಳ್ಳಬೇಕು ಎಂದು ಅವನಿಗೆ ಹೇಳಲು,",
			"6": "<sup>6</sup> ಅವನು ತಕ್ಷಣ ಇಳಿದು ಬಂದು ಆತನನ್ನು ಸಂತೋಷದಿಂದ ಬರಮಾಡಿಕೊಂಡನು",
			"7": "<sup>7</sup> ಇದನ್ನು ನೋಡಿದವರೆಲ್ಲರು - ಈತನು ಪಾಪಿಯಾಗಿರುವವನ ಬಳಿಯಲ್ಲಿ ಉಳಿದುಕೊಳ್ಳುವದಕ್ಕೆ ಹೋಗಿದ್ದಾನೆ ಎಂದು ಗುಣುಗುಟ್ಟಿದರು.",
			"8": "<sup>8</sup> ಆದರೆ ಜಕ್ಕಾಯನು ನಿಂತುಕೊಂಡು ಸ್ವಾಮಿಗೆ - ಕರ್ತನೇ, ನೋಡು, ನನ್ನ ಆಸ್ತಿಯಲ್ಲಿ ಅರ್ಧವನ್ನು ಬಡವರಿಗೆ ಕೊಡುತ್ತೇನೆ; ಮತ್ತು ನಾನು ಅನ್ಯಾಯವಾಗಿ ಯಾರಿಂದ ಏನಾದರೂ ಕಿತ್ತುಕೊಂಡಿದ್ದೇಯಾದರೆ ನಾಲ್ಕರಷ್ಟು ಹಿಂದಕ್ಕೆ ಕೊಡುತ್ತೇನೆ ಎಂದು ಹೇಳಿದನು.",
			"9": "<sup>9</sup> ಇದನ್ನು ಕೇಳಿ ಯೇಸು - ಈಹೊತ್ತು ಈ ಮನೆಗೆ ರಕ್ಷಣೆಯಾಯಿತು; ಇವನು ಸಹ ಅಬ್ರಹಾಮನ ವಂಶದವನಲ್ಲವೇ.",
			"10": "<sup>10</sup> ಮನುಷ್ಯಕುಮಾರನು ದಾರಿತಪ್ಪಿ ಹೋದವರನ್ನು ಹುಡುಕಿ ರಕ್ಷಿಸುವುದಕ್ಕೆ ಬಂದನು ಎಂದು ಹೇಳಿದನು.",
			"11": "<sup>11</sup> ಜನರು ಈ ಮಾತುಗಳನ್ನು ಕೇಳುತ್ತಿರಲಾಗಿ ಯೇಸು ತಾನು ಯೆರೂಸಲೇಮಿಗೆ ಸಮೀಪವಾಗಿದ್ದದರಿಂದಲೂ ದೇವರ ರಾಜ್ಯವು ಕೂಡಲೆ ಪ್ರತ್ಯಕ್ಷವಾಗುವ ಹಾಗಿದೆ ಎಂದು ಅವರು ಭಾವಿಸಿದ್ದರಿಂದಲೂ ಇನ್ನೂ ಒಂದು ಸಾಮ್ಯವನ್ನು ಅವರಿಗೆ ಹೇಳಿದನು.",
			"12": "<sup>12</sup> ಅದೇನಂದರೆ - ಶ್ರೀಮಂತನಾದ ಒಬ್ಬ ಮನುಷ್ಯನು ತಾನು ರಾಜ್ಯಾಧಿಕಾರವನ್ನು ಪಡೆದುಕೊಂಡು ಬರಬೇಕೆಂದು ದೂರದೇಶಕ್ಕೆ ಹೊರಟನು.",
			"13": "<sup>13</sup> ಹೊರಡುವಾಗ ತನ್ನ ಆಳುಗಳಲ್ಲಿ ಹತ್ತು ಮಂದಿಯನ್ನು ಕರೆದು ಅವರಿಗೆ ಹತ್ತು ನಾಣ್ಯಗಳನ್ನು ಕೊಟ್ಟು - ನಾನು ಬರುವತನಕ ವ್ಯಾಪಾರಮಾಡಿಕೊಂಡಿರಿ ಎಂದು ಹೇಳಿ ಹೊರಟು ಹೋದನು.",
			"14": "<sup>14</sup> ಆದರೆ ಅವನ ಪಟ್ಟಣದ ನಿವಾಸಿಗಳು ಅವನನ್ನು ದ್ವೇಷಿಸಿ ಅವನ ಹಿಂದೆ ರಾಯಭಾರಿಗಳನ್ನು ಕಳುಹಿಸಿ ನೀನು ನಮ್ಮ ಮೇಲೆ ದೊರೆತನಮಾಡುವುದು ನಮಗೆ ಇಷ್ಟವಿಲ್ಲ ಎಂದು ಹೇಳಿಸಿದನು.",
			"15": "<sup>15</sup> ಆ ಮೇಲೆ ಅವನು ರಾಜ್ಯಾಧಿಕಾರವನ್ನು ಪಡೆದುಕೊಂಡು ಹಿಂತಿರುಗಿ ಬಂದು ತಾನು ಹಣವನ್ನು ಕೊಟ್ಟಿದ್ದ ಆಳುಗಳು ವ್ಯಾಪಾರದಿಂದ ಎಷ್ಟೆಷ್ಟು ಲಾಭ ಸಂಪಾದಿಸಿಕೊಂಡಿದ್ದಾರೆಂದು ತಿಳಿದುಕೊಳ್ಳೂವುದಕ್ಕಾಗಿ ಅವರನ್ನು ತನ್ನ ಬಳಿಗೆ ಕರೆಸಿದನು.",
			"16": "<sup>16</sup> ಮೊದಲನೆಯವನು ಆತನ ಮುಂದೆ ಬಂದು - ದೊರೆಯೇ, ನೀನು ಕೊಟ್ಟ ನಾಣ್ಯಗಳಿಂದ ಇನ್ನೂ ಹತ್ತು ನಾಣ್ಯಗಳು ಸಂಪಾದನೆಯಾದವು ಅನ್ನಲು",
			"17": "<sup>17</sup> ಅವನು ಅವನಿಗೆ - ಭಲಾ! ನೀನು ಒಳ್ಳೇ ಆಳು; ನೀನು ಬಹು ಸ್ವಲ್ಪವಾದದ್ದರಲ್ಲಿ ನಂಬಿಕೆಯುಳ್ಳವನಾಗಿದ್ದರಿಂದ ಹತ್ತು ಗ್ರಾಮಗಳ ಮೇಲೆ ಅಧಿಕಾರಿಯಾಗಿರು ಎಂದು ಹೇಳಿದನು.",
			"18": "<sup>18</sup> ಎರಡನೆಯವನು ಬಂದು - ದೊರೆಯೇ, ನೀನು ಕೊಟ್ಟ ನಾಣ್ಯಗಳಿಂದ ಇನ್ನೂ ಐದು ನಾಣ್ಯಗಳು ದೊರಕಿದವು ಅನ್ನಲು",
			"19": "<sup>19</sup> ಅವನು ಆ ಆಳಿಗೆ - ನೀನು ಸಹ ಐದು ಗ್ರಾಮಗಳ ಮೇಲೆ ಅಧಿಕಾರಿಯಾಗಿರು ಎಂದು ಹೇಳಿದನು.",
			"20": "<sup>20</sup> ಬಳಿಕ ಮತ್ತೊಬ್ಬನು ಬಂದು - ದೊರೆಯೇ, ಇಗೋ ನೀನು ಕೊಟ್ಟ ನಾಣ್ಯ.",
			"21": "<sup>21</sup> ನೀನು ಇಡದೆಯಿರುವದನ್ನು ಎತ್ತಿಕೊಂಡು ಹೋಗುವವನು ಬಿತ್ತದೆಯಿರುವದನ್ನು ಕೊಯ್ಯುವ ಕಠಿನ ಮನುಷ್ಯನು ಎಂದು ನಿನಗೆ ಹೆದರಿಕೊಂಡು ಇದನ್ನು ವಸ್ತ್ರದಲ್ಲಿ ಕಟ್ಟಿ ಇಟ್ಟುಕೊಂಡೆನು ಅಂದನು,",
			"22": "<sup>22</sup> ಅದಕ್ಕೆ ಅವನು - ನೀನು ಕೆಟ್ಟ ಆಳು, ನಿನ್ನ ಮಾತಿನ ಮೇಲೆಯೇ ನಿನಗೆ ತೀರ್ಪುಮಾಡುತ್ತೇನೆ. ನಾನು ಇಡದೆಯಿರುವದನ್ನು ಎತ್ತಿಕೊಂಡು ಹೋಗುವವನು, ಬಿತ್ತದೆಯಿರುವದನ್ನು ಕೊಯ್ಯುವ ಉಗ್ರವಾದ ಮನುಷ್ಯನು ಎಂದು ತಿಳಿದಿರುವೆಯಾ?",
			"23": "<sup>23</sup> ಹಾಗಾದರೆ ನೀನು ನನ್ನ ಹಣವನ್ನು ಸಾಹುಕಾರನ ಕೈಯಲ್ಲಿ ಯಾಕೆ ಕೊಡಲಿಲ್ಲ? ಆ ಮೇಲೆ ನಾನು ಬಂದು ಅದನ್ನು ಬಡ್ಡಿ ಸಹಿತ ತೆಗೆದುಕೊಳ್ಳುತ್ತಿದ್ದೆನು ಎಂದು ಹೇಳಿ",
			"24": "<sup>24</sup> ಹತ್ತಿರ ನಿಂತಿದ್ದವರಿಗೆ - ಆ ನಾಣ್ಯವನ್ನು ಅವನಿಂದ ತೆಗೆದುಕೊಂಡು ಹತ್ತು ಮೊಹರಿಗಳುಳ್ಳವನಿಗೆ ಕೊಡಿರಿ ಎಂದು ಹೇಳಿದನು.",
			"25": "<sup>25</sup> ಅವರು - ದೊರೆಯೇ, ಹತ್ತು ನಾಣ್ಯಗಳು ಅವನಲ್ಲಿ ಅವೆಯಲ್ಲಾ ಎಂದು ಅವನಿಗೆ ಹೇಳಿದರು.",
			"26": "<sup>26</sup> ಅದಕ್ಕವನು - ಇದ್ದವರಿಗೆಲ್ಲಾ ಕೊಡಲ್ಪಡುವದು, ಇಲ್ಲದವನ ಕಡೆಯಿಂದ ಇದ್ದದ್ದೂ ತೆಗೆಯಲ್ಪಡುವದು ಎಂದು ನಾನು ನಿಮಗೆ ಹೇಳುತ್ತೇನೆ;",
			"27": "<sup>27</sup> ಮತ್ತು ತಮ್ಮ ಮೇಲೆ ನಾನು ದೊರೆತನ ಮಾಡುವುದು ಇಷ್ಟವಿಲ್ಲವೆಂದು ಹೇಳಿದ ಆ ನನ್ನ ವೈರಿಗಳನ್ನು ಇಲ್ಲಿಗೆ ತಂದು ನನ್ನ ಮುಂದೆ ಸಂಹಾರ ಮಾಡಿರಿ ಅಂದನು.",
			"28": "<sup>28</sup> ಯೇಸು ಈ ಮಾತುಗಳನ್ನು ಹೇಳಿದ ಮೇಲೆ ಗಟ್ಟಾ ಹತ್ತಿ ಯೆರೂಸಲೇಮಿಗೆ ಮುಂದಾಗಿ ಹೋದನು.",
			"29": "<sup>29</sup> ಆನಂತರ ಆತನು ಎಣ್ಣೇಮರಗಳ ಗುಡ್ಡ ಎನಿಸಿಕೊಳ್ಳುವ ಗುಡ್ಡದ ಬಳಿಯಲ್ಲಿರುವ ಬೇತ್ಫಾಗೆಗೂ ಬೇಥಾನ್ಯಕ್ಕೂ ಸಮೀಪಿಸಿದಾಗ ಶಿಷ್ಯರಲ್ಲಿ ಇಬ್ಬರನ್ನು ಕರೆದು - ನಿಮ್ಮೆದುರಿಗಿರುವ ಹಳ್ಳಿಗೆ ಹೋಗಿರಿ;",
			"30": "<sup>30</sup> ಅದರೊಳಗೆ ಹೋಗುತ್ತಿರುವಾಗಲೇ ಅಲ್ಲಿ ಕಟ್ಟಿರುವ ಒಂದು ಕತ್ತೇಮರಿಯನ್ನು ಕಾಣುವಿರಿ; ಇದುವರೆಗೆ ಅದರಮೇಲೆ ಯಾರೂ ಹತ್ತಿರುವುದಿಲ್ಲ; ಅದನ್ನು ಬಿಚ್ಚಿ ಹಿಡುಕೊಂಡು ಬನ್ನಿರಿ.",
			"31": "<sup>31</sup> ಯಾವನಾದರೂ ನಿಮ್ಮನ್ನು ಮರಿಯನ್ನು ಯಾಕೆ ಬಿಚ್ಚುತ್ತೀರಿ ಎಂದು ಕೇಳಿದರೆ ಇದು ಸ್ವಾಮಿಯವರಿಗೆ ಬೇಕಾಗಿದೆ ಅನ್ನಿರಿ ಎಂದು ಹೇಳಿಕಳುಹಿಸಿದನು.",
			"32": "<sup>32</sup> ಕಳುಹಿಸಲ್ಪಟ್ಟವರು ಹೋಗಿ ಆತನು ತಮಗೆ ಹೇಳಿದಂತೆಯೇ ಕಂಡರು.",
			"33": "<sup>33</sup> ಅವರು ಆ ಕತ್ತೇಮರಿಯನ್ನು ಬಿಚ್ಚುತ್ತಿರುವಾಗ ಅದರ ಯಜಮಾನನು - ಈ ಮರಿಯನ್ನು ಯಾಕೆ ಬಿಚ್ಚುತ್ತೀರಿ ಎಂದು ಅವರನ್ನು ಕೇಳಿದ್ದಕ್ಕೆ",
			"34": "<sup>34</sup> ಅವರು - ಇದು ಸ್ವಾಮಿಯವರಿಗೆ ಬೇಕಾಗಿದೆ ಎಂದು ಹೇಳಿದರು.",
			"35": "<sup>35</sup> ಅದನ್ನು ಯೇಸುವಿನ ಬಳಿಗೆ ತಂದು ತಮ್ಮ ಬಟ್ಟೆಗಳನ್ನು ಆ ಕತ್ತೇಮರಿಯ ಮೇಲೆ ಹಾಕಿ ಯೇಸುವನ್ನು ಕೂರಿಸಿದರು.",
			"36": "<sup>36</sup> ಆತನು ಹೋಗುತ್ತಿರುವಾಗ ಅವರು ತಮ್ಮ ಬಟ್ಟೆಗಳನ್ನು ದಾರಿಯಲ್ಲಿ ಹಾಸಿದರು.",
			"37": "<sup>37</sup> ಆತನು ಪಟ್ಟಣವನ್ನು ಸಮೀಪಿಸಿ ಎಣ್ಣೇಮರಗಳ ಗುಡ್ಡದಿಂದ ಇಳಿಯುವ ಸ್ಥಳಕ್ಕೆ ಬಂದಾಗ ಶಿಷ್ಯರ ಗುಂಪೆಲ್ಲಾ ಸಂತೋಷಪಡುತ್ತಾ ತಾವು ಕಂಡಿದ್ದ ಎಲ್ಲಾ ಮಹತ್ಕಾರ್ಯಗಳ ವಿಷಯದಲ್ಲಿ -",
			"38": "<sup>38</sup> ಕರ್ತನ ಹೆಸರಿನಲ್ಲಿ ಬರುವ ಅರಸನಿಗೆ ಆಶೀರ್ವಾದ, ಪರಲೋಕದಲ್ಲಿ ಸಮಾಧಾನ, ಮೇಲಣಲೋಕಗಳಲ್ಲಿ ಮಹಿಮೆ ಎಂದು ಮಹಾ ಶಬ್ದದಿಂದ ದೇವರನ್ನು ಕೊಂಡಾಡುವದಕ್ಕೆ ತೊಡಗಿದರು.",
			"39": "<sup>39</sup> ಜನರ ಗುಂಪಿನೊಳಗಿದ್ದ ಫರಿಸಾಯರಲ್ಲಿ ಕೆಲವರು ಆತನಿಗೆ - ಬೋಧಕನೇ, ನಿನ್ನ ಶಿಷ್ಯರನ್ನು ಗದರಿಸು ಎಂದು ಹೇಳಿದ್ದಕ್ಕೆ",
			"40": "<sup>40</sup> ಆತನು - ಇವರು ಸುಮ್ಮನಾದರೆ ಈ ಕಲ್ಲುಗಳೇ ಕೂಗುವವು ಎಂದು ನಿಮಗೆ ಹೇಳುತ್ತೇನೆ ಅಂದನು.",
			"41": "<sup>41</sup> ತರುವಾಯ ಆತನು ಪಟ್ಟಣದ ಸಮೀಪಕ್ಕೆ ಬಂದಾಗ ಆ ಪಟ್ಟಣವನ್ನು ನೋಡಿ ಅದರ ವಿಷಯವಾಗಿ ಕಣ್ಣೀರಿಡುತಾ -",
			"42": "<sup>42</sup> ನೀನಾದರೂ ಸಮಾಧಾನಕ್ಕೆ ಬೇಕಾದದ್ದನ್ನು ಇದೇ ದಿನದಲ್ಲಿ ತಿಳಿದುಕೊಂಡರೆ ಎಷ್ಟೋ ಒಳ್ಳೇಯದು. ಆದರೆ ಈಗ ಅದು ನಿನ್ನ ಕಣ್ಣಿಗೆ ಮರೆಯಾಗಿದೆ.",
			"43": "<sup>43</sup> ದೇವರು ನಿನಗೆ ದರ್ಶನಕೊಟ್ಟ ಸಮಯವನ್ನು ನೀನು ತಿಳಿದುಕೊಳ್ಳಲಿಲ್ಲವಾದ್ದರಿಂದ ನಿನ್ನ ವೈರಿಗಳು ನಿನ್ನ ಸುತ್ತಲೂ ಒಡ್ದುಕಟ್ಟಿ ಮುತ್ತಿಗೆ ಹಾಕಿ ಎಲ್ಲಾ ಕಡೆಗಳಿಂದ ನಿನ್ನನ್ನು ಒಂದುಗೂಡಿಸಿ ನಿನ್ನನ್ನೂ ನಿನ್ನೊಳಗಿರುವ ನಿನ್ನ ಜನರನ್ನೂ ನಿರ್ಮೂಲಮಾಡಿ ನಿನ್ನಲ್ಲಿ ಕಲ್ಲಿನ ಮೇಲೆ ಕಲ್ಲು ನಿಲ್ಲದಂತೆ ಮಾಡುವ ದಿವಸಗಳು ನಿನ್ನ ಮೇಲೆ ಬರುವವು ಅಂದನು.",
			"45": "<sup>45</sup> ಬಳಿಕ ಆತನು ದೇವಾಲಯಕ್ಕೆ ಹೋಗಿ ವ್ಯಾಪಾರಮಾಡುವವರನ್ನು ಹೊರಹಾಕುವುದಕ್ಕೆ ಪ್ರಾರಂಭಿಸಿ ಅವರಿಗೆ -",
			"46": "<sup>46</sup> ನನ್ನ ಆಲಯವು ಪ್ರಾರ್ಥನಾಲಯವಾಗಿರುವದು ಎಂದು ಬರೆಯಲ್ಪಟ್ಟಿದೆಯಲ್ಲಾ; ಆದರೆ ನೀವು ಅದನ್ನು ಕಳ್ಳರ ಗವಿ ಮಾಡಿದ್ದೀರಿ ಎಂದು ಹೇಳಿ ಅವರನ್ನು ಹೊರಗೆ ಅಟ್ಟಿದನು.",
			"47": "<sup>47</sup> ಅಲ್ಲದೆ ಆತನು ಪ್ರತಿದಿನ ದೇವಾಲಯದಲ್ಲಿ ಬಂದು ಉಪದೇಶಮಾಡುತ್ತಿದ್ದನು. ಅಷ್ಟರಲ್ಲಿ ಮಹಾಯಾಜಕರೂ ಶಾಸ್ತ್ರಿಗಳೂ ಪ್ರಜೆಗಳಲ್ಲಿ ನಾಯಕರೂ ಆತನನ್ನು ಕೊಲ್ಲುವದಕ್ಕೆ ಸಂದರ್ಭನೋಡುತ್ತಿದ್ದರು.",
			"48": "<sup>48</sup> ಆದರೆ ಜನರೆಲ್ಲರು ಆತನ ಹತ್ತಿರದಲ್ಲಿದ್ದುಕೊಂಡು ಉಪದೇಶವನ್ನು ಗಮನಿಸಿ ಕೇಳುತ್ತಿದ್ದುದರಿಂದ ಏನು ಮಾಡಬೇಕೆಂದು ಅವರಿಗೆ ತಿಳಿಯಲಿಲ್ಲ."
		},
		"20": {
			"1": "<sup>1</sup> ಆ ದಿವಸಗಳಲ್ಲಿ ಒಂದು ದಿನ ಆತನು ದೇವಾಲಯದಲ್ಲಿ ಜನರಿಗೆ ಉಪದೇಶಮಾಡುತ್ತಾ ಸುವಾರ್ತೆಯನ್ನು ಸಾರುತ್ತಿರಲಾಗಿ ಮಹಾಯಾಜಕರೂ ಶಾಸ್ತ್ರಿಗಳೂ ಸಭೆಯ ಹಿರಿಯರನ್ನು ಕೂಡಿಸಿಕೊಂಡು ಫಕ್ಕನೆ ಬಂದು -",
			"2": "<sup>2</sup> ನೀನು ಯಾವ ಅಧಿಕಾರದಿಂದ ಇದನ್ನೆಲ್ಲಾ ಮಾಡುತ್ತೀ? ಈ ಅಧಿಕಾರವನ್ನು ನಿನಗೆ ಕೊಟ್ಟವರು ಯಾರು? ನಮಗೆ ಹೇಳು ಎಂದು ಆತನನ್ನು ಕೇಳಲು",
			"3": "<sup>3</sup> ಯೇಸುವು ಅವರಿಗೆ - ನಾನು ಸಹ ನಿಮ್ಮನ್ನು ಒಂದು ಮಾತು ಕೇಳುತ್ತೇನೆ, ನನಗೆ ಹೇಳಿರಿ.",
			"4": "<sup>4</sup> ದೀಕ್ಷಾಸ್ನಾನ ಮಾಡಿಸುವ ಅಧಿಕಾರವು ಯೋಹಾನನಿಗೆ ಪರಲೋಕದಿಂದ ಬಂದಿತೋ ಅಥವಾ ಮನುಷ್ಯರಿಂದ ಬಂದಿತೋ ಎಂದು ಅವರನ್ನು ಕೇಳಿದನು.",
			"5": "<sup>5</sup> ಆಗ ಅವರು - ಪರಲೋಕದಿಂದ ಬಂತೆಂದು ನಾವು ಹೇಳಿದರೆ ಮತ್ತೆ ನೀವು ಅವನನ್ನು ಯಾಕೆ ನಂಬಲಿಲ್ಲ ಅಂದಾನು;",
			"6": "<sup>6</sup> ಮನುಷ್ಯರಿಂದ ಬಂತೆಂದು ಹೇಳಿದರೆ ಜನರೆಲ್ಲರೂ ಯೋಹಾನನನ್ನು ಪ್ರವಾದಿಯೆಂದು ನಂಬಿರುವದರಿಂದ ಅವರು ನಮ್ಮನ್ನು ಕಲ್ಲೆಸೆದು ಕೊಂದಾರು ಎಂದು ತಮ್ಮ ತಮ್ಮೊಳಗೆ ಮಾತಾಡಿಕೊಂಡು",
			"7": "<sup>7</sup> ಅದು ಎಲ್ಲಿಂದ ಬಂದಿತೋ ನಾವರಿಯೆವು ಎಂದು ಉತ್ತರಕೊಟ್ಟರು.",
			"8": "<sup>8</sup> ಆಗ ಯೇಸು ಅವರಿಗೆ - ಇದನ್ನೆಲ್ಲಾ ಯಾವ ಅಧಿಕಾರದಿಂದ ಮಾಡುತ್ತೇನೋ ಅದನ್ನು ನಾನೂ ನಿಮಗೆ ಹೇಳುವುದಿಲ್ಲ ಎಂದು ಹೇಳಿದನು.",
			"9": "<sup>9</sup> ಆಗ ಆತನು ಜನರಿಗೆ ಒಂದು ಸಾಮ್ಯವನ್ನು ಹೇಳುವುದಕ್ಕೆ ತೊಡಗಿದನು. ಅದೇನಂದರೆ - ಒಬ್ಬ ಮನುಷ್ಯನು ಒಂದು ದ್ರಾಕ್ಷಿಯ ತೋಟವನ್ನು ಮಾಡಿ ಆದನ್ನು ಒಕ್ಕಲಿಗರಿಗೆ ಗುತ್ತಿಗೆಗೆ ಕೊಟ್ಟು ಬೇರೊಂದು ದೇಶಕ್ಕೆ ಹೋಗಿ ಅಲ್ಲಿ ಬಹುಕಾಲ ಇದ್ದನು.",
			"10": "<sup>10</sup> ಫಲಕಾಲ ಬಂದಾಗ ದ್ರಾಕ್ಷಿಯತೋಟದ ಪಾಲನ್ನು ತೆಗೆದುಕೊಳ್ಳವುದಕ್ಕಾಗಿ ಒಬ್ಬ ಆಳನ್ನು ಒಕ್ಕಲಿಗರ ಬಳಿಗೆ ಕಳುಹಿಸಿದನು. ಆದರೆ ಆ ಒಕ್ಕಲಿಗರು ಅವನನ್ನು ಹೊಡೆದು ಬರಿಗೈಲಿ ಕಳುಹಿಸಿಬಿಟ್ಟರು.",
			"11": "<sup>11</sup> ಆ ಧಣಿಯು ಬೇರೊಬ್ಬ ಆಳನ್ನು ಕಳುಹಿಸಲಾಗಿ ಅವರು ಅವನನ್ನೂ ಹೊಡೆದು ಅವಮಾನಪಡಿಸಿ ಬರಿಗೈಲಿ ಕಳುಹಿಸಿಬಿಟ್ಟರು.",
			"12": "<sup>12</sup> ಆ ಮೇಲೆ ಮೂರನೆಯವನನ್ನು ಕಳುಹಿಸಲು ಅವರು ಅವನನ್ನೂ ಗಾಯವಾಗುವಷ್ಟು ಹೊಡೆದು ಹೊರಕ್ಕೆ ಅಟ್ಟಿದರು.",
			"13": "<sup>13</sup> ಆಗ ಆ ದ್ರಾಕ್ಷಿಯ ತೋಟದ ಧಣಿಯು - ನಾನೇನು ಮಾಡಲಿ? ನನ್ನ ಮುದ್ದುಮಗನನ್ನು ಕಳುಹಿಸುತ್ತೇನೆ, ಒಂದು ವೇಳೆ ಅವನಿಗಾದರೂ ಮರ್ಯಾದೆ ತೋರಿಸಾರು ಅಂದುಕೊಂಡನು.",
			"14": "<sup>14</sup> ಆದರೆ ಆ ಒಕ್ಕಲಿಗರು ದ್ರಾಕ್ಷಾತೋಟದ ಯಜಮಾನನ ಮಗನನ್ನು ಕಂಡು - ಇವನೇ ಬಾಧ್ಯಸ್ಥನು; ಇವನನ್ನು ಕೊಂದುಹಾಕೋಣ, ಇವನ ಪಿತ್ರಾರ್ಜಿತವೆಲ್ಲಾ ನಮ್ಮದಾಗುವದು ಎಂದು ಒಬ್ಬರಸಂಗಡಲೊಬ್ಬರು ಮಾತನಾಡಿಕೊಂಡು",
			"15": "<sup>15</sup> ಅವನನ್ನು ದ್ರಾಕ್ಷಿಯ ತೋಟದಿಂದ ಹೊರಕ್ಕೆ ದೊಬ್ಬಿ ಕೊಂದುಹಾಕಿದರು. ಹಾಗಾದರೆ ದ್ರಾಕ್ಷಿಯತೋಟದ ಧಣಿಯು ಅವರಿಗೆ ಏನು ಮಾಡಿಯಾನು?",
			"16": "<sup>16</sup> ಅವನು ಬಂದು ಆ ಒಕ್ಕಲಿಗರನ್ನು ಸಂಹರಿಸಿ ತನ್ನ ತೋಟವನ್ನು ಬೇರೆ ಜನರಿಗೆ ಮಾಡುವದಕ್ಕೆ ಕೊಡುವನು ಅಂದನು.",
			"17": "<sup>17</sup> ಇದನ್ನು ಜನರು ಕೇಳಿ - ಹಾಗಾಗಬಾರದು ಅಂದರು. ಆದರೆ ಆತನು ಅವರನ್ನು ದೃಷ್ಟಿಸಿ ನೋಡಿ - ಹಾಗಾದರೆ ಮನೆ ಕಟ್ಟುವವರು ಬೇಡವೆಂದು ಬಿಟ್ಟ ಕಲ್ಲೇ ಮುಖ್ಯವಾದ ಮೂಲೆಗಲ್ಲಾಯಿತು ಎಂದು ಬರೆದಿರುವ ಮಾತೇನು?",
			"18": "<sup>18</sup> ಈ ಕಲ್ಲಿನ ಮೇಲೆ ಬೀಳುವ ಪ್ರತಿಯೊಬ್ಬನು ತುಂಡುತುಂಡಾಗುವನು; ಆ ಕಲ್ಲು ಯಾರ ಮೇಲೆ ಬೀಳುವದೋ ಅದು ಅವನನ್ನು ಪುಡಿಪುಡಿ ಮಾಡುವುದು ಅಂದನು.",
			"19": "<sup>19</sup> ಶಾಸ್ತ್ರಿಗಳೂ ಮಹಾಯಾಜಕರೂ ಈ ಮಾತುಗಳನ್ನು ಕೇಳಿ ತಮ್ಮನ್ನೇ ಕುರಿತು ಈ ಸಾಮ್ಯವನ್ನು ಹೇಳಿದನು ಎಂದು ತಿಳಿದುಕೊಂಡು ಅದೇ ಗಳಿಗೆಯಲ್ಲಿ ಆತನನ್ನು ಹಿಡಿಯುವದಕ್ಕೆ ಸಂದರ್ಭನೋಡಿದರು; ಆದರೆ ಜನರಿಗೆ ಭಯಪಟ್ಟರು.",
			"20": "<sup>20</sup> ಆಮೇಲೆ ಅವರು ಯೇಸುವನ್ನು ಅಧಿಪತಿಯ ವಶಕ್ಕೂ ಅಧಿಕಾರಿಗಳಿಗೂ ಒಪ್ಪಿಸಬೇಕೆಂದು ಹೊಂಚ್ಚು ಹಾಕುತ್ತಾ ನೀತಿವಂತರಂತೆ ನಟಿಸುತ್ತಿರುವ ಗೊಢಾಚಾರರನ್ನು ಆತನ ಮಾತಿನಲ್ಲಿ ಏನನ್ನಾದರೂ ತಪ್ಪುಗಳನ್ನು ಹಿಡಿಯುವದಕ್ಕೆ (ಆತನ ಬಳಿಗೆ) ಕಳುಹಿಸಿದರು.",
			"21": "<sup>21</sup> ಅವರು - ಬೋಧಕನೇ, ನೀನು ಸರಿಯಾಗಿ ಮಾತಾಡುತ್ತಾ ಉಪದೇಶಮಾಡುತ್ತೀ, ನೀನು ಮುಖದಾಕ್ಷಿಣ್ಯವಿಲ್ಲದೆ ದೇವರ ಮಾರ್ಗವನ್ನು ಸತ್ಯವಾಗಿ ಬೋಧಿಸುವವನಾಗಿದ್ದೀ ಎಂದು ಬಲ್ಲೆವು.",
			"22": "<sup>22</sup> ಕೈಸರನಿಗೆ ಕಂದಾಯ ಕೊಡುವದು ಸರಿಯೋ ಸರಿಯಲ್ಲವೋ ಎಂದು ಆತನನ್ನು ಕೇಳಿದರು.",
			"23": "<sup>23</sup> ಯೇಸುವು ಅವರ ಕುಯುಕ್ತಿಯನ್ನು ಕಂಡುಕೊಂಡು",
			"24": "<sup>24</sup> ಅವರಿಗೆ - ನನಗೆ ಒಂದು ನಾಣ್ಯವನ್ನು ತೋರಿಸಿರಿ. ಇದರಲ್ಲಿ ಯಾರ ತಲೆ ಹಾಗು ಮುದ್ರೆಯದೆ ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ ಅವರು - ಕೈಸರನದು ಅಂದರು.",
			"25": "<sup>25</sup> ಆತನು ಅವರಿಗೆ - ಹಾಗಾದರೆ ಕೈಸರನದನ್ನು ಕೈಸರನಿಗೆ ಕೊಡಿರಿ, ದೇವರದನ್ನು ದೇವರಿಗೆ ಕೊಡಿರಿ ಎಂದು ಹೇಳಿದನು.",
			"26": "<sup>26</sup> ಆತನ ಮಾತಿನಲ್ಲಿ ಅವರು ಯಾವ ತಪ್ಪನ್ನೂ ಹಿಡಿಯಲಾಗದೆ ಜನರ ಮುಂದೆ ಆತನು ಕೊಟ್ಟ ಉತ್ತರಕ್ಕೆ ಆಶ್ಚರ್ಯಪಟ್ಟು ಸುಮ್ಮನಾದರು.",
			"27": "<sup>27</sup> ತರುವಾಯ ಪುನರುತ್ಥಾನವಿಲ್ಲವೆಂದು ಹೇಳುವವರಾದ ಸದ್ದುಕಾಯರಲ್ಲಿ ಕೆಲವರು ಬಂದು ಆತನಿಗೆ ಪ್ರಶ್ನೆಮಾಡಿದ್ದೇನಂದರೆ -",
			"28": "<sup>28</sup> ಬೋಧಕನೇ, ಅಣ್ಣನಾದವನು ಹೆಂಡತಿಯುಳ್ಳವನಾಗಿದ್ದು ಮಕ್ಕಳಿಲ್ಲದೆ ಸತ್ತರೆ ಅವನ ತಮ್ಮನು ಆಕೆಯನ್ನು ಮದುವೆಮಾಡಿಕೊಂಡು ತನ್ನ ಅಣ್ಣನಿಗೆ ಸಂತಾನವನ್ನು ಪಡೆಯಬೇಕೆಂದು ಮೋಶೆಯು ನಮಗೆ ಬರೆದಿಟ್ಟನಷ್ಟೆ.",
			"29": "<sup>29</sup> ಒಳ್ಳೇಯದು, ಏಳು ಮಂದಿ ಅಣ್ಣತಮ್ಮಂದಿರಿದ್ದರು. ಮೊದಲನೆಯವನು ಮದುವೆಯಾಗಿ ಮಕ್ಕಳಿಲ್ಲದೆ ಸತ್ತನು.",
			"30": "<sup>30</sup> ಎರಡನೆಯವನೂ ಅವನ ತರುವಾಯ ಮೂರನೆಯವನೂ ಆಕೆಯನ್ನು ಮದುವೆಮಾಡಿಕೊಂಡರು. ಇದೇ ರೀತಿಯಾಗಿ ಏಳು ಮಂದಿಯೂ ಮದುವೆಯಾಗಿ ಮಕ್ಕಳಿಲ್ಲದೆ ಸತ್ತರು.",
			"32": "<sup>32</sup> ಕಡೆಯಲ್ಲಿ ಆ ಹೆಂಗಸೂ ಸತ್ತಳು.",
			"33": "<sup>33</sup> ಹಾಗಾದರೆ ಪುನರುತ್ಥಾನದಲ್ಲಿ ಆ ಸ್ತ್ರೀಯು ಅವರೊಳಗೆ ಯಾರ ಹೆಂಡತಿಯಾಗಿರುವಳು? ಆಕೆಯನ್ನು ಏಳು ಮಂದಿಯೂ ಮದುವೆ ಮಾಡಿಕೊಂಡಿದ್ದರಲ್ಲಾ ಅಂದರು.",
			"34": "<sup>34</sup> ಯೇಸು ಅವರಿಗೆ - ಈ ಲೋಕದ ಜನರು ಮದುವೆಮಾಡಿಕೊಳ್ಳುತ್ತಾರೆ ಹಾಗೂ ಮದುವೆಮಾಡಿಕೊಡುತ್ತಾರೆ;",
			"35": "<sup>35</sup> ಆದರೆ ಮೇಲಣ ಲೋಕವನ್ನೂ ಸತ್ತವರೊಳಗಿಂದ ಪುನರುತ್ಥಾನವನ್ನೂ ಪಡೆಯುವದಕ್ಕೆ ಯೋಗ್ಯರೆನಿಸಿಕೊಂಡಿರುವವರು ಮದುವೆಮಾಡಿಕೊಳ್ಳುವದೂ ಇಲ್ಲ, ಮಾಡಿಕೊಡುವದೂ ಇಲ್ಲ.",
			"36": "<sup>36</sup> ಅವರು ಇನ್ನು ಮರಣಕ್ಕೆ ಗುರಿಯಾಗುವದೇ ಇಲ್ಲ. ಪುನರುತ್ಥಾನವನ್ನು ಹೊಂದಿದವರಾಗಿದ್ದು ದೇವದೂತರಿಗೆ ಸರಿಸಮಾನರೂ ದೇವರ ಮಕ್ಕಳೂ ಆಗಿರುತ್ತಾರೆ.",
			"37": "<sup>37</sup> ಸತ್ತವರು ಬದುಕಿ ಏಳುತ್ತಾರೆಂಬದನ್ನು ಮೋಶೆಯೂ ಸೂಚಿಸಿದ್ದಾನೆ. ಅವನು ಪೊದೆಯ ಸಂಗತಿಯಿರುವ ಅಧ್ಯಾಯದಲ್ಲಿ ಕರ್ತನನ್ನು ಅಬ್ರಹಾಮನ ದೇವರು ಇಸಾಕನ ದೇವರು ಯಾಕೋಬನ ದೇವರು ಎಂದು ಹೇಳಿದ್ದಾನೆ.",
			"38": "<sup>38</sup> ದೇವರು ಜೀವಿತರಿಗೆ ದೇವರಾಗಿದ್ದಾನೆ ಹೊರತು ಜೀವವಿಲ್ಲದವರಿಗೆ ಅಲ್ಲ. ಆತನಿಗೆ ಎಲ್ಲರೂ ಜೀವಿಸುವವರೇ ಎಂದು ಹೇಳಿದನು.",
			"39": "<sup>39</sup> ಶಾಸ್ತ್ರಿಗಳಲ್ಲಿ ಕೆಲವರು-ಬೋಧಕನೇ, ಚೆನ್ನಾಗಿ ಹೇಳಿದಿ ಅಂದರು.",
			"40": "<sup>40</sup> ಆತನನ್ನು ಇನ್ನೇನು ಕೇಳುವದಕ್ಕೂ ಅವರಿಗೆ ಧೈರ್ಯವಿಲ್ಲದೆ ಹೋಯಿತು.",
			"41": "<sup>41</sup> ಆಗ ಯೇಸುವು - ಬರಬೇಕಾದ ಕ್ರಿಸ್ತನು ದಾವೀದನ ಮಗನೆಂದು ಹೇಳುತ್ತಾರಲ್ಲಾ, ಅದು ಹೇಗಾದೀತು?",
			"42": "<sup>42</sup> ನಾನು ನಿನ್ನ ವಿರೋಧಿಗಳನ್ನು ನಿನ್ನ ಪಾದಗಳಿಗೆ ಪೀಠವನ್ನಾಗಿ ಮಾಡುವ ತನಕ ನೀನು ನನ್ನ ಬಲಗಡೆಯಲ್ಲಿ ಕೂಳಿತುಕೊಂಡಿರು ಎಂದು ಕರ್ತನು ನನ್ನ ಒಡೆಯನಿಗೆ ನುಡಿದನು ಎಂಬದಾಗಿ ಕೀರ್ತನೆಗಳ ಗ್ರಂಥದಲ್ಲಿ ದಾವೀದನೇ ಹೇಳುತ್ತಾನಲ್ಲಾ.",
			"44": "<sup>44</sup> ದಾವೀದನು ಆತನನ್ನು ಒಡೆಯನೆಂದು ಹೇಳಿದಮೇಲೆ ಆತನು ಅವನಿಗೆ ಮಗನಾಗುವದು ಹೇಗೆ ಎಂದು ಅವರನ್ನು ಕೇಳಿದನು.",
			"45": "<sup>45</sup> ಜನರೆಲ್ಲರು ಕೇಳುತ್ತಿರುವಲ್ಲಿ ಆತನು ತನ್ನ ಶಿಷ್ಯರಿಗೆ ಹೇಳಿದ್ದೇನಂದರೆ-",
			"46": "<sup>46</sup> ಶಾಸ್ತ್ರಿಗಳ ವಿಷಯದಲ್ಲಿ ಎಚ್ಚರಿಕೆಯಾಗಿರಿ. ಅವರು ನಿಲುವಂಗಿಗಳನ್ನು ತೊಟ್ಟುಕೊಂಡು ತಿರುಗಾಡುವದರಲ್ಲಿ ಮನಸ್ಸುಳ್ಳವರೂ, ಅಂಗಡಿಬೀದಿಗಳಲ್ಲಿ ನಮಸ್ಕಾರಗಳು, ಸಭಾಮಂದಿರಗಳಲ್ಲಿ ಮುಖ್ಯ ಪೀಠಗಳು, ಔತಣಕೂಟಗಳಲ್ಲಿ ಪ್ರಥಮಸ್ಥಾನ, ಇವುಗಳನ್ನು ಬಯಸುವವರೂ ಆಗಿದ್ದಾರೆ.",
			"47": "<sup>47</sup> ಅವರು ವಿಧವೆಯರ ಮನೆಗಳನ್ನು ನುಂಗಿ ನಟನೆಗಾಗಿ ದೇವರಿಗೆ ಉದ್ದವಾದ ಪ್ರಾರ್ಥನೆಗಳನ್ನು ಮಾಡುತ್ತಾರೆ; ಇಂಥವರು ಹೆಚ್ಚಾದ ದಂಡನೆಯನ್ನು ಹೊಂದುವರು ಅಂದನು."
		},
		"21": {
			"1": "<sup>1</sup> ಯೇಸು ತಲೆಯೆತ್ತಿ ನೋಡಿ ಐಶ್ವರ್ಯವಂತರು ಕಾಣಿಕೆಗಳನ್ನು ಬೊಕ್ಕಸದಲ್ಲಿ ಹಾಕುವುದನ್ನು ಕಂಡನು.",
			"2": "<sup>2</sup> ಆಗ ಒಬ್ಬ ಬಡ ವಿಧವೆಯು ಬಂದು ಎರಡು ಕಾಸುಗಳನ್ನು ಹಾಕುವದನ್ನು ಯೇಸುವು ಗಮನಿಸಿ -",
			"3": "<sup>3</sup> ನಿಮಗೆ ಸತ್ಯವಾಗಿ ಹೇಳುತ್ತೇನೆ, ಈ ಬಡ ವಿಧವೆ ಎಲ್ಲರಿಗಿಂತಲೂ ಹೆಚ್ಚು ಹಾಕಿದ್ದಾಳೆ.",
			"4": "<sup>4</sup> ಹೇಗಂದರೆ ಅವರೆಲ್ಲರು ತಮಗೆ ಸಾಕಾಗಿ ಮಿಕ್ಕದ್ದರಲ್ಲಿ ಕಾಣಿಕೆಕೊಟ್ಟರು; ಈಕೆಯೋ ತನ್ನ ಬಡತನದಲ್ಲಿಯೂ ತನ್ನ ಜೀವನಕಿದ್ದುದ್ದೆಲ್ಲವನ್ನೂ ಕೊಟ್ಟುಬಿಟ್ಟಳು ಅಂದನು.",
			"5": "<sup>5</sup> ಕೆಲವರು ದೇವಾಲಯದ ವಿಷಯದಲ್ಲಿ ಅದು ಅಂದವಾದ ಕಲ್ಲುಗಳಿಂದಲೂ ಕಾಣಿಕೆಯ ವಸ್ತುಗಳಿಂದಲೂ ಅಲಂಕಾರವಾಗಿದೆ ಎಂದು ಹೇಳುತ್ತಿರುವಾಗ",
			"6": "<sup>6</sup> ಯೇಸುವು - ನೀವು ಇವುಗಳನ್ನು ನೋಡುತ್ತಿದ್ದೀರಲ್ಲಾ, ಇಲ್ಲಿ ಕಲ್ಲಿನ ಮೇಲೆ ಕಲ್ಲು ಉಳಿಯದೆ ಎಲ್ಲಾ ಕೆಡವಲ್ಪಡುವ ದಿನಗಳು ಬರುವವು ಅಂದನು.",
			"7": "<sup>7</sup> ಅವರು - ಬೋಧಕನೇ, ಅದು ಯಾವಾಗ ಆಗುವದು? ಅದು ಸಂಭವಿಸುವಾಗ ಯಾವ ಸೂಚನೆ ಗಳುಂಟಾಗುವುವು? ಎಂದು ಆತನನ್ನು ಕೇಳಲು",
			"8": "<sup>8</sup> ಯೇಸುವು ಹೇಳಿದ್ದೇನಂದರೆ - ನೀವು ಮೋಸಹೋಗದಂತೆ ಎಚ್ಚರವಾಗಿರಿ. ಯಾಕೆಂದರೆ ಅನೇಕರು ಬಂದು ನನ್ನ ಹೆಸರನ್ನು ಹೇಳಿಕೊಂಡು ನಾನೇ ಕ್ರಿಸ್ತನು, ನಾನೇ ಕ್ರಿಸ್ತನು ಎಂತಲೂ ಆ ಕಾಲ ಹತ್ತಿರವಾಯಿತು ಎಂತಲೂ ಹೇಳುವರು; ಅವರ ಹಿಂದೆ ಹೋಗಬೇಡಿರಿ.",
			"9": "<sup>9</sup> ಇದಲ್ಲದೆ ಯುದ್ಧಗಳೂ ಗಲಭೆಗಳೂ ಆಗುವದನ್ನು ನೀವು ಕೇಳುವಾಗ, ನೋಡುವಾಗ ಭಯಭೀತರಾಗಬೇಡಿರಿ; ಯಾಕೆಂದರೆ ಇದೆಲ್ಲಾ ಮೊದಲು ಆಗುವದು ಅಗತ್ಯ; ಆದರೂ ಕೂಡಲೆ ಅಂತ್ಯ ಬರುವದಿಲ್ಲ ಅಂದನು.",
			"10": "<sup>10</sup> ಮತ್ತು ಯೇಸು ಅವರಿಗೆ ಹೇಳಿದ್ದೇನಂದರೆ - ಜನಕ್ಕೆ ವಿರೋಧವಾಗಿ ಜನರೂ ರಾಜ್ಯಕ್ಕೆ ವಿರೋಧವಾಗಿ ರಾಜ್ಯವೂ ಏಳುವವು;",
			"11": "<sup>11</sup> ಮತ್ತು ಮಹಾಭೂಕಂಪಗಳಾಗುವವು; ಅಲ್ಲಲ್ಲಿ ಬರಗಳೂ ಉಪದ್ರವಗಳೂ ಬರುವವು; ಭಯಾನಕ ಘಟನೆಗಳೂ ಮಹಾ ಸೂಚನೆಗಳೂ ಆಕಾಶದಲ್ಲಿ ತೋರುವವು.",
			"12": "<sup>12</sup> ಆದರೆ ಇವೆಲ್ಲಾ ನಡೆಯುವದಕ್ಕಿಂತ ಮುಂಚೆ ಅವರು ನಿಮ್ಮನ್ನು ಹಿಡಿದು ಸಭಾಮಂದಿರಗಳ ಮತ್ತು ಸೆರೆಮನೆಯ ಅಧಿಕಾರಿಗಳ ವಶಕ್ಕೆ ಕೊಟ್ಟು ನನ್ನ ಹೆಸರಿನ ನಿಮಿತ್ತವಾಗಿ ಅರಸುಗಳ ಮುಂದೆಯು ಅಧಿಪತಿಗಳ ಮುಂದೆಯು ಕರೆದುಕೊಂಡುಹೋಗಿ ಹಿಂಸೆಪಡಿಸುವರು.",
			"13": "<sup>13</sup> ಇದು ಸಾಕ್ಷಿಹೇಳುವದಕ್ಕೆ ನಿಮಗೆ ಅನುಕೂಲವಾಗುವದು.",
			"14": "<sup>14</sup> ಆದದರಿಂದ ಏನು ಉತ್ತರ ಕೊಡಬೇಕೆಂಬ ವಿಷಯದಲ್ಲಿ ನಾವು ಮುಂದಾಗಿ ಯೋಚಿಸುವದಿಲ್ಲವೆಂದು ನಿಮ್ಮ ಮನಸ್ಸಿನಲ್ಲಿ ನಿಶ್ಚಯಮಾಡಿಕೊಳ್ಳಿರಿ.",
			"15": "<sup>15</sup> ಯಾಕೆಂದರೆ ನಿಮ್ಮ ವಿರೋಧಿಗಳೆಲ್ಲರೂ ಎದುರು ನಿಲ್ಲುವುದಕ್ಕೂ ಎದುರು ಮಾತಾಡುವದಕ್ಕೂ ಆಗದಂಥ ಬಾಯನ್ನೂ, ಬುದ್ಧಿಯನ್ನೂ ನಾನೇ ನಿಮಗೆ ಕೊಡುತ್ತೇನೆ.",
			"16": "<sup>16</sup> ಆದರೆ ತಂದೆತಾಯಿಗಳೂ ಅಣ್ಣತಮ್ಮಂದಿರೂ ಬಂಧುಬಾಂಧವರೂ ಸ್ನೇಹಿತರೂ ನಿಮ್ಮನ್ನು ಅವರಿಗೆ ಒಪ್ಪಿಸಿಕೊಡುವರು; ಮತ್ತು ನಿಮ್ಮಲ್ಲಿ ಕೆಲವರನ್ನು ಕೊಲ್ಲಿಸುವರು.",
			"17": "<sup>17</sup> ಇದಲ್ಲದೆ ನನ್ನ ಹೆಸರಿನ ನಿಮಿತ್ತ ನಿಮ್ಮನ್ನು ಎಲ್ಲರೂ ಹಗೆಮಾಡುವರು.",
			"18": "<sup>18</sup> ಆದರೂ ನಿಮ್ಮದೊಂದು ತಲೆಕೂದಲಾದರೂ ನಾಶವಾಗುವದಿಲ್ಲ.",
			"19": "<sup>19</sup> ನೀವು ಸಹನೆಯಿಂದ ನಿಮ್ಮ ಪ್ರಾಣಗಳನ್ನು ಪಡದುಕೊಳ್ಳುವಿರಿ.",
			"20": "<sup>20</sup> ಆದರೆ ಸೈನಿಕರ ದಂಡುಗಳು ಯೆರೂಸಲೇಮ್ ಪಟ್ಟಣಕ್ಕೆ ಮುತ್ತಿಗೆ ಹಾಕಿರುವದನ್ನು ನೀವು ಕಾಣುವಾಗ ಅದು ಹಾಳಾಗುವ ಕಾಲ ಸಮೀಪವಾಯಿತೆಂದು ತಿಳಿದುಕೊಳ್ಳಿರಿ.",
			"21": "<sup>21</sup> ಆಗ ಯೂದಾಯದಲ್ಲಿರುವವರು ಬೆಟ್ಟಗಳಿಗೆ ಓಡಿಹೋಗಲಿ; ಆ ಪಟ್ಟಣದಲ್ಲಿರುವವರು ಅದರೊಳಗಿಂದ ಹೊರಟುಹೋಗಲಿ; ಹಳ್ಳಿಯವರು ಅದರೊಳಕ್ಕೆ ಹೋಗದಿರಲಿ.",
			"22": "<sup>22</sup> ಯಾಕೆಂದರೆ ಬರೆದಿರುವದೆಲ್ಲಾ ನೆರವೇರುವುದಕ್ಕಾಗಿ ಅವು ದಂಡನೆಯ ದಿನಗಳಾಗಿವೆ.",
			"23": "<sup>23</sup> ಆ ದಿನಗಳಲ್ಲಿ ಗರ್ಭಿಣಿಯರಿಗೂ ಮೊಲೆಕುಡಿಸುವ ಹೆಂಗಸರಿಗೂ ಆಗುವ ಕಷ್ಟವನ್ನು ಏನು ಹೇಳಲಿ! ಈ ಸೀಮೆಯ ಮೇಲೆ ಮಹಾವಿಪತ್ತೂ ಈ ಜನರಿಗೆ ಉಗ್ರದಂಡನೆಯೂ ಆಗುವವು.",
			"24": "<sup>24</sup> ಅವರು ಕತ್ತಿಯ ಬಾಯಿಗೆ ಗುರಿಯಾಗುವರು; ಅವರನ್ನು ಅನ್ಯದೇಶಗಳಿಗೆಲ್ಲಾ ಸೆರೆಹಿಡಿದುಕೊಂಡು ಹೋಗುವರು; ಅನ್ಯದೇಶದವರ ಸಮಯಗಳು ಪೂರೈಸುವ ತನಕ ಯೆರೂಸಲೇಮ್ ಪಟ್ಟಣವು ಅನ್ಯದೇಶದವರಿಂದ ತುಳಿದಾಡಲ್ಪಡುತ್ತಿರುವದು.",
			"25": "<sup>25</sup> ಇದಲ್ಲದೆ ಸೂರ್ಯ ಚಂದ್ರ ನಕ್ಷತ್ರಗಳಲ್ಲಿ ಸೂಚನೆಗಳು ತೋರುವವು. ಭೂಮಿಯ ಮೇಲೆ ಸಮುದ್ರದ ಮತ್ತು ತೆರೆಗಳ ಘೋಷದ ನಿಮಿತ್ತವಾಗಿ ಜನಗಳಿಗೆ ದಿಕ್ಕುಕಾಣದೆ ಸಂಕಟವು ಉಂಟಾಗುವದು.",
			"26": "<sup>26</sup> ಆಕಾಶದ ಶಕ್ತಿಗಳು ಕದಲುವದರಿಂದ ಮನುಷ್ಯರು ಭಯಹಿಡಿದವರಾಗಿ ಲೋಕಕ್ಕೆ ಏನು ಬರುವದೋ ಎಂದು ಎದುರು ನೋಡುತ್ತಾ ಪ್ರಾಣಹೋದಂತಾಗುವರು.",
			"27": "<sup>27</sup> ಆಗ ಮನುಷ್ಯಕುಮಾರನು ಬಲದಿಂದಲೂ ಬಹುಮಹಿಮೆಯಿಂದಲೂ ಮೇಘದಲ್ಲಿ ಬರುವದನ್ನು ಕಾಣುವರು.",
			"28": "<sup>28</sup> ಆದರೆ ಇವು ಸಂಭವಿಸುವುದಕ್ಕೆ ತೊಡಗುವಾಗ, ನಿಮ್ಮ ತಲೆಗಳನ್ನು ಮೇಲಕ್ಕೆ ಎತ್ತಿರಿ; ನಿಮ್ಮ ಬಿಡುಗಡೆಯು ಸಮೀಪವಾಗಿದೆ ಅಂದನು.",
			"29": "<sup>29</sup> ಯೇಸುವು ಅವರಿಗೆ ಒಂದು ಸಾಮ್ಯವಾಗಿ ಹೇಳಿದ್ದೇನಂದರೆ - ಅಂಜೂರ ಮುಂತಾದ ಮರಗಳನ್ನೂ ನೋಡಿರಿ.",
			"30": "<sup>30</sup> ಅವು ಚಿಗುರಿದ ಕೂಡಲೆ ನೀವು ಅವನ್ನು ಕಂಡು ಈಗ ಬೇಸಿಗೆಯು ಹತ್ತಿರವಾಯಿತೆಂದು ನೀವಾಗಿ ತಿಳುಕೊಳ್ಳೂತ್ತೀರಲ್ಲಾ.",
			"31": "<sup>31</sup> ಹಾಗೆಯೇ ಇವುಗಳಾಗುವದನ್ನು ನೋಡುವಾಗ ದೇವರ ರಾಜ್ಯವು ಹತ್ತಿರವಾಯಿತೆಂದು ನೀವು ತಿಳಿದುಕೊಳ್ಳಿರಿ.",
			"32": "<sup>32</sup> ಇವೆಲ್ಲಾ ಆಗುವ ತನಕ ಈ ಸಂತತಿಯು ಅಳಿದು ಹೋಗುವದೇ ಇಲ್ಲವೆಂದು ನಿಮಗೆ ಸತ್ಯವಾಗಿ ಹೇಳುತ್ತೇನೆ.",
			"33": "<sup>33</sup> ಭೂಮ್ಯಾಕಾಶಗಳು ಅಳಿದುಹೋಗುವವು, ಆದರೆ ನನ್ನ ಮಾತುಗಳು ಅಳಿದುಹೋಗುವದೇ ಇಲ್ಲ.",
			"34": "<sup>34</sup> ನಿಮ್ಮ ವಿಷಯದಲ್ಲಿ ಜಾಗರೂಕರಾಗಿರ್ರಿ. ಅತಿ ಭೋಜನದ ಮದದಿಂದಲೂ ಅಮಲಿನಿಂದಲೂ ಪ್ರಾಪಂಚಿಕವಾದ ಚಿಂತೆಗಳಿಂದಲೂ ನಿಮ್ಮ ಹೃದಯಗಳು ಭಾರವಾಗಿರಲಾಗಿ ಆ ದಿವಸವು ನಿಮ್ಮ ಮೇಲೆ ಉರುಲಿನಂತೆ ಫಕ್ಕನೆ ಬಂದೀತು.",
			"35": "<sup>35</sup> ಆ ದಿವಸವು ಭೂನಿವಾಸಿಗಳೆಲ್ಲರ ಮೇಲೆ ಬರುವದು.",
			"36": "<sup>36</sup> ಆದರೆ ಅಂದು ಬರುವುದಕ್ಕಿರುವ ಇವೆಲ್ಲವುಗಳಿಂದ ತಪ್ಪಿಸಿಕೊಳ್ಳುವದಕ್ಕೂ ಮನುಷ್ಯಕುಮಾರನ ಮುಂದೆ ನಿಂತುಕೊಳ್ಳುವದಕ್ಕೂ ನೀವು ಪೂರ್ಣ ಶಕ್ತರಾಗುವಂತೆ ಎಲ್ಲಾ ಕಾಲದಲ್ಲಿಯೂ ದೇವರಿಗೆ ವಿಜ್ಞಾಪನೆಮಾಡಿಕೊಳ್ಳುತ್ತಾ ಎಚ್ಚರವಾಗಿರ್ರಿ ಅಂದನು.",
			"37": "<sup>37</sup> ಯೇಸು ಹಗಲಿನಲ್ಲಿ ದೇವಾಲಯದಲ್ಲಿ ಉಪದೇಶಮಾಡುತ್ತಾ ರಾತ್ರಿ ಪಟ್ಟಣದ ಹೊರಗೆ ಹೋಗಿ ಎಣ್ಣೇ ಮರಗಳ ತೋಪು ಎನಿಸಿಕೊಳ್ಳುವ ಗುಡ್ಡದಲ್ಲಿ ಉಳಿದುಕೊಳ್ಳುತ್ತಾ ಇದ್ದನು.",
			"38": "<sup>38</sup> ಜನರೆಲ್ಲರು ಆತನ ಉಪದೇಶವನ್ನು ಕೇಳಬೇಕೆಂದು ಬೆಳಿಗ್ಗೆ ಬೇಗನೆ ಎದ್ದು ದೇವಾಲಯಕ್ಕೆ ಆತನ ಬಳಿಗೆ ಬರುತ್ತಿದ್ದರು."
		},
		"22": {
			"1": "<sup>1</sup> ಹುಳಿಯಿಲ್ಲದ ರೊಟ್ಟಿಯನ್ನು ತಿನ್ನುವ ಪಸ್ಕವೆಂಬ ಹಬ್ಬವು ಹತ್ತಿರವಾದಾಗ",
			"2": "<sup>2</sup> ಮಹಾಯಾಜಕರೂ ಶಾಸ್ತ್ರಿಗಳೂ ಯೇಸುವನ್ನು ಕೊಲ್ಲಬೇಕೆಂದಿದ್ದು ಜನರಿಗೆ ಹೆದರಿ ತಕ್ಕ ಮಾರ್ಗವನ್ನು ಹುಡುಕುತ್ತಿದ್ದರು.",
			"3": "<sup>3</sup> ಆಗ ಸೈತಾನನು ಹನ್ನೆರಡು ಮಂದಿಶಿಷ್ಯರಲ್ಲಿ ಒಬ್ಬನಾದ ಇಸ್ಕರಿಯೋತನೆಂಬ ಯೂದನಲ್ಲಿ ಪ್ರವೇಶಿಸಲು ಅವನು",
			"4": "<sup>4</sup> ಮಹಾಯಾಜಕರ ಬಳಿಗೂ ಕಾವಲಿನ ದಳವಾಯಿಗಳ ಬಳಿಗೂ ಹೋಗಿ ತಾನು ಯೇಸುವನ್ನು ಹಿಡುಕೊಡುವ ಉಪಾಯವನ್ನು ಕುರಿತು ಅವರ ಸಂಗಡ ಮಾತಾಡಿದನು.",
			"5": "<sup>5</sup> ಅವರು ಸಂತೋಷಪಟ್ಟು ನಿನಗೆ ಹಣ ಕೊಡುತ್ತೇವೆಂದು ಒಪ್ಪಂದ ಮಾಡಲು",
			"6": "<sup>6</sup> ಅವನು ಒಡಂಬಟ್ಟು ಆತನನ್ನು ಗದ್ದಲವಾಗದಂತೆ ಅವರಿಗೆ ಹಿಡುಕೊಡುವದಕ್ಕೆ ಅನುಕೂಲವಾದ ಸಮಯವನ್ನು ನೋಡುತ್ತಿದ್ದನು.",
			"7": "<sup>7</sup> ಹುಳಿಯಿಲ್ಲದ ರೊಟ್ಟಿಯನ್ನು ತಿನ್ನುವ ದಿವಸಗಳಲ್ಲಿ ಪಸ್ಕದ ಕುರಿಮರಿಯನ್ನು ಕೊಯ್ಯತಕ್ಕ ದಿವಸ ಬಂದಾಗ",
			"8": "<sup>8</sup> ಯೇಸು ಪೇತ್ರನಿಗೂ ಯೋಹಾನನಿಗೂ - ನೀವು ಹೋಗಿ ನಾವು ಪಸ್ಕದ ಊಟಮಾಡುವಂತೆ ಸಿದ್ಧತೆಮಾಡಿರಿ ಎಂಧು ಹೇಳಿ ಅವರನ್ನು ಕಳುಹಿಸಿದನು.",
			"9": "<sup>9</sup> ಅವರು - ನಾವು ಎಲ್ಲಿ ಸಿದ್ಧಮಾಡಬೇಕನ್ನುತ್ತೀ? ಎಂದು ಆತನನ್ನು ಕೇಳಲು",
			"10": "<sup>10</sup> ಆತನು ಅವರಿಗೆ - ನೋಡಿರಿ, ನೀವು ಪಟ್ಟಣದೊಳಕ್ಕೆ ಹೋದ ಮೇಲೆ ಒಬ್ಬ ಮನುಷ್ಯನು ತುಂಬಿದ ಕೊಡವನ್ನು ಹೊತ್ತುಕೊಂಡು ನಿಮ್ಮೆದುರಿಗೆ ಬರುವನು. ಅವನು ಹೋಗುವ ಮನೆಗೆ ನೀವು ಅವನ ಹಿಂದೆ ಹೋಗಿ",
			"11": "<sup>11</sup> ಆ ಮನೆಯ ಯಜಮಾನನಿಗೆ, 'ನನ್ನ ಶಿಷ್ಯರ ಸಂಗಡ ಪಸ್ಕದ ಊಟವನ್ನು ಮಾಡುವುದಕ್ಕಾಗಿ ಕೊಠಡಿ ಎಲ್ಲಿ, ಎಂದು ನಿನ್ನನ್ನು ವಿಚಾರಿಸಲು ನಮ್ಮ ಗುರು ಹೇಳಿದ್ದಾರೆ,' ಎಂದು ಹೇಳಿರಿ.",
			"12": "<sup>12</sup> ಅವನು ತಕ್ಕ ಪೀಠೋಪಕರಣಗಳಿಂದ ಅಲಂಕೃತವಾದ ಮೇಲ್ಮಾಳಿಗೆಯಲ್ಲಿ ದೊಡ್ಡ ಕೊಠಡಿಯನ್ನು ನಿಮಗೆ ತೋರಿಸುವನು; ಅಲ್ಲಿ ನೀವು ಭೋಜನಕ್ಕೆ ಸಿದ್ಧ ಮಾಡಿರಿ ಎಂದು ಹೇಳಿ ಕಳುಹಿಸಿದನು.",
			"13": "<sup>13</sup> ಅವರು ಹೊರಟು ಆತನು ತಮಗೆ ಹೇಳಿದಂತೆ ಕಂಡು ಪಸ್ಕ ಹಬ್ಬಕ್ಕೆ ಸಿದ್ದಮಾಡಿದರು.",
			"14": "<sup>14</sup> ಆ ಸಮಯ ಬಂದಾಗ ಆತನು ತನ್ನ ಹನ್ನೆರಡು ಮಂದಿ ಅಪೊಸ್ತಲರ ಸಂಗಡ ಊಟಕ್ಕೆ ಕುಳಿತುಕೊಂಡನು.",
			"15": "<sup>15</sup> ಆಗ ಆತನು ಅವರಿಗೆ - ನಾನು ಯಾತನೆಯನ್ನು ಅನುಭವಿಸುವುದಕ್ಕಿಂತ ಮೊದಲೇ ನಿಮ್ಮ ಸಂಗಡ ಈ ಪಸ್ಕದ ಊಟವನ್ನು ಮಾಡುವುದಕ್ಕೆ ಮನಃಪೂರ್ವಕವಾಗಿ ಬಯಸಿದ್ದೇನೆ.",
			"16": "<sup>16</sup> ಇನ್ನು ದೇವರ ರಾಜ್ಯದಲ್ಲಿ ಇದು ನೆರವೇರುವ ತನಕ ನಾನು ಇನ್ನು ಪಸ್ಕದ ಊಟವನ್ನು ಮಾಡುವದೇ ಇಲ್ಲ ಎಂದು ಹೇಳಿದನು.",
			"17": "<sup>17</sup> ಆ ಮೇಲೆ ಆತನು (ದ್ರಾಕ್ಷಾರಸದ) ಪಾತ್ರೆಯನ್ನು ತೆಗೆದುಕೊಂಡು ದೇವರ ಸ್ತೋತ್ರಮಾಡಿ - ಇದನ್ನು ತೆಗೆದುಕೊಂಡು ಹಂಚಿಕೊಂಡುಕುಡಿಯಿರಿ.",
			"18": "<sup>18</sup> ಇಂದಿನಿಂದ ದೇವರ ರಾಜ್ಯವು ಬರುವ ತನಕ ನಾನು ದ್ರಾಕ್ಷಾರಸವನ್ನು ಕುಡಿಯುವದೇ ಇಲ್ಲವೆಂದು ನಿಮಗೆ ಹೇಳುತ್ತೇನೆ ಅಂದನು.",
			"19": "<sup>19</sup> ಬಳಿಕ ಆತನು ರೊಟ್ಟಿಯನ್ನು ತೆಗೆದುಕೊಂಡು ಸ್ತೋತ್ರಮಾಡಿ ಮುರಿದು ಅವರಿಗೆ ಕೊಟ್ಟು - ಇದು ನಿಮಗೋಸ್ಕರ ಕೊಟ್ಟಿರುವ ನನ್ನ ದೇಹ; ನನ್ನನ್ನು ನೆನಪಿಸಿಕೊಳ್ಳುವುದಕ್ಕೋಸ್ಕರ ಹೀಗೆ ಮಾಡಿರಿ ಅಂದನು.",
			"20": "<sup>20</sup> ಊಟವಾದ ಮೇಲೆ ಆತನು ಅದೇ ರೀತಿಯಾಗಿ ಪಾತ್ರೆಯನ್ನು ತೆಗೆದುಕೊಂಡು - ಈ ಪಾತ್ರೆಯು ನಿಮಗೋಸ್ಕರ ಸುರಿಸಲ್ಪಡುವ ನನ್ನ ರಕ್ತದಿಂದ ಸ್ಥಾಪಿತವಾಗುವ ಹೊಸ ಒಡಂಬಡಿಕೆಯನ್ನು ಸೂಚಿಸುತ್ತದೆ.",
			"21": "<sup>21</sup> ಆದರೂ ನೋಡಿರಿ, ನನ್ನನ್ನು ಹಿಡುಕೊಡುವವನು ನನ್ನೊಡನೆ ಮೇಜಿನ ಮೇಲೆ ಕೈಯಿಟ್ಟು ಊಟಮಾಡುತ್ತಿದ್ದಾನೆ.",
			"22": "<sup>22</sup> ನಿರ್ಣಯವಾಗಿರುವ ಪ್ರಕಾರ ಮನುಷ್ಯಕುಮಾರನು ಹೊರಟುಹೋಗುತ್ತಾನೆ ಸರಿ; ಆದರೆ ಆತನನ್ನು ಹಿಡುಕೊಡುವ ಮನುಷ್ಯನ ಗತಿಯನ್ನು ಏನು ಹೇಳಲಿ ಅಂದನು.",
			"23": "<sup>23</sup> ಇದನ್ನು ಕೇಳಿ ಅವರು - ಇಂಥ ಕೆಲಸವನ್ನು ಮಾಡಬೇಕೆಂದಿರುವವನು ನಮ್ಮಲ್ಲಿ ಯಾವನಾಗಿರಬಹುದು ಎಂದು ಒಬ್ಬರನ್ನೊಬ್ಬರು ಕೇಳ ತೊಡಗಿದರು.",
			"24": "<sup>24</sup> ಇದಲ್ಲದೆ ತಮ್ಮಲ್ಲಿ ಯಾವನು ಹೆಚ್ಚಿನವನೆನಿಸಿಕೊಳ್ಳುವವನು ಎಂಬ ವಿಷಯದಲ್ಲಿ ಅವರೊಳಗೆ ಚರ್ಚೆ ಹುಟ್ಟಿತು.",
			"25": "<sup>25</sup> ಆಗ ಆತನು ಅವರಿಗೆ - ಅನ್ಯಜನಾಂಗದ ಅರಸರು ತಮ್ಮ ತಮ್ಮ ಜನಗಳ ಮೇಲೆ ದೊರೆತನ ಮಾಡುತ್ತಾರೆ, ಮತ್ತು ಅವರ ಮೇಲೆ ಅಧಿಕಾರದಿಂದ ಆಡಳಿತ ನಡಿಸುವವರು ಉಪಕಾರಿಗಳೆಂದು ಕರೆಸಿಕೊಳ್ಳುತ್ತಾರೆ.",
			"26": "<sup>26</sup> ನೀವು ಹಾಗಿರಬಾರದು; ನಿಮ್ಮಲ್ಲಿ ಹೆಚ್ಚಿನವನು ಚಿಕ್ಕವನಂತಾಗಬೇಕು, ಮುಖ್ಯಸ್ಥನು ಸೇವಕನಂತಾಗಬೇಕು.",
			"27": "<sup>27</sup> ಯಾರು ಹೆಚ್ಚಿನವನು? ಊಟಕ್ಕೆ ಕುಳಿತವನೋ ಸೇವೆಮಾಡುವವನೋ? ಊಟಕ್ಕೆ ಕುಳಿತವನಲ್ಲವೇ. ಆದರೆ ನಾನು ನಿಮ್ಮಲ್ಲಿ ಸೇವೆ ಮಾಡುವವನಂತಿದ್ದೇನೆ.",
			"28": "<sup>28</sup> ನೀವು ನನ್ನ ಕಷ್ಟಗಳಲ್ಲಿ ನನ್ನ ಸಂಗಡ ಎಡೆಬಿಡದೆ ಇದ್ದವರು.",
			"29": "<sup>29</sup> ಆದದರಿಂದ ನನ್ನ ತಂದೆಯು ನನಗೆ ರಾಜ್ಯವನ್ನು ನಿಯಮಿಸಿರುವ ಪ್ರಕಾರ ನಾನು ನಿಮಗೂ ನೇಮಿಸುತ್ತೇನೆ;",
			"30": "<sup>30</sup> ನನ್ನ ರಾಜ್ಯದಲ್ಲಿ ನೀವು ನನ್ನ ಮೇಜಿನ ಮೇಲೆ ಊಟಮಾಡುವಿರಿ, ಕುಡಿಯುವಿರಿ; ಮತ್ತು ಸಿಂಹಾಸನಗಳ ಮೇಲೆ ಕುಳಿತುಕೊಂಡು ಇಸ್ರಾಯೇಲಿನ ಹನ್ನೆರಡು ಕುಲಗಳಿಗೆ ನ್ಯಾಯತೀರ್ಪುಮಾಡುವಿರಿ.",
			"31": "<sup>31</sup> ಸೀಮೋನನೇ, ಸೀಮೋನನೇ, ನೋಡು, ಸೈತಾನನು ನಿಮನ್ನು ಗೋಧಿಯಂತೆ ಕೇರಬೇಕೆಂದು ಅಪ್ಪಣೆ ಕೇಳಿಕೊಂಡನು;",
			"32": "<sup>32</sup> ಆದರೆ ನಿನ್ನ ನಂಬಿಕೆ ಕುಂದಿಹೋಗಬಾರದೆಂದು ನಾನು ನಿನ್ನ ವಿಷಯದಲ್ಲಿ ದೇವರಿಗೆ ವಿಜ್ಞಾಪನೆಮಾಡಿ ಕೊಂಡೆನು. ನೀನು ತಿರುಗಿಕೊಂಡ ಮೇಲೆ ನಿನ್ನ ಸಹೋದರರನ್ನು ದೃಢಪಡಿಸು ಎಂದು ಹೇಳಿದನು.",
			"33": "<sup>33</sup> ಅದರೆ ಅವನು - ಕರ್ತನೇ, ನಿನ್ನ ಸಂಗಡ ಸೆರೆಮನೆಗೆ ಹೋಗುವದಕ್ಕೂ ಸಾಯುವುದಕ್ಕೂ ನಾನು ಸಿದ್ಧನಾಗಿದ್ದೇನೆ ಅನ್ನಲು ಆತನು - ಪೇತ್ರನೇ,",
			"34": "<sup>34</sup> ನನ್ನ ವಿಷಯದಲ್ಲಿ - ಇವನನ್ನು ಅರಿಯೆನೆಂದು ನೀನು ಮೂರು ಸಾರಿ ಹೇಳುವ ತನಕ ಈಹೊತ್ತು ಕೋಳಿ ಕೂಗುವುದಿಲ್ಲವೆಂದು ನಿನಗೆ ಹೇಳುತ್ತೇನೆ ಅಂದನು.",
			"35": "<sup>35</sup> ಮತ್ತು ಆತನು - ನಿಮ್ಮನ್ನು ನಾನು ಹಣ, ಕೈಚೀಲ ಜೋಡುಗಳಿಲ್ಲದೆ ಕಳುಹಿಸಿದಾಗ ನಿಮಗೆ ಏನಾದರೂ ಕೊರತೆಯಾಯಿತೋ ಎಂದು ಕೇಳಲು ಅವರು ಏನೂ ಇಲ್ಲವೆಂದು ಉತ್ತರಕೊಟ್ಟರು.",
			"36": "<sup>36</sup> ಅದಕ್ಕೆ ಆತನು - ಈಗಲಾದರೋ ಹಣವಿದ್ದವನು ಅದನ್ನು ತೆಗೆದುಕೊಳ್ಳಲಿ, ಕೈಚೀಲವಿರುವವನು ಅದನ್ನು ತೆಗೆದುಕೊಳ್ಳಲಿ, ಮತ್ತು ಕತ್ತಿಯಿಲ್ಲದವನು ತನ್ನ ಮೇಲಂಗಿಯನ್ನು ಮಾರಿ ಒಂದು ಕತ್ತಿಯನ್ನು ಕೊಂಡುಕೊಳ್ಳಲಿ.",
			"37": "<sup>37</sup> ಯಾಕೆಂದರೆ - ಆತನು ಅಪರಾಧಿಗಳಲ್ಲಿ ಒಬ್ಬನಂತೆ ಎಣಿಸಲ್ಪಟ್ಟನು ಎಂದು ಬರೆದಿರುವ ಮಾತು ನನ್ನಲ್ಲಿ ನೆರವೇರಬೇಕಾಗಿದೆ ಎಂದು ನಿಮಗೆ ಹೇಳುತ್ತೇನೆ; ನನ್ನ ವಿಷಯವಾದದ್ದು ಕೊನೆಗಾಣಬೇಕು ಎಂದು ಹೇಳಿದನು.",
			"38": "<sup>38</sup> ಅವರು - ಕರ್ತನೇ, ಇಗೋ ಇಲ್ಲಿ ಎರಡು ಕತ್ತಿಗಳಿವೆ ಅನ್ನಲು ಆತನು ಅವರಿಗೆ - ಅಷ್ಟು ಸಾಕು ಅಂದನು.",
			"39": "<sup>39</sup> ತರುವಾಯ ಆತನು ಪ್ರತಿದಿನದಂತೆ ಹೊರಟು ಎಣ್ಣೇಮರಗಳ ಗುಡ್ಡಕ್ಕೆ ಹೋದನು. ಶಿಷ್ಯರೂ ಆತನ ಹಿಂದೆ ಹೋದರು.",
			"40": "<sup>40</sup> ಆತನು ಆ ಕ್ಲುಪ್ತ ಸ್ಥಳಕ್ಕೆ ಬಂದಾಗ ಅವರಿಗೆ - ನೀವು ಶೋಧನೆಗೆ ಒಳಗಾಗದಂತೆ ದೇವರನ್ನು ಪ್ರಾರ್ಥಿಸಿರಿ ಎಂದು ಹೇಳಿದನು.",
			"41": "<sup>41</sup> ಆ ಮೇಲೆ ಆತನು ಅವರನ್ನು ಬಿಟ್ಟು ಸ್ವಲ್ಪ ದೂರ ಹೋಗಿ ಮೊಣಕಾಲೂರಿ - ತಂದೆಯೇ,",
			"42": "<sup>42</sup> ಈ ಪಾತ್ರೆಯನ್ನು ನನ್ನಿಂದ ತೊಲಗಿಸು; ಹೇಗೂ ನನ್ನ ಚಿತ್ತವಲ್ಲ, ನಿನ್ನ ಚಿತ್ತದಂತೆಯೇ ಆಗಲಿ ಅಂದನು.",
			"43": "<sup>43</sup> ಆಗ ಪರಲೋಕದಿಂದ ಬಂದ ಒಬ್ಬ ದೇವದೂತನು ಆತನಿಗೆ ಕಾಣಿಸಿಕೊಂಡು ಆತನನ್ನು ಬಲಪಡಿಸಿದನು.",
			"44": "<sup>44</sup> ಯೇಸು ತೀವ್ರವಾದ ಮನೋವ್ಯಥೆಯುಳ್ಳವನಾಗಿ ವೇದನೆಯಿಂದ ಪ್ರಾರ್ಥಿಸುತ್ತಿರಲಾಗಿ ಆತನ ಬೆವರು ಭೂಮಿಗೆ ಬೀಳುತ್ತಿರುವ ರಕ್ತದ ದೊಡ್ಡ ಹನಿಗಳೋಪಾದಿಯಲ್ಲಿತ್ತು.",
			"45": "<sup>45</sup> ಆತನು ಪ್ರಾರ್ಥನೆ ಮುಗಿಸಿ ಎದ್ದು ಶಿಷ್ಯರ ಬಳಿಗೆ ಬಂದು ಅವರು, ದುಃಖದಿಂದ ಬಳಲಿಹೋದವರಾಗಿ ನಿದ್ರೆಹತ್ತಿರುವದನ್ನು ಕಂಡು ಅವರಿಗೆ -",
			"46": "<sup>46</sup> ನೀವು ನಿದ್ರೆಮಾಡುವುದೇನು? ಏಳಿರಿ, ಶೋಧನೆಗೆ ಒಳಗಾಗದಂತೆ ಪ್ರಾರ್ಥಿಸಿರಿ ಎಂದು ಹೇಳಿದನು.",
			"47": "<sup>47</sup> ಆತನು ಇನ್ನೂ ಮಾತಾಡುತ್ತಿರುವಾಗಲೇ ಇಗೋ, ಜನರ ಗುಂಪು ಕಾಣಿಸಿತು. ಹನ್ನೆರಡು ಮಂದಿ ಶಿಷ್ಯರಲ್ಲಿ ಒಬ್ಬನಾದ ಯೂದನೆಂಬುವನು ಆ ಜನರ ಗುಂಪಿನ ಮುಂದೆ ನಡೆಯುತ್ತಾ,",
			"48": "<sup>48</sup> ಯೇಸುವಿಗೆ ಮುದ್ದಿಡುವುದಕ್ಕಾಗಿ ಆತನ ಬಳಿಗೆ ಬರಲು ಯೇಸು ಅವನಿಗೆ - ಯೂದನೇ, ಮುದ್ದಿಟ್ಟು ಮನುಷ್ಯಕುಮಾರನನ್ನು ಹಿಡಿದುಕೊಡುತ್ತೀಯಾ ಎಂದು ಕೇಳಿದನು.",
			"49": "<sup>49</sup> ಆತನ ಸುತ್ತಲಿದ್ದವರು ಘಟನೆಯ ಸೂಕ್ಷ್ಮತೆಯನ್ನು ತಿಳಿದು ತಕ್ಷಣ - ಕರ್ತನೇ, ನಾವು ಕತ್ತಿಯಿಂದ ಹೊಡೆಯೊಣವೋ ಎಂದು ಕೇಳಿದರು.",
			"50": "<sup>50</sup> ಅಷ್ಟರಲ್ಲಿ ಅವರಲ್ಲಿ ಒಬ್ಬನು ಮಹಾಯಾಜಕನ ಆಳನ್ನು ಕತ್ತಿಯಿಂದ ಹೊಡೆದು ಅವನ ಬಲಗಿವಿಯನ್ನು ಕತ್ತರಿಸಿಹಾಕಿದನು.",
			"51": "<sup>51</sup> ಆದರೆ ಯೇಸು - ಇಷ್ಟಕ್ಕೇ ಬಿಡಿರಿ ಎಂದು ಹೇಳಿ ಆ ಆಳಿನ ಕಿವಿಯನ್ನು ಮುಟ್ಟಿ ವಾಸಿಮಾಡಿದನು.",
			"52": "<sup>52</sup> ಬಳಿಕ ಯೇಸು ತನ್ನನ್ನು ಹಿಡಿಯುವದಕ್ಕೆ ಬಂದ ಮಹಾಯಾಜಕರಿಗೂ ದೇವಾಲಯದ ಕಾವಲಿನ ದಳವಾಯಿಗಳಿಗೂ ಸಭೆಯ ಹಿರಿಯರಿಗೂ - ಕಳ್ಳನನ್ನು ಹಿಡಿಯುವದಕ್ಕೆ ಬಂದಂತೆ ಕತ್ತಿಗಳನ್ನೂ ದೊಣ್ಣೆಗಳನ್ನೂ ತೆಗೆದುಕೊಂಡು ಬಂದಿರುವಿರಾ?",
			"53": "<sup>53</sup> ನಾನು ಪ್ರತಿದಿನ ದೇವಾಲಯದಲ್ಲಿ ನಿಮ್ಮ ಸಂಗಡ ಇದ್ದಾಗ ನೀವು ನನ್ನ ಮೇಲೆ ಕೈಹಾಕಲಿಲ್ಲ; ಆದರೆ ಇದು ನಿಮ್ಮ ಕಾಲ, ಇದು ಅಂಧಕಾರದ ದೊರೆತನದ ಕಾಲ ಎಂದು ಹೇಳಿದನು.",
			"54": "<sup>54</sup> ಅವರು ಆತನನ್ನು ಹಿಡಿದು ಮಹಾ ಯಾಜಕನ ಮನೆಯೊಳಕ್ಕೆ ಕರೆದುಕೊಂಡು ಹೋದರು; ಪೇತ್ರನು ದೂರದಿಂದ ಅವರನ್ನು ಹಿಂಬಾಲಿಸುತ್ತಿದ್ದನು.",
			"55": "<sup>55</sup> ಅಂಗಳದ ನಡುವೆ ಅವರು ಬೆಂಕಿ ಹೊತ್ತಿಸಿ ಒಟ್ಟಾಗಿ ಕೂಳಿತುಕೊಂಡಿರಲು ಅವರ ನಡುವೆ ಪೇತ್ರನೂ ಕುಳಿತುಕೊಂಡನು.",
			"56": "<sup>56</sup> ಅವನು ಬೆಳಕಿಗೆ ಮುಖವಾಗಿ ಕುಳಿತಿರುವಾಗ ಒಬ್ಬ ದಾಸಿಯು ಅವನನ್ನು ಕಂಡು ಅವನನ್ನು ದೃಷ್ಟಿಸಿ ನೋಡಿ - ಇವನು ಸಹ ಅವನ ಸಂಗಡ ಇದ್ದವನು ಅಂದಳು.",
			"57": "<sup>57</sup> ಆದರೆ ಅವನು - ನಾನು ಅವನನ್ನು ಅರಿಯೆನಮ್ಮಾ ಎಂದು ಹೇಳಿದನು.",
			"58": "<sup>58</sup> ಸ್ವಲ್ಪಹೊತ್ತಿನ ಮೇಲೆ ಮತ್ತೊಬ್ಬನು ಬಂದು ಅವನನ್ನು ಕಂಡು - ನೀನೂ ಸಹ ಅವರಲ್ಲಿ ಒಬ್ಬನು ಅನ್ನಲು ಪೇತ್ರನು - ನಾನಲ್ಲಪ್ಪಾ ಅಂದನು.",
			"59": "<sup>59</sup> ಹೆಚ್ಚು ಕಡಿಮೆ ಒಂದು ಘಂಟೆ ಹೊತ್ತಿನ ಮೇಲೆ ಇನ್ನೊಬ್ಬನು - ನಿಶ್ಚಯವಾಗಿ ಇವನು ಸಹ ಅವನ ಸಂಗಡ ಇದ್ದವನು, ಇವನು ಗಲಿಲಾಯದವನಷ್ಟೆ ಎಂದು ದೃಡವಾಗಿ ಹೇಳಿದನು.",
			"60": "<sup>60</sup> ಆದರೆ ಪೇತ್ರನು - ನೀನು ಹೇಳುವದೇನೋ ನಾನರಿಯೆನಪ್ಪಾ ಅಂದನು. ಅವನು ಈ ಮಾತನ್ನು ಆಡುತ್ತಿರುವಾಗಲೇ ಕೋಳಿ ಕೂಗಿತು.",
			"61": "<sup>61</sup> ಸ್ವಾಮಿಯು ಹಿಂತಿರುಗಿಕೊಂಡು ಪೇತ್ರನನ್ನು ದೃಷ್ಟಿಸಿ ನೋಡಿದನು. ಆಗ ಪೇತ್ರನು - ಈಹೊತ್ತು ಕೋಳಿ ಕೂಗುವದಕ್ಕಿಂತ ಮುಂಚೆ ಮೂರು ಸಾರಿ ನನ್ನ ವಿಷಯವಾಗಿ ಅವನನ್ನು ಅರಿಯೆ ನೆಂಬದಾಗಿ ಹೇಳುವಿ ಎಂದು ಸ್ವಾಮಿ ನುಡಿದ ಮಾತನ್ನು ನೆನಪಿಸಿಕೊಂಡು",
			"62": "<sup>62</sup> ಹೊರಗೆ ಹೋಗಿ ತುಂಬಾ ದುಃಖದಿಂದ ಅತ್ತನು.",
			"63": "<sup>63</sup> ಆ ಮೇಲೆ ಯೇಸುವನ್ನು ಹಿಡಿದವರು ಆತನನ್ನು ಹಾಸ್ಯಮಾಡುತ್ತಾ",
			"64": "<sup>64</sup> ಆತನಿಗೆ ಮುಸುಕುಹಾಕಿ ಹೊಡೆದು - ನಿನ್ನನ್ನು ಹೊಡೆದವನಾರು? ಪ್ರವಾದನೆ ಹೇಳು ಎಂದು ಆತನನ್ನು ಕೇಳಿದರು.",
			"65": "<sup>65</sup> ಆತನ ಕುರಿತು ಇನ್ನೂ ಅನೇಕ ದೂಷಣೆಯ ಮಾತುಗಳನ್ನಾಡಿದರು.",
			"66": "<sup>66</sup> ಬೆಳಗಾಗುವಾಗ ಯೆಹೂದ್ಯರ ಹಿರೀಸಭೆಯವರು ಅಂದರೆ ಮಹಾಯಾಜಕರೂ ಶಾಸ್ತ್ರಿಗಳೂ ಕೂಡಿ ಆತನನ್ನು ತಮ್ಮ ನ್ಯಾಯವಿಚಾರಣಾಸಭೆಗೆ ತೆಗೆದುಕೊಂಡು ಬಂದು -",
			"67": "<sup>67</sup> ನೀನು ಕ್ರಿಸ್ತನಾಗಿದ್ದರೆ ನಮಗೆ ಹೇಳು ಅಂದರು. ಆತನು ಅವರಿಗೆ - ನಾನು ನಿಮಗೆ ಹೇಳಿದರೆ ನೀವು ನಂಬುವದಿಲ್ಲ,",
			"68": "<sup>68</sup> ನಾನು ನಿಮ್ಮನ್ನು ಏನಾದರೂ ಕೇಳಿದರೆ ನೀವು ಉತ್ತರಕೊಡುವದಿಲ್ಲ.",
			"69": "<sup>69</sup> ಆದರೆ ಇಂದಿನಿಂದ ಮನುಷ್ಯಕುಮಾರನು ಸರ್ವಶಕ್ತನಾದ ದೇವರ ಬಲಗಡೆಯಲ್ಲಿ ಆಸೀನನಾಗಿರುವನು ಎಂದು ಹೇಳಿದನು.",
			"70": "<sup>70</sup> ಆಗ ಎಲ್ಲರು - ಹಾಗಾದರೆ ನೀನು ದೇವರ ಕುಮಾರನೋ ಎಂದು ಕೇಳಲು ಆತನು ಅವರಿಗೆ - ನನ್ನನ್ನು ಕುರಿತು ದೇವಕುಮಾರನೆಂದು ನೀವೇ ಹೇಳಿದ್ದೀರಿ ಎಂದು ಹೇಳಿದನು.",
			"71": "<sup>71</sup> ಅದಕ್ಕೆ ಅವರು - ನಮಗೆ ಸಾಕ್ಷಿ ಇನ್ನುಯಾತಕ್ಕೆ ಬೇಕು? ನಾವೇ ಇವನ ಬಾಯಿಂದ ಕೇಳಿದೆವಲ್ಲಾ ಅಂದರು."
		},
		"23": {
			"1": "<sup>1</sup> ಅಲ್ಲಿ ಕೂಡಿದ್ದವರೆಲ್ಲರೂ ಎದ್ದು ಆತನನ್ನು ಪಿಲಾತನ ಬಳಿಗೆ ಕರೆದುಕೊಂಡು ಹೋಗಿ –",
			"2": "<sup>2</sup> ಇವನು ತಾನೇ ಕ್ರಿಸ್ತನು, ಅರಸನಾಗಿದ್ದೇನೆಂದು ಹೇಳುತ್ತಾ ಕೈಸರನಿಗೆ ತೆರಿಗೆ ಕೊಡಬಾರದೆಂದು ಬೋಧಿಸುತ್ತಾ ನಮ್ಮ ದೇಶದವರ ಮನಸ್ಸು ಕೆಡಿಸುವುದನ್ನು ನಾವು ಕಂಡಿದ್ದೇವೆ ಎಂಬುದಾಗಿ ಆತನ ಮೇಲೆ ದೂರು ಹೇಳುವುದಕ್ಕೆ ತೊಡಗಿದರು.",
			"3": "<sup>3</sup> ಪಿಲಾತನು ಯೇಸುವನ್ನು ನೀನು ಯೆಹೂದ್ಯರ ಅರಸನೋ ಎಂದು ಕೇಳಲು, ಆತನು – ನೀನೇ ಹೇಳಿದ್ದೀ ಎಂದು ಉತ್ತರ ಕೊಟ್ಟನು.",
			"4": "<sup>4</sup> ಪಿಲಾತನು ಮಹಾಯಾಜಕರಿಗೂ ಜನರ ಗುಂಪಿಗೂ - ಈ ಮನುಷ್ಯನಲ್ಲಿ ನನಗೆ ಯಾವ ಅಪರಾಧವೂ ಕಾಣಿಸುತ್ತಿಲ್ಲವೆಂದು ಹೇಳಿದನು.",
			"5": "<sup>5</sup> ಅದಕ್ಕೆ ಅವರು – ಗಲಿಲಾಯದಿಂದ ಮೊದಲುಗೊಂಡು ಇಲ್ಲಿಯವರೆಗೂ ಯೂದಾಯ ಸೀಮೆಯಲ್ಲೆಲ್ಲಾ ಇವನು ಬೋಧನೆ ಮಾಡುತ್ತಾ ಜನರ ಮನಸ್ಸನ್ನು ಕದಲಿಸುತ್ತಾನೆ ಎಂದು ಇನ್ನೂ ಒತ್ತಿ ಹೇಳಿದರು.",
			"6": "<sup>6</sup> ಇದನ್ನು ಪಿಲಾತನು ಕೇಳಿ – ಈ ಮನುಷ್ಯನು ಗಲಿಲಾಯದವನೋ ಎಂದು ವಿಚಾರಿಸಿ.",
			"7": "<sup>7</sup> ಈತನು ಹೆರೋದನ ಅಧಿಕಾರಕ್ಕೆ ಒಳಪಟ್ಟವನು ಎಂದು ತಿಳಿದುಕೊಂಡು ಯೇಸುವನ್ನು ಹೆರೋದನ ಬಳಿಗೆ ಕಳುಹಿಸಿದನು. ಹೆರೋದನು ಆ ದಿವಸಗಳಲ್ಲಿ ಯೆರೂಸಲೇಮಿನಲ್ಲೇ ಇದ್ದನು.",
			"8": "<sup>8</sup> ಹೆರೋದನು ಯೇಸುವನ್ನು ನೋಡಿದಾಗ ಬಹಳ ಸಂತೋಷಪಟ್ಟನು. ಯಾಕೆಂದರೆ ಆತನ ವಿಷಯವಾಗಿ ಅನೇಕ ಸಂಗತಿಗಳನ್ನು ಕೇಳಿದ್ದರಿಂದ ಆತನನ್ನು ಕಾಣಬೇಕೆಂದು ಬಹು ಕಾಲದಿಂದ ಅಪೇಕ್ಷಿಸಿದ್ದನು. ಆತನಿಂದ ಯಾವುದಾದರೂ ಒಂದು ಸೂಚಕ ಕಾರ್ಯವಾಗುವುದನ್ನು ನೋಡಬೇಕೆಂದು ನಿರೀಕ್ಷಿಸಿದನು.",
			"9": "<sup>9</sup> ಹೆರೋದನು ಅನೇಕ ಮಾತುಗಳಿಂದ ಯೇಸುವನ್ನು ಪ್ರಶ್ನೆ ಮಾಡಿದಾಗ್ಯೂ ಆತನು ಅವನಿಗೆ ಏನೂ ಉತ್ತರ ಕೊಡಲಿಲ್ಲ.",
			"10": "<sup>10</sup> ಮಹಾಯಾಜಕರೂ ಶಾಸ್ತ್ರಿಗಳೂ ನಿಂತುಕೊಂಡು ಆತನ ಕುರಿತು ಬಹು ತೀವ್ರವಾಗಿ ಆರೋಪಿಸುತ್ತಿದ್ದರು.",
			"11": "<sup>11</sup> ಕಡೆಗೆ ಹೆರೋದನು ತನ್ನ ಸಿಪಾಯಿಗಳನ್ನು ಕೂಡಿಕೊಂಡು ಯೇಸುವನ್ನು ಅವಮಾನಗೊಳಿಸಿ, ಅಪಹಾಸ್ಯ ಮಾಡಿ ಶೋಭಾಯಮಾನವಾದ ಉಡುಪನ್ನು ಹಾಕಿಸಿ ಪಿಲಾತನ ಬಳಿಗೆ ತಿರುಗಿ ಕಳುಹಿಸಿಕೊಟ್ಟನು.",
			"12": "<sup>12</sup> ಅದೇ ದಿನ ಹೆರೋದನು ಪಿಲಾತನೂ ಒಬ್ಬರಿಗೊಬ್ಬರು ಸ್ನೇಹಿತರಾದರು. ಅದಕ್ಕಿಂತ ಮೊದಲು ಅವರಿಬ್ಬರೂ ವೈರಿಗಳಾಗಿದ್ದರು.",
			"13": "<sup>13</sup> ತರುವಾಯ ಪಿಲಾತನು ಮಹಾಯಾಜಕರನ್ನೂ ಅಧಿಕಾರಿಗಳನ್ನೂ ಪ್ರಜೆಗಳನ್ನೂ ಒಟ್ಟಾಗಿ ಕರಸಿ ಅವರಿಗೆ –",
			"14": "<sup>14</sup> ಈ ಮನುಷ್ಯನು ಪ್ರಜೆಗಳನ್ನು ತಿರಿಗಿ ಬೀಳುವಂತೆ ಮಾಡುವವನು ಎಂದು ಈತನನ್ನು ನನ್ನ ಬಳಿಗೆ ಕರತಂದಿರಲ್ಲಾ. ನೀವು ಈತನ ಮೇಲೆ ಮಾಡಿದ ದೂರುಗಳ ವಿಷಯವಾಗಿ ನಾನು ನಿಮ್ಮ ಮುಂದೆಯೇ ವಿಚಾರಣೆ ಮಾಡಿದರೂ ಈತನಲ್ಲಿ ಒಂದು ತಪ್ಪಾದರೂ ನನಗೆ ಕಾಣಲಿಲ್ಲ.",
			"15": "<sup>15</sup> ಹೆರೋದನು ಈತನನ್ನು ಹಿಂತಿರುಗಿ ನಮ್ಮ ಬಳಿಗೆ ಕಳುಹಿಸಿದನಲ್ಲಾ. ಆದದರಿಂದ ಈತನು ಮರಣದಂಡನೆಗೆ ಯೋಗ್ಯವಾದದ್ದೇನೂ ಮಾಡಿದವನಲ್ಲವೆಂದಾಯಿತು.",
			"16": "<sup>16</sup> ಆದುದರಿಂದ ನಾನು ಈತನನ್ನು ಹೊಡಿಸಿ ಬಿಟ್ಟು ಬಿಡುತ್ತೇನೆ ಎಂದು ಹೇಳಿದನು.",
			"17": "<sup>17</sup> ಆದರೆ ಅವರು – ಅವನನ್ನು ಕೊಲ್ಲಿಸು",
			"18": "<sup>18</sup> ನಮಗೆ ಬರಬ್ಬನನ್ನು ಬಿಟ್ಟುಕೊಡು ಎಂದು ಒಟ್ಟಾಗಿ ಕೂಗಿಕೊಂಡರು.",
			"19": "<sup>19</sup> ಈ ಬರಬ್ಬನನ್ನು ಆ ಪಟ್ಟಣದಲ್ಲಿ ನಡೆದ ಒಂದು ದಂಗೆಯ ನಿಮಿತ್ತವಾಗಿಯೂ ಕೊಲೆಯ ನಿಮಿತ್ತವಾಗಿಯೂ ಸೆರೆಮನೆಯಲ್ಲಿ ಹಾಕಿದ್ದರು.",
			"20": "<sup>20</sup> ಪಿಲಾತನು ಯೇಸುವನ್ನು ಬಿಡಿಸಬೇಕೆಂದು ಮನಸ್ಸುಳ್ಳವನಾಗಿದ್ದು ತಿರಿಗಿ ಅವರ ಸಂಗಡ ಮಾತನಾಡಿದನು.",
			"21": "<sup>21</sup> ಆದರೆ ಅವರು – ಅವನನ್ನು ಶಿಲುಬೆಗೆ ಹಾಕಿಸು. ಶಿಲುಬೆಗೆ ಹಾಕಿಸು. ಎಂದು ಕೂಗುತ್ತಿದ್ದರು.",
			"22": "<sup>22</sup> ಅವನು ಮೂರನೆಯ ಸಾರಿ - ಯಾಕೆ? ಕೆಟ್ಟದ್ದೇನು ಮಾಡಿದನು? ನಾನು ಇವನಲ್ಲಿ ಮರಣದಂಡನೆಗೆ ತಕ್ಕ ಅಪರಾಧವೇನೂ ಕಾಣಲಿಲ್ಲ ಆದ್ದರಿಂದ ಇವನನ್ನು ಹೊಡಿಸಿ ಬಿಟ್ಟುಬಿಡುತ್ತೇನೆ ಎಂದು ಅವರಿಗೆ ಹೇಳಿದನು.",
			"23": "<sup>23</sup> ಆದರೆ ಅವರು ಜೋರಾಗಿ ಕೂಗುತ್ತಾ - ಅವನನ್ನು ಶಿಲುಬೆಗೆ ಹಾಕಿಸಬೇಕೆಂದು ಕೇಳಿಕೊಂಡು ಬಲವಂತ ಮಾಡಿದರು. ಅವರ ಕೂಗಾಟವು ಗೆದ್ದಿತು.",
			"24": "<sup>24</sup> ಆಗ ಪಿಲಾತನು – ಅವರು ಕೇಳಿಕೊಂಡಂತೆ ಆಗಲಿ ಎಂದು ನಿರ್ಣಯಿಸಿ",
			"25": "<sup>25</sup> ದಂಗೆ ಕೊಲೆಗಳ ನಿಮಿತ್ತವಾಗಿ ಸೆರೆಮನೆಯಲ್ಲಿ ಬಿದ್ದ ಆ ಮನುಷ್ಯನನ್ನು ಅವರು ಕೇಳಿಕೊಳ್ಳುತ್ತಿದ್ದ ಪ್ರಕಾರ ಬಿಟ್ಟುಕೊಟ್ಟು ಯೇಸುವನ್ನು ಅವರ ಚಿತ್ತಾನುಸಾರ ದಂಡಿಸಲು ಒಪ್ಪಿಸಿದನು.",
			"26": "<sup>26</sup> ಅವರು ಯೇಸುವನ್ನು ಕರೆದುಕೊಂಡು ಹೋಗುತ್ತಿರುವಾಗ ಹೊಲದಿಂದ ಬರುತ್ತಿದ್ದ ಕುರೇನೆಪಟ್ಟಣದ ಸೀಮೋನನೆಂಬುವನನ್ನು ಕರೆದು ಶಿಲುಬೆಯನ್ನು ಅವನ ಮೇಲೆ ಹೊರೆಸಿ ಯೇಸುವಿನ ಹಿಂದೆ ಹೊತ್ತುಕೊಂಡು ಬರುವ ಹಾಗೆ ಮಾಡಿದರು.",
			"27": "<sup>27</sup> ಜನರೂ ಸ್ತ್ರೀಯರೂ ದೊಡ್ಡ ಗುಂಪಾಗಿ ಯೇಸುವಿನ ಹಿಂದೆ ಬಂದರು. ಆ ಸ್ತ್ರೀಯರು ಎದೆಬಡಿದುಕೊಳ್ಳುತ್ತಾ ಯೇಸುವಿನ ಕುರಿತಾಗಿ ಗೋಳಾಡುತ್ತಾ ಇದ್ದರು.",
			"28": "<sup>28</sup> ಯೇಸು ಅವರ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡು ಯೇರುಸಲೇಮಿನ ಸ್ತ್ರೀಯರೇ ನನಗೋಸ್ಕರ ಅಳಬೇಡಿರಿ ನಿಮಗೋಸ್ಕರವೂ ನಿಮ್ಮ ಮಕ್ಕಳಿಗೋಸ್ಕರವೂ ಅಳಿರಿ.",
			"29": "<sup>29</sup> ಯಾಕಂದರೆ ಬಂಜೆಯರೂ ಬಸುರಾಗದವರೂ ಮೊಲೆಕುಡಿಸದವರೂ ಧನ್ಯರು ಎಂದು ಜನರು ಹೇಳುವ ದಿವಸಗಳು ಬರುತ್ತವೆ.",
			"30": "<sup>30</sup> ಆ ಕಾಲದಲ್ಲಿ ಜನರು - ಬೆಟ್ಟಗಳೇ ನಮ್ಮ ಮೇಲೆ ಬೀಳಿರಿ. ಗುಡ್ಡಗಳೇ ನಮ್ಮನ್ನು ಮುಚ್ಚಿಕೊಳ್ಳಿರಿ ಅನ್ನುವರು.",
			"31": "<sup>31</sup> ಹಸಿ ಮರಕ್ಕೆ ಇಷ್ಟೆಲ್ಲಾ ಮಾಡಿದರೆ ಒಣಮರದಗತಿ ಏನಾಗಬಹುದು? ಅಂದನು.",
			"32": "<sup>32</sup> ಆತನ ಸಂಗಡ ಶಿಲುಬೇಗೆರಿಸುವುದಕ್ಕಾಗಿ ದುಷ್ಕರ್ಮಿಗಳಾಗಿದ್ದ ಬೇರೆ ಇಬ್ಬರನ್ನೂ ಕರೆದುಕೊಂಡು ಹೋದರು.",
			"33": "<sup>33</sup> ಅವರು ಕಪಾಲವೆಂಬ ಸ್ಥಳಕ್ಕೆ ಬಂದಾಗ ಅಲ್ಲಿ ಯೇಸುವನ್ನೂ ದುಷ್ಕರ್ಮಿಗಳನ್ನು ಶಿಲುಬೆಗೆ ಹಾಕಿದರು. ಒಬ್ಬನನ್ನು ಯೇಸುವಿನ ಬಲಗಡೆಯಲ್ಲಿಯೂ ಮತ್ತೋಬ್ಬನನ್ನು ಎಡಗಡೆಯಲ್ಲಿಯು ಹಾಕಿದರು.",
			"34": "<sup>34</sup> ಆಗ ಯೇಸು ತಂದೆಯೇ ಅವರನ್ನು ಕ್ಷಮಿಸು ತಾವು ಏನು ಮಾಡುತ್ತೇವೆಂಬದನ್ನು ಅವರು ಅರಿಯರು ಅಂದನು. ಆ ಮೇಲೆ ಯೇಸುವಿನ ಬಟ್ಟೆಗಳನ್ನು ಪಾಲು ಮಾಡಿ ಚೀಟು ಹಾಕಿದರು.",
			"35": "<sup>35</sup> ಜನರೆಲ್ಲರೂ ನೋಡುತ್ತಾ ನಿಂತರು. ಇದಲ್ಲದೆ ಅಧಿಕಾರಿಗಳು ಅವನು ಮತ್ತೊಬ್ಬರನ್ನು ರಕ್ಷಿಸಿದನು, ಅವನು ದೇವರಾರಿಸಿಕೊಂಡ ಕ್ರಿಸ್ತನೇ ಆಗಿದ್ದರೆ ತನ್ನನ್ನು ತಾನೇ ರಕ್ಷಿಸಿಕೊಳ್ಳಲಿ ಎಂದು ಗೇಲಿಮಾಡಿದರು.",
			"36": "<sup>36</sup> ಸಿಪಾಯಿಗಳೂ ಆತನನ್ನು ಹಾಸ್ಯ ಮಾಡಿದರು. ಅವರು ಯೇಸುವಿನ ಹತ್ತಿರ ಬಂದು ಹುಳಿಮದ್ಯವನ್ನು ತೋರಿಸಿ -",
			"37": "<sup>37</sup> ನೀನು ಯೆಹೂದ್ಯರ ಅರಸನಾಗಿದ್ದರೆ ನಿನ್ನನ್ನು ನೀನು ರಕ್ಷಿಸಿಕೋ ಅಂದರು.",
			"38": "<sup>38</sup> ಇದಲ್ಲದೆ ಆತನ ಶಿಲುಬೆಯ ಮೇಲೆ ‘ಇವನು ಯೆಹೂದ್ಯರ ಅರಸನು’ ಎಂಬುದಾಗಿ ಒಂದು ಪ್ರಕಟಣಾ ಫಲಕವನ್ನು ಹಾಕಲಾಗಿತ್ತು.",
			"39": "<sup>39</sup> ಶಿಲುಬೇಗೆರಿಸಿದ್ದ ಆ ದುಷ್ಕರ್ಮಿಗಳಲ್ಲಿ ಒಬ್ಬನು ಯೇಸುವನ್ನು ದೂಷಿಸುತ್ತಾ – ನೀನು ಬರಬೇಕಾದ ಕ್ರಿಸ್ತನಲ್ಲವೇ? ನಿನ್ನನ್ನು ರಕ್ಷಿಸಿಕೋ, ನಮ್ಮನ್ನು ರಕ್ಷಿಸು ಎಂದು ಹೇಳಿನು.",
			"40": "<sup>40</sup> ಅದಕ್ಕೆ ಎರಡನೆಯವನು ಅವನನ್ನು ಗದರಿಸಿ – ನೀನು ಇದೇ ದಂಡನೆಯಲ್ಲಿರುವಾಗಲೂ ದೇವರಿಗೆ ಹೆದರುವದಿಲ್ಲವೋ?",
			"41": "<sup>41</sup> ನಾವಂತೂ ನ್ಯಾಯವಾಗಿ ದಂಡನೆಯಲ್ಲಿದ್ದೆವೆ, ನಾವು ಮಾಡಿದ್ದಕ್ಕೆ ತಕ್ಕ ಪ್ರತಿಫಲವನ್ನು ಹೊಂದುತ್ತೆವೆ. ಈತನಾದರೋ ಅಂತಹದ್ದೇನನ್ನೂ ಮಾಡಲಿಲ್ಲ ಎಂದು ಹೇಳಿ,",
			"42": "<sup>42</sup> ಯೇಸುವೇ ನೀನು ನಿನ್ನ ರಾಜ್ಯವನ್ನು ಪಡೆದವನಾಗಿ ಬರುವಾಗ ನನ್ನನ್ನು ನೆನಪಿಸಿಕೋ ಅಂದನು.",
			"43": "<sup>43</sup> ಅದಕ್ಕೆ ಯೇಸು - ‘ಈ ಹೊತ್ತೇ ನೀನು ನನ್ನ ಸಂಗಡ ಪರದೈಸಿನಲ್ಲಿರುವಿ ಎಂದು ನಿನಗೆ ಸತ್ಯವಾಗಿ ಹೇಳುತ್ತೇನೆ’ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"44": "<sup>44</sup> ಇಷ್ಟರಲ್ಲಿ ಹೆಚ್ಚು ಕಡಿಮೆ ಮದ್ಯಾಹ್ನವಾಯಿತು.",
			"45": "<sup>45</sup> ಸೂರ್ಯನು ಕಾಂತಿಗುಂದಿ ಮೂರು ಘಂಟೆಯ ತನಕ ಆ ದೇಶದ ಮೇಲೆಲ್ಲಾ ಕತ್ತಲೆ ಕವಿಯಿತು. ಇದಲ್ಲದೆ ದೇವಾಲಯದ ಪರದೆಯು ನಡುವೆ ಹರಿದು ಹೋಯಿತು.",
			"46": "<sup>46</sup> ಆ ಮೇಲೆ ಯೇಸು – ತಂದೆಯೇ ನನ್ನ ಆತ್ಮವನ್ನು ನಿನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸಿಕೊಡುತ್ತೇನೆ ಎಂದು ಮಹಾಧ್ವನಿಯಿಂದ ಕೂಗಿದನು. ಇದನ್ನು ಹೇಳಿದ ಮೇಲೆ ಪ್ರಾಣಬಿಟ್ಟನು.",
			"47": "<sup>47</sup> ಶತಾಧಿಪತಿಯು ನಡೆದ ಸಂಗತಿಯನ್ನು ನೋಡಿ – ಈ ಮನುಷ್ಯನು ನಿಜವಾಗಲೂ ಸತ್ಪುರುಷನೇ ಆಗಿದ್ದನು ಎಂದು ದೇವರನ್ನು ಕೊಂಡಾಡಿದನು.",
			"48": "<sup>48</sup> ಇದನ್ನು ನೋಡಲು ಸೇರಿ ಬಂದಿದ್ದ ಜನರೆಲ್ಲರು ನಡೆದ ಘಟನೆಯನ್ನು ನೋಡಿ ಎದೆಬಡಿದುಕೊಳ್ಳುತ್ತಾ ಹಿಂತಿರುಗಿ ಹೋದರು.",
			"49": "<sup>49</sup> ಇದಲ್ಲದೆ ಆತನ ಪರಿಚಿತರೆಲ್ಲರೂ, ಗಲಿಲಾಯದಿಂದ ಆತನ ಹಿಂದೆ ಬಂದಿದ್ದ ಸ್ತ್ರೀಯರೂ ದೂರದಲ್ಲಿ ನಿಂತು ಈ ಸಂಗತಿಗಳನ್ನು ನೋಡುತ್ತಿದ್ದರು.",
			"50": "<sup>50</sup> ಅರಿಮಥಾಯ ಎಂಬುದು ಯೆಹೂದ್ಯರದೊಂದು ಪಟ್ಟಣದಲ್ಲಿ ಒಬ್ಬ ಮನುಷ್ಯನಿದ್ದನು. ಅವನ ಹೆಸರು ಯೋಸೇಫ. ಆತನು ಹಿರಿಸಭೆಯವನು, ಉತ್ತಮನು, ಸತ್ಪುರುಷನು ಹಾಗೂ ದೇವರ ರಾಜ್ಯವನ್ನು ಎದುರು ನೋಡುತ್ತಿದ್ದವನೂ ಆಗಿದ್ದನಲ್ಲದೆ ಮಂತ್ರಿಯೂ ಆಗಿದ್ದನು.",
			"51": "<sup>51</sup> ಅವನು ಹಿರೀಸಭೆಯವರ ಆಲೋಚನೆಗೂ ಕೃತ್ಯಗಳಿಗೂ ಇವನು ಅನುಮತಿಸಿರಲಿಲ್ಲ.",
			"52": "<sup>52</sup> ಅವನು ಪಿಲಾತನ ಬಳಿಗೆ ಹೋಗಿ ಯೇಸುವಿನ ದೇಹವನ್ನು ತನಗೆ ಕೊಡಿಸಬೇಕೆಂದು ಬೇಡಿಕೊಂಡು.",
			"53": "<sup>53</sup> ಅದನ್ನು ಇಳಿಸಿ ನಾರುಮಡಿಯಲ್ಲಿ ಸುತ್ತಿ ಬಂಡೆಯಲ್ಲಿ ತೋಡಿದ್ದ ಸಮಾಧಿಯಲ್ಲಿ ಇಟ್ಟನು. ಅದರಲ್ಲಿ ಅದುವರೆಗೆ ಯಾರನ್ನು ಇಟ್ಟಿರಲಿಲ್ಲ.",
			"54": "<sup>54</sup> ಆ ದಿನವು ಸೌರಣೆಯ ದಿನವಾಗಿತ್ತು. ಮತ್ತು ಸಬ್ಬತ್ ದಿನ ಪ್ರಾರಂಭವಾಗುವುದಕ್ಕೆ ಸ್ವಲ್ಪ ಹೊತ್ತು ಮಾತ್ರ ಇತ್ತು",
			"55": "<sup>55</sup> ಹೀಗಿರುವಲ್ಲಿ ಗಲಿಲಾಯದಿಂದ ಆತನ ಸಂಗಡ ಬಂದಿದ್ದ ಸ್ತ್ರೀಯರು ಜೊತೆಯಲ್ಲಿ ಅವನ ಹಿಂದೆಯೇ ಹೋಗಿ ಆ ಸಮಾಧಿಯನ್ನೂ ಆತನ ದೇಹವನ್ನಿಟ್ಟ ಸ್ಥಳವನ್ನು ನೋಡಿ,",
			"56": "<sup>56</sup> ಹಿಂತಿರುಗಿ ಬಂದು ಪರಿಮಳದ್ರವ್ಯಗಳನ್ನೂ ಸುಗಂಧ ತೈಲವನ್ನೂ ಸಿದ್ಧಮಾಡಿಕೊಂಡರು."
		},
		"24": {
			"1": "<sup>1</sup> ಆ ಸ್ತ್ರೀಯರು ಸಬ್ಬತ್ ದಿನದಲ್ಲಿ ಧರ್ಮನಿಯಮದ ಪ್ರಕಾರ ವಿಶ್ರಮಿಸಿಕೊಂಡು ವಾರದ ಮೊದಲನೆಯ ದಿವಸದಲ್ಲಿ ಇನ್ನೂ ಮೊಬ್ಬಿರುವಾಗಲೇ ಎದ್ದು ತಾವು ಸಿದ್ಧಮಾಡಿದ್ದ ಸುಗಂಧದ್ರವ್ಯಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಸಮಾಧಿಗೆ ಬಂದರು.",
			"2": "<sup>2</sup> ಸಮಾಧಿಗೆ ಮುಚ್ಚಿದ ಕಲ್ಲು ಅಲ್ಲಿಂದ ಉರುಳಿಸಲ್ಪಟ್ಟಿರುವುದನ್ನು ಕಂಡು ಒಳಕ್ಕೆ ಹೋಗಿ ನೋಡುವಲ್ಲಿ",
			"3": "<sup>3</sup> ಯೇಸು ಸ್ವಾಮಿಯ ದೇಹವು ಅಲ್ಲಿ ಕಾಣಲಿಲ್ಲ.",
			"4": "<sup>4</sup> ಈ ವಿಷಯವಾಗಿ ಅವರಿಗೆ ಗಲಿಬಿಲಿ ಉಂಟಾದಾಗ ಹೊಳೆಯುವ ಉಡುಪನ್ನು ಧರಿಸಿದ್ದ ಇಬ್ಬರು ಪುರುಷರು ಅಕಸ್ಮಾತಾಗಿ ಅವರ ಬಳಿಯಲ್ಲಿ ನಿಂತುಕೊಂಡರು.",
			"5": "<sup>5</sup> ಆ ಸ್ತ್ರೀಯರು ಭಯಹಿಡಿದವರಾಗಿ ತಲೆ ತಗ್ಗಿಸಿಕೊಂಡು ನಿಂತಿರುವಾಗ ಆ ಪುರುಷರು ಅವರಿಗೆ – ‘ಸತ್ತವರೊಳಗೆ ಬದುಕಿರುವವನನ್ನು ಹುಡುಕುವದೇನು? ಆತನು ಇಲ್ಲಿ ಇಲ್ಲ, ಜೀವಿತನಾಗಿ ಎದ್ದಿದ್ದಾನೆ.’",
			"6": "<sup>6</sup> ಆತನು ಇನ್ನೂ ಗಲಿಲಾಯದಲ್ಲಿದ್ದಾಗ - ಮನುಷ್ಯಕುಮಾರನು ಪಾಪಿಗಳಾದ ಮನುಷ್ಯರ ಕೈಗೆ ಒಪ್ಪಿಸಲ್ಪಟ್ಟು ಶಿಲುಬೆಗೆ ಹಾಕಲ್ಪಟ್ಟು ಮೂರನೇಯ ದಿನದಲ್ಲಿ ಜೀವಿತನಾಗಿ ಎದ್ದು ಬರುವುದು ಅಗತ್ಯವೆಂದು ನಿಮಗೆ ಹೇಳಿದ್ದನ್ನು ನೆನಪಿಗೆ ತಂದುಕೊಳ್ಳಿರಿ ಎಂದು ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ಸ್ತ್ರೀಯರು ಆತನ ಮಾತುಗಳನ್ನು ನೆನಪಿಗೆ ತಂದುಕೊಂಡು,",
			"9": "<sup>9</sup> ಸಮಾಧಿಯ ಬಳಿಯಿಂದ ಹಿಂತಿರುಗಿ ಹೋಗಿ ಆ ಸಂಗತಿಗಳನ್ನೆಲ್ಲಾ ಹನ್ನೊಂದು ಮಂದಿ ಶಿಷ್ಯರಿಗೂ ಉಳಿದವರೆಲ್ಲರಿಗೂ ತಿಳಿಸಿದರು.",
			"10": "<sup>10</sup> ಆ ಸ್ತ್ರೀಯರು ಯಾರಂದರೆ ಮಗ್ದಲದ ಮರಿಯಳು, ಯೋಹಾನಳು, ಯಾಕೋಬನ ತಾಯಿಯಾದ ಮರಿಯಳು ಇವರೇ. ಇವರ ಜೊತೆಯಲ್ಲಿದ್ದ ಬೇರೆ ಸ್ತ್ರೀಯರು ಸಹ ಅಪೊಸ್ತಲರಿಗೆ ಆ ಸಂಗತಿಗಳನ್ನು ಹೇಳಿದರೂ. ಅವರು ನಂಬಲಿಲ್ಲ.",
			"11": "<sup>11</sup> ಆ ಮಾತುಗಳು ಅವರಿಗೆ ಬರೀ ಹರಟೆಯಾಗಿ ತೋರಿದವು.",
			"12": "<sup>12</sup> ಆದರೆ ಪೇತ್ರನು ಎದ್ದು ಸಮಾಧಿ ಬಳಿ ಓಡಿಹೋಗಿ ಬೊಗ್ಗಿ ನೋಡಿ ನಾರುಬಟ್ಟೆಗಳನ್ನು ಮಾತ್ರ ಕಂಡು ನಡೆದ ಸಂಗತಿಗೆ ತನ್ನಲ್ಲಿ ಆಶ್ಚರ್ಯಪಡುತ್ತಾ ಹೊರಟು ಹೋದನು.",
			"13": "<sup>13</sup> ಅದೇ ದಿನದಲ್ಲಿ ಶಿಷ್ಯರಲ್ಲಿ ಇಬ್ಬರು ಯೆರೂಸಲೇಮಿನಿಂದ ಆರುವರೆ ಮೈಲು ದೂರದಲ್ಲಿದ್ದ ಎಮ್ಮಾಹು ಎಂಬ ಹಳ್ಳಿಗೆ ಹೋಗುತ್ತಾ",
			"14": "<sup>14</sup> ಸಂಭವಿಸಿದ ಈ ಎಲ್ಲಾ ಕಾರ್ಯಗಳ ವಿಷಯವಾಗಿ ಸಂಭಾಷಣೆಮಾಡುತ್ತಿದ್ದರು.",
			"15": "<sup>15</sup> ಅವರು ಸಂಭಾಷಣೆಯಲ್ಲಿ ಚರ್ಚಿಸುತ್ತಿರುವಾಗ ಯೇಸು ತಾನೇ ಹತ್ತಿರಕ್ಕೆ ಬಂದು ಅವರ ಜೊತೆಯಲ್ಲಿ ಸಾಗಿದನು.",
			"16": "<sup>16</sup> ಆದರೆ ಅವರು ಆತನ ಗುರುತನ್ನು ಹಿಡಿಯಲಿಲ್ಲ. ಅವರ ಕಣ್ಣು ಕಟ್ಟಿದ ಹಾಗಾಗಿತು.",
			"17": "<sup>17</sup> ಯೇಸುವು – ‘ನೀವು ಮಾರ್ಗದಲ್ಲಿ ಹೋಗುತ್ತಾ ಚರ್ಚಿಸಿ ಮಾತಾಡಿಕೊಳ್ಳುವ ಈ ಸಂಗತಿಗಳೇನು,’ ಎಂದು ಕೇಳಲು, ಅವರು ದುಃಖದ ಮುಖವುಳ್ಳವರಾಗಿ ನಿಂತರು.",
			"18": "<sup>18</sup> ಅವರಲ್ಲಿ ಕ್ಲೆಯೋಫನೆಂಬವನು ಆತನಿಗೆ – ಯೆರೂಸಲೇಮಿಗೆ ಬಂದ ಪರಸ್ಥಳದವರೆಲ್ಲರಿಗೂ ಈ ದಿವಸಗಳಲ್ಲಿ ನಡೆದಿರುವ ಸಂಗತಿಗಳು ಗೊತ್ತಿರಲಾಗಿ ನಿನಗೆ ಮಾತ್ರ ಗೊತ್ತಿಲ್ಲವೋ? ಎಂದು ಕೇಳಲು,",
			"19": "<sup>19</sup> ಆತನು ಅವರನ್ನು – ‘ಯಾವ ಸಂಗತಿಗಳು’ ಎಂದು ಕೇಳಿದನು. ಅದಕ್ಕೆ ಅವರು – ನಜರೇತಿನವನಾದ ಯೇಸುವಿನ ಸಂಗತಿಗಳೇ. ಆತನು ದೇವರ ಮುಂದೆ ಮತ್ತು ಮನುಷ್ಯರ ಮುಂದೆ ಕೃತ್ಯದಲ್ಲಿಯೂ ಮಾತಿನಲ್ಲಿಯೂ ಉನ್ನತನಾದ ಪ್ರವಾದಿಯಾಗಿದ್ದನು.",
			"20": "<sup>20</sup> ಆತನನ್ನು ಮಹಾಯಾಜಕರೂ ನಮ್ಮ ಅಧಿಕಾರಿಗಳೂ ಮರಣದಂಡನೆಗೆ ಒಪ್ಪಿಸಿಕೊಟ್ಟು ಶಿಲುಬೆಗೆ ಹಾಕಿಸಿದರು.",
			"21": "<sup>21</sup> ನಾವಾದರೋ ಇಸ್ರಾಯೇಲ್ ಜನರನ್ನು ಬಿಡಿಸತಕ್ಕವನು ಆತನೇ ಎಂದು ನಿರೀಕ್ಷಿಸಿಕೊಂಡಿದ್ದೆವು. ಇದು ಮಾತ್ರವಲ್ಲದೆ ಆ ಕಾರ್ಯಗಳು ನಡೆದು ಇಂದಿಗೆ ಮೂರನೆಯ ದಿನ.",
			"22": "<sup>22</sup> ಆದರೆ ನಮ್ಮಲ್ಲಿ ಕೆಲವು ಮಂದಿ ಸ್ತ್ರೀಯರು ಬೆಳಗಾಗುವಾಗ ಸಮಾಧಿಯ ಬಳಿಗೆ ತೆರಳಿ ಆತನ ದೇಹವನ್ನು ಕಾಣದೇ,",
			"23": "<sup>23</sup> ಅಲ್ಲಿಂದ ಬಂದು ತಮಗೆ ದೇವದೂತರ ದರ್ಶನವಾಯಿತೆಂತಲೂ, ಆತನು ಬದುಕಿದ್ದಾನೆಂದು ಅವರು ಹೇಳಿದರೆಂತಲೂ ತಿಳಿಸಿ ನಮಗೆ ಅತ್ಯಾಶ್ಚರ್ಯವನ್ನುಂಟುಮಾಡಿದರು.",
			"24": "<sup>24</sup> ನಮ್ಮ ಸಂಗಡ ಇದ್ದವರಲ್ಲಿ ಕೆಲವರು ಸಮಾಧಿಗೆ ಹೋಗಿ ಆ ಸ್ತ್ರೀಯರು ಹೇಳಿದ ಪ್ರಕಾರವೇ ಕಂಡರು. ಆತನನ್ನು ಮಾತ್ರ ಕಾಣಲಿಲ್ಲ. ಎಂದು ಹೇಳಿದರು.",
			"25": "<sup>25</sup> ಯೇಸು ಅವರಿಗೆ – ಎಲಾ ಬುದ್ಧಿಯಿಲ್ಲದವರೇ, ಪ್ರವಾದಿಗಳು ಹೇಳಿದ್ದೆಲ್ಲವನ್ನು ನಂಬದ ಮಂದ ಹೃದಯವುಳ್ಳವರೇ,",
			"26": "<sup>26</sup> ಕ್ರಿಸ್ತನು ಇಂಥ ಶ್ರಮಗಳನ್ನು ಅನುಭವಿಸಿ ತನ್ನ ಮಹಾಪದವಿಗೆ ಸೇರುವುದು ಅಗತ್ಯವಾಗಿತ್ತಲ್ಲವೇ ಎಂದು ಹೇಳಿ,",
			"27": "<sup>27</sup> ಮೋಶೆಯ ಮತ್ತು ಎಲ್ಲಾ ಪ್ರವಾದಿಗಳ ಗ್ರಂಥಗಳು ಮೊದಲುಗೊಂಡು ಸಮಸ್ತ ಗ್ರಂಥಗಳಲ್ಲಿ ತನ್ನ ವಿಷಯವಾಗಿರುವ ಸೂಚನೆಗಳನ್ನು ಅವರಿಗೆ ವಿವರಿಸಿದನು.",
			"28": "<sup>28</sup> ಅವರು ಹೋಗುತ್ತಿರುವ ಹಳ್ಳಿಯ ಸಮೀಪಕ್ಕೆ ಬಂದಾಗ ಆತನು ಅವರನ್ನು ದಾಟಿ ಇನ್ನೂ ಮುಂದಕ್ಕೆ ಹೋಗುವವನಂತೆ ಮಾಡಿದನು.",
			"29": "<sup>29</sup> ಆದರೆ ಅವರು – ನಮ್ಮ ಸಂಗಡ ಇರು. ಸಂಜೆಯಾಯಿತು, ಹೊತ್ತು ಇಳಿಯುತ್ತಾ ಬಂತು ಎಂದು ಆತನಿಗೆ ಹೇಳಿ ಬಲವಂತ ಮಾಡಲು ಆತನು ಅವರ ಕೂಡ ಇರುವುದಕ್ಕೆ ಊರಿನೊಳಗೆ ಹೋದನು.",
			"30": "<sup>30</sup> ಆತನು ಅವರ ಸಂಗಡ ಊಟಕ್ಕೆ ಕುಳಿತುಕೊಂಡಾಗ ರೊಟ್ಟಿಯನ್ನು ತೆಗೆದುಕೊಂಡು ದೇವರ ಸ್ತೋತ್ರ ಮಾಡಿ ಮುರಿದು ಅವರಿಗೆ ಕೊಡುತ್ತಿರಲಾಗಿ ಅವರ ಕಣ್ಣುಗಳು ತೆರೆದವು. ಆಗ ಅವರು ಆತನ ಗುರುತು ಹಿಡಿದರು.",
			"31": "<sup>31</sup> ಆತನು ಅವರ ಕಣ್ಣಿಗೆ ಕಾಣದಂತೆ ಮಾಯವಾದನು.",
			"32": "<sup>32</sup> ಆಗ ಅವರು ಒಬ್ಬರಿಗೊಬ್ಬರು – ಆತನು ದಾರಿಯಲ್ಲಿ ನಮ್ಮ ಸಂಗಡ ಮಾತನಾಡಿದಾಗಲೂ ಗ್ರಂಥಗಳ ಅರ್ಥವನ್ನು ನಮಗೆ ಬಿಡಿಸಿ ಹೇಳಿದಾಗಲೂ ನಮ್ಮ ಹೃದಯವು ನಮ್ಮೊಳಗೆ ಕುದಿಯಿತಲ್ಲವೇ? ಎಂದು ಹೇಳಿಕೊಂಡು",
			"33": "<sup>33</sup> ಅದೇ ಗಳಿಗೆಯಲ್ಲಿ ಎದ್ದು ಯೆರೂಸಲೇಮಿಗೆ ಹಿಂತಿರುಗಿ ಹೋದರು. ಅಲ್ಲಿ ಹನ್ನೊಂದು ಮಂದಿ ಶಿಷ್ಯರೂ ಅವರ ಸಂಗಡ ಇದ್ದವರೂ ಒಟ್ಟಾಗಿ ಕೂಡಿಕೊಂಡಿದ್ದು,",
			"34": "<sup>34</sup> ಸ್ವಾಮಿ ಎದ್ದದ್ದು ನಿಜ. ಆತನು ಸೀಮೋನನಿಗೆ ಕಾಣಿಸಿಕೊಂಡನು ಎಂದು ಹೇಳುತ್ತಿದ್ದರು.",
			"35": "<sup>35</sup> ಇದನ್ನು ಕಂಡು ಅವರು ದಾರಿಯಲ್ಲಿ ನಡೆದ ಸಂಗತಿಯನ್ನೂ ರೊಟ್ಟಿ ಮುರಿಯುವುದರಲ್ಲಿ ತಾವು ಆತನ ಗುರುತು ಹಿಡಿದೆವೆಂಬದನ್ನೂ ವಿವರಿಸಿದರು.",
			"36": "<sup>36</sup> ಅವರು ಈ ಸಂಗತಿಗಳನ್ನು ಕುರಿತು ಮಾತಾಡುತ್ತಿರುವಾಗ ಯೇಸುವೇ ಅವರ ನಡುವೆ ನಿಂತು ‘ನಿಮಗೆ ಮನಶಾಂತಿಯಾಗಲಿ’; ಎಂದು ಅವರಿಗೆ ಹೇಳಿದನು.",
			"37": "<sup>37</sup> ಅವರು ದಿಗಿಲುಬಿದ್ದು ಭಯಹಿಡಿದವರಾಗಿ ತಾವು ಕಂಡದ್ದನ್ನು ಭೂತವೆಂದು ಭಾವಿಸಿದರು.",
			"38": "<sup>38</sup> ಆತನು ಅವರಿಗೆ – ಯಾಕೆ ಕಳವಳಗೊಳ್ಳುತ್ತೀರಿ? ನಿಮ್ಮ ಹೃದಯಗಳಲ್ಲಿ ಅನುಮಾನಗಳು ಹುಟ್ಟುವುದೇಕೆ? ನನ್ನ ಕೈಗಳನ್ನೂ ನನ್ನ ಕಾಲುಗಳನ್ನೂ ನೋಡಿರಿ. ನಾನೇ ಅಲ್ಲವೇ, ನನ್ನನ್ನು ಮುಟ್ಟಿ ನೋಡಿರಿ.",
			"39": "<sup>39</sup> ನಿಮಗೆ ಕಾಣುವ ಪ್ರಕಾರ ನನಗೆ ಮಾಂಸವೂ ಎಲುಬುಗಳೂ ಉಂಟು. ಅವು ಭೂತಕ್ಕಿಲ್ಲ ಎಂದು ಹೇಳಿದನು.",
			"40": "<sup>40</sup> ಇದನ್ನು ಹೇಳಿದ ಮೇಲೆ ಆತನು ತನ್ನ ಕೈಗಳನ್ನೂ ಕಾಲುಗಳನ್ನೂ ಅವರಿಗೆ ತೋರಿಸಿದನು.",
			"41": "<sup>41</sup> ಆದರೆ ಅವರು ಸಂತೋಷ ಭರಿತರಾಗಿ ಇನ್ನೂ ನಂಬದೇ ಆಶ್ಚರ್ಯಪಡುತ್ತಿರಲಾಗಿ ಆತನು – ತಿನ್ನತಕ್ಕ ಪದಾರ್ಥವೇನಾದರೂ ನಿಮಲ್ಲುoಟೋ ಎಂದು ಅವರನ್ನು ಕೇಳಲು",
			"42": "<sup>42</sup> ಅವರು ಸುಟ್ಟ ಮೀನಿನ ಒಂದು ತುಂಡನ್ನು ಆತನಿಗೆ ಕೊಟ್ಟರು.",
			"43": "<sup>43</sup> ಆತನು ಅದನ್ನು ತೆಗೆದುಕೊಂಡು ಅವರ ಮುಂದೆ ತಿಂದನು.",
			"44": "<sup>44</sup> ತರುವಾಯ ಯೇಸುವು – ನಾನು ನಿಮ್ಮ ಸಂಗಡ ಇದ್ದಾಗ ಇದೆಲ್ಲಾ ನಿಮಗೆ ತಿಳಿಸಲಿಲ್ಲವೇ? ನನ್ನ ವಿಷಯವಾಗಿ ಮೋಶೆಯ ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿಯೂ ಪ್ರವಾದಿಗಳ ಗ್ರಂಥಗಳಲ್ಲಿಯೂ ಕೀರ್ತನೆಗಳಲ್ಲಿಯೂ ಬರೆದಿರುವುದೆಲ್ಲಾ ನೆರವೇರುವುದು ಅಗತ್ಯವೆಂದು ನಿಮಗೆ ಹೇಳಲಿಲ್ಲವೇ? ಅಂದನು.",
			"45": "<sup>45</sup> ಆ ಮೇಲೆ ಅವರು ಶಾಸ್ತ್ರ ವಚನಗಳನ್ನು ತಿಳುಕೊಳ್ಳುವಂತೆ ಆತನು ಅವರಿಗೆ ಜ್ಞಾನವನ್ನೂ ಅನುಗ್ರಹಿಸಿದನು,",
			"46": "<sup>46</sup> ಅನಂತರ, “ಮೊದಲೇ ಪ್ರಕಟವಾದ ಪ್ರಕಾರ ಯೇಸು ಕ್ರಿಸ್ತನು ಕಷ್ಟ ಪಟ್ಟು ಸತ್ತು ಮೂರನೆಯ ದಿನದಲ್ಲಿ ಜೀವಿತನಾಗಿ ಎದ್ದು ಬರುವನೆಂತಲೂ,",
			"47": "<sup>47</sup> ಅದಲ್ಲದೆ ಜನರು ಪಾಪಪರಿಹಾರಕ್ಕೋಸ್ಕರ ಪಶ್ಚಾತ್ತಾಪಟ್ಟು, ಪಾಪಕ್ಕೆ ವಿಮುಖರಾಗಿ, ದೇವರ ಕಡೆಗೆ ತಿರುಗಿಕೊಳ್ಳಬೇಕೆಂಬ ಈ ಸುವಾರ್ತೆಯು ಯೆರೂಸಲೇಮ್ ಮೊದಲುಗೊಂಡು ಸಮಸ್ತ ದೇಶದವರಿಗೂ ಆತನ ಹೆಸರಿನಲ್ಲಿ ಸಾರಲ್ಪಡಬೇಕೆಂತಲೂ ಬರೆದದೆ.",
			"48": "<sup>48</sup> ಇವುಗಳ ವಿಷಯದಲ್ಲಿ ನೀವೇ ಸಾಕ್ಷಿ ಹೇಳುವವರಾಗಿದ್ದೀರಿ.",
			"49": "<sup>49</sup> ಇಗೋ ನನ್ನ ತಂದೆಯು ವಾಗ್ದಾನ ಮಾಡಿದ ಶಕ್ತಿಯನ್ನು ನಿಮಗೆ ಕಳುಹಿಸಿಕೊಡುತ್ತೇನೆ. ದೇವರು ಮೇಲಣ ಲೋಕದಿಂದ ನಿಮಗೆ ಶಕ್ತಿಯನ್ನು ಕೊಡುವ ತನಕ ಈ ಪಟ್ಟಣದಲ್ಲೇ ಕಾದುಕೊಂಡಿರಿ,” ಎಂದು ಹೇಳಿದನು.",
			"50": "<sup>50</sup> ಮತ್ತು ಯೇಸು ಅವರನ್ನು ಬೇಥಾನ್ಯದ ತನಕ ಕರಕೊಂಡು ಹೋಗಿ ತನ್ನ ಕೈಗಳನ್ನು ಎತ್ತಿ ಅವರನ್ನು ಆಶೀರ್ವದಿಸಿದನು.",
			"51": "<sup>51</sup> ಆಶೀರ್ವದಿಸುತ್ತಿರುವಾಗ ಆತನು ಅವರನ್ನು ಬಿಟ್ಟು ಪರಲೋಕಕ್ಕೆ ಒಯ್ಯಲ್ಪಟ್ಟನು.",
			"52": "<sup>52</sup> ಅವರು ಆತನನ್ನು ಆರಾಧಿಸಿ ಬಹು ಸಂತೋಷದಿಂದ ಯೆರೂಸಲೇಮಿಗೆ ಹಿಂತಿರುಗಿ ಹೋಗಿ",
			"53": "<sup>53</sup> ನಿರಂತರವಾಗಿ ದೇವಾಲಯದಲ್ಲಿ ದೇವರನ್ನು ಕೊಂಡಾಡುತ್ತಿದ್ದರು."
		}
	}
};
module.exports = book;