var book = {
	"name": "ಗಲಾತ್ಯದವರಿಗೆ ಬರೆದ ಪತ್ರಿಕೆ",
	"numChapters": 6,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ಮನುಷ್ಯರಿಂದಾಗಲಿ ಮನುಷ್ಯರ ಮುಖಾಂತರದಿಂದಾಗಲಿ ಅಪೊಸ್ತಲನಾಗಿರದೆ ಯೇಸು ಕ್ರಿಸ್ತನ ಮುಖಾಂತರವೂ ಮತ್ತು ಆತನನ್ನು ಸತ್ತವರೊಳಗಿಂದ ಎಬ್ಬಿಸಿದ ತಂದೆಯಾದ ದೇವರಿಂದಲೂ ಅಪೊಸ್ತಲೋದ್ಯೋಗವನ್ನು ಹೊಂದಿದ ಪೌಲನೆಂಬ ನಾನು ಹಾಗೂ",
			"2": "<sup>2</sup> ನನ್ನ ಜೊತೆಯಲ್ಲಿರುವ ಎಲ್ಲಾ ಸಹೋದರರೂ ಗಲಾತ್ಯದಲ್ಲಿರುವ ಸಭೆಗಳಿಗೆ ಬರೆಯುವುದೇನೆಂದರೆ,",
			"3": "<sup>3</sup> ತಂದೆಯಾದ ದೇವರಿಂದಲೂ ನಮ್ಮ ಕರ್ತನಾದ ಯೇಸು ಕ್ರಿಸ್ತನಿಂದಲೂ ನಿಮಗೆ ಕೃಪೆಯೂ ಶಾಂತಿಯೂ ಆಗಲಿ.",
			"4": "<sup>4</sup> ಆತನು ನಮ್ಮನ್ನು ಈಗಿನ ದುಷ್ಟ ಯುಗದಿಂದ ಬಿಡಿಸಬೇಕೆಂದು ನಮ್ಮ ತಂದೆಯಾದ ದೇವರ ಚಿತ್ತಕ್ಕನುಸಾರವಾಗಿ, ನಮ್ಮ ಪಾಪಗಳ ದೆಸೆಯಿಂದ ತನ್ನನ್ನು ಮರಣಕ್ಕೆ ಒಪ್ಪಿಸಿಕೊಟ್ಟನು.",
			"5": "<sup>5</sup> ದೇವರಿಗೆ ಯುಗಯುಗಾಂತರಗಳಲ್ಲಿಯೂ ಮಹಿಮೆಯಾಗಲಿ. ಆಮೆನ್.",
			"6": "<sup>6</sup> ಕ್ರಿಸ್ತನ ಕೃಪೆಯಿಂದ ನಿಮ್ಮನ್ನು ಕರೆದಾತನನ್ನು ಬಿಟ್ಟು, ಇಷ್ಟು ಬೇಗನೆ ಬೇರೆ ಸುವಾರ್ತೆಯ ಕಡೆಗೆ ನೀವು ತಿರುಗಿದಿರೆಂದು ನಾನು ಆಶ್ಚರ್ಯಪಡುತ್ತೇನೆ.",
			"7": "<sup>7</sup> ವಾಸ್ತವವಾಗಿ ಬೇರೊಂದು ಸುವಾರ್ತೆ ಇಲ್ಲ, ಆದರೆ ಕೆಲವರು ನಿಮ್ಮಲ್ಲಿ ಬೇಧವನ್ನು ಹುಟ್ಟಿಸುತ್ತಾ ಕ್ರಿಸ್ತನ ಸುವಾರ್ತೆಯನ್ನು ಮಾರ್ಪಡಿಸುವುದಕ್ಕೆ ಪ್ರಯತ್ನಿಸುತ್ತಿದ್ದಾರೆ.",
			"8": "<sup>8</sup> ಆದರೂ ನಾವು ನಿಮಗೆ ಸಾರಿದ ಸುವಾರ್ತೆಗೆ ವಿರುದ್ಧವಾದದ್ದನ್ನು, ನಾವೇ ಆಗಲಿ ಅಥವಾ ಪರಲೋಕದಿಂದ ಬಂದ ದೇವದೂತನೇ ಆಗಲಿ, ನಿಮಗೆ ಸಾರಿದರೆ ಅವನು ಶಾಪಗ್ರಸ್ತನಾಗಲಿ.",
			"9": "<sup>9</sup> ನಾವು ಮೊದಲು ಹೇಳಿದಂತೆಯೇ ಈಗಲೂ ನಾನು ತಿರುಗಿ ಹೇಳುತ್ತೇನೆ. ನೀವು ಸ್ವೀಕರಿಸಿದ ಸುವಾರ್ತೆಗೆ ವಿರುದ್ಧವಾದದ್ದನ್ನು ಯಾವನಾದರೂ ನಿಮಗೆ ಸಾರಿದರೆ ಅವನು ಶಾಪಗ್ರಸ್ತನಾಗುವನು.",
			"10": "<sup>10</sup> ನಾನೀಗ ಯಾರನ್ನು ಒಲಿಸಿಕೊಳ್ಳುತ್ತಾ ಇದ್ದೇನೆ? ಮನುಷ್ಯರನ್ನೋ? ದೇವರನ್ನೋ? ನಾನು ಮನುಷ್ಯರನ್ನು ಮೆಚ್ಚಿಸುವುದಕ್ಕೆ ಪ್ರಯತ್ನಿಸುತ್ತಾ ಇದ್ದೇನೋ? ಇನ್ನೂ ಮನುಷ್ಯರನ್ನು ಮೆಚ್ಚಿಸುವವನಾಗಿದ್ದರೆ ನಾನು ಕ್ರಿಸ್ತನ ಸೇವಕನಲ್ಲ.",
			"11": "<sup>11</sup> ಸಹೋದರರೇ, ನಾನು ಸಾರಿದ ಸುವಾರ್ತೆಯಂತೂ ಕೇವಲ ಮನುಷ್ಯನಿಂದ ಬಂದದ್ದಲ್ಲವೆಂದು ನೀವು ತಿಳಿದುಕೊಳ್ಳಬೇಕೆಂದು ನಾನು ಬಯಸುತ್ತೇನೆ.",
			"12": "<sup>12</sup> ನಾನು ಅದನ್ನು ಮನುಷ್ಯನಿಂದ ಹೊಂದಲಿಲ್ಲ, ನನಗೆ ಯಾರೂ ಉಪದೇಶಿಸಲಿಲ್ಲ, ಬದಲಾಗಿ ಯೇಸು ಕ್ರಿಸ್ತನೇ ಅದನ್ನು ನನಗೆ ಪ್ರಕಟಪಡಿಸಿದನು.",
			"13": "<sup>13</sup> ಹಿಂದೆ ನಾನು ಯೆಹೂದ್ಯ ಮತದಲ್ಲಿದ್ದಾಗ ನನ್ನ ನಡತೆ ಎಂಥದ್ದೆಂದು ನೀವು ಕೇಳಿದ್ದೀರಿ. ನಾನು ದೇವರ ಸಭೆಯನ್ನು ಬಹಳವಾಗಿ ಹಿಂಸೆಪಡಿಸಿ ಹಾಳುಮಾಡುತ್ತಿದ್ದೆನು.",
			"14": "<sup>14</sup> ಇದಲ್ಲದೆ ನಾನು ನನ್ನ ಪಿತೃಗಳಿಂದ ಬಂದ ಸಂಪ್ರದಾಯಗಳ ಬಗ್ಗೆ ಬಹು ಅಭಿಮಾನವುಳ್ಳವನಾಗಿ, ನನ್ನ ಜನರೊಳಗೆ ಸಮಪ್ರಾಯದವರಾದ ಅನೇಕರಿಗಿಂತ ಯೆಹೂದ್ಯ ಮತಾಚಾರದಲ್ಲಿ ಆಸಕ್ತನಾಗಿದ್ದೆನು.",
			"15": "<sup>15</sup> ಆದರೆ ನಾನು ನನ್ನ ತಾಯಿಯ ಗರ್ಭದಲ್ಲಿದ್ದಾಗಲೇ ನನ್ನನ್ನು ಪ್ರತ್ಯೇಕಿಸಿ, ತನ್ನ ಕೃಪೆಯಿಂದ ಕರೆದ ದೇವರು,",
			"16": "<sup>16</sup> ತನ್ನ ಮಗನನ್ನು ನನ್ನೊಳಗೆ ಪ್ರಕಟಪಡಿಸುವುದಕ್ಕೆ ಮತ್ತು ಅನ್ಯಜನರಲ್ಲಿ ಆತನನ್ನು ನಾನು ಪ್ರಸಿದ್ಧಿ ಪಡಿಸುವವನಾಗಬೇಕೆಂದು ಇಚ್ಛಿಸಿದನು. ನಾನು ಕೂಡಲೇ ಮನುಷ್ಯರ ಆಲೋಚನೆಯನ್ನು ಕೇಳದೆ,",
			"17": "<sup>17</sup> ಯೆರೂಸಲೇಮಿಗೆ ನನಗಿಂತ ಮೊದಲು ಅಪೊಸ್ತಲರಾಗಿದ್ದವರ ಬಳಿಗೂ ಹೋಗದೆ ಅರಬಸ್ಥಾನಕ್ಕೆ ಹೋಗಿ ತಿರುಗಿ ದಮಸ್ಕಕ್ಕೆ ಬಂದೆನು.",
			"18": "<sup>18</sup> ಮೂರು ವರ್ಷಗಳ ತರುವಾಯ ಕೇಫನ ಪರಿಚಯವನ್ನು ಮಾಡಿಕೊಳ್ಳಬೇಕೆಂದು ಯೆರೂಸಲೇಮಿಗೆ ಹೋಗಿ ಅವನ ಬಳಿಯಲ್ಲಿ ಹದಿನೈದು ದಿನಗಳು ಇದ್ದೆನು.",
			"19": "<sup>19</sup> ಆದರೆ ಕರ್ತನ ಸಹೋದರನಾದ ಯಾಕೋಬನಲ್ಲದೆ ಅಪೊಸ್ತಲರಲ್ಲಿ ಬೇರೆ ಯಾರನ್ನೂ ನಾನು ಕಾಣಲಿಲ್ಲ.",
			"20": "<sup>20</sup> ನಾನು ನಿಮಗೆ ಬರೆಯುವ ಸಂಗತಿಗಳು ಸುಳ್ಳಲ್ಲವೆಂಬುದಕ್ಕೆ ಇಗೋ, ದೇವರೇ ಸಾಕ್ಷೀ.",
			"21": "<sup>21</sup> ಆ ಮೇಲೆ ಸಿರಿಯ ಮತ್ತು ಕಿಲಿಕ್ಯ ಪ್ರಾಂತ್ಯಗಳಿಗೆ ಹೋದೆನು.",
			"22": "<sup>22</sup> ಆದರೆ ಕ್ರಿಸ್ತನಲ್ಲಿರುವ ಯೂದಾಯದ ಸಭೆಗಳಿಗೆ ನನ್ನ ಪರಿಚಯವಿರಲಿಲ್ಲ.",
			"23": "<sup>23</sup> ಪೂರ್ವದಲ್ಲಿ ನಮ್ಮನ್ನು ಹಿಂಸೆಪಡಿಸಿದವನು, ತಾನು ಹಾಳು ಮಾಡುತ್ತಿದ್ದ ನಂಬಿಕೆಯನ್ನು ಈಗ ಸಾರುತ್ತಿದ್ದಾನೆ ಎಂಬ ಸುದ್ದಿಯನ್ನು ಅವರು ಕೇಳಿ,",
			"24": "<sup>24</sup> ನನ್ನ ಕುರಿತಾಗಿ ದೇವರನ್ನು ಕೊಂಡಾಡಿದರು."
		},
		"2": {
			"1": "<sup>1</sup> ಹದಿನಾಲ್ಕು ವರುಷಗಳ ನಂತರ ನಾನು ಬಾರ್ನಬನ ಜೊತೆಯಲ್ಲಿ ತೀತನನ್ನು ಸಹ ಕರೆದುಕೊಂಡು, ಮತ್ತೊಮ್ಮೆ ಯೆರೂಸಲೇಮಿಗೆ ಹೋದೆನು.",
			"2": "<sup>2</sup> ದೈವಪ್ರೇರಣೆಗೆ ಅನುಸಾರವಾಗಿ ಹೋಗಿ, ಅನ್ಯಜನರಲ್ಲಿ ನಾನು ಸಾರುವ ಸುವಾರ್ತೆಯು ಏನೆಂಬುದನ್ನು ಅಲ್ಲಿದ್ದ ಗಣ್ಯರಿಗೆ ಪ್ರತ್ಯೇಕವಾಗಿ ತಿಳಿಸಿದೆನು. ಏಕೆಂದರೆ ನಾನು ಸುವಾರ್ತೆಗಾಗಿ ಪಟ್ಟ ಶ್ರಮವಾಗಲಿ, ಈಗ ಪಡುತ್ತಿರುವ ಶ್ರಮವಾಗಲಿ ನಿಷ್ಫಲವಾಗಬಾರದೆಂದು ತಿಳಿಸಿದೆನು.",
			"3": "<sup>3</sup> ನನ್ನ ಜೊತೆಯಲ್ಲಿದ್ದ ತೀತನು ಗ್ರೀಕನಾಗಿದ್ದರೂ ಅವನಿಗೆ ಸುನ್ನತಿಯಾಗಬೇಕೆಂದು ಯಾರೂ ಒತ್ತಾಯಮಾಡಲಿಲ್ಲ.",
			"4": "<sup>4</sup> ಆದರೆ ರಹಸ್ಯವಾಗಿ ಒಳಗೆ ಬಂದಿದ್ದ ಸುಳ್ಳು ಸಹೋದರರು ನಮ್ಮನ್ನು ಧರ್ಮಶಾಸ್ತ್ರದ ದಾಸತ್ವದಲ್ಲಿ ಸಿಕ್ಕಿಸಬೇಕೆಂದು, ಕ್ರಿಸ್ತ ಯೇಸುವಿನಲ್ಲಿರುವ ನಮ್ಮ ಸ್ವಾತಂತ್ರ್ಯವನ್ನು ಗೂಢವಾಗಿ ವಿಚಾರಿಸಲು ಬಂದಿದ್ದರು.",
			"5": "<sup>5</sup> ಸುವಾರ್ತೆಯ ಸತ್ಯಾರ್ಥವು ನಿಮ್ಮಲ್ಲಿ ಸ್ಥಿರವಾಗಿರಬೇಕೆಂದು ನಾವು ಅವರ ಅಧೀನಕ್ಕೆ ಒಂದು ಗಳಿಗೆಯೂ ಒಳಪಡಲಿಲ್ಲ.",
			"6": "<sup>6</sup> ಆದರೆ ಗಣ್ಯವ್ಯಕ್ತಿಗಳಾಗಿದ್ದವರಿಂದ ನನಗೇನೂ ದೊರೆಯಲಿಲ್ಲ. ಅವರು ಹಿಂದಿನ ಕಾಲದಲ್ಲಿ ಎಂಥವರಾಗಿದ್ದರೋ ನನಗೆ ಲಕ್ಷ್ಯವಿಲ್ಲ. ದೇವರಿಗೆ ಮನುಷ್ಯರನ್ನು ಮೆಚ್ಚಿಸಬೇಕಾಗಿಲ್ಲ. ಗಣ್ಯವ್ಯಕ್ತಿಗಳೆಂದು ಎನಿಸಿಕೊಂಡವರು ನನಗೇನನ್ನೂ ತಿಳಿಸಿಕೊಡಲಿಲ್ಲ.",
			"7": "<sup>7</sup> ಆದರೆ ಸುನ್ನತಿಯಾದವರಿಗೆ ಸುವಾರ್ತೆಯನ್ನು ಸಾರುವ ಕೆಲಸವು ಹೇಗೆ ಪೇತ್ರನಿಗೆ ಕೊಡಲ್ಪಟ್ಟಿತೋ, ಹಾಗೆಯೇ ಅನ್ಯಜನರಿಗೆ ಅದನ್ನು ಸಾರುವ ಕಾರ್ಯವನ್ನು ನನಗೆ ಕೊಡಲ್ಪಟ್ಟಿತು.",
			"8": "<sup>8</sup> ಸುನ್ನತಿಯಾದವರಲ್ಲಿ ಅಪೊಸ್ತಲತನವನ್ನು ನಡೆಸುವುದಕ್ಕೋಸ್ಕರ ಪೇತ್ರನಿಗೆ ವಹಿಸಿಕೊಟ್ಟಂಥ ದೇವರು, ಸುನ್ನತಿಯಿಲ್ಲದವರಲ್ಲಿ ಅದನ್ನು ನಡೆಸುವುದಕ್ಕಾಗಿ ನನಗೆ ವಹಿಸಿದ್ದನು.",
			"9": "<sup>9</sup> ಸಭೆಯ ಸ್ತಂಭಗಳೆಂದು ಕರೆಸಿಕೊಂಡಿರುವ ಯಾಕೋಬ, ಕೇಫ, ಯೋಹಾನರು ದೇವರು ನನಗೆ ದಯಪಾಲಿಸಿರುವ ವರವನ್ನು ತಿಳಿದುಕೊಂಡು, ಅನ್ಯೋನ್ಯತೆಯನ್ನು ತೋರಿಸುವುದಕ್ಕಾಗಿ ನನಗೂ ಬಾರ್ನಬನಿಗೂ ಸಹಕಾರ ನೀಡಿ ಬಲಗೈ ಕೊಟ್ಟು, ನೀವು ಅನ್ಯಜನಗಳ ಬಳಿಗೆ ಹೋಗಿರಿ, ನಾವು ಸುನ್ನತಿಯವರ ಬಳಿಗೆ ಹೋಗುತ್ತೇವೆ ಅಂದರು.",
			"10": "<sup>10</sup> ಆದರೆ ನೀವು ನಮ್ಮಲ್ಲಿರುವ ಬಡವರನ್ನು ಮರೆಯಬಾರದೆಂಬ ಒಂದೇ ಸಂಗತಿಯನ್ನು ಬೇಡಿಕೊಂಡರು, ಹಾಗೆ ಮಾಡುವುದರಲ್ಲಿ ನಾನೂ ಆಸಕ್ತನಾಗಿದ್ದೆನು.",
			"11": "<sup>11</sup> ಕೇಫನು ಅಂತಿಯೋಕ್ಯಕ್ಕೆ ಬಂದಾಗ ಅವನಲ್ಲಿ ತಪ್ಪು ಕಾಣಿಸಿಕೊಂಡದ್ದರಿಂದ ನಾನು ಅವನನ್ನು ಮುಖಾಮುಖಿಯಾಗಿ ಖಂಡಿಸಿದೆನು.",
			"12": "<sup>12</sup> ಏಕೆಂದರೆ ಯಾಕೋಬನ ಕಡೆಯಿಂದ ಕೆಲವರು ಬರುವುದಕ್ಕೆ ಮೊದಲು ಅವನು ಅನ್ಯಜನರೊಂದಿಗೆ ಊಟ ಮಾಡುತ್ತಿದ್ದನು. ಅವರು ಬಂದ ಮೇಲೆ ಸುನ್ನತಿಯವರಾದ ಅವರಿಗೆ ಅವನು ಭಯಪಟ್ಟು, ಅನ್ಯಜನರನ್ನು ಬಿಟ್ಟು ತನ್ನನ್ನು ಪ್ರತ್ಯೇಕಿಸಿಕೊಂಡನು.",
			"13": "<sup>13</sup> ಇದಲ್ಲದೆ ಉಳಿದ ಯೆಹೂದ್ಯರೂ ಅವನೊಂದಿಗೆ ಸೇರಿ ಹಾಗೆಯೇ ಕಪಟಮಾಡಿದರು. ಹೀಗೆ ಬಾರ್ನಬನೂ ಅವರ ಕಪಟದ ಸೆಳವಿಗೆ ಬಿದ್ದನು.",
			"14": "<sup>14</sup> ಅವರು ಸುವಾರ್ತೆಯ ಸತ್ಯಾರ್ಥದ ಪ್ರಕಾರ ಸರಿಯಾಗಿ ನಡೆಯುತ್ತಿಲ್ಲವೆಂಬುದನ್ನು ನಾನು ಕಂಡಾಗ ಎಲ್ಲರ ಮುಂದೆ ಕೇಫನಿಗೆ ಹೇಳಿದ್ದೇನಂದರೆ, ನೀನು ಯೆಹೂದ್ಯನಾಗಿದ್ದು ಯೆಹೂದ್ಯರಂತೆ ನಡೆಯದೆ ಅನ್ಯಜನರಂತೆ ಬದುಕಿ, ಅನ್ಯಜನರಿಗೆ, ನೀವು ಯೆಹೂದ್ಯರಂತೆ ನಡೆದುಕೊಳ್ಳಬೇಕೆಂದು ಒತ್ತಾಯಪಡಿಸಿ ಹೇಳುವುದಾದರೂ ಹೇಗೆ?",
			"15": "<sup>15</sup> ನಾವಂತೂ ಜನ್ಮತ ಯೆಹೂದ್ಯರು ಪಾಪಿಗಳೆನಿಸಿಕೊಂಡ ಅನ್ಯಜನರಲ್ಲ.",
			"16": "<sup>16</sup> ಯಾವನಾದರೂ ಯೇಸು ಕ್ರಿಸ್ತನ ಮೇಲೆ ನಂಬಿಕೆ ಇಡುವುದರಿಂದಲೇ ಹೊರತು ಧರ್ಮಶಾಸ್ತ್ರದ ನೇಮನಿಷ್ಠೆಗಳನ್ನು ಅನುಸರಿಸುವುದರಿಂದ ನೀತಿವಂತನೆಂದು ನಿರ್ಣಯಿಸಲ್ಪಡುವುದಿಲ್ಲವೆಂಬುದು ನಮಗೆ ತಿಳಿದಿರುವುದರಿಂದ ನಾವು ಸಹ ಧರ್ಮಶಾಸ್ತ್ರದ ನೇಮನಿಷ್ಠೆಗಳನ್ನು ಬಿಟ್ಟು ಕ್ರಿಸ್ತ ಯೇಸುವಿನಲ್ಲಿ ನಂಬಿಕೆಯಿಟ್ಟೆವು. ಏಕೆಂದರೆ ಯಾರೂ ಧರ್ಮಶಾಸ್ತ್ರದ ನೇಮನಿಷ್ಠೆಗಳನ್ನನುಸರಿಸಿ ನೀತಿವಂತನೆಂದು ನಿರ್ಣಯಿಸಲ್ಪಡುವುದಿಲ್ಲ.",
			"17": "<sup>17</sup> ಆದರೆ ನಾವು ಕ್ರಿಸ್ತನ ಮೂಲಕ ನೀತಿವಂತರೆಂಬ ನಿರ್ಣಯ ಹೊಂದುವುದಕ್ಕೆ ಪ್ರಯತ್ನಿಸುತ್ತಿರುವಾಗ, ನಾವೂ ಪಾಪಿಗಳಾಗಿ ತೋರಿಬಂದರೆ ಕ್ರಿಸ್ತನು ಪಾಪಕ್ಕೆ ಸಹಾಯಕನಾಗಿರುವನೋ? ಎಂದಿಗೂ ಇಲ್ಲ.",
			"18": "<sup>18</sup> ನಾನು ಕೆಡವಿದ್ದನ್ನೇ ತಿರುಗಿ ಕಟ್ಟಿದರೆ ನನ್ನನ್ನು ನಾನೇ ಅಪರಾಧಿ ಎಂದು ತೋರಿಸಿಕೊಳ್ಳುತ್ತೇನಲ್ಲಾ.",
			"19": "<sup>19</sup> ನಾನಂತೂ ದೇವರಿಗಾಗಿ ಜೀವಿಸುವುದಕ್ಕೋಸ್ಕರ ಧರ್ಮಶಾಸ್ತ್ರದ ಮೂಲಕವಾಗಿ ಧರ್ಮಶಾಸ್ತ್ರದ ಪಾಲಿಗೆ ಸತ್ತವನಾಗಿದ್ದೇನೆ.",
			"20": "<sup>20</sup> ನಾನು ಸಹ ಕ್ರಿಸ್ತನೊಂದಿಗೆ ಶಿಲುಬೆಗೆ ಹಾಕಿಸಿಕೊಂಡವನಾಗಿದ್ದೇನೆ, ಇನ್ನು ಜೀವಿಸುವವನು ನಾನಲ್ಲ, ಕ್ರಿಸ್ತನು ನನ್ನಲ್ಲಿ ಜೀವಿಸುತ್ತಾನೆ. ಈಗ ಶರೀರದಲ್ಲಿರುವ ನಾನು ಜೀವಿಸುವುದು ಹೇಗೆಂದರೆ ದೇವಕುಮಾರನ ಮೇಲಣ ನಂಬಿಕೆಯಿಂದಲೇ. ಆತನು ನನ್ನನ್ನು ಪ್ರೀತಿಸಿ ನನಗಾಗಿ ತನ್ನನ್ನು ಒಪ್ಪಿಸಿಕೊಟ್ಟನು.",
			"21": "<sup>21</sup> ನಾನು ದೇವರ ಕೃಪೆಯನ್ನು ತಿರಸ್ಕರಿಸುವುದಿಲ್ಲ. ಧರ್ಮಶಾಸ್ತ್ರದ ಮೂಲಕ ನೀತಿವಂತರಾಗಬಹುದಾದರೆ ಕ್ರಿಸ್ತನು ಮರಣಕ್ಕೀಡಾದುದು ನಿರರ್ಥಕವೇ ಸರಿ."
		},
		"3": {
			"1": "<sup>1</sup> ಎಲೈ ಬುದ್ಧಿಹೀನರಾದ ಗಲಾತ್ಯದವರೇ! ನಿಮ್ಮನ್ನು ಮರುಳುಗೊಳಿಸಿದವರು ಯಾರು? ಯೇಸು ಕ್ರಿಸ್ತನು ಶಿಲುಬೆಗೆ ಹಾಕಿಸಿಕೊಂಡವನಾಗಿ ನಿಮ್ಮ ಕಣ್ಣೆದುರಿನಲ್ಲಿಯೇ ವರ್ಣಿಸಲ್ಪಟ್ಟನಲ್ಲವೇ?",
			"2": "<sup>2</sup> ಒಂದು ಸಂಗತಿಯನ್ನು ಮಾತ್ರ ನಿಮ್ಮಿಂದ ತಿಳಿದುಕೊಳ್ಳಬೇಕೆಂದಿದ್ದೇನೆ. ನೀವು ದೇವರಾತ್ಮನನ್ನು ಹೊಂದಿದ್ದು ಧರ್ಮಶಾಸ್ತ್ರದ ನೇಮನಿಷ್ಠೆಗಳನ್ನು ಅನುಸರಿಸಿದ್ದರಿಂದಲೋ ಅಥವಾ ನಂಬಿಕೆಯ ಸುವಾರ್ತೆಯನ್ನು ಕೇಳಿದ್ದರಿಂದಲೋ?",
			"3": "<sup>3</sup> ನೀವು ಇಷ್ಟು ಬುದ್ಧಿಯಿಲ್ಲದವರೋ? ದೇವರಾತ್ಮಾನುಸಾರವಾಗಿ ಪ್ರಾರಂಭಿಸಿ ಈಗ ಶಾರೀರಿಕ ಸಂಬಂಧವಾಗಿ ಕೊನೆಗೊಳಿಸುವಿರೋ?",
			"4": "<sup>4</sup> ನೀವು ಇವೆಲ್ಲವುಗಳನ್ನು ಅನುಭವಿಸಿದ್ದು ವ್ಯರ್ಥವೋ? ವ್ಯರ್ಥವೇ ಎಂದು ಹೇಳಬೇಕೇನು?",
			"5": "<sup>5</sup> ದೇವರು ತನ್ನ ಆತ್ಮನನ್ನು ನಿಮಗೆ ಹೇರಳವಾಗಿ ಕೊಟ್ಟು ನಿಮ್ಮಲ್ಲಿ ಮಹತ್ಕಾರ್ಯಗಳನ್ನು ನಡಿಸುತ್ತಾ ಬಂದದ್ದು, ನೀವು ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಅನುಸರಿಸಿದ್ದರಿಂದಲೋ ಅಥವಾ ಸುವಾರ್ತೆಯನ್ನು ಕೇಳಿ ನಂಬಿದ್ದರಿಂದಲೋ?",
			"6": "<sup>6</sup> ಅಬ್ರಹಾಮನು ದೇವರನ್ನು ನಂಬಿದನು, ಆ ನಂಬಿಕೆಯು ಅವನ ಪಾಲಿಗೆ ನೀತಿಯೆಂದು ಎಣಿಸಲ್ಪಟ್ಟಿತು ಎಂಬುದಾಗಿ ಬರೆದಿದೆಯಲ್ಲಾ.",
			"7": "<sup>7</sup> ಆದ್ದರಿಂದ ನಂಬುವವರೇ ಅಬ್ರಹಾಮನ ಮಕ್ಕಳೆಂದು ತಿಳಿದುಕೊಳ್ಳಿರಿ.",
			"8": "<sup>8</sup> ದೇವರು ಅನ್ಯಜನರನ್ನು ನಂಬಿಕೆಯ ನಿಮಿತ್ತವಾಗಿಯೇ ನೀತಿವಂತರೆಂದು ನಿರ್ಣಯಿಸುವನೆಂಬುದಾಗಿರುವ ವೇದವಾಕ್ಯವನ್ನು ಮೊದಲೇ ಕಂಡು ಅಬ್ರಹಾಮನಿಗೆ, ನಿನ್ನ ಮೂಲಕ ಎಲ್ಲಾ ಜನಾಂಗಗಳವರಿಗೆ ಆಶೀರ್ವಾದವುಂಟಾಗುವುದೆಂಬ ಸುವಾರ್ತೆಯನ್ನು ಮುಂಚಿತವಾಗಿಯೇ ತಿಳಿಸಿತು.",
			"9": "<sup>9</sup> ಹೀಗಿರಲಾಗಿ ಯೇಸು ಕ್ರಿಸ್ತನಲ್ಲಿ ನಂಬಿಕೆಯಿಡುವವರು, ನಂಬಿಕೆಯಿಟ್ಟ ಅಬ್ರಹಾಮನೊಂದಿಗೆ ಆಶೀರ್ವಾದವನ್ನು ಹೊಂದುವರು.",
			"10": "<sup>10</sup> ಮೋಶೆಯ ಧರ್ಮಶಾಸ್ತ್ರದ ನೇಮನಿಷ್ಠೆಗಳನ್ನು ಆಧಾರಮಾಡಿಕೊಂಡಿರುವವರೆಲ್ಲರೂ ಶಾಪಾಗ್ರಸ್ತರಾಗಿದಾರೆ. ಹೇಗೆಂದರೆ, ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿ ಬರೆದಿರುವವುಗಳೆಲ್ಲವನ್ನೂ ಪ್ರತಿನಿತ್ಯವೂ ಪಾಲಿಸದಿರುವ ಪ್ರತಿಯೊಬ್ಬನು ಶಾಪಗ್ರಸ್ತನೇ ಎಂದು ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿ ಬರೆದಿದೆ.",
			"11": "<sup>11</sup> ಇದಲ್ಲದೆ ಧರ್ಮಶಾಸ್ತ್ರದಿಂದ ಯಾರೂ ದೇವರ ಸನ್ನಿಧಿಯಲ್ಲಿ ನೀತಿವಂತರಾಗಲು ಸಾಧ್ಯವಿಲ್ಲವೆಂದು ಸ್ಪಷ್ಟವಾಗಿದೆ, ಏಕೆಂದರೆ ನೀತಿವಂತನು ನಂಬಿಕೆಯಿಂದಲೇ ಬದುಕುವನು ಎಂದು ಪ್ರಕಟವಾಗಿದೆ.",
			"12": "<sup>12</sup> ಹಾಗೆಯೇ ಮೋಶೆಯ ಧರ್ಮಶಾಸ್ತ್ರಕ್ಕೆ ನಂಬಿಕೆಯು ಆಧಾರವಾಗಿಲ್ಲ, ಅದರ ನೇಮಗಳನ್ನು ಆಚರಿಸುವವನೇ ಅವುಗಳ ಮೂಲಕ ಬದುಕುವನು ಎಂದು ಬರೆದಿದೆ.",
			"13": "<sup>13</sup> ಯೇಸು ಕ್ರಿಸ್ತನು ನಮನ್ನು ಶಾಪದಿಂದ ಬಿಡಿಸುವುದಕ್ಕಾಗಿ ತಾನೇ ಶಾಪಗ್ರಸ್ತನಾದನು. ಏಕೆಂದರೆ ಮೋಶೆಯ ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿ ಬರೆದಿರುವ ಪ್ರಕಾರ ಮರಕ್ಕೆ ತೂಗುಹಾಕಲ್ಪಟ್ಟು ಪ್ರತಿಯೊಬ್ಬನು ಶಾಪಗ್ರಸ್ತನು ಎಂದು ತಿಳಿದಿದ್ದೇವೆ.",
			"14": "<sup>14</sup> ಅಬ್ರಹಾಮನಿಗೆ ಉಂಟಾದ ಆಶೀರ್ವಾದವು, ಕ್ರಿಸ್ತ ಯೇಸುವಿನಲ್ಲಿ ಅನ್ಯಜನರಿಗೆಲ್ಲ ಸಿಗುವಂತೆಯೂ ಮತ್ತು ದೇವರು ವಾಗ್ದಾನ ಮಾಡಿದ ಪರಿಶುದ್ಧಾತ್ಮನು ನಮಗೆ ನಂಬಿಕೆಯ ಮೂಲಕ ದೊರಕುವಂತೆಯೂ, ತನ್ನ ಮರಣದ ಮೂಲಕ ಕ್ರಿಸ್ತನು ನಮ್ಮನ್ನು ಕೊಂಡುಕೊಂಡನು.",
			"15": "<sup>15</sup> ಸಹೋದರರೇ, ದೈನಂದಿನ ಜೀವನದಿಂದ ಒಂದು ದೃಷ್ಟಾಂತವನ್ನು ನಿಮಗೆ ಹೇಳುತ್ತೇನೆ, ಒಬ್ಬನು ಮತ್ತೊಬ್ಬನೊಡನೆ ಸ್ಥಿರಪಡಿಡಿಸಿದ ಒಂದು ಒಪ್ಪಂದವು ಕೇವಲ ಮನುಷ್ಯನದ್ದಾಗಿದ್ದರೂ, ಅದನ್ನು ಯಾರೂ ರದ್ದುಮಾಡುವಂತಿಲ್ಲ ಮತ್ತು ಅದಕ್ಕೆ ಏನನ್ನೂ ಸೇರಿಸುವಂತಿಲ್ಲ.",
			"16": "<sup>16</sup> ಹಾಗೆಯೇ, ದೇವರು ಅಬ್ರಹಾಮನಿಗೆ ಮತ್ತು ಅವನಿಂದ ಬರುವ ಪೀಳಿಗೆಗೆ ವಾಗ್ದಾನ ಮಾಡಿದನು. ಆದರೆ ಪೀಳಿಗೆ ಎಂದು ಹೇಳಿದಾಗ, ಅದು ವಂಶದಲ್ಲಿ ಇರುವ ಎಲ್ಲರಿಗೂ ಅನ್ವಯಿಸದೇ, ಅಬ್ರಹಾಮನ ವಂಶಕ್ಕೆ ಸೇರಿದ ಒಬ್ಬ ವ್ಯಕ್ತಿಯನ್ನೇ ಸೂಚಿಸುತ್ತದೆ. ಆ ಒಬ್ಬನು ಕ್ರಿಸ್ತನೇ.",
			"17": "<sup>17</sup> ನಾನು ಹೇಳಬಯಸುವುದೇನೆಂದರೆ, ದೇವರು ಮೊದಲು ಅನುಮೋದಿಸಿದ ಒಡಂಬಡಿಕೆಯನ್ನು, ನಾನೂರ ಮೂವತ್ತು ವರುಷಗಳ ನಂತರ ಬಂದ ಮೋಶೆಯ ಧರ್ಮಶಾಸ್ತ್ರವು ಅದನ್ನು ನಿರರ್ಥಕಗೊಳಿಸಿ, ಆ ವಾಗ್ದಾನವನ್ನು ಬರಿದಾಗಿಮಾಡಲು ಸಾಧ್ಯವಿಲ್ಲ.",
			"18": "<sup>18</sup> ಅಬ್ರಹಾಮನಿಗೆ ಸಿಕ್ಕ ಆ ಬಾಧ್ಯತೆ ಮೋಶೆಯ ಧರ್ಮಶಾಸ್ತ್ರದ ಮೇಲೆ ಆಧಾರಗೊಂಡಿರುವುದಾದರೆ, ವಾಗ್ದಾನದ ಮೇಲೆ ಆಧಾರವಾಗಲು ಸಾಧ್ಯವಾಗುತ್ತಿರಲಿಲ್ಲ. ಆದರೆ ದೇವರು ಅಬ್ರಹಾಮನಿಗೆ ಕೊಟ್ಟ ಬಾಧ್ಯತೆಯನ್ನು ವಾಗ್ದಾನದ ಮೂಲಕವಾಗಿಯೇ ದಯಪಾಲಿಸಿದ್ದಾನೆ.",
			"19": "<sup>19</sup> ಹಾಗಾದರೆ ಮೋಶೆಯ ಧರ್ಮಶಾಸ್ತ್ರವು ಯಾತಕ್ಕೆ? ವಾಗ್ದಾನದಲ್ಲಿ ಸೂಚಿತನಾದವನು ಹುಟ್ಟಿ ಬರುವತನಕ ದೇವರು ಇಂಥಿಂಥದು ಅಪರಾಧವೆಂದು ತೋರಿಸುವದಕ್ಕಾಗಿ ಅದನ್ನು ವಾಗ್ದಾನದ ತರುವಾಯ ನೇಮಿಸಿ ದೇವದೂತರ ಮುಖಾಂತರ ಒಬ್ಬ ಮಧ್ಯಸ್ಥನ ಕೈಯಲ್ಲಿ ಕೊಟ್ಟನು.",
			"20": "<sup>20</sup> ಒಂದೇ ಪಕ್ಷವಿದ್ದರೆ ಮಧ್ಯಸ್ಥನು ಬೇಕಿಲ್ಲ. ದೇವರಾದರೋ ಒಬ್ಬನೇ.",
			"21": "<sup>21</sup> ಹಾಗಾದರೆ ಮೋಶೆಯ ಧರ್ಮಶಾಸ್ತ್ರವು ದೇವರ ವಾಗ್ದಾನಗಳಿಗೆ ವಿರುದ್ಧವೋ? ಹಾಗೆ ಎಂದಿಗೂ ತಿಳಿದುಕೊಳ್ಳಬಾರದು. ಬದುಕಿಸುವುದಕ್ಕೆ ಶಕ್ತವಾಗಿರುವ ಧರ್ಮಶಾಸ್ತ್ರವು ಕೊಡಲ್ಪಟ್ಟಿದ್ದರೆ ಅದನ್ನು ಅನುಸರಿಸುವುದರ ಮೂಲಕ ನಿಸ್ಸಂದೇಹವಾಗಿ ನೀತಿಯುಂಟಾಗುತ್ತಿತ್ತು.",
			"22": "<sup>22</sup> ಸಮಸ್ತ ಲೋಕವು ಪಾಪಕ್ಕೆ ಅಧೀನವಾಯಿತೆಂದು ಪವಿತ್ರಗ್ರಂಥವು ಸ್ಪಷ್ಪೀಕರಿಸುತ್ತದೆ. ಹೀಗಿರುವುದರಿಂದ ವಾಗ್ದಾನ ಮಾಡಿದ ಸೌಭಾಗ್ಯವನ್ನು ಯೇಸು ಕ್ರಿಸ್ತನಲ್ಲಿ ನಂಬಿಕೆಯಿಟ್ಟವರಿಗೆ ಅವರ ನಂಬಿಕೆಯ ಆಧಾರದ ಮೇಲೆ ಕೊಡಲಾಗುತ್ತದೆ.",
			"23": "<sup>23</sup> ಆದರೆ ಯೇಸು ಕ್ರಿಸ್ತನ ನಂಬಿಕೆಯಲ್ಲಿ ಜೀವಿಸುವ ಕಾಲ ಬರುವುದಕ್ಕೆ ಮೊದಲು ನಾವು ಮುಂದಕ್ಕೆ ಪ್ರಕಟವಾಗಬೇಕಾಗಿದ್ದ ಆ ನಂಬಿಕೆಗೆ ವಶವಾಗುವುದಕ್ಕಾಗಿ ಮೋಶೆಯ ಧರ್ಮಶಾಸ್ತ್ರದಿಂದ ನಾವು ಬಂಧಿಸಲ್ಪಟ್ಟಿದ್ದೆವು.",
			"24": "<sup>24</sup> ನಾವು ನಂಬಿಕೆಯಿಂದ ಯೇಸು ಕ್ರಿಸ್ತನಲ್ಲಿ ನೀತಿವಂತರೆಂಬ ನಿರ್ಣಯವನ್ನು ಹೊಂದುವ ಕಾಲ ಬರುವ ತನಕ, ಮೋಶೆಯ ಧರ್ಮಶಾಸ್ತ್ರವು ನಮ್ಮನ್ನು ಮುನ್ನಡೆಸುವ ಪಾಲಕನಂತಾಗಿತ್ತು.",
			"25": "<sup>25</sup> ಆದರೆ ಈಗ ಕ್ರಿಸ್ತ ನಂಬಿಕೆಯು ಬಂದಿರಲಾಗಿ ನಾವು ಮುನ್ನಡೆಸುವಂಥ ಧರ್ಮಶಾಸ್ತ್ರದ ಹತೋಟಿಯಲ್ಲಿ ಇಲ್ಲ.",
			"26": "<sup>26</sup> ಹಾಗೆ ನೀವೆಲ್ಲರೂ ಕ್ರಿಸ್ತ ಯೇಸುವಿನಲ್ಲಿಟ್ಟಿರುವ ನಂಬಿಕೆಯ ಮೂಲಕ ದೇವರ ಮಕ್ಕಳಾಗಿದ್ದೀರಿ.",
			"27": "<sup>27</sup> ಹೇಗೆಂದರೆ ಕ್ರಿಸ್ತನಲ್ಲಿ ಸೇರುವುದಕ್ಕೆ ದೀಕ್ಷಾಸ್ನಾನ ಮಾಡಿಸಿಕೊಂಡಿರುವ ನೀವೆಲ್ಲರೂ ಕ್ರಿಸ್ತನನ್ನು ಧರಿಸಿಕೊಂಡಿದ್ದೀರಿ.",
			"28": "<sup>28</sup> ನೀವೆಲ್ಲರೂ ಕ್ರಿಸ್ತ ಯೇಸುವಿನಲ್ಲಿ ಒಂದೇ ಆಗಿರುವುದರಿಂದ ಯೆಹೂದ್ಯನು ಅಥವಾ ಗ್ರೀಕನು ಎಂದಾಗಲೀ, ಆಳು ಅಥವಾ ಒಡೆಯ ಎಂದಾಗಲೀ, ಗಂಡು ಹಾಗೂ ಹೆಣ್ಣು ಎಂದಾಗಲೀ ಭೇದವಿಲ್ಲ.",
			"29": "<sup>29</sup> ನೀವು ಕ್ರಿಸ್ತನವರಾಗಿದ್ದರೆ ಅಬ್ರಹಾಮನ ವಂಶದವರಾಗಿ ಹಾಗೂ ದೇವರ ವಾಗ್ದಾನಕ್ಕನುಸಾರವಾಗಿ ಬಾಧ್ಯರೂ ಆಗಿದ್ದೀರಿ."
		},
		"4": {
			"1": "<sup>1</sup> ಆದರೆ ನಾನು ಹೇಳುವುದೇನಂದರೆ, ವಾರಸುದಾರನು, ತಾನು ಆಸ್ತಿಗೆಲ್ಲಾ ಒಡೆಯನಾಗಿದ್ದರೂ ಬಾಲಕನಾಗಿರುವ ತನಕ ದಾಸನಂತೆಯೇ ಇರುವನು.",
			"2": "<sup>2</sup> ತಂದೆಯು ಗೊತ್ತುಮಾಡಿದ ಸಮಯದವರೆಗೂ ಪಾಲಕರ ಮತ್ತು ಕಾರ್ಯನಿರ್ವಾಹಕರ ಅಧೀನದಲ್ಲಿರುವನು.",
			"3": "<sup>3</sup> ಹಾಗೆಯೇ ನಾವು ಸಹ ಬಾಲಕರಾಗಿದ್ದಾಗ ಲೋಕದ ಮೂಲತತ್ವಗಳಿಗೆ ಅಧೀನರಾಗಿದ್ದೆವು.",
			"4": "<sup>4</sup> ಆದರೆ ಸೂಕ್ತಸಮಯ ಬಂದಾಗ ದೇವರು ತನ್ನ ಮಗನನ್ನು ಕಳುಹಿಸಿಕೊಟ್ಟನು. ಧರ್ಮಶಾಸ್ತ್ರಕ್ಕೆ ಅಧೀನರಾದವರನ್ನು ಬಿಡಿಸಬೇಕೆಂತಲೂ, ಪುತ್ರರ ಬಾಧ್ಯತೆಯ ಪದವಿಯನ್ನು ನಮಗೆ ದೊರಕಿಸಿಕೊಡಬೇಕೆಂತಲೂ ಆತನು ಸ್ತ್ರೀಯಲ್ಲಿ ಹುಟ್ಟಿದವನಾಗಿಯೂ, ಧರ್ಮಶಾಸ್ತ್ರಾಧೀನನಾಗಿಯೂ ಬಂದನು.",
			"6": "<sup>6</sup> ನೀವು ಪುತ್ರರಾಗಿರುವುದರಿಂದ ದೇವರು, ಅಪ್ಪಾ ತಂದೆಯೇ ಎಂದು ಕೂಗುವ ತನ್ನ ಮಗನ ಆತ್ಮನನ್ನು ನಮ್ಮ ಹೃದಯಗಳಲ್ಲಿ ಕಳುಹಿಸಿಕೊಟ್ಟನು.",
			"7": "<sup>7</sup> ಹೀಗಿರುವಲ್ಲಿ ಇನ್ನು ನೀನು ಸೇವಕನಲ್ಲ, ಮಗನಾಗಿದ್ದೀ. ಮಗನೆಂದ ಮೇಲೆ ದೇವರ ಮೂಲಕ ವಾರಸುದಾರನು ಆಗಿದ್ದೀ.",
			"8": "<sup>8</sup> ಹಿಂದೆ ನೀವು ದೇವರನ್ನು ಅರಿಯದೆ, ಸ್ವಾಭಾವಿಕವಾಗಿ ದೇವರಲ್ಲದವರುಗಳಿಗೆ ದಾಸರಾಗಿದ್ದೀರಿ.",
			"9": "<sup>9</sup> ಈಗಲಾದರೋ ನೀವು ದೇವರನ್ನು ತಿಳಿದುಕೊಂಡಿದ್ದೀರಿ. ವಿಶೇಷವಾಗಿ ದೇವರು ನಿಮ್ಮನ್ನು ತಿಳಿದುಕೊಂಡಿದ್ದಾನೆ. ಹೀಗಿರಲಾಗಿ ನೀವು ಬಲವಿಲ್ಲದ, ಕೆಲಸಕ್ಕೆ ಬಾರದ, ದರಿದ್ರವಾದ ಮೂಲತತ್ವಗಳಿಗೆ ಹಿಂತಿರುಗಿ ಅದಕ್ಕೆ ದಾಸರಾಗುವುದಕ್ಕೆ ಏಕೆ ಬಯಸುತ್ತೀರಿ?",
			"10": "<sup>10</sup> ನೀವು ಆಯಾ ದಿನಗಳನ್ನೂ, ತಿಂಗಳುಗಳನ್ನೂ, ಕಾಲಗಳನ್ನೂ, ಸಂವತ್ಸರಗಳನ್ನೂ ನಿಷ್ಠೆಯಿಂದ ಆಚರಿಸಿದ್ದೀರಿ.",
			"11": "<sup>11</sup> ನಾನು ನಿಮಗೋಸ್ಕರ ಪ್ರಯಾಸಪಟ್ಟದ್ದು ನಿಷ್ಫಲವಾಯಿತೋ ಏನೋ ಎಂದು ನಿಮ್ಮ ಕುರಿತು ಭಯಪಡುತ್ತೇನೆ.",
			"12": "<sup>12</sup> ಸಹೋದರರೇ, ನಾನು ನಿಮ್ಮಂತೆ ಆದಹಾಗೆ ನೀವು ನನ್ನಂತೆ ಹಾಗೆ ಆಗಬೇಕೆಂದು ನಿಮ್ಮನ್ನು ಬೇಡಿಕೊಳ್ಳುತ್ತೇನೆ. ನೀವು ನನಗೇನೂ ಅನ್ಯಾಯ ಮಾಡಲಿಲ್ಲ.",
			"13": "<sup>13</sup> ನಾನು ದೈಹಿಕವಾಗಿ ಅಸ್ವಸ್ಥನಾಗಿದ್ದುದರಿಂದ ನಿಮ್ಮಲ್ಲಿದ್ದು ಮೊದಲನೆಯ ಬಾರಿಗೆ ನಿಮಗೆ ಸುವಾರ್ತೆ ಸಾರಿದ್ದನ್ನು ನೀವು ಬಲ್ಲಿರಿ.",
			"14": "<sup>14</sup> ನನ್ನ ದೇಹಸ್ಥಿತಿಯಿಂದ ನಿಮಗೆ ಸಮಸ್ಯೆಗಳು ಬಂದಾಗ್ಯೂ ಕೂಡ, ನೀವು ನನ್ನನ್ನು ಹೀನೈಸಲಿಲ್ಲ, ತಿರಸ್ಕರಿಸಲಿಲ್ಲ. ನನ್ನನ್ನು ದೇವದೂತನಂತೆ, ಕ್ರಿಸ್ತ ಯೇಸುವಿನಂತೆ ಸೇರಿಸಿಕೊಂಡಿದ್ದೀರಿ.",
			"15": "<sup>15</sup> ಆಗ ನಮಗೆ ಶುಭವಾಯಿತೆಂದು ನೀವು ಅಂದುಕೊಂಡಿರಲ್ಲಾ, ಆ ನಿಮ್ಮ ಶುಭವು ಎಲ್ಲಿ ಹೋಯಿತು? ಸಾಧ್ಯವಾಗಿದ್ದರೆ ನಿಮ್ಮ ಕಣ್ಣುಗಳನ್ನಾದರೂ ಕಿತ್ತು ನನಗೆ ಕೊಡುತ್ತಿದ್ದಿರೆಂದು ನಿಮ್ಮನ್ನು ಕುರಿತು ಸಾಕ್ಷಿ ಹೇಳುತ್ತೇನೆ.",
			"16": "<sup>16</sup> ಹೀಗಿರಲಾಗಿ ನಾನು ನಿಮಗೆ ಸತ್ಯವನ್ನು ಹೇಳುವುದರಿಂದ ನಿಮಗೆ ಶತ್ರುವಾಗಿದ್ದೇನೋ?",
			"17": "<sup>17</sup> ಅವರು ನಿಮ್ಮನ್ನು ಮೆಚ್ಚಿಸುವುದಕ್ಕೆ ಆಸಕ್ತಿಯನ್ನು ತೋರಿಸುತ್ತಿರುವುದು ಒಳಿತಾಗಿ ಅಲ್ಲ, ನೀವು ಅವರಿಗಾಗಿ ಆಸಕ್ತಿಯನ್ನು ತೋರಿಸಬೇಕೆಂದು ಅವರು ನಿಮ್ಮನ್ನು ಹೊರಗೆ ತಳ್ಳಿ ಬಿಡಲು ಬಯಸುತ್ತಿದ್ದಾರೆ.",
			"18": "<sup>18</sup> ನಾನು ನಿಮ್ಮ ಸಂಗಡ ಇರುವಾಗ ಮಾತ್ರವಲ್ಲದೆ ಯಾವಾಗಲೂ ಒಳ್ಳೆಯ ವಿಷಯಕ್ಕಾಗಿ ನೀವು ಆಸಕ್ತಿಯನ್ನು ತೋರಿಸುವುದು ಒಳ್ಳೆಯದೇ.",
			"19": "<sup>19</sup> ನನ್ನ ಪ್ರಿಯರಾದ ಮಕ್ಕಳೇ, ಕ್ರಿಸ್ತನ ಸಾರೂಪ್ಯವು ನಿಮ್ಮಲ್ಲಿ ಉಂಟಾಗುವ ತನಕ ನಾನು ನಿಮಗಾಗಿ ಪ್ರಸವವೇದನೆ ಪಡುತ್ತಿರುವೆನು.",
			"20": "<sup>20</sup> ನಿಮ್ಮ ವಿಷಯದಲ್ಲಿ ಏನು ಮಾಡಬೇಕೋ ನನಗೆ ತೋಚುತ್ತಿಲ್ಲ. ಬಹುಶಃ ನೇರವಾಗಿ ನಿಮ್ಮ ಬಳಿಗೆ ಬಂದು, ನಿಮ್ಮ ಮಧ್ಯದಲ್ಲೇ ಇದ್ದು ನನ್ನ ಮಾತಿನ ವೈಖರಿಯನ್ನು ಬದಲಾಯಿಸಿದ್ದರೆ ಚೆನ್ನಾಗಿರುತ್ತಿತ್ತೇನೋ!",
			"21": "<sup>21</sup> ಧರ್ಮಶಾಸ್ತ್ರಕ್ಕೆ ಅಧೀನರಾಗಿರುವುದಕ್ಕೆ ಬಯಸುವವರೇ, ಧರ್ಮಶಾಸ್ತ್ರವು ಏನು ಹೇಳುತ್ತದೆಂದು ನಿಮಗೆ ತಿಳಿದಿಲ್ಲವೋ? ನನಗೆ ತಿಳಿಯಪಡಿಸಿರಿ.",
			"22": "<sup>22</sup> ಅದರಲ್ಲಿ ಬರೆದಿರುವುದೇನಂದರೆ, ಅಬ್ರಹಾಮನಿಗೆ ಇಬ್ಬರು ಮಕ್ಕಳಿದ್ದರು. ಒಬ್ಬನು ದಾಸಿಯಲ್ಲಿ ಹುಟ್ಟಿದವನು, ಮತ್ತೊಬ್ಬನು ಸ್ವತಂತ್ರಳಾದವಳಲ್ಲಿ ಹುಟ್ಟಿದವನು.",
			"23": "<sup>23</sup> ದಾಸಿಯ ಮಗನು ಶರೀರಕ್ಕನುಸಾರವಾಗಿ ಹುಟ್ಟಿದವನು, ಸ್ವತಂತ್ರಳಾದವಳ ಮಗನಾದರೋ ವಾಗ್ದಾನದ ಮೂಲಕ ಹುಟ್ಟಿದವನು.",
			"24": "<sup>24</sup> ಈ ಸಂಗತಿಗಳು ಹೋಲಿಕೆಗಳಾಗಿವೆ. ಹೇಗೆಂದರೆ ಆ ಇಬ್ಬರು ಸ್ತ್ರೀಯರು ಎರಡು ಒಡಂಬಡಿಕೆಗಳೇ, ಒಂದು ಒಡಂಬಡಿಕೆಯು ಸೀನಾಯಿ ಪರ್ವತದಿಂದ ಉಂಟಾಗಿ ದಾಸತ್ವದಲ್ಲಿರಬೇಕಾದ ಮಕ್ಕಳನ್ನು ಹೆರುವಂಥದು, ಅದೇ ಹಾಗರಳು.",
			"25": "<sup>25</sup> ಹಾಗರಳು ಅಂದರೆ ಅರಬಸ್ಥಾನದಲ್ಲಿರುವ ಸೀನಾಯಿ ಪರ್ವತವನ್ನು ಸೂಚಿಸುವಂತಹದ್ದು ಈಗಿನ ಯೆರೂಸಲೇಮಿಗೆ ಎಂಬುವಳಿಗೆ ಹೋಲಿಕೆಯಾಗಿದ್ದಾಳೆ, ಏಕೆಂದರೆ ಆಕೆಯು ತನ್ನ ಮಕ್ಕಳೊಂದಿಗೆ ಈಗ ದಾಸತ್ವದಲ್ಲಿದ್ದಾಳೆ.",
			"26": "<sup>26</sup> ಆದರೆ ಮೇಲಣ ಯೆರೂಸಲೇಮ್ ಎಂಬವಳು ಸ್ವತಂತ್ರಳು, ಇವಳೇ ನಮಗೆ ತಾಯಿ.",
			"27": "<sup>27</sup> ಹೆರದ ಬಂಜೆಯೇ, ಆನಂದಿಸು. ಪ್ರಸವವೇದನೆಯಿಲ್ಲದವಳೇ, ಹರ್ಷೋದ್ಗಾರಮಾಡು, ಗಂಡನುಳ್ಳವಳಿಗಿಂತ ಗಂಡಬಿಟ್ಟವಳಿಗೆ ಮಕ್ಕಳು ಹೆಚ್ಚು ಎಂದು ಬರೆದಿದೆಯಲ್ಲಾ.",
			"28": "<sup>28</sup> ಸಹೋದರರೇ, ನಾವು ಇಸಾಕನಂತೆ ವಾಗ್ದಾನದ ಫಲವಾಗಿ ಹುಟ್ಟಿದ ಮಕ್ಕಳಾಗಿದ್ದೇವೆ.",
			"29": "<sup>29</sup> ಆದರೆ ಪೂರ್ವದಲ್ಲಿ ಶಾರೀರಿಕವಾಗಿ ಹುಟ್ಟಿದವನು, ದೇವರಾತ್ಮಬಲದಿಂದ ಹುಟ್ಟಿದವನನ್ನು ಹಿಂಸೆಪಡಿಸಿದಂತೆಯೇ ಈಗಲೂ ಆಗುತ್ತಿದೆ.",
			"30": "<sup>30</sup> ಆದರೆ ದೇವರ ವಾಕ್ಯವು ಏನು ಹೇಳುತ್ತದೆ? ದಾಸಿಯನ್ನೂ ಅವಳ ಮಗನನ್ನೂ ಹೊರಗೆ ಹಾಕು. ದಾಸಿಯ ಮಗನು ಸ್ವತಂತ್ರಳ ಮಗನೊಂದಿಗೆ ಎಷ್ಟು ಮಾತ್ರಕ್ಕೂ ಬಾಧ್ಯಸ್ಥನಾಗಬಾರದು ಎಂದು ಹೇಳುತ್ತದೆ.",
			"31": "<sup>31</sup> ಸಹೋದರರೇ, ನಾವು ದಾಸಿಯ ಮಕ್ಕಳಲ್ಲ, ಸ್ವತಂತ್ರಳಾದವಳ ಮಕ್ಕಳೇ ಎಂದು ತಿಳಿದುಕೊಳ್ಳಿರಿ."
		},
		"5": {
			"1": "<sup>1</sup> ಕ್ರಿಸ್ತನು ನಮ್ಮನ್ನು ಸ್ವತಂತ್ರರಾಗಿರಿಸಬೇಕೆಂದೇ ನಮಗೆ ಬಿಡುಗಡೆ ಮಾಡಿದನು. ಅದರಲ್ಲಿ ಸ್ಥಿರವಾಗಿ ನಿಲ್ಲಿರಿ, ದಾಸತ್ವದ ನೊಗದಲ್ಲಿ ತಿರುಗಿ ಸಿಕ್ಕಿಕೊಳ್ಳಬೇಡಿರಿ.",
			"2": "<sup>2</sup> ನೋಡಿರಿ, ಪೌಲನೆಂಬ ನಾನು ನಿಮಗೆ ಹೇಳುತ್ತೇನೆ, ನೀವು ಸುನ್ನತಿಮಾಡಿಸಿಕೊಂಡರೆ ಕ್ರಿಸ್ತನಿಂದ ನಿಮಗೆ ಏನೂ ಪ್ರಯೋಜನವಿಲ್ಲ.",
			"3": "<sup>3</sup> ಸುನ್ನತಿ ಮಾಡಿಸಿಕೊಳ್ಳುವ ಪ್ರತಿಯೊಬ್ಬನಿಗೆ, ನೀನು ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿ ಹೇಳಿರುವುದನ್ನೆಲ್ಲಾ ಮಾಡುವ ಹಂಗಿನಲ್ಲಿದ್ದೀ ಎಂದು ತಿರುಗಿ ಪ್ರಮಾಣವಾಗಿ ಹೇಳುತ್ತೇನೆ.",
			"4": "<sup>4</sup> ನಿಮ್ಮಲ್ಲಿ ಯಾರಾರು ಧರ್ಮಶಾಸ್ತ್ರದಿಂದ ನೀತಿವಂತರಾಗಬೇಕೆಂದು ಬಯಸಿದ್ದೀರೋ ಅವರು ಕ್ರಿಸ್ತನಿಂದ ಅಗಲಿಹೋದವರಾಗಿದ್ದೀರಿ. ಕೃಪೆಯಿಂದ ಬಿದ್ದಹೋದವರಾಗಿದ್ದೀರಿ.",
			"5": "<sup>5</sup> ನಾವಾದರೋ ದೇವರಾತ್ಮನ ಮೂಲಕ ನಂಬಿಕೆಯಿಂದ ನೀತಿವಂತರಾಗುತ್ತೇವೆಂಬುವ ನಿರೀಕ್ಷೆ ಸಫಲವಾಗುವುದೆಂದು ಎದುರುನೋಡುವವರಾಗಿದ್ದೇವೆ.",
			"6": "<sup>6</sup> ಕ್ರಿಸ್ತ ಯೇಸುವಿನಲ್ಲಿರುವವರಿಗೆ ಸುನ್ನತಿಯಾದರೂ ಆಗದಿದ್ದರೂ ಪ್ರಯೋಜನವಿಲ್ಲ, ಆದರೆ ಅವರಲ್ಲಿ ಪ್ರೀತಿಯಿಂದ ಹೊರಹೊಮ್ಮುವ ನಂಬಿಕೆಯೇ ಪ್ರಯೋಜವಾಗಿದೆ.",
			"7": "<sup>7</sup> ಚೆನ್ನಾಗಿ ಓಡುತ್ತಾ ಇದ್ದೀರಿ. ಆದರೆ ನೀವು ಸತ್ಯವನ್ನು ಅನುಸರಿಸದಂತೆ ನಿಮ್ಮನ್ನು ತಡೆದವರು ಯಾರು?",
			"8": "<sup>8</sup> ಈ ಮನವೊಲಿಕೆಯು ನಿಮ್ಮನ್ನು ಕರೆದಾತನಿಂದ ಹುಟ್ಟಿದ್ದಲ್ಲ.",
			"9": "<sup>9</sup> ಸ್ವಲ್ಪ ಹುಳಿಯಿಂದ ಕಣಕವೆಲ್ಲಾ ಹುಳಿಯಾಗುವುದು.",
			"10": "<sup>10</sup> ನಿಮ್ಮಲ್ಲಿ ಭಿನ್ನಾಭಿಪ್ರಾಯಗಳು ಉಂಟಾಗುವುದಿಲ್ಲವೆಂದು ಕರ್ತನಲ್ಲಿ ನಿಮ್ಮನ್ನು ಕುರಿತಾಗಿ ನನಗೆ ಭರವಸೆ ಉಂಟು. ನಿಮ್ಮಲ್ಲಿ ಬೇಧವನ್ನು ಹುಟ್ಟಿಸುವವನು ಯಾವನಾದರೂ ಸರಿಯೇ, ಅವನು ದಂಡನೆಯನ್ನು ಅನುಭವಿಸುವನು.",
			"11": "<sup>11</sup> ಸಹೋದರರೇ, ನಾನಾದರೋ ಸುನ್ನತಿಯಾಗಬೇಕೆಂದು ಇನ್ನೂ ಸಾರುವವನಾಗಿದ್ದರೆ ನನಗೆ ಹಿಂಸೆಯಾಗುವುದಾದರೂ ಯಾಕೆ? ಹಾಗಿದ್ದ ಪಕ್ಷದಲ್ಲಿ ಶಿಲುಬೆಯ ದೆಸೆಯಿಂದ ಉಂಟಾಗುವ ತೊಂದರೆಗಳು ನೀಗಿ ಹೋಯಿತಲ್ಲಾ?",
			"12": "<sup>12</sup> ನಿಮ್ಮನ್ನು ಕಳವಳಪಡಿಸುವವರು ತಮ್ಮನೇ ತಾವು ಅಂಗಚ್ಛೇದನೆಯನ್ನಾದರೂ ಮಾಡಿಕೊಂಡರೆ ಚೆನ್ನಾಗಿತ್ತು.",
			"13": "<sup>13</sup> ಸಹೋದರರೇ, ನೀವು ಸ್ವತಂತ್ರರಾಗಿರಬೇಕೆಂದು ದೇವರು ನಿಮ್ಮನ್ನು ಕರೆದನು. ಆದರೆ ನಿಮಗಿರುವ ಸ್ವಾತಂತ್ರ್ಯವನ್ನು ಶರೀರಾಧೀನ ಸ್ವಭಾವಕ್ಕೆ ಆಸ್ಪದವಾಗಿ ಬಳಸದೆ, ಪ್ರೀತಿಯಿಂದ ಒಬ್ಬರಿಗೊಬ್ಬರು ಸೇವೆ ಮಾಡಿರಿ.",
			"14": "<sup>14</sup> ನಿನ್ನ ನೆರೆಯವನನ್ನು ನಿನ್ನಂತೆಯೇ ಪ್ರೀತಿಸು ಎಂಬ ಒಂದೇ ಮಾತಿನಲ್ಲಿ ಧರ್ಮಶಾಸ್ತ್ರವೆಲ್ಲಾ ಅಡಗಿದೆ.",
			"15": "<sup>15</sup> ಆದರೆ ನೀವು ಒಬ್ಬರನ್ನೊಬ್ಬರು ಕಚ್ಚಾಡಿ, ಹರಕೊಂಡು, ತಿನ್ನುವವರಾದರೆ ಒಬ್ಬರಿಂದೊಬ್ಬರು ನಾಶವಾದೀರಿ, ಎಚ್ಚರಿಕೆ.",
			"16": "<sup>16</sup> ನಾನು ಹೇಳುವುದೇನಂದರೆ ಪವಿತ್ರಾತ್ಮನನ್ನು ಅನುಸರಿಸಿ ನಡೆದುಕೊಳ್ಳಿರಿ, ಆಗ ನೀವು ಶರೀರಭಾವದ ಅಭಿಲಾಷೆಗಳನ್ನು ಎಷ್ಟು ಮಾತ್ರಕ್ಕೂ ನೆರವೇರಿಸುವುದಿಲ್ಲ.",
			"17": "<sup>17</sup> ಏಕೆಂದರೆ ಶರೀರಾಭಿಲಾಷೆಯೂ ಆತ್ಮನಿಗೆ ವಿರುದ್ಧವಾಗಿದೆ, ಆತ್ಮನ ಅಭಿಲಾಷೆಯು ಶರೀರಭಾವಕ್ಕೆ ವಿರುದ್ಧವಾಗಿದೆ. ನೀವು ಮಾಡಲಿಚ್ಚಿಸುವುದನ್ನು ಮಾಡದಂತೆ ಇವು ಒಂದಕ್ಕೊಂದು ವಿರುದ್ಧವಾಗಿವೆ.",
			"18": "<sup>18</sup> ಆದರೆ ನೀವು ಆತ್ಮನಿಂದ ನಡಿಸಿಕೊಳ್ಳುವವರಾದರೆ ಧರ್ಮಶಾಸ್ತ್ರಕ್ಕೆ ಅಧೀನರಲ್ಲ.",
			"19": "<sup>19</sup> ಶರೀರಭಾವದ ಸ್ವಭಾವಗಳು ಪ್ರತ್ಯಕ್ಷವಾಗಿಯೇ ಇವೆ, ಅದು ಯಾವುವೆಂದರೆ, ಜಾರತ್ವ, ಬಂಡುತನ, ನಾಚಿಕೆಗೇಡಿತನ,",
			"20": "<sup>20</sup> ವಿಗ್ರಹಾರಾಧನೆ, ಮಾಟ, ಹಗೆತನ, ಜಗಳ, ಹೊಟ್ಟೆಕಿಚ್ಚು, ಸಿಟ್ಟು, ಪಕ್ಷಪಾತ, ಭಿನ್ನಮತ,",
			"21": "<sup>21</sup> ಮತ್ಸರ, ಕುಡುಕುತನ, ಗಲಭೆ, ಢಂಭಾಚಾರ ಇಂಥವುಗಳೇ. ಇಂಥ ಕಾರ್ಯಗಳನ್ನು ನಡಿಸುವವರು ದೇವರ ರಾಜ್ಯಕ್ಕೆ ಬಾಧ್ಯಸ್ಥರಾಗುವುದಿಲ್ಲವೆಂದು ನಾನು ಈ ಮೊದಲು ಹೇಳಿದಂತೆಯೇ ಈಗಲೂ ನಿಮ್ಮನ್ನು ಎಚ್ಚರಿಸುತ್ತೇನೆ.",
			"22": "<sup>22</sup> ಆದರೆ ದೇವರಾತ್ಮನಿಂದ ಉಂಟಾಗುವ ಫಲವೇನಂದರೆ, ಪ್ರೀತಿ, ಸಂತೋಷ, ಸಮಾಧಾನ, ದೀರ್ಘಶಾಂತಿ, ದಯೆ, ಪರೋಪಕಾರ, ನಂಬಿಕೆ,",
			"23": "<sup>23</sup> ಸಾಧುತ್ವ, ಶಮೆದಮೆ ಇಂಥವುಗಳೇ. ಇವುಗಳಿಗೆ ವಿರುದ್ಧವಾಗಿ ಯಾವ ಧರ್ಮಶಾಸ್ತ್ರವೂ ಇಲ್ಲ.",
			"24": "<sup>24</sup> ಕ್ರಿಸ್ತ ಯೇಸುವಿನಲ್ಲಿರುವವರು ತಮ್ಮ ಶರೀರಭಾವವನ್ನು ಅದರ ವಿಷಯಾಭಿಲಾಷೆ, ಸ್ವೇಚ್ಛಾಭಿಲಾಷೆಗಳ ಸಹಿತವಾಗಿ ಶಿಲುಬೆಗೆ ಹಾಕಿದವರಾಗಿದ್ದಾರೆ.",
			"25": "<sup>25</sup> ನಾವು ಆತ್ಮನಿಂದ ಜೀವಿಸುತ್ತಿರಲಾಗಿ ಆತ್ಮನನ್ನನುಸರಿಸಿ ನಡೆಯೋಣ.",
			"26": "<sup>26</sup> ಅಹಂಕಾರಿಗಳೂ, ಒಬ್ಬರನ್ನೊಬ್ಬರು ಕೆಣಕುವವರೂ, ಒಬ್ಬರ ಮೇಲೊಬ್ಬರು ಮತ್ಸರವುಳ್ಳವರೂ ಆಗದೆ ಇರೋಣ."
		},
		"6": {
			"1": "<sup>1</sup> ಸಹೋದರರೇ, ನಿಮ್ಮಲ್ಲಿ ಯಾರಾದರೂ ಯಾವುದಾದರೂ ಪಾಪದಲ್ಲಿ ಸಿಕ್ಕಿಹಾಕಿಕೊಂಡಿದ್ದರೆ ಅಂಥವರನ್ನು ಆತ್ಮಭರಿತರಾದ ನೀವು ಶಾಂತಭಾವದಿಂದ ತಿದ್ದಿ ಸರಿಮಾಡಿರಿ. ನೀವಾದರೂ ಶೋಧನೆಗೆ ಒಳಗಾಗದಂತೆ ನಿಮ್ಮ ವಿಷಯದಲ್ಲಿ ಎಚ್ಚರಿಕೆಯಾಗಿರಿ.",
			"2": "<sup>2</sup> ಒಬ್ಬರು ಮತ್ತೊಬ್ಬರ ಭಾರವನ್ನು ಹೊತ್ತುಕೊಳ್ಳಲಿ, ಹೀಗೆ ಕ್ರಿಸ್ತನ ನೀತಿನಿಯಮಗಳನ್ನು ನೆರವೇರಿಸಿರಿ.",
			"3": "<sup>3</sup> ಯಾವನಾದರೂ ಅಲ್ಪನಾಗಿದ್ದು ತಾನು ದೊಡ್ಡವನೆಂದು ಭಾವಿಸಿಕೊಂಡರೆ ತನ್ನನ್ನು ತಾನೇ ಮೋಸಗೊಳಿಸಿಕೊಳ್ಳುವನಾಗಿದ್ದಾನೆ.",
			"4": "<sup>4</sup> ಪ್ರತಿಯೊಬ್ಬನು ತಾನು ಮಾಡಿದ ಕೆಲಸವನ್ನು ಪರೀಕ್ಷಿಸಿಕೊಳ್ಳಲಿ. ಆಗ ಅವನು ತನ್ನ ನಿಮಿತ್ತದಿಂದ ಹೆಚ್ಚಳಪಡಬಹುದೇ ಹೊರತು ಮತ್ತೊಬ್ಬನೊಂದಿಗೆ ಹೋಲಿಸಿಕೊಂಡು ಹೆಚ್ಚಳಪಡಬೇಕಾಗಿರುವುದಿಲ್ಲ.",
			"5": "<sup>5</sup> ಏಕೆಂದರೆ ಪ್ರತಿಯೊಬ್ಬನು ತನ್ನ ಸ್ವಂತ ಹೊರೆಯನ್ನು ತಾನೇ ಹೊತ್ತುಕೊಳ್ಳಬೇಕು.",
			"6": "<sup>6</sup> ದೇವರ ವಾಕ್ಯವನ್ನು ಕಲಿಯುವವನು, ಅದನ್ನು ಕಲಿಸುವವನಿಗೆ ತನ್ನಲ್ಲಿರುವ ಎಲ್ಲಾ ಮೇಲುಗಳಲ್ಲಿಯೂ ಪಾಲನ್ನು ಕೊಡಬೇಕು.",
			"7": "<sup>7</sup> ಮೋಸಹೋಗಬೇಡಿರಿ. ದೇವರನ್ನು ಅಪಹಾಸ್ಯ ಮಾಡಲಾಗುವುದಿಲ್ಲ. ಮನುಷ್ಯನು ತಾನು ಏನು ಬಿತ್ತುತ್ತಾನೋ ಅದನ್ನೇ ಕೊಯ್ಯಬೇಕು.",
			"8": "<sup>8</sup> ತನ್ನ ಶರೀರಭಾವವನ್ನು ಕುರಿತು ಬಿತ್ತುವವನು ಆ ಭಾವದಿಂದ ನಾಶವನ್ನು ಕೊಯ್ಯುವನು. ಆತ್ಮನ ಕುರಿತಾಗಿ ಬಿತ್ತುವವನು ಆತ್ಮನಿಂದ ನಿತ್ಯಜೀವವನ್ನು ಕೊಯ್ಯುವನು.",
			"9": "<sup>9</sup> ಒಳ್ಳೆಯದನ್ನು ಮಾಡುವುದರಲ್ಲಿ ಬೇಸರಗೊಳ್ಳದೇ ಇರೋಣ. ಏಕೆಂದರೆ ಮನಗುಂದದಿದ್ದರೆ ತಕ್ಕ ಸಮಯದಲ್ಲಿ ಬೆಳೆಯನ್ನು ಕೊಯ್ಯುವೆವು.",
			"10": "<sup>10</sup> ಆದ್ದರಿಂದ ಅವಕಾಶ ಸಿಕ್ಕಿದಾಗಲೆಲ್ಲಾ ಎಲ್ಲರಿಗೂ ಒಳ್ಳೆಯದನ್ನು ಮಾಡೋಣ, ವಿಶೇಷವಾಗಿ ಒಂದೇ ಮನೆಯವರಂತಿರುವ ಕ್ರೈಸ್ತ ವಿಶ್ವಾಸಿಗಳಿಗೆ ಮಾಡೋಣ.",
			"11": "<sup>11</sup> ಇಗೋ ನೋಡಿರಿ, ಸ್ವಂತ ಕೈಯಿಂದ ಎಂಥಾ ದೊಡ್ಡ ಅಕ್ಷರಗಳಲ್ಲಿ ನಿಮಗೆ ಬರೆಯುತ್ತಾ ಇದ್ದೇನೆ.",
			"12": "<sup>12</sup> ದೈಹಿಕವಾಗಿ ಪ್ರಶಂಸೆಯನ್ನು ಹೊಂದಬೇಕೆಂದಿರುವವರು ಸುನ್ನತಿ ಮಾಡಿಸಿಕೊಳ್ಳಿರಿ ಎಂಬುದಾಗಿ ನಿಮ್ಮನ್ನು ಒತ್ತಾಯಿಸುತ್ತಾರೆ. ಏಕೆಂದರೆ ಕ್ರಿಸ್ತನ ಶಿಲುಬೆಯ ದೆಸೆಯಿಂದ ತಮಗೆ ಹಿಂಸೆಯಾಗಬಾರದೆಂಬುದೇ ಅವರ ಉದ್ದೇಶ.",
			"13": "<sup>13</sup> ಸುನ್ನತಿ ಮಾಡಿಸಿಕೊಂಡವರೇ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಕೈಕೊಂಡು ನಡೆಯುತ್ತಿಲ್ಲವಲ್ಲಾ. ಅವರು ನಿಮ್ಮ ಶರೀರದ ವಿಷಯದಲ್ಲಿ ಹೆಚ್ಚಳಪಡುವುದಕ್ಕಾಗಿ ನಿಮಗೆ ಸುನ್ನತಿಯಾಗಬೇಕೆಂದು ಅಪೇಕ್ಷಿಸುತ್ತಾರೆ.",
			"14": "<sup>14</sup> ನನಗಾದರೋ ನಮ್ಮ ಕರ್ತನಾದ ಯೇಸು ಕ್ರಿಸ್ತನ ಶಿಲುಬೆಯ ವಿಷಯದಲ್ಲಿಯೇ ಹೊರತು ಬೇರೆ ಯಾವುದರಿಂದಲೂ ಹೆಚ್ಚಳಪಡುವುದು ಬೇಕಾಗಿಲ್ಲ. ಆತನ ಮೂಲಕ ನಾನು ಲೋಕಕ್ಕೂ, ಲೋಕವು ನನಗಾಗಿಯೂ ಶಿಲುಬೆಗೆ ಹಾಕಿಕೊಂಡು ಸತ್ತಿತು.",
			"15": "<sup>15</sup> ಸುನ್ನತಿ ಮಾಡಿಸಿಕೊಳ್ಳುವುದೋ ಅಥವಾ ಮಾಡಿಸಿಕೊಳ್ಳದಿರುವುದೋ ಏನು ಅಲ್ಲ, ಆದರೆ ಹೊಸ ಸೃಷ್ಟಿಯಾಗುವುದೇ ಪ್ರಮುಖವಾದುದು.",
			"16": "<sup>16</sup> ಈ ನಿಯಮಗಳನ್ನು ಸರಿಯಾಗಿ ನಡೆಯುವವರೆಲ್ಲರಿಗೂ ಮತ್ತು ದೇವರ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಶಾಂತಿಯೂ, ಕರುಣೆಯೂ ಉಂಟಾಗಲಿ.",
			"17": "<sup>17</sup> ಇನ್ನು ಮೇಲೆ ಯಾರೂ ನನಗೆ ತೊಂದರೆಪಡಿಸಬಾರದು, ನನ್ನ ದೇಹದಲ್ಲಿ ಯೇಸುಕ್ರಿಸ್ತನ ಗುರುತುಗಳನ್ನು ಒತ್ತಿಸಿಕೊಂಡಿದ್ದೇನೆ.",
			"18": "<sup>18</sup> ಸಹೋದರರೇ, ನಮ್ಮ ಕರ್ತನಾದ ಯೇಸು ಕ್ರಿಸ್ತನ ಕೃಪೆಯು ನಿಮ್ಮ ಆತ್ಮದೊಂದಿಗಿರಲಿ. ಆಮೆನ್."
		}
	}
};
module.exports = book;