var book = {
	"name": "ಕೊಲೊಸ್ಸೆಯರಿಗೆ ಬರೆದ ಪತ್ರಿಕೆ",
	"numChapters": 4,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ದೇವರ ಚಿತ್ತಾನುಸಾರವಾಗಿ ಕ್ರಿಸ್ತಯೇಸುವಿನ ಅಪೊಸ್ತಲನಾಗಿರುವ ಪೌಲನೂ ಮತ್ತು ಸಹೋದರನಾದ ತಿಮೊಥೆಯನು,",
			"2": "<sup>2</sup> ಕೊಲೊಸ್ಸೆಯಲ್ಲಿರುವ ದೇವಜನರಿಗೆ ಹಾಗು ಕ್ರಿಸ್ತನಲ್ಲಿ ನಂಬಿಗಸ್ತರಾದ ಸಹೋದರರಿಗೆ ಬರೆಯುವುದೇನೆಂದರೆ, ನಮ್ಮ ತಂದೆಯಾದ ದೇವರಿಂದ ನಿಮಗೆ ಕೃಪೆಯೂ ಮತ್ತು ಶಾಂತಿಯೂ ಉಂಟಾಗಲಿ.",
			"3": "<sup>3</sup> ನಿಮಗೋಸ್ಕರ ನಾವು ಪ್ರಾರ್ಥಿಸುವಾಗಲೆಲ್ಲಾ ನಿಮ್ಮ ನಿಮಿತ್ತವಾಗಿ ನಮ್ಮ ಕರ್ತನಾದ ಯೇಸು ಕ್ರಿಸ್ತನ ತಂದೆಯಾಗಿರುವ ದೇವರಿಗೆ ಕೃತಜ್ಞತಾಸ್ತುತಿ ಸಲ್ಲಿಸುತ್ತೇವೆ.",
			"4": "<sup>4</sup> ನೀವು ಕ್ರಿಸ್ತಯೇಸುವಿನಲ್ಲಿ ಇಟ್ಟಿರುವ ನಂಬಿಕೆ ಹಾಗು ದೇವಜನರೆಲ್ಲರಲ್ಲಿ ನೀವು ಇಟ್ಟಿರುವ ಪ್ರೀತಿಯ ಬಗ್ಗೆ ನಾವು ಕೇಳಿದ್ದೇವೆ.",
			"5": "<sup>5</sup> ಪರಲೋಕದಲ್ಲಿ ನಿಮಗೋಸ್ಕರ ಕಾದಿರಿಸಿರುವ ನಿರೀಕ್ಷೆಯನ್ನು ಕುರಿತು ಸುವಾರ್ತೆಯ ಸತ್ಯವಾಕ್ಯದಿಂದ ನೀವು ಮೊದಲೇ ಕೇಳಿದ್ದಿರಿ.",
			"6": "<sup>6</sup> ನೀವು ಸತ್ಯದಲ್ಲಿ ದೇವರ ಕೃಪೆಯನ್ನು ಕೇಳಿ ತಿಳಿದುಕೊಂಡ ದಿವಸದಿಂದ ನಿಮ್ಮ ಬಳಿಗೆ ಬಂದ ಆ ಸುವಾರ್ತೆಯು ನಿಮ್ಮಲ್ಲಿ ಫಲಕೊಟ್ಟಂತೆ ಲೋಕದಲ್ಲೆಲ್ಲಾ ಫಲಕೊಟ್ಟು ವೃದ್ಧಿಯಾಗುತ್ತಿದೆ.",
			"7": "<sup>7</sup> ಈ ಸುವಾರ್ತೆಯನ್ನು ಕ್ರಿಸ್ತನ ನಂಬಿಗಸ್ತ ಸೇವಕನೂ, ನಮ್ಮ ಪ್ರಿಯ ಜೊತೆಸೇವಕನೂ ಆದ ಎಪಫ್ರನಿಂದ ನೀವು ಕಲಿತುಕೊಂಡಿದ್ದೀರಿ.",
			"8": "<sup>8</sup> ಪವಿತ್ರಾತ್ಮಪ್ರೇರಿತವಾದ ನಿಮ್ಮ ಪ್ರೀತಿಯ ವಿಷಯವನ್ನು ನನಗೆ ತಿಳಿಸಿದವನು ಆತನೇ.",
			"9": "<sup>9</sup> ಹೀಗಿರುವುದ್ದರಿಂದ ಈ ನಿಮ್ಮ ಪ್ರೀತಿಯ ಕುರಿತು ನಾವು ಕೇಳಿದ ದಿನದಿಂದ ನಿಮಗೋಸ್ಕರ ಪ್ರಾರ್ಥಿಸುವುದನ್ನು ನಿಲ್ಲಿಸದೆ, ನೀವು ಸಕಲ ಆತ್ಮಿಕ ಗ್ರಹಿಕೆಯಿಂದಲೂ ಜ್ಞಾನದಿಂದಲೂ ದೇವರ ಚಿತ್ತದ ತಿಳುವಳಿಕೆಯ ಕುರಿತು ಸಂಪೂರ್ಣರಾಗಬೇಕೆಂತಲೂ,",
			"10": "<sup>10</sup> ಕರ್ತನಿಗೆ ಮೆಚ್ಚಿಕೆಯಾಗುವ ರೀತಿಯಲ್ಲಿ ನೀವು ಯೋಗ್ಯರಾಗಿ ಜೀವಿಸಬೇಕೆಂತಲೂ, ಸಕಲ ಸತ್ಕಾರ್ಯಗಳಲ್ಲಿ ಫಲವನ್ನು ಕೊಡುತ್ತಾ ದೈವಜ್ಞಾನದಲ್ಲಿ ವೃದ್ಧಿಯಾಗಬೇಕೆಂತಲೂ,",
			"11": "<sup>11</sup> ಆತನ ಮಹಿಮಾ ಶಕ್ತಿಯ ಪ್ರಕಾರ ಎಲ್ಲಾ ಸಾಮರ್ಥ್ಯದಲ್ಲಿ ಬಲಹೊಂದಿ, ಎಲ್ಲವನ್ನೂ ತಾಳ್ಮೆಯಿಂದಲೂ, ದೀರ್ಘಶಾಂತಿಯಿಂದಲೂ ಸಹಿಸಿಕೊಳ್ಳುವವರಾಗಿರಬೇಕೆಂತಲೂ,",
			"12": "<sup>12</sup> ದೇವಭಕ್ತರಿಗಾಗಿ ಬೆಳಕಿನಲ್ಲಿರುವ ಬಾಧ್ಯತೆಯಲ್ಲಿ ಪಾಲುಗಾರರಾಗುವುದಕ್ಕೆ ನಮ್ಮನ್ನು ಯೋಗ್ಯರನ್ನಾಗಿ ಮಾಡಿದ ತಂದೆಯಾದ ದೇವರಿಗೆ ಆನಂದಪೂರ್ವಕವಾಗಿ ಕೃತಜ್ಞತಾಸ್ತುತಿಯನ್ನು ಸಲ್ಲಿಸುವವರಾಗಬೇಕೆಂತಲೂ ದೇವರನ್ನು ಬೇಡಿಕೊಳ್ಳುತ್ತೇನೆ.",
			"13": "<sup>13</sup> ದೇವರು ನಮ್ಮನ್ನು ಅಂಧಕಾರದ ಅಧಿಪತ್ಯದಿಂದ ಬಿಡಿಸಿ ತನ್ನ ಪ್ರಿಯ ಕುಮಾರನ ಸಾಮ್ರಾಜ್ಯಕ್ಕೆ ಸೇರಿಸಿದನು.",
			"14": "<sup>14</sup> ಈ ಕುಮಾರನಲ್ಲಿ ನಮ್ಮ ಪಾಪಗಳು ಕ್ಷಮಿಸಲ್ಪಟ್ಟು ನಮಗೆ ವಿಮೋಚನೆಯಾಯಿತು.",
			"15": "<sup>15</sup> ಕ್ರಿಸ್ತನು ಅದೃಶ್ಯನಾದ ದೇವರ ಪ್ರತಿರೂಪನೂ, ಸೃಷ್ಟಿಗೆಲ್ಲಾ ಜೇಷ್ಠಪುತ್ರನೂ ಆಗಿದ್ದಾನೆ.",
			"16": "<sup>16</sup> ಭೂಪರಲೋಕಗಳಲ್ಲಿರುವ ದೃಶ್ಯ ಅದೃಶ್ಯವಾದವುಗಳೆಲ್ಲವೂ, ಸಿಂಹಾಸನಗಳಾಗಲಿ, ಪ್ರಭುತ್ವಗಳಾಗಲಿ, ದೊರೆತನಗಳಾಗಲಿ, ಅಧಿಕಾರಗಳಾಗಲಿ ಆತನಿಂದ ಸೃಷ್ಟಿಸಲ್ಪಟ್ಟವು. ಸರ್ವವೂ ಆತನ ಮುಖಾಂತರವಾಗಿ ಆತನಿಗೋಸ್ಕರವಾಗಿ ಸೃಷ್ಟಿಸಲ್ಪಟ್ಟಿತು.",
			"17": "<sup>17</sup> ಆತನು ಎಲ್ಲಕ್ಕೂ ಮೊದಲು ಇದ್ದಾತನು, ಆತನಲ್ಲಿ ಸಮಸ್ತವು ಒಂದಾಗಿ ನೆಲೆಗೊಂಡಿದ್ದೆ.",
			"18": "<sup>18</sup> ಸಭೆಯೆಂಬ ದೇಹಕ್ಕೆ ಆತನು ತಲೆಯಾಗಿದ್ದಾನೆ, ಆತನೇ ಆದಿಸಂಭೂತನು, ಎಲ್ಲಾದರಲ್ಲಿ ಆತನು ಮೊದಲಿಗನಾಗುವುದಕ್ಕಾಗಿ ಸತ್ತವರೊಳಗಿಂದ ಮೊದಲು ಎದ್ದು ಬಂದವನು ಆತನೇ.",
			"19": "<sup>19</sup> ಏಕೆಂದರೆ ಆತನಲ್ಲಿಯೇ ತನ್ನ ಸರ್ವಸಂಪೂರ್ಣತೆಯು ವಾಸವಾಗಿರಬೇಕೆಂತಲೂ,",
			"20": "<sup>20</sup> ಮತ್ತು ಆತನು ಶಿಲುಬೆಯ ಮೇಲೆ ಸುರಿಸಿದ ರಕ್ತದಿಂದ ತಾನು ಸಮಾಧಾನವನ್ನುಂಟುಮಾಡಿ, ಭೂಪರಲೋಕಗಳಲ್ಲಿರುವ ಸಮಸ್ತವನ್ನೂ ತನ್ನ ಕುಮಾರನ ಮೂಲಕ ತನಗೆ ಸಂಧಾನಪಡಿಸಿಕೊಳ್ಳಬೇಕೆಂತಲೂ ತಂದೆಯಾದ ದೇವರು ಇಚ್ಛಿಸಿದನು.",
			"21": "<sup>21</sup> ಇದಲ್ಲದೆ ನೀವು ಪೂರ್ವದಲ್ಲಿ ಅನ್ಯರು ನಿಮ್ಮ ದುಷ್ಕೃತ್ಯಗಳಿಂದಲೂ ದ್ವೇಷಮನಸ್ಸುಳ್ಳವರಾಗಿ ಆತನಿಗೆ ವಿರೋಧಿಗಳೂ ಆಗಿದ್ದಿರಿ.",
			"22": "<sup>22</sup> ಈಗಲಾದರೋ ದೇವರು ನಿಮ್ಮನ್ನು ಯೇಸುಕ್ರಿಸ್ತನ ಶಾರೀರಿಕ ಮರಣದ ಮೂಲಕವಾಗಿ ಸಂಧಾನಪಡಿಸಿಕೊಂಡಿದ್ದಾನೆ. ದೇವರು ತನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನಿಮ್ಮನ್ನು ಪರಿಶುದ್ಧರನ್ನಾಗಿಯೂ, ನಿರ್ದೋಷಿಗಳನ್ನಾಗಿಯೂ, ನಿರಪರಾಧಿಗಳನ್ನಾಗಿಯೂ ನಿಲ್ಲಿಸಬೇಕೆಂದು ಹೀಗೆ ಮಾಡಿದ್ದಾನೆ.",
			"23": "<sup>23</sup> ಆಕಾಶದ ಕೆಳಗಿರುವ ಸರ್ವ ಸೃಷ್ಟಿಗೂ ಸಾರಲ್ಪಟ್ಟಂತಹ ಮತ್ತು ನೀವು ಕೇಳಿದಂತಹ ಸುವಾರ್ತೆಯಿಂದ ಉಂಟಾದ ನಿರೀಕ್ಷೆಯಿಂದ ಕದಲಿಹೋಗದಂತೆ ನಂಬಿಕೆಯಲ್ಲಿ ನೆಲೆಗೊಳ್ಳುವುದಾದರೆ, ನೀವು ಅಡಿಪಾಯವುಳ್ಳವರೂ ಮತ್ತು ಸ್ಥಿರತೆಯುಳ್ಳವರೂ ಆಗುವಿರಿ. ಇದೇ ಸುವಾರ್ತೆಗೆ ಪೌಲನೆಂಬ ನಾನು ಸೇವಕನಾಗಿದ್ದೇನೆ.",
			"24": "<sup>24</sup> ನಾನು ಈಗ ನಿಮಗೋಸ್ಕರ ಅನುಭವಿಸುತ್ತಿರುವ ಬಾಧೆಗಳಲ್ಲಿ ನನಗೆ ಸಂತೋಷವಿದೆ. ಕ್ರಿಸ್ತನ ಯಾತನೆಗಳಲ್ಲಿ ಕೊರತೆಯಾಗಿರುವುದನ್ನು ಆತನ ಸಭೆಯೆಂಬ ದೇಹಕೋಸ್ಕರ ನಾನು ನನ್ನ ದೇಹದಲ್ಲಿ ಅನುಭವಿಸಿ ತೀರಿಸುತ್ತೇನೆ.",
			"25": "<sup>25</sup> ದೇವರ ಸಂಕಲ್ಪದ ಮೇರೆಗೆ ನಿಮ್ಮ ಪ್ರಯೋಜನಕ್ಕೋಸ್ಕರವಾಗಿ ನಾನು ಸಭೆಗೆ ಸೇವಕನಾದೆನು. ದೇವರು ತನ್ನ ವಾಕ್ಯವನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ತಿಳಿಸುವ ಕಾರ್ಯವನ್ನು ನನಗೆ ದಯಪಾಲಿಸಿದನು.",
			"26": "<sup>26</sup> ಈ ರಹಸ್ಯವಾದ ಸತ್ಯವಾಕ್ಯವು ಹಿಂದಿನ ಯುಗಯುಗಗಳಿಂದಲೂ, ತಲತಲಾಂತರಗಳಿಂದಲೂ ಮರೆಯಾಗಿತ್ತು, ಆದರೆ ಈಗ ದೇವರು ಅದನ್ನು ತನ್ನ ದೇವಜನರಿಗೆ ಪ್ರಕಟಿಸಿದ್ದಾನೆ.",
			"27": "<sup>27</sup> ಈ ಮರ್ಮದ ಮಹಿಮಾತಿಶಯವು ಎಷ್ಟೆಂಬುದನ್ನು ಅನ್ಯಜನಗಳಿಗೂ ತಿಳಿಸಲಿಕ್ಕೆ ದೇವರು ಇಚ್ಛಿಸಿದನು. ಈ ಮರ್ಮವು ಏನೆಂದರೆ ಮಹಿಮೆಯ ನಿರೀಕ್ಷೆಗೆ ಆಧಾರನಾಗಿರುವ ಕ್ರಿಸ್ತನು ನಿಮ್ಮಲ್ಲಿದ್ದಾನೆಂಬುದೇ.",
			"28": "<sup>28</sup> ನಾವು ಆತನನ್ನು ಸಾರುತ್ತಲಿದ್ದೇವೆ, ಸಕಲರಿಗೂ ಬುದ್ಧಿ ಹೇಳುತ್ತಾ, ಸರ್ವರಿಗೂ ಪೂರ್ಣಜ್ಞಾನವನ್ನು ಉಪದೇಶಿಸುತ್ತಾ, ದೇವರ ಮುಂದೆ ಎಲ್ಲರನ್ನೂ ಕ್ರಿಸ್ತನಲ್ಲಿ ಪ್ರವೀಣರನ್ನಾಗಿ ನಿಲ್ಲಿಸುವುದಕ್ಕೆ ಪ್ರಯತ್ನಿಸುತ್ತಿದ್ದೇವೆ.",
			"29": "<sup>29</sup> ನನ್ನಲ್ಲಿ ಕಾರ್ಯಸಾಧಿಸುವ ದೇವರ ಬಲದಿಂದ ಇದಕ್ಕೊಸ್ಕರವೇ ಶ್ರಮಿಸಿ ಹೋರಾಡುತ್ತೇನೆ."
		},
		"2": {
			"1": "<sup>1</sup> ನಿಮಗಾಗಿಯೂ, ಲವೊದಿಕೀಯದವರಿಗಾಗಿಯೂ ಹಾಗೂ ನನ್ನನ್ನು ಮುಖಾಮುಖಿಯಾಗಿ ನೋಡದಿರುವವರೆಲ್ಲರಿಗಾಗಿಯೂ,",
			"2": "<sup>2</sup> ಅವರ ಹೃದಯಗಳು ಉತ್ತೇಜನಗೊಂಡು, ಪ್ರೀತಿಯಿಂದ ಒಂದಾಗಿದ್ದು, ದೇವರ ಮರ್ಮವನ್ನು ಅಂದರೆ ಕ್ರಿಸ್ತನನ್ನು ತಿಳಿದುಕೊಳ್ಳುವ ಮೂಲಕ ಬರುವ ನಿಶ್ಚಯವಾದ ಭಾಗ್ಯವನ್ನು ಪಡೆಯಬೇಕೆಂಬುದೇ ನನ್ನ ಅಪೇಕ್ಷೆ. ಅದಕ್ಕಾಗಿಯೇ ನಾನು ಎಷ್ಟು ಪ್ರಯಾಸಪಡುತ್ತಿದ್ದೇನೆಂಬುದು ನಿಮಗೆ ತಿಳಿದಿರಬೇಕೆಂದು ಬಯಸುತ್ತೇನೆ.",
			"3": "<sup>3</sup> ಈ ಕ್ರಿಸ್ತನಲ್ಲಿಯೇ ಜ್ಞಾನ ಮತ್ತು ವಿವೇಕದ ಸರ್ವಸಂಪತ್ತು ಅಡಗಿದೆ.",
			"4": "<sup>4</sup> ಯಾರೂ ನಿಮ್ಮನ್ನು ತಮ್ಮ ಮನವೊಲಿಸುವ ಮಾತುಗಳಿಂದ ಮೋಸಗೊಳಿಸದಿರಲಿ ಎಂದು ನಾನು ನಿಮಗೆ ಇದನ್ನು ಹೇಳುತ್ತಿದ್ದೇನೆ.",
			"5": "<sup>5</sup> ನಾನು ಶರೀರದಿಂದ ನಿಮ್ಮ ಬಳಿಯಲ್ಲಿ ಇಲ್ಲದಿದ್ದರೂ ಆತ್ಮನಿಂದ ನಿಮ್ಮೊಂದಿಗಿದ್ದು ನೀವು ಕ್ರಮಬದ್ಧವಾಗಿ ನಡೆಯುವುದನ್ನು ಮತ್ತು ಕ್ರಿಸ್ತ ನಂಬಿಕೆಯಲ್ಲಿ ದೃಢವಾಗಿ ನಿಂತಿರುವುದನ್ನೂ ನೋಡಿ ಸಂತೋಷಪಡುತ್ತೇನೆ.",
			"6": "<sup>6</sup> ನೀವು ಕರ್ತನಾದ ಯೇಸು ಕ್ರಿಸ್ತನನ್ನು ಸ್ವೀಕರಿಸಿದಂತೆಯೇ ಆತನಲ್ಲಿದ್ದವರಾಗಿ ಬಾಳಿರಿ.",
			"7": "<sup>7</sup> ಆತನಲ್ಲಿ ಬೇರೂರಿಕೊಂಡು, ಆತನಲ್ಲಿ ಕಟ್ಟಲ್ಪಟ್ಟು ಮತ್ತು ನಿಮಗೆ ಬೋಧಿಸಲ್ಪಟ್ಟ ಉಪದೇಶದ ಪ್ರಕಾರವೇ ಕ್ರಿಸ್ತ ನಂಬಿಕೆಯಲ್ಲಿ ನೆಲೆಗೊಂಡು ದೇವರಿಗೆ ಹೆಚ್ಚೆಚ್ಚಾಗಿ ಸ್ತೋತ್ರ ಸಲ್ಲಿಸುವವರಾಗಿರಿ.",
			"8": "<sup>8</sup> ಮೋಸವಾದ ಮತ್ತು ವ್ಯರ್ಥವಾದ ತತ್ವಜ್ಞಾನ ಬೋಧನೆಯಿಂದ ಯಾರೂ ನಿಮ್ಮನ್ನು ವಶಮಾಡಿಕೊಳ್ಳದಂತೆ ಎಚ್ಚರಿಕೆಯಾಗಿರಿ. ಇವುಗಳು ಮನುಷ್ಯರ ಸಂಪ್ರದಾಯಗಳಿಗೆ ಮತ್ತು ಪ್ರಾಪಂಚಿಕ ಮೂಲ ಬೋಧನೆಗಳಿಗೆ ಸಂಬಂಧಿಸಿದವುಗಳೇ ಹೊರತು ಕ್ರಿಸ್ತನಿಗಲ್ಲ.",
			"9": "<sup>9</sup> ದೇವರ ಸರ್ವಸಂಪೂರ್ಣತೆಯು ಕ್ರಿಸ್ತನ ದೇಹದಲ್ಲಿ ವಾಸಮಾಡುತ್ತಿದೆ,",
			"10": "<sup>10</sup> ಆತನು ಎಲ್ಲಾ ದೊರೆತನಗಳಿಗೂ ಹಾಗೂ ಅಧಿಕಾರಿಗಳಿಗೂ ತಲೆಯಾಗಿರುವುದರಿಂದ ಆತನಲ್ಲಿದ್ದುಕೊಂಡೇ ನೀವು ಪರಿಪೂರ್ಣತೆಯನ್ನು ಹೊಂದಿದವರಾಗಿದ್ದೀರಿ.",
			"11": "<sup>11</sup> ದೇವರು ಆತನಲ್ಲಿ ನಿಮಗೆ ಮನುಷ್ಯರ ಕೈಯಿಂದ ಮಾಡಲಾಗದಂತಹ ಸುನ್ನತಿಯನ್ನು ಮಾಡಿದ್ದಾನೆ. ಕ್ರಿಸ್ತನ ಸುನ್ನತಿಯು ಶಾರೀರಿಕವಾದ ಪಾಪಸ್ವಭಾವವನ್ನು ತೆಗೆದುಹಾಕುವುದೇ.",
			"12": "<sup>12</sup> ನೀವು ದೀಕ್ಷಾಸ್ನಾನದಲ್ಲಿ ಕ್ರಿಸ್ತನೊಂದಿಗೆ ಹೂಣಲ್ಪಟ್ಟಿದ್ದೀರಿ, ಮತ್ತು ಆತನನ್ನು ಸತ್ತವರೊಳಗಿಂದ ಎಬ್ಬಿಸಿದ ದೇವರ ಶಕ್ತಿಯಲ್ಲಿ ನಂಬಿಕೆಯಿಡುವುದರ ಮೂಲಕ ಆತನ ಜೊತೆಯಲ್ಲಿ ನೀವು ಎದ್ದು ಬಂದಿದ್ದೀರಿ.",
			"13": "<sup>13</sup> ಅಪರಾಧಗಳನ್ನು ಮಾಡುವುದರಿಂದಲೂ ಹಾಗೂ ಸುನ್ನತಿಯಿಲ್ಲದ ಶರೀರಭಾವದಿಂದಲೂ ಸತ್ತವರಾಗಿದ್ದ ನಿಮ್ಮನ್ನು ದೇವರು ಕ್ರಿಸ್ತನೊಂದಿಗೆ ಬದುಕಿಸಿದ್ದಾನೆ ಮತ್ತು ನಮ್ಮ ಅಪರಾಧಗಳನ್ನೆಲ್ಲಾ ಕ್ಷಮಿಸಿದ್ದಾನೆ.",
			"14": "<sup>14</sup> ನಮ್ಮ ಮೇಲೆ ದೋಷಾರೋಪಣೆ ಮಾಡಿದಂಥ ಆಜ್ಞಾರೂಪವಾಗಿದ್ದಂಥ ಪತ್ರವನ್ನು ತೊಡೆದುಹಾಕಿ ಅದನ್ನು ಶಿಲುಬೆಗೆ ಜಡಿದು ಇಲ್ಲದಂತಾಗಿ ಮಾಡಿದನು.",
			"15": "<sup>15</sup> ಆತನು ದೊರೆತನಗಳನ್ನೂ ಮತ್ತು ಅಧಿಕಾರಗಳನ್ನೂ ನಿರಾಯುಧರನ್ನಾಗಿ ಮಾಡಿ, ತನ್ನ ಶಿಲುಬೆಯ ಮೂಲಕ ಅವರನ್ನು ಜಯಿಸಿ ಬಹಿರಂಗಪಡಿಸಿದನು.",
			"16": "<sup>16</sup> ಹೀಗಿರುವುದರಿಂದ, ಅನ್ನಪಾನಗಳ ವಿಷಯದಲ್ಲಿಯೂ ಅಥವಾ ಹಬ್ಬ ಅಮಾವಾಸ್ಯೆ ಹಾಗೂ ಸಬ್ಬತ್ತು ಎಂಬಿವುಗಳ ವಿಷಯದಲ್ಲಿಯೂ ನಿಮ್ಮನ್ನು ದೋಷಿಗಳೆಂದು ಯಾರೂ ನಿರ್ಣಯಿಸದಿರಲಿ.",
			"17": "<sup>17</sup> ಇವುಗಳು ಮುಂದೆ ಬರಬೇಕಾಗಿದ್ದ ಕಾರ್ಯಗಳ ಛಾಯೆಯಾಗಿವೆ, ಇವುಗಳ ನಿಜಸ್ವರೂಪವು ಕ್ರಿಸ್ತನೇ.",
			"18": "<sup>18</sup> ಕಪಟದೀನತೆಯನ್ನು ಅಪೇಕ್ಷಿಸಿ ದೇವದೂತರ ಆರಾಧನೆಯಲ್ಲಿ ಆಸಕ್ತರಾಗಿದ್ದು, ದರ್ಶನಗಳಾದವೆಂದು ಕೊಚ್ಚಿಕೊಂಡು, ಪ್ರಾಪಂಚಿಕ ಬುದ್ಧಿಯಿಂದಾಗಿ, ಕಾರಣವಿಲ್ಲದೆ ಉಬ್ಬಿಕೊಂಡು ಇರುವಂಥವರಿಗೆ ನಿಮಗೆ ದೊರಕಿರುವ ಬಿರುದನ್ನು ಅಪಹರಿಸುವುದಕ್ಕೆ ಅವಕಾಶಕೊಡಬೇಡಿರಿ.",
			"19": "<sup>19</sup> ಇಂಥವನು ಕ್ರಿಸ್ತನೆಂಬ ತಲೆಯೊಂದಿಗೆ ಸಂಬಂಧ ಕಳೆದುಕೊಂಡವನಾಗಿದ್ದಾನೆ, ತಲೆಯಿಂದಲೇ ದೇಹವೆಲ್ಲಾ ಕೀಲುನರಗಳ ಮೂಲಕ ಬೇಕಾದ ಪೋಷಣೆಯನ್ನು ಹೊಂದಿ, ಒಂದಾಗಿ ಜೋಡಿಸಲ್ಪಟ್ಟ ದೇಹವಾಗಿ, ದೇವರು ಕೊಡುವ ಅಭಿವೃದ್ಧಿಯಿಂದ ಬೆಳೆಯುತ್ತಾ ಬರುತ್ತದೆ.",
			"20": "<sup>20</sup> ಪ್ರಾಪಂಚಿಕವಾದ ಪ್ರಥಮಬೋಧನೆಯ ಪಾಲಿಗೆ ನೀವು ಕ್ರಿಸ್ತನೊಂದಿಗೆ ಸತ್ತವರಾಗಿದ್ದರೆ, ಇನ್ನೂ ಪ್ರಾಪಂಚಿಕರಾಗಿ ಬದುಕುವವರಂತೆ ಮನುಷ್ಯಕಲ್ಪಿತ ಆಜ್ಞೆಗಳನ್ನು ಮತ್ತು ಉಪದೇಶಗಳನ್ನು ಅನುಸರಿಸಿ,",
			"21": "<sup>21</sup> ಇದನ್ನು ಹಿಡಿಯಬೇಡ, ಇದರ ರುಚಿನೋಡಬೇಡ, ಅದನ್ನು ಮುಟ್ಟಬೇಡ ಎನ್ನುವ ನಿಬಂಧನೆಗಳಿಗೆ ಅಧೀನರಾಗಿರುವುದೇತಕ್ಕೆ? ಈ ಮಾನವ ಆಜ್ಞೆಗಳೂ ಹಾಗೂ ಬೋಧನೆಗಳೂ ಉಪಯೋಗದಿಂದ ನಾಶವಾಗುವ ಪದಾರ್ಥಗಳಿಗೆ ಅನುಸಾರವಾಗಿದೆ.",
			"23": "<sup>23</sup> ಇಂಥ ಉಪದೇಶಗಳು ಮನುಷ್ಯಕಲ್ಪಿತವಾದ ಆಚರಣೆಗಳಿಂದಲೂ, ಕಪಟದೀನತೆಯಿಂದಲೂ ಮತ್ತು ದೇಹದಂಡನೆಯಿಂದಲೂ ಜ್ಞಾನವುಳ್ಳದೆಂದು ತೋರಿಬರುತ್ತವೆ, ಆದರೆ ಶಾರೀರಿಕ ಇಚ್ಛೆಗಳನ್ನು ನಿಯಂತ್ರಿಸುವಲ್ಲಿ ಇವುಗಳಿಂದ ಯಾವ ಪ್ರಯೋಜನವೂ ಉಂಟಾಗುವುದಿಲ್ಲ."
		},
		"3": {
			"1": "<sup>1</sup> ನೀವು ಕ್ರಿಸ್ತನೊಂದಿಗೆ ಎಬ್ಬಿಸಲ್ಪಟ್ಟಿರುವುದಾದರೆ ಮೇಲಿನವುಗಳನ್ನೇ ಹುಡುಕಿರಿ, ಅಲ್ಲಿ ಕ್ರಿಸ್ತನು ದೇವರ ಬಲಗಡೆಯಲ್ಲಿ ಕುಳಿತುಕೊಂಡಿದ್ದಾನೆ.",
			"2": "<sup>2</sup> ಭೂಲೋಕದಲ್ಲಿರುವಂಥವುಗಳ ಬಗ್ಗೆ ಅಲ್ಲ, ಪರಲೋಕದಲ್ಲಿರುವಂಥವುಗಳ ಬಗ್ಗೆ ಯೋಚಿಸಿರಿ.",
			"3": "<sup>3</sup> ಯಾಕೆಂದರೆ ನೀವು ಸತ್ತು ಮತ್ತು ನಿಮ್ಮ ಜೀವವು ಕ್ರಿಸ್ತನೊಂದಿಗೆ ದೇವರಲ್ಲಿ ಮರೆಯಾಗಿದೆ.",
			"4": "<sup>4</sup> ನಿಮಗೆ ಜೀವವಾಗಿರುವ ಕ್ರಿಸ್ತನು ಪ್ರತ್ಯಕ್ಷನಾಗುವಾಗ ನೀವು ಸಹ ಆತನೊಂದಿಗೆ ಮಹಿಮೆಯಲ್ಲಿ ಪ್ರತ್ಯಕ್ಷರಾಗುವಿರಿ.",
			"5": "<sup>5</sup> ಆದುದರಿಂದ ನಿಮ್ಮಲ್ಲಿರುವ ಲೌಕಿಕ ಆಸೆಗಳು ಅಂದರೆ, ಜಾರತ್ವ, ಅಶುದ್ಧತ್ವ, ಕಾಮಾಭಿಲಾಷೆ, ಕೆಟ್ಟ ಅಭಿಲಾಷೆ ಮತ್ತು ವಿಗ್ರಹಾರಾಧನೆಗೆ ಸಮವಾಗಿರುವ ದುರಾಶೆ ಇಂಥವುಗಳನ್ನು ಸಾಯಿಸಿರಿ.",
			"6": "<sup>6</sup> ಇವುಗಳ ನಿಮಿತ್ತ ಅವಿಧೇಯರಾಗುವವರ ಮೇಲೆ ದೇವರ ಕೋಪವು ಉಂಟಾಗುತ್ತದೆ.",
			"7": "<sup>7</sup> ಹಿಂದೆ ನೀವು ಸಹ ಅಂಥವುಗಳಲ್ಲಿ ಜೀವಿಸುತ್ತಿದ್ದು ಅವುಗಳನ್ನು ನಡಿಸುತ್ತಿದ್ದಿರಿ.",
			"8": "<sup>8</sup> ಆದರೆ ಈಗಲಾದರೋ ಕ್ರೋಧ, ಕೋಪ, ಮತ್ಸರ, ದೂಷಣೆ ಮತ್ತು ಬಾಯಿಂದ ಹೊರಡುವ ದುರ್ಭಾಷೆ ಇವುಗಳನ್ನು ಬಿಟ್ಟುಬಿಡಿರಿ.",
			"9": "<sup>9</sup> ಒಬ್ಬರಿಗೊಬ್ಬರು ಸುಳ್ಳಾಡಬೇಡಿರಿ, ಯಾಕೆಂದರೆ ನೀವು ಹಿಂದಿನಸ್ವಭಾವವನ್ನು ಅದರ ಕೃತ್ಯಗಳೊಂದಿಗೆ ತೆಗೆದುಹಾಕಿ ನೂತನಸ್ವಭಾವವನ್ನು ಧರಿಸಿಕೊಂಡಿದ್ದೀರಲ್ಲವೇ.",
			"10": "<sup>10</sup> ಈ ನೂತನ ಸ್ವಭಾವವು ಸೃಷ್ಟಿಸಿದಾತನ ಹೋಲಿಕೆಯ ಜ್ಞಾನದ ಮೇರೆಗೆ ಅದು ನೂತನವಾಗುತ್ತಾ ಬರುತ್ತದೆ.",
			"11": "<sup>11</sup> ಈ ಜ್ಞಾನದಲ್ಲಿ ಗ್ರೀಕನು ಮತ್ತು ಯೆಹೂದ್ಯನು ಎಂಬ ಭೇದವಿಲ್ಲ, ಸುನ್ನತಿಮಾಡಿಸಿಕೊಂಡವರು ಮತ್ತು ಸುನ್ನತಿ ಮಾಡಿಸಿಕೊಳ್ಳದವರು ಎಂಬ ಭೇದವಿಲ್ಲ, ನಾಗರಿಕ, ಅನಾಗರಿಕನು ಎಂಬ ಭೇದವಿಲ್ಲ, ದಾಸನು, ಸ್ವತಂತ್ರನು ಎಂಬ ಭೇದವಿಲ್ಲ, ಆದರ ಬದಲಾಗಿ ಕ್ರಿಸ್ತನೇ ಸಮಸ್ತವೂ ಹಾಗೂ ಸಮಸ್ತರಲ್ಲಿಯೂ ಇರುವಾತನಾಗಿದ್ದಾನೆ.",
			"12": "<sup>12</sup> ಹೀಗಿರಲಾಗಿ ದೇವರಿಂದ ಆರಿಸಿಕೊಂಡವರಾಗಿ, ಪರಿಶುದ್ಧರೂ ಹಾಗೂ ಪ್ರಿಯರೂ ಆಗಿರುವುದರಿಂದ ಕನಿಕರ, ದಯೆ, ದೀನತೆ, ಸಾತ್ವಿಕತ್ವ ಮತ್ತು ಸಹನೆ ಎಂಬ ಸದ್ಗುಣಗಳನ್ನು ಧರಿಸಿಕೊಳ್ಳಿರಿ.",
			"13": "<sup>13</sup> ಒಬ್ಬರನ್ನೊಬ್ಬರು ಸೈರಿಸಿಕೊಂಡು ಯಾರಿಗಾದರೂ ಮತ್ತೊಬ್ಬನ ಮೇಲೆ ತಪ್ಪುಹೊರಿಸುವುದಕ್ಕೆ ಕಾರಣವಿದ್ದರೂ, ತಪ್ಪುಹೊರಿಸದೆ ಕ್ಷಮಿಸಿರಿ, ಕರ್ತನು ನಿಮ್ಮನ್ನು ಕ್ಷಮಿಸಿದಂತೆಯೇ ನೀವೂ ಕ್ಷಮಿಸಿರಿ.",
			"14": "<sup>14</sup> ಇವೆಲ್ಲಕ್ಕಿಂತ ಮಿಗಿಲಾಗಿ ಸಂಪೂರ್ಣತೆಯ ಬಂಧವಾಗಿರುವ ಪ್ರೀತಿಯನ್ನು ಧರಿಸಿಕೊಳ್ಳಿರಿ.",
			"15": "<sup>15</sup> ಕ್ರಿಸ್ತನ ಸಮಾಧಾನವು ನಿಮ್ಮ ಹೃದಯಗಳಲ್ಲಿ ಆಳಲಿ. ಇದೇ ಸಮಾಧಾನದಲ್ಲಿ ಏಕ ದೇಹವಾಗಿರುವಂತೆ ನೀವು ಕರೆಯಲ್ಪಟ್ಟಿರುವಿರಿ ಇದಲ್ಲದೆ ಕೃತಜ್ಞತೆಯುಳ್ಳವರಾಗಿರಿ.",
			"16": "<sup>16</sup> ಕ್ರಿಸ್ತನ ವಾಕ್ಯವು ನಿಮ್ಮಲ್ಲಿ ಸಮೃದ್ಧಿಯಾಗಿ ವಾಸಿಸಲಿ. ಸಕಲಜ್ಞಾನದಿಂದ ಒಬ್ಬರಿಗೊಬ್ಬರು ಉಪದೇಶಮಾಡಿಕೊಂಡು ಬುದ್ಧಿಹೇಳಿಕೊಳ್ಳಿರಿ. ಕೀರ್ತನೆಗಳಿಂದಲೂ, ಸ್ತುತಿಪದಗಳಿಂದಲೂ ಮತ್ತು ಆತ್ಮಿಕವಾದ ಗೀತೆಗಳಿಂದಲೂ ಹಾಗೂ ನಿಮ್ಮ ಹೃದಯಗಳಲ್ಲಿ ದೇವರಿಗೆ ಕೃತಜ್ಞತೆಯಿಂದಲೂ ಹಾಡಿರಿ.",
			"17": "<sup>17</sup> ನೀವು ನುಡಿಯಿಂದಾಗಲಿ ನಡೆಯಿಂದಾಗಲಿ ಏನೇ ಮಾಡಿದರೂ ಅದೆಲ್ಲವನ್ನೂ ಕರ್ತನಾದ ಯೇಸುವಿನ ಹೆಸರಿನಲ್ಲಿಯೇ ಮಾಡಿರಿ ಮತ್ತು ತಂದೆಯಾದ ದೇವರಿಗೆ ಆತನ ಮೂಲಕ ಕೃತಜ್ಞತಾಸ್ತುತ್ತಿಯನ್ನು ಸಲ್ಲಿಸಿರಿ.",
			"18": "<sup>18</sup> ಸತಿಯರೇ, ಕರ್ತನಿಗೆ ಯೋಗ್ಯವಾದ ರೀತಿಯಲ್ಲಿ ನಿಮ್ಮ ಗಂಡಂದಿರಿಗೆ ಅಧೀನರಾಗಿರಿ.",
			"19": "<sup>19</sup> ಗಂಡಂದಿರೇ, ನಿಮ್ಮ ಹೆಂಡತಿಯರನ್ನು ಪ್ರೀತಿಸಿರಿ, ಅವರೊಂದಿಗೆ ಕಠಿಣವಾಗಿ ವರ್ತಿಸಬೇಡಿರಿ.",
			"20": "<sup>20</sup> ಮಕ್ಕಳೇ, ಎಲ್ಲಾ ವಿಷಯಗಳಲ್ಲಿ ನಿಮ್ಮ ತಂದೆತಾಯಿಗಳಿಗೆ ವಿಧೇಯರಾಗಿರಿ, ಯಾಕೆಂದರೆ ಇದು ಕರ್ತನ ದೃಷ್ಟಿಯಲ್ಲಿ ಮೆಚ್ಚಿಕೆಯಾಗಿದೆ.",
			"21": "<sup>21</sup> ತಂದೆಗಳೇ, ನಿಮ್ಮ ಮಕ್ಕಳನ್ನು ಕೆಣಕಿ ಅವರನ್ನು ಮನಗುಂದಿಸಿಬೇಡಿರಿ.",
			"22": "<sup>22</sup> ದಾಸರೇ, ಈ ಲೋಕದಲ್ಲಿರುವ ನಿಮ್ಮ ಯಜಮಾನರಿಗೆ ಎಲ್ಲಾ ವಿಷಯಗಳಲ್ಲಿ ವಿಧೇಯರಾಗಿರಿ, ಮನುಷ್ಯರನ್ನು ಮೆಚ್ಚಿಸುವವರು ಮಾಡುವ ಹಾಗೆ ನಿಮ್ಮ ಯಜಮಾನರು ನೋಡುತ್ತಿರುವಾಗ ಮಾತ್ರ ಕೆಲಸಮಾಡದೆ, ಕರ್ತನಿಗೆ ಭಯಪಡುವ ಪ್ರಾಮಾಣಿಕವಾದ ಹೃದಯದಿಂದ ಕೆಲಸ ಮಾಡಿರಿ.",
			"23": "<sup>23</sup> ನೀವು ಯಾವ ಕೆಲಸವನ್ನು ಮಾಡಿದರೋ ಅದನ್ನು ಮನುಷ್ಯರಿಗೋಸ್ಕರವೆಂದು ಮಾಡದೇ ಕರ್ತನಿಗೋಸ್ಕರವೇ ಎಂದು ಮನಃಪೂರ್ವಕವಾಗಿ ಮಾಡಿರಿ.",
			"24": "<sup>24</sup> ಕರ್ತನಿಂದ ಬಾಧ್ಯತೆಯೆಂಬ ಪ್ರತಿಫಲವನ್ನು ಹೊಂದುವಿರೆಂದು ತಿಳಿದುಕೊಂಡು ಕರ್ತನಾದ ಕ್ರಿಸ್ತನನ್ನು ಸೇವಿಸುವವರಾಗಿರಿ.",
			"25": "<sup>25</sup> ಅನ್ಯಾಯಮಾಡುವವನು ತಾನು ಮಾಡಿದ ಅನ್ಯಾಯಕ್ಕೆ ತಕ್ಕ ದಂಡನೆಯನ್ನು ಹೊಂದುವನಷ್ಟೆ ಮತ್ತು ಅದರಲ್ಲಿ ಪಕ್ಷಪಾತವಿರುವುದಿಲ್ಲ."
		},
		"4": {
			"1": "<sup>1</sup> ಯಜಮಾನರೇ, ಪರಲೋಕದಲ್ಲಿ ನಿಮಗೂ ಒಬ್ಬ ಯಜಮಾನನೊಬ್ಬನಿದ್ದಾನೆಂದು ತಿಳಿದು ನಿಮ್ಮ ದಾಸರೊಂದಿಗೆ ನೀತಿಯಿಂದಲೂ ನ್ಯಾಯದಿಂದಲೂ ವರ್ತಿಸಿರಿ.",
			"2": "<sup>2</sup> ಪ್ರಾರ್ಥನೆಯನ್ನು ತಪ್ಪದೆ ಮಾಡುವವರಾಗಿ, ಎಚ್ಚರವಾಗಿದ್ದು ದೇವರಿಗೆ ಕೃತಜ್ಞತಾಸ್ತುತಿಮಾಡಿರಿ.",
			"3": "<sup>3</sup> ಕ್ರಿಸ್ತನ ಮರ್ಮವನ್ನು ಅಂದರೆ ಸುರ್ವಾತೆಯನ್ನು ನಾವು ಸಾರುವುದಕ್ಕೆ ದೇವರು ನಮಗೆ ಬಾಗಿಲನ್ನು ತೆರೆದು ಕೊಡುವುದಕ್ಕಾಗಿ ನಮಗೋಸ್ಕರವಾಗಿ ಪ್ರಾರ್ಥಿಸಿರಿ. ಈ ಸುರ್ವಾತೆಯ ನಿಮಿತ್ತವೇ ನಾನು ಸೆರೆಯಲ್ಲಿದ್ದೇನಲ್ಲಾ.",
			"4": "<sup>4</sup> ನಾನು ಆ ಸತ್ಯಾರ್ಥವನ್ನು ಹೇಳಬೇಕಾದ ರೀತಿಯಲ್ಲಿ ಸ್ಪಷ್ಟವಾಗಿ ಹೇಳುವಂತೆ ಪ್ರಾರ್ಥಿಸಿರಿ.",
			"5": "<sup>5</sup> ಸಮಯವನ್ನು ಸರಿಯಾಗಿ ಸದುಪಯೋಗಿಸಿಕೊಂಡು ಹೊರಗಿನವರೊಂದಿಗೆ ಜ್ಞಾನವುಳ್ಳವರಾಗಿ ನಡೆದುಕೊಳ್ಳಿರಿ.",
			"6": "<sup>6</sup> ನಿಮ್ಮ ಸಂಭಾಷಣೆಯು ಯಾವಾಗಲೂ ಕೃಪೆಯುಳ್ಳದ್ದಾಗಿಯೂ, ಉಪ್ಪಿನಂತೆ ರುಚಿಕರವಾಗಿಯೂ ಇರಲಿ. ಹೀಗೆ ನೀವು ಪ್ರತಿಯೊಬ್ಬರಿಗೂ ಯಾವಾವ ರೀತಿಯಲ್ಲಿ ಉತ್ತರಹೇಳಬೇಕು ಎಂಬುದನ್ನು ತಿಳಿದುಕೊಳ್ಳುವಿರಿ.",
			"7": "<sup>7</sup> ಪ್ರಿಯ ಸಹೋದರನೂ, ನಂಬಿಗಸ್ತನಾದ ಸೇವಕನೂ ಮತ್ತು ಕರ್ತನಲ್ಲಿ ಜೊತೆಯ ದಾಸನೂ ಆಗಿರುವ ತುಖಿಕನು ನನ್ನ ವಿಷಯಗಳನ್ನೆಲ್ಲಾ ನಿಮಗೆ ತಿಳಿಸುವನು.",
			"8": "<sup>8</sup> ನೀವು ನಮ್ಮ ಕುರಿತು ತಿಳಿದುಕೊಳ್ಳುವಂತೆ, ಅವನು ನಿಮ್ಮ ಹೃದಯಗಳನ್ನು ಉರಿದುಂಬಿಸುವಂತೆ,",
			"9": "<sup>9</sup> ಅವನನ್ನು ನಂಬಿಗಸ್ತನು ಮತ್ತು ಪ್ರಿಯ ಸಹೋದರನಾಗಿರುವ ನಿಮ್ಮ ಊರಿನವನೇ ಆದ ಓನೇಸಿಮನ ಜೊತೆಯಲ್ಲಿ ಕಳುಹಿಸಿದ್ದೇನೆ. ಅವರು ಇಲ್ಲಿ ನಡೆಯುತ್ತಿರುವ ವಿಷಯಗಳನ್ನೆಲ್ಲಾ ನಿಮಗೆ ತಿಳಿಸುವರು.",
			"10": "<sup>10</sup> ನನ್ನ ಜೊತೆ ಸೆರೆಯವನಾದ ಅರಿಸ್ತಾರ್ಕನೂ ಮತ್ತು ಬಾರ್ನಬನ ಸೋದರಸಂಬಂಧಿಯಾಗಿರುವ ಮಾರ್ಕನೂ ನಿಮಗೆ ವಂದನೆ ಹೇಳುತ್ತಾರೆ. ಅವನು ನಿಮ್ಮ ಬಳಿಗೆ ಬಂದರೆ ಅವನನ್ನು ಸೇರಿಸಿಕೊಳ್ಳಿರಿ. ಅವನ ವಿಷಯದಲ್ಲಿ ಅಪ್ಪಣೆಗಳನ್ನು ಹೊಂದಿದ್ದೀರಲ್ಲಾ.",
			"11": "<sup>11</sup> ಯುಸ್ತನೆನಿಸಿಕೊಳ್ಳುವ ಯೇಸು ಸಹ ನಿಮಗೆ ವಂದನೆ ಹೇಳುತ್ತಾನೆ. ಸುನ್ನತಿಯವರೊಳಗೆ ಇವರು ಮಾತ್ರವೇ ದೇವರ ರಾಜ್ಯಕ್ಕಾಗಿ ನನ್ನ ಜೊತೆಸೇವಕರಾಗಿದ್ದಾರೆ, ಇವರಿಂದ ನನಗೆ ಆದರಣೆ ಉಂಟಾಯಿತು.",
			"12": "<sup>12</sup> ಕ್ರಿಸ್ತಯೇಸುವಿನ ದಾಸನಾಗಿರುವ ನಿಮ್ಮ ಊರಿನವನಾದ ಎಪಫ್ರನು ನಿಮಗೆ ವಂದನೆ ಹೇಳುತ್ತಾನೆ, ನೀವು ಪ್ರವೀಣರಾಗಿಯೂ ದೇವರ ಎಲ್ಲಾ ಚಿತ್ತದಲ್ಲಿ ಪೂರ್ಣ ನಿಶ್ಚಯವುಳ್ಳವರಾಗಿರಬೇಕೆಂದು ನಿಮಗೋಸ್ಕರ ಯಾವಾಗಲೂ ಪ್ರಾರ್ಥನೆಯಲ್ಲಿ ಹೋರಾಡುತ್ತಾನೆ.",
			"13": "<sup>13</sup> ಇವನು ನಿಮಗಾಗಿಯೂ, ಲವೊದಿಕೀಯದವರಿಗಾಗಿಯೂ ಮತ್ತು ಹಿರಿಯಾಪೋಲಿಯದವರಿಗಾಗಿಯೂ ಬಹಳ ಪ್ರಯಾಸ ಪಡುತ್ತಾನೆಂದು ನಾನು ಸಾಕ್ಷಿಹೇಳುತ್ತೇನೆ.",
			"14": "<sup>14</sup> ಪ್ರಿಯ ವೈದ್ಯನಾಗಿರುವ ಲೂಕನು ಮತ್ತು ದೇಮನು ನಿಮಗೆ ವಂದನೆ ಹೇಳುತ್ತಾರೆ.",
			"15": "<sup>15</sup> ಲವೊದಿಕೀಯದಲ್ಲಿರುವ ಸಹೋದರರಿಗೂ ಮತ್ತು ನುಂಫಳಿಗೂ ಹಾಗೂ ಆಕೆಯ ಮನೆಯಲ್ಲಿ ಸೇರಿಬರುವ ಸಭೆಯವರಿಗೂ ವಂದನೆ ಹೇಳಿರಿ.",
			"16": "<sup>16</sup> ನಿಮ್ಮಲ್ಲಿ ಈ ಪತ್ರವನ್ನು ಓದಿದ ತರುವಾಯ ಲವೊದಿಕೀಯದವರ ಸಭೆಯಲ್ಲಿಯೂ ಇದನ್ನು ಓದಿಸಿರಿ ಮತ್ತು ನಾನು ಬರೆದ ಪತ್ರವನ್ನು ಲವೊದಿಕೀಯದಿಂದ ತರಿಸಿ ನೀವೂ ಓದಿಕೊಳ್ಳಿರಿ.",
			"17": "<sup>17</sup> ಅರ್ಖಿಪ್ಪನಿಗೆ, ನೀನು ಕರ್ತನಿಂದ ಹೊಂದಿರುವ ಸೇವೆಯನ್ನು ಎಚ್ಚರವಾಗಿದ್ದು ನೆರವೇರಿಸಬೇಕೆಂದು ಹೇಳಿರಿ.",
			"18": "<sup>18</sup> ಇದು ಪೌಲನೆಂಬ ನಾನು ಸ್ವಂತ ಕೈಯಿಂದಲೇ ಬರೆದ ವಂದನೆ. ನಾನು ಸೆರೆಯಲ್ಲಿದ್ದೇನೆಂಬುದನ್ನು ಜ್ಞಾಪಕ ಮಾಡಿಕೊಳ್ಳಿರಿ. ಕೃಪೆಯು ನಿಮ್ಮೊಂದಿಗಿರಲಿ."
		}
	}
};
module.exports = book;