var book = {
	"name": "ಆದಿಕಾಂಡ",
	"numChapters": 50,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ಆದಿಯಲ್ಲಿ ದೇವರು ಆಕಾಶವನ್ನೂ ಭೂಮಿಯನ್ನೂ ಉಂಟುಮಾಡಿದನು.",
			"2": "<sup>2</sup> ಭೂಮಿಯು ಕ್ರಮವಿಲ್ಲದೆಯೂ ಬರಿದಾಗಿಯೂ ಇತ್ತು; ಆದಿಸಾಗರದ ಮೇಲೆ ಕತ್ತಲಿತ್ತು; ದೇವರಾತ್ಮವು ಜಲಸಮೂಹಗಳ ಮೇಲೆ ಚಲಿಸುತ್ತಿತ್ತು.",
			"3": "<sup>3</sup> ಆಗ ದೇವರು - ಬೆಳಕಾಗಲಿ ಅನ್ನಲು ಬೆಳಕಾಯಿತು.",
			"4": "<sup>4</sup> ದೇವರು ಆ ಬೆಳಕನ್ನು ಒಳ್ಳೇದೆಂದು ನೋಡಿದನು. ದೇವರು ಬೆಳಕನ್ನೂ ಕತ್ತಲೆಯನ್ನೂ ಬೇರೆ ಬೇರೆ ಮಾಡಿ ಬೆಳಕಿಗೆ ಹಗಲೆಂದೂ ಕತ್ತಲೆಗೆ ಇರುಳೆಂದೂ ಹೆಸರಿಟ್ಟನು.",
			"5": "<sup>5</sup> ಹೀಗೆ ಸಾಯಂಕಾಲವೂ ಪ್ರಾತಃಕಾಲವೂ ಆಗಿ ಮೊದಲನೆಯ ದಿನವಾಯಿತು.",
			"6": "<sup>6</sup> ಬಳಿಕ ದೇವರು - ಜಲಸಮೂಹಗಳ ನಡುವೆ ವಿಸ್ತಾರವಾದ ಗುಮಟವು ಉಂಟಾಗಲಿ; ಅದು ಕೆಳಗಣ ನೀರುಗಳನ್ನೂ ಮೇಲಣ ನೀರುಗಳನ್ನೂ ಬೇರೆ ಬೇರೆ ಮಾಡಲಿ ಅಂದನು.",
			"7": "<sup>7</sup> ದೇವರು ವಿಸ್ತಾರವಾದ ಗುಮಟವನ್ನು ಮಾಡಿ ಅದರ ಕೆಳಗಿದ್ದ ನೀರುಗಳನ್ನು ಅದರ ಮೇಲಿದ್ದ ನೀರುಗಳಿಂದ ವಿಂಗಡಿಸಿದನು; ಹಾಗೆಯೇ ಆಯಿತು.",
			"8": "<sup>8</sup> ದೇವರು ಆ ಗುಮಟಕ್ಕೆ ಆಕಾಶವೆಂದು ಹೆಸರಿಟ್ಟನು. ಹೀಗೆ ಸಾಯಂಕಾಲವೂ ಪ್ರಾತಃಕಾಲವೂ ಆಗಿ ಎರಡನೆಯ ದಿನವಾಯಿತು.",
			"9": "<sup>9</sup> ಅನಂತರ ದೇವರು - ಆಕಾಶದ ಕೆಳಗಿರುವ ನೀರೆಲ್ಲಾ ಒಂದೇ ಸ್ಥಳದಲ್ಲಿ ಕೂಡಿಕೊಳ್ಳಲಿ, ಒಣನೆಲವು ಕಾಣಿಸಲಿ ಅಂದನು; ಹಾಗೆಯೇ ಆಯಿತು.",
			"10": "<sup>10</sup> ದೇವರು ಒಣನೆಲಕ್ಕೆ ಭೂಮಿಯೆಂದೂ ಜಲಸಮೂಹಕ್ಕೆ ಸಮುದ್ರವೆಂದೂ ಹೆಸರಿಟ್ಟನು. ಆತನು ಅದನ್ನು ಒಳ್ಳೇದೆಂದು ನೋಡಿದನು.",
			"11": "<sup>11</sup> ತರುವಾಯ ದೇವರು - ಭೂಮಿಯು ಹುಲ್ಲನ್ನೂ ಬೀಜಬಿಡುವ ಕಾಯಿಪಲ್ಯದ ಗಿಡಗಳನ್ನೂ ಬೆಳೆಸಲಿ; ಮತ್ತು ಬೀಜವುಳ್ಳ ಹಣ್ಣಿನ ಮರಗಳನ್ನೂ ಅವುಗಳ ಜಾತಿಗನುಸಾರವಾಗಿ ಹುಟ್ಟಿಸಲಿ ಎಂದು ಹೇಳಿದನು; ಹಾಗೆಯೇ ಆಯಿತು.",
			"12": "<sup>12</sup> ಭೂಮಿಯಲ್ಲಿ ಹುಲ್ಲು ಬೆಳೆಯಿತು; ತಮ್ಮತಮ್ಮ ಜಾತಿಯ ಪ್ರಕಾರ ಬೀಜಬಿಡುವ ಕಾಯಿಪಲ್ಯದ ಗಿಡಗಳು ಉಂಟಾದವು; ತಮ್ಮತಮ್ಮ ಜಾತಿಗನುಸಾರವಾಗಿ ಬೀಜವುಳ್ಳ ಹಣ್ಣಿನ ಮರಗಳು ಕಾಣಿಸಿದವು. ದೇವರು ಅದನ್ನು ಒಳ್ಳೇದೆಂದು ನೋಡಿದನು.",
			"13": "<sup>13</sup> ಹೀಗೆ ಸಾಯಂಕಾಲವೂ ಪ್ರಾತಃಕಾಲವೂ ಆಗಿ ಮೂರನೆಯ ದಿನವಾಯಿತು.",
			"14": "<sup>14</sup> ಬಳಿಕ ದೇವರು - ಆಕಾಶಮಂಡಲದಲ್ಲಿ ಬೆಳಕುಗಳು ಉಂಟಾಗಲಿ; ಅವು ಹಗಲಿರುಳುಗಳನ್ನು ಬೇರೆ ಬೇರೆ ಮಾಡಲಿ; ಇದಲ್ಲದೆ ಅವು ಗುರುತುಗಳಾಗಿದ್ದು ಸಮಯಗಳನ್ನೂ ದಿನಸಂವತ್ಸರಗಳನ್ನೂ ತೋರಿಸಲಿ;",
			"15": "<sup>15</sup> ಮತ್ತು ಭೂಮಿಯ ಮೇಲೆ ಬೆಳಕುಕೊಡುವದಕ್ಕೆ ಅವು ಆಕಾಶಮಂಡಲದಲ್ಲಿ ದೀಪಗಳಂತಿರಲಿ ಅಂದನು; ಹಾಗೆಯೇ ಆಯಿತು.",
			"16": "<sup>16</sup> ದೇವರು ಹಗಲನ್ನಾಳುವದಕ್ಕೆ ದೊಡ್ಡ ಬೆಳಕನ್ನೂ ಇರುಳನ್ನಾಳುವದಕ್ಕೆ ಚಿಕ್ಕ ಬೆಳಕನ್ನೂ, ಈ ಎರಡು ದೊಡ್ಡ ಬೆಳಕುಗಳನ್ನು ಉಂಟುಮಾಡಿದನು. ಆತನು ನಕ್ಷತ್ರಗಳನ್ನು ಸಹ ಉಂಟುಮಾಡಿದನು.",
			"17": "<sup>17</sup> ದೇವರು ಆ ಬೆಳಕುಗಳನ್ನು ಆಕಾಶದಲ್ಲಿಟ್ಟು ಭೂಮಿಯ ಮೇಲೆ ಬೆಳಕು ಕೊಡುವದಕ್ಕೂ",
			"18": "<sup>18</sup> ಹಗಲಿರುಳುಗಳನ್ನು ಆಳುವದಕ್ಕೂ ಬೆಳಕನ್ನು ಕತ್ತಲನ್ನು ಬೇರೆ ಬೇರೆ ಮಾಡುವದಕ್ಕೂ ಅವುಗಳನ್ನು ನೇಮಿಸಿದನು. ಆತನು ಅದನ್ನು ಒಳ್ಳೇದೆಂದು ನೋಡಿದನು.",
			"19": "<sup>19</sup> ಹೀಗೆ ಸಾಯಂಕಾಲವೂ ಪ್ರಾತಃಕಾಲವೂ ಆಗಿ ನಾಲ್ಕನೆಯ ದಿನವಾಯಿತು.",
			"20": "<sup>20</sup> ತರುವಾಯ ದೇವರು - ಗುಂಪುಗುಂಪಾಗಿ ಚಲಿಸುವ ಜಲಜಂತುಗಳು ನೀರಿನಲ್ಲಿ ತುಂಬಿಕೊಳ್ಳಲಿ; ಪಕ್ಷಿಗಳು ಭೂಮಿಯ ಮೇಲೆ ಅಂತರಿಕ್ಷದೊಳಗೆ ಹಾರಾಡಲಿ ಎಂದು ಹೇಳಿದನು.",
			"21": "<sup>21</sup> ಹೀಗೆ ದೇವರು ಮಹಾಜಲಚರಗಳನ್ನೂ ನೀರಿನಲ್ಲಿ ತುಂಬಿರುವ ಸಕಲವಿಧವಾದ ಜೀವಿಗಳನ್ನೂ ರೆಕ್ಕೆಗಳುಳ್ಳ ಸಕಲವಿಧವಾದ ಪಕ್ಷಿಗಳನ್ನೂ ಅವುಗಳ ಜಾತಿಗನುಸಾರವಾಗಿ ಉಂಟುಮಾಡಿದನು. ಆತನು ಅದನ್ನು ಒಳ್ಳೇದೆಂದು ನೋಡಿದನು.",
			"22": "<sup>22</sup> ಇದಲ್ಲದೆ ದೇವರು ಅವುಗಳಿಗೆ - ನೀವು ಅಭಿವೃದ್ಧಿಯಾಗಿ ಹೆಚ್ಚಿರಿ; ಜಲಚರಗಳು ಸಮುದ್ರದ ನೀರಿನಲ್ಲಿ ತುಂಬಿಕೊಳ್ಳಲಿ; ಪಕ್ಷಿಗಳು ಭೂಮಿಯ ಮೇಲೆ ಹೆಚ್ಚಲಿ ಎಂದು ಹೇಳಿ ಆಶೀರ್ವದಿಸಿದನು.",
			"23": "<sup>23</sup> ಹೀಗೆ ಸಾಯಂಕಾಲವೂ ಪ್ರಾತಃಕಾಲವೂ ಆಗಿ ಐದನೆಯ ದಿನವಾಯಿತು.",
			"24": "<sup>24</sup> ಮತ್ತು ದೇವರು - ಭೂಮಿಯಿಂದ ಜೀವಜಂತುಗಳು ಉಂಟಾಗಲಿ; ಪಶುಕ್ರಿಮಿಗಳೂ ಕಾಡುಮೃಗಗಳೂ ತಮ್ಮ ತಮ್ಮ ಜಾತಿಗನುಸಾರವಾಗಿ ಹುಟ್ಟಲಿ ಅಂದನು; ಹಾಗೆಯೇ ಆಯಿತು.",
			"25": "<sup>25</sup> ದೇವರು ಕಾಡುಮೃಗಗಳನ್ನೂ ಪಶುಗಳನ್ನೂ ನೆಲದ ಮೇಲೆ ಹರಿದಾಡುವ ಕ್ರಿಮಿಕೀಟಗಳನ್ನೂ ಅವುಗಳ ಜಾತಿಗನುಸಾರ ಉಂಟುಮಾಡಿದನು. ಆತನು ಅದನ್ನು ಒಳ್ಳೇದೆಂದು ನೋಡಿದನು.",
			"26": "<sup>26</sup> ಆಮೇಲೆ ದೇವರು - ನಮ್ಮ ಸ್ವರೂಪದಲ್ಲಿ ನಮ್ಮ ಹೋಲಿಕೆಗೆ ಸರಿಯಾಗಿ ಮನುಷ್ಯರನ್ನು ಉಂಟುಮಾಡೋಣ; ಅವರು ಸಮುದ್ರದಲ್ಲಿರುವ ಮೀನುಗಳ ಮೇಲೆಯೂ ಅಂತರಿಕ್ಷದಲ್ಲಿ ಹಾರಾಡುವ ಪಕ್ಷಿಗಳ ಮೇಲೆಯೂ ಪಶುಗಳ ಮೇಲೆಯೂ ನೆಲದ ಮೇಲೆ ಹರಿದಾಡುವ ಎಲ್ಲಾ ಕ್ರಿಮಿಕೀಟಗಳ ಮೇಲೆಯೂ ಎಲ್ಲಾ ಭೂಮಿಯ ಮೇಲೆಯೂ ದೊರೆತನಮಾಡಲಿ ಅಂದನು.",
			"27": "<sup>27</sup> ಹೀಗೆ ದೇವರು ಮನುಷ್ಯನನ್ನು ತನ್ನ ಸ್ವರೂಪದಲ್ಲಿ ಉಂಟುಮಾಡಿದನು; ದೇವಸ್ವರೂಪದಲ್ಲಿ ಅವನನ್ನು ಉಂಟುಮಾಡಿದನು; ಅವರನ್ನು ಗಂಡುಹೆಣ್ಣಾಗಿ ನಿರ್ಮಿಸಿದನು.",
			"28": "<sup>28</sup> ಇದಲ್ಲದೆ ದೇವರು ಅವರನ್ನು ಆಶೀರ್ವದಿಸಿ - ನೀವು ಬಹುಸಂತಾನವುಳ್ಳವರಾಗಿ ಹೆಚ್ಚಿರಿ; ಭೂಮಿಯಲ್ಲಿ ತುಂಬಿಕೊಂಡು ಅದನ್ನು ವಶಮಾಡಿಕೊಳ್ಳಿರಿ. ಸಮುದ್ರದ ಮೀನುಗಳ ಮೇಲೆಯೂ ಆಕಾಶದ ಪಕ್ಷಿಗಳ ಮೇಲೆಯೂ ಭೂಮಿಯಲ್ಲಿ ಚಲಿಸುವ ಎಲ್ಲಾ ಜೀವಿಗಳ ಮೇಲೆಯೂ ದೊರೆತನಮಾಡಿರಿ ಅಂದನು.",
			"29": "<sup>29</sup> ಮತ್ತು ದೇವರು - ಇಗೋ, ಸಮಸ್ತಭೂಮಿಯ ಮೇಲೆ ಬೀಜವುಳ್ಳ ಎಲ್ಲಾ ಪೈರುಗಳನ್ನೂ ಬೀಜವುಳ್ಳ ಎಲ್ಲಾ ಹಣ್ಣಿನ ಮರಗಳನ್ನೂ ನಿಮಗೆ ಆಹಾರಕ್ಕಾಗಿ ಕೊಟ್ಟಿದ್ದೇನೆ.",
			"30": "<sup>30</sup> ಇದಲ್ಲದೆ ಭೂಮಿಯ ಮೇಲೆ ತಿರುಗಾಡುವ ಮೃಗಗಳು, ಆಕಾಶದಲ್ಲಿ ಹಾರಾಡುವ ಪಕ್ಷಿಗಳು, ನೆಲದಲ್ಲಿ ಹರಿದಾಡುವ ಕ್ರಿಮಿಕೀಟಗಳು ಎಂಬ ಜೀವಿಗಳಿಗೆ ಎಲ್ಲಾ ಹುಲ್ಲುಸೊಪ್ಪುಗಳನ್ನು ಆಹಾರಕ್ಕಾಗಿ ಕೊಟ್ಟಿದ್ದೇನೆ ಎಂದು ಹೇಳಿದನು; ಅದು ಹಾಗೆಯೇ ಆಯಿತು.",
			"31": "<sup>31</sup> ದೇವರು ತಾನು ಉಂಟುಮಾಡಿದ್ದನ್ನೆಲ್ಲಾ ನೋಡಲಾಗಿ ಅದು ಬಹು ಒಳ್ಳೇದಾಗಿತ್ತು. ಸಾಯಂಕಾಲವೂ ಪ್ರಾತಃಕಾಲವೂ ಆಗಿ ಆರನೆಯ ದಿನವಾಯಿತು."
		},
		"2": {
			"1": "<sup>1</sup> ಹೀಗೆ ಭೂಮ್ಯಾಕಾಶಗಳೂ ಅವುಗಳಲ್ಲಿರುವ ಸಮಸ್ತವೂ ನಿರ್ಮಿತವಾದವು.",
			"2": "<sup>2</sup> ದೇವರು ತನ್ನ ಸೃಷ್ಟಿಕಾರ್ಯವನ್ನು ಮುಗಿಸಿಬಿಟ್ಟು ಏಳನೆಯ ದಿನದಲ್ಲಿ ವಿಶ್ರಮಿಸಿಕೊಂಡನು.",
			"3": "<sup>3</sup> ದೇವರು ತನ್ನ ಸೃಷ್ಟಿಕಾರ್ಯವನ್ನು ಮುಗಿಸಿ ಆ ಏಳನೆಯ ದಿನದಲ್ಲಿ ವಿಶ್ರಮಿಸಿಕೊಂಡದ್ದರಿಂದ ಆ ದಿನವನ್ನು ಪರಿಶುದ್ಧ ದಿನವಾಗಿರಲಿ ಎಂದು ಆಶೀರ್ವದಿಸಿದನು.",
			"4": "<sup>4</sup> ಇದೇ ಭೂಮ್ಯಾಕಾಶಗಳ ನಿರ್ಮಾಣ ಚರಿತ್ರೆ.",
			"5": "<sup>5</sup> ಯೆಹೋವದೇವರು ಭೂಮ್ಯಾಕಾಶಗಳನ್ನು ಉಂಟುಮಾಡಿದಾಗ ಯಾವ ಗಿಡವಾದರೂ ಭೂಮಿಯಲ್ಲಿ ಇರಲಿಲ್ಲ, ಯಾವ ಪಲ್ಯವೂ ಹುಟ್ಟಿರಲಿಲ್ಲ. ಯಾಕಂದರೆ ಯೆಹೋವದೇವರು ಭೂಮಿಯ ಮೇಲೆ ಮಳೆಯನ್ನು ಸುರಿಸಿರಲಿಲ್ಲ; ಭೂಮಿಯನ್ನು ವ್ಯವಸಾಯ ಮಾಡುವದಕ್ಕೆ ಮನುಷ್ಯನೂ ಇರಲಿಲ್ಲ.",
			"6": "<sup>6</sup> ಆದರೂ ಭೂಮಿಯಿಂದ ಮಂಜು ಹಬ್ಬಿ ನೆಲವನ್ನೆಲ್ಲಾ ತೋಯಿಸುತ್ತಿತ್ತು.",
			"7": "<sup>7</sup> ಹೀಗಿರಲು ಯೆಹೋವದೇವರು ನೆಲದ ಮಣ್ಣಿನಿಂದ ಮನುಷ್ಯನನ್ನು ರೂಪಿಸಿ ಅವನ ಮೂಗಿನಲ್ಲಿ ಜೀವಶ್ವಾಸವನ್ನು ಊದಿದನು; ಆಗ ಮನುಷ್ಯನು ಬದುಕುವ ಪ್ರಾಣಿಯಾದನು.",
			"8": "<sup>8</sup> ಇದಲ್ಲದೆ ಯೆಹೋವದೇವರು ಮೂಡಣ ದಿಕ್ಕಿನಲ್ಲಿರುವ ಏದೆನ್ ಸೀಮೆಯಲ್ಲಿ ಒಂದು ಉದ್ಯಾನವನವನ್ನು ಮಾಡಿ ತಾನು ರೂಪಿಸಿದ ಮನುಷ್ಯನನ್ನು ಅದರಲ್ಲಿ ಇರಿಸಿದನು.",
			"9": "<sup>9</sup> ಮತ್ತು ಯೆಹೋವದೇವರು ನೋಟಕ್ಕೆ ರಮ್ಯವಾಗಿಯೂ ಊಟಕ್ಕೆ ಅನುಕೂಲವಾಗಿಯೂ ಇರುವ ಎಲ್ಲಾ ತರದ ಮರಗಳನ್ನು ಆ ಭೂಮಿಯಲ್ಲಿ ಬೆಳೆಯ ಮಾಡಿದನು. ಅದಲ್ಲದೆ ಆ ವನಮಧ್ಯದಲ್ಲಿ ಜೀವದಾಯಕ ವೃಕ್ಷವನ್ನೂ ಒಳ್ಳೇದರ ಕೆಟ್ಟದ್ದರ ಅರುಹನ್ನು ಹುಟ್ಟಿಸುವ ವೃಕ್ಷವನ್ನೂ ಬೆಳೆಯಿಸಿದನು.",
			"10": "<sup>10</sup> ಏದೆನ್ ಸೀಮೆಯಲ್ಲಿ ಒಂದು ನದಿ ಹುಟ್ಟಿ ಆ ವನವನ್ನು ತೋಯಿಸುತ್ತಿತ್ತು. ಅದು ಆಚೇ ಕಡೆಯಲ್ಲಿ ಒಡೆದು ನಾಲ್ಕು ಶಾಖೆಗಳಾಯಿತು.",
			"11": "<sup>11</sup> ಮೊದಲನೆಯದರ ಹೆಸರು ಪೀಶೋನ್; ಅದು ಬಂಗಾರ ದೊರಕುವ ಹವೀಲ ದೇಶವನ್ನೆಲ್ಲಾ ಸುತ್ತುವದು.",
			"12": "<sup>12</sup> ಆ ದೇಶದ ಬಂಗಾರವು ಶ್ರೇಷ್ಠವಾದದ್ದು; ಅಲ್ಲಿ ಬದೋಲಖ ಧೂಪವೂ ಗೋಮೇಧಿಕ ರತ್ನವೂ ಸಿಕ್ಕುತ್ತವೆ.",
			"13": "<sup>13</sup> ಎರಡನೆಯ ನದಿಯ ಹೆಸರು ಗೀಹೋನ್; ಅದು ಕೂಷ್ ದೇಶವನ್ನೆಲ್ಲಾ ಸುತ್ತುವದು.",
			"14": "<sup>14</sup> ಮೂರನೆಯ ನದಿಯ ಹೆಸರು ಹಿದ್ದೆಕೆಲ್; ಅದು ಅಶ್ಶೂರ್ ದೇಶದ ಮುಂದೆ ಹರಿಯುವದು. ನಾಲ್ಕನೆಯದು ಯೂಫ್ರೇಟೀಸ್ ನದಿ.",
			"15": "<sup>15</sup> ಯೆಹೋವದೇವರು ಆ ಮನುಷ್ಯನನ್ನು ಕರಕೊಂಡು ಹೋಗಿ ಏದೆನ್ ತೋಟವನ್ನು ವ್ಯವಸಾಯ ಮಾಡುವದಕ್ಕೂ ಕಾಯುವದಕ್ಕೂ ಅದರಲ್ಲಿ ಇಟ್ಟನು.",
			"16": "<sup>16</sup> ಇದಲ್ಲದೆ ಯೆಹೋವದೇವರು ಆ ಮನುಷ್ಯನಿಗೆ - ನೀನು ತೋಟದಲ್ಲಿರುವ ಎಲ್ಲಾ ಮರಗಳ ಹಣ್ಣುಗಳನ್ನು ಯಥೇಚ್ಫವಾಗಿ ತಿನ್ನಬಹುದು;",
			"17": "<sup>17</sup> ಒಳ್ಳೇದರ ಕೆಟ್ಟದ್ದರ ಅರುಹನ್ನು ಹುಟ್ಟಿಸುವ ಮರದ ಹಣ್ಣನ್ನು ಮಾತ್ರ ತಿನ್ನಬಾರದು; ತಿಂದ ದಿನ ಸತ್ತೇಹೋಗುವಿ ಎಂದು ವಿಧಿಸಿದನು.",
			"18": "<sup>18</sup> ಮತ್ತು ಯೆಹೋವದೇವರು - ಮನುಷ್ಯನು ಒಂಟಿಗನಾಗಿರುವದು ಒಳ್ಳೇದಲ್ಲ; ಅವನಿಗೆ ಸರಿಬೀಳುವ ಸಹಕಾರಿಯನ್ನು ಉಂಟುಮಾಡುವೆನು ಅಂದನು.",
			"19": "<sup>19</sup> ಆಗ ಆತನು ಎಲ್ಲಾ ಭೂಜಂತುಗಳನ್ನೂ ಆಕಾಶದ ಪಕ್ಷಿಗಳನ್ನೂ ಮಣ್ಣಿನಿಂದ ನಿರ್ಮಿಸಿ - ಇವುಗಳಿಗೆ ಆ ಮನುಷ್ಯನು ಏನೇನು ಹೆಸರಿಡುವನು ನೋಡೋಣ ಎಂದು ಅವನ ಬಳಿಗೆ ಬರಮಾಡಿದನು. ಮನುಷ್ಯನು ಒಂದೊಂದು ಪ್ರಾಣಿಗೆ ಯಾವ ಯಾವ ಹೆಸರಿಟ್ಟನೋ ಅದೇ ಹೆಸರಾಯಿತು.",
			"20": "<sup>20</sup> ಹೀಗೆ ಆ ಮನುಷ್ಯನು ಎಲ್ಲಾ ಪಶುಗಳಿಗೂ ಆಕಾಶದ ಪಕ್ಷಿಗಳಿಗೂ ಕಾಡು ಮೃಗಗಳಿಗೂ ಹೆಸರಿಟ್ಟನು. ಆದರೆ ಆ ಮನುಷ್ಯನಿಗೆ ಸರಿಬೀಳುವ ಸಹಕಾರಿ ಕಾಣಿಸಲಿಲ್ಲ.",
			"21": "<sup>21</sup> ಹೀಗಿರುವಲ್ಲಿ ಯೆಹೋವದೇವರು ಆ ಮನುಷ್ಯನಿಗೆ ಗಾಢನಿದ್ರೆಯನ್ನು ಬರಮಾಡಿ ಅವನ ಪಕ್ಕೆಯ ಎಲುಬುಗಳಲ್ಲಿ ಒಂದನ್ನು ತೆಗೆದುಕೊಂಡು ಅದರ ಸ್ಥಳವನ್ನು ಮಾಂಸದಿಂದ ಮುಚ್ಚಿ",
			"22": "<sup>22</sup> ತಾನು ಮನುಷ್ಯನಿಂದ ತೆಗೆದಿದ್ದ ಎಲುಬನ್ನು ಸ್ತ್ರೀಯಾಗ ಮಾಡಿ ಆಕೆಯನ್ನು ಅವನ ಬಳಿಗೆ ಕರತಂದನು.",
			"23": "<sup>23</sup> ಅವನು ಆಕೆಯನ್ನು ನೋಡಿ- ಈಗ ಸರಿ; ಈಕೆಯು ನನ್ನ ಎಲುಬುಗಳಿಂದ ಬಂದ ಎಲುಬೂ ನನ್ನ ಮಾಂಸದಿಂದ ಬಂದ ಮಾಂಸವೂ ಆಗಿದ್ದಾಳೆ; ಈಕೆಯು ನರನಿಂದ ಉತ್ಪತ್ತಿಯಾದ ಕಾರಣ ನಾರೀ ಎನ್ನಿಸಿಕೊಳ್ಳುವಳು ಅಂದನು.",
			"24": "<sup>24</sup> ಈ ಕಾರಣದಿಂದ ಪುರುಷನು ತಂದೆತಾಯಿಗಳನ್ನು ಬಿಟ್ಟು ತನ್ನ ಹೆಂಡತಿಯನ್ನು ಸೇರಿಕೊಳ್ಳುವನು; ಅವರಿಬ್ಬರು ಒಂದೇ ಶರೀರವಾಗಿರುವರು.",
			"25": "<sup>25</sup> ಆ ಸ್ತ್ರೀಪುರುಷರಿಬ್ಬರು ಬೆತ್ತಲೆಯಾಗಿದ್ದರೂ ನಾಚಿಕೊಳ್ಳಲಿಲ್ಲ."
		},
		"3": {
			"1": "<sup>1</sup> ಯೆಹೋವದೇವರು ಉಂಟುಮಾಡಿದ ಎಲ್ಲಾ ಭೂಜಂತುಗಳಲ್ಲಿ ಸರ್ಪವು ಯುಕ್ತಿಯುಳ್ಳದ್ದಾಗಿತ್ತು. ಅದು ಸ್ತ್ರೀಯ ಬಳಿಗೆ ಬಂದು - ಏನವ್ವಾ, ತೋಟದಲ್ಲಿರುವ ಯಾವ ಮರದ ಹಣ್ಣನ್ನೂ ನೀವು ತಿನ್ನಬಾರದೆಂದು ದೇವರು ಅಪ್ಪಣೆಕೊಟ್ಟಿರುವದು ನಿಜವೋ ಎಂದು ಕೇಳಲು ಸ್ತ್ರೀಯು -",
			"2": "<sup>2</sup> ತೋಟದಲ್ಲಿರುವ ಮರಗಳ ಹಣ್ಣುಗಳನ್ನು ನಾವು ತಿನ್ನಬಹುದು;",
			"3": "<sup>3</sup> ಆದರೆ ತೋಟದ ಮಧ್ಯದಲ್ಲಿರುವ ಈ ಮರದ ಫಲದ ವಿಷಯವಾಗಿ - ಇದನ್ನು ತಿನ್ನಲೂ ಕೂಡದು, ಮುಟ್ಟಲೂ ಕೂಡದು; ತಿಂದರೆ ಸಾಯುವಿರಿ ಎಂದು ದೇವರು ಹೇಳಿದ್ದಾನೆ ಅಂದಳು.",
			"4": "<sup>4</sup> ಆಗ ಸರ್ಪವು ಸ್ತ್ರೀಗೆ - ನೀವು ಹೇಗೂ ಸಾಯುವದಿಲ್ಲ;",
			"5": "<sup>5</sup> ನೀವು ಇದರ ಹಣ್ಣನ್ನು ತಿಂದಾಗಲೇ ನಿಮ್ಮ ಕಣ್ಣುಗಳು ತೆರೆಯುವವು; ನೀವು ದೇವರಂತೆ ಆಗಿ ಒಳ್ಳೇದರ ಕೆಟ್ಟದ್ದರ ಭೇದವನ್ನು ಅರಿತವರಾಗುವಿರಿ; ಇದು ದೇವರಿಗೆ ಚೆನ್ನಾಗಿ ಗೊತ್ತುಂಟು ಅಂದಿತು.",
			"6": "<sup>6</sup> ಆಗ ಸ್ತ್ರೀಯು - ಆ ಮರದ ಹಣ್ಣು ತಿನ್ನುವದಕ್ಕೆ ಉತ್ತಮವಾಗಿಯೂ ನೋಡುವದಕ್ಕೆ ರಮ್ಯವಾಗಿಯೂ ಜ್ಞಾನೋದಯಕ್ಕೆ ಅಪೇಕ್ಷಿಸತಕ್ಕದ್ದಾಗಿಯೂ ಇದೆ ಎಂದು ತಿಳಿದು ಅದನ್ನು ತೆಗೆದುಕೊಂಡು ತಿಂದಳು. ಸಂಗಡ ಇದ್ದ ಗಂಡನಿಗೂ ಕೊಡಲು ಅವನೂ ತಿಂದನು.",
			"7": "<sup>7</sup> ಕೂಡಲೆ ಅವರಿಬ್ಬರ ಕಣ್ಣುಗಳು ತೆರೆದವು; ತಾವು ಬೆತ್ತಲೆಯಾಗಿದ್ದೇವೆಂದು ತಿಳಿದು ಅಂಜೂರದ ಎಲೆಗಳನ್ನು ಹೊಲಿದು ಉಟ್ಟುಕೊಂಡರು.",
			"8": "<sup>8</sup> ತರುವಾಯ ಯೆಹೋವದೇವರು ಸಂಜೆಯ ತಂಗಾಳಿಯಲ್ಲಿ ತೋಟದೊಳಗೆ ಸಂಚರಿಸುತ್ತಿರುವಾಗ ಆ ಸ್ತ್ರೀಪುರುಷರು ಆತನ ಸಪ್ಪಳವನ್ನು ಕೇಳಿ ಆತನಿಗೆ ಕಾಣಿಸಬಾರದೆಂದು ತೋಟದ ಮರಗಳ ಹಿಂದೆ ಅಡಗಿಕೊಂಡರು.",
			"9": "<sup>9</sup> ಯೆಹೋವದೇವರು ಮನುಷ್ಯನನ್ನು -",
			"10": "<sup>10</sup> ನೀನು ಎಲ್ಲಿರುತ್ತಿ ಎಂದು ಕೂಗಿ ಕೇಳಲು ಅವನು - ನೀನು ತೋಟದಲ್ಲಿ ಸಂಚರಿಸುವ ಸಪ್ಪಳವನ್ನು ನಾನು ಕೇಳಿ ಬೆತ್ತಲೆಯಾಗಿದ್ದೇನಲ್ಲಾ ಎಂದು ಹೆದರಿ ಅಡಗಿಕೊಂಡೆನು ಅಂದನು.",
			"11": "<sup>11</sup> ಅದಕ್ಕಾತನು - ನೀನು ಬೆತ್ತಲೆಯಾಗಿದ್ದಿ ಎಂದು ನಿನಗೆ ತಿಳಿಸಿದವರಾರು? ತಿನ್ನಬಾರದೆಂದು ನಾನು ನಿನಗೆ ಆಜ್ಞಾಪಿಸಿದ ಮರದ ಹಣ್ಣನ್ನು ತಿಂದಿಯಾ ಎಂದು ಕೇಳಲು",
			"12": "<sup>12</sup> ಆ ಮನುಷ್ಯನು - ನನ್ನ ಜೊತೆಯಲ್ಲಿರುವದಕ್ಕೆ ನೀನು ಕೊಟ್ಟ ಸ್ತ್ರೀಯು ಆ ಮರದ ಹಣ್ಣನ್ನು ನನಗೆ ಕೊಟ್ಟಳು; ನಾನು ತಿಂದೆನು ಎಂದು ಹೇಳಿದನು.",
			"13": "<sup>13</sup> ಯೆಹೋವದೇವರು ಸ್ತ್ರೀಯನ್ನು - ಇದೇನು ನೀನು ಮಾಡಿದ್ದು ಎಂದು ಕೇಳಲು ಸ್ತ್ರೀಯು - ಸರ್ಪವು ನನ್ನನ್ನು ವಂಚಿಸಿತು, ನಾನು ತಿಂದೆನು ಎಂದು ಉತ್ತರ ಕೊಟ್ಟಳು.",
			"14": "<sup>14</sup> ಆಗ ಯೆಹೋವದೇವರು ಸರ್ಪಕ್ಕೆ - ನೀನು ಈ ಕಾರ್ಯವನ್ನು ಮಾಡಿದ್ದರಿಂದ ಎಲ್ಲಾ ಪಶುಗಳಲ್ಲಿಯೂ ಅಡವಿಯ ಎಲ್ಲಾ ಮೃಗಗಳಲ್ಲಿಯೂ ಶಾಪಗ್ರಸ್ತವಾದಿ; ನೀನು ಹೊಟ್ಟೆಯಿಂದ ಹರಿದು ನಿನ್ನ ಜೀವಮಾನದ ದಿನಗಳಲ್ಲೆಲ್ಲಾ ಮಣ್ಣೇ ತಿನ್ನುವಿ.",
			"15": "<sup>15</sup> ನಿನಗೂ ಈ ಸ್ತ್ರೀಗೂ, ನಿನ್ನ ಸಂತಾನಕ್ಕೂ ಈ ಸ್ತ್ರೀಯ ಸಂತಾನಕ್ಕೂ ಹಗೆತನವಿರುವ ಹಾಗೆ ಮಾಡುವೆನು. ಈಕೆಯ ಸಂತಾನವು ನಿನ್ನ ತಲೆಯನ್ನು ಜಜ್ಜುವದು, ನೀನು ಅದರ ಹಿಮ್ಮಡಿಯನ್ನು ಕಚ್ಚುವಿ. ಎಂದು ಹೇಳಿದನು.",
			"16": "<sup>16</sup> ಆಮೇಲೆ ಸ್ತ್ರೀಗೆ - ನೀನು ಗರ್ಭಿಣಿಯಾಗಿರುವ ಕಾಲದಲ್ಲಿ ಬಹು ಸಂಕಟಪಡಬೇಕೆಂದು ನಾನು ನೇಮಿಸಿದ್ದೇನೆ; ಕಷ್ಟದಿಂದಲೇ ಮಕ್ಕಳನ್ನು ಹೆರುವಿ; ಗಂಡನ ಮೇಲೆ ನಿನಗೆ ಆಶೆಯಿರುವದು; ಅವನು ನಿನಗೆ ಒಡೆಯನಾಗುವನು. ಎಂದು ಹೇಳಿದನು.",
			"17": "<sup>17</sup> ಮತ್ತು ಪುರುಷನಿಗೆ - ತಿನ್ನಬಾರದೆಂದು ನಾನು ನಿನಗೆ ಆಜ್ಞಾಪಿಸಿದ ಮರದ ಫಲವನ್ನು ನೀನು ಹೆಂಡತಿಯ ಮಾತು ಕೇಳಿ ತಿಂದಕಾರಣ ನಿನ್ನ ನಿಮಿತ್ತ ಭೂಮಿಗೆ ಶಾಪ ಬಂತು. ನಿನ್ನ ಜೀವಮಾನವೆಲ್ಲಾ ದುಡಿದು ದುಡಿದು ಭೂಮಿಯ ಹುಟ್ಟುವಳಿಯನ್ನು ತಿನ್ನಬೇಕು.",
			"18": "<sup>18</sup> ಆ ಭೂಮಿಯಲ್ಲಿ ಮುಳ್ಳುಗಿಡಗಳೂ ಕಳೆಗಳೂ ಬಹಳವಾಗಿ ಹುಟ್ಟುವವು. ಹೊಲದ ಬೆಳೆಯನ್ನು ಅನುಭವಿಸುವಿ.",
			"19": "<sup>19</sup> ನೀನು ತಿರಿಗಿ ಮಣ್ಣಿಗೆ ಸೇರುವತನಕ ಬೆವರಿಡುತ್ತಾ ಬೇಕಾದ ಆಹಾರವನ್ನು ಸಂಪಾದಿಸಬೇಕು. ನೀನು ಮಣ್ಣಿನಿಂದ ತೆಗೆಯಲ್ಪಟ್ಟವನಲ್ಲವೋ; ನೀನು ಮಣ್ಣೇ; ಪುನಃ ಮಣ್ಣಿಗೆ ಸೇರತಕ್ಕವನಾಗಿದ್ದೀ ಎಂದು ಹೇಳಿದನು.",
			"20": "<sup>20</sup> ಆ ಮನುಷ್ಯನು ತನ್ನ ಹೆಂಡತಿಗೆ ಹವ್ವ ಎಂದು ಹೆಸರಿಟ್ಟನು; ಯಾಕಂದರೆ ಬದುಕುವವರೆಲ್ಲರಿಗೂ ಅವಳೇ ಮೂಲಮಾತೆ.",
			"21": "<sup>21</sup> ಯೆಹೋವದೇವರು ಆದಾಮನಿಗೂ ಅವನ ಹೆಂಡತಿಗೂ ಚರ್ಮದ ಅಂಗಿಗಳನ್ನು ಮಾಡಿ ತೊಡಿಸಿದನು.",
			"22": "<sup>22</sup> ಮತ್ತು ಯೆಹೋವದೇವರು - ಈ ಮನುಷ್ಯನು ಒಳ್ಳೇದರ ಕೆಟ್ಟದ್ದರ ಭೇದವನ್ನರಿತು ನಮ್ಮಲ್ಲಿ ಒಬ್ಬನಂತಾದನಲ್ಲಾ. ಇದರಿಂದ ಇವನು ಕೈಚಾಚಿ ಜೀವವೃಕ್ಷದ ಫಲವನ್ನು ಸಹ ತೆಗೆದು ತಿಂದು ಶಾಶ್ವತವಾಗಿ ಬದುಕುವವನಾಗಬಾರದು ಅಂದುಕೊಂಡು",
			"23": "<sup>23</sup> ಅವನು ಉತ್ಪತ್ತಿಯಾದ ಭೂಮಿಯನ್ನೇ ವ್ಯವಸಾಯ ಮಾಡುವದಕ್ಕಾಗಿ ಅವನನ್ನು ಏದೆನ್ ತೋಟದಿಂದ ಹೊರಡಿಸಿಬಿಟ್ಟನು.",
			"24": "<sup>24</sup> ಅದಲ್ಲದೆ ಜೀವವೃಕ್ಷಕ್ಕೆ ಹೋಗುವ ದಾರಿಯನ್ನು ಕಾಯುವದಕ್ಕೆ ಆತನು ಏದೆನ್ ವನದ ಪೂರ್ವದಿಕ್ಕಿನಲ್ಲಿ ಕೆರೂಬಿಯರನ್ನೂ ಧಗಧಗನೆ ಪ್ರಜ್ವಲಿಸುತ್ತಾ ಸುತ್ತುವ ಕತ್ತಿಯನ್ನೂ ಇರಿಸಿದನು."
		},
		"4": {
			"1": "<sup>1</sup> ಆ ಪುರುಷನು ತನ್ನ ಹೆಂಡತಿಯಾದ ಹವ್ವಳನ್ನು ಕೂಡಲು ಆಕೆ ಗರ್ಭಿಣಿಯಾಗಿ ಕಾಯಿನನನ್ನು ಹೆತ್ತು - ಯೆಹೋವನ ಅನುಗ್ರಹದಿಂದ ಗಂಡುಮಗುವನ್ನು ಪಡೆದಿದ್ದೇನೆ ಎಂದು ಹೇಳಿದಳು.",
			"2": "<sup>2</sup> ತರುವಾಯ ಅವನ ತಮ್ಮನಾದ ಹೇಬೆಲನನ್ನು ಹೆತ್ತಳು. ಹೇಬೆಲನು ಕುರಿಕಾಯುವವನಾದನು; ಕಾಯಿನನು ವ್ಯವಸಾಯಮಾಡುವವನಾದನು.",
			"3": "<sup>3</sup> ಕಾಲಾಂತರದಲ್ಲಿ ಕಾಯಿನನು ಹೊಲದ ಬೆಳೆಯಲ್ಲಿ ಕೆಲವನ್ನು ತಂದು ಯೆಹೋವನಿಗೆ ಕಾಣಿಕೆಯಾಗಿ ಸಮರ್ಪಿಸಿದನು.",
			"4": "<sup>4</sup> ಹೇಬೆಲನು ಹಾಗೆಯೇ ತನ್ನ ಹಿಂಡಿನಿಂದ ಚೊಚ್ಚಲ ಕುರಿಗಳನ್ನು ಕಾಣಿಕೆಯಾಗಿ ತಂದು ಅವುಗಳ ಕೊಬ್ಬನ್ನು ಹೋಮಮಾಡಿದನು.",
			"5": "<sup>5</sup> ಯೆಹೋವನು ಹೇಬೆಲನನ್ನೂ ಅವನ ಕಾಣಿಕೆಯನ್ನೂ ಮೆಚ್ಚಿ ಕಾಯಿನನನ್ನೂ ಅವನ ಕಾಣಿಕೆಯನ್ನೂ ಮೆಚ್ಚಲಿಲ್ಲ. ಇದರಿಂದ ಕಾಯಿನನು ಬಹು ಕೋಪಗೊಂಡನು; ಅವನ ಮುಖವು ಕಳೆಗುಂದಿತು.",
			"6": "<sup>6</sup> ಆಗ ಯೆಹೋವನು ಅವನಿಗೆ - ಯಾಕೆ ಕೋಪಿಸಿಕೊಂಡಿ? ನಿನ್ನ ತಲೆ ಯಾಕೆ ಬೊಗ್ಗಿತು?",
			"7": "<sup>7</sup> ಒಳ್ಳೇ ಕೆಲಸ ಮಾಡಿದರೆ ನಿನ್ನ ತಲೆ ಎತ್ತಲ್ಪಡುವದಲ್ಲವೇ; ಅಲ್ಲದಿದ್ದರೆ ಪಾಪವು ನಿನ್ನನ್ನು ಹಿಡಿಯಬೇಕೆಂದು ಬಾಗಿಲಲ್ಲಿ ಹೊಂಚಿಕೊಂಡಿರುವದು; ಆದರೂ ನೀನು ಅದನ್ನು ವಶಮಾಡಿಕೊಳ್ಳಬೇಕು ಎಂದು ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ತರುವಾಯ ಕಾಯಿನನು ತನ್ನ ತಮ್ಮನಾದ ಹೇಬೆಲನಿಗೆ - ಅಡವಿಗೆ ಹೋಗೋಣ ಬಾ ಎಂದನು. ಅಡವಿಗೆ ಬಂದಾಗ ಕಾಯಿನನು ತನ್ನ ತಮ್ಮನ ಮೇಲೆ ಬಿದ್ದು ಅವನನ್ನು ಕೊಂದನು.",
			"9": "<sup>9</sup> ಯೆಹೋವನು ಕಾಯಿನನನ್ನು - ನಿನ್ನ ತಮ್ಮನಾದ ಹೇಬೆಲನು ಎಲ್ಲಿ ಎಂದು ಕೇಳಲು ಅವನು - ನಾನರಿಯೆ; ನನ್ನ ತಮ್ಮನನ್ನು ಕಾಯುವವನು ನಾನೋ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"10": "<sup>10</sup> ಅದಕ್ಕೆ ಯೆಹೋವನು - ನೀನು ಏನು ಮಾಡಿದಿ? ನಿನ್ನ ತಮ್ಮನ ರಕ್ತವು ಭೂಮಿಯ ಕಡೆಯಿಂದ ನನ್ನನ್ನು ಕೂಗುತ್ತದೆ, ಕೇಳು.",
			"11": "<sup>11</sup> ನೀನು ಸುರಿಸಿದ ನಿನ್ನ ತಮ್ಮನ ರಕ್ತವನ್ನು ಕುಡಿದ ಈ ಭೂಮಿಯನ್ನು ಬಿಟ್ಟುಹೋಗಬೇಕೆಂದು ನಿನಗೆ ಶಾಪ ಬಂತು.",
			"12": "<sup>12</sup> ನೀನು ಭೂಮಿಯನ್ನು ವ್ಯವಸಾಯ ಮಾಡಿದರೂ ಇದು ಇನ್ನು ಮುಂದೆ ನಿನಗೆ ಫಲಿಸುವದಿಲ್ಲ. ನೀನು ಭೂಲೋಕದಲ್ಲಿ ಅಲೆಯುವವನೂ ದೇಶಭ್ರಷ್ಟನೂ ಆಗಿರುವಿ ಅಂದನು.",
			"13": "<sup>13</sup> ಕಾಯಿನನು ಯೆಹೋವನಿಗೆ - ನನ್ನ ಅಪರಾಧಫಲ ತಾಳಕೂಡದಷ್ಟಾಗಿದೆ.",
			"14": "<sup>14</sup> ನೀನು ಈ ಹೊತ್ತು ನನ್ನನ್ನು ಸ್ವದೇಶದಿಂದ ಹೊರಡಿಸುತ್ತೀಯಲ್ಲಾ; ನಿನ್ನ ಸಾನ್ನಿಧ್ಯವು ತಪ್ಪಿತು; ನಾನು ಲೋಕದಲ್ಲಿ ಅಲೆಯುವವನೂ ದೇಶಭ್ರಷ್ಟನೂ ಆಗಬೇಕಾಯಿತು. ಇದಲ್ಲದೆ ನನ್ನನ್ನು ಕಂಡವರು ಕೊಲ್ಲುವರು ಎಂದು ಹೇಳಿದನು.",
			"15": "<sup>15</sup> ಅದಕ್ಕೆ ಯೆಹೋವನು - ಕಾಯಿನನ ಪ್ರಾಣವನ್ನು ತೆಗೆದವನಿಗೆ ಏಳರಷ್ಟು ಪ್ರತಿದಂಡನೆಯಾಗುವದು ಎಂದು ಹೇಳಿ ಅವನನ್ನು ಕಂಡವರು ಕೊಲ್ಲಕೂಡದಂತೆ ಅವನ ಮೇಲೆ ಒಂದು ಗುರುತಿಟ್ಟನು.",
			"16": "<sup>16</sup> ಕಾಯಿನನು ಯೆಹೋವನ ಸನ್ನಿಧಾನದಿಂದ ಹೊರಟು ನೋದು ಎಂಬ ದೇಶದಲ್ಲಿ ವಾಸಿಸಿದನು; ಅದು ಏದೆನ್ ಸೀಮೆಗೆ ಮೂಡಲಲ್ಲಿರುವದು.",
			"17": "<sup>17</sup> ಕಾಯಿನನು ತನ್ನ ಹೆಂಡತಿಯನ್ನು ಕೂಡಲು ಅವಳು ಗರ್ಭಿಣಿಯಾಗಿ ಹನೋಕನನ್ನು ಹೆತ್ತಳು. ಇದಲ್ಲದೆ ಕಾಯಿನನು ಒಂದು ಊರನ್ನು ಕಟ್ಟಿ ಅದಕ್ಕೆ ಹನೋಕ ಎಂದು ತನ್ನ ಮಗನ ಹೆಸರಿಟ್ಟನು. ಹನೋಕನಿಂದ ಈರಾದನು ಹುಟ್ಟಿದನು.",
			"18": "<sup>18</sup> ಈರಾದನು ಮೆಹೂಯಾಯೇಲನನ್ನು ಪಡೆದನು. ಮೆಹೂಯಾಯೇಲನು ಮೆತೂಷಾಯೇಲನನ್ನು ಪಡೆದನು. ಮೆತೂಷಾಯೇಲನು ಲೆಮೆಕನನ್ನು ಪಡೆದನು.",
			"19": "<sup>19</sup> ಲೆಮೆಕನು ಆದಾ ಚಿಲ್ಲಾ ಎಂಬ ಇಬ್ಬರು ಹೆಂಡರನ್ನು ಮಾಡಿಕೊಂಡನು.",
			"20": "<sup>20</sup> ಆದಳು ಯಾಬಾಲನನ್ನು ಹೆತ್ತಳು; ದನಕರುಗಳನ್ನು ಸಾಕುವವರಾಗಿದ್ದು ಗುಡಾರಗಳಲ್ಲಿ ವಾಸಿಸುವವರೆಲ್ಲರ ಮೂಲಪುರುಷನು ಇವನೇ.",
			"21": "<sup>21</sup> ಅವನ ತಮ್ಮನ ಹೆಸರು ಯೂಬಾಲನು; ಇವನು ಕಿನ್ನರಿಕೊಳಲುಗಳನ್ನು ನುಡಿಸುವವರ ಮೂಲ ಪುರುಷನು.",
			"22": "<sup>22</sup> ಚಿಲ್ಲಾ ಎಂಬವಳು ತೂಬಲ್‍ಕಾಯಿನನನ್ನು ಹೆತ್ತಳು; ಅವನು ಕುಲಿಮೆ ಮಾಡಿ ಕಬ್ಬಿಣ ತಾಮ್ರದ ಆಯುಧಗಳನ್ನು ಕಲ್ಪಿಸಿದವನು. ತೂಬಲ್‍ಕಾಯಿನನ ತಂಗಿ ನಯಮಾ.",
			"23": "<sup>23</sup> ಲೆಮೆಕನು ತನ್ನ ಹೆಂಡತಿಯರಿಗೆ ಹೀಗೆಂದನು - ಆದಾ, ಚಿಲ್ಲಾ, ನನ್ನ ಮಾತಿಗೆ ಕಿವಿಗೊಡಿರಿ; ಲೆಮೆಕನ ಹೆಂಡತಿಯರೇ, ನಾನು ಹೇಳುವದನ್ನು ಕೇಳಿರಿ. ನನಗೆ ಗಾಯಮಾಡಿದ ಒಬ್ಬ ಮನುಷ್ಯನನ್ನು ಕೊಂದೆನು; ನನ್ನನ್ನು ಹೊಡೆದ ಒಬ್ಬ ಪ್ರಾಯಸ್ಥನನ್ನು ಹತಮಾಡಿದೆನು.",
			"24": "<sup>24</sup> ಕಾಯಿನನನ್ನು ಕೊಂದವನಿಗೆ ಏಳರಷ್ಟು ಪ್ರತಿದಂಡನೆ ಬರುವದಾದರೆ ಲೆಮೆಕನನ್ನು ಹೊಡೆಯುವವನಿಗೆ ಎಪ್ಪತ್ತೇಳರಷ್ಟಾಗುವದಲ್ಲವೇ.",
			"25": "<sup>25</sup> ಆದಾಮನು ತಿರಿಗಿ ತನ್ನ ಹೆಂಡತಿಯನ್ನು ಕೂಡಲು ಆಕೆ ಗಂಡುಮಗುವನ್ನು ಹೆತ್ತು - ಕಾಯಿನನು ಕೊಂದುಹಾಕಿದ ಹೇಬೆಲನಿಗೆ ಬದಲಾಗಿ ದೇವರು ನನಗೆ ಬೇರೆ ಸಂತಾನವನ್ನು ನೇಮಿಸಿದನೆಂದು ಆ ಮಗುವಿಗೆ ಸೇತನೆಂಬ ಹೆಸರಿಟ್ಟಳು.",
			"26": "<sup>26</sup> ತರುವಾಯ ಸೇತನು ಸಹ ಮಗನನ್ನು ಪಡೆದು ಅವನಿಗೆ ಎನೋಷ್ ಎಂಬ ಹೆಸರಿಟ್ಟನು; ಆ ಕಾಲದಲ್ಲಿ ಯೆಹೋವ ಎಂಬ ಹೆಸರನ್ನು ಹೇಳಿಕೊಂಡು ಆರಾಧಿಸುವದಕ್ಕೆ ಪ್ರಾರಂಭವಾಯಿತು."
		},
		"5": {
			"1": "<sup>1</sup> ಆದಾಮನ ವಂಶದವರ ಚರಿತ್ರೆಯು - ದೇವರು ಸೃಷ್ಟಿಕಾಲದಲ್ಲಿ ಮನುಷ್ಯನನ್ನು ತನ್ನ ಹೋಲಿಕೆಗೆ ಸರಿಯಾಗಿ ಉಂಟುಮಾಡಿದನು.",
			"2": "<sup>2</sup> ಅವರನ್ನು ಗಂಡು ಹೆಣ್ಣಾಗಿ ಉಂಟುಮಾಡಿದನು. ಇದಲ್ಲದೆ ಅದೇ ದಿವಸದಲ್ಲಿ ಅವರನ್ನು ಆಶೀರ್ವದಿಸಿ ಅವರಿಗೆ ಮನುಷ್ಯ ಎಂದು ಹೆಸರಿಟ್ಟನು.",
			"3": "<sup>3</sup> ಆದಾಮನು ನೂರಮೂವತ್ತು ವರುಷದವನಾದಾಗ ರೂಪದಲ್ಲಿ ತನ್ನ ಹೋಲಿಕೆಗೆ ಸರಿಯಾದ ಮಗನನ್ನು ಪಡೆದು ಅವನಿಗೆ ಸೇತನೆಂದು ಹೆಸರಿಟ್ಟನು.",
			"4": "<sup>4</sup> ಸೇತನು ಹುಟ್ಟಿದ ಮೇಲೆ ಆದಾಮನು ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ಪಡೆದು ಎಂಟುನೂರು ವರುಷ ಬದುಕಿದನು.",
			"5": "<sup>5</sup> ಅವನು ಒಟ್ಟು ಒಂಭೈನೂರ ಮೂವತ್ತು ವರುಷ ಬದುಕಿ ಸತ್ತನು.",
			"6": "<sup>6</sup> ಸೇತನು ನೂರ ಐದು ವರುಷದವನಾದಾಗ ಎನೋಷನನ್ನು ಪಡೆದನು.",
			"7": "<sup>7</sup> ಎನೋಷನು ಹುಟ್ಟಿದ ಮೇಲೆ ಸೇತನು ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ಪಡೆದು ಎಂಟುನೂರ ಏಳು ವರುಷ ಬದುಕಿದನು.",
			"8": "<sup>8</sup> ಅವನು ಒಟ್ಟು ಒಂಭೈನೂರ ಹನ್ನೆರಡು ವರುಷ ಬದುಕಿ ಸತ್ತನು.",
			"9": "<sup>9</sup> ಎನೋಷನು ತೊಂಭತ್ತು ವರುಷದವನಾದಾಗ ಕೇನಾನನನ್ನು ಪಡೆದನು.",
			"10": "<sup>10</sup> ಕೇನಾನನು ಹುಟ್ಟಿದ ಮೇಲೆ ಎನೋಷನು ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ಪಡೆದು ಎಂಟುನೂರ ಹದಿನೈದು ವರುಷ ಬದುಕಿದನು.",
			"11": "<sup>11</sup> ಅವನು ಒಟ್ಟು ಒಂಭೈನೂರ ಐದು ವರುಷ ಬದುಕಿ ಸತ್ತನು.",
			"12": "<sup>12</sup> ಕೇನಾನನು ಎಪ್ಪತ್ತು ವರುಷದವನಾದಾಗ ಮಹಲಲೇಲನನ್ನು ಪಡೆದನು.",
			"13": "<sup>13</sup> ಮಹಲಲೇಲನು ಹುಟ್ಟಿದ ಮೇಲೆ ಕೇನಾನನು ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ಪಡೆದು ಎಂಟುನೂರ ನಾಲ್ವತ್ತು ವರುಷ ಬದುಕಿದನು.",
			"14": "<sup>14</sup> ಅವನು ಒಟ್ಟು ಒಂಭೈನೂರ ಹತ್ತು ವರುಷ ಬದುಕಿ ಸತ್ತನು.",
			"15": "<sup>15</sup> ಮಹಲಲೇಲನು ಅರುವತ್ತೈದು ವರುಷದವನಾದಾಗ ಯೆರೆದನನ್ನು ಪಡೆದನು.",
			"16": "<sup>16</sup> ಯೆರೆದನು ಹುಟ್ಟಿದ ಮೇಲೆ ಮಹಲಲೇಲನು ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ಪಡೆದು ಎಂಟುನೂರ ಮೂವತ್ತು ವರುಷ ಬದುಕಿದನು.",
			"17": "<sup>17</sup> ಅವನು ಒಟ್ಟು ಎಂಟುನೂರ ತೊಂಭತ್ತೈದು ವರುಷ ಬದುಕಿ ಸತ್ತನು.",
			"18": "<sup>18</sup> ಯೆರೆದನು ನೂರ ಅರುವತ್ತೆರಡು ವರುಷದವನಾದಾಗ ಹನೋಕನನ್ನು ಪಡೆದನು.",
			"19": "<sup>19</sup> ಹನೋಕನು ಹುಟ್ಟಿದ ಮೇಲೆ ಯೆರೆದನು ಎಂಟುನೂರು ವರುಷ ಬದುಕಿದನು.",
			"20": "<sup>20</sup> ಅವನು ಒಟ್ಟು ಒಂಭೈನೂರ ಅರುವತ್ತೆರಡು ವರುಷ ಬದುಕಿ ಸತ್ತನು.",
			"21": "<sup>21</sup> ಹನೋಕನು ಅರುವತ್ತೈದು ವರುಷದವನಾದಾಗ ಮೆತೂಷೆಲಹನನ್ನು ಪಡೆದನು.",
			"22": "<sup>22</sup> ಮೆತೂಷೆಲಹನು ಹುಟ್ಟಿದ ಮೇಲೆ ಹನೋಕನು ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ಪಡೆದು ದೇವರ ಅನ್ಯೋನ್ಯತೆಯಲ್ಲಿ ಮುನ್ನೂರು ವರುಷ ಬದುಕಿದನು.",
			"23": "<sup>23</sup> ಅವನು ಬದುಕಿದ ಕಾಲವೆಲ್ಲಾ ಮುನ್ನೂರ ಅರುವತ್ತೈದು ವರುಷ.",
			"24": "<sup>24</sup> ಹನೋಕನು ದೇವರೊಂದಿಗೆ ಅನ್ಯೋನ್ಯವಾಗಿ ನಡೆದುಕೊಳ್ಳುತ್ತಾ ಇರುವಾಗ ದೇವರು ಅವನನ್ನು ಕರೆದುಕೊಂಡದ್ದರಿಂದ ಕಾಣದೆ ಹೋದನು.",
			"25": "<sup>25</sup> ಮೆತೂಷೆಲಹನು ನೂರ ಎಂಭತ್ತೇಳು ವರುಷದವನಾದಾಗ ಲೆಮೆಕನನ್ನು ಪಡೆದನು.",
			"26": "<sup>26</sup> ಲೆಮೆಕನು ಹುಟ್ಟಿದ ಮೇಲೆ ಮೆತೂಷೆಲಹನು ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ಪಡೆದು ಏಳುನೂರ ಎಂಭತ್ತೆರಡು ವರುಷ ಬದುಕಿದನು.",
			"27": "<sup>27</sup> ಅವನು ಒಟ್ಟು ಒಂಭೈನೂರ ಅರುವತ್ತೊಂಭತ್ತು ವರುಷ ಬದುಕಿ ಸತ್ತನು.",
			"28": "<sup>28</sup> ಲೆಮೆಕನು ನೂರ ಎಂಭತ್ತೆರಡು ವರುಷದವನಾದಾಗ ಒಬ್ಬ ಮಗನನ್ನು ಪಡೆದು -",
			"29": "<sup>29</sup> ಯೆಹೋವನು ಶಪಿಸಿದ ಭೂಮಿಯಿಂದ ನಮಗೆ ಉಂಟಾದ ಕೈಕಷ್ಟದಲ್ಲಿಯೂ ಶ್ರಮೆಯಲ್ಲಿಯೂ ಈ ಮಗನು ನಮ್ಮನ್ನು ಉಪಶಮನಗೊಳಿಸುವನು ಎಂದು ಹೇಳಿ ಅವನಿಗೆ ನೋಹ ಎಂದು ಹೆಸರಿಟ್ಟನು.",
			"30": "<sup>30</sup> ನೋಹನು ಹುಟ್ಟಿದ ಮೇಲೆ ಲೆಮೆಕನು ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ಪಡೆದು ಐನೂರ ತೊಂಭತ್ತೈದು ವರುಷ ಬದುಕಿದನು.",
			"31": "<sup>31</sup> ಅವನು ಒಟ್ಟು ಏಳುನೂರ ಎಪ್ಪತ್ತೇಳು ವರುಷ ಬದುಕಿ ಸತ್ತನು.",
			"32": "<sup>32</sup> ನೋಹನು ಐನೂರು ವರುಷದವನಾದಾಗ ಶೇಮ್, ಹಾಮ್, ಯೆಫೆತ್ ಎಂಬ ಮೂರು ಮಂದಿ ಮಕ್ಕಳನ್ನು ಪಡೆದನು."
		},
		"6": {
			"1": "<sup>1</sup> ಭೂಮಿಯ ಮೇಲೆ ಜನರು ಹೆಚ್ಚುತ್ತಾ ಅವರಿಗೆ ಹೆಣ್ಣು ಮಕ್ಕಳು ಹುಟ್ಟಲು",
			"2": "<sup>2</sup> ದೇವಪುತ್ರರು ಮನುಷ್ಯಪುತ್ರಿಯರ ಸೌಂದರ್ಯವನ್ನು ನೋಡಿ ತಮಗೆ ಇಷ್ಟರಾದವರನ್ನು ಹೆಂಡರನ್ನಾಗಿ ಮಾಡಿಕೊಂಡರು.",
			"3": "<sup>3</sup> ಆಗ ಯೆಹೋವನು - ನನ್ನ ಆತ್ಮವು ಮನುಷ್ಯರಲ್ಲಿ ಶಾಶ್ವತವಾಗಿರುವದಿಲ್ಲ; ಅವರು ಭ್ರಷ್ಟರಾದದರಿಂದ ಮರ್ತ್ಯರೇ. ಅವರ ಆಯುಷ್ಯವು ನೂರ ಇಪ್ಪತ್ತು ವರುಷವಾಗಿರಲಿ ಅಂದನು.",
			"4": "<sup>4</sup> ಆ ಕಾಲದಲ್ಲಿ ಅಂದರೆ ದೇವಪುತ್ರರು ಮನುಷ್ಯಪುತ್ರಿಯರನ್ನು ಕೂಡಿ ಅವರಲ್ಲಿ ಮಕ್ಕಳನ್ನು ಪಡೆಯುವ ಕಾಲದಲ್ಲಿ ಮಹಾಶರೀರಿಗಳು ಭೂಮಿಯ ಮೇಲಿದ್ದರು; ಅನಂತರದಲ್ಲಿಯೂ ಇದ್ದರು. ಪೂರ್ವದಲ್ಲಿ ಹೆಸರುಗೊಂಡ ಪರಾಕ್ರಮಶಾಲಿಗಳು ಇವರೇ.",
			"5": "<sup>5</sup> ಮನುಷ್ಯರ ಕೆಟ್ಟತನವು ಭೂಮಿಯ ಮೇಲೆ ಹೆಚ್ಚಾಗಿರುವದನ್ನೂ ಅವರು ಹೃದಯದಲ್ಲಿ ಯೋಚಿಸುವದೆಲ್ಲವು ಯಾವಾಗಲೂ ಬರೀ ಕೆಟ್ಟದ್ದಾಗಿರುವದನ್ನೂ",
			"6": "<sup>6</sup> ಯೆಹೋವನು ನೋಡಿ ತಾನು ಭೂಮಿಯ ಮೇಲೆ ಮನುಷ್ಯರನ್ನು ಉಂಟುಮಾಡಿದ್ದಕ್ಕೆ ಪಶ್ಚಾತ್ತಾಪಪಟ್ಟು ತನ್ನ ಹೃದಯದಲ್ಲಿ ನೊಂದುಕೊಂಡನು.",
			"7": "<sup>7</sup> ಮತ್ತು ಯೆಹೋವನು - ನಾನು ಸೃಷ್ಟಿಸಿದ ಮನುಷ್ಯ ಜಾತಿಯನ್ನು ಭೂಮಿಯ ಮೇಲಿನಿಂದ ಅಳಿಸಿಬಿಡುವೆನು; ಅದನ್ನು ಉಂಟುಮಾಡಿದ್ದಕ್ಕೆ ನನ್ನಲ್ಲಿ ಪಶ್ಚಾತ್ತಾಪ ಹುಟ್ಟಿತು; ಮನುಷ್ಯರೊಂದಿಗೆ ಸಕಲಮೃಗ ಕ್ರಿಮಿಪಕ್ಷಿಗಳನ್ನೂ ಅಳಿಸಿ ಬಿಡುವೆನು ಅಂದುಕೊಂಡನು.",
			"8": "<sup>8</sup> ಆದರೆ ನೋಹನಿಗೆ ಯೆಹೋವನ ದಯವು ದೊರಕಿತು.",
			"9": "<sup>9</sup> ನೋಹನ ಚರಿತ್ರೆಯು - ನೋಹನು ನೀತಿವಂತನೂ ತನ್ನ ಕಾಲದವರಲ್ಲಿ ತಪ್ಪಿಲ್ಲದವನೂ ಆಗಿದ್ದನು; ಅವನು ದೇವರೊಂದಿಗೆ ಅನ್ಯೋನ್ಯವಾಗಿ ನಡೆದುಕೊಂಡನು.",
			"10": "<sup>10</sup> ಶೇಮ್, ಹಾಮ್, ಯೆಫೆತ್ ಎಂಬ ಮೂರು ಮಂದಿ ಮಕ್ಕಳನ್ನು ಪಡೆದನು.",
			"11": "<sup>11</sup> ಭೂಲೋಕದವರು ದೇವರ ದೃಷ್ಟಿಗೆ ಕೆಟ್ಟುಹೋಗಿದ್ದರು; ಅನ್ಯಾಯವು ಲೋಕವನ್ನು ತುಂಬಿಕೊಂಡಿತ್ತು.",
			"12": "<sup>12</sup> ದೇವರು ಲೋಕವನ್ನು ನೋಡಿದಾಗ ಅದು ಕೆಟ್ಟುಹೋಗಿತ್ತು; ಭೂನಿವಾಸಿಗಳೆಲ್ಲರೂ ತಮ್ಮ ನಡವಳಿಕೆಯನ್ನು ಕೆಡಿಸಿಕೊಂಡಿದ್ದರು.",
			"13": "<sup>13</sup> ಆಗ ದೇವರು ನೋಹನಿಗೆ - ಎಲ್ಲಾ ದೇಹಿಗಳಿಗೂ ಅಂತ್ಯವನ್ನು ತೀರ್ಮಾನಿಸಿದ್ದೇನೆ; ಭೂಲೋಕವು ಅವರ ಅನ್ಯಾಯದಿಂದ ತುಂಬಿ ಅದೆ; ನಾನು ಅವರನ್ನೂ ಭೂಮಿಯ ಮೇಲಿರುವದೆಲ್ಲವನ್ನೂ ಅಳಿಸಿಬಿಡುತ್ತೇನೆ.",
			"14": "<sup>14</sup> ನೀನು ತುರಾಯಿ ಮರದಿಂದ ನಾವೆಯನ್ನು ಮಾಡಿಕೋ; ಅದರಲ್ಲಿ ತುಂಬ ಕೋಣೆಗಳು ಇರಬೇಕು; ಒಳಕ್ಕೂ ಹೊರಕ್ಕೂ ರಾಳವನ್ನು ಹಚ್ಚು.",
			"15": "<sup>15</sup> ನೀನು ಅದನ್ನು ಮಾಡಬೇಕಾದ ವಿಧಾನ ಹೇಗಂದರೆ - ಅದು ಮುನ್ನೂರು ಮೊಳ ಉದ್ದವೂ ಐವತ್ತು ಮೊಳ ಅಗಲವೂ ಮೂವತ್ತು ಮೊಳ ಎತ್ತರವೂ ಉಳ್ಳದ್ದಾಗಿರಬೇಕು.",
			"16": "<sup>16</sup> ಅದರ ಚಾವಣಿಯ ಕೆಳಗೆ ಸುತ್ತಲೂ ಒಂದು ಮೊಳ ಎತ್ತರದ ಬೆಳಕು ಕಂಡಿಯನ್ನು ಮಾಡಬೇಕು; ಪಕ್ಕದಲ್ಲಿ ಬಾಗಲನ್ನಿಡಬೇಕು. ನಾವೆಯಲ್ಲಿ ಒಂದರ ಮೇಲೆ ಒಂದಾಗಿ ಮೂರು ಅಂತಸ್ತುಗಳನ್ನು ಮಾಡಬೇಕು.",
			"17": "<sup>17</sup> ನಾನಂತೂ ಭೂಮಿಯ ಮೇಲೆ ಜಲಪ್ರಳಯವನ್ನು ಬರಮಾಡಿ ಆಕಾಶದ ಕೆಳಗಿರುವ ಸಕಲ ಪ್ರಾಣಿಗಳನ್ನೂ ಅಳಿಸಿಬಿಡುವೆನು; ಭೂಮಿಯಲ್ಲಿರುವ ಸಮಸ್ತವೂ ಲಯವಾಗುವದು.",
			"18": "<sup>18</sup> ಆದರೆ ನಿನ್ನ ಕೂಡ ನನ್ನ ನಿಬಂಧನೆಯನ್ನು ಮಾಡುತ್ತೇನೆ. ನೀನು ಹೆಂಡತಿ ಮಕ್ಕಳು ಸೊಸೆಯರು ಸಹಿತವಾಗಿ ನಾವೆಯಲ್ಲಿ ಸೇರಬೇಕು.",
			"19": "<sup>19</sup> ಇದಲ್ಲದೆ ಜೀವಿಗಳ ಪ್ರತಿಜಾತಿಯಲ್ಲಿಯೂ ಒಂದು ಗಂಡು ಒಂದು ಹೆಣ್ಣು ಹೀಗೆ ಎರಡೆರಡನ್ನು ನಾವೆಯಲ್ಲಿ ಸೇರಿಸಿ ನಿನ್ನೊಂದಿಗೆ ಉಳಿಸಿ ಕಾಪಾಡಬೇಕು.",
			"20": "<sup>20</sup> ಪಶು, ಪಕ್ಷಿ, ಕ್ರಿಮಿ ಇವುಗಳ ಸಕಲ ಜಾತಿಗಳಲ್ಲಿಯೂ ಎರಡೆರಡು ಬದುಕುವದಕ್ಕಾಗಿ ನಿನ್ನ ಬಳಿಗೆ ಬರುವವು.",
			"21": "<sup>21</sup> ಮತ್ತು ನಿನಗೂ ಅವುಗಳಿಗೂ ಪೋಷಣೆಯಾಗುವಂತೆ ಸಕಲ ವಿಧವಾದ ಆಹಾರ ಪದಾರ್ಥಗಳನ್ನು ನಿನ್ನ ಬಳಿಯಲ್ಲಿ ಕೂಡಿಸಿಡಬೇಕು ಎಂದು ಹೇಳಿದನು.",
			"22": "<sup>22</sup> ದೇವರು ಅಪ್ಪಣೆಕೊಟ್ಟ ಪ್ರಕಾರವೇ ನೋಹನು ಮಾಡಿದನು."
		},
		"7": {
			"1": "<sup>1</sup> ಆಗ ಯೆಹೋವನು ನೋಹನಿಗೆ - ನೀನೂ ನಿನ್ನ ಮನೆಯವರೆಲ್ಲರೂ ನಾವೆಯಲ್ಲಿ ಸೇರಿರಿ; ಈಗಿನವರಲ್ಲಿ ನೀನೊಬ್ಬನೇ ನನ್ನ ಮುಂದೆ ನೀತಿವಂತನಾಗಿ ನಡೆಯುವದನ್ನು ನೋಡಿದ್ದೇನೆ.",
			"2": "<sup>2</sup> ಎಲ್ಲಾ ಶುದ್ಧಪಶುಗಳಲ್ಲಿ ಏಳೇಳು ಗಂಡು ಹೆಣ್ಣುಗಳನ್ನೂ ಶುದ್ಧವಲ್ಲದ ಪಶುಗಳಲ್ಲಿ ಎರಡೆರಡು ಗಂಡು ಹೆಣ್ಣುಗಳನ್ನೂ ನಿನ್ನೊಂದಿಗೆ ತೆಗೆದುಕೋ.",
			"3": "<sup>3</sup> ಪಕ್ಷಿಗಳಲ್ಲಿಯೂ ಏಳೇಳು ಗಂಡುಹೆಣ್ಣುಗಳನ್ನು ತೆಗೆದುಕೋ. ಹೀಗೆ ಆಯಾ ಜಾತಿಯನ್ನು ಭೂಮಿಯ ಮೇಲೆ ಉಳಿಸಿ ಕಾಪಾಡಬೇಕು.",
			"4": "<sup>4</sup> ಏಳುದಿನಗಳ ನಂತರ ನಾನು ಭೂಮಿಯ ಮೇಲೆ ನಾಲ್ವತ್ತು ದಿವಸ ಹಗಲಿರುಳು ಮಳೆಯನ್ನು ಸುರಿಸಿ ನನ್ನಿಂದಾದ ಜೀವರಾಶಿಗಳನ್ನೆಲ್ಲಾ ಭೂಮಿಯ ಮೇಲಿನಿಂದ ಅಳಿಸಿಬಿಡುತ್ತೇನೆ ಎಂದು ಹೇಳಿದನು.",
			"5": "<sup>5</sup> ಯೆಹೋವನು ಆಜ್ಞಾಪಿಸಿದ್ದನ್ನೆಲ್ಲಾ ನೋಹನು ಮಾಡಿದನು.",
			"6": "<sup>6</sup> ಭೂಮಿಯ ಮೇಲೆ ಜಲಪ್ರಳಯವುಂಟಾದಾಗ ನೋಹನು ಆರುನೂರು ವರುಷದವನಾಗಿದ್ದನು.",
			"7": "<sup>7</sup> ಆಗ ನೋಹನು ಪ್ರಳಯವನ್ನು ತಪ್ಪಿಸಿಕೊಳ್ಳುವದಕ್ಕೆ ಹೆಂಡತಿ ಮಕ್ಕಳು ಸೊಸೆಯರು ಸಹಿತವಾಗಿ ನಾವೆಯಲ್ಲಿ ಸೇರಿದನು.",
			"8": "<sup>8</sup> ದೇವರು ನೋಹನಿಗೆ ಅಪ್ಪಣೆಕೊಟ್ಟಂತೆ ಶುದ್ಧಾಶುದ್ಧ ಪಶುಗಳಲ್ಲಿಯೂ ಪಕ್ಷಿಗಳಲ್ಲಿಯೂ ಕ್ರಿಮಿಕೀಟಗಳಲ್ಲಿಯೂ",
			"9": "<sup>9</sup> ಗಂಡುಹೆಣ್ಣುಗಳು ಜೋಡಿಜೋಡಿಯಾಗಿ ಬಂದು ನೋಹನ ಬಳಿಗೆ ನಾವೆಯಲ್ಲಿ ಸೇರಿದವು.",
			"10": "<sup>10</sup> ಆ ಏಳು ದಿವಸಗಳಾದನಂತರ ಜಲಪ್ರಳಯವು ಭೂಮಿಯ ಮೇಲೆ ಬಂತು.",
			"11": "<sup>11</sup> ನೋಹನ ಜೀವಮಾನದ ಆರುನೂರನೆಯ ವರುಷದ ಎರಡನೆಯ ತಿಂಗಳಿನ ಹದಿನೇಳನೆಯ ದಿನದಲ್ಲಿ ಭೂಮಿಯ ಕೆಳಗಿರುವ ಸಾಗರದ ಸೆಲೆಗಳು ಒಡೆದವು; ಆಕಾಶದ ತೂಬುಗಳು ತೆರೆದವು.",
			"12": "<sup>12</sup> ನಾಲ್ವತ್ತು ದಿನವೂ ಹಗಲಿರುಳು ಭೂಮಿಯ ಮೇಲೆ ದೊಡ್ಡ ಮಳೆ ಸುರಿಯಿತು.",
			"13": "<sup>13</sup> ಆ ದಿನದಲ್ಲಿ ನೋಹನೂ ಶೇಮ್, ಹಾಮ್, ಯೆಫೆತರೆಂಬ ಅವನ ಮಕ್ಕಳೂ ಅವನ ಹೆಂಡತಿಯೂ ಮೂರು ಮಂದಿ ಸೊಸೆಯರೂ ನಾವೆಯಲ್ಲಿ ಸೇರಿದರು.",
			"14": "<sup>14</sup> ಎಲ್ಲಾ ಪ್ರಾಣಿಗಳಲ್ಲಿ ಅಂದರೆ ಸಕಲ ವಿಧವಾದ ಮೃಗ ಪಶು ಕ್ರಿಮಿಕೀಟಗಳಲ್ಲಿಯೂ ಪಕ್ಷಿ ಮೊದಲಾದ ರೆಕ್ಕೆಯಿರುವ ಎಲ್ಲಾ ಜೀವಿಗಳಲ್ಲಿಯೂ",
			"15": "<sup>15</sup> ಎರಡೆರಡು ತಮ್ಮ ತಮ್ಮ ಜಾತಿಗನುಸಾರವಾಗಿ ನೋಹನ ಬಳಿಗೆ ಬಂದು ನಾವೆಯಲ್ಲಿ ಸೇರಿದವು.",
			"16": "<sup>16</sup> ದೇವರು ನೋಹನಿಗೆ ಅಪ್ಪಣೆಕೊಟ್ಟಂತೆ ಎಲ್ಲಾ ಪ್ರಾಣಿಗಳಲ್ಲಿ ಒಂದು ಗಂಡು ಒಂದು ಹೆಣ್ಣು ಈ ಮೇರೆಗೆ ಸೇರಿದವು. ಯೆಹೋವನು ನೋಹನನ್ನು ಒಳಗಿಟ್ಟು ಮುಚ್ಚಿದನು.",
			"17": "<sup>17</sup> ಜಲಪ್ರಳಯವು ನಾಲ್ವತ್ತು ದಿನ ಭೂಮಿಯ ಮೇಲೆ ಇದ್ದು ನೀರು ಹೆಚ್ಚುತ್ತಾ ನಾವೆಯನ್ನು ಎತ್ತಲು ಅದು ನೀರಿನ ಮೇಲೆ ತೇಲಾಡಿತು.",
			"18": "<sup>18</sup> ನೀರು ಪ್ರಬಲವಾಗಿ ಬಲು ಹೆಚ್ಚಲು ನಾವೆಯು ಅದರ ಮೇಲೆ ಚಲಿಸಿತು.",
			"19": "<sup>19</sup> ನೀರು ಭೂಮಿಯ ಮೇಲೆ ಅತ್ಯಧಿಕವಾಗಿ ಪ್ರಬಲವಾದ್ದರಿಂದ ಆಕಾಶಮಂಡಲದ ಕೆಳಗಿರುವ ಎಲ್ಲಾ ದೊಡ್ಡ ಬೆಟ್ಟಗಳೂ ಮುಚ್ಚಿಹೋದವು.",
			"20": "<sup>20</sup> ನೀರು ಆ ಬೆಟ್ಟಗಳಿಗಿಂತಲೂ ಹದಿನೈದು ಮೊಳ ಹೆಚ್ಚಲು ಅವುಗಳು ಸಂಪೂರ್ಣವಾಗಿ ಮುಚ್ಚಿಹೋದದರಿಂದ",
			"21": "<sup>21</sup> ಪಶುಪಕ್ಷಿ ಮೃಗಕ್ರಿಮಿಗಳು ಮನುಷ್ಯರು ಸಹಿತವಾಗಿ ಭೂಮಿಯ ಮೇಲೆ ಸಂಚರಿಸುವ ಸಕಲ ಪ್ರಾಣಿಗಳೂ ಲಯವಾದವು.",
			"22": "<sup>22</sup> ಮೂಗಿನಿಂದ ಶ್ವಾಸಬಿಡುವ ಭೂಜಂತುಗಳೆಲ್ಲಾ ಸತ್ತವು.",
			"23": "<sup>23</sup> ಮನುಷ್ಯರು ಮೊದಲುಗೊಂಡು ಪಶುಪಕ್ಷಿಕ್ರಿಮಿಗಳವರೆಗೂ ಭೂಮಿಯ ಮೇಲಿದ್ದದ್ದೆಲ್ಲಾ ನಾಶವಾಯಿತು; ಅವು ಭೂಮಿಯ ಮೇಲಿನಿಂದ ಲಯವಾಗಿ ಹೋದವು. ನೋಹನೂ ಅವನೊಂದಿಗೆ ನಾವೆಯಲ್ಲಿದ್ದ ಜೀವಿಗಳೂ ಮಾತ್ರ ಉಳಿದರು.",
			"24": "<sup>24</sup> ಪ್ರಳಯದ ನೀರು ಭೂಮಿಯ ಮೇಲೆ ನೂರೈವತ್ತು ದಿನ ಪ್ರಬಲವಾಯಿತು."
		},
		"8": {
			"1": "<sup>1</sup> ತರುವಾಯ ದೇವರು ನೋಹನನ್ನೂ ಅವನ ಸಂಗಡ ನಾವೆಯಲ್ಲಿದ್ದ ಎಲ್ಲಾ ಮೃಗಪಶುಗಳನ್ನೂ ನೆನಪಿಗೆ ತಂದುಕೊಂಡು ಭೂಲೋಕದ ಮೇಲೆ ಗಾಳಿ ಬೀಸುವಂತೆ ಮಾಡಲಾಗಿ ನೀರು ತಗ್ಗಿತು.",
			"2": "<sup>2</sup> ಭೂಮಿಯ ಕೆಳಗಿರುವ ಸಾಗರದ ಸೆಲೆಗಳೂ ಆಕಾಶದ ತೂಬುಗಳೂ ಮುಚ್ಚಿಹೋದವು. ಆಕಾಶದಿಂದ ಸುರಿಯುತ್ತಿದ್ದ ಮಳೆ ನಿಂತುಹೋಯಿತು. ಆಗ ಭೂಮಿಯ ಮೇಲಿದ್ದ ನೀರು ಕ್ರಮವಾಗಿ ತಗ್ಗುತ್ತಾ ಬಂತು.",
			"3": "<sup>3</sup> ಹೀಗೆ ನೂರೈವತ್ತು ದಿನಗಳಾದ ಮೇಲೆ ನೀರು ಕಡಿಮೆಯಾಯಿತು.",
			"4": "<sup>4</sup> ಏಳನೆಯ ತಿಂಗಳಿನ ಹದಿನೇಳನೆಯ ದಿನದಲ್ಲಿ ನಾವೆಯು ಅರಾರಾಟ್ ಸೀಮೆಯ ಬೆಟ್ಟಗಳಲ್ಲಿ ನಿಂತಿತು.",
			"5": "<sup>5</sup> ಹತ್ತನೆಯ ತಿಂಗಳಿನವರೆಗೂ ನೀರು ಕಡಿಮೆ ಕಡಿಮೆಯಾಗುತ್ತಾ ಬಂತು. ಹತ್ತನೆಯ ತಿಂಗಳಿನ ಮೊದಲನೆಯ ದಿನದಲ್ಲಿ ಬೆಟ್ಟಗಳ ಶಿಖರಗಳು ಕಾಣಬಂದವು.",
			"6": "<sup>6</sup> ನಾಲ್ವತ್ತು ದಿನಗಳಾದ ಮೇಲೆ ನೋಹನು ತಾನು ಮಾಡಿದ ನಾವೆಯ ಕಿಟಕಿಯನ್ನು ತೆರೆದು ಕಾಗೆಯನ್ನು ಹೊರಕ್ಕೆ ಬಿಟ್ಟನು.",
			"7": "<sup>7</sup> ಭೂಮಿಯ ಮೇಲಿದ್ದ ನೀರು ಒಣಗುವ ತನಕ ಆ ಕಾಗೆ ಹೋಗುತ್ತಾ ಬರುತ್ತಾ ಇತ್ತು.",
			"8": "<sup>8</sup> ಹೀಗಿರಲಾಗಿ ನೀರು ಇಳಿಯಿತೋ ಇಲ್ಲವೋ ಎಂದು ತಿಳುಕೊಳ್ಳುವದಕ್ಕೆ ನೋಹನು ಪಾರಿವಾಳವನ್ನು ಹೊರಕ್ಕೆ ಬಿಟ್ಟನು.",
			"9": "<sup>9</sup> ನೀರು ಭೂಮಿಯ ಮೇಲೆಲ್ಲಾ ಇರುವದರಿಂದ ಪಾರಿವಾಳವು ಕಾಲಿಡುವದಕ್ಕೆ ಸ್ಥಳವನ್ನು ಎಲ್ಲಿಯೂ ಕಾಣದೆ ತಿರಿಗಿ ನಾವೆಗೆ ಬಂತು. ನೋಹನು ಕೈಚಾಚಿ ಅದನ್ನು ಹಿಡಿದು ನಾವೆಯಲ್ಲಿ ತನ್ನ ಬಳಿಗೆ ತೆಗೆದುಕೊಂಡನು.",
			"10": "<sup>10</sup> ಅವನು ಇನ್ನೂ ಏಳು ದಿವಸ ತಡೆದು ಪಾರಿವಾಳವನ್ನು ತಿರಿಗಿ ಹೊರಕ್ಕೆ ಬಿಟ್ಟನು.",
			"11": "<sup>11</sup> ಸಂಜೆಯಲ್ಲಿ ಆ ಪಾರಿವಾಳವು ಅವನ ಬಳಿಗೆ ತಿರಿಗಿ ಬರಲು, ಆಹಾ, ಅದರ ಬಾಯಲ್ಲಿ ಎಣ್ಣೇಮರದ ಹೊಸ ಚಿಗುರು ಇತ್ತು. ನೋಹನು ಅದನ್ನು ನೋಡಿ ನೀರು ಭೂಮಿಯ ಮೇಲಿಂದ ಹರಿದು ಇಳಿದು ಹೋಯಿತು ಎಂದು ತಿಳುಕೊಂಡನು.",
			"12": "<sup>12</sup> ಮತ್ತೇಳು ದಿವಸದ ಮೇಲೆ ಪಾರಿವಾಳವನ್ನು ಬಿಡಲು ಅದು ಅವನ ಬಳಿಗೆ ತಿರಿಗಿ ಬರಲೇ ಇಲ್ಲ.",
			"13": "<sup>13</sup> ಆರುನೂರ ಒಂದನೆಯ ವರುಷದ ಮೊದಲನೆಯ ತಿಂಗಳಿನ ಮೊದಲನೆಯ ದಿನದಲ್ಲಿ ಭೂಮಿಯ ಮೇಲಿದ್ದ ನೀರು ಇಳಿದಿತ್ತು. ನೋಹನು ನಾವೆಯ ಗವಸಣಿಗೆಯನ್ನು ತೆಗೆದು ನೋಡಲಾಗಿ, ಆಹಾ, ಭೂಮಿಯು ಆರಿಹೋಗಿತ್ತು.",
			"14": "<sup>14</sup> ಎರಡನೆಯ ತಿಂಗಳಿನ ಇಪ್ಪತ್ತೇಳನೆಯ ದಿನದಲ್ಲಿ ಭೂಮಿಯು ಒಣಗಿತ್ತು.",
			"15": "<sup>15</sup> ಆಗ ದೇವರು ನೋಹನಿಗೆ - ನೀನು ಹೆಂಡತಿ ಮಕ್ಕಳು ಸೊಸೆಯರು ಸಹಿತವಾಗಿ ನಾವೆಯನ್ನು ಬಿಟ್ಟು ಹೊರಗೆ ಬಾ;",
			"17": "<sup>17</sup> ನಿನ್ನ ಬಳಿಯಲ್ಲಿರುವ ಪಶು, ಪಕ್ಷಿ, ಕ್ರಿಮಿ ಮುಂತಾದ ಎಲ್ಲಾ ಜೀವಿಗಳೂ ಹೊರಗೆ ಬರಲಿ; ಅವುಗಳಿಗೆ ಭೂಮಿಯ ಮೇಲೆ ಬಹುಸಂತಾನವಾಗಲಿ; ಅವು ಅಭಿವೃದ್ಧಿಯಾಗಿ ಹೆಚ್ಚಲಿ ಎಂದು ಆಜ್ಞಾಪಿಸಲು",
			"18": "<sup>18</sup> ನೋಹನು ಹೆಂಡತಿ - ಮಕ್ಕಳು ಸೊಸೆಯರು ಸಹಿತವಾಗಿ ಹೊರಗೆ ಬಂದನು.",
			"19": "<sup>19</sup> ಮೃಗ ಪಶುಪಕ್ಷಿಕ್ರಿಮಿಗಳೆಲ್ಲವು ತಮ್ಮ ತಮ್ಮ ಜಾತಿಗನುಸಾರವಾಗಿ ನಾವೆಯಿಂದ ಹೊರಗೆ ಬಂದವು.",
			"20": "<sup>20</sup> ಆಗ ನೋಹನು ಯೆಹೋವನಿಗೋಸ್ಕರ ಯಜ್ಞವೇದಿಯನ್ನು ಮಾಡಿ ಅದರ ಮೇಲೆ ಶುದ್ಧವಾದ ಎಲ್ಲಾ ಪಶುಪಕ್ಷಿಜಾತಿಗಳಲ್ಲಿ ಸರ್ವಾಂಗಹೋಮ ಮಾಡಿದನು.",
			"21": "<sup>21</sup> ಅದರ ಸುವಾಸನೆಯು ಯೆಹೋವನಿಗೆ ಗಮಗಮಿಸಲು ಆತನು ಹೃದಯದೊಳಗೆ - ಮನುಷ್ಯರ ಮನಸ್ಸಂಕಲ್ಪವು ಚಿಕ್ಕಂದಿನಿಂದಲೇ ಕೆಟ್ಟದ್ದು; ಆದರೂ ನಾನು ಇನ್ನು ಮೇಲೆ ಅವರ ನಿಮಿತ್ತವಾಗಿ ಭೂಮಿಯನ್ನು ಶಪಿಸುವದಿಲ್ಲ; ನಾನು ಎಲ್ಲಾ ಜೀವಿಗಳನ್ನೂ ಈಗ ಸಂಹರಿಸಿದಂತೆ ಇನ್ನು ಮೇಲೆ ಸಂಹರಿಸುವದಿಲ್ಲ.",
			"22": "<sup>22</sup> ಭೂಮಿಯು ಇರುವ ತನಕ ಬಿತ್ತನೆಯೂ ಕೊಯಿಲೂ, ಚಳಿಯೂ ಸೆಕೆಯೂ, ಬೇಸಿಗೆಕಾಲವೂ ಹಿಮಕಾಲವೂ, ಹಗಲೂ ಇರುಳೂ ಇವುಗಳ ಕ್ರಮ ತಪ್ಪುವದೇ ಇಲ್ಲ ಅಂದುಕೊಂಡನು."
		},
		"9": {
			"1": "<sup>1</sup> ದೇವರು ನೋಹನನ್ನೂ ಅವನ ಮಕ್ಕಳನ್ನೂ ಆಶೀರ್ವದಿಸಿ ಅವರಿಗೆ - ಬಹುಸಂತಾನವುಳ್ಳವರಾಗಿ ಹೆಚ್ಚಿರಿ; ಭೂಲೋಕದಲ್ಲೆಲ್ಲಾ ತುಂಬಿಕೊಳ್ಳಿರಿ.",
			"2": "<sup>2</sup> ಭೂಮಿಯ ಮೇಲಿರುವ ಎಲ್ಲಾ ಮೃಗಗಳೂ ಆಕಾಶದ ಎಲ್ಲಾ ಪಕ್ಷಿಗಳೂ ನೆಲದ ಮೇಲೆ ಹರಿದಾಡುವ ಎಲ್ಲಾ ಕ್ರಿಮಿಗಳೂ ಸಮುದ್ರದ ಮೀನುಗಳೂ ನಿಮಗೆ ಬೆದರಿ ಅಂಜಿಕೊಳ್ಳುವವು; ನಾನು ಅವುಗಳನ್ನೆಲ್ಲಾ ನಿಮ್ಮ ಸ್ವಾಧೀನಕ್ಕೆ ಕೊಟ್ಟಿದ್ದೇನೆ.",
			"3": "<sup>3</sup> ಭೂಮಿಯ ಮೇಲೆ ತಿರುಗುವ ಎಲ್ಲಾ ಜೀವಜಂತುಗಳೂ ನಿಮಗೆ ಆಹಾರವಾಗಿರುವವು. ನಾನು ನಿಮ್ಮ ಆಹಾರಕ್ಕೆ ಪೈರುಗಳನ್ನು ಕೊಟ್ಟಂತೆ ಇವುಗಳನ್ನೂ ಕೊಟ್ಟಿದ್ದೇನೆ.",
			"4": "<sup>4</sup> ಆದರೆ ರಕ್ತದಿಂದ ಕೂಡಿರುವ ಮಾಂಸವನ್ನು ನೀವು ತಿನ್ನಬಾರದು; ರಕ್ತವು ಜೀವವಷ್ಟೆ.",
			"5": "<sup>5</sup> ಇದಲ್ಲದೆ ನಿಮ್ಮ ರಕ್ತವನ್ನು ಸುರಿಸಿ ಜೀವತೆಗೆಯುವವರಿಗೆ ಮುಯ್ಯಿತೀರಿಸುವೆನು. ಮೃಗವಾಗಿದ್ದರೆ ಅದಕ್ಕೂ ಮನುಷ್ಯನಾಗಿದ್ದರೆ, ಹತವಾದವನು ಅವನ ಸಹೋದರನಾಗಿರುವದರಿಂದ, ಅವನಿಗೂ ಮುಯ್ಯಿ ತೀರಿಸುವೆನೆಂದು ತಿಳಿದುಕೊಳ್ಳಿರಿ.",
			"6": "<sup>6</sup> ನರಹತ್ಯವು ಸಹೋದರ ಹತ್ಯವಲ್ಲವೇ. ದೇವರು ಮನುಷ್ಯರನ್ನು ತನ್ನ ಸ್ವರೂಪದಲ್ಲಿಯೇ ಉಂಟುಮಾಡಿದನಾದ್ದರಿಂದ ಯಾರು ಮನುಷ್ಯನ ರಕ್ತವನ್ನು ಸುರಿಸುತ್ತಾರೋ ಅವರ ರಕ್ತವನ್ನು ಮನುಷ್ಯರೇ ಸುರಿಸುವರು.",
			"7": "<sup>7</sup> ನೀವು ಅಭಿವೃದ್ಧಿಯಾಗಿ ಹೆಚ್ಚಿರಿ; ಭೂಮಿಯ ಮೇಲೆ ನಿಮಗೆ ಬಹು ಸಂತಾನವಾಗಲಿ ಎಂದು ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ಇದಲ್ಲದೆ ದೇವರು ನೋಹನಿಗೂ ಅವನ ಮಕ್ಕಳಿಗೂ -",
			"9": "<sup>9</sup> ಕೇಳಿರಿ, ನಾನು ನಿಮ್ಮನ್ನೂ ನಿಮ್ಮ ಸಂತತಿಯವರನ್ನೂ",
			"10": "<sup>10</sup> ನಿಮ್ಮ ಕೂಡ ನಾವೆಯಿಂದ ಹೊರಟುಬಂದ ಪಶುಪಕ್ಷಿಮೃಗಾದಿ ಸಕಲ ಭೂಜಂತುಗಳನ್ನೂ ಕುರಿತು ಸ್ಥಿರಪ್ರತಿಜ್ಞೆ ಮಾಡುತ್ತೇನೆ;",
			"11": "<sup>11</sup> ಆ ಪ್ರತಿಜ್ಞೆ ಯಾವದೆಂದರೆ - ಇನ್ನು ಮೇಲೆ ಎಲ್ಲಾ ಪ್ರಾಣಿಗಳು ಪ್ರಳಯದಿಂದ ನಾಶವಾಗುವದಿಲ್ಲ; ಇನ್ನು ಮುಂದೆ ಭೂಮಿಯನ್ನು ಹಾಳು ಮಾಡುವ ಪ್ರಳಯವು ಬರುವದೇ ಇಲ್ಲ ಎಂದು ಹೇಳಿದನು.",
			"12": "<sup>12</sup> ದೇವರು ಮತ್ತೂ ಹೇಳಿದ್ದೇನಂದರೆ - ನಾನು ನಿಮ್ಮನ್ನೂ ನಿಮ್ಮ ಸಂಗಡ ಇರುವ ಸಮಸ್ತ ಜೀವರಾಶಿಗಳನ್ನೂ ಕುರಿತು ಎಲ್ಲಾ ತಲತಲಾಂತರಗಳಲ್ಲಿಯೂ ಮಾಡುವ ಈ ಪ್ರತಿಜ್ಞೆಗೆ",
			"13": "<sup>13</sup> ಮೇಘಗಳಲ್ಲಿ ನಾನಿಟ್ಟಿರುವ ಮುಗಿಲುಬಿಲ್ಲೇ ಗುರುತಾಗಿರುವದು. ನನಗೂ ಭೂಪ್ರಾಣಿಗಳಿಗೂ ಆದ ಒಡಂಬಡಿಕೆಗೆ ಇದೇ ಗುರುತಾಗಿರಲಿ.",
			"14": "<sup>14</sup> ನಾನು ಭೂಲೋಕದ ಮೇಲೆ ಮೇಘಗಳನ್ನು ಕವಿಸುವಾಗ ಆ ಬಿಲ್ಲು ಮೇಘಗಳಲ್ಲಿ ಕಂಡು ಬರುವದು.",
			"15": "<sup>15</sup> ಆಗ ನಾನು ನಿಮ್ಮನ್ನೂ ಎಲ್ಲಾ ಜೀವರಾಶಿಗಳನ್ನೂ ಕುರಿತು ಮಾಡಿದ ಪ್ರತಿಜ್ಞೆಯನ್ನು ಜ್ಞಾಪಕಮಾಡಿಕೊಳ್ಳುವೆನು. ಇನ್ನು ಮುಂದೆ ನೀರು ಹೆಚ್ಚಿ ಎಲ್ಲಾ ಭೂಪ್ರಾಣಿಗಳನ್ನು ಹಾಳುಮಾಡುವ ಪ್ರಳಯವಾಗುವದಿಲ್ಲ.",
			"16": "<sup>16</sup> ಆ ಬಿಲ್ಲು ಮೇಘಗಳಲ್ಲಿ ಕಾಣಿಸುವಾಗ ನಾನು ಅದನ್ನು ನೋಡಿ ದೇವರಾದ ನನಗೂ ಭೂಮಿಯ ಮೇಲಿರುವ ಎಲ್ಲಾ ಜೀವಜಂತುಗಳಿಗೂ ಆದ ಶಾಶ್ವತವಾದ ಒಡಂಬಡಿಕೆಯನ್ನು ಜ್ಞಾಪಕಮಾಡಿಕೊಳ್ಳುವೆನು ಅಂದನು.",
			"17": "<sup>17</sup> ಮತ್ತು ದೇವರು ನೋಹನಿಗೆ - ನನಗೂ ಎಲ್ಲಾ ಭೂಜೀವಿಗಳಿಗೂ ಆದ ಒಡಂಬಡಿಕೆಗೆ ಇದೇ ಗುರುತು ಎಂದು ಹೇಳಿದನು.",
			"18": "<sup>18</sup> ನಾವೆಯಿಂದ ಹೊರಟುಬಂದ ನೋಹನ ಮಕ್ಕಳು ಶೇಮ್ ಹಾಮ್ ಯೆಫೆತ್ ಎಂಬವರೇ (ಹಾಮನು ಕಾನಾನನ ತಂದೆಯು).",
			"19": "<sup>19</sup> ಈ ಮೂವರು ನೋಹನ ಮಕ್ಕಳು; ಇವರಿಂದಲೇ ಭೂಮಿಯಲ್ಲೆಲ್ಲಾ ಹರಡಿಕೊಂಡಿರುವ ಜನರು ಉತ್ಪತ್ತಿಯಾದರು.",
			"20": "<sup>20</sup> ನೋಹನು ವ್ಯವಸಾಯಗಾರನಾಗಿದ್ದನು; ಅವನೇ ದ್ರಾಕ್ಷೇತೋಟವನ್ನು ಪ್ರಾರಂಭಿಸಿದನು.",
			"21": "<sup>21</sup> ಅವನು ದ್ರಾಕ್ಷಾರಸವನ್ನು ಕುಡಿಯಲು ಅಮಲೇರಿದ್ದರಿಂದ ಗುಡಾರದಲ್ಲಿ ಬೆತ್ತಲೆಯಾಗಿ ಬಿದ್ದಿದ್ದನು.",
			"22": "<sup>22</sup> ಕಾನಾನನ ತಂದೆಯಾದ ಹಾಮನು ತಂದೆಯು ಬೆತ್ತಲೆಯಾಗಿರುವದನ್ನು ಕಂಡು ಹೊರಗಿದ್ದ ಅಣ್ಣತಮ್ಮಂದಿರಾದ ಶೇಮ್ ಯೆಫೆತರಿಗೆ ತಿಳಿಸಿದನು.",
			"23": "<sup>23</sup> ಇವರಿಬ್ಬರು ಕಂಬಳಿಯನ್ನು ತೆಗೆದುಕೊಂಡು ತಮ್ಮಿಬ್ಬರ ಬೆನ್ನಿನ ಮೇಲೆ ಹಾಕಿಕೊಂಡು ಹಿಂಧಿಂದಕ್ಕೆ ನಡೆದು ತಂದೆಗೆ ಹೊದಿಸಿ ಅವನ ಮಾನವನ್ನು ಕಾಪಾಡಿದರು; ಅವರು ಹಿಮ್ಮುಖರಾಗಿದ್ದದರಿಂದ ತಂದೆಯು ಬೆತ್ತಲೆಯಾಗಿದ್ದದ್ದನ್ನು ನೋಡಲಿಲ್ಲ.",
			"24": "<sup>24</sup> ನೋಹನು ಅಮಲಿಳಿದೆದ್ದು ಕಿರೀಮಗನು ಮಾಡಿದ್ದನ್ನು ತಿಳಿದು -",
			"25": "<sup>25</sup> ಕಾನಾನನು ಶಾಪಗ್ರಸ್ತನಾಗಲಿ. ಅವನು ತನ್ನ ಅಣ್ಣತಮ್ಮಂದಿರಿಗೆ ದಾಸಾನುದಾಸನಾಗಲಿ ಅಂದನು.",
			"26": "<sup>26</sup> ಅವನು ಇನ್ನೂ ನುಡಿದದ್ದೇನಂದರೆ - ಶೇಮನ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಸ್ತೋತ್ರವಾಗಲಿ; ಕಾನಾನನು ಅವರಿಗೆ ದಾಸನಾಗಲಿ.",
			"27": "<sup>27</sup> ಯೆಫೆತನನ್ನು ದೇವರು ವಿಸ್ತರಿಸಲಿ. ಅವನು ಶೇಮನ ಗುಡಾರಗಳಲ್ಲಿ ವಾಸವಾಗಿರಲಿ. ಕಾನಾನನು ಅವರಿಗೆ ದಾಸನಾಗಿರಲಿ ಎಂಬದು.",
			"28": "<sup>28</sup> ಪ್ರಳಯವಾದ ಮೇಲೆ ನೋಹನು ಮುನ್ನೂರೈವತ್ತು ವರುಷ ಬದುಕಿದನು.",
			"29": "<sup>29</sup> ಅವನು ಒಟ್ಟು ಒಂಭೈನೂರೈವತ್ತು ವರುಷ ಬದುಕಿ ಸತ್ತನು."
		},
		"10": {
			"1": "<sup>1</sup> ನೋಹನ ಮಕ್ಕಳಾದ ಶೇಮ್ ಹಾಮ್ ಯೆಫೆತರ ವಂಶದವರ ಚರಿತ್ರೆಯು - ಜಲಪ್ರಳಯವಾದ ಮೇಲೆ ಅವರಿಗೆ ಮಕ್ಕಳು ಹುಟ್ಟಿದರು.",
			"2": "<sup>2</sup> ಯೆಫೆತನ ಸಂತಾನದವರು ಯಾರಂದರೆ - ಗೋಮೆರ್, ಮಾಗೋಗ್, ಮಾದಯ್, ಯಾವಾನ್, ತೂಬಲ್, ಮೆಷೆಕ್, ತೀರಾಸ್ ಎಂಬವರೇ.",
			"3": "<sup>3</sup> ಗೋಮೆರನ ಸಂತಾನದವರು - ಅಷ್ಕೆನಸ್, ರೀಫತ್, ತೋಗರ್ಮ ಎಂಬವರು.",
			"4": "<sup>4</sup> ಯಾವಾನನ ಸಂತಾನದವರು - ಎಲೀಷಾ, ತಾರ್ಷೀಷ್, ಕಿತ್ತೀಮ್, ದೋದಾ ಎಂಬ ಸ್ಥಳಗಳವರು; ಇವರು ಸಮುದ್ರದ ರೇವುಗಳಲ್ಲಿ ಹರಡಿಕೊಂಡರು.",
			"5": "<sup>5</sup> ದೇಶಭಾಷಾ ಕುಲಜನಾಂಗಗಳ ಪ್ರಕಾರ [ಇವರೇ ಯೆಫೆತನ ವಂಶದವರು].",
			"6": "<sup>6</sup> ಹಾಮನ ಸಂತಾನದವರು ಯಾರಂದರೆ - ಕೂಷ್, ಮಿಚ್ರಯಿಮ್, ಪೂತ್, ಕಾನಾನ್ ಎಂಬವರೇ.",
			"7": "<sup>7</sup> ಕೂಷನ ಸಂತಾನದವರು - ಸೆಬಾ, ಹವೀಲ, ಸಬ್ತಾ, ರಗ್ಮ, ಸಬ್ತಕಾ ಎಂಬ ಜನಾಂಗಗಳು. ರಗ್ಮ ಸಂತಾನದವರು - ಶೆಬಾ, ದೆದಾನ್ ಎಂಬ ಜನಾಂಗಗಳು.",
			"8": "<sup>8</sup> ಕೂಷನು ನಿಮ್ರೋದನನ್ನು ಪಡೆದನು. ಅವನು ಪರಾಕ್ರಮದಿಂದ ಮೊದಲನೆಯ ಭೂರಾಜನಾದನು.",
			"9": "<sup>9</sup> ಅವನು ಅತಿ ಸಾಹಸಿಯಾದ ಬೇಟೆಗಾರನು. ನಿಮ್ರೋದನಂತೆ ಅತಿ ಸಾಹಸಿಯಾದ ಬೇಟೆಗಾರನೆಂದು ಈಗಲೂ ಹೇಳುವದುಂಟಲ್ಲಾ.",
			"10": "<sup>10</sup> ಶಿನಾರ್ ದೇಶದಲ್ಲಿರುವ ಬಾಬೆಲ್, ಯೆರೆಕ್, ಅಕ್ಕದ್, ಕಲ್ನೇ ಎಂಬ ಪಟ್ಟಣಗಳೇ ಅವನ ರಾಜ್ಯದ ಮೂಲ ಪಟ್ಟಣಗಳು.",
			"11": "<sup>11</sup> ಅವನು ಆ ದೇಶದಿಂದ ಹೊರಟು ಅಶ್ಶೂರ್ ದೇಶಕ್ಕೆ ಬಂದು ನಿನೆವೆ, ರೆಹೋಬೋತೀರ್, ಕೆಲಹ ಎಂಬ ಪಟ್ಟಣಗಳನ್ನೂ",
			"12": "<sup>12</sup> ನಿನೆವೆಗೂ ಕೆಲಹಕ್ಕೂ ನಡುವೆ ಇರುವ ರೆಸೆನ್ ಪಟ್ಟಣವನ್ನೂ ಕಟ್ಟಿಸಿದನು. ಇವೆಲ್ಲಾ ಒಟ್ಟಾಗಿ ಮಹಾ ಪಟ್ಟಣವೆಂದು ಪ್ರಸಿದ್ಧವಾಗಿದೆ.",
			"13": "<sup>13</sup> ಮಿಚ್ರಯಿಮ್ಯರಿಂದ ಲೂದ್ಯರೂ ಅನಾಮ್ಯರೂ ಲೆಹಾಬ್ಯರೂ ನಫ್ತುಹ್ಯರೂ ಪತ್ರುಸ್ಯರೂ ಕಸ್ಲುಹ್ಯರೂ ಕಫ್ತೋರ್ಯರೂ ಹುಟ್ಟಿದರು.",
			"14": "<sup>14</sup> ಕಸ್ಲುಹ್ಯರ ಬಳಿಯಿಂದ ಫಿಲಿಷ್ಟಿಯರು ಹೊರಟುಬಂದರು.",
			"15": "<sup>15</sup> ಕಾನಾನ್ ವಂಶದಲ್ಲಿ ಮೊದಲು ಹುಟ್ಟಿದವನು ಸೀದೋನ್; ಆಮೇಲೆ ಹೇತ್ ಹುಟ್ಟಿದನು.",
			"16": "<sup>16</sup> ಇದಲ್ಲದೆ ಯೆಬೂಸಿಯರೂ ಅಮೋರಿಯರೂ ಗಿರ್ಗಾಷಿಯರೂ",
			"17": "<sup>17</sup> ಹಿವ್ವಿಯರೂ ಅರ್ಕಿಯರೂ ಸೀನಿಯರೂ",
			"18": "<sup>18</sup> ಅರ್ವಾದಿಯರೂ ಚೆಮಾರಿಯರೂ ಹಮಾತಿಯರೂ ಕಾನಾನನಿಂದ ಹುಟ್ಟಿದರು. ಕಾಲಕ್ರಮದಿಂದ ಈ ಕಾನಾನ್ ಕುಲಗಳವರು ಹರಡಿಕೊಂಡರು.",
			"19": "<sup>19</sup> ಕಾನಾನ್ಯರ ಸೀಮೆಯು ಸೀದೋನ್ ಪಟ್ಟಣದಿಂದ ಗೆರಾರಿಗೆ ಹೋಗುವ ದಾರಿಯಲ್ಲಿರುವ ಗಾಜಾ ಪಟ್ಟಣದವರೆಗೂ ಮತ್ತು ಸೊದೋಮ್, ಗೊಮೋರ, ಅದ್ಮಾ, ಚೆಬೋಯಿಮ್ ಎಂಬ ಪಟ್ಟಣಗಳಿಗೆ ಹೋಗುವ ದಾರಿಯಲ್ಲಿರುವ ಲೆಷಾ ಊರಿನವರೆಗೂ ಇತ್ತು.",
			"20": "<sup>20</sup> ಕುಲಭಾಷಾ ದೇಶಜನಾಂಗಗಳ ಪ್ರಕಾರ ಇವರೇ ಹಾಮನ ವಂಶದವರು.",
			"21": "<sup>21</sup> ಇಬ್ರಿಯರೆಲ್ಲರಿಗೆ ಮೂಲಪುರುಷನೂ ಯೆಫೆತನ ಅಣ್ಣನೂ ಆಗಿದ್ದ ಶೇಮನಿಗೆ ಸಹ ಸಂತಾನವಾಯಿತು.",
			"22": "<sup>22</sup> ಶೇಮನ ಸಂತಾನದವರು - ಏಲಾಮ್, ಅಶ್ಶೂರ್, ಅರ್ಪಕ್ಷದ್, ಲೂದ್, ಅರಾಮ್ ಎಂಬವರೇ.",
			"23": "<sup>23</sup> ಅರಾಮ್ ಸಂತಾನದವರು - ಊಸ್, ಹೂಲ್, ಗೆತೆರ್, ಮಷ್ ಎಂಬವರೇ.",
			"24": "<sup>24</sup> ಅರ್ಪಕ್ಷದನಿಂದ ಶೆಲಹನೂ ಶೆಲಹನಿಂದ ಎಬರನೂ ಹುಟ್ಟಿದರು.",
			"25": "<sup>25</sup> ಎಬರನಿಗೆ ಇಬ್ಬರು ಮಕ್ಕಳು ಹುಟ್ಟಿದರು. ಒಬ್ಬನಿಗೆ ಪೆಲೆಗೆಂಬ ಹೆಸರು; ಅವನ ಕಾಲದಲ್ಲಿ ಭೂಮಿಯ ಜನಗಳು ವಿಂಗಡವಾದವು. ಅವನ ತಮ್ಮನ ಹೆಸರು ಯೊಕ್ತಾನ್.",
			"26": "<sup>26</sup> ಯೊಕ್ತಾನನ ಸಂತಾನದವರು - ಅಲ್ಮೋದಾದ್ ಶೆಲೆಪ್ ಹಚರ್ಮಾವೆತ್ ಯೆರಹ ಹದೋರಾಮ್",
			"27": "<sup>27</sup> ಊಜಾಲ್ ದಿಕ್ಲಾ ಓಬಾಲ್ ಅಬೀಮಯೇಲ್ ಶೆಬಾ",
			"29": "<sup>29</sup> ಓಫೀರ್‍‍ಹವೀಲ ಯೋಬಾಬ್ ಎಂಬ ಸ್ಥಳಗಳವರು. ಈ ಕುಲಗಳೆಲ್ಲಾ ಯೊಕ್ತಾನನಿಂದ ಹುಟ್ಟಿದವು.",
			"30": "<sup>30</sup> ಇವರ ನಿವಾಸವು ಮೇಶಾಸೀಮೆ ಮೊದಲುಗೊಂಡು ಮೂಡಲಲ್ಲಿರುವ ಸೆಫಾರ್ ಎಂಬ ಬೆಟ್ಟದವರೆಗೂ ಇತ್ತು.",
			"31": "<sup>31</sup> ಕುಲಭಾಷಾ ಜನಾಂಗಗಳ ಪ್ರಕಾರ ಇವರೇ ಶೇಮನ ವಂಶದವರು.",
			"32": "<sup>32</sup> ಸಂತತಿ ಜನಾಂಗಗಳ ಪ್ರಕಾರ ಇವರೇ ನೋಹನ ವಂಶದವರು. ಜಲಪ್ರಳಯವಾದನಂತರ ಭೂಮಿಯ ಮೇಲೆ ಹರಡಿಕೊಂಡ ಜನಾಂಗಗಳವರು ಇವರೇ."
		},
		"11": {
			"1": "<sup>1</sup> ಲೋಕದವರೆಲ್ಲರಿಗೂ ಒಂದೇ ಭಾಷೆ ಇತ್ತು. ಭಾಷಾಭೇದವೇನೂ ಇರಲಿಲ್ಲ.",
			"2": "<sup>2</sup> ಅವರು ಪೂರ್ವದಿಕ್ಕಿಗೆ ಮುಂದೆ ಮುಂದೆ ಪ್ರಯಾಣ ಮಾಡುತ್ತಿರುವಾಗ ಶಿನಾರ್ ದೇಶದಲ್ಲಿ ಬೈಲುಸೀಮೆ ಸಿಕ್ಕಲು ಅಲ್ಲೇ ವಾಸಮಾಡಿಕೊಂಡು ತಮ್ಮ ತಮ್ಮೊಳಗೆ -",
			"3": "<sup>3</sup> ಬನ್ನಿ, ಒಳ್ಳೊಳ್ಳೇ ಸುಟ್ಟ ಇಟ್ಟಿಗೆಗಳನ್ನು ಮಾಡೋಣ;",
			"4": "<sup>4</sup> ಒಂದು ಪಟ್ಟಣವನ್ನೂ ಆಕಾಶವನ್ನು ಮುಟ್ಟುವ ಗೋಪುರವನ್ನೂ ಕಟ್ಟಿ ದೊಡ್ಡ ಹೆಸರನ್ನು ಪಡೆಯೋಣ; ಹೀಗೆ ಮಾಡಿದರೆ ಭೂಮಿಯ ಮೇಲೆಲ್ಲಾ ಚದರುವದಕ್ಕೆ ಆಸ್ಪದವಾಗುವದಿಲ್ಲ ಅಂದುಕೊಂಡರು. ಕಟ್ಟುವಾಗ ಕಲ್ಲಿಗೆ ಬದಲಾಗಿ ಇಟ್ಟಿಗೆಯನ್ನೂ ಸುಣ್ಣಕ್ಕೆ ಬದಲಾಗಿ ಕಲ್ಲರಗನ್ನೂ ಉಪಯೋಗಿಸಿದರು.",
			"5": "<sup>5</sup> ನರಪುತ್ರರು ಕಟ್ಟುತ್ತಿದ್ದ ಆ ಪಟ್ಟಣವನ್ನೂ ಗೋಪುರವನ್ನೂ ನೋಡುವದಕ್ಕೆ ಯೆಹೋವನು ಇಳಿದು ಬಂದನು.",
			"6": "<sup>6</sup> ನೋಡಿದ ಮೇಲೆ ಆತನು - ಇವರು ಒಂದೇ ಜನಾಂಗ; ಇವರೆಲ್ಲರಿಗೂ ಒಂದೇ ಭಾಷೆ. ಪ್ರಾರಂಭದಲ್ಲೇ ಇಷ್ಟು ದೊಡ್ಡ ಕೆಲಸ ಮಾಡುವವರಾಗಿರಲು ಮುಂದೆ ಏನು ಆಲೋಚಿಸಿದರೂ ಇವರಿಗೆ ಅಸಾಧ್ಯವಲ್ಲ.",
			"7": "<sup>7</sup> ನಾವು ಇಳಿದು ಇವರಲ್ಲಿ ಒಬ್ಬರ ಮಾತು ಒಬ್ಬರಿಗೆ ತಿಳಿಯದಂತೆ ಇವರ ಭಾಷೆಯನ್ನು ತಾರುಮಾರುಮಾಡೋಣ ಬನ್ನಿ ಅಂದನು.",
			"8": "<sup>8</sup> ಹಾಗೆಯೇ ಮಾಡಿ ಯೆಹೋವನು ಅವರನ್ನು ಅಲ್ಲಿಂದ ಭೂಲೋಕದಲ್ಲೆಲ್ಲಾ ಚದರಿಸಿದನು. ಅವರು ಆ ಪಟ್ಟಣ ಕಟ್ಟುವದನ್ನು ನಿಲ್ಲಿಸಿಬಿಟ್ಟರು.",
			"9": "<sup>9</sup> ಸಮಸ್ತಲೋಕದ ಭಾಷೆಯನ್ನು ಯೆಹೋವನು ಅಲ್ಲಿ ತಾರುಮಾರುಮಾಡಿ ಅವರನ್ನು ಅಲ್ಲಿಂದ ಭೂಲೋಕದಲ್ಲೆಲ್ಲಾ ಚದರಿಸಿದ್ದರಿಂದ ಆ ಪಟ್ಟಣಕ್ಕೆ ಬಾಬೆಲ್ ಎಂಬ ಹೆಸರಾಯಿತು.",
			"10": "<sup>10</sup> ಶೇಮನ ವಂಶದವರ ಚರಿತ್ರೆಯು. ಜಲಪ್ರಳಯವು ಕಳೆದು ಎರಡು ವರುಷಗಳಾದ ಮೇಲೆ ಶೇಮನು ನೂರು ವರುಷದವನಾಗಿ ಅರ್ಪಕ್ಷದನನ್ನು ಪಡೆದನು.",
			"11": "<sup>11</sup> ಅವನು ಅರ್ಪಕ್ಷದನನ್ನು ಪಡೆದ ಮೇಲೆ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ಪಡೆದು ಐನೂರು ವರುಷ ಬದುಕಿದನು.",
			"12": "<sup>12</sup> ಅರ್ಪಕ್ಷದನು ಮೂವತ್ತೈದು ವರುಷದವನಾಗಿ ಶೆಲಹನನ್ನು ಪಡೆದನು.",
			"13": "<sup>13</sup> ಅವನು ಶೆಲಹನನ್ನು ಪಡೆದ ಮೇಲೆ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ಪಡೆದು ನಾನೂರ ಮೂರು ವರುಷ ಬದುಕಿದನು.",
			"14": "<sup>14</sup> ಶೆಲಹನು ಮೂವತ್ತು ವರುಷದವನಾಗಿ ಎಬರನನ್ನು ಪಡೆದನು.",
			"15": "<sup>15</sup> ಅವನು ಎಬರನನ್ನು ಪಡೆದ ಮೇಲೆ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ಪಡೆದು ನಾನೂರ ಮೂರು ವರುಷ ಬದುಕಿದನು.",
			"16": "<sup>16</sup> ಎಬರನು ಮೂವತ್ತು ನಾಲ್ಕು ವರುಷದವನಾಗಿ ಪೆಲೆಗನನ್ನು ಪಡೆದನು.",
			"17": "<sup>17</sup> ಅವನು ಪೆಲೆಗನನ್ನು ಪಡೆದ ಮೇಲೆ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ಪಡೆದು ನಾನೂರ ಮೂವತ್ತು ವರುಷ ಬದುಕಿದನು.",
			"18": "<sup>18</sup> ಪೆಲೆಗನು ಮೂವತ್ತು ವರುಷದವನಾಗಿ ರೆಗೂವನನ್ನು ಪಡೆದನು.",
			"19": "<sup>19</sup> ಅವನು ರೆಗೂವನನ್ನು ಪಡೆದ ಮೇಲೆ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ಪಡೆದು ಇನ್ನೂರ ಒಂಭತ್ತು ವರುಷ ಬದುಕಿದನು.",
			"20": "<sup>20</sup> ರೆಗೂವನು ಮೂವತ್ತೆರಡು ವರುಷದವನಾಗಿ ಸೆರೂಗನನ್ನು ಪಡೆದನು.",
			"21": "<sup>21</sup> ಅವನು ಸೆರಗೂನನ್ನು ಪಡೆದ ಮೇಲೆ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ಪಡೆದು ಇನ್ನೂರ ಏಳು ವರುಷ ಬದುಕಿದನು.",
			"22": "<sup>22</sup> ಸೆರೂಗನು ಮೂವತ್ತು ವರುಷದವನಾಗಿ ನಾಹೋರನನ್ನು ಪಡೆದನು.",
			"23": "<sup>23</sup> ಅವನು ನಾಹೋರನನ್ನು ಪಡೆದ ಮೇಲೆ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ಪಡೆದು ಇನ್ನೂರು ವರುಷ ಬದುಕಿದನು.",
			"24": "<sup>24</sup> ನಾಹೋರನು ಇಪ್ಪತ್ತೊಂಭತ್ತು ವರುಷದವನಾಗಿ ತೆರಹನನ್ನು ಪಡೆದನು.",
			"25": "<sup>25</sup> ಅವನು ತೆರಹನನ್ನು ಪಡೆದ ಮೇಲೆ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ಪಡೆದು ನೂರ ಹತ್ತೊಂಭತ್ತು ವರುಷ ಬದುಕಿದನು.",
			"26": "<sup>26</sup> ತೆರಹನು ಎಪ್ಪತ್ತು ವರುಷದವನಾಗಿ ಅಬ್ರಾಮ, ನಾಹೋರ, ಹಾರಾನ ಎಂಬ ಮಕ್ಕಳನ್ನು ಪಡೆದನು.",
			"27": "<sup>27</sup> ತೆರಹನ ವಂಶದವರ ಚರಿತ್ರೆ. ತೆರಹನು ಅಬ್ರಾಮ ನಾಹೋರ ಹಾರಾನರನ್ನು ಪಡೆದನು.",
			"28": "<sup>28</sup> ಹಾರಾನನು ಲೋಟನನ್ನು ಪಡೆದನು. ಹಾರಾನನು ತಾನು ಹುಟ್ಟಿದ ದೇಶದೊಳಗೆ ಕಲ್ದೀಯರ ಊರ್ ಎಂಬ ಪಟ್ಟಣದಲ್ಲಿ ತನ್ನ ತಂದೆಯಾದ ತೆರಹನ ಹತ್ತಿರವೇ ಸತ್ತನು.",
			"29": "<sup>29</sup> ಅಬ್ರಾಮನೂ ನಾಹೋರನೂ ಮದುವೆಮಾಡಿಕೊಂಡರು. ಅಬ್ರಾಮನ ಹೆಂಡತಿಯ ಹೆಸರು ಸಾರಯಳು, ನಾಹೋರನ ಹೆಂಡತಿಯ ಹೆಸರು ಮಿಲ್ಕಾ; ಈಕೆಯು ಹಾರಾನನ ಮಗಳು; ಹಾರಾನನು ಮಿಲ್ಕಳಿಗೂ ಇಸ್ಕಳಿಗೂ ತಂದೆ.",
			"30": "<sup>30</sup> ಸಾರಯಳು ಬಂಜೆಯಾಗಿದ್ದದರಿಂದ ಆಕೆಗೆ ಮಕ್ಕಳಿರಲಿಲ್ಲ.",
			"31": "<sup>31</sup> ತೆರಹನು ತನ್ನ ಮಗನಾದ ಅಬ್ರಾಮನನ್ನೂ ತನಗೆ ಮೊಮ್ಮಗನೂ ಹಾರಾನನಿಗೂ ಮಗನೂ ಆಗಿರುವ ಲೋಟನನ್ನೂ ತನಗೆ ಸೊಸೆಯೂ ಅಬ್ರಾಮನಿಗೆ ಹೆಂಡತಿಯೂ ಆಗಿರುವ ಸಾರಯಳನ್ನೂ ಕರಕೊಂಡು ಕಾನಾನ್‍ದೇಶಕ್ಕೆ ಹೋಗಬೇಕೆಂದು ಕಲ್ದೀಯರ ಊರ್ ಎಂಬ ಪಟ್ಟಣವನ್ನು ಬಿಟ್ಟನು. ಅವರು ಹಾರಾನ್ ಪಟ್ಟಣಕ್ಕೆ ಬಂದು ಅಲ್ಲೇ ವಾಸಮಾಡಿಕೊಂಡರು.",
			"32": "<sup>32</sup> ತೆರಹನು ಇನ್ನೂರ ಐದು ವರುಷದವನಾಗಿ ಖಾರಾನಿನಲ್ಲಿ ಸತ್ತನು."
		},
		"12": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಅಬ್ರಾಮನಿಗೆ - ನೀನು ಸ್ವದೇಶವನ್ನೂ ಬಂಧು ಬಳಗವನ್ನೂ ತಂದೆಯ ಮನೆಯನ್ನೂ ಬಿಟ್ಟು ನಾನು ತೋರಿಸುವ ದೇಶಕ್ಕೆ ಹೊರಟುಹೋಗು.",
			"2": "<sup>2</sup> ನಾನು ನಿನ್ನನ್ನು ದೊಡ್ಡ ಜನಾಂಗವಾಗುವಂತೆ ಮಾಡಿ ಆಶೀರ್ವದಿಸಿ ನಿನ್ನ ಹೆಸರನ್ನು ಪ್ರಖ್ಯಾತಿಗೆ ತರುವೆನು.",
			"3": "<sup>3</sup> ನೀನು ಆಶೀರ್ವಾದನಿಧಿಯಾಗುವಿ. ನಿನ್ನನ್ನು ಹರಸುವವರನ್ನು ಹರಸುವೆನು; ನಿನ್ನನ್ನು ಶಪಿಸುವವರನ್ನು ಶಪಿಸುವೆನು. ನಿನ್ನ ಮೂಲಕ ಭೂಲೋಕದ ಎಲ್ಲಾ ಕುಲದವರಿಗೂ ಆಶೀರ್ವಾದವುಂಟಾಗುವದು ಎಂದು ಹೇಳಿದನು.",
			"4": "<sup>4</sup> ಯೆಹೋವನು ಹೇಳಿದ ಮೇರೆಗೆ ಅಬ್ರಾಮನು ಹೊರಟುಹೋದನು; ಲೋಟನೂ ಸಂಗಡ ಹೋದನು. ಅಬ್ರಾಮನು ಖಾರಾನ್ ಪಟ್ಟಣವನ್ನು ಬಿಟ್ಟು ಹೊರಟಾಗ ಎಪ್ಪತ್ತೈದು ವರುಷದವನಾಗಿದ್ದನು.",
			"5": "<sup>5</sup> ಅವನು ತನ್ನ ಹೆಂಡತಿಯಾದ ಸಾರಯಳನ್ನೂ ತನ್ನ ತಮ್ಮನ ಮಗನಾದ ಲೋಟನನ್ನೂ ತಾನೂ ಲೋಟನೂ ಖಾರಾನಿನಲ್ಲಿ ಸಂಪಾದಿಸಿದ್ದ ಎಲ್ಲಾ ಸೊತ್ತನ್ನೂ ದಾಸದಾಸಿಯರನ್ನೂ ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ಕಾನಾನ್ ದೇಶಕ್ಕೆ ಸೇರಿದನು.",
			"6": "<sup>6</sup> ಅಬ್ರಾಮನು ಆ ದೇಶದಲ್ಲಿ ಸಂಚರಿಸುತ್ತಾ ಶೆಕೆಮ್ ಕ್ಷೇತ್ರದಲ್ಲಿರುವ ಮೋರೆಯೆಂಬ ವೃಕ್ಷದ ಬಳಿಗೆ ಬಂದನು. ಆ ಕಾಲದಲ್ಲಿ ಕಾನಾನ್ಯರು ದೇಶದಲ್ಲಿದ್ದರು.",
			"7": "<sup>7</sup> ಅಲ್ಲಿ ಯೆಹೋವನು ಅಬ್ರಾಮನಿಗೆ ದರ್ಶನಕೊಟ್ಟು - ನಾನು ಈ ದೇಶವನ್ನು ನಿನ್ನ ಸಂತಾನಕ್ಕೆ ಕೊಡುವೆನು ಅಂದನು. ತನಗೆ ದರ್ಶನಕೊಟ್ಟ ಯೆಹೋವನಿಗೆ ಅಬ್ರಾಮನು ಯಜ್ಞವೇದಿಯನ್ನು ಕಟ್ಟಿಸಿದನು.",
			"8": "<sup>8</sup> ಅವನು ಅಲ್ಲಿಂದ ಹೊರಟು ಬೇತೇಲಿಗೆ ಮೂಡಲಲ್ಲಿರುವ ಗುಡ್ಡಕ್ಕೆ ಬಂದು ತನ್ನ ಗುಡಾರವನ್ನು ಹಾಕಿಸಿ ಇಳುಕೊಂಡನು; ಪಶ್ಚಿಮಕ್ಕೆ ಬೇತೇಲೂ ಪೂರ್ವಕ್ಕೆ ಆಯಿ ಎಂಬ ಊರೂ ಇದ್ದವು. ಅಲ್ಲಿಯೂ ಯೆಹೋವನಿಗೋಸ್ಕರ ಯಜ್ಞವೇದಿಯನ್ನು ಕಟ್ಟಿಸಿ ಆತನ ಹೆಸರನ್ನು ಹೇಳಿಕೊಂಡು ಆರಾಧಿಸಿದನು.",
			"9": "<sup>9</sup> ತರುವಾಯ ಅಬ್ರಾಮನು ಅಲ್ಲಿಂದ ಹೊರಟು ಕಾನಾನ್‍ದೇಶದ ದಕ್ಷಿಣಸೀಮೆಗೆ ಮುಂದೆ ಮುಂದೆ ಹೋಗುತ್ತಾ ಇದ್ದನು.",
			"10": "<sup>10</sup> ಆ ದೇಶದಲ್ಲಿ ಘೋರ ಕ್ಷಾಮವಿದ್ದದರಿಂದ ಅಲ್ಲಿರದೆ, ಐಗುಪ್ತ ದೇಶದಲ್ಲಿ ಕೆಲವು ಕಾಲ ಇರಬೇಕೆಂದು ಆ ಬೆಟ್ಟದ ಸೀಮೆಯಿಂದ ಇಳಿದುಹೋದನು.",
			"11": "<sup>11</sup> ಅವನು ಐಗುಪ್ತದೇಶದ ಹತ್ತಿರಕ್ಕೆ ಬಂದಾಗ, ತನ್ನ ಹೆಂಡತಿಯಾದ ಸಾರಯಳಿಗೆ -",
			"12": "<sup>12</sup> ಕೇಳು, ನೀನು ಸುಂದರಿ ಎಂದು ನಾನು ಬಲ್ಲೆ; ಐಗುಪ್ತದೇಶದವರು ನಿನ್ನನ್ನು ಕಂಡು - ಈಕೆಯು ಇವನ ಹೆಂಡತಿ ಎಂದು ನನ್ನನ್ನು ಕೊಂದು ನಿನ್ನನ್ನು ಉಳಿಸಾರು.",
			"13": "<sup>13</sup> ಆದಕಾರಣ ನೀನು ನನಗೆ ತಂಗಿಯಾಗಬೇಕೆಂದು ಅವರಿಗೆ ಹೇಳು; ಹೀಗೆ ಹೇಳಿದರೆ ನಿನ್ನ ನಿಮಿತ್ತ ನನಗೆ ಹಿತವಾಗುವದು, ನಾನು ನಿನ್ನ ದೆಸೆಯಿಂದ ಸಾಯದೆ ಬದುಕುವೆನು ಎಂದು ಹೇಳಿದನು.",
			"14": "<sup>14</sup> ಅಬ್ರಾಮನು ಐಗುಪ್ತದೇಶಕ್ಕೆ ಬಂದಾಗ ಐಗುಪ್ತ್ಯರು ಅವನ ಸಂಗಡ ಇದ್ದ ಸ್ತ್ರೀಯನ್ನು ನೋಡಿ ಬಹು ಸುಂದರಿಯೆಂದರು.",
			"15": "<sup>15</sup> ಫರೋಹನ ಪ್ರಧಾನರು ಆಕೆಯನ್ನು ನೋಡಿ ಬಂದು ಅವನ ಮುಂದೆ ಹೊಗಳಲಾಗಿ",
			"16": "<sup>16</sup> ಫರೋಹನು ಆ ಸ್ತ್ರೀಯನ್ನು ಅರಮನೆಗೆ ಕರತರಿಸಿ ಆಕೆಯ ನಿಮಿತ್ತ ಅಬ್ರಾಮನಿಗೆ ಉಪಕಾರ ಮಾಡಿದನು. ಆಗ ಅಬ್ರಾಮನಿಗೆ ಕುರಿದನಗಳೂ ಗಂಡು ಹೆಣ್ಣು ಕತ್ತೆಗಳೂ ದಾಸದಾಸಿಯರೂ ಒಂಟೆಗಳೂ ದೊರೆತವು.",
			"17": "<sup>17</sup> ಆದರೆ ಯೆಹೋವನು ಫರೋಹನಿಗೂ ಅವನ ಮನೆಯವರಿಗೂ ಅಬ್ರಾಮನ ಹೆಂಡತಿಯಾದ ಸಾರಯಳ ನಿಮಿತ್ತ ಬಹಳ ಉಪದ್ರವಗಳನ್ನು ಉಂಟುಮಾಡಿ ಬಾಧಿಸಿದನು.",
			"18": "<sup>18</sup> ಆಗ ಫರೋಹನು ಅಬ್ರಾಮನನ್ನು ಕರಿಸಿ - ಯಾಕೆ ಹೀಗೆ ಮಾಡಿದೆ? ಹೆಂಡತಿಯೆಂದು ಯಾಕೆ ನನಗೆ ತಿಳಿಸಲಿಲ್ಲ?",
			"19": "<sup>19</sup> ತಂಗಿಯೆಂದು ಯಾಕೆ ಹೇಳಿದೆ? ಹೀಗೆ ಹೇಳಿದ್ದರಿಂದ ಆಕೆಯನ್ನು ಹೆಂಡತಿಯಾಗಲಿಕ್ಕೆ ತೆಗೆದುಕೊಂಡೆನಲ್ಲಾ. ಇಗೋ ನಿನ್ನ ಹೆಂಡತಿ; ಕರೆದುಕೊಂಡು ಹೋಗು ಎಂದು ಹೇಳಿ",
			"20": "<sup>20</sup> ಅವನ ವಿಷಯವಾಗಿ ತನ್ನ ಸೇವಕರಿಗೆ ಅಪ್ಪಣೆಕೊಡಲು ಅವರು ಅಬ್ರಾಮನನ್ನೂ ಅವನ ಹೆಂಡತಿಯನ್ನೂ ಅವನಿಗಿದ್ದ ಸರ್ವಸ್ವಸಹಿತವಾಗಿ ಸಾಗಕಳುಹಿಸಿದರು."
		},
		"13": {
			"1": "<sup>1</sup> ಹೀಗೆ ಅಬ್ರಾಮನು ತನ್ನದನ್ನೆಲ್ಲಾ ತೆಗೆದುಕೊಂಡು ಹೆಂಡತಿಯನ್ನೂ ಲೋಟನನ್ನೂ ಸಂಗಡ ಕರಕೊಂಡು ಐಗುಪ್ತ ದೇಶವನ್ನು ಬಿಟ್ಟು ಕಾನಾನ್‍ದೇಶದ ದಕ್ಷಿಣ ಪ್ರಾಂತ್ಯಕ್ಕೆ ಏರಿ ಬಂದನು.",
			"2": "<sup>2</sup> ಅಬ್ರಾಮನು ಬಹು ಐಶ್ವರ್ಯವಂತನಾಗಿದ್ದನು; ಅವನಿಗೆ ಪಶುಗಳೂ ಬೆಳ್ಳಿಬಂಗಾರವೂ ಇದ್ದವು.",
			"3": "<sup>3</sup> ಅವನು ದಕ್ಷಿಣ ದೇಶವನ್ನು ಬಿಟ್ಟು ಮುಂದೆ ಮುಂದೆ ಪ್ರಯಾಣ ಮಾಡುತ್ತಾ ಬೇತೇಲಿನವರೆಗೆ ಅಂದರೆ",
			"4": "<sup>4</sup> ಬೇತೇಲಿಗೂ ಆಯಿ ಎಂಬ ಊರಿಗೂ ನಡುವೆ ತಾನು ಪೂರ್ವದಲ್ಲಿ ಗುಡಾರಹಾಕಿಸಿ ಯಜ್ಞವೇದಿಯನ್ನು ಕಟ್ಟಿದ್ದ ಕ್ಷೇತ್ರಕ್ಕೆ ತಿರಿಗಿ ಬಂದು ಅಲ್ಲಿ ಯೆಹೋವನ ಹೆಸರನ್ನು ಹೇಳಿಕೊಂಡು ಆರಾಧಿಸಿದನು.",
			"5": "<sup>5</sup> ಅವನ ಜೊತೆಯಲ್ಲಿದ್ದ ಲೋಟನಿಗೂ ಕುರಿದನ ಗುಡಾರಗಳಿದ್ದವು.",
			"6": "<sup>6</sup> ಆ ಸ್ಥಳವು ಅವರಿಬ್ಬರ ಜೀವನಕ್ಕೆ ಸಾಲದೆಹೋಯಿತು. ಅವರಿಬ್ಬರ ಆಸ್ತಿ ಬಹಳವಾದದರಿಂದ ಒಂದೇ ಸ್ಥಳದಲ್ಲಿ ವಾಸವಾಗಿರುವದು ಅಸಾಧ್ಯವಾಯಿತು.",
			"7": "<sup>7</sup> ಇದರಿಂದ ಅಬ್ರಾಮನ ದನಕಾಯುವವರಿಗೂ ಲೋಟನ ದನಕಾಯುವವರಿಗೂ ಜಗಳ ಹುಟ್ಟಿತು. ಇದಲ್ಲದೆ ಕಾನಾನ್ಯರೂ ಪೆರಿಜೀಯರೂ ಆ ಕಾಲದಲ್ಲಿ ದೇಶದೊಳಗೆ ಇದ್ದರು.",
			"8": "<sup>8</sup> ಹೀಗಿರಲು ಅಬ್ರಾಮನು ಲೋಟನಿಗೆ - ನನಗೂ ನಿನಗೂ, ನನ್ನ ದನಕಾಯುವವರಿಗೂ ನಿನ್ನ ದನಕಾಯುವವರಿಗೂ ಜಗಳವಿರಬಾರದು; ನಾವು ಸಹೋದರರಲ್ಲವೇ.",
			"9": "<sup>9</sup> ದೇಶವೆಲ್ಲಾ ನಿನ್ನೆದುರಿಗೆ ಇದೆ; ದಯವಿಟ್ಟು ನನ್ನನ್ನು ಬಿಟ್ಟು ಪ್ರತ್ಯೇಕ ಹೋಗು. ನೀನು ಎಡಗಡೆಗೆ ಹೋದರೆ ನಾನು ಬಲಗಡೆಗೆ ಹೋಗುವೆನು; ನೀನು ಬಲಗಡೆಗೆ ಹೋದರೆ ನಾನು ಎಡಗಡೆಗೆ ಹೋಗುವೆನು ಎಂದು ಹೇಳಿದನು.",
			"10": "<sup>10</sup> ಲೋಟನು ಕಣ್ಣೆತ್ತಿ ನೋಡಲಾಗಿ ಯೊರ್ದನ್ ಹೊಳೆಯ ಸುತ್ತಲಿನ ಪ್ರದೇಶವು ಚೋಗರೂರಿನ ತನಕ ಎಲ್ಲಾ ಕಡೆಯಲ್ಲಿಯೂ ನೀರಾವರಿಯ ಸ್ಥಳವೆಂದು ತಿಳುಕೊಂಡನು. ಯೆಹೋವನು ಸೊದೋಮ್‍ಗೊಮೋರ ಪಟ್ಟಣಗಳನ್ನು ನಾಶಮಾಡುವದಕ್ಕಿಂತ ಮುಂಚೆ ಆ ಸೀಮೆಯು ಯೆಹೋವನ ವನದಂತೆಯೂ ಐಗುಪ್ತದೇಶದಂತೆಯೂ ನೀರಾವರಿಯಾಗಿತ್ತು.",
			"11": "<sup>11</sup> ಆದದರಿಂದ ಲೋಟನು ಯೊರ್ದನ್ ಹೊಳೆಯ ಸುತ್ತಣ ಪ್ರದೇಶವನ್ನು ಆದುಕೊಂಡು ಮೂಡಣ ಕಡೆಗೆ ಹೊರಟನು. ಹೀಗೆ ಅವರಿಬ್ಬರೂ ಪ್ರತ್ಯೇಕವಾದರು;",
			"12": "<sup>12</sup> ಅಬ್ರಾಮನು ಕಾನಾನ್‍ದೇಶದಲ್ಲಿ ವಾಸಮಾಡಿದನು; ಲೋಟನು ಯೊರ್ದನ್ ಹೊಳೆಯ ಸುತ್ತಣ ಊರುಗಳಲ್ಲಿ ಗುಡಾರ ಹಾಕಿಸಿ ವಸತಿ ಮಾಡುತ್ತಾ ಸೊದೋಮಿಗೆ ಬಂದನು.",
			"13": "<sup>13</sup> ಆದರೆ ಸೊದೋಮ್ ಪಟ್ಟಣದವರು ದುಷ್ಟರೂ ಯೆಹೋವನಿಗೆ ಮಹಾಪರಾಧಿಗಳೂ ಆಗಿದ್ದರು.",
			"14": "<sup>14</sup> ಲೋಟನು ಅಬ್ರಾಮನನ್ನು ಬಿಟ್ಟು ಬೇರೆಯಾದ ನಂತರ ಯೆಹೋವನು ಅಬ್ರಾಮನಿಗೆ - ನೀನಿರುವ ಸ್ಥಳದಿಂದ ದಕ್ಷಿಣೋತ್ತರಪೂರ್ವಪಶ್ಚಿಮಗಳಿಗೆ ಕಣ್ಣೆತ್ತಿ ನೋಡು.",
			"15": "<sup>15</sup> ನೀನು ನೋಡುವ ಈ ದೇಶವನ್ನೆಲ್ಲಾ ನಿನಗೂ ನಿನ್ನ ಸಂತತಿಗೂ ಶಾಶ್ವತವಾಗಿ ಕೊಡುವೆನು.",
			"16": "<sup>16</sup> ನಿನ್ನ ಸಂತಾನದವರನ್ನು ಭೂಮಿಯ ಧೂಳಿನಷ್ಟು ಅಸಂಖ್ಯವಾಗಿ ಮಾಡುವೆನು. ಭೂಮಿಯಲ್ಲಿರುವ ಧೂಳನ್ನು ಲೆಕ್ಕಮಾಡುವದಾದರೆ ನಿನ್ನ ಸಂತಾನವನ್ನೂ ಲೆಕ್ಕಿಸಲಾದೀತು.",
			"17": "<sup>17</sup> ನೀನೆದ್ದು ಈ ದೇಶದ ಎಲ್ಲಾ ಕಡೆಯಲ್ಲಿಯೂ ತಿರುಗಾಡು; ಇದನ್ನು ನಿನಗೆ ಕೊಡುವೆನು ಎಂದು ಹೇಳಿದನು.",
			"18": "<sup>18</sup> ತರುವಾಯ ಅಬ್ರಾಮನು ಅಲ್ಲಿಂದ ಹೊರಟು ಅಲ್ಲಲ್ಲಿ ಗುಡಾರಹಾಕಿಸಿಕೊಳ್ಳುತ್ತಾ ಹೆಬ್ರೋನಿನಲ್ಲಿರುವ ಮಮ್ರೆ ತೋಪಿಗೆ ಬಂದು ಅಲ್ಲೇ ವಾಸಮಾಡಿಕೊಂಡು ಯೆಹೋವನಿಗೆ ಯಜ್ಞವೇದಿಯನ್ನು ಕಟ್ಟಿಸಿದನು."
		},
		"14": {
			"1": "<sup>1</sup> ಆ ದಿವಸಗಳಲ್ಲಿ ಶಿನಾರಿನ ಅರಸನಾದ ಅಮ್ರಾಫೆಲನು, ಎಲ್ಲಸಾರಿನ ಅರಸನಾದ ಅರಿಯೋಕನು. ಏಲಾಮಿನ ಅರಸನಾದ ಕೆದೊರ್ಲಗೋಮರನು, ಗೋಯಿಮದ ಅರಸನಾದ ತಿದ್ಗಾಲನು ಈ ನಾಲ್ವರು",
			"2": "<sup>2</sup> ಈಗ ಲವಣಸಮುದ್ರವಾಗಿರುವ ಸಿದ್ದೀಮ್ ತಗ್ಗಿನೊಳಗೆ ಕೂಡಿದ್ದ ಸೊದೋಮಿನ ಅರಸನಾದ ಬೆರಗನು, ಗೊಮೋರದ ಅರಸನಾದ ಬಿರ್ಶಗನು, ಅದ್ಮಾಹದ ಅರಸನಾದ ಶಿನಾಬನು, ಚೆಬೋಯೀಮಿನ ಅರಸನಾದ ಶೆಮೇಬರನು, ಬೇಲಗಿನ ಅಂದರೆ ಚೋಗರದ ಅರಸನು ಇವರೊಡನೆ ಯುದ್ಧಮಾಡಿದರು.",
			"4": "<sup>4</sup> ಬೆರಗ ಮೊದಲಾದ ಇವರು ಹನ್ನೆರಡು ವರುಷ ಕೆದೊರ್ಲಗೋಮರನಿಗೆ ಅಧೀನರಾಗಿದ್ದು ಹದಿಮೂರನೆಯ ವರುಷದಲ್ಲಿ ತಿರುಗಿಬಿದ್ದರು.",
			"5": "<sup>5</sup> ಹದಿನಾಲ್ಕನೆಯ ವರುಷದಲ್ಲಿ ಕೆದೊರ್ಲಗೋಮರನೂ ಅವನಿಗೆ ಸೇರಿದ್ದ ರಾಜರೂ ಬಂದು ಅಷ್ಟರೋತ್ ಕರ್ನಯಿಮಿನಲ್ಲಿ ರೆಫಾಯರನ್ನೂ ಹಾಮಿನಲ್ಲಿ ಜೂಜ್ಯರನ್ನೂ ಶಾವೆಕಿರ್ಯಾತಯಿಮಿನಲ್ಲಿ ಏಮಿಯರನ್ನೂ ಸೋಲಿಸಿದರು.",
			"6": "<sup>6</sup> ಇದಲ್ಲದೆ ಸೇಯೀರೆಂಬ ಬೆಟ್ಟದ ಸೀಮೆಯಲ್ಲಿ ವಾಸವಾಗಿದ್ದ ಹೋರಿಯರನ್ನು ಅಲ್ಲೇ ಸೋಲಿಸಿ ಮರಳುಕಾಡಿನ ಹತ್ತಿರವಿರುವ ಏಲ್ಪಾರಾನಿನವರೆಗೂ ಹಿಂದಟ್ಟಿದರು.",
			"7": "<sup>7</sup> ಆಮೇಲೆ ಅವರು ಹಿಂದಿರುಗಿಕೊಂಡು ಕಾದೇಶ್ ಎನ್ನುವ ಎನ್ಮಿಷ್ಪಾಟಿಗೆ ಬಂದು ಅಮಾಲೇಕ್ಯರ ಸಮಸ್ತದೇಶವನ್ನೂ ಹಚಚೋನ್‍ತಾಮರಿನಲ್ಲಿ ವಾಸವಾಗಿದ್ದ ಅಮೋರಿಯರನ್ನೂ ಜಯಿಸಿದರು.",
			"8": "<sup>8</sup> ಆಗ ಸೊದೋಮ್, ಗೊಮೋರ, ಅದ್ಮಾಹ, ಚೆಬೋಯೀಮ್, ಚೋಗರೆಂಬ ಬೇಲಗ್ ಈ ಪಟ್ಟಣಗಳ ರಾಜರು ಹೊರಟು ಅವರಿಗೆದುರಾಗಿ,",
			"9": "<sup>9</sup> ಅಂದರೆ ಏಲಾಮಿನ ಅರಸನಾದ ಕೆದೂರ್ಲಗೋಮರ್, ಗೋಯಿಮದ ಅರಸನಾದ ತಿದ್ಗಾಲ, ಶಿನಾರಿನ ಅರಸನಾದ ಅಮ್ರಾಫೆಲ, ಎಲ್ಲಸಾರಿನ ಅರಸನಾದ ಅರಿಯೋಕ ಇವರಿಗೆ ವಿರೋಧವಾಗಿ ಸಿದ್ದೀಮೆಂಬ ತಗ್ಗುಮೈದಾನದಲ್ಲಿ ತಮ್ಮ ದಂಡನ್ನು ನಿಲ್ಲಿಸಿದರು. ಹೀಗೆ ನಾಲ್ಕು ಮಂದಿ ರಾಜರು ಐದು ಮಂದಿ ರಾಜರನ್ನು ಎದುರಿಸಿದರು.",
			"10": "<sup>10</sup> ಆದರೆ ಸಿದ್ದೀಮ್ ತಗ್ಗಿನಲ್ಲಿ ಕಲ್ಲರಗಿನ ಕೆಸರುಕುಣಿಗಳು ತುಂಬಾ ಇದ್ದವು. ಸೊದೋಮ್ ಗೊಮೋರಗಳ ರಾಜರ ಕಡೆಯವರು ಓಡಿಹೋಗುವಾಗ ಆ ಕುಣಿಗಳಲ್ಲಿ ಬಿದ್ದು ಸತ್ತರು; ಉಳಿದವರು ಬೆಟ್ಟಗಳಿಗೆ ಓಡಿಹೋದರು.",
			"11": "<sup>11</sup> ಗೆದ್ದವರು ಸೊದೋಮ್ ಗೊಮೋರ ಪಟ್ಟಣಗಳನ್ನು ಸೂರೆಮಾಡಿ ಅವುಗಳಲ್ಲಿದ್ದ ಎಲ್ಲಾ ಆಸ್ತಿಯನ್ನೂ ದವಸವನ್ನೂ ತೆಗೆದುಕೊಂಡು ಹೋದರು.",
			"12": "<sup>12</sup> ಸೊದೋಮಿನಲ್ಲಿ ವಾಸವಾಗಿದ್ದ ಅಬ್ರಾಮನ ತಮ್ಮನ ಮಗನಾದ ಲೋಟನನ್ನೂ ಅವನ ಆಸ್ತಿ ಸಹಿತ ಹಿಡಿದುಕೊಂಡು ಹೋದರು.",
			"13": "<sup>13</sup> ತಪ್ಪಿಸಿಕೊಂಡವನೊಬ್ಬನು ಇಬ್ರಿಯನಾದ ಅಬ್ರಾಮನ ಬಳಿಗೆ ಬಂದು ಇದನ್ನು ತಿಳಿಸಿದನು. ಅಬ್ರಾಮನು ಅಮೋರಿಯನಾದ ಮಮ್ರೆಯನ ತೋಪಿನ ಬಳಿಯಲ್ಲಿ ವಾಸವಾಗಿದ್ದನು. ಮಮ್ರೆಯನು ಎಷ್ಕೋಲ ಆನೇರರಿಗೆ ಸಹೋದರನು; ಇವರಿಬ್ಬರಿಗೂ ಅಬ್ರಾಮನಿಗೂ ಒಡಂಬಡಿಕೆಯಿತ್ತು.",
			"14": "<sup>14</sup> ಅಬ್ರಾಮನು ತನ್ನ ತಮ್ಮನ ಮಗನು ಸೆರೆಗೆ ಸಿಕ್ಕಿದ್ದನ್ನು ಕೇಳಿ ತನ್ನ ಮನೆಯಲ್ಲೇ ಹುಟ್ಟಿ ಬೆಳೆದ ಶಿಕ್ಷಿತರಾದ ಮುನ್ನೂರ ಹದಿನೆಂಟು ಮಂದಿ ಆಳುಗಳನ್ನು ಯುದ್ಧಕ್ಕೆ ಸಿದ್ಧಮಾಡಿಕೊಂಡು ಹೊರಟು ಆ ರಾಜರನ್ನು ದಾನೂರಿನವರೆಗೆ ಹಿಂದಟ್ಟಿದನು.",
			"15": "<sup>15</sup> ಆಗ ಅವನು ರಾತ್ರಿ ವೇಳೆಯಲ್ಲಿ ತನ್ನ ದಂಡನ್ನು ಎರಡು ಭಾಗ ಮಾಡಿ ತನ್ನ ಭಟರೊಡನೆ ಅವರ ಮೇಲೆ ಬಿದ್ದು ಹೊಡೆದು ದಮಸ್ಕಪಟ್ಟಣದ ಉತ್ತರ ಕಡೆಯಲ್ಲಿರುವ ಹೋಬಾ ಊರಿನ ತನಕ ಹಿಂದಟ್ಟಿದನು.",
			"16": "<sup>16</sup> ರಾಜರು ಅಪಹರಿಸಿದ್ದ ಎಲ್ಲಾ ವಸ್ತುಗಳನ್ನು ಅವನು ತೆಗೆದುಕೊಂಡನು. ತನ್ನ ತಮ್ಮನ ಮಗನಾದ ಲೋಟನನ್ನೂ ಅವನ ಆಸ್ತಿಯನ್ನೂ ಬಿಡಿಸಿಕೊಂಡದ್ದಲ್ಲದೆ ಸೆರೆಯಲ್ಲಿದ್ದ ಸ್ತ್ರೀಯರನ್ನೂ ಮಿಕ್ಕಾದವರನ್ನೂ ತೆಗೆದುಕೊಂಡು ಬಂದನು.",
			"17": "<sup>17</sup> ಅವನು ಕೆದೊರ್ಲಗೋಮರನನ್ನೂ ಅವನೊಂದಿಗೆ ಇದ್ದ ರಾಜರನ್ನೂ ಹೊಡೆದು ಬಂದಮೇಲೆ ಸೊದೋಮಿನ ಅರಸನು ಅವನನ್ನು ಅರಸನ ತಗ್ಗು ಎನ್ನಿಸಿಕೊಳ್ಳುವ ಶಾವೆ ತಗ್ಗಿನಲ್ಲಿ ಎದುರುಗೊಂಡನು.",
			"18": "<sup>18</sup> ಸಾಲೇಮಿನ ಅರಸನಾದ ಮೆಲ್ಕೀಚೆದೆಕನು ಸಹ ಬಂದು ರೊಟ್ಟಿಯನ್ನೂ ದ್ರಾಕ್ಷಾರಸವನ್ನೂ ಕೊಟ್ಟನು. ಇವನು ಪರಾತ್ಪರನಾದ ದೇವರ ಯಾಜಕನಾಗಿದ್ದು ಅಬ್ರಾಮನನ್ನು ಆಶೀರ್ವದಿಸಿ -",
			"19": "<sup>19</sup> ಭೂಮ್ಯಾಕಾಶಗಳನ್ನು ನಿರ್ಮಾಣಮಾಡಿದ ಪರಾತ್ಪರನಾದ ದೇವರ ಆಶೀರ್ವಾದವು ಅಬ್ರಾಮನಿಗೆ ಆಗಲಿ;",
			"20": "<sup>20</sup> ಪರಾತ್ಪರನಾದ ದೇವರು ನಿನ್ನ ಶತೃಗಳನ್ನು ನಿನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸಿದ್ದಕ್ಕಾಗಿ ಆತನಿಗೆ ಸ್ತೋತ್ರ ಎಂದು ಹೇಳಿದನು. ಅಬ್ರಾಮನು ತಾನು ಗೆದ್ದು ತಂದಿದ್ದ ಎಲ್ಲಾ ವಸ್ತುಗಳಲ್ಲಿ ಹತ್ತನೆಯ ಒಂದು ಭಾಗವನ್ನು ಅವನಿಗೆ ಕೊಟ್ಟನು.",
			"21": "<sup>21</sup> ಸೊದೋಮಿನ ಅರಸನು ಅಬ್ರಾಮನಿಗೆ - ನೀನು ಬಿಡಿಸಿದ ಜನರನ್ನು ನನಗೆ ಒಪ್ಪಿಸು;",
			"22": "<sup>22</sup> ಆಸ್ತಿಯನ್ನು ನೀನೇ ತಕ್ಕೋ ಎಂದು ಹೇಳಲು ಅಬ್ರಾಮನು ಅವನಿಗೆ - ಒಂದು ದಾರವನ್ನಾಗಲಿ ಕೆರದ ಬಾರನ್ನಾಗಲಿ ನಿನ್ನದರಲ್ಲಿ ಯಾವದನ್ನೂ ತೆಗೆದುಕೊಳ್ಳುವದಿಲ್ಲವೆಂದು ಭೂಮ್ಯಾಕಾಶಗಳನ್ನು ನಿರ್ಮಾಣಮಾಡಿದ ಪರಾತ್ಪರದೇವರಾಗಿರುವ ಯೆಹೋವನ ಕಡೆಗೆ ಕೈಯೆತ್ತಿ ಪ್ರಮಾಣ ಮಾಡುತ್ತೇನೆ. ಅಬ್ರಾಮನು ನನ್ನ ಸೊತ್ತಿನಿಂದಲೇ ಐಶ್ವರ್ಯವಂತನಾದನೆಂದು ಹೇಳಿಕೊಳ್ಳುವದಕ್ಕೆ ನಿನಗೆ ಆಸ್ಪದವಾಗಬಾರದು, ನನಗೆ ಏನೂ ಬೇಡ.",
			"24": "<sup>24</sup> ಆಳುಗಳು ತಿಂದದ್ದು ಹೊರತು ನನ್ನ ಜೊತೆಯವರಾದ ಆನೇರ ಎಷ್ಕೋಲ ಮಮ್ರೆಯರಿಗೆ ಬರತಕ್ಕ ಪಾಲು ಮಾತ್ರ ಇರಲಿ ಎಂದು ಹೇಳಿದನು."
		},
		"15": {
			"1": "<sup>1</sup> ಈ ಸಂಗತಿಗಳು ನಡೆದ ಮೇಲೆ ಅಬ್ರಾಮನಿಗೆ ದರ್ಶನದಲ್ಲಿ ಯೆಹೋವನ ವಾಕ್ಯವುಂಟಾಯಿತು; ಏನಂದರೆ- ಅಬ್ರಾಮನೇ, ಭಯಪಡಬೇಡ, ನಾನು ನಿನಗೆ ಗುರಾಣಿಯಾಗಿದ್ದೇನೆ; ನಿನಗೋಸ್ಕರ ಅತ್ಯಧಿಕ ಬಹುಮಾನವು ಇಟ್ಟದೆ ಎಂಬುದೇ.",
			"2": "<sup>2</sup> ಅದಕ್ಕೆ ಅಬ್ರಾಮನು - ಕರ್ತನಾದ ಯೆಹೋವನೇ, ನನಗೆ ಏನು ಕೊಟ್ಟರೇನು? ನಾನು ಸಂತಾನವಿಲ್ಲದವನಾಗಿ ಹೋಗುವೆನಾದದರಿಂದ ನನ್ನ ಆಸ್ತಿಯೆಲ್ಲಾ ದಮಸ್ಕದ ಎಲೀಯೆಜರನ ಪಾಲಾಗುವದಲ್ಲಾ ಅಂದನು.",
			"3": "<sup>3</sup> ತಿರಿಗಿ ಅಬ್ರಾಮನು - ನೀನು ನನಗೆ ಸಂತಾನ ಕೊಡಲಿಲ್ಲವಾದ್ದರಿಂದ ನನ್ನ ಮನೆಯಲ್ಲಿ ಹುಟ್ಟಿದವನೇ ನನಗೆ ಬಾಧ್ಯಸ್ಥನಾಗಬೇಕಾಯಿತು ಎಂದು ಹೇಳಲು ಯೆಹೋವನು ಅವನ ಸಂಗಡ ಮಾತಾಡಿ -",
			"4": "<sup>4</sup> ಇವನು ನಿನಗೆ ಬಾಧ್ಯಸ್ಥನಾಗುವದಿಲ್ಲ; ನಿನ್ನ ಹೊಟ್ಟೆಯಲ್ಲೇ ಹುಟ್ಟುವವನು ನಿನಗೆ ಬಾಧ್ಯಸ್ಥನಾಗುವನು ಎಂದು ಹೇಳಿ",
			"5": "<sup>5</sup> ಅವನನ್ನು ಹೊರಗೆ ಕರತಂದು - ಆಕಾಶದ ಕಡೆಗೆ ನೋಡು, ನಕ್ಷತ್ರಗಳನ್ನು ಲೆಕ್ಕಿಸುವದು ನಿನ್ನಿಂದಾದರೆ ಲೆಕ್ಕಿಸು; ನಿನ್ನ ಸಂತಾನವು ಅಷ್ಟಾಗುವದು ಅಂದನು.",
			"6": "<sup>6</sup> ಅಬ್ರಾಮನು ಯೆಹೋವನನ್ನು ನಂಬಿದನು; ಯೆಹೋವನು ಅವನ ನಂಬಿಕೆಯನ್ನು ಅವನ ಲೆಕ್ಕಕ್ಕೆ ನೀತಿಯೆಂದು ಎಣಿಸಿದನು.",
			"7": "<sup>7</sup> ಆಮೇಲೆ ಆತನು ಅವನಿಗೆ - ಈ ದೇಶದ ಬಾಧ್ಯತೆಯನ್ನು ನಿನಗೆ ಕೊಡುವದಕ್ಕೋಸ್ಕರ ನಿನ್ನನ್ನು ಊರ್ ಎಂಬ ಕಲ್ದೀಯರ ಪಟ್ಟಣದಿಂದ ಬರಮಾಡಿದ ಯೆಹೋವನೇ ನಾನು ಎಂದು ಹೇಳಲು ಅವನು -",
			"8": "<sup>8</sup> ಕರ್ತನಾದ ಯೆಹೋವನೇ, ನಾನು ಇದನ್ನು ಬಾಧ್ಯವಾಗಿ ಹೊಂದುವೆನೆಂದು ತಿಳುಕೊಳ್ಳುವದು ಹೇಗೆ ಎಂದು ಕೇಳಿದನು.",
			"9": "<sup>9</sup> ಅದಕ್ಕೆ ಆತನು - ನೀನು ಮೂರು ಮೂರು ವರುಷಗಳ ಮಣಕ, ಆಡು, ಟಗರು ಇವುಗಳನ್ನೂ ಬೆಳವಕ್ಕಿಯನ್ನೂ ಪಾರಿವಾಳದ ಮರಿಯನ್ನೂ ತೆಗೆದುಕೊಂಡು ಬಾ ಅಂದನು.",
			"10": "<sup>10</sup> ಅವನು ಇವುಗಳನ್ನೆಲ್ಲಾ ತೆಗೆದುಕೊಂಡು ಬಂದು ಪಶುಗಳನ್ನು ಕಡಿದು ಎರಡೆರಡು ಹೋಳುಮಾಡಿ ತುಂಡಿಗೆ ತುಂಡನ್ನು ಎದುರಾಗಿ ಇಟ್ಟನು; ಪಕ್ಷಿಗಳನ್ನು ಮಾತ್ರ ಕಡಿಯಲಿಲ್ಲ.",
			"11": "<sup>11</sup> ಆ ಶವಗಳ ಮೇಲೆ ಹದ್ದುಗಳು ಎರಗಲು ಅಬ್ರಾಮನು ಅವುಗಳನ್ನು ಓಡಿಸಿಬಿಟ್ಟನು.",
			"12": "<sup>12</sup> ಹೊತ್ತು ಮುಣುಗುತ್ತಿರುವಾಗ ಅಬ್ರಾಮನಿಗೆ ಗಾಢನಿದ್ರೆ ಹತ್ತಿತು; ಕಾರ್ಗತ್ತಲು ಅವನ ಮೇಲೆ ಕವಿಯಿತು; ಅವನು ಭಯಭ್ರಾಂತನಾದನು.",
			"13": "<sup>13</sup> ಆಗ ಯೆಹೋವನು ಅವನಿಗೆ - ನೀನು ಚೆನ್ನಾಗಿ ತಿಳಿಯತಕ್ಕದ್ದೇನಂದರೆ - ನಿನ್ನ ಸಂತತಿಯವರು ಅನ್ಯದೇಶದಲ್ಲಿ ಕೆಲವು ಕಾಲವಾಸವಾಗಿದ್ದು ಆ ದೇಶದವರಿಗೆ ದಾಸರಾಗುವರು. ನಾನೂರು ವರುಷ ಆ ದೇಶದವರಿಂದ ಬಾಧೆಪಡುವರು.",
			"14": "<sup>14</sup> ಅವರಿಂದ ಬಿಟ್ಟೀಕೆಲಸ ಮಾಡಿಸಿಕೊಂಡ ಜನಾಂಗವನ್ನು ನಾನು ಶಿಕ್ಷಿಸಿದ ನಂತರ ಅವರು ಬಹಳ ಆಸ್ತಿವಂತರಾಗಿ ಆ ದೇಶದಿಂದ ಬಿಡುಗಡೆಯಾಗಿ ಬರುವರು.",
			"15": "<sup>15</sup> ನೀನಂತೂ ಸಮಾಧಾನದೊಡನೆ ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರುವಿ; ತುಂಬಾ ವೃದ್ಧನಾಗಿ ಉತ್ತರಕ್ರಿಯೆಯನ್ನು ಹೊಂದುವಿ.",
			"16": "<sup>16</sup> ನಿನ್ನ ಸಂತತಿಯವರು ನಾಲ್ಕನೆಯ ತಲಾಂತರದಲ್ಲಿ ಇಲ್ಲಿಗೆ ತಿರಿಗಿಬರುವರು; ಅಮೋರಿಯರ ಅಪರಾಧವು ಇನ್ನೂ ಪೂರ್ಣಸ್ಥಿತಿಗೆ ಬರಲಿಲ್ಲ ಎಂದು ಹೇಳಿದನು.",
			"17": "<sup>17</sup> ಹೊತ್ತು ಮುಣುಗಿ ಕಾರ್ಗತ್ತಲಾದಾಗ ಇಗೋ ಹೊಗೆಹಾಯುವ ಒಲೆಯೂ ಉರಿಯುವ ದೀವಟಿಗೆಯೂ ಕಾಣಿಸಿ ಆ ತುಂಡುಗಳ ನಡುವೆ ಹೋದವು.",
			"18": "<sup>18</sup> ಆ ದಿನದಲ್ಲಿ ಯೆಹೋವನು ಅಬ್ರಾಮನ ಸಂಗಡ ಒಡಂಬಡಿಕೆ ಮಾಡಿಕೊಂಡು - ಐಗುಪ್ತದೇಶದ ನದಿಯಿಂದ ಯೂಫ್ರೇಟೀಸ್ ಮಹಾನದಿಯವರೆಗೂ ಈ ದೇಶವನ್ನೆಲ್ಲಾ ನಿನ್ನ ಸಂತತಿಯವರಿಗೆ ಕೊಟ್ಟಿದ್ದೇನೆ ಅಂದನು;",
			"19": "<sup>19</sup> ಅಂದರೆ ಕೇನಿಯರೂ ಕೆನಿಜೀಯರೂ ಕದ್ಮೋನಿಯರೂ",
			"20": "<sup>20</sup> ಹಿತ್ತಿಯರೂ ಪೆರಿಜೀಯರೂ ರೆಫಾಯರೂ ಅಮೋರಿಯರೂ",
			"21": "<sup>21</sup> ಕಾನಾನ್ಯರೂ ಗಿರ್ಗಾಷಿಯರೂ ಯೆಬೂಸಿಯರೂ ವಾಸವಾಗಿರುವ ದೇಶವನ್ನು ಕೊಡುವೆನೆಂದು ವಾಗ್ದಾನ ಮಾಡಿದನು."
		},
		"16": {
			"1": "<sup>1</sup> ಅಬ್ರಾಮನ ಹೆಂಡತಿಯಾದ ಸಾರಯಳಿಗೆ ಮಕ್ಕಳಿರಲಿಲ್ಲ. ಆಕೆಗೆ ಐಗುಪ್ತ ದೇಶದವಳಾದ ಹಾಗರಳೆಂಬ ದಾಸಿಯಿದ್ದಳು.",
			"2": "<sup>2</sup> ಹೀಗಿರಲಾಗಿ ಸಾರಯಳು ಅಬ್ರಾಮನಿಗೆ - ಯೆಹೋವನು ನನಗೆ ಮಕ್ಕಳನ್ನು ಕೊಡಲಿಲ್ಲವಷ್ಟೆ; ನೀನು ನನ್ನ ದಾಸಿಯ ಬಳಿಗೆ ಹೋಗಬೇಕು; ಒಂದು ವೇಳೆ ಅವಳ ಮೂಲಕ ನನಗೆ ಸಂತಾನವಾದೀತು ಎಂದು ಹೇಳಲು ಅಬ್ರಾಮನು ಆಕೆಯ ಮಾತಿಗೆ ಸಮ್ಮತಿಪಟ್ಟನು.",
			"3": "<sup>3</sup> ಅಬ್ರಾಮನು ಹತ್ತು ವರುಷ ಕಾನಾನ್ ದೇಶದಲ್ಲಿ ವಾಸಿಸಿದ ಮೇಲೆ ಅವನ ಹೆಂಡತಿಯಾದ ಸಾರಯಳು ಐಗುಪ್ತ್ಯಳಾದ ಹಾಗರಳೆಂಬ ದಾಸಿಯನ್ನು ಕರೆದು ತನ್ನ ಗಂಡನಿಗೆ ಹೆಂಡತಿಯಾಗಿ ಒಪ್ಪಿಸಿದಳು.",
			"4": "<sup>4</sup> ಅವನು ಹಾಗರಳನ್ನು ಕೂಡಲು ಬಸುರಾದಳು. ತಾನು ಬಸುರಾದೆನೆಂದು ತಿಳುಕೊಂಡಾಗ ಅವಳು ಯಾಜಮಾನಿಯನ್ನು ತಾತ್ಸಾರ ಮಾಡಿದಳು.",
			"5": "<sup>5</sup> ಆಗ ಸಾರಯಳು ಅಬ್ರಾಮನಿಗೆ - ನನ್ನ ಗೋಳು ನಿನಗೆ ತಗಲಲಿ; ನನ್ನ ದಾಸಿಯನ್ನು ನಿನಗೆ ಹೆಂಡತಿಯಾಗಿ ಒಪ್ಪಿಸಿದೆನಷ್ಟೆ; ಅವಳು ತಾನು ಬಸುರಾದೆನೆಂದು ತಿಳಿದು ನನ್ನನ್ನು ತಾತ್ಸಾರಮಾಡುತ್ತಾಳೆ; ನಿನಗೂ ನನಗೂ ಯೆಹೋವನೇ ನ್ಯಾಯತೀರಿಸಲಿ ಎಂದು ಹೇಳಲು ಅಬ್ರಾಮನು -",
			"6": "<sup>6</sup> ನಿನ್ನ ದಾಸಿಯು ನಿನ್ನ ಕೈಯಲ್ಲೇ ಇದ್ದಾಳೆ; ಮನಸ್ಸುಬಂದಂತೆ ಮಾಡು ಅಂದನು. ಆಗ ಸಾರಯಳು ಅವಳನ್ನು ಬಾಧಿಸಲು ಅವಳು ಓಡಿಹೋದಳು.",
			"7": "<sup>7</sup> ಅವಳು ಮರಳುಕಾಡಿನಲ್ಲಿ ಶೂರಿನ ಮಾರ್ಗದ ಒರತೆಯ ಹತ್ತಿರ ಇರುವಾಗ ಯೆಹೋವನ ದೂತನು ಅವಳನ್ನು ಕಂಡು -",
			"8": "<sup>8</sup> ಸಾರಯಳ ದಾಸಿಯಾದ ಹಾಗರಳೇ, ಎಲ್ಲಿಂದ ಬಂದಿ? ಎಲ್ಲಿಗೆ ಹೋಗುತ್ತೀ ಎಂದು ಕೇಳಲು ಅವಳು - ನನ್ನ ಯಜಮಾನಿಯಾದ ಸಾರಯಳ ಬಳಿಯಿಂದ ಓಡಿಹೋಗುತ್ತಾ ಇದ್ದೇನೆ ಅಂದಳು.",
			"9": "<sup>9</sup> ಅದಕ್ಕೆ ಯೆಹೋವನ ದೂತನು - ನೀನು ಯಜಮಾನಿಯ ಬಳಿಗೆ ಹಿಂದಿರುಗಿ ಹೋಗಿ ಅವಳಿಗೆ ತಗ್ಗಿ ನಡೆದುಕೋ ಅಂದನು.",
			"10": "<sup>10</sup> ಅದಲ್ಲದೆ ಯೆಹೋವನ ದೂತನು ಅವಳಿಗೆ - ನಿನಗೆ ಬಹುಸಂತಾನವಾಗ ಮಾಡುವೆನು; ಅದು ಲೆಕ್ಕಿಸಕೂಡದಷ್ಟು ಅಪರಿಮಿತವಾಗುವದು ಎಂದು ಹೇಳಿದನು.",
			"11": "<sup>11</sup> ಮತ್ತು ಯೆಹೋವನ ದೂತನು ಅವಳಿಗೆ - ನೀನು ಬಸುರಾಗಿದ್ದೀಯಷ್ಟೆ; ನಿನಗೆ ಮಗನು ಹುಟ್ಟುವನು; ಯೆಹೋವನು ನಿನ್ನ ಕಷ್ಟದ ಕೂಗಿಗೆ ಕಿವಿಗೊಟ್ಟದ್ದರಿಂದ ಆ ಮಗನಿಗೆ ಇಷ್ಮಾಯೇಲ್ ಎಂದು ಹೆಸರಿಡಬೇಕು.",
			"12": "<sup>12</sup> ಅವನು ಕಾಡುಗತ್ತೆಯಂತೆ ಇರುವನು. ಅವನು ಎಲ್ಲರ ಮೇಲೆ ಕೈಯೆತ್ತುವನು; ಹಾಗೆಯೇ ಅವನ ಮೇಲೆ ಎಲ್ಲರೂ ಕೈಯೆತ್ತುವರು. ತನ್ನ ಅಣ್ಣತಮ್ಮಂದಿರ ಎದುರುಗಡೆಯೇ ವಾಸವಾಗಿರುವನು ಎಂದು ಹೇಳಿ ಹೋದನು.",
			"13": "<sup>13</sup> ಅವಳು - ನನ್ನನ್ನು ನೋಡುವಾತನನ್ನು ನಾನು ಇಲ್ಲಿಯೂ ನೋಡಿದ್ದೇನಲ್ಲಾ ಅಂದುಕೊಂಡು ತನ್ನ ಸಂಗಡ ಮಾತಾಡಿದ್ದ ಯೆಹೋವನಿಗೆ [ಎಲ್ಲವನ್ನೂ] ನೋಡುವ ದೇವರೆಂದು ಹೆಸರಿಟ್ಟಳು.",
			"14": "<sup>14</sup> ಈ ಸಂಗತಿಯಿಂದ ಅಲ್ಲಿರುವ ಬಾವಿಗೆ ಲಹೈರೋಯಿ ಬಾವಿ ಎಂದು ಹೆಸರಾಯಿತು; ಅದು ಕಾದೇಶಿಗೂ ಬೆರೆದಿಗೂ ನಡುವೆ ಅದೆ.",
			"15": "<sup>15</sup> ಹಾಗರಳು ಅಬ್ರಾಮನಿಂದ ಮಗನನ್ನು ಹೆತ್ತಳು; ಅಬ್ರಾಮನು ಅವನಿಗೆ ಇಷ್ಮಾಯೇಲ್ ಎಂದು ಹೆಸರಿಟ್ಟನು.",
			"16": "<sup>16</sup> ಅವಳು ಇಷ್ಮಾಯೇಲನನ್ನು ಹೆತ್ತಾಗ ಅಬ್ರಾಮನು ಎಂಭತ್ತಾರು ವರುಷದವನಾಗಿದ್ದನು."
		},
		"17": {
			"1": "<sup>1</sup> ಅಬ್ರಾಮನು ತೊಂಭತ್ತೊಂಭತ್ತು ವರುಷದವನಾದಾಗ ಯೆಹೋವನು ಅವನಿಗೆ ದರ್ಶನಕೊಟ್ಟು - ನಾನು ಸರ್ವಶಕ್ತನಾದ ದೇವರು; ನನಗೆ ನಡಕೊಂಡು ದೋಷವಿಲ್ಲದವನಾಗಿರು.",
			"2": "<sup>2</sup> ನಾನು ನಿನ್ನ ಸಂಗಡ ಒಡಂಬಡಿಕೆ ಮಾಡಿಕೊಳ್ಳುತ್ತೇನೆ; ಅತ್ಯಧಿಕವಾದ ಸಂತತಿಯನ್ನು ಕೊಡುವೆನು ಅಂದನು.",
			"3": "<sup>3</sup> ಅಬ್ರಾಮನು ಅಡ್ಡಬೀಳಲು ದೇವರು ಅವನ ಸಂಗಡ ಮಾತಾಡಿ -",
			"4": "<sup>4</sup> ನಾನಂತೂ ನಿನಗೆ ವಾಗ್ದಾನಮಾಡುತ್ತೇನೆ; ಏನಂದರೆ ನೀನು ಅನೇಕ ಜನಾಂಗಗಳಿಗೆ ಮೂಲಪುರುಷನಾಗುವಿ.",
			"5": "<sup>5</sup> ಇನ್ನು ಮುಂದೆ ನಿನಗೆ ಅಬ್ರಾಮ ಎಂದು ಹೆಸರಿರುವದಿಲ್ಲ. ನಿನ್ನನ್ನು ಅನೇಕ ಜನಾಂಗಗಳಿಗೆ ಮೂಲ ಪಿತೃವಾಗಿ ನೇಮಿಸಿರುವದರಿಂದ ನಿನಗೆ ಅಬ್ರಹಾಮನೆಂದು ಹೆಸರಿರುವದು.",
			"6": "<sup>6</sup> ನಿನಗೆ ಅತ್ಯಧಿಕವಾದ ಸಂತಾನವನ್ನು ಕೊಡುವೆನು; ನಿನ್ನಿಂದ ಜನಾಂಗಗಳು ಉತ್ಪತ್ತಿಯಾಗಿ ಅರಸರೂ ಹುಟ್ಟುವರು.",
			"7": "<sup>7</sup> ನಿನಗೂ ನಿನ್ನ ಸಂತತಿಗೂ ದೇವರಾಗಿರುವೆನೆಂದು ನಾನು ನಿನಗೋಸ್ಕರವೂ ನಿನ್ನ ಸಂತತಿಯ ಎಲ್ಲಾ ತಲಾಂತರಗಳಿಗೋಸ್ಕರವೂ ಮಾಡಿಕೊಂಡ ನನ್ನ ಒಡಂಬಡಿಕೆಯನ್ನು ಸ್ಥಿರಪಡಿಸುವೆನು.",
			"8": "<sup>8</sup> ನೀನು ಪ್ರವಾಸವಾಗಿರುವ ಕಾನಾನ್‍ದೇಶವನ್ನೆಲ್ಲಾ ನಿನಗೂ ನಿನ್ನ ಸಂತತಿಗೂ ಶಾಶ್ವತ ಸ್ವಾಸ್ತ್ಯವನ್ನಾಗಿ ಕೊಟ್ಟು ಅವರಿಗೆ ದೇವರಾಗಿರುವೆನು ಅಂದನು.",
			"9": "<sup>9</sup> ಇದಲ್ಲದೆ ದೇವರು ಅಬ್ರಹಾಮನಿಗೆ - ನೀನಂತೂ ನನ್ನ ಒಡಂಬಡಿಕೆಯನ್ನು ಕೈಕೊಂಡು ನಡೆಯಬೇಕು. ನೀನು ಮಾತ್ರವಲ್ಲದೆ ನಿನ್ನ ಸಂತತಿಯವರೂ ಎಲ್ಲಾ ತಲಾಂತರಗಳಲ್ಲಿ ಅದನ್ನು ಕೈಕೊಂಡು ನಡೆಯಬೇಕು.",
			"10": "<sup>10</sup> ನೀನೂ ನಿನ್ನ ಹಿಂದೆ ಬರುವ ಸಂತತಿಯವರೂ ಕೈಕೊಳ್ಳಬೇಕಾದ ನನ್ನ ನಿಬಂಧನೆಯು ಯಾವದಂದರೆ - ನಿಮ್ಮಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬ ಗಂಡಸಿಗೂ ಸುನ್ನತಿಯಾಗಬೇಕೆಂಬದೇ.",
			"11": "<sup>11</sup> ನೀವು ಮಾನಸ್ಥಾನದಲ್ಲಿ ಸುನ್ನತಿ ಮಾಡಿಸಿಕೊಳ್ಳಬೇಕು; ಅದೇ ನಿಮಗೂ ನನಗೂ ಉಂಟಾದ ಒಡಂಬಡಿಕೆಗೆ ಗುರುತು.",
			"12": "<sup>12</sup> ಎಲ್ಲಾ ತಲಾಂತರಗಳಲ್ಲಿ ನಿಮ್ಮಲ್ಲಿರುವ ಪ್ರತಿಯೊಬ್ಬ ಗಂಡಸೂ, ಮನೆಯಲ್ಲಿ ಹುಟ್ಟಿದವನಾದರೂ ನಿನ್ನ ಸಂತತಿಯಲ್ಲಿ ಹುಟ್ಟದೆ ಅನ್ಯರಿಂದ ಕ್ರಯಕ್ಕೆ ತೆಗೆದುಕೊಂಡವನಾದರೂ ಎಂಟು ದಿನಗಳ ವಯಸ್ಸುಳ್ಳವನಾದಾಗ ಅವನಿಗೆ ಸುನ್ನತಿ ಮಾಡಿಸಬೇಕು.",
			"13": "<sup>13</sup> ನಿನ್ನ ಮನೆಯಲ್ಲಿ ಹುಟ್ಟಿದವರಿಗೂ ನೀನು ಕ್ರಯಕ್ಕೆ ತೆಗೆದುಕೊಂಡವರಿಗೂ ತಪ್ಪದೆ ಸುನ್ನತಿಯಾಗಬೇಕು. ಹೀಗೆ ನಾನು ಮಾಡುವ ನಿಬಂಧನೆಯ ಗುರುತು ನಿಮ್ಮ ಶರೀರದಲ್ಲೇ ಇದ್ದು ಶಾಶ್ವತವಾದ ಒಡಂಬಡಿಕೆಯನ್ನು ಸೂಚಿಸುವದು.",
			"14": "<sup>14</sup> ಸುನ್ನತಿ ಮಾಡಿಸಿಕೊಳ್ಳದ ಗಂಡಸು ನನ್ನ ಒಡಂಬಡಿಕೆಯನ್ನು ಮೀರಿದವನಾದ ಕಾರಣ ಅವನು ಕುಲದಿಂದ ತೆಗೆದುಹಾಕಲ್ಪಡಬೇಕು ಎಂದು ಹೇಳಿದನು.",
			"15": "<sup>15</sup> ಇದಲ್ಲದೆ ದೇವರು ಅಬ್ರಹಾಮನಿಗೆ - ನೀನು ಇನ್ನು ಮುಂದೆ ನಿನ್ನ ಪತ್ನಿಯನ್ನು ಸಾರಯಳೆಂದು ಕರೆಯದೆ ಸಾರಾ ಎಂದು ಕರೆಯಬೇಕು.",
			"16": "<sup>16</sup> ನಾನು ಆಕೆಯನ್ನು ಆಶೀರ್ವದಿಸಿ ಆಕೆಯಲ್ಲಿ ನಿನಗೆ ಮಗನನ್ನು ಕೊಡುವೆನು. ನಾನು ಆಕೆಯನ್ನು ಆಶೀರ್ವದಿಸಿದ್ದರಿಂದ ಆಕೆಯಿಂದ ಅನೇಕ ಜನಾಂಗಗಳೂ ಅರಸರೂ ಉತ್ಪತ್ತಿಯಾಗುವರು ಎಂದು ಹೇಳಲು ಅಬ್ರಹಾಮನು ಅಡ್ಡಬಿದ್ದು ನಕ್ಕು -",
			"17": "<sup>17</sup> ನೂರು ವರುಷದವನಿಗೆ ಮಗ ಹುಟ್ಟುವದುಂಟೇ? ತೊಂಭತ್ತು ವರುಷದವಳಾದ ಸಾರಳು ಹೆತ್ತಾಳೇ ಎಂದು ಮನಸ್ಸಿನಲ್ಲಿ ಅಂದುಕೊಂಡು ದೇವರಿಗೆ -",
			"18": "<sup>18</sup> ಇಷ್ಮಾಯೇಲನಿದ್ದಾನಲ್ಲಾ, ಅವನೇ ನಿನ್ನ ದಯೆಹೊಂದಿ ಬಾಳಲಿ ಎನ್ನಲು ದೇವರು -",
			"19": "<sup>19</sup> ಹಾಗಲ್ಲ, ನಿನ್ನ ಪತ್ನಿಯಾದ ಸಾರಳಲ್ಲಿಯೇ ನಿನ್ನಿಂದ ಮಗನು ಹುಟ್ಟುವನು. ಅವನಿಗೆ ಇಸಾಕನೆಂದು ಹೆಸರಿಡಬೇಕು. ಅವನ ಸಂಗಡ ನನ್ನ ಒಡಂಬಡಿಕೆಯನ್ನು ಸ್ಥಾಪಿಸಿಕೊಳ್ಳುವೆನು; ಅದು ಅವನ ಸಂತತಿಯವರಲ್ಲಿಯೂ ಶಾಶ್ವತವಾಗಿರಬೇಕು.",
			"20": "<sup>20</sup> ಇಷ್ಮಾಯೇಲನ ವಿಷಯದಲ್ಲಿ ನೀನು ಮಾಡಿದ ಬಿನ್ನಹವನ್ನು ಕಿವಿಗೊಟ್ಟು ಕೇಳಿದ್ದೇನೆ. ಅವನನ್ನೂ ಆಶೀರ್ವದಿಸಿದ್ದೇನೆ; ಅವನನ್ನು ಅಭಿವೃದ್ಧಿಮಾಡಿ ಅವನಿಗೆ ಅತ್ಯಧಿಕವಾದ ಸಂತಾನವನ್ನು ಕೊಡುವೆನು. ಅವನು ಹನ್ನೆರಡು ಮಂದಿ ಅರಸರನ್ನು ಪಡೆಯುವನು; ಅವನಿಂದ ದೊಡ್ಡ ಜನಾಂಗವಾಗುವಂತೆ ಮಾಡುವೆನು.",
			"21": "<sup>21</sup> ಆದರೆ ಆ ನನ್ನ ಒಡಂಬಡಿಕೆಯನ್ನು ಇಸಾಕನ ಸಂಗಡಲೇ ಸ್ಥಾಪಿಸಿಕೊಳ್ಳುತ್ತೇನೆ; ಬರುವ ವರುಷ ಇದೇ ಕಾಲದಲ್ಲಿ ಸಾರಳು ಅವನನ್ನು ಹೆರುವಳು ಅಂದನು.",
			"22": "<sup>22</sup> ಆಗ ದೇವರು ಅಬ್ರಹಾಮನ ಸಂಗಡ ಮಾತಾಡುವದನ್ನು ಮುಗಿಸಿ ಅವನ ಬಳಿಯಿಂದ ಮೇಲಕ್ಕೆ ಹೋದನು.",
			"23": "<sup>23</sup> ಅದೇ ದಿನದಲ್ಲಿ ಅಬ್ರಹಾಮನು ತನ್ನ ಮಗನಾದ ಇಷ್ಮಾಯೇಲನಿಗೂ ತನ್ನ ಮನೆಯಲ್ಲಿದ್ದ ಎಲ್ಲಾ ಗಂಡಸರಿಗೂ, ಅಂದರೆ ಮನೆಯಲ್ಲಿ ಹುಟ್ಟಿದವರಿಗೆ ಮಾತ್ರವಲ್ಲದೆ, ತಾನು ಕ್ರಯಕ್ಕೆ ತೆಗೆದುಕೊಂಡವರಿಗೂ ದೇವರ ಅಪ್ಪಣೆಯಂತೆ ಸುನ್ನತಿಮಾಡಿಸಿದನು.",
			"24": "<sup>24</sup> ಅಬ್ರಹಾಮನಿಗೆ ಸುನ್ನತಿಯಾದಾಗ ಅವನಿಗೆ ತೊಂಭತ್ತೊಂಭತ್ತು ವರುಷವಾಗಿತ್ತು.",
			"25": "<sup>25</sup> ಇಷ್ಮಾಯೇಲನಿಗೆ ಸುನ್ನತಿಯಾದಾಗ ಅವನಿಗೆ ಹದಿಮೂರು ವರುಷವಾಗಿತ್ತು.",
			"26": "<sup>26</sup> ಅಬ್ರಹಾಮನಿಗೂ ಅವನ ಮಗನಾದ ಇಷ್ಮಾಯೇಲನಿಗೂ ಒಂದೇ ದಿನದಲ್ಲಿ ಸುನ್ನತಿಯಾಯಿತು.",
			"27": "<sup>27</sup> ಅವನ ಮನೆಯಲ್ಲಿದ್ದ ಗಂಡಸರೆಲ್ಲರಿಗೂ ಅಂದರೆ ಮನೆಯಲ್ಲಿ ಹುಟ್ಟಿದವರಿಗೆ ಮಾತ್ರವಲ್ಲದೆ ಅನ್ಯರಿಂದ ಕ್ರಯಕ್ಕೆ ತೆಗೆದುಕೊಂಡವರಿಗೂ ಅವನೊಂದಿಗೆ ಸುನ್ನತಿಯಾಯಿತು."
		},
		"18": {
			"1": "<sup>1</sup> ಅಬ್ರಹಾಮನು ಮಧ್ಯಾಹ್ನದಲ್ಲಿ ಮಮ್ರೇತೋಪಿನ ತನ್ನ ಗುಡಾರದ ಬಾಗಲಲ್ಲಿ ಕೂತಿರುವಾಗ ಯೆಹೋವನು ಅವನಿಗೆ ದರ್ಶನಕೊಟ್ಟನು.",
			"2": "<sup>2</sup> ಹೇಗಂದರೆ, ಅಬ್ರಹಾಮನು ಕಣ್ಣೆತ್ತಿ ನೋಡಲು ಅವನ ಎದುರಿನಲ್ಲಿ ಯಾರೋ ಮೂರು ಮಂದಿ ಪುರುಷರು ನಿಂತಿದ್ದರು. ಕೂಡಲೆ ಅವರನ್ನು ಎದುರುಗೊಳ್ಳುವದಕ್ಕೆ ಅವನು ಗುಡಾರದ ಬಾಗಲಿನಿಂದ ಓಡಿ ಹೋಗಿ",
			"3": "<sup>3</sup> ಬೊಗ್ಗಿ ನಮಸ್ಕರಿಸಿ - ಸ್ವಾಮಿ, ದಯವಿರಲಿ; ದಾಸನ ಬಳಿಗೆ ದಯಮಾಡದೆ ಮುಂದೆ ಹೋಗಬೇಡಿರಿ. ನೀವು ದಾಸನಿರುವ ಸ್ಥಳದ ಹತ್ತಿರ ಹಾದು ಹೋಗುತ್ತೀರಲ್ಲಾ.",
			"4": "<sup>4</sup> ನೀರು ತರಿಸಿಕೊಡುತ್ತೇನೆ; ನಿಮ್ಮ ಕಾಲುಗಳನ್ನು ತೊಳೆದುಕೊಂಡು ಮರದ ನೆರಳಿನಲ್ಲಿ ವಿಶ್ರಮಿಸಿಕೊಳ್ಳಿರಿ.",
			"5": "<sup>5</sup> ಸ್ವಲ್ಪ ಆಹಾರ ತರುತ್ತೇನೆ, ಊಟವಾದ ಮೇಲೆ ನೀವು ಮುಂದಕ್ಕೆ ಪ್ರಯಾಣ ಬೆಳಿಸಬಹುದು ಎನ್ನಲು ಅವರು - ನೀನು ಹೇಳಿದಂತೆ ಮಾಡಬಹುದು ಅಂದರು.",
			"6": "<sup>6</sup> ಆಗ ಅಬ್ರಹಾಮನು ಗುಡಾರದಲ್ಲಿದ್ದ ಸಾರಳ ಬಳಿಗೆ ಓಡಿಹೋಗಿ ಆಕೆಗೆ - ಹಸನಾದ ಮೂರು ಸೇರು ಹಿಟ್ಟನ್ನು ನಾದಿ ಬೇಗ ರೊಟ್ಟಿಗಳನ್ನು ಮಾಡು ಎಂದು ಹೇಳಿದನು.",
			"7": "<sup>7</sup> ಆಮೇಲೆ ಅವನು ದನಗಳ ಕಡೆಗೆ ಓಡಿಹೋಗಿ ಕೊಬ್ಬಿದ ಎಳೇ ಕರುವನ್ನು ತೆಗೆದು ಆಳಿನ ಕೈಗೆ ಕೊಟ್ಟನು. ಆಳು ಬೇಗನೆ ಅಡಿಗೆ ಮಾಡಿದನು.",
			"8": "<sup>8</sup> ತರುವಾಯ ಅಬ್ರಹಾಮನು ಹಾಲುಮೊಸರನ್ನೂ ಅಟ್ಟ ಮಾಂಸವನ್ನೂ ತೆಗೆದುಕೊಂಡು ಬಂದು ಆ ಮನುಷ್ಯರಿಗೆ ಬಡಿಸಿದನು. ಅವರು ಮರದ ಕೆಳಗೆ ಊಟಮಾಡುವವರೆಗೂ ಅವನು ಹತ್ತಿರ ನಿಂತುಕೊಂಡು ಉಪಚಾರ ಮಾಡಿದನು.",
			"9": "<sup>9</sup> ಬಳಿಕ ಅವರು ಅವನನ್ನು - ನಿನ್ನ ಪತ್ನಿಯಾದ ಸಾರಳು ಎಲ್ಲಿದ್ದಾಳೆ ಎಂದು ಕೇಳಲು ಅವನು - ಅಗೋ, ಗುಡಾರದಲ್ಲಿದ್ದಾಳೆ ಅಂದನು.",
			"10": "<sup>10</sup> ಅದಕ್ಕಾತನು - ಬರುವ ವರುಷದ ಇದೇ ಕಾಲದಲ್ಲಿ ನಾನು ತಪ್ಪದೆ ತಿರಿಗಿ ನಿನ್ನ ಬಳಿಗೆ ಬರುತ್ತೇನೆ; ಬಂದಾಗ ನಿನ್ನ ಪತ್ನಿಯಾದ ಸಾರಳಿಗೆ ಮಗನಿರುವನು ಅಂದನು. ಆ ಮಾತು ಹಿಂದೆ ಗುಡಾರದ ಬಾಗಲಲ್ಲಿ ನಿಂತಿದ್ದ ಸಾರಳ ಕಿವಿಗೆ ಬಿತ್ತು.",
			"11": "<sup>11</sup> ಅಬ್ರಹಾಮನೂ ಸಾರಳೂ ಬಹು ವೃದ್ಧರಾಗಿದ್ದರು; ಸಾರಳಿಗೆ ಮುಟ್ಟು ನಿಂತು ಹೋಗಿತ್ತು. ಹೀಗಿರಲಾಗಿ ಆಕೆ -",
			"12": "<sup>12</sup> ನನ್ನಂಥ ಮುದುಕಿಗೆ ಭೋಗವಾದೀತೇ? ನನ್ನ ಯಜಮಾನನೂ ಮುದುಕನಲ್ಲವೇ ಎಂದು ತನ್ನೊಳಗೆ ನಕ್ಕಳು.",
			"13": "<sup>13</sup> ಯೆಹೋವನು ಅಬ್ರಹಾಮನಿಗೆ - ಸಾರಳು ನಕ್ಕು ಮುದುಕಿಯಾದ ನಾನು ಮಗುವನ್ನು ಹೆರುವದಾದೀತೇ ಎಂದು ಹೇಳಿದ್ದೇನು?",
			"14": "<sup>14</sup> ಯೆಹೋವನಿಗೆ ಅಸಾಧ್ಯವಾದದ್ದುಂಟೋ? ನಾನು ಹೇಳಿದಂತೆಯೇ ಬರುವ ವರುಷದ ಇದೇ ಕಾಲದಲ್ಲಿ ನಾನು ನಿನ್ನ ಬಳಿಗೆ ಬಂದಾಗ ಸಾರಳಿಗೆ ಮಗನಿರುವನು ಎಂದು ಹೇಳಿದನು.",
			"15": "<sup>15</sup> ಆಗ ಸಾರಳು ಭಯಪಟ್ಟು - ನಾನು ನಗಲಿಲ್ಲ ಎಂದು ಬೊಂಕಲು ಆತನು- ಹಾಗನ್ನಬೇಡಮ್ಮಾ, ನೀನು ನಕ್ಕದ್ದುಂಟು ಅಂದನು.",
			"16": "<sup>16</sup> ಆ ಮನುಷ್ಯರು ಅಲ್ಲಿಂದ ಹೊರಟು ಸೊದೋಮಿನ ಕಡೆಗೆ ನೋಡಿದರು; ಅಬ್ರಹಾಮನು ಅವರನ್ನು ಸಾಗಕಳುಹಿಸುತ್ತಾ ಅವರ ಜೊತೆಯಲ್ಲೇ ಬಂದಿದ್ದನು.",
			"17": "<sup>17</sup> ಆಗ ಯೆಹೋವನು ತನ್ನೊಳಗೆ - ನಾನು ಮಾಡಬೇಕೆಂದಿರುವ ಕಾರ್ಯವನ್ನು ಅಬ್ರಹಾಮನಿಗೆ ಮರೆಮಾಡುವದು ಸರಿಯೋ?",
			"18": "<sup>18</sup> ಅವನಿಂದ ಬಲಿಷ್ಠವಾದ ಮಹಾಜನಾಂಗವು ಹುಟ್ಟಬೇಕಲ್ಲಾ; ಅವನ ಮೂಲಕ ಭೂಮಿಯ ಎಲ್ಲಾ ಜನಾಂಗಗಳಿಗೂ ಆಶೀರ್ವಾದ ಉಂಟಾಗುವದಲ್ಲಾ;",
			"19": "<sup>19</sup> ಅವನು ತನ್ನ ಪುತ್ರಪೌತ್ರರಿಗೆ - ನೀವು ನ್ಯಾಯನೀತಿಗಳನ್ನು ನಡಿಸಿ ಯೆಹೋವನ ಮಾರ್ಗವನ್ನು ಅನುಸರಿಸಬೇಕೆಂದು ಬೋಧಿಸುವಂತೆ ಅವನನ್ನು ನಾನು ಆದುಕೊಂಡೆನಲ್ಲಾ; ಅವನು ಹೀಗೆ ಮಾಡುವದರಿಂದ ಯೆಹೋವನಾದ ನನ್ನ ವಾಗ್ದಾನವು ನೆರವೇರುವದು ಅಂದುಕೊಂಡನು.",
			"20": "<sup>20</sup> ಇದಲ್ಲದೆ ಯೆಹೋವನು - ಸೊದೋಮ್ ಗೊಮೋರಗಳ ವಿಷಯವಾಗಿ ಎಷ್ಟೋ ದೊಡ್ಡ ಮೊರೆ ನನಗೆ ಮುಟ್ಟಿತು; ಆ ಊರಿನವರ ಮೇಲೆ ಹೊರಿಸಿರುವ ಪಾಪವು ಎಷ್ಟೋ ಘೋರವಾದದ್ದು;",
			"21": "<sup>21</sup> ನಾನು ಇಳಿದು ಹೋಗಿ ನನಗೆ ಮುಟ್ಟಿದ ಮೊರೆಯಂತೆಯೇ ಅವರು ಮಾಡಿದರೋ ಇಲ್ಲವೋ ಎಂದು ನೋಡಿ ತಿಳುಕೊಳ್ಳುತ್ತೇನೆ ಅಂದನು.",
			"22": "<sup>22</sup> ಆ ಮನುಷ್ಯರು ಅಲ್ಲಿಂದ ಸೊದೋಮಿನ ಕಡೆಗೆ ಹೋದರು; ಆದರೆ ಅಬ್ರಹಾಮನು ಯೆಹೋವನ ಎದುರಾಗಿ ಇನ್ನೂ ನಿಂತನು.",
			"23": "<sup>23</sup> ಆಗ ಅಬ್ರಹಾಮನು ಹತ್ತಿರಕ್ಕೆ ಬಂದು - ನೀನು ದುಷ್ಟರ ಸಂಗಡ ನೀತಿವಂತರನ್ನೂ ನಾಶಮಾಡುವಿಯಾ?",
			"24": "<sup>24</sup> ಒಂದು ವೇಳೆ ಆ ಊರೊಳಗೆ ಐವತ್ತು ಮಂದಿ ನೀತಿವಂತರಿದ್ದಾರು; ಅದರಲ್ಲಿ ಐವತ್ತು ಮಂದಿ ನೀತಿವಂತರಿದ್ದರೂ ಆ ಸ್ಥಳವನ್ನು ಉಳಿಸದೆ ನಾಶಮಾಡುವಿಯಾ?",
			"25": "<sup>25</sup> ಆ ರೀತಿಯಾಗಿ ದುಷ್ಟರಿಗೂ ಶಿಷ್ಟರಿಗೂ ಭೇದಮಾಡದೆ ದುಷ್ಟರ ಸಂಗಡ ನೀತಿವಂತರನ್ನೂ ಸಂಹರಿಸುವದು ನಿನ್ನಿಂದ ಎಂದಿಗೂ ಆಗಬಾರದು; ಸರ್ವಲೋಕಕ್ಕೆ ನ್ಯಾಯತೀರಿಸುವವನು ನ್ಯಾಯವನ್ನೇ ನಡಿಸುವನಲ್ಲವೇ ಎಂದು ಹೇಳಲು ಯೆಹೋವನು -",
			"26": "<sup>26</sup> ಸೊದೋಮಿನಲ್ಲಿ ಐವತ್ತು ಮಂದಿ ನೀತಿವಂತರು ನನಗೆ ಸಿಕ್ಕಿದರೆ ಅವರ ನಿಮಿತ್ತ ಊರನ್ನೆಲ್ಲಾ ಉಳಿಸುವೆನು ಅಂದನು.",
			"27": "<sup>27</sup> ಅದಕ್ಕೆ ಅಬ್ರಹಾಮನು - ಇಗೋ, ಮಣ್ಣೂ ಬೂದಿಯೂ ಆಗಿರುವ ನಾನು ಸ್ವಾಮಿಯ ಸಂಗಡ ವಾದಿಸುವದಕ್ಕೆ ಧೈರ್ಯಗೊಂಡಿದ್ದೇನೆ;",
			"28": "<sup>28</sup> ಒಂದು ವೇಳೆ ಐವತ್ತು ಮಂದಿ ನೀತಿವಂತರಿಗೆ ಐದು ಮಂದಿ ಕಡಿಮೆಯಾಗಿದ್ದಾರು; ಐದು ಮಂದಿ ಕಡಿಮೆಯಾದದ್ದಕ್ಕೆ ಪಟ್ಟಣವನ್ನೆಲ್ಲಾ ನಾಶಮಾಡುವಿಯಾ ಎಂದು ಹೇಳಲು ಯೆಹೋವನು - ಅಲ್ಲಿ ನಾಲ್ವತ್ತೈದು ಮಂದಿ ಸಿಕ್ಕಿದರೆ ಅದನ್ನು ನಾಶಮಾಡುವದಿಲ್ಲ ಅಂದನು.",
			"29": "<sup>29</sup> ಅಬ್ರಹಾಮನು ಆತನ ಸಂಗಡ ಇನ್ನೂ ಮಾತಾಡಿ - ಒಂದು ವೇಳೆ ಅಲ್ಲಿ ನಾಲ್ವತ್ತು ಮಂದಿ ಸಿಕ್ಕಾರು ಎನ್ನಲು ಆತನು - ನಾಲ್ವತ್ತು ಮಂದಿ ಸಿಕ್ಕಿದರೆ ಆ ಪಟ್ಟಣವನ್ನು ನಾಶಮಾಡುವದಿಲ್ಲ ಅಂದನು.",
			"30": "<sup>30</sup> ಅಬ್ರಹಾಮನು - ಸ್ವಾಮೀ, ಕೋಪಮಾಡಬಾರದು; ಇನ್ನೂ ಮಾತಾಡುತ್ತೇನೆ; ಒಂದು ವೇಳೆ ಮೂವತ್ತು ಮಂದಿ ಅಲ್ಲಿ ಸಿಕ್ಕಾರು ಎನ್ನಲು ಆತನು - ಅಲ್ಲಿ ಮೂವತ್ತು ಮಂದಿ ಸಿಕ್ಕಿದರೆ ಅದನ್ನು ನಾಶಮಾಡುವದಿಲ್ಲ ಅಂದನು.",
			"31": "<sup>31</sup> ಅವನು - ಇಗೋ, ಸ್ವಾಮಿಯ ಸಂಗಡ ಮಾತಾಡುವದಕ್ಕೆ ಧೈರ್ಯಗೊಂಡಿದ್ದೇನೆ; ಒಂದು ವೇಳೆ ಇಪ್ಪತ್ತು ಮಂದಿ ಅಲ್ಲಿ ಸಿಕ್ಕಾರು ಎನ್ನಲು ಆತನು - ಇಪ್ಪತ್ತು ಮಂದಿಯಿದ್ದರೆ ಅವರ ನಿಮಿತ್ತ ಅದನ್ನು ಉಳಿಸುವೆನು, ನಾಶಮಾಡುವದಿಲ್ಲ ಅಂದನು.",
			"32": "<sup>32</sup> ಅಬ್ರಹಾಮನು - ಸ್ವಾಮೀ, ನಿನಗೆ ಸಿಟ್ಟಾಗಬಾರದು; ಇನ್ನು ಒಂದೇ ಸಾರಿ ಮಾತಾಡುತ್ತೇನೆ; ಒಂದು ವೇಳೆ ಹತ್ತು ಮಂದಿ ಸಿಕ್ಕಾರು ಎನ್ನಲು ಆತನು - ಹತ್ತು ಮಂದಿಯ ನಿಮಿತ್ತವೂ ಅದನ್ನು ಉಳಿಸುವೆನು, ನಾಶಮಾಡುವದಿಲ್ಲ ಅಂದನು.",
			"33": "<sup>33</sup> ಯೆಹೋವನು ಅಬ್ರಹಾಮನ ಸಂಗಡ ಮಾತಾಡುವದನ್ನು ಮುಗಿಸಿ ಹೊರಟುಹೋದನು; ಅಬ್ರಹಾಮನು ತನ್ನ ಸ್ಥಳಕ್ಕೆ ಹಿಂದಿರುಗಿ ಹೋದನು."
		},
		"19": {
			"1": "<sup>1</sup> ಆ ದೂತರಿಬ್ಬರು ಸಾಯಂಕಾಲದಲ್ಲಿ ಸೊದೋಮಿಗೆ ಬಂದಾಗ ಲೋಟನು ಸೊದೋಮಿನ ಊರುಬಾಗಲಲ್ಲಿ ಕೂತುಕೊಂಡಿದ್ದನು. ಅವನು ಅವರನ್ನು ಕಂಡಾಗ ಎದ್ದು ಎದುರುಗೊಂಡು ಬೊಗ್ಗಿ ನಮಸ್ಕರಿಸಿ -",
			"2": "<sup>2</sup> ಸ್ವಾಮಿಗಳೇ, ನಿಮ್ಮ ದಾಸನ ಮನೆಗೆ ಬಂದು ಇಳುಕೊಂಡು ಕಾಲುಗಳನ್ನು ತೊಳಕೊಳ್ಳಿರಿ; ಬೆಳಿಗ್ಗೆ ನಿಮ್ಮ ಮಾರ್ಗ ಹಿಡುಕೊಂಡು ಹೋಗಬಹುದು ಅಂದನು. ಅವರು - ಹಾಗಲ್ಲ, ಬೀದಿಯಲ್ಲೇ ರಾತ್ರಿ ಕಳೆಯುತ್ತೇವೆ,",
			"3": "<sup>3</sup> ಅನ್ನಲು ಅವನು ಅವರನ್ನು ಬಹಳ ಬಲವಂತಮಾಡಿದ್ದರಿಂದ ಅವರು ಅವನ ಬಳಿಯಲ್ಲಿ ಇಳುಕೊಳ್ಳುವದಕ್ಕೆ ಒಪ್ಪಿದರು. ಅವರು ಮನೆಗೆ ಬಂದಾಗ ಅವನು ಹುಳಿಯಿಲ್ಲದ ರೊಟ್ಟಿಗಳನ್ನು ಸುಡಿಸಿ ಔತಣವನ್ನು ಮಾಡಿಸಲು ಅವರು ಊಟಮಾಡಿದರು.",
			"4": "<sup>4</sup> ಅವರು ಮಲಗುವದಕ್ಕಿಂತ ಮುಂಚೆ ಹುಡುಗರು ಮುದುಕರು ಸಹಿತವಾಗಿ ಸೊದೋಮಿನ ಪಟ್ಟಣದವರೆಲ್ಲರೂ ಒಗ್ಗಟ್ಟಾಗಿ ಮನೆಯನ್ನು ಸುತ್ತಿಕೊಂಡು ಲೋಟನಿಗೆ -",
			"5": "<sup>5</sup> ಈ ರಾತ್ರಿ ನಿನ್ನ ಬಳಿಗೆ ಬಂದ ಮನುಷ್ಯರು ಎಲ್ಲಿ? ಅವರನ್ನು ಹೊರಕ್ಕೆ ತೆಗೆದುಕೊಂಡು ಬಾ; ಅವರೊಡನೆ ನಮಗೆ ಸಂಗಮವಾಗಬೇಕೆಂದು ಕೂಗಿ ಹೇಳಿದರು.",
			"6": "<sup>6</sup> ಲೋಟನು ಅವರ ಬಳಿಗೆ ಹೊರಕ್ಕೆ ಬಂದು ತನ್ನ ಹಿಂದೆ ಕದಹಾಕಿ -",
			"7": "<sup>7</sup> ಅಣ್ಣಂದಿರೇ, ಈ ಪಾತಕವನ್ನು ಮಾಡಬೇಡಿರಿ. ಕೇಳಿರಿ,",
			"8": "<sup>8</sup> ನನಗೆ ಪುರುಷರನ್ನರಿಯದ ಇಬ್ಬರು ಹೆಣ್ಣುಮಕ್ಕಳು ಇದ್ದಾರೆ; ಅವರನ್ನಾದರೂ ನಿಮಗೆ ತಂದು ಒಪ್ಪಿಸೇನು; ಮನಸ್ಸು ಬಂದಂತೆ ಮಾಡಬಹುದು; ಆದರೆ ಆ ಮನುಷ್ಯರು ನನ್ನ ಆಶ್ರಯಕ್ಕೆ ಬಂದವರು; ಅವರಿಗೆ ಏನೂ ಮಾಡಕೂಡದು ಎಂದು ಹೇಳಲು ಅವರು -",
			"9": "<sup>9</sup> ದಾರಿ ಬಿಡು ಎಂದು ಹೇಳಿ - ಇವನು ಯಾರೋ ಒಬ್ಬ ಪರದೇಶಿಯಾಗಿ ಬಂದು ಈಗ ನ್ಯಾಯ ಹೇಳುವವನಾಗಬೇಕೋ; ಆ ಮನುಷ್ಯರಿಗೆ ಕೇಡು ಮಾಡುವದಕ್ಕಿಂತ ಹೆಚ್ಚಾಗಿ ನಿನಗೇ ಕೇಡು ಮಾಡುತ್ತೇವೆ ಎಂದು ಹೇಳಿ ಲೋಟನ ಮೇಲೆ ಬಿದ್ದು ಬಹಳವಾಗಿ ಇರುಕಿಸಿ ಕದವನ್ನು ಒಡೆದು ಬಿಡಬೇಕೆಂದಿದ್ದರು.",
			"10": "<sup>10</sup> ಆದರೆ ಒಳಗಿದ್ದ ಆ ಮನುಷ್ಯರು ಕೈಚಾಚಿ ಲೋಟನನ್ನು ತಮ್ಮ ಕಡೆಗೆ ಮನೆಯೊಳಗೆ ಸೇರಿಸಿ ಕದಮುಚ್ಚಿದರು.",
			"11": "<sup>11</sup> ಇದಲ್ಲದೆ ಅವರು ಮನೆಯ ಹೊರಗಿದ್ದ ಮನುಷ್ಯರಿಗೆ ಚಿಕ್ಕವರಿಗೂ ದೊಡ್ಡವರಿಗೂ ಕೂಡ ಕಣ್ಣು ಮೊಬ್ಬಾಗುವಂತೆ ಮಾಡಿದ್ದರಿಂದ ಇವರು ಬಾಗಿಲು ಯಾವದೆಂದು ತಿಳಿಯದೆ ಬೇಸರಗೊಂಡರು.",
			"12": "<sup>12</sup> ಆಗ ಆ ದೂತರು ಲೋಟನಿಗೆ - ಇಲ್ಲಿ ನಿನಗೆ ಇನ್ನಾರಿದ್ದಾರೆ? ಅಳಿಯಂದಿರನ್ನೂ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನೂ ಪಟ್ಟಣದಲ್ಲಿ ನಿನಗಿರುವ ಬೇರೆ ಎಲ್ಲರನ್ನೂ ಊರಹೊರಕ್ಕೆ ಕರೆದುಕೊಂಡು ಬಾ.",
			"13": "<sup>13</sup> ನಾವು ಈ ಸ್ಥಳವನ್ನು ನಾಶಮಾಡುವದಕ್ಕೆ ಬಂದವರು. ಇಲ್ಲಿಯವರ ವಿಷಯವಾಗಿ ಬಲು ದೊಡ್ಡ ಮೊರೆಯು ಯೆಹೋವನಿಗೆ ಮುಟ್ಟಿದ್ದರಿಂದ ಇವರನ್ನು ನಾಶಮಾಡುವದಕ್ಕಾಗಿ ಆತನು ನಮ್ಮನ್ನು ಕಳುಹಿಸಿದ್ದಾನೆ ಎಂದು ಹೇಳಿದರು.",
			"14": "<sup>14</sup> ಆಗ ಲೋಟನು ಹೊರಕ್ಕೆ ಹೋಗಿ ತನ್ನ ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ಗೊತ್ತುಮಾಡಿಕೊಂಡಿದ್ದ ಅಳಿಯಂದಿರಿಗೆ ಈ ಸಂಗತಿಯನ್ನು ತಿಳಿಸಿ - ನೀವೆದ್ದು ಈ ಸ್ಥಳವನ್ನು ಬಿಟ್ಟು ಹೋಗಿರಿ, ಯೆಹೋವನು ಈ ಊರನ್ನು ನಾಶಮಾಡುತ್ತಾನೆ ಎಂದು ಹೇಳಿದನು. ಆದರೆ ಅವನು ಅವರಿಗೆ ಗೇಲಿಮಾಡುವವನಾಗಿ ಕಾಣಿಸಿದನು.",
			"15": "<sup>15</sup> ಹೊತ್ತು ಮೂಡುವದಕ್ಕೆ ಮುಂಚೆ ಆ ದೂತರು ಲೋಟನಿಗೆ - ನೀನೆದ್ದು ಇಲ್ಲಿರುವ ನಿನ್ನ ಹೆಂಡತಿಯನ್ನೂ ನಿನ್ನ ಇಬ್ಬರು ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನೂ ಬೇಗ ಕರಕೊಂಡು ಹೋಗು; ಊರಿಗೆ ಉಂಟಾಗುವ ದಂಡನೆಯಿಂದ ನಿನಗೂ ನಾಶವುಂಟಾದೀತು ಎಂದು ಹೇಳಿ ತ್ವರೆಪಡಿಸಿದರು.",
			"16": "<sup>16</sup> ಅವನು ತಡಮಾಡಲು ಯೆಹೋವನು ಅವನನ್ನು ಕನಿಕರಿಸಿದ್ದರಿಂದ ಆ ಮನುಷ್ಯರು ಅವನನ್ನೂ ಅವನ ಹೆಂಡತಿ ಮಕ್ಕಳನ್ನೂ ಕೈಹಿಡಿದು ಹೊರಗೆ ತಂದು ಊರಾಚೆಗೆ ಬಿಟ್ಟರು. ಹೊರಗೆ ತಂದ ಮೇಲೆ ಆತನು -",
			"17": "<sup>17</sup> ಓಡಿಹೋಗು, ಪ್ರಾಣವನ್ನು ಉಳಿಸಿಕೋ; ಹಿಂದಕ್ಕೆ ನೋಡಬೇಡ; ಈ ಪ್ರದೇಶದೊಳಗೆ ಎಲ್ಲಿಯೂ ನಿಲ್ಲದೆ ಬೆಟ್ಟದ ಸೀಮೆಗೆ ಓಡಿಹೋಗು; ನಿನಗೂ ನಾಶವುಂಟಾದೀತು ಅಂದನು.",
			"18": "<sup>18</sup> ಅದಕ್ಕೆ ಲೋಟನು - ಸ್ವಾಮಿ, ಅದು ನನ್ನಿಂದಾಗದು;",
			"19": "<sup>19</sup> ನೀನು ನಿನ್ನ ದಾಸನ ಮೇಲೆ ದಯವಿಟ್ಟು ನನ್ನ ಪ್ರಾಣವನ್ನು ಉಳಿಸಿದ್ದು ಬಹು ವಿಶೇಷವಾದ ಉಪಕಾರವೇ; ಆದರೆ ಬೆಟ್ಟಕ್ಕೆ ಓಡಿಹೋಗಲಾರೆನು: ನಾನು ಹೋಗುತ್ತಿರುವಾಗ ಆ ವಿಪತ್ತು ನನಗೂ ಉಂಟಾಗಿ ಸತ್ತೇನು.",
			"20": "<sup>20</sup> ಅಗೋ, ಅಲ್ಲಿ ಒಂದು ಊರು ಹತ್ತಿರವಾಗಿದೆ; ಅದು ಸಣ್ಣದು; ಅಲ್ಲಿಗಾದರೂ ಹೋಗುವದಕ್ಕೆ ಅಪ್ಪಣೆಯಾದರೆ ನನ್ನ ಪ್ರಾಣ ಉಳಿಯುವದು; ಆ ಊರು ಸಣ್ಣದಲ್ಲವೇ ಅಂದನು.",
			"21": "<sup>21</sup> ಅದಕ್ಕಾತನು - ಈ ವಿಷಯದಲ್ಲಿಯೂ ನಿನಗೆ ಅನುಗ್ರಹಮಾಡಿದ್ದೇನೆ, ನೋಡು; ನೀನು ಹೇಳಿದ ಊರನ್ನು ನಾನು ಕೆಡಹುವದಿಲ್ಲ.",
			"22": "<sup>22</sup> ಬೇಗ ಅಲ್ಲಿಗೆ ಹೋಗಿ ತಪ್ಪಿಸಿಕೋ; ನೀನು ಅಲ್ಲಿಗೆ ಮುಟ್ಟುವ ತನಕ ನಾನೇನೂ ಮಾಡುವದಕ್ಕಾಗುವದಿಲ್ಲ ಅಂದನು. ಇದರಿಂದ ಆ ಊರಿಗೆ ಚೋಗರ್ ಎಂದು ಹೆಸರಾಯಿತು.",
			"23": "<sup>23</sup> ಲೋಟನು ಚೋಗರಿಗೆ ಮುಟ್ಟುವಷ್ಟರಲ್ಲಿ ಸೂರ್ಯನು ಮೂಡಿದನು.",
			"24": "<sup>24</sup> ಆಗ ಯೆಹೋವನು ಸೊದೋಮ್ ಗೊಮೋರಗಳ ಮೇಲೆ",
			"25": "<sup>25</sup> ಆಕಾಶದಿಂದ ಅಗ್ನಿಗಂಧಕಗಳನ್ನು ಸುರಿಸಿ ಆ ಪಟ್ಟಣಗಳನ್ನೂ ಸುತ್ತಲಿರುವ ಸೀಮೆಯೆಲ್ಲವನ್ನೂ ಊರುಗಳಲ್ಲಿದ್ದ ಜನರೆಲ್ಲರನ್ನೂ ಭೂಮಿಯ ಮೇಲಣ ಎಲ್ಲಾ ಬೆಳೆಯನ್ನೂ ಹಾಳುಮಾಡಿದನು.",
			"26": "<sup>26</sup> ಲೋಟನ ಹೆಂಡತಿ ಅವನ ಹಿಂದೆ ಬರುತ್ತಿರುವಾಗ ಹಿಂದಿರುಗಿ ನೋಡಿದ್ದರಿಂದ ಉಪ್ಪಿನ ಕಂಬವಾದಳು.",
			"27": "<sup>27</sup> ಇತ್ತಲಾಗಿ ಅಬ್ರಹಾಮನು ಬೆಳಿಗ್ಗೆ ಎದ್ದು ತಾನು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನಿಂತಿದ್ದ ಸ್ಥಳಕ್ಕೆ ತಿರಿಗಿ ಬಂದು",
			"28": "<sup>28</sup> ಸೊದೋಮ್ ಗೊಮೋರಗಳ ಕಡೆಗೂ ಆ ಎಲ್ಲಾ ಸೀಮೆಯ ಕಡೆಗೂ ನೋಡಿದಾಗ, ಅಯ್ಯೋ, ಆ ಪ್ರದೇಶದಿಂದ ಹೊಗೆಯು ಆವಿಗೆಯ ಹೊಗೆಯಂತೆ ಏರಿತು.",
			"29": "<sup>29</sup> ದೇವರು ಆ ಸೀಮೆಯ ಪಟ್ಟಣಗಳನ್ನು ನಾಶಮಾಡಿದಾಗ ಲೋಟನು ವಾಸವಾಗಿದ್ದ ಊರುಗಳನ್ನು ಕೆಡವಿದರೂ ಅಬ್ರಹಾಮನನ್ನು ನೆನಸಿ ಲೋಟನನ್ನು ತಪ್ಪಿಸಿ ಕಾಪಾಡಿದನು.",
			"30": "<sup>30</sup> ಲೋಟನು ಚೋಗರಿನಲ್ಲಿರುವದಕ್ಕೆ ಅಂಜಿ ತನ್ನಿಬ್ಬರು ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ಕರಕೊಂಡು ಬೆಟ್ಟವನ್ನು ಹತ್ತಿ ಅಲ್ಲಿ ವಾಸಮಾಡಿದನು. ಅವನೂ ಅವನ ಇಬ್ಬರು ಹೆಣ್ಣುಮಕ್ಕಳೂ ಒಂದು ಗವಿಯಲ್ಲಿ ವಾಸಿಸಿದರು.",
			"31": "<sup>31</sup> ಹೀಗಿರುವಲ್ಲಿ ಹಿರೀಮಗಳು ತನ್ನ ತಂಗಿಗೆ - ನಮ್ಮ ತಂದೆ ಮುದುಕನಷ್ಟೆ; ಸರ್ವಲೋಕದ ಪದ್ಧತಿಯ ಮೇರೆಗೆ ನಮ್ಮನ್ನು ಮದುವೆ ಮಾಡಿಕೊಳ್ಳುವ ಪುರುಷರು ಎಲ್ಲಿಯೂ ಇಲ್ಲ;",
			"32": "<sup>32</sup> ನಾವು ತಂದೆಗೆ ದ್ರಾಕ್ಷಾರಸವನ್ನು ಕುಡಿಸಿ, ಅವನ ಸಂಗಡ ಮಲಗಿಕೊಂಡು ತಂದೆಯಿಂದ ಸಂತಾನವನ್ನು ಪಡಕೊಳ್ಳೋಣ ಎಂದು ಹೇಳಿದಳು.",
			"33": "<sup>33</sup> ಆ ರಾತ್ರಿ ಅವರು ತಂದೆಗೆ ದ್ರಾಕ್ಷಾರಸವನ್ನು ಕುಡಿಸಿದಾಗ ಹಿರೀ ಮಗಳು ಅವನ ಸಂಗಡ ಮಲಗಿಕೊಂಡಳು; ಯಾವಾಗ ಮಲಗಿಕೊಂಡಳೋ ಯಾವಾಗ ಎದ್ದು ಹೋದಳೋ ಅವನಿಗೇನೂ ತಿಳಿಯಲಿಲ್ಲ.",
			"34": "<sup>34</sup> ಮಾರಣೇ ದಿನ ಹಿರಿಯವಳು ಕಿರಿಯವಳಿಗೆ - ನಿನ್ನೆಯ ರಾತ್ರಿ ನಾನೇ ಅಪ್ಪನ ಸಂಗಡ ಮಲಗಿಕೊಂಡೆನು; ಈ ರಾತ್ರಿಯೂ ಅವನಿಗೆ ದ್ರಾಕ್ಷಾರಸವನ್ನು ಕುಡಿಸೋಣ; ಆಮೇಲೆ ನೀನು ಅವನ ಸಂಗಡ ಮಲಗಿಕೋ; ಹೀಗೆ ನಮ್ಮ ತಂದೆಯಿಂದ ಸಂತಾನವನ್ನು ಪಡೆದುಕೊಳ್ಳೋಣ ಎಂದು ಹೇಳಿದಳು.",
			"35": "<sup>35</sup> ಆ ರಾತ್ರಿಯೂ ಅವರು ತಂದೆಗೆ ದ್ರಾಕ್ಷಾರಸವನ್ನು ಕುಡಿಸಿದ ಮೇಲೆ ಕಿರೀ ಮಗಳು ಅವನ ಸಂಗಡ ಮಲಗಿಕೊಂಡಳು; ಅವಳು ಯಾವಾಗ ಮಲಗಿದಳೋ ಯಾವಾಗ ಎದ್ದು ಹೋದಳೋ ಅವನಿಗೇನೂ ತಿಳಿಯಲಿಲ್ಲ.",
			"36": "<sup>36</sup> ಹೀಗೆ ಲೋಟನ ಇಬ್ಬರು ಹೆಣ್ಣುಮಕ್ಕಳು ತಂದೆಯಿಂದ ಬಸುರಾದರು.",
			"37": "<sup>37</sup> ಹಿರಿಯವಳು ಗಂಡು ಮಗುವನ್ನು ಹೆತ್ತು ಅದಕ್ಕೆ ಮೋವಾಬ್ ಎಂದು ಹೆಸರಿಟ್ಟಳು. ಇಂದಿನವರೆಗೂ ಇರುವ ಮೋವಾಬ್ಯರಿಗೆ ಅವನೇ ಮೂಲ ಪುರುಷನು.",
			"38": "<sup>38</sup> ಕಿರೀ ಮಗಳು ಗಂಡು ಮಗುವನ್ನು ಹೆತ್ತು ಅದಕ್ಕೆ ಬೆನಮ್ಮಿ ಎಂದು ಹೆಸರಿಟ್ಟಳು. ಇಂದಿನವರೆಗೂ ಇರುವ ಅಮ್ಮೋನಿಯರಿಗೆ ಇವನೇ ಮೂಲ ಪುರುಷನು."
		},
		"20": {
			"1": "<sup>1</sup> ಅಬ್ರಹಾಮನು ಅಲ್ಲಿಂದ ಕಾನಾನ್ ದೇಶದ ದಕ್ಷಿಣ ಸೀಮೆಗೆ ಹೋಗುತ್ತಾ ಹೋಗುತ್ತಾ ಕಾದೇಶಿಗೂ ಶೂರಿಗೂ ನಡುವೆ ವಾಸಮಾಡಿಕೊಂಡು ಕೆಲವು ಕಾಲ ಗೆರಾರಿನಲ್ಲಿ ಇದ್ದನು.",
			"2": "<sup>2</sup> ಅವನು ತನ್ನ ಹೆಂಡತಿಯಾದ ಸಾರಳನ್ನು ತಂಗಿಯೆಂದು ಹೇಳಿದನಾದ್ದರಿಂದ ಗೆರಾರಿನ ಅರಸನಾದ ಅಬೀಮೆಲೆಕನು ಕರೇಕಳುಹಿಸಿ ಆಕೆಯನ್ನು ತನ್ನ ಮನೆಯಲ್ಲಿ ಸೇರಿಸಿಕೊಂಡನು.",
			"3": "<sup>3</sup> ಆದರೆ ರಾತ್ರಿಯಲ್ಲಿ ದೇವರು ಅಬೀಮೆಲೆಕನ ಕನಸಿನಲ್ಲಿ ಬಂದು - ನೀನು ಆ ಸ್ತ್ರೀಯನ್ನು ಸೇರಿಸಿಕೊಂಡದರಿಂದ ಸಾಯತಕ್ಕವನಾಗಿದ್ದಿ; ಆಕೆ ಮುತ್ತೈದೆ ಅಂದನು.",
			"4": "<sup>4</sup> ಅಬೀಮೆಲೆಕನು ಆಕೆಯನ್ನು ಕೂಡಲಿಲ್ಲವಾದ್ದರಿಂದ ಅವನು - ಸ್ವಾಮೀ, ನಿರಪರಾಧಿಯಾದ ಜನವನ್ನೂ ನಾಶಮಾಡುವಿಯಾ?",
			"5": "<sup>5</sup> ಆ ಮನುಷ್ಯನು ಈಕೆ ತನಗೆ ತಂಗಿಯಾಗಬೇಕೆಂದು ತಾನೇ ಹೇಳಲಿಲ್ಲವೇ. ಈಕೆಯೂ ಅವನು ತನಗೆ ಅಣ್ಣನಾಗಬೇಕೆಂದು ಹೇಳಿದಳು. ನಾನು ಯರ್ಥಾರ್ಥಮನಸ್ಸಿನಿಂದಲೂ ಶುದ್ಧಹಸ್ತದಿಂದಲೂ ಇದನ್ನು ಮಾಡಿದೆನು ಅಂದನು.",
			"6": "<sup>6</sup> ಅದಕ್ಕೆ ದೇವರು - ನೀನು ಇದನ್ನು ಯರ್ಥಾರ್ಥಮನಸ್ಸಿನಿಂದ ಮಾಡಿದೆ ಎಂಬುದನ್ನು ನಾನು ಬಲ್ಲೆ; ಆದಕಾರಣ ನಾನಂತೂ ನೀನು ನನಗೆ ವಿರುದ್ಧವಾಗಿ ಪಾಪಮಾಡದಂತೆ ನಿನ್ನನ್ನು ತಡೆದು ಆಕೆಯನ್ನು ಮುಟ್ಟಗೊಡಿಸಲಿಲ್ಲ.",
			"7": "<sup>7</sup> ಹೀಗಿರುವಲ್ಲಿ ಆ ಮನುಷ್ಯನ ಹೆಂಡತಿಯನ್ನು ತಿರಿಗಿ ಅವನಿಗೆ ಒಪ್ಪಿಸಿ ಬಿಡು. ಅವನು ಪ್ರವಾದಿ, ನಿನಗೋಸ್ಕರ ನನಗೆ ಪ್ರಾರ್ಥಿಸುವನು, ಮತ್ತು ನೀನು ಬದುಕುವಿ. ಆಕೆಯನ್ನು ಒಪ್ಪಿಸುವದಿಲ್ಲವೆಂದರೆ ನೀನೂ ನಿನ್ನವರೆಲ್ಲರೂ ಸತ್ತೇ ಸಾಯುವಿರೆಂದು ತಿಳಿದುಕೋ ಎಂದು ಕನಸಿನಲ್ಲಿ ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ಬೆಳಿಗ್ಗೆ ಅಬೀಮೆಲೆಕನು ತನ್ನ ಸೇವಕರೆಲ್ಲರನ್ನು ಕೂಡಿಸಿ ಈ ಸಂಗತಿಗಳನ್ನೆಲ್ಲಾ ತಿಳಿಸಲು ಅವರು ಬಹು ಭಯಪಟ್ಟರು.",
			"9": "<sup>9</sup> ಆಮೇಲೆ ಅವನು ಅಬ್ರಹಾಮನನ್ನು ಕರಿಸಿ - ನೀನು ನಮಗೆ ಮಾಡಿದ್ದೇನು? ನಾನು ಯಾವ ತಪ್ಪು ಮಾಡಿದ್ದರಿಂದ ನೀನು ನನ್ನನ್ನೂ ನನ್ನ ರಾಜ್ಯವನ್ನೂ ಮಹಾಪಾತಕಕ್ಕೆ ಒಳಪಡಿಸಿದಿ? ಮಾಡಬಾರದ ಕಾರ್ಯಗಳನ್ನು ನನಗೆ ಮಾಡಿದೆ ಎಂದು ಹೇಳಿ -",
			"10": "<sup>10</sup> ನೀನು ಯಾವ ಉದ್ದೇಶದಿಂದ ಈ ಕಾರ್ಯವನ್ನು ಮಾಡಿದೆ ಎಂದು ಅಬ್ರಹಾಮನನ್ನು ಕೇಳಿದನು.",
			"11": "<sup>11</sup> ಅದಕ್ಕೆ ಅಬ್ರಹಾಮನು - ಈ ಸ್ಥಳದವರು ದೇವರ ಭಯವಿಲ್ಲದವರಾಗಿ ನನ್ನ ಹೆಂಡತಿಯ ನಿಮಿತ್ತ ನನ್ನನ್ನು ಕೊಂದಾರೆಂದು ನೆನಸಿದೆನು.",
			"12": "<sup>12</sup> ಅದಲ್ಲದೆ ಆಕೆ ನಿಜವಾಗಿ ನನ್ನ ತಂಗಿ ನನ್ನ ತಂದೆಯ ಮಗಳೇ; ಆದರೆ ನನ್ನ ತಾಯಿಯ ಮಗಳಲ್ಲವಾದದರಿಂದ ನನಗೆ ಹೆಂಡತಿಯಾದಳು.",
			"13": "<sup>13</sup> ನಾನು ದೇವಸಂಕಲ್ಪದಿಂದ ತಂದೆಯ ಮನೆಯನ್ನು ಬಿಟ್ಟು ದೇಶಾಂತರ ಹೋಗುವದಕ್ಕೆ ಹೊರಟಾಗ ನಾನು ಆಕೆಗೆ - ನಾವು ಹೋಗುವ ಎಲ್ಲಾ ಸ್ಥಳಗಳಲ್ಲಿಯೂ ನನಗೆ ನಿನ್ನಿಂದ ಒಂದು ಉಪಕಾರವಾಗಬೇಕು, ಏನಂದರೆ ನೀನು ನನ್ನನ್ನು ಅಣ್ಣನೆಂಬದಾಗಿ ಹೇಳಬೇಕೆಂದು ಬೋಧಿಸಿದೆನು ಅಂದನು.",
			"14": "<sup>14</sup> ಆಗ ಅಬೀಮೆಲೆಕನು ಅಬ್ರಹಾಮನಿಗೆ ಕುರಿದನಗಳನ್ನೂ ದಾಸದಾಸಿಯರನ್ನೂ ಕೊಟ್ಟು ಅವನ ಹೆಂಡತಿಯಾದ ಸಾರಳನ್ನು ಅವನಿಗೆ ಒಪ್ಪಿಸಿ -",
			"15": "<sup>15</sup> ಇಗೋ ನನ್ನ ದೇಶವು; ಇದರೊಳಗೆ ನಿನಗೆ ಇಷ್ಟವಿದ್ದ ಕಡೆಯಲ್ಲಿ ವಾಸಮಾಡಬಹುದು ಅಂದನು.",
			"16": "<sup>16</sup> ಇದಲ್ಲದೆ ಅವನು ಸಾರಳಿಗೆ - ನಿನ್ನ ಅಣ್ಣನಿಗೆ ಸಾವಿರ ರೂಪಾಯಿ ಕೊಟ್ಟಿದ್ದೇನೆ; ನಡೆದದ್ದನ್ನು ನಿನ್ನ ಕಡೆಯವರು ಲಕ್ಷ್ಯಕ್ಕೆ ತೆಗೆದುಕೊಳ್ಳದಂತೆ ಇದು ಪ್ರಾಯಶ್ಚಿತ್ತವಾಗಿರಲಿ; ನೀನು ಮಾನಸ್ಥಳೆಂದು ಇದರಿಂದ ಎಲ್ಲರೂ ತಿಳಿದುಕೊಳ್ಳುವರು ಎಂದು ಹೇಳಿದನು.",
			"17": "<sup>17</sup> ತರುವಾಯ ಅಬ್ರಹಾಮನು ದೇವರನ್ನು ಬೇಡಿಕೊಳ್ಳಲು ದೇವರು ಅಬೀಮೆಲೆಕನನ್ನೂ ಅವನ ಪತ್ನಿಯನ್ನೂ ದಾಸಿಯರನ್ನೂ ವಾಸಿಮಾಡಿದ್ದರಿಂದ ಅವರಿಗೆ ಮಕ್ಕಳಾದರು.",
			"18": "<sup>18</sup> ಮುಂಚೆ ಅಬ್ರಹಾಮನ ಹೆಂಡತಿಯಾದ ಸಾರಳ ನಿಮಿತ್ತ ಯೆಹೋವನು ಅಬೀಮೆಲೆಕನ ಮನೆಯಲ್ಲಿದ್ದ ಸ್ತ್ರೀಯರೆಲ್ಲರೂ ಬಂಜೆಯರಾಗುವಂತೆ ಮಾಡಿದ್ದನು."
		},
		"21": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ತಾನು ಹೇಳಿದ್ದಂತೆಯೇ ಸಾರಳ ಮೇಲೆ ದಯವಿಟ್ಟು ಆಕೆಗೆ ಕೊಟ್ಟ ಮಾತನ್ನು ನೆರವೇರಿಸಿದನು.",
			"2": "<sup>2</sup> ಹೇಗಂದರೆ ಸಾರಳು ಬಸುರಾಗಿ ದೇವರು ಮೊದಲು ಸೂಚಿಸಿದ ಕಾಲದಲ್ಲಿ ಅಬ್ರಹಾಮನಿಂದ ಅವನ ಮುಪ್ಪಿನಲ್ಲೇ ಮಗನನ್ನು ಹೆತ್ತಳು.",
			"3": "<sup>3</sup> ಅಬ್ರಹಾಮನು ಸಾರಳಲ್ಲಿ ತನ್ನಿಂದ ಹುಟ್ಟಿದ ಮಗನಿಗೆ",
			"4": "<sup>4</sup> ಇಸಾಕನೆಂದು ಹೆಸರಿಟ್ಟು ಎಂಟನೆಯ ದಿನದಲ್ಲಿ ದೇವರ ಅಪ್ಪಣೆಯ ಮೇರೆಗೆ ಸುನ್ನತಿ ಮಾಡಿದನು.",
			"5": "<sup>5</sup> ಇಸಾಕನು ಹುಟ್ಟಿದಾಗ ಅಬ್ರಹಾಮನು ನೂರು ವರುಷದವನಾಗಿದ್ದನು.",
			"6": "<sup>6</sup> ಸಾರಳು - ದೇವರು ನನ್ನನ್ನು ನಗುವಂತೆ ಮಾಡಿದ್ದಾನೆ; ಕೇಳುವವರೆಲ್ಲರೂ ನನ್ನೊಡನೆ ನಗುವರು.",
			"7": "<sup>7</sup> ಸಾರಳೂ ಮಕ್ಕಳಿಗೆ ಮೊಲೆ ಕುಡಿಸುವಳೆಂದು ಯಾರಾದರೂ ಅಬ್ರಹಾಮನಿಗೆ ಹೇಳುತ್ತಿದ್ದರೋ? ಆದರೂ ಅವನ ಮುಪ್ಪಿನಲ್ಲೇ ಅವನಿಗೆ ಮಗನನ್ನು ಹೆತ್ತಿದ್ದೇನಷ್ಟೆ ಎಂದು ಹೇಳಿಕೊಂಡಳು.",
			"8": "<sup>8</sup> ಆ ಕೂಸು ಬೆಳೆದು ಮೊಲೆಬಿಟ್ಟಿತು. ಇಸಾಕನು ಮೊಲೆಬಿಟ್ಟ ದಿನದಲ್ಲಿ ಅಬ್ರಹಾಮನು ದೊಡ್ಡ ಔತಣವನ್ನು ಮಾಡಿಸಿದನು.",
			"9": "<sup>9</sup> ಆದರೆ ಐಗುಪ್ತ್ಯಳಾದ ಹಾಗರಳಲ್ಲಿ ಅಬ್ರಹಾಮನಿಗೆ ಹುಟ್ಟಿದ ಮಗನು ನಗುವದನ್ನು ಸಾರಳು ಕಂಡು ಅಬ್ರಹಾಮನಿಗೆ -",
			"10": "<sup>10</sup> ಈ ದಾಸಿಯನ್ನೂ ಅವಳ ಮಗನನ್ನೂ ಹೊರಗೆ ಹಾಕು; ಈ ದಾಸಿಯ ಮಗನು ನನ್ನ ಮಗನಾದ ಇಸಾಕನೊಂದಿಗೆ ಬಾಧ್ಯನಾಗಬಾರದು ಎಂದು ಹೇಳಿದಳು.",
			"11": "<sup>11</sup> ಮಗನ ದೆಸೆಯಿಂದ ಈ ಮಾತು ಅಬ್ರಹಾಮನಿಗೆ ಬಹುದುಃಖವನ್ನು ಹುಟ್ಟಿಸಿತು.",
			"12": "<sup>12</sup> ಆದರೆ ದೇವರು ಅವನಿಗೆ - ಮಗನ ಮತ್ತು ದಾಸಿಯ ದೆಸೆಯಿಂದ ನಿನಗೆ ಕರಕರೆಯಾಗಬಾರದು; ಸಾರಳು ಹೇಳಿದಂತೆಯೇ ಮಾಡು; ಇಸಾಕನಿಂದ ಹುಟ್ಟುವವರೇ ನಿನ್ನ ಸಂತತಿ ಅನ್ನಿಸಿಕೊಳ್ಳುವರು;",
			"13": "<sup>13</sup> ಈ ದಾಸಿಯ ಮಗನು ನಿನ್ನಿಂದ ಹುಟ್ಟಿದವನಾದ್ದರಿಂದ ಅವನಿಂದಲೂ ಜನಾಂಗವಾಗುವಂತೆ ಮಾಡುವೆನು ಎಂದು ಹೇಳಿದನು.",
			"14": "<sup>14</sup> ಮಾರಣೇ ದಿನ ಬೆಳಿಗ್ಗೆ ಅಬ್ರಹಾಮನು ಎದ್ದು ಹಾಗರಳಿಗೆ ಬುತ್ತಿಯನ್ನೂ ಒಂದು ತಿತ್ತಿ ತಣ್ಣೀರನ್ನೂ ಕೊಟ್ಟು ಅವಳ ಹೆಗಲಿನ ಮೇಲೆ ಇಟ್ಟು ಮಗುವನ್ನು ಒಪ್ಪಿಸಿ ಕಳುಹಿಸಿಬಿಟ್ಟನು. ಅವಳು ಹೊರಟು ಬೇರ್ಷೆಬದ ಕಾಡಿನಲ್ಲಿ ಅಲೆಯುತ್ತಿದ್ದಳು.",
			"15": "<sup>15</sup> ತಿತ್ತಿಯಲ್ಲಿದ್ದ ನೀರು ಮುಗಿದ ಮೇಲೆ ಅವಳು ಮಗುವನ್ನು ಒಂದು ಗಿಡದ ನೆರಳಿನಲ್ಲಿ ಹಾಕಿ",
			"16": "<sup>16</sup> ಮಗುವು ಸಾಯುವದನ್ನು ನೋಡಲಾರೆನು ಅಂದುಕೊಂಡು ಬಿಲ್ಲೆಸುಗೆಯಷ್ಟು ದೂರ ಹೋಗಿ ಅವನೆದುರಾಗಿ ಕೂತುಕೊಂಡು ಗಟ್ಟಿಯಾಗಿ ಅತ್ತಳು.",
			"17": "<sup>17</sup> ಆ ಹುಡುಗನ ಮೊರೆಯು ದೇವರಿಗೆ ಕೇಳಿಸಿತು; ದೇವದೂತನು ಆಕಾಶದಿಂದ ಹಾಗರಳನ್ನು ಕರೆದು - ಹಾಗರಳೇ, ನಿನಗೇನಾಯಿತು? ಅಂಜಬೇಡ; ಆ ಹುಡುಗನು ಬಿದ್ದಿರುವ ಸ್ಥಳದಿಂದಲೇ ಅವನ ಶಬ್ದವು ದೇವರಿಗೆ ಕೇಳಿಸಿತು;",
			"18": "<sup>18</sup> ನೀನೆದ್ದು ಅವನನ್ನು ಎತ್ತಿಕೊಂಡು ಕೈಬಿಡದೆ ಇರು; ಅವನಿಂದ ದೊಡ್ಡ ಜನಾಂಗವಾಗುವಂತೆ ಮಾಡುವೆನು ಎಂದು ಹೇಳಿದನು.",
			"19": "<sup>19</sup> ಇದಲ್ಲದೆ ದೇವರು ಅವಳ ಕಣ್ಣನ್ನು ತೆರೆದದರಿಂದ ಅವಳು ನೀರಿನ ಬಾವಿಯನ್ನು ಕಂಡು ತಿತ್ತಿಯಲ್ಲಿ ನೀರನ್ನು ತುಂಬಿಕೊಂಡು ಹುಡುಗನಿಗೆ ಕುಡಿಸಿದಳು.",
			"20": "<sup>20</sup> ದೇವರು ಆ ಹುಡುಗನ ಸಂಗಡ ಇದ್ದನು; ಅವನು ಬೆಳೆದು ಕಾಡಿನಲ್ಲಿ ವಾಸವಾಗಿದ್ದು ಬಿಲ್ಲುಗಾರನಾದನು.",
			"21": "<sup>21</sup> ಅವನು ಪಾರಾನಿನ ಅರಣ್ಯದಲ್ಲಿ ವಾಸಮಾಡಿದನು. ಅವನ ತಾಯಿ ಐಗುಪ್ತದೇಶದಿಂದ ಕನ್ನಿಕೆಯನ್ನು ತರಿಸಿ ಮದುವೆಮಾಡಿಸಿದಳು.",
			"22": "<sup>22</sup> ಆ ಕಾಲದಲ್ಲಿ ಅಬೀಮೆಲೆಕನು ತನ್ನ ಸೇನಾಪತಿಯಾದ ಫೀಕೋಲನ ಸಮೇತ ಅಬ್ರಹಾಮನಿಗೆ - ನೀನು ಮಾಡುವ ಎಲ್ಲಾ ಕೆಲಸಗಳಲ್ಲಿಯೂ ದೇವರು ನಿನ್ನ ಸಂಗಡ ಇದ್ದಾನಾದ್ದರಿಂದ",
			"23": "<sup>23</sup> ನೀನು ಈ ಸ್ಥಳದಲ್ಲಿ ನನ್ನೊಂದಿಗೆ [ಒಡಂಬಡಿಕೆಮಾಡಿಕೊಂಡು] ನೀನು ನನಗಾಗಲಿ ನನ್ನ ಪುತ್ರಪೌತ್ರರಿಗಾಗಲಿ ಏನೂ ಅನ್ಯಾಯ ಮಾಡದೆ ನಾನು ನಿನಗೆ ಹಿತವನ್ನು ಮಾಡಿದಂತೆಯೇ ನನಗೂ ನೀನು ಇಳುಕೊಂಡಿರುವ ಈ ದೇಶಕ್ಕೂ ಹಿತವನ್ನು ಮಾಡುವದಾಗಿ ದೇವರ ಮೇಲೆ ಪ್ರಮಾಣಮಾಡಬೇಕೆಂದು ಹೇಳಿದನು.",
			"24": "<sup>24</sup> ಅದಕ್ಕೆ ಅಬ್ರಹಾಮನು - ಹಾಗೆಯೇ ಪ್ರಮಾಣ ಮಾಡುತ್ತೇನೆ ಅಂದನು.",
			"25": "<sup>25</sup> ಅಬೀಮೆಲೆಕನ ಆಳುಗಳು ಒಂದು ಬಾವಿಯನ್ನು ಬಲಾತ್ಕಾರದಿಂದ ಸ್ವಾಧೀನಪಡಿಸಿಕೊಂಡದರಿಂದ ಅಬ್ರಹಾಮನು ಅಬೀಮೆಲೆಕನ ಮೇಲೆ ತಪ್ಪು ಹೊರಿಸಲು ಅಬೀಮೆಲೆಕನು -",
			"26": "<sup>26</sup> ಈ ಕೆಲಸವನ್ನು ಮಾಡಿದವರು ಯಾರೋ ನಾನರಿಯೆ; ನೀನು ನನಗೆ ತಿಳಿಸಲೂ ಇಲ್ಲ, ಈಗಿನವರೆಗೆ ನಾನು ಈ ಸಂಗತಿಯನ್ನು ಕೇಳಲೂ ಇಲ್ಲ ಅಂದನು.",
			"27": "<sup>27</sup> ಆಗ ಅಬ್ರಹಾಮನು ಅಬೀಮೆಲೆಕನಿಗೆ ಕುರಿದನಗಳನ್ನು ದಾನಮಾಡಿದನು. ಹೀಗೆ ಅವರಿಬ್ಬರೂ ಒಡಂಬಡಿಕೆಮಾಡಿಕೊಂಡರು.",
			"28": "<sup>28</sup> ತರುವಾಯ ಅಬ್ರಹಾಮನು ಹಿಂಡಿನ ಏಳು ಕುರಿಗಳನ್ನು ಪ್ರತ್ಯೇಕವಾಗಿ ಇರಿಸಲು ಅಬೀಮೆಲೆಕನು ಅವನನ್ನು",
			"29": "<sup>29</sup> ನೀನು ಪ್ರತ್ಯೇಕವಾಗಿ ಇರಿಸಿರುವ ಈ ಏಳು ಕುರಿಗಳು ಯಾತಕ್ಕೆ ಎಂದು ಕೇಳಿದನು. ಅದಕ್ಕೆ ಅಬ್ರಹಾಮನು",
			"30": "<sup>30</sup> ಈ ಬಾವಿಯನ್ನು ತೋಡಿಸಿದವನು ನಾನೇ ಎಂಬದಕ್ಕೆ ಸಾಕ್ಷಿಯಾಗಿ ನೀನು ಈ ಏಳು ಕುರಿಗಳನ್ನು ನನ್ನ ಕೈಯಿಂದ ತೆಗೆದುಕೊಳ್ಳಬೇಕು ಅಂದನು.",
			"31": "<sup>31</sup> ಅವರಿಬ್ಬರು ಅಲ್ಲಿ ಪ್ರಮಾಣಮಾಡಿದ್ದರಿಂದ ಆ ಸ್ಥಳಕ್ಕೆ ಬೇರ್ಷೆಬ ಎಂದು ಹೆಸರಾಯಿತು.",
			"32": "<sup>32</sup> ಬೇರ್ಷೆಬದಲ್ಲಿ ಅವರು ಒಡಂಬಡಿಕೆಯನ್ನು ಮಾಡಿಕೊಂಡರು. ತರುವಾಯ ಅಬೀಮೆಲೆಕನೂ ಅವನ ಸೇನಾಪತಿಯಾದ ಫೀಕೋಲನೂ ಫಿಲಿಷ್ಟಿಯರ ದೇಶಕ್ಕೆ ತಿರಿಗಿ ಹೊರಟು ಹೋದರು.",
			"33": "<sup>33</sup> ಅಬ್ರಹಾಮನು ಬೇರ್ಷೆಬದಲ್ಲಿ ಪಿಚುಲವೃಕ್ಷವನ್ನು ನೆಟ್ಟು ನಿತ್ಯದೇವರಾದ ಯೆಹೋವನ ಹೆಸರನ್ನು ಹೇಳಿಕೊಂಡು ಆರಾಧಿಸಿದನು.",
			"34": "<sup>34</sup> ಅಬ್ರಹಾಮನು ಫಿಲಿಷ್ಟಿಯರ ದೇಶದಲ್ಲಿ ಬಹುದಿವಸ ಇಳುಕೊಂಡಿದ್ದನು."
		},
		"22": {
			"1": "<sup>1</sup> ಈ ಸಂಗತಿಗಳು ಆದ ಮೇಲೆ ದೇವರು ಅಬ್ರಹಾಮನನ್ನು ಪರಿಶೋಧಿಸಿದನು. ಹೇಗಂದರೆ ಆತನು ಅವನನ್ನು - ಅಬ್ರಹಾಮನೇ ಎಂದು ಕರೆಯಲು ಅವನು - ಇಗೋ, ಇದ್ದೇನೆ ಅಂದನು.",
			"2": "<sup>2</sup> ಆಗಲಾತನು - ನಿನಗೆ ಪ್ರಿಯನಾಗಿರುವ ಒಬ್ಬನೇ ಮಗನಾದ ಇಸಾಕನನ್ನು ತೆಗೆದುಕೊಂಡು ಮೊರೀಯ ದೇಶಕ್ಕೆ ಹೋಗಿ ಅಲ್ಲಿ ಅವನನ್ನು ನಾನು ಹೇಳುವ ಒಂದು ಬೆಟ್ಟದ ಮೇಲೆ ಸರ್ವಾಂಗಹೋಮವಾಗಿ ಅರ್ಪಿಸಬೇಕು ಅಂದನು.",
			"3": "<sup>3</sup> ಬೆಳಿಗ್ಗೆ ಅಬ್ರಹಾಮನು ಎದ್ದು ಕತ್ತೆಗೆ ತಡಿಹಾಕಿಸಿ ಯಜ್ಞಕ್ಕೆ ಬೇಕಾದ ಕಟ್ಟಿಗೆಯನ್ನು ಒಡಿಸಿ ತನ್ನ ಸೇವಕರಲ್ಲಿ ಇಬ್ಬರನ್ನೂ ತನ್ನ ಮಗನಾದ ಇಸಾಕನನ್ನೂ ಕರೆದುಕೊಂಡು ದೇವರು ಹೇಳಿದ ಸ್ಥಳಕ್ಕೆ ಹೊರಟನು.",
			"4": "<sup>4</sup> ಮೂರನೆಯ ದಿನದಲ್ಲಿ ಅಬ್ರಹಾಮನು ಕಣ್ಣೆತ್ತಿ ನೋಡುವಾಗ ಆ ಸ್ಥಳವು ದೂರದಲ್ಲಿ ಕಾಣಿಸಲು",
			"5": "<sup>5</sup> ಅವನು ತನ್ನ ಸೇವಕರಿಗೆ - ನೀವು ಇಲ್ಲೇ ಕತ್ತೆಯ ಬಳಿಯಲ್ಲಿರ್ರಿ; ನಾನೂ ನನ್ನ ಮಗನೂ ಅಲ್ಲಿಗೆ ಹೋಗಿ ದೇವಾರಾಧನೆ ಮಾಡಿಕೊಂಡು ನಿಮ್ಮ ಬಳಿಗೆ ತಿರಿಗಿ ಬರುತ್ತೇವೆ ಎಂದು ಹೇಳಿದನು.",
			"6": "<sup>6</sup> ಆಗ ಅಬ್ರಹಾಮನು ಯಜ್ಞಕ್ಕೆ ಬೇಕಾದ ಕಟ್ಟಿಗೆಯನ್ನು ತನ್ನ ಮಗನಾದ ಇಸಾಕನ ಮೇಲೆ ಹೊರಿಸಿ ತನ್ನ ಕೈಯಲ್ಲೇ ಬೆಂಕಿಯನ್ನೂ ಕತ್ತಿಯನ್ನೂ ತೆಗೆದುಕೊಂಡನಂತರ ಅವರಿಬ್ಬರೂ ಹೊರಟು ಹೋದರು.",
			"7": "<sup>7</sup> ಹೋಗುವಾಗ ಇಸಾಕನು ತನ್ನ ತಂದೆಯಾದ ಅಬ್ರಹಾಮನಿಗೆ - ಅಪ್ಪಾ ಎಂದು ಕರೆಯಲು ಅಬ್ರಹಾಮನು - ಏನು ಮಗನೇ ಅಂದನು. ಇಸಾಕನು - ಇಗೋ, ಬೆಂಕಿಯೂ ಕಟ್ಟಿಗೆಯೂ ಉಂಟು; ಆದರೆ ಹೋಮಕ್ಕೆ ಬೇಕಾದ ಕುರಿ ಎಲ್ಲಿ ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ ಅಬ್ರಹಾಮನು -",
			"8": "<sup>8</sup> ಮಗನೇ, ಹೋಮಕ್ಕೆ ಬೇಕಾದ ಕುರಿಯನ್ನು ದೇವರೇ ಒದಗಿಸುವನು ಅಂದನು. ಹೀಗೆ ಅವರಿಬ್ಬರೂ ಹೋದರು.",
			"9": "<sup>9</sup> ದೇವರು ಹೇಳಿದ ಸ್ಥಳಕ್ಕೆ ಅವರು ಸೇರಿದಾಗ ಅಬ್ರಹಾಮನು ಯಜ್ಞವೇದಿಯನ್ನು ಕಟ್ಟಿ ಕಟ್ಟಿಗೆಯನ್ನು ಒಟ್ಟಿ ತನ್ನ ಮಗನಾದ ಇಸಾಕನ ಕೈಕಾಲುಗಳನ್ನು ಬಿಗಿದು ಅವನನ್ನು ವೇದಿಯಲ್ಲಿ ಕಟ್ಟಿಗೆಯ ಮೇಲೆ ಇಟ್ಟನು.",
			"10": "<sup>10</sup> ಆದರೆ ತನ್ನ ಮಗನನ್ನು ವಧಿಸುವದಕ್ಕೆ ಅವನು",
			"11": "<sup>11</sup> ಕೈಚಾಚಿ ಕತ್ತಿಯನ್ನು ಹಿಡಿದುಕೊಳ್ಳಲು ಯೆಹೋವನ ದೂತನು ಆಕಾಶದೊಳಗಿಂದ - ಅಬ್ರಹಾಮನೇ, ಅಬ್ರಹಾಮನೇ ಎಂದು ಅವನನ್ನು ಕರೆದನು. ಅದಕ್ಕೆ ಅಬ್ರಹಾಮನು - ಇಗೋ, ಇದ್ದೇನೆ ಅಂದನು.",
			"12": "<sup>12</sup> ಆ ದೂತನು ಅವನಿಗೆ - ನಿನ್ನ ಹುಡುಗನ ಮೇಲೆ ಕೈಹಾಕಬೇಡ, ಅವನಿಗೆ ಏನೂ ಮಾಡಬೇಡ. ನೀನು ನಿನ್ನ ಒಬ್ಬನೇ ಮಗನನ್ನಾದರೂ ನನಗೆ ಸಮರ್ಪಿಸುವದಕ್ಕೆ ಹಿಂದೆಗೆಯಲಿಲ್ಲವಾದ್ದರಿಂದ ನೀನು ದೇವರಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವನೆಂಬದು ಈಗ ತೋರಬಂತು ಎಂದು ಹೇಳಿದನು.",
			"13": "<sup>13</sup> ಅಬ್ರಹಾಮನು ಕಣ್ಣೆತ್ತಿ ನೋಡುವಾಗ ತನ್ನ ಹಿಂದುಗಡೆ ಒಂದು ಟಗರು ಪೊದೆಯಲ್ಲಿ ಕೊಂಬುಗಳಿಂದ ಸಿಕ್ಕಿಕೊಂಡಿತ್ತು. ಅವನು ಹೋಗಿ ಅದನ್ನು ಹಿಡಿದುತಂದು ತನ್ನ ಮಗನಿಗೆ ಬದಲಾಗಿ ಹೋಮಮಾಡಿದನು.",
			"14": "<sup>14</sup> ಆ ಸ್ಥಳಕ್ಕೆ ಯೆಹೋವ ಯೀರೆ ಎಂದು ಹೆಸರಿಟ್ಟನು. ಯೆಹೋವನ ಬೆಟ್ಟದಲ್ಲಿ ಒದಗುವದು ಎಂಬದಾಗಿ ಇಂದಿನವರೆಗೂ ಹೇಳುವದುಂಟಲ್ಲಾ.",
			"15": "<sup>15</sup> ಯೆಹೋವನ ದೂತನು ಆಕಾಶದೊಳಗಿಂದ ಎರಡನೆಯ ಸಾರಿ ಅಬ್ರಹಾಮನನ್ನು ಕರೆದು ಅವನಿಗೆ -",
			"16": "<sup>16</sup> ಯೆಹೋವನು ನುಡಿದದ್ದನ್ನು ಕೇಳು; ನೀನು ನಿನ್ನ ಒಬ್ಬನೇ ಮಗನನ್ನು ಸಮರ್ಪಿಸುವದಕ್ಕೆ ಹಿಂದೆಗೆಯದೆ ಹೋದದ್ದರಿಂದ ನಾನು ನಿನ್ನನ್ನು ಆಶೀರ್ವದಿಸೇ ಆಶೀರ್ವದಿಸುವೆನು;",
			"17": "<sup>17</sup> ನಿನ್ನ ಸಂತತಿಯನ್ನು ಹೆಚ್ಚಿಸೇ ಹೆಚ್ಚಿಸುವೆನು; ಅದನ್ನು ಆಕಾಶದ ನಕ್ಷತ್ರಗಳಂತೆಯೂ ಸಮುದ್ರತೀರದಲ್ಲಿರುವ ಉಸುಬಿನಂತೆಯೂ ಅಸಂಖ್ಯವಾಗಿ ಮಾಡುವೆನು; ಅವರು ಶತ್ರುಗಳ ಪಟ್ಟಣಗಳನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವರು.",
			"18": "<sup>18</sup> ನೀನು ನನ್ನ ಮಾತನ್ನು ಕೇಳಿದ್ದರಿಂದ ಭೂಮಿಯ ಎಲ್ಲಾ ಜನಾಂಗಗಳಿಗೂ ನಿನ್ನ ಸಂತತಿಯ ಮೂಲಕ ಆಶೀರ್ವಾದವುಂಟಾಗುವದು ಎಂಬದಾಗಿ ಯೆಹೋವನು ತನ್ನಾಣೆಯಿಟ್ಟು ಹೇಳಿದ್ದಾನೆ ಅಂದನು.",
			"19": "<sup>19</sup> ತರುವಾಯ ಅಬ್ರಹಾಮನು ತನ್ನ ಸೇವಕರ ಬಳಿಗೆ ತಿರಿಗಿ ಬಂದನು. ಅವರು ಹೊರಟು ಅವನ ಜೊತೆಯಲ್ಲಿ ಬೇರ್ಷೆಬಕ್ಕೆ ಹೋದರು. ಅಬ್ರಹಾಮನು ಬೇರ್ಷೆಬದಲ್ಲಿ ವಾಸವಾಗಿದ್ದನು.",
			"20": "<sup>20</sup> ಇವುಗಳಾದ ಮೇಲೆ ಅಬ್ರಹಾಮನು ತನ್ನ ತಮ್ಮನಾದ ನಾಹೋರನಿಗೆ ಮಿಲ್ಕಳಲ್ಲಿ ಮಕ್ಕಳು ಹುಟ್ಟಿದರೆಂದು ವರ್ತಮಾನವನ್ನು ಕೇಳಿದನು.",
			"21": "<sup>21</sup> ಅವರು ಯಾರಾರಂದರೆ - ಅವನ ಚೊಚ್ಚಲನಾದ ಊಚ್, ಇವನ ತಮ್ಮನಾದ ಬೂಜ್, ಅರಾಮನ ತಂದೆಯಾದ ಕೆಮೂವೇಲ್, ಕೆಸೆದ್, ಹಜೋ, ಪಿಲ್ದಾಷ್, ಇದ್ಲಾಫ್, ಬೆತೂವೇಲ್ ಎಂಬಿವರೇ.",
			"22": "<sup>22</sup> ಬೆತೂವೇಲನು ರೆಬೆಕ್ಕಳನ್ನು ಪಡೆದನು.",
			"23": "<sup>23</sup> ಈ ಎಂಟು ಮಂದಿಯನ್ನು ಮಿಲ್ಕಳು ಅಬ್ರಹಾಮನ ತಮ್ಮನಾದ ನಾಹೋರನಿಗೆ ಹೆತ್ತಳು.",
			"24": "<sup>24</sup> ಇದಲ್ಲದೆ ಅವನ ಉಪಪತ್ನಿಯಾದ ರೂಮಳೆಂಬವಳು ಟೆಬಹ, ಗಹಮ್, ತಹಷ್, ಮಾಕಾ ಎಂಬವರನ್ನು ಹೆತ್ತಳು."
		},
		"23": {
			"1": "<sup>1</sup> ಸಾರಳ ಜೀವಮಾನಕಾಲವು ನೂರಿಪ್ಪತ್ತೇಳು ವರುಷ;",
			"2": "<sup>2</sup> ಇಷ್ಟು ವರುಷ ಬದುಕಿದ ಮೇಲೆ ಆಕೆ ಕಾನಾನ್ ದೇಶದಲ್ಲಿರುವ ಹೆಬ್ರೋನೆಂಬ ಕಿರ್ಯತರ್ಬದಲ್ಲಿ ಸತ್ತಳು. ಅಬ್ರಹಾಮನು ಬಂದು ಆಕೆಯ ನಿಮಿತ್ತ ಗೋಳಾಡಿ ಕಣ್ಣೀರು ಸುರಿಸಿದನು.",
			"3": "<sup>3</sup> ಆಮೇಲೆ ಆಕೆಯ ಶವದ ಬಳಿಯಿಂದ ಎದ್ದು ಹಿತ್ತಿಯರ ಬಳಿಗೆ ಹೋಗಿ ಅವರಿಗೆ -",
			"4": "<sup>4</sup> ನಾನು ನಿಮ್ಮಲ್ಲಿ ಪರದೇಶದವನೂ ಪರವಾಸಿಯೂ ಆಗಿದ್ದೇನಷ್ಟೆ. ತೀರಿಹೋಗಿರುವ ನನ್ನ ಪತ್ನಿಯ ಸಮಾಧಿಗೋಸ್ಕರ ನಿಮ್ಮಲ್ಲಿ ನನ್ನ ಸ್ವಂತಕ್ಕೆ ಭೂಮಿಯನ್ನು ಕೊಡಬೇಕೆಂದು ಕೇಳಿಕೊಂಡನು.",
			"5": "<sup>5</sup> ಹಿತ್ತಿಯರು ಅವನಿಗೆ - ಸ್ವಾಮೀ, ನಮ್ಮ ಮಾತನ್ನು ಕೇಳು; ನೀನು ನಮಗೆ ಮಹಾಪ್ರಭುವಾಗಿದ್ದೀಯಷ್ಟೆ. ತೀರಿಹೋದ ನಿನ್ನ ಪತ್ನಿಯ ದೇಹವನ್ನು ನಮಗಿರುವ ಸಮಾಧಿಗಳೊಳಗೆ ಶ್ರೇಷ್ಠವಾದದ್ದರಲ್ಲಿ ಇಡಬಹುದು. ನಿನ್ನ ಹೆಂಡತಿಯ ಶವವನ್ನು ಇಡುವದಕ್ಕೆ ನಮ್ಮೊಳಗೆ ಒಬ್ಬನಾದರೂ ತನಗಿರುವ ಶ್ಮಶಾನ ಭೂಮಿಯನ್ನು ಕೊಡುವದಕ್ಕೆ ಹಿಂದೆಗೆಯುವದಿಲ್ಲವೆಂದು ಉತ್ತರ ಕೊಡಲು ಅಬ್ರಹಾಮನು ಎದ್ದು ಹಿತ್ತಿಯರಾಗಿದ್ದ",
			"7": "<sup>7</sup> ಆ ದೇಶದವರಿಗೆ ಬೊಗ್ಗಿ ನಮಸ್ಕರಿಸಿ ಅವರ ಸಂಗಡ ಇನ್ನೂ ಮಾತಾಡಿ -",
			"8": "<sup>8</sup> ನಾನು ನಿಮ್ಮಲ್ಲಿ ನನ್ನ ಪತ್ನಿಯ ಶವವನ್ನು ಸಮಾಧಿಮಾಡುವದು ನಿಮಗೆ ಒಪ್ಪಿಗೆಯಾಗಿದ್ದರೆ ಒಂದು ವಿಜ್ಞಾಪನೆಯುಂಟು. ನೀವು ಚೋಹರನ ಮಗನಾದ ಎಫ್ರೋನನ ಸಂಗಡ ನನಗೋಸ್ಕರ ಮಾತಾಡಿ",
			"9": "<sup>9</sup> ಅವನ ಭೂಮಿಯ ಅಂಚಿನಲ್ಲಿರುವ ಮಕ್ಪೇಲದ ಗವಿಯನ್ನು ನನಗೆ ಕೊಡಿಸಬೇಕೆಂದು ಬೇಡಿಕೊಳ್ಳುತ್ತೇನೆ. ಅವನು ಸಮಾಧಿಯ ಸ್ಥಳವನ್ನು ನನ್ನ ಸ್ವಂತಕ್ಕಾಗಿ ನಿಮ್ಮೆದುರಿನಲ್ಲಿ ಕೊಟ್ಟರೆ ಪೂರ್ಣಕ್ರಯವನ್ನು ಕೊಡುತ್ತೇನೆ ಅಂದನು.",
			"10": "<sup>10</sup> ಇಷ್ಟರಲ್ಲಿ ಎಫ್ರೋನನೇ ಅಲ್ಲಿ ಹಿತ್ತಿಯರ ನಡುವೆ ಕೂತಿದ್ದನು. ಹೀಗಿರಲಾಗಿ ಹಿತ್ತಿಯನಾದ ಎಫ್ರೋನನು ಹಿತ್ತಿಯರಾಗಿದ್ದ  ಆ ಊರಿನವರೆಲ್ಲರ ಎದುರಿನಲ್ಲಿ ಅಬ್ರಹಾಮನಿಗೆ -",
			"11": "<sup>11</sup> ಹಾಗಲ್ಲ ಸ್ವಾಮೀ, ನನ್ನ ಮಾತನ್ನು ಲಾಲಿಸು. ಆ ಭೂಮಿಯನ್ನೂ ಅದರಲ್ಲಿರುವ ಗವಿಯನ್ನೂ ನಿನಗೆ ಸುಮ್ಮನೆ ಕೊಡುತ್ತೇನೆ; ನನ್ನ ಜನರ ಮುಂದೆಯೇ ಕೊಡುತ್ತೇನೆ; ತೀರಿಹೋದ ನಿನ್ನ ಪತ್ನಿಗೆ ಅದರಲ್ಲಿ ಸಮಾಧಿಮಾಡಬಹುದು ಎಂದು ಹೇಳಲು",
			"12": "<sup>12</sup> ಅಬ್ರಹಾಮನು ಆ ದೇಶದ ಜನರಿಗೆ ಬೊಗ್ಗಿ ನಮಸ್ಕರಿಸಿ ಅವರೆಲ್ಲರ ಮುಂದೆ ಎಫ್ರೋನನಿಗೆ -",
			"13": "<sup>13</sup> ಕೊಡಲಿಕ್ಕೆ ಮನಸ್ಸಿದ್ದರೆ ದಯವಿಟ್ಟು ನಾನು ಅರಿಕೆಮಾಡುವದನ್ನು ಕೇಳು; ಆ ಭೂಮಿಗೆ ಕ್ರಯವನ್ನು ಕೊಡುತ್ತೇನೆ. ನನ್ನಿಂದ ಕ್ರಯ ತೆಗೆದುಕೊಂಡರೆ ಅದರಲ್ಲಿ ನನ್ನ ಪತ್ನಿಗೆ ಸಮಾಧಿಮಾಡುವೆನು ಎಂದು ಹೇಳಿದನು.",
			"14": "<sup>14</sup> ಅದಕ್ಕೆ ಎಫ್ರೋನನು - ಸ್ವಾಮೀ, ನನ್ನ ಮಾತನ್ನು ಲಾಲಿಸು;",
			"15": "<sup>15</sup> ನಾನೂರು ರೂಪಾಯಿ ಬಾಳುವ ಭೂಮಿಯ ವಿಷಯದಲ್ಲಿ ನಿನಗೂ ನನಗೂ ವಾದವೇತಕ್ಕೆ? ಸಮಾಧಿಮಾಡಬಹುದು ಅಂದನು.",
			"16": "<sup>16</sup> ಅಬ್ರಹಾಮನು ಎಫ್ರೋನನ ಮಾತಿಗೆ ಒಪ್ಪಿದನು. ಎಫ್ರೋನನು ಹಿತ್ತಿಯರ ಎದುರಿನಲ್ಲಿ ಹೇಳಿದ ನಾನೂರು ರೂಪಾಯಿಗಳನ್ನು ಅಬ್ರಹಾಮನು ಸಾಹುಕಾರರಲ್ಲಿ ಸಲ್ಲುವ ಬೆಳ್ಳಿಯಿಂದ ತೂಕ ಮಾಡಿಕೊಟ್ಟನು.",
			"17": "<sup>17</sup> ಹೀಗೆ ಮಮ್ರೆಗೆ ಎದುರಾಗಿರುವ ಮಕ್ಪೇಲಕ್ಕೆ ಸೇರಿದ ಎಫ್ರೋನನ ಭೂಮಿಯು, ಅದಕ್ಕೆ ಸೇರಿದ ಗವಿಯೂ ಅದರಲ್ಲಿ ಮತ್ತು ಅದರ ಸುತ್ತಣ ಅಂಚಿನಲ್ಲಿ ಇದ್ದ ಮರಗಳೂ ಸಹಿತವಾಗಿ,",
			"18": "<sup>18</sup> ಅಬ್ರಹಾಮನ ಸ್ವಂತ ಭೂಮಿಯೆಂದು ಹಿತ್ತಿಯರಾಗಿದ್ದ ಆ ಊರಿನವರೆಲ್ಲರ ಮುಂದೆ ಧೃಢವಾಯಿತು.",
			"19": "<sup>19</sup> ಇದಾದ ಮೇಲೆ ಅಬ್ರಹಾಮನು ತನ್ನ ಹೆಂಡತಿಯಾದ ಸಾರಳ ಶವವನ್ನು ಕಾನಾನ್‍ದೇಶದಲ್ಲಿ ಹೆಬ್ರೋನೆಂಬ ಮಮ್ರೆಗೆ ಎದುರಾಗಿರುವ ಮಕ್ಪೇಲದ ಭೂಮಿಯಲ್ಲಿರುವ ಗವಿಯೊಳಗೆ ಹೂಣಿಟ್ಟನು.",
			"20": "<sup>20</sup> ಹಿತ್ತಿಯರು ಆ ಭೂಮಿಯನ್ನೂ ಅದರಲ್ಲಿರುವ ಗವಿಯನ್ನೂ ಸ್ವಂತವಾದ ಶ್ಮಶಾನಭೂಮಿಯಾಗುವದಕ್ಕೆ ಅಬ್ರಹಾಮನಿಗೆ ಕೊಟ್ಟು ನಿರ್ಣಯಿಸಿದರು."
		},
		"24": {
			"1": "<sup>1</sup> ಅಬ್ರಹಾಮನು ಬಹುದಿನದ ವೃದ್ಧನಾದನು. ಯೆಹೋವನು ಅವನನ್ನು ಸಕಲ ವಿಷಯಗಳಲ್ಲಿ ಅಭಿವೃದ್ಧಿಪಡಿಸಿದ್ದನು.",
			"2": "<sup>2</sup> ಹೀಗಿರಲು ಅವನು ತನಗಿದ್ದ ಆಸ್ತಿಯ ಮೇಲೆ ಆಡಳಿತ ಮಾಡುತ್ತಿದ್ದ ಹಿರೀ ಸೇವಕನಿಗೆ -",
			"3": "<sup>3</sup> ನಾನು ವಾಸವಾಗಿರುವ ಕಾನಾನ್ಯರಲ್ಲಿ ನೀನು ನನ್ನ ಮಗನಿಗೋಸ್ಕರ ಹೆಣ್ಣನ್ನು ತೆಗೆದುಕೊಳ್ಳದೆ ನನ್ನ ಸ್ವದೇಶಕ್ಕೂ",
			"4": "<sup>4</sup> ನನ್ನ ಬಂಧುಗಳ ಬಳಿಗೂ ಹೋಗಿ ಅವರಲ್ಲೇ ನನ್ನ ಮಗನಾದ ಇಸಾಕನಿಗೆ ಹೆಣ್ಣನ್ನು ತೆಗೆದುಕೊಳ್ಳಬೇಕು. ಹಾಗೆ ತೆಗೆದುಕೊಳ್ಳುತ್ತೇನೆಂಬದಕ್ಕೆ ನೀನು ನನ್ನ ತೊಡೆಯ ಕೆಳಗೆ ಕೈಯಿಟ್ಟು ಭೂಲೋಕಪರಲೋಕಗಳ ದೇವರಾಗಿರುವ ಯೆಹೋವನ ಮೇಲೆ ಪ್ರಮಾಣಮಾಡಬೇಕು ಎಂದು ಹೇಳಿದನು.",
			"5": "<sup>5</sup> ಅದಕ್ಕೆ ಆ ಸೇವಕನು - ಒಂದು ವೇಳೆ ನನ್ನ ಹಿಂದೆ ಈ ದೇಶಕ್ಕೆ ಬರುವದಕ್ಕೆ ಆ ಕನ್ಯೆಗೆ ಇಷ್ಟವಿಲ್ಲದೆ ಹೋದೀತು; ನೀನು ಬಿಟ್ಟುಬಂದ ಆ ದೇಶಕ್ಕೆ ನಾನು ನಿನ್ನ ಮಗನನ್ನು ತಿರಿಗಿ ಕರಕೊಂಡು ಹೋಗಬೇಕನ್ನುತ್ತೀಯೋ ಎಂದು ಕೇಳಲು",
			"6": "<sup>6</sup> ಅಬ್ರಹಾಮನು ಅವನಿಗೆ - ಎಂದಿಗೂ ಬೇಡ; ಅಲ್ಲಿಗೆ ನನ್ನ ಮಗನನ್ನು ಕರಕೊಂಡು ಹೋಗಲೇ ಕೂಡದು.",
			"7": "<sup>7</sup> ನನ್ನ ತಂದೆಯ ಮನೆಯಿಂದಲೂ ನಾನು ಹುಟ್ಟಿದ ದೇಶದಿಂದಲೂ ನನ್ನನ್ನು ಕರತಂದು - ನಿನ್ನ ಸಂತತಿಗೆ ಈ ದೇಶವನ್ನು ಕೊಡುವೆನೆಂದು ಪ್ರಮಾಣಮಾಡಿ ಹೇಳಿದ ಪರಲೋಕದೇವರಾದ ಯೆಹೋವನು ನಿನ್ನ ಮುಂದೆ ತನ್ನ ದೂತನನ್ನು ಕಳುಹಿಸಿ ನೀನು ಅಲ್ಲಿಂದ ನನ್ನ ಮಗನಿಗೋಸ್ಕರ ಹೆಣ್ಣನ್ನು ತೆಗೆದುಕೊಳ್ಳುವಂತೆ ಅನುಕೂಲಮಾಡುವನು.",
			"8": "<sup>8</sup> ನಿನ್ನ ಹಿಂದೆ ಬರುವದಕ್ಕೆ ಆ ಕನ್ಯೆಗೆ ಇಷ್ಟವಿಲ್ಲದೆ ಹೋದರೆ ನಾನು ಮಾಡಿಸಿದ ಪ್ರಮಾಣದಿಂದ ನೀನು ಬಿಡುಗಡೆಯಾಗಿರುವಿ. ಹೇಗೂ ನನ್ನ ಮಗನನ್ನು ತಿರಿಗಿ ಅಲ್ಲಿಗೆ ಕರಕೊಂಡು ಹೋಗಕೂಡದು ಎಂದು ಹೇಳಿದನು.",
			"9": "<sup>9</sup> ಆಗ ಆ ಸೇವಕನು ತನ್ನ ದಣಿಯಾದ ಅಬ್ರಹಾಮನ ತೊಡೆಯ ಕೆಳಗೆ ಕೈಯಿಟ್ಟು ಅವನು ಹೇಳಿದ್ದಂತೆಯೇ ಪ್ರಮಾಣ ಮಾಡಿದನು.",
			"10": "<sup>10</sup> ತರುವಾಯ ಆ ಸೇವಕನು ತನ್ನ ದಣಿಯ ಒಂಟೆಗಳಲ್ಲಿ ಹತ್ತು ಒಂಟೆಗಳನ್ನು ಸಿದ್ಧಮಾಡಿ ದಣಿಯ ಆಸ್ತಿಯಿಂದ ಶ್ರೇಷ್ಠವಾದ ಒಡವೆಗಳನ್ನು ತನ್ನ ವಶಕ್ಕೆ ತೆಗೆದುಕೊಂಡು ಹೊರಟು ಎರಡು ನದಿಗಳ ಮಧ್ಯದಲ್ಲಿರುವ ಅರಾಮ್ ಸೀಮೆಗೆ ಬಂದು ನಾಹೋರನು ವಾಸಿಸಿದ ಊರನ್ನು ಮುಟ್ಟಿದನು.",
			"11": "<sup>11</sup> ಸಾಯಂಕಾಲದಲ್ಲಿ ಸ್ತ್ರೀಯರು ನೀರುತರುವದಕ್ಕೆ ಹೋಗುವಾಗ ಅವನು ಊರಿನ ಹೊರಗಡೆ ಬಾವಿಯ ಹತ್ತಿರ ಒಂಟೆಗಳನ್ನು ಮಲಗಿಸಿ -",
			"12": "<sup>12</sup> ನನ್ನ ದಣಿಯಾದ ಅಬ್ರಹಾಮನ ದೇವರಾಗಿರುವ ಯೆಹೋವನೇ, ಈ ಹೊತ್ತು ನಾನು ಬಂದ ಕಾರ್ಯವನ್ನು ನೀನು ಅನುಕೂಲಪಡಿಸಿ ನನ್ನ ದಣಿಯಾದ ಅಬ್ರಹಾಮನಿಗೆ ಉಪಕಾರಮಾಡಬೇಕೆಂದು ಬೇಡುತ್ತೇನೆ.",
			"13": "<sup>13</sup> ಇಗೋ, ನೀರಿನ ಬುಗ್ಗೆಯ ಬಳಿಯಲ್ಲಿ ನಿಂತಿದ್ದೇನೆ; ಈ ಊರಿನ ಹೆಣ್ಣುಮಕ್ಕಳು ನೀರಿಗೆ ಬರುತ್ತಾರೆ.",
			"14": "<sup>14</sup> ನಾನು ಯಾವ ಹುಡುಗಿಗೆ - ನೀನು ದಯವಿಟ್ಟು ನಿನ್ನ ಕೊಡವನ್ನು ಇಳಿಸಿ ನನಗೆ ಕುಡಿಯುವದಕ್ಕೆ ಕೊಡು ಎಂದು ಹೇಳುವಾಗ - ನೀನು ಕುಡಿಯಬಹುದು, ಮತ್ತು ನಿನ್ನ ಒಂಟೆಗಳಿಗೂ ನೀರು ಕೊಡುತ್ತೇನೆ ಅನ್ನುವಳೋ, ಅವಳೇ ನಿನ್ನ ದಾಸನಾದ ಇಸಾಕನಿಗೆ ನೀನು ನೇಮಿಸಿರುವ ಕನ್ಯೆಯಾಗಲಿ. ನನ್ನ ದಣಿಯ ಮೇಲೆ ನಿನ್ನ ದಯವದೆ ಎಂದು ಇದರಿಂದ ನನಗೆ ಗೊತ್ತಾಗುವದು ಅಂದನು.",
			"15": "<sup>15</sup> ಅವನು ಹೀಗೆ ಅಂದುಕೊಳ್ಳುತ್ತಿರುವಾಗಲೇ ಅಬ್ರಹಾಮನ ತಮ್ಮನಾದ ನಾಹೋರನ ಹೆಂಡತಿಯಾಗಿರುವ ಮಿಲ್ಕಳ ಮಗನಾದ ಬೆತೂವೇಲನಿಗೆ ಹುಟ್ಟಿದ ರೆಬೆಕ್ಕಳು ಹೆಗಲಿನ ಮೇಲೆ ಕೊಡವನ್ನಿಟ್ಟುಕೊಂಡು ಊರ ಹೊರಗೆ ಬರುವದನ್ನು ಕಂಡನು.",
			"16": "<sup>16</sup> ಆ ಹುಡುಗಿ ಬಹು ಸುಂದರಿಯಾಗಿಯೂ ಇನ್ನೂ ಮದುವೆಯಿಲ್ಲದ ಕನ್ನಿಕೆಯಾಗಿಯೂ ಇದ್ದಳು. ಆಕೆ ಬುಗ್ಗೆಯ ಬಳಿಗೆ ಇಳಿದು ಕೊಡದಲ್ಲಿ ನೀರು ತುಂಬಿಕೊಂಡು ಮೇಲಕ್ಕೆ ಬಂದಾಗ",
			"17": "<sup>17</sup> ಆ ಸೇವಕನು ಓಡಿಬಂದು ಎದುರುಗೊಂಡು - ಅಮ್ಮಾ, ದಯಮಾಡಿ ನಿನ್ನ ಕೊಡದಿಂದ ನನಗೆ ಸ್ವಲ್ಪ ನೀರನ್ನು ಕುಡಿಯುವದಕ್ಕೆ ಕೊಡು ಎಂದು ಕೇಳಿಕೊಳ್ಳಲು",
			"18": "<sup>18</sup> ಆಕೆ - ಕುಡಿಯಪ್ಪಾ ಎಂದು ಹೇಳಿ ತತ್‍ಕ್ಷಣವೇ ಕೊಡವನ್ನು ತನ್ನ ಕೈಯ ಮೇಲೆ ಇಳಿಸಿ ಕುಡಿಯಕೊಟ್ಟಳು.",
			"19": "<sup>19</sup> ನೀರು ಕುಡಿಸಿದ ಮೇಲೆ - ನಾನು ನಿನ್ನ ಒಂಟೆಗಳಿಗೂ ಬೇಕಾದಷ್ಟು ನೀರು ತಂದುಕೊಡುತ್ತೇನೆ ಎಂದು ಹೇಳಿ",
			"20": "<sup>20</sup> ಕೊಡದಲ್ಲಿದ್ದ ನೀರನ್ನು ದೋಣಿಯೊಳಗೆ ಹೊಯಿದು ತಿರಿಗಿ ತರುವದಕ್ಕೆ ಬಾವಿಗೆ ತ್ವರೆಯಾಗಿ ಹೋದಳು. ಹೀಗೆ ಅವನ ಎಲ್ಲಾ ಒಂಟೆಗಳಿಗೂ ತಂದು ಕೊಟ್ಟಳು.",
			"21": "<sup>21</sup> ಅಷ್ಟರಲ್ಲಿ ಆ ಮನುಷ್ಯನು ಏನೂ ಮಾತಾಡದೆ ಆಕೆಯನ್ನು ದೃಷ್ಟಿಸಿ ನೋಡುತ್ತಾ - ಯೆಹೋವನು ನನ್ನ ಪ್ರಯಾಣವನ್ನು ಸಫಲ ಮಾಡಿದನೋ ಇಲ್ಲವೋ ಎಂದು ತಿಳಿಯುವದಕ್ಕಾಗಿ ಕಾದುಕೊಂಡಿದ್ದನು.",
			"22": "<sup>22</sup> ಒಂಟೆಗಳು ಕುಡಿದ ನಂತರ ಅವನು ಆಕೆಯ ಮೂಗಿಗೆ ಅರ್ಧ ತೊಲೆಯ ತೂಕವುಳ್ಳ ಒಂದು ಚಿನ್ನದ ಮೂಗುತಿಯನ್ನೂ ಆಕೆಯ ಕೈಗಳಿಗೆ ಹತ್ತು ತೊಲೆಯ ತೂಕವುಳ್ಳ ಎರಡು ಚಿನ್ನದ ಬಳೆಗಳನ್ನೂ ಇಟ್ಟು - ನೀನು ಯಾರ ಮಗಳು? ದಯವಿಟ್ಟು ನನಗೆ ಹೇಳು.",
			"23": "<sup>23</sup> ನಿನ್ನ ತಂದೆಯ ಮನೆಯಲ್ಲಿ ನಾವು ಇಳುಕೊಳ್ಳುವದಕ್ಕೆ ಸ್ಥಳವಿದೆಯೋ ಎಂದು ಕೇಳಲು ಆಕೆ ಅವನಿಗೆ -",
			"24": "<sup>24</sup> ನಾನು ನಾಹೋರನಿಗೆ ಮಿಲ್ಕಳಲ್ಲಿ ಹುಟ್ಟಿದ ಬೆತೂವೇಲನ ಮಗಳು ಎಂದು ಉತ್ತರಕೊಟ್ಟು -",
			"25": "<sup>25</sup> ಹುಲ್ಲೂ ಮೇವೂ ನಮ್ಮಲ್ಲಿ ಬಹಳ ಉಂಟು, ಮತ್ತು ಇಳುಕೊಳ್ಳುವದಕ್ಕೆ ಸ್ಥಳವದೆ ಅಂದಳು.",
			"26": "<sup>26</sup> ಆ ಮನುಷ್ಯನು ಇದನ್ನು ಕೇಳಿ ಬೊಗ್ಗಿಕೊಂಡು ಯೆಹೋವನಿಗೆ ನಮಸ್ಕರಿಸಿ -",
			"27": "<sup>27</sup> ನನ್ನ ದಣಿಯಾದ ಅಬ್ರಹಾಮನ ದೇವರಾಗಿರುವ ಯೆಹೋವನಿಗೆ ಸ್ತೋತ್ರವಾಗಲಿ. ಆತನು ತನ್ನ ಪ್ರೀತಿಯನ್ನೂ ಸತ್ಯತೆಯನ್ನೂ ನನ್ನ ದಣಿಯಿಂದ ತೆಗೆಯಲಿಲ್ಲ; ನನ್ನನ್ನು ನನ್ನ ದಣಿಯ ಬಂಧುಗಳ ಮನೆಗೆ ನೀಟಾದ ದಾರಿಯಿಂದಲೇ ಕರಕೊಂಡು ಬಂದಿದ್ದಾನೆ ಅಂದನು.",
			"28": "<sup>28</sup> ಆ ಹುಡುಗಿ ಓಡಿಹೋಗಿ ನಡೆದ ಸಂಗತಿಯನ್ನು ತಾಯಿಯ ಮನೆಯಲ್ಲಿದ್ದವರೆಲ್ಲರಿಗೆ ತಿಳಿಸಿದಳು.",
			"29": "<sup>29</sup> ರೆಬೆಕ್ಕಳಿಗೆ ಲಾಬಾನನೆಂಬ ಅಣ್ಣನಿದ್ದನು.",
			"30": "<sup>30</sup> ಅವನು ತನ್ನ ತಂಗಿಯ ಮೇಲೆ ಇದ್ದ ಮೂಗುತಿಯನ್ನೂ ಬಳೆಗಳನ್ನೂ ನೋಡಿ ಆ ಮನುಷ್ಯನು ನನ್ನ ಸಂಗಡ ಹೀಗೆ ಹೀಗೆ ಮಾತಾಡಿದನೆಂದು ತಂಗಿ ಹೇಳಿದ ಮಾತನ್ನು ಕೇಳಿ ಬುಗ್ಗೆಯ ಹತ್ತಿರ ಒಂಟೆಗಳ ಬಳಿಯಲ್ಲಿ ನಿಂತಿದ್ದ ಆ ಮನುಷ್ಯನ ಬಳಿಗೆ ಓಡಿಬಂದನು.",
			"31": "<sup>31</sup> ಬಂದಾಗ ಅವನಿಗೆ - ಯೆಹೋವನ ದಯೆಯನ್ನು ಹೊಂದಿದವನೇ, ಒಳಕ್ಕೆ ಬಾ; ಯಾಕೆ ಹೊರಗೆ ನಿಂತಿರುತ್ತೀ? ನಿನಗೋಸ್ಕರ ನನ್ನ ಮನೆಯನ್ನೂ ಒಂಟೆಗಳಿಗೆ ಸ್ಥಳವನ್ನೂ ಸಿದ್ಧಮಾಡಿದ್ದೇನೆ ಎಂದು ಹೇಳಿದನು.",
			"32": "<sup>32</sup> ಆ ಮನುಷ್ಯನು ಮನೆಯೊಳಕ್ಕೆ ಬಂದಾಗ ಲಾಬಾನನು ಒಂಟೆಗಳ ಹೊರೆಗಳನ್ನು ಇಳಿಸಿ ಅವುಗಳಿಗೆ ಹುಲ್ಲನ್ನೂ ಮೇವನ್ನೂ ಕೊಡಿಸಿ ಆ ಮನುಷ್ಯನ ಮತ್ತು ಅವನ ಸಂಗಡಿದ್ದವರ ಕಾಲುಗಳನ್ನು ತೊಳೆಯುವದಕ್ಕೆ ನೀರನ್ನು ತರಿಸಿದನು.",
			"33": "<sup>33</sup> ಆದರೆ ಅವನಿಗೆ ಊಟಕ್ಕೆ ಬಡಿಸಿದಾಗ ಅವನು - ನಾನು ಬಂದ ಕೆಲಸವನ್ನು ಹೇಳದೆ ಊಟ ಮಾಡುವದಿಲ್ಲ ಅನ್ನಲು ಲಾಬಾನನು - ಹೇಳು ಅಂದನು.",
			"34": "<sup>34</sup> ಅವನು ಹೇಳಿದ್ದೇನಂದರೆ ನಾನು ಅಬ್ರಹಾಮನ ಸೇವಕನು,",
			"35": "<sup>35</sup> ಯೆಹೋವನು ನನ್ನ ದಣಿಯನ್ನು ಬಹಳವಾಗಿ ಅಭಿವೃದ್ಧಿಪಡಿಸಿ ಅವನಿಗೆ ಕುರಿದನಗಳನ್ನೂ ಬೆಳ್ಳಿಬಂಗಾರವನ್ನೂ ದಾಸದಾಸಿಯರನ್ನೂ ಒಂಟೆಗಳನ್ನೂ ಕತ್ತೆಗಳನ್ನೂ ಕೊಟ್ಟದ್ದರಿಂದ ಅವನು ಧನವಂತನಾದನು.",
			"36": "<sup>36</sup> ನನ್ನ ದಣಿಯ ಪತ್ನಿಯಾದ ಸಾರಳು ವೃದ್ಧಾಪ್ಯದಲ್ಲಿ ಅವನಿಗೆ ಮಗನನ್ನು ಹೆತ್ತಳು; ಆ ಮಗನಿಗೆ ನನ್ನ ದಣಿಯು ತನಗಿರುವದನ್ನೆಲ್ಲಾ ಕೊಟ್ಟಿದ್ದಾನೆ.",
			"37": "<sup>37</sup> ಇದಲ್ಲದೆ ನನ್ನ ದಣಿಯು ನನಗೆ - ನಾನು ವಾಸವಾಗಿರುವ ಕಾನಾನ್ ದೇಶದವರಿಂದ",
			"38": "<sup>38</sup> ನನ್ನ ಮಗನಿಗೆ ಹೆಣ್ಣನ್ನು ತೆಗೆದುಕೊಳ್ಳದೆ ನನ್ನ ತಂದೆಯ ಮನೆಗೂ ನನ್ನ ಬಂಧುಗಳ ಬಳಿಗೂ ಹೋಗಿ ಅವರಲ್ಲೇ ಹೆಣ್ಣನ್ನು ತೆಗೆದುಕೊಳ್ಳಬೇಕೆಂದು ಹೇಳಿ ನನ್ನಿಂದ ಪ್ರಮಾಣ ಮಾಡಿಸಿದನು.",
			"39": "<sup>39</sup> ನಾನು ಅವನಿಗೆ - ಒಂದು ವೇಳೆ ನನ್ನ ಹಿಂದೆ ಬರುವದಕ್ಕೆ ಆ ಕನ್ಯೆಗೆ ಮನಸ್ಸಿಲ್ಲದೆ ಹೋದೀತು ಎಂದು ಹೇಳಲು",
			"40": "<sup>40</sup> ಅವನು ನನಗೆ - ನಾನು ಯೆಹೋವನಿಗೆ ನಡೆದುಕೊಳ್ಳುತ್ತೇನಲ್ಲಾ; ಆತನು ತನ್ನ ದೂತನನ್ನು ನಿನ್ನೊಂದಿಗೆ ಕಳುಹಿಸಿ ನೀನು ನನ್ನ ತಂದೆಯ ಮನೆತನಕ್ಕೆ ಸೇರಿರುವ ಬಂಧುಗಳಲ್ಲಿ ನನ್ನ ಮಗನಿಗೆ ಹೆಣ್ಣು ತೆಗೆದುಕೊಳ್ಳುವಂತೆ ಅನುಕೂಲಮಾಡುವನು.",
			"41": "<sup>41</sup> ಆಮೇಲೆ ನಾನು ಮಾಡಿಸಿದ ಪ್ರಮಾಣದಿಂದ ನೀನು ಬಿಡುಗಡೆಯಾಗಿರುವಿ. ನೀನು ನನ್ನ ಜನರ ಬಳಿಗೆ ಹೋದಾಗ ಅವರು ಹೆಣ್ಣನ್ನು ಕೊಡದಿದ್ದರೆ ನಾನು ಮಾಡಿಸಿದ ಪ್ರಮಾಣದಿಂದ ನೀನು ಬಿಡುಗಡೆಯಾಗಿರುವಿ ಎಂದು ಹೇಳಿದನು.",
			"42": "<sup>42</sup> ನಾನು ಈ ಹೊತ್ತು ಈ ಊರಿನ ಬುಗ್ಗೆಯ ಬಳಿಗೆ ಬಂದಾಗ - ನನ್ನ ದಣಿಯಾದ ಅಬ್ರಹಾಮನ ದೇವರಾಗಿರುವ ಯೆಹೋವನೇ, ನೀರಿನ ಬುಗ್ಗೆಯ ಬಳಿಯಲ್ಲಿ ನಿಂತಿದ್ದೇನಷ್ಟೆ; ನೀನು ನನ್ನ ಪ್ರಯಾಣವನ್ನು ಸಫಲಮಾಡಿದ್ದಾದರೆ ನೀರಿಗೆ ಬರುವ ಯಾವ ಹುಡುಗಿಗೆ -",
			"43": "<sup>43</sup> ನೀನು ದಯವಿಟ್ಟು ನಿನ್ನ ಕೊಡದಿಂದ ನನಗೆ ಸ್ವಲ್ಪ ನೀರನ್ನು ಕುಡಿಯುವದಕ್ಕೆ ಕೊಡಮ್ಮಾ ಎಂದು ನಾನು ಹೇಳುವಾಗ",
			"44": "<sup>44</sup> ಅವಳು - ಕುಡಿಯಪ್ಪಾ ಮತ್ತು ನಿನ್ನ ಒಂಟೆಗಳಿಗೂ ನೀರು ತಂದುಕೊಡುತ್ತೇನೆ ಅನ್ನುವಳೋ, ಅವಳೇ ಯೆಹೋವನಿಂದ ನನ್ನ ದಣಿಯ ಮಗನಿಗೆ ನೇಮಕವಾದ ಕನ್ನಿಕೆಯಾಗಿರಲಿ ಅಂದೆನು.",
			"45": "<sup>45</sup> ನಾನು ಹೀಗೆ ಮನಸ್ಸಿನೊಳಗೆ ಮಾತಾಡಿಕೊಳ್ಳುತ್ತಿರುವಾಗಲೇ ರೆಬೆಕ್ಕಳು ಹೆಗಲಿನ ಮೇಲೆ ಕೊಡವನ್ನು ಹೊತ್ತುಕೊಂಡು ಬರುವದನ್ನು ಕಂಡೆನು. ಆಕೆ ಬುಗ್ಗೆಗೆ ಇಳಿದು ನೀರನ್ನು ತೆಗೆದುಕೊಂಡು ಬಂದಾಗ ನಾನು ಆಕೆಗೆ - ನನಗೆ ನೀರನ್ನು ಕುಡಿಯುವದಕ್ಕೆ ಕೊಡಬೇಕಮ್ಮಾ ಎಂದು ಹೇಳಿದೆನು.",
			"46": "<sup>46</sup> ತತ್‍ಕ್ಷಣವೇ ಆಕೆ ಕೊಡವನ್ನು ಹೆಗಲಿನಿಂದ ಇಳಿಸಿ - ಕುಡಿಯಪ್ಪಾ, ನಿನ್ನ ಒಂಟೆಗಳಿಗೂ ತಂದು ಕೊಡುತ್ತೇನೆ ಅಂದಳು. ನಾನು ಕುಡಿದ ಮೇಲೆ ಆಕೆ ಒಂಟೆಗಳಿಗೂ ನೀರು ತಂದುಕೊಟ್ಟಳು.",
			"47": "<sup>47</sup> ನೀನು ಯಾರ ಮಗಳೆಂದು ನಾನು ಕೇಳಿದ್ದಕ್ಕೆ ಆಕೆ - ನಾಹೋರನಿಗೆ ಮಿಲ್ಕಳಲ್ಲಿ ಹುಟ್ಟಿದ ಬೆತೂವೇಲನ ಮಗಳು ಅಂದಳು. ಅದನ್ನು ಕೇಳಿ ನಾನು ಆಕೆಯ ಮೂಗಿಗೆ ಮೂಗುತಿಯನ್ನೂ ಕೈಗಳಿಗೆ ಬಳೆಗಳನ್ನೂ ಇಟ್ಟು",
			"48": "<sup>48</sup> ನನ್ನ ದಣಿಯಾದ ಅಬ್ರಹಾಮನ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಬೊಗ್ಗಿ ನಮಸ್ಕರಿಸಿ ಆತನು ನನ್ನ ದಣಿಯ ಮಗನಿಗೋಸ್ಕರ ಅವನ ತಮ್ಮನ ಮಗಳನ್ನೇ ತೆಗೆದುಕೊಳ್ಳುವಂತೆ ನನ್ನನ್ನು ನೀಟಾದ ದಾರಿಯಲ್ಲಿ ಕರೆದುಕೊಂಡು ಬಂದದ್ದಕ್ಕಾಗಿ ಆತನನ್ನು ಕೊಂಡಾಡಿದೆನು.",
			"49": "<sup>49</sup> ಹೀಗಿರುವಲ್ಲಿ ನೀವು ನನ್ನ ದಣಿಗೆ ಪ್ರೀತಿಯಿಂದಲೂ ನಂಬಿಕೆಯಿಂದಲೂ ನಡೆಯುವದಕ್ಕೆ ಒಪ್ಪಿದರೆ ನನಗೆ ಹೇಳಿರಿ; ಇಲ್ಲವಾದರೆ ಇಲ್ಲವೆನ್ನಿರಿ; ಆಗ ನಾನು ಬಲಗಡೆಗಾಗಲಿ ಎಡಗಡೆಗಾಗಲಿ ತಿರುಗಿಕೊಂಡು ಹೋಗುವೆನು ಅಂದನು.",
			"50": "<sup>50</sup> ಅದಕ್ಕೆ ಲಾಬಾನನು ಬೆತೂವೇಲನೂ - ಈ ಕಾರ್ಯವು ಯೆಹೋವನಿಂದಲೇ ಉಂಟಾಯಿತು. ನಾವಂತೂ ಹೌದೆಂದೂ ಅಲ್ಲವೆಂದೂ ಹೇಳಲಾರೆವು.",
			"51": "<sup>51</sup> ರೆಬೆಕ್ಕಳನ್ನು ನಿನ್ನ ವಶಕ್ಕೆ ಕೊಡುತ್ತೇವೆ; ಕರೆದುಕೊಂಡು ಹೋಗಬಹುದು; ಯೆಹೋವನು ಹೇಳಿದಂತೆಯೇ ಆಕೆ ನಿನ್ನ ದಣಿಯ ಮಗನಿಗೆ ಹೆಂಡತಿಯಾಗಲಿ ಅಂದರು.",
			"52": "<sup>52</sup> ಅಬ್ರಹಾಮನ ಸೇವಕನು ಅವರ ಮಾತನ್ನು ಕೇಳಿದಾಗ ಯೆಹೋವನಿಗೆ ಬೊಗ್ಗಿ ನಮಸ್ಕರಿಸಿದನು.",
			"53": "<sup>53</sup> ಆಮೇಲೆ ತಾನು ತಂದಿದ್ದ ಬೆಳ್ಳಿ ಬಂಗಾರದ ಒಡವೆಗಳನ್ನೂ ವಸ್ತ್ರಗಳನ್ನೂ ತೆಗೆದು ರೆಬೆಕ್ಕಳಿಗೆ ಕೊಟ್ಟನು. ಆಕೆಯ ಅಣ್ಣನಿಗೂ ತಾಯಿಗೂ ಸಹ ಬೆಲೆಯುಳ್ಳ ಉಡುಗೊರೆಗಳನ್ನು ಕೊಟ್ಟನು.",
			"54": "<sup>54</sup> ಬಳಿಕ ಅವನೂ ಅವನ ಸಂಗಡ ಬಂದ ಮನುಷ್ಯರೂ ಊಟ ಉಪಚಾರಗಳನ್ನು ತೀರಿಸಿಕೊಂಡು ರಾತ್ರಿಯೆಲ್ಲಾ ಅಲ್ಲೇ ಇದ್ದರು. ಬೆಳಿಗ್ಗೆ ಎದ್ದಾಗ ಅವನು - ನನ್ನ ದಣಿಯ ಬಳಿಗೆ ಹೊರಡುವದಕ್ಕೆ ನನಗೆ ಅಪ್ಪಣೆಯಾಗಲಿ ಎಂದು ಕೇಳಲು",
			"55": "<sup>55</sup> ರೆಬೆಕ್ಕಳ ಅಣ್ಣನೂ ತಾಯಿಯೂ ಅವನಿಗೆ -ಹುಡುಗಿಯು ಇನ್ನು ಎಂಟು ಹತ್ತು ದಿನವಾದರೂ ನಮ್ಮಲ್ಲಿರಲಿ; ಆಮೇಲೆ ಆಕೆ ಹೋಗಬಹುದು ಎಂದು ಹೇಳಿದರು.",
			"56": "<sup>56</sup> ಅವನು ಅವರಿಗೆ - ಯೆಹೋವನು ನನ್ನ ಪ್ರಯಾಣವನ್ನು ಸಫಲ ಮಾಡಿದ್ದಾನೆ; ಆದದರಿಂದ ನನ್ನನ್ನು ತಡೆಯಬೇಡಿರಿ; ನನ್ನ ದಣಿಯ ಬಳಿಗೆ ಹೋಗುವದಕ್ಕೆ ನನಗೆ ಅಪ್ಪಣೆಯಾಗಬೇಕೆಂದು ಹೇಳಲು",
			"57": "<sup>57</sup> ಅವರು - ನಾವು ಹುಡುಗಿಯನ್ನು ಕರೆದು ಆಕೆಯ ಅಭಿಪ್ರಾಯ ತಿಳಿದುಕೊಳ್ಳುತ್ತೇವೆ ಎಂದು ಹೇಳಿ ರೆಬೆಕ್ಕಳನ್ನು ಕರೆದು -",
			"58": "<sup>58</sup> ಈ ಮನುಷ್ಯನ ಜೊತೆಯಲ್ಲಿ ಹೋಗುವಿಯಾ ಎಂದು ಕೇಳಿದರು.",
			"59": "<sup>59</sup> ಆಕೆ - ಹೋಗುತ್ತೇನೆ ಅನ್ನಲು ಅವರು ತಮ್ಮ ತಂಗಿಯಾದ ರೆಬೆಕ್ಕಳನ್ನೂ ಆಕೆಯ ದಾದಿಯನ್ನೂ ಅಬ್ರಹಾಮನ ಸೇವಕನನ್ನೂ ಅವನ ಸಂಗಡ ಬಂದ ಮನುಷ್ಯರನ್ನೂ ಸಾಗಕಳುಹಿಸಿದರು.",
			"60": "<sup>60</sup> ಮತ್ತು ರೆಬೆಕ್ಕಳಿಗೆ - ತಂಗಿಯೇ, ನಿನ್ನಿಂದ ಲಕ್ಷಾಂತರ ಸಂತಾನವಾಗಲಿ; ನಿನ್ನ ಸಂತಾನದವರು ತಮ್ಮ ವೈರಿಗಳ ಪಟ್ಟಣಗಳನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳಲಿ ಎಂದು ಹೇಳಿ ಹರಸಿದರು.",
			"61": "<sup>61</sup> ರೆಬೆಕ್ಕಳೂ ಆಕೆಯ ದಾಸಿಯರೂ ಒಂಟೆಗಳ ಮೇಲೆ ಹತ್ತಿದವರಾಗಿ ಅಬ್ರಹಾಮನ ಸೇವಕನ ಹಿಂದೆ ಹೋದರು. ಹೀಗೆ ಆ ಸೇವಕನು ರೆಬೆಕ್ಕಳನ್ನು ಕರಕೊಂಡು ಹೊರಟನು.",
			"62": "<sup>62</sup> ಅಷ್ಟರಲ್ಲಿ ಇಸಾಕನು ಲಹೈರೋಯಿ ಬಾವಿಗೆ ಹೋಗಿ ಬಂದಿದ್ದನು; ಕಾನಾನ್ ದೇಶದ ದಕ್ಷಿಣ ಸೀಮೆಯಲ್ಲಿ ವಾಸವಾಗಿದ್ದನು.",
			"63": "<sup>63</sup> ಅವನು ಸಂಜೇವೇಳೆಯಲ್ಲಿ ಧ್ಯಾನಮಾಡುವದಕ್ಕೋಸ್ಕರ ಅಡವಿಗೆ ಹೋಗಿದ್ದಾಗ ಕಣ್ಣೆತ್ತಿ ನೋಡಲಾಗಿ ಒಂಟೆಗಳು ಬರುವದನ್ನು ಕಂಡನು.",
			"64": "<sup>64</sup> ರೆಬೆಕ್ಕಳು ಕಣ್ಣೆತ್ತಿ ಇಸಾಕನನ್ನು ನೋಡಿ ಒಂಟೆಯಿಂದ ಬೇಗ ಇಳಿದು -",
			"65": "<sup>65</sup> ನಮ್ಮನ್ನು ಎದುರುಗೊಳ್ಳುವದಕ್ಕೆ ಅಡವಿಯಲ್ಲಿ ನಡೆದು ಬರುವ ಆ ಮನುಷ್ಯನು ಯಾರೆಂದು ಆ ಸೇವಕನನ್ನು ಕೇಳಿದಳು. ಅವನೇ ನನ್ನ ದಣಿಯೆಂದು ಅವನು ಹೇಳಿದಾಗ ಆಕೆ ಮುಸುಕುಹಾಕಿಕೊಂಡಳು.",
			"66": "<sup>66</sup> ಆ ಸೇವಕನು ತಾನು ಮಾಡಿದ ಕಾರ್ಯಗಳನ್ನೆಲ್ಲಾ ಇಸಾಕನಿಗೆ ತಿಳಿಸಿದನಂತರ",
			"67": "<sup>67</sup> ಇಸಾಕನು ಆಕೆಯನ್ನು ತಾಯಿಯಾದ ಸಾರಳ ಗುಡಾರಕ್ಕೆ ಕರೆದುಕೊಂಡು ಹೋದನು. ಈ ರೀತಿಯಾಗಿ ಅವನು ರೆಬೆಕ್ಕಳನ್ನು ವರಿಸಿದನು. ಆಕೆ ಅವನ ಹೆಂಡತಿಯಾದಳು. ಅವನು ಆಕೆಯನ್ನು ಪ್ರೀತಿಸಿ ತನ್ನ ತಾಯಿ ಸತ್ತ ದುಃಖವನ್ನು ಶಮನ ಮಾಡಿಕೊಂಡನು."
		},
		"25": {
			"1": "<sup>1</sup> ಅಬ್ರಹಾಮನು ಕೆಟೂರಳೆಂಬ ಇನ್ನೊಬ್ಬ ಹೆಂಡತಿಯನ್ನು ಮದುವೆ ಮಾಡಿಕೊಂಡನು.",
			"2": "<sup>2</sup> ಆಕೆ ಅವನಿಗೆ ಜಿಮ್ರಾನ್, ಯೊಕ್ಷಾನ್, ಮೆದಾನ್, ಮಿದ್ಯಾನ್, ಇಷ್ಬಾಕ್, ಶೂಹ ಇವರನ್ನು ಹೆತ್ತಳು.",
			"3": "<sup>3</sup> ಯೊಕ್ಷಾನನು ಶೆಬಾ, ದೆದಾನ್ ಎಂಬಿವರನ್ನು ಪಡೆದನು. ಅಶ್ಯೂರ್ಯರೂ ಲೆಟೂಶ್ಯರೂ ಲೆಯುಮ್ಯರೂ ದೆದಾನನಿಂದ ಹುಟ್ಟಿದವರು.",
			"4": "<sup>4</sup> ಗೇಫಾ, ಗೇಫೆರ್, ಹನೋಕ್, ಅಬೀದಾ, ಎಲ್ದಾಗ ಎಂಬಿವರು ಮಿದ್ಯಾನನಿಂದ ಹುಟ್ಟಿದರು. ಇವರೆಲ್ಲರು ಕೆಟೂರಳ ಸಂತತಿಯವರು.",
			"5": "<sup>5</sup> ಅಬ್ರಹಾಮನು ತನಗಿದ್ದ ಆಸ್ತಿಯನ್ನೆಲ್ಲಾ ಇಸಾಕನಿಗೆ ಕೊಟ್ಟನು.",
			"6": "<sup>6</sup> ತನ್ನ ಉಪಪತ್ನಿಯರ ಮಕ್ಕಳಿಗೆ ಕೆಲವು ಸೊತ್ತುಗಳನ್ನು ದಾನಮಾಡಿ ತಾನು ಇನ್ನೂ ಜೀವದಿಂದಿರುವಾಗ ಅವರನ್ನು ತನ್ನ ಮಗನಾದ ಇಸಾಕನ ಬಳಿಯಿಂದ ಪೂರ್ವದಿಕ್ಕಿನಲ್ಲಿರುವ ಕೆದೆಮ್ ದೇಶಕ್ಕೆ ಕಳುಹಿಸಿದನು.",
			"7": "<sup>7</sup> ಅಬ್ರಹಾಮನು ನೂರೆಪ್ಪತ್ತೈದು ವರುಷ ಬದುಕಿದನು.",
			"8": "<sup>8</sup> ಅವನು ಪೂರ್ಣಾಯುಷ್ಯದಿಂದ ದಿನತುಂಬಿದ ಮುದುಕನಾಗಿ ಪ್ರಾಣಬಿಟ್ಟು ತನ್ನ ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಿದನು.",
			"9": "<sup>9</sup> ಅವನ ಮಕ್ಕಳಾದ ಇಸಾಕ್ ಇಷ್ಮಾಯೇಲರು ಹಿತ್ತಿಯನಾದ ಚೋಹರನ ಮಗ ಎಫ್ರೋನನ ಭೂಮಿಯಲ್ಲಿರುವ ಮಕ್ಪೇಲದ ಗವಿಯಲ್ಲಿ ಅವನಿಗೆ ಸಮಾಧಿಮಾಡಿದರು. ಅದು ಮಮ್ರೆಗೆ ಪೂರ್ವದಿಕ್ಕಿನಲ್ಲಿರುವದು;",
			"10": "<sup>10</sup> ಅಬ್ರಹಾಮನು ಅದನ್ನು ಹಿತ್ತಿಯರಿಂದ ಕೊಂಡುಕೊಂಡಿದ್ದನು; ಅದರಲ್ಲಿ ಅಬ್ರಹಾಮನಿಗೂ ಅವನ ಹೆಂಡತಿಯಾದ ಸಾರಳಿಗೂ ಸಮಾಧಿ ಆಯಿತು.",
			"11": "<sup>11</sup> ಅಬ್ರಹಾಮನು ತೀರಿಹೋದನಂತರ ದೇವರ ಅನುಗ್ರಹವು ಅವನ ಮಗನಾದ ಇಸಾಕನಿಗೂ ಉಂಟಾಯಿತು. ಇಸಾಕನು ಲಹೈರೋಯಿ ಎಂಬ ಬಾವಿಯ ಹತ್ತಿರ ವಾಸವಾಗಿದ್ದನು.",
			"12": "<sup>12</sup> ಅಬ್ರಹಾಮನಿಗೆ ಸಾರಳ ದಾಸಿಯಾದ ಐಗುಪ್ತ್ಯಳಾದ ಹಾಗರಳಲ್ಲಿ ಹುಟ್ಟಿದ ಇಷ್ಮಾಯೇಲನ ವಂಶದ ಚರಿತ್ರೆ.",
			"13": "<sup>13</sup> ಇಷ್ಮಾಯೇಲನ ಮಕ್ಕಳಿಗೂ ಇವರಿಂದ ಹುಟ್ಟಿದ ಕುಲಗಳಿಗೂ ಇರುವ ಹೆಸರುಗಳು ಯಾವವಂದರೆ - ಮೊದಲು ಹುಟ್ಟಿದವನು ನೆಬಾಯೋತ್; ಆಮೇಲೆ ಹುಟ್ಟಿದವರು ಕೇದಾರ್, ಅದ್ಬಯೇಲ್,",
			"14": "<sup>14</sup> ಮಿಬ್ಸಾಮ್, ಮಿಷ್ಮಾ, ದೂಮಾ, ಮಸ್ಸಾ, ಹದದ್, ತೇಮಾ, ಯಟೂರ್, ನಾಫೀಷ್, ಕೇದ್ಮಾ ಎಂಬವರೇ.",
			"16": "<sup>16</sup> ಈ ಹೆಸರುಗಳು ಊರುಗಳಲ್ಲಿಯೂ ಪಾಳೆಯಗಳಲ್ಲಿಯೂ ವಾಸಿಸುವ ಇಷ್ಮಾಯೇಲನ ಸಂತಾನದವರಿಗೆ ಇರುವವು. ಇವರ ಕುಲಗಳ ಸಂಖ್ಯೆಗೆ ಸರಿಯಾಗಿ ಹನ್ನೆರಡು ಮಂದಿ ಅರಸರಿರುತ್ತಾರೆ.",
			"17": "<sup>17</sup> ಇಷ್ಮಾಯೇಲನು ನೂರಮೂವತ್ತೇಳು ವರುಷ ಬದುಕಿದನು; ಆ ವಯಸ್ಸಿನಲ್ಲಿ ಅವನು ಪ್ರಾಣ ಬಿಟ್ಟು ತನ್ನ ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಿದನು.",
			"18": "<sup>18</sup> ಇಷ್ಮಾಯೇಲ್ಯರು ಹವೀಲದಿಂದ ಐಗುಪ್ತದೇಶದ ಮೂಡಲಲ್ಲಿರುವ ಅಶ್ಶೂರಿನ ದಾರಿಯಲ್ಲಿರುವ ಶೂರಿನ ತನಕ ವಾಸಮಾಡಿದರು. ಹೀಗೆ ಅವರಿಗೆ ತಮ್ಮ ಸಂಬಂಧಿಕರ ಎದುರಿನಲ್ಲಿಯೇ ವಾಸಸ್ಥಳ ಸಿಕ್ಕಿತು.",
			"19": "<sup>19</sup> ಅಬ್ರಹಾಮನ ಮಗನಾದ ಇಸಾಕನ ಚರಿತ್ರೆ. ಅಬ್ರಹಾಮನು ಇಸಾಕನನ್ನು ಪಡೆದನು.",
			"20": "<sup>20</sup> ಇಸಾಕನು ನಾಲ್ವತ್ತು ವರುಷದವನಾದಾಗ ಅರಾಮ್ಯನಾದ ಬೆತೂವೇಲನ ಮಗಳಾಗಿಯೂ ಅರಾಮ್ಯನಾದ ಲಾಬಾನನ ತಂಗಿಯಾಗಿಯೂ ಇದ್ದ ರೆಬೆಕ್ಕಳನ್ನು ಪದ್ದನ್ ಅರಾಮಿನಿಂದ ತರಿಸಿ ಹೆಂಡತಿಯಾಗಿ ಮಾಡಿಕೊಂಡನು.",
			"21": "<sup>21</sup> ಆಕೆ ಬಂಜೆಯಾಗಿರಲಾಗಿ ಅವನು ಆಕೆಗೋಸ್ಕರ ಯೆಹೋವನನ್ನು ಬೇಡಿಕೊಂಡನು. ಯೆಹೋವನು ಅವನ ವಿಜ್ಞಾಪನೆಯನ್ನು ಲಾಲಿಸಿದ್ದರಿಂದ ರೆಬೆಕ್ಕಳು ಬಸುರಾದಳು.",
			"22": "<sup>22</sup> ಆಕೆಯ ಗರ್ಭದಲ್ಲಿ ಶಿಶುಗಳು ಒಂದನ್ನೊಂದು ನೂಕಿಕೊಂಡಾಗ ಆಕೆಯು - ಹೀಗಾದರೆ ನಾನು ಯಾಕೆ ಬದುಕಬೇಕು ಎಂದು ಹೇಳಿ ಇದರ ವಿಷಯದಲ್ಲಿ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ವಿಚಾರಿಸುವದಕ್ಕೆ ಹೋದಳು.",
			"23": "<sup>23</sup> ಯೆಹೋವನು ಆಕೆಗೆ - ನಿನ್ನ ಗರ್ಭದಲ್ಲಿ ಎರಡು ಜನಾಂಗಗಳು ಅವೆ; ಆ ಎರಡು ಜನಾಂಗಗಳು ಜನ್ಮಾರಭ್ಯ ವಿರೋಧದಿಂದ ಭೇದವಾಗುವವು; ಅವುಗಳಲ್ಲಿ ಒಂದು ಮತ್ತೊಂದಕ್ಕಿಂತ ಬಲಿಷ್ಠವಾಗುವದು; ಹಿರಿಯದು ಕಿರಿಯದಕ್ಕೆ ಸೇವೆ ಮಾಡುವದು ಎಂದು ಹೇಳಿದನು.",
			"24": "<sup>24</sup> ಆಕೆ ದಿನತುಂಬಿದಾಗ ಅವಳಿಮಕ್ಕಳನ್ನು ಹೆತ್ತಳು.",
			"25": "<sup>25</sup> ಮೊದಲು ಹುಟ್ಟಿದ್ದು ಕೆಂಪಾಗಿಯೂ ಮೈಮೇಲೆಲ್ಲಾ ಕೂದಲಿನ ವಸ್ತ್ರದಂತೆ ರೋಮವುಳ್ಳದ್ದಾಗಿಯೂ ಇತ್ತು; ಅದಕ್ಕೆ ಏಸಾವ ಎಂದು ಹೆಸರಿಟ್ಟರು.",
			"26": "<sup>26</sup> ಎರಡನೆಯ ಶಿಶುವು ಏಸಾವನ ಹಿಮ್ಮಡಿಯನ್ನು ಕೈಯಿಂದ ಹಿಡುಕೊಂಡು ಹುಟ್ಟಿದ್ದರಿಂದ ಅದಕ್ಕೆ ಯಾಕೋಬ ಎಂದು ಹೆಸರಿಟ್ಟರು. ಇವರು ಹುಟ್ಟಿದಾಗ ಇಸಾಕನು ಅರುವತ್ತು ವರುಷದವನಾಗಿದ್ದನು.",
			"27": "<sup>27</sup> ಆ ಹುಡುಗರಿಬ್ಬರೂ ಬೆಳೆದಾಗ ಅವರಲ್ಲಿ ಏಸಾವನೆಂಬವನು ಬೇಟೆಯಾಡುವದರಲ್ಲಿ ಜಾಣನಾದನು; ಅವನು ಅರಣ್ಯವಾಸಿ. ಯಾಕೋಬನು ಸಾಧುಮನುಷ್ಯನಾಗಿ ಗುಡಾರಗಳಲ್ಲೇ ವಾಸಿಸಿದನು.",
			"28": "<sup>28</sup> ಬೇಟೆಯ ಮಾಂಸವು ಇಸಾಕನಿಗೆ ಇಷ್ಟವಾದದರಿಂದ ಅವನು ಏಸಾವನನ್ನು ಪ್ರೀತಿಸಿದನು. ರೆಬೆಕ್ಕಳು ಯಾಕೋಬನನ್ನು ಪ್ರೀತಿಸಿದಳು.",
			"29": "<sup>29</sup> ಒಂದು ದಿನ ಯಾಕೋಬನು ಅಡಿಗೆ ಮಾಡುತ್ತಿರುವಾಗ ಏಸಾವನು ಕಾಡಿನಿಂದ ದಣಿದು ಬಂದು",
			"30": "<sup>30</sup> ಅವನಿಗೆ - ನಾನು ಬಹು ದಣಿದು ಬಂದಿದ್ದೇನೆ; ಆ ಕೆಂಪಾದ ರುಚಿ ಪದಾರ್ಥವನ್ನು ಈಗಲೇ ತಿನ್ನುವದಕ್ಕೆ ಕೊಡಪ್ಪಾ ಎಂದು ಹೇಳಿದನು. (ಈ ಸಂಗತಿಯಿಂದ ಏಸಾವನಿಗೆ ಎದೋಮ್ ಎಂದು ಹೆಸರಾಯಿತು.)",
			"31": "<sup>31</sup> ಯಾಕೋಬನು ಅವನಿಗೆ - ನೀನು ಮೊದಲು ನಿನ್ನ ಚೊಚ್ಚಲತನದ ಹಕ್ಕನ್ನು ನನಗೆ ಮಾರಿಬಿಡು ಅನ್ನಲು ಏಸಾವನು -",
			"32": "<sup>32</sup> ಆಗಲಿ, ನನ್ನಂಥ ಸಾಯುವವನಿಗೆ ಚೊಚ್ಚಲತನದಿಂದ ಪ್ರಯೋಜನವೇನು ಅಂದನು.",
			"33": "<sup>33</sup> ಯಾಕೋಬನು - ಮೊದಲು ಪ್ರಮಾಣಮಾಡು ಅಂದಾಗ ಏಸಾವನು ಪ್ರಮಾಣಮಾಡಿ ಅವನಿಗೆ ತನ್ನ ಚೊಚ್ಚಲತನದ ಹಕ್ಕನ್ನು ಕೊಟ್ಟುಬಿಟ್ಟನು.",
			"34": "<sup>34</sup> ಆಗ ಯಾಕೋಬನು ಏಸಾವನಿಗೆ ರೊಟ್ಟಿಯನ್ನೂ ಅಲಸಂದಿ ಗುಗ್ಗರಿಯನ್ನೂ ಕೊಟ್ಟನು. ಏಸಾವನು ತಿಂದು ಕುಡಿದು ಎದ್ದುಹೋದನು. ಹೀಗೆ ಅವನು ತನ್ನ ಚೊಚ್ಚಲತನದ ಹಕ್ಕನ್ನು ತಾತ್ಸಾರ ಮಾಡಿದನು."
		},
		"26": {
			"1": "<sup>1</sup> ಅಬ್ರಹಾಮನ ಕಾಲದಲ್ಲಿ ಬಂದ ಮೊದಲನೆಯ ಬರವಲ್ಲದೆ ಇನ್ನೂ ಒಂದು ಬರವು ಕಾನಾನ್‍ದೇಶಕ್ಕೆ ಬಂತು. ಆಗ ಇಸಾಕನು ಫಿಲಿಷ್ಟಿಯರ ಅರಸನಾದ ಅಬೀಮೆಲೆಕನ ಬಳಿಗೆ ಗೆರಾರಿಗೆ ಹೋದನು.",
			"2": "<sup>2</sup> ಅಲ್ಲಿ ಯೆಹೋವನು ಅವನಿಗೆ ದರ್ಶನಕೊಟ್ಟು - ನೀನು ಐಗುಪ್ತದೇಶಕ್ಕೆ ಇಳಿದುಹೋಗಬೇಡ; ನಾನು ಹೇಳುವ ದೇಶದಲ್ಲಿ ನೀನು ವಾಸಮಾಡಬೇಕು.",
			"3": "<sup>3</sup> ನೀನು ಈ ದೇಶದಲ್ಲಿ ಪ್ರವಾಸಿಯಾಗಿರು; ನಾನು ನಿನ್ನ ಬಳಿಯಲ್ಲಿದ್ದು ನಿನ್ನನ್ನು ಅಭಿವೃದ್ಧಿಪಡಿಸಿ ನಿನಗೂ ನಿನ್ನ ಸಂತತಿಯವರಿಗೂ ಈ ಪ್ರದೇಶಗಳನ್ನೆಲ್ಲಾ ಕೊಡುವೆನು.",
			"4": "<sup>4</sup> ನಿನ್ನ ತಂದೆಯಾದ ಅಬ್ರಹಾಮನು ನನ್ನ ಮಾತನ್ನು ಕೇಳಿ ನಾನು ಹೇಳಿದಂತೆ ಮಾಡಿ ನನ್ನ ಆಜ್ಞಾನೇಮವಿಧಿಗಳನ್ನು ಕೈಕೊಂಡು ನಡೆದದರಿಂದ ನಾನು ಅವನಿಗೆ ಮಾಡಿದ ಪ್ರಮಾಣವನ್ನು ನೆರವೇರಿಸಿ",
			"5": "<sup>5</sup> ನಿನ್ನ ಸಂತತಿಯವರನ್ನು ಆಕಾಶದಲ್ಲಿರುವ ನಕ್ಷತ್ರಗಳಷ್ಟಾಗಿ ಮಾಡಿ ಅವರಿಗೆ ಈ ಪ್ರದೇಶಗಳನ್ನೆಲ್ಲಾ ಕೊಡುವೆನು. ಭೂಮಿಯ ಎಲ್ಲಾ ಜನಾಂಗಗಳಿಗೂ ನಿನ್ನ ಸಂತತಿಯ ಮೂಲಕ ಆಶೀರ್ವಾದವುಂಟಾಗುವದು ಅಂದನು.",
			"6": "<sup>6</sup> ಇಸಾಕನು ಗೆರಾರಿನಲ್ಲಿ ವಾಸವಾಗಿದ್ದಾಗ ಆ ಸ್ಥಳದ ಜನರು ಅವನ ಹೆಂಡತಿಯನ್ನು ನೋಡಿ ಆಕೆಯ ವಿಷಯದಲ್ಲಿ ವಿಚಾರಿಸಿದಾಗ ಅವನು - ರೆಬೆಕ್ಕಳು ಸುಂದರಿಯಾಗಿರುವದರಿಂದ ಈ ಸ್ಥಳದ ಜನರು ಈಕೆಯ ನಿಮಿತ್ತ ನನ್ನನ್ನು ಕೊಂದಾರು ಅಂದುಕೊಂಡು ಆಕೆಯನ್ನು ತನ್ನ ಹೆಂಡತಿ ಎಂದು ಹೇಳುವದಕ್ಕೆ ಭಯಪಟ್ಟು ತಂಗಿಯಾಗಬೇಕು ಎಂದು ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ಅವನು ಆ ಸ್ಥಳದಲ್ಲಿ ಬಹಳ ದಿವಸ ಇದ್ದ ಮೇಲೆ ಒಂದು ದಿನ ಫಿಲಿಷ್ಟಿಯರ ಅರಸನಾದ ಅಬೀಮೆಲೆಕನು ಕಿಟಕಿಯಿಂದ ನೋಡಿದಾಗ ಇಸಾಕನು ತನ್ನ ಹೆಂಡತಿಯಾದ ರೆಬೆಕ್ಕಳ ಸಂಗಡ ಸರಸವಾಡುವದನ್ನು ಕಂಡನು.",
			"9": "<sup>9</sup> ಆಗ ಅವನು ಇಸಾಕನನ್ನು ಕರೆಸಿ - ನಿಶ್ಚಯವಾಗಿ ಈಕೆಯು ನಿನ್ನ ಹೆಂಡತಿಯಲ್ಲವೇ, ತಂಗಿ ಅಂತ ಯಾಕೆ ಹೇಳಿದಿ ಎಂದು ಕೇಳಲು ಇಸಾಕನು - ಜನರು ಈಕೆಯ ನಿಮಿತ್ತ ನನ್ನನ್ನು ಕೊಂದಾರೆಂದು ಹಾಗೆ ಹೇಳಿದೆನು ಅಂದನು.",
			"10": "<sup>10</sup> ಅದಕ್ಕೆ ಅಬೀಮೆಲೆಕನು - ನೀನು ನಮಗೆ ಹೀಗೆ ಯಾಕೆ ಮಾಡಿದಿ? ಜನರಲ್ಲಿ ಒಬ್ಬನು ನಿನ್ನ ಹೆಂಡತಿಯೊಡನೆ ಕೂಡುವದಕ್ಕೆ ಆಸ್ಪದವಾಗಿತ್ತು. ನಿನ್ನ ಮೂಲಕ ನಮಗೆ ದೋಷ ಪ್ರಾಪ್ತವಾಗುತ್ತಿತ್ತಲ್ಲಾ ಎಂದು ಹೇಳಿ ತನ್ನ ಪ್ರಜೆಗಳಿಗೆ -",
			"11": "<sup>11</sup> ಈ ಮನುಷ್ಯನಿಗಾಗಲಿ ಇವನ ಹೆಂಡತಿಗಾಗಲಿ ಕೇಡುಮಾಡುವವನಿಗೆ ತಪ್ಪದೆ ಮರಣದಂಡನೆಯಾಗುವದು ಎಂದು ಆಜ್ಞೆಮಾಡಿದನು.",
			"12": "<sup>12</sup> ಇಸಾಕನು ಆ ದೇಶದಲ್ಲಿ ಬೀಜವನ್ನು ಬಿತ್ತಿ ಅದೇ ವರುಷದಲ್ಲಿ ನೂರರಷ್ಟು ಬೆಳೆಯನ್ನು ಹೊಂದಿದನು; ಯೆಹೋವನು ಅವನನ್ನು ಅಭಿವೃದ್ಧಿಪಡಿಸಿದನು;",
			"13": "<sup>13</sup> ಅವನ ಐಶ್ವರ್ಯವು ದಿನೇ ದಿನೇ ಹೆಚ್ಚಿದ್ದರಿಂದ ಬಹು ಧನವಂತನಾದನು;",
			"14": "<sup>14</sup> ಅವನಿಗೆ ದನಕುರಿಗಳ ಸಂಪತ್ತೂ ಅನೇಕ ಸೇವಕಜನವೂ ಇದ್ದವು. ಇದನ್ನು ನೋಡಿ ಫಿಲಿಷ್ಟಿಯರು ಹೊಟ್ಟೆಕಿಚ್ಚುಪಟ್ಟರು.",
			"15": "<sup>15</sup> (ಅವನ ತಂದೆಯಾದ ಅಬ್ರಹಾಮನ ಸೇವಕರು ಅಬ್ರಹಾಮನ ಕಾಲದಲ್ಲೇ ತೋಡಿದ್ದ ಬಾವಿಗಳನ್ನು ಫಿಲಿಷ್ಟಿಯರು ಮಣ್ಣು ಹಾಕಿ ಮುಚ್ಚಿದ್ದರು.)",
			"16": "<sup>16</sup> ಹೀಗಿರಲಾಗಿ ಅಬೀಮೆಲೆಕನು ಇಸಾಕನಿಗೆ - ನೀನು ನಮಗಿಂತ ಬಹು ಬಲಿಷ್ಟನಾಗಿದ್ದೀ;",
			"17": "<sup>17</sup> ಆದದರಿಂದ ನಮ್ಮ ಬಳಿಯಿಂದ ಹೊರಟುಹೋಗಬೇಕು ಎಂದು ಹೇಳಲು ಇಸಾಕನು ಅಲ್ಲಿಂದ ಹೋಗಿ ಗೆರಾರ್ ತಗ್ಗಿನ ಬೈಲಿನಲ್ಲಿ ಬೀಡಾರ ಮಾಡಿಕೊಂಡು ಅಲ್ಲಿ ವಾಸವಾಗಿದ್ದನು.",
			"18": "<sup>18</sup> ಅಬ್ರಹಾಮನು ತೋಡಿಸಿದ್ದ ಬಾವಿಗಳನ್ನು ಅವನು ಸತ್ತ ಮೇಲೆ ಫಿಲಿಷ್ಟಿಯರು ಮುಚ್ಚಿಹಾಕಿದ್ದರಿಂದ ಇಸಾಕನು ಅವುಗಳನ್ನು ತಿರಿಗಿ ತೆಗಿಸಿ ತಂದೆ ಇಟ್ಟಿದ್ದ ಹೆಸರುಗಳನ್ನೇ ಇಟ್ಟನು.",
			"19": "<sup>19</sup> ಇಸಾಕನ ಸೇವಕರು ತಗ್ಗಿನಲ್ಲಿ ಅಗೆಯುವಾಗ ಅವರಿಗೆ ಸೆಲೆ ನೀರಿನ ಬಾವಿಯು ಸಿಕ್ಕಿತು.",
			"20": "<sup>20</sup> ಗೆರಾರಿನ ದನಕಾಯುವವರು ಬಂದು ಆ ನೀರು ತಮ್ಮದು ಎಂದು ಹೇಳಿ ಇಸಾಕನ ದನಕಾಯುವವರ ಸಂಗಡ ಜಗಳವಾಡಿದ್ದರಿಂದ ಇಸಾಕನು ಆ ಬಾವಿಗೆ ಏಸೆಕ್ ಎಂದು ಹೆಸರಿಟ್ಟನು.",
			"21": "<sup>21</sup> ತರುವಾಯ ಅವನ ಜನರು ಬೇರೊಂದು ಬಾವಿಯನ್ನು ತೋಡಿದಾಗ ಆ ದೇಶದವರು ಅದಕ್ಕಾಗಿಯೂ ಜಗಳವಾಡಿದ್ದರಿಂದ ಅವನು ಅದಕ್ಕೆ ಸಿಟ್ನಾ ಎಂದು ಹೆಸರಿಟ್ಟನು.",
			"22": "<sup>22</sup> ಅವನು ಅಲ್ಲಿಂದ ಹೊರಟು ಮತ್ತೊಂದು ಬಾವಿಯನ್ನು ಅಗೆಸಿದಾಗ ಅದರ ವಿಷಯದಲ್ಲಿ ಯಾರೂ ಜಗಳವಾಡದೆ ಹೋದದರಿಂದ ಅವನು - ಈಗ ಯೆಹೋವನು ನಮಗೋಸ್ಕರ ಸ್ಥಳ ಮಾಡಿದ್ದಾನಾದದರಿಂದ ಅಭಿವೃದ್ಧಿಯಾಗುವೆವು ಎಂದು ಹೇಳಿ ಅದಕ್ಕೆ ರೆಹೋಬೋತ್ ಎಂದು ಹೆಸರಿಟ್ಟನು.",
			"23": "<sup>23</sup> ಇಸಾಕನು ಅಲ್ಲಿಂದ ಹೊರಟು ಗಟ್ಟಾ ಹತ್ತಿ ಬೇರ್ಷೆಬಕ್ಕೆ ಬಂದನು.",
			"24": "<sup>24</sup> ಆ ರಾತ್ರಿ ಯೆಹೋವನು ಅವನಿಗೆ ಕಾಣಿಸಿಕೊಂಡು - ನಾನು ನಿನ್ನ ತಂದೆಯಾದ ಅಬ್ರಹಾಮನ ದೇವರು; ನೀನು ಭಯಪಡಬೇಡ; ನಾನು ನಿನ್ನ ಬಳಿಯಲ್ಲಿದ್ದೇನೆ; ನನ್ನ ಸೇವಕನಾದ ಅಬ್ರಹಾಮನ ನಿಮಿತ್ತ ನಿನ್ನನ್ನು ಆಶೀರ್ವದಿಸಿ ನಿನ್ನ ಸಂತತಿಯನ್ನು ಹೆಚ್ಚಿಸುವೆನು ಎಂದು ಹೇಳಲಾಗಿ",
			"25": "<sup>25</sup> ಇಸಾಕನು ಯಜ್ಞವೇದಿಯನ್ನು ಕಟ್ಟಿಸಿ ಯೆಹೋವನ ಹೆಸರನ್ನು ಹೇಳಿಕೊಂಡು ಆರಾಧಿಸಿ ಅಲ್ಲಿ ತನ್ನ ಗುಡಾರವನ್ನು ಹಾಕಿಸಿಕೊಂಡನು.",
			"26": "<sup>26</sup> ಅಲ್ಲಿಯೂ ಅವನ ಸೇವಕರು ಬಾವಿಯನ್ನು ಅಗೆದರು. ಆ ಸಮಯದಲ್ಲಿ ಅಬೀಮೆಲೆಕನು ತನ್ನ ಮಂತ್ರಿಯಾದ ಅಹುಜ್ಜತನನ್ನೂ ಸೇನಾಪತಿಯಾದ ಫೀಕೋಲನನ್ನೂ ಸಂಗಡ ಕರೆದುಕೊಂಡು ಗೆರಾರಿನಿಂದ ಇಸಾಕನ ಬಳಿಗೆ ಬರಲು ಇಸಾಕನು -",
			"27": "<sup>27</sup> ನೀವು ನನ್ನನ್ನು ದ್ವೇಷಿಸಿ ನಿಮ್ಮ ಬಳಿಯಿಂದ ಕಳುಹಿಸಿಬಿಟ್ಟಿರಲ್ಲಾ; ಈಗ ನನ್ನ ಬಳಿಗೆ ಯಾಕೆ ಬಂದಿರಿ ಎಂದು ಅವರನ್ನು ಕೇಳಿದ್ದಕ್ಕೆ ಅವರು -",
			"28": "<sup>28</sup> ಯೆಹೋವನು ನಿನ್ನ ಸಂಗಡ ಇದ್ದಾನೆ ಎಂದು ನಮಗೆ ಸ್ಪಷ್ಟವಾಗಿ ಕಂಡುಬಂದದರಿಂದ ನೀನೂ ನಾವೂ ಒಬ್ಬರಿಗೊಬ್ಬರು ಪ್ರಮಾಣಪೂರ್ವಕವಾಗಿ ಒಡಂಬಡಿಕೆ ಮಾಡಿಕೊಳ್ಳೋಣವೆಂದು ಆಲೋಚಿಸಿದೆವು.",
			"29": "<sup>29</sup> ನಾವು ನಿನಗೆ ಯಾವ ಕೇಡನ್ನೂ ಮಾಡದೆ ಹಿತವನ್ನೇ ಮಾಡಿ ನಿನ್ನನ್ನು ಸಮಾಧಾನದೊಡನೆ ಕಳುಹಿಸಿದೆವಲ್ಲಾ; ಅದರಂತೆ ನೀನು ನಮಗೆ ಕೇಡನ್ನು ಮಾಡುವದಿಲ್ಲವೆಂಬದಾಗಿ ಪ್ರಮಾಣಮಾಡಬೇಕು. ನೀನು ಈಗ ಯೆಹೋವನ ದಯೆಯನ್ನು ಹೊಂದಿದವನಾಗಿದ್ದೀಯಷ್ಟೆ ಎಂದು ಹೇಳಿದರು.",
			"30": "<sup>30</sup> ಆಗ ಇಸಾಕನು ಅವರಿಗೆ ಔತಣವನ್ನು ಮಾಡಿಸಿದನು; ಅವರೆಲ್ಲರೂ ಭೋಜನೋಪಚಾರಗಳನ್ನು ತೀರಿಸಿಕೊಂಡು",
			"31": "<sup>31</sup> ಬೆಳಿಗ್ಗೆ ಎದ್ದು ಒಬ್ಬರಿಗೊಬ್ಬರು ಪ್ರಮಾಣಮಾಡಿಕೊಂಡರು. ಇಸಾಕನು ಅವರನ್ನು ಸಾಗಕಳುಹಿಸಲು ಅವರು ಸಮಾಧಾನದೊಡನೆ ಹೊರಟುಹೋದರು.",
			"32": "<sup>32</sup> ಅದೇ ದಿನದಲ್ಲಿ ಇಸಾಕನ ಸೇವಕರು ಬಂದು - ನಮಗೆ ನೀರು ಸಿಕ್ಕಿತು ಎಂದು ಹೇಳಿ ತಾವು ತೋಡಿದ್ದ ಬಾವಿಯ ಸಂಗತಿಯನ್ನು ತಿಳಿಸಲು",
			"33": "<sup>33</sup> ಅವನು ಆ ಬಾವಿಗೆ ಷಿಬಾ ಎಂದು ಹೆಸರಿಟ್ಟನು. ಆದದರಿಂದ ಅಲ್ಲಿರುವ ಊರಿಗೆ ಇಂದಿನವರೆಗೂ ಬೇರ್ಷೆಬ ಎಂದು ಹೆಸರಾಯಿತು.",
			"34": "<sup>34</sup> ಏಸಾವನು ನಾಲ್ವತ್ತು ವರುಷದವನಾದಾಗ ಹಿತ್ತಿಯನಾದ ಬೇರಿಯ ಮಗಳಾದ ಯೆಹೂದೀತಳನ್ನೂ ಹಿತ್ತಿಯನಾದ ಏಲೋನನ ಮಗಳಾದ ಬಾಸೆಮತಳನ್ನೂ ಮದುವೆಮಾಡಿಕೊಂಡನು.",
			"35": "<sup>35</sup> ಇವರ ದೆಸೆಯಿಂದ ಇಸಾಕನಿಗೂ ರೆಬೆಕ್ಕಳಿಗೂ ಮನೋವ್ಯಥೆಯುಂಟಾಯಿತು."
		},
		"27": {
			"1": "<sup>1</sup> ಇಸಾಕನಿಗೆ ಮುಪ್ಪಿನಿಂದ ಕಣ್ಣು ಕಾಣಲಾರದಷ್ಟು ಮೊಬ್ಬಾಗಿರಲು ತನ್ನ ಹಿರೀ ಮಗನಾದ ಏಸಾವನನ್ನು ಕರೆದು - ಮಗನೇ ಅನ್ನಲು ಏಸಾವನು - ಇದ್ದೇನೆ ಅಂದನು.",
			"2": "<sup>2</sup> ಇಸಾಕನು ಅವನಿಗೆ - ನಾನು ಮುದುಕನಾದೆನಷ್ಟೆ; ಯಾವಾಗ ಸಾಯುವೆನೋ ಗೊತ್ತಿಲ್ಲ;",
			"3": "<sup>3</sup> ಆದದರಿಂದ ನೀನು ಬಿಲ್ಲುಬತ್ತಳಿಕೆ ಮುಂತಾದ ಆಯುಧಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಕಾಡಿಗೆ ಹೋಗಿ ಬೇಟೆಯಾಡಿ",
			"4": "<sup>4</sup> ಬೇಟೇ ಮಾಂಸದಿಂದ ನನಗೆ ಇಷ್ಟವಾಗಿರುವ ರುಚಿಪದಾರ್ಥವನ್ನು ನನ್ನ ಊಟಕ್ಕೆ ಸಿದ್ಧಪಡಿಸಿಕೊಡು; ಸಾವು ಬರುವದಕ್ಕಿಂತ ಮುಂಚೆ ನಾನು ನಿನ್ನನ್ನು ಆಶೀರ್ವದಿಸುತ್ತೇನೆ ಎಂದು ಹೇಳಿದನು.",
			"5": "<sup>5</sup> ಇಸಾಕನು ತನ್ನ ಮಗನಾದ ಏಸಾವನಿಗೆ ಹೇಳಿದ ಮಾತು ರೆಬೆಕ್ಕಳ ಕಿವಿಗೆ ಬಿತ್ತು.",
			"6": "<sup>6</sup> ಏಸಾವನು ಬೇಟೆಯಾಡುವದಕ್ಕೆ ಕಾಡಿಗೆ ಹೋಗಿದ್ದಾಗ ರೆಬೆಕ್ಕಳು ತನ್ನ ಮಗನಾದ ಯಾಕೋಬನನ್ನು ಬೋಧಿಸಿ - ನಿನ್ನ ತಂದೆಯು ನಿನ್ನಣ್ಣನಾದ ಏಸಾವನ ಸಂಗಡ ಮಾತಾಡಿ -",
			"7": "<sup>7</sup> ನೀನು ಹೋಗಿ ಬೇಟೆಯಾಡಿ ಬೇಟೇ ಮಾಂಸದಿಂದ ನನ್ನ ಊಟಕ್ಕೆ ರುಚಿಪದಾರ್ಥವನ್ನು ಸಿದ್ಧಮಾಡು; ಸಾವು ಬರುವದಕ್ಕಿಂತ ಮುಂಚೆ ನಾನು ನಿನ್ನನ್ನು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಆಶೀರ್ವದಿಸುತ್ತೇನೆಂದು ಹೇಳುವದನ್ನು ಕೇಳಿದೆನು.",
			"8": "<sup>8</sup> ಆದದರಿಂದ ಮಗನೇ, ನೀನು ನನ್ನ ಮಾತಿಗೆ ಕಿವಿಗೊಟ್ಟು ನನ್ನಪ್ಪಣೆಯಂತೆ ಮಾಡು.",
			"9": "<sup>9</sup> ಆಡಿನ ಹಿಂಡಿಗೆ ಹೋಗಿ ಎರಡು ಒಳ್ಳೇ ಆಡಿನ ಮರಿಗಳನ್ನು ಬೇಗ ತೆಗೆದುಕೊಂಡು ಬಾ; ಅವುಗಳಿಂದ ನಿನ್ನ ತಂದೆಗೆ ಇಷ್ಟವಾಗಿರುವ ರುಚಿಪದಾರ್ಥವನ್ನು ನಾನೇ ಸಿದ್ಧಮಾಡುತ್ತೇನೆ;",
			"10": "<sup>10</sup> ನೀನು ಅದನ್ನು ಅವನ ಬಳಿಗೆ ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ಬಡಿಸಬೇಕು; ಹೀಗೆ ಮಾಡಿದರೆ ಅವನು ಸಾಯುವದಕ್ಕಿಂತ ಮೊದಲು ನಿನ್ನನ್ನೇ ಆಶೀರ್ವದಿಸುವನು ಅಂದಳು.",
			"11": "<sup>11</sup> ಅದಕ್ಕೆ ಯಾಕೋಬನು - ನನ್ನ ಅಣ್ಣನ ಮೈ ಬಹಳ ರೋಮವುಳ್ಳದ್ದು, ನನ್ನ ಮೈ ನುಣ್ಣಗದೆ;",
			"12": "<sup>12</sup> ಒಂದು ವೇಳೆ ತಂದೆಯು ನನ್ನನ್ನು ಮುಟ್ಟಿ ನೋಡಿದರೆ ನಾನು ಅವನಿಗೆ ಗೇಲಿಮಾಡುವವನಾಗಿ ತೋರಿಬಂದು ನನ್ನ ಮೇಲೆ ಶಾಪವನ್ನು ಬರಮಾಡಿಕೊಳ್ಳುವೆನೇ ಹೊರತು ಆಶೀರ್ವಾದವನ್ನು ಬರಮಾಡಿಕೊಳ್ಳುವದಿಲ್ಲ ಅನ್ನಲು",
			"13": "<sup>13</sup> ಅವನ ತಾಯಿ - ಮಗನೇ, ಅವನು ನಿನಗೆ ಶಾಪಕೊಟ್ಟರೆ ಆ ಶಾಪ ನನಗಿರಲಿ; ನೀನು ನನ್ನ ಮಾತನ್ನು ಕೇಳಿ ಆ ಮರಿಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಬಾ ಎಂದು ಹೇಳಲು",
			"14": "<sup>14</sup> ಅವನು ಹೋಗಿ ಅವುಗಳನ್ನು ತಂದು ತಾಯಿಗೆ ಕೊಟ್ಟನು. ರೆಬೆಕ್ಕಳು ಅವನ ತಂದೆಗೆ ಇಷ್ಟವಾಗಿದ್ದ",
			"15": "<sup>15</sup> ಸವಿಯೂಟವನ್ನು ಸಿದ್ಧಪಡಿಸಿದ ಮೇಲೆ ಮನೆಯಲ್ಲಿ ತನ್ನ ವಶದಲ್ಲಿದ್ದ ಹಿರೀಮಗನಾದ ಏಸಾವನ ಶ್ರೇಷ್ಠ ವಸ್ತ್ರಗಳನ್ನು ತೆಗೆದು ತನ್ನ ಕಿರೀಮಗನಾದ ಯಾಕೋಬನಿಗೆ ಹೊದ್ದಿಸಿ",
			"16": "<sup>16</sup> ಆ ಆಡಿನ ಮರಿಗಳ ಚರ್ಮಗಳನ್ನು ಅವನ ಕೈಗಳಿಗೂ ನುಣುಪಾದ ಕೊರಳಿಗೂ ಸುತ್ತಿ ತಾನು ಸಿದ್ಧಮಾಡಿದ್ದ ರುಚಿಪದಾರ್ಥವನ್ನೂ ರೊಟ್ಟಿಯನ್ನೂ",
			"17": "<sup>17</sup> ತನ್ನ ಮಗನಾದ ಯಾಕೋಬನ ಕೈಗೆ ಕೊಟ್ಟಳು.",
			"18": "<sup>18</sup> ಅವನು ತಂದೆಯ ಬಳಿಗೆ ಹೋಗಿ - ಅಪ್ಪಾ ಎಂದು ಹೇಳಲು ತಂದೆಯು - ಏನು ಮಗನೇ, ನೀನು ಯಾರು ಎಂದು ಕೇಳಿದನು.",
			"19": "<sup>19</sup> ಯಾಕೋಬನು ಅವನಿಗೆ - ನಾನು ನಿನ್ನ ಹಿರೀ ಮಗನಾದ ಏಸಾವನು; ನಿನ್ನ ಅಪ್ಪಣೆಯಂತೆ ಮಾಡಿಕೊಂಡು ಬಂದಿದ್ದೇನೆ. ಎದ್ದು ಕೂತುಕೊಂಡು ನಾನು ತಂದಿರುವ ಬೇಟೇ ಮಾಂಸವನ್ನು ಊಟಮಾಡಿ ನನ್ನನ್ನು ಆಶೀರ್ವದಿಸು ಎಂದು ಹೇಳಲು ಇಸಾಕನು -",
			"20": "<sup>20</sup> ಏನು ಮಗನೇ, ಇಷ್ಟು ಬೇಗ ಹೇಗೆ ಸಿಕ್ಕಿತು ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ ಅವನು - ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನು ಅದನ್ನು ನನ್ನೆದುರಿಗೆ ಬರಮಾಡಿದನು ಅಂದನು. ಇಸಾಕನು ಅವನಿಗೆ -",
			"21": "<sup>21</sup> ಕಂದಾ, ನನ್ನ ಬಳಿಗೆ ಬಾ; ನೀನು ನನ್ನ ಮಗನಾದ ಏಸಾವನೋ ಅಲ್ಲವೋ ನಿನ್ನನ್ನು ಮುಟ್ಟಿ ತಿಳುಕೊಳ್ಳಬೇಕು ಎಂದು ಹೇಳಿದನು.",
			"22": "<sup>22</sup> ಯಾಕೋಬನು ತನ್ನ ತಂದೆಯ ಹತ್ತರಕ್ಕೆ ಬಂದಾಗ ಇಸಾಕನು ಅವನನ್ನು ಮುಟ್ಟಿ ನೋಡಿ - ಸ್ವರವೇನೋ ಯಾಕೋಬನ ಸ್ವರ, ಕೈ ಏಸಾವನ ಕೈ ಅಂದುಕೊಂಡನು.",
			"23": "<sup>23</sup> ಯಾಕೋಬನ ಕೈಗಳು ಅವನ ಅಣ್ಣನ ಕೈಗಳಂತೆ ರೋಮವುಳ್ಳವುಗಳಾಗಿದ್ದದರಿಂದ ಇಸಾಕನು ಅವನ ಗುರುತನ್ನು ತಿಳಿಯಲಾರದೆ ಅವನನ್ನು ಆಶೀರ್ವದಿಸಿದನು.",
			"24": "<sup>24</sup> ಅವನು - ನೀನು ನಿಜವಾಗಿ ನನ್ನ ಮಗನಾದ ಏಸಾವನೋ ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ ಯಾಕೋಬನು - ಹೌದು ಅನ್ನಲು",
			"25": "<sup>25</sup> ಇಸಾಕನು - ಆ ಪದಾರ್ಥವನ್ನು ಹತ್ತರಕ್ಕೆ ತೆಗೆದುಕೊಂಡು ಬಾ; ನೀನು ತಂದ ಬೇಟೇ ಮಾಂಸವನ್ನು ನಾನು ಊಟಮಾಡಿದ ಮೇಲೆ ನಿನ್ನನ್ನು ಆಶೀರ್ವದಿಸುವೆನೆಂದು ಹೇಳಿದನು. ಯಾಕೋಬನು ಅದನ್ನು ಅವನ ಹತ್ತರಕ್ಕೆ ತರಲು ಅವನು ತಿಂದನು; ದ್ರಾಕ್ಷಾರಸವನ್ನು ಕೊಡಲು ಕುಡಿದನು.",
			"26": "<sup>26</sup> ಆಮೇಲೆ ಅವನ ತಂದೆಯಾದ ಇಸಾಕನು ಅವನಿಗೆ - ಮಗನೇ, ನೀನು ಹತ್ತರ ಬಂದು ನನಗೆ ಮುದ್ದಿಡು ಎಂದು ಹೇಳಲು",
			"27": "<sup>27</sup> ಅವನು ಹತ್ತರ ಬಂದು ಅವನಿಗೆ ಮುದ್ದಿಟ್ಟನು. ಇಸಾಕನು ಅವನ ವಸ್ತ್ರಗಳ ವಾಸನೆಯನ್ನು ನೋಡಿದಾಗ ಅವನನ್ನು ಆಶೀರ್ವದಿಸಿ - ಆಹಾ, ನನ್ನ ಮಗನ ಸುವಾಸನೆಯು ಯೆಹೋವನು ಆಶೀರ್ವದಿಸಿದ ಹೊಲದ ಸುವಾಸನೆಯಂತಿರುವದು.",
			"28": "<sup>28</sup> ದೇವರು ನಿನಗೆ ಆಕಾಶದ ಮಂಜನ್ನೂ ಸಾರವುಳ್ಳ ಭೂಮಿಯನ್ನೂ ಕೊಟ್ಟು ದವಸಧಾನ್ಯಗಳನ್ನೂ ದ್ರಾಕ್ಷಾರಸವನ್ನೂ ಹೇರಳವಾಗಿ ಅನುಗ್ರಹಿಸಲಿ.",
			"29": "<sup>29</sup> ಪರಜನಗಳು ನಿನ್ನನ್ನು ಸೇವಿಸಲಿ. ಪರಕುಲಗಳು ನಿನಗೆ ಅಡ್ಡಬೀಳಲಿ. ನಿನ್ನ ಅಣ್ಣತಮ್ಮಂದಿರಲ್ಲಿ ನೀನು ದೊರೆಯಾಗು, ನಿನ್ನ ತಾಯಿಯ ಮಕ್ಕಳು ನಿನಗೆ ಅಡ್ಡಬೀಳಲಿ. ನಿನ್ನನ್ನು ಶಪಿಸುವವರಿಗೆ ಶಾಪವು ನಿನ್ನನ್ನು ಆಶೀರ್ವದಿಸುವವರಿಗೆ ಆಶೀರ್ವಾದವೂ ಉಂಟಾಗಲಿ ಅಂದನು.",
			"30": "<sup>30</sup> ಇಸಾಕನು ಅವನನ್ನು ಆಶೀರ್ವದಿಸಿದ ಮೇಲೆ ಯಾಕೋಬನು ತನ್ನ ತಂದೆಯ ಬಳಿಯಿಂದ ಹೊರಟುಹೋದ ಕ್ಷಣವೇ ಅವನಣ್ಣನಾದ ಏಸಾವನು ಬೇಟೆಯಿಂದ ಬಂದನು.",
			"31": "<sup>31</sup> ಅವನೂ ಸವಿಯೂಟವನ್ನು ಸಿದ್ಧಮಾಡಿ ತನ್ನ ತಂದೆಯ ಬಳಿಗೆ ತಂದು ಅವನಿಗೆ - ಅಪ್ಪಾ, ನೀನು ಎದ್ದು ನಿನ್ನ ಮಗನಾದ ನಾನು ಬೇಟೆಯಿಂದ ತಂದಿರುವ ಮಾಂಸವನ್ನು ಊಟಮಾಡಿ ನನ್ನನ್ನು ಆಶೀರ್ವದಿಸು ಎಂದು ಹೇಳಿದನು.",
			"32": "<sup>32</sup> ಅವನ ತಂದೆಯಾದ ಇಸಾಕನು - ನೀನು ಯಾರು ಎಂದು ಅವನನ್ನು ಕೇಳಲು ಅವನು - ನಾನು ನಿನ್ನ ಚೊಚ್ಚಲ ಮಗನಾದ ಏಸಾವನು ಅಂದನು.",
			"33": "<sup>33</sup> ಅದಕ್ಕೆ ಇಸಾಕನು ಬಹಳವಾಗಿ ಗಡಗಡನೆ ನಡುಗುತ್ತಾ - ಯಾರೋ ಬೇರೊಬ್ಬನು ಬೇಟೆ ಮಾಂಸವನ್ನು ತಂದು ನನಗೆ ಕೊಟ್ಟುಹೋದನಲ್ಲಾ. ನೀನು ಬರುವದಕ್ಕಿಂತ ಮುಂಚೆ ಅವನು ತಂದದ್ದರಲ್ಲಿ ನಾನು ಊಟ ಮಾಡಿ ಅವನನ್ನೇ ಆಶೀರ್ವದಿಸಿದೆನು; ಅವನಿಗೆ ಮಾಡಿದ ಆಶೀರ್ವಾದ ತಪ್ಪಲಾರದು ಅಂದನು.",
			"34": "<sup>34</sup> ಏಸಾವನು ತನ್ನ ತಂದೆಯ ಮಾತುಗಳನ್ನು ಕೇಳಿದಾಗ ದುಃಖಾಕ್ರಾಂತನಾಗಿ ಬಹಳ ಅಳುತ್ತಾ - ಅಪ್ಪಾ ತಂದೆಯೇ, ನನ್ನನ್ನು, ನನ್ನನ್ನು ಸಹ ಆಶೀರ್ವದಿಸು ಎಂದು ಬೇಡಿಕೊಳ್ಳಲು",
			"35": "<sup>35</sup> ಇಸಾಕನು - ನಿನ್ನ ತಮ್ಮನು ಮೋಸದಿಂದ ಬಂದು ನಿನಗಾಗಬೇಕಾಗಿದ್ದ ಆಶೀರ್ವಾದವನ್ನು ಪಡಕೊಂಡನು ಅಂದನು.",
			"36": "<sup>36</sup> ಅದಕ್ಕೆ ಏಸಾವನು - ಯಾಕೋಬನೆಂಬ ಹೆಸರು ಅವನಿಗೆ ಉಂಟಾದದ್ದು ನ್ಯಾಯವಲ್ಲವೋ? ಎರಡು ಸಾರಿ ನನ್ನನ್ನು ವಂಚಿಸಿದ್ದಾನೆ, ಹಿಂದೆ ನನ್ನ ಚೊಚ್ಚಲತನದ ಹಕ್ಕನ್ನು ಅಪಹರಿಸಿದನು; ಈಗ ಬಂದು ನನಗಾಗಬೇಕಾಗಿದ್ದ ಆಶೀರ್ವಾದವನ್ನೂ ತೆಗೆದುಕೊಂಡಿದ್ದಾನೆ ಎಂದು ಹೇಳಿ ತನ್ನ ತಂದೆಯನ್ನು - ನನಗೋಸ್ಕರವೂ ನಿನ್ನಲ್ಲಿ ಆಶೀರ್ವಾದವಿಲ್ಲವೋ ಎಂದು ಕೇಳಲು",
			"37": "<sup>37</sup> ಇಸಾಕನು - ನನ್ನ ಮಗನೇ, ಅವನನ್ನು ನಿನ್ನ ದೊರೆಯನ್ನಾಗಿ ನೇಮಿಸಿದ್ದೇನೆ; ಅವನ ಅಣ್ಣತಮ್ಮಂದಿರನ್ನು ಅವನಿಗೆ ಸೇವಕರನ್ನಾಗಿ ಕೊಟ್ಟಿದ್ದೇನೆ; ದವಸಧಾನ್ಯಗಳನ್ನೂ ದ್ರಾಕ್ಷಾರಸವನ್ನೂ ಅವನ ಪೋಷಣೆಗಾಗಿ ಕೊಟ್ಟಿದ್ದೇನೆ ನೋಡು. ಹೀಗಿರುವಲ್ಲಿ ನಾನು ನಿನಗೋಸ್ಕರ ಏನು ಮಾಡಲಿ ಅಂದನು.",
			"38": "<sup>38</sup> ಏಸಾವನು ಅವನಿಗೆ - ಅಪ್ಪಾ, ತಂದೆಯೇ, ನಿನ್ನಲ್ಲಿ ಆ ಒಂದೇ ಆಶೀರ್ವಾದವಿತ್ತೋ? ಅಪ್ಪಾ, ನನ್ನನ್ನು, ನನ್ನನ್ನೂ ಆಶೀರ್ವದಿಸಬೇಕು",
			"39": "<sup>39</sup> ಎಂದು ಹೇಳಿ ಗೋಳಾಡುತ್ತಾ ಅಳಲು ಅವನ ತಂದೆಯಾದ ಇಸಾಕನು ಅವನಿಗೆ - ಸಾರವುಳ್ಳ ಭೂಮಿಯೂ ಮೇಲಿನಿಂದ ಬೀಳುವ ಆಕಾಶದ ಮಂಜೂ ಇರುವ ಸ್ಥಳದಲ್ಲಿ ನಿನ್ನ ನಿವಾಸವಿರುವದು.",
			"40": "<sup>40</sup> ನೀನು ಕತ್ತಿಯಿಂದಲೇ ಜೀವನ ಮಾಡುವಿ. ನಿನ್ನ ತಮ್ಮನಿಗೆ ಸೇವಕನಾಗಿರುವಿ. ಆದರೂ ನೀನು ಕೊಸರಿಕೊಳ್ಳುವಾಗ ಅವನು ನಿನ್ನ ಹೆಗಲಿನ ಮೇಲೆ ಹಾಕಿರುವ ನೊಗವನ್ನು ಮುರಿದುಹಾಕುವಿ ಎಂದು ಹೇಳಿದನು.",
			"41": "<sup>41</sup> ತಂದೆಯು ಯಾಕೋಬನಿಗೆ ಮಾಡಿದ ಆಶೀರ್ವಾದದ ನಿಮಿತ್ತ ಏಸಾವನು ಯಾಕೋಬನನ್ನು ಹಗೆಮಾಡಿ ತನ್ನ ಮನಸ್ಸಿನೊಳಗೆ - ತಂದೆಗೋಸ್ಕರ ದುಃಖಿಸುವ ಕಾಲ ಸಮೀಪಿಸಿತು; ಆಗ ನನ್ನ ತಮ್ಮನಾದ ಯಾಕೋಬನನ್ನು ಕೊಲ್ಲುವೆನು ಅಂದುಕೊಂಡನು.",
			"42": "<sup>42</sup> ಹಿರೀ ಮಗನಾದ ಏಸಾವನು ಹೇಳಿದ ಮಾತು ರೆಬೆಕ್ಕಳಿಗೆ ತಿಳಿದುಬಂದಾಗ ಆಕೆಯು ತನ್ನ ಕಿರೀ ಮಗನಾದ ಯಾಕೋಬನನ್ನು ಕರೆದು ಅವನಿಗೆ - ನೋಡು, ನಿನ್ನ ಅಣ್ಣನಾದ ಏಸಾವನು ನಿನ್ನನ್ನು ಕೊಲ್ಲಬೇಕೆಂದು ಯೋಚಿಸಿ ತನ್ನ ಹೊಟ್ಟೆಯ ಉರಿಯನ್ನು ಆರಿಸಿಕೊಳ್ಳುತ್ತಾನೆ.",
			"43": "<sup>43</sup> ಆದದರಿಂದ ಮಗನೇ, ನನ್ನ ಮಾತನ್ನು ಕೇಳು; ನೀನು ಎದ್ದು ಖಾರಾನ್ ಊರಿನಲ್ಲಿರುವ ನನ್ನಣ್ಣನಾದ ಲಾಬಾನನ ಬಳಿಗೆ ಓಡಿಹೋಗಿ ಕೆಲವು ಕಾಲ,",
			"44": "<sup>44</sup> ಅಂದರೆ ನಿನ್ನಣ್ಣನ ಕೋಪವು ಶಮನವಾಗುವವರೆಗೂ, ಅವನ ಬಳಿಯಲ್ಲಿರು.",
			"45": "<sup>45</sup> ನಿನ್ನಣ್ಣನು ನೀನು ಮಾಡಿರುವದನ್ನು ಮರೆತು ತನ್ನ ಕೋಪವನ್ನು ಬಿಟ್ಟಾಗ ನಾನು ನಿನ್ನನ್ನು ಅಲ್ಲಿಂದ ಕರಸುತ್ತೇನೆ. ನಾನು ಒಂದೇ ದಿನದಲ್ಲಿ ನಿಮ್ಮಿಬ್ಬರನ್ನೂ ಕಳಕೊಳ್ಳುವದು ಯಾತಕ್ಕೆ ಎಂದು ಹೇಳಿದಳು.",
			"46": "<sup>46</sup> ರೆಬೆಕ್ಕಳು ಇಸಾಕನಿಗೆ - ಹಿತ್ತಿಯರಾದ ಈ ಸ್ತ್ರೀಯರ ದೆಸೆಯಿಂದ ನನಗೆ ಬೇಸರವಾಯಿತು. ಯಾಕೋಬನೂ ಈ ದೇಶದವರಲ್ಲಿ ಹೆಣ್ಣನ್ನು ಆದುಕೊಂಡು ಇಂಥಾ ಹಿತ್ತಿಯ ಸ್ತ್ರೀಯನ್ನು ಮದುವೆಮಾಡಿಕೊಂಡರೆ ನಾನು ಇನ್ನೂ ಬದುಕುವದರಿಂದ ಪ್ರಯೋಜನವೇನು ಎಂದು ಹೇಳಿದಳು."
		},
		"28": {
			"1": "<sup>1</sup> ಆಗ ಇಸಾಕನು ಯಾಕೋಬನನ್ನು ಕರೆದು ಅವನಿಗೆ - ನೀನು ಕಾನಾನ್ಯರ ಹೆಣ್ಣನ್ನು ಮದುವೆಮಾಡಿಕೊಳ್ಳಬೇಡ;",
			"2": "<sup>2</sup> ಪದ್ದನ್ಅರಾಮ್ ದೇಶದಲ್ಲಿರುವ ನಿನ್ನ ತಾಯಿಯ ತಂದೆಯಾದ ಬೆತೂವೇಲನ ಮನೆಗೆ ಹೊರಟುಹೋಗಿ ಅಲ್ಲಿ ನಿನ್ನ ಸೋದರ ಮಾವನಾದ ಲಾಬಾನನ ಹೆಣ್ಣು ಮಕ್ಕಳಲ್ಲಿ ಒಬ್ಬಳನ್ನು ಮದುವೆಮಾಡಿಕೋ ಎಂದು ಅಪ್ಪಣೆಕೊಟ್ಟು -",
			"3": "<sup>3</sup> ಸರ್ವಶಕ್ತನಾದ ದೇವರು ನಿನ್ನನ್ನು ಆಶೀರ್ವದಿಸಿ ನಿನಗೆ ಬಹಳ ಸಂತತಿಯನ್ನು ಕೊಟ್ಟು ನಿನ್ನಿಂದ ಅನೇಕ ಜನಾಂಗಗಳು ಹುಟ್ಟುವಂತೆ ಅನುಗ್ರಹಿಸಲಿ;",
			"4": "<sup>4</sup> ಆತನು ಅಬ್ರಹಾಮನಿಗೆ ಕೊಟ್ಟ ಆಶೀರ್ವಾದವನ್ನು ನಿನಗೂ ನಿನ್ನ ಸಂತತಿಗೂ ಕೊಟ್ಟು ತಾನು ಅಬ್ರಹಾಮನಿಗೆ ವಾಗ್ದಾನ ಮಾಡಿದ್ದೂ ನೀನು ಪ್ರವಾಸವಾಗಿರುವಂಥದೂ ಆಗಿರುವ ಈ ದೇಶವನ್ನು ನೀನು ಬಾಧ್ಯನಾಗಿ ಹೊಂದುವಂತೆ ಮಾಡಲಿ ಎಂದು ಹೇಳಿ ಆಶೀರ್ವದಿಸಿ ಕಳುಹಿಸಿಬಿಟ್ಟನು.",
			"5": "<sup>5</sup> ಯಾಕೋಬನು ಪದ್ದನ್ಅರಾಮಿಗೆ ಹೊರಟು ಅರಾಮ್ಯನಾದ ಬೆತೂವೇಲನ ಮಗನಾಗಿಯೂ ಯಾಕೋಬನಿಗೂ ಏಸಾವನಿಗೂ ತಾಯಿಯಾದ ರೆಬೆಕ್ಕಳ ಅಣ್ಣನಾಗಿಯೂ ಇದ್ದ ಲಾಬಾನನ ಬಳಿಗೆ ಹೋದನು.",
			"6": "<sup>6</sup> ಇಸಾಕನು ಯಾಕೋಬನನ್ನು ಆಶೀರ್ವದಿಸಿ ಪದ್ದನ್ಅರಾಮಿನಲ್ಲಿ ಹೆಣ್ಣನ್ನು ತೆಗೆದುಕೊಳ್ಳುವದಕ್ಕೆ ಕಳುಹಿಸಿದ್ದನ್ನೂ ಅವನು ಯಾಕೋಬನನ್ನು ಆಶೀರ್ವದಿಸುವಾಗ ಅವನಿಗೆ - ನೀನು ಕಾನಾನ್ಯರ ಹೆಣ್ಣನ್ನು ತೆಗೆದುಕೊಳ್ಳಬೇಡವೆಂದು ಅಪ್ಪಣೆಕೊಟ್ಟದ್ದನ್ನೂ",
			"7": "<sup>7</sup> ಯಾಕೋಬನು ತನ್ನ ತಂದೆತಾಯಿಗಳು ಹೇಳಿದಂತೆ ಪದ್ದನ್ಅರಾಮಿಗೆ ಹೋದದ್ದನ್ನೂ ಏಸಾವನು ನೋಡಿದಾಗ",
			"8": "<sup>8</sup> ಕಾನಾನ್ಯರ ಹೆಣ್ಣುಮಕ್ಕಳು ತನ್ನ ತಂದೆಯಾದ ಇಸಾಕನಿಗೆ ಇಷ್ಟವಿಲ್ಲವೆಂದು ತಿಳುಕೊಂಡು",
			"9": "<sup>9</sup> ಇಷ್ಮಾಯೇಲನ ಬಳಿಗೆ ಹೋಗಿ ಅಬ್ರಹಾಮನ ಮಗನಾದ ಇಷ್ಮಾಯೇಲನ ಮಗಳೂ ನೆಬಾಯೋತನ ತಂಗಿಯೂ ಆಗಿದ್ದ ಮಹಲತ್ ಎಂಬಾಕೆಯನ್ನು ಮದುವೆ ಮಾಡಿಕೊಂಡು ತನ್ನ ಹೆಂಡರಲ್ಲಿ ಸೇರಿಸಿದನು.",
			"10": "<sup>10</sup> ಯಾಕೋಬನು ಖಾರಾನಿಗೆ ಹೋಗಬೇಕೆಂದು ಬೇರ್ಷೆಬದಿಂದ ಹೊರಟನು.",
			"11": "<sup>11</sup> ಅವನು ಹೋಗುತ್ತಿರಲು ಒಂದು ಸ್ಥಳಕ್ಕೆ ಸೇರಿದಾಗ ಹೊತ್ತು ಮುಣುಗಿದ್ದರಿಂದ ಆ ರಾತ್ರಿ ಅಲ್ಲಿಯೇ ಇಳಿದುಕೊಂಡನು. ಅವನು ಅಲ್ಲಿದ್ದ ಕಲ್ಲುಗಳಲ್ಲಿ ಒಂದು ಕಲ್ಲನ್ನು ತಲೆಗಿಂಬಾಗಿ ಇಟ್ಟುಕೊಂಡು ಆ ಸ್ಥಳದಲ್ಲಿ ಮಲಗಿಕೊಂಡನು.",
			"12": "<sup>12</sup> ಆ ರಾತ್ರಿ ಅವನು ಕನಸುಕಂಡನು. ಆ ಕನಸಿನಲ್ಲಿ ಒಂದು ನಿಚ್ಚಣಿಗೆ ನೆಲದ ಮೇಲೆ ನಿಂತಿತ್ತು; ಅದರ ತುದಿ ಆಕಾಶವನ್ನು ಮುಟ್ಟಿತ್ತು; ಅದರ ಮೇಲೆ ದೇವದೂತರು ಹತ್ತುತ್ತಾ ಇಳಿಯುತ್ತಾ ಇದ್ದರು.",
			"13": "<sup>13</sup> ಇದಲ್ಲದೆ ಯೆಹೋವನು ಅವನ ಬಳಿಯಲ್ಲಿ ನಿಂತು - ನಾನು ನಿನ್ನ ತಂದೆಯಾದ ಅಬ್ರಹಾಮನ ದೇವರೂ ಇಸಾಕನ ದೇವರೂ ಆಗಿರುವ ಯೆಹೋವನು. ನೀನು ಮಲಗಿಕೊಂಡಿರುವ ಈ ದೇಶವನ್ನು ನಿನಗೂ ನಿನ್ನ ಸಂತತಿಗೂ ಕೊಡುತ್ತೇನೆ.",
			"14": "<sup>14</sup> ನಿನ್ನ ಸಂತತಿಯು ಭೂಮಿಯ ಧೂಳಿನಂತೆ ಅಸಂಖ್ಯವಾಗುವದು; ನೀನು ಪೂರ್ವಪಶ್ಚಿಮದಕ್ಷಿಣೋತ್ತರ ದಿಕ್ಕುಗಳಿಗೆ ಹರಡಿಕೊಳ್ಳುವಿ. ನಿನ್ನ ಮೂಲಕವೂ ನಿನ್ನ ಸಂತತಿಯ ಮೂಲಕವೂ ಭೂಮಿಯ ಎಲ್ಲಾ ಕುಲದವರಿಗೂ ಆಶೀರ್ವಾದವುಂಟಾಗುವದು.",
			"15": "<sup>15</sup> ಇದಲ್ಲದೆ ನಾನು ನಿನ್ನ ಸಂಗಡ ಇದ್ದು ನೀನು ಹೋಗುವ ಎಲ್ಲಾ ಸ್ಥಳಗಳಲ್ಲಿ ನಿನ್ನನ್ನು ಕಾಪಾಡಿ ತಿರಿಗಿ ಈ ದೇಶಕ್ಕೆ ಬರಮಾಡುತ್ತೇನೆ. ನಾನು ನಿನಗೆ ಹೇಳಿದ್ದನ್ನೆಲ್ಲಾ ನೆರವೇರಿಸಿದ ಹೊರತು ಬಿಡುವದಿಲ್ಲ ಅಂದನು.",
			"16": "<sup>16</sup> ಯಾಕೋಬನು ನಿದ್ದೆಯಿಂದ ಎಚ್ಚತ್ತು - ನಿಜವಾಗಿ ಯೆಹೋವನು ಈ ಸ್ಥಳದಲ್ಲಿ ಇದ್ದಾನೆ;",
			"17": "<sup>17</sup> ಅದು ನನಗೆ ತಿಳಿಯದೆ ಹೋಯಿತು ಅಂದುಕೊಂಡು ಭಯಪಟ್ಟವನಾಗಿ - ಈ ಸ್ಥಳವು ಎಷ್ಟೋ ಭಯಂಕರವಾದದ್ದು; ಇದು ದೇವರ ಮನೆಯೇ ಹೊರತು ಬೇರೆಯಲ್ಲ; ಇದು ಪರಲೋಕದ ಬಾಗಿಲು ಎಂದು ಹೇಳಿದನು.",
			"18": "<sup>18</sup> ಹೊತ್ತಾರೆ ಅವನು ಎದ್ದಾಗ ತಾನು ತಲೆಗಿಂಬಿಗೆ ಇಟ್ಟುಕೊಂಡಿದ್ದ ಕಲ್ಲನ್ನು ಕಂಬವಾಗಿ ನಿಲ್ಲಿಸಿ ಅದರ ತುದಿಯಲ್ಲಿ ಎಣ್ಣೇ ಹೊಯಿದನು.",
			"19": "<sup>19</sup> ಆ ಸ್ಥಳಕ್ಕೆ ಬೇತೇಲ್ ಎಂದು ಹೆಸರಿಟ್ಟನು. ಅಲ್ಲಿರುವ ಊರಿಗೆ ಮೊದಲು ಲೂಜ್ ಎಂದು ಹೆಸರಿತ್ತು.",
			"20": "<sup>20</sup> ಯಾಕೋಬನು ಅಲ್ಲಿ - ದೇವರು ನನ್ನ ಸಂಗಡ ಇದ್ದು ನಾನು ಹೋಗುವ ಈ ಮಾರ್ಗದಲ್ಲಿ ನನ್ನನ್ನು ಕಾಪಾಡಿ ತಿನ್ನುವದಕ್ಕೆ ಆಹಾರವನ್ನೂ ಉಡುವದಕ್ಕೆ ವಸ್ತ್ರವನ್ನೂ ಕೊಟ್ಟು",
			"21": "<sup>21</sup> ತಿರಿಗಿ ನನ್ನನ್ನು ತಂದೆಯ ಮನೆಗೆ ಸುರಕ್ಷಿತವಾಗಿ ಬರಮಾಡಿದರೆ ಯೆಹೋವನೇ ನನ್ನ ದೇವರಾಗಿರುವನು.",
			"22": "<sup>22</sup> ಮತ್ತು ನಾನು ಕಂಬವಾಗಿ ನಿಲ್ಲಿಸಿರುವ ಈ ಕಲ್ಲು ದೇವರ ಮನೆಯಾಗುವದು. ಇದಲ್ಲದೆ ನಿನ್ನಿಂದ ನನಗೆ ಬರುವ ಎಲ್ಲಾ ಆಸ್ತಿಯಲ್ಲಿ ಹತ್ತರಲ್ಲೊಂದು ಪಾಲನ್ನು ನಿನಗೆ ಸಮರ್ಪಿಸುವೆನೆಂಬದಾಗಿ ಮಾತುಕೊಡುತ್ತೇನೆ ಎಂದು ಹರಕೆಮಾಡಿಕೊಂಡನು."
		},
		"29": {
			"1": "<sup>1</sup> ಆಮೇಲೆ ಯಾಕೋಬನು ದಾರಿ ಹಿಡಿದು ಮೂಡಲ ದೇಶದವರ ಸೀಮೆಗೆ ಬಂದನು.",
			"2": "<sup>2</sup> ಅಲ್ಲಿಗೆ ಬಂದಾಗ ಅಡವಿಯೊಳಗೆ ಬಾವಿಯನ್ನು ಕಂಡನು. ಬಾವಿಯ ಹತ್ತರ ಮೂರು ಕುರಿಹಿಂಡುಗಳು ಮಲಗಿದ್ದವು. ಕುರಿಗಳನ್ನು ನೀರು ಕುಡಿಸುವದಕ್ಕಾಗಿ ಆ ಬಾವಿಗೆ ಹೊಡಕೊಂಡು ಹೋಗುವ ಪದ್ಧತಿಯಿತ್ತು. ಬಾವಿಯ ಮೇಲೆ ಮುಚ್ಚಿದ್ದ ಕಲ್ಲು ದೊಡ್ಡದಾಗಿತ್ತು.",
			"3": "<sup>3</sup> ಹಿಂಡುಗಳೆಲ್ಲಾ ಅಲ್ಲಿ ಕೂಡಿದಾಗ ಕಾಯುವವರು ಬಾವಿಯ ಮೇಲೆ ಮುಚ್ಚಿದ್ದ ಕಲ್ಲನ್ನು ಸರಿಸಿ ಕುರಿಗಳಿಗೆ ನೀರು ಕುಡಿಸಿ ತಿರಿಗಿ ಕಲ್ಲನ್ನು ಬಾವಿಯ ಮೇಲೆ ಮುಚ್ಚುವರು.",
			"4": "<sup>4</sup> ಯಾಕೋಬನು ಅವರಿಗೆ - ಅಣ್ಣಂದಿರಾ, ನೀವು ಯಾವ ಊರಿನವರು ಎಂದು ಕೇಳಲು - ನಾವು ಖಾರಾನ್ ಊರಿನವರು ಅಂದರು.",
			"5": "<sup>5</sup> ಅವನು ಅವರನ್ನು - ನಾಹೋರನ ಮಗನಾದ ಲಾಬಾನನನ್ನು ಬಲ್ಲಿರಾ ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ - ಬಲ್ಲೆವು ಅಂದರು.",
			"6": "<sup>6</sup> ಅವನಿಗೆ ಕ್ಷೇಮವೋ ಎಂದು ಕೇಳಲು ಅವರು - ಕ್ಷೇಮ; ಅಗೋ, ಅವನ ಮಗಳಾದ ರಾಹೇಲಳು ಕುರಿಗಳ ಸಂಗಡ ಬರುತ್ತಾಳೆ ಅಂದರು.",
			"7": "<sup>7</sup> ಯಾಕೋಬನು - ಇನ್ನೂ ಬಹಳ ಹೊತ್ತು ಇದೆ; ಕುರಿಗಳನ್ನು ಕೂಡಿಸುವ ಸಮಯವಾಗಿಲ್ಲ; ನೀರು ಕುಡಿಸಿ ಅವುಗಳನ್ನು ಮೇಯಿಸಿರಿ ಅನ್ನಲು",
			"8": "<sup>8</sup> ಅವರು - ಹಿಂಡುಗಳೆಲ್ಲಾ ಕೂಡಿ ಬಂದಾಗ ಕಾಯುವವರು ಬಾವಿಯ ಮೇಲೆ ಮುಚ್ಚಿರುವ ಕಲ್ಲನ್ನು ತೆಗೆಯುವ ತನಕ ಕುಡಿಸಕೂಡದು; ಅನಂತರ ಕುರಿಗಳಿಗೆ ನೀರು ಕುಡಿಸುತ್ತೇವೆ ಅಂದರು.",
			"9": "<sup>9</sup> ಅವನು ಅವರೊಂದಿಗೆ ಮಾತಾಡುತ್ತಿರುವಾಗಲೇ ರಾಹೇಲಳು ತನ್ನ ತಂದೆಯ ಕುರಿಗಳನ್ನು ಹೊಡಕೊಂಡು ಬಂದಳು; ಆಕೆಯೇ ಅವುಗಳನ್ನು ಮೇಯಿಸುವವಳಾಗಿದ್ದಳು.",
			"10": "<sup>10</sup> ಯಾಕೋಬನು ತನ್ನ ಸೋದರಮಾವನಾದ ಲಾಬಾನನ ಮಗಳಾಗಿರುವ ರಾಹೇಲಳನ್ನೂ ತನ್ನ ಸೋದರಮಾವನ ಕುರಿಗಳನ್ನೂ ಕಂಡಾಗ ಬಾವಿಯ ಹತ್ತರಕ್ಕೆ ಹೋಗಿ ಅದರ ಮೇಲೆ ಮುಚ್ಚಿದ್ದ ಕಲ್ಲನ್ನು ಸರಿಸಿ ತನ್ನ ಸೋದರಮಾವನ ಕುರಿಗಳಿಗೆ ನೀರು ಕುಡಿಸಿದನು.",
			"11": "<sup>11</sup> ಅವನು ರಾಹೇಲಳಿಗೆ ಮುದ್ದಿಟ್ಟು ಸ್ವರವೆತ್ತಿ ಕಣ್ಣೀರುಸುರಿಸಿ ಆಕೆಗೆ -",
			"12": "<sup>12</sup> ನಾನು ನಿನ್ನ ತಂದೆಗೆ ಸೋದರಳಿಯನೂ ರೆಬೆಕ್ಕಳ ಮಗನೂ ಆಗಿದ್ದೇನೆ ಎಂದು ಹೇಳಿದಾಗ ರಾಹೇಲಳು ಓಡಿಹೋಗಿ ಆ ಮಾತನ್ನು ತನ್ನ ತಂದೆಗೆ ತಿಳಿಸಿದಳು.",
			"13": "<sup>13</sup> ಲಾಬಾನನು ತನ್ನ ಸೋದರಳಿಯನು ಬಂದ ವರ್ತಮಾನವನ್ನು ಕೇಳಿದಾಗ ಅವನನ್ನು ಎದುರುಗೊಳ್ಳುವದಕ್ಕೆ ಓಡಿಬಂದು ಅಪ್ಪಿಕೊಂಡು ಮುದ್ದಿಟ್ಟು ತನ್ನ ಮನೆಗೆ ಕರಕೊಂಡು ಬಂದನು. ಯಾಕೋಬನು ಲಾಬಾನನಿಗೆ ಎಲ್ಲಾ ಸಂಗತಿಗಳನ್ನು ತಿಳಿಸಲು",
			"14": "<sup>14</sup> ಲಾಬಾನನು ಅವನಿಗೆ - ನಿಜವಾಗಿ ನೀನು ನನ್ನ ರಕ್ತ ಸಂಬಂಧಿಯಾಗಿದ್ದೀ ಎಂದು ಹೇಳಿದನು. ಅವನು ಒಂದು ತಿಂಗಳಿನವರೆಗೂ ಲಾಬಾನನ ಬಳಿಯಲ್ಲಿ ವಾಸಮಾಡಿದನು.",
			"15": "<sup>15</sup> ಆಮೇಲೆ ಲಾಬಾನನು ಅವನನ್ನು - ನೀನು ನನ್ನನ್ನು ಸಂಬಂಧಿಯೆಂದು ಸುಮ್ಮನೆ ಸೇವಿಸುವದು ನ್ಯಾಯವೋ? ನಿನ್ನ ಕೆಲಸಕ್ಕಾಗಿ ನಾನು ನಿನಗೆ ಏನು ಕೊಡಲಿ ಎಂದು ಕೇಳಿದನು.",
			"16": "<sup>16</sup> ಲಾಬಾನನಿಗೆ ಇಬ್ಬರು ಹೆಣ್ಣುಮಕ್ಕಳಿದ್ದರು; ದೊಡ್ಡವಳ ಹೆಸರು ಲೇಯಾ, ಚಿಕ್ಕವಳ ಹೆಸರು ರಾಹೇಲ್.",
			"17": "<sup>17</sup> ಲೇಯಾ ಎಂಬಾಕೆಯ ಕಣ್ಣುಗಳು ಕಾಂತಿಹೀನವಾಗಿದ್ದವು; ರಾಹೇಲಳು ರೂಪವತಿಯೂ ಲಾವಣ್ಯವತಿಯೂ ಆಗಿದ್ದಳು.",
			"18": "<sup>18</sup> ಯಾಕೋಬನು ರಾಹೇಲಳನ್ನು ಮೆಚ್ಚಿಕೊಂಡು - ನಿನ್ನ ಕಿರೀ ಮಗಳಾದ ರಾಹೇಲಳಿಗೋಸ್ಕರ ನಿನ್ನಲ್ಲಿ ಏಳು ವರುಷ ಸೇವೆ ಮಾಡುವೆನು ಎಂದು ಹೇಳಲು",
			"19": "<sup>19</sup> ಲಾಬಾನನು - ಆಕೆಯನ್ನು ಬೇರೊಬ್ಬನಿಗೆ ಕೊಡುವದಕ್ಕಿಂತ ನಿನಗೆ ಕೊಡುವದೇ ಉಚಿತ; ನನ್ನ ಬಳಿಯಲ್ಲಿರು ಅಂದನು.",
			"20": "<sup>20</sup> ಆದದರಿಂದ ಯಾಕೋಬನು ರಾಹೇಲಳಿಗೋಸ್ಕರ ಏಳು ವರುಷ ಸೇವೆ ಮಾಡಿದನು; ಆದರೂ ಆಕೆಯಲ್ಲಿ ಬಹಳ ಪ್ರೀತಿಯನ್ನಿಟ್ಟಿದ್ದದರಿಂದ ಅದು ಅವನಿಗೆ ಸ್ವಲ್ಪ ದಿವಸದಂತೆ ಕಾಣಿಸಿತು.",
			"21": "<sup>21</sup> ತರುವಾಯ ಯಾಕೋಬನು ಲಾಬಾನನಿಗೆ - ಗೊತ್ತುಮಾಡಿದ ಕಾಲವು ಮುಗಿದು ಹೋಯಿತು; ಆಕೆಯನ್ನು ನನ್ನ ಹೆಂಡತಿಯಾಗುವಂತೆ ನನ್ನ ಸ್ವಾಧೀನಕ್ಕೆ ಕೊಡು ಎಂದು ಹೇಳಿದನು.",
			"22": "<sup>22</sup> ಆಗ ಲಾಬಾನನು ಆ ಸ್ಥಳದವರೆಲ್ಲರನ್ನು ಕರಸಿ ಔತಣವನ್ನು ಮಾಡಿಸಿದನು.",
			"23": "<sup>23</sup> ಸಾಯಂಕಾಲದಲ್ಲಿ ತನ್ನ ಹಿರೀ ಮಗಳಾದ ಲೇಯಳನ್ನೇ ಯಾಕೋಬನಿಗೆ ಒಪ್ಪಿಸಿಕೊಟ್ಟನು. ಅವನು ಆಕೆಯನ್ನು ಕೂಡಿದನು.",
			"24": "<sup>24</sup> (ಲಾಬಾನನು ತನ್ನ ಮಗಳಾದ ಲೇಯಳಿಗೆ ಜಿಲ್ಪಾ ಎಂಬ ಒಬ್ಬ ದಾಸಿಯನ್ನು ಕೊಟ್ಟನು.)",
			"25": "<sup>25</sup> ಬೆಳಿಗ್ಗೆ ಆಕೆ ಲೇಯಳೆಂದು ಯಾಕೋಬನಿಗೆ ತಿಳಿದು ಬರಲು ಅವನು",
			"26": "<sup>26</sup> ಲಾಬಾನನಿಗೆ - ಇದೇನು ನೀನು ನನಗೆ ಮಾಡಿದ್ದು? ರಾಹೇಲಳಿಗೋಸ್ಕರ ನಿನಗೆ ಸೇವೆಮಾಡಿದೆನಲ್ಲಾ; ಯಾಕೆ ನನಗೆ ಮೋಸಮಾಡಿದಿ ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ ಲಾಬಾನನು - ಹಿರೀಮಗಳಿಗಿಂತ ಮೊದಲು ಕಿರೀ ಮಗಳನ್ನು ಮದುವೆಮಾಡಿಸಿಕೊಡುವದು ನಮ್ಮ ದೇಶದ ಪದ್ಧತಿಯಲ್ಲ.",
			"27": "<sup>27</sup> ಆಕೆಯ [ಮದುವೆಯ] ವಾರವನ್ನು ಪೂರೈಸು; ಅನಂತರ ಈ ನನ್ನ ಕಿರೀ ಮಗಳನ್ನೂ ನಿನಗೆ ಕೊಡುತ್ತೇನೆ; ಈಕೆಗೋಸ್ಕರ ನೀನು ಇನ್ನೂ ಏಳು ವರುಷ ಸೇವೆಮಾಡು ಅಂದನು.",
			"28": "<sup>28</sup> ಯಾಕೋಬನು ಅದಕ್ಕೆ ಒಪ್ಪಿ ಹಿರಿಯವಳ [ಮದುವೆಯ] ವಾರವನ್ನು ತೀರಿಸಿದನು. ಆಗ ಲಾಬಾನನು ತನ್ನ ಮಗಳಾದ ರಾಹೇಲಳನ್ನು",
			"29": "<sup>29</sup> ಯಾಕೋಬನಿಗೆ ಹೆಂಡತಿಯಾಗಿ ಕೊಟ್ಟು ಆಕೆಗೆ ಬಿಲ್ಹಾ ಎಂಬ ಒಬ್ಬ ದಾಸಿಯನ್ನು ಕೊಟ್ಟನು.",
			"30": "<sup>30</sup> ಯಾಕೋಬನು ರಾಹೇಲಳನ್ನೂ ಕೂಡಿ ಆಕೆಯನ್ನು ಲೇಯಳಿಗಿಂತ ಹೆಚ್ಚಾಗಿ ಪ್ರೀತಿಸಿದನು. ಆಕೆಗೋಸ್ಕರ ಲಾಬಾನನ ಬಳಿಯಲ್ಲಿ ಇನ್ನೂ ಏಳುವರುಷ ಸೇವೆ ಮಾಡಿದನು.",
			"31": "<sup>31</sup> ಲೇಯಳು ಯಾಕೋಬನಿಗೆ ಅಲಕ್ಷ್ಯವಾದಳೆಂದು ಯೆಹೋವನು ನೋಡಿ ಆಕೆಯನ್ನು ಬಸುರಾಗುವಂತೆ ಮಾಡಿದನು; ಆದರೆ ರಾಹೇಲಳು ಬಂಜೆಯಾಗಿದ್ದಳು.",
			"32": "<sup>32</sup> ಲೇಯಳು ಗರ್ಭಿಣಿಯಾಗಿ ಗಂಡು ಮಗುವನ್ನು ಹೆತ್ತು - ಯೆಹೋವನು ನನ್ನ ವ್ಯಥೆಯನ್ನು ನೋಡಿದ್ದಾನೆ; ಇನ್ನು ಮುಂದೆ ನನ್ನ ಗಂಡನು ನನ್ನನ್ನು ಪ್ರೀತಿಸುವನು ಎಂದು ಹೇಳಿ ಅದಕ್ಕೆ ರೂಬೇನೆಂದು ಹೆಸರಿಟ್ಟಳು.",
			"33": "<sup>33</sup> ಆಕೆ ತಿರಿಗಿ ಗರ್ಭಿಣಿಯಾಗಿ ಗಂಡು ಮಗುವನ್ನು ಹೆತ್ತು - ನಾನು ಗಂಡನ ಅಲಕ್ಷ್ಯಕ್ಕೆ ಗುರಿಯಾದೆನೆಂದು ಯೆಹೋವನು ತಿಳಿದಿದ್ದರಿಂದ ಈ ಮಗುವನ್ನೂ ದಯಪಾಲಿಸಿದನೆಂದು ಹೇಳಿ ಅದಕ್ಕೆ ಸಿಮೆಯೋನ್ ಎಂದು ಹೆಸರಿಟ್ಟಳು.",
			"34": "<sup>34</sup> ಆಕೆಯು ತಿರಿಗಿ ಗರ್ಭಧರಿಸಿ ಗಂಡುಮಗುವನ್ನು ಹೆತ್ತು - ಈಗಲಾದರೂ ನನ್ನ ಗಂಡನೂ ನಾನೂ ಒಂದಾಗುವೆವು; ಅವನಿಗೆ ಮೂರು ಮಂದಿ ಗಂಡು ಮಕ್ಕಳನ್ನು ಹೆತ್ತಿದ್ದೇನಲ್ಲಾ ಎಂದು ಹೇಳಿ ಅದಕ್ಕೆ ಲೇವಿಯೆಂದು ಹೆಸರಿಟ್ಟಳು.",
			"35": "<sup>35</sup> ಆಕೆಯು ತಿರಿಗಿ ಗರ್ಭಿಣಿಯಾಗಿ ಗಂಡುಮಗುವನ್ನು ಹೆತ್ತು - ಈಗ ಯೆಹೋವನಿಗೆ ಉಪಕಾರಸ್ತುತಿ ಮಾಡುವೆನು ಎಂದು ಹೇಳಿ ಅದಕ್ಕೆ ಯೆಹೂದಾ ಎಂದು ಹೆಸರಿಟ್ಟಳು. ಆಮೇಲೆ ಆಕೆಗೆ ಗರ್ಭಧಾರಣೆಯಾಗುವದು ತಡವಾಯಿತು."
		},
		"30": {
			"1": "<sup>1</sup> ರಾಹೇಲಳು ತಾನು ಮಕ್ಕಳನ್ನು ಹೆರದೆ ಇರುವದನ್ನು ನೋಡಿ ತನ್ನ ಅಕ್ಕನ ಮೇಲೆ ಹೊಟ್ಟೆಕಿಚ್ಚುಪಟ್ಟು ಯಾಕೋಬನಿಗೆ - ಮಕ್ಕಳನ್ನು ನನಗೆ ಕೊಡು; ಇಲ್ಲದಿದ್ದರೆ ಸಾಯುವೆನು ಎಂದು ಹೇಳಲು",
			"2": "<sup>2</sup> ಅವನು ಆಕೆಯ ಮೇಲೆ ಕೋಪಗೊಂಡು - ದೇವರು ನಿನಗೆ ಮಕ್ಕಳನ್ನು ಕೊಡದೆಹೋದ ಮೇಲೆ ಕೊಡಲಿಕ್ಕೆ ನನ್ನಿಂದಾದೀತೋ ಅಂದನು.",
			"3": "<sup>3</sup> ಅದಕ್ಕೆ ಆಕೆಯು - ನನ್ನ ದಾಸಿಯಾದ ಬಿಲ್ಹಾ ಇದ್ದಾಳಲ್ಲಾ; ಅವಳನ್ನು ಕೂಡು; ಅವಳು ಬಸುರಾದರೆ ನನ್ನ ತೊಡೆಯ ಮೇಲೆ ಹೆರಿಸುವೆನು; ಹಾಗೆ ನನಗೂ ಸಂತಾನವಾಗುವದು ಎಂದು ಹೇಳಿ",
			"4": "<sup>4</sup> ಅವನಿಗೆ ತನ್ನ ದಾಸಿಯಾದ ಬಿಲ್ಹಳನ್ನು ಹೆಂಡತಿಯಾಗುವದಕ್ಕೆ ಒಪ್ಪಿಸಿದಳು.",
			"5": "<sup>5</sup> ಅವನು ಅವಳನ್ನು ಕೂಡಲು ಬಿಲ್ಹಳು ಗರ್ಭಿಣಿಯಾಗಿ ಗಂಡುಮಗುವನ್ನು ಹೆರಲು",
			"6": "<sup>6</sup> ರಾಹೇಲಳು - ದೇವರು ನನ್ನ ಕಡೆಗೆ ನ್ಯಾಯ ತೀರಿಸಿದ್ದಾನೆ; ಆತನು ನನ್ನ ಮೊರೆಯನ್ನು ಕೇಳಿ ನನಗೆ ಮಗನನ್ನು ಅನುಗ್ರಹಿಸಿದ್ದಾನೆ ಎಂದು ಹೇಳಿ ಅದಕ್ಕೆ ದಾನ್ ಎಂದು ಹೆಸರಿಟ್ಟಳು.",
			"7": "<sup>7</sup> ರಾಹೇಲಳ ದಾಸಿಯಾದ ಬಿಲ್ಹಳು ತಿರಿಗಿ ಬಸುರಾಗಿ ಯಾಕೋಬನಿಗೆ ಎರಡನೆಯ ಗಂಡು ಮಗುವನ್ನು ಹೆರಲು",
			"8": "<sup>8</sup> ರಾಹೇಲಳು - ನನ್ನ ಅಕ್ಕನ ಸಂಗಡ ಬಲವಾಗಿ ಹೋರಾಡಿ ಗೆದ್ದಿದ್ದೇನೆ ಎಂದು ಹೇಳಿ ಅದಕ್ಕೆ ನಫ್ತಾಲಿ ಎಂದು ಹೆಸರಿಟ್ಟಳು.",
			"9": "<sup>9</sup> ಲೇಯಳು ತನಗೆ ಹೆರಿಗೆ ನಿಂತುಹೋಗಿರುವದನ್ನು ತಿಳಿದು ತನ್ನ ದಾಸಿಯಾದ ಜಿಲ್ಪಳನ್ನು ಯಾಕೋಬನ ಹೆಂಡತಿಯಾಗುವದಕ್ಕೆ ಒಪ್ಪಿಸಿದಳು.",
			"10": "<sup>10</sup> ಲೇಯಳ ದಾಸಿಯಾದ ಜಿಲ್ಪಳು ಯಾಕೋಬನಿಗೆ ಗಂಡು ಮಗುವನ್ನು ಹೆರಲು",
			"11": "<sup>11</sup> ಲೇಯಳು - ನನಗೆ ಶುಭವಾಯಿತೆಂದು ಹೇಳಿ ಅದಕ್ಕೆ ಗಾದ್ ಎಂದು ಹೆಸರಿಟ್ಟಳು.",
			"12": "<sup>12</sup> ಲೇಯಳ ದಾಸಿಯಾದ ಜಿಲ್ಪಳು ಯಾಕೋಬನಿಗೆ ಇನ್ನೊಂದು ಗಂಡುಮಗುವನ್ನು ಹೆರಲು",
			"13": "<sup>13</sup> ಲೇಯಳು - ನಾನು ಧನ್ಯಳಾದೆ; ಸ್ತ್ರೀಯರು ನನ್ನನ್ನು ಧನ್ಯಳೆಂದು ಹೊಗಳುವರು ಎಂದು ಹೇಳಿ ಅದಕ್ಕೆ ಆಶೇರೆಂದು ಹೆಸರಿಟ್ಟಳು.",
			"14": "<sup>14</sup> ಗೋದೀ ಸುಗ್ಗಿಯ ಕಾಲದಲ್ಲಿ ರೂಬೇನನು ಅಡವಿಗೆ ಹೋಗಿದ್ದಾಗ ಕಾಮಜನಕ ಫಲಗಳನ್ನು ಕಂಡು ತನ್ನ ತಾಯಿಯಾದ ಲೇಯಳಿಗೆ ತಂದು ಕೊಡಲು ರಾಹೇಲಳು ಆಕೆಗೆ - ನಿನ್ನ ಮಗನು ತಂದಿರುವ ಕಾಮಜನಕ ಫಲಗಳಲ್ಲಿ ಕೆಲವನ್ನು ನನಗೆ ಕೊಡಮ್ಮಾ ಎಂದು ಕೇಳಿಕೊಂಡಳು.",
			"15": "<sup>15</sup> ಅದಕ್ಕೆ ಲೇಯಳು - ನೀನು ನನ್ನ ಗಂಡನನ್ನು ಅಪಹರಿಸಿದ್ದು ಸಾಲದೋ? ನನ್ನ ಮಗನು ತಂದ ಕಾಮಜನಕ ಫಲಗಳನ್ನೂ ತೆಗೆದುಕೊಳ್ಳಬೇಕೆಂದು ಅಪೇಕ್ಷಿಸುತ್ತೀಯಾ ಎಂದು ಹೇಳಲು ರಾಹೇಲಳು - ಒಳ್ಳೇದು, ನೀನು ನಿನ್ನ ಮಗನ ಹಣ್ಣುಗಳನ್ನು ನನಗೆ ಕೊಟ್ಟರೆ ಗಂಡನು ಈ ಹೊತ್ತು ರಾತ್ರಿ ನಿನ್ನಲ್ಲೇ ಇರಲಿ ಅಂದಳು.",
			"16": "<sup>16</sup> ಸಾಯಂಕಾಲದಲ್ಲಿ ಯಾಕೋಬನು ಅಡವಿಯಿಂದ ಬರುವಾಗ ಲೇಯಳು ಅವನೆದುರಿಗೆ ಹೋಗಿ - ನೀನು ನನ್ನಲ್ಲಿ ಬರಬೇಕು; ನಾನು ನನ್ನ ಮಗನ ಕಾಮಜನಕ ಫಲಗಳನ್ನು ಕೊಟ್ಟು ನಿನ್ನನ್ನು ಸಂಪಾದಿಸಿಕೊಂಡಿದ್ದೇನೆ ಅಂದಳು. ಆ ಹೊತ್ತು ರಾತ್ರಿ ಅವನು ಆಕೆಯೊಂದಿಗೆ ಇದ್ದನು.",
			"17": "<sup>17</sup> ದೇವರು ಲೇಯಳ ಪ್ರಾರ್ಥನೆಯನ್ನು ಕೇಳಿದನು; ಆಕೆಯು ಗರ್ಭಿಣಿಯಾಗಿ ಯಾಕೋಬನಿಗೆ ಐದನೆಯ ಗಂಡುಮಗುವನ್ನು ಹೆತ್ತು -",
			"18": "<sup>18</sup> ನಾನು ನನ್ನ ದಾಸಿಯನ್ನು ನನ್ನ ಗಂಡನ ವಶಕ್ಕೆ ಒಪ್ಪಿಸಿದ್ದರಿಂದ ದೇವರು ನನಗೆ ಪ್ರತಿಫಲವನ್ನು ಕೊಟ್ಟಿದ್ದಾನೆಂದು ಹೇಳಿ ಅದಕ್ಕೆ ಇಸ್ಸಾಕಾರೆಂದು ಹೆಸರಿಟ್ಟಳು.",
			"19": "<sup>19</sup> ಲೇಯಳು ತಿರಿಗಿ ಗರ್ಭಿಣಿಯಾಗಿ ಯಾಕೋಬನಿಗೆ ಆರನೆಯ ಗಂಡುಮಗುವನ್ನು ಹೆತ್ತಳು.",
			"20": "<sup>20</sup> ಆಕೆಯು - ದೇವರು ನನಗೆ ಒಳ್ಳೇ ಬಳುವಳಿಯನ್ನು ಕೊಟ್ಟಿದ್ದಾನೆ; ನಾನು ನನ್ನ ಗಂಡನಿಗೆ ಆರು ಮಂದಿ ಗಂಡುಮಕ್ಕಳನ್ನು ಹೆತ್ತದ್ದರಿಂದ ಅವನು ನನ್ನೊಡನೆಯೇ ವಾಸಿಸುವನು ಎಂದು ಹೇಳಿ ಅದಕ್ಕೆ ಜೆಬುಲೂನ್ ಎಂದು ಹೆಸರಿಟ್ಟಳು.",
			"21": "<sup>21</sup> ತರುವಾಯ ಆಕೆಯು ಹೆಣ್ಣುಮಗುವನ್ನು ಹೆತ್ತು ಅದಕ್ಕೆ ದೀನಾ ಎಂದು ಹೆಸರಿಟ್ಟಳು.",
			"22": "<sup>22</sup> ಆಮೇಲೆ ದೇವರು ರಾಹೇಲಳನ್ನು ನೆನಸಿಕೊಂಡು ಆಕೆಯ ಮೊರೆಯನ್ನು ಕೇಳಿ ಆಕೆಗೆ ಮಕ್ಕಳಾಗುವಂತೆ ಮಾಡಿದನು.",
			"23": "<sup>23</sup> ಆಕೆಯು ಗರ್ಭಿಣಿಯಾಗಿ ಗಂಡುಮಗುವನ್ನು ಹೆತ್ತು ದೇವರು ನನಗಿದ್ದ ಅವಮಾನವನ್ನು ಪರಿಹರಿಸಿದ್ದಾನೆ ಅಂದಳು.",
			"24": "<sup>24</sup> ಇದಲ್ಲದೆ ಆಕೆಯು - ಯೆಹೋವನು ಇನ್ನೊಂದು ಗಂಡು ಮಗುವನ್ನು ನನಗೆ ದಯಪಾಲಿಸಬೇಕೆಂದು ಅದಕ್ಕೆ ಯೋಸೇಫನೆಂದು ಹೆಸರಿಟ್ಟಳು.",
			"25": "<sup>25</sup> ರಾಹೇಲಳು ಯೋಸೇಫನನ್ನು ಹೆತ್ತ ನಂತರ ಯಾಕೋಬನು ಲಾಬಾನನಿಗೆ - ನನಗೆ ಅಪ್ಪಣೆ ಕೊಡಬೇಕು; ನಾನು ನನ್ನ ದೇಶದಲ್ಲಿರುವ ಸ್ವಂತ ಊರಿಗೆ ಹೋಗಬೇಕು.",
			"26": "<sup>26</sup> ನಾನು ಸೇವೆ ಮಾಡಿ ಪಡಕೊಂಡ ಹೆಂಡತಿಯರನ್ನೂ ನನ್ನ ಮಕ್ಕಳನ್ನೂ ನನಗೆ ಒಪ್ಪಿಸು; ನಾನು ಹೋಗುತ್ತೇನೆ. ನಾನು ನಿನಗೆ ಮಾಡಿದ ಸೇವೆ ನಿನಗೆ ತಿಳಿದೇ ಅದೆ ಎಂದು ಹೇಳಲು",
			"27": "<sup>27</sup> ಲಾಬಾನನು ಅವನಿಗೆ - ನನ್ನ ಮೇಲೆ ದಯವಿಟ್ಟು ನನ್ನ ಬಳಿಯಲ್ಲೇ ಇರು; ಯೆಹೋವನು ನಿನ್ನ ನಿಮಿತ್ತ ನನ್ನನ್ನು ಅಭಿವೃದ್ಧಿಪಡಿಸಿದ್ದಾನೆಂದು ಚೆನ್ನಾಗಿ ತಿಳಿದಿದ್ದೇನೆ.",
			"28": "<sup>28</sup> ನಾನು ನಿನಗೆ ಏನು ಕೊಡಬೇಕು ಹೇಳು, ಕೊಡುತ್ತೇನೆ ಎಂದು ಹೇಳಿದನು.",
			"29": "<sup>29</sup> ಅದಕ್ಕೆ ಯಾಕೋಬನು - ನಾನು ನಿನಗೆ ಮಾಡಿದ ಸೇವೆಯನ್ನೂ ನನ್ನ ವಶದಲ್ಲಿದ್ದ ನಿನ್ನ ಪಶುಗಳು ಅಭಿವೃದ್ಧಿಯಾದದ್ದನ್ನೂ ನೀನು ಬಲ್ಲೆ.",
			"30": "<sup>30</sup> ನಾನು ಬರುವದಕ್ಕೆ ಮೊದಲು ನಿನಗಿದ್ದದ್ದು ಸ್ವಲ್ಪವೇ; ಈಗ ಬಹಳವಾಗಿ ಹೆಚ್ಚಾಯಿತು. ನಾನು ಕೈಹಾಕಿದ ಎಲ್ಲಾ ಕೆಲಸದಲ್ಲಿಯೂ ಯೆಹೋವನು ನಿನ್ನನ್ನು ಅಭಿವೃದ್ಧಿಪಡಿಸಿದ್ದಾನೆ.",
			"31": "<sup>31</sup> ನಾನು ಸ್ವಂತ ಮನೆಗೋಸ್ಕರ ಯಾವಾಗ ಸಂಪಾದನೆ ಮಾಡಿಕೊಳ್ಳಲಿ ಎಂದು ಹೇಳಿದನು. ಲಾಬಾನನು - ನಿನಗೆ ನಾನೇನು ಕೊಡಬೇಕು ಹೇಳು ಅನ್ನಲು ಯಾಕೋಬನು - ನನಗೆ ಏನೂ ಕೊಡಬೇಡ; ಒಂದು ಕೆಲಸ ಮಾತ್ರ ಮಾಡಿದರೆ ನಾನು ತಿರಿಗಿ ನಿನ್ನ ಹಿಂಡನ್ನು ಮೇಯಿಸಿ ಕಾಯುವೆನು.",
			"32": "<sup>32</sup> ಅದೇನಂದರೆ, ಈ ಹೊತ್ತು ನಾನು ನಿನ್ನ ಹಿಂಡಿನೊಳಗೆ ಹೋಗಿ ವಿಕಾರವರ್ಣವುಳ್ಳವುಗಳನ್ನೆಲ್ಲಾ ಅಂದರೆ ಕುರಿಗಳಲ್ಲಿ ಕಪ್ಪಾಗಿರುವವುಗಳನ್ನೂ ಆಡುಗಳಲ್ಲಿ ಚುಕ್ಕೆ ಅಥವಾ ಮಚ್ಚೆ ಉಳ್ಳವುಗಳನ್ನೂ ವಿಂಗಡಿಸುವೆನು; [ಮುಂದೆ ಅಂಥಾದ್ದು ಹುಟ್ಟಿದರೆ] ಅದೇ ನನ್ನ ಸಂಬಳವೆಂದು ನೀನು ಭಾವಿಸಬೇಕು.",
			"33": "<sup>33</sup> ನೀನು ಇನ್ನು ಮೇಲೆ ಬಂದು ನಾನು ಪಡೆದದ್ದನ್ನು ಪರೀಕ್ಷಿಸುವಾಗ ನಾನು ಪ್ರಾಮಾಣಿಕನೋ ಅಪ್ರಾಮಾಣಿಕನೋ ಎಂಬದು ಪ್ರತ್ಯಕ್ಷವಾಗುವದು. ಆಡುಗಳಲ್ಲಿ ಚುಕ್ಕೆ ಮಚ್ಚೆಯಿಲ್ಲದ್ದೂ ಕುರಿಗಳಲ್ಲಿ ಕಪ್ಪಲ್ಲದ್ದೂ ನನ್ನ ಬಳಿಯಲ್ಲಿ ಸಿಕ್ಕಿದರೆ ಅದನ್ನು ಕದ್ದದ್ದೆಂದು ಎಣಿಸಬಹುದು ಅಂದನು.",
			"34": "<sup>34</sup> ಅದಕ್ಕೆ ಲಾಬಾನನು - ಒಳ್ಳೇದು, ನೀನು ಹೇಳಿದಂತೆಯೇ ಆಗಲಿ ಅಂದನು.",
			"35": "<sup>35</sup> ಅದೇ ದಿನ ಲಾಬಾನನು ಹೋತಗಳಲ್ಲಿ ರೇಖೆ ಮಚ್ಚೆ ಇದ್ದವುಗಳನ್ನೂ ಮೇಕೆಗಳಲ್ಲಿ ಚುಕ್ಕೆ ಮಚ್ಚೆ ಇದ್ದವುಗಳನ್ನೂ ಅಂದರೆ ಸ್ವಲ್ಪ ಬಿಳುಪಾದ ಬಣ್ಣವು ತೋರಿದ ಎಲ್ಲವುಗಳನ್ನೂ ಕುರಿಗಳಲ್ಲಿ ಕಪ್ಪಾಗಿದ್ದವುಗಳನ್ನೂ ವಿಂಗಡಿಸಿ ತನ್ನ ಮಕ್ಕಳ ವಶಕ್ಕೆ ಒಪ್ಪಿಸಿ",
			"36": "<sup>36</sup> ತನಗೂ ಯಾಕೋಬನಿಗೂ ಮೂರು ದಿನದ ಪ್ರಯಾಣದಷ್ಟು ದೂರ ಅಂತರವನ್ನು ಬಿಟ್ಟನು. ಯಾಕೋಬನು ಲಾಬಾನನ ಹಿಂಡಿನಲ್ಲಿ ಮಿಕ್ಕಾದವುಗಳನ್ನು ಮೇಯಿಸಿದನು.",
			"37": "<sup>37</sup> ಹೀಗಿರುವಲ್ಲಿ ಯಾಕೋಬನು ಲಿಬ್ನೆ, ಲೂಜು, ಅರ್ಮೋನ್ ಎಂಬೀ ಮರಗಳಲ್ಲಿ ಹಸಿಕೋಲುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಪಟ್ಟೆಪಟ್ಟೆಯಾಗಿ ತೊಗಟೆಯನ್ನು ಸುಲಿದು ಅವುಗಳಲ್ಲಿರುವ ಬಿಳೀ ಬಣ್ಣವು ಕಾಣಿಸುವಂತೆ ಮಾಡಿ",
			"38": "<sup>38</sup> ಅವುಗಳನ್ನು ಆಡುಕುರಿಗಳ ಹಿಂಡು ನೀರುಕುಡಿಯುವ ದೋಣಿಗಳಲ್ಲಿ ಇಟ್ಟನು. ಆಡುಕುರಿಗಳು ಕುಡಿಯುವದಕ್ಕೆ ಬಂದ ಸಮಯವೇ ಅವುಗಳಿಗೆ ಸಂಗಮ ಕಾಲವಾಗಿತ್ತು.",
			"39": "<sup>39</sup> ಅವು ಕೋಲುಗಳನ್ನು ನೋಡುತ್ತಾ ಸಂಗಮಮಾಡಿದ್ದರಿಂದ ರೇಖೆ ಚುಕ್ಕೆ ಮಚ್ಚೆಗಳುಳ್ಳ ಮರಿಗಳನ್ನು ಈದವು.",
			"40": "<sup>40</sup> ಯಾಕೋಬನು ಆ ಮರಿಗಳನ್ನು ಲಾಬಾನನ ಹಿಂಡಿಗೆ ಸೇರಿಸದೆ ತನ್ನವೆಂದು ಪ್ರತ್ಯೇಕಿಸಿದನು; ಮತ್ತು ಲಾಬಾನನ ಆಡುಕುರಿಗಳ ಮುಖಗಳನ್ನು ರೇಖೆಯುಳ್ಳ ಆ ಆಡುಗಳ ಮತ್ತು ಕಪ್ಪಾದ ಕುರಿಗಳ ಕಡೆಗೆ ತಿರುಗಿಸಿದನು.",
			"41": "<sup>41</sup> ಇದಲ್ಲದೆ ಬಲಿಷ್ಠವಾದ ಆಡುಕುರಿಗಳು ಸಂಗಮಮಾಡುವಾಗ ಆ ಕೋಲುಗಳನ್ನು ನೋಡುತ್ತಾ ಸಂಗಮಮಾಡಲಿ ಎಂದು ಯಾಕೋಬನು ದೋಣಿಗಳಲ್ಲಿ ಕೋಲುಗಳನ್ನಿಟ್ಟನು; ಬಲಹೀನ ಆಡುಕುರಿಗಳ ಎದುರಾಗಿ ಇಡಲಿಲ್ಲ.",
			"42": "<sup>42</sup> ಹೀಗೆ ಬಲವಿಲ್ಲದವುಗಳು ಲಾಬಾನನ ಪಾಲಿಗೆ ಬಿದ್ದವು, ಬಲಿಷ್ಠವಾದವುಗಳು ಯಾಕೋಬನ ಪಾಲಿಗೆ ಬಿದ್ದವು.",
			"43": "<sup>43</sup> ಯಾಕೋಬನು ಬಹಳ ಸಂಪತ್ತುಳ್ಳವನಾದನು; ಅವನಿಗೆ ದೊಡ್ಡ ದೊಡ್ಡ ಹಿಂಡುಗಳೂ ದಾಸದಾಸಿಯರೂ ಒಂಟೆಕತ್ತೆಗಳೂ ಹೇರಳವಾಗಿದ್ದವು."
		},
		"31": {
			"1": "<sup>1</sup> ನಮ್ಮ ತಂದೆಯ ಆಸ್ತಿಯೆಲ್ಲಾ ಯಾಕೋಬನ ಪಾಲಾಯಿತು; ನಮ್ಮ ತಂದೆಯ ಆಸ್ತಿಯಿಂದಲೇ ಅವನಿಗೆ ಇಷ್ಟು ಐಶ್ವರ್ಯವುಂಟಾಯಿತು ಎಂಬದಾಗಿ ಲಾಬಾನನ ಮಕ್ಕಳು ಹೇಳಿಕೊಳ್ಳುವ ಮಾತುಗಳು ಯಾಕೋಬನ ಕಿವಿಗೆ ಬಿದ್ದವು.",
			"2": "<sup>2</sup> ಇದಲ್ಲದೆ ಅವನು ಲಾಬಾನನ ಮುಖಭಾವವನ್ನು ನೋಡಿದಾಗ ಅದು ಮೊದಲಿದ್ದಂತೆ ತೋರಲಿಲ್ಲ.",
			"3": "<sup>3</sup> ಮತ್ತು ಯೆಹೋವನು ಅವನಿಗೆ - ನಿನ್ನ ತಂದೆತಾತಂದಿರ ದೇಶಕ್ಕೂ ನಿನ್ನ ಬಂಧುಗಳ ಬಳಿಗೂ ತಿರಿಗಿ ಹೋಗು; ನಾನು ನಿನ್ನೊಂದಿಗೆ ಇರುವೆನು ಎಂದು ಹೇಳಿದನು.",
			"4": "<sup>4</sup> ಹೀಗಿರುವದರಿಂದ ಯಾಕೋಬನು ರಾಹೇಲಳನ್ನೂ ಲೇಯಳನ್ನೂ ತಾನು ಆಡುಕುರಿಗಳನ್ನು ಮೇಯಿಸುತ್ತಿದ್ದ ಅಡವಿಗೆ ಕರಸಿಕೊಂಡು",
			"5": "<sup>5</sup> ಅವರಿಗೆ ಹೇಳಿದ್ದೇನಂದರೆ - ನಿಮ್ಮ ತಂದೆಯ ಮುಖಭಾವವು ನನ್ನ ವಿಷಯದಲ್ಲಿ ಮೊದಲಿದ್ದಂತೆ ಇಲ್ಲವೆಂದು ತೋರಬಂತು. ಆದರೂ ನನ್ನ ತಂದೆಯ ದೇವರು ನನ್ನೊಂದಿಗೆ ಇದ್ದಾನೆ.",
			"6": "<sup>6</sup> ನಾನು ನಿಮ್ಮ ತಂದೆಯ ಸೇವೆಯನ್ನು ಸಾಹಸದಿಂದ ಮಾಡಿದ್ದೇನೆ; ನೀವೂ ಅದನ್ನು ಬಲ್ಲಿರಿ.",
			"7": "<sup>7</sup> ಅವನು ಚಪಲಚಿತ್ತನಾಗಿ ಹತ್ತುಸಾರಿ ನನ್ನ ಸಂಬಳವನ್ನು ಬದಲಾಯಿಸಿ ವಂಚಿಸಿದರೂ ನನಗೆ ಕೇಡುಮಾಡುವದಕ್ಕೆ ದೇವರು ಅವನಿಗೆ ಅವಕಾಶಕೊಡಲಿಲ್ಲ.",
			"8": "<sup>8</sup> ಚುಕ್ಕೆಯುಳ್ಳವುಗಳನ್ನು ನಿನ್ನ ಸಂಬಳಕ್ಕೆ ತಕ್ಕೋ ಎಂದು ಅವನು ಹೇಳಿದಾಗ ಹಿಂಡಿನ ಆಡುಕುರಿಗಳೆಲ್ಲವೂ ಚುಕ್ಕೆಯುಳ್ಳ ಮರಿಗಳನ್ನೇ ಈದವು. ರೇಖೆಗಳಿರುವವುಗಳನ್ನು ಸಂಬಳಕ್ಕೆ ತಕ್ಕೋ ಎಂದು ಹೇಳಿದಾಗ ಆಡುಕುರಿಗಳೆಲ್ಲವೂ ರೇಖೆಯುಳ್ಳ ಮರಿಗಳನ್ನು ಈದವು.",
			"9": "<sup>9</sup> ಹೀಗೆ ದೇವರು ನಿಮ್ಮ ತಂದೆಯ ಆಡುಕುರಿಗಳನ್ನು ಅವನಿಂದ ತೆಗೆದು ನನಗೆ ಕೊಟ್ಟನು.",
			"10": "<sup>10</sup> ಕುರಿಗಳು ಸಂಗಮಮಾಡುವ ಕಾಲದಲ್ಲಿ ನಾನು ಕನಸಿನಲ್ಲಿ ಕಣ್ಣೆತ್ತಿ ನೋಡಿದಾಗ ಕುರಿಗಳ ಮೇಲೆ ಹಾರಿದ ಟಗರುಗಳೆಲ್ಲವೂ ರೇಖೆ ಚುಕ್ಕೆ ಮಚ್ಚೆಗಳುಳ್ಳವುಗಳಾಗಿ ಕಾಣಿಸಿದವು.",
			"11": "<sup>11</sup> ಆ ಕನಸಿನಲ್ಲಿ ದೇವದೂತನು - ಯಾಕೋಬನೇ ಎಂದು ಕರೆಯಲು ನಾನು - ಇದ್ದೇನೆ ಎಂದು ಹೇಳಿದಾಗ",
			"12": "<sup>12</sup> ಆತನು ನನಗೆ - ಲಾಬಾನನು ನಿನ್ನ ವಿಷಯದಲ್ಲಿ ನಡಕೊಂಡ ರೀತಿಯನ್ನು ನಾನು ನೋಡಿದ್ದೇನೆ; ಆದದರಿಂದ ನೀನು ಕಣ್ಣೆತ್ತಿ ನೋಡು; ಕುರಿಗಳ ಮೇಲೆ ಹಾರುವ ಟಗರುಗಳೆಲ್ಲವೂ ರೇಖೆ ಚುಕ್ಕೆ ಮಚ್ಚೆಗಳುಳ್ಳವುಗಳಾಗಿವೆ.",
			"13": "<sup>13</sup> ಬೇತೇಲಿನಲ್ಲಿ ನಿನಗೆ ಕಾಣಿಸಿದ ದೇವರು ನಾನೇ; ಅಲ್ಲಿ ಕಂಬದ ಮೇಲೆ ಎಣ್ಣೆಹೊಯಿದು ನನಗೆ ಹರಕೆ ಮಾಡಿಕೊಂಡಿಯಷ್ಟೆ. ಈಗ ನೀನೆದ್ದು ಈ ದೇಶವನ್ನು ಬಿಟ್ಟು ನೀನು ಹುಟ್ಟಿದ ದೇಶಕ್ಕೆ ತಿರಿಗಿಹೋಗು ಎಂದು ಹೇಳಿದನು.",
			"14": "<sup>14</sup> ಯಾಕೋಬನು ಈ ಮಾತುಗಳನ್ನಾಡಿದಾಗ ರಾಹೇಲಳು ಮತ್ತು ಲೇಯಳು - ನಮ್ಮ ತಂದೆಯ ಮನೆಯಲ್ಲಿ ನಮಗೆ ಪಾಲೂ ಬಾಧ್ಯತೆಯೂ ಇನ್ನೇನದೆ?",
			"15": "<sup>15</sup> ಅವನು ನಮ್ಮನ್ನು ಅನ್ಯರೆಂದು ಎಣಿಸುತ್ತಾನಲ್ಲಾ; ಅವನು ನಮ್ಮನ್ನು ಮಾರಿ ನಮ್ಮ ಮೂಲಕ ಸಿಕ್ಕಿದ ದ್ರವ್ಯವನ್ನು ತಾನೇ ನುಂಗಿಬಿಟ್ಟನು.",
			"16": "<sup>16</sup> ಆಗಲಿ; ದೇವರು ನಮ್ಮ ತಂದೆಯಿಂದ ತೆಗೆದ ಆಸ್ತಿಯೆಲ್ಲಾ ನಮಗೂ ನಮ್ಮ ಮಕ್ಕಳಿಗೂ ಬಂತಲ್ಲಾ. ಆದದರಿಂದ ದೇವರು ನಿನಗೆ ಹೇಳಿದಂತೆಯೇ ಮಾಡು ಎಂದು ಉತ್ತರ ಕೊಟ್ಟರು.",
			"17": "<sup>17</sup> ಆಗ ಯಾಕೋಬನು ತನ್ನ ಮಕ್ಕಳನ್ನೂ ಹೆಂಡತಿಯರನ್ನೂ ಒಂಟೆಗಳ ಮೇಲೆ ಹತ್ತಿಸಿ",
			"18": "<sup>18</sup> ತಾನು ಗಳಿಸಿಕೊಂಡಿದ್ದ ಎಲ್ಲಾ ಆಸ್ತಿಯನ್ನೂ ಪದ್ದನ್ಅರಾಮ್ ದೇಶದಲ್ಲಿ ಸಂಪಾದಿಸಿಕೊಂಡಿದ್ದ ಎಲ್ಲಾ ಪಶುಗಳನ್ನೂ ತೆಗೆದುಕೊಂಡು ಕಾನಾನ್ ದೇಶಕ್ಕೆ ತನ್ನ ತಂದೆಯಾದ ಇಸಾಕನ ಬಳಿಗೆ ಹೋಗುವದಕ್ಕಾಗಿ ಹೊರಟನು.",
			"19": "<sup>19</sup> ಲಾಬಾನನು ತನ್ನ ಕುರಿಗಳ ಉಣ್ಣೆಯನ್ನು ಕತ್ತರಿಸುವದಕ್ಕೋಸ್ಕರ ಹೋಗಿದ್ದನು. ಹೀಗಿರುವಲ್ಲಿ ರಾಹೇಲಳು ತನ್ನ ತಂದೆಯ ಮನೆಯಲ್ಲಿದ್ದ ವಿಗ್ರಹಗಳನ್ನು ಕದ್ದುಕೊಂಡಳು.",
			"20": "<sup>20</sup> ಅದಲ್ಲದೆ ಯಾಕೋಬನು ತಾನು ಹೋಗುತ್ತೇನೆಂದು ಅರಾಮ್ಯನಾದ ಲಾಬಾನನಿಗೆ ತಿಳಿಸದೆ ಅವನನ್ನು ಮೋಸಗೊಳಿಸಿ ಓಡಿಹೋದನು.",
			"21": "<sup>21</sup> ಹೀಗೆ ಅವನು ತನ್ನ ಆಸ್ತಿಯನ್ನೆಲ್ಲಾ ತೆಗೆದುಕೊಂಡು ಓಡಿಹೋಗಿ ಯೂಫ್ರೇಟೀಸ್ ಮಹಾನದಿಯನ್ನು ದಾಟಿ ಗಿಲ್ಯಾದೆಂಬ ಬೆಟ್ಟದ ಸೀಮೆಯ ದಾರಿಯನ್ನು ಹಿಡಿದನು.",
			"22": "<sup>22</sup> ಯಾಕೋಬನು ಓಡಿಹೋದ ವರ್ತಮಾನವು ಮೂರನೆಯ ದಿನದಲ್ಲಿ ಲಾಬನನಿಗೆ ತಿಳಿದುಬರಲು",
			"23": "<sup>23</sup> ಅವನು ತನ್ನ ಬಂಧು ಜನರನ್ನು ಕೂಡಿಕೊಂಡು, ಏಳುದಿನ ಪ್ರಯಾಣಮಾಡಿ ಯಾಕೋಬನನ್ನು ಹಿಂದಟ್ಟಿ, ಗಿಲ್ಯಾದ್ ಬೆಟ್ಟದ ಸೀಮೆಯಲ್ಲಿ ಅವನನ್ನು ಸಂಧಿಸಿದನು.",
			"24": "<sup>24</sup> ಆದರೆ ರಾತ್ರಿಯಲ್ಲಿ ದೇವರು ಅರಾಮ್ಯನಾದ ಲಾಬಾನನ ಕನಸಿನಲ್ಲಿ ಬಂದು - ನೋಡಿಕೋ, ನೀನು ಯಾಕೋಬನಿಗೆ ಏನೂ ಅನ್ನಬೇಡ ಎಂದು ಹೇಳಿದನು.",
			"25": "<sup>25</sup> ಲಾಬಾನನು ಯಾಕೋಬನನ್ನು ಸಂಧಿಸಿದಾಗ ಯಾಕೋಬನು ಬೆಟ್ಟದಲ್ಲಿ ತನ್ನ ಗುಡಾರವನ್ನು ಹಾಕಿಸಿದನು. ಲಾಬಾನನೂ ತನ್ನವರೊಡನೆ ಅದೇ ಗಿಲ್ಯಾದ್ ಬೆಟ್ಟದಲ್ಲಿ ಗುಡಾರವನ್ನು ಹಾಕಿಸಿದ್ದನು.",
			"26": "<sup>26</sup> ಲಾಬಾನನು ಯಾಕೋಬನಿಗೆ - ಇದೇನು ನೀನು ಮಾಡಿದ್ದು? ನೀನು ನನ್ನ ಹೆಣ್ಣುಮಕ್ಕಳನ್ನು ಯುದ್ಧದಲ್ಲಿ ಸೆರೆಹಿಡಿದವರಂತೆ ತೆಗೆದುಕೊಂಡು ನನಗೆ ಏನೂ ಹೇಳದೆ ಓಡಿ ಬಂದಿಯಲ್ಲಾ.",
			"27": "<sup>27</sup> ಯಾಕೆ ನನ್ನನ್ನು ಮೋಸಗೊಳಿಸಿ ಕಳ್ಳತನದಿಂದ ಬಂದಿ? ನನಗೆ ತಿಳಿಸಿದ್ದರೆ ನಾನು ಸಂಗೀತಮಾಡಿಸಿ ಮದ್ದಳೆ, ವೀಣೆ ಮುಂತಾದ ವಾದ್ಯಗಳೊಡನೆ ಸಂತೋಷದಿಂದ ನಿನ್ನನ್ನು ಸಾಗಕಳುಹಿಸುತ್ತಿದ್ದೆನು.",
			"28": "<sup>28</sup> ನನ್ನ ಹೆಣ್ಣುಮಕ್ಕಳಿಗೂ ಮೊಮ್ಮಕ್ಕಳಿಗೂ ಮುದ್ದಿಡುವದಕ್ಕಾದರೂ ಆಗದಂತೆ ಮಾಡಿದಿ. ನೀನು ಮಾಡಿದ್ದು ಹುಚ್ಚು ಕೆಲಸವೇ ಸರಿ.",
			"29": "<sup>29</sup> ನಿಮಗೆ ಕೇಡುಮಾಡುವದಕ್ಕೆ ನನ್ನಲ್ಲಿ ಸಾಮರ್ಥ್ಯವುಂಟು; ಆದರೆ ಹೋದ ರಾತ್ರಿಯಲ್ಲಿ ನಿಮ್ಮ ತಂದೆಯ ದೇವರು - ಯಾಕೋಬನಿಗೆ ಏನೂ ಅನ್ನಬೇಡ ನೋಡಿಕೋ ಎಂದು ನನಗೆ ಎಚ್ಚರಿಸಿದನು.",
			"30": "<sup>30</sup> ಆದರೆ ತಂದೆಯ ಮನೆಗೆ ಹೋಗುವದಕ್ಕೆ ನಿನಗೆ ಬಹಳ ಆಶೆಯಿರುವದರಿಂದ ಹೋಗಬೇಕಾಯಿತು. ಅದಿರಲಿ; ನನ್ನ ದೇವರುಗಳನ್ನು ಕದ್ದದ್ದು ಯಾಕೆ ಎಂದು ಹೇಳಿದನು.",
			"31": "<sup>31</sup> ಅದಕ್ಕೆ ಯಾಕೋಬನು - ನೀನು ನಿನ್ನ ಹೆಣ್ಣುಮಕ್ಕಳನ್ನು ಬಲಾತ್ಕಾರದಿಂದ ತೆಗೆದುಕೊಳ್ಳುವಿ ಎಂದು ಭಯಪಟ್ಟು [ಹೀಗೆ ಹೊರಟು ಬಂದೆನು].",
			"32": "<sup>32</sup> ನಿನ್ನ ದೇವರುಗಳು ಯಾರ ಬಳಿಯಲ್ಲಿ ಸಿಕ್ಕುತ್ತವೆಯೋ ಅವನು ಸಾಯಲಿ. ನಮ್ಮ ಬಂಧುಗಳ ಎದುರಾಗಿ ನನ್ನ ಸೊತ್ತನ್ನು ಪರೀಕ್ಷಿಸಬಹುದು; ಅದರಲ್ಲಿ ನಿನ್ನದೇನಾದರೂ ಸಿಕ್ಕಿದರೆ ಅದನ್ನು ತೆಗೆದುಕೋ ಎಂದು ಹೇಳಿದನು. ರಾಹೇಲಳು ಆ ದೇವರುಗಳನ್ನು ಕದ್ದದ್ದು ಯಾಕೋಬನಿಗೆ ತಿಳಿದಿರಲಿಲ್ಲ.",
			"33": "<sup>33</sup> ಲಾಬಾನನು ಯಾಕೋಬನ ಗುಡಾರದಲ್ಲಿಯೂ ಲೇಯಳ ಗುಡಾರದಲ್ಲಿಯೂ ಆ ಇಬ್ಬರು ದಾಸಿಯರ ಗುಡಾರದಲ್ಲಿಯೂ ಹುಡುಕಿದನು. ಆದರೂ ಅವನಿಗೇನೂ ಸಿಕ್ಕಲಿಲ್ಲ. ಲೇಯಳ ಗುಡಾರವನ್ನು ಬಿಟ್ಟು ರಾಹೇಲಳ ಗುಡಾರಕ್ಕೆ ಬಂದನು.",
			"34": "<sup>34</sup> ರಾಹೇಲಳು ಆ ವಿಗ್ರಹಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಒಂಟೆಯ ಸಬರದೊಳಗಿಟ್ಟು ಅವುಗಳ ಮೇಲೆ ಕೂತಿದ್ದಳು. ಲಾಬಾನನು ಗುಡಾರದಲ್ಲಿದ್ದ ಸಾಮಾನನ್ನೆಲ್ಲಾ ಮುಟ್ಟಿಮುಟ್ಟಿ ನೋಡಿದರೂ ಅವುಗಳನ್ನು ಕಾಣಲಿಲ್ಲ.",
			"35": "<sup>35</sup> ರಾಹೇಲಳು ತನ್ನ ತಂದೆಗೆ - ಅಪ್ಪಾ, ನಾನು ನಿನ್ನ ಮುಂದೆ ಎದ್ದು ನಿಂತುಕೊಳ್ಳದೆ ಹೋದರೂ ಕೋಪಿಸಿಕೊಳ್ಳಬೇಡ; ನಾನು ಮುಟ್ಟಾಗಿದ್ದೇನೆ ಎಂದು ಹೇಳಿದ್ದರಿಂದ ಅವನು ಚೆನ್ನಾಗಿ ಹುಡುಕಿದರೂ ಆ ವಿಗ್ರಹಗಳನ್ನು ಕಂಡುಕೊಳ್ಳಲಿಲ್ಲ.",
			"36": "<sup>36</sup> ಆಗ ಯಾಕೋಬನು ಕೋಪಿಸಿಕೊಂಡು ಲಾಬಾನನನ್ನು ಗದರಿಸಿ - ನೀನು ಇಷ್ಟು ಆತುರಪಟ್ಟು ನನ್ನನ್ನು ಹಿಂದಟ್ಟಿ ಬರುವಂತೆ ನಾನೇನು ದ್ರೋಹಮಾಡಿದೆನು?",
			"37": "<sup>37</sup> ನನ್ನ ಸಾಮಾನನ್ನೆಲ್ಲಾ ಪರೀಕ್ಷಿಸಿ ನೋಡುವಂತೆ ನಾನೇನು ತಪ್ಪುಮಾಡಿದೆನು? ನಿನ್ನ ಸೊತ್ತು ಏನಾದರೂ ನನ್ನಲ್ಲಿ ಸಿಕ್ಕಿದೆಯೋ? ನನ್ನವರ ಮುಂದೆಯೂ ನಿನ್ನವರ ಮುಂದೆಯೂ ಅದನ್ನು ತಂದಿಡು ನೋಡೋಣ; ಇವರೇ ನಮ್ಮಿಬ್ಬರ ನ್ಯಾಯವನ್ನು ತೀರಿಸಲಿ.",
			"38": "<sup>38</sup> ನಾನು ಇಪ್ಪತ್ತು ವರುಷ ನಿನ್ನ ಬಳಿಯಲ್ಲಿದ್ದೆನಲ್ಲಾ. ನಿನ್ನ ಆಡುಕುರಿಗಳು ಕಂದು ಹಾಕಲಿಲ್ಲ; ನಿನ್ನ ಹಿಂಡಿನ ಟಗರುಗಳನ್ನು ನಾನೇನೂ ತಿಂದುಬಿಡಲಿಲ್ಲ;",
			"39": "<sup>39</sup> ಕಾಡುಮೃಗಗಳು ಕೊಂದ ಪಶುಗಳನ್ನು ನಿನ್ನ ಭಾಗಕ್ಕೆ ಹಾಕದೆ ನಾನೇ ಬದಲುಕೊಟ್ಟೆನು; ಹಗಲಾಗಲಿ ಇರುಳಾಗಲಿ ಕದ್ದುಹೋದದ್ದರ ಲೆಕ್ಕವನ್ನು ನನ್ನಿಂದಲೇ ತೆಗೆದುಕೊಂಡಿ;",
			"40": "<sup>40</sup> ಹಗಲಲ್ಲಿ ಬಿಸಿಲಿನಿಂದಲೂ ಇರುಳಲ್ಲಿ ಚಳಿಯಿಂದಲೂ ಬಾಧೆಪಟ್ಟೆನು; ನಿದ್ದೆ ಮಾಡುವದಕ್ಕಾದರೂ ಅವಕಾಶವಾಗಲಿಲ್ಲ; ನನ್ನ ಸ್ಥಿತಿ ಹೀಗಿತ್ತು.",
			"41": "<sup>41</sup> ಇಪ್ಪತ್ತು ವರುಷ ನಿನ್ನ ಮನೆಯಲ್ಲಿದ್ದೆನು; ನಿನ್ನಿಬ್ಬರ ಹೆಣ್ಣುಮಕ್ಕಳಿಗಾಗಿ ಹದಿನಾಲ್ಕು ವರುಷವೂ ನಿನ್ನ ಆಡುಕುರಿಗಳಿಗಾಗಿ ಆರು ವರುಷವೂ ಸೇವೆ ಮಾಡಿದೆನು. ನೀನು ಹತ್ತು ಸಾರಿ ನನ್ನ ಸಂಬಳವನ್ನು ಬದಲಾಯಿಸಿದಿ.",
			"42": "<sup>42</sup> ನನ್ನ ಹಿರಿಯರ ದೇವರು, ಅಂದರೆ ಅಬ್ರಹಾಮನ ದೇವರೂ ಇಸಾಕನು ಭಯಭಕ್ತಿಯಿಂದ ಸೇವಿಸುವ ದೇವರೂ ಆಗಿರುವಾತನು, ನನ್ನ ಪಕ್ಷದಲ್ಲಿ ಇರದಿದ್ದರೆ ನಿಶ್ಚಯವಾಗಿ ನೀನು ನನ್ನನ್ನು ಬರಿಗೈಯಾಗಿ ಕಳುಹಿಸುತ್ತಿದ್ದಿ. ದೇವರು ನನ್ನ ಕಷ್ಟವನ್ನೂ ನಾನು ಪಟ್ಟ ಪ್ರಯಾಸವನ್ನೂ ನೋಡಿದ್ದರಿಂದಲೇ ನಿನ್ನೆಯ ರಾತ್ರಿ ನಿನ್ನನ್ನು ಗದರಿಸಿದನು ಅಂದನು.",
			"43": "<sup>43</sup> ಅದಕ್ಕೆ ಲಾಬಾನನು ಯಾಕೋಬನಿಗೆ - ಈ ಹೆಂಗಸರು ನನ್ನ ಕುಮಾರ್ತೆಗಳಲ್ಲವೇ, ಈ ಮಕ್ಕಳು ನನ್ನ ಮೊಮ್ಮಕ್ಕಳಲ್ಲವೇ, ಈ ಹಿಂಡುಗಳೂ ನನ್ನವೇ, ನಿನ್ನ ಕಣ್ಣಮುಂದೆ ಇರುವದೆಲ್ಲವೂ ನನ್ನದಲ್ಲವೇ. ಆದರೆ ಈ ನನ್ನ ಕುಮಾರ್ತೆಗಳಿಗೋಸ್ಕರವೂ ಇವರು ಹೆತ್ತ ಮಕ್ಕಳಿಗೋಸ್ಕರವೂ ಈಗ ನಾನೇನು ಮಾಡುವೆನು?",
			"44": "<sup>44</sup> ನಾವಿಬ್ಬರು ಸೇರಿ ಒಂದು ಒಡಂಬಡಿಕೆಯನ್ನು ಮಾಡಿಕೊಳ್ಳೋಣ ಬಾ; ಅದಕ್ಕೆ ಒಂದು ಸಾಕ್ಷಿಯಿರಲಿ ಎಂದು ಹೇಳಿದನು.",
			"45": "<sup>45</sup> ಆಗ ಯಾಕೋಬನು ಕಲ್ಲನ್ನು ತೆಗೆದುಕೊಂಡು ಕಂಬವಾಗಿ ನಿಲ್ಲಿಸಿದನು.",
			"46": "<sup>46</sup> ಯಾಕೋಬನು ತನ್ನ ಕಡೆಯವರಿಗೆ - ಕಲ್ಲುಗಳನ್ನು ಕೂಡಿಸಿರಿ ಎಂದು ಹೇಳಲು ಅವರು ಕಲ್ಲುಗಳನ್ನು ಕೂಡಿಸಿ ಕುಪ್ಪೆಯನ್ನು ಮಾಡಿದಾಗ ಅವರೆಲ್ಲರೂ ಆ ಕುಪ್ಪೆಯ ಬಳಿಯಲ್ಲಿ ಸಹ ಭೋಜನವನ್ನು ಮಾಡಿದರು.",
			"47": "<sup>47</sup> ಆ ಕುಪ್ಪೆಗೆ ಲಾಬಾನನು ಯಗರಸಾಹದೂತ ಎಂದೂ ಯಾಕೋಬನು ಗಲೇದ್ ಎಂದೂ ಹೆಸರಿಟ್ಟರು. ಲಾಬಾನನು -",
			"48": "<sup>48</sup> ಈ ಹೊತ್ತು ನಿನಗೂ ನನಗೂ ಆದ ಒಡಂಬಡಿಕೆಗೆ ಈ ಕುಪ್ಪೆಯೇ ಸಾಕ್ಷಿ ಎಂದು ಹೇಳಿದ್ದರಿಂದ ಅದಕ್ಕೆ ಗಲೇದೆಂದು ಹೆಸರಾಯಿತು.",
			"49": "<sup>49</sup> ಅದಲ್ಲದೆ ಅವನು - ನಾವು ಒಬ್ಬರಿಗೊಬ್ಬರು ಅಗಲಿರುವಾಗ ಯೆಹೋವನೇ ನಮ್ಮುಭಯರನ್ನೂ ನೋಡಿಕೊಳ್ಳುತ್ತಾ ಇರುವನು ಎಂದು ಹೇಳಿದ್ದರಿಂದ ಅದಕ್ಕೆ ಮಿಚ್ಪಾ ಎಂದು ಹೆಸರಾಯಿತು.",
			"50": "<sup>50</sup> ಮತ್ತು ಲಾಬಾನನು - ನೀನು ನನ್ನ ಹೆಣ್ಣುಮಕ್ಕಳನ್ನು ನೋಯಿಸಿದರೆ ಅಥವಾ ಬೇರೆ ಸ್ತ್ರೀಯರನ್ನು ಮದುವೆ ಮಾಡಿಕೊಂಡರೆ ವಿಚಾರಿಸುವವರು ಮನುಷ್ಯರೊಳಗೆ ಯಾರೂ ಇಲ್ಲದಿದ್ದರೂ ದೇವರೇ ನಮ್ಮ ಒಡಂಬಡಿಕೆಗೆ ಸಾಕ್ಷಿ ಅಂದನು.",
			"51": "<sup>51</sup> ಇದಲ್ಲದೆ ಲಾಬಾನನು ಯಾಕೋಬನಿಗೆ - ನಿನಗೂ ನನಗೂ ನಡುವೆ ನಾನು ನಿಲ್ಲಿಸಿರುವ ಈ ಕಂಬವನ್ನು ನೋಡು, ಈ ಕುಪ್ಪೆಯನ್ನೂ ನೋಡು.",
			"52": "<sup>52</sup> ಕೇಡುಮಾಡುವದಕ್ಕೋಸ್ಕರ ನಾನಂತೂ ಈ ಕುಪ್ಪೆಯನ್ನು ದಾಟಿ ನಿನ್ನ ಬಳಿಗೆ ಬರಕೂಡದು; ಹಾಗೆಯೇ ನೀನು ಈ ಕುಪ್ಪೆಯನ್ನೂ ಈ ಕಂಬವನ್ನೂ ದಾಟಿ ನನ್ನ ಬಳಿಗೆ ಬರಕೂಡದು; ಇದಕ್ಕೆ ಕುಪ್ಪೆಯೂ ಕಂಬವೂ ಈ ಎರಡೇ ಸಾಕ್ಷಿ.",
			"53": "<sup>53</sup> ಅಬ್ರಹಾಮ ನಾಹೋರರ ದೇವರು ಅಂದರೆ ಅವರ ತಂದೆಯ ದೇವರು ನಿನಗೂ ನನಗೂ ನ್ಯಾಯತೀರಿಸಲಿ ಅಂದನು. ಅದೇ ಮೇರೆಗೆ ಯಾಕೋಬನು ತನ್ನ ತಂದೆಯಾದ ಇಸಾಕನು ಭಯಭಕ್ತಿಯಿಂದ ಸೇವಿಸುವ ದೇವರ ಮೇಲೆ ಆಣೆಯಿಟ್ಟು ಪ್ರಮಾಣ ಮಾಡಿದನು.",
			"54": "<sup>54</sup> ಇದಲ್ಲದೆ ಯಾಕೋಬನು ಆ ಬೆಟ್ಟದ ಮೇಲೆ ಯಜ್ಞವನ್ನು ಮಾಡಿ ತನ್ನ ಬಂಧುಗಳನ್ನು ತನ್ನೊಡನೆ ಸಹಭೋಜನಕ್ಕೆ ಕರಸಿದನು. ಅವರು ಸಹಭೋಜನ ಮಾಡಿ ಆ ರಾತ್ರಿಯೆಲ್ಲಾ ಬೆಟ್ಟದ ಮೇಲೆ ಇದ್ದರು.",
			"55": "<sup>55</sup> ಬೆಳಿಗ್ಗೆ ಲಾಬಾನನು ಎದ್ದು ತನ್ನ ಹೆಣ್ಣುಮಕ್ಕಳಿಗೂ ಮೊಮ್ಮಕ್ಕಳಿಗೂ ಮುದ್ದಿಟ್ಟು ಅವರನ್ನು ಆಶೀರ್ವದಿಸಿ ತನ್ನ ಊರಿಗೆ ಹೊರಟುಹೋದನು."
		},
		"32": {
			"1": "<sup>1</sup> ಯಾಕೋಬನು ಮುಂದೆ ಪ್ರಯಾಣಮಾಡಲು ದೇವದೂತರು ಅವನೆದುರಿಗೆ ಬಂದರು.",
			"2": "<sup>2</sup> ಅವನು ಅವರನ್ನು ನೋಡಿದಾಗ - ಅದು ದೇವರ ಪಾಳೆಯ ಎಂದು ಹೇಳಿ ಆ ಸ್ಥಳಕ್ಕೆ ಮಹನಯಿಮ್ ಎಂದು ಹೆಸರಿಟ್ಟನು.",
			"3": "<sup>3</sup> ಬಳಿಕ ಯಾಕೋಬನು ಎದೋಮ್ಯರ ದೇಶವಾಗಿರುವ ಸೇಯೀರ್‍ಸೀಮೆಗೆ ತನ್ನ ಅಣ್ಣನಾದ ಏಸಾವನ ಬಳಿಗೆ ದೂತರನ್ನು ಮುಂದೆ ಕಳುಹಿಸಿದನು.",
			"4": "<sup>4</sup> ಕಳುಹಿಸುವಾಗ ಅವರಿಗೆ - ನೀವು ನನ್ನ ಪ್ರಭುವಾದ ಏಸಾವನ ಬಳಿಗೆ ಹೋಗಿ ಅವನಿಗೆ - ನಿನ್ನ ಸೇವಕನಾದ ಯಾಕೋಬನು ಇಷ್ಟು ದಿವಸ ಲಾಬಾನನ ಬಳಿಯಲ್ಲಿ ವಾಸವಾಗಿದ್ದನು;",
			"5": "<sup>5</sup> ಅವನಿಗೆ ಎತ್ತು, ಕತ್ತೆ ಮುಂತಾದ ಪಶುಗಳ ಹಿಂಡುಗಳೂ ದಾಸದಾಸೀಜನವೂ ಉಂಟಾದವು; ತಮ್ಮ ದಯವಿರಬೇಕೆಂದು ಪ್ರಭುಗಳಿಗೆ ತಿಳಿಸುವದಕ್ಕೆ ನಮ್ಮನ್ನು ಕಳುಹಿಸಿದ್ದಾನೆಂದು ಹೇಳಿರಿ ಅಂದನು.",
			"6": "<sup>6</sup> ತರುವಾಯ ಆ ದೂತರು ಯಾಕೋಬನ ಬಳಿಗೆ ತಿರಿಗಿ ಬಂದು - ನಾವು ನಿನ್ನ ಅಣ್ಣನಾದ ಏಸಾವನ ಹತ್ತಿರಕ್ಕೆ ಹೋಗಿ ಬಂದಿದ್ದೇವೆ, ಅವನು ನಾನೂರು ಜನರ ಸಮೇತ ನಿನ್ನನ್ನು ಎದುರುಗೊಳ್ಳುವದಕ್ಕೆ ಬರುತ್ತಾನೆಂದು ಹೇಳಿದಾಗ",
			"7": "<sup>7</sup> ಯಾಕೋಬನಿಗೆ ಬಹು ಭಯವೂ ಕಳವಳವೂ ಉಂಟಾದವು. ಅವನು ತನ್ನೊಂದಿಗಿದ್ದ ಜನರನ್ನೂ ಕುರಿ ದನ ಒಂಟೆಗಳನ್ನೂ ಎರಡು ಪಾಳೆಯ ಮಾಡಿ -",
			"8": "<sup>8</sup> ಏಸಾವನು ಒಂದು ಪಾಳೆಯದ ಮೇಲೆ ಬಿದ್ದರೂ ಮತ್ತೊಂದು ಪಾಳೆಯವು ತಪ್ಪಿಸಿಕೊಂಡು ಹೋಗುವದು ಅಂದುಕೊಂಡನು.",
			"9": "<sup>9</sup> ಇದಲ್ಲದೆ ಅವನು ದೇವರನ್ನು ಪ್ರಾರ್ಥಿಸಿ - ಯೆಹೋವನೇ, ನನ್ನ ತಂದೆತಾತಂದಿರಾದ ಇಸಾಕ ಅಬ್ರಹಾಮರ ದೇವರೇ, ಸ್ವದೇಶಕ್ಕೆ ಬಂಧುಗಳ ಬಳಿಗೆ ತಿರಿಗಿ ಹೋಗಬೇಕೆಂದು ನನಗೆ ಆಜ್ಞಾಪಿಸಿ ನಿನಗೆ ಒಳ್ಳೇದನ್ನು ಮಾಡುವೆನೆಂದು ನನಗೆ ವಾಗ್ದಾನ ಮಾಡಿದವನು ನೀನೇ ಅಲ್ಲವೇ.",
			"10": "<sup>10</sup> ನೀನು ನಿನ್ನ ದಾಸನಾದ ನನ್ನ ಮೇಲೆ ಎಷ್ಟೋ ಪ್ರೀತಿಯನ್ನಿಟ್ಟು ನಿನ್ನ ವಾಗ್ದಾನವನ್ನು ನೆರವೇರಿಸಿದ್ದೀಯಲ್ಲಾ. ನಾನು ಅದಕ್ಕೆ ಕೇವಲ ಅಪಾತ್ರನಾಗಿದ್ದೇನೆ. ನಾನು ಮೊದಲು ಈ ಯೊರ್ದನ್ ನದಿಯನ್ನು ದಾಟಿದಾಗ ನನಗೆ ಕೋಲು ಮಾತ್ರವೇ ಇತ್ತು; ಈಗ ಎರಡು ಪಾಳೆಯಗಳಿಗೆ ಒಡೆಯನಾಗಿದ್ದೇನೆ.",
			"11": "<sup>11</sup> ನನ್ನ ಅಣ್ಣನಾದ ಏಸಾವನು ಬಂದು ನನ್ನನ್ನೂ ನನ್ನ ಮಕ್ಕಳನ್ನೂ ಅವರ ತಾಯಿಯನ್ನೂ ಕೊಲ್ಲುವನೋ ಏನೋ ಎಂದು ನನಗೆ ಭಯವದೆ. ಅವನ ಕೈಗೆ ಸಿಕ್ಕದಂತೆ ನಮ್ಮನ್ನು ಕಾಪಾಡಬೇಕೆಂದು ನಿನ್ನನ್ನು ಬೇಡಿಕೊಳ್ಳುತ್ತೇನೆ.",
			"12": "<sup>12</sup> ನಿನಗೆ ಒಳ್ಳೇದನ್ನು ಮಾಡಿ ನಿನ್ನ ಸಂತತಿಯನ್ನು ಸಮುದ್ರದ ಉಸುಬಿನಂತೆ ಅಸಂಖ್ಯವಾಗಿ ಅಭಿವೃದ್ಧಿ ಮಾಡುವೆನೆಂದು ನೀನು ನನಗೆ ಹೇಳಿದ್ದೀಯಲ್ಲವೇ ಅಂದನು.",
			"13": "<sup>13</sup> ಅವನು ಆ ರಾತ್ರಿ ಅಲ್ಲೇ ಇಳುಕೊಂಡು ಇನ್ನೂರು ಆಡು, ಇಪ್ಪತ್ತು ಹೋತ, ಇನ್ನೂರು ಕುರಿ,",
			"15": "<sup>15</sup> ಇಪ್ಪತ್ತು ಟಗರು, ಮರಿಯೊಡನೆ ಹಾಲು ಕರೆಯುವ ಮೂವತ್ತು ಒಂಟೆ, ನಾಲ್ವತ್ತು ಆಕಳು, ಹತ್ತು ಹೋರಿ, ಇಪ್ಪತ್ತು ಹೆಣ್ಣುಕತ್ತೆ, ಹತ್ತು ಕತ್ತೇಮರಿ ಇವುಗಳನ್ನು ತನ್ನ ಅಣ್ಣನಾದ ಏಸಾವನಿಗೆ ಕಾಣಿಕೆಗಾಗಿ ತೆಗೆದುಕೊಂಡನು.",
			"16": "<sup>16</sup> ಇವುಗಳ ಪ್ರತಿಯೊಂದು ಹಿಂಡನ್ನು ತನ್ನ ಸೇವಕರ ವಶಕ್ಕೆ ಒಪ್ಪಿಸಿ - ನೀವು ನನಗಿಂತ ಮುಂಚಿತವಾಗಿ ನದಿಯನ್ನು ದಾಟಿಹೋಗಿರಿ, ಮತ್ತು ಹಿಂಡು ಹಿಂಡಿಗೆ ಬಿಡುವು ಇರಲಿ ಅಂದನು.",
			"17": "<sup>17</sup> ಮೊದಲನೆಯ ಹಿಂಡಿನ ಮೇಲಿದ್ದವನಿಗೆ - ನನ್ನ ಅಣ್ಣನಾದ ಏಸಾವನು ನಿನ್ನೆದುರಿಗೆ ಬಂದು- ನೀನು ಯಾರ ಸೇವಕನು? ಎಲ್ಲಿಗೆ ಹೋಗುತ್ತಿ? ನಿನ್ನ ಮುಂದೆ ಇರುವ ಇವೆಲ್ಲಾ ಯಾರವು ಎಂದು ಕೇಳಿದರೆ",
			"18": "<sup>18</sup> ನೀನು - ಇವು ನಿಮ್ಮ ಸೇವಕನಾದ ಯಾಕೋಬನವು; ತನ್ನ ಪ್ರಭುಗಳಾದ ನಿಮಗೆ ಇವುಗಳನ್ನು ಕಾಣಿಕೆಯಾಗಿ ಕಳುಹಿಸಿಕೊಟ್ಟಿದ್ದಾನೆ; ತಾನೂ ನಮ್ಮ ಹಿಂದೆ ಬರುತ್ತಾನೆ ಎಂದು ಉತ್ತರಕೊಡಬೇಕು ಎಂಬದಾಗಿ ಅಪ್ಪಣೆಕೊಟ್ಟನು.",
			"19": "<sup>19</sup> ಹಾಗೆಯೇ ಎರಡನೆಯವನಿಗೂ ಮೂರನೆಯವನಿಗೂ ಹಿಂಡುಗಳ ಹಿಂದೆ ಹೋದವರೆಲ್ಲರಿಗೂ - ನೀವು ಏಸಾವನನ್ನು ಕಾಣುವಾಗ",
			"20": "<sup>20</sup> ಇದೇ ರೀತಿಯಾಗಿ ಮಾತಾಡಿ - ನಿಮ್ಮ ಸೇವಕನಾದ ಯಾಕೋಬನೇ ನಮ್ಮ ಹಿಂದೆ ಬರುತ್ತಾನೆ ಅನ್ನಿರಿ ಎಂದು ಅಪ್ಪಣೆಕೊಟ್ಟನು. ಯಾಕೋಬನು ತನ್ನೊಳಗೆ - ನನ್ನ ಮುಂದೆ ಹೋಗುವ ಈ ಕಾಣಿಕೆಯಿಂದ ಏಸಾವನನ್ನು ಸಮಾಧಾನಪಡಿಸಿದ ಮೇಲೆ ಅವನ ಸನ್ನಿಧಿಗೆ ಸೇರುವೆನು; ಒಂದು ವೇಳೆ ನನ್ನನ್ನು ಪ್ರೀತಿಯಿಂದ ಅಂಗೀಕರಿಸುವನು ಅಂದುಕೊಂಡನು.",
			"21": "<sup>21</sup> ಆದಕಾರಣ ಅವನು ಆ ಕಾಣಿಕೆಯನ್ನು ತನಗೆ ಮುಂಚಿತವಾಗಿ ಹೊಳೆಯಿಂದಾಚೆಗೆ ದಾಟಿಸಿ ತಾನು ಆ ರಾತ್ರಿ ತನ್ನ ಪಾಳೆಯದೊಳಗೆ ಇದ್ದನು.",
			"22": "<sup>22</sup> ಆ ರಾತ್ರಿ ಅವನು ತನ್ನ ಇಬ್ಬರು ಹೆಂಡರನ್ನೂ ಇಬ್ಬರು ದಾಸಿಯರನ್ನೂ ಹನ್ನೊಂದು ಮಂದಿ ಮಕ್ಕಳನ್ನೂ ಕರೆದುಕೊಂಡು ಯಬ್ಬೋಕ್ ಹೊಳೆಯನ್ನು ದಾಟುವ ಸ್ಥಳದಲ್ಲಿ ದಾಟಿದನು.",
			"23": "<sup>23</sup> ಅವರನ್ನೂ ತನ್ನ ಆಸ್ತಿಯೆಲ್ಲವನ್ನೂ ದಾಟಿಸಿದ ಮೇಲೆ",
			"24": "<sup>24</sup> ಯಾಕೋಬನು ಒಂಟಿಗನಾಗಿ ಹಿಂದೆ ನಿಂತಿರಲು ಯಾರೋ ಒಬ್ಬ ಪುರುಷನು ಬೆಳಗಾಗುವ ತನಕ ಅವನ ಸಂಗಡ ಹೋರಾಡಿದನು.",
			"25": "<sup>25</sup> ಆ ಪುರುಷನು ತಾನು ಗೆಲ್ಲದೆ ಇರುವದನ್ನು ಕಂಡು ಯಾಕೋಬನ ತೊಡೆಯ ಕೀಲನ್ನು ಮುಟ್ಟಿದ್ದರಿಂದ ಯಾಕೋಬನು ಅವನ ಸಂಗಡ ಹೋರಾಡುತ್ತಿರುವಾಗಲೇ ಅವನ ತೊಡೆಯ ಕೀಲು ತಪ್ಪಿತು.",
			"26": "<sup>26</sup> ಆ ಪುರುಷನು - ನನ್ನನ್ನು ಬಿಡು, ಬೆಳಗಾಗುತ್ತದೆ ಅನ್ನಲು, ಯಾಕೋಬನು - ನೀನು ನನ್ನನ್ನು ಆಶೀರ್ವದಿಸಿದ ಹೊರತು ನಿನ್ನನ್ನು ಬಿಡುವದಿಲ್ಲ ಅಂದನು.",
			"27": "<sup>27</sup> ಆ ಪುರುಷನು - ನಿನ್ನ ಹೆಸರೇನು ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ ಅವನು - ಯಾಕೋಬನು ಅಂದಾಗ,",
			"28": "<sup>28</sup> ಅವನು ಯಾಕೋಬನಿಗೆ - ಇನ್ನು ಮೇಲೆ ನೀನು ಯಾಕೋಬನೆನ್ನಿಸಿಕೊಳ್ಳುವದಿಲ್ಲ; ದೇವರ ಸಂಗಡಲೂ ಮನುಷ್ಯರ ಸಂಗಡಲೂ ಹೋರಾಡಿ ಗೆದ್ದವನಾದ್ದರಿಂದ ನಿನಗೆ ಇಸ್ರಾಯೇಲೆಂದು ಹೆಸರುಂಟಾಗುವದು ಎಂದು ಹೇಳಿದನು.",
			"29": "<sup>29</sup> ಯಾಕೋಬನು - ನಿನ್ನ ಹೆಸರನ್ನು ನನಗೆ ತಿಳಿಸಬೇಕು ಸ್ವಾಮೀ ಅಂದಾಗ ಆ ಪುರುಷನು - ನನ್ನ ಹೆಸರನ್ನು ವಿಚಾರಿಸುವದೇಕೆ ಎಂದು ಹೇಳಿ ಅಲ್ಲಿ ಅವನನ್ನು ಆಶೀರ್ವದಿಸಿದನು.",
			"30": "<sup>30</sup> ಯಾಕೋಬನು - ನಾನು ದೇವರನ್ನೇ ಪ್ರತ್ಯಕ್ಷವಾಗಿ ನೋಡಿದ್ದೇನಲ್ಲಾ; ಆದರೂ ನನ್ನ ಪ್ರಾಣ ಉಳಿದದೆ ಅಂದುಕೊಂಡು ಆ ಸ್ಥಳಕ್ಕೆ ಪೆನೀಯೇಲ್ ಎಂದು ಹೆಸರಿಟ್ಟನು.",
			"31": "<sup>31</sup> ಅವನು ಪೆನೂವೇಲನ್ನು ದಾಟುತ್ತಿರುವಾಗ ಸೂರ್ಯೋದಯವಾಯಿತು. ಅವನು ತೊಡೆಯ ನಿಮಿತ್ತ ಕುಂಟಿಕೊಂಡು ನಡೆದನು.",
			"32": "<sup>32</sup> ಆ ಪುರುಷನು ಯಾಕೋಬನ ತೊಡೆಯ ಕೀಲನ್ನು ಮುಟ್ಟಿದ್ದರಿಂದ ಇಸ್ರಾಯೇಲ್ಯರು ಇಂದಿನವರೆಗೂ ತೊಡೆಯ ಕೀಲಿನ ಮೇಲಿರುವ ಸೊಂಟದ ಮಾಂಸವನ್ನು ತಿನ್ನುವದಿಲ್ಲ."
		},
		"33": {
			"1": "<sup>1</sup> ಯಾಕೋಬನು ಕಣ್ಣೆತ್ತಿ ನೋಡಿದಾಗ ಏಸಾವನು ನಾನೂರು ಮಂದಿ ಮನುಷ್ಯರ ಸಮೇತ ಬರುವದನ್ನು ಕಂಡನು. ಆಗ ಅವನು ಲೇಯಳಿಗೂ ರಾಹೇಲಳಿಗೂ ಇಬ್ಬರು ದಾಸಿಯರಿಗೂ ಅವರವರ ಮಕ್ಕಳನ್ನು ಒಪ್ಪಿಸಿ ಅವರನ್ನು ಬೇರೆ ಬೇರೆ ಮಾಡಿ ಮುಂದುಗಡೆಯಲ್ಲಿ ದಾಸಿಯರನ್ನೂ",
			"2": "<sup>2</sup> ಅವರ ಮಕ್ಕಳನ್ನೂ ಅವರ ಹಿಂದೆ ಲೇಯಳನ್ನೂ ಆಕೆಯ ಮಕ್ಕಳನ್ನೂ ಕಟ್ಟಕಡೆಯಲ್ಲಿ ರಾಹೇಲಳನ್ನೂ ಯೋಸೇಫನನ್ನೂ ಬರಮಾಡಿದನು.",
			"3": "<sup>3</sup> ತಾನೇ ಅವರ ಮುಂದಾಗಿ ಹೋಗಿ ತನ್ನ ಅಣ್ಣನಿಗೆ ಸಮೀಪಿಸುತ್ತಿರುವಾಗ ಏಳು ಸಾರಿ ನೆಲದ ತನಕ ಬೊಗ್ಗಿ ನಮಸ್ಕರಿಸಿದನು.",
			"4": "<sup>4</sup> ಆದರೆ ಏಸಾವನು ಅವನನ್ನು ಎದುರುಗೊಳ್ಳುವದಕ್ಕೆ ಓಡಿ ಬಂದು ಅವನ ಕೊರಳನ್ನು ಅಪ್ಪಿಕೊಂಡು ಮುದ್ದಿಟ್ಟನು. ಅವರಿಬ್ಬರೂ ಕಣ್ಣೀರು ಸುರಿಸಿದರು.",
			"5": "<sup>5</sup> ತರುವಾಯ ಏಸಾವನು ಕಣ್ಣೆತ್ತಿ ಆ ಸ್ತ್ರೀಯರನ್ನೂ ಮಕ್ಕಳನ್ನೂ ಕಂಡು - ನಿನ್ನ ಜೊತೆಯಲ್ಲಿರುವ ಇವರು ಯಾರೆಂದು ಕೇಳಲು ಯಾಕೋಬನು ಅವನಿಗೆ - ದೇವರು ನಿನ್ನ ಸೇವಕನಿಗೆ ಅನುಗ್ರಹಿಸಿಕೊಟ್ಟ ಮಕ್ಕಳೇ ಇವರು ಎಂದು ಹೇಳಿದನು.",
			"6": "<sup>6</sup> ಆಗ ಅವನ ದಾಸಿಯರಿಬ್ಬರೂ ತಮ್ಮ ಮಕ್ಕಳನ್ನು ಕರಕೊಂಡು ಹತ್ತಿರಕ್ಕೆ ಬಂದು ನಮಸ್ಕಾರ ಮಾಡಿದರು;",
			"7": "<sup>7</sup> ಆಮೇಲೆ ಲೇಯಳು ತನ್ನ ಮಕ್ಕಳೊಡನೆ ಬಂದು ಅಡ್ಡಬಿದ್ದಳು; ಕಡೆಯಲ್ಲಿ ಯೋಸೇಫನೂ ರಾಹೇಲಳೂ ಬಂದು ಎರಗಿದರು.",
			"8": "<sup>8</sup> ಏಸಾವನು - ನಾನು ದಾರಿಯಲ್ಲಿ ಕಂಡ ಆ ಪಶುಗಳ ಹಿಂಡುಗಳು ಯಾತಕ್ಕೆ ಎಂದು ಕೇಳಲು ಯಾಕೋಬನು - ಸ್ವಾಮಿಯವರ ದಯ ನನಗೆ ದೊರಕಬೇಕೆಂದು ನಾನು ತಮಗೆ ಕಳುಹಿಸಿಕೊಟ್ಟೆನು ಅಂದನು.",
			"9": "<sup>9</sup> ಏಸಾವನು - ತಮ್ಮನೇ, ನನಗೆ ಬೇಕಾದಷ್ಟು ಆಸ್ತಿಯುಂಟು; ನಿನ್ನದು ನಿನಗೇ ಇರಲಿ ಅನ್ನಲು",
			"10": "<sup>10</sup> ಯಾಕೋಬನು - ಹಾಗೆ ಅಪ್ಪಣೆಕೊಡಕೂಡದು; ನೀನು ನನ್ನ ಮೇಲೆ ಕಟಾಕ್ಷವಿಟ್ಟು ನಾನು ಸಮರ್ಪಿಸುವ ಕಾಣಿಕೆಯನ್ನು ಅಂಗೀಕರಿಸಬೇಕು. ನಿನ್ನನ್ನು ನೋಡಿದ್ದು ದೇವರನ್ನು ನೋಡಿದ ಹಾಗಾಯಿತು. ನನ್ನನ್ನು ಪ್ರೀತಿಯಿಂದ ಅಂಗೀಕರಿಸಿದ್ದೇ ವಿಶೇಷ.",
			"11": "<sup>11</sup> ದೇವರು ನನಗೆ ಕೃಪೆಯನ್ನು ಮಾಡಿದ್ದರಿಂದ ನನಗೆ ಸಮೃದ್ಧಿಯುಂಟು; ಆದಕಾರಣ ನಾನು ಸಮರ್ಪಿಸುವ ಕಾಣಿಕೆಯನ್ನು ನೀನು ದಯವಿಟ್ಟು ಅಂಗೀಕರಿಸಬೇಕು ಎಂದು ಹೇಳಿ ಏಸಾವನನ್ನು ಬಲವಂತಮಾಡಿದ್ದರಿಂದ ಅವನು ಆ ಕಾಣಿಕೆಯನ್ನು ತೆಗೆದುಕೊಂಡನು.",
			"12": "<sup>12</sup> ತರುವಾಯ ಏಸಾವನು - ನಾವು ಮುಂದಕ್ಕೆ ಪ್ರಯಾಣವನ್ನು ಬೆಳೆಸೋಣ; ನಾನು ಮುಂದಾಗಿ ಹೋಗುತ್ತೇನೆ ಅಂದನು.",
			"13": "<sup>13</sup> ಅದಕ್ಕೆ ಯಾಕೋಬನು - ನನ್ನ ಮಕ್ಕಳು ಎಳೇ ಪ್ರಾಯದವರು; ಅದಲ್ಲದೆ ಈದಿರುವ ದನಕುರಿಗಳು ನನ್ನ ಬಳಿಯಲ್ಲಿರುವದು ಪ್ರಭುವಿಗೆ ತಿಳಿದಿದೆ; ಒಂದೇ ದಿನ ಹೆಚ್ಚಾಗಿ ದಾರಿ ನಡಿಸಿದರೆ ಆಡುಕುರಿಗಳೆಲ್ಲವೂ ಸತ್ತು ಹೋದಾವು.",
			"14": "<sup>14</sup> ತಾವು ದಯವಿಟ್ಟು ಸೇವಕನಿಗಿಂತಲೂ ಮುಂಚೆ ಹೊರಡಬಹುದು. ನಾನು ನನ್ನ ಮುಂದಿರುವ ಆಡುಕುರಿಗಳ ನಡಿಗೆಗೂ ಮಕ್ಕಳ ಶಕ್ತಿಗೂ ಸರಿಯಾಗಿ ಮೆಲ್ಲಮೆಲ್ಲನೆ ನಡೆದು ನನ್ನ ಪ್ರಭುವಿನ ಸೀಮೆಯಾಗಿರುವ ಸೇಯೀರಿಗೆ ಬರುತ್ತೇನೆ ಅಂದನು.",
			"15": "<sup>15</sup> ಏಸಾವನು - ನನ್ನ ಆಳುಗಳಲ್ಲಿ ಕೆಲವರನ್ನು ನಿನ್ನ ಸಂಗಡ ಬಿಟ್ಟು ಹೋಗುತ್ತೇನೆ ಅನ್ನಲು ಯಾಕೋಬನು - ಅದೇಕೆ, ತಮ್ಮ ದಯೆ ನನ್ನ ಮೇಲಿದ್ದರೆ ಸಾಕು ಎಂದು ಹೇಳಿದನು.",
			"16": "<sup>16</sup> ಏಸಾವನು ಆ ದಿವಸವೇ ಸೇಯೀರ್ ಸೀಮೆಗೆ ಹೊರಟು ಹೋದನು.",
			"17": "<sup>17</sup> ಯಾಕೋಬನು ಪ್ರಯಾಣ ಮಾಡಿ ಸುಕ್ಕೋತಿಗೆ ಬಂದನು. ಅಲ್ಲಿ ತನಗೋಸ್ಕರ ಮನೆಯನ್ನೂ ದನಗಳಿಗೆ ಚಪ್ಪರಗಳನ್ನೂ ಮಾಡಿಸಿದನು; ಆದದರಿಂದ ಆ ಸ್ಥಳಕ್ಕೆ ಸುಕ್ಕೋತ್ ಎಂದು ಹೆಸರಾಯಿತು.",
			"18": "<sup>18</sup> ಯಾಕೋಬನು ಪದ್ದನ್ಅರಾಮಿನಿಂದ ಬಂದ ಮೇಲೆ ಕಾನಾನ್‍ದೇಶದ ಶೆಕೆಮ್ ಪಟ್ಟಣಕ್ಕೆ ಸುರಕ್ಷಿತವಾಗಿ ಸೇರಿದನು. ಆ ಊರಿನ ಮುಂದೆ ತನ್ನ ಗುಡಾರಗಳನ್ನು ಹಾಕಿಸಿಕೊಂಡನು.",
			"19": "<sup>19</sup> ಅವನು ತನ್ನ ಗುಡಾರಗಳನ್ನು ಹಾಕಿಸಿದ ಭೂಮಿಯನ್ನು ಹಮೋರನ ಮಕ್ಕಳಿಗೆ ನೂರು ವರಹಾ ಕೊಟ್ಟು ಅವರಿಂದ ಕೊಂಡುಕೊಂಡನು. ಹಮೋರನು ಶೆಕೆಮನ ತಂದೆ.",
			"20": "<sup>20</sup> ಅಲ್ಲಿ ಯಾಕೋಬನು ಯಜ್ಞವೇದಿಯನ್ನು ಕಟ್ಟಿಸಿ ಅದಕ್ಕೆ ಏಲೆಲೋಹೇ ಇಸ್ರಾಯೇಲ್ ಎಂದು ಹೆಸರಿಟ್ಟನು."
		},
		"34": {
			"1": "<sup>1</sup> ಯಾಕೋಬನಿಗೆ ಲೇಯಳಲ್ಲಿ ಹುಟ್ಟಿದ ದೀನಳು ಒಂದಾನೊಂದು ದಿವಸ ಆ ದೇಶದ ಸ್ತ್ರೀಯರನ್ನು ನೋಡುವದಕ್ಕೆ ಹೊರಗೆ ಬಂದಾಗ",
			"2": "<sup>2</sup> ದೇಶಾಧಿಪತಿಯಾಗಿರುವ ಹಿವ್ವಿಯನಾದ ಹಮೋರನ ಮಗ ಶೆಕೆಮನು ಆಕೆಯನ್ನು ನೋಡಿ ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ಕೂಡಿ ಮಾನಭಂಗ ಪಡಿಸಿದನು.",
			"3": "<sup>3</sup> ಅವನ ಮನಸ್ಸು ಯಾಕೋಬನ ಮಗಳಾದ ದೀನಳ ಮೇಲೆಯೇ ಇತ್ತು; ಆ ಹುಡುಗಿಯನ್ನು ಮೋಹಿಸಿ ಆಕೆಯ ಸಂಗಡ ಮನವೊಲಿಸುವ ಮಾತುಗಳನ್ನಾಡಿದನು.",
			"4": "<sup>4</sup> ಅವನು ತನ್ನ ತಂದೆಯಾದ ಹಮೋರನನ್ನು - ನೀನು ಆ ಹುಡುಗಿಯನ್ನು ನನಗೆ ಮದುವೆ ಮಾಡಿಸಬೇಕೆಂದು ಕೇಳಿಕೊಂಡನು.",
			"5": "<sup>5</sup> ಯಾಕೋಬನು ತನ್ನ ಮಗಳಾದ ದೀನಳಿಗೆ ಶೆಕೆಮನಿಂದ ಮಾನಭಂಗವಾದ ವರ್ತಮಾನವನ್ನು ಕೇಳಿದಾಗ ಅವನ ಗಂಡುಮಕ್ಕಳು ಅಡವಿಯಲ್ಲಿ ದನಗಳನ್ನು ಕಾಯುತ್ತಿದ್ದರು. ಅವರು ಬರುವ ತನಕ ಅವನು ಸುಮ್ಮನೇ ಇದ್ದನು.",
			"6": "<sup>6</sup> ಅಷ್ಟರಲ್ಲಿ ಶೆಕೆಮನ ತಂದೆಯಾದ ಹಮೋರನು ಯಾಕೋಬನ ಸಂಗಡ ಮಾತಾಡುವದಕ್ಕೆ ಊರಿಂದ ಹೊರಗೆ ಬಂದನು.",
			"7": "<sup>7</sup> ಯಾಕೋಬನ ಗಂಡುಮಕ್ಕಳು ತಂಗಿಯ ಸಂಗತಿಯನ್ನು ಕೇಳಿ ಅಡವಿಯಿಂದ ಬಂದಾಗ ವ್ಯಥೆಪಟ್ಟು ಬಹಳ ಕೋಪಗೊಂಡರು. ಶೆಕೆಮನು ಯಾಕೋಬನ ಮಗಳನ್ನು ಕೂಡಿ ಇಸ್ರಾಯೇಲ್ಯರೊಳಗೆ ಬಹಳ ಅವಮಾನಕರವಾದ ಕೆಲಸವನ್ನು ನಡಿಸಿದನು; ಅದು ಆಗಬಾರದ ಕಾರ್ಯ.",
			"8": "<sup>8</sup> ಹಮೋರನು ಅವರಿಗೆ - ನನ್ನ ಮಗನಾದ ಶೆಕೆಮನು ನಿಮ್ಮ ಹುಡುಗಿಯನ್ನು ಬಹಳ ಅಪೇಕ್ಷೆಯಿಂದ ಮೋಹಿಸಿದ್ದಾನೆ; ಆಕೆಯನ್ನು ಅವನಿಗೆ ಮದುವೆಮಾಡಿಕೊಡಬೇಕೆಂದು ಕೇಳಿಕೊಳ್ಳುತ್ತೇನೆ.",
			"9": "<sup>9</sup> ಇದಲ್ಲದೆ ನೀವು ನಮ್ಮಲ್ಲೇ ವಾಸವಾಗಿದ್ದು ನಿಮ್ಮ ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ನಮಗೆ ಕೊಡುತ್ತಾ ನಮ್ಮ ಹೆಣ್ಣುಮಕ್ಕಳನ್ನು ನೀವು ತೆಗೆದುಕೊಳ್ಳುತ್ತಾ ಬೀಗರಾಗಿರಬೇಕು.",
			"10": "<sup>10</sup> ದೇಶವೆಲ್ಲಾ ನಿಮ್ಮ ಮುಂದೆ ಅದೆ; ಅದರಲ್ಲಿ ವಾಸಿಸಿ ವ್ಯಾಪಾರಮಾಡಿ ಆಸ್ತಿಯನ್ನು ಸಂಪಾದಿಸಿಕೊಳ್ಳಬಹುದು ಎಂದು ಹೇಳಿದನು.",
			"11": "<sup>11</sup> ಶೆಕೆಮನು ಆ ಹುಡುಗಿಯ ತಂದೆಗೂ ಅಣ್ಣಂದಿರಿಗೂ - ನಿಮ್ಮ ದಯೆ ನನ್ನ ಮೇಲೆ ಇರಲಿ; ನೀವು ಹೇಳುವಷ್ಟು ಕೊಡುತ್ತೇನೆ;",
			"12": "<sup>12</sup> ಹೆಣ್ಣಿಗಾಗಿ ತೆರವನ್ನೂ ಕಾಣಿಕೆಯನ್ನೂ ನೀವು ಹೇಳುವ ಮೇರೆಗೆ ಎಷ್ಟಾದರೂ ಕೊಡುತ್ತೇನೆ. ಹೇಗೂ ಆ ಹುಡುಗಿಯನ್ನು ನನಗೆ ಮದುವೆ ಮಾಡಿಕೊಡಬೇಕು ಎಂದು ಹೇಳಿದನು.",
			"13": "<sup>13</sup> ಶೆಕೆಮನು ತಮ್ಮ ತಂಗಿಯಾದ ದೀನಳನ್ನು ಕೆಡಿಸಿದ್ದರಿಂದ ಯಾಕೋಬನ ಮಕ್ಕಳು ಅವನಿಗೂ ಅವನ ತಂದೆಯಾದ ಹಮೋರನಿಗೂ ಕಪಟದಿಂದ ಉತ್ತರಕೊಟ್ಟು -",
			"14": "<sup>14</sup> ಸುನ್ನತಿಯಿಲ್ಲದವನಿಗೆ ನಮ್ಮ ತಂಗಿಯನ್ನು ಕೊಡುವದಕ್ಕಾಗುವದಿಲ್ಲ; ಹಾಗೆ ಕೊಡುವದು ನಮಗೆ ಅಪಮಾನ.",
			"15": "<sup>15</sup> ನಿಮ್ಮಲ್ಲಿ ಪುರುಷರೆಲ್ಲರೂ ಸುನ್ನತಿ ಮಾಡಿಸಿಕೊಂಡು ನಮ್ಮಂತೆ ಆಗಬೇಕು.",
			"16": "<sup>16</sup> ಹೀಗಾಗುವ ಪಕ್ಷದಲ್ಲಿ ಮಾತ್ರ ನಾವು ನಿಮ್ಮ ಮಾತಿಗೆ ಒಪ್ಪಿ ನಮ್ಮ ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ನಿಮಗೆ ಕೊಡುತ್ತಾ ನಿಮ್ಮ ಹೆಣ್ಣುಮಕ್ಕಳನ್ನು ನಾವು ತೆಗೆದುಕೊಳ್ಳುತ್ತಾ ನಿಮ್ಮಲ್ಲಿ ವಾಸಮಾಡಿ ನಿಮ್ಮೊಂದಿಗೆ ಒಂದೇ ಕುಲವಾಗಿರುವೆವು.",
			"17": "<sup>17</sup> ನೀವು ನಮ್ಮ ಮಾತಿಗೆ ಒಪ್ಪದೆ ಸುನ್ನತಿ ಮಾಡಿಸಿಕೊಳ್ಳದೆ ಹೋದರೆ ನಾವು ನಮ್ಮ ಹುಡುಗಿಯನ್ನು ಕರೆದುಕೊಂಡು ಹೊರಟುಹೋಗುತ್ತೇವೆ ಅಂದರು.",
			"18": "<sup>18</sup> ಅವರು ಹೇಳಿದ್ದು ಹಮೋರನಿಗೂ ಅವನ ಮಗನಾದ ಶೆಕೆಮನಿಗೂ ಒಳ್ಳೇದೆಂದು ತೋಚಿತು.",
			"19": "<sup>19</sup> ಆ ಯೌವನಸ್ಥನು ಯಾಕೋಬನ ಮಗಳಲ್ಲಿ ಅನುರಕ್ತನಾಗಿದ್ದದರಿಂದ ಅವರು ಹೇಳಿದಂತೆ ಮಾಡುವದಕ್ಕೆ ತಡೆಮಾಡಲಿಲ್ಲ. ಅವನೇ ತನ್ನ ತಂದೆಯ ಮನೆಯವರೆಲ್ಲರಲ್ಲಿ ಘನವಂತನಾಗಿದ್ದನು.",
			"20": "<sup>20</sup> ಹಮೋರನೂ ಅವನ ಮಗನಾದ ಶೆಕೆಮನೂ ಊರುಬಾಗಿಲಿಗೆ ಹೋಗಿ ಊರಿನವರೆಲ್ಲರ ಸಂಗಡ ಮಾತಾಡಿ -",
			"21": "<sup>21</sup> ಆ ಜನರು ನಮ್ಮೊಡನೆ ಸಮಾಧಾನದಿಂದಿದ್ದಾರೆ; ಅವರು ನಮ್ಮ ದೇಶದಲ್ಲೇ ವಾಸಮಾಡಿಕೊಂಡು ವ್ಯಾಪಾರ ಮಾಡಲಿ; ಈ ದೇಶವು ಅವರಿಗೂ ನಮಗೂ ಸಾಕಾಗುವಷ್ಟು ವಿಸ್ತಾರವಾಗಿದೆಯಷ್ಟೆ; ನಾವು ಅವರ ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ಮದುವೆ ಮಾಡಿಕೊಳ್ಳೋಣ, ನಮ್ಮ ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ಅವರಿಗೆ ಕೊಡೋಣ.",
			"22": "<sup>22</sup> ಆದರೆ ಅವರು ಸುನ್ನತಿ ಮಾಡಿಸಿಕೊಂಡವರಾದ್ದರಿಂದ ನಮ್ಮಲ್ಲಿಯೂ ಪುರುಷರೆಲ್ಲರು ಸುನ್ನತಿ ಮಾಡಿಸಿಕೊಳ್ಳಬೇಕೆನ್ನುತ್ತಾರೆ; ಹೀಗಾದರೆ ಮಾತ್ರ ಅವರು ನಮ್ಮಲ್ಲಿ ವಾಸಿಸುವದಕ್ಕೂ ನಮ್ಮ ಸಂಗಡ ಒಂದೇ ಕುಲವಾಗುವದಕ್ಕೂ ಒಪ್ಪುವರು.",
			"23": "<sup>23</sup> ಅವರ ಕುರಿದನಗಳೂ ಅವರ ಆಸ್ತಿಯೂ ಅವರ ಎಲ್ಲಾ ಪಶುಗಳೂ ನಮ್ಮವುಗಳೇ ಆಗುವವಲ್ಲವೇ. ಅವರು ನಮ್ಮಲ್ಲಿ ವಾಸಮಾಡುವ ಹಾಗೆ ನಾವು ಅವರ ಮಾತಿಗೆ ಒಪ್ಪಿಕೊಳ್ಳೋಣ ಎಂದು ಹೇಳಿದರು.",
			"24": "<sup>24</sup> ಹಮೋರನೂ ಅವನ ಮಗನಾದ ಶೆಕೆಮನೂ ಹೇಳಿದ ಮಾತಿಗೆ ಊರಿನವರೆಲ್ಲರೂ ಸಮ್ಮತಿಪಟ್ಟದ್ದರಿಂದ ಅವರಲ್ಲಿದ್ದ ಪುರುಷರೆಲ್ಲರೂ ಸುನ್ನತಿಮಾಡಿಸಿಕೊಂಡರು.",
			"25": "<sup>25</sup> ಮೂರನೆಯ ದಿನದಲ್ಲಿ ಅವರು ಗಾಯದಿಂದ ಬಹು ಬಾಧೆಪಡುತ್ತಿರುವಾಗ ಯಾಕೋಬನ ಮಕ್ಕಳಲ್ಲಿ ದೀನಳ ಅಣ್ಣಂದಿರಾದ ಸಿಮೆಯೋನ್, ಲೇವಿ ಎಂಬ ಇಬ್ಬರು ಕೈಯಲ್ಲಿ ಕತ್ತಿಹಿಡುಕೊಂಡು ನಿರ್ಭಯವಾಗಿದ್ದ ಆ ಊರಿನವರ ಮೇಲೆ ಬಿದ್ದು",
			"26": "<sup>26</sup> ಹಮೋರ್ ಮತ್ತು ಅವನ ಮಗನಾದ ಶೆಕೆಮ್ ಸಹಿತವಾಗಿ ಪುರುಷರೆಲ್ಲರನ್ನೂ ದಾಕ್ಷಿಣ್ಯವಿಲ್ಲದೆ ಕೊಂದು ಶೆಕೆಮನ ಮನೆಯಿಂದ ದೀನಳನ್ನು ಕರೆದುಕೊಂಡು ಹೊರಟುಹೋದರು.",
			"27": "<sup>27</sup> ಅವರು ಹತವಾದನಂತರ ಯಾಕೋಬನ ಮಕ್ಕಳು ಬಂದು - ಇವರು ನಮ್ಮ ತಂಗಿಯನ್ನು ಅವಮಾನಪಡಿಸಿದರಲ್ಲಾ ಎಂದು ಹೇಳಿ ಊರನ್ನು ಸೂರೆಮಾಡಿಬಿಟ್ಟರು.",
			"28": "<sup>28</sup> ಅವರ ಕುರಿ ದನ ಕತ್ತೆಗಳನ್ನೂ ಊರಲ್ಲಿಯೂ ಅಡವಿಯಲ್ಲಿಯೂ ಇದ್ದ ಅವರ ಆಸ್ತಿಯೆಲ್ಲವನ್ನೂ ಸುಲುಕೊಂಡು",
			"29": "<sup>29</sup> ಅವರ ಮಕ್ಕಳನ್ನೂ ಹೆಂಡರನ್ನೂ ಸೆರೆಹಿಡಿದು ಮನೆಯಲ್ಲಿದ್ದದ್ದನ್ನೆಲ್ಲಾ ಬಾಚಿಕೊಂಡರು.",
			"30": "<sup>30</sup> ಯಾಕೋಬನು ಸಿಮೆಯೋನ್ ಲೇವಿಯರಿಗೆ - ನೀವು ಈ ದೇಶದ ನಿವಾಸಿಗಳಾದ ಕಾನಾನ್ಯರಲ್ಲಿಯೂ ಪೆರಿಜೀಯರಲ್ಲಿಯೂ ನನ್ನ ಹೆಸರು ದುರ್ವಾಸನೆಯಾಗುವಂತೆ ಮಾಡಿ ನನ್ನನ್ನು ಅಪಾಯಕ್ಕೆ ಗುರಿಮಾಡಿದ್ದೀರಿ. ನನಗಿರುವ ಜನರು ಸ್ವಲ್ಪವೇ; ಈ ದೇಶದವರು ಒಟ್ಟಾಗಿ ನನ್ನ ಮೇಲೆ ಬಂದು ನನ್ನನ್ನು ಹೊಡೆದರೆ ನಾನೂ ನನ್ನ ಮನೆಯವರೆಲ್ಲರೂ ನಾಶವಾಗುವೆವು ಅಲ್ಲವೇ ಎಂದು ಹೇಳಲು",
			"31": "<sup>31</sup> ಅವರು - ನಮ್ಮ ತಂಗಿಯನ್ನು ಸೂಳೆಯಂತೆ ನಡಿಸಬಹುದೋ ಎಂದು ಹೇಳಿದರು."
		},
		"35": {
			"1": "<sup>1</sup> ದೇವರು ಯಾಕೋಬನಿಗೆ - ನೀನು ಈ ಸ್ಥಳವನ್ನು ಬಿಟ್ಟು ಗಟ್ಟಾಹತ್ತಿ ಬೇತೇಲಿಗೆ ಹೋಗಿ ಅಲ್ಲೇ ವಾಸಮಾಡು. ನೀನು ನಿನ್ನ ಅಣ್ಣನಾದ ಏಸಾವನ ಬಳಿಯಿಂದ ಓಡಿಹೋದ ಕಾಲದಲ್ಲಿ ದೇವರು ಅಲ್ಲಿ ನಿನಗೆ ದರ್ಶನಕೊಟ್ಟನಲ್ಲಾ; ಆತನಿಗಾಗಿ ಯಜ್ಞವೇದಿಯನ್ನು ಕಟ್ಟಿಸು ಎಂದು ಹೇಳಿದನು.",
			"2": "<sup>2</sup> ಆಗ ಯಾಕೋಬನು ತನ್ನ ಮನೆಯವರಿಗೂ ತನ್ನ ಸಂಗಡ ಇದ್ದವರೆಲ್ಲರಿಗೂ - ನಿಮ್ಮಲ್ಲಿರುವ ಅನ್ಯದೇವರುಗಳನ್ನು ತಳ್ಳಿಬಿಟ್ಟು ನಿಮ್ಮನ್ನು ಶುದ್ಧೀಕರಿಸಿಕೊಂಡು ಮಡಿವಸ್ತ್ರಗಳನ್ನು ಹಾಕಿಕೊಳ್ಳಿರಿ; ನಾವು ಬೇತೇಲಿಗೆ ಹೊರಟುಹೋಗೋಣ.",
			"3": "<sup>3</sup> ಕಷ್ಟಕಾಲದಲ್ಲಿ ನನ್ನ ವಿಜ್ಞಾಪನೆಯನ್ನು ಲಾಲಿಸಿ ನಾನು ಹೋದ ಮಾರ್ಗದಲ್ಲೆಲ್ಲಾ ನನ್ನ ಸಂಗಡ ಇದ್ದ ದೇವರಿಗೆ ಅಲ್ಲಿ ಯಜ್ಞವೇದಿಯನ್ನು ಕಟ್ಟಿಸುತ್ತೇನೆ ಎಂದು ಹೇಳಿದನು.",
			"4": "<sup>4</sup> ಆಗ ಅವರು ತಮ್ಮಲ್ಲಿದ್ದ ಎಲ್ಲಾ ಅನ್ಯದೇವರುಗಳನ್ನೂ ತಮ್ಮ ಕಿವಿಗಳಲ್ಲಿದ್ದ ಮುರುವುಗಳನ್ನೂ ಯಾಕೋಬನಿಗೆ ಒಪ್ಪಿಸಲು ಅವನು ಅವುಗಳನ್ನು ಶೆಕೆಮ್ ಪಟ್ಟಣದ ಹತ್ತಿರವಿರುವ ಏಲಾವೃಕ್ಷದ ಬುಡದಲ್ಲಿ ಹೂಳಿಟ್ಟನು.",
			"5": "<sup>5</sup> ಆಮೇಲೆ ಅವರು ಪ್ರಯಾಣ ಮಾಡುತ್ತಿರುವಾಗ ದೇವರು ಹುಟ್ಟಿಸಿದ ಭಯವು ಸುತ್ತುಮುತ್ತಣ ಊರುಗಳಲ್ಲಿ ಇದ್ದದರಿಂದ ಆ ಊರುಗಳವರು ಯಾಕೋಬನ ಮಕ್ಕಳನ್ನು ಹಿಂದಟ್ಟಿ ಬರಲಿಲ್ಲ.",
			"6": "<sup>6</sup> ಯಾಕೋಬನೂ ಅವನ ಸಂಗಡ ಇದ್ದ ಜನರೆಲ್ಲರೂ ಕಾನಾನ್‍ದೇಶದಲ್ಲಿರುವ ಬೇತೇಲೆಂಬ ಲೂಜಿಗೆ ಮುಟ್ಟಿದರು.",
			"7": "<sup>7</sup> ಅವನು ತನ್ನ ಅಣ್ಣನ ಬಳಿಯಿಂದ ಓಡಿಹೋಗಲು ದೇವರು ಅಲ್ಲೇ ಅವನಿಗೆ ಪ್ರತ್ಯಕ್ಷನಾದದರಿಂದ ಅವನು ಅಲ್ಲಿ ಯಜ್ಞವೇದಿಯನ್ನು ಕಟ್ಟಿಸಿ ಆ ಸ್ಥಳಕ್ಕೆ ಏಲ್ ಬೇತೇಲ್ ಎಂದು ಹೆಸರಿಟ್ಟನು.",
			"8": "<sup>8</sup> ಅಲ್ಲಿ ರೆಬೆಕ್ಕಳ ದಾದಿಯಾದ ದೆಬೋರಳು ಸತ್ತುಹೋಗಲು ಅವರು ಆಕೆಗೆ ಬೇತೇಲಿನ ತಗ್ಗಿನಲಿರುವ ಅಲ್ಲೋನ್ ಮರದ ಬುಡದಲ್ಲಿ ಸಮಾಧಿಮಾಡಿ ಅದಕ್ಕೆ ಅಲ್ಲೋನ್ ಬಾಕೂತ್ ಎಂದು ಹೆಸರಿಟ್ಟರು.",
			"9": "<sup>9</sup> ಯಾಕೋಬನು ಪದ್ದನ್ಅರಾಮಿನಿಂದ ಬಂದಾಗ ದೇವರು ತಿರಿಗಿ ಅವನಿಗೆ ದರ್ಶನ ಕೊಟ್ಟು ಅವನನ್ನು ಆಶೀರ್ವದಿಸಿದನು.",
			"10": "<sup>10</sup> ದೇವರು ಅವನಿಗೆ - ನಿನ್ನ ಹೆಸರು ಯಾಕೋಬನಲ್ಲವೇ; ಇನ್ನು ಮೇಲೆ ನೀನು ಯಾಕೋಬನೆನಿಸಿಕೊಳ್ಳದೆ ಇಸ್ರಾಯೇಲನೆನಿಸಿಕೊಳ್ಳಬೇಕು ಎಂದು ಹೇಳಿ ಅವನಿಗೆ ಇಸ್ರಾಯೇಲ ಎಂದು ಹೆಸರಿಟ್ಟನು.",
			"11": "<sup>11</sup> ಇದಲ್ಲದೆ ನಾನು ಸರ್ವಶಕ್ತನಾದ ದೇವರು. ನೀನು ಬಹು ಸಂತಾನವುಳ್ಳವನಾಗಿ ಹೆಚ್ಚು. ನಿನ್ನಿಂದ ಜನಾಂಗವುಂಟಾಗುವದು; ಅನೇಕ ಜನಾಂಗಗಳೂ ಅರಸರೂ ನಿನ್ನಿಂದ ಹುಟ್ಟುವರು.",
			"12": "<sup>12</sup> ನಾನು ಅಬ್ರಹಾಮ್ ಇಸಾಕರಿಗೆ ವಾಗ್ದಾನಮಾಡಿದ ದೇಶವನ್ನು ನಿನಗೂ ನಿನ್ನ ತರುವಾಯ ಬರುವ ನಿನ್ನ ಸಂತತಿಗೂ ಕೊಡುವೆನು ಎಂದು ಹೇಳಿ",
			"13": "<sup>13</sup> ತಾನು ಅವನ ಸಂಗಡ ಮಾತಾಡಿದ ಸ್ಥಳದಿಂದ ಮೇಲಕ್ಕೇರಿ ಹೋದನು.",
			"14": "<sup>14</sup> ಯಾಕೋಬನು ತನ್ನ ಸಂಗಡ ದೇವರು ಮಾತಾಡಿದ ಸ್ಥಳದಲ್ಲಿ ಕಲ್ಲಿನ ಕಂಬವನ್ನು ನಿಲ್ಲಿಸಿ ಅದರ ಮೇಲೆ ಪಾನಕಾಭಿಷೇಕಮಾಡಿ ಎಣ್ಣೇ ಹೊಯಿದನು.",
			"15": "<sup>15</sup> ಅವನು ತನ್ನ ಸಂಗಡ ದೇವರು ಮಾತಾಡಿದ ಸ್ಥಳಕ್ಕೆ ಬೇತೇಲೆಂದು ಹೆಸರಿಟ್ಟನು.",
			"16": "<sup>16</sup> ಅವರು ಬೇತೇಲಿನಿಂದ ಪ್ರಯಾಣ ಮಾಡುತ್ತಿರಲು ಎಫ್ರಾತೂರಿಗೆ ಸೇರುವದಕ್ಕೆ ಇನ್ನೂ ಸ್ವಲ್ಪ ದೂರವಿದ್ದಾಗ ರಾಹೇಲಳಿಗೆ ಹೆರಿಗೇ ಕಾಲಬಂತು.",
			"17": "<sup>17</sup> ಆಕೆ ಹೆರಿಗೆಯ ಬೇನೆಯಿಂದ ಬಹು ಕಷ್ಟಪಡುತ್ತಿರುವಾಗ ಸೂಲಗಿತ್ತಿಯು ಆಕೆಗೆ - ಅಂಜಬೇಡ; ಇನ್ನೊಂದು ಗಂಡು ಮಗುವು ಹುಟ್ಟುವದಲ್ಲಾ ಎಂದು ಹೇಳಿದಳು.",
			"18": "<sup>18</sup> ಆದರೆ ರಾಹೇಲಳು ಸತ್ತುಹೋದಳು; ಪ್ರಾಣಹೋಗುತ್ತಿರುವಾಗ ಆಕೆಯು ಆ ಮಗುವಿಗೆ ಬೆನೋನಿ ಎಂದು ಹೆಸರಿಟ್ಟಳು; ಆದರೆ ಅದರ ತಂದೆಯು ಅದಕ್ಕೆ ಬೆನ್ಯಾಮೀನ್ ಎಂದು ಹೆಸರಿಟ್ಟನು. ಹಾಗೆ ರಾಹೇಲಳು ಮೃತಪಟ್ಟಳು.",
			"19": "<sup>19</sup> ಬೇತ್ಲೆಹೇಮೆಂಬ ಎಫ್ರಾತಿಗೆ ಹೋಗುವ ದಾರಿಯಲ್ಲಿ ಆಕೆಗೆ ಸಮಾಧಿಮಾಡಿದರು.",
			"20": "<sup>20</sup> ಯಾಕೋಬನು ಆಕೆಯ ಸಮಾಧಿಯ ಮೇಲೆ ಕಂಬವನ್ನು ನಿಲ್ಲಿಸಿದನು; ಅದು ಇಂದಿನವರೆಗೂ ರಾಹೇಲಳ ಸಮಾಧಿಸ್ತಂಭವೆನಿಸಿಕೊಳ್ಳುತ್ತದೆ.",
			"21": "<sup>21</sup> ಅಲ್ಲಿಂದ ಇಸ್ರಾಯೇಲನು ಪ್ರಯಾಣ ಮಾಡಿ ಮಿಗ್ದಲ್ಏದರಿನ ಆಚೆಯಲ್ಲಿ ತನ್ನ ಗುಡಾರವನ್ನು ಹಾಕಿಸಿದನು.",
			"22": "<sup>22</sup> ಇಸ್ರಾಯೇಲನು ಆ ದೇಶದಲ್ಲಿ ವಾಸವಾಗಿದ್ದಾಗ ರೂಬೇನನು ತನ್ನ ತಂದೆಯ ಉಪಪತ್ನಿಯಾದ ಬಿಲ್ಹಳಲ್ಲಿ ಸಂಗಮಿಸಿದನು; ಮತ್ತು ಆ ಸಂಗತಿಯು ಇಸ್ರಾಯೇಲನಿಗೆ ತಿಳಿದುಬಂತು. ಯಾಕೋಬನ ಗಂಡು ಮಕ್ಕಳು ಹನ್ನೆರಡು ಮಂದಿ.",
			"23": "<sup>23</sup> ಅವರು ಯಾರಾರಂದರೆ - ಯಾಕೋಬನ ಚೊಚ್ಚಲಮಗನಾದ ರೂಬೇನ್; ಆಮೇಲೆ ಸಿಮೆಯೋನ್, ಲೇವಿ, ಯೆಹೂದಾ, ಇಸ್ಸಾಕಾರ್, ಜೆಬುಲೂನ್; ಇವರು ಲೇಯಳಲ್ಲಿ ಹುಟ್ಟಿದವರು.",
			"24": "<sup>24</sup> ಯೋಸೇಫ, ಬೆನ್ಯಾಮೀನ್ ಇವರಿಬ್ಬರು ರಾಹೇಲಳಲ್ಲಿ ಹುಟ್ಟಿದವರು.",
			"25": "<sup>25</sup> ದಾನ್, ನಫ್ತಾಲಿಯರು ರಾಹೇಲಳ ದಾಸಿಯಾದ ಬಿಲ್ಹಳಲ್ಲಿ ಹುಟ್ಟಿದವರು.",
			"26": "<sup>26</sup> ಗಾದ್, ಆಶೇರ್ ಇವರಿಬ್ಬರು ಲೇಯಳ ದಾಸಿಯಾದ ಜಿಲ್ಪಳಲ್ಲಿ ಹುಟ್ಟಿದವರು. ಪದ್ದನ್ಅರಾಮಿನಲ್ಲಿ ಯಾಕೋಬನಿಗೆ ಹುಟ್ಟಿದ ಮಕ್ಕಳು ಇವರೇ.",
			"27": "<sup>27</sup> ಯಾಕೋಬನು ತನ್ನ ತಂದೆಯಾದ ಇಸಾಕನ ಬಳಿಗೆ ಮಮ್ರೆಗೆ ಬಂದನು. ಮಮ್ರೆಯು ಹೆಬ್ರೊನೆಂಬ ಕಿರ್ಯತರ್ಬಕ್ಕೆ ಸೇರಿರುವದು; ಅದೇ ಅಬ್ರಹಾಮ್ ಇಸಾಕರು ಪರದೇಶಸ್ಥರಾಗಿ ವಾಸವಾಗಿದ್ದ ಸ್ಥಳ.",
			"28": "<sup>28</sup> ಇಸಾಕನು ದಿನತುಂಬಿದ ಮುದುಕನಾಗಿದ್ದು ನೂರ ಎಂಭತ್ತನೆಯ ವರುಷದಲ್ಲಿ ಪ್ರಾಣಬಿಟ್ಟು ತನ್ನ ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಿದನು.",
			"29": "<sup>29</sup> ಅವನ ಮಕ್ಕಳಾದ ಏಸಾವ ಯಾಕೋಬರು ಅವನಿಗೆ ಸಮಾಧಿಮಾಡಿದರು."
		},
		"36": {
			"1": "<sup>1</sup> ಎದೋಮನೆಂಬ ಏಸಾವನ ವಂಶಸ್ಥರು.",
			"2": "<sup>2</sup> ಏಸಾವನು ಕಾನಾನ್ಯರ ಸ್ತ್ರೀಯರನ್ನು ಮದುವೆಮಾಡಿಕೊಂಡಿದ್ದನು. ಅವರು ಯಾರಾರಂದರೆ - ಹಿತ್ತಿಯನಾದ ಏಲೋನನ ಮಗಳಾಗಿದ್ದ ಆದಾ, ಹಿವ್ವಿಯನಾದ ಸಿಬೆಯೋನನ ಮಗಳಾದ",
			"3": "<sup>3</sup> ಅನಾಹಳ ಮಗಳಾಗಿದ್ದ ಒಹೊಲೀಬಾಮ; ಮತ್ತು ಇಷ್ಮಾಯೇಲನ ಮಗಳೂ ನೆಬಾಯೋತನ ತಂಗಿಯೂ ಆಗಿದ್ದ ಬಾಸೆಮತ್ ಇವರೇ.",
			"4": "<sup>4</sup> ಆದಾ ಎಂಬಾಕೆಯು ಏಸಾವನಿಗೆ ಎಲೀಫಜನನ್ನು ಹೆತ್ತಳು. ಬಾಸೆಮತಳು ರೆಗೂವೇಲನನ್ನು ಹೆತ್ತಳು.",
			"5": "<sup>5</sup> ಒಹೊಲೀಬಾಮಳು ಯೆಗೂಷನನ್ನೂ ಯಳಾಮನನ್ನೂ ಕೋರಹನನ್ನೂ ಹೆತ್ತಳು. ಕಾನಾನ್‍ದೇಶದಲ್ಲಿ ಏಸಾವನಿಗೆ ಹುಟ್ಟಿದ ಮಕ್ಕಳು ಇವರೇ.",
			"6": "<sup>6</sup> ಏಸಾವನು ತನ್ನ ಹೆಂಡರನ್ನೂ ಗಂಡು ಹೆಣ್ಣುಮಕ್ಕಳನ್ನೂ ತನ್ನ ಮನೆಗೆ ಸೇರಿದ ಸೇವಕರೆಲ್ಲರನ್ನೂ ತನಗಿದ್ದ ಎಲ್ಲಾ ಪಶುಗಳ ಹಿಂಡುಗಳನ್ನೂ ತಾನು ಕಾನಾನ್ ದೇಶದಲ್ಲಿ ಸಂಪಾದಿಸಿದ್ದ ಆಸ್ತಿಯೆಲ್ಲವನ್ನೂ ತೆಗೆದುಕೊಂಡು ತನ್ನ ತಮ್ಮನಾದ ಯಾಕೋಬನ ಬಳಿಯಿಂದ ಪರದೇಶಕ್ಕೆ ಹೊರಟುಹೋದನು.",
			"7": "<sup>7</sup> ಅವರ ಸಂಪತ್ತು ಹೆಚ್ಚಿದ್ದರಿಂದ ಅವರಿಬ್ಬರೂ ಒಂದೇ ಸ್ಥಳದಲ್ಲಿರುವದಕ್ಕೆ ಅನುಕೂಲವಿರಲಿಲ್ಲ; ಅವರ ಪಶುಗಳು ಬಹಳವಾಗಿದ್ದದರಿಂದ ಅವರು ಪ್ರವಾಸವಾಗಿದ್ದ ದೇಶವು ಅವರಿಗೆ ಸಾಲದೆಹೋಯಿತು.",
			"8": "<sup>8</sup> ಹೀಗಿರಲಾಗಿ ಏಸಾವನು ಸೇಯೀರ್‍ಬೆಟ್ಟದ ಸೀಮೆಗೆ ಹೋಗಿ ಅಲ್ಲೇ ವಾಸವಾಗಿದ್ದನು. ಏಸಾವನಂದರೆ ಎದೋಮನು.",
			"9": "<sup>9</sup> ಸೇಯೀರ್ ಬೆಟ್ಟದ ಸೀಮೆಯಲ್ಲಿದ್ದ ಎದೋಮ್ಯರ ಮೂಲಪುರುಷನಾದ ಏಸಾವನ ವಂಶಸ್ಥರ ಚರಿತ್ರೆ.",
			"10": "<sup>10</sup> ಏಸಾವನ ಮಕ್ಕಳ ಹೆಸರುಗಳು ಯಾವವೆಂದರೆ - ಎಲೀಫಜನು ಏಸಾವನ ಹೆಂಡತಿಯಾದ ಆದಾ ಎಂಬಾಕೆಯ ಮಗನು; ರೆಗೂವೇಲನು ಏಸಾವನ ಹೆಂಡತಿಯಾದ ಬಾಸೆಮತಳ ಮಗನು.",
			"11": "<sup>11</sup> ಎಲೀಫಜನ ಮಕ್ಕಳು ಯಾರಂದರೆ - ತೇಮಾನ್, ಓಮಾರ್, ಚೆಫೋ, ಗತಾಮ್, ಕೆನಜ್,",
			"12": "<sup>12</sup> ತಿಮ್ನ ಎಂಬವಳು ಏಸಾವನ ಮಗನಾದ ಏಲೀಫಜನಿಗೆ ಉಪಪತ್ನಿಯಾಗಿದ್ದು ಅವನಿಗೆ ಅಮಾಲೇಕನನ್ನು ಹೆತ್ತಳು ಇವರೇ ಏಸಾವನ ಹೆಂಡತಿಯಾದ ಆದಾ ಎಂಬಾಕೆಯ ಸಾಲಿನವರು.",
			"13": "<sup>13</sup> ರೆಗೂವೇಲನ ಮಕ್ಕಳು ಯಾರಂದರೆ - ನಹತ್, ಜೆರಹ, ಶಮ್ಮಾ, ಮಿಜ್ಜಾ. ಇವರು ಏಸಾವನ ಹೆಂಡತಿಯಾದ ಬಾಸೆಮತಳ ಸಾಲಿನವರು.",
			"14": "<sup>14</sup> ಏಸಾವನ ಹೆಂಡತಿಯಾಗಿಯೂ ಸಿಬೆಯೋನನ ಮಗಳಾದ ಅನಾಹಳ ಮಗಳಾಗಿಯೂ ಇದ್ದ ಒಹೊಲೀಬಾಮಳ ಮಕ್ಕಳು ಯಾರಂದರೆ - ಯೆಗೂಷ್, ಯಳಾಮ್, ಕೋರಹ ಇವರೇ.",
			"15": "<sup>15</sup> ಏಸಾವನ ವಂಶಸ್ಥರ ಕುಲಪತಿಗಳು. ಏಸಾವನ ಚೊಚ್ಚಲಮಗನಾದ ಎಲೀಫಜನಿಂದ ಹುಟ್ಟಿದವರು ಯಾರಂದರೆ - ತೇಮಾನ ಕುಲಪತಿ, ಓಮಾರಕುಲಪತಿ, ಚೆಫೋಕುಲಪತಿ, ಕೆನಜ್‍ಕುಲಪತಿ,",
			"16": "<sup>16</sup> ಕೋರಹಕುಲಪತಿ, ಗತಾಮಕುಲಪತಿ, ಅಮಾಲೇಕಕುಲಪತಿ ಇವರೇ. ಈ ಕುಲಪತಿಗಳು ಎದೋಮ್ಯರ ದೇಶದಲ್ಲಿ ಆದಾ ಎಂಬಾಕೆಯ ಸಂತತಿಯಲ್ಲಿ ಹುಟ್ಟಿದವರು.",
			"17": "<sup>17</sup> ಏಸಾವನ ಮಗನಾದ ರೆಗೂವೇಲನಿಗೆ ಹುಟ್ಟಿದವರು ಯಾರಾಂದರೆ - ನಹತಕುಲಪತಿ, ಜೆರಹಕುಲಪತಿ, ಶಮ್ಮಾಕುಲಪತಿ, ಮಿಜ್ಜಾಕುಲಪತಿ ಇವರೇ. ಇವರು ಎದೋಮ್ಯರ ದೇಶದಲ್ಲಿ ಏಸಾವನ ಹೆಂಡತಿಯಾದ ಬಾಸೆಮತಳ ಸಂತತಿಯೊಳಗೆ ಹುಟ್ಟಿದವರು.",
			"18": "<sup>18</sup> ಏಸಾವನ ಹೆಂಡತಿಯಾದ ಒಹೊಲೀಬಾಮಳ ಸಂತಾನದವರು ಯಾರಂದರೆ - ಯೆಗೂಷ್‍ಕುಲಪತಿ, ಯಳಾಮಕುಲಪತಿ, ಕೋರಹಕುಲಪತಿ ಇವರೇ. ಇವರು ಅನಾಹಳ ಮಗಳಾಗಿಯೂ ಏಸಾವನ ಹೆಂಡತಿಯಾಗಿಯೂ ಇದ್ದ ಒಹೊಲೀಬಾಮಳ ಸಂತಾನದವರು.",
			"19": "<sup>19</sup> ಎದೋಮನೆನಿಸಿಕೊಳ್ಳುವ ಏಸಾವನಿಂದ ಹುಟ್ಟಿದ ಕುಲಗಳಿಗೆ ಇವರೇ ಕುಲಪತಿಗಳು.",
			"20": "<sup>20</sup> ಆ ಸೀಮೆಯ ಮೂಲನಿವಾಸಿಗಳು ಹೋರಿಯನಾದ ಸೇಯೀರನಿಂದ ಹುಟ್ಟಿದವರು; ಅವರು ಯಾರಂದರೆ - ಲೋಟಾನ್, ಶೋಬಾಲ್, ಸಿಬೆಯೋನ್, ಅನಾಹ, ದೀಶೋನ್, ಏಚೆರ್, ದೀಶಾನ್ ಇವರೇ.",
			"21": "<sup>21</sup> ಇವರು ಎದೋಮ್ಯರ ದೇಶದಲ್ಲಿ ಸೇಯೀರನ ಸಂತತಿಯವರಾದ ಹೋರಿಯರಲ್ಲಿ ಹುಟ್ಟಿದ ಕುಲಪತಿಗಳು.",
			"22": "<sup>22</sup> ಲೋಟಾನನ ಮಕ್ಕಳು - ಹೋರಿ, ಹೇಮಾಮ್; ಲೋಟಾನನ ತಂಗಿಯು ತಿಮ್ನಾ.",
			"23": "<sup>23</sup> ಶೋಬಾಲನ ಮಕ್ಕಳು - ಅಲ್ವಾನ್, ಮಾನಹತ್, ಗೇಬಾಲ್, ಶೆಫೋ, ಓನಾಮ್.",
			"24": "<sup>24</sup> ಸಿಬೆಯೋನನ ಮಕ್ಕಳು - ಅಯ್ಯಾ, ಅನಾಹ. ಈ ಅನಾಹನೇ ತನ್ನ ತಂದೆಯಾದ ಸಿಬೆಯೋನನ ಕತ್ತೆಗಳನ್ನು ಮೇಯಿಸುತ್ತಿದ್ದಾಗ ಕಾಡಿನಲ್ಲಿ ಬಿಸಿನೀರಿನ ಒರತೆಗಳನ್ನು ಕಂಡುಕೊಂಡವನು.",
			"25": "<sup>25</sup> ಅನಾಹನ ಮಕ್ಕಳು - ದೀಶೋನ್ ಮತ್ತು ಅನಾಹನ ಮಗಳಾದ ಒಹೊಲೀಬಾಮ.",
			"26": "<sup>26</sup> ದೀಶೋನನ ಮಕ್ಕಳು - ಹೆಮ್ದಾನ್, ಎಷ್ಬಾನ್, ಇತ್ರಾನ್, ಕೆರಾನ್.",
			"27": "<sup>27</sup> ಏಚೆರನ ಮಕ್ಕಳು - ಬಿಲ್ಹಾನ್, ಜಾವಾನ್ ಅಕಾನ್.",
			"28": "<sup>28</sup> ದೀಶಾನನ ಮಕ್ಕಳು - ಊಚ್, ಅರಾನ್.",
			"29": "<sup>29</sup> ಹೋರಿಯರಿಂದ ಹುಟ್ಟಿದ ಕುಲಪತಿಗಳು - ಲೋಟಾನ್‍ಕುಲಪತಿ, ಶೋಬಾಲಕುಲಪತಿ, ಸಿಬೆಯೋನ್‍ಕುಲಪತಿ,",
			"30": "<sup>30</sup> ಅನಾಹಕುಲಪತಿ, ದೀಶೋನ್‍ಕುಲಪತಿ, ಏಚೆರ್‍ಕುಲಪತಿ, ದೀಶಾನ್‍ಕುಲಪತಿ ಇವರೇ. ಇವರು ಹೋರಿಯರಿಂದ ಹುಟ್ಟಿದವರಾಗಿ ಸೇಯೀರ್ ಸೀಮೆಯಲ್ಲಿ ಆಧಿಪತ್ಯನಡಿಸಿದ ಕುಲಪತಿಗಳು.",
			"31": "<sup>31</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸರು ಆಳುವದಕ್ಕಿಂತ ಮುಂಚೆ ಎದೋಮ್ಯರ ದೇಶದಲ್ಲಿ ಆಳುತ್ತಿದ್ದ ಅರಸರ ಚರಿತ್ರೆ ಹೇಗಂದರೆ -",
			"32": "<sup>32</sup> ಬೆಯೋರನ ಮಗನಾದ ಬೆಲಗನು ಎದೋಮ್ಯರಲ್ಲಿ ಆಳಿದನು; ಅವನ ರಾಜಧಾನಿಯ ಹೆಸರು ದಿನ್ಹಾಬಾ.",
			"33": "<sup>33</sup> ಬೆಲಗನು ಸತ್ತ ಮೇಲೆ ಬೊಚ್ರದವನಾದ ಜೆರಹನ ಮಗನಾಗಿದ್ದ ಯೋಬಾಬನು ಆಧಿಪತ್ಯಕ್ಕೆ ಬಂದನು.",
			"34": "<sup>34</sup> ಯೋಬಾಬನು ಸತ್ತ ಮೇಲೆ ತೇಮಾನೀಯರ ದೇಶದವನಾದ ಹುಷಾಮನು ಅವನಿಗೆ ಬದಲಾಗಿ ಆಳಿದನು.",
			"35": "<sup>35</sup> ಹುಷಾಮನು ಸತ್ತ ಮೇಲೆ ಮೊವಾಬ್ಯರ ಬೈಲಿನಲ್ಲಿ ಮಿದ್ಯಾನರನ್ನು ಸೋಲಿಸಿದ ಬೆದದನ ಮಗನಾದ ಹದದನು ಅರಸನಾದನು; ಅವನ ರಾಜಧಾನಿಯ ಹೆಸರು ಅವೀತ್.",
			"36": "<sup>36</sup> ಹದದನು ಸತ್ತ ಮೇಲೆ ಮಸ್ರೇಕದವನಾದ ಸಮ್ಲಾಹನು ಪಟ್ಟಕ್ಕೆ ಬಂದನು.",
			"37": "<sup>37</sup> ಸಮ್ಲಾಹನು ಸತ್ತ ಮೇಲೆ ನದೀತೀರದಲ್ಲಿರುವ ರೆಹೋಬೋತೂರಿನ ಸೌಲನು ಅರಸನಾದನು.",
			"38": "<sup>38</sup> ಸೌಲನು ಸತ್ತ ಮೇಲೆ ಅಕ್ಬೋರನ ಮಗನಾದ ಬಾಳ್ಹಾನಾನನು ಅರಸನಾದನು.",
			"39": "<sup>39</sup> ಅಕ್ಬೋರನ ಮಗನಾದ ಬಾಳ್ಹಾನಾನನು ಸತ್ತ ಮೇಲೆ ಹದರನು ಅರಸನಾದನು; ಅವನ ರಾಜಧಾನಿಯ ಹೆಸರು ಪಾಗು; ಅವನ ಹೆಂಡತಿಯ ಹೆಸರು ಮಹೇಟಬೇಲ್; ಆಕೆ ಮೇಜಾಹಾಬನ ಮಗಳಾದ ಮಟ್ರೇದಳ ಮಗಳು.",
			"40": "<sup>40</sup> ಸ್ಥಳಕುಲನಾಮಗಳ ಪ್ರಕಾರ ಏಸಾವನಿಂದ ಹುಟ್ಟಿದ ಕುಲಪತಿಗಳ ಹೆಸರುಗಳು ಯಾವವಂದರೆ - ತಿಮ್ನಕುಲಪತಿ, ಅಲ್ವಾಕುಲಪತಿ, ಯೆತೇತಕುಲಪತಿ,",
			"41": "<sup>41</sup> ಒಹೊಲೀಬಾಮಾಕುಲಪತಿ, ಏಲಾಕುಲಪತಿ,",
			"42": "<sup>42</sup> ಪೀನೋನ್‍ಕುಲಪತಿ, ಕೆನೆಜ್‍ಕುಲಪತಿ,",
			"43": "<sup>43</sup> ತೇಮಾನ್‍ಕುಲಪತಿ, ಮಿಪ್ಚಾರಕುಲಪತಿ, ಮಗ್ದೀಯೇಲ್‍ಕುಲಪತಿ, ಗೀರಾಮ್‍ಕುಲಪತಿ ಇವೇ. ಇವರು ತಮ್ಮ ದೇಶದೊಳಗಣ ನಿವಾಸಸ್ಥಳಗಳ ಪ್ರಕಾರ ಎದೋಮ್ಯರ ಕುಲಪತಿಗಳು. ಮೇಲೆ ಹೇಳಿರುವದು ಎದೋಮ್ಯರ ಮೂಲ ಪುರುಷನಾದ ಏಸಾವನ ವಂಶಸ್ಥರ ಚರಿತ್ರೆ."
		},
		"37": {
			"1": "<sup>1</sup> ಆದರೆ ಯಾಕೋಬನು ತನ್ನ ತಂದೆ ಪ್ರವಾಸವಾಗಿದ್ದ ಕಾನಾನ್‍ದೇಶದಲ್ಲಿಯೇ ವಾಸವಾಗಿದ್ದನು.",
			"2": "<sup>2</sup> ಯಾಕೋಬನ ಮಕ್ಕಳ ಚರಿತ್ರೆ. ಯೋಸೇಫನು ಹದಿನೇಳು ವರುಷದವನಾಗಿ ಇನ್ನೂ ಹುಡುಗನಾಗಿದ್ದು ತನ್ನ ಅಣ್ಣಂದಿರ ಜೊತೆಯಲ್ಲಿ ಅಂದರೆ ತನ್ನ ಮಲತಾಯಿಗಳಾದ ಬಿಲ್ಹಾ, ಜಿಲ್ಪಾ ಎಂಬವರ ಮಕ್ಕಳ ಜೊತೆಯಲ್ಲಿ ಆಡುಕುರಿಗಳನ್ನು ಮೇಯಿಸುತ್ತಿದ್ದನು. ಅವರು ಏನಾದರೂ ಕೆಟ್ಟ ಕೆಲಸ ಮಾಡುವಾಗ ಅವನು ತಂದೆಗೆ ತಿಳಿಸುತ್ತಿದ್ದನು.",
			"3": "<sup>3</sup> ಯೋಸೇಫನು ಇಸ್ರಾಯೇಲನಿಗೆ ಮುಪ್ಪಿನಲ್ಲಿ ಹುಟ್ಟಿದವನಾದ್ದರಿಂದ ಇಸ್ರಾಯೇಲನು ಅವನನ್ನು ತನ್ನ ಎಲ್ಲಾ ಮಕ್ಕಳಿಗಿಂತ ಹೆಚ್ಚಾಗಿ ಪ್ರೀತಿಸಿ ಅವನಿಗೆ ನಿಲುವಂಗಿಯನ್ನು ಮಾಡಿಸಿಕೊಟ್ಟಿದ್ದನು.",
			"4": "<sup>4</sup> ಅವನ ಅಣ್ಣತಮ್ಮಂದಿರು - ನಮ್ಮ ತಂದೆ ತನ್ನ ಎಲ್ಲಾ ಮಕ್ಕಳಿಗಿಂತಲೂ ಇವನನ್ನೇ ಹೆಚ್ಚಾಗಿ ಪ್ರೀತಿಸುತ್ತಾನೆಂದು ನೋಡಿ ಯೋಸೇಫನನ್ನು ಹಗೆಮಾಡಿ ಅವನೊಡನೆ ಸ್ನೇಹಭಾವದಿಂದ ಮಾತಾಡಲಾರದೆ ಹೋದರು.",
			"5": "<sup>5</sup> ಒಂದು ದಿನ ಯೋಸೇಫನು ಕನಸುಕಂಡು ಅದನ್ನು ತನ್ನ ಅಣ್ಣಂದಿರಿಗೆ ತಿಳಿಸಲು ಅವರು ಅವನನ್ನು ಇನ್ನೂ ಹೆಚ್ಚಾಗಿ ದ್ವೇಷಿಸಿದರು.",
			"6": "<sup>6</sup> ಅವನು ಅವರಿಗೆ - ನಾನು ಕನಸಿನಲ್ಲಿ ಕಂಡದ್ದನ್ನು ಹೇಳುತ್ತೇನೆ, ಕೇಳಿರಿ.",
			"7": "<sup>7</sup> ಆ ಕನಸಿನಲ್ಲಿ ನಾವು ಹೊಲದಲ್ಲಿ ಸಿವುಡುಗಳನ್ನು ಕಟ್ಟುತ್ತಾ ಇದ್ದೆವು; ಆಗ ನನ್ನ ಸಿವುಡು ಎದ್ದು ನಿಲ್ಲಲು ನಿಮ್ಮ ಸಿವುಡುಗಳು ಸುತ್ತಲೂ ಬಂದು ನನ್ನ ಸಿವುಡಿಗೆ ಅಡ್ಡಬಿದ್ದದ್ದನ್ನು ಕಂಡೆನು ಎಂದು ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ಅದಕ್ಕೆ ಅವನ ಅಣ್ಣಂದಿರು - ನೀನು ನಿಜವಾಗಿ ನಮ್ಮನ್ನು ಆಳುವಿಯಾ? ನೀನು ನಮ್ಮ ಮೇಲೆ ದೊರೆತನಮಾಡುವಿಯಾ ಎಂದು ಅವನಿಗೆ ಹೇಳಿ ಅವನ ಕನಸುಗಳಿಗಾಗಿಯೂ ಮಾತುಗಳಿಗಾಗಿಯೂ ಮತ್ತಷ್ಟು ಹಗೆಮಾಡಿದರು.",
			"9": "<sup>9</sup> ಅವನು ಇನ್ನೊಂದು ಕನಸು ಕಂಡು ಅದನ್ನೂ ತನ್ನ ಅಣ್ಣಂದಿರಿಗೆ ತಿಳಿಸಿದನು. ಅವನು ಅವರಿಗೆ - ಇನ್ನೊಂದು ಕನಸು ಕಂಡಿದ್ದೇನೆ; ಅದರಲ್ಲಿ ಸೂರ್ಯಚಂದ್ರರೂ ಹನ್ನೊಂದು ನಕ್ಷತ್ರಗಳೂ ನನಗೆ ಅಡ್ಡಬಿದ್ದವು ಎಂದು ಹೇಳಿದನು.",
			"10": "<sup>10</sup> ಅವನು ಈ ಕನಸನ್ನು ತನ್ನ ತಂದೆಗೂ ಅಣ್ಣಂದಿರಿಗೂ ತಿಳಿಸಿದಾಗ ತಂದೆಯು ಅವನಿಗೆ - ಇದು ಎಂಥಾ ಕನಸು ನೀನು ಕಂಡದ್ದು? ನಾನೂ ನಿನ್ನ ತಾಯಿಯೂ ಅಣ್ಣತಮ್ಮಂದಿರೂ ನಿನ್ನ ಮುಂದೆ ಅಡ್ಡಬೀಳುವದಕ್ಕೆ ಬಂದೇವೋ ಎಂದು ಹೇಳಿ ಗದರಿಸಿದನು.",
			"11": "<sup>11</sup> ಯೋಸೇಫನ ಅಣ್ಣಂದಿರು ಅವನ ಮೇಲೆ ಹೊಟ್ಟೆಕಿಚ್ಚುಪಟ್ಟರು; ಆದರೆ ಅವನ ತಂದೆಯು ಅವನ ಮಾತನ್ನು ಮನಸ್ಸಿನಲ್ಲಿಟ್ಟುಕೊಂಡನು.",
			"12": "<sup>12</sup> ಒಂದಾನೊಂದು ಕಾಲದಲ್ಲಿ ಅವನ ಅಣ್ಣಂದಿರು ತಂದೆಯ ಆಡುಕುರಿಗಳನ್ನು ಶೆಕೆಮಿನಲ್ಲಿ ಮೇಯಿಸುವದಕ್ಕೆ ಹೋಗಿದ್ದಾಗ ಇಸ್ರಾಯೇಲನು",
			"13": "<sup>13</sup> ಯೋಸೇಫನಿಗೆ - ನಿನ್ನ ಅಣ್ಣಂದಿರು ಶೆಕೆಮಿನಲ್ಲಿ ಆಡುಕುರಿಗಳನ್ನು ಮೇಯಿಸುತ್ತಿದ್ದಾರಲ್ಲಾ; ಅವರ ಬಳಿಗೆ ನಿನ್ನನ್ನು ಕಳುಹಿಸುತ್ತೇನೆ ಬಾ ಎಂದು ಹೇಳಲು ಅವನು - ಇಗೋ ಇದ್ದೇನೆ ಅಂದನು.",
			"14": "<sup>14</sup> ಇಸ್ರಾಯೇಲನು ಅವನಿಗೆ - ನೀನು ಹೋಗಿ ನಿನ್ನ ಅಣ್ಣಂದಿರ ಯೋಗಕ್ಷೇಮವನ್ನೂ ಆಡುಕುರಿಗಳ ಯೋಗಕ್ಷೇಮವನ್ನೂ ವಿಚಾರಿಸಿಕೊಂಡು ಬಾ ಎಂದು ಅಪ್ಪಣೆಕೊಟ್ಟು ಅವನನ್ನು ಹೆಬ್ರೋನಿರುವ ತಗ್ಗಿನಿಂದ ಕಳುಹಿಸಲು ಯೋಸೇಫನು ಹೊರಟನು.",
			"15": "<sup>15</sup> ಅವನು ಶೆಕೆಮಿಗೆ ಬಂದು ಅಲ್ಲಿ ಅಡವಿಯೊಳಗೆ ತಿರುಗಾಡುತ್ತಿರುವಾಗ ಒಬ್ಬ ಮನುಷ್ಯನು ಅವನನ್ನು ಕಂಡು -",
			"16": "<sup>16</sup> ಏನು ಹುಡುಕುತ್ತಿ ಎಂದು ವಿಚಾರಿಸಲು ಅವನು - ನನ್ನ ಅಣ್ಣಂದಿರನ್ನು ಹುಡುಕುತ್ತಾ ಇದ್ದೇನೆ; ಅವರು ಆಡುಕುರಿಗಳನ್ನು ಎಲ್ಲಿ ಮೇಯಿಸುತ್ತಾರೆ? ದಯವಿಟ್ಟು ಹೇಳು ಅಂದನು.",
			"17": "<sup>17</sup> ಅದಕ್ಕೆ ಆ ಮನುಷ್ಯನು - ಅವರು ಇಲ್ಲಿಂದ ಹೊರಟುಹೋದರು; ದೋತಾನಿಗೆ ಹೋಗೋಣ ಎಂಬದಾಗಿ ಮಾತಾಡುವದನ್ನು ಕೇಳಿದೆನು ಎಂದು ಹೇಳಲು ಯೋಸೇಫನು ಅವರನ್ನು ಹುಡುಕುತ್ತಾ ಹೋಗಿ ದೋತಾನಿನಲ್ಲಿ ಅವರನ್ನು ಕಂಡನು.",
			"18": "<sup>18</sup> ಅವರು ಅವನನ್ನು ದೂರದಿಂದ ನೋಡಿ ಅವನು ತಮ್ಮ ಬಳಿಗೆ ಬರುವಷ್ಟರೊಳಗೆ ಅವನನ್ನು ಕೊಲ್ಲುವದಕ್ಕೆ ಒಳಸಂಚುಮಾಡಿಕೊಂಡರು.",
			"19": "<sup>19</sup> ಅವರು ಒಬ್ಬರಿಗೊಬ್ಬರು - ಅಗೋ, ಆ ಕನಸಿನವನು ಬರುತ್ತಾನೆ;",
			"20": "<sup>20</sup> ನಾವು ಅವನನ್ನು ಕೊಂದು ಈ ಗುಂಡಿಗಳಲ್ಲಿ ಒಂದರೊಳಗೆ ಹಾಕಿ ದುಷ್ಟಮೃಗವು ಅವನನ್ನು ತಿಂದುಬಿಟ್ಟಿತೆಂದು ಹೇಳೋಣ ಬನ್ನಿ; ಆಗ ಅವನ ಕನಸುಗಳು ಏನಾಗುವವೋ ನೋಡೋಣ ಎಂದು ಮಾತಾಡಿಕೊಂಡರು.",
			"21": "<sup>21</sup> ರೂಬೇನನು ಈ ಮಾತನ್ನು ಕೇಳಿ - ನಾವು ಅವನ ಪ್ರಾಣವನ್ನು ತೆಗೆಯಬಾರದು ಎಂದು ಹೇಳಿ ಅವನನ್ನು ಅವರ ಕೈಗೆ ತಪ್ಪಿಸಿದನು.",
			"22": "<sup>22</sup> ರೂಬೇನನು ಅವನನ್ನು ಅವರ ಕೈಯಿಂದ ತಪ್ಪಿಸಿ ತಂದೆಗೆ ತಿರಿಗಿ ಒಪ್ಪಿಸಬೇಕೆಂದು ಅವರಿಗೆ - ಪ್ರಾಣಹತ್ಯ ಮಾಡಬೇಡಿರಿ; ಅವನನ್ನು ಕಾಡಿನಲ್ಲಿರುವ ಈ ಗುಂಡಿಯೊಳಗೆ ಹಾಕಬಹುದು; ಅವನ ಮೇಲೆ ಕೈಹಾಕಬೇಡಿರಿ ಎಂದು ಹೇಳಿದನು.",
			"23": "<sup>23</sup> ಯೋಸೇಫನು ತನ್ನ ಅಣ್ಣಂದಿರ ಹತ್ತಿರಕ್ಕೆ ಬಂದಾಗ ಅವರು ಅವನ ಮೇಲಿದ್ದ ನಿಲುವಂಗಿಯನ್ನು ತೆಗೆದುಬಿಟ್ಟು",
			"24": "<sup>24</sup> ಅವನನ್ನು ಹಿಡಿದು ಆ ಗುಂಡಿಯೊಳಗೆ ಹಾಕಿದರು. ಆ ಗುಂಡಿ ನೀರಿಲ್ಲದೆ ಬರಿದಾಗಿತ್ತು.",
			"25": "<sup>25</sup> ಆಮೇಲೆ ಅವರು ಊಟಕ್ಕೆ ಕೂತುಕೊಂಡರು. ಅಷ್ಟರಲ್ಲಿ ಅವರು ಕಣ್ಣೆತ್ತಿ ನೋಡಿ ಇಷ್ಮಾಯೇಲ್ಯರ ಗುಂಪು ಒಂಟೆಗಳ ಮೇಲೆ ಹಾಲುಮಡ್ಡಿ, ಸುಗಂಧತೈಲ, ರಕ್ತಬೋಳ ಇವುಗಳನ್ನು ಹೇರಿಕೊಂಡು ಗಿಲ್ಯಾದಿನಿಂದ ಐಗುಪ್ತದೇಶಕ್ಕೆ ಪ್ರಯಾಣಮಾಡುತ್ತಾ ಬರುವದನ್ನು ಕಂಡರು.",
			"26": "<sup>26</sup> ಯೆಹೂದನು ತನ್ನ ಅಣ್ಣತಮ್ಮಂದಿರಿಗೆ - ನಾವು ನಮ್ಮ ತಮ್ಮನನ್ನು ಕೊಂದು ಆ ಕೊಲೆಯನ್ನು ಮರೆಮಾಡಿದರೆ ಪ್ರಯೋಜನವೇನು?",
			"27": "<sup>27</sup> ಅವನನ್ನು ಆ ಇಷ್ಮಾಯೇಲ್ಯರಿಗೆ ಮಾರಿಬಿಡೋಣ ಬನ್ನಿ; ನಾವು ಅವನ ಮೇಲೆ ಕೈಹಾಕಬಾರದು; ಅವನು ನಮ್ಮ ಒಡಹುಟ್ಟಿದ ತಮ್ಮನಲ್ಲವೇ ಎಂದು ಹೇಳಿದನು. ಆ ಮಾತಿಗೆ ಅವನ ಅಣ್ಣಂದಿರು ಒಪ್ಪಿದರು.",
			"28": "<sup>28</sup> (ಅಷ್ಟರಲ್ಲಿ ಮಿದ್ಯಾನ್ಯರಾದ ವರ್ತಕರು ಹಾದುಹೋಗುತ್ತಿದ್ದರು.) ಅವರು ಯೋಸೇಫನನ್ನು ಗುಂಡಿಯೊಳಗಿಂದ ಎತ್ತಿ ಆ ಇಷ್ಮಾಯೇಲ್ಯರಿಗೆ ಇಪ್ಪತ್ತು ರೂಪಾಯಿಗಳಿಗೆ ಮಾರಿಬಿಟ್ಟರು. ಇವರು ಅವನನ್ನು ಐಗುಪ್ತದೇಶಕ್ಕೆ ತೆಗೆದುಕೊಂಡು ಹೋದರು.",
			"29": "<sup>29</sup> ರೂಬೇನನು ತಿರಿಗಿ ಆ ಗುಂಡಿಯ ಹತ್ತಿರಕ್ಕೆ ಬಂದು ಅದರಲ್ಲಿ ಯೋಸೇಫನಿಲ್ಲದ್ದನ್ನು ಕಂಡು",
			"30": "<sup>30</sup> ತನ್ನ ಬಟ್ಟೆಗಳನ್ನು ಹರಿದುಕೊಂಡು ತನ್ನ ತಮ್ಮಂದಿರ ಬಳಿಗೆ ಬಂದು - ಆ ಹುಡುಗನು ಇಲ್ಲವಲ್ಲಾ; ಅಯ್ಯೋ, ನಾನೆಲ್ಲಿಗೆ ಹೋಗಲಿ ಎಂದು ಗೋಳಾಡಿದನು.",
			"31": "<sup>31</sup> ಆಮೇಲೆ ಅವರು ಒಂದು ಹೋತವನ್ನು ಕೊಯಿದು ಅದರ ರಕ್ತದಲ್ಲಿ ಯೋಸೇಫನ ಅಂಗಿಯನ್ನು ಅದ್ದಿ",
			"32": "<sup>32</sup> ಆ ನಿಲುವಂಗಿಯನ್ನು ತಮ್ಮ ತಂದೆಗೆ ಕಳುಹಿಸಿದರು; ತಂದವರು - ಇದು ನಮಗೆ ಸಿಕ್ಕಿತು; ಇದು ನಿನ್ನ ಮಗನ ಅಂಗಿ ಹೌದೋ ಅಲ್ಲವೋ ನೋಡು ಎಂದು ಅವನಿಗೆ ಹೇಳಲು",
			"33": "<sup>33</sup> ಅವನು ಅದರ ಗುರುತನ್ನು ಹಿಡಿದು - ಈ ಅಂಗಿ ನನ್ನ ಮಗನದೇ ಹೌದು; ದುಷ್ಟಮೃಗವು ಅವನನ್ನು ಕೊಂದು ತಿಂದಿರಬೇಕು; ಯೋಸೇಫನು ಸಂದೇಹವಿಲ್ಲದೆ ಸೀಳಿ ಹಾಕಲ್ಪಟ್ಟಿರಬೇಕು ಎಂದು ಹೇಳಿ",
			"34": "<sup>34</sup> ತನ್ನ ಬಟ್ಟೆಗಳನ್ನು ಹರಿದುಕೊಂಡು ನಡುವಿನ ಮೇಲೆ ಗೋಣೀತಟ್ಟು ಸುತ್ತಿಕೊಂಡು ತನ್ನ ಮಗನಿಗಾಗಿ ಬಹುದಿನಗಳವರೆಗೂ ಹಂಬಲಿಸುತ್ತಿದ್ದನು.",
			"35": "<sup>35</sup> ಅವನ ಗಂಡುಮಕ್ಕಳೂ ಹೆಣ್ಣು ಮಕ್ಕಳೂ ಎಲ್ಲರೂ ದುಃಖಶಮನ ಮಾಡುವದಕ್ಕೆ ಪ್ರಯತ್ನಿಸಿದಾಗ್ಯೂ ಅವನು ಶಾಂತಿಯನ್ನು ಹೊಂದಲೊಲ್ಲದೆ - ನಾನು ಹೀಗೇ ಹಂಬಲಿಸುತ್ತಾ ನನ್ನ ಮಗನಿರುವ ಪಾತಾಳವನ್ನು ಸೇರುವೆನು ಅಂದನು. ಹೀಗೆ ತಂದೆಯು ಮಗನಿಗೋಸ್ಕರ ಅಳುತ್ತಿದ್ದನು.",
			"36": "<sup>36</sup> ಮಿದ್ಯಾನ್ಯರು ಯೋಸೇಫನನ್ನು ಐಗುಪ್ತದೇಶಕ್ಕೆ ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ಫರೋಹನ ಬಳಿಯಲ್ಲಿದ್ದ ದೊಡ್ಡ ಉದ್ಯೋಗಸ್ಥನಾದ ಪೋಟೀಫರನಿಗೆ ಮಾರಿದರು; ಇವನು ಮೈಗಾವಲಿನವರ ದಳವಾಯಿ."
		},
		"38": {
			"1": "<sup>1</sup> ಆ ಕಾಲದಲ್ಲಿ ಯೆಹೂದನು ತನ್ನ ಅಣ್ಣತಮ್ಮಂದಿರನ್ನು ಬಿಟ್ಟು ಗಟ್ಟಾ ಇಳಿದು ಅದುಲ್ಲಾಮೂರಿನವನಾದ ಹೀರಾ ಎಂಬವನ ಹತ್ತಿರ ಇದ್ದುಕೊಂಡನು.",
			"2": "<sup>2</sup> ಅಲ್ಲಿ ಅವನು ಶೂಗನೆಂದು ಹೆಸರುಳ್ಳ ಒಬ್ಬ ಕಾನಾನ್ಯನ ಮಗಳನ್ನು ಕಂಡು ಅವಳನ್ನು ವರಿಸಿ ಅವಳ ಸಹವಾಸ ಮಾಡಿದನು.",
			"3": "<sup>3</sup> ಅವಳು ಬಸುರಾಗಿ ಗಂಡುಮಗುವನ್ನು ಹೆತ್ತಳು; ಅದಕ್ಕೆ ಅವನು ಏರನೆಂದು ಹೆಸರಿಟ್ಟನು.",
			"4": "<sup>4</sup> ಅವಳು ಎರಡನೆಯ ಸಾರಿ ಬಸುರಾಗಿ ಗಂಡುಮಗುವನ್ನು ಹೆರಲು ಅದಕ್ಕೆ ಅವನು ಓನಾನನೆಂದು ಹೆಸರಿಟ್ಟನು.",
			"5": "<sup>5</sup> ಅವಳು ಮತ್ತೊಂದು ಸಾರಿ ಗರ್ಭಿಣಿಯಾಗಿ ಗಂಡುಮಗುವನ್ನು ಹೆತ್ತು ಅದಕ್ಕೆ ಶೇಲಹನೆಂದು ಹೆಸರಿಟ್ಟಳು; ಅವಳು ಆ ಮಗುವನ್ನು ಹೆರುವಾಗ ಯೆಹೂದನು ಕಜೀಬೂರಿನಲ್ಲಿದ್ದನು.",
			"6": "<sup>6</sup> ಯೆಹೂದನು ತನ್ನ ಚೊಚ್ಚಲಮಗನಾದ ಏರನಿಗೆ ತಾಮಾರೆಂಬ ಹೆಣ್ಣನ್ನು ಮದುವೆ ಮಾಡಿದನು.",
			"7": "<sup>7</sup> ಆದರೆ ಯೆಹೂದನ ಚೊಚ್ಚಲಮಗನಾದ ಏರನು ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ಕೆಟ್ಟವನಾದ್ದರಿಂದ ಯೆಹೋವನು ಅವನನ್ನು ಸಾಯಿಸಿದನು.",
			"8": "<sup>8</sup> ಬಳಿಕ ಯೆಹೂದನು ಓನಾನನಿಗೆ - ನೀನು ನಿನ್ನ ಅತ್ತಿಗೆಯನ್ನು ಮದುವೆ ಮಾಡಿಕೊಂಡು ಮೈದುನಧರ್ಮಕ್ಕೆ ಸರಿಯಾಗಿ ನಡೆದು ನಿನ್ನ ಅಣ್ಣನಿಗೆ ಸಂತತಿಯನ್ನು ಹುಟ್ಟಿಸು ಅಂದನು.",
			"9": "<sup>9</sup> ಆದರೆ ಓನಾನನು ಈ ರೀತಿಯಾಗಿ ಆಗುವ ಸಂತಾನವು ತನ್ನದಾಗುವದಿಲ್ಲವೆಂದು ತಿಳಿದು ಅಣ್ಣನಿಗೆ ಸಂತತಿಯನ್ನು ಹುಟ್ಟಿಸಲೊಲ್ಲದೆ ತನ್ನ ಅತ್ತಿಗೆಯಲ್ಲಿ ಸಂಗಮಮಾಡುವಾಗೆಲ್ಲಾ ತನ್ನ ವೀರ್ಯವನ್ನು ನೆಲದ ಪಾಲು ಮಾಡುತ್ತಿದ್ದನು.",
			"10": "<sup>10</sup> ಈ ನಡತೆ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ಕೆಟ್ಟದ್ದಾಗಿದ್ದದರಿಂದ ಆತನು ಅವನನ್ನೂ ಸಾಯಿಸಿದನು.",
			"11": "<sup>11</sup> ಆಮೇಲೆ ಯೆಹೂದನು - ಒಂದು ವೇಳೆ ಶೇಲಹನೂ ತನ್ನ ಅಣ್ಣಂದಿರಂತೆಯೇ ಸತ್ತಾನೆಂದು ಯೋಚಿಸಿ ತನ್ನ ಸೊಸೆಯಾದ ತಾಮಾರಳಿಗೆ - ನನ್ನ ಮಗನಾದ ಶೇಲಹನು ಪ್ರಾಯಸ್ಥನಾಗುವ ತನಕ ನೀನು ವಿಧವೆಯಾಗಿ ತೌರಮನೆಯಲ್ಲಿರು ಎಂದು ನೆವ ಹೇಳಿದನು. ಅವಳು ತೌರಮನೆಗೆ ಹೋಗಿ ಅಲ್ಲೇ ವಾಸಮಾಡಿದಳು.",
			"12": "<sup>12</sup> ಬಹಳ ದಿನಗಳಾದ ಮೇಲೆ ಯೆಹೂದನ ಹೆಂಡತಿಯಾಗಿದ್ದ ಶೂಗನ ಮಗಳು ತೀರಿಹೋದಳು. ಯೆಹೂದನು ತನಗೆ ದುಃಖ ಶಮನವಾದ ಮೇಲೆ ತನ್ನ ಕುರಿಗಳ ಉಣ್ಣೇ ಕತ್ತರಿಸುವವರ ಬಳಿಗೆ ತನ್ನ ಸ್ನೇಹಿತನಾದ ಹೀರಾ ಎಂಬ ಅದುಲ್ಲಾಮ್ಯನ ಜೊತೆಯಲ್ಲಿ ಗಟ್ಟಾ ಹತ್ತಿ ತಿಮ್ನಾ ಊರಿಗೆ ಹೋದನು.",
			"13": "<sup>13</sup> ಮಾವನು ತನ್ನ ಕುರಿಗಳ ಉಣ್ಣೇ ಕತ್ತರಿಸುವದಕ್ಕೋಸ್ಕರ ತಿಮ್ನಾ ಊರಿಗೆ ಹೋಗುತ್ತಾನೆಂಬ ವರ್ತಮಾನವು ತಾಮಾರಳಿಗೆ ತಿಳಿದು ಬಂತು.",
			"14": "<sup>14</sup> ಆಗ ಆಕೆ - ಶೇಲಹನು ಪ್ರಾಯಸ್ಥನಾದಾಗ್ಯೂ ನನ್ನನ್ನು ಅವನಿಗೆ ಮದುವೆ ಮಾಡಿಸಲಿಲ್ಲವಲ್ಲಾ ಅಂದುಕೊಂಡು ತನ್ನ ವಿಧವಾವಸ್ತ್ರಗಳನ್ನು ತೆಗೆದಿಟ್ಟು ಮುಸುಕನ್ನು ಹಾಕಿಕೊಂಡು [ವೇಶ್ಯಾಸ್ತ್ರೀಯಂತೆ] ಅಲಂಕೃತಳಾಗಿ ತಿಮ್ನಾವೂರಿನ ದಾರಿಯಲ್ಲಿರುವ ಏನಯಿಮೂರಿನ ಬಾಗಿಲ ಬಳಿಯಲ್ಲಿ ಕೂತುಕೊಂಡಳು.",
			"15": "<sup>15</sup> ಯೆಹೂದನು ಆಕೆಯನ್ನು ಕಂಡಾಗ ಆಕೆಯ ಮುಖದ ಮೇಲೆ ಮುಸುಕು ಇದ್ದದರಿಂದ ಸೊಸೆ ಎಂದು ತಿಳಿಯದೆ ಸೂಳೆಯೆಂದು ಭಾವಿಸಿ ಮಾರ್ಗದಿಂದ ಓರೆಯಾಗಿ ಆಕೆಯ ಬಳಿಗೆ ಹೋಗಿ -",
			"16": "<sup>16</sup> ನಾನು ನಿನ್ನ ಸಂಗಮ ಮಾಡುವದಕ್ಕೆ ಒಪ್ಪುತ್ತೀಯಾ ಅನ್ನಲು ಆಕೆ - ನನ್ನಲ್ಲಿ ಬರಬೇಕಾದರೆ ನೀನು ಏನು ಕೊಡುತ್ತೀ ಎಂದು ಕೇಳಿದಳು.",
			"17": "<sup>17</sup> ಅವನು - ಹಿಂಡಿನಿಂದ ಒಂದು ಹೋತಮರಿಯನ್ನು ಕಳುಹಿಸಿಕೊಡುತ್ತೇನೆ ಅಂದಾಗ ಆಕೆ - ನೀನು ಅದನ್ನು ಕೊಡುವ ತನಕ ನನ್ನಲ್ಲಿ ಏನಾದರೂ ಒತ್ತೆಯಿಡಬೇಕು ಎಂದು ಹೇಳಿದ್ದಕ್ಕೆ",
			"18": "<sup>18</sup> ಅವನು - ಏನು ಒತ್ತೆ ಇಡಲಿ ಎಂದು ಕೇಳಲು ಆಕೆ - ನಿನ್ನ ಮುದ್ರೆ, ದಾರ, ಕೈಕೋಲು ಈ ಮೂರನ್ನು ಇಡು ಅಂದಳು. ಅವನು ಅವುಗಳನ್ನು ಕೊಟ್ಟು ಆಕೆಯ ಸಂಗಮ ಮಾಡಲು ಆಕೆ ಅವನಿಗೆ ಬಸುರಾದಳು.",
			"19": "<sup>19</sup> ಆಕೆ ಎದ್ದುಹೋದ ಮೇಲೆ ತಾನು ಹಾಕಿಕೊಂಡಿದ್ದ ಮುಸುಕನ್ನು ತೆಗೆದಿಟ್ಟು ತಿರಿಗಿ ವಿಧವಾವಸ್ತ್ರಗಳನ್ನು ಉಟ್ಟುಕೊಂಡಳು.",
			"20": "<sup>20</sup> ಯೆಹೂದನು ತಾನು ಇಟ್ಟಿದ್ದ ಒತ್ತೆಯನ್ನು ಆ ಹೆಂಗಸಿನಿಂದ ಬಿಡಿಸಿಕೊಳ್ಳುವದಕ್ಕೋಸ್ಕರ ತನ್ನ ಸ್ನೇಹಿತನಾದ ಆ ಅದುಲ್ಲಾಮ್ಯನ ಕೈಯಲ್ಲಿ ಹೋತಮರಿಯನ್ನು ಕಳುಹಿಸಲು ಆಕೆ ಅವನಿಗೆ ಸಿಕ್ಕಲಿಲ್ಲ.",
			"21": "<sup>21</sup> ಅವನು ಅವಳ ಊರಿನವರನ್ನು - ಏನಯಿಮಿನ ಸಮೀಪದಲ್ಲಿ ದಾರಿಯ ಬಳಿಯಲ್ಲಿ ಕೂತಿದ್ದ ದೇವದಾಸಿ ಎಲ್ಲಿರುವಳು ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ ಅವರು - ಇಲ್ಲಿ ಯಾವ ದೇವದಾಸಿಯೂ ಇಲ್ಲ ಅಂದರು.",
			"22": "<sup>22</sup> ಅವನು ಯೆಹೂದನ ಬಳಿಗೆ ಹಿಂದಿರುಗಿಬಂದು - ನಾನು ಅವಳನ್ನು ಕಾಣಲಿಲ್ಲ. ಮತ್ತು ವಿಚಾರಿಸಿದಾಗ ಆ ಊರಿನವರು ಅಲ್ಲಿ ಯಾವ ದೇವದಾಸಿಯೂ ಇಲ್ಲವೆಂದರು ಎಂದು ತಿಳಿಸಿದನು.",
			"23": "<sup>23</sup> ಅದಕ್ಕೆ ಯೆಹೂದನು - ಇನ್ನು ಹಾಸ್ಯಕ್ಕೆ ಗುರಿಯಾದೇವು; ನಾನಿಟ್ಟ ಒತ್ತೆಯನ್ನು ಅವಳೇ ಇಟ್ಟುಕೊಳ್ಳಲಿ; ನಾನಂತೂ ಹೋತಮರಿಯನ್ನು ಕಳುಹಿಸಿಕೊಟ್ಟೆನು; ಅವಳು ನಿನಗೆ ಸಿಕ್ಕಲಿಲ್ಲವಷ್ಟೆ ಅಂದನು.",
			"24": "<sup>24</sup> ಸುಮಾರು ಮೂರು ತಿಂಗಳಾದ ಮೇಲೆ ಯೆಹೂದನು ತನ್ನ ಸೊಸೆಯಾದ ತಾಮಾರಳು ವ್ಯಭಿಚಾರದಿಂದ ಬಸುರಾಗಿದ್ದಾಳೆ ಎಂಬ ವರ್ತಮಾನವನ್ನು ತಿಳಿದು - ಅವಳನ್ನು ಹೊರಗೆ ತನ್ನಿರಿ, ಸುಡಬೇಕಾಗಿದೆ ಎಂದು ಹೇಳಿದನು.",
			"25": "<sup>25</sup> ಅವರು ಆಕೆಯನ್ನು ಹೊರಕ್ಕೆ ತಂದಾಗ ಆಕೆ ತನ್ನ ಮಾವನಿಗೆ ಆ ಒತ್ತೆಯ ಸಾಮಾನುಗಳನ್ನು ಕಳುಹಿಸಿ - ಇವು ಯಾವನವೋ ಆ ಮನುಷ್ಯನಿಂದಲೇ ನಾನು ಬಸುರಾಗಿದ್ದೇನೆ; ಮುದ್ರೆ, ದಾರ, ಕೋಲು ಇವುಗಳ ಗುರುತನ್ನು ತಿಳಿಯಬಹುದು ಎಂದು ಹೇಳಿಸಿದಳು.",
			"26": "<sup>26</sup> ಯೆಹೂದನು ಅವುಗಳ ಗುರುತನ್ನು ತಿಳಿದು - ನಾನು ನನ್ನ ಮಗನಾದ ಶೇಲಹನನ್ನು ಆಕೆಗೆ ಮದುವೆಮಾಡಿಸಲಿಲ್ಲವಾದ್ದರಿಂದ ಆಕೆ ನನಗಿಂತಲೂ ನ್ಯಾಯವಾಗಿ ನಡೆದಳು ಎಂದು ಹೇಳಿದನು. ಅವನು ತಿರಿಗಿ ಆಕೆಯ ಸಹವಾಸ ಮಾಡಲಿಲ್ಲ.",
			"27": "<sup>27</sup> ತಾಮಾರಳಿಗೆ ಹೆರಿಗೆ ಕಾಲ ಬಂದಾಗ ಆಕೆಯ ಗರ್ಭದಲ್ಲಿ ಅವಳಿಮಕ್ಕಳಿರುವದು ಕಂಡುಬಂತು.",
			"28": "<sup>28</sup> ಆಕೆ ಹೆರುವಾಗ ಒಂದು ಮಗುವು ಕೈಚಾಚಲು ಸೂಲಗಿತ್ತಿಯು - ಇದು ಮೊದಲಾಗಿ ಬಂದದ್ದು ಎಂದು ಹೇಳಿ ಅದರ ಕೈಗೆ ಕೆಂಪು ನೂಲನ್ನು ಕಟ್ಟಿದಳು. ಅದು ಕೈಯನ್ನು ಹಿಂದಕ್ಕೆ ತೆಗೆಯಲು ಅದರೊಡನೆ ಇದ್ದ ಮತ್ತೊಂದು ಶಿಶುವು ಹೊರಗೆ ಬಂತು.",
			"29": "<sup>29</sup> ಸೂಲಗಿತ್ತಿಯು ಇದನ್ನು ಕಂಡು - ನೀನು ಛೇದಿಸಿಕೊಂಡು ಬಂದದ್ದೇನು ಎಂದು ಹೇಳಿದ್ದದರಿಂದ ಅದಕ್ಕೆ ಪೆರೆಚ್ ಎಂದು ಹೆಸರಾಯಿತು.",
			"30": "<sup>30</sup> ತರುವಾಯ ಕೈಗೆ ಕೆಂಪು ನೂಲು ಕಟ್ಟಿಸಿಕೊಂಡ ಶಿಶುವು ಹುಟ್ಟಿತು; ಅದಕ್ಕೆ ಜೆರಹ ಎಂದು ಹೆಸರಾಯಿತು."
		},
		"39": {
			"1": "<sup>1</sup> ಯೋಸೇಫನನ್ನು ತೆಗೆದುಕೊಂಡುಹೋದ ಇಷ್ಮಾಯೇಲ್ಯರು ಐಗುಪ್ತದೇಶಕ್ಕೆ ಸೇರಿದಾಗ ಒಬ್ಬ ಐಗುಪ್ತದವನು ಅವನನ್ನು ಅವರಿಂದ ಕ್ರಯಕ್ಕೆ ತೆಗೆದುಕೊಂಡನು; ಇವನು ಯಾರಂದರೆ ಫರೋಹನ ಉದ್ಯೋಗಸ್ಥನೂ ಮೈಗಾವಲಿನವರ ದಳವಾಯಿಯೂ ಆಗಿದ್ದ ಪೋಟೀಫರನು.",
			"2": "<sup>2</sup> ಯೆಹೋವನು ಯೋಸೇಫನ ಸಂಗಡ ಇದ್ದದರಿಂದ ಅವನು ಏಳಿಗೆಯಾಗಿ ಐಗುಪ್ತ್ಯನಾದ ತನ್ನ ದಣಿಯ ಮನೆಯೊಳಗೆ ಸೇವಕನಾದನು.",
			"3": "<sup>3</sup> ಯೆಹೋವನು ಯೋಸೇಫನ ಸಂಗಡವಿದ್ದು ಅವನು ಮಾಡುವ ಕೆಲಸವನ್ನೆಲ್ಲಾ ಸಫಲವಾಗುವಂತೆ ಮಾಡುತ್ತಾನೆಂದು ಅವನ ದಣಿಯು ತಿಳಿದು",
			"4": "<sup>4</sup> ಅವನ ಮೇಲೆ ದಯೆ ಇಟ್ಟು ಅವನನ್ನು ಸ್ವಂತ ಸೇವಕನನ್ನಾಗಿ ನೇಮಿಸಿಕೊಂಡನು. ಇದಲ್ಲದೆ ಆ ದಣಿಯು ಅವನಿಗೆ ತನ್ನ ಮನೆಯಲ್ಲಿ ಪಾರುಪತ್ಯವನ್ನು ಕೊಟ್ಟು ತನ್ನ ಆಸ್ತಿಯನ್ನೆಲ್ಲಾ ಅವನ ವಶಕ್ಕೆ ಒಪ್ಪಿಸಿದನು.",
			"5": "<sup>5</sup> ಅವನು ಯೋಸೇಫನನ್ನು ತನ್ನ ಮನೆಯ ಮೇಲೆಯೂ ಆಸ್ತಿಯ ಮೇಲೆಯೂ ಅಧ್ಯಕ್ಷನಾಗಿ ಇಟ್ಟಂದಿನಿಂದ ಯೆಹೋವನು ಯೋಸೇಫನ ನಿಮಿತ್ತವಾಗಿ ಆ ಐಗುಪ್ತ್ಯನ ಮನೆಯನ್ನು ಅಭಿವೃದ್ಧಿಗೆ ತಂದನು. ಮನೆಯಲ್ಲಾಗಲಿ ಹೊಲದಲ್ಲಾಗಲಿ ಅವನಿಗಿದ್ದ ಎಲ್ಲಾದರ ಮೇಲೆ ಯೆಹೋವನ ಆಶೀರ್ವಾದವುಂಟಾಯಿತು.",
			"6": "<sup>6</sup> ಅವನು ತನ್ನ ಆಸ್ತಿಯನ್ನೆಲ್ಲಾ ಯೋಸೇಫನ ವಶಕ್ಕೆ ಒಪ್ಪಿಸಿದನು; ಇವನು ತನ್ನ ಬಳಿಯಲ್ಲೇ ಇದ್ದದರಿಂದ ತಾನು ತಿನ್ನುವ ಆಹಾರ ಒಂದನ್ನೇ ಹೊರತು ಬೇರೆ ಯಾವದನ್ನೂ ಚಿಂತಿಸಲಿಲ್ಲ. ಇದಲ್ಲದೆ ಯೋಸೇಫನು ರೂಪದಲ್ಲಿಯೂ ಮುಖಭಾವದಲ್ಲಿಯೂ ಸುಂದರನಾಗಿದ್ದನು.",
			"7": "<sup>7</sup> ಹೀಗಿರುವಲ್ಲಿ ಅವನ ದಣಿಯ ಹೆಂಡತಿಯು ಅವನ ಮೇಲೆ ಕಣ್ಣು ಹಾಕಿ ತನ್ನೊಡನೆ ಸಂಗಮಕ್ಕೆ ಕರೆದಳು.",
			"8": "<sup>8</sup> ಆದರೆ ಅವನು ಒಪ್ಪದೆ - ನನ್ನ ದಣಿಯು ತನ್ನ ಆಸ್ತಿಯನ್ನೆಲ್ಲಾ ನನ್ನ ವಶಕ್ಕೆ ಒಪ್ಪಿಸಿ ನಾನು ಇರುವದರಿಂದ ಮನೆಯೊಳಗೆ ನಡೆಯುವ ಯಾವ ಕೆಲಸವನ್ನೂ ಚಿಂತಿಸದೆ ಇದ್ದಾನೆ;",
			"9": "<sup>9</sup> ಈ ಮನೆಯಲ್ಲಿ ಅವನೂ ನನಗಿಂತ ದೊಡ್ಡವನಲ್ಲ; ನೀನು ಅವನ ಧರ್ಮಪತ್ನಿಯಾದದರಿಂದ ನಿನ್ನನ್ನು ಮಾತ್ರ ನನಗೆ ಅಧೀನಮಾಡಲಿಲ್ಲ; ಹೀಗಿರುವಲ್ಲಿ ನಾನು ಇಂಥಾ ಮಹಾದುಷ್ಕೃತ್ಯವನ್ನು ನಡಿಸಿ ದೇವರಿಗೆ ವಿರುದ್ಧವಾಗಿ ಹೇಗೆ ಪಾಪಮಾಡಲಿ ಎಂದು ತನ್ನ ದಣಿಯ ಹೆಂಡತಿಗೆ ಉತ್ತರಕೊಟ್ಟನು.",
			"10": "<sup>10</sup> ಆಕೆ ಯೋಸೇಫನ ಸಂಗಡ ಪ್ರತಿ ದಿನವೂ ಈ ಮಾತನ್ನು ಆಡಿದಾಗ್ಯೂ ಅವನು ಅದಕ್ಕೆ ಕಿವಿಗೊಡದೆ ಆಕೆಯಲ್ಲಿ ಸಂಗಮಮಾಡುವದಕ್ಕಾಗಲಿ ಆಕೆಯ ಬಳಿಯಲ್ಲಿರುವದಕ್ಕಾಗಲಿ ಒಪ್ಪಿಕೊಳ್ಳಲೇ ಇಲ್ಲ.",
			"11": "<sup>11</sup> ಹೀಗಿರಲು ಒಂದು ದಿನ ಅವನು ತನ್ನ ಕೆಲಸದ ಮೇಲೆ ಮನೆಗೆ ಬಂದಾಗ ಮನೆಯವರಲ್ಲಿ ಯಾರೂ ಒಳಗೆ ಇಲ್ಲದಿರುವಲ್ಲಿ",
			"12": "<sup>12</sup> ಆಕೆ ಅವನ ಬಟ್ಟೆಯನ್ನು ಹಿಡಿದುಕೊಂಡು - ನನ್ನ ಸಂಗಮಕ್ಕೆ ಬಾ ಅನ್ನಲು ಅವನು ತನ್ನ ಬಟ್ಟೆಯನ್ನು ಆಕೆಯ ಕೈಯಲ್ಲೇ ಬಿಟ್ಟು ತಪ್ಪಿಸಿಕೊಂಡು ಓಡಿಹೋದನು.",
			"13": "<sup>13</sup> ಅವನು ತನ್ನ ಬಟ್ಟೆಯನ್ನು ನನ್ನ ಕೈಯಲ್ಲಿ ಬಿಟ್ಟು ತಪ್ಪಿಸಿಕೊಂಡು ಹೋದನಲ್ಲಾ ಎಂದು ಆಕೆ ನೋಡಿ ಮನೆಯ ಸೇವಕರನ್ನು",
			"14": "<sup>14</sup> ಕರೆದು ಅವರಿಗೆ - ನೋಡಿರಿ, ನನ್ನ ಯಜಮಾನರು ಒಬ್ಬ ಇಬ್ರಿಯನನ್ನು ನಮ್ಮೊಳಗೆ ಸೇರಿಸಿ ನಮ್ಮನ್ನು ಅವಮಾನಕ್ಕೆ ಗುರಿಮಾಡಿದ್ದಾರೆ. ಅವನು ನನ್ನನ್ನು ಮಾನಭಂಗಪಡಿಸುವದಕ್ಕೆ ನನ್ನ ಹತ್ತಿರ ಬಂದನು. ನಾನು ಗಟ್ಟಿಯಾಗಿ ಕೂಗಿಕೊಂಡೆನು.",
			"15": "<sup>15</sup> ನಾನು ಕೂಗುವದನ್ನು ಕೇಳಿ ಅವನು ತನ್ನ ಬಟ್ಟೆಯನ್ನು ನನ್ನ ಬಳಿಯಲ್ಲೇ ಬಿಟ್ಟು ತಪ್ಪಿಸಿಕೊಂಡು ಓಡಿಹೋದನು ಎಂದು ಹೇಳಿ",
			"16": "<sup>16</sup> ಅವನ ದಣಿಯು ಮನೆಗೆ ಬರುವ ತನಕ ಆ ಬಟ್ಟೆಯನ್ನು ತನ್ನ ಬಳಿಯಲ್ಲಿ ಇಟ್ಟುಕೊಂಡಳು.",
			"17": "<sup>17</sup> ಪತಿಯು ಬಂದಾಗ ಆಕೆ ಅದೇ ಮಾತನ್ನು ಹೇಳಿ ಅವನಿಗೆ - ನೀನು ನಮ್ಮಲ್ಲಿ ಸೇರಿಸಿಕೊಂಡ ಆ ಇಬ್ರಿಯ ಸೇವಕನು ನನ್ನನ್ನು ಮಾನಭಂಗಪಡಿಸುವದಕ್ಕೆ ನನ್ನ ಬಳಿಗೆ ಬಂದನು. ನಾನು ಕೂಗಿಕೊಂಡೆನು.",
			"18": "<sup>18</sup> ಕೂಗುತ್ತಲೇ ಅವನು ತನ್ನ ಬಟ್ಟೆಯನ್ನು ಬಿಟ್ಟು ತಪ್ಪಿಸಿಕೊಂಡು ಓಡಿಹೋದನು ಎಂದು ಹೇಳಿದಳು.",
			"19": "<sup>19</sup> ನಿನ್ನ ಸೇವಕನು ಈ ಮೇರೆಗೆ ನನಗೆ ಮಾಡಿದನೆಂಬದಾಗಿ ಆಕೆ ಹೇಳಿದ ಮಾತುಗಳನ್ನು ಯೋಸೇಫನ ದಣಿಯು ಕೇಳಿ ಬಹಳ ಸಿಟ್ಟುಗೊಂಡು",
			"20": "<sup>20</sup> ಅವನನ್ನು ಹಿಡಿಸಿ ಅರಸನು ಸೆರೆಯವರನ್ನು ಕಟ್ಟಿಹಾಕಿಸುತ್ತಿದ್ದ ಸೆರೆಮನೆಯಲ್ಲಿ ಹಾಕಿಸಿದನು. ಅಲ್ಲಿ ಯೋಸೇಫನು ಸೆರೆಯಲ್ಲಿ ಇರಬೇಕಾಯಿತು.",
			"21": "<sup>21</sup> ಆದರೂ ಯೆಹೋವನು ಯೋಸೇಫನ ಸಂಗಡ ಇದ್ದು ಅವನ ಮೇಲೆ ಕೃಪೆಯನ್ನಿಟ್ಟು ಅವನಿಗೆ ಸೆರೆಯಜಮಾನನ ಬಳಿಯಲ್ಲಿ ದಯೆದೊರಕುವಂತೆ ಮಾಡಿದನು.",
			"22": "<sup>22</sup> ಸೆರೆಮನೆಯ ಯಜಮಾನನು ಸೆರೆಯಲ್ಲಿದ್ದವರೆಲ್ಲರನ್ನೂ ಯೋಸೇಫನ ವಶಕ್ಕೆ ಕೊಟ್ಟುಬಿಟ್ಟನು. ಅಲ್ಲಿ ಮಾಡಬೇಕಾದದ್ದನ್ನೆಲ್ಲಾ ಯೋಸೇಫನೇ ಮಾಡಿಸಿದನು.",
			"23": "<sup>23</sup> ಯೆಹೋವನು ಅವನ ಸಂಗಡ ಇದ್ದು ಅವನು ನಡಿಸಿದ್ದೆಲ್ಲವನ್ನು ಕೈಗೂಡಿಸಿದ್ದರಿಂದ ಅವನ ವಶದಲ್ಲಿದ್ದ ಯಾವದೊಂದರ ವಿಷಯದಲ್ಲಿಯೂ ಸೆರೆಮನೆಯ ಯಜಮಾನನು ಏನೂ ಚಿಂತಿಸಲಿಲ್ಲ."
		},
		"40": {
			"1": "<sup>1</sup> ಈ ಸಂಗತಿಗಳಾದ ಮೇಲೆ ಐಗುಪ್ತ ದೇಶದ ಅರಸನಾದ ಫರೋಹನಿಗೆ ಪಾನಕ ಕೊಡುವವನೂ ಭಕ್ಷ್ಯಗಳನ್ನು ಮಾಡಿಕೊಡುವವನೂ",
			"2": "<sup>2</sup> ತಮ್ಮ ದಣಿಗೆ ವಿರೋಧವಾಗಿ ಯಾವದೋ ತಪ್ಪುಮಾಡಿದ್ದರಿಂದ ಫರೋಹನು ಆ ಇಬ್ಬರು ಉದ್ಯೋಗಸ್ಥರ ಮೇಲೆ, ಅಂದರೆ ಪಾನದಾಯಕರಲ್ಲಿ ಮುಖ್ಯಸ್ಥನ ಮೇಲೆಯೂ ಭಕ್ಷ್ಯಕಾರರಲ್ಲಿ ಮುಖ್ಯಸ್ಥನ ಮೇಲೆಯೂ ಕೋಪಮಾಡಿ",
			"3": "<sup>3</sup> ಅವರನ್ನು ಮೈಗಾವಲಿನವರ ದಳವಾಯಿಯ ಮನೆಯೊಳಗೆ ಕಾವಲಲ್ಲಿರಿಸಿದನು.",
			"4": "<sup>4</sup> ಅದು ಯೋಸೇಫನು ಬಿದ್ದಿದ್ದ ಸೆರೆಮನೆಯೇ. ದಳವಾಯಿಯು ಯೋಸೇಫನನ್ನು ಅವರ ಉಪಚಾರಕ್ಕೆ ನೇಮಿಸಿದ್ದರಿಂದ ಯೋಸೇಫನು ಅವರಿಗೆ ಸೇವೆ ಮಾಡುವವನಾದನು.",
			"5": "<sup>5</sup> ಹೀಗೆ ಅವರು ಕೆಲವು ಕಾಲ ಕಾವಲಲ್ಲಿದ್ದ ಮೇಲೆ ಅವರಿಬ್ಬರಿಗೂ, ಅಂದರೆ ಐಗುಪ್ತದೇಶದ ಅರಸನು ಸೆರೆಯಲ್ಲಿ ಹಾಕಿಸಿದ್ದ ಪಾನದಾಯಕನಿಗೂ ಭಕ್ಷ್ಯಕಾರನಿಗೂ, ಒಂದೇ ರಾತ್ರಿ ಕನಸುಬಿತ್ತು; ಅವರವರ ಕನಸಿಗೆ ಬೇರೆ ಬೇರೆ ಅರ್ಥವಿತ್ತು.",
			"6": "<sup>6</sup> ಬೆಳಿಗ್ಗೆ ಯೋಸೇಫನು ಅವರ ಬಳಿಗೆ ಬಂದಾಗ ಅವರು ಚಿಂತೆಯುಳ್ಳವರಾಗಿ ಕಾಣಿಸಿದರು.",
			"7": "<sup>7</sup> ಅದನ್ನು ಅವನು ನೋಡಿ - ನಿಮ್ಮ ಮುಖವು ಈ ಹೊತ್ತು ಯಾಕೆ ಕಳೆಗುಂದಿತು ಎಂದು ತನ್ನ ದಣಿಯ ಮನೆಯೊಳಗೆ ತನ್ನೊಂದಿಗೆ ಕಾವಲಲ್ಲಿದ್ದ ಫರೋಹನ ಉದ್ಯೋಗಸ್ಥರನ್ನು ಕೇಳಿದನು.",
			"8": "<sup>8</sup> ಅವರು ಅವನಿಗೆ - ನಮಗೆ ಸ್ವಪ್ನಬಿತ್ತು; ಅದರ ಅರ್ಥವನ್ನು ಹೇಳುವವರು ಯಾರೂ ಇಲ್ಲ ಎಂದು ಹೇಳಲು ಯೋಸೇಫನು ಅವರಿಗೆ - ಸ್ವಪ್ನಗಳ ಅರ್ಥವು ದೇವರಿಂದ ದೊರಕಬಹುದಲ್ಲವೇ; ದಯಮಾಡಿ ನಿಮ್ಮ ಸ್ವಪ್ನವನ್ನು ನನಗೆ ತಿಳಿಸಿರಿ ಎಂದು ಹೇಳಿದನು.",
			"9": "<sup>9</sup> ಆಗ ಪಾನದಾಯಕರಲ್ಲಿ ಮುಖ್ಯಸ್ಥನು ಯೋಸೇಫನಿಗೆ - ನನ್ನ ಕನಸಿನಲ್ಲಿ ಒಂದು ದ್ರಾಕ್ಷಾಲತೆಯನ್ನು ನನ್ನೆದುರಾಗಿ ಕಂಡೆನು;",
			"10": "<sup>10</sup> ಅದಕ್ಕೆ ಮೂರು ಕವಲುಗಳಿದ್ದವು. ಅದು ಚಿಗುರುತ್ತಲೇ ಹೂವುಗಳನ್ನು ಬಿಟ್ಟಿತು; ಆ ಹೂವುಗಳು ಗೊಂಚಲುಗಳಾಗಿ ಹಣ್ಣಾದವು.",
			"11": "<sup>11</sup> ಫರೋಹನ ಪಾನಪಾತ್ರೆಯು ನನ್ನ ಕೈಯಲ್ಲಿದ್ದದರಿಂದ ನಾನು ಆ ಗೊಂಚಲುಗಳನ್ನು ಕೊಯಿದು ಪಾತ್ರೆಯಲ್ಲಿ ಹಿಂಡಿ ಪಾತ್ರೆಯನ್ನು ಫರೋಹನ ಕೈಗೆ ಒಪ್ಪಿಸಿದೆನು ಎಂದು ಹೇಳಿದನು.",
			"12": "<sup>12</sup> ಅದಕ್ಕೆ ಯೋಸೇಫನು - ಆ ಕನಸಿನ ಅರ್ಥವೇನಂದರೆ -",
			"13": "<sup>13</sup> ಆ ಮೂರು ಕೊಂಬೆಗಳೇ ಮೂರು ದಿನಗಳು; ಈ ಹೊತ್ತಿಗೆ ಮೂರು ದಿನಗಳೊಳಗೆ ಫರೋಹನು ನಿನ್ನನ್ನು ಮೇಲಕ್ಕೆ ಎತ್ತಿ ನಿನ್ನ ಉದ್ಯೋಗಕ್ಕೆ ತಿರಿಗಿ ನೇಮಿಸುವನು. ನೀನು ಮುಂಚೆ ಫರೋಹನ ಪಾನದಾಯಕನಾಗಿದ್ದು ಅವನ ಕೈಗೆ ಪಾನಪಾತ್ರೆಯನ್ನು ಒಪ್ಪಿಸುತ್ತಿದ್ದಂತೆಯೇ ಮುಂದೆಯೂ ಒಪ್ಪಿಸುವಿ.",
			"14": "<sup>14</sup> ಆದರೆ ನೀನು ಸುಖದಿಂದಿರುವಾಗ ನನ್ನನ್ನು ಜ್ಞಾಪಕಮಾಡಿಕೊಂಡು ನನಗೆ ಉಪಕಾರ ಮಾಡಿ ಫರೋಹನಿಗೆ ನನ್ನ ಸಂಗತಿಯನ್ನು ತಿಳಿಸಿ ನನ್ನನ್ನು ಈ ಸೆರೆಯಿಂದ ಬಿಡಿಸಬೇಕು.",
			"15": "<sup>15</sup> ನಾನು ಇಬ್ರಿಯದೇಶಸ್ಥನು; ಕೆಲವರು ನನ್ನನ್ನು ಕದ್ದು ಈ ದೇಶಕ್ಕೆ ತಂದರು. ಇಲ್ಲಿಯೂ ನಾನೇನೂ ತಪ್ಪಿಲ್ಲದವನಾಗಿ ಸೆರೆಯಲ್ಲಿ ಬಿದ್ದೆನು ಅಂದನು.",
			"16": "<sup>16</sup> ಅವನು ಹೇಳಿದ ಅರ್ಥವು ಶುಭಕರವಾದದ್ದೆಂದು ಭಕ್ಷ್ಯಕಾರರಲ್ಲಿ ಮುಖ್ಯಸ್ಥನು ತಿಳಿದು ಯೋಸೇಫನಿಗೆ - ನಾನೂ ಕಂಡ ಕನಸನ್ನು ಹೇಳುತ್ತೇನೆ ಕೇಳು. ನನಗೆ ಬಿದ್ದ ಕನಸಿನಲ್ಲಿ ಸೊಗಸಾದ ಭಕ್ಷ್ಯಗಳು ತುಂಬಿದ್ದ ಮೂರು ಪುಟ್ಟಿಗಳು ನನ್ನ ತಲೆಯ ಮೇಲಿದ್ದವು.",
			"17": "<sup>17</sup> ಮೇಲಣ ಪುಟ್ಟಿಯಲ್ಲಿ ಫರೋಹನಿಗೋಸ್ಕರ ನಾನಾ ವಿಧವಾದ ಭಕ್ಷ್ಯಗಳು ಇದ್ದವು; ಪಕ್ಷಿಗಳು ಬಂದು ಅವುಗಳನ್ನು ನನ್ನ ತಲೆಯ ಮೇಲಿದ್ದ ಪುಟ್ಟಿಯೊಳಗಿಂದಲೇ ತಿನ್ನುತ್ತಿದ್ದವು ಎಂದು ಹೇಳಿದನು.",
			"18": "<sup>18</sup> ಅದಕ್ಕೆ ಯೋಸೇಫನು - ನಿನ್ನ ಕನಸಿನ ಅರ್ಥವೇನಂದರೆ - ಆ ಮೂರು ಪುಟ್ಟಿಗಳೇ ಮೂರು ದಿನಗಳು;",
			"19": "<sup>19</sup> ಈ ಹೊತ್ತಿಗೆ ಮೂರು ದಿನಗಳೊಳಗೆ ಫರೋಹನು ನಿನ್ನ ತಲೆಯನ್ನು ಎತ್ತಿಸುವನು; ನಿನ್ನನ್ನು ಮರಕ್ಕೆ ತೂಗಹಾಕಿಸುವನು; ಹಕ್ಕಿಗಳು ಬಂದು ನಿನ್ನ ಮಾಂಸವನ್ನು ತಿಂದುಬಿಡುವವು ಅಂದನು.",
			"20": "<sup>20</sup> ಮೂರನೆಯ ದಿನದಲ್ಲಿ ಫರೋಹನ ಜನ್ಮದಿನವಾದ್ದರಿಂದ ಅವನು ತನ್ನ ಸೇವಕರೆಲ್ಲರಿಗೆ ಔತಣವನ್ನು ಮಾಡಿಸಿ ಮುಖ್ಯ ಪಾನದಾಯಕನನ್ನೂ ಮುಖ್ಯಭಕ್ಷ್ಯಕಾರನನ್ನೂ ಬಿಡಿಸಿ ಸೇವಕರ ಮಧ್ಯದಲ್ಲಿ ನಿಲ್ಲಿಸಿದನು.",
			"21": "<sup>21</sup> ಪಾನದಾಯಕರ ಮುಖ್ಯಸ್ಥನನ್ನು ತಿರಿಗಿ ಅವನ ಉದ್ಯೋಗಕ್ಕೆ ನೇಮಿಸಿದನು; ಅವನು ಪಾನಪಾತ್ರೆಯನ್ನು ಫರೋಹನ ಕೈಗೆ ಕೊಡುವವನಾದನು;",
			"22": "<sup>22</sup> ಆದರೆ ಮುಖ್ಯ ಭಕ್ಷ್ಯಕಾರನನ್ನು ಫರೋಹನು ಗಲ್ಲಿಗೆ ಹಾಕಿಸಿದನು. ಹೀಗೆ ಯೋಸೇಫನು ಹೇಳಿದ್ದಂತೆಯೇ ಆಯಿತು.",
			"23": "<sup>23</sup> ಆದಾಗ್ಯೂ ಮುಖ್ಯಪಾನದಾಯಕನು ಯೋಸೇಫನನ್ನು ನೆನಪುಮಾಡದೆ ಮರೆತುಬಿಟ್ಟನು."
		},
		"41": {
			"1": "<sup>1</sup> ಎರಡು ವರುಷಗಳಾದ ಮೇಲೆ ಫರೋಹನಿಗೆ ಕನಸುಬಿತ್ತು. ಆ ಕನಸು ಏನಂದರೆ - ಅವನು ನೈಲ್‍ನದೀತೀರದಲ್ಲಿ ನಿಂತಿದ್ದನು.",
			"2": "<sup>2</sup> ಅಷ್ಟರಲ್ಲಿ ಲಕ್ಷಣವಾದ ಏಳು ಕೊಬ್ಬಿದ ಆಕಳುಗಳು ಆ ನದಿಯೊಳಗಿಂದ ಬಂದು ಆಪುಹುಲ್ಲಿನಲ್ಲಿ ಮೇಯುತ್ತಿದ್ದವು.",
			"3": "<sup>3</sup> ಅವುಗಳ ಹಿಂದೆ ಮೈಯೊಣಗಿ ಬಡವಾದ ಏಳು ಆಕಳುಗಳು ನದಿಯೊಳಗಣಿಂದ ಬಂದು ಮೊದಲು ಕಾಣಿಸಿದ ಆಕಳುಗಳ ಹತ್ತರ ನದೀತೀರದಲ್ಲಿ ನಿಂತವು.",
			"4": "<sup>4</sup> ಮೈಯೊಣಗಿದ ಈ ಬಡ ಆಕಳುಗಳು ಲಕ್ಷಣವಾದ ಆ ಕೊಬ್ಬಿದ ಆಕಳುಗಳನ್ನು ತಿಂದುಬಿಟ್ಟವು. ಹೀಗೆ ಕಾಣಿಸುವಷ್ಟರಲ್ಲಿ ಫರೋಹನಿಗೆ ಎಚ್ಚರವಾಯಿತು.",
			"5": "<sup>5</sup> ಅವನು ತಿರಿಗಿ ನಿದ್ರೆಮಾಡಿದಾಗ ಮತ್ತೊಂದು ಕನಸುಬಿತ್ತು. ಅದೇನಂದರೆ - ಒಳ್ಳೇ ಪುಷ್ಟಿಯುಳ್ಳ ಏಳು ತೆನೆಗಳು ಒಂದೇ ದಂಟಿನಲ್ಲಿ ಹುಟ್ಟಿದವು.",
			"6": "<sup>6</sup> ಅವುಗಳ ಹಿಂದೆ ಮೂಡಣ ಗಾಳಿಯಿಂದ ಕೆಟ್ಟು ಬತ್ತಿಹೋಗಿದ್ದ ಬೇರೆ ಏಳು ತೆನೆಗಳು ಮೊಳೆತು ಬಂದವು;",
			"7": "<sup>7</sup> ಬತ್ತಿಹೋಗಿದ್ದ ಈ ತೆನೆಗಳು ಆ ಏಳು ಒಳ್ಳೇ ಪುಷ್ಟಿಯುಳ್ಳ ತೆನೆಗಳನ್ನು ನುಂಗಿಬಿಟ್ಟವು. ಹೀಗೆ ಕಾಣಿಸುವಷ್ಟರಲ್ಲಿ ಫರೋಹನು ಎಚ್ಚತ್ತು ಅದನ್ನು ಕನಸೆಂದು ತಿಳುಕೊಂಡನು.",
			"8": "<sup>8</sup> ಬೆಳಿಗ್ಗೆ ಅವನು ಮನದಲ್ಲಿ ಕಳವಳಗೊಂಡು ಐಗುಪ್ತದೇಶದಲ್ಲಿದ್ದ ಎಲ್ಲಾ ಜೋಯಿಸರನ್ನೂ ವಿದ್ವಾಂಸರನ್ನೂ ಕರಸಿ ಅವರಿಗೆ ತನ್ನ ಕನಸನ್ನು ತಿಳಿಸಲಾಗಿ ಅದರ ಅರ್ಥವನ್ನು ಅವನಿಗೆ ಹೇಳಬಲ್ಲವರು ಅವರಲ್ಲಿ ಒಬ್ಬರೂ ಸಿಕ್ಕಲಿಲ್ಲ.",
			"9": "<sup>9</sup> ಹೀಗಿರುವಾಗ ಪಾನದಾಯಕರಲ್ಲಿ ಮುಖ್ಯಸ್ಥನು ಫರೋಹನಿಗೆ - [ಅರಸನೇ,] ಈ ಹೊತ್ತು ನನ್ನ ತಪ್ಪುಗಳನ್ನು ನೆನಸಿ ಸನ್ನಿಧಿಯಲ್ಲಿ ಅರಿಕೆ ಮಾಡುತ್ತೇನೆ.",
			"10": "<sup>10</sup> ತಾವು ತಮ್ಮ ಸೇವಕರ ಮೇಲೆ ಸಿಟ್ಟುಗೊಂಡು ನನ್ನನ್ನೂ ಭಕ್ಷ್ಯಕಾರರಲ್ಲಿ ಮುಖ್ಯಸ್ಥನನ್ನೂ ದಳವಾಯಿಯ ಮನೆಯೊಳಗೆ ಕಾವಲಲ್ಲಿರಿಸಿದಾಗ ನಮ್ಮಿಬ್ಬರಿಗೂ ಒಂದೇ ರಾತ್ರಿಯಲ್ಲಿ ಕನಸುಬಿತ್ತು;",
			"11": "<sup>11</sup> ಒಬ್ಬೊಬ್ಬನ ಕನಸಿಗೆ ಬೇರೆ ಬೇರೆ ಅರ್ಥವಿತ್ತು.",
			"12": "<sup>12</sup> ಅಲ್ಲಿ ನಮ್ಮ ಸಂಗಡ ಇಬ್ರಿಯನಾದ ಒಬ್ಬ ಪ್ರಾಯಸ್ಥನಿದ್ದನು; ಅವನು ದಳವಾಯಿಯ ದಾಸನು. ನಾವು ಅವನಿಗೆ ನಮ್ಮ ಕನಸುಗಳನ್ನು ತಿಳಿಸಿದಾಗ ಅವನು ಅವುಗಳ ಅರ್ಥವನ್ನು ನಮಗೆ ಹೇಳಿದನು; ನಮ್ಮ ನಮ್ಮ ಕನಸಿನ ಪ್ರಕಾರ ಅರ್ಥವನ್ನು ಹೇಳಿದನು.",
			"13": "<sup>13</sup> ಮಾತ್ರವಲ್ಲದೆ ಅವನು ನಮಗೆ ಹೇಳಿದ ಅರ್ಥಕ್ಕೆ ಸರಿಯಾಗಿ ಕಾರ್ಯವು ನಡೆಯಿತು; ಅವನು ಹೇಳಿದಂತೆಯೇ ನನ್ನ ಉದ್ಯೋಗವು ತಿರಿಗಿ ನನಗೆ ದೊರಕಿತು; ಭಕ್ಷ್ಯಕಾರರಲ್ಲಿ ಮುಖ್ಯಸ್ಥನೋ ಗಲ್ಲಿಗೆ ಹಾಕಲ್ಪಟ್ಟನು ಅಂದನು.",
			"14": "<sup>14</sup> ಫರೋಹನು ಇದನ್ನು ಕೇಳಿ ಯೋಸೇಫನನ್ನು ಕರೇ ಕಳುಹಿಸಿದನು. ಯೋಸೇಫನು ಬೇಗನೆ ಸೆರೆಮನೆಯಿಂದ ಬಿಡಿಸಲ್ಪಟ್ಟವನಾಗಿ ಕ್ಷೌರಮಾಡಿಸಿಕೊಂಡು ಬೇರೆ ವಸ್ತ್ರಗಳನ್ನು ಹಾಕಿಕೊಂಡು ಫರೋಹನ ಸನ್ನಿಧಿಗೆ ಬಂದನು.",
			"15": "<sup>15</sup> ಆಗ ಫರೋಹನು ಯೋಸೇಫನಿಗೆ - ನಾನು ಒಂದು ಕನಸು ಕಂಡಿದ್ದೇನೆ; ಅದರ ಅರ್ಥವನ್ನು ಹೇಳಬಲ್ಲವರು ಯಾರೂ ಇಲ್ಲ. ಆದರೆ ನೀನು ಕನಸನ್ನು ಕೇಳುತ್ತಲೇ ಅದರ ಅರ್ಥವನ್ನು ಹೇಳಬಲ್ಲವನೆಂದು ವರ್ತಮಾನ ಕೇಳಿದ್ದೇನೆ ಎಂದು ಹೇಳಲು",
			"16": "<sup>16</sup> ಯೋಸೇಫನು - ನನ್ನಲ್ಲಿ ಅಂಥ ಸಾಮರ್ಥ್ಯವೇನೂ ಇಲ್ಲ; ಆದರೆ ದೇವರು ಫರೋಹನಿಗೆ ಶುಭಕರವಾದ ಉತ್ತರವನ್ನು ಕೊಡಬಲ್ಲನೆಂದು ಹೇಳಿದನು.",
			"17": "<sup>17</sup> ಆಗ ಫರೋಹನು ಯೋಸೇಫನಿಗೆ - ನನ್ನ ಕನಸಿನೊಳಗೆ ನಾನು ನೈಲ್‍ನದೀತೀರದಲ್ಲಿ ನಿಂತಿದ್ದೆನು.",
			"18": "<sup>18</sup> ನದಿಯೊಳಗಿಂದ ಲಕ್ಷಣವಾದ ಏಳು ಕೊಬ್ಬಿದ ಆಕಳುಗಳು ಬಂದು ಆಪುಹುಲ್ಲಿನಲ್ಲಿ ಮೇಯುತ್ತಿದ್ದವು.",
			"19": "<sup>19</sup> ಅವುಗಳ ಹಿಂದೆ ಅವಲಕ್ಷಣವಾದ ಕೊಬ್ಬಿಲ್ಲದ ಬಡವಾದ ಬೇರೆ ಏಳು ಆಕಳುಗಳು ಬಂದವು; ಅಂಥ ಬಡವಾದ ಆಕಳುಗಳನ್ನು ನಾನು ಐಗುಪ್ತದೇಶದಲ್ಲಿ ಎಲ್ಲಿಯೂ ನೋಡಿದ್ದಿಲ್ಲ.",
			"20": "<sup>20</sup> ಅದಲ್ಲದೆ ಕೃಶವಾಗಿದ್ದ ಆ ಬಡ ಆಕಳುಗಳು ಮೊದಲು ಕಾಣಿಸಿದ ಆ ಏಳು ಕೊಬ್ಬಿದ ಆಕಳುಗಳನ್ನು ತಿಂದುಬಿಟ್ಟವು;",
			"21": "<sup>21</sup> ತಿಂದಾಗ್ಯೂ ತಿಂದವೆಂಬದು ತೋರಲಿಲ್ಲ; ಅವು ಮೊದಲಿದ್ದಂತೆ ಬಡವಾಗಿಯೇ ಇದ್ದವು. ಅಲ್ಲಿಗೆ ಎಚ್ಚರವಾಯಿತು.",
			"22": "<sup>22</sup> ನನಗೆ ಬಿದ್ದ ಇನ್ನೊಂದು ಕನಸಿನೊಳಗೆ ಏಳು ಒಳ್ಳೇ ಪುಷ್ಟಿಯುಳ್ಳ ತೆನೆಗಳು ಒಂದೇ ದಂಟಿನಲ್ಲಿ ಹುಟ್ಟಿಬಂದವು.",
			"23": "<sup>23</sup> ಅವುಗಳ ಹಿಂದೆ ಮೂಡಣ ಗಾಳಿಯಿಂದ ಕೆಟ್ಟು ಬತ್ತಿ ಒಣಗಿಹೋಗಿದ್ದ ಬೇರೆ ಏಳು ತೆನೆಗಳು ಮೊಳೆತುಬಂದವು.",
			"24": "<sup>24</sup> ಬತ್ತಿಹೋಗಿದ್ದ ತೆನೆಗಳು ಆ ಏಳು ಒಳ್ಳೇ ತೆನೆಗಳನ್ನು ನುಂಗಿಬಿಟ್ಟವು. ಈ ಕನಸುಗಳನ್ನು ಜೋಯಿಸರಿಗೆ ತಿಳಿಸಿದೆನು. ಆದರೆ ಅವುಗಳ ಅರ್ಥವನ್ನು ಬಿಚ್ಚಿಹೇಳುವದಕ್ಕೆ ಅವರಲ್ಲಿ ಯಾರಿಂದಲೂ ಆಗಲಿಲ್ಲ.",
			"25": "<sup>25</sup> ಯೋಸೇಫನು ಫರೋಹನಿಗೆ - ತಾವು ಕಂಡ ಎರಡು ಕನಸುಗಳೂ ಒಂದೇ ವಿಷಯವಾದವುಗಳು. ದೇವರು ತಾನು ಮಾಡಬೇಕೆಂದಿರುವದನ್ನು ಇವುಗಳ ಮೂಲಕ ತಮಗೆ ತಿಳಿಸಿದ್ದಾನೆ.",
			"26": "<sup>26</sup> ಆ ಏಳು ಒಳ್ಳೆಯ ಆಕಳುಗಳೇ ಆ ಏಳು ವರುಷಗಳು; ಏಳು ಒಳ್ಳೆಯ ತೆನೆಗಳೂ ಏಳು ವರುಷಗಳು; ಎರಡು ಕನಸುಗಳ ಅರ್ಥವೊಂದೇ.",
			"27": "<sup>27</sup> ಒಳ್ಳೇ ಆಕಳುಗಳ ಹಿಂದೆ ಬಂದ ಅವಲಕ್ಷಣವಾದ ಆ ಬಡ ಆಕಳುಗಳೂ ಮೂಡಣ ಗಾಳಿಯಿಂದ ಕೆಟ್ಟುಹೋಗಿದ್ದ ಆ ಏಳು ಕಾಳಿಲ್ಲದ ತೆನೆಗಳೂ ಬರವುಂಟಾಗುವ ಏಳು ವರುಷಗಳನ್ನು ಸೂಚಿಸುತ್ತವೆ.",
			"28": "<sup>28</sup> ದೇವರು ತಾನು ಮಾಡಬೇಕೆಂದಿರುವದನ್ನು ತಮಗೆ ತಿಳಿಸಿದ್ದಾನೆಂಬದಾಗಿ ನಾನು ಅರಿಕೆ ಮಾಡಿದೆನಷ್ಟೆ.",
			"29": "<sup>29</sup> ಕೇಳಯ್ಯಾ, ಐಗುಪ್ತದೇಶದಲ್ಲೆಲ್ಲಾ ಬಹುವಿಶೇಷವಾದ",
			"30": "<sup>30</sup> ಏಳು ಸುಭಿಕ್ಷ ವರುಷಗಳೂ ಅವುಗಳ ತರುವಾಯ ಏಳು ದುರ್ಭಿಕ್ಷವರುಷಗಳೂ ಬರುವವು. ಆಗ ಐಗುಪ್ತದೇಶದವರು ಮೊದಲಿದ್ದ ಸುಭಿಕ್ಷವನ್ನು ಮರೆತುಬಿಡುವರು; ಆ ಬರದಿಂದ ದೇಶವು ಹಾಳಾಗಿ ಹೋಗುವದು.",
			"31": "<sup>31</sup> ಮುಂದೆ ಬರುವ ಕ್ಷಾಮವು ಅತ್ಯಂತ ಘೋರವಾಗಿರುವದರಿಂದ ಮೊದಲಿದ್ದ ಸಮೃದ್ಧಿಯ ಗುರುತೇ ಕಾಣಿಸದೆ ಹೋಗುವದು.",
			"32": "<sup>32</sup> ಫರೋಹನಿಗೆ ಕನಸು ಎರಡು ವಿಧವಾಗಿ ಬಿದ್ದದ್ದರಿಂದ ದೇವರು ಈ ಕಾರ್ಯವನ್ನು ನಿಶ್ಚಯಿಸಿ ಇದನ್ನು ಬೇಗ ಮಾಡುವನೆಂಬದಾಗಿ ತಿಳುಕೊಳ್ಳಬೇಕು.",
			"33": "<sup>33</sup> ಆದದರಿಂದ ತಾವು ವಿವೇಕಿಯೂ ಬುದ್ಧಿವಂತನೂ ಆಗಿರುವ ಪುರುಷನನ್ನು ಆರಿಸಿಕೊಂಡು ಅವನನ್ನು ಐಗುಪ್ತದೇಶದ ಮೇಲೆ ಅಧಿಕಾರಿಯಾಗಿ ನೇಮಿಸಬೇಕು.",
			"34": "<sup>34</sup> ಮತ್ತು ತಾವು ಒಂದು ಕೆಲಸ ಮಾಡಬೇಕು; ದೇಶದ ಎಲ್ಲಾ ಭಾಗಗಳಲ್ಲಿಯೂ ಕರಣೀಕರನ್ನು ನೇಮಿಸಿ ಅವರ ಕೈಯಿಂದ ಸುಭಿಕ್ಷವಾದ ಏಳು ವರುಷಗಳಲ್ಲಿ ದೇಶದ ಬೆಳೆಯೊಳಗೆ ಐದನೆಯ ಪಾಲನ್ನು ಕಂದಾಯವಾಗಿ ಎತ್ತಿಸಬೇಕು.",
			"35": "<sup>35</sup> ಅವರು ಮುಂದಣ ಒಳ್ಳೇ ವರುಷಗಳಲ್ಲಿ ಆಹಾರಪದಾರ್ಥಗಳನ್ನೂ ದವಸಧಾನ್ಯಗಳನ್ನೂ ಕೂಡಿಸಿ ಫರೋಹನ ವಶದೊಳಗೆ ಪಟ್ಟಣಗಳಲ್ಲಿಟ್ಟು ಜೋಪಾನ ಮಾಡಲಿ.",
			"36": "<sup>36</sup> ಆ ಆಹಾರಪದಾರ್ಥಗಳನ್ನು ಇಟ್ಟು ಕೂಡಿಸಿಕೊಳ್ಳುವದರಿಂದ ಐಗುಪ್ತದೇಶದಲ್ಲಿ ದುರ್ಭಿಕ್ಷವುಂಟಾಗುವ ಏಳು ವರುಷಗಳಲ್ಲಿ ಜನರು ಕ್ಷಾಮದಿಂದ ಸಾಯುವದಿಲ್ಲ ಎಂದು ಹೇಳಿದನು.",
			"37": "<sup>37</sup> ಆ ಮಾತು ಫರೋಹನಿಗೂ ಅವನ ಪರಿವಾರದವರಿಗೂ ಒಳ್ಳೇದಾಗಿ ತೋಚಿದ್ದರಿಂದ",
			"38": "<sup>38</sup> ಫರೋಹನು ತನ್ನ ಪರಿವಾರದವರಿಗೆ - ಈತನಲ್ಲಿ ದೇವರ ಆತ್ಮ ಉಂಟಲ್ಲಾ;",
			"39": "<sup>39</sup> ಈತನಿಗಿಂತ ಯೋಗ್ಯನಾದ ಪುರುಷನು ನಮಗೆ ಸಿಕ್ಕಾನೇ ಎಂದು ಹೇಳಿ ಯೋಸೇಫನಿಗೆ - ದೇವರು ಇದನ್ನೆಲ್ಲಾ ನಿನಗೇ ತಿಳಿಸಿರುವದರಿಂದ ನಿನಗೆ ಸಮಾನನಾದ ಬುದ್ಧಿ ವಿವೇಕಗಳುಳ್ಳ ಪುರುಷನು ಯಾವನೂ ಇಲ್ಲ;",
			"40": "<sup>40</sup> ನೀನೇ ಅರಮನೆಯಲ್ಲಿ ಸರ್ವಾಧಿಕಾರಿಯಾಗಿರಬೇಕು; ನಿನ್ನ ಅಪ್ಪಣೆಯ ಮೇರೆಗೆ ಪ್ರಜೆಗಳೆಲ್ಲರೂ ನಡೆದುಕೊಳ್ಳಬೇಕು; ಸಿಂಹಾಸನದ ವಿಷಯದಲ್ಲಿ ಮಾತ್ರ ನಾನು ನಿನಗಿಂತ ದೊಡ್ಡವನಾಗಿರುವೆನು ಎಂದು ಹೇಳಿದನು.",
			"41": "<sup>41</sup> ಫರೋಹನು ಯೋಸೇಫನಿಗೆ - ನೋಡು ಐಗುಪ್ತದೇಶದ ಮೇಲೆಲ್ಲಾ ನಿನ್ನನ್ನು ಅಧಿಕಾರಿಯನ್ನಾಗಿ ಇಟ್ಟಿದ್ದೇನೆ ಎಂದು ಹೇಳಿ",
			"42": "<sup>42</sup> ತನ್ನ ಕೈಯಿಂದ ಮುದ್ರಿಕೆಯನ್ನು ತೆಗೆದು ಯೋಸೇಫನ ಕೈಗೆ ಇಟ್ಟು ಅವನಿಗೆ ನಾರುಮಡಿಯನ್ನು ಹೊದಿಸಿ ಅವನ ಕೊರಳಿಗೆ ಚಿನ್ನದ ಸರಪಣಿಯನ್ನು ಹಾಕಿಸಿ",
			"43": "<sup>43</sup> ತನಗಿದ್ದ ಎರಡನೆಯ ರಥದಲ್ಲಿ ಕುಳ್ಳಿರಿಸಿ ಅವನ ಮುಂದೆ ಅಡ್ಡಬೀಳಿರಿ ಎಂದು ಪ್ರಕಟಣೆಮಾಡಿಸಿ ಅವನನ್ನು ಐಗುಪ್ತದೇಶಕ್ಕೆ ಸರ್ವಾಧಿಕಾರಿಯನ್ನಾಗಿ ಇಟ್ಟನು.",
			"44": "<sup>44</sup> ಇನ್ನೂ ಫರೋಹನು ಅವನಿಗೆ - ನಾನು ಫರೋಹನು; ನಿನ್ನ ಅಪ್ಪಣೆಯಿಲ್ಲದೆ ಐಗುಪ್ತದೇಶದಲ್ಲೆಲ್ಲಾ ಒಬ್ಬನೂ ಕೈಯನ್ನಾಗಲಿ ಕಾಲನ್ನಾಗಲಿ ಕದಲಿಸಕೂಡದು ಎಂದು ಹೇಳಿದನು.",
			"45": "<sup>45</sup> ಮತ್ತು ಫರೋಹನು ಯೋಸೇಫನಿಗೆ ಸಾಫ್ನತ್ಪನ್ನೇಹ ಎಂದು ಹೆಸರಿಟ್ಟು ಓನ್ ಎಂಬ ಗುರುಪೀಠದ ಆಚಾರ್ಯನಾಗಿದ್ದ ಪೋಟೀಫೆರನ ಮಗಳಾದ ಆಸನತ್ ಎಂಬಾಕೆಯನ್ನು ಅವನಿಗೆ ಮದುವೆ ಮಾಡಿಸಿದನು. ಯೋಸೇಫನು ಐಗುಪ್ತದೇಶದ ಮೇಲೆ ಅಧಿಕಾರಿಯಾಗಿ ಹೊರಟನು.",
			"46": "<sup>46</sup> ಯೋಸೇಫನು ಐಗುಪ್ತದೇಶದ ಅರಸನಾದ ಫರೋಹನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನಿಂತಾಗ ಮೂವತ್ತು ವರುಷದವನಾಗಿದ್ದನು. ಅವನು ಫರೋಹನ ಸನ್ನಿಧಿಯಿಂದ ಹೊರಟು ಐಗುಪ್ತದೇಶವನ್ನೆಲ್ಲಾ ಸಂಚಾರ ಮಾಡಿದನು.",
			"47": "<sup>47</sup> ಸುಭಿಕ್ಷವಾದ ಏಳು ವರುಷಗಳಲ್ಲಿ ಭೂಮಿಯು ಹೇರಳವಾಗಿ ಫಲಕೊಟ್ಟಿತು.",
			"48": "<sup>48</sup> ಐಗುಪ್ತದೇಶದಲ್ಲಿ ಸುಭಿಕ್ಷವಾದ ಆ ಏಳು ವರುಷಗಳ ಬೆಳೆಯನ್ನು ಯೋಸೇಫನು ಕೂಡಿಸಿ ಪಟ್ಟಣಗಳಲ್ಲಿ ಇರಿಸಿದನು; ಒಂದೊಂದು ಪಟ್ಟಣದ ಸುತ್ತಲಿದ್ದ ಹೊಲಗಳ ಬೆಳೆಯನ್ನು ಆಯಾ ಪಟ್ಟಣದಲ್ಲಿಯೇ ಕೂಡಿಸಿಟ್ಟನು.",
			"49": "<sup>49</sup> ಅವನು ದವಸಧಾನ್ಯವನ್ನು ಸಮುದ್ರದ ಉಸಬಿನಷ್ಟು ರಾಶಿರಾಶಿಯಾಗಿ ಕೂಡಿಸಿ ಲೆಕ್ಕಮಾಡುವದನ್ನು ಬಿಟ್ಟು ಬಿಟ್ಟನು; ಅದನ್ನು ಲೆಕ್ಕ ಮಾಡುವದಕ್ಕೆ ಆಗದೆಹೋಯಿತು.",
			"50": "<sup>50</sup> ಬರಗಾಲ ಬರುವದಕ್ಕಿಂತ ಮುಂಚೆ ಯೋಸೇಫನಿಗೆ ಇಬ್ಬರು ಗಂಡು ಮಕ್ಕಳು ಹುಟ್ಟಿದರು. ಇವರು ಓನ್ ಪಟ್ಟಣದ ಆಚಾರ್ಯನಾಗಿದ್ದ ಪೋಟೀಫೆರನ ಮಗಳಾದ ಆಸನತೆಂಬಾಕೆಯಲ್ಲಿ ಅವನಿಗೆ ಹುಟ್ಟಿದರು.",
			"51": "<sup>51</sup> ಚೊಚ್ಚಲುಮಗನು ಹುಟ್ಟಿದಾಗ ಯೋಸೇಫನು - ನಾನು ನನ್ನ ಎಲ್ಲಾ ಕಷ್ಟವನ್ನೂ ತಂದೆಯ ಮನೆಯವರನ್ನೂ ಮರೆತುಬಿಡುವಂತೆ ದೇವರು ಮಾಡಿದ್ದಾನಲ್ಲಾ ಎಂದು ಹೇಳಿ ಅವನಿಗೆ ಮನಸ್ಸೆ ಎಂದು ಹೆಸರಿಟ್ಟನು.",
			"52": "<sup>52</sup> ಎರಡನೆಯ ಮಗನು ಹುಟ್ಟಿದಾಗ ಅವನು - ನನಗೆ ಸಂಕಟ ಬಂದ ದೇಶದಲ್ಲೇ ದೇವರು ಅಭಿವೃದ್ಧಿಯನ್ನು ದಯಪಾಲಿಸಿದ್ದಾನೆಂದು ಹೇಳಿ ಆ ಮಗನಿಗೆ ಎಫ್ರಾಯೀಮ್ ಎಂದು ಹೆಸರಿಟ್ಟನು.",
			"53": "<sup>53</sup> ಐಗುಪ್ತದೇಶದಲ್ಲಿ ಸುಭಿಕ್ಷದ ಏಳು ವರುಷಗಳು ಮುಗಿದ ಮೇಲೆ",
			"54": "<sup>54</sup> ಯೋಸೇಫನು ಹೇಳಿದ್ದ ಪ್ರಕಾರ ಕ್ಷಾಮದ ಏಳು ವರುಷಗಳಿಗೆ ಪ್ರಾರಂಭವಾಯಿತು. ಬರವು ಸುತ್ತಲಿದ್ದ ಎಲ್ಲಾ ದೇಶಗಳಲ್ಲಿಯೂ ಹಬ್ಬಿತು; ಐಗುಪ್ತದೇಶದಲ್ಲಿ ಮಾತ್ರ ಆಹಾರ ದೊರಕುವದು.",
			"55": "<sup>55</sup> ಐಗುಪ್ತದೇಶಕ್ಕೆಲ್ಲಾ ಬರಬಂದಾಗ ಪ್ರಜೆಗಳು ಆಹಾರಬೇಕೆಂದು ಫರೋಹನಿಗೆ ಮೊರೆಯಿಟ್ಟುಕೊಂಡರು. ಅವನು ಅವರಿಗೆ - ಯೋಸೇಫನ ಬಳಿಗೆ ಹೋಗಿರಿ, ಅವನ ಮಾತಿನ ಮೇರೆಗೆ ಮಾಡಿರಿ ಎಂದು ಹೇಳಿದನು.",
			"56": "<sup>56</sup> ಬರವು ಲೋಕದಲ್ಲೆಲ್ಲಾ ಹರಡಿಕೊಂಡಿರುವಾಗ ಯೋಸೇಫನು ಕಣಜಗಳನ್ನೆಲ್ಲಾ ತೆಗಿಸಿ ಐಗುಪ್ತ್ಯರಿಗೆ ಧಾನ್ಯವನ್ನು ಮಾರಿಸಿದನು. ಐಗುಪ್ತದೇಶದಲ್ಲಿ ಬರವು ಬಹು ಘೋರವಾಗಿತ್ತು.",
			"57": "<sup>57</sup> ಇದಲ್ಲದೆ ಭೂಲೋಕದಲ್ಲೆಲ್ಲಾ ಬರವು ಭಯಂಕರವಾಗಿದ್ದದರಿಂದ ಎಲ್ಲಾ ದೇಶದವರೂ ಧಾನ್ಯವನ್ನು ಕೊಂಡುಕೊಳ್ಳುವದಕ್ಕಾಗಿ ಐಗುಪ್ತಕ್ಕೆ ಯೋಸೇಫನ ಬಳಿಗೆ ಬಂದರು."
		},
		"42": {
			"1": "<sup>1</sup> ಐಗುಪ್ತದೇಶದಲ್ಲಿ ದವಸಧಾನ್ಯವುಂಟೆಂದು ಯಾಕೋಬನು ಕೇಳಿ ತನ್ನ ಮಕ್ಕಳಿಗೆ - ನೀವು ಒಬ್ಬರನ್ನೊಬ್ಬರು ನೋಡುತ್ತಿರುವದೇಕೆ?",
			"2": "<sup>2</sup> ಐಗುಪ್ತ ದೇಶದಲ್ಲಿ ದವಸಧಾನ್ಯ ಉಂಟೆಂಬದನ್ನು ಕೇಳಿದ್ದೇನೆ; ನಾವು ಸಾಯದೆ ಬದುಕುವಂತೆ ನೀವು ಅಲ್ಲಿಗೆ ಹೋಗಿ ಬೇಕಾದ ದವಸವನ್ನು ಕೊಂಡುಕೊಂಡು ಬನ್ನಿರಿ ಎಂದು ಹೇಳಿದ್ದರಿಂದ",
			"3": "<sup>3</sup> ಯೋಸೇಫನ ಹತ್ತು ಮಂದಿ ಅಣ್ಣಂದಿರು ಧಾನ್ಯವನ್ನು ತರುವದಕ್ಕೋಸ್ಕರ ಐಗುಪ್ತದೇಶಕ್ಕೆ ಹೊರಟರು.",
			"4": "<sup>4</sup> ಆದರೆ ಯಾಕೋಬನು ಯೋಸೇಫನ ಒಡಹುಟ್ಟಿದ ತಮ್ಮನಾದ ಬೆನ್ಯಾಮೀನನನ್ನು ಅವನಿಗೆ ಕೆಡುಕುಂಟಾದೀತೆಂದು ಹೇಳಿ ಅಣ್ಣಂದಿರ ಜೊತೆಯಲ್ಲಿ ಕಳುಹಿಸಲಿಲ್ಲ.",
			"5": "<sup>5</sup> ಧಾನ್ಯವನ್ನು ಕೊಂಡುಕೊಳ್ಳುವದಕ್ಕೋಸ್ಕರ ಐಗುಪ್ತದೇಶಕ್ಕೆ ಬಂದವರಲ್ಲಿ ಇಸ್ರಾಯೇಲನ ಮಕ್ಕಳೂ ಇದ್ದರು; ಕಾನಾನ್ ದೇಶದಲ್ಲಿಯೂ ಬರವಿತ್ತಷ್ಟೆ.",
			"6": "<sup>6</sup> ಐಗುಪ್ತದೇಶದ ಮೇಲೆ ಅಧಿಕಾರವನ್ನು ನಡಿಸಿ ಜನರಿಗೆ ಧಾನ್ಯವನ್ನು ಮಾರಿಸುವವನು ಯೋಸೇಫನೇ. ಹೀಗಿರುವದರಿಂದ ಅವನ ಅಣ್ಣಂದಿರು ಬಂದು ಅವನ ಮುಂದೆಯೇ ಅಡ್ಡಬಿದ್ದರು.",
			"7": "<sup>7</sup> ಯೋಸೇಫನು ಅವರನ್ನು ಕಂಡಾಗ ಅವರ ಗುರುತು ಹಿಡಿದು ತನ್ನ ಅಣ್ಣಂದಿರೆಂದು ತಿಳಿದಾಗ್ಯೂ ಅವರಿಗೆ ಅನ್ಯನಂತೆ ತೋರ್ಪಡಿಸಿಕೊಂಡು ಬಿರುನುಡಿಯಿಂದ - ನೀವು ಎಲ್ಲಿಂದ ಬಂದವರು ಎಂದು ಕೇಳಿದನು. ಅವರು - ಧಾನ್ಯವನ್ನು ಕೊಂಡುಕೊಳ್ಳುವದಕ್ಕೋಸ್ಕರ ಕಾನಾನ್ ದೇಶದಿಂದ ಬಂದಿದ್ದೇವೆ ಅಂದರು.",
			"8": "<sup>8</sup> ಅಣ್ಣಂದಿರ ಗುರುತು ಯೋಸೇಫನಿಗೆ ತಿಳಿದಾಗ್ಯೂ ಅವನ ಗುರುತು ಅವರಿಗೆ ತಿಳಿಯಲಿಲ್ಲ.",
			"9": "<sup>9</sup> ಅವನು ಅವರ ವಿಷಯ ತನಗೆ ಬಿದ್ದ ಕನಸುಗಳನ್ನು ನೆನಪಿಗೆ ತಂದುಕೊಂಡು ಅವರಿಗೆ - ನೀವು ಗೂಢಚಾರರು; ನಮ್ಮ ದೇಶದ ದುರ್ಬಲ ಸ್ಥಳಗಳನ್ನು ನೋಡಿಕೊಳ್ಳುವದಕ್ಕಾಗಿ ಬಂದವರು ಎಂದು ಹೇಳಲು,",
			"10": "<sup>10</sup> ಅವರು - ಅಲ್ಲ, ಸ್ವಾಮೀ, ತಮ್ಮ ಸೇವಕರಾದ ನಾವು ಆಹಾರ ಪದಾರ್ಥಗಳನ್ನು ಕೊಂಡುಕೊಳ್ಳುವದಕ್ಕೆ ಬಂದಿದ್ದೇವೆ. ನಾವೆಲ್ಲರೂ ಒಬ್ಬನೇ ತಂದೆಯ ಮಕ್ಕಳು;",
			"11": "<sup>11</sup> ನಾವು ಯಥಾರ್ಥವಾಗಿ ಬಂದವರೇ ಹೊರತು ಗೂಢಚಾರರಲ್ಲ ಅಂದರು.",
			"12": "<sup>12</sup> ಅವನು ಅವರಿಗೆ - ಸುಳ್ಳು, ನಮ್ಮ ದೇಶದ ದುರ್ಬಲ ಸ್ಥಳಗಳನ್ನು ತಿಳಿದುಕೊಳ್ಳುವದಕ್ಕೆ ಬಂದಿರಿ ಎಂದು ಹೇಳಲು",
			"13": "<sup>13</sup> ಅವರು - ತಮ್ಮ ಸೇವಕರಾದ ನಾವು ಹನ್ನೆರಡು ಮಂದಿ ಅಣ್ಣತಮ್ಮಂದಿರು; ನಾವು ಕಾನಾನ್ ದೇಶದವರು, ಒಬ್ಬ ತಂದೆಯ ಮಕ್ಕಳು; ನಮ್ಮಲ್ಲಿ ಚಿಕ್ಕವನು ತಂದೆಯ ಬಳಿಯಲ್ಲಿದ್ದಾನೆ; ಒಬ್ಬನಿಲ್ಲ ಅಂದರು.",
			"14": "<sup>14</sup> ಯೋಸೇಫನು ಅವರಿಗೆ - ನೀವು ಗೂಢಚಾರರೆಂದು ನಾನು ಹೇಳಿದೆನಷ್ಟೆ;",
			"15": "<sup>15</sup> ಅದು ಹೌದೋ ಅಲ್ಲವೋ ಗೊತ್ತಾಗುವದಕ್ಕಾಗಿ ನಿಮ್ಮ ತಮ್ಮನು ಬರಬೇಕು; ಅವನು ಬಂದ ಹೊರತು ಫರೋಹನ ಜೀವದಾಣೆ ನೀವು ಇಲ್ಲಿಂದ ಹೊರಟು ಹೋಗಕೂಡದು.",
			"16": "<sup>16</sup> ಅವನನ್ನು ಕರೆದುಕೊಂಡು ಬರುವದಕ್ಕೆ ನಿಮ್ಮಲ್ಲಿ ಒಬ್ಬನನ್ನು ಕಳುಹಿಸಿರಿ; ಅವನು ಬರುವವರೆಗೂ ನಿಮ್ಮನ್ನು ಸೆರೆಯಲ್ಲಿ ಇಡಿಸುತ್ತೇನೆ. ಹೀಗೆ ನಾನು ನಿಮ್ಮ ಮಾತುಗಳನ್ನು ಪರೀಕ್ಷಿಸಿ ನೀವು ಸತ್ಯವಂತರೋ ಅಲ್ಲವೋ ಎಂದು ತಿಳಿದುಕೊಳ್ಳುತ್ತೇನೆ. ನಿಮ್ಮ ತಮ್ಮನು ಬಾರದೆ ಹೋದರೆ, ಫರೋಹನ ಜೀವದಾಣೆ, ನೀವು ಗೂಢಚಾರರೇ ಎಂದು ಹೇಳಿ",
			"17": "<sup>17</sup> ಅವರೆಲ್ಲರನ್ನೂ ಮೂರು ದಿನಗಳವರೆಗೂ ಕಾವಲಲ್ಲಿರಿಸಿದನು.",
			"18": "<sup>18</sup> ಮೂರನೆಯ ದಿನದಲ್ಲಿ ಯೋಸೇಫನು ಅವರನ್ನು ಕರಸಿ - ನಾನು ದೇವರಿಗೆ ಭಯಪಡುವವನು; ನೀವು ಸತ್ಯವಂತರಾಗಿದ್ದರೆ ಒಂದು ಕೆಲಸವನ್ನು ಮಾಡಿ ಬದುಕಿಕೊಳ್ಳಿರಿ.",
			"19": "<sup>19</sup> ನಿಮ್ಮಲ್ಲಿ ಒಬ್ಬನನ್ನು ಮಾತ್ರ ಸೆರೆಯಲ್ಲಿರಿಸುತ್ತೇನೆ; ಮಿಕ್ಕಾದವರು ನಿಮ್ಮ ಮನೆಯವರ ಅವಸರಕ್ಕೆ ಧಾನ್ಯವನ್ನು ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ನಿಮ್ಮ ತಮ್ಮನನ್ನು",
			"20": "<sup>20</sup> ನನ್ನ ಬಳಿಗೆ ಕರೆದುಕೊಂಡು ಬರಬೇಕು; ಕರೆದುಕೊಂಡು ಬಂದರೆ, ನಿಮ್ಮ ಮಾತನ್ನು ಸತ್ಯವೆಂದು ನಾನು ತಿಳಿದುಕೊಳ್ಳುವದರಿಂದ ನೀವು ಸಾಯುವದಿಲ್ಲ ಅಂದನು. ಅವರು ಆ ಮಾತಿಗೆ ಒಪ್ಪಿದರು.",
			"21": "<sup>21</sup> ಆದರೆ ಅವರು ತಮ್ಮತಮ್ಮೊಳಗೆ - ನಾವು ನಮ್ಮ ತಮ್ಮನಿಗೆ ಮಾಡಿದ್ದು ದ್ರೋಹವೇ ಸರಿ; ಅವನು ನಮ್ಮನ್ನು ಬೇಡಿಕೊಂಡಾಗ ನಾವು ಅವನ ಪ್ರಾಣಸಂಕಟವನ್ನು ತಿಳಿದರೂ ಅವನ ಮೊರೆಗೆ ಕಿವಿಗೊಡಲಿಲ್ಲ; ಆ ಕಾರಣದಿಂದಲೇ ಈ ಸಂಕಟವು ನಮಗೆ ಪ್ರಾಪ್ತವಾಗಿದೆ ಎಂದು ಮಾತಾಡಿಕೊಂಡರು.",
			"22": "<sup>22</sup> ಅದಕ್ಕೆ ರೂಬೇನನು - ಆ ಹುಡುಗನಿಗೆ ಏನೂ ಕೇಡು ಮಾಡಬೇಡಿರಿ ಎಂದು ನಾನು ಹೇಳಿದೆನಲ್ಲವೇ; ನೀವು ಕೇಳಲಿಲ್ಲ. ಆದದರಿಂದ ಅವನಿಗಾದ ಪ್ರಾಣಹಾನಿಯ ವಿಷಯದಲ್ಲಿ ನಾವು ಉತ್ತರ ಹೇಳಬೇಕಾಗಿ ಬಂತು ಅಂದನು.",
			"23": "<sup>23</sup> ಯೋಸೇಫನು ದ್ವಿಭಾಷಿಯ ಮುಖಾಂತರ ಅವರ ಸಂಗಡ ಮಾತಾಡಿದದರಿಂದ, ಅವರು ತಮ್ಮ ಮಾತು ಅವನಿಗೆ ಗೊತ್ತಾಗುವದೆಂದು ನೆನಸಲೇ ಇಲ್ಲ.",
			"24": "<sup>24</sup> ಯೋಸೇಫನಾದರೋ ಅವರ ಬಳಿಯಿಂದ ಒಂದು ಕಡೆಗೆ ಹೋಗಿ ಕಣ್ಣೀರು ಸುರಿಸಿದನು. ತರುವಾಯ ಅವರ ಬಳಿಗೆ ತಿರಿಗಿ ಬಂದು ಅವರ ಸಂಗಡ ಮಾತಾಡಿ ಅವರೊಳಗೆ ಸಿಮೆಯೋನನನ್ನು ಹಿಡಿಸಿ ಅವರ ಕಣ್ಣೆದುರಾಗಿ ಅವನಿಗೆ ಬೇಡಿಗಳನ್ನು ಹಾಕಿಸಿದನು.",
			"25": "<sup>25</sup> ಆಮೇಲೆ ಯೋಸೇಫನು ತನ್ನ ಆಳುಗಳಿಗೆ - ಆ ಮನುಷ್ಯರ ಚೀಲಗಳಲ್ಲಿ ಧಾನ್ಯವನ್ನು ತುಂಬಿಸಿ ಒಬ್ಬೊಬ್ಬನ ಚೀಲದಲ್ಲಿ ಅವನವನ ಹಣವನ್ನು ತಿರಿಗಿ ಇಟ್ಟು ಅವರ ಪ್ರಯಾಣಕ್ಕೆ ಬೇಕಾದ ಆಹಾರವನ್ನು ಕೊಡಬೇಕೆಂದು ಅಪ್ಪಣೆಮಾಡಲು ಅವರು ಹಾಗೆಯೇ ಮಾಡಿದರು.",
			"26": "<sup>26</sup> ಇವರು ತಾವು ಕೊಂಡುಕೊಂಡ ಧಾನ್ಯವನ್ನು ಕತ್ತೆಗಳ ಮೇಲೆ ಹೇರಿಕೊಂಡು ಹೊರಟುಹೋದರು.",
			"27": "<sup>27</sup> ದಾರಿಯಲ್ಲಿ ಅವರು ಒಂದು ಸ್ಥಳದಲ್ಲಿ ಇಳಿದುಕೊಂಡಾಗ ಅವರಲ್ಲಿ ಒಬ್ಬನು ತನ್ನ ಕತ್ತೆಗೆ ದಾಣಕೊಡಬೇಕೆಂದು ತನ್ನ ಚೀಲವನ್ನು ಬಿಚ್ಚಿದಾಗ ಅದರ ಬಾಯಲ್ಲೇ ತಾನು ತಂದಿದ್ದ ಹಣವನ್ನು ಕಂಡನು.",
			"28": "<sup>28</sup> ಅವನು ಅಣ್ಣತಮ್ಮಂದಿರಿಗೆ - ನಾನು ಕೊಟ್ಟ ಹಣವು ಹಿಂದಕ್ಕೆ ಬಂದದೆ; ಇಗೋ, ನನ್ನ ಚೀಲದಲ್ಲಿದೆ ಎಂದು ಹೇಳಲು ಅವರು ಧೈರ್ಯಗೆಟ್ಟು ನಡುಗುತ್ತಾ ಒಬ್ಬರನ್ನೊಬ್ಬರು ನೋಡಿ - ದೇವರು ನಮಗೆ ಮಾಡಿದ್ದು ಇದೇನು ಅಂದರು.",
			"29": "<sup>29</sup> ಅವರು ಕಾನಾನ್‍ದೇಶಕ್ಕೆ ತಮ್ಮ ತಂದೆಯಾದ ಯಾಕೋಬನ ಬಳಿಗೆ ಬಂದು ನಡೆದದ್ದನ್ನೆಲ್ಲಾ ತಿಳಿಸಿ -",
			"30": "<sup>30</sup> ಆ ದೇಶಾಧಿಪತಿ ನಮ್ಮ ಸಂಗಡ ಬಿರಿಸಿನಿಂದ ಮಾತಾಡಿ ನಮ್ಮನ್ನು ಗೂಢಚಾರರೆಂದು ಹೇಳಿದನು.",
			"31": "<sup>31</sup> ನಾವು ಅವನಿಗೆ - ನಾವು ಸತ್ಯವಂತರೇ ಹೊರತು ಗೂಢಚಾರರಲ್ಲ;",
			"32": "<sup>32</sup> ನಾವು ಒಬ್ಬ ತಂದೆಯಿಂದಲೇ ಹುಟ್ಟಿದ ಹನ್ನೆರಡು ಮಂದಿ ಅಣ್ಣತಮ್ಮಂದಿರಾಗಿದ್ದೇವೆ; ನಮ್ಮಲ್ಲಿ ಒಬ್ಬನಿಲ್ಲ; ಚಿಕ್ಕವನೋ ಈಗ ಕಾನಾನ್ ದೇಶದಲ್ಲಿ ನಮ್ಮ ತಂದೆಯ ಬಳಿಯಲ್ಲಿದ್ದಾನೆ ಎಂದು ಹೇಳಿದೆವು.",
			"33": "<sup>33</sup> ಅದಕ್ಕೆ ದೇಶಾಧಿಪತಿ - ನೀವು ಸತ್ಯವಂತರೋ ಅಲ್ಲವೋ ಎಂಬದು ಗೊತ್ತಾಗುವದಕ್ಕಾಗಿ ನಿಮ್ಮೊಳಗೆ ಒಬ್ಬನನ್ನು ನನ್ನ ಬಳಿಯಲ್ಲಿ ಬಿಟ್ಟು ನಿಮ್ಮ ಮನೆಯವರ ಕ್ಷಾಮ ನಿವಾರಣೆಗೆ ಬೇಕಾದದ್ದನ್ನು ತೆಗೆದುಕೊಂಡು ಹೋಗಿ",
			"34": "<sup>34</sup> ನಿಮ್ಮ ತಮ್ಮನನ್ನು ನನ್ನ ಬಳಿಗೆ ಕರಕೊಂಡು ಬರಬೇಕು; ಕರಕೊಂಡು ಬಂದರೆ ನೀವು ಗೂಢಚಾರರಲ್ಲ, ಸತ್ಯವಂತರೇ ಎಂದು ತಿಳಿದುಕೊಂಡು ನಿಮ್ಮ ಅಣ್ಣನನ್ನು ಬಿಡಿಸಿಬಿಡುತ್ತೇನೆ, ಮತ್ತು ನೀವು ಈ ದೇಶದಲ್ಲಿ ವ್ಯಾಪಾರ ನಡಿಸಬಹುದೆಂದು ಹೇಳಿದನು ಅಂದರು.",
			"35": "<sup>35</sup> ಅವರು ತಮ್ಮ ಚೀಲಗಳನ್ನು ಬಿಚ್ಚಿನೋಡಿದಾಗ ಪ್ರತಿಯೊಬ್ಬನ ಚೀಲದಲ್ಲಿಯೂ ಅವನವನ ಹಣದ ಗಂಟು ಸಿಕ್ಕಿತು. ಅವರೂ ಅವರ ತಂದೆಯೂ ಹಣದ ಗಂಟುಗಳನ್ನು ಕಂಡಾಗ ಭಯಪಟ್ಟರು.",
			"36": "<sup>36</sup> ಆಗ ಅವರ ತಂದೆಯಾದ ಯಾಕೋಬನು ಅವರಿಗೆ - ನನ್ನನ್ನು ಮಕ್ಕಳಿಲ್ಲದವನಂತೆ ಮಾಡಿದ್ದೀರಿ; ಯೋಸೇಫನೂ ಇಲ್ಲ, ಸಿಮೆಯೋನನೂ ಇಲ್ಲ; ಬೆನ್ಯಾಮೀನನನ್ನೂ ಕರಕೊಂಡು ಹೋಗಬೇಕೆಂದಿದ್ದೀರಿ, ಈ ಕಷ್ಟವೆಲ್ಲಾ ನನ್ನ ಮೇಲೆಯೇ ಬಂತು ಎಂದು ಹೇಳಲು ರೂಬೇನನು ಅವನಿಗೆ -",
			"37": "<sup>37</sup> ನಾನು ಈ ಹುಡುಗನನ್ನು ತಿರಿಗಿ ತಂದು ಒಪ್ಪಿಸದೆ ಹೋದರೆ ನನ್ನಿಬ್ಬರು ಗಂಡು ಮಕ್ಕಳನ್ನು ಕೊಂದು ಹಾಕಬಹುದು; ಇವನನ್ನು ನನ್ನ ವಶಕ್ಕೆ ಕೊಟ್ಟರೆ ನಾನು ತಪ್ಪದೆ ಇವನನ್ನು ನಿನ್ನ ಬಳಿಗೆ ಕರಕೊಂಡು ಬರುತ್ತೇನೆ ಎಂದು ಹೇಳಿದನು.",
			"38": "<sup>38</sup> ಆದರೆ ಯಾಕೋಬನು ಅವರಿಗೆ - ನನ್ನ ಮಗನು ನಿಮ್ಮ ಸಂಗಡ ಹೋಗಬಾರದು; ಇವನ ಒಡಹುಟ್ಟಿದವನು ಸತ್ತು ಹೋದನು; ಇವನೊಬ್ಬನೇ ಉಳಿದಿದ್ದಾನೆ; ಮಾರ್ಗದಲ್ಲಿ ಇವನಿಗೇನಾದರೂ ಕೇಡಾದರೆ ಈ ಮುದಿತಲೆ ದುಃಖದಿಂದಲೇ ಪಾತಾಳಕ್ಕೆ ಸೇರಲು ನೀವು ಕಾರಣವಾಗುವಿರಿ ಎಂದು ಹೇಳಿದನು."
		},
		"43": {
			"1": "<sup>1</sup> ಆ ದೇಶದಲ್ಲಿ ಬರವು ಬಹುಘೋರವಾಗಿತ್ತು.",
			"2": "<sup>2</sup> ಅವರು ಐಗುಪ್ತದೇಶದಿಂದ ತಂದಿದ್ದ ದವಸವು ಮುಗಿದನಂತರ ಅವರ ತಂದೆಯು ಅವರಿಗೆ - ನೀವು ತಿರಿಗಿ ಹೋಗಿ ಇನ್ನು ಸ್ವಲ್ಪ ಧಾನ್ಯವನ್ನು ಕೊಂಡುಕೊಂಡು ಬನ್ನಿರಿ ಅಂದನು.",
			"3": "<sup>3</sup> ಅದಕ್ಕೆ ಯೆಹೂದನು - ಆ ಮನುಷ್ಯನು ನಮಗೆ, ನಿಮ್ಮ ತಮ್ಮನನ್ನು ಕರಕೊಂಡುಬಂದ ಹೊರತು ನನ್ನ ಮುಖವನ್ನು ನೋಡಕೂಡದು ಎಂದು ಖಂಡಿತವಾಗಿ ಹೇಳಿದನಲ್ಲಾ.",
			"4": "<sup>4</sup> ನೀನು ನಮ್ಮ ತಮ್ಮನನ್ನು ನಮ್ಮ ಜೊತೆಯಲ್ಲಿ ಕಳುಹಿಸಿದರೆ ನಾವು ಹೋಗಿ ನಿನಗೆ ಧಾನ್ಯವನ್ನು ಕೊಂಡುಕೊಂಡು ಬರುತ್ತೇವೆ;",
			"5": "<sup>5</sup> ಅವನನ್ನು ಕಳುಹಿಸಿಕೊಡದಿದ್ದರೆ ನಾವು ಹೋಗುವದಿಲ್ಲ. ಆ ಮನುಷ್ಯನು ನಮಗೆ - ನಿಮ್ಮ ತಮ್ಮನು ನಿಮ್ಮ ಸಂಗಡ ಇಲ್ಲದಿದ್ದರೆ ನನ್ನ ಮುಖವನ್ನು ನೋಡಲೇಕೂಡದೆಂದು ಹೇಳಿದ್ದಾನೆ ಅಂದನು.",
			"6": "<sup>6</sup> ಅದಕ್ಕೆ ಇಸ್ರಾಯೇಲನು - ಇನ್ನೊಬ್ಬ ತಮ್ಮನಿದ್ದಾನೆಂದು ಆ ಮನುಷ್ಯನಿಗೆ ಹೇಳಿ ನೀವು ಯಾಕೆ ನನಗೆ ಈ ಅಪಕಾರ ಮಾಡಿದಿರಿ ಎಂದು ಹೇಳಲು ಅವರು -",
			"7": "<sup>7</sup> ನಮ್ಮ ವಿಷಯದಲ್ಲಿಯೂ ನಮ್ಮ ಮನೆಯವರ ವಿಷಯದಲ್ಲಿಯೂ ಆ ಮನುಷ್ಯನು ಸೂಕ್ಷ್ಮವಾಗಿ ವಿಚಾರಿಸಿ, ನಿಮ್ಮ ತಂದೆಯು ಇನ್ನೂ ಜೀವದಿಂದಿದ್ದಾನೋ? ನಿಮಗೆ ತಮ್ಮನಿದ್ದಾನೋ? ಎಂದು ಕೇಳಿದನು. ಆ ಪ್ರಶ್ನೆಗಳಿಗೆ ತಕ್ಕಂತೆ ಉತ್ತರಕೊಟ್ಟೆವು. ಅವನು - ನಿಮ್ಮ ತಮ್ಮನನ್ನು ಕರೆದುಕೊಂಡು ಬನ್ನಿರಿ ಎಂದು ಹೇಳುವನೆಂಬದು ನಮಗೆ ಹೇಗೆ ತಿಳಿಯುವದು ಅಂದರು.",
			"8": "<sup>8</sup> ಆಗ ಯೆಹೂದನು ತನ್ನ ತಂದೆಯಾದ ಇಸ್ರಾಯೇಲನಿಗೆ - ನೀನೂ ನಾವೂ ನಮ್ಮ ಮಕ್ಕಳೂ ಎಲ್ಲರೂ ಸಾಯದೆ ಬದುಕಿಕೊಳ್ಳುವಂತೆ ಆ ಹುಡುಗನನ್ನು ನನ್ನ ಜೊತೆಯಲ್ಲಿ ಕಳುಹಿಸಿಕೊಡಪ್ಪಾ; ನಾವು ಹೊರಟು ಹೋಗುವೆವು.",
			"9": "<sup>9</sup> ನಾನೇ ಅವನಿಗೆ ಹೊಣೆ; ಅವನ ವಿಷಯ ನನ್ನನ್ನೇ ಕೇಳಬಹುದು. ನಾನು ಅವನನ್ನು ತಿರಿಗಿ ಕರಕೊಂಡು ಬಂದು ನಿನ್ನೆದುರಿನಲ್ಲಿ ನಿಲ್ಲಿಸದೆ ಹೋದರೆ ಆ ದೋಷ ಎಂದೆಂದಿಗೂ ನನ್ನ ಮೇಲೆ ಇರಲಿ.",
			"10": "<sup>10</sup> ನಾವು ತಡಮಾಡದಿದ್ದರೆ ಇಷ್ಟರೊಳಗೆ ಎರಡನೆಯ ಸಾರಿ ಹೋಗಿ ಬರುತ್ತಿದ್ದೆವು ಎಂದು ಹೇಳಿದನು.",
			"11": "<sup>11</sup> ಅದಕ್ಕೆ ಅವರ ತಂದೆಯಾದ ಇಸ್ರಾಯೇಲನು ಅವರಿಗೆ - ನೀವು ಹೋಗಲೇಬೇಕಾಗಿದ್ದರೆ ಒಂದು ಕೆಲಸ ಮಾಡಿರಿ; ಈ ದೇಶದಲ್ಲಿ ದೊರಕುವ ಶ್ರೇಷ್ಠವಾದ ವಸ್ತುಗಳಲ್ಲಿ ಕೆಲವನ್ನು ನಿಮ್ಮ ಸಾಮಾನಿನಲ್ಲಿಟ್ಟು, ಆ ಮನುಷ್ಯನಿಗೆ ಕಾಣಿಕೆಯಾಗಿ ತೆಗೆದುಕೊಂಡು ಹೋಗಿರಿ; ಸ್ವಲ್ಪ ತೈಲ, ಸ್ವಲ್ಪ ದ್ರಾಕ್ಷಾರಸದ ಕಾಕಂಬಿ, ಹಾಲುಮಡ್ಡಿ, ರಕ್ತಬೋಳ, ಆಕ್ರೋಡು, ಬಾದಾಮಿ ಇವುಗಳನ್ನು ತೆಗೆದುಕೊಂಡುಹೋಗಿ ಕೊಡಿರಿ.",
			"12": "<sup>12</sup> ಅದಲ್ಲದೆ ಎರಡರಷ್ಟು ಹಣವನ್ನು ಕೈಯಲ್ಲಿ ತೆಗೆದುಕೊಂಡು ಹೋಗಿರಿ; ಅವರು ನಿಮ್ಮ ಚೀಲಗಳ ಬಾಯಲ್ಲಿ ಹಾಕಿ ಹಿಂದಕ್ಕೆ ಕಳುಹಿಸಿದ ಹಣವನ್ನು ತೆಗೆದುಕೊಂಡು ಹೋಗಿರಿ; ಒಂದು ವೇಳೆ ಅದು ಅವರಿಗೆ ತಿಳಿಯದೆ ನಿಮ್ಮ ಕೈಗೆ ಬಂದಿರಬಹುದು.",
			"13": "<sup>13</sup> ನಿಮ್ಮ ತಮ್ಮನನ್ನೂ ಕರೆದುಕೊಂಡು ಹೊರಟು ತಿರಿಗಿ ಆ ಮನುಷ್ಯನ ಬಳಿಗೆ ಹೋಗಿರಿ.",
			"14": "<sup>14</sup> ಅವನು ನಿಮ್ಮ ಮೇಲೆ ಕನಿಕರವಿಟ್ಟು ನಿಮ್ಮ ಅಣ್ಣನನ್ನೂ ಬೆನ್ಯಾಮೀನನನ್ನೂ ನಿಮ್ಮೊಂದಿಗೆ ಕಳುಹಿಸಿ ಬಿಡುವಂತೆ ಸರ್ವಶಕ್ತನಾದ ದೇವರು ಅನುಗ್ರಹಿಸಲಿ; ನಾನಂತೂ ಮಕ್ಕಳನ್ನು ಕಳಕೊಂಡವನಾಗಬೇಕಾದರೆ ಹಾಗೆಯೇ ಆಗಲಿ ಎಂದು ಹೇಳಿದನು.",
			"15": "<sup>15</sup> ಆ ಮನುಷ್ಯರು ಆ ಕಾಣಿಕೆಯ ವಸ್ತುಗಳನ್ನು ಸಿದ್ಧಪಡಿಸಿ ಎರಡರಷ್ಟು ಹಣವನ್ನು ತೆಗೆದುಕೊಂಡು ಬೆನ್ಯಾಮೀನನನ್ನು ತಮ್ಮ ಜೊತೆಯಲ್ಲಿ ಕರೆದುಕೊಂಡು ಹೊರಟು ಐಗುಪ್ತದೇಶಕ್ಕೆ ಸೇರಿ ಯೋಸೇಫನೆದುರಿನಲ್ಲಿ ನಿಂತುಕೊಂಡರು.",
			"16": "<sup>16</sup> ಬೆನ್ಯಾಮೀನನು ಅವರ ಸಂಗಡ ಇರುವದನ್ನು ಯೋಸೇಫನು ಕಂಡು ತನ್ನ ಮನೆವಾರ್ತೆಯವನನ್ನು ಕರಸಿ - ಈ ಮನುಷ್ಯರು ಈ ಹೊತ್ತು ಮಧ್ಯಾಹ್ನ ನನ್ನ ಸಂಗಡ ಊಟಮಾಡಬೇಕಾಗಿದೆ; ಇವರನ್ನು ಮನೆಯೊಳಗೆ ಕರೆದುಕೊಂಡು ಹೋಗಿ ಪಶುವನ್ನು ಕೊಯಿಸಿ ಎಲ್ಲಾ ಸಿದ್ಧಪಡಿಸು ಎಂದು ಅಪ್ಪಣೆಕೊಟ್ಟನು.",
			"17": "<sup>17</sup> ಅಪ್ಪಣೆಯ ಮೇರೆಗೆ ಮನೆವಾರ್ತೆಯವನು ಅವರನ್ನು ಯೋಸೇಫನ ಮನೆಯೊಳಕ್ಕೆ ಕರೆದುಕೊಂಡು ಹೋಗಲು ಅವರು ಅದಕ್ಕೆ ಭಯಪಟ್ಟು -",
			"18": "<sup>18</sup> ಮುಂಚೆ ನಾವು ಚೀಲಗಳಲ್ಲಿ ಹಿಂದಕ್ಕೆ ತೆಗೆದುಕೊಂಡು ಹೋದ ಹಣದ ನಿಮಿತ್ತವೇ ಅವನು ನಮ್ಮನ್ನು ತನ್ನ ಮನೆಯೊಳಗೆ ಕರೆಸಿದ್ದಾನೆ; ನಮ್ಮ ಮೇಲೆ ಫಕ್ಕನೆ ಬಿದ್ದು ನಮ್ಮನ್ನು ಕತ್ತೆಗಳ ಸಹಿತವಾಗಿ ಹಿಡಿಸಿ ಗುಲಾಮರನ್ನಾಗಿ ಮಾಡಿಕೊಳ್ಳಬೇಕೆಂದಿದ್ದಾನೆ ಎಂಬದಾಗಿ ತಮ್ಮ ತಮ್ಮೊಳಗೆ ಮಾತಾಡಿಕೊಂಡು",
			"19": "<sup>19</sup> ಮನೇಬಾಗಲಲ್ಲಿದ್ದ ಮನೆವಾರ್ತೆಯವನ ಬಳಿಗೆ ಹೋಗಿ ಅವನಿಗೆ -",
			"20": "<sup>20</sup> ಅಯ್ಯಾ, ಮೊದಲು ಒಂದು ಸಾರಿ ಆಹಾರಪದಾರ್ಥಗಳನ್ನು ಕೊಂಡುಕೊಳ್ಳುವದಕ್ಕೆ ಬಂದಿದ್ದೆವು;",
			"21": "<sup>21</sup> ನಾವು ಹೊರಟುಹೋದ ನಂತರ ಇಳಿದುಕೊಂಡಿದ್ದ ಒಂದು ಸ್ಥಳದಲ್ಲಿ ನಮ್ಮ ಚೀಲಗಳನ್ನು ಬಿಚ್ಚಿ ನೋಡಿದಾಗ ಪ್ರತಿಯೊಬ್ಬನ ಹಣವು ತೂಕದಲ್ಲಿ ಏನೂ ಕಡಿಮೆಯಿಲ್ಲದೆ ಅವನವನ ಚೀಲದಲ್ಲೇ ಇತ್ತು; ಅದನ್ನು ತಿರಿಗಿ ತಂದಿದ್ದೇವೆ.",
			"22": "<sup>22</sup> ದವಸವನ್ನು ಕೊಂಡುಕೊಳ್ಳುವದಕ್ಕೆ ಬೇರೆ ಹಣವನ್ನೂ ತಂದಿದ್ದೇವೆ. ಆ ಹಣವನ್ನು ನಮ್ಮ ಚೀಲಗಳಲ್ಲಿ ಯಾರು ಇಟ್ಟರೋ ನಮಗೆ ತಿಳಿಯದು ಎಂದು ಹೇಳಿದರು.",
			"23": "<sup>23</sup> ಅದಕ್ಕೆ ಅವನು - ಸಮಾಧಾನವಾಗಿರ್ರಿ. ನೀವೇನೂ ಹೆದರಬೇಡಿರಿ; ನಿಮ್ಮ ತಂದೆಗೂ ನಿಮಗೂ ದೇವರಾಗಿರುವಾತನು ನಿಮ್ಮ ಚೀಲಗಳಲ್ಲಿಯೇ ನಿಕ್ಷೇಪ ಸಿಕ್ಕುವಂತೆ ಅನುಗ್ರಹಿಸಿದ್ದಾನೆ; ನಿಮ್ಮ ಹಣವು ನನಗೆ ಮುಟ್ಟಿತು ಎಂದು ಹೇಳಿ ಸಿಮೆಯೋನನನ್ನು ಅವರ ಬಳಿಗೆ ಕರೆದುಕೊಂಡು ಬಂದು",
			"24": "<sup>24</sup> ಅವರೆಲ್ಲರನ್ನೂ ಯೋಸೇಫನ ಮನೆಯೊಳಗೆ ಸೇರಿಸಿ ಕಾಲುಗಳನ್ನು ತೊಳೆಯುವದಕ್ಕೆ ನೀರನ್ನು ಕೊಡಿಸಿ ಅವರ ಕತ್ತೆಗಳಿಗೆ ಮೇವು ಹಾಕಿಸಿದನು.",
			"25": "<sup>25</sup> ತಾವು ಅಲ್ಲೇ ಊಟ ಮಾಡಬೇಕೆಂಬುವ ಸಂಗತಿಯನ್ನು ಅವರು ಕೇಳಿದ್ದರಿಂದ ತಾವು ತಂದಿದ್ದ ಕಾಣಿಕೆಯನ್ನು ಸಿದ್ಧವಾಗಿ ಇಟ್ಟುಕೊಂಡು ಯೋಸೇಫನು ಮಧ್ಯಾಹ್ನದಲ್ಲಿ ಬರುವ ತನಕ ಕಾದು ಕೊಂಡಿದ್ದರು.",
			"26": "<sup>26</sup> ಯೋಸೇಫನು ಮನೆಗೆ ಬಂದಾಗ ಅವರು ಕಾಣಿಕೆಯನ್ನು ಮನೆಯೊಳಕ್ಕೆ ತಂದುಕೊಟ್ಟು ಅವನ ಮುಂದೆ ಅಡ್ಡಬಿದ್ದರು.",
			"27": "<sup>27</sup> ಅವನು ಅವರ ಯೋಗಕ್ಷೇಮವನ್ನು ವಿಚಾರಿಸಿ - ನೀವು ಹೇಳಿದ ಮುದುಕನಾಗಿರುವ ನಿಮ್ಮ ತಂದೆಗೆ ಕ್ಷೇಮವೋ? ಅವನು ಇನ್ನೂ ಬದುಕಿದ್ದಾನೋ ಎಂದು ಕೇಳಲು ಅವರು ಬೊಗ್ಗಿ ನಮಸ್ಕಾರಮಾಡಿ -",
			"28": "<sup>28</sup> ನಿನ್ನ ಸೇವಕನಾದ ನಮ್ಮ ತಂದೆಗೆ ಕ್ಷೇಮ; ಅವನು ಇನ್ನೂ ಇದ್ದಾನೆ ಅಂದರು.",
			"29": "<sup>29</sup> ಆಗ ಅವನು ಕಣ್ಣೆತ್ತಿ ತನ್ನ ಒಡಹುಟ್ಟಿದ ತಮ್ಮನಾದ ಬೆನ್ಯಾಮೀನನನ್ನು ನೋಡಿ - ನೀವು ಹೇಳಿದ ನಿಮ್ಮ ಕಿರೀ ತಮ್ಮನು ಇವನೋ ಎಂದು ಕೇಳಿ ಅವನಿಗೆ - ಮಗನೇ, ದೇವರ ದಯೆ ನಿನ್ನ ಮೇಲಿರಲಿ ಎಂದು ಹೇಳಿದನು.",
			"30": "<sup>30</sup> ಯೋಸೇಫನಿಗೆ ತಮ್ಮನ ಮೇಲೆ ಕರುಳು ಮರುಗಿದ್ದರಿಂದ ಅವನು ಬೇಗನೆ ಎದ್ದು ಅಳುವದಕ್ಕೆ ಸ್ಥಳವನ್ನು ನೋಡಿಕೊಂಡು ತನ್ನ ಕೋಣೆಯೊಳಗೆ ಹೋಗಿ ಅಲ್ಲಿ ಅತ್ತನು.",
			"31": "<sup>31</sup> ಆಮೇಲೆ ಮುಖವನ್ನು ತೊಳಕೊಂಡು ಹೊರಗೆ ಬಂದು ಮನಸ್ಸನ್ನು ಬಿಗಿಹಿಡಿದು ಊಟಕ್ಕೆ ಬಡಿಸಿರಿ ಎಂದು ಅಪ್ಪಣೆ ಕೊಟ್ಟನು.",
			"32": "<sup>32</sup> ಪರಿಚಾರಕರು ಅವನಿಗೂ ಅವನ ಅಣ್ಣತಮ್ಮಂದಿರಿಗೂ ಅವನ ಸಂಗಡವಿದ್ದ ಐಗುಪ್ತ್ಯರಿಗೂ ಬೇರೆಬೇರೆಯಾಗಿ ಊಟಕ್ಕೆ ಬಡಿಸಿದರು. ಐಗುಪ್ತ್ಯರು ಇಬ್ರಿಯರ ಸಹಪಂಕ್ತಿಯಲ್ಲಿ ಊಟಮಾಡುವದಿಲ್ಲ; ಅದು ಅವರಿಗೆ ಅಸಹ್ಯ.",
			"33": "<sup>33</sup> ಯೋಸೇಫನು ತನ್ನ ಅಣ್ಣತಮ್ಮಂದಿರನ್ನು ಹಿರಿಯವನು ಮೊದಲುಗೊಂಡು ಕಿರಿಯವನವರೆಗೂ ಅವರವರ ವಯಸ್ಸಿನ ಪ್ರಕಾರವೇ ಕುಳ್ಳಿರಿಸಿದ್ದರಿಂದ ಅವರು ಒಬ್ಬರನ್ನೊಬ್ಬರು ನೋಡುತ್ತಾ ಆಶ್ಚರ್ಯಪಟ್ಟರು.",
			"34": "<sup>34</sup> ಯೋಸೇಫನು ತನ್ನ ಮುಂದೆ ಬಡಿಸಿದ್ದ ಪದಾರ್ಥಗಳಲ್ಲಿ ಅವರಿಗೆ ಭಾಗಗಳನ್ನು ಕಳುಹಿಸಿದನು; ಆದರೆ ಬೆನ್ಯಾಮೀನನಿಗೆ ಬಂದ ಭಾಗವು ಮಿಕ್ಕಾದವರ ಭಾಗಗಳಿಗಿಂತ ಐದರಷ್ಟು ಹೆಚ್ಚಾಗಿತ್ತು. ಅವರು ಯಥೇಚ್ಫವಾಗಿ ಪಾನಮಾಡಿ ಅವನ ಸಂಗಡ ಸಂಭ್ರಮದಿಂದಿದ್ದರು."
		},
		"44": {
			"1": "<sup>1</sup> ತರುವಾಯ ಯೋಸೇಫನು ತನ್ನ ಮನೆವಾರ್ತೆಯವನಿಗೆ - ಆ ಮನುಷ್ಯರ ಚೀಲಗಳಲ್ಲಿ ಹೊರುವಷ್ಟು ಧಾನ್ಯವನ್ನು ತುಂಬಿಸಿ ಒಬ್ಬೊಬ್ಬನ ಚೀಲದ ಬಾಯಲ್ಲಿ",
			"2": "<sup>2</sup> ಅವನವನ ಹಣದ ಗಂಟನ್ನಿಟ್ಟು ಕಿರಿಯವನ ಚೀಲದ ಬಾಯೊಳಗೆ ಅವನು ದವಸಕ್ಕೆ ತಂದ ಹಣವನ್ನಲ್ಲದೆ ನನ್ನ ಬೆಳ್ಳಿಯ ಪಾನಪಾತ್ರೆಯನ್ನೂ ಇಡಬೇಕೆಂದು ಅಪ್ಪಣೆಕೊಡಲು ಮನೆವಾರ್ತೆಯವನು ಹಾಗೆಯೇ ಮಾಡಿದನು.",
			"3": "<sup>3</sup> ಬೆಳಿಗ್ಗೆ ಹೊತ್ತು ಮೂಡುವಾಗ ಆ ಮನುಷ್ಯರು ಅಪ್ಪಣೆ ಪಡೆದು ಕತ್ತೆಗಳನ್ನು ಹೊಡಕೊಂಡು ಹೊರಟು ಹೋದರು.",
			"4": "<sup>4</sup> ಅವರು ಪಟ್ಟಣವನ್ನು ಬಿಟ್ಟು ಸ್ವಲ್ಪದೂರ ಹೋಗುವಷ್ಟರಲ್ಲಿ ಯೋಸೇಫನು ತನ್ನ ಮನೆವಾರ್ತೆಯವನಿಗೆ - ನೀನೆದ್ದು ಆ ಮನುಷ್ಯರನ್ನು ಹಿಂದಟ್ಟಿ ಸಂಧಿಸಿ - ನೀವು ಉಪಕಾರಕ್ಕೆ ಪ್ರತಿಯಾಗಿ ಯಾಕೆ ಅಪಕಾರ ಮಾಡಿದ್ದೀರಿ?",
			"5": "<sup>5</sup> ಆ ಪಾತ್ರೆಯಲ್ಲಿ ನನ್ನ ದಣಿಯು ಪಾನಮಾಡುತ್ತಾನಲ್ಲವೇ. ಅದರಲ್ಲಿ ನೋಡಿ ಶಕುನ ಹೇಳುವದಿಲ್ಲವೇ. ನೀವು ಮಾಡಿದ್ದು ಬಹಳ ಕೆಟ್ಟದ್ದು ಅನ್ನಬೇಕೆಂದು ಹೇಳಿದನು.",
			"6": "<sup>6</sup> ಮನೆವಾರ್ತೆಯವನು ಅವರನ್ನು ಹಿಂದಟ್ಟಿ ಸಂಧಿಸಿ ಅವರಿಗೆ ಆ ಮಾತುಗಳನ್ನು ಹೇಳಿದಾಗ",
			"7": "<sup>7</sup> ಅವರು ಅವನಿಗೆ - ನಮ್ಮ ಸ್ವಾಮಿಯು ಇಂಥಾ ಮಾತುಗಳನ್ನು ಹೇಳುವದೇನು! ನಿನ್ನ ಸೇವಕರು ಇಂಥ ಕೃತ್ಯವನ್ನು ಎಂದಿಗೂ ಮಾಡುವವರಲ್ಲ.",
			"8": "<sup>8</sup> ಮೊದಲು ನಮ್ಮ ಚೀಲಗಳ ಬಾಯಲ್ಲಿ ನಮಗೆ ಸಿಕ್ಕಿದ ಹಣವನ್ನು ನಾವು ಕಾನಾನ್‍ದೇಶದಿಂದ ತಿರಿಗಿ ತಂದು ನಿನಗೆ ಕೊಟ್ಟೆವಲ್ಲವೇ. ಹೀಗಿರುವಾಗ ನಾವು ನಿನ್ನ ದಣಿಯ ಮನೆಯೊಳಗಿಂದ ಬೆಳ್ಳಿಬಂಗಾರವನ್ನು ಹೇಗೆ ಕದ್ದುಕೊಂಡೇವು.",
			"9": "<sup>9</sup> ಆ ಪಾತ್ರೆಯು ನಿನ್ನ ಸೇವಕರೊಳಗೆ ಯಾರ ಬಳಿಯಲ್ಲಿ ಸಿಕ್ಕುತ್ತದೋ ಅವನು ಮರಣದಂಡನೆಯನ್ನು ಹೊಂದಲಿ; ಅದಲ್ಲದೆ ನಾವೆಲ್ಲರೂ ನಮ್ಮ ಸ್ವಾಮಿಗೆ ದಾಸರಾಗುವೆವು ಅಂದರು.",
			"10": "<sup>10</sup> ಅದಕ್ಕೆ ಅವನು - ಒಳ್ಳೇದು, ನೀವು ಹೇಳಿದಂತೆ ಆಗಲಿ; ಆ ಪಾತ್ರೆ ಯಾವನ ಬಳಿಯಲ್ಲಿ ಸಿಕ್ಕುತ್ತದೋ ಅವನು ನನಗೆ ದಾಸನಾಗಬೇಕು; ಉಳಿದವರು ತಪ್ಪಿಲ್ಲದವರು ಎಂದು ಹೇಳಿದಾಗ,",
			"11": "<sup>11</sup> ಅವರು ತ್ವರೆಪಟ್ಟು ಪ್ರತಿಯೊಬ್ಬನೂ ತನ್ನ ತನ್ನ ಚೀಲವನ್ನು ನೆಲಕ್ಕಿಳಿಸಿ ಬಿಚ್ಚಿದರು.",
			"12": "<sup>12</sup> ಅವನು ಹಿರಿಯವನಿಂದ ಹಿಡಿದು ಕಿರಿಯವನ ತನಕ ಪರೀಕ್ಷಿಸಿ ನೋಡಿದನು. ಆ ಪಾತ್ರೆಯು ಬೆನ್ಯಾಮೀನನ ಚೀಲದಲ್ಲಿ ಸಿಕ್ಕಿತು.",
			"13": "<sup>13</sup> ಸಿಕ್ಕಿದಾಗ ಅವರು [ದುಃಖಾಕ್ರಾಂತರಾಗಿ] ತಮ್ಮ ಬಟ್ಟೆಗಳನ್ನು ಹರಿದುಕೊಂಡು ಕತ್ತೆಗಳ ಮೇಲೆ ಚೀಲಗಳನ್ನು ಹೇರಿ ಪಟ್ಟಣಕ್ಕೆ ಹಿಂದಿರುಗಿ ಬಂದರು.",
			"14": "<sup>14</sup> ಯೆಹೂದನೂ ಅವನ ಅಣ್ಣತಮ್ಮಂದಿರೂ ಯೋಸೇಫನ ಮನೆಗೆ ಬಂದಾಗ ಅವನು ಇನ್ನೂ ಅಲ್ಲೇ ಇದ್ದನು.",
			"15": "<sup>15</sup> ಅವರು ಅವನೆದುರಿಗೆ ಅಡ್ಡಬೀಳಲು ಯೋಸೇಫನು ಅವರನ್ನು - ನೀವು ಮಾಡಿರುವ ಈ ಕೃತ್ಯವು ಏನು! ನನ್ನಂಥವನು ಶಕುನ ನೋಡಿ ಗುಟ್ಟನ್ನು ಬಿಚ್ಚುವನೆಂಬದು",
			"16": "<sup>16</sup> ನಿಮಗೆ ತಿಳಿಯಲಿಲ್ಲವೋ ಎಂದು ಕೇಳಲು ಯೆಹೂದನು - ನಾವು ನಮ್ಮ ಸ್ವಾಮಿಗೆ ಏನು ಹೇಳೋಣ? ನಾವು ಹೇಗೆ ಮಾತಾಡೇವು? ನಾವು ನಿರ್ದೋಷಿಗಳಾಗಿ ತೋರುವಂತೆ ಏನು ಮಾಡಬೇಕು? ನಿನ್ನ ಸೇವಕರ ಪಾಪಕೃತ್ಯವನ್ನು ದೇವರು ಹೊರಪಡಿಸಿದ್ದಾನೆ. ಈ ಪಾತ್ರೆಯು ಯಾರ ಬಳಿಯಲ್ಲಿ ಸಿಕ್ಕಿತೋ ಅವನು ಮಾತ್ರವಲ್ಲದೆ ನಾವೆಲ್ಲರೂ ನಮ್ಮ ಸ್ವಾಮಿಗೆ ಗುಲಾಮರಾದೆವಲ್ಲವೇ ಅನ್ನಲು ಯೋಸೇಫನು -",
			"17": "<sup>17</sup> ಹಾಗೆ ಎಂದಿಗೂ ಆಗಬಾರದು; ಈ ಪಾತ್ರೆಯು ಯಾರ ಬಳಿಯಲ್ಲಿ ಸಿಕ್ಕಿತೋ ಅವನು ಮಾತ್ರವೇ ನನಗೆ ಗುಲಾಮನಾಗಬೇಕು; ನೀವಾದರೋ ಅಡ್ಡಿಯಿಲ್ಲದೆ ನಿಮ್ಮ ತಂದೆಯ ಬಳಿಗೆ ಹೋಗಬಹುದು ಅಂದನು.",
			"18": "<sup>18</sup> ಆಗ ಯೆಹೂದನು ಹತ್ತರಕ್ಕೆ ಬಂದು - ನಿನ್ನ ಸೇವಕನಾದ ನಾನು ಒಂದು ಮಾತನ್ನು ಅರಿಕೆಮಾಡಿಕೊಳ್ಳುತ್ತೇನೆ, ನನ್ನ ಮೇಲೆ ಸಿಟ್ಟು ಮಾಡಬಾರದು; ನೀನು ಫರೋಹನಿಗೆ ಸಮಾನನಾಗಿದ್ದೀಯೆಂದು ನಾನು ಬಲ್ಲೆ.",
			"19": "<sup>19</sup> ಸ್ವಾಮಿಯು ಸೇವಕರನ್ನು - ನಿಮಗೆ ತಂದೆಯಾಗಲಿ ತಮ್ಮನಾಗಲಿ ಇದ್ದಾನೋ ಎಂದು ಕೇಳಲು ನಾವು -",
			"20": "<sup>20</sup> ತಂದೆಯಿದ್ದಾನೆ, ಅವನು ಮುದುಕನು; ಅವನಿಗೆ ಮುಪ್ಪಿನಲ್ಲಿ ಹುಟ್ಟಿದ ಒಬ್ಬ ಚಿಕ್ಕ ಹುಡುಗನೂ ಇದ್ದಾನೆ. ಅವನ ಒಡಹುಟ್ಟಿದವನು ಸತ್ತು ಹೋದನು; ಅವನ ತಾಯಿಯಲ್ಲಿ ಹುಟ್ಟಿದವರೊಳಗೆ ಅವನೊಬ್ಬನೇ ಉಳಿದಿದ್ದಾನೆ, ಅವನ ಮೇಲೆ ತಂದೆಗೆ ಬಹಳ ಪ್ರೀತಿಯುಂಟು ಎಂದು ಹೇಳಿದೆವು.",
			"21": "<sup>21</sup> ಅದಕ್ಕೆ ನೀನು ಸೇವಕರಾದ ನಮಗೆ - ನಾನು ಆ ಹುಡುಗನನ್ನು ನೋಡಬೇಕು, ಅವನನ್ನು ನನ್ನ ಬಳಿಗೆ ಕರಕೊಂಡು ಬನ್ನಿರಿ ಎಂದು ಅಪ್ಪಣೆಕೊಡಲು",
			"22": "<sup>22</sup> ನಾವು ಸ್ವಾಮಿಯವರಿಗೆ - ಆ ಹುಡುಗನು ತನ್ನ ತಂದೆಯನ್ನು ಅಗಲುವದಕ್ಕಾಗುವದಿಲ್ಲ; ಅಗಲಿದರೆ ತಂದೆ ಸತ್ತಾನು ಅಂದೆವು.",
			"23": "<sup>23</sup> ಅದಕ್ಕೆ - ನಿಮ್ಮ ತಮ್ಮನು ಸಂಗಡ ಬಾರದಿದ್ದರೆ ನೀವು ಇನ್ನೊಂದು ಸಾರಿ ನನ್ನ ಮುಖವನ್ನು ನೋಡಕೂಡದು ಎಂದು ಅಪ್ಪಣೆಕೊಟ್ಟಿಯಷ್ಟೆ.",
			"24": "<sup>24</sup> ನಾವು ನಿನ್ನ ಸೇವಕನಾದ ನಮ್ಮ ತಂದೆಯ ಬಳಿಗೆ ಬಂದಾಗ ಸ್ವಾಮಿಯ ಮಾತನ್ನು ತಿಳಿಸಿದೆವು.",
			"25": "<sup>25</sup> ನಮ್ಮ ತಂದೆ - ನೀವು ತಿರಿಗಿ ಹೋಗಿ ನಮಗೆ ಧಾನ್ಯವನ್ನು ಕೊಂಡುಕೊಂಡು ಬನ್ನಿರಿ ಎಂದು ಹೇಳಿದಾಗ ನಾವು ಆತನಿಗೆ - ಹೇಗೆ ಹೋಗುವದು?",
			"26": "<sup>26</sup> ತಮ್ಮನು ನಮ್ಮ ಜೊತೆಯಲ್ಲಿ ಇಲ್ಲವಾದರೆ ಆ ಮನುಷ್ಯನ ಮುಖವನ್ನು ನೋಡಲಾಗದು; ತಮ್ಮನು ನಮ್ಮ ಸಂಗಡ ಬಂದರೆ ಹೋಗುತ್ತೇವೆ ಎಂದು ಹೇಳಿದ್ದಕ್ಕೆ ನಿನ್ನ ಸೇವಕನಾದ",
			"27": "<sup>27</sup> ನಮ್ಮ ತಂದೆಯು - ನನ್ನ ಪತ್ನಿಯಲ್ಲಿ ನನಗೆ ಇಬ್ಬರೇ ಗಂಡುಮಕ್ಕಳು ಹುಟ್ಟಿದರೆಂಬದು ನಿಮಗೆ ತಿಳಿದೇ ಇದೆಯಷ್ಟೆ;",
			"28": "<sup>28</sup> ಅವರಲ್ಲಿ ಒಬ್ಬನು ನನ್ನ ಬಳಿಯಿಂದ ಹೊರಟುಹೋದನು; ಅವನು ಸಂದೇಹವಿಲ್ಲದೆ ಮೃಗದಿಂದ ಸೀಳಿಹಾಕಲ್ಪಟ್ಟಿರಬೇಕೆಂದು ತಿಳುಕೊಂಡೆನು; ಅಂದಿನಿಂದ ನಾನು ಅವನನ್ನು ಕಾಣಲಿಲ್ಲ.",
			"29": "<sup>29</sup> ಈಗ ಇವನನ್ನೂ ನನ್ನ ಬಳಿಯಿಂದ ತೆಗೆದುಕೊಂಡು ಹೋಗಬೇಕೆಂದಿದ್ದೀರಿ. ಇವನಿಗೂ ಕೇಡು ಬಂದರೆ ಈ ಮುದಿತಲೆ ದುಃಖದಿಂದಲೇ ಪಾತಾಳಕ್ಕೆ ಸೇರಲು ನೀವು ಕಾರಣರಾಗುವಿರಿ ಅಂದನು.",
			"30": "<sup>30</sup> ನಮ್ಮ ತಂದೆಯ ಪ್ರಾಣವೂ ಇವನ ಪ್ರಾಣವೂ ಒಂದೇಯಾಗಿರುವದರಿಂದ ನಾನು ನಿನ್ನ ಸೇವಕನಾದ ನನ್ನ ತಂದೆಯ ಬಳಿಗೆ ಸೇರಿದಾಗ ಈ ಹುಡುಗನು ಸಂಗಡ ಇಲ್ಲದೆ ಹೋದರೆ ಹುಡುಗನು ಬರಲಿಲ್ಲವೆಂದು ಅವನು ತಿಳಿದಕೂಡಲೇ ಸಾಯುವನು.",
			"31": "<sup>31</sup> ಹೀಗೆ ಸ್ವಾಮಿಯ ಸೇವಕರಾದ ನಾವು ನಿನ್ನ ಸೇವಕನಾದ ನಮ್ಮ ತಂದೆಯ ಮುದಿತಲೆ ದುಃಖದಿಂದಲೇ ಪಾತಾಳಕ್ಕೆ ಸೇರಲು ಕಾರಣರಾಗುವೆವು.",
			"32": "<sup>32</sup> ಸೇವಕನಾದ ನಾನು ನನ್ನ ತಂದೆಯ ಬಳಿಯಲ್ಲಿ ಈ ಹುಡುಗನಿಗೆ ಹೊಣೆಯಾಗಿ - ನಾನು ಇವನನ್ನು ನಿನ್ನ ಬಳಿಗೆ ಕರೆದುಕೊಂಡು ಬಾರದಿದ್ದರೆ ತಂದೆಗೆ ತಪ್ಪಿದವನ ದೋಷ ಎಂದೆಂದಿಗೂ ನನಗಿರಲಿ ಎಂದು ಮಾತು ಕೊಟ್ಟೆನು.",
			"33": "<sup>33</sup> ಆದದರಿಂದ ಸೇವಕನಾದ ನಾನು ಈ ಹುಡುಗನಿಗೆ ಬದಲಾಗಿ ಸ್ವಾಮಿಗೆ ಗುಲಾಮನಾಗುವಂತೆಯೂ ಇವನು ತನ್ನ ಅಣ್ಣಂದಿರ ಸಂಗಡ ಹೋಗುವಂತೆಯೂ ಅನುಗ್ರಹ ಮಾಡಬೇಕೆಂದು ನಿನ್ನನ್ನು ಬೇಡಿಕೊಳ್ಳುತ್ತೇನೆ.",
			"34": "<sup>34</sup> ಈ ಹುಡುಗನನ್ನು ಬಿಟ್ಟು ನಾನು ನನ್ನ ತಂದೆಯ ಬಳಿಗೆ ಹೇಗೆ ಹೋಗುವದಕ್ಕಾದೀತು? ತಂದೆಗೆ ಮಹಾ ಶೋಕವುಂಟಾಗುವದನ್ನು ನಾನು ನೋಡಕೂಡದು ಅಂದನು."
		},
		"45": {
			"1": "<sup>1</sup> ಯೋಸೇಫನು ಇದನ್ನು ಕೇಳಿ ತನ್ನ ಎದುರಿನಲ್ಲಿದ್ದವರ ಮುಂದೆ ತಾಳಲಾರದೆ - ನನ್ನ ಬಳಿಯಿಂದ ಎಲ್ಲರನ್ನೂ ಆಚೆಗೆ ಕಳುಹಿಸಿರಿ ಎಂದು ಬಾಯಿಬಿಟ್ಟು ಹೇಳಿದನು. ಯೋಸೇಫನು ಅಣ್ಣತಮ್ಮಂದಿರಿಗೆ ತನ್ನ ಗುರುತು ಸಿಕ್ಕುವಂತೆ ಮಾಡುವ ವೇಳೆಯಲ್ಲಿ ಇತರರು ಯಾರೂ ಹತ್ತರ ಇರಲಿಲ್ಲ.",
			"2": "<sup>2</sup> ಅವನು ಗಟ್ಟಿಯಾಗಿ ಅತ್ತದ್ದರಿಂದ ಐಗುಪ್ತ್ಯರಿಗೂ ಫರೋಹನ ಮನೆಯವರಿಗೂ ಆ ಶಬ್ದವು ಕೇಳಿಸಿತು.",
			"3": "<sup>3</sup> ಅವನು ತನ್ನ ಅಣ್ಣಂದಿರಿಗೆ - ನಾನು ಯೋಸೇಫನು; ನನ್ನ ತಂದೆ ಇನ್ನೂ ಇದ್ದಾನೋ ಎಂದು ಹೇಳಲು ಅವರು ಅವನ ಮುಂದೆ ತತ್ತರಗೊಂಡು ಉತ್ತರಕೊಡಲಾರದೆ ಹೋದರು.",
			"4": "<sup>4</sup> ಯೋಸೇಫನು - ನನ್ನ ಹತ್ತರಕ್ಕೆ ಬನ್ನಿರಿ ಎಂದು ತನ್ನಣ್ಣಂದಿರಿಗೆ ಹೇಳಲು ಅವರು ಹತ್ತರಕ್ಕೆ ಬಂದರು. ಅವನು ಅವರಿಗೆ - ಐಗುಪ್ತದೇಶಕ್ಕೆ ಸೇರುವಂತೆ ನೀವು ಮಾರಿಬಿಟ್ಟ ನಿಮ್ಮ ತಮ್ಮನಾದ ಯೋಸೇಫನೇ ನಾನು.",
			"5": "<sup>5</sup> ನೀವು ನನ್ನನ್ನು ಮಾರಿದ್ದಕ್ಕಾಗಿ ವ್ಯಸನಪಟ್ಟು ದುಃಖಿಸಬೇಡಿರಿ. ಪ್ರಾಣಸಂರಕ್ಷಣೆಗಾಗಿ ದೇವರು ನನ್ನನ್ನು ನಿಮ್ಮ ಮುಂದೆ ಕಳುಹಿಸಿದನು.",
			"6": "<sup>6</sup> ದೇಶಕ್ಕೆ ಬರಬಂದು ಎರಡು ವರುಷವಾಗಿದೆಯಷ್ಟೆ; ಇನ್ನೂ ಐದು ವರುಷಗಳ ಪರ್ಯಂತರ ಬಿತ್ತುವದಕ್ಕಾಗಲಿ ಕೊಯ್ಯುವದಕ್ಕಾಗಲಿ ಅವಕಾಶವಿಲ್ಲ.",
			"7": "<sup>7</sup> ನಿಮ್ಮ ಜನವು ಭೂಮಿಯ ಮೇಲೆ ಉಳಿಯುವಂತೆಯೂ ನೀವು ಈ ವಿಪತ್ತಿಗೆ ಸಿಕ್ಕದೆ ಬಹುಜನವಾಗುವಂತೆಯೂ ದೇವರು ನನ್ನನ್ನು ನಿಮ್ಮ ಮುಂದೆ ಕಳುಹಿಸಿದನು.",
			"8": "<sup>8</sup> ದೇವರೇ ನನ್ನನ್ನು ಇಲ್ಲಿಗೆ ಕಳುಹಿಸಿದನಲ್ಲದೆ ನೀವು ಕಳುಹಿಸಲಿಲ್ಲ; ಆತನು ನನ್ನನ್ನು ಫರೋಹನ ಮಂತ್ರಿಯನ್ನಾಗಿಯೂ ಫರೋಹನ ಅರಮನೆಯಲ್ಲಿ ಅಧಿಪತಿಯನ್ನಾಗಿಯೂ ಐಗುಪ್ತದೇಶಕ್ಕೆ ಸರ್ವಾಧಿಕಾರಿಯನ್ನಾಗಿಯೂ ಮಾಡಿದ್ದಾನೆ.",
			"9": "<sup>9</sup> ನೀವು ಬೇಗ ನನ್ನ ತಂದೆಯ ಬಳಿಗೆ ಹೋಗಿ ಅವನಿಗೆ - ನಿನ್ನ ಮಗನಾದ ಯೋಸೇಫನು ಹೀಗನ್ನುತ್ತಾನೆ - ದೇವರು ನನ್ನನ್ನು ಐಗುಪ್ತದೇಶಕ್ಕೆ ಸರ್ವಾಧಿಕಾರಿಯನ್ನಾಗಿ ನೇಮಿಸಿದ್ದಾನೆ; ನೀನು ತಡಮಾಡದೆ ನನ್ನ ಬಳಿಗೆ ಬಾ;",
			"10": "<sup>10</sup> ಗೋಷೆನ್ ಸೀಮೆಯಲ್ಲಿ ನೀನು ವಾಸಮಾಡಬಹುದು; ನೀನೂ ನಿನ್ನ ಮಕ್ಕಳೂ ಮೊಮ್ಮಕ್ಕಳೂ ಕುರಿ ದನ ಮೊದಲಾದ ಸ್ವಾಸ್ತ್ಯಸಹಿತರಾಗಿ ನನ್ನ ಬಳಿಯಲ್ಲೇ ಇರಬಹುದು;",
			"11": "<sup>11</sup> ಈ ಬರಗಾಲ ತೀರುವದಕ್ಕೆ ಇನ್ನೂ ಐದು ವರುಷ ಹೋಗಬೇಕು; ಆದದರಿಂದ ನಿನಗೂ ನಿನ್ನ ಮನೆಯವರಿಗೂ ನಿನಗಿರುವ ಎಲ್ಲವುಗಳಿಗೂ ಬಡತನವುಂಟಾಗದಂತೆ ಇಲ್ಲಿ ನಿನ್ನನ್ನು ಪೋಷಿಸುವೆನೆಂದು ಹೇಳುತ್ತಾನೆಂಬದಾಗಿ ತಿಳಿಸಿರಿ.",
			"12": "<sup>12</sup> ನಾನು ಸ್ವತಃ ನಿಮ್ಮ ಸಂಗಡ ಮಾತಾಡಿದ್ದೇನಲ್ಲಾ; ಅದಕ್ಕೆ ನೀವೇ ಸಾಕ್ಷಿ, ನನ್ನ ಒಡಹುಟ್ಟಿದವನಾದ ಬೆನ್ಯಾಮೀನನೂ ಸಾಕ್ಷಿ.",
			"13": "<sup>13</sup> ಐಗುಪ್ತದೇಶದಲ್ಲಿ ನನಗಿರುವ ಎಲ್ಲಾ ವೈಭವವನ್ನೂ ನೀವು ಕಂಡದ್ದೆಲ್ಲವನ್ನೂ ತಂದೆಗೆ ತಿಳಿಸಿ ಬೇಗನೆ ಅವನನ್ನು ಇಲ್ಲಿಗೆ ಕರಕೊಂಡು ಬರಬೇಕೆಂದು ಹೇಳಿ",
			"14": "<sup>14</sup> ತನ್ನ ತಮ್ಮನಾದ ಬೆನ್ಯಾಮೀನನ ಕೊರಳನ್ನು ಅಪ್ಪಿಕೊಂಡು ಅತ್ತನು. ಬೆನ್ಯಾಮೀನನೂ ಅವನ ಕೊರಳನ್ನು ಅಪ್ಪಿಕೊಂಡು ಅತ್ತನು.",
			"15": "<sup>15</sup> ಇದಲ್ಲದೆ ಅವನು ತನ್ನ ಅಣ್ಣಂದಿರಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬನಿಗೂ ಮುದ್ದಿಟ್ಟು ಅವರನ್ನು ಅಪ್ಪಿಕೊಂಡು ಅತ್ತನು. ತರುವಾಯ ಅವರು ಅವನ ಸಂಗಡ ಮಾತಾಡಿದರು.",
			"16": "<sup>16</sup> ಯೋಸೇಫನ ಅಣ್ಣತಮ್ಮಂದಿರು ಬಂದ ಸಮಾಚಾರವು ಫರೋಹನ ಮನೆಯವರಿಗೆ ತಿಳಿದಾಗ ಫರೋಹನಿಗೂ ಅವನ ಪರಿವಾರದವರಿಗೂ ಸಂತೋಷವಾಯಿತು.",
			"17": "<sup>17</sup> ಫರೋಹನು ಯೋಸೇಫನನ್ನು ಕರಿಸಿ - ನೀನು ನಿನ್ನ ಅಣ್ಣತಮ್ಮಂದಿರಿಗೆ ತಿಳಿಸಬೇಕಾದ ನನ್ನ ಆಜ್ಞೆಯೇನಂದರೆ - ನೀವು ನಿಮ್ಮ ಪಶುಗಳ ಮೇಲೆ ಸಾಮಾನುಗಳನ್ನು ಹೇರಿ ಕಾನಾನ್‍ದೇಶಕ್ಕೆ ಹೋಗಿ",
			"18": "<sup>18</sup> ನಿಮ್ಮ ತಂದೆಯನ್ನೂ ಮನೆಯವರನ್ನೂ ನನ್ನ ಬಳಿಗೆ ಕರಕೊಂಡು ಬನ್ನಿರಿ; ಐಗುಪ್ತದೇಶದಲ್ಲಿ ದೊರಕುವ ಉತ್ತಮ ವಸ್ತುಗಳನ್ನು ನಿಮಗೆ ಕೊಡುವೆನು, ಮತ್ತು ನೀವು ಈ ದೇಶದ ಸುಖವನ್ನು ಅನುಭವಿಸಬಹುದು.",
			"19": "<sup>19</sup> ಮತ್ತು ನಿಮ್ಮ ಹೆಂಡತಿ ಮಕ್ಕಳಿಗೋಸ್ಕರ ಐಗುಪ್ತದೇಶದಿಂದ ರಥಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ನಿಮ್ಮ ತಂದೆಯನ್ನು ಕರಕೊಂಡು ಬನ್ನಿರಿ.",
			"20": "<sup>20</sup> ಐಗುಪ್ತದೇಶದಲ್ಲೆಲ್ಲಾ ಇರುವ ಉತ್ತಮ ವಸ್ತುಗಳು ನಿಮ್ಮ ಪಾಲಿಗೆ ಬಂದದರಿಂದ ನಿಮಗಿರುವ ಸೊತ್ತಿನ ವಿಷಯ ಚಿಂತೆಪಡಬೇಕಾದ ಅವಶ್ಯವಿಲ್ಲ ಎಂಬದೇ ಅಂದನು.",
			"21": "<sup>21</sup> ಇಸ್ರಾಯೇಲನ ಮಕ್ಕಳು ಅದೇ ಮೇರೆಗೆ ಮಾಡಿದರು. ಫರೋಹನ ಅಪ್ಪಣೆಯ ಪ್ರಕಾರ ಯೋಸೇಫನು ಅವರಿಗೆ ರಥಗಳನ್ನೂ ಮಾರ್ಗಕ್ಕೆ ಬೇಕಾದ ಆಹಾರವನ್ನೂ ಕೊಡಿಸಿದನು.",
			"22": "<sup>22</sup> ಇದಲ್ಲದೆ ಪ್ರತಿಯೊಬ್ಬನಿಗೂ ಶ್ರೇಷ್ಠ ವಸ್ತ್ರಗಳನ್ನೂ ಕೊಟ್ಟನು; ಬೆನ್ಯಾಮೀನನಿಗೋ ಮುನ್ನೂರು ರೂಪಾಯಿಗಳನ್ನೂ ಐದು ಶ್ರೇಷ್ಠ ವಸ್ತ್ರಗಳನ್ನೂ ಕೊಟ್ಟನು.",
			"23": "<sup>23</sup> ತನ್ನ ತಂದೆಗೋಸ್ಕರ ಐಗುಪ್ತದೇಶದ ಒಳ್ಳೇ ವಸ್ತುಗಳನ್ನು ಹತ್ತು ಕತ್ತೆಗಳ ಮೇಲೆ ಹೇರಿಸಿ ಹತ್ತು ಹೆಣ್ಣುಕತ್ತೆಗಳ ಮೇಲೆ ತನ್ನ ತಂದೆಯ ಪ್ರಯಾಣಕ್ಕೆ ಬೇಕಾದ ದವಸ ಧಾನ್ಯಗಳನ್ನೂ ಬೇರೆ ತಿನ್ನುವ ಪದಾರ್ಥಗಳನ್ನೂ ಕಟ್ಟಿಸಿಕೊಟ್ಟನು.",
			"24": "<sup>24</sup> ಆಗ ತನ್ನ ಅಣ್ಣತಮ್ಮಂದಿರಿಗೆ - ನೀವು ದಾರಿಯಲ್ಲಿ ಜಗಳಮಾಡಬೇಡಿರಿ ಎಂದು ಬುದ್ಧಿ ಹೇಳಿ ಅವರಿಗೆ ಹೋಗುವದಕ್ಕೆ ಅಪ್ಪಣೆ ಕೊಟ್ಟನು; ಅವರು ಹೊರಟುಹೋದರು.",
			"25": "<sup>25</sup> ಅವರು ಐಗುಪ್ತದೇಶವನ್ನು ಬಿಟ್ಟು ಕಾನಾನ್ ದೇಶಕ್ಕೆ ಸೇರಿ ತಂದೆಯಾದ ಯಾಕೋಬನ ಬಳಿಗೆ ಬಂದು ಅವನಿಗೆ-",
			"26": "<sup>26</sup> ಯೋಸೇಫನು ಇನ್ನೂ ಜೀವದಿಂದಿದ್ದಾನೆ; ಅವನು ಐಗುಪ್ತದೇಶದ ಸರ್ವಾಧಿಕಾರಿಯಾಗಿದ್ದಾನೆ ಎಂದು ತಿಳಿಸಲು ಅವನು ಸ್ತಬ್ಧನಾಗಿ ನಂಬಲಿಲ್ಲ.",
			"27": "<sup>27</sup> ತರುವಾಯ ಯೋಸೇಫನು ಹೇಳಿಕಳುಹಿಸಿದ್ದ ಎಲ್ಲಾ ಮಾತುಗಳನ್ನು ಕೇಳಿ ತನ್ನ ಪ್ರಯಾಣಕ್ಕೋಸ್ಕರ ಯೋಸೇಫನ ಕಡೆಯಿಂದ ಬಂದ ರಥಗಳನ್ನು ನೋಡಿ ಇಸ್ರಾಯೇಲನೆನಿಸಿಕೊಳ್ಳುವ ಅವರ ತಂದೆ ಯಾಕೋಬನು ಚೇತರಿಸಿಕೊಂಡು -",
			"28": "<sup>28</sup> ಸಾಕು, ನನ್ನ ಮಗನಾದ ಯೋಸೇಫನು ಸಜೀವನಾಗಿದ್ದಾನೆ; ನಾನು ಸಾಯುವದಕ್ಕಿಂತ ಮುಂಚೆ ಹೋಗಿ ಅವನನ್ನು ನೋಡುವೆನು ಅಂದನು."
		},
		"46": {
			"1": "<sup>1</sup> ಇಸ್ರಾಯೇಲನು ತನಗಿದ್ದ ಸರ್ವಸ್ವವನ್ನು ತೆಗೆದುಕೊಂಡು ಪ್ರಯಾಣಮಾಡಿ ಬೇರ್ಷಬಕ್ಕೆ ಬಂದು ತನ್ನ ತಂದೆಯಾದ ಇಸಾಕನ ದೇವರಿಗೆ ಯಜ್ಞಗಳನ್ನು ಮಾಡಿದನು.",
			"2": "<sup>2</sup> ಅಲ್ಲಿ ದೇವರು ರಾತ್ರಿಯ ಸ್ವಪ್ನದಲ್ಲಿ ಇಸ್ರಾಯೇಲನನ್ನು - ಯಾಕೋಬನೇ, ಯಾಕೋಬನೇ ಎಂದು ಕರೆಯಲು, ಅವನು - ಇದ್ದೇನೆ ಅಂದನು.",
			"3": "<sup>3</sup> ಆತನು - ನಾನು ದೇವರು, ನಿನ್ನ ತಂದೆ ಆರಾಧಿಸಿದ ದೇವರು; ನೀನು ಗಟ್ಟಾ ಇಳಿದು ಹೆದರದೆ ಐಗುಪ್ತ ದೇಶಕ್ಕೆ ಹೋಗು; ಅಲ್ಲಿ ನಿನ್ನಿಂದ ಮಹಾಜನಾಂಗವುಂಟಾಗುವಂತೆ ಮಾಡುವೆನು.",
			"4": "<sup>4</sup> ನಾನೇ ನಿನ್ನೊಂದಿಗೆ ಐಗುಪ್ತಕ್ಕೆ ಬರುವೆನು; ಅಲ್ಲಿಂದ ನಿಶ್ಚಯವಾಗಿ ನಿನ್ನನ್ನು ತಿರಿಗಿ ಕರೆದುಕೊಂಡು ಬರುವೆನು; [ನಿನ್ನ ಅವಸಾನಕಾಲದಲ್ಲಿ] ಯೋಸೇಫನು ನಿನ್ನ ಕಣ್ಣುಗಳ ಮೇಲೆ ಕೈಯಿಡುವನು ಅಂದನು.",
			"5": "<sup>5</sup> ಬಳಿಕ ಯಾಕೋಬನು ಬೇರ್ಷೆಬದಿಂದ ಹೊರಟನು. ಫರೋಹನು ಅವನನ್ನು ಕರಕೊಂಡು ಬರುವದಕ್ಕೆ ಕಳುಹಿಸಿದ್ದ ರಥಗಳ ಮೇಲೆ ಇಸ್ರಾಯೇಲನ ಮಕ್ಕಳು ತಮ್ಮ ತಂದೆಯಾದ ಯಾಕೋಬನನ್ನೂ ಹೆಂಡತಿ ಮಕ್ಕಳನ್ನೂ ಕುಳ್ಳಿರಿಸಿಕೊಂಡು ಹೊರಟರು.",
			"6": "<sup>6</sup> ಯಾಕೋಬನೂ ಅವನ ಮನೆತನದವರೆಲ್ಲರೂ ತಮ್ಮ ದನಕುರಿಗಳನ್ನೂ ತಾವು ಕಾನಾನ್‍ದೇಶದಲ್ಲಿ ಸಂಪಾದಿಸಿಕೊಂಡಿದ್ದ ಸೊತ್ತುಗಳನ್ನೂ ತೆಗೆದುಕೊಂಡು ಐಗುಪ್ತದೇಶವನ್ನು ತಲಪಿದರು.",
			"7": "<sup>7</sup> ಹೀಗೆ ಯಾಕೋಬನು ತನ್ನ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನೂ ಮೊಮ್ಮಕ್ಕಳನ್ನೂ ತನ್ನ ಮನೆತನದವರೆಲ್ಲರನ್ನೂ ತನ್ನ ಸಂಗಡಲೇ ಐಗುಪ್ತದೇಶಕ್ಕೆ ಕರಕೊಂಡು ಹೋದನು.",
			"8": "<sup>8</sup> ಇಸ್ರಾಯೇಲನೆಂಬ ಯಾಕೋಬನು ತನ್ನ ಮಕ್ಕಳೊಡನೆ ಐಗುಪ್ತದೇಶಕ್ಕೆ ಬಂದಾಗ ಅವನಿಗಿದ್ದ ಸಂತಾನದವರ ವಿವರಣೆ. ಯಾಕೋಬನ ಚೊಚ್ಚಲ ಮಗನು ರೂಬೇನನು.",
			"9": "<sup>9</sup> ರೂಬೇನನ ಮಕ್ಕಳಾದ ಹನೋಕ್ ಫಲ್ಲೂ ಹೆಚ್ರೋನ್ ಕರ್ಮೀ;",
			"10": "<sup>10</sup> ಸಿಮೆಯೋನನ ಮಕ್ಕಳಾದ ಯೆಮೂವೇಲ್ ಯಾಮೀನ್ ಓಹದ್ ಯಾಕೀನ್ ಚೋಹರ್ ಮತ್ತು ಕಾನಾನ್ಯ ಹೆಂಗಸಿನಲ್ಲಿ ಹುಟ್ಟಿದ ಸೌಲ;",
			"11": "<sup>11</sup> ಲೇವಿಯ ಮಕ್ಕಳಾದ ಗೇರ್ಷೋನ್ ಕೆಹಾತ್ ಮೆರಾರೀ;",
			"12": "<sup>12</sup> ಯೆಹೂದನ ಮಕ್ಕಳಾದ ಏರ್ ಓನಾನ್ ಶೇಲಾಹ ಪೆರೆಚ್‍ಜೆರಹ (ಇವರಲ್ಲಿ ಏರ್ ಓನಾನ್ ಎಂಬವರು ಕಾನಾನ್ ದೇಶದಲ್ಲೇ ಸತ್ತರು);",
			"13": "<sup>13</sup> ಪೆರೆಚನ ಮಕ್ಕಳಾದ ಹೆಚ್ರೋನ್ ಹಾಮೂಲ್; ಇಸ್ಸಾಕಾರನ ಮಕ್ಕಳಾದ ತೋಲಾ ಪುವ್ವಾ ಯೋಬ್ ಶಿಮ್ರೋನ್;",
			"14": "<sup>14</sup> ಜೆಬುಲೂನನ ಮಕ್ಕಳಾದ ಸೆರೆದ್ ಏಲೋನ್ ಯಹ್ಲೇಲ್; - ಇವರೆಲ್ಲರು ಲೇಯಳ ಸಂತತಿಯವರು.",
			"15": "<sup>15</sup> ಆಕೆಯು ಯಾಕೋಬನಿಗೆ ಇವರನ್ನೂ ದೀನಳೆಂಬ ಹೆಣ್ಣುಮಗಳನ್ನೂ ಪದ್ದನ್ಅರಾಮಿನಲ್ಲಿ ಪಡೆದಳು. ಆಕೆಯಿಂದಾದ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳು ಮೂವತ್ತು ಮೂರು ಮಂದಿ.",
			"16": "<sup>16</sup> ಗಾದನ ಮಕ್ಕಳಾದ ಚಿಪ್ಯೋನ್‌ ಹಗ್ಗೀ ಶೂನೀ ಎಚ್ಬೋನ್ ಏರೀ ಅರೋದೀ ಅರೇಲೀ;",
			"17": "<sup>17</sup> ಆಶೇರನ ಮಕ್ಕಳಾದ ಇಮ್ನಾ ಇಷ್ವಾ ಇಷ್ವೀ ಬೆರೀಗಾ ಇವರ ತಂಗಿಯಾದ ಸೆರಹ; ಬೆರೀಗನ ಮಕ್ಕಳಾದ ಹೆಬೆರ್ ಮಲ್ಕೀಯೇಲ್, -",
			"18": "<sup>18</sup> ಇವರು ಲಾಬಾನನು ತನ್ನ ಮಗಳಾದ ಲೇಯಳಿಗೆ ಕೊಟ್ಟ ಜಿಲ್ಪಳ ಸಂತತಿಯವರು. ಈಕೆಯಿಂದ ಯಾಕೋಬನಿಗಾದ ಮಕ್ಕಳೂ ಮೊಮ್ಮಕ್ಕಳೂ ಹದಿನಾರು ಮಂದಿ.",
			"19": "<sup>19</sup> ಯಾಕೋಬನ ಹೆಂಡತಿಯಾದ ರಾಹೇಲಳ ಮಕ್ಕಳು ಯೋಸೇಫ್ ಬೆನ್ಯಾಮೀನ್.",
			"20": "<sup>20</sup> ಐಗುಪ್ತದೇಶದಲ್ಲಿ ಯೋಸೇಫನಿಗೆ ಓನ್ ಪಟ್ಟಣದ ಆಚಾರ್ಯನಾದ ಪೋಟೀಫೆರನ ಮಗಳಾದ ಆಸನತ್ ಎಂಬಾಕೆಯಲ್ಲಿ ಹುಟ್ಟಿದ ಮನಸ್ಸೆ ಎಫ್ರಾಯೀಮ್;",
			"21": "<sup>21</sup> ಬೆನ್ಯಾಮೀನನ ಮಕ್ಕಳಾದ ಬಿಳಾ ಬೆಕೆರ್ ಅಶ್ಬೇಲ್ ಗೇರಾ ನಾಮಾನ್",
			"22": "<sup>22</sup> ಏಹೀರೋಷ್ ಮುಪ್ಪೀಮ್ ಹುಪ್ಪೀಮ್ ಆರ್ದ್; - ಈ ಹದಿನಾಲ್ಕು ಮಂದಿಯು ರಾಹೇಲಳ ಮೂಲಕ ಯಾಕೋಬನಿಂದಾದ ಸಂತತಿಯವರು.",
			"23": "<sup>23</sup> ದಾನನ ಮಗನಾದ ಹುಶೀಮ್; ನಫ್ತಾಲಿಯ ಮಕ್ಕಳಾದ ಯಹೇಲ್ ಗೂನೀ ಯೇಚೆರ್ ಶಿಲ್ಲೇಮ್; -",
			"25": "<sup>25</sup> ಇವರು ಲಾಬಾನನು ತನ್ನ ಮಗಳಾದ ರಾಹೇಲಳಿಗೆ ಕೊಟ್ಟ ಬಿಲ್ಹಳ ಸಂತತಿಯವರು. ಈಕೆಯಿಂದ ಯಾಕೋಬನಿಗಾದ ಮಕ್ಕಳೂ ಮೊಮ್ಮಕ್ಕಳೂ ಏಳು ಮಂದಿ.",
			"26": "<sup>26</sup> ಯಾಕೋಬನ ಸೊಸೆಯರಲ್ಲದೆ ಯಾಕೋಬನಿಂದಲೇ ಹುಟ್ಟಿ ಅವನೊಂದಿಗೆ ಐಗುಪ್ತದೇಶಕ್ಕೆ ಹೋದವರು ಒಟ್ಟು ಅರುವತ್ತಾರು ಮಂದಿ.",
			"27": "<sup>27</sup> ಅವರಲ್ಲದೆ ಐಗುಪ್ತದೇಶದಲ್ಲಿ ಯೋಸೇಫನಿಗೆ ಹುಟ್ಟಿದವರು ಇಬ್ಬರು. ಐಗುಪ್ತದೇಶಕ್ಕೆ ಹೋದ ಯಾಕೋಬನ ಕುಟುಂಬದವರೆಲ್ಲರು ಒಟ್ಟಾಗಿ ಎಪ್ಪತ್ತು ಮಂದಿ.",
			"28": "<sup>28</sup> ಯಾಕೋಬನು ಗೋಷೆನ್‍ಸೀಮೆಯ ದಾರೀ ವಿಚಾರಿಸುವದಕ್ಕೆ ಯೆಹೂದನನ್ನು ಮುಂದಾಗಿ ಯೋಸೇಫನ ಬಳಿಗೆ ಕಳುಹಿಸಿದನು.",
			"29": "<sup>29</sup> ಅವರು ಗೋಷೆನ್‍ಸೀಮೆಗೆ ಬಂದಾಗ ಯೋಸೇಫನು ತನ್ನ ರಥವನ್ನು ಸಿದ್ಧಪಡಿಸಿಕೊಂಡು ತನ್ನ ತಂದೆಯಾದ ಇಸ್ರಾಯೇಲನನ್ನು ಎದುರುಗೊಳ್ಳುವದಕ್ಕೋಸ್ಕರ ಗೋಷೆನಿಗೆ ಹೋಗಿ ಅವನಿಗೆ ಕಾಣಿಸಿಕೊಂಡು ತಂದೆಯನ್ನು ಬಹಳ ಹೊತ್ತಿನವರೆಗೆ ಅಪ್ಪಿಕೊಂಡು ಅತ್ತನು.",
			"30": "<sup>30</sup> ಇಸ್ರಾಯೇಲನು ಯೋಸೇಫನಿಗೆ - ನಾನು ನಿನ್ನ ಮುಖವನ್ನು ಕಂಡು ನೀನು ಇನ್ನೂ ಜೀವದಿಂದಿರುವದನ್ನು ತಿಳುಕೊಂಡದ್ದರಿಂದ ಸಂತೃಪ್ತನಾಗಿ ಸಾಯುವೆನು ಎಂದು ಹೇಳಿದನು.",
			"31": "<sup>31</sup> ಯೋಸೇಫನು ತನ್ನ ಅಣ್ಣ ತಮ್ಮಂದಿರಿಗೂ ತಂದೆಯ ಮನೆಯವರೆಲ್ಲರಿಗೂ - ನಾನು ಹೋಗಿ ಫರೋಹನ ಸನ್ನಿಧಾನದಲ್ಲಿ ನೀವು ಬಂದ ವರ್ತಮಾನವನ್ನು ತಿಳಿಸಿ - ಕಾನಾನ್‍ದೇಶದಲ್ಲಿದ್ದ ನನ್ನ ಅಣ್ಣತಮ್ಮಂದಿರೂ ನನ್ನ ತಂದೆಯ ಮನೆಯವರೂ ನನ್ನ ಬಳಿಗೆ ಬಂದಿದ್ದಾರೆ;",
			"32": "<sup>32</sup> ಅವರು ಪಶುಪಾಲಕರಾದದ್ದರಿಂದ ಕುರಿಕಾಯುವವರಿಗೆ ಸಂಬಂಧಪಟ್ಟವರು; ಅವರು ತಮ್ಮ ಕುರಿದನಗಳನ್ನೂ ತಮಗಿರುವ ಸರ್ವಸ್ವವನ್ನೂ ತಂದಿದ್ದಾರೆ ಎಂದು ಹೇಳುತ್ತೇನೆ.",
			"33": "<sup>33</sup> ಫರೋಹನು ನಿಮ್ಮನ್ನು ಕರಸಿ - ನಿಮ್ಮ ಕೆಲಸವೇನು ಎಂದು ಕೇಳಿದರೆ",
			"34": "<sup>34</sup> ನೀವು - ಚಿಕ್ಕಂದಿನಿಂದ ಇದುವರೆಗೂ ನಿನ್ನ ಸೇವಕರಾದ ನಾವು ನಮ್ಮ ಪೂರ್ವಿಕರ ಪದ್ಧತಿಯ ಮೇರೆಗೆ ಪಶುಪಾಲಕರು ಅನ್ನಿರಿ. ಪಶುಪಾಲಕರು ಐಗುಪ್ತ್ಯರಿಗೆ ಅಸಹ್ಯವಾಗಿರುವದರಿಂದ ಗೋಷೆನ್‍ಸೀಮೆಯನ್ನು ನಿಮ್ಮ ನಿವಾಸಕ್ಕೆ ನೇಮಿಸುವನು ಅಂದನು."
		},
		"47": {
			"1": "<sup>1</sup> ಆಗ ಯೋಸೇಫನು ಫರೋಹನ ಬಳಿಗೆ ಹೋಗಿ - ನನ್ನ ತಂದೆಯೂ ಅಣ್ಣತಮ್ಮಂದಿರೂ ದನಕುರಿಗಳನ್ನೂ ಸಮಸ್ತ ಆಸ್ತಿಯನ್ನೂ ತೆಗೆದುಕೊಂಡು ಕಾನಾನ್ ದೇಶದಿಂದ ಬಂದು ಗೋಷೆನ್ ಸೀಮೆಯಲ್ಲಿದ್ದಾರೆಂದು ಹೇಳಿ",
			"2": "<sup>2</sup> ತನ್ನ ಅಣ್ಣತಮ್ಮಂದಿರಲ್ಲಿ ಐದು ಮಂದಿಯನ್ನು ಕರೆದುಕೊಂಡು ಬಂದು ಫರೋಹನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನಿಲ್ಲಿಸಿದನು.",
			"3": "<sup>3</sup> ಫರೋಹನು - ನಿಮ್ಮ ಕೆಲಸವೇನು ಎಂದು ಅವರನ್ನು ಕೇಳಲು ಅವರು - ನಿನ್ನ ಸೇವಕರಾದ ನಾವು ನಮ್ಮ ಪೂರ್ವಿಕರ ಪದ್ಧತಿಯ ಮೇರೆಗೆ ಕುರಿಕಾಯುವವರು ಎಂದು ಹೇಳಿದರು.",
			"4": "<sup>4</sup> ಅದಲ್ಲದೆ ಅವರು ಅವನಿಗೆ - ಕಾನಾನ್‍ದೇಶದಲ್ಲಿ ಬರವು ಘೋರವಾದ್ದರಿಂದ ನಿನ್ನ ಸೇವಕರ ದನಕುರಿಗಳಿಗೆ ಮೇವು ಸಿಕ್ಕಲಿಲ್ಲ; ಆದದರಿಂದ ಈ ದೇಶದಲ್ಲಿ ಸ್ವಲ್ಪಕಾಲ ಇರಬೇಕೆಂದು ಬಂದಿದ್ದೇವೆ. ಸೇವಕರು ಗೋಷೆನ್‍ಸೀಮೆಯಲ್ಲಿ ವಾಸಮಾಡುವಂತೆ ಅಪ್ಪಣೆಯಾಗಬೇಕು ಎಂದು ಹೇಳಿಕೊಂಡರು.",
			"5": "<sup>5</sup> ಆಗ ಫರೋಹನು ಯೋಸೇಫನಿಗೆ - ನಿನ್ನ ತಂದೆಯೂ ಅಣ್ಣತಮ್ಮಂದಿರೂ ನಿನ್ನ ಬಳಿಗೆ ಬಂದಿದ್ದಾರಷ್ಟೆ; ಐಗುಪ್ತದೇಶವು ನಿನ್ನೆದುರಿನಲ್ಲಿ ಅದೆ,",
			"6": "<sup>6</sup> ಅದರೊಳಗಣ ಉತ್ತಮಭಾಗದಲ್ಲಿ ನಿನ್ನ ತಂದೆಗೂ ನಿನ್ನ ಅಣ್ಣತಮ್ಮಂದಿರಿಗೂ ವಾಸಸ್ಥಾನವನ್ನು ನೇಮಿಸಬಹುದು. ಅವರು ಗೋಷೆನ್ ಸೀಮೆಯಲ್ಲಿ ವಾಸಮಾಡಲಿ; ಅವರಲ್ಲಿ ಯಾರಾದರೂ ಯೋಗ್ಯರಾಗಿ ತೋರಿದರೆ ಅವರನ್ನು ನನ್ನ ದನಗಳ ಮೇಲ್ವಿಚಾರಣೆಗೆ ನೇಮಿಸು ಎಂದು ಹೇಳಿದನು.",
			"7": "<sup>7</sup> ಯೋಸೇಫನು ತನ್ನ ತಂದೆಯಾದ ಯಾಕೋಬನನ್ನು ಕರಕೊಂಡು ಫರೋಹನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನಿಲ್ಲಿಸಲು ಯಾಕೋಬನು ಫರೋಹನನ್ನು ಆಶೀರ್ವದಿಸಿದನು.",
			"8": "<sup>8</sup> ಫರೋಹನು - ನಿನಗೆಷ್ಟು ವರುಷಗಳು ಎಂದು ಕೇಳಲು",
			"9": "<sup>9</sup> ಯಾಕೋಬನು - ನಾನು ಲೋಕದಲ್ಲಿ ಸಂಚಾರಮಾಡಿದ್ದು ನೂರಮೂವತ್ತು ವರುಷಗಳೇ; ನಾನು ಜೀವಿಸಿರುವ ಕಾಲವು ಸ್ವಲ್ಪವಾಗಿಯೂ ದುಃಖಕರವಾಗಿಯೂ ಇತ್ತು; ನನ್ನ ಪಿತೃಗಳು ಲೋಕಯಾತ್ರೆ ಮಾಡಿದಷ್ಟು ವರುಷಗಳು ನನಗಾಗಿಲ್ಲ ಎಂದು ಹೇಳಿ",
			"10": "<sup>10</sup> ಫರೋಹನನ್ನು ಆಶೀರ್ವದಿಸಿ ಅವನ ಸನ್ನಿಧಿಯಿಂದ ಹೊರಟುಹೋದನು.",
			"11": "<sup>11</sup> ಫರೋಹನ ಅಪ್ಪಣೆಯ ಮೇರೆಗೆ ಯೋಸೇಫನು ತನ್ನ ತಂದೆಗೂ ಅಣ್ಣತಮ್ಮಂದಿರಿಗೂ ಐಗುಪ್ತ ದೇಶದಲ್ಲಿ ವಾಸಸ್ಥಾನವನ್ನು ನೇಮಿಸಿ ಅದರೊಳಗೆ ಉತ್ತಮವಾಗಿದ್ದ ರಮ್ಸೇಸ್ ಪ್ರದೇಶದಲ್ಲಿ ಸ್ವಾಸ್ತಿಯನ್ನು ಕೊಟ್ಟನು.",
			"12": "<sup>12</sup> ಯೋಸೇಫನು ತನ್ನ ತಂದೆಗೂ ಅಣ್ಣತಮ್ಮಂದಿರಿಗೂ ತಂದೆಯ ಮನೆಯವರೆಲ್ಲರಿಗೂ ಅವರವರ ಕುಟುಂಬಗಳಿಗೆ ಸೇರಿದವರ ಲೆಕ್ಕದ ಪ್ರಕಾರ ಆಹಾರಕ್ಕೆ ಕೊಟ್ಟು ಅವರನ್ನು ಸಂರಕ್ಷಿಸಿದನು.",
			"13": "<sup>13</sup> ಬರವು ಬಹು ಘೋರವಾಗಿದ್ದದರಿಂದ ದೇಶದ ಯಾವ ಭಾಗದಲ್ಲಿಯೂ ಆಹಾರ ಸಿಕ್ಕುತ್ತಿರಲಿಲ್ಲ. ಆ ಬರದ ನಿಮಿತ್ತ ಐಗುಪ್ತದೇಶದವರಿಗೂ ಕಾನಾನ್‍ದೇಶದವರಿಗೂ ದಿಕ್ಕುತೋರದೆ ಹೋಯಿತು.",
			"14": "<sup>14</sup> ಯೋಸೇಫನು ಜನರಿಗೆ ಧಾನ್ಯವನ್ನು ಮಾರುತ್ತಾ ಐಗುಪ್ತದೇಶದಲ್ಲಿಯೂ ಕಾನಾನ್‍ದೇಶದಲ್ಲಿಯೂ ಇದ್ದ ಎಲ್ಲಾ ಹಣವನ್ನೂ ಸೆಳಕೊಂಡು ಫರೋಹನ ಬೊಕ್ಕಸದೊಳಗೆ ತುಂಬಿದನು.",
			"15": "<sup>15</sup> ಐಗುಪ್ತದೇಶದಲ್ಲಿಯೂ ಕಾನಾನ್ ದೇಶದಲ್ಲಿಯೂ ಇದ್ದ ಹಣವೆಲ್ಲಾ ಮುಗಿದುಹೋದ ನಂತರ ಐಗುಪ್ತ್ಯರೆಲ್ಲರೂ ಯೋಸೇಫನ ಬಳಿಗೆ ಬಂದು - ನಮ್ಮ ಹಣವು ಮುಗಿದು ಹೋಯಿತು; ನೀನು [ಧರ್ಮವಾಗಿಯೇ] ಆಹಾರಕೊಡಬೇಕು; ನಾವು ನಿನ್ನ ಮುಂದೆ ಸತ್ತರೆ ಪ್ರಯೋಜನವೇನು ಎಂದು ಹೇಳಿದರು.",
			"16": "<sup>16</sup> ಅದಕ್ಕೆ ಯೋಸೇಫನು - ನಿಮ್ಮ ದನಗಳನ್ನು ತನ್ನಿರಿ; ಹಣವಿಲ್ಲದಿದ್ದರೆ ದನಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಧಾನ್ಯವನ್ನು ಕೊಡಿಸುತ್ತೇನೆ ಅಂದನು.",
			"17": "<sup>17</sup> ಅವರು ತಮ್ಮ ಪಶುಗಳನ್ನು ಯೋಸೇಫನ ಬಳಿಗೆ ತರಲು ಅವನು ಅವರ ಕುದುರೆಗಳನ್ನೂ ಕುರಿದನಕತ್ತೆಗಳನ್ನೂ ತೆಗೆದುಕೊಂಡು ಅವರಿಗೆ ಧಾನ್ಯವನ್ನು ಕೊಡಿಸಿದನು. ಆ ವರುಷದಲ್ಲಿ ಅವರ ದನಕುರಿಗಳನ್ನೆಲ್ಲಾ ತೆಗೆದುಕೊಂಡು ಧಾನ್ಯವನ್ನು ಕೊಟ್ಟು ಅವರನ್ನು ಸಂರಕ್ಷಿಸಿದನು.",
			"18": "<sup>18</sup> ಆ ವರುಷ ಕಳೆದನಂತರ ಅವರು ಎರಡನೆಯ ವರುಷದಲ್ಲಿ ಅವನ ಬಳಿಗೆ ಬಂದು - ನಮ್ಮ ಹಣವೆಲ್ಲಾ ವೆಚ್ಚವಾಗಿ ಹೋಗಿದೆ; ನಮ್ಮ ಕುರಿದನಗಳು ಸ್ವಾಮಿಯ ವಶವಾದವು; ಹೀಗಿರುವದರಿಂದ ನಮ್ಮ ಭೂಮಿಯೂ ನಮ್ಮ ದೇಹಗಳೂ ಹೊರತು ಬೇರೆ ಇನ್ನೇನೂ ಉಳಿದಿಲ್ಲವೆಂದು ನಮ್ಮ ಸ್ವಾಮಿಗೆ ಮರೆಮಾಜದೆ ತಿಳಿಸಬೇಕಾಯಿತು.",
			"19": "<sup>19</sup> ನಿನ್ನ ಕಣ್ಣೆದುರಿಗೆ ನಾವು ಯಾಕೆ ಸಾಯಬೇಕು; ನಮ್ಮ ಭೂಮಿಯು ಯಾಕೆ ಹಾಳಾಗಬೇಕು. ನಮ್ಮನ್ನೂ ನಮ್ಮ ಭೂಮಿಯನ್ನೂ ತೆಗೆದುಕೊಂಡು ಧಾನ್ಯವನ್ನು ಕೊಡು; ನಾವು ನಮ್ಮ ಭೂಮಿಯನ್ನು ಫರೋಹನಿಗೆ ಕೊಟ್ಟುಬಿಟ್ಟು ಅವನಿಗೆ ಗುಲಾಮರಾಗುವೆವು. ನಾವು ಸಾಯದೆ ಬದುಕಬೇಕಾದರೆ ಮತ್ತು ಭೂಮಿಯು ಹಾಳು ಬೀಳಬಾರದಿದ್ದರೆ ನೀನು ಬೀಜವನ್ನು ಕೊಡಬೇಕೆಂದು ಹೇಳಲು",
			"20": "<sup>20</sup> ಯೋಸೇಫನು ಐಗುಪ್ತದೇಶದ ಎಲ್ಲಾ ಸಾಗುವಳಿಯ ಭೂಮಿಯನ್ನು ಫರೋಹನಿಗೋಸ್ಕರ ಕೊಂಡುಕೊಂಡನು. ಬರವು ಐಗುಪ್ತ್ಯರಿಗೆ ಘೋರವಾಗಿದ್ದದರಿಂದ ಅವರೆಲ್ಲರೂ ತಮ್ಮ ತಮ್ಮ ಹೊಲಗದ್ದೆಗಳನ್ನು ಮಾರಿಬಿಟ್ಟರು. ಹೀಗೆ ಸಾಗುವಳಿಯ ಭೂಮಿಯೆಲ್ಲಾ ಫರೋಹನದಾಯಿತು.",
			"21": "<sup>21</sup> ಯೋಸೇಫನು ಐಗುಪ್ತದೇಶದ ಒಂದು ಮೇರೆಯಿಂದ ಮತ್ತೊಂದು ಮೇರೆಯವರೆಗೂ ಪ್ರಜೆಗಳನ್ನು ದಾಸರನ್ನಾಗಿ ಮಾಡಿದನು.",
			"22": "<sup>22</sup> ವೈದಿಕರ ಭೂಮಿಯನ್ನು ಮಾತ್ರ ಅವನು ಕೊಂಡುಕೊಳ್ಳಲಿಲ್ಲ. ವೈದಿಕರಿಗೆ ಫರೋಹನ ಕಡೆಯಿಂದ ಆಯವು ಸಿಕ್ಕಿದ್ದರಿಂದ ಅವರು ಫರೋಹನು ಕೊಟ್ಟ ಆಯದಿಂದ ಜೀವನ ಮಾಡುತ್ತಿದ್ದರು; ಆದದರಿಂದ ತಮ್ಮ ಭೂಮಿಯನ್ನು ಮಾರಲಿಲ್ಲ.",
			"23": "<sup>23</sup> ಯೋಸೇಫನು ಪ್ರಜೆಗಳಿಗೆ - ನಾನು ಈ ಹೊತ್ತು ನಿಮ್ಮನ್ನೂ ನಿಮ್ಮ ಭೂಮಿಯನ್ನೂ ಫರೋಹನಿಗೋಸ್ಕರ ಕೊಂಡುಕೊಂಡಿದ್ದೇನೆಂದು ತಿಳಿದುಕೊಳ್ಳಿರಿ. ಇಗೋ, ಬಿತ್ತನೆಗಾಗಿ ನಿಮಗೆ ಬೀಜವನ್ನು ಕೊಟ್ಟಿದ್ದೇನೆ; ಭೂಮಿಯಲ್ಲಿ ಬಿತ್ತಿರಿ.",
			"24": "<sup>24</sup> ಪೈರು ಕೊಯ್ಯುವ ಕಾಲದಲ್ಲಿ ಐದರಲ್ಲೊಂದು ಪಾಲು ಫರೋಹನಿಗೆ ಸಲ್ಲಬೇಕು; ಮಿಕ್ಕ ನಾಲ್ಕು ಪಾಲು ನಿಮ್ಮದು; ಅದು ಬೀಜಕ್ಕೆ ಆಗುತ್ತದೆ, ಮತ್ತು ನಿಮಗೂ ನಿಮ್ಮ ಮನೆಯವರಿಗೂ ನಿಮ್ಮ ಕುಟುಂಬಕ್ಕೆ ಸೇರಿದವರೆಲ್ಲರಿಗೂ ಜೀವನಕ್ಕಾಗುತ್ತದೆ ಎಂದು ಹೇಳಿದನು.",
			"25": "<sup>25</sup> ಅದಕ್ಕೆ ಅವರು - ನಮ್ಮ ಪ್ರಾಣಗಳನ್ನು ಉಳಿಸಿದ್ದೀ; ನಮ್ಮ ಸ್ವಾಮಿಯ ದಯೆ ನಮ್ಮ ಮೇಲಿರಲಿ; ನಾವು ಫರೋಹನಿಗೆ ಗುಲಾಮರಾಗಿದ್ದೇವೆ ಅಂದರು.",
			"26": "<sup>26</sup> ಬೆಳೆಯಲ್ಲಿ ಐದನೆಯ ಒಂದು ಪಾಲು ಫರೋಹನಿಗೆ ಸಲ್ಲಬೇಕೆಂಬದನ್ನು ಯೋಸೇಫನು ಐಗುಪ್ತದೇಶದಲ್ಲಿ ಕಟ್ಟಳೆಯಾಗಿ ನೇಮಿಸಿದನು. ಈ ದಿನದವರೆಗೂ ಹಾಗೆಯೇ ಇದೆ. ವೈದಿಕರ ಭೂಮಿಯು ಮಾತ್ರ ಫರೋಹನಿಗೆ ಸ್ವಾಧೀನವಾಗಲಿಲ್ಲ.",
			"27": "<sup>27</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಐಗುಪ್ತದೇಶಕ್ಕೆ ಸೇರಿರುವ ಗೋಷೆನ್ ಸೀಮೆಯಲ್ಲಿ ವಾಸಮಾಡಿದರು. ಅದರಲ್ಲಿ ಅವರು ಆಸ್ತಿಯನ್ನು ಸಂಪಾದಿಸಿ ಬಹುಸಂತಾನವಾಗಿ ಬಹಳವಾಗಿ ಹೆಚ್ಚಿದರು.",
			"28": "<sup>28</sup> ಯಾಕೋಬನು ಐಗುಪ್ತ ದೇಶದಲ್ಲಿ ಹದಿನೇಳು ವರುಷ ಇದ್ದನು. ಅವನ ಜೀವಿತಕಾಲವು ಒಟ್ಟಿಗೆ ನೂರ ನಾಲ್ವತ್ತೇಳು ವರುಷಗಳು.",
			"29": "<sup>29</sup> ಇಸ್ರಾಯೇಲನಿಗೆ ಅವಸಾನಕಾಲ ಸಮೀಪಿಸಿದಾಗ ಅವನು ತನ್ನ ಮಗನಾದ ಯೋಸೇಫನನ್ನು ಕರಸಿ - ನಿನಗೆ ನನ್ನ ಮೇಲೆ ಪ್ರೀತಿಯಿದ್ದರೆ ನೀನು ನಂಬಿಕೆಯಿಂದಲೂ ಪ್ರೀತಿಯಿಂದಲೂ ನನ್ನ ಮಾತನ್ನು ನಡಿಸಬೇಕು; ಏನಂದರೆ, ನನಗೆ ಐಗುಪ್ತ ದೇಶದಲ್ಲಿ ಸಮಾಧಿಮಾಡಬೇಡವೆಂದು ಕೇಳಿಕೊಳ್ಳುತ್ತೇನೆ.",
			"30": "<sup>30</sup> ನಾನು ಪಿತೃಗಳಲ್ಲಿ ಸೇರಿದಾಗ ನನ್ನ ಶವವನ್ನು ಐಗುಪ್ತದೇಶದಿಂದ ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ಆ ಪಿತೃಗಳ ಶ್ಮಶಾನ ಭೂಮಿಯಲ್ಲಿಯೇ ಸಮಾಧಿಮಾಡಬೇಕು. ಹಾಗೆ ಮಾಡುತ್ತೇನೆಂಬದಾಗಿ ನೀನು ನನ್ನ ತೊಡೆಯ ಕೆಳಗೆ ಕೈಯಿಟ್ಟು ಪ್ರಮಾಣ ಮಾಡಬೇಕೆಂದು ಹೇಳಲು ಅವನು - ನೀನು ಹೇಳಿದಂತೆಯೇ ಮಾಡುತ್ತೇನೆ ಅಂದನು.",
			"31": "<sup>31</sup> ಇಸ್ರಾಯೇಲನು - ಪ್ರಮಾಣ ಮಾಡು ಅನ್ನಲು ಅವನು ಪ್ರಮಾಣಮಾಡಿದನು. ಆಗ ಇಸ್ರಾಯೇಲನು ಮಂಚದ ತಲೆದೆಸೆಯಲ್ಲಿ ಬಾಗಿ ದೇವರಿಗೆ ನಮಸ್ಕಾರಮಾಡಿದನು."
		},
		"48": {
			"1": "<sup>1</sup> ಕೆಲವು ಕಾಲವಾದ ಮೇಲೆ ತಂದೆಯು ಬಹಳ ಬಲಹೀನನಾಗಿ ಬಿದ್ದಿದ್ದಾನೆಂಬ ವರ್ತಮಾನವು ಯೋಸೇಫನಿಗೆ ಬರಲು ಅವನು ಮನಸ್ಸೆ ಎಫ್ರಾಯೀಮರೆಂಬ ತನ್ನಿಬ್ಬರು ಮಕ್ಕಳನ್ನು ಸಂಗಡ ಕರೆದುಕೊಂಡು ಅವನ ಬಳಿಗೆ ಬಂದನು.",
			"2": "<sup>2</sup> ನಿನ್ನ ಮಗನಾದ ಯೋಸೇಫನು ಬಂದನೆಂದು ಯಾಕೋಬನಿಗೆ ಒಬ್ಬನು ತಿಳಿಸಲು ಅವನು ಚೇತರಿಸಿಕೊಂಡು ಮಂಚದ ಮೇಲೆ ಕೂತುಕೊಂಡನು.",
			"3": "<sup>3</sup> ಯಾಕೋಬನು ಯೋಸೇಫನಿಗೆ - ಸರ್ವಶಕ್ತನಾದ ದೇವರು ಕಾನಾನ್‍ದೇಶದ ಲೂಜಿನಲ್ಲಿ ನನಗೆ ದರ್ಶನಕೊಟ್ಟು ಆಶೀರ್ವದಿಸಿ -",
			"4": "<sup>4</sup> ನಾನು ನಿನ್ನನ್ನು ಅಭಿವೃದ್ಧಿಪಡಿಸಿ ನಿನ್ನ ಸಂತತಿಯನ್ನು ಹೆಚ್ಚಿಸುವೆನು; ಅನೇಕ ಜನಾಂಗಗಳು ನಿನ್ನಿಂದುಂಟಾಗುವಂತೆ ಮಾಡುವೆನು; ನಿನ್ನ ತರುವಾಯ ನಿನ್ನ ಸಂತತಿಗೆ ಈ ದೇಶವನ್ನು ಶಾಶ್ವತ ಸ್ವಾಸ್ತ್ಯವನ್ನಾಗಿ ಕೊಡುವೆನು ಎಂದು ಹೇಳಿದನು.",
			"5": "<sup>5</sup> ನಾನು ಐಗುಪ್ತದೇಶಕ್ಕೆ ನಿನ್ನ ಬಳಿಗೆ ಬರುವದಕ್ಕಿಂತ ಮುಂಚೆ ಇಲ್ಲಿ ಹುಟ್ಟಿದ ನಿನ್ನಿಬ್ಬರು ಮಕ್ಕಳು ನನ್ನ ಮಕ್ಕಳಾಗಿರಬೇಕು. ರೂಬೇನ್ ಸಿಮೆಯೋನರು ನನ್ನ ಮಕ್ಕಳಾಗಿರುವಂತೆಯೇ ಎಫ್ರಾಯೀಮ್ ಮನಸ್ಸೆಯರೂ ನನ್ನ ಮಕ್ಕಳಾಗಿರಲಿ.",
			"6": "<sup>6</sup> ಅವರ ತರುವಾಯ ನೀನು ಪಡೆದ ಮಕ್ಕಳು ನಿನ್ನವರಾಗಲಿ, ಅವರು ತಮ್ಮ ಅಣ್ಣಂದಿರ ಸ್ವಾಸ್ತ್ಯದಲ್ಲಿ ಬಾಧ್ಯತೆ ಹೊಂದಿ ಅವರ ಕುಲದ ಹೆಸರನ್ನೇ ಇಟ್ಟುಕೊಳ್ಳಲಿ.",
			"7": "<sup>7</sup> ನಾನು ಪದ್ದನ್ಅರಾಮಿನಿಂದ ಬರುತ್ತಿರಲು ಕಾನಾನ್‍ದೇಶದಲ್ಲಿ ಎಫ್ರಾತನ್ನು ಸೇರುವದಕ್ಕೆ ಇನ್ನೂ ಸ್ವಲ್ಪ ದೂರವಿದ್ದಾಗ ರಾಹೇಲಳು ದಾರಿಯಲ್ಲೇ ಸತ್ತು ನನ್ನನ್ನು ಅಗಲಿಹೋದಳು. ಬೇತ್ಲೆಹೇಮೆಂಬ ಎಫ್ರಾತಿಗೆ ಹೋಗುವ ಮಾರ್ಗದಲ್ಲಿ ಆಕೆಗೆ ಸಮಾಧಿ ಮಾಡಿದೆನು ಎಂದು ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ಇಸ್ರಾಯೇಲನು ಯೋಸೇಫನ ಮಕ್ಕಳನ್ನು ನೋಡಿ -",
			"9": "<sup>9</sup> ಇವರು ಯಾರೆಂದು ಕೇಳಲು ಯೋಸೇಫನು ತನ್ನ ತಂದೆಗೆ - ಇವರು ಈ ದೇಶದಲ್ಲಿ ನನಗೆ ದೇವರು ಅನುಗ್ರಹಿಸಿದ ಮಕ್ಕಳು ಎಂದು ಹೇಳಿದನು. ಅದಕ್ಕವನು - ಇವರನ್ನು ನಾನು ಆಶೀರ್ವದಿಸುತ್ತೇನೆ; ನನ್ನ ಹತ್ತಿರಕ್ಕೆ ಕರಕೊಂಡು ಬಾ ಅಂದನು.",
			"10": "<sup>10</sup> ಇಸ್ರಾಯೇಲನು ವೃದ್ಧಾಪ್ಯದಿಂದ ಮೊಬ್ಬುಕಣ್ಣುಳ್ಳವನಾಗಿ ನೋಡಲಾರದೆ ಇದ್ದನು. ಯೋಸೇಫನು ಅವರನ್ನು ಅವನ ಬಳಿಗೆ ಕರಕೊಂಡು ಬರಲು ಅವನು ಅವರನ್ನು ಮುದ್ದಿಟ್ಟು ಅಪ್ಪಿಕೊಂಡನು.",
			"11": "<sup>11</sup> ಆಗ ಇಸ್ರಾಯೇಲನು ಯೋಸೇಫನಿಗೆ - ನಿನ್ನ ಮುಖವನ್ನು ತಿರಿಗಿ ನೋಡುವೆನೆಂದು ನಾನು ನೆನಸಿರಲಿಲ್ಲ; ಆದರೆ ನಾನು ನಿನ್ನನ್ನು ಮಾತ್ರವಲ್ಲದೆ ನಿನ್ನ ಸಂತಾನವನ್ನೂ ನೋಡುವಂತೆ ದೇವರು ಅನುಗ್ರಹಿಸಿದ್ದಾನಲ್ಲಾ ಅಂದನು.",
			"12": "<sup>12</sup> ಆಗ ಯೋಸೇಫನು ಅವರನ್ನು ತಂದೆಯ ಮೊಣಕಾಲಿನ ಕಡೆಯಿಂದ ಬರಮಾಡಿ [ಅವನಿಗೆ] ಅಡ್ಡಬಿದ್ದನು.",
			"13": "<sup>13</sup> ತರುವಾಯ ಯೋಸೇಫನು ತನ್ನ ಬಲಗಡೆಯಲ್ಲಿ ಇಸ್ರಾಯೇಲನ ಎಡಗೈಗೆ ಎದುರಾಗಿ ಎಫ್ರಾಯೀಮನನ್ನೂ ತನ್ನ ಎಡಗಡೆಯಲ್ಲಿ ಇಸ್ರಾಯೇಲನ ಬಲಗೈಗೆ ಎದುರಾಗಿ ಮನಸ್ಸೆಯನ್ನೂ ನಿಲ್ಲಿಸಿ ಅವರಿಬ್ಬರನ್ನೂ ತಂದೆಯ ಬಳಿಗೆ ಬರಮಾಡಿದನು.",
			"14": "<sup>14</sup> ಮನಸ್ಸೆಯು ಚೊಚ್ಚಲುಮಗನಾಗಿದ್ದರೂ ಇಸ್ರಾಯೇಲನು ತನ್ನ ಕೈಗಳನ್ನು ಅಡ್ಡವಾಗಿ ಚಾಚಿ ಕಿರಿಯವನಾದ ಎಫ್ರಾಯೀಮನ ತಲೆಯ ಮೇಲೆ ಬಲಗೈಯನ್ನೂ ಮನಸ್ಸೆಯ ತಲೆಯ ಮೇಲೆ ಎಡಗೈಯನ್ನೂ ಇಟ್ಟು ಯೋಸೇಫನನ್ನು ಆಶೀರ್ವದಿಸಿ -",
			"15": "<sup>15</sup> ನನ್ನ ಪಿತೃಗಳಾದ ಅಬ್ರಹಾಮ್ ಇಸಾಕರು ಸೇವಿಸಿದ ದೇವರಾಗಿಯೂ ನನ್ನನ್ನು ಚಿಕ್ಕಂದಿನಿಂದ ಈ ದಿನದವರೆಗೂ ಪರಾಂಬರಿಸುತ್ತಾ ಬಂದ ದೇವರಾಗಿಯೂ",
			"16": "<sup>16</sup> ನನ್ನನ್ನು ಎಲ್ಲಾ ಕೇಡುಗಳಿಂದ ತಪ್ಪಿಸಿ ಕಾಪಾಡುತ್ತಾ ಬಂದ ದೂತನಾಗಿಯೂ ಇರುವಾತನು ಈ ಹುಡುಗರನ್ನು ಆಶೀರ್ವದಿಸಲಿ; ಇವರು ನನ್ನ ಹೆಸರನ್ನೂ ನನ್ನ ಪಿತೃಗಳಾದ ಅಬ್ರಹಾಮ್ ಇಸಾಕರ ಹೆಸರುಗಳನ್ನೂ ಮುಂದಕ್ಕೆ ತರಲಿ; ಇವರು ಭೂಮಿಯ ಮೇಲೆ ಹೆಚ್ಚಿ ಬಹುಸಂತಾನವನ್ನು ಪಡೆಯಲಿ ಅಂದನು.",
			"17": "<sup>17</sup> ತನ್ನ ತಂದೆಯು ಬಲಗೈಯನ್ನು ಎಫ್ರಾಯೀಮನ ತಲೆಯ ಮೇಲೆ ಇಡುವದನ್ನು ಯೋಸೇಫನು ಕಂಡು ಅಸಮಾಧಾನಪಟ್ಟು ಅವನ ಕೈಯನ್ನು ಎಫ್ರಾಯೀಮನ ತಲೆಯಿಂದ ಎತ್ತಿ ಮನಸ್ಸೆಯ ತಲೆಯ ಮೇಲೆ ಇಡುವದಕ್ಕಾಗಿ ಹಿಡಿದು ತಂದೆಗೆ -",
			"18": "<sup>18</sup> ಹಾಗಲ್ಲಪ್ಪಾ; ಇವನೇ ಚೊಚ್ಚಲಮಗನು; ಬಲಗೈಯನ್ನು ಇವನ ತಲೆಯ ಮೇಲೆ ಇಡಬೇಕು ಎಂದು ಹೇಳಿದನು.",
			"19": "<sup>19</sup> ಅದರೆ ಅವನ ತಂದೆ ಒಪ್ಪದೆ - ಮಗನೇ, ನಾನು ಬಲ್ಲೆ, ಬಲ್ಲೆ; ಇವನಿಂದಲೂ ಜನಾಂಗವುಂಟಾಗುವದು; ಇವನೂ ಬಲಿಷ್ಠನಾಗುವನು; ಆದರೂ ಇವನಿಗಿಂತಲೂ ಇವನ ತಮ್ಮನು ಬಲಿಷ್ಠನಾಗುವನು; ತಮ್ಮನ ಸಂತತಿಯಲ್ಲಿಯೇ ಜನರು ಹೇರಳವಾಗಿ ಹುಟ್ಟುವರು ಎಂದು ಹೇಳಿದನು.",
			"20": "<sup>20</sup> ಆ ಕಾಲದಲ್ಲಿ ಅವನು ಅವರನ್ನು ಆಶೀರ್ವದಿಸಿ - ಇಸ್ರಾಯೇಲ್ಯರು ಯಾರನ್ನಾದರೂ ಆಶೀರ್ವದಿಸುವಾಗ - ಎಫ್ರಾಯೀಮನಿಗೂ ಮನಸ್ಸೆಗೂ ಉಂಟಾದ ಸಂತತಿಯಂತೆ ದೇವರು ನಿನಗೂ ಸಂತಾನವನ್ನು ಅನುಗ್ರಹಿಸಲಿ ಎಂದು ನಿಮ್ಮನ್ನು ದೃಷ್ಟಾಂತಮಾಡಿ ಹೇಳುವರು ಅಂದನು. ಹೀಗೆ ಹೇಳಿದ್ದರಿಂದ ಎಫ್ರಾಯೀಮನನ್ನು ಮನಸ್ಸೆಗೆ ಮುಂದಾಗಿ ಇಟ್ಟನು.",
			"21": "<sup>21</sup> ಇಸ್ರಾಯೇಲನು ಯೋಸೇಫನಿಗೆ - ನನಗೆ ಮರಣ ಸಮೀಪಿಸಿದೆ; ಆದರೆ ದೇವರು ನಿಮ್ಮೊಂದಿಗಿದ್ದು ನಿಮ್ಮನ್ನು ಪಿತೃಗಳ ದೇಶಕ್ಕೆ ತಿರಿಗಿ ಬರಮಾಡುವನು.",
			"22": "<sup>22</sup> ಇದಲ್ಲದೆ ನಾನು ನಿನ್ನ ಅಣ್ಣಂದಿರಿಗೆ ಕೊಟ್ಟ ಪಾಲಿಗಿಂತ ಹೆಚ್ಚಾಗಿ ನಾನು ಕತ್ತಿಬಿಲ್ಲುಗಳಿಂದ ಅಮೋರಿಯರ ವಶದಿಂದ ತಪ್ಪಿಸಿ ಪಡೆದ ಬೆಟ್ಟದ ತಪ್ಪಲನ್ನು ನಿನಗೆ ಕೊಟ್ಟಿದ್ದೇನೆ ಎಂದು ಹೇಳಿದನು."
		},
		"49": {
			"1": "<sup>1</sup> ಯಾಕೋಬನು ತನ್ನ ಮಕ್ಕಳನ್ನು ಕರಸಿ - ನೀವೆಲ್ಲರೂ ಕೂ ಡಿಬನ್ನಿರಿ; ಮುಂದಿನ ಕಾಲದಲ್ಲಿ ನಿಮಗೆ ಸಂಭವಿಸುವದನ್ನು ತಿಳಿಸುತ್ತೇನೆ.",
			"2": "<sup>2</sup> ಯಾಕೋಬನ ಮಕ್ಕಳೇ, ನೀವೆಲ್ಲರೂ ಕೂಡಿಬಂದು ಕೇಳಿರಿ; ನಿಮ್ಮ ತಂದೆಯಾದ ಇಸ್ರಾಯೇಲನ ಮಾತನ್ನು ಲಾಲಿಸಿರಿ.",
			"3": "<sup>3</sup> ರೂಬೇನನೇ, ನೀನು ನನ್ನ ಚೊಚ್ಚಲಮಗನೂ, ನನ್ನ ಚೈತನ್ಯಸ್ವರೂಪನೂ ನನ್ನ ವೀರ್ಯಕ್ಕೆ ಪ್ರಥಮ ಫಲವೂ ಗೌರವದಲ್ಲಿಯೂ ಅಧಿಕಾರದಲ್ಲಿಯೂ ಪ್ರಮುಖನೂ ಆಗಿದ್ದೀ.",
			"4": "<sup>4</sup> ಆದರೂ ದಡವನ್ನು ಮೀರಿದ ಪ್ರವಾಹದಂತಿರುವ ನೀನು ಇನ್ನು ಪ್ರಮುಖನ ಸ್ಥಾನದಲ್ಲಿ ಇರುವದಿಲ್ಲ. ನಿನ್ನ ತಂದೆಯ ಹಾಸಿಗೆಯನ್ನು ಹತ್ತಿ ಅದನ್ನು ಹೊಲೆ ಮಾಡಿದಿಯಲ್ಲಾ. ಇವನು ನನ್ನ ಮಂಚವನ್ನು ಹತ್ತಿದನು!",
			"5": "<sup>5</sup> ಸಿಮೆಯೋನನೂ ಲೇವಿಯೂ ಅಣ್ಣತಮ್ಮಂದಿರೇ ಸರಿ; ಇವರ ಕತ್ತಿಗಳು ಬಲಾತ್ಕಾರದ ಆಯುಧಗಳೇ.",
			"6": "<sup>6</sup> ನನ್ನ ಪ್ರಾಣವೇ, ಅವರ ಗುಪ್ತವಾದ ದುರಾಲೋಚನೆಗೆ ನೀನು ಒಳಪಡಬಾರದು; ನನ್ನ ಮನವೇ, ಅವರ ಗುಂಪಿಗೆ ನೀನು ಸೇರಬೇಡ. ಅವರು ಕೋಪೋದ್ರೇಕದಿಂದ ಮನುಷ್ಯರನ್ನು ಸಂಹರಿಸಿದರು; ಮದದಿಂದ ಎತ್ತುಗಳನ್ನು ಊನಪಡಿಸಿದರು.",
			"7": "<sup>7</sup> ಅವರ ಕೋಪವು ಭಯಂಕರವಾಗಿಯೂ ಅವರ ರೌದ್ರವು ಕ್ರೂರವಾಗಿಯೂ ಇದೆ; ಅದಕ್ಕೆ ಶಾಪವುಂಟಾಗಲಿ. ಯಾಕೋಬನ ಕುಲದವರಲ್ಲಿ ಅವರನ್ನು ವಿಭಾಗಿಸುವೆನು; ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಅವರನ್ನು ಚದರಿಸುವೆನು.",
			"8": "<sup>8</sup> ಯೆಹೂದನೇ, ನಿನ್ನ ಅಣ್ಣತಮ್ಮಂದಿರು ನಿನ್ನನ್ನೇ ಸ್ತುತಿಸುವರು. ನಿನ್ನ ಕೈ ನಿನ್ನ ಶತ್ರುಗಳ ಕುತ್ತಿಗೆಯ ಮೇಲಿರುವದು; ನಿನ್ನ ಸಹೋದರರು ನಿನಗೇ ಎರಗುವರು.",
			"9": "<sup>9</sup> ಯೆಹೂದನು ಪ್ರಾಯದ ಸಿಂಹದಂತಿದ್ದಾನೆ; ನನ್ನ ಪುತ್ರನೇ, ನೀನು ಮೃಗವನ್ನು ಹಿಡಿದು ಕೊಂದು ಬೆಟ್ಟವನ್ನು ಸೇರಿದ ಸಿಂಹದೋಪಾದಿಯಲಿದ್ದೀ. ಅವನು ಸಿಂಹದಂತೆ ಕಾಲುಮುದುರಿ ಹೊಂಚುಹಾಕಿಕೊಂಡಿದ್ದಾನೆ; ಮೃಗೇಂದ್ರನಿಗೆ ಸಮಾನನಾದ ಇವನನ್ನು ಕೆಣಕುವದು ಯಾರಿಂದಾದೀತು?",
			"10": "<sup>10</sup> ರಾಜದಂಡವನ್ನು ಹಿಡಿಯತಕ್ಕವನು ಬರುವ ತನಕ ಆ ದಂಡವು ಯೆಹೂದನ ಕೈಯಿಂದ ತಪ್ಪುವದಿಲ್ಲ, ಮುದ್ರೆಕೋಲು ಅವನ ಪಾದಗಳ ಬಳಿಯಿಂದ ಕದಲುವದಿಲ್ಲ; ಅವನಿಗೆ ಅನ್ಯಜನಗಳೂ ವಿಧೇಯರಾಗಿರುವರು.",
			"11": "<sup>11</sup> ಅವನು ತನ್ನ ವಾಹನಪಶುವನ್ನು ದ್ರಾಕ್ಷಾಲತೆಗೆ ಕಟ್ಟುವನು, ರಾಜದ್ರಾಕ್ಷೆಗೆ ತನ್ನ ಕತ್ತೆಯನ್ನು ಬಿಗಿಯುವನು; ದ್ರಾಕ್ಷಾರಸದಲ್ಲಿ ತನ್ನ ಬಟ್ಟೆಗಳನ್ನು ಒಗೆಯುವನು, ದ್ರಾಕ್ಷಾರಸದಲ್ಲಿಯೇ ತನ್ನ ವಸ್ತ್ರಗಳನ್ನು ಅದ್ದಿ ತೊಳೆಯುವನು:",
			"12": "<sup>12</sup> ದ್ರಾಕ್ಷಾರಸದ ಸಮೃದ್ಧಿಯಿಂದ ಅವನ ಕಣ್ಣುಗಳು ಕೆಂಪಾಗಿಯೇ ಇರುವವು. ಹಾಲಿನ ಸಮೃದ್ಧಿಯಿಂದ ಅವನ ಹಲ್ಲುಗಳು ಬೆಳ್ಳಗಾಗಿಯೇ ಇರುವವು.",
			"13": "<sup>13</sup> ಜೆಬುಲೂನನು ಸಮುದ್ರದ ಕರಾವಳಿಯಲ್ಲಿ ವಾಸಮಾಡುವನು; ಅವನಿಗೆ ಹಡಗುಗಳು ಸೇರುವ ರೇವು ಇರುವದು. ಅವನ ಸೀಮೆಯ ಒಂದು ಮೇರೆ ಚೀದೋನಿಗೆ ಮುಟ್ಟುವದು.",
			"14": "<sup>14</sup> ಇಸ್ಸಾಕಾರನು ಕುರೀಹಟ್ಟಿಗಳ ನಡುವೆ ಮಲಗಿಕೊಂಡಿರುವ ಬಲವುಳ್ಳ ಕತ್ತೆಯಂತಿರುವನು.",
			"15": "<sup>15</sup> ತಾನು ಸೇರಿದ ಪ್ರದೇಶವು ವಿಶ್ರಾಂತಿಗೆ ಅನುಕೂಲವೂ ಸುಖಾಸ್ಪದವೂ ಆಗಿರುವದನ್ನು ನೋಡಿ ಅವನು ಹೊರೆಯನ್ನು ಹೊರುವದಕ್ಕೆ ಬೆನ್ನು ಬೊಗ್ಗಿಸಿಕೊಂಡು ಬಿಟ್ಟೀ ಕೆಲಸವನ್ನು ಮಾಡುವವನಾದನು.",
			"16": "<sup>16</sup> ದಾನನು ಇಸ್ರಾಯೇಲ್ಯರ ಕುಲಗಳಿಗೆ ಯೋಗ್ಯವಾಗುವ ರೀತಿಯಲ್ಲಿ ತನ್ನ ಜನರ ವ್ಯಾಜ್ಯವನ್ನು ಜಯಕ್ಕೆ ತರುವನು.",
			"17": "<sup>17</sup> ದಾನನು ದಾರಿಯಲ್ಲಿರುವ ಹಾವಿನಂತೆಯೂ ಮಾರ್ಗದಲ್ಲಿ ಕುದುರೆಯ ಹಿಮ್ಮಡಿಯನ್ನು ಕಚ್ಚಿ ಸವಾರನನ್ನು ಕೆಡಹುವ ವಿಷ ಸರ್ಪದಂತೆಯೂ ಆಗಿರುವನು.",
			"18": "<sup>18</sup> ಯೆಹೋವನೇ, ನಿನ್ನಿಂದುಂಟಾಗುವ ರಕ್ಷಣೆಯು ಯಾವಾಗ ಆಗುವದೋ ಎಂದು ನಿರೀಕ್ಷಿಸಿಕೊಂಡಿದ್ದೇನೆ.",
			"19": "<sup>19</sup> ಗಾದನ ಸಂಗತಿ - ಸುಲಿಗೆಮಾಡುವವರು ದಂಡೆತ್ತಿ ಅವನ ಮೇಲೆ ಬೀಳಲು ಇವನು ಅವರನ್ನು ಹಿಮ್ಮೆಟ್ಟಿಕೊಂಡು ಹೋಗುವನು.",
			"20": "<sup>20</sup> ಆಶೇರನಿಗೆ ಧಾನ್ಯಸಮೃದ್ಧಿಯಾಗುವದು; ಅವನಲ್ಲಿ ರಾಜಭೋಗ್ಯವಾದ ಪದಾರ್ಥಗಳು ದೊರಕುವವು.",
			"21": "<sup>21</sup> ನಫ್ತಾಲಿ ಬಿಡಿಸಿಕೊಂಡಿರುವ ಜಿಂಕೆಯಂತಿದ್ದಾನೆ. ಅವನಿಂದ ಇಂಪಾದ ಮಾತುಗಳುಂಟಾಗುತ್ತವೆ.",
			"22": "<sup>22</sup> ಯೋಸೇಫನು ಬಹುಫಲವನ್ನು ಬಿಡುವ ವೃಕ್ಷಕ್ಕೆ ಸಮಾನನಾಗಿದ್ದಾನೆ, ಒರತೆಯ ಬಳಿಯಲ್ಲಿ ಬೆಳೆದು ರೆಂಬೆಗಳನ್ನು ಗೋಡೆಯ ಆಚೆಗೆ ಚಾಚಿರುವ ಫಲವೃಕ್ಷದಂತಿದ್ದಾನೆ.",
			"23": "<sup>23</sup> ಬಿಲ್ಲುಗಾರರು ಬಂದು ಅವನನ್ನು ಕೆಣಕಿ ಬಾಣ ಎಸೆದು ಕವಿದುಕೊಂಡರು.",
			"24": "<sup>24</sup> ಆದರೂ ಯಾಕೋಬನ ವಂಶಸ್ಥರನ್ನು ರಕ್ಷಿಸುವ ಪರಾಕ್ರಮಿಯ ಭುಜಬಲದಿಂದಲೂ ಇಸ್ರಾಯೇಲನಿಗೆ ಪಾಲಕನೂ ಬಂಡೆಯೂ ಆಗಿರುವಾತನ ಕಡೆಯಿಂದಲೂ ಅವನ ಬಿಲ್ಲು ಸ್ಥಿರವಾಗಿ ನಿಂತಿತು, ಅವನ ಕೈಗಳು ಚುರುಕಾಗಿ ಮಾಡಲ್ಪಟ್ಟವು.",
			"25": "<sup>25</sup> ಇದು ನಿನ್ನ ತಂದೆಯ ದೇವರಿಂದಾಯಿತು, ಆತನು ನಿನಗೆ ಸಹಾಯ ಮಾಡಲಿ; ಸರ್ವಶಕ್ತನಾದ ದೇವರಿಂದಾಯಿತು, ಅತನು ನಿನ್ನನ್ನು ಆಶೀರ್ವದಿಸಲಿ. ಆತನು ಮೇಲಣ ಆಕಾಶದಿಂದಲೂ ಕೆಳಗಣ ಸಾಗರದ ಸೆಲೆಗಳಿಂದಲೂ ಸ್ತನ್ಯದಿಂದಲೂ ಗರ್ಭದಿಂದಲೂ ಉಂಟಾಗುವ ಸೌಭಾಗ್ಯಗಳನ್ನು ಕೊಟ್ಟು ನಿನ್ನನ್ನು ಆಶೀರ್ವದಿಸಲಿ.",
			"26": "<sup>26</sup> ನಿನ್ನ ತಂದೆಯ ಆಶೀರ್ವಾದಗಳು ಆದಿಯಿಂದಿದ್ದ ಪರ್ವತಗಳಿಂದುಟಾಗುವ ಮೇಲುಗಳಿಗಿಂತಲೂ ಸದಾಕಾಲವಾಗಿರುವ ಬೆಟ್ಟಗಳಿಂದುಂಟಾಗುವ ಉತ್ತಮ ವಸ್ತುಗಳಿಗಿಂತಲೂ ವಿಶೇಷವಾಗಿವೆ. ಇವೆಲ್ಲಾ ಯೋಸೇಫನ ಪಾಲಿಗೆ ಬರಲಿ; ಅಣ್ಣತಮ್ಮಂದಿರಲ್ಲಿ ಪ್ರಭುವಾಗಿರುವವನ ಶಿರಸ್ಸಿನ ಮೇಲುಂಟಾಗಲಿ.",
			"27": "<sup>27</sup> ಬೆನ್ಯಾಮೀನನು ಕುರಿಗಳನ್ನು ಹಿಡಿದುಕೊಳ್ಳುವ ತೋಳದಂತಿದ್ದಾನೆ. ಹಿಡಿದುಕೊಂಡದ್ದನ್ನು ಬೆಳಿಗ್ಗೆ ಉಣ್ಣುವನು. ಕೊಳ್ಳೆಮಾಡಿದ್ದನ್ನು ಸಂಜೆಯಲ್ಲಿ ಹಂಚಿಕೊಳ್ಳುವನು.",
			"28": "<sup>28</sup> ಇವರೆಲ್ಲರೂ ಇಸ್ರಾಯೇಲನಿಂದುಂಟಾದ ಹನ್ನೆರಡು ಕುಲಗಳು. ಇದೇ ಅವರ ತಂದೆ ಅವರಿಗೆ ಹೇಳಿದ ಆಶೀರ್ವಾದ. ಒಂದೊಂದು ಕುಲಕ್ಕೆ ಒಂದೊಂದು ಆಶೀರ್ವಚನವನ್ನು ನುಡಿದನು.",
			"29": "<sup>29</sup> ಇಸ್ರಾಯೇಲನು ತನ್ನ ಮಕ್ಕಳಿಗೆ ಆಜ್ಞಾಪಿಸಿ ಹೇಳಿದ್ದೇನಂದರೆ - ನಾನು ನನ್ನ ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಬೇಕಾದ ಕಾಲ ಸಮೀಪಿಸಿತು. ಹಿತ್ತಿಯನಾದ ಎಫ್ರೋನನ ಭೂಮಿಯಲ್ಲಿರುವ ಗವಿಯೊಳಗೆ ನನಗೆ ಪಿತೃಗಳ ಬಳಿಯಲ್ಲಿ ಸಮಾಧಿಮಾಡಬೇಕು.",
			"30": "<sup>30</sup> ಆ ಗವಿಯು ಕಾನಾನ್‍ದೇಶದ ಮಮ್ರೆಗೆದುರಾಗಿರುವ ಮಕ್ಪೇಲ ಎಂಬ ಬೈಲಿನಲ್ಲಿ ಅದೆ. ಅದನ್ನು ಅಬ್ರಹಾಮನು ಅದರ ಸುತ್ತಲಿರುವ ಭೂಮಿಸಹಿತವಾಗಿ ಹಿತ್ತಿಯನಾದ ಎಫ್ರೋನನಿಂದ ಸ್ವಕೀಯ ಶ್ಮಶಾನಭೂಮಿಯಾಗುವದಕ್ಕೆ ಕೊಂಡುಕೊಂಡನು.",
			"31": "<sup>31</sup> ಅಲ್ಲಿ ಅಬ್ರಹಾಮನಿಗೂ ಅವನ ಪತ್ನಿಯಾದ ಸಾರಳಿಗೂ ಸಮಾಧಿಯಾಯಿತು; ಅಲ್ಲಿ ಇಸಾಕನಿಗೂ ಅವನ ಹೆಂಡತಿಯಾದ ರೆಬೆಕ್ಕಳಿಗೂ ಸಮಾಧಿಯಾಯಿತು; ಅಲ್ಲಿ ನಾನು ಲೇಯಳನ್ನೂ ಹೂಣಿಟ್ಟೆನು.",
			"32": "<sup>32</sup> ಹೊಲದ ಕೂಡ ಹಿತ್ತಿಯರಿಂದ ಕ್ರಯಕ್ಕೆ ತೆಗೆದುಕೊಂಡ ಆ ಗವಿಯೊಳಗೆ ನನಗೂ ಸಮಾಧಿಮಾಡಬೇಕು ಅಂದನು.",
			"33": "<sup>33</sup> ಯಾಕೋಬನು ತನ್ನ ಮಕ್ಕಳಿಗೆ ಅಪ್ಪಣೆಕೊಡುವದನ್ನು ಮುಗಿಸಿದನಂತರ ಹಾಸಿಗೆಯ ಮೇಲೆ ತನ್ನ ಕಾಲುಗಳನ್ನು ಮುದುರಿಕೊಂಡು ಪ್ರಾಣಬಿಟ್ಟು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಿದನು."
		},
		"50": {
			"1": "<sup>1</sup> ಆಗ ಯೋಸೇಫನು ತನ್ನ ತಂದೆಯ ಮುಖದ ಮೇಲೆ ಬಿದ್ದು ಕಣ್ಣೀರು ಸುರಿಸುತ್ತಾ ಅವನಿಗೆ ಮುದ್ದಿಟ್ಟನು.",
			"2": "<sup>2</sup> ತರುವಾಯ ಅವನು ತನ್ನ ಸೇವಕರಾದ ವೈದ್ಯರಿಗೆ - ನನ್ನ ತಂದೆಯ ಶವವನ್ನು ಸುಗಂಧದ್ರವ್ಯಗಳಿಂದ ತುಂಬಿ ಸಿದ್ಧಪಡಿಸಿರಿ ಎಂದು ಅಪ್ಪಣೆಕೊಡಲು ಅವರು ಹಾಗೆಯೇ ಮಾಡಿದರು.",
			"3": "<sup>3</sup> ನಾಲ್ವತ್ತು ದಿನಗಳು ಸುಗಂಧದ್ರವ್ಯಗಳನ್ನು ತುಂಬುವ ಪದ್ಧತಿಯಿರುವದರಿಂದ ಅಷ್ಟು ದಿವಸ ಸಿದ್ಧಪಡಿಸುತ್ತಾ ಇದ್ದರು. ಐಗುಪ್ತ್ಯರು ಅವನಿಗೋಸ್ಕರ ಎಪ್ಪತ್ತು ದಿನಗಳವರೆಗೂ ಗೋಳಾಡಿದರು.",
			"4": "<sup>4</sup> ಗೋಳಾಡುವ ದಿನಗಳು ಮುಗಿದನಂತರ ಯೋಸೇಫನು ಫರೋಹನ ಮನೆಯವರ ಬಳಿಗೆ ಹೋಗಿ - ನನ್ನ ತಂದೆಯು ತಾನು ಕಾನಾನ್‍ದೇಶದಲ್ಲಿ ಸಿದ್ಧಮಾಡಿಕೊಂಡಿರುವ ಸ್ಥಳದಲ್ಲಿಯೇ ತನಗೆ ಸಮಾಧಿಮಾಡಬೇಕೆಂದು ಸಾಯುವದಕ್ಕಿಂತ ಮೊದಲು ನನ್ನಿಂದ ಪ್ರಮಾಣಮಾಡಿಸಿದನು. ಆದದರಿಂದ ನನ್ನ ಮೇಲೆ ಕಟಾಕ್ಷವಿದ್ದರೆ ನೀವು ಈ ಸಂಗತಿಯನ್ನು ಫರೋಹನಿಗೆ ತಿಳಿಸಿ ನಾನು ಹೋಗಿ ತಂದೆಗೆ ಸಮಾಧಿಮಾಡಿ ಬರುವದಕ್ಕೆ ಅಪ್ಪಣೆ ಕೊಡಿಸಬೇಕೆಂದು ಬೇಡಿಕೊಳ್ಳುತ್ತೇನೆ ಅಂದನು.",
			"6": "<sup>6</sup> ಫರೋಹನು ಈ ಸಂಗತಿಯನ್ನು ಕೇಳಿ ಯೋಸೇಫನಿಗೆ - ನಿನ್ನ ತಂದೆ ಪ್ರಮಾಣಮಾಡಿಸಿದ ಮೇರೆಗೆ ನೀನು ಹೋಗಿ ಅವನಿಗೆ ಸಮಾಧಿಮಾಡಿಬರಬಹುದೆಂದು ಅಪ್ಪಣೆಕೊಟ್ಟನು.",
			"7": "<sup>7</sup> ಯೋಸೇಫನು ತಂದೆಗೆ ಸಮಾಧಿಮಾಡುವದಕ್ಕೆ ಹೊರಟುಹೋದಾಗ ಅವನ ಜೊತೆಯಲ್ಲಿ ಫರೋಹನ ಪರಿವಾರದವರೆಲ್ಲರೂ ಅರಮನೆಯ ಮುಖಂಡರೆಲ್ಲರೂ",
			"8": "<sup>8</sup> ಐಗುಪ್ತದೇಶದ ಮುಖ್ಯಸ್ಥರೆಲ್ಲರೂ ಯೋಸೇಫನ ಮನೆಯವರೆಲ್ಲರೂ ಅಣ್ಣತಮ್ಮಂದಿರೂ ತಂದೆಯ ಮನೆಯವರೆಲ್ಲರೂ ಹೋದರು. ಅವರು ತಮ್ಮ ಹೆಂಡತಿ ಮಕ್ಕಳನ್ನೂ ಕುರಿದನಗಳನ್ನೂ ಮಾತ್ರ ಗೋಷೆನ್ ಸೀಮೆಯಲ್ಲಿ ಬಿಟ್ಟು ಹೋದರು.",
			"9": "<sup>9</sup> ಇದಲ್ಲದೆ ಅವನ ಸಂಗಡ ರಥಗಳೂ ಕುದುರೆಗಳೂ ಸಹ ಹೋದವು. ಹೊರಟುಹೋದವರು ಬಹು ಮಂದಿ.",
			"10": "<sup>10</sup> ಅವರು ಯೊರ್ದನ್ ಹೊಳೆಯಿಂದಾಚೆ ಇರುವ ಆಟಾದ್ ಎಂಬ ಕಣಕ್ಕೆ ಬಂದು ಅಲ್ಲಿ ಅತ್ಯಧಿಕವಾಗಿ ಗೋಳಾಡಿದರು. ಯೋಸೇಫನು ತನ್ನ ತಂದೆಗೋಸ್ಕರ ಏಳು ದಿನಗಳ ತನಕ ಗೋಳಾಡಿಸಿದನು.",
			"11": "<sup>11</sup> ಆ ದೇಶದ ಜನರಾಗಿದ್ದ ಕಾನಾನ್ಯರು ಆಟಾದ್ ಎಂಬ ಕಣದಲ್ಲಿ ನಡೆದ ಗೋಳಾಟವನ್ನು ನೋಡಿ - ಐಗುಪ್ತ್ಯರಿಗೆ ಬಂದಿರುವ ಈ ಶೋಕವು ಬಹುವಿಶೇಷವಾಗಿದೆ ಎಂದು ಹೇಳಿದ್ದರಿಂದ ಆ ಸ್ಥಳಕ್ಕೆ ಆಬೇಲ್‍ಮಿಚ್ರಯೀಮ್ ಎಂದು ಹೆಸರಾಯಿತು. ಇದು ಯೊರ್ದನ್ ಹೊಳೆಯಿಂದ ಆಚೆಯಿದೆ.",
			"12": "<sup>12</sup> ಯಾಕೋಬನ ಮಕ್ಕಳು ತಂದೆಯ ಅಪ್ಪಣೆಯ ಮೇರೆಗೆ",
			"13": "<sup>13</sup> ಅವನ ಶವವನ್ನು ಕಾನಾನ್‍ದೇಶಕ್ಕೆ ಹೊತ್ತುಕೊಂಡುಹೋಗಿ ಮಕ್ಪೇಲ ಎಂಬ ಬೈಲಿನಲ್ಲಿರುವ ಗವಿಯೊಳಗೆ ಸಮಾಧಿಮಾಡಿದರು. ಅಬ್ರಹಾಮನು ಹಿತ್ತಿಯನಾದ ಎಫ್ರೋನನಿಂದ ಮಮ್ರೆಗೆದುರಿನಲ್ಲಿರುವ ಆ ಗವಿಯನ್ನು ಸುತ್ತಲಿರುವ ಭೂಮಿಸಹಿತ ಸ್ವಕೀಯ ಶ್ಮಶಾನಭೂಮಿಯಾಗುವದಕ್ಕೆ ಕೊಂಡುಕೊಂಡಿದ್ದನು.",
			"14": "<sup>14</sup> ತಂದೆಗೆ ಸಮಾಧಿಮಾಡಿದ ಮೇಲೆ ಯೋಸೇಫನೂ ಅವನ ಅಣ್ಣತಮ್ಮಂದಿರೂ ಅವನ ತಂದೆಯ ಉತ್ತರಕ್ರಿಯೆಗೋಸ್ಕರ ಜೊತೆಯಲ್ಲಿ ಹೋಗಿದ್ದವರೆಲ್ಲರೂ ಐಗುಪ್ತಕ್ಕೆ ಹಿಂದಿರುಗಿ ಬಂದರು.",
			"15": "<sup>15</sup> ಯೋಸೇಫನ ಅಣ್ಣತಮ್ಮಂದಿರು ತಂದೆ ಸತ್ತದ್ದನ್ನು ಕಂಡು - ಒಂದು ವೇಳೆ ಯೋಸೇಫನು ನಮ್ಮನ್ನು ದ್ವೇಷಿಸಿ ನಾವು ಅವನಿಗೆ ಮಾಡಿದ ಎಲ್ಲಾ ಕೇಡಿಗೆ ಪ್ರತಿಯಾಗಿ ಪೂರಾ ಶಿಕ್ಷೆಯನ್ನು ಕೊಟ್ಟಾನು ಎಂದು ತಮ್ಮತಮ್ಮೊಳಗೆ ಮಾತಾಡಿಕೊಂಡು ಯೋಸೇಫನಿಗೆ -",
			"16": "<sup>16</sup> ನಿನ್ನ ತಂದೆಯು ಸಾಯುವದಕ್ಕಿಂತ ಮುಂಚೆ ನಮಗೆ ಅಪ್ಪಣೆಮಾಡಿದ್ದೇನಂದರೆ, ನೀವು ಯೋಸೇಫನಿಗೆ -",
			"17": "<sup>17</sup> ನಿನ್ನ ಅಣ್ಣಂದಿರಾದ ನಾವು ನಿನಗೆ ಕೇಡು ಮಾಡಿದ್ದು ನಿಜವೇ; ಆದರೂ ನಮ್ಮ ಅಪರಾಧವನ್ನೂ ಪಾಪವನ್ನೂ ಕ್ಷಮಿಸು ಎಂಬದಾಗಿ ಹೇಳಿಕೊಳ್ಳಬೇಕು; ಹೀಗೆ ಅವನು ಅಪ್ಪಣೆಮಾಡಿದ್ದರಿಂದ ನೀನು ಈಗ ನಿನ್ನ ತಂದೆಯ ದೇವರಿಗೆ ಭಕ್ತರಾಗಿರುವ ನಾವು ಮಾಡಿದ ಅಪರಾಧವನ್ನು ಕ್ಷಮಿಸಬೇಕೆಂದು ಬೇಡಿಕೊಳ್ಳುತ್ತೇವೆಂದು ಹೇಳಿಕಳುಹಿಸಿದರು. ಯೋಸೇಫನು ಈ ಮಾತುಗಳನ್ನು ಕೇಳಿ ಕಣ್ಣೀರು ಸುರಿಸಿದನು.",
			"18": "<sup>18</sup> ಇದಲ್ಲದೆ ಅಣ್ಣಂದಿರು ತಾವೇ ಬಂದು ಅವನ ಮುಂದೆ ಅಡ್ಡಬಿದ್ದು - ಇಗೋ, ನಾವು ನಿನಗೆ ಗುಲಾಮರು ಅಂದರು.",
			"19": "<sup>19</sup> ಯೋಸೇಫನು ಅವರಿಗೆ - ಹೆದರಬೇಡಿರಿ; ನಾನೇನು ದೇವರ ಪ್ರತಿನಿಧಿಯೇ? ನೀವಂತೂ ನನಗೆ ಕೇಡಾಗಬೇಕೆಂದು ಎಣಿಸಿದ್ದಿರಿ;",
			"20": "<sup>20</sup> ಆದರೆ ದೇವರು ಮೇಲಾಗಬೇಕೆಂದು ಸಂಕಲ್ಪಿಸಿದನು. ಈಗ ಅನುಭವಕ್ಕೆ ಬಂದ ಪ್ರಕಾರವೇ ಅನೇಕ ಪ್ರಾಣಿಗಳಿಗೆ ಸಂರಕ್ಷಣೆಯುಂಟಾಗುವಂತೆ ಮಾಡಿದನು.",
			"21": "<sup>21</sup> ಆದದರಿಂದ ನೀವು ಸ್ವಲ್ಪವೂ ಭಯಪಡಬೇಡಿರಿ; ನಾನು ನಿಮ್ಮನ್ನೂ ನಿಮಗೆ ಸೇರಿದವರೆಲ್ಲರನ್ನೂ ಪೋಷಿಸುವೆನು ಎಂದು ಹೇಳಿ ಅವರನ್ನು ಸಂತೈಸಿ ಅವರ ಸಂಗಡ ಪ್ರೀತಿಯಿಂದ ಮಾತಾಡಿದನು.",
			"22": "<sup>22</sup> ಹೀಗೆ ಯೋಸೇಫನೂ ಅವನ ತಂದೆಯ ಮನೆಯವರೂ ಐಗುಪ್ತದೇಶದಲ್ಲಿ ವಾಸಮಾಡಿದರು. ಯೋಸೇಫನು ನೂರಹತ್ತು ವರುಷ ಬದುಕಿದನು.",
			"23": "<sup>23</sup> ಅವನು ಎಫ್ರಾಯೀಮನ ಮಕ್ಕಳ ಮೊಮ್ಮಕ್ಕಳನ್ನು ನೋಡಿದನು; ಮತ್ತು ಮನಸ್ಸೆಯ ಮಗನಾದ ಮಾಕೀರನಿಗೂ ಮಕ್ಕಳು ಹುಟ್ಟಿದಾಗ ಯೋಸೇಫನು ಅವರನ್ನು ತನ್ನ ತೊಡೆಯ ಮೇಲೆ ಮಲಗಿಸಿಕೊಂಡನು.",
			"24": "<sup>24</sup> ಯೋಸೇಫನು ತನ್ನ ಅಣ್ಣತಮ್ಮಂದಿರಿಗೆ - ನನಗೆ ಅವಸಾನಕಾಲ ಸಮೀಪಿಸಿತು. ಆದರೆ ದೇವರು ನಿಶ್ಚಯವಾಗಿ ನಿಮ್ಮನ್ನು ಪರಾಂಬರಿಸಿ ಈ ದೇಶದಿಂದ ತಾನು ಅಬ್ರಹಾಮ್ ಇಸಾಕ್ ಯಾಕೋಬರಿಗೆ ಕೊಡುತ್ತೇನೆಂದು ಪ್ರಮಾಣವಾಗಿ ಹೇಳಿದ ದೇಶಕ್ಕೆ ನೀವು ಹೋಗಿ ಸೇರುವಂತೆ ಮಾಡುವನೆಂದು ತಿಳಿದುಕೊಳ್ಳಿರಿ ಎಂದು ಹೇಳಿದನು.",
			"25": "<sup>25</sup> ಇದಲ್ಲದೆ ಯೋಸೇಫನು ಇಸ್ರಾಯೇಲನ ಮಕ್ಕಳಿಗೆ - ದೇವರು ನಿಸ್ಸಂದೇಹವಾಗಿ ನಿಮ್ಮನ್ನು ಪರಾಂಬರಿಸುವನು; ಆಗ ನೀವು ನನ್ನ ಶವವನ್ನು ನಿಮ್ಮ ಸಂಗಡ ಹೊತ್ತುಕೊಂಡು ಹೋಗಬೇಕೆಂದು ಹೇಳಿ, ಹಾಗೆಯೇ ಮಾಡುತ್ತೇವೆಂಬದಾಗಿ ಅವರಿಂದ ಪ್ರಮಾಣ ಮಾಡಿಸಿದನು.",
			"26": "<sup>26</sup> ಯೋಸೇಫನು ನೂರಹತ್ತು ವರುಷದವನಾಗಿ ಸತ್ತನು. ಅವನ ಶವಕ್ಕೆ ಸುಗಂಧದ್ರವ್ಯಗಳನ್ನು ತುಂಬಿ ಐಗುಪ್ತದೇಶದೊಳಗೆ ಅದನ್ನು ಪೆಟ್ಟಿಗೆಯಲ್ಲಿ ಇರಿಸಿದರು."
		}
	}
};
module.exports = book;