var book = {
	"name": "ಯಾಜಕಕಾಂಡ",
	"numChapters": 27,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಮೋಶೆಯನ್ನು ಕರೆದು ದೇವದರ್ಶನದ ಗುಡಾರದೊಳಗಿಂದ ಅವನ ಸಂಗಡ ಮಾತಾಡಿ ಹೀಗಂದನು - ನೀನು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ",
			"2": "<sup>2</sup> ಆಜ್ಞಾಪಿಸಬೇಕಾದದ್ದೇನಂದರೆ - ನಿಮ್ಮಲ್ಲಿ ಯಾವನಾದರೂ ಯೆಹೋವನಿಗೆ ಪಶುವನ್ನು ಕಾಣಿಕೆಯಾಗಿ ಸಮರ್ಪಿಸಬೇಕೆಂದಿದ್ದರೆ ಅದನ್ನು ದನಗಳಿಂದಾಗಲಿ ಆಡುಕುರಿಗಳಿಂದಾಗಲಿ ತೆಗೆದುಕೊಂಡು ಸಮರ್ಪಿಸಬೇಕು.",
			"3": "<sup>3</sup> ಅವನು ದನವನ್ನು ಸರ್ವಾಂಗಹೋಮವಾಗಿ ಸಮರ್ಪಿಸುವದಾದರೆ ಪೂರ್ಣಾಂಗವಾದ ಗಂಡನ್ನು ತರಬೇಕು. ತನಗೆ ಯೆಹೋವನು ಒಲಿಯುವಂತೆ ದೇವದರ್ಶನದ ಗುಡಾರದ ಬಾಗಲಿಗೆ ಅದನ್ನು ತರಬೇಕು.",
			"4": "<sup>4</sup> ಅವನು ಆ ಪಶುವಿನ ತಲೆಯ ಮೇಲೆ ಕೈಯಿಡಬೇಕು; ಆಗ ಅದು ಅವನಿಗೋಸ್ಕರ ದೋಷಪರಿಹಾರವನ್ನು ಮಾಡುವದಕ್ಕಾಗಿ ಅಂಗೀಕಾರವಾಗುವದು.",
			"5": "<sup>5</sup> ಅವನು ಆ ಹೋರಿಯನ್ನು ಯೆಹೋವನ ಎದುರಿನಲ್ಲಿ ವಧಿಸಿದ ಮೇಲೆ ಆರೋನನ ವಂಶದವರಾದ ಯಾಜಕರು ಅದರ ರಕ್ತವನ್ನು ತೆಗೆದುಕೊಂಡು ದೇವದರ್ಶನದ ಗುಡಾರದ ಬಾಗಲಿಗೆ ಎದುರಾಗಿರುವ ಯಜ್ಞವೇದಿಗೆ ಸುತ್ತಲೂ ಎರಚಬೇಕು.",
			"6": "<sup>6</sup> ಮತ್ತು ಅವನು ಆ ಪಶುವಿನ ಚರ್ಮವನ್ನು ಸುಲಿದು ಅದರ ದೇಹವನ್ನು ತುಂಡುತುಂಡಾಗಿ ಕಡಿಯಬೇಕು.",
			"7": "<sup>7</sup> ಆರೋನನ ವಂಶದವರಾದ ಯಾಜಕರು ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಬೆಂಕಿಯನ್ನಿಟ್ಟು",
			"8": "<sup>8</sup> ಅದರ ಮೇಲೆ ಕಟ್ಟಿಗೆಯನ್ನು ಒಟ್ಟಿ ಆ ತುಂಡುಗಳನ್ನೂ ತಲೆಯನ್ನೂ ಕೊಬ್ಬನ್ನೂ ಅದರ ಮೇಲೆ ಕ್ರಮವಾಗಿ ಇಡಬೇಕು.",
			"9": "<sup>9</sup> ಅದರ ಕರುಳುಗಳನ್ನೂ ಕಾಲುಗಳನ್ನೂ ನೀರಿನಲ್ಲಿ ತೊಳೆದನಂತರ ಯಾಜಕನು ಅದನ್ನು ಪೂರ್ತಿಯಾಗಿ ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಹೋಮಮಾಡಬೇಕು. ಅದು ಅಗ್ನಿಯ ಮೂಲಕ ಯೆಹೋವನಿಗೆ ಸುವಾಸನೆಯನ್ನುಂಟುಮಾಡುವ ಸರ್ವಾಂಗಹೋಮವಾಗಿದೆ.",
			"10": "<sup>10</sup> ಒಬ್ಬನು ಆಡನ್ನಾಗಲಿ ಕುರಿಯನ್ನಾಗಲಿ ಸರ್ವಾಂಗಹೋಮ ಮಾಡಬೇಕೆಂದಿದ್ದರೆ ಅವನು ಪೂರ್ಣಾಂಗವಾದ ಗಂಡನ್ನು ತರಬೇಕು.",
			"11": "<sup>11</sup> ಅದನ್ನು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಯಜ್ಞವೇದಿಯ ಉತ್ತರಕಡೆ ವಧಿಸಬೇಕು. ತರುವಾಯ ಆರೋನನ ವಂಶದವರಾದ ಯಾಜಕರು ಅದರ ರಕ್ತವನ್ನು ಯಜ್ಞವೇದಿಗೆ ಸುತ್ತಲೂ ಎರಚಬೇಕು.",
			"12": "<sup>12</sup> ಅವನು ಆ ಪಶುವಿನ ದೇಹವನ್ನು ತುಂಡುತುಂಡಾಗಿ ಕಡಿದ ಮೇಲೆ ಯಾಜಕನು ಆ ತುಂಡುಗಳನ್ನೂ ತಲೆಯನ್ನೂ ಕೊಬ್ಬನ್ನೂ ಯಜ್ಞವೇದಿಯ ಮೇಲಣ ಬೆಂಕಿಯಲ್ಲಿ ಕಟ್ಟಿಗೆಯ ಮೇಲೆ ಕ್ರಮವಾಗಿ ಇಡಬೇಕು.",
			"13": "<sup>13</sup> ಅದರ ಕರುಳುಗಳನ್ನೂ ಕಾಲುಗಳನ್ನೂ ನೀರಿನಲ್ಲಿ ತೊಳೆಸಿದನಂತರ ಯಾಜಕನು ಅದನ್ನು ಪೂರ್ತಿಯಾಗಿ ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಹೋಮಮಾಡಬೇಕು. ಅದು ಅಗ್ನಿಯ ಮೂಲಕ ಯೆಹೋವನಿಗೆ ಸುವಾಸನೆಯನ್ನುಂಟು ಮಾಡುವ ಸರ್ವಾಂಗಹೋಮವಾಗಿದೆ.",
			"14": "<sup>14</sup> ಯೆಹೋವನಿಗೆ ಸರ್ವಾಂಗಹೋಮವಾಗಿ ಅರ್ಪಿಸುವಂಥದು ಪಕ್ಷಿಜಾತಿಯಾಗಿದ್ದರೆ ಅದು ಬೆಳವಕ್ಕಿಯಾಗಲಿ ಪಾರಿವಾಳದ ಮರಿಯಾಗಲಿ ಇರಬೇಕು.",
			"15": "<sup>15</sup> ಯಾಜಕನು ಅದನ್ನು ಯಜ್ಞವೇದಿಯ ಬಳಿಗೆ ತಂದು ಕುತ್ತಿಗೆ ಮುರಿದು ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಹೋಮಮಾಡಬೇಕು. ಅವನು ಅದರ ರಕ್ತವನ್ನು ಯಜ್ಞವೇದಿಯ ಪಕ್ಕದ ಮೇಲೆ ಹಿಂಡಬೇಕು;",
			"16": "<sup>16</sup> ಅದರ ಕರುಳುಗಳನ್ನೂ ಗರಿಗಳನ್ನೂ ತೆಗೆದುಬಿಟ್ಟು ಯಜ್ಞವೇದಿಯ ಪೂರ್ವದಿಕ್ಕಿನಲ್ಲಿರುವ ಬೂದಿಯ ಸ್ಥಳದಲ್ಲಿ ಬೀಸಾಡಬೇಕು.",
			"17": "<sup>17</sup> ಅವನು ಆ ಪಕ್ಷಿಯನ್ನು ರೆಕ್ಕೆಗಳ ಸಂದುಗಳಲ್ಲಿ ಹರಿಯಬೇಕು, ಆದರೆ ರೆಕ್ಕೆಗಳನ್ನು ಪೂರಾ ಕಿತ್ತು ಹಾಕಕೂಡದು. ಅನಂತರ ಅವನು ಅದನ್ನು ಯಜ್ಞವೇದಿಯ ಮೇಲಣ ಬೆಂಕಿಯಲ್ಲಿರುವ ಕಟ್ಟಿಗೆಯ ಮೇಲೆ ಹೋಮಮಾಡಬೇಕು. ಅದು ಅಗ್ನಿಯ ಮೂಲಕ ಯೆಹೋವನಿಗೆ ಸುವಾಸನೆಯನ್ನುಂಟುಮಾಡುವ ಸರ್ವಾಂಗಹೋಮವಾಗಿದೆ."
		},
		"2": {
			"1": "<sup>1</sup> ಯಾವನಾದರೂ ಯೆಹೋವನಿಗೆ ಧಾನ್ಯ ನೈವೇದ್ಯ ಮಾಡಬೇಕೆಂದಿದ್ದರೆ ಅದು ಗೋದಿಹಿಟ್ಟಾಗಿರಬೇಕು. ಅವನು ಅದರ ಮೇಲೆ ಎಣ್ಣೆಯನ್ನು ಹೊಯಿದು ಧೂಪವನ್ನಿಟ್ಟು",
			"2": "<sup>2</sup> ಅದನ್ನು ಆರೋನನ ವಂಶದವರಾದ ಯಾಜಕರ ಬಳಿಗೆ ತರಬೇಕು. ಯಾಜಕನು ದೇವರಿಗೆ ನೈವೇದ್ಯವಾದದ್ದನ್ನು ಸೂಚಿಸುವದಕ್ಕಾಗಿ ಆ ಎಣ್ಣೆ ಬೆರಸಿದ ಹಿಟ್ಟಿನಲ್ಲಿ ಒಂದು ಹಿಡಿಯನ್ನೂ ಧೂಪವೆಲ್ಲವನ್ನೂ ತೆಗೆದುಕೊಂಡು ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಹೋಮ ಮಾಡಬೇಕು. ಅದು ಅಗ್ನಿಯ ಮೂಲಕ ಯೆಹೋವನಿಗೆ ಸುವಾಸನೆಯನ್ನುಂಟುಮಾಡುವದು.",
			"3": "<sup>3</sup> ಆ ನೈವೇದ್ಯದಲ್ಲಿ ಮಿಕ್ಕದ್ದು ಆರೋನನಿಗೂ ಅವನ ವಂಶದವರಿಗೂ ಆಗಬೇಕು; ಅದು ಯೆಹೋವನಿಗೆ ಅರ್ಪಿತವಾದ ಹೋಮಶೇಷವಾದದರಿಂದ ಮಹಾಪರಿಶುದ್ಧವಾಗಿದೆ.",
			"4": "<sup>4</sup> ನೀವು ಒಲೆಯಲ್ಲಿ ಅಡಿಗೆಮಾಡಿದ್ದನ್ನು ನೈವೇದ್ಯವಾಗಿ ಸಮರ್ಪಿಸಬೇಕಾದರೆ ಅದು ಎಣ್ಣೆ ಬೆರಸಿದ ಹುಳಿಯಿಲ್ಲದ ಗೋದಿಹಿಟ್ಟಿನ ಹೋಳಿಗೆಗಳಾದರೂ ಎಣ್ಣೆ ಹಾಕಿದ ಹುಳಿಯಿಲ್ಲದ ಕಡುಬುಗಳಾದರೂ ಆಗಿರಬೇಕು.",
			"5": "<sup>5</sup> ನೀವು ಅರ್ಪಿಸುವದು ಕಬ್ಬಿಣದ ಹಂಚಿನ ಮೇಲೆ ಸುಟ್ಟದ್ದಾದರೆ ಅದು ಎಣ್ಣೆ ಬೆರಸಿದ ಹುಳಿಯಿಲ್ಲದ ಗೋದಿಹಿಟ್ಟಿನದಾಗಿರಬೇಕು.",
			"6": "<sup>6</sup> ಅದನ್ನು ಚೂರುಚೂರಾಗಿ ಮುರಿದು ಅದರ ಮೇಲೆ ಎಣ್ಣೆ ಹೊಯಿದು ನೈವೇದ್ಯ ಮಾಡಬೇಕು.",
			"7": "<sup>7</sup> ನೀವು ಅರ್ಪಿಸುವದು ಬಾಂಡ್ಲೆಯಲ್ಲಿ ಪಕ್ವಮಾಡಿದ್ದಾದರೆ ಅದು ಎಣ್ಣೆ ಹೊಯಿದ ಗೋದಿಹಿಟ್ಟಿನದಾಗಿರಬೇಕು.",
			"8": "<sup>8</sup> ನೀವು ಈ ಮೂರು ವಿಧವಾದ ನೈವೇದ್ಯವನ್ನು ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಿಸುವದಾದರೆ ಯಾಜಕನಿಗೆ ಒಪ್ಪಿಸಬೇಕು; ಅವನೇ ಅದನ್ನು ಯಜ್ಞವೇದಿಯ ಬಳಿಗೆ ತರಬೇಕು.",
			"9": "<sup>9</sup> ಯಾಜಕನು ನೈವೇದ್ಯವನ್ನು ಸೂಚಿಸುವದಕ್ಕಾಗಿ ಅದರಲ್ಲಿ ಸ್ವಲ್ಪಭಾಗವನ್ನು ಪ್ರತ್ಯೇಕಿಸಿ ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಹೋಮಮಾಡಬೇಕು. ಅದು ಅಗ್ನಿಯ ಮೂಲಕ ಯೆಹೋವನಿಗೆ ಸುವಾಸನೆಯನ್ನುಂಟುಮಾಡುವದು.",
			"10": "<sup>10</sup> ನೈವೇದ್ಯದಲ್ಲಿ ಮಿಕ್ಕದ್ದು ಆರೋನನಿಗೂ ಅವನ ವಂಶದವರಿಗೂ ಆಗಬೇಕು; ಅದು ಯೆಹೋವನಿಗೆ ಅರ್ಪಿತವಾದ ಹೋಮಶೇಷವಾದದರಿಂದ ಮಹಾಪರಿಶುದ್ಧವಾಗಿದೆ.",
			"11": "<sup>11</sup> ನೀವು ಯೆಹೋವನಿಗೆ ನೈವೇದ್ಯವಾಗಿ ಸಮರ್ಪಿಸುವ ಯಾವ ಪದಾರ್ಥವನ್ನಾಗಲಿ ಹುಳಿಹಿಟ್ಟಿನಿಂದ ಮಾಡಬಾರದು. ಯಾವ ಹುಳಿಪದಾರ್ಥವನ್ನಾಗಲಿ ಸಿಹಿಪದಾರ್ಥವನ್ನಾಗಲಿ ಯೆಹೋವನಿಗೆ ಹೋಮ ಮಾಡಕೂಡದು.",
			"12": "<sup>12</sup> ಅವುಗಳನ್ನು ಪ್ರಥಮಫಲವಾಗಿ ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಿಸಬಹುದೇ ಹೊರತು ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಸುವಾಸನೆಯನ್ನುಂಟುಮಾಡುವದಕ್ಕಾಗಿ ಹೋಮಮಾಡಕೂಡದು.",
			"13": "<sup>13</sup> ಎಲ್ಲಾ ನೈವೇದ್ಯ ಪದಾರ್ಥಗಳಿಗೂ ಉಪ್ಪುಹಾಕಿ ಸಮರ್ಪಿಸಬೇಕು. ಉಪ್ಪು ಯೆಹೋವನ ಸಂಗಡ ನಿಮಗಿರುವ ಒಡಂಬಡಿಕೆಯನ್ನು ಸೂಚಿಸುವದರಿಂದ ಅದು ಯಾವ ನೈವೇದ್ಯದ್ರವ್ಯವಾದರೂ ಉಪ್ಪಿಲ್ಲದೆ ಇರಬಾರದು. ನೀವು ಅರ್ಪಿಸುವ ಎಲ್ಲಾ ಪದಾರ್ಥಗಳಲ್ಲಿಯೂ ಉಪ್ಪು ಸೇರೇ ಇರಬೇಕು.",
			"14": "<sup>14</sup> ನೀವು ಯೆಹೋವನಿಗೆ ಪ್ರಥಮಫಲ ನೈವೇದ್ಯವನ್ನು ಮಾಡಬೇಕಾದರೆ ಗೋದಿಯ ಹಸೀ ತೆನೆಗಳನ್ನು ಬೆಂಕಿಯಲ್ಲಿ ಸುಟ್ಟು ಉಮ್ಮಿಗೆ ಸಮರ್ಪಿಸಬೇಕು.",
			"15": "<sup>15</sup> ಅದು ನೈವೇದ್ಯ ವಸ್ತುವಾದದರಿಂದ ಅದರ ಮೇಲೆ ಎಣ್ಣೆ ಹೊಯಿದು ಧೂಪವಿಡಬೇಕು.",
			"16": "<sup>16</sup> ಯಾಜಕನು ನೈವೇದ್ಯವನ್ನು ಸೂಚಿಸುವದಕ್ಕಾಗಿ ಆ ಉಮ್ಮಿಗೆಯಲ್ಲಿಯೂ ಎಣ್ಣೆಯಲ್ಲಿಯೂ ಸ್ವಲ್ಪಭಾಗವನ್ನು ತೆಗೆದುಕೊಂಡು ಧೂಪವೆಲ್ಲವನ್ನು ಅದಕ್ಕೆ ಸೇರಿಸಿ ಅಗ್ನಿಯ ಮೂಲಕ ಯೆಹೋವನಿಗೆ ಹೋಮಮಾಡಬೇಕು."
		},
		"3": {
			"1": "<sup>1</sup> ಯಾವನಾದರೂ ಸಮಾಧಾನಯಜ್ಞ ಮಾಡಬೇಕಾದರೆ ಅವನು ಸಮರ್ಪಿಸುವ ಪಶುವು ದನವಾಗಿದ್ದ ಪಕ್ಷಕ್ಕೆ ಅದು ಗಂಡಾಗಲಿ ಹೆಣ್ಣಾಗಲಿ ಪೂರ್ಣಾಂಗವಾಗಿಯೇ ಇರಬೇಕು.",
			"2": "<sup>2</sup> ಅವನು ಅದನ್ನು ಯೆಹೋವನ ಸನ್ನಿಧಿಗೆ ತಂದು ಅದರ ತಲೆಯ ಮೇಲೆ ಕೈಯಿಟ್ಟು ದೇವದರ್ಶನದ ಗುಡಾರದ ಬಾಗಲಲ್ಲಿ ವಧಿಸಬೇಕು. ಆರೋನನ ವಂಶದವರಾದ ಯಾಜಕರು ಅದರ ರಕ್ತವನ್ನು ಯಜ್ಞವೇದಿಗೆ ಸುತ್ತಲೂ ಎರಚಬೇಕು;",
			"3": "<sup>3</sup> ಆ ಯಜ್ಞಪಶುವಿನ ವಪೆಯನ್ನೂ ಕರುಳುಗಳ ಮೇಲಣ ಎಲ್ಲಾ ಕೊಬ್ಬನ್ನೂ",
			"4": "<sup>4</sup> ಎರಡು ಹುರುಳಿಕಾಯಿಗಳನ್ನೂ ಇವುಗಳ ಮೇಲೆ ಸೊಂಟದಲ್ಲಿರುವ ಕೊಬ್ಬನ್ನೂ ಕಾಳಿಜದ ಹತ್ತಿರ ಹುರುಳಿಕಾಯಿಗಳ ತನಕ ಇರುವ ಕೊಬ್ಬನ್ನೂ ತೆಗೆದು ಯೆಹೋವನಿಗೆ ಹೋಮಮಾಡಬೇಕು.",
			"5": "<sup>5</sup> ಆರೋನನ ವಂಶದವರಾದ ಯಾಜಕರು ಅದನ್ನು ಯಜ್ಞವೇದಿಯಲ್ಲಿ ಉರಿಯುವ ಕಟ್ಟಿಗೆಯ ಮೇಲಿರುವ ಸರ್ವಾಂಗಹೋಮದ್ರವ್ಯದ ಮೇಲೆಯೇ ಹೋಮಮಾಡಬೇಕು. ಅದು ಯೆಹೋವನಿಗೆ ಸುಗಂಧಹೋಮವಾಗಿರುವದು.",
			"6": "<sup>6</sup> ಸಮಾಧಾನಯಜ್ಞಕ್ಕಾಗಿ ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಿಸುವಂಥದು ಕುರಿ ಅಥವಾ ಆಡು ಆಗಿರುವ ಪಕ್ಷದಲ್ಲಿ ಅದು ಗಂಡಾಗಲಿ ಹೆಣ್ಣಾಗಲಿ ಪೂರ್ಣಾಂಗವಾಗಿರಬೇಕು.",
			"7": "<sup>7</sup> ಅದು ಕುರಿಯಾಗಿದ್ದರೆ ತಂದವನು ಅದನ್ನು ಯೆಹೋವನ ಸನ್ನಿಧಿಗೆ ತೆಗೆದುಕೊಂಡು ಬಂದು",
			"8": "<sup>8</sup> ಅದರ ತಲೆಯ ಮೇಲೆ ಕೈಯಿಟ್ಟು ದೇವದರ್ಶನದ ಗುಡಾರದ ಎದುರಾಗಿ ಅದನ್ನು ವಧಿಸಬೇಕು. ಆರೋನನ ವಂಶದವರಾದ ಯಾಜಕರು ಅದರ ರಕ್ತವನ್ನು ಯಜ್ಞವೇದಿಗೆ ಸುತ್ತಲೂ ಎರಚಬೇಕು.",
			"9": "<sup>9</sup> ಆ ಸಮಾಧಾನಯಜ್ಞಪಶುವಿನ ಕೊಬ್ಬನ್ನು ಯೆಹೋವನಿಗೋಸ್ಕರ ಹೋಮವಾಗಿ ಸಮರ್ಪಿಸಬೇಕು. ಹೇಗಂದರೆ, ಬಾಲದ ಕೊಬ್ಬನ್ನೆಲ್ಲಾ ಬೆನ್ನೆಲುಬಿನ ಬಳಿಯಿಂದ ತೆಗೆದುಬಿಟ್ಟು ಅದನ್ನೂ ವಪೆಯನ್ನೂ",
			"10": "<sup>10</sup> ಕರುಳುಗಳ ಮೇಲಣ ಎಲ್ಲಾ ಕೊಬ್ಬನ್ನೂ ಎರಡು ಹುರುಳಿಕಾಯಿಗಳನ್ನೂ ಇವುಗಳ ಮೇಲೆ ಸೊಂಟದಲ್ಲಿರುವ ಕೊಬ್ಬನ್ನೂ ಕಾಳಿಜದ ಮೇಲೆ ಹುರುಳಿಕಾಯಿಗಳ ತನಕ ಇರುವ ಕೊಬ್ಬನ್ನೂ",
			"11": "<sup>11</sup> ಯಾಜಕನು ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಹೋಮಮಾಡಬೇಕು. ಅದು ಅಗ್ನಿಮೂಲಕವಾಗಿ ಯೆಹೋವನಿಗೆ ಆಹಾರವಾಗುವದು.",
			"12": "<sup>12</sup> ಸಮರ್ಪಿಸುವಂಥದು ಆಡಾಗಿದ್ದರೆ ಅರ್ಪಿಸುವವನು ಅದನ್ನು ಯೆಹೋವನ ಸನ್ನಿಧಿಗೆ ತೆಗೆದುಕೊಂಡು ಬಂದು",
			"13": "<sup>13</sup> ಅದರ ತಲೆಯ ಮೇಲೆ ಕೈಯಿಟ್ಟು ದೇವದರ್ಶನದ ಗುಡಾರದ ಎದುರಾಗಿ ವಧಿಸಬೇಕು. ಆರೋನನ ವಂಶದವರಾದ ಯಾಜಕರು ಅದರ ರಕ್ತವನ್ನು ಯಜ್ಞವೇದಿಗೆ ಸುತ್ತಲೂ ಎರಚಬೇಕು.",
			"14": "<sup>14</sup> ಅದರ ವಪೆಯನ್ನೂ ಕರುಳುಗಳ ಮೇಲಣ ಎಲ್ಲಾ ಕೊಬ್ಬನ್ನೂ ಎರಡು ಹುರುಳಿಕಾಯಿಗಳನ್ನೂ",
			"15": "<sup>15</sup> ಇವುಗಳ ಮೇಲೆ ಸೊಂಟದಲ್ಲಿರುವ ಕೊಬ್ಬನ್ನೂ ಕಾಳಿಜದ ಹತ್ತಿರ ಹುರುಳಿಕಾಯಿಗಳ ತನಕ ಇರುವ ಕೊಬ್ಬನ್ನೂ ಯೆಹೋವನಿಗೋಸ್ಕರ ಅಗ್ನಿಯ ಮೂಲಕ ಸಮರ್ಪಿಸಬೇಕು.",
			"16": "<sup>16</sup> ಯಾಜಕನು ಅದನ್ನು ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಹೋಮ ಮಾಡಬೇಕು. ಅದು ಸುಗಂಧ ಹೋಮರೂಪವಾಗಿ ಯೆಹೋವನಿಗೆ ಆಹಾರವಾಗಿರುವದು. ಯಜ್ಞಪಶುಗಳ ಕೊಬ್ಬೆಲ್ಲವೂ ಯೆಹೋವನದು.",
			"17": "<sup>17</sup> ಕೊಬ್ಬನ್ನಾಗಲಿ ರಕ್ತವನ್ನಾಗಲಿ ತಿನ್ನಕೂಡದೆಂಬದು ನಿಮಗೂ ನಿಮ್ಮ ಸಂತತಿಯವರಿಗೂ ನೀವು ವಾಸಿಸುವ ಎಲ್ಲಾ ಸ್ಥಳಗಳಲ್ಲಿಯೂ ಶಾಶ್ವತ ನಿಯಮ."
		},
		"4": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಮೋಶೆಗೆ ಇಂತೆಂದನು -",
			"2": "<sup>2</sup> ನೀನು ಇಸ್ರಾಯೇಲ್ಯರ ಸಂಗಡ ಮಾತಾಡಿ ಹೇಳಬೇಕಾದದ್ದೇನಂದರೆ - ಯಾವನೇ ಆಗಲಿ ತಿಳಿಯದೆ ಯೆಹೋವನು ನಿಷೇಧಿಸಿದ ಕಾರ್ಯಗಳಲ್ಲಿ ಯಾವದನ್ನಾದರೂ ಮಾಡಿ ದೋಷಿಯಾದರೆ [ಅವನಿಗೆ ದೋಷಪರಿಹಾರ ಮಾಡುವ ಕ್ರಮ ಹೇಗಂದರೆ - ]",
			"3": "<sup>3</sup> ಅಭಿಷಿಕ್ತನಾದ ಯಾಜಕನೇ ದೋಷಿಯಾಗಿ ಜನರೆಲ್ಲರನ್ನೂ ಅಪರಾಧಕ್ಕೆ ಒಳಪಡಿಸಿದರೆ ತನ್ನ ದೋಷಪರಿಹಾರಕ್ಕಾಗಿ ಅವನು ಪೂರ್ಣಾಂಗವಾದ ಹೋರಿಯನ್ನು ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಿಸಬೇಕು.",
			"4": "<sup>4</sup> ಅವನು ಆ ಹೋರಿಯನ್ನು ಯೆಹೋವನ ಸನ್ನಿಧಿಗೆ ದೇವದರ್ಶನದ ಗುಡಾರದ ಬಾಗಲಿಗೆ ತಂದು ಅದರ ತಲೆಯ ಮೇಲೆ ಕೈಯಿಟ್ಟು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಅದನ್ನು ವಧಿಸಬೇಕು.",
			"5": "<sup>5</sup> ಅಭಿಷಿಕ್ತ ಯಾಜಕನು ಆ ಹೋರಿಯ ರಕ್ತದಲ್ಲಿ ಸ್ವಲ್ಪವನ್ನು ತೆಗೆದುಕೊಂಡು ದೇವದರ್ಶನದ ಗುಡಾರದೊಳಗೆ ಬಂದು",
			"6": "<sup>6</sup> ಅದರಲ್ಲಿ ಬೆರಳನ್ನು ಅದ್ದಿ ಮಹಾಪವಿತ್ರಸ್ಥಾನದ ಮುಂದಣ ತೆರೆಯ ಎದುರಾಗಿ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಏಳು ಸಾರಿ ಚಿಮಿಕಿಸಬೇಕು.",
			"7": "<sup>7</sup> ಆಗ ಯಾಜಕನು ದೇವದರ್ಶನದ ಗುಡಾರದೊಳಗೆ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿರುವ ಪರಿಮಳಧೂಪವೇದಿಯ ಕೊಂಬುಗಳಿಗೆ ಆ ರಕ್ತವನ್ನು ಹಚ್ಚಿ ಹೋರಿಯ ಮಿಕ್ಕ ರಕ್ತವನ್ನೆಲ್ಲಾ ದೇವದರ್ಶನದ ಗುಡಾರದ ಬಾಗಲಿಗೆ ಎದುರಾಗಿ ಸರ್ವಾಂಗಹೋಮಮಾಡುವ ಯಜ್ಞವೇದಿಯ ಬುಡದಲ್ಲಿ ಸುರಿದುಬಿಡಬೇಕು.",
			"8": "<sup>8</sup> ಆ ದೋಷ ಪರಿಹಾರಕಯಜ್ಞದ ಹೋರಿಯ ಕೊಬ್ಬನ್ನೆಲ್ಲಾ ಅಂದರೆ ವಪೆಯನ್ನೂ ಕರುಳುಗಳ ಮೇಲಣ ಕೊಬ್ಬನ್ನೂ",
			"9": "<sup>9</sup> ಎರಡು ಹುರುಳಿಕಾಯಿಗಳನ್ನೂ ಇವುಗಳ ಮೇಲೆ ಸೊಂಟದ ಹತ್ತಿರ ಇರುವ ಕೊಬ್ಬನ್ನೂ ಕಾಳಿಜದ ಹತ್ತಿರ ಹುರುಳಿಕಾಯಿಗಳ ತನಕ ಇರುವ ಕೊಬ್ಬನ್ನೂ",
			"10": "<sup>10</sup> ಅದನ್ನೆಲ್ಲಾ ಸಮಾಧಾನಯಜ್ಞದ ಹೋರಿಯಲ್ಲಿ ಪ್ರತ್ಯೇಕಿಸಿದಂತೆಯೇ ಇದರಲ್ಲಿಯೂ ಪ್ರತ್ಯೇಕಿಸಿ ಸರ್ವಾಂಗಹೋಮವೇದಿಯ ಮೇಲೆ ಹೋಮಮಾಡಬೇಕು.",
			"11": "<sup>11</sup> ಆ ಹೋರಿಯಲ್ಲಿ ಮಿಕ್ಕದ್ದನ್ನೆಲ್ಲಾ ಅಂದರೆ ಅದರ ಚರ್ಮ, ಮಾಂಸ, ತಲೆ, ಕಾಲುಗಳು, ಕರುಳುಗಳು,",
			"12": "<sup>12</sup> ಕರುಳುಗಳಲ್ಲಿರುವ ಕಲ್ಮಶ ಇವುಗಳನ್ನೆಲ್ಲಾ ಪಾಳೆಯದ ಹೊರಗೆ ವೇದಿಯ ಬೂದಿಯನ್ನು ಹಾಕುವ ಶುದ್ಧವಾದ ಸ್ಥಳಕ್ಕೆ ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ಕಟ್ಟಿಗೆಯ ಮೇಲಿರಿಸಿ ಬೆಂಕಿಯಲ್ಲಿ ಸುಡಿಸಿಬಿಡಬೇಕು. ಬೂದಿಯನ್ನು ಹಾಕುವ ಸ್ಥಳದಲ್ಲೇ ಅದನ್ನು ಸುಡಿಸಿಬಿಡಬೇಕು.",
			"13": "<sup>13</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಸಮೂಹವೆಲ್ಲಾ ತಿಳಿಯದೆ ದೋಷಿಗಳಾದರೆ ಅಂದರೆ ಯೆಹೋವನು ನಿಷೇಧಿಸಿದ ಕಾರ್ಯಗಳಲ್ಲಿ ಅವರು ಯಾವದನ್ನಾದರೂ ಮಾಡಿ ದೋಷಕ್ಕೆ ಗುರಿಯಾದರೆ",
			"14": "<sup>14</sup> ಮೊದಲು ತಮಗೂ ಮರೆಯಾಗಿದ್ದ ಆ ದೋಷವು ತಿಳಿದುಬಂದನಂತರ ಅದನ್ನು ಪರಿಹರಿಸಿಕೊಳ್ಳುವದಕ್ಕಾಗಿ [ಪೂರ್ಣಾಂಗವಾದ] ಹೋರಿಯನ್ನು ಸಮರ್ಪಿಸಬೇಕು. ಆ ಹೋರಿಯನ್ನು ದೇವದರ್ಶನದ ಗುಡಾರದ ಬಾಗಲಿಗೆ ತರಿಸಿದಾಗ",
			"15": "<sup>15</sup> ಸಮೂಹದ ಹಿರಿಯರು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಅದರ ತಲೆಯ ಮೇಲೆ ತಮ್ಮ ಕೈಗಳನ್ನಿಟ್ಟು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಅದನ್ನು ವಧಿಸಬೇಕು.",
			"16": "<sup>16</sup> ಅಭಿಷಿಕ್ತ ಯಾಜಕನು ಅದರ ರಕ್ತದಲ್ಲಿ ಸ್ವಲ್ಪವನ್ನು ದೇವದರ್ಶನದ ಗುಡಾರದೊಳಗೆ ತಂದು",
			"17": "<sup>17</sup> ಅದರಲ್ಲಿ ಬೆರಳನ್ನು ಅದ್ದಿ ತೆರೆಯ ಮುಂದೆ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಅದನ್ನು ಏಳು ಸಾರಿ ಚಿಮಿಕಿಸಬೇಕು.",
			"18": "<sup>18</sup> ಆ ರಕ್ತದಲ್ಲಿ ಸ್ವಲ್ಪವನ್ನು ದೇವದರ್ಶನದ ಗುಡಾರದೊಳಗೆ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿರುವ ಧೂಪವೇದಿಯ ಕೊಂಬುಗಳಿಗೆ ಹಚ್ಚಿ ಮಿಕ್ಕದ್ದನ್ನೆಲ್ಲಾ ದೇವದರ್ಶನದ ಗುಡಾರದ ಬಾಗಲಿಗೆದುರಾಗಿರುವ ಯಜ್ಞವೇದಿಯ ಬುಡದಲ್ಲಿ ಸುರಿದುಬಿಡಬೇಕು.",
			"19": "<sup>19</sup> ಅವನು ಹೋರಿಯ ಕೊಬ್ಬನ್ನೆಲ್ಲಾ ಪ್ರತ್ಯೇಕಿಸಿ ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಹೋಮಮಾಡಬೇಕು.",
			"20": "<sup>20</sup> ಅವನು [ಅಭಿಷಿಕ್ತ ಯಾಜಕನ] ದೋಷಪರಿಹಾರಕಯಜ್ಞದ ಹೋರಿಯ ವಿಷಯದಲ್ಲಿ ಹೇಗೆ ಮಾಡಬೇಕೆಂದು ವಿಧಿಸಲ್ಪಟ್ಟಿತೋ ಇದರ ವಿಷಯದಲ್ಲಿಯೂ ಹಾಗೆಯೇ ಮಾಡಬೇಕು. ಯಾಜಕನು ಅವರಿಗೋಸ್ಕರ ದೋಷಪರಿಹಾರ ಮಾಡಿದನಂತರ ಅವರಿಗೆ ಕ್ಷಮಾಪಣೆಯಾಗುವದು.",
			"21": "<sup>21</sup> ಆ ಮೊದಲನೆಯ ಹೋರಿಯನ್ನು ಪಾಳೆಯದ ಹೊರಗೆ ತೆಗೆದುಕೊಂಡುಹೋಗಿ ಸುಡಿಸಿಬಿಟ್ಟ ಪ್ರಕಾರ ಈ ಹೋರಿಯನ್ನೂ ಸುಡಿಸಿಬಿಡಬೇಕು. ಇದು ಜನಸಮೂಹದ ದೋಷಪರಿಹಾರಕಯಜ್ಞ.",
			"22": "<sup>22</sup> ಮುಖ್ಯಾಧಿಕಾರಿ ತನ್ನ ದೇವರಾಗಿರುವ ಯೆಹೋವನು ನಿಷೇಧಿಸಿದ ಕಾರ್ಯಗಳಲ್ಲಿ ಯಾವದನ್ನಾದರೂ ತಿಳಿಯದೆ ಮಾಡಿ ದೋಷಿಯಾದರೆ",
			"23": "<sup>23</sup> ತನ್ನ ತಪ್ಪು ತನಗೆ ತಿಳಿದು ಬಂದಾಗ ಅವನು ಪೂರ್ಣಾಂಗವಾದ ಹೋತವನ್ನು ತಂದು ಸಮರ್ಪಿಸಬೇಕು;",
			"24": "<sup>24</sup> ಅವನು ಆ ಹೋತದ ತಲೆಯ ಮೇಲೆ ಕೈಯಿಟ್ಟು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸರ್ವಾಂಗಹೋಮದ ಪಶುಗಳನ್ನು ವಧಿಸುವ ಸ್ಥಳದಲ್ಲೇ ಅದನ್ನು ವಧಿಸಬೇಕು. ಅದು ದೋಷಪರಿಹಾರಕಯಜ್ಞ.",
			"25": "<sup>25</sup> ಯಾಜಕನು ಆ ಪಶುರಕ್ತದಲ್ಲಿ ಸ್ವಲ್ಪವನ್ನು ಬೆರಳಿನಿಂದ ತೆಗೆದುಕೊಂಡು ಯಜ್ಞವೇದಿಯ ಕೊಂಬುಗಳಿಗೆ ಹಚ್ಚಿ ಮಿಕ್ಕ ರಕ್ತವನ್ನು ಯಜ್ಞವೇದಿಯ ಬುಡದಲ್ಲಿ ಸುರಿದುಬಿಡಬೇಕು.",
			"26": "<sup>26</sup> ಸಮಾಧಾನಯಜ್ಞದ ಪಶುವಿನ ಕೊಬ್ಬನ್ನು ಅವನು ಹೋಮಮಾಡುವ ಪ್ರಕಾರವೇ ಇದರ ಎಲ್ಲಾ ಕೊಬ್ಬನ್ನೂ ಹೋಮಮಾಡಬೇಕು. ಹೀಗೆ ಯಾಜಕನು ಅವನಿಗೋಸ್ಕರ ದೋಷಪರಿಹಾರವನ್ನು ಮಾಡುವದರಿಂದ ಅವನಿಗೆ ಕ್ಷಮಾಪಣೆಯಾಗುವದು.",
			"27": "<sup>27</sup> ನಿಮ್ಮ ದೇಶಸ್ಥರಲ್ಲಿ ಬೇರೆ ಯಾವನಾದರೂ ಯೆಹೋವನು ನಿಷೇಧಿಸಿದ ಕಾರ್ಯಗಳಲ್ಲಿ ಯಾವದನ್ನಾದರೂ ತಿಳಿಯದೆ ಮಾಡಿ ದೋಷಿಯಾದರೆ",
			"28": "<sup>28</sup> ತನ್ನ ತಪ್ಪು ತನಗೆ ತಿಳಿದುಬಂದಾಗ ಆ ದೋಷಪರಿಹಾರಕ್ಕಾಗಿ ಪೂರ್ಣಾಂಗವಾದ ಹೆಣ್ಣು ಮೇಕೆಯನ್ನು ತಂದು ಸಮರ್ಪಿಸಬೇಕು.",
			"29": "<sup>29</sup> ಅವನು ಅದರ ತಲೆಯ ಮೇಲೆ ಕೈಯಿಟ್ಟನಂತರ ಸರ್ವಾಂಗಹೋಮದ ಪಶುಗಳನ್ನು ವಧಿಸುವ ಸ್ಥಳದಲ್ಲೇ ಅದನ್ನು ವಧಿಸಬೇಕು.",
			"30": "<sup>30</sup> ಯಾಜಕನು ಅದರ ರಕ್ತದಲ್ಲಿ ಸ್ವಲ್ಪವನ್ನು ಬೆರಳಿನಿಂದ ತೆಗೆದುಕೊಂಡು ಯಜ್ಞವೇದಿಯ ಕೊಂಬುಗಳಿಗೆ ಹಚ್ಚಿ ಮಿಕ್ಕ ರಕ್ತವನ್ನೆಲ್ಲಾ ಯಜ್ಞವೇದಿಯ ಬುಡದಲ್ಲಿ ಸುರಿದುಬಿಡಬೇಕು.",
			"31": "<sup>31</sup> ಸಮಾಧಾನಯಜ್ಞಪಶುಗಳ ಕೊಬ್ಬನ್ನು ಪ್ರತ್ಯೇಕಿಸುವ ಪ್ರಕಾರ ಯಾಜಕನು ಇದರ ಎಲ್ಲಾ ಕೊಬ್ಬನ್ನೂ ಪ್ರತ್ಯೇಕಿಸಿ ಯೆಹೋವನಿಗೆ ಸುವಾಸನೆಯನ್ನುಂಟುಮಾಡುವದಕ್ಕೆ ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಹೋಮಮಾಡಬೇಕು. ಹೀಗೆ ಯಾಜಕನು ಅವನಿಗೋಸ್ಕರ ದೋಷಪರಿಹಾರವನ್ನು ಮಾಡುವದರಿಂದ ಅವನಿಗೆ ಕ್ಷಮಾಪಣೆಯಾಗುವದು.",
			"32": "<sup>32</sup> ಅವನು ತನ್ನ ದೋಷಪರಿಹಾರಾರ್ಥವಾಗಿ ತರುವದು ಕುರಿಯಾದರೆ ಅದು ಪೂರ್ಣಾಂಗವಾದ ಹೆಣ್ಣಾಗಿರಬೇಕು.",
			"33": "<sup>33</sup> ಅವನು ಅದರ ತಲೆಯ ಮೇಲೆ ಕೈಯಿಟ್ಟನಂತರ ಸರ್ವಾಂಗಹೋಮದ ಪಶುಗಳನ್ನು ವಧಿಸುವ ಸ್ಧಳದಲ್ಲೇ ಅದನ್ನು ದೋಷಪರಿಹಾರಾರ್ಥವಾಗಿ ವಧಿಸಬೇಕು.",
			"34": "<sup>34</sup> ಯಾಜಕನು ಅದರ ರಕ್ತದಲ್ಲಿ ಸ್ವಲ್ಪವನ್ನು ಬೆರಳಿನಿಂದ ತೆಗೆದುಕೊಂಡು ಯಜ್ಞವೇದಿಯ ಕೊಂಬುಗಳಿಗೆ ಹಚ್ಚಿ ಮಿಕ್ಕ ರಕ್ತವನ್ನೆಲ್ಲಾ ಯಜ್ಞವೇದಿಯ ಬುಡದಲ್ಲಿ ಸುರಿದುಬಿಡಬೇಕು.",
			"35": "<sup>35</sup> ಸಮಾಧಾನ ಯಜ್ಞಪಶುಗಳ ಕೊಬ್ಬನ್ನು ಪ್ರತ್ಯೇಕಿಸುವ ಪ್ರಕಾರ ಯಾಜಕನು ಇದರ ಎಲ್ಲಾ ಕೊಬ್ಬನ್ನೂ ಪ್ರತ್ಯೇಕಿಸಿ ಯಜ್ಞವೇದಿಯಲ್ಲಿ ಉರಿಯುವ ಹೋಮದ್ರವ್ಯಗಳ ಮೇಲೆ ಯೆಹೋವನಿಗೆ ಹೋಮಮಾಡಬೇಕು. ಅವನ ತಪ್ಪಿನ ನಿಮಿತ್ತ ಯಾಜಕನು ಹೀಗೆ ಅವನಿಗೋಸ್ಕರ ದೋಷಪರಿಹಾರವನ್ನು ಮಾಡುವದರಿಂದ ಅವನಿಗೆ ಕ್ಷಮಾಪಣೆಯಾಗುವದು."
		},
		"5": {
			"1": "<sup>1</sup> ನ್ಯಾಯಾಧಿಕಾರಿಯು ತಿಳಿಸಬೇಕೆಂದು ಆಣೆ ಇಟ್ಟರೂ ಯಾವನಾದರೂ ತಾನು ಕಂಡು ಕೇಳಿದ್ದನ್ನು ತಿಳಿಸದೆಹೋದರೆ ಅವನು ಪಾಪಕ್ಕೆ ಗುರಿಯಾಗುವನು.",
			"2": "<sup>2</sup> ಯಾವನಿಗಾದರೂ ಅಶುದ್ಧವಾದ ಕಾಡುಮೃಗ, ಪಶು, ಜಂತು ಇವುಗಳ ಹೆಣವಾಗಲಿ ಬೇರೆ ಯಾವ ಅಶುದ್ಧವಸ್ತುವಾಗಲಿ ತಗಲಿದರೆ ಅವನಿಗೆ ತಿಳಿಯದೆಹೋದರೂ ಅವನು ಅಶುದ್ಧನೂ ದೋಷಿಯೂ ಆಗಿರುವನು.",
			"3": "<sup>3</sup> ಮನುಷ್ಯದೇಹದಿಂದುಂಟಾದ ಯಾವದಾದರೂ ಒಂದು ಅಶುದ್ಧವಸ್ತು ತಗಲಿದ್ದು ಅವನಿಗೆ ತಿಳಿಯದೆಹೋದರೂ ತಿಳಿದುಬಂದಾಗ ಅವನು ದೋಷಿಯಾಗುವನು.",
			"4": "<sup>4</sup> ಯಾವನಾದರೂ ಆಲೋಚಿಸದೆ ಮೇಲಿಗಾಗಲಿ ಕೇಡಿಗಾಗಲಿ ಯಾವದಾದರೂ ಆಣೆಯಿಟ್ಟುಕೊಂಡರೆ ಅದು ಅವನಿಗೆ ತಿಳಿಯದೆ ಹೋದರೂ ತಿಳಿದುಬಂದಾಗ ಅವನು ಇದರಿಂದಲೂ ದೋಷಿಯಾಗುವನು.",
			"5": "<sup>5</sup> ಅವನು ಈ ವಿಷಯಗಳಲ್ಲಿ ಯಾವದಾದರೂ ಒಂದರಲ್ಲಿ ದೋಷಿಯಾದಾಗ ತಾನು ಮಾಡಿದ ತಪ್ಪನ್ನು ಒಪ್ಪಿಕೊಳ್ಳಬೇಕು;",
			"6": "<sup>6</sup> ಮತ್ತು ದೋಷದ ಪ್ರಾಯಶ್ಚಿತ್ತಕ್ಕಾಗಿ ಹೆಣ್ಣು ಕುರಿಯನ್ನಾಗಲಿ ಹೆಣ್ಣು ಮೇಕೆಯನ್ನಾಗಲಿ ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಿಸಬೇಕು. ಯಾಜಕನು ಅವನಿಗೋಸ್ಕರ ದೋಷಪರಿಹಾರಮಾಡುವನು.",
			"7": "<sup>7</sup> ಕುರಿಯನ್ನು ಕೊಡುವದಕ್ಕೆ ಅವನಿಗೆ ಗತಿಯಿಲ್ಲದ ಪಕ್ಷದಲ್ಲಿ ದೋಷದ ಪ್ರಾಯಶ್ಚಿತ್ತಕ್ಕಾಗಿ ಎರಡು ಬೆಳವಕ್ಕಿಗಳನ್ನಾಗಲಿ ಎರಡು ಪಾರಿವಾಳದ ಮರಿಗಳನ್ನಾಗಲಿ ತೆಗೆದುಕೊಂಡು ಬಂದು ಸರ್ವಾಂಗಹೋಮವಾಗಿ ಒಂದನ್ನೂ ದೋಷಪರಿಹಾರಕ ಯಜ್ಞವಾಗಿ ಮತ್ತೊಂದನ್ನೂ ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಿಸಬೇಕು.",
			"8": "<sup>8</sup> ಅವನು ಅವುಗಳನ್ನು ಯಾಜಕನ ಬಳಿಗೆ ತಂದಾಗ ಯಾಜಕನು ಮೊದಲು ದೋಷಪರಿಹಾರಾರ್ಥವಾದದ್ದನ್ನು ಸಮರ್ಪಿಸಬೇಕು. ಅವನು ಅದರ ಕುತ್ತಿಗೆ ಮುರಿಯಬೇಕು; ಅದರ ತಲೆಯನ್ನು ಪೂರಾ ತೆಗೆದು ಬಿಡಕೂಡದು.",
			"9": "<sup>9</sup> ಆಗ ದೋಷಪರಿಹಾರಾರ್ಥವಾದ ಆ ಪಕ್ಷಿಯ ರಕ್ತದಲ್ಲಿ ಸ್ವಲ್ಪವನ್ನು ಯಜ್ಞವೇದಿಯ ಪಕ್ಕಕ್ಕೆ ಚಿಮಿಕಿಸಿ ಮಿಕ್ಕ ರಕ್ತವನ್ನು ಯಜ್ಞವೇದಿಯ ಬುಡದಲ್ಲಿ ಹಿಂಡಬೇಕು. ಅದು ದೋಷಪರಿಹಾರಕಯಜ್ಞ.",
			"10": "<sup>10</sup> ಮತ್ತೊಂದನ್ನು ಯಥಾವಿಧಿಯಾಗಿ ಸರ್ವಾಂಗಹೋಮ ಮಾಡಿಬಿಡಬೇಕು. ಅವನ ತಪ್ಪಿನ ನಿಮಿತ್ತ ಯಾಜಕನು ಹೀಗೆ ದೋಷಪರಿಹಾರವನ್ನು ಮಾಡಿದಾಗ ಅವನಿಗೆ ಕ್ಷಮಾಪಣೆಯಾಗುವದು.",
			"11": "<sup>11</sup> ಎರಡು ಬೆಳವಕ್ಕಿಗಳನ್ನಾಗಲಿ ಎರಡು ಪಾರಿವಾಳದ ಮರಿಗಳನ್ನಾಗಲಿ ಕೊಡುವದಕ್ಕೂ ಗತಿಯಿಲ್ಲದೆ ಹೋದರೆ ಅವನು ದೋಷಪರಿಹಾರಕ್ಕಾಗಿ ಮೂರು ಸೇರು ಗೋದಿಹಿಟ್ಟನ್ನು ತರಬೇಕು. ಆ ಹೋಮದ್ರವ್ಯವು ದೋಷಪರಿಹಾರಾರ್ಥವಾದ್ದರಿಂದ ಅದರ ಮೇಲೆ ಎಣ್ಣೆಯನ್ನು ಹೊಯ್ಯಕೂಡದು, ಧೂಪವನ್ನು ಇಡಕೂಡದು.",
			"12": "<sup>12</sup> ಅವನು ಯಾಜಕನ ಬಳಿಗೆ ಅದನ್ನು ತಂದನಂತರ ಅದು ದೇವಾರ್ಪಿತವೆಂದು ಸೂಚಿಸುವದಕ್ಕಾಗಿ ಯಾಜಕನು ಅದರಲ್ಲಿ ಒಂದು ಹಿಡಿಯನ್ನು ತೆಗೆದುಕೊಂಡು ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಿಸಿದ ಹೋಮದ್ರವ್ಯಗಳ ಮೇಲೆ ಯಜ್ಞವೇದಿಯಲ್ಲಿ ಹೋಮಮಾಡಬೇಕು; ಅದು ದೋಷಪರಿಹಾರಕಯಜ್ಞ.",
			"13": "<sup>13</sup> ಮೇಲೆ ಕಂಡ ವಿಷಯಗಳಲ್ಲಿ ಯಾವ ವಿಧವಾಗಿ ಅವನು ದೋಷಿಯಾದರೂ ಯಾಜಕನು ಅವನಿಗೋಸ್ಕರ ಹೀಗೆ ದೋಷಪರಿಹಾರವನ್ನು ಮಾಡಿದನಂತರ ಅವನಿಗೆ ಕ್ಷಮಾಪಣೆಯಾಗುವದು. ನೈವೇದ್ಯದ್ರವ್ಯದಲ್ಲಿ ಮಿಕ್ಕದ್ದು ಹೇಗೆ ಯಾಜಕನಿಗೆ ಆಗುತ್ತದೋ ಇದರಲ್ಲಿಯೂ ಮಿಕ್ಕದ್ದು ಯಾಜಕನಿಗೆ ಆಗಬೇಕು.",
			"14": "<sup>14</sup> ಯೆಹೋವನು ಮೋಶೆಗೆ ಹೇಳಿದ್ದೇನಂದರೆ -",
			"15": "<sup>15</sup> ಯಾವನಾದರೂ ಯೆಹೋವನಿಗೆ ಸಲ್ಲಿಸಬೇಕಾದ ದೇವರ ವಸ್ತುಗಳನ್ನು ಸಮರ್ಪಿಸುವದರಲ್ಲಿ ಅಜಾಗ್ರತೆಯಿಂದ ದ್ರೋಹಮಾಡಿ ದೋಷಕ್ಕೆ ಒಳಗಾದರೆ ಅವನು ಆ ಅಪರಾಧಪ್ರಾಯಶ್ಚಿತ್ತಕ್ಕಾಗಿ ದೇವರ ಸೇವೆಗೆ ನೇಮಕವಾದ ರೂಪಾಯಿಯ ಮೇರೆಗೆ ಎರಡು ಅಥವಾ ಹೆಚ್ಚು ರೂಪಾಯಿ ಬಾಳುವದೆಂದು ನಿಮಗೆ ತೋರುವ ಪೂರ್ಣಾಂಗವಾದ ಟಗರನ್ನು ಆಡುಕುರಿಗಳ ಹಿಂಡಿನಿಂದ ತೆಗೆದುಕೊಂಡು ಬಂದು ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಿಸಬೇಕು.",
			"16": "<sup>16</sup> ಮತ್ತು ತಾನು ಅನ್ಯಾಯವಾಗಿ ಇಟ್ಟುಕೊಂಡಿರುವ ದೇವರ ವಸ್ತುಗಳನ್ನು ಅಲ್ಲದೆ ಐದನೆಯ ಒಂದು ಭಾಗವನ್ನು ಹೆಚ್ಚಾಗಿ ಯಾಜಕನಿಗೆ ತಂದುಕೊಡಬೇಕು. ಅವನು ಪ್ರಾಯಶ್ಚಿತ್ತಕ್ಕಾಗಿ ತಂದ ಟಗರಿನಿಂದ ಯಾಜಕನು ಅವನಿಗೋಸ್ಕರ ದೋಷಪರಿಹಾರ ಮಾಡಿದಾಗ ಅವನಿಗೆ ಕ್ಷಮಾಪಣೆಯಾಗುವದು.",
			"17": "<sup>17</sup> ಯಾವನಾದರೂ ಯೆಹೋವನು ನಿಷೇಧಿಸಿದ ಕಾರ್ಯಗಳಲ್ಲಿ ಯಾವದನ್ನಾದರೂ ಮಾಡಿ ದೋಷಕ್ಕೆ ಒಳಗಾದರೆ ಅದು ಅವನಿಗೆ ತಿಳಿಯದೆಹೋದರೂ ಅವನು ಅದರಿಂದ ಅಪರಾಧಿಯಾಗಿ ತನ್ನ ಪಾಪಫಲವನ್ನು ಅನುಭವಿಸಬೇಕು.",
			"18": "<sup>18</sup> ಅವನು ಪ್ರಾಯಶ್ಚಿತ್ತಕ್ಕಾಗಿ ಹಿಂಡಿನಿಂದ ನಿಮಗೆ ಯೋಗ್ಯವಾಗಿ ತೋರುವ ಪೂರ್ಣಾಂಗವಾದ ಟಗರನ್ನು ಯಾಜಕನ ಬಳಿಗೆ ತೆಗೆದುಕೊಂಡು ಬರಬೇಕು. ಅವನು ತಿಳಿಯದೆ ಮಾಡಿದ ತಪ್ಪನ್ನು ಪರಿಹರಿಸುವದಕ್ಕಾಗಿ ಯಾಜಕನು ಅವನಿಗೋಸ್ಕರ ದೋಷಪರಿಹಾರ ಮಾಡಬೇಕು; ಆಗ ಅವನಿಗೆ ಕ್ಷಮಾಪಣೆಯಾಗುವದು.",
			"19": "<sup>19</sup> ಅವನು ಅಪರಾಧಿಯಾದದರಿಂದ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಈ ಪ್ರಾಯಶ್ಚಿತ್ತಯಜ್ಞವನ್ನು ಮಾಡಬೇಕು."
		},
		"6": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಮೋಶೆಗೆ ಹೇಳಿದ್ದೇನಂದರೆ -",
			"2": "<sup>2</sup> ಯಾವನಾದರೂ ಮತ್ತೊಬ್ಬನಿಂದ ತನ್ನ ವಶಕ್ಕೆ ಕೊಡಲ್ಪಟ್ಟ ವಸ್ತುವಿನ ವಿಷಯದಲ್ಲಾಗಲಿ ತನ್ನ ಬಳಿಯಲ್ಲಿ ಇಡಲ್ಪಟ್ಟ ಅಡವಿನ ವಿಷಯದಲ್ಲಾಗಲಿ ತಾನು ಮಾಡಿದ ಕಳವಿನ ವಿಷಯದಲ್ಲಾಗಲಿ ಸುಳ್ಳಾಡುವದರಿಂದಲೋ ಮತ್ತೊಬ್ಬನನ್ನು ಮೋಸಗೊಳಿಸಿದ್ದರಿಂದಲೋ",
			"3": "<sup>3</sup> ಮತ್ತೊಬ್ಬನು ಕಳಕೊಂಡದ್ದನ್ನು ತಾನು ಕಂಡು ಬೊಂಕಿ ಸುಳ್ಳಾಣೆಯಿಟ್ಟದ್ದರಿಂದಲೋ ಇವುಗಳಲ್ಲಿ ಯಾವ ವಿಷಯದಲ್ಲಾದರೂ ಒಬ್ಬನು ಪಾಪಮಾಡಿ ಯೆಹೋವನಿಗೆ ದ್ರೋಹಿಯಾಗಿ ದೋಷಕ್ಕೆ ಒಳಗಾದರೆ",
			"4": "<sup>4</sup> ಅವನು ಆಯಾ ವಸ್ತುಗಳನ್ನೇ ಅಂದರೆ ಕದ್ದದ್ದನ್ನೂ ಮೋಸದಿಂದ ಪಡೆದದ್ದನ್ನೂ ತನ್ನ ವಶಕ್ಕೆ ತೆಗೆದುಕೊಂಡದ್ದನ್ನೂ ತಾನು ಕಂಡುಕೊಂಡದ್ದನ್ನೂ",
			"5": "<sup>5</sup> ಬೇರೆ ಯಾವದರ ವಿಷಯದಲ್ಲಿ ಅವನು ಸುಳ್ಳಾಣೆಯಿಟ್ಟನೋ ಅದನ್ನೂ ಪೂರ್ತಿಯಾಗಿ ತಂದುಕೊಡಬೇಕಲ್ಲದೆ ಐದನೆಯ ಒಂದು ಪಾಲನ್ನು ಹೆಚ್ಚಾಗಿ ಕೊಡಬೇಕು. ಅವನು ಪ್ರಾಯಶ್ಚಿತ್ತಯಜ್ಞವನ್ನು ಮಾಡುವ ದಿನದಲ್ಲೇ ಅದನ್ನು ಅದರ ನ್ಯಾಯವಾದ ಒಡೆಯನಿಗೆ ಹಿಂದಕ್ಕೆ ಕೊಡಬೇಕು.",
			"6": "<sup>6</sup> ಮತ್ತು ಅವನು ಪ್ರಾಯಶ್ಚಿತ್ತಕ್ಕಾಗಿ ನಿನಗೆ ಸರಿಯೆಂದು ತೋರುವ ಪೂರ್ಣಾಂಗವಾದ ಟಗರನ್ನು ಹಿಂಡಿನಿಂದ ತಂದು ಯಾಜಕನಿಗೆ ಒಪ್ಪಿಸಬೇಕು.",
			"7": "<sup>7</sup> ಯಾಜಕನು ಅವನಿಗೋಸ್ಕರ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ದೋಷಪರಿಹಾರವನ್ನು ಮಾಡಿದಾಗ ಅವನು ಯಾವ ವಿಷಯದಲ್ಲಿ ಅಪರಾಧಕ್ಕೆ ಒಳಗಾಗಿದ್ದನೋ ಆ ವಿಷಯದಲ್ಲಿ ಅವನಿಗೆ ಕ್ಷಮಾಪಣೆಯಾಗುವದು.",
			"8": "<sup>8</sup> ಯೆಹೋವನು ಮೋಶೆಗೆ ಹೇಳಿದ್ದೇನಂದರೆ - ನೀನು ಆರೋನನಿಗೂ ಅವನ ವಂಶದವರಿಗೂ ಮುಂದಿನ ಸಂಗತಿಗಳನ್ನು ಆಜ್ಞಾಪಿಸಬೇಕು; ಯಾವವಂದರೆ - ಸರ್ವಾಂಗಹೋಮ ವಿಷಯವಾದ ನಿಯಮಗಳು. ಸರ್ವಾಂಗಹೋಮದ್ರವ್ಯವು ರಾತ್ರಿಯೆಲ್ಲಾ ಮರುದಿನದ ಬೆಳಗಿನವರೆಗೂ ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಉರಿಯುತ್ತಾ ಇರಬೇಕು. ಅದರಿಂದಲೇ ಯಜ್ಞವೇದಿಯ ಮೇಲಣ ಬೆಂಕಿ ಉರಿಯುತ್ತಿರಬೇಕು.",
			"10": "<sup>10</sup> ಯಾಜಕನು ನಾರಿನ ನಿಲುವಂಗಿಯನ್ನು ಧರಿಸಿಕೊಂಡು ಮಾನರಕ್ಷಕವಾದ ನಾರಿನ ಚಡ್ಡಿಯನ್ನು ಹಾಕಿಕೊಂಡು ಯಜ್ಞವೇದಿಯ ಮೇಲಣ ಸರ್ವಾಂಗಹೋಮದ್ರವ್ಯದ ಬೂದಿಯನ್ನು ಎತ್ತಿ ವೇದಿಯ ಬಳಿಯಲ್ಲಿ ಇಡಬೇಕು.",
			"11": "<sup>11</sup> ಅನಂತರ ಆ ವಸ್ತ್ರಗಳನ್ನು ತೆಗೆದಿಟ್ಟು ಬೇರೆ ವಸ್ತ್ರಗಳನ್ನು ಹಾಕಿಕೊಂಡು ಆ ಬೂದಿಯನ್ನು ಪಾಳೆಯದ ಹೊರಗೆ ಶುದ್ಧವಾದ ಒಂದು ಸ್ಥಳಕ್ಕೆ ತೆಗೆದುಕೊಂಡು ಹೋಗಬೇಕು.",
			"12": "<sup>12</sup> ಯಜ್ಞವೇದಿಯ ಮೇಲಣ ಬೆಂಕಿ ಸರ್ವಾಂಗಹೋಮದ್ರವ್ಯದಿಂದ ಉರಿಯುತ್ತಲೇ ಇರಬೇಕು; ಅದು ಆರಿಹೋಗಬಾರದು. ಪ್ರತಿದಿನವೂ ಹೊತ್ತಾರೆಯಲ್ಲಿ ಯಾಜಕನು ಕಟ್ಟಿಗೆಯನ್ನು ತಂದುಹಾಕಿ ಬೆಂಕಿ ಉರಿಸಿ ಅದರ ಮೇಲೆ ಹೋಮದ್ರವ್ಯವನ್ನು ಕ್ರಮಪಡಿಸಿ ಸಮಾಧಾನಯಜ್ಞಪಶುಗಳ ಕೊಬ್ಬನ್ನು ಅದರ ಮೇಲೆ ಹೋಮಮಾಡಬೇಕು.",
			"13": "<sup>13</sup> ಬೆಂಕಿಯು ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಯಾವಾಗಲೂ ಉರಿಯುತ್ತಿರಬೇಕು; ಆರಿಹೋಗಬಾರದು.",
			"14": "<sup>14</sup> ಧಾನ್ಯನೈವೇದ್ಯ ವಿಷಯವಾದ ನಿಯಮಗಳು. ಆರೋನನ ವಂಶದವರು ನೈವೇದ್ಯದ್ರವ್ಯವನ್ನು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಯಜ್ಞವೇದಿಯ ಎದುರಾಗಿಯೇ ಸಮರ್ಪಿಸಬೇಕು.",
			"15": "<sup>15</sup> ಅವರಲ್ಲಿ ಒಬ್ಬನು ನೈವೇದ್ಯವಾದದ್ದನ್ನು ಸೂಚಿಸುವದಕ್ಕಾಗಿ ಎಣ್ಣೆ ಹೊಯಿದ ಹಿಟ್ಟಿನಲ್ಲಿ ಒಂದು ಹಿಡಿಯನ್ನೂ ಅದರ ಮೇಲಿರುವ ಧೂಪವೆಲ್ಲವನ್ನೂ ತೆಗೆದುಕೊಂಡು ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಹೋಮಮಾಡಿ ಯೆಹೋವನಿಗೆ ಸುವಾಸನೆಯನ್ನುಂಟುಮಾಡಬೇಕು.",
			"16": "<sup>16</sup> ಮಿಕ್ಕದ್ದನ್ನು ಆರೋನನೂ ಅವನ ವಂಶದವರೂ ತಿನ್ನಬಹುದು; ಅವರು ಅದರಿಂದ ಹುಳಿಯಿಲ್ಲದ ರೊಟ್ಟಿಗಳನ್ನು ಮಾಡಿಸಿಕೊಂಡು ಪವಿತ್ರಸ್ಥಳದೊಳಗೆ ಅಂದರೆ ದೇವದರ್ಶನದ ಗುಡಾರದ ಅಂಗಳದಲ್ಲಿ ತಿನ್ನಬೇಕು.",
			"17": "<sup>17</sup> ಅದಕ್ಕೆ ಹುಳಿಕಲಸಿ ಸುಡಕೂಡದು. ನನಗೆ ಹೋಮರೂಪವಾಗಿ ಸಮರ್ಪಿಸಿದ ದ್ರವ್ಯಗಳಲ್ಲಿ ಅದನ್ನು ಅವರ ಪಾಲಿಗೆ ಬಿಟ್ಟಿದ್ದೇನೆ. ದೋಷಪರಿಹಾರಕ ಯಜ್ಞದ್ರವ್ಯದಂತೆಯೂ ಪ್ರಾಯಶ್ಚಿತ್ತ ಯಜ್ಞದ್ರವ್ಯದಂತೆಯೂ ಅದು ಮಹಾಪರಿಶುದ್ಧವಾದದ್ದು.",
			"18": "<sup>18</sup> ಆರೋನನ ವಂಶದವರಾದ ಗಂಡಸರೆಲ್ಲರೂ ಅದನ್ನು ತಿನ್ನಬಹುದು. ಯೆಹೋವನಿಗೆ ಹೋಮವಾಗಿ ಅರ್ಪಿತವಾದ ದ್ರವ್ಯಗಳಲ್ಲಿ ಅದು ನಿಮಗೂ ನಿಮ್ಮಸಂತತಿಯವರಿಗೂ ಸಲ್ಲತಕ್ಕದ್ದೆಂಬದು ಶಾಶ್ವತ ನಿಯಮ. ಆ ದ್ರವ್ಯಗಳಿಗೆ ತಗಲಿದ್ದೆಲ್ಲಾ ಪರಿಶುದ್ಧವಾಗುವದು.",
			"19": "<sup>19</sup> ಯೆಹೋವನು ಮೋಶೆಗೆ ಹೇಳಿದ್ದೇನಂದರೆ -",
			"20": "<sup>20</sup> ಆರೋನನಿಗೆ ಅಭಿಷೇಕವಾದ ದಿನ ಮೊದಲುಗೊಂಡು ಅವನೂ ಅವನ ವಂಶದವರೂ ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಿಸಬೇಕಾದ ಧಾನ್ಯನೈವೇದ್ಯಕ್ರಮ ಹೇಗಂದರೆ - ಅವರು ನಿತ್ಯವೂ ಮೂರು ಸೇರು ಗೋದಿ ಹಿಟ್ಟನ್ನು, ಹೊತ್ತಾರೆ ಅರ್ಧವನ್ನೂ ಸಾಯಂಕಾಲ ಅರ್ಧವನ್ನೂ ಸಮರ್ಪಿಸಬೇಕು.",
			"21": "<sup>21</sup> ಕಬ್ಬಿಣದ ಹಂಚಿನ ಮೇಲೆ ಎಣ್ಣೆ ಸವರಿ ಅದನ್ನು ಸುಡಬೇಕು. ಅದನ್ನು ತರುವಾಗ ಅದು ಎಣ್ಣೆಯಿಂದ ನೆನೆದೇ ಇರಬೇಕು. ಅದನ್ನು ಭಾಗ ಭಾಗವಾಗಿ ಮಾಡಿ ಯೆಹೋವನಿಗೆ ಸುವಾಸನೆಯುಳ್ಳ ನೈವೇದ್ಯವನ್ನಾಗಿ ಸಮರ್ಪಿಸಬೇಕು.",
			"22": "<sup>22</sup> ಆರೋನನ ಸಂತತಿಯವರಲ್ಲಿ ಯಾವನು ಅವನ ತರುವಾಯ ಅಭಿಷಿಕ್ತಯಾಜಕನ ಪಟ್ಟಕ್ಕೆ ಬರುವನೋ ಅವನೇ ಅದನ್ನು ಸಮರ್ಪಿಸಬೇಕು; ಇದು ಶಾಶ್ವತನಿಯಮ. ಅದನ್ನು ನಿಶ್ಶೇಷವಾಗಿ ಹೋಮಮಾಡಿಬಿಡಬೇಕು.",
			"23": "<sup>23</sup> ಯಾಜಕರು ತಮಗೋಸ್ಕರ ಸಮರ್ಪಿಸುವ ನೈವೇದ್ಯದ್ರವ್ಯಗಳನ್ನು ನಿಶ್ಶೇಷವಾಗಿ ಹೋಮಮಾಡಿಬಿಡಬೇಕು; ಅದನ್ನು ತಿನ್ನಲೇಬಾರದು.",
			"24": "<sup>24</sup> ಯೆಹೋವನು ಮೋಶೆಗೆ ಹೇಳಿದ್ದೇನಂದರೆ -",
			"25": "<sup>25</sup> ನೀನು ಆರೋನನಿಗೂ ಅವನ ವಂಶದವರಿಗೂ ಹೀಗೆ ಆಜ್ಞಾಪಿಸು - ದೋಷಪರಿಹಾರಕ ಯಜ್ಞನಿಯಮಗಳು. ಸರ್ವಾಂಗಹೋಮದ ಪಶುವನ್ನು ವಧಿಸುವ ಸ್ಥಳದಲ್ಲೇ ದೋಷಪರಿಹಾರಕ ಯಜ್ಞಪಶುವನ್ನೂ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ವಧಿಸಬೇಕು.",
			"26": "<sup>26</sup> ಅದು ಮಹಾಪರಿಶುದ್ಧವಾದದ್ದರಿಂದ ಸಮರ್ಪಿಸುವ ಯಾಜಕನು ಅದರ ಮಾಂಸವನ್ನು ಊಟಮಾಡಬೇಕು. ಪವಿತ್ರಸ್ಥಳದೊಳಗೆ ಅಂದರೆ ದೇವದರ್ಶನದ ಗುಡಾರದ ಅಂಗಳದಲ್ಲಿ ಅದನ್ನು ಊಟಮಾಡಬೇಕು.",
			"27": "<sup>27</sup> ಆ ಪಶುವಿನ ಮಾಂಸಕ್ಕೆ ಸೋಂಕಿದ್ದೆಲ್ಲಾ ಪರಿಶುದ್ಧವಾಗುವದು. ಅದರ ರಕ್ತದಲ್ಲಿ ಸ್ವಲ್ಪವಾದರೂ ಬಟ್ಟೆಯ ಮೇಲೆ ಬಿದ್ದರೆ ಆ ಬಟ್ಟೆಯನ್ನು ಪವಿತ್ರಸ್ಥಳದೊಳಗೆ ಒಗೆದುಕೊಳ್ಳಬೇಕು.",
			"28": "<sup>28</sup> ಅದನ್ನು ಬೇಯಿಸಿದ ಮಣ್ಣೆನ ಪಾತ್ರೆಯನ್ನು ಒಡೆದುಬಿಡಬೇಕು. ತಾಮ್ರದ ಪಾತ್ರೆಯಲ್ಲಿ ಬೇಯಿಸಿದ್ದಾದರೆ ಆ ಪಾತ್ರೆಯನ್ನು ಬೆಳಗಿ ನೀರಿನಿಂದ ತೊಳೆಯಬೇಕು.",
			"29": "<sup>29</sup> ಯಾಜಕರಲ್ಲಿ ಗಂಡಸರೆಲ್ಲರೂ ಅದನ್ನು ತಿನ್ನಬಹುದು; ಅದು ಮಹಾಪರಿಶುದ್ಧವಾದದ್ದು.",
			"30": "<sup>30</sup> ಆದರೆ ದೋಷಪರಿಹಾರಕ ಯಜ್ಞಪಶುಗಳಲ್ಲಿ ಯಾವ ಪಶುವಿನ ರಕ್ತವು ದೋಷಪರಿಹಾರಕ್ಕಾಗಿ ದೇವದರ್ಶನದ ಗುಡಾರದೊಳಗೆ ತಂದಿರಬೇಕೋ ಅದರ ಮಾಂಸವನ್ನು ತಿನ್ನಲೇಬಾರದು. ಬೆಂಕಿಯಲ್ಲಿ ಸುಟ್ಟುಬಿಡಬೇಕು."
		},
		"7": {
			"1": "<sup>1</sup> ಪ್ರಾಯಶ್ಚಿತ್ತ ಯಜ್ಞನಿಯಮಗಳು. ಆ ಯಜ್ಞವು ಮಹಾಪರಿಶುದ್ಧವಾದದ್ದು.",
			"2": "<sup>2</sup> ಸರ್ವಾಂಗಹೋಮಪಶುವನ್ನು ವಧಿಸುವ ಸ್ಥಳದಲ್ಲೇ ಪ್ರಾಯಶ್ಚಿತ್ತ ಯಜ್ಞಪಶುವನ್ನೂ ವಧಿಸಬೇಕು. ಯಾಜಕನು ಅದರ ರಕ್ತವನ್ನು ಯಜ್ಞವೇದಿಗೆ ಸುತ್ತಲೂ ಎರಚಬೇಕು.",
			"3": "<sup>3</sup> ಅವನು ಅದರ ಕೊಬ್ಬನ್ನೆಲ್ಲಾ ಅಂದರೆ ಬಾಲದ ಕೊಬ್ಬನ್ನೂ ವಪೆಯನ್ನೂ ಎರಡು ಹುರುಳಿಕಾಯಿಗಳನ್ನೂ",
			"4": "<sup>4</sup> ಇವುಗಳ ಮೇಲೆ ಸೊಂಟದಲ್ಲಿರುವ ಕೊಬ್ಬನ್ನೂ ಕಾಳಿಜದ ಮೇಲೆ ಹುರುಳಿಕಾಯಿಗಳ ತನಕ ಇರುವ ಕೊಬ್ಬನ್ನೂ ತೆಗೆದು",
			"5": "<sup>5</sup> ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಯೆಹೋವನಿಗೆ ಹೋಮಮಾಡಬೇಕು. ಅದು ಪ್ರಾಯಶ್ಚಿತ್ತ ಯಜ್ಞವಾಗಿದೆ.",
			"6": "<sup>6</sup> ಹೋಮಶೇಷವನ್ನು ಯಾಜಕರಲ್ಲಿ ಗಂಡಸರೆಲ್ಲರೂ ತಿನ್ನಬಹುದು; ಅದು ಮಹಾಪರಿಶುದ್ಧವಾದದ್ದರಿಂದ ಪವಿತ್ರಸ್ಥಳದೊಳಗೆ ಅದನ್ನು ತಿನ್ನಬೇಕು.",
			"7": "<sup>7</sup> ಪ್ರಾಯಶ್ಚಿತ್ತಯಜ್ಞದ ಹೋಮಶೇಷದ ವಿಷಯದಲ್ಲಿಯೂ ದೋಷಪರಿಹಾರಕಯಜ್ಞದ ಹೋಮಶೇಷದ ವಿಷಯದಲ್ಲಿಯೂ ಒಂದೇ ವಿಧಿಯುಂಟು; ಹೇಗಂದರೆ ಆ ಹೋಮಶೇಷವು ದೋಷಪರಿಹಾರವನ್ನು ಮಾಡಿಸುವ ಯಾಜಕನಿಗೆ ಸಲ್ಲತಕ್ಕದ್ದು;",
			"8": "<sup>8</sup> ಯಾರಾದರೂ ಸರ್ವಾಂಗಹೋಮಕ್ಕಾಗಿ ಪಶುವನ್ನು ತಂದಾಗ ಅದರ ಚರ್ಮವು ಆ ಪಶುವನ್ನು ಸಮರ್ಪಿಸುವ ಯಾಜಕನದಾಗಿಯೇ ಇರಬೇಕು.",
			"9": "<sup>9</sup> ಒಲೆಯಲ್ಲಾಗಲಿ ಕಬ್ಬಿಣದ ಹಂಚಿನಲ್ಲಾಗಲಿ ಬಾಂಡ್ಲೆಯಲ್ಲಾಗಲಿ ಬೇಯಿಸಿದ ನೈವೇದ್ಯಪದಾರ್ಥವೆಲ್ಲಾ ಅದನ್ನು ಸಮರ್ಪಿಸುವ ಯಾಜಕನಿಗೆ ಆಗಬೇಕು.",
			"10": "<sup>10</sup> ಆದರೆ ಎಣ್ಣೆ ಹೊಯಿದ ಹಿಟ್ಟನ್ನಾಗಲಿ ಬರೀ ಹಿಟ್ಟನ್ನಾಗಲಿ ಯಾರಾದರೂ ನೈವೇದ್ಯಕ್ಕಾಗಿ ತಂದಾಗ ಅದನ್ನು ಆರೋನನ ವಂಶದವರೆಲ್ಲರೂ ಸಮವಾಗಿ ಅನುಭವಿಸಬೇಕು.",
			"11": "<sup>11</sup> ಸಮಾಧಾನಯಜ್ಞನಿಯಮಗಳು. ಯಾರಾದರೂ ಕೃತಜ್ಞತೆಯನ್ನು ತೋರಿಸುವದಕ್ಕಾಗಿ ಸಮಾಧಾನಯಜ್ಞವನ್ನು ಯೆಹೋವನಿಗೆ ಮಾಡುವದಾದರೆ ಅದರೊಡನೆ ಎಣ್ಣೆ ಮಿಶ್ರವಾದ ಹುಳಿಯಿಲ್ಲದ ಹೋಳಿಗೆಗಳನ್ನೂ ಎಣ್ಣೆ ಹಾಕಿದ ಹುಳಿಯಿಲ್ಲದ ಕಡುಬುಗಳನ್ನೂ ಎಣ್ಣೆಯಿಂದ ಪೂರಾ ನೆನಸಿದ ಗೋದಿಹಿಟ್ಟಿನ ಹೋಳಿಗೆಗಳನ್ನೂ ಸಮರ್ಪಿಸಬೇಕು.",
			"13": "<sup>13</sup> ಕೃತಜ್ಞತೆಯನ್ನು ತೋರಿಸುವದಕ್ಕಾಗಿ ಸಮಾಧಾನಯಜ್ಞದಲ್ಲಿ ಒಪ್ಪಿಸುವ ಪಶುವಿನೊಡನೆ ಹುಳಿರೊಟ್ಟಿಗಳನ್ನೂ ಸಮರ್ಪಿಸಬೇಕು.",
			"14": "<sup>14</sup> ಸಮರ್ಪಿಸಿದ ಪ್ರತಿಯೊಂದು ವಿಧವಾದ ಪದಾರ್ಥಗಳಲ್ಲಿಯೂ ಒಂದೊಂದನ್ನು ಯೆಹೋವನಿಗೋಸ್ಕರ ಪ್ರತ್ಯೇಕಿಸಬೇಕು. ಅವು ವೇದಿಗೆ ಯಜ್ಞಪಶುವಿನ ರಕ್ತವನ್ನು ಎರಚಿದ ಯಾಜಕನಿಗೆ ಆಗಬೇಕು.",
			"15": "<sup>15</sup> ಕೃತಜ್ಞತಾಯಜ್ಞಪಶುವಿನ ಮಾಂಸವನ್ನು ಯಜ್ಞವು ನಡೆದ ದಿನದಲ್ಲೇ ಭೋಜನ ಮಾಡಬೇಕು; ಮರುದಿನದವರೆಗೆ ಸ್ವಲ್ಪವನ್ನಾದರೂ ಉಳಿಸಕೂಡದು.",
			"16": "<sup>16</sup> ಯಾರಾದರೂ ಹರಕೆಯನ್ನು ಸಲ್ಲಿಸುವದಕ್ಕಾಗಲಿ ಸ್ವೇಚ್ಫೆಯಿಂದಾಗಲಿ ಅಂಥ ಯಜ್ಞವನ್ನು ಮಾಡಿಸಿದರೆ ಅದನ್ನು ಸಮರ್ಪಿಸಿದ ದಿನದಲ್ಲೇ ಅದರ ಮಾಂಸವನ್ನು ಊಟಮಾಡಬೇಕು; ಆದರೆ ಮಿಕ್ಕದ್ದನ್ನು ಮರುದಿನದಲ್ಲಿ ತಿನ್ನಬಹುದು.",
			"17": "<sup>17</sup> ಮೂರನೆಯ ದಿನದವರೆಗೆ ಉಳಿದಿದ್ದನ್ನು ಬೆಂಕಿಯಿಂದ ಸುಟ್ಟುಬಿಡಬೇಕು.",
			"18": "<sup>18</sup> ಆ ಯಜ್ಞಪಶುವಿನ ಮಾಂಸದಲ್ಲಿ ಸ್ವಲ್ಪವನ್ನಾದರೂ ಮೂರನೆಯ ದಿನದಲ್ಲಿ ಊಟಮಾಡಿದರೆ ಆ ಯಜ್ಞವೇ ಸಮರ್ಪಕವಾದದ್ದಲ್ಲ; ಅರ್ಪಿಸಿದವನಿಗೆ ಅದರಿಂದ ಫಲವೇನೂ ದೊರೆಯುವದಿಲ್ಲ. ಅದು ಹೇಯವಾದದ್ದರಿಂದ ಅದನ್ನು ತಿಂದವನು ಆ ಪಾಪದ ಫಲವನ್ನು ಅನುಭವಿಸಬೇಕು.",
			"19": "<sup>19</sup> ಅಶುದ್ಧವಾದ ವಸ್ತುವಿಗೆ ಸೋಂಕಿದ ಮಾಂಸವನ್ನು ತಿನ್ನದೆ ಬೆಂಕಿಯಲ್ಲಿ ಸುಟ್ಟುಬಿಡಬೇಕು. ಶುದ್ಧರಾಗಿರುವವರೆಲ್ಲರೂ [ಯಜ್ಞಪಶುವಿನ] ಮಾಂಸವನ್ನು ತಿನ್ನಬಹುದು.",
			"20": "<sup>20</sup> ಯಾವನಾದರೂ ಅಶುದ್ಧನಾಗಿದ್ದು ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಿತವಾದ ಸಮಾಧಾನಯಜ್ಞಪಶುವಿನ ಮಾಂಸವನ್ನು ತಿಂದರೆ ಅವನು ತನ್ನ ಕುಲದಿಂದ ತೆಗೆದುಹಾಕಲ್ಪಡಬೇಕು.",
			"21": "<sup>21</sup> ಯಾವನಿಗಾದರೂ ಮನುಷ್ಯ ದೇಹದಿಂದುಂಟಾದ ಅಶುದ್ಧ ವಸ್ತು, ಅಶುದ್ಧ ಮೃಗ, ನಿಷಿದ್ಧವಸ್ತು ಇವುಗಳಲ್ಲಿ ಯಾವದಾದರೂ ಸೋಂಕಿದರೆ ಅವನು ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಿತವಾದ ಯಜ್ಞಪಶುವಿನ ಮಾಂಸವನ್ನು ತಿಂದರೆ ಕುಲದಿಂದ ತೆಗೆದುಹಾಕಲ್ಪಡಬೇಕು.",
			"22": "<sup>22</sup> ಯೆಹೋವನು ಮೋಶೆಗೆ ಹೇಳಿದ್ದೇನಂದರೆ -",
			"23": "<sup>23</sup> ನೀನು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಹೀಗೆ ಆಜ್ಞಾಪಿಸು - ನೀವು ಎತ್ತು ಕುರಿ ಆಡುಗಳ ಕೊಬ್ಬನ್ನು ತಿನ್ನಬಾರದು.",
			"24": "<sup>24</sup> ಅಂಥ ಪಶುವು ರೋಗದಿಂದ ಸತ್ತದ್ದಾದರೆ ಇಲ್ಲವೇ ಕಾಡು ಮೃಗವು ಕೊಂದದ್ದಾದರೆ ಅದರ ಕೊಬ್ಬನ್ನು ಬೇರೆ ಕೆಲಸಕ್ಕೆ ಉಪಯೋಗಿಸಬಹುದೇ ಹೊರತು ಎಷ್ಟು ಮಾತ್ರವೂ ತಿನ್ನಬಾರದು.",
			"25": "<sup>25</sup> ಮನುಷ್ಯನು ಯೆಹೋವನಿಗೆ ಹೋಮಮಾಡುವಂಥ ಪಶುಜಾತಿಯ ಕೊಬ್ಬನ್ನು ಯಾವನಾದರೂ ತಿಂದರೆ ಅವನು ಕುಲದಿಂದ ತೆಗೆದುಹಾಕಲ್ಪಡಬೇಕು.",
			"26": "<sup>26</sup> ಪಕ್ಷಿಯದಾಗಲಿ ಪಶುವಿನದಾಗಲಿ ಯಾವ ರಕ್ತವನ್ನೂ ನೀವು ಎಲ್ಲಿಯೂ ಊಟಮಾಡಬಾರದು.",
			"27": "<sup>27</sup> ರಕ್ತಭೋಜನಮಾಡಿದವನು ಕುಲದಿಂದ ತೆಗೆದುಹಾಕಲ್ಪಡಬೇಕು.",
			"28": "<sup>28</sup> ಯೆಹೋವನು ಮೋಶೆಗೆ ಹೇಳಿದ್ದೇನಂದರೆ -",
			"29": "<sup>29</sup> ನೀನು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಹೀಗೆ ಅಜ್ಞಾಪಿಸು - ಸಮಾಧಾನಯಜ್ಞಪಶುಗಳನ್ನು ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಿಸುವವನು ಆ ಯಜ್ಞದ್ರವ್ಯಗಳಲ್ಲಿ ಯೆಹೋವನಿಗೆ ಸಲ್ಲಬೇಕಾದದ್ದನ್ನು ತಂದುಕೊಡಬೇಕು.",
			"30": "<sup>30</sup> ಯೆಹೋವನಿಗೆ ಹೋಮರೂಪವಾಗಿ ಸಮರ್ಪಿಸಬೇಕಾದದ್ದನ್ನು ಅಂದರೆ ಪಶುವಿನ ಕೊಬ್ಬನ್ನು ತನ್ನ ಕೈಯಿಂದಲೇ ಕೊಡಬೇಕು. ಅದರೊಂದಿಗೆ ಅದರ ಎದೆಯ ಭಾಗವನ್ನೂ ನೈವೇದ್ಯರೂಪವಾಗಿ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನಿವಾಳಿಸುವದಕ್ಕಾಗಿ ತಂದು ಸಮರ್ಪಿಸಬೇಕು.",
			"31": "<sup>31</sup> ಯಾಜಕನು ಆ ಕೊಬ್ಬನ್ನು ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಹೋಮಮಾಡಬೇಕು; ಎದೆಯ ಭಾಗವು ಆರೋನನಿಗೂ ಅವನ ವಂಶದವರಿಗೂ ಆಗಬೇಕು.",
			"32": "<sup>32</sup> ನೀವು ಸಮಾಧಾನಯಜ್ಞದ್ರವ್ಯಗಳಲ್ಲಿ ಬಲದೊಡೆಯನ್ನು ಯಾಜಕನಿಗೋಸ್ಕರ ಪ್ರತ್ಯೇಕಿಸಿಕೊಡಬೇಕು.",
			"33": "<sup>33</sup> ಆರೋನನ ವಂಶದವರಲ್ಲಿ ಯಾವನು ಆ ಯಜ್ಞಪಶುವಿನ ರಕ್ತವನ್ನೂ ಕೊಬ್ಬನ್ನೂ ಸಮರ್ಪಿಸುತ್ತಾನೋ ಅದರ ಬಲದೊಡೆಯು ಅವನ ಭಾಗವಾಗಿರಬೇಕು.",
			"34": "<sup>34</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಸಮಾಧಾನಯಜ್ಞಪಶುವಿನ ಮಾಂಸದಲ್ಲಿ ನೈವೇದ್ಯರೂಪವಾಗಿ ನಿವಾಳಿಸುವ ಎದೆಯ ಭಾಗವನ್ನೂ ಯಾಜಕನಿಗೋಸ್ಕರ ಪ್ರತ್ಯೇಕಿಸುವ ತೊಡೆಯನ್ನೂ ನಾನು ತೆಗೆದುಕೊಂಡು ಮಹಾಯಾಜಕನಾದ ಆರೋನನಿಗೂ ಅವನ ವಂಶದವರಿಗೂ ಕೊಟ್ಟು ಇವು ಇಸ್ರಾಯೇಲ್ಯರಿಂದ ಯಾಜಕರಿಗೆ ಯಾವಾಗಲೂ ಸಲ್ಲಬೇಕೆಂದು ಶಾಶ್ವತನಿಯಮ ಮಾಡಿದ್ದೇನೆ.",
			"35": "<sup>35</sup> ಯೆಹೋವನಿಗೆ ಹೋಮರೂಪವಾಗಿ ಅರ್ಪಿತವಾದ ದ್ರವ್ಯಗಳಲ್ಲಿ ಇವೇ ಆರೋನನಿಗೂ ಅವನ ವಂಶದವರಿಗೂ ಶಾಶ್ವತವಾಗಿ ಸಲ್ಲತಕ್ಕ ಭಾಗಗಳಾಗಿರುವವು. [ಮೋಶೆ] ಅವರನ್ನು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಯಾಜಕೋದ್ಯೋಗಕ್ಕಾಗಿ ಪ್ರತಿಷ್ಠಿಸಿದ ದಿನದಲ್ಲೇ ಇದು ನೇಮಕವಾಯಿತು.",
			"36": "<sup>36</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಇವುಗಳನ್ನು ಯಾಜಕರಿಗೆ ಸಲ್ಲಿಸಬೇಕೆಂದು ಯೆಹೋವನು [ಮೋಶೆಯ ಕೈಯಿಂದ] ಅವರನ್ನು ಪ್ರತಿಷ್ಠಿಸಿ ಅಭಿಷೇಕಿಸಿದ ದಿನದಲ್ಲಿ ಅಪ್ಪಣೆಮಾಡಿದನು.",
			"37": "<sup>37</sup> ಸರ್ವಾಂಗಹೋಮ, ಧಾನ್ಯನೈವೇದ್ಯ, ದೋಷಪರಿಹಾರಕಯಜ್ಞ, ಪ್ರಾಯಶ್ಚಿತ್ತಯಜ್ಞ, ಯಾಜಕ ಪ್ರತಿಷ್ಠೆ, ಸಮಾಧಾನ ಯಜ್ಞ ಎಂಬಿವುಗಳ ವಿಷಯದಲ್ಲಿ ಮೇಲೆ ಕಂಡದ್ದೆ ವಿಧಿ.",
			"38": "<sup>38</sup> ಯೆಹೋವನು ಸೀನಾಯಿ ಬೆಟ್ಟದಲ್ಲಿ ಮೋಶೆಗೆ ಈ ಆಜ್ಞೆಗಳನ್ನು ಕೊಟ್ಟನು. ಮೋಶೆ ಸೀನಾಯಿ ಅರಣ್ಯದಲ್ಲಿ - ಇಸ್ರಾಯೇಲ್ಯರು ಅರ್ಪಿಸಬೇಕಾದದ್ದನ್ನು ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಿಸಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿ ಅವರಿಗೆ ಮೇಲೆ ಕಂಡ ವಿಧಿಗಳನ್ನು ಕೊಟ್ಟನು."
		},
		"8": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಮೋಶೆಗೆ - ನೀನು ಆರೋನನನ್ನೂ ಅವನ ಗಂಡು ಮಕ್ಕಳನ್ನೂ ಕರಕೊಂಡು ಅವರಿಗೆ ಬೇಕಾದ ದೀಕ್ಷಾವಸ್ತ್ರಗಳನ್ನೂ ಅಭಿಷೇಕ ತೈಲವನ್ನೂ ದೋಷಪರಿಹಾರಕ್ಕಾಗಿ ಸಮರ್ಪಿಸಬೇಕಾದ ಹೋರಿಯನ್ನೂ ಎರಡು ಟಗರುಗಳನ್ನೂ ಹುಳಿಯಿಲ್ಲದ ಭಕ್ಷ್ಯಗಳು ತುಂಬಿರುವ ಪುಟ್ಟಿಯನ್ನೂ ತೆಗೆದುಕೊಂಡು ಬಂದು",
			"3": "<sup>3</sup> ಜನಸಮೂಹವನ್ನೆಲ್ಲಾ ದೇವದರ್ಶನದ ಗುಡಾರದ ಬಾಗಲಿನ ಹತ್ತಿರಕ್ಕೆ ಕರಿಸು ಎಂದು ಆಜ್ಞಾಪಿಸಲಾಗಿ",
			"4": "<sup>4</sup> ಯೆಹೋವನು ಹೇಳಿದಂತೆಯೇ ಮೋಶೆ ಮಾಡಿದನು. ಜನಸಮೂಹವೆಲ್ಲಾ ದೇವದರ್ಶನದ ಗುಡಾರದ ಬಾಗಲಿನ ಹತ್ತಿರ ಕೂಡಿದಾಗ ಮೋಶೆ ಅವರಿಗೆ -",
			"5": "<sup>5</sup> ನಾನು ಈಗ ಮಾಡುವ ಕಾರ್ಯವು ಯೆಹೋವನು ಆಜ್ಞಾಪಿಸಿದ್ದು ಎಂದು ಹೇಳಿದನು.",
			"6": "<sup>6</sup> ಆಗ ಮೋಶೆ ಆರೋನನನ್ನೂ ಅವನ ಮಕ್ಕಳನ್ನೂ ಹತ್ತಿರಕ್ಕೆ ಬರಮಾಡಿಕೊಂಡು ಸ್ನಾನಮಾಡಿಸಿದನು.",
			"7": "<sup>7</sup> ಆಮೇಲೆ ಯೆಹೋವನು ಆಜ್ಞಾಪಿಸಿದಂತೆ ಆರೋನನಿಗೆ ನಿಲುವಂಗಿಯನ್ನು ತೊಡಿಸಿ ನಡುಕಟ್ಟನ್ನು ಸುತ್ತಿ ಮೇಲಂಗಿಯನ್ನು ತೊಡಿಸಿ ಕವಚವನ್ನು ಹಾಕಿಸಿ ಕವಚದ ಮೇಲಣ ವಿಚಿತ್ರವಾದ ನಡುಕಟ್ಟನ್ನು ಕಟ್ಟಿ ಅದರಿಂದ ಕವಚವನ್ನು ಅವನಿಗೆ ಬಂಧಿಸಿ",
			"8": "<sup>8</sup> ಚೀಲದ ಪದಕವನ್ನು ಅವನಿಗೆ ಬಿಗಿಸಿ ಅದರೊಳಗೆ ಊರೀಮ್ ತುಮ್ಮೀಮ್ ಎಂಬ ವಸ್ತುಗಳನ್ನು ಹಾಕಿ",
			"9": "<sup>9</sup> ಅವನ ತಲೆಗೆ ಮುಂಡಾಸವನ್ನು ಇಟ್ಟು ಅದರ ಮುಂಭಾಗದಲ್ಲಿ ಬಂಗಾರದ ಪಟ್ಟವನ್ನು ಕಟ್ಟಿದನು; ಅದೇ ಅವನಿಗೆ ಪರಿಶುದ್ಧ ಕಿರೀಟವು.",
			"10": "<sup>10</sup> ಅನಂತರ ಮೋಶೆ ಅಭಿಷೇಕತೈಲವನ್ನು ತೆಗೆದುಕೊಂಡು ದೇವದರ್ಶನದ ಗುಡಾರವನ್ನೂ ಅದರಲ್ಲಿದ್ದ ಎಲ್ಲವನ್ನೂ ಅಭಿಷೇಕಿಸಿ ಪ್ರತಿಷ್ಠಿಸಿದನು.",
			"11": "<sup>11</sup> ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಏಳು ಸಾರಿ ಆ ತೈಲವನ್ನು ಚಿಮಿಕಿಸಿ ವೇದಿಯನ್ನೂ ಅದರ ಎಲ್ಲಾ ಉಪಕರಣಗಳನ್ನೂ ಗಂಗಾಳವನ್ನೂ ಅದರ ಪೀಠವನ್ನೂ ಅಭಿಷೇಕಿಸಿ ಪ್ರತಿಷ್ಠಿಸಿದನು.",
			"12": "<sup>12</sup> ಅದರಲ್ಲಿ ಸ್ವಲ್ಪವನ್ನು ಆರೋನನ ತಲೆಯ ಮೇಲೆ ಹೊಯಿದು ಅವನನ್ನು ಅಭಿಷೇಕಿಸಿ ಪ್ರತಿಷ್ಠಿಸಿದನು.",
			"13": "<sup>13</sup> ಆಮೇಲೆ ಯೆಹೋವನು ಆಜ್ಞಾಪಿಸಿದಂತೆ ಮೋಶೆ ಆರೋನನ ಮಕ್ಕಳನ್ನು ಹತ್ತಿರಕ್ಕೆ ಕರೆದು ಅವರಿಗೆ ನಿಲುವಂಗಿಗಳನ್ನು ತೊಡಿಸಿ ನಡುಕಟ್ಟುಗಳನ್ನು ಸುತ್ತಿ ಮುಂಡಾಸಗಳನ್ನು ಇಟ್ಟನು.",
			"14": "<sup>14</sup> ಅನಂತರ ಅವನು ದೋಷಪರಿಹಾರಾರ್ಥವಾದ ಹೋರಿಯನ್ನು ತರಿಸಿದನು. ಆರೋನನೂ ಅವನ ಮಕ್ಕಳೂ ತಮ್ಮ ಕೈಗಳನ್ನು ಅದರ ತಲೆಯ ಮೇಲೆ ಇಟ್ಟರು.",
			"15": "<sup>15</sup> ಆ ಹೋರಿಯನ್ನು ವಧಿಸಿದ ಮೇಲೆ ಮೋಶೆ ಅದರ ರಕ್ತವನ್ನು ತನ್ನ ಬೆರಳಿನಿಂದ ಯಜ್ಞವೇದಿಯ ಕೊಂಬುಗಳಿಗೆ ಸುತ್ತಲೂ ಹಚ್ಚಿ ಯಜ್ಞವೇದಿಯನ್ನು ಶುದ್ಧಪಡಿಸಿ ಮಿಕ್ಕ ರಕ್ತವನ್ನು ಯಜ್ಞವೇದಿಯ ಬುಡದಲ್ಲಿ ಸುರಿದುಬಿಟ್ಟನು. ಹೀಗೆ ಯಜ್ಞವೇದಿಯ ನಿಮಿತ್ತ ದೋಷಪರಿಹಾರಕಾಚಾರವನ್ನು ನಡಿಸಿ ಅದನ್ನು ಪ್ರತಿಷ್ಠಿಸಿದನು.",
			"16": "<sup>16</sup> ಆ ಹೋರಿಯ ಕರುಳುಗಳ ಮೇಲಣ ಕೊಬ್ಬನ್ನೂ ಕಾಳಿಜದ ಹತ್ತಿರವಿರುವ ಕೊಬ್ಬನ್ನೂ ಎರಡು ಹುರುಳಿಕಾಯಿಗಳನ್ನೂ ಅವುಗಳ ಮೇಲಿರುವ ಕೊಬ್ಬನ್ನೂ ಮೋಶೆ ತೆಗೆದುಕೊಂಡು ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಹೋಮಮಾಡಿದನು.",
			"17": "<sup>17</sup> ಯೆಹೋವನು ಆಜ್ಞಾಪಿಸಿದಂತೆ ಅದರಲ್ಲಿ ಮಿಕ್ಕದ್ದನ್ನೆಲ್ಲಾ ಅಂದರೆ ಅದರ ಚರ್ಮವನ್ನೂ ಮಾಂಸವನ್ನೂ ಕಲ್ಮಷವನ್ನೂ ಪಾಳೆಯದ ಹೊರಗೆ ಬೆಂಕಿಯಿಂದ ಸುಡಿಸಿಬಿಟ್ಟನು.",
			"18": "<sup>18</sup> ತರುವಾಯ ಅವನು ಸರ್ವಾಂಗಹೋಮಕ್ಕಾಗಿ ಟಗರನ್ನು ತರಿಸಿದನು. ಆರೋನನೂ ಅವನ ಮಕ್ಕಳೂ ಅದರ ತಲೆಯ ಮೇಲೆ ತಮ್ಮ ಕೈಗಳನ್ನು ಇಟ್ಟರು.",
			"19": "<sup>19</sup> ಅದನ್ನು ವಧಿಸಿದ ಮೇಲೆ ಮೋಶೆ ಅದರ ರಕ್ತವನ್ನು ಯಜ್ಞವೇದಿಗೆ ಸುತ್ತಲೂ ಎರಚಿದನು.",
			"20": "<sup>20</sup> ಆ ಟಗರನ್ನು ತುಂಡುತುಂಡಾಗಿ ಕಡಿದ ಮೇಲೆ ಮೋಶೆ ಅದರ ತಲೆಯನ್ನೂ ಮಾಂಸಖಂಡಗಳನ್ನೂ ಕೊಬ್ಬನ್ನೂ ಹೋಮಮಾಡಿದನು.",
			"21": "<sup>21</sup> ಅದರ ಕರುಳುಗಳನ್ನೂ ಕಾಲುಗಳನ್ನೂ ನೀರಿನಿಂದ ತೊಳಿಸಿ ಮೋಶೆ ಆ ಟಗರನ್ನು ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಪೂರ್ತಿಯಾಗಿ ಹೋಮಮಾಡಿಬಿಟ್ಟನು. ಯೆಹೋವನು ಮೋಶೆಗೆ ಆಜ್ಞಾಪಿಸಿದಂತೆಯೇ ಅದು ಅಗ್ನಿಯ ಮೂಲಕ ಯೆಹೋವನಿಗೆ ಸುಗಂಧಕರವಾದ ಸರ್ವಾಂಗಹೋಮವಾಗಿತ್ತು.",
			"22": "<sup>22</sup> ಆಮೇಲೆ ಅವನು ಪಟ್ಟಾಭಿಷೇಕಕ್ಕಾಗಿ ಸಮರ್ಪಿಸಬೇಕಾದ ಎರಡನೆಯ ಟಗರನ್ನು ತರಿಸಿದನು. ಆರೋನನೂ ಅವನ ಮಕ್ಕಳೂ ಅದರ ತಲೆಯ ಮೇಲೆ ತಮ್ಮ ಕೈಗಳನ್ನಿಟ್ಟರು.",
			"23": "<sup>23</sup> ಅದನ್ನು ವಧಿಸಿದ ನಂತರ ಮೋಶೆ ಅದರ ರಕ್ತದಲ್ಲಿ ಸ್ವಲ್ಪ ತೆಗೆದುಕೊಂಡು ಆರೋನನ ಬಲಗಿವಿಯ ತುದಿಗೂ ಬಲಗೈಯ ಹೆಬ್ಬೆರಳಿಗೂ ಬಲಗಾಲಿನ ಹೆಬ್ಬೆಟ್ಟಿಗೂ ಹಚ್ಚಿದನು.",
			"24": "<sup>24</sup> ಆಗ ಮೋಶೆ ಆರೋನನ ಮಕ್ಕಳನ್ನು ಹತ್ತಿರಕ್ಕೆ ಕರೆದು ಆ ರಕ್ತದಲ್ಲಿ ಸ್ವಲ್ಪವನ್ನು ಅವರವರ ಬಲಗಿವಿಯ ತುದಿಗೂ ಬಲಗೈಯ ಹೆಬ್ಬೆರಳಿಗೂ ಬಲಗಾಲಿನ ಹೆಬ್ಬೆಟ್ಟಿಗೂ ಹಚ್ಚಿ ಉಳಿದ ರಕ್ತವನ್ನು ಯಜ್ಞವೇದಿಗೆ ಸುತ್ತಲೂ ಎರಚಿದನು.",
			"25": "<sup>25</sup> ಅವನು ಆ ಟಗರಿನ ಕೊಬ್ಬನ್ನೂ ಅಂದರೆ ಬಾಲದ ಕೊಬ್ಬು, ಕರುಳುಗಳ ಮೇಲಣ ಎಲ್ಲಾ ಕೊಬ್ಬು, ಕಾಳಿಜದ ಹತ್ತಿರವಿರುವ ಕೊಬ್ಬು, ಎರಡು ಹುರುಳಿಕಾಯಿಗಳು, ಅವುಗಳ ಮೇಲಿದ್ದ ಕೊಬ್ಬು ಇವುಗಳನ್ನೂ ಬಲದೊಡೆಯನ್ನೂ",
			"26": "<sup>26</sup> ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಪುಟ್ಟಿಯಲ್ಲಿ ಇಟ್ಟಿದ್ದ ಹುಳಿಯಿಲ್ಲದ ಭಕ್ಷ್ಯಗಳಲ್ಲಿ ಒಂದು ರೊಟ್ಟಿಯನ್ನೂ ಎಣ್ಣೆ ಮಿಶ್ರವಾದ ಒಂದು ಹೋಳಿಗೆಯನ್ನೂ ಒಂದು ಕಡುಬನ್ನೂ ತೆಗೆದುಕೊಂಡು ಈ ಭಕ್ಷ್ಯಗಳನ್ನು ಆ ಕೊಬ್ಬಿನ ಮತ್ತು ಬಲದೊಡೆಯ ಮೇಲೆ ಇಟ್ಟು",
			"27": "<sup>27</sup> ಎಲ್ಲವುಗಳನ್ನು ಆರೋನನ ಮತ್ತು ಅವನ ಮಕ್ಕಳ ಕೈಗಳಿಗೆ ಕೊಟ್ಟು ನೈವೇದ್ಯವೆಂದು ಸೂಚಿಸುವದಕ್ಕೆ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನಿವಾಳಿಸಿದನು.",
			"28": "<sup>28</sup> ಆಮೇಲೆ ಮೋಶೆ ಅವುಗಳನ್ನು ಅವರ ಕೈಯಿಂದ ತೆಗೆದುಕೊಂಡು ಯಜ್ಞವೇದಿಯಲ್ಲಿ ಸರ್ವಾಂಗಹೋಮದ್ರವ್ಯದ ಮೇಲಿಟ್ಟು ಹೋಮಮಾಡಿದನು. ಅದು ಅಗ್ನಿಯ ಮೂಲಕ ಯೆಹೋವನಿಗೆ ಸುಗಂಧಕರವಾದ ಪಟ್ಟಾಭಿಷೇಕಯಜ್ಞವಾಗಿತ್ತು.",
			"29": "<sup>29</sup> ಮೋಶೆ ಅದರ ಎದೆಯ ಭಾಗವನ್ನು ತೆಗೆದುಕೊಂಡು ನೈವೇದ್ಯವಾಗಿ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನಿವಾಳಿಸಿದನು. ಯೆಹೋವನು ಆಜ್ಞಾಪಿಸಿದಂತೆ ಆ ಪಟ್ಟಾಭಿಷೇಕಯಜ್ಞದ ಟಗರಿನಲ್ಲಿ ಆ ಎದೆಯ ಭಾಗವೇ ಮೋಶೆಗೆ ಸಲ್ಲಬೇಕಾದದ್ದು.",
			"30": "<sup>30</sup> ಆಗ ಮೋಶೆ ಅಭಿಷೇಕತೈಲದಲ್ಲಿಯೂ ಯಜ್ಞವೇದಿಯ ಮೇಲಿದ್ದ ರಕ್ತದಲ್ಲಿಯೂ ಸ್ವಲ್ಪ ತೆಗೆದುಕೊಂಡು ಆರೋನನ ಮತ್ತು ಅವನ ವಸ್ತ್ರಗಳ ಮೇಲೆಯೂ ಅವನ ಮಕ್ಕಳ ಮತ್ತು ಅವರ ವಸ್ತ್ರಗಳ ಮೇಲೆಯೂ ಚಿಮಿಕಿಸಿದನು. ಹೀಗೆ ಆರೋನನನ್ನೂ ಅವನ ವಸ್ತ್ರಗಳನ್ನೂ ಅವನ ಮಕ್ಕಳನ್ನೂ ಅವರ ವಸ್ತ್ರಗಳನ್ನೂ ಪ್ರತಿಷ್ಠಿಸಿದನು.",
			"31": "<sup>31</sup> ಮತ್ತು ಮೋಶೆ ಆರೋನನಿಗೂ ಅವನ ಮಕ್ಕಳಿಗೂ ಹೀಗಂದನು - ಮಾಂಸವನ್ನು ನೀವು ದೇವದರ್ಶನದ ಗುಡಾರದ ಬಾಗಲಿನ ಹತ್ತಿರ ಬೇಯಿಸಿ ಅದನ್ನೂ ಪಟ್ಟಾಭಿಷೇಕಕ್ಕಾಗಿ ಪುಟ್ಟಿಯಲ್ಲಿ ಇಟ್ಟಿರುವ ಭಕ್ಷ್ಯಗಳನ್ನೂ ಅಲ್ಲೇ ಊಟಮಾಡಬೇಕು.",
			"32": "<sup>32</sup> ಆರೋನನೂ ಅವನ ಮಕ್ಕಳೂ ಅದನ್ನು ತಿನ್ನಬೇಕೆಂಬದೇ ನನಗಾದ ಅಪ್ಪಣೆ. ಆ ಮಾಂಸದಲ್ಲಿಯೂ ಭಕ್ಷ್ಯಗಳಲ್ಲಿಯೂ ಮಿಕ್ಕದ್ದನ್ನು ನೀವು ಬೆಂಕಿಯಿಂದ ಸುಟ್ಟುಬಿಡಬೇಕು.",
			"33": "<sup>33</sup> ಇದಲ್ಲದೆ ಈ ನಿಮ್ಮ ಯಾಜಕೋದ್ಯೋಗದ ದೀಕ್ಷೆಯು ಪೂರೈಸುವದಕ್ಕೆ ಏಳು ದಿವಸ ಹಿಡಿಯುವದಾದದರಿಂದ ಆವರೆಗೂ ನೀವು ದೇವದರ್ಶನದ ಗುಡಾರದ ಬಾಗಲಿನ ಆಚೆಗೆ ಹೋಗಬಾರದು.",
			"34": "<sup>34</sup> ನಿಮ್ಮ ದೋಷಪರಿಹಾರಕ್ಕಾಗಿ ಈ ಹೊತ್ತು ಏನೇನು ನಡೆಯಿತೋ ಅದನ್ನು [ಏಳು ದಿನವೂ] ನಡಿಸಬೇಕೆಂದು ಯೆಹೋವನು ಆಜ್ಞಾಪಿಸಿದ್ದಾನೆ.",
			"35": "<sup>35</sup> ಆದದರಿಂದ ನೀವು ಯೆಹೋವನ ಆಜ್ಞೆಯ ಮೇರೆಗೆ ಏಳು ದಿನವೂ ಹಗಲಿರುಳು ದೇವದರ್ಶನದ ಗುಡಾರದ ಬಾಗಲಿನ ಹತ್ತಿರ ಇರಬೇಕು; ಇಲ್ಲದಿದ್ದರೆ ಸಾಯುವಿರಿ; ಹೀಗೆಯೇ ನನಗೆ ಅಪ್ಪಣೆಯಾಯಿತು.",
			"36": "<sup>36</sup> ಯೆಹೋವನು ಮೋಶೆಯ ಮೂಲಕವಾಗಿ ಆಜ್ಞಾಪಿಸಿದ್ದನ್ನೆಲ್ಲಾ ಆರೋನನೂ ಅವನ ಮಕ್ಕಳೂ ಮಾಡಿದರು."
		},
		"9": {
			"1": "<sup>1</sup> ಎಂಟನೆಯ ದಿನದಲ್ಲಿ ಮೋಶೆ ಆರೋನನನ್ನೂ ಅವನ ಮಕ್ಕಳನ್ನೂ ಇಸ್ರಾಯೇಲ್ಯರ ಹಿರಿಯರನ್ನೂ ಕರೆದು ಆರೋನನಿಗೆ ಹೀಗಂದನು -",
			"2": "<sup>2</sup> ನೀನು ದೋಷಪರಿಹಾರಕಯಜ್ಞಕ್ಕಾಗಿ ಪೂರ್ಣಾಂಗವಾದ ಹೋರಿಕರುವನ್ನೂ ಸರ್ವಾಂಗಹೋಮಕ್ಕಾಗಿ ಪೂರ್ಣಾಂಗವಾದ ಟಗರನ್ನೂ ನಿನಗೋಸ್ಕರ ತೆಗೆದುಕೊಂಡು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸಮರ್ಪಿಸಬೇಕು.",
			"3": "<sup>3</sup> ಮತ್ತು ನೀನು ಇಸ್ರಾಯೇಲ್ಯರ ಸಂಗಡ ಮಾತಾಡಿ ಅವರಿಗೆ - ಈ ಹೊತ್ತು ಯೆಹೋವನು ಪ್ರತ್ಯಕ್ಷನಾಗುತ್ತಾನಾದದರಿಂದ ನೀವು ಆತನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸಮರ್ಪಿಸುವದಕ್ಕೋಸ್ಕರ ದೋಷಪರಿಹಾರಕ್ಕಾಗಿ ಹೋತವನ್ನೂ ಸರ್ವಾಂಗಹೋಮಕ್ಕಾಗಿ ಒಂದು ವರುಷದ ಪೂರ್ಣಾಂಗವಾದ ಕರುವನ್ನೂ ಕುರಿಯನ್ನೂ",
			"4": "<sup>4</sup> ಸಮಾಧಾನಯಜ್ಞಕ್ಕಾಗಿ ಒಂದು ಹೋರಿಯನ್ನೂ ಟಗರನ್ನೂ ನೈವೇದ್ಯಕ್ಕಾಗಿ ಎಣ್ಣೆಮಿಶ್ರವಾದ ಪದಾರ್ಥವನ್ನೂ ತೆಗೆದುಕೊಂಡು ಬರಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸು ಅಂದನು.",
			"5": "<sup>5</sup> ಮೋಶೆ ಆಜ್ಞಾಪಿಸಿದ ಮೇರೆಗೆ ಅವರು ದೇವದರ್ಶನದ ಗುಡಾರದ ಹತ್ತಿರಕ್ಕೆ ತಂದರು. ಸಮೂಹದವರೆಲ್ಲರೂ ಹತ್ತಿರಕ್ಕೆ ಬಂದು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನಿಂತಿರಲಾಗಿ ಮೋಶೆ ಅವರಿಗೆ -",
			"6": "<sup>6</sup> ನೀವು ಮಾಡಬೇಕೆಂದು ಯೆಹೋವನು ಆಜ್ಞಾಪಿಸಿದ್ದು ಇದೇ; ಅದರಂತೆ ನಡೆದರೆ ಯೆಹೋವನ ಮಹಿಮೆ ನಿಮಗೆ ಪ್ರತ್ಯಕ್ಷವಾಗುವದು ಎಂದು ಹೇಳಿದನು.",
			"7": "<sup>7</sup> ಆಗ ಮೋಶೆ ಆರೋನನಿಗೆ - ಯಜ್ಞವೇದಿಯ ಹತ್ತಿರಕ್ಕೆ ಬಂದು ನೀನು ಮಾಡಬೇಕಾದ ದೋಷಪರಿಹಾರಕಯಜ್ಞವನ್ನೂ ಸರ್ವಾಂಗಹೋಮವನ್ನೂ ಸಮರ್ಪಿಸಿ ನಿನಗೋಸ್ಕರವೂ ಜನರಿಗೋಸ್ಕವೂ ದೋಷವನ್ನು ಪರಿಹರಿಸು; ಜನರು ತಂದದ್ದನ್ನು ಸಮರ್ಪಿಸಿ ಯೆಹೋವನ ಆಜ್ಞೆಯಂತೆ ಅವರಿಗೋಸ್ಕರ ದೋಷಪರಿಹಾರವನ್ನು ಮಾಡು ಎಂದು ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ಆರೋನನು ಯಜ್ಞವೇದಿಯ ಹತ್ತಿರಕ್ಕೆ ಹೋಗಿ ತನಗೋಸ್ಕರ ದೋಷಪರಿಹಾರಕಯಜ್ಞದ ಹೋರಿಯನ್ನು ವಧಿಸಿದನು.",
			"9": "<sup>9</sup> ಆರೋನನ ಮಕ್ಕಳು ಅದರ ರಕ್ತವನ್ನು ಅವನಿಗೆ ಒಪ್ಪಿಸಿದಾಗ ಅವನು ಅದರಲ್ಲಿ ತನ್ನ ಬೆರಳನ್ನು ಅದ್ದಿ ಯಜ್ಞವೇದಿಯ ಕೊಂಬುಗಳಿಗೆ ಹಚ್ಚಿ ಮಿಕ್ಕ ರಕ್ತವನ್ನು ಯಜ್ಞವೇದಿಯ ಬುಡದಲ್ಲಿ ಹೊಯಿದುಬಿಟ್ಟನು.",
			"10": "<sup>10</sup> ಆ ದೋಷಪರಿಹಾರಕಯಜ್ಞಪಶುವಿನ ಕೊಬ್ಬನ್ನೂ ಹುರುಳಿಕಾಯಿಗಳನ್ನೂ ಕಾಳಿಜದ ಹತ್ತಿರವಿರುವ ಕೊಬ್ಬನ್ನೂ ಯೆಹೋವನು ಮೋಶೆಯ ಮೂಲಕ ಆಜ್ಞಾಪಿಸಿದಂತೆ ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಹೋಮಮಾಡಿದನು.",
			"11": "<sup>11</sup> ಅದರ ಮಾಂಸವನ್ನೂ ಚರ್ಮವನ್ನೂ ಪಾಳೆಯದ ಹೊರಗೆ ಬೆಂಕಿಯಿಂದ ಸುಡಿಸಿಬಿಟ್ಟನು.",
			"12": "<sup>12</sup> ತರುವಾಯ ಸರ್ವಾಂಗಹೋಮಪಶುವನ್ನು ವಧಿಸಿದನು. ಆರೋನನ ಮಕ್ಕಳು ಅದರ ರಕ್ತವನ್ನು ಅವನಿಗೆ ತಂದು ಒಪ್ಪಿಸಿದಾಗ ಅವನು ಅದನ್ನು ಯಜ್ಞವೇದಿಗೆ ಸುತ್ತಲೂ ಎರಚಿದನು.",
			"13": "<sup>13</sup> ಅವರು ಆ ಪಶುವಿನ ಮಾಂಸ ಖಂಡಗಳನ್ನೂ ತಲೆಯನ್ನೂ ಒಂದೊಂದಾಗಿ ಒಪ್ಪಿಸಿದಾಗ ಅವನು ಅವುಗಳನ್ನು ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಹೋಮಮಾಡಿದನು.",
			"14": "<sup>14</sup> ಅದರ ಕರುಳುಗಳನ್ನೂ ಕಾಲುಗಳನ್ನೂ ತೊಳಿಸಿ ಯಜ್ಞವೇದಿಯಲ್ಲಿ ಸರ್ವಾಂಗಹೋಮದ್ರವ್ಯದ ಮೇಲಿಟ್ಟು ಹೋಮಮಾಡಿದನು.",
			"15": "<sup>15</sup> ತರುವಾಯ ಜನಸಮೂಹವು ಸಮರ್ಪಿಸುವ ಪಶುಗಳನ್ನು ಆರೋನನು ತರಿಸಿ ಅವುಗಳಲ್ಲಿ ದೋಷಪರಿಹಾರಕವಾದ ಹೋತವನ್ನು ಮೊದಲನೆಯ ಪಶುವಿನಂತೆ ವಧಿಸಿ ಅವರಿಗೋಸ್ಕರ ದೋಷಪರಿಹಾರಕ್ಕಾಗಿ ಸಮರ್ಪಿಸಿದನು.",
			"16": "<sup>16</sup> ಸರ್ವಾಂಗಹೋಮಪಶುವನ್ನೂ ಯಥಾವಿಧಿಯಾಗಿ ಸಮರ್ಪಿಸಿದನು.",
			"17": "<sup>17</sup> ಅವರು ತಂದ ನೈವೇದ್ಯದ್ರವ್ಯಗಳಲ್ಲಿ ಒಂದು ಹಿಡಿಯನ್ನು ತೆಗೆದುಕೊಂಡು ಹೊತ್ತಾರೆಯ ಸರ್ವಾಂಗ ಹೋಮವನ್ನಲ್ಲದೆ ಅದನ್ನೂ ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಹೋಮಮಾಡಿದನು.",
			"18": "<sup>18</sup> ಬಳಿಕ ಜನರಿಗೋಸ್ಕರ ಸಮಾಧಾನಯಜ್ಞಕ್ಕಾಗಿ ನೇಮಕವಾದ ಹೋರಿಯನ್ನೂ ಟಗರನ್ನೂ ವಧಿಸಿದನು. ಆರೋನನ ಮಕ್ಕಳು ಅವುಗಳ ರಕ್ತವನ್ನು ಒಪ್ಪಿಸಲಾಗಿ ಅವನು ಅದನ್ನು ಯಜ್ಞವೇದಿಗೆ ಸುತ್ತಲೂ ಎರಚಿದನು.",
			"19": "<sup>19</sup> ಅವರು ಹೋರಿಯ ಕೊಬ್ಬನ್ನೂ ಟಗರಿನ ಬಾಲದ ಕೊಬ್ಬನ್ನೂ ವಪೆಯನ್ನೂ ಹುರುಳಿಕಾಯಿಗಳನ್ನೂ ಕಾಳಿಜದ ಹತ್ತಿರವಿರುವ ಕೊಬ್ಬನ್ನೂ",
			"20": "<sup>20</sup> ಎದೆಯ ಭಾಗಗಳ ಮೇಲೆ ಇಟ್ಟು ಒಪ್ಪಿಸಲಾಗಿ ಅವನು ಆ ಕೊಬ್ಬನ್ನು ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಹೋಮಮಾಡಿದನು.",
			"21": "<sup>21</sup> ಮೋಶೆಯು ಆಜ್ಞಾಪಿಸಿದಂತೆ ಆರೋನನು ಅವುಗಳ ಎದೆಯ ಭಾಗಗಳನ್ನೂ ಬಲದೊಡೆಗಳನ್ನೂ ನೈವೇದ್ಯವಾಗಿ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನಿವಾಳಿಸಿದನು.",
			"22": "<sup>22</sup> ಆರೋನನು ಆ ದೋಷಪರಿಹಾರಕ ಯಜ್ಞವನ್ನೂ ಸರ್ವಾಂಗಹೋಮವನ್ನೂ ಸಮಾಧಾನಯಜ್ಞಗಳನ್ನೂ ಸಮರ್ಪಿಸಿದನಂತರ ತನ್ನ ಕೈಗಳನ್ನು ಜನರ ಕಡೆಗೆ ಎತ್ತಿ ಅವರನ್ನು ಆಶೀರ್ವದಿಸಿ ಇಳಿದು ಬಂದನು.",
			"23": "<sup>23</sup> ತರುವಾಯ ಮೋಶೆಯೂ ಆರೋನನೂ ದೇವದರ್ಶನದ ಗುಡಾರದೊಳಗೆ ಹೋದರು. ಅವರು ಅಲ್ಲಿಂದ ಹೊರಗೆ ಬಂದಾಗ ಜನರನ್ನು ಆಶೀರ್ವದಿಸಿದರು. ಆಗ ಯೆಹೋವನ ಮಹಿಮೆಯು ಜನರೆಲ್ಲರಿಗೆ ಪ್ರತ್ಯಕ್ಷವಾಯಿತು.",
			"24": "<sup>24</sup> ಯೆಹೋವನ ಸನ್ನಿಧಿಯಿಂದ ಬೆಂಕಿಯು ಹೊರಟು ಯಜ್ಞವೇದಿಯ ಮೇಲಿದ್ದ ಸರ್ವಾಂಗಹೋಮದ್ರವ್ಯವನ್ನೂ ಕೊಬ್ಬನ್ನೂ ದಹಿಸಿಬಿಟ್ಟಿತು. ಜನರೆಲ್ಲರೂ ಅದನ್ನು ಕಂಡು ಉತ್ಸಾಹದಿಂದ ಆರ್ಭಟಿಸಿ ಅಡ್ಡಬಿದ್ದು ನಮಸ್ಕರಿಸಿದರು."
		},
		"10": {
			"1": "<sup>1</sup> ಆರೋನನ ಮಕ್ಕಳಲ್ಲಿ ನಾದಾಬ್ ಅಬೀಹು ಎಂಬಿಬ್ಬರು ತಮ್ಮತಮ್ಮ ಧೂಪಾರತಿಗಳಲ್ಲಿ ಯೆಹೋವನು ಆಜ್ಞಾಪಿಸದೆ ಇದ್ದ ಬೇರೆ ಬೆಂಕಿಯನ್ನಿಟ್ಟು ಅದರ ಮೇಲೆ ಧೂಪದ್ರವ್ಯವನ್ನು ಹಾಕಿ",
			"2": "<sup>2</sup> ಆತನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸಮರ್ಪಿಸಲಾಗಿ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಿಂದ ಬೆಂಕಿ ಹೊರಟು ಅವರನ್ನು ನಾಶಮಾಡಿತು; ಅವರು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿಯೇ ಸತ್ತರು.",
			"3": "<sup>3</sup> ಆಗ ಮೋಶೆ ಆರೋನನಿಗೆ - ಯೆಹೋವನು ಹೇಳಿದ ಮಾತಿಗೆ ಇದೇ ದೃಷ್ಟಾಂತ; ಆ ಮಾತು ಏನಂದರೆ - ನಾನು ಪರಿಶುದ್ಧನೆಂಬದನ್ನು ನನ್ನ ಬಳಿಯಲ್ಲಿರುವವರ ಮೂಲಕವಾಗಿಯೇ ತೋರ್ಪಡಿಸುವೆನು, ಜನರೆಲ್ಲರಿಗೆ ತಿಳಿಯುವಂತೆ ನನ್ನ ಘನತೆಯನ್ನು ಸ್ಥಾಪಿಸುವೆನು ಎಂಬದೇ ಎಂದು ಹೇಳಿದನು. ಅದಕ್ಕೆ ಆರೋನನು ಮೌನವಾಗಿದ್ದನು.",
			"4": "<sup>4</sup> ಮೋಶೆ ಆರೋನರ ಚಿಕ್ಕಪ್ಪನಾದ ಉಜ್ಜಿಯೇಲನ ಮಕ್ಕಳಾದ ಮೀಶಾಯೇಲನನ್ನೂ ಎಲ್ಸಾಫಾನನನ್ನೂ ಕರಿಸಿ - ನೀವು ಸಮೀಪಕ್ಕೆ ಬಂದು ನಿಮ್ಮ ಅಣ್ಣಂದಿರ ಶವಗಳನ್ನು ದೇವಮಂದಿರದ ಎದುರಿನಿಂದ ಪಾಳೆಯದ ಹೊರಕ್ಕೆ ಹೊತ್ತುಕೊಂಡು ಹೋಗಬೇಕು ಅಂದನು.",
			"5": "<sup>5</sup> ಮೋಶೆಯ ಅಪ್ಪಣೆಯ ಮೇರೆಗೆ ಅವರು ಸಮೀಪಕ್ಕೆ ಬಂದು ಸತ್ತವರು ತೊಟ್ಟುಕೊಂಡಿದ್ದ ನಿಲುವಂಗಿಗಳನ್ನು ತೆಗೆದು ಬಿಡದೆ ಅವರ ಶವಗಳನ್ನು ಎತ್ತಿ ಪಾಳೆಯದ ಹೊರಕ್ಕೆ ಹೊತ್ತುಕೊಂಡು ಹೋದರು.",
			"6": "<sup>6</sup> ಮೋಶೆ ಆರೋನನಿಗೂ ಅವನ ಮಕ್ಕಳಾದ ಎಲ್ಲಾಜಾರ್ ಈತಾಮಾರ್ ಎಂಬವರಿಗೂ - ನೀವು ತಲೆಯನ್ನು ಕೆದರಿಕೊಳ್ಳಬಾರದು; ಬಟ್ಟೆಗಳನ್ನು ಹರಿದುಕೊಳ್ಳಬಾರದು. ಹಾಗೆ ಮಾಡಿದರೆ ನೀವೂ ಸತ್ತೀರಿ; ಅದಲ್ಲದೆ ಯೆಹೋವನಿಗೆ ಜನಸಮೂಹದವರೆಲ್ಲರ ಮೇಲೆ ಸಿಟ್ಟು ಉಂಟಾದೀತು. ಯೆಹೋವನು ಹೊತ್ತಿಸಿದ ಈ ಬೆಂಕಿಯ ದೆಸೆಯಿಂದ ನಿಮ್ಮ ಸಹೋದರರಾಗಿರುವ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರ ಮನೆತನದವರೇ ದುಃಖಿಸಲಿ.",
			"7": "<sup>7</sup> ಯೆಹೋವನ ಅಭಿಷೇಕ ತೈಲವು ನಿಮ್ಮ ಮೇಲಿರುವದರಿಂದ ನೀವು ದೇವದರ್ಶನದ ಗುಡಾರದ ಬಾಗಲನ್ನು ಬಿಟ್ಟು ಹೋಗಬಾರದು; ಹೋದರೆ ಸತ್ತೀರಿ ಎಂದು ಹೇಳಿದನು. ಮೋಶೆಯ ಮಾತಿನಂತೆಯೇ ಅವರು ನಡಕೊಂಡರು.",
			"8": "<sup>8</sup> ಯೆಹೋವನು ಆರೋನನಿಗೆ - ನೀನೂ ನಿನ್ನ ಮಕ್ಕಳೂ ದ್ರಾಕ್ಷಾರಸವನ್ನಾಗಲಿ ಮದ್ಯವನ್ನಾಗಲಿ ಕುಡಿದು ದೇವದರ್ಶನದ ಗುಡಾರದೊಳಗೆ ಬರಬಾರದು; ಹಾಗೆ ಬಂದರೆ ಸತ್ತೀರಿ. ಇದು ನಿನಗೂ ನಿನ್ನ ಸಂತತಿಯವರೆಲ್ಲರಿಗೂ ಶಾಶ್ವತವಿಧಿ.",
			"10": "<sup>10</sup> ಅದಲ್ಲದೆ ದೇವರಿಗೆ ಮೀಸಲಾದ ವಸ್ತುಗಳನ್ನೂ ಅಲ್ಲದ ವಸ್ತುಗಳನ್ನೂ ಶುದ್ಧವಾದವುಗಳನ್ನೂ ಅಶುದ್ಧವಾದವುಗಳನ್ನೂ ವಿವೇಚಿಸುವದೂ",
			"11": "<sup>11</sup> ಯೆಹೋವನು ಮೋಶೆಯ ಮೂಲಕ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಮಾಡಿದ ಎಲ್ಲಾ ಆಜ್ಞೆಗಳನ್ನು ಜನರಿಗೆ ಬೋಧಿಸುವದೂ ನಿಮ್ಮ ಕರ್ತವ್ಯವಾಗಿದೆ ಎಂದು ಹೇಳಿದನು.",
			"12": "<sup>12</sup> ಮೋಶೆ ಆರೋನನಿಗೂ ಅವನ ಉಳಿದ ಮಕ್ಕಳಾದ ಎಲ್ಲಾಜಾರ್ ಈತಾಮಾರ್ ಎಂಬವರಿಗೂ - ಯೆಹೋವನಿಗೆ ಅಗ್ನಿಯ ಮೂಲಕ ಸಮರ್ಪಿತವಾದ ದ್ರವ್ಯಗಳಲ್ಲಿ ಮಿಕ್ಕಿರುವ ನೈವೇದ್ಯದ್ರವ್ಯವನ್ನು ತೆಗೆದುಕೊಂಡು ಅದರಿಂದ ಹುಳಿಯಿಲ್ಲದ ರೊಟ್ಟಿಗಳನ್ನು ಮಾಡಿಸಿ ಯಜ್ಞವೇದಿಯ ಬಳಿಯಲ್ಲಿ ಊಟಮಾಡಬೇಕು.",
			"13": "<sup>13</sup> ಅದು ಮಹಾಪರಿಶುದ್ಧವಾಗಿರುವದರಿಂದ ಪವಿತ್ರಸ್ಥಳದೊಳಗೇ ಅದನ್ನು ಊಟಮಾಡಬೇಕು. ಜನರು ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಿಸುವ ಹೋಮದ್ರವ್ಯಗಳಲ್ಲಿ ಅದು ನಿನಗೂ ನಿನ್ನ ಮಕ್ಕಳಿಗೂ ಸಲ್ಲತಕ್ಕದ್ದೆಂದು ಅಪ್ಪಣೆಯಾಗಿದೆ.",
			"14": "<sup>14</sup> ನೀವು ನೈವೇದ್ಯ ಮಾಡಿದ ಎದೆಯ ಭಾಗವನ್ನೂ ಯಾಜಕರಿಗೋಸ್ಕರ ಪ್ರತ್ಯೇಕಿಸಿದ ತೊಡೆಯನ್ನೂ ಯಾವದಾದರೂ ಒಂದು ಶುದ್ಧಸ್ಥಳದಲ್ಲಿ ಊಟಮಾಡಬಹುದು. ನೀನೂ ನಿನ್ನ ಗಂಡುಮಕ್ಕಳೂ ಹೆಣ್ಣು ಮಕ್ಕಳೂ ಅದನ್ನು ತಿನ್ನಬಹುದು. ಯಾಕಂದರೆ ಇಸ್ರಾಯೇಲ್ಯರು ಸಮರ್ಪಿಸುವ ಸಮಾಧಾನಯಜ್ಞದ್ರವ್ಯಗಳಲ್ಲಿ ಇವೇ ನಿನಗೂ ನಿನ್ನ ಮಕ್ಕಳಿಗೂ ಸಲ್ಲಬೇಕೆಂದು ನೇಮಕವಾಗಿದೆ.",
			"15": "<sup>15</sup> ಜನರು ಹೋಮಕ್ಕಾಗಿ ಕೊಬ್ಬನ್ನು ತಂದು ಸಮರ್ಪಿಸುವಾಗೆಲ್ಲಾ ಯಾಜಕರಿಗೋಸ್ಕರ ಪ್ರತ್ಯೇಕಿಸಬೇಕಾದ ಆ ತೊಡೆಯನ್ನೂ ನೈವೇದ್ಯವಾಗಿ ನಿವಾಳಿಸಬೇಕಾದ ಆ ಎದೆಯ ಭಾಗವನ್ನೂ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ತರಬೇಕು. ಯೆಹೋವನು ಆಜ್ಞಾಪಿಸಿದಂತೆ ಅವು ನಿನಗೂ ನಿನ್ನ ವಂಶದವರಿಗೂ ಸಲ್ಲತಕ್ಕದ್ದು; ಇದು ಶಾಶ್ವತನಿಯಮ ಎಂದು ಹೇಳಿದನು.",
			"16": "<sup>16</sup> [ಜನರೆಲ್ಲರಿಗೋಸ್ಕರ] ದೋಷಪರಿಹಾರಕ್ಕಾಗಿ ಸಮರ್ಪಿತವಾದ ಹೋತನ ವಿಷಯದಲ್ಲಿ ಅದು ಏನಾಯಿತೆಂದು ಮೋಶೆ ವಿಚಾರಿಸಲಾಗಿ ಅದನ್ನು ಸುಟ್ಟುಬಿಟ್ಟರೆಂದು ತಿಳಿಯಬಂತು. ಇದನ್ನು ಕೇಳಿದಾಗ ಅವನು ಆರೋನನ ಉಳಿದ ಮಕ್ಕಳಾದ ಎಲ್ಲಾಜಾರ್ ಈತಾಮಾರ್ ಎಂಬವರ ಮೇಲೆ ಸಿಟ್ಟುಗೊಂಡು -",
			"17": "<sup>17</sup> ಆ ದೋಷಪರಿಹಾರಕ ಯಜ್ಞದ್ರವ್ಯವು ಮಹಾಪರಿಶುದ್ಧವಾದದ್ದಲ್ಲವೇ; ನೀವು ಜನಸಮೂಹದ ಪಾಪಗಳನ್ನು ಪರಿಹಾರಮಾಡುವಂತೆಯೂ ಅವರಿಗೋಸ್ಕರ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ದೋಷಪರಿಹಾರವನ್ನು ಮಾಡುವಂತೆಯೂ ಅದು ನಿಮ್ಮ ಭಾಗವಾಗಿ ನೇಮಕವಾಗಿದೆಯಲ್ಲಾ. ನೀವು ಯಾಕೆ ಅದನ್ನು ಪವಿತ್ರಸ್ಥಳದೊಳಗೆ ಊಟಮಾಡಲಿಲ್ಲ?",
			"18": "<sup>18</sup> ಅದರ ರಕ್ತವು ಪವಿತ್ರಸ್ಥಾನದೊಳಕ್ಕೆ ತರಬೇಕಾದದ್ದಲ್ಲವಲ್ಲಾ; ಆದಕಾರಣ ನಾನು ಆಜ್ಞಾಪಿಸಿದಂತೆ ನೀವು ದೇವಸ್ಥಾನದ ಪ್ರಾಕಾರದೊಳಗೆ ಅದನ್ನು ತಿನ್ನಬೇಕಾಗಿತ್ತು ಅಂದನು.",
			"19": "<sup>19</sup> ಅದಕ್ಕೆ ಆರೋನನು ಮೋಶೆಗೆ - ಇವರು ಈ ಹೊತ್ತು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ತಮಗೋಸ್ಕರ ದೋಷಪರಿಹಾರಕಯಜ್ಞವನ್ನೂ ಸರ್ವಾಂಗಹೋಮವನ್ನೂ ಸಮರ್ಪಿಸಿದ್ದರೂ ಈ ಆಪತ್ತು ನನಗೆ ಸಂಭವಿಸಿತು; ಹೀಗಿರುವಾಗ ನಾನು ದೋಷಪರಿಹಾರಕ ಯಜ್ಞದ್ರವ್ಯವನ್ನು ಈ ಹೊತ್ತು ಊಟಮಾಡಿದ್ದರೆ ಅದು ಯೆಹೋವನಿಗೆ ಒಳ್ಳೇದಾಗಿ ತೋಚುತ್ತಿತ್ತೋ ಎಂದು ಉತ್ತರಕೊಡಲಾಗಿ",
			"20": "<sup>20</sup> ಮೋಶೆ ಆ ಮಾತನ್ನು ಕೇಳಿ ಒಪ್ಪಿಕೊಂಡನು."
		},
		"11": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಮೋಶೆ ಆರೋನರ ಸಂಗಡ ಮಾತಾಡಿ ಆಜ್ಞಾಪಿಸಿದ್ದೇನೆಂದರೆ -",
			"2": "<sup>2</sup> ನೀವು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಹೀಗೆ ಹೇಳಬೇಕು- ಭೂಮಿಯ ಮೇಲಿರುವ ಚತುಷ್ಪಾದ ಪ್ರಾಣಿಗಳಲ್ಲಿ ಇವುಗಳ ಮಾಂಸವನ್ನು ನೀವು ತಿನ್ನಬಹುದು.",
			"3": "<sup>3</sup> ಯಾವವಂದರೆ - ಯಾವ ಪ್ರಾಣಿಯ ಕಾಲ್ಗೊರಸು ಸೀಳಿದೆಯೋ ಅದು ಮೆಲಕು ಹಾಕುವಂಥದಾದರೆ ಅದರ ಮಾಂಸವನ್ನು ತಿನ್ನಬಹುದು.",
			"4": "<sup>4</sup> ಆದರೆ ಯಾವ ಪ್ರಾಣಿಯು ಮೆಲಕು ಹಾಕಿದರೂ ಗೊರಸು ಸೀಳಿರುವದಿಲ್ಲವೋ ಮತ್ತು ಯಾವ ಪ್ರಾಣಿಯು ಗೊರಸುಸೀಳಿದ್ದರೂ ಮೆಲಕು ಹಾಕುವದಿಲ್ಲವೋ ಅವುಗಳ ಮಾಂಸವನ್ನು ನೀವು ತಿನ್ನಬಾರದು. ದೃಷ್ಟಾಂತ, ಒಂಟೆಯು ಮೆಲಕುಹಾಕುವಂಥದು ಸರಿ; ಆದರೂ ಅದಕ್ಕೆ ಸೀಳುಗೊರಸು ಇಲ್ಲವಾದದರಿಂದ ನೀವು ಅದರ ಮಾಂಸವನ್ನು ಅಶುದ್ಧವೆಂದೆಣಿಸಬೇಕು.",
			"5": "<sup>5</sup> ಬೆಟ್ಟದ ಮೊಲವೂ ಮೆಲಕು ಹಾಕುವಂಥದು; ಆದರೂ ಸೀಳುಗೊರಸು ಇಲ್ಲವಾದದರಿಂದ ಅದು ನಿಮಗೆ ಅಶುದ್ಧ.",
			"6": "<sup>6</sup> ಮೊಲವೂ ಮೆಲಕುಹಾಕುವಂಥದು; ಆದರೂ ಸೀಳುಗೊರಸು ಇಲ್ಲವಾದ ಕಾರಣ ಅದೂ ನಿಮಗೆ ಅಶುದ್ಧ.",
			"7": "<sup>7</sup> ಹಂದಿಯ ಗೊರಸು ಸೀಳಿಯದೆ; ಆದರೂ ಅದು ಮೆಲಕು ಹಾಕುವದಿಲ್ಲವಾದದರಿಂದ ಅದು ನಿಮಗೆ ಅಶುದ್ಧ. ಇವುಗಳ ಮಾಂಸವನ್ನು ನೀವು ತಿನ್ನಬಾರದು;",
			"8": "<sup>8</sup> ಇವುಗಳ ಹೆಣವನ್ನು ಮುಟ್ಟಬಾರದು; ಇವುಗಳನ್ನು ಅಶುದ್ಧವೆಂದೆಣಿಸಬೇಕು.",
			"9": "<sup>9</sup> ಜಲಜಂತುಗಳಲ್ಲಿ ನೀವು ತಿನ್ನಬಹುದಾದವುಗಳು ಯಾವವಂದರೆ - ಸಮುದ್ರದಲ್ಲಿಯಾಗಲಿ ನದಿಯಲ್ಲಿಯಾಗಲಿ ಬೇರೆ ಜಲಾಶಯದಲ್ಲಿಯಾಗಲಿ ಯಾವ ಜಾತಿಯ ಪ್ರಾಣಿಗೆ ರೆಕ್ಕೆ ಇದ್ದು ಮೈಯೆಲ್ಲಾ ಪರೆಪರೆಯಾಗಿರುವದೋ ಅದರ ಮಾಂಸವನ್ನು ತಿನ್ನಬಹುದು.",
			"10": "<sup>10</sup> ಆದರೆ ಸಮುದ್ರದಲ್ಲಿದ್ದರೂ ನದಿಯಲ್ಲಿದ್ದರೂ ಜಲಚರಗಳಾದ ಸಕಲವಿಧವಾದ ಜೀವಜಂತುಗಳಲ್ಲಿ ಯಾವ ಜಾತಿಗೆ ರೆಕ್ಕೆಯೂ ಪರೆಪರೆಯಾದ ಮೈಯೂ ಇರುವದಿಲ್ಲವೋ ಅದು ನಿಮಗೆ ನಿಷಿದ್ಧವಾದದ್ದು.",
			"11": "<sup>11</sup> ಅಂಥವು ಸಂಪೂರ್ಣವಾಗಿ ನಿಷಿದ್ಧವಾಗಿರುವದರಿಂದ ಅವುಗಳ ಮಾಂಸವನ್ನು ನೀವು ತಿನ್ನಬಾರದು; ಅವುಗಳ ಹೆಣಗಳು ನಿಮಗೆ ಹೇಯವಾಗಿರಬೇಕು.",
			"12": "<sup>12</sup> ಜಲಜಂತುಗಳಲ್ಲಿ ಯಾವದಕ್ಕೆ ರೆಕ್ಕೆಗಳೂ ಪರೆಪರೆಯಾದ ಮೈಯೂ ಇಲ್ಲವೋ ಅವು ನಿಮಗೆ ಹೇಯವಾಗಿರಬೇಕು.",
			"13": "<sup>13</sup> ಪಕ್ಷಿಗಳಲ್ಲಿ ಇವುಗಳು ನಿಮಗೆ ನಿಷಿದ್ಧವು; ಇವುಗಳ ಮಾಂಸವನ್ನು ತಿನ್ನಬಾರದು; ಇವು ಹೇಯವಾಗಿರಬೇಕು, ಯಾವವಂದರೆ - ಗರುಡ, ಬೆಟ್ಟದ ಹದ್ದು,",
			"14": "<sup>14</sup> ಕ್ರೌಂಚ, ಹದ್ದು, ಸಕಲವಿಧವಾದ ಗಿಡುಗ, ಸಕಲವಿಧವಾದ ಕಾಗೆ,",
			"16": "<sup>16</sup> ಉಷ್ಟ್ರಪಕ್ಷಿ, ಉಲೂಕ, ಕಡಲ್ಹಕ್ಕಿ,",
			"17": "<sup>17</sup> ಸಕಲವಿಧವಾದ ಡೇಗೆ, ಗೂಬೆ, ಹೆಗ್ಗೂಬೆ, ಕರೇಟು,",
			"18": "<sup>18</sup> ಚೀಲಮೂಗಿ, ರಣಹದ್ದು, ನೀರುಕಾಗೆ, ಕೊಕ್ಕರೆ, ಸಕಲವಿಧವಾದ ಬಕ, ಹೆಡೆಹಕ್ಕಿ, ಕಣ್ಣ ಕಪಡಿ.",
			"20": "<sup>20</sup> ರೆಕ್ಕೆಯುಳ್ಳವುಗಳಾಗಿ ಕಾಲುಗಳಿಂದ ಹರಿದಾಡುವ ಕ್ರಿಮಿಕೀಟಗಳೆಲ್ಲವೂ ನಿಮಗೆ ಹೇಯವಾಗಿರಬೇಕು.",
			"21": "<sup>21</sup> ಆದರೆ ಕಾಲುಳ್ಳ ಯಾವ ಕ್ರಿಮಿಕೀಟಗಳಿಗೆ ನೆಲದ ಮೇಲೆ ಹಾರುವದಕ್ಕೋಸ್ಕರ ಮುದುರಿಕೊಂಡಿರುವ ತೊಡೆಗಳು ಇರುತ್ತವೆಯೋ ಅವುಗಳನ್ನು ನೀವು ತಿನ್ನಬಹುದು.",
			"22": "<sup>22</sup> ಸಕಲವಿಧವಾದ ಮಿಡತೆಗಳನ್ನೂ ಬೋಳಮಿಡತೆಗಳನ್ನೂ ಜಿಟ್ಟಿಮಿಡತೆಗಳನ್ನೂ ಸಣ್ಣಮಿಡತೆಗಳನ್ನೂ ಇವುಗಳನ್ನೆಲ್ಲಾ ತಿನ್ನಬಹುದು.",
			"23": "<sup>23</sup> ರೆಕ್ಕೆಯುಳ್ಳವುಗಳಾಗಿ ಕಾಲುಗಳಿಂದ ಹರಿದಾಡುವ ಸಕಲವಿಧವಾದ ಕ್ರಿಮಿಕೀಟಗಳು ನಿಮಗೆ ನಿಷಿದ್ಧವು.",
			"24": "<sup>24</sup> ಅದಲ್ಲದೆ ಮುಂದೆ ಹೇಳಿರುವ ಜಂತುಗಳಿಂದ ನಿಮಗೆ ಅಪವಿತ್ರತೆಯುಂಟಾಗುತ್ತದೆ. ಅವುಗಳ ಹೆಣ ಯಾವನಿಗೆ ಸೋಂಕುವದೋ ಅವನು ಆ ದಿನದ ಸಾಯಂಕಾಲದವರೆಗೂ ಅಶುದ್ಧನಾಗಿರುವನು.",
			"25": "<sup>25</sup> ಅವುಗಳ ಹೆಣವನ್ನು ಎತ್ತಿದವನು ತನ್ನ ಬಟ್ಟೆಗಳನ್ನು ಒಗೆದುಕೊಳ್ಳಬೇಕು. ಮತ್ತು ಸಾಯಂಕಾಲದವರೆಗೂ ಅಶುದ್ಧನಾಗಿರುವನು.",
			"26": "<sup>26</sup> ಅವು ಯಾವವಂದರೆ, ಯಾವ ಪಶುವು ಗೊರಸು ಸ್ವಲ್ಪ ಸೀಳಿದ್ದರೂ ಇಗ್ಗೊರಸಾಗಿಲ್ಲವೋ ಮತ್ತು ಮೆಲಕುಹಾಕುವದಿಲ್ಲವೋ ಅದು ನಿಮಗೆ ಅಶುದ್ಧ. ಅವುಗಳು ಯಾವನಿಗೆ ಸೋಂಕುವವೋ ಅವನು ಅಶುದ್ಧನಾಗುವನು.",
			"27": "<sup>27</sup> ಚತುಷ್ಪಾದ ಪ್ರಾಣಿಗಳಲ್ಲಿ ಅಂಗಾಲುಗಳಿಂದ ನಡೆಯುವವುಗಳೆಲ್ಲಾ ನಿಮಗೆ ಅಶುದ್ಧವಾಗಿರುವವು; ಅವುಗಳ ಹೆಣ ಯಾವನಿಗೆ ಸೋಂಕುವದೋ ಅವನು ಆ ದಿನದ ಸಾಯಂಕಾಲದವರೆಗೂ ಅಶುದ್ಧನಾಗಿರುವನು.",
			"28": "<sup>28</sup> ಅವುಗಳ ಹೆಣವನ್ನು ಎತ್ತಿದವನು ತನ್ನ ಬಟ್ಟೆಗಳನ್ನು ಒಗೆದುಕೊಳ್ಳಬೇಕು ಮತ್ತು ಆ ಸಾಯಂಕಾಲದವರೆಗೂ ಅಶುದ್ಧನಾಗಿರುವನು. ಅವು ನಿಮಗೆ ಅಶುದ್ಧ.",
			"29": "<sup>29</sup> ನೆಲದ ಮೇಲೆ ಸಂಚರಿಸುವ ಸಣ್ಣ ಜಂತುಗಳಲ್ಲಿ ಇವು ನಿಮಗೆ ಅಶುದ್ಧವಾಗಿರಬೇಕು. ಯಾವವಂದರೆ - ಮುಂಗುಸಿ, ಇಲಿ, ಸಕಲವಿಧವಾದ ಉಡ.",
			"30": "<sup>30</sup> ಹಾವರಾಣಿ, ಊಸರವಳ್ಳಿ ಹಲ್ಲಿ, ಬಸವನಹುಳ, ಚಿಟ್ಟಿಲಿ ಇವೇ.",
			"31": "<sup>31</sup> ನೆಲದ ಮೇಲೆ ಸಂಚರಿಸುವ ಈ ಅಶುದ್ಧವಾದ ಸಣ್ಣ ಜಂತುಗಳ ಹೆಣವು ಯಾರಿಗೆ ಸೋಂಕುವದೋ ಅವರು ಆ ಸಾಯಂಕಾಲದವರೆಗೂ ಅಶುದ್ಧರಾಗಿರುವರು.",
			"32": "<sup>32</sup> ಇವುಗಳ ಹೆಣವು ಯಾವ ವಸ್ತುವಿನ ಮೇಲೆ ಬೀಳುವದೋ ಆ ವಸ್ತುವು ಅಶುದ್ಧವಾಗಿರುವದು. ಅದು ಮರದ ವಸ್ತುವಾಗಲಿ ಬಟ್ಟೆಯಾಗಲಿ ಚರ್ಮವಾಗಲಿ ಗೋಣಿಯಾಗಲಿ ಅದು ಎಂಥದಾದರೂ ಯಾವ ಕೆಲಸಕ್ಕೆ ಉಪಯೋಗವಾಗಿದ್ದರೂ ಅದನ್ನು ನೀರಿನಲ್ಲಿ ನೆನಸಬೇಕು; ಅದು ಸಾಯಂಕಾಲದವರೆಗೂ ಅಶುದ್ಧವಾಗಿರುವದು; ತರುವಾಯ ಶುದ್ಧಿಯಾಗುವದು.",
			"33": "<sup>33</sup> ಆ ಜಂತುಗಳ ಹೆಣ ಮಣ್ಣಿನ ಪಾತ್ರೆಯಲ್ಲಿ ಬಿದ್ದರೆ ಆ ಪಾತ್ರೆಯಲ್ಲಿರುವದೆಲ್ಲಾ ಅಶುದ್ಧವಾಗಿರುವದು. ಮತ್ತು ಆ ಪಾತ್ರೆಯನ್ನು ನೀವು ಒಡೆದುಬಿಡಬೇಕು.",
			"34": "<sup>34</sup> ಅದರಲ್ಲಿರುವ ತಿನ್ನತಕ್ಕ ಪದಾರ್ಥವೆಲ್ಲಾ ನೀರಿನಿಂದ ನೆನದದ್ದಾಗಿದ್ದರೆ ಅಶುದ್ಧವಾಗುವದು. ಪಾನದ್ರವ್ಯವು ಆ ಪಾತ್ರೆಯಲ್ಲಿದ್ದರೆ ಅದೂ ಅಶುದ್ಧವಾಗುವದು.",
			"35": "<sup>35</sup> ಆ ಜಂತುಗಳ ಹೆಣ ಯಾವ ಸಾಮಾನಿನ ಮೇಲೆ ಬಿದ್ದರೂ ಆ ಸಾಮಾನು ಅಶುದ್ಧವಾಗುವದು. ಒಲೆಗಳಲ್ಲಿ ಅದು ಒಂಟೊಲೆಯಾಗಿದ್ದರೂ ಜೋಡೊಲೆಯಾಗಿದ್ದರೂ ಅದು ಅಶುದ್ಧವಾದದರಿಂದ ಅದನ್ನು ಒಡೆದುಬಿಡಬೇಕು; ಅದು ಅಶುದ್ಧವೇ.",
			"36": "<sup>36</sup> ಒರತೆ ಮೊದಲಾದ ಜಲಾಶಯಗಳನ್ನು ಮಾತ್ರ ನೀವು ಶುದ್ಧವೆಂದೆಣಿಸಬೇಕು. ಆದರೆ ಇವುಗಳೊಳಗಿಂದ ಆ ಹೆಣವನ್ನು ಎತ್ತಿದವನು ಅಶುದ್ಧನಾಗುವನು.",
			"37": "<sup>37</sup> ಬಿತ್ತಬೇಕಾದ ಬೀಜದ ಮೇಲೆ ಈ ಜಂತುಗಳ ಹೆಣ ಬಿದ್ದರೆ ಆ ಬೀಜವು ಅಶುದ್ಧವಾಗುವದಿಲ್ಲ.",
			"38": "<sup>38</sup> ನೀರು ಹಾಕಿ ನೆನಸಿದ ಬೀಜದ ಮೇಲೆ ಆ ಹೆಣ ಬಿದ್ದರೆ ಅದು ನಿಮಗೆ ಅಶುದ್ಧ.",
			"39": "<sup>39</sup> ಆಹಾರಕ್ಕೆ ಯೋಗ್ಯವಾಗಿರುವ ಪಶುವು ಸತ್ತರೆ ಅದರ ಹೆಣವನ್ನು ಮುಟ್ಟಿದವನು ಆ ದಿನದ ಸಾಯಂಕಾಲದವರೆಗೂ ಅಶುದ್ಧನಾಗಿರುವನು.",
			"40": "<sup>40</sup> ಆ ಹೆಣದಲ್ಲಿ ಸ್ವಲ್ಪವಾಗಿ ತಿಂದವನು ತನ್ನ ಬಟ್ಟೆಗಳನ್ನು ಒಗೆದುಕೊಳ್ಳಬೇಕು. ಅವನು ಆ ದಿನದ ಸಾಯಂಕಾಲದವರೆಗೂ ಅಶುದ್ಧನಾಗಿರುವನು. ಆ ಹೆಣವನ್ನು ಹೊತ್ತವನೂ ತನ್ನ ಬಟ್ಟೆಗಳನ್ನು ಒಗೆದುಕೊಳ್ಳಬೇಕು; ಅವನು ಆ ಸಾಯಂಕಾಲದವರೆಗೂ ಅಶುದ್ಧನಾಗಿರುವನು.",
			"41": "<sup>41</sup> ನೆಲದ ಮೇಲೆ ಹರಿದಾಡುವ ಕ್ರಿಮಿಕೀಟಗಳೆಲ್ಲಾ ಹೇಯವಾಗಿವೆ; ಅವುಗಳ ಮಾಂಸವನ್ನು ತಿನ್ನಬಾರದು.",
			"42": "<sup>42</sup> ಹೊಟ್ಟೆಯಿಂದ ನಡೆಯುವಂಥದನ್ನೂ ಕಾಲಿನಿಂದ ಹರಿದಾಡುವಂಥದನ್ನೂ ಬಹಳ ಕಾಲುಳ್ಳದ್ದನ್ನೂ ಅಂತೂ ನೆಲದ ಮೇಲೆ ಹರಿದಾಡುವ ಯಾವ ಸಣ್ಣ ಜೀವಿಯನ್ನೂ ನೀವು ತಿನ್ನಬಾರದು; ಅವು ಹೇಯವಾಗಿವೆ.",
			"43": "<sup>43</sup> ನೀವು ಅಂಥ ಯಾವ ಸಣ್ಣ ಜೀವಿಯನ್ನೂ ತಿಂದು ನಿಮ್ಮನ್ನು ನೀವೇ ಹೇಸಿಕೆ ಮಾಡಿಕೊಂಡು ಅಶುದ್ಧರಾಗಬಾರದು.",
			"44": "<sup>44</sup> ಯಾಕಂದರೆ ನಾನು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು; ನೀವು ದೇವಜನರಿಗೆ ತಕ್ಕಂತಿರಬೇಕು. ನಾನು ಪರಿಶುದ್ಧನಾಗಿರುವದರಿಂದ ನೀವೂ ಪರಿಶುದ್ಧರಾಗಿರಬೇಕು. ನೆಲದ ಮೇಲೆ ಹರಿದಾಡುವ ಯಾವ ಜಂತುವಿನಿಂದಾದರೂ ನೀವು ನಿಮ್ಮನ್ನು ಅಶುದ್ಧಮಾಡಿಕೊಳ್ಳಬಾರದು.",
			"45": "<sup>45</sup> ನಿಮ್ಮ ದೇವರಾಗಿರುವದಕ್ಕೆ ನಿಮ್ಮನ್ನು ಐಗುಪ್ತ ದೇಶದೊಳಗಿಂದ ಬರಮಾಡಿದ ಯೆಹೋವನೇ ನಾನು; ನಾನು ಪರಿಶುದ್ಧನಾಗಿರುವದರಿಂದ ನೀವೂ ಪರಿಶುದ್ಧರಾಗಿರಬೇಕು.",
			"46": "<sup>46</sup> ಇದೇ ಪಶುಪಕ್ಷಿ ಜಲಚರ ಕ್ರಿಮಿಕೀಟಗಳ ವಿಷಯವಾದ ವಿಧಿ.",
			"47": "<sup>47</sup> ಇದರಿಂದ ಶುದ್ಧಾಶುದ್ಧಗಳನ್ನೂ ಭಕ್ಷ್ಯಾಭಕ್ಷ್ಯಗಳನ್ನೂ ವಿವೇಚಿಸುವದಕ್ಕೆ ನಿಮ್ಮಿಂದಾಗುವದು."
		},
		"12": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಮೋಶೆಗೆ ಹೇಳಿದ್ದೇನೆಂದರೆ -",
			"2": "<sup>2</sup> ನೀನು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಹೀಗೆ ಆಜ್ಞಾಪಿಸಬೇಕು - ಒಬ್ಬ ಸ್ತ್ರೀ ಗಂಡುಮಗುವನ್ನು ಹೆತ್ತರೆ ತಾನು ತಿಂಗಳ ಮುಟ್ಟಿನಿಂದ ಹೇಗೆ ಅಶುದ್ಧಳಾಗುವಳೋ ಹಾಗೆಯೇ ಏಳು ದಿನಗಳ ತನಕ ಅಶುದ್ಧಳಾಗಿರಬೇಕು.",
			"3": "<sup>3</sup> ಎಂಟನೆಯ ದಿನದಲ್ಲಿ ಆ ಮಗುವಿಗೆ ಸುನ್ನತಿಮಾಡಿಸಬೇಕು.",
			"4": "<sup>4</sup> ಆಮೇಲೆ ಅವಳ ಶುದ್ಧೀಕರಣ ಪೂರ್ತಿಗೆ ಇನ್ನೂ ಮೂವತ್ತು ಮೂರು ದಿವಸ ಹಿಡಿಯುವದು. ಅದು ಪೂರೈಸುವ ತನಕ ಅವಳು ದೇವರ ವಸ್ತುವನ್ನು ಮುಟ್ಟಕೂಡದು, ದೇವಸ್ಥಾನಕ್ಕೆ ಬರಕೂಡದು.",
			"5": "<sup>5</sup> ಹೆಣ್ಣು ಮಗುವನ್ನು ಹೆತ್ತರೆ ತಾನು ತಿಂಗಳ ಮುಟ್ಟಿನಿಂದ ಹೇಗೆ ಅಶುದ್ಧಳಾಗುವಳೋ ಹಾಗೆಯೇ ಎರಡು ವಾರಗಳು ಅಶುದ್ಧಳಾಗಿರಬೇಕು; ಮತ್ತು ಶುದ್ಧೀಕರಣಪೂರ್ತಿಗೆ ಅರುವತ್ತಾರು ದಿವಸ ಹಿಡಿಯುವದು.",
			"6": "<sup>6</sup> ಗಂಡುಮಗುವನ್ನು ಹೆತ್ತರೂ ಹೆಣ್ಣು ಮಗುವನ್ನು ಹೆತ್ತರೂ ಅವಳ ಶುದ್ಧೀಕರಣದ ದಿನಗಳು ಪೂರೈಸಿದಾಗ ಅವಳು ಸರ್ವಾಂಗಹೋಮಕ್ಕಾಗಿ ಒಂದು ವರುಷದ ಕುರಿಯನ್ನು, ದೋಷಪರಿಹಾರಕ್ಕಾಗಿ ಪಾರಿವಾಳದ ಮರಿಯನ್ನು ಇಲ್ಲವೆ ಬೆಳವಕ್ಕಿಯನ್ನು ದೇವದರ್ಶನದ ಗುಡಾರದ ಬಾಗಲಿಗೆ ತಂದು ಯಾಜಕನಿಗೆ ಒಪ್ಪಿಸಬೇಕು.",
			"7": "<sup>7</sup> ಅವನು ಅವುಗಳನ್ನು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸಮರ್ಪಿಸಿ ಅವಳಿಗೋಸ್ಕರ ದೋಷಪರಿಹಾರವನ್ನು ಮಾಡಿದಾಗ ಅವಳಿಗೆ ರಕ್ತಸ್ರಾವದಿಂದುಂಟಾದ ಅಪರಿಶುದ್ಧತೆ ನಿವೃತ್ತಿಯಾಗಿ ಶುದ್ಧಳಾಗುವಳು. ಗಂಡುಮಗುವನ್ನಾಗಲಿ ಹೆಣ್ಣುಮಗುವನ್ನಾಗಲಿ ಹೆತ್ತವಳಿಗೆ ಇದೇ ವಿಧಿ.",
			"8": "<sup>8</sup> ಕುರಿಯನ್ನು ಕೊಡುವದಕ್ಕೆ ಗತಿಯಿಲ್ಲದಿದ್ದರೆ ಎರಡು ಬೆಳವಕ್ಕಿಗಳನ್ನಾಗಲಿ ಎರಡು ಪಾರಿವಾಳದ ಮರಿಗಳನ್ನಾಗಲಿ ತಂದು ಸರ್ವಾಂಗಹೋಮಕ್ಕಾಗಿ ಒಂದನ್ನೂ ದೋಷಪರಿಹಾರಕ್ಕಾಗಿ ಒಂದನ್ನೂ ಸಮರ್ಪಿಸಬೇಕು. ಯಾಜಕನು ಅವಳಿಗೋಸ್ಕರ ದೋಷಪರಿಹಾರವನ್ನು ಮಾಡಿದಾಗ ಶುದ್ಧಳಾಗುವಳು."
		},
		"13": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಮೋಶೆ ಆರೋನರಿಗೆ ಹೇಳಿದ್ದೇನಂದರೆ -",
			"2": "<sup>2</sup> ಒಬ್ಬ ಮನುಷ್ಯನ ಮೈಮೇಲೆ ಬಾವಾಗಲಿ ಗುಳ್ಳೆಯಾಗಲಿ ಹೊಳೆಯುವ ಮಚ್ಚೆಯಾಗಲಿ ಉಂಟಾಗಿ ಅದರಲ್ಲಿ ಕುಷ್ಠರೋಗದ ಲಕ್ಷಣಗಳು ತೋರಿದರೆ ಅವನನ್ನು ಮಹಾಯಾಜಕನಾದ ಆರೋನನ ಬಳಿಗೆ ಇಲ್ಲವೆ ಆರೋನನ ಮಕ್ಕಳಾದ ಯಾಜಕರಲ್ಲಿ ಒಬ್ಬನ ಬಳಿಗೆ ಕರಕೊಂಡು ಬರಬೇಕು.",
			"3": "<sup>3</sup> ಯಾಜಕನು ಅವನ ಚರ್ಮದಲ್ಲಿರುವ ಮಚ್ಚೆಯನ್ನು ನೋಡುವಾಗ ಆ ಮಚ್ಚೆಯಲ್ಲಿರುವ ರೋಮ ಬೆಳ್ಳಗಾಗಿಹೋಗಿದ್ದರೆ ಮತ್ತು ಆ ಮಚ್ಚೆ ಮಿಕ್ಕ ಚರ್ಮಕ್ಕಿಂತ ತಗ್ಗಾಗಿದ್ದರೆ ಅದು ಕುಷ್ಠರೋಗ. ಯಾಜಕನು ಅದನ್ನು ಪರೀಕ್ಷಿಸಿ ಅವನನ್ನು ಅಶುದ್ಧನೆಂದು ನಿರ್ಣಯಿಸಬೇಕು.",
			"4": "<sup>4</sup> ಆ ಹೊಳೆಯುವ ಮಚ್ಚೆ ಬೆಳ್ಳಗಾಗಿದ್ದು ಮಿಕ್ಕ ಚರ್ಮಕ್ಕಿಂತ ತಗ್ಗಾಗಿರದೆ ಹೋದರೆ ಮತ್ತು ಅಲ್ಲಿರುವ ರೋಮ ಬೆಳ್ಳಗಾಗದಿದ್ದರೆ ಯಾಜಕನು ಆ ಮಚ್ಚೆಯಿದ್ದವನನ್ನು ಏಳು ದಿನಗಳವರೆಗೆ ಪ್ರತ್ಯೇಕವಾಗಿ ಇರಿಸಬೇಕು.",
			"5": "<sup>5</sup> ಏಳನೆಯ ದಿನದಲ್ಲಿ ಯಾಜಕನು ಅವನನ್ನು ಪರೀಕ್ಷಿಸುವಾಗ ಆ ಮಚ್ಚೆ ದೇಹದ ಚರ್ಮದಲ್ಲಿ ಹರಡಿಕೊಳ್ಳದೆ ಮೊದಲು ಇದ್ದಂತೆಯೇ ತೋರಿದರೆ ಯಾಜಕನು ಇನ್ನು ಏಳು ದಿನಗಳವರೆಗೂ ಅವನನ್ನು ಪ್ರತ್ಯೇಕವಾಗಿ ಇರಿಸಬೇಕು. ಆ",
			"6": "<sup>6</sup> ಏಳು ದಿನಗಳಾದ ಮೇಲೆ ಯಾಜಕನು ತಿರಿಗಿ ಅವನನ್ನು ಪರೀಕ್ಷಿಸುವಾಗ ಆ ಮಚ್ಚೆ ದೇಹದ ಚರ್ಮದಲ್ಲಿ ಹರಡಿಕೊಳ್ಳದೆ ಮೊಬ್ಬಾಗಿದ್ದರೆ ಯಾಜಕನು ಅದು ಬರೀ ಗುಳ್ಳೆಯೆಂದು ತಿಳಿದು ಅವನನ್ನು ಶುದ್ಧನೆಂದು ನಿರ್ಣಯಿಸಬೇಕು; ಅವನು ತನ್ನ ಬಟ್ಟೆಗಳನ್ನು ಒಗೆದುಕೊಂಡು ಶುದ್ಧನಾಗುವನು.",
			"7": "<sup>7</sup> ಆದರೆ ಅವನು ತನ್ನ ಶುದ್ಧಿಯ ವಿಷಯದಲ್ಲಿ ತನ್ನನ್ನು ಯಾಜಕನಿಗೆ ತೋರಿಸಿಕೊಂಡನಂತರ ಆ ಗುಳ್ಳೆ ದೇಹದ ಚರ್ಮದಲ್ಲಿ ಹರಡಿಕೊಂಡರೆ ಅವನು ತಿರಿಗಿ ಯಾಜಕನಿಗೆ ತೋರಿಸಿಕೊಳ್ಳಬೇಕು.",
			"8": "<sup>8</sup> ಯಾಜಕನು ನೋಡುವಾಗ ಆ ಮಚ್ಚೆ ದೇಹದ ಚರ್ಮದಲ್ಲಿ ಹರಡಿಕೊಂಡಿದ್ದರೆ ಅದು ಕುಷ್ಠವೆಂದು ತಿಳಿದು ಅವನನ್ನು ಅಶುದ್ಧನೆಂದು ನಿರ್ಣಯಿಸಬೇಕು.",
			"9": "<sup>9</sup> ಕುಷ್ಠರೋಗದ ಚಿಹ್ನೆಗಳು ಯಾವನಲ್ಲಾದರೂ ಕಾಣಬಂದರೆ ಅವನನ್ನು ಯಾಜಕನ ಬಳಿಗೆ ಕರಕೊಂಡು ಬರಬೇಕು.",
			"10": "<sup>10</sup> ಯಾಜಕನು ಅವನನ್ನು ನೋಡುವಾಗ ದೇಹದ ಚರ್ಮದಲ್ಲಿ ಬೆಳ್ಳಗಾದ ಬಾವು ಇದ್ದರೆ ಮತ್ತು ಅಲ್ಲಿರುವ ರೋಮವು ಬೆಳ್ಳಗಾಗಿಹೋಗಿದ್ದರೆ ಮತ್ತು ಆ ಬಾವಿನಲ್ಲಿ ಮಾಂಸ ಕಾಣಿಸಿದರೆ ಅದು ಹಳೇ ಕುಷ್ಠರೋಗದ ಗುರುತು.",
			"11": "<sup>11</sup> ಅವನು ಅಶುದ್ಧನಾದದರಿಂದ ಯಾಜಕನು ಅವನನ್ನು ಪ್ರತ್ಯೇಕಿಸದೆ ಆಗಲೇ ಅಶುದ್ಧನೆಂದು ನಿರ್ಣಯಿಸಬೇಕು.",
			"12": "<sup>12</sup> ಆದರೆ ಒಬ್ಬನ ಚರ್ಮದಲ್ಲಿ ತೊನ್ನು ಹತ್ತಿ ಯಾಜಕನು ನೋಡುವ ಎಲ್ಲಾ ಕಡೆಯಲ್ಲಿಯೂ ತಲೆ ಮೊದಲುಗೊಂಡು ಅಂಗಾಲಿನವರೆಗೂ ಹರಡಿಕೊಂಡಿದ್ದರೆ",
			"13": "<sup>13</sup> ಯಾಜಕನು ನೋಡುವಾಗ ಆ ತೊನ್ನು ದೇಹದಲ್ಲೆಲ್ಲಾ ವ್ಯಾಪಿಸಿಕೊಂಡಿದ್ದರೆ ಅಂಥವನನ್ನು ಶುದ್ಧನೆಂದು ಯಾಜಕನು ನಿರ್ಣಯಿಸಬೇಕು. ಅವನ ಚರ್ಮವೆಲ್ಲಾ ಬೆಳ್ಳಗಾಗಿ ಹೋದದರಿಂದ ಅವನು ಶುದ್ಧನು.",
			"14": "<sup>14</sup> ಆದರೆ ಅಂಥವನ ದೇಹದಲ್ಲಿ ಯಾವಾಗಲಾದರೂ ಮಾಂಸ ಕಾಣಬಂದರೆ ಅವನು ಅಶುದ್ಧನಾಗುವನು.",
			"15": "<sup>15</sup> ಯಾಜಕನು ಆ ಮಾಂಸವನ್ನು ನೋಡಿ ಅವನನ್ನು ಅಶುದ್ಧನೆಂದು ನಿರ್ಣಯಿಸಬೇಕು. ಅಂಥ ಮಾಂಸವು ಅಶುದ್ಧವೇ; ಅದು ಕುಷ್ಠವು.",
			"16": "<sup>16</sup> ಆದರೆ ಆ ಮಾಂಸವು ಅದೇ ಪ್ರಕಾರವಾಗಿ ಇರದೆ ತಿರಿಗಿ ಬೆಳ್ಳಗಾದರೆ ಅವನು ಯಾಜಕನ ಬಳಿಗೆ ಬರಬೇಕು.",
			"17": "<sup>17</sup> ಯಾಜಕನು ನೋಡುವಾಗ ಆ ಮಚ್ಚೆಯು ಬೆಳ್ಳಗಾಗಿಹೋಗಿದ್ದರೆ ಆ ಮನುಷ್ಯನನ್ನು ಶುದ್ಧನೆಂದು ನಿರ್ಣಯಿಸಬೇಕು; ಅವನು ಶುದ್ಧನೇ.",
			"18": "<sup>18</sup> ಒಬ್ಬನ ದೇಹದ ಚರ್ಮದಲ್ಲಿ ಹುಣ್ಣು ಆಗಿದ್ದು ವಾಸಿಯಾದ ಮೇಲೆ",
			"19": "<sup>19</sup> ಅದು ಇದ್ದ ಸ್ಥಳದಲ್ಲಿ ಬಿಳೀ ಬಾವಾಗಲಿ ಕೆಂಪು ಬಿಳುಪು ಮಿಶ್ರವಾಗಿ ಹೊಳೆಯುವ ಕಲೆಯಾಗಲಿ ಉಂಟಾದರೆ ಅವನು ತನ್ನನ್ನು ಯಾಜಕನಿಗೆ ತೋರಿಸಿಕೊಳ್ಳಬೇಕು.",
			"20": "<sup>20</sup> ಯಾಜಕನು ನೋಡುವಾಗ ಆ ಕಲೆ ಮಿಕ್ಕ ಚರ್ಮಕ್ಕಿಂತ ತಗ್ಗಾಗಿ ತೋರಿದರೆ ಮತ್ತು ಅದರಲ್ಲಿರುವ ರೋಮ ಬೆಳ್ಳಗಾಗಿ ಹೋಗಿದ್ದರೆ ಆ ಹುಣ್ಣಿನಲ್ಲಿ ಕುಷ್ಠಹುಟ್ಟಿದದರಿಂದ ಅವನು ಅಶುದ್ಧನೆಂದು ಯಾಜಕನು ನಿರ್ಣಯಿಸಬೇಕು.",
			"21": "<sup>21</sup> ಆದರೆ ಯಾಜಕನು ನೋಡುವಾಗ ಅದರಲ್ಲಿ ಬಿಳೀ ರೋಮವಿಲ್ಲದೆ ಹೋದರೆ ಮತ್ತು ಆ ಕಲೆ ಮಿಕ್ಕ ಚರ್ಮಕ್ಕಿಂತ ತಗ್ಗಾಗದೆ ಮೊಬ್ಬಾಗಿದ್ದರೆ ಯಾಜಕನು ಅವನನ್ನು ಏಳು ದಿನಗಳು ಪ್ರತ್ಯೇಕವಾಗಿ ಇರಿಸಬೇಕು.",
			"22": "<sup>22</sup> ತರುವಾಯ ಅದು ಚರ್ಮದಲ್ಲಿ ಹರಡಿಕೊಂಡಿದ್ದರೆ ಯಾಜಕನು ಅವನನ್ನು ಅಶುದ್ಧನೆಂದು ನಿರ್ಣಯಿಸಬೇಕು; ಅದು ಕುಷ್ಠರೋಗವೇ.",
			"23": "<sup>23</sup> ಆದರೆ ಆ ಹೊಳೆಯುವ ಕಲೆ ಹರಡಿಕೊಳ್ಳದೆ ಮೊದಲಿದ್ದಂತೆಯೇ ಇದ್ದರೆ ಅದು ಆ ಹುಣ್ಣಿನ ಕಲೆಯೆಂದು ತಿಳುಕೊಂಡು ಯಾಜಕನು ಅವನನ್ನು ಶುದ್ಧನೆಂದು ನಿರ್ಣಯಿಸಬೇಕು.",
			"24": "<sup>24</sup> ಒಬ್ಬನ ದೇಹದ ಚರ್ಮದಲ್ಲಿ ಬೆಂಕಿಯಿಂದುಂಟಾದ ಬೊಬ್ಬೆ ಇರಲಾಗಿ ಆ ಬೊಬ್ಬೆಯ ಸ್ಥಳವು ಹೊಳೆಯುವ ಕಲೆಯಾಗಿ ಬೆಳ್ಳಗಾಗಿಯಾಗಲಿ ಕೆಂಪು ಬಿಳುಪು ಮಿಶ್ರವಾಗಿಯಾಗಲಿ ಇದ್ದರೆ ಯಾಜಕನು ಅದನ್ನು ನೋಡಬೇಕು.",
			"25": "<sup>25</sup> ಅವನು ನೋಡುವಾಗ ಆ ಹೊಳೆಯುವ ಕಲೆಯಲ್ಲಿರುವ ರೋಮವು ಬೆಳ್ಳಗಾಗಿಹೋಗಿದ್ದರೆ ಮತ್ತು ಆ ಕಲೆಯು ಮಿಕ್ಕ ಚರ್ಮಕ್ಕಿಂತ ತಗ್ಗಾಗಿ ತೋರಿದರೆ ಅದು ಆ ಬೊಬ್ಬೆಯಲ್ಲಿ ಹುಟ್ಟಿದ ಕುಷ್ಠವೆಂದು ತಿಳಿದು ಯಾಜಕನು ಅವನನ್ನು ಅಶುದ್ಧನೆಂದು ನಿರ್ಣಯಿಸಬೇಕು.",
			"26": "<sup>26</sup> ಆದರೆ ಯಾಜಕನು ನೋಡುವಾಗ ಆ ಹೊಳೆಯುವ ಕಲೆಯಲ್ಲಿ ಬಿಳೀ ರೋಮವಿಲ್ಲದೆ ಹೋದರೆ ಮತ್ತು ಅದು ಮಿಕ್ಕ ಚರ್ಮಕ್ಕಿಂತ ತಗ್ಗಾಗಿ ತೋರದೆ ಮೊಬ್ಬಾಗಿಹೋಗಿದ್ದರೆ ಯಾಜಕನು ಅವನನ್ನು ಏಳು ದಿನಗಳ ತನಕ ಪ್ರತ್ಯೇಕವಾಗಿ ಇರಿಸಬೇಕು.",
			"27": "<sup>27</sup> ಏಳನೆಯ ದಿನದಲ್ಲಿ ಯಾಜಕನು ಅವನನ್ನು ನೋಡುವಾಗ ಅದು ಚರ್ಮದಲ್ಲಿ ಹರಡಿಕೊಂಡಿದ್ದರೆ ಅದು ಕುಷ್ಠರೋಗವೆಂದು ತಿಳಿದು ಅವನನ್ನು ಅಶುದ್ಧನೆಂದು ನಿರ್ಣಯಿಸಬೇಕು.",
			"28": "<sup>28</sup> ಆ ಹೊಳೆಯುವ ಕಲೆಯು ಚರ್ಮದಲ್ಲಿ ಹರಡಿಕೊಳ್ಳದೆ ಮೊದಲಿನಂತೆಯೇ ಇದ್ದು ಮೊಬ್ಬಾಗಿಹೋಗಿದ್ದರೆ ಅದು ಬೆಂಕಿಸುಟ್ಟ ಬೊಬ್ಬೆಯೆಂದು ತಿಳಿದು ಯಾಜಕನು ಅವನನ್ನು ಶುದ್ಧನೆಂದು ನಿರ್ಣಯಿಸಬೇಕು.",
			"29": "<sup>29</sup> ಹೆಂಗಸಿನ ತಲೆಯಲ್ಲಿ ಆಗಲಿ ಗಂಡಸಿನ ತಲೆಯಲ್ಲಿ ಅಥವಾ ಗಡ್ಡದಲ್ಲಿ ಆಗಲಿ ಕಲೆಯುಂಟಾದರೆ ಯಾಜಕನು ಅದನ್ನು ನೋಡಬೇಕು.",
			"30": "<sup>30</sup> ಆಗ ಅದು ಮಿಕ್ಕ ಚರ್ಮಕ್ಕಿಂತ ತಗ್ಗಾಗಿ ತೋರಿದರೆ ಮತ್ತು ಅದರಲ್ಲಿ ಹಳದಿ ಬಣ್ಣವುಳ್ಳ ಸಣ್ಣ ಕೂದಲು ಇದ್ದರೆ ಅದು ತಲೆಯಲ್ಲಾಗಲಿ ಗಡ್ಡದಲ್ಲಾಗಲಿ ಉಂಟಾದ ಕುಷ್ಠವನ್ನು ಸೂಚಿಸುವ ದದ್ದು ಆಗಿರುವದರಿಂದ ಯಾಜಕನು ಅಂಥವನನ್ನು ಅಶುದ್ಧನೆಂದು ನಿರ್ಣಯಿಸಬೇಕು.",
			"31": "<sup>31</sup> ಯಾಜಕನು ಆ ದದ್ದನ್ನು ನೋಡುವಾಗ ಅದು ಮಿಕ್ಕ ಚರ್ಮಕ್ಕಿಂತ ತಗ್ಗಾಗಿ ತೋರದೆ ಹೋದಾಗ್ಯೂ ಅದು ಇರುವ ಕಡೆ ಕರೇಕೂದಲು ಇಲ್ಲದಿದ್ದರೆ ಯಾಜಕನು ಅವನನ್ನು ಏಳು ದಿನಗಳ ತನಕ ಪ್ರತ್ಯೇಕವಾಗಿ ಇರಿಸಬೇಕು.",
			"32": "<sup>32</sup> ಏಳನೆಯ ದಿನದಲ್ಲಿ ಯಾಜಕನು ನೋಡುವಾಗ ಆ ದದ್ದು ಹರಡಿಕೊಳ್ಳದೆ ಇದ್ದರೆ ಮತ್ತು ಅದು ಇರುವ ಕಡೆ ಹಳದಿ ಬಣ್ಣದ ಕೂದಲು ಇಲ್ಲದಿದ್ದರೆ ಮತ್ತು ಅದು ಮಿಕ್ಕ ಚರ್ಮಕ್ಕಿಂತ ತಗ್ಗಾಗಿ ತೋರದೆಹೋದರೆ ಅವನು ಕ್ಷೌರ ಮಾಡಿಸಿಕೊಳ್ಳಬೇಕು.",
			"33": "<sup>33</sup> ಆ ದದ್ದು ಇರುವ ಭಾಗವನ್ನು ಮಾತ್ರ ಕ್ಷೌರ ಮಾಡಿಸಿಕೊಳ್ಳಬಾರದು. ಯಾಜಕನು ಅವನನ್ನು ಇನ್ನು ಏಳು ದಿನಗಳವರೆಗೂ ಪ್ರತ್ಯೇಕಿಸಬೇಕು.",
			"34": "<sup>34</sup> ಏಳನೆಯ ದಿನದಲ್ಲಿ ಯಾಜಕನು ತಿರಿಗಿ ಆ ದದ್ದನ್ನು ನೋಡುವಾಗ ಅದು ಚರ್ಮದಲ್ಲಿ ಹರಡಿಕೊಳ್ಳದೆ ಹೋಗಿದ್ದರೆ ಮಿಕ್ಕ ಚರ್ಮಕ್ಕಿಂತ ತಗ್ಗಾಗಿ ತೋರದೆ ಇದ್ದರೆ ಯಾಜಕನು ಅವನನ್ನು ಶುದ್ಧನೆಂದು ನಿರ್ಣಯಿಸಬೇಕು. ಅವನು ತನ್ನ ಬಟ್ಟೆಗಳನ್ನು ಒಗೆದುಕೊಂಡಾಗ ಶುದ್ಧನಾಗುವನು.",
			"35": "<sup>35</sup> ಆದರೆ ಅವನು ಶುದ್ಧನೆಂದು ನಿರ್ಣಯಿಸಲ್ಪಟ್ಟ ಮೇಲೆ ಆ ದದ್ದು ಚರ್ಮದಲ್ಲಿ ಹರಡಿಕೊಂಡರೆ ಯಾಜಕನು ಅವನನ್ನು ನೋಡಬೇಕು;",
			"36": "<sup>36</sup> ಆದರೆ ಹಳದಿಬಣ್ಣದ ಕೂದಲು ಇದೆಯೋ ಇಲ್ಲವೋ ಎಂದು ನೋಡಬೇಕಾದ ಅಗತ್ಯವಿಲ್ಲ; ಅವನು ಅಶುದ್ಧನೇ.",
			"37": "<sup>37</sup> ಆ ದದ್ದು ಮೊದಲು ಇದ್ದಂತೆಯೇ ಇದ್ದು ಅದರಲ್ಲಿ ಕರೇ ಕೂದಲು ಹುಟ್ಟಿದ್ದರೆ ಅದು ವಾಸಿಯಾಯಿತು. ಅವನು ಶುದ್ಧನು; ಯಾಜಕನು ಅವನನ್ನು ಶುದ್ಧನೆಂದು ನಿರ್ಣಯಿಸಬೇಕು.",
			"38": "<sup>38</sup> ಗಂಡಸಿಗಾಗಲಿ ಹೆಂಗಸಿಗಾಗಲಿ ಚರ್ಮದಲ್ಲಿ ಹೊಳೆಯುವ ಬಿಳುಪಾದ ಕಲೆಗಳು ಅಲ್ಲಲ್ಲಿ ಹುಟ್ಟಿದ್ದರೆ ಯಾಜಕನು ಅವುಗಳನ್ನು ನೋಡಬೇಕು.",
			"39": "<sup>39</sup> ಆ ಹೊಳೆಯುವ ಕಲೆಗಳು ಮೊಬ್ಬಾದ ಬಿಳೀ ಬಣ್ಣವಾಗಿದ್ದರೆ ಅದು ದೇಹದ ಚರ್ಮದಲ್ಲಿ ಹುಟ್ಟಿದ ಚಿಬ್ಬು; ಅವನು ಶುದ್ಧನು.",
			"40": "<sup>40</sup> ಯಾವನಾದರೂ ತಲೆಯ ಕೂದಲು ಉದುರಿಹೋದದರಿಂದ ಬೋಳತಲೆಯವನಾದರೂ ಅವನು ಶುದ್ಧನೇ.",
			"41": "<sup>41</sup> ಮುಂದಲೆಯ ಕೂದಲು ಉದುರಿದರೆ ಅವನು ಪಟ್ಟೆ ತಲೆಯವನಾದರೂ ಶುದ್ಧನೇ.",
			"42": "<sup>42</sup> ಆದರೆ ಬೋಳತಲೆಯಲ್ಲಾಗಲಿ ಪಟ್ಟೆತಲೆಯಲ್ಲಾಗಲಿ ಕೆಂಪು ಬಿಳುಪು ಮಿಶ್ರವಾದ ಮಚ್ಚೆ ಹುಟ್ಟಿದರೆ ಅದು ಕುಷ್ಠವೇ.",
			"43": "<sup>43</sup> ಯಾಜಕನು ಅವನನ್ನು ನೋಡಬೇಕು. ಆಗ ಅದರಿಂದುಂಟಾದ ಬಾವು ಕೆಂಪು ಬಿಳುಪು ಮಿಶ್ರವಾಗಿ ಚರ್ಮದಲ್ಲಿನ ಕುಷ್ಠದಂತೆ ತೋರಿದರೆ ಅವನು ಕುಷ್ಠರೋಗಿ; ಅಶುದ್ಧನು.",
			"44": "<sup>44</sup> ಅವನ ತಲೆಯ ಮೇಲೆ ಕುಷ್ಠದ ಗುರುತು ಕಾಣಿಸಿದದರಿಂದ ಅವನು ಅಶುದ್ಧನೆಂದು ಯಾಜಕನು ನಿರ್ಣಯಿಸಬೇಕು.",
			"45": "<sup>45</sup> ಯಾರಲ್ಲಿ ಕುಷ್ಠದ ಗುರುತು ಕಾಣಬಂತೋ ಅವನು ತನ್ನ ಬಟ್ಟೆಗಳನ್ನು ಹರಿದುಕೊಂಡು ತಲೆಯನ್ನು ಕೆದರಿಕೊಂಡು ಬಾಯಿಯನ್ನು ಬಟ್ಟೆಯಿಂದ ಮುಚ್ಚಿಕೊಂಡು - ನಾನು ಅಶುದ್ಧನು ಅಶುದ್ಧನು ಎಂದು ಕೂಗಿಕೊಳ್ಳಬೇಕು.",
			"46": "<sup>46</sup> ಆ ರೋಗದ ಗುರುತುಗಳು ಅವನಲ್ಲಿ ಇರುವ ದಿನಗಳೆಲ್ಲಾ ಅವನು ಅಶುದ್ಧನಾಗಿರುವನು. ಅವನು ಅಶುದ್ಧನಾದದರಿಂದ ಪ್ರತ್ಯೇಕವಾಗಿಯೇ ವಾಸವಾಗಿರಬೇಕು; ಅವನ ನಿವಾಸವು ಪಾಳೆಯದ ಹೊರಗೆ ಇರಬೇಕು.",
			"47": "<sup>47</sup> ಕುಷ್ಠರೋಗದ ಗುರುತು ಬಟ್ಟೆಯಲ್ಲಿ ಕಂಡುಬಂದಾಗ ಅದು ಉಣ್ಣೆಯ ಬಟ್ಟೆಯಾಗಲಿ ನಾರಿನ ಬಟ್ಟೆಯಾಗಲಿ,",
			"48": "<sup>48</sup> ನಾರಿನ ಅಥವಾ ಉಣ್ಣೆಯ ಹಾಸಿನಲ್ಲಾಗಲಿ ಹೊಕ್ಕಿನಲ್ಲಾಗಲಿ, ಇಲ್ಲವೆ ತೊಗಲಿನಲ್ಲಾಗಲಿ ತೊಗಲಿನಿಂದ ಮಾಡಲ್ಪಟ್ಟ ವಸ್ತುವಿನಲ್ಲಾಗಲಿ,",
			"49": "<sup>49</sup> ಆ ಬಟ್ಟೆ ತೊಗಲು ಹಾಸು ಹೊಕ್ಕು ತೊಗಲಿನ ಸಾಮಾನುಗಳಲ್ಲಿ ಹಸುರಾಗಿ ಇಲ್ಲವೆ ಕೆಂಪಾಗಿ ಮಚ್ಚೆಕಾಣಿಸಿದರೆ ಅದು ಕುಷ್ಠದ ಗುರುತು; ಅದನ್ನು ಯಾಜಕನಿಗೆ ತೋರಿಸಬೇಕು.",
			"50": "<sup>50</sup> ಯಾಜಕನು ಅದನ್ನು ನೋಡಿ ಏಳು ದಿನಗಳವರೆಗೆ ಪ್ರತ್ಯೇಕವಾಗಿ ಇಡಿಸಬೇಕು.",
			"51": "<sup>51</sup> ಏಳನೆಯ ದಿನದಲ್ಲಿ ಅವನು ಅದನ್ನು ನೋಡುವಾಗ ಆ ಬಟ್ಟೆಯ ಹಾಸಿನಲ್ಲಾಗಲಿ ಹೊಕ್ಕಿನಲ್ಲಾಗಲಿ ಇಲ್ಲವೆ ಯಾವದಾದರೂ ಒಂದು ಕೆಲಸಕ್ಕೆ ಉಪಯೋಗವಾಗಿರುವ ಆ ತೊಗಲಿನಲ್ಲಿ ಆ ಮಚ್ಚೆ ಹರಡಿಕೊಂಡಿದ್ದರೆ ಅದು ಪ್ರಾಣಕ್ಕೆ ಅಪಾಯಕರವಾದ ಕುಷ್ಠವೇ; ಆ ವಸ್ತು ಅಶುದ್ಧ.",
			"52": "<sup>52</sup> ಆ ವಸ್ತುವು ಬಟ್ಟೆಯಾದರೂ ಹಾಸಾದರೂ ಹೊಕ್ಕಾದರೂ ಉಣ್ಣೆಯದಾದರೂ ನಾರಿನದಾದರೂ ತೊಗಲಿನದಾದರೂ ಅದರಲ್ಲಿ ಪ್ರಾಣಕ್ಕೆ ಅಪಾಯಕರವಾದ ಕುಷ್ಠವಿರುವದರಿಂದ ಅದನ್ನು ಬೆಂಕಿಯಿಂದ ಸುಡಿಸಿಬಿಡಬೇಕು.",
			"53": "<sup>53</sup> ಆದರೆ ಯಾಜಕನು ನೋಡುವಾಗ ಆ ವಸ್ತುವಿನಲ್ಲಿ ಅಂದರೆ ಆ ಬಟ್ಟೆ, ಹಾಸು, ಹೊಕ್ಕು, ತೊಗಲಿನ ಸಾಮಾನು ಇವುಗಳಲ್ಲಿ ಆ ಮಚ್ಚೆ ಹರಡಿಕೊಳ್ಳದಿದ್ದರೆ",
			"54": "<sup>54</sup> ಅದನ್ನು ನೀರಿನಿಂದ ತೊಳೆಯಬೇಕೆಂದು ಯಾಜಕನು ಅಪ್ಪಣೆಕೊಟ್ಟು ಇನ್ನೂ ಏಳು ದಿನಗಳ ತನಕ ಅದನ್ನು ಪ್ರತ್ಯೇಕವಾಗಿ ಇಡಿಸಬೇಕು.",
			"55": "<sup>55</sup> ಆ ಮಚ್ಚೆ ಇದ್ದ ವಸ್ತುವನ್ನು ತೊಳೆಸಿದ ಮೇಲೆ ಯಾಜಕನು ನೋಡುವಾಗ ಆ ಮಚ್ಚೆಯು ಹರಡಿಕೊಳ್ಳದೆ ಇದ್ದಾಗ್ಯೂ ಅದರ ಬಣ್ಣ ಮೊದಲಿದ್ದಂತೆಯೇ ಇದ್ದರೆ ಆ ವಸ್ತು ಅಶುದ್ಧ. ಕುಷ್ಠರೋಗದ ಗುರುತು ಅದರ ಮೇಲಣ ಕಡೆಯಿಂದಾಗಲಿ ಕೆಳಗಣ ಕಡೆಯಿಂದಾಗಲಿ ಆ ವಸ್ತುವಿನೊಳಗೆ ವ್ಯಾಪಿಸಿದದರಿಂದ ಅದನ್ನು ಬೆಂಕಿಯಿಂದ ಸುಟ್ಟುಬಿಡಬೇಕು.",
			"56": "<sup>56</sup> ಆದರೆ ತೊಳೆದ ಬಟ್ಟೆಯನ್ನು ಯಾಜಕನು ನೋಡುವಾಗ ಅದರಲ್ಲಿದ್ದ ಮಚ್ಚೆ ಮೊಬ್ಬಾಗಿಹೋಗಿದ್ದರೆ ಅವನು ಆ ಮಚ್ಚೆ ಇರುವ ಭಾಗವನ್ನು ಆ ಬಟ್ಟೆಯಿಂದಾಗಲಿ ತೊಗಲಿನಿಂದಾಗಲಿ ಹಾಸಿನಿಂದಾಗಲಿ ಹೊಕ್ಕಿನಿಂದಾಗಲಿ ಕತ್ತರಿಸಿಬಿಡಬೇಕು.",
			"57": "<sup>57</sup> ಆಮೇಲೆಯೂ ಕುಷ್ಠದ ಮಚ್ಚೆ ಆ ಬಟ್ಟೆಯಲ್ಲಾಗಲಿ ಹಾಸಿನಲ್ಲಾಗಲಿ ಹೊಕ್ಕಿನಲ್ಲಾಗಲಿ ತೊಗಲಿನ ಸಾಮಾನಿನಲ್ಲಾಗಲಿ ಕಂಡುಬಂದರೆ ಕುಷ್ಠರೋಗವು ಇನ್ನೂ ಅದರಲ್ಲಿ ಉಂಟೆಂದು ತಿಳಿದುಕೊಳ್ಳಬೇಕು. ಆ ಮಚ್ಚೆ ಇರುವ ವಸ್ತುವನ್ನೇ ಬೆಂಕಿಯಿಂದ ಸುಟ್ಟುಬಿಡಬೇಕು.",
			"58": "<sup>58</sup> ಆ ಬಟ್ಟೆಯನ್ನಾಗಲಿ ಹಾಸನ್ನಾಗಲಿ ಹೊಕ್ಕನ್ನಾಗಲಿ ತೊಗಲಿನ ಸಾಮಾನನ್ನಾಗಲಿ ತೊಳೆದನಂತರ ಆ ಮಚ್ಚೆ ಕಾಣದೆಹೋದರೆ ಅದನ್ನು ಎರಡನೆಯ ಸಾರಿ ತೊಳೆಯಬೇಕು; ಆಗ ಅದು ಶುದ್ಧವಾಗುವದು.",
			"59": "<sup>59</sup> ಉಣ್ಣೇಬಟ್ಟೆಯಲ್ಲಾಗಲಿ ನಾರಿನ ಬಟ್ಟೆಯಲ್ಲಾಗಲಿ ಹಾಸಿನಲ್ಲಾಗಲಿ ಹೊಕ್ಕಿನಲ್ಲಾಗಲಿ ತೊಗಲಿನ ಸಾಮಾನಿನಲ್ಲಾಗಲಿ ಕಾಣಬಂದ ಕುಷ್ಠರೋಗದ ಗುರುತಿನ ವಿಷಯವಾದ ವಿಧಿ ಇದೇ. ಅದು ಶುದ್ಧವೆಂದು ನಿರ್ಣಯಿಸಬೇಕೋ ಅಶುದ್ಧವೆಂದು ನಿರ್ಣಯಿಸಬೇಕೋ ಎಂಬದು ಇದರಿಂದ ತಿಳಿಯುವದು."
		},
		"14": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಮೋಶೆಗೆ ಹೇಳಿದ್ದೇನಂದರೆ -",
			"2": "<sup>2</sup> ಕುಷ್ಠರೋಗಿ ಶುದ್ಧನಾಗುವ ದಿನದಲ್ಲಿ ಅವನ ವಿಷಯವಾಗಿ ನಡಿಸಬೇಕಾದ ಕ್ರಮ ಹೇಗಂದರೆ - ಅವನನ್ನು ಯಾಜಕನಿಗೆ ತೋರಿಸಬೇಕು.",
			"3": "<sup>3</sup> ಯಾಜಕನು ಪಾಳೆಯದ ಹೊರಕ್ಕೆ ಹೋಗಿ ಅವನನ್ನು ನೋಡಬೇಕು.",
			"4": "<sup>4</sup> ಆಗ ಅವನಲ್ಲಿದ್ದ ಕುಷ್ಠವು ವಾಸಿಯಾಯಿತೆಂದು ಕಂಡರೆ ಶುದ್ಧ ಮಾಡಿಸಿಕೊಳ್ಳುವವನಿಗೋಸ್ಕರ ಸಜೀವವಾದ ಎರಡು ಶುದ್ಧ ಪಕ್ಷಿಗಳನ್ನೂ ದೇವದಾರಿನ ಕಟ್ಟಿಗೆಯನ್ನೂ ರಕ್ತವರ್ಣವುಳ್ಳ ದಾರವನ್ನೂ ಹಿಸ್ಸೋಪ್‍ಗಿಡದ ಬರಲನ್ನೂ ತರಬೇಕೆಂದು ಯಾಜಕನು ಆಜ್ಞಾಪಿಸಬೇಕು.",
			"5": "<sup>5</sup> ಆ ಪಕ್ಷಿಗಳಲ್ಲಿ ಒಂದನ್ನು ಸೆಲೇ ನೀರಿನ ಮೇಲೆ ಮಣ್ಣಿನ ಪಾತ್ರೆಯಲ್ಲಿ ವಧಿಸಬೇಕೆಂದು ಯಾಜಕನು ಆಜ್ಞಾಪಿಸಿ",
			"6": "<sup>6</sup> ಮತ್ತೊಂದು ಪಕ್ಷಿಯನ್ನೂ ಆ ದೇವದಾರಿನ ಕಟ್ಟಿಗೆಯನ್ನೂ ರಕ್ತವರ್ಣವುಳ್ಳ ದಾರವನ್ನೂ ಹಿಸ್ಸೋಪ್ ಗಿಡದ ಬರಲನ್ನೂ ತೆಗೆದುಕೊಂಡು ಸೆಲೇ ನೀರಿನ ಮೇಲೆ ವಧಿಸಿದ ಆ ಮೊದಲನೆಯ ಪಕ್ಷಿಯ ರಕ್ತದಲ್ಲಿ ಅವುಗಳನ್ನೆಲ್ಲಾ ಆ ಪಕ್ಷಿಯನ್ನೂ ಸಜೀವವಾಗಿಯೇ ಅದ್ದಿ",
			"7": "<sup>7</sup> ಕುಷ್ಠರೋಗದ ವಿಷಯದಲ್ಲಿ ಶುದ್ಧಮಾಡಿಸಿಕೊಳ್ಳುವವನ ಮೇಲೆ ಏಳು ಸಾರಿ ಆ ರಕ್ತವನ್ನು ಚಿಮಿಕಿಸಿ ಅವನನ್ನು ಶುದ್ಧನೆಂದು ನಿರ್ಣಯಿಸಿ ಆ ಜೀವವುಳ್ಳ ಪಕ್ಷಿಯನ್ನು ಅಡವಿಯ ಕಡೆಗೆ ಬಿಟ್ಟುಬಿಡಬೇಕು.",
			"8": "<sup>8</sup> ಆಗ ಶುದ್ಧಮಾಡಿಸಿಕೊಳ್ಳುವವನು ತನ್ನ ಬಟ್ಟೆಗಳನ್ನು ಒಗೆದುಕೊಂಡು ಸರ್ವಾಂಗಕ್ಷೌರಮಾಡಿಸಿಕೊಂಡು ಸ್ನಾನ ಮಾಡಿದ ಮೇಲೆ ಶುದ್ಧನಾಗುವನು; ತರುವಾಯ ಅವನು ಪಾಳೆಯದೊಳಗೆ ಬರಬಹುದು; ಆದರೂ ಏಳು ದಿನಗಳ ತನಕ ತನ್ನ ಡೇರೆಯ ಹೊರಗೇ ಇರಬೇಕು.",
			"9": "<sup>9</sup> ಏಳನೆಯ ದಿನದಲ್ಲಿ ತನ್ನ ತಲೆಗೂದಲು, ಗಡ್ಡ, ಹುಬ್ಬು, ಮೈಗೂದಲು ಇವುಗಳನ್ನೆಲ್ಲಾ ಕ್ಷೌರಮಾಡಿಸಿಕೊಂಡು ತನ್ನ ಬಟ್ಟೆಗಳನ್ನು ಒಗೆದುಕೊಂಡು ಜಲಸ್ನಾನಮಾಡಬೇಕು. ಆಗಿನಿಂದ ಶುದ್ಧನಾಗುವನು.",
			"10": "<sup>10</sup> ಎಂಟನೆಯ ದಿನದಲ್ಲಿ ಅವನು ಪೂರ್ಣಾಂಗವಾದ ಎರಡು ಟಗರುಗಳನ್ನೂ ಪೂರ್ಣಾಂಗವಾದ ಒಂದು ವರುಷದ ಕುರಿಯನ್ನೂ ನೈವೇದ್ಯಕ್ಕಾಗಿ ಎಣ್ಣೇ ಕಲಸಿದ ಒಂಭತ್ತು ಸೇರು ಗೋದಿಹಿಟ್ಟನ್ನೂ ಒಂದು ಸೇರು ಎಣ್ಣೆಯನ್ನೂ ತೆಗೆದುಕೊಂಡು ಬರಬೇಕು.",
			"11": "<sup>11</sup> ಶುದ್ಧಿಮಾಡುವ ಯಾಜಕನು ಶುದ್ಧಮಾಡಿಸಿಕೊಳ್ಳುವವನನ್ನು ಇವುಗಳೊಂದಿಗೆ ದೇವದರ್ಶನದ ಗುಡಾರದ ಬಾಗಲಲ್ಲಿ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನಿಲ್ಲಿಸಿ",
			"12": "<sup>12</sup> ಆ ಟಗರುಗಳಲ್ಲಿ ಒಂದನ್ನೂ ಆ ಸೇರು ಎಣ್ಣೆಯನ್ನೂ ಪ್ರಾಯಶ್ಚಿತ್ತಯಜ್ಞವಾಗಿ ಸಮರ್ಪಿಸಬೇಕು; ಅವುಗಳನ್ನು ನೈವೇದ್ಯವಾಗಿ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನಿವಾಳಿಸಬೇಕು.",
			"13": "<sup>13</sup> ದೋಷಪರಿಹಾರಕಯಜ್ಞಪಶುವನ್ನೂ ಸರ್ವಾಂಗಹೋಮಪಶುವನ್ನೂ ವಧಿಸುವ ಸ್ಥಳದಲ್ಲೇ ಅಂದರೆ ದೇವಸ್ಥಾನದ ಪ್ರಾಕಾರದೊಳಗೆ ಆ ಟಗರು ವಧಿಸಲ್ಪಡಬೇಕು. ಯಾಕಂದರೆ ದೋಷಪರಿಹಾರಕಯಜ್ಞದ್ರವ್ಯದಂತೆಯೇ ಪ್ರಾಯಶ್ಚಿತ್ತಯಜ್ಞದ್ರವ್ಯವೂ ಯಾಜಕನಿಗೆ ಸಲ್ಲತಕ್ಕದ್ದು; ಅದು ಮಹಾಪರಿಶುದ್ಧವಾದದ್ದು.",
			"14": "<sup>14</sup> ಆಗ ಯಾಜಕನು ಆ ಪ್ರಾಯಶ್ಚಿತ್ತಯಜ್ಞಪಶುವಿನ ರಕ್ತದಲ್ಲಿ ಸ್ವಲ್ಪ ತೆಗೆದುಕೊಂಡು ಶುದ್ಧಮಾಡಿಸಿಕೊಳ್ಳುವವನ ಬಲಗಿವಿಯ ತುದಿಗೂ ಬಲಗೈಯ ಹೆಬ್ಬೆರಳಿಗೂ ಬಲಗಾಲಿನ ಹೆಬ್ಬೆಟ್ಟಿಗೂ ಹಚ್ಚಬೇಕು.",
			"15": "<sup>15</sup> ಆಗ ಯಾಜಕನು ಆ ಸೇರು ಎಣ್ಣೆಯಲ್ಲಿ ಸ್ವಲ್ಪವನ್ನು ತನ್ನ ಎಡಗೈಯಲ್ಲಿ ಹೊಯಿದುಕೊಂಡು",
			"16": "<sup>16</sup> ತನ್ನ ಬಲಗೈಯ ಬೆರಳನ್ನು ಅದರಲ್ಲಿ ಅದ್ದಿ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಏಳು ಸಾರಿ ಬೆರಳಿನಿಂದ ಚಿಮಿಕಿಸಬೇಕು.",
			"17": "<sup>17</sup> ಅವನು ತನ್ನ ಕೈಯಲ್ಲಿರುವ ಮಿಕ್ಕ ಎಣ್ಣೆಯಲ್ಲಿ ಸ್ವಲ್ಪವನ್ನು ಶುದ್ಧಮಾಡಿಸಿಕೊಳ್ಳುವವನ ಬಲಗಿವಿಯ ತುದಿಗೂ ಬಲಗೈಯ ಹೆಬ್ಬೆರಳಿಗೂ ಬಲಗಾಲಿನ ಹೆಬ್ಬೆಟ್ಟಿಗೂ ಪ್ರಾಯಶ್ಚಿತ್ತ ಯಜ್ಞಪಶುವಿನ ರಕ್ತವನ್ನು ಹಚ್ಚಿದ ಸ್ಥಳಗಳಲ್ಲಿಯೇ ಹಚ್ಚಿ",
			"18": "<sup>18</sup> ಕೈಯಲ್ಲಿರುವ ಮಿಕ್ಕ ಎಣ್ಣೆಯನ್ನು ಶುದ್ಧಮಾಡಿಸಿಕೊಳ್ಳುವವನ ತಲೆಯ ಮೇಲೆ ಹೊಯಿದುಬಿಡಬೇಕು. ಹೀಗೆ ಯಾಜಕನು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಅವನ ದೋಷವನ್ನು ಪರಿಹರಿಸುವನು.",
			"19": "<sup>19</sup> ಅದಲ್ಲದೆ ಯಾಜಕನು ಶುದ್ಧಮಾಡಿಸಿಕೊಳ್ಳುವವನ ಅಪವಿತ್ರತೆಯನ್ನು ಹೋಗಲಾಡಿಸುವದಕ್ಕೆ [ಆ ಕುರಿಯನ್ನು] ದೋಷಪರಿಹಾರಕ ಯಜ್ಞವಾಗಿ ಸಮರ್ಪಿಸಿ ಅವನ ದೋಷವನ್ನು ಪರಿಹರಿಸುವನು. ಆಮೇಲೆ ಯಾಜಕನು [ಆ ಎರಡನೆಯ ಟಗರು ಮರಿಯನ್ನು] ಸರ್ವಾಂಗಹೋಮಕ್ಕಾಗಿ ವಧಿಸಬೇಕು. ಅವನು ಆ ಸರ್ವಾಂಗಹೋಮವನ್ನೂ ನೈವೇದ್ಯವನ್ನೂ ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಸಮರ್ಪಿಸಬೇಕು.",
			"20": "<sup>20</sup> ಯಾಜಕನು ಅವನಿಗೋಸ್ಕರ ಹೀಗೆ ದೋಷಪರಿಹಾರಮಾಡಿದಾಗ ಅವನು ಶುದ್ಧನಾಗುವನು.",
			"21": "<sup>21</sup> ಅವನು ಅಷ್ಟನ್ನು ಸಮರ್ಪಿಸುವದಕ್ಕೆ ಬಡವನಾಗಿದ್ದರೆ ಪ್ರಾಯಶ್ಚಿತ್ತಯಜ್ಞಕ್ಕಾಗಿ ಒಂದು ಟಗರುಮರಿಯನ್ನು ತಂದು ದೋಷಪರಿಹಾರಮಾಡಿಸಿಕೊಳ್ಳುವದಕ್ಕೋಸ್ಕರ [ಯಾಜಕನ ಕೈಯಿಂದ] ನೈವೇದ್ಯವಾಗಿ ನಿವಾಳಿಸಬೇಕು; ಮತ್ತು ಧಾನ್ಯನೈವೇದ್ಯಕ್ಕಾಗಿ ಎಣ್ಣೆ ಕಲಸಿದ ಮೂರು ಸೇರು ಗೋದಿಹಿಟ್ಟನ್ನೂ ಒಂದು ಸೇರು ಎಣ್ಣೆಯನ್ನೂ ತರಬೇಕು.",
			"22": "<sup>22</sup> ಅದಲ್ಲದೆ ತನ್ನ ಸ್ಥಿತಿಗೆ ತಕ್ಕಂತೆ ಎರಡು ಬೆಳವಕ್ಕಿಗಳನ್ನಾಗಲಿ ಎರಡು ಪಾರಿವಾಳದ ಮರಿಗಳನ್ನಾಗಲಿ ತಂದು ದೋಷಪರಿಹಾರಕಯಜ್ಞವಾಗಿ ಒಂದನ್ನೂ ಸರ್ವಾಂಗಹೋಮವಾಗಿ ಮತ್ತೊಂದನ್ನೂ ಸಮರ್ಪಿಸಬೇಕು.",
			"23": "<sup>23</sup> ಅವನು ತನ್ನ ಶುದ್ಧೀಕರಣಕ್ಕೋಸ್ಕರವಾಗಿ ಎಂಟನೆಯ ದಿನದಲ್ಲಿ ಇವುಗಳನ್ನು ದೇವದರ್ಶನದ ಗುಡಾರದ ಬಾಗಲಿಗೆ ಯೆಹೋವನ ಸನ್ನಿಧಿಗೆ ತಂದು ಯಾಜಕನಿಗೆ ಒಪ್ಪಿಸಬೇಕು.",
			"24": "<sup>24</sup> ಯಾಜಕನು ಪ್ರಾಯಶ್ಚಿತ್ತಯಜ್ಞದ ಕುರಿಯನ್ನೂ ಆ ಸೇರು ಎಣ್ಣೆಯನ್ನೂ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನೈವೇದ್ಯವಾಗಿ ನಿವಾಳಿಸಬೇಕು.",
			"25": "<sup>25</sup> ಪ್ರಾಯಶ್ಚಿತ್ತಯಜ್ಞದ ಕುರಿಯನ್ನು ವಧಿಸಿದ ನಂತರ ಯಾಜಕನು ಅದರ ರಕ್ತದಲ್ಲಿ ಸ್ವಲ್ಪ ತೆಗೆದುಕೊಂಡು ಶುದ್ಧಮಾಡಿಸಿಕೊಳ್ಳುವವನ ಬಲಗಿವಿಯ ತುದಿಗೂ ಬಲಗೈಯ ಹೆಬ್ಬೆರಳಿಗೂ ಬಲಗಾಲಿನ ಹೆಬ್ಬೆಟ್ಟಿಗೂ ಹಚ್ಚಬೇಕು.",
			"26": "<sup>26</sup> ಮತ್ತು ಯಾಜಕನು ಆ ಎಣ್ಣೆಯಲ್ಲಿ ಸ್ವಲ್ಪವನ್ನು ತನ್ನ ಎಡಗೈಯಲ್ಲಿ ಹೊಯಿದುಕೊಂಡು",
			"27": "<sup>27</sup> ಅದರಲ್ಲಿ ಸ್ವಲ್ಪವನ್ನು ಬಲಗೈಯ ಬೆರಳಿನಿಂದ ಏಳು ಸಾರಿ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಚಿಮಿಕಿಸಬೇಕು.",
			"28": "<sup>28</sup> ಮತ್ತು ಯಾಜಕನು ತನ್ನ ಕೈಯಲ್ಲಿರುವ ಎಣ್ಣೆಯಲ್ಲಿ ಸ್ವಲ್ಪವನ್ನು ಶುದ್ಧಮಾಡಿಸಿಕೊಳ್ಳುವವನ ಬಲಗಿವಿಯ ತುದಿಗೂ ಬಲಗೈಯ ಹೆಬ್ಬೆರಳಿಗೂ ಬಲಗಾಲಿನ ಹೆಬ್ಬೆಟ್ಟಿಗೂ ಪ್ರಾಯಶ್ಚಿತ್ತಯಜ್ಞಪಶುವಿನ ರಕ್ತವನ್ನು ಹಚ್ಚಿದ ಸ್ಥಳಗಳಲ್ಲಿಯೇ ಹಚ್ಚಬೇಕು.",
			"29": "<sup>29</sup> ಶುದ್ಧಮಾಡಿಸಿಕೊಳ್ಳುವವನಿಗೋಸ್ಕರ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ದೋಷಪರಿಹಾರವನ್ನು ಮಾಡುವದಕ್ಕಾಗಿ ಯಾಜಕನು ತನ್ನ ಕೈಯಲ್ಲಿರುವ ಮಿಕ್ಕ ಎಣ್ಣೆಯನ್ನೆಲ್ಲಾ ಅವನ ತಲೆಯ ಮೇಲೆ ಹೊಯ್ಯಬೇಕು.",
			"30": "<sup>30</sup> ತರುವಾಯ ಅವನು ತನ್ನ ಸ್ಥಿತಿಗೆ ತಕ್ಕಂತೆ ತಂದ ಎರಡು ಬೆಳವಕ್ಕಿಗಳಲ್ಲಿಯಾಗಲಿ ಪಾರಿವಾಳದ ಮರಿಗಳಲ್ಲಿಯಾಗಲಿ",
			"31": "<sup>31</sup> ದೋಷಪರಿಹಾರಕಯಜ್ಞವಾಗಿ ಒಂದನ್ನೂ ಸರ್ವಾಂಗಹೋಮವಾಗಿ ಒಂದನ್ನೂ ನೈವೇದ್ಯದ್ರವ್ಯವನ್ನೂ ಸಮರ್ಪಿಸಬೇಕು. ಹೀಗೆ ಯಾಜಕನು ಶುದ್ಧಮಾಡಿಸಿಕೊಳ್ಳುವವನಿಗೋಸ್ಕರ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ದೋಷಪರಿಹಾರವನ್ನು ಮಾಡುವನು.",
			"32": "<sup>32</sup> ಶುದ್ಧೀಕರಣಯಜ್ಞಗಳನ್ನು ಮಾಡುವದಕ್ಕೆ ಗತಿಯಿಲ್ಲದ ಕುಷ್ಠರೋಗಿಯ ವಿಷಯದಲ್ಲಿ ಇದೇ ವಿಧಿ.",
			"33": "<sup>33</sup> ಯೆಹೋವನು ಮೋಶೆ ಆರೋನರಿಗೆ ಹೇಳಿದ್ದೇನಂದರೆ -",
			"34": "<sup>34</sup> ನಿಮಗೆ ಸ್ವದೇಶವಾಗಿರುವದಕ್ಕೋಸ್ಕರ ನಾನು ಕೊಡುವ ಕಾನಾನ್ ದೇಶಕ್ಕೆ ನೀವು ಬಂದ ನಂತರ ಆ ದೇಶದ ಯಾವ ಮನೆಯ ಗೋಡೆಗಳಲ್ಲಿ ನಾನು ಕುಷ್ಠದ ಗುರುತನ್ನು ಉಂಟುಮಾಡುವೆನೋ",
			"35": "<sup>35</sup> ಆ ಮನೆಯ ಒಡೆಯನು ಯಾಜಕನ ಬಳಿಗೆ ಬಂದು - ನನ್ನ ಮನೆಯಲ್ಲಿ ಕುಷ್ಠರೋಗದ ಗುರುತು ಉಂಟಾದಂತೆ ತೋರುತ್ತದೆ ಎಂದು ಅವನಿಗೆ ತಿಳಿಸಬೇಕು.",
			"36": "<sup>36</sup> ಯಾಜಕನು ತಾನು ಆ ರೋಗದ ಗುರುತನ್ನು ನೋಡಲು ಬರುವದಕ್ಕೆ ಮುಂಚೆ ಆ ಮನೆಯನ್ನು ಬರಿದುಮಾಡಲಾಜ್ಞಾಪಿಸಬೇಕು. ಹಾಗೆ ಬರಿದುಮಾಡದಿದ್ದರೆ ಆ ಮನೆಯಲ್ಲಿರುವ ಎಲ್ಲಾ ವಸ್ತುಗಳೂ ಅಶುದ್ಧವಾದಾವು. ಬರಿದುಮಾಡಿದ ಮೇಲೆ ಯಾಜಕನು ಆ ಮನೆಯನ್ನು ನೋಡುವದಕ್ಕೆ ಒಳಗೆ ಹೋಗಬೇಕು.",
			"37": "<sup>37</sup> ಅವನು ಮನೆಯ ಗೋಡೆಗಳಲ್ಲಿರುವ ರೋಗದ ಗುರುತುಗಳನ್ನು ನೋಡುವಾಗ ಆ ಗುರುತುಗಳು ಹಸುರಾಗಿ ಅಥವಾ ಕೆಂಪಾಗಿ ಇದ್ದು ಗೋಡೆಯ ಮಟ್ಟಕ್ಕಿಂತ ತಗ್ಗಾಗಿ ತೋರಿದರೆ",
			"38": "<sup>38</sup> ಯಾಜಕನು ಹೊರಗೆ ಬಂದು ಆ ಮನೆಯನ್ನು ಏಳು ದಿನಗಳವರೆಗೂ ಮುಚ್ಚಿಸಿಬಿಡಬೇಕು.",
			"39": "<sup>39</sup> ಏಳನೆಯ ದಿನದಲ್ಲಿ ಯಾಜಕನು ಬಂದು ನೋಡುವಾಗ ಆ ಗುರುತು ಮನೆಯ ಗೋಡೆಗಳಲ್ಲಿ ಹಬ್ಬಿಕೊಂಡಿದ್ದರೆ",
			"40": "<sup>40</sup> ಆ ಗುರುತುಗಳು ಇರುವ ಕಲ್ಲುಗಳನ್ನು ತೆಗಿಸಿಬಿಟ್ಟು ಊರಿನ ಹೊರಗೆ ಅಪವಿತ್ರವಾದ ಸ್ಥಳದಲ್ಲಿ ಹಾಕಿಸಿಬಿಡಬೇಕು.",
			"41": "<sup>41</sup> ಅದಲ್ಲದೆ ಅವನು ಆ ಮನೆಯ ಗೋಡೆಗಳ ಒಳಗಡೆಯನ್ನೆಲ್ಲಾ ಗೀಚಿಸಿ ಗೀಚಿದ ಮಣ್ಣನ್ನು ಊರಿನ ಹೊರಗೆ ಅಪವಿತ್ರ ಸ್ಥಳದಲ್ಲಿ ಹಾಕಿಸಬೇಕು.",
			"42": "<sup>42</sup> ಆಗ ಬೇರೆ ಕಲ್ಲುಗಳನ್ನು ತರಿಸಿ ಮೊದಲಿದ್ದ ಕಲ್ಲುಗಳ ಸ್ಥಳದಲ್ಲಿ ಹಾಕಿಸಿ ಹೊಸ ಮಣ್ಣನ್ನು ತರಿಸಿ ಆ ಗೋಡೆಗಳಿಗೆ ಗಿಲಾವು ಮಾಡಿಸಬೇಕು.",
			"43": "<sup>43</sup> ಅವನು ಆ ಕಲ್ಲುಗಳನ್ನು ತೆಗಿಸಿ ಮನೆಯ ಗೋಡೆಗಳನ್ನು ಗೀಚಿಸಿ ಗಿಲಾವು ಮಾಡಿಸಿದ ಮೇಲೆ ಆ ರೋಗದ ಗುರುತು ತಿರಿಗಿ ಕಾಣಬಂದರೆ ಯಾಜಕನು ಬಂದು ನೋಡಬೇಕು.",
			"44": "<sup>44</sup> ಆ ಗುರುತು ಮನೆಯ ಗೋಡೆಗಳಲ್ಲಿ ಹಬ್ಬಿಕೊಂಡಿದ್ದರೆ ಅದು ಪ್ರಾಣಹಾನಿಕರವಾದ ಕುಷ್ಠವೇ; ಆ ಮನೆ ಅಶುದ್ಧವಾಗಿರುವದು.",
			"45": "<sup>45</sup> ಅವನು ಆ ಮನೆಯನ್ನು ಕೆಡಹಿಸಿ ಅದರ ಎಲ್ಲಾ ಕಲ್ಲುಗಳನ್ನೂ ತೊಲೆಗಳನ್ನೂ ಮಣ್ಣನ್ನೂ ಊರಿನ ಹೊರಗೆ ಅಪವಿತ್ರಸ್ಥಳದಲ್ಲಿ ಹಾಕಿಸಿಬಿಡಬೇಕು.",
			"46": "<sup>46</sup> ಆ ಮನೆಯು ಮುಚ್ಚಿರುವ ದಿನಗಳಲ್ಲಿ ಯಾವನಾದರೂ ಒಳಕ್ಕೆ ಹೋದರೆ ಅವನು ಆ ದಿನದ ಸಾಯಂಕಾಲದವರೆಗೆ ಅಶುದ್ಧನಾಗಿರುವನು.",
			"47": "<sup>47</sup> ಆ ಮನೆಯಲ್ಲಿ ಮಲಗಿಕೊಂಡವನು ತನ್ನ ಬಟ್ಟೆಗಳನ್ನು ಒಗೆದುಕೊಳ್ಳಬೇಕು; ಅಲ್ಲಿ ಊಟಮಾಡಿದವನೂ ಬಟ್ಟೆಗಳನ್ನು ಒಗೆದುಕೊಳ್ಳಬೇಕು.",
			"48": "<sup>48</sup> ಆದರೆ ತಾನು ಆ ಮನೆಗೆ ಹೊಸದಾಗಿ ಗಿಲಾವು ಮಾಡಿಸಿದ ಮೇಲೆ ಯಾಜಕನು ಬಂದು ನೋಡುವಾಗ ಆ ರೋಗದ ಗುರುತು ಕಾಣಿಸದೆಹೋದರೆ ರೋಗಪರಿಹಾರವಾಗಿ ಆ ಮನೆ ಶುದ್ಧವಾಯಿತೆಂದು ನಿರ್ಣಯಿಸಬೇಕು.",
			"49": "<sup>49</sup> ಆ ಮನೆಯ ಶುದ್ಧೀಕರಣಕ್ಕಾಗಿ ಅವನು ಎರಡು ಪಕ್ಷಿಗಳನ್ನೂ ದೇವದಾರಿನ ಕಟ್ಟಿಗೆಯನ್ನೂ ರಕ್ತವರ್ಣವುಳ್ಳ ದಾರವನ್ನೂ ಹಿಸ್ಸೋಪ್‍ಗಿಡದ ಬರಲನ್ನೂ ತೆಗೆದುಕೊಂಡು",
			"50": "<sup>50</sup> ಒಂದು ಪಕ್ಷಿಯನ್ನು ಸೆಲೇ ನೀರಿನ ಮೇಲೆ ಮಣ್ಣಿನ ಪಾತ್ರೆಯಲ್ಲಿ ವಧಿಸಿ",
			"51": "<sup>51</sup> ಆ ದೇವದಾರಿನ ಕಟ್ಟಿಗೆಯನ್ನೂ ಹಿಸ್ಸೋಪಿನ ಬರಲನ್ನೂ ರಕ್ತವರ್ಣವುಳ್ಳ ದಾರವನ್ನೂ ಸಜೀವವಾದ ಮತ್ತೊಂದು ಪಕ್ಷಿಯನ್ನೂ ತೆಗೆದುಕೊಂಡು ತಾನು ವಧಿಸಿದ ಪಕ್ಷಿಯ ರಕ್ತದಲ್ಲಿಯೂ ಸೆಲೇ ನೀರಿನಲ್ಲಿಯೂ ಅದ್ದಿ ಏಳು ಸಾರಿ ಆ ಮನೆಯ ಗೋಡೆಗಳಿಗೆ ಪ್ರೋಕ್ಷಿಸಬೇಕು.",
			"52": "<sup>52</sup> ಹಾಗೆ ಆ ಪಕ್ಷಿಯ ರಕ್ತ, ಸೆಲೇ ನೀರು, ಸಜೀವಪಕ್ಷಿ, ದೇವದಾರಿನ ಕಟ್ಟಿಗೆ, ಹಿಸ್ಸೋಪು, ರಕ್ತವರ್ಣದ ದಾರ ಇವುಗಳಿಂದ ಆ ಮನೆಯನ್ನು ಶುದ್ಧಿಮಾಡುವನು.",
			"53": "<sup>53</sup> ಅವನು ಆ ಜೀವವುಳ್ಳ ಪಕ್ಷಿಯನ್ನು ಊರ ಹೊರಕ್ಕೆ ಅಡವಿಯ ಕಡೆಗೆ ಬಿಟ್ಟುಬಿಡಬೇಕು. ಹೀಗೆ ಅವನು ಆ ಮನೆಗೋಸ್ಕರ ದೋಷಪರಿಹಾರಮಾಡಿದಾಗ ಅದು ಶುದ್ಧವಾಗಿರುವದು.",
			"54": "<sup>54</sup> ನಾನಾ ವಿಧವಾದ ಕುಷ್ಠರೋಗದ ಗುರುತುಗಳು ಅಂದರೆ ಕೂದಲು ಬೆಳೆಯದ ದದ್ದು,",
			"55": "<sup>55</sup> ಬಟ್ಟೆಯಲ್ಲಿ ಅಥವಾ ಮನೆಯ ಗೋಡೆಯಲ್ಲಿ ಕಾಣಿಸುವ ಕುಷ್ಠರೋಗದ ಗುರುತು,",
			"56": "<sup>56</sup> ಶರೀರದ ಚರ್ಮದ ಮೇಲೆ ಉಂಟಾಗುವ ಬಾವು, ಗುಳ್ಳೆ, ಹೊಳೆಯುವ ಕಲೆ",
			"57": "<sup>57</sup> ಇವುಗಳಲ್ಲಿ ಶುದ್ಧಾಶುದ್ಧ ಭೇದವನ್ನು ತಿಳಿಸುವ ವಿಧಿ ಇದೇ."
		},
		"15": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಮೋಶೆ ಆರೋನರಿಗೆ ಹೀಗಂದನು -",
			"2": "<sup>2</sup> ನೀವು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಆಜ್ಞಾಪಿಸಬೇಕಾದದ್ದೇನಂದರೆ - ಒಬ್ಬನ ರಹಸ್ಯಾಂಗದಲ್ಲಿ ಮೇಹಸ್ರಾವವಾದರೆ ಅದರ ದೆಸೆಯಿಂದ ಅವನು ಅಶುದ್ಧನಾಗುವನು.",
			"3": "<sup>3</sup> ಆ ಸ್ರಾವವು ಹರಿಯುತ್ತಿದ್ದರೂ ನಿಂತುಹೋಗಿದ್ದರೂ ಆ ದೇಹಸ್ಥಿತಿಯಿಂದ ಅವನು ಅಶುದ್ಧನು.",
			"4": "<sup>4</sup> ಅಂಥವನು ಮಲಗಿರುವ ಪ್ರತಿಯೊಂದು ಹಾಸಿಗೆಯೂ ಕೂತಿರುವ ಪ್ರತಿಯೊಂದು ಆಸನವೂ ಅಶುದ್ಧವು.",
			"5": "<sup>5</sup> ಅವನ ಹಾಸಿಗೆಯನ್ನು ಮುಟ್ಟಿದವನು ತನ್ನ ಬಟ್ಟೆಗಳನ್ನು ಒಗೆದುಕೊಂಡು ಸ್ನಾನಮಾಡಬೇಕು; ಅವನು ಆ ದಿನದ ಸಾಯಂಕಾಲದವರೆಗೆ ಅಶುದ್ಧನಾಗಿರುವನು.",
			"6": "<sup>6</sup> ಸ್ರಾವವುಳ್ಳವನು ಕೂತಿದ್ದ ಆಸನದ ಮೇಲೆ ಕೂತುಕೊಂಡವನು ತನ್ನ ಬಟ್ಟೆಗಳನ್ನು ಒಗೆದುಕೊಂಡು ಸ್ನಾನಮಾಡಬೇಕು; ಅವನು ಆ ಸಾಯಂಕಾಲದವರೆಗೆ ಅಶುದ್ಧನಾಗಿರುವನು.",
			"7": "<sup>7</sup> ಅವನ ಶರೀರವನ್ನು ಮುಟ್ಟಿದವನು ತನ್ನ ಬಟ್ಟೆಗಳನ್ನು ಒಗೆದುಕೊಂಡು ಸ್ನಾನಮಾಡಬೇಕು; ಅವನು ಆ ಸಾಯಂಕಾಲದವರೆಗೆ ಅಶುದ್ಧನಾಗಿರುವನು.",
			"8": "<sup>8</sup> ಶುದ್ಧನಾಗಿರುವವನ ಮೇಲೆ ಸ್ರಾವವುಳ್ಳವನು ಉಗುಳಿದರೆ ಅವನು ತನ್ನ ಬಟ್ಟೆಗಳನ್ನು ಒಗೆದುಕೊಂಡು ಸ್ನಾನಮಾಡಬೇಕು; ಅವನು ಆ ಸಾಯಂಕಾಲದವರೆಗೆ ಅಶುದ್ಧನಾಗಿರುವನು.",
			"9": "<sup>9</sup> ಸ್ರಾವವುಳ್ಳವನು ಕೂತಿದ್ದ ತಡಿಯು ಅಶುದ್ಧವು.",
			"10": "<sup>10</sup> ಅವನ ಕೆಳಗಿದ್ದ ಯಾವ ವಸ್ತುವನ್ನಾದರೂ ಮುಟ್ಟಿದವನು ಆ ಸಾಯಂಕಾಲದವರೆಗೆ ಅಶುದ್ಧನಾಗಿರುವನು. ಅಂಥ ವಸ್ತುವನ್ನು ಎತ್ತಿದವನು ತನ್ನ ಬಟ್ಟೆಗಳನ್ನು ಒಗೆದುಕೊಂಡು ಸ್ನಾನಮಾಡಬೇಕು; ಅವನು ಆ ಸಾಯಂಕಾಲದವರೆಗೆ ಅಶುದ್ಧನಾಗಿರುವನು.",
			"11": "<sup>11</sup> ಸ್ರಾವವುಳ್ಳವನು ಕೈಗಳನ್ನು ತೊಳೆದುಕೊಳ್ಳದೆ ಯಾವನನ್ನು ಮುಟ್ಟಿದರೂ ಆ ಮನುಷ್ಯನು ತನ್ನ ಬಟ್ಟೆಗಳನ್ನು ಒಗೆದುಕೊಂಡು ಸ್ನಾನಮಾಡಬೇಕು; ಅವನು ಆ ಸಾಯಂಕಾಲದವರೆಗೆ ಅಶುದ್ಧನಾಗಿರುವನು.",
			"12": "<sup>12</sup> ಸ್ರಾವವುಳ್ಳವನು ಮುಟ್ಟಿದ ಮಣ್ಣಿನ ಪಾತ್ರೆಯನ್ನು ಒಡೆದುಬಿಡಬೇಕು. ಅವನು ಮರದ ಪಾತ್ರೆಯನ್ನು ಮುಟ್ಟಿದರೆ ಅದನ್ನು ನೀರಿನಿಂದ ತೊಳೆಯಬೇಕು.",
			"13": "<sup>13</sup> ಸ್ರಾವವುಳ್ಳವನ ಸ್ರಾವವು ನಿಂತು ವಾಸಿಯಾದಾಗ ಅವನು ಶುದ್ಧಮಾಡಿಸಿಕೊಳ್ಳುವದಕ್ಕೆ ಆಗಿನಿಂದ ಏಳು ದಿನಗಳನ್ನು ಲೆಕ್ಕಿಸಿಕೊಂಡು ತನ್ನ ಬಟ್ಟೆಗಳನ್ನು ಒಗೆದುಕೊಂಡು ಸೆಲೇ ನೀರಿನಿಂದ ತನ್ನ ಶರೀರವನ್ನು ತೊಳೆದುಕೊಂಡು ಶುದ್ಧನಾಗುವನು.",
			"14": "<sup>14</sup> ಎಂಟನೆಯ ದಿನದಲ್ಲಿ ಎರಡು ಬೆಳವಕ್ಕಿಗಳನ್ನಾಗಲಿ ಎರಡು ಪಾರಿವಾಳದ ಮರಿಗಳನ್ನಾಗಲಿ ತೆಗೆದುಕೊಂಡು ದೇವದರ್ಶನದ ಗುಡಾರದ ಬಾಗಲಿಗೆ ಯೆಹೋವನ ಸನ್ನಿಧಿಗೆ ಬಂದು ಯಾಜಕನಿಗೆ ಒಪ್ಪಿಸಬೇಕು.",
			"15": "<sup>15</sup> ಯಾಜಕನು ದೋಷಪರಿಹಾರಕಯಜ್ಞಕ್ಕಾಗಿ ಒಂದನ್ನೂ ಸರ್ವಾಂಗಹೋಮಕ್ಕಾಗಿ ಇನ್ನೊಂದನ್ನೂ ಸಮರ್ಪಿಸಬೇಕು. ಹೀಗೆ ಯಾಜಕನು ಅವನ ಸ್ರಾವದ ವಿಷಯದಲ್ಲಿ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಅವನಿಗೋಸ್ಕರ ದೋಷಪರಿಹಾರ ಮಾಡುವನು.",
			"16": "<sup>16</sup> ಒಬ್ಬನಿಗೆ ವೀರ್ಯಸ್ಖಲನವಾದರೆ ಅವನು ಸರ್ವಾಂಗ ಸ್ನಾನಮಾಡಬೇಕು; ಅವನು ಆ ಸಾಯಂಕಾಲದವರೆಗೆ ಅಶುದ್ಧನಾಗಿರುವನು.",
			"17": "<sup>17</sup> ಯಾವ ಬಟ್ಟೆಯ ಮೇಲಾಗಲಿ ತೊಗಲಿನ ಮೇಲಾಗಲಿ ವೀರ್ಯವು ಬಿದ್ದರೆ ಅದನ್ನು ನೀರಿನಿಂದ ತೊಳೆಯಬೇಕು. ಅದು ಸಾಯಂಕಾಲದವರೆಗೆ ಅಶುದ್ಧವಾಗಿರುವದು.",
			"18": "<sup>18</sup> ಪುರುಷನಿಗೆ ಸ್ತ್ರೀಸಂಗದಿಂದ ವೀರ್ಯಸ್ಖಲನವಾದರೆ ಅವರಿಬ್ಬರೂ ಸ್ನಾನಮಾಡಿಕೊಳ್ಳಬೇಕು; ಅವರು ಸಾಯಂಕಾಲದವರೆಗೆ ಅಶುದ್ಧರಾಗಿರುವರು.",
			"19": "<sup>19</sup> ಸ್ತ್ರೀಗೆ ರಕ್ತಸ್ರಾವವಾದರೆ ಅವಳು ಏಳು ದಿನಗಳ ತನಕ ಪ್ರತ್ಯೇಕವಾಗಿರಬೇಕು. ಅವಳನ್ನು ಮುಟ್ಟಿದವರು ಆ ದಿನದ ಸಾಯಂಕಾಲದವರೆಗೆ ಅಶುದ್ಧರಾಗುವರು.",
			"20": "<sup>20</sup> ಅವಳು ಮುಟ್ಟಾಗಿರುವಾಗ ಯಾವದರ ಮೇಲೆ ಮಲಗಿದರೂ ಕೂತುಕೊಂಡರೂ ಅದು ಅಶುದ್ಧವಾಗಿರುವದು.",
			"21": "<sup>21</sup> ಅವಳ ಹಾಸಿಗೆಯನ್ನು ಮುಟ್ಟಿದವನು ತನ್ನ ಬಟ್ಟೆಗಳನ್ನು ಒಗೆದುಕೊಂಡು ಸ್ನಾನಮಾಡಬೇಕು; ಅವನು ಸಾಯಂಕಾಲದವರೆಗೆ ಅಶುದ್ಧನಾಗಿರುವನು.",
			"22": "<sup>22</sup> ಅವಳು ಯಾವದರ ಮೇಲೆ ಕೂತಿದ್ದಳೋ ಅದನ್ನು ಮುಟ್ಟಿದವನು ತನ್ನ ಬಟ್ಟೆಗಳನ್ನು ಒಗೆದುಕೊಂಡು ಸ್ನಾನ ಮಾಡಬೇಕು; ಅವನು ಸಾಯಂಕಾಲದವರೆಗೆ ಅಶುದ್ಧನಾಗಿರುವನು.",
			"23": "<sup>23</sup> ಅವಳು ಮಲಗಿದ್ದ ಹಾಸಿಗೆಯ ಮೇಲಾಗಲಿ ಕೂತಿದ್ದ ಸ್ಥಳದ ಮೇಲಾಗಲಿ ಇದ್ದ ಯಾವದನ್ನಾದರೂ ಮುಟ್ಟಿದವನು ಸಾಯಂಕಾಲದವರೆಗೆ ಅಶುದ್ಧನಾಗಿರುವನು.",
			"24": "<sup>24</sup> ಪುರುಷನು ಒಂದು ವೇಳೆ ಅಂಥವಳೊಡನೆ ಸಂಗಮಿಸಿದದರಿಂದ ಅವಳ ರಜಸ್ಸು ಅವನಿಗೆ ತಗಲಿದರೆ ಅವನು ಏಳು ದಿನಗಳ ತನಕ ಅಶುದ್ಧನಾಗಿರುವನು. ಅಷ್ಟರಲ್ಲಿ ಅವನು ಯಾವ ಹಾಸಿಗೆಯ ಮೇಲೆ ಮಲಗಿಕೊಂಡರೂ ಅದು ಅಶುದ್ಧವಾಗಿರುವದು.",
			"25": "<sup>25</sup> ಸ್ತ್ರೀಗೆ ಮುಟ್ಟಿನ ಕಾಲದಲ್ಲಿ ಮಾತ್ರವಲ್ಲದೆ ಬೇರೆ ದಿನಗಳಲ್ಲಿಯೂ ರಕ್ತಸ್ರಾವವಾದರೆ ಇಲ್ಲವೆ ಮುಟ್ಟಿನ ಕಾಲಕ್ಕೆ ತಕ್ಕದ್ದಕ್ಕಿಂತ ಹೆಚ್ಚು ದಿನಗಳು ರಕ್ತಸ್ರಾವವಾದರೆ ಅವಳು ತಿಂಗಳಿನ ಮುಟ್ಟಿನಿಂದ ಹೇಗೆ ಅಶುದ್ಧಳೋ ಹಾಗೆಯೇ ಆ ಸ್ರಾವವಾಗುವ ದಿನಗಳಲ್ಲೆಲ್ಲಾ ಅಶುದ್ಧಳಾಗಿರುವಳು.",
			"26": "<sup>26</sup> ಆ ಸ್ರಾವವಿರುವ ತನಕ ಅವಳು ಯಾವ ಹಾಸಿಗೆಯ ಮೇಲೆ ಮಲಗಿದರೂ ಅದು ಮುಟ್ಟಿನ ಕಾಲದಲ್ಲಿ ಮಲಗಿದ ಹಾಸಿಗೆಯಂತೆಯೇ ಅಶುದ್ಧವಾಗುವದು. ಅವಳು ಯಾವದರ ಮೇಲೆ ಕೂತುಕೊಂಡರೂ ಅದು ಮುಟ್ಟಿನ ಕಾಲದಲ್ಲಿ ಕೂತುಕೊಂಡ ವಸ್ತುವಿನಂತೆ ಅಶುದ್ಧವಾಗಿರುವದು.",
			"27": "<sup>27</sup> ಆ ವಸ್ತುಗಳನ್ನು ಮುಟ್ಟಿದವನು ಅಶುದ್ಧನಾದದರಿಂದ ತನ್ನ ಬಟ್ಟೆಗಳನ್ನು ಒಗೆದುಕೊಂಡು ಸ್ನಾನಮಾಡಬೇಕು; ಅವನು ಸಾಯಂಕಾಲದವರೆಗೆ ಅಶುದ್ಧನಾಗಿರುವನು.",
			"28": "<sup>28</sup> ಅವಳಿಗೆ ರಕ್ತಸ್ರಾವ ನಿಂತ ದಿನದಿಂದ ಅವಳು ಏಳು ದಿನಗಳನ್ನು ಲೆಕ್ಕಿಸಿಕೊಂಡು ತರುವಾಯ ಶುದ್ಧಳಾಗುವಳು.",
			"29": "<sup>29</sup> ಎಂಟನೆಯ ದಿನದಲ್ಲಿ ಅವಳು ಎರಡು ಬೆಳವಕ್ಕಿಗಳನ್ನಾಗಲಿ ಎರಡು ಪಾರಿವಾಳದ ಮರಿಗಳನ್ನಾಗಲಿ ದೇವದರ್ಶನದ ಗುಡಾರದ ಬಾಗಲಿಗೆ ಯಾಜಕನ ಬಳಿಗೆ ತರಬೇಕು.",
			"30": "<sup>30</sup> ಯಾಜಕನು ದೋಷಪರಿಹಾರಕಯಜ್ಞಕ್ಕಾಗಿ ಒಂದನ್ನೂ ಸರ್ವಾಂಗಹೋಮಕ್ಕಾಗಿ ಇನ್ನೊಂದನ್ನೂ ಸಮರ್ಪಿಸಬೇಕು. ಹೀಗೆ ಯಾಜಕನು ಅವಳ ಸ್ರಾವದಿಂದುಂಟಾದ ಅಪರಿಶುದ್ಧತೆಯ ವಿಷಯವಾಗಿ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಅವಳಿಗೋಸ್ಕರ ದೋಷಪರಿಹಾರ ಮಾಡುವನು.",
			"31": "<sup>31</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಅಶುದ್ಧತೆಗೆ ದೂರವಾಗಿರುವಂತೆ ನೀವು ಅವರನ್ನು ಈ ರೀತಿಯಾಗಿ ಕಾಪಾಡಬೇಕು. ಅವರು ಅಪವಿತ್ರರಾಗಿದ್ದು ತಮ್ಮ ಮಧ್ಯದಲ್ಲಿರುವ ನನ್ನ ಗುಡಾರವನ್ನು ಹೊಲೆಮಾಡಿದರೆ ನಾಶವಾದಾರು.",
			"32": "<sup>32</sup> ಮೇಹಸ್ರಾವವುಳ್ಳವನು, ವೀರ್ಯಸ್ಖಲನದಿಂದ ಅಶುದ್ಧನಾದವನು,",
			"33": "<sup>33</sup> ಮುಟ್ಟಾದ ಸ್ತ್ರೀಯು, ಸ್ರಾವವುಂಟಾಗುವ ಸ್ತ್ರೀಪುರುಷರು, ಅಶುದ್ಧಳಾದ ಸ್ತ್ರೀಯನ್ನು ಸಂಗಮಿಸಿದವನು ಇವರ ವಿಷಯವಾಗಿ ಇದೇ ವಿಧಿ."
		},
		"16": {
			"1": "<sup>1</sup> ಆರೋನನ ಇಬ್ಬರು ಮಕ್ಕಳು ಯೆಹೋವನ ಸನ್ನಿಧಿಗೆ ಬಂದು ನಾಶವಾದ ಮೇಲೆ",
			"2": "<sup>2</sup> ಯೆಹೋವನು ಮೋಶೆಯ ಸಂಗಡ ಮಾತಾಡಿ ಹೇಳಿದ್ದೇನಂದರೆ - ನಿನ್ನ ಅಣ್ಣನಾದ ಆರೋನನು ಮನಸ್ಸಿಗೆ ಬಂದಾಗೆಲ್ಲಾ ತೆರೆಯನ್ನು ದಾಟಿ ಮಹಾಪವಿತ್ರಸ್ಥಾನದೊಳಕ್ಕೆ ಮಂಜೂಷದ ಮೇಲಣ ಕೃಪಾಸನದ ಹತ್ತಿರಕ್ಕೆ ಬರಬಾರದೆಂದು ಅವನಿಗೆ ಹೇಳು. ನಾನು ಕೃಪಾಸನದ ಮೇಲೆ ಮೇಘದಲ್ಲಿ ಕಾಣಿಸಿಕೊಳ್ಳುವದರಿಂದ ಅವನು ನಾಶವಾದಾನು.",
			"3": "<sup>3</sup> ಆರೋನನು ಮಹಾಪವಿತ್ರಸ್ಥಾನದೊಳಗೆ ಬರುವಾಗ ಮುಂದೆ ಹೇಳುವ ರೀತಿಯಾಗಿ ಬರಬೇಕು. ಅವನು ದೋಷಪರಿಹಾರಕಯಜ್ಞಕ್ಕಾಗಿ ಹೋರಿಯನ್ನೂ ಸರ್ವಾಂಗಹೋಮಕ್ಕಾಗಿ ಟಗರನ್ನೂ ತರಬೇಕು.",
			"4": "<sup>4</sup> ಅವನು ಪರಿಶುದ್ಧವಾದ ನಾರಿನ ನಿಲುವಂಗಿಯನ್ನು ತೊಟ್ಟುಕೊಂಡು ನಾರಿನ ಚಡ್ಡಿಯನ್ನು ಹಾಕಿಕೊಂಡು ನಾರಿನ ನಡುಕಟ್ಟನ್ನು ಕಟ್ಟಿಕೊಂಡು ತಲೆಗೆ ಮುಂಡಾಸವನ್ನು ಸುತ್ತಿಕೊಳ್ಳಬೇಕು. ಅವು ದೀಕ್ಷಾವಸ್ತ್ರಗಳಾದದರಿಂದ ಅವನು ಅವುಗಳನ್ನು ಧರಿಸಿಕೊಳ್ಳುವದಕ್ಕೆ ಮುಂಚೆ ಸ್ನಾನಮಾಡಬೇಕು.",
			"5": "<sup>5</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಜನಸಮೂಹದಿಂದ ದೋಷಪರಿಹಾರಕಯಜ್ಞಕ್ಕಾಗಿ ಎರಡು ಹೋತಗಳನ್ನೂ ಸರ್ವಾಂಗಹೋಮಕ್ಕಾಗಿ ಒಂದು ಟಗರನ್ನೂ ತೆಗೆದುಕೊಂಡು ಬರಬೇಕು.",
			"6": "<sup>6</sup> ಆರೋನನು ತನಗೋಸ್ಕರವಾಗಿ ತಂದ ಹೋರಿಯನ್ನು ಸಮರ್ಪಿಸಿ ತನಗೋಸ್ಕರವೂ ತನ್ನ ಮನೆತನದವರಿಗೋಸ್ಕರವೂ ದೋಷಪರಿಹಾರ ಮಾಡಬೇಕು.",
			"7": "<sup>7</sup> ಆ ಎರಡು ಹೋತಗಳನ್ನು ಅವನು ದೇವದರ್ಶನದ ಗುಡಾರದ ಬಾಗಲಲ್ಲಿ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನಿಲ್ಲಿಸಬೇಕು.",
			"8": "<sup>8</sup> ಆಗ ಅವನು ಆ ಹೋತಗಳ ವಿಷಯವಾಗಿ ಯೆಹೋವನಿಗೋಸ್ಕರವೆಂದೂ ಅಜಾಜೇಲನಿಗೋಸ್ಕರವೆಂದೂ ಚೀಟುಗಳನ್ನು ಬರೆದುಹಾಕಬೇಕು.",
			"9": "<sup>9</sup> ಯಾವ ಹೋತ ಯೆಹೋವನದೆಂದು ಗೊತ್ತಾಗುವದೋ ಅದನ್ನು ಆರೋನನು ದೋಷಪರಿಹಾರಕ ಯಜ್ಞರೂಪವಾಗಿ ಸಮರ್ಪಿಸಬೇಕು.",
			"10": "<sup>10</sup> ಯಾವ ಹೋತ ಅಜಾಜೇಲನದೆಂದು ಗೊತ್ತಾಗುವದೋ ಅದನ್ನು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸಜೀವವಾಗಿಯೇ ನಿಲ್ಲಿಸಿ ಅದರ ಮೇಲೆ ದೋಷಹೊರಿಸಿ ಅರಣ್ಯಕ್ಕೆ ಅಜಾಜೇಲನಿಗಾಗಿ ಬಿಡಿಸಿಬಿಡಬೇಕು.",
			"11": "<sup>11</sup> ಆರೋನನು ತನಗೋಸ್ಕರವಾದ ದೋಷಪರಿಹಾರಕಯಜ್ಞದ ಹೋರಿಯನ್ನು ಸಮರ್ಪಿಸಿ ತನಗೋಸ್ಕರವೂ ತನ್ನ ಮನೆತನದವರಿಗೋಸ್ಕರವೂ ದೋಷಪರಿಹಾರವನ್ನು ಮಾಡಬೇಕು.",
			"12": "<sup>12</sup> ಅವನು ತನಗೋಸ್ಕರವಾದ ಹೋರಿಯನ್ನು ವಧಿಸಿದ ಮೇಲೆ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿರುವ ವೇದಿಯಿಂದ ಕೆಂಡಗಳನ್ನು ಧೂಪಾರತಿಯಲ್ಲಿ ತುಂಬಿಸಿ ಪರಿಮಳಧೂಪದ್ರವ್ಯದ ಚೂರ್ಣದಲ್ಲಿ ಎರಡು ಹಿಡಿ ತೆಗೆದುಕೊಂಡು ತೆರೆಯನ್ನು ದಾಟಿ",
			"13": "<sup>13</sup> ಆ ಧೂಪವನ್ನು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿಯೇ ಕೆಂಡಗಳ ಮೇಲೆ ಹಾಕಬೇಕು. ಅವನಿಗೆ ಪ್ರಾಣನಷ್ಟವಾಗದಂತೆ ಧೂಪದ ಹೊಗೆ ಮೇಘದೋಪಾದಿಯಲ್ಲಿ ಆಜ್ಞಾಶಾಸನಗಳ ಮೇಲಣ ಕೃಪಾಸನವನ್ನು ಮುಚ್ಚುವದು.",
			"14": "<sup>14</sup> ಆಗ ಆ ಹೋರಿಯ ರಕ್ತದಲ್ಲಿ ಸ್ವಲ್ಪ ತೆಗೆದುಕೊಂಡು ಕೃಪಾಸನದ ಪೂರ್ವಭಾಗದ ಮೇಲೆ ಬೆರಳಿನಿಂದ ಚಿಮಿಕಿಸಿ ಕೃಪಾಸನದ ಎದುರಾಗಿಯೂ ಏಳು ಬಾರಿ ಚಿಮಿಕಿಸಬೇಕು.",
			"15": "<sup>15</sup> ತರುವಾಯ ಅವನು ಜನರಿಗೋಸ್ಕರವಾದ ದೋಷಪರಿಹಾರಕಯಜ್ಞದ ಹೋತವನ್ನು ವಧಿಸಿ ಅದರ ರಕ್ತವನ್ನು ತೆರೆಯೊಳಗೆ ತಂದು ಹೋರಿಯ ರಕ್ತದಿಂದ ಮಾಡಿದಂತೆಯೇ ಇದರ ರಕ್ತದಿಂದಲೂ ಮಾಡಿ ಕೃಪಾಸನದ ಮೇಲೆಯೂ ಎದುರಾಗಿಯೂ ಚಿಮಿಕಿಸಬೇಕು.",
			"16": "<sup>16</sup> ಹೀಗೆ ಇಸ್ರಾಯೇಲ್ಯರು ನಾನಾ ವಿಧವಾದ ಅಪರಿಶುದ್ಧತ್ವ, ಅಪರಾಧ, ದ್ರೋಹ ಇವುಗಳಿಂದ ಮಹಾಪವಿತ್ರಸ್ಥಾನಕ್ಕೆ ಉಂಟುಮಾಡಿದ ದೋಷವನ್ನು ಆರೋನನು ಪರಿಹರಿಸುವನು. ಅಶುದ್ಧರಾದ ಅವರ ನಡುವೆ ಇರುವ ದೇವದರ್ಶನದ ಗುಡಾರಕ್ಕೋಸ್ಕರವೂ ಹಾಗೆಯೇ ದೋಷಪರಿಹಾರ ಮಾಡುವನು.",
			"17": "<sup>17</sup> ಅವನು ದೋಷಪರಿಹಾರಮಾಡುವದಕ್ಕಾಗಿ ಮಹಾಪವಿತ್ರಸ್ಥಾನದೊಳಗೆ ಹೋಗಿ ಹೊರಗೆ ಬರುವ ತನಕ ಯಾರೂ ದೇವದರ್ಶನದ ಗುಡಾರದಲ್ಲಿ ಹೋಗಕೂಡದು. ಹಾಗೆ ತನಗೋಸ್ಕರವಾಗಿಯೂ ತನ್ನ ಮನೆತನದವರಿಗೋಸ್ಕರವಾಗಿಯೂ ಇಸ್ರಾಯೇಲ್ಯರ ಎಲ್ಲಾ ಜನಸಮೂಹಕ್ಕೋಸ್ಕರವಾಗಿಯೂ ದೋಷಪರಿಹಾರಮಾಡುವನು.",
			"18": "<sup>18</sup> ತರುವಾಯ ಅವನು ಹೊರಗೆ ಬಂದು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿರುವ ಯಜ್ಞವೇದಿಯ ಬಳಿಗೆ ಹೋಗಿ ಅದಕ್ಕಾಗಿ ದೋಷಪರಿಹಾರ ಮಾಡಬೇಕು. ಆ ಹೋರಿಯ ರಕ್ತದಲ್ಲಿಯೂ ಆ ಹೋತನ ರಕ್ತದಲ್ಲಿಯೂ ಸ್ವಲ್ಪ ಸ್ವಲ್ಪ ತೆಗೆದುಕೊಂಡು ವೇದಿಯ ಕೊಂಬುಗಳಿಗೆ ಸುತ್ತಲೂ ಹಚ್ಚಬೇಕು.",
			"19": "<sup>19</sup> ಅವನು ಆ ರಕ್ತದಲ್ಲಿ ಸ್ವಲ್ಪವನ್ನು ಅದರ ಮೇಲೆ ಏಳು ಸಾರಿ ಬೆರಳಿನಿಂದ ಚಿಮಿಕಿಸಿ ಇಸ್ರಾಯೇಲ್ಯರಿಂದ ಅದಕ್ಕುಂಟಾದ ಅಶುದ್ಧತ್ವವನ್ನು ಹೋಗಲಾಡಿಸಿ ಅದನ್ನು ಪರಿಶುದ್ಧಪಡಿಸಬೇಕು.",
			"20": "<sup>20</sup> ಆರೋನನು ಮಹಾಪವಿತ್ರಸ್ಥಾನ, ದೇವದರ್ಶನದ ಗುಡಾರ, ವೇದಿ ಇವುಗಳಿಗೋಸ್ಕರ ದೋಷಪರಿಹಾರಮಾಡಿ ಮುಗಿಸಿದ ಮೇಲೆ ಆ ಸಜೀವವಾದ ಹೋತವನ್ನು ತರಿಸಿ",
			"21": "<sup>21</sup> ಅದರ ತಲೆಯ ಮೇಲೆ ಎರಡು ಕೈಗಳನ್ನೂ ಇಟ್ಟು ಇಸ್ರಾಯೇಲ್ಯರ ಎಲ್ಲಾ ಪಾಪಗಳನ್ನೂ ದ್ರೋಹಗಳನ್ನೂ ಅಪರಾಧಗಳನ್ನೂ ಯೆಹೋವನಿಗೆ ಅರಿಕೆಮಾಡಿ ಆ ಹೋತದ ತಲೆಯ ಮೇಲೆ ಹೊರಿಸಿ ಅದನ್ನು ಆ ಕೆಲಸಕ್ಕೆ ನೇಮಕವಾದವನ ಕೈಯಿಂದ ಅರಣ್ಯಕ್ಕೆ ಕಳುಹಿಸಿಬಿಡಬೇಕು.",
			"22": "<sup>22</sup> ಆ ಹೋತ ಅವರ ಎಲ್ಲಾ ಪಾಪಗಳನ್ನೂ ತನ್ನ ಮೇಲೆ ಹೊತ್ತುಕೊಂಡು ದುರ್ಗಮವಾದ ಪ್ರದೇಶಕ್ಕೆ ಒಯ್ಯುವಂತೆ ಆ ಮನುಷ್ಯನು ಅದನ್ನು ಅರಣ್ಯಕ್ಕೆ ತೆಗೆದುಕೊಂಡುಹೋಗಿ ಅಲ್ಲೇ ಬಿಟ್ಟುಬಿಡಬೇಕು.",
			"23": "<sup>23</sup> ತರುವಾಯ ಆರೋನನು ದೇವದರ್ಶನದ ಗುಡಾರದೊಳಗೆ ಬಂದು ತಾನು ಮಹಾಪವಿತ್ರಸ್ಥಾನದೊಳಗೆ ಹೋಗುವದಕ್ಕಾಗಿ ಧರಿಸಿಕೊಂಡಿದ್ದ ನಾರುಮಡಿಗಳನ್ನು ತೆಗೆದು ಅಲ್ಲೇ ಇಟ್ಟುಬಿಟ್ಟು ಪ್ರಾಕಾರದಲ್ಲಿ ಸ್ನಾನಮಾಡಿ ತನ್ನ ವಸ್ತ್ರಗಳನ್ನು ಧರಿಸಿಕೊಳ್ಳಬೇಕು.",
			"24": "<sup>24</sup> ಆಗ ಅವನು ಹೊರಗೆ ಬಂದು ತನಗೋಸ್ಕರವೂ ಜನಸಮೂಹಕ್ಕೋಸ್ಕರವೂ ಸರ್ವಾಂಗಹೋಮಗಳನ್ನು ಸಮರ್ಪಿಸಿ ತನಗೋಸ್ಕರವಾಗಿಯೂ ಜನರಿಗೋಸ್ಕರವಾಗಿಯೂ ದೋಷಪರಿಹಾರಮಾಡಬೇಕು.",
			"25": "<sup>25</sup> ಅವನು ದೋಷಪರಿಹಾರಕಯಜ್ಞ ಪಶುಗಳ ಕೊಬ್ಬನ್ನು ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಹೋಮಮಾಡಬೇಕು.",
			"26": "<sup>26</sup> ಅಜಾಜೇಲನಿಗೆ ಹೋತವನ್ನು ಬಿಟ್ಟು ಬಂದವನು ತನ್ನ ಬಟ್ಟೆಗಳನ್ನು ಒಗೆದುಕೊಂಡು ಸ್ನಾನಮಾಡಿಯೇ ಪಾಳೆಯಕ್ಕೆ ಬರಬೇಕು.",
			"27": "<sup>27</sup> ದೋಷಪರಿಹಾರಕಯಜ್ಞಪಶುಗಳಾದ ಹೋರಿಹೋತಗಳ ರಕ್ತವನ್ನು ಮಹಾಪವಿತ್ರಸ್ಥಾನದಲ್ಲಿ ದೋಷಪರಿಹಾರ ಮಾಡುವದಕ್ಕಾಗಿ ತಂದ ಮೇಲೆ ಅವುಗಳ ಶರೀರಗಳನ್ನು ಪಾಳೆಯದ ಆಚೆಗೆ ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ಚರ್ಮಮಾಂಸ ಕಲ್ಮಷಗಳೆಲ್ಲವನ್ನೂ ಬೆಂಕಿಯಿಂದ ಸುಡಿಸಿಬಿಡಬೇಕು.",
			"28": "<sup>28</sup> ಅವುಗಳನ್ನು ಸುಟ್ಟವನು ತನ್ನ ಬಟ್ಟೆಗಳನ್ನು ಒಗೆದುಕೊಂಡು ಸ್ನಾನ ಮಾಡಿಯೇ ಪಾಳೆಯದೊಳಗೆ ಬರಬೇಕು.",
			"29": "<sup>29</sup> ಇದು ನಿಮಗೆ ಶಾಶ್ವತವಾದ ನಿಯಮ; ಯಾವದಂದರೆ ಸ್ವದೇಶಸ್ಥರಾದ ನೀವೂ ನಿಮ್ಮಲ್ಲಿ ಇಳುಕೊಂಡಿರುವ ಅನ್ಯದೇಶದವರೂ ಎಲ್ಲರೂ ಏಳನೆಯ ತಿಂಗಳಿನ ಹತ್ತನೆಯ ದಿನದಲ್ಲಿ ಸಕಲ ವಿಧವಾದ ಕೆಲಸಗಳನ್ನೂ ಬಿಟ್ಟು [ಉಪವಾಸಮಾಡಿ] ಪ್ರಾಣವನ್ನು ಕುಂದಿಸಿಕೊಳ್ಳಬೇಕು.",
			"30": "<sup>30</sup> ಯಾಕಂದರೆ ನೀವು ಪರಿಶುದ್ಧರಾಗುವದಕ್ಕಾಗಿ ಈ ದಿನದಲ್ಲಿ ನಿಮಗೋಸ್ಕರ ದೋಷಪರಿಹಾರವಾಗುವದು, ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ನಿಮ್ಮ ಎಲ್ಲಾ ದೋಷಗಳು ನಿವಾರಣೆಯಾಗುವವು.",
			"31": "<sup>31</sup> ಇದು ನಿಮಗೆ ಸಂಪೂರ್ಣವಾಗಿ ಕೆಲಸವನ್ನು ನಿಲ್ಲಿಸಬೇಕಾದ ಸಬ್ಬತ್ ದಿನವಾಗಿರಬೇಕು; ಇದರಲ್ಲಿ ನೀವು ಉಪವಾಸವಾಗಿರಬೇಕು; ಇದೇ ನಿಮಗೆ ಶಾಶ್ವತ ನಿಯಮ.",
			"32": "<sup>32</sup> [ಆರೋನನ ವಂಶದವರಲ್ಲಿ] ಯಾವನಿಗೆ ಪಿತೃಸ್ಥಾನದಲ್ಲಿ ಮಹಾಯಾಜಕನ ಉದ್ಯೋಗವನ್ನು ನಡಿಸುವದಕ್ಕೆ ಪಟ್ಟಾಭಿಷೇಕವಾಗುವದೋ ಅವನೇ ಈ ದೋಷಪರಿಹಾರಕಾರ್ಯವನ್ನು ಆಚರಿಸಬೇಕು;",
			"33": "<sup>33</sup> ಅವನೇ ಆ ನಾರುಮಡಿಗಳನ್ನು ಧರಿಸಿಕೊಂಡು ಪರಿಶುದ್ಧವಾದ ದೇವಸ್ಥಾನ, ದೇವದರ್ಶನದ ಗುಡಾರ, ಯಜ್ಞವೇದಿ, ಯಾಜಕರು, ಜನಸಮೂಹದವರು ಈ ಎಲ್ಲರಿಗೋಸ್ಕರ ದೋಷಪರಿಹಾರವನ್ನು ಮಾಡಬೇಕು.",
			"34": "<sup>34</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಎಲ್ಲಾ ದೋಷಗಳನ್ನು ನಿವಾರಣೆಮಾಡುವದಕ್ಕಾಗಿ ಅವರಿಗೋಸ್ಕರ ವರುಷಕ್ಕೆ ಒಂದಾವರ್ತಿ ದೋಷಪರಿಹಾರವನ್ನು ಮಾಡಬೇಕೆಂಬದೇ ನಿಮಗೆ ಶಾಶ್ವತನಿಯಮ. ಯೆಹೋವನು ಮೋಶೆಗೆ ಆಜ್ಞಾಪಿಸಿದಂತೆಯೇ ಆರೋನನು ಮಾಡಿದನು."
		},
		"17": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಮೋಶೆಗೆ ಹೇಳಿದ್ದೇನಂದರೆ -",
			"2": "<sup>2</sup> ನೀನು ಆರೋನನಿಗೂ ಅವನ ಮಕ್ಕಳಿಗೂ ಇಸ್ರಾಯೇಲ್ಯರಿಗೂ ಹೀಗೆ ಹೇಳಬೇಕು. ಯೆಹೋವನು ಆಜ್ಞಾಪಿಸುವದೇನಂದರೆ -",
			"3": "<sup>3</sup> ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಯಾವನಾದರೂ ಹೋರಿಯನ್ನಾಗಲಿ ಕುರಿಯನ್ನಾಗಲಿ ಆಡನ್ನಾಗಲಿ ಕೊಯ್ಯುವಾಗ ಅದನ್ನು ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಿಸುವದಕ್ಕಾಗಿ ಆತನ ಗುಡಾರದ ಬಾಗಲಿಗೆ ತಾರದೆ",
			"4": "<sup>4</sup> ಪಾಳೆಯದ ಒಳಗಾಗಲಿ ಹೊರಗಾಗಲಿ ಕೊಯಿದರೆ ಅವನನ್ನು ಕೊಲೆಪಾತಕ ಮಾಡಿದವನೆಂದು ನೀವು ನಿರ್ಣಯಿಸಬೇಕು. ಅವನು ರಕ್ತವನ್ನು ಸುರಿಸಿದವನಾದದರಿಂದ ಕುಲದಿಂದ ತೆಗೆದುಹಾಕಲ್ಪಡಬೇಕು.",
			"5": "<sup>5</sup> ಯಾಕಂದರೆ ಯಜ್ಞಪಶುಗಳನ್ನು ಬೈಲಿನಲ್ಲಿ ವಧಿಸುತ್ತಿದ್ದ ಇಸ್ರಾಯೇಲ್ಯರು ಇನ್ನು ಮೇಲೆ ಅವುಗಳನ್ನು ದೇವದರ್ಶನದ ಗುಡಾರದ ಬಾಗಲಿಗೆ ಯಾಜಕನ ಬಳಿಗೆ ತಂದು ಯೆಹೋವನ ಮುಂದೆ ಸಮಾಧಾನಯಜ್ಞರೂಪವಾಗಿ ಯೆಹೋವನಿಗೇ ಸಮರ್ಪಿಸಬೇಕು.",
			"6": "<sup>6</sup> ಯಾಜಕನು ಅವುಗಳ ರಕ್ತವನ್ನು ದೇವದರ್ಶನದ ಗುಡಾರದ ಬಾಗಲಿನ ಮುಂದಿರುವ ಯಜ್ಞವೇದಿಗೆ ಎರಚಿ ಅವುಗಳ ಕೊಬ್ಬನ್ನು ಹೋಮಮಾಡಿ ಯೆಹೋವನಿಗೆ ಸುವಾಸನೆಯನ್ನುಂಟುಮಾಡಬೇಕು.",
			"7": "<sup>7</sup> ಅವರು ಇದುವರೆಗೆ ಪೂಜಿಸುತ್ತಿದ್ದ ಅಜದೇವತೆಗಳಿಗೆ ಇನ್ನು ಮುಂದೆ ಬಲಿಕೊಟ್ಟು ದೇವದ್ರೋಹಿಗಳಾಗಬಾರದು. ಅವರಿಗೂ ಅವರ ಸಂತತಿಯವರಿಗೂ ಇದು ಶಾಶ್ವತ ನಿಯಮ.",
			"8": "<sup>8</sup> ಮತ್ತು ನೀನು ಅವರಿಗೆ ಹೀಗೆ ಆಜ್ಞಾಪಿಸು - ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಆಗಲಿ ಅವರ ನಡುವೆ ಇಳುಕೊಂಡಿರುವ ಅನ್ಯದೇಶದವರಲ್ಲಿಯಾಗಲಿ ಯಾವನಾದರೂ ಸರ್ವಾಂಗಹೋಮವನ್ನು ಅಥವಾ ಬೇರೆ ವಿಧವಾದ ಯಜ್ಞವನ್ನು ಮಾಡುವಾಗ",
			"9": "<sup>9</sup> ಅದರ ಪಶುವನ್ನು ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಿಸುವದಕ್ಕಾಗಿ ದೇವದರ್ಶನದ ಗುಡಾರದ ಬಾಗಲಿಗೆ ತಾರದಿದ್ದರೆ ಅವನು ಕುಲದಿಂದ ತೆಗೆದುಹಾಕಲ್ಪಡಬೇಕು.",
			"10": "<sup>10</sup> ಇದಲ್ಲದೆ ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿಯಾಗಲಿ ಅವರ ನಡುವೆ ಇಳುಕೊಂಡಿರುವ ಅನ್ಯದೇಶದವರಲ್ಲಿಯಾಗಲಿ ಯಾವನಾದರೂ ರಕ್ತಭೋಜನಮಾಡಿದರೆ ನಾನು ಆತನ ಮೇಲೆ ಉಗ್ರವಾದ ಕೋಪವನ್ನು ಮಾಡಿ ಅವನನ್ನು ಕುಲದಿಂದ ತೆಗೆದುಹಾಕುವೆನು.",
			"11": "<sup>11</sup> ಯಾಕಂದರೆ ಪ್ರತಿದೇಹಿಗೂ ರಕ್ತವೇ ಪ್ರಾಣಾಧಾರ. ಅಂಥ ರಕ್ತವನ್ನು ನೀವು ಯಜ್ಞವೇದಿಗೆ ಎರಚಿ ನಿಮಗೋಸ್ಕರ ದೋಷಪರಿಹಾರ ಮಾಡಿಕೊಳ್ಳಬೇಕೆಂದು ನಿಮಗೆ ಅನುಗ್ರಹ ಮಾಡಿದ್ದೇನೆ. ರಕ್ತವು ಪ್ರಾಣಾಧಾರವಾಗಿರುವ ಕಾರಣ ಅದರಿಂದ ದೋಷಪರಿಹಾರವಾಗುತ್ತದಷ್ಟೆ.",
			"12": "<sup>12</sup> ಆದದರಿಂದ - ನಿಮ್ಮಲ್ಲಿಯಾಗಲಿ ನಿಮ್ಮ ನಡುವೆ ಇಳುಕೊಂಡಿರುವ ಅನ್ಯದೇಶದವರಲ್ಲಿಯಾಗಲಿ ಯಾರೂ ರಕ್ತಭೋಜನ ಮಾಡಬಾರದೆಂದು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಆಜ್ಞಾಪಿಸಿದ್ದೇನೆ.",
			"13": "<sup>13</sup> ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿಯಾಗಲಿ ಅವರ ನಡುವೆ ಇಳುಕೊಂಡಿರುವ ಅನ್ಯದೇಶದವರಲ್ಲಿಯಾಗಲಿ ಯಾವನಾದರೂ ಬೇಟೆಯಾಡಿ ತಿನ್ನತಕ್ಕ ಮೃಗವನ್ನು ಇಲ್ಲವೆ ಪಕ್ಷಿಯನ್ನು ಹಿಡಿದರೆ ಅವನು ಅದರ ರಕ್ತವನ್ನು ಸುರಿಸಿ ಮಣ್ಣಿನಿಂದ ಮುಚ್ಚಿಬಿಡಬೇಕು.",
			"14": "<sup>14</sup> ಯಾಕಂದರೆ ಪ್ರತಿದೇಹಿಗೂ ರಕ್ತವೇ ಪ್ರಾಣಾಧಾರವು. ಆದಕಾರಣ ನಾನು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ - ಪ್ರತಿ ಪ್ರಾಣಿಗೂ ರಕ್ತವೇ ಪ್ರಾಣಾಧಾರವಾದದರಿಂದ ನೀವು ಯಾವ ವಿಧವಾದ ಪ್ರಾಣಿಯ ರಕ್ತವನ್ನೂ ಉಣ್ಣಬಾರದು; ರಕ್ತಭೋಜನ ಮಾಡಿದವನಿಗೆ ಬಹಿಷ್ಕಾರವಾಗಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿದ್ದೇನೆ.",
			"15": "<sup>15</sup> ತಾನಾಗಿ ಸತ್ತು ಬಿದ್ದದ್ದನ್ನಾಗಲಿ ಕಾಡುಮೃಗಕೊಂದದ್ದನ್ನಾಗಲಿ ತಿಂದವನು ಸ್ವದೇಶದವನಾದರೂ ಅನ್ಯದೇಶದವನಾದರೂ ಅವನು ತನ್ನ ಬಟ್ಟೆಗಳನ್ನು ಒಗೆದುಕೊಂಡು ಸ್ನಾನಮಾಡಬೇಕು; ಆ ಸಾಯಂಕಾಲದವರೆಗೆ ಅಶುದ್ಧನಾಗಿದ್ದು ಬಳಿಕ ಶುದ್ಧನಾಗುವನು.",
			"16": "<sup>16</sup> ಅವನು ತನ್ನ ಬಟ್ಟೆಗಳನ್ನು ಒಗೆದುಕೊಳ್ಳದೆಯೂ ಸ್ನಾನಮಾಡದೆಯೂ ಹೋದರೆ ತನ್ನ ಪಾಪದ ಫಲವನ್ನು ಅನುಭವಿಸಬೇಕು."
		},
		"18": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಮೋಶೆಗೆ ಹೇಳಿದ್ದೇನಂದರೆ -",
			"2": "<sup>2</sup> ನೀನು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಹೀಗೆ ಆಜ್ಞಾಪಿಸಬೇಕು - ನಾನು ನಿಮ್ಮ ದೇವರಾಗಿರುವ ಯೆಹೋವನು;",
			"3": "<sup>3</sup> ನೀವು ವಾಸವಾಗಿದ್ದ ಐಗುಪ್ತದೇಶದ ಆಚಾರಗಳನ್ನು ಅನುಸರಿಸಬಾರದು; ನಾನು ನಿಮ್ಮನ್ನು ಬರಮಾಡುವ ಕಾನಾನ್‍ದೇಶದ ಆಚರಣೆಗಳನ್ನೂ ನೀವು ಅನುಸರಿಸಬಾರದು; ಅವರಲ್ಲಿರುವ ನಿಯಮಗಳಿಗೆ ನೀವು ಒಳಗಾಗಬಾರದು.",
			"4": "<sup>4</sup> ಯೆಹೋವನೆಂಬ ನಾನೇ ನಿಮ್ಮ ದೇವರಾಗಿರುವದರಿಂದ ನನ್ನ ನಿರ್ಣಯಗಳ ಪ್ರಕಾರವೇ ನೀವು ನಡೆಯಬೇಕು, ನನ್ನ ವಿಧಿಗಳನ್ನು ಅನುಸರಿಸಬೇಕು.",
			"5": "<sup>5</sup> ನನ್ನ ಆಜ್ಞಾವಿಧಿಗಳ ಪ್ರಕಾರ ನಡೆದುಕೊಳ್ಳುವವರು ಆ ಆಜ್ಞಾವಿಧಿಗಳ ಮೂಲಕ ಬದುಕುವರು. ಆದದರಿಂದ ನೀವು ಅವುಗಳನ್ನೇ ಅನುಸರಿಸಬೇಕು; ನಾನು ಯೆಹೋವನು.",
			"6": "<sup>6</sup> ನಿಮ್ಮಲ್ಲಿ ಯಾರೂ ರಕ್ತಸಂಬಂಧವುಳ್ಳ ಸ್ತ್ರೀಯ ಸಹವಾಸವನ್ನು ಮಾಡಬಾರದು; ನಾನು ಯೆಹೋವನು.",
			"7": "<sup>7</sup> ತಾಯಿಯನ್ನು ಎಷ್ಟು ಮಾತ್ರವೂ ಸಂಗಮಿಸಬಾರದು; ಆಕೆ ಹೆತ್ತವಳಲ್ಲವೆ; ಸಂಗಮಿಸಿದರೆ ತಂದೆಗೆ ಮಾನಭಂಗವಾಗುವದು.",
			"8": "<sup>8</sup> ಮಲತಾಯಿಯ ಸಂಗ ಮಾಡಬಾರದು; ಆಕೆ ತಂದೆಗೆ ಹೆಂಡತಿಯಾದವಳಲ್ಲವೇ.",
			"9": "<sup>9</sup> ತಂದೆಯ ಮಗಳನ್ನಾಗಲಿ ತಾಯಿಯ ಮಗಳನ್ನಾಗಲಿ ಇಟ್ಟುಕೊಳ್ಳಬಾರದು; ಅವರು ಒಡಹುಟ್ಟಿದವರಾದರೂ ಪರಸ್ತ್ರೀಯಲ್ಲಿ ಹುಟ್ಟಿದವರಾದರೂ ನಿಮಗೆ ಅಕ್ಕತಂಗಿಯರಲ್ಲವೇ.",
			"10": "<sup>10</sup> ಮಗನ ಮಗಳನ್ನಾಗಲಿ ಮಗಳ ಮಗಳನ್ನಾಗಲಿ ಇಟ್ಟುಕೊಳ್ಳಬಾರದು; ಅವರು ನಿಮಗೆ ಮಕ್ಕಳಂತಲ್ಲವೇ.",
			"11": "<sup>11</sup> ತಂದೆಯ ಮತ್ತೊಬ್ಬ ಹೆಂಡತಿಯಲ್ಲಿ ಹುಟ್ಟಿದವಳ ಸಂಗಮಾಡಬಾರದು; ಆಕೆ ಸಹೋದರಿ.",
			"12": "<sup>12</sup> ತಂದೆಯ ಒಡಹುಟ್ಟಿದವಳ ಸಂಗಮಾಡಬಾರದು;",
			"13": "<sup>13</sup> ಆಕೆ ತಂದೆಯ ರಕ್ತಸಂಬಂಧಿ. ತಾಯಿಯ ಒಡಹುಟ್ಟಿದವಳ ಸಂಗಮಾಡಬಾರದು; ಆಕೆ ತಾಯಿಯ ರಕ್ತಸಂಬಂಧಿ.",
			"14": "<sup>14</sup> ತಂದೆಯ ಅಣ್ಣತಮ್ಮಂದಿರ ಹೆಂಡತಿಯರ ಸಂಗಮಾಡಬಾರದು; ಅವರು ದೊಡ್ಡತಾಯಿ ಚಿಕ್ಕತಾಯಿ.",
			"15": "<sup>15</sup> ಸೊಸೆಯ ಸಹವಾಸ ಮಾಡಬಾರದು; ಆಕೆ ಮಗನ ಹೆಂಡತಿಯಾದವಳಲ್ಲವೇ.",
			"16": "<sup>16</sup> ಅತ್ತಿಗೆ ನಾದಿನಿಯರ ಸಂಗಮಾಡಬಾರದು; ಅವರು ಅಣ್ಣತಮ್ಮಂದಿರ ಹೆಂಡತಿಯರಲ್ಲವೇ.",
			"17": "<sup>17</sup> ಒಬ್ಬ ಸ್ತ್ರೀಯನ್ನು ಮದುವೆಮಾಡಿಕೊಂಡರೆ ಆಕೆಯ ಮಗಳನ್ನಾಗಲಿ ಮೊಮ್ಮಗಳನ್ನಾಗಲಿ ಸಂಗಮಿಸಬಾರದು; ಅವರು ರಕ್ತಸಂಬಂಧಿಗಳಾದದರಿಂದ ಅದು ದುಷ್ಕಾರ್ಯ.",
			"18": "<sup>18</sup> ಹೆಂಡತಿ ಜೀವದಿಂದಿರುವಾಗಲೇ ಆಕೆಯ ಒಡಹುಟ್ಟಿದವಳನ್ನೂ ಮದುವೆಮಾಡಿಕೊಂಡು ಹೆಂಡತಿಗೆ ಮನಸ್ತಾಪವನ್ನುಂಟುಮಾಡಬಾರದು.",
			"19": "<sup>19</sup> ಮುಟ್ಟಿನಿಂದ ಅಶುದ್ಧಳಾದ ಸ್ತ್ರೀಯನ್ನು ಸಂಗಮಿಸಬಾರದು.",
			"20": "<sup>20</sup> ಪರಸ್ತ್ರೀಗಮನದಿಂದ ಅಪರಿಶುದ್ಧರಾಗಬಾರದು.",
			"21": "<sup>21</sup> ನಿಮ್ಮ ಮಕ್ಕಳಲ್ಲಿ ಯಾರನ್ನಾದರೂ ಮೋಲೆಕನಿಗೆ ಸಮರ್ಪಿಸಿ ನಿಮ್ಮ ದೇವರ ಹೆಸರನ್ನು ಅಪಕೀರ್ತಿಗೆ ಗುರಿಮಾಡಬಾರದು; ನಾನು ಯೆಹೋವನು.",
			"22": "<sup>22</sup> ಸ್ತ್ರೀಯನ್ನು ಸಂಗಮಿಸುವಂತೆ ಪುರುಷನನ್ನು ಸಂಗಮಿಸಬಾರದು; ಅದು ಹೇಸಿಗೆಯಾದ ಕೆಲಸ.",
			"23": "<sup>23</sup> ಪಶುಸಂಗಮವನ್ನು ಮಾಡಿ ಅಶುದ್ಧರಾಗಬಾರದು. ಯಾವ ಸ್ತ್ರೀಯೂ ಸಂಗಮಕ್ಕಾಗಿ ಪಶುವಿನ ಮುಂದೆ ನಿಲ್ಲಬಾರದು; ಅದು ಸ್ವಭಾವಕ್ಕೆ ವಿರುದ್ಧವಾದದ್ದು.",
			"24": "<sup>24</sup> ಈ ದುರಾಚಾರಗಳಲ್ಲಿ ಯಾವದರಿಂದಲೂ ನೀವು ಅಶುದ್ಧರಾಗಬಾರದು. ನಾನು ನಿಮ್ಮ ಎದುರಿನಿಂದ ಹೊರಡಿಸುವ ಜನಾಂಗಗಳವರು ಇಂಥ ದುರಾಚಾರಗಳಿಂದ ಅಶುದ್ಧರಾದರು.",
			"25": "<sup>25</sup> ಅವರ ದೇಶವು ಅಶುದ್ಧವಾಗಿ ಹೋದದರಿಂದ ಅವರ ಪಾಪಕೃತ್ಯಗಳಿಗಾಗಿ ಅವರನ್ನು ಶಿಕ್ಷಿಸಿದ್ದೇನೆ; ಆ ದೇಶವು ತನ್ನಲ್ಲಿ ವಾಸಿಸಿದವರನ್ನು ಕಾರಿಬಿಟ್ಟಿತು.",
			"26": "<sup>26</sup> ಆದದರಿಂದ ಸ್ವದೇಶದವರಾದ ನಿಮ್ಮಲ್ಲಿಯೂ ನಿಮ್ಮ ನಡುವೆ ಇಳುಕೊಂಡಿರುವ ಅನ್ಯದೇಶದವರಲ್ಲಿಯೂ ಯಾರೂ ಈ ಹೇಸಿಗೆಯಾದ ಕಾರ್ಯಗಳಲ್ಲಿ ಒಂದನ್ನಾದರೂ ನಡಿಸದೆ ಎಲ್ಲರೂ ನನ್ನ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ಅನುಸರಿಸಿ ನಡೆಯಬೇಕು.",
			"27": "<sup>27</sup> ನಿಮಗಿಂತ ಮುಂಚೆ ಆ ದೇಶದಲ್ಲಿ ಇದ್ದವರು ಇಂಥ ಹೇಸಿಗೆಯಾದ ಕೆಲಸಗಳನ್ನು ಮಾಡಿದ್ದರಿಂದ ಆ ದೇಶವು ಅಶುದ್ಧವಾಗಿ ಹೋಯಿತಲ್ಲಾ.",
			"28": "<sup>28</sup> ಆ ದೇಶವು ನಿಮಗಿಂತ ಮುಂಚೆ ಇದ್ದ ಜನಾಂಗಗಳನ್ನು ಕಾರಿಬಿಟ್ಟ ಪ್ರಕಾರವೇ ನಿಮ್ಮಿಂದ ಅಶುದ್ಧವಾದರೆ ನಿಮ್ಮನ್ನೂ ಕಾರಿಬಿಟ್ಟೀತು.",
			"29": "<sup>29</sup> ಯಾವನಾದರೂ ಈ ಹೇಸಿಗೆಯಾದ ಕೆಲಸಗಳಲ್ಲಿ ಒಂದನ್ನಾದರೂ ಮಾಡಿದರೆ ಅವನು ಕುಲದಿಂದ ತೆಗೆದುಹಾಕಲ್ಪಡಬೇಕು.",
			"30": "<sup>30</sup> ಆದದರಿಂದ ನಿಮಗಿಂತ ಮುಂಚೆ ನಡೆಯುತ್ತಿದ್ದ ಅಸಹ್ಯವಾದ ಆಚರಣೆಗಳನ್ನು ನಡಿಸಿ ನಿಮ್ಮನ್ನು ಅಶುದ್ಧಮಾಡಿಕೊಳ್ಳದೆ ನನ್ನ ವಿಧಿಗಳನ್ನೇ ಅನುಸರಿಸಿ ನಡೆಯಬೇಕು; ನಾನು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು."
		},
		"19": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಮೋಶೆಗೆ ಹೇಳಿದ್ದೇನಂದರೆ -",
			"2": "<sup>2</sup> ನೀನು ಇಸ್ರಾಯೇಲ್ಯರ ಸಮೂಹದವರಿಗೆ ಹೀಗೆ ಆಜ್ಞಾಪಿಸಬೇಕು - ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನೆಂಬ ನಾನು ಪರಿಶುದ್ಧನಾಗಿರುವದರಿಂದ ನೀವೂ ಪರಿಶುದ್ಧರಾಗಿರಬೇಕು.",
			"3": "<sup>3</sup> ಪ್ರತಿಯೊಬ್ಬನು ತನ್ನ ತಾಯಿತಂದೆಗಳ ವಿಷಯದಲ್ಲಿ ಭಯಭಕ್ತಿಯಿಂದ ನಡೆದುಕೊಳ್ಳಬೇಕು. ನಾನು ನೇಮಿಸಿರುವ ಸಬ್ಬತ್‍ದಿನಗಳನ್ನು ಆಚರಿಸಬೇಕು. ನಾನು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು.",
			"4": "<sup>4</sup> ಸುಳ್ಳುದೇವರುಗಳ ಕಡೆಗೆ ತಿರುಗಿಕೊಳ್ಳಬಾರದು; ದೇವರೆಂದು ಎರಕದ ವಿಗ್ರಹಗಳನ್ನು ಮಾಡಿಕೊಳ್ಳಬಾರದು. ನಾನು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು.",
			"5": "<sup>5</sup> ಸಮಾಧಾನಯಜ್ಞಗಳನ್ನು ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಿಸುವಾಗ ಆತನಿಗೆ ಮೆಚ್ಚಿಕೆಯಾದ ರೀತಿಯಲ್ಲಿ ಸಮರ್ಪಿಸಬೇಕು.",
			"6": "<sup>6</sup> ನೀವು ಮಾಡುವ ಯಜ್ಞದ ಮಾಂಸವನ್ನು ಆ ದಿನದಲ್ಲಿಯಾಗಲಿ ಮರುದಿನದಲ್ಲಿಯಾಗಲಿ ಊಟಮಾಡಬೇಕು. ಮೂರನೆಯ ದಿನದವರೆಗೆ ಏನಾದರೂ ಮಿಕ್ಕಿದ್ದರೆ ಅದನ್ನು ಬೆಂಕಿಯಿಂದ ಸುಟ್ಟುಬಿಡಬೇಕು.",
			"7": "<sup>7</sup> ಮೂರನೆಯ ದಿನದ ತನಕ ಮಿಕ್ಕಿದ್ದು ಹೇಯವಾದದರಿಂದ ಅದರಲ್ಲಿ ಏನಾದರೂ ತಿಂದರೆ ಆ ಯಜ್ಞವು ಯೆಹೋವನಿಗೆ ಅಂಗೀಕಾರವಾಗುವದೇ ಇಲ್ಲ.",
			"8": "<sup>8</sup> ಅದರಲ್ಲಿ ಏನಾದರೂ ತಿಂದವನು ಯೆಹೋವನ ದ್ರವ್ಯವನ್ನು ಹೊಲೆಮಾಡಿದವನಾದದರಿಂದ ತನ್ನ ಪಾಪದ ಫಲವನ್ನು ಅನುಭವಿಸಬೇಕು; ಅವನು ಕುಲದಿಂದ ತೆಗೆದುಹಾಕಲ್ಪಡಬೇಕು.",
			"9": "<sup>9</sup> ನೀವು ಪೈರುಗಳನ್ನು ಕೊಯ್ಯುವಾಗ ಹೊಲಗಳ ಮೂಲೆಗಳಲ್ಲಿರುವದನ್ನೆಲ್ಲಾ ಕೊಯ್ಯಬಾರದು; ಮತ್ತು ಕೊಯ್ದಾಗ ಹಕ್ಕಲಾಯಬಾರದು.",
			"10": "<sup>10</sup> ದ್ರಾಕ್ಷೆಯ ತೋಟಗಳಲ್ಲಿಯೂ ಹಕ್ಕಲಾಯಕೂಡದು; ಉದುರಿದ ಹಣ್ಣುಗಳನ್ನು ಆರಿಸಿಕೊಳ್ಳದೆ ಬಡವರಿಗೂ ಪರದೇಶಿಗಳಿಗೂ ಬಿಟ್ಟುಬಿಡಬೇಕು. ನಾನು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು.",
			"11": "<sup>11</sup> ಕದಿಯಬಾರದು; ಮೋಸಮಾಡಬಾರದು; ಒಬ್ಬರಿಗೊಬ್ಬರು ಸುಳ್ಳಾಡಬಾರದು.",
			"12": "<sup>12</sup> ನನ್ನ ಹೆಸರಿನ ಮೇಲೆ ಸುಳ್ಳಾಣೆಯಿಟ್ಟು ನಿಮ್ಮ ದೇವರ ನಾಮವನ್ನು ಅಪಕೀರ್ತಿಗೆ ಒಳಪಡಿಸಬಾರದು. ನಾನು ಯೆಹೋವನು.",
			"13": "<sup>13</sup> ಮತ್ತೊಬ್ಬನನ್ನು ಬಲಾತ್ಕರಿಸಬಾರದು; ಅವನ ಸೊತ್ತನ್ನು ಅಪಹರಿಸಬಾರದು. ಕೂಲಿಯವನ ಕೂಲಿಯನ್ನು ಮರುದಿನದವರೆಗೆ ನಿನ್ನ ಬಳಿಯಲ್ಲಿ ಇಟ್ಟುಕೊಳ್ಳಬಾರದು.",
			"14": "<sup>14</sup> ಕಿವುಡರನ್ನು ದೂಷಿಸಬಾರದು; ಕುರುಡರು ನಡೆಯುವ ದಾರಿಯಲ್ಲಿ ಕಾಲು ಎಡವುವಂತೆ ಕಲ್ಲನ್ನು ಇಡಬಾರದು; ನಿಮ್ಮ ದೇವರಿಗೆ ಭಯಭಕ್ತಿಯಿಂದ ನಡೆದುಕೊಳ್ಳಬೇಕು. ನಾನು ಯೆಹೋವನು.",
			"15": "<sup>15</sup> ವ್ಯಾಜ್ಯವನ್ನು ವಿಚಾರಿಸುವಾಗ ಅನ್ಯಾಯವಾದ ತೀರ್ಪನ್ನು ಮಾಡಬಾರದು. ಬಡವನ ಬಡತನವನ್ನಾಗಲಿ ದೊಡ್ಡ ಮನುಷ್ಯನ ಘನತೆಯನ್ನಾಗಲಿ ಲಕ್ಷ್ಯಮಾಡದೆ ಪಕ್ಷಪಾತವಿಲ್ಲದೆ ತೀರ್ಪನ್ನು ಕೊಡಬೇಕು.",
			"16": "<sup>16</sup> ಪರಿಚಯವುಳ್ಳವರಲ್ಲಿ ಒಬ್ಬರ ಮೇಲೊಬ್ಬರು ಚಾಡಿಹೇಳಬಾರದು. ಮತ್ತೊಬ್ಬನಿಗೆ ಮರಣ ಶಿಕ್ಷೆಯಾಗಲೇಬೇಕೆಂದು ಛಲ ಹಿಡಿಯಬಾರದು. ನಾನು ಯೆಹೋವನು.",
			"17": "<sup>17</sup> ಸಹೋದರನ ವಿಷಯವಾಗಿ ಮನಸ್ಸಿನಲ್ಲಿ ದ್ವೇಷವನ್ನು ಇಟ್ಟುಕೊಳ್ಳಬಾರದು. ನೆರೆಯವನ ದೋಷಕ್ಕೆ ನೀವು ಒಳಗಾಗದಂತೆ ಅವನ ತಪ್ಪನ್ನು ಅವನಿಗೆ ತಿಳಿಸಲೇಬೇಕು.",
			"18": "<sup>18</sup> ನಿಮ್ಮ ಸ್ವಜನರಲ್ಲಿ ಯಾರಿಗಾದರೂ ಕೇಡಿಗೆ ಕೇಡನ್ನು ಮಾಡದೆ ಮನಸ್ಸಿನಲ್ಲಿ ಮತ್ಸರವನ್ನು ಇಟ್ಟುಕೊಳ್ಳದೆ ನಿಮ್ಮ ನೆರೆಯವರನ್ನು ನಿಮ್ಮಂತೆಯೇ ಪ್ರೀತಿಸಬೇಕು. ನಾನು ಯೆಹೋವನು.",
			"19": "<sup>19</sup> ನನ್ನ ಆಜ್ಞೆಗಳನ್ನು ನೀವು ಅನುಸರಿಸಿ ನಡೆಯಬೇಕು. ಪಶುಗಳಿಗೆ ಬೇರೆ ಜಾತಿಯ ಪಶುಗಳನ್ನು ಹಾರಿಸಬಾರದು; ನಿಮ್ಮ ಹೊಲಗಳಲ್ಲಿ ಮಿಶ್ರಬೀಜಗಳನ್ನು ಬಿತ್ತಬಾರದು; ನಾರೂ ಉಣ್ಣೆಯೂ ಕೂಡಿರುವ ಬಟ್ಟೆಯನ್ನು ಹಾಕಿಕೊಳ್ಳಬಾರದು.",
			"20": "<sup>20</sup> ಒಬ್ಬನ ಅಧೀನದಲ್ಲಿರುವ ದಾಸಿ ಹಣಕೊಡುವದರಿಂದಾಗಲಿ ಉಚಿತವಾಗಿಯಾಗಲಿ ಬಿಡುಗಡೆಯನ್ನು ಹೊಂದದೆ ಇರುವಲ್ಲಿ ಯಾವನಾದರೂ ಅವಳಲ್ಲಿ ಕೂಡಿದರೆ ಅವರಿಗೆ ವಿಧಿಸಬೇಕಾದ ಶಿಕ್ಷೆಯ ವಿಷಯವಾಗಿ ನ್ಯಾಯವಿಚಾರಣೆಯಾಗಬೇಕು. ಅವಳು ಸ್ವತಂತ್ರಳಲ್ಲವಾದದರಿಂದ ಅವರಿಗೆ ಮರಣಶಿಕ್ಷೆಯನ್ನು ವಿಧಿಸಬಾರದು.",
			"21": "<sup>21</sup> ಆ ಪುರುಷನು ಪ್ರಾಯಶ್ಚಿತ್ತಯಜ್ಞಕ್ಕಾಗಿ ಯೆಹೋವನ ಸನ್ನಿಧಿಗೆ ದೇವದರ್ಶನದ ಗುಡಾರದ ಬಾಗಲಿಗೆ ಒಂದು ಟಗರನ್ನು ತರಬೇಕು.",
			"22": "<sup>22</sup> ಅವನ ದೋಷಪರಿಹಾರಕ್ಕಾಗಿ ಯಾಜಕನು ಆ ಟಗರನ್ನು ಪ್ರಾಯಶ್ಚಿತ್ತಯಜ್ಞವಾಗಿ ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಿಸಿ ಅವನಿಗೋಸ್ಕರ ದೋಷಪರಿಹಾರವನ್ನು ಮಾಡಿದಾಗ ಅವನಿಗೆ ಕ್ಷಮಾಪಣೆಯಾಗುವದು.",
			"23": "<sup>23</sup> ನೀವು ಆ ದೇಶದಲ್ಲಿ ಸೇರಿದಾಗ ಆಹಾರಕ್ಕಾಗಿ ಯಾವ ಜಾತಿಯ ಹಣ್ಣಿನ ಸಸಿಯನ್ನು ನೆಟ್ಟರೂ ಅದರ ಫಲವನ್ನು ಮೂರು ವರುಷದ ತನಕ ಅಶುದ್ಧವೆಂದೆಣಿಸಿ ತಿನ್ನದಿರಬೇಕು.",
			"24": "<sup>24</sup> ನಾಲ್ಕನೆಯ ವರುಷದಲ್ಲಿ ಅದರ ಎಲ್ಲಾ ಹಣ್ಣುಗಳೂ ದೇವರವಾಗಿರಬೇಕು; ಅವುಗಳನ್ನು ಯೆಹೋವನಿಗೆ ಕೃತಜ್ಞತೆಯ ಕಾಣಿಕೆಯನ್ನಾಗಿ ಸಮರ್ಪಿಸಬೇಕು.",
			"25": "<sup>25</sup> ಐದನೆಯ ವರುಷದಲ್ಲಿ ಅದರಿಂದುಂಟಾಗುವ ಆದಾಯವು ನಿಮ್ಮದೇ ಆಗಿರುವದು; ಅದರ ಫಲಗಳನ್ನು ನೀವು ತಿನ್ನಬಹುದು. ನಾನು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು.",
			"26": "<sup>26</sup> ರಕ್ತ ಸಹಿತವಾದ ಯಾವ ಮಾಂಸವನ್ನೂ ತಿನ್ನಬಾರದು. ಯಂತ್ರಮಂತ್ರಗಳನ್ನು ಮಾಡಬಾರದು, ಶಕುನಗಳನ್ನು ನೋಡಬಾರದು.",
			"27": "<sup>27</sup> ಚಂಡಿಕೆ ಬಿಡಬಾರದು, ಗಡ್ಡವನ್ನು ವಿಕಾರಗೊಳಿಸಬಾರದು.",
			"28": "<sup>28</sup> ಸತ್ತವರಿಗೋಸ್ಕರ ದುಃಖವನ್ನು ಸೂಚಿಸುವದಕ್ಕಾಗಿ ದೇಹವನ್ನು ಗಾಯಮಾಡಿಕೊಳ್ಳಬಾರದು. ಶರೀರದ ಮೇಲೆ ಹಚ್ಚೇ ಚುಚ್ಚಿಸಿಕೊಳ್ಳಬಾರದು. ನಾನು ಯೆಹೋವನು.",
			"29": "<sup>29</sup> ಮಗಳನ್ನು ಬಸವಿಬಿಟ್ಟು ಸೂಳೆಯನ್ನಾಗಿ ಮಾಡಬಾರದು. ಹಾಗೆ ಮಾಡಿದರೆ ಸೂಳೆಗಾರಿಕೆ ಪ್ರಬಲವಾಗಿ ದೇಶದಲ್ಲೆಲ್ಲಾ ತುಂಬಿಹೋಗುವದು.",
			"30": "<sup>30</sup> ನಾನು ನೇಮಿಸಿರುವ ಸಬ್ಬತ್‍ದಿನಗಳನ್ನು ಆಚರಿಸಬೇಕು; ನನ್ನ ದೇವಸ್ಥಾನದ ವಿಷಯದಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿರಬೇಕು. ನಾನು ಯೆಹೋವನು.",
			"31": "<sup>31</sup> ಸತ್ತವರಲ್ಲಿ ವಿಚಾರಿಸುವವರ ಮತ್ತು ಬೇತಾಳಿಕರ ಹತ್ತಿರ ಹೋಗಬಾರದು; ಅವರ ಆಲೋಚನೆಯನ್ನು ಕೇಳಿ ಅಶುದ್ಧರಾಗಬಾರದು. ನಾನು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು.",
			"32": "<sup>32</sup> ತಲೆನರೆತ ವೃದ್ಧರ ಮುಂದೆ ಎದ್ದು ನಿಂತು ಅವರನ್ನು ಸನ್ಮಾನಿಸಬೇಕು. ನಿಮ್ಮ ದೇವರಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿರಬೇಕು; ನಾನು ಯೆಹೋವನು.",
			"33": "<sup>33</sup> ನಿಮ್ಮ ದೇಶದಲ್ಲಿ ಇಳುಕೊಂಡಿರುವ ಪರದೇಶದವರಿಗೆ ಅನ್ಯಾಯವೇನೂ ಮಾಡಬಾರದು.",
			"34": "<sup>34</sup> ಅವರು ನಿಮಗೆ ಸ್ವದೇಶದವರಂತೆಯೇ ಇರಬೇಕು; ಅವರನ್ನು ನಿಮ್ಮಂತೆಯೇ ಪ್ರೀತಿಸಬೇಕು. ಐಗುಪ್ತದೇಶದಲ್ಲಿದ್ದಾಗ ನೀವೂ ಅನ್ಯರಾಗಿದ್ದಿರಲ್ಲವೇ. ನಾನು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು.",
			"35": "<sup>35</sup> ನ್ಯಾಯವಿಚಾರಣೆ, ತೂಕ, ಅಳತೆ, ಪರಿಮಾಣ ಇವುಗಳಲ್ಲಿ ನೀವು ನ್ಯಾಯವಿರುದ್ಧವಾಗಿ ನಡೆಯಬಾರದು.",
			"36": "<sup>36</sup> ತಕ್ಕಡಿ, ತೂಕದ ಕಲ್ಲು, ಕೊಳಗ, ಸೇರು ಇವುಗಳೆಲ್ಲಾ ನ್ಯಾಯವಾಗಿಯೇ ಇರಬೇಕು. ನಾನು ನಿಮ್ಮನ್ನು ಐಗುಪ್ತದೇಶದಿಂದ ಬರಮಾಡಿದ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು.",
			"37": "<sup>37</sup> ಆದದರಿಂದ ನನ್ನ ಆಜ್ಞಾವಿಧಿಗಳನ್ನೆಲ್ಲಾ ನೀವು ಅನುಸರಿಸಿ ನಡೆಯಬೇಕು. ನಾನು ಯೆಹೋವನು."
		},
		"20": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಮೋಶೆಗೆ ಹೇಳಿದ್ದೇನಂದರೆ -",
			"2": "<sup>2</sup> ನೀನು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಹೀಗೆ ಹೀಗೆ ಆಜ್ಞಾಪಿಸಬೇಕು - ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಾಗಲಿ ಅವರ ನಡುವೆ ಇಳುಕೊಂಡಿರುವ ಪರದೇಶದವರಲ್ಲಾಗಲಿ ಯಾವನಾದರೂ ತನ್ನ ಮಕ್ಕಳಲ್ಲಿ ಯಾರನ್ನಾದರೂ ಮೋಲೆಕನಿಗೆ ಕೊಟ್ಟರೆ ಅವನಿಗೆ ಮರಣಶಿಕ್ಷೆಯಾಗಬೇಕು; ದೇಶದ ಜನರು ಅವನನ್ನು ಕಲ್ಲೆಸೆದು ಕೊಲ್ಲಬೇಕು.",
			"3": "<sup>3</sup> ಅವನು ತನ್ನ ಮಕ್ಕಳಲ್ಲಿ ಯಾರನ್ನಾದರೂ ಮೋಲೆಕನಿಗೆ ಕೊಟ್ಟು ನನ್ನ ದೇವಸ್ಥಾನವನ್ನು ಅಶುದ್ಧಮಾಡಿ ನನ್ನ ಪರಿಶುದ್ಧವಾದ ಹೆಸರನ್ನು ಅಪಕೀರ್ತಿಗೆ ಒಳಪಡಿಸಿದದರಿಂದ ನಾನು ಅವನಿಗೆ ವಿಮುಖನಾಗಿ ಅವನನ್ನು ಕುಲದಿಂದ ತೆಗೆದುಹಾಕುವೆನು.",
			"4": "<sup>4</sup> ಆ ಮನುಷ್ಯನು ತನ್ನ ಮಕ್ಕಳಲ್ಲಿ ಯಾರನ್ನಾದರೂ ಮೋಲೆಕನಿಗೆ ಕೊಡಲಾಗಿ ದೇಶದವರು ಅವನನ್ನು ಕೊಲ್ಲದೆ ಅವನು ಮಾಡಿದ ಕೆಲಸವನ್ನು ನೋಡಿಯೂ ನೋಡದವರಂತಿದ್ದರೆ",
			"5": "<sup>5</sup> ನಾನಂತೂ ಆ ಮನುಷ್ಯನಿಗೂ ಅವನ ಕುಟುಂಬದವರಿಗೂ ವಿಮುಖನಾಗಿ ಅವನನ್ನು ಮಾತ್ರವಲ್ಲದೆ ಅವನನ್ನು ಹೊಂದಿಕೊಂಡು ದೇವದ್ರೋಹಿಗಳಾಗಿ ಮೋಲೆಕನಿಗೆ ಸೇವೆಮಾಡಿದವರೆಲ್ಲರನ್ನೂ ಕುಲದಿಂದ ತೆಗೆದುಹಾಕುವೆನು.",
			"6": "<sup>6</sup> ಯಾವನಾದರೂ ಸತ್ತವರಲ್ಲಿ ವಿಚಾರಿಸುವವರ ಅಥವಾ ಬೇತಾಳಿಕರ ಬಳಿಗೆ ಹೋಗಿ ಅವರ ಆಲೋಚನೆ ಕೇಳಿಕೊಂಡು ದೇವದ್ರೋಹಿಯಾದರೆ ನಾನು ಆ ಮನುಷ್ಯನಿಗೆ ವಿಮುಖನಾಗಿ ಅವನನ್ನು ಕುಲದಿಂದ ತೆಗೆದುಹಾಕುವೆನು.",
			"7": "<sup>7</sup> ನಾನು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಾಗಿರುವದರಿಂದ ನೀವು ನನ್ನವರಾಗಿ ಪರಿಶುದ್ಧರಾಗಿರಬೇಕು.",
			"8": "<sup>8</sup> ನನ್ನ ಆಜ್ಞೆಗಳನ್ನು ಅನುಸರಿಸಿ ನಡೆಯಬೇಕು. ನಾನು ನಿಮ್ಮನ್ನು ಪರಿಶುದ್ಧ ಜನವಾಗುವಂತೆ ಮಾಡುವ ಯೆಹೋವನು.",
			"9": "<sup>9</sup> ತಂದೆಯನ್ನಾಗಲಿ ತಾಯಿಯನ್ನಾಗಲಿ ದೂಷಿಸುವವನಿಗೆ ಮರಣಶಿಕ್ಷೆಯಾಗಬೇಕು. ತಂದೆತಾಯಿಗಳನ್ನು ದೂಷಿಸಿದದರಿಂದ ಅವನಿಗೆ ಸಂಭವಿಸಿದ ಮರಣಶಿಕ್ಷೆಗೆ ಅವನೇ ಕಾರಣನು.",
			"10": "<sup>10</sup> ಯಾವನಾದರೂ ಪರಪತ್ನಿಯನ್ನು ಸಂಗಮಿಸಿದರೆ ಅವರಿಬ್ಬರಿಗೂ ಮರಣಶಿಕ್ಷೆಯಾಗಬೇಕು.",
			"11": "<sup>11</sup> ಯಾವನಾದರೂ ಮಲತಾಯಿಯನ್ನು ಕೂಡಿದರೆ ಅವನು ತಂದೆಗೇ ಮಾನಭಂಗಮಾಡಿದ ಹಾಗಾಯಿತು; ಅವರಿಬ್ಬರಿಗೂ ಮರಣಶಿಕ್ಷೆಯಾಗಬೇಕು; ಆ ಶಿಕ್ಷೆಗೆ ಅವರೇ ಕಾರಣರು.",
			"12": "<sup>12</sup> ಯಾವನಾದರೂ ಸೊಸೆಯನ್ನು ಸಂಗಮಿಸಿದರೆ ಅವರಿಬ್ಬರಿಗೂ ಮರಣ ಶಿಕ್ಷೆಯಾಗಬೇಕು; ಅವರು ಧರ್ಮವಿರುದ್ಧವಾದ ಕಾರ್ಯವನ್ನು ಮಾಡಿದದರಿಂದ ಆ ಶಿಕ್ಷೆಗೆ ಅವರೇ ಕಾರಣರು.",
			"13": "<sup>13</sup> ಯಾವನಾದರೂ ಸ್ತ್ರೀಯನ್ನು ಸಂಗಮಿಸುವಂತೆ ಪುರುಷನನ್ನು ಸಂಗಮಿಸಿದರೆ ಅವರಿಬ್ಬರೂ ಹೇಸಿಗೆಯ ಕೆಲಸವನ್ನು ಮಾಡಿದದರಿಂದ ಅವರಿಗೆ ಮರಣಶಿಕ್ಷೆಯಾಗಬೇಕು; ಆ ಶಿಕ್ಷೆಗೆ ಅವರೇ ಕಾರಣರು.",
			"14": "<sup>14</sup> ಯಾವನಾದರೂ ಒಬ್ಬ ಸ್ತ್ರೀಯನ್ನೂ ಅವಳ ತಾಯಿಯನ್ನೂ ಇಟ್ಟುಕೊಂಡರೆ ಅದು ಅತಿದುಷ್ಟಕಾರ್ಯವಾದದರಿಂದ ಆ ಮೂರು ಮಂದಿಯನ್ನೂ ಬೆಂಕಿಯಿಂದ ಸುಡಿಸಿಬಿಡಬೇಕು; ಅಂಥ ದುರಾಚಾರವು ನಿಮ್ಮಲ್ಲಿ ಎಷ್ಟುಮಾತ್ರವೂ ನಡೆಯಬಾರದು.",
			"15": "<sup>15</sup> ಯಾವನಾದರೂ ಪಶುಸಂಗ ಮಾಡಿದರೆ ಅವನಿಗೆ ಮರಣ ಶಿಕ್ಷೆಯಾಗಬೇಕು; ಆ ಪಶುವನ್ನೂ ಕೊಲ್ಲಿಸಬೇಕು.",
			"16": "<sup>16</sup> ಯಾವ ಸ್ತ್ರೀಯಾದರೂ ಪಶುವಿನಿಂದ ಸಂಗಮಮಾಡಿಸಿಕೊಂಡರೆ ಆ ಸ್ತ್ರೀಯನ್ನೂ ಪಶುವನ್ನೂ ಕೊಲ್ಲಿಸಬೇಕು; ಅವಳಿಗೂ ಅದಕ್ಕೂ ಮರಣವೇ ವಿಧಿ; ಆ ಮರಣಶಿಕ್ಷೆಗೆ ತಾವೇ ಕಾರಣರು.",
			"17": "<sup>17</sup> ಯಾವನಾದರೂ ತನ್ನ ಒಡಹುಟ್ಟಿದವಳನ್ನು ಅಂದರೆ ತಂದೆಯ ಮಗಳನ್ನಾಗಲಿ ತಾಯಿಯ ಮಗಳನ್ನಾಗಲಿ ಕರೆದುಕೊಂಡು ಅವಳ ಮಾನವನ್ನು ಇವನೂ ಇವನ ಮಾನವನ್ನು ಅವಳೂ ನೋಡಿದರೆ ಅದು ನಾಚಿಕೆಗೇಡಾದ ಕೆಲಸವಾಗಿರುವದರಿಂದ ಅವರ ಕುಲದವರ ಎದುರಾಗಿಯೇ ಅವರಿಗೆ ಮರಣಶಿಕ್ಷೆಯನ್ನು ಮಾಡಿಸಬೇಕು. ಆ ಮನುಷ್ಯನು ಒಡಹುಟ್ಟಿದವಳನ್ನು ಸಂಗಮಿಸಿದದರಿಂದ ತನ್ನ ಪಾಪದ ಫಲವನ್ನು ಅನುಭವಿಸಬೇಕು.",
			"18": "<sup>18</sup> ಯಾವನಾದರೂ ಮುಟ್ಟಾದ ಸ್ತ್ರೀಯನ್ನು ಸಂಗಮಿಸಿದರೆ ಅವನು ಅವಳ ಶೋಣಿತಸ್ಥಾನವನ್ನು ಬೈಲುಪಡಿಸಿದವನು. ಅವಳು ಬೈಲುಪಡಿಸಿಕೊಂಡವಳು; ಆದದರಿಂದ ಅವರಿಬ್ಬರೂ ಕುಲದಿಂದ ತೆಗೆದುಹಾಕಲ್ಪಡಬೇಕು.",
			"19": "<sup>19</sup> ತಂದೆಯ ಅಥವಾ ತಾಯಿಯ ಒಡಹುಟ್ಟಿದವಳ ಸಂಗ ಮಾಡಬಾರದು; ಹಾಗೆ ಮಾಡಿದವನು ರಕ್ತಸಂಬಂಧಿಯನ್ನು ಸಂಗಮಿಸಿದವನಾದದರಿಂದ ಅವರಿಬ್ಬರೂ ತಮ್ಮ ಪಾಪದ ಫಲವನ್ನು ಅನುಭವಿಸಬೇಕು.",
			"20": "<sup>20</sup> ಯಾವನಾದರೂ ದೊಡ್ಡ ತಾಯಿಯನ್ನಾಗಲಿ ಚಿಕ್ಕ ತಾಯಿಯನ್ನಾಗಲಿ ಸಂಗಮಿಸಿದರೆ ಅವನು ದೊಡ್ಡಪ್ಪ ಚಿಕ್ಕಪ್ಪನವರ ಮಾನವನ್ನು ಭಂಗಪಡಿಸಿದವನಾದದರಿಂದ ಆ ಸ್ತ್ರೀಪುರುಷರು ಆ ಪಾಪದ ಫಲವನ್ನು ಅನುಭವಿಸಬೇಕು; ಅವರು ಸಂತಾನವಿಲ್ಲದೆ ಸಾಯುವರು.",
			"21": "<sup>21</sup> ಯಾವನಾದರೂ ಅತ್ತಿಗೆನಾದಿನಿಯರನ್ನು ಇಟ್ಟುಕೊಂಡರೆ ಅದು ಅಶುದ್ಧವಾದ ಕೆಲಸ; ಅವನು ಅಣ್ಣತಮ್ಮಂದಿರ ಮಾನವನ್ನೇ ಭಂಗಪಡಿಸಿದವನು; ಅವರಿಗೆ ಸಂತಾನವಿರುವದಿಲ್ಲ.",
			"22": "<sup>22</sup> ಈ ನನ್ನ ಆಜ್ಞಾವಿಧಿಗಳನ್ನೆಲ್ಲಾ ನೀವು ಅನುಸರಿಸಿ ನಡೆಯಬೇಕು; ಹಾಗೆ ನಡೆದರೆ ನಾನು ನಿಮ್ಮನ್ನು ಯಾವ ದೇಶಕ್ಕೆ ಬರಮಾಡಿ ಅದನ್ನು ನಿವಾಸಕ್ಕಾಗಿ ಕೊಡುತ್ತೇನೋ ಆ ದೇಶವು ನಿಮ್ಮನ್ನು ಕಾರಿಬಿಡುವದಿಲ್ಲ.",
			"23": "<sup>23</sup> ನಾನು ನಿಮ್ಮ ಎದುರಿನಿಂದ ಹೊರಡಿಸುವ ಜನಾಂಗದ ಆಚಾರಗಳನ್ನು ನೀವು ಅನುಸರಿಸಬಾರದು; ಅವರು ಈ ದುರಾಚಾರಗಳನ್ನೆಲ್ಲಾ ನಡಿಸಿದದರಿಂದ ನನಗೆ ಹೇಸಿಗೆಯಾದರು.",
			"24": "<sup>24</sup> ನಿಮಗಾದರೋ - ನೀವು ಅವರ ದೇಶವನ್ನು ಸ್ವಾಧೀನಪಡಿಸಿಕೊಳ್ಳುವಿರಿ; ಹಾಲೂ ಜೇನೂ ಹರಿಯುವಂಥ ಆ ದೇಶವನ್ನು ನಿಮಗೆ ಸ್ವದೇಶವಾಗುವದಕ್ಕೆ ಕೊಡುವೆನು ಎಂದು ನಾನು ಮಾತುಕೊಟ್ಟೆನಲ್ಲವೇ. ನಾನು ನಿಮ್ಮನ್ನು ಇತರ ಜನಾಂಗಗಳಿಂದ ಪ್ರತ್ಯೇಕಿಸಿದ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು.",
			"25": "<sup>25</sup> ನೀವು ಶುದ್ಧಾಶುದ್ಧ ಜಂತುಗಳನ್ನೂ ಪಕ್ಷಿಗಳನ್ನೂ ವಿವೇಚನೆಮಾಡಿ ನಾನು ಅಶುದ್ಧವೆಂದು ನಿರ್ಣಯಿಸಿರುವ ಯಾವ ಪಶುಪಕ್ಷಿ ಕ್ರಿಮಿಕೀಟಗಳಿಂದಲೂ ನಿಮ್ಮನ್ನು ಹೇಯಮಾಡಿಕೊಳ್ಳಬಾರದು.",
			"26": "<sup>26</sup> ಯೆಹೋವನೆಂಬ ನಾನು ಪರಿಶುದ್ಧನಾಗಿದ್ದೇನೆ, ಮತ್ತು ನೀವು ನನ್ನ ಜನರಾಗುವಂತೆ ನಿಮ್ಮನ್ನು ಇತರ ಜನಾಂಗಗಳಿಂದ ಪ್ರತ್ಯೇಕಿಸಿದ್ದೇನೆ; ಆದದರಿಂದ ನೀವು ನನಗೆ ಮೀಸಲಾಗಿರಬೇಕು.",
			"27": "<sup>27</sup> ಸತ್ತವರಲ್ಲಿ ವಿಚಾರಿಸುವವರೂ ಬೇತಾಳಿಕರೂ ಸ್ತ್ರೀಯರಾಗಲಿ ಪುರುಷರಾಗಲಿ ಮರಣಶಿಕ್ಷೆ ಹೊಂದಬೇಕು. ಕಲ್ಲೆಸೆದು ಅವರನ್ನು ಕೊಲ್ಲಬೇಕು; ಆ ಶಿಕ್ಷೆಗೆ ಅವರೇ ಕಾರಣರು."
		},
		"21": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಮೋಶೆಗೆ ಹೇಳಿದ್ದೇನಂದರೆ - ನೀನು ಆರೋನನ ಮಕ್ಕಳಾದ ಯಾಜಕರಿಗೆ ಹೀಗೆ ಆಜ್ಞಾಪಿಸಬೇಕು - ಕುಲದಲ್ಲಿ ಸತ್ತವರಿಗೋಸ್ಕರ ಯಾವ ಯಾಜಕನೂ ತನ್ನನ್ನು ಅಪವಿತ್ರಮಾಡಿಕೊಳ್ಳಬಾರದು.",
			"2": "<sup>2</sup> (ಆದರೂ ಸಮೀಪ ರಕ್ತಸಂಬಂಧಿಗಳಾದ ತಾಯಿ, ತಂದೆ, ಮಕ್ಕಳು, ಅಣ್ಣತಮ್ಮಂದಿರು ಇವರಿಗೋಸ್ಕರ ಈ ವಿಧಿ ಇರುವದಿಲ್ಲ.",
			"3": "<sup>3</sup> ಇದಲ್ಲದೆ ಕನ್ನಿಕೆಯಾಗಿರುವ ತಂಗಿ ಇನ್ನೂ ಮದುವೆಯಾಗದೆ ಅವನ ಆಶ್ರಯದಲ್ಲಿರುವದರಿಂದ ಅವಳಿಗೋಸ್ಕರ ಅವನು ಅಪವಿತ್ರ ಮಾಡಿಕೊಳ್ಳಬಹುದು.)",
			"4": "<sup>4</sup> ಅವನು ಕುಲದಲ್ಲಿ ನಾಯಕನಾಗಿರುವದರಿಂದ ತನ್ನನ್ನು ಅಪವಿತ್ರಮಾಡಿಕೊಳ್ಳಕೂಡದು; ಮಾಡಿದರೆ ಯಾಜಕಸೇವೆಗೆ ಅಯೋಗ್ಯನಾದಾನು.",
			"5": "<sup>5</sup> ಅವರು ತಲೆಬೋಳಿಸಿಕೊಳ್ಳಬಾರದು; ಗಡ್ಡವನ್ನು ಕತ್ತರಿಸಿ ವಿಕಾರಗೊಳಿಸಬಾರದು; ದೇಹವನ್ನು ಗಾಯ ಮಾಡಿಕೊಳ್ಳಬಾರದು.",
			"6": "<sup>6</sup> ಅವರು ದೇವರಿಗೆ ಮೀಸಲಾಗಿರಬೇಕು; ತಾವು ಸೇವಿಸುವ ದೇವರ ಹೆಸರನ್ನು ಅಪಕೀರ್ತಿಗೆ ಗುರಿಪಡಿಸಬಾರದು. ತಮ್ಮ ದೇವರ ಆಹಾರವನ್ನು ಅಂದರೆ ಯೆಹೋವನ ಹೋಮದ್ರವ್ಯಗಳನ್ನು ಅವರು ಸಮರ್ಪಿಸುವವರಾಗಿರುವದರಿಂದ ಪವಿತ್ರರಾಗಿರಬೇಕು.",
			"7": "<sup>7</sup> ಯಾಜಕರು ತಮ್ಮ ದೇವರಿಗೆ ಮೀಸಲಾದವರಾಗಿರುವದರಿಂದ ವೇಶ್ಯಾ ಸ್ತ್ರೀಯನ್ನಾಗಲಿ ಮಾನವನ್ನು ಭಂಗಪಡಿಸಿಕೊಂಡ ಸ್ತ್ರೀಯನ್ನಾಗಲಿ ಗಂಡನಿಂದ ಬಿಡಲ್ಪಟ್ಟವಳನ್ನಾಗಲಿ ಹೆಂಡತಿಯನ್ನಾಗಿ ತೆಗೆದುಕೊಳ್ಳಬಾರದು.",
			"8": "<sup>8</sup> ಅವರು ನಿಮ್ಮ ದೇವರ ಆಹಾರವನ್ನು ಸಮರ್ಪಿಸುವವರಾದ ಕಾರಣ ಅವರನ್ನು ದೇವರ ಸೇವಕರೆಂದು ಭಾವಿಸಬೇಕು. ನಿಮ್ಮನ್ನು ನನ್ನ ಸೇವೆಗೆ ಪ್ರತಿಷ್ಠಿಸಿಕೊಂಡಿರುವ ಯೆಹೋವನೆಂಬ ನಾನು ಪರಿಶುದ್ಧನಾಗಿರುವದರಿಂದ ಅವರನ್ನೂ ಪರಿಶುದ್ಧರೆಂದು ನೀವು ಭಾವಿಸಬೇಕು.",
			"9": "<sup>9</sup> ಯಾಜಕನ ಮಗಳು ಸೂಳೆತನದಿಂದ ನಿಂದೆಗೆ ಒಳಗಾದರೆ ತನ್ನ ತಂದೆಯನ್ನೂ ನಿಂದೆಗೆ ಒಳಪಡಿಸಿದವಳಾದಳು; ಅವಳನ್ನು ಬೆಂಕಿಯಿಂದ ಸುಟ್ಟು ಬಿಡಬೇಕು.",
			"10": "<sup>10</sup> ಯಾಜಕರಲ್ಲಿ ಪ್ರಧಾನನು ಅಂದರೆ ಯಾವನು ತೈಲಾಭಿಷೇಕಹೊಂದಿ ದೀಕ್ಷಾವಸ್ತ್ರಗಳನ್ನು ಧರಿಸಿ ಪಟ್ಟಕ್ಕೆ ಬರುವನೋ ಅವನು [ದುಃಖಸೂಚನೆಗಾಗಿ] ತನ್ನ ತಲೆಯ ಕೂದಲನ್ನು ಕೆದರಿಕೊಳ್ಳಬಾರದು; ಬಟ್ಟೆಗಳನ್ನು ಹರಿದುಕೊಳ್ಳಬಾರದು.",
			"11": "<sup>11</sup> ಅವನು ಶವವಿರುವ ಯಾವ ಸ್ಥಳಕ್ಕೂ ಹೋಗಬಾರದು. ತಂದೆತಾಯಿಗಳ ಮರಣದ ನಿಮಿತ್ತ ತನ್ನನ್ನು ಅಪವಿತ್ರಮಾಡಿಕೊಳ್ಳಬಾರದು.",
			"12": "<sup>12</sup> ಇದಕ್ಕಾಗಿ ದೇವಸ್ಥಾನವನ್ನು ಬಿಡಲೇಬಾರದು; ಬಿಟ್ಟುಹೋದರೆ ತಾನು ಸೇವಿಸುವ ದೇವರ ಮಂದಿರದ ಗೌರವಕ್ಕೆ ಕುಂದುಬರುವದು. ತನ್ನ ದೇವರ ಅಭಿಷೇಕತೈಲವನ್ನು ತಲೆಯ ಮೇಲೆ ಹೊಯಿಸಿಕೊಂಡು ಪ್ರತಿಷ್ಠಿತನಾಗಿದ್ದಾನಲ್ಲಾ; ನಾನು ಯೆಹೋವನು.",
			"13": "<sup>13</sup> ಅವನು ಪುರುಷ ಸಂಪರ್ಕವೇ ಇಲ್ಲದ ಯುವತಿಯನ್ನು ಮದುವೆ ಮಾಡಿಕೊಳ್ಳಬೇಕೇ ಹೊರತು",
			"14": "<sup>14</sup> ವಿಧವೆಯನ್ನಾಗಲಿ ಗಂಡನಿಂದ ಬಿಡಲ್ಪಟ್ಟವಳನ್ನಾಗಲಿ ಮಾನವನ್ನು ಭಂಗಪಡಿಸಿಕೊಂಡವಳನ್ನಾಗಲಿ ವೇಶ್ಯಾಸ್ತ್ರೀಯನ್ನಾಗಲಿ ಹೆಂಡತಿಯನ್ನಾಗಿ ಮಾಡಿಕೊಳ್ಳಬಾರದು. ಸ್ವದೇಶದವರಲ್ಲಿಯೇ ಹೆಣ್ಣನ್ನು ಪರಿಗ್ರಹಿಸಬೇಕು;",
			"15": "<sup>15</sup> ಇಲ್ಲವಾದರೆ ಅವನ ಸಂತತಿಯು ಸ್ವಜನರೊಳಗೆ ಅಪವಾದಕ್ಕೆ ಗುರಿಯಾದೀತು. ಅವನನ್ನು ನನ್ನ ಸೇವೆಗೆ ಪ್ರತಿಷ್ಠಿಸಿಕೊಂಡ ಯೆಹೋವನು ನಾನು.",
			"16": "<sup>16</sup> ಯೆಹೋವನು ಮೋಶೆಗೆ ಹೇಳಿದ್ದೇನಂದರೆ - ನೀನು ಆರೋನನಿಗೆ ಹೀಗೆ ಆಜ್ಞಾಪಿಸು -",
			"17": "<sup>17</sup> ನಿನ್ನ ಸಂತತಿಯವರ ಎಲ್ಲಾ ತಲಾಂತರಗಳಲ್ಲಿಯೂ ಯಾವ ಅಂಗಹೀನನೂ ದೇವರ ಆಹಾರವನ್ನು ಸಮರ್ಪಿಸುವದಕ್ಕೆ ಸನ್ನಿಧಿಗೆ ಬರಬಾರದು; ಅಂಗಹೀನನು ಈ ಕಾರ್ಯವನ್ನು ವಹಿಸಿಕೊಳ್ಳಲೇಕೂಡದು.",
			"18": "<sup>18</sup> ಅವನು ಕುರುಡನಾಗಲಿ ಕುಂಟನಾಗಲಿ ಕೊರೆಮೂಗನಾಗಲಿ",
			"19": "<sup>19</sup> ವಿಪರೀತ ಅವಯವಗಳುಳ್ಳವನಾಗಲಿ ಕೈಕಾಲು ಮುರುಕನಾಗಲಿ",
			"20": "<sup>20</sup> ಗೂನು ಗುಜ್ಜಾರಿಯಾಗಲಿ ಹೂಗಣ್ಣ ಕಾಯಿಗಣ್ಣನಾಗಲಿ ಕಜ್ಜಿ ತುರಿಗಳುಳ್ಳವನಾಗಲಿ",
			"21": "<sup>21</sup> ಬೀಜದೋಷವುಳ್ಳವನಾಗಲಿ ಬೇರೆ ಯಾವ ಕಳಂಕವಿದ್ದವನಾಗಲಿ ಯೆಹೋವನಿಗೆ ಹೋಮದ್ರವ್ಯಗಳನ್ನು ಸಮರ್ಪಿಸುವದಕ್ಕೆ ಸನ್ನಿಧಿಗೆ ಬರಬಾರದು. ಅಂಥವನು ದೇಹದಲ್ಲಿ ದೋಷವಿರುವದರಿಂದ ದೇವರ ಆಹಾರವನ್ನು ಸಮರ್ಪಿಸಲೇಬಾರದು.",
			"22": "<sup>22</sup> ದೇವರಿಗೆ ನೈವೇದ್ಯವಾದ ಆಹಾರದಲ್ಲಿ ಪರಿಶುದ್ಧವಾದದ್ದನ್ನೂ ಮಹಾಪರಿಶುದ್ಧವಾದದ್ದನ್ನೂ ಅವನು ಊಟಮಾಡಬಹುದೇ ಹೊರತು",
			"23": "<sup>23</sup> ಅವನಿಗೆ ಕಳಂಕವಿರುವದರಿಂದ ತೆರೆಯನ್ನು ದಾಟಿ ಒಳಗೆ ಬರಬಾರದು; ಯಜ್ಞವೇದಿಯ ಬಳಿಗೆ ಬರಬಾರದು; ನನ್ನ ಪವಿತ್ರಸ್ಥಾನಗಳ ಗೌರವಕ್ಕೆ ಅವನಿಂದ ಕುಂದು ಉಂಟಾಗಬಾರದು; ಆ ಸ್ಥಾನಗಳನ್ನು ನನ್ನ ಸೇವೆಗೆ ಪ್ರತಿಷ್ಠಿಸಿಕೊಂಡ ಯೆಹೋವನು ನಾನು.",
			"24": "<sup>24</sup> ಮೋಶೆಯು ಆರೋನನಿಗೂ ಅವನ ಮಕ್ಕಳಿಗೂ ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರಿಗೂ ಈ ಆಜ್ಞೆಗಳನ್ನು ತಿಳಿಸಿದನು."
		},
		"22": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಮೋಶೆಗೆ ಹೇಳಿದ್ದೇನಂದರೆ -",
			"2": "<sup>2</sup> ಆರೋನನೂ ಅವನ ಮಕ್ಕಳೂ [ತಾವು ಅಶುದ್ಧರಾಗಿರುವಾಗ] ಇಸ್ರಾಯೇಲ್ಯರಿಂದ ನನಗೆ ಸಮರ್ಪಿಸಲ್ಪಡುವ ಪರಿಶುದ್ಧದ್ರವ್ಯಗಳನ್ನು ಮುಟ್ಟಬಾರದೆಂದು ಅವರಿಗೆ ಆಜ್ಞಾಪಿಸು. ಅವರು ನನ್ನ ಪರಿಶುದ್ಧ ನಾಮದ ಘನತೆಗೆ ಕುಂದನ್ನು ತರಬಾರದು; ನಾನು ಯೆಹೋವನು.",
			"3": "<sup>3</sup> ಅವರಿಗೆ ನೀನು ಹೀಗೆ ಆಜ್ಞಾಪಿಸಬೇಕು - ನಿಮ್ಮಲ್ಲಿಯಾಗಲಿ ನಿಮ್ಮ ಸಂತತಿಯವರಲ್ಲಿಯಾಗಲಿ ಯಾವನಾದರೂ ಅಶುದ್ಧನಾಗಿರುವಾಗ ಇಸ್ರಾಯೇಲ್ಯರು ಯೆಹೋವನಿಗೆ ಮೀಸಲೆಂದು ಸಮರ್ಪಿಸುವ ದ್ರವ್ಯಗಳ ಬಳಿಗೆ ಬಂದರೆ ಅವನು ನನ್ನ ಸಾನ್ನಿಧ್ಯಸೇವೆಯಿಂದ ತೆಗೆಯಲ್ಪಡಬೇಕು; ನಾನು ಯೆಹೋವನು.",
			"4": "<sup>4</sup> ಆರೋನನ ಸಂತತಿಯವರಲ್ಲಿ ಯಾವನಿಗಾದರೂ ಕುಷ್ಠರೋಗವಾಗಲಿ ಮೇಹವಾಗಲಿ ಇದ್ದರೆ ಅವನು ಶುದ್ಧನಾಗುವ ತನಕ ದೇವರಿಗೆ ನೈವೇದ್ಯವಾದ ಪದಾರ್ಥಗಳನ್ನು ಉಣ್ಣಕೂಡದು. ಹೆಣದ ಸಂಪರ್ಕದಿಂದ ಅಶುದ್ಧವಾದದ್ದನ್ನು ಮುಟ್ಟಿದವನಾಗಲಿ ವೀರ್ಯಸ್ಖಲನ ಮಾಡಿಕೊಂಡವನಾಗಲಿ",
			"5": "<sup>5</sup> ಅಶುದ್ಧವಾದ ಕ್ರಿಮಿಕೀಟ ಮೂಷಕಾದಿಗಳನ್ನು ಅಥವಾ ಅಶುದ್ಧನಾದ ಮನುಷ್ಯನನ್ನು ಮುಟ್ಟಿದವನಾಗಲಿ ಆ ದಿನದ ಸಾಯಂಕಾಲದವರೆಗೆ ಅಶುದ್ಧನಾಗಿರುವನು;",
			"6": "<sup>6</sup> ಆದದರಿಂದ ಸ್ನಾನಮಾಡುವ ತನಕ ಅವನು ನೈವೇದ್ಯದ್ರವ್ಯವನ್ನು ತಿನ್ನಕೂಡದು.",
			"7": "<sup>7</sup> ಹೊತ್ತು ಮುಣುಗಿದನಂತರ ಅವನು ಶುದ್ಧನಾಗಿ ನೈವೇದ್ಯದ್ರವ್ಯವನ್ನು ಊಟಮಾಡಬಹುದು;",
			"8": "<sup>8</sup> ಅದು ಅವನ ಜೀವನವಷ್ಟೆ. ತಾನಾಗಿ ಸತ್ತುಬಿದ್ದದ್ದನ್ನಾಗಲಿ ಕಾಡುಮೃಗದಿಂದ ಕೊಲ್ಲಲ್ಪಟ್ಟದ್ದನ್ನಾಗಲಿ ತಿಂದು ತನ್ನನ್ನು ಅಪವಿತ್ರಮಾಡಿಕೊಳ್ಳಬಾರದು; ನಾನು ಯೆಹೋವನು.",
			"9": "<sup>9</sup> ಈ ನನ್ನ ಆಜ್ಞೆಗಳನ್ನು ಅವರು ಅನುಸರಿಸಿ ನಡೆಯಬೇಕು. ಇವುಗಳನ್ನು ಮೀರಿ ನನ್ನ ನೈವೇದ್ಯವನ್ನು ಅಪವಿತ್ರಪಡಿಸಿದರೆ ಆ ದೋಷದ ಫಲವನ್ನು ಅನುಭವಿಸಿ ಸತ್ತಾರು. ಅವರನ್ನು ನನ್ನ ಸೇವೆಗೆ ಪ್ರತಿಷ್ಠಿಸಿಕೊಂಡ ಯೆಹೋವನು ನಾನು.",
			"10": "<sup>10</sup> ಯಾಜಕನಲ್ಲದ ಇತರನು ನೈವೇದ್ಯ ಪದಾರ್ಥವನ್ನು ಊಟಮಾಡಕೂಡದು. ಯಾಜಕನ ಬಳಿಯಲ್ಲಿ ಇಳುಕೊಂಡವನಾಗಲಿ ಕೂಲಿಯಾಳಾಗಲಿ ಅದನ್ನು ಊಟಮಾಡಬಾರದು.",
			"11": "<sup>11</sup> ಆದರೆ ಯಾಜಕನು ಕ್ರಯಕ್ಕೆ ತೆಗೆದುಕೊಂಡ ದಾಸರೂ ಅವನ ಮನೆಯಲ್ಲಿ ಹುಟ್ಟಿದ ದಾಸರೂ ಅದನ್ನು ಊಟಮಾಡಬಹುದು.",
			"12": "<sup>12</sup> ಯಾಜಕನ ಮಗಳು ಯಾಜಕನಲ್ಲದ ಇತರನಿಗೆ ಮದುವೆಯಾದರೆ ಅವಳು ನೈವೇದ್ಯ ಪದಾರ್ಥಗಳನ್ನು ಊಟಮಾಡಕೊಡದು.",
			"13": "<sup>13</sup> ಯಾಜಕನ ಮಗಳು ವಿಧವೆಯಾಗಿಯಾಗಲಿ ಗಂಡನಿಂದ ಬಿಡಲ್ಪಟ್ಟವಳಾಗಿಯಾಗಲಿ ಮಕ್ಕಳಿಲ್ಲದ ಪಕ್ಷಕ್ಕೆ ಬಾಲ್ಯದಲ್ಲಿ ತಂದೆಯ ಬಳಿಯಲ್ಲಿ ಇದ್ದಂತೆಯೇ ತಿರಿಗಿ ತಂದೆಯ ಮನೆ ಸೇರಿ ತಂದೆಯ ಆಹಾರದಲ್ಲಿ ಭಾಗಿಯಾಗಬಹುದು. ಯಾಜಕರಲ್ಲದ ಇತರರು ಅದನ್ನು ಉಣ್ಣಲೇಬಾರದು.",
			"14": "<sup>14</sup> ಯಾವನಾದರೂ ತಿಳಿಯದೆ ನೈವೇದ್ಯವಾದದ್ದನ್ನು ಊಟಮಾಡಿದರೆ ಅಷ್ಟನ್ನೂ ಅದರ ಐದನೆಯ ಭಾಗವನ್ನೂ ಯಾಜಕನಿಗೆ ತೆರಬೇಕು.",
			"15": "<sup>15</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಯೆಹೋವನಿಗೆ ಮೀಸಲೆಂದು ಇಟ್ಟ ನೈವೇದ್ಯಪದಾರ್ಥಗಳನ್ನು [ಯಾಜಕರು] ಸಾಧಾರಣವೆಂದೆಣಿಸಬಾರದು.",
			"16": "<sup>16</sup> ಹಾಗೆ ಎಣಿಸಿದರೆ ತಾವು ಆ ನೈವೇದ್ಯಪದಾರ್ಥಗಳನ್ನು ಊಟಮಾಡುವದರಿಂದ ಪಾಪಕ್ಕೆ ಒಳಗಾಗಿ ಶಿಕ್ಷೆಹೊಂದಾರು. ನನ್ನ ಸೇವೆಗೆ ಅವರನ್ನು ಪ್ರತಿಷ್ಠಿಸಿಕೊಂಡ ಯೆಹೋವನು ನಾನು.",
			"17": "<sup>17</sup> ಯೆಹೋವನು ಮೋಶೆಗೆ ಹೇಳಿದ್ದೇನಂದರೆ -",
			"18": "<sup>18</sup> ನೀನು ಆರೋನನಿಗೂ ಅವನ ಮಕ್ಕಳಿಗೂ ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರಿಗೂ ಹೀಗೆ ಆಜ್ಞಾಪಿಸಬೇಕು - ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಆಗಲಿ ಅವರ ನಡುವೆ ವಾಸಿಸುವ ಇತರರಲ್ಲಿಯಾಗಲಿ ಯಾವನಾದರೂ ಹರಕೆಯಾಗಿ ಅಥವಾ ಕಾಣಿಕೆಯಾಗಿ ಯೆಹೋವನಿಗೆ ಸರ್ವಾಂಗಹೋಮಮಾಡುವಾಗ",
			"19": "<sup>19</sup> ಅದು ಸಮರ್ಪಕವಾಗುವಂತೆ ದನಗಳಿಂದಾಗಲಿ ಆಡುಕುರಿಗಳಿಂದಾಗಲಿ ಪೂರ್ಣಾಂಗವಾದ ಗಂಡನ್ನೇ ಒಪ್ಪಿಸಬೇಕು.",
			"20": "<sup>20</sup> ಕುಂದುಕೊರತೆ ಇರುವಂಥದನ್ನು ತರಬಾರದು; ಅಂಥದು ಸಮರ್ಪಕವಾಗುವದಿಲ್ಲ.",
			"21": "<sup>21</sup> ಯಾವನಾದರೂ ಸಮಾಧಾನಯಜ್ಞಕ್ಕಾಗಿ ಹರಕೆಯಾಗಿಯಾಗಲಿ ಕಾಣಿಕೆಯಾಗಿಯಾಗಲಿ ದನವನ್ನು ಅಥವಾ ಆಡುಕುರಿಗಳನ್ನು ತಂದರೆ ಅವು ಸಮರ್ಪಕವಾಗುವದಕ್ಕೆ ಪೂರ್ಣಾಂಗವಾಗಿಯೇ ಇರಬೇಕು; ಅದರಲ್ಲಿ ಯಾವ ಕುಂದುಕೊರತೆಯೂ ಇರಬಾರದು.",
			"22": "<sup>22</sup> ಕುರುಡಾದದ್ದು ಕುಂಟಾದದ್ದು ಗಾಯವಾದದ್ದು ಹುಣ್ಣುಳ್ಳದ್ದು ಕಜ್ಜಿ ತುರಿಯುಳ್ಳದ್ದು ಇಂಥವುಗಳನ್ನು ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಿಸಲೂಬಾರದು, ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಹೋಮಮಾಡಲೂ ಬಾರದು.",
			"23": "<sup>23</sup> ಹೋರಿಯ ಇಲ್ಲವೆ ಕುರಿಯ ಅವಯವಗಳಲ್ಲಿ ಏನಾದರೂ ಹೆಚ್ಚುಕಡಿಮೆ ಇದ್ದರೆ ಅದನ್ನು ಕಾಣಿಕೆಯಾಗಿ ಸಮರ್ಪಿಸಬಹುದೇ ಹೊರತು ಹರಕೆಯಾಗಿ ಒಪ್ಪಿಸಿದರೆ ಅದು ಸಮರ್ಪಕವಾಗುವದಿಲ್ಲ.",
			"24": "<sup>24</sup> ಬೀಜಹೊಡೆದ ಕುಟ್ಟಿದ ಒಡೆದ ಕೊಯಿದ ಪಶುವನ್ನೂ ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಿಸಕೂಡದು; ಮತ್ತು ನಿಮ್ಮ ದೇಶದಲ್ಲಿ [ಪಶುಗಳಿಗೆ] ಹಾಗೆ ಮಾಡಲೇ ಬಾರದು.",
			"25": "<sup>25</sup> ಅನ್ಯದೇಶದವನಿಂದ ತೆಗೆದುಕೊಂಡು ಅಂಥ ಪಶುವನ್ನು ನಿಮ್ಮ ದೇವರಿಗೆ ಆಹಾರವಾಗಿ ಒಪ್ಪಿಸಬಾರದು; ಅದು ಕುಂದುಳ್ಳದ್ದು, ಪೂರ್ಣಾಂಗವಾದದ್ದಲ್ಲ; ಆದಕಾರಣ ಸಮರ್ಪಕವಾಗುವದಿಲ್ಲ.",
			"26": "<sup>26</sup> ಯೆಹೋವನು ಮೋಶೆಗೆ ಹೇಳಿದ್ದೇನಂದರೆ -",
			"27": "<sup>27</sup> ಕರುವಾಗಲಿ ಕುರಿಮರಿಯಾಗಲಿ ಆಡುಮರಿಯಾಗಲಿ ಹುಟ್ಟಿದಾಗ ಅದು ಏಳು ದಿವಸ ತಾಯಿಯ ಬಳಿಯಲ್ಲಿ ಇರಬೇಕು. ಎಂಟನೆಯ ದಿವಸ ಮೊದಲುಗೊಂಡು ಅದನ್ನು ಯೆಹೋವನಿಗೆ ಹೋಮವಾಗಿ ಸಮರ್ಪಿಸಿದರೆ ಅಂಗೀಕಾರವಾಗುವದು.",
			"28": "<sup>28</sup> ಈದ ಆಕಳನ್ನೂ ಅದರ ಕರುವನ್ನೂ ಇಲ್ಲವೆ ಈದ ಆಡುಕುರಿಗಳನ್ನೂ ಅವುಗಳ ಮರಿಗಳನ್ನೂ ಒಂದೇ ದಿನದಲ್ಲಿ ವಧಿಸಬಾರದು.",
			"29": "<sup>29</sup> ನೀವು ಕೃತಜ್ಞತೆಯನ್ನು ತೋರಿಸುವದಕ್ಕಾಗಿ ಯೆಹೋವನಿಗೆ ಮಾಡುವ ಯಜ್ಞವನ್ನು ಮೆಚ್ಚಿಗೆಯಾದ ರೀತಿಯಲ್ಲೇ ಸಮರ್ಪಿಸಬೇಕು.",
			"30": "<sup>30</sup> ಅದೇ ದಿವಸದಲ್ಲಿ ಅದನ್ನು ಊಟಮಾಡಬೇಕು; ಮರುದಿನದವರೆಗೆ ಸ್ವಲ್ಪವನ್ನಾದರೂ ಮಿಗಿಸಬಾರದು. ನಾನು ಯೆಹೋವನು.",
			"31": "<sup>31</sup> ಈ ನನ್ನ ಆಜ್ಞೆಗಳನ್ನು ನೀವು ಅನುಸರಿಸಿ ನಡೆಯಬೇಕು; ನಾನು ಯೆಹೋವನು.",
			"32": "<sup>32</sup> ನನ್ನ ಪರಿಶುದ್ಧ ನಾಮದ ಘನತೆಗೆ ಕುಂದುತರಬಾರದು; ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ನಾನು ಪರಿಶುದ್ಧನೆಂದೆಣಿಸಲ್ಪಡಬೇಕು; ನಾನು ನಿಮ್ಮನ್ನು ನನ್ನ ಸೇವೆಗೆ ಪ್ರತಿಷ್ಠಿಸಿಕೊಂಡ ಯೆಹೋವನು.",
			"33": "<sup>33</sup> ನಿಮ್ಮ ದೇವರಾಗುವದಕ್ಕೆ ನಿಮ್ಮನ್ನು ಐಗುಪ್ತದೇಶದಿಂದ ಬರಮಾಡಿದೆನಲ್ಲಾ; ನಾನು ಯೆಹೋವನು."
		},
		"23": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಮೋಶೆಗೆ ಹೇಳಿದ್ದೇನಂದರೆ -",
			"2": "<sup>2</sup> ನೀನು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಹೀಗೆ ಆಜ್ಞಾಪಿಸಬೇಕು - ಯೆಹೋವನಿಂದ ನೇಮಕವಾದ ಹಬ್ಬಗಳ ದಿನಗಳು ಇವೇ; ಈ ದಿನಗಳಲ್ಲಿ ದೇವಾರಾಧನೆಗಾಗಿ ಸಭೆಕೂಡಬೇಕೆಂಬದಾಗಿ ಸಾರಬೇಕು. ನಾನು ನೇಮಿಸಿರುವ ಹಬ್ಬದದಿನಗಳು ಯಾವವಂದರೆ -",
			"3": "<sup>3</sup> ಆರು ದಿನಗಳಲ್ಲಿ ನೀವು ಕೆಲಸವನ್ನು ನಡಿಸಬೇಕು; ಏಳನೆಯ ದಿನವು ಯಾವ ಕೆಲಸವನ್ನೂ ಮಾಡಕೂಡದ ಸಬ್ಬತ್ ದಿನವಾಗಿದೆ. ಅದರಲ್ಲಿ ದೇವಾರಾಧನೆಗಾಗಿ ಸಭೆಕೂಡಬೇಕು. ಆ ದಿನದಲ್ಲಿ ಯಾವ ವಿಧವಾದ ಕೆಲಸವನ್ನೂ ಮಾಡಕೂಡದು. ನಿಮ್ಮ ಎಲ್ಲಾ ನಿವಾಸಸ್ಥಳಗಳಲ್ಲಿ ಅದು ಯೆಹೋವನಿಗೆ ಮೀಸಲಾದ ವಿಶ್ರಾಂತಿ ದಿನವಾಗಿದೆ.",
			"4": "<sup>4</sup> ಯೆಹೋವನಿಂದ ನೇಮಕವಾದ ಹಬ್ಬದ ಕಾಲಗಳು ಇವೇ; ಈ ಕಾಲಗಳಲ್ಲಿ ದೇವಾರಾಧನೆಗಾಗಿ ಸಭೆಕೂಡಬೇಕು. ನೇಮಕವಾದ ದಿನಗಳಲ್ಲಿ ಇವುಗಳನ್ನು ಪ್ರಸಿದ್ಧಪಡಿಸಬೇಕು.",
			"5": "<sup>5</sup> ಮೊದಲನೆಯ ತಿಂಗಳಿನ ಹದಿನಾಲ್ಕನೆಯ ದಿನದ ಸಂಜೇವೇಳೆಯಲ್ಲಿ ಯೆಹೋವನು ನೇಮಿಸಿದ ಪಸ್ಕಹಬ್ಬವಾಗಬೇಕು.",
			"6": "<sup>6</sup> ಅದೇ ತಿಂಗಳಿನ ಹದಿನೈದನೆಯ ದಿನದಲ್ಲಿ ಹುಳಿಯಿಲ್ಲದ ರೊಟ್ಟಿಗಳನ್ನು ತಿನ್ನಬೇಕಾದ ಜಾತ್ರೆಯನ್ನು ಯೆಹೋವನಿಗೋಸ್ಕರ ಆಚರಿಸಬೇಕು. ಅದು ಮೊದಲುಗೊಂಡು ಏಳು ದಿನಗಳಲ್ಲಿಯೂ ಹುಳಿಯಿಲ್ಲದ ರೊಟ್ಟಿಗಳನ್ನು ಊಟಮಾಡಬೇಕು.",
			"7": "<sup>7</sup> ಮೊದಲನೆಯ ದಿನದಲ್ಲಿ ದೇವಾರಾಧನೆಗಾಗಿ ಸಭೆಕೂಡಬೇಕು; ಆ ದಿನದಲ್ಲಿ ಯಾವ ಉದ್ಯೋಗವನ್ನೂ ನಡಿಸಬಾರದು.",
			"8": "<sup>8</sup> ಏಳು ದಿನಗಳಲ್ಲಿಯೂ ನೀವು ಯೆಹೋವನಿಗೆ ಹೋಮವನ್ನು ಸಮರ್ಪಿಸಬೇಕು. ಏಳನೆಯ ದಿನದಲ್ಲಿ ದೇವಾರಾಧನೆಗಾಗಿ ಸಭೆಕೂಡಬೇಕು. ಆ ದಿನದಲ್ಲಿ ಯಾವ ಉದ್ಯೋಗವನ್ನೂ ನಡಿಸಬಾರದು.",
			"9": "<sup>9</sup> ಯೆಹೋವನು ಮೋಶೆಗೆ ಹೇಳಿದ್ದೇನಂದರೆ -",
			"10": "<sup>10</sup> ನೀನು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಹೀಗೆ ಆಜ್ಞಾಪಿಸಬೇಕು - ನಾನು ನಿಮಗೆ ಕೊಡುವ ದೇಶದಲ್ಲಿ ನೀವು ಸೇರಿದ ನಂತರ ಅಲ್ಲಿನ [ಜವೆಗೋದಿಯ] ಪೈರನ್ನು ಕೊಯ್ಯುವಾಗ ಪ್ರಥಮಫಲದ ಸಿವುಡನ್ನು ಯಾಜಕನ ಬಳಿಗೆ ತಂದು ಒಪ್ಪಿಸಬೇಕು. ನೀವು ಅಂಗೀಕಾರವಾಗುವಂತೆ ಅವನು ಅದನ್ನು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನೈವೇದ್ಯವಾಗಿ ನಿವಾಳಿಸಬೇಕು.",
			"11": "<sup>11</sup> ಸಬ್ಬತ್‍ದಿನದ ಮಾರಣೆಯ ದಿನದಲ್ಲಿಯೇ ಅದನ್ನು ನಿವಾಳಿಸಬೇಕು.",
			"12": "<sup>12</sup> ಆ ಸಿವುಡನ್ನು ನೈವೇದ್ಯವಾಗಿ ನಿವಾಳಿಸುವ ದಿನದಲ್ಲಿ ನೀವು ಒಂದು ವರುಷದ ಪೂರ್ಣಾಂಗವಾದ ಟಗರನ್ನು ಸರ್ವಾಂಗಹೋಮವಾಗಿ ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಿಸಬೇಕು.",
			"13": "<sup>13</sup> ಅದರೊಂದಿಗೆ ಮಾಡಬೇಕಾದ ಧಾನ್ಯನೈವೇದ್ಯವು ಯಾವದಂದರೆ ಎಣ್ಣೆ ಬೆರೆತ ಆರು ಸೇರು ಗೋದಿಯ ಹಿಟ್ಟೇ. ಯೆಹೋವನಿಗೆ ಸುವಾಸನೆಯನ್ನುಂಟುಮಾಡುವದಕ್ಕೆ ಇದನ್ನು ಬೆಂಕಿಯಲ್ಲಿ ಹೋಮಮಾಡಬೇಕು. ಅದರೊಂದಿಗೆ ಸಮರ್ಪಿಸಬೇಕಾದ ಪಾನದ್ರವ್ಯವು ಒಂದುವರೆ ಸೇರು ದ್ರಾಕ್ಷಾರಸ.",
			"14": "<sup>14</sup> ನೀವು ನಿಮ್ಮ ದೇವರಿಗೆ ಸಲ್ಲಿಸಬೇಕಾದದ್ದನ್ನು ತಂದುಕೊಡುವವರೆಗೂ [ಆ ವರುಷದ ಬೆಳೆಯ] ರೊಟ್ಟಿಯನ್ನಾಗಲಿ ಸುಟ್ಟ ತೆನೆಗಳನ್ನಾಗಲಿ ಹಸಿತೆನೆಗಳನ್ನಾಗಲಿ ತಿನ್ನಲೇಕೂಡದು. ನಿಮಗೂ ನಿಮ್ಮ ಸಂತತಿಯವರಿಗೂ ನಿಮ್ಮ ಎಲ್ಲಾ ನಿವಾಸಸ್ಥಾನಗಳಲ್ಲಿಯೂ ಇದು ಶಾಶ್ವತನಿಯಮ.",
			"15": "<sup>15</sup> ಸಬ್ಬತ್‍ದಿನದ ಮಾರಣೆಯ ದಿನ ಮೊದಲುಗೊಂಡು ಅಂದರೆ ಆ ಪ್ರಥಮ ಸಿವುಡನ್ನು ನೈವೇದ್ಯವಾಗಿ ನಿವಾಳಿಸಿದ ದಿನ ಮೊದಲುಗೊಂಡು ಪೂರ್ಣವಾಗಿ ಏಳು ವಾರಗಳು ಮುಗಿಯುವಂತೆ ಐವತ್ತು ದಿನಗಳನ್ನು ಲೆಕ್ಕಿಸಬೇಕು.",
			"16": "<sup>16</sup> ಏಳನೆಯ ಸಬ್ಬತ್‍ದಿನದ ಮರುದಿನದಲ್ಲಿ ಯೆಹೋವನಿಗೆ ಹೊಸ ಬೆಳೆಯ ನೈವೇದ್ಯದ್ರವ್ಯವನ್ನು ಸಮರ್ಪಿಸಬೇಕು.",
			"17": "<sup>17</sup> ನಿಮ್ಮ ನಿವಾಸಗಳಿಂದ [ತಂದ ಹಿಟ್ಟಿನಲ್ಲಿ] ಆರು ಸೇರು ಹಿಟ್ಟಿನಿಂದ ಎರಡು ನೈವೇದ್ಯದ ರೊಟ್ಟಿಗಳನ್ನು ಮಾಡಬೇಕು. ಇವುಗಳನ್ನು ಹುಳಿಹಾಕಿದ ಗೋದಿಯ ಹಿಟ್ಟಿನಿಂದ ಮಾಡಿ ಪ್ರಥಮಫಲವಾಗಿ ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಿಸಬೇಕು.",
			"18": "<sup>18</sup> ಆ ರೊಟ್ಟಿಗಳೊಡನೆ ಒಂದು ವರುಷದ ಪೂರ್ಣಾಂಗವಾದ ಏಳು ಕುರಿಗಳನ್ನೂ ಒಂದು ಹೋರಿಯನ್ನೂ ಎರಡು ಟಗರುಗಳನ್ನೂ ಸಮರ್ಪಿಸಬೇಕು. ಇವು ಧಾನ್ಯದ್ರವ್ಯದೊಡನೆಯೂ ಪಾನದ್ರವ್ಯದೊಡನೆಯೂ ಅಗ್ನಿ ಮೂಲಕ ಯೆಹೋವನಿಗೆ ಸುವಾಸನೆಯನ್ನುಂಟುಮಾಡುವ ಸರ್ವಾಂಗಹೋಮವಾಗುವವು.",
			"19": "<sup>19</sup> ಅದಲ್ಲದೆ ದೋಷಪರಿಹಾರಕ ಯಜ್ಞವಾಗಿ ಒಂದು ಹೋತವನ್ನೂ ಸಮಾಧಾನಯಜ್ಞಕ್ಕಾಗಿ ಒಂದು ವರುಷದ ಎರಡು ಟಗರುಗಳನ್ನೂ ಸಮರ್ಪಿಸಬೇಕು.",
			"20": "<sup>20</sup> ಯಾಜಕನು ಇವುಗಳನ್ನೂ ಪ್ರಥಮ ಫಲದ ರೊಟ್ಟಿಗಳನ್ನೂ ಎರಡು ಕುರಿಗಳನ್ನೂ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನೈವೇದ್ಯವಾಗಿ ನಿವಾಳಿಸಬೇಕು. ಅವು ಯೆಹೋವನಿಗೆ ಮೀಸಲಾಗಿ ಯಾಜಕನಿಗೆ ಸಲ್ಲಬೇಕು.",
			"21": "<sup>21</sup> ಆ ದಿವಸದಲ್ಲಿ ದೇವಾರಾಧನೆಗಾಗಿ ಸಭೆ ಕೂಡಬೇಕೆಂಬದಾಗಿ ಪ್ರಸಿದ್ಧಪಡಿಸಬೇಕು. ಅದರಲ್ಲಿ ಯಾವ ಉದ್ಯೋಗವನ್ನೂ ನಡಿಸಬಾರದು. ಇದು ನಿಮಗೂ ನಿಮ್ಮ ಸಂತತಿಯವರಿಗೂ ನಿಮ್ಮ ಎಲ್ಲಾ ನಿವಾಸಗಳಲ್ಲಿ ಶಾಶ್ವತನಿಯಮ.",
			"22": "<sup>22</sup> ನಿಮ್ಮ ದೇಶದ ಪೈರನ್ನು ಕೊಯ್ಯುವಾಗ ಹೊಲಗಳ ಮೂಲೆಗಳಲ್ಲಿರುವದನ್ನೆಲ್ಲಾ ಕೊಯ್ಯಬಾರದು, ಮತ್ತು ಹಕ್ಕಲಾಯಬಾರದು. ಇವನ್ನು ಬಡವರಿಗೋಸ್ಕರವೂ ಪರದೇಶದವರಿಗೋಸ್ಕರವೂ ಬಿಟ್ಟುಬಿಡಬೇಕು. ನಾನು ನಿಮ್ಮ ದೇವರಾಗಿರುವ ಯೆಹೋವನು.",
			"23": "<sup>23</sup> ಯೆಹೋವನು ಮೋಶೆಗೆ ಹೇಳಿದ್ದೇನಂದರೆ -",
			"24": "<sup>24</sup> ನೀನು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಹೀಗೆ ಆಜ್ಞಾಪಿಸಬೇಕು - ಏಳನೆಯ ತಿಂಗಳಿನ ಮೊದಲನೆಯ ದಿನದಲ್ಲಿ ನಿಮಗೆ ಸಂಪೂರ್ಣ ವಿರಾಮವಿರಬೇಕು. ಅದನ್ನು ತುತೂರಿಗಳ ಧ್ವನಿಯಿಂದ ಪ್ರಕಟಪಡಿಸಬೇಕು. ದೇವಾರಾಧನೆಗಾಗಿ ಸಭೆಕೂಡಬೇಕು.",
			"25": "<sup>25</sup> ಆ ದಿನದಲ್ಲಿ ಯಾವ ಉದ್ಯೋಗವನ್ನೂ ನಡಿಸಬಾರದು; ಯೆಹೋವನಿಗೆ ಹೋಮವನ್ನು ಮಾಡಬೇಕು.",
			"26": "<sup>26</sup> ಯೆಹೋವನು ಮೋಶೆಗೆ ಹೇಳಿದ್ದೇನಂದರೆ -",
			"27": "<sup>27</sup> ಅದೇ ಏಳನೆಯ ತಿಂಗಳಿನ ಹತ್ತನೆಯ ದಿನವು ಸರ್ವದೋಷಪರಿಹಾರಕ ದಿನ. ಅದರಲ್ಲಿ ದೇವಾರಾಧನೆಗಾಗಿ ಸಭೆಕೂಡಬೇಕು; ನೀವು ಪೂರ್ಣವಾಗಿ ಉಪವಾಸಮಾಡಬೇಕು, ಮತ್ತು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಹೋಮವನ್ನು ಮಾಡಬೇಕು.",
			"28": "<sup>28</sup> ಆ ದಿನದಲ್ಲಿ ಯಾವ ಉದ್ಯೋಗವನ್ನೂ ನಡಿಸಬಾರದು. ಅದು ದೋಷಪರಿಹಾರಕ ದಿವಸ; ಅದರಲ್ಲಿ ನಿಮಗೋಸ್ಕರ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ದೋಷಪರಿಹಾರಕಾಚಾರವು ನಡೆಯುವದು.",
			"29": "<sup>29</sup> ಯಾವನಾದರೂ ಆ ದಿನದಲ್ಲಿ ಉಪವಾಸ ಮಾಡದೆಹೋದರೆ ಅವನು ಕುಲದಿಂದ ತೆಗೆದು ಹಾಕಲ್ಪಡಬೇಕು.",
			"30": "<sup>30</sup> ಯಾವನಾದರೂ ಆ ದಿನದಲ್ಲಿ ಕೆಲಸವನ್ನು ಸ್ವಲ್ಪವಾಗಿ ನಡಿಸಿದರೂ ಅವನನ್ನು ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಇರದಂತೆ ನಾಶ ಮಾಡುವೆನು.",
			"31": "<sup>31</sup> ಆ ದಿನದಲ್ಲಿ ಯಾವ ಕೆಲಸವನ್ನೂ ಮಾಡಬಾರದು. ಇದು ನಿಮಗೂ ನಿಮ್ಮ ಸಂತತಿಯವರಿಗೂ ನಿಮ್ಮ ಎಲ್ಲಾ ನಿವಾಸಗಳಲ್ಲಿ ಶಾಶ್ವತನಿಯಮ.",
			"32": "<sup>32</sup> ಆ ದಿನವು ಸಂಪೂರ್ಣ ವಿರಾಮವುಳ್ಳ ವಿಶ್ರಾಂತಿ ದಿನವಾಗಿರಬೇಕು. ಅದರಲ್ಲಿ ಉಪವಾಸದಿಂದಿರಬೇಕು. ಆ ತಿಂಗಳಿನ ಒಂಭತ್ತನೆಯ ದಿನದ ಸಾಯಂಕಾಲದಲ್ಲಿ ಪ್ರಾರಂಭಿಸಿ ಮರುದಿನದ ಸಾಯಂಕಾಲದವರೆಗೆ ನೀವು ಆ ವಿರಾಮದಿನವನ್ನು ಆಚರಿಸಬೇಕು.",
			"33": "<sup>33</sup> ಯೆಹೋವನು ಮೋಶೆಗೆ ಹೇಳಿದ್ದೇನಂದರೆ -",
			"34": "<sup>34</sup> ನೀನು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಹೀಗೆ ಆಜ್ಞಾಪಿಸು - ಏಳನೆಯ ತಿಂಗಳಿನ ಹದಿನೈದನೆಯ ದಿನ ಮೊದಲುಗೊಂಡು ಏಳು ದಿನಗಳವರೆಗೆ ಪರ್ಣಶಾಲೆಗಳ ಜಾತ್ರೆಯನ್ನು ಯೆಹೋವನಿಗೋಸ್ಕರ ಆಚರಿಸಬೇಕು.",
			"35": "<sup>35</sup> ಮೊದಲನೆಯ ದಿನದಲ್ಲಿ ದೇವಾರಾಧನೆಗಾಗಿ ಸಭೆಕೂಡಬೇಕು; ಯಾವ ಉದ್ಯೋಗವನ್ನೂ ನಡಿಸಬಾರದು.",
			"36": "<sup>36</sup> ಆ ಏಳು ದಿನಗಳಲ್ಲಿಯೂ ನೀವು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಹೋಮಮಾಡಬೇಕು. ಎಂಟನೆಯ ದಿನದಲ್ಲಿಯೂ ದೇವಾರಾಧನೆಗಾಗಿ ಸಭೆಕೂಡಬೇಕು, ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಹೋಮಮಾಡಬೇಕು. ಅದು ಸಭೆಕೂಡುವ ದಿನವಾದದರಿಂದ ಅದರಲ್ಲಿ ಯಾವ ಉದ್ಯೋಗವನ್ನೂ ನಡಿಸಬಾರದು.",
			"37": "<sup>37</sup> ಯೆಹೋವನು ನೇಮಿಸಿರುವ ಸಬ್ಬತ್ ದಿನಗಳ ಹೊರತಾಗಿ ಮೇಲೆ ಕಂಡವುಗಳೇ ಯೆಹೋವನ ಹಬ್ಬದ ದಿನಗಳು. ಆ ದಿನಗಳಲ್ಲಿ ದೇವಾರಾಧನೆಗಾಗಿ ಸಭೆಕೂಡುವಂತೆ ನೀವು ಪ್ರಕಟಿಸಬೇಕು.",
			"38": "<sup>38</sup> ಯೆಹೋವನಿಗೆ ಒಪ್ಪಿಸಬೇಕಾದ ಕಪ್ಪ, ಕಾಣಿಕೆ, ಹರಕೆ ಇವುಗಳನ್ನು ನೀವು ಸಮರ್ಪಿಸಬೇಕಲ್ಲದೆ ಮೇಲೆ ಸೂಚಿಸಿರುವ ಹಬ್ಬದ ದಿನಗಳಲ್ಲಿಯೂ ಆಯಾ ದಿನಕ್ಕೆ ನೇಮಿಸಿರುವ ಪ್ರಕಾರ ಸರ್ವಾಂಗಹೋಮ, ನೈವೇದ್ಯದ್ರವ್ಯ, ಸಮಾಧಾನಯಜ್ಞ, ಪಾನದ್ರವ್ಯ ಇವುಗಳನ್ನೂ ತಂದು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಹೋಮಮಾಡಬೇಕು.",
			"39": "<sup>39</sup> ಏಳನೆಯ ತಿಂಗಳಿನ ಹದಿನೈದನೆಯ ದಿನದಲ್ಲಿ ನೀವು ತೋಟ ಮತ್ತು ತೋಪುಗಳ ಬೆಳೆಯನ್ನು ಕೂಡಿಸಿದನಂತರ ಯೆಹೋವನು ನೇಮಿಸಿದ [ಪರ್ಣಶಾಲೆಗಳ] ಜಾತ್ರೆಯನ್ನು ಏಳು ದಿನಗಳವರೆಗೂ ಆಚರಿಸಬೇಕು. ಮೊದಲನೆಯ ದಿನದಲ್ಲಿಯೂ ಎಂಟನೆಯ ದಿನದಲ್ಲಿಯೂ ಯಾವ ಕೆಲಸವನ್ನೂ ಮಾಡದೆ ಸಂಪೂರ್ಣವಿರಾಮದಿಂದಿರಬೇಕು.",
			"40": "<sup>40</sup> ಮೊದಲನೆಯ ದಿನದಲ್ಲಿ ಶ್ರೇಷ್ಠ ವೃಕ್ಷದ ಹಣ್ಣುಗಳನ್ನೂ ಖರ್ಜೂರ ಮರದ ಗರಿಗಳನ್ನೂ ಎಲೆಗಳು ದಟ್ಟವಾಗಿರುವ ಮರಗಳ ಕೊಂಬೆಗಳನ್ನೂ ನೀರಿನ ಕಾಲುವೆಗಳ ಬಳಿಯಲ್ಲಿ ಬೆಳೆಯುವ ನೀರವಂಜಿ ಚಿಗುರುಗಳನ್ನೂ ತೆಗೆದುಕೊಂಡು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಏಳು ದಿನಗಳು ಸಂಭ್ರಮವಾಗಿರಬೇಕು.",
			"41": "<sup>41</sup> ಹೀಗೆ ಪ್ರತಿ ವರುಷದಲ್ಲಿಯೂ ಏಳು ದಿನಗಳವರೆಗೆ ಈ ಉತ್ಸವವನ್ನು ಆಚರಿಸಬೇಕು; ನಿಮಗೂ ನಿಮ್ಮ ಸಂತತಿಯವರಿಗೂ ಇದು ಶಾಶ್ವತನಿಯಮ; ಏಳನೆಯ ತಿಂಗಳಿನಲ್ಲಿ ಇದನ್ನು ಆಚರಿಸಬೇಕು.",
			"42": "<sup>42</sup> ಏಳು ದಿನಗಳು ನೀವು ಪರ್ಣಶಾಲೆಗಳಲ್ಲಿ ವಾಸವಾಗಿರಬೇಕು; ಸ್ವದೇಶಸ್ಥರಾದ ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರೂ ಪರ್ಣಶಾಲೆಗಳಲ್ಲಿಯೇ ವಾಸವಾಗಿರಬೇಕು.",
			"43": "<sup>43</sup> ನಾನು ನಿಮ್ಮನ್ನು ಐಗುಪ್ತದೇಶದಿಂದ ಬರಮಾಡಿದಾಗ ಇಸ್ರಾಯೇಲ್ಯರು ಪರ್ಣಶಾಲೆಗಳಲ್ಲಿ ವಾಸವಾಗಿರುವಂತೆ ಮಾಡಿದೆನೆಂಬದಾಗಿ ಇದರಿಂದ ನಿಮ್ಮ ಸಂತತಿಯವರಿಗೆ ತಿಳಿಯುವದು. ನಾನು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು.",
			"44": "<sup>44</sup> ಯೆಹೋವನಿಂದ ನೇಮಕವಾದ ಹಬ್ಬಗಳ ದಿನಗಳ ವಿಷಯವಾಗಿ ಮೋಶೆಯು ಅದೇ ಮೇರೆಗೆ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ತಿಳಿಸಿದನು."
		},
		"24": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಮೋಶೆಗೆ ಹೇಳಿದ್ದೇನಂದರೆ -",
			"2": "<sup>2</sup> ದೇವಸ್ಥಾನದಲ್ಲಿನ ದೀಪಗಳನ್ನು ಪ್ರತಿನಿತ್ಯವೂ ಉರಿಸುವದಕ್ಕಾಗಿ ಇಸ್ರಾಯೇಲ್ಯರು ಎಣ್ಣೇಮರದ ಕಾಯಿಗಳನ್ನು ಕುಟ್ಟಿತೆಗೆದ ನಿರ್ಮಲವಾದ ಎಣ್ಣೆಯನ್ನು ನಿನಗೆ ತಂದುಕೊಡಬೇಕೆಂದು ಅವರಿಗೆ ಆಜ್ಞಾಪಿಸು.",
			"3": "<sup>3</sup> ದೇವದರ್ಶನದ ಗುಡಾರದಲ್ಲಿ ಆಜ್ಞಾಶಾಸನಗಳ ಮಂಜೂಷದ ಮುಂದಣ ತೆರೆಯ ಹೊರಗೆ ಆ ದೀಪಗಳು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸಾಯಂಕಾಲದಿಂದ ಉದಯದವರೆಗೂ ಉರಿಯುತ್ತಿರುವಂತೆ ಆರೋನನು ಅವುಗಳನ್ನು ಸರಿಪಡಿಸಬೇಕು. ಇದು ನಿಮಗೂ ನಿಮ್ಮ ಸಂತತಿಯವರಿಗೂ ಶಾಶ್ವತನಿಯಮ.",
			"4": "<sup>4</sup> ಅವನು ಪ್ರತಿನಿತ್ಯವೂ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಹಣತಿಗಳನ್ನು ಆ ಚೊಕ್ಕಬಂಗಾರದ ದೀಪಸ್ತಂಭದ ಮೇಲೆ ಸರಿಯಾಗಿ ಇಡಬೇಕು.",
			"5": "<sup>5</sup> ಅದಲ್ಲದೆ ನೀನು ಗೋದಿಯ ಹಿಟ್ಟಿನಿಂದ ಹನ್ನೆರಡು ರೊಟ್ಟಿಗಳನ್ನು ಮಾಡಬೇಕು; ಪ್ರತಿಯೊಂದು ರೊಟ್ಟಿಯು ಆರಾರು ಸೇರಿನದಾಗಿರಬೇಕು.",
			"6": "<sup>6</sup> ಅವುಗಳನ್ನು ಆರಾರರ ಮೇರೆಗೆ ಎರಡು ರಾಶಿಗಳಾಗಿ ಚೊಕ್ಕ ಬಂಗಾರದ ಮೇಜಿನ ಮೇಲೆ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಇಡಬೇಕು.",
			"7": "<sup>7</sup> ಒಂದೊಂದು ರಾಶಿಯ ಮೇಲೆ ಸ್ವಚ್ಫವಾದ ಧೂಪವನ್ನು ಇಡಬೇಕು. ಆ ರೊಟ್ಟಿಗಳ ನೈವೇದ್ಯವನ್ನು ಸೂಚಿಸುವದಕ್ಕಾಗಿ ಆ ಧೂಪವೇ ಯೆಹೋವನಿಗೆ ಹೋಮಮಾಡಲ್ಪಡಬೇಕು.",
			"8": "<sup>8</sup> ಯಾಜಕನು ಯಾವಾಗಲೂ ಪ್ರತಿ ಸಬ್ಬತ್‍ದಿನದಲ್ಲಿ ರೊಟ್ಟಿಗಳನ್ನು ತಂದು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಕ್ರಮಪಡಿಸಬೇಕು. ಶಾಶ್ವತವಾದ ನಿಬಂಧನೆಯ ಮೇರೆಗೆ ಅವುಗಳನ್ನು ಇಸ್ರಾಯೇಲ್ಯರಿಗೋಸ್ಕರ ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಿಸಬೇಕು.",
			"9": "<sup>9</sup> ಅವು ಆರೋನನಿಗೂ ಅವನ ಸಂತತಿಯವರಿಗೂ ಸಲ್ಲಬೇಕು. ಮಹಾಪರಿಶುದ್ಧವಾದ ಅವುಗಳನ್ನು ದೇವಸ್ಥಾನದ ಪ್ರಾಕಾರದಲ್ಲಿಯೇ ತಿನ್ನಬೇಕು. ಅವು ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಿತವಾದ ಹೋಮದ್ರವ್ಯಗಳಿಗೆ ಸೇರಿದವುಗಳಾದದರಿಂದ ಶಾಶ್ವತನಿಯಮದ ಪ್ರಕಾರ ಯಾಜಕರಿಗೇ ಸಲ್ಲಬೇಕು.",
			"10": "<sup>10</sup> ಇಸ್ರಾಯೇಲ್ ಸ್ತ್ರೀಯಲ್ಲಿ ಐಗುಪ್ತ ಪುರುಷನಿಂದ ಹುಟ್ಟಿದವನೊಬ್ಬನು ಬಂದು ಇಸ್ರಾಯೇಲ್ಯರ ಪಾಳೆಯದಲ್ಲಿ ಇಸ್ರಾಯೇಲ್ಯನಾದ ಒಬ್ಬ ಮನುಷ್ಯನ ಸಂಗಡ ಜಗಳವಾಡುತ್ತಾ ಬೈದು",
			"11": "<sup>11</sup> [ಯೆಹೋವನ] ಹೆಸರನ್ನು ದೂಷಿಸಿದದರಿಂದ ಅವನನ್ನು ಮೋಶೆಯ ಬಳಿಗೆ ಹಿಡಿದು ತಂದರು. ಅವನ ತಾಯಿಯು ದಾನ್‍ಕುಲದ ದಿಬ್ರೀಯ ಮಗಳಾದ ಶೆಲೋಮೀತ್ ಎಂಬವಳು.",
			"12": "<sup>12</sup> ಅವನ ವಿಷಯವಾಗಿ ಯೆಹೋವನ ತೀರ್ಪನ್ನು ತಿಳುಕೊಳ್ಳುವ ಪರ್ಯಂತರ ಅವನನ್ನು ಕಾವಲಲ್ಲಿರಿಸಿದರು.",
			"13": "<sup>13</sup> ಆಗ ಯೆಹೋವನು ಮೋಶೆಗೆ ಹೇಳಿದ್ದೇನಂದರೆ -",
			"14": "<sup>14</sup> ಆ ದೂಷಿಸಿದವನನ್ನು ಪಾಳೆಯದ ಹೊರಗೆ ಒಯ್ಯಬೇಕು. ಅವನ ದೂಷಣೆಯ ಮಾತುಗಳನ್ನು ಕೇಳಿದವರೆಲ್ಲರೂ ಅವನ ತಲೆಯ ಮೇಲೆ ತಮ್ಮ ಕೈಗಳನ್ನಿಟ್ಟನಂತರ ಸಮೂಹದವರೆಲ್ಲರೂ ಅವನನ್ನು ಕಲ್ಲೆಸೆದು ಕೊಲ್ಲಬೇಕು.",
			"15": "<sup>15</sup> ಮತ್ತು ನೀನು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಹೀಗೆ ಹೇಳಬೇಕು - ತನ್ನ ದೇವರನ್ನು ದೂಷಿಸಿದವನು ಆ ದೋಷದ ಫಲವನ್ನು ಅನುಭವಿಸಬೇಕು.",
			"16": "<sup>16</sup> ಯೆಹೋವನ ಹೆಸರನ್ನು ನಿಂದಿಸಿದವನಿಗೆ ಮರಣಶಿಕ್ಷೆಯಾಗಬೇಕು; ಸಮೂಹದವರೆಲ್ಲರೂ ಅವನನ್ನು ಕಲ್ಲೆಸೆದು ಕೊಲ್ಲಬೇಕು. ಅನ್ಯದೇಶಸ್ಥನೇಯಾಗಲಿ ಸ್ವದೇಶಸ್ಥನೇಯಾಗಲಿ ಯೆಹೋವನ ಹೆಸರನ್ನು ನಿಂದಿಸಿದರೆ ಅವನಿಗೆ ಮರಣಶಿಕ್ಷೆಯಾಗಬೇಕು.",
			"17": "<sup>17</sup> ಮನುಷ್ಯನನ್ನು ಹೊಡೆದು ಕೊಂದವನಿಗೆ ಮರಣ ಶಿಕ್ಷೆಯಾಗಬೇಕು.",
			"18": "<sup>18</sup> ಪಶುವನ್ನು ಹೊಡೆದು ಕೊಂದವನಿಂದ ಅದಕ್ಕೆ ಪ್ರತಿಯಾಗಿ ಈಡನ್ನು ಕೊಡಿಸಬೇಕು; ಪ್ರಾಣಿಗೆ ಪ್ರತಿಯಾಗಿ ಪ್ರಾಣಿಯನ್ನು ಕೊಡಿಸಬೇಕು.",
			"19": "<sup>19</sup> ಯಾವನಾದರೂ ಮತ್ತೊಬ್ಬನನ್ನು ಅಂಗಹೀನಮಾಡಿದರೆ ಅವನು ಮಾಡಿದಂತೆಯೇ ಅವನಿಗೆ ಮಾಡಿಸಬೇಕು.",
			"20": "<sup>20</sup> ಅವಯವವನ್ನು ಮುರಿದವನಿಗೆ ಅವಯವವನ್ನು ಮುರಿಯುವದೇ ಶಿಕ್ಷೆ. ಕಣ್ಣಿಗೆ ಪ್ರತಿಯಾಗಿ ಕಣ್ಣನ್ನೂ ಹಲ್ಲಿಗೆ ಪ್ರತಿಯಾಗಿ ಹಲ್ಲನ್ನೂ ತೆಗಿಸಬೇಕು. ಮತ್ತೊಬ್ಬನನ್ನು ಅಂಗಹೀನ ಮಾಡಿದವನಿಗೆ ಈ ಪ್ರಕಾರ ಪ್ರತೀಕಾರ ಮಾಡಿಸಬೇಕು.",
			"21": "<sup>21</sup> ಪಶುವನ್ನು ಕೊಂದವನು ಅದಕ್ಕೆ ಬದಲಾಗಿ ಈಡು ಕೊಡಬೇಕು. ಮನುಷ್ಯನನ್ನು ಕೊಂದವನಿಗೆ ಮರಣಶಿಕ್ಷೆಯಾಗಬೇಕು.",
			"22": "<sup>22</sup> ಅನ್ಯರಿಗಾಗಲಿ ಸ್ವದೇಶದವರಿಗಾಗಲಿ ಪಕ್ಷಪಾತವಿಲ್ಲದೆ ಒಂದೇ ವಿಧಿಯಿರಬೇಕು. ನಾನು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು.",
			"23": "<sup>23</sup> ಮೋಶೆ ಈ ಮಾತುಗಳನ್ನು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ತಿಳಿಸಲಾಗಿ ಅವರು ದೂಷಿಸಿದವನಾದ ಆ ಮನುಷ್ಯನನ್ನು ಪಾಳೆಯದ ಹೊರಕ್ಕೆ ತೆಗೆದುಕೊಂಡುಹೋಗಿ ಕಲ್ಲೆಸೆದು ಕೊಂದುಬಿಟ್ಟರು. ಯೆಹೋವನು ಮೋಶೆಗೆ ಆಜ್ಞಾಪಿಸಿದಂತೆಯೇ ಇಸ್ರಾಯೇಲ್ಯರು ಮಾಡಿದರು."
		},
		"25": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಸೀನಾಯಿಬೆಟ್ಟದಲ್ಲಿ ಮೋಶೆಗೆ ಹೇಳಿದ್ದೇನಂದರೆ -",
			"2": "<sup>2</sup> ನೀನು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಹೀಗೆ ಆಜ್ಞಾಪಿಸು - ನಾನು ನಿಮಗೆ ಕೊಡುವ ದೇಶಕ್ಕೆ ನೀವು ಬಂದಾಗ ಆ ದೇಶವೂ ಯೆಹೋವನಿಗೆ ಸಬ್ಬತ್ ಕಾಲವನ್ನು ಆಚರಿಸಬೇಕು.",
			"3": "<sup>3</sup> ಆರು ವರುಷಗಳು ನೀವು ಹೊಲದಲ್ಲಿ ಬೀಜವನ್ನು ಬಿತ್ತಬಹುದು, ದ್ರಾಕ್ಷೇತೋಟದ ಕೆಲಸವನ್ನು ನಡಿಸಬಹುದು; ಹೊಲತೋಟಗಳ ಬೆಳೆಗಳನ್ನು ಸಂಗ್ರಹಿಸಬಹುದು.",
			"4": "<sup>4</sup> ಆದರೆ ಏಳನೆಯ ವರುಷವು ಯಾವ ಬೇಸಾಯವೂ ನಡೆಯಬಾರದ ವಿಶ್ರಾಂತಿಕಾಲವಾಗಿಯೂ ಯೆಹೋವನಿಗೆ ಆಚರಿಸಬೇಕಾದ ಸಬ್ಬತ್‍ಕಾಲವಾಗಿಯೂ ಇರಬೇಕು; ಹೊಲದಲ್ಲಿ ಬೀಜವನ್ನು ಬಿತ್ತಬಾರದು, ದ್ರಾಕ್ಷೇತೋಟದ ಕೆಲಸವನ್ನು ನಡಿಸಬಾರದು.",
			"5": "<sup>5</sup> ಹೊಲದಲ್ಲಿ ತಾನಾಗಿ ಬೆಳೆದ ಪೈರನ್ನು ನೀವು ಕೂಡಿಸಿಟ್ಟುಕೊಳ್ಳಬಾರದು; ನೀವು ನೋಡಿಕೊಳ್ಳದೆ ಬಿಟ್ಟ ದ್ರಾಕ್ಷಾಲತೆಗಳಲ್ಲಿ ಬಂದ ಹಣ್ಣನ್ನು ಸಂಗ್ರಹಿಸಬಾರದು. ಆ ವರುಷವೆಲ್ಲಾ ಭೂಮಿಗೆ ಸಂಪೂರ್ಣವಾಗಿ ವಿಶ್ರಾಂತಿಯಿರಬೇಕು.",
			"6": "<sup>6</sup> ಆದರೆ ಅಂಥ ಸಬ್ಬತ್ ಸಂವತ್ಸರದಲ್ಲಿ ತಾನಾಗಿ ಭೂಮಿಯಲ್ಲಿ ಹುಟ್ಟಿದ್ದು ನಿಮಗೂ ನಿಮ್ಮ ದಾಸದಾಸಿಯರಿಗೂ ಕೂಲಿಯವರಿಗೂ",
			"7": "<sup>7</sup> ನಿಮ್ಮ ಬಳಿಯಲ್ಲಿ ಇಳುಕೊಂಡವರಿಗೂ ನಿಮ್ಮ ಪಶುಗಳಿಗೂ ದೇಶದಲ್ಲಿರುವ ಕಾಡುಮೃಗಗಳಿಗೂ ಆಹಾರವಾಗಬಹುದು.",
			"8": "<sup>8</sup> ಅದಲ್ಲದೆ ಏಳು ವರುಷಕ್ಕೊಂದರಂತೆ ಏಳು ಸಬ್ಬತ್ ವರುಷಗಳನ್ನು ಲೆಕ್ಕಿಸಬೇಕು. ಆ ಏಳು ಸಬ್ಬತ್ ಸಂವತ್ಸರಗಳ ಕಾಲವು ಅಂದರೆ ನಾಲ್ವತ್ತೊಂಭತ್ತು ವರುಷಗಳು ಕಳೆದ ಮೇಲೆ",
			"9": "<sup>9</sup> ಏಳನೆಯ ತಿಂಗಳಿನ ಹತ್ತನೆಯ ದಿವಸದಲ್ಲಿ ನಿಮ್ಮ ದೇಶದಲ್ಲೆಲ್ಲಾ ಕೊಂಬಿನ ಧ್ವನಿಯನ್ನು ಮಾಡಿಸಬೇಕು; ಸಕಲ ದೋಷಪರಿಹಾರಕವಾದ ಆ ದಿನದಲ್ಲೇ ನಿಮ್ಮ ದೇಶದಲ್ಲೆಲ್ಲಾ ಆ ಕೊಂಬಿನ ಧ್ವನಿಯನ್ನು ಮಾಡಿಸಬೇಕು.",
			"10": "<sup>10</sup> ನೀವು ಐವತ್ತನೆಯ ವರುಷವನ್ನು ದೇವರಿಗೆ ಮೀಸಲಾದ ವರುಷವೆಂದು ಭಾವಿಸಿ ಅದರಲ್ಲಿ ದೇಶದ ನಿವಾಸಿಗಳೆಲ್ಲರಿಗೂ ಬಿಡುಗಡೆಯಾಯಿತೆಂಬದಾಗಿ ಸಾರಬೇಕು. ಅದು ಜೂಬಿಲಿ ಸಂವತ್ಸರವಾದದರಿಂದ ನೀವೆಲ್ಲರು ನಿಮ್ಮ ನಿಮ್ಮ ಸ್ವಂತ ಭೂಮಿಗಳಿಗೂ ಸ್ವಜನರ ಬಳಿಗೂ ಹೋಗಿ ಇರಬಹುದು.",
			"11": "<sup>11</sup> ಆ ಐವತ್ತನೆಯ ವರುಷದಲ್ಲಿ ಅಂದರೆ ಜೂಬಿಲಿ ಸಂವತ್ಸರದಲ್ಲಿ ನೀವು ಬೀಜವನ್ನು ಬಿತ್ತಬಾರದು; ತಾನಾಗಿ ಬೆಳೆದ ಪೈರನ್ನು ಕೂಡಿಸಿಟ್ಟುಕೊಳ್ಳಬಾರದು; ನೀವು ನೋಡಿಕೊಳ್ಳದೆ ಬಿಟ್ಟ ದ್ರಾಕ್ಷಾಲತೆಗಳಲ್ಲಿ ಆದ ಹಣ್ಣನ್ನು ಸಂಗ್ರಹಿಸಬಾರದು.",
			"12": "<sup>12</sup> ಅದು ಜೂಬಿಲಿ ವರುಷವಾದದರಿಂದ ನೀವು ಅದನ್ನು ಮೀಸಲಾದದ್ದೆಂದು ಭಾವಿಸಬೇಕು. ಹೊಲದಲ್ಲಿ ತಾನಾಗಿ ಬೆಳೆದದ್ದನ್ನು ಊಟಮಾಡಬಹುದು.",
			"13": "<sup>13</sup> ಜೂಬಿಲಿ ವರುಷದಲ್ಲಿ ನಿಮ್ಮ ನಿಮ್ಮ ಸ್ವಂತ ಭೂಮಿಗಳು ತಿರಿಗಿ ನಿಮ್ಮ ವಶಕ್ಕೆ ಬರುವವು.",
			"14": "<sup>14</sup> ನೀವು [ಸ್ಥಿರಸೊತ್ತನ್ನು] ಸ್ವದೇಶದವರೊಡನೆ ಕ್ರಯವಿಕ್ರಯ ಮಾಡುವಾಗ ಇದರ ವಿಷಯದಲ್ಲಿ ಅನ್ಯಾಯಮಾಡಬಾರದು.",
			"15": "<sup>15</sup> ಕಳೆದ ಜೂಬಿಲಿ ಸಂವತ್ಸರದಿಂದ ಇಷ್ಟು ವರುಷವಾಯಿತೆಂದು ಲೆಕ್ಕಿಸಿ ಸ್ವದೇಶದವನಿಂದ ಕ್ರಯಕ್ಕೆ ತೆಗೆದುಕೊಳ್ಳಬೇಕು;",
			"16": "<sup>16</sup> ಅದರಂತೆ ಮಾರುವವನು ಇಷ್ಟು ವರುಷಗಳ ಬೆಳೆಯಾಯಿತೆಂದು ಲೆಕ್ಕಿಸಿ ಕ್ರಯಕ್ಕೆ ಕೊಡಬೇಕು. [ಮುಂದಣ ಜೂಬಿಲಿ ಸಂವತ್ಸರದ ತನಕ] ಹೆಚ್ಚು ವರುಷಗಳಾದರೆ ಭೂಮಿಯ ಕ್ರಯವನ್ನು ಹೆಚ್ಚಿಸಬೇಕು; ಕಡಿಮೆಯಾದರೆ ತಗ್ಗಿಸಬೇಕು; ಭೂಮಿಯನ್ನಲ್ಲ, ಲೆಕ್ಕದ ಪ್ರಕಾರ ಬೆಳೆಗಳನ್ನೇ ಮಾರುತ್ತಾನಲ್ಲಾ.",
			"17": "<sup>17</sup> ಆದದರಿಂದ ನೀವು ಒಬ್ಬರಿಗೊಬ್ಬರು ಅನ್ಯಾಯಮಾಡಬಾರದು; ನಿಮ್ಮ ದೇವರಿಗೆ ಭಯಪಡುವವರಾಗಿರಬೇಕು; ನಾನು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು.",
			"18": "<sup>18</sup> ನನ್ನ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ನೀವು ಅನುಸರಿಸಿ ನಡೆಯಬೇಕು. ಆಗ ನೀವು ಆ ದೇಶದಲ್ಲಿ ನಿರ್ಭಯವಾಗಿ ವಾಸವಾಗಿರುವಿರಿ;",
			"19": "<sup>19</sup> ನಿಮ್ಮ ಭೂಮಿಯು ಫಲವತ್ತಾಗುವದು, ನೀವು ಸಮೃದ್ಧಿಯಾಗಿ ಊಟಮಾಡುವಿರಿ, ಮತ್ತು ಆ ದೇಶದಲ್ಲಿ ನೀವು ನಿರ್ಭಯವಾಗಿ ವಾಸಮಾಡುವಿರಿ.",
			"20": "<sup>20</sup> ಏಳನೆಯ ವರುಷದಲ್ಲಿ ಬೀಜವನ್ನು ಬಿತ್ತುವದಕ್ಕಾಗಲಿ ಬೆಳೆದದ್ದನ್ನು ಕೂಡಿಸಿಟ್ಟುಕೊಳ್ಳುವದಕ್ಕಾಗಲಿ ಅಪ್ಪಣೆಯಿಲ್ಲವಲ್ಲಾ; ಆ ವರುಷದಲ್ಲಿ ಏನು ತಿನ್ನಬೇಕೆಂದು ವಿಚಾರಿಸುತ್ತೀರೋ? ಕೇಳಿರಿ,",
			"21": "<sup>21</sup> ಆರನೆಯ ವರುಷದ ಬೆಳೆ ನನ್ನ ಪೂರ್ಣಾನುಗ್ರಹದಿಂದ ಮೂರು ವರುಷಗಳ ಬೆಳೆಯಷ್ಟಾಗುವದು.",
			"22": "<sup>22</sup> ನೀವು ಎಂಟನೆಯ ವರುಷದಲ್ಲಿ ಬೀಜವನ್ನು ಬಿತ್ತಿ ಅದರ ಬೆಳೆ ದೊರೆಯುವ ತನಕ ಅಂದರೆ ಒಂಭತ್ತನೆಯ ವರುಷದವರೆಗೆ ಹಿಂದಿನ ಬೆಳೆಯಿಂದಲೇ ಜೀವನಮಾಡುವಿರಿ.",
			"23": "<sup>23</sup> ಭೂಮಿಯನ್ನು ಶಾಶ್ವತವಾಗಿ ವಿಕ್ರಯಿಸಿಬಿಡಬಾರದು. ಯಾಕಂದರೆ ಆ ಭೂಮಿ ನನ್ನದು; ನೀವಾದರೋ ಪರವಾಸಿಗಳಾಗಿ ನನ್ನ ಆಶ್ರಯದಲ್ಲಿ ಇಳುಕೊಂಡವರೇ.",
			"24": "<sup>24</sup> ನಿಮ್ಮಲ್ಲಿ ಮತ್ತೊಬ್ಬನ ಸ್ವಾಸ್ತ್ಯಭೂಮಿ ನಿಮ್ಮ ವಶಕ್ಕೆ ಬಂದರೆ ಅದನ್ನು ಈಡುಕೊಟ್ಟು ಬಿಡಿಸಿಕೊಳ್ಳುವ ಅಧಿಕಾರವು ಕೊಟ್ಟವನಿಗೆ ಇರಬೇಕು.",
			"25": "<sup>25</sup> ನಿಮ್ಮ ಸಹೋದರರಲ್ಲಿ ಒಬ್ಬನು ಬಡವನಾಗಿ ತನ್ನ ಭೂಸ್ಥಿತಿಯಲ್ಲಿ ಏನಾದರೂ ಮಾರಿಕೊಂಡರೆ ಅವನ ಸಮೀಪವಾದ ಬಂಧುವು ಅದನ್ನು ಬಿಡಿಸಿಕೊಳ್ಳಬೇಕು.",
			"26": "<sup>26</sup> ಬಿಡಿಸುವ ಬಂಧುವು ಇಲ್ಲದೆಹೋದ ಪಕ್ಷಕ್ಕೆ ಮಾರಿದವನೇ ಸ್ಥಿತಿವಂತನಾಗಿ ಅದನ್ನು ಬಿಡಿಸಿಕೊಳ್ಳುವಷ್ಟು ಹಣವನ್ನು ಸಂಪಾದಿಸಿದರೆ",
			"27": "<sup>27</sup> ಆ ಭೂಮಿಯನ್ನು ಮಾರಿದಂದಿನಿಂದ ಕಳೆದ ವರುಷಗಳನ್ನು ಬಿಟ್ಟು ಮಿಕ್ಕ ಕ್ರಯವನ್ನು ಕೊಟ್ಟು ತನ್ನ ಭೂಮಿಯನ್ನು ಸ್ವಾಧೀನಪಡಿಸಿಕೊಳ್ಳಬಹುದು.",
			"28": "<sup>28</sup> ಅವನು ಆ ಭೂಮಿಯನ್ನು ಸ್ವಾಧೀನಪಡಿಸಿಕೊಳ್ಳಲಾರದೆ ಹೋದರೆ ಅದು ಜೂಬಿಲಿ ಸಂವತ್ಸರದ ತನಕ ಕೊಂಡುಕೊಂಡವನ ವಶದಲ್ಲೇ ಇರಬೇಕು. ಜೂಬಿಲಿ ವರುಷದಲ್ಲಿ ಅದು ಬಿಡುಗಡೆಯಾಗುವದು; ಆಗ ಆ ಭೂಮಿ ತಿರಿಗಿ ಅವನ ವಶಕ್ಕೆ ಬರುವದು.",
			"29": "<sup>29</sup> ಯಾವನಾದರೂ ಪೌಳಿ ಗೋಡೆಯುಳ್ಳ ಪಟ್ಟಣದಲ್ಲಿರುವ ಮನೆಯನ್ನು ಮಾರಿದರೆ ಅದನ್ನು ಮಾರಿದ ದಿನ ಮೊದಲುಗೊಂಡು ಒಂದು ವರುಷ ಪೂರ್ತಿಯಾಗುವದರೊಳಗೆ ಈಡುಕೊಟ್ಟು ಬಿಡಿಸಬಹುದು; ಪೂರಾ ಒಂದು ವರುಷದ ತನಕ ಅದನ್ನು ಬಿಡಿಸುವ ಅಧಿಕಾರವು ಅವನಿಗಿರುವದು.",
			"30": "<sup>30</sup> ಪೌಳಿ ಗೋಡೆಯುಳ್ಳ ಪಟ್ಟಣದಲ್ಲಿರುವ ಆ ಮನೆ ಒಂದು ವರುಷದೊಳಗಾಗಿ ಬಿಡಿಸಲ್ಪಡದೆ ಹೋದರೆ ಅದು ಜೂಬಿಲಿ ಸಂವತ್ಸರದಲ್ಲಿಯೂ ಬಿಡುಗಡೆಯಾಗದೆ ಕ್ರಯಕೊಟ್ಟವನಿಗೂ ಅವನ ಸಂತತಿಯವರಿಗೂ ಶಾಶ್ವತವಾಗಿ ನಿಲ್ಲುವದು.",
			"31": "<sup>31</sup> ಪೌಳಿಗೋಡೆಯಿಲ್ಲದ ಊರುಗಳಲ್ಲಿರುವ ಮನೆಗಳು ಬೈಲಿನ ಹೊಲಗಳಂತೆ ಎಣಿಸಲ್ಪಡಬೇಕು. ಅವುಗಳನ್ನು ಬಿಡಿಸುವ ಅಧಿಕಾರವಿರುವದು; ಜೂಬಿಲಿ ಸಂವತ್ಸರದಲ್ಲಿ ಅವು ಬಿಡುಗಡೆಯಾಗುವವು.",
			"32": "<sup>32</sup> ಆದರೆ ಲೇವಿಯರ ಸ್ವಾಸ್ತ್ಯವಾಗಿರುವ ಪಟ್ಟಣಗಳಲ್ಲಿನ ಮನೆಗಳು ಮಾರಲ್ಪಟ್ಟರೆ ಅವುಗಳನ್ನು ಬಿಡಿಸುವ ಅಧಿಕಾರವು ಲೇವಿಯರಿಗೆ ಯಾವಾಗಲೂ ಇರುವದು.",
			"33": "<sup>33</sup> ಲೇವಿಯರೊಳಗೆ ಯಾರೂ ಅದನ್ನು ಬಿಡಿಸದೆಹೋದರೆ ಲೇವಿಯರ ಸ್ವಂತವಾಗಿರುವ ಅಂಥ ಪಟ್ಟಣಗಳಲ್ಲಿ ಮಾರಲ್ಪಟ್ಟ ಮನೆ ಜೂಬಿಲಿ ಸಂವತ್ಸರದಲ್ಲಿ ಬಿಡುಗಡೆಯಾಗುವದು. ಲೇವಿಯರ ಪಟ್ಟಣಗಳಲ್ಲಿನ ಮನೆಗಳು ಇಸ್ರಾಯೇಲ್ಯರ ಮಧ್ಯದಲ್ಲಿರುವ ಅವರ ಸ್ವಾಸ್ತ್ಯವಷ್ಟೆ.",
			"34": "<sup>34</sup> ಅವರು ತಮ್ಮ ಪಟ್ಟಣಗಳಿಗೆ ಸೇರಿರುವ ಹುಲ್ಲುಗಾವಲನ್ನು ಮಾರಲೇಕೂಡದು. ಅವು ಅವರಿಗೆ ಶಾಶ್ವತವಾದ ಸ್ವಾಸ್ತ್ಯ.",
			"35": "<sup>35</sup> ನಿಮ್ಮಲ್ಲಿ ಒಬ್ಬ ಸಹೋದರನು ಬಡತನದಿಂದ ಗತಿಹೀನನಾದರೆ ಅವನು ಬದುಕಿಕೊಳ್ಳುವಂತೆ ನೀವು ಅವನನ್ನು ನಿಮ್ಮ ನಡುವೆ ಇಳುಕೊಂಡ ಪರವಾಸಿಯೆಂದು ಉದ್ಧಾರಮಾಡಬೇಕು.",
			"36": "<sup>36</sup> ನೀವು ಅವನಿಂದ ಬಡ್ಡಿಯನ್ನಾಗಲಿ ಲಾಭವನ್ನಾಗಲಿ ತೆಗೆದುಕೊಳ್ಳಬಾರದು; ಅವನು ನಿಮ್ಮ ಬಳಿಯಲ್ಲಿ ಬದುಕುವಂತೆ ನೀವು ದೇವರಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿ ನಡೆದುಕೊಳ್ಳಬೇಕು.",
			"37": "<sup>37</sup> ನೀವು ಅವನಿಗೆ ಸಾಲಕೊಟ್ಟರೆ ಬಡ್ಡಿಯನ್ನು ಕೇಳಬಾರದು, ದವಸಕೊಟ್ಟರೆ ಲಾಭವನ್ನು ಕೇಳಬಾರದು.",
			"38": "<sup>38</sup> ನಾನು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು; ನಿಮಗೆ ದೇವರಾಗುವದಕ್ಕೂ ನಿಮಗೆ ಕಾನಾನ್‍ದೇಶವನ್ನು ಕೊಡುವದಕ್ಕೂ ನಿಮ್ಮನ್ನು ಐಗುಪ್ತದೇಶದೊಳಗಿಂದ ಬರಮಾಡಿದವನೇ.",
			"39": "<sup>39</sup> ನಿಮ್ಮಲ್ಲಿ ಒಬ್ಬ ಸಹೋದರನು ಬಡವನಾಗಿ ತನ್ನನ್ನೇ ಮಾರಿಕೊಂಡರೆ ಅವನನ್ನು ದಾಸನಂತೆ ನಡಿಸಬಾರದು.",
			"40": "<sup>40</sup> ಅವನು ಕೂಲಿಯವನಂತೆಯೂ ಪರವಾಸಿಯಂತೆಯೂ ನಿಮ್ಮ ಬಳಿಯಲ್ಲಿದ್ದು ಜೂಬಿಲಿ ಸಂವತ್ಸರದ ತನಕ ನಿಮ್ಮ ಸೇವೆಯನ್ನು ಮಾಡಲಿ.",
			"41": "<sup>41</sup> ಆಗ ಅವನನ್ನೂ ಅವನ ಮಕ್ಕಳನ್ನೂ ಬಿಟ್ಟುಬಿಡಬೇಕು; ಅವನು ಸ್ವಜನರ ಬಳಿಗೂ ಪಿತ್ರಾರ್ಜಿತ ಸ್ವಾಸ್ತ್ಯಕ್ಕೂ ಹೋಗಬಹುದು.",
			"42": "<sup>42</sup> ಅವರು ನನಗೇ ದಾಸರಾಗಿದ್ದಾರೆ; ನಾನು ಅವರನ್ನು ಐಗುಪ್ತದೇಶದೊಳಗಿಂದ ಬರಮಾಡಿದೆನು; ದಾಸರನ್ನು ಮಾರುವಂತೆ ಅವರನ್ನು ವಿಕ್ರಯಿಸಬಾರದು.",
			"43": "<sup>43</sup> ಅವರಿಂದ ಕಠಿಣವಾಗಿ ಸೇವೆಮಾಡಿಸಿಕೊಳ್ಳಬಾರದು; ನಿಮ್ಮ ದೇವರಿಗೆ ಭಯಪಡಬೇಕು.",
			"44": "<sup>44</sup> ನಿಮಗೆ ದಾಸದಾಸಿಯರು ಬೇಕಾಗಿದ್ದರೆ ಸುತ್ತಲಿರುವ ಅನ್ಯರನ್ನು ಕ್ರಯಕ್ಕೆ ತೆಗೆದುಕೊಳ್ಳಬಹುದು.",
			"45": "<sup>45</sup> ನಿಮ್ಮ ನಡುವೆ ಇರುವ ಪರವಾಸಿಗಳನ್ನೂ ನಿಮ್ಮ ದೇಶದಲ್ಲಿ ಅವರಿಂದ ಹುಟ್ಟಿದವರನ್ನೂ ಕ್ರಯಕ್ಕೆ ತೆಗೆದುಕೊಳ್ಳಬಹುದು; ಅಂಥವರು ನಿಮಗೆ ಸೊತ್ತಾಗಬಹುದು.",
			"46": "<sup>46</sup> ನೀವು ಅಂಥವರನ್ನು ಸ್ವಾಧೀನಪಡಿಸಿಕೊಂಡು ನಿಮ್ಮ ತರುವಾಯ ನಿಮ್ಮ ಸಂತತಿಯವರಿಗೆ ಸ್ವಾಸ್ತ್ಯವಾಗಿ ಕೊಟ್ಟು ಬಿಡಬಹುದು. ಅವರನ್ನು ಶಾಶ್ವತದಾಸರನ್ನಾಗಿ ಮಾಡಿಕೊಳ್ಳಬಹುದು. ಇಸ್ರಾಯೇಲ್ಯರಾದ ನೀವಾದರೋ ಎಲ್ಲರೂ ಸಹೋದರರಾಗಿರುವದರಿಂದ ಒಬ್ಬರಿಂದೊಬ್ಬರು ಕಠಿಣವಾಗಿ ಸೇವೆಮಾಡಿಸಿಕೊಳ್ಳಬಾರದು.",
			"47": "<sup>47</sup> ನಿಮ್ಮ ಸಹೋದರರಲ್ಲಿ ಒಬ್ಬನು ಬಡವನಾಗಿ ನಿಮ್ಮ ನಡುವೆ ಮನೆಮಾಡಿಕೊಂಡ ಧನವಂತನಾದ ಅನ್ಯದೇಶದವನಿಗಾಗಲಿ ಅವನಿಗೆ ಸೇರಿದವನಿಗಾಗಲಿ ತನ್ನನ್ನು ಮಾರಿಕೊಂಡಿದ್ದರೂ ಅವನಿಗೆ ಬಿಡುಗಡೆಯಾಗಬಹುದು.",
			"48": "<sup>48</sup> ಅವನ ಬಂಧುಗಳಲ್ಲಿ ಯಾರಾದರೂ ಈಡುಕೊಟ್ಟು ಅವನನ್ನು ಬಿಡಿಸಬಹುದು.",
			"49": "<sup>49</sup> ಅವನ ದೊಡ್ಡಪ್ಪ ಚಿಕ್ಕಪ್ಪಂದಿರಾಗಲಿ ಇವರ ಮಕ್ಕಳಾಗಲಿ ಅಥವಾ ಸಮೀಪ ಬಂಧುಗಳಲ್ಲಿ ಯಾರೇಯಾಗಲಿ ಈಡುಕೊಟ್ಟು ಅವನನ್ನು ಬಿಡಿಸಬಹುದು. ಬೇಕಾದಷ್ಟು ಹಣವು ದೊರೆತರೆ ತನ್ನನ್ನು ತಾನೇ ಬಿಡಿಸಿಕೊಳ್ಳಬಹುದು.",
			"50": "<sup>50</sup> ಅವನು ತನ್ನನ್ನು ಮಾರಿಕೊಂಡ ವರುಷ ಮೊದಲುಗೊಂಡು ಮುಂದಣ ಜೂಬಿಲಿ ಸಂವತ್ಸರದ ತನಕ ದಣಿಯ ಸಂಗಡ ವರುಷಗಳ ಲೆಕ್ಕವನ್ನು ಮಾಡಿ ಆ ಲೆಕ್ಕದ ಮೇರೆಗೆ ಬಿಡುಗಡೆಯ ಕ್ರಯವನ್ನು ಕೊಡಬೇಕು. ಕೂಲಿಯವನ ವಿಷಯದಲ್ಲಿ ಲೆಕ್ಕಿಸುವಂತೆ ಅವನ ವಿಷಯದಲ್ಲಿಯೂ ಲೆಕ್ಕಿಸಬೇಕು.",
			"51": "<sup>51</sup> ಜೂಬಿಲಿ ಸಂವತ್ಸರವು ಬರುವದಕ್ಕೆ ಇನ್ನು ಅನೇಕ ವರುಷಗಳಿದ್ದರೆ ಹೆಚ್ಚು ಭಾಗವನ್ನೂ",
			"52": "<sup>52</sup> ಕೆಲವು ವರುಷಗಳು ಮಾತ್ರ ಉಳಿದರೆ ಸ್ವಲ್ಪ ಭಾಗವನ್ನೂ ದಣಿಗೆ ಕೊಟ್ಟು ತನ್ನನ್ನು ಬಿಡಿಸಿಕೊಳ್ಳಬೇಕು.",
			"53": "<sup>53</sup> ಅವನು ವರುಷವರುಷಕ್ಕೆ ಗೊತ್ತುಮಾಡಿಕೊಂಡ ಕೂಲಿಯವನಂತೆಯೇ ದಣಿಯ ಬಳಿಯಲ್ಲಿರಬೇಕು. ಆ ದಣಿ ಅವನಿಂದ ಕಠಿಣವಾಗಿ ಸೇವೆಮಾಡಿಸಿಕೊಳ್ಳುವದನ್ನು ನೀವು ನೋಡಿ ಸುಮ್ಮನೆ ಇರಬಾರದು.",
			"54": "<sup>54</sup> ಮೇಲೆ ಹೇಳಿದ ರೀತಿಯಲ್ಲಿ ಅವನು ಬಿಡಿಸಿಕೊಳ್ಳದೆ ಹೋದರೆ ಜೂಬಿಲಿ ಸಂವತ್ಸರದಲ್ಲಿ ಅವನೂ ಅವನ ಮಕ್ಕಳೂ ಬಿಡುಗಡೆಯಾಗಬೇಕು.",
			"55": "<sup>55</sup> ಯಾಕಂದರೆ ಇಸ್ರಾಯೇಲ್ಯರು ನನ್ನ ದಾಸರೇ; ನಾನು ಅವರನ್ನು ಐಗುಪ್ತದೇಶದೊಳಗಿಂದ ಬರಮಾಡಿದೆನಲ್ಲವೇ. ನಾನು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು."
		},
		"26": {
			"1": "<sup>1</sup> ನೀವು ವಿಗ್ರಹಗಳನ್ನು ಮಾಡಿಸಿಕೊಳ್ಳಬಾರದು; ಕೆತ್ತಿದ ಪ್ರತಿಮೆಯನ್ನಾಗಲಿ ಕಲ್ಲುಕಂಬವನ್ನಾಗಲಿ ನಿಲ್ಲಿಸಿಕೊಳ್ಳಬಾರದು; ಅಡ್ಡಬೀಳುವದಕ್ಕಾಗಿ ವಿಚಿತ್ರವಾಗಿ ಕೆತ್ತಿದ ಕಲ್ಲುಗಳನ್ನು ನಿಮ್ಮ ದೇಶದಲ್ಲಿ ಇಡಬಾರದು; ನಾನೇ ಯೆಹೋವನೆಂಬ ನಿಮ್ಮ ದೇವರು.",
			"2": "<sup>2</sup> ನಾನು ನೇಮಿಸಿರುವ ಸಬ್ಬತ್‍ದಿನಗಳನ್ನು ನೀವು ಆಚರಿಸಬೇಕು; ನನ್ನ ದೇವಸ್ಥಾನದ ವಿಷಯದಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿರಬೇಕು. ನಾನು ಯೆಹೋವನು.",
			"3": "<sup>3</sup> ನೀವು ನನ್ನ ನಿಯಮಗಳನ್ನು ಕೈಕೊಂಡು ನನ್ನ ಆಜ್ಞೆಗಳನ್ನು ಅನುಸರಿಸಿ ನಡೆದರೆ",
			"4": "<sup>4</sup> ನಾನು ಮುಂಗಾರು ಹಿಂಗಾರು ಮಳೆಗಳನ್ನು ಆಯಾ ಕಾಲದಲ್ಲಿಯೇ ಬರಮಾಡುವೆನು; ನಿಮ್ಮ ಹೊಲಗಳು ಒಳ್ಳೇ ಬೆಳೆಯನ್ನು ಕೊಡುವವು, ತೋಟದ ಮರಗಳು ಬಹಳ ಫಲಕೊಡುವವು;",
			"5": "<sup>5</sup> ಕಣತುಳಿಸುವ ಕೆಲಸವು ದ್ರಾಕ್ಷೆಯಬೆಳೆಯ ಪರ್ಯಂತರವೂ ದ್ರಾಕ್ಷೆಯ ಬೆಳೆಯನ್ನು ಕೂಡಿಸುವ ಕೆಲಸವು ಬಿತ್ತನೆಯ ಕಾಲದ ಪರ್ಯಂತರವೂ ನಡೆಯುವವು; ನೀವು ಸಮೃದ್ಧಿಯಾಗಿ ಊಟಮಾಡುವಿರಿ. ನಿಮ್ಮ ದೇಶದಲ್ಲಿ ನಿರ್ಭಯವಾಗಿ ವಾಸಮಾಡುವಿರಿ;",
			"6": "<sup>6</sup> ನಿಮ್ಮ ದೇಶದಲ್ಲಿ ನಿಮಗೆ ಸುಕ್ಷೇಮವನ್ನು ಅನುಗ್ರಹಿಸುವೆನು; ಯಾರ ಭಯವೂ ಇಲ್ಲದೆ ಮಲಗಿಕೊಳ್ಳುವಿರಿ; ದುಷ್ಟಮೃಗಗಳು ದೇಶದಲ್ಲಿ ಇರದಂತೆ ಮಾಡುವೆನು; ನಿಮ್ಮ ದೇಶವು ಶತ್ರುಗಳ ಕತ್ತಿಯಿಂದ ಹಾಳಾಗುವದಿಲ್ಲ.",
			"7": "<sup>7</sup> ನೀವು ನಿಮ್ಮ ವೈರಿಗಳನ್ನು ಓಡಿಸುವಿರಿ, ಅವರನ್ನು ಕತ್ತಿಯಿಂದ ಸಂಹರಿಸುವಿರಿ;",
			"8": "<sup>8</sup> ನಿಮ್ಮಲ್ಲಿ ಐದು ಮಂದಿಯು ನೂರು ಮಂದಿಯನ್ನೂ ನೂರು ಮಂದಿಯು ಹತ್ತುಸಾವಿರ ಮಂದಿಯನ್ನೂ ಓಡಿಸುವರು; ನಿಮ್ಮ ಶತ್ರುಗಳು ನಿಮ್ಮ ಕತ್ತಿಯಿಂದ ಹತರಾಗುವರು.",
			"9": "<sup>9</sup> ನಾನು ನಿಮ್ಮ ಮೇಲೆ ಕಟಾಕ್ಷವಿಟ್ಟು ಬಹುಸಂತಾನವನ್ನು ಕೊಟ್ಟು ಹೆಚ್ಚಿಸಿ ನಿಮಗೆ ಮಾಡಿದ ವಾಗ್ದಾನವನ್ನು ನೆರವೇರಿಸುವೆನು.",
			"10": "<sup>10</sup> ನೀವು ಬಹುದಿವಸದಿಂದ ಇಟ್ಟುಕೊಂಡಿರುವ ಹಳೆಯ ಧಾನ್ಯವನ್ನು ಊಟಮಾಡುವಿರಿ; ಹೊಸದಕ್ಕೆ ಸ್ಥಳ ಉಂಟಾಗುವಂತೆ ಹಳೆಯದನ್ನು ತೆಗೆಯುವಿರಿ.",
			"11": "<sup>11</sup> ನಾನು ನಿಮ್ಮ ನಡುವೆ ನಿವಾಸಮಾಡುವೆನು; ನಿಮ್ಮನ್ನು ತಳ್ಳಿಬಿಡುವದಿಲ್ಲ;",
			"12": "<sup>12</sup> ನಿಮ್ಮ ನಡುವೆ ತಿರುಗಾಡುತ್ತಾ ನಿಮಗೆ ದೇವರಾಗಿರುವೆನು; ನೀವು ನನಗೆ ಪ್ರಜೆಯಾಗಿರುವಿರಿ.",
			"13": "<sup>13</sup> ನೀವು ಐಗುಪ್ತ್ಯರಿಗೆ ದಾಸರಾಗಿರಬಾರದೆಂದು ನಿಮ್ಮನ್ನು ಅವರ ದೇಶದೊಳಗಿಂದ ಬರಮಾಡಿದ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಾನೇ; ನಿಮ್ಮನ್ನು ಕುಗ್ಗಿಸಿದ ನೊಗವನ್ನು ಮುರಿದು ನೀವು ನೆಟ್ಟಗೆ ನಿಂತು ನಡೆಯುವಂತೆ ಮಾಡಿದೆನಲ್ಲವೇ.",
			"14": "<sup>14</sup> ಆದರೆ ನೀವು ನನ್ನ ಮಾತನ್ನು ಕೇಳದೆ ಈ ಆಜ್ಞೆಗಳನ್ನೆಲ್ಲಾ ಅನುಸರಿಸದೆ",
			"15": "<sup>15</sup> ನನ್ನ ನಿಯಮಗಳನ್ನು ಬೇಡವೆಂದು ನನ್ನ ವಿಧಿಗಳನ್ನು ತಳ್ಳಿಬಿಟ್ಟರೆ ನೀವು ನನ್ನ ಆಜ್ಞೆಗಳನ್ನು ಅನುಸರಿಸದೆ ನನ್ನ ನಿಬಂಧನವನ್ನು ಮೀರಿ ನಡೆದರೆ",
			"16": "<sup>16</sup> ನಾನು ನಿಮಗೆ ಮಾಡುವದೇನಂದರೆ - ಕ್ಷಯರೋಗ, ಚಳಿಜ್ವರ ಮುಂತಾದ ಭಯಂಕರ ವ್ಯಾಧಿಗಳನ್ನು ನಿಮ್ಮಲ್ಲಿ ಇರುವಂತೆ ಮಾಡುವೆನು; ಇವುಗಳ ದೆಸೆಯಿಂದ ನೀವು ಕಂಗೆಟ್ಟವರಾಗಿಯೂ ಮನಗುಂದಿದವರಾಗಿಯೂ ಇರುವಿರಿ. ನೀವು ಬೀಜ ಬಿತ್ತಿದಾಗ ಅದರ ಫಲವು ನಿಮಗೆ ದೊರೆಯದೆ ಹೋಗುವದು; ಶತ್ರುಗಳು ಬಂದು ಅದನ್ನು ತಿಂದುಬಿಡುವರು.",
			"17": "<sup>17</sup> ನಾನು ನಿಮ್ಮ ಮೇಲೆ ಉಗ್ರಕೋಪವನ್ನು ಮಾಡುವದರಿಂದ ನೀವು ನಿಮ್ಮ ಶತ್ರುಗಳ ಎದುರಿನಿಂದ ಸೋತು ಬೀಳುವಿರಿ; ನಿಮ್ಮ ವೈರಿಗಳು ನಿಮ್ಮನ್ನು ಆಳುವರು; ಯಾರೂ ಹಿಂದಟ್ಟದೆ ಇರುವಾಗಲೂ ನೀವು ಹೆದರಿ ಓಡುವಿರಿ.",
			"18": "<sup>18</sup> ಇಷ್ಟಾದರೂ ನೀವು ನನ್ನ ಮಾತಿಗೆ ಲಕ್ಷ್ಯಕೊಡದೆಹೋದರೆ ನಾನು ನಿಮ್ಮ ಪಾಪಗಳ ನಿಮಿತ್ತ ಏಳರಷ್ಟಾಗಿ ನಿಮ್ಮನ್ನು ಶಿಕ್ಷಿಸುವೆನು.",
			"19": "<sup>19</sup> ನಿಮ್ಮ ಗರ್ವಕ್ಕೆ ಕಾರಣವಾದ ಬಲವನ್ನು ಮುರಿಯುವೆನು. ನಿಮ್ಮ ಮೇಲಿರುವ ಆಕಾಶವನ್ನು ಕಬ್ಬಿಣದಂತೆಯೂ ನೀವು ಸಾಗುವಳಿಮಾಡುವ ಭೂಮಿಯನ್ನು ತಾಮ್ರದಂತೆಯೂ ಮಾಡುವೆನು;",
			"20": "<sup>20</sup> ನೀವು ದುಡಿದದ್ದೆಲ್ಲಾ ವ್ಯರ್ಥವಾಗುವದು; ನಿಮ್ಮ ಭೂಮಿಯಲ್ಲಿ ಬೆಳೆಯಾಗುವದಿಲ್ಲ, ತೋಟಗಳ ಮರಗಳು ಫಲಿಸುವದಿಲ್ಲ.",
			"21": "<sup>21</sup> ನೀವು ನನ್ನ ಮಾತನ್ನು ಕೇಳಲೊಲ್ಲದೆ ನನಗೆ ವಿರೋಧವಾಗಿ ನಡೆದರೆ ನಾನು ನಿಮ್ಮ ಪಾಪಗಳ ನಿಮಿತ್ತ ಇನ್ನೂ ಏಳರಷ್ಟಾಗಿ ನಿಮ್ಮನ್ನು ಬಾಧಿಸುವೆನು.",
			"22": "<sup>22</sup> ನಿಮ್ಮ ಮೇಲೆ ಕಾಡುಮೃಗಗಳನ್ನು ಬರಮಾಡುವೆನು; ಅವು ನಿಮ್ಮನ್ನು ಸಂತಾನವಿಲ್ಲದವರನ್ನಾಗಿ ಮಾಡುವವು; ನಿಮ್ಮ ಪಶುಗಳನ್ನು ಕೊಲ್ಲುವವು; ನಿಮ್ಮನ್ನು ಸ್ವಲ್ಪ ಮಂದಿಯನ್ನಾಗಿ ಮಾಡುವವು; ನಿಮ್ಮ ದಾರಿಗಳು ಹಾಳುಬೀಳುವವು.",
			"23": "<sup>23</sup> ಇಷ್ಟು ಶಿಕ್ಷೆಗಳಿಂದಲೂ ನೀವು ನನ್ನ ಆಜ್ಞೆಗೆ ಒಳಗಾಗದೆ ನನಗೆ ವಿರೋಧವಾಗಿ ನಡೆದರೆ",
			"24": "<sup>24</sup> ನಾನು ನಿಮಗೆ ವಿರೋಧವಾಗಿ ನಡೆಯುವೆನು; ನಿಮ್ಮ ಪಾಪಗಳ ನಿಮಿತ್ತ ನಾನೇ ನಿಮ್ಮನ್ನು ಏಳರಷ್ಟಾಗಿ ಬಾಧಿಸುವೆನು.",
			"25": "<sup>25</sup> ಶತ್ರುಗಳ ಕತ್ತಿಯ ಮೂಲಕವಾಗಿ ನಿಮ್ಮನ್ನು ಸಂಹರಿಸುವೆನು; ನೀವು ನನ್ನ ನಿಬಂಧನವನ್ನು ಮೀರಿದದರಿಂದ ಆ ಕತ್ತಿಯು ಪ್ರತಿದಂಡನೆಮಾಡುವದು. ನೀವು ನಿಮ್ಮ ಪಟ್ಟಣಗಳಲ್ಲಿ ಕೂಡಿರುವಾಗ ನಿಮ್ಮಲ್ಲಿ ಅಂಟುರೋಗವುಂಟಾಗುವಂತೆ ಮಾಡುವೆನು; ಹೀಗೆ ನೀವು ಶತ್ರುಗಳ ಕೈಗೆ ಬೀಳುವಿರಿ.",
			"26": "<sup>26</sup> ನಾನು ನಿಮ್ಮ ಜೀವನಾಧಾರವನ್ನು ತೆಗೆದುಬಿಟ್ಟಾಗ ಹತ್ತು ಮಂದಿ ಹೆಂಗಸರು ಒಂದೇ ಒಲೆಯಲ್ಲಿ ರೊಟ್ಟಿ ಸುಟ್ಟು ಅದನ್ನು ತೂಕದ ಪ್ರಕಾರ ಹಂಚಿಕೊಡುವರು; ನೀವು ಅದನ್ನು ಉಂಡಾಗ ತೃಪ್ತಿಹೊಂದುವದಿಲ್ಲ.",
			"27": "<sup>27</sup> ಇದನ್ನೆಲ್ಲಾ ನೀವು ಅನುಭವಿಸಿದ ಮೇಲೆಯೂ ಇನ್ನೂ ನನ್ನ ಮಾತನ್ನು ಕೇಳದೆ ನನಗೆ ವಿರೋಧವಾಗಿ ನಡೆದರೆ",
			"28": "<sup>28</sup> ನಾನು ನಿಮಗೆ ವಿರೋಧವಾಗಿ ಕೋಪದಿಂದ ನಡೆಯುವವನಾಗಿ ನಿಮ್ಮ ಪಾಪಗಳ ನಿಮತ್ತ ಏಳರಷ್ಟಾಗಿ ಶಿಕ್ಷಿಸುವೆನು.",
			"29": "<sup>29</sup> ನೀವು ನಿಮ್ಮ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳ ಮಾಂಸವನ್ನೇ ತಿನ್ನುವಿರಿ.",
			"30": "<sup>30</sup> ನಿಮ್ಮ ಪೂಜಾಸ್ಥಳಗಳನ್ನು ಇಲ್ಲದಂತೆ ಮಾಡುವೆನು; ನೀವು ಸೂರ್ಯನ ಪೂಜೆಗೆ ಇಟ್ಟಿರುವ ಕಂಬಗಳನ್ನು ಕಡಿದು ಹಾಕುವೆನು; ನಿಮ್ಮ ಬೊಂಬೆಗಳ ಮುಂಡಗಳ ಮೇಲೆ ನಿಮ್ಮ ಮುಂಡಗಳನ್ನು ಬೀಸಾಡುವೆನು; ನಿಮ್ಮ ವಿಷಯದಲ್ಲಿ ಅಸಹ್ಯಪಡುವೆನು.",
			"31": "<sup>31</sup> ನಿಮ್ಮ ಪಟ್ಟಣಗಳನ್ನು ಹಾಳುಮಾಡುವೆನು. ನಿಮ್ಮ ದೇವಸ್ಥಾನಗಳನ್ನು ನಾಶಮಾಡುವೆನು; ನೀವು ಸಮರ್ಪಿಸುವ ಸುಗಂಧದ್ರವ್ಯಗಳ ಸುವಾಸನೆಯನ್ನು ನಾನು ಮೂಸಿನೋಡುವದಿಲ್ಲ.",
			"32": "<sup>32</sup> ನಾನು ನಿಮ್ಮ ದೇಶವನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ಹಾಳುಮಾಡಿದಾಗ ಅದರಲ್ಲಿ ಒಕ್ಕಲಾಗುವ ನಿಮ್ಮ ಶತ್ರುಗಳೂ ಅದನ್ನು ನೋಡಿ ಆಶ್ಚರ್ಯಪಡುವರು.",
			"33": "<sup>33</sup> ನಿಮ್ಮನ್ನು ಅನ್ಯಜನಗಳಲ್ಲಿ ಚದರಿಸಿ ನಿಮ್ಮ ಹಿಂದೆ ಕತ್ತಿಯನ್ನು ಬೀಸುವೆನು. ನಿಮ್ಮ ದೇಶವು ಹಾಳಾಗುವದು, ನಿಮ್ಮ ಪಟ್ಟಣಗಳು ನಾಶವಾಗುವವು.",
			"34": "<sup>34</sup> ನೀವು ನಿಮ್ಮ ಶತ್ರುಗಳ ದೇಶದಲ್ಲಿ ಇರುವ ದಿನಗಳಲ್ಲೆಲ್ಲಾ ನಿಮ್ಮ ದೇಶವು ಹಾಳುಬಿದ್ದು ತನಗೆ ಸಲ್ಲಬೇಕಾಗಿದ್ದ ಸಬ್ಬತ್ ಕಾಲವನ್ನು ಅನುಭವಿಸುವದು; ಅದು ವಿಶ್ರಾಂತಿಯನ್ನು ಹೊಂದಿ ಸಬ್ಬತ್‍ಕಾಲವನ್ನು ಅನುಭವಿಸುವದು.",
			"35": "<sup>35</sup> ನೀವು ಅದರಲ್ಲಿ ವಾಸವಾಗಿದ್ದಾಗ ಅದಕ್ಕೆ ತಕ್ಕ ಕಾಲದಲ್ಲಿ ದೊರೆಯದ ವಿಶ್ರಾಂತಿಯನ್ನು, ಅದು ಹಾಳುಬಿದ್ದಿರುವ ಎಲ್ಲಾ ದಿವಸಗಳಲ್ಲಿ ಅನುಭವಿಸುವದು.",
			"36": "<sup>36</sup> ನಿಮ್ಮಲ್ಲಿ ಯಾರಾರು ಉಳಿದು ಶತ್ರುಗಳ ದೇಶದಲ್ಲಿರುವರೋ ಅವರ ಹೃದಯಗಳಲ್ಲಿ ನಾನು ಭೀತಿಯನ್ನು ಹುಟ್ಟಿಸುವೆನು. ಗಾಳಿಯಿಂದ ಬಡಿದಾಡುವ ಉದುರೆಲೆಯ ಸಪ್ಪಳವು ಅವರಲ್ಲಿ ದಿಗಿಲುಹುಟ್ಟಿಸುವದು; ಆ ಸಪ್ಪಳವನ್ನು ಕೇಳಿ ಕತ್ತಿಯ ಎದುರಿನಿಂದ ಓಡಿಹೋಗುವವರಂತೆ ಓಡಿಹೋಗುವರು; ಯಾರೂ ಹಿಂದಟ್ಟಿ ಬಾರದಿರುವಾಗಲೂ ಅವರು ಓಡಿ ಬೀಳುವರು;",
			"37": "<sup>37</sup> ಯಾರೂ ಓಡಿಸದಿದ್ದರೂ ಕತ್ತಿಯ ಎದುರಿನಿಂದ ಓಡುವವರಂತೆ ಒಬ್ಬರ ಮೇಲೊಬ್ಬರು ಬೀಳುವರು; ಶತ್ರುಗಳ ಎದುರಿನಲ್ಲಿ ನಿಲ್ಲಲಾರದವರಾಗುವಿರಿ.",
			"38": "<sup>38</sup> ಅನ್ಯಜನಗಳಲ್ಲಿ ಚದರಿಸಲ್ಪಟ್ಟು ಕಾಣಿಸದೆ ಹೋಗುವಿರಿ; ನಿಮ್ಮ ಶತ್ರುಗಳ ದೇಶವು ನಿಮ್ಮನ್ನು ನುಂಗಿಬಿಡುವದು.",
			"39": "<sup>39</sup> ನಿಮ್ಮಲ್ಲಿ ಉಳಿದವರು ತಮ್ಮ ಪಾಪದ ದೆಸೆಯಿಂದಲೂ ತಮ್ಮ ಪಿತೃಗಳ ಪಾಪದ ದೆಸೆಯಿಂದಲೂ ಅವರಂತೆಯೇ ಶತ್ರುಗಳ ದೇಶಗಳಲ್ಲಿ ಕ್ಷೀಣವಾಗಿ ಹೋಗುವರು.",
			"40": "<sup>40</sup> ಆದರೆ ಅವರು ತಾವೂ ತಮ್ಮ ಪಿತೃಗಳೂ ನನಗೆ ದ್ರೋಹಿಗಳಾಗಿ ಪಾಪಮಾಡಿದವರೆಂದೂ ತಾವು ನನಗೆ ವಿರೋಧವಾಗಿ ನಡೆದದ್ದರಿಂದಲೇ",
			"41": "<sup>41</sup> ನಾನು ತಮಗೆ ವಿರೋಧವಾಗಿ ನಡೆದುಕೊಂಡು ಶತ್ರುದೇಶಕ್ಕೆ [ಸೆರೆಯವರನ್ನಾಗಿ] ಬರಮಾಡಬೇಕಾಯಿತೆಂದೂ ಒಪ್ಪಿ ತಮ್ಮ ಮೊಂಡವಾದ ಮನಸ್ಸನ್ನು ನನ್ನ ಆಜ್ಞೆಗೆ ಒಳಪಡಿಸಿ, ತಮ್ಮ ಪಾಪದಿಂದುಂಟಾದ ಶಿಕ್ಷೆಯನ್ನು ಸ್ವೀಕರಿಸುವದಾದರೆ",
			"42": "<sup>42</sup> ನಾನು ಯಾಕೋಬ್ ಇಸಾಕ್ ಅಬ್ರಹಾಮ್ ಇವರಿಗೆ ಮಾಡಿದ ವಾಗ್ದಾನಗಳನ್ನು ನೆನಪಿಗೆ ತಂದುಕೊಂಡು ನೆರವೇರಿಸುವೆನು; ಅವರ ಸ್ವದೇಶವನ್ನೂ ನಾನು ದಯೆಯಿಂದ ಜ್ಞಾಪಿಸಿಕೊಳ್ಳುವೆನು.",
			"43": "<sup>43</sup> ಅವರು ಬಿಟ್ಟುಹೋದ ಸ್ವದೇಶವು ನಿರ್ಜನವಾಗಿ ಬಿದ್ದಿರುವಾಗ ತನಗೆ ಸಲ್ಲಬೇಕಾದ ಸಬ್ಬತ್ ವಿಶ್ರಾಂತಿಯನ್ನು ಹೊಂದುವದು. ಅವರು ಯೆಹೋವನ ಆಜ್ಞೆಬೇಡವೆಂದು ಆತನ ವಿಧಿಗಳನ್ನು ತಾತ್ಸಾರ ಮಾಡಿದ ಕಾರಣವೇ ತಮ್ಮ ಪಾಪದ ಫಲವನ್ನು ಅನುಭವಿಸಬೇಕಾಗಿರುವದು.",
			"44": "<sup>44</sup> ಆದರೂ ಅವರು ಶತ್ರುಗಳ ದೇಶದಲ್ಲಿರುವಾಗಲೂ ನಾನು ಅವರನ್ನು ಬೇಡವೆನ್ನುವದಿಲ್ಲ, ತಾತ್ಸಾರಮಾಡುವದಿಲ್ಲ; ನಾನು ಅವರಿಗೆ ಮಾಡಿದ ವಾಗ್ದಾನವನ್ನು ಮೀರುವದಿಲ್ಲ; ಅವರನ್ನು ಇಲ್ಲದಂತೆ ಮಾಡುವದಿಲ್ಲ; ನಾನು ಅವರ ದೇವರಾದ ಯೆಹೋವನಲ್ಲವೇ.",
			"45": "<sup>45</sup> ಅವರ ಪಿತೃಗಳ ಸಂಗಡ ಮಾಡಿಕೊಂಡ ಒಡಂಬಡಿಕೆಯನ್ನು ನೆನಪಿಗೆ ತಂದುಕೊಂಡು ಅವರಿಗೆ ಹಿತವನ್ನು ಮಾಡುವೆನು. ನಾನು ಅವರಿಗೆ ದೇವರಾಗಿರಬೇಕೆಂದು ಅವರ ಪಿತೃಗಳನ್ನು ಎಲ್ಲಾ ಜನಾಂಗಗಳ ಎದುರಿನಲ್ಲಿಯೇ ಐಗುಪ್ತ ದೇಶದೊಳಗಿಂದ ಬರಮಾಡಿದೆನಲ್ಲವೇ. ನಾನು ಯೆಹೋವನು.",
			"46": "<sup>46</sup> ಯೆಹೋವನು ಸೀನಾಯಿಬೆಟ್ಟದಲ್ಲಿ ಮೋಶೆಯ ಮೂಲಕವಾಗಿ ಇಸ್ರಾಯೇಲ್ಯರ ಸಂಗಡ ಒಡಂಬಡಿಕೆಯನ್ನು ಮಾಡಿಕೊಂಡು ಅವರಿಗೆ ಕೊಟ್ಟ ಆಜ್ಞಾವಿಧಿಗಳು ಮೇಲೆ ಕಂಡವುಗಳೇ."
		},
		"27": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಮೋಶೆಗೆ ಹೇಳಿದ್ದೇನಂದರೆ -",
			"2": "<sup>2</sup> ನೀನು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಹೀಗೆ ಆಜ್ಞಾಪಿಸು - ಯಾವನಾದರೂ ಮನುಷ್ಯರ ಪ್ರಾಣಗಳನ್ನು ಯೆಹೋವನಿಗೆ ಹರಕೆಮಾಡಿ ಪ್ರತಿಷ್ಠಿಸಿದರೆ [ಅವುಗಳನ್ನು ಬಿಡಿಸುವದಕ್ಕೆ] ದೇವರ ಸೇವೆಗೆ ನೇಮಕವಾದ ರೂಪಾಯಿಯ ಮೇರೆಗೆ ನೀವು ಅವನಿಂದ ಕೊಡಿಸಬೇಕಾದ ಈಡು ಹೇಗಂದರೆ -",
			"3": "<sup>3</sup> ಇಪ್ಪತ್ತು ವರುಷದಿಂದ ಅರುವತ್ತು ವರುಷದ ಪರ್ಯಂತರ ವಯಸ್ಸುಳ್ಳ ಪುರುಷನಿಗೋಸ್ಕರ ಐವತ್ತು ರೂಪಾಯಿಗಳನ್ನೂ",
			"4": "<sup>4</sup> ಸ್ತ್ರೀಗೋಸ್ಕರ ಮೂವತ್ತು ರೂಪಾಯಿಗಳನ್ನೂ ಕೊಡಿಸಬೇಕು.",
			"5": "<sup>5</sup> ಐದು ವರುಷದ ವಯಸ್ಸಿನಿಂದ ಇಪ್ಪತ್ತು ವರುಷದ ವಯಸ್ಸಿನವರೆಗೆ ಪುರುಷನಿಗೋಸ್ಕರ ಇಪ್ಪತ್ತು ರೂಪಾಯಿಗಳನ್ನೂ ಸ್ತ್ರೀಗೋಸ್ಕರ ಹತ್ತು ರೂಪಾಯಿಗಳನ್ನೂ ಕೊಡಿಸಬೇಕು.",
			"6": "<sup>6</sup> ಒಂದು ತಿಂಗಳಿನ ವಯಸ್ಸು ಮೊದಲುಗೊಂಡು ಐದು ವರುಷದ ವಯಸ್ಸಿನ ಪರ್ಯಂತರ ಹುಡುಗನಿಗೋಸ್ಕರ ಐದು ರೂಪಾಯಿಗಳನ್ನೂ ಹುಡುಗಿಗೋಸ್ಕರ ಮೂರು ರೂಪಾಯಿಗಳನ್ನೂ ಕೊಡಿಸಬೇಕು.",
			"7": "<sup>7</sup> ಅರುವತ್ತು ವರುಷಗಳ ಪ್ರಾಯವನ್ನು ದಾಟಿದ ಪುರುಷನಿಗೋಸ್ಕರ ಹದಿನೈದು ರೂಪಾಯಿಗಳನ್ನೂ ಸ್ತ್ರೀಗೋಸ್ಕರ ಹತ್ತು ರೂಪಾಯಿಗಳನ್ನೂ ಕೊಡಿಸಬೇಕು.",
			"8": "<sup>8</sup> ಯಾವನಾದರೂ ಬಡವನಾಗಿ ನೇಮಕವಾದ ಹಣವನ್ನು ಕೊಡಲಾರದೆ ಹೋದರೆ ಅವನು ಹರಕೆಮಾಡಿ ಪ್ರತಿಷ್ಠಿಸಿದ ಮನುಷ್ಯನನ್ನು ಯಾಜಕನ ಮುಂದೆ ನಿಲ್ಲಿಸಬೇಕು. ಯಾಜಕನು ಹರಕೆಮಾಡಿದವನ ಸ್ಥಿತಿಗತಿಗೆ ತಕ್ಕಂತೆ ಅವನು ಕೊಡಬೇಕಾದ ಹಣ ಇಷ್ಟೆಂದು ನಿರ್ಣಯಿಸಬೇಕು.",
			"9": "<sup>9</sup> ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಿಸಬಹುದಾದ ಪಶುವನ್ನು ಯಾವನಾದರೂ ಹರಕೆಮಾಡಿ ಪ್ರತಿಷ್ಠಿಸಿದ್ದಾದರೆ ಅದು ದೇವರ ಸೊತ್ತಾಗಿರಬೇಕು.",
			"10": "<sup>10</sup> ಅದನ್ನು ಬದಲಾಯಿಸಕೂಡದು; ಕೆಟ್ಟದ್ದಕ್ಕೆ ಬದಲಾಗಿ ಒಳ್ಳೇದನ್ನೂ ಒಳ್ಳೇದಕ್ಕೆ ಬದಲಾಗಿ ಕೆಟ್ಟದ್ದನ್ನೂ ಒಂದಕ್ಕೆ ಬದಲಾಗಿ ಬೇರೊಂದನ್ನೂ ಕೊಡಕೂಡದು. ಅವನು ಗೊತ್ತುಮಾಡಿದ ಪಶುವಿಗೆ ಬದಲಾಗಿ ಬೇರೊಂದು ಪಶುವನ್ನು ಪ್ರತ್ಯೇಕಿಸಿಟ್ಟಿರುವ ಪಕ್ಷಕ್ಕೆ ಮೊದಲನೆಯ ಪಶುವೂ ಅದಕ್ಕೆ ಬದಲಾಗಿ ಇಟ್ಟ ಪಶುವೂ ಎರಡೂ ಯೆಹೋವನವಾಗಿರಬೇಕು.",
			"11": "<sup>11</sup> ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಿಸಕೂಡದ ಬೇರೆ ಜಾತಿಯ ಪಶುವನ್ನು ಪ್ರತಿಷ್ಠಿಸಿದ್ದಾದರೆ ಅದನ್ನು ಯಾಜಕನ ಮುಂದೆ ನಿಲ್ಲಿಸಬೇಕು;",
			"12": "<sup>12</sup> ಅದು ಒಳ್ಳೇದೋ ಕೆಟ್ಟದ್ದೋ ಎಂದು ಯಾಜಕನು ನೋಡಿಕೊಂಡು ಅದರ ಬೆಲೆ ಇಷ್ಟೆಂದು ನಿರ್ಣಯಿಸಬೇಕು; ಯಾಜಕನು ನಿರ್ಣಯಿಸಿದ ಬೆಲೆ ಸ್ಥಿರವಾಗಿರುವದು.",
			"13": "<sup>13</sup> ಹರಕೆ ಮಾಡಿದವನು ಅದನ್ನು ಬಿಡಿಸಿಕೊಳ್ಳಬೇಕೆಂದಿದ್ದರೆ ಅದರ ಕ್ರಯದೊಡನೆ ಐದನೆಯ ಭಾಗವನ್ನು ಹೆಚ್ಚಾಗಿ ಕೊಡಬೇಕು.",
			"14": "<sup>14</sup> ಯಾವನಾದರೂ ತನ್ನ ಮನೆಯನ್ನು ಯೆಹೋವನಿಗೆ ಮೀಸಲಾಗಿ ಪ್ರತಿಷ್ಠಿಸಿದರೆ ಯಾಜಕನು ಅದು ಉತ್ತಮವಾದ ಮನೆಯೋ ಅಲ್ಲವೋ ಎಂದು ಪರೀಕ್ಷಿಸಿ ಬೆಲೆಯನ್ನು ನಿರ್ಣಯಿಸಬೇಕು; ಯಾಜಕನು ನಿರ್ಣಯಿಸಿದ ಬೆಲೆ ಸ್ಥಿರವಾಗಿರುವದು.",
			"15": "<sup>15</sup> ಹರಕೆಮಾಡಿದವನು ಆ ಮನೆಯನ್ನು ಬಿಡಿಸಿಕೊಳ್ಳಬೇಕೆಂದಿದ್ದರೆ ಅದರ ಕ್ರಯದೊಡನೆ ಐದನೆಯ ಭಾಗವನ್ನು ಹೆಚ್ಚಾಗಿ ಕೊಡಬೇಕು; ಆಗ ಆ ಮನೆ ಅವನದಾಗುವದು.",
			"16": "<sup>16</sup> ಯಾವನಾದರೂ ಪಿತ್ರಾರ್ಜಿತಭೂಮಿಯಲ್ಲಿ ಒಂದು ಭಾಗವನ್ನು ಯೆಹೋವನಿಗೆ ಹರಕೆಮಾಡಿ ಪ್ರತಿಷ್ಠಿಸಿದರೆ ಅದರಲ್ಲಿ ಇಷ್ಟು ಬೀಜವನ್ನು ಬಿತ್ತತಕ್ಕದ್ದು ಎಂದು ನೀವು ಆಲೋಚಿಸಿ ಅದರ ಬೆಲೆಯನ್ನು ನಿರ್ಣಯಿಸಬೇಕು. ಎರಡು ಕಂಡುಗ ಜವೆಗೋದಿಯ ಬೀಜವರಿಯುಳ್ಳ ಹೊಲವು ಐವತ್ತು ರೂಪಾಯಿ ಬಾಳುವದು.",
			"17": "<sup>17</sup> ಅವನು ಜೂಬಿಲಿ ಸಂವತ್ಸರದಿಂದ ಆ ಹೊಲವನ್ನು ಪ್ರತಿಷ್ಠಿಸಿದರೆ ಈ ಕ್ರಯ ಸ್ಥಿರವಾಗಿರುವದು.",
			"18": "<sup>18</sup> ಜೂಬಿಲಿ ಸಂವತ್ಸರದ ತರುವಾಯ ಭೂಮಿಯನ್ನು ಹರಕೆಮಾಡಿದರೆ ಮುಂದಣ ಜೂಬಿಲಿ ಸಂವತ್ಸರಕ್ಕೆ ಕಳೆಯಬೇಕಾದ ವರುಷಗಳ ಸಂಖ್ಯೆಯ ಮೇರೆಗೆ ಅದರ ಬೆಲೆಯನ್ನು ಕಮ್ಮಿಮಾಡಬೇಕು.",
			"19": "<sup>19</sup> ಹರಕೆಮಾಡಿದವನು ಅದನ್ನು ಬಿಡಿಸಿಕೊಳ್ಳಬೇಕೆಂದಿರುವ ಪಕ್ಷಕ್ಕೆ ಅದರ ಕ್ರಯದೊಡನೆ ಐದನೆಯ ಭಾಗವನ್ನು ಹೆಚ್ಚಾಗಿ ಕೊಡಬೇಕು; ಆಗ ಆ ಹೊಲ ಅವನದಾಗಿರುವದು.",
			"20": "<sup>20</sup> ತಾನು ಅದನ್ನು ಬಿಡಿಸಿಕೊಳ್ಳದೆ ಮತ್ತೊಬ್ಬನಿಗೆ ಮಾರಿದರೆ ಮುಂದೆ ಅದನ್ನು ಬಿಡಿಸಿಕೊಳ್ಳುವ ಅಧಿಕಾರವಿರುವದಿಲ್ಲ.",
			"21": "<sup>21</sup> ಬಿಡುಗಡೆಯಾಗುವ ಜೂಬಿಲಿ ಸಂವತ್ಸರದಲ್ಲಿ ಆ ಹೊಲವು ಯೆಹೋವನ ಸ್ವಕೀಯವಾದ ಹೊಲದಂತೆ ಆತನ ಸೊತ್ತಾಗಿಯೇ ಇರಬೇಕು; ಅದು ಯಾಜಕರ ವಶದಲ್ಲಿರಬೇಕು.",
			"22": "<sup>22</sup> ಯಾವನಾದರೂ ಕ್ರಯಕ್ಕೆ ತೆಗೆದುಕೊಂಡ ಹೊಲವನ್ನು ಅಂದರೆ ಪಿತ್ರಾರ್ಜಿತ ಭೂಮಿಗೆ ಸೇರದಿರುವ ಹೊಲವನ್ನು ಯೆಹೋವನಿಗೆ ಹರಕೆಮಾಡಿ ಪ್ರತಿಷ್ಠಿಸಿದರೆ",
			"23": "<sup>23</sup> ಯಾಜಕನು ಮುಂದಣ ಜೂಬಿಲಿ ಸಂವತ್ಸರದ ತನಕ ಇರುವ ವರುಷಗಳಿಗೆ ತಕ್ಕಂತೆ ಅದರ ಬೆಲೆಯನ್ನು ನಿರ್ಣಯಿಸಬೇಕು. ಪ್ರತಿಷ್ಠಿಸಿದವನು ನಿರ್ಣಯಿಸಲ್ಪಟ್ಟ ಹಣವನ್ನು ಯೆಹೋವನಿಗೆ ಮೀಸಲಾದದ್ದೆಂದು ಭಾವಿಸಿ ಅದೇ ದಿನದಲ್ಲಿ ಕೊಟ್ಟುಬಿಡಬೇಕು.",
			"24": "<sup>24</sup> ಜೂಬಿಲಿ ಸಂವತ್ಸರದಲ್ಲಿ ಆ ಹೊಲವು ಮಾರಿದವನಿಗೆ ಅಂದರೆ ಯಾರ ಪಿತ್ರಾರ್ಜಿತ ಭೂಮಿಗೆ ಸೇರಿದೆಯೋ ಅವನಿಗೆ ತಿರಿಗಿ ಬರಬೇಕು.",
			"25": "<sup>25</sup> ದೇವರ ಸೇವೆಯಲ್ಲಿ ನೇಮಕವಾದ ಇಪ್ಪತ್ತು ಗೇರಾ ತೂಕದ ರೂಪಾಯಿಯ ಮೇರೆಗೆ ನೀವು ಬೆಲೆಯನ್ನು ಯಾವಾಗಲೂ ನಿರ್ಣಯಿಸಬೇಕು.",
			"26": "<sup>26</sup> [ಶುದ್ಧ] ಪಶುವಿನಲ್ಲಿ ಚೊಚ್ಚಲಾಗಿ ಹುಟ್ಟಿದ್ದು ಯೆಹೋವನದಾಗಿರುವದರಿಂದ ಅದನ್ನು ಯಾರೂ ಹರಕೆಯಾಗಿ ಕೊಡಕೂಡದು; ಹೋರಿಯಾಗಿದ್ದರೂ ಆಡುಕುರಿಯಾಗಿದ್ದರೂ ಅದು ಯೆಹೋವನ ಸೊತ್ತೇ.",
			"27": "<sup>27</sup> ಅಶುದ್ಧ ಪಶುವಿನಲ್ಲಿ ಚೊಚ್ಚಲುಮರಿ ಹುಟ್ಟಿದ್ದಾದರೆ ಹರಕೆಮಾಡಿದವನು ನಿರ್ಣಯವಾದ ಬೆಲೆಯೊಂದಿಗೆ ಐದನೆಯ ಭಾಗವನ್ನು ಹೆಚ್ಚಾಗಿ ಕೊಟ್ಟು ಅದನ್ನು ಬಿಡಿಸಿಕೊಳ್ಳಬಹುದು. ಅವನು ಬಿಡಿಸದೆಹೋದರೆ ಅದನ್ನು ನಿರ್ಣಯಿಸಲ್ಪಟ್ಟ ಕ್ರಯಕ್ಕೆ ಮಾರಬೇಕು.",
			"28": "<sup>28</sup> ಯಾವನಾದರೂ ಮನುಷ್ಯನನ್ನಾಗಲಿ ಪಶುವನ್ನಾಗಲಿ ಪಿತ್ರಾರ್ಜಿತಭೂಮಿಯನ್ನಾಗಲಿ ಬೇರೆ ಯಾವದನ್ನಾಗಲಿ ಕೇವಲ ಯೆಹೋವನದಾಗಿರುವದಕ್ಕೆ ಹರಕೆಮಾಡಿಕೊಟ್ಟರೆ ಅದನ್ನು ಮಾರಲೂ ಕೂಡದು ಬಿಡಿಸಿಕೊಳ್ಳಲೂ ಕೂಡದು. ಕೇವಲ ಯೆಹೋವನದಾಗಿರುವದಕ್ಕೆ ಸಮರ್ಪಿಸುವಂಥದೆಲ್ಲಾ ಯೆಹೋವನಿಗೆ ಮೀಸಲಾಗಿಯೇ ಇರಬೇಕು.",
			"29": "<sup>29</sup> ಕೇವಲ ಯೆಹೋವನ ಸೊತ್ತಾಗುವದಕ್ಕೆ ಒಪ್ಪಿಸಲ್ಪಟ್ಟದ್ದು ಮನುಷ್ಯ ಜಾತಿಯಾದರೆ ಬಿಡಿಸುವದಕ್ಕಾಗದು; ಅವನಿಗೆ ಮರಣವೇ ಆಗಬೇಕು.",
			"30": "<sup>30</sup> ಹೊಲದ ಬೆಳೆಯಾಗಲಿ ತೋಟದ ಹಣ್ಣುಗಳಾಗಲಿ ಭೂಮಿಯಿಂದುಂಟಾದ ಎಲ್ಲಾ ಆದಾಯದಲ್ಲಿ ಹತ್ತನೆಯ ಪಾಲು ಯೆಹೋವನದಾಗಿರಬೇಕು; ಅದು ಯೆಹೋವನಿಗೆ ಮೀಸಲಾದದ್ದು.",
			"31": "<sup>31</sup> ಯಾವನಾದರೂ ತಾನು ಕೊಡಬೇಕಾದ ಹತ್ತನೆಯ ಪಾಲಿನಲ್ಲಿ ಏನಾದರೂ ಬಿಡಿಸಿಕೊಳ್ಳಬೇಕೆಂದಿದ್ದರೆ ಅಷ್ಟರೊಡನೆ ಐದನೆಯ ಭಾಗವನ್ನು ಹೆಚ್ಚಾಗಿ ಕೊಡಬೇಕು.",
			"32": "<sup>32</sup> ದನಗಳೇಯಾಗಲಿ ಆಡುಕುರಿಗಳೇಯಾಗಲಿ ಒಡೆಯನು ಲೆಕ್ಕಿಸಿದ ಎಲ್ಲಾ ಪಶುಗಳಲ್ಲಿ ಪ್ರತಿ ಹತ್ತನೆಯದು ಯೆಹೋವನಿಗೆ ಮೀಸಲಾಗಿರಬೇಕು.",
			"33": "<sup>33</sup> ಆ ಪಶುವು ಒಳ್ಳೇದೋ ಕೆಟ್ಟದ್ದೋ ಎಂದು ನೋಡಬಾರದು; ಅದನ್ನು ಬದಲಾಯಿಸಬಾರದು. ಕೊಡಬೇಕಾದವನು ಅದನ್ನು ಬದಲಾಯಿಸಿದ್ದಾದರೆ ಅವನು ಮೊದಲಾಗಿ ಲೆಕ್ಕಿಸಿದ್ದೂ ಅದಕ್ಕೆ ಬದಲಾಗಿ ಇಟ್ಟದ್ದೂ ಎರಡೂ ಯೆಹೋವನವಾಗಿರಬೇಕು; ಅದನ್ನು ಬಿಡಿಸಿಕೊಳ್ಳುವ ಅಧಿಕಾರವಿರುವದಿಲ್ಲ.",
			"34": "<sup>34</sup> ಯೆಹೋವನು ಸೀನಾಯಿಬೆಟ್ಟದಲ್ಲಿ ಮೋಶೆಯ ಮೂಲಕ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಕೊಟ್ಟ ಆಜ್ಞೆಗಳು ಇವೇ."
		}
	}
};
module.exports = book;