var book = {
	"name": "ಧರ್ಮೋಪದೇಶಕಾಂಡ",
	"numChapters": 34,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ಯೊರ್ದನ್ ಹೊಳೆಯ ಆಚೆ, ಪಾರಾನಿಗೂ ತೋಫೆಲ್ ಲಾಬಾನ್ ಹಚೇರೋತ್ ದೀಜಾಹಾಬ್ ಎಂಬ ಸ್ಥಳಗಳಿಗೂ ನಡುವೆ, ಅರಣ್ಯದಲ್ಲಿ ಅರಾಬಾ ಎಂಬ ತಗ್ಗಾದ ಪ್ರದೇಶದಲ್ಲಿ ಸೂಫಿಗೆ ಎದುರಾಗಿ ಮೋಶೆಯು ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರಿಗೆ ಮಾಡಿದ ಉಪನ್ಯಾಸಗಳು.",
			"2": "<sup>2</sup> [ಹೋರೇಬಿನಿಂದ ಸೇಯೀರ್ ಬೆಟ್ಟಗಳ ಮಾರ್ಗವಾಗಿ ಕಾದೇಶ್‍ಬರ್ನೇಯದವರೆಗೆ ಹನ್ನೊಂದು ದಿನದ ದಾರಿ.]",
			"3": "<sup>3</sup> ಮೋಶೆಯು ಹೆಷ್ಬೋನಿನಲ್ಲಿದ್ದ ಅಮೋರಿಯರ ಅರಸನಾದ ಸೀಹೋನನನ್ನೂ ಅಷ್ಟಾರೋತ್, ಎದ್ರೈ ಎಂಬ ಪಟ್ಟಣಗಳಲ್ಲಿದ್ದ ಬಾಷಾನಿನ ಅರಸನಾದ ಓಗನನ್ನೂ ಜಯಿಸಿದ ತರುವಾಯ,",
			"4": "<sup>4</sup> ಅಂದರೆ [ಇಸ್ರಾಯೇಲ್ಯರು ಐಗುಪ್ತದೇಶವನ್ನು ಬಿಟ್ಟ] ನಾಲ್ವತ್ತನೆಯ ವರುಷದ ಹನ್ನೊಂದನೆಯ ತಿಂಗಳಿನ ಮೊದಲನೆಯ ದಿನದಲ್ಲಿ ಯೆಹೋವನು ತನಗೆ ಆಜ್ಞಾಪಿಸಿದ್ದನ್ನೆಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ತಿಳಿಸಿದನು.",
			"5": "<sup>5</sup> ಯೊರ್ದನ್ ಹೊಳೆಯ ಆಚೆ ಮೋವಾಬ್ಯರ ದೇಶದಲ್ಲಿ ಮೋಶೆಯು ಈ ಮುಂದಣ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ವಿವರಿಸುವದಕ್ಕೆ ಪ್ರಾರಂಭಿಸಿ ಹೇಳಿದನು -",
			"6": "<sup>6</sup> ಹೋರೇಬಿನಲ್ಲಿ ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಮಗೆ - ನೀವು ಈ ಬೆಟ್ಟದ ಬಳಿಯಲ್ಲಿ ವಾಸಿಸಿದ್ದು ಸಾಕು;",
			"7": "<sup>7</sup> ಈಗ ಹಿಂದಿರುಗಿ ಅಮೋರಿಯರು ಇರುವ ಬೆಟ್ಟದ ಸೀಮೆಗೂ ಅದಕ್ಕೆ ಸೇರಿದ ಪ್ರದೇಶವೆಲ್ಲಕ್ಕೂ ಪ್ರಯಾಣಮಾಡಿರಿ. ಅವು ಯಾವವಂದರೆ - ತಗ್ಗಾದ ಪ್ರದೇಶ, ಬೆಟ್ಟದ ಮೇಲಿನ ಪ್ರದೇಶ, ಇಳಕಲಿನ ಪ್ರದೇಶ, ದಕ್ಷಿಣಸೀಮೆ, ಸಮುದ್ರತೀರ ಎಂಬ ನಾಡುಗಳನ್ನು ಒಳಗೊಂಡಿರುವ ಕಾನಾನ್ಯರ ದೇಶ, ಲೆಬನೋನ್ ಪರ್ವತ ಮತ್ತು ಯೂಫ್ರೇಟೀಸ್ ಎಂಬ ಮಹಾನದಿಯವರೆಗೆ ವಿಸ್ತರಿಸಿಕೊಂಡಿರುವ ಪ್ರದೇಶ ಇವುಗಳೇ.",
			"8": "<sup>8</sup> ಆ ದೇಶವನ್ನು ನಿಮಗೇ ಕೊಟ್ಟಿದ್ದೇನೆ; ಯೆಹೋವನೆಂಬ ನಾನು ನಿಮ್ಮ ಪಿತೃಗಳಾದ ಅಬ್ರಹಾಮ್ ಇಸಾಕ್ ಯಾಕೋಬರಿಗೂ ಅವರ ಸಂತತಿಯವರಿಗೂ ಆ ದೇಶವನ್ನು ಪ್ರಮಾಣಪೂರ್ವಕವಾಗಿ ವಾಗ್ದಾನ ಮಾಡಿದೆನಲ್ಲಾ. ಅದರಲ್ಲಿ ಪ್ರವೇಶಮಾಡಿ ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳಿರಿ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"9": "<sup>9</sup> ಆ ಕಾಲದಲ್ಲಿ ನಾನು ನಿಮ್ಮ ಸಂಗಡ ಮಾತಾಡಿ - ನಿಮ್ಮ ಭಾರವನ್ನು ನಾನೊಬ್ಬನೇ ಹೊರುವದು ಅಸಾಧ್ಯ;",
			"10": "<sup>10</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಹೆಚ್ಚಿಸಿದ್ದರಿಂದ ನೀವು ಈಗ ಆಕಾಶದ ನಕ್ಷತ್ರಗಳಷ್ಟಾದಿರಲ್ಲಾ.",
			"11": "<sup>11</sup> ನೀವು ಈಗ ಇರುವದಕ್ಕಿಂತಲೂ ಇನ್ನು ಸಾವಿರದಷ್ಟಾಗುವಂತೆ ನಿಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಹೆಚ್ಚಿಸಿ ತಾನು ವಾಗ್ದಾನ ಮಾಡಿದ ಪ್ರಕಾರವೇ ನಿಮಗೆ ಶುಭಗಳನ್ನು ಅನುಗ್ರಹಿಸಲಿ.",
			"12": "<sup>12</sup> ಆದರೆ ನಿಮ್ಮಿಂದಾಗುವ ತೊಂದರೆಗಳನ್ನೂ ಭಾರವನ್ನೂ ವ್ಯಾಜ್ಯಗಳನ್ನೂ ನಾನೊಬ್ಬನೇ ಸಹಿಸುವದು ಹೇಗೆ?",
			"13": "<sup>13</sup> ಆದದರಿಂದ ನೀವು ಪ್ರತಿಯೊಂದು ಕುಲದಿಂದ ಪ್ರಸಿದ್ಧರಾದ ಬುದ್ಧಿವಿವೇಕವುಳ್ಳ ಪುರುಷರನ್ನು ಆರಿಸಿಕೊಳ್ಳಿರಿ; ನಾನು ಅವರನ್ನು ನಿಮಗೆ ಅಧಿಪತಿಗಳನ್ನಾಗಿ ನೇಮಿಸುವೆನು ಎಂದು ಹೇಳಿದೆನು.",
			"14": "<sup>14</sup> ಅದಕ್ಕೆ ನೀವು - ನಿನ್ನ ಆಲೋಚನೆಯಂತೆ ನಡೆಯುವದು ವಿಹಿತವಾದದ್ದೇ ಎಂದು ಉತ್ತರಕೊಟ್ಟಿರಿ.",
			"15": "<sup>15</sup> ಆಗ ನಾನು ನಿಮ್ಮಲ್ಲಿ ಪ್ರಸಿದ್ಧರಾದ ಬುದ್ಧಿವಂತರನ್ನು ಕರಿಸಿ ಒಂದೊಂದು ಕುಲದಲ್ಲಿ ಸಾವಿರ ಮಂದಿಯ ಮೇಲೆಯೂ ನೂರು ಮಂದಿಯ ಮೇಲೆಯೂ ಐವತ್ತು ಮಂದಿಯ ಮೇಲೆಯೂ ಹತ್ತು ಮಂದಿಯ ಮೇಲೆಯೂ ಅಧಿಕಾರಿಗಳನ್ನಾಗಿಯೂ ಉಪಾಧಿಕಾರಿಗಳನ್ನಾಗಿಯೂ ನೇಮಿಸಿದೆನು.",
			"16": "<sup>16</sup> ಆ ನ್ಯಾಯಾಧಿಪತಿಗಳಿಗೆ ನಾನು ಆ ಕಾಲದಲ್ಲಿ ಅಪ್ಪಣೆಮಾಡಿ - ನೀವು ಸ್ವಕುಲದವರ ವ್ಯಾಜ್ಯಗಳನ್ನು ವಿಚಾರಿಸಬೇಕು; ಅವರು ಇಸ್ರಾಯೇಲ್ಯರೊಡನೆ ವ್ಯಾಜ್ಯವಾಡಿದರೂ ಅನ್ಯರೊಡನೆ ವ್ಯಾಜ್ಯವಾಡಿದರೂ ನೀವು ನ್ಯಾಯದ ಪ್ರಕಾರವೇ ತೀರ್ಪುಮಾಡಬೇಕು.",
			"17": "<sup>17</sup> ನ್ಯಾಯವಿಚಾರಿಸುವಾಗ ಮುಖದಾಕ್ಷಿಣ್ಯಮಾಡದೆ ಅಧಿಕರನ್ನೂ ಅಲ್ಪರನ್ನೂ ಸಮನಾಗಿ ತಿಳಿಯಬೇಕು. ನೀವು ದೇವರ ಹೆಸರಿನಲ್ಲಿ ನ್ಯಾಯತೀರಿಸುವವರಾದ ಕಾರಣ ಮನುಷ್ಯರ ಮುಖವನ್ನು ನೋಡಿ ಹೆದರಬೇಡಿರಿ. ನೀವು ತೀರಿಸಲಿಕ್ಕಾಗದ ವ್ಯಾಜ್ಯಗಳನ್ನು ನನ್ನ ಬಳಿಗೆ ತರಬೇಕು; ನಾನೇ ಅವುಗಳನ್ನು ತೀರಿಸುವೆನು ಎಂದು ಹೇಳಿದೆನು.",
			"18": "<sup>18</sup> ಅದಲ್ಲದೆ ನೀವು ಮಾಡಬೇಕಾದ ಎಲ್ಲಾ ಕಾರ್ಯಗಳ ವಿಷಯದಲ್ಲಿಯೂ ನಾನು ಆ ಕಾಲದಲ್ಲಿ ನಿಮಗೆ ಆಜ್ಞೆ ಕೊಟ್ಟೆನು.",
			"19": "<sup>19</sup> ನಾವು ಹೋರೇಬಿನಿಂದ ಹೊರಟು ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಆಜ್ಞಾಪಿಸಿದಂತೆ ಅಮೋರಿಯರ ಬೆಟ್ಟದ ಸೀಮೆಯ ಮಾರ್ಗವನ್ನು ಹಿಡಿದು ನೀವು ನೋಡಿದ ಆ ಘೋರವಾದ ಮಹಾರಣ್ಯದಲ್ಲಿ ನಡೆದು ಕಾದೇಶ್‍ಬರ್ನೇಯಕ್ಕೆ ಸೇರಿದೆವು.",
			"20": "<sup>20</sup> ಆಗ ನಾನು - ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಮಗೆ ಕೊಡುವ ಅಮೋರಿಯರ ಬೆಟ್ಟದ ಸೀಮೆಯ ಹತ್ತಿರಕ್ಕೆ ಬಂದಿದ್ದೀರಿ;",
			"21": "<sup>21</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಆ ದೇಶವನ್ನು ನಿಮಗೇ ಕೊಟ್ಟಿದ್ದಾನೆ; ನಿಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಯೆಹೋವನು ಹೇಳಿದಂತೆ ಅದನ್ನು ಹತ್ತಿ ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳಿರಿ; ನೀವು ದಿಗಿಲುಪಡದೆ ಧೈರ್ಯವಾಗಿಯೇ ಇರಬೇಕೆಂದು ಹೇಳಿದೆನು.",
			"22": "<sup>22</sup> ಆಗ ನೀವೆಲ್ಲರೂ ನನ್ನ ಬಳಿಗೆ ಬಂದು - ನಮಗಿಂತ ಮುಂದಾಗಿ ಮನುಷ್ಯರನ್ನು ಕಳುಹಿಸುವೆವು; ಅವರು ನಮಗೋಸ್ಕರ ಆ ದೇಶವನ್ನು ಪರೀಕ್ಷಿಸಿ ನೋಡಿ ನಾವು ಹತ್ತಿ ಹೋಗಬೇಕಾದ ದಾರಿಯ ವಿಷಯದಲ್ಲೂ ಸೇರಬೇಕಾದ ಊರುಗಳ ವಿಷಯದಲ್ಲೂ ನಮಗೆ ವರ್ತಮಾನ ತಿಳಿಸಲಿ ಎಂದು ಹೇಳಿದಿರಿ.",
			"23": "<sup>23</sup> ಅದು ಒಳ್ಳೇ ಆಲೋಚನೆಯೆಂದು ನಾನು ತಿಳಿದುಕೊಂಡು ನಿಮ್ಮಲ್ಲಿ ಕುಲಕ್ಕೆ ಒಬ್ಬೊಬ್ಬನ ಮೇರೆಗೆ ಹನ್ನೆರಡು ಮಂದಿಯನ್ನು ಆರಿಸಿಕೊಂಡೆನು.",
			"24": "<sup>24</sup> ಅವರು ಹೋಗಿ ಆ ಬೆಟ್ಟದ ಸೀಮೆಯನ್ನು ಹತ್ತಿ ಎಷ್ಕೋಲೆಂಬ ತಗ್ಗಿನ ಬಳಿಗೆ ಬಂದು ಆ ಪ್ರದೇಶವನ್ನು ಸಂಚರಿಸಿ ನೋಡಿ ಅಲ್ಲಿನ ಹಣ್ಣುಗಳಲ್ಲಿ ಕೆಲವನ್ನು ತಂದು ತೋರಿಸಿ -",
			"25": "<sup>25</sup> ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಮಗೆ ಕೊಡುವ ಆ ದೇಶವು ಒಳ್ಳೆಯ ದೇಶವೆಂದು ನಮಗೆ ವರ್ತಮಾನ ತಿಳಿಸಿದರು.",
			"26": "<sup>26</sup> ಆದರೂ ನೀವು ಹತ್ತಲೊಲ್ಲದೆ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಆಜ್ಞೆಯನ್ನು ಉಲ್ಲಂಘಿಸಿ ನಿಮ್ಮ ನಿಮ್ಮ ಡೇರೆಗಳಲ್ಲಿ ಗುಣುಗುಟ್ಟುತ್ತಾ -",
			"27": "<sup>27</sup> ಯೆಹೋವನು ನಮ್ಮನ್ನು ಐಗುಪ್ತದೇಶದಿಂದ ಬರಮಾಡಿದ್ದು ಹಗೆತನದಿಂದಲೇ; ಆತನು ನಮ್ಮನ್ನು ಸಂಹರಿಸಬೇಕೆಂದು ಅಮೋರಿಯರ ಕೈಗೆ ಒಪ್ಪಿಸುತ್ತಾನೆ.",
			"28": "<sup>28</sup> ನಾವು ಹೋಗಬೇಕಾದ ಆ ಸೀಮೆ ಎಂಥದೆಂದು ಆಲೋಚಿಸಿರಿ; ಅಲ್ಲಿ ಹೋಗಿದ್ದ ನಮ್ಮ ಸಹೋದರರು ನಮಗೆ - ಆ ದೇಶದ ಜನರು ನಮಗಿಂತ ಹೆಚ್ಚಾಗಿಯೂ ಉದ್ದವಾಗಿಯೂ ಇದ್ದಾರೆ; ಅವರಿರುವ ಪಟ್ಟಣಗಳು ದೊಡ್ಡವೂ ಆಕಾಶವನ್ನು ಮುಟ್ಟುವ ಕೋಟೆಕೊತ್ತಲುಳ್ಳವೂ ಆಗಿವೆ; ಅಲ್ಲಿ ಉನ್ನತಪುರುಷರನ್ನು ನೋಡಿದೆವೆಂದು ಹೇಳಿ ನಮ್ಮ ಧೈರ್ಯವನ್ನು ಕೆಡಿಸಿದ್ದಾರೆ ಅಂದುಕೊಳ್ಳುತ್ತಿದ್ದಿರಿ.",
			"29": "<sup>29</sup> ಅದಕ್ಕೆ ನಾನು - ಕಳವಳಪಡಬೇಡಿರಿ, ಅವರಿಗೆ ಭಯಪಡಬೇಡಿರಿ.",
			"30": "<sup>30</sup> ನಿಮ್ಮ ಮುಂದುಗಡೆಯಲ್ಲಿ ಹೋಗುವ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಐಗುಪ್ತ ದೇಶದಲ್ಲಿಯೂ ನೀವು ನೋಡಿದ ಅರಣ್ಯದಲ್ಲಿಯೂ ಪ್ರತ್ಯಕ್ಷನಾಗಿ ನಿಮಗೋಸ್ಕರ ಯುದ್ಧಮಾಡಿದಂತೆಯೇ ಈಗಲೂ ನಿಮ್ಮ ಕಡೆಯವನಾಗಿ ಯುದ್ಧಮಾಡುವನು.",
			"31": "<sup>31</sup> ನೀವು ಈ ಸ್ಥಳಕ್ಕೆ ಸೇರಿದ ಪರ್ಯಂತರವೂ ನಿಮ್ಮ ಪ್ರಯಾಣದಲ್ಲೆಲ್ಲಾ ಮಗನನ್ನು ತಂದೆ ಹೇಗೋ ಹಾಗೆಯೇ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಹೊತ್ತನಲ್ಲವೇ ಎಂದು ನಿಮಗೆ ಹೇಳಿದೆನು.",
			"32": "<sup>32</sup> ಆದರೂ ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಇನ್ನೂ ನಂಬಲೇ ಇಲ್ಲ.",
			"33": "<sup>33</sup> ನೀವು ಹೋಗಬೇಕಾದ ದಾರಿಯನ್ನು ತೋರಿಸುವದಕ್ಕೂ ದಂಡು ಇಳಿಯಬೇಕಾದ ಸ್ಥಳಗಳನ್ನು ಗೊತ್ತುಮಾಡುವದಕ್ಕೂ ರಾತ್ರಿಹೊತ್ತು ಬೆಂಕಿಯಲ್ಲಿಯೂ ಹಗಲುಹೊತ್ತು ಮೇಘದಲ್ಲಿಯೂ ಮಾರ್ಗದಲ್ಲಿ ನಿಮ್ಮ ಮುಂದುಗಡೆ ಹೋದ ದೇವರನ್ನು ನೀವು ನಂಬಲಿಲ್ಲ.",
			"34": "<sup>34</sup> ಯೆಹೋವನು ನಿಮ್ಮ ಮಾತುಗಳನ್ನು ಕೇಳಿ ಕೋಪಗೊಂಡು ತಾನು ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ವಾಗ್ದಾನಮಾಡಿದ ಆ ಒಳ್ಳೇ ದೇಶವನ್ನು ಈ ದುಷ್ಟಜನರಲ್ಲಿ ಯಾರೂ ನೋಡುವದಿಲ್ಲವೆಂದು ಪ್ರಮಾಣಮಾಡಿ -",
			"36": "<sup>36</sup> ಯೆಫುನ್ನೆಯ ಮಗನಾದ ಕಾಲೇಬನೊಬ್ಬನೇ ಯೆಹೋವನನ್ನು ಮನಃಪೂರ್ವಕವಾಗಿ ಅನುಸರಿಸಿದದರಿಂದ ಅವನೇ ಅದನ್ನು ನೋಡುವನು; ಅವನು ಸಂಚರಿಸಿದ ಪ್ರದೇಶವನ್ನು ಅವನಿಗೂ ಅವನ ಸಂತತಿಯವರಿಗೂ ಕೊಡುವೆನೆಂದು ಹೇಳಿದನು.",
			"37": "<sup>37</sup> ಅದು ಮಾತ್ರವಲ್ಲದೆ ಯೆಹೋವನು ನಿಮ್ಮ ದೆಸೆಯಿಂದ ನನ್ನ ಮೇಲೆಯೂ ಕೋಪಮಾಡಿ - ನೀನೂ ಆ ದೇಶಕ್ಕೆ ಸೇರುವದಿಲ್ಲ;",
			"38": "<sup>38</sup> ಆದರೆ ನಿನ್ನ ಶಿಷ್ಯನಾದ ನೂನನ ಮಗ ಯೆಹೋಶುವನು ಅಲ್ಲಿಗೆ ಸೇರುವನು. ಅವನೇ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಆ ದೇಶವನ್ನು ಸ್ವಾಧೀನಪಡಿಸುವನು; ಆದದರಿಂದ ಅವನನ್ನು ಧೈರ್ಯಗೊಳಿಸು.",
			"39": "<sup>39</sup> ಆದರೆ ಪರರ ಪಾಲಾಗುವರೆಂದು ನೀವು ಹೇಳಿದ ನಿಮ್ಮ ಮನೆಯವರೂ ಒಳ್ಳೇದು ಕೆಟ್ಟದ್ದು ಅರಿಯದ ನಿಮ್ಮ ಮಕ್ಕಳೂ ಅಲ್ಲಿಗೆ ಸೇರುವರು; ಅವರಿಗೇ ಆ ದೇಶವನ್ನು ಕೊಡುವೆನು; ಅವರು ಅದನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವರು.",
			"40": "<sup>40</sup> ನೀವಾದರೋ ಹಿಂದಿರುಗಿ ಕೆಂಪು ಸಮುದ್ರದ ಮಾರ್ಗವನ್ನು ಹಿಡಿದು ಅರಣ್ಯಕ್ಕೆ ಹೊರಟುಹೋಗಿರಿ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"41": "<sup>41</sup> ಅದಕ್ಕೆ ನೀವು - ನಾವು ಯೆಹೋವನಿಗೆ ದ್ರೋಹಿಗಳಾದೆವು; ಆದರೂ ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಆಜ್ಞಾಪಿಸಿದಂತೆ ನಾವೇ ಈಗ ಆ ಬೆಟ್ಟದ ಸೀಮೆಯನ್ನು ಹತ್ತಿ ಯುದ್ಧಮಾಡುವೆವು ಎಂದು ಉತ್ತರ ಕೊಟ್ಟು ನೀವೆಲ್ಲರೂ ನಿಮ್ಮ ನಿಮ್ಮ ಆಯುಧಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಆ ಬೆಟ್ಟದ ಸೀಮೆಯನ್ನು ಹತ್ತಿ ಜಯಿಸುವದು ಅಲ್ಪಕಾರ್ಯವೆಂದು ಭಾವಿಸಿ ಹೊರಡುವದಕ್ಕಿದ್ದಿರಿ.",
			"42": "<sup>42</sup> ಆದರೆ ಯೆಹೋವನು ನನಗೆ - ನಾನು ಇವರ ಮಧ್ಯದಲ್ಲಿ ಇಲ್ಲವಾದದರಿಂದ ಇವರು ಸೋತು ಹೋಗುವರು; ಯುದ್ಧಮಾಡಲೂಬಾರದು, ಆ ಬೆಟ್ಟದ ಸೀಮೆಗೆ ಹೋಗಲೂಬಾರದು ಎಂದು ಇವರಿಗೆ ಆಜ್ಞಾಪಿಸು ಅಂದನು.",
			"43": "<sup>43</sup> ನಾನು ಆ ಮಾತನ್ನು ನಿಮಗೆ ತಿಳಿಸಿದರೂ ನೀವು ಕಿವಿಗೊಡದೆ ಯೆಹೋವನ ಆಜ್ಞೆಯನ್ನು ತಾತ್ಸಾರ ಮಾಡಿ ಸೊಕ್ಕಿನಿಂದ ಆ ಬೆಟ್ಟವನ್ನು ಹತ್ತಿದಿರಿ.",
			"44": "<sup>44</sup> ಆಗ ಬೆಟ್ಟದಲ್ಲಿದ್ದ ಅಮೋರಿಯರು ನಿಮಗೆ ವಿರೋಧವಾಗಿ ಹೊರಟು ಜೇನುಹುಳಗಳಂತೆ ನಿಮ್ಮನ್ನು ಮುತ್ತಿ ಸೇಯೀರಿನಲ್ಲಿ ಹೊರ್ಮದವರೆಗೂ ಬೆನ್ನಟ್ಟಿ ಸಂಹರಿಸಿದರು.",
			"45": "<sup>45</sup> ನೀವು ಹಿಂದಿರುಗಿ ಬಂದು ಯೆಹೋವನ ಮುಂದೆ ಗೋಳಾಡಿದಾಗ ಆತನು ನಿಮ್ಮ ಮೊರೆಯನ್ನು ಕೇಳಲೂ ಇಲ್ಲ, ರಕ್ಷಿಸಲೂ ಇಲ್ಲ.",
			"46": "<sup>46</sup> ಆಮೇಲೆ ನೀವು ಕಾದೇಶಿನಲ್ಲಿ ಬಹುಕಾಲ ವಾಸವಾಗಿದ್ದಿರಿ."
		},
		"2": {
			"1": "<sup>1</sup> ಆಗ ಯೆಹೋವನು ನನಗೆ ಆಜ್ಞಾಪಿಸಿದಂತೆ ನಾವು ಹಿಂದಿರುಗಿ ಅರಣ್ಯಕ್ಕೆ ಹೊರಟು ಸಮುದ್ರದ ಮಾರ್ಗದಲ್ಲಿ ನಡೆದು ಅನೇಕ ದಿನಗಳು ಸೇಯೀರ್ ಬೆಟ್ಟದ ಸೀಮೆಯನ್ನು ಸುತ್ತುತ್ತಿದ್ದೆವು.",
			"2": "<sup>2</sup> ಆಮೇಲೆ ಯೆಹೋವನು ನನಗೆ -",
			"3": "<sup>3</sup> ನೀವು ಈ ಬೆಟ್ಟದ ಸೀಮೆಯನ್ನು ಸುತ್ತಿದ್ದು ಸಾಕು; ಉತ್ತರದಿಕ್ಕಿಗೆ ತಿರುಗಿಕೊಳ್ಳಿರಿ. ಮತ್ತು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಹೀಗೆ ಆಜ್ಞಾಪಿಸು -",
			"4": "<sup>4</sup> ಸೇಯೀರ್ ದೇಶದಲ್ಲಿ ವಾಸವಾಗಿರುವ ನಿಮ್ಮ ಬಂಧುಗಳಾದ ಏಸಾವನ ವಂಶದವರ ದೇಶವನ್ನು ದಾಟುವದಕ್ಕಿದ್ದೀರಷ್ಟೆ. ಅವರು ನಿಮಗೆ ಅಂಜುವರು; ನೀವು ಅವರ ಸಂಗಡ ಯುದ್ಧಕ್ಕೆ ಹೋಗದೆ ಬಹಳ ಜಾಗರೂಕತೆಯಿಂದಿರಬೇಕು.",
			"5": "<sup>5</sup> ನಾನು ಸೇಯೀರ್‍ಬೆಟ್ಟದ ಸೀಮೆಯನ್ನು ಏಸಾವ್ಯರಿಗೇ ಸ್ವದೇಶವಾಗ ಕೊಟ್ಟಿರುವದರಿಂದ ಅದರಲ್ಲಿ ನಿಮಗೆ ಹೆಜ್ಜೆಯಿಡುವಷ್ಟು ನೆಲವನ್ನಾದರೂ ಕೊಡುವದಿಲ್ಲ.",
			"6": "<sup>6</sup> ನೀವು ಅಲ್ಲಿ ರೊಕ್ಕಕೊಟ್ಟು ಆಹಾರ ಪದಾರ್ಥಗಳನ್ನು ಕೊಂಡು ತಿನ್ನಬೇಕು; ನೀರನ್ನೂ ಕೊಂಡುಕೊಂಡು ಕುಡಿಯಬೇಕು.",
			"7": "<sup>7</sup> ನೀವು ಕೈಹಾಕಿದ ಎಲ್ಲಾ ಕೆಲಸಗಳನ್ನೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಸಫಲಪಡಿಸಿದ್ದಾನಲ್ಲಾ. ಈ ದೊಡ್ಡ ಅರಣ್ಯದಲ್ಲಿ ನೀವು ಸಂಚರಿಸುತ್ತಿರುವಾಗೆಲ್ಲಾ ಆತನು ನಿಮ್ಮನ್ನು ಪರಾಂಬರಿಸುತ್ತಾ ಬಂದನು. ಈ ನಾಲ್ವತ್ತು ವರುಷ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮ ಸಂಗಡ ಇದ್ದದರಿಂದ ನಿಮಗೆ ಏನೂ ಕಡಿಮೆಯಾಗಲಿಲ್ಲ ಎಂದು ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ಆದಕಾರಣ ನಾವು ಸೇಯೀರಿನಲ್ಲಿರುವ ನಮ್ಮ ಬಂಧುಗಳಾದ ಏಸಾವ್ಯರ ಕಡೆಯಿಂದ ಓರೆಯಾಗಿ ತಿರುಗಿಕೊಂಡು ಅರಾಬದ ಮಾರ್ಗವನ್ನು ಬಿಟ್ಟು ಏಲತ್, ಎಚ್ಯೋನ್ಗೆಬೆರ್ ಎಂಬ ಪಟ್ಟಣಗಳಿಂದ ಪ್ರಯಾಣಮಾಡಿ ಮೋವಾಬ್ಯರ ಅಡವಿಯ ದಾರಿಯಲ್ಲಿ ನಡೆದೆವು.",
			"9": "<sup>9</sup> ಆಗ ಯೆಹೋವನು ನನಗೆ - ನೀವು ಮೋವಾಬ್ಯರಿಗೆ ವೈರಿಗಳಾಗಿ ನಡೆದು ಅವರೊಡನೆ ಯುದ್ಧಮಾಡಬಾರದು. ಲೋಟನ ವಂಶದವರಾದ ಅವರಿಗೆ ನಾನು ಆರ್ ಎಂಬ ಪ್ರದೇಶವನ್ನು ಸ್ವದೇಶವಾಗುವದಕ್ಕೆ ಕೊಟ್ಟಿರುವದರಿಂದ ಅದರಲ್ಲಿ ನಿಮಗೆ ಸ್ವಾಸ್ತ್ಯವನ್ನು ಕೊಡುವದಿಲ್ಲ. ನೀವು ಎದ್ದು ಜೆರೆದ್ ಹಳ್ಳವನ್ನು ದಾಟಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"10": "<sup>10</sup> (ತತ್ಪೂರ್ವದಲ್ಲಿ ಏಮಿಯರು ಆ ದೇಶದಲ್ಲಿ ವಾಸವಾಗಿದ್ದರು. ಅವರು ಬಲಿಷ್ಠರೂ ಬಹುಮಂದಿಯೂ ಅನಾಕ್ಯರಂತೆ ಉನ್ನತರೂ ಆಗಿದ್ದರು.",
			"11": "<sup>11</sup> ಅವರು ಅನಾಕ್ಯರಂತೆ ರೆಫಾಯರಿಗೆ ಸೇರಿದವರೆಂದು ಹೇಳುವದುಂಟು; ಆದರೆ ಮೋವಾಬ್ಯರು ಅವರನ್ನು ಏಮಿಯರೆಂದು ಹೇಳುತ್ತಾರೆ.",
			"12": "<sup>12</sup> ಅದೇ ಪೂರ್ವಕಾಲದಲ್ಲಿ ಹೋರಿಯರು ಸೇಯೀರಿನಲ್ಲಿ ವಾಸವಾಗಿದ್ದರು. ತರುವಾಯ ಇಸ್ರಾಯೇಲ್ಯರು ತಮಗೆ ಯೆಹೋವನು ಕೊಟ್ಟ ದೇಶವನ್ನು ಹೇಗೆ ಸ್ವಾಧೀನ ಮಾಡಿಕೊಂಡರೋ ಹಾಗೆಯೇ ಏಸಾವ್ಯರು ಬಂದು ಹೋರಿಯರನ್ನು ಸಂಹರಿಸಿ ಅವರ ದೇಶದಲ್ಲಿ ವಾಸಮಾಡಿದರು.)",
			"13": "<sup>13</sup> ಆಗ ನಾವು ಜೆರೆದ್ ಹಳ್ಳವನ್ನು ದಾಟಿದೆವು. ನಾವು ಕಾದೇಶ್‍ಬರ್ನೇಯದಿಂದ ಹೊರಟು ಜೆರೆದ್ ಹಳ್ಳವನ್ನು ದಾಟಿದವರೆಗೆ ಮೂವತ್ತೆಂಟು ವರುಷ ಕಾಲ. ಯೆಹೋವನು ಪ್ರಮಾಣಪೂರ್ವಕವಾಗಿ ಹೇಳಿದ್ದಂತೆ ಅಷ್ಟರಲ್ಲಿ ಆ ತಲೆಯ ಸೈನಿಕರೆಲ್ಲರೂ ಸತ್ತು ಹೋಗಿದ್ದರು.",
			"15": "<sup>15</sup> ಯೆಹೋವನ ಹಸ್ತವು ಅವರಿಗೆ ವಿರೋಧವಾದದರಿಂದ ಪಾಳೆಯದಲ್ಲಿ ಒಬ್ಬನೂ ಉಳಿಯದಂತೆ ನಾಶವಾದರು.",
			"16": "<sup>16</sup> ಆ ಸೈನಿಕರೆಲ್ಲರೂ ಸತ್ತುಹೋದನಂತರ ಯೆಹೋವನು ನನಗೆ - ನೀವು ಈಗ ಮೋವಾಬ್ಯರ ಪ್ರಾಂತ್ಯವಾಗಿರುವ ಆರ್ ಎಂಬ ಪ್ರದೇಶವನ್ನು ದಾಟುವವರಾಗಿದ್ದೀರಿ.",
			"19": "<sup>19</sup> ಮುಂದೆ ನೀವು ಅಮ್ಮೋನಿಯರ ಹತ್ತಿರಕ್ಕೆ ಬರುವಾಗ ಅವರಿಗೆ ವೈರಿಗಳಾಗಿ ನಡೆದು ಯುದ್ಧಮಾಡಬೇಡಿರಿ. ನಾನು ಆ ದೇಶವನ್ನು ಲೋಟನ ವಂಶದವರಾದ ಅಮ್ಮೋನಿಯರಿಗೆ ಸ್ವಾಸ್ತ್ಯವಾಗಿ ಕೊಟ್ಟಿರುವದರಿಂದ ನಿಮಗೆ ಅದರಲ್ಲಿ ಎಷ್ಟು ಭಾಗವನ್ನಾದರೂ ಕೊಡುವದಿಲ್ಲ.",
			"20": "<sup>20</sup> (ಪೂರ್ವಕಾಲದಲ್ಲಿ ರೆಫಾಯರು ಆ ದೇಶದಲ್ಲಿ ವಾಸವಾಗಿದ್ದದರಿಂದ ಅದೂ ರೆಫಾಯರ ದೇಶವೆನಿಸಿಕೊಳ್ಳುತ್ತದೆ. ಅಮ್ಮೋನಿಯರು ಅವರನ್ನು ಜಂಜುಮ್ಯರೆಂದು ಹೇಳುತ್ತಾರೆ;",
			"21": "<sup>21</sup> ಅವರು ಬಲಿಷ್ಠರೂ ಬಹುಜನವೂ ಅನಾಕ್ಯರಂತೆ ಉನ್ನತರಾದವರೂ ಆಗಿದ್ದರು.",
			"22": "<sup>22</sup> ಆದರೆ ಹೇಗೆ ಯೆಹೋವನು ಸೇಯೀರಿನಲ್ಲಿರುವ ಏಸಾವ್ಯರಿಗೋಸ್ಕರ ಹೋರಿಯರನ್ನು ಹೊರಡಿಸಿದಾಗ ಏಸಾವ್ಯರು ಆ ದೇಶವನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡು ಇಂದಿನವರೆಗೂ ಅಲ್ಲೇ ವಾಸವಾಗಿದ್ದಾರೋ,",
			"23": "<sup>23</sup> ಮತ್ತು ಹೇಗೆ ಕಫ್ತೋರಿಂದ ಬಂದ ಕಫ್ತೋರ್ಯರು ಗಾಜಾ ಪಟ್ಟಣದವರೆಗೂ ಹಳ್ಳಿಗಳಲ್ಲಿದ್ದ ಅವ್ವಿಯರನ್ನು ನಾಶಮಾಡಿ ಅವರ ಸ್ಥಳದಲ್ಲಿ ವಾಸಮಾಡಿದರೋ ಹಾಗೆಯೇ ಯೆಹೋವನು ರೆಫಾಯರನ್ನು ಅಮ್ಮೋನಿಯರ ಎದುರಿನಿಂದ ಹೊರಡಿಸಿದನು; ಅಮ್ಮೋನಿಯರು ಆ ದೇಶವನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡು ಅದರಲ್ಲಿ ವಾಸಿಸಿದರು.)",
			"24": "<sup>24</sup> ಈಗ ನೀವು ಹೊರಟು ಅರ್ನೋನ್ ಹಳ್ಳವನ್ನು ದಾಟಿರಿ. ಅಮೋರಿಯನಾಗಿರುವ ಹೆಷ್ಬೋನಿನ ಅರಸನಾದ ಸೀಹೋನನು ನಿಮ್ಮಿಂದ ಸೋತು ಹೋಗಿ ಅವನ ರಾಜ್ಯವು ನಿಮಗೇ ವಶವಾಗಬೇಕೆಂದು ನಿಮಗೆ ಅನುಗ್ರಹಿಸಿದ್ದೇನೆ. ಅವನೊಡನೆ ಯುದ್ಧಮಾಡಿ ಆ ದೇಶವನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವದಕ್ಕೆ ಪ್ರಾರಂಭಿಸಿರಿ.",
			"25": "<sup>25</sup> ಲೋಕದಲ್ಲಿರುವ ಎಲ್ಲಾ ಜನಗಳಿಗೂ ನಿಮ್ಮಿಂದ ದಿಗಿಲೂ ಹೆದರಿಕೆಯೂ ಉಂಟಾಗುವಂತೆ ಇಂದಿನಿಂದ ಮಾಡುತ್ತೇನೆ. ಅವರು ನಿಮ್ಮ ಸುದ್ದಿಯನ್ನು ಕೇಳಿದ ಮಾತ್ರಕ್ಕೆ ಗಡಗಡನೆ ನಡುಗಿ ಸಂಕಟಪಡುವರು ಎಂದು ಹೇಳಿದನು.",
			"26": "<sup>26</sup> ಆಗ ನಾನು ಕೆದೇಮೋತಿನ ಅರಣ್ಯದಿಂದ ಹೆಷ್ಬೋನಿನ ಅರಸನಾದ ಸೀಹೋನನ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿ -",
			"27": "<sup>27</sup> ನಿನ್ನ ದೇಶವನ್ನು ದಾಟಿಹೋಗುವದಕ್ಕೆ ನಮಗೆ ಅಪ್ಪಣೆಯಾಗಬೇಕು. ನಾವು ಎಡಬಲಕ್ಕೆ ತಿರುಗದೆ ದಾರಿಹಿಡಿದು ನಡೆದುಹೋಗುವೆವು.",
			"28": "<sup>28</sup> ನಮ್ಮಿಂದ ಕ್ರಯತೆಗೆದುಕೊಂಡು ಆಹಾರಪದಾರ್ಥಗಳನ್ನೂ ಕುಡಿಯಲಿಕ್ಕೆ ನೀರನ್ನೂ ಕೊಡಿರಿ.",
			"29": "<sup>29</sup> ನಾವು ಯೊರ್ದನ್ ಹೊಳೆಯನ್ನು ದಾಟಿ ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಮಗೆ ಕೊಡುವ ದೇಶವನ್ನು ಸೇರಬೇಕೆಂದಿದ್ದೇವೆ. ಸೇಯೀರಿನಲ್ಲಿರುವ ಏಸಾವ್ಯರೂ ಆರ್ ಪ್ರದೇಶದಲ್ಲಿರುವ ಮೋವಾಬ್ಯರೂ ನಮಗೆ ದಾರಿಕೊಟ್ಟಂತೆ ನೀನೂ ಕೊಡಬೇಕು. ಕಾಲ್ನಡೆಯಾಗಿ ನಿನ್ನ ದೇಶವನ್ನು ದಾಟಿಹೋಗುವದಕ್ಕೆ ನಿನ್ನ ಅಪ್ಪಣೆಯಾಗಬೇಕೇ ಹೊರತು ಬೇರೆ ಏನೂ ಬೇಕಿಲ್ಲವೆಂದು ವಿನಯಪೂರ್ವಕವಾದ ಮಾತುಗಳಿಂದ ಹೇಳಿಸಿದೆನು.",
			"30": "<sup>30</sup> ಆದರೆ ಯೆಹೋವನು ಅವನಿಗೆ ಮೂರ್ಖಬುದ್ಧಿಯನ್ನು ಕೊಟ್ಟು ಹಟವನ್ನು ಹುಟ್ಟಿಸಿದದರಿಂದ ಅವನು ಸಮ್ಮತಿಸಲಿಲ್ಲ. ಅವನು ನಿಮ್ಮಿಂದ ಸೋತುಹೋಗಬೇಕೆಂಬದೇ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಸಂಕಲ್ಪ; ಅದು ಈಗಾಗಲೇ ನೆರವೇರಿತಲ್ಲಾ.",
			"31": "<sup>31</sup> ತರುವಾಯ ಯೆಹೋವನು ನನಗೆ - ಈಗ ನಾನು ಸೀಹೋನನನ್ನೂ ಅವನ ದೇಶವನ್ನೂ ನಿಮಗೆ ವಶಪಡಿಸುವದಕ್ಕೆ ಪ್ರಾರಂಭಿಸಿದ್ದೇನೆ; ಅದನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವದಕ್ಕೆ ಪ್ರಾರಂಭಮಾಡಿರಿ ಎಂದು ಹೇಳಿದನು.",
			"32": "<sup>32</sup> ಸೀಹೋನನು ತನ್ನ ಜನರೆಲ್ಲರ ಸಮೇತ ನಮ್ಮೊಡನೆ ಯುದ್ಧಮಾಡುವದಕ್ಕೆ ಯಹಜಿಗೆ ಹೊರಟುಬರಲಾಗಿ",
			"33": "<sup>33</sup> ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಅವನನ್ನು ನಮ್ಮಿಂದ ಪರಾಜಯಪಡಿಸಿದನು. ನಾವು ಅವನನ್ನೂ ಅವನ ಮಕ್ಕಳನ್ನೂ ಜನರೆಲ್ಲರನ್ನೂ ಸಂಹರಿಸಿದೆವು.",
			"34": "<sup>34</sup> ಆ ಕಾಲದಲ್ಲಿ ನಾವು ಅವನ ಎಲ್ಲಾ ಪಟ್ಟಣಗಳನ್ನೂ ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡು ಅವುಗಳಲ್ಲಿದ್ದ ಗಂಡಸರನ್ನೂ ಹೆಂಗಸರನ್ನೂ ಮಕ್ಕಳನ್ನೂ ನಿಶ್ಶೇಷವಾಗಿ ಹತಮಾಡಿದೆವು; ಒಬ್ಬನನ್ನೂ ಉಳಿಸಲಿಲ್ಲ.",
			"35": "<sup>35</sup> ಪಶುಗಳನ್ನು ಮಾತ್ರ ಉಳಿಸಿ ನಮ್ಮ ಸ್ವಂತಕ್ಕಾಗಿ ತೆಗೆದುಕೊಂಡು ಊರುಗಳನ್ನು ಸೂರೆಮಾಡಿಬಿಟ್ಟೆವು.",
			"36": "<sup>36</sup> ಅರ್ನೋನ್ ತಗ್ಗಿನ ಅಂಚಿನಲ್ಲಿರುವ ಅರೋಯೇರ್ ಪಟ್ಟಣವು ಮತ್ತು ತಗ್ಗಿನಲ್ಲಿಯೇ ಇರುವ ಪಟ್ಟಣವು ಇವುಗಳು ಮೊದಲುಗೊಂಡು ಗಿಲ್ಯಾದ್ ಸೀಮೆಯವರೆಗೂ ಯಾವ ಪಟ್ಟಣವೂ ನಮಗೆ ಅಸಾಧ್ಯವಾಗಲಿಲ್ಲ. ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಅವುಗಳನ್ನೆಲ್ಲಾ ನಮ್ಮ ಕೈಗೆ ಸಿಕ್ಕುವಂತೆ ಮಾಡಿದನು.",
			"37": "<sup>37</sup> ಅಮ್ಮೋನಿಯರ ದೇಶವನ್ನು ಮಾತ್ರ ಅಂದರೆ ಯಬ್ಬೋಕ್ ಹೊಳೆಯ ಬಳಿಯಲ್ಲಿರುವ ಪ್ರದೇಶವು ಮತ್ತು ಬೆಟ್ಟದ ಸೀಮೆಯ ಊರುಗಳು ಇವೇ ಮೊದಲಾದ ಸ್ಥಳಗಳನ್ನು ನೀವು ಅತಿಕ್ರಮಿಸಲಿಲ್ಲ; ಅಲ್ಲಿಗೆ ಹೋಗಲೇಬಾರದೆಂದು ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಆಜ್ಞಾಪಿಸಿದ್ದನಷ್ಟೆ."
		},
		"3": {
			"1": "<sup>1</sup> ತರುವಾಯ ನಾವು ಹಿಂದಿರುಗಿ ಬಾಷಾನಿನ ಮಾರ್ಗವನ್ನು ಹಿಡಿದು ಹತ್ತಿಹೋಗಲಾಗಿ ಬಾಷಾನಿನ ಅರಸನಾದ ಓಗನು ತನ್ನ ಜನರೆಲ್ಲರ ಸಮೇತ ನಮಗೆ ವಿರೋಧವಾಗಿ ಯುದ್ಧಮಾಡುವದಕ್ಕೆ ಎದ್ರೈವೂರಿಗೆ ಹೊರಟುಬಂದನು.",
			"2": "<sup>2</sup> ಆಗ ಯೆಹೋವನು ನನಗೆ - ಅವನಿಗೆ ಭಯಪಡಬೇಡ; ನಾನು ಅವನನ್ನೂ ಅವನ ಸಮಸ್ತಜನರನ್ನೂ ದೇಶವನ್ನೂ ನಿನ್ನ ಕೈವಶ ಮಾಡಿದ್ದೇನೆ; ನೀನು ಹೆಷ್ಬೋನಿನಲ್ಲಿದ್ದ ಅಮೋರಿಯರ ಅರಸನಾದ ಸೀಹೋನನಿಗೆ ಮಾಡಿದ ಪ್ರಕಾರವೇ ಇವನಿಗೂ ಮಾಡಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"3": "<sup>3</sup> ಬಾಷಾನಿನ ಅರಸನಾದ ಓಗನೂ ಅವನ ಜನರೆಲ್ಲರೂ ನಮ್ಮಿಂದ ಸೋತು ಹೋಗುವಂತೆ ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಮಾಡಲಾಗಿ ಅವರಲ್ಲಿ ಒಬ್ಬರಾದರೂ ಉಳಿಯದಂತೆ ಅವರನ್ನು ಹತಮಾಡಿದೆವು.",
			"4": "<sup>4</sup> ಆ ಕಾಲದಲ್ಲಿ ನಾವು ಅವನ ಎಲ್ಲಾ ಪಟ್ಟಣಗಳನ್ನು ಜಯಿಸಿದೆವು; ನಾವು ಜಯಿಸದ ಕೋಟೆ ಒಂದೂ ಇರಲಿಲ್ಲ; ಬಾಷಾನಿನಲ್ಲಿರುವ ಓಗನ ರಾಜ್ಯದಲ್ಲಿ ಅಂದರೆ ಅರ್ಗೋಬ್ ಎಂಬ ಸಮಸ್ತ ಪ್ರದೇಶದಲ್ಲಿ ಅರುವತ್ತು ಪಟ್ಟಣಗಳನ್ನು ತೆಗೆದುಕೊಂಡೆವು.",
			"5": "<sup>5</sup> ಅವುಗಳೆಲ್ಲಾ ಎತ್ತರವಾದ ಪೌಳಿಗೋಡೆಗಳಿಂದಲೂ ಕದಗಳಿಂದಲೂ ಅಗುಳಿಗಳಿಂದಲೂ ಭದ್ರಮಾಡಲ್ಪಟ್ಟ ಪಟ್ಟಣಗಳೇ. ಅವುಗಳಲ್ಲದೆ ಹಳ್ಳಿಪಳ್ಳಿಗಳು ಬಹಳ ಇದ್ದವು.",
			"6": "<sup>6</sup> ನಾವು ಹೆಷ್ಬೋನಿನ ಅರಸನಾದ ಸೀಹೋನನ ಪಟ್ಟಣಗಳಿಗೆ ಮಾಡಿದಂತೆ ಇವುಗಳಿಗೂ ಮಾಡಿದೆವು. ಅವುಗಳಲ್ಲಿದ್ದ ಗಂಡಸರನ್ನೂ ಹೆಂಗಸರನ್ನೂ ಮಕ್ಕಳನ್ನೂ ನಿಶ್ಶೇಷವಾಗಿ ನಾಶಮಾಡಿದೆವು.",
			"7": "<sup>7</sup> ಪಶುಗಳನ್ನು ಮಾತ್ರ ಉಳಿಸಿ ಸ್ವಂತಕ್ಕೆ ತೆಗೆದುಕೊಂಡು ಊರುಗಳನ್ನೆಲ್ಲಾ ಸೂರೆಮಾಡಿಬಿಟ್ಟೆವು.",
			"8": "<sup>8</sup> ಆ ಕಾಲದಲ್ಲಿ ನಾವು ಅರ್ನೋನ್ ಹೊಳೆ ಮೊದಲುಗೊಂಡು ಹೆರ್ಮೋನ್ ಪರ್ವತದವರೆಗೂ ಯೊರ್ದನ್ ಹೊಳೆಯ ಈಚೆ ಇರುವ ದೇಶವನ್ನೆಲ್ಲಾ ಅಮೋರಿಯರ ಇಬ್ಬರು ಅರಸರಿಂದ ತೆಗೆದುಕೊಂಡೆವು.",
			"9": "<sup>9</sup> (ಹೆರ್ಮೋನ್ ಪರ್ವತಕ್ಕೆ ಚೀದೋನ್ಯರು ಸಿರ್ಯೋನೆಂದೂ ಅಮೋರಿಯರು ಸೆನೀರ್ ಎಂದೂ ಹೆಸರಿಟ್ಟುಕೊಂಡಿದ್ದಾರೆ.)",
			"10": "<sup>10</sup> ಹೀಗೆ ಮೀಶೋರೆಂಬ ಎತ್ತರವಾದ ಬೈಲುಸೀಮೆಯ ಎಲ್ಲಾ ಪಟ್ಟಣಗಳನ್ನೂ ಗಿಲ್ಯಾದ್ ಸೀಮೆಯನ್ನೂ ಓಗನ ರಾಜ್ಯವಾದ ಸಲ್ಕಾ, ಎದ್ರೈ ಎಂಬ ಪಟ್ಟಣಗಳಿಂದ ಕೂಡಿರುವ ಬಾಷಾನ್ ಸೀಮೆಯನ್ನೂ ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡೆವು.",
			"11": "<sup>11</sup> (ರೆಫಾಯರೊಳಗೆ ಬಾಷಾನಿನ ಅರಸನಾದ ಓಗನೊಬ್ಬನೇ ಉಳಿದಿದ್ದನು. ಅವನ ಮಂಚ ಕಬ್ಬಿಣದು; ಅಮ್ಮೋನಿಯರ ರಬ್ಬಾ ಎಂಬ ಪಟ್ಟಣದಲ್ಲಿ ಅದನ್ನು ಈಗಲೂ ನೋಡಬಹುದು. ಪುರುಷನ ಕೈಯಳತೆಯ ಪ್ರಕಾರ ಅದರ ಉದ್ದ ಒಂಭತ್ತು ಮೊಳ; ಅಗಲ ನಾಲ್ಕು ಮೊಳ.)",
			"12": "<sup>12</sup> ಆ ಕಾಲದಲ್ಲಿ ನಾವು ಆ ದೇಶವನ್ನೆಲ್ಲಾ ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡೆವು. ಅರ್ನೋನ್ ಹಳ್ಳದ ಬಳಿಯಲ್ಲಿರುವ ಅರೋಯೇರ್ ಮೊದಲುಗೊಂಡು ಗಿಲ್ಯಾದ್ ಸೀಮೆಯ ಮಧ್ಯದವರೆಗೂ ಇರುವ ಪ್ರದೇಶವನ್ನೆಲ್ಲಾ ಅದರ ಪಟ್ಟಣಗಳು ಸಹಿತವಾಗಿ ನಾನು ರೂಬೇನ್ಯರಿಗೂ ಗಾದ್ಯರಿಗೂ ಕೊಟ್ಟೆನು.",
			"13": "<sup>13</sup> ಗಿಲ್ಯಾದಿನ ಮಿಕ್ಕ ಭಾಗವನ್ನೂ ಓಗನ ರಾಜ್ಯವಾಗಿದ್ದ ಅರ್ಗೋಬ್ ಎನಿಸಿಕೊಳ್ಳುವ ಸಮಸ್ತ ಬಾಷಾನ್ ದೇಶವನ್ನೂ ಮನಸ್ಸೆ ಕುಲದ ಅರ್ಧ ಜನರಿಗೆ ಕೊಟ್ಟೆನು.",
			"14": "<sup>14</sup> (ಬಾಷಾನ್ ದೇಶವು ರೆಫಾಯರ ದೇಶವೆನಿಸಿಕೊಳ್ಳುತ್ತದೆ. ಮನಸ್ಸೆಯ ವಂಶಸ್ಥನಾದ ಯಾಯೀರನು ಗೆಷೂರ್ಯರ ಮತ್ತು ಮಾಕಾತ್ಯರ ಮೇರೆಯವರೆಗೆ ಅರ್ಗೋಬ್ ಎಂಬ ಪ್ರದೇಶವನ್ನೆಲ್ಲಾ ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡು ಬಾಷಾನಿಗೆ ಸೇರಿರುವ ಆ ಪ್ರಾಂತ್ಯಕ್ಕೆ ಯಾಯೀರನ ಗ್ರಾಮಗಳೆಂದು ತನ್ನ ಹೆಸರಿಟ್ಟನು. ಈ ಹೆಸರು ಇಂದಿನವರೆಗೂ ಉಂಟು.)",
			"15": "<sup>15</sup> ನಾನು ಗಿಲ್ಯಾದ್ ದೇಶವನ್ನು ಮಾಕೀರನಿಗೂ",
			"16": "<sup>16</sup> ಗಿಲ್ಯಾದ್ ಮೊದಲುಗೊಂಡು [ದಕ್ಷಿಣಕಡೆಯಲ್ಲಿ] ಅರ್ನೋನ್ ತಗ್ಗಿನವರೆಗೂ [ಪೂರ್ವಕಡೆಯಲ್ಲಿ] ಯಬ್ಬೋಕ್ ಹೊಳೆಯವರೆಗೂ ಇರುವ ಪ್ರದೇಶವನ್ನು ರೂಬೇನ್ಯರಿಗೂ ಗಾದ್ಯರಿಗೂ ಕೊಟ್ಟೆನು. ಅರ್ನೋನ್ ತಗ್ಗಿನ ಮಧ್ಯಭಾಗವೇ ಅವರ [ದಕ್ಷಿಣ] ಮೇರೆ. ಅವರಿಗೂ ಅಮ್ಮೋನಿಯರಿಗೂ ನಡುವೆ ಇರುವ ಯಬ್ಬೋಕ್ ಹೊಳೆಯು ಅವರ [ಪೂರ್ವದಿಕ್ಕಿನ] ಮೇರೆ.",
			"17": "<sup>17</sup> ಅದಲ್ಲದೆ ಅರಾಬಾ ಎಂಬ ತಗ್ಗನ್ನೂ ಅವರಿಗೆ ಕೊಟ್ಟೆನು; ಅದರಲ್ಲಿ ಕಿನ್ನೆರೆತ್ ಸಮುದ್ರ ಮೊದಲುಗೊಂಡು ಪಿಸ್ಗಾ ಬೆಟ್ಟದ ಪಶ್ಚಿಮದಲ್ಲಿರುವ ಅರಾಬದ ಸಮುದ್ರವೆನಿಸಿಕೊಳ್ಳುವ ಲವಣ ಸಮುದ್ರದವರೆಗೆ ಯೊರ್ದನ್ ಹೊಳೆಯು ಅವರ [ಪಶ್ಚಿಮ] ಮೇರೆ.",
			"18": "<sup>18</sup> ಆ ಕಾಲದಲ್ಲಿ ನಾನು ನಿಮಗೆ - ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಈ ಪ್ರದೇಶವನ್ನೇ ನಿಮಗೆ ಸ್ವಾಸ್ತ್ಯಕ್ಕಾಗಿ ಕೊಟ್ಟಿದ್ದಾನೆ. ಆದರೆ ನಿಮ್ಮ ಭಟರೆಲ್ಲರೂ ಯುದ್ಧಸನ್ನದ್ಧರಾಗಿ ನಿಮ್ಮ ಸಹೋದರರಾದ ಇಸ್ರಾಯೇಲ್ಯರ ಮುಂದೆ ಹೊರಟು ಹೊಳೇ ದಾಟಿ ಹೋಗಬೇಕು.",
			"19": "<sup>19</sup> ನಿಮಗೆ ಬಹಳ ದನಕುರಿಗಳುಂಟೆಂಬದನ್ನು ನಾನು ಬಲ್ಲೆ. ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಯೊರ್ದನ್ ಹೊಳೆಯ ಆಚೆ ನಿಮ್ಮ ಸಹೋದರರಿಗೆ ಕೊಡುವ ದೇಶವು ಅವರಿಗೆ ಸ್ವಾಧೀನವಾಗುವವರೆಗೆ ನಿಮ್ಮ ಹೆಂಡರುಮಕ್ಕಳೂ ದನಕುರಿಗಳೂ ನಾನು ನಿಮಗೆ ಕೊಟ್ಟಿರುವ ಪಟ್ಟಣಗಳಲ್ಲೇ ಇರಲಿ.",
			"20": "<sup>20</sup> ಅನಂತರ ನಿಮ್ಮಂತೆ ನಿಮ್ಮ ಸಹೋದರರಿಗೂ ಯೆಹೋವನಿಂದ ವಿಶ್ರಾಂತಿ ದೊರಕಿದಾಗ ನಿಮ್ಮಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬನೂ ನಾನು ಕೊಟ್ಟ ಸ್ವಾಸ್ತ್ಯಕ್ಕೆ ತಿರಿಗಿ ಬರಬಹುದು ಎಂದು ಆಜ್ಞಾಪಿಸಿದೆನು.",
			"21": "<sup>21</sup> ಆ ಕಾಲದಲ್ಲಿ ನಾನು ಯೆಹೋಶುವನಿಗೆ - ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಆ ಇಬ್ಬರು ಅರಸರಿಗೆ ಮಾಡಿದ್ದನ್ನು ನೀನು ನೋಡೇ ಇದ್ದೀ. ನೀನು ಹೊಳೇ ದಾಟಿಹೋಗುವ ಎಲ್ಲಾ ರಾಜ್ಯಗಳನ್ನೂ ಆತನು ಹಾಗೆಯೇ ನಾಶಮಾಡುವನು.",
			"22": "<sup>22</sup> ನೀವು ಅವರಿಗೆ ಭಯಪಡಬೇಡಿರಿ; ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ತಾನೇ ನಿಮ್ಮ ಕಡೆಯವನಾಗಿ ಯುದ್ಧಮಾಡುವನು ಎಂದು ಆಜ್ಞಾಪಿಸಿದೆನು.",
			"23": "<sup>23</sup> ಆ ಕಾಲದಲ್ಲಿ ನಾನು ಯೆಹೋವನಿಗೆ - ಕರ್ತನಾದ ಯೆಹೋವನೇ, ನೀನು ನಿನ್ನ ಮಹತ್ವವನ್ನೂ ಭುಜಬಲವನ್ನೂ ನಿನ್ನ ದಾಸನಿಗೆ ತೋರಿಸಲಾರಂಭಿಸಿದಿ. ನೀನು ನಡಿಸುವ ಈ ಮಹತ್ಕಾರ್ಯಗಳಿಗೆ ಸಮಾನವಾದ ಕಾರ್ಯಗಳನ್ನು ಆಕಾಶದಲ್ಲಾಗಲಿ ಭೂಮಿಯಲ್ಲಾಗಲಿ ಬೇರೆ ಯಾವ ದೇವರು ನಡಿಸಬಲ್ಲನು?",
			"25": "<sup>25</sup> ಕರ್ತನೇ, ನಾನೂ ಈ ಹೊಳೆಯನ್ನು ದಾಟಿ ಆಚೆಯಿರುವ ಒಳ್ಳೆಯ ದೇಶವನ್ನು ಅಂದರೆ ಆ ಅಂದವಾದ ಬೆಟ್ಟದ ಸೀಮೆಯನ್ನೂ ಲೆಬನೋನ್ ಪರ್ವತವನ್ನೂ ನೋಡುವದಕ್ಕೆ ಅಪ್ಪಣೆಯಾಗಲಿ ಎಂದು ಬಿನ್ನೈಸಿದೆನು.",
			"26": "<sup>26</sup> ಆದರೆ ಯೆಹೋವನು ನಿಮ್ಮ ದೆಸೆಯಿಂದ ನನ್ನ ಮೇಲೆ ಕೋಪವುಳ್ಳವನಾಗಿ ನನ್ನ ಮನವಿಯನ್ನು ಕೇಳದೆ - ಸಾಕು; ಇದರ ವಿಷಯದಲ್ಲಿ ಇನ್ನು ನನ್ನ ಸಂಗಡ ಮಾತಾಡಬೇಡ; ನೀನು ಯೊರ್ದನ್ ಹೊಳೆಯನ್ನು ದಾಟಕೂಡದು.",
			"27": "<sup>27</sup> ಆದರೆ ಪಿಸ್ಗಾ ಬೆಟ್ಟವನ್ನು ಹತ್ತಿ ತುದಿಯಲ್ಲಿ ನಿಂತು ಚತುರ್ದಿಕ್ಕುಗಳಲ್ಲಿ ಸುತ್ತಲೂ ಇರುವ ದೇಶವನ್ನು ಕಣ್ಣು ತುಂಬಾ ನೋಡಬಹುದು.",
			"28": "<sup>28</sup> ಯೆಹೋಶುವನೇ ಈ ಜನರ ನಾಯಕನಾಗಿ ಹೋಗಿ ನೀನು ನೋಡುವ ದೇಶವನ್ನು ಅವರಿಗೆ ಸ್ವಾಧೀನಪಡಿಸುವನು. ಅವನಿಗೇ ಈ ಅಧಿಕಾರವನ್ನು ಕೊಡಬೇಕು; ಅವನು ದೃಢನಾಗುವಂತೆ ಅವನನ್ನು ಧೈರ್ಯಪಡಿಸಬೇಕೆಂದು ಉತ್ತರ ಕೊಟ್ಟನು.",
			"29": "<sup>29</sup> ಆಗ ನಾವು ಬೇತ್‍ಪೆಗೋರಿಗೆ ಎದುರಾಗಿರುವ ಕಣಿವೆಯಲ್ಲಿ ವಾಸವಾಗಿದ್ದೆವು."
		},
		"4": {
			"1": "<sup>1</sup> ಇಸ್ರಾಯೇಲ್ಯರೇ, ಕೇಳಿರಿ; ನೀವು ಬದುಕಿಕೊಂಡು ನಿಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಕೊಡುವ ದೇಶವನ್ನು ಸೇರಿ ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವಂತೆ ನಾನು ಈಗ ಬೋಧಿಸುವ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ಗಮನಿಸಿ ಅನುಸರಿಸಬೇಕು.",
			"2": "<sup>2</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಕೊಟ್ಟ ಆಜ್ಞೆಗಳನ್ನೇ ನಾನು ನಿಮಗೆ ತಿಳಿಸುತ್ತಾ ಇದ್ದೇನೆ. ಈ ಮಾತುಗಳನ್ನು ನೀವು ಕೈಕೊಳ್ಳಬೇಕೇ ಹೊರತು ಅವುಗಳಿಗೆ ಏನೂ ಕೂಡಿಸಬಾರದು, ಅವುಗಳಿಂದ ಏನೂ ತೆಗೆದುಬಿಡಬಾರದು.",
			"3": "<sup>3</sup> ಪೆಗೋರದ ಬಾಳನ ಸಂಗತಿಯಲ್ಲಿ ಯೆಹೋವನು ಮಾಡಿದ ಕಾರ್ಯವನ್ನು ನೀವು ನೋಡೇ ಇದ್ದೀರಷ್ಟೆ; ಪೆಗೋರದ ಬಾಳನನ್ನು ಅನುಸರಿಸಿದವರೆಲ್ಲರನ್ನೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮಲ್ಲಿರದಂತೆ ನಾಶಮಾಡಿದನಲ್ಲಾ;",
			"4": "<sup>4</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಹೊಂದಿಕೊಂಡವರಾದ ನೀವಾದರೋ ಇಂದಿನವರೆಗೂ ಉಳಿದುಕೊಂಡಿದ್ದೀರಿ.",
			"5": "<sup>5</sup> ನೀವು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವದಕ್ಕೆ ಹೋಗುವ ದೇಶದಲ್ಲಿ ಅನುಸರಿಸಬೇಕಾದ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ನನ್ನ ದೇವರಾದ ಯೆಹೋವನು ನನಗೆ ಆಜ್ಞಾಪಿಸಿದಂತೆಯೇ ನಿಮಗೆ ಬೋಧಿಸಿದ್ದೇನೆ.",
			"6": "<sup>6</sup> ಇವುಗಳನ್ನು ಕೈಕೊಂಡು ಅನುಸರಿಸಿರಿ. ಅನುಸರಿಸಿದರೇ ಅನ್ಯಜನಗಳು ನಿಮ್ಮನ್ನು ಜ್ಞಾನಿಗಳೂ ವಿವೇಕಿಗಳೂ ಎಂದು ತಿಳಿಯುವರು. ಅವರು ಈ ಆಜ್ಞೆಗಳ ವಿಷಯದಲ್ಲಿ ವರ್ತಮಾನವನ್ನು ಕೇಳಿ - ಈ ದೊಡ್ಡ ಜನಾಂಗವು ಜ್ಞಾನ ವಿವೇಕವುಳ್ಳ ಜನಾಂಗ ಎಂದು ಮಾತಾಡಿಕೊಳ್ಳುವರು.",
			"7": "<sup>7</sup> ನಾವು ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಡುವಾಗೆಲ್ಲಾ ಆತನು ಸಮೀಪವಾಗಿಯೇ ಇದ್ದಾನಲ್ಲಾ; ಬೇರೆ ಎಷ್ಟು ದೊಡ್ಡ ಜನಾಂಗಕ್ಕಾದರೂ ಯಾವ ದೇವರು ಹೀಗೆ ಸಮೀಪವಾಗಿರುವನು?",
			"8": "<sup>8</sup> ನಾನು ಈ ಹೊತ್ತು ನಿಮ್ಮ ಮುಂದೆ ಇಡುವ ಇಂಥ ನ್ಯಾಯವಾದ ಆಜ್ಞಾವಿಧಿಗಳುಳ್ಳ ಈ ಧರ್ಮಶಾಸ್ತ್ರಕ್ಕೆ ಸಮಾನವಾದದ್ದು ಬೇರೆ ಯಾವ ಜನಾಂಗಕ್ಕೆ ಉಂಟು?",
			"9": "<sup>9</sup> ಹೀಗಿರುವದರಿಂದ ನೀವು ಬಹು ಜಾಗರೂಕತೆಯಿಂದ ನಿಮ್ಮನಿಮ್ಮ ಮನಸ್ಸುಗಳನ್ನು ಕಾಪಾಡಿಕೊಳ್ಳುವವರಾಗಿ ನೀವು ನೋಡಿದ ಸಂಗತಿಗಳನ್ನು ಎಷ್ಟು ಮಾತ್ರವೂ ಮರೆಯದೆ ಜೀವದಿಂದಿರುವವರೆಗೂ ಅವುಗಳನ್ನು ನೆನಪಿನಲ್ಲಿಟ್ಟುಕೊಂಡು ನಿಮ್ಮ ಮಕ್ಕಳಿಗೂ ಮೊಮ್ಮಕ್ಕಳಿಗೂ ತಿಳಿಸುತ್ತಾ ಹೋಗಬೇಕು.",
			"10": "<sup>10</sup> ಆ ಸಂಗತಿಗಳು ಯಾವವಂದರೆ - ನೀವು ಹೋರೇಬಿನಲ್ಲಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನಿಂತಿದ್ದಾಗ ಆತನು ನನಗೆ - ಜನರೆಲ್ಲರನ್ನು ನನ್ನ ಹತ್ತಿರಕ್ಕೆ ಕೂಡಿಸು; ಅವರು ತಾವು ಭೂಮಿಯ ಮೇಲಿರುವ ದಿನಗಳಲ್ಲೆಲ್ಲಾ ನನಗೆ ಭಯಭಕ್ತಿಯಿಂದಿರುವದಕ್ಕೆ ಕಲಿತುಕೊಂಡು ತಮ್ಮ ಮಕ್ಕಳಿಗೂ ಕಲಿಸಿಕೊಡುವಂತೆ ಅವರಿಗೆ ಆಜ್ಞೆಗಳನ್ನು ತಿಳಿಸುತ್ತೇನೆ ಎಂದು ಹೇಳಿದ ದಿನದ ವಿಷಯಗಳೇ.",
			"11": "<sup>11</sup> ಆ ದಿನದಲ್ಲಿ ನೀವು ಹತ್ತಿರ ಬಂದು ಬೆಟ್ಟದ ಬುಡದಲ್ಲಿ ನಿಂತುಕೊಂಡಿರಲು ಮೊಬ್ಬೂ ಮೋಡವೂ ಕಾರ್ಗತ್ತಲೂ ಕವಿದು ಆ ಬೆಟ್ಟವು ಆಕಾಶದ ತುದಿಯವರೆಗೂ ಬೆಂಕಿಯಿಂದ ಉರಿದದ್ದೂ;",
			"12": "<sup>12</sup> ಯೆಹೋವನು ಆ ಬೆಂಕಿಯ ಜ್ವಾಲೆಯೊಳಗಿಂದ ನಿಮ್ಮ ಸಂಗಡ ಮಾತಾಡಿದ್ದೂ; ಮಾತಾಡಿದ ಸ್ವರ ನಿಮಗೆ ಕೇಳಿಸಿತೇ ಹೊರತು ಯಾವ ಆಕಾರವೂ ಕಾಣಿಸದೆ ಹೋದದ್ದೂ;",
			"13": "<sup>13</sup> ನೀವು ಅನುಸರಿಸಬೇಕೆಂದು ಆತನು ನೇಮಿಸಿದ ನಿಬಂಧನೆಯನ್ನು ಅಂದರೆ ಹತ್ತು ಕಟ್ಟಳೆಗಳನ್ನು ನಿಮಗೆ ವಿವರಿಸಿ ಎರಡುಕಲ್ಲಿನ ಹಲಗೆಗಳಲ್ಲಿ ಕೆತ್ತಿಸಿದ್ದೂ;",
			"14": "<sup>14</sup> ನೀವು ಹೊಳೆ ದಾಟಿ ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವ ದೇಶದಲ್ಲಿ ಅನುಸರಿಸಬೇಕಾದ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ನಿಮಗೆ ಬೋಧಿಸಬೇಕೆಂದು ಯೆಹೋವನು ಆ ಕಾಲದಲ್ಲಿ ನನಗೆ ಆಜ್ಞಾಪಿಸಿದ್ದೂ; ಇವುಗಳನ್ನೆಲ್ಲಾ ನೀವು ನೆನಪಿನಲ್ಲಿಟ್ಟುಕೊಂಡು ನಿಮ್ಮ ಸಂತತಿಯವರಿಗೆ ತಿಳಿಸಬೇಕು.",
			"15": "<sup>15</sup> ಹೋರೇಬಿನಲ್ಲಿ ಯೆಹೋವನು ಬೆಂಕಿಯ ಜ್ವಾಲೆಯೊಳಗಿಂದ ನಿಮ್ಮ ಸಂಗಡ ಮಾತಾಡಿದಾಗ ನಿಮಗೆ ಯಾವ ಆಕಾರವೂ ಕಾಣಿಸಲಿಲ್ಲವಷ್ಟೆ. ಆದದರಿಂದ ನೀವು ನಿಮ್ಮ ನಿಮ್ಮ ಮನಸ್ಸುಗಳನ್ನು ಬಹು ಜಾಗರೂಕತೆಯಿಂದ ಕಾಪಾಡಿಕೊಳ್ಳಬೇಕು.",
			"16": "<sup>16</sup> ನೀವು ಸ್ತ್ರೀ ಪುರುಷರ ರೂಪವನ್ನಾಗಲಿ",
			"17": "<sup>17</sup> ಭೂಮಿಯ ಮೇಲೆ ಸಂಚರಿಸುವ ಮೃಗದ ರೂಪವನ್ನಾಗಲಿ ಆಕಾಶದಲ್ಲಿ ಹಾರಾಡುವ ಪಕ್ಷಿಯ ರೂಪವನ್ನಾಗಲಿ",
			"18": "<sup>18</sup> ನೆಲದ ಮೇಲೆ ಹರಿದಾಡುವ ಕ್ರಿಮಿಕೀಟಗಳ ರೂಪವನ್ನಾಗಲಿ ಭೂಮಿಯ ಕೆಳಗಣ ನೀರಿನಲ್ಲಿರುವ ಮೀನಿನ ರೂಪವನ್ನಾಗಲಿ ದೇವರ ಸ್ವರೂಪವೆಂದು ಮಾಡಿಕೊಂಡು ಭ್ರಷ್ಟರಾಗಬಾರದು.",
			"19": "<sup>19</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಭೂಲೋಕದಲ್ಲಿರುವ ಬೇರೆ ಎಲ್ಲಾ ಜನಾಂಗಗಳಿಗೋಸ್ಕರ ದಯಪಾಲಿಸಿದ ಸೂರ್ಯಚಂದ್ರ ನಕ್ಷತ್ರಗಳೆನಿಸಿಕೊಳ್ಳುವ ಆಕಾಶ ಸೈನ್ಯವನ್ನು ನೀವು ಆಕಾಶದ ಕಡೆಗೆ ಕಣ್ಣೆತ್ತಿ ನೋಡುವಾಗ, ಮರುಳುಗೊಂಡು ಅವುಗಳನ್ನು ಪೂಜಿಸಲೂಬಾರದು ನಮಸ್ಕರಿಸಲೂಬಾರದು.",
			"20": "<sup>20</sup> ನಿಮ್ಮನ್ನಾದರೋ ಯೆಹೋವನು ತನಗೆ ಸ್ವಕೀಯಜನರನ್ನಾಗಿ ಮಾಡಿಕೊಳ್ಳಬೇಕೆಂದು ಸಂಕಲ್ಪಿಸಿ ಕಬ್ಬಿಣವನ್ನು ಕರಗಿಸುವ ಕುಲುಮೆಯೋಪಾದಿಯಲ್ಲಿದ್ದ ಐಗುಪ್ತದೇಶದಿಂದ ತಪ್ಪಿಸಿದನು; ಆ ಸಂಕಲ್ಪವು ಈಗ ನೆರವೇರಿತಲ್ಲಾ.",
			"21": "<sup>21</sup> ಅದಲ್ಲದೆ ಯೆಹೋವನು ನಿಮ್ಮ ದೆಸೆಯಿಂದ ನನ್ನ ಮೇಲೆಯೂ ಕೋಪಗೊಂಡು ನಾನು ಯೊರ್ದನ್ ಹೊಳೆಯನ್ನು ದಾಟಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಸ್ವದೇಶವಾಗುವದಕ್ಕೆ ಕೊಡುವ ಆ ಒಳ್ಳೆಯ ದೇಶವನ್ನು ಸೇರದೆ",
			"22": "<sup>22</sup> ಇಲ್ಲಿಯೇ ಸಾಯಬೇಕೆಂದು ಖಂಡಿತವಾಗಿ ಹೇಳಿದನು. ನೀವಾದರೋ ಹೊಳೆ ದಾಟಿ ಆ ಒಳ್ಳೇ ದೇಶವನ್ನು ಸೇರಿ ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವಿರಿ.",
			"23": "<sup>23</sup> ಎಚ್ಚರಿಕೆಯಾಗಿರಿ. ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮ ಸಂಗಡ ಮಾಡಿದ ನಿಬಂಧನೆಯನ್ನು ನೀವು ಮರೆತು ಪೂಜಿಸಬೇಡಿರೆಂದು ಆತನು ನಿಷೇಧಿಸಿದ ಯಾವ ವಸ್ತುವಿನ ವಿಗ್ರಹವನ್ನೂ ಮಾಡಿಕೊಳ್ಳಬಾರದು.",
			"24": "<sup>24</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ತನಗೆ ಸಲ್ಲಬೇಕಾದ ಗೌರವವನ್ನು ಮತ್ತೊಬ್ಬನಿಗೆ ಸಲ್ಲಗೊಡಿಸದೆ ಉರಿಯುವ ಬೆಂಕಿಯಂತೆ [ದ್ರೋಹಿಗಳನ್ನು] ದಹಿಸಿ ಬಿಡುವವನಾಗಿದ್ದಾನೆಂದು ತಿಳಿಯಿರಿ.",
			"25": "<sup>25</sup> ನೀವು ಮಕ್ಕಳನ್ನೂ ಮೊಮ್ಮಕ್ಕಳನ್ನೂ ಪಡೆದು ಬಹುಕಾಲ ಆ ದೇಶದಲ್ಲಿದ್ದ ಮೇಲೆ ದ್ರೋಹಿಗಳಾಗಿ ಯಾವ ವಿಗ್ರಹವನ್ನಾದರೂ ಮಾಡಿಕೊಂಡು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ದೃಷ್ಟಿಗೆ ಕೆಟ್ಟದ್ದನ್ನು ನಡಿಸಿ",
			"26": "<sup>26</sup> ಆತನನ್ನು ಕೋಪಗೊಳಿಸಿದರೆ ನೀವು ಯೊರ್ದನ್ ಹೊಳೆಯನ್ನು ದಾಟಿ ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವ ಆ ದೇಶದಲ್ಲಿ ಉಳಿಯದೆ ಬೇಗನೆ ನಾಶವಾಗಿ ಹೋಗುವಿರಿ ಎಂದು ಭೂಮ್ಯಾಕಾಶಗಳನ್ನು ಸಾಕ್ಷಿಗಳಾಗಿ ಕರೆದು ಎಚ್ಚರಿಸುತ್ತೇನೆ. ನೀವು ಆ ದೇಶದಲ್ಲಿ ಬಹುದಿವಸ ಇರದೆ ನಾಶವಾಗಿಯೇ ಹೋಗುವಿರಿ.",
			"27": "<sup>27</sup> ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಅನ್ಯಜನಗಳಲ್ಲಿ ಚದರಿಸುವನು; ಆತನು ನಿಮ್ಮನ್ನು ಓಡಿಸುವ ದೇಶಗಳ ಜನರ ಮಧ್ಯದಲ್ಲಿ ನೀವು ಸ್ವಲ್ಪ ಮಂದಿ ಮಾತ್ರ ಉಳಿಯುವಿರಿ.",
			"28": "<sup>28</sup> ನೀವು ಆ ದೇಶಗಳಲ್ಲಿ ಮನುಷ್ಯರು ಮಾಡಿಕೊಂಡ ದೇವರುಗಳನ್ನು ಅಂದರೆ ಮರದಲ್ಲಾಗಲಿ ಕಲ್ಲಿನಲ್ಲಾಗಲಿ ಕೆತ್ತಲ್ಪಟ್ಟು ನೋಡಲಾರದೆ ಕೇಳಲಾರದೆ ತಿನ್ನಲಾರದೆ ವಾಸನೆ ತಿಳುಕೊಳ್ಳಲಾರದೆ ಇರುವ ದೇವರುಗಳನ್ನು ಪೂಜಿಸುವವರಾಗುವಿರಿ.",
			"29": "<sup>29</sup> ಅಲ್ಲಿಂದಲಾದರೂ ನೀವು ಸಂಪೂರ್ಣ ಹೃದಯದಿಂದಲೂ ಮನಸ್ಸಿನಿಂದಲೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಹುಡುಕಿದರೆ ಆತನು ನಿಮಗೆ ಸಿಕ್ಕುವನು.",
			"30": "<sup>30</sup> ಮೇಲೆ ಹೇಳಿದ ಎಲ್ಲಾ ಕಷ್ಟಗಳು ನಿಮಗೆ ಸಂಭವಿಸಿ ನೀವು ಸಂಕಟದಲ್ಲಿರುವಾಗ ಅಂತ್ಯದಲ್ಲಿ ನೀವು ಆತನ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡು ಆತನ ಮಾತಿಗೆ ಕಿವಿಗೊಡುವಿರಿ.",
			"31": "<sup>31</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಕನಿಕರವುಳ್ಳ ದೇವರಾದದರಿಂದ ಆತನು ನಿಮ್ಮನ್ನು ಅಲಕ್ಷ್ಯಮಾಡುವದಿಲ್ಲ, ನಾಶನಕ್ಕೆ ಬಿಡುವದಿಲ್ಲ; ತಾನು ನಿಮ್ಮ ಪಿತೃಗಳ ಸಂಗಡ ಪ್ರಮಾಣಪೂರ್ವಕವಾಗಿ ಮಾಡಿಕೊಂಡ ಒಡಂಬಡಿಕೆಯನ್ನು ಮರೆಯುವದಿಲ್ಲ.",
			"32": "<sup>32</sup> ದೇವರು ಮನುಷ್ಯರನ್ನು ಸೃಷ್ಟಿಸಿ ಭೂಮಿಯ ಮೇಲೆ ಇರಿಸಿದ ದಿನ ಮೊದಲುಗೊಂಡು ಇಂದಿನವರೆಗೂ ಒಂದು ದಿಕ್ಕಿನಿಂದ ಮತ್ತೊಂದು ದಿಕ್ಕಿನವರೆಗೂ ಅಂಥ ದೊಡ್ಡ ಕಾರ್ಯವು ನಡೆದದ್ದುಂಟೋ, ಇಲ್ಲವೆ ಅಂಥದರ ಸುದ್ದಿ ಕೇಳಿದ್ದುಂಟೋ ಎಂದು ವಿಚಾರಿಸಿಕೊಳ್ಳಿರಿ.",
			"33": "<sup>33</sup> ದೇವರು ಬೆಂಕಿಯ ಜ್ವಾಲೆಯೊಳಗಿಂದ ಮಾತಾಡಿದ ಸ್ವರವು ನಿಮಗೆ ಕೇಳಿಸಿತಲ್ಲಾ; ಬೇರೆ ಯಾವ ಜನರಾದರೂ ದೇವರ ಸ್ವರವನ್ನು ಕೇಳಿ ಬದುಕಿದ್ದುಂಟೋ?",
			"34": "<sup>34</sup> ಬೇರೆ ಯಾವ ದೇವರಾದರೂ ಮನಶ್ಶೋಧನೆ, ಮಹತ್ಕಾರ್ಯ, ಉತ್ಪಾತ, ಯುದ್ಧ, ಭುಜಪರಾಕ್ರಮ, ಶಿಕ್ಷಾಹಸ್ತ, ಮಹಾಭೀತಿ ಇವುಗಳನ್ನು ಪ್ರಯೋಗಿಸಿ ಒಂದು ಜನಾಂಗವನ್ನು ಮತ್ತೊಂದು ಜನಾಂಗದ ಕೈಯೊಳಗಿಂದ ತಪ್ಪಿಸುವದಕ್ಕೆ ಪ್ರಯತ್ನಿಸಿದ್ದುಂಟೋ? ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಾದರೋ ಐಗುಪ್ತದೇಶದಲ್ಲಿ ನಿಮಗೋಸ್ಕರ ಇದನ್ನೆಲ್ಲಾ ನಿಮ್ಮ ಕಣ್ಣಿಗೆದುರಾಗಿ ನಡಿಸಿದನಲ್ಲಾ.",
			"35": "<sup>35</sup> ಯೆಹೋವನೊಬ್ಬನೇ ದೇವರು, ಬೇರೆ ದೇವರೇ ಇಲ್ಲವೆಂದು ನೀವು ತಿಳಿದುಕೊಳ್ಳುವದಕ್ಕಾಗಿ ಇದೆಲ್ಲಾ ನಿಮಗೆ ಮಾತ್ರ ತೋರಿಸಲ್ಪಟ್ಟಿತು.",
			"36": "<sup>36</sup> ಆತನು ನಿಮ್ಮ ಶಿಕ್ಷಣಕ್ಕೋಸ್ಕರ ಆಕಾಶದಿಂದ ತನ್ನ ಸ್ವರವನ್ನು ನಿಮಗೆ ಕೇಳಿಸುವಂತೆ ಮಾಡಿದನು; ಮತ್ತು ಭೂಮಿಯ ಮೇಲೆ ತಾನಿದ್ದ ಮಹಾ ಅಗ್ನಿಜ್ವಾಲೆಯನ್ನು ನೋಡಿಸಿದನು; ಅದಲ್ಲದೆ ಆ ಅಗ್ನಿಜ್ವಾಲೆಯೊಳಗಿಂದ ಆತನು ಆಡಿದ ಮಾತುಗಳನ್ನು ಕೇಳಿಸುವಂತೆ ಮಾಡಿದನು.",
			"37": "<sup>37</sup> ಆತನು ನಿಮ್ಮ ಪಿತೃಗಳನ್ನು ಪ್ರೀತಿಸಿ ಅವರ ತರುವಾಯ ಅವರ ಸಂತತಿಯವರಾದ ನಿಮ್ಮನ್ನೂ ಆದುಕೊಂಡು",
			"38": "<sup>38</sup> ಈಗ ನಿಮ್ಮ ಅನುಭವಕ್ಕೆ ಬಂದ ಪ್ರಕಾರ ನಿಮಗಿಂತ ಮಹಾಬಲಿಷ್ಠವಾದ ಜನಾಂಗಗಳನ್ನು ಹೊರಡಿಸಲೂ ಅವರ ದೇಶದಲ್ಲಿ ನಿಮ್ಮನ್ನು ಸೇರಿಸಿ ಆ ದೇಶವನ್ನು ನಿಮಗೇ ಸ್ವದೇಶವಾಗಿ ಕೊಡಲೂಬೇಕೆಂದು ಸಂಕಲ್ಪಿಸಿ ತಾನೇ ನಿಮ್ಮೊಂದಿಗಿದ್ದು ತನ್ನ ಅಪಾರ ಸಾಮರ್ಥ್ಯದಿಂದ ನಿಮ್ಮನ್ನು ಐಗುಪ್ತದೇಶದೊಳಗಿಂದ ಬರಮಾಡಿದನಲ್ಲಾ.",
			"39": "<sup>39</sup> ನೀವು ಇದನ್ನೆಲ್ಲಾ ಆಲೋಚಿಸಿ ಆಕಾಶದಲ್ಲಿಯೂ ಭೂಮಿಯಲ್ಲಿಯೂ ಯೆಹೋವನೊಬ್ಬನೇ ದೇವರು, ಬೇರೆ ಯಾವ ದೇವರೂ ಇಲ್ಲವೆಂಬದನ್ನು ಈಗ ಗ್ರಹಿಸಿಕೊಂಡು ಜ್ಞಾಪಕದಲ್ಲಿಡಬೇಕು.",
			"40": "<sup>40</sup> ನಿಮಗೂ ನಿಮ್ಮ ಸಂತತಿಯವರಿಗೂ ಶುಭವುಂಟಾಗುವಂತೆಯೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಕೊಡುವ ದೇಶದಲ್ಲಿ ನೀವು ಸದಾಕಾಲ ಬಾಳುವಂತೆಯೂ ನಾನು ನಿಮಗೆ ಈಗ ತಿಳಿಸಿದ ಆತನ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ನೀವು ಅನುಸರಿಸಿ ನಡೆಯಬೇಕು.",
			"41": "<sup>41</sup> ಆ ಕಾಲದಲ್ಲಿ ಮೋಶೆ ಯೊರ್ದನ್ ಹೊಳೆಯ ಪೂರ್ವದಿಕ್ಕಿನಲ್ಲಿ ಮೂರು ಆಶ್ರಯನಗರಗಳನ್ನು ಗೊತ್ತುಮಾಡಿದನು.",
			"42": "<sup>42</sup> ಮೊದಲು ಯಾವ ದ್ವೇಷವೂ ಇಲ್ಲದೆ ಕೈತಪ್ಪಿ ಮತ್ತೊಬ್ಬನನ್ನು ಕೊಂದವನು ಆ ಪಟ್ಟಣಗಳಲ್ಲಿ ಒಂದಕ್ಕೆ ಓಡಿಹೋಗಿ ಬದುಕಿಕೊಳ್ಳಲಿ ಎಂದು ಆ ಪಟ್ಟಣಗಳನ್ನು ನೇಮಿಸಿದನು.",
			"43": "<sup>43</sup> ಅವು ಯಾವವಂದರೆ - ರೂಬೇನ್ಯರಿಗೋಸ್ಕರ ಮೀಶೋರ್ ಎಂಬ ಎತ್ತರವಾದ ಬೈಲುಸೀಮೆಯ ಅರಣ್ಯದಲ್ಲಿರುವ ಬೆಚೆರ್; ಗಾದ್ಯರಿಗೋಸ್ಕರ ಗಿಲ್ಯಾದಿನಲ್ಲಿರುವ ರಾಮೋತ್; ಮನಸ್ಸೆಯವರಿಗೋಸ್ಕರ ಬಾಷಾನಿನಲ್ಲಿರುವ ಗೋಲಾನ್ ಇವುಗಳೇ.",
			"44": "<sup>44</sup> ಮೋಶೆ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಕೊಟ್ಟ ಧರ್ಮಶಾಸ್ತ್ರವು ಇದೇ.",
			"45": "<sup>45</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಐಗುಪ್ತದೇಶದಿಂದ ಬಂದಾಗ ಯೊರ್ದನ್ ಹೊಳೆಯ ಆಚೆ ಬೇತ್‍ಪೆಗೋರಿಗೆ ಎದುರಾಗಿರುವ ಕಣಿವೆಯಲ್ಲಿ ಹೆಷ್ಬೋನಿನಲ್ಲಿ ವಾಸಿಸಿದ ಅಮೋರಿಯರ ಅರಸನಾದ ಸೀಹೋನನ ದೇಶದಲ್ಲಿ ಮೋಶೆ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ತಿಳಿಸಿದ ಆಜ್ಞಾವಿಧಿನಿರ್ಣಯಗಳು ಇವೇ. (ಮೋಶೆಯೂ ಇಸ್ರಾಯೇಲ್ಯರೂ ಐಗುಪ್ತದೇಶದಿಂದ ಬಂದಾಗ ಆ ಸೀಹೋನನನ್ನು ಜಯಿಸಿದ್ದರು.",
			"47": "<sup>47</sup> ಅವನ ರಾಜ್ಯವನ್ನೂ ಬಾಷಾನ್ ದೇಶದ ಅರಸನಾದ ಓಗನ ರಾಜ್ಯವನ್ನೂ ಅಂತು ಅರ್ನೋನ್ ತಗ್ಗಿನ ಅಂಚಿನಲ್ಲಿರುವ ಅರೋಯೇರ್ ಪಟ್ಟಣ ಮೊದಲುಗೊಂಡು ಹೆರ್ಮೋನ್ ಎಂಬ ಸೀಯೋನ್ ಪರ್ವತದವರೆಗೂ ಯೊರ್ದನ್ ಹೊಳೆಯ ಆಚೆ ಪೂರ್ವದಿಕ್ಕಿನಲ್ಲಿದ್ದ ಅಮೋರಿಯರ ಇಬ್ಬರು ಅರಸರ ರಾಜ್ಯಗಳನ್ನು ಮತ್ತು",
			"49": "<sup>49</sup> ಅರಾಬಾ ಎಂಬ ತಗ್ಗಿನಲ್ಲಿ ಪಿಸ್ಗಾ ಬೆಟ್ಟದ ಬುಡದಲ್ಲಿರುವ ಅರಾಬದ ಸಮುದ್ರದವರೆಗೆ ಯೊರ್ದನ್ ಹೊಳೆಯ ಆಚೆ ಇರುವ ಭಾಗವನ್ನೆಲ್ಲಾ ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡಿದ್ದರು.)"
		},
		"5": {
			"1": "<sup>1</sup> ಮೋಶೆ ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರನ್ನು ಕರೆದು ಹೀಗಂದನು - ಇಸ್ರಾಯೇಲ್ಯರೇ, ನಾನು ಈಗ ನಿಮಗೆ ತಿಳಿಸುವ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ಕೇಳಿರಿ; ಇವುಗಳನ್ನು ಗ್ರಹಿಸಿಕೊಂಡು ಅನುಸರಿಸಿ ನಡೆಯಬೇಕು.",
			"2": "<sup>2</sup> ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಹೋರೇಬಿನಲ್ಲಿ ನಮ್ಮ ಸಂಗಡ ನಿಬಂಧನೆಯನ್ನು ಮಾಡಿದನು.",
			"3": "<sup>3</sup> ಆತನು ನಮ್ಮ ಪೂರ್ವಿಕರ ಸಂಗಡ ಈ ನಿಬಂಧನೆಯನ್ನು ಮಾಡದೆ ಈಗ ಇಲ್ಲಿ ಜೀವದಿಂದಿರುವ ನಮ್ಮ ಸಂಗಡಲೇ ಇದನ್ನು ಮಾಡಿದನು.",
			"4": "<sup>4</sup> ಯೆಹೋವನು ಆ ಬೆಟ್ಟದ ಮೇಲೆ ಬೆಂಕಿಯ ಜ್ವಾಲೆಯೊಳಗಿಂದ ಮುಖಾಮುಖಿಯಾಗಿ ನಿಮ್ಮ ಸಂಗಡ ಮಾತಾಡಲಾಗಿ",
			"5": "<sup>5</sup> ನೀವು ಆ ಬೆಂಕಿಯ ಜ್ವಾಲೆಯ ದೆಸೆಯಿಂದ ಭಯಪಟ್ಟು ಬೆಟ್ಟವನ್ನು ಹತ್ತದೆಹೋಗಲಾಗಿ ನಾನು ಯೆಹೋವನಿಗೂ ನಿಮಗೂ ನಡುವೆ ನಿಂತು ಆತನು ಆಡಿದ ಮಾತುಗಳನ್ನು ನಿಮಗೆ ತಿಳಿಸಿದೆನು. ಆತನು ಹೇಳಿದ್ದೇನಂದರೆ -",
			"6": "<sup>6</sup> ನೀನು ದಾಸತ್ವದಲ್ಲಿದ್ದ ಐಗುಪ್ತದೇಶದೊಳಗಿಂದ ನಿನ್ನನ್ನು ಬಿಡುಗಡೆ ಮಾಡಿದ ಯೆಹೋವನೆಂಬ ನಿನ್ನ ದೇವರು ನಾನೇ.",
			"7": "<sup>7</sup> ನಾನಲ್ಲದೆ ನಿನಗೆ ಬೇರೆ ದೇವರುಗಳು ಇರಬಾರದು.",
			"8": "<sup>8</sup> ಯಾವ ಮೂರ್ತಿಯನ್ನೂ ಮಾಡಿಕೊಳ್ಳಬಾರದು. ಆಕಾಶದಲ್ಲಾಗಲಿ ಭೂಮಿಯಲ್ಲಾಗಲಿ ಭೂಮಿಯ ಕೆಳಗಣ ನೀರಿನಲ್ಲಾಗಲಿ ಇರುವ ಯಾವದರ ರೂಪವನ್ನೂ ಮಾಡಿಕೊಳ್ಳಬಾರದು.",
			"9": "<sup>9</sup> ಅವುಗಳಿಗೆ ಅಡ್ಡ ಬೀಳಲೂಬಾರದು, ಪೂಜೆಮಾಡಲೂಬಾರದು. ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನೆಂಬ ನಾನು ನನಗೆ ಸಲ್ಲತಕ್ಕ ಗೌರವವನ್ನು ಮತ್ತೊಬ್ಬನಿಗೆ ಸಲ್ಲಗೊಡಿಸದವನಾದದರಿಂದ ನನ್ನನ್ನು ದ್ವೇಷಿಸುವವರ ವಿಷಯದಲ್ಲಿ ತಂದೆಗಳ ದೋಷಫಲವನ್ನು ಮಕ್ಕಳ ಮೇಲೆ ಮೂರು ನಾಲ್ಕು ತಲೆಗಳವರೆಗೆ ಬರಮಾಡುವವನಾಗಿಯೂ",
			"10": "<sup>10</sup> ನನ್ನನ್ನು ಪ್ರೀತಿಸಿ ನನ್ನ ಆಜ್ಞೆಗಳನ್ನು ಕೈಕೊಳ್ಳುವವರಿಗೋ ಸಾವಿರ ತಲೆಗಳವರೆಗೆ ದಯೆತೋರಿಸುವವನಾಗಿಯೂ ಇದ್ದೇನೆ.",
			"11": "<sup>11</sup> ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನ ಹೆಸರನ್ನು ಅಯೋಗ್ಯ ಕಾರ್ಯಕ್ಕಾಗಿ ಎತ್ತಬಾರದು. ಯೆಹೋವನು ತನ್ನ ಹೆಸರನ್ನು ಅಯೋಗ್ಯ ಕಾರ್ಯಕ್ಕಾಗಿ ಎತ್ತುವವನನ್ನು ಶಿಕ್ಷಿಸದೆ ಬಿಡುವದಿಲ್ಲ.",
			"12": "<sup>12</sup> ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನು ಆಜ್ಞಾಪಿಸಿದಂತೆ ನೀನು ಸಬ್ಬತ್ ದಿನವನ್ನು ದೇವರ ದಿನವೆಂದು ಆಚರಿಸಬೇಕು.",
			"13": "<sup>13</sup> ಆರು ದಿನಗಳಲ್ಲಿ ನೀನು ದುಡಿದು ನಿನ್ನ ಕೆಲಸವನ್ನೆಲ್ಲಾ ಮಾಡಬೇಕು.",
			"14": "<sup>14</sup> ಏಳನೆಯ ದಿನವು ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಮೀಸಲಾದ ವಿಶ್ರಾಂತಿದಿನವಾಗಿದೆ; ಅದರಲ್ಲಿ ನೀನು ಯಾವ ಕೆಲಸವನ್ನೂ ಮಾಡಬಾರದು. ನಿನ್ನ ಗಂಡುಮಕ್ಕಳು ಹೆಣ್ಣುಮಕ್ಕಳು ಗಂಡಾಳು ಹೆಣ್ಣಾಳು ಎತ್ತು ಕತ್ತೆ ಪಶುಗಳು ನಿನ್ನ ಊರಲ್ಲಿರುವ ಅನ್ಯದೇಶದವರು ಯಾವ ಕೆಲಸವನ್ನೂ ಮಾಡಬಾರದು. ನಿನ್ನಂತೆಯೇ ನಿನ್ನ ಗಂಡಾಳು ಹೆಣ್ಣಾಳುಗಳಿಗೂ ವಿಶ್ರಾಂತಿದೊರೆಯಬೇಕು.",
			"15": "<sup>15</sup> ಐಗುಪ್ತದೇಶದಲ್ಲಿ ನೀವೂ ದಾಸತ್ವದಲ್ಲಿದ್ದದ್ದನ್ನೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಭುಜಪರಾಕ್ರಮದಿಂದಲೂ ಶಿಕ್ಷಾಹಸ್ತದಿಂದಲೂ ನಿಮ್ಮನ್ನು ಬಿಡುಗಡೆಮಾಡಿದ್ದನ್ನೂ ಜ್ಞಾಪಕಮಾಡಿಕೊಳ್ಳಿರಿ. ನೀವು ಸಬ್ಬತ್ ದಿನವನ್ನು ಆಚರಿಸಬೇಕೆಂಬದನ್ನು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಇದಕ್ಕಾಗಿಯೇ ಆಜ್ಞಾಪಿಸಿದ್ದಾನೆ.",
			"16": "<sup>16</sup> ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನು ಆಜ್ಞಾಪಿಸಿದಂತೆ ನಿನ್ನ ತಂದೆ ತಾಯಿಗಳನ್ನು ಸನ್ಮಾನಿಸಬೇಕು; ಸನ್ಮಾನಿಸಿದರೆ ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನು ನಿನಗೆ ಅನುಗ್ರಹಿಸುವ ದೇಶದಲ್ಲಿ ನೀನು ಬಹುಕಾಲ ಇರುವಿ, ಮತ್ತು ನಿನಗೆ ಮೇಲಾಗುವದು.",
			"17": "<sup>17</sup> ನರಹತ್ಯಮಾಡಬಾರದು.",
			"18": "<sup>18</sup> ವ್ಯಭಿಚಾರಮಾಡಬಾರದು.",
			"19": "<sup>19</sup> ಕದಿಯಬಾರದು.",
			"20": "<sup>20</sup> ಮತ್ತೊಬ್ಬನ ಮೇಲೆ ಸುಳ್ಳುಸಾಕ್ಷಿ ಹೇಳಬಾರದು.",
			"21": "<sup>21</sup> ಮತ್ತೊಬ್ಬನ ಹೆಂಡತಿಯನ್ನು ಅಪೇಕ್ಷಿಸಬಾರದು; ಮತ್ತೊಬ್ಬನ ಹೊಲ ಮನೆ ಗಂಡಾಳು ಹೆಣ್ಣಾಳು ಎತ್ತು ಕತ್ತೆ ಮುಂತಾದ ಯಾವದನ್ನೂ ಆಶಿಸಬಾರದು.",
			"22": "<sup>22</sup> ಈ ಮಾತುಗಳನ್ನು ಯೆಹೋವನು ಆ ಬೆಟ್ಟದ ಮೇಲೆ ಬೆಂಕಿ, ಮೋಡ, ಕಾರ್ಗತ್ತಲು ಇವುಗಳ ಮಧ್ಯದೊಳಗಿಂದ ನಿಮ್ಮ ಸರ್ವಸಮೂಹಕ್ಕೆ ಕೇಳಿಸುವಂತೆ ಮಹಾ ಸ್ವರದಿಂದ ನುಡಿದನು; ಆಮೇಲೆ ಇನ್ನು ಮಾತಾಡಲಿಲ್ಲ. ಆತನು ಆ ಮಾತುಗಳನ್ನು ಎರಡು ಕಲ್ಲಿನ ಹಲಗೆಗಳಲ್ಲಿ ಕೆತ್ತಿ ನನಗೆ ಕೊಟ್ಟನು.",
			"23": "<sup>23</sup> ಆ ಬೆಟ್ಟವು ಬೆಂಕಿಯಿಂದ ಪ್ರಜ್ವಲಿಸುತ್ತಿರುವಾಗ ಆ ಕತ್ತಲೊಳಗಿಂದ ದೇವರ ಸ್ವರವನ್ನು ಕೇಳಿ ನೀವು ಅಂದರೆ ನಿಮ್ಮ ಕುಲಾಧಿಪತಿಗಳೂ ಹಿರಿಯರೂ ನನ್ನ ಬಳಿಗೆ ಬಂದು -",
			"24": "<sup>24</sup> ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ತನ್ನ ಘನವನ್ನೂ ಮಹಿಮೆಯನ್ನೂ ನಮಗೆ ತೋರಿಸಿ ಅಗ್ನಿಜ್ವಾಲೆಯೊಳಗಿಂದ ತನ್ನ ಸ್ವರವನ್ನು ನಮಗೆ ಕೇಳಿಸಿದನಲ್ಲಾ. ಮನುಷ್ಯನು ತನ್ನ ಸಂಗಡ ದೇವರು ಮಾತಾಡಿದರೂ ಸಾಯದೆ ಬದುಕಿದ್ದುಂಟೆಂಬದು ಈ ಹೊತ್ತು ನಮಗೆ ತಿಳಿಯಬಂತು.",
			"25": "<sup>25</sup> ಆದರೂ ಈ ಘೋರವಾದ ಅಗ್ನಿಜ್ವಾಲೆಯು ನಮ್ಮನ್ನು ದಹಿಸಿಬಿಟ್ಟೀತು, ಮತ್ತು ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಸ್ವರವನ್ನು ನಾವು ತಿರಿಗಿ ಕೇಳಿದರೆ ಸತ್ತೇವು.",
			"26": "<sup>26</sup> ಬೆಂಕಿಯ ಜ್ವಾಲೆಯೊಳಗಿಂದ ಮಾತಾಡುವ ಚೈತನ್ಯಸ್ವರೂಪನಾದ ದೇವರ ಸ್ವರವನ್ನು ನಾವು ಕೇಳಿದಂತೆ ಎಲ್ಲಾ ಮನುಷ್ಯರೊಳಗೆ ಬೇರೆ ಯಾರು ಕೇಳಿ ಬದುಕಿದರು? ಹೀಗಿರುವದರಿಂದ ನಾವು ಯಾಕೆ ಸಾಯಬೇಕು?",
			"27": "<sup>27</sup> ನೀನೇ ಹತ್ತಿರಕ್ಕೆ ಹೋಗಿ ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಆಜ್ಞೆಗಳನ್ನೆಲ್ಲಾ ತಿಳಿದುಕೊಂಡು ಬಂದು ನಮಗೆ ತಿಳಿಸು; ತಿಳಿಸಿದಾಗ ನಾವು ನಡಿಸುವೆವು ಎಂದು ಹೇಳಿದಿರಿ.",
			"28": "<sup>28</sup> ನಿಮ್ಮ ಮಾತುಗಳನ್ನು ಯೆಹೋವನು ಕೇಳಿ ನನಗೆ - ಈ ಜನರು ನಿನಗೆ ಹೇಳಿದ ಮಾತುಗಳನ್ನು ಕೇಳಿದ್ದೇನೆ; ಆ ಮಾತುಗಳೆಲ್ಲಾ ಒಳ್ಳೆಯವೇ.",
			"29": "<sup>29</sup> ನನ್ನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿ ನನ್ನ ಆಜ್ಞೆಗಳನ್ನು ಅನುಸರಿಸುವ ಮನಸ್ಸು ಯಾವಾಗಲೂ ಅವರಲ್ಲಿ ಇದ್ದರೆ ಎಷ್ಟೋ ಮೇಲು; ಆಗ ಅವರಿಗೂ ಅವರ ಸಂತತಿಯವರಿಗೂ ಯಾವಾಗಲೂ ಶುಭವುಂಟಾಗುವದು.",
			"30": "<sup>30</sup> ನಿಮ್ಮ ನಿಮ್ಮ ಡೇರೆಗಳಿಗೆ ತಿರಿಗಿ ಹೋಗಿರಿ ಎಂದು ಅವರಿಗೆ ಆಜ್ಞಾಪಿಸು.",
			"31": "<sup>31</sup> ನೀನಾದರೋ ಇಲ್ಲೇ ನನ್ನ ಹತ್ತಿರ ನಿಂತಿರು; ನೀನು ಅವರಿಗೆ ಬೋಧಿಸಬೇಕಾದ ಎಲ್ಲಾ ಧರ್ಮೋಪದೇಶವನ್ನೂ ವಿಧಿನಿರ್ಣಯಗಳನ್ನೂ ನಿನಗೆ ತಿಳಿಸುತ್ತೇನೆ. ನಾನು ಅವರಿಗೆ ಸ್ವಂತಕ್ಕಾಗಿ ಕೊಡುವ ದೇಶದಲ್ಲಿ ಅವರು ಇವುಗಳನ್ನು ಅನುಸರಿಸಬೇಕು ಎಂದು ಹೇಳಿದನು.",
			"32": "<sup>32</sup> ಆದದರಿಂದ ನೀವು ಸ್ವಲ್ಪವಾದರೂ ತಪ್ಪದೆ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಆಜ್ಞಾಪಿಸಿದಂತೆಯೇ ನಡೆದುಕೊಳ್ಳಬೇಕು.",
			"33": "<sup>33</sup> ನೀವು ಸ್ವತಂತ್ರಿಸಿಕೊಳ್ಳುವ ದೇಶದಲ್ಲಿ ಬಹುಕಾಲ ಶುಭವುಳ್ಳವರಾಗಿ ಬದುಕಿಕೊಳ್ಳುವಂತೆ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಆಜ್ಞಾಪಿಸಿದ ಮಾರ್ಗದಲ್ಲೇ ನಡೆಯಬೇಕು."
		},
		"6": {
			"1": "<sup>1</sup> ನೀವು ಹೊಳೆ ದಾಟಿ ಸ್ವತಂತ್ರಿಸಿಕೊಳ್ಳುವ ದೇಶದಲ್ಲಿ ಅನುಸರಿಸುವದಕ್ಕಾಗಿ ನಿಮಗೆ ಬೋಧಿಸಬೇಕೆಂದು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನನಗೆ ಆಜ್ಞಾಪಿಸಿದ ಧರ್ಮೋಪದೇಶವೂ ವಿಧಿನಿರ್ಣಯಗಳೂ ಇವೇ.",
			"2": "<sup>2</sup> ನೀವೂ ನಿಮ್ಮ ಪುತ್ರಪೌತ್ರಾದಿಸಂತತಿಯವರೂ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿದ್ದುಕೊಂಡು ನಾನು ಈಗ ಬೋಧಿಸುವ ಆತನ ವಿಧಿನಿರ್ಣಯಗಳನ್ನೆಲ್ಲಾ ಅನುಸರಿಸಬೇಕೆಂದೂ ನೀವು ಬಹುಕಾಲ ಬಾಳಬೇಕೆಂದೂ ಇವುಗಳನ್ನು ಆಜ್ಞಾಪಿಸಿದ್ದಾನೆ.",
			"3": "<sup>3</sup> ಆದದರಿಂದ ಇಸ್ರಾಯೇಲ್ಯರೇ, ನಿಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಯೆಹೋವನು ವಾಗ್ದಾನಮಾಡಿದ ಪ್ರಕಾರ ಹಾಲೂ ಜೇನೂ ಹರಿಯುವ ಆ ದೇಶದಲ್ಲಿ ನಿಮಗೆ ಶುಭವುಂಟಾಗುವದಕ್ಕೂ ನೀವು ಬಹಳವಾಗಿ ಹೆಚ್ಚುವದಕ್ಕೂ ಈ ಆಜ್ಞೆಗಳನ್ನು ಲಕ್ಷ್ಯವಿಟ್ಟು ಕೇಳಿ ಅನುಸರಿಸಲೇಬೇಕು.",
			"4": "<sup>4</sup> ಇಸ್ರಾಯೇಲ್ಯರೇ, ಕೇಳಿರಿ; ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಒಬ್ಬನೇ ದೇವರು;",
			"5": "<sup>5</sup> ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಪೂರ್ಣಹೃದಯದಿಂದಲೂ ಪೂರ್ಣಪ್ರಾಣದಿಂದಲೂ ಪೂರ್ಣಶಕ್ತಿಯಿಂದಲೂ ಪ್ರೀತಿಸಬೇಕು.",
			"6": "<sup>6</sup> ನಾನು ಈಗ ನಿಮಗೆ ತಿಳಿಸುವ ಮಾತುಗಳು ನಿಮ್ಮ ಹೃದಯದಲ್ಲಿರಬೇಕು.",
			"7": "<sup>7</sup> ಇವುಗಳನ್ನು ನಿಮ್ಮ ಮಕ್ಕಳಿಗೆ ಅಭ್ಯಾಸಮಾಡಿಸಿ ಮನೆಯಲ್ಲಿ ಕೂತಿರುವಾಗಲೂ ದಾರಿನಡೆಯುವಾಗಲೂ ಮಲಗುವಾಗಲೂ ಏಳುವಾಗಲೂ ಇವುಗಳ ವಿಷಯದಲ್ಲಿ ಮಾತಾಡಬೇಕು.",
			"8": "<sup>8</sup> ಇವುಗಳನ್ನು ಜ್ಞಾಪಕಾರ್ಥವಾಗಿ ಕೈಗೆ ಕಟ್ಟಿಕೊಳ್ಳಬೇಕು; ಇವು ಹಣೆಗೆ ಕಟ್ಟಿಕೊಳ್ಳುವ ಜ್ಞಾಪಕದ ಪಟ್ಟಿಯಂತೆ ಇರಬೇಕು.",
			"9": "<sup>9</sup> ನಿಮ್ಮ ಮನೆ ಬಾಗಲಿನ ನಿಲುವುಪಟ್ಟಿಗಳಲ್ಲಿಯೂ ತಲೆಬಾಗಲುಗಳ ಮೇಲೆಯೂ ಇವುಗಳನ್ನು ಬರೆಯಬೇಕು.",
			"10": "<sup>10</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮ ಪಿತೃಗಳಾದ ಅಬ್ರಹಾಮ್ ಇಸಾಕ್ ಯಾಕೋಬರಿಗೆ ಪ್ರಮಾಣ ಮಾಡಿಕೊಟ್ಟ ದೇಶದಲ್ಲಿ ನಿಮ್ಮನ್ನು ಸೇರಿಸಿದಾಗ ನೀವು ಕಟ್ಟದ ಒಳ್ಳೆಯ ದೊಡ್ಡ ಪಟ್ಟಣಗಳನ್ನೂ",
			"11": "<sup>11</sup> ನೀವು ಕೂಡಿಸದ ಉತ್ತಮವಸ್ತುಗಳಿಂದ ತುಂಬಿದ ಮನೆಗಳನ್ನೂ ನೀವು ಅಗೆಯದ ನೀರಗುಂಡಿಗಳನ್ನೂ ನೀವು ಬೆಳಸದ ದ್ರಾಕ್ಷೇತೋಟಗಳನ್ನೂ ಎಣ್ಣೆಮರಗಳನ್ನೂ ಅನುಭವಿಸುತ್ತಾ ತೃಪ್ತರಾಗಿರುವಾಗ",
			"12": "<sup>12</sup> ಐಗುಪ್ತದೇಶದಲ್ಲಿ ದಾಸರಾಗಿದ್ದ ನಿಮ್ಮನ್ನು ಬಿಡುಗಡೆ ಮಾಡಿದ ಯೆಹೋವನನ್ನು ಮರೆಯಬಾರದು, ನೋಡಿರಿ.",
			"13": "<sup>13</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಲ್ಲಿಯೇ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿರಬೇಕು; ಆತನನ್ನೇ ಸೇವಿಸಬೇಕು; ಆತನ ಹೆಸರು ಹೇಳಿ ಪ್ರಮಾಣಮಾಡಬೇಕು.",
			"14": "<sup>14</sup> ಸುತ್ತಲಿರುವ ಜನಾಂಗಗಳು ಸೇವಿಸುವ ದೇವರುಗಳನ್ನು ನೀವು ಆರಾಧಿಸಬಾರದು.",
			"15": "<sup>15</sup> ನಿಮ್ಮ ಮಧ್ಯದಲ್ಲಿರುವ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ತನಗೆ ಸಲ್ಲಬೇಕಾದ ಗೌರವವನ್ನು ಇನ್ನೊಬ್ಬನಿಗೆ ಸಲ್ಲಗೊಡಿಸದ ದೇವರು; ಆತನು ನಿಮ್ಮ ಮೇಲೆ ಕೋಪಗೊಂಡರೆ ನಿಮ್ಮನ್ನು ಭೂಮಿಯ ಮೇಲೆ ಉಳಿಯದಂತೆ ನಾಶಮಾಡಾನು.",
			"16": "<sup>16</sup> ನೀವು ಮಸ್ಸದಲ್ಲಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಪರೀಕ್ಷಿಸಿದಂತೆ ಇನ್ನು ಮುಂದೆ ಮಾಡಬೇಡಿರಿ.",
			"17": "<sup>17</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ನೇಮಿಸಿದ ಆಜ್ಞಾವಿಧಿನಿರ್ಣಯಗಳನ್ನು ಅನುಸರಿಸಲೇಬೇಕು.",
			"18": "<sup>18</sup> ಯೆಹೋವನ ದೃಷ್ಟಿಗೆ ಯಾವದು ನ್ಯಾಯವೂ ಯೋಗ್ಯವೂ ಆಗಿದೆಯೋ ಅದನ್ನೇ ಮಾಡಬೇಕು. ಹೀಗೆ ನಡೆದರೆ ನಿಮಗೆ ಶುಭವುಂಟಾಗುವದು, ಮತ್ತು ಯೆಹೋವನು ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ಪ್ರಮಾಣಪೂರ್ವಕವಾಗಿ ಕೊಟ್ಟ ಉತ್ತಮದೇಶವನ್ನು ನೀವು ಸೇರಿ ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವಿರಿ.",
			"19": "<sup>19</sup> ಆತನು ತಾನೇ ನಿಮ್ಮ ಶತ್ರುಗಳನ್ನು ನಿಮ್ಮ ಎದುರಿನಿಂದ ಹೊರಡಿಸುವೆನೆಂದು ಮಾತು ಕೊಟ್ಟನಲ್ಲವೇ.",
			"20": "<sup>20</sup> ಇನ್ನು ಮುಂದೆ ನಿಮ್ಮ ಮಕ್ಕಳು - ಈ ಆಜ್ಞಾವಿಧಿ ನಿರ್ಣಯಗಳನ್ನು ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಯಾತಕ್ಕೆ ನೇಮಿಸಿದನು ಎಂದು ವಿಚಾರಿಸುವಾಗ",
			"21": "<sup>21</sup> ನೀವು ಅವರಿಗೆ - ನಾವು ಐಗುಪ್ತದೇಶದಲ್ಲಿ ಫರೋಹನಿಗೆ ದಾಸರಾಗಿದ್ದಾಗ ಯೆಹೋವನು ತನ್ನ ಭುಜಬಲದಿಂದ ನಮ್ಮನ್ನು ಬಿಡುಗಡೆ ಮಾಡಿದನು;",
			"22": "<sup>22</sup> ಆತನು ಐಗುಪ್ತ್ಯರನ್ನೂ ಫರೋಹನನ್ನೂ ಅವನ ಮನೆಯವರನ್ನೂ ಬಾಧಕವಾದ ದೊಡ್ಡ ಮಹತ್ಕಾರ್ಯಗಳಿಂದಲೂ ಉತ್ಪಾತಗಳಿಂದಲೂ ನಮ್ಮ ಕಣ್ಣೆದುರಿನಲ್ಲಿ ಶಿಕ್ಷಿಸಿ",
			"23": "<sup>23</sup> ತಾನು ನಮ್ಮ ಪಿತೃಗಳಿಗೆ ಪ್ರಮಾಣಮಾಡಿಕೊಟ್ಟ ದೇಶದಲ್ಲಿ ನಮ್ಮನ್ನು ಸೇರಿಸುವದಕ್ಕಾಗಿ ಐಗುಪ್ತದೇಶದೊಳಗಿಂದ ಬರಮಾಡಿದನು;",
			"24": "<sup>24</sup> ಆ ಕಾರಣದಿಂದಲೇ ಈ ನಿಯಮಗಳನ್ನೆಲ್ಲಾ ಆಚರಿಸಬೇಕೆಂದು ಆತನು ನಮಗೆ ಅಪ್ಪಣೆಕೊಟ್ಟಿದ್ದಾನೆ. ಯಾವಾಗಲೂ ಶುಭವುಂಟಾಗುವಂತೆಯೂ ಆತನು ಇಂದಿನಂತೆ ನಮ್ಮ ಪ್ರಾಣಗಳನ್ನು ಕಾಪಾಡುವಂತೆಯೂ ನಾವು ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಭಯ ಭಕ್ತಿಯುಳ್ಳವರಾಗಿರಬೇಕು.",
			"25": "<sup>25</sup> ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿರೂಪಿಸಿದ ಈ ಧರ್ಮೋಪದೇಶವನ್ನೆಲ್ಲಾ ನಾವು ಅನುಸರಿಸಿದರೆ ಆತನ ದೃಷ್ಟಿಯಲ್ಲಿ ನೀತಿವಂತರೆಂದು ಎಣಿಸಲ್ಪಡುವೆವೆಂಬದಾಗಿ ನೀವು ಉತ್ತರಕೊಡಬೇಕು."
		},
		"7": {
			"1": "<sup>1</sup> ನೀವು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವದಕ್ಕೆ ಹೋಗುವ ದೇಶದಲ್ಲಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಸೇರಿಸಿ ಸಂಖ್ಯೆಯಲ್ಲಿಯೂ ಬಲದಲ್ಲಿಯೂ ನಿಮಗೆ ಮೀರುವ ಹಿತ್ತಿಯರು, ಗಿರ್ಗಾಷಿಯರು, ಅಮೋರಿಯರು, ಕಾನಾನ್ಯರು, ಪೆರಿಜ್ಜೀಯರು, ಹಿವ್ವಿಯರು, ಯೆಬೂಸಿಯರು ಎಂಬ ಏಳು ಜನಾಂಗಗಳನ್ನು",
			"2": "<sup>2</sup> ನಿಮ್ಮ ಎದುರಿನಿಂದ ಹೊರಡಿಸುವವನಾಗಿ ಅವರನ್ನು ನಿಮ್ಮಿಂದ ಪರಾಜಯಪಡಿಸುವಾಗ ನೀವು ಅವರನ್ನು ನಿಶ್ಶೇಷವಾಗಿ ಸಂಹರಿಸಬೇಕು. ಅವರ ಸಂಗಡ ಒಡಂಬಡಿಕೆಮಾಡಿಕೊಳ್ಳಬಾರದು, ಅವರನ್ನು ಕನಿಕರಿಸಬಾರದು.",
			"3": "<sup>3</sup> ಅವರೊಡನೆ ಬೀಗತನಮಾಡಬಾರದು; ಅವರ ಮಕ್ಕಳಿಗೆ ಹೆಣ್ಣುಗಳನ್ನು ಕೊಡಲೂಬಾರದು. ಅವರಿಂದ ತರಲೂಬಾರದು.",
			"4": "<sup>4</sup> ಹಾಗೆ ಮಾಡಿದರೆ ಅವರು ನಿಮ್ಮ ಮಕ್ಕಳನ್ನು ಯೆಹೋವನ ಸೇವೆಯಿಂದ ತಪ್ಪಿಸಿ ಇತರ ದೇವರುಗಳನ್ನು ಪೂಜಿಸುವದಕ್ಕೆ ತಿರುಗಿಸಾರು; ಆಗ ಯೆಹೋವನು ನಿಮ್ಮ ಮೇಲೆ ಕೋಪಗೊಂಡು ಬೇಗನೆ ನಿಮ್ಮನ್ನು ನಾಶ ಮಾಡುವನು.",
			"5": "<sup>5</sup> ಆದದರಿಂದ ನೀವು ಹೀಗೆ ಮಾಡಬೇಕು - ಅವರ ಬಲಿಪೀಠಗಳನ್ನು ಕೆಡವಬೇಕು; ಅವರ ಕಲ್ಲಿನ ಕಂಬಗಳನ್ನು ಒಡೆದು ಅಶೇರವೆಂಬ ವಿಗ್ರಹಸ್ತಂಭಗಳನ್ನು ಕಡಿದು ಅವರ ದೇವತಾಪ್ರತಿಮೆಗಳನ್ನು ಬೆಂಕಿಯಿಂದ ಸುಟ್ಟುಬಿಡಬೇಕು.",
			"6": "<sup>6</sup> ಯಾಕಂದರೆ ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಮೀಸಲಾದ ಜನರಾಗಿದ್ದೀರಲ್ಲವೇ; ಆತನು ಭೂಲೋಕದಲ್ಲಿರುವ ಸಮಸ್ತ ಜನಾಂಗಗಳಲ್ಲಿ ನಿಮ್ಮನ್ನೇ ಸ್ವಕೀಯ ಜನವಾಗುವದಕ್ಕೆ ಆದುಕೊಂಡನು.",
			"7": "<sup>7</sup> ನಿಮ್ಮನ್ನು ಎಲ್ಲಾ ಜನಾಂಗಗಳಲ್ಲಿ ಹೆಚ್ಚು ಮಂದಿ ಎಂದು ಇಷ್ಟಪಟ್ಟು ಆದುಕೊಳ್ಳಲಿಲ್ಲ; ನೀವು ಎಲ್ಲಾ ಜನಾಂಗಗಳಿಗಿಂತಲೂ ಸ್ವಲ್ಪಮಂದಿಯಷ್ಟೆ.",
			"8": "<sup>8</sup> ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಪ್ರೀತಿಸಿ ತಾನು ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ಮಾಡಿದ ಪ್ರಮಾಣವನ್ನು ನೆರವೇರಿಸಬೇಕೆಂದು ಐಗುಪ್ತ್ಯರ ಅರಸನಾದ ಫರೋಹನ ಕೈಕೆಳಗೆ ದಾಸರಾಗಿದ್ದ ನಿಮ್ಮನ್ನು ಬಿಡುಗಡೆ ಮಾಡಿ ತನ್ನ ಭುಜಬಲವನ್ನು ಪ್ರಯೋಗಿಸಿ ಆ ದೇಶದೊಳಗಿಂದ ಬರಮಾಡಿದನು.",
			"9": "<sup>9</sup> ಆದದರಿಂದ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನೊಬ್ಬನೇ ದೇವರೆಂದು ತಿಳಿದುಕೊಳ್ಳಬೇಕು. ಆತನು ನಂಬಿಗಸ್ತನಾದ ದೇವರು; ತನ್ನನ್ನು ಪ್ರೀತಿಸಿ ತನ್ನ ಆಜ್ಞೆಗಳನ್ನು ಅನುಸರಿಸುವವರಿಗೆ ಮಾಡಿದ ವಾಗ್ದಾನವನ್ನೂ ಕೃಪೆಯನ್ನೂ ಅವರ ಸಂತತಿಯವರಲ್ಲಿ ಸಾವಿರ ತಲೆಗಳವರೆಗೂ ನೆರವೇರಿಸುವವನಾಗಿಯೂ",
			"10": "<sup>10</sup> ತನ್ನನ್ನು ತಿರಸ್ಕರಿಸಿದವರಿಗೆ ಪ್ರತಿಯಾಗಿ ನಾಶವನ್ನುಂಟುಮಾಡುವವನಾಗಿಯೂ ಇದ್ದಾನೆಂದು ತಿಳಿದುಕೊಳ್ಳಿರಿ. ಹಗೆಯವರ ವಿಷಯದಲ್ಲಿ ತಡಮಾಡದೆ ಆಗಲೇ ಮುಯ್ಯಿತೀರಿಸುವನು.",
			"11": "<sup>11</sup> ಆದಕಾರಣ ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ಧರ್ಮೋಪದೇಶವನ್ನೂ ಆಜ್ಞಾವಿಧಿಗಳನ್ನೂ ನೀವು ಅನುಸರಿಸುವವರಾಗಿರಬೇಕು.",
			"12": "<sup>12</sup> ನೀವು ಈ ವಿಧಿಗಳನ್ನು ಲಕ್ಷ್ಯವಿಟ್ಟು ಅನುಸರಿಸಿದರೆ ಅದಕ್ಕೆ ಪ್ರತಿಯಾಗಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ಪ್ರಮಾಣಪೂರ್ವಕವಾಗಿ ಮಾಡಿದ ವಾಗ್ದಾನವನ್ನೂ ಕೃಪೆಯನ್ನೂ ನಿಮ್ಮಲ್ಲಿ ನೆರವೇರಿಸುವವನಾಗಿ",
			"13": "<sup>13</sup> ನಿಮ್ಮನ್ನು ಪ್ರೀತಿಸಿ ಅಭಿವೃದ್ಧಿಪಡಿಸಿ ಹೆಚ್ಚಿಸುವನು. ಆತನು ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ಪ್ರಮಾಣಮಾಡಿಕೊಟ್ಟ ದೇಶದಲ್ಲಿ ನಿಮ್ಮ ಸಂತಾನವನ್ನೂ ವ್ಯವಸಾಯವನ್ನೂ ನಿಮಗಿರುವ ಧಾನ್ಯದ್ರಾಕ್ಷಾರಸ, ಎಣ್ಣೆ ಇವುಗಳನ್ನೂ ನಿಮ್ಮ ದನಕುರಿಗಳಲ್ಲಿ ಹುಟ್ಟಿದ್ದನ್ನೂ ಅಭಿವೃದ್ಧಿಪಡಿಸುವನು.",
			"14": "<sup>14</sup> ನೀವು ಎಲ್ಲಾ ಜನಗಳಿಗಿಂತಲೂ ಹೆಚ್ಚಾಗಿ ದೇವರ ಅನುಗ್ರಹವನ್ನು ಹೊಂದಿದವರಾಗಿರುವಿರಿ. ನಿಮ್ಮೊಳಗೆ ಸ್ತ್ರೀಪುರುಷರಲ್ಲಿಯಾಗಲಿ ಹೆಣ್ಣು ಗಂಡು ಪಶುಗಳಲ್ಲಿಯಾಗಲಿ ಬಂಜೆತನವು ಇರುವದಿಲ್ಲ.",
			"15": "<sup>15</sup> ನಿಮ್ಮಲ್ಲಿ ಯಾವ ವ್ಯಾಧಿಯೂ ಉಂಟಾಗದಂತೆ ಯೆಹೋವನು ಮಾಡುವನು. ನಿಮ್ಮ ಅನುಭವಕ್ಕೆ ಬಂದ ಪ್ರಕಾರ ಐಗುಪ್ತದೇಶದಲ್ಲಿ ಪ್ರಬಲವಾಗಿರುವ ಕ್ರೂರವ್ಯಾಧಿಗಳನ್ನು ನಿಮಗೆ ಬರಗೊಡಿಸದೆ ಅವುಗಳನ್ನು ನಿಮ್ಮ ಶತ್ರುಗಳ ಮೇಲೆಯೇ ಬರಮಾಡುವನು.",
			"16": "<sup>16</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮಿಂದ ಪರಾಜಯಪಡಿಸುವ ಜನಾಂಗಗಳನ್ನೆಲ್ಲಾ ನೀವು ಕನಿಕರಿಸದೆ ನಾಶಮಾಡಬೇಕು. ಅವರ ದೇವರುಗಳನ್ನು ಪೂಜಿಸಲೇಬಾರದು; ಪೂಜಿಸಿದರೆ ಅವು ನಿಮಗೆ ಉರುಲಾಗುವವು.",
			"17": "<sup>17</sup> ಆ ಜನಗಳು ನಮಗಿಂತ ಹೆಚ್ಚು ಮಂದಿ; ಅವರನ್ನು ಹೊರಡಿಸುವದು ನಮ್ಮಿಂದ ಹೇಗಾದೀತು ಅಂದುಕೊಳ್ಳುತ್ತೀರೋ? ಅವರಿಗೆ ಹೆದರಬೇಡಿರಿ;",
			"18": "<sup>18</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಫರೋಹನಿಗೂ ಐಗುಪ್ತ್ಯರೆಲ್ಲರಿಗೂ ಮಾಡಿದ್ದನ್ನು ನೆನಪಿಗೆ ತಂದುಕೊಳ್ಳಿರಿ.",
			"19": "<sup>19</sup> ನೀವು ಆಗ ಕಣ್ಣಾರೆ ನೋಡಿದ ಪ್ರಕಾರ ಆತನು ವಿಶೇಷ ಮನಶ್ಶೋಧನೆ, ಮಹತ್ಕಾರ್ಯ, ಉತ್ಪಾತ, ಭುಜಪರಾಕ್ರಮ, ಶಿಕ್ಷೆ ಇವುಗಳನ್ನು ಪ್ರಯೋಗಿಸಿ ನಿಮ್ಮನ್ನು ಬಿಡುಗಡೆಮಾಡಿದನಲ್ಲಾ. ನೀವು ಹೆದರಿಕೊಳ್ಳುವ ಆ ಎಲ್ಲಾ ಜನಾಂಗಗಳಿಗೂ ಆತನು ಹಾಗೆಯೇ ಮಾಡುವನು.",
			"20": "<sup>20</sup> ಅದು ಮಾತ್ರವಲ್ಲದೆ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಕಡಜದ ಹುಳಗಳನ್ನು ಅವರೊಳಗೆ ಕಳುಹಿಸುವನು; ಮರೆಯಾಗಿದ್ದು ಉಳುಕೊಂಡವರು ಆ ಹುಳಗಳ ದೆಸೆಯಿಂದ ನಾಶವಾಗಿ ನಿಮಗೆ ಕಾಣದೆ ಹೋಗುವರು.",
			"21": "<sup>21</sup> ನೀವು ಅವರಿಗೆ ಹೆದರಿಕೊಳ್ಳಬೇಡಿರಿ; ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮ ಮಧ್ಯದಲ್ಲಿದ್ದಾನೆ; ಆತನು ಭಯಂಕರನಾದ ಮಹಾದೇವರು.",
			"22": "<sup>22</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಆ ಜನಾಂಗಗಳನ್ನು ನಿಮ್ಮ ಎದುರಿನಿಂದ ಸ್ವಲ್ಪಸ್ವಲ್ಪವಾಗಿ ಹೊರಡಿಸುತ್ತಿರುವನು. ನೀವು ಬಹು ಶೀಘ್ರವಾಗಿ ಅವರನ್ನು ನಿರ್ಮೂಲಮಾಡಕೂಡದು; ಮಾಡಿದರೆ ಕಾಡುಮೃಗಗಳು ಹೆಚ್ಚಿ ನಿಮಗೆ ತೊಂದರೆಯನ್ನುಂಟುಮಾಡಾವು.",
			"23": "<sup>23</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಅವರನ್ನು ನಿಮ್ಮಿಂದ ಸೋಲಿಸಿ ಬಹಳವಾಗಿ ಗಲಿಬಿಲಿಮಾಡಿ ಕಡೆಗೆ ಇಲ್ಲದಂತೆ ಮಾಡುವನು.",
			"24": "<sup>24</sup> ಆತನು ಅವರ ಅರಸುಗಳನ್ನು ನಿಮ್ಮ ಕೈಗೆ ಸಿಕ್ಕುವಂತೆ ಮಾಡಲಾಗಿ ನೀವು ಅವರ ಹೆಸರುಗಳೇ ಭೂಮಿಯ ಮೇಲೆ ಉಳಿಯದಂತೆ ಮಾಡಬೇಕು. ಯಾರೂ ನಿಮ್ಮ ಮುಂದೆ ನಿಲ್ಲುವದಿಲ್ಲ; ಎಲ್ಲರನ್ನೂ ನಾಶಮಾಡುವಿರಿ.",
			"25": "<sup>25</sup> ಅವರು ದೇವರೆಂದು ಮಾಡಿಕೊಂಡ ಮರದ ವಿಗ್ರಹಗಳನ್ನು ನೀವು ಬೆಂಕಿಯಿಂದ ಸುಟ್ಟುಬಿಡಬೇಕು. ಆ ವಿಗ್ರಹಗಳ ಮೇಲಿರುವ ಬೆಳ್ಳಿಬಂಗಾರವನ್ನು ನೀವು ಆಶಿಸಬಾರದು; ಅದನ್ನು ತೆಗೆದುಕೊಂಡರೆ ಅದು ನಿಮಗೆ ಉರುಲಾಗುವದು. ಅದು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಹೇಯವಾದದ್ದು.",
			"26": "<sup>26</sup> ಅದು ಕೇವಲ ಯೆಹೋವನದಾಗುವದಕ್ಕೆ ನಾಶವಾಗತಕ್ಕದ್ದಾಗಿರುವದರಿಂದ ನೀವು ಅಂಥ ವಸ್ತುವನ್ನು ಮನೆಯೊಳಕ್ಕೆ ತಂದು ಅದರಂತೆಯೇ ನಾಶಕ್ಕೆ ಗುರಿಯಾಗಬಾರದು. ನೀವು ಅದನ್ನು ಅಸಹ್ಯಪಟ್ಟು ಮುಟ್ಟದಿರಬೇಕು."
		},
		"8": {
			"1": "<sup>1</sup> ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ಧರ್ಮೋಪದೇಶವನ್ನೆಲ್ಲಾ ನೀವು ಅನುಸರಿಸಬೇಕು. ಅನುಸರಿಸಿದರೆ ನೀವು ಬದುಕಿಕೊಂಡು ಹೆಚ್ಚಿ ಯೆಹೋವನು ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ಪ್ರಮಾಣರೂಪವಾಗಿ ಕೊಟ್ಟ ದೇಶವನ್ನು ಸೇರಿ ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವಿರಿ.",
			"2": "<sup>2</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಈ ನಾಲ್ವತ್ತು ವರುಷ ಅರಣ್ಯದಲ್ಲಿ ನಡಿಸಿದ್ದನ್ನೂ ನೀವು ತನ್ನ ಆಜ್ಞೆಗಳನ್ನು ಕೈಕೊಳ್ಳುವವರೋ ಅಲ್ಲವೋ ಎಂದು ತಿಳಿದುಕೊಳ್ಳುವದಕ್ಕೆ ನಿಮ್ಮನ್ನು ಕಷ್ಟಕ್ಕೆ ಒಳಪಡಿಸಿ ನಿಮ್ಮ ಮನೋಭಾವವನ್ನು ಪರೀಕ್ಷಿಸಿದ್ದನ್ನೂ ನೆನಪಿಗೆ ತಂದುಕೊಳ್ಳಿರಿ.",
			"3": "<sup>3</sup> ಮನುಷ್ಯರು ಆಹಾರಮಾತ್ರದಿಂದಲ್ಲ, ಯೆಹೋವನ ಬಾಯಿಂದ ಹೊರಡುವ ಪ್ರತಿಯೊಂದು ಮಾತಿನಿಂದಲೂ ಬದುಕುತ್ತಾರೆಂಬದು ನಿಮಗೆ ತಿಳಿಯುವಂತೆ ಆತನು ನಿಮ್ಮನ್ನು ಕಷ್ಟಕ್ಕೆ ಒಳಪಡಿಸಿ ಹಸಿವೆಯಿಂದ ಬಳಲಿಸಿ ನಿಮಗೂ ನಿಮ್ಮ ಪೂರ್ವಿಕರಿಗೂ ಗೊತ್ತಿಲ್ಲದ ಮನ್ನವನ್ನು ಕೊಟ್ಟು ಪೋಷಿಸಿದನು.",
			"4": "<sup>4</sup> ಈ ನಾಲ್ವತ್ತು ವರುಷ ನಿಮ್ಮ ಮೈಮೇಲಿದ್ದ ಉಡುಪು ಜೀರ್ಣವಾಗಲಿಲ್ಲ; ನಿಮ್ಮ ಕಾಲುಗಳು ಬಾತುಹೋಗಲಿಲ್ಲ.",
			"5": "<sup>5</sup> ತಂದೆ ಮಗನನ್ನು ಹೇಗೋ ಹಾಗೆಯೇ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಶಿಕ್ಷಿಸುತ್ತಾ ಬಂದನೆಂಬದನ್ನು ನೀವು ಆಲೋಚಿಸಿಕೊಂಡು",
			"6": "<sup>6</sup> ಆತನ ಆಜ್ಞೆಗಳನ್ನು ಅನುಸರಿಸುವವರಾಗಿ ಆತನು ಹೇಳಿದ ಮಾರ್ಗದಲ್ಲೇ ನಡೆದು ಆತನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿರಬೇಕು.",
			"7": "<sup>7</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಉತ್ತಮದೇಶಕ್ಕೆ ನಿಮ್ಮನ್ನು ಸೇರಿಸುತ್ತಾನೆ. ಆ ದೇಶದ ಹಳ್ಳಗಳಲ್ಲಿ ನೀರು ಯಾವಾಗಲೂ ಹರಿಯುತ್ತದೆ; ಮತ್ತು ತಗ್ಗುಗಳಲ್ಲಾಗಲಿ ಗುಡ್ಡಗಳಲ್ಲಾಗಲಿ ಎಲ್ಲಾ ಕಡೆಯೂ ಬಾವಿಗಳಲ್ಲಿಯೂ ಬುಗ್ಗೆಗಳಿಂದಲೂ ನೀರು ಉಕ್ಕುತ್ತದೆ.",
			"8": "<sup>8</sup> ಆ ದೇಶದಲ್ಲಿ ಗೋದಿ, ಜವೆಗೋದಿ, ದ್ರಾಕ್ಷೆ, ಅಂಜೂರ, ದಾಳಿಂಬ ಇವುಗಳು ಬೆಳೆಯುತ್ತವೆ; ಎಣ್ಣೆ ಮರಗಳೂ ಜೇನೂ ಸಿಕ್ಕುತ್ತವೆ.",
			"9": "<sup>9</sup> ಅಲ್ಲಿ ನೀವು ದುರ್ಭಿಕ್ಷವನ್ನೇ ಕಾಣದೆ ಸಮೃದ್ಧಿಯಾಗಿ ಉಂಡು ಯಾವ ಕೊರತೆಯೂ ಇಲ್ಲದೆ ಇರುವಿರಿ. ಆ ದೇಶದಲ್ಲಿ ಕಬ್ಬಿಣದ ಕಲ್ಲು ಸಿಕ್ಕುತ್ತದೆ; ಅಲ್ಲಿಯ ಬೆಟ್ಟಗಳಲ್ಲಿ ತಾಮ್ರದ ಗಣಿಗಳುಂಟು.",
			"10": "<sup>10</sup> ನೀವು ಹೊಟ್ಟೆತುಂಬಾ ಉಂಡು ಸುಖದಿಂದಿರುವಾಗ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಉತ್ತಮ ದೇಶವನ್ನು ಕೊಟ್ಟದ್ದಕ್ಕಾಗಿ ಆತನನ್ನು ಸ್ತುತಿಸಬೇಕು.",
			"11": "<sup>11</sup> ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಆಜ್ಞಾವಿಧಿನಿರ್ಣಯಗಳನ್ನು ಕೈಕೊಳ್ಳದವರೂ ಆತನನ್ನು ಮರೆಯುವವರೂ ಆಗಬೇಡಿರಿ, ನೋಡಿರಿ.",
			"12": "<sup>12</sup> ನೀವು ಹೊಟ್ಟೆತುಂಬಾ ಉಂಡು ಸುಖವಾಗಿದ್ದು ಒಳ್ಳೇ ಮನೆಗಳನ್ನು ಕಟ್ಟಿಸಿಕೊಂಡು",
			"13": "<sup>13</sup> ಅವುಗಳಲ್ಲಿ ವಾಸವಾಗಿರುವ ಕಾಲದಲ್ಲಿ ನಿಮ್ಮ ದನಗಳೂ ಆಡುಕುರಿಗಳೂ ನಿಮ್ಮ ಬೆಳ್ಳಿಬಂಗಾರವೂ ಆಸ್ತಿಯೆಲ್ಲವೂ ಹೆಚ್ಚುತ್ತಿರುವಾಗ",
			"14": "<sup>14</sup> ಒಂದು ವೇಳೆ ನೀವು ಮದಿಸಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಮರೆತೀರಿ.",
			"15": "<sup>15</sup> ಆತನು ನಿಮ್ಮನ್ನು ಕಷ್ಟಕ್ಕೆ ಒಳಪಡಿಸಿ ಪರೀಕ್ಷಿಸಿದನಂತರ ನಿಮಗೆ ಮೇಲನ್ನುಂಟುಮಾಡಬೇಕೆಂಬ ಉದ್ದೇಶದಿಂದ ಐಗುಪ್ತದೇಶದಲ್ಲಿ ದಾಸರಾಗಿದ್ದ ನಿಮ್ಮನ್ನು ಬಿಡುಗಡೆಮಾಡಿ ವಿಷಸರ್ಪಗಳೂ ಚೇಳುಗಳೂ ಇದ್ದ ಆ ಘೋರವಾದ ಮಹಾರಣ್ಯವನ್ನೂ ನೀರು ಬತ್ತಿಹೋದ ಭೂಮಿಗಳನ್ನೂ ದಾಟಿಸಿ ಗಟ್ಟಿಯಾದ ಬಂಡೆಯೊಳಗಿಂದ ನೀರನ್ನು ಹೊರಡಿಸಿಕೊಟ್ಟು ನಿಮ್ಮ ಪೂರ್ವಿಕರಿಗೆ ತಿಳಿಯದೆ ಇದ್ದ ಮನ್ನವನ್ನು ಕೊಟ್ಟು ಪೋಷಿಸಿದಾತನನ್ನು ನೀವು ಮರೆತು ಮನಸ್ಸಿನೊಳಗೆ -",
			"17": "<sup>17</sup> ಈ ಭಾಗ್ಯವು ನಮ್ಮ ಸಾಮರ್ಥ್ಯ ಸಾಹಸಗಳಿಂದಲೇ ನಮಗುಂಟಾಯಿತು ಅಂದುಕೊಂಡೀರಿ.",
			"18": "<sup>18</sup> ಆದದರಿಂದ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನೇ ತಾನು ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ಪ್ರಮಾಣಪೂರ್ವಕವಾಗಿ ಮಾಡಿದ ವಾಗ್ದಾನವನ್ನು ಈಗ ನಿಮ್ಮ ಅನುಭವಕ್ಕೆ ಬಂದ ರೀತಿಯಲ್ಲಿ ನೆರವೇರಿಸುವವನಾಗಿ ನಿಮಗೆ ಇಷ್ಟು ಭಾಗ್ಯವನ್ನು ಸಂಪಾದಿಸುವದಕ್ಕೆ ಸಾಮರ್ಥ್ಯವನ್ನು ಕೊಟ್ಟವನೆಂದು ಜ್ಞಾಪಕಮಾಡಿಕೊಳ್ಳಿರಿ.",
			"19": "<sup>19</sup> ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಮರೆತು ಇತರ ದೇವರುಗಳನ್ನು ಅವಲಂಬಿಸಿ ಪೂಜಿಸಿದರೆ ನೀವು ತಪ್ಪದೆ ನಾಶವಾಗಿ ಹೋಗುವಿರೆಂದು ನಿಮ್ಮನ್ನು ಈಗ ಖಂಡಿತವಾಗಿ ಎಚ್ಚರಿಸುತ್ತೇನೆ.",
			"20": "<sup>20</sup> ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಮಾತನ್ನು ಕೇಳದೆ ಹೋದರೆ ಆತನಿಂದ ನಿಮ್ಮ ಎದುರಾಗಿ ನಾಶವಾಗಿ ಹೋಗುವ ಜನಾಂಗಗಳಂತೆಯೇ ನೀವೂ ನಾಶವಾಗಿ ಹೋಗುವಿರಿ."
		},
		"9": {
			"1": "<sup>1</sup> ಇಸ್ರಾಯೇಲ್ಯರೇ ಕೇಳಿರಿ. ನಿಮಗಿಂತ ಮಹಾಬಲಿಷ್ಠ ಜನಾಂಗಗಳನ್ನೂ ಆಕಾಶವನ್ನು ಮುಟ್ಟುವ ಕೋಟೆಕೊತ್ತಲುಗಳುಳ್ಳ ಪಟ್ಟಣಗಳನ್ನೂ ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವದಕ್ಕೆ ನೀವು ಈ ಯೊರ್ದನ್ ಹೊಳೆಯನ್ನು ಇಂದು ದಾಟುವವರಾಗಿದ್ದೀರಿ.",
			"2": "<sup>2</sup> ಆ ದೇಶದವರು ಅನಾಕ್ಯರೆಂಬ ಬಲಿಷ್ಠರಾದ ಉನ್ನತಪುರುಷರು. ಅವರ ವಿಷಯ ನಿಮಗೆ ತಿಳಿದೇ ಇದೆ; ಅನಾಕ್ಯರ ಮುಂದೆ ನಿಲ್ಲಬಲ್ಲವರು ಯಾರಿದ್ದಾರೆ ಎಂಬ ಮಾತನ್ನು ಕೇಳಿದ್ದೀರಷ್ಟೆ.",
			"3": "<sup>3</sup> ಆದದರಿಂದ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ತಾನೇ ವಿರೋಧಿಗಳನ್ನು ದಹಿಸುವ ಬೆಂಕಿಯೋಪಾದಿಯಲ್ಲಿ ನಿಮ್ಮ ಮುಂದೆ ಹೋಗುತ್ತಾನೆಂದು ಈಗ ತಿಳಿದುಕೊಳ್ಳಿರಿ. ಆತನೇ ಅವರನ್ನು ನಾಶ ಮಾಡುವನು; ಆತನೇ ಅವರನ್ನು ನಿಮ್ಮ ಮುಂದೆ ಸೋತುಹೋಗುವಂತೆ ಮಾಡುವನು. ಯೆಹೋವನು ನಿಮಗೆ ಹೇಳಿದಂತೆ ನೀವು ಅವರನ್ನು ಹೊರಡಿಸಿ ಬೇಗನೆ ನಾಶಮಾಡುವಿರಿ.",
			"4": "<sup>4</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಅವರನ್ನು ನಿಮ್ಮ ಎದುರಿನಿಂದ ಹೊರಡಿಸಿದನಂತರ ನೀವು ಮನಸ್ಸಿನಲ್ಲಿ - ಯೆಹೋವನು ನಮ್ಮನ್ನು ಈ ದೇಶಕ್ಕೆ ಕರತಂದು ಅದನ್ನು ನಮಗೆ ಸ್ವಾಧೀನಪಡಿಸಿದ್ದು ನಮ್ಮ ಪುಣ್ಯಫಲವೇ ಅಂದುಕೊಳ್ಳಬಾರದು. ಆ ಜನಾಂಗಗಳ ದುರ್ನಡತೆಯ ದೆಸೆಯಿಂದಲೇ ಯೆಹೋವನು ನಿಮ್ಮ ಎದುರಿನಿಂದ ಅವರನ್ನು ಹೊರಡಿಸುತ್ತಾನೆ.",
			"5": "<sup>5</sup> ನೀವು ಅವರ ದೇಶಕ್ಕೆ ಬಂದು ಅದನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವದಕ್ಕೆ ನಿಮ್ಮ ಪುಣ್ಯವೇ ಆಗಲಿ ನಿಮ್ಮ ಸುಸ್ವಭಾವವೇ ಆಗಲಿ ಕಾರಣವಲ್ಲ. ಆ ಜನಾಂಗಗಳ ದುರ್ನಡತೆಯ ದೆಸೆಯಿಂದಲೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮ ಪಿತೃಗಳಾದ ಅಬ್ರಹಾಮ್ ಇಸಾಕ್ ಯಾಕೋಬರಿಗೆ ಪ್ರಮಾಣಪೂರ್ವಕವಾಗಿ ಮಾಡಿದ ವಾಗ್ದಾನವನ್ನು ನೆರವೇರಿಸಬೇಕೆಂದಿದ್ದದರಿಂದಲೂ ಅವರನ್ನು ನಿಮ್ಮ ಎದುರಿನಿಂದ ಹೊರಡಿಸಿಬಿಡುತ್ತಾನೆ.",
			"6": "<sup>6</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಆ ಉತ್ತಮದೇಶವನ್ನು ನಿಮಗೆ ಸ್ವದೇಶವಾಗಿ ಕೊಡುವದು ನಿಮ್ಮ ಪುಣ್ಯಫಲವಲ್ಲವೆಂದು ಚೆನ್ನಾಗಿ ತಿಳಿದುಕೊಳ್ಳಿರಿ. ನೀವು ಆತನ ಆಜ್ಞೆಗೆ ಮಣಿಯದ ಜನರೇ ಆಗಿದ್ದೀರಿ.",
			"7": "<sup>7</sup> ನೀವು ಅರಣ್ಯದಲ್ಲಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಕೋಪಹುಟ್ಟಿಸಿದ್ದನ್ನು ನೆನಪುಮಾಡಿಕೊಳ್ಳಿರಿ; ಅದನ್ನು ಮರೆಯಬೇಡಿರಿ. ನೀವು ಐಗುಪ್ತದೇಶವನ್ನು ಬಿಟ್ಟಂದಿನಿಂದ ಈ ಸ್ಥಳಕ್ಕೆ ಬಂದ ದಿನದವರೆಗೂ ಯೆಹೋವನ ಆಜ್ಞೆಗಳನ್ನು ಧಿಕ್ಕರಿಸುವವರಾಗಿದ್ದಿರಿ.",
			"8": "<sup>8</sup> ದೃಷ್ಟಾಂತ - ಹೋರೇಬಿನಲ್ಲಿ ನೀವು ಯೆಹೋವನಿಗೆ ಕೋಪವನ್ನು ಹುಟ್ಟಿಸಿದಿರಿ. ಆಗ ಆತನು ಬಲು ಸಿಟ್ಟುಗೊಂಡು ನಿಮ್ಮನ್ನು ಧ್ವಂಸಮಾಡಬೇಕೆಂದಿದ್ದನು.",
			"9": "<sup>9</sup> ಯೆಹೋವನು ನಿಮ್ಮ ಸಂಗಡ ಮಾಡಿದ ನಿಬಂಧನೆಯನ್ನು ಬರೆದ ಆ ಕಲ್ಲಿನ ಹಲಿಗೆಗಳನ್ನು ತೆಗೆದುಕೊಳ್ಳುವದಕ್ಕೆ ನಾನು ಬೆಟ್ಟವನ್ನು ಹತ್ತಿದ್ದಾಗ ನಾನು ಅನ್ನಪಾನಗಳನ್ನು ಬಿಟ್ಟು ಹಗಲಿರುಳು ನಾಲ್ವತ್ತು ದಿನ ಆ ಬೆಟ್ಟದಲ್ಲಿದ್ದೆನು.",
			"10": "<sup>10</sup> ಆಗ ಯೆಹೋವನು ತನ್ನ ಕೈಯಿಂದಲೇ ಬರೆದು ಆ ಎರಡು ಕಲ್ಲಿನ ಹಲಿಗೆಗಳನ್ನು ನನ್ನ ವಶಕ್ಕೆ ಕೊಟ್ಟನು. ನೀವು ಸಭೆಕೂಡಿದ ದಿನದಲ್ಲಿ ಯೆಹೋವನು ಆ ಬೆಟ್ಟದ ಮೇಲೆ ಅಗ್ನಿಜ್ವಾಲೆಯೊಳಗಿಂದ ನಿಮ್ಮ ಸಂಗಡ ಹೇಳಿದ ಮಾತುಗಳು ಆ ಹಲಿಗೆಗಳಲ್ಲಿದ್ದವು.",
			"11": "<sup>11</sup> ಆ ನಾಲ್ವತ್ತು ದಿನಗಳು ಕಳೆದ ಮೇಲೆ ಯೇಹೋವನು ತನ್ನ ನಿಬಂಧನೆಯಿರುವ ಆ ಎರಡು ಕಲ್ಲಿನ ಹಲಿಗೆಗಳನ್ನು ನನಗೆ ಕೊಟ್ಟು ನೀನು ಬೇಗ ಇಲ್ಲಿಂದ ಇಳಿದುಹೋಗು;",
			"12": "<sup>12</sup> ನೀನು ಐಗುಪ್ತದೇಶದಿಂದ ಕರಕೊಂಡು ಬಂದ ನಿನ್ನ ಜನರು ಕೆಟ್ಟುಹೋದರು; ನಾನು ಅವರಿಗೆ ಆಜ್ಞಾಪಿಸಿದ ಮಾರ್ಗವನ್ನು ಅವರು ಬೇಗನೆ ಬಿಟ್ಟುಹೋಗಿ ತಮಗೆ ಲೋಹವಿಗ್ರಹವನ್ನು ಮಾಡಿಸಿಕೊಂಡಿದ್ದಾರೆ ಎಂದು ಹೇಳಿದನು.",
			"13": "<sup>13</sup> ಅದಲ್ಲದೆ ಆತನು ನನಗೆ - ಈ ಜನರ ಸ್ವಭಾವವನ್ನು ನಾನು ನೋಡುತ್ತಾ ಬಂದಿದ್ದೇನೆ; ಇವರು ನನ್ನ ಆಜ್ಞೆಗೆ ಒಳಗಾಗದವರು.",
			"14": "<sup>14</sup> ಬಿಡು, ನಾನು ಅವರನ್ನು ನಾಶಮಾಡಿ ಅವರ ಹೆಸರನ್ನು ಭೂಮಿಯ ಮೇಲೆ ಉಳಿಯದಂತೆ ಮಾಡುವೆನು. ತರುವಾಯ ಅವರಿಗಿಂತಲೂ ಮಹಾಬಲಿಷ್ಠ ಜನಾಂಗವು ನಿನ್ನಿಂದುಂಟಾಗುವಂತೆ ಮಾಡುವೆನು ಎಂದು ಹೇಳಿದನು.",
			"15": "<sup>15</sup> ಆಗ ನಾನು ಆ ಎರಡು ಆಜ್ಞಾಶಾಸನಗಳನ್ನು ನನ್ನ ಎರಡು ಕೈಗಳಲ್ಲಿ ಹಿಡಿದುಕೊಂಡು ಬೆಂಕಿಯಿಂದ ಉರಿಯುವ ಆ ಬೆಟ್ಟದಿಂದ ಇಳಿದು ಬಂದು ನೋಡಲಾಗಿ",
			"16": "<sup>16</sup> ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ದ್ರೋಹಿಗಳಾಗಿ ಲೋಹದ ಬಸವನನ್ನು ಮಾಡಿಸಿಕೊಂಡು ಯೆಹೋವನು ನಿಮಗೆ ಆಜ್ಞಾಪಿಸಿದ್ದ ಮಾರ್ಗವನ್ನು ಬೇಗನೆ ಬಿಟ್ಟುಹೋದದ್ದು ನನಗೆ ಕಂಡು ಬಂತು.",
			"17": "<sup>17</sup> ಅದನ್ನು ನೋಡಿ ನಾನು ಕೈಯಲ್ಲಿದ್ದ ಆ ಎರಡು ಶಿಲಾಶಾಸನಗಳನ್ನು ನಿಮಗೆದುರಾಗಿಯೇ ನೆಲಕ್ಕೆ ಹಾಕಿ ಒಡೆದು ಬಿಟ್ಟೆನು.",
			"18": "<sup>18</sup> ನೀವು ಹೀಗೆ ಅಪರಾಧಮಾಡಿ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ಕೆಟ್ಟದ್ದನ್ನು ನಡಿಸಿ ಆತನನ್ನು ಕೋಪಗೊಳಿಸಿದ ಕಾರಣ ನಾನು ಮೊದಲಿನಂತೆ ಅನ್ನಪಾನಗಳನ್ನು ಬಿಟ್ಟು ನಾಲ್ವತ್ತು ದಿನಗಳು ಹಗಲಿರುಳು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲೇ ಬಿದ್ದಿದ್ದೆನು.",
			"19": "<sup>19</sup> ಯೆಹೋವನು ನಿಮ್ಮ ಮೇಲೆ ಬಹುಕೋಪವನ್ನು ಮಾಡಿ ನಿಮ್ಮನ್ನು ನಾಶಮಾಡಬೇಕೆಂದು ಆಲೋಚಿಸಿದದರಿಂದ ನಾನು ಹೆದರಿಕೊಂಡೆನು. ಆದರೆ ಆ ಕಾಲದಲ್ಲಿಯೂ ಆತನು ನನ್ನ ಮನವಿಯನ್ನು ಕೇಳಿದನು.",
			"20": "<sup>20</sup> ಅದಲ್ಲದೆ ಯೆಹೋವನು ಆರೋನನ ಮೇಲೆ ಬಹಳ ಸಿಟ್ಟುಗೊಂಡು ಅವನನ್ನೂ ನಾಶಮಾಡಬೇಕೆಂದಿದ್ದದರಿಂದ ನಾನು ಅವನಿಗೋಸ್ಕರವಾಗಿಯೂ ಆ ಕಾಲದಲ್ಲಿ ಪ್ರಾರ್ಥಿಸಿದೆನು.",
			"21": "<sup>21</sup> ಮತ್ತು ನೀವು ಯಾವದರ ಮೂಲಕ ಪಾಪವನ್ನು ಮಾಡಿದ್ದಿರೋ ಆ ಬಸವನನ್ನು ನಾನು ತೆಗೆದುಕೊಂಡು ಬೆಂಕಿಯಿಂದ ಸುಟ್ಟು ಒಡೆದು ಅರೆದು ಧೂಳುಮಾಡಿ ಬೆಟ್ಟದಿಂದ ಹರಿಯುವ ಹಳ್ಳದಲ್ಲಿ ಬೀಸಾಡಿಬಿಟ್ಟೆನು.",
			"22": "<sup>22</sup> ತಬೇರಾ, ಮಸ್ಸಾ, ಕಿಬ್ರೋತ್‍ಹತಾವಾ ಎಂಬ ಸ್ಥಳಗಳಲ್ಲಿಯೂ ನೀವು ಯೆಹೋವನಿಗೆ ಕೋಪವನ್ನು ಹುಟ್ಟಿಸಿದಿರಲ್ಲಾ.",
			"23": "<sup>23</sup> ಅದಲ್ಲದೆ ಕಾದೇಶ್‍ಬರ್ನೇಯದಲ್ಲಿ ಯೆಹೋವನು ನಿಮಗೆ - ಬೆಟ್ಟದ ಸೀಮೆಯನ್ನು ಹತ್ತಿ ನಾನು ನಿಮಗೆ ಕೊಟ್ಟಿರುವ ದೇಶವನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳಿರಿ ಎಂದು ಅಪ್ಪಣೆಕೊಟ್ಟಾಗಲೂ ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಆ ಆಜ್ಞೆಯನ್ನು ಧಿಕ್ಕರಿಸಿ ಆತನನ್ನು ನಂಬದೆ ಆತನ ಮಾತನ್ನು ಅಲಕ್ಷ್ಯಮಾಡಿದಿರಿ.",
			"24": "<sup>24</sup> ನನಗೆ ನಿಮ್ಮ ಪರಿಚಯವಾದಂದಿನಿಂದಲೂ ನೀವು ಯೆಹೋವನ ಆಜ್ಞೆಯನ್ನು ಉಲ್ಲಂಘಿಸುವವರೇ.",
			"25": "<sup>25</sup> ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ನಾಶ ಮಾಡಬೇಕೆಂದಿದ್ದದರಿಂದ ನಾನು ಆ ನಾಲ್ವತ್ತು ದಿನವೂ ಹಗಲಿರುಳು ಆತನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಬಿದ್ದಿದ್ದೆನು.",
			"26": "<sup>26</sup> ನಾನು ಆತನನ್ನು ಬೇಡಿಕೊಳ್ಳುತ್ತಾ - ಕರ್ತನಾದ ಯೆಹೋವನೇ, ನೀನು ನಿನ್ನ ಮಹಿಮೆಯಿಂದ ರಕ್ಷಿಸಿ ನಿನ್ನ ಭುಜಬಲದಿಂದ ಐಗುಪ್ತ್ಯರಿಂದ ಬಿಡುಗಡೆಮಾಡಿದ ನಿನ್ನ ಸ್ವಕೀಯ ಜನರನ್ನು ನಾಶಮಾಡಬೇಡ.",
			"27": "<sup>27</sup> ಇವರ ಮೊಂಡತನ, ದುಷ್ಟತ್ವ, ಪಾಪ ಇವುಗಳನ್ನು ಮನಸ್ಸಿಗೆ ತೆಗೆದುಕೊಳ್ಳದೆ ನಿನ್ನ ಸೇವಕರಾದ ಅಬ್ರಾಹಾಮ್ ಇಸಾಕ್ ಯಾಕೋಬರನ್ನು ನೆನಪಿಗೆ ತಂದುಕೋ.",
			"28": "<sup>28</sup> ನೀನು ಯಾವ ದೇಶದಿಂದ ನಮ್ಮನ್ನು ಬರಮಾಡಿದಿಯೋ ಆ ದೇಶದವರು - ಯೆಹೋವನು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ವಾಗ್ದಾನಮಾಡಿದ ದೇಶಕ್ಕೆ ಅವರನ್ನು ಸೇರಿಸಲಾರದೆ ಹಗೆಮಾಡಿ ಅರಣ್ಯದಲ್ಲಿ ಕೊಲ್ಲುವದಕ್ಕೆ ಕರಕೊಂಡು ಹೋದನೆಂದು ಹೇಳುವದಕ್ಕೆ ಆಸ್ಪದವಾದೀತು.",
			"29": "<sup>29</sup> ಇವರು ಮಹಾಶಕ್ತಿಯಿಂದಲೂ ಭುಜಬಲದಿಂದಲೂ ನೀನು ಬಿಡಿಸಿದ ನಿನ್ನ ಸ್ವಕೀಯ ಜನರಲ್ಲವೇ ಎಂದು ಬಿನ್ನೈಸಿದೆನು."
		},
		"10": {
			"1": "<sup>1</sup> ಆ ಕಾಲದಲ್ಲಿ ಯೆಹೋವನು ನನಗೆ - ನೀನು ಮೊದಲಿನ ಕಲ್ಲಿನ ಹಲಿಗೆಗಳಂತೆ ಬೇರೆ ಎರಡು ಹಲಿಗೆಗಳನ್ನು ಸಿದ್ಧಪಡಿಸಿಕೊಂಡು ಬೆಟ್ಟವನ್ನು ಹತ್ತಿ ನನ್ನ ಬಳಿಗೆ ಬಾ; ಮತ್ತು ಒಂದು ಮರದ ಮಂಜೂಷವನ್ನು ಮಾಡಿಸಿಕೊಳ್ಳಬೇಕು.",
			"2": "<sup>2</sup> ನೀನು ಒಡೆದುಬಿಟ್ಟ ಆ ಮೊದಲಿನ ಹಲಿಗೆಗಳಲ್ಲಿ ಬರೆದಿದ್ದ ಮಾತುಗಳನ್ನು ನಾನು ಈ ಹಲಿಗೆಗಳ ಮೇಲೆ ಬರೆಯುವೆನು. ತರುವಾಯ ನೀನು ಅವುಗಳನ್ನು ಆ ಮಂಜೂಷದಲ್ಲಿಡಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"3": "<sup>3</sup> ಆದಕಾರಣ ನಾನು ಜಾಲೀಮರದಿಂದ ಮಂಜೂಷವನ್ನು ಮಾಡಿಸಿ ಮೊದಲಿನ ಕಲ್ಲಿನ ಹಲಿಗೆಗಳಂತೆ ಬೇರೆ ಎರಡು ಹಲಿಗೆಗಳನ್ನು ಸಿದ್ಧಪಡಿಸಿ ಆ ಎರಡು ಹಲಿಗೆಗಳನ್ನು ಕೈಯಲ್ಲಿ ಹಿಡಿದುಕೊಂಡು ಬೆಟ್ಟವನ್ನು ಹತ್ತಿದೆನು.",
			"4": "<sup>4</sup> ಯೆಹೋವನು ಬೆಟ್ಟದ ಮೇಲೆ ಅಗ್ನಿಜ್ವಾಲೆಯೊಳಗಿಂದ ನೀವು ಸಭೇಕೂಡಿದ ದಿನದಲ್ಲಿ ನಿಮ್ಮ ಸಂಗಡ ಹೇಳಿದ ಮಾತುಗಳನ್ನು ಅಂದರೆ ಆ ಹತ್ತು ಕಟ್ಟಳೆಗಳನ್ನು ಆತನು ಮೊದಲಿನಂತೆಯೇ ಆ ಹಲಿಗೆಗಳ ಮೇಲೆ ಬರೆದನು.",
			"5": "<sup>5</sup> ಯೆಹೋವನು ಆ ಹಲಿಗೆಗಳನ್ನು ನನ್ನ ವಶಕ್ಕೆ ಕೊಟ್ಟನಂತರ ನಾನು ಬೆಟ್ಟದಿಂದ ಇಳಿದುಬಂದು ನನ್ನಿಂದ ಸಿದ್ಧವಾಗಿದ್ದ ಮಂಜೂಷದಲ್ಲಿ ಆತನ ಅಪ್ಪಣೆಯ ಮೇರೆಗೆ ಅವುಗಳನ್ನು ಇಟ್ಟೆನು; ಅವು ಇಂದಿನವರೆಗೂ ಅದರಲ್ಲೇ ಅವೆ.",
			"6": "<sup>6</sup> (ಇಸ್ರಾಯೇಲ್ಯರು ಯಾಕಾನ್ಯರ ಬಾವಿಗಳ ಬಳಿಯಿಂದ ಪ್ರಯಾಣಮಾಡಿ ಮೋಸೇರಕ್ಕೆ ಬಂದರು. ಅಲ್ಲಿ ಆರೋನನು ಸಾಯಲಾಗಿ ಅವನಿಗೆ ಸಮಾಧಿಮಾಡಿದರು; ಅವನ ಮಗನಾದ ಎಲ್ಲಾಜಾರನು ಅವನಿಗೆ ಬದಲಾಗಿ ಮಹಾಯಾಜಕನಾದನು.",
			"7": "<sup>7</sup> ಅಲ್ಲಿಂದ ಅವರು ಗುದ್ಗೋದಕ್ಕೂ ಗುದ್ಗೋದದಿಂದ ನೀರಿನ ಹಳ್ಳಗಳುಳ್ಳ ಯೊಟ್ಬಾತಕ್ಕೂ ಪ್ರಯಾಣ ಮಾಡಿದರು.)",
			"8": "<sup>8</sup> ಆ ಕಾಲದಲ್ಲಿ ಯೆಹೋವನು ಲೇವಿ ಕುಲದವರನ್ನು ಪ್ರತ್ಯೇಕಿಸಿ ತನ್ನ ಒಡಂಬಡಿಕೆಯ ಮಂಜೂಷವನ್ನು ಹೊರುವದಕ್ಕೂ ತನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸೇವೆಮಾಡುವದಕ್ಕೂ ತನ್ನ ಹೆಸರಿನಿಂದ ಜನರನ್ನು ಆಶೀರ್ವದಿಸುವದಕ್ಕೂ ಅವರನ್ನು ನೇಮಿಸಿದನು. ಅವರು ಇಂದಿನವರೆಗೂ ಆ ಕೆಲಸವನ್ನು ನಡಿಸುತ್ತಾರೆ.",
			"9": "<sup>9</sup> ಆದದರಿಂದ ಮಿಕ್ಕ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ದೊರಕಿದಂತೆ ಲೇವಿಯರಿಗೆ ಸ್ವಂತವಾದ ಭೂಸ್ಥಿತಿಯು ದೊರೆಯಲಿಲ್ಲ. ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಅವರಿಗೆ ಹೇಳಿದಂತೆ ಯೆಹೋವನೇ ಅವರಿಗೆ ಸ್ವಾಸ್ತ್ಯ.",
			"10": "<sup>10</sup> ನಾನು ಮುಂಚಿನಂತೆ ಹಗಲಿರುಳು ನಾಲ್ವತ್ತು ದಿನವು ಬೆಟ್ಟದ ಮೇಲೆ ಇರಲಾಗಿ ಯೆಹೋವನು ಆ ಕಾಲದಲ್ಲಿಯೂ ನನ್ನ ಪ್ರಾರ್ಥನೆಯನ್ನು ಕೇಳಿ ನಿಮ್ಮನ್ನು ನಾಶ ಮಾಡಬೇಕೆಂಬ ಆಲೋಚನೆಯನ್ನು ಬಿಟ್ಟುಬಿಟ್ಟನು.",
			"11": "<sup>11</sup> ತರುವಾಯ ಯೆಹೋವನು ನನಗೆ - ನೀನು ಈ ಜನರ ಮುಂದುಗಡೆಯಲ್ಲಿ ಹೊರಡು; ನಾನು ಅವರ ಪಿತೃಗಳಿಗೆ ಪ್ರಮಾಣಮಾಡಿಕೊಟ್ಟ ದೇಶವನ್ನು ಇವರು ಸೇರಿ ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳಲಿ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"12": "<sup>12</sup> ಆದದರಿಂದ ಇಸ್ರಾಯೇಲ್ಯರೇ, ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿ ಎಲ್ಲಾ ವಿಷಯಗಳಲ್ಲಿಯೂ ಆತನು ಹೇಳುವ ಮಾರ್ಗದಲ್ಲೇ ನಡೆಯುತ್ತಾ ಆತನನ್ನು ಪ್ರೀತಿಸುತ್ತಾ ಸಂಪೂರ್ಣವಾದ ಹೃದಯದಿಂದಲೂ ಮನಸ್ಸಿನಿಂದಲೂ ಸೇವೆಮಾಡುತ್ತಾ",
			"13": "<sup>13</sup> ನಾನು ನಿಮ್ಮ ಮೇಲಿಗಾಗಿ ಈಗ ಬೋಧಿಸುವ ಆತನ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ಅನುಸರಿಸುತ್ತಾ ಇರುವದನ್ನೇ ಹೊರತು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮಿಂದ ಬೇರೇನು ಕೇಳಿಕೊಳ್ಳುತ್ತಾನೆ? ಆಲೋಚಿಸಿರಿ;",
			"14": "<sup>14</sup> ಉನ್ನತೋನ್ನತವಾದ ಆಕಾಶಮಂಡಲವೂ ಮತ್ತು ಭೂಮಿಯೂ ಅದರಲ್ಲಿರುವ ಎಲ್ಲವೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನವೇ;",
			"15": "<sup>15</sup> ಆದರೂ ಆತನು ನಿಮ್ಮ ಪಿತೃಗಳಲ್ಲಿ ಇಷ್ಟವುಳ್ಳವನಾಗಿ ಅವರನ್ನು ಪ್ರೀತಿಸಿದದರಿಂದ ಈಗ ನಿಮ್ಮ ಅನುಭವಕ್ಕೆ ಬಂದಂತೆ ಅವರ ತರುವಾಯ ಅವರ ಸಂತತಿಯವರಾದ ನಿಮ್ಮನ್ನೇ ಎಲ್ಲಾ ಜನಾಂಗಗಳೊಳಗಿಂದ ಆದುಕೊಂಡನು.",
			"16": "<sup>16</sup> ಆದದರಿಂದ ನೀವು ಆಜ್ಞೆಗೆ ಮಣಿಯದ ನಿಮ್ಮ ದುಸ್ವಭಾವವನ್ನು ಬಿಟ್ಟುಬಿಟ್ಟು ನಿಮ್ಮ ಹೃದಯದಲ್ಲೇ ಸುನ್ನತಿ ಮಾಡಿಕೊಳ್ಳಿರಿ.",
			"17": "<sup>17</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ದೇವಾಧಿದೇವನಾಗಿಯೂ ಕರ್ತರ ಕರ್ತನಾಗಿಯೂ ಇದ್ದಾನೆ; ಆತನು ಪರಮದೇವರೂ ಪರಾಕ್ರಮಿಯೂ ಭಯಂಕರನೂ ಆಗಿದ್ದಾನೆ. ಆತನು ದಾಕ್ಷಿಣ್ಯ ನೋಡುವವನಲ್ಲ, ಲಂಚತೆಗೆದುಕೊಳ್ಳುವವನಲ್ಲ.",
			"18": "<sup>18</sup> ಆತನು ತಾಯಿತಂದೆಯಿಲ್ಲದವರ ಮತ್ತು ವಿಧವೆಯರ ನ್ಯಾಯವನ್ನು ಸ್ಥಾಪಿಸುತ್ತಾನೆ; ಪರದೇಶಿಗಳಾದವರಲ್ಲಿ ಪ್ರೀತಿಯಿಟ್ಟು ಅವರಿಗೆ ಅನ್ನವಸ್ತ್ರಗಳನ್ನು ಕೊಡುತ್ತಾನೆ.",
			"19": "<sup>19</sup> ಐಗುಪ್ತದೇಶದಲ್ಲಿ ನೀವೇ ಪರದೇಶಿಗಳಾಗಿದ್ದದ್ದನ್ನು ಜ್ಞಾಪಿಸಿಕೊಂಡು ಪರದೇಶದವರಲ್ಲಿ ಪ್ರೀತಿಯಿಡಿರಿ.",
			"20": "<sup>20</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿ ಆತನನ್ನೇ ಸೇವಿಸಬೇಕು; ಆತನನ್ನು ಹೊಂದಿಕೊಂಡು ಆತನ ಹೆಸರಿನ ಮೇಲೆಯೇ ಪ್ರಮಾಣ ಮಾಡಬೇಕು.",
			"21": "<sup>21</sup> ಆತನೊಬ್ಬನೇ ನಿಮ್ಮ ಸ್ತೋತ್ರಕ್ಕೆ ಪಾತ್ರನು; ಆತನು ನಿಮ್ಮ ದೇವರು. ನೀವು ನೋಡಿದ ಆ ಮಹಾಭಯಂಕರವಾದ ಮಹತ್ಕಾರ್ಯಗಳನ್ನು ನಿಮಗೋಸ್ಕರ ನಡಿಸಿದವನು ಆತನೇ.",
			"22": "<sup>22</sup> ನಿಮ್ಮ ಪಿತೃಗಳು ಎಪ್ಪತ್ತು ಮಂದಿ ಮಾತ್ರ ಐಗುಪ್ತದೇಶಕ್ಕೆ ಹೋದರು; ಈಗಲೋ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಆಕಾಶದ ನಕ್ಷತ್ರಗಳಷ್ಟು ಅಸಂಖ್ಯರಾಗಿರುವಂತೆ ಮಾಡಿದ್ದಾನಲ್ಲಾ."
		},
		"11": {
			"1": "<sup>1</sup> ಆದದರಿಂದ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಲ್ಲಿ ಪ್ರೀತಿಯಿಟ್ಟು ಆತನ ನಿಯಮಗಳನ್ನು ಕೈಕೊಂಡು ಆತನ ಆಜ್ಞಾವಿಧಿನಿರ್ಣಯಗಳನ್ನು ಯಾವಾಗಲೂ ಅನುಸರಿಸಬೇಕು.",
			"2": "<sup>2</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಶಿಕ್ಷಣಕ್ರಮವನ್ನೂ ಮಹಿಮೆಯನ್ನೂ ಭುಜಬಲವನ್ನೂ ಶಿಕ್ಷಾಹಸ್ತವನ್ನೂ",
			"3": "<sup>3</sup> ಅಂದರೆ ಆತನು ಐಗುಪ್ತದೇಶದಲ್ಲಿ ಫರೋಹನನ್ನೂ ಅವನ ದೇಶವನ್ನೂ ಶಿಕ್ಷಿಸುವದಕ್ಕಾಗಿ ನಡಿಸಿದ ಉತ್ಪಾತ ಮಹತ್ಕಾರ್ಯಗಳನ್ನೂ;",
			"4": "<sup>4</sup> ಐಗುಪ್ತ್ಯರ ದಂಡು ಅಂದರೆ ಅವರ ರಥಾಶ್ವಬಲಗಳು ನಿಮ್ಮನ್ನು ಹಿಂದಟ್ಟಿ ಬಂದಾಗ ಆತನು ಕೆಂಪು ಸಮುದ್ರದ ನೀರನ್ನು ಅವರ ಮೇಲೆ ಬರಮಾಡಿ ಸಂಪೂರ್ಣವಾಗಿ ನಾಶಮಾಡಿದ್ದನ್ನೂ;",
			"5": "<sup>5</sup> ನೀವು ಈ ಸ್ಥಳಕ್ಕೆ ಸೇರುವತನಕ ಆತನು ನಿಮಗೋಸ್ಕರ ಅರಣ್ಯದಲ್ಲಿ ಉಪಕಾರ ಮಾಡಿದ್ದನ್ನೂ;",
			"6": "<sup>6</sup> ರೂಬೇನನ ಮೊಮ್ಮಕ್ಕಳೂ ಎಲೀಯಾಬನ ಮಕ್ಕಳೂ ಆದ ದಾತಾನ್ ಅಬೀರಾಮರು [ತಿರುಗಿಬಿದ್ದಾಗ] ಭೂಮಿಯು ಬಾಯ್ದೆರೆದು ಅವರನ್ನೂ ಅವರ ಮನೆಯವರನ್ನೂ ಡೇರೆಗಳನ್ನೂ ಅವರಿಗೆ ಸೇರಿದ ಎಲ್ಲಾ ಪ್ರಾಣಿಗಳನ್ನೂ ಇಸ್ರಾಯೇಲ್ಯರ ನಡುವೆ ನುಂಗಿಬಿಟ್ಟದ್ದನ್ನೂ ಈಗ ಜ್ಞಾಪಕಮಾಡಿಕೊಳ್ಳಿರಿ. ಈ ಕಾರ್ಯಗಳನ್ನು ನೋಡದೆ ತಿಳಿಯದೆ ಇರುವ ನಿಮ್ಮ ಮಕ್ಕಳಿಗೆ ನಾನು ಈ ಮಾತನ್ನು ಹೇಳುವದಿಲ್ಲ;",
			"7": "<sup>7</sup> ಯೆಹೋವನು ನಡಿಸಿದ ಆ ವಿಶೇಷಕಾರ್ಯಗಳನ್ನು ಪ್ರತ್ಯಕ್ಷವಾಗಿ ನೋಡಿದವರಾದ ನಿಮಗೇ ಹೇಳುತ್ತಾ ಇದ್ದೇನೆ.",
			"8": "<sup>8</sup> ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ಈ ಧರ್ಮೋಪದೇಶವನ್ನೆಲ್ಲಾ ನೀವು ಅನುಸರಿಸಬೇಕು. ಆಗ ನೀವು ಬಲವುಳ್ಳವರಾಗಿ ಈ ಹೊಳೆ ದಾಟಿ ಆಚೆಯಿರುವ ದೇಶವನ್ನು ಸೇರಿ ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವಿರಿ;",
			"9": "<sup>9</sup> ಮತ್ತು ಯೆಹೋವನು ನಿಮ್ಮ ಪಿತೃಗಳಿಗೂ ಅವರ ಸಂತತಿಗೂ ಕೊಡುತ್ತೇನೆಂದು ಪ್ರಮಾಣ ಮಾಡಿದ ಆ ದೇಶದಲ್ಲಿ ನೀವು ಬಹುಕಾಲ ಬದುಕಿಕೊಳ್ಳುವಿರಿ; ಅದು ಹಾಲೂ ಜೇನೂ ಹರಿಯುವ ದೇಶ.",
			"10": "<sup>10</sup> ನೀವು ಸ್ವತಂತ್ರಿಸಿಕೊಳ್ಳುವದಕ್ಕೆ ಹೋಗುವ ದೇಶವು ಬಿಟ್ಟುಬಂದ ಐಗುಪ್ತದೇಶದ ಹಾಗಲ್ಲ. ಐಗುಪ್ತದೇಶದಲ್ಲಿ ನೀವು ಹೊಲಗದ್ದೆಗಳಲ್ಲಿ ಬೀಜ ಬಿತ್ತಿದ ಮೇಲೆ ಕಾಯಿಪಲ್ಯಗಳ ತೋಟವನ್ನು ಸಾಗುವಳಿಮಾಡುವ ರೀತಿಯಲ್ಲಿ ಏತವನ್ನು ತುಳಿದು ನೀರುಕಟ್ಟುತ್ತಿದ್ದಿರಿ.",
			"11": "<sup>11</sup> ನೀವು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವದಕ್ಕೆ ಹೋಗುವ ದೇಶವೋ ಹಳ್ಳದಿನ್ನೆಗಳ ದೇಶ; ಆಕಾಶದಿಂದ ಮಳೆಬಿದ್ದ ಪ್ರಕಾರವೇ ಅದಕ್ಕೆ ನೀರು ದೊರೆಯುವದು.",
			"12": "<sup>12</sup> ಅದು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಪರಾಂಬರಿಸುವ ದೇಶ; ಸಂವತ್ಸರದ ಪ್ರಾರಂಭ ಮೊದಲುಗೊಂಡು ಕೊನೆಯವರೆಗೂ ಆತನು ಅದನ್ನು ಸದಾ ಕಟಾಕ್ಷಿಸುವನು.",
			"13": "<sup>13</sup> ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ಆಜ್ಞೆಗಳಿಗೆ ನೀವು ಲಕ್ಷ್ಯಕೊಟ್ಟು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಸಂಪೂರ್ಣ ಹೃದಯದಿಂದಲೂ ಮನಸ್ಸಿನಿಂದಲೂ ಪ್ರೀತಿಸಿ ಆತನನ್ನು ಸೇವಿಸಿದರೆ",
			"14": "<sup>14</sup> ನಿಮಗೆ ಗೋದಿ, ದ್ರಾಕ್ಷೆ, ಎಣ್ಣೇಕಾಯಿ ಇವುಗಳ ಬೆಳೆ ಚೆನ್ನಾಗಿ ಉಂಟಾಗುವಂತೆ ಆತನು ನಿಮ್ಮ ಭೂಮಿಗೆ ಬೇಕಾದ ಮುಂಗಾರು ಹಿಂಗಾರುಗಳನ್ನು ಸರಿಯಾಗಿ ನಡಿಸುವನು;",
			"15": "<sup>15</sup> ಅಡವಿಯಲ್ಲಿ ದನಗಳಿಗೆ ಬೇಕಾದಷ್ಟು ಹುಲ್ಲನ್ನು ಕೊಡುವನು; ನೀವು ಆಹಾರದಿಂದ ತೃಪ್ತರಾಗಿರುವಿರಿ.",
			"16": "<sup>16</sup> ಆದರೆ ನೀವು ಎಚ್ಚರದಿಂದಿರಬೇಕು; ನೀವು ಭ್ರಾಂತಿಗೊಂಡು ಯೆಹೋವನು ಹೇಳಿದ ಮಾರ್ಗವನ್ನು ಬಿಟ್ಟು ಇತರ ದೇವರುಗಳನ್ನು ಅವಲಂಬಿಸಿ ಪೂಜಿಸಿದರೆ",
			"17": "<sup>17</sup> ಆತನು ನಿಮ್ಮ ಮೇಲೆ ಸಿಟ್ಟುಗೊಂಡು ಮಳೆಬಾರದಂತೆ ಆಕಾಶವನ್ನು ಮುಚ್ಚಿಬಿಟ್ಟಾನು; ಆಗ ಭೂಮಿಯಲ್ಲಿ ಬೆಳೆಯಾಗದೆ ಯೆಹೋವನು ನಿಮಗೆ ಕೊಡುವ ಆ ಉತ್ತಮದೇಶದಲ್ಲಿ ನೀವು ಉಳಿಯದೆ ಬೇಗ ನಾಶವಾಗಿ ಹೋಗುವಿರಿ.",
			"18": "<sup>18</sup> ಆದದರಿಂದ ನೀವು ಈ ನನ್ನ ಮಾತುಗಳನ್ನು ನಿಮ್ಮ ಹೃದಯದಲ್ಲಿಯೂ ಮನಸ್ಸಿನಲ್ಲಿಯೂ ಇಟ್ಟುಕೊಳ್ಳಬೇಕು; ಇವುಗಳನ್ನು ಜ್ಞಾಪಕಾರ್ಥವಾಗಿ ಕೈಗೆ ಕಟ್ಟಿಕೊಳ್ಳಬೇಕು; ಇವು ನಿಮ್ಮ ಹುಬ್ಬುಗಳ ನಡುವೆ ಕಟ್ಟಿಕೊಳ್ಳುವ ಜ್ಞಾಪಕದ ಪಟ್ಟಿಯಂತಿರಬೇಕು.",
			"19": "<sup>19</sup> ನೀವು ಮನೆಯಲ್ಲಿರುವಾಗಲೂ ಪ್ರಯಾಣದಲ್ಲಿರುವಾಗಲೂ ಮಲಗುವಾಗಲೂ ಏಳುವಾಗಲೂ ಇವುಗಳ ವಿಷಯದಲ್ಲಿ ಮಾತಾಡುತ್ತಾ ನಿಮ್ಮ ಮಕ್ಕಳಿಗೆ ಇವುಗಳನ್ನು ಅಭ್ಯಾಸಮಾಡಿಸಬೇಕು.",
			"20": "<sup>20</sup> ನಿಮ್ಮ ಮನೇಬಾಗಲಿನ ನಿಲುವುಪಟ್ಟಿಗಳಲ್ಲಿಯೂ ಹೆಬ್ಬಾಗಲುಗಳ ಮೇಲೆಯೂ ಇವುಗಳನ್ನು ಬರೆಯಬೇಕು.",
			"21": "<sup>21</sup> ಹೀಗಾದರೆ ಭೂಮಿಯ ಮೇಲೆ ಆಕಾಶವು ಎಷ್ಟು ಕಾಲ ನಿಂತಿರುವದೋ ಅಷ್ಟು ಕಾಲ ನೀವೂ ನಿಮ್ಮ ಸಂತತಿಯವರೂ ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ಯೆಹೋವನು ಪ್ರಮಾಣಪೂರ್ವಕವಾಗಿ ವಾಗ್ದಾನ ಮಾಡಿದ ಆ ದೇಶದಲ್ಲಿ ಬಾಳುವಿರಿ.",
			"22": "<sup>22</sup> ನಾನು ಬೋಧಿಸುವ ಈ ಧರ್ಮೋಪದೇಶವನ್ನೆಲ್ಲಾ ನೀವು ಅನುಸರಿಸುವವರಾಗಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಪ್ರೀತಿಸಿ ಎಲ್ಲಾ ವಿಷಯಗಳಲ್ಲಿಯೂ ಆತನು ಹೇಳುವ ಮಾರ್ಗದಲ್ಲೇ ನಡೆದು ಆತನನ್ನೇ ಹೊಂದಿಕೊಂಡಿದ್ದರೆ",
			"23": "<sup>23</sup> ಆತನು ಆ ಜನಾಂಗಗಳನ್ನೆಲ್ಲಾ ನಿಮ್ಮ ಎದುರಿನಿಂದ ಹೊರಡಿಸುವನು; ನಿಮಗಿಂತಲೂ ಮಹಾಬಲಿಷ್ಠ ಜನಾಂಗಗಳ ದೇಶವನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವಿರಿ.",
			"24": "<sup>24</sup> ನೀವು ಹೆಜ್ಜೆಯಿಡುವ ಎಲ್ಲಾ ಸ್ಥಳಗಳೂ ನಿಮಗಾಗುವವು; ಅರಣ್ಯ ಮೊದಲುಗೊಂಡು ಲೆಬನೋನ್ ಪರ್ವತದವರೆಗೂ ಮತ್ತು ಯೂಫ್ರೇಟೀಸ್ ನದಿಯಿಂದ ಪಶ್ಚಿಮ ಸಮುದ್ರದವರೆಗೂ ನಿಮ್ಮ ಸೀಮೆ ವ್ಯಾಪಿಸುವದು.",
			"25": "<sup>25</sup> ಯಾರೂ ನಿಮ್ಮ ಮುಂದೆ ನಿಲ್ಲುವದಿಲ್ಲ. ತಾನು ನಿಮಗೆ ಹೇಳಿದಂತೆ ನೀವು ಕಾಲಿಡುವ ಎಲ್ಲಾ ಪ್ರದೇಶಗಳ ಜನಗಳಿಗೂ ನಿಮ್ಮಿಂದ ದಿಗಿಲೂ ಹೆದರಿಕೆಯೂ ಉಂಟಾಗುವಂತೆ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಮಾಡುವನು.",
			"26": "<sup>26</sup> ನೋಡಿರಿ, ಈ ಹೊತ್ತು ನಾನು ಆಶೀರ್ವಾದವನ್ನೂ ಶಾಪವನ್ನೂ ನಿಮ್ಮ ಮುಂದೆ ಇಡುತ್ತಾ ಇದ್ದೇನೆ.",
			"27": "<sup>27</sup> ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಆಜ್ಞೆಗಳಿಗೆ ನೀವು ವಿಧೇಯರಾಗಿ ನಡೆದರೆ ಆಶೀರ್ವಾದವೂ",
			"28": "<sup>28</sup> ಈ ಆಜ್ಞೆಗಳಿಗೆ ವಿಧೇಯರಾಗದೆ ನಾನು ಈಗ ಬೋಧಿಸುವ ಮಾರ್ಗವನ್ನು ಬಿಟ್ಟು ನಿಮಗೆ ಗೊತ್ತಿಲ್ಲದ ಇತರ ದೇವರುಗಳನ್ನು ಅವಲಂಬಿಸಿದರೆ ಶಾಪವೂ ನಿಮಗುಂಟಾಗುವವು.",
			"29": "<sup>29</sup> ನೀವು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವದಕ್ಕೆ ಹೋಗುವ ಆ ದೇಶಕ್ಕೆ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಸೇರಿಸಿದನಂತರ ನೀವು ಗೆರಿಜ್ಜೀಮ್ ಬೆಟ್ಟದ ಮೇಲೆ ಆಶೀರ್ವಾದವನ್ನೂ ಏಬಾಲ್ ಬೆಟ್ಟದ ಮೇಲೆ ಶಾಪವನ್ನೂ ಪ್ರಕಟಿಸಬೇಕು.",
			"30": "<sup>30</sup> ಆ ಬೆಟ್ಟಗಳು ಯೊರ್ದನ್ ಹೊಳೆಯ ಆಚೆ, ಪಡುವಣ ದಾರಿಯ ಪಶ್ಚಿಮದಲ್ಲಿ ಕಾನಾನ್ಯರು ವಾಸಿಸುವ ಅರಾಬಾ ಪ್ರದೇಶದಲ್ಲಿ ಗಿಲ್ಗಾಲಿಗೆ ಎದುರಾಗಿ ಮೋರೆ ಎಂಬ ವೃಕ್ಷದ ಬಳಿಯಲ್ಲಿ ಅವೆ.",
			"31": "<sup>31</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಕೊಡುವ ದೇಶವನ್ನು ಸ್ವಾಧೀನ ಮಾಡಿಕೊಳ್ಳುವದಕ್ಕೆ ನೀವು ಯೊರ್ದನ್ ಹೊಳೆಯನ್ನು ದಾಟುತ್ತೀರಲ್ಲಾ.",
			"32": "<sup>32</sup> ನೀವು ಆ ದೇಶವನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡು ಅದರಲ್ಲಿ ವಾಸಮಾಡುವಾಗ ನಾನು ಈಗ ನಿಮಗೆ ತಿಳಿಸುವ ಎಲ್ಲಾ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ಅನುಸರಿಸಿ ನಡೆಯಲೇಬೇಕು."
		},
		"12": {
			"1": "<sup>1</sup> ನಿಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಸ್ವದೇಶವಾಗುವದಕ್ಕೆ ಕೊಡುವ ದೇಶದಲ್ಲಿ ನೀವು ಭೂಮಿಯ ಮೇಲಿರುವ ದಿನಗಳಲ್ಲೆಲ್ಲಾ ಅನುಸರಿಸಬೇಕಾದ ಆಜ್ಞಾವಿಧಿಗಳು ಇವೇ -",
			"2": "<sup>2</sup> ನೀವು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವ ದೇಶದ ಜನಾಂಗಗಳು ದೊಡ್ಡ ಬೆಟ್ಟಗಳ ಮೇಲೆಯೂ ದಿನ್ನೆಗಳ ಮೇಲೆಯೂ ಹರಡಿಕೊಂಡ ಮರಗಳ ಕೆಳಗೆಯೂ ತಮ್ಮ ದೇವರುಗಳನ್ನು ಆರಾಧಿಸುತ್ತಾರಷ್ಟೆ; ಆ ಸ್ಥಳಗಳನ್ನೆಲ್ಲಾ ನೀವು ಅಗತ್ಯವಾಗಿ ನಾಶಮಾಡಬೇಕು.",
			"3": "<sup>3</sup> ಅವರ ಬಲಿಪೀಠಗಳನ್ನು ಕೆಡವಿ ಅವರ ಕಲ್ಲುಕಂಬಗಳನ್ನು ಒಡೆದು ಅಶೇರವೆಂಬ ವಿಗ್ರಹಸ್ತಂಭಗಳನ್ನು ಸುಟ್ಟು ಅವರ ದೇವತಾಪ್ರತಿಮೆಗಳನ್ನು ಕಡಿದು ಬಿಟ್ಟು ಆ ದೇವರುಗಳ ಹೆಸರೇ ಉಳಿಯದಂತೆ ಮಾಡಿಬಿಡಬೇಕು.",
			"4": "<sup>4</sup> ಅವರ ರೀತಿಯಲ್ಲಿ ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಪೂಜಿಸಬಾರದು.",
			"5": "<sup>5</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ತನ್ನ ಹೆಸರನ್ನು ಸ್ಥಾಪಿಸಿಕೊಳ್ಳುವದಕ್ಕೂ ತಾನು ವಾಸಮಾಡುವದಕ್ಕೂ ನಿಮ್ಮ ಎಲ್ಲಾ ಕುಲಗಳೊಳಗಿಂದ ಆದುಕೊಳ್ಳುವ ಸ್ಥಳದಲ್ಲೇ ನೀವು ಆತನ ದರ್ಶನಕ್ಕಾಗಿ ಕೂಡಬೇಕು.",
			"6": "<sup>6</sup> ಆ ಸ್ಥಳಕ್ಕೆ ಮಾತ್ರ ನೀವು ಸಮರ್ಪಿಸುವ ಸರ್ವಾಂಗಹೋಮಗಳೇ ಮುಂತಾದ ಯಜ್ಞಗಳ ಪಶುಗಳನ್ನೂ ನಿಮ್ಮ ಬೆಳೆಯಲ್ಲಿ ದಶಮಾಂಶವನ್ನೂ ಯಾಜಕರಿಗೋಸ್ಕರ ಪ್ರತ್ಯೇಕಿಸುವ ಪದಾರ್ಥಗಳನ್ನೂ ಹರಕೇ ಮುಡುಪುಗಳನ್ನೂ ಕಾಣಿಕೆಗಳನ್ನೂ ದನಕುರಿಗಳ ಚೊಚ್ಚಲು ಮರಿಗಳನ್ನೂ ತಂದು",
			"7": "<sup>7</sup> ಅಲ್ಲಿಯೇ ಆತನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಊಟಮಾಡಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮ ಪ್ರಯತ್ನಗಳನ್ನು ಕೈಗೂಡುವಂತೆ ಮಾಡಿದ್ದಕ್ಕಾಗಿ ನೀವೂ ನಿಮ್ಮ ಮನೆಗಳವರೂ ಸಂತೋಷದಿಂದಿರಬೇಕು.",
			"8": "<sup>8</sup> ನಾವು ಈಗ ಈ ಸ್ಥಳದಲ್ಲಿ ನಡೆಯುವಂತೆ ಎಲ್ಲರೂ ತಮ್ಮತಮ್ಮ ಮನಸ್ಸಿಗೆ ತೋರಿದಂತೆ ನಡೆಯಕೂಡದು;",
			"9": "<sup>9</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಕೊಡುವ ಸ್ವಾಸ್ತ್ಯಕ್ಕೆ ಸೇರಿ ನೀವು ಇನ್ನು ವಿಶ್ರಾಂತಿ ಹೊಂದಲಿಲ್ಲವಲ್ಲಾ.",
			"10": "<sup>10</sup> ಆದರೆ ನೀವು ಯೊರ್ದನ್ ಹೊಳೆಯನ್ನು ದಾಟಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಸ್ವಾಸ್ತ್ಯವಾಗಿ ಕೊಡುವ ಆ ದೇಶದಲ್ಲಿ ಮನೆಮಾಡಿಕೊಂಡಿರುವಾಗ ನಿಮ್ಮ ಸುತ್ತಲು ಶತ್ರುಗಳು ಯಾರೂ ಇಲ್ಲದಂತೆ ಯೆಹೋವನು ಮಾಡಿದ್ದರಿಂದ ನೀವು ನಿರ್ಭಯವಾಗಿರುವ ಕಾಲದಲ್ಲಿ",
			"11": "<sup>11</sup> ನಾನು ನಿಮಗೆ ಆಜ್ಞಾಪಿಸಿದ ಎಲ್ಲಾ ಕಾಣಿಕೆಗಳನ್ನು ಅಂದರೆ ಸರ್ವಾಂಗಹೋಮಗಳೇ ಮುಂತಾದ ಯಜ್ಞಪಶುಗಳನ್ನೂ ಬೆಳೆಯ ದಶಮಾಂಶಗಳನ್ನೂ ಯೆಹೋವನಿಗೋಸ್ಕರ ಪ್ರತ್ಯೇಕಿಸುವ ಪದಾರ್ಥಗಳನ್ನೂ ಹರಕೆಮಾಡಿದ ವಿಶೇಷವಾದ ಕಾಣಿಕೆಗಳನ್ನೂ ಇವುಗಳನ್ನೆಲ್ಲಾ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ತನ್ನ ಹೆಸರನ್ನು ಸ್ಥಾಪಿಸುವದಕ್ಕೆ",
			"12": "<sup>12</sup> ಆದುಕೊಳ್ಳುವ ಸ್ಥಳಕ್ಕೇ ತಂದು ನೀವೂ ನಿಮ್ಮ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳೂ ದಾಸ ದಾಸಿಯರೂ ನಿಮ್ಮೊಡನೆ ಸ್ವಾಸ್ತ್ಯವನ್ನು ಹೊಂದದೆ ಇರುವ ನಿಮ್ಮ ಊರಿನ ಲೇವಿಯರೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸಂತೋಷವಾಗಿರಬೇಕು.",
			"13": "<sup>13</sup> ನೀವು ಕಂಡ ಕಂಡ ಸ್ಥಳಗಳಲ್ಲಿ ಸರ್ವಾಂಗಹೋಮಗಳನ್ನು ಸಮರ್ಪಿಸಬಾರದು, ನೋಡಿಕೊಳ್ಳಿರಿ.",
			"14": "<sup>14</sup> ನಿಮ್ಮ ಕುಲಗಳಲ್ಲಿ ಯಾವದೋ ಒಂದು ಕುಲದಲ್ಲಿ ಯೆಹೋವನು ಆದುಕೊಳ್ಳುವ ಸ್ಥಳದಲ್ಲೇ ನಿಮ್ಮ ಸರ್ವಾಂಗಹೋಮಗಳನ್ನು ಸಮರ್ಪಿಸಬೇಕು; ಅಲ್ಲಿಯೇ ನಾನು ಆಜ್ಞಾಪಿಸಿದ ಎಲ್ಲಾ ಆಚಾರಗಳನ್ನು ನಡಿಸಬೇಕು.",
			"15": "<sup>15</sup> ಆದರೆ ನೀವು ಎಲ್ಲಾ ಊರುಗಳಲ್ಲಿಯೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಅಭಿವೃದ್ಧಿಪಡಿಸಿದ ಮೇರೆಗೆ ಪಶುಗಳನ್ನು ಇಷ್ಟಾನುಸಾರವಾಗಿ ಕೊಯಿದು ಊಟಮಾಡಬಹುದು. ಶುದ್ಧರೂ ಅಶುದ್ಧರೂ ಜಿಂಕೆದುಪ್ಪಿಗಳ ಮಾಂಸವನ್ನು ತಿನ್ನುವ ಪ್ರಕಾರ ಇವುಗಳನ್ನೂ ತಿನ್ನಬಹುದು.",
			"16": "<sup>16</sup> ರಕ್ತವನ್ನು ಮಾತ್ರ ಭುಜಿಸಬಾರದು; ಅದನ್ನು ನೀರಿನಂತೆ ಭೂಮಿಯ ಮೇಲೆ ಸುರಿದುಬಿಡಬೇಕು.",
			"17": "<sup>17</sup> ಧಾನ್ಯ, ದ್ರಾಕ್ಷೆ, ಎಣ್ಣೇಕಾಯಿ ಈ ಬೆಳೆಗಳ ದಶಮಾಂಶಗಳನ್ನೂ ದನಕುರಿಗಳ ಚೊಚ್ಚಲುಮರಿಗಳನ್ನೂ ಹರಕೆಮಾಡಿದ ಪದಾರ್ಥಗಳನ್ನೂ ಕಾಣಿಕೆಗಳನ್ನೂ ಯಾಜಕರಿಗೋಸ್ಕರ ಪ್ರತ್ಯೇಕಿಸಿದ ಪದಾರ್ಥಗಳನ್ನೂ ನಿಮ್ಮ ನಿಮ್ಮ ಊರುಗಳಲ್ಲಿ ತಿನ್ನಬಾರದು.",
			"18": "<sup>18</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಆದುಕೊಳ್ಳುವ ಸ್ಥಳದಲ್ಲೇ ಆತನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಅವುಗಳನ್ನು ಊಟಮಾಡಬೇಕು. ನಿಮ್ಮ ಎಲ್ಲಾ ಪ್ರಯತ್ನಗಳು ಕೈಗೂಡಿದ್ದಕ್ಕಾಗಿ ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸಂತೋಷದಿಂದಿದ್ದು ನೀವೂ ನಿಮ್ಮ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳೂ ಆಳುಗಳೂ ನಿಮ್ಮ ಊರಲ್ಲಿರುವ ಲೇವಿಯರೂ ಇಂಥವುಗಳನ್ನು ಊಟಮಾಡಬೇಕು.",
			"19": "<sup>19</sup> ನೀವು ನಿಮ್ಮ ದೇಶದಲ್ಲಿ ಇರುವ ತನಕ ಲೇವಿಯರನ್ನು ಕೈಬಿಡಬಾರದು; ಜ್ಞಾಪಕವಿರಲಿ.",
			"20": "<sup>20</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ವಾಗ್ದಾನ ಮಾಡಿದಂತೆ ನಿಮ್ಮ ರಾಜ್ಯವನ್ನು ವಿಸ್ತರಿಸಿದಾಗ ನೀವು ಮಾಂಸಾಹಾರವನ್ನು ಮಾಡುವದಕ್ಕೆ ಇಷ್ಟಪಟ್ಟರೆ ಮಾಡಬಹುದು.",
			"21": "<sup>21</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ತನ್ನ ಹೆಸರನ್ನು ಸ್ಥಾಪಿಸಿಕೊಳ್ಳುವದಕ್ಕಾಗಿ ಆದುಕೊಂಡ ಸ್ಥಳವು ನಿಮಗೆ ದೂರವಾದರೆ ಆತನು ನಿಮಗೆ ಅನುಗ್ರಹಿಸಿದ ದನಕುರಿಗಳಲ್ಲಿ ಬೇಕಾದಷ್ಟನ್ನು ನಾನು ಅಪ್ಪಣೆಕೊಟ್ಟ ಮೇರೆಗೆ ನೀವು ಕೊಯಿದು ನಿಮ್ಮ ಊರಲ್ಲೇ ಊಟಮಾಡಬಹುದು.",
			"22": "<sup>22</sup> ಜಿಂಕೆದುಪ್ಪಿಗಳನ್ನು ಶುದ್ಧರೂ ಅಶುದ್ಧರೂ ತಿನ್ನುವ ಪ್ರಕಾರ ದನಕುರಿಗಳನ್ನೂ ತಿನ್ನಬಹುದು.",
			"23": "<sup>23</sup> ಆದರೆ ರಕ್ತವು ಜೀವಾಧಾರವಾದದರಿಂದ ಮಾಂಸದೊಡನೆ ರಕ್ತವನ್ನು ಭುಜಸಲೇ ಕೂಡದೆಂಬದನ್ನು ಜ್ಞಾಪಕದಲ್ಲಿಡಿರಿ. ಪಶುಮಾಂಸದೊಡನೆ ಅದರ ಜೀವಾಧಾರವಾದದ್ದನ್ನೂ ತಿನ್ನಬಾರದಷ್ಟೆ;",
			"24": "<sup>24</sup> ಅದನ್ನು ಭುಜಿಸದೆ ನೀರಿನಂತೆ ಭೂಮಿಯ ಮೇಲೆ ಸುರಿದುಬಿಡಬೇಕು.",
			"25": "<sup>25</sup> ನೀವು ಅದನ್ನು ಭುಜಿಸದೆ ಯೆಹೋವನಿಗೆ ಮೆಚ್ಚಿಗೆಯಾಗಿರುವದನ್ನು ನಡಿಸಿದರೆ ನಿಮಗೂ ನಿಮ್ಮ ಸಂತತಿಯವರಿಗೂ ಶುಭವುಂಟಾಗುವದು.",
			"26": "<sup>26</sup> ಆದರೆ ನಿಮ್ಮ ಬಳಿಯಲ್ಲಿರುವ ದೇವರ ವಸ್ತುಗಳನ್ನೂ ಹರಕೆಮಾಡಿದ್ದನ್ನೂ ಯೆಹೋವನು ಆದುಕೊಂಡ ಸ್ಥಳಕ್ಕೇ ತೆಗೆದುಕೊಂಡು ಹೋಗಬೇಕು.",
			"27": "<sup>27</sup> ಸರ್ವಾಂಗಹೋಮಗಳನ್ನು ಸಮರ್ಪಿಸುವಾಗ ರಕ್ತಮಾಂಸಗಳೆರಡನ್ನೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಯಜ್ಞವೇದಿಯಲ್ಲಿ ದಹಿಸಿಬಿಡಬೇಕು. [ಸಮಾಧಾನಯಜ್ಞಗಳನ್ನು ಮಾಡುವಾಗ ರಕ್ತವನ್ನು ಆತನ ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಸುರಿದುಬಿಟ್ಟು ಮಾಂಸವನ್ನು ಊಟಮಾಡಬೇಕು.",
			"28": "<sup>28</sup> ನಾನು ಬೋಧಿಸುವ ಈ ಎಲ್ಲಾ ಆಜ್ಞೆಗಳಿಗೆ ನೀವು ಚೆನ್ನಾಗಿ ಕಿವಿಗೊಟ್ಟು ಅವುಗಳನ್ನು ಅನುಸರಿಸಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಒಳ್ಳೇದೂ ಯುಕ್ತವೂ ಆಗಿರುವದನ್ನು ಮಾಡಿದರೆ ನಿಮಗೂ ನಿಮ್ಮ ಸಂತತಿಯವರಿಗೂ ಸದಾಕಾಲ ಶುಭವುಂಟಾಗುವದು.",
			"29": "<sup>29</sup> ನೀವು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವದಕ್ಕೆ ಹೋಗುವ ದೇಶದ ಜನಾಂಗಗಳನ್ನು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮ ಮುಂದೆ ನಾಶಮಾಡಿದ ತರುವಾಯ ನೀವು ಅವರ ದೇಶವನ್ನು ತೆಗೆದುಕೊಂಡು ಅದರಲ್ಲಿ ವಾಸವಾಗಿರುವಾಗ",
			"30": "<sup>30</sup> ನೀವು ಭ್ರಮೆಗೊಂಡು ನಿಮ್ಮ ಎದುರಿನಿಂದ ನಾಶವಾಗಿ ಹೋದವರ ದುಷ್ಪದ್ಧತಿಗಳನ್ನು ಅನುಸರಿಸಬಾರದು ನೋಡಿರಿ. ನೀವು - ಈ ದೇಶದ ಜನರು ತಮ್ಮ ದೇವರುಗಳನ್ನು ಹೇಗೆ ಸೇವಿಸುತ್ತಿದ್ದರು? ಹಾಗೆಯೇ ನಾವೂ ಸೇವಿಸುವೆವು ಎಂದು ಹೇಳಿಕೊಳ್ಳುವವರಾಗಿ ಅವರ ದೇವರುಗಳ ವಿಷಯದಲ್ಲಿ ವಿಚಾರಣೆಯನ್ನು ಎಷ್ಟು ಮಾತ್ರವೂ ಮಾಡಬಾರದು.",
			"31": "<sup>31</sup> ಯೆಹೋವನಿಗೆ ಅಸಹ್ಯವಾಗಿರುವ ಹಲವು ಹೇಸಿಗೆ ಕೆಲಸಗಳನ್ನು ಅವರು ತಮ್ಮ ದೇವತೆಗಳಿಗೋಸ್ಕರ ನಡಿಸುತ್ತಾರಲ್ಲಾ; ತಮ್ಮ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ತಮ್ಮ ದೇವತೆಗಳಿಗೋಸ್ಕರ ಬೆಂಕಿಯಲ್ಲಿ ಸುಡುತ್ತಾರಲ್ಲಾ. ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಹಾಗೆ ಸೇವಿಸಲೇಬಾರದು.",
			"32": "<sup>32</sup> ನಾನು ನಿಮಗೆ ಆಜ್ಞಾಪಿಸುವದನ್ನೆಲ್ಲಾ ನೀವು ಅನುಸರಿಸಲೇಬೇಕು; ಅದಕ್ಕೆ ಏನೂ ಕೂಡಿಸಬಾರದು; ಅದರಿಂದ ಏನೂ ತೆಗೆದುಬಿಡಬಾರದು."
		},
		"13": {
			"1": "<sup>1</sup> ಯಾವ ಪ್ರವಾದಿಯಾಗಲಿ ಕನಸುಕಂಡವನಾಗಲಿ ನಿಮ್ಮ ಮಧ್ಯದಲ್ಲಿ ಬಂದು ನಿಮಗೆ ಗೊತ್ತಿಲ್ಲದ ಇತರ ದೇವರುಗಳನ್ನು ಸೂಚನೆಮಾಡಿ -",
			"2": "<sup>2</sup> ಈ ದೇವತೆಗಳನ್ನು ಅವಲಂಬಿಸಿ ಸೇವಿಸೋಣ ಎಂದು ಬೋಧಿಸುತ್ತಾ ಆ ಬೋಧನೆಯನ್ನು ಸ್ಥಾಪಿಸುವದಕ್ಕೆ ಒಂದು ಅದ್ಭುತವನ್ನಾಗಲಿ ಮಹತ್ಕಾರ್ಯವನ್ನಾಗಲಿ ತೋರಿಸಿಕೊಡುತ್ತೇನೆಂದು ಹೇಳಲು ಅವನು ಹೇಳಿದಂತೆಯೇ ನಡೆದರೂ ನೀವು ಅವನ ಮಾತಿಗೆ ಕಿವಿಗೊಡಬಾರದು.",
			"3": "<sup>3</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು - ಇವರು ಸಂಪೂರ್ಣ ಹೃದಯದಿಂದಲೂ ಮನಸ್ಸಿನಿಂದಲೂ ತನ್ನನ್ನೇ ಪ್ರೀತಿಸುವವರು ಹೌದೋ ಅಲ್ಲವೋ ಎಂಬದನ್ನು ತಿಳಿದುಕೊಳ್ಳುವದಕ್ಕೆ ನಿಮ್ಮನ್ನು ಪರೀಕ್ಷಿಸುತ್ತಾನೆ.",
			"4": "<sup>4</sup> ಆತನು ಹೇಳುವ ಮಾರ್ಗದಲ್ಲೇ ನೀವು ನಡೆದು ಆತನಲ್ಲಿಯೇ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿ ಆತನ ಆಜ್ಞೆಗಳನ್ನೇ ಅನುಸರಿಸಿ ಆತನಿಗೇ ವಿಧೇಯರಾಗಿ ಆತನನ್ನೇ ಸೇವಿಸುತ್ತಾ ಹೊಂದಿಕೊಂಡಿರಬೇಕು.",
			"5": "<sup>5</sup> ಆ ಪ್ರವಾದಿಗೆ ಅಥವಾ ಆ ಕನಸುಕಂಡವನಿಗೆ ಮರಣ ಶಿಕ್ಷೆಯಾಗಬೇಕು. ದಾಸತ್ವದಲ್ಲಿದ್ದ ನಿಮ್ಮನ್ನು ಬಿಡುಗಡೆಮಾಡಿ ಐಗುಪ್ತದೇಶದೊಳಗಿಂದ ಕರತಂದ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ವಿರೋಧವಾಗಿ ಅವನು ದ್ರೋಹದ ಮಾತುಗಳನ್ನಾಡಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಹೇಳಿದ ಮಾರ್ಗದಿಂದ ನಿಮ್ಮನ್ನು ತಪ್ಪಿಸಬೇಕೆಂದಿದ್ದನಲ್ಲಾ. ಅವನನ್ನು ಕೊಲ್ಲಿಸಿ ಆ ದುಷ್ಟತ್ವವನ್ನು ನಿಮ್ಮ ಮಧ್ಯದಿಂದ ತೆಗೆದುಹಾಕಬೇಕು.",
			"6": "<sup>6</sup> ಒಡಹುಟ್ಟಿದ ಅಣ್ಣತಮ್ಮಂದಿರಾಗಲಿ ಮಗನಾಗಲಿ ಮಗಳಾಗಲಿ ಪ್ರಾಣಪ್ರಿಯಳಾದ ಹೆಂಡತಿಯಾಗಲಿ ಆಪ್ತಮಿತ್ರನಾಗಲಿ ನಿಮಗಾದರೂ ನಿಮ್ಮ ಪಿತೃಗಳಿಗಾದರೂ ಗೊತ್ತಿಲ್ಲದ ಇತರ ದೇವರುಗಳನ್ನು ಸೂಚಿಸಿ -",
			"7": "<sup>7</sup> ಆ ದೇವರುಗಳನ್ನು ಸೇವಿಸೋಣ ಬನ್ನಿ ಎಂದು ಅಂತರಂಗದಲ್ಲಿ ನಿಮಗೆ ಬೋಧಿಸಿದರೆ ಆ ದೇವರುಗಳು ಹತ್ತಿರವಿರುವ ಜನಾಂಗಗಳ ದೇವರುಗಳಾದರೂ ದೂರವಾದವರ ದೇವರುಗಳಾದರೂ ಭೂಲೋಕದ ಯಾವ ಭಾಗದವರ ದೇವರುಗಳಾದರೂ ನೀವು ಸಮ್ಮತಿಸಲೂಬಾರದು ಕಿವಿಗೊಡಲೂಬಾರದು.",
			"8": "<sup>8</sup> ಅವನನ್ನು ಕನಿಕರಿಸಕೂಡದು, ತಪ್ಪಿಸಕೂಡದು, ಬಚ್ಚಿಡಕೂಡದು; ಕೊಲ್ಲಿಸಲೇಬೇಕು.",
			"9": "<sup>9</sup> ಆ ಶಿಕ್ಷೆಯನ್ನು ನಡಿಸುವಾಗ ತಪ್ಪು ಕಂಡವನೇ ಮೊದಲು ಕೈಹಾಕಬೇಕು;",
			"10": "<sup>10</sup> ತರುವಾಯ ಜನರೆಲ್ಲರೂ ಹಾಕಲಿ. ದಾಸತ್ವದಲ್ಲಿದ್ದ ನಿಮ್ಮನ್ನು ಬಿಡುಗಡೆಮಾಡಿ ಐಗುಪ್ತದೇಶದೊಳಗಿಂದ ಕರತಂದ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಆಶ್ರಯದಿಂದ ಅವನು ನಿಮ್ಮನ್ನು ತಪ್ಪಿಸಬೇಕೆಂದಿದ್ದದರಿಂದ ಅವನನ್ನು ಕಲ್ಲೆಸೆದು ಕೊಲ್ಲಲೇಬೇಕು.",
			"11": "<sup>11</sup> ಇದನ್ನು ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರೂ ಕೇಳಿ ಭಯಪಟ್ಟು ಅಂಥ ದುಷ್ಕಾರ್ಯವನ್ನು ಅಲ್ಲಿಂದ ಮುಂದೆ ಮಾಡದೆ ಇರುವರು.",
			"12": "<sup>12</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೇಹೋವನು ನಿಮ್ಮ ನಿವಾಸಕ್ಕಾಗಿ ಕೊಡುವ ಯಾವದಾದರೂ ಒಂದು ಊರಿನ ವಿಷಯದಲ್ಲಿ -",
			"13": "<sup>13</sup> ಅಲ್ಲಿ ವಾಸವಾಗಿರುವ ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಕೆಲವು ಮಂದಿ ದುಷ್ಟರು ನಿಮಗೆ ಗೊತ್ತಿಲ್ಲದ ಇತರ ದೇವರುಗಳನ್ನು ತಮ್ಮ ಊರಿನವರಿಗೆ ಸೂಚಿಸಿ - ಆ ದೇವರುಗಳನ್ನು ಸೇವಿಸೋಣ ಬನ್ನಿ ಎಂದು ಹೇಳಿ ಅವರನ್ನು ಸನ್ಮಾರ್ಗದಿಂದ ತಪ್ಪಿಸಿದ್ದಾರೆಂಬ ಸುದ್ದಿಯನ್ನು ನೀವು ಕೇಳಿದರೆ",
			"14": "<sup>14</sup> ಆ ಸಂಗತಿಯನ್ನು ಚೆನ್ನಾಗಿ ವಿಚಾರಿಸಿ ತಿಳಿದುಕೊಳ್ಳಬೇಕು. ಅಂಥ ಅಸಹ್ಯಕಾರ್ಯವು ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ನಡೆದದ್ದು ನಿಜವೆಂದು ತಿಳಿದುಬಂದರೆ",
			"15": "<sup>15</sup> ಆ ಊರನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ಹಾಳುಮಾಡಿ ಅದರಲ್ಲಿರುವ ಎಲ್ಲಾ ಜನರನ್ನೂ ದನಗಳನ್ನೂ ಕತ್ತಿಯಿಂದ ಸಂಹರಿಸಿಬಿಡಬೇಕು.",
			"16": "<sup>16</sup> ಅದರಲ್ಲಿರುವ ಎಲ್ಲಾ ಸಾಮಾನುಗಳನ್ನು ಗ್ರಾಮಮಧ್ಯದಲ್ಲಿ ಕೂಡಿಸಿ ಊರನ್ನೂ ಅದರಲ್ಲಿರುವ ಎಲ್ಲಾ ಸಾಮಾನುಗಳನ್ನೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೋಸ್ಕರ ನಿಶ್ಶೇಷವಾಗಿ ಸುಟ್ಟುಬಿಡಬೇಕು. ಅದು ತಿರಿಗಿ ಕಟ್ಟಲ್ಪಡದೆ ಯಾವಾಗಲೂ ಹಾಳುದಿಬ್ಬವಾಗಿರಬೇಕು.",
			"17": "<sup>17</sup> ಕೇವಲ ಯೆಹೋವನಿಗೇ ಆಗಲಿ ಎಂದು ನೀವು ಗೊತ್ತುಮಾಡಿದ್ದರಲ್ಲಿ ಸ್ವಲ್ಪವನ್ನಾದರೂ ತೆಗೆದುಕೊಳ್ಳಬಾರದು. ನೀವು ಹೀಗೆ ನಡೆದು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಮಾತಿಗೆ ಕಿವಿಗೊಟ್ಟು ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ಆತನ ಎಲ್ಲಾ ಆಜ್ಞೆಗಳನ್ನು ಕೈಕೊಂಡು ಆತನ ದೃಷ್ಟಿಗೆ ಸರಿಯಾದದ್ದನ್ನು ಮಾಡಿದರೆ ಆತನು ರೋಷಾಗ್ನಿಯನ್ನು ಬಿಟ್ಟು ನಿಮಗೆ ದಯೆಯನ್ನು ತೋರಿಸಿ ನಿಮ್ಮನ್ನು ಕರುಣಿಸಿ ತಾನು ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ವಾಗ್ದಾನಮಾಡಿದಂತೆ ನಿಮ್ಮ ಸಂತತಿಯನ್ನು ಹೆಚ್ಚಿಸುವನು."
		},
		"14": {
			"1": "<sup>1</sup> ನೀವು ನಿಮ್ಮ ದೇವರಾಗಿರುವ ಯೆಹೋವನ ಮಕ್ಕಳಾಗಿರುವದರಿಂದ ಸತ್ತವರಿಗೋಸ್ಕರ ದೇಹವನ್ನು ಗಾಯಮಾಡಿಕೊಳ್ಳಬಾರದು; ಮುಂದಲೆಯನ್ನು ಬೋಳಿಸಿಕೊಳ್ಳಲೂಬಾರದು.",
			"2": "<sup>2</sup> ಯಾಕಂದರೆ ನೀವು ಕೇವಲ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಜನರೇ; ಆತನು ಭೂಲೋಕದಲ್ಲಿರುವ ಸಮಸ್ತ ಜನಾಂಗಗಳಲ್ಲಿ ನಿಮ್ಮನ್ನೇ ಸ್ವಕೀಯಜನರಾಗುವದಕ್ಕೆ ಆದುಕೊಂಡಿದ್ದಾನಲ್ಲಾ.",
			"3": "<sup>3</sup> ನಿಷಿದ್ಧವಾಗಿರುವ ಯಾವದನ್ನೂ ನೀವು ತಿನ್ನಕೂಡದು.",
			"4": "<sup>4</sup> ನೀವು ತಿನ್ನಬಹುದಾದ ಪಶುಜಾತಿಗಳು ಯಾವವಂದರೆ -",
			"5": "<sup>5</sup> ದನ, ಕುರಿ, ಆಡು, ದುಪ್ಪಿ, ಜಿಂಕೆ, ಸಾರಂಗ, ಕಾಡುಮೇಕೆ, ಚಿಗರಿ, ಕಡವೆ, ಕೊಂಡಗುರಿ ಎಂಬಿವುಗಳೇ.",
			"6": "<sup>6</sup> ಯಾವ ಪ್ರಾಣಿಯ ಕಾಲ್ಗೊರಸು ಸೀಳಿದೆಯೋ ಅದು ಮೆಲಕುಹಾಕುವಂಥದಾದರೆ ಅದರ ಮಾಂಸವನ್ನು ತಿನ್ನಬಹುದು.",
			"7": "<sup>7</sup> ಆದರೆ ಯಾವ ಪ್ರಾಣಿಯು ಮೆಲಕುಹಾಕಿದರೂ ಗೊರಸು ಸೀಳಿರುವದಿಲ್ಲವೋ ಮತ್ತು ಯಾವ ಪ್ರಾಣಿಯ ಗೊರಸು ಸೀಳಿದ್ದರೂ ಮೆಲಕುಹಾಕುವದಿಲ್ಲವೋ ಅವುಗಳ ಮಾಂಸವನ್ನು ನೀವು ತಿನ್ನಬಾರದು. ದೃಷ್ಟಾಂತ - ಒಂಟೆ, ಮೊಲ, ಬೆಟ್ಟದ ಮೊಲ ಇವುಗಳು ಮೆಲಕುಹಾಕುವಂಥವುಗಳಾದರೂ ಸೀಳುಗೊರಸು ಇಲ್ಲವಾದ ಕಾರಣ ಅವು ನಿಮಗೆ ಅಶುದ್ಧ.",
			"8": "<sup>8</sup> ಹಂದಿಯ ಗೊರಸು ಸೀಳಿದ್ದರೂ ಅದು ಮೆಲಕುಹಾಕುವದಿಲ್ಲವಾದದರಿಂದ ಅದು ನಿಮಗೆ ಅಶುದ್ಧ. ಇವುಗಳ ಮಾಂಸವನ್ನು ನೀವು ತಿನ್ನಕೂಡದು; ಇವುಗಳ ಹೆಣವನ್ನು ಮುಟ್ಟಕೂಡದು.",
			"9": "<sup>9</sup> ಜಲಜಂತುಗಳಲ್ಲಿ ನೀವು ತಿನ್ನಬಹುದಾದವುಗಳು ಯಾವವಂದರೆ - ಯಾವ ಜಾತಿಯ ಪ್ರಾಣಿಗೆ ರೆಕ್ಕೆ ಇದ್ದು ಮೈಯೆಲ್ಲಾ ಪರೆಪರೆಯಾಗಿರುವದೋ ಅದರ ಮಾಂಸವನ್ನು ತಿನ್ನಬಹುದು.",
			"10": "<sup>10</sup> ಯಾವ ಜಾತಿಗೆ ರೆಕ್ಕೆಯೂ ಪರೆಪರೆಯಾದ ಮೈಯೂ ಇರುವದಿಲ್ಲವೋ ಅವುಗಳ ಮಾಂಸವನ್ನು ನೀವು ತಿನ್ನಕೂಡದು; ಅದು ನಿಮಗೆ ಅಶುದ್ಧ.",
			"11": "<sup>11</sup> ಎಲ್ಲಾ ಶುದ್ಧಪಕ್ಷಿಗಳ ಮಾಂಸವನ್ನು ತಿನ್ನಬಹುದು.",
			"12": "<sup>12</sup> ತಿನ್ನಕೂಡದವುಗಳು ಯಾವವಂದರೆ -",
			"13": "<sup>13</sup> ಗರುಡ, ಬೆಟ್ಟದ ಹದ್ದು, ಕ್ರೌಂಚ, ಗಿಡುಗ, ಹಕ್ಕಿಸಾಲೆ, ಸಕಲವಿಧವಾದ ಹದ್ದು,",
			"14": "<sup>14</sup> ಸಕಲವಿಧವಾದ ಕಾಗೆ, ಉಷ್ಟ್ರಪಕ್ಷಿ, ಉಲೂಕ, ಕಡಲ್ಹಕ್ಕಿ, ಸಕಲವಿಧವಾದ ಡೇಗೆ,",
			"16": "<sup>16</sup> ಗೂಬೆ, ಹೆಗ್ಗೂಬೆ, ಕರೇಟು, ಚೀಲಮೂಗಿ, ರಣಹದ್ದು, ನೀರುಕಾಗೆ, ಕೊಕ್ಕರೆ,",
			"18": "<sup>18</sup> ಸಕಲವಿಧವಾದ ಬಕ, ಹೆಡೆಹಕ್ಕಿ, ಕಣ್ಣಕಪಡಿ.",
			"19": "<sup>19</sup> ರೆಕ್ಕೆಯುಳ್ಳ ಕ್ರಿಮಿಕೀಟಗಳೆಲ್ಲವೂ ನಿಮಗೆ ಅಶುದ್ಧ; ಅವುಗಳನ್ನು ತಿನ್ನಕೂಡದು.",
			"20": "<sup>20</sup> ರೆಕ್ಕೆಯುಳ್ಳವುಗಳಲ್ಲಿ ಶುದ್ಧಜಾತಿಯವುಗಳನ್ನು ಮಾತ್ರ ತಿನ್ನಬಹುದು.",
			"21": "<sup>21</sup> ಸತ್ತುಬಿದ್ದದ್ದನ್ನು ನೀವು ತಿನ್ನಕೂಡದು; ನಿಮ್ಮ ಊರಲ್ಲಿರುವ ಪರದೇಶೀಯರಿಗೆ ಅದನ್ನು ತಿನ್ನುವದಕ್ಕೆ ಕೊಡಬಹುದು, ಅನ್ಯರಿಗೆ ಮಾರಬಹುದು; ನೀವಾದರೋ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಪ್ರತಿಷ್ಠಿತರಾದವರು. ಆಡುಮರಿಯನ್ನು ಅದರ ತಾಯಿಯ ಹಾಲಿನಲ್ಲಿ ಬೇಯಿಸಕೂಡದು.",
			"22": "<sup>22</sup> ಪ್ರತಿವರುಷದ ನಿಮ್ಮ ಹೊಲದ ಬೆಳೆಯಲ್ಲಿ ದಶಮಭಾಗವನ್ನು ಪ್ರತ್ಯೇಕಿಸಬೇಕು.",
			"23": "<sup>23</sup> ಧಾನ್ಯ, ದ್ರಾಕ್ಷೆ, ಎಣ್ಣೆ ಇವುಗಳಲ್ಲಿ ದಶಮಭಾಗವನ್ನೂ ದನಕುರಿಗಳ ಚೊಚ್ಚಲುಮರಿಗಳನ್ನೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಆತನು ತನ್ನ ಹೆಸರನ್ನು ಸ್ಥಾಪಿಸಿಕೊಳ್ಳುವದಕ್ಕೆ ಆದುಕೊಳ್ಳುವ ಸ್ಥಳದಲ್ಲೇ ನೀವು ತಿನ್ನಬೇಕು. ಹೀಗೆ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಲ್ಲೇ ಯಾವಾಗಲೂ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿರುವದನ್ನು ಅಭ್ಯಾಸಮಾಡಿಕೊಳ್ಳುವಿರಿ.",
			"24": "<sup>24</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೇಹೋವನು ಆದುಕೊಳ್ಳುವ ಸ್ಥಳವು ನಿಮಗೆ ದೂರವಾಗಿದ್ದು ಆ ಕಾಣಿಕೆಗಳನ್ನು ಅಷ್ಟು ದೂರ ಹೊತ್ತುಕೊಂಡು ಹೋಗುವದು ಅಸಾಧ್ಯವಾದರೆ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಅಭಿವೃದ್ಧಿಯನ್ನುಂಟುಮಾಡಿದ ಕಾಲದಲ್ಲಿ",
			"25": "<sup>25</sup> ಅವುಗಳನ್ನು ಮಾರಿಬಿಟ್ಟು ಆ ಹಣದ ಗಂಟನ್ನು ಆತನು ಆದುಕೊಂಡ ಸ್ಥಳಕ್ಕೆ ತೆಗೆದುಕೊಂಡು ಹೋಗಿ",
			"26": "<sup>26</sup> ಅಲ್ಲಿ ನಿಮ್ಮ ಇಷ್ಟಾನುಸಾರ ದನ, ಕುರಿ, ದ್ರಾಕ್ಷಾರಸ, ಮದ್ಯ ಮೊದಲಾದವುಗಳನ್ನು ಕೊಂಡುಕೊಂಡು ನೀವೂ ನಿಮ್ಮ ಮನೆಯವರೂ ನಿಮ್ಮ ಊರಿನ ಲೇವಿಯರೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಊಟಮಾಡಿ ಸಂತೋಷವಾಗಿರಬೇಕು.",
			"27": "<sup>27</sup> ಲೇವಿಯರಿಗೆ ನಿಮ್ಮೊಡನೆ ಯಾವ ಸ್ವಾಸ್ತ್ಯವೂ ಇಲ್ಲವಾದದರಿಂದ ನೀವು ಅವರನ್ನು ಕೈಬಿಡಬಾರದು.",
			"28": "<sup>28</sup> ಪ್ರತಿ ಮೂರನೆಯ ವರುಷದ ಬೆಳೆಯ ದಶಮಭಾಗವನ್ನು ತಂದು ತಂದು ನೀವು ಇರುವ ಊರಲ್ಲಿಯೇ ಕೂಡಿಸಬೇಕು.",
			"29": "<sup>29</sup> ಆಗ ಊರಲ್ಲಿರುವ ಅನ್ಯದೇಶದವರೂ ತಾಯಿತಂದೆಯಿಲ್ಲದವರೂ ವಿಧವೆಯರೂ ನಿಮ್ಮೊಡನೆ ಸ್ವಾಸ್ತ್ಯವನ್ನು ಹೊಂದದೆಹೋದ ಲೇವಿಯರೂ ಉಂಡು ಸಂತೋಷವಾಗಿರಲಿ. ನೀವು ಹೀಗೆ ನಡೆದುಕೊಂಡರೆ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮ ಎಲ್ಲಾ ಪ್ರಯತ್ನಗಳನ್ನು ಸಫಲಮಾಡುವನು."
		},
		"15": {
			"1": "<sup>1</sup> ಏಳು ಸಂವತ್ಸರಗಳು ಕಳೆದನಂತರ ಬಡವರಿಗೆ ಬಿಡುಗಡೆಯಾಗಬೇಕು.",
			"2": "<sup>2</sup> ಹೇಗಂದರೆ ಯೆಹೋವನು ನೇಮಿಸಿದ ಬಿಡುಗಡೆಯ ಸಂವತ್ಸರವು ಬಂತೆಂದು ಪ್ರಕಟವಾದದರಿಂದ ಸಾಲಕೊಟ್ಟವನು ತೆಗೆದುಕೊಂಡವನಿಗೆ ಆ ಸಾಲವನ್ನು ಬಿಟ್ಟುಬಿಡಬೇಕು; ಅವನು ಸ್ವದೇಶದವನಿಗೆ ಕೊಟ್ಟ ಸಾಲವನ್ನು ಕೇಳಬಾರದು.",
			"3": "<sup>3</sup> ಅನ್ಯದೇಶದವರಿಗೆ ಕೊಟ್ಟ ಸಾಲವನ್ನು ಕೇಳಿ ತೆಗೆದುಕೊಳ್ಳಬಹುದೇ ಹೊರತು ಸ್ವದೇಶದವನಿಗೆ ಕೊಟ್ಟದ್ದನ್ನು ಕೇಳಕೂಡದು.",
			"4": "<sup>4</sup> ಆದರೆ ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಮಾತಿಗೆ ಕಿವಿಗೊಟ್ಟು ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ಈ ಧರ್ಮೋಪದೇಶವನ್ನೆಲ್ಲಾ ಅನುಸರಿಸಿದರೆ ಯೆಹೋವನು ತಾನು ನಿಮಗೆ ಸ್ವದೇಶವಾಗಿ ಕೊಡುವ ದೇಶದಲ್ಲಿ ನಿಮ್ಮನ್ನು ಅಭಿವೃದ್ಧಿಪಡಿಸುವದರಿಂದ ನಿಮ್ಮಲ್ಲಿ ಬಡವರೇ ಇರುವದಿಲ್ಲ.",
			"6": "<sup>6</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ವಾಗ್ದಾನ ಮಾಡಿದಂತೆ ನಿಮ್ಮನ್ನು ಅಭಿವೃದ್ಧಿಪಡಿಸುವನು. ನೀವು ಅನೇಕ ಜನಗಳಿಗೆ ಸಾಲಕೊಡುವಿರೇ ಹೊರತು ಅವರಿಂದ ಸಾಲತೆಗೆದುಕೊಳ್ಳುವದಿಲ್ಲ; ಅನೇಕ ಜನಾಂಗಗಳ ಮೇಲೆ ದೊರೆತನ ಮಾಡುವಿರೇ ಹೊರತು ಅವರು ನಿಮ್ಮ ಮೇಲೆ ದೊರೆತನಮಾಡುವದಿಲ್ಲ.",
			"7": "<sup>7</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಕೊಡುವ ದೇಶದ ಯಾವ ಊರಲ್ಲಿಯಾದರೂ ಸ್ವದೇಶದವನಾದ ಬಡವನು ಇದ್ದರೆ ನೀವು ಆ ಬಡ ಸಹೋದರನಿಗೆ ಮನಸ್ಸನ್ನು ಕಠಿಣಮಾಡಿಕೊಂಡು ಅವನಿಗೆ ಸಹಾಯಮಾಡದೆ ಇರಬಾರದು.",
			"8": "<sup>8</sup> ನೀವು ಕೈದೆರೆದು ಅವನ ಅವಸರಕ್ಕೆ ಬೇಕಾದದ್ದನ್ನು ಕೊಟ್ಟು ಸಹಾಯಮಾಡಬೇಕು.",
			"9": "<sup>9</sup> ನೀವು - ಬಿಡುಗಡೆಯುಂಟಾಗುವ ಏಳನೆಯ ವರುಷವು ಸಮೀಪವಾಯಿತೆಂದು ನೀಚವಾದ ಆಲೋಚನೆಯನ್ನು ಮಾಡಿ ಆ ಬಡ ಸಹೋದರನ ವಿಷಯದಲ್ಲಿ ಮೋರೆ ಕರ್ರಗೆಮಾಡಿಕೊಂಡು ಏನೂ ಕೊಡದೆ ಇರಬಾರದು, ನೋಡಿರಿ. [ಹಾಗೆ ಧಿಕ್ಕರಿಸಿದರೆ] ಅವನು ಒಂದು ವೇಳೆ ನಿಮ್ಮ ವಿಷಯದಲ್ಲಿ ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಟ್ಟಾನು, ನೀವು ದೋಷಿಗಳಾಗಿ ತೋರಿಬಂದೀರಿ.",
			"10": "<sup>10</sup> ನೀವು ಕೊಡುವಾಗ ಬೇಸರಗೊಳ್ಳದೆ ಉದಾರವಾದ ಮನಸ್ಸಿನಿಂದಲೇ ಕೊಡಬೇಕು. ಇದರಿಂದ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮ ಎಲ್ಲಾ ಕೆಲಸಗಳಲ್ಲಿಯೂ ಪ್ರಯತ್ನಗಳಲ್ಲಿಯೂ ನಿಮ್ಮನ್ನು ಅಭಿವೃದ್ಧಿಪಡಿಸುವನು.",
			"11": "<sup>11</sup> ದೇಶದಲ್ಲಿ ಯಾವಾಗಲೂ ಬಡವರು ಇರುವರಷ್ಟೆ; ಆದದರಿಂದ ನೀವು ಸ್ವದೇಶದವರಾದ ಬಡವರಿಗೂ ಗತಿಯಿಲ್ಲದವರಿಗೂ ಕೈನೀಡಿ ಸಹಾಯ ಮಾಡಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿದ್ದೇನೆ.",
			"12": "<sup>12</sup> ಸ್ವದೇಶದವರೊಳಗೆ ಯಾವ ಇಬ್ರಿಯ ಪುರುಷನಾಗಲಿ ಸ್ತ್ರೀಯಾಗಲಿ ನಿಮಗೆ ದಾಸರಾಗಿರುವದಕ್ಕಾಗಿ ಮಾರಲ್ಪಡುವ ಪಕ್ಷಕ್ಕೆ ಅಂಥವರು ಆರು ವರುಷಗಳವರೆಗೂ ನಿಮ್ಮ ಸೇವೆಮಾಡಲಿ; ಏಳನೆಯ ವರುಷದಲ್ಲಿ ನೀವು ಅವರನ್ನು ಬಿಟ್ಟುಬಿಡಬೇಕು.",
			"13": "<sup>13</sup> ಬಿಡುವಾಗ ಬರೀಗೈಯಲ್ಲಿ ಕಳುಹಿಸಿಬಿಡಬಾರದು.",
			"14": "<sup>14</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಅನುಗ್ರಹಿಸಿರುವ ಮೇರೆಗೆ ನಿಮಗಿರುವ ಆಡುಕುರಿಗಳು, ದವಸ, ದ್ರಾಕ್ಷಾರಸ ಇವುಗಳಲ್ಲಿ ಉದಾರವಾಗಿ ಕೊಟ್ಟು ಕಳುಹಿಸಿಬಿಡಬೇಕು.",
			"15": "<sup>15</sup> ಐಗುಪ್ತದೇಶದಲ್ಲಿ ನೀವೇ ದಾಸರಾಗಿರಲು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಬಿಡುಗಡೆಮಾಡಿದನೆಂಬದನ್ನು ಜ್ಞಾಪಕಮಾಡಿಕೊಳ್ಳಿರಿ. ಇದಕ್ಕೋಸ್ಕರವಾಗಿಯೇ ನಾನು ಈ ಆಜ್ಞೆಯನ್ನು ನಿಮಗೆ ಈಗ ಕೊಟ್ಟಿದ್ದೇನೆ.",
			"16": "<sup>16</sup> ಒಂದು ವೇಳೆ ಅಂಥ ದಾಸನು ನಿಮ್ಮ ಬಳಿಯಲ್ಲಿ ಸುಖವಾಗಿದ್ದು ನಿಮ್ಮನ್ನೂ ನಿಮ್ಮ ಮನೆಯವರನ್ನೂ ಪ್ರೀತಿಸಿ ತಾನು ಬಿಡುಗಡೆಯಾಗಿ ಹೋಗಲೊಲ್ಲೆನೆಂದು ಹೇಳುವ ಪಕ್ಷಕ್ಕೆ ನೀವು ಶಲಾಕಿಯಿಂದ ಅವನ ಕಿವಿಯನ್ನು ಚುಚ್ಚಿ ಕದಕ್ಕೆ ಸಿಕ್ಕಿಸಬೇಕು.",
			"17": "<sup>17</sup> ಆ ದಿನ ಮೊದಲುಗೊಂಡು ಅವನು ನಿಮಗೆ ಶಾಶ್ವತ ದಾಸನಾಗಿರಬೇಕು. ಅದೇ ರೀತಿಯಲ್ಲಿ ದಾಸಿಯರ ವಿಷಯದಲ್ಲಿಯೂ ನಡೆದುಕೊಳ್ಳಬೇಕು.",
			"18": "<sup>18</sup> ದಾಸನನ್ನು ಬಿಡುಗಡೆಮಾಡುವದು ಕಷ್ಟವೆಂದು ಹೇಳಿಕೊಳ್ಳಬಾರದು. ಅವನು ಆರು ವರುಷಗಳು ನಿಮ್ಮ ದಾಸನಾಗಿದ್ದು ಕೂಲಿಯಾಳಿಗಿಂತ ಎರಡರಷ್ಟು ಪ್ರಯೋಜನವನ್ನುಂಟುಮಾಡಿದನಲ್ಲಾ. ನೀವು ಹೀಗೆ ನಡೆದುಕೊಂಡರೆ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮ ಎಲ್ಲಾ ಪ್ರಯತ್ನಗಳನ್ನು ಸಫಲಗೊಳಿಸುವನು.",
			"19": "<sup>19</sup> ದನಗಳಲ್ಲಿಯೂ ಆಡುಕುರಿಗಳಲ್ಲಿಯೂ ಚೊಚ್ಚಲಾದ ಗಂಡನ್ನು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗಾಗಿ ಪ್ರತಿಷ್ಠಿಸಬೇಕು. ಚೊಚ್ಚಲಾದ ಹೋರಿಯಿಂದ ಕೆಲಸಮಾಡಿಸಬಾರದು.; ಚೊಚ್ಚಲಾದ ಕುರಿಯ ಉಣ್ಣೆಯನ್ನು ಕತ್ತರಿಸಬಾರದು.",
			"20": "<sup>20</sup> ಪ್ರತಿ ವರುಷದಲ್ಲಿ ನೀವೂ ನಿಮ್ಮ ಮನೆಯವರೂ ಅವುಗಳನ್ನು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಆದುಕೊಳ್ಳುವ ಸ್ಥಳದಲ್ಲಿ ಆತನ ಸನ್ನಿಧಿಯಲ್ಲೇ ಕೊಯಿದು ತಿನ್ನಬೇಕು.",
			"21": "<sup>21</sup> ಅದು ಕುಂಟಾಗಿ ಕುರುಡಾಗಿ ಇಲ್ಲವೆ ಬೇರೆ ವಿಧದಿಂದ ವಿರೂಪವಾಗಿದ್ದರೆ ಅದನ್ನು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಿಸಕೂಡದು; ಅದನ್ನು ನಿಮ್ಮ ಊರಲ್ಲೇ ತಿನ್ನಬೇಕು.",
			"22": "<sup>22</sup> ಶುದ್ಧರೂ ಅಶುದ್ಧರೂ ಜಿಂಕೆ ದುಪ್ಪಿಗಳ ಮಾಂಸವನ್ನು ತಿನ್ನುವ ಪ್ರಕಾರ ಇವುಗಳನ್ನೂ ತಿನ್ನಬಹುದು.",
			"23": "<sup>23</sup> ನೀವು ರಕ್ತವನ್ನು ಮಾತ್ರ ಭುಜಿಸಬಾರದು; ಅದನ್ನು ನೀರಿನಂತೆ ಭೂಮಿಯ ಮೇಲೆ ಸುರಿದುಬಿಡಬೇಕು."
		},
		"16": {
			"1": "<sup>1</sup> ನೀವು ಚೈತ್ರಮಾಸದಲ್ಲಿ ಪಸ್ಕ ಹಬ್ಬವನ್ನು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೋಸ್ಕರ ಆಚರಿಸಬೇಕು. ಆ ಮಾಸದಲ್ಲಿಯೇ ರಾತ್ರಿಕಾಲದಲ್ಲಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಐಗುಪ್ತದೇಶದಿಂದ ಬಿಡಿಸಿದನಲ್ಲವೇ.",
			"2": "<sup>2</sup> ನೀವು ಆಡುಕುರಿಗಳಲ್ಲಾಗಲಿ ದನಗಳಲ್ಲಾಗಲಿ ಪಸ್ಕದ ಪಶುವನ್ನು ಯೆಹೋವನು ತನ್ನ ಹೆಸರನ್ನು ಸ್ಥಾಪಿಸುವದಕ್ಕೆ ಆದುಕೊಳ್ಳುವ ಸ್ಥಳದಲ್ಲೇ ಆತನಿಗಾಗಿ ವಧಿಸಬೇಕು.",
			"3": "<sup>3</sup> ಅದರೊಂದಿಗೆ ಹುಳಿಹಾಕಿದ ರೊಟ್ಟಿಯನ್ನು ತಿನ್ನಬಾರದು; ಯಾಕಂದರೆ ನೀವು [ರೊಟ್ಟಿಗೆ ಹುಳಿಹಾಕುವದಕ್ಕೆ ಅವಕಾಶವಿಲ್ಲದೆ] ಅವಸರದಿಂದ ಐಗುಪ್ತದೇಶವನ್ನು ಬಿಟ್ಟುಬಂದಿರಿ. ಐಗುಪ್ತದೇಶದಿಂದ ನಿಮಗೆ ಬಿಡುಗಡೆಯಾದ ದಿನವು ನಿಮ್ಮ ಜೀವಮಾನವೆಲ್ಲಾ ನೆನಪಿನಲ್ಲಿರುವಂತೆ ನೀವು ಕಷ್ಟವನ್ನು ಸೂಚಿಸುವ ಹುಳಿಯಿಲ್ಲದ ರೊಟ್ಟಿಗಳನ್ನು ಏಳು ದಿವಸಗಳವರೆಗೂ ತಿನ್ನಬೇಕು.",
			"4": "<sup>4</sup> ಆ ಏಳು ದಿವಸವೂ ನಿಮ್ಮ ದೇಶದಲ್ಲೆಲ್ಲಾ ಹುಳಿಬೆರೆದ ಯಾವ ಪದಾರ್ಥವೂ ನಿಮ್ಮ ಬಳಿಯಲ್ಲಿ ಇರಲೇಬಾರದು. ಮೊದಲನೆಯ ದಿನದ ಸಂಜೆಯಲ್ಲಿ ನೀವು ವಧಿಸಿದ ಪಶುಮಾಂಸದಲ್ಲಿ ಸ್ವಲ್ಪವನ್ನಾದರೂ ಮರುದಿನದವರೆಗೆ ಉಳಿಸಬಾರದು.",
			"5": "<sup>5</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಕೊಡುವ ಯಾವ ಊರಲ್ಲಿಯಾದರೂ ಪಸ್ಕದ ಪಶುವನ್ನು ವಧಿಸದೆ",
			"6": "<sup>6</sup> ಆತನು ತನ್ನ ಹೆಸರನ್ನು ಸ್ಥಾಪಿಸುವದಕ್ಕೆ ಆದುಕೊಳ್ಳುವ ಸ್ಥಳದಲ್ಲೇ ನೀವು ಐಗುಪ್ತದೇಶದಿಂದ ಹೊರಟುಬಂದ ಕಾಲವಾದ ಸೂರ್ಯಾಸ್ತಮಾನಕಾಲದಲ್ಲಿ ಅದನ್ನು ವಧಿಸಬೇಕು.",
			"7": "<sup>7</sup> ಆ ಸ್ಥಳದಲ್ಲಿಯೇ ನೀವು ಅದನ್ನು ಅಡಿಗೆಮಾಡಿ ತಿನ್ನಬೇಕು. ಮರುದಿವಸ ನಿಮ್ಮ ನಿಮ್ಮ ಮನೆಗಳಿಗೆ ನೀವು ಹೊರಟುಹೋಗಬಹುದು.",
			"8": "<sup>8</sup> ಆರು ದಿವಸ ನೀವು ಹುಳಿಯಿಲ್ಲದ ರೊಟ್ಟಿಗಳನ್ನು ತಿನ್ನಬೇಕು; ಏಳನೆಯ ದಿನದಲ್ಲಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಘನಕ್ಕಾಗಿ ಸಭೆಕೂಡಬೇಕು; ಆ ದಿನದಲ್ಲಿ ಯಾವ ಉದ್ಯೋಗವನ್ನೂ ನಡಿಸಕೂಡದು.",
			"9": "<sup>9</sup> ಬೆಳೆ ಕೊಯ್ಯುವದಕ್ಕೆ ಕುಡುಗೋಲು ಹಾಕುವ ಕಾಲ ಮೊದಲುಗೊಂಡು ಏಳು ವಾರಗಳನ್ನು ಎಣಿಸಿ",
			"10": "<sup>10</sup> ಆ ಏಳು ವಾರಗಳಾದನಂತರ ಪಂಚಾಶತ್ತಮ ದಿನದ ಜಾತ್ರೆಯನ್ನು ಆಚರಿಸಬೇಕು. ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಅನುಗ್ರಹಿಸಿದ ಬೆಳೆಗೆ ತಕ್ಕಷ್ಟು ಕಾಣಿಕೆಯನ್ನು ನೀವು ಆಗ ಆತನಿಗೋಸ್ಕರ ತರಬೇಕು.",
			"11": "<sup>11</sup> ಮತ್ತು ಆತನು ತನ್ನ ಹೆಸರನ್ನು ಸ್ಥಾಪಿಸುವದಕ್ಕೆ ಆದುಕೊಳ್ಳುವ ಸ್ಥಳದಲ್ಲಿ ನೀವೂ ನಿಮ್ಮ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳೂ ದಾಸದಾಸಿಯರೂ ನಿಮ್ಮ ಊರಲ್ಲಿರುವ ಲೇವಿಯರೂ ಪರದೇಶದವರೂ ತಾಯಿ ತಂದೆಯಿಲ್ಲದವರೂ ವಿಧವೆಯರೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸಂಭ್ರಮದಿಂದಿರಬೇಕು.",
			"12": "<sup>12</sup> ಐಗುಪ್ತದೇಶದಲ್ಲಿ ದಾಸರಾಗಿದ್ದೇವೆಂಬದನ್ನು ನೀವು ನೆನಪಿಗೆ ತಂದುಕೊಂಡು ಈ ವಿಧಿಗಳನ್ನು ಅನುಸರಿಸಿ ನಡೆಯಬೇಕು.",
			"13": "<sup>13</sup> ನೀವು ಕಣದ ಕೆಲಸವನ್ನೂ ಆಲೆಯ ಕೆಲಸವನ್ನೂ ಪೂರೈಸಿ ಬೆಳೆಯನ್ನು ಮನೆಗೆ ತಂದಾಗ ಏಳು ದಿನಗಳವರೆಗೂ ಪರ್ಣಶಾಲೆಗಳ ಜಾತ್ರೆಯನ್ನು ಆಚರಿಸಬೇಕು.",
			"14": "<sup>14</sup> ಅದರಲ್ಲಿ ನೀವೂ ನಿಮ್ಮ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳೂ ದಾಸದಾಸಿಯರೂ ನಿಮ್ಮ ಊರಲ್ಲಿರುವ ಲೇವಿಯರೂ ಪರದೇಶದವರೂ ತಾಯಿತಂದೆಯಿಲ್ಲದವರೂ ವಿಧವೆಯರೂ ಸಂಭ್ರಮದಿಂದಿರಬೇಕು.",
			"15": "<sup>15</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಆದುಕೊಳ್ಳುವ ಸ್ಥಳದಲ್ಲಿ ನೀವು ಏಳು ದಿನವೂ ಆ ಜಾತ್ರೆಯನ್ನು ಆಚರಿಸಬೇಕು. ನಿಮ್ಮ ವ್ಯವಸಾಯವನ್ನೂ ಬೇರೆ ಎಲ್ಲಾ ಕೆಲಸಗಳನ್ನೂ ಆತನು ಸಫಲವಾಗಿ ಮಾಡಿದ್ದರಿಂದ ನೀವು ಬಹಳ ಆನಂದದಿಂದಿರಬೇಕು.",
			"16": "<sup>16</sup> ಹುಳಿಯಿಲ್ಲದ ರೊಟ್ಟಿಗಳ ಜಾತ್ರೆಯಲ್ಲಿಯೂ ಪಂಚಾಶತ್ತಮದಿನದ ಜಾತ್ರೆಯಲ್ಲಿಯೂ ಪರ್ಣಶಾಲೆಗಳ ಜಾತ್ರೆಯಲ್ಲಿಯೂ ಅಂತು ವರುಷಕ್ಕೆ ಮೂರಾವರ್ತಿ ನಿಮ್ಮಲ್ಲಿರುವ ಗಂಡಸರೆಲ್ಲರೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಆತನು ಆದುಕೊಳ್ಳುವ ಸ್ಥಳದಲ್ಲಿ ಕಾಣಿಸಿಕೊಳ್ಳಬೇಕು. ಒಬ್ಬರೂ ಕೈಯಲ್ಲಿ ಕಾಣಿಕೆಯಿಲ್ಲದೆ ಆತನ ಸನ್ನಿಧಿಗೆ ಬರಕೂಡದು.",
			"17": "<sup>17</sup> ಪ್ರತಿಯೊಬ್ಬನು ತನಗೆ ಯೆಹೋವನು ಅನುಗ್ರಹಿಸಿದ ಆದಾಯದ ಮೇರೆಗೆ ತನ್ನ ಶಕ್ತ್ಯನುಸಾರ ಕೊಡಬೇಕು.",
			"18": "<sup>18</sup> ಪ್ರತಿಯೊಂದು ಕುಲವು ಇರುವ ಪ್ರದೇಶದಲ್ಲಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಕೊಡುವ ಎಲ್ಲಾ ಊರುಗಳಲ್ಲಿಯೂ ನ್ಯಾಯಾಧಿಪತಿಗಳನ್ನೂ ಅಧಿಕಾರಿಗಳನ್ನೂ ನೀವು ನೇಮಿಸಬೇಕು. ಅವರು ಜನರಿಗೋಸ್ಕರ ನ್ಯಾಯವನ್ನು ವಿಚಾರಿಸಿ ಸರಿಯಾದ ತೀರ್ಪುಕೊಡಬೇಕು.",
			"19": "<sup>19</sup> ನೀವು ನ್ಯಾಯವನ್ನು ಬಿಟ್ಟು ತೀರ್ಮಾನಮಾಡಬಾರದು; ಪಕ್ಷಪಾತಮಾಡಬಾರದು; ಲಂಚತೆಗೆದುಕೊಳ್ಳಬಾರದು. ಲಂಚವು ಬುದ್ಧಿವಂತರನ್ನೂ ಕುರುಡರಂತೆ ಮಾಡುತ್ತದೆ; ನಿರಪರಾಧಿಗಳ ನ್ಯಾಯವನ್ನು ಕೆಡಿಸುತ್ತದೆ.",
			"20": "<sup>20</sup> ನೀವು ಕೇವಲ ನ್ಯಾಯವನ್ನೇ ಅನುಸರಿಸಬೇಕು. ಹಾಗೆ ನಡೆದರೆ ನೀವು ಬದುಕಿಕೊಂಡು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಕೊಡುವ ದೇಶವನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವಿರಿ.",
			"21": "<sup>21</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೋಸ್ಕರ ನೀವು ಕಟ್ಟಿಸಿಕೊಳ್ಳುವ ಯಜ್ಞವೇದಿಯ ಬಳಿಯಲ್ಲಿ ಯಾವ ವಿಧವಾದ ಮರದ ಅಶೇರವೆಂಬ ವಿಗ್ರಹ ಸ್ತಂಭವನ್ನೂ ನೆಡಬಾರದು;",
			"22": "<sup>22</sup> ಕಲ್ಲಿನ ಕಂಬವನ್ನೂ ನಿಲ್ಲಿಸಬಾರದು; ಇದು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಹೇಯವಾದದ್ದು."
		},
		"17": {
			"1": "<sup>1</sup> ಕುಂದು ಕೊರತೆಯಾಗಲಿ ಯಾವ ದುರ್ಲಕ್ಷಣವಾಗಲಿ ಇರುವ ದನಕುರಿಗಳನ್ನು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಿಸಬಾರದು. ಅಂಥವುಗಳನ್ನು ಸಮರ್ಪಿಸುವದು ಆತನಿಗೆ ಅಸಹ್ಯವಾಗಿದೆ.",
			"2": "<sup>2</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಕೊಡುವ ಯಾವ ಊರಲ್ಲಿಯಾದರೂ ಯಾವ ಪುರುಷನಾಗಲಿ ಸ್ತ್ರೀಯಾಗಲಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ನಿಬಂಧನೆಯನ್ನು ಮೀರಿ",
			"3": "<sup>3</sup> ಅನ್ಯದೇವರುಗಳನ್ನಾಗಲಿ ಇಲ್ಲವೆ ಪೂಜಿಸಬಾರದೆಂದು ಆತನು ನಿಷೇಧಿಸಿರುವ ಸೂರ್ಯಚಂದ್ರನಕ್ಷತ್ರಗಳನ್ನಾಗಲಿ ಸೇವಿಸಿ ಪೂಜಿಸಿ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ದುಷ್ಟವಾದದ್ದನ್ನು ನಡಿಸಿದರೆ",
			"4": "<sup>4</sup> ನೀವು ಆ ಸಂಗತಿಯನ್ನು ಕೇಳಿದಾಗ ಸೂಕ್ಷ್ಮವಾಗಿ ವಿಚಾರಣೆಯನ್ನು ಮಾಡಬೇಕು. ಆಗ ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಯಾರಿಂದಲಾದರೂ ಆ ನಿಷಿದ್ಧಕಾರ್ಯವು ನಡೆದದ್ದು ನಿಜವೆಂದು ತಿಳಿದುಬಂದರೆ",
			"5": "<sup>5</sup> ಆ ಪುರುಷನನ್ನಾಗಲಿ ಸ್ತ್ರೀಯನ್ನಾಗಲಿ ಹಿಡಿದು ಊರ ಹೊರಗೆ ತಂದು ಕಲ್ಲುಗಳಿಂದ ಕೊಲ್ಲಬೇಕು.",
			"6": "<sup>6</sup> ಒಬ್ಬನೇ ಒಬ್ಬನ ಸಾಕ್ಷಿಯ ಮಾತಿನ ಮೇಲೆ ಯಾರಿಗೂ ಮರಣಶಿಕ್ಷೆಯಾಗಬಾರದು; ಮರಣಶಿಕ್ಷೆಯನ್ನು ವಿಧಿಸುವದಕ್ಕೆ ಇಬ್ಬರು ಇಲ್ಲವೆ ಮೂವರು ಸಾಕ್ಷಿಗಳು ಬೇಕು.",
			"7": "<sup>7</sup> ಅಪರಾಧಿಯನ್ನು ಕೊಲ್ಲುವದಕ್ಕೆ ಸಾಕ್ಷಿಗಳೇ ಮೊದಲು ಕಲ್ಲನ್ನು ಹಾಕಬೇಕು; ತರುವಾಯ ಜನರೆಲ್ಲರೂ ಹಾಕಲಿ. ಹೀಗೆ ಮಾಡಿ ನೀವು ಆ ದುಷ್ಟತ್ವವನ್ನು ನಿಮ್ಮ ಮಧ್ಯದಿಂದ ತೆಗೆದುಹಾಕಬೇಕು.",
			"8": "<sup>8</sup> ವಿಧವಿಧವಾದ ಜೀವಹತ್ಯ, ನ್ಯಾಯ, ಹೊಡೆದಾಟ ಇವುಗಳ ವಿಷಯದಲ್ಲಿ ಅನುಮಾನ ಚರ್ಚೆಗಳು ನಿಮ್ಮ ಊರಲ್ಲಿ ಉಂಟಾದಾಗ, ಸರಿಯಾಗಿ ವಿವೇಚಿಸುವದು ನಿಮ್ಮ ಶಕ್ತಿಗೆ ಮೀರಿದ ಪಕ್ಷಕ್ಕೆ ನೀವು ಊರಿನಿಂದ ಹೊರಟು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಆದುಕೊಳ್ಳುವ ಸ್ಥಳಕ್ಕೆ",
			"9": "<sup>9</sup> ಯಾಜಕಸೇವೆಮಾಡುವ ಲೇವಿಯರ ಮತ್ತು ಆ ಕಾಲದ ನ್ಯಾಯಾಧಿಪತಿಯ ಬಳಿಗೆ ಹೋಗಿ ವಿಚಾರಿಸಲು ನೀವು ಕೊಡಬೇಕಾದ ತೀರ್ಪನ್ನು ಅವರು ತಿಳಿಸುವರು.",
			"10": "<sup>10</sup> ಯೆಹೋವನು ಆದುಕೊಂಡ ಆ ಸ್ಥಳದಿಂದ ಅವರು ನಿಮಗೆ ತಿಳಿಸುವ ಮಾತಿನ ಪ್ರಕಾರ ನೀವು ಮಾಡಬೇಕು; ಅವರು ಬೋಧಿಸುವದನ್ನೇ ನೀವು ಅನುಸರಿಸಬೇಕು.",
			"11": "<sup>11</sup> ಅವರು ಬೋಧಿಸುವ ಬೋಧನೆಯಂತೆಯೂ ಅವರು ಹೇಳಿಕೊಡುವ ತೀರ್ಪಿನಂತೆಯೂ ನೀವು ಮಾಡಬೇಕು; ಅವರು ತಿಳಿಸುವ ಮಾತನ್ನು ಬಿಟ್ಟು ಎಡಬಲಕ್ಕೆ ತಿರುಗಬಾರದು.",
			"12": "<sup>12</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸೇವೆಮಾಡುವ ಯಾಜಕರ ಅಥವಾ ನ್ಯಾಯಾಧಿಪತಿಯ ಮಾತನ್ನು ಯಾವನು ಅಹಂಕಾರದಿಂದ ನಿರಾಕರಿಸುತ್ತಾನೋ ಅವನಿಗೆ ಮರಣಶಿಕ್ಷೆಯಾಗಬೇಕು. ಹೀಗೆ ಮಾಡಿ ನೀವು ಆ ದುಷ್ಟತ್ವವನ್ನು ಇಸ್ರಾಯೇಲ್ಯರ ಮಧ್ಯದಿಂದ ತೆಗೆದುಹಾಕಬೇಕು.",
			"13": "<sup>13</sup> ಜನರೆಲ್ಲರೂ ಕೇಳಿ ಭಯಪಟ್ಟು ಇನ್ನು ಅಹಂಕಾರದಿಂದ ನಡೆಯದೆ ಇರುವರು.",
			"14": "<sup>14</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಕೊಡುವ ದೇಶವನ್ನು ನೀವು ಸೇರಿ ಸ್ವಾಧೀನ ಮಾಡಿಕೊಂಡು ಅದರಲ್ಲಿ ವಾಸವಾಗಿರುವಾಗ - ಸುತ್ತಲಿರುವ ಎಲ್ಲಾ ಜನಾಂಗಗಳಂತೆ ನಾವೂ ಅರಸನನ್ನು ನೇಮಿಸಿಕೊಳ್ಳೋಣ ಎಂದು ಹೇಳಿಕೊಳ್ಳುವ ಪಕ್ಷಕ್ಕೆ",
			"15": "<sup>15</sup> ನೀವು ಅಗತ್ಯವಾಗಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಆದುಕೊಂಡವನನ್ನೇ ನೇಮಿಸಿಕೊಳ್ಳಬೇಕು. ನಿಮ್ಮ ಬಂಧುಗಳಾದ ಸ್ವಜನರಲ್ಲೇ ಒಬ್ಬನನ್ನು ಅರಸನನ್ನಾಗಿ ನೇಮಿಸಿಕೊಳ್ಳಬೇಕೇ ಹೊರತು ಅನ್ಯದೇಶದವನನ್ನು ನೇಮಿಸಬಾರದು.",
			"16": "<sup>16</sup> ಅವನು ಕುದುರೆಯ ದಂಡನ್ನು ಕೂಡಿಸಿಕೊಳ್ಳಬಾರದು. ಕುದುರೆಗಳನ್ನು ಸಂಪಾದಿಸಿಕೊಳ್ಳುವದಕ್ಕಾಗಿ ತನ್ನ ಪ್ರಜೆಗಳನ್ನು ಐಗುಪ್ತದೇಶಕ್ಕೆ ಹೋಗಗೊಡಿಸಬಾರದು. ನೀವು ಇನ್ನು ಆ ದೇಶಕ್ಕೆ ಹೋಗಲೇಕೂಡದೆಂದು ಯೆಹೋವನು ಆಜ್ಞಾಪಿಸಿದ್ದಾನಲ್ಲಾ.",
			"17": "<sup>17</sup> ಅವನು ಅನೇಕ ಸ್ತ್ರೀಯರನ್ನು ಮದುವೆಮಾಡಿಕೊಳ್ಳಬಾರದು; ಮಾಡಿಕೊಂಡರೆ ಅವನ ಮನಸ್ಸು ಯೆಹೋವನ ಕಡೆಯಿಂದ ತಿರುಗುವದಕ್ಕೆ ಅವಕಾಶವಾಗುವದು. ಅವನು ಬೆಳ್ಳಿಬಂಗಾರವನ್ನು ವಿಶೇಷವಾಗಿ ಗಳಿಸಿಕೊಳ್ಳಬಾರದು.",
			"18": "<sup>18</sup> ಅವನು ರಾಜ್ಯಾಧಿಕಾರಕ್ಕೆ ಬಂದಾಗ ಯಾಜಕ ಸೇವೆಮಾಡುವ ಲೇವಿಯರ ವಶದಲ್ಲಿರುವ ಈ ಧರ್ಮಶಾಸ್ತ್ರಗ್ರಂಥದ ಒಂದು ಪ್ರತಿಯನ್ನು ತನಗೋಸ್ಕರ ಪುಸ್ತಕರೂಪವಾಗಿ ಬರೆಯಿಸಿಕೊಳ್ಳಬೇಕು.",
			"19": "<sup>19</sup> ಅವನು ಮದದಿಂದ ತನ್ನ ಸ್ವದೇಶದವರನ್ನು ಹಿಯ್ಯಾಳಿಸದೆ ಯೆಹೋವನ ಆಜ್ಞೆಯನ್ನು ಮೀರಿ ಎಡಬಲಕ್ಕೆ ತಿರುಗಿಕೊಳ್ಳದೆ ತನ್ನ ದೇವರಾದ ಯೆಹೋವನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವನಾಗಿ ಈ ಧರ್ಮಶಾಸ್ತ್ರದ ಎಲ್ಲಾ ವಾಕ್ಯಗಳನ್ನೂ ವಿಧಿಗಳನ್ನೂ ಅನುಸರಿಸುವದಕ್ಕೆ ಅಭ್ಯಾಸ ಮಾಡಿಕೊಳ್ಳುವಂತೆ ಈ ಗ್ರಂಥವು ಅವನ ಬಳಿಯಲ್ಲೇ ಇರಬೇಕು; ಅವನು ತನ್ನ ಜೀವಮಾನದ ದಿನಗಳೆಲ್ಲಾ ಅದರಲ್ಲಿ ಓದಿಕೊಳ್ಳುತ್ತಾ ಇರಬೇಕು.",
			"20": "<sup>20</sup> ಹೀಗಾದರೆ ಅವನೂ ಅವನ ಸಂತತಿಯವರೂ ಬಹುಕಾಲ ರಾಜ್ಯಾಧಿಕಾರವನ್ನು ನಡಿಸುತ್ತಾ ಇಸ್ರಾಯೇಲ್ಯರ ನಡುವೆ ಬಾಳುವರು."
		},
		"18": {
			"1": "<sup>1</sup> ಯಾಜಕಸೇವೆಮಾಡುವ ಲೇವಿಯರಿಗಾಗಲಿ ಲೇವಿಕುಲದವರಲ್ಲಿ ಯಾರಿಗೇಯಾಗಲಿ ಇತರ ಇಸ್ರಾಯೇಲ್ಯರೊಡನೆ ಸ್ವಂತಕ್ಕಾಗಿ ಭೂಸ್ಥಿತಿ ದೊರೆಯುವದಿಲ್ಲವಷ್ಟೆ. ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಿಸಲ್ಪಡುವ ಹೋಮದ್ರವ್ಯಗಳೂ ಆತನಿಗೋಸ್ಕರ ಪ್ರತ್ಯೇಕಿಸಲ್ಪಟ್ಟದ್ದೆಲ್ಲವೂ ಅವರಿಗೆ ಜೀವನಾಧಾರ.",
			"2": "<sup>2</sup> ಸ್ವದೇಶಸ್ಥರೊಂದಿಗೆ ಅವರಿಗೆ ಸ್ವಾಸ್ತ್ಯವು ದೊರೆಯುವದಿಲ್ಲ; ಯೆಹೋವನು ಅವರಿಗೆ ಹೇಳಿದಂತೆ ತಾನೇ ಅವರಿಗೆ ಸ್ವಾಸ್ತ್ಯವು.",
			"3": "<sup>3</sup> ಜನರ ಕಡೆಯಿಂದ ಯಾಜಕರು ಹೊಂದಬೇಕಾದವುಗಳು ಯಾವವಂದರೆ - ದನಗಳಲ್ಲಿಯಾಗಲಿ ಆಡುಕುರಿಗಳಲ್ಲಿಯಾಗಲಿ ಪಶುವನ್ನು ಕೊಯಿದು ಯಜ್ಞಮಾಡುವವರೆಲ್ಲರೂ ಅದರ ಮುಂದೊಡೆಯನ್ನೂ ಎರಡು ದವಡೆಗಳನ್ನೂ ಕೋಷ್ಠವನ್ನೂ ಯಾಜಕರಿಗೆ ಕೊಡಬೇಕು.",
			"4": "<sup>4</sup> ಧಾನ್ಯ, ದ್ರಾಕ್ಷಾರಸ, ಎಣ್ಣೆ ಇವುಗಳ ಪ್ರಥಮಫಲಗಳನ್ನೂ ಮೊದಲನೆಯ ಸಾರಿ ಕತ್ತರಿಸುವ ಕುರಿಗಳ ಉಣ್ಣೆಯನ್ನೂ ಅವರಿಗೆ ಕೊಡಬೇಕು.",
			"5": "<sup>5</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮ ಎಲ್ಲಾ ಕುಲಗಳಲ್ಲಿ ಇವರನ್ನೂ ಇವರ ತರುವಾಯ ಇವರ ಸಂತತಿಯವರನ್ನೂ ತನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಶಾಶ್ವತವಾಗಿ ಸೇವೆಮಾಡುವದಕ್ಕೆ ತಾನೇ ನೇಮಿಸಿಕೊಂಡನಲ್ಲಾ.",
			"6": "<sup>6</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಯಾವದಾದರೂ ಒಂದು ಊರಲ್ಲಿ ಇಳಿದುಕೊಂಡಿರುವ ಲೇವಿಯನು ಸ್ವಂತ ಮನಸ್ಸಿನಿಂದ ಆ ಊರನ್ನು ಬಿಟ್ಟು ಯೆಹೋವನು ಆದುಕೊಂಡ ಸ್ಥಳಕ್ಕೆ ಬಂದು",
			"7": "<sup>7</sup> ಅಲ್ಲೇ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸೇವೆಮಾಡುವ ತನ್ನ ಸ್ವಕುಲದವರಂತೆ ತನ್ನ ದೇವರಾದ ಯೆಹೋವನ ಹೆಸರನ್ನು ಹೇಳಿ ತಾನೂ ಸೇವೆ ನಡಿಸಿದರೆ",
			"8": "<sup>8</sup> ಅವನ ಕೈಯಲ್ಲಿ ಪಿತ್ರಾರ್ಜಿತ ಸೊತ್ತನ್ನು ಮಾರಿದ ಹಣವಿದ್ದರೂ ಮಿಕ್ಕ ಲೇವಿಯರೊಂದಿಗೆ ಸಮವಾಗಿ ಭೋಗಿಸುವದಕ್ಕೆ ಬಾಧ್ಯನಾಗಿರುವನು.",
			"9": "<sup>9</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಕೊಡುವ ದೇಶದಲ್ಲಿ ನೀವು ಸೇರಿದಾಗ ಅಲ್ಲಿರುವ ಜನಗಳು ಮಾಡುವ ಹೇಸಿಗೆ ಕೆಲಸಗಳನ್ನು ಅನುಸರಿಸಲೇಬಾರದು.",
			"10": "<sup>10</sup> ಮಕ್ಕಳನ್ನು ಆಹುತಿಕೊಡುವವರು, ಕಣಿಹೇಳುವವರು, ಶಕುನನೋಡುವವರು, ಯಂತ್ರಮಂತ್ರಗಳನ್ನು ಮಾಡುವವರು,",
			"11": "<sup>11</sup> ಮಾಟಗಾರರು, ತಂತ್ರಗಾರರು, ಸತ್ತವರನ್ನು ವಿಚಾರಿಸುವವರು, ಬೇತಾಳಕರು, ಪ್ರೇತಸಿದ್ಧರು ಇಂಥವರು ಯಾರೂ ನಿಮ್ಮಲ್ಲಿ ಇರಬಾರದು.",
			"12": "<sup>12</sup> ಇಂಥ ಕೆಲಸಗಳನ್ನು ನಡಿಸುವವರು ಯೆಹೋವನಿಗೆ ಅಸಹ್ಯರಾಗಿದ್ದಾರೆ; ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಇಂಥ ಹೇಯಕೃತ್ಯಗಳನ್ನು ಮಾಡುವ ಆ ಜನಾಂಗಗಳನ್ನು ನಿಮ್ಮೆದುರಿನಿಂದ ಹೊರಡಿಸಿಬಿಡುತ್ತಾನಲ್ಲಾ.",
			"13": "<sup>13</sup> ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಸೇವೆಯ ವಿಷಯದಲ್ಲಿ ಸದಾಚಾರವುಳ್ಳವರಾಗಿರಬೇಕು.",
			"14": "<sup>14</sup> ನೀವು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವ ದೇಶದ ಜನಾಂಗಗಳು ಶಕುನಗಳನ್ನು ನೋಡುತ್ತಾರೆ. ಯಂತ್ರಮಂತ್ರಗಳನ್ನು ಮಾಡುತ್ತಾರೆ. ನೀವಾದರೋ ಹಾಗೆ ಮಾಡುವದಕ್ಕೆ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಂದ ಅಪ್ಪಣೆ ಹೊಂದಲಿಲ್ಲ.",
			"15": "<sup>15</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮ ಸಹೋದರರಲ್ಲಿ ನನ್ನಂಥ ಪ್ರವಾದಿಯನ್ನು (ನಿಮಗೆ) ಏರ್ಪಡಿಸುವನು; ಅವನಿಗೇ ನೀವು ಕಿವಿಗೊಡಬೇಕು.",
			"16": "<sup>16</sup> ಹೋರೇಬಿನಲ್ಲಿ ನೀವು ಸಭೆಕೂಡಿದ ದಿನದಲ್ಲಿ ಹಾಗೆಯೇ ಕೋರಿಕೊಂಡು - ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಸ್ವರವನ್ನು ಇನ್ನು ಕೇಳಲೂ ಈ ಘೋರವಾದ ಅಗ್ನಿಜ್ವಾಲೆಯನ್ನು ಇನ್ನು ನೋಡಲೂ ನಮಗೆ ಮನಸ್ಸಿಲ್ಲ; ಕೇಳಿ ನೋಡಿದರೆ ಸತ್ತೇವು ಎಂದು ನೀವು ಹೇಳಿದಿರಷ್ಟೆ.",
			"17": "<sup>17</sup> ಆಗ ಯೆಹೋವನು ನನಗೆ - ಇವರು ಹೇಳಿದ ಮಾತು ಒಳ್ಳೇದೇ;",
			"18": "<sup>18</sup> ಇವರ ಸ್ವದೇಶದವರಲ್ಲಿ ನಿನ್ನಂಥ ಪ್ರವಾದಿಯನ್ನು ಅವರಿಗೋಸ್ಕರ ಏರ್ಪಡಿಸುವೆನು; ಅವನ ಬಾಯಿಂದ ನನ್ನ ಮಾತುಗಳನ್ನು ನುಡಿಸುವೆನು; ನಾನು ಅವನಿಗೆ ಆಜ್ಞಾಪಿಸಿದ್ದನ್ನೆಲ್ಲಾ ಅವನು ಅವರಿಗೆ ತಿಳಿಸುವನು.",
			"19": "<sup>19</sup> ಅವನು ನನ್ನ ಹೆಸರಿನಲ್ಲಿ ಹೇಳಿದ ಮಾತುಗಳಿಗೆ ಯಾರು ಕಿವಿಗೊಡುವದಿಲ್ಲವೋ ಅಂಥವರನ್ನು ನಾನು ಶಿಕ್ಷಿಸುವೆನು.",
			"20": "<sup>20</sup> ಆದರೆ ಯಾವ ಪ್ರವಾದಿ ನನ್ನಿಂದ ಅಧಿಕಾರಹೊಂದದೆ ನಾನು ಪ್ರೇರಣೆಮಾಡದ ಮಾತುಗಳನ್ನು ಯೆಹೋವನ ಮಾತೆಂದು ಹೇಳಿ ಜನರಿಗೆ ತಿಳಿಸುವನೋ ಇಲ್ಲವೆ ಇತರ ದೇವರುಗಳ ಹೆಸರಿನಲ್ಲಿ ಮಾತಾಡುವನೋ ಅವನಿಗೆ ಮರಣಶಿಕ್ಷೆಯಾಗಬೇಕೆಂದು ಹೇಳಿದನು.",
			"21": "<sup>21</sup> ಪ್ರವಾದಿಯು ಹೇಳಿದ ಮಾತು ಯೆಹೋವನ ಮಾತಲ್ಲವೆಂದು ತಿಳಿದುಕೊಳ್ಳುವದು ಹೇಗೆ ಅಂದುಕೊಳ್ಳುತ್ತೀರೋ?",
			"22": "<sup>22</sup> ಪ್ರವಾದಿಯು ಯೆಹೋವನ ಮಾತೆಂದು ಹೇಳಿ ಮುಂತಿಳಿಸಿದ ಸಂಗತಿ ನಡೆಯದೆ ಹೋದರೆ ಅವನ ಮಾತು ಯೆಹೋವನದಲ್ಲವೆಂದು ನೀವು ತಿಳಿದುಕೊಳ್ಳಬೇಕು. ಅವನು ಅಧಿಕಾರವಿಲ್ಲದೆ ಮಾತಾಡಿದವನು; ಅವನಿಗೆ ಹೆದರಬಾರದು."
		},
		"19": {
			"1": "<sup>1</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಕೊಡುವ ದೇಶದಲ್ಲಿರುವ ಜನಾಂಗಗಳನ್ನು ಆತನು ತೆಗೆದುಹಾಕಿಬಿಟ್ಟ ತರುವಾಯ ನೀವು ಆ ದೇಶವನ್ನು ಸ್ವಾಧೀನಕ್ಕೆ ತೆಗೆದುಕೊಂಡು ಅವರ ಗ್ರಾಮಗಳಲ್ಲಿಯೂ ಮನೆಗಳಲ್ಲಿಯೂ ವಾಸವಾಗಿರುವಾಗ",
			"2": "<sup>2</sup> ಆ ನಿಮ್ಮ ದೇಶದ ಮಧ್ಯದಲ್ಲಿ ನೀವು ಮೂರು ಆಶ್ರಯ ನಗರಗಳನ್ನು ಗೊತ್ತುಮಾಡಬೇಕು.",
			"3": "<sup>3</sup> ನರಹತ್ಯಮಾಡಿದವರು ಆ ಪಟ್ಟಣಗಳಿಗೆ ಓಡಿಹೋಗುವದಕ್ಕೆ ಅನುಕೂಲವಾಗುವಂತೆ ನೀವು ಅವುಗಳ ದಾರಿಗಳನ್ನು ಸರಿಪಡಿಸಬೇಕು. ಮತ್ತು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಸ್ವದೇಶವಾಗುವದಕ್ಕೆ ಕೊಡುವ ಆ ದೇಶವನ್ನು ಮೂರು ಭಾಗ ಮಾಡಬೇಕು.",
			"4": "<sup>4</sup> ಆ ಪಟ್ಟಣಗಳ ಸಂಗತಿ ಹೇಗಂದರೆ - ಯಾವನಾದರೂ ದ್ವೇಷವಿಲ್ಲದೆ ಕೈತಪ್ಪಿ ಮತ್ತೊಬ್ಬನನ್ನು ಕೊಂದರೆ ಅವನು ಆ ಪಟ್ಟಣಗಳಲ್ಲಿ ಒಂದಕ್ಕೆ ಓಡಿಹೋಗಿ ಬದುಕಿಕೊಳ್ಳಬಹುದು.",
			"5": "<sup>5</sup> ದೃಷ್ಟಾಂತ - ಒಬ್ಬ ಮನುಷ್ಯನು ಕಟ್ಟಿಗೆಯನ್ನು ಕಡಿಯಬೇಕೆಂದು ಮತ್ತೊಬ್ಬನ ಜೊತೆಯಲ್ಲಿ ಅಡವಿಗೆ ಹೋಗಿ ಅಲ್ಲಿ ಒಂದು ಮರವನ್ನು ಕಡಿಯಬೇಕೆಂದು ಕೊಡಲಿಯಿಂದ ಹೊಡೆಯುತ್ತಿರುವಾಗ ಕೊಡಲಿಯು ಕಾವಿನಿಂದ ಜಾರಿ ಆ ಮತ್ತೊಬ್ಬನಿಗೆ ತಗಲಿದ್ದರಿಂದ ಅವನು ಸತ್ತರೆ ಹೊಡೆದವನು ಆ ಪಟ್ಟಣಗಳಲ್ಲಿ ಒಂದಕ್ಕೆ ಓಡಿಹೋಗಿ ಬದುಕಿಕೊಳ್ಳಬಹುದು.",
			"6": "<sup>6</sup> ಸತ್ತವನಲ್ಲಿ ಅವನಿಗೆ ಮೊದಲು ದ್ವೇಷವಿಲ್ಲದ್ದರಿಂದ ಅವನು ಮರಣಶಿಕ್ಷೆಗೆ ಪಾತ್ರನಲ್ಲವಲ್ಲಾ. ಆದರೂ ಹತ್ಯಮಾಡಿದವನಿಗೆ ಮುಯ್ಯಿತೀರಿಸುವ ಹಂಗುಳ್ಳ ಸಮೀಪಬಂಧುವು ಇನ್ನು ಕೋಪದಿಂದ ಉರಿಯುತ್ತಿರುವಾಗಲೇ ಅವನನ್ನು ಹಿಂದಟ್ಟಾನು; ಮತ್ತು ಮಾರ್ಗ ದೂರವಾಗಿರುವ ಪಕ್ಷಕ್ಕೆ ಅವನನ್ನು ಹಿಡಿದು ಕೊಂದುಹಾಕಿಬಿಟ್ಟಾನು.",
			"7": "<sup>7</sup> ಈ ಕಾರಣದಿಂದ ನೀವು ಮೂರು ಆಶ್ರಯ ನಗರಗಳನ್ನು ಗೊತ್ತುಮಾಡಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿದ್ದೇನೆ.",
			"8": "<sup>8</sup> ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ಈ ಧರ್ಮೋಪದೇಶವನ್ನೆಲ್ಲಾ ನೀವು ಅನುಸರಿಸುವವರಾಗಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಪ್ರೀತಿಸಿ ಆತನು ಹೇಳುವ ಮಾರ್ಗದಲ್ಲೇ ಯಾವಾಗಲೂ ನಡೆದರೆ ಆತನು ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ವಾಗ್ದಾನಮಾಡಿದಂತೆ ನಿಮ್ಮ ರಾಜ್ಯವನ್ನು ವಿಸ್ತರಿಸಿ ತಾನು ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ಸೂಚಿಸಿದ ಪ್ರದೇಶವನ್ನೆಲ್ಲಾ ಕೊಡುವನು. ಆಗ ಈ ಮೂರು ಪಟ್ಟಣಗಳನ್ನಲ್ಲದೆ ಇನ್ನು ಮೂರು ಪಟ್ಟಣಗಳನ್ನು ಗೊತ್ತುಮಾಡಬೇಕು.",
			"10": "<sup>10</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಸ್ವದೇಶವಾಗುವದಕ್ಕೆ ಕೊಡುವ ದೇಶದಲ್ಲಿ ನಿರಪರಾಧಿಗೆ ಮರಣಶಿಕ್ಷೆಯಾಗಬಾರದು; ಅಂಥವನಿಗೆ ಮರಣಶಿಕ್ಷೆಯಾದರೆ ಆ ದೋಷವು ನಿಮ್ಮದಾಗಿಯೇ ಇರುವದು.",
			"11": "<sup>11</sup> ಆದರೆ ಯಾವನಾದರೂ ಒಬ್ಬನಲ್ಲಿ ದ್ವೇಷವನ್ನೇ ಇಟ್ಟು ಸಮಯನೋಡಿಕೊಂಡು ಅವನ ಮೇಲೆ ಬಿದ್ದು ಅವನನ್ನು ಹೊಡೆದು ಕೊಂದ ತರುವಾಯ ಈ ಪಟ್ಟಣಗಳಲ್ಲಿ ಒಂದಕ್ಕೆ ಓಡಿಹೋದರೆ",
			"12": "<sup>12</sup> ಅವನ ಊರಿನ ಹಿರಿಯರು ಅವನನ್ನು ಅಲ್ಲಿಂದ ಹಿಡತರಿಸಿ ಅವನಿಗೆ ಮರಣಶಿಕ್ಷೆಯಾಗುವಂತೆ ಮುಯ್ಯಿತೀರಿಸುವ ಹಂಗುಳ್ಳವನ ಕೈಗೆ ಕೊಡಬೇಕು.",
			"13": "<sup>13</sup> ನೀವು ಅವನನ್ನು ಕನಿಕರಿಸಬಾರದು. ನಿಮಗೆ ಶುಭವುಂಟಾಗುವಂತೆ ನಿರಪರಾಧಿಯ ಪ್ರಾಣವನ್ನು ತೆಗೆದವನು ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಉಳಿಯದಂತೆ ಮಾಡಬೇಕು.",
			"14": "<sup>14</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಸ್ವದೇಶವಾಗುವದಕ್ಕೆ ಕೊಡುವ ದೇಶದಲ್ಲಿ ನಿಮ್ಮ ಪಾಲಿಗೆ ಬಂದ ಸ್ವಾಸ್ತ್ಯದಲ್ಲಿ ನೆರೆಯವನ ಭೂಮಿಯ ಪೂರ್ವಕಾಲದ ಮೇರೆಯನ್ನು ಒತ್ತಬಾರದು.",
			"15": "<sup>15</sup> ಯಾವ ದೋಷದ ಅಥವಾ ಅಪರಾಧದ ವಿಷಯದಲ್ಲೂ ಒಬ್ಬನೇ ಒಬ್ಬನ ಸಾಕ್ಷಿಯನ್ನು ನೀವು ಅಂಗೀಕರಿಸಬಾರದು. ನಡೆದ ಸಂಗತಿ ಸ್ಥಾಪನೆಯಾಗುವದಕ್ಕೆ ಇಬ್ಬರು ಇಲ್ಲವೆ ಮೂವರು ಸಾಕ್ಷಿಗಳು ಬೇಕು.",
			"16": "<sup>16</sup> ಯಾವನಾದರೂ ನ್ಯಾಯವಿರುದ್ಧವಾಗಿ ಬಂದು ಮತ್ತೊಬ್ಬನನ್ನು ಅಪರಾಧಿ ಎಂದು ಸಾಕ್ಷಿಹೇಳಿದರೆ",
			"17": "<sup>17</sup> ವ್ಯಾಜ್ಯವಾಡುವ ಆ ಇಬ್ಬರೂ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಯಾಜಕರ ಮುಂದೆಯೂ ಆಗ ಇರುವ ನ್ಯಾಯಾಧಿಪತಿಗಳ ಮುಂದೆಯೂ ನಿಲ್ಲಬೇಕು.",
			"18": "<sup>18</sup> ನ್ಯಾಯಾಧಿಪತಿಗಳು ಚೆನ್ನಾಗಿ ವಿಚಾರಣೆಮಾಡಿದ ಮೇಲೆ ಆ ಮನುಷ್ಯನ ಸಾಕ್ಷಿ ಸುಳ್ಳೆಂದು ತೋರಿಬಂದರೆ ಅವನು ಆ ಮತ್ತೊಬ್ಬನಿಗೆ ಮಾಡಿಸಬೇಕೆಂದಿದ್ದ ಶಿಕ್ಷೆಯನ್ನು ಅವನಿಗೇ ವಿಧಿಸಬೇಕು.",
			"19": "<sup>19</sup> ಹೀಗೆ ನೀವು ಆ ದುಷ್ಟತ್ವವನ್ನು ನಿಮ್ಮ ಮಧ್ಯದೊಳಗಿಂದ ತೆಗೆದುಹಾಕಬೇಕು.",
			"20": "<sup>20</sup> ಉಳಿದವರು ಆ ಸಂಗತಿಯನ್ನು ಕೇಳಿ ಭಯಪಟ್ಟು ಇನ್ನು ಮುಂದೆ ಇಂಥ ದುಷ್ಟತ್ವವನ್ನು ನಡಿಸದಿರುವರು.",
			"21": "<sup>21</sup> ನೀವು ಅಂಥವರನ್ನು ಕನಿಕರಿಸಬಾರದು; ಪ್ರಾಣಕ್ಕೆ ಪ್ರತಿಯಾಗಿ ಪ್ರಾಣವನ್ನೂ ಕಣ್ಣಿಗೆ ಪ್ರತಿಯಾಗಿ ಕಣ್ಣನ್ನೂ ಹಲ್ಲಿಗೆ ಪ್ರತಿಯಾಗಿ ಹಲ್ಲನ್ನೂ ಕೈಗೆ ಪ್ರತಿಯಾಗಿ ಕೈಯನ್ನೂ ಕಾಲಿಗೆ ಪ್ರತಿಯಾಗಿ ಕಾಲನ್ನೂ ಅವನಿಗೆ ನಷ್ಟಪಡಿಸಬೇಕು."
		},
		"20": {
			"1": "<sup>1</sup> ನೀವು ಶತ್ರುಗಳೊಡನೆ ಯುದ್ಧಕ್ಕೆ ಹೊರಟಾಗ ಅವರ ಕಡೆ ಕುದುರೆಗಳೂ ರಥಗಳೂ ನಿಮಗಿಂತ ಹೆಚ್ಚಾದ ಕಾಲ್ಬಲವೂ ಇರುವದನ್ನು ಕಂಡರೆ ಹೆದರಬಾರದು. ಐಗುಪ್ತದೇಶದಿಂದ ನಿಮ್ಮನ್ನು ಬಿಡಿಸಿದ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮ ಕಡೆ ಇದ್ದಾನಲ್ಲಾ .",
			"2": "<sup>2</sup> ನಿಮಗೆ ಕಾಳಗ ಸಮೀಪಿಸಿದಾಗ ಯಾಜಕನು ಮುಂದೆ ಬಂದು ಭಟರಿಗೆ - ಇಸ್ರಾಯೇಲ್ಯರೇ, ಕೇಳಿರಿ; ನೀವು ಈಗ ಶತ್ರುಗಳೊಡನೆ ಯುದ್ಧಮಾಡುವ ಕಾಲ ಸಮೀಪಿಸಿತಷ್ಟೆ. ನೀವು ಎದೆಗೆಡಬಾರದು, ದಿಗಿಲುಪಡಬಾರದು, ನಡುಗಬಾರದು, ಶತ್ರುಗಳಿಗೆ ಭಯಪಡಬಾರದು.",
			"4": "<sup>4</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ತಾನೇ ನಿಮ್ಮ ಸಂಗಡ ಇದ್ದು ಶತ್ರುಗಳೊಡನೆ ನಿಮಗೋಸ್ಕರ ಯುದ್ಧಮಾಡಿ ನಿಮಗೆ ಜಯವನ್ನುಂಟುಮಾಡುವನು ಎಂದು ಹೇಳಬೇಕು.",
			"5": "<sup>5</sup> ಮತ್ತು ಸೈನ್ಯಾಧಿಪತಿಗಳು ಅವರಿಗೆ - ನಿಮ್ಮಲ್ಲಿ ಯಾವನಾದರೂ ಹೊಸ ಮನೆಯನ್ನು ಕಟ್ಟಿಸಿಕೊಂಡು ಇನ್ನೂ ಗೃಹಪ್ರವೇಶ ಮಾಡದಿದ್ದರೆ ಅವನು ಮನೆಗೆ ಹೋಗಲಿ; ಅವನು ಕಾಳಗದಲ್ಲಿ ಸತ್ತರೆ ಮತ್ತೊಬ್ಬನು ಆ ಮನೆಯಲ್ಲಿ ಗೃಹಪ್ರವೇಶಮಾಡಿಕೊಂಡಾನು.",
			"6": "<sup>6</sup> ಯಾವನಾದರೂ ದ್ರಾಕ್ಷೇತೋಟವನ್ನು ಮಾಡಿ ಅದರ ಹಣ್ಣುಗಳನ್ನು ಇನ್ನೂ ಅನುಭೋಗಿಸದಿದ್ದರೆ ಅವನು ಮನೆಗೆ ಹೋಗಲಿ; ಅವನು ಕಾಳಗದಲ್ಲಿ ಸತ್ತರೆ ಮತ್ತೊಬ್ಬನು ಆ ತೋಟದ ಹಣ್ಣುಗಳನ್ನು ಅನುಭೋಗಿಸಾನು.",
			"7": "<sup>7</sup> ಯಾವನಾದರೂ ತಾನು ಮದುವೆಮಾಡಿಕೊಂಡ ಹೆಣ್ಣನ್ನು ಇನ್ನೂ ಸೇರಿಸಿಕೊಳ್ಳದಿದ್ದರೆ ಅವನು ಮನೆಗೆ ಹೋಗಲಿ; ಅವನು ಕಾಳಗದಲ್ಲಿ ಸತ್ತರೆ ಮತ್ತೊಬ್ಬನು ಅವಳನ್ನು ಸೇರಿಸಿಕೊಂಡಾನು ಎಂದು ಹೇಳಬೇಕು.",
			"8": "<sup>8</sup> ಮತ್ತು - ಯಾವನಾದರೂ ಧೈರ್ಯಕಡಿಮೆಯಾಗಿ ಅಂಜಿಕೊಂಡರೆ ಅವನೂ ಮನೆಗೆ ಹೋಗಲಿ; ಅವನು ದಿಗಿಲುಪಡುವದರಿಂದ ಅವನ ಜೊತೆಗಾರರೂ ದಿಗಿಲು ಪಟ್ಟಾರು ಎಂದು ಹೇಳಬೇಕು.",
			"9": "<sup>9</sup> ಸೈನ್ಯಾಧಿಪತಿಗಳು ಭಟರಿಗೆ ಈ ರೀತಿಯಾಗಿ ಹೇಳಿದನಂತರ ದಂಡಿನವರ ಮೇಲೆ ನಾಯಕರನ್ನು ನೇಮಿಸಬೇಕು.",
			"10": "<sup>10</sup> ನೀವು ಒಂದು ಪಟ್ಟಣವನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವದಕ್ಕೆ ಅದರ ಹತ್ತಿರಕ್ಕೆ ಬಂದಾಗ ಮೊದಲು ಅದು ಯುದ್ಧವಿಲ್ಲದೆ ಸಮಾಧಾನದಿಂದಲೇ ಅಧೀನವಾಗಬಹುದೆಂದು ಪ್ರಕಟಿಸಬೇಕು.",
			"11": "<sup>11</sup> ಅದರಲ್ಲಿರುವವರು ಒಪ್ಪಿ ಬಾಗಿಲನ್ನು ನಿಮಗೆ ತೆರೆದುಕೊಟ್ಟರೆ ಅವರೆಲ್ಲರೂ ನಿಮಗೆ ಬಿಟ್ಟೀಕೆಲಸವನ್ನು ಮಾಡುವ ದಾಸರಾಗಬೇಕು.",
			"12": "<sup>12</sup> ಅವರು ನಿಮ್ಮ ಮಾತಿಗೆ ಸಮ್ಮತಿಸದೆ ಎದುರಿಸಿದರೆ ನೀವು ಆ ಪಟ್ಟಣಕ್ಕೆ ಮುತ್ತಿಗೆ ಹಾಕಬೇಕು.",
			"13": "<sup>13</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಆ ಪಟ್ಟಣವನ್ನು ನಿಮಗೆ ವಶಮಾಡಿದಾಗ ಅದರಲ್ಲಿರುವ ಗಂಡಸರೆಲ್ಲರನ್ನೂ ಕನಿಕರಿಸದೆ ಸಂಹಾರಮಾಡಬೇಕು.",
			"14": "<sup>14</sup> ಹೆಂಗಸರನ್ನೂ ಮಕ್ಕಳನ್ನೂ ದನಗಳನ್ನೂ ಊರಲ್ಲಿರುವ ಆಸ್ತಿಯನ್ನೂ ನೀವು ಸ್ವಂತಕ್ಕೆ ತೆಗೆದುಕೊಳ್ಳಬಹುದು. ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ವಶಪಡಿಸಿದ ಶತ್ರುಗಳ ಆಸ್ತಿಯನ್ನು ನೀವೇ ಅನುಭೋಗಿಸಬಹುದು.",
			"15": "<sup>15</sup> ಈ ದೇಶದ ಜನಾಂಗಗಳಿಗೆ ಸೇರದೆ ದೂರವಾಗಿರುವ ಜನಾಂಗಗಳ ಪಟ್ಟಣಗಳ ವಿಷಯದಲ್ಲಿ ನೀವು ಹಾಗೆಯೇ ನಡೆದುಕೊಳ್ಳಬೇಕು.",
			"16": "<sup>16</sup> ಆದರೆ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಸ್ವಂತಕ್ಕಾಗಿ ಕೊಡುವ ಈ ಜನಾಂಗಗಳ ಪಟ್ಟಣಗಳ ವಿಷಯದಲ್ಲಿ ಹಾಗೆ ಮಾಡದೆ ಉಸುರುಬಿಡುವ ಒಬ್ಬರನ್ನಾದರೂ ಉಳಿಸಬಾರದು.",
			"17": "<sup>17</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಅಪ್ಪಣೆಕೊಟ್ಟಂತೆ ಇವರನ್ನು ಅಂದರೆ ಹಿತ್ತಿಯರು, ಅಮೋರಿಯರು, ಕಾನಾನ್ಯರು, ಪೆರಿಜ್ಜೀಯರು, ಹಿವ್ವಿಯರು, ಯೆಬೂಸಿಯರು, ಇವರೆಲ್ಲರನ್ನೂ ನಾಶಮಾಡಿಬಿಡಬೇಕು.",
			"18": "<sup>18</sup> ಇಲ್ಲವಾದರೆ ಅವರು ತಮ್ಮ ದೇವರುಗಳಿಗಾಗಿ ಆಚರಿಸುವ ನಿಷಿದ್ಧಾಚಾರಗಳನ್ನು ನಿಮಗೆ ಕಲಿಸಿಕೊಟ್ಟಾರು; ಮತ್ತು ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ದ್ರೋಹಿಗಳಾದೀರಿ.",
			"19": "<sup>19</sup> ನೀವು ಒಂದು ಪಟ್ಟಣವನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವದಕ್ಕಾಗಿ ಅದಕ್ಕೆ ಬಹುದಿವಸ ಮುತ್ತಿಗೆಹಾಕಿರುವಾಗ ಅದಕ್ಕೆ ಸೇರಿರುವ ಮರಗಳನ್ನು ಹಾಳುಮಾಡಬಾರದು; ಅವುಗಳಿಗೆ ಕೊಡಲಿಯನ್ನು ಹಾಕಲೇಬಾರದು. ಅವುಗಳ ಹಣ್ಣನ್ನು ತಿನ್ನಬಹುದೇ ಹೊರತು ಮರಗಳನ್ನು ಕಡಿಯಬಾರದು. ಅಡವಿಯ ಮರಗಳು ಶತ್ರುಗಳೇನು? ಅವುಗಳೊಡನೆ ಯುದ್ಧಮಾಡಬಹುದೇ?",
			"20": "<sup>20</sup> ತಿನ್ನತಕ್ಕ ಹಣ್ಣು ಕೊಡುವದಿಲ್ಲವೆಂದು ನೀವು ತಿಳಿದ ಮರಗಳನ್ನು ಮಾತ್ರ ನೀವು ಕೆಡಿಸಬಹುದು, ಕಡಿಯಬಹುದು. ಅವುಗಳಿಂದ ಯುದ್ಧಯಂತ್ರಗಳನ್ನು ಮಾಡಿಕೊಂಡು ನಿಮಗೆ ವಿರುದ್ಧವಾಗಿರುವ ಆ ಪಟ್ಟಣವು ಬೀಳುವ ತನಕ ಅದಕ್ಕೆ ಮುತ್ತಿಗೆ ಹಾಕಬೇಕು."
		},
		"21": {
			"1": "<sup>1</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಸ್ವದೇಶವಾಗುವದಕ್ಕೆ ಕೊಡುವ ದೇಶದಲ್ಲಿ ಹತನಾದ ಮನುಷ್ಯನ ಶವವು ಅಡವಿಯಲ್ಲಿ ಬಿದ್ದಿರುವದನ್ನು ನೀವು ಕಂಡರೆ ಮತ್ತು ಅವನನ್ನು ಕೊಂದವನು ಯಾರೆಂಬದು ತಿಳಿಯದೆಹೋದರೆ",
			"2": "<sup>2</sup> ನಿಮ್ಮ ಹಿರಿಯರೂ ನ್ಯಾಯಾಧಿಪತಿಗಳೂ ಬಂದು ಹತನಾದವನ ಶವದ ಸುತ್ತಲಿರುವ ಊರುಗಳಲ್ಲಿ ಯಾವದು ಹತ್ತಿರವೆಂದು ತಿಳಿದುಕೊಳ್ಳುವದಕ್ಕೆ ಅಳತೆಮಾಡಬೇಕು.",
			"3": "<sup>3</sup> ಯಾವ ಊರು ಹತ್ತಿರವಾಗಿದೆಯೋ ಆ ಊರಿನ ಹಿರಿಯರು ಇನ್ನೂ ನೊಗಕ್ಕೆ ಕಟ್ಟಲ್ಪಡದೆಯೂ ಯಾವ ಕೆಲಸವನ್ನೂ ಮಾಡದೆಯೂ ಇರುವ ಒಂದು ಮಣಕವನ್ನು ತೆಗೆದುಕೊಂಡು ಎಂದಿಗೂ ವ್ಯವಸಾಯವಿಲ್ಲದಂಥ,",
			"4": "<sup>4</sup> ನೀರು ಯಾವಾಗಲೂ ಹರಿಯುವಂಥ ತಗ್ಗಿಗೆ ಹೋಗಿ ಅಲ್ಲಿ ಅದರ ಕುತ್ತಿಗೆಯನ್ನು ಮುರಿದು ಕೊಲ್ಲಬೇಕು.",
			"5": "<sup>5</sup> ಲೇವಿಕುಲದವರಾದ ಯಾಜಕರಲ್ಲಿ ಕೆಲವರು ಹತ್ತಿರವಿರಬೇಕು. ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಅವರನ್ನೇ ತನ್ನ ಸಾನ್ನಿಧ್ಯಸೇವೆಯನ್ನು ಮಾಡುವದಕ್ಕೂ ತನ್ನ ಹೆಸರಿನಿಂದ ಜನರನ್ನು ಆಶೀರ್ವದಿಸುವದಕ್ಕೂ ಆದುಕೊಂಡಿದ್ದಾನಲ್ಲ; ಅನುಮಾನವಾದ ಎಲ್ಲಾ ವ್ಯಾಜ್ಯಗಳ ಮತ್ತು ಹೊಡೆದಾಟಗಳ ವಿಷಯದಲ್ಲಿ ಅವರೇ ತೀರ್ಮಾನಿಸಬೇಕು.",
			"6": "<sup>6</sup> ಹತನಾದವನ ಶವಕ್ಕೆ ಹತ್ತಿರವಿರುವ ಗ್ರಾಮದ ಹಿರಿಯರು ತಗ್ಗಿನಲ್ಲಿ ಕುತ್ತಿಗೆ ಮುರಿಯಲ್ಪಟ್ಟ ಆ ಮಣಕದ ಮೇಲೆ ಕೈಗಳನ್ನು ತೊಳೆದುಕೊಂಡು -",
			"7": "<sup>7</sup> ನಮ್ಮ ಕೈಗಳು ಈ ಹತ್ಯವನ್ನು ಮಾಡಲಿಲ್ಲ. ನಮ್ಮ ಕಣ್ಣುಗಳು ನೋಡಲಿಲ್ಲ;",
			"8": "<sup>8</sup> ಯೆಹೋವನೇ, ನೀನು ಬಿಡುಗಡೆಮಾಡಿದ ನಿನ್ನ ಜನರನ್ನು ಕ್ಷಮಿಸಬೇಕು; ಅನ್ಯಾಯವಾದ ನರಹತ್ಯದೋಷಫಲವು ನಿನ್ನ ಜನರಾದ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ತಗಲದಿರಲಿ ಎಂದು ಹೇಳಬೇಕು.",
			"9": "<sup>9</sup> ಆಗ ಆ ರಕ್ತಾಪರಾಧವು ಕ್ಷಮಿಸಲ್ಪಡುವದು. ಹೀಗೆ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ಸರಿಯಾದದ್ದನ್ನು ಮಾಡುವದರಿಂದ ನೀವೇ ಅನ್ಯಾಯವಾದ ನರಹತ್ಯದೋಷವನ್ನು ನಿಮ್ಮ ಮಧ್ಯದಿಂದ ತೆಗೆದುಹಾಕುವಿರಿ.",
			"10": "<sup>10</sup> ನೀವು ಶತ್ರುಗಳೊಡನೆ ಯುದ್ಧಮಾಡಲಾಗಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಅನುಗ್ರಹದಿಂದ ಅವರನ್ನು ನಿಮ್ಮಿಂದ ಅಪಜಯಪಡಿಸಿ ಸೆರೆಹಿಡಿದಾಗ",
			"11": "<sup>11</sup> ನಿಮ್ಮಲ್ಲಿ ಯಾವನಾದರೂ ಸೆರೆಯವರಲ್ಲಿ ಸುಂದರಸ್ತ್ರೀಯನ್ನು ಕಂಡು ಮೋಹಿಸಿ ಮದುವೆಮಾಡಿಕೊಳ್ಳಬೇಕೆಂದು ಅಪೇಕ್ಷಿಸಿದರೆ",
			"12": "<sup>12</sup> ಅವಳನ್ನು ಮನೆಗೆ ಕರಕೊಂಡು ಬರಲಿ; ತರುವಾಯ ಅವಳು ಕ್ಷೌರಮಾಡಿಸಿಕೊಂಡು ಉಗುರುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು",
			"13": "<sup>13</sup> ಸೆರೆಯ ಬಟ್ಟೆಗಳನ್ನು ತೆಗೆದಿಟ್ಟು ಅವನ ಮನೆಯಲ್ಲಿ ಒಂದು ತಿಂಗಳಿನವರೆಗೆ ತಾಯಿತಂದೆಗಳ ವಿಯೋಗದ ನಿಮಿತ್ತ ಹಂಬಲಿಸಲಿ. ಆಮೇಲೆ ಅವನು ಅವಳನ್ನು ಹೆಂಡತಿಯನ್ನಾಗಿ ಮಾಡಿಕೊಳ್ಳಬಹುದು.",
			"14": "<sup>14</sup> ತರುವಾಯ ಅವಳು ಅವನಿಗೆ ಇಷ್ಟವಾಗದೆ ಹೋದರೆ ಅವನು ಅವಳನ್ನು ಅವಳ ಮನಸ್ಸು ಬಂದಲ್ಲಿಗೆ ಕಳುಹಿಸಿಬಿಡಬೇಕು. ಅವಳನ್ನು ಕೂಡಿದ್ದರಿಂದ ದಾಸತ್ವಕ್ಕೆ ಮಾರಲೂಬಾರದು, ದಾಸಿಯಂತೆ ನಡಿಸಲೂ ಬಾರದು.",
			"15": "<sup>15</sup> ಯಾವನಾದರೂ ಇಬ್ಬರು ಹೆಂಡತಿಯರನ್ನು ಮಾಡಿಕೊಂಡು ಅವರಲ್ಲಿ ಒಬ್ಬಳನ್ನು ಪ್ರೀತಿಸಿ ಮತ್ತೊಬ್ಬಳನ್ನು ತಿರಸ್ಕರಿಸುವ ಸಂದರ್ಭದಲ್ಲಿ ಅವರಿಬ್ಬರೂ ಅವನಿಂದ ಮಕ್ಕಳನ್ನು ಪಡೆದವರಾಗಿರಲಾಗಿ ಚೊಚ್ಚಲುಮಗನು ತಿರಸ್ಕರಿಸಲ್ಪಟ್ಟವಳಲ್ಲಿಯೇ ಹುಟ್ಟಿದ ಪಕ್ಷಕ್ಕೆ",
			"16": "<sup>16</sup> ತಂದೆಯು ತನ್ನ ಆಸ್ತಿಯನ್ನು ಮಕ್ಕಳಿಗೆ ಸ್ವಾಸ್ತ್ಯವಾಗಿ ಕೊಡುವಾಗ ಆ ತಿರಸ್ಕರಿಸಲ್ಪಟ್ಟವಳ ಮಗನನ್ನು ತಳ್ಳಿಬಿಟ್ಟು ಪ್ರೀತಿಯ ಹೆಂಡತಿಯ ಮಗನನ್ನೇ ಚೊಚ್ಚಲವನೆಂದು ಭಾವಿಸಕೂಡದು.",
			"17": "<sup>17</sup> ತಿರಸ್ಕರಿಸಲ್ಪಟ್ಟವಳ ಮಗನೇ ಚೊಚ್ಚಲವನೆಂದು ಒಪ್ಪಿ ಅವನಿಗೆ ಎರಡು ಭಾಗಗಳನ್ನು ಕೊಡಬೇಕು. ಅವನೇ ತಂದೆಯ ವೀರ್ಯಕ್ಕೆ ಪ್ರಥಮಫಲವೂ ಚೊಚ್ಚಲುತನದ ಹಕ್ಕಿಗೆ ಬಾಧ್ಯನೂ ಆಗಿದ್ದಾನಲ್ಲಾ,",
			"18": "<sup>18</sup> ಒಬ್ಬ ಮಗನು ತಂದೆತಾಯಿಗಳ ಆಜ್ಞೆಗೆ ಒಳಗಾಗದೆ ಶಿಕ್ಷಿಸಲ್ಪಟ್ಟರೂ ಮೊಂಡನೂ ಅವಿಧೇಯನೂ ಆಗಿ",
			"19": "<sup>19</sup> ಅವರ ಮಾತನ್ನು ಕೇಳದೆಹೋದರೆ ತಂದೆತಾಯಿಗಳು ಅವನನ್ನು ಹಿಡಿದು ಊರುಬಾಗಿಲಿಗೆ ಹಿರಿಯರ ಮುಂದೆ ತಂದು ಅವರಿಗೆ -",
			"20": "<sup>20</sup> ಈ ನಮ್ಮ ಮಗನು ನಮ್ಮ ಮಾತನ್ನು ಕೇಳುವದೇ ಇಲ್ಲ. ಆಜ್ಞೆಗೆ ಒಳಗಾಗುವದಿಲ್ಲ; ಇವನು ಮೊಂಡ ಕುಡಿಕ ತುಂಟ ಎಂದು ಸಾಕ್ಷಿ ಹೇಳಬೇಕು. ಆಗ ಊರಿನವರೆಲ್ಲರೂ ಅವನನ್ನು ಕಲ್ಲೆಸೆದು ಕೊಲ್ಲಬೇಕು.",
			"21": "<sup>21</sup> ಹೀಗೆ ನೀವು ಆ ದುಷ್ಟತ್ವವನ್ನು ನಿಮ್ಮ ಮಧ್ಯದೊಳಗಿಂದ ತೆಗೆದುಹಾಕಿಬಿಡಬೇಕು. ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರೂ ಕೇಳಿ ಭಯಪಡುವರು.",
			"22": "<sup>22</sup> ಅಪರಾಧ ಮಾಡಿದವನು ಮರಣಶಿಕ್ಷೆಯನ್ನು ಹೊಂದಿದ ಮೇಲೆ ನೀವು ಅವನ ಶವವನ್ನು ಮರಕ್ಕೆ ತೂಗಹಾಕಿದರೆ ಅದು ರಾತ್ರಿಯಲ್ಲಿಯೂ ಮರದ ಮೇಲೆ ಇರಬಾರದು;",
			"23": "<sup>23</sup> ಅದೇ ದಿನದಲ್ಲಿ ಅದನ್ನು ನೆಲದಲ್ಲಿ ಹೂಣಬೇಕು. ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಸ್ವದೇಶವಾಗುವದಕ್ಕೆ ಕೊಡುವ ದೇಶವು ಅಪವಿತ್ರವಾಗಬಾರದು. ಮರಕ್ಕೆ ತೂಗಹಾಕಲ್ಪಟ್ಟವನು ದೇವರ ಶಾಪವನ್ನು ಹೊಂದಿದವನಲ್ಲವೇ."
		},
		"22": {
			"1": "<sup>1</sup> ಸ್ವದೇಶದವನ ಎತ್ತಾಗಲಿ ಕುರಿಯಾಗಲಿ ದಾರಿತಪ್ಪಿ ಹೋಗಿರುವದನ್ನು ನೀವು ಕಂಡರೆ ಕಾಣದವರಂತೆ ಅದನ್ನು ಬಿಟ್ಟು ಹೋಗಬಾರದು; ಅವನ ಬಳಿಗೆ ಅಟ್ಟಿಕೊಂಡುಹೋಗಿ ಕೊಡಲೇಬೇಕು.",
			"2": "<sup>2</sup> ಅವನು ನಿಮಗೆ ದೂರವಾಗಿದ್ದರೆ ಇಲ್ಲವೆ ಅವನು ಯಾರೋ ಎಂದು ನಿಮಗೆ ತಿಳಿಯದೆಹೋದರೆ ಆ ಪಶುವನ್ನು ನಿಮ್ಮ ಮನೆಗೆ ಹೊಡಕೊಂಡು ಬಂದು ಕಾಯಬೇಕು; ಅವನು ಅದನ್ನು ಹುಡುಕುತ್ತಾ ಬಂದಾಗ ನೀವು ಅದನ್ನು ಅವನಿಗೆ ತಿರಿಗಿ ಕೊಡಬೇಕು.",
			"3": "<sup>3</sup> ಅವನ ಕತ್ತೆಯಾಗಲಿ ಬಟ್ಟೆಯಾಗಲಿ ಅವನು ಕಳೆದುಕೊಂಡ ಯಾವ ವಸ್ತುವಾಗಲಿ ನಿಮಗೆ ಸಿಕ್ಕಿದರೆ ಹಾಗೆಯೇ ಮಾಡಬೇಕು; ಕಾಣದವರಂತೆ ಇರಲೇ ಬಾರದು.",
			"4": "<sup>4</sup> ಸ್ವದೇಶದವನ ಕತ್ತೆಯಾಗಲಿ ಎತ್ತಾಗಲಿ ದಾರಿಯಲ್ಲಿ ಬಿದ್ದಿರುವದನ್ನು ನೀವು ಕಂಡರೆ ಕಾಣದವರಂತೆ ಹೋಗದೆ ಅವನಿಗೆ ಸಹಾಯಮಾಡಿ ಎಬ್ಬಿಸಬೇಕು.",
			"5": "<sup>5</sup> ಸ್ತ್ರೀಯು ಪುರುಷವೇಷವನ್ನೂ ಪುರುಷನು ಸ್ತ್ರೀವೇಷವನ್ನೂ ಹಾಕಿಕೊಳ್ಳಕೂಡದು; ಹಾಗೆ ಮಾಡುವವರು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಹೇಯರು.",
			"6": "<sup>6</sup> ನೀವು ದಾರಿಯಲ್ಲಿ ಹೋಗುವಾಗ ಮರದ ಮೇಲಾಗಲಿ ನೆಲದ ಮೇಲಾಗಲಿ ಇರುವ ಒಂದು ಪಕ್ಷಿಯ ಗೂಡಿನೊಳಗೆ ಮರಿಗಳು ಅಥವಾ ತತ್ತಿಗಳು ಇದ್ದು ಅವುಗಳ ಮೇಲೆ ತಾಯಿ ಹೊದಗಿರುವದನ್ನು ಕಂಡರೆ ಮರಿಗಳನ್ನು ತೆಗೆದುಕೊಳ್ಳಬಹುದೇ ಹೊರತು ತಾಯಿಯನ್ನು ಹಿಡಿದುಕೊಳ್ಳಬಾರದು.",
			"7": "<sup>7</sup> ನೀವು ಹೀಗೆ ನಡೆದುಕೊಂಡರೆ ನಿಮಗೆ ಶ್ರೇಯಸ್ಸುಂಟಾಗುವದು. ಮತ್ತು ಬಹುಕಾಲ ಬಾಳುವಿರಿ.",
			"8": "<sup>8</sup> ಹೊಸ ಮನೆಯನ್ನು ಕಟ್ಟಿಸಿಕೊಳ್ಳುವಾಗ ಯಾವನಾದರೂ ಅದರ ಮಾಳಿಗೆಯಿಂದ ಬಿದ್ದು ನಿಮಗೆ ಜೀವಹತ್ಯ ದೋಷವುಂಟಾಗದಂತೆ ಮಾಳಿಗೆಯ ಮೇಲೆ ಸುತ್ತಲೂ ಸಣ್ಣ ಗೋಡೆಯನ್ನು ಕಟ್ಟಿಸಬೇಕು.",
			"9": "<sup>9</sup> ದ್ರಾಕ್ಷೇತೋಟಗಳಲ್ಲಿ ಬೇರೆ ವಿಧವಾದ ಬಿತ್ತನೆ ಮಾಡಬಾರದು; ಹಾಗೆ ಮಾಡಿದರೆ ಆ ಬೆಳೆಯೂ ಮತ್ತು ದ್ರಾಕ್ಷೆಯ ಬೆಳೆಯೂ ಅಂತೂ ಆ ತೋಟದ ಎಲ್ಲಾ ಬೆಳೆಯೂ ದೇವರದಾಗುವದು.",
			"10": "<sup>10</sup> ಎತ್ತನ್ನೂ ಕತ್ತೆಯನ್ನೂ ಜೊತೆಮಾಡಿ ನೇಗಿಲಿಗೆ ಕಟ್ಟಬಾರದು.",
			"11": "<sup>11</sup> ನಾರೂ ಉಣ್ಣೆಯೂ ಕೂಡಿರುವ ಬಟ್ಟೆಯನ್ನು ಹಾಕಿಕೊಳ್ಳಬಾರದು.",
			"12": "<sup>12</sup> ನೀವು ಹೊದ್ದುಕೊಳ್ಳುವ ಬಟ್ಟೆಯ ನಾಲ್ಕು ಮೂಲೆಗಳಲ್ಲಿ ಗೊಂಡೆಗಳನ್ನು ಕಟ್ಟಬೇಕು.",
			"13": "<sup>13</sup> ಯಾವನಾದರೂ ತಾನು ಮದುವೆಮಾಡಿಕೊಂಡ ಸ್ತ್ರೀಯನ್ನು ದ್ವೇಷಿಸಿ ಅವಳ ವಿಷಯದಲ್ಲಿ ಸುಮ್ಮಸುಮ್ಮನೆ ಕೆಟ್ಟ ಮಾತುಗಳನ್ನು ಆಡಿ -",
			"14": "<sup>14</sup> ನಾನು ಇವಳನ್ನು ಮದುವೆಮಾಡಿಕೊಂಡೆನು; ಆದರೆ ಕೂಡಿದಾಗ ಇವಳು ಕನ್ನಿಕೆಯಲ್ಲವೆಂದು ತಿಳಿದುಬಂತು ಎಂದು ಹೇಳಿ ಅವಳ ಹೆಸರನ್ನು ಕೆಡಿಸಿದರೆ",
			"15": "<sup>15</sup> ಅವಳ ತಾಯಿತಂದೆಗಳು ತಮ್ಮ ಮಗಳು ವಿವಾಹಕ್ಕೆ ಮುಂಚೆ ಪುರುಷ ಸಂಪರ್ಕವಿಲ್ಲದವಳು ಎಂಬುದಕ್ಕೆ ನಿದರ್ಶನವನ್ನು ಊರುಬಾಗಿಲಿಗೆ ಹಿರಿಯರ ಮುಂದೆ ತಂದು ತೋರಿಸಬೇಕು.",
			"16": "<sup>16</sup> ತಂದೆ ಅವರಿಗೆ - ನಾನು ನನ್ನ ಮಗಳನ್ನು ಈ ಪುರುಷನಿಗೆ ಮದುವೆಮಾಡಿಕೊಟ್ಟ ಮೇಲೆ",
			"17": "<sup>17</sup> ಇವನು ಅವಳನ್ನು ದ್ವೇಷಿಸಿ ಪರಪುರುಷಸಂಪರ್ಕ ಮಾಡಿದವಳೆಂದು ನಿರಾಧಾರವಾದ ಮಾತುಗಳನ್ನಾಡಿ ಅವಳ ಹೆಸರನ್ನು ಅವಮಾನಪಡಿಸುತ್ತಾನೆ. ಇಗೋ ನನ್ನ ಮಗಳು ವಿವಾಹಕ್ಕೆ ಮುಂಚೆ ಪುರುಷಸಂಪರ್ಕ ಮಾಡಿದವಳಲ್ಲವೆಂಬದಕ್ಕೆ ಇದೇ ಪ್ರಮಾಣ ಎಂದು ಹೇಳಿ ಅವಳ ಹಚ್ಚಡವನ್ನು ಹಿರಿಯರ ಮುಂದೆ ಇಡಬೇಕು.",
			"18": "<sup>18</sup> ಇಸ್ರಾಯೇಲ್ಯಳಾದ ಹೆಣ್ಣಿನ ವಿಷಯದಲ್ಲಿ ಆ ಪುರುಷನು ನಿರಾಧಾರವಾದ ತಪ್ಪುಹೊರಿಸಿದ್ದರಿಂದ",
			"19": "<sup>19</sup> ಆ ಊರಿನ ಹಿರಿಯರು ಅವನನ್ನು ಹಿಡಿಸಿ ಹೊಡಿಸಿ ನೂರು ರೂಪಾಯಿ ದಂಡವನ್ನು ವಿಧಿಸಿ ಆ ಹಣವನ್ನು ಅವಳ ತಂದೆಗೆ ಕೊಡಿಸಬೇಕು. ಮತ್ತು ಅವನು ಆ ಸ್ತ್ರೀಯನ್ನು ಹೆಂಡತಿಯನ್ನಾಗಿಯೇ ಇಟ್ಟುಕೊಳ್ಳಬೇಕು; ಅವನ ಜೀವಮಾನಕಾಲವೆಲ್ಲಾ ಅವಳನ್ನು ಪರಿತ್ಯಾಗಮಾಡುವದಕ್ಕೆ ಅವನಿಗೆ ಅಧಿಕಾರವಿರುವದಿಲ್ಲ.",
			"20": "<sup>20</sup> ಆದರೆ ಆ ಹೆಣ್ಣು ವಿವಾಹಕ್ಕೆ ಮುಂಚೆ ಪುರುಷ ಸಂಪರ್ಕಮಾಡಿದವಳೇ ಎಂದು ತಿಳಿದುಬಂದರೆ",
			"21": "<sup>21</sup> ಅವಳು ತಂದೆಯ ಮನೆಯಲ್ಲೇ ಸೂಳೆತನಮಾಡಿ ಇಸ್ರಾಯೇಲ್ಯರೊಳಗೆ ದುರಾಚಾರವನ್ನು ನಡಿಸಿದ್ದರಿಂದ ಅವರು ಅವಳನ್ನು ತಂದೆಯ ಮನೆಯ ಬಾಗಿಲಿಗೆ ತರಿಸಬೇಕು; ಊರಿನವರೆಲ್ಲರು ಕಲ್ಲೆಸೆದು ಅವಳನ್ನು ಕೊಲ್ಲಬೇಕು. ಹೀಗೆ ಆ ದುಷ್ಟತ್ವವನ್ನು ನಿಮ್ಮ ಮಧ್ಯದಿಂದ ತೆಗೆದುಹಾಕಬೇಕು.",
			"22": "<sup>22</sup> ಯಾವನಾದರೂ ಪರನ ಹೆಂಡತಿಯೊಡನೆ ವ್ಯಭಿಚಾರಮಾಡಿದ್ದು ಹೊರಬಿದ್ದರೆ ಆ ಸ್ತ್ರೀ ಪುರುಷರಿಬ್ಬರಿಗೂ ಮರಣ ಶಿಕ್ಷೆಯಾಗಬೇಕು. ಹೀಗೆ ನೀವು ಆ ದುಷ್ಟತ್ವವನ್ನು ಇಸ್ರಾಯೇಲ್ಯರ ಮಧ್ಯದಿಂದ ತೆಗೆದುಹಾಕಬೇಕು.",
			"23": "<sup>23</sup> ಒಬ್ಬನಿಗೆ ನಿಶ್ಚಿತಳಾದ ಹೆಣ್ಣನ್ನು ಯಾವನಾದರೂ ಊರೊಳಗೆ ಮರುಳುಗೊಳಿಸಿ ಸಂಗಮಿಸಿದರೆ",
			"24": "<sup>24</sup> ನೀವು ಅವರಿಬ್ಬರನ್ನೂ ಊರುಬಾಗಿಲಿನ ಹೊರಕ್ಕೆ ತರಿಸಿ ಕಲ್ಲೆಸೆದು ಕೊಲ್ಲಬೇಕು. ಒಬ್ಬನಿಗೆ ನಿಶ್ಚಿತಳಾದ ಸ್ತ್ರೀಯನ್ನು ಕೆಡಿಸಿದ್ದರಿಂದ ಆ ಪುರುಷನಿಗೂ ಊರಲ್ಲಿದ್ದು ಕೂಗಿಕೊಳ್ಳದೆಹೋದದರಿಂದ ಆ ಸ್ತ್ರೀಗೂ ಮರಣಶಿಕ್ಷೆಯಾಗಬೇಕು. ಹೀಗೆ ನೀವು ಆ ದುಷ್ಟತ್ವವನ್ನು ನಿಮ್ಮ ಮಧ್ಯದಿಂದ ತೆಗೆದುಹಾಕಬೇಕು.",
			"25": "<sup>25</sup> ಆದರೆ ಆ ಪುರುಷನು ನಿಶ್ಚಿತಳಾದ ಆ ಸ್ತ್ರೀಯನ್ನು ಅಡವಿಯಲ್ಲಿ ಬಲಾತ್ಕಾರದಿಂದ ಸಂಗಮಿಸಿದ್ದರೆ ಪುರುಷನಿಗೆ ಮಾತ್ರ ಮರಣಶಿಕ್ಷೆಯಾಗಬೇಕು.",
			"26": "<sup>26</sup> ಆ ಸ್ತ್ರೀಗೆ ಯಾವ ಶಿಕ್ಷೆಯೂ ಆಗಬಾರದು; ಅವಳಲ್ಲಿ ಮರಣ ಶಿಕ್ಷೆಗೆ ಪಾತ್ರವಾದ ಅಪರಾಧವೇನೂ ಇಲ್ಲ. ಒಬ್ಬ ಮನುಷ್ಯನು ಮತ್ತೊಬ್ಬನ ಮೇಲೆ ಬಿದ್ದು ಕೊಲ್ಲುವದು ಹೇಗೋ ಈ ಸಂಗತಿ ಹಾಗೆಯೇ ಆಯಿತು;",
			"27": "<sup>27</sup> ಆ ಸ್ತ್ರೀಯು ಅಡವಿಯಲ್ಲೇ ಅವನ ಕೈಗೆ ಸಿಕ್ಕಿದ್ದರಿಂದ ಅವಳು ಕೂಗಿಕೊಂಡರೂ ತಪ್ಪಿಸುವವರು ಇರಲಿಲ್ಲ.",
			"28": "<sup>28</sup> ಯಾರಿಗೂ ನಿಶ್ಚಯವಾಗದೆ ಇರುವ ಸ್ತ್ರೀಯನ್ನು ಒಬ್ಬನು ಹಿಡಿದು ಸಂಗಮಿಸಿದ್ದು ಬೈಲಿಗೆ ಬಂದರೆ",
			"29": "<sup>29</sup> ಅವನು ಅವಳನ್ನು ಮಾನಭಂಗಪಡಿಸಿದ್ದರಿಂದ ಅವಳ ತಂದೆಗೆ ಐವತ್ತು ರೂಪಾಯಿ ಕೊಟ್ಟು ಅವಳನ್ನು ಹೆಂಡತಿಯನ್ನಾಗಿ ಮಾಡಿಕೊಳ್ಳಬೇಕು. ಅವನು ಬದುಕುವ ತನಕ ಅವಳನ್ನು ಪರಿತ್ಯಾಗಮಾಡುವದಕ್ಕೆ ಅವನಿಗೆ ಅಧಿಕಾರವಿರುವದಿಲ್ಲ.",
			"30": "<sup>30</sup> ತಂದೆಗೆ ಹೆಂಡತಿಯಾದವಳನ್ನು ಸಂಗಮಿಸಲೇಬಾರದು; ಅದು ತಂದೆಗೆ ಅಪಮಾನಪಡಿಸಿದ ಹಾಗಾಗುವದು."
		},
		"23": {
			"1": "<sup>1</sup> ಬೀಜಹೊಡಿಸಿಕೊಂಡಾಗಲಿ ರಹಸ್ಯಾಂಗ ಛೇದಿಸಿಕೊಂಡಾಗಲಿ ನಪುಂಸಕರಾದವರು ಯೆಹೋವನ ಸಭೆಯಲ್ಲಿ ಸೇರಬಾರದು.",
			"2": "<sup>2</sup> ಅಗಮ್ಯಾಗಮನದಿಂದುಂಟಾದ ಸಂತತಿಯವರು ಹತ್ತನೆಯ ತಲೆಯವರಾದರೂ ಯೆಹೋವನ ಸಭೆಗೆ ಸೇರಬಾರದು.",
			"3": "<sup>3</sup> ಅಮ್ಮೋನಿಯರಾಗಲಿ ಮೋವಾಬ್ಯರಾಗಲಿ ಯೆಹೋವನ ಸಭೆಗೆ ಎಂದೆಂದಿಗೂ ಸೇರಬಾರದು. ಅವರ ಸಂತತಿಯವರು ಹತ್ತನೆಯ ತಲೆಯವರಾದರೂ ಯೆಹೋವನ ಸಭೆಗೆ ಸೇರಬಾರದು.",
			"4": "<sup>4</sup> ಯಾಕಂದರೆ ನೀವು ಐಗುಪ್ತದೇಶದಿಂದ ಬಂದಾಗ [ಅಮ್ಮೋನಿಯರು] ಅನ್ನ ಪಾನಗಳನ್ನು ತಂದು ನಿಮ್ಮನ್ನು ಎದುರುಗೊಳ್ಳಲಿಲ್ಲ; [ಮೋವಾಬ್ಯರು] ನಿಮ್ಮನ್ನು ಶಪಿಸುವದಕ್ಕಾಗಿ ಬೆಯೋರನ ಮಗನಾದ ಬಿಳಾಮನಿಗೆ ಹಣ ಕೊಟ್ಟು ಎರಡು ನದಿಗಳ ಮಧ್ಯದಲ್ಲಿರುವ ಅರಾಮಿನ ಪೆತೋರೂರಿನಿಂದ ಅವನನ್ನು ಕರಿಸಿದರು.",
			"5": "<sup>5</sup> (ಆದರೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಪ್ರೀತಿಸಿದ್ದರಿಂದ ಬಿಳಾಮನ ಮಾತಿಗೆ ಸಮ್ಮತಿಸದೆ ಅವನಿಂದ ಶಾಪವನ್ನು ನುಡಿಸದೆ ಆಶೀರ್ವಾದವನ್ನೇ ಹೇಳಿಸಿದನು.)",
			"6": "<sup>6</sup> ನಿಮ್ಮ ಜೀವಮಾನಕಾಲವೆಲ್ಲಾ ಅವರ ಯೋಗಕ್ಷೇಮವನ್ನು ಬಯಸಲೇ ಬಾರದು.",
			"7": "<sup>7</sup> ಎದೋಮ್ಯರನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ನಿಷೇಧಿಸಬಾರದು; ಅವರು ಬಂಧುಜನಗಳು. ಐಗುಪ್ತ್ಯರನ್ನೂ ಸಂಪೂರ್ಣವಾಗಿ ನಿಷೇಧಿಸಬಾರದು; ಅವರ ದೇಶದಲ್ಲಿ ನೀವು ಪ್ರವಾಸಿಗಳಾಗಿದ್ದಿರಲ್ಲಾ.",
			"8": "<sup>8</sup> ಅವರ ಸಂತತಿಯವರಲ್ಲಿ ಮೂರನೆಯ ತಲೆಯವರು ಯೆಹೋವನ ಸಭೆಗೆ ಸೇರಬಹುದು.",
			"9": "<sup>9</sup> ನೀವು ಶತ್ರುಗಳೊಡನೆ ಯುದ್ಧಕ್ಕೆ ಹೊರಟು ಪಾಳೆಯದಲ್ಲಿರುವಾಗ ಯಾವ ಅಶುಚಿಯೂ ಆಗದಂತೆ ಎಚ್ಚರದಿಂದಿರಬೇಕು.",
			"10": "<sup>10</sup> ರಾತ್ರಿಕಾಲದಲ್ಲಿ ವೀರ್ಯಸ್ಖಲನದಿಂದ ಯಾವನಾದರೂ ಅಶುದ್ಧನಾದರೆ ಅವನು ಪಾಳೆಯದೊಳಗೆ ಇರದೆ ಹೊರಗೆ ಇರಬೇಕು.",
			"11": "<sup>11</sup> ಅವನು ಸಂಜೇವೇಳೆಯಲ್ಲಿ ಸ್ನಾನಮಾಡಿ ಸೂರ್ಯನು ಮುಣುಗಿದ ಮೇಲೆ ಪಾಳೆಯದೊಳಗೆ ಬರಬಹುದು.",
			"12": "<sup>12</sup> ಪಾಳೆಯದ ಹೊರಗೆ ಪಾಯಖಾನೆಗಾಗಿ ಒಂದು ಸ್ಥಳವನ್ನು ಗೊತ್ತುಮಾಡಬೇಕು.",
			"13": "<sup>13</sup> ಯುದ್ಧದ ಸಾಮಾನುಗಳಲ್ಲದೆ ಸಲಿಕೆ ನಿಮ್ಮ ಬಳಿಯಲ್ಲಿ ಇರಬೇಕು; ನೀವು ಬಹಿರ್ಭೂಮಿಗೆ ಹೋದಾಗ ಆ ಸಲಿಕೆಯಿಂದ ಅಗೆದು ಕಲ್ಮಷವನ್ನು ಮುಚ್ಚಿಬಿಡಬೇಕು.",
			"14": "<sup>14</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಕಾಪಾಡುವದಕ್ಕೂ ಶತ್ರುಗಳನ್ನು ನಿಮ್ಮ ಕೈವಶಮಾಡುವದಕ್ಕೂ ನಿಮ್ಮ ಪಾಳೆಯದೊಳಗೆ ಸಂಚಾರಮಾಡುತ್ತಾನಲ್ಲಾ; ಆದದರಿಂದ ಪಾಳೆಯವು ನಿರ್ಮಲವಾಗಿರಬೇಕು; ನಿಮ್ಮಲ್ಲಿ ಅಶುಚಿಯೇನಾದರೂ ಕಂಡುಬಂದರೆ ಆತನು ನಿಮ್ಮನ್ನು ಬಿಟ್ಟುಹೋದಾನು.",
			"15": "<sup>15</sup> ತಪ್ಪಿಸಿಕೊಂಡ ದಾಸನು ನಿಮ್ಮಲ್ಲಿರುವದಕ್ಕೆ ಬಂದರೆ ಅವನನ್ನು ಅವನ ದಣಿಗೆ ತಿರಿಗಿ ವಶಪಡಿಸಬಾರದು.",
			"16": "<sup>16</sup> ನಿಮ್ಮ ಊರುಗಳಲ್ಲಿ ಯಾವ ಸ್ಥಳವು ಅವನಿಗೆ ಇಷ್ಟವಾಗಿದೆಯೋ ಅಲ್ಲೇ ಅವನು ವಾಸವಾಗಿರಬಹುದು; ನಿರ್ಬಂಧಪಡಿಸಬಾರದು.",
			"17": "<sup>17</sup> ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಯಾವ ಸ್ತ್ರೀಯೂ ದೇವದಾಸಿಯಾಗಬಾರದು; ಯಾವ ಪುರುಷನೂ ಗುದಮೈಥುನಿಯಾಗಬಾರದು.",
			"18": "<sup>18</sup> ಸೂಳೆತನದಿಂದಾಗಲಿ ಗುದಮೈಥುನದಿಂದಾಗಲಿ ಸಂಪಾದಿಸಿದ ಹಣವನ್ನು ಹರಕೆಯಾಗಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಮಂದಿರದೊಳಗೆ ತರಲೇಬಾರದು; ಈ ಎರಡೂ ಆತನಿಗೆ ಅಸಹ್ಯ.",
			"19": "<sup>19</sup> ಹಣವನ್ನಾಗಲಿ ಆಹಾರಪದಾರ್ಥಗಳನ್ನಾಗಲಿ ಬೇರೆ ಯಾವದನ್ನಾಗಲಿ",
			"20": "<sup>20</sup> ಪರದೇಶದವನಿಗೆ ಬಡ್ಡಿಗೆ ಕೊಡಬಹುದೇ ಹೊರತು ಸ್ವದೇಶದವನಿಗೆ ಕೊಡಬಾರದು. ಹೀಗೆ ನಡೆದರೆ ನೀವು ಸ್ವದೇಶವಾಗಿ ಹೊಂದುವದಕ್ಕೆ ಹೋಗುವ ದೇಶದಲ್ಲಿ ನಿಮ್ಮ ಎಲ್ಲಾ ಪ್ರಯತ್ನಗಳಲ್ಲಿಯೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಅಭಿವೃದ್ಧಿಪಡಿಸುವನು.",
			"21": "<sup>21</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಹರಕೆಮಾಡಿದ ಮೇಲೆ ಅದನ್ನು ತಡಮಾಡದೆ ತೀರಿಸಬೇಕು; ಆತನು ತಪ್ಪದೆ ಅದನ್ನು ವಿಚಾರಿಸುವನು; ತೀರಿಸದೆ ಹೋಗುವದು ಪಾಪ.",
			"22": "<sup>22</sup> ನೀವು ಹರಕೆಮಾಡದಿದ್ದರೆ ದೋಷವೇನೂ ಇರಲಿಲ್ಲ;",
			"23": "<sup>23</sup> ಆದರೆ ಬಾಯಿಂದ ನುಡಿದದ್ದನ್ನು ನೆರವೇರಿಸಲೇಬೇಕು. ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಬಾಯಿಂದ ಹರಕೆಮಾಡಿಕೊಂಡಂತೆಯೇ ಅದನ್ನು ಒಪ್ಪಿಸಿಬಿಡಬೇಕು.",
			"24": "<sup>24</sup> ಮತ್ತೊಬ್ಬನ ದ್ರಾಕ್ಷೇತೋಟದಲ್ಲಿ ನೀವು ಹೋಗುತ್ತಿರುವಾಗ ಇಷ್ಟಾನುಸಾರ ಹಣ್ಣುಗಳನ್ನು ತಿನ್ನಬಹುದೇ ಹೊರತು ಪಾತ್ರೆಯಲ್ಲಿ ತೆಗೆದುಕೊಂಡು ಹೋಗಬಾರದು.",
			"25": "<sup>25</sup> ಮತ್ತೊಬ್ಬನ ಪೈರಿನಲ್ಲಿ ಹೋಗುತ್ತಿರುವಾಗ ತೆನೆಗಳನ್ನು ಕೈಯಿಂದ ಮುರುಕೊಳ್ಳಬಹುದೇ ಹೊರತು ಆ ಪೈರಿಗೆ ಕುಡುಗೋಲು ಹಾಕಬಾರದು."
		},
		"24": {
			"1": "<sup>1</sup> ಯಾವನಾದರೂ ತಾನು ಮದುವೆಮಾಡಿಕೊಂಡ ಸ್ತ್ರೀಯಲ್ಲಿ ಏನೋ ಅವಲಕ್ಷಣವನ್ನು ಕಂಡು ಅವಳಲ್ಲಿ ಸಂತೋಷಪಡದೆ ತ್ಯಾಗಪತ್ರವನ್ನು ಬರೆದುಕೊಟ್ಟು ಅವಳನ್ನು ಮನೆಯಿಂದ ಕಳುಹಿಸಿಬಿಟ್ಟಾಗ",
			"2": "<sup>2</sup> ಅವಳು ಮತ್ತೊಬ್ಬನನ್ನು ಮದುವೆಯಾಗಿ ಅವನಿಂದಲೂ ತಿರಸ್ಕರಿಸಲ್ಪಟ್ಟು ತ್ಯಾಗಪತ್ರವನ್ನು ಹೊಂದಿ ಕಳುಹಿಸಲ್ಪಟ್ಟರೆ ಅಥವಾ ಈ ಎರಡನೆಯ ಗಂಡನು ಸತ್ತರೆ",
			"4": "<sup>4</sup> ಅವಳನ್ನು ಕಳುಹಿಸಿಬಿಟ್ಟ ಮೊದಲನೆಯ ಗಂಡನು ಅವಳನ್ನು ಪುನಃ ಪರಿಗ್ರಹಿಸಬಾರದು; ಅವಳು ಅಶುದ್ಧಳಾದಳು; ಅವಳನ್ನು ಪುನಃ ಪರಿಗ್ರಹಿಸುವದು ಯೆಹೋವನಿಗೆ ಹೇಯಕಾರ್ಯ. ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಸ್ವದೇಶವಾಗುವದಕ್ಕೆ ಕೊಡುವ ದೇಶಕ್ಕೆ ದೋಷವುಂಟಾಗುವಂತೆ ಅವಕಾಶ ಕೊಡಬಾರದು.",
			"5": "<sup>5</sup> ಹೊಸದಾಗಿ ಮದುವೆಮಾಡಿಕೊಂಡವನು ಸೈನ್ಯದವರೊಡನೆ ಯುದ್ಧಕ್ಕೆ ಹೋಗಬಾರದು; ಭಾರವಾದ ಯಾವ ಕೆಲಸವನ್ನೂ ಅವನಿಗೆ ನೇಮಿಸಬಾರದು; ಅವನು ಒಂದು ವರುಷದವರೆಗೂ ಬಿಡುವಾಗಿ ಮನೆಯಲ್ಲಿ ಇದ್ದುಕೊಂಡು ಪರಿಗ್ರಹಿಸಿದ ಹೆಂಡತಿಯೊಡನೆ ಸುಖವಾಗಿರಲಿ.",
			"6": "<sup>6</sup> ಬೀಸುವಕಲ್ಲನ್ನು ಪೂರ್ತಿಯಾಗಲಿ ಅರ್ಧವಾಗಲಿ ಒತ್ತೆ ತೆಗೆದುಕೊಳ್ಳಬಾರದು; ಅದು ಜೀವನಾಧಾರವನ್ನೇ ಒತ್ತೆಯಾಗಿ ತೆಗೆದುಕೊಂಡ ಹಾಗಾಗುವದು.",
			"7": "<sup>7</sup> ಯಾವನಾದರೂ ಸ್ವದೇಶದವನಾದ ಇಸ್ರಾಯೇಲ್ಯನನ್ನು ಕದ್ದು ಅವನನ್ನು ದಾಸತ್ವದಲ್ಲಿ ನಡಿಸಿದ್ದಾಗಲಿ ಮಾರಿಬಿಟ್ಟದ್ದಾಗಲಿ ಕಂಡುಬಂದರೆ ಕದ್ದವನಿಗೆ ಮರಣಶಿಕ್ಷೆಯಾಗಬೇಕು. ಹೀಗೆ ಅಂಥ ದುಷ್ಟತ್ವವನ್ನು ನಿಮ್ಮ ಮಧ್ಯದಿಂದ ತೆಗೆದುಹಾಕಿಬಿಡಬೇಕು.",
			"8": "<sup>8</sup> ಕುಷ್ಠರೋಗಿಗಳ ವಿಷಯದಲ್ಲಿ ಯಾಜಕರಾದ ಲೇವಿಯರು ಬೋಧಿಸುವಂತೆಯೇ ಮಾಡುವದಕ್ಕೆ ನೀವು ಜಾಗರೂಕರಾಗಿರಬೇಕು; ನಾನು ಅವರಿಗೆ ಆಜ್ಞಾಪಿಸಿದ ನಿಯಮಗಳನ್ನೇ ನೀವು ಅನುಸರಿಸಬೇಕು.",
			"9": "<sup>9</sup> ನೀವು ಐಗುಪ್ತದೇಶದಿಂದ ಬಂದಾಗ ದಾರಿಯಲ್ಲಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಮಿರ್ಯಾಮಳಿಗೆ ಮಾಡಿದ್ದನ್ನು ನೆನಪುಮಾಡಿಕೊಳ್ಳಿರಿ.",
			"10": "<sup>10</sup> ಮತ್ತೊಬ್ಬನಿಗೆ ಸಾಲಕೊಡುವಾಗ ಒತ್ತೆಯನ್ನು ತೆಗೆದುಕೊಳ್ಳುವದಕ್ಕೆ ಅವನ ಮನೆಯೊಳಕ್ಕೆ ಹೋಗದೆ ಹೊರಗೆ ಇರಬೇಕು;",
			"11": "<sup>11</sup> ಸಾಲತೆಗೆದುಕೊಂಡವನೇ ಒತ್ತೆಯ ಸಾಮಾನನ್ನು ತಂದು ಕೊಡಬೇಕು.",
			"12": "<sup>12</sup> ಅವನು ಕೇವಲ ಬಡತನದಿಂದ ತನ್ನ ಕಂಬಳಿಯನ್ನೇ ಒತ್ತೆಯಾಗಿ ಇಟ್ಟ ಸಂದರ್ಭದಲ್ಲಿ ಹೊತ್ತು ಮುಣುಗಿದಾಗ ಅದನ್ನು ಹಿಂದಕ್ಕೆ ಕೊಡಬೇಕು;",
			"13": "<sup>13</sup> ಅದನ್ನು ಇಟ್ಟುಕೊಂಡು ರಾತ್ರಿ ಮಲಗಬಾರದು; ಅವನು ಅದನ್ನು ಹೊದ್ದುಕೊಂಡೇ ಮಲಗಿಕೊಳ್ಳಬೇಕಲ್ಲಾ; ಅದಲ್ಲದೆ ಅವನು ನಿಮ್ಮನ್ನು ಹರಸುವನು, ಮತ್ತು ನೀವು ಮಾಡಿದ್ದು ಧರ್ಮಕಾರ್ಯವೆಂದು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ತಿಳಿದುಕೊಳ್ಳುವನು.",
			"14": "<sup>14</sup> ನೀವು ಸ್ವದೇಶದವರಲ್ಲಿಯಾಗಲಿ ನಿಮ್ಮಲ್ಲಿರುವ ಅನ್ಯದೇಶದವರಲ್ಲಿಯಾಗಲಿ ಗತಿಯಿಲ್ಲದ ಬಡಕೂಲಿಯವನಿಗೆ ಏನೂ ಅನ್ಯಾಯಮಾಡದೆ",
			"15": "<sup>15</sup> ದಿನದ ಕೂಲಿಯನ್ನು ಆ ದಿನದಲ್ಲೇ ಹೊತ್ತು ಮುಣುಗುವದಕ್ಕೆ ಮುಂಚಿತವಾಗಿ ಅವನಿಗೆ ಕೊಡಬೇಕು. ಅವನಿಗೆ ಬೇರೆ ಗತಿಯಿಲ್ಲದ್ದರಿಂದ ಅದನ್ನೇ ಎದುರು ನೋಡುತ್ತಾನಲ್ಲಾ. ನೀವು ಕೊಡದಿದ್ದರೆ ಅವನು ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಟ್ಟಾನು; ಆಗ ನೀವು ದೋಷಿಗಳಾಗಿ ಕಂಡುಬಂದೀರಿ.",
			"16": "<sup>16</sup> ಮಕ್ಕಳ ಪಾಪದ ದೆಸೆಯಿಂದ ತಂದೆಗೂ ತಂದೆಯ ಪಾಪದ ದೆಸೆಯಿಂದ ಮಕ್ಕಳಿಗೂ ಮರಣಶಿಕ್ಷೆಯಾಗಬಾರದು. ಪ್ರತಿಯೊಬ್ಬನೂ ತನ್ನ ಪಾಪಫಲವನ್ನು ತಾನೇ ಅನುಭವಿಸಬೇಕು.",
			"17": "<sup>17</sup> ನೀವು ಪರದೇಶಿಯ ಅಥವಾ ದಿಕ್ಕಿಲ್ಲದವನ ವ್ಯಾಜ್ಯವನ್ನು ವಿಚಾರಿಸುವಾಗ ನ್ಯಾಯವನ್ನು ಬಿಟ್ಟು ತೀರ್ಪುಕೊಡಬಾರದು. ವಿಧವೆಯಿಂದ ಉಡುವ ಬಟ್ಟೆಯನ್ನು ಒತ್ತೆಯಿಡಿಸಿಕೊಳ್ಳಬಾರದು.",
			"18": "<sup>18</sup> ನೀವೇ ಐಗುಪ್ತದೇಶದಲ್ಲಿ ದಾಸರಾಗಿದ್ದಾಗ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಬಿಡಿಸಿದನೆಂಬದನ್ನು ನೆನಪುಮಾಡಿಕೊಳ್ಳಿರಿ; ಅದಕ್ಕಾಗಿಯೇ ಇದನ್ನು ಆಜ್ಞಾಪಿಸಿದ್ದೇನೆ.",
			"19": "<sup>19</sup> ನೀವು ಪೈರುಗಳನ್ನು ಕೊಯ್ಯುವಾಗ ಒಂದು ಸಿವುಡನ್ನು ಹೊಲದಲ್ಲೇ ಮರೆತು ಬಂದರೆ ಅದನ್ನು ತರುವದಕ್ಕೆ ಹಿಂದಕ್ಕೆ ಹೋಗಬಾರದು; ಪರದೇಶಿ, ತಾಯಿತಂದೆಯಿಲ್ಲದವ, ವಿಧವೆ ಇಂಥವರಿಗೋಸ್ಕರ ಇರಲಿ. ನೀವು ಹೀಗೆ ನಡೆದುಕೊಂಡರೆ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮ ಎಲ್ಲಾ ಕೆಲಸಗಳಲ್ಲಿಯೂ ನಿಮ್ಮನ್ನು ಅಭಿವೃದ್ಧಿಪಡಿಸುವನು.",
			"20": "<sup>20</sup> ಎಣ್ಣೆಯ ಮರಗಳ ರೆಂಬೆಗಳನ್ನು ಹೊಡೆದು ಕಾಯಿಗಳನ್ನು ಉದುರಿಸಿದ ಮೇಲೆ ಪುನಃ ಉದುರಿಸುವದಕ್ಕೆ ಹೋಗಬಾರದು; ಮಿಕ್ಕ ಕಾಯಿಗಳು ಪರದೇಶಿ, ತಾಯಿತಂದೆಯಿಲ್ಲದವ, ವಿಧವೆ ಇಂಥವರಿಗೋಸ್ಕರ ಇರಲಿ.",
			"21": "<sup>21</sup> ದ್ರಾಕ್ಷೆಯ ತೋಟದ ಬೆಳೆಯನ್ನು ಕೂಡಿಸಿಕೊಳ್ಳುವಾಗ ಹಕ್ಕಲಾಯಬಾರದು; ಅದು ಪರದೇಶಿ, ತಾಯಿತಂದೆಯಿಲ್ಲದವ, ವಿಧವೆ ಇಂಥವರಿಗೋಸ್ಕರ ಇರಲಿ.",
			"22": "<sup>22</sup> ನೀವೇ ಐಗುಪ್ತದೇಶದಲ್ಲಿ ದಾಸರಾಗಿದ್ದದ್ದು ಜ್ಞಾಪಕದಲ್ಲಿರಬೇಕು; ಅದಕ್ಕಾಗಿಯೇ ಇದನ್ನು ನಿಮಗೆ ಆಜ್ಞಾಪಿಸಿದ್ದೇನೆ."
		},
		"25": {
			"1": "<sup>1</sup> ಇಬ್ಬರು ವ್ಯಾಜ್ಯವಾಡುತ್ತಾ ನ್ಯಾಯಾಧಿಪತಿಗಳ ಬಳಿಗೆ ಬಂದರೆ ನ್ಯಾಯಾಧಿಪತಿಗಳು ತಪ್ಪಿಲ್ಲದವನನ್ನು ನಿರಪರಾಧಿ ಎಂದೂ ತಪ್ಪುಳ್ಳವನನ್ನು ಅಪರಾಧಿ ಎಂದೂ ತೀರ್ಮಾನಿಸಬೇಕು.",
			"2": "<sup>2</sup> ಅಪರಾಧಿಗೆ ಪೆಟ್ಟಿನ ಶಿಕ್ಷೆ ತೀರ್ಮಾನವಾದರೆ ನ್ಯಾಯಾಧಿಪತಿಯು ಅವನನ್ನು ಮಲಗಿಸಿ ಅವನ ಅಪರಾಧಕ್ಕೆ ಅನುಸಾರವಾಗಿ ಪೆಟ್ಟುಗಳನ್ನು ತನ್ನ ಮುಂದೆಯೇ ಹೊಡಿಸಿ ಲೆಕ್ಕಿಸಬೇಕು.",
			"3": "<sup>3</sup> ಪೆಟ್ಟುಗಳ ಶಿಕ್ಷೆಯು ನಾಲ್ವತ್ತಕ್ಕಿಂತ ಹೆಚ್ಚಿರಬಾರದು. ಅಷ್ಟಕ್ಕಿಂತ ಮೀರಿ ನೀವು ಹೆಚ್ಚು ಪೆಟ್ಟುಗಳನ್ನು ಹೊಡಿಸಿದರೆ ನಿಮ್ಮ ಸ್ವದೇಶದವನು ನಿಮಗೆ ಕೇವಲ ನೀಚನಾಗಿ ಕಾಣಿಸಾನು.",
			"4": "<sup>4</sup> ಕಣತುಳಿಯುವ ಎತ್ತಿನ ಬಾಯಿ ಕಟ್ಟಬಾರದು.",
			"5": "<sup>5</sup> ಅಣ್ಣ ತಮ್ಮಂದಿರು ಒಂದೇ ಕುಟುಂಬದಲ್ಲಿರುವಾಗ ಅಣ್ಣನು ಮಗನಿಲ್ಲದೆ ಸತ್ತರೆ ಅವನ ಹೆಂಡತಿ ಬೇರೆ ಪುರುಷನನ್ನು ಮದುವೆಯಾಗಬಾರದು. ಅವಳ ಮೈದುನ ಅವಳನ್ನು ಹೆಂಡತಿಯಾಗಿ ತೆಗೆದುಕೊಂಡು ಮೈದುನ ಧರ್ಮವನ್ನು ನೆರವೇರಿಸಬೇಕು.",
			"6": "<sup>6</sup> ಅವಳಲ್ಲಿ ಹುಟ್ಟುವ ಚೊಚ್ಚಲುಮಗನು ಸತ್ತವನ ಮಗನೆಂದು ಎಣಿಸಲ್ಪಡಬೇಕು; ಹೀಗೆ ಆಗುವದರಿಂದ ಸತ್ತವನ ಹೆಸರು ಇಸ್ರಾಯೇಲ್ಯರೊಳಗಿಂದ ನಾಶವಾಗಿ ಹೋಗುವದಿಲ್ಲ.",
			"7": "<sup>7</sup> ತಮ್ಮನು ಅಣ್ಣನ ಹೆಂಡತಿಯನ್ನು ಪರಿಗ್ರಹಿಸಲೊಲ್ಲದೆ ಹೋದರೆ ಅವಳು ಚಾವಡಿಗೆ ಹೋಗಿ ಹಿರಿಯರಿಗೆ - ನನ್ನ ಗಂಡನ ತಮ್ಮನು ತನ್ನ ಅಣ್ಣನ ಹೆಸರನ್ನು ಉಳಿಸಲೊಲ್ಲನು; ಮೈದುನ ಧರ್ಮವನ್ನು ನಡಿಸುವದಿಲ್ಲ ಅನ್ನುತ್ತಾನೆ ಎಂದು ತಿಳಿಸಬೇಕು.",
			"8": "<sup>8</sup> ಆಗ ಆ ಊರಿನ ಹಿರಿಯರು ಅವನನ್ನು ಕರಿಸಿ ವಿಚಾರಿಸಿದಲ್ಲಿ ಅವನು ಅವರ ಮುಂದೆ ನಿಂತು - ಈಕೆಯನ್ನು ಪರಿಗ್ರಹಿಸುವದಕ್ಕೆ ನನಗೆ ಇಷ್ಟವಿಲ್ಲ ಎಂದು ಹೇಳುವ ಪಕ್ಷಕ್ಕೆ",
			"9": "<sup>9</sup> ಅವನ ಅತ್ತಿಗೆ ಹಿರಿಯರ ಎದುರಿನಲ್ಲಿ ಅವನ ಕೆರವನ್ನು ಅವನ ಕಾಲಿನಿಂದ ತೆಗೆದುಬಿಟ್ಟು ಅವನ ಮುಖದ ಮೇಲೆ ಉಗುಳಿ - ಅಣ್ಣನಿಗೋಸ್ಕರ ಸಂತಾನವನ್ನು ಹುಟ್ಟಿಸಲೊಲ್ಲದವರೆಲ್ಲರಿಗೂ ಇಂಥ ಅವಮಾನವಾಗಲಿ ಎಂದು ಹೇಳಬೇಕು.",
			"10": "<sup>10</sup> ಆ ಮನುಷ್ಯನ ಮನೆಯವರಿಗೆ - ಕೆರವನ್ನು ಬಿಚ್ಚಿಸಿಕೊಂಡವನ ಮನೆಯವರು ಎಂದು ಹೆಸರುಂಟಾಗುವದು.",
			"11": "<sup>11</sup> ಇಬ್ಬರು ಜಗಳವಾಡುತ್ತಿರಲಾಗಿ ಅವರಲ್ಲಿ ಒಬ್ಬನ ಹೆಂಡತಿ ಬಂದು ತನ್ನ ಗಂಡನನ್ನು ಬಿಡಿಸಬೇಕೆಂದು",
			"12": "<sup>12</sup> ಆ ಪರಪುರುಷನ ಪ್ರಧಾನ ಸ್ಥಾನವನ್ನು ಹಿಡಿದುಕೊಂಡರೆ ಕನಿಕರಿಸದೆ ಅವಳ ಕೈ ಕಡಿದುಹಾಕಿಸಬೇಕು.",
			"13": "<sup>13</sup> ಹೆಚ್ಚು ಕಡಮೆಯಾಗಿರುವ ಎರಡು ವಿಧವಾದ ತೂಕದ ಕಲ್ಲುಗಳನ್ನು ನಿಮ್ಮ ಚೀಲದಲ್ಲಿಟ್ಟುಕೊಳ್ಳಬಾರದು. ಹೆಚ್ಚು ಕಡಿಮೆಯಾದ ಎರಡು ವಿಧವಾದ ಸೇರುಗಳು ನಿಮ್ಮ ಮನೆಯಲ್ಲಿರಬಾರದು.",
			"14": "<sup>14</sup> ನಿಮ್ಮ ತೂಕದ ಕಲ್ಲೂ ನಿಮ್ಮ ಅಳತೆಯ ಸೇರೂ ನ್ಯಾಯವಾಗಿಯೇ ಇರಬೇಕು.",
			"15": "<sup>15</sup> ನೀವು ಹೀಗೆ ನಡೆದುಕೊಂಡರೆ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಕೊಡುವ ದೇಶದಲ್ಲಿ ಬಹುಕಾಲ ಬಾಳುವಿರಿ.",
			"16": "<sup>16</sup> ನ್ಯಾಯವಿರುದ್ಧವಾದ ಅಳತೆತೂಕಗಳನ್ನು ಮಾಡುವವರೆಲ್ಲರೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಹೇಯವಾಗಿದ್ದಾರೆ.",
			"17": "<sup>17</sup> ನೀವು ಐಗುಪ್ತದೇಶದಿಂದ ಬರುವಾಗ ದಾರಿಯಲ್ಲಿ ಅಮಾಲೇಕ್ಯರು ನಿಮ್ಮನ್ನು ಎದುರಿಸಿದ್ದನ್ನು ಜ್ಞಾಪಿಸಿಕೊಳ್ಳಿರಿ.",
			"18": "<sup>18</sup> ಅವರು ದೇವರಲ್ಲಿ ಸ್ವಲ್ಪವೂ ಭಯಭಕ್ತಿಯಿಲ್ಲದವರಾಗಿ ನೀವು ದಣಿದು ಬಳಲಿ ಇದ್ದಾಗ ನಿಮ್ಮವರಲ್ಲಿ ಹಿಂದೆಬಿದ್ದ ಬಲಹೀನರನ್ನು ಸಂಹಾರ ಮಾಡಿದರಲ್ಲಾ.",
			"19": "<sup>19</sup> ಆದಕಾರಣ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಸ್ವದೇಶವಾಗುವದಕ್ಕೆ ಕೊಡುವ ದೇಶದಲ್ಲಿ ನಿಮ್ಮನ್ನು ಸೇರಿಸಿ ಸುತ್ತಲು ಯಾವ ಶತ್ರುಗಳೂ ಇಲ್ಲದಂತೆ ಮಾಡಿದಾಗ ಭೂಮಿಯ ಮೇಲೆ ಅಮಾಲೇಕ್ಯರ ಹೆಸರೇ ಉಳಿಯದಂತೆ ನೀವು ಮಾಡಬೇಕು; ಇದನ್ನು ಮರೆಯಬಾರದು."
		},
		"26": {
			"1": "<sup>1</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಸ್ವದೇಶವಾಗುವದಕ್ಕೆ ಕೊಡುವ ದೇಶವನ್ನು ನೀವು ಸೇರಿ ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡು ಅದರಲ್ಲಿ ವಾಸಿಸುವಾಗ",
			"2": "<sup>2</sup> ಆತನು ದಯಪಾಲಿಸಿದ ಹೊಲದ ಎಲ್ಲಾ ಬೆಳೆಗಳಲ್ಲಿಯೂ ನೀವು ಕೆಲವು ಪ್ರಥಮಫಲಗಳನ್ನು ಪುಟ್ಟಿಯಲ್ಲಿಟ್ಟು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ತನ್ನ ಹೆಸರನ್ನು ಸ್ಥಾಪಿಸುವದಕ್ಕೆ ಆದುಕೊಳ್ಳುವ ಸ್ಥಳಕ್ಕೆ ತೆಗೆದುಕೊಂಡು ಹೋಗಿ",
			"3": "<sup>3</sup> ಆ ಕಾಲದಲ್ಲಿ ಇರುವ ಮಹಾಯಾಜಕನಿಗೆ - ಯೆಹೋವನು ನಮ್ಮ ಪಿತೃಗಳಿಗೆ ಪ್ರಮಾಣಮಾಡಿಕೊಟ್ಟ ದೇಶದಲ್ಲಿ ನಾವು ಸೇರಿದ್ದಾಯಿತೆಂದು ನೀನು ಸೇವಿಸುವ ದೇವರಾದ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಈಗ ಒಪ್ಪಿಕೊಂಡಿದ್ದೇವೆ ಎಂದು ಹೇಳಬೇಕು.",
			"4": "<sup>4</sup> ಯಾಜಕನು ಆ ಪುಟ್ಟಿಯನ್ನು ನಿಮ್ಮ ಕೈಯಿಂದ ತೆಗೆದುಕೊಂಡು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಯಜ್ಞವೇದಿಯ ಮುಂದೆ ಇಟ್ಟಾಗ",
			"5": "<sup>5</sup> ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ - ಅರಾಮ್ಯನಾದ ನಮ್ಮ ಮೂಲಪಿತೃವು ಗತಿಯಿಲ್ಲದವನಾಗಿ ಸ್ವಲ್ಪ ಮಂದಿಯೊಡನೆ ಐಗುಪ್ತದೇಶಕ್ಕೆ ಹೋಗಿ ಅಲ್ಲಿ ಇಳಿದುಕೊಂಡನು. ಅಲ್ಲಿ ಅವನ ಸಂತಾನದವರು ಹೆಚ್ಚಿ ಮಹಾಬಲಿಷ್ಠ ಜನಾಂಗವಾದರು.",
			"6": "<sup>6</sup> ಐಗುಪ್ತ್ಯರು ನಮ್ಮನ್ನು ಉಪದ್ರವಪಡಿಸಿ ಬಾಧಿಸಿ ನಮ್ಮಿಂದ ಕಠಿಣವಾಗಿ ದುಡಿಸಿಕೊಂಡಾಗ",
			"7": "<sup>7</sup> ನಾವು ನಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಡಲಾಗಿ ಆತನು ಕೇಳಿ ನಮ್ಮ ದುರವಸ್ಥೆಯನ್ನೂ ಕಷ್ಟವನ್ನೂ ಉಪದ್ರವವನ್ನೂ ನೋಡಿ",
			"8": "<sup>8</sup> ಭುಜಬಲವನ್ನೂ ಶಿಕ್ಷಾಹಸ್ತವನ್ನೂ ಮಹಾಭೀತಿಗಳನ್ನೂ ಮಹತ್ಕಾರ್ಯಗಳನ್ನೂ ಉತ್ಪಾತಗಳನ್ನೂ ಪ್ರಯೋಗಿಸಿ",
			"9": "<sup>9</sup> ನಮ್ಮನ್ನು ಬಿಡಿಸಿ ಐಗುಪ್ತದೇಶದಿಂದ ಕರತಂದು ಹಾಲೂ ಜೇನೂ ಹರಿಯುವ ಈ ದೇಶವನ್ನು ನಮಗೆ ಕೊಟ್ಟಿದ್ದಾನೆ.",
			"10": "<sup>10</sup> ಆದದರಿಂದ ಯೆಹೋವನೇ, ನೀನು ಅನುಗ್ರಹಿಸಿರುವ ಭೂಮಿಯ ಬೆಳೆಯಲ್ಲಿ ಪ್ರಥಮಫಲಗಳನ್ನು ನಾವು ಕಾಣಿಕೆಯಾಗಿ ತಂದಿದ್ದೇವೆ; ಸ್ವೀಕರಿಸಬೇಕು ಎಂದು ಹೇಳಿ ಆ ಪ್ರಥಮಫಲಗಳನ್ನು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿಟ್ಟು ಆತನನ್ನು ಆರಾಧಿಸಬೇಕು.",
			"11": "<sup>11</sup> ಆತನು ನಿಮಗೂ ನಿಮ್ಮ ಮನೆಯವರಿಗೂ ಉಂಟುಮಾಡಿದ ಎಲ್ಲಾ ಸುಖಸಂತೋಷಗಳಿಗಾಗಿ ನೀವೂ ಲೇವಿಯರೂ ನಿಮ್ಮ ಮಧ್ಯದಲ್ಲಿರುವ ಪರದೇಶಿಗಳೂ ಸಂಭ್ರಮದಿಂದಿರಬೇಕು.",
			"12": "<sup>12</sup> ದಶಮಾಂಶವನ್ನು ಕೊಡಬೇಕಾದ ಮೂರನೆಯ ಸಂವತ್ಸರದಲ್ಲಿ ನಿಮಗುಂಟಾದ ಆದಾಯದ ದಶಮಭಾಗವನ್ನು ಪ್ರತ್ಯೇಕಿಸಿದಾಗ ನಿಮ್ಮನಿಮ್ಮ ಊರುಗಳಲ್ಲಿರುವ ಲೇವಿಯರೂ ಪರದೇಶಿಗಳೂ ತಾಯಿತಂದೆಯಿಲ್ಲದವರೂ ವಿಧವೆಯರೂ ಉಂಡು ಸಂತೋಷವಾಗಿರುವಂತೆ ಅದನ್ನು ಅವರಿಗೆ ಕೊಡಬೇಕಲ್ಲಾ.",
			"13": "<sup>13</sup> ಆಗ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ - ನಾವು ಮೀಸಲಾದ ವಸ್ತುಗಳನ್ನು ಮನೆಯಿಂದ ತೆಗೆದುಬಿಟ್ಟಿದ್ದೇವೆ; ನೀನು ಆಜ್ಞಾಪಿಸಿದ ಮೇರೆಗೆ ಅವುಗಳನ್ನು ಲೇವಿಯ, ಪರದೇಶಿ, ತಾಯಿತಂದೆಯಿಲ್ಲದವ, ವಿಧವೆ ಇಂಥವರಿಗೆ ಕೊಟ್ಟಿದ್ದೇವೆ; ನಿನ್ನ ಆಜ್ಞೆಗಳಲ್ಲಿ ಒಂದನ್ನೂ ಮೀರಲೂ ಇಲ್ಲ ಮರೆಯಲೂ ಇಲ್ಲ.",
			"14": "<sup>14</sup> ನಾವು [ಸತ್ತವರಿಗೋಸ್ಕರ] ದುಃಖಿಸುವ ಕಾಲದಲ್ಲೆಲ್ಲಾ ಅದರಲ್ಲಿ ಏನೂ ತಿನ್ನಲಿಲ್ಲ; ಅಶುದ್ಧರಾಗಿ ಅದನ್ನು ತೆಗೆದುಹಾಕಲಿಲ್ಲ; ಸತ್ತವರಿಗೋಸ್ಕರ ಅದರಲ್ಲಿ ಏನೂ ಕೊಡಲಿಲ್ಲ; ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಮಾತಿನಲ್ಲಿ ಲಕ್ಷ್ಯವಿಟ್ಟು ನೀನು ನೇಮಿಸಿದ್ದನ್ನೆಲ್ಲಾ ಅನುಸರಿಸಿದ್ದೇವೆ.",
			"15": "<sup>15</sup> ನೀನು ನಿನ್ನ ಪರಿಶುದ್ಧವಾಸಸ್ಥಾನವಾಗಿರುವ ಪರಲೋಕದಿಂದ ಕೆಳಗಿರುವ ನಮ್ಮನ್ನು ಕಟಾಕ್ಷಿಸಿ ನಿನ್ನ ಜನರಾದ ಇಸ್ರಾಯೇಲ್ಯರನ್ನೂ ಹಾಲೂ ಜೇನೂ ಹರಿಯುವ ದೇಶವೆಂದು ನೀನು ನಮ್ಮ ಪಿತೃಗಳಿಗೆ ವಾಗ್ದಾನಮಾಡಿದ ಮೇರೆಗೆ ನಮಗೆ ಕೊಟ್ಟಿರುವ ಈ ಭೂಮಿಯನ್ನೂ ಆಶೀರ್ವದಿಸು ದೇವರೇ ಎಂದು ಹೇಳಬೇಕು.",
			"16": "<sup>16</sup> ಈ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ನೀವು ಅನುಸರಿಸಬೇಕೆಂದು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಈಗ ಆಜ್ಞಾಪಿಸಿದ್ದಾನೆ. ಆದದರಿಂದ ನೀವು ಸಂಪೂರ್ಣವಾದ ಹೃದಯದಿಂದಲೂ ಮನಸ್ಸಿನಿಂದಲೂ ಇವುಗಳನ್ನು ಕೈಕೊಂಡು ನಡೆಯಬೇಕು.",
			"17": "<sup>17</sup> ನಮಗೆ ಯೆಹೋವನೇ ದೇವರಾಗಿರುವನೆಂದೂ ಆತನು ಹೇಳಿದ ಮಾರ್ಗದಲ್ಲೇ ನಡೆದು ಆತನ ಆಜ್ಞಾವಿಧಿನಿರ್ಣಯಗಳನ್ನು ಕೈಕೊಂಡು ಆತನ ಮಾತಿಗೆ ಲಕ್ಷ್ಯವಿಡುವೆವೆಂದೂ ನೀವು ಈಗ ಒಡಂಬಟ್ಟಿದ್ದೀರಿ.",
			"18": "<sup>18</sup> ಯೆಹೋವನೋ ನಿಮ್ಮ ವಿಷಯದಲ್ಲಿ - ಇವರು ನನ್ನ ಆಜ್ಞೆಗಳನ್ನೆಲ್ಲಾ ಅನುಸರಿಸುವ ಪಕ್ಷಕ್ಕೆ ನನ್ನ ವಾಗ್ದಾನಾನುಸಾರ ನನಗೆ ಸ್ವಕೀಯಜನರಾಗಿರುವರೆಂದೂ",
			"19": "<sup>19</sup> ನಾನು ನಿರ್ಮಿಸಿದ ಬೇರೆ ಎಲ್ಲಾ ಜನಾಂಗಗಳಿಗಿಂತಲೂ ಇವರಿಗೆ ಹೆಚ್ಚಾದ ಕೀರ್ತಿಘನಮಾನಗಳನ್ನುಂಟುಮಾಡುವೆನೆಂದೂ ಇವರು ನನ್ನ ಮಾತಿನ ಮೇರೆಗೆ ತಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೋಸ್ಕರ ಮೀಸಲಾದ ಜನರಾಗುವರೆಂದೂ ಒಡಂಬಟ್ಟಿದ್ದಾನೆ."
		},
		"27": {
			"1": "<sup>1</sup> ಮೋಶೆಯು ಇಸ್ರಾಯೇಲ್ಯರ ಹಿರಿಯರ ಸಹಿತ ಜನರಿಗೆ - ನಾನು ಈಗ ಆಜ್ಞಾಪಿಸಿದ ಧರ್ಮೋಪದೇಶವನ್ನೆಲ್ಲಾ ನೀವು ಅನುಸರಿಸಬೇಕು.",
			"2": "<sup>2</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಕೊಡುವ ದೇಶದಲ್ಲಿ ಪ್ರವೇಶಿಸುವದಕ್ಕೆ ನೀವು ಯೊರ್ದನ್ ಹೊಳೆಯನ್ನು ದಾಟುವಾಗ ಅಂದರೆ ನಿಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಯೆಹೋವನು ತಾನು ವಾಗ್ದಾನ ಮಾಡಿದಂತೆ ನಿಮಗೆ ಕೊಡುವ ಹಾಲೂ ಜೇನೂ ಹರಿಯುವ ದೇಶವನ್ನು ಪ್ರವೇಶಿಸುವದಕ್ಕೆ ನೀವು ಯೊರ್ದನ್ ಹೊಳೆಯನ್ನು ದಾಟುವಾಗ ನೀವು ದೊಡ್ಡ ಕಲ್ಲುಗಳನ್ನು ನಿಲ್ಲಿಸಿ ಅವುಗಳಿಗೆ ಗಿಲಾವುಮಾಡಿಸಿ ಈ ಧರ್ಮಶಾಸ್ತ್ರದ ವಾಕ್ಯಗಳನ್ನೆಲ್ಲಾ ಅವುಗಳ ಮೇಲೆ ಬರಿಸಬೇಕು.",
			"4": "<sup>4</sup> ನೀವು ಯೊರ್ದನ್ ಹೊಳೆಯನ್ನು ದಾಟಿದ ನಂತರ ನಾನು ಈಗ ಆಜ್ಞಾಪಿಸಿದ ಕಲ್ಲುಗಳನ್ನು ಏಬಾಲ್‍ಬೆಟ್ಟದ ಮೇಲೆ ನಿಲ್ಲಿಸಿ ಅವುಗಳಿಗೆ ಗಿಲಾವು ಮಾಡಿಸಬೇಕು.",
			"5": "<sup>5</sup> ಅಲ್ಲಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೋಸ್ಕರ ನೀವು ಕಲ್ಲುಗಳಿಂದ ಯಜ್ಞವೇದಿಯನ್ನು ಕಟ್ಟಿಸಬೇಕು.",
			"6": "<sup>6</sup> ಉಳಿ ಮುಂತಾದದ್ದನ್ನು ಉಪಯೋಗಿಸದೆ ಹುಟ್ಟುಕಲ್ಲುಗಳಿಂದಲೇ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಯಜ್ಞವೇದಿಯನ್ನು ಕಟ್ಟಿಸಬೇಕು.",
			"7": "<sup>7</sup> ಅಲ್ಲಿ ಸರ್ವಾಂಗಹೋಮಗಳನ್ನೂ ಸಮಾಧಾನಯಜ್ಞಗಳನ್ನೂ ಸಮರ್ಪಿಸಿ ಸಹಭೋಜನಮಾಡಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸಂಭ್ರಮದಿಂದಿರಬೇಕು.",
			"8": "<sup>8</sup> ನೀವು ಆ ಕಲ್ಲುಗಳ ಮೇಲೆ ಈ ಧರ್ಮಶಾಸ್ತ್ರದ ವಾಕ್ಯಗಳನ್ನೆಲ್ಲಾ ವಿರಲವಾಗಿಯೂ ಸ್ಪಷ್ಟವಾಗಿಯೂ ಬರಿಸಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"9": "<sup>9</sup> ಯಾಜಕರಾದ ಲೇವಿಯರೊಡನೆ ಮೋಶೆಯು ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರಿಗೆ - ಇಸ್ರಾಯೇಲ್ಯರೇ, ನಿಶಬ್ದವಾಗಿದ್ದು ಕೇಳಿರಿ. ನೀವು ಈಗ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಜನರಾದಿರಷ್ಟೆ.",
			"10": "<sup>10</sup> ಆದದರಿಂದ ನೀವು ಈಗ ನಾನು ನಿಮಗೆ ಹೇಳುವ ಆತನ ಮಾತುಗಳಿಗೆ ಕಿವಿಗೊಟ್ಟು ಆತನ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ಅನುಸರಿಸಬೇಕು ಎಂದು ಹೇಳಿದನು.",
			"11": "<sup>11</sup> ಮೋಶೆ ಆ ದಿನದಲ್ಲಿ ಜನರಿಗೆ ಆಜ್ಞಾಪಿಸಿದ್ದೇನಂದರೆ -",
			"12": "<sup>12</sup> ನೀವು ಯೊರ್ದನ್ ಹೊಳೆಯನ್ನು ದಾಟಿದಾಗ ಸಿಮೆಯೋನ್, ಲೇವಿ, ಯೆಹೂದ, ಇಸ್ಸಾಕಾರ್, ಯೋಸೇಫ್, ಬೆನ್ಯಾಮೀನ್ ಎಂಬೀ ಕುಲಗಳವರು ಗೆರಿಜ್ಜೀಮ್ ಬೆಟ್ಟದ ಮೇಲೆ ನಿಂತುಕೊಂಡು ಆಶೀರ್ವಾದಗಳನ್ನು ಉಚ್ಚರಿಸಬೇಕು.",
			"13": "<sup>13</sup> ರೂಬೇನ್, ಗಾದ್, ಆಶೇರ್, ಜೆಬುಲೂನ್, ದಾನ್, ನಫ್ತಾಲಿ ಎಂಬೀ ಕುಲಗಳವರು ಏಬಾಲ್ ಬೆಟ್ಟದ ಮೇಲೆ ನಿಂತುಕೊಂಡು ಶಾಪೋಕ್ತಿಗಳನ್ನು ಉಚ್ಚರಿಸಬೇಕು.",
			"14": "<sup>14</sup> ಲೇವಿಯರು ಗಟ್ಟಿಯಾದ ಸ್ವರದಿಂದ ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರಿಗೂ -",
			"15": "<sup>15</sup> ಶಿಲ್ಪಿಯ ಕೆಲಸವಾಗಿರುವ ಮರದ ವಿಗ್ರಹವೂ ಲೋಹ ವಿಗ್ರಹವೂ ಯೆಹೋವನಿಗೆ ಹೇಯವಾದದ್ದರಿಂದ ಅವನ್ನು ಮಾಡಿಸಿ ಗೋಪ್ಯವಾಗಿ ನಿಲ್ಲಿಸಿಕೊಂಡವನಾದರೂ ಶಾಪಗ್ರಸ್ತ ಅನ್ನಲಾಗಿ ಜನರೆಲ್ಲರೂ - ಹೌದು ಅನ್ನಬೇಕು.",
			"16": "<sup>16</sup> ಲೇವಿಯರು - ತಂದೆತಾಯಿಗಳನ್ನು ಅವಮಾನಪಡಿಸಿದವನು ಶಾಪಗ್ರಸ್ತ ಎನ್ನಲಾಗಿ ಜನರೆಲ್ಲರೂ - ಹೌದು ಅನ್ನಬೇಕು.",
			"17": "<sup>17</sup> ಅವರು - ಮತ್ತೊಬ್ಬನ ಮೇರೆಯನ್ನು ಸರಿಸಿದವನು ಶಾಪಗ್ರಸ್ತ ಎನ್ನಲಾಗಿ ಜನರೆಲ್ಲರೂ - ಹೌದು ಅನ್ನಬೇಕು.",
			"18": "<sup>18</sup> ಅವರು - ಕುರುಡರಿಗೆ ದಾರಿತಪ್ಪಿಸಿದವನು ಶಾಪಗ್ರಸ್ತ ಎನ್ನಲಾಗಿ ಜನರೆಲ್ಲರೂ - ಹೌದು ಅನ್ನಬೇಕು.",
			"19": "<sup>19</sup> ಅವರು - ಪರದೇಶಿ, ತಾಯಿತಂದೆಯಿಲ್ಲದವ, ಇವರ ವ್ಯಾಜ್ಯದಲ್ಲಿ ನ್ಯಾಯ ಬಿಟ್ಟು ತೀರ್ಪುಹೇಳಿದವನು ಶಾಪಗ್ರಸ್ತ ಎನ್ನಲಾಗಿ ಜನರೆಲ್ಲರೂ - ಹೌದು ಅನ್ನಬೇಕು.",
			"20": "<sup>20</sup> ಅವರು - ಮಲತಾಯಿಯನ್ನು ಸಂಗಮಿಸಿ ತಂದೆಗೆ ಅವಮಾನಪಡಿಸಿದವನು ಶಾಪಗ್ರಸ್ತ ಎನ್ನಲಾಗಿ ಜನರೆಲ್ಲರೂ - ಹೌದು ಅನ್ನಬೇಕು.",
			"21": "<sup>21</sup> ಅವರು - ಪಶುಸಂಗಮಮಾಡಿದವನು ಶಾಪಗ್ರಸ್ತ ಎನ್ನಲಾಗಿ ಜನರೆಲ್ಲರೂ - ಹೌದು ಅನ್ನಬೇಕು.",
			"22": "<sup>22</sup> ಅವರು - ತಂದೆಯ ಮಗಳನ್ನಾಗಲಿ ತಾಯಿಯ ಮಗಳನ್ನಾಗಲಿ ಸಂಗಮಿಸಿದವನು ಶಾಪಗ್ರಸ್ತ ಎನ್ನಲಾಗಿ ಜನರೆಲ್ಲರೂ - ಹೌದು ಅನ್ನಬೇಕು.",
			"23": "<sup>23</sup> ಅವರು - ಅತ್ತೆಯನ್ನು ಸಂಗಮಿಸಿದವನು ಶಾಪಗ್ರಸ್ತ ಎನ್ನಲಾಗಿ ಜನರೆಲ್ಲರೂ - ಹೌದು ಅನ್ನಬೇಕು.",
			"24": "<sup>24</sup> ಅವರು - ರಹಸ್ಯವಾಗಿ ನರಹತ್ಯ ಮಾಡಿದವನು ಶಾಪಗ್ರಸ್ತ ಎನ್ನಲಾಗಿ ಜನರೆಲ್ಲರೂ - ಹೌದು ಅನ್ನಬೇಕು.",
			"25": "<sup>25</sup> ಅವರು - ಹಣತೆಗೆದುಕೊಂಡು ನಿರಪರಾಧಿಯನ್ನು ಕೊಂದವನು ಶಾಪಗ್ರಸ್ತ ಎನ್ನಲಾಗಿ ಜನರೆಲ್ಲರೂ - ಹೌದು ಅನ್ನಬೇಕು.",
			"26": "<sup>26</sup> ಅವರು - ಈ ಧರ್ಮಶಾಸ್ತ್ರವಾಕ್ಯಗಳಿಗೆ ಒಡಂಬಟ್ಟು ಕೈಕೊಳ್ಳದೆ ಇರುವವನು ಶಾಪಗ್ರಸ್ತ ಎನ್ನಲಾಗಿ ಜನರೆಲ್ಲರೂ - ಹೌದು ಅನ್ನಬೇಕು."
		},
		"28": {
			"1": "<sup>1</sup> ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಮಾತನ್ನು ಶ್ರದ್ಧೆಯಿಂದ ಕೇಳಿ ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ಆತನ ಆಜ್ಞೆಗಳನ್ನೆಲ್ಲಾ ಅನುಸರಿಸಿ ನಡೆದರೆ ಆತನು ಭೂಮಿಯ ಮೇಲಿರುವ ಎಲ್ಲಾ ಜನಾಂಗಗಳಿಗಿಂತಲೂ ನಿಮ್ಮನ್ನು ಉನ್ನತಸ್ಥಿತಿಗೆ ತರುವನು.",
			"2": "<sup>2</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಮಾತಿಗೆ ಕಿವಿಗೊಟ್ಟು ನಡೆದರೆ ಈ ಶುಭಗಳೆಲ್ಲಾ ನಿಮಗೆ ಪ್ರಾಪ್ತವಾಗುವವು. ಯಾವವಂದರೆ -",
			"3": "<sup>3</sup> ನಿಮಗೆ ಊರಲ್ಲಿಯೂ ಅಡವಿಯಲ್ಲಿಯೂ ಶುಭವುಂಟಾಗುವದು.",
			"4": "<sup>4</sup> ನಿಮ್ಮ ಸಂತಾನ ವ್ಯವಸಾಯಗಳಿಗೂ ದನಕುರಿ ಮುಂತಾದ ಪಶುಗಳಿಗೂ ಶುಭವುಂಟಾಗುವದು.",
			"5": "<sup>5</sup> ನಿಮ್ಮ ಪುಟ್ಟಿಗಳಿಗೂ ಕೊಣವಿಗೆಗಳಿಗೂ ಶುಭವುಂಟಾಗುವದು.",
			"6": "<sup>6</sup> ನೀವು ಕೆಲಸಕ್ಕೆ ಹೋಗುವಾಗಲೂ ಬರುವಾಗಲೂ ಶುಭವುಂಟಾಗುವದು.",
			"7": "<sup>7</sup> ನಿಮ್ಮ ಮೇಲೆ ಬರುವ ಶತ್ರುಗಳು ನಿಮ್ಮಿಂದ ಸೋತುಹೋಗುವಂತೆ ಯೆಹೋವನು ಮಾಡುವನು. ಅವರು ಒಂದೇ ದಾರಿಯಿಂದ ನಿಮ್ಮ ಮೇಲೆ ಬಂದರೂ ಏಳು ದಾರಿಗಳಿಂದ ಓಡಿಹೋಗುವರು.",
			"8": "<sup>8</sup> ನಿಮ್ಮ ಕಣಜಗಳು ಯಾವಾಗಲೂ ತುಂಬಿರುವಂತೆಯೂ ನಿಮ್ಮ ಪ್ರಯತ್ನಗಳೆಲ್ಲಾ ಸಫಲವಾಗುವಂತೆಯೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಶುಭವನ್ನು ಅನುಗ್ರಹಿಸುವನು. ಆತನು ನಿಮಗೆ ಕೊಡುವ ದೇಶದಲ್ಲಿ ನಿಮಗೆ ಶುಭವನ್ನೇ ಉಂಟುಮಾಡುವನು.",
			"9": "<sup>9</sup> ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಆಜ್ಞೆಗಳನ್ನು ಅನುಸರಿಸಿ ಆತನು ಹೇಳಿದ ಮಾರ್ಗದಲ್ಲೇ ನಡೆದರೆ ಆತನು ವಾಗ್ದಾನ ಮಾಡಿದಂತೆ ನಿಮ್ಮನ್ನು ತನಗೋಸ್ಕರ ಮೀಸಲಾದ ಜನರನ್ನಾಗಿ ಸ್ಥಾಪಿಸುವನು.",
			"10": "<sup>10</sup> ಭೂಮಿಯಲ್ಲಿರುವ ಎಲ್ಲಾ ಜನಗಳೂ ನಿಮ್ಮನ್ನು ಯೆಹೋವನ ಜನರೆಂದು ತಿಳಿದುಕೊಂಡು ನಿಮಗೆ ಭಯಪಡುವರು.",
			"11": "<sup>11</sup> ಯೆಹೋವನು ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ಪ್ರಮಾಣ ಮಾಡಿದ ಮೇರೆಗೆ ನಿಮಗೆ ಕೊಡುವ ದೇಶದಲ್ಲಿ ಆತನು ನಿಮಗೆ ಸಂತಾನಪಶುವ್ಯವಸಾಯಗಳ ಸಮೃದ್ಧಿಯನ್ನುಂಟುಮಾಡುವನು.",
			"12": "<sup>12</sup> ಯೆಹೋವನು ಆಕಾಶದಲ್ಲಿರುವ ತನ್ನ ಜಲನಿಧಿಯನ್ನು ತೆರೆದು ನಿಮ್ಮ ದೇಶದ ಮೇಲೆ ಬೆಳೆಗೆ ಬೇಕಾದ ಹಾಗೆ ಮಳೆಯನ್ನು ಸುರಿಸಿ ನಿಮ್ಮ ಎಲ್ಲಾ ವ್ಯವಸಾಯವನ್ನೂ ಸಫಲಮಾಡುವನು. ನೀವು ಅನೇಕ ಜನಗಳಿಗೆ ಸಾಲಕೊಡುವಿರೇ ಹೊರತು ಸಾಲತೆಗೆದುಕೊಳ್ಳುವದಿಲ್ಲ.",
			"13": "<sup>13</sup> ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ಮಾತುಗಳನ್ನು ನೀವು ಬಿಟ್ಟು ಎಡಬಲಕ್ಕೆ ತೊಲಗದೆ ಬೇರೆ ದೇವರುಗಳನ್ನು ಅವಲಂಬಿಸದೆ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಆಜ್ಞೆಗಳಲ್ಲೇ ಲಕ್ಷ್ಯವಿಟ್ಟು ಅವುಗಳನ್ನೇ ಅನುಸರಿಸಿ ನಡೆದರೆ ಆತನು ಇತರರಿಗೆ ನಿಮ್ಮನ್ನು ಅಧೀನಮಾಡದೆ ಎಲ್ಲರಿಗೂ ಶಿರಸ್ಸನ್ನಾಗಿಯೇ ಮಾಡುವನು; ನೀವು ಎಲ್ಲರಿಗಿಂತಲೂ ಮೇಲಣವರಾಗಿರುವಿರೇ ಹೊರತು ಕೆಳಗಣವರಾಗಿರುವದಿಲ್ಲ.",
			"15": "<sup>15</sup> ಆದರೆ ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಮಾತಿಗೆ ಕಿವಿಗೊಡದೆ ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ಆತನ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ಅನುಸರಿಸದೆ ಹೋದರೆ ಈ ಅಶುಭಗಳು ನಿಮಗೆ ಪ್ರಾಪ್ತವಾಗುವವು -",
			"16": "<sup>16</sup> ನಿಮಗೆ ಊರಲ್ಲಿಯೂ ಅಡವಿಯಲ್ಲಿಯೂ ಅಶುಭವುಂಟಾಗುವದು.",
			"17": "<sup>17</sup> ನಿಮ್ಮ ಪುಟ್ಟಿಗಳಿಗೂ ಕೊಣವಿಗೆಗಳಿಗೂ ಅಶುಭವುಂಟಾಗುವದು.",
			"18": "<sup>18</sup> ನಿಮ್ಮ ಸಂತಾನ ವ್ಯವಸಾಯಗಳಿಗೂ ದನಕುರಿಗಳಿಗೂ ಅಶುಭವುಂಟಾಗುವದು.",
			"19": "<sup>19</sup> ನೀವು ಕೆಲಸಕ್ಕೆ ಹೋಗುವಾಗಲೂ ಬರುವಾಗಲೂ ಅಶುಭವುಂಟಾಗುವದು.",
			"20": "<sup>20</sup> ನೀವು ದುರ್ನಡತೆಯುಳ್ಳವರಾಗಿ ಯೆಹೋವನನ್ನು ಬಿಟ್ಟದ್ದರಿಂದ ಆತನು ನಿಮ್ಮ ಎಲ್ಲಾ ಪ್ರಯತ್ನಗಳಲ್ಲಿಯೂ ವಿಪತ್ತು, ಕಳವಳ, ಶಾಪ ಇವುಗಳನ್ನುಂಟುಮಾಡುತ್ತಾ ನಿಮ್ಮನ್ನು ಬೇಗನೆ ನಾಶಮಾಡುವನು.",
			"21": "<sup>21</sup> ವ್ಯಾಧಿಯು ನಿಮಗೆ ಹತ್ತಿಕೊಂಡೇ ಇರುವಂತೆ ಯೆಹೋವನು ಮಾಡಿ ನೀವು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವದಕ್ಕೆ ಹೋಗುವ ದೇಶದಲ್ಲಿ ನಿಮ್ಮಲ್ಲಿ ಒಬ್ಬರೂ ಉಳಿಯದಂತೆ ಮಾಡುವನು.",
			"22": "<sup>22</sup> ಆತನು ನಿಮ್ಮನ್ನು ಕ್ಷಯರೋಗ ಚಳಿಜ್ವರ ಉರಿತ ಉಷ್ಣಜ್ವರಗಳಿಂದಲೂ ದೇಶವನ್ನು ಕ್ಷಾಮದಿಂದಲೂ ಬೆಳೆಯನ್ನು ಕಾಡಿಗೆ ಬಿಸಿಗಾಳಿಗಳಿಂದಲೂ ಬಾಧಿಸಲಾಗಿ ನೀವು ಸಾಯುವ ತನಕ ಇವು ನಿಮ್ಮನ್ನು ಬೆನ್ಹತ್ತುವವು.",
			"23": "<sup>23</sup> ಮೇಲೆ ಆಕಾಶವು [ಮಳೆಸುರಿಸದೆ] ತಾಮ್ರದಂತೆಯೂ ಕೆಳಗೆ ಭೂಮಿಯು [ಬೆಳೆಕೊಡದೆ] ಕಬ್ಬಿಣದಂತೆಯೂ ಇರುವವು.",
			"24": "<sup>24</sup> ನಿಮ್ಮ ದೇಶದಲ್ಲಿ ಯೆಹೋವನು ಆಕಾಶದಿಂದ ಮಳೆಗೆ ಬದಲಾಗಿ ಧೂಳಿಯನ್ನೂ ಉಸುಬನ್ನೂ ನಿಮ್ಮ ಮೇಲೆ ಸುರಿಸುವನು; ಅದರಿಂದ ನೀವು ನಾಶವಾಗಿ ಹೋಗುವಿರಿ.",
			"25": "<sup>25</sup> ನೀವು ಶತ್ರುಗಳಿಂದ ಪರಾಜಯವನ್ನು ಹೊಂದುವಂತೆ ಯೆಹೋವನು ಮಾಡುವನು; ನೀವು ಒಂದೇ ದಾರಿಯಿಂದ ಅವರೆದುರಿಗೆ ಹೋಗಿ ಏಳು ದಾರಿಗಳಿಂದ ಓಡಿಹೋಗುವಿರಿ. ಲೋಕದ ಎಲ್ಲಾ ರಾಜ್ಯಗಳವರೂ ಇದನ್ನು ಕಂಡು ಬೆರಗಾಗುವರು.",
			"26": "<sup>26</sup> ನಿಮ್ಮ ಹೆಣಗಳು ಪಕ್ಷಿಗಳಿಗೂ ಕಾಡುಮೃಗಗಳಿಗೂ ಆಹಾರವಾಗುವವು; ಅವುಗಳನ್ನು ಬೆದರಿಸುವವರು ಯಾರೂ ಇರುವದಿಲ್ಲ.",
			"27": "<sup>27</sup> ಐಗುಪ್ತ್ಯರ ಹುಣ್ಣು, ಬಾವು, ತುರಿಕಜ್ಜಿ ಮುಂತಾದ ವಾಸಿಯಾಗದ ರೋಗಗಳಿಂದ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಬಾಧಿಸುವನು.",
			"28": "<sup>28</sup> ಚಿತ್ತಭ್ರಮಣೆ, ಕುರುಡತನ, ಮನೋವಿಸ್ಮಯ ಇವುಗಳಿಂದ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಬಾಧಿಸಲು",
			"29": "<sup>29</sup> ನೀವು ಕುರುಡರಂತೆ ಮಧ್ಯಾಹ್ನದಲ್ಲಿಯೂ ಕತ್ತಲಾಯಿತೆಂದು ತಡವರಿಸುತ್ತಾ ಇರುವಿರಿ; ನೀವು ಮಾಡುವ ಯಾವ ಕೆಲಸವೂ ಕೈಗೂಡುವದಿಲ್ಲ. ಅನ್ಯರು ಯಾವಾಗಲೂ ನಿಮ್ಮನ್ನು ಪೀಡಿಸುತ್ತಾ ನಿಮ್ಮ ಸೊತ್ತನ್ನು ಸೂರೆಮಾಡುತ್ತಾ ಇರುವರು; ತಪ್ಪಿಸುವವರು ಯಾರೂ ಇರುವದಿಲ್ಲ.",
			"30": "<sup>30</sup> ನೀವು ಮದುವೆಮಾಡಿಕೊಂಡ ಸ್ತ್ರೀಯನ್ನು ಮತ್ತೊಬ್ಬನು ಮಾನಭಂಗಪಡಿಸುವನು; ನೀವು ಕಟ್ಟಿಕೊಂಡ ಮನೆಯಲ್ಲಿ ಮತ್ತೊಬ್ಬನು ವಾಸಮಾಡುವನು; ನೀವು ಮಾಡಿಕೊಂಡ ದ್ರಾಕ್ಷೇತೋಟದ ಬೆಳೆಯು ನಿಮಗೆ ದೊರೆಯುವದಿಲ್ಲ.",
			"31": "<sup>31</sup> ನಿಮ್ಮ ದನಗಳನ್ನು ನಿಮ್ಮ ಕಣ್ಣು ಮುಂದೆ ಕೊಯ್ಯುವರು; ಅವುಗಳ ಮಾಂಸವು ನಿಮಗೆ ಸಿಕ್ಕುವದಿಲ್ಲ. ನಿಮ್ಮ ಕತ್ತೆಯನ್ನು ನಿಮ್ಮೆದುರಾಗಿ ಬಲಾತ್ಕಾರದಿಂದ ಹಿಡಿದುಕೊಂಡುಹೋಗಿ ನಿಮಗೆ ಹಿಂದಕ್ಕೆ ಕೊಡುವದಿಲ್ಲ. ನಿಮ್ಮ ಆಡುಕುರಿಗಳು ಶತ್ರುಗಳ ಪಾಲಾಗುವವು. ನಿಮಗೆ ರಕ್ಷಕರು ಯಾರೂ ಇರುವದಿಲ್ಲ.",
			"32": "<sup>32</sup> ನಿಮ್ಮ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ನಿಮ್ಮ ಮುಂದೆಯೇ ಹಿಡಿದು ಅನ್ಯರಿಗೆ ವಶಪಡಿಸುವರು; ನೀವು ಹಗಲೆಲ್ಲಾ ಅವರನ್ನು ಕಾಣಬೇಕೆಂದು ಹಂಬಲಿಸುತ್ತಾ ಕಂಗೆಡುವಿರಿ; ಆದರೆ ನಿಮ್ಮ ಯತ್ನವೇನೂ ಸಾಗುವದಿಲ್ಲ.",
			"33": "<sup>33</sup> ನಿಮಗೆ ಗುರುತೇ ಇಲ್ಲದ ಜನಾಂಗದವರು ಬಂದು ನಿಮ್ಮ ದೇಶದ ಬೆಳೆಯನ್ನೂ ನಿಮ್ಮ ಕಷ್ಟಾರ್ಜಿತವನ್ನೂ ತಿಂದುಬಿಡುವರು.",
			"34": "<sup>34</sup> ನೀವಾದರೋ ಯಾವಾಗಲೂ ಹಿಂಸೆಗೂ ಬಲಾತ್ಕಾರಕ್ಕೂ ಗುರಿಯಾಗಿ ನಿಮ್ಮ ಮುಂದೆ ನಡೆಯುವ ಸಂಗತಿಗಳ ದೆಸೆಯಿಂದ ಹುಚ್ಚರಾಗಿ ಹೋಗುವಿರಿ.",
			"35": "<sup>35</sup> ಯೆಹೋವನು ನಿಮ್ಮ ಮೊಣಕಾಲುಗಳಲ್ಲಿಯೂ ತೊಡೆಗಳಲ್ಲಿಯೂ ಮತ್ತು ಅಂಗಾಲು ಮೊದಲುಗೊಂಡು ನಡುನೆತ್ತಿಯವರೆಗೂ ವಾಸಿಯಾಗದ ಕೆಟ್ಟಕೆಟ್ಟ ಹುಣ್ಣುಗಳನ್ನು ಹುಟ್ಟಿಸಿ ಬಾಧಿಸುವನು.",
			"36": "<sup>36</sup> ನಿಮಗೂ ನಿಮ್ಮ ಪಿತೃಗಳಿಗೂ ಗೊತ್ತಿಲ್ಲದ ಜನಾಂಗದವರ ದೇಶಕ್ಕೆ ಯೆಹೋವನು ನಿಮ್ಮನ್ನೂ ನೀವು ನೇಮಿಸಿಕೊಳ್ಳುವ ಅರಸರನ್ನೂ ಒಯ್ಯಿಸುವನು; ಅಲ್ಲಿ ನೀವು ಮರದ ದೇವರುಗಳನ್ನೂ ಕಲ್ಲಿನ ದೇವರುಗಳನ್ನೂ ಸೇವಿಸುವಿರಿ.",
			"37": "<sup>37</sup> ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಒಯ್ಯಿಸಿದ ಜನಾಂಗಗಳಲ್ಲಿ ನೀವು ಆಶ್ಚರ್ಯಕ್ಕೂ ಗಾದೆಗೂ ಪರಿಹಾಸ್ಯಕ್ಕೂ ಗುರಿಯಾಗುವಿರಿ.",
			"38": "<sup>38</sup> ನೀವು ಹೊಲದಲ್ಲಿ ಎಷ್ಟು ಬೀಜವನ್ನು ಬಿತ್ತಿದ್ದರೂ ಮಿಡತೆಯ ದಂಡು ಬಂದು ಅದನ್ನು ತಿಂದು ಬಿಡುವದರಿಂದ ನಿಮಗೆ ದೊರೆಯುವ ಬೆಳೆ ಅತ್ಯಲ್ಪವಾಗುವದು;",
			"39": "<sup>39</sup> ನೀವು ದ್ರಾಕ್ಷಾವ್ಯವಸಾಯವನ್ನು ಎಷ್ಟು ಮಾಡಿದರೂ ಅದರ ಹಣ್ಣುಗಳನ್ನು ಹುಳಗಳೇ ತಿಂದುಹಾಕುವದರಿಂದ ನೀವು ಕೂಡಿಸಿಕೊಳ್ಳುವದಿಲ್ಲ, ಅವುಗಳ ರಸವನ್ನು ರುಚಿನೋಡುವದಿಲ್ಲ.",
			"40": "<sup>40</sup> ನಿಮ್ಮ ಎಲ್ಲಾ ಪ್ರದೇಶಗಳಲ್ಲಿ ಎಣ್ಣೇಮರಗಳು ಎಷ್ಟಿದ್ದರೂ ಅವುಗಳ ಕಾಯಿಗಳು ಉದುರಿಹೋಗುವದರಿಂದ ನೀವು ಎಣ್ಣೆಯನ್ನು ಮೈಗೆ ಹಚ್ಚಿಕೊಳ್ಳುವದಿಲ್ಲ.",
			"41": "<sup>41</sup> ನಿಮಗೆ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳು ಎಷ್ಟು ಹುಟ್ಟಿದರೂ ಅವರು ಸೆರೆಯವರಾಗಿ ಒಯ್ಯಲ್ಪಡುವದರಿಂದ ನಿಮ್ಮ ಬಳಿಯಲ್ಲಿ ಇರುವದಿಲ್ಲ.",
			"42": "<sup>42</sup> ನಿಮ್ಮ ಎಲ್ಲಾ ಮರಗಳೂ ಪೈರುಗಳೂ ಮಿಡತೆಯ ಪಾಲಾಗುವವು.",
			"43": "<sup>43</sup> ನಿಮ್ಮ ಮಧ್ಯದಲ್ಲಿರುವ ಅನ್ಯರು ನಿಮಗಿಂತಲೂ ಹೆಚ್ಚೆಚ್ಚಾಗಿ ಅಭಿವೃದ್ಧಿಗೆ ಬರುವರು; ನೀವೋ ಕಡಮೆಯಾಗುತ್ತಾ ಹೀನಸ್ಥಿತಿಗೆ ಬರುವಿರಿ.",
			"44": "<sup>44</sup> ಅವರು ನಿಮಗೆ ಸಾಲಕೊಡುವರೇ ಹೊರತು ನೀವು ಅವರಿಗೆ ಕೊಡುವದಿಲ್ಲ. ನೀವು ಅವರಿಗೆ ಅಧೀನರಾಗುವಿರಿ; ಅವರು ಶಿರಸ್ಸಾಗುವರು.",
			"45": "<sup>45</sup> ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಮಾತಿಗೆ ಕಿವಿಗೊಡದೆಯೂ ಆತನು ನೇಮಿಸಿದ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ಅನುಸರಿಸದೆಯೂ ಹೋದದರಿಂದ ಈ ಎಲ್ಲಾ ಅಶುಭಗಳು ನಿಮಗೆ ಪ್ರಾಪ್ತವಾಗಿ ನಿಮ್ಮನ್ನು ಹಿಂದಟ್ಟಿ ಹಿಡಿದು ಕಡೆಗೆ ನಾಶಮಾಡುವವು.",
			"46": "<sup>46</sup> ಇವು ನಿಮಗೂ ನಿಮ್ಮ ಸಂತತಿಯವರಿಗೂ ಯಾವಾಗಲೂ ಪ್ರಾಪ್ತವಾಗುತ್ತಾ [ಎಲ್ಲರಿಗೂ] ಎಚ್ಚರಿಕೆಯನ್ನೂ ಬೆರಗನ್ನೂ ಉಂಟುಮಾಡುವವು.",
			"47": "<sup>47</sup> ನಿಮಗೆ ಸರ್ವಸಮೃದ್ಧಿಯುಂಟಾದ ಕಾಲದಲ್ಲಿಯೂ ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಹರ್ಷಾನಂದಗಳುಳ್ಳವರಾಗಿ ಸೇವಿಸದೆಹೋದದರಿಂದ ಯೆಹೋವನು ನಿಮ್ಮ ಮೇಲೆ ಶತ್ರುಗಳನ್ನು ಬರಮಾಡುವನು;",
			"48": "<sup>48</sup> ಆಗ ನೀವು ಹಸಿವು ಬಾಯಾರಿಕೆಗಳುಳ್ಳವರಾಗಿ ಬಟ್ಟೆಯೂ ಏನೂ ಇಲ್ಲದೆ ಆ ಶತ್ರುಗಳಿಗೇ ಸೇವಕರಾಗಬೇಕಾಗುವದು. ಆತನು ಕಬ್ಬಿಣದ ನೊಗವನ್ನು ಹೇರಿಸಿ ನಿಮ್ಮನ್ನು ನಾಶಮಾಡುವನು.",
			"49": "<sup>49</sup> ನಿಮಗೆ ತಿಳಿಯದ ಭಾಷೆಯನ್ನಾಡುವ ಒಂದು ಜನಾಂಗವು ರಣಹದ್ದು ಹೇಗೆ ದೂರದಿಂದ ಹಾರಿಬರುವದೋ ಹಾಗೆಯೇ ದೂರದಿಂದ ನಿಮ್ಮ ಮೇಲೆ ಬರುವಂತೆ ಯೆಹೋವನು ಮಾಡುವನು.",
			"50": "<sup>50</sup> ಆ ಜನಾಂಗದವರು ಕ್ರೂರಮುಖವುಳ್ಳವರಾಗಿ ನಿಮ್ಮನ್ನು ವೃದ್ಧರೆಂದು ಮರ್ಯಾದೆ ತೋರಿಸುವದಿಲ್ಲ, ಚಿಕ್ಕವರೆಂದು ಕನಿಕರಿಸುವದಿಲ್ಲ.",
			"51": "<sup>51</sup> ಅವರು ನಿಮ್ಮ ದನಗಳನ್ನೂ ಬೆಳೆಗಳನ್ನೂ ತಿಂದುಬಿಟ್ಟು ನಿಮಗೆ ಧಾನ್ಯವನ್ನಾಗಲಿ ದ್ರಾಕ್ಷಾರಸವನ್ನಾಗಲಿ ಎಣ್ಣೆಯನ್ನಾಗಲಿ ದನಕುರಿಗಳ ಸಂತಾನವನ್ನಾಗಲಿ ಉಳಿಸದೆ ನಿಮ್ಮನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ನಾಶಮಾಡುವರು.",
			"52": "<sup>52</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಕೊಟ್ಟಿರುವ ದೇಶದ ಎಲ್ಲಾ ಪಟ್ಟಣಗಳಿಗೂ ಅವರು ಮುತ್ತಿಗೆಹಾಕಿ ನೀವು ನಂಬಿಕೊಂಡಿದ್ದ ಎತ್ತರವಾದ ಕೊತ್ತಲುಗಳುಳ್ಳ ಗೋಡೆಗಳನ್ನು ಕೆಡವಿಬಿಡುವರು.",
			"53": "<sup>53</sup> ಶತ್ರುಗಳು ನಿಮ್ಮ ಪಟ್ಟಣಗಳಿಗೆ ಮುತ್ತಿಗೆಹಾಕಿ ನಿಮ್ಮನ್ನು ಬಹಳವಾಗಿ ಇರಿಕಿಸುವದರಿಂದ ನೀವು ನಿಮ್ಮ ಸಂತಾನವನ್ನೇ ಅಂದರೆ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಕೊಟ್ಟಿರುವ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳ ಮಾಂಸವನ್ನೇ ತಿನ್ನುವಿರಿ.",
			"54": "<sup>54</sup> ಶತ್ರುಗಳು ನಿಮ್ಮ ಎಲ್ಲಾ ಪಟ್ಟಣಗಳಿಗೆ ಮುತ್ತಿಗೆ ಹಾಕಿ ನಿಮ್ಮನ್ನು ಬಹಳವಾಗಿ ಇರಿಕಿಸುವ ಕಾಲದಲ್ಲಿ ನಿಮ್ಮಲ್ಲಿ ಮೃದುಸ್ವಭಾವಿಯೂ ಅತಿಸುಕುಮಾರನೂ ಆಗಿರುವವನೂ ತನಗೆ ತಿನ್ನಲಿಕ್ಕೆ ಏನೂ ಉಳಿಯದೆಹೋದದರಿಂದ ತನ್ನ ಮಕ್ಕಳ ಮಾಂಸವನ್ನು ತಾನೇ ತಿನ್ನಬೇಕೆಂದು ಅಣ್ಣತಮ್ಮಂದಿರಿಗೂ ಪ್ರಾಣಪ್ರಿಯಳಾದ ಹೆಂಡತಿಗೂ ಉಳಿದ ಮಕ್ಕಳಿಗೂ ಮೋರೆಯನ್ನು ಕಪ್ಪುಮಾಡಿಕೊಂಡು ಆ ಮಾಂಸದಲ್ಲಿ ಅವರಿಗೆ ಸ್ವಲ್ಪವನ್ನಾದರೂ ಕೊಡದೆಹೋಗುವನು.",
			"56": "<sup>56</sup> ಶತ್ರುಗಳು ನಿಮ್ಮ ಪಟ್ಟಣಗಳಿಗೆ ಮುತ್ತಿಗೆ ಹಾಕಿ ನಿಮ್ಮನ್ನು ಬಹಳವಾಗಿ ಇರಿಕಿಸುವ ಕಾಲದಲ್ಲಿ ಕೋಮಲತೆಯ ಮತ್ತು ಅತಿಸುಕುಮಾರತೆಯ ದೆಸೆಯಿಂದ ಅಂಗಾಲನ್ನೂ ನೆಲಕ್ಕೆ ಇಡದವಳಾದ ಸ್ತ್ರೀಯು ತಿನ್ನಲಿಕ್ಕೆ ಏನೂ ಇಲ್ಲದವಳಾಗಿ ತಾನು ಆಗಲೇ ಹೆತ್ತ ಮಗುವನ್ನೂ ಅದರ ಮೇಲಣ ಮಾಸನ್ನೂ ರಹಸ್ಯವಾಗಿ ತಿನ್ನಬೇಕೆಂದು ಪ್ರಾಣಪ್ರಿಯರಾದ ಗಂಡನಿಗೂ ಮಗನಿಗೂ ಮಗಳಿಗೂ ಮೋರೆಯನ್ನು ಕಪ್ಪುಮಾಡಿಕೊಳ್ಳವಳು.",
			"58": "<sup>58</sup> ಈ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುವ ಧರ್ಮಶಾಸ್ತ್ರವಾಕ್ಯಗಳನ್ನೆಲ್ಲಾ ನೀವು ಅನುಸರಿಸಲೊಲ್ಲದೆ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನೆಂಬ ಮಹಾಮಹಿಮೆಯುಳ್ಳ ಭಯಂಕರ ನಾಮದಲ್ಲಿ ನೀವು ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿರದೆ ಹೋದರೆ",
			"59": "<sup>59</sup> ಆತನು ನಿಮಗೂ ನಿಮ್ಮ ಸಂತತಿಯವರಿಗೂ ದೀರ್ಘಕಾಲವಿರುವ ಬಹುವಿಶೇಷವಾದ ಉಪದ್ರವಗಳನ್ನು ಬರಮಾಡುವನು. ಬಹುಕಾಲದವರೆಗೂ ವಾಸಿಯಾಗದ ಘೋರ ವ್ಯಾಧಿಗಳಿಂದ ನಿಮ್ಮನ್ನು ಬಾಧಿಸುವನು.",
			"60": "<sup>60</sup> ನೀವು ಅಂಜಿಕೊಳ್ಳುತ್ತಿದ್ದ ಐಗುಪ್ತದೇಶದ ರೋಗಗಳನ್ನೆಲ್ಲಾ ತಿರಿಗಿ ನಿಮ್ಮ ಮೇಲೆ ಬರಮಾಡುವನು; ಅವು ನಿಮ್ಮನ್ನು ಹತ್ತಿಕೊಂಡೇ ಇರುವವು.",
			"61": "<sup>61</sup> ಇಷ್ಟು ಮಾತ್ರವಲ್ಲದೆ ಈ ಧರ್ಮಶಾಸ್ತ್ರಗ್ರಂಥದಲ್ಲಿ ಬರೆಯದೆ ಇರುವ ವಿಧವಿಧವಾದ ರೋಗಗಳನ್ನೂ ವ್ಯಾಧಿಗಳನ್ನೂ ನಿಮ್ಮ ಮೇಲೆ ಬರಮಾಡಿ ನಿಮ್ಮನ್ನು ನಾಶಮಾಡುವನು.",
			"62": "<sup>62</sup> ನಕ್ಷತ್ರಗಳಷ್ಟು ಅಸಂಖ್ಯರಾಗಿರುವ ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಮಾತಿನಲ್ಲಿ ಲಕ್ಷ್ಯವಿಡದೆಹೋದದರಿಂದ ಸ್ವಲ್ಪಮಂದಿಯೇ ಉಳಿಯುವಿರಿ.",
			"63": "<sup>63</sup> ಹೇಗೆ ಯೆಹೋವನು ಈಗ ನಿಮ್ಮನ್ನು ಅಭಿವೃದ್ಧಿಪಡಿಸುವದರಲ್ಲಿಯೂ ಹೆಚ್ಚಿಸುವದರಲ್ಲಿಯೂ ಸಂತೋಷಪಡುತ್ತಾನೋ ಹಾಗೆಯೇ ಆಗ ನಿಮ್ಮನ್ನು ಕೆಡಿಸಿ ನಾಶಮಾಡುವದರಲ್ಲಿ ಸಂತೋಷಪಟ್ಟು ನೀವು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವದಕ್ಕೆ ಹೋಗುವ ದೇಶದೊಳಗಿಂದ ನಿಮ್ಮನ್ನು ಕಿತ್ತುಹಾಕುವನು.",
			"64": "<sup>64</sup> ಭೂಮಂಡಲದ ಚತುರ್ದಿಕ್ಕುಗಳಲ್ಲಿರುವ ಎಲ್ಲಾ ಜನಾಂಗಗಳಲ್ಲಿಯೂ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಚದರಿಸುವನು; ಅಲ್ಲಿ ನಿಮಗಾದರೂ ನಿಮ್ಮ ಪಿತೃಗಳಿಗಾದರೂ ಗೊತ್ತಿಲ್ಲದ ಮರದ ದೇವರುಗಳನ್ನೂ ಕಲ್ಲಿನ ದೇವರುಗಳನ್ನೂ ಪೂಜಿಸುವಿರಿ.",
			"65": "<sup>65</sup> ಆ ದೇಶಗಳಲ್ಲಿ ನಿಮಗೆ ಯಾವ ವಿಶ್ರಾಂತಿಯೂ ದೊರೆಯುವದಿಲ್ಲ; ಸ್ವಲ್ಪ ಹೊತ್ತು ಅಂಗಾಲಿಡುವದಕ್ಕೂ ಸ್ಥಳ ಸಿಕ್ಕುವದಿಲ್ಲ. ನೀವು ನಡುಗುವ ಹೃದಯವುಳ್ಳವರಾಗಿಯೂ ದಿಕ್ಕು ತೋರದೆ ಕಂಗೆಟ್ಟವರಾಗಿಯೂ ಮನಗುಂದಿದವರಾಗಿಯೂ ಇರುವಂತೆ ಯೆಹೋವನು ಮಾಡುವನು.",
			"66": "<sup>66</sup> ಬದುಕುವೆವೋ ಇಲ್ಲವೋ ಎಂಬದಾಗಿ ಅನುಮಾನಪಟ್ಟು ನೀವು ಹಗಲಿರುಳು ಪ್ರಾಣಭಯದಲ್ಲಿರುವಿರಿ; ಪ್ರಾಣದ ಮೇಲಣ ನಂಬಿಕೆಯನ್ನು ಬಿಟ್ಟೇಬಿಡುವಿರಿ.",
			"67": "<sup>67</sup> ಮನಸ್ಸಿನಲ್ಲಿ ಯಾವಾಗಲೂ ಪ್ರಾಣಭಯವಿರುವದರಿಂದಲೂ ಕಣ್ಣಿನಿಂದ ಭಯಂಕರವಾದ ಸಂಗತಿಗಳನ್ನು ನೋಡುವದರಿಂದಲೂ ನೀವು ಹೊತ್ತಾರೆಯಲ್ಲಿ - ಅಯ್ಯೋ, ಅಯ್ಯೋ! ಸಾಯಂಕಾಲ ಯಾವಾಗ ಬರುವದೋ ಎಂದೂ ಸಾಯಂಕಾಲದಲ್ಲಿ - ಅಯ್ಯೋ, ಅಯ್ಯೋ! ಮರುದಿನ ಯಾವಾಗ ಬರುವದೋ ಎಂದೂ ಹೇಳುವಿರಿ.",
			"68": "<sup>68</sup> ನೀವು ಐಗುಪ್ತದೇಶವನ್ನು ತಿರಿಗಿ ಎಂದಿಗೂ ನೋಡುವದಿಲ್ಲವೆಂದು ಯೆಹೋವನು ಹೇಳಿದ್ದರೂ ಆತನು ನಿಮ್ಮನ್ನು ಹಡಗುಗಳನ್ನೇರಿಸಿ ಅಲ್ಲಿಗೆ ತಿರಿಗಿ ಹೋಗಮಾಡುವನು. ಅಲ್ಲಿ ನೀವು ಶತ್ರುಗಳಿಗೆ ನಿಮ್ಮನ್ನು ದಾಸದಾಸಿಯರನ್ನಾಗಿ ಮಾರಿಕೊಳ್ಳಬೇಕೆಂದು ಅಪೇಕ್ಷಿಸಿದರೂ ಯಾರೂ ನಿಮ್ಮನ್ನು ಕೊಂಡುಕೊಳ್ಳುವದಿಲ್ಲ."
		},
		"29": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಹೋರೇಬಿನಲ್ಲಿ ಇಸ್ರಾಯೇಲ್ಯರೊಡನೆ ಮಾಡಿದ ನಿಬಂಧನವಲ್ಲದೆ ಆತನು ಮೋವಾಬ್ಯರ ದೇಶದಲ್ಲಿ ಅವರೊಡನೆ ಮಾಡಬೇಕೆಂದು ಮೋಶೆಗೆ ಆಜ್ಞಾಪಿಸಿದ ನಿಬಂಧನ ವಚನಗಳು.",
			"2": "<sup>2</sup> ಮೋಶೆ ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರನ್ನು ಕರೆದು ಹೀಗಂದನು - ಯೆಹೋವನು ಐಗುಪ್ತದೇಶದಲ್ಲಿ ನಿಮ್ಮ ಕಣ್ಣು ಮುಂದೆ ಫರೋಹನಿಗೂ ಅವನ ಪರಿವಾರದವರಿಗೂ",
			"3": "<sup>3</sup> ಸಮಸ್ತ ಪ್ರಜೆಗಳಿಗೂ ಮಾಡಿದ ಮಹಾಮನಶ್ಶೋಧನೆ ಉತ್ಪಾತ ಮಹತ್ಕಾರ್ಯಗಳನ್ನು ನೀವು ನೋಡೇನೋಡಿದ್ದೀರಷ್ಟೆ.",
			"4": "<sup>4</sup> ಆದರೂ ಗ್ರಹಿಸುವ ಬುದ್ಧಿ, ನೋಡುವ ಕಣ್ಣು, ಕೇಳುವ ಕಿವಿ ಇವುಗಳನ್ನು ಯೆಹೋವನು ಇಂದಿನವರೆಗೂ ನಿಮಗೆ ಅನುಗ್ರಹಿಸಲಿಲ್ಲ ಅಯ್ಯೋ.",
			"5": "<sup>5</sup> ನಾಲ್ಪತ್ತು ವರುಷ ನಾನು ನಿಮ್ಮನ್ನು ಅರಣ್ಯದಲ್ಲಿ ನಡಿಸಿಕೊಂಡು ಬಂದಿದ್ದೇನೆ. ನಿಮ್ಮ ಮೈಮೇಲಿದ್ದ ಉಡುಪಾಗಲಿ ಕಾಲಲ್ಲಿದ್ದ ಕೆರವಾಗಲಿ ಜೀರ್ಣವಾಗಲಿಲ್ಲ;",
			"6": "<sup>6</sup> ನೀವು ರೊಟ್ಟಿಯನ್ನು ತಿನ್ನಲಿಲ್ಲ, ದ್ರಾಕ್ಷಾರಸವನ್ನಾಗಲಿ ಯಾವ ಮದ್ಯವನ್ನಾಗಲಿ ಕುಡಿಯಲೇ ಇಲ್ಲ. ಯೆಹೋವನೇ ನಿಮ್ಮ ದೇವರು ಎಂಬದು ನಿಮಗೆ ತಿಳಿಯಬೇಕೆಂಬ ಉದ್ದೇಶದಿಂದ ಹೀಗಾಯಿತು.",
			"7": "<sup>7</sup> ನೀವು ಈ ಸ್ಥಳಕ್ಕೆ ಬಂದಾಗ ಹೆಷ್ಬೋನಿನ ಅರಸನಾದ ಸೀಹೋನನೂ ಬಾಷಾನಿನ ಅರಸನಾದ ಓಗನೂ ನಮ್ಮೊಡನೆ ಯುದ್ಧಮಾಡುವದಕ್ಕೆ ಬರಲಾಗಿ",
			"8": "<sup>8</sup> ನಾವು ಅವರನ್ನು ಸೋಲಿಸಿ ಅವರ ರಾಜ್ಯಗಳನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡು ರೂಬೇನ್ಯರಿಗೂ ಗಾದ್ಯರಿಗೂ ಮನಸ್ಸೆ ಕುಲದವರಲ್ಲಿ ಅರ್ಧ ಜನರಿಗೂ ಸ್ವಾಸ್ತ್ಯವಾಗಿ ಕೊಟ್ಟೆವಷ್ಟೆ.",
			"9": "<sup>9</sup> ಈ ಸಂಗತಿಗಳನ್ನೆಲ್ಲಾ ನೀವು ಜ್ಞಾಪಕಮಾಡಿಕೊಂಡು ಈ ನಿಬಂಧನವಾಕ್ಯಗಳನ್ನು ಅನುಸರಿಸಿ ನಡೆಯಬೇಕು; ಆಗ ನೀವು ನಡಿಸುವ ಎಲ್ಲಾ ಕೆಲಸಗಳಲ್ಲಿಯೂ ಜಾಣರಾಗಿ ಅಭಿವೃದ್ಧಿಹೊಂದುವಿರಿ.",
			"10": "<sup>10</sup> ಇಸ್ರಾಯೇಲ ಪುರುಷರೇ, ನಿಮ್ಮ ಪ್ರಧಾನರು, ಗೋತ್ರದವರು, ಹಿರಿಯರು, ಅಧಿಕಾರಿಗಳು,",
			"11": "<sup>11</sup> ಹೆಂಗಸರು, ಮಕ್ಕಳು, ನಿಮ್ಮ ಪಾಳೆಯದಲ್ಲಿರುವ ಅನ್ಯರಾದ ಕಟ್ಟಿಗೆಯವರು, ನೀರಿನವರು ಮುಂತಾದ ಆಳುಗಳು,",
			"12": "<sup>12</sup> ನೀವೆಲ್ಲರೂ ಈ ಹೊತ್ತು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನಿಂತಿರುವದಕ್ಕೆ ಕಾರಣವೇನಂದರೆ - ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮಗೆ ಹೇಳಿದಂತೆಯೂ ನಿಮ್ಮ ಪಿತೃಗಳಾದ ಅಬ್ರಹಾಮ್ ಇಸಾಕ್ ಯಾಕೋಬರಿಗೆ ವಾಗ್ದಾನಮಾಡಿದಂತೆಯೂ ನಿಮ್ಮನ್ನು ತನಗೆ ಸ್ವಕೀಯಜನರನ್ನಾಗಿಯೂ ತನ್ನನ್ನು ನಿಮಗೆ ದೇವರನ್ನಾಗಿಯೂ ಸ್ಥಾಪಿಸಿಕೊಳ್ಳುವದಕ್ಕೋಸ್ಕರ ಈಗ ನಿಮ್ಮೊಡನೆ ಪ್ರಮಾಣಪೂರ್ವಕವಾದ ಒಂದು ಒಡಂಬಡಿಕೆಯನ್ನು ಮಾಡಿಕೊಳ್ಳುವವನಾಗಿದ್ದಾನೆ; ನೀವು ಅದಕ್ಕೆ ಸೇರುವದಕ್ಕಾಗಿ ಕೂಡಿಬಂದಿದ್ದೀರಿ.",
			"14": "<sup>14</sup> ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸಿದ್ಧರಾಗಿ ನಿಂತಿರುವ ನಮ್ಮೊಡನೆ ಮಾತ್ರವಲ್ಲದೆ ಮುಂದೆ ಹುಟ್ಟುವ ನಮ್ಮ ಸಂತತಿಯವರೊಡನೆಯೂ ಪ್ರಮಾಣಪೂರ್ವಕವಾದ ಈ ಒಡಂಬಡಿಕೆಯನ್ನು ಮಾಡಿಕೊಳ್ಳುವವನಾಗಿದ್ದಾನೆ.",
			"16": "<sup>16</sup> ನಾವು ಐಗುಪ್ತದೇಶದಲ್ಲಿ ವಾಸಮಾಡಿದ್ದೂ ಅನೇಕ ಜನಾಂಗಗಳ ಪ್ರದೇಶಗಳನ್ನು ದಾಟಿಬಂದದ್ದೂ ನಿಮಗೆ ತಿಳಿದೇ ಇದೆ;",
			"17": "<sup>17</sup> ಆ ಜನಾಂಗಗಳಲ್ಲಿ ನಡೆಯುವ ಹೇಯಾಚಾರಗಳನ್ನೂ ಅವರು ಮರ ಕಲ್ಲು ಬೆಳ್ಳಿ ಬಂಗಾರಗಳಿಂದ ಮಾಡಿಕೊಂಡು ಪೂಜಿಸುವ ಬೊಂಬೆಗಳನ್ನೂ ಕಂಡೇ ಕಂಡಿದ್ದೀರಷ್ಟೆ.",
			"18": "<sup>18</sup> ಎಚ್ಚರಿಕೆಯಾಗಿರ್ರಿ; ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಬಿಟ್ಟು ಆ ಜನಾಂಗಗಳ ದೇವರುಗಳನ್ನು ಸೇವಿಸುವ ಯಾವ ಕುಲವಾಗಲಿ ಕುಟುಂಬವಾಗಲಿ ಸ್ತ್ರೀಪುರುಷರಾಗಲಿ ನಿಮ್ಮಲ್ಲಿ ಇರಲೇಬಾರದು; ನಿಮ್ಮಲ್ಲಿ ಯಾವ ವಿಷಲತೆಯ ಬೇರೂ ಇರಬಾರದು.",
			"19": "<sup>19</sup> ಶಾಪದಿಂದ ಕೂಡಿರುವ ಈ ಪ್ರತಿಜ್ಞೆಯನ್ನು ಕೇಳಿದ ಯಾವನಾದರೂ ತನ್ನೊಳಗೆ - ನಾನು ಹಟವನ್ನು ಹಿಡಿದು ಅವಿಧೇಯನಾದರೂ ನನಗೆ ಕ್ಷೇಮವಾಗಿಯೇ ಇರುವದು ಅಂದುಕೊಂಡು ಹಿಗ್ಗಬಾರದು. ಹಾಗೆ ಮಾಡುವವನು ದೋಷಿಗಳಿಗೂ ನಿರ್ದೋಷಿಗಳಿಗೂ ನಾಶನವನ್ನುಂಟುಮಾಡಾನು.",
			"20": "<sup>20</sup> ಯೆಹೋವನು ಅಂಥವನನ್ನು ಎಂದಿಗೂ ಕ್ಷಮಿಸದೆ ಬಹಳ ಸಿಟ್ಟುಮಾಡಿಕೊಂಡು ತನ್ನ ಗೌರವವನ್ನು ಕಾಪಾಡಿಕೊಳ್ಳುವವನಾಗಿ ಈ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುವ ಎಲ್ಲಾ ಶಾಪೋಕ್ತಿಗಳಿಗೂ ಅವನನ್ನು ಗುರಿಪಡಿಸಿ ಅವನ ಹೆಸರನ್ನು ಭೂಮಿಯ ಮೇಲೆ ಇಲ್ಲದಂತೆ ಮಾಡುವನು.",
			"21": "<sup>21</sup> ಈ ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿ ಬರೆದಿರುವ ನಿಬಂಧನೆಯನ್ನು ಮೀರುವವರಿಗೆ ಉಂಟಾಗುವ ಶಾಪಗಳು ಅವನಲ್ಲಿ ನೆರವೇರಲೆಂದು ಯೆಹೋವನು ಅವನನ್ನು ಇಸ್ರಾಯೇಲ್ಯರ ಎಲ್ಲಾ ಕುಲಗಳಿಂದ ಪ್ರತ್ಯೇಕಿಸಿ ದಂಡಿಸುವನು.",
			"22": "<sup>22</sup> ಮುಂದೆ ಹುಟ್ಟುವ ನಿಮ್ಮ ಸಂತತಿಯವರೂ ದೇಶಾಂತರಗಳಿಂದ ಬರುವ ಅನ್ಯರೂ ನಿಮ್ಮ ದೇಶಕ್ಕೆ ಯೆಹೋವನು ಉಂಟುಮಾಡಿದ ರೋಗೋಪದ್ರವಗಳನ್ನು ಕಂಡು ಆಶ್ಚರ್ಯಪಡುವರು.",
			"23": "<sup>23</sup> ಎಲ್ಲಾ ಜನಾಂಗಗಳವರೂ ನಿಮ್ಮ ಆ ದೇಶವು ಯಾವ ವ್ಯವಸಾಯವಾದರೂ ಇಲ್ಲದೆ ಹುಲ್ಲಾದರೂ ಬೆಳೆಯದೆ ಹಾಳುಬಿದ್ದು ಯೆಹೋವನು ಮಹಾ ಕೋಪದಿಂದ ಕೆಡವಿದ ಸೊದೋಮ್ ಗೊಮೋರ ಅದ್ಮಾ ಚೆಬೋಯೀಮ್ ಎಂಬ ಪಟ್ಟಣಗಳ ಪ್ರದೇಶದಂತೆ ಸುಟ್ಟುಹೋದದ್ದಾಗಿ ಎಲ್ಲಾ ಕಡೆಗಳಲ್ಲಿಯೂ ಗಂಧಕ ಉಪ್ಪುಗಳಿಂದ ತುಂಬಿರುವದನ್ನು ಕಂಡು -",
			"24": "<sup>24</sup> ಯೆಹೋವನು ಈ ದೇಶಕ್ಕೆ ಹೀಗೆ ಮಾಡಿದ್ದೇಕೆ? ಇಂಥ ಕೋಪಾಗ್ನಿಗೆ ಕಾರಣವೇನಿದ್ದೀತು? ಎಂಬದಾಗಿ ವಿಚಾರಿಸುವರು.",
			"25": "<sup>25</sup> ಅದಕ್ಕೆ ಜನರು - ಈ ದೇಶದವರ ಪಿತೃಗಳ ದೇವರಾದ ಯೆಹೋವನು ಅವರನ್ನು ಐಗುಪ್ತದೇಶದಿಂದ ಬಿಡಿಸಿ ಅವರೊಡನೆ ಮಾಡಿದ ನಿಬಂಧನೆಯನ್ನು ಅವರು ಉಲ್ಲಂಘಿಸಿ",
			"26": "<sup>26</sup> ತಮಗೆ ನೇಮಿಸಲ್ಪಡದೆಯೂ ಗೊತ್ತಿಲ್ಲದೆಯೂ ಇದ್ದ ಇತರ ದೇವರುಗಳನ್ನು ಸೇವಿಸಿ ಪೂಜಿಸಿದದರಿಂದ",
			"27": "<sup>27</sup> ಯೆಹೋವನು ಅವರ ದೇಶದ ಮೇಲೆ ಕೋಪಮಾಡಿ ಈ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುವ ಶಾಪಗಳನ್ನು ಅವರ ಮೇಲೆ ಬರಮಾಡಿ",
			"28": "<sup>28</sup> ಮಹಾಕೋಪೋದ್ರೇಕದಿಂದ ಆ ಜನರನ್ನು ದೇಶದಿಂದ ಕಿತ್ತುಹಾಕಿ ಈಗ ಅನುಭವಕ್ಕೆ ಬಂದಿರುವಂತೆ ದೇಶಾಂತರದಲ್ಲಿ ಹಾಕಿಬಿಟ್ಟನೆಂದು ಉತ್ತರಕೊಡುವರು.",
			"29": "<sup>29</sup> ರಹಸ್ಯವಾದವುಗಳು ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನವು; ಆದರೆ ಆತನಿಂದ ಪ್ರಕಟವಾದವುಗಳು ಸದಾ ನಮ್ಮ ಮತ್ತು ನಮ್ಮ ಸಂತತಿಯವರ ಪಾಲಾಗಿರುವದರಿಂದ ನಾವು ಈ ಧರ್ಮಶಾಸ್ತ್ರನಿಯಮಗಳನ್ನು ಅನುಸರಿಸಿ ನಡೆಯಬೇಕಾದ ಹಂಗಿನಲ್ಲಿದ್ದೇವೆ."
		},
		"30": {
			"1": "<sup>1</sup> ನಾನು ನಿಮ್ಮ ಮುಂದೆ ಇಟ್ಟಿರುವ ಆಶೀರ್ವಾದವೂ ಶಾಪವೂ ನಿಮ್ಮ ಅನುಭವಕ್ಕೆ ಬಂದಾಗ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಚದರಿಸಿರುವ ದೇಶಗಳಲ್ಲಿ ನೀವು ಇವುಗಳನ್ನು ನೆನಪಿಗೆ ತಂದುಕೊಂಡು",
			"2": "<sup>2</sup> ನಿಮ್ಮ ಮಕ್ಕಳು ಸಹಿತವಾಗಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಆಶ್ರಯಕ್ಕೆ ತಿರಿಗಿ ಬಂದು ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ಆತನ ಮಾತುಗಳಿಗೆ ಸಂಪೂರ್ಣವಾದ ಹೃದಯದಿಂದಲೂ ಮನಸ್ಸಿನಿಂದಲೂ ವಿಧೇಯರಾದರೆ",
			"3": "<sup>3</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ದುರವಸ್ಥೆಯಿಂದ ನಿಮ್ಮನ್ನು ತಪ್ಪಿಸಿ ನಿಮ್ಮನ್ನು ಕನಿಕರಿಸಿ ತಾನು ನಿಮ್ಮನ್ನು ಚದರಿಸಿರುವ ಎಲ್ಲಾ ದೇಶಗಳಿಂದ ತಿರಿಗಿ ಕೂಡಿಸುವನು.",
			"4": "<sup>4</sup> ನಿಮ್ಮಲ್ಲಿ ಯಾರಾದರೂ ಭೂಲೋಕದ ಕೊನೆಯವರೆಗೂ ಒಯ್ಯಲ್ಪಟ್ಟರೂ ಅಲ್ಲಿಂದಲೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಅವರನ್ನೂ ಕರತಂದು",
			"5": "<sup>5</sup> ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ಸ್ವಾಸ್ತ್ಯವಾಗಿದ್ದ ದೇಶದಲ್ಲಿ ಸೇರಿಸಿ ಅದು ನಿಮಗೂ ಸ್ವದೇಶವಾಗುವಂತೆ ಮಾಡಿ ನಿಮಗೆ ಮೇಲನ್ನುಂಟುಮಾಡಿ ಪಿತೃಗಳಿಗಿಂತಲೂ ಅಧಿಕವಾಗಿ ನಿಮ್ಮನ್ನು ಹೆಚ್ಚಿಸುವನು.",
			"6": "<sup>6</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮ ಮತ್ತು ನಿಮ್ಮ ಸಂತತಿಯವರ ಹೃದಯಕ್ಕೇ ಸುನ್ನತಿಮಾಡುವನು. ಆಗ ನೀವು ಆತನನ್ನು ಸಂಪೂರ್ಣವಾದ ಹೃದಯದಿಂದಲೂ ಮನಸ್ಸಿನಿಂದಲೂ ಪ್ರೀತಿಸಿ ಬದುಕಿಕೊಳ್ಳುವಿರಿ.",
			"7": "<sup>7</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಆ ಶಾಪಗಳನ್ನೆಲ್ಲಾ ನಿಮ್ಮನ್ನು ದ್ವೇಷಿಸಿ ಹಿಂಸಿಸಿದ ನಿಮ್ಮ ಶತ್ರುಗಳ ಮೇಲೆಯೇ ಬರಮಾಡುವನು.",
			"8": "<sup>8</sup> ನೀವಾದರೋ ಯೆಹೋವನ ಮಾತಿಗೆ ಪುನಃ ವಿಧೇಯರಾಗಿ ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ಆತನ ಆಜ್ಞೆಗಳಂತೆ ನಡೆಯುವಿರಿ;",
			"9": "<sup>9</sup> ಮತ್ತು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮ ಸಂತಾನ ಪಶು ವ್ಯವಸಾಯಗಳನ್ನು ಹೆಚ್ಚಿಸಿ ನಿಮ್ಮ ಎಲ್ಲಾ ಪ್ರಯತ್ನಗಳನ್ನು ಸಫಲಪಡಿಸಿ ನಿಮಗೆ ಮೇಲನ್ನುಂಟುಮಾಡುವನು. ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಮಾತಿಗೆ ವಿಧೇಯರಾಗಿ ಈ ಧರ್ಮಶಾಸ್ತ್ರದ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ಅನುಸರಿಸಿ ಸಂಪೂರ್ಣವಾದ ಹೃದಯದಿಂದಲೂ ಮನಸ್ಸಿನಿಂದಲೂ ಆತನ ಕಡೆಗೆ ತಿರುಗಿಕೊಳ್ಳಲು ಆತನು ನಿಮ್ಮ ಪಿತೃಗಳ ವಿಷಯದಲ್ಲಿ ಸಂತೋಷಪಟ್ಟ ಹಾಗೆಯೇ ನಿಮ್ಮ ವಿಷಯದಲ್ಲಿಯೂ ತಿರಿಗಿ ಸಂತೋಷಪಟ್ಟು ನಿಮಗೆ ಮೇಲನ್ನುಂಟುಮಾಡುವನು.",
			"11": "<sup>11</sup> ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸಿದ ಧರ್ಮೋಪದೇಶವು ನಿಮಗೆ ಗ್ರಹಿಸುವದಕ್ಕೆ ಕಷ್ಟವಾದದ್ದೂ ಅಲ್ಲ, ಹೊಂದುವದಕ್ಕೆ ಅಸಾಧ್ಯವಾದದ್ದೂ ಅಲ್ಲ.",
			"12": "<sup>12</sup> ಇದು ಮೇಲಣ ಲೋಕದಲ್ಲಿರುವ ಮಾತಲ್ಲ. ನಮಗೋಸ್ಕರ ಮೇಲಣ ಲೋಕವನ್ನು ಏರಿ ಅದನ್ನು ತಿಳಿದುಕೊಂಡುಬಂದು ನಮಗೆ ತಿಳಿಸುವಷ್ಟು ಶಕ್ತರು ಯಾರಿದ್ದಾರೆ? ಇದ್ದರೆ ನಾವು ಕೇಳಿ ಕೈಕೊಂಡೇವು ಅಂದುಕೊಳ್ಳುವದಕ್ಕೆ ಅವಕಾಶವೇನೂ ಇಲ್ಲ.",
			"13": "<sup>13</sup> ಮತ್ತು ಇದು ಸಮುದ್ರದ ಆಚೆ ಇರುವ ಮಾತಲ್ಲ. ನಮಗೋಸ್ಕರ ಸಮುದ್ರವನ್ನು ದಾಟಿ ಅದನ್ನು ತೆಗೆದುಕೊಂಡು ಬಂದು ನಮಗೆ ತಿಳಿಸುವಷ್ಟು ಶಕ್ತರು ಯಾರಿದ್ದಾರೆ? ಇದ್ದರೆ ನಾವು ಕೈಕೊಂಡೇವು ಅಂದುಕೊಳ್ಳುವದಕ್ಕೆ ಆಸ್ಪದವೇನೂ ಇಲ್ಲ.",
			"14": "<sup>14</sup> ಈ ವಾಕ್ಯವು ನಿಮಗೆ ಸಮೀಪದಲ್ಲಿಯೇ ಇದೆ; ಇದು ನಿಮ್ಮ ಬಾಯಲ್ಲಿಯೂ ಹೃದಯದಲ್ಲಿಯೂ ಇದೆ; ಇದನ್ನು ಅನುಸರಿಸುವದಕ್ಕೆ ಅವಕಾಶವುಂಟಷ್ಟೆ.",
			"15": "<sup>15</sup> ನೋಡಿರಿ; ನಾನು ಜೀವಶುಭಗಳನ್ನೂ ಮರಣಾಶುಭಗಳನ್ನೂ ಈಗ ನಿಮ್ಮ ಮುಂದೆ ಇಟ್ಟಿದ್ದೇನೆ.",
			"16": "<sup>16</sup> ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ಪ್ರಕಾರ ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಪ್ರೀತಿಸಿ ಆತನು ಹೇಳಿದ ಮಾರ್ಗದಲ್ಲಿ ನಡೆದು ಆತನ ಆಜ್ಞಾವಿಧಿ ನಿರ್ಣಯಗಳನ್ನು ಅನುಸರಿಸಿದರೆ ನೀವು ಬದುಕಿಕೊಂಡು ಹೆಚ್ಚುವಿರಿ; ಮತ್ತು ನೀವು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವದಕ್ಕೆ ಹೋಗುವ ದೇಶದಲ್ಲಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಅಭಿವೃದ್ಧಿಪಡಿಸುವನು.",
			"17": "<sup>17</sup> ಆದರೆ ನೀವು ಆತನನ್ನು ಬಿಟ್ಟು ಆತನ ಮಾತನ್ನು ಕೇಳಲೊಲ್ಲದೆ ಮರುಳುಗೊಂಡವರಾಗಿ ಇತರ ದೇವರುಗಳನ್ನು ಪೂಜಿಸಿ ಸೇವಿಸಿದರೆ",
			"18": "<sup>18</sup> ನೀವು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವದಕ್ಕೆ ಯೊರ್ದನ್ ಹೊಳೆಯನ್ನು ದಾಟಿಹೋಗುವ ದೇಶದಲ್ಲಿ ಬಹುಕಾಲ ಇರದೆ ನಾಶವಾಗಿಯೇ ಹೋಗುವಿರೆಂದು ನಾನು ಈಗ ನಿಮಗೆ ಖಂಡಿತವಾಗಿ ತಿಳಿಸುತ್ತೇನೆ.",
			"19": "<sup>19</sup> ನಾನು ಜೀವಮರಣಗಳನ್ನೂ ಆಶೀರ್ವಾದಶಾಪಗಳನ್ನೂ ಈಗ ನಿಮ್ಮ ಮುಂದೆ ಇಟ್ಟಿದ್ದೇನೆ; ಇದಕ್ಕೆ ಭೂಮ್ಯಾಕಾಶಗಳು ಸಾಕ್ಷಿಗಳಾಗಿರಲಿ. ಆದದರಿಂದ ನೀವೂ ನಿಮ್ಮ ಸಂತತಿಯವರೂ ಬದುಕಿಕೊಳ್ಳುವಂತೆ ಜೀವವನ್ನೇ ಆದುಕೊಳ್ಳಿರಿ;",
			"20": "<sup>20</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಪ್ರೀತಿಸಿ ಆತನ ಮಾತಿಗೆ ವಿಧೇಯರಾಗಿರ್ರಿ, ಆತನನ್ನು ಹೊಂದಿಕೊಂಡೇ ಇರ್ರಿ. ಯೆಹೋವನು ನಿಮ್ಮ ಪಿತೃಗಳಾದ ಅಬ್ರಹಾಮ್ ಇಸಾಕ್ ಯಾಕೋಬರಿಗೆ ಪ್ರಮಾಣ ಮಾಡಿಕೊಟ್ಟ ದೇಶದಲ್ಲಿ ನೀವು ಬದುಕಿಕೊಳ್ಳುವದಕ್ಕೂ ಬಹುಕಾಲ ಇರುವದಕ್ಕೂ ಆತನೇ ಆಧಾರ."
		},
		"31": {
			"1": "<sup>1</sup> ಮೋಶೆ ಹೋಗಿ ಈ ಮಾತುಗಳನ್ನು ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರಿಗೆ ತಿಳಿಸಿದನು.",
			"2": "<sup>2</sup> ಅವನು ಅವರಿಗೆ - ನಾನು ಈಗ ನೂರಿಪ್ಪತ್ತು ವರುಷದವನಾಗಿದ್ದೇನೆ; ವಿಶೇಷವಾದ ಕಾರ್ಯಗಳನ್ನು ನಿರ್ವಹಿಸುವದು ಇನ್ನು ನನ್ನಿಂದಾಗದು; ಯೆಹೋವನು ನನಗೆ - ನೀನು ಈ ಯೊರ್ದನ್ ಹೊಳೆಯನ್ನು ದಾಟಕೂಡದೆಂದು ಆಜ್ಞಾಪಿಸಿದ್ದಾನೆ.",
			"3": "<sup>3</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ತಾನೇ ನಿಮ್ಮ ಸೇನಾನಾಯಕನಾಗಿ ಹೊಳೆಯನ್ನು ದಾಟಿಹೋಗುವನು; ಆತನು ಆ ಜನಾಂಗಗಳನ್ನು ನಿಮ್ಮ ಎದುರಿನಲ್ಲಿ ನಿಲ್ಲದಂತೆ ನಾಶಮಾಡುವದರಿಂದ ನೀವು ಅವರ ದೇಶವನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವಿರಿ. ಯೆಹೋವನು ಆಜ್ಞಾಪಿಸಿದಂತೆ ಯೆಹೋಶುವನು ನಿಮ್ಮ ನಾಯಕನಾಗಿ ಹೊಳೆಯನ್ನು ದಾಟಿಹೋಗುವನು.",
			"4": "<sup>4</sup> ಯೆಹೋವನು ಅಮೋರಿಯರ ಅರಸನಾದ ಸೀಹೋನ್ ಓಗರನ್ನು ನಾಶಮಾಡಿ ಅವರ ದೇಶವನ್ನು ನಿಮಗೆ ಸ್ವಾಧೀನಪಡಿಸಿದಂತೆ ಆ ಜನಾಂಗಗಳನ್ನೂ ನಾಶಮಾಡಿ ಅವರ ದೇಶವನ್ನು ನಿಮಗೆ ವಶಪಡಿಸುವನು.",
			"5": "<sup>5</sup> ಆತನು ಅವರನ್ನು ನಿಮ್ಮಿಂದ ಪರಾಜಯಪಡಿಸಿದಾಗ ನೀವು ಅವರ ವಿಷಯದಲ್ಲಿ ನನ್ನ ಆಜ್ಞೆಗಳನ್ನೆಲ್ಲಾ ಅನುಸರಿಸಿ ನಡೆದುಕೊಳ್ಳಬೇಕು.",
			"6": "<sup>6</sup> ನೀವು ಶೂರರಾಗಿ ಧೈರ್ಯದಿಂದಿರ್ರಿ; ಅವರಿಗೆ ಅಂಜಬೇಡಿರಿ, ಕಳವಳಪಡಬೇಡಿರಿ. ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನೇ ನಿಮ್ಮ ಸಂಗಡ ಇರುತ್ತಾನಲ್ಲಾ; ಆತನು ನಿಮ್ಮನ್ನು ಕೈಬಿಡುವದಿಲ್ಲ, ಅಪಜಯಕ್ಕೆ ಗುರಿಪಡಿಸುವದೇ ಇಲ್ಲ ಎಂದು ಹೇಳಿದನು.",
			"7": "<sup>7</sup> ಆಗ ಮೋಶೆ ಯೆಹೋಶುವನನ್ನು ಕರೆದು ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರ ಮುಂದೆ ಅವನಿಗೆ - ಯೆಹೋವನು ಇವರ ಪಿತೃಗಳಿಗೆ ಪ್ರಮಾಣ ಮಾಡಿಕೊಟ್ಟ ದೇಶಕ್ಕೆ ನೀನೇ ಇವರೊಡನೆ ಹೋಗಿ ಅದನ್ನು ಅವರಿಗೆ ಸ್ವಾಧೀನಪಡಿಸಬೇಕು. ಆದದರಿಂದ ಶೂರನಾಗಿ ಧೈರ್ಯದಿಂದಿರು;",
			"8": "<sup>8</sup> ಯೆಹೋವನು ತಾನೇ ನಿನ್ನ ಮುಂದುಗಡೆ ಹೋಗುವನು; ಆತನೇ ನಿನ್ನ ಸಂಗಡ ಇರುವನು; ನಿನ್ನನ್ನು ಅಪಜಯಕ್ಕೆ ಗುರಿಪಡಿಸುವದೇ ಇಲ್ಲ, ಕೈಬಿಡುವದಿಲ್ಲ; ಅಂಜಬೇಡ, ಧೈರ್ಯದಿಂದಿರು ಎಂದು ಹೇಳಿದನು.",
			"9": "<sup>9</sup> ಮೋಶೆ ಈ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಬರೆದು ಯೆಹೋವನ ಆಜ್ಞಾಶಾಸನಗಳ ಮಂಜೂಷವನ್ನು ಹೊರುವ ಲೇವಿಯರಾದ ಯಾಜಕರ ಮತ್ತು ಹಿರಿಯರ ವಶಕ್ಕೆ ಕೊಟ್ಟು ಅವರಿಗೆ -",
			"10": "<sup>10</sup> ಪ್ರತಿ ಏಳು ವರುಷಗಳು ಪೂರ್ತಿಯಾದಾಗ ಬಿಡುಗಡೆಯ ಸಂವತ್ಸರದ ನೇಮಕ ಕಾಲದಲ್ಲಿ ಅಂದರೆ ಪರ್ಣಶಾಲೆಗಳ ಜಾತ್ರೆಯಲ್ಲಿ",
			"11": "<sup>11</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಆದುಕೊಂಡ ಸ್ಥಳದಲ್ಲಿ ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರು ಆತನ ಸನ್ನಿಧಿಗೆ ಕೂಡಿ ಬಂದಾಗ ನೀವು ಅವರೆಲ್ಲರಿಗೂ ಕೇಳಿಸುವಂತೆ ಈ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಅವರ ಮುಂದೆ ಓದಬೇಕು.",
			"12": "<sup>12</sup> ಜನರೆಲ್ಲರೂ ಈ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಕೇಳಿ ತಿಳಿದುಕೊಳ್ಳುವಂತೆ ಸ್ತ್ರೀಪುರುಷರನ್ನೂ ಮಕ್ಕಳನ್ನೂ ನಿಮ್ಮ ಊರುಗಳಲ್ಲಿರುವ ಅನ್ಯರನ್ನೂ ಕೂಡಿಸಬೇಕು. ಆಗ ಅವರು ತಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಲ್ಲಿ ಭಯ ಭಕ್ತಿಯುಳ್ಳವರಾಗಿ ಈ ಧರ್ಮಶಾಸ್ತ್ರದ ನಿಯಮಗಳನ್ನು ಅನುಸರಿಸಿ ನಡೆಯುವರು.",
			"13": "<sup>13</sup> ಮತ್ತು ಯೆಹೋವನ ಮಹತ್ಕಾರ್ಯಗಳನ್ನು ನೋಡದಿರುವ ನಿಮ್ಮ ಸಂತತಿಯವರೂ ಕೇಳಿ ನೀವು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳವದಕ್ಕೆ ಯೊರ್ದನ್ ಹೊಳೆಯನ್ನು ದಾಟಿಹೋಗುವ ದೇಶದಲ್ಲಿ ವಾಸವಾಗಿರುವವರೆಗೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿರುವದಕ್ಕೆ ಕಲಿತುಕೊಳ್ಳುವರು ಎಂದು ಹೇಳಿದನು.",
			"14": "<sup>14</sup> ಯೆಹೋವನು ಮೋಶೆಗೆ - ನೀನು ಸಾಯಬೇಕಾದ ಕಾಲ ಸಮೀಪವಾಯಿತು; ಆದಕಾರಣ ನೀನು ಯೆಹೋಶುವನನ್ನು ಕರೆದುಕೊಂಡು ಬಂದು ದೇವದರ್ಶನದ ಗುಡಾರದಲ್ಲಿ ನನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನಿಲ್ಲಬೇಕು; ಆಗ ನಾನು ಅವನಿಗೆ ಅಧಿಕಾರವನ್ನು ಕೊಡುವೆನು ಎಂದು ಆಜ್ಞಾಪಿಸಲಾಗಿ ಮೋಶೆಯೂ ಯೆಹೋಶುವನೂ ದೇವದರ್ಶನದ ಗುಡಾರದಲ್ಲಿ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನಿಂತುಕೊಂಡರು.",
			"15": "<sup>15</sup> ಅಲ್ಲಿ ಯೆಹೋವನು ಮೇಘಸ್ತಂಭದಲ್ಲಿ ಕಾಣಿಸಿದನು; ಆ ಮೇಘಸ್ತಂಭವು ಗುಡಾರದ ಬಾಗಲಿನ ಬಳಿಯಲ್ಲಿ ನಿಂತಿತು.",
			"16": "<sup>16</sup> ಯೆಹೋವನು ಮೋಶೆಗೆ - ನೀನು ಪಿತೃಗಳಲ್ಲಿ ಸೇರಿದ ಮೇಲೆ ಈ ಜನರು ನನ್ನನ್ನು ಬಿಟ್ಟು ನಾನು ಅವರೊಡನೆ ಮಾಡಿಕೊಂಡ ಒಡಂಬಡಿಕೆಯನ್ನು ಮೀರಿ ದೇವದ್ರೋಹಿಗಳಾಗಿ ತಾವು ಹೋಗುವ ದೇಶದಲ್ಲಿರುವ ಅನ್ಯದೇವತೆಗಳನ್ನು ಪೂಜಿಸುವರು.",
			"17": "<sup>17</sup> ಆಗ ನಾನು ಅವರ ಮೇಲೆ ಬಲುಸಿಟ್ಟಾಗಿ ಅವರನ್ನು ಬಿಟ್ಟು ಅವರಿಗೆ ವಿಮುಖನಾಗಿರುವೆನು; ಆದದರಿಂದ ಅವರು ಸಂಹಾರಕ್ಕೆ ಗುರಿಯಾಗುವರು; ಮತ್ತು ಅನೇಕ ಕಷ್ಟಗಳೂ ವಿಪತ್ತುಗಳೂ ಅವರಿಗೆ ಒದಗುವವು. ಆಗ ಅವರು - ನಮ್ಮ ದೇವರು ನಮ್ಮ ಮಧ್ಯದಲ್ಲಿ ಇಲ್ಲದೆಹೋದದರಿಂದಲೇ ಈ ವಿಪತ್ತುಗಳೆಲ್ಲಾ ನಮಗೆ ಸಂಭವಿಸಿದವಲ್ಲಾ ಅಂದುಕೊಳ್ಳುವರು.",
			"18": "<sup>18</sup> ಅವರು ನನ್ನನ್ನು ಬಿಟ್ಟು ಇತರ ದೇವರುಗಳನ್ನು ಆಶ್ರಯಿಸಿ ಬಹಳ ದುಷ್ಕಾರ್ಯಗಳನ್ನು ನಡಿಸುವದರಿಂದ ನಾನು ಆ ಕಾಲದಲ್ಲಿ ಖಂಡಿತವಾಗಿ ಅವರಿಗೆ ವಿಮುಖನಾಗಿರುವೆನು.",
			"19": "<sup>19</sup> ಆದಕಾರಣ ನೀನು ಈ ಪದ್ಯವನ್ನು ಬರೆದುಕೊಂಡು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಕಲಿಸಿಕೊಡಬೇಕು. ಈ ಪದ್ಯವು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ವಿರೋಧವಾಗಿ ನನ್ನ ಪಕ್ಷದಲ್ಲಿ ಸಾಕ್ಷಿಯಾಗಿರುವಂತೆ ಇದನ್ನು ಅವರಿಗೆ ಬಾಯಿ ಪಾಠಮಾಡಿಸು.",
			"20": "<sup>20</sup> ನಾನು ಪ್ರಮಾಣಮಾಡಿಕೊಟ್ಟ ದೇಶದಲ್ಲಿ ಇವರು ಸೇರುವದಕ್ಕಿಂತ ಮುಂಚಿತವಾಗಿಯೇ ಇವರು ಈಗಲೂ ಮಾಡುವ ದುರಾಲೋಚನೆಗಳನ್ನು ನಾನು ಬಲ್ಲೆ. ನಾನು ಇವರ ಪಿತೃಗಳಿಗೆ ಪ್ರಮಾಣಮಾಡಿದಂತೆ ಹಾಲೂ ಜೇನೂ ಹರಿಯುವ ದೇಶಕ್ಕೆ ಇವರನ್ನು ಸೇರಿಸಿದ ಮೇಲೆ ಇವರು ಚೆನ್ನಾಗಿ ಉಂಡು ಕೊಬ್ಬಿದವರಾದಾಗ ಇತರ ದೇವರುಗಳನ್ನು ಅವಲಂಬಿಸಿ ಸೇವಿಸಿ ನನ್ನನ್ನು ತಾತ್ಸಾರಮಾಡಿ ನನ್ನ ನಿಬಂಧನೆಯನ್ನು ಮೀರುವರು. ಅನಂತರ ಅನೇಕ ಕಷ್ಟಗಳೂ ವಿಪತ್ತುಗಳೂ ಸಂಭವಿಸಿದಾಗ ಈ ಪದ್ಯವು ಇವರ ಸಂತತಿಯವರ ಬಾಯಲ್ಲಿ ಇದ್ದುಕೊಂಡು ಅವರ ಮುಂದೆ ಸಾಕ್ಷಿಕೊಡುವದು ಎಂದು ಹೇಳಿದನು.",
			"22": "<sup>22</sup> ಆದಕಾರಣ ಮೋಶೆ ಆ ದಿನದಲ್ಲೇ ಈ ಪದ್ಯವನ್ನು ಬರೆದು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಕಲಿಸಿದನು.",
			"23": "<sup>23</sup> ಆಗ ಯೆಹೋವನು ನೂನನ ಮಗನಾದ ಯೆಹೋಶುವನಿಗೆ - ನಾನು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಪ್ರಮಾಣಮಾಡಿಕೊಟ್ಟ ದೇಶದಲ್ಲಿ ನೀನೇ ಅವರನ್ನು ಸೇರಿಸಬೇಕು; ಆದದರಿಂದ ಶೂರನಾಗಿ ಧೈರ್ಯದಿಂದಿರು; ನಾನೇ ನಿನ್ನ ಸಂಗಡ ಇರುವೆನು ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"24": "<sup>24</sup> ಮೋಶೆ ಈ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಗ್ರಂಥರೂಪವಾಗಿ ಬರೆದು ಪೂರೈಸಿದಾಗ",
			"25": "<sup>25</sup> ಯೆಹೋವನ ಆಜ್ಞಾಶಾಸನಗಳ ಮಂಜೂಷವನ್ನು ಹೊರುವ ಲೇವಿಯರಿಗೆ -",
			"26": "<sup>26</sup> ನೀವು ಈ ಧರ್ಮಶಾಸ್ತ್ರಪುಸ್ತಕವನ್ನು ನಿಮ್ಮ ವಶಕ್ಕೆ ತೆಗೆದುಕೊಂಡು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಆಜ್ಞಾಶಾಸನಗಳ ಮಂಜೂಷದ ಬಳಿಯಲ್ಲಿ ಇಡಿರಿ. [ಇಸ್ರಾಯೇಲ್ಯರೇ,] ಅಲ್ಲಿ ಇದು ನಿಮಗೆ ವಿರೋಧವಾಗಿ ಸಾಕ್ಷಿಕೊಡುವದಾಗಿರಲಿ.",
			"27": "<sup>27</sup> ಯಾಕಂದರೆ ನೀವು ಹಟಹಿಡಿದು ಆಜ್ಞೆಗೆ ಒಳಗಾಗದವರೆಂಬದು ನನಗೆ ತಿಳಿದೇ ಇದೆ. ನಾನು ಇನ್ನೂ ಜೀವದಿಂದಿರುವಾಗಲೇ ನೀವು ಯೆಹೋವನಿಗೆ ವಿರೋಧವಾಗಿ ತಿರುಗಿ ಬಿದ್ದಿದ್ದೀರಿ; ನಾನು ಹೋದ ತರುವಾಯ ನೀವು ತಿರುಗಿ ಬೀಳುವದು ಮತ್ತಷ್ಟು ನಿಶ್ಚಯ.",
			"28": "<sup>28</sup> ನಿಮ್ಮ ಕುಲಗಳ ಹಿರಿಯರನ್ನೂ ಅಧಿಪತಿಗಳನ್ನೂ ನನ್ನ ಬಳಿಗೆ ಕೂಡಿಸಿರಿ; ನಾನು ಈ ಮಾತುಗಳನ್ನು ಅವರಿಗೆ ತಿಳಿಸಿ ಅವರಿಗೆ ವಿರೋಧವಾಗಿ ಸಾಕ್ಷಿಗಳಾಗುವದಕ್ಕೆ ಭೂಮ್ಯಾಕಾಶಗಳನ್ನು ಕರೆಯುವೆನು.",
			"29": "<sup>29</sup> ನಾನು ಹೋದ ಮೇಲೆ ನೀವು ದ್ರೋಹಿಗಳಾಗಿ ನಾನು ಬೋಧಿಸಿದ ಮಾರ್ಗವನ್ನು ತಪ್ಪಿಹೋಗುವಿರೆಂದೂ ಅನಂತರದಲ್ಲಿ ನೀವು ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ಕೆಟ್ಟದ್ದನ್ನು ನಡೆಸಿ ದುರಾಚಾರಿಗಳಾಗಿ ಆತನನ್ನು ಕೋಪಗೊಳಿಸುವದರಿಂದ ನಿಮಗೆ ಆಪತ್ತುಗಳು ಉಂಟಾಗುವವೆಂದೂ ನಾನು ಬಲ್ಲೆ ಎಂದು ಹೇಳಿದನು.",
			"30": "<sup>30</sup> ಆಗ ಮೋಶೆ ಇಸ್ರಾಯೇಲ್ಯರ ಸರ್ವಸಮೂಹದವರಿಗೆ ಕೇಳಿಸುವಂತೆ ಈ ಪದ್ಯದ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ಹೇಳಿದನು; ಏನಂದರೆ -"
		},
		"32": {
			"1": "<sup>1</sup> ಆಕಾಶಮಂಡಲವೇ, ನಾನು ಹೇಳುವದನ್ನು ಕೇಳು: ಭೂಮಂಡಲವೇ, ನನ್ನ ಮಾತುಗಳಿಗೆ ಕಿವಿಕೊಡು.",
			"2": "<sup>2</sup> ನನ್ನ ಉಪದೇಶವು ಹಸಿಹುಲ್ಲಿನ ಮೇಲೆ ಮೆಲ್ಲಗೆ ಸುರಿಯುವ ಮಳೆಯ ತುಂತುರುಗಳಂತೆ [ತಣ್ಣಗಿರುವದು;] ನನ್ನ ಬೋಧನೆಯು ಮಂಜಿನಂತೆಯೂ ಕಾಯಿಪಲ್ಯಗಳ ಮೇಲೆ ಬೀಳುವ ಹದಮಳೆಯಂತೆಯೂ [ಹಿತವಾಗಿರುವದು].",
			"3": "<sup>3</sup> ಯೆಹೋವನ ನಾಮಮಹತ್ವವನ್ನು ಪ್ರಕಟಿಸುವೆನು; ನಮ್ಮ ದೇವರನ್ನು ಮಹಾಮಹಿಮೆಯುಳ್ಳವನೆಂದು ಕೊಂಡಾಡಿರಿ.",
			"4": "<sup>4</sup> ನಮಗೆ ಶರಣನಾದ ದೇವರು ಮಾಡುವ ಕಾರ್ಯದಲ್ಲಿ ಯಾವ ಕುಂದೂ ಇಲ್ಲ; ಆತನು ನಡಿಸುವದೆಲ್ಲಾ ನ್ಯಾಯ; ಆತನು ನಿರ್ವಂಚಕನಾದ ನಂಬಿಗಸ್ತ ದೇವರು, ನೀತಿಯುಳ್ಳವನೂ ಯಥಾರ್ಥನೂ ಆಗಿದ್ದಾನೆ.",
			"5": "<sup>5</sup> ಆದರೆ ಅವರು ದ್ರೋಹಿಗಳೇ, ಮಕ್ಕಳಲ್ಲ; ಇದು ಅವರ ದೋಷವು; ಅವರು ವಕ್ರಬುದ್ಧಿಯುಳ್ಳ ಮೂರ್ಖಜಾತಿಯವರು.",
			"6": "<sup>6</sup> ದುರಾಚಾರಿಗಳಿರಾ, ಅವಿವೇಕಿಗಳಿರಾ, ಯೆಹೋವನ ವಿಷಯದಲ್ಲಿ ಈ ರೀತಿಯಾಗಿ ನಡೆಯಬಹುದೇ? ಆತನು ನಿಮ್ಮನ್ನು ಸೃಷ್ಟಿಸಿದ ತಂದೆಯಲ್ಲವೇ; ನಿಮ್ಮನ್ನು [ಜನಾಂಗವನ್ನಾಗಿ] ಮಾಡಿ ಸ್ಥಾಪಿಸಿದನಲ್ಲವೇ.",
			"7": "<sup>7</sup> ಪೂರ್ವಕಾಲವನ್ನು ನೆನಪಿಗೆ ತಂದುಕೊಳ್ಳಿರಿ; ಪೂರ್ವಿಕರ ಚರಿತ್ರೆಯನ್ನು ಆಲೋಚಿಸಿರಿ. ನಿಮ್ಮ ನಿಮ್ಮ ತಂದೆಗಳನ್ನು ವಿಚಾರಿಸಿದರೆ ಅವರು ತಿಳಿಸುವರು; ಹಿರಿಯರನ್ನು ಕೇಳಿದರೆ ಅವರು ವಿವರಿಸುವರು; ಹೇಗಂದರೆ -",
			"8": "<sup>8</sup> ಪರಾತ್ಪರನಾದ ದೇವರು ಜನಾಂಗಗಳನ್ನು ಬೇರೆ ಬೇರೆ ಮಾಡಿ ಅವರವರಿಗೆ ಸ್ವದೇಶಗಳನ್ನು ನೇಮಿಸಿಕೊಟ್ಟಾಗ ಇಸ್ರಾಯೇಲ್ಯರ ಸಂಖ್ಯೆಗೆ ತಕ್ಕಂತೆ ಆಯಾ ಜನಾಂಗಕ್ಕೆ ಒಂದೊಂದು ಪ್ರದೇಶವನ್ನು ಗೊತ್ತು ಮಾಡಿದ್ದೂ;",
			"9": "<sup>9</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಮಾತ್ರ ಯೆಹೋವನ ಸ್ವಂತ ಜನರಾದದ್ದೂ; ಯಾಕೋಬ್ ವಂಶಸ್ಥರು ಆತನಿಗೆ ಸ್ವಕೀಯ ಪ್ರಜೆಯಾದದ್ದೂ;",
			"10": "<sup>10</sup> ಆತನು ಅವರನ್ನು ಶೂನ್ಯವೂ ಭಯಂಕರವೂ ಆಗಿರುವ ಮರಳುಕಾಡಿನಲ್ಲಿ ಕಂಡು ಪರಾಮರಿಸಿ ಪ್ರೀತಿಯಿಂದ ಆವರಿಸಿಕೊಂಡು ಕಣ್ಣುಗುಡ್ಡಿನಂತೆ ಕಾಪಾಡಿದ್ದೂ;",
			"11": "<sup>11</sup> ಹದ್ದು ತನ್ನ ಮರಿಗಳನ್ನು ಗೂಡಿನೊಳಗಿಂದ ಹೊರಡಿಸಿ ಅವುಗಳ ಬಳಿಯಲ್ಲಿ ಹಾರಾಡುವಂತೆ ಯೆಹೋವನು ತನ್ನ ರೆಕ್ಕೆಗಳನ್ನು ಚಾಚಿ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಆತುಕೊಂಡದ್ದೂ;",
			"12": "<sup>12</sup> ಯಾವ ಅನ್ಯದೇವರಾದರೂ ಇಲ್ಲದೆ ಯೆಹೋವನೊಬ್ಬನೇ ಅವರನ್ನು ನಡಿಸಿಕೊಂಡು ಬಂದದ್ದೂ;",
			"13": "<sup>13</sup> ಆತನು ಅವರನ್ನು ಭೂಮಿಯ ಎತ್ತರವಾದ ಪ್ರದೇಶಗಳ ಮೇಲೆ ಹತ್ತಿಸಿ ಅವರಿಗೆ ವ್ಯವಸಾಯವೃದ್ಧಿಯನ್ನುಂಟುಮಾಡಿ ಬಂಡೆಯಿಂದ ಜೇನೂ ಗಿರಿಯಿಂದ ಎಣ್ಣೆಯೂ ದೊರೆಯುವಂತೆ ಮಾಡಿದ್ದೂ;",
			"14": "<sup>14</sup> ಆಹಾರಕ್ಕಾಗಿ ಆಕಳಿನ ಮೊಸರು, ಆಡುಕುರಿಗಳ ಹಾಲು, ಕೊಬ್ಬಿದ ಕುರಿಟಗರುಗಳ ಮಾಂಸವು, ಬಾಷಾನ್ ಸೀಮೆಯ ಉತ್ತಮಪಶುಗಳು, ಹೋತಗಳು, ಶ್ರೇಷ್ಠವಾದ ಗೋದಿ ಇವುಗಳನ್ನೂ ಪಾನಕ್ಕಾಗಿ ದ್ರಾಕ್ಷೆಯ ರಕ್ತರಸವನ್ನೂ ಕೊಟ್ಟದ್ದೂ; ಇದನ್ನೆಲ್ಲಾ ನೆನಪಿಗೆ ತಂದುಕೊಳ್ಳಿರಿ.",
			"15": "<sup>15</sup> ಆದರೆ ಯೆಶುರೂನು ಚೆನ್ನಾಗಿ ತಿಂದು ಕೊಬ್ಬಿ ಅಗಡಾಗಿ ತನ್ನನ್ನು ಸೃಷ್ಟಿಸಿದ ದೇವರನ್ನು ಬಿಟ್ಟು ತನ್ನ ಆಶ್ರಯದುರ್ಗವನ್ನು ತಿರಸ್ಕರಿಸಿತು.",
			"16": "<sup>16</sup> ಅವರು ಅನ್ಯದೇವರುಗಳನ್ನು ಪೂಜಿಸಿ ಆತನನ್ನು ರೇಗಿಸಿದರು; ನಿಷಿದ್ಧಾಚಾರಗಳನ್ನು ನಡಿಸಿ ಆತನಿಗೆ ಸಿಟ್ಟೇರಿಸಿದರು.",
			"17": "<sup>17</sup> ದೇವರೇ ಅಲ್ಲದ ಕ್ಷುದ್ರದೇವತೆಗಳಿಗೂ ಪಿತೃಗಳು ಭಜಿಸದೆಯೂ ತಮಗೆ ಮುಂಚಿತವಾಗಿ ಗೊತ್ತಿಲ್ಲದೆಯೂ ಇರುವ ನೂತನ ದೇವತೆಗಳಿಗೂ ಬಲಿಯನ್ನರ್ಪಿಸಿದರು.",
			"18": "<sup>18</sup> [ಇಸ್ರಾಯೇಲ್ಯರೇ,] ನಿಮ್ಮನ್ನು ಹುಟ್ಟಿಸಿದ ತಂದೆಯಂತಿರುವ ಶರಣನನ್ನು ನೀವು ನೆನಸಲಿಲ್ಲ; ಹೆತ್ತ ತಾಯಿಯಂತಿರುವ ದೇವರನ್ನು ಮರೆತುಬಿಟ್ಟಿರಿ.",
			"19": "<sup>19</sup> ಯೆಹೋವನು ಇದನ್ನು ಕಂಡು ಅವರ ವಿಷಯದಲ್ಲಿ ಬೇಸರಗೊಂಡನು; ತನ್ನ ಕುಮಾರ ಕುಮಾರ್ತೆಗಳ ವಿಷಯದಲ್ಲಿ ವ್ಯಥೆಪಟ್ಟನು.",
			"20": "<sup>20</sup> ಆತನು ಅವರ ವಿಷಯದಲ್ಲಿ ಹೀಗೆ ಅಂದುಕೊಂಡನು - ನಾನು ಅವರಿಗೆ ವಿಮುಖನಾಗಿ ಅವರಿಗೆ ಪ್ರಾಪ್ತವಾಗುವ ಗತಿಯನ್ನು ನೋಡುವೆನು. ಅವರು ಸತ್ಯವನ್ನು ತಿಳಿದೂ ಮಾಡದವರೂ ದ್ರೋಹಿಗಳಾದ ಮಕ್ಕಳೂ ಆಗಿದ್ದಾರೆ.",
			"21": "<sup>21</sup> ಅವರು ದೇವರಲ್ಲದವುಗಳ ಮೂಲಕ ನನ್ನನ್ನು ರೇಗಿಸಿದ್ದರಿಂದ ನಾನು ಜನಾಂಗವಲ್ಲದವರ ಮೂಲಕ ಅವರಲ್ಲಿ ಹುರುಡು ಹುಟ್ಟಿಸುವೆನು. ಅವರು ಅಚೇತನ ವಿಗ್ರಹಗಳ ಮೂಲಕ ನನ್ನನ್ನು ಸಿಟ್ಟಿಗೆಬ್ಬಿಸಿದ್ದರಿಂದ ನಾನು ಸದಾಚಾರವಿಲ್ಲದ ಜನರ ಮೂಲಕ ಅವರನ್ನು ಸಿಟ್ಟಿಗೆಬ್ಬಿಸುವೆನು.",
			"22": "<sup>22</sup> ನನ್ನ ಕೋಪಾಗ್ನಿ ಪ್ರಜ್ವಲಿಸುತ್ತದೆ; ಅದು ಪಾತಾಳದವರೆಗೂ ವ್ಯಾಪಿಸಿ ಬೆಳೆ ಸಹಿತವಾಗಿ ಭೂಮಿಯನ್ನೂ ಬುಡಸಹಿತವಾಗಿ ಬೆಟ್ಟಗಳನ್ನೂ ದಹಿಸಿಬಿಡುವದು.",
			"23": "<sup>23</sup> ಅವರಿಗೆ ಒಂದರ ಮೇಲೊಂದಾಗಿ ವಿಪತ್ತುಗಳನ್ನು ಬರಮಾಡುವೆನು; ನನ್ನ ಎಲ್ಲಾ ಬಾಣಗಳನ್ನೂ ಅವರಿಗೆ ವಿರೋಧವಾಗಿ ಪ್ರಯೋಗಿಸುವೆನು.",
			"24": "<sup>24</sup> ಅವರು ಬರದಿಂದ ಕ್ಷೀಣವಾಗುವರು, ತಾಪದಿಂದಲೂ ಅಂಟುರೋಗದಿಂದಲೂ ಸಾಯುವರು; ಅದಲ್ಲದೆ ನಾನು ದುಷ್ಟಮೃಗಗಳನ್ನೂ ವಿಷಸರ್ಪಗಳನ್ನೂ ಅವರ ಮೇಲೆ ಬರಮಾಡುವೆನು.",
			"25": "<sup>25</sup> ಮನೆಯ ಹೊರಗೆ ಕತ್ತಿಯೂ ಒಳಗೆ ಭಯವೂ ಇರುವವು. ಯೌವನಸ್ಥರು, ಕನ್ಯೆಯರು, ಮೊಲೆಕೂಸುಗಳು, ನರೇತಲೆಯವರು ಇವರೆಲ್ಲರೂ ಕತ್ತಿಯಿಂದ ಕೊಲ್ಲಲ್ಪಡುವರು.",
			"26": "<sup>26</sup> ಅವರನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ನಾಶಮಾಡುತ್ತಿದ್ದೆನು, ಅವರು ಮನುಷ್ಯರ ಜ್ಞಾಪಕದಲ್ಲೂ ಇಲ್ಲದಂತೆ ಮಾಡುತ್ತಿದ್ದೆನು;",
			"27": "<sup>27</sup> ಆದರೆ ಅವರ ವಿರೋಧಿಗಳು ಹಮ್ಮಿನಿಂದ ತಪ್ಪಾದ ಭಾವನೆ ಮಾಡಿಕೊಂಡು - ಇದು ನಮ್ಮ ಭುಜಬಲದಿಂದಲೇ ಹೊರತು ಯೆಹೋವನಿಂದ ಆಗಲಿಲ್ಲ ಅಂದುಕೊಂಡಾರೆಂದು ನಾನು ಹಿಂದೆಗೆದೆನು ಎಂಬದೇ.",
			"28": "<sup>28</sup> ಅವರು ಆಲೋಚನೆಯಿಲ್ಲದ ಜನರು; ಅವರಿಗೆ ಸ್ವಲ್ಪವಾದರೂ ವಿವೇಕವಿಲ್ಲ.",
			"29": "<sup>29</sup> ಅವರಿಗೆ ಜ್ಞಾನವಿದ್ದರೆ ಈ ಸಂಗತಿಗಳನ್ನೆಲ್ಲಾ ಗ್ರಹಿಸುತ್ತಿದ್ದರು; ತಮಗೆ ಅಂತ್ಯದಲ್ಲಿ ದುರವಸ್ಥೆ ಪ್ರಾಪ್ತವಾಗುವದೆಂದು ತಿಳಿದುಕೊಳ್ಳುತ್ತಿದ್ದರು.",
			"30": "<sup>30</sup> ಅವರ ಶರಣನು ಅವರನ್ನು ಶತ್ರುಗಳಿಗೆ ಒಪ್ಪಿಸಿಕೊಡದಿದ್ದರೆ ಒಬ್ಬನಿಂದ ಸಾವಿರ ಮಂದಿ ಸೋತುಹೋಗುತ್ತಿದ್ದರೋ? ಯೆಹೋವನು ಕೈಬಿಡದಿದ್ದರೆ ಇಬ್ಬರಿಗೆ ಹೆದರಿ ಹತ್ತುಸಾವಿರ ಮಂದಿ ಓಡಿಹೋಗುತ್ತಿದ್ದರೋ?",
			"31": "<sup>31</sup> ನಮ್ಮ ಶರಣನಿಗೆ ತಮ್ಮ ಶರಣರು ಸಮಾನರಲ್ಲವೆಂದು ನಮ್ಮ ಶತ್ರುಗಳೇ ತೀರ್ಮಾನಿಸುತ್ತಾರಲ್ಲಾ.",
			"32": "<sup>32</sup> ಅವರು ಸೊದೋಮ್ಯರೆಂಬ ದ್ರಾಕ್ಷಾಲತೆಯ ಒಂದು ಬಳ್ಳಿಯಾಗಿದ್ದಾರೆ; ಅದು ಗೊಮೋರ ಪಟ್ಟಣದ ತೋಟಗಳಲ್ಲಿ ಬೆಳೆಯುವ ಜಾತಿ. ಅದರ ಹಣ್ಣಿನ ಗೊಂಚಲು ವಿಷ;",
			"33": "<sup>33</sup> ಅದರ ರಸ ಸರ್ಪವಿಷದಂತೆ ಮರಣಕರವಾದದ್ದು.",
			"34": "<sup>34</sup> ಇದನ್ನೆಲ್ಲಾ ಮುದ್ರೆಹಾಕಿ ನನ್ನ ಉಗ್ರಾಣದಲ್ಲಿಟ್ಟುಕೊಂಡಿದ್ದೇನೆ.",
			"35": "<sup>35</sup> ಪ್ರತೀಕಾರಮಾಡಿ ಮುಯ್ಯಿಗೆ ಮುಯ್ಯಿ ತೀರಿಸುವದು ನನ್ನ ಕೆಲಸ; ಅವರು ಜಾರಿಬೀಳುವ ಸಮಯ ಬರುವದು. ಅವರಿಗೆ ಆಪತ್ಕಾಲವು ಸಮೀಪಿಸಿತು; ಅವರಿಗೋಸ್ಕರ ಸಿದ್ಧವಾಗಿರುವ ದುರ್ಗತಿ ಬೇಗ ಬರುವದು.",
			"36": "<sup>36</sup> ಆಗ ಯೆಹೋವನು ತನ್ನ ಜನರಿಗೋಸ್ಕರ ನ್ಯಾಯತೀರಿಸುವನು. ಪರತಂತ್ರರಾಗಲಿ ಸ್ವತಂತ್ರರಾಗಲಿ ಅವರೆಲ್ಲರೂ ನಿರಾಶ್ರಯರಾಗಿ ನಿಶ್ಶೇಷರಾದದ್ದನ್ನು ಆತನು ತಿಳಿದಾಗ ತನ್ನ ಸೇವಕರಾದ ಅವರ ವಿಷಯದಲ್ಲಿ ಪಶ್ಚಾತ್ತಾಪಪಡುವನು.",
			"37": "<sup>37</sup> ಯೆಹೋವನು ಅವರ ವಿಷಯದಲ್ಲಿ ಹೇಳುವದೇನಂದರೆ - ಅವರು ಪೂಜಿಸುತ್ತಿದ್ದ ದೇವರುಗಳು ಎಲ್ಲಿ ಹೋದರು? ಅವರು ಆಶ್ರಯಿಸಿಕೊಂಡಿದ್ದ ಶರಣನು ಎಲ್ಲಿದ್ದಾನೆ?",
			"38": "<sup>38</sup> ಅವರ ನೈವೇದ್ಯಗಳ ಕೊಬ್ಬನ್ನು ತಿಂದು ಅವರು ಸಮರ್ಪಿಸಿದ ಪಾನದ್ರವ್ಯಗಳನ್ನು ಕುಡಿದ ದೇವತೆಗಳು ಎಲ್ಲಿ? ಅವರೇ ನಿಮ್ಮ ಸಹಾಯಕ್ಕೆ ಬರಲಿ; ಅವರೇ ನಿಮ್ಮನ್ನು ಕಾಯಲಿ.",
			"39": "<sup>39</sup> ನಾನೇ, ನಾನೊಬ್ಬನೇ ದೇವರಾಗಿರಲಾಗಿ ನನ್ನ ವಿನಾ ಯಾವ ದೇವರೂ ಇಲ್ಲವೆಂದು ಈಗಲಾದರೂ ತಿಳಿದುಕೊಳ್ಳಿರಿ. ಬದುಕಿಸುವವನೂ ಕೊಲ್ಲುವವನೂ ನಾನೇ; ಗಾಯಪಡಿಸುವವನೂ ವಾಸಿಮಾಡುವವನೂ ನಾನೇ; ನನ್ನ ಕೈಯಿಂದ ತಪ್ಪಿಸ ಶಕ್ತನು ಯಾವನೂ ಇಲ್ಲ.",
			"40": "<sup>40</sup> ನಾನು ಆಕಾಶದ ಕಡೆಗೆ ಕೈಯೆತ್ತಿ ನಾನು ಸದಾಕಾಲ ಜೀವಿಸುವವನೆಂಬದು ಎಷ್ಟು ನಿಶ್ಚಯವೋ ಅಷ್ಟು ಖಂಡಿತವಾಗಿ ಪ್ರಮಾಣಮಾಡುವೆನು; ಏನಂದರೆ -",
			"41": "<sup>41</sup> ಥಳಥಳಿಸುವ ನನ್ನ ಕತ್ತಿಯನ್ನು ನಾನು ಹದಮಾಡಿ ನ್ಯಾಯವನ್ನು ಸ್ಥಾಪಿಸುವದಕ್ಕೆ ಮುಂಗೊಂಡಾಗ ನನ್ನ ಶತ್ರುಗಳಿಗೆ ಮುಯ್ಯಿತೀರಿಸುವೆನು, ನನ್ನನ್ನು ದ್ವೇಷಿಸಿದವರಿಗೆ ಪ್ರತಿದಂಡನೆ ಮಾಡುವೆನು;",
			"42": "<sup>42</sup> ನನ್ನ ಬಾಣಗಳು ರಕ್ತವನ್ನು ಕುಡಿಕುಡಿದು ಮತ್ತವಾಗುವವು; ನನ್ನ ಕತ್ತಿಯು ಕೊಲ್ಲಲ್ಪಟ್ಟವರ ಮತ್ತು ಸೆರೆಹಿಡಿದವರ ರಕ್ತಮಾಂಸಗಳನ್ನು ಭಕ್ಷಿಸಿ ಶತ್ರುಗಳಲ್ಲಿರುವ ವೀರರ ತಲೆಗಳನ್ನು ಚಂಡಾಡುವದು ಎಂಬದೇ.",
			"43": "<sup>43</sup> ಜನಾಂಗಗಳಿರಾ, ದೇವರ ಜನರನ್ನು ಹೊಗಳಿರಿ. ತನ್ನ ಸೇವಕರ ರಕ್ತವನ್ನು ಚೆಲ್ಲಿದ ಶತ್ರುಗಳಿಗೆ ಯೆಹೋವನು ಪ್ರತಿದಂಡನೆ ಮಾಡುತ್ತಾನೆ; ಅವರಿಗೆ ಮುಯ್ಯಿತೀರಿಸುತ್ತಾನೆ; ತನ್ನ ಜನರಿಗೋಸ್ಕರವೂ ದೇಶಕ್ಕೋಸ್ಕರವೂ ದೋಷಪರಿಹಾರಮಾಡುತ್ತಾನೆ.",
			"44": "<sup>44</sup> ಮೋಶೆಯೂ ನೂನನ ಮಗನಾದ ಯೆಹೋಶುವನೂ ಈ ಪದ್ಯದ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ಜನರಿಗೆ ಕೇಳಿಸುವಂತೆ ಹೇಳಿದರು.",
			"45": "<sup>45</sup> ಮೋಶೆ ಈ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಹೇಳಿ ಮುಗಿಸಿದನಂತರ ಅವರಿಗೆ -",
			"46": "<sup>46</sup> ನಾನು ಈಗ ನಿಮಗೆ ಹೇಳಿದ ಎಚ್ಚರಿಕೆಯ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ಮನಸ್ಸಿನಲ್ಲಿಟ್ಟುಕೊಳ್ಳಿರಿ. ಮತ್ತು ನಿಮ್ಮ ಸಂತತಿಯವರಿಗೆ ಈ ಧರ್ಮಶಾಸ್ತ್ರದ ನಿಯಮಗಳನ್ನು ಅನುಸರಿಸಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿರಿ.",
			"47": "<sup>47</sup> ಇದು ನಿರರ್ಥಕವೆಂದು ಭಾವಿಸಬಾರದು; ಇದರಿಂದ ನೀವು ಬಾಳುವಿರಿ; ನೀವು ಯೊರ್ದನ್ ಹೊಳೆಯನ್ನು ದಾಟಿ ಸ್ವಾಧೀನ ಮಾಡಿಕೊಳ್ಳುವದಕ್ಕೆ ಹೋಗುವ ದೇಶದಲ್ಲಿ ಇದನ್ನು ಅನುಸರಿಸುವದರಿಂದಲೇ ಬಹುಕಾಲ ಇರುವಿರಿ ಎಂದು ಹೇಳಿದನು.",
			"48": "<sup>48</sup> ಆ ದಿನದಲ್ಲೇ ಯೆಹೋವನು ಮೋಶೆಗೆ - ನೀನು ಈ ಅಬಾರೀಮ್ ಬೆಟ್ಟಗಳನ್ನು ಅಂದರೆ ಮೋವಾಬ್ಯರ ದೇಶದಲ್ಲಿ ಯೆರಿಕೋ ಪಟ್ಟಣಕ್ಕೆ ಎದುರಾಗಿರುವ ನೆಬೋ ಬೆಟ್ಟವನ್ನು ಹತ್ತಿ ನಾನು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಸ್ವದೇಶವಾಗುವದಕ್ಕೆ ಕೊಡುವ ಕಾನಾನ್ ದೇಶವನ್ನು ನೋಡು.",
			"50": "<sup>50</sup> ಅನಂತರ ನಿನ್ನ ಅಣ್ಣನಾದ ಆರೋನನು ಹೇಗೆ ಹೋರೆಂಬ ಬೆಟ್ಟದಲ್ಲಿ ದೇಹವನ್ನು ಬಿಟ್ಟು ತನ್ನ ಪಿತೃಗಳಲ್ಲಿ ಸೇರಿದನೋ ಹಾಗೆಯೇ ನೀನೂ ಈ ಬೆಟ್ಟದಲ್ಲಿ ದೇಹವನ್ನು ಬಿಟ್ಟು ಪಿತೃಗಳಲ್ಲಿ ಸೇರಬೇಕು.",
			"51": "<sup>51</sup> ನೀವಿಬ್ಬರೂ ಚಿನ್ ಅರಣ್ಯದಲ್ಲಿನ ಮೆರೀಬಾ ಕಾದೇಶಿನಲ್ಲಿ ನೀರು ಹೊರಟ ಸ್ಥಳದ ಬಳಿಯಲ್ಲಿ ಇಸ್ರಾಯೇಲ್ಯರ ಮಧ್ಯದಲ್ಲಿ ನನಗೆ ವಿರೋಧವಾಗಿ ದ್ರೋಹಮಾಡಿ ಅವರ ಮುಂದೆ ನನ್ನ ಗೌರವವನ್ನು ಕಾಪಾಡದೆ ಹೋದದರಿಂದ ಹೀಗೆ ಆಗಬೇಕು.",
			"52": "<sup>52</sup> ನಾನು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಕೊಡುವ ದೇಶವನ್ನು ನೀನು ದೂರದಿಂದ ನೋಡಬಹುದೇ ಹೊರತು ಅದರಲ್ಲಿ ಪ್ರವೇಶಿಸಕೂಡದು ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು."
		},
		"33": {
			"1": "<sup>1</sup> ದೇವಪುರುಷನಾದ ಮೋಶೆ ದೇಹವನ್ನು ಬಿಡುವದಕ್ಕಿಂತ ಮುಂಚೆ ಇಸ್ರಾಯೇಲ್ಯರ ವಿಷಯವಾಗಿ ಹೇಳಿದ ಆಶೀರ್ವಾದವು ಹೇಗಂದರೆ -",
			"2": "<sup>2</sup> ಯೆಹೋವನು ಸೀನಾಯಿಬೆಟ್ಟದಿಂದ ಬಂದು ಸೇಯೀರ್ ಎಂಬ ಬೆಟ್ಟದ ಸೀಮೆಯೊಳಗಿಂದ ಪ್ರಕಾಶಿಸಿ ಪಾರಾನ್ ಪರ್ವತದಿಂದ ಹೊಳೆದು ಲಕ್ಷಾಂತರ ಪರಿಶುದ್ಧದೂತರ ಮಧ್ಯದಿಂದ ಅವರಿಗೋಸ್ಕರ ದಯಮಾಡಿದನು; ಆತನ ಬಲಪಾರ್ಶ್ವದಲ್ಲಿ ಅಗ್ನಿಸದೃಶವಾದ ಧರ್ಮಶಾಸ್ತ್ರವಿತ್ತು.",
			"3": "<sup>3</sup> ಆತನು ತನ್ನ ಜನರನ್ನು ಪ್ರೀತಿಸುತ್ತಾನೆ; [ಯೆಹೋವನೇ,] ನಿನ್ನ ಭಕ್ತರೆಲ್ಲರೂ ನಿನ್ನ ಆಶ್ರಯದಲ್ಲಿಯೇ ಇದ್ದಾರೆ; ನಿನ್ನ ಚರಣ ಸನ್ನಿಧಾನದಲ್ಲಿ ಕೂತಿರುವರು; ನೀನು ಹೇಳುವ ಆಜ್ಞೆಗಳನ್ನು ಶಿರಸ್ಸಾವಹಿಸುವರು.",
			"4": "<sup>4</sup> ಮೋಶೆಯು ಯಾಕೋಬ್ ಸಂತತಿಯವರಾದ ನಮಗೆ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಸ್ವಾಸ್ತ್ಯವಾಗಿ ಕೊಟ್ಟಿದ್ದಾನೆ.",
			"5": "<sup>5</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಅಧಿಪತಿಗಳು ಕುಲಗಳವರೊಡನೆ ಒಟ್ಟಾಗಿ ಕೂಡಿದಾಗ [ಯೆಹೋವನು] ತಾನೇ ಯೆಶುರೂನಿನಲ್ಲಿ ಅರಸನಾದನು.",
			"6": "<sup>6</sup> [ರೂಬೇನ್ ಕುಲದ ವಿಷಯದಲ್ಲಿ ಹೇಳಿದ್ದೇನಂದರೆ -] ರೂಬೇನ್ ಕುಲವು ನಾಶವಾಗದೆ ಉಳಿಯಲಿ; ಆದರೆ ಅದು ಸ್ವಲ್ಪ ಮಂದಿಯುಳ್ಳದ್ದಾಗಿರುವದು.",
			"7": "<sup>7</sup> ಯೆಹೂದ ಕುಲದ ವಿಷಯದಲ್ಲಿ ಹೀಗಂದನು - ಯೆಹೋವನೇ, ಯೆಹೂದ ಕುಲದವರ ಮೊರೆಯನ್ನು ಲಾಲಿಸಿ ಅವರನ್ನು ಅವರ ಬಂಧುಗಳೊಡನೆ ಸೇರಿಸು. ಅವರಿಗೋಸ್ಕರ ಯುದ್ಧಮಾಡಿದರಲ್ಲಾ; ಅವರ ಶತ್ರುಗಳಿಂದ ಅವರನ್ನು ತಪ್ಪಿಸಬೇಕು.",
			"8": "<sup>8</sup> ಲೇವಿಕುಲದ ವಿಷಯದಲ್ಲಿ ಹೀಗಂದನು - [ಯೆಹೋವನೇ,] ನಿನ್ನ ನಿರ್ಣಯವನ್ನು ತಿಳಿಸುವ ಊರೀಮ್ ತುಮ್ಮೀಮ್ ಎಂಬ ವಸ್ತುಗಳು ನಿನ್ನ ಭಕ್ತರಾದ ಇವರ ವಶದಲ್ಲಿ ಉಂಟಷ್ಟೆ. ನೀನು ಮಸ್ಸದಲ್ಲಿ ಅವರನ್ನು ಪರೀಕ್ಷಿಸಿದಿಯಲ್ಲಾ; ಮೆರೀಬದಲ್ಲಿ ನೀರು ಹೊರಟ ಸ್ಥಳದ ಹತ್ತಿರ ಅವರೊಡನೆ ವಿವಾದ ಮಾಡಿದಿ.",
			"9": "<sup>9</sup> ಅವರು ನಿನ್ನ ಆಜ್ಞೆಯನ್ನು ಅನುಸರಿಸುತ್ತಾ ನಿನ್ನ ನಿಬಂಧನೆಯನ್ನು ಕೈಕೊಳ್ಳುವವರಾಗಿ ತಾಯಿತಂದೆಗಳನ್ನು ಪರಿಚಯವಿಲ್ಲವೆಂದೂ ಅಣ್ಣತಮ್ಮಂದಿರನ್ನು ಅರಿಯೆವೆಂದೂ ಸ್ವಂತ ಮಕ್ಕಳನ್ನು ಗುರುತಿಲ್ಲವೆಂದೂ ಹೇಳಿ ಬಿಟ್ಟರಲ್ಲಾ.",
			"10": "<sup>10</sup> ಅವರು ಯಾಕೋಬ್ ವಂಶದವರಿಗೆ ನಿನ್ನ ನಿರ್ಣಯಗಳನ್ನು ತಿಳಿಸುವರು; ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ನಿನ್ನ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಕಲಿಸುವರು. ನಿನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಧೂಪಹಾಕುವರು; ನಿನ್ನ ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಸರ್ವಾಂಗಹೋಮಗಳನ್ನು ಸಮರ್ಪಿಸುವರು.",
			"11": "<sup>11</sup> ಯೆಹೋವನೇ, ಅವರ ಆಸ್ತಿಯನ್ನು ವೃದ್ಧಿಪಡಿಸು; ಅವರ ಸೇವೆಯು ನಿನಗೆ ಸಮರ್ಪಕವಾಗಿರಲಿ; ಅವರಿಗೆ ವಿರೋಧವಾಗಿ ಬರುವವರ ನಡುವನ್ನು ಮುರಿದು ಅವರ ವೈರಿಗಳು ಪುನಃ ಶ್ವಾಸಬಿಡದಂತೆ ಮಾಡು.",
			"12": "<sup>12</sup> ಬೆನ್ಯಾಮೀನ್ ಕುಲದವರ ವಿಷಯದಲ್ಲಿ ಹೀಗಂದನು - ಯೆಹೋವನು ಇವರ ಬೆಟ್ಟಗಳ ತಪ್ಪಲುಗಳಲ್ಲೇ ನಿವಾಸಿಸುತ್ತಾ ಇವರನ್ನು ಯಾವಾಗಲೂ ಆವರಿಸಿಕೊಂಡಿರುವದರಿಂದ ಆತನ ಪ್ರಿಯರಾದ ಇವರು ನಿರ್ಭಯವಾಗಿ ವಾಸಿಸುವರು.",
			"13": "<sup>13</sup> ಯೋಸೇಫ್ ಕುಲಗಳ ವಿಷಯದಲ್ಲಿ ಹೇಳಿದ್ದೇನಂದರೆ - ಅವರ ಸೀಮೆಗೆ ಯೆಹೋವನ ಆಶೀರ್ವಾದವಾಗಲಿ. ಮೇಲಣ ಆಕಾಶದಿಂದುಂಟಾಗುವ ಮಂಜು, ಕೆಳಗಣ ಸಾಗರದ ಸೆಲೆ,",
			"14": "<sup>14</sup> ವರುಷ ವರುಷಕ್ಕೂ ತಿಂಗಳು ತಿಂಗಳಿಗೂ ಆಗುವ ಬೆಳೆ,",
			"15": "<sup>15</sup> ಅನಾದಿಯಾದ ಪರ್ವತಗಳ ಉತ್ಪನ್ನ, ಶಾಶ್ವತವಾದ ಬೆಟ್ಟಗಳಿಂದುಂಟಾಗುವ ಮೇಲು,",
			"16": "<sup>16</sup> ಭೂಮಿಯಲ್ಲಿ ಸಮೃದ್ಧಿಯಾಗಿ ಬೆಳೆಯುವ ಫಲ ಇವೆಲ್ಲವುಗಳಲ್ಲಿ ಶ್ರೇಷ್ಠವಾದದ್ದೂ [ಹೋರೇಬ್‍ಬೆಟ್ಟದಲ್ಲಿನ] ಮುಳ್ಳುಗಿಡದಲ್ಲಿ ವಾಸಿಸಿದಾತನ ದಯೆಯೂ ಯೋಸೇಫ್ ವಂಶದವರ ಪಾಲಿಗೆ ಬರಲಿ; ಅಣ್ಣತಮ್ಮಂದಿರಿಗೆ ಪ್ರಭುವಾಗಿರುವ ಕುಲದವರ ಶಿರಸ್ಸಿನ ಮೇಲೆ ಉಂಟಾಗಲಿ.",
			"17": "<sup>17</sup> ಯೋಸೇಫನ ಜ್ಯೇಷ್ಠಸಂತತಿಯವರು ಗೂಳಿಯೋಪಾದಿಯಲ್ಲಿ ಗಾಂಭೀರ್ಯವುಳ್ಳವರು; ಅವರ ಕೊಂಬುಗಳು ಕಾಡುಕೋಣದ ಕೊಂಬುಗಳಷ್ಟು [ಬಲವುಳ್ಳವು]; ಅವುಗಳಿಂದ ಭೂಮಂಡಲದ ಜನಾಂಗಗಳನ್ನೆಲ್ಲಾ ಇರಿದು ಓಡಿಸುವರು. ಎಫ್ರಾಯೀಮ್ ಕುಲದ ಕೋಟ್ಯಾಂತರ ಜನರೂ ಮನಸ್ಸೆಕುಲದ ಲಕ್ಷಾಂತರ ಮಂದಿಯೂ ಇಂಥವರೇ.",
			"18": "<sup>18</sup> ಜೆಬುಲೂನ್ಯರ [ಮತ್ತು ಇಸ್ಸಾಕಾರ್ಯರ] ವಿಷಯದಲ್ಲಿ ಹೀಗಂದನು - ಜೆಬುಲೂನ್ಯರೇ, ನಿಮ್ಮ ಪ್ರಯಾಣಗಳಲ್ಲಿ ಸಂತೋಷವಾಗಿರ್ರಿ; ಇಸ್ಸಾಕಾರ್ಯರೇ, ನಿಮ್ಮ ಪಾಳೆಯಗಳಲ್ಲಿ ಆನಂದವಾಗಿರ್ರಿ.",
			"19": "<sup>19</sup> ಅವರು ಸಮುದ್ರದಿಂದ ಐಶ್ವರ್ಯವನ್ನು ಹೊಂದಿ ಉಸುಬಿನಲ್ಲಿ ಅಡಗಿರುವ ಸಂಪತ್ತನ್ನು ಪಡೆಯುವರು. ಅನ್ಯಜನಗಳನ್ನು ತಮ್ಮ ಬೆಟ್ಟದ ಸೀಮೆಗೆ ಕರಿಸಿ ಅಲ್ಲಿ ನ್ಯಾಯವಾದ ಯಜ್ಞಗಳನ್ನು ಸಮರ್ಪಿಸುವರು.",
			"20": "<sup>20</sup> ಗಾದ್ ಕುಲದವರ ವಿಷಯದಲ್ಲಿ ಹೀಗಂದನು - ಗಾದ್ಯರ ಪ್ರದೇಶವನ್ನು ವಿಸ್ತಾರಮಾಡಿದ ಯೆಹೋವನಿಗೆ ಸ್ತೋತ್ರ. ಅವರು ಸಿಂಹದಂತೆ ಹೊಂಚಿಕೊಂಡು [ಶತ್ರುಗಳ] ಭುಜವನ್ನೂ ಶಿರಸ್ಸನ್ನೂ ಮುರಿಯುತ್ತಾರೆ.",
			"21": "<sup>21</sup> ಅವರು ದೇಶದ ಮೊದಲನೆಯ ಭಾಗವನ್ನು ತಮಗೋಸ್ಕರ ತೆಗೆದುಕೊಂಡರು; ಅಲ್ಲಿ ಪ್ರಧಾನನಿಗೆ ಯೋಗ್ಯವಾದ ಸ್ವಾಸ್ತ್ಯವು ದೊರಕಿತು. ಅವರು ಜನಾಧಿಪತಿಗಳ ಜೊತೆಯಲ್ಲಿ ಬಂದು ಇಸ್ರಾಯೇಲ್ಯರೊಡನೆ ಯೆಹೋವನ ಆಜ್ಞೆಯನ್ನು ನೆರವೇರಿಸಿ ಆತನ ನ್ಯಾಯವನ್ನು ಸ್ಥಾಪಿಸಿದರು.",
			"22": "<sup>22</sup> ದಾನ್‍ಕುಲದ ವಿಷಯದಲ್ಲಿ ಹೀಗಂದನು - ದಾನ್‍ಕುಲವು ಬಾಷಾನ್ ಸೀಮೆಯಿಂದ ಹೊರಟು ಹಾರಿಬರುವ ಪ್ರಾಯದ ಸಿಂಹದಂತಿದೆ.",
			"23": "<sup>23</sup> ನಫ್ತಾಲಿಕುಲದ ವಿಷಯದಲ್ಲಿ ಹೀಗಂದನು - ನಫ್ತಾಲಿಕುಲವೇ, ನೀನು ಯೆಹೋವನ ದಯೆಯನ್ನು ಹೊಂದಿ ಸಂತೃಪ್ತವಾದಿ; ಆತನ ಆಶೀರ್ವಾದದಿಂದ ನಿನಗೆ ಸಮೃದ್ಧಿಯುಂಟು. [ಕಿನ್ನೆರೆತ್] ಸಮುದ್ರವೂ ದಕ್ಷಿಣ ಪ್ರದೇಶವೂ ನಿನಗೆ ಸ್ವಾಸ್ತ್ಯವಾಗಲಿ.",
			"24": "<sup>24</sup> ಆಶೇರ್ ಕುಲದ ವಿಷಯದಲ್ಲಿ ಹೀಗಂದನು - ಪುತ್ರಶ್ರೇಷ್ಠರಾದ ಆಶೇರ್ಯರು ಭಾಗ್ಯವನ್ನು ಹೊಂದಲಿ; ಅವರು ಸಹೋದರರ ದಯೆಯನ್ನು ಪಡೆಯಲಿ; ಅವರು ಎಣ್ಣೆಯಲ್ಲೇ ಕಾಲನ್ನು ಅದ್ದಲಿ.",
			"25": "<sup>25</sup> ನಿಮ್ಮ ಕೋಟೆಯ ಬಾಗಲುಗಳಿಗೆ ಕಬ್ಬಿಣದ ಮತ್ತು ತಾಮ್ರದ ಅಗುಳಿಗಳು ಇರುವವು; ನೀವು ಇರುವವರೆಗೂ ನಿಮಗೆ ಬಲವು ಇರುವದು.",
			"26": "<sup>26</sup> ಯೆಶುರೂನೇ, ನಿಮ್ಮ ದೇವರಿಗೆ ಸಮಾನನು ಯಾವನೂ ಇಲ್ಲ; ಆತನು ಆಕಾಶವನ್ನೇರಿ ಮೇಘಾರೂಢನಾಗಿ ಮಹಾಗಾಂಭೀರ್ಯದಿಂದ ನಿಮ್ಮ ಸಹಾಯಕ್ಕೆ ಬರುವನು.",
			"27": "<sup>27</sup> ಆದಿಯಿಂದಲೂ ದೇವರೇ ನಿಮಗೆ ನಿವಾಸಸ್ಥಾನವಾಗಿದ್ದಾನಲ್ಲಾ; ಸದಾ ದೇವರ ಹಸ್ತವೇ ನಿಮಗೆ ಆಧಾರ. ಆತನು ನಿಮ್ಮೆದುರಿನಿಂದ ಶತ್ರುಗಳನ್ನು ಹೊರಡಿಸಿ ಅವರನ್ನು ಸಂಹರಿಸಿರಿ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು;",
			"28": "<sup>28</sup> ಆದಕಾರಣ ಇಸ್ರಾಯೇಲ್ಯರು ಆಕಾಶದಿಂದ ಮಂಜು ಸುರಿದು ಧಾನ್ಯವೂ ದ್ರಾಕ್ಷಾರಸವೂ ಸಮೃದ್ಧಿಯಾಗಿರುವ ದೇಶದಲ್ಲೇ ಸೇರಿ ನಿರ್ಭಯವಾಗಿ ವಾಸಿಸುವವರಾದರು, ಯಾಕೋಬ್‍ಸಂಭವರು ಸುರಕ್ಷಿತರಾದರು.",
			"29": "<sup>29</sup> ಇಸ್ರಾಯೇಲ್ಯರೇ, ನೀವು ಎಷ್ಟೋ ಧನ್ಯರು; ನಿಮ್ಮಷ್ಟು ಭಾಗ್ಯವಂತರು ಯಾರಿದ್ದಾರೆ? ನೀವು ಯೆಹೋವನ ಅನುಗ್ರಹದಿಂದ ಜಯವನ್ನು ಹೊಂದಿದವರು. ಆತನೇ ನಿಮ್ಮನ್ನು ಕಾಯುವ ಡಾಲೂ ನಿಮ್ಮ ಗೌರವವನ್ನು ಕಾಪಾಡುವ ಕತ್ತಿಯೂ ಆಗಿದ್ದಾನೆ. ಆದದರಿಂದ ನಿಮ್ಮ ಶತ್ರುಗಳು ನಿಮ್ಮ ಮುಂದೆ ಮುದುರಿಕೊಳ್ಳುತ್ತಾರೆ; ನೀವೋ ಅವರಿಗಿದ್ದ ಎತ್ತರವಾದ ಸೀಮೆಯಲ್ಲಿ ಜಯಶಾಲಿಗಳಾಗಿ ಸಂಚರಿಸುತ್ತೀರಿ."
		},
		"34": {
			"1": "<sup>1</sup> ಮೋಶೆಯು ಮೋವಾಬ್ಯರ ಮೈದಾನದಿಂದ ಯೆರಿಕೋ ಪಟ್ಟಣಕ್ಕೆ ಎದುರಾಗಿರುವ ನೆಬೋ ಪರ್ವತಕ್ಕೆ ಹೋಗಿ ಪಿಸ್ಗಾ ಎಂಬ ಬೆಟ್ಟದ ಶಿಖರವನ್ನು ಹತ್ತಿದನು. ಆಗ ಯೆಹೋವನು [ಕಾನಾನ್] ದೇಶವನ್ನೆಲ್ಲಾ ಅಂದರೆ ದಾನ್ ಪಟ್ಟಣದವರೆಗೂ",
			"2": "<sup>2</sup> ಗಿಲ್ಯಾದ್‍ಸೀಮೆ, ನಫ್ತಾಲಿ ಪ್ರದೇಶ, ಎಫ್ರಾಯೀಮ್ ಮನಸ್ಸೆ ಕುಲಗಳವರ ಸೀಮೆ, ಪಶ್ಚಿಮಸಮುದ್ರದವರೆಗೂ ಯೆಹೂದ ಸೀಮೆ,",
			"3": "<sup>3</sup> ದಕ್ಷಿಣಪ್ರದೇಶ, ಚೋಗರೂರಿನ ತನಕ ಯೆರಿಕೋ ಎಂಬ ಖರ್ಜೂರಗಳ ಪಟ್ಟಣದ ಸುತ್ತಲಿರುವ ಮೈದಾನ ಇದನ್ನೆಲ್ಲಾ ಅವನಿಗೆ ತೋರಿಸಿ -",
			"4": "<sup>4</sup> ನಾನು ಅಬ್ರಹಾಮ್ ಇಸಾಕ್ ಯಾಕೋಬರಿಗೆ ಪ್ರಮಾಣಮಾಡಿ ಅವರ ಸಂತತಿಯವರಿಗೆ ಕೊಡುವೆನೆಂದು ವಾಗ್ದಾನಮಾಡಿದ ದೇಶವು ಇದೇ; ನಿನಗೆ ಪ್ರತ್ಯಕ್ಷವಾಗಿ ತೋರಿಸಿದ್ದೇನೆ. ಆದರೆ ನೀನು ಹೊಳೆದಾಟಿ ಅಲ್ಲಿಗೆ ಹೋಗಕೂಡದೆಂದು ಹೇಳಿದನು.",
			"5": "<sup>5</sup> ಯೆಹೋವನ ಮಾತಿನಂತೆ ಆತನ ಸೇವಕನಾದ ಮೋಶೆ ಅಲ್ಲೇ ಮೋವಾಬ್ಯರ ದೇಶದಲ್ಲಿ ದೇಹಬಿಟ್ಟನು.",
			"6": "<sup>6</sup> ಮೋವಾಬ್ಯರ ದೇಶದಲ್ಲಿ ಬೇತ್ಪೆಗೋರಿಗೆ ಎದುರಾಗಿರುವ ಕಣಿವೆಯಲ್ಲಿ [ಯೆಹೋವನು] ಅವನ ದೇಹವನ್ನು ಸಮಾಧಿಮಾಡಿದನು; ಅವನ ಸಮಾಧಿ ಎಲ್ಲಿದೆಯೋ ಇಂದಿನವರೆಗೆ ಯಾರಿಗೂ ತಿಳಿಯದು.",
			"7": "<sup>7</sup> ಮೋಶೆ ಸಾಯುವಾಗ ನೂರಿಪ್ಪತ್ತು ವರುಷದವನಾಗಿದ್ದನು. ಅವನ ಕಣ್ಣು ಮೊಬ್ಬಾಗಲಿಲ್ಲ, ಅವನ ಜೀವಕಳೆ ಕುಂದಿಹೋಗಿರಲಿಲ್ಲ.",
			"8": "<sup>8</sup> ಮೋವಾಬ್ಯರ ಮೈದಾನದಲ್ಲಿ ಇಸ್ರಾಯೇಲ್ಯರು ಮೋಶೆಗೋಸ್ಕರ ಮೂವತ್ತು ದಿನ ದುಃಖಿಸಿದರು. ಅಲ್ಲಿಗೆ ಮೋಶೆಯ ಪ್ರಲಾಪದ ದಿನಗಳು ಮುಗಿದವು.",
			"9": "<sup>9</sup> ಮೋಶೆಯು ನೂನನ ಮಗನಾದ ಯೆಹೋಶುವನ ಮೇಲೆ ಕೈಯಿಟ್ಟದರಿಂದ ಅವನು ಜ್ಞಾನವರ ಸಂಪನ್ನನಾದನು. ಯೆಹೋವನು ಮೋಶೆಗೆ ಕೊಟ್ಟ ಆಜ್ಞೆಗೆ ಅನುಸಾರವಾಗಿ ಇಸ್ರಾಯೇಲ್ಯರು ಯೆಹೋಶುವನ ಮಾತಿನ ಪ್ರಕಾರ ನಡೆದರು.",
			"10": "<sup>10</sup> ಯೆಹೋವನು ಮೋಶೆಯ ಸಂಗಡ ಮುಖಾಮುಖಿಯಾಗಿ ಬಳಿಕೆಯಾಗಿದ್ದನು.",
			"11": "<sup>11</sup> ಆತನು ಐಗುಪ್ತ ದೇಶದಲ್ಲಿ ಫರೋಹನ ಮುಂದೆಯೂ ಅವನ ಪ್ರಜಾಪರಿವಾರದವರ ಮುಂದೆಯೂ ವಿಧವಿಧವಾದ ಅದ್ಭುತ ಮಹತ್ಕಾರ್ಯಗಳನ್ನು ನಡಿಸುವದಕ್ಕೆ ಯೆಹೋವನು ಅವನನ್ನು ಕಳುಹಿಸಿದನು;",
			"12": "<sup>12</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಮುಂದೆ ವಿಶೇಷವಾದ ಭುಜಪರಾಕ್ರಮವನ್ನೂ ಭಯಂಕರ ಕಾರ್ಯಗಳನ್ನೂ ನಡಿಸಿದನು. ಇಂಥವನಿಗೆ ಸಮಾನನಾದ ಮತ್ತೊಬ್ಬ ಪ್ರವಾದಿ ಈಚೆಗೆ ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಹುಟ್ಟಲೇ ಇಲ್ಲ."
		}
	}
};
module.exports = book;