var book = {
	"name": "ನ್ಯಾಯಸ್ಥಾಪಕರು",
	"numChapters": 21,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ಯೆಹೋಶುವನು ಮರಣಹೊಂದಿದ ನಂತರ ಇಸ್ರಾಯೇಲ್ಯರು - ಕಾನಾನ್ಯರೊಡನೆ ಯುದ್ಧಮಾಡುವದಕ್ಕೆ ನಮ್ಮಲ್ಲಿ ಮೊದಲು ಯಾರು ಹೋಗಬೇಕು ಎಂದು ಯೆಹೋವನನ್ನು ಕೇಳಲು",
			"2": "<sup>2</sup> ಆತನು ಅವರಿಗೆ - ಯೆಹೂದ್ಯರು ಹೋಗಲಿ; ಇಗೋ, ದೇಶವನ್ನು ಅವರಿಗೆ ಒಪ್ಪಿಸಿದ್ದೇನೆ ಅಂದನು.",
			"3": "<sup>3</sup> ಆಗ ಯೆಹೂದ್ಯರು ತಮ್ಮ ಸಹೋದರರಾದ ಸಿಮೆಯೋನ್ಯರಿಗೆ - ಕಾನಾನ್ಯರೊಡನೆ ಯುದ್ಧಮಾಡುವದಕ್ಕೋಸ್ಕರ ನೀವು ನಮ್ಮ ಸಂಗಡ ನಮ್ಮ ಸ್ವಾಸ್ತ್ಯಭೂಮಿಗೆ ಬನ್ನಿರಿ; ಅನಂತರ ನಾವೂ ನಿಮ್ಮ ಸಂಗಡ ನಿಮ್ಮ ಸ್ವಾಸ್ತ್ಯ ಭೂಮಿಗೆ ಬರುವೆವು ಎಂದು ಹೇಳಲು",
			"4": "<sup>4</sup> ಅವರು ಒಪ್ಪಿ ಯೆಹೂದ್ಯರ ಜೊತೆಯಲ್ಲಿ ಯುದ್ಧಕ್ಕೆ ಹೋದರು. ಆಗ ಯೆಹೋವನು ಕಾನಾನ್ಯರನ್ನೂ ಪೆರಿಜ್ಜೀಯರನ್ನೂ ಅವರ ಕೈಗೆ ಒಪ್ಪಿಸಿದ್ದರಿಂದ ಅವರು ಅವರಲ್ಲಿ ಹತ್ತು ಸಾವಿರ ಮಂದಿಯನ್ನು ಬೆಜೆಕಿನಲ್ಲಿ ಹತಮಾಡಿದರು.",
			"5": "<sup>5</sup> ಅವರು ಅಲ್ಲಿ ಅದೋನೀಬೆಜೆಕನನ್ನು ಸಂಧಿಸಿ ಅವನೊಡನೆ ಯುದ್ಧಮಾಡಿ ಕಾನಾನ್ಯರನ್ನೂ ಪೆರಿಜ್ಜೀಯರನ್ನೂ ಸೋಲಿಸಿದರು.",
			"6": "<sup>6</sup> ಅದೋನೀಬೆಜೆಕನು ಓಡಿಹೋಗಲು ಅವನನ್ನು ಹಿಂದಟ್ಟಿ ಹಿಡಿದು ಅವನ ಕೈಕಾಲುಗಳ ಹೆಬ್ಬೆರಳುಗಳನ್ನು ಕತ್ತರಿಸಿಬಿಟ್ಟರು.",
			"7": "<sup>7</sup> ಆಗ ಅದೋನೀಬೆಜೆಕನು - ನಾನು ಕೈಕಾಲುಗಳ ಹೆಬ್ಬೆರಳುಗಳನ್ನು ಕತ್ತರಿಸಿ ಬಿಟ್ಟ ಎಪ್ಪತ್ತು ಮಂದಿ ಅರಸರು ನನ್ನ ಮೇಜಿನ ಕೆಳಗೆ ಬೀಳುವ ಚೂರುಗಳನ್ನು ಕೂಡಿಸಿಕೊಂಡು ತಿನ್ನುತ್ತಿದ್ದರು; ನಾನು ಅವರಿಗೆ ಮಾಡಿದಂತೆಯೇ ದೇವರು ನನಗೆ ಮಾಡಿದ್ದಾನೆ ಅಂದನು. ಅವರು ಅವನನ್ನು ಯೆರೂಸಲೇಮಿಗೆ ತೆಗೆದುಕೊಂಡು ಬರಲು ಅಲ್ಲಿ ಸತ್ತನು.",
			"8": "<sup>8</sup> ಯೆಹೂದ್ಯರು ಯೆರೂಸಲೇಮಿನವರೊಡನೆ ಯುದ್ಧಮಾಡಿ ಅದನ್ನು ಹಿಡಿದು ಜನರನ್ನು ಕತ್ತಿಯಿಂದ ಸಂಹರಿಸಿ ಪಟ್ಟಣಕ್ಕೆ ಬೆಂಕಿಹೊತ್ತಿಸಿದರು.",
			"9": "<sup>9</sup> ತರುವಾಯ ಅವರು ಹೋಗಿ ಪರ್ವತಪ್ರದೇಶ, ದಕ್ಷಿಣ ಸೀಮೆ, ಇಳಕಲಿನ ಪ್ರದೇಶ ಇವುಗಳಲ್ಲಿದ್ದ ಕಾನಾನ್ಯರೊಡನೆ ಯುದ್ಧಮಾಡಿದರು.",
			"10": "<sup>10</sup> ಅವರು ಮುಂಚೆ ಕಿರ್ಯತರ್ಬ ಎಂಬ ಹೆಸರಿದ್ದ ಹೆಬ್ರೋನಿಗೆ ಹೋಗಿ ಅಲ್ಲಿದ್ದ ಕಾನಾನ್ಯರ ಮೇಲೆ ಬಿದ್ದು ಅವರಲ್ಲಿ ಶೇಷೈ, ಅಹೀಮನ್, ತಲ್ಮೈ ಎಂಬವರನ್ನು ಹೊಡೆದು ಬಿಟ್ಟರು.",
			"11": "<sup>11</sup> ಅಲ್ಲಿಂದ ದೆಬೀರಿನವರಿಗೆ ವಿರೋಧವಾಗಿ ಹೋದರು; ದೆಬೀರಕ್ಕೆ ಮುಂಚೆ ಕಿರ್ಯತ್‍ಸೇಫೆರ್ ಎಂಬ ಹೆಸರಿತ್ತು.",
			"12": "<sup>12</sup> ಕಿರ್ಯತ್‍ಸೇಫೆರನ್ನು ಹಿಡಿದುಕೊಳ್ಳುವವನಿಗೆ ನನ್ನ ಮಗಳಾದ ಅಕ್ಷಾ ಎಂಬಾಕೆಯನ್ನು ಮದುವೆಮಾಡಿಕೊಡುತ್ತೇನೆಂದು ಕಾಲೇಬನು ಹೇಳಲು",
			"13": "<sup>13</sup> ಅವನ ತಮ್ಮನೂ ಕೆನಜನ ಮಗನೂ ಆದ ಒತ್ನೀಯೇಲನು ಅದನ್ನು ಹಿಡಿದನು. ಆಗ ಕಾಲೇಬನು ತನ್ನ ಮಗಳಾದ ಅಕ್ಷಾ ಎಂಬಾಕೆಯನ್ನು ಅವನಿಗೆ ಮದುವೆಮಾಡಿಕೊಟ್ಟನು.",
			"14": "<sup>14</sup> ಆಕೆಯು ಬರುತ್ತಿರುವಾಗ ತನ್ನ ತಂದೆಯ ಹತ್ತಿರ ಭೂಮಿಯನ್ನು ಕೇಳಬೇಕೆಂದು ಗಂಡನನ್ನು ಪ್ರೇರೇಪಿಸಿ ತಾನು ಕತ್ತೆಯಿಂದ ಇಳಿದಳು. ಕಾಲೇಬನು ನಿನಗೇನು ಬೇಕೆಂದು ಆಕೆಯನ್ನು ಕೇಳಲು",
			"15": "<sup>15</sup> ಆಕೆಯು - ನನಗೊಂದು ದಾನಕೊಡಬೇಕು; ನನ್ನನ್ನು ಬೆಗ್ಗಾಡಿಗೆ ಕೊಟ್ಟುಬಿಟ್ಟಿಯಲ್ಲಾ, ಬುಗ್ಗೆಗಳಿರುವ ಸ್ಥಳವನ್ನು ನನಗೆ ಕೊಡು ಅಂದಳು. ಆಗ ಅವನು ಆಕೆಗೆ ಮೇಲಣ ಮತ್ತು ಕೆಳಗಣ ಬುಗ್ಗೆಗಳನ್ನು ಕೊಟ್ಟನು.",
			"16": "<sup>16</sup> ಕೇನ್ಯನೂ ಮೋಶೆಯ ಮಾವನೂ ಆದ [ಹೋಬಾಬನ] ವಂಶದವರು ಖರ್ಜೂರ ನಗರದಿಂದ ಹೊರಟು ಯೆಹೂದ್ಯರ ಜೊತೆಯಲ್ಲಿ ಅರಾದಿನ ದಕ್ಷಿಣದಲ್ಲಿರುವ ಯೆಹೂದ ಅರಣ್ಯಕ್ಕೆ ಬಂದು ಅಲ್ಲಿನ ಜನರ ಸಂಗಡ ವಾಸಮಾಡಿದರು.",
			"17": "<sup>17</sup> ತರುವಾಯ ಯೆಹೂದ್ಯರು ತಮ್ಮ ಬಂಧುಗಳಾದ ಸಿಮೆಯೋನ್ಯರ ಸಂಗಡ ಹೋಗಿ ಚೆಫತ್ ಎಂಬ ಪಟ್ಟಣದಲ್ಲಿದ್ದ ಕಾನಾನ್ಯರನ್ನು ಸೋಲಿಸಿ ಆ ಪಟ್ಟಣವನ್ನು ಹಾಳುಮಾಡಿ ಅದನ್ನು ಹೊರ್ಮಾ ಎಂದು ಕರೆದರು.",
			"18": "<sup>18</sup> ಅನಂತರ ಅವರು ಗಾಜಾ, ಅಷ್ಕೆಲೋನ್, ಎಕ್ರೋನ್ ಎಂಬ ಪಟ್ಟಣಗಳನ್ನೂ ಅವುಗಳ ಮೇರೆಗಳನ್ನೂ ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡರು.",
			"19": "<sup>19</sup> ಯೆಹೋವನು ಯೆಹೂದ್ಯರ ಸಂಗಡ ಇದ್ದದರಿಂದ ಅವರು ಪರ್ವತಪ್ರದೇಶಗಳನ್ನೆಲ್ಲಾ ಸ್ವತಂತ್ರಿಸಿಕೊಂಡರು. ಆದರೆ ತಗ್ಗಿನ ಪ್ರದೇಶಗಳಲ್ಲಿ ವಾಸಿಸುವವರಿಗೆ ಕಬ್ಬಿಣದ ರಥಗಳಿದ್ದದರಿಂದ ಅವರನ್ನು ಹೊರಡಿಸುವದು ಆಗಲಿಲ್ಲ.",
			"20": "<sup>20</sup> ಮೋಶೆ ಆಜ್ಞಾಪಿಸಿದಂತೆ ಅವರು ಕಾಲೇಬನಿಗೆ ಹೆಬ್ರೋನ್ ಪಟ್ಟಣವನ್ನು ಕೊಟ್ಟರು; ಅವನು ಅನಾಕನ ಮೂರು ಮಂದಿ ಮಕ್ಕಳನ್ನು ಅಲ್ಲಿಂದ ಅಟ್ಟಿಬಿಟ್ಟನು.",
			"21": "<sup>21</sup> ಯೆರೂಸಲೇಮಿನಲ್ಲಿದ್ದ ಯೆಬೂಸಿಯರನ್ನು ಹೊರಡಿಸುವದು ಬೆನ್ಯಾಮೀನ್ಯರಿಂದ ಆಗದೆ ಹೋಯಿತು. ಅವರು ಇಂದಿನವರೆಗೂ ಬೆನ್ಯಾಮೀನ್ಯರ ಸಂಗಡ ಯೆರೂಸಲೇಮಿನಲ್ಲಿಯೇ ವಾಸವಾಗಿದ್ದಾರೆ.",
			"22": "<sup>22</sup> ಇವರ ಹಾಗೆಯೇ ಯೋಸೇಫನ ವಂಶದವರೂ ಹೊರಟು ಬೇತೇಲಿಗೆ ಬಂದರು. ಯೆಹೋವನು ಅವರ ಸಂಗಡ ಇದ್ದನು.",
			"23": "<sup>23</sup> ಅವರು ಲೂಜ್ ಅನ್ನಿಸಿಕೊಳ್ಳುತ್ತಿದ್ದ ಬೇತೇಲ್ ಊರನ್ನು ಸಂಚರಿಸಿನೋಡುವದಕ್ಕೆ ಗೂಢಚಾರರನ್ನು ಕಳುಹಿಸಿದರು.",
			"24": "<sup>24</sup> ಅವರು ಆ ಊರೊಳಗಿಂದ ಬರುವ ಒಬ್ಬ ಮನುಷ್ಯನನ್ನು ಕಂಡು ಅವನಿಗೆ - ದಯವಿಟ್ಟು ಪಟ್ಟಣದೊಳಗೆ ನುಗ್ಗಬಹುದಾದ ದಾರಿಯನ್ನು ನಮಗೆ ತೋರಿಸು; ನಾವೂ ನಿನಗೆ ದಯೆ ತೋರಿಸುವೆವು ಎಂದು ಹೇಳಲು ಅವನು ಅಂಥ ದಾರಿಯನ್ನು ತೋರಿಸಿದನು.",
			"25": "<sup>25</sup> ಆಗ ಅವರು ಆ ಪಟ್ಟಣವನ್ನು ಕತ್ತಿಯಿಂದ ಸಂಹರಿಸಿದರು. ಆದರೆ ಆ ಮನುಷ್ಯನನ್ನೂ ಅವನ ಕುಟುಂಬವನ್ನೂ ಕಳುಹಿಸಿಬಿಟ್ಟರು.",
			"26": "<sup>26</sup> ಅವನು ಹಿತ್ತಿಯರ ದೇಶಕ್ಕೆ ಹೋಗಿ ಒಂದು ಪಟ್ಟಣವನ್ನು ಕಟ್ಟಿಕೊಂಡು ಅದಕ್ಕೆ ಲೂಜೆಂದು ಹೆಸರಿಟ್ಟನು; ಅದಕ್ಕೆ ಇಂದಿನವರೆಗೂ ಅದೇ ಹೆಸರಿರುತ್ತದೆ.",
			"27": "<sup>27</sup> ಮನಸ್ಸೆಯವರು ಬೇತ್‍ಷೆಯಾನ್, ತಾನಾಕ್, ದೋರ್, ಇಬ್ಲೆಯಾಮ್, ಮೆಗಿದ್ದೋ ಎಂಬ ಪಟ್ಟಣಗಳನ್ನೂ ಅವುಗಳ ಗ್ರಾಮಗಳನ್ನೂ ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳಲಿಲ್ಲ. ಆದದರಿಂದ ಕಾನಾನ್ಯರು ಆ ಪ್ರಾಂತಗಳಲ್ಲೇ ವಾಸಿಸುವದಕ್ಕೆ ದೃಢಮಾಡಿದರು.",
			"28": "<sup>28</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಬಲಗೊಂಡ ಮೇಲೆ ಅವರನ್ನು ದಾಸತ್ವಕ್ಕೆ ಹಚ್ಚಿದರೇ ಹೊರತು ಅಲ್ಲಿಂದ ಅಟ್ಟಿಬಿಡಲಿಲ್ಲ.",
			"29": "<sup>29</sup> ಎಫ್ರಾಯೀಮ್ಯರು ಗೆಜೆರಿನಲ್ಲಿದ್ದ ಕಾನಾನ್ಯರನ್ನು ಹೊರಡಿಸಿಬಿಡಲಿಲ್ಲ. ಆದದರಿಂದ ಅವರು ಅವರ ಮಧ್ಯದಲ್ಲೇ ವಾಸಿಸುವವರಾದರು.",
			"30": "<sup>30</sup> ಜೆಬುಲೂನ್ಯರು ಕಿಟ್ರೋನ್, ನಹಲೋಲ್ ಎಂಬ ಪಟ್ಟಣಗಳ ನಿವಾಸಿಗಳನ್ನು ಅಟ್ಟಿಬಿಡಲಿಲ್ಲ. ಆದರೆ ಆ ಕಾನಾನ್ಯರು ಅವರಿಗೆ ದಾಸರಾಗಿ ಅವರ ಮಧ್ಯದಲ್ಲೇ ವಾಸಮಾಡುವವರಾದರು.",
			"31": "<sup>31</sup> ಆಶೇರ್ಯರು ಅಕ್ಕೋ, ಚೀದೋನ್, ಅಹ್ಲಾಬ್, ಅಕ್ಜೀಬ್, ಹೆಲ್ಬಾ, ಅಫೀಕ್, ರೆಹೋಬ್ ಎಂಬ ಪಟ್ಟಣಗಳನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳಲಿಲ್ಲ;",
			"32": "<sup>32</sup> ಅವರು ಆ ದೇಶದ ಕಾನಾನ್ಯರನ್ನು ಹೊರಡಿಸದೆ ಅವರ ಮಧ್ಯದಲ್ಲೇ ವಾಸಮಾಡಿದರು.",
			"33": "<sup>33</sup> ನಫ್ತಾಲ್ಯರು ಬೇತ್‍ಷೆಮೆಷ್, ಬೇತನಾತ್ ಎಂಬ ಊರುಗಳನ್ನು ಸ್ವತಂತ್ರಿಸಿಕೊಳ್ಳದೆ ಅಲ್ಲಿಯ ನಿವಾಸಿಗಳಾದ ಕಾನಾನ್ಯರ ಮಧ್ಯದಲ್ಲೇ ವಾಸಮಾಡಿದರು. ಮತ್ತು ಆ ಬೇತ್‍ಷೆಮೆಷ್ ಬೇತನಾತ್ ಊರುಗಳ ಜನರು ಅವರಿಗೆ ದಾಸರಾದರು.",
			"34": "<sup>34</sup> ಇದಲ್ಲದೆ ಅಮೋರಿಯರು ದಾನ್ ಕುಲದವರನ್ನು ತಗ್ಗಿನ ಪ್ರದೇಶಕ್ಕೆ ಇಳಿಯಗೊಡದೆ ಹಿಂದಟ್ಟಿ ಬೆಟ್ಟಗಳಿಗೆ ಓಡಿಸಿಬಿಟ್ಟರು.",
			"35": "<sup>35</sup> ಹೀಗೆ ಅಮೋರಿಯರು ಹರ್‍ಹೆರೆಸ್, ಅಯ್ಯಾಲೋನ್, ಶಾಲ್ಬೀಮ್ ಎಂಬ ಊರುಗಳಲ್ಲೇ ವಾಸಿಸ ದೃಢಮಾಡಿದರು. ಆದರೆ ಯೋಸೇಫ್ಯರ ಹಸ್ತವು ಭಾರವಾದ ಮೇಲೆ ಅವರಿಗೆ ದಾಸರಾದರು.",
			"36": "<sup>36</sup> ಅಮೋರಿಯರ ಮೇರೆಯು ಅಕ್ರಬ್ಬೀಮ್ ಕಣಿವೆಯಲ್ಲಿರುವ ಬಂಡೆಯಿಂದ ಮೇಲಕ್ಕೆ ವಿಸ್ತರಿಸಿಕೊಂಡಿರುವದು."
		},
		"2": {
			"1": "<sup>1</sup> ಯೆಹೋವನ ದೂತನು ಗಿಲ್ಗಾಲಿನಿಂದ ಬೋಕೀಮಿಗೆ ಬಂದು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ - ನಾನು ನಿಮ್ಮನ್ನು ಐಗುಪ್ತದಿಂದ ಬರಮಾಡಿ ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ಪ್ರಮಾಣ ಮಾಡಿದ ದೇಶಕ್ಕೆ ಕರತಂದೆನು. ನಿಮ್ಮೊಡನೆ ಮಾಡಿಕೊಂಡ ಒಡಂಬಡಿಕೆಯನ್ನು ಎಂದೂ ಭಂಗಪಡಿಸುವದಿಲ್ಲವೆಂದು ನಾನು ಮಾತು ಕೊಟ್ಟಾಗ -",
			"2": "<sup>2</sup> ನೀವು ಈ ದೇಶದ ನಿವಾಸಿಗಳ ಸಂಗಡ ಒಡಂಬಡಿಕೆ ಮಾಡಿಕೊಳ್ಳಬಾರದು; ಅವರ ಯಜ್ಞವೇದಿಗಳನ್ನು ಕೆಡವಿಬಿಡಬೇಕು ಎಂದು ಆಜ್ಞಾಪಿಸಿದೆನು. ಆದರೆ ನೀವು ನನ್ನ ಮಾತನ್ನು ಕೇಳಲಿಲ್ಲ. ಹೀಗೆ ಯಾಕೆ ಮಾಡಿದಿರಿ?",
			"3": "<sup>3</sup> ನೀವು ಹೀಗೆ ಮಾಡುವದಾದರೆ ನಾನು ಈ ದೇಶ ನಿವಾಸಿಗಳನ್ನು ನಿಮ್ಮೆದುರಿನಿಂದ ಓಡಿಸಿ ಬಿಡುವದಿಲ್ಲ; ಅವರು ನಿಮ್ಮನ್ನು ಉಪದ್ರವಪಡಿಸುವರು; ಅವರ ದೇವತೆಗಳು ನಿಮಗೆ ಉರುಲಾಗುವವು ಎಂದು ಮೊದಲೇ ಹೇಳಿದೆನಲ್ಲಾ ಅಂದನು.",
			"4": "<sup>4</sup> ಯೆಹೋವನ ದೂತನು ಈ ಮಾತುಗಳನ್ನು ಹೇಳಿದಾಗ ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರೂ ಗಟ್ಟಿಯಾಗಿ ಅತ್ತರು.",
			"5": "<sup>5</sup> ಮತ್ತು ಆ ಸ್ಥಳಕ್ಕೆ ಬೋಕೀಮೆಂಬ ಹೆಸರಿಟ್ಟು ಅಲ್ಲಿಯೇ ಯೆಹೋವನಿಗೋಸ್ಕರ ಯಜ್ಞಮಾಡಿದರು.",
			"6": "<sup>6</sup> ಯೆಹೋಶುವನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಕಳುಹಿಸಿದ ಮೇಲೆ ಅವರು ತಮ್ಮ ತಮ್ಮ ಪಾಲಿಗೆ ಬಂದ ಪ್ರದೇಶಗಳಿಗೆ ಹೋಗಿ ಅವುಗಳನ್ನು ಸ್ವತಂತ್ರಿಸಿಕೊಂಡರು.",
			"7": "<sup>7</sup> ಯೆಹೋಶುವನ ದಿನಗಳಲ್ಲಿಯೂ ಅವನ ಕಾಲದಿಂದ ಇನ್ನೂ ಜೀವಿಸುತ್ತಾ ಯೆಹೋವನು ಇಸ್ರಾಯೇಲ್ಯರಿಗೋಸ್ಕರ ನಡಿಸಿದ ಮಹತ್ಕಾರ್ಯಗಳಿಗೆ ಸಾಕ್ಷಿಗಳಾಗಿದ್ದ ಹಿರಿಯರ ದಿನಗಳಲ್ಲಿಯೂ ಇಸ್ರಾಯೇಲ್ಯರು ಯೆಹೋವನನ್ನು ಸೇವಿಸುತ್ತಿದ್ದರು.",
			"8": "<sup>8</sup> ಯೆಹೋವನ ಸೇವಕನಾದ ನೂನನ ಮಗ ಯೆಹೋಶುವನು ನೂರಹತ್ತು ವರುಷದವನಾಗಿ ಮರಣಹೊಂದಿದನು.",
			"9": "<sup>9</sup> ಅವನನ್ನು ಅವನ ಸ್ವಾಸ್ತ್ಯಭೂಮಿಯಾದ ತಿಮ್ನತ್‍ಹೆರೆಸ್ ಎಂಬಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು; ಅದು ಎಫ್ರಾಯೀಮ್ ಪರ್ವತಪ್ರದೇಶದಲ್ಲಿರುವ ಗಾಷ್‍ಬೆಟ್ಟದ ಉತ್ತರದಿಕ್ಕಿನಲ್ಲಿರುತ್ತದೆ.",
			"10": "<sup>10</sup> ಅವನ ಕಾಲದವರೆಲ್ಲರೂ ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಿದ ಮೇಲೆ ಯೆಹೋವನನ್ನೂ ಆತನು ಇಸ್ರಾಯೇಲ್ಯರಿಗೋಸ್ಕರ ಮಾಡಿದ ಮಹತ್ಕಾರ್ಯಗಳನ್ನೂ ಅರಿಯದಿದ್ದ ಬೇರೊಂದು ಸಂತಾನವು ಹುಟ್ಟಿತು.",
			"11": "<sup>11</sup> ಈ ಇಸ್ರಾಯೇಲ್ಯರು ಬಾಳನ ಪ್ರತಿಮೆಗಳನ್ನು ಪೂಜಿಸಿ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಗಳಾದರು.",
			"12": "<sup>12</sup> ತಮ್ಮ ಪಿತೃಗಳನ್ನು ಐಗುಪ್ತದಿಂದ ಕರತಂದ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಬಿಟ್ಟು ಅನ್ಯದೇವತೆಗಳನ್ನು ಅಂದರೆ ತಮ್ಮ ಸುತ್ತಣ ಜನಾಂಗಗಳ ದೇವರುಗಳನ್ನು ಅವಲಂಬಿಸಿ ಅವುಗಳಿಗೆ ಅಡ್ಡಬಿದ್ದು ಯೆಹೋವನನ್ನು ರೇಗಿಸಿದರು.",
			"13": "<sup>13</sup> ಅವರು ಯೆಹೋವನನ್ನು ಬಿಟ್ಟು ಬಾಳ್ ಅಷ್ಟೋರೆತ್ ಎಂಬ ದೇವತೆಗಳನ್ನು ಪೂಜಿಸುವವರಾದದರಿಂದ",
			"14": "<sup>14</sup> ಯೆಹೋವನ ಕೋಪವು ಅವರ ಮೇಲೆ ಉರಿಯಹತ್ತಿತು. ಆತನು ಅವರನ್ನು ಸೂರೆಮಾಡುವವರ ಕೈಗೆ ಒಪ್ಪಿಸಲು ಅವರು ಅವರನ್ನು ಸೂರೆಮಾಡಿದರು. ಆತನು ಅವರನ್ನು ಸುತ್ತಣ ವೈರಿಗಳಿಗೆ ಮಾರಿ ಬಿಟ್ಟನು; ಅವರು ಆ ಶತ್ರುಗಳ ಮುಂದೆ ನಿಲ್ಲಲಾರದವರಾದರು.",
			"15": "<sup>15</sup> ಯೆಹೋವನು ತಾನು ಮೊದಲೇ ಆಣೆಯಿಟ್ಟು ಹೇಳಿದಂತೆ ಎಲ್ಲಿ ಹೋದರೂ ಆತನ ಕೈ ಅವರಿಗೆ ವಿರೋಧವಾಗಿಯೇ ಇತ್ತು; ಅವರಿಗೆ ಬಹುಸಂಕಟವಾಯಿತು.",
			"16": "<sup>16</sup> ಆಗ ಯೆಹೋವನು ಅವರನ್ನು ಸೂರೆ ಮಾಡುವವರ ಕೈಯಿಂದ ತಪ್ಪಿಸುವದಕ್ಕೋಸ್ಕರ ನ್ಯಾಯಸ್ಥಾಪಕರನ್ನು ಕಳುಹಿಸಿದರೂ",
			"17": "<sup>17</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಅವರ ಮಾತನ್ನೂ ಕೇಳದೆ ಅನ್ಯದೇವತೆಗಳನ್ನು ಪೂಜಿಸಿ ಅವುಗಳಿಗೆ ಅಡ್ಡಬಿದ್ದು ದೇವದ್ರೋಹಿಗಳಾದರು. ಯೆಹೋವನ ಆಜ್ಞೆಗಳನ್ನು ಕೈಕೊಳ್ಳುತ್ತಿದ್ದ ತಮ್ಮ ಪಿತೃಗಳ ಮಾರ್ಗವನ್ನು ಬೇಗನೆ ಬಿಟ್ಟುಬಿಟ್ಟರು; ಅವರಂತೆ ನಡೆಯಲೇ ಇಲ್ಲ.",
			"18": "<sup>18</sup> ವೈರಿಗಳ ಹಿಂಸೆಯನ್ನು ತಾಳಲಾರದ ಇಸ್ರಾಯೇಲ್ಯರ ಗೋಳಾಟವನ್ನು ಯೆಹೋವನು ಕೇಳಿ ಕನಿಕರಪಟ್ಟು ನ್ಯಾಯಸ್ಥಾಪಕರನ್ನು ಎಬ್ಬಿಸಿ ಅವರ ಜೀವಮಾನವೆಲ್ಲಾ ಅವರ ಸಂಗಡ ಇದ್ದು ಅವರ ಮೂಲಕವಾಗಿ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಶತ್ರುಗಳಿಂದ ಬಿಡಿಸಿದನು.",
			"19": "<sup>19</sup> ಅಂಥ ನ್ಯಾಯಸ್ಥಾಪಕರು ತೀರಿಹೋದನಂತರ ಇಸ್ರಾಯೇಲ್ಯರು ತಿರುಗಿಕೊಂಡು ತಮ್ಮ ಹಿರಿಯರಿಗಿಂತಲೂ ಭ್ರಷ್ಟರಾಗಿ ಅನ್ಯದೇವತೆಗಳನ್ನು ಅವಲಂಬಿಸಿ ಸೇವಿಸಿ ಅವುಗಳಿಗೆ ಅಡ್ಡಬಿದ್ದರು; ಅವರು ಈ ತಮ್ಮ ದುರ್ಮಾರ್ಗವನ್ನೂ ಮೊಂಡತನವನ್ನೂ ಬಿಡಲೇ ಇಲ್ಲ.",
			"20": "<sup>20</sup> ಆದದರಿಂದ ಯೆಹೋವನ ಕೋಪವು ಇಸ್ರಾಯೇಲ್ಯರ ಮೇಲೆ ಉರಿಯಹತ್ತಿತು; ಆತನು - ಈ ಜನರ ಪಿತೃಗಳಿಗೆ ನಾನು ಕೊಟ್ಟ ನಿಬಂಧನೆಯನ್ನು ಇವರು ಕೈಕೊಳ್ಳಲಿಲ್ಲ; ನನ್ನ ಮಾತಿಗೆ ಕಿವಿಗೊಡಲಿಲ್ಲ.",
			"21": "<sup>21</sup> ಹೀಗಿರುವದರಿಂದ ಯೆಹೋಶುವನು ಸಾಯುವ ಮೊದಲು ಹೊರಡಿಸದೆ ಬಿಟ್ಟ ಜನಾಂಗಗಳಲ್ಲಿ ನಾನೂ ಒಂದನ್ನಾದರೂ ಹೊರಡಿಸುವದಿಲ್ಲ.",
			"22": "<sup>22</sup> ಇವರೂ ತಮ್ಮ ಹಿರಿಯರಂತೆ ಜಾಗರೂಕತೆಯಿಂದ ಯೆಹೋವನಾದ ನನ್ನ ಮಾರ್ಗದಲ್ಲಿ ನಡೆಯುವರೋ ಇಲ್ಲವೋ ಎಂದು ಈ ಜನಾಂಗಗಳ ಮೂಲಕವಾಗಿ ಪರೀಕ್ಷಿಸುವೆನು ಅಂದನು.",
			"23": "<sup>23</sup> ಇದರಿಂದ ಯೆಹೋವನು ಯೆಹೋಶುವನ ಕೈಗೆ ಒಪ್ಪಿಸದೆ ಉಳಿಸಿದ್ದ ಜನಾಂಗಗಳನ್ನು ಬೇಗನೆ ಅಟ್ಟಿಬಿಡಲಿಲ್ಲ."
		},
		"3": {
			"1": "<sup>1</sup> ಕಾನಾನ್ಯರೊಡನೆ ನಡೆದ ಯುದ್ಧಗಳಲ್ಲಿ ಒಂದನ್ನೂ ನೋಡದೆ ಇದ್ದ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಪರೀಕ್ಷಿಸುವದಕ್ಕೂ",
			"2": "<sup>2</sup> ಇಸ್ರಾಯೇಲ್ ಸಂತತಿಯವರಲ್ಲಿ ಯುದ್ಧವಿದ್ಯೆಯನ್ನು ಅರಿಯದವರಿಗೆ ಅದನ್ನು ಕಲಿಸಿಕೊಡುವದಕ್ಕೂ ಯೆಹೋವನು ಉಳಿಸಿದ ಜನಾಂಗಗಳವರು ಯಾರಂದರೆ -",
			"3": "<sup>3</sup> ಐದು ಮಂದಿ ಫಿಲಿಷ್ಟಿಯ ಪ್ರಭುಗಳು, ಸರ್ವ ಕಾನಾನ್ಯರು, ಚೀದೋನ್ಯರು, ಲೆಬನೋನ್ ಪರ್ವತಾವಳಿಯಲ್ಲಿ ಬಾಳ್‍ಹೆರ್ಮೊನ್ ಬೆಟ್ಟದಿಂದ ಹಮಾತಿನ ದಾರಿಯವರೆಗೆ ವಾಸವಾಗಿರುವ ಹಿವ್ವಿಯರು ಇವರೇ.",
			"4": "<sup>4</sup> ತಾನು ಮೋಶೆಯ ಮೂಲಕವಾಗಿ ಇವರ ಪಿತೃಗಳಿಗೆ ವಿಧಿಸಿದ ಆಜ್ಞೆಗಳನ್ನು ಇಸ್ರಾಯೇಲ್ಯರು ಕೈಕೊಂಡು ನಡೆಯುವರೋ ಇಲ್ಲವೋ ಎಂಬದನ್ನು ಪರೀಕ್ಷಿಸಿ ತಿಳುಕೊಳ್ಳುವದಕ್ಕೋಸ್ಕರ ಯೆಹೋವನು ಅವರನ್ನು ಉಳಿಸಿದನು.",
			"5": "<sup>5</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಕಾನಾನ್ಯ, ಹಿತ್ತಿಯ, ಅಮೋರಿಯ, ಪೆರಿಜ್ಜೀಯ, ಹಿವ್ವಿಯ, ಯೆಬೂಸಿಯ ಎಂಬೀ ಜನಾಂಗಗಳ ಮಧ್ಯದಲ್ಲಿ ವಾಸಮಾಡುತ್ತಾ",
			"6": "<sup>6</sup> ಅವರ ಕನ್ಯೆಯರನ್ನು ತಾವು ತಂದು ತಮ್ಮ ಕನ್ಯೆಯರನ್ನು ಅವರ ಕುಮಾರರಿಗೆ ಕೊಟ್ಟು ಅವರ ದೇವತೆಗಳನ್ನು ಸೇವಿಸಿದರು.",
			"7": "<sup>7</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಬಾಳ್, ಅಶೇರ ಎಂಬ ದೇವತೆಗಳನ್ನು ಪೂಜಿಸಿ ತಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಮರೆತುಬಿಟ್ಟು ಆತನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಗಳಾದರು.",
			"8": "<sup>8</sup> ಆದದರಿಂದ ಆತನು ಇಸ್ರಾಯೇಲ್ಯರ ಮೇಲೆ ಕೋಪಗೊಂಡು ಅವರನ್ನು ಎರಡು ನದಿಗಳ ಮಧ್ಯದಲ್ಲಿರುವ ಅರಾಮ್ ರಾಜ್ಯದ ಅರಸನಾದ ಕೂಷನ್ ರಿಷಾತಯಿಮ್ ಎಂಬವನಿಗೆ ಮಾರಿಬಿಟ್ಟನು. ಅವರು ಎಂಟು ವರುಷಗಳವರೆಗೆ ಅವನಿಗೆ ದಾಸರಾಗಿದ್ದರು.",
			"9": "<sup>9</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಟ್ಟಾಗ ಆತನು ಅವರನ್ನು ಬಿಡಿಸುವದಕ್ಕೋಸ್ಕರ ರಕ್ಷಕನನ್ನು ಎಬ್ಬಿಸಿದನು; ಕಾಲೇಬನ ತಮ್ಮನೂ ಕೆನಜನ ಮಗನೂ ಆದ ಒತ್ನೀಯೇಲನೇ ಆ ರಕ್ಷಕನು.",
			"10": "<sup>10</sup> ಯೆಹೋವನ ಆತ್ಮವು ಅವನ ಮೇಲೆ ಬಂದದರಿಂದ ಅವನು ಇಸ್ರಾಯೇಲ್ಯರ ನ್ಯಾಯಸ್ಥಾಪನೆಗೋಸ್ಕರ ಯುದ್ಧಕ್ಕೆ ಹೊರಟನು. ಯೆಹೋವನು ಎರಡು ನದಿಗಳ ಮಧ್ಯದಲ್ಲಿರುವ ಅರಾಮ್ ರಾಜ್ಯದ ಅರಸನಾದ ಕೂಷನ್ ರಿಷಾತಯಿಮನನ್ನು ಅವನ ಕೈಗೆ ಒಪ್ಪಿಸಿಕೊಟ್ಟದರಿಂದ ಅವನು ಅವನನ್ನು ಪೂರ್ಣವಾಗಿ ಸೋಲಿಸಿಬಿಟ್ಟನು.",
			"11": "<sup>11</sup> ದೇಶದಲ್ಲಿ ನಾಲ್ವತ್ತು ವರುಷಗಳ ಪರ್ಯಂತರ ಸಮಾಧಾನವಿತ್ತು. ತರುವಾಯ ಕೆನಜನ ಮಗನಾದ ಒತ್ನೀಯೇಲನು ಮರಣಹೊಂದಿದನು.",
			"12": "<sup>12</sup> ಇಸ್ರಾಯೇಲ್ಯರು ತಿರಿಗಿ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಗಳಾದರು; ಆತನು ಅವರ ದ್ರೋಹದ ನಿಮಿತ್ತ ಮೋವಾಬ್ಯರ ಅರಸನಾದ ಎಗ್ಲೋನನನ್ನು ಅವರಿಗೆ ವಿರೋಧವಾಗಿ ಬಲಪಡಿಸಿದನು.",
			"13": "<sup>13</sup> ಇವನು ಅಮ್ಮೋನಿಯರನ್ನೂ ಅಮಾಲೇಕ್ಯರನ್ನೂ ಕೂಡಿಸಿಕೊಂಡು ಹೊರಟು ಬಂದು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಸೋಲಿಸಿ ಖರ್ಜೂರನಗರವನ್ನು ಹಿಡಿದನು.",
			"14": "<sup>14</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಅವನಿಗೆ ಹದಿನೆಂಟು ವರುಷ ದಾಸರಾಗಿದ್ದರು.",
			"15": "<sup>15</sup> ಅವರು ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಡಲು ಆತನು ಅವರನ್ನು ರಕ್ಷಿಸುವದಕ್ಕೋಸ್ಕರ ಬೆನ್ಯಾಮೀನ್ ಕುಲದ ಗೇರನ ಮಗನಾದ ಏಹೂದನನ್ನು ಎಬ್ಬಿಸಿದನು. ಅವನು ಎಡಚನಾಗಿದ್ದನು. ಇಸ್ರಾಯೇಲ್ಯರು ಅವನ ಮೂಲಕವಾಗಿ ಮೋವಾಬ್ಯರ ಅರಸನಾದ ಎಗ್ಲೋನನಿಗೆ ಕಪ್ಪವನ್ನು ಕಳುಹಿಸಿದರು.",
			"16": "<sup>16</sup> ಅವನು ತನಗೋಸ್ಕರ ಒಂದು ಗುದ್ದುಮೊಳ ಉದ್ದವಾದ ಇಬ್ಬಾಯಿಕತ್ತಿಯನ್ನು ಮಾಡಿ ಅದನ್ನು ಬಟ್ಟೆಗಳ ಕೆಳಗೆ ಬಲಗಡೆಯ ಸೊಂಟಕ್ಕೆ ಕಟ್ಟಿಕೊಂಡು ಹೋಗಿ",
			"17": "<sup>17</sup> ಮೋವಾಬ್ಯರ ಅರಸನಾದ ಎಗ್ಲೋನನಿಗೆ ಕಪ್ಪವನ್ನು ಒಪ್ಪಿಸಿದನು. ಎಗ್ಲೋನನು ಬಲು ಕೊಬ್ಬಿದವನು.",
			"18": "<sup>18</sup> ಏಹೂದನು ಕಪ್ಪವನ್ನು ಒಪ್ಪಿಸಿದ ಮೇಲೆ ಅದನ್ನು ಹೊತ್ತುಕೊಂಡು ಬಂದ ಆಳುಗಳನ್ನು ಕಳುಹಿಸಿಬಿಟ್ಟು",
			"19": "<sup>19</sup> ತಾನು ಗಿಲ್ಗಾಲಿನಲ್ಲಿರುವ ವಿಗ್ರಹಸ್ಥಳದಿಂದ ಹಿಂದಿರುಗಿ ಎಗ್ಲೋನನ ಬಳಿಗೆ ಬಂದು - ಅರಸೇ, ನಿನಗೆ ತಿಳಿಸತಕ್ಕದ್ದೊಂದು ರಹಸ್ಯವದೆ ಅಂದನು. ಆಗ ಅರಸನು ನಿಶ್ಶಬ್ದ ಅನ್ನಲು ಅವನ ಸೇವಕರೆಲ್ಲರೂ ಹೊರಗೆ ಹೋದರು.",
			"20": "<sup>20</sup> ಅರಸನು ತನ್ನ ತಂಪಾದ ಮೇಲುಪ್ಪರಿಗೆಯಲ್ಲಿ ಒಬ್ಬನೇ ಕೂತುಕೊಂಡಿದ್ದಾಗ ಏಹೂದನು ಅವನ ಬಳಿಗೆ ಹೋಗಿ - ನಿನಗೆ ಹೇಳಬೇಕಾದದ್ದೊಂದು ದೇವೋಕ್ತಿಯಿದೆ ಅನ್ನಲು ಅವನು ತನ್ನ ಸಿಂಹಾಸನದಿಂದೆದ್ದನು.",
			"21": "<sup>21</sup> ಆಗ ಏಹೂದನು ಎಡಗೈ ಚಾಚಿ ಬಲಗಡೆಯ ಸೊಂಟಕ್ಕೆ ಕಟ್ಟಿದ್ದ ಕತ್ತಿಯನ್ನು ಹಿರಿದು ಅವನ ಹೊಟ್ಟೆಯಲ್ಲಿ ತಿವಿದನು.",
			"22": "<sup>22</sup> ಅಲಗೂ ಹಿಡಿಯೂ ಹೊಟ್ಟೆಯೊಳಗೆ ಹೊಕ್ಕವು. ಅವನು ಕತ್ತಿಯನ್ನು ಹೊರಗೆ ತೆಗೆಯದ್ದರಿಂದ ಕೊಬ್ಬು ಅಲಗನ್ನು ಸುತ್ತಿಕೊಂಡಿತು; ಮಲವು ಹೊರಗೆ ಬಂದಿತು.",
			"23": "<sup>23</sup> ಏಹೂದನು ಪಡಸಾಲೆಗೆ ಬಂದು ಆ ಮೇಲುಪ್ಪರಿಗೆಯ ಕದವನ್ನು ಮುಚ್ಚಿ ಬೀಗಹಾಕಿ ಹೊರಟು ಹೋದನು.",
			"24": "<sup>24</sup> ತರುವಾಯ ಸೇವಕರು ಅಲ್ಲಿ ಬಂದು ಬಾಗಲಿಗೆ ಬೀಗಹಾಕಿರುವದನ್ನು ಕಂಡು - ಅರಸನು ತಂಪಾದ ಕೋಣೆಗೆ ಸೇರಿದ ಪಾಯಖಾನೆಗೆ ಹೋಗಿರಬೇಕು ಅಂದುಕೊಂಡು",
			"25": "<sup>25</sup> ತಮಗೆ ಬೇಸರವಾಗುವ ತನಕ ಕಾಯುತ್ತಾ ಇದ್ದರು. ಆದರೂ ಕದಗಳು ತೆರೆಯಲ್ಪಡದೆ ಇರುವದನ್ನು ನೋಡಿ ಬೀಗದ ಕೈಯನ್ನು ತೆಗೆದುಕೊಂಡು ಬಾಗಲನ್ನು ತೆರೆಯಲು ಇಗೋ ಅವರ ಒಡೆಯನು ಸತ್ತು ಬಿದ್ದಿದ್ದನು.",
			"26": "<sup>26</sup> ಅವರು ತಡಮಾಡುವಷ್ಟರಲ್ಲಿ ಏಹೂದನು ತಪ್ಪಿಸಿಕೊಂಡು ವಿಗ್ರಹಗಳಿದ್ದ ಸ್ಥಳದಲ್ಲಿ ಹೊಳೆದಾಟಿ ಸೆಯೀರಾ ಎಂಬಲ್ಲಿಗೆ ಬಂದು",
			"27": "<sup>27</sup> ಎಫ್ರಾಯೀಮ್ ಪರ್ವತಪ್ರದೇಶದಲ್ಲಿ ಕೊಂಬನ್ನು ಊದಿದನು. ಆಗ ಇಸ್ರಾಯೇಲ್ಯರು ಅವನ ಬಳಿಗೆ ಬಂದು ಗಟ್ಟಾ ಇಳಿದು ಅವನ ಸಂಗಡ ಹೊರಟರು.",
			"28": "<sup>28</sup> ಅವನು ಅವರ ನಾಯಕನಾಗಿ - ನನ್ನನ್ನು ಹಿಂಬಾಲಿಸಿರಿ; ಯೆಹೋವನು ನಿಮ್ಮ ಶತ್ರುಗಳಾದ ಮೋವಾಬ್ಯರನ್ನು ನಿಮ್ಮ ಕೈಗೆ ಒಪ್ಪಿಸಿದ್ದಾನೆಂದು ಹೇಳಲು ಅವರು ಅವನನ್ನು ಹಿಂಬಾಲಿಸಿ ಮೋವಾಬಿಗೆ ಹೋಗುವ ಹಾಯಗಡಗಳನ್ನೆಲ್ಲಾ ಹಿಡಿದರು; ಯಾರನ್ನೂ ದಾಟಗೊಡಲಿಲ್ಲ.",
			"29": "<sup>29</sup> ಅವರು ಆ ಕಾಲದಲ್ಲಿ ಪುಷ್ಟರೂ ಪರಾಕ್ರಮಿಗಳೂ ಆದ ಸುಮಾರು ಹತ್ತು ಸಾವಿರ ಮಂದಿ ಮೋವಾಬ್ಯರನ್ನು ಒಬ್ಬನೂ ತಪ್ಪಿಸಿಕೊಳ್ಳದಂತೆ ಹತಮಾಡಿದರು.",
			"30": "<sup>30</sup> ಆ ದಿವಸದಲ್ಲಿ ಮೋವಾಬ್ಯರು ಇಸ್ರಾಯೇಲ್ಯರಿಂದ ತಗ್ಗಿಸಲ್ಪಟ್ಟರು; ದೇಶದಲ್ಲಿ ಎಂಭತ್ತು ವರುಷ ಸಮಾಧಾನವಿತ್ತು.",
			"31": "<sup>31</sup> ಏಹೂದನ ತರುವಾಯ ಅನಾತನ ಮಗನಾದ ಶಮ್ಗರನು ಎದ್ದು ಎತ್ತಿನ ಮುಳ್ಳುಗೋಲಿನಿಂದ ಆರುನೂರು ಮಂದಿ ಫಿಲಿಷ್ಟಿಯರನ್ನು ಹತಮಾಡಿ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ರಕ್ಷಿಸಿದನು."
		},
		"4": {
			"1": "<sup>1</sup> ಏಹೂದನು ಮರಣಹೊಂದಿದನಂತರ ಇಸ್ರಾಯೇಲ್ಯರು ತಿರಿಗಿ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಗಳಾದದರಿಂದ",
			"2": "<sup>2</sup> ಆತನು ಅವರನ್ನು ಹಾಚೋರಿನಲ್ಲಿ ಆಳುತ್ತಿದ್ದ ಕಾನಾನ್ಯರಾಜನಾದ ಯಾಬೀನನಿಗೆ ಮಾರಿಬಿಟ್ಟನು. ಮ್ಲೇಚ್ಫರ ಹರೋಷೆತ್ ಎಂಬ ಪಟ್ಟಣದಲ್ಲಿ ವಾಸವಾಗಿದ್ದ ಸೀಸೆರನು ಅವನ ಸೇನಾಪತಿಯಾಗಿದ್ದನು.",
			"3": "<sup>3</sup> ಒಂಭೈನೂರು ಕಬ್ಬಿಣದ ರಥಗಳುಳ್ಳ ಇವನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಇಪ್ಪತ್ತು ವರುಷಗಳ ತನಕ ಬಲವಾಗಿ ಬಾಧಿಸುತ್ತಿರಲು ಅವರು ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಟ್ಟರು.",
			"4": "<sup>4</sup> ಆ ಕಾಲದಲ್ಲಿ ಲಪ್ಪೀದೋತನ ಹೆಂಡತಿಯಾದ ದೆಬೋರಳೆಂಬ ಪ್ರವಾದಿನಿಯು ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ನ್ಯಾಯತೀರಿಸುತ್ತಿದ್ದಳು.",
			"5": "<sup>5</sup> ಆಕೆಯು ಎಫ್ರಾಯೀಮ್ ಬೆಟ್ಟದ ಸೀಮೆಯಲ್ಲಿ ರಾಮಕ್ಕೂ ಬೇತೇಲಿಗೂ ಮಧ್ಯದಲ್ಲಿರುವ ದೆಬೋರಳ ಖರ್ಜೂರವೃಕ್ಷವೆಂದು ಹೆಸರುಗೊಂಡ ಮರದ ಕೆಳಗೆ ಆಸೀನಳಾಗಿರುವಲ್ಲಿ ಇಸ್ರಾಯೇಲ್ಯರು ನ್ಯಾಯ ನಿರ್ಣಯಕ್ಕೋಸ್ಕರ ಆಕೆಯ ಬಳಿಗೆ ಬರುತ್ತಿದ್ದರು.",
			"6": "<sup>6</sup> ಆಕೆಯು ನಫ್ತಾಲಿ ದೇಶದ ಕೆದೆಷ್ ಊರಿನಲ್ಲಿದ್ದ ಅಬೀನೋವಮನ ಮಗನಾದ ಬಾರಾಕನನ್ನು ಕರೇಕಳುಹಿಸಿ ಅವನಿಗೆ - ನಿಶ್ಚಯವಾಗಿ ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರಾದ ಯೆಹೋವನು ನಿನಗೆ - ಎದ್ದು ನಫ್ತಾಲಿ ಜೆಬುಲೂನ್ ಕುಲಗಳಿಂದ ಹತ್ತು ಸಾವಿರ ಜನರನ್ನು ಕೂಡಿಸಿಕೊಂಡು ತಾಬೋರ್ ಬೆಟ್ಟಕ್ಕೆ ಹೋಗು;",
			"7": "<sup>7</sup> ನಾನು ಯಾಬೀನನ ಸೇನಾಪತಿಯಾದ ಸೀಸೆರನನ್ನೂ ಅವನ ಸೈನ್ಯರಥಗಳನ್ನೂ ನಿನ್ನ ಬಳಿಗೆ ಕೀಷೋನ್ ಹಳ್ಳಕ್ಕೆ ಎಳೆದುಕೊಂಡು ಬಂದು ನಿನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸುವೆನು ಎಂದು ಆಜ್ಞಾಪಿಸಿದ್ದಾನೆ ಅಂದಳು.",
			"8": "<sup>8</sup> ಬಾರಾಕನು ಆಕೆಗೆ - ನೀನು ನನ್ನ ಸಂಗಡ ಬರುವದಾದರೆ ಹೋಗುವೆನು; ಇಲ್ಲವಾದರೆ ಹೋಗುವದಿಲ್ಲ ಅನ್ನಲು",
			"9": "<sup>9</sup> ಆಕೆಯು - ನಾನು ಹೇಗೂ ನಿನ್ನ ಸಂಗಡ ಬರುವೆನು; ಆದರೆ ಈ ಯುದ್ಧಪ್ರಯಾಣದಲ್ಲುಂಟಾಗುವ ಮಾನವು ನಿನಗಲ್ಲ. ಯೆಹೋವನು ಸೀಸೆರನನ್ನು ಒಬ್ಬ ಸ್ತ್ರೀಗೆ ಒಪ್ಪಿಸಿಕೊಡುವನು ಎಂದು ಹೇಳಿ ಎದ್ದು ಬಾರಾಕನೊಡನೆ ಕೆದೆಷಿಗೆ ಹೋದಳು.",
			"10": "<sup>10</sup> ಮತ್ತು ಬಾರಾಕನು ಜೆಬೂಲೂನ್ಯರನ್ನೂ ನಫ್ತಾಲ್ಯರನ್ನೂ ಕೆದೆಷಿಗೆ ಕರಿಸಿದನು; ಅವರಲ್ಲಿ ಹತ್ತು ಸಾವಿರ ಮಂದಿ ಅವನ ಹೆಜ್ಜೆ ಹಿಡಿದು ಯುದ್ಧಕ್ಕೆ ಹೋದರು; ದೆಬೋರಳೂ ಹೋದಳು.",
			"11": "<sup>11</sup> (ಕೇನ್ಯನಾದ ಹೆಬೆರನು ಮೋಶೆಯ ಮಾವನಾದ ಹೋಬಾಬನ ವಂಶದ ಉಳಿದ ಕೇನ್ಯರನ್ನು ಅಗಲಿ ಕೆದೆಷಿನ ಹತ್ತಿರ ಇರುವ ಚಾನನ್ನೀಮೆಂಬ ಊರಿನ ಏಲೋನ್ ವೃಕ್ಷದವರೆಗೆ ಬಂದು ಅಲ್ಲಿ ಗುಡಾರಹಾಕಿಕೊಂಡಿದ್ದನು.)",
			"12": "<sup>12</sup> ಅಬೀನೋವಮನ ಮಗನಾದ ಬಾರಾಕನು ತಾಬೋರ್ ಬೆಟ್ಟವನ್ನೇರಿ ಬಂದಿದ್ದಾನೆಂಬ ವರ್ತಮಾನವು ಸೀಸೆರನಿಗೆ ಮುಟ್ಟಿದಾಗ",
			"13": "<sup>13</sup> ಅವನು ತನ್ನ ಒಂಭೈನೂರು ಕಬ್ಬಿಣದ ರಥಗಳನ್ನೂ ಎಲ್ಲಾ ಸೈನ್ಯವನ್ನೂ ತೆಗೆದುಕೊಂಡು ಮ್ಲೇಚ್ಫರ ಹರೋಷೆತಿನಿಂದ ಕೀಷೋನ್ ಹಳ್ಳಕ್ಕೆ ಬಂದನು.",
			"14": "<sup>14</sup> ಆಗ ದೆಬೋರಳು ಬಾರಾಕನಿಗೆ - ಏಳು, ಯೆಹೋವನು ಸೀಸೆರನನ್ನು ನಿನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸುವ ದಿನವು ಇದೇ; ನಿಶ್ಚಯವಾಗಿ ಆತನು ತಾನೇ ನಿನ್ನ ಮುಂದಾಗಿ ಯುದ್ಧಕ್ಕೆ ಹೊರಡುವನು ಅನ್ನಲು ಬಾರಾಕನು ಬೇಗನೆ ಹತ್ತು ಸಾವಿರ ಜನರ ಸಹಿತವಾಗಿ ತಾಬೋರ್ ಬೆಟ್ಟದಿಂದಿಳಿದನು.",
			"15": "<sup>15</sup> ಯೆಹೋವನು ಸೀಸೆರನನ್ನೂ ಅವನ ಎಲ್ಲಾ ಸೈನ್ಯರಥಗಳನ್ನೂ ಬಾರಾಕನ ಮುಂದೆ ಗಲಿಬಿಲಿಪಡಿಸಿ ಕತ್ತಿಗೆ ಬಲಿಕೊಟ್ಟನು. ಸೀಸೆರನು ರಥದಿಂದ ಹಾರಿ ಓಡಿಹೋದನು.",
			"16": "<sup>16</sup> ಬಾರಾಕನು ಅವನ ಸೈನ್ಯರಥಗಳನ್ನು ಮ್ಲೇಚ್ಫರ ಹರೋಷೆತಿನವರೆಗೂ ಹಿಂದಟ್ಟಿದನು. ಸೀಸೆರನ ಸೈನ್ಯದವರೆಲ್ಲಾ ಕತ್ತಿಯಿಂದ ಹತರಾದರು; ಒಬ್ಬನೂ ಉಳಿಯಲಿಲ್ಲ.",
			"17": "<sup>17</sup> ಹಾಚೋರಿನ ಅರಸನಾದ ಯಾಬೀನನಿಗೂ ಕೇನ್ಯನಾದ ಹೆಬೆರನ ಮನೆಯವರಿಗೂ ಸಮಾಧಾನವಿದ್ದದರಿಂದ ಸೀಸೆರನು ಕಾಲುನಡೆಯಾಗಿ ಕೇನ್ಯನಾದ ಹೆಬೆರನ ಹೆಂಡತಿಯಾದ ಯಾಯೇಲಳ ಗುಡಾರದ ಕಡೆಗೆ ಹೋದನು.",
			"18": "<sup>18</sup> ಯಾಯೇಲಳು ಹೊರಗೆ ಹೋಗಿ ಸೀಸೆರನನ್ನು ಎದುರುಗೊಂಡು ಅವನಿಗೆ - ಸ್ವಾಮೀ, ಒಳಗೆ ಬನ್ನಿರಿ; ಹೆದರಬೇಡಿರಿ, ನಮ್ಮಲ್ಲಿ ಬನ್ನಿರಿ ಅನ್ನಲು ಅವನು ಗುಡಾರದೊಳಗೆ ಹೋದನು. ಆಗ ಆಕೆಯು ಅವನನ್ನು ಕಂಬಳಿಯಿಂದ ಮುಚ್ಚಿದಳು.",
			"19": "<sup>19</sup> ಅವನು ಆಕೆಗೆ - ದಯವಿಟ್ಟು ಸ್ವಲ್ಪ ನೀರನ್ನು ಕೊಡು, ನನಗೆ ಬಹಳ ದಾಹವಾಗಿದೆ ಅಂದನು;",
			"20": "<sup>20</sup> ಆಕೆಯು ಬುದ್ದಲಿಯನ್ನು ಬಿಚ್ಚಿ ಹಾಲನ್ನು ಕುಡಿಯಕೊಟ್ಟು ಅವನನ್ನು ತಿರಿಗಿ ಮುಚ್ಚಿದಳು. ಅವನು ಆಕೆಗೆ - ನೀನು ಗುಡಾರದ ಬಾಗಲಲ್ಲೇ ನಿಂತಿರು; ಯಾರಾದರೂ ಬಂದು ಇಲ್ಲಿ ಒಬ್ಬ ಮನುಷ್ಯನಿರುತ್ತಾನೋ ಎಂದು ವಿಚಾರಿಸಿದರೆ ಇಲ್ಲ ಅನ್ನು ಎಂದು ಹೇಳಿದನು.",
			"21": "<sup>21</sup> ಹೆಬೆರನ ಹೆಂಡತಿಯಾದ ಯಾಯೇಲಳು ಕೈಯಲ್ಲಿ ಗುಡಾರದ ಗೂಟವನ್ನೂ ಒಂದು ಕೊಡತಿಯನ್ನೂ ತೆಗೆದುಕೊಂಡು ಅವನು ಆಯಾಸದಿಂದ ಗಾಢನಿದ್ರೆಯಲ್ಲಿದ್ದಾಗ ಮೆಲ್ಲಗೆ ಹತ್ತಿರ ಹೋಗಿ ಅವನ ಕಣತಲೆಯಲ್ಲಿ ಆ ಗೂಟವನ್ನು ಹೊಡೆದು ನೆಲಕ್ಕೆ ನಾಟಿಸಿದಳು; ಅವನು ಸತ್ತನು.",
			"22": "<sup>22</sup> ಅದೇ ಕ್ಷಣದಲ್ಲಿ ಸೀಸೆರನನ್ನು ಹಿಂದಟ್ಟುತ್ತಿದ್ದ ಬಾರಾಕನು ಅಲ್ಲಿ ಬಂದನು. ಯಾಯೇಲಳು ಹೊರಗೆ ಹೋಗಿ ಅವನನ್ನು ಎದುರುಗೊಂಡು - ಬಾ, ನೀನು ಹುಡುಕುವ ಮನುಷ್ಯನನ್ನು ತೋರಿಸುತ್ತೇನೆಂದು ಹೇಳಲು ಅವನು ಒಳಗೆ ಹೋಗಿ ಸೀಸೆರನು ಸತ್ತು ಬಿದ್ದದ್ದನ್ನು ಕಂಡನು. ಅವನ ತಲೆಯಲ್ಲಿ ಗೂಟವು ಜಡಿಯಲ್ಪಟ್ಟಿತ್ತು.",
			"23": "<sup>23</sup> ಆ ದಿನದಲ್ಲಿ ದೇವರು ಕಾನಾನ್ಯರಾಜನಾದ ಯಾಬೀನನನ್ನು ಇಸ್ರಾಯೇಲ್ಯರ ಮುಂದೆ ತಗ್ಗಿಸಿದನು.",
			"24": "<sup>24</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಹಸ್ತವು ಹೆಚ್ಚು ಹೆಚ್ಚು ಬಲಗೊಂಡದರಿಂದ ಕಾನಾನ್ಯರ ಅರಸನಾದ ಯಾಬೀನನು ಪೂರ್ಣವಾಗಿ ಸೋತು ನಿರ್ನಾಮವಾದನು."
		},
		"5": {
			"1": "<sup>1</sup> ಆ ದಿನದಲ್ಲಿ ದೆಬೋರಳೂ ಅಬೀನೋವಮನ ಮಗನಾದ ಬಾರಾಕನೂ ಹಾಡಿದ್ದೇನಂದರೆ -",
			"2": "<sup>2</sup> ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಸೇನಾನಾಯಕರು ಎದ್ದಿದ್ದಾರೆ; ಜನರು ಸ್ವೇಚ್ಫೆಯಿಂದ ಸೈನ್ಯದಲ್ಲಿ ಸೇರಿದ್ದಾರೆ, ಯೆಹೋವನನ್ನು ಕೊಂಡಾಡಿರಿ.",
			"3": "<sup>3</sup> ಅರಸರೇ, ಕೇಳಿರಿ; ಪ್ರಭುಗಳೇ, ಕಿವಿಗೊಡಿರಿ; ನಾನು ಯೆಹೋವನನ್ನು ಕೀರ್ತಿಸುವೆನು; ಇಸ್ರಾಯೇಲ್‍ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಗಾನ ಮಾಡುವೆನು.",
			"4": "<sup>4</sup> ಯೆಹೋವನೇ, ನೀನು ಹೊರಟು ಎದೋಮ್ಯರ ಪ್ರಾಂತವಾದ ಸೇಯೀರಿನ ಮಾರ್ಗವಾಗಿ ಪಯಣಮಾಡುತ್ತಾ ಬರುವಾಗ ಭೂಲೋಕವು ಕಂಪಿಸಿತು, ಆಕಾಶದಿಂದ ಹನಿ ಬಿದ್ದಿತು; ಮೇಘಮಂಡಲವು ಮಳೆಗರೆಯಿತು.",
			"5": "<sup>5</sup> ಯೆಹೋವನ ಮುಂದೆ ಪರ್ವತಗಳು ಕರಗಿ ಹೋದವು, ಇಸ್ರಾಯೇಲ್ ದೇವರಾದ ಯೆಹೋವನ ಎದುರಿಗೆ ಆ ಸೀನಾಯಿಬೆಟ್ಟವೂ ನೀರಾಯಿತು.",
			"6": "<sup>6</sup> ಅನಾತನ ಮಗನಾದ ಶಮ್ಗರನ ಕಾಲದಲ್ಲಿಯೂ ಯಾಯೇಲಳ ದಿನಗಳಲ್ಲಿಯೂ ರಾಜಮಾರ್ಗಗಳಲ್ಲಿ ಸಂಚಾರವು ನಿಂತು ಹೋಯಿತು; ಪ್ರಯಾಣಿಕರು ಸೀಳುದಾರಿಹಿಡಿದು ಹೋಗುವರು.",
			"7": "<sup>7</sup> ದೆಬೋರಳಾದ ನಾನು ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ತಾಯಿಯಂತೆ ಎದ್ದು ಬರುವವರೆಗೆ ಇಸ್ರಾಯೇಲ್‍ಗ್ರಾಮಗಳು ಹಾಳು ಬಿದ್ದಿದ್ದವು.",
			"8": "<sup>8</sup> ಜನರು ಅನ್ಯದೇವತೆಗಳನ್ನು ಆರಿಸಿಕೊಂಡಿದ್ದರು; ಯುದ್ಧವು ಊರುಬಾಗಲವರೆಗೆ ಬಂದಿತ್ತು. ಇಸ್ರಾಯೇಲ್ಯರ ನಾಲ್ವತ್ತು ಸಾವಿರ ಸೈನಿಕರಲ್ಲಿ ಒಬ್ಬನಿಗೂ ಗುರಾಣಿ ಬರ್ಜಿಗಳು ಇದ್ದಿಲ್ಲ.",
			"9": "<sup>9</sup> ನಾನು ಇಸ್ರಾಯೇಲ್ ನಾಯಕರಲ್ಲಿಯೂ ಸ್ವೇಚ್ಫೆಯಿಂದ ಸೈನ್ಯದಲ್ಲಿ ಸೇರಿದ ಜನರಲ್ಲಿಯೂ ಸಂತೋಷಿಸುತ್ತೇನೆ; ಯೆಹೋವನನ್ನು ಕೊಂಡಾಡಿರಿ.",
			"10": "<sup>10</sup> ಬಿಳೀ ಕತ್ತೆಗಳ ಮೇಲೆ ಸವಾರಿಮಾಡುವವರೇ, ರತ್ನಗಂಬಳಿಗಳ ಮೇಲೆ ಕೂತುಕೊಳ್ಳುವವರೇ, ಪ್ರಯಾಣಿಕರೇ, ಗಾನ ಮಾಡಿರಿ.",
			"11": "<sup>11</sup> ಸೇದುವ ಬಾವಿಗಳ ಬಳಿಯಲ್ಲಿ ಕೊಳ್ಳೆಯನ್ನು ಹಂಚಿಕೊಳ್ಳುವವರ ಧ್ವನಿಗಿಂತ ಗಟ್ಟಿಯಾಗಿ ಯೆಹೋವನ ನೀತಿಸಾಧನೆಯನ್ನು ವರ್ಣಿಸುತ್ತಾರೆ. ಆತನು ತನ್ನ ಪ್ರಜೆಗಳಾಗಿರುವ ಇಸ್ರಾಯೇಲ್ ಗ್ರಾಮಸ್ಥರ ನ್ಯಾಯವನ್ನು ಸ್ಥಾಪಿಸಿದ್ದಾನಲ್ಲಾ. ಆ ಕಾಲದಲ್ಲಿ ಯೆಹೋವನ ಜನರು ಊರು ಬಾಗಲುಗಳಿಗೆ ಇಳಿದು ಬಂದರು.",
			"12": "<sup>12</sup> ಎಚ್ಚರವಾಗು, ದೆಬೋರಳೇ, ಎಚ್ಚರವಾಗು; ಎಚ್ಚರವಾಗು, ಎಚ್ಚರವಾಗಿ ಗಾನ ಮಾಡು. ಬಾರಾಕನೇ, ಏಳು; ಅಬೀನೋವಮನ ಮಗನೇ, ನೀನು ಸೆರೆಹಿಡಿದವರನ್ನು ಸಾಗಿಸಿಕೊಂಡು ಹೋಗು.",
			"13": "<sup>13</sup> ಆಗ ಚದರಿಹೋದವರು ನಾಯಕರ ಬಳಿಗೆ ಕೂಡಿಬಂದರು; ಯೆಹೋವನ ಜನರು ವೀರರಾಗಿ ನನ್ನ ಸಹಾಯಕ್ಕೆ ನೆರೆದು ಬಂದರು.",
			"14": "<sup>14</sup> ಅಮಾಲೇಕ್ಯರ ಪ್ರದೇಶದಲ್ಲಿ ನೆಲೆಗೊಂಡಿರುವ ಎಫ್ರಾಯೀಮ್ಯರೂ ಅವರ ಜೊತೆಯಲ್ಲಿ ಬೆನ್ಯಾಮೀನ್ಯರೂ ಬಂದರು; ಮಾಕೀರನ ಗೋತ್ರದಿಂದ ಪ್ರಧಾನರೂ ಜೆಬುಲೂನ್ ಕುಲದಿಂದ ದಂಡಧಾರಿಗಳಾದ ಸೇನಾನಾಯಕರೂ",
			"15": "<sup>15</sup> ದೆಬೋರಳ ಜೊತೆಯಲ್ಲಿ ಇಸ್ಸಾಕಾರ್ ಕುಲ ಪ್ರಭುಗಳೂ ಇಸ್ಸಾಕಾರ್ಯರೊಡನೆ ಬಾರಾಕನೂ ಆಗಮಿಸಿದರು. ಇವರೆಲ್ಲರೂ ಬಾರಾಕನ ಹೆಜ್ಜೆಹಿಡಿದು ಸರ್ರನೆ ತಗ್ಗಿಗೆ ಬಂದರು. ರೂಬೇನ್ಯರು ನೀರಿನ ಕಾಲುವೆಗಳ ಬಳಿಯಲ್ಲಿ ಮಾಡಿದ ತೀರ್ಮಾನಗಳು ಘನವಾದವುಗಳೇ ಸರಿ.",
			"16": "<sup>16</sup> ರೂಬೇನ್ಯರೇ, ನೀವು ಕುರಿಹಟ್ಟಿಗಳಲ್ಲಿ ಕೂತಿರುವದೇಕೆ? ಮಂದೆಗಳ ಮಧ್ಯದಲ್ಲಿ ಕೊಳಲೂದುವದನ್ನು ಕೇಳಬೇಕೆಂದಿದ್ದೀರೋ? ರೂಬೇನ್ಯರು ನೀರಿನ ಕಾಲುವೆಗಳ ಬಳಿಯಲ್ಲಿ ಮಾಡಿದ ವಿಚಾರಗಳು ಬಹು ಘನವಾದವುಗಳೇ ಸರಿ.",
			"17": "<sup>17</sup> ಗಿಲ್ಯಾದ್ಯರು ಯೊರ್ದನಿನ ಆಚೆಯಲ್ಲೇ ಉಳಿದರು; ದಾನ್ ಕುಲದವರು ಹಡಗುಗಳಲ್ಲಿಯೇ ಉಳಿದದ್ದೇಕೆ? ಆಶೇರ್ ಕುಲದವರು ತಮ್ಮ ರೇವುಗಳನ್ನು ಬಿಡದೆ ಸಮುದ್ರತೀರದಲ್ಲಿ ಸುಮ್ಮನೆ ಕೂತುಬಿಟ್ಟರು.",
			"18": "<sup>18</sup> ಜೆಬುಲೂನ್ಯರೂ ನಫ್ತಾಲ್ಯರೂ ಅಂಥವರಲ್ಲ; ಅವರು ಬೈಲಿನ ದಿನ್ನೆಗಳಲ್ಲಿ ತಮ್ಮ ಜೀವವನ್ನು ಮರಣಾಪತ್ತಿಗೊಪ್ಪಿಸಿದರು.",
			"19": "<sup>19</sup> ಅರಸರು ಬಂದು ಯುದ್ಧಮಾಡಿದರು; ಕಾನಾನ್ಯರಾಜರು ಮೆಗಿದ್ದೋ ಪ್ರವಾಹಗಳ ಬಳಿಯಲ್ಲಿರುವ ತಾನಾಕದಲ್ಲಿ ಕೂಡಿಬಂದು ಕಾದಿದರು. ಅವರಿಗೆ ದ್ರವ್ಯವೇನೂ ಸಿಕ್ಕಲಿಲ್ಲ.",
			"20": "<sup>20</sup> ನಕ್ಷತ್ರಗಳೂ ಆಕಾಶಪಥದಲ್ಲಿದ್ದು ಸೀಸೆರನೊಡನೆ ಯುದ್ಧಮಾಡಿದವು.",
			"21": "<sup>21</sup> ಪೂರ್ವಪ್ರಸಿದ್ಧವಾದ ಕೀಷೋನ್ ಹೊಳೆಯು ಶತ್ರುಗಳನ್ನು ಬಡಕೊಂಡು ಹೋಯಿತು. (ನನ್ನ ಮನವೇ, ಧೈರ್ಯದಿಂದ ಮುಂದೆ ಹೊರಡು.)",
			"22": "<sup>22</sup> ಭರಧೌಡಿನಿಂದ ಓಡುವ ಅವರ ಬಲವಾದ ಕುದುರೆಗಳ ಕಾಲುಗಳ ಪೆಟ್ಟಿನಿಂದ ನೆಲವು ಕಂಪಿಸಿತು.",
			"23": "<sup>23</sup> ಮೇರೋಜ್ ಊರನ್ನು ಶಪಿಸಿರೆಂದು ಯೆಹೋವನ ದೂತನು ಹೇಳುತ್ತಾನೆ. ಅದರ ನಿವಾಸಿಗಳು ಯೆಹೋವನ ಸಹಾಯಕ್ಕೆ ಬರಲಿಲ್ಲ; ಯುದ್ಧವೀರರ ಜೊತೆಯಲ್ಲಿ ಯೆಹೋವನ ಸಹಾಯಕ್ಕೆ ಬರಲಿಲ್ಲವಲ್ಲಾ; ಅವರನ್ನು ಶಪಿಸೇ ಶಪಿಸಿರಿ.",
			"24": "<sup>24</sup> ಎಲ್ಲಾ ಸ್ತ್ರೀಯರಲ್ಲಿ ಕೇನ್ಯನಾದ ಹೆಬೆರನ ಹೆಂಡತಿ ಯಾಯೇಲಳಿಗೇ ಆಶೀರ್ವಾದವು; ಗುಡಾರಗಳಲ್ಲಿ ವಾಸಿಸುವ ಸ್ತ್ರೀಯರಲ್ಲಿ ಆಕೆಗೇ ಆಶೀರ್ವಾದವು.",
			"25": "<sup>25</sup> ನೀರು ಕೇಳಿದನು, ಹಾಲು ಕೊಟ್ಟಳು; ಉತ್ತಮಪಾತ್ರೆಯಲ್ಲಿ ಮಜ್ಜಿಗೆಕೊಟ್ಟಳು.",
			"26": "<sup>26</sup> ಕೈಚಾಚಿ ಗೂಟವನ್ನು ತೆಗೆದುಕೊಂಡಳು; ಬಲಗೈಯಲ್ಲಿ ದೊಡ್ಡ ಕೊಡತಿಯನ್ನು ಹಿಡಿದಳು; ಆ ಕೊಡತಿಯಿಂದ ಸೀಸೆರನ ತಲೆಯನ್ನು ಒಡೆದುಬಿಟ್ಟಳು. ಅವನ ಕಣತಲೆಗೆ ತಿವಿದು ಬಡಿದಳು.",
			"27": "<sup>27</sup> ಅವನು ಆಕೆಯ ಕಾಲುಗಳ ಬಳಿಯಲ್ಲಿ ಬೊಗ್ಗಿಕೊಂಡು ಉರುಳಿ ಬಿದ್ದನು; ಅವನು ಆಕೆಯ ಕಾಲುಗಳ ಬಳಿಯಲ್ಲಿ ಮುದುರಿಕೊಂಡು ಉರುಳಿದನು. ಅವನು ಬಿದ್ದಲ್ಲೇ ಸತ್ತನು.",
			"28": "<sup>28</sup> ಸೀಸೆರನ ತಾಯಿಯು ಕಿಟಿಕಿಯಿಂದ ನೋಡಿದಳು; ಸೀಸೆರನ ತಾಯಿಯು ಕಿಟಿಕಿಯ ಜಾಲರಿಗಳಿಂದ ಕೂಗಿದಳು. ಅವನ ರಥವು ಬರುವದಕ್ಕೆ ಇಷ್ಟು ಹೊತ್ತೇಕಾಯಿತು! ಅವನ ಕುದುರೆಗಳ ಕಾಲುಗಳಿಗೆ ಇಷ್ಟು ಸಾವಕಾಶವೇಕೆ ಅಂದಳು.",
			"29": "<sup>29</sup> ಸಭ್ಯಸ್ತ್ರೀಯರಲ್ಲಿ ಬುದ್ಧಿವಂತೆಯರು ಕೊಟ್ಟ ಉತ್ತರವನ್ನು ಈಕೆಯು ತನ್ನಲ್ಲಿ ತಾನೇ ಅಂದುಕೊಳ್ಳುತ್ತಿದ್ದದ್ದು ಹೇಗಂದರೆ -",
			"30": "<sup>30</sup> ನಿಶ್ಚಯವಾಗಿ ಅವರಿಗೆ ಕೊಳ್ಳೆಸಿಕ್ಕಿರುತ್ತದೆ; ಅದನ್ನು ಹಂಚಿಕೊಳ್ಳುತ್ತಿದ್ದಾರೆ. ಪ್ರತಿಯೊಬ್ಬ ಸೈನಿಕನಿಗೆ ಒಬ್ಬಿಬ್ಬರು ದಾಸಿಯರೂ ಸೀಸೆರನಿಗೆ ನಾನಾ ವರ್ಣಗಳುಳ್ಳ ಬಟ್ಟೆಗಳೂ ವಿಚಿತ್ರವಾಗಿ ಕಸೂತಿಹಾಕಿದ ಒಂದೆರಡು ವಸ್ತ್ರಗಳೂ ಕಂಠಮಾಲೆಯೂ ದೊರಕಿರುತ್ತವೆ ಎಂಬದೇ.",
			"31": "<sup>31</sup> ಯೆಹೋವನ ಎಲ್ಲಾ ಶತ್ರುಗಳೂ ಇವರಂತೆಯೇ ನಾಶವಾಗಲಿ; ಆತನ ಭಕ್ತರು ಪ್ರತಾಪದಿಂದ ಉದಯಿಸುವ ಸೂರ್ಯನಂತಿರಲಿ. ದೇಶದಲ್ಲಿ ನಾಲ್ವತ್ತು ವರುಷ ಸಮಾಧಾನವಿತ್ತು."
		},
		"6": {
			"1": "<sup>1</sup> ಇಸ್ರಾಯೇಲ್ಯರು ತಿರಿಗಿ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಗಳಾದದರಿಂದ ಆತನು ಅವರನ್ನು ಏಳು ವರುಷಗಳ ಪರ್ಯಂತರ ಮಿದ್ಯಾನ್ಯರ ಕೈಗೆ ಒಪ್ಪಿಸಿದನು.",
			"2": "<sup>2</sup> ಮಿದ್ಯಾನ್ಯರ ಹಸ್ತವು ಬಲಗೊಂಡದರಿಂದ ಇಸ್ರಾಯೇಲ್ಯರು ಅವರಿಗೆ ಹೆದರಿ ಪರ್ವತಗಳಲ್ಲಿ ಕಂದರಗುಹೆದುರ್ಗಗಳನ್ನು ಮಾಡಿ ಸೇರಿಕೊಂಡರು.",
			"3": "<sup>3</sup> ಇವರು ಬೀಜ ಬಿತ್ತಿದ ತರುವಾಯ ಮಿದ್ಯಾನ್ಯರೂ ಅಮಾಲೇಕ್ಯರೂ ಮೂಡಣ ದೇಶದವರೂ ಇವರಿಗೆ ವಿರೋಧವಾಗಿ ದಂಡೆತ್ತಿ ಬಂದು ಇಳುಕೊಂಡು",
			"4": "<sup>4</sup> ಗಾಜಾ ಪ್ರಾಂತದವರೆಗಿದ್ದ ಎಲ್ಲಾ ಭೂಮಿಯ ಹುಟ್ಟುವಳಿಯನ್ನು ಹಾಳುಮಾಡಿಬಿಟ್ಟದರಿಂದ ಇವರಿಗೆ ದವಸ ಧಾನ್ಯವಾಗಲಿ ಕುರಿದನಕತ್ತೆಗಳಾಗಲಿ ಉಳಿಯಲೇ ಇಲ್ಲ.",
			"5": "<sup>5</sup> ಆ ಶತ್ರುಗಳು ತಮ್ಮ ಕುರಿದನ ಗುಡಾರಗಳ ಸಹಿತವಾಗಿ ಮಿಡಿತೆಗಳಂತೆ ಗುಂಪುಗುಂಪಾಗಿ ಬಂದರು; ಅವರೂ ಅವರ ಒಂಟೆಗಳೂ ಅಸಂಖ್ಯ. ಆ ಗುಂಪೆಲ್ಲಾ ಬಂದು ದೇಶವನ್ನು ಹಾಳುಮಾಡುತ್ತಿತ್ತು.",
			"6": "<sup>6</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಮಿದ್ಯಾನ್ಯರ ದೆಸೆಯಿಂದ ಬಲು ಕುಗ್ಗಿ ಹೋಗಿ ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಟ್ಟರು.",
			"7": "<sup>7</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಮಿದ್ಯಾನ್ಯರ ಕಾಟವನ್ನು ಸಹಿಸಲಾರದೆ ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಟ್ಟಾಗ",
			"8": "<sup>8</sup> ಆತನು ಅವರ ಬಳಿಗೆ ಒಬ್ಬ ಪ್ರವಾದಿಯನ್ನು ಕಳುಹಿಸಿದನು. ಅವನು ಬಂದು ಅವರಿಗೆ - ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರಾದ ಯೆಹೋವನ ಮಾತನ್ನು ಕೇಳಿರಿ; ಆತನು ಹೇಳುವದೇನಂದರೆ - ನೀವು ದಾಸತ್ವದಲ್ಲಿದ್ದ ಐಗುಪ್ತದಿಂದ ನಿಮ್ಮನ್ನು ಕರತಂದೆನು.",
			"9": "<sup>9</sup> ಐಗುಪ್ತ್ಯರಿಂದ ನಿಮ್ಮನ್ನು ಬಿಡಿಸಿದ್ದಲ್ಲದೆ ಬೇರೆ ಎಲ್ಲಾ ಬಾಧಕರಿಂದಲೂ ನಿಮ್ಮನ್ನು ಬಿಡಿಸಿ ಅವರನ್ನು ನಿಮ್ಮೆದುರಿನಿಂದ ಅಟ್ಟಿಬಿಟ್ಟು ಅವರ ದೇಶವನ್ನು ನಿಮಗೆ ಕೊಟ್ಟೆನು.",
			"10": "<sup>10</sup> ಯೆಹೋವನೆಂಬ ನಾನೇ ನಿಮ್ಮ ದೇವರಾಗಿದ್ದೇನೆಂದೂ ನೀವು ಅಮೋರಿಯರ ಮಧ್ಯದಲ್ಲಿ ವಾಸಿಸುವಾಗ ಅವರ ದೇವತೆಗಳನ್ನು ಪೂಜಿಸಬಾರದೆಂದೂ ಹೇಳಿದೆನು; ಆದರೆ ನೀವು ನನ್ನ ಮಾತನ್ನು ಕೇಳಲಿಲ್ಲ ಎಂಬದೇ ಅಂದನು.",
			"11": "<sup>11</sup> ಆಮೇಲೆ ಯೆಹೋವನ ದೂತನು ಬಂದು ಒಫ್ರದಲ್ಲಿದ್ದ ಏಲಾವೃಕ್ಷದ ಅಡಿಯಲ್ಲಿ ಕೂತುಕೊಂಡನು. ಅದು ಅಬೀಯೆಜೆರನ ಗೋತ್ರದವನಾದ ಯೋವಾಷನದಾಗಿತ್ತು. ಅವನ ಮಗನಾದ ಗಿದ್ಯೋನನು ಮಿದ್ಯಾನ್ಯರಿಗೆ ಗೊತ್ತಾಗದ ಹಾಗೆ ಅಲ್ಲಿನ ದ್ರಾಕ್ಷೆಯ ಆಲೆಯ ಮರೆಯಲ್ಲಿ ಗೋದಿಯನ್ನು ಬಡಿಯುತ್ತಿದ್ದನು.",
			"12": "<sup>12</sup> ಯೆಹೋವನ ದೂತನು ಗಿದ್ಯೋನನಿಗೆ ಪ್ರತ್ಯಕ್ಷನಾಗಿ ಅವನಿಗೆ - ಪರಾಕ್ರಮಶಾಲಿಯೇ, ಯೆಹೋವನು ನಿನ್ನ ಸಂಗಡ ಇದ್ದಾನೆ ಅಂದನು.",
			"13": "<sup>13</sup> ಆಗ ಗಿದ್ಯೋನನು ಅವನಿಗೆ - ಸ್ವಾಮೀ, ಯೆಹೋವನು ನಮ್ಮ ಸಂಗಡ ಇದ್ದರೆ ನಮಗೆ ಇದೆಲ್ಲಾ ಯಾಕೆ ಸಂಭವಿಸಿತು? ನಮ್ಮ ಹಿರಿಯರು, ಯೆಹೋವನು ಅದ್ಭುತಗಳನ್ನು ನಡಿಸಿ ತಮ್ಮನ್ನು ಐಗುಪ್ತದಿಂದ ಬಿಡಿಸಿದನೆಂಬದಾಗಿ ವಿವರಿಸುತ್ತಿದ್ದರು. ಅಂಥ ಅದ್ಭುತಗಳು ಈಗೆಲ್ಲಿವೆ? ಆತನು ನಮ್ಮನ್ನು ತಿರಸ್ಕರಿಸಿ ಮಿದ್ಯಾನ್ಯರ ಕೈಗೆ ಒಪ್ಪಿಸಿಬಿಟ್ಟಿದ್ದಾನಲ್ಲಾ ಅನ್ನಲು",
			"14": "<sup>14</sup> ಯೆಹೋವನು ಅವನನ್ನು ಚೆನ್ನಾಗಿ ನೋಡಿ - ನಾನು ನಿನ್ನನ್ನು ಕಳುಹಿಸುತ್ತೇನೆ, ಹೋಗು; ಈ ನಿನ್ನ ಬಲದಿಂದ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಮಿದ್ಯಾನ್ಯರಿಂದ ಬಿಡಿಸು ಎಂದು ಹೇಳಿದನು.",
			"15": "<sup>15</sup> ಆಗ ಗಿದ್ಯೋನನು ಆತನಿಗೆ - ಸ್ವಾಮೀ, ನಾನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ರಕ್ಷಿಸುವದು ಹೇಗೆ? ಮನಸ್ಸೆ ಕುಲದಲ್ಲಿ ನನ್ನ ಮನೆಯು ಕನಿಷ್ಠವಾದದ್ದು; ಮತ್ತು ನಾನು ನಮ್ಮ ಕುಟುಂಬದಲ್ಲಿ ಅಲ್ಪನು ಅನ್ನಲು",
			"16": "<sup>16</sup> ಯೆಹೋವನು ಅವನಿಗೆ - ನಾನು ನಿನ್ನ ಸಂಗಡ ಇರುವದರಿಂದ ನೀನು ಮಿದ್ಯಾನ್ಯರೆಲ್ಲರನ್ನೂ ಒಬ್ಬ ಮನುಷ್ಯನನ್ನೋ ಎಂಬಂತೆ ಸಂಹರಿಸಿಬಿಡುವಿ ಅಂದನು.",
			"17": "<sup>17</sup> ಅವನು ಆತನಿಗೆ - ಸ್ವಾಮೀ, ದಯವಿರಲಿ; ನನ್ನೊಂದಿಗೆ ಮಾತಾಡುತ್ತಿರುವ ತಾವು ಸ್ವಾಮಿಯವರೇ ಆಗಿದ್ದೀರೆಂಬದಕ್ಕೆ ನನಗೊಂದು ಗುರುತನ್ನು ಅನುಗ್ರಹಿಸಬೇಕು.",
			"18": "<sup>18</sup> ನಾನು ಹೋಗಿ ತಮಗೆ ಕಾಣಿಕೆಯನ್ನು ತೆಗೆದುಕೊಂಡು ಬರುವವರೆಗೆ ತಾವು ಈ ಸ್ಥಳವನ್ನು ಬಿಡಬಾರದು ಎಂದು ಬಿನ್ನವಿಸಲು ಆತನು - ನೀನು ತಿರಿಗಿ ಬರುವವರೆಗೆ ನಾನು ಇಲ್ಲೇ ಇರುವೆನು ಅಂದನು.",
			"19": "<sup>19</sup> ಗಿದ್ಯೋನನು ಮನೆಗೆ ಹೋಗಿ ಒಂದು ಆಡಿನ ಮರಿಯನ್ನು ಪಕ್ವಮಾಡಿ ಒಂದು ಏಫಾ ಹಿಟ್ಟಿನಿಂದ ಹುಳಿಯಿಲ್ಲದ ರೊಟ್ಟಿಗಳನ್ನು ಸುಟ್ಟು ಮಾಂಸವನ್ನು ಪುಟ್ಟಿಯಲ್ಲಿಟ್ಟು ಅದರ ರಸವನ್ನು ಬಟ್ಟಲಿನಲ್ಲಿ ಹೊಯ್ದು ಎಲ್ಲವನ್ನೂ ಏಲಾ ಮರದ ಕೆಳಗೆ ಆತನ ಮುಂದೆ ತಂದಿಟ್ಟನು.",
			"20": "<sup>20</sup> ಆಗ ದೇವದೂತನು ಅವನಿಗೆ - ಮಾಂಸವನ್ನೂ ರೊಟ್ಟಿಗಳನ್ನೂ ಆ ಬಂಡೆಯ ಮೇಲಿಟ್ಟು ರಸವನ್ನು ಹೊಯ್ಯಿ ಅನ್ನಲು ಅವನು ಹಾಗೆಯೇ ಮಾಡಿದನು.",
			"21": "<sup>21</sup> ಅನಂತರ ಯೆಹೋವನ ದೂತನು ತನ್ನ ಕೈಕೋಲನ್ನು ಚಾಚಿ ಅದರ ತುದಿಯನ್ನು ಆ ರೊಟ್ಟಿಗಳಿಗೂ ಮಾಂಸಕ್ಕೂ ಮುಟ್ಟಿಸಲು ಬಂಡೆಯಿಂದ ಬೆಂಕಿಯೆದ್ದು ಅವೆರಡನ್ನೂ ದಹಿಸಿಬಿಟ್ಟಿತು; ಯೆಹೋವನ ದೂತನು ಅದೃಶ್ಯನಾದನು.",
			"22": "<sup>22</sup> ಆತನು ಯೆಹೋವನ ದೂತನೆಂದು ಗಿದ್ಯೋನನು ತಿಳಿದು ಭಯಪಟ್ಟು - ಅಯ್ಯೋ ಕರ್ತನೇ, ಯೆಹೋವನೇ, ಯೆಹೋವನ ದೂತನನ್ನು ಮುಖಾಮುಖಿಯಾಗಿ ನೋಡಿಬಿಟ್ಟೆನಲ್ಲಾ ಎಂದು ಕೂಗಿದನು.",
			"23": "<sup>23</sup> ಆದರೆ ಯೆಹೋವನು ಅವನಿಗೆ - ಸಮಾಧಾನದಿಂದಿರು, ಹೆದರಬೇಡ; ನೀನು ಸಾಯುವದಿಲ್ಲ ಅಂದನು.",
			"24": "<sup>24</sup> ಗಿದ್ಯೋನನು ಅಲ್ಲಿ ಯೆಹೋವನಿಗೋಸ್ಕರ ಒಂದು ಯಜ್ಞವೇದಿಯನ್ನು ಕಟ್ಟಿ ಅದಕ್ಕೆ ಯೆಹೋವ ಷಾಲೋಮ್ ಎಂದು ಹೆಸರಿಟ್ಟನು. ಅದು ಈವರೆಗೂ ಅಬೀಯೆಜೆರ್ ಗೋತ್ರದವರ ಒಫ್ರದಲ್ಲಿರುತ್ತದೆ.",
			"25": "<sup>25</sup> ಅದೇ ದಿನ ರಾತ್ರಿಯಲ್ಲಿ ಯೆಹೋವನು ಅವನಿಗೆ - ನೀನು ನಿನ್ನ ತಂದೆಯ ಹೋರಿಗಳಲ್ಲಿ ಒಂದು ಚಿಕ್ಕ ಹೋರಿಯನ್ನೂ ಏಳು ವರುಷದ ಇನ್ನೊಂದು ಹೋರಿಯನ್ನೂ ತೆಗೆದುಕೊಂಡುಹೋಗಿ ನಿನ್ನ ತಂದೆಯು ಕಟ್ಟಿರುವ ಬಾಳನ ಯಜ್ಞವೇದಿಯನ್ನು ಕೆಡವಿ ಅದರ ಬಳಿಯಲ್ಲಿರುವ ಅಶೇರವೆಂಬ ವಿಗ್ರಹ ಸ್ತಂಭವನ್ನು ಕಡಿದುಹಾಕಿ",
			"26": "<sup>26</sup> ಈ ಗುಡ್ಡದ ಶಿಖರದಲ್ಲಿ ಯೆಹೋವನಿಗೋಸ್ಕರ ನೇಮಕವಾದ ರೀತಿಯಿಂದ ಒಂದು ಯಜ್ಞವೇದಿಯನ್ನು ಕಟ್ಟಿಸಿ ನೀನು ಕಡಿದು ಹಾಕಿದ ಅಶೇರ ವಿಗ್ರಹಸ್ತಂಭದಿಂದ ಬೆಂಕಿಮಾಡಿ ಆ ಎರಡನೆಯ ಹೋರಿಯನ್ನೂ ಸರ್ವಾಂಗಹೋಮವಾಗಿ ಸಮರ್ಪಿಸು ಎಂದು ಹೇಳಿದನು.",
			"27": "<sup>27</sup> ಆಗ ಗಿದ್ಯೋನನು ತನ್ನ ಸೇವಕರಲ್ಲಿ ಹತ್ತು ಮಂದಿಯನ್ನು ಕರೆದುಕೊಂಡು ಹೋಗಿ ಯೆಹೋವನು ಹೇಳಿದಂತೆಯೇ ಮಾಡಿದನು. ಆದರೆ ಅವನು ತನ್ನ ಮನೆಯವರಿಗೂ ಊರಿನವರಿಗೂ ಹೆದರಿಕೊಂಡದರಿಂದ ಅದನ್ನು ಹಗಲಿನಲ್ಲಿ ಮಾಡದೆ ರಾತ್ರಿಯಲ್ಲಿ ಮಾಡಿದನು.",
			"28": "<sup>28</sup> ಊರಿನ ಜನರು ಹೊತ್ತಾರೆಯಲ್ಲೆದ್ದು ಬಾಳನ ಯಜ್ಞವೇದಿಯು ಕೆಡವಲ್ಪಟ್ಟದ್ದನ್ನೂ ಅದರ ಸಮೀಪದಲ್ಲಿದ್ದ ವಿಗ್ರಹ ಸ್ತಂಭವು ಕಡಿಯಲ್ಪಟ್ಟದ್ದನ್ನೂ ಹೊಸದಾಗಿ ಕಟ್ಟಿದ ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಎರಡನೆಯ ಹೋರಿಯು ಯಜ್ಞವಾದದ್ದನ್ನೂ ಕಂಡು",
			"29": "<sup>29</sup> ಹೀಗೆ ಮಾಡಿದವರಾರು ಎಂದು ಒಬ್ಬರನ್ನೊಬ್ಬರು ವಿಚಾರಿಸುವಲ್ಲಿ ಯೋವಾಷನ ಮಗನಾದ ಗಿದ್ಯೋನನೇ ಎಂದು ಗೊತ್ತಾಗಲು",
			"30": "<sup>30</sup> ಆ ಜನರು ಯೋವಾಷನಿಗೆ - ನಿನ್ನ ಮಗನನ್ನು ಹೊರಗೆ ತೆಗೆದುಕೊಂಡು ಬಾ; ಅವನು ಸಾಯಬೇಕು; ಯಾಕಂದರೆ ಬಾಳನ ಯಜ್ಞವೇದಿಯನ್ನು ಕೆಡವಿ ಅದರ ಬಳಿಯಲ್ಲಿದ್ದ ಅಶೇರವಿಗ್ರಹಸ್ತಂಭವನ್ನು ಕಡಿದು ಹಾಕಿದ್ದಾನೆ ಅಂದರು.",
			"31": "<sup>31</sup> ಯೋವಾಷನು ತನಗೆ ವಿರೋಧವಾಗಿ ನಿಂತವರೆಲ್ಲರಿಗೆ - ಬಾಳನಿಗೋಸ್ಕರ ನೀವು ವ್ಯಾಜ್ಯವಾಡಬೇಕೋ? ನೀವು ಅವನನ್ನು ರಕ್ಷಿಸಬೇಕೋ? ಅವನಿಗಾಗಿ ವ್ಯಾಜ್ಯವಾಡುವವರು ಈ ಹೊತ್ತೇ ಕೊಲ್ಲಲ್ಪಡಲಿ. ಅವನು ದೇವನಾಗಿದ್ದರೆ ತನ್ನ ಯಜ್ಞವೇದಿಯನ್ನು ಕೆಡವಿ ಬಿಟ್ಟದ್ದಕ್ಕಾಗಿ ತಾನೇ ವ್ಯಾಜ್ಯವಾಡಲಿ ಎಂದನು.",
			"32": "<sup>32</sup> ಆ ದಿವಸದಲ್ಲಿ ಅವನು - ಬಾಳನು ತನ್ನ ಯಜ್ಞವೇದಿಯನ್ನು ಕೆಡವಿದವನೊಡನೆ ತಾನೇ ವ್ಯಾಜ್ಯವಾಡಲಿ ಎಂದು ಹೇಳಿದ್ದರಿಂದ ಗಿದ್ಯೋನನಿಗೆ ಯೆರುಬ್ಬಾಳನೆಂಬ ಹೆಸರಾಯಿತು.",
			"33": "<sup>33</sup> ಇಷ್ಟರಲ್ಲಿ ಮಿದ್ಯಾನ್ಯರೂ ಅಮಾಲೇಕ್ಯರೂ ಮೂಡಣ ದೇಶದವರೂ ಕೂಡಿ ಹೊಳೆದಾಟಿ ಬಂದು ಇಜ್ರೇಲಿನ ತಗ್ಗಿನಲ್ಲಿ ಪಾಳೆಯಮಾಡಿಕೊಂಡರು.",
			"34": "<sup>34</sup> ಆಗ ಗಿದ್ಯೋನನು ಯೆಹೋವನ ಆತ್ಮನ ಆವೇಶವುಳ್ಳವನಾಗಿ ಕೊಂಬನ್ನು ಊದಲು ಅಬೀಯೆಜೆರನ ಗೋತ್ರದವರು ಬಂದು ಅವನನ್ನು ಹಿಂಬಾಲಿಸಿದರು.",
			"35": "<sup>35</sup> ಅವನು ಉಳಿದ ಮನಸ್ಸೆಯವರ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಲಾಗಿ ಅವರೂ ಕೂಡಿಬಂದರು. ಆಶೇರ್ ಜೆಬುಲೂನ್ ನಫ್ತಾಲಿ ಕುಲಗಳವರ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಲು ಅವರೂ ಬಂದು ಯುದ್ಧಪ್ರಯಾಣಕ್ಕೋಸ್ಕರ ಸೇರಿಕೊಂಡರು.",
			"36": "<sup>36</sup> ಮತ್ತು ಗಿದ್ಯೋನನು ದೇವರಿಗೆ - ನೀನು ಹೇಳಿದಂತೆ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ನನ್ನ ಮೂಲಕವಾಗಿ ರಕ್ಷಿಸುವಿಯೋ ಇಲ್ಲವೋ ಎಂಬದನ್ನು ತಿಳಿಯಪಡಿಸು.",
			"37": "<sup>37</sup> ಇಗೋ, ನಾನು ಕಣದಲ್ಲಿ ಕುರಿಯ ತುಪ್ಪಟವನ್ನು ಇಡುತ್ತೇನೆ, ಅದರಲ್ಲಿ ಮಾತ್ರ ಮಂಜುಬಿದ್ದು ನೆಲವೆಲ್ಲಾ ಒಣಗಿದ್ದರೆ ನೀನು ಹೇಳಿದಂತೆಯೇ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ನನ್ನ ಮೂಲಕವಾಗಿ ರಕ್ಷಿಸುವಿಯೆಂದು ತಿಳಿಯುವೆನು ಎಂಬದಾಗಿ ವಿಜ್ಞಾಪಿಸಿದನು.",
			"38": "<sup>38</sup> ಹಾಗೆಯೇ ಆಯಿತು; ಅವನು ಮರುದಿವಸ ಹೊತ್ತಾರೆಯಲ್ಲಿ ಎದ್ದು ತುಪ್ಪಟವನ್ನು ಒತ್ತಿ ಹಿಂಡಲು ಮಂಜಿನ ನೀರು ಒಂದು ಬಟ್ಟಲು ತುಂಬಾ ಆಯಿತು.",
			"39": "<sup>39</sup> ಅವನು ತಿರಿಗಿ ದೇವರಿಗೆ - ಸ್ವಾಮೀ, ಸಿಟ್ಟಾಗಬಾರದು; ಇನ್ನೊಂದು ಸಾರಿ ಮಾತಾಡುತ್ತೇನೆ. ಇನ್ನು ಒಂದೇ ಸಾರಿ ಈ ತುಪ್ಪಟದಿಂದ ನಿನ್ನನ್ನು ಪರೀಕ್ಷಿಸುವದಕ್ಕೆ ಅಪ್ಪಣೆಯಾಗಲಿ; ಈ ತುಪ್ಪಟವು ಮಾತ್ರವೇ ಒಣಗಿದ್ದು ನೆಲದ ಮೇಲೆಲ್ಲಾ ಹನಿಬಿದ್ದಿರಲಿ ಅಂದನು.",
			"40": "<sup>40</sup> ಆ ರಾತ್ರಿ ದೇವರು ಹಾಗೆಯೇ ಮಾಡಿದನು; ತುಪ್ಪಟವು ಮಾತ್ರ ಒಣಗಿತ್ತು, ನೆಲದ ಮೇಲೆಲ್ಲಾ ಹನಿ ಬಿದ್ದಿತ್ತು."
		},
		"7": {
			"1": "<sup>1</sup> ಯೆರುಬ್ಬಾಳನೆನಿಸಿಕೊಳ್ಳುವ ಗಿದ್ಯೋನನೂ ಅವನ ಸಂಗಡ ಇದ್ದ ಜನರೂ ಬೆಳಿಗ್ಗೆ ಎದ್ದು ಹೊರಟುಹೋಗಿ ಹರೋದಿನ ಬುಗ್ಗೆಯ ಬಳಿಯಲ್ಲಿ ಇಳುಕೊಂಡರು. ಇವರಿಗೆ ಉತ್ತರದಿಕ್ಕಿನಲ್ಲಿ ಮೋರೆ ಗುಡ್ಡದ ಹಿಂದಿನ ತಗ್ಗಿನಲ್ಲಿ ಮಿದ್ಯಾನ್ಯರ ದಂಡಿಳಿದಿತ್ತು.",
			"2": "<sup>2</sup> ಆಗ ಯೆಹೋವನು ಗಿದ್ಯೋನನಿಗೆ - ನಿನ್ನ ಸಂಗಡ ಇರುವ ಜನರು ಹೆಚ್ಚಾಗಿದ್ದಾರೆ; ಇವರ ಕೈಗೆ ಮಿದ್ಯಾನ್ಯರನ್ನು ಒಪ್ಪಿಸುವದು ನನಗೆ ಸರಿಯಾಗಿ ಕಾಣುವದಿಲ್ಲ; ಒಪ್ಪಿಸಿಕೊಟ್ಟರೆ ಸ್ವಹಸ್ತದಿಂದಲೇ ನಮಗೆ ರಕ್ಷಣೆಯುಂಟಾಯಿತೆಂದು ಹೆಚ್ಚಳ ಪಟ್ಟು ನನ್ನನ್ನು ಅಲಕ್ಷ್ಯಮಾಡಾರು.",
			"3": "<sup>3</sup> ಆದದರಿಂದ ಎಲ್ಲಾ ಜನರಿಗೆ ಕೇಳಿಸುವಂತೆ - ಧೈರ್ಯವಿಲ್ಲದವರೂ ಅಂಜುವವರೂ ಈ ಗಿಲ್ಯಾದ್ ಪರ್ವತವನ್ನು ಬಿಟ್ಟು ಮನೆಗೆ ಹೋಗಲಿ ಎಂದು ಪ್ರಕಟಿಸು ಅಂದನು. ಹಾಗೆ ಮಾಡಲು ಇಪ್ಪತ್ತೆರಡು ಸಾವಿರ ಮಂದಿ ಹಿಂದಿರುಗಿ ಹೋದರು; ಹತ್ತು ಸಾವಿರ ಮಂದಿ ಉಳಿದರು.",
			"4": "<sup>4</sup> ಯೆಹೋವನು ತಿರಿಗಿ ಗಿದ್ಯೋನನಿಗೆ - ನಿನ್ನ ಬಳಿಯಲ್ಲಿರುವ ಜನರು ಇನ್ನೂ ಹೆಚ್ಚಾಗಿದ್ದಾರೆ; ನೀನು ಅವರನ್ನು ಹಳ್ಳಕ್ಕೆ ಕರೆದುಕೊಂಡು ಹೋಗು; ಅಲ್ಲಿ ಅವರನ್ನು ನಿನಗೋಸ್ಕರ ಪರೀಕ್ಷಿಸುವೆನು. ಯಾರು ನಿನ್ನ ಸಂಗಡ ಹೋಗಬಹುದೆಂದು ಹೇಳುವೆನೋ ಅವರು ಮಾತ್ರ ಹೋಗಲಿ; ಯಾರಿಗೆ ಹೋಗಬಾರದೆಂದು ಹೇಳುವೆನೋ ಅವರು ಹೋಗದಿರಲಿ ಅಂದನು.",
			"5": "<sup>5</sup> ಗಿದ್ಯೋನನು ಜನರನ್ನು ಹಳ್ಳಕ್ಕೆ ಕರೆದುಕೊಂಡು ಬಂದಾಗ ಯೆಹೋವನು ಅವನಿಗೆ - ನಾಯಿಯಂತೆ ನೀರನ್ನು ನಾಲಿಗೆಯಿಂದ ನೆಕ್ಕುವವರನ್ನೂ ಮೊಣಕಾಲೂರಿ ಕುಡಿಯುವವರನ್ನೂ ಬೇರೆ ಬೇರೆಯಾಗಿ ನಿಲ್ಲುಸು ಎಂದು ಹೇಳಿದನು.",
			"6": "<sup>6</sup> ಕೈಯಿಂದ ನೀರನ್ನು ಬಾಯಿಗೆ ತೆಗೆದುಕೊಂಡು ನೆಕ್ಕಿದವರ ಸಂಖ್ಯೆ ಮುನ್ನೂರು. ಇತರ ಜನರು ಮೊಣಕಾಲೂರಿ ಕುಡಿದವರು.",
			"7": "<sup>7</sup> ಆಗ ಯೆಹೋವನು ಗಿದ್ಯೋನನಿಗೆ - ನೀರನ್ನು ನೆಕ್ಕಿದ ಆ ಮುನ್ನೂರು ಮಂದಿಯಿಂದಲೇ ನಿಮಗೆ ಜಯವನ್ನುಂಟುಮಾಡಿ ಮಿದ್ಯಾನ್ಯರನ್ನು ನಿನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸಿಕೊಡುವೆನು; ಉಳಿದವರು ತಮ್ಮ ತಮ್ಮ ಸ್ಥಳಗಳಿಗೆ ಹೋಗಲಿ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"8": "<sup>8</sup> ಗಿದ್ಯೋನನು ಆ ಮುನ್ನೂರು ಜನರನ್ನು ಇಟ್ಟುಕೊಂಡು ಉಳಿದ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಅವರವರ ಗುಡಾರಗಳಿಗೆ ಕಳುಹಿಸಿದನು. ಕಳುಹಿಸುವಾಗ ಅವರ ಹತ್ತಿರ ಇದ್ದ ಆಹಾರವನ್ನೂ ಕೊಂಬುಗಳನ್ನೂ ತನ್ನ ಜನರಿಗೋಸ್ಕರ ತೆಗೆದುಕೊಂಡನು. ಮಿದ್ಯಾನ್ಯರ ದಂಡು ಕೆಳಗೆ ತಗ್ಗಿನಲ್ಲಿ ಇಳುಕೊಂಡಿತ್ತು.",
			"9": "<sup>9</sup> ಅದೇ ದಿವಸ ರಾತ್ರಿಯಲ್ಲಿ ಯೆಹೋವನು ಅವನಿಗೆ - ನೀನು ಎದ್ದು ಹೋಗಿ ಶತ್ರುಗಳ ಪಾಳೆಯದ ಮೇಲೆ ಬೀಳು; ಅದನ್ನು ನಿನಗೆ ಒಪ್ಪಿಸಿಕೊಟ್ಟಿದ್ದೇನೆ.",
			"10": "<sup>10</sup> ಅವರ ಮೇಲೆ ಬೀಳುವದಕ್ಕೆ ಹೆದರುತ್ತೀಯಾದರೆ ಮೊದಲು ನಿನ್ನ ಸೇವಕನಾದ ಪುರನ ಸಂಗಡ ಅಲ್ಲಿಗೆ ಹೋಗಿ ಅವರು ಮಾತಾಡಿಕೊಳ್ಳುವದನ್ನು ಲಾಲಿಸು;",
			"11": "<sup>11</sup> ಆಗ ಅವರ ಮೇಲೆ ಬೀಳುವದಕ್ಕೆ ನಿನಗೆ ಧೈರ್ಯ ಬರುವದು ಅಂದನು. ಗಿದ್ಯೋನನು ಅದರಂತೆಯೇ ತನ್ನ ಸೇವಕನಾದ ಪುರನ ಸಂಗಡ ಶತ್ರುಸೈನಿಕರ ಪಾಳೆಯದ ಕಡೇ ಭಾಗಕ್ಕೆ ಹೋದನು.",
			"12": "<sup>12</sup> ಮಿದ್ಯಾನ್ಯರೂ ಅಮಾಲೇಕ್ಯರೂ ಮೂಡಣ ದೇಶದವರೂ ಮಿಡಿತೆಗಳಂತೆ ಗುಂಪಾಗಿ ತಗ್ಗಿನಲ್ಲಿ ಇಳುಕೊಂಡಿದ್ದರು. ಅವರ ಒಂಟೆಗಳು ಸಮುದ್ರತೀರದ ಮರಳಿನಂತೆ ಅಸಂಖ್ಯವಾಗಿದ್ದವು.",
			"13": "<sup>13</sup> ಗಿದ್ಯೋನನು ಬಂದಾಗ ಅವರಲ್ಲೊಬ್ಬನು ತನ್ನ ಜೊತೆಗಾರನಿಗೆ ಒಂದು ಕನಸನ್ನು ತಿಳಿಸುತ್ತಿದ್ದನು. ಅವನು - ಇಗೋ, ನಾನು ಒಂದು ಕನಸು ಕಂಡೆನು; ಅದರಲ್ಲಿ ಒಂದು ಜವೆಗೋದಿಯ ರೊಟ್ಟಿಯು ಉರುಳುತ್ತಾ ಮಿದ್ಯಾನ್ಯರ ಪಾಳೆಯದೊಳಗೆ ಬಂದು ಒಂದು ಡೇರೆಗೆ ತಗಲಿ ಅದನ್ನು ಅಡಿಮೇಲು ಮಾಡಿ ಬೀಳಿಸಿ ಬಿಟ್ಟಿತು ಎಂದು ಹೇಳಲು",
			"14": "<sup>14</sup> ಅವನ ಜೊತೆಗಾರನು - ಇದು ಇಸ್ರಾಯೇಲ್ಯನಾದ ಯೋವಾಷನ ಮಗ ಗಿದ್ಯೋನನ ಕತ್ತಿಯೇ ಹೊರತು ಮತ್ತೊಂದಲ್ಲ; ದೇವರು ಮಿದ್ಯಾನ್ಯರನ್ನೂ ಅವರ ಪಾಳೆಯಗಳನ್ನೂ ಅವನ ಕೈಗೆ ಒಪ್ಪಿಸಿದನು ಅಂದನು.",
			"15": "<sup>15</sup> ಗಿದ್ಯೋನನು ಆ ಕನಸನ್ನೂ ಅದರ ಅರ್ಥವನ್ನೂ ಕೇಳಿದಾಗ ಯೆಹೋವನಿಗೆ ಅಡ್ಡಬಿದ್ದು ಇಸ್ರಾಯೇಲ್ಯರ ಪಾಳೆಯಕ್ಕೆ ಹಿಂದಿರುಗಿ ಬಂದು ಅವರಿಗೆ - ಏಳಿರಿ, ಯೆಹೋವನು ಮಿದ್ಯಾನ್ಯರ ಪಾಳೆಯವನ್ನು ನಿಮ್ಮ ಕೈಗೆ ಒಪ್ಪಿಸಿದ್ದಾನೆ ಅಂದನು.",
			"16": "<sup>16</sup> ಮತ್ತು ಅವನು ಆ ಮುನ್ನೂರು ಮಂದಿಯನ್ನು ಮೂರು ಗುಂಪು ಮಾಡಿ ಪ್ರತಿಯೊಬ್ಬನ ಕೈಯಲ್ಲಿ ಕೊಂಬನ್ನೂ ಉರಿಯುವ ಪಂಜಡಗಿರುವ ಬರಿಕೊಡವನ್ನೂ ಕೊಟ್ಟು",
			"17": "<sup>17</sup> ಅವರಿಗೆ - ನನ್ನನ್ನೇ ನೋಡುತ್ತಾ ನಾನು ಮಾಡುವ ಹಾಗೆ ಮಾಡಿರಿ. ನಾನು ಪಾಳೆಯದ ಅಂಚಿಗೆ ಬಂದಾಗ ಹೇಗೆ ಮಾಡುತ್ತೇನೋ ಹಾಗೆ ನೀವೂ ಮಾಡಬೇಕು.",
			"18": "<sup>18</sup> ನಾನೂ ನನ್ನ ಸಂಗಡ ಇದ್ದವರೂ ಕೊಂಬುಗಳನ್ನು ಊದಲು ನೀವೂ ಪಾಳೆಯದ ಸುತ್ತಲೂ ಕೊಂಬನ್ನು ಊದಿ - ಯೆಹೋವನಿಗೆ ಜಯ, ಗಿದ್ಯೋನನಿಗೆ ಜಯ ಎಂದು ಕೂಗಿರಿ ಎಂದು ಹೇಳಿದನು.",
			"19": "<sup>19</sup> ರಾತ್ರಿಯ ಮಧ್ಯಯಾಮವು ಪ್ರಾರಂಭಿಸಿ ಕಾವಲುಗಾರರು ಬದಲಾದ ಕೂಡಲೆ ಗಿದ್ಯೋನನೂ ಅವನ ಸಂಗಡ ಇದ್ದ ನೂರು ಮಂದಿಯೂ [ಶತ್ರುಗಳ] ಪಾಳೆಯದ ಅಂಚಿಗೆ ಬಂದು ಕೊಂಬುಗಳನ್ನು ಊದಿ ಕೈಯಲ್ಲಿದ್ದ ಕೊಡಗಳನ್ನು ಒಡೆದುಬಿಟ್ಟರು.",
			"20": "<sup>20</sup> ಕೂಡಲೆ ಮೂರು ಗುಂಪಿನವರೂ ಕೊಂಬುಗಳನ್ನು ಊದಿ ಕೊಡಗಳನ್ನು ಒಡೆದುಬಿಟ್ಟು ಎಡಗೈಯಲ್ಲಿ ಪಂಜುಗಳನ್ನೂ ಬಲಗೈಯಲ್ಲಿ ಊದುವ ಕೊಂಬುಗಳನ್ನೂ ಹಿಡಿದು - ಯೆಹೋವನ ಕತ್ತಿ, ಗಿದ್ಯೋನನ ಕತ್ತಿ ಎಂದು ಕೂಗಿ",
			"21": "<sup>21</sup> ಪಾಳೆಯದ ಸುತ್ತಲೂ ತಮ್ಮ ತಮ್ಮ ಸ್ಥಳದಲ್ಲಿ ನಿಂತರು; ಪಾಳೆಯದವರಾದರೋ ಭ್ರಾಂತರಾಗಿ ಕೂಗುತ್ತಾ ಓಡಿಹೋಗ ಪ್ರಾರಂಭಿಸಿದರು.",
			"22": "<sup>22</sup> ಆ ಮುನ್ನೂರು ಮಂದಿಯು ಕೊಂಬುಗಳನ್ನು ಊದುತ್ತಿರುವಲ್ಲಿ ಪಾಳೆಯದವರು ಒಬ್ಬರನ್ನೊಬ್ಬರು ಹತಮಾಡಿಕೊಳ್ಳುವಂತೆ ಯೆಹೋವನು ಮಾಡಿದನು. ಪಾಳೆಯದವರು ಚೆರೇರದ ದಾರಿಯಲ್ಲಿರುವ ಬೇತ್‍ಷಿಟ್ಟದವರೆಗೂ ಟಬ್ಬಾತಿನ ಬಳಿಯಲ್ಲಿರುವ ಆಬೇಲ್ಮೆಹೋಲಾ ಪ್ರಾಂತ್ಯದವರೆಗೂ ಓಡಿಹೋದರು.",
			"23": "<sup>23</sup> ಇಸ್ರಾಯೇಲ್ಯರಾದ ನಫ್ತಾಲಿ ಆಶೇರ್ ಕುಲಗಳವರೂ ಮನಸ್ಸೆಕುಲದ ಎಲ್ಲಾ ಜನರೂ ಕೂಡಿ ಬಂದು ಮಿದ್ಯಾನ್ಯರನ್ನು ಹಿಂದಟ್ಟಿದರು.",
			"24": "<sup>24</sup> ಇದಲ್ಲದೆ ಗಿದ್ಯೋನನು ಎಫ್ರಾಯೀಮ್ ಪರ್ವತಪ್ರದೇಶಗಳಿಗೆ ದೂತರನ್ನು ಅಟ್ಟಿ ಅಲ್ಲಿನವರಿಗೆ - ನೀವು ಮಿದ್ಯಾನ್ಯರಿಗೆ ವಿರೋಧವಾಗಿ ಹೊರಟು ಅವರು ಬೇತ್‍ಬಾರದವರೆಗಿರುವ ಪ್ರವಾಹಗಳನ್ನೂ ಯೊರ್ದನ್ ಹೊಳೆಯನ್ನೂ ದಾಟಿ ಹೋಗದಂತೆ ಅಡ್ಡಗಟ್ಟಿರಿ ಎಂದು ಹೇಳಿಸಿದನು. ಆಗ ಎಫ್ರಾಯೀಮ್ಯರೆಲ್ಲರೂ ಬೇತ್‍ಬಾರದವರೆಗಿರುವ ಪ್ರವಾಹಗಳಿಗೂ ಯೊರ್ದನ್‍ಹೊಳೆಗೂ ಬಂದು ಅವುಗಳ ಹಾಯಗಡಗಳನ್ನೆಲ್ಲಾ ಹಿಡಿದರು.",
			"25": "<sup>25</sup> ಇದಲ್ಲದೆ ಅವರು ಮಿದ್ಯಾನ್ಯರ ನಾಯಕರಾದ ಓರೇಬ್, ಜೇಬ್ ಎಂಬವರನ್ನು ಹಿಡಿದು ಓರೇಬನನ್ನು ಓರೇಬನ ಬಂಡೆಯ ಮೇಲೆಯೂ ಜೇಬನನ್ನು ಜೇಬನ ದ್ರಾಕ್ಷೆಯ ಆಲೆಯಲ್ಲಿಯೂ ಕೊಂದು ಅವರ ತಲೆಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಮಿದ್ಯಾನ್ಯರನ್ನು ಹಿಂದಟ್ಟುತ್ತಾ ಯೊರ್ದನಿನ ಆಚೆಗೆ ಹೋಗಿ ಅಲ್ಲಿದ್ದ ಗಿದ್ಯೋನನಿಗೆ ಅವುಗಳನ್ನು ಒಪ್ಪಿಸಿದರು."
		},
		"8": {
			"1": "<sup>1</sup> ಅನಂತರ ಎಫ್ರಾಯೀಮ್ಯರು ಗಿದ್ಯೋನನಿಗೆ - ನೀನು ಹೀಗೆ ಯಾಕೆ ಮಾಡಿದಿ? ಮಿದ್ಯಾನ್ಯರೊಡನೆ ಯುದ್ಧಮಾಡುವದಕ್ಕೆ ಹೊರಟಾಗ ನಮ್ಮನ್ನೇಕೆ ಕರೆಯಲಿಲ್ಲ ಎಂದು ಉಗ್ರದಿಂದ ಕಲಹಮಾಡಿದರು.",
			"2": "<sup>2</sup> ಆಗ ಅವನು ಅವರಿಗೆ - ನೀವು ಮಾಡಿದ ಕಾರ್ಯಕ್ಕೆ ಸಮಾನವಾದದ್ದನ್ನು ನಾನೇನು ಮಾಡಿದೆನು? ಅಬೀಯೆಜೆರನವರು ಸುಗ್ಗಿಯಲ್ಲಿ ಕೂಡಿಸಿದ್ದಕ್ಕಿಂತಲೂ ಎಫ್ರಾಯೀಮ್ಯರು ಹಕ್ಕಲಾಯ್ದು ಕೂಡಿಸಿದ್ದು ಹೆಚ್ಚಾಗಿದೆಯಲ್ಲವೇ.",
			"3": "<sup>3</sup> ದೇವರು ನಿಮ್ಮ ಕೈಯಲ್ಲಿ ಮಿದ್ಯಾನ್ಯರ ನಾಯಕರಾದ ಓರೇಬ್ ಜೇಬರನ್ನು ಒಪ್ಪಿಸಿಕೊಟ್ಟಿದ್ದಾನಲ್ಲಾ! ಇದಕ್ಕೆ ಸಮಾನವಾದದ್ದನ್ನು ನಾನೇನು ಮಾಡಿದ್ದೇನೆ ಎಂದನು. ಈ ಮಾತುಗಳನ್ನು ಕೇಳಿದಾಗ ಅವರ ಸಿಟ್ಟಿಳಿಯಿತು.",
			"4": "<sup>4</sup> ಗಿದ್ಯೋನನೂ ಅವನ ಸಂಗಡ ಇದ್ದ ಮುನ್ನೂರು ಮಂದಿಯೂ ಬಹಳ ದಣಿದವರಾಗಿದ್ದರೂ ಹಿಂದಟ್ಟುತ್ತಾ ಯೊರ್ದನನ್ನು ದಾಟಿ [ಸುಖೋತಿಗೆ] ಬಂದರು.",
			"5": "<sup>5</sup> ಗಿದ್ಯೋನನು ಸುಖೋತಿನವರಿಗೆ - ದಯವಿಟ್ಟು ನನ್ನ ಜೊತೆಯಲ್ಲಿ ಬಂದಿರುವವರಿಗೆ ಕೆಲವು ರೊಟ್ಟಿಗಳನ್ನು ಕೊಡಿರಿ, ಅವರು ಬಹಳ ದಣಿದಿದ್ದಾರೆ; ನಾನು ಮಿದ್ಯಾನ್ಯರ ಅರಸರಾದ ಜೆಬಹ, ಚಲ್ಮುನ್ನ ಎಂಬವರನ್ನು ಹಿಂದಟ್ಟುತ್ತಾ ಇದ್ದೇನೆ ಅಂದನು.",
			"6": "<sup>6</sup> ಆದರೆ ಸುಖೋತಿನ ಮುಖಂಡರು ಅವನಿಗೆ - ನಾವು ನಿನ್ನ ಸೈನಿಕರಿಗೆ ರೊಟ್ಟಿಗಳನ್ನು ಕೊಡುವದಕ್ಕೆ ಜೆಬಹ, ಚಲ್ಮುನ್ನ ಎಂಬವರನ್ನು ಕೈಕಟ್ಟಿ ವಶಮಾಡಿಕೊಂಡಿದ್ದೀಯೋ ಎಂದು ಉತ್ತರಕೊಟ್ಟರು.",
			"7": "<sup>7</sup> ಆಗ ಗಿದ್ಯೋನನು ಅವರಿಗೆ - ಒಳ್ಳೇದು, ಯೆಹೋವನು ಜೆಬಹ, ಚಲ್ಮುನ್ನ ಎಂಬವರನ್ನು ನನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸಿ ಕೊಟ್ಟ ತರುವಾಯ ನಾನು ನಿಮ್ಮನ್ನು ಜಾಲಿಮುಳ್ಳುಗಳಿಂದಲೂ ಕಾರೆಗಿಡಗಳಿಂದಲೂ ಹೊಡಿಸುವೆನು ಎಂದು ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ಅವನು ಅಲ್ಲಿಂದ ಪೆನೂವೇಲಿಗೆ ಬಂದು ಅಲ್ಲಿನವರನ್ನು ಅದೇ ಪ್ರಕಾರ ಬಿನ್ನವಿಸಲು ಅವರೂ ಸುಖೋತಿನವರಂತೆಯೇ ಉತ್ತರಕೊಟ್ಟರು.",
			"9": "<sup>9</sup> ಗಿದ್ಯೋನನು ಅವರಿಗೆ - ನಾನು ಸುರಕ್ಷಿತವಾಗಿ ಹಿಂದಿರುಗಿ ಬಂದಾಗ ಈ ಬುರುಜನ್ನು ಕೆಡವಿಬಿಡುವೆನು ಅಂದನು.",
			"10": "<sup>10</sup> ಜೆಬಹ, ಚಲ್ಮುನ್ನ ಎಂಬವರು ಸುಮಾರು ಹದಿನೈದು ಸಾವಿರ ಮಂದಿ ಸೈನಿಕರೊಡನೆ ಕರ್ಕೋರಿನಲ್ಲಿ ಇಳುಕೊಂಡಿದ್ದರು. ಮೂಡಣ ದೇಶದವರ ಸೈನ್ಯದಲ್ಲಿ ಲಕ್ಷದ ಇಪ್ಪತ್ತು ಸಾವಿರ ಭಟರು ಹತರಾಗಿ ಉಳಿದವರು ಇಷ್ಟೇ ಮಂದಿ.",
			"11": "<sup>11</sup> ಗಿದ್ಯೋನನು ಕಾಡುಗೊಲ್ಲರ ಪ್ರದೇಶಮಾರ್ಗವಾಗಿ ನೋಬಹ, ಯೊಗ್ಬೆಹಾ ಇವುಗಳ ಪೂರ್ವದಿಕ್ಕಿಗೆ ಹೋಗಿ ನಿರ್ಭಯದಿಂದ ಇಳುಕೊಂಡಿದ್ದ ಆ ಪಾಳೆಯದ ಮೇಲೆ ಬಿದ್ದನು.",
			"12": "<sup>12</sup> ಜೆಬಹನೂ ಚಲ್ಮುನ್ನನೂ ಓಡಿ ಹೋಗಲಾಗಿ ಅವರನ್ನು ಹಿಂದಟ್ಟಿ ಹಿಡಿದು ಅವರ ಸೈನ್ಯವನ್ನು ಚದರಿಸಿ ಬಿಟ್ಟನು.",
			"13": "<sup>13</sup> ಯೋವಾಷನ ಮಗನಾದ ಗಿದ್ಯೋನನು ಯುದ್ಧದಿಂದ ಹೆರೆಸಿನ ಗಟ್ಟದ ಮಾರ್ಗವಾಗಿ ಹಿಂದಿರುಗಿ ಬಂದು",
			"14": "<sup>14</sup> ಸುಖೋತಿನ ಒಬ್ಬ ಯೌವನಸ್ಥನನ್ನು ಹಿಡಿದು ಆ ಊರಿನ ಮುಖಂಡರೂ ಹಿರಿಯರೂ ಯಾರಾರೆಂದು ವಿಚಾರಿಸಿ ಎಪ್ಪತ್ತೇಳು ಮಂದಿಯ ಹೆಸರುಗಳನ್ನು ಬರಿಸಿಕೊಂಡನು.",
			"15": "<sup>15</sup> ಅನಂತರ ಅವನು ಸುಖೋತಿನವರ ಬಳಿಗೆ ಹೋಗಿ ಅವರಿಗೆ - ನಾವು ದಣಿದಿರುವ ನಿನ್ನ ಸೈನಿಕರಿಗೋಸ್ಕರ ರೊಟ್ಟಿಗಳನ್ನು ಕೊಡುವದಕ್ಕೆ ಜೆಬಹ, ಚಲ್ಮುನ್ನ ಎಂಬವರನ್ನು ಕೈಕಟ್ಟಿ ಸ್ವಾಧೀನ ಮಾಡಿಕೊಂಡಿದ್ದೀಯೋ ಎಂದು ನನ್ನನ್ನು ನಿಂದಿಸಿದಿರಲ್ಲಾ;",
			"16": "<sup>16</sup> ನೋಡಿರಿ, ಅವರು ಇಲ್ಲಿರುತ್ತಾರೆ ಅಂದು ಊರಿನ ಹಿರಿಯರನ್ನೂ ಮುಖಂಡರನ್ನೂ ಜಾಲಿ ಮುಳ್ಳುಗಳಿಂದಲೂ ಕಾರೆಗಿಡಗಳಿಂದಲೂ ಹೊಡಿಸಿ ಸುಖೋತಿನವರಿಗೆ ಬುದ್ಧಿಕಲಿಸಿದನು.",
			"17": "<sup>17</sup> ಮತ್ತು ಪೆನೂವೇಲಿನ ಬುರುಜನ್ನು ಕೆಡವಿಬಿಟ್ಟು ಆ ಊರಿನ ಜನರನ್ನು ಹತಿಸಿದನು.",
			"18": "<sup>18</sup> ಮತ್ತು ಅವನು ಜೆಬಹ, ಚಲ್ಮುನ್ನ ಎಂಬವರನ್ನು - ನೀವು ತಾಬೋರದಲ್ಲಿ ಕೊಂದು ಹಾಕಿದ ಮನುಷ್ಯರು ಹೇಗಿದ್ದರು ಎಂದು ಕೇಳಲಾಗಿ ಅವರು ಅವನಿಗೆ - ನಿನ್ನ ಹಾಗೆಯೇ ಇದ್ದರು; ಎಲ್ಲರೂ ರೂಪದಲ್ಲಿ ರಾಜಪುತ್ರರಂತಿದ್ದರು ಎಂದು ಉತ್ತರಕೊಟ್ಟರು.",
			"19": "<sup>19</sup> ಆಗ ಅವನು - ಅವರು ನನ್ನ ತಾಯಿಯ ಮಕ್ಕಳು; ನನ್ನ ಸಹೋದರರು; ಯೆಹೋವನಾಣೆ, ನೀವು ಅವರನ್ನು ಉಳಿಸಿದ್ದರೆ ನಾನು ನಿಮ್ಮನ್ನು ಕೊಲ್ಲುತ್ತಿದ್ದಿಲ್ಲ ಎಂದು ಹೇಳಿ",
			"20": "<sup>20</sup> ತನ್ನ ಹಿರೀ ಮಗನಾದ ಎತೆರನಿಗೆ - ನೀನೆದ್ದು ಇವರನ್ನು ಕೊಂದುಹಾಕು ಅಂದನು. ಆದರೆ ಅವನು ಇನ್ನೂ ಹುಡುಗನಾಗಿದ್ದದರಿಂದ ಭಯಪಟ್ಟು ಕತ್ತಿಯನ್ನು ಹಿರಿಯಲೇ ಇಲ್ಲ.",
			"21": "<sup>21</sup> ಆಗ ಜೆಬಹನೂ ಚಲ್ಮುನ್ನನೂ ಗಿದ್ಯೋನನಿಗೆ - ನೀನೇ ಎದ್ದು ಬಂದು ನಮ್ಮ ಮೇಲೆ ಬೀಳು; ಪ್ರಾಯಕ್ಕೆ ತಕ್ಕಂತೆ ಪುರುಷನಿಗೆ ಬಲವಿರುತ್ತದಲ್ಲಾ ಅನ್ನಲು ಅವನೆದ್ದು ಜೆಬಹ, ಚಲ್ಮುನ್ನ ಎಂಬವರನ್ನು ಕೊಂದುಹಾಕಿ, ಅವರ ಒಂಟೆಗಳ ಕೊರಳಲ್ಲಿದ್ದ ಅರ್ಧಚಂದ್ರಾಕಾರದ ಆಭರಣಗಳನ್ನು ತೆಗೆದುಕೊಂಡನು.",
			"22": "<sup>22</sup> ತರುವಾಯ ಇಸ್ರಾಯೇಲ್ಯರು ಗಿದ್ಯೋನನಿಗೆ - ನೀನು ನಮ್ಮನ್ನು ಮಿದ್ಯಾನ್ಯರ ಕೈಯಿಂದ ಬಿಡಿಸಿದ್ದೀಯಲ್ಲಾ; ನೀನೂ ನಿನ್ನ ಮಗನೂ ಮೊಮ್ಮಗನೂ ವಂಶಪಾರಂಪರ್ಯವಾಗಿ ನಮ್ಮ ಮೇಲೆ ಅರಸರಾಗಿರಬೇಕು ಅನ್ನಲು",
			"23": "<sup>23</sup> ಅವನು ಅವರಿಗೆ - ನಾನಾಗಲಿ ನನ್ನ ಮಗನಾಗಲಿ ನಿಮ್ಮನ್ನು ಆಳುವದಿಲ್ಲ; ಯೆಹೋವನೇ ನಿಮ್ಮ ಅರಸನಾಗಿರುವನು.",
			"24": "<sup>24</sup> ಆದರೆ ನಿಮಗೆ ಒಂದು ಬಿನ್ನಹ ಮಾಡುತ್ತೇನೆ; ಪ್ರತಿಯೊಬ್ಬನು ತಾನು ಕೊಳ್ಳೆ ಹೊಡೆದ ಮುರುವುಗಳನ್ನು ನನಗೆ ಕೊಡಲಿ ಅಂದನು. ಮಿದ್ಯಾನ್ಯರು ಇಷ್ಮಾಯೇಲ್ಯರಾಗಿದ್ದದರಿಂದ ಬಂಗಾರದ ಮುರುವುಗಳನ್ನು ಹಾಕಿಕೊಳ್ಳುತ್ತಿದ್ದರು. ಗಿದ್ಯೋನನ ಬಿನ್ನಹಕ್ಕೆ ಇಸ್ರಾಯೇಲ್ಯರು -",
			"25": "<sup>25</sup> ನಾವು ಸಂತೋಷದಿಂದ ಕೊಡುವೆವು ಎಂದು ಹೇಳಿ ಅಲ್ಲಿ ಒಂದು ಬಟ್ಟೆಯನ್ನು ಹಾಸಿ ಅದರ ಮೇಲೆ ತಾವು ಕೊಳ್ಳೆಯಾಗಿ ತಂದ ಮುರುವುಗಳನ್ನು ಇಟ್ಟುಬಿಟ್ಟರು.",
			"26": "<sup>26</sup> ಗಿದ್ಯೋನನಿಗೆ ಮೊದಲೇ ದೊರಕಿದ್ದ ಅರ್ಧಚಂದ್ರಾಕಾರದ ಆಭರಣ, ಕುಂಡಲ, ಮಿದ್ಯಾನ್ ರಾಜರು ಹೊದ್ದುಕೊಂಡಿದ್ದ ರಕ್ತಾಂಬರ, ಅವರ ಒಂಟೆಗಳ ಕಂಠಮಾಲೆ ಇವುಗಳ ಹೊರತಾಗಿ ಅವನು ಇಸ್ರಾಯೇಲ್ಯರಿಂದ ಕೇಳಿ ತೆಗೆದುಕೊಂಡ ಮುರುವುಗಳ ಬಂಗಾರವೇ ಸಾವಿರದ ಏಳುನೂರು ತೊಲೆ ತೂಕವಾಗಿತ್ತು.",
			"27": "<sup>27</sup> ಈ ಬಂಗಾರದಿಂದ ಗಿದ್ಯೋನನು ಒಂದು ಏಫೋದನ್ನು ಮಾಡಿಸಿ ಅದನ್ನು ತನ್ನ ಊರಾದ ಒಫ್ರದಲ್ಲಿಟ್ಟನು. ಅದರಿಂದ ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರೂ ದೇವದ್ರೋಹಿಗಳಾದರು; ಅದು ಗಿದ್ಯೋನನಿಗೂ ಅವನ ಮನೆಯವರಿಗೂ ಉರುಲಾಯಿತು.",
			"28": "<sup>28</sup> ಮಿದ್ಯಾನ್ಯರು ಇಸ್ರಾಯೇಲ್ಯರ ಮುಂದೆ ಬಹಳವಾಗಿ ತಗ್ಗಿಸಲ್ಪಟ್ಟರು; ಅವರು ತಿರಿಗಿ ತಲೆಯೆತ್ತಲಿಲ್ಲ. ಗಿದ್ಯೋನನ ಜೀವಮಾನದಲ್ಲಿ ನಾಲ್ವತ್ತು ವರುಷಗಳ ಪರ್ಯಂತ ದೇಶದಲ್ಲಿ ಸಮಾಧಾನವಿತ್ತು.",
			"29": "<sup>29</sup> ಯೋವಾಷನ ಮಗ ಯೆರುಬ್ಬಾಳನು ಹೋಗಿ ತನ್ನ ಮನೆಯಲ್ಲಿ ವಾಸವಾಗಿದ್ದನು.",
			"30": "<sup>30</sup> ಗಿದ್ಯೋನನಿಗೆ ಅನೇಕ ಮಂದಿ ಹೆಂಡತಿಯರಿದ್ದದರಿಂದ ಅವನಿಗೆ ಎಪ್ಪತ್ತು ಮಂದಿ ಗಂಡು ಮಕ್ಕಳು ಹುಟ್ಟಿದರು.",
			"31": "<sup>31</sup> ಅವನು ಶೆಕೆಮಿನಲ್ಲಿದ್ದ ತನ್ನ ಉಪಪತ್ನಿಯಲ್ಲಿ ಇನ್ನೊಬ್ಬ ಮಗನು ಹುಟ್ಟಲು ಅವನಿಗೆ ಅಬೀಮೆಲೆಕನೆಂದು ಹೆಸರಿಟ್ಟನು.",
			"32": "<sup>32</sup> ಯೋವಾಷನ ಮಗನಾದ ಗಿದ್ಯೋನನು ದಿನತುಂಬಿದ ಮುದುಕನಾಗಿ ಮರಣ ಹೊಂದಲು ಅವನ ಶವವನ್ನು ಅಬೀಯೆಜೆರೀಯರ ಒಫ್ರದಲ್ಲಿದ್ದ ಒಂದು ಸಮಾಧಿಯಲ್ಲಿ ಹೂಣಿಟ್ಟರು. ಆ ಸಮಾಧಿಯು ಅವನ ತಂದೆಯಾದ ಯೋವಾಷನದಾಗಿತ್ತು.",
			"33": "<sup>33</sup> ಗಿದ್ಯೋನನು ಸತ್ತನಂತರ ಇಸ್ರಾಯೇಲ್ಯರು ದೇವದ್ರೋಹಿಗಳಾಗಿ ಬಾಳನ ಪ್ರತಿಮೆಗಳನ್ನು ಪೂಜಿಸಿದರು; ಬಾಳ್‍ಬೆರೀತನನ್ನು ತಮಗೆ ದೇವರು ಮಾಡಿಕೊಂಡರು.",
			"34": "<sup>34</sup> ಸುತ್ತಣ ಎಲ್ಲಾ ಶತ್ರುಗಳಿಂದ ತಮ್ಮನ್ನು ಬಿಡಿಸಿದ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಮರೆತುಬಿಟ್ಟರು.",
			"35": "<sup>35</sup> ಯೆರುಬ್ಬಾಳನೆಂಬ ಗಿದ್ಯೋನನು ತಮಗೆ ಮಾಡಿದ ಉಪಕಾರಗಳನ್ನು ನೆನಸಲಿಲ್ಲ; ಅವನ ಮನೆಯವರಿಗೆ ದಯೆತೋರಿಸಲಿಲ್ಲ."
		},
		"9": {
			"1": "<sup>1</sup> ಯೆರುಬ್ಬಾಳನ ಮಗನಾದ ಅಬೀಮೆಲೆಕನು ಶೆಕೆಮಿಗೆ ಹೋಗಿ ತನ್ನ ಸೋದರ ಮಾವಂದಿರಿಗೂ ತಾಯಿಯ ಬಂಧುಗಳೆಲ್ಲರಿಗೂ ಹೇಳಿದ್ದೇನಂದರೆ -",
			"2": "<sup>2</sup> ನೀವು ದಯವಿಟ್ಟು ಶೆಕೆಮಿನ ಹಿರಿಯರನ್ನು ಮಾತಾಡಿಸಿ - ಯೆರುಬ್ಬಾಳನ ಎಪ್ಪತ್ತು ಮಂದಿ ಮಕ್ಕಳು ನಿಮ್ಮನ್ನು ಆಳುವದು ಒಳ್ಳೇದೋ ಅಥವಾ ಒಬ್ಬನೇ ಆಳುವದು ಮೇಲೋ ಎಂದು ಅವರನ್ನು ಕೇಳಿ ನಾನು ಅವರ ರಕ್ತ ಸಂಬಂಧಿಯಾಗಿದ್ದೇನೆಂಬದನ್ನು ಜ್ಞಾಪಕಪಡಿಸಿರಿ ಅಂದನು.",
			"3": "<sup>3</sup> ಅವರು ಶೆಕೆಮಿನ ಹಿರಿಯರ ಬಳಿಗೆ ಹೋಗಿ ಅಬೀಮೆಲೆಕನ ವಿಷಯದಲ್ಲಿ ಹಾಗೆಯೇ ಹೇಳಲು ಅವರು - ಇವನು ನಮ್ಮ ಸಹೋದರನಾಗಿದ್ದಾನಲ್ಲಾ ಅಂದುಕೊಂಡು ಅವನ ಪಕ್ಷವನ್ನು ಹಿಡಿಯುವದಕ್ಕೆ ಒಡಂಬಟ್ಟರು.",
			"4": "<sup>4</sup> ಮತ್ತು ಅವರು ಬಾಳ್‍ಬೆರೀತಿನ ದೇವಸ್ಥಾನದಿಂದ ಎಪ್ಪತ್ತು ರೂಪಾಯಿಗಳನ್ನು ಅವನಿಗೆ ಕೊಟ್ಟರು. ಅವನು ಈ ಹಣದಿಂದ ಕೆಲವು ಮಂದಿ ಕಾಕಪೋಕರನ್ನು ಕೂಡಿಸಿ",
			"5": "<sup>5</sup> ಅವರ ನಾಯಕನಾಗಿ ಹೊರಟು ಒಫ್ರದಲ್ಲಿದ್ದ ತನ್ನ ತಂದೆಯಾದ ಯೆರುಬ್ಬಾಳನ ಮನೆಗೆ ಹೋಗಿ ಅವನ ಎಪ್ಪತ್ತು ಮಂದಿ ಮಕ್ಕಳನ್ನು ಹಿಡಿದು ಒಂದೇ ಬಂಡೆಯ ಮೇಲೆ ಕೊಲ್ಲಿಸಿದನು. ಆದರೆ ಕಿರಿಯವನಾದ ಯೋತಾಮನೆಂಬವನು ಅಡಗಿಕೊಂಡು ಉಳಿದನು.",
			"6": "<sup>6</sup> ತರುವಾಯ ಎಲ್ಲಾ ಶೆಕೆಮಿನವರೂ ಮಿಲ್ಲೋ ಕೋಟೆಯವರೂ ಶೆಕೆಮಿನ ಬಳಿಯಲ್ಲಿ ಜ್ಞಾಪಕಸ್ತಂಭವಿರುವ ಏಲೋನ್ ವೃಕ್ಷದ ಹತ್ತಿರ ಕೂಡಿಬಂದು ಅಬೀಮೆಲೆಕನನ್ನು ತಮ್ಮ ಅರಸನನ್ನಾಗಿ ಏರ್ಪಡಿಸಿಕೊಂಡರು.",
			"7": "<sup>7</sup> ಯೋತಾಮನು ಇದನ್ನು ಕೇಳಿ ಗೆರಿಜ್ಜೀಮ್ ಬೆಟ್ಟದ ತುದಿಯಲ್ಲಿ ನಿಂತು ಗಟ್ಟಿಯಾದ ಧ್ವನಿಯಿಂದ - ಶೆಕೆಮಿನವರೇ, ನನ್ನ ಮಾತನ್ನು ಕೇಳಿರಿ; ಆಗ ದೇವರು ನಿಮಗೆ ಕಿವಿಗೊಡುವನು.",
			"8": "<sup>8</sup> ಮರಗಳು ತಮಗೋಸ್ಕರ ಒಬ್ಬ ಅರಸನನ್ನು ಅಭಿಷೇಕಿಸಬೇಕೆಂದು ಹೊರಟು ಎಣ್ಣೆಯ ಮರದ ಬಳಿಗೆ ಬಂದು - ನೀನು ಬಂದು ನಮಗೆ ಅರಸನಾಗು ಅಂದವು.",
			"9": "<sup>9</sup> ಆಗ ಅದು ಅವುಗಳಿಗೆ - ನನ್ನಲ್ಲಿರುವ ಎಣ್ಣೆಗೋಸ್ಕರ ದೇವತೆಗಳೂ ಮನುಷ್ಯರೂ ನನ್ನನ್ನು ಘನಪಡಿಸುತ್ತಾರೆ; ಇದನ್ನು ಬಿಟ್ಟು ಮರಗಳ ಮೇಲೆ ಓಲಾಡುವದಕ್ಕೆ ಬರಬೇಕೋ ಅಂದಿತು.",
			"10": "<sup>10</sup> ತರುವಾಯ ಅವು ಅಂಜೂರ ಗಿಡದ ಬಳಿಗೆ ಹೋಗಿ ಅದಕ್ಕೆ - ನೀನು ಬಂದು ನಮ್ಮ ಅರಸನಾಗು ಅನ್ನಲು",
			"11": "<sup>11</sup> ಅದು - ನಾನು ನನ್ನ ಶ್ರೇಷ್ಠವಾದ ಮಧುರಫಲ ದಾನವನ್ನು ಬಿಟ್ಟು ಮರಗಳ ಮೇಲೆ ಓಲಾಡುವದಕ್ಕೆ ಬರಬೇಕೋ ಅಂದಿತು.",
			"12": "<sup>12</sup> ಅನಂತರ ಅವು ದ್ರಾಕ್ಷಾಲತೆಯ ಬಳಿಗೆ ಹೋಗಿ - ನೀನು ಬಂದು ನಮ್ಮ ಅರಸನಾಗು ಎನ್ನಲು",
			"13": "<sup>13</sup> ಅದು ಅವುಗಳಿಗೆ - ನಾನು ನನ್ನ ರಸದಿಂದ ದೇವತೆಗಳಿಗೂ ಮನುಷ್ಯರಿಗೂ ಆನಂದಕೊಡುವದನ್ನು ಬಿಟ್ಟು ಮರಗಳ ಮೇಲೆ ಓಲಾಡುವದಕ್ಕೆ ಬರಬೇಕೋ ಅಂದಿತು.",
			"14": "<sup>14</sup> ಕಡೆಗೆ ಎಲ್ಲಾ ಮರಗಳು ಮುಳ್ಳುಗಿಡದ ಬಳಿಗೆ ಹೋಗಿ - ನೀನು ಬಂದು ನಮ್ಮ ಅರಸನಾಗು ಅನ್ನಲು",
			"15": "<sup>15</sup> ಅದು - ನೀವು ಯಥಾರ್ಥಮನಸ್ಸಿನಿಂದ ನನಗೆ ರಾಜ್ಯಾಭಿಷೇಕ ಮಾಡಬೇಕೆಂದಿರುವದಾದರೆ ಬಂದು ನನ್ನ ನೆರಳನ್ನು ಆಶ್ರಯಿಸಿಕೊಳ್ಳಿರಿ; ಇಲ್ಲವಾದರೆ ನನ್ನಿಂದ ಬೆಂಕಿಹೊರಟು ಲೆಬನೋನಿನ ದೇವದಾರುವೃಕ್ಷಗಳನ್ನು ದಹಸಿಬಿಡುವದು ಎಂದಿತು.",
			"16": "<sup>16</sup> ನೀವು ಅಬೀಮೆಲೆಕನನ್ನು ಅರಸನನ್ನಾಗಿ ಮಾಡಿದ್ದು ನ್ಯಾಯವೋ? ಅದು ಧರ್ಮಕಾರ್ಯವೋ? ಯೆರುಬ್ಬಾಳನು ನಿಮಗೆ ಮಾಡಿದ ಉಪಕಾರಕ್ಕೋಸ್ಕರ ನೀವು ಅವನಿಗೂ ಅವನ ಮನೆಯವರಿಗೂ ಪ್ರತ್ಯುಪಕಾರಮಾಡಿದಿರೋ?",
			"17": "<sup>17</sup> ನನ್ನ ತಂದೆಯು ತನ್ನ ಜೀವದ ಆಶೆಯನ್ನು ತೊರೆದು ಹೋರಾಡಿ ನಿಮ್ಮನ್ನು ಮಿದ್ಯಾನ್ಯರ ಕೈಯಿಂದ ಬಿಡಿಸಿದನಲ್ಲಾ.",
			"18": "<sup>18</sup> ಆದರೆ ನೀವು ಈಗ ನನ್ನ ತಂದೆಯ ಮನೆಗೆ ವಿರೋಧವಾಗಿ ನಿಂತು ಅವನ ಎಪ್ಪತ್ತು ಮಂದಿ ಮಕ್ಕಳನ್ನು ಒಂದೇ ಬಂಡೆಯ ಮೇಲೆ ವಧಿಸಿ ಅವನ ದಾಸಿಯ ಮಗನಾದ ಅಬೀಮೆಲೆಕನನ್ನು ನಮ್ಮ ಬಂಧುವೆಂದು ಹೇಳಿ ಶೆಕೆಮಿನ ಅರಸನನ್ನಾಗಿ ಮಾಡಿಕೊಂಡಿದ್ದೀರಿ.",
			"19": "<sup>19</sup> ನೀವು ಈಗ ಯೆರುಬ್ಬಾಳನಿಗೂ ಅವನ ಮನೆಯವರಿಗೂ ಮಾಡಿದ್ದು ನ್ಯಾಯವೂ ಧರ್ಮವೂ ಆಗಿದ್ದರೆ ಅಬೀಮೆಲೆಕನಲ್ಲಿ ಆನಂದಿಸಿರಿ; ಅವನು ನಿಮ್ಮಲ್ಲಿ ಆನಂದಿಸಲಿ.",
			"20": "<sup>20</sup> ಇಲ್ಲವಾದರೆ ಅಬೀಮೆಲೆಕನಿಂದ ಬೆಂಕಿಹೊರಟು ಶೆಕೆಮಿನವರನ್ನೂ ಮಿಲ್ಲೋ ಕೋಟೆಯವರನ್ನೂ ದಹಿಸಿ ಬಿಡಲಿ; ಶೆಕೆಮಿನವರಿಂದಲೂ ಮಿಲ್ಲೋ ಕೋಟೆಯವರಿಂದಲೂ ಬೆಂಕಿಹೊರಟು ಅಬೀಮೆಲೆಕನನ್ನು ದಹಿಸಿ ಬಿಡಲಿ.",
			"21": "<sup>21</sup> ಯೋತಾಮನು ಇಷ್ಟು ಹೇಳಿ ತನ್ನ ಸಹೋದರನಾದ ಅಬೀಮೆಲೆಕನಿಗೆ ತಪ್ಪಿಸಿಕೊಂಡು ಬೇರಕ್ಕೆ ಓಡಿಹೋಗಿ ಅಲ್ಲೇ ವಾಸಮಾಡಿದನು.",
			"22": "<sup>22</sup> ಅಬೀಮೆಲೆಕನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಮೂರು ವರುಷ ಆಳಿದ ನಂತರ",
			"23": "<sup>23</sup> ಯೆಹೋವನು ಅವನಿಗೂ ಶೆಕೆಮಿನ ಹಿರಿಯರಿಗೂ ವೈಮನಸ್ಯ ಹುಟ್ಟುವಂತೆ ಮಾಡಿದನು. ಅವರು ಅವನಿಗೆ ದ್ರೋಹಮಾಡಿದರು.",
			"24": "<sup>24</sup> ಇದರಿಂದ ಯೆರುಬ್ಬಾಳನ ಎಪ್ಪತ್ತು ಮಂದಿ ಮಕ್ಕಳನ್ನು ಕ್ರೂರತನದಿಂದ ಕೊಂದುಹಾಕಿದ ರಕ್ತಾಪರಾಧಫಲವು ಅವರ ಸಹೋದರನಾದ ಅಬೀಮೆಲೆಕನ ಮೇಲೆಯೂ ಆ ಕೃತ್ಯದಲ್ಲಿ ಅವನಿಗೆ ಸಹಾಯಕರಾಗಿದ್ದ ಶೆಕೆಮಿನವರ ಮೇಲೆಯೂ ಬರುವದಕ್ಕೆ ಮಾರ್ಗವಾಯಿತು.",
			"25": "<sup>25</sup> ಶೆಕೆಮಿನವರು ಅಬೀಮೆಲೆಕನಿಗೆ ವಿರೋಧವಾಗಿ ಪರ್ವತಶಿಖರಗಳಲ್ಲಿ ಹೊಂಚುಗಾರರನ್ನಿಟ್ಟರು. ಇವರು ಆ ದಾರಿಗಳಲ್ಲಿ ಸಂಚರಿಸುವ ಪ್ರಯಾಣಿಕರನ್ನು ಸುಲುಕೊಳ್ಳುತ್ತಿದ್ದರು. ಈ ವರ್ತಮಾನವು ಅಬೀಮೆಲೆಕನಿಗೆ ಮುಟ್ಟಿತು.",
			"26": "<sup>26</sup> ಅದೇ ಸಮಯದಲ್ಲಿ ಎಬೆದನ ಮಗನಾದ ಗಾಳನೆಂಬವನು ತನ್ನ ಬಂಧುಗಳೊಡನೆ ಶೆಕೆಮಿಗೆ ಬಂದನು. ಶೆಕೆಮಿನ ಹಿರಿಯರು ಅವನಲ್ಲಿ ಭರವಸವಿಟ್ಟರು.",
			"27": "<sup>27</sup> ಆ ಊರಿನವರು ಒಂದಾನೊಂದು ದಿವಸ ತಮ್ಮ ದ್ರಾಕ್ಷಾಫಲಗಳನ್ನು ಕೊಯಿದು ಆಲೆಗಳಲ್ಲಿ ರಸತೆಗೆದು ತಮ್ಮ ದೇವರ ಗುಡಿಗೆ ಹೋಗಿ ಹಬ್ಬಮಾಡಿ ಉಂಡು ಕುಡಿದು ಅಬೀಮೆಲೆಕನನ್ನು ಶಪಿಸಿದರು.",
			"28": "<sup>28</sup> ಎಬೆದನ ಮಗನಾದ ಗಾಳನೂ ಅವರ ಸಂಗಡ ಕೂಡಿಕೊಂಡು - ಅಬೀಮೆಲೆಕನು ಎಷ್ಟರವನು? ಶೆಕೆಮಿನವರಾದ ನಾವು ಎಷ್ಟರವರು? ನಾವು ಅಬೀಮೆಲೆಕನನ್ನು ಯಾಕೆ ಸೇವಿಸಬೇಕು? ಅವನು ಯೆರುಬ್ಬಾಳನ ಮಗನಲ್ಲವೋ? ಅವನ ಪುರಾಧಿಕಾರಿಯು ಜೆಬುಲನಲ್ಲವೋ? ನಾವು ಅವನನ್ನು ಯಾಕೆ ಸೇವಿಸಬೇಕು? ಶೆಕೆಮನ ತಂದೆಯಾದ ಹಮೋರನ ವಂಶದವರನ್ನೇ ಸೇವಿಸುವಾ.",
			"29": "<sup>29</sup> ಈ ಜನರ ಮೇಲೆ ನನಗೆ ಅಧಿಕಾರವಿದ್ದರೆ ಅಬೀಮೆಲೆಕನನ್ನು ಓಡಿಸಿ ಬಿಡುತ್ತಿದ್ದೆನು ಎಂದು ಹೇಳಿ - ಎಲಾ, ಅಬೀಮೆಲೆಕನೇ, ಸೈನ್ಯವನ್ನು ತೆಗೆದುಕೊಂಡು ನನ್ನೊಡನೆ ಯುದ್ಧಕ್ಕೆ ಬಾ ಎಂದು ಕೊಚ್ಚಿಕೊಂಡನು.",
			"30": "<sup>30</sup> ಪುರಾಧಿಕಾರಿಯಾದ ಜೆಬುಲನು ಎಬೆದನ ಮಗನಾದ ಗಾಳನ ಮಾತುಗಳನ್ನು ಕೇಳಿ ರೇಗಿದವನಾಗಿ ಯಾರಿಗೂ ತಿಳಿಯದಂತೆ ಅಬೀಮೆಲೆಕನ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿ -",
			"31": "<sup>31</sup> ಇಗೋ, ಎಬೆದನ ಮಗನಾದ ಗಾಳನೂ ಅವನ ಬಂಧುಗಳೂ ಇಲ್ಲಿಗೆ ಬಂದು ಶೆಕೆಮಿನವರೆಲ್ಲರನ್ನೂ ನಿನಗೆ ವಿರೋಧವಾಗಿ ಹುರಿದುಂಬಿಸುತ್ತಿದ್ದಾರೆ.",
			"32": "<sup>32</sup> ಆದದರಿಂದ ನೀನು ಈ ರಾತ್ರಿಯಲ್ಲೇ ಸೈನ್ಯವನ್ನು ತೆಗೆದುಕೊಂಡು ಬಂದು ಹೊಲಗಳಲ್ಲಿ ಹೊಂಚುಹಾಕಿಕೊಂಡಿರು.",
			"33": "<sup>33</sup> ಸೂರ್ಯನು ಉದಯಿಸುತ್ತಲೇ ಎದ್ದು ಪಟ್ಟಣದ ಮೇಲೆ ಬೀಳು; ಅವನೂ ಅವನ ಜೊತೆಯಲ್ಲಿರುವವರೂ ನಿನಗೆ ವಿರೋಧವಾಗಿ ಹೊರಟು ಬಂದಾಗ ನಿನಗೆ ಅನುಕೂಲ ತೋರಿದ ಹಾಗೆ ಮಾಡು ಎಂದು ಹೇಳಿಸಿದನು.",
			"34": "<sup>34</sup> ಅಬೀಮೆಲೆಕನೂ ಅವನ ಜನರೂ ರಾತ್ರಿಯಲ್ಲೇ ಹೊರಟು ಬಂದು ನಾಲ್ಕು ಗುಂಪಾಗಿ ಶೆಕೆಮಿನವರನ್ನು ಹೊಂಚಿ ಕಾಯುತ್ತಿದ್ದರು.",
			"35": "<sup>35</sup> ಎಬೆದನ ಮಗನಾದ ಗಾಳನು ಹೊರಗೆ ಹೋಗಿ ಊರುಬಾಗಲಲ್ಲಿ ನಿಂತುಕೊಂಡಾಗ ಅಬೀಮೆಲೆಕನೂ ಅವನ ಜನರೂ ತಾವು ಹೊಂಚಿನೋಡುತ್ತಿದ್ದ ಸ್ಥಳದಿಂದ ಎದ್ದು ಬಂದರು.",
			"36": "<sup>36</sup> ಗಾಳನು ಅವರನ್ನು ಕಂಡು ಜೆಬುಲನಿಗೆ - ಇಗೋ, ಬೆಟ್ಟದ ಮೇಲಣಿಂದ ಜನರು ಬರುತ್ತಿದ್ದಾರೆ ಎಂದು ಹೇಳಲು ಜೆಬುಲನು ಅವನಿಗೆ - ನೀನು ಬೆಟ್ಟದ ನೆರಳನ್ನು ನೋಡಿ ಜನರೆಂದು ಭಾವಿಸುತ್ತೀ ಅನ್ನಲು",
			"37": "<sup>37</sup> ಅವನು ತಿರಿಗಿ - ನೋಡು, ದೇಶನಾಭಿಗಿರಿಯಿಂದ ಜನರು ಬರುತ್ತಿದ್ದಾರೆ; ಕಣಿಹೇಳುವವರ ಮರದ ಮಾರ್ಗವಾಗಿ ಇನ್ನೊಂದು ಗುಂಪು ಬರುತ್ತಿದೆ ಅಂದನು.",
			"38": "<sup>38</sup> ಆಗ ಜೆಬುಲನು ಅವನಿಗೆ - ಈಗ ನಿನ್ನ ಬಾಯೆಲ್ಲಿದೆ? ಅಬೀಮೆಲೆಕನು ಎಷ್ಟರವನು? ನಾವು ಅವನನ್ನು ಯಾಕೆ ಸೇವಿಸಬೇಕು ಎಂದು ಕೊಚ್ಚಿಕೊಂಡಿಯಲ್ಲಾ; ಅವರು ನೀನು ತಿರಸ್ಕರಿಸಿದ ಜನರಲ್ಲವೋ? ಹಾಗಾದರೆ ಹೋಗಿ ಅವರೊಡನೆ ಯುದ್ಧಮಾಡು, ನೋಡೋಣ ಎಂದು ಹೇಳಿದನು.",
			"39": "<sup>39</sup> ಗಾಳನು ಶೆಕೆಮಿನವರ ನಾಯಕನಾಗಿ ಹೊರಟು ಅಬೀಮೆಲೆಕನೊಡನೆ ಕಾದಿದನು.",
			"40": "<sup>40</sup> ಆದರೆ ಅಬೀಮೆಲೆಕನಿಗೆ ಬೆನ್ನುತೋರಿಸಿ ಓಡಿಹೋಗಬೇಕಾಗಲು ಅವನು ಇವನನ್ನು ಹಿಂದಟ್ಟಿದನು. ಊರುಬಾಗಲಿನವರೆಗೆ ಅನೇಕ ಮಂದಿ ಹತರಾಗಿ ಬಿದ್ದರು.",
			"41": "<sup>41</sup> ಅನಂತರ ಅಬೀಮೆಲೆಕನು ಹೋಗಿ ಅರೂಮದಲ್ಲಿ ವಾಸಿಸಿದನು. ಇತ್ತ ಜೆಬುಲನು ಗಾಳನನ್ನೂ ಅವನ ಬಂಧುಗಳನ್ನೂ ಶೆಕೆಮಿನಿಂದ ಅಟ್ಟಿಬಿಟ್ಟನು.",
			"42": "<sup>42</sup> ಮರುದಿವಸ ಜನರು ಬೈಲಿಗೆ ಹೊರಟು ಬಂದರು;",
			"43": "<sup>43</sup> ಇದು ಅಬೀಮೆಲೆಕನಿಗೆ ಗೊತ್ತಿದ್ದದರಿಂದ ಅವನು ತನ್ನ ಜನರನ್ನು ಮೂರು ಗುಂಪು ಮಾಡಿ ಅವರನ್ನು ತೆಗೆದುಕೊಂಡು ಬಂದು ಹೊಲಗಳಲ್ಲಿ ಹೊಂಚಿ ನೋಡುತ್ತಿದ್ದನು. ಜನರು ಪಟ್ಟಣದಿಂದ ಹೊರಟು ಬರುವದನ್ನು ಇವನು ಕಾಣುತ್ತಲೆ ಅಲ್ಲಿಂದ ಎದ್ದು ಅವರನ್ನು ಹೊಡೆದನು.",
			"44": "<sup>44</sup> ಅಬೀಮೆಲೆಕನೂ ಅವನ ಸಂಗಡ ಇದ್ದ ಗುಂಪಿನವರೂ ಓಡಿ ಬಂದು ಊರು ಬಾಗಲಲ್ಲಿ ನಿಂತರು; ಉಳಿದ ಎರಡು ಗುಂಪಿನವರು ಬೈಲಿನಲ್ಲಿದ್ದ ಶೆಕೆಮಿನವರ ಮೇಲೆ ಬಿದ್ದು ಅವರನ್ನು ಹತಮಾಡಿದರು.",
			"45": "<sup>45</sup> ಅಬೀಮೆಲೆಕನು ಆ ದಿವಸವೆಲ್ಲಾ ಯುದ್ಧಮಾಡಿ ಪಟ್ಟಣವನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡು ಅದರ ಜನರನ್ನೆಲ್ಲಾ ಸಂಹರಿಸಿ ಊರನ್ನು ಕೆಡವಿಬಿಟ್ಟು ಅಲ್ಲಿ ಉಪ್ಪನ್ನು ಎರಚಿಬಿಟ್ಟನು.",
			"46": "<sup>46</sup> ಶೆಕೆಮಿನ ಕೋಟೆಯವರು ಇದನ್ನು ಕೇಳಿ ಏಲ್‍ಬೆರೀತೆಂಬ ದೇವರ ಗುಡಿಯ ನೆಲಮನೆಯಲ್ಲಿ ಅಡಗಿಕೊಂಡರು.",
			"47": "<sup>47</sup> ಕೋಟೆಯವರೆಲ್ಲರೂ ಅಲ್ಲಿ ಅಡಗಿಕೊಂಡಿದ್ದಾರೆಂಬದು ಅಬೀಮೆಲೆಕನಿಗೆ ಗೊತ್ತಾದಾಗ",
			"48": "<sup>48</sup> ಅವನು ಕೊಡಲಿಯನ್ನು ತೆಗೆದುಕೊಂಡು ತನ್ನ ಜನರೆಲ್ಲರೊಡನೆ ಚಲ್ಮೊನ್ ಗುಡ್ಡವನ್ನೇರಿ ತನ್ನವರಿಗೆ - ನಾನು ಮಾಡುವಂತೆಯೇ ನೀವೂ ತೀವ್ರವಾಗಿ ಮಾಡಿರಿ ಎಂದು ಹೇಳಿ ತಾನು ಒಂದು ಮರದ ಕೊಂಬೆಯನ್ನು ಕಡಿದು ಹೆಗಲ ಮೇಲೆ ಹೊತ್ತುಕೊಳ್ಳಲು",
			"49": "<sup>49</sup> ಅವರೂ ಅದರಂತೆಯೇ ಮಾಡಿ ಅವನ ಹಿಂದೆ ಹೋಗಿ ಆ ಕೊಂಬೆಗಳನ್ನು ಅವರಿದ್ದ ನೆಲಮನೆಯ ಸುತ್ತಲೂ ಹಾಕಿ ಬೆಂಕಿಹೊತ್ತಿಸಿ ಅದನ್ನು ಸುಟ್ಟುಬಿಟ್ಟರು. ಹೀಗೆ ಶೆಕೆಮ್‍ಕೋಟೆಯವರೆಲ್ಲರೂ ಸತ್ತರು. ಅವರು ಸ್ತ್ರೀಪುರುಷರೆಲ್ಲಾ ಸುಮಾರು ಸಾವಿರ ಜನರಿದ್ದರು.",
			"50": "<sup>50</sup> ಅನಂತರ ಅಬೀಮೆಲೆಕನು ತೇಬೇಚಿಗೆ ಹೋಗಿ ಮುತ್ತಿಗೆಹಾಕಿ ಅದನ್ನು ಹಿಡಿದನು.",
			"51": "<sup>51</sup> ಆ ಊರಿನ ಮಧ್ಯದಲ್ಲಿ ಒಂದು ಭದ್ರವಾದ ಬುರುಜು ಇತ್ತು; ಊರಿನ ಸ್ತ್ರೀಪುರುಷರೆಲ್ಲರೂ ಓಡಿಹೋಗಿ ಅದರೊಳಗೆ ಹೊಕ್ಕು ಬಾಗಲನ್ನು ಮುಚ್ಚಿಕೊಂಡು ಮೇಲೆ ಹತ್ತಿದರು.",
			"52": "<sup>52</sup> ಅಬೀಮೆಲೆಕನು ಅಲ್ಲಿಗೆ ಬಂದು ಯುದ್ಧಮಾಡುತ್ತಾ ಬುರುಜಿನ ಬಾಗಲಿಗೆ ಬೆಂಕಿ ಹೊತ್ತಿಸಬೇಕೆಂದು ಅದರ ಸಮೀಪಕ್ಕೆ ಹೋಗಲು",
			"53": "<sup>53</sup> ಒಬ್ಬ ಸ್ತ್ರೀಯು ಮೇಲಣಿಂದ ಒಂದು ಬೀಸುವ ಕಲ್ಲನ್ನು ಬೀಳಿಸಿ ಅವನ ತಲೆಬುರುಡೆಯನ್ನು ಒಡೆದುಬಿಟ್ಟಳು.",
			"54": "<sup>54</sup> ಅವನು ಕೂಡಲೆ ತನ್ನ ಆಯುಧವಾಹಕನನ್ನು ಕರೆದು ಅವನಿಗೆ - ಕತ್ತಿಯನ್ನು ಹಿರಿದು ನನ್ನನ್ನು ಕೊಲ್ಲು; ಇಲ್ಲವಾದರೆ ಹೆಂಗಸಿನ ಕೈಯಿಂದ ಸತ್ತನೆಂದು ಹೇಳಾರು ಎಂದನ್ನಲು ಆ ಪ್ರಾಯಸ್ಥನು ಅವನನ್ನು ತಿವಿದು ಕೊಂದು ಹಾಕಿದನು.",
			"55": "<sup>55</sup> ಅಬೀಮೆಲೆಕನು ಸತ್ತು ಹೋದದ್ದನ್ನು ಕಂಡು ಇಸ್ರಾಯೇಲ್ಯರು ತಮ್ಮ ತಮ್ಮ ಸ್ಥಳಗಳಿಗೆ ಹಿಂದಿರುಗಿದರು.",
			"56": "<sup>56</sup> ಎಪ್ಪತ್ತು ಮಂದಿ ಸಹೋದರರನ್ನು ಕೊಂದುಹಾಕಿ ತನ್ನ ತಂದೆಗೆ ದ್ರೋಹಿಯಾದ ಅಬೀಮೆಲೆಕನಿಗೆ ದೇವರು ಈ ರೀತಿಯಾಗಿ ಮುಯ್ಯಿತೀರಿಸಿದನು.",
			"57": "<sup>57</sup> ಶೆಕೆಮಿನವರಿಗಾದರೋ ಅವರ ದುಷ್ಟತ್ವವು ಅವರ ತಲೆಯ ಮೇಲೆಯೇ ಬರಮಾಡಿದನು. ಹೀಗೆ ಯೆರುಬ್ಬಾಳನ ಮಗನಾದ ಯೋತಾಮನು ಇವರ ವಿಷಯದಲ್ಲಿ ನುಡಿದ ಶಾಪವು ನೆರವೇರಿತು."
		},
		"10": {
			"1": "<sup>1</sup> ಅಬೀಮೆಲೆಕನ ತರುವಾಯ ಇಸ್ಸಾಕಾರ್ ಕುಲದ ಪೂವನ ಮಗನೂ ದೋದೋವಿನ ಮೊಮ್ಮಗನೂ ಆಗಿದ್ದ ತೋಲನು ಎದ್ದು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ರಕ್ಷಿಸಿದನು. ಅವನು ಎಫ್ರಾಯೀಮ್ ಬೆಟ್ಟದ ಸೀಮೆಯ ಶಾಮೀರೆಂಬ ಊರಲ್ಲಿ ವಾಸವಾಗಿದ್ದನು.",
			"2": "<sup>2</sup> ಇಪ್ಪತ್ತಮೂರು ವರುಷಗಳವರೆಗೆ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಪಾಲಿಸಿದನಂತರ ಸತ್ತು ಶಾಮೀರಿನಲ್ಲಿ ಹೂಣಲ್ಪಟ್ಟನು.",
			"3": "<sup>3</sup> ಅವನ ತರುವಾಯ ಗಿಲ್ಯಾದ್ಯನಾದ ಯಾಯೀರನು ಎದ್ದು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಇಪ್ಪತ್ತೆರಡು ವರುಷ ಪಾಲಿಸಿದನು.",
			"4": "<sup>4</sup> ಇವನಿಗೆ ಮೂವತ್ತು ಮಂದಿ ಮಕ್ಕಳಿದ್ದರು. ಇವರಿಗೆ ಮೂವತ್ತು ಸವಾರೀ ಕತ್ತೆಗಳೂ ಗಿಲ್ಯಾದ್ ದೇಶದಲ್ಲಿ ಮೂವತ್ತು ಊರುಗಳೂ ಇದ್ದವು. ಅವುಗಳಿಗೆ ಇಂದಿನವರೆಗೂ ಯಾಯೀರಿನ ಗ್ರಾಮಗಳೆಂದು ಹೆಸರಿದೆ.",
			"5": "<sup>5</sup> ಯಾಯೀರನು ಸತ್ತು ಕಾಮೋನಿನಲ್ಲಿ ಹೂಣಲ್ಪಟ್ಟನು.",
			"6": "<sup>6</sup> ಇಸ್ರಾಯೇಲ್ಯರು ತಿರಿಗಿ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಗಳಾದರು; ಅವರು ಆತನನ್ನು ಬಿಟ್ಟು ಬಾಳ್, ಅಷ್ಟೋರೆತ್ ಎಂಬ ದೇವತೆಗಳನ್ನೂ ಅರಾಮ್ಯರು, ಚೀದೋನ್ಯರು, ಮೋವಾಬ್ಯರು, ಅಮ್ಮೋನಿಯರು, ಫಿಲಿಷ್ಟಿಯರು ಇವರ ದೇವತೆಗಳನ್ನೂ ಪೂಜಿಸಹತ್ತಿ ಯೆಹೋವನನ್ನು ಬಿಟ್ಟೇ ಬಿಟ್ಟರು.",
			"7": "<sup>7</sup> ಆದದರಿಂದ ಆತನು ಇಸ್ರಾಯೇಲ್ಯರ ಮೇಲೆ ಕೋಪವುಳ್ಳವನಾಗಿ ಅವರನ್ನು ಫಿಲಿಷ್ಟಿಯರ ಮತ್ತು ಅಮ್ಮೋನಿಯರ ಕೈಗೆ ಒಪ್ಪಿಸಿಬಿಟ್ಟನು.",
			"8": "<sup>8</sup> ಇವರು ಆ ವರುಷದಿಂದ ಹದಿನೆಂಟು ವರುಷಗಳವರೆಗೆ ಯೊರ್ದನಿನ ಆಚೆ ಗಿಲ್ಯಾದಿನಲ್ಲಿದ್ದ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಬಹಳವಾಗಿ ಪೀಡಿಸುತ್ತಾ ಕುಗ್ಗಿಸುತ್ತಾ ಇದ್ದರು. ಈ ದೇಶವು ಮೊದಲು ಅಮೋರಿಯರದಾಗಿತ್ತು.",
			"9": "<sup>9</sup> ಇದಲ್ಲದೆ ಅಮ್ಮೋನಿಯರು ಯೊರ್ದನ್ ಹೊಳೆಯನ್ನು ದಾಟಿ ಯೆಹೂದ ಬೆನ್ಯಾಮೀನ್ ಎಫ್ರಾಯೀಮ್ ಕುಲಗಳೊಡನೆ ಯುದ್ಧಮಾಡಿದ್ದರಿಂದ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಬಹಳ ಕಷ್ಟವಾಯಿತು.",
			"10": "<sup>10</sup> ಆಗ ಅವರು ಯೆಹೋವನಿಗೆ - ನಾವು ನಮ್ಮ ದೇವರಾದ ನಿನ್ನನ್ನು ಬಿಟ್ಟು ಬಾಳನ ಪ್ರತಿಮೆಗಳನ್ನು ಪೂಜಿಸಿ ನಿನಗೆ ವಿರುದ್ಧವಾಗಿ ಪಾಪ ಮಾಡಿದ್ದೇವೆ ಎಂದು ಮೊರೆಯಿಡಲು ಆತನು ಅವರಿಗೆ -",
			"11": "<sup>11</sup> ಐಗುಪ್ತ, ಅಮೋರಿಯ, ಅಮ್ಮೋನಿಯ, ಫಿಲಿಷ್ಟಿಯ, ಚೀದೋನ್ಯ, ಅಮಾಲೇಕ್ಯ, ಮಾವೋನ್ಯ",
			"12": "<sup>12</sup> ಇವೇ ಮೊದಲಾದ ಜನಾಂಗಗಳು ನಿಮ್ಮನ್ನು ಪೀಡಿಸಿದಾಗ ನೀವು ನನಗೆ ಮೊರೆಯಿಟ್ಟಿರಿ; ನಾನು ಅವುಗಳಿಂದ ನಿಮ್ಮನ್ನು ಬಿಡಿಸಿದರೂ",
			"13": "<sup>13</sup> ನೀವು ತಿರಿಗಿ ನನ್ನನ್ನು ಬಿಟ್ಟು ಅನ್ಯದೇವತೆಗಳನ್ನು ಸೇವಿಸುತ್ತಾ ಬಂದಿರಿ; ಆದದರಿಂದ ನಾನು ಇನ್ನು ಮುಂದೆ ನಿಮ್ಮನ್ನು ರಕ್ಷಿಸುವದೇ ಇಲ್ಲ.",
			"14": "<sup>14</sup> ಹೋಗಿ ನೀವು ಆರಿಸಿಕೊಂಡ ದೇವತೆಗಳಿಗೆ ಮೊರೆಯಿಡಿರಿ; ಅವು ನಿಮ್ಮ ಈ ಇಕ್ಕಟ್ಟಿನಲ್ಲಿ ಸಹಾಯ ಮಾಡಲಿ ಅಂದನು.",
			"15": "<sup>15</sup> ಇಸ್ರಾಯೇಲ್ಯರು ತಿರಿಗಿ ಯೆಹೋವನಿಗೆ - ನಾವು ಪಾಪ ಮಾಡಿದ್ದೇವೆ; ನಿನಗೆ ಸರಿ ಕಾಣುವ ಪ್ರಕಾರ ನಮಗೆ ಮಾಡು; ಆದರೆ ಈ ಸಾರಿ ಹೇಗೂ ನಮ್ಮನ್ನು ರಕ್ಷಿಸಬೇಕು ಎಂದು ಮೊರೆಯಿಟ್ಟು ಅನ್ಯದೇವತೆಗಳನ್ನು ತಮ್ಮಲ್ಲಿಂದ ತೆಗೆದುಹಾಕಿ ಯೆಹೋವನನ್ನು ಸೇವಿಸುವವರಾದರು.",
			"16": "<sup>16</sup> ಆಗ ಆತನ ಮನಸ್ಸು ಇಸ್ರಾಯೇಲ್ಯರ ಸಂಕಟದ ನಿಮಿತ್ತ ಬಲು ನೊಂದಿತು.",
			"17": "<sup>17</sup> ಅಮ್ಮೋನಿಯರು ದಂಡೆತ್ತಿ ಬಂದು ಗಿಲ್ಯಾದಿನಲ್ಲಿ ಇಳುಕೊಳ್ಳಲು ಇಸ್ರಾಯೇಲ್ಯರು ಕೂಡಿ ಮಿಚ್ಪೆಯಲ್ಲಿ ಪಾಳೆಯಮಾಡಿಕೊಂಡರು.",
			"18": "<sup>18</sup> ಗಿಲ್ಯಾದಿನ ಜನರೂ ಅಧಿಪತಿಗಳೂ - ನಮ್ಮಲ್ಲಿ ಅಮ್ಮೋನಿಯರೊಡನೆ ಯುದ್ಧಕ್ಕೆ ಕೈಹಾಕುವವನಾವನು? ಅಂಥವನನ್ನು ಗಿಲ್ಯಾದಿನವರೆಲ್ಲರ ಮೇಲೆ ನಾಯಕನನ್ನಾಗಿ ಮಾಡೇವಲ್ಲಾ ಎಂದು ತಮ್ಮ ತಮ್ಮೊಳಗೆ ಮಾತಾಡಿಕೊಂಡರು."
		},
		"11": {
			"1": "<sup>1</sup> ಗಿಲ್ಯಾದ್ಯನಾದ ಯೆಪ್ತಾಹನು ಮಹಾಪರಾಕ್ರಮಿಯಾಗಿದ್ದನು. ಅವನು ಗಿಲ್ಯಾದನಿಗೆ ಒಬ್ಬ ಸೂಳೆಯಲ್ಲಿ ಹುಟ್ಟಿದವನು.",
			"2": "<sup>2</sup> ಗಿಲ್ಯಾದನಿಗೆ ಹೆಂಡತಿಯಲ್ಲಿಯೂ ಮಕ್ಕಳು ಹುಟ್ಟಿದರು. ಇವರು ದೊಡ್ಡವರಾದ ಮೇಲೆ ಯೆಪ್ತಾಹನಿಗೆ - ನೀನು ಪರಸ್ತ್ರೀಯಿಂದ ಹುಟ್ಟಿದವನು; ಆದದರಿಂದ ನಿನಗೆ ನಮ್ಮ ತಂದೆಯ ಮನೆಯಲ್ಲಿ ಬಾಧ್ಯತೆ ಸಿಕ್ಕಲಾರದು ಎಂದು ಹೇಳಿ ಅವನನ್ನು ಹೊರಗೆ ಹಾಕಿದರು.",
			"3": "<sup>3</sup> ಅವನು ತನ್ನ ಸಹೋದರರನ್ನು ಬಿಟ್ಟು ಟೋಬ್ ದೇಶಕ್ಕೆ ಹೋಗಲು ಅಲ್ಲಿನ ಕಾಕಪೋಕರು ಕೂಡಿ ಅವನನ್ನು ಹಿಂಬಾಲಿಸಿದರು.",
			"4": "<sup>4</sup> ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ವಿರೋಧವಾಗಿ ಬಂದ ಅಮ್ಮೋನಿಯರು ಸ್ವಲ್ಪದಿನಗಳಾದ ಮೇಲೆ ಯುದ್ಧವನ್ನು ಪ್ರಾರಂಭಿಸಿದರು.",
			"5": "<sup>5</sup> ಆಗ ಗಿಲ್ಯಾದಿನ ಹಿರಿಯರು ಯೆಪ್ತಾಹನನ್ನು ಟೋಬ್‍ದೇಶದಿಂದ ಕರೆದುಕೊಂಡು ಬರುವದಕ್ಕಾಗಿ ಅಲ್ಲಿಗೆ ಹೋಗಿ ಅವನಿಗೆ -",
			"6": "<sup>6</sup> ನಾವು ಅಮ್ಮೋನಿಯರೊಡನೆ ಯುದ್ಧಮಾಡುವ ಹಾಗೆ ನೀನು ಬಂದು ನಮ್ಮ ನಾಯಕನಾಗು ಎಂದು ಬೇಡಿಕೊಂಡರು.",
			"7": "<sup>7</sup> ಅವನು ಅವರಿಗೆ - ನನ್ನನ್ನು ಹಗೆಮಾಡಿ ನನ್ನ ತಂದೆಯ ಮನೆಯಿಂದ ಅಟ್ಟಿಬಿಟ್ಟವರು ನೀವೇ ಅಲ್ಲವೋ? ನಿಮಗೆ ಕಷ್ಟ ಬಂದಾಗ ನನ್ನ ಬಳಿಗೆ ಯಾಕೆ ಬಂದಿರಿ ಅನ್ನಲು",
			"8": "<sup>8</sup> ಅವರು - ಆದದರಿಂದಲೇ ಈಗ ತಿರಿಗಿ ನಿನ್ನ ಬಳಿಗೆ ಬಂದಿದ್ದೇವೆ; ನಮ್ಮ ಸಂಗಡ ಬಂದು ಅಮ್ಮೋನಿಯರೊಡನೆ ಯುದ್ಧಮಾಡುವಿಯಾದರೆ ಗಿಲ್ಯಾದಿನವರಿಗೆಲ್ಲಾ ನೀನೇ ಶಿರಸ್ಸಾಗಿರುವಿ ಎಂದು ಹೇಳಿದರು.",
			"9": "<sup>9</sup> ಆಗ ಯೆಪ್ತಾಹನು ಅವರಿಗೆ - ನೀವು ನನ್ನನ್ನು ಅಮ್ಮೋನಿಯರೊಡನೆ ಯುದ್ಧಮಾಡುವದಕ್ಕೋಸ್ಕರ ಕರೆದುಕೊಂಡು ಹೋಗುವಲ್ಲಿ ಯೆಹೋವನು ಅವರನ್ನು ನನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸಿಕೊಟ್ಟರೆ ನಿಜವಾಗಿ ನನ್ನನ್ನು ಅಧಿಪತಿಯನ್ನಾಗಿ ಮಾಡುವಿರೋ ಎಂದು ಕೇಳಲು",
			"10": "<sup>10</sup> ಅವರು ಅವನಿಗೆ - ಹಾಗೆಯೇ ಮಾಡುವೆವು; ಉಭಯತರ ಮಾತುಗಳಿಗೆ ಯೆಹೋವನೇ ಸಾಕ್ಷಿ ಎಂದು ಉತ್ತರಕೊಟ್ಟರು.",
			"11": "<sup>11</sup> ಆಗ ಯೆಪ್ತಾಹನು ಗಿಲ್ಯಾದಿನ ಹಿರಿಯರ ಸಂಗಡ ಹೋದನು; ಜನರು ಅವನನ್ನು ಅಧಿಪತಿಯನ್ನಾಗಿಯೂ ನಾಯಕನನ್ನಾಗಿಯೂ ನೇಮಿಸಿದರು. ಮತ್ತು ಯೆಪ್ತಾಹನು ತನ್ನ ಸಂಗತಿಗಳನ್ನೆಲ್ಲಾ ಮಿಚ್ಪೆಯಲ್ಲಿ ಯೆಹೋವನ ಮುಂದೆ ಅರಿಕೆಮಾಡಿದನು.",
			"12": "<sup>12</sup> ಯೆಪ್ತಾಹನು ಅಮ್ಮೋನಿಯರ ಅರಸನ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿ ಅವನನ್ನು - ನನಗೂ ನಿನಗೂ ಏನಿದೆ? ನೀನು ನನ್ನ ದೇಶಕ್ಕೆ ವಿರೋಧವಾಗಿ ಯುದ್ಧಮಾಡುವದಕ್ಕೇನು ಕಾರಣ ಎಂದು ಕೇಳಿದನು.",
			"13": "<sup>13</sup> ಆ ಅರಸನು ದೂತರಿಗೆ - ಇಸ್ರಾಯೇಲ್ಯರು ಐಗುಪ್ತದಿಂದ ಬಂದಾಗ ಅರ್ನೋನಿನಿಂದ ಯಬ್ಬೋಕ್, ಯೊರ್ದನ್ ಹೊಳೆಗಳವರೆಗೂ ಇದ್ದ ನನ್ನ ದೇಶವನ್ನು ಕಸಕೊಂಡರಲ್ಲಾ; ನೀನು ಈಗ ಅದನ್ನು ಸಮಾಧಾನದಿಂದ ಹಿಂದಕ್ಕೆ ಕೊಡು ಎಂದು ಹೇಳಿ ಕಳುಹಿಸಿದನು.",
			"14": "<sup>14</sup> ಯೆಪ್ತಾಹನು ಇನ್ನೊಂದು ಸಾರಿ ಅಮ್ಮೋನಿಯರ ಅರಸನ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿ",
			"15": "<sup>15</sup> ಅವನಿಗೆ - ಯೆಪ್ತಾಹನಾದ ನನ್ನ ಮಾತನ್ನು ಕೇಳು; ಇಸ್ರಾಯೇಲ್ಯರು ಮೋವಾಬ್ಯರ ಮತ್ತು ಅಮ್ಮೋನಿಯರ ದೇಶವನ್ನು ತೆಗೆದುಕೊಳ್ಳಲೇ ಇಲ್ಲ.",
			"16": "<sup>16</sup> ಅವರು ಐಗುಪ್ತವನ್ನು ಬಿಟ್ಟನಂತರ ಅರಣ್ಯದಲ್ಲಿ ಸಂಚರಿಸಿ ಕೆಂಪುಸಮುದ್ರಕ್ಕೂ ಅನಂತರ ಕಾದೇಶಿಗೂ ಬಂದರು.",
			"17": "<sup>17</sup> ಅವರು ಅಲ್ಲಿಂದ ಎದೋಮ್ಯರ ಅರಸನ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿ - ನಿನ್ನ ದೇಶವನ್ನು ದಾಟಿಹೋಗುವದಕ್ಕೆ ಅಪ್ಪಣೆಯಾಗಬೇಕೆಂದು ಬೇಡಿಕೊಳ್ಳಲು ಅವನು ಒಪ್ಪಿಕೊಳ್ಳಲಿಲ್ಲ. ತರುವಾಯ ಮೋವಾಬ್ಯರ ಅರಸನ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಲು ಅವನೂ ಒಪ್ಪಿಕೊಳ್ಳಲಿಲ್ಲ; ಆದದರಿಂದ ಅವರು ಕಾದೇಶಿನಲ್ಲಿಯೇ ನಿಂತರು.",
			"18": "<sup>18</sup> ಅನಂತರ ಅವರು ಅರಣ್ಯದಲ್ಲಿ ಪ್ರಯಾಣಮಾಡಿ ಎದೋಮ್ ಮೋವಾಬ್ ದೇಶಗಳನ್ನು ಸುತ್ತಿಕೊಂಡು ಮೋವಾಬ್ ದೇಶದ ಪೂರ್ವದಿಕ್ಕಿಗೆ ಬಂದು ಅದರ ಮೇರೆಯಾಗಿರುವ ಅರ್ನೋನ್ ಹೊಳೆಯ ಆಚೆಯಲ್ಲಿ ಇಳುಕೊಂಡರು; ಮೋವಾಬ್ಯರ ಮೇರೆಯೊಳಗೆ ಕಾಲಿಡಲಿಲ್ಲ.",
			"19": "<sup>19</sup> ತರುವಾಯ ಅವರು ಹೆಷ್ಬೋನನ್ನು ರಾಜಧಾನಿಮಾಡಿಕೊಂಡಿದ್ದ ಅಮೋರಿಯರ ಅರಸನಾದ ಸೀಹೋನನ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿ - ನಿನ್ನ ದೇಶವನ್ನು ದಾಟಿ ನಮ್ಮ ಸ್ಥಳಕ್ಕೆ ಹೋಗುವದಕ್ಕೆ ಅಪ್ಪಣೆಕೊಡು ಎಂದು ಅವನನ್ನು ಬೇಡಿಕೊಂಡರು.",
			"20": "<sup>20</sup> ಆದರೆ ಸೀಹೋನನು ಅವರನ್ನು ನಂಬದವನಾಗಿ ತನ್ನ ಸೀಮೆಯನ್ನು ದಾಟಿಹೋಗುವದಕ್ಕೆ ಅಪ್ಪಣೆಕೊಡದೆ ತನ್ನ ಜನರನ್ನು ಕೂಡಿಸಿಕೊಂಡು ಯಹಚಕ್ಕೆ ಬಂದು ಅವರೊಡನೆ ಯುದ್ಧಮಾಡಿದನು.",
			"21": "<sup>21</sup> ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರಾದ ಯೆಹೋವನು ಸೀಹೋನನನ್ನೂ ಅವನ ಜನರೆಲ್ಲರನ್ನೂ ಇಸ್ರಾಯೇಲ್ಯರ ಕೈಗೆ ಒಪ್ಪಿಸಿದ್ದರಿಂದ ಅವರು ಈ ದೇಶದ ನಿವಾಸಿಗಳಾದ ಅಮೋರಿಯರನ್ನು ಹೊಡೆದು ಅವರ ದೇಶವನ್ನೆಲ್ಲಾ ಸ್ವತಂತ್ರಿಸಿಕೊಂಡರು.",
			"22": "<sup>22</sup> ಹೀಗೆ ಇಸ್ರಾಯೇಲ್ಯರು ಅರ್ನೋನಿನಿಂದ ಯಬ್ಬೋಕಿನವರೆಗೂ ಅರಣ್ಯದಿಂದ ಯೊರ್ದನಿನವರೆಗೂ ಇದ್ದ ಅಮೋರಿಯರ ಪ್ರಾಂತವನ್ನೆಲ್ಲಾ ವಶಮಾಡಿಕೊಂಡರು.",
			"23": "<sup>23</sup> ಇಸ್ರಾಯೇಲ್‍ದೇವರಾದ ಯೆಹೋವನು ಈ ದೇಶವನ್ನು ಅಮೋರಿಯರಿಂದ ತೆಗೆದು ತನ್ನ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಕೊಟ್ಟ ಮೇಲೆ ನೀನು ಅದನ್ನು ತೆಗೆದುಕೊಳ್ಳುವದು ಹೇಗೆ?",
			"24": "<sup>24</sup> ನಿನ್ನ ದೇವನಾದ ಕೆಮೋಷನು ನಿನಗೆ ಕೊಡುವ ದೇಶಗಳನ್ನು ನೀನು ಸ್ವತಂತ್ರಿಸಿಕೊಳ್ಳುವದಿಲ್ಲವೋ? ಹಾಗೆಯೇ ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಮಗೆ ಒಪ್ಪಿಸಿಕೊಡುವದನ್ನು ನಾವು ಸ್ವತಂತ್ರಿಸಿಕೊಳ್ಳುವದು ನ್ಯಾಯವಾಗಿದೆ.",
			"25": "<sup>25</sup> ಚಿಪ್ಪೋರನ ಮಗನೂ ಮೋವಾಬ್ಯರ ಅರಸನೂ ಆದ ಬಾಲಾಕನಿಗಿಂತ ನೀನು ಹೆಚ್ಚಿನವನೋ? ಅವನು ಇಸ್ರಾಯೇಲ್ಯರೊಡನೆ ಎಂದಾದರೂ ವಿವಾದಮಾಡಿದನೋ? ಅಥವಾ ಅವರಿಗೆ ವಿರೋಧವಾಗಿ ಯುದ್ಧಕ್ಕೆ ಬಂದನೋ?",
			"26": "<sup>26</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಮುನ್ನೂರು ವರುಷಗಳಿಂದ ಹೆಷ್ಬೋನ್, ಅರೋಯೇರ್ ಎಂಬ ಪಟ್ಟಣಗಳಲ್ಲಿಯೂ ಅವುಗಳ ಗ್ರಾಮಗಳಲ್ಲಿಯೂ ಅರ್ನೋನ್ ತೀರದ ಎಲ್ಲಾ ಪಟ್ಟಣಗಳಲ್ಲಿಯೂ ವಾಸಿಸುತ್ತಿದ್ದಾರಲ್ಲಾ; ಇಷ್ಟು ದಿವಸಗಳವರೆಗೆ ನೀನು ಅದನ್ನು ಬಿಡಿಸಿಕೊಳ್ಳದೆ ಇದ್ದದ್ದೇಕೆ?",
			"27": "<sup>27</sup> ನಾನು ನಿನಗೆ ಯಾವ ಅನ್ಯಾಯವನ್ನೂ ಮಾಡಲಿಲ್ಲ. ನೀನು ಈಗ ನನಗೆ ವಿರೋಧವಾಗಿ ಯುದ್ಧಕ್ಕೆ ಬಂದದರಿಂದ ನೀನೇ ಅನ್ಯಾಯಮಾಡಿದ ಹಾಗಾಯಿತು. ನ್ಯಾಯಸ್ಥಾಪಕನಾದ ಯೆಹೋವನೇ ಈ ಹೊತ್ತು ಇಸ್ರಾಯೇಲ್ಯರಿಗೂ ಅಮ್ಮೋನಿಯರಿಗೂ ಮಧ್ಯದಲ್ಲಿ ನ್ಯಾಯತೀರಿಸಲಿ ಎಂದು ಹೇಳಿಸಿದನು.",
			"28": "<sup>28</sup> ಆದರೆ ಯೆಪ್ತಾಹನು ಹೇಳಿಕಳುಹಿಸಿದ ಮಾತುಗಳಿಗೆ ಅಮ್ಮೋನಿಯರ ಅರಸನು ಕಿವಿಗೊಡಲಿಲ್ಲ.",
			"29": "<sup>29</sup> ಆಗ ಯೆಹೋವನ ಆತ್ಮವು ಯೆಪ್ತಾಹನ ಮೇಲೆ ಬಂದಿತು. ಅವನು ಗಿಲ್ಯಾದ್ ಪ್ರಾಂತ, ಮನಸ್ಸೆಯ ದೇಶ ಇವುಗಳಲ್ಲಿ ಸಂಚರಿಸಿ ತಿರಿಗಿ ಗಿಲ್ಯಾದಿನ ಮಿಚ್ಪೆಗೆ ಬಂದು ಅಲ್ಲಿಂದ ಅಮ್ಮೋನಿಯರ ಮೇಲೆ ಯುದ್ಧಕ್ಕೆ ಹೋದನು.",
			"30": "<sup>30</sup> ಇದಲ್ಲದೆ ಅವನು ಯೆಹೋವನಿಗೆ - ನೀನು ಅಮ್ಮೋನಿಯರನ್ನು ನನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸುವದಾದರೆ",
			"31": "<sup>31</sup> ನಾನು ಸುರಕ್ಷಿತನಾಗಿ ಮನೆಗೆ ಮುಟ್ಟಿದಾಗ ನನ್ನನ್ನು ಎದುರುಗೊಳ್ಳುವದಕ್ಕಾಗಿ ನನ್ನ ಮನೆಯ ಬಾಗಲಿನಿಂದ ಮೊದಲು ಬರುವಂಥ ಪ್ರಾಣಿಯು ನಿನ್ನದೇ ಎಂದು ಅದನ್ನು ನಿನಗೋಸ್ಕರ ಹೋಮಮಾಡುವೆನು ಎಂದು ಹರಕೆಮಾಡಿದನು.",
			"32": "<sup>32</sup> ಯೆಪ್ತಾಹನು ಅಮ್ಮೋನಿಯರಿಗೆ ವಿರೋಧವಾಗಿ ಯುದ್ಧಕ್ಕೆ ಹೋಗಲು ಯೆಹೋವನು ಅವರನ್ನು ಅವನ ಕೈಗೆ ಒಪ್ಪಿಸಿದನು.",
			"33": "<sup>33</sup> ಅವನು ಅವರನ್ನು ಸೋಲಿಸಿ ಅರೋಯೇರಿನಿಂದ ಮಿನ್ನೀತಿನ ದಾರಿಯವರೆಗೂ ಆಬೇಲ್ ಕೆರಾಮೀಮಿನವರೆಗೂ ಇಪ್ಪತ್ತು ಪಟ್ಟಣಗಳನ್ನು ಹಾಳುಮಾಡಿಬಿಟ್ಟನು. ಹೀಗೆ ದೊಡ್ಡ ಜಯವಾಗಿ ಅಮ್ಮೋನಿಯರು ಇಸ್ರಾಯೇಲ್ಯರ ಮುಂದೆ ತಗ್ಗಿಸಲ್ಪಟ್ಟರು.",
			"34": "<sup>34</sup> ಯೆಪ್ತಾಹನು ಮಿಚ್ಪೆಯಲ್ಲಿದ್ದ ತನ್ನ ಮನೆಯನ್ನು ಸಮೀಪಿಸಿದಾಗ ಅವನ ಮಗಳು ದಮ್ಮಡಿಬಡಿಯುತ್ತಾ ನಾಟ್ಯವಾಡುತ್ತಾ ಅವನನ್ನು ಎದುರುಗೊಳ್ಳುವದಕ್ಕಾಗಿ ಬಂದಳು. ಆಕೆಯು ಅವನ ಒಬ್ಬಳೇ ಮಗಳು. ಆಕೆಯ ಹೊರತು ಅವನಿಗೆ ಬೇರೆ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳೇ ಇರಲಿಲ್ಲ.",
			"35": "<sup>35</sup> ಆಕೆಯನ್ನು ಕಾಣುತ್ತಲೆ ಅವನು ತನ್ನ ಬಟ್ಟೆಯನ್ನು ಹರಿದುಕೊಂಡು - ಅಯ್ಯೋ ನನ್ನ ಮಗಳೇ, ಮಗಳೇ, ನೀನು ನನ್ನನ್ನು ಕುಗ್ಗಿಸೇ ಬಿಟ್ಟಿಯಲ್ಲಾ; ನನಗೆ ಮಹಾಸಂಕಟವನ್ನುಂಟುಮಾಡಿದಿ. ನಾನು ಬಾಯ್ದೆರೆದು ಯೆಹೋವನಿಗೆ ಹರಕೆಮಾಡಿದ್ದೇನೆ; ಅದಕ್ಕೆ ಹಿಂದೆಗೆಯಲಾರೆನು ಎಂದು ಕೂಗಿಕೊಳ್ಳಲು",
			"36": "<sup>36</sup> ಆಕೆಯು ಅವನಿಗೆ - ನನ್ನ ತಂದೆಯೇ, ನೀನು ಬಾಯ್ದೆರೆದು ಯೆಹೋವನಿಗೆ ಹರಕೆ ಮಾಡಿದ ಮೇಲೆ ಆತನು ನಿನ್ನ ಶತ್ರುಗಳಾದ ಅಮ್ಮೋನಿಯರಿಗೆ ಮುಯ್ಯಿತೀರಿಸಿದದರಿಂದ ನಿನ್ನ ಬಾಯಿಂದ ಹೊರಟದ್ದನ್ನೇ ನೆರವೇರಿಸು ಅಂದಳು.",
			"37": "<sup>37</sup> ಆಕೆಯು ತಿರಿಗಿ ತನ್ನ ತಂದೆಗೆ - ನನ್ನ ಬಿನ್ನಹವನ್ನು ಲಾಲಿಸು; ಎರಡು ತಿಂಗಳುಗಳವರೆಗೆ ನನ್ನನ್ನು ಬಿಡು. ನಾನು ನನ್ನ ಗೆಳತಿಯರೊಡನೆ ಬೆಟ್ಟದ ಪ್ರದೇಶಗಳಿಗೆ ಹೋಗಿ ನನ್ನ ಕನ್ಯಾವಸ್ಥೆಗೋಸ್ಕರ ಗೋಳಾಡುವೆನು ಅನ್ನಲು ಅವನು -",
			"38": "<sup>38</sup> ಎರಡು ತಿಂಗಳ ತನಕ ಹೋಗಿ ಬಾ ಎಂದು ಕಳುಹಿಸಿದನು. ಆಕೆಯು ಸಖಿಯರ ಸಹಿತವಾಗಿ ಬೆಟ್ಟದ ಪ್ರದೇಶಗಳಿಗೆ ಹೋಗಿ ತನ್ನ ಕನ್ಯಾವಸ್ಥೆಗೋಸ್ಕರ ಗೋಳಾಡಿದಳು.",
			"39": "<sup>39</sup> ಎರಡು ತಿಂಗಳು ಕಳೆದನಂತರ ಆಕೆಯು ತಿರಿಗಿ ತಂದೆಯ ಹತ್ತಿರ ಬರಲು ಅವನು ತನ್ನ ಹರಕೆಯನ್ನು ತೀರಿಸಿದನು. ಆಕೆಯು ಪುರುಷನನ್ನರಿಯದವಳು.",
			"40": "<sup>40</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಹೆಣ್ಣುಮಕ್ಕಳು ಪ್ರತಿವರುಷದಲ್ಲಿಯೂ ನಾಲ್ಕು ದಿವಸ ಗಿಲ್ಯಾದ್ಯನಾದ ಯೆಪ್ತಾಹನ ಮಗಳನ್ನು ವರ್ಣಿಸುತ್ತಾರೆ. ಇದು ಅವರಲ್ಲಿ ಒಂದು ಪದ್ಧತಿ."
		},
		"12": {
			"1": "<sup>1</sup> ಎಫ್ರಾಯೀಮ್ಯರು ಕೂಡಿಕೊಂಡು ಯೊರ್ದನನ್ನು ದಾಟಿ ಉತ್ತರದಿಕ್ಕಿಗೆ ಯೆಪ್ತಾಹನಿದ್ದಲ್ಲಿಗೆ ಹೋಗಿ ಅವನಿಗೆ - ನೀನು ಅಮ್ಮೋನಿಯರ ಸಂಗಡ ಯುದ್ಧಕ್ಕೆ ಹೋಗುವಾಗ ನಮ್ಮನ್ನು ಯಾಕೆ ಕರೆಯಲಿಲ್ಲ? ಈಗ ನಾವು ನಿನ್ನನ್ನೂ ನಿನ್ನ ಮನೆಯನ್ನೂ ಸುಟ್ಟುಬಿಡುತ್ತೇವೆ ಅಂದರು.",
			"2": "<sup>2</sup> ಆಗ ಅವನು ಅವರಿಗೆ - ನನಗೂ ನನ್ನ ಜನರಿಗೂ ಅಮ್ಮೋನಿಯರೊಡನೆ ವ್ಯಾಜ್ಯವಿದ್ದಾಗ ನಾನು ನಿಮ್ಮನ್ನು ಕರೆದೆನು; ಆದರೆ ನೀವು ಬಂದು ನನ್ನನ್ನು ಅವರ ಕೈಯಿಂದ ಬಿಡಿಸಲಿಲ್ಲ.",
			"3": "<sup>3</sup> ನೀವು ಸಹಾಯಮಾಡುವದಿಲ್ಲವೆಂದು ತಿಳಿದು ಜೀವವನ್ನು ಕೈಯಲ್ಲಿ ಹಿಡಿದು ಅಮ್ಮೋನಿಯರೊಡನೆ ಯುದ್ಧಕ್ಕೆ ಹೋದೆನು; ಯೆಹೋವನು ಅವರನ್ನು ನನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸಿದನು. ಹೀಗಿರಲು ನೀವು ಈಗ ಬಂದು ನನ್ನೊಡನೆ ಕಲಹಮಾಡುವದೇಕೆ ಎಂದು ಉತ್ತರಕೊಟ್ಟು",
			"4": "<sup>4</sup> ಗಿಲ್ಯಾದಿನವರೆಲ್ಲರನ್ನೂ ಕೂಡಿಸಿ ಎಫ್ರಾಯೀಮ್ಯರಿಗೆ ವಿರೋಧವಾಗಿ ಯುದ್ಧಕ್ಕೆ ನಿಂತನು. ಗಿಲ್ಯಾದ್ಯರಾದ ನೀವು ನಮ್ಮ ಮತ್ತು ಮನಸ್ಸೆಯವರ ಮಧ್ಯದಲ್ಲಿದ್ದು ಸ್ವಕುಲವನ್ನು ಬಿಟ್ಟು ಇಲ್ಲಿಗೆ ಓಡಿ ಬಂದಿರಿ ಎಂದು ಎಫ್ರಾಯೀಮ್ಯರು ಅಂದದರಿಂದ ಗಿಲ್ಯಾದ್ಯರು [ಕೋಪಗೊಂಡು] ಅವರನ್ನು ಪೂರ್ಣವಾಗಿ ಸೋಲಿಸಿಬಿಟ್ಟರು.",
			"5": "<sup>5</sup> ಇದಲ್ಲದೆ ಅವರು ಎಫ್ರಾಯೀಮಿಗೆ ಹೋಗುವ ಯೊರ್ದನಿನ ಹಾಯಗಡಗಳನ್ನೆಲ್ಲಾ ಹಿಡಿದರು. ತಪ್ಪಿಸಿಕೊಂಡ ಎಫ್ರಾಯೀಮ್ಯರಲ್ಲೊಬ್ಬನು ಅಲ್ಲಿಗೆ ಬಂದು ನನ್ನನ್ನು ದಾಟಗೊಡಿರಿ ಎಂದು ಅವರನ್ನು ಕೇಳಿದರೆ ಅವರು - ನೀನು ಎಫ್ರಾಯೀಮ್ಯನೋ ಎಂದು ಕೇಳುವರು.",
			"6": "<sup>6</sup> ಅವನು ಅಲ್ಲವೆಂದರೆ ಅವನಿಗೆ - ನೀನು ಷಿಬ್ಬೋಲೆತ್ ಅನ್ನು ಎಂದು ಹೇಳುವರು. ಹಾಗೆ ಅನ್ನಲಿಕ್ಕೆ ಬಾರದೆ ಅವನು ಸಿಬ್ಬೋಲೆತ್ ಅನ್ನುವನು. ಕೂಡಲೆ ಅವರು ಅವನನ್ನು ಹಿಡಿದು ಯೊರ್ದನಿನ ಹಾಯಗಡಗಳ ಬಳಿಯಲ್ಲೇ ಕೊಂದುಹಾಕುವರು. ಹೀಗೆ ಆ ಕಾಲದಲ್ಲಿ ಎಫ್ರಾಯೀಮ್ಯರೊಳಗೆ ನಾಲ್ವತ್ತೆರಡು ಸಾವಿರ ಜನರು ಹತರಾದರು.",
			"7": "<sup>7</sup> ಗಿಲ್ಯಾದ್ಯನಾದ ಯೆಪ್ತಾಹನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಆರು ವರುಷ ಪಾಲಿಸಿದನಂತರ ಸತ್ತನು; ಅವನ ಶವವನ್ನು ಗಿಲ್ಯಾದಿನ ಪಟ್ಟಣಗಳಲ್ಲೊಂದರಲ್ಲಿ ಸಮಾಧಿ ಮಾಡಿದರು.",
			"8": "<sup>8</sup> ಅವನ ತರುವಾಯ ಬೇತ್ಲೆಹೇಮಿನವನಾದ ಇಬ್ಚಾನನು ಇಸ್ರಾಯೇಲ್ಯರ ಪಾಲಕನಾದನು.",
			"9": "<sup>9</sup> ಅವನಿಗೆ ಮೂವತ್ತು ಮಂದಿ ಗಂಡುಮಕ್ಕಳೂ ಮೂವತ್ತು ಮಂದಿ ಹೆಣ್ಣುಮಕ್ಕಳೂ ಇದ್ದರು; ತನ್ನ ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ಬೇರೆಯವರಿಗೆ ಕೊಟ್ಟು ಗಂಡು ಮಕ್ಕಳಿಗೋಸ್ಕರ ಹೊರಗಿನ ಮೂವತ್ತು ಮಂದಿ ಕನ್ಯೆಯರನ್ನು ತಂದನು.",
			"10": "<sup>10</sup> ಇವನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಏಳು ವರುಷ ಪಾಲಿಸಿದನಂತರ ಮೃತನಾಗಲು ಅವನ ಶವವನ್ನು ಬೇತ್ಲೆಹೇಮಿನಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು.",
			"11": "<sup>11</sup> ಇವನ ತರುವಾಯ ಜೆಬುಲೂನ್ಯನಾದ ಏಲೋನನು ಇಸ್ರಾಯೇಲ್ಯರ ಪಾಲಕನಾಗಿ ಹತ್ತು ವರುಷಗಳವರೆಗೆ",
			"12": "<sup>12</sup> ಅವರನ್ನು ಪಾಲಿಸಿದನಂತರ ಮರಣ ಹೊಂದಿದನು. ಅವನ ಶವವನ್ನು ಜೆಬುಲೂನ್ ದೇಶದ ಅಯ್ಯಾಲೋನಿನಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು.",
			"13": "<sup>13</sup> ಇವನ ತರುವಾಯ ಪಿರಾತೋನಿನವನೂ ಹಿಲ್ಲೇಲನ ಮಗನೂ ಆದ ಅಬ್ದೋನನು ಇಸ್ರಾಯೇಲ್ಯರ ಪಾಲಕನಾದನು.",
			"14": "<sup>14</sup> ಇವನಿಗೆ ನಾಲ್ವತ್ತು ಮಂದಿ ಮಕ್ಕಳೂ ಮೂವತ್ತು ಮಂದಿ ಮೊಮ್ಮಕ್ಕಳೂ ಇದ್ದರು. ಇವರೆಲ್ಲರಿಗೆ ಸವಾರಿಮಾಡುವದಕ್ಕೋಸ್ಕರ ಎಪ್ಪತ್ತು ಕತ್ತೆಗಳಿದ್ದವು. ಪಿರಾತೋನಿನವನೂ ಹಿಲ್ಲೇಲನ ಮಗನೂ ಆದ ಅಬ್ದೋನನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಎಂಟು ವರುಷ ಪಾಲಿಸಿದನಂತರ ಮರಣಹೊಂದಿದನು;",
			"15": "<sup>15</sup> ಅವನ ಶವವನ್ನು ಎಫ್ರಾಯೀಮ್ ದೇಶದಲ್ಲಿ ಅಮಾಲೇಕ್ಯರ ಬೆಟ್ಟದ ಮೇಲಿರುವ ಪಿರಾತೋನಿನಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು."
		},
		"13": {
			"1": "<sup>1</sup> ಇಸ್ರಾಯೇಲ್ಯರು ತಿರಿಗಿ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಗಳಾದದರಿಂದ ಆತನು ಅವರನ್ನು ನಾಲ್ವತ್ತು ವರುಷಗಳ ತನಕ ಫಿಲಿಷ್ಟಿಯರ ಕೈಗೆ ಒಪ್ಪಿಸಿದನು.",
			"2": "<sup>2</sup> ಚೊರ್ಗಾ ಎಂಬ ಊರಲ್ಲಿ ದಾನ್ ಕುಲದವನಾದ ಮಾನೋಹ ಎಂಬ ಒಬ್ಬ ಮನುಷ್ಯನಿದ್ದನು. ಅವನ ಹೆಂಡತಿಯು ಬಂಜೆಯಾಗಿದ್ದದರಿಂದ ಅವನಿಗೆ ಮಕ್ಕಳಿರಲಿಲ್ಲ.",
			"3": "<sup>3</sup> ಒಂದಾನೊಂದು ದಿವಸ ಯೆಹೋವನ ದೂತನು ಆಕೆಗೆ ಪ್ರತ್ಯಕ್ಷನಾಗಿ - ಇಗೋ, ಬಂಜೆಯಾಗಿ ಮಕ್ಕಳಿಲ್ಲದಿರುವ ನೀನು ಗರ್ಭವತಿಯಾಗಿ ಮಗನನ್ನು ಹೆರುವಿ.",
			"4": "<sup>4</sup> ಆದದರಿಂದ ಜಾಗರೂಕತೆಯಿಂದಿರು; ದ್ರಾಕ್ಷಾರಸವನ್ನಾಗಲಿ ಬೇರೆ ಯಾವ ಮದ್ಯವನ್ನಾಗಲಿ ಕುಡಿಯದಿರು; ಯಾವ ನಿಷಿದ್ಧಪದಾರ್ಥವನ್ನೂ ಊಟಮಾಡದಿರು.",
			"5": "<sup>5</sup> ನೀನು ಗರ್ಭವತಿಯಾಗಿ ಹೆರುವ ಮಗನ ತಲೆಯ ಮೇಲೆ ಕ್ಷೌರದ ಕತ್ತಿಯನ್ನು ಉಪಯೋಗಿಸಲೇಬಾರದು; ಅವನು ಹುಟ್ಟಿದಂದಿನಿಂದ ದೇವರಿಗೆ ಪ್ರತಿಷ್ಠಿತನಾಗಿರುವನು; ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಫಿಲಿಷ್ಟಿಯರ ಕೈಯಿಂದ ಬಿಡಿಸುವದಕ್ಕೆ ಪ್ರಾರಂಭಿಸುವನು ಅಂದನು.",
			"6": "<sup>6</sup> ತರುವಾಯ ಆ ಸ್ತ್ರೀಯು ತನ್ನ ಗಂಡನ ಬಳಿಗೆ ಹೋಗಿ - ಒಬ್ಬ ದೇವಪುರುಷನು ನನ್ನ ಹತ್ತಿರ ಬಂದಿದ್ದನು. ಅವನ ರೂಪವು ದೇವದೂತನ ರೂಪದಂತಿದ್ದು ಭಯಂಕರನಾಗಿದ್ದನು. ನೀನು ಎಲ್ಲಿಂದ ಬಂದಿ ಎಂದು ನಾನು ಅವನನ್ನು ಕೇಳಲಿಲ್ಲ; ಅವನೂ ತನ್ನ ಹೆಸರನ್ನು ತಿಳಿಸಲಿಲ್ಲ.",
			"7": "<sup>7</sup> ಆದರೆ ನನಗೆ - ನೀನು ಗರ್ಭವತಿಯಾಗಿ ಮಗನನ್ನು ಹೆರುವಿ; ಆದದರಿಂದ ನೀನು ದ್ರಾಕ್ಷಾರಸವನ್ನಾಗಲಿ ಬೇರೆ ಯಾವ ಮದ್ಯವನ್ನಾಗಲಿ ಕುಡಿಯದಿರು, ಯಾವ ನಿಷಿದ್ಧಾಹಾರವನ್ನೂ ಮುಟ್ಟಬೇಡ; ಆ ಹುಡುಗನು ಹುಟ್ಟಿದಂದಿನಿಂದ ಸಾಯುವವರೆಗೆ ದೇವರಿಗೆ ಪ್ರತಿಷ್ಠಿತನಾಗಿರುವನು ಎಂದು ಹೇಳಿದನು ಅಂದಳು.",
			"8": "<sup>8</sup> ಮಾನೋಹನು ಇದನ್ನು ಕೇಳಿ ಯೆಹೋವನಿಗೆ - ಸ್ವಾಮೀ, ದಯವಿರಲಿ; ನೀನು ಕಳುಹಿಸಿದ ದೇವಪುರುಷನು ಇನ್ನೊಂದು ಸಾರಿ ನಮ್ಮ ಬಳಿಗೆ ಬಂದು ಹುಟ್ಟಲಿಕ್ಕಿರುವ ಮಗುವಿಗೋಸ್ಕರ ಮಾಡಬೇಕಾದದ್ದನ್ನು ನಮಗೆ ಬೋಧಿಸಲಿ ಎಂದು ಬೇಡಿಕೊಳ್ಳಲು ದೇವರು ಅವನ ಮೊರೆಯನ್ನು ಕೇಳಿದನು;",
			"9": "<sup>9</sup> ಆ ಸ್ತ್ರೀಯು ಹೊಲದಲ್ಲಿ ಕೂತಿರುವಾಗ ದೇವದೂತನು ತಿರಿಗಿ ಬಂದನು. ಆಕೆಯ ಗಂಡನಾದ ಮಾನೋಹನು ಅಲ್ಲಿರಲಿಲ್ಲ.",
			"10": "<sup>10</sup> ಆದದರಿಂದ ಆಕೆಯು ಬೇಗನೆ ಗಂಡನ ಬಳಿಗೆ ಹೋಗಿ - ಮೊನ್ನೆ ನನಗೆ ಪ್ರತ್ಯಕ್ಷನಾದ ಪುರುಷನು ತಿರಿಗಿ ಬಂದಿದ್ದಾನೆಂದು ತಿಳಿಸಲು",
			"11": "<sup>11</sup> ಅವನೆದ್ದು ಹೆಂಡತಿಯೊಡನೆ ಬಂದು ಆ ಪುರುಷನಿಗೆ - ಮೊನ್ನೆ ಈಕೆಯೊಡನೆ ಮಾತಾಡಿದವನು ನೀನೋ ಎಂದು ಕೇಳಲು",
			"12": "<sup>12</sup> ಅವನು - ಹೌದು, ನಾನೇ ಅಂದನು. ಆಗ ಮಾನೋಹನು - ನೀನು ಹೇಳಿದ್ದು ನೆರವೇರಿದಾಗ ನಾವು ಆ ಮಗುವಿಗೋಸ್ಕರ ಮಾಡತಕ್ಕದ್ದೇನು? ಅವನನ್ನು ಹೇಗೆ ನಡಿಸತಕ್ಕದ್ದು ಎಂದು ಕೇಳಲು",
			"13": "<sup>13</sup> ಯೆಹೋವನ ದೂತನು ಮಾನೋಹನಿಗೆ - ನಾನು ಹೇಳಿದ್ದನ್ನೆಲ್ಲಾ ಈಕೆಯು ಜಾಗರೂಕತೆಯಿಂದ ಕೈಕೊಳ್ಳಲಿ.",
			"14": "<sup>14</sup> ದ್ರಾಕ್ಷಾಫಲವನ್ನು ತಿನ್ನದಿರಲಿ; ದ್ರಾಕ್ಷಾರಸವನ್ನಾಗಲಿ ಬೇರೆ ಯಾವ ಮದ್ಯವನ್ನಾಗಲಿ ಕುಡಿಯದಿರಲಿ; ನಿಷಿದ್ಧಾಹಾರವನ್ನು ಮುಟ್ಟದಿರಲಿ; ಹೀಗೆ ನಾನು ಆಜ್ಞಾಪಿಸಿದ್ದನ್ನೆಲ್ಲಾ ಕೈಕೊಳ್ಳಲಿ ಅಂದನು.",
			"15": "<sup>15</sup> ಮತ್ತು ಮಾನೋಹನು ಯೆಹೋವನ ದೂತನನ್ನು - ನಾವು ನಿನಗೋಸ್ಕರ ಒಂದು ಹೋತಮರಿಯನ್ನು ಪಕ್ವಮಾಡಿ ತರುವವರೆಗೂ ದಯವಿಟ್ಟು ಇಲ್ಲೇ ನಿಲ್ಲಬೇಕೆಂದು ಬೇಡಿಕೊಳ್ಳಲು",
			"16": "<sup>16</sup> ಅವನು - ನೀನು ನನ್ನನ್ನು ನಿಲ್ಲಿಸಿಕೊಂಡರೂ ನಾನು ನಿನ್ನ ಆಹಾರವನ್ನು ಊಟಮಾಡುವದಿಲ್ಲ; ಯಜ್ಞಮಾಡಬೇಕೆಂದು ನಿನಗೆ ಮನಸ್ಸಿದ್ದರೆ ಅದನ್ನು ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಿಸು ಅಂದನು. ಅವನು ಯೆಹೋವನ ದೂತನೆಂಬದು ಮಾನೋಹನಿಗೆ ಗೊತ್ತಿದ್ದಿಲ್ಲ.",
			"17": "<sup>17</sup> ಆದದರಿಂದ ಅವನು ಆ ದೂತನನ್ನು - ನೀನು ಹೇಳಿದ್ದು ನೆರವೇರಿದಾಗ ನಿನ್ನನ್ನು ಸನ್ಮಾನಿಸಬೇಕೆಂದಿರುತ್ತೇವೆ, ನಿನ್ನ ಹೆಸರೇನು ಎಂದು ಕೇಳಿದನು.",
			"18": "<sup>18</sup> ಯೆಹೋವನ ದೂತನು - ನನ್ನ ಹೆಸರನ್ನು ಕೇಳುವದೇಕೆ? ಅದು ಆಶ್ಚರ್ಯಕರವಾದದ್ದು ಅಂದನು.",
			"19": "<sup>19</sup> ಮಾನೋಹನು ಹೋತಮರಿಯನ್ನೂ ಧಾನ್ಯದ್ರವ್ಯವನ್ನೂ ತಂದು ಬಂಡೆಯ ಮೇಲಿಟ್ಟು ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಿಸಿದನು. ಮಾನೋಹನೂ ಅವನ ಹೆಂಡತಿಯೂ ನೋಡುತ್ತಿರುವಾಗಲೇ ಯೆಹೋವನ ದೂತನು ಆಶ್ಚರ್ಯವನ್ನು ನಡಿಸಿದನು.",
			"20": "<sup>20</sup> ಏನಂದರೆ ಅವನು ಯಜ್ಞವೇದಿಯಿಂದ ಆಕಾಶಕ್ಕೆ ಹೋಗುತ್ತಿರುವ ಅಗ್ನಿ ಜ್ವಾಲೆಯೊಳಗೆ ಮೇಲಕ್ಕೇರಿ ಹೋದನು. ಅವರು ಇದನ್ನು ನೋಡುತ್ತಲೆ ನೆಲದ ಮೇಲೆ ಬೋರಲ ಬಿದ್ದರು.",
			"21": "<sup>21</sup> ಯೆಹೋವನ ದೂತನು ಮಾನೋಹನಿಗೂ ಅವನ ಹೆಂಡತಿಗೂ ತಿರಿಗಿ ಕಾಣಿಸಲಿಲ್ಲ; ಆಗ ಮಾನೋಹನು ಆತನು ಯೆಹೋವನ ದೂತನೆಂದು ತಿಳಿದು ತನ್ನ ಹೆಂಡತಿಗೆ -",
			"22": "<sup>22</sup> ನಾವು ಸಾಯಬೇಕು. ದೇವರನ್ನು ಕಣ್ಣಾರೆ ಕಂಡೆವಲ್ಲಾ ಅನ್ನಲು",
			"23": "<sup>23</sup> ಆಕೆಯು ಅವನಿಗೆ - ಯೆಹೋವನು ನಮ್ಮನ್ನು ಕೊಲ್ಲಬೇಕೆಂದಿದ್ದರೆ ಆತನು ನಮ್ಮ ಕೈಯಿಂದ ಯಜ್ಞವನ್ನೂ ನೈವೇದ್ಯವನ್ನೂ ಸ್ವೀಕರಿಸುತ್ತಿದ್ದಿಲ್ಲ; ಈಗ ಇವುಗಳನ್ನೆಲ್ಲಾ ನಮಗೆ ತೋರಿಸುತ್ತಿದ್ದಿಲ್ಲ, ಹೇಳುತ್ತಿದ್ದಿಲ್ಲ ಅಂದಳು.",
			"24": "<sup>24</sup> ಆ ಸ್ತ್ರೀಯು ಮಗನನ್ನು ಹೆತ್ತು ಅವನಿಗೆ ಸಂಸೋನನೆಂದು ಹೆಸರಿಟ್ಟಳು. ಹುಡುಗನು ದೊಡ್ಡವನಾದನು. ಯೆಹೋವನ ಆಶೀರ್ವಾದವು ಅವನ ಮೇಲಿತ್ತು.",
			"25": "<sup>25</sup> ಇದಲ್ಲದೆ ಅವನು ಚೊರ್ಗಕ್ಕೂ ಎಷ್ಟಾವೋಲಿಗೂ ಮಧ್ಯದಲ್ಲಿರುವ ದಾನ್‍ಕುಲದ ಪಾಳೆಯದಲ್ಲಿದ್ದಾಗ ಯೆಹೋವನ ಆತ್ಮವು ಅವನನ್ನು ಪ್ರೇರೇಪಿಸಹತ್ತಿತು."
		},
		"14": {
			"1": "<sup>1</sup> ಸಂಸೋನನು ಗಟ್ಟಾ ಇಳಿದು ತಿಮ್ನಾ ಊರಿಗೆ ಹೋಗಿ ಅಲ್ಲಿನ ಫಿಲಿಷ್ಟಿಯರ ಹೆಣ್ಣುಗಳಲ್ಲಿ ಒಬ್ಬಳನ್ನು ನೋಡಿ",
			"2": "<sup>2</sup> ತಿರಿಗಿ ತಂದೆತಾಯಿಗಳ ಬಳಿಗೆ ಬಂದು ಅವರಿಗೆ - ನಾನು ತಿಮ್ನಾ ಊರಲ್ಲಿ ಒಬ್ಬ ಫಿಲಿಷ್ಟಿಯ ಹೆಣ್ಣನ್ನು ನೋಡಿದ್ದೇನೆ; ಆಕೆಯನ್ನು ನನಗೆ ಮದುವೆ ಮಾಡಿಕೊಡಿರಿ ಎಂದು ಹೇಳಿದನು.",
			"3": "<sup>3</sup> ಆಗ ಅವರು ಅವನಿಗೆ - ಸುನ್ನತಿಯಿಲ್ಲದ ಫಿಲಿಷ್ಟಿಯರ ಹೆಣ್ಣನ್ನು ವರಿಸಬೇಕೆಂದಿರುತ್ತೀ; ನಿನ್ನ ಬಂಧುಗಳಲ್ಲಿಯೂ ನಮ್ಮ ಎಲ್ಲಾ ಜನರಲ್ಲಿಯೂ ನಿನಗೆ ಹೆಣ್ಣು ಸಿಕ್ಕುವದಿಲ್ಲವೋ ಎಂದು ಕೇಳಲು ಅವನು - ನನಗೋಸ್ಕರ ಆಕೆಯನ್ನೇ ತೆಗೆದುಕೊಳ್ಳಬೇಕು; ಆಕೆಯನ್ನೇ ಮೆಚ್ಚಿಕೊಂಡಿದ್ದೇನೆ ಅಂದನು.",
			"4": "<sup>4</sup> ಫಿಲಿಷ್ಟಿಯರ ಕೇಡಿಗೆ ಕಾರಣ ಹುಡುಕುತ್ತಿದ್ದ ಯೆಹೋವನ ಪ್ರೇರಣೆಯಿಂದಲೇ ಈ ಸಂಗತಿ ಉಂಟಾಯಿತೆಂಬದು ಅವನ ತಂದೆತಾಯಿಗಳಿಗೆ ಗೊತ್ತಿದ್ದಿಲ್ಲ. ಆ ಕಾಲದಲ್ಲಿ ಫಿಲಿಷ್ಟಿಯರು ಇಸ್ರಾಯೇಲ್ಯರ ಮೇಲೆ ದೊರೆತನ ನಡಿಸುತ್ತಿದ್ದರು.",
			"5": "<sup>5</sup> ಸಂಸೋನನು ತನ್ನ ತಂದೆತಾಯಿಗಳ ಸಹಿತವಾಗಿ ತಿಮ್ನಾ ಊರಿಗೆ ಹೊರಟು ಅಲ್ಲಿನ ದ್ರಾಕ್ಷೇತೋಟಗಳ ಬಳಿಗೆ ಬಂದಾಗ ಒಂದು ಪ್ರಾಯದ ಸಿಂಹವು ಗರ್ಜಿಸುತ್ತಾ ಅವನೆದುರಿಗೆ ಬಂದಿತು.",
			"6": "<sup>6</sup> ಅವನ ಕೈಯಲ್ಲಿ ಏನೂ ಇಲ್ಲದಿದ್ದರೂ ಯೆಹೋವನ ಆತ್ಮವು ಅವನ ಮೇಲೆ ಫಕ್ಕನೆ ಬಂದದರಿಂದ ಆ ಸಿಂಹವನ್ನು ಹೋತ ಮರಿಯನ್ನೋ ಎಂಬಂತೆ ಸೀಳಿಬಿಟ್ಟನು. ಈ ಸಂಗತಿಯನ್ನು ತಂದೆತಾಯಿಗಳಿಗೆ ತಿಳಿಸಲಿಲ್ಲ.",
			"7": "<sup>7</sup> ಅನಂತರ ಅವನು ಊರೊಳಗೆ ಹೋಗಿ ಆ ಸ್ತ್ರೀಯನ್ನು ಮಾತಾಡಿಸಿ ಆಕೆಯನ್ನು ಮೆಚ್ಚಿಕೊಂಡನು.",
			"8": "<sup>8</sup> ಕೆಲವು ದಿವಸಗಳಾದ ಮೇಲೆ ಅವನು ಆಕೆಯನ್ನು ವರಿಸುವದಕ್ಕೋಸ್ಕರ ತಿರಿಗಿ ಆ ಊರಿಗೆ ಹೋಗುತ್ತಿದ್ದಾಗ ದಾರೀ ಬಿಟ್ಟು ಸಿಂಹದ ಹೆಣವನ್ನು ನೋಡುವದಕ್ಕೆ ಹೋಗಲು ಅದರ ಒಡಲಲ್ಲಿ ಜೇನುಹುಳಗಳನ್ನೂ ಜೇನನ್ನೂ ಕಂಡು",
			"9": "<sup>9</sup> ಆ ಜೇನನ್ನು ಕೈಯಲ್ಲಿ ತೆಗೆದುಕೊಂಡು ತಿನ್ನುತ್ತಾ ಹೋದನು; ತನ್ನ ತಂದೆತಾಯಿಗಳಿಗೂ ಕೊಟ್ಟನು; ಅವರು ತಿಂದರು. ಆದರೆ ಅದು ತನಗೆ ಸಿಂಹದ ಒಡಲಲ್ಲಿ ಸಿಕ್ಕಿತೆಂಬದನ್ನು ಅವರಿಗೆ ತಿಳಿಸಲಿಲ್ಲ.",
			"10": "<sup>10</sup> ಸಂಸೋನನ ತಂದೆಯು ಹೆಣ್ಣಿನ ಮನೆಗೆ ಬಂದ ಮೇಲೆ ಸಂಸೋನನು ಯೌವನಸ್ಥರ ಪದ್ಧತಿಯ ಪ್ರಕಾರ ಅಲ್ಲಿ ಔತಣವನ್ನು ಸಿದ್ಧಮಾಡಿಸಿದನು.",
			"11": "<sup>11</sup> ಅವರು ಅವನನ್ನು ನೋಡಿದಾಗ ತಮ್ಮಲ್ಲಿಂದ ಮೂವತ್ತು ಮಂದಿಯನ್ನು ಆರಿಸಿ ಅವನ ಜೊತೆಯಲ್ಲಿರಿಸಿದರು.",
			"12": "<sup>12</sup> ಸಂಸೋನನು ಇವರಿಗೆ - ನಾನು ನಿಮಗೆ ಒಂದು ಒಗಟನ್ನು ಹೇಳುತ್ತೇನೆ; ನೀವು ಔತಣದ ಏಳು ದಿನಗಳಲ್ಲಿಯೇ ಅದನ್ನು ಬಿಚ್ಚಿ ಅರ್ಥವನ್ನು ಹೇಳಿದರೆ ನಿಮಗೆ ಮೂವತ್ತು ದುಪ್ಪಟಿಗಳನ್ನೂ ಮೂವತ್ತು ವಿಶೇಷವಸ್ತ್ರಗಳನ್ನೂ ಕೊಡುವೆನು.",
			"13": "<sup>13</sup> ನಿಮ್ಮಿಂದ ಆಗದೆ ಹೋದರೆ ನೀವು ನನಗೆ ಮೂವತ್ತು ದುಪ್ಪಟಿಗಳನ್ನೂ ಮೂವತ್ತು ವಿಶೇಷವಸ್ತ್ರಗಳನ್ನೂ ಕೊಡಬೇಕು ಅನ್ನಲು ಅವರು ಅವನಿಗೆ - ನಿನ್ನ ಒಗಟನ್ನು ಹೇಳು, ಕೇಳೋಣ ಎಂದು ಹೇಳಿದರು.",
			"14": "<sup>14</sup> ಆಗ ಅವನು - ತಿಂದುಬಿಡುವಂಥದರಿಂದ ತಿನ್ನತಕ್ಕದ್ದು ದೊರಕಿತು; ಕ್ರೂರವಾದದ್ದರಿಂದ ಮಧುರವಾದದ್ದು ಸಿಕ್ಕಿತು ಎಂದು ಹೇಳಿದನು. ಮೂರು ದಿನಗಳಾದರೂ ಅವರು ಅದನ್ನು ಬಿಚ್ಚಲಾರದೆ ಹೋದರು.",
			"15": "<sup>15</sup> ಅವರು ಏಳನೆಯ ದಿನದಲ್ಲಿ ಸಂಸೋನನ ಹೆಂಡತಿಗೆ - ನಿನ್ನ ಗಂಡನನ್ನು ಮರುಳುಗೊಳಿಸಿ ಅವನಿಂದ ಒಗಟಿನ ಅರ್ಥವನ್ನು ತಿಳಿದುಕೋ; ಇಲ್ಲವಾದರೆ ನಾವು ನಿನ್ನನ್ನೂ ನಿನ್ನ ತಂದೆಯ ಮನೆಯವರನ್ನೂ ಬೆಂಕಿಯಿಂದ ಸುಟ್ಟುಬಿಡುವೆವು; ನಮಗಿರುವದೆಲ್ಲವನ್ನೂ ಕಸಕೊಳ್ಳುವದಕ್ಕೆ ನಮ್ಮನ್ನು ಇಲ್ಲಿಗೆ ಕರಿಸಿದಿರೋ ಅಂದರು.",
			"16": "<sup>16</sup> ಆಕೆಯು ಸಂಸೋನನ ಮುಂದೆ ಅಳುತ್ತಾ - ನೀನು ನನ್ನನ್ನು ಪ್ರೀತಿಸುವದಿಲ್ಲ, ದ್ವೇಷಿಸುತ್ತೀಯಷ್ಟೆ; ನನ್ನ ಜನರಿಗೆ ಒಗಟನ್ನು ಹೇಳಿದ್ದೀ, ಅದರ ಅರ್ಥವನ್ನು ನನಗೆ ತಿಳಿಸಲಿಲ್ಲವಲ್ಲಾ ಎಂದು ತೊಂದರೆಪಡಿಸಿದಳು. ಅವನು ಆಕೆಗೆ - ಅದನ್ನು ನನ್ನ ತಂದೆತಾಯಿಗಳಿಗೇ ಹೇಳಲಿಲ್ಲ; ನಿನಗೆ ಹೇಳುವೆನೋ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"17": "<sup>17</sup> ಆಕೆಯು ಔತಣದ ಏಳು ದಿನಗಳಲ್ಲಿಯೂ ಅವನ ಮುಂದೆ ಅಳುತ್ತಿದ್ದಳು. ಏಳನೆಯ ದಿನದಲ್ಲಿ ಆಕೆಯು ಬಹಳವಾಗಿ ಪೀಡಿಸಿದ್ದರಿಂದ ಅವನು ಹೇಳಿಬಿಟ್ಟನು. ಆಕೆಯು ಒಗಟಿನ ಅರ್ಥವನ್ನು ತನ್ನ ಜನರಿಗೆ ತಿಳಿಸಿದಳು.",
			"18": "<sup>18</sup> ಅವರು ಏಳನೆಯ ದಿನ ಸೂರ್ಯಾಸ್ತಮಾನಕ್ಕೆ ಮೊದಲು ಅವನಿಗೆ - ಜೇನಿಗಿಂತ ಸಿಹಿಯಾದದ್ದು ಯಾವದು? ಸಿಂಹಕ್ಕಿಂತ ಕ್ರೂರವಾದದಾವದು ಎಂದು ಹೇಳಲು ಅವನು - ನೀವು ನನ್ನ ಕಡಸಿನಿಂದ ಉಳದಿದ್ದರೆ ಒಗಟನ್ನು ಬಿಚ್ಚುವದು ನಿಮ್ಮಿಂದಾಗುತ್ತಿರಲಿಲ್ಲ ಅಂದನು.",
			"19": "<sup>19</sup> ಯೆಹೋವನ ಆತ್ಮವು ಫಕ್ಕನೆ ಅವನ ಮೇಲೆ ಬಂದಿತು; ಅವನು ಅಷ್ಕೆಲೋನಿಗೆ ಹೋಗಿ ಅಲ್ಲಿನ ಮೂವತ್ತು ಮಂದಿಯನ್ನು ಹೊಡೆದು ಅವರ ವಸ್ತ್ರಗಳನ್ನು ಸುಲುಕೊಂಡು ಒಗಟನ್ನು ಬಿಚ್ಚಿದವರಿಗೆ ತಂದುಕೊಟ್ಟನು. ಮತ್ತು ರೇಗಿದವನಾಗಿ ತನ್ನ ತಂದೆಯ ಮನೆಗೆ ಹೋದನು.",
			"20": "<sup>20</sup> ಇತ್ತ ಸಂಸೋನನ ಹೆಂಡತಿಯನ್ನು ಆ [ಮೂವತ್ತು] ಮಂದಿಯಲ್ಲಿ ಅವನಿಗೆ ಸ್ನೇಹಿತನಾಗಿದ್ದವನಿಗೆ ಮದುವೆಮಾಡಿ ಕೊಟ್ಟರು."
		},
		"15": {
			"1": "<sup>1</sup> ಕೆಲವು ದಿನಗಳಾದ ಮೇಲೆ ಗೋದಿಯ ಸುಗ್ಗಿಯಲ್ಲಿ ಸಂಸೋನನು ಒಂದು ಹೋತಮರಿಯನ್ನು ತೆಗೆದುಕೊಂಡು ತನ್ನ ಹೆಂಡತಿಯನ್ನು ನೋಡುವದಕ್ಕೋಸ್ಕರ ಹೊರಟುಹೋಗಿ ಆಕೆಯ ತಂದೆಗೆ - ನನ್ನನ್ನು ನನ್ನ ಹೆಂಡತಿಯ ಹತ್ತಿರ ಒಳಗಿನ ಕೋಣೆಗೆ ಹೋಗಗೊಡಿಸು ಅನ್ನಲು ಅವನು - ಹೋಗಬಾರದು;",
			"2": "<sup>2</sup> ನೀನು ನಿಜವಾಗಿ ಆಕೆಯನ್ನು ಹಗೆ ಮಾಡುತ್ತೀಯೆಂದು ನಿನ್ನ ಗೆಳೆಯರಲ್ಲೊಬ್ಬನಿಗೆ ಮದುವೆಮಾಡಿ ಕೊಟ್ಟೆನು. ಆಕೆಯ ತಂಗಿಯು ಆಕೆಗಿಂತ ಸುಂದರಿಯಾಗಿದ್ದಾಳಲ್ಲಾ; ಈಕೆಯು ನಿನಗಿರಲಿ ಅಂದನು.",
			"3": "<sup>3</sup> ಆಗ ಸಂಸೋನನು - ಈ ಸಾರಿ ನಾನು ಫಿಲಿಷ್ಟಿಯರಿಗೆ ಕೇಡುಮಾಡುವದಾದರೆ ತಪ್ಪು ನನ್ನದಲ್ಲ ಎಂದಂದುಕೊಂಡು ಹೋಗಿ",
			"4": "<sup>4</sup> ಮುನ್ನೂರು ನರಿಗಳನ್ನು ಹಿಡಿದು ಬಾಲಕ್ಕೆ ಬಾಲವನ್ನು ಸೇರಿಸಿ ಕಟ್ಟಿ ಅವುಗಳ ನಡುವೆ ಒಂದೊಂದು ಪಂಜನ್ನು ಸಿಕ್ಕಿಸಿ",
			"5": "<sup>5</sup> ಪಂಜುಗಳಿಗೆ ಬೆಂಕಿಹೊತ್ತಿಸಿ ಆ ನರಿಗಳನ್ನು ಫಿಲಿಷ್ಟಿಯರ ಪೈರುಗಳಲ್ಲಿ ಬಿಟ್ಟು ಅವರ ತೆನೆಗೂಡುಗಳನ್ನೂ ಇನ್ನೂ ಕೊಯ್ಯದೆ ಇದ್ದ ಪೈರುಗಳನ್ನೂ ಎಣ್ಣೆಯ ಮರಗಳ ತೋಟಗಳನ್ನೂ ಸುಟ್ಟುಬಿಟ್ಟನು.",
			"6": "<sup>6</sup> ಫಿಲಿಷ್ಟಿಯರು ಇದನ್ನು ಮಾಡಿದವರಾರೆಂದು ವಿಚಾರಿಸುವಲ್ಲಿ ತಿಮ್ನಾ ಊರಿನವನ ಅಳಿಯನಾದ ಸಂಸೋನನೆಂದು ಗೊತ್ತಾಯಿತು. ಅವನ ಮಾವನು ಅವನ ಹೆಂಡತಿಯನ್ನು ಬೇರೊಬ್ಬನಿಗೆ ಕೊಟ್ಟುಬಿಟ್ಟದ್ದೇ ಇದಕ್ಕೆ ಕಾರಣವೆಂದು ಅವರು ತಿಳಿದು ಬಂದು ಆಕೆಯನ್ನೂ ಆಕೆಯ ತಂದೆಯನ್ನೂ ಸುಟ್ಟುಬಿಟ್ಟರು.",
			"7": "<sup>7</sup> ಸಂಸೋನನು ಅವರಿಗೆ - ನೀವು ಹೀಗೆ ಮಾಡಿದ್ದರಿಂದ ನಾನು ನಿಮಗೆ ಮುಯ್ಯಿತೀರಿಸದೆ ಬಿಡುವದಿಲ್ಲ ಎಂದು ಹೇಳಿ",
			"8": "<sup>8</sup> ಅವರ ತೊಡೆ ಸೊಂಟಗಳನ್ನು ಮುರಿದು ಅವರನ್ನು ಸಂಹರಿಸಿ ತಾನು ಹೋಗಿ ಏಟಾಮ್ ಗಿರಿಯ ಗುಹೆಯಲ್ಲಿ ವಾಸಮಾಡಿದನು.",
			"9": "<sup>9</sup> ಆಗ ಫಿಲಿಷ್ಟಿಯರು ಹೊರಟು ಯೆಹೂದಾ ಪ್ರಾಂತದ ಲೆಹೀ ಎಂಬಲ್ಲಿಗೆ ಹೋಗಿ ಪಾಳೆಯಮಾಡಿಕೊಂಡರು.",
			"10": "<sup>10</sup> ನೀವು ನಮಗೆ ವಿರೋಧವಾಗಿ ಯುದ್ಧಕ್ಕೆ ಬಂದದ್ದೇಕೆ ಎಂದು ಯೆಹೂದ್ಯರು ಅವರನ್ನು ಕೇಳಲು ಅವರು - ಸಂಸೋನನನ್ನು ಹಿಡಿದು ಕಟ್ಟಿ ಅವನು ನಮಗೆ ಮಾಡಿದಂತೆಯೇ ಅವನಿಗೂ ಮಾಡಬೇಕೆಂದು ಬಂದೆವು ಎಂದು ಉತ್ತರಕೊಟ್ಟರು.",
			"11": "<sup>11</sup> ಮೂರು ಸಾವಿರ ಮಂದಿ ಯೆಹೂದ್ಯರು ಏಟಾಮ್ ಗಿರಿಗೆ ಹೋಗಿ ಸಂಸೋನನಿಗೆ - ಫಿಲಿಷ್ಟಿಯರು ನಮ್ಮ ಮೇಲೆ ದೊರೆತನ ಮಾಡುತ್ತಿದ್ದಾರೆಂಬದು ನಿನಗೆ ಗೊತ್ತಿಲ್ಲವೋ? ಹೀಗೆ ಯಾಕೆ ಮಾಡಿದಿ ಎಂದು ಕೇಳಲು ಅವನು - ಅವರು ನನಗೆ ಮಾಡಿದಂತೆಯೇ ನಾನು ಅವರಿಗೆ ಮಾಡಿದ್ದೇನೆ ಅಂದನು.",
			"12": "<sup>12</sup> ಅವರು - ನಾವು ನಿನ್ನನ್ನು ಕಟ್ಟಿ ಫಿಲಿಷ್ಟಿಯರ ಕೈಗೆ ಒಪ್ಪಿಸಬೇಕೆಂದು ಬಂದಿದ್ದೇವೆ ಅನ್ನಲು ಸಂಸೋನನು - ಹಾಗಾದರೆ ನೀವು ನನ್ನನ್ನು ಕೊಲ್ಲುವದಿಲ್ಲವೆಂದು ಆಣೆಮಾಡಿರಿ ಎಂದನು.",
			"13": "<sup>13</sup> ಅದಕ್ಕೆ ಅವರು - ನಾವು ಸತ್ಯವಾಗಿ ನಿನ್ನನ್ನು ಕೊಲ್ಲುವದಿಲ್ಲ; ನಿನ್ನನ್ನು ಕಟ್ಟಿ ಫಿಲಿಷ್ಟಿಯರ ಕೈಗೆ ಒಪ್ಪಿಸುತ್ತೇವೆ ಎಂದು ಹೇಳಿ ಅವನನ್ನು ಎರಡು ಹೊಸ ಹಗ್ಗಗಳಿಂದ ಕಟ್ಟಿ ಗಿರಿಯಿಂದ ತಂದರು.",
			"14": "<sup>14</sup> ಸಂಸೋನನು ಲೆಹೀಯ ಸಮೀಪಕ್ಕೆ ಬರಲು ಫಿಲಿಷ್ಟಿಯರು ಅವನನ್ನು ಕಂಡು ಆರ್ಭಟಿಸಿದರು. ಯೆಹೋವನ ಆತ್ಮವು ಅವನ ಮೇಲೆ ಬಂದದರಿಂದ ಅವನ ರಟ್ಟೆಗಳಿಗೆ ಸುತ್ತಲೂ ಕಟ್ಟಿದ್ದ ಹಗ್ಗಗಳು ಸುಟ್ಟ ಸೆಣಬಿನ ಹಾಗಾದವು; ಕೈಗೆ ಹಾಕಿದ ಬೇಡಿಗಳು ಕಳಚಿ ಬಿದ್ದವು.",
			"15": "<sup>15</sup> ಅಲ್ಲಿ ಒಂದು ಕತ್ತೆಯ ದವಡೇ ಎಲುಬು ಬಿದ್ದಿತು. ಅದು ಇನ್ನೂ ಹಸಿ ಇತ್ತು. ಅವನು ಅದನ್ನು ತೆಗೆದುಕೊಂಡು ಅದರಿಂದ ಸಾವಿರ ಜನರನ್ನು ಹತಿಸಿಬಿಟ್ಟು -",
			"16": "<sup>16</sup> ಕತ್ತೆಯ ದವಡೇ ಎಲುಬಿನಿಂದ ಸಾವಿರ ಜನರನ್ನು ವಧಿಸಿದೆನು; ಕತ್ತೆಯ ದವಡೇ ಎಲುಬಿನಿಂದ ಹೆಣಗಳನ್ನು ರಾಶಿರಾಶಿಯಾಗಿ ಬೀಳಿಸಿದೆನು",
			"17": "<sup>17</sup> ಎಂದು ಹೇಳಿ ತನ್ನ ಕೈಯಲ್ಲಿದ್ದ ಎಲುಬನ್ನು ಬೀಸಾಡಿದನು. ಆ ಸ್ಥಳಕ್ಕೆ ರಾಮತ್ ಲೆಹೀ ಎಂದು ಹೆಸರಾಯಿತು.",
			"18": "<sup>18</sup> ಮತ್ತು ಅವನು ಬಹಳ ದಾಹವುಳ್ಳವನಾಗಿ ಯೆಹೋವನಿಗೆ - ನೀನು ನಿನ್ನ ಸೇವಕನ ಮೂಲಕವಾಗಿ ಈ ಮಹಾಜಯವನ್ನುಂಟು ಮಾಡಿದ ಮೇಲೆ ನಾನು ದಾಹದಿಂದ ಸತ್ತು ಸುನ್ನತಿಯಿಲ್ಲದವರ ಕೈಯಲ್ಲಿ ಬೀಳಬೇಕೋ ಎಂದು ಮೊರೆಯಿಡಲು",
			"19": "<sup>19</sup> ಆತನು ಲೆಹೀಯಲ್ಲಿ ಒರಳಬಂಡೆಯನ್ನು ಸೀಳಿ ಅದರೊಳಗಿಂದ ನೀರು ಬರುವಂತೆ ಮಾಡಲು ಅವನು ಕುಡಿದು ಪುನಃ ಚೈತನ್ಯ ಹೊಂದಿದನು. ಆದದರಿಂದ ಅದಕ್ಕೆ ಏನ್‍ಹಕ್ಕೋರೇ ಎಂದು ಹೆಸರಾಯಿತು; ಅದು ಇಂದಿನವರೆಗೂ ಲೆಹೀಯಲ್ಲಿರುತ್ತದೆ.",
			"20": "<sup>20</sup> ಫಿಲಿಷ್ಟಿಯರ ಪ್ರಾಬಲ್ಯದಲ್ಲಿ ಸಂಸೋನನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಇಪ್ಪತ್ತು ವರುಷಗಳವರೆಗೆ ಪಾಲಿಸಿದನು."
		},
		"16": {
			"1": "<sup>1</sup> ತರುವಾಯ ಸಂಸೋನನು ಗಾಜಕ್ಕೆ ಹೋಗಿ ಅಲ್ಲಿ ಒಬ್ಬ ಸೂಳೆಯನ್ನು ಕಂಡು ಅವಳ ಬಳಿಗೆ ಹೋದನು.",
			"2": "<sup>2</sup> ಸಂಸೋನನು ಬಂದಿರುವದು ಗಾಜದವರಿಗೆ ಗೊತ್ತಾಗಲು ಅವರು ಬಂದು ಅವನನ್ನು ಸುತ್ತಿಕೊಂಡರು; ಸೂರ್ಯೋದಯವಾಗುತ್ತಲೆ ಅವನನ್ನು ಕೊಂದುಹಾಕೋಣ ಎಂದುಕೊಂಡು ರಾತ್ರಿಯೆಲ್ಲಾ ಊರುಬಾಗಲಲ್ಲಿ ಹೊಂಚಿನೋಡುತ್ತಾ ಸುಮ್ಮನಿದ್ದರು.",
			"3": "<sup>3</sup> ಸಂಸೋನನು ಮಲಗಿ ಮಧ್ಯರಾತ್ರಿಯಲ್ಲೆದ್ದು ಊರುಬಾಗಲಿನ ಕದಗಳನ್ನೂ ಅದರ ಎರಡು ನಿಲುವು ಪಟ್ಟಿಗಳನ್ನೂ ಅಗುಳಿಗಳನ್ನೂ ಕಿತ್ತು ಹೆಗಲಮೇಲೆ ಹೊತ್ತುಕೊಂಡು ಹೋಗಿ ಹೆಬ್ರೋನಿನ ಎದುರಿಗಿರುವ ಪರ್ವತದ ಶಿಖರದ ಮೇಲೆ ಇಟ್ಟನು.",
			"4": "<sup>4</sup> ಅನಂತರ ಸಂಸೋನನು ಸೋರೇಕ್ ತಗ್ಗಿನಲ್ಲಿ ವಾಸವಾಗಿದ್ದ ದೆಲೀಲಾ ಎಂಬ ಹೆಸರುಳ್ಳ ಒಬ್ಬ ಸ್ತ್ರೀಯಲ್ಲಿ ಮೋಹಿತನಾದನು.",
			"5": "<sup>5</sup> ಫಿಲಿಷ್ಟಿಯ ಪ್ರಭುಗಳು ಅವಳ ಬಳಿಗೆ ಹೋಗಿ ಅವಳಿಗೆ - ನೀನು ಅವನನ್ನು ಮರುಳುಗೊಳಿಸಿ ಅವನ ಮಹಾಶಕ್ತಿಯ ಮೂಲ ಯಾವದೆಂಬದನ್ನೂ ನಾವು ಅವನನ್ನು ಗೆದ್ದು ಕಟ್ಟಿ ಕುಂದಿಸುವದು ಹೇಗೆಂಬದನ್ನೂ ಗೊತ್ತುಮಾಡಿಕೊಂಡು ನಮಗೆ ತಿಳಿಸು; ನಮ್ಮಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬನೂ ನಿನಗೆ ಸಾವಿರದ ನೂರು ರೂಪಾಯಿ ಕೊಡುವೆವು ಎಂದು ಹೇಳಿದರು.",
			"6": "<sup>6</sup> ದೆಲೀಲಳು ಸಂಸೋನನಿಗೆ - ನಿನಗೆ ಇಂಥ ಮಹಾಶಕ್ತಿ ಹೇಗೆ ಬಂದಿತು? ನಿನ್ನನ್ನು ಕಟ್ಟಿ ಕುಂದಿಸುವದು ಹೇಗೆ ಎಂಬದನ್ನು ದಯವಿಟ್ಟು ನನಗೆ ತಿಳಿಸು ಅನ್ನಲು",
			"7": "<sup>7</sup> ಅವನು - ಕರುಳಿನ ಏಳು ಹಸೀ ತಂತಿಗಳಿಂದ ನನ್ನನ್ನು ಕಟ್ಟಿದರೆ ನಾನು ಬಲಹೀನನಾಗಿ ಬೇರೆ ಮನುಷ್ಯನಂತಾಗುವೆನು ಎಂದು ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ಫಿಲಿಷ್ಟಿಯ ಪ್ರಭುಗಳು ಅಂಥ ಏಳು ಹಸೀ ತಂತಿಗಳನ್ನು ಅವಳಿಗೆ ತಂದುಕೊಟ್ಟರು.",
			"9": "<sup>9</sup> ಅವಳು ಒಳಗಿನ ಕೋಣೆಯಲ್ಲಿ ಹೊಂಚುಗಾರರನ್ನಿಟ್ಟು ಅವುಗಳಿಂದ ಅವನನ್ನು ಕಟ್ಟಿ - ಸಂಸೋನನೇ, ನಿನ್ನ ಮೇಲೆ ಫಿಲಿಷ್ಟಿಯರು ಬಂದರು ಎಂದು ಕೂಗಿದಳು. ಅವನು ಬೆಂಕಿತಗುಲಿದ ಸೆಣಬಿನ ದಾರವನ್ನೋ ಎಂಬಂತೆ ಆ ತಂತಿಗಳನ್ನು ಹರಿದುಬಿಟ್ಟನು; ಹೀಗೆ ಅವನ ಶಕ್ತಿಯ ರಹಸ್ಯವು ತಿಳಿಯದೆ ಹೋಯಿತು.",
			"10": "<sup>10</sup> ದೆಲೀಲಳು ತಿರಿಗಿ ಸಂಸೋನನಿಗೆ - ನೀನು ನನ್ನನ್ನು ವಂಚಿಸಿದಿ; ಸುಳ್ಳಾಡಿದಿ; ನಿನ್ನನ್ನು ಯಾವದರಿಂದ ಕಟ್ಟಬಹುದೆಂಬದನ್ನು ದಯವಿಟ್ಟು ನನಗೆ ತಿಳಿಸು ಎಂದಳು.",
			"11": "<sup>11</sup> ಅವನು ಅವಳಿಗೆ - ಯಾವದಕ್ಕೂ ಉಪಯೋಗಿಸದ ಹೊಸ ಹಗ್ಗಗಳಿಂದ ನನ್ನನ್ನು ಕಟ್ಟಿದರೆ ನಾನು ಬಲಹೀನನಾಗಿ ಬೇರೆ ಮನುಷ್ಯರಂತಾಗುವೆನು ಅಂದನು.",
			"12": "<sup>12</sup> ಅವಳು ಒಳಗಿನ ಕೋಣೆಯಲ್ಲಿ ಹೊಂಚುಗಾರರನ್ನಿರಿಸಿ ಹೊಸ ಹಗ್ಗಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಅವುಗಳಿಂದ ಅವನನ್ನು ಕಟ್ಟಿ - ಸಂಸೋನನೇ, ಫಿಲಿಷ್ಟಿಯರು ಬಂದಿದ್ದಾರೆಂದು ಕೂಗಲು ಅವನೆದ್ದು ತೋಳುಗಳಿಗೆ ಕಟ್ಟಿದ ಹಗ್ಗಗಳನ್ನು ದಾರವನ್ನೋ ಎಂಬಂತೆ ಹರಿದುಬಿಟ್ಟನು.",
			"13": "<sup>13</sup> ಆಗ ದೆಲೀಲಳು ಸಂಸೋನನಿಗೆ - ನೀನು ತಿರಿಗಿ ನನ್ನನ್ನು ವಂಚಿಸಿದಿ; ಸುಳ್ಳಾಡಿದಿ; ನಿನ್ನನ್ನು ಯಾವದರಿಂದ ಕಟ್ಟಬಹುದೆಂದು ತಿಳಿಸು ಅನ್ನಲು ಅವನು - ನನ್ನ ತಲೇಕೂದಲಿನ ಏಳು ಜಡೆಗಳನ್ನು ಮಗ್ಗದಲ್ಲಿ ನೇಯಿದರೆ ಸಾಕು ಎಂದು ಹೇಳಿದನು.",
			"14": "<sup>14</sup> ಅವಳು ಅವನ ತಲೇಕೂದಲಿನ ಏಳು ಜಡೆಗಳನ್ನು ಗೂಟಕ್ಕೆ ಭದ್ರಮಾಡಿ ಸಂಸೋನನೇ, ಫಿಲಿಷ್ಟಿಯರು ಬಂದಿದ್ದಾರೆ ಎಂದು ಕೂಗಿದಳು. ಅವನು ನಿದ್ರೆಯಿಂದ ಎಚ್ಚತ್ತು ಮಗ್ಗವನ್ನೂ ಮಗ್ಗದ ಗೂಟವನ್ನೂ ಕಿತ್ತುಕೊಂಡು ಹೋದನು.",
			"15": "<sup>15</sup> ಆಕೆಯು ತಿರಿಗಿ ಅವನಿಗೆ - ನಿನ್ನನ್ನು ಪ್ರೀತಿಸುತ್ತೇನೆಂದು ನೀನು ನನಗೆ ಹೇಗೆ ಹೇಳಬಹುದು? ನಿನ್ನ ಮನಸ್ಸು ನನ್ನ ಮೇಲೆ ಇಲ್ಲವಲ್ಲಾ; ಮೂರುಸಾರಿ ನನ್ನನ್ನು ವಂಚಿಸಿದಿ. ನಿನಗೆ ಇಂಥ ಮಹಾಶಕ್ತಿ ಯಾವದರಿಂದ ಬಂದಿತೆಂಬದನ್ನು ನನಗೆ ತಿಳಿಸಲಿಲ್ಲ ಅಂದಳು.",
			"16": "<sup>16</sup> ಇದಲ್ಲದೆ ಅವಳು ಅವನನ್ನು ದಿನ ದಿನವೂ ಮಾತಿನಿಂದ ಪೀಡಿಸಿದ್ದರಿಂದ ಅವನಿಗೆ ಸಾಯುವದು ಒಳ್ಳೇದನ್ನುವಷ್ಟು ಬೇಸರವಾಯಿತು.",
			"17": "<sup>17</sup> ಮತ್ತು ಅವನು - ಕ್ಷೌರಕತ್ತಿಯು ನನ್ನ ತಲೆಯ ಮೇಲೆ ಬಂದದ್ದಿಲ್ಲ; ನಾನು ಮಾತೃಗರ್ಭ ಬಿಟ್ಟಂದಿನಿಂದಲೇ ದೇವರಿಗೆ ಪ್ರತಿಷ್ಠಿತನಾದವನು. ನನ್ನ ತಲೆಯನ್ನು ಕ್ಷೌರಮಾಡುವದಾದರೆ ನನ್ನ ಶಕ್ತಿಯು ಹೋಗುವದು; ಮತ್ತು ನಾನು ಬಲಹೀನನಾಗಿ ಬೇರೆ ಮನುಷ್ಯನಂತಾಗುವೆನು ಎಂದು ತನ್ನ ಗುಟ್ಟನ್ನೆಲ್ಲಾ ಆಕೆಯ ಮುಂದೆ ಬಿಚ್ಚಿಹೇಳಿದನು.",
			"18": "<sup>18</sup> ಅವನು ಇದ್ದದ್ದನ್ನು ಇದ್ದ ಹಾಗೆಯೇ ಹೇಳಿದನೆಂದು ದೆಲೀಲಳು ತಿಳಿದು ಫಿಲಿಷ್ಟಿಯ ಪ್ರಭುಗಳ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿ ಅವರಿಗೆ - ಇದೊಂದು ಸಾರಿ ಬನ್ನಿರಿ; ಅವನು ನನಗೆ ತನ್ನ ಹೃದಯವನ್ನೆಲ್ಲಾ ತಿಳಿಸಿದ್ದಾನೆ ಎಂದು ಹೇಳಿಸಿ ಕರಿಸಿದಳು. ಅವರು ಹಣವನ್ನು ತೆಗೆದುಕೊಂಡು ಆಕೆಯ ಬಳಿಗೆ ಬಂದರು.",
			"19": "<sup>19</sup> ಆಕೆಯು ಸಂಸೋನನನ್ನು ತನ್ನ ತೊಡೆಯ ಮೇಲೆ ಮಲಗಿಸಿಕೊಂಡು ಅವನಿಗೆ ನಿದ್ರೆಹತ್ತಿದಾಗ ಒಬ್ಬ ಮನುಷ್ಯನಿಂದ ಅವನ ತಲೆಯ ಏಳು ಜಡೆಗಳನ್ನು ಬೋಳಿಸಿ ಅವನನ್ನು ದುರ್ಬಲಪಡಿಸಿದಳು. ಅವನ ಶಕ್ತಿಯು ಹೋಯಿತು,",
			"20": "<sup>20</sup> ಆಗ ಅವಳು - ಸಂಸೋನನೇ, ಫಿಲಿಷ್ಟಿಯರು ಬಂದಿದ್ದಾರೆಂದು ಕೂಗಲು ಅವನು ಎಚ್ಚತ್ತು - ಮುಂಚಿನಂತೆ ಈಗಲೂ ಕೊಸರಿಕೊಂಡು ಹೋಗುವೆನು ಅಂದುಕೊಂಡನು; ಆದರೆ ಯೆಹೋವನು ತನ್ನನ್ನು ಬಿಟ್ಟುಹೋಗಿದ್ದಾನೆಂಬದು ಅವನಿಗೆ ತಿಳಿಯಲಿಲ್ಲ.",
			"21": "<sup>21</sup> ಫಿಲಿಷ್ಟಿಯರು ಅವನನ್ನು ಹಿಡಿದು ಕಣ್ಣುಗಳನ್ನು ಕಿತ್ತು ಗಾಜಕ್ಕೆ ಒಯ್ದು ಕಬ್ಬಿಣದ ಬೇಡಿಗಳನ್ನು ಹಾಕಿ ಸೆರೆಮನೆಯಲ್ಲಿ ಬೀಸುವದಕ್ಕೆ ಹಚ್ಚಿದರು.",
			"22": "<sup>22</sup> ಅಷ್ಟರಲ್ಲಿ ಅವನ ತಲೆಗೂದಲುಗಳು ಬೆಳೆಯ ಹತ್ತಿದವು.",
			"23": "<sup>23</sup> ಶತ್ರುವಾದ ಸಂಸೋನನನ್ನು ತಮ್ಮ ಕೈಗೆ ಒಪ್ಪಿಸಿದ ತಮ್ಮ ದೇವರಾದ ದಾಗೋನನಿಗೆ ಮಹಾಯಜ್ಞವನ್ನು ಸಮರ್ಪಿಸಿ ಸಂತೋಷಪಡಬೇಕೆಂದು ಫಿಲಿಷ್ಟಿಯ ಪ್ರಭುಗಳು ಕೂಡಿಬಂದರು.",
			"24": "<sup>24</sup> ಜನರು ಸಂಸೋನನನ್ನು ನೋಡಿ - ನಮ್ಮ ದೇಶವನ್ನು ಹಾಳುಮಾಡಿ ನಮ್ಮಲ್ಲಿ ಅನೇಕರನ್ನು ಕೊಂದು ಹಾಕಿದ ಈ ಶತ್ರುವನ್ನು ನಮ್ಮ ದೇವರು ನಮ್ಮ ಕೈಗೆ ಒಪ್ಪಿಸಿ ಕೊಟ್ಟಿದ್ದಾನೆ ಎಂದು ಗಾನಮಾಡಿದರು.",
			"25": "<sup>25</sup> ಅವರು ಆನಂದಲಹರಿಯಲ್ಲಿದ್ದಾಗ - ನಮ್ಮ ವಿನೋದಕ್ಕೋಸ್ಕರ ಸಂಸೋನನನ್ನು ಕರಕೊಂಡು ಬನ್ನಿರಿ ಅಂದರು; ಅದರಂತೆಯೇ ಸಂಸೋನನನ್ನು ಸೆರೆಮನೆಯಿಂದ ಕರಕೊಂಡು ಬಂದರು. ಅವನು ಎರಡು ಸ್ತಂಭಗಳ ನಡುವೆ ನಿಂತು ವಿನೋದಮಾಡಬೇಕಾಯಿತು.",
			"26": "<sup>26</sup> ಆಗ ಸಂಸೋನನು ತನ್ನನ್ನು ಕೈಹಿಡಿದು ನಡಿಸುವ ಹುಡುಗನಿಗೆ - ನನ್ನ ಕೈಗಳನ್ನು ಬಿಡು; ನಾನು ಕೈಯಾಡಿಸಿ ಮನೆಗೆ ಆಧಾರವಾಗಿರುವ ಸ್ತಂಭಗಳನ್ನು ಹುಡುಕಿ ಅವುಗಳಿಗೆ ಸ್ವಲ್ಪ ಒರಗಿಕೊಳ್ಳುತ್ತೇನೆ ಅಂದನು.",
			"27": "<sup>27</sup> ಆ ಮನೆಯು ಸ್ತ್ರೀಪುರುಷರಿಂದ ಕಿಕ್ಕಿರಿಯುತ್ತಿತ್ತು. ಫಿಲಿಷ್ಟಿಯರ ಅಧಿಪತಿಗಳೆಲ್ಲರೂ ಅಲ್ಲಿದ್ದರು; ಸಂಸೋನನ ವಿನೋದವನ್ನು ನೋಡುವದಕ್ಕಾಗಿ ಬಂದವರಲ್ಲಿ ಸುಮಾರು ಮೂರು ಸಾವಿರ ಮಂದಿ ಸ್ತ್ರೀಪುರುಷರು ಮಾಳಿಗೆಯ ಮೇಲಿದ್ದರು.",
			"28": "<sup>28</sup> ಆಗ ಸಂಸೋನನು ಯೆಹೋವನಿಗೆ - ಕರ್ತನೇ, ಯೆಹೋವನೇ, ಫಿಲಿಷ್ಟಿಯರು ಕಿತ್ತುಹಾಕಿದ ನನ್ನ ಎರಡೂ ಕಣ್ಣುಗಳಲ್ಲಿ ಒಂದಕ್ಕೋಸ್ಕರವಾದರೂ ನಾನು ಅವರಿಗೆ ಮುಯ್ಯಿತೀರಿಸುವ ಹಾಗೆ ನನ್ನನ್ನು ಈ ಸಾರಿ ಬಲಪಡಿಸು ಎಂದು ಮೊರೆಯಿಟ್ಟು",
			"29": "<sup>29</sup> ಮನೆಯ ಆಧಾರಸ್ತಂಭಗಳಲ್ಲಿ ಒಂದನ್ನು ಬಲಗೈಯಿಂದಲೂ ಇನ್ನೊಂದನ್ನು ಎಡಗೈಯಿಂದಲೂ ಹಿಡಿದು",
			"30": "<sup>30</sup> ಅವುಗಳನ್ನು ಒತ್ತಿ ನಾನೂ ಫಿಲಿಷ್ಟಿಯರ ಸಂಗಡ ಸಾಯುವೆನು ಎಂದು ಹೇಳಿ ಬಲವಾಗಿ ಬಾಗಿಕೊಳ್ಳಲು ಮನೆಯು ಅಧಿಪತಿಗಳ ಮೇಲೆಯೂ ಅಲ್ಲಿ ಬಂದಿದ್ದ ಎಲ್ಲಾ ಜನರ ಮೇಲೆಯೂ ಬಿದ್ದಿತು. ಅವನು ಜೀವದಿಂದಿದ್ದಾಗ ಕೊಂದುಹಾಕಿದವರಿಗಿಂತ ಸಾಯುವಾಗ ಕೊಂದವರ ಸಂಖ್ಯೆಯೇ ಹೆಚ್ಚಿತ್ತು.",
			"31": "<sup>31</sup> ಅವನ ಬಂಧುಗಳೂ ತಂದೆಯ ಮನೆಯವರೆಲ್ಲರೂ ಬಂದು ಅವನ ಶವವನ್ನು ಎತ್ತಿ ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ಚೊರ್ಗಕ್ಕೂ ಎಷ್ಟಾವೋಲಿಗೂ ಮಧ್ಯದಲ್ಲಿದ್ದ ಅವನ ತಂದೆಯಾದ ಮಾನೋಹನ ಸಮಾಧಿಯಲ್ಲಿ ಹೂಣಿಟ್ಟರು. ಅವನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಇಪ್ಪತ್ತು ವರುಷ ಪಾಲಿಸಿದನು."
		},
		"17": {
			"1": "<sup>1</sup> ಎಫ್ರಾಯೀಮ್ ಬೆಟ್ಟದ ಸೀಮೆಯಲ್ಲಿ ಮೀಕನೆಂಬೊಬ್ಬ ಮನುಷ್ಯನಿದ್ದನು.",
			"2": "<sup>2</sup> ಅವನು ಒಂದಾನೊಂದು ದಿವಸ ತನ್ನ ತಾಯಿಗೆ - ಅಮ್ಮಾ, ಕಳವಾಗಿದ್ದ ನಿನ್ನ ಸಾವಿರದ ನೂರು ರೂಪಾಯಿಗಳಿಗೋಸ್ಕರ ನೀನು ನನಗೆ ಕೇಳಿಸುವಂತೆ ಶಪಿಸಿದಿಯಲ್ಲಾ; ಇಗೋ, ಆ ರೂಪಾಯಿಗಳು ನನ್ನ ಹತ್ತಿರ ಇವೆ; ನಾನು ತೆಗೆದುಕೊಂಡಿದ್ದೆನು ಎಂದು ಹೇಳಲು ಆಕೆಯು - ನನ್ನ ಮಗನೇ, ಯೆಹೋವನು ನಿನ್ನನ್ನು ಆಶೀರ್ವದಿಸಲಿ ಅಂದಳು.",
			"3": "<sup>3</sup> ಅವನು ಆ ಸಾವಿರದ ನೂರು ರೂಪಾಯಿಗಳನ್ನು ತನ್ನ ತಾಯಿಗೆ ಹಿಂದಕ್ಕೆ ಕೊಡಲು ಆಕೆಯು - ನನ್ನ ಮಗನಿಗೆ ಹಿತವಾಗಲೆಂದು ಈ ಹಣವನ್ನು ಯೆಹೋವನಿಗೆ ಹರಕೆ ಮಾಡಿದ್ದೇನೆ; ಇದರಿಂದ ಒಂದು ಎರಕದ ವಿಗ್ರಹವನ್ನು ಮಾಡಿಸಿ ನಿನ್ನ ವಶಕ್ಕೆ ಕೊಡುವೆನು ಎಂದು ಹೇಳಿ",
			"4": "<sup>4</sup> ಮಗನು ಹಿಂದಕ್ಕೆ ಕೊಟ್ಟ ರೂಪಾಯಿಗಳಲ್ಲಿ ಇನ್ನೂರು ರೂಪಾಯಿಗಳನ್ನು ತೆಗೆದು ಅಕ್ಕಸಾಲಿಗನಿಗೆ ಕೊಟ್ಟಳು. ಅವನು ಅವುಗಳಿಂದ ಕೆತ್ತನೆಯ ಮತ್ತು ಎರಕದ ವಿಗ್ರಹಗಳನ್ನು ಮಾಡಿಕೊಟ್ಟನು. ಮೀಕನು ಅದನ್ನು ತನ್ನ ಮನೆಯಲ್ಲಿಟ್ಟುಕೊಂಡನು.",
			"5": "<sup>5</sup> ಈ ಮೀಕನು ಏಫೋದನ್ನೂ ವಿಗ್ರಹಗಳನ್ನೂ ಮಾಡಿಸಿ ಅವುಗಳನ್ನು ತಾನು ಕಟ್ಟಿಸಿದ ದೇವಸ್ಥಾನದಲ್ಲಿಟ್ಟು ತನ್ನ ಕುಮಾರರಲ್ಲೊಬ್ಬನನ್ನು ಅರ್ಚಕ ಸೇವೆಗೆ ಪ್ರತಿಷ್ಠಿಸಿದನು.",
			"6": "<sup>6</sup> ಆ ಕಾಲದಲ್ಲಿ ಇಸ್ರಾಯೇಲ್ಯರೊಳಗೆ ಅರಸನಿರಲಿಲ್ಲ. ಪ್ರತಿಯೊಬ್ಬನೂ ಮನಸ್ಸಿಗೆ ಬಂದ ಹಾಗೆಯೇ ನಡೆಯುತ್ತಿದ್ದನು.",
			"7": "<sup>7</sup> ಯೆಹೂದದ ಬೇತ್ಲೆಹೇಮಿನವನೂ ಯೆಹೂದ ಕುಲದವನೂ ಆಗಿದ್ದ ಒಬ್ಬ ಯೌವನಸ್ಥನಾದ ಲೇವಿಯು",
			"8": "<sup>8</sup> ಉಪಜೀವನಕ್ಕೆ ಅನುಕೂಲವಾಗುವಲ್ಲಿ ವಾಸಿಸಬೇಕೆಂದು ಸ್ವಂತ ಊರನ್ನು ಬಿಟ್ಟು ಪ್ರಯಾಣ ಮಾಡುತ್ತಾ ಎಫ್ರಾಯೀಮ್ ಬೆಟ್ಟದ ಸೀಮೆಯಲ್ಲಿದ್ದ ಮೀಕನ ಮನೆಗೆ ಬಂದನು.",
			"9": "<sup>9</sup> ನೀನು ಎಲ್ಲಿಂದ ಬಂದಿ ಎಂದು ಮೀಕನು ಅವನನ್ನು ಕೇಳಲು ಅವನು - ನಾನು ಯೆಹೂದದ ಬೇತ್ಲೆಹೇಮಿನವನಾದ ಲೇವಿಯನು; ಉಪಜೀವನಕ್ಕೆ ಅನುಕೂಲವಾಗುವಲ್ಲಿ ವಾಸಿಸಬೇಕೆಂದು ಹೊರಟಿದ್ದೇನೆ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"10": "<sup>10</sup> ಮೀಕನು ಅವನಿಗೆ - ನೀನು ನಮ್ಮಲ್ಲಿರು; ನಮಗೆ ತಂದೆಯೂ ಯಾಜಕನೂ ಆಗು. ನಿನಗೆ ವರುಷಕ್ಕೆ ಹತ್ತು ರೂಪಾಯಿಗಳನ್ನೂ ಒಂದು ದುಸ್ತು ಬಟ್ಟೆಯನ್ನೂ ಆಹಾರವನ್ನೂ ಕೊಡುತ್ತೇನೆ ಅನ್ನಲು",
			"11": "<sup>11</sup> ಆ ಲೇವಿಯನು ಒಪ್ಪಿಕೊಂಡು ಅಲ್ಲೇ ವಾಸಮಾಡಿದನು; ಹೀಗೆ ಆ ಯೌವನಸ್ಥನು ಅವನ ಮಕ್ಕಳಲ್ಲೊಬ್ಬನಂತಾದನು.",
			"12": "<sup>12</sup> ಮೀಕನು ಯೌವನಸ್ಥನಾದ ಆ ಲೇವಿಯನನ್ನು ಯಾಜಕಸೇವೆಗೋಸ್ಕರ ಪ್ರತಿಷ್ಠಿಸಿ ತನ್ನ ಮನೆಯಲ್ಲಿಟ್ಟುಕೊಂಡು -",
			"13": "<sup>13</sup> ಒಬ್ಬ ಲೇವಿಯು ತನ್ನ ಮನೆಯಲ್ಲಿ ಯಾಜಕನಾಗಿರುವದರಿಂದ ಯೆಹೋವನು ನನ್ನನ್ನು ಆಶೀರ್ವದಿಸುವನೆಂದು ಬಲ್ಲೆನು ಅಂದುಕೊಂಡನು."
		},
		"18": {
			"1": "<sup>1</sup> ಆ ಕಾಲದಲ್ಲಿ ಇಸ್ರಾಯೇಲ್ಯರೊಳಗೆ ಅರಸನಿರಲಿಲ್ಲ. ದಾನ್‍ಕುಲದವರಿಗೆ ಆವರೆಗೂ ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಸ್ವಾಸ್ತ್ಯವು ಸಿಕ್ಕದ್ದರಿಂದ ಅವರು ತಮಗೋಸ್ಕರ ವಾಸಸ್ಥಳವನ್ನು ಹುಡುಕುತ್ತಿದ್ದರು.",
			"2": "<sup>2</sup> ಮತ್ತು ಅವರು ಚೊರ್ಗಾ, ಎಷ್ಟಾವೋಲ್ ಎಂಬ ಊರುಗಳಿಂದ ತಮ್ಮ ಕುಲದಲ್ಲಿ ಐದು ಮಂದಿ ಪರಾಕ್ರಮ ಶಾಲಿಗಳಾದ ಗೂಢಚಾರರನ್ನು ಆರಿಸಿ ಅವರಿಗೆ - ನೀವು ಹೋಗಿ [ಲಯಿಷ್] ದೇಶವನ್ನು ಸಂಚರಿಸಿ ನೋಡಿರಿ ಎಂದು ಆಜ್ಞಾಪಿಸಿ ಕಳುಹಿಸಿದರು. ಇವರು ಪ್ರಯಾಣಮಾಡುತ್ತಾ ಎಫ್ರಾಯೀಮ್ ಬೆಟ್ಟದ ಸೀಮೆಯಲ್ಲಿದ್ದ ಮೀಕನ ಮನೆಗೆ ಬಂದು ಇಳುಕೊಂಡರು.",
			"3": "<sup>3</sup> ಇವರು ಮೀಕನ ಮನೆಯ ಹತ್ತಿರದಲ್ಲಿ ಆ ಯೌವನಸ್ಥನಾದ ಲೇವಿಯನು ಮಾತಾಡುವದನ್ನು ಕೇಳಿ ಗುರುತುಹಿಡಿದು ಅವನ ಬಳಿಗೆ ಹೋಗಿ - ನಿನ್ನನ್ನು ಇಲ್ಲಿಗೆ ಕರತಂದವರಾರು? ಇಲ್ಲಿ ಏನು ಕೆಲಸ ಮಾಡುತ್ತೀ? ಏನು ಸಿಕ್ಕುತ್ತದೆ ಎಂದು ಅವನನ್ನು ಕೇಳಲು ಅವನು,",
			"4": "<sup>4</sup> ಮೀಕನು ತನಗೆ ಇಂಥಿಂಥವುಗಳನ್ನು ಸಂಬಳವಾಗಿ ಕೊಡುತ್ತಾನೆಂದೂ ತಾನು ಅವನ ಯಾಜಕನಾಗಿದ್ದೇನೆಂದೂ ಹೇಳಿದನು.",
			"5": "<sup>5</sup> ಆಗ ಅವರು ಅವನಿಗೆ - ದಯವಿಟ್ಟು ನಮ್ಮ ಪ್ರಯಾಣವು ಸಫಲವಾಗುವದೋ ಇಲ್ಲವೋ ಎಂಬದನ್ನು ದೇವರ ಸನ್ನಿಧಿಯಲ್ಲಿ ವಿಚಾರಿಸು ಎಂದು ಬೇಡಿಕೊಳ್ಳಲು",
			"6": "<sup>6</sup> ಅವನು - ಸಮಾಧಾನದಿಂದ ಹೋಗಿರಿ; ಯೆಹೋವನು ನಿಮ್ಮ ಪ್ರಯಾಣವನ್ನು ಕಟಾಕ್ಷಿಸುವನು ಅಂದನು.",
			"7": "<sup>7</sup> ಆ ಐದು ಮಂದಿ ಹೊರಟು ಲಯಿಷಿಗೆ ಬಂದು - ಅಲ್ಲಿನ ಜನರು ನಿರ್ಭೀತರಾಗಿ ಚೀದೋನ್ಯರಂತೆ ಸುಖ ಸಮಾಧಾನಗಳಿಂದ ಜೀವಿಸುತ್ತಾರೆ; ಅವರನ್ನು ತಗ್ಗಿಸುವ ಅಧಿಕಾರಿಗಳು ದೇಶದಲ್ಲಿಲ್ಲ; ಅವರು ಚೀದೋನ್ಯರಿಗೆ ದೂರವಾಗಿದ್ದು ಯಾರೊಡನೆಯೂ ಬಳಕೆಯಿಲ್ಲದವರು ಎಂಬದನ್ನು ತಿಳಿದುಕೊಂಡು",
			"8": "<sup>8</sup> ಹಿಂದಿರುಗಿ ಚೊರ್ಗಾ, ಎಷ್ಟಾವೋಲ್ ಎಂಬ ಊರುಗಳಲ್ಲಿರುವ ತಮ್ಮ ಬಂಧುಗಳ ಬಳಿಗೆ ಬಂದರು. ಅವರ ಬಂಧುಗಳು - ನೀವು ಯಾವ ವರ್ತಮಾನ ತಂದಿದ್ದೀರಿ ಎಂದು ಅವರನ್ನು ಕೇಳಲು",
			"9": "<sup>9</sup> ಅವರು - ಏಳಿರಿ, ಅವರಿಗೆ ವಿರೋಧವಾಗಿ ಯುದ್ಧಕ್ಕೆ ಹೋಗೋಣ. ನಾವು ಆ ದೇಶವನ್ನು ನೋಡಿದೆವು; ಅದು ಬಹು ಉತ್ತಮದೇಶ. ನೀವು ಸುಮ್ಮನೆ ಕೂತುಕೊಳ್ಳುವದೇಕೆ? ತಡಮಾಡದೆ ಹೊರಡಿರಿ; ಆ ದೇಶವನ್ನು ಸ್ವತಂತ್ರಿಸಿಕೊಳ್ಳೋಣ.",
			"10": "<sup>10</sup> ನೀವು ಅಲ್ಲಿಗೆ ಹೋಗುವದಾದರೆ ಅಲ್ಲಿನ ಜನರು ನಿರ್ಭಯದಿಂದ ಜೀವಿಸುವವರೆಂದೂ ಅವರ ದೇಶವು ಬಹು ವಿಸ್ತಾರವಾಗಿದೆಯೆಂದೂ ತಿಳಿದುಕೊಳ್ಳುವಿರಿ. ದೇವರು ಅದನ್ನು ನಿಮ್ಮ ಕೈಗೆ ಒಪ್ಪಿಸಿದ್ದಾನೆ; ಅದರಲ್ಲಿ ದೊರಕದಿರುವ ಪದಾರ್ಥಗಳು ಲೋಕದಲ್ಲೇ ಇಲ್ಲ ಅಂದರು.",
			"11": "<sup>11</sup> ಆಗ ಚೊರ್ಗಾ ಎಷ್ಟಾವೋಲ್ ಊರುಗಳಿಂದ ಆರುನೂರು ಮಂದಿ ದಾನ್‍ಕುಲದವರು ಯುದ್ಧ ಸನ್ನದ್ಧರಾಗಿ ಹೊರಟು",
			"12": "<sup>12</sup> ಗಟ್ಟಾಹತ್ತಿ ಯೆಹೂದದ ಕಿರ್ಯತ್ಯಾರೀಮಿನ ಬಳಿಯಲ್ಲಿ ಇಳುಕೊಂಡರು. ಆದದರಿಂದ ಆ ಸ್ಥಳಕ್ಕೆ ಇಂದಿನವರೆಗೂ ದಾನ್ಯರ ಪಾಳೆಯವೆಂಬ ಹೆಸರಿದೆ; ಅದು ಕಿರ್ಯತ್ಯಾರೀಮಿನ ಪಶ್ಚಿಮದಲ್ಲಿರುತ್ತದೆ.",
			"13": "<sup>13</sup> ಅವರು ಅಲ್ಲಿಂದ ಮುಂದೆ ಪ್ರಯಾಣಮಾಡಿ ಎಫ್ರಾಯೀಮ್ ಪರ್ವತಪ್ರದೇಶದಲ್ಲಿದ್ದ ಮೀಕನ ಮನೆಯ ಹತ್ತಿರ ಬಂದರು.",
			"14": "<sup>14</sup> ಆಗ ಲಯಿಷ್ ದೇಶವನ್ನು ಸಂಚರಿಸಿ ನೋಡುವದಕ್ಕೆ ಹೋಗಿದ್ದ ಐದು ಮಂದಿ ತಮ್ಮ ಬಂಧುಗಳಿಗೆ - ಈ ಗ್ರಾಮದಲ್ಲಿ ಒಂದು ಏಫೋದೂ ದೇವತಾಪ್ರತಿಮೆಗಳೂ ಕೆತ್ತನೆಯ ಮತ್ತು ಎರಕದ ವಿಗ್ರಹಗಳೂ ಇರುತ್ತವೆಂಬದು ನಿಮಗೆ ಗೊತ್ತುಂಟೋ?",
			"15": "<sup>15</sup> ಈ ವಿಷಯದಲ್ಲಿ ಏನು ಮಾಡಬೇಕೆಂಬದನ್ನು ಆಲೋಚಿಸಿರಿ ಎಂದು ಹೇಳಿ ತಾವು ಅಲ್ಲಿಂದ ಮೀಕನ ಮನೆಯಲ್ಲಿದ್ದ ಆ ಯೌವನಸ್ಥನಾದ ಲೇವಿಯನ ಬಳಿಗೆ ಬಂದು ಅವನ ಕ್ಷೇಮಸಮಾಚಾರವನ್ನು ವಿಚಾರಿಸಹತ್ತಿದರು.",
			"16": "<sup>16</sup> ಇಷ್ಟರಲ್ಲಿ ಯುದ್ಧಸನ್ನದ್ಧರಾದ ಆರುನೂರು ಮಂದಿ ದಾನ್ಯರು ಬಂದು ಬಾಗಲಲ್ಲಿ ನಿಂತರು.",
			"17": "<sup>17</sup> ಯಾಜಕನೂ ಬಂದು ಇವರೊಡನೆ ಬಾಗಲಲ್ಲಿ ನಿಂತಿದ್ದಾಗ ಆ ಐದು ಮಂದಿ ಮನೆಯನ್ನು ಹೊಕ್ಕು ಕೆತ್ತನೆಯ ಮತ್ತು ಎರಕದ ವಿಗ್ರಹಗಳನ್ನೂ ಏಫೋದನ್ನೂ ದೇವತಾ ಪ್ರತಿಮೆಗಳನ್ನೂ ತೆಗೆದುಕೊಂಡರು.",
			"18": "<sup>18</sup> ಅವರು ಮೀಕನ ಮನೆಯನ್ನು ಹೊಕ್ಕು ಅವುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಬರುವಾಗ ಯಾಜಕನು ಕಂಡು ಅವರಿಗೆ - ನೀವು ಮಾಡಿದ್ದೇನು ಅಂದನು.",
			"19": "<sup>19</sup> ಅವರು ಅವನಿಗೆ - ಸುಮ್ಮನಿರು; ಬಾಯಿ ಮುಚ್ಚು; ನಮ್ಮ ಜೊತೆಯಲ್ಲಿ ಬಂದು ನಮಗೆ ತಂದೆಯೂ ಯಾಜಕನೂ ಆಗಿರು. ಒಂದು ಮನೆಗೆ ಯಾಜಕನಾಗಿರುವದು ಉತ್ತಮವೋ ಅಥವಾ ಇಸ್ರಾಯೇಲ್ಯರ ಒಂದು ಕುಲಕ್ಕೂ ಗೋತ್ರಕ್ಕೂ ಯಾಜಕನಾಗಿರುವದು ಉತ್ತಮವೋ ಅಂದರು.",
			"20": "<sup>20</sup> ಯಾಜಕನು ಈ ಮಾತನ್ನು ಕೇಳಿ ಸಂತೋಷಪಟ್ಟು ಏಫೋದನ್ನೂ ದೇವತಾಪ್ರತಿಮೆಗಳನ್ನೂ ಕೆತ್ತನೆಯ ವಿಗ್ರಹವನ್ನೂ ತೆಗೆದುಕೊಂಡು ಅವರ ಬಳಿಗೆ ಬಂದನು.",
			"21": "<sup>21</sup> ತರುವಾಯ ಅವರು ತಮ್ಮ ಹೆಂಡರು ಮಕ್ಕಳನ್ನೂ ಕುರಿದನ ಮೊದಲಾದ ಸೊತ್ತನ್ನೂ ಮುಂದಾಗಿ ಕಳುಹಿಸಿ ತಾವು ಹಿಂದಿನಿಂದ ಹೊರಟರು.",
			"22": "<sup>22</sup> ಅವರು ಮೀಕನ ಮನೆಗೆ ದೂರವಾದ ಮೇಲೆ ಮೀಕನೂ ಅವನ ನೆರೆಹೊರೆಯವರೆಲ್ಲರೂ ದಾನ್ಯರನ್ನು ಹಿಂದಟ್ಟಿ",
			"23": "<sup>23</sup> ಅವರ ಸಮೀಪಕ್ಕೆ ಹೋಗಿ ಅವರನ್ನು ಕೂಗಿದರು. ದಾನ್ಯರು ಹಿಂದಿರುಗಿ ನೋಡಿ ಮೀಕನಿಗೆ - ನಿನಗೇನಾಯಿತು? ಗುಂಪನ್ನು ಕೂಡಿಸಿಕೊಂಡು ಬಂದದ್ದೇಕೆ ಎಂದು ಕೇಳಲು",
			"24": "<sup>24</sup> ಅವನು - ನೀವು ಯಾಜಕನನ್ನೂ ನಾನು ಮಾಡಿಸಿಕೊಂಡ ದೇವರುಗಳನ್ನೂ ಅಪಹರಿಸಿಕೊಂಡಿದ್ದೀರಿ ನನಗೆ ಇನ್ನೇನಿದೆ; ಹೀಗಿರಲಾಗಿ ನಿನಗೇನಾಯಿತೆಂದು ನೀವು ನನ್ನನ್ನು ಕೇಳುವದು ಹೇಗೆ ಅಂದನು.",
			"25": "<sup>25</sup> ದಾನ್ಯರು ಅವನಿಗೆ - ನಮ್ಮಲ್ಲಿ ಕೆಲವರು ಸಿಟ್ಟಿನವರಿದ್ದಾರೆ; ಅವರು ನಿಮ್ಮ ಮೇಲೆ ಬಿದ್ದರೆ ನೀನೂ ನಿನ್ನ ಮನೆಯವರೂ ಪ್ರಾಣನಷ್ಟಪಟ್ಟೀರಿ; ಆದದರಿಂದ ಬಾಯಿಮುಚ್ಚಿಕೊಂಡು ಹೋಗು ಎಂದು ಹೇಳಿ ಮುಂದೆ ನಡೆದರು.",
			"26": "<sup>26</sup> ಅವರು ತನಗಿಂತ ಬಲಿಷ್ಠರೆಂದು ಮೀಕನು ತಿಳಿದು ಹಿಂದಿರುಗಿ ಮನೆಗೆ ಹೋದನು.",
			"27": "<sup>27</sup> ದಾನ್ಯರು ಮೀಕನು ಮಾಡಿಸಿದ್ದ ವಿಗ್ರಹಗಳನ್ನೂ ಅವನ ಯಾಜಕನನ್ನೂ ಅಪಹರಿಸಿಕೊಂಡು ಲಯಿಷಿಗೆ ಹೋಗಿ ಸುಖದಿಂದಲೂ ನಿರ್ಭಯದಿಂದಲೂ ವಾಸವಾಗಿದ್ದ ಅಲ್ಲಿನ ಜನರ ಮೇಲೆ ಬಿದ್ದು ಅವರನ್ನು ಕತ್ತಿಯಿಂದ ಸಂಹರಿಸಿ ಅವರ ಪಟ್ಟಣವನ್ನು ಸುಟ್ಟುಬಿಟ್ಟರು.",
			"28": "<sup>28</sup> ಆ ಪಟ್ಟಣವು ಚೀದೋನಿಗೆ ದೂರವಾಗಿದ್ದದ್ದರಿಂದಲೂ ಸಮೀಪದಲ್ಲಿ ಪರಿಚಯಸ್ಥರಾರೂ ಇಲ್ಲದ್ದರಿಂದಲೂ ಅವರಿಗೆ ಸಹಾಯದೊರಕಲಿಲ್ಲ. ಬೇತ್‍ರೆಹೋಬಿನ ತಗ್ಗಿನಲ್ಲಿದ್ದ ಈ ಪಟ್ಟಣವನ್ನು ದಾನ್ಯರು ತಿರಿಗಿ ಕಟ್ಟಿಸಿ ಅದರಲ್ಲಿ ವಾಸಿಸಿದರು.",
			"29": "<sup>29</sup> ಮುಂಚೆ ಲಯಿಷ್ ಎಂಬ ಹೆಸರಿದ್ದ ಆ ಪಟ್ಟಣಕ್ಕೆ ಈಗ ದಾನ್ ಎಂದು ಹೆಸರಿಟ್ಟರು. ದಾನ್ ಎಂಬದು ಇವರ ಮೂಲಪುರುಷನೂ ಇಸ್ರಾಯೇಲನ ಮಗನೂ ಆದವನ ಹೆಸರು.",
			"30": "<sup>30</sup> ಇದಲ್ಲದೆ ದಾನ್ಯರು ಆ ವಿಗ್ರಹವನ್ನು ತಮ್ಮ ಪಟ್ಟಣದಲ್ಲಿಟ್ಟರು. ಇಸ್ರಾಯೇಲ್ಯರು ಸೆರೆಗೆ ಹೋಗುವವರೆಗೆ ಮೋಶೆಯ ಮೊಮ್ಮಗನೂ ಗೇರ್ಷೋಮನ ಮಗನೂ ಆದ ಯೆಹೋನಾತಾನನೂ ಅವನ ವಂಶದವರೂ ದಾನ್ಯರಿಗೆ ಯಾಜಕರಾಗಿದ್ದರು.",
			"31": "<sup>31</sup> ಶೀಲೋವಿನಲ್ಲಿ ದೇವಸ್ಥಾನವಿದ್ದ ಕಾಲದಲ್ಲೆಲ್ಲಾ ದಾನ್ಯರಲ್ಲಿ ಮೀಕನು ಮಾಡಿಸಿದ ವಿಗ್ರಹವಿತ್ತು."
		},
		"19": {
			"1": "<sup>1</sup> ಆ ಕಾಲದಲ್ಲಿ ಇಸ್ರಾಯೇಲ್ಯರೊಳಗೆ ಅರಸನಿರಲಿಲ್ಲ. ಎಫ್ರಾಯೀಮ್ ಪರ್ವತಪ್ರದೇಶದ ಒಂದು ಮೂಲೆಯಲ್ಲಿ ಒಬ್ಬಾನೊಬ್ಬ ಲೇವಿಯನು ಪ್ರವಾಸಿಯಾಗಿದ್ದನು. ಯೆಹೂದದ ಬೇತ್ಲೆಹೇಮಿನವಳಾದ ಒಬ್ಬ ಸ್ತ್ರೀಯು ಅವನಿಗೆ ಉಪಪತ್ನಿಯಾಗಿದ್ದಳು.",
			"2": "<sup>2</sup> ಈಕೆಯು ಇವನನ್ನು ಬಿಟ್ಟು ಜಾರತ್ವಮಾಡಿ ಯೆಹೂದದ ಬೇತ್ಲೆಹೇಮಿನಲ್ಲಿದ್ದ ತನ್ನ ತೌರಮನೆಗೆ ಹೋಗಿ ಅಲ್ಲಿ ನಾಲ್ಕು ತಿಂಗಳು ಇದ್ದಳು.",
			"3": "<sup>3</sup> ಅನಂತರ ಆಕೆಯ ಗಂಡನಾದ ಆ ಲೇವಿಯನು ಆಕೆಯನ್ನು ಪ್ರೀತಿಯಿಂದ ಮಾತಾಡಿಸಿ ತಿರಿಗಿ ಮನೆಗೆ ಕರತರಬೇಕೆಂದು ಒಬ್ಬ ಸೇವಕನನ್ನೂ ಎರಡು ಕತ್ತೆಗಳನ್ನೂ ತೆಗೆದುಕೊಂಡು ಅಲ್ಲಿಗೆ ಹೋದನು. ಆಕೆಯು ಅವನನ್ನು ತನ್ನ ತೌರ ಮನೆಯಲ್ಲಿ ಸೇರಿಸಿಕೊಂಡಳು; ಆಕೆಯ ತಂದೆಯೂ ಅವನನ್ನು ನೋಡಿ ಸಂತೋಷಪಟ್ಟನು.",
			"4": "<sup>4</sup> ಆ ಸ್ತ್ರೀಯ ತಂದೆಯಾದ ಅವನ ಮಾವನು ಅವನನ್ನು ಮೂರು ದಿವಸ ಅಲ್ಲೇ ನಿಲ್ಲಿಸಿಕೊಂಡನು; ಅವನೂ ಅವನ ಸೇವಕನೂ ಅನ್ನಪಾನಗಳನ್ನು ತೆಗೆದುಕೊಳ್ಳುತ್ತಾ ಅಲ್ಲಿದ್ದರು.",
			"5": "<sup>5</sup> ಅವರು ನಾಲ್ಕನೆಯ ದಿನ ಬೆಳಿಗ್ಗೆ ಎದ್ದು ಹೋಗುವದಕ್ಕೆ ಸಿದ್ಧರಾಗಲು ಆ ಸ್ತ್ರೀಯ ತಂದೆಯು ತನ್ನ ಅಳಿಯನಿಗೆ - ಮೊದಲು ಸ್ವಲ್ಪ ಊಟಮಾಡಿ ಬಲಹೊಂದು; ಆಮೇಲೆ ಹೋಗಬಹುದು ಎಂದು ಹೇಳಲು",
			"6": "<sup>6</sup> ಅವರಿಬ್ಬರೂ ಕೂತು ಅನ್ನಪಾನಗಳನ್ನು ತೆಗೆದುಕೊಂಡರು. ಊಟವಾದ ಮೇಲೆ ಆ ಸ್ತ್ರೀಯ ತಂದೆಯು ತಿರಿಗಿ ಅವನಿಗೆ - ದಯವಿಟ್ಟು ಈ ಹೊತ್ತಿನ ರಾತ್ರಿ ಇಲ್ಲೇ ಇದ್ದು ಸಂತೋಷಪಡು ಎಂದು ಹೇಳಿದರೂ ಅವನು ಹೊರಡುವದಕ್ಕಿದ್ದನು;",
			"7": "<sup>7</sup> ಆದರೆ ಅವನ ಮಾವನು ಬಹಳ ಬಲಾತ್ಕಾರಮಾಡಿದ್ದರಿಂದ ಆ ರಾತ್ರಿ ಅಲ್ಲಿ ಇಳುಕೊಂಡನು.",
			"8": "<sup>8</sup> ಅವರು ಐದನೆಯ ದಿನ ಬೆಳಿಗ್ಗೆ ಎದ್ದು ಹೋಗುವದಕ್ಕೆ ಸಿದ್ಧರಾಗಲು ಆ ಸ್ತ್ರೀಯ ತಂದೆಯು - ದಯವಿಟ್ಟು ಇಳಿಹೊತ್ತಿನವರೆಗೆ ಇಲ್ಲೇ ಇದ್ದು ಬಲಹೊಂದು ಎಂದು ಅವನನ್ನು ಬೇಡಿಕೊಳ್ಳಲು ತಿರಿಗಿ ಊಟಕ್ಕೆ ನಿಂತನು.",
			"9": "<sup>9</sup> ಅನಂತರ ಅವನೂ ಅವನ ಉಪಪತ್ನಿಯೂ ಸೇವಕನೂ ಹೋಗುವದಕ್ಕೆ ಸಿದ್ಧರಾಗಲು ಆ ಸ್ತ್ರೀಯ ತಂದೆಯು ತನ್ನ ಅಳಿಯನಿಗೆ - ಹೊತ್ತು ಹೋಯಿತು; ಸಂಜೆಯಾಯಿತು. ದಯವಿಟ್ಟು ಈ ಹೊತ್ತಿನ ರಾತ್ರಿ ಇಲ್ಲೇ ಇರು; ನೋಡು, ಸಾಯಂಕಾಲವಾಯಿತು. ಈ ರಾತ್ರಿ ಇಲ್ಲಿದ್ದು ನಮ್ಮೊಡನೆ ಸಂತೋಷಪಡು; ನಾಳೆ ಬೆಳಿಗ್ಗೆ ಎದ್ದು ನಿನ್ನ ಮನೆಗೆ ಹೋಗಬಹುದು ಎಂದು ಬೇಡಿಕೊಂಡರೂ",
			"10": "<sup>10</sup> ಆ ಮನುಷ್ಯನು ಒಪ್ಪದೆ ತನ್ನ ಎರಡು ಕತ್ತೆಗಳಿಗೆ ತಡಿಹಾಕಿಸಿ ಉಪಪತ್ನಿಯನ್ನು ಕರೆದುಕೊಂಡು ಹೊರಟನು.",
			"11": "<sup>11</sup> ಅವರು ಯೆಬೂಸಿಯರ ಊರಾದ ಯೆರೂಸಲೇಮಿನ ಸಮೀಪಕ್ಕೆ ಬಂದಾಗ ಹೊತ್ತು ಮುಣುಗುವ ಕಾಲವಾದದರಿಂದ ಸೇವಕನು ದಣಿಗೆ - ದಯವಿಟ್ಟು ಬಾ; ನಾವು ಯೆಬೂಸಿಯರ ಈ ಪಟ್ಟಣಕ್ಕೆ ಹೋಗಿ ಅಲ್ಲಿ ರಾತ್ರಿಯನ್ನು ಕಳೆಯೋಣ ಅಂದನು.",
			"12": "<sup>12</sup> ಆಗ ದಣಿಯು - ನಾವು ಇಸ್ರಾಯೇಲ್ಯರಿಲ್ಲದ ಅನ್ಯರ ಪಟ್ಟಣದಲ್ಲಿ ಇಳಿಯಬಾರದು; ಗಿಬೆಯಕ್ಕೆ ಹೋಗೋಣ ಅಂದನು.",
			"13": "<sup>13</sup> ಅವನು ತಿರಿಗಿ ತನ್ನ ಆಳಿಗೆ - ನಾವು ಬೇಗನೆ ಹೋಗಿ ಮುಂದಿರುವ ಗಿಬೆಯದಲ್ಲಾಗಲಿ ರಾಮದಲ್ಲಾಗಲಿ ಇಳುಕೊಳ್ಳೋಣ ಬಾ ಅಂದನು.",
			"14": "<sup>14</sup> ಹೀಗೆ ಅವರು ಮುಂದೆ ಸಾಗಿ ಬೆನ್ಯಾಮೀನ್ಯರ ಗಿಬೆಯ ಊರಿನ ಸಮೀಪಕ್ಕೆ ಬಂದಾಗ ಸೂರ್ಯನು ಮುಣುಗಿದನು.",
			"15": "<sup>15</sup> ಆದದರಿಂದ ಅವರು ಇಳುಕೊಳ್ಳುವದಕ್ಕೋಸ್ಕರ ಗಿಬೆಯಕ್ಕೆ ಹೋಗಿ ಯಾರೂ ಮನೆಯಲ್ಲಿ ಸೇರಿಸಿಕೊಳ್ಳದ್ದರಿಂದ ಸಂತೇ ಬೀದಿಯಲ್ಲಿ ಕೂತುಕೊಂಡರು.",
			"16": "<sup>16</sup> ಅಷ್ಟರಲ್ಲಿ ಎಫ್ರಾಯೀಮ್ ಪರ್ವತ ಪ್ರದೇಶಕ್ಕೆ ಸೇರಿದವನೂ ಬೆನ್ಯಾಮೀನ್ಯರ ಊರಾದ ಗಿಬೆಯದಲ್ಲಿ ವಾಸಿಸುವವನೂ ಆಗಿದ್ದ ಒಬ್ಬಾನೊಬ್ಬ ಮುದುಕನು ಸಾಯಂಕಾಲವಾದದರಿಂದ ಕೆಲಸವನ್ನು ಬಿಟ್ಟು ಹೊಲದಿಂದ ಮನೆಗೆ ಬರುತ್ತಿದ್ದನು.",
			"17": "<sup>17</sup> ಈ ಮುದುಕನು ಬೀದಿಯಲ್ಲಿ ಕೂತಿದ್ದ ಆ ದಾರಿಗನನ್ನು ಕಂಡು - ಎಲ್ಲಿಂದ ಬಂದಿ? ಎಲ್ಲಿಗೆ ಹೋಗುತ್ತೀ ಎಂದು ಕೇಳಲು",
			"18": "<sup>18</sup> ಅವನು ಇವನಿಗೆ - ನಾವು ಯೆಹೂದದ ಬೇತ್ಲೆಹೇಮಿನಿಂದ ಎಫ್ರಾಯೀಮ್ ಪರ್ವತಪ್ರದೇಶದ ಒಂದು ಮೂಲೆಯಲ್ಲಿರುವ ನಮ್ಮ ವಾಸಸ್ಥಳಕ್ಕೆ ಹೋಗುತ್ತಿದ್ದೇವೆ. ನಾನು ಅಲ್ಲಿಂದ ಯೆಹೂದದ ಬೇತ್ಲೆಹೇಮಿಗೆ ಹೋಗಿದ್ದೆನು; ಈಗ ತಿರಿಗಿ ಯೆಹೋವನ ಮಂದಿರಕ್ಕೆ ಹೋಗುತ್ತೇನೆ. ಇಲ್ಲಿ ಯಾರೂ ನಮ್ಮನ್ನು ತಮ್ಮ ಮನೆಗೆ ಕರಕೊಂಡು ಹೋಗಲಿಲ್ಲ.",
			"19": "<sup>19</sup> ನಮ್ಮ ಕತ್ತೆಗಳಿಗೆ ಹುಲ್ಲೂ ಕಾಳೂ ಇವೆ; ನನಗೂ ನಿನ್ನ ಸೇವಕಿಗೂ ನಿನ್ನ ಸೇವಕನ ಸಂಗಡ ಬಂದಿರುವ ಈ ಪ್ರಾಯಸ್ಥನಿಗೂ ಸಾಕಾಗುವಷ್ಟು ರೊಟ್ಟಿಯೂ ದ್ರಾಕ್ಷಾರಸವೂ ಇರುತ್ತವೆ; ನಮಗೆ ಹೆಚ್ಚು ಏನೂ ಬೇಕಾಗಿರುವದಿಲ್ಲ ಎಂದು ಹೇಳಿದನು.",
			"20": "<sup>20</sup> ಆಗ ಆ ಮುದುಕನು - ನಿನಗೆ ಶುಭವಾಗಲಿ; ನಿನಗೆ ಏನಾದರೂ ಕಡಿಮೆಯಾದರೆ ಅದನ್ನು ನೋಡಿಕೊಳ್ಳುತ್ತೇನೆ;",
			"21": "<sup>21</sup> ಈ ಬೀದಿಯಲ್ಲಿ ರಾತ್ರಿ ಕಳೆಯಬೇಡ, ನನ್ನ ಮನೆಗೆ ಬಾ ಎಂದು ಹೇಳಿ ಅವನನ್ನು ಕರಕೊಂಡು ಹೋಗಿ ಅವನ ಕತ್ತೆಗಳಿಗೆ ಮೇವು ಕೊಟ್ಟನು. ಅವರು ತಮ್ಮ ಕೈಕಾಲುಗಳನ್ನು ತೊಳೆದುಕೊಂಡು ಅಲ್ಲಿ ಅನ್ನಪಾನಗಳನ್ನು ತೆಗೆದುಕೊಂಡರು.",
			"22": "<sup>22</sup> ಅವರು ಭೋಜನದಿಂದ ತೃಪ್ತಿಪಡುವಷ್ಟರಲ್ಲಿ ಆ ಊರಲ್ಲಿದ್ದ ನೀಚಜನರು ಬಂದು ಆ ಮನೆಯನ್ನು ಸುತ್ತಿಕೊಂಡು ಕದಗಳನ್ನು ಬಡಿದು ಮನೆಯ ಯಜಮಾನನಾದ ಮುದುಕನಿಗೆ - ನಿನ್ನ ಮನೆಗೆ ಬಂದಿರುವಂಥ ಮನುಷ್ಯನನ್ನು ಹೊರಗೆ ತೆಗೆದುಕೊಂಡು ಬಾ; ಅವನೊಡನೆ ನಮಗೆ ಸಂಗಮವಾಗಬೇಕು ಎಂದು ಕೂಗಿದರು.",
			"23": "<sup>23</sup> ಆಗ ಆ ಮುದುಕನು ಹೊರಗೆ ಹೋಗಿ ಅವರಿಗೆ - ಸಹೋದರರೇ, ಇಂಥ ದುಷ್ಟತನವನ್ನು ನಡಿಸಬೇಡಿರಿ; ಅವನು ಆಶ್ರಯಕ್ಕಾಗಿ ನನ್ನ ಮನೆಗೆ ಬಂದಿದ್ದಾನೆ; ನೀವು ಇಂಥ ಹುಚ್ಚುತನವನ್ನು ಮಾಡಬೇಡಿರಿ.",
			"24": "<sup>24</sup> ಇನ್ನೂ ಮದುವೆಯಾಗದಂಥ ನನ್ನ ಮಗಳನ್ನೂ ಅವನ ಉಪಪತ್ನಿಯನ್ನೂ ಹೊರಗೆ ತರುತ್ತೇನೆ; ಅವರನ್ನು ಭಂಗಪಡಿಸಿರಿ; ನಿಮ್ಮ ಮನಸ್ಸಿಗೆ ಬಂದ ಹಾಗೆ ಅವರಿಗೆ ಮಾಡಿರಿ. ಈ ಮನುಷ್ಯನಿಗೆ ಮಾತ್ರ ಅಂಥ ದುಷ್ಕೃತ್ಯವನ್ನು ಮಾಡಬೇಡಿರಿ ಅಂದನು.",
			"25": "<sup>25</sup> ಆದರೆ ಅವರು ಆ ಮುದುಕನಿಗೆ ಕಿವಿಗೊಡಲೊಲ್ಲದೆ ಹೋದದರಿಂದ ಆ ಮನುಷ್ಯನು ತನ್ನ ಉಪಪತ್ನಿಯನ್ನು ಹೊರಗೆ ಅವರ ಮಧ್ಯಕ್ಕೆ ಕಳುಹಿಸಿಬಿಟ್ಟನು. ಅವರು ಆಕೆಯನ್ನು ಕೆಡಿಸಿ ರಾತ್ರಿಯಲ್ಲೆಲ್ಲಾ ಬಹುಕ್ರೂರತನದಿಂದ ನಡಿಸಿ ಕೋಳಿಕೂಗುವ ಹೊತ್ತಾಗಲು ಬಿಟ್ಟು ಹೋದರು.",
			"26": "<sup>26</sup> ಆ ಸ್ತ್ರೀಯು ಸೂರ್ಯೋದಯಕ್ಕೆ ಮುಂಚೆಯೇ ಬಂದು ತನ್ನ ಯಜಮಾನನು ಇಳುಕೊಂಡಿದ್ದ ಮನೆಯ ಬಾಗಲಲ್ಲಿ ಬಿದ್ದವಳು ಬೆಳಗಾದರೂ ಏಳಲೇ ಇಲ್ಲ.",
			"27": "<sup>27</sup> ಆಕೆಯ ಪತಿಯು ಬೆಳಿಗ್ಗೆ ಎದ್ದು ಹೊರಡುವದಕ್ಕೆ ಸಿದ್ಧನಾಗಿ ಬಾಗಲನ್ನು ತೆರೆಯಲು ತನ್ನ ಉಪಪತ್ನಿಯು ಬಾಗಲ ಮುಂದೆ ಬಿದ್ದಿರುವದನ್ನೂ ಆಕೆಯ ಕೈಗಳು ಹೊಸ್ತಲಿನ ಮೇಲಿರುವದನ್ನೂ ಕಂಡನು.",
			"28": "<sup>28</sup> ಅವನು - ಏಳು, ಹೋಗೋಣ ಎಂದು ಆಕೆಯನ್ನು ಕರೆಯಲು ಪ್ರತ್ಯುತ್ತರವೇ ಇಲ್ಲ. ಆಗ ಅವನು ಆಕೆಯ ಶವವನ್ನು ಕತ್ತೆಯ ಮೇಲೆ ಹಾಕಿಕೊಂಡು ಸ್ವಸ್ಥಳಕ್ಕೆ ಹೋದನು.",
			"29": "<sup>29</sup> ಅವನು ಮನೆ ಸೇರಿದ ಮೇಲೆ ಒಂದು ಕತ್ತಿಯನ್ನು ತೆಗೆದುಕೊಂಡು ತನ್ನ ಉಪಪತ್ನಿಯ ಶವವನ್ನು ಎಲುಬುಗಳ ಸಹಿತವಾಗಿ ಹನ್ನೆರಡು ತುಂಡುಮಾಡಿ ಅವುಗಳನ್ನು ಇಸ್ರಾಯೇಲ್ಯರ ಎಲ್ಲಾ ಪ್ರಾಂತಗಳಿಗೆ ಕಳುಹಿಸಿದನು.",
			"30": "<sup>30</sup> ಅವುಗಳನ್ನು ಕಂಡವರೆಲ್ಲರೂ - ಇಸ್ರಾಯೇಲ್ಯರು ಐಗುಪ್ತವನ್ನು ಬಿಟ್ಟು ಬಂದ ದಿವಸ ಮೊದಲುಗೊಂಡು ಈ ದಿವಸದವರೆಗೆ ಇಂಥ ಕೃತ್ಯವು ನಡೆಯಲೂ ಇಲ್ಲ. ನಾವು ಅದನ್ನು ನೋಡಿಯೂ ಇಲ್ಲ. ಈ ಸಂಗತಿಯನ್ನು ಮನಸ್ಸಿಗೆ ತಂದು ವಿಚಾರಿಸತಕ್ಕದ್ದು ಅಂದುಕೊಂಡರು."
		},
		"20": {
			"1": "<sup>1</sup> ದಾನ್ ಪಟ್ಟಣದಿಂದ ಬೇರ್ಷೆಬದವರೆಗಿರುವ ಪ್ರಾಂತ್ಯಗಳಲ್ಲಿಯೂ ಗಿಲ್ಯಾದಿನಲ್ಲಿಯೂ ಇರುವ ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರೂ ಏಕಮನಸ್ಸಿನಿಂದ ಹೊರಟು ಮಿಚ್ಪೆಗೆ ಬಂದು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸಭೆ ನೆರೆದರು.",
			"2": "<sup>2</sup> ಆ ದೇವಪ್ರಜಾಸಭೆಯಲ್ಲಿ ಎಲ್ಲಾ ಕುಲಾಧಿಪತಿಗಳೂ ಯುದ್ಧಸನ್ನದ್ಧರಾದ ನಾಲ್ಕು ಲಕ್ಷ ಕಾಲಾಳುಗಳೂ ಇದ್ದರು.",
			"3": "<sup>3</sup> (ಇಸ್ರಾಯೇಲ್ಯರು ಕೂಡಿಕೊಂಡು ಮಿಚ್ಪೆಗೆ ಬಂದಿದ್ದಾರೆಂಬ ವರ್ತಮಾನವು ಬೆನ್ಯಾಮೀನ್ಯರಿಗೆ ಮುಟ್ಟಿತು.) ಇಸ್ರಾಯೇಲ್ಯರು - ಈ ದುಷ್ಟತನವು ಹೇಗೆ ನಡೆಯಿತೆಂದು ತಿಳಿಸಿರಿ ಎಂದು ಕೇಳಲು",
			"4": "<sup>4</sup> ಹತಳಾದ ಸ್ತ್ರೀಯ ಗಂಡನಾದ ಆ ಲೇವಿಯನು - ನಾನು ನನ್ನ ಉಪಪತ್ನಿಯ ಸಹಿತವಾಗಿ ಬೆನ್ಯಾಮೀನ್ಯರ ಊರಾದ ಗಿಬೆಯದಲ್ಲಿ ಒಂದು ರಾತ್ರಿ ಇಳುಕೊಂಡೆನು.",
			"5": "<sup>5</sup> ಆ ಊರಿನ ಜನರು ಅದೇ ರಾತ್ರಿ ನಾನು ಇಳುಕೊಂಡಿದ್ದ ಮನೆಯನ್ನು ಸುತ್ತಿಕೊಂಡು ನನ್ನನ್ನು ಕೊಲ್ಲಬೇಕೆಂದಿದ್ದರು; ಮತ್ತು ನನ್ನ ಉಪಪತ್ನಿಯನ್ನು ಬಹಳವಾಗಿ ಭಂಗಪಡಿಸಿದರು. ಆಕೆಯು ಸತ್ತಳು.",
			"6": "<sup>6</sup> ಅವರು ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ನಡಿಸಿದ ಇಂಥ ಕೆಟ್ಟ ಹುಚ್ಚುಗೆಲಸವು ಎಲ್ಲರಿಗೂ ಗೊತ್ತಾಗಲೆಂದು ನಾನು ಆಕೆಯ ಶವವನ್ನು ತುಂಡುಮಾಡಿ ಇಸ್ರಾಯೇಲ್ಯರ ಎಲ್ಲಾ ಪ್ರಾಂತಗಳಿಗೆ ಕಳುಹಿಸಿದೆನು.",
			"7": "<sup>7</sup> ಇಲ್ಲಿ ಕೂಡಿದ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರೇ, ಈಗ ನಿಮ್ಮ ಆಲೋಚನಾಭಿಪ್ರಾಯಗಳನ್ನು ಹೇಳಿರಿ ಅಂದನು.",
			"8": "<sup>8</sup> ಇದನ್ನು ಕೇಳಿ ಜನರು ಏಕಮನಸ್ಸಿನಿಂದ - ನಮ್ಮಲ್ಲಿ ಯಾವನೂ ತನ್ನ ಗುಡಾರಕ್ಕೆ ಹೋಗಬಾರದು; ತನ್ನ ಮನೆಗೆ ತಿರುಗಕೂಡದು.",
			"9": "<sup>9</sup> ನಾವು ಚೀಟು ಹಾಕೋಣ; ಅದು ಬಿದ್ದ ಪ್ರಕಾರವೇ ಗಿಬೆಯದವರಿಗೆ ವಿರೋಧವಾಗಿ ಯುದ್ಧಕ್ಕೆ ಹೋಗೋಣ.",
			"10": "<sup>10</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಕುಲಗಳಲ್ಲಿ ನೂರಕ್ಕೆ ಹತ್ತು, ಸಾವಿರಕ್ಕೆ ನೂರು, ಹತ್ತು ಸಾವಿರಕ್ಕೆ ಸಾವಿರ ಈ ಪ್ರಕಾರ ಜನರನ್ನು ಆರಿಸಿಕೊಂಡು ಅವರನ್ನು ಆಹಾರ ತರುವದಕ್ಕಾಗಿ ಕಳುಹಿಸೋಣ. ಅವರು ಬಂದ ಮೇಲೆ ಬೆನ್ಯಾಮೀನ್ಯರಾದ ಗಿಬೆಯದವರು ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ನಡಿಸಿದ ದುಷ್ಕರ್ಮಕ್ಕಾಗಿ ಅವರಿಗೆ ದಂಡನೆ ಮಾಡೋಣ ಅಂದುಕೊಂಡರು.",
			"11": "<sup>11</sup> ಹೀಗೆ ಅವರೆಲ್ಲರೂ ಏಕಮನಸ್ಸಿನಿಂದ ಆ ಪಟ್ಟಣಕ್ಕೆ ವಿರೋಧವಾಗಿ ಕೂಡಿಕೊಂಡರು.",
			"12": "<sup>12</sup> ಅವರು ಬೆನ್ಯಾಮೀನ್ಯರ ಸಮಸ್ತ ಕುಲಸ್ಥರ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿ ಅವರಿಗೆ - ನಿಮ್ಮಲ್ಲಿ ನಡೆದ ಈ ದುಷ್ಕರ್ಮ ಎಂಥದು?",
			"13": "<sup>13</sup> ಈಗ ಗಿಬೆಯ ಊರಲ್ಲಿರುವ ಆ ನೀಚರನ್ನು ನಮಗೆ ಒಪ್ಪಿಸಿರಿ; ನಾವು ಅವರನ್ನು ಕೊಂದು ಇಸ್ರಾಯೇಲ್ಯರ ಮಧ್ಯದಿಂದ ಇಂಥ ದುಷ್ಟತನವನ್ನು ತೆಗೆದುಹಾಕುತ್ತೇವೆ ಎಂದು ಹೇಳಿಸಿದರು.",
			"14": "<sup>14</sup> ಆದರೆ ಬೆನ್ಯಾಮೀನ್ಯರು ತಮ್ಮ ಬಂಧುಗಳ ಮಾತಿಗೆ ಕಿವಿಗೊಡದೆ ತಮ್ಮ ಎಲ್ಲಾ ಊರುಗಳಿಂದ ಗಿಬೆಯಕ್ಕೆ ಬಂದು ಇಸ್ರಾಯೇಲ್ಯರ ಸಂಗಡ ಯುದ್ಧಕ್ಕೆ ನಿಂತರು.",
			"15": "<sup>15</sup> ಆ ದಿವಸದಲ್ಲಿ ಆಯಾ ಊರುಗಳಿಂದ ಯುದ್ಧಸನ್ನದ್ಧರಾಗಿ ಕೂಡಿಬಂದ ಬೆನ್ಯಾಮೀನ್ಯರ ಸಂಖ್ಯೆಯು ಇಪ್ಪತ್ತಾರು ಸಾವಿರವಾಗಿತ್ತು. ಇವರಲ್ಲದೆ ಗಿಬೆಯದಲ್ಲಿಯೇ ಏಳು ನೂರು ಮಂದಿ ಯುದ್ಧವೀರರಿದ್ದರು.",
			"16": "<sup>16</sup> ಈ ಎಲ್ಲಾ ಜನರಲ್ಲಿ ಏಳು ನೂರು ಮಂದಿ ಎಡಚರಾದ ಯುದ್ಧವೀರರಿದ್ದರು. ಅವರಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬನೂ ಕೂದಲೆಳೆಯಷ್ಟೂ ಗುರಿ ತಪ್ಪದ ಹಾಗೆ ಕವಣೆಯನ್ನು ಹೊಡಿಯುವದರಲ್ಲಿ ನಿಪುಣನು;",
			"17": "<sup>17</sup> ಬೆನ್ಯಾಮೀನ್ಯರಲ್ಲದ ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ನಾಲ್ಕು ಲಕ್ಷ ಭಟರಿದ್ದರು; ಇವರೆಲ್ಲರೂ ವೀರರೇ.",
			"18": "<sup>18</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಬೇತೇಲಿಗೆ ಹೋಗಿ - ಬೆನ್ಯಾಮೀನ್ಯರ ಮೇಲೆ ಯುದ್ಧಕ್ಕೆ ನಮ್ಮಲ್ಲಿ ಮೊದಲು ಯಾರು ಹೋಗಬೇಕು ಎಂದು ದೇವರಾದ ಯೆಹೋವನನ್ನು ಕೇಳಲು ಆತನು - ಮೊದಲು ಯೆಹೂದಕುಲದವರು ಹೋಗಲಿ ಎಂದು ಹೇಳಿದನು.",
			"19": "<sup>19</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಬೆಳಿಗ್ಗೆ ಹೊರಟುಹೋಗಿ ಗಿಬೆಯದ ಎದುರಿನಲ್ಲಿ ಪಾಳೆಯಮಾಡಿಕೊಂಡು",
			"20": "<sup>20</sup> ಬೆನ್ಯಾಮೀನ್ಯರೊಡನೆ ಯುದ್ಧಮಾಡುವದಕ್ಕೋಸ್ಕರ ಸಿದ್ಧರಾಗಿ ಗಿಬೆಯದ ಎದುರಿನಲ್ಲಿ ವ್ಯೂಹಕಟ್ಟಿ ನಿಂತರು.",
			"21": "<sup>21</sup> ಆ ದಿನದಲ್ಲಿ ಬೆನ್ಯಾಮೀನ್ಯರು ಗಿಬೆಯದಿಂದ ಹೊರಗೆ ಬಂದು ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಇಪ್ಪತ್ತೆರಡು ಸಾವಿರ ಜನರನ್ನು ನೆಲಕ್ಕುರುಳಿಸಿದರು.",
			"22": "<sup>22</sup> ಇಸ್ರಾಯೇಲ್ಯರು ತಿರಿಗಿ ಧೈರ್ಯ ತಂದುಕೊಂಡು ಮೊದಲನೆಯ ದಿನದಲ್ಲಿ ವ್ಯೂಹ ಕಟ್ಟಿದ ಸ್ಥಳದಲ್ಲೇ ತಿರಿಗಿ ವ್ಯೂಹಕಟ್ಟಿ ಯುದ್ಧಕ್ಕೆ ನಿಂತರು.",
			"23": "<sup>23</sup> (ಇಸ್ರಾಯೇಲ್ಯರು ಹೋಗಿ ಯೆಹೋವನ ಮುಂದೆ ಅಳುತ್ತಾ - ನಾವು ತಿರಿಗಿ ನಮ್ಮ ಬಂಧುಗಳಾದ ಬೆನ್ಯಾಮೀನ್ಯರೊಡನೆ ಯುದ್ಧಮಾಡಬೇಕೋ ಎಂದು ಕೇಳಲು ಆತನು - ಹೋಗಿ ಯುದ್ಧಮಾಡಿರಿ ಅಂದನು.",
			"24": "<sup>24</sup> ಆದದರಿಂದ ಅವರು ಎರಡನೆಯ ದಿನದಲ್ಲಿಯೂ ಯುದ್ಧಕ್ಕೆ ಸಿದ್ಧರಾಗಿ ಬೆನ್ಯಾಮೀನ್ಯರ ಸಮೀಪಕ್ಕೆ ಬಂದರು.)",
			"25": "<sup>25</sup> ಬೆನ್ಯಾಮೀನ್ಯರು ಎರಡನೆಯ ದಿನದಲ್ಲಿಯೂ ಗಿಬೆಯದಿಂದ ಹೊರಗೆ ಬಂದು ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಹದಿನೆಂಟು ಸಾವಿರ ಮಂದಿ ಯುದ್ಧವೀರರನ್ನು ನೆಲಕ್ಕೆ ಬೀಳಿಸಿದರು.",
			"26": "<sup>26</sup> ಆಗ ಎಲ್ಲಾ ಜನರೂ ಬೇತೇಲಿಗೆ ಹೋಗಿ ಅಲ್ಲಿ ಯೆಹೋವನ ಮುಂದೆ ಅಳುತ್ತಾ ಬಿದ್ದು ಸಾಯಂಕಾಲದವರೆಗೂ ಉಪವಾಸ ಮಾಡಿದರು. ಇದಲ್ಲದೆ ಅವರು ಆತನಿಗೆ ಸರ್ವಾಂಗಹೋಮಗಳನ್ನೂ ಸಮಾಧಾನಯಜ್ಞಗಳನ್ನೂ ಸಮರ್ಪಿಸಿದರು.",
			"27": "<sup>27</sup> ಆ ಕಾಲದಲ್ಲಿ ಯೆಹೋವನ ಒಡಂಬಡಿಕೆಯ ಮಂಜೂಷವು ಆ ಊರೊಳಗಿತ್ತು;",
			"28": "<sup>28</sup> ಆರೋನನ ಮೊಮ್ಮಗನೂ ಎಲ್ಲಾಜಾರನ ಮಗನೂ ಆದ ಫೀನೆಹಾಸನು ಯಾಜಕಸೇವೆಮಾಡುತ್ತಿದ್ದನು. ಹೀಗಿರುವದರಿಂದ ಇಸ್ರಾಯೇಲ್ಯರು - ನಾವು ನಮ್ಮ ಬಂಧುಗಳಾದ ಬೆನ್ಯಾಮೀನ್ಯರೊಡನೆ ಯುದ್ಧಕ್ಕೆ ಹೋಗಬೇಕೋ ಬೇಡವೋ ಎಂದು ಯೆಹೋವನನ್ನು ಕೇಳಿದರು. ಆತನು ಅವರಿಗೆ - ಹೋಗಿರಿ, ನಾಳೆ ಅವರನ್ನು ನಿಮ್ಮ ಕೈಗೆ ಒಪ್ಪಿಸುವೆನು ಅಂದನು.",
			"29": "<sup>29</sup> ಆಗ ಇಸ್ರಾಯೇಲ್ಯರು ಗಿಬೆಯದ ಸುತ್ತಲೂ ಕೆಲವರನ್ನು ಹೊಂಚಿನೋಡುವದಕ್ಕೆ ಇರಿಸಿ",
			"30": "<sup>30</sup> ಉಳಿದವರು ಬೆನ್ಯಾಮೀನ್ಯರ ಸಂಗಡ ಕಾದಾಡುವದಕ್ಕೋಸ್ಕರ ಮುಂಚಿನಂತೆ ಮೂರನೆಯ ದಿನದಲ್ಲಿಯೂ ಗಿಬೆಯದ ಸಮೀಪದಲ್ಲಿ ವ್ಯೂಹಕಟ್ಟಿದರು.",
			"31": "<sup>31</sup> ಬೆನ್ಯಾಮೀನ್ಯರು ಇಸ್ರಾಯೇಲ್ಯರೊಡನೆ ಯುದ್ಧಮಾಡುವದಕ್ಕಾಗಿ ಪಟ್ಟಣವನ್ನು ಬಿಟ್ಟು ದೂರಕ್ಕೆ ಬಂದರು. ಅವರು ಮುಂಚಿನಂತೆ ಜನರನ್ನು ವಧಿಸುವದಕ್ಕೆ ಪ್ರಾರಂಭಿಸಿ ಬೇತೇಲಿಗೂ ಬೈಲಿನಲ್ಲಿರುವ ಗಿಬೆಯಕ್ಕೂ ಹೋಗುವ ರಾಜ ಮಾರ್ಗಗಳಲ್ಲಿ ಸುಮಾರು ಮೂವತ್ತು ಮಂದಿಯನ್ನು ಹತಮಾಡಿದರು.",
			"32": "<sup>32</sup> ಬೆನ್ಯಾಮೀನ್ಯರು - ಅವರು ಮುಂಚಿನಂತೆ ಈಗಲೂ ಸೋತುಹೋಗುತ್ತಾರೆಂದು ನೆನಸಿದರು, ಇಸ್ರಾಯೇಲ್ಯರಾದರೋ - ಅವರು ತಮ್ಮ ಪಟ್ಟಣಕ್ಕೆ ದೂರವಾಗುವಂತೆ ನಾವು ಸ್ವಲ್ಪ ದೂರ ಓಡಿಹೋಗೋಣ ಎಂದು ಮಾತಾಡಿಕೊಂಡು",
			"33": "<sup>33</sup> ತಾವು ಮೊದಲು ನಿಂತ ಸ್ಥಳದಿಂದ ಓಡತೊಡಗಿದರು. ಬಾಳ್‍ತಾಮರಿಗೆ ಮುಟ್ಟಿದ ಕೂಡಲೆ ಅಲ್ಲಿ ನಿಂತು ತಿರಿಗಿ ಯುದ್ಧವನ್ನು ಪ್ರಾರಂಭಿಸಿದರು. ಅಷ್ಟರಲ್ಲಿ ಹೊಂಚಿ ನೋಡುತ್ತಿದ್ದವರು ತಾವು ಅಡಗಿಕೊಂಡಿದ್ದ ಗಿಬೆಯದ ಮೈದಾನಿನಿಂದ ಎದ್ದು",
			"34": "<sup>34</sup> ಪಟ್ಟಣದ ಮುಂದೆ ಬಂದರು; ಅವರು ಸುಮಾರು ಹತ್ತು ಸಾವಿರ ಮಂದಿ ಯುದ್ಧವೀರರು. ಯುದ್ಧವು ಬಹು ಘೋರವಾಯಿತು. ತಮಗೆ ಅಪಾಯವು ಒದಗಿ ಬಂದದೆ ಎಂಬದು ಬೆನ್ಯಾಮೀನ್ಯರಿಗೆ ಇನ್ನೂ ಗೊತ್ತಾಗಲಿಲ್ಲ.",
			"35": "<sup>35</sup> ಯೆಹೋವನು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಜಯಕೊಟ್ಟದರಿಂದ ಅವರು ಆ ದಿನದಲ್ಲಿ ಇಪ್ಪತ್ತೈದು ಸಾವಿರದ ನೂರು ಮಂದಿ ಬೆನ್ಯಾಮೀನ್ಯ ಭಟರನ್ನು ಹತಮಾಡಿದರು.",
			"36": "<sup>36</sup> ತಾವು ಸೋತು ಹೋದೆವೆಂಬದು ಬೆನ್ಯಾಮೀನ್ಯರಿಗೆ ಆಗ ತಿಳಿಯಿತು. (ಇಸ್ರಾಯೇಲ್ಯರು ತಾವು ಗಿಬೆಯದ ಸಮೀಪದಲ್ಲಿ ಹೊಂಚಿನೋಡುವದಕ್ಕೋಸ್ಕರ ಇಟ್ಟ ಜನರಲ್ಲಿ ಭರವಸವುಳ್ಳವರಾಗಿದ್ದದರಿಂದ ತಮ್ಮ ಸ್ಥಳವನ್ನು ಬಿಟ್ಟು ಬೆನ್ಯಾಮೀನ್ಯರ ಮುಂದೆ ಓಡಿಹೋದರು.",
			"37": "<sup>37</sup> ಅಷ್ಟರಲ್ಲಿ ಅಡಗಿಕೊಂಡಿದ್ದವರು ಎದ್ದು ಗಿಬೆಯಕ್ಕೆ ಶೀಘ್ರವಾಗಿ ಹೋಗಿ ಅದರೊಳಗಿದ್ದವರೆಲ್ಲರನ್ನೂ ಕತ್ತಿಯಿಂದ ಸಂಹರಿಸಿದರು.",
			"38": "<sup>38</sup> ಅಡಗಿದ್ದವರು ಊರಿಗೆ ಬೆಂಕಿಹೊತ್ತಿಸಿ ಹೊಗೆ ಏರಿಹೋಗುವಂತೆ ಮಾಡಿದ ಮೇಲೆ ಓಡಿಹೋಗುತ್ತಿದ್ದವರು ತಿರುಗಿಕೊಂಡು ಯುದ್ಧಕ್ಕೆ ನಿಲ್ಲಬೇಕೆಂದು ಹೊಂಚಿನೋಡುವವರೂ ಉಳಿದ ಇಸ್ರಾಯೇಲ್ಯರೂ ತಮ್ಮ ತಮ್ಮೊಳಗೆ ಗೊತ್ತು ಮಾಡಿದ್ದರು.",
			"39": "<sup>39</sup> ಬೆನ್ಯಾಮೀನ್ಯರಾದರೋ - ಇಸ್ರಾಯೇಲ್ಯರು ಮುಂಚಿನಂತೆ ನಮ್ಮ ಮುಂದೆ ಸೋತುಹೋದರೆಂದು ನೆನಸಿ ಅವರನ್ನು ಹತಿಸ ಪ್ರಾರಂಭಿಸಿ ಸುಮಾರು ಮೂವತ್ತು ಮಂದಿಯನ್ನು ಕೊಂದುಹಾಕಿದರು.",
			"40": "<sup>40</sup> ಆದರೆ ಪಟ್ಟಣದಿಂದ ಹೊಗೆಯೆದ್ದು ಸ್ತಂಭದೋಪಾದಿಯಲ್ಲಿ ಕಾಣಿಸಲು ಇಸ್ರಾಯೇಲ್ಯರು ತಿರುಗಿ ನಿಂತರು.",
			"41": "<sup>41</sup> ಬೆನ್ಯಾಮೀನ್ಯರು ಹಿಂದಿರುಗಿ ನೋಡಿ ತಮ್ಮ ಪಟ್ಟಣವು ಅಗ್ನಿಗೆ ಆಹುತಿಯಾದದ್ದನ್ನು ಕಂಡು ತಮಗೆ ಅಪಾಯ ಪ್ರಾಪ್ತವಾಯಿತೆಂದು ಕಳವಳಗೊಂಡರು.",
			"42": "<sup>42</sup> ಅವರು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಬೆನ್ನುತೋರಿಸಿ ಅರಣ್ಯಮಾರ್ಗವಾಗಿ ಓಡಿಹೋದರು. ಆದರೆ ಯುದ್ಧಸ್ಥರು ಅವರನ್ನು ಹಿಂದಟ್ಟಿದ್ದರಿಂದಲೂ ಪಟ್ಟಣಕ್ಕೆ ಬೆಂಕಿ ಹೊತ್ತಿಸಿದವರು ಬಂದುಬಿಟ್ಟದ್ದರಿಂದಲೂ ಅವರು ಉಭಯರ ಮಧ್ಯದಲ್ಲಿ ಸಿಕ್ಕಿ ಹಾಳಾದರು.",
			"43": "<sup>43</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಅವರನ್ನು ಸುತ್ತಿಕೊಂಡು ಎಲ್ಲಿಯೂ ಓಡಗೊಡದೆ ಗಿಬೆಯ ಊರಿನ ಪೂರ್ವದಿಕ್ಕಿನಲ್ಲಿರುವ ಮೆನೂಹದಲ್ಲಿ ತುಳಿದುಬಿಟ್ಟರು.",
			"44": "<sup>44</sup> ಈ ಪ್ರಕಾರ ಬೆನ್ಯಾಮೀನ್ಯರಲ್ಲಿ ಹದಿನೆಂಟು ಸಾವಿರ ಮಂದಿ ಯುದ್ಧವೀರರು ಹತರಾದರು.",
			"45": "<sup>45</sup> ಇದಲ್ಲದೆ ಅವರು ಅರಣ್ಯದಲ್ಲಿರುವ ರಿಮ್ಮೋನ್ ಗಿರಿಗೆ ಓಡಿಹೋಗುವಾಗ ಹಕ್ಕಲುತೆನೆಗಳನ್ನೋ ಎಂಬಂತೆ ಐದು ಸಾವಿರ ಜನರನ್ನು ರಾಜಮಾರ್ಗಗಳಲ್ಲಿ ಕೊಂದು ಹಾಕಿದರು. ಅಲ್ಲಿಂದ ಗಿದೋಮಿನವರೆಗೆ ಹಿಂದಟ್ಟಿ ತಿರಿಗಿ ಎರಡು ಸಾವಿರ ಜನರನ್ನು ಹತಮಾಡಿದರು.",
			"46": "<sup>46</sup> ಹೀಗೆ ಆ ದಿವಸ ಬೆನ್ಯಾಮೀನ್ಯರಲ್ಲಿ ಇಪ್ಪತ್ತೈದು ಸಾವಿರ ಯುದ್ಧಸನ್ನದ್ಧರಾದ ಭಟರು ಸಂಹೃತರಾದರು.)",
			"47": "<sup>47</sup> ಆದರೆ ಆರು ನೂರು ಮಂದಿ ಅರಣ್ಯಮಾರ್ಗವಾಗಿ ಓಡಿಹೋಗಿ ರಿಮ್ಮೋನ್ ಗಿರಿಯನ್ನು ಸೇರಿ ಅಲ್ಲಿ ನಾಲ್ಕು ತಿಂಗಳಿದ್ದರು.",
			"48": "<sup>48</sup> ಇಸ್ರಾಯೇಲ್ಯರು ತಿರುಗಿಕೊಂಡು ಬೆನ್ಯಾಮೀನ್ ದೇಶದ ಉಳಿದ ಗ್ರಾಮನಗರಗಳಿಗೆ ಹೋಗಿ ಅವುಗಳ ನಿವಾಸಿಗಳನ್ನೂ ದನಕುರಿಗಳನ್ನೂ ಸಿಕ್ಕಿದ್ದೆಲ್ಲವನ್ನೂ ಸಂಹರಿಸಿ ಎಲ್ಲಾ ಊರುಗಳಿಗೆ ಬೆಂಕಿಹೊತ್ತಿಸಿದರು."
		},
		"21": {
			"1": "<sup>1</sup> ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರೂ ಮಿಚ್ಪೆಯಲ್ಲಿದ್ದಾಗ ತಾವು ಬೆನ್ಯಾಮೀನ್ಯರಿಗೆ ತಮ್ಮ ಹೆಣ್ಣುಗಳನ್ನು ಕೊಡುವದಿಲ್ಲವೆಂದು ಆಣೆಯಿಟ್ಟಿದ್ದರು.",
			"2": "<sup>2</sup> ಅವರು ಬೇತೇಲಿಗೆ ಬಂದು ಸಾಯಂಕಾಲದವರೆಗೆ ದೇವರ ಮುಂದೆ ಬಿದ್ದುಕೊಂಡು ಗಟ್ಟಿಯಾಗಿ ಅಳುತ್ತಾ -",
			"3": "<sup>3</sup> ಯೆಹೋವನೇ, ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರೇ, ನಮ್ಮಲ್ಲಿ ಹೀಗೆ ಯಾಕೆ ಆಯಿತು? ಇಸ್ರಾಯೇಲ್ಯರ ಒಂದು ಕುಲವು ಹಾಳಾಗಿ ಹೋಯಿತಲ್ಲಾ ಎಂದು ಕೂಗಿದರು.",
			"4": "<sup>4</sup> ಮರುದಿವಸ ಅವರು ಬೆಳಿಗ್ಗೆ ಎದ್ದು ಯಜ್ಞವೇದಿಯನ್ನು ಕಟ್ಟಿ ಸರ್ವಾಂಗಹೋಮಗಳನ್ನೂ ಸಮಾಧಾನಯಜ್ಞಗಳನ್ನೂ ಸಮರ್ಪಿಸಿದರು.",
			"5": "<sup>5</sup> ಮತ್ತು ಯೆಹೋವನ ಮುಂದೆ ಸಭೆ ನೆರೆದಾಗ ಅದಕ್ಕೆ ಬಾರದೆ ಇದ್ದಂಥ ಇಸ್ರಾಯೇಲ್ಯರು ಯಾರಾರೆಂದು ವಿಚಾರಮಾಡಿದರು. ಯಾಕಂದರೆ ಮಿಚ್ಪೆಯಲ್ಲಿ - ಯೆಹೋವನ ಸನ್ನಿಧಿಗೆ ಬಾರದವರನ್ನು ಕೊಂದೇಹಾಕುವೆವೆಂದು ಆಣೆಯಿಟ್ಟು ಖಂಡಿತ ಪ್ರಮಾಣಮಾಡಿದ್ದರು.",
			"6": "<sup>6</sup> ಇಸ್ರಾಯೇಲ್ಯರು ತಮ್ಮ ಬಂಧುಗಳಾದ ಬೆನ್ಯಾಮೀನ್ಯರ ಅವಸ್ಥೆಯನ್ನು ನೆನಸಿ ದುಃಖದಿಂದ - ಅಯ್ಯೋ, ಇಸ್ರಾಯೇಲ್ಯರಾದ ನಮ್ಮಲ್ಲಿ ಒಂದು ಕುಲವು ಕಡಮೆಯಾಯಿತಲ್ಲಾ;",
			"7": "<sup>7</sup> ಜೀವದಿಂದುಳಿದವರಿಗೆ ಹೆಂಡತಿಯರನ್ನು ದೊರಕಿಸಿಕೊಡುವದು ಹೇಗೆ? ನಮ್ಮ ಹೆಣ್ಣುಗಳನ್ನು ಅವರಿಗೆ ಕೊಡುವದಿಲ್ಲವೆಂದು ಯೆಹೋವನ ಹೆಸರಿನಲ್ಲಿ ಆಣೆಯಿಟ್ಟೆವಲ್ಲಾ ಎಂದು ಗೋಳಾಡಿದರು.",
			"8": "<sup>8</sup> ಮಿಚ್ಪೆಯಲ್ಲಿ ಯೆಹೋವನ ಮುಂದೆ ಸಭೆನೆರೆದಾಗ ಅದಕ್ಕೆ ಬಾರದಿದ್ದ ಇಸ್ರಾಯೇಲ್ಯರು ಯಾರಾದರೂ ಇದ್ದಾರೋ ಎಂದು ವಿಚಾರಿಸುವಲ್ಲಿ ಯಾಬೇಷ್ ಗಿಲ್ಯಾದಿನಿಂದ ಒಬ್ಬನೂ ಬರಲಿಲ್ಲವೆಂದು ಗೊತ್ತಾಯಿತು.",
			"9": "<sup>9</sup> ಜನರನ್ನು ಲೆಕ್ಕಿಸಿದಾಗ ಯಾಬೇಷ್ ಗಿಲ್ಯಾದಿನಿಂದ ಯಾರೂ ಬರಲಿಲ್ಲವೆಂಬದು ದೃಢವಾಯಿತು.",
			"10": "<sup>10</sup> ಆಗ ಅವರು ತಮ್ಮಲ್ಲಿಂದ ಹನ್ನೆರಡು ಸಾವಿರ ಮಂದಿ ಪರಾಕ್ರಮಶಾಲಿಗಳನ್ನು ಆರಿಸಿಕೊಂಡು ಅವರಿಗೆ - ನೀವು ಯಾಬೇಷ್ ಗಿಲ್ಯಾದಿಗೆ ಹೋಗಿ ಎಲ್ಲಾ ನಿವಾಸಿಗಳನ್ನೂ ಸ್ತ್ರೀಯರನ್ನೂ ಚಿಕ್ಕ ಮಕ್ಕಳನ್ನೂ ಬಿಡದೆ ಸಂಹರಿಸಬೇಕು;",
			"11": "<sup>11</sup> ಹೇಗೂ ಗಂಡಾದದ್ದೆಲ್ಲವನ್ನೂ ಮದುವೆಯಾದ ಸ್ತ್ರೀಯರನ್ನೂ ನಿರ್ಮೂಲಮಾಡುವದು ನಿಮ್ಮ ಕೆಲಸ ಎಂದು ಆಜ್ಞಾಪಿಸಿ ಕಳುಹಿಸಿದರು.",
			"12": "<sup>12</sup> ಯಾಬೇಷ್ ಗಿಲ್ಯಾದಿನಲ್ಲಿ ಇನ್ನೂ ಮದುವೆಯಾಗದಿದ್ದ ನಾನೂರು ಮಂದಿ ಯುವತಿಯರಿದ್ದರು. ಪಟ್ಟಣವನ್ನು ಸಂಹರಿಸುವದಕ್ಕೆ ಹೋದವರು ಇವರನ್ನು ಉಳಿಸಿ ಕಾನಾನ್ ದೇಶದ ಶೀಲೋವಿನಲ್ಲಿದ್ದ ಪಾಳೆಯಕ್ಕೆ ತೆಗೆದುಕೊಂಡು ಬಂದರು.",
			"13": "<sup>13</sup> ಅನಂತರ ಸರ್ವಸಭೆಯವರು ರಿಮ್ಮೋನ್ ಗಿರಿಯಲ್ಲಿದ್ದ ಬೆನ್ಯಾಮೀನ್ಯರ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿ ಅವರಲ್ಲಿ ಸಮಾಧಾನವಾಕ್ಯವನ್ನು ಪ್ರಕಟಿಸಲು ಅವರು ತಿರಿಗಿ ಬಂದರು.",
			"14": "<sup>14</sup> ಇಸ್ರಾಯೇಲ್ಯರು ತಾವು ಯಾಬೇಷ್ ಗಿಲ್ಯಾದಿನಿಂದ ಉಳಿಸಿ ತಂದ ಕನ್ಯೆಯರನ್ನು ಅವರಿಗೆ ಮದುವೆಮಾಡಿಕೊಟ್ಟರು; ಅವರು ಸಾಲಲಿಲ್ಲ.",
			"15": "<sup>15</sup> ಯೆಹೋವನು ನಮ್ಮಲ್ಲಿ ಒಂದು ಲೋಪವನ್ನುಂಟು ಮಾಡಿದ್ದಾನೆಂದು ಇಸ್ರಾಯೇಲ್ಯರು ಬೆನ್ಯಾಮೀನ್ಯರ ವಿಷಯದಲ್ಲಿ ದುಃಖಪಟ್ಟರು.",
			"16": "<sup>16</sup> ಸಭೆಯ ಹಿರಿಯರು - ಉಳಿದಿರುವ ಬೆನ್ಯಾಮೀನ್ಯರಿಗೋಸ್ಕರ ಹೆಂಡತಿಯರನ್ನು ದೊರಕಿಸುವದು ಹೇಗೆ? ಅವರ ಸ್ತ್ರೀಯರೆಲ್ಲಾ ಸಂಹೃತರಾದರಲ್ಲಾ.",
			"17": "<sup>17</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಒಂದು ಪೂರ್ಣ ಕುಲವು ಅಳಿದುಹೋಗಬಾರದು; ತಪ್ಪಿಸಿಕೊಂಡು ಉಳಿದಿರುವ ಬೆನ್ಯಾಮೀನ್ಯರಿಗೆ ಬಾಧ್ಯಸ್ಥರು ಹುಟ್ಟಬೇಕಲ್ಲಾ.",
			"18": "<sup>18</sup> ಬೆನ್ಯಾಮೀನ್ಯರಿಗೆ ತಮ್ಮ ಹೆಣ್ಣುಗಳನ್ನು ಕೊಡುವ ಇಸ್ರಾಯೇಲ್ಯರು ಶಾಪಗ್ರಸ್ತರಾಗಲಿ ಎಂದು ಆಣೆಯಿಟ್ಟುಕೊಂಡಿದ್ದೇವಾದದರಿಂದ ನಾವು ಅವರಿಗೆ ನಮ್ಮ ಹೆಣ್ಣುಗಳನ್ನು ಕೊಡುವದಕ್ಕಾಗುವದಿಲ್ಲ ಎಂದು ಮಾತಾಡಿಕೊಳ್ಳುತ್ತಿರುವಾಗ",
			"19": "<sup>19</sup> ಶೀಲೋವಿನಲ್ಲಿ ವರ್ಷ ವರ್ಷ ಯೆಹೋವನ ಉತ್ಸವ ನಡೆಯುತ್ತದೆಂಬದು ಅವರಿಗೆ ನೆನಪಿಗೆ ಬಂದಿತು. (ಶೀಲೋ ಎಂಬದು ಬೇತೇಲಿನ ಉತ್ತರಕ್ಕೂ ಬೇತೇಲಿನಿಂದ ಶೆಕೆಮಿಗೆ ಹೋಗುವ ರಾಜಮಾರ್ಗದ ಪೂರ್ವಕ್ಕೂ ಲೆಬೋನದ ದಕ್ಷಿಣಕ್ಕೂ ಇರುತ್ತದೆ.)",
			"20": "<sup>20</sup> ಆಗ ಅವರು ಬೆನ್ಯಾಮೀನ್ಯರನ್ನು ಕರೆದು ಅವರಿಗೆ - ಇಗೋ, ನೀವು ದ್ರಾಕ್ಷೇ ತೋಟಗಳಲ್ಲಿ ಅಡಗಿಕೊಳ್ಳಿರಿ;",
			"21": "<sup>21</sup> ಶೀಲೋವಿನ ಕನ್ಯೆಯರು ಹೊರಗೆ ಬಂದು ನಾಟ್ಯವಾಡುವಾಗ ನೀವು ತೋಟಗಳಿಂದ ಹೊರಗೆ ಬಂದು ಪ್ರತಿಯೊಬ್ಬನು ತನತನಗೆ ಶೀಲೋವಿನ ಕನ್ನಿಕೆಗಳಿಂದ ಒಬ್ಬಳನ್ನು ಹೆಂಡತಿಯಾಗಿ ತೆಗೆದುಕೊಂಡು ನಿಮ್ಮ ಸೀಮೆಗೆ ಓಡಿ ಹೋಗಿರಿ.",
			"22": "<sup>22</sup> ಅವರ ತಂದೆಗಳೂ ಅಣ್ಣತಮ್ಮಂದಿರೂ ನಮ್ಮ ಹತ್ತಿರ ದೂರು ತಂದರೆ ನಾವು ಅವರಿಗೆ - ನಮ್ಮನ್ನು ನೋಡಿ ಅವರಿಗೆ ಕೃಪೆತೋರಿಸಿರಿ; ಯುದ್ಧದಿಂದ ಪ್ರತಿಯೊಬ್ಬನಿಗೆ ಹೆಣ್ಣನ್ನು ದೊರಕಿಸುವದು ನಮ್ಮಿಂದಾಗಲಿಲ್ಲವಲ್ಲಾ; ಮತ್ತು ನಿಮ್ಮ ಹೆಣ್ಣುಮಕ್ಕಳನ್ನು ನೀವೇ ಕೊಡಲಿಲ್ಲವಾದದರಿಂದ ನೀವು ನಿರಪರಾಧಿಗಳು ಎಂದು ಹೇಳಿ ಸಮಾಧಾನಪಡಿಸುವೆವು ಅಂದರು.",
			"23": "<sup>23</sup> ಅದರಂತೆಯೇ ಬೆನ್ಯಾಮೀನ್ಯರು ನಾಟ್ಯವಾಡುವದಕ್ಕೋಸ್ಕರ ಹೊರಗೆ ಬಂದಿದ್ದ ಕನ್ಯೆಯರಲ್ಲಿ ತಮ್ಮ ಸಂಖ್ಯೆಗೆ ಸರಿಯಾಗುವಷ್ಟು ಮಂದಿಯನ್ನು ಹೆಂಡತಿಯರನ್ನಾಗಿ ಹಿಡಿದುಕೊಂಡು ತಮ್ಮ ಸ್ವಾಸ್ತ್ಯಭೂಮಿಗೆ ಹೋಗಿ ಅಲ್ಲಿ ಪಟ್ಟಣಗಳನ್ನು ಕಟ್ಟಿ ವಾಸಮಾಡಿದರು.",
			"24": "<sup>24</sup> ಅನಂತರ ಇಸ್ರಾಯೇಲ್ಯರು ತಮ್ಮ ತಮ್ಮ ಕುಲಗೋತ್ರಗಳಿಗೆ ಸ್ವಾಸ್ತ್ಯವಾಗಿ ಸಿಕ್ಕಿದ ಪ್ರದೇಶಗಳಿಗೆ ಹಿಂದಿರುಗಿ ಹೋದರು.",
			"25": "<sup>25</sup> ಆ ಕಾಲದಲ್ಲಿ ಇಸ್ರಾಯೇಲ್ಯರೊಳಗೆ ಅರಸನಿರಲಿಲ್ಲ; ಪ್ರತಿಯೊಬ್ಬನೂ ತನ್ನ ಮನಸ್ಸಿಗೆ ಬಂದಂತೆ ನಡೆಯುತ್ತಿದ್ದನು."
		}
	}
};
module.exports = book;