var book = {
	"name": "1 ಸಮುವೇಲನು",
	"numChapters": 31,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ಎಫ್ರಾಯೀಮ್ ಬೆಟ್ಟದ ಸೀಮೆಯಲ್ಲಿನ ರಾಮಾತಯಿಮ್ ಚೋಫೀಮ್ ಊರಿನಲ್ಲಿ ಎಲ್ಕಾನನೆಂಬ ಒಬ್ಬ ಮನುಷ್ಯನಿದ್ದನು. ಇವನು ಯೆರೋಹಾಮನ ಮಗನೂ ಎಲೀಹುವಿನ ಮೊಮ್ಮಗನೂ ತೋಹುವಿನ ಮರಿಮಗನೂ ಆಗಿದ್ದನು. ತೋಹು ಎಂಬವನು ಎಫ್ರಾಯೀಮ್ಯನಾದ ಚೂಫನ ಮಗನು.",
			"2": "<sup>2</sup> ಎಲ್ಕಾನನಿಗೆ ಹನ್ನಳು, ಪೆನಿನ್ನಳು ಎಂಬ ಇಬ್ಬರು ಹೆಂಡತಿಯರಿದ್ದರು. ಪೆನಿನ್ನಳಿಗೆ ಮಕ್ಕಳಿದ್ದರು; ಹನ್ನಳಿಗೆ ಇರಲಿಲ್ಲ.",
			"3": "<sup>3</sup> ಅವನು ಪ್ರತಿವರುಷವೂ ಸೈನ್ಯಾಧೀಶ್ವರನಾದ ಯೆಹೋವನಿಗೆ ಯಜ್ಞವನ್ನು ಸಮರ್ಪಿಸಿ ಆತನನ್ನು ಆರಾಧಿಸುವದಕ್ಕೋಸ್ಕರ ತನ್ನ ಊರಿನಿಂದ ಶೀಲೋವಿಗೆ ಹೋಗುತ್ತಿದ್ದನು. ಅಲ್ಲಿ ಏಲಿಯ ಮಕ್ಕಳಾದ ಹೊಫ್ನಿ, ಫೀನೆಹಾಸ್ ಎಂಬವರು ಯೆಹೋವನ ಯಾಜಕರಾಗಿದ್ದರು.",
			"4": "<sup>4</sup> ಎಲ್ಕಾನನು ಯಜ್ಞವನ್ನರ್ಪಿಸಿದಾಗೆಲ್ಲಾ ತನ್ನ ಹೆಂಡತಿಯಾದ ಪೆನಿನ್ನಳಿಗೂ ಆಕೆಯ ಎಲ್ಲಾ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳಿಗೂ ಭಾಗಗಳನ್ನು ಕೊಡುತ್ತಿದ್ದನು.",
			"5": "<sup>5</sup> ಅವನು ಹನ್ನಳನ್ನು ಬಹಳವಾಗಿ ಪ್ರೀತಿಸಿದರೂ ಯೆಹೋವನು ಆಕೆಗೆ ಮಕ್ಕಳನ್ನು ಕೊಡಲಿಲ್ಲವಾದದರಿಂದ ಆಕೆಗೆ ಒಂದು ಭಾಗವನ್ನು ಮಾತ್ರ ಕೊಡುತ್ತಿದ್ದನು.",
			"6": "<sup>6</sup> ಇದಲ್ಲದೆ ಆಕೆಯ ಸವತಿಯು - ಯೆಹೋವನು ನಿನ್ನನ್ನು ಬಂಜೆಯನ್ನಾಗಿ ಮಾಡಿದ್ದಾನೆ ಎಂದು ಹೇಳಿ ಆಕೆಯನ್ನು ಕೆಣಕಿ ನೋಯಿಸುತ್ತಿದ್ದಳು.",
			"7": "<sup>7</sup> ಪ್ರತಿವರ್ಷವೂ ಯೆಹೋವನ ಮಂದಿರಕ್ಕೆ ಹೋದಾಗೆಲ್ಲಾ ಎಲ್ಕಾನನು ಹಾಗೆಯೇ ಮಾಡುತ್ತಿದ್ದದರಿಂದಲೂ ಪೆನಿನ್ನಳು ಆಕೆಯನ್ನು ರೇಗಿಸುತ್ತಿದ್ದದರಿಂದಲೂ ಹನ್ನಳು ಒಂದಾನೊಂದು ಸಾರಿ ಉಣ್ಣಲೊಲ್ಲದೆ ಅಳುತ್ತಾ ಇದ್ದಳು.",
			"8": "<sup>8</sup> ಆಗ ಆಕೆಯ ಗಂಡನಾದ ಎಲ್ಕಾನನು ಆಕೆಗೆ - ಹನ್ನಾ, ಏಕೆ ಅಳುತ್ತೀ? ಉಣ್ಣದಿರುವದಕ್ಕೇನು ಕಾರಣ? ನೀನು ವ್ಯಸನಪಡುವದೇಕೆ? ನಾನು ನಿನಗೆ ಹತ್ತು ಮಂದಿ ಮಕ್ಕಳಿಗಿಂತ ಹೆಚ್ಚಾಗಿದ್ದೇನಲ್ಲಾ ಅಂದನು.",
			"9": "<sup>9</sup> ಆಗ ಅವರು ಶೀಲೋವಿನಲ್ಲಿ ಅನ್ನಪಾನಗಳನ್ನು ತೆಗೆದುಕೊಂಡ ಮೇಲೆ ಹನ್ನಳು ಎದ್ದು ಯಾಜಕನಾದ ಏಲಿಯು ಯೆಹೋವನ ಮಂದಿರದ್ವಾರದ ನಿಲುವುಪಟ್ಟಿಗಳ ಬಳಿಯಲ್ಲಿ ಒಂದು ಪೀಠದ ಮೇಲೆ ಕೂತಿರುವಾಗ ಹೋಗಿ",
			"10": "<sup>10</sup> ಬಹು ದುಃಖದಿಂದ ಕಣ್ಣೀರು ಸುರಿಸುತ್ತಾ ಯೆಹೋವನನ್ನು ಕುರಿತು -",
			"11": "<sup>11</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೇ, ನಿನ್ನ ದಾಸಿಯ ದುಃಖವನ್ನು ಪರಾಂಬರಿಸು; ನನ್ನನ್ನು ತಿರಸ್ಕರಿಸದೆ ಕನಿಕರವಿಟ್ಟು ನನಗೊಬ್ಬ ಮಗನನ್ನು ಕೊಡಬೇಕು; ಅವನು ಜೀವದಿಂದಿರುವ ತನಕ ನಿನ್ನವನಾಗಿಯೇ ಇರುವ ಹಾಗೆ ನಿನಗೆ ಪ್ರತಿಷ್ಠಿಸಿಕೊಡುವೆನು; ಅವನ ತಲೆಯ ಮೇಲೆ ಕ್ಷೌರ ಕತ್ತಿಯನ್ನು ಬರಗೊಡುವದಿಲ್ಲ ಎಂದು ಪ್ರಾರ್ಥಿಸಿ ಹರಕೆಮಾಡಿದಳು.",
			"12": "<sup>12</sup> ಆಕೆಯು ಬಲು ಹೊತ್ತಿನವರೆಗೆ ಯೆಹೋವನ ಮುಂದೆ ಪ್ರಾರ್ಥನೆ ಮಾಡುತ್ತಿರುವಾಗ ಏಲಿಯು ಆಕೆಯ ಬಾಯನ್ನೇ ನೋಡುತ್ತಿದ್ದನು.",
			"13": "<sup>13</sup> ಆಕೆಯು ಹೃದಯದಲ್ಲಿ ಪ್ರಾರ್ಥಿಸುತ್ತಿದ್ದದರಿಂದ ತುಟಿಗಳು ಮಾತ್ರ ಅಲ್ಲಾಡುತ್ತಿದ್ದವು; ಶಬ್ದ ಕೇಳಿಸಲಿಲ್ಲ. ಆದದರಿಂದ ಈಕೆಯು ಮದ್ಯಪಾನಮಾಡಿದ್ದಾಳೆಂದು ಅವನು ನೆನಸಿ -",
			"14": "<sup>14</sup> ನಿನ್ನ ಅಮಲು ಇನ್ನೂ ಇಳಿಯಲಿಲ್ಲವೋ? ದ್ರಾಕ್ಷಾರಸದ ಮತ್ತು ನಿನ್ನನ್ನು ಬಿಟ್ಟುಹೋಗಲಿ ಅಂದನು.",
			"15": "<sup>15</sup> ಅದಕ್ಕೆ ಹನ್ನಳು - ಸ್ವಾಮೀ, ಹಾಗಲ್ಲ; ನಾನು ಬಹುದುಃಖಪೀಡಿತಳು; ದ್ರಾಕ್ಷಾರಸವನ್ನಾದರೂ ಬೇರೆ ಯಾವ ಮದ್ಯವನ್ನಾದರೂ ಕುಡಿದವಳಲ್ಲ. ನನ್ನ ಮನೋವೇದನೆಯನ್ನು ಯೆಹೋವನ ಮುಂದೆ ಹೊಯ್ದಿದ್ದೇನೆ.",
			"16": "<sup>16</sup> ನಿನ್ನ ದಾಸಿಯಾದ ನನ್ನನ್ನು ಅಯೋಗ್ಯಳೆಂದು ನೆನಸಬೇಡ; ಈವರೆಗೆ ನನ್ನ ಹೆಚ್ಚಾದ ಚಿಂತೆಯನ್ನೂ ವ್ಯಥೆಯನ್ನೂ ಅರಿಕೆಮಾಡಿಕೊಳ್ಳುತ್ತಿದ್ದೆನು ಎಂದು ಹೇಳಿದಳು.",
			"17": "<sup>17</sup> ಆಗ ಏಲಿಯು ಆಕೆಗೆ - ಸಮಾಧಾನದಿಂದ ಹೋಗು; ಇಸ್ರಾಯೇಲ್ ದೇವರು ನಿನ್ನ ಪ್ರಾರ್ಥನೆಯನ್ನು ನೆರವೇರಿಸಲಿ ಎನ್ನಲು ಹನ್ನಳು ಏಲಿಗೆ -",
			"18": "<sup>18</sup> ನಿನ್ನ ದಾಸಿಯ ಮೇಲೆ ಕಟಾಕ್ಷವಿರಲಿ ಎಂದು ಹೇಳಿ ಹೊರಟುಹೋಗಿ ಊಟಮಾಡಿದಳು. ಆಮೇಲೆ ಆಕೆಯ ಮೋರೆಯಲ್ಲಿ ದುಃಖವು ಕಾಣಲಿಲ್ಲ.",
			"19": "<sup>19</sup> ಅವರು ಮರುದಿನ ಬೆಳಿಗ್ಗೆ ಎದ್ದು ಯೆಹೋವನನ್ನು ಆರಾಧಿಸಿ ರಾಮದಲ್ಲಿದ್ದ ತಮ್ಮ ಮನೆಗೆ ಬಂದರು. ಎಲ್ಕಾನನು ತನ್ನ ಹೆಂಡತಿಯಾದ ಹನ್ನಳನ್ನು ಕೂಡಲು ಆಕೆಯು ಯೆಹೋವನ ಅನುಗ್ರಹದಿಂದ ಗರ್ಭಿಣಿಯಾಗಿ",
			"20": "<sup>20</sup> ದಿನತುಂಬಿದ ಮೇಲೆ ಒಬ್ಬ ಮಗನನ್ನು ಹೆತ್ತಳು. ಯೆಹೋವನನ್ನು ಬೇಡಿ ಪಡಕೊಂಡೆನೆಂದು ಹೇಳಿ ಆ ಮಗನಿಗೆ ಸಮುವೇಲನೆಂದು ಹೆಸರಿಟ್ಟಳು.",
			"21": "<sup>21</sup> ಎಲ್ಕಾನನು ಕುಟುಂಬಸಹಿತವಾಗಿ ಎಂದಿನಂತೆ ಯೆಹೋವನಿಗೆ ವಾರ್ಷಿಕಯಜ್ಞವನ್ನು ಅರ್ಪಿಸುವದಕ್ಕೂ ಹರಕೆಸಲ್ಲಿಸುವದಕ್ಕೂ ಹೋಗುವದಕ್ಕೆ ಸಿದ್ಧನಾಗಲು ಹನ್ನಳು -",
			"22": "<sup>22</sup> ಮಗನು ಮೊಲೆಬಿಡುವವರೆಗೂ ನಾನು ಬರುವದಿಲ್ಲ; ಅವನು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಕಾಣಿಸಿಕೊಂಡು ಯಾವಾಗಲೂ ಅಲ್ಲಿಯೇ ಇರುವ ಹಾಗೆ ಆಮೇಲೆ ತೆಗೆದುಕೊಂಡು ಬರುವೆನು ಎಂದು ಹೇಳಿದಳು.",
			"23": "<sup>23</sup> ಆಕೆಯ ಗಂಡನಾದ ಎಲ್ಕಾನನು ಆಕೆಗೆ - ನಿನಗೆ ಸರಿತೋರುವ ಪ್ರಕಾರ ಆಗಲಿ; ಮಗನು ಮೊಲೆಬಿಡುವವರೆಗೆ ಇರು. ಯೆಹೋವನು ತನ್ನ ವಾಕ್ಯವನ್ನು ದೃಢಪಡಿಸಲಿ ಅಂದನು. ಮಗನು ಮೊಲೆಬಿಡುವ ತನಕ ಹನ್ನಳು ಮನೆಯಲ್ಲೇ ಇದ್ದು ಅವನನ್ನು ಸಾಕಿದಳು.",
			"24": "<sup>24</sup> ಅನಂತರ ಆಕೆಯು ಮೂರು ವರುಷದ ಒಂದು ಹೋರಿಯನ್ನೂ ಮೂವತ್ತು ಸೇರು ಹಿಟ್ಟನ್ನೂ ಒಂದು ತಿತ್ತಿ ದ್ರಾಕ್ಷಾರಸವನ್ನೂ ತೆಗೆದುಕೊಂಡು ಪುತ್ರಸಹಿತವಾಗಿ ಶೀಲೋವಿನಲ್ಲಿದ್ದ ಯೆಹೋವನ ಮಂದಿರಕ್ಕೆ ಬಂದಳು; ಮಗನು ಇನ್ನೂ ಚಿಕ್ಕವನಾಗಿದ್ದನು.",
			"25": "<sup>25</sup> ಅವರು ಹೋರಿಯನ್ನು ಯಜ್ಞಮಾಡಿದ ಮೇಲೆ ಮಗನನ್ನು ಏಲಿಯ ಬಳಿಗೆ ತಂದರು.",
			"26": "<sup>26</sup> ಹನ್ನಳು ಏಲಿಗೆ - ಸ್ವಾಮೀ, ನಿನ್ನ ಜೀವದಾಣೆ; ಮೊದಲು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಪ್ರಾರ್ಥನೆಮಾಡುತ್ತಾ ಇಲ್ಲಿ ನಿನ್ನ ಹತ್ತಿರ ನಿಂತಿದ್ದ ಸ್ತ್ರೀಯು ನಾನೇ.",
			"27": "<sup>27</sup> ಆತನು ನನ್ನ ಪ್ರಾರ್ಥನೆಯ ಫಲವಾಗಿ ಅನುಗ್ರಹಿಸಿದ ಮಗನು ಇವನೇ;",
			"28": "<sup>28</sup> ನಾನು ಇವನನ್ನು ಯೆಹೋವನಿಗೇ ಒಪ್ಪಿಸಿಬಿಟ್ಟಿದ್ದೇನೆ. ಇವನು ಜೀವದಿಂದಿರುವ ತನಕ ಆತನಿಗೇ ಪ್ರತಿಷ್ಠಿತನಾಗಿರುವನು ಎಂದು ಹೇಳಿದಳು. ಆಮೇಲೆ ಅವರು ಯೆಹೋವನಿಗೆ ನಮಸ್ಕರಿಸಿದರು."
		},
		"2": {
			"1": "<sup>1</sup> ಹನ್ನಳ ವಿಜ್ಞಾಪನೆ - ನನ್ನ ಹೃದಯವು ಯೆಹೋವನಲ್ಲಿ ಉಲ್ಲಾಸಿಸುತ್ತದೆ; ನನ್ನ ಕೊಂಬು ಯೆಹೋವನಿಂದ ಎತ್ತಲ್ಪಟ್ಟಿದೆ. ಶತ್ರುಗಳ ಮುಂದೆ ನನ್ನ ಬಾಯಿ ತೆರೆದಿರುವದು. ನಿನ್ನ ರಕ್ಷಣೆಯಲ್ಲಿ ಆನಂದಿಸುತ್ತೇನೆ.",
			"2": "<sup>2</sup> ಯೆಹೋವನಂಥ ಪರಿಶುದ್ಧನು ಇಲ್ಲವೇ ಇಲ್ಲ; ನಿನ್ನ ಹೊರತು ದೇವರು ಯಾರು? ನಮ್ಮ ದೇವರೇ ಅಸಮಾನವಾದ ಆಶ್ರಯದುರ್ಗವು.",
			"3": "<sup>3</sup> ಇನ್ನು ಮುಂದೆ ಹಮ್ಮಿನಿಂದ ನುಡಿಯಬೇಡಿರಿ; ಸೊಕ್ಕಿನ ಮಾತು ನಿಮ್ಮ ಬಾಯಿಂದ ಹೊರಡದಿರಲಿ. ದೇವರಾದ ಯೆಹೋವನು ಸರ್ವಜ್ಞನು; ಆತನು ಮನುಷ್ಯರ ಕ್ರಿಯೆಗಳನ್ನು ತೂಗುವನು.",
			"4": "<sup>4</sup> ಶೂರರ ಬಿಲ್ಲುಗಳು ಮುರಿಯಲ್ಪಡುತ್ತವೆ, ಎಡವಿದವರು ಶೌರ್ಯವೆಂಬ ನಡುಕಟ್ಟನ್ನು ಬಿಗಿದಿದ್ದಾರೆ.",
			"5": "<sup>5</sup> ಹೊಟ್ಟೆತುಂಬಾ ಉಣ್ಣುತ್ತಿದ್ದವರು ಅನ್ನಕ್ಕೋಸ್ಕರ ಕೂಲಿಮಾಡುತ್ತಾರೆ. ಹಸಿವುಗೊಂಡವರು ಉಂಡು ಸುಖದಿಂದಿರುತ್ತಾರೆ. ಬಂಜೆಯು ಏಳು ಮಂದಿ ಮಕ್ಕಳನ್ನು ಹೆರುವಳು; ಮಕ್ಕಳುಳ್ಳವಳು ದಿಕ್ಕಿಲ್ಲದೆ ಹೋಗುವಳು.",
			"6": "<sup>6</sup> ಯೆಹೋವನೇ ಸಾಯಿಸುವವನೂ ಬದುಕಿಸುವವನೂ; ಪಾತಾಳದಲ್ಲಿ ದೊಬ್ಬುವವನೂ ಮೇಲಕ್ಕೆ ಬರಮಾಡುವವನೂ ಆತನೇ.",
			"7": "<sup>7</sup> ಬಡತನ ಸಿರಿತನಗಳನ್ನು ಕೊಡುವವನೂ ತಗ್ಗಿಸುವವನೂ ಹೆಚ್ಚಿಸುವವನೂ ಯೆಹೋವನೇ.",
			"8": "<sup>8</sup> ಆತನು ದೀನನನ್ನು ಧೂಳಿಯಿಂದ ಎತ್ತಿ ದರಿದ್ರನನ್ನು ತಿಪ್ಪೆಯಿಂದ ಎಬ್ಬಿಸಿ ಪ್ರಭುಗಳೊಂದಿಗೆ ಕುಳ್ಳಿರಿಸಿ ಅವನಿಗೆ ಮಹಿಮಾಸನವನ್ನು ಬಾಧ್ಯತೆಯಾಗಿ ಅನುಗ್ರಹಿಸುವವನಾಗಿದ್ದಾನೆ. ಭೂಮಿಯ ಆಧಾರಸ್ತಂಭಗಳು ಯೆಹೋವನವೇ; ಆತನೇ ಅವುಗಳ ಮೇಲೆ ಭೂಮಂಡಲವನ್ನು ಸ್ಥಾಪಿಸಿದ್ದಾನೆ.",
			"9": "<sup>9</sup> ಆತನು ತನ್ನ ಭಕ್ತರ ಹೆಜ್ಜೆಗಳನ್ನು ಕಾಯುವನು; ದುಷ್ಟರು ಅಂಧಕಾರದಲ್ಲಿ ಮುಳುಗಿ ಮೌನಹೊಂದುವರು. ಬಾಹುಬಲದಿಂದಲೇ ಯಾವನೂ ಜಯಹೊಂದಲಾರನು.",
			"10": "<sup>10</sup> ಯೆಹೋವನ ವಿರೋಧಿಗಳು ಮುರಿಯಲ್ಪಡುತ್ತಾರೆ; ಆತನು ಆಕಾಶದಿಂದ ಅವರ ಮೇಲೆ ಗರ್ಜಿಸುತ್ತಾನೆ. ಯೆಹೋವನು ಭೂಮಿಯ ಕಟ್ಟಕಡೆಯಲ್ಲಿರುವವರಿಗೂ ನ್ಯಾಯತೀರಿಸುವನು. ತಾನು ನೇಮಿಸಿದ ಅರಸನಿಗೆ ಬಲವನ್ನು ಅನುಗ್ರಹಿಸುವನು; ತನ್ನ ಅಭಿಷಿಕ್ತನ ಕೊಂಬನ್ನು ಉನ್ನತಮಾಡುವನು.",
			"11": "<sup>11</sup> ಎಲ್ಕಾನನು ರಾಮದಲ್ಲಿದ್ದ ತನ್ನ ಮನೆಗೆ ಹೋದನು; ಹುಡುಗನು ಯಾಜಕನಾದ ಏಲಿಯ ಕೈಕೆಳಗಿದ್ದುಕೊಂಡು ಯೆಹೋವನನ್ನು ಸೇವಿಸುತ್ತಿದ್ದನು.",
			"12": "<sup>12</sup> ಏಲಿಯ ಮಕ್ಕಳು ಬಹುದುಷ್ಟರಾಗಿದ್ದರು; ಅವರು ಯೆಹೋವನನ್ನು ಲಕ್ಷಿಸುತ್ತಿರಲಿಲ್ಲ.",
			"13": "<sup>13</sup> ಯಜ್ಞವನ್ನರ್ಪಿಸುವದಕ್ಕೆ ಬಂದ ಜನರಲ್ಲಿ ಈ ಯಾಜಕರು ನಡಿಸಿದ್ದೇನಂದರೆ - ಯಜ್ಞಮಾಂಸವು ಬೇಯುತ್ತಿರುವಾಗಲೇ ಯಾಜಕನ ಆಳು ಕೈಯಲ್ಲಿ ತ್ರಿಶೂಲವನ್ನು ಹಿಡಿದುಕೊಂಡು ಬಂದು",
			"14": "<sup>14</sup> ಅದನ್ನು ಆ ಮಾಂಸ ಬೇಯುತ್ತಿರುವ ಕೊಪ್ಪರಿಗೆಯಲ್ಲಾಗಲಿ, ಭಾಂಡದಲ್ಲಾಗಲಿ, ತಪ್ಪಲೆಯಲ್ಲಾಗಲಿ, ಗಡಿಗೆಯಲ್ಲಾಗಲಿ ಚುಚ್ಚಿ ಅದಕ್ಕೆ ಸಿಕ್ಕಿದ್ದೆಲ್ಲವನ್ನೂ ಯಾಜಕನಿಗೋಸ್ಕರ ತೆಗೆದುಕೊಳ್ಳುತ್ತಿದ್ದನು. ಶೀಲೋವಿಗೆ ಬರುವ ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರಿಗೂ ಹೀಗೆಯೇ ಮಾಡುತ್ತಿದ್ದರು.",
			"15": "<sup>15</sup> ಇದಲ್ಲದೆ ಯಾಜಕನ ಆಳು ಕೊಬ್ಬನ್ನು ಹೋಮಮಾಡುವದಕ್ಕಿಂತ ಮುಂಚೆಯೇ ಬಂದು ಯಜ್ಞವನ್ನರ್ಪಿಸುವವನಿಗೆ - ಯಾಜಕನಿಗೋಸ್ಕರ ಕರಿಯತಕ್ಕ ಮಾಂಸವನ್ನು ಕೊಡು; ನೀನು ಬೇಯಿಸಿದ ಮಾಂಸವನ್ನು ಅವನು ತೆಗೆದುಕೊಳ್ಳುವದಿಲ್ಲ; ಅವನಿಗೆ ಹಸೀ ಮಾಂಸವೇ ಬೇಕು ಅನ್ನುವನು.",
			"16": "<sup>16</sup> ಅರ್ಪಿಸುವವನು - ಮೊದಲು ಕೊಬ್ಬನ್ನು ಹೋಮಮಾಡಲಿ, ಅನಂತರ ನಿನಗೆ ಬೇಕಾದದ್ದನ್ನು ತೆಗೆದುಕೋ ಎಂದು ಹೇಳಿದಾಗ ಅವನು - ಈಗಲೇ ಕೊಡಬೇಕು: ಇಲ್ಲವಾದರೆ ಬಲಾತ್ಕಾರದಿಂದ ತೆಗೆದುಕೊಳ್ಳುವೆನು ಎನ್ನುವನು.",
			"17": "<sup>17</sup> ಹೀಗೆ ಆ ಯೌವನಸ್ಥರು ಯೆಹೋವನ ನೈವೇದ್ಯವನ್ನು ತುಚ್ಫೀಕರಿಸಿದ್ದರಿಂದ ಅವರ ಅಪರಾಧವು ಆತನ ದೃಷ್ಟಿಯಲ್ಲಿ ಅಧಿಕವಾಯಿತು.",
			"18": "<sup>18</sup> ಬಾಲಕನಾದ ಸಮುವೇಲನು ಏಫೋದೆಂಬ ನಾರುಮಡಿಯಂಗಿಯನ್ನು ತೊಟ್ಟುಕೊಂಡು ಯೆಹೋವನ ಸಾನ್ನಿಧ್ಯಸೇವೆಯನ್ನು ಮಾಡುತ್ತಿದ್ದನು.",
			"19": "<sup>19</sup> ಅವನ ತಾಯಿಯು ಪ್ರತಿವರುಷವೂ ಗಂಡನ ಜೊತೆಯಲ್ಲಿ ಯಜ್ಞವನ್ನರ್ಪಿಸುವದಕ್ಕೆ ಬಂದಾಗ ಒಂದು ಚಿಕ್ಕ ಅಂಗಿಯನ್ನು ಹೊಲಿದು ಅವನಿಗೆ ತಂದು ಕೊಡುತ್ತಿದ್ದಳು.",
			"20": "<sup>20</sup> ಏಲಿಯು ಎಲ್ಕಾನನಿಗೆ - ನೀನು ಯೆಹೋವನಿಗೆ ಒಪ್ಪಿಸಿಬಿಟ್ಟ ಮಗನಿಗೆ ಬದಲಾಗಿ ಆತನು ಈ ಸ್ತ್ರೀಯಿಂದ ನಿನಗೆ ಬೇರೆ ಮಕ್ಕಳನ್ನು ಕೊಡಲಿ ಎಂದು ಹೇಳಿ ಅವನನ್ನೂ ಅವನ ಹೆಂಡತಿಯನ್ನೂ ಆಶೀರ್ವದಿಸುತ್ತಿದ್ದನು. ಅನಂತರ ಅವರು ತಿರಿಗಿ ಸ್ವಸ್ಥಳಕ್ಕೆ ಹೋಗುತ್ತಿದ್ದರು.",
			"21": "<sup>21</sup> ಯೆಹೋವನು ಕಟಾಕ್ಷಿಸಿದದರಿಂದ ಹನ್ನಳಿಗೆ ಮೂರು ಮಂದಿ ಗಂಡು ಮಕ್ಕಳೂ ಇಬ್ಬರು ಹೆಣ್ಣುಮಕ್ಕಳೂ ಆದರು. ಬಾಲಕನಾಗಿದ್ದ ಸಮುವೇಲನು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲೇ ದೊಡ್ಡವನಾದನು.",
			"22": "<sup>22</sup> ಬಹುವೃದ್ಧನಾದ ಏಲಿಯು ತನ್ನ ಮಕ್ಕಳು ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ನಡಿಸುತ್ತಿರುವದನ್ನೂ ಅವರು ದೇವದರ್ಶನದ ಗುಡಾರದ ಬಾಗಲಿನಲ್ಲಿ ಸೇವೆಮಾಡುವ ಸ್ತ್ರೀಯರೊಡನೆ ಸಂಗಮಿಸುತ್ತಿರುವದನ್ನೂ ಕೇಳಿ ಅವರಿಗೆ -",
			"23": "<sup>23</sup> ಎಲ್ಲಾ ಜನರಿಂದಲೂ ನಿಮ್ಮ ದುಷ್ಕೃತ್ಯಗಳನ್ನು ಕೇಳುತ್ತಿರುತ್ತೇನೆ. ಯಾಕೆ ಹೀಗೆ ಮಾಡುತ್ತೀರಿ?",
			"24": "<sup>24</sup> ನನ್ನ ಮಕ್ಕಳೇ, ಹೀಗೆ ಮಾಡಬಾರದು, ನೀವು ಯೆಹೋವನ ಜನರನ್ನು ದುರ್ಮಾರ್ಗಕ್ಕೆ ಎಳೆಯುವವರಾಗಿದ್ದೀರೆಂದು ಕೇಳಿದ್ದೇನೆ; ಇದು ಒಳ್ಳೇದಲ್ಲ.",
			"25": "<sup>25</sup> ಮನುಷ್ಯನು ಮನುಷ್ಯನಿಗೆ ಅಪರಾಧ ಮಾಡಿದರೆ ದೇವರು ಮಧ್ಯಸ್ಥಿಕೆಯನ್ನು ವಹಿಸುವನು; ಮನುಷ್ಯನು ಯೆಹೋವನಿಗೆ ಅಪರಾಧಮಾಡಿದರೆ ವಹಿಸುವವರಾರು ಎಂದು ಎಚ್ಚರಿಸಿದರೂ ಅವರು ತಮ್ಮ ತಂದೆಯ ಮಾತಿಗೆ ಕಿವಿಗೊಡಲಿಲ್ಲ. ಯೆಹೋವನು ಅವರನ್ನು ಸಾಯಿಸಬೇಕೆಂದಿದ್ದನಷ್ಟೆ.",
			"26": "<sup>26</sup> ಬಾಲಕನಾದ ಸಮುವೇಲನಾದರೋ ಬೆಳೆಯುತ್ತಾ ಬಂದ ಹಾಗೆಲ್ಲಾ ಯೆಹೋವನ ಮತ್ತು ಮನುಷ್ಯರ ದಯೆಗೆ ಪಾತ್ರನಾದನು.",
			"27": "<sup>27</sup> ದೇವರ ಮನುಷ್ಯನೊಬ್ಬನು ಏಲಿಯ ಬಳಿಗೆ ಬಂದು ಅವನಿಗೆ - ಯೆಹೋವನು ನಿನಗೆ ಹೇಳುವದೇನಂದರೆ - ಇಸ್ರಾಯೇಲ್ಯರು ಐಗುಪ್ತದಲ್ಲಿ ಫರೋಹನ ದಾಸತ್ವದಲ್ಲಿದ್ದಾಗ ನಾನು ನಿನ್ನ ಗೋತ್ರದವರಿಗೆ ಪ್ರತ್ಯಕ್ಷನಾಗಿ",
			"28": "<sup>28</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಎಲ್ಲಾ ಕುಟುಂಬಗಳಿಂದ ಅವರನ್ನೇ ಯಾಜಕೋದ್ಯೋಗಕ್ಕೆ ಅಂದರೆ ಯಜ್ಞವನ್ನರ್ಪಿಸುವದಕ್ಕೂ ಧೂಪಹಾಕುವದಕ್ಕೂ ನನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಏಫೋದನ್ನು ಧರಿಸಿಕೊಳ್ಳುವದಕ್ಕೂ ಆರಿಸಿಕೊಂಡು ಅವರಿಗೆ ಇಸ್ರಾಯೇಲ್ಯರ ಯಜ್ಞಶೇಷದ ಹಕ್ಕನ್ನು ಅನುಗ್ರಹಿಸಿದೆನು.",
			"29": "<sup>29</sup> ನನ್ನ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಾಯೇಲ್ಯರು ನನ್ನ ಆಜ್ಞಾನುಸಾರವಾಗಿ ನನ್ನ ಮಂದಿರಕ್ಕೆ ತರುವ ಯಜ್ಞನೈವೇದ್ಯಗಳ ಘನತೆಯನ್ನು ನೀವು ಭಂಗಪಡಿಸಿ ಅವುಗಳ ಶ್ರೇಷ್ಠ ಭಾಗಗಳಿಂದ ನಿಮ್ಮನ್ನು ಕೊಬ್ಬಿಸಿಕೊಳ್ಳುವದೇಕೆ? ನೀನು ನನ್ನನ್ನು ಗೌರವಿಸುವದಕ್ಕಿಂತ ಹೆಚ್ಚಾಗಿ ನಿನ್ನ ಮಕ್ಕಳನ್ನೇ ಗೌರವಿಸುವದು ಸರಿಯೋ?",
			"30": "<sup>30</sup> ನಿನ್ನ ಗೋತ್ರದವರೂ ಸಂತಾನದವರೂ ನಿರಂತರವೂ ನನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸೇವೆಮಾಡಬಹುದು ಎಂದು ಆಜ್ಞಾಪಿಸಿದ ಇಸ್ರಾಯೇಲ್‍ದೇವರಾದ ಯೆಹೋವನೆಂಬ ನಾನು ಈಗ ತಿಳಿಸುವದೇನಂದರೆ - ಅದು ನನಗೆ ದೂರವಾಗಿರಲಿ; ನನ್ನನ್ನು ಸನ್ಮಾನಿಸುವವರನ್ನು ಸನ್ಮಾನಿಸುವೆನು; ನನ್ನನ್ನು ತಿರಸ್ಕರಿಸುವವರು ತಿರಸ್ಕಾರಹೊಂದುವರು.",
			"31": "<sup>31</sup> ನಾನು ನಿನ್ನ ಮತ್ತು ನಿನ್ನ ಮನೆಯವರ ಬಾಹುಬಲವನ್ನು ಮುರಿಯುವ ದಿನಗಳು ಬರುವವು. ನಿನ್ನ ಮನೆಯಲ್ಲಿ ಯಾರೂ ಪೂರ್ಣಾಯುಷ್ಯವನ್ನು ಮುಟ್ಟುವದಿಲ್ಲ.",
			"32": "<sup>32</sup> ನಿನ್ನ ವೈರಿಯು ನನ್ನ ಮಂದಿರದಲ್ಲಿ ಸೇವೆಮಾಡುವದನ್ನು ನೋಡುವಿ. ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಸರ್ವಶುಭವನ್ನು ದಯಪಾಲಿಸುವೆನು; ಆದರೆ ನಿನ್ನ ಮನೆಯಲ್ಲಿ ಯಾರೂ ಪೂರ್ಣಾಯುಷ್ಯವನ್ನು ಮುಟ್ಟುವದೇ ಇಲ್ಲ.",
			"33": "<sup>33</sup> ನಾನು ನನ್ನ ಸಾನ್ನಿಧ್ಯಸೇವೆಯಿಂದ ತೆಗೆದುಹಾಕದೆ ಒಬ್ಬನನ್ನಾದರೂ ಉಳಿಸಿದರೆ ಅವನು ನಿನ್ನ ಕಣ್ಗೇಡಿಗೂ ಮನೋವ್ಯಥೆಗೂ ಕಾರಣನಾಗುವನು. ನಿನ್ನ ಸಂತಾನದವರೆಲ್ಲರೂ ಕತ್ತಿಯಿಂದ ಸಂಹೃತರಾಗುವರು.",
			"34": "<sup>34</sup> ಇದೆಲ್ಲಾ ನೆರವೇರುವದೆಂಬದಕ್ಕೆ ನಿನ್ನ ಮಕ್ಕಳಾದ ಹೊಫ್ನಿ ಫೀನೆಹಾಸರು ಒಂದೇ ದಿವಸದಲ್ಲಿ ಸಾಯುವದೇ ಗುರುತಾಗಿರುವದು.",
			"35": "<sup>35</sup> ನನ್ನ ದೃಷ್ಟಿಗೂ ಮನಸ್ಸಿಗೂ ಸರಿಯಾದದ್ದನ್ನೇ ಮಾಡುವ ಒಬ್ಬ ನಂಬಿಗಸ್ತನಾದ ಯಾಜಕನನ್ನು ಎಬ್ಬಿಸುವೆನು; ಅವನಿಗೆ ಶಾಶ್ವತಗೃಹವನ್ನು ಅನುಗ್ರಹಿಸುವೆನು. ಅವನು ನನ್ನ ಅಭಿಷಿಕ್ತನ ಬಳಿಯಲ್ಲಿ ಸದಾಕಾಲ ಸೇವೆಮಾಡುವನು.",
			"36": "<sup>36</sup> ನಿನ್ನ ಮನೆಯಲ್ಲಿ ಉಳಿದವರು ಬಂದು ಅವನ ಮುಂದೆ ಅಡ್ಡಬಿದ್ದು - ನಮಗೆ ಒಂದು ದುಡ್ಡನ್ನೂ ರೊಟ್ಟಿಯ ಚೂರನ್ನೂ ಕೊಡು; ಒಂದು ತುತ್ತು ಅನ್ನಕ್ಕೆ ಮಾರ್ಗವಾಗುವ ಹಾಗೆ ದೇವಸ್ಥಾನದ ಸೇವೆಯಲ್ಲಿ ಯಾವದಕ್ಕಾದರೂ ನಮ್ಮನ್ನು ಸೇರಿಸಿಕೋ ಎಂದು ಅವನನ್ನು ಬೇಡಿಕೊಳ್ಳುವರು ಎಂಬದೇ ಅಂದನು."
		},
		"3": {
			"1": "<sup>1</sup> ಬಾಲಕನಾದ ಸಮುವೇಲನು ಏಲಿಯ ಕೈಕೆಳಗಿದ್ದುಕೊಂಡು ಯೆಹೋವನ ಸೇವೆಮಾಡುತ್ತಿದ್ದನಷ್ಟೆ. ಆ ಕಾಲದಲ್ಲಿ ದೇವೋಕ್ತಿಗಳು ವಿರಳವಾಗಿದ್ದವು, ದೇವದರ್ಶನಗಳು ಅಪರೂಪವಾಗಿದ್ದವು.",
			"2": "<sup>2</sup> ಏಲಿಯ ಕಣ್ಣುಗಳು ದಿನದಿನಕ್ಕೆ ಮೊಬ್ಬಾಗುತ್ತಾ ಬಂದು ಚೆನ್ನಾಗಿ ಕಾಣದೆಹೋಗಿದ್ದವು. ಅವನು ಒಂದಾನೊಂದು ರಾತ್ರಿ ತನ್ನ ಸ್ಥಳದಲ್ಲಿ ಮಲಗಿದ್ದನು;",
			"3": "<sup>3</sup> ಸಮುವೇಲನು ಯೆಹೋವನ ಮಂದಿರದಲ್ಲಿ ದೇವರ ಮಂಜೂಷವಿದ್ದ ಸ್ಥಳದಲ್ಲಿ ಮಲಗಿದ್ದನು. ಆಗ ದೇವದೀಪವು ಇನ್ನೂ ಉರಿಯುತ್ತಿರುವಾಗಲೇ",
			"4": "<sup>4</sup> ಯೆಹೋವನು ಸಮುವೇಲನನ್ನು ಕರೆದನು; ಸಮುವೇಲನು ಬಂದೆನೆಂದು ಉತ್ತರಕೊಟ್ಟು ಒಡನೆ ಏಲಿಯ ಬಳಿಗೆ ಹೋಗಿ -",
			"5": "<sup>5</sup> ಇಗೋ ಬಂದಿದ್ದೇನೆ; ನೀನು ನನ್ನನ್ನು ಕರೆದಿಯಲ್ಲಾ ಎನ್ನಲು ಏಲಿಯು - ನಾನು ನಿನ್ನನ್ನು ಕರೆಯಲಿಲ್ಲ, ಹೋಗಿ ಮಲಗಿಕೋ ಅಂದನು. ಸಮುವೇಲನು ಹೋಗಿ ಮಲಗಿದನು.",
			"6": "<sup>6</sup> ಯೆಹೋವನು ತಿರಿಗಿ - ಸಮುವೇಲನೇ ಎಂದು ಕರೆದನು. ಸಮುವೇಲನು ಕೂಡಲೆ ಎದ್ದು ಏಲಿಯ ಬಳಿಗೆ ಹೋಗಿ - ಇಗೋ, ಬಂದಿದ್ದೇನೆ; ನೀನು ನನ್ನನ್ನು ಕರೆದಿಯಲ್ಲಾ ಅನ್ನಲು ಅವನು - ನನ್ನ ಮಗನೇ, ನಾನು ನಿನ್ನನ್ನು ಕರೆಯಲಿಲ್ಲಾ, ಹೋಗಿ ಮಲಗಿಕೋ ಎಂದನು.",
			"7": "<sup>7</sup> ಆವರೆಗೂ ಸಮುವೇಲನಿಗೆ ಯೆಹೋವನ ಅನುಭವವಿರಲಿಲ್ಲ; ಅವನು ದೈವೋತ್ತರಗಳನ್ನು ಹೊಂದಿರಲಿಲ್ಲ.",
			"8": "<sup>8</sup> ಯೆಹೋವನು ಸಮುವೇಲನನ್ನು ಮೂರನೆಯ ಸಾರಿ ಕರೆದನು. ಅವನು ತಟ್ಟನೆ ಏಲಿಯ ಹತ್ತಿರ ಹೋಗಿ - ಇಗೋ, ಬಂದಿದ್ದೇನೆ, ನನ್ನನ್ನು ಕರೆದಿಯಲ್ಲಾ ಅನ್ನಲು ಹುಡುಗನನ್ನು ಕರೆದವನು ಯೆಹೋವನೇ ಎಂದು ಏಲಿಯು ತಿಳಿದು ಅವನಿಗೆ - ಹೋಗಿ ಮಲಗಿಕೋ;",
			"9": "<sup>9</sup> ತಿರಿಗಿ ಶಬ್ದವಾದರೆ ಯೆಹೋವನೇ, ಅಪ್ಪಣೆಯಾಗಲಿ, ನಿನ್ನ ದಾಸನು ಕಾದಿದ್ದಾನೆ ಎಂದು ಹೇಳು ಅಂದನು. ಸಮುವೇಲನು ತಿರಿಗಿ ಹೋಗಿ ತನ್ನ ಸ್ಥಳದಲ್ಲಿ ಮಲಗಿಕೊಳ್ಳಲು",
			"10": "<sup>10</sup> ಯೆಹೋವನು ಪ್ರತ್ಯಕ್ಷನಾಗಿ ಮುಂಚಿನಂತೆ ಸಮುವೇಲನೇ, ಸಮುವೇಲನೇ ಅಂದನು. ಸಮುವೇಲನು - ಅಪ್ಪಣೆಯಾಗಲಿ, ನಿನ್ನ ದಾಸನು ಕಾದಿದ್ದಾನೆ ಎಂದನು.",
			"11": "<sup>11</sup> ಯೆಹೋವನು ಸಮುವೇಲನಿಗೆ - ನಾನು ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಒಂದು ವಿಶೇಷ ಕಾರ್ಯವನ್ನು ನಡಿಸುವೆನು; ಅದರ ವಿಷಯ ಕೇಳುವವರ ಎರಡು ಕಿವಿಗಳೂ ಮೊರ್ರೆನ್ನುವವು.",
			"12": "<sup>12</sup> ಏಲಿಯ ಮನೆಯನ್ನು ಕುರಿತು ತಿಳಿಸಿದ್ದೆಲ್ಲವನ್ನೂ ಆ ದಿನದಲ್ಲಿ ತಪ್ಪದೆ ನೆರವೇರಿಸುವೆನು.",
			"13": "<sup>13</sup> ತನ್ನ ಮಕ್ಕಳು ದೇವದೂಷಕರೆಂದು ಅವನಿಗೆ ತಿಳಿದುಬಂದರೂ ಅವನು ಅವರನ್ನು ಗದರಿಸಲಿಲ್ಲ. ಈ ಪಾಪದ ನಿಮಿತ್ತವಾಗಿ ಅವನ ಮನೆಯನ್ನು ನಿತ್ಯದಂಡನೆಗೆ ಗುರಿಮಾಡಿದ್ದೇನೆಂದು ಅವನಿಗೆ ಮೊದಲೇ ತಿಳಿಸಿದೆನು.",
			"14": "<sup>14</sup> ಏಲಿಯ ಮನೆಯವರ ಅಪರಾಧಕ್ಕೆ ಯಜ್ಞ ನೈವೇದ್ಯಗಳಿಂದಲೂ ಎಂದಿಗೂ ಪ್ರಾಯಶ್ಚಿತ್ತವಾಗುವದಿಲ್ಲವೆಂಬದಾಗಿ ಆಣೆಯಿಟ್ಟಿದ್ದೇನೆ ಎಂದು ಹೇಳಿದನು.",
			"15": "<sup>15</sup> ಅನಂತರ ಸಮುವೇಲನು ಮಲಗಿ ಹೊತ್ತಾರೆಯಲ್ಲೆದ್ದು ಯೆಹೋವಮಂದಿರದ ಕದಗಳನ್ನು ತೆರೆದನು; ಅವನು ತಾನು ಕಂಡದ್ದನ್ನು ಏಲಿಗೆ ತಿಳಿಸುವದಕ್ಕೆ ಭಯಪಟ್ಟನು.",
			"16": "<sup>16</sup> ಆದರೆ ಏಲಿಯು - ನನ್ನ ಮಗನೇ, ಸಮುವೇಲನೇ ಎಂದು ಕರೆಯಲು ಅವನು - ಇಗೋ, ಇದ್ದೇನೆ ಅಂದನು.",
			"17": "<sup>17</sup> ಆಗ ಏಲಿಯು ಅವನಿಗೆ - ಯೆಹೋವನು ನಿನಗೆ ತಿಳಿಸಿದ್ದೇನು? ಆತನ ಮಾತುಗಳಲ್ಲಿ ಒಂದನ್ನಾದರೂ ಮುಚ್ಚಬೇಡ; ಮುಚ್ಚಿದರೆ ಆತನು ಬೇಕಾದ ಹಾಗೆ ನಿನ್ನನ್ನು ದಂಡಿಸಲಿ ಎಂದು ಹೇಳಲು",
			"18": "<sup>18</sup> ಸಮುವೇಲನು ಒಂದನ್ನೂ ಮುಚ್ಚದೆ ಎಲ್ಲವನ್ನೂ ತಿಳಿಸಿದನು; ಏಲಿಯು ಅದನ್ನು ಕೇಳಿ - ಆತನು ಯೆಹೋವನು; ತನಗೆ ಸರಿಕಾಣುವದನ್ನು ಮಾಡಲಿ ಅಂದನು.",
			"19": "<sup>19</sup> ಸಮುವೇಲನು ದೊಡ್ಡವನಾಗುತ್ತಾ ಬಂದನಷ್ಟೆ. ಯೆಹೋವನು ಅವನೊಡನೆ ಇದ್ದದರಿಂದ ಅವನ ಪ್ರವಾದನೆಗಳಲ್ಲಿ ಒಂದೂ ಬಿದ್ದುಹೋಗಲಿಲ್ಲ.",
			"20": "<sup>20</sup> ಯೆಹೋವನು ಅವನನ್ನು ತನ್ನ ಪ್ರವಾದಿಯನ್ನಾಗಿ ನೇಮಿಸಿಕೊಂಡದ್ದು ದಾನ್ ಊರಿನಿಂದ ಬೇರ್ಷೆಬದವರೆಗಿದ್ದ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಗೊತ್ತಾಯಿತು.",
			"21": "<sup>21</sup> ಯೆಹೋವನು ಶೀಲೋವಿನಲ್ಲಿ ದರ್ಶನಕೊಡುವದಕ್ಕೆ ಪ್ರಾರಂಭಿಸಿ ತನ್ನ ವಾಕ್ಯದಿಂದ ಸಮುವೇಲನಿಗೆ ತನ್ನನ್ನು ಪ್ರಕಟಿಸಿಕೊಳ್ಳುತ್ತಾ ಬಂದನು."
		},
		"4": {
			"1": "<sup>1</sup> ಸಮುವೇಲನು ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರಿಗೆ ದೈವೋತ್ತರಗಳನ್ನು ತಿಳಿಸುತ್ತಿದ್ದನು. ಇಸ್ರಾಯೇಲ್ಯರು ಫಿಲಿಷ್ಟಿಯರಿಗೆ ವಿರೋಧವಾಗಿ ಯುದ್ಧಕ್ಕೆ ಹೊರಟು ಎಬೆನೆಜೆರಿನ ಸಮೀಪದಲ್ಲಿ ಪಾಳೆಯಮಾಡಿಕೊಂಡರು; ಫಿಲಿಷ್ಟಿಯರು ಬಂದು ಅಫೇಕಿನಲ್ಲಿ ಇಳುಕೊಂಡರು.",
			"2": "<sup>2</sup> ಇವರು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಎದುರಾಗಿ ವ್ಯೂಹಕಟ್ಟಿ ಯುದ್ಧಮಾಡಲು ಇಸ್ರಾಯೇಲ್ಯರು ಸೋತರು; ಫಿಲಿಷ್ಟಿಯರು ರಣರಂಗದಲ್ಲಿ ಸುಮಾರು ನಾಲ್ಕು ಸಾವಿರ ಮಂದಿ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಸಂಹರಿಸಿದರು.",
			"3": "<sup>3</sup> ಇಸ್ರಾಯೇಲ್ಯರು ತಮ್ಮ ಪಾಳೆಯಕ್ಕೆ ಹಿಂದಿರುಗಿದ ಮೇಲೆ ಅವರ ಹಿರಿಯರು ಅವರಿಗೆ - ಯೆಹೋವನು ಈ ಹೊತ್ತು ನಮ್ಮನ್ನು ಫಿಲಿಷ್ಟಿಯರಿಂದ ಅಪಜಯಪಡಿಸಿದ್ದೇಕೆ? ಶೀಲೋವಿನಿಂದ ಯೆಹೋವನ ಒಡಂಬಡಿಕೆಯ ಮಂಜೂಷವನ್ನು ತರಿಸೋಣ; ಆತನು ನಮ್ಮ ಮಧ್ಯದಲ್ಲಿ ಬಂದು ಶತ್ರುಗಳ ಕೈಗೆ ನಮ್ಮನ್ನು ತಪ್ಪಿಸಲಿ ಎಂದು ಆಲೋಚನೆ ಹೇಳಿದ್ದರಿಂದ",
			"4": "<sup>4</sup> ಅವರು ಜನರನ್ನು ಕಳುಹಿಸಿ ಕೆರೂಬಿಗಳ ಮಧ್ಯದಲ್ಲಿ ಆಸೀನನಾಗಿರುವ ಸೈನ್ಯಾಧೀಶ್ವರ ಯೆಹೋವನ ಒಡಂಬಡಿಕೆಯ ಮಂಜೂಷವನ್ನು ತರಿಸಿದರು. ಏಲಿಯ ಇಬ್ಬರು ಮಕ್ಕಳಾದ ಹೊಫ್ನಿ, ಫೀನೆಹಾಸ್ ಎಂಬವರು ದೇವರ ಒಡಂಬಡಿಕೆಯ ಮಂಜೂಷದ ಸಂಗಡ ಇದ್ದರು.",
			"5": "<sup>5</sup> ಯೆಹೋವನ ಒಡಂಬಡಿಕೆಯ ಮಂಜೂಷವು ಪಾಳೆಯಕ್ಕೆ ಬಂದಾಗ ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರೂ ಭೂಮಿಯು ಪ್ರತಿಧ್ವನಿಕೊಡುವಷ್ಟು ಆರ್ಭಟಿಸಿದರು.",
			"6": "<sup>6</sup> ಫಿಲಿಷ್ಟಿಯರು ಇದನ್ನು ಕೇಳಿ - ಇಬ್ರಿಯರ ಪಾಳೆಯದಲ್ಲಿ ಇಂಥ ಆರ್ಭಟಕ್ಕೇನು ಕಾರಣವೆಂದು ವಿಚಾರಿಸುವಲ್ಲಿ ಯೆಹೋವನ ಮಂಜೂಷವು ಪಾಳೆಯದಲ್ಲಿ ಬಂದದ್ದೇ ಕಾರಣವೆಂದು ಗೊತ್ತಾಯಿತು.",
			"7": "<sup>7</sup> ದೇವರು ಇವರ ಪಾಳೆಯಕ್ಕೆ ಬಂದಿದ್ದಾನಲ್ಲಾ ಎಂದು ಫಿಲಿಷ್ಟಿಯರು ಬಹಳವಾಗಿ ಭಯಪಟ್ಟು - ಅಯ್ಯೋ, ಮುಂಚೆ ಹೀಗಿದ್ದಿಲ್ಲ.",
			"8": "<sup>8</sup> ಅಯ್ಯೋ, ಪ್ರತಾಪವುಳ್ಳ ಈ ದೇವರುಗಳ ಕೈಯಿಂದ ನಮ್ಮನ್ನು ಬಿಡಿಸುವವರಾರು? ಐಗುಪ್ತ್ಯರನ್ನು ಅರಣ್ಯದಲ್ಲಿ ಸಂಪೂರ್ಣವಾಗಿ ನಾಶಮಾಡಿಬಿಟ್ಟ ದೇವರುಗಳು ಇವರೇ ಅಲ್ಲವೋ!",
			"9": "<sup>9</sup> ಫಿಲಿಷ್ಟಿಯರೇ, ಬಲಗೊಳ್ಳಿರಿ, ಶೂರರಾಗಿರ್ರಿ; ನಿಮಗೆ ದಾಸರಾಗಿದ್ದ ಇಬ್ರಿಯರಿಗೆ ನೀವೇ ದಾಸರಾದೀರಿ.",
			"10": "<sup>10</sup> ಆದಕಾರಣ ಶೌರ್ಯದಿಂದ ಯುದ್ಧಮಾಡಬೇಕು ಎಂದು ಹೇಳಿಕೊಂಡು ಯುದ್ಧಮಾಡಲು ಇಸ್ರಾಯೇಲ್ಯರು ಸೋತು ತಮ್ಮ ತಮ್ಮ ಮನೆಗಳಿಗೆ ಓಡಿಹೋದರು. ಮಹಾಸಂಹಾರವಾಯಿತು; ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಮೂವತ್ತು ಸಾವಿರ ಕಾಲಾಳುಗಳು ಮಡಿದರು.",
			"11": "<sup>11</sup> ದೇವರ ಮಂಜೂಷವು ಶತ್ರುವಶವಾಯಿತು; ಏಲಿಯ ಮಕ್ಕಳಾದ ಹೊಫ್ನಿ ಫೀನೆಹಾಸರು ಹತರಾದರು.",
			"12": "<sup>12</sup> ಅದೇ ದಿವಸ ಒಬ್ಬ ಬೆನ್ಯಾಮೀನ್ಯನು ರಣರಂಗದಿಂದ ತಪ್ಪಿಸಿಕೊಂಡು ತನ್ನ ಬಟ್ಟೆಗಳನ್ನು ಹರಿದುಕೊಂಡು ತಲೆಯ ಮೇಲೆ ಮಣ್ಣುಹಾಕಿಕೊಂಡು",
			"13": "<sup>13</sup> ಶೀಲೋವಿಗೆ ಬಂದು ನಡೆದದ್ದನ್ನು ತಿಳಿಸಲಾಗಿ ಜನರೆಲ್ಲಾ ಗೋಳಾಡತೊಡಗಿದರು. ದೇವರ ಮಂಜೂಷವೇನಾಯಿತೋ ಎಂಬ ಚಿಂತೆಯಿಂದ ಮಂದಿರದ್ವಾರದ ಬಳಿಯಲ್ಲಿ ಆಸೀನನಾಗಿ ದಾರಿಯನ್ನು ನೋಡುತ್ತಿದ್ದ ಏಲಿಯು ಆ ದುಃಖ ಧ್ವನಿಯನ್ನು ಕೇಳಿ",
			"14": "<sup>14</sup> ಈ ಗದ್ದಲವೇನೆಂದು ವಿಚಾರಿಸಲು ಒಡನೆ ಆ ಮನುಷ್ಯನು ಏಲಿಯ ಬಳಿಗೆ ಬಂದು ಎಲ್ಲವನ್ನೂ ತಿಳಿಸಿದನು.",
			"15": "<sup>15</sup> ಏಲಿಯು ತೊಂಭತ್ತೆಂಟು ವರುಷದವನು; ಕಣ್ಣುಗಳು ಇಂಗಿಹೋಗಿ ಕುರುಡನಾಗಿದ್ದನು.",
			"16": "<sup>16</sup> ಆ ಮನುಷ್ಯನು ಏಲಿಗೆ - ನಾನು ರಣರಂಗದಲ್ಲಿದ್ದವನು; ಈ ಹೊತ್ತೇ ಅಲ್ಲಿಂದ ಓಡಿಬಂದೆನು ಅನ್ನಲು ಏಲಿಯು ಅವನನ್ನು - ನನ್ನ ಮಗನೇ, ವರ್ತಮಾನವೇನು ಎಂದು ಕೇಳಿದನು.",
			"17": "<sup>17</sup> ಅವನು ಏಲಿಗೆ - ಇಸ್ರಾಯೇಲ್ಯರು ಫಿಲಿಷ್ಟಿಯರಿಗೆ ಬೆನ್ನು ತೋರಿಸಿದರು; ಮಹಾಸಂಹಾರವಾಯಿತು. ನಿನ್ನ ಇಬ್ಬರು ಮಕ್ಕಳಾದ ಹೊಫ್ನಿ ಫೀನೆಹಾಸರು ಸತ್ತರು; ದೇವರ ಮಂಜೂಷವು ಶತ್ರುವಶವಾಯಿತು ಎಂದು ತಿಳಿಸಿದನು.",
			"18": "<sup>18</sup> ಮುದುಕನೂ ಸ್ಥೂಲಕಾಯನೂ ಆದ ಏಲಿಯು ದೇವರ ಒಡಂಬಡಿಕೆಯ ಮಂಜೂಷದ ವರ್ತಮಾನವನ್ನು ಕೇಳಿದೊಡನೆ ಪೀಠದಿಂದ ಹಿಂದಕ್ಕೆ ಬಾಗಲಿನ ಕಡೆಗೆ ಬಿದ್ದು ಕುತ್ತಿಗೆ ಮುರಿದು ಸತ್ತನು. ಅವನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ನಾಲ್ವತ್ತು ವರುಷ ಪಾಲಿಸಿದನು.",
			"19": "<sup>19</sup> ಫೀನೆಹಾಸನ ಹೆಂಡತಿಯಾದ ಅವನ ಸೊಸೆಯು ದಿನತುಂಬಿದ ಗರ್ಭಿಣಿಯಾಗಿದ್ದು ದೇವರ ಮಂಜೂಷವು ಶತ್ರುವಶವಾದದ್ದನ್ನೂ ಮಾವನೂ ಗಂಡನೂ ಹತರಾದದ್ದನ್ನೂ ಕೇಳಿದೊಡನೆ ಪ್ರಸವವೇದನೆಯುಂಟಾಗಿ ಮಗುವನ್ನು ಹೆತ್ತಳು.",
			"20": "<sup>20</sup> ಆಕೆಯು ಸಾಯುವ ಸಮಯದಲ್ಲಿ ಬಳಿಯಲ್ಲಿ ನಿಂತಿದ್ದ ಸ್ತ್ರೀಯರು - ಭಯಪಡಬೇಡ, ಮಗನನ್ನು ಹೆತ್ತಿದ್ದೀ ಎಂದು ಹೇಳಿದರು. ಆಕೆಯು ಅವರ ಮಾತಿಗೆ ಲಕ್ಷ್ಯಕೊಡಲಿಲ್ಲ, ಪ್ರತ್ಯುತ್ತರ ಕೊಡಲಿಲ್ಲ.",
			"21": "<sup>21</sup> ದೇವರ ಮಂಜೂಷವು ಶತ್ರುವಶವಾದದರಿಂದಲೂ ಮಾವನೂ ಗಂಡನೂ ಮೃತಿ ಹೊಂದಿದರಿಂದಲೂ - ಮಹಿಮೆಯು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಬಿಟ್ಟುಹೋಯಿತೆಂದು ಹೇಳಿ ಆ ಕೂಸಿಗೆ ಈಕಾಬೋದ್ ಎಂದು ಹೆಸರಿಟ್ಟಳು.",
			"22": "<sup>22</sup> ದೇವರ ಮಂಜೂಷವು ಶತ್ರುವಶವಾದದರಿಂದಲೇ ಇಸ್ರಾಯೇಲ್ಯರ ಮಹಿಮೆಯು ಹೋಯಿತೆಂದು ಹೇಳಿದಳು."
		},
		"5": {
			"1": "<sup>1</sup> ಫಿಲಿಷ್ಟಿಯರು ದೇವರ ಮಂಜೂಷವನ್ನು ಎಬೆನೆಜೆರಿನಿಂದ ಅಷ್ಡೋದಿನಲ್ಲಿರುವ",
			"2": "<sup>2</sup> ದಾಗೋನನ ಗುಡಿಗೆ ಒಯ್ದು ಅಲ್ಲಿ ದಾಗೋನನ ಮಗ್ಗುಲಲ್ಲೇ ಇಟ್ಟರು.",
			"3": "<sup>3</sup> ಅಷ್ಡೋದಿನವರು ಮರುದಿನ ಬೆಳಿಗ್ಗೆ ಎದ್ದು ನೋಡಲಾಗಿ ದಾಗೋನ್ ವಿಗ್ರಹವು ಯೆಹೋವನ ಮಂಜೂಷದ ಮುಂದೆ ಬೋರಲಬಿದ್ದಿರುವದನ್ನು ಕಂಡು ಅದನ್ನು ಎತ್ತಿ ತಿರಿಗಿ ಅದರ ಸ್ಥಳದಲ್ಲಿಟ್ಟರು.",
			"4": "<sup>4</sup> ಮುಂದಿನ ದಿನ ಬೆಳಿಗ್ಗೆ ನೋಡುವಾಗ ದಾಗೋನನ ತಲೆಯೂ ಕೈಗಳೂ ಕಡಿಯಲ್ಪಟ್ಟು ಹೊಸ್ತಲಿನ ಮೇಲೆ ಬಿದ್ದಿದ್ದವು; ಮುಂಡ ಮಾತ್ರ ಯೆಹೋವನ ಮಂಜೂಷದ ಮುಂದೆ ಬಿದ್ದಿತ್ತು;",
			"5": "<sup>5</sup> ಇಂದಿನವರೆಗೂ ದಾಗೋನನ ಯಾಜಕನೂ ಅವನ ಬಳಿಗೆ ಬರುವವರೆಲ್ಲರೂ ಅಷ್ಡೋದಿನ ಗುಡಿಯ ಹೊಸ್ತಿಲನ್ನು ತುಳಿಯದಿರುವದಕ್ಕೆ ಇದೇ ಕಾರಣ.",
			"6": "<sup>6</sup> ಯೆಹೋವನ ಹಸ್ತವು ಅಷ್ಡೋದಿನವರಿಗೆ ಬಾಧಕವಾಗಿತ್ತು; ಆತನು ಆ ನಗರದಲ್ಲಿಯೂ ಅದರ ಗ್ರಾಮಗಳಲ್ಲಿಯೂ ಗಡ್ಡೆರೋಗವನ್ನು ಬರಮಾಡಿ ಅವುಗಳನ್ನು ಹಾಳುಮಾಡಿದನು.",
			"7": "<sup>7</sup> ಅಷ್ಡೋದಿನ ಜನರು ಅದನ್ನು ನೋಡಿ - ಇಸ್ರಾಯೇಲ್‍ದೇವರ ಮಂಜೂಷವು ನಮ್ಮಲ್ಲಿರಬಾರದು; ಆತನ ಹಸ್ತವು ನಮಗೂ ನಮ್ಮ ದೇವರಾದ ದಾಗೋನನಿಗೂ ಬಾಧಕವಾಗಿದೆ ಎಂದು ಹೇಳಿ",
			"8": "<sup>8</sup> ಫಿಲಿಷ್ಟಿಯ ಪ್ರಭುಗಳೆಲ್ಲರನ್ನೂ ಕೂಡಿಸಿ - ಇಸ್ರಾಯೇಲ್‍ದೇವರ ಮಂಜೂಷವನ್ನು ಏನು ಮಾಡೋಣ ಎಂದು ಕೇಳಲು ಅವರು - ಅದನ್ನು ಗತ್ ಊರಿಗೆ ಕಳುಹಿಸಬೇಕೆಂದು ನಿರ್ಣಯಿಸಿದರು. ಅವರು ಹಾಗೆಯೇ ಇಸ್ರಾಯೇಲ್‍ದೇವರ ಮಂಜೂಷವನ್ನು ಅಲ್ಲಿಗೆ ಕಳುಹಿಸಿದರು.",
			"9": "<sup>9</sup> ಮಂಜೂಷವು ಅಲ್ಲಿ ಹೋದ ಮೇಲೆ ಯೆಹೋವನ ಹಸ್ತವು ಆ ಊರಿನವರಿಗೂ ಬಾಧಕವಾದದರಿಂದ ಅಲ್ಲಿ ದೊಡ್ಡ ಗದ್ದಲವಾಯಿತು. ಯೆಹೋವನು ಊರಿನ ಚಿಕ್ಕವರಲ್ಲಿಯೂ ದೊಡ್ಡವರಲ್ಲಿಯೂ ಗಡ್ಡೆಗಳನ್ನು ಬರಮಾಡಿದನು.",
			"10": "<sup>10</sup> ಆದದರಿಂದ ಅವರು ದೇವರ ಮಂಜೂಷವನ್ನು ಎಕ್ರೋನಿಗೆ ಕಳುಹಿಸಿದರು. ಅದು ಅಲ್ಲಿಗೆ ಬಂದಾಗ ಆ ಊರಿನವರು - ನೋಡಿರಿ, ನಮ್ಮನ್ನೂ ನಮಗೆ ಸೇರಿದವರನ್ನೂ ಕೊಲ್ಲುವದಕ್ಕೋಸ್ಕರ ಇಸ್ರಾಯೇಲ್‍ದೇವರ ಮಂಜೂಷವನ್ನು ಇಲ್ಲಿಗೆ ಕಳುಹಿಸಿದ್ದಾರೆಂದು ಕೂಗಿಕೊಂಡರು.",
			"11": "<sup>11</sup> ದೇವರ ಹಸ್ತವು ಅವರಿಗೂ ಬಾಧಕವಾಗಿದ್ದದರಿಂದ ಅವರಿಗೆ ಮರಣಭಯವುಂಟಾಯಿತು. ಅವರು ಫಿಲಿಷ್ಟಿಯ ಪ್ರಭುಗಳೆಲ್ಲರನ್ನೂ ಕೂಡಿಸಿ ಅವರಿಗೆ - ಇಸ್ರಾಯೇಲ್‍ದೇವರ ಮಂಜೂಷದಿಂದ ನಮಗೂ ನಮ್ಮ ಜನರಿಗೂ ಮರಣವುಂಟಾಗದ ಹಾಗೆ ಅದನ್ನು ಅದರ ಸ್ಥಳಕ್ಕೆ ಕಳುಹಿಸಿಬಿಡಿರಿ ಅಂದರು.",
			"12": "<sup>12</sup> ಸಾಯದೆ ಉಳಿದ ಜನರಿಗೂ ಗಡ್ಡೆರೋಗ ಬಂದಿತ್ತು. ಪಟ್ಟಣದ ಗೋಳಾಟವು ಆಕಾಶವನ್ನು ಮುಟ್ಟಿತು."
		},
		"6": {
			"1": "<sup>1</sup> ಯೆಹೋವನ ಮಂಜೂಷವು ಫಿಲಿಷ್ಟಿಯರ ದೇಶದಲ್ಲಿ ಏಳು ತಿಂಗಳಿದ್ದ ನಂತರ",
			"2": "<sup>2</sup> ಫಿಲಿಷ್ಟಿಯರು ಪೂಜಾರಿಗಳನ್ನೂ ಕಣಿಹೇಳುವವರನ್ನೂ ಕರೆದು ಅವರಿಗೆ - ಯೆಹೋವನ ಮಂಜೂಷವನ್ನು ಏನು ಮಾಡಬೇಕು? ಅದನ್ನು ತಿರಿಗಿ ಅದರ ಸ್ಥಳಕ್ಕೆ ಹೇಗೆ ಕಳುಹಿಸಬೇಕು? ತಿಳಿಸಿರಿ ಅನ್ನಲು ಅವರು -",
			"3": "<sup>3</sup> ನೀವು ಇಸ್ರಾಯೇಲ್‍ದೇವರ ಮಂಜೂಷವನ್ನು ಸುಮ್ಮನೆ ಕಳುಹಿಸಬಾರದು; ಪ್ರಾಯಶ್ಚಿತ್ತದ್ರವ್ಯದೊಡನೆ ಕಳುಹಿಸಿದರೆ ಮಾತ್ರ ಗುಣಹೊಂದುವಿರಿ; ಆತನ ಶಿಕ್ಷಾಹಸ್ತವು ನಿಮ್ಮನ್ನು ಬಾಧಿಸುತ್ತಿದ್ದದ್ದಕ್ಕೆ ಕಾರಣವು ಗೊತ್ತಾಗುವದು ಎಂದು ಹೇಳಿದರು.",
			"4": "<sup>4</sup> ಜನರು - ನಾವು ಪ್ರಾಯಶ್ಚಿತ್ತಾರ್ಥವಾಗಿ ಕಳುಹಿಸತಕ್ಕದ್ದೇನು ಎನ್ನಲು ಅವರು - ನಿಮಗೂ ನಿಮ್ಮ ಪ್ರಭುಗಳಿಗೂ ಒಂದೇ ವಿಧ ವ್ಯಾಧಿಯಿರುವದರಿಂದ ಪ್ರಭುಗಳ ಸಂಖ್ಯೆಗೆ ಸರಿಯಾಗಿ ಬಂಗಾರದ ಐದು ಗಡ್ಡೆಗಳನ್ನೂ ಇಲಿಗಳನ್ನೂ ಮಾಡಿಸಬೇಕು.",
			"5": "<sup>5</sup> ನೀವು ನಿಮ್ಮ ದೇಶನಿವಾಸಿಗಳ ವಿನಾಶಕ್ಕೆ ಕಾರಣವಾಗಿರುವ ಗಡ್ಡೆ ಮತ್ತು ಇಲಿ ಇವುಗಳ ಸ್ವರೂಪಗಳನ್ನು ಚಿನ್ನದಿಂದ ಮಾಡಿಸಿ ಇಸ್ರಾಯೇಲ್‍ದೇವರಿಗೆ ಬಹುಮಾನವಾಗಿ ಕೊಟ್ಟರೆ ಆತನ ಶಿಕ್ಷಾಹಸ್ತವು ನಿಮ್ಮನ್ನೂ ನಿಮ್ಮ ದೇವತೆಗಳನ್ನೂ ಪ್ರಾಂತಗಳನ್ನೂ ಬಿಟ್ಟುಹೋದೀತು.",
			"6": "<sup>6</sup> ಐಗುಪ್ತ್ಯರಂತೆಯೂ ಫರೋಹನಂತೆಯೂ ನಿಮ್ಮ ಹೃದಯಗಳನ್ನು ಕಠಿಣಪಡಿಸಿಕೊಳ್ಳುವದೇಕೆ? ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಕಳುಹಿಸಲೊಲ್ಲದ ಇವರನ್ನು ಯೆಹೋವನು ಎಷ್ಟೋ ವಿಧವಾಗಿ ಬಾಧಿಸಬೇಕಾಯಿತಲ್ಲಾ.",
			"7": "<sup>7</sup> ಈಗ ನೀವು ಒಂದು ಹೊಸ ಬಂಡಿಯನ್ನು ಮಾಡಿಸಿ ಅದಕ್ಕೆ ಹಾಲು ಕರೆಯುವ ಮತ್ತು ಎಂದೂ ನೊಗಹೊರದ ಎರಡು ಹಸುಗಳನ್ನು",
			"8": "<sup>8</sup> ಹೂಡಿ ಆ ಬಂಡಿಯ ಮೇಲೆ ಯೆಹೋವನ ಮಂಜೂಷವನ್ನೂ ನೀವು ಪ್ರಾಯಶ್ಚಿತ್ತಾರ್ಥವಾಗಿ ಕೊಡುವ ಬಂಗಾರದ ವಸ್ತುಗಳುಳ್ಳ ಚಿಕ್ಕ ಪೆಟ್ಟಿಗೆಯನ್ನೂ ಇಟ್ಟು ಕಳುಹಿಸಿಬಿಡಿರಿ. ಆ ಹಸುಗಳ ಕರುಗಳನ್ನು ಮನೆಗೆ ತೆಗೆದುಕೊಂಡು ಹೋಗಿರಿ.",
			"9": "<sup>9</sup> ಅದು ತಾನಾಗಿ ಸ್ವದೇಶದ ದಾರಿ ಹಿಡಿದು ಬೇತ್‍ಷೆಮೆಷಿನ ಕಡೆಗೆ ಹೋದರೆ ಈ ದೊಡ್ಡ ಕೇಡನ್ನು ಬರಮಾಡಿದವನು ಯೆಹೋವನೇ ಎಂದೂ ಆ ಮಾರ್ಗ ಹಿಡಿಯದಿದ್ದರೆ ಆತನ ಹಸ್ತವು ನಮ್ಮನ್ನು ಮುಟ್ಟಲಿಲ್ಲ, ಅದು ನಮ್ಮ ಅದೃಷ್ಟವೇ ಎಂದೂ ತಿಳಿದುಕೊಳ್ಳಿರಿ ಅಂದರು.",
			"10": "<sup>10</sup> ಅದೇ ಪ್ರಕಾರ ಫಿಲಿಷ್ಟಿಯರು ಹಾಲು ಕರೆಯುವ ಎರಡು ಹಸುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಬಂಡಿಗೆ ಹೂಡಿ ಅವುಗಳ ಕರುಗಳನ್ನು ಮನೆಯಲ್ಲಿ ಕಟ್ಟಿಹಾಕಿ",
			"11": "<sup>11</sup> ಯೆಹೋವನ ಮಂಜೂಷವನ್ನೂ ಚಿನ್ನದ ಇಲಿಗಳೂ ಗಡ್ಡೆಗಳೂ ಉಳ್ಳ ಚಿಕ್ಕ ಪೆಟ್ಟಿಗೆಯನ್ನೂ ಬಂಡಿಯ ಮೇಲಿಟ್ಟರು.",
			"12": "<sup>12</sup> ಕೂಡಲೆ ಆ ಹಸುಗಳು ಬೇತ್‍ಷೆಮೆಷಿನ ರಾಜಮಾರ್ಗವನ್ನು ಹಿಡಿದು ಕೂಗುತ್ತಾ ನೆಟ್ಟಗೆ ಮುಂದೆ ನಡೆದವು; ಎಡಬಲಕ್ಕೆ ತಿರುಗಲಿಲ್ಲ. ಫಿಲಿಷ್ಟಿಯ ಪ್ರಭುಗಳು ಬೇತ್‍ಷೆಮೆಷಿನ ಎಲ್ಲೆಯವರೆಗೂ ಅವುಗಳ ಹಿಂದೆ ಹೋದರು.",
			"13": "<sup>13</sup> ಬೇತ್‍ಷೆಮೆಷಿನವರು ಅಲ್ಲಿನ ತಗ್ಗಿನಲ್ಲಿ ಗೋದಿಯ ಬೆಳೆಯನ್ನು ಕೊಯ್ಯುತ್ತಿದ್ದರು. ಅವರು ಕಣ್ಣೆತ್ತಿ ಯೆಹೋವನ ಮಂಜೂಷವನ್ನು ನೋಡಿ ಬಹುಸಂತೋಷಪಟ್ಟರು.",
			"14": "<sup>14</sup> ಬಂಡಿಯು ಬೇತ್‍ಷೆಮೆಷಿನವನಾದ ಯೆಹೋಶುವನ ಹೊಲದಲ್ಲಿದ್ದ ದೊಡ್ಡ ಕಲ್ಲಿನ ಬಳಿಯಲ್ಲಿ ಬಂದು ನಿಲ್ಲಲು ಅವರು ಹೋಗಿ ಬಂಡಿಯ ಕಟ್ಟಿಗೆಗಳನ್ನು ಒಡೆದು ಅಗ್ನಿಮಾಡಿ ಆ ಹಸುಗಳನ್ನು ಯೆಹೋವನಿಗೋಸ್ಕರ ಆಹುತಿಕೊಟ್ಟರು.",
			"15": "<sup>15</sup> ಲೇವಿಯರು ಯೆಹೋವನ ಮಂಜೂಷವನ್ನೂ ಚಿನ್ನದ ವಸ್ತುಗಳುಳ್ಳ ಚಿಕ್ಕ ಪೆಟ್ಟಿಗೆಯನ್ನೂ ಇಳಿಸಿ ಆ ದೊಡ್ಡ ಕಲ್ಲಿನ ಮೇಲಿಟ್ಟರು. ಬೇತ್‍ಷೆಮೆಷಿನವರು ಅದೇ ದಿವಸ ಯೆಹೋವನಿಗೆ ಸರ್ವಾಂಗಹೋಮಗಳನ್ನೂ ಸಮಾಧಾನಯಜ್ಞಗಳನ್ನೂ ಸಮರ್ಪಿಸಿದರು.",
			"16": "<sup>16</sup> ಇದನ್ನೆಲ್ಲಾ ನೋಡಿದ ಮೇಲೆ ಫಿಲಿಷ್ಟಿಯರ ಐದು ಮಂದಿ ಪ್ರಭುಗಳು ಅದೇ ದಿವಸ ಎಕ್ರೋನಿಗೆ ಹಿಂದಿರುಗಿದರು.",
			"17": "<sup>17</sup> ಫಿಲಿಷ್ಟಿಯರು ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಿಸಿದ ಚಿನ್ನದ ಗಡ್ಡೆಗಳು ಅಷ್ಡೋದ್, ಗಾಜಾ, ಅಷ್ಕೆಲೋನ್, ಗತ್ಊರು, ಎಕ್ರೋನ್ ಎಂಬ ಅವರ ಸಂಸ್ಥಾನಗಳ ಸಂಖ್ಯೆಗೂ",
			"18": "<sup>18</sup> ಅವರು ಕಳುಹಿಸಿದ ಬಂಗಾರದ ಇಲಿಗಳು ಫಿಲಿಷ್ಟಿಯರ ಐದು ಮಂದಿ ಪ್ರಭುಗಳ ಅಧೀನದಲ್ಲಿದ್ದ ಎಲ್ಲಾ ಗ್ರಾಮನಗರಗಳ ಸಂಖ್ಯೆಗೂ ಸರಿಯಾಗಿದ್ದವು. ಬೇತ್‍ಷೆಮೆಷಿನವನಾದ ಯೆಹೋಶುವನ ಹೊಲದಲ್ಲಿ ಇಂದಿನವರೆಗೂ ಇರುವ ಯೆಹೋವನ ಮಂಜೂಷವು ಇಡಲ್ಪಟ್ಟ ಆ ದೊಡ್ಡ ಕಲ್ಲೇ ಈ ಗ್ರಾಮನಗರಗಳಿಗೆ ಮೇರೆಯಾಗಿದೆ.",
			"19": "<sup>19</sup> ಬೇತ್‍ಷೆಮೆಷಿನವರು  ಯೆಹೋವನ ಮಂಜೂಷದಲ್ಲಿ ಹಣಿಕಿನೋಡಿದ್ದರಿಂದ ಯೆಹೋವನು ಅವರಲ್ಲಿ ಎಪ್ಪತ್ತು ಮಂದಿಯನ್ನು ಸಾಯಿಸಿದನು. ಇದಲ್ಲದೆ ಐವತ್ತು ಸಾವಿರ ಜನರು ಮರಣಹೊಂದಿದರು. ಯೆಹೋವನು ತಮ್ಮಲ್ಲಿ ಮಹಾನಾಶನವನ್ನುಂಟುಮಾಡಿದದರಿಂದ ಬೇತ್‍ಷೆಮೆಷಿನವರು ಗೋಳಾಡಿ -",
			"20": "<sup>20</sup> ಅಗಮ್ಯದೇವರಾದ ಯೆಹೋವನ ಮುಂದೆ ಯಾರು ನಿಂತಾರು? ಇನ್ನು ಮೇಲೆ ಈತನು ಹೋಗತಕ್ಕ ಸ್ಥಳವಾವದು ಎಂದು ಮಾತಾಡಿಕೊಂಡು",
			"21": "<sup>21</sup> ಕಿರ್ಯತ್ಯಾರೀಮಿನವರ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿ ಅವರಿಗೆ - ಫಿಲಿಷ್ಟಿಯರು ಯೆಹೋವನ ಮಂಜೂಷವನ್ನು ಕಳುಹಿಸಿದ್ದಾರೆ; ನೀವು ಬಂದು ಅದನ್ನು ತೆಗೆದುಕೊಂಡು ಹೋಗಿರಿ ಎಂದು ಹೇಳಿಸಿದರು."
		},
		"7": {
			"1": "<sup>1</sup> ಆಗ ಕಿರ್ಯತ್ಯಾರೀಮಿನವರು ಬಂದು ಯೆಹೋವನ ಮಂಜೂಷವನ್ನು ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ಗುಡ್ಡದ ಮೇಲೆ ವಾಸವಾಗಿದ್ದ ಅಬೀನಾದಾಬನ ಮನೆಯಲ್ಲಿಟ್ಟು ಅವನ ಮಗನಾದ ಎಲ್ಲಾಜಾರನನ್ನು ಅದರ ಸೇವೆಗೋಸ್ಕರ ಪ್ರತಿಷ್ಠಿಸಿದರು.",
			"2": "<sup>2</sup> ಮಂಜೂಷವು ಕಿರ್ಯತ್ಯಾರೀಮಿಗೆ ಬಂದು ಬಹಳ ದಿವಸಗಳು ಅಂದರೆ ಇಪ್ಪತ್ತು ವರುಷಗಳು ಕಳೆದುಹೋದವು; ಈ ಕಾಲದಲ್ಲಿ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರು ಯೆಹೋವನನ್ನೇ ಹಂಬಲಿಸುತ್ತಿದ್ದರು.",
			"3": "<sup>3</sup> ಆಗ ಸಮುವೇಲನು ಅವರಿಗೆ - ನೀವು ಪೂರ್ಣಮನಸ್ಸಿನಿಂದ ಯೆಹೋವನ ಕಡೆಗೆ ತಿರಿಗಿಕೊಂಡಿರುವದಾದರೆ ನಿಮ್ಮ ಮಧ್ಯದಲ್ಲಿರುವ ಅಷ್ಟೋರೆತ್ ಮೊದಲಾದ ಅನ್ಯದೇವತೆಗಳನ್ನು ತೆಗೆದುಹಾಕಿ ಯೆಹೋವನ ಮೇಲೆಯೇ ಮನಸ್ಸಿಟ್ಟು ಆತನೊಬ್ಬನನ್ನೇ ಸೇವಿಸಿರಿ; ಆಗ ಆತನು ನಿಮ್ಮನ್ನು ಫಿಲಿಷ್ಟಿಯರ ಕೈಯಿಂದ ಬಿಡಿಸಿ ಕಾಪಾಡುವನು ಅಂದನು.",
			"4": "<sup>4</sup> ಹಾಗೆಯೇ ಇಸ್ರಾಯೇಲ್ಯರು ಬಾಳ್ ಅಷ್ಟೋರೆತ್ ದೇವತೆಗಳನ್ನು ತೆಗೆದುಹಾಕಿ ಯೆಹೋವನೊಬ್ಬನನ್ನೇ ಸೇವಿಸತೊಡಗಿದರು.",
			"5": "<sup>5</sup> ಸಮುವೇಲನು ತಿರಿಗಿ ಅವರಿಗೆ - ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರೂ ಮಿಚ್ಪೆಯಲ್ಲಿ ಕೂಡಿ ಬರಲಿ; ನಾನು ನಿಮಗೋಸ್ಕರ ಯೆಹೋವನನ್ನು ಪ್ರಾರ್ಥಿಸುವೆನು ಅನ್ನಲು",
			"6": "<sup>6</sup> ಅವರು ಅಲ್ಲಿ ಕೂಡಿಬಂದು ನೀರು ಸೇದಿ ಯೆಹೋವನ ಮುಂದೆ ಹೊಯ್ದು ಆ ದಿವಸ ಉಪವಾಸವಿದ್ದು - ನಿನಗೆ ದ್ರೋಹಿಗಳಾಗಿದ್ದೇವೆಂದು ಆತನಿಗೆ ಅರಿಕೆ ಮಾಡಿದರು. ಸಮುವೇಲನು ಮಿಚ್ಪೆಯಲ್ಲಿ ಇಸ್ರಾಯೇಲ್ಯರ ವ್ಯಾಜ್ಯಗಳನ್ನು ತೀರಿಸಿದನು.",
			"7": "<sup>7</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಮಿಚ್ಪೆಯಲ್ಲಿ ಕೂಡಿಬಂದಿದ್ದಾರೆಂಬದು ಫಿಲಿಷ್ಟಿಯರಿಗೆ ಗೊತ್ತಾದಾಗ ಅವರ ಪ್ರಭುಗಳು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ವಿರೋಧವಾಗಿ ಹೊರಟರು. ಇದನ್ನು ತಿಳಿದ ಇಸ್ರಾಯೇಲ್ಯರು ಬಹಳವಾಗಿ ಭಯಪಟ್ಟು ಸಮುವೇಲನಿಗೆ -",
			"8": "<sup>8</sup> ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಮ್ಮನ್ನು ಫಿಲಿಷ್ಟಿಯರ ಕೈಯಿಂದ ತಪ್ಪಿಸಿ ರಕ್ಷಿಸುವ ಹಾಗೆ ನೀನು ನಮಗೋಸ್ಕರ ಆತನಿಗೆ ಮೊರೆಯಿಡು, ಸುಮ್ಮನಿರಬೇಡ ಅಂದರು.",
			"9": "<sup>9</sup> ಆಗ ಸಮುವೇಲನು ಹಾಲುಕುಡಿಯುವ ಕುರಿಮರಿಯನ್ನು ತಂದು ಯೆಹೋವನಿಗೆ ಸರ್ವಾಂಗಹೋಮವಾಗಿ ಸಮರ್ಪಿಸಿ ಇಸ್ರಾಯೇಲ್ಯರಿಗೋಸ್ಕರ ಮೊರೆಯಿಡಲು ಆತನು ಸದುತ್ತರವನ್ನು ದಯಪಾಲಿಸಿದನು. ಹೇಗಂದರೆ -",
			"10": "<sup>10</sup> ಸಮುವೇಲನು ಯಜ್ಞವನ್ನರ್ಪಿಸುವ ವೇಳೆಯಲ್ಲಿ ಫಿಲಿಷ್ಟಿಯರು ಯುದ್ಧಕ್ಕೋಸ್ಕರ ಸಮೀಪಿಸಲಾಗಿ ಯೆಹೋವನು ದೊಡ್ಡ ಗುಡುಗಿನಿಂದ ಅವರನ್ನು ಕಳವಳಗೊಳಿಸಿ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಸೋತು ಓಡಿಹೋಗುವಂತೆ ಮಾಡಿದನು.",
			"11": "<sup>11</sup> ಇಸ್ರಾಯೇಲ್ಯರಾದರೋ ಮಿಚ್ಪೆಯಿಂದ ಹೊರಟು ಹಿಂದಟ್ಟಿ ಬೇತ್ಕರಿನ ತಗ್ಗಿನವರೆಗೆ ಅವರನ್ನು ಹತಮಾಡಿದರು.",
			"12": "<sup>12</sup> ಅನಂತರ ಸಮುವೇಲನು ಮಿಚ್ಪೆಗೂ ಶೇನಿಗೂ ಮಧ್ಯದಲ್ಲಿ ಒಂದು ಕಲ್ಲನ್ನು ನಿಲ್ಲಿಸಿ - ಯೆಹೋವನು ಇಲ್ಲಿಯವರೆಗೆ ನಮಗೆ ಸಹಾಯಮಾಡಿದನೆಂದು ಹೇಳಿ ಅದಕ್ಕೆ ಎಬೆನೆಜೆರೆಂದು ಹೆಸರಿಟ್ಟನು.",
			"13": "<sup>13</sup> ಫಿಲಿಷ್ಟಿಯರು ಬಹಳವಾಗಿ ತಗ್ಗಿಸಲ್ಪಟ್ಟದರಿಂದ ತಿರಿಗಿ ಇಸ್ರಾಯೇಲ್ಯರ ಪ್ರಾಂತದೊಳಗೆ ಬರಲೇ ಇಲ್ಲ. ಸಮುವೇಲನ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ಯೆಹೋವನ ಹಸ್ತವು ಫಿಲಿಷ್ಟಿಯರಿಗೆ ವಿರೋಧವಾಗಿಯೇ ಇತ್ತು.",
			"14": "<sup>14</sup> ಅವರು ಎಕ್ರೋನ್ ಮೊದಲುಗೊಂಡು ಗತ್ ಊರಿನವರೆಗೆ ಇಸ್ರಾಯೇಲ್ಯರಿಂದ ಕಿತ್ತುಕೊಂಡಿದ್ದ ಎಲ್ಲಾ ಪಟ್ಟಣಗಳು ಇಸ್ರಾಯೇಲ್ಯರಿಗೇ ದೊರಕಿದವು. ಅವುಗಳಿಗೆ ಸೇರಿದ ಗ್ರಾಮಗಳನ್ನೂ ಇಸ್ರಾಯೇಲ್ಯರು ಫಿಲಿಷ್ಟಿಯರಿಂದ ತೆಗೆದುಕೊಂಡರು. ಇಸ್ರಾಯೇಲ್ಯರಿಗೂ ಅಮೋರಿಯರಿಗೂ ಸಮಾಧಾನವಿತ್ತು.",
			"15": "<sup>15</sup> ಸಮುವೇಲನು ಜೀವದಿಂದಿರುವವರೆಗೆ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಪಾಲಿಸುತ್ತಿದ್ದನು.",
			"16": "<sup>16</sup> ಅವನು ಇಸ್ರಾಯೇಲ್ಯರ ನ್ಯಾಯಗಳನ್ನು ತೀರಿಸುವದಕ್ಕೋಸ್ಕರ ಪ್ರತಿವರ್ಷವೂ ಬೇತೇಲ್, ಗಿಲ್ಗಾಲ್, ಮಿಚ್ಪೆ ಎಂಬ ಪಟ್ಟಣಗಳನ್ನು ಸುತ್ತಿ ರಾಮಕ್ಕೆ ತಿರಿಗಿ ಬರುತ್ತಿದ್ದನು.",
			"17": "<sup>17</sup> ಅಲ್ಲಿ ಅವನ ಸ್ವಂತ ಮನೆಯಿದ್ದದರಿಂದ ಅಲ್ಲಿಯೇ ಯೆಹೋವನಿಗೆ ಒಂದು ಯಜ್ಞವೇದಿಯನ್ನು ಕಟ್ಟಿಸಿ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಪಾಲಿಸುತ್ತಿದ್ದನು."
		},
		"8": {
			"1": "<sup>1</sup> ಸಮುವೇಲನು ಮುದುಕನಾದ ಮೇಲೆ ತನ್ನ ಮಕ್ಕಳನ್ನು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ನ್ಯಾಯಸ್ಥಾಪಕರನ್ನಾಗಿ ಮಾಡಿದನು.",
			"2": "<sup>2</sup> ಅವನ ಚೊಚ್ಚಲ ಮಗನ ಹೆಸರು ಯೋವೇಲ್, ಮರಚಲ ಮಗನ ಹೆಸರು ಅಬೀಯ.",
			"3": "<sup>3</sup> ಇವರ ನ್ಯಾಯಾಸನವು ಬೇರ್ಷೆಬದಲ್ಲಿತ್ತು. ಇವರು ತಂದೆಯ ಮಾರ್ಗದಲ್ಲಿ ನಡೆಯದೆ ದ್ರವ್ಯಾಶೆಯಿಂದ ಲಂಚತೆಗೆದುಕೊಂಡು ನ್ಯಾಯವಿರುದ್ಧವಾದ ತೀರ್ಪುಮಾಡುತ್ತಿದ್ದದರಿಂದ",
			"4": "<sup>4</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಹಿರಿಯರೆಲ್ಲರೂ ಕೂಡಿಕೊಂಡು ರಾಮದಲ್ಲಿದ್ದ ಸಮುವೇಲನ ಬಳಿಗೆ ಬಂದು ಅವನಿಗೆ -",
			"5": "<sup>5</sup> ನೀನಂತೂ ಮುದುಕನಾದಿ; ನಿನ್ನ ಮಕ್ಕಳು ನಿನ್ನ ಮಾರ್ಗದಲ್ಲಿ ನಡೆಯುವದಿಲ್ಲ. ಆದದರಿಂದ ಬೇರೆ ಎಲ್ಲಾ ಜನಾಂಗಗಳಿಗಿರುವಂತೆ ನಮಗೂ ಒಬ್ಬ ಅರಸನನ್ನು ನೇಮಿಸು; ಅವನೇ ನಮ್ಮ ನ್ಯಾಯಸ್ಥಾಪಕನಾಗಿರಲಿ ಅಂದರು.",
			"6": "<sup>6</sup> ನ್ಯಾಯಸ್ಥಾಪನೆಗೋಸ್ಕರ ನಮಗೊಬ್ಬ ಅರಸನನ್ನು ನೇಮಿಸಿಕೊಡು ಎಂಬ ಮಾತಿಗೆ ಸಮುವೇಲನು ದುಃಖಪಟ್ಟು ಯೆಹೋವನನ್ನು ಪ್ರಾರ್ಥಿಸಲು ಆತನು ಅವನಿಗೆ -",
			"7": "<sup>7</sup> ಜನರು ಹೇಳಿದಂತೆಯೇ ಮಾಡು; ಅವರು ನಿನ್ನನ್ನಲ್ಲ, ನನ್ನನ್ನು ತಿರಸ್ಕರಿಸಿದ್ದಾರೆ. ನನ್ನ ಆಳಿಕೆಗೆ ಬೇಡವೆನ್ನುತ್ತಾರೆ.",
			"8": "<sup>8</sup> ನಾನು ಅವರನ್ನು ಐಗುಪ್ತದಿಂದ ಬಿಡಿಸಿದಂದಿನಿಂದ ಇಂದಿನವರೆಗೂ ಅವರು ನನ್ನನ್ನು ಬಿಟ್ಟು ಅನ್ಯದೇವತೆಗಳನ್ನು ಸೇವಿಸಿದರು. ನನಗೆ ಹೇಗೆ ಮಾಡಿದರೋ ಹಾಗೆಯೇ ನಿನಗೂ ಮಾಡುತ್ತಾರೆ.",
			"9": "<sup>9</sup> ನೀನು ಈಗ ಅವರು ಕೇಳಿಕೊಂಡಂತೆ ಮಾಡು; ಆದರೆ ಅವರ ಮೇಲೆ ರಾಜರಿಗಿರುವ ಅಧಿಕಾರವೆಷ್ಟೆಂಬದನ್ನು ಅವರಿಗೆ ತಿಳಿಸಿ ಎಚ್ಚರಿಸದೆ ಇರಬೇಡ ಎಂದು ಹೇಳಿದನು.",
			"10": "<sup>10</sup> ಸಮುವೇಲನು ಅರಸನನ್ನು ಬೇಡಿಕೊಂಡ ಜನರಿಗೆ ಯೆಹೋವನ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ತಿಳಿಸಿ",
			"11": "<sup>11</sup> ಅವರಿಗೆ - ಅರಸನ ಅಧಿಕಾರವೆಷ್ಟೆಂದು ಕೇಳಿರಿ - ಅವನು ನಿಮ್ಮ ಮಕ್ಕಳನ್ನು ತೆಗೆದುಕೊಂಡು ತನ್ನ ಸಾರಥಿಗಳನ್ನಾಗಿಯೂ ರಾಹುತರನ್ನಾಗಿಯೂ ಮಾಡಿಕೊಳ್ಳುವನು; ಅವರು ಅವನ ರಥಗಳ ಮುಂದೆ ಓಡುವವರಾಗಬೇಕು.",
			"12": "<sup>12</sup> ಕೆಲವರನ್ನು ಸಹಸ್ರಾಧಿಪತಿಗಳನ್ನಾಗಿಯೂ ಕೆಲವರನ್ನು ಪಂಚಶತಾಧಿಪತಿಗಳನ್ನಾಗಿಯೂ ನೇಮಿಸುವನು. ಇನ್ನು ಕೆಲವರು ಅವನ ಭೂಮಿಯನ್ನು ಉಳುವವರೂ ಪೈರನ್ನು ಕೊಯ್ಯುವವರೂ ಯುದ್ಧಾಯುಧ, ರಥಸಾಹಿತ್ಯ ಇವುಗಳನ್ನು ಮಾಡುವವರೂ ಆಗಬೇಕು.",
			"13": "<sup>13</sup> ಇದಲ್ಲದೆ ಅವನು ನಿಮ್ಮ ಹೆಣ್ಣುಮಕ್ಕಳನ್ನು ಸುಗಂಧದ್ರವ್ಯಮಾಡುವದಕ್ಕೂ ಅಡಿಗೆ ಮಾಡುವದಕ್ಕೂ ರೊಟ್ಟಿಸುಡುವದಕ್ಕೂ ನೇಮಿಸಿಕೊಳ್ಳುವನು.",
			"14": "<sup>14</sup> ನಿಮ್ಮ ಉತ್ತಮವಾದ ಹೊಲಗಳನ್ನೂ ದ್ರಾಕ್ಷೇತೋಟಗಳನ್ನೂ ಎಣ್ಣೇ ಮರದ ತೋಪುಗಳನ್ನೂ ಕಿತ್ತುಕೊಂಡು ತನ್ನ ಸೇವಕರಿಗೆ ಕೊಡುವನು.",
			"15": "<sup>15</sup> ನಿಮ್ಮ ಧಾನ್ಯದ್ರಾಕ್ಷೆಗಳಲ್ಲಿ ದಶಮಾಂಶವನ್ನು ತೆಗೆದುಕೊಂಡು ತನ್ನ ಪ್ರಧಾನರಿಗೂ ಪರಿವಾರದವರಿಗೂ ಕೊಡುವನು.",
			"16": "<sup>16</sup> ನಿಮ್ಮ ದಾಸದಾಸಿಯರನ್ನೂ ಉತ್ತಮವಾದ ಎತ್ತು ಕತ್ತೆಗಳನ್ನೂ ತನ್ನ ಕೆಲಸಕ್ಕೆ ಹಚ್ಚಿಕೊಳ್ಳುವನು.",
			"17": "<sup>17</sup> ಕುರಿಹಿಂಡುಗಳಲ್ಲಿ ಹತ್ತರಲ್ಲೊಂದು ಭಾಗವನ್ನು ತೆಗೆದುಕೊಳ್ಳುವನು; ನೀವು ಅವನಿಗೆ ದಾಸರಾಗಿರಬೇಕು.",
			"18": "<sup>18</sup> ಆಗ ನೀವು ಆರಿಸಿಕೊಂಡ ಅರಸನ ದೆಸೆಯಿಂದ ಆ ದಿನದಲ್ಲಿ ಬೇಸತ್ತು ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಡುವಿರಿ; ಆಗ ಆತನು ನಿಮ್ಮನ್ನು ಲಕ್ಷಿಸುವದಿಲ್ಲ ಅಂದನು.",
			"19": "<sup>19</sup> ಹೀಗೆ ಸಮುವೇಲನು ಎಷ್ಟು ಹೇಳಿದರೂ ಜನರು ಕೇಳದೆ - ಅದಿರಲಿ; ನಮಗೆ ಅರಸನನ್ನು ಕೊಡು;",
			"20": "<sup>20</sup> ನಾವು ಇತರ ಜನಾಂಗಗಳಂತೆ ಆಗಬೇಕು. ನಮ್ಮ ನ್ಯಾಯಗಳನ್ನು ತೀರಿಸಿ ನಮಗೆ ಮುಂದಾಗಿ ಹೊರಟು ನಮಗೋಸ್ಕರ ಯುದ್ಧಮಾಡುವ ಒಬ್ಬ ಅರಸನು ಬೇಕು ಎಂದು ಹೇಳಿದರು.",
			"21": "<sup>21</sup> ಸಮುವೇಲನು ಜನರ ಈ ಎಲ್ಲಾ ಮಾತುಗಳನ್ನು ಯೆಹೋವನಿಗೆ ತಿಳಿಸಲಾಗಿ ಆತನು ಅವನಿಗೆ -",
			"22": "<sup>22</sup> ಅವರ ಇಷ್ಟದಂತೆ ಅವರಿಗೊಬ್ಬ ಅರಸನನ್ನು ನೇಮಿಸು ಎಂದನು. ಆಗ ಸಮುವೇಲನು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ - ನಿಮ್ಮ ನಿಮ್ಮ ಊರುಗಳಿಗೆ ಹೋಗಿರಿ ಎಂದು ಅಪ್ಪಣೆಕೊಟ್ಟನು."
		},
		"9": {
			"1": "<sup>1</sup> ಬೆನ್ಯಾಮೀನ್‍ಕುಲದಲ್ಲಿ ಕೀಷನೆಂಬ ಒಬ್ಬ ಐಶ್ವರ್ಯವಂತನಿದ್ದನು. ಇವನು ಚೆರೋರನ ಮಗನೂ ಬೆಕೋರತನ ಮೊಮ್ಮಗನೂ ಅಫೀಹನ ಮರಿಮಗನೂ ಆದ ಅಬೀಯೇಲನ ಮಗನು.",
			"2": "<sup>2</sup> ಇವನಿಗೆ ಸೌಲನೆಂಬ ಹೆಸರುಳ್ಳ ಒಬ್ಬ ಮಗನಿದ್ದನು. ಈ ಸೌಲನು ಉತ್ತಮನೂ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಅತಿ ಸುಂದರನೂ ಆಗಿದ್ದನು; ಜನರ ಗುಂಪಿನಲ್ಲಿ ಅವನ ಹೆಗಲೂ ತಲೆಯೂ ಕಾಣುವಷ್ಟು ಉನ್ನತವಾಗಿದ್ದನು.",
			"3": "<sup>3</sup> ಒಂದು ದಿವಸ ಸೌಲನ ತಂದೆಯಾದ ಕೀಷನ ಕತ್ತೆಗಳು ತಪ್ಪಿಸಿಕೊಂಡು ಹೋದದರಿಂದ ಅವನು ತನ್ನ ಮಗನಾದ ಸೌಲನಿಗೆ - ನೀನೆದ್ದು ಆಳುಗಳಲ್ಲೊಬ್ಬನನ್ನು ಕರೆದುಕೊಂಡು ಕತ್ತೆಗಳನ್ನು ಹುಡುಕುವದಕ್ಕೆ ಹೋಗು ಎಂದು ಹೇಳಿದನು.",
			"4": "<sup>4</sup> ಅದರಂತೆಯೇ ಅವನು ಹೊರಟು ಎಫ್ರಾಯೀಮ್ ಪರ್ವತಪ್ರಾಂತ್ಯ, ಶಾಲಿಷಾದೇಶ ಇವುಗಳಲ್ಲಿ ಸಂಚರಿಸಿದರೂ ಅವು ಸಿಕ್ಕಲಿಲ್ಲ; ಶಾಲೀಮ್, ಬೆನ್ಯಾಮೀನ್ ಈ ಪ್ರಾಂತಗಳಲ್ಲಿಯೂ ಸಿಕ್ಕಲಿಲ್ಲ.",
			"5": "<sup>5</sup> ಕಡೆಗೆ ಅವರು ಚೂಫ್‍ದೇಶಕ್ಕೆ ಬಂದಾಗ ಅವನು ತನ್ನ ಆಳಿಗೆ - ಹಿಂದಿರುಗಿ ಹೋಗೋಣ ನಡಿ; ನಮ್ಮ ತಂದೆಯು ಈಗ ಕತ್ತೆಗಳ ಚಿಂತೆ ಬಿಟ್ಟು ನಮ್ಮ ಚಿಂತೆಯಲ್ಲಿದ್ದಾನು ಅನ್ನಲು ಆ ಆಳು -",
			"6": "<sup>6</sup> ಇಗೋ, ಈ ಪಟ್ಟಣದಲ್ಲಿ ಗೌರವಸ್ಥನಾದ ಒಬ್ಬ ದೇವರ ಮನುಷ್ಯನಿರುತ್ತಾನೆ. ಅವನು ನುಡಿಯುವದೆಲ್ಲಾ ನೆರವೇರುತ್ತದೆ; ಅಲ್ಲಿಗೆ ಹೋಗೋಣ; ಒಂದು ವೇಳೆ ಅವನು ನಾವು ಹೋಗತಕ್ಕ ಮಾರ್ಗವನ್ನು ತಿಳಿಸುವನು ಎಂದನು.",
			"7": "<sup>7</sup> ಅದಕ್ಕೆ ಸೌಲನು - ನಾವು ಅವನ ಬಳಿಗೆ ಹೋಗಬೇಕಾದರೆ ಏನಾದರೂ ತೆಗೆದುಕೊಂಡು ಹೋಗಬೇಕು; ನಾವು ತಂದಿದ್ದ ಆಹಾರಸಾಮಗ್ರಿಯು ಮುಗಿದುಹೋಯಿತು. ಆ ದೇವರ ಮನುಷ್ಯನಿಗೆ ಕೊಡತಕ್ಕ ಕಾಣಿಕೆ ನಮ್ಮಲ್ಲಿಲ್ಲವಲ್ಲಾ; ಏನು ಕೊಡಬೇಕು ಎನ್ನಲು ಆಳು -",
			"8": "<sup>8</sup> ಇಗೋ, ನನ್ನ ಕೈಯಲ್ಲಿ ಒಂದು ಪಾವಲಿಯಿದೆ; ಇದನ್ನು ಅವನಿಗೆ ಕೊಡೋಣ; ಅವನು ನಮಗೆ ಮಾರ್ಗ ತೋರಿಸುವನು ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"9": "<sup>9</sup> (ಪೂರ್ವಕಾಲದಲ್ಲಿ ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಯಾವನಾದರೂ ದೈವೋತ್ತರಕೇಳಬೇಕಾದರೆ ದರ್ಶಿಯ ಬಳಿಗೆ ಹೋಗೋಣ ಬನ್ನಿರಿ ಎನ್ನುವನು; ಈ ಕಾಲದಲ್ಲಿ ಪ್ರವಾದಿಗಳೆನಿಸಿಕೊಳ್ಳುವವರನ್ನು ಆ ಕಾಲದಲ್ಲಿ ದರ್ಶಿಗಳೆಂದು ಕರೆಯುತ್ತಿದ್ದರು.)",
			"10": "<sup>10</sup> ಆಗ ಸೌಲನು ಆಳಿಗೆ - ನೀನು ಹೇಳುವದು ಒಳ್ಳೇದು; ಹೋಗೋಣ ನಡಿ ಅಂದನು.",
			"11": "<sup>11</sup> ಅವರಿಬ್ಬರೂ ದಿನ್ನೆಯನ್ನು ಹತ್ತಿ ದೇವರ ಮನುಷ್ಯನಿದ್ದ ಪಟ್ಟಣವನ್ನು ಸಮೀಪಿಸಲು ನೀರು ಸೇದುವದಕ್ಕೋಸ್ಕರ ಹೊರಗೆ ಬಂದಿದ್ದ ಸ್ತ್ರೀಯರನ್ನು ಕಂಡು - ದರ್ಶಿಯು ಊರಲ್ಲಿದ್ದಾನೋ ಎಂದು ಕೇಳಲು ಅವರು - ಇದ್ದಾನೆ;",
			"12": "<sup>12</sup> ಈ ಹೊತ್ತು ನಮ್ಮ ಊರಿನವರು ಗುಡ್ಡದ ಮೇಲೆ ಯಜ್ಞವನ್ನರ್ಪಿಸುವದರಿಂದ ಅವನು ಈಗಲೇ ಇಲ್ಲಿಗೆ ಬಂದನು; ಬೇಗನೆ ಹೋಗಿರಿ.",
			"13": "<sup>13</sup> ಅವನು ಯಜ್ಞಭೋಜನಕ್ಕೋಸ್ಕರ ಗುಡ್ಡಕ್ಕೆ ಹೋಗುವ ಮೊದಲು ನೀವು ಊರೊಳಕ್ಕೆ ಹೋದರೆ ಸಿಕ್ಕುವನು. ಅವನು ಅಲ್ಲಿ ಹೋಗುವವರೆಗೆ ಜನರು ಊಟಮಾಡುವದಿಲ್ಲ. ಅವನು ಹೋಗಿ ಯಜ್ಞಾಹಾರವನ್ನು ಆಶೀರ್ವದಿಸುತ್ತಾನೆ; ಅನಂತರ ಕರೆಯಲ್ಪಟ್ಟವರು ಊಟಮಾಡುತ್ತಾರೆ. ಈಗಲೇ ಹೋಗಿರಿ; ಅವನು ಸಿಕ್ಕುವ ಸಮಯ ಇದೇ ಅಂದರು.",
			"14": "<sup>14</sup> ಅವರು ಮೇಲೆ ಹತ್ತಿ ಊರಿಗೆ ಬಂದಾಗ ಸಮುವೇಲನು ಗುಡ್ಡಕ್ಕೆ ಹೋಗುವದಕ್ಕೋಸ್ಕರ ಹೊರಟು ಅವರೆದುರಿಗೆ ಬಂದನು.",
			"15": "<sup>15</sup> ಸೌಲನು ಬರುವದಕ್ಕೆ ಒಂದು ದಿವಸ ಮುಂದಾಗಿ ಯೆಹೋವನು ಸಮುವೇಲನಿಗೆ -",
			"16": "<sup>16</sup> ನಾಳೆ ಇಷ್ಟು ಹೊತ್ತಿಗೆ ಬೆನ್ಯಾಮೀನ್ ದೇಶದವನೊಬ್ಬನನ್ನು ನಿನ್ನ ಬಳಿಗೆ ಬರಮಾಡುವೆನು; ನೀನು ಅವನನ್ನು ಇಸ್ರಾಯೇಲ್ಯರ ನಾಯಕನನ್ನಾಗಿ ಅಭಿಷೇಕಿಸಬೇಕು. ಅವನು ನನ್ನ ಜನರನ್ನು ಫಿಲಿಷ್ಟಿಯರ ಕೈಯಿಂದ ಬಿಡಿಸಿ ಕಾಪಾಡುವನು. ಅವರ ಕೂಗು ನನಗೆ ಮುಟ್ಟಿತು; ಅವರನ್ನು ಕಟಾಕ್ಷಿಸಿದ್ದೇನೆ ಎಂದು ತಿಳಿಸಿದ್ದನು.",
			"17": "<sup>17</sup> ಸಮುವೇಲನು ಸೌಲನನ್ನು ಕಂಡಾಗ ಯೆಹೋವನು ಅವನಿಗೆ - ನಾನು ತಿಳಿಸಿದ್ದ ಮನುಷ್ಯನು ಇವನೇ; ಇವನೇ ನನ್ನ ಪ್ರಜೆಯನ್ನು ಆಳತಕ್ಕವನು ಎಂದು ಸೂಚಿಸಿದನು.",
			"18": "<sup>18</sup> ಊರುಬಾಗಲಿಗೆ ಬಂದು ಸೌಲನು ಎದುರಿಗೆ ಬಂದ ಸಮುವೇಲನನ್ನು ನೋಡಿ - ದರ್ಶಿಯ ಮನೆ ಎಲ್ಲಿದೆ ಎಂಬದನ್ನು ದಯವಿಟ್ಟು ತಿಳಿಸುವಿಯೋ ಎನ್ನಲು ಸಮುವೇಲನು -",
			"19": "<sup>19</sup> ನಾನೇ ದರ್ಶಿಯು; ನೀನು ನನ್ನ ಜೊತೆಯಲ್ಲಿ ಗುಡ್ಡಕ್ಕೆ ಬಾ; ನೀವು ಈ ಹೊತ್ತು ನನ್ನ ಸಂಗಡ ಊಟಮಾಡಬೇಕು. ನಿನ್ನ ಚಿಂತೆಯನ್ನೆಲ್ಲಾ ಪರಿಹರಿಸಿ ನಾಳೆ ಬೆಳಿಗ್ಗೆ ನಿನ್ನನ್ನು ಕಳುಹಿಸಿಕೊಡುವೆನು.",
			"20": "<sup>20</sup> ಮೂರು ದಿವಸಗಳ ಹಿಂದೆ ತಪ್ಪಿಸಿಕೊಂಡ ನಿನ್ನ ಕತ್ತೆಗಳಿಗೋಸ್ಕರ ಚಿಂತೆಮಾಡಬೇಡ; ಅವು ಸಿಕ್ಕಿವೆ. ಮತ್ತು ಇಸ್ರಾಯೇಲ್ಯರ ಶ್ರೇಷ್ಠವಸ್ತುಗಳೆಲ್ಲಾ ನಿನಗೂ ನಿನ್ನ ಕುಟುಂಬದವರಿಗೂ ಸಲ್ಲುವವಲ್ಲವೋ ಅಂದನು.",
			"21": "<sup>21</sup> ಅದಕ್ಕೆ ಸೌಲನು - ನಾನು ಇಸ್ರಾಯೇಲ್ ಕುಲಗಳಲ್ಲಿ ಅಲ್ಪವಾಗಿರುವ ಬೆನ್ಯಾಮೀನ್ ಕುಲದವನಲ್ಲವೋ? ಬೆನ್ಯಾಮೀನ್ಯರ ಎಲ್ಲಾ ಕುಟುಂಬಗಳಲ್ಲಿ ನನ್ನದು ಕನಿಷ್ಠವಾದದ್ದು; ನನಗೆ ಇಂಥ ದೊಡ್ಡ ಮಾತನ್ನು ಯಾಕೆ ಹೇಳುತ್ತೀ ಎಂದನು.",
			"22": "<sup>22</sup> ಸಮುವೇಲನು ಸೌಲನನ್ನೂ ಅವನ ಸೇವಕನನ್ನೂ ಮಂಟಪಕ್ಕೆ ಕರಕೊಂಡು ಹೋದನು; ಭೋಜನಕ್ಕೆ ಬಂದಿದ್ದ ಸುಮಾರು ಮೂವತ್ತು ಜನರಲ್ಲಿ ಅವರಿಬ್ಬರಿಗೂ ಮುಖ್ಯಸ್ಥಾನವನ್ನು ಕೊಟ್ಟನು. ಅನಂತರ ಸಮುವೇಲನು ಅಡಿಗೆಯವನಿಗೆ -",
			"23": "<sup>23</sup> ನಾನು ನಿನಗೆ ಹೇಳಿ ಪ್ರತ್ಯೇಕವಾಗಿ ಇಡಿಸಿದ್ದ ಮಾಂಸಭಾಗವನ್ನು ತಂದಿಡು ಅನ್ನಲು",
			"24": "<sup>24</sup> ಅವನು ಒಂದು ತೊಡೆಯ ಮಾಂಸವನ್ನೂ ಅದರ ಸಂಗಡ ಇದ್ದದ್ದನ್ನೂ ತಂದು ಸೌಲನ ಮುಂದಿಟ್ಟು ಅವನಿಗೆ - ಇಗೋ, ಇದು ನಿನಗೋಸ್ಕರ ಪ್ರತ್ಯೇಕಿಸಿದ್ದು; ಇದನ್ನು ತಿನ್ನು. ಜನರನ್ನು ಭೋಜನಕ್ಕೆ ಕರೆದಾಗಿನಿಂದ ಇದು ನಿನಗೋಸ್ಕರವಾಗಿಯೇ ಇಡಲ್ಪಟ್ಟಿತ್ತು ಎಂದು ಹೇಳಿದನು. ಸೌಲನು ಆ ದಿನ ಸಮುವೇಲನ ಸಂಗಡ ಊಟಮಾಡಿದನು.",
			"25": "<sup>25</sup> ಅವರು ಗುಡ್ಡದಿಂದಿಳಿದು ಪಟ್ಟಣಕ್ಕೆ ಬಂದನಂತರ ಸಮುವೇಲನು ಸೌಲನನ್ನು ತನ್ನ ಮನೇ ಮಾಳಿಗೆಯ ಮೇಲೆ ಕರೆದುಕೊಂಡು ಹೋಗಿ ಅವನೊಡನೆ ಮಾತಾಡಿದನು.",
			"26": "<sup>26</sup> ಮರುದಿನ ಬೆಳಿಗ್ಗೆ ಸಮುವೇಲನು ಮಾಳಿಗೆಯ ಮೇಲೆ ಮಲಗಿದ್ದ ಸೌಲನನ್ನು - ಏಳು, ನಿನ್ನನ್ನು ಸಾಗಕಳುಹಿಸುತ್ತೇನೆ ಎಂದು ಎಬ್ಬಿಸಲು ಅವನೆದ್ದನು. ತರುವಾಯ ಅವರಿಬ್ಬರೂ ಹೊರಟು ಊರ ಹೊರಗೆ ಬಂದಾಗ",
			"27": "<sup>27</sup> ಸಮುವೇಲನು ಸೌಲನಿಗೆ - ನಿನ್ನ ಸೇವಕನನ್ನು ಮುಂದೆ ಕಳುಹಿಸಿ ನೀನು ಇಲ್ಲಿ ಸ್ವಲ್ಪ ನಿಲ್ಲು; ನಿನಗೆ ತಿಳಿಸಬೇಕಾದ ದೈವೋಕ್ತಿಯೊಂದುಂಟು ಎಂದನು."
		},
		"10": {
			"1": "<sup>1</sup> ಆ ಆಳು ಮುಂದೆ ಹೋದಾಗ ಸಮುವೇಲನು ಎಣ್ಣೇ ಕುಪ್ಪಿಯಿಂದ ಅವನ ತಲೆಯ ಮೇಲೆ ತೈಲವನ್ನು ಹೊಯ್ದು ಅವನನ್ನು ಮುದ್ದಿಟ್ಟು ಅವನಿಗೆ - ಯೆಹೋವನು ತನ್ನ ಸ್ವಾಸ್ತ್ಯದ ಮೇಲೆ ಪ್ರಭುವಾಗಿರುವದಕ್ಕೋಸ್ಕರ ನಿಜವಾಗಿ ನಿನ್ನನ್ನು ಅಭಿಷೇಕಿಸಿದ್ದಾನೆ.",
			"2": "<sup>2</sup> ಈ ಹೊತ್ತು ನೀನು ನನ್ನನ್ನು ಬಿಟ್ಟುಹೋದ ನಂತರ ಬೆನ್ಯಾಮೀನ್ಯರ ಮೇರೆಯೊಳಗಿರುವ ರಾಹೇಲಳ ಸಮಾಧಿಯ ಬಳಿಯಲ್ಲಿರುವಂಥ ಚೆಲ್ಚಹಿನಲ್ಲಿ ಇಬ್ಬರು ಮನುಷ್ಯರನ್ನು ಕಾಣುವಿ; ಅವರು ನಿನಗೆ - ನೀನು ಹುಡುಕುತ್ತಿರುವ ಕತ್ತೆಗಳು ಸಿಕ್ಕಿದವು; ನಿನ್ನ ತಂದೆಯು ಕತ್ತೆಗಳ ಚಿಂತೆಬಿಟ್ಟು ನನ್ನ ಮಗನೆಲ್ಲಿ ಹೋದನೋ ಎಂಬದಾಗಿ ನಿನಗೋಸ್ಕರ ಹಂಬಲಿಸುತ್ತಿರುತ್ತಾನೆ ಎಂದು ಹೇಳುವರು.",
			"3": "<sup>3</sup> ನೀನು ಅಲ್ಲಿಂದ ಮುಂದೆ ನಡೆದು ತಾಬೋರಿನ ಹತ್ತಿರವಿರುವ ಏಲೋನ್ ವೃಕ್ಷದ ಬಳಿಗೆ ಬಂದಾಗ ದೇವದರ್ಶನಕ್ಕಾಗಿ ಬೇತೇಲಿಗೆ ಹೋಗುವ ಮೂವರು ಜನರು ನಿನಗೆದುರಾಗುವರು. ಅವರಲ್ಲೊಬ್ಬನು ಮೂರು ಹೋತಮರಿಗಳನ್ನೂ ಮತ್ತೊಬ್ಬನು ಮೂರು ರೊಟ್ಟಿಗಳನ್ನೂ ಇನ್ನೊಬ್ಬನು ದ್ರಾಕ್ಷಾರಸದ ಒಂದು ಬುದ್ದಲಿಯನ್ನೂ ಹೊತ್ತುಕೊಂಡಿರುವರು.",
			"4": "<sup>4</sup> ಅವರು ನಿನ್ನ ಕ್ಷೇಮಸಮಾಚಾರವನ್ನು ವಿಚಾರಿಸಿ ಎರಡು ರೊಟ್ಟಿಗಳನ್ನು ಕೊಡುವರು; ನೀನು ಅವುಗಳನ್ನು ತೆಗೆದುಕೊಳ್ಳಬೇಕು.",
			"5": "<sup>5</sup> ಅಲ್ಲಿಂದ ಫಿಲಿಷ್ಟಿಯರ ಠಾಣವಿರುವ ದೇವಗಿರಿಯನ್ನು ಮುಟ್ಟಿದಾಗ ಮುಂಗಡೆಯಲ್ಲಿ ಸ್ವರಮಂಡಲ, ದಮ್ಮಡಿ, ಕೊಳಲು, ಕಿನ್ನರಿ ಈ ವಾದ್ಯಗಾರರೊಡನೆ ಗುಡ್ಡದಿಂದಿಳಿದು ಬರುತ್ತಿರುವ ಪ್ರವಾದಿಸಮೂಹವನ್ನು ಕಾಣುವಿ. ಅವರು ಪರವಶರಾಗಿ ಪ್ರವಾದಿಸುವರು;",
			"6": "<sup>6</sup> ಆಗ ಯೆಹೋವನ ಆತ್ಮವು ನಿನ್ನ ಮೇಲೂ ಬರುವದರಿಂದ ನೀನೂ ಮಾರ್ಪಟ್ಟು ಪ್ರವಾದಿಸುವಿ.",
			"7": "<sup>7</sup> ಈ ಗುರುತುಗಳೆಲ್ಲಾ ಸಂಭವಿಸಿದಾಗ ನಿನಗೆ ಅನುಕೂಲವಿದ್ದದ್ದನ್ನು ನಡಿಸು; ದೇವರು ನಿನ್ನ ಸಂಗಡ ಇದ್ದಾನೆ.",
			"8": "<sup>8</sup> ನೀನು ಮುಂದಾಗಿ ಗಿಲ್ಗಾಲಿಗೆ ಹೋಗು; ಏಳು ದಿವಸಗಳಾದನಂತರ ನಾನು ಸರ್ವಾಂಗಹೋಮಗಳನ್ನೂ ಸಮಾಧಾನಯಜ್ಞಗಳನ್ನೂ ಸಮರ್ಪಿಸುವದಕ್ಕೋಸ್ಕರ ಅಲ್ಲಿಗೆ ಬಂದು ನೀನು ಮಾಡಬೇಕಾದದ್ದನ್ನು ನಿನಗೆ ತಿಳಿಸುವೆನು; ಅಲ್ಲಿಯವರೆಗೆ ಕಾದುಕೊಂಡಿರು ಎಂದು ಹೇಳಿದನು.",
			"9": "<sup>9</sup> ಸೌಲನು ಸಮುವೇಲನನ್ನು ಬಿಟ್ಟು ಹೊರಟ ಕೂಡಲೆ ದೇವರು ಅವನಿಗೆ ನೂತನ ಹೃದಯವನ್ನು ಕೊಟ್ಟನು. ಅದೇ ದಿನದಲ್ಲಿ ಆ ಎಲ್ಲಾ ಗುರುತುಗಳು ಸಂಭವಿಸಿದವು.",
			"10": "<sup>10</sup> ಅವನು ದೇವಗಿರಿಗೆ ಬಂದ ಕೂಡಲೆ ಪರವಶವಾದ ಪ್ರವಾದಿಸಮೂಹವು ತನ್ನೆದುರಿಗೆ ಬರುವದನ್ನು ಕಂಡನು. ದೇವರ ಆತ್ಮವು ಅವನ ಮೇಲೆ ಬಂದದರಿಂದ ಅವನೂ ಪರವಶನಾಗಿ ಅವರೊಳಗೆ ಸೇರಿ ಪ್ರವಾದಿಸಿದನು.",
			"11": "<sup>11</sup> ಅವನು ಪರವಶನಾಗಿ ಮಾತಾಡುವದನ್ನು ಗುರುತಿನವರು ಕಂಡು ತಮ್ಮ ತಮ್ಮೊಳಗೆ - ಕೀಷನ ಮಗನಿಗೆ ಏನಾಯಿತು? ಸೌಲನೂ ಪ್ರವಾದಿಗಳಲ್ಲಿದ್ದಾನೋ ಎಂದು ಮಾತಾಡಿಕೊಳ್ಳುತ್ತಿರುವಾಗ",
			"12": "<sup>12</sup> ಆ ಸ್ಥಳದವನೊಬ್ಬನು - ಹಾಗಾದರೆ ಇವರ ತಂದೆ ಯಾರು ಎಂದು ಕೇಳಿದನು. ಈ ಸಂಗತಿಯಿಂದ ಸೌಲನು ಪ್ರವಾದಿಗಳಲ್ಲಿದ್ದಾನೋ ಎಂಬ ಗಾದೆ ಹುಟ್ಟಿತು.",
			"13": "<sup>13</sup> ಅವನು ಪ್ರವಾದಿಸಿ ಆದ ಮೇಲೆ ಪೂಜಾಸ್ಥಳಕ್ಕೆ ಬಂದನು.",
			"14": "<sup>14</sup> ಸೌಲನ ಚಿಕ್ಕಪ್ಪನು ಅವನನ್ನೂ ಅವನ ಸೇವಕನನ್ನೂ - ನೀವು ಎಲ್ಲಿಗೆ ಹೋಗಿದ್ದಿರಿ ಎಂದು ಕೇಳಲು ಅವನು - ಕತ್ತೆಗಳನ್ನು ಹುಡುಕುವದಕ್ಕೆ ಹೋಗಿದ್ದೆವು. ಅವು ಸಿಕ್ಕಲಿಲ್ಲವಾದದರಿಂದ ಸಮುವೇಲನ ಬಳಿಗೆ ಹೋದೆವು ಅಂದನು.",
			"15": "<sup>15</sup> ಸಮುವೇಲನು ಏನು ಹೇಳಿದನೆಂದು ಚಿಕ್ಕಪ್ಪನು ಕೇಳಲು ಸೌಲನು -",
			"16": "<sup>16</sup> ಕತ್ತೆಗಳು ಸಿಕ್ಕಿದವೆಂದು ತಿಳಿಸಿದನು ಎಂಬದಾಗಿ ಹೇಳಿದನೇ ಹೊರತು ಅವನು ಹೇಳಿದ ರಾಜ್ಯದ ಮಾತನ್ನು ತಿಳಿಸಲಿಲ್ಲ.",
			"17": "<sup>17</sup> ಸಮುವೇಲನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಮಿಚ್ಪೆಗೆ ಕರಿಸಿ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಕೂಡಿಸಿಕೊಂಡು",
			"18": "<sup>18</sup> ಅವರಿಗೆ - ಇಸ್ರಾಯೇಲ್ ದೇವರಾದ ಯೆಹೋವನು ಹೇಳುವದನ್ನು ಕೇಳಿರಿ; ಆತನು ನಿಮಗೆ - ನೀವು ಐಗುಪ್ತ್ಯರ ಕೈಗೆ ತಪ್ಪಿಸಿಕೊಳ್ಳುವಂತೆ ನಿಮ್ಮನ್ನು ಅವರ ದೇಶದಿಂದ ಬಿಡಿಸಿದವನೂ ಬಾಧಿಸುತ್ತಿದ್ದ ಬೇರೆ ಎಲ್ಲಾ ಜನಾಂಗಗಳಿಂದ ನಿಮ್ಮನ್ನು ರಕ್ಷಿಸಿದವನೂ ನಾನೇ.",
			"19": "<sup>19</sup> ನೀವಾದರೋ ನಿಮ್ಮನ್ನು ಕಷ್ಟಸಂಕಟಗಳಿಂದ ಬಿಡಿಸಿದ ನಿಮ್ಮ ದೇವರಾದ ನನ್ನನ್ನು ಈಗ ತಿರಸ್ಕರಿಸಿ ನಮಗೊಬ್ಬ ಅರಸನನ್ನು ನೇಮಿಸೆಂದು ಹೇಳುತ್ತೀರಿ ಅನ್ನುತ್ತಾನೆ. ಆದದರಿಂದ ನೀವು ಕುಲಕುಲವಾಗಿಯೂ ಗೋತ್ರಗೋತ್ರವಾಗಿಯೂ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಕಾಣಿಸಿಕೊಳ್ಳಿರಿ ಎಂದು ಅಪ್ಪಣೆ ಮಾಡಿದನು.",
			"20": "<sup>20</sup> ಸಮುವೇಲನು ಇಸ್ರಾಯೇಲ್ಯರ ಎಲ್ಲಾ ಕುಲಗಳನ್ನು ಸಮೀಪಕ್ಕೆ ಕರೆದಾಗ ಬೆನ್ಯಾಮೀನ್ ಕುಲಕ್ಕೆ ಚೀಟುಬಿದ್ದಿತು.",
			"21": "<sup>21</sup> ಆ ಕುಲದ ಗೋತ್ರಗಳನ್ನು ಬರಮಾಡಿದಾಗ ಮಟ್ರಿಯ ಗೋತ್ರದಲ್ಲಿ ಕೀಷನ ಮಗನಾದ ಸೌಲನಿಗೆ ಚೀಟು ಬಿದ್ದಿತು. ಅವನನ್ನು ಹುಡುಕಲು ಅವನು ಸಿಕ್ಕಲಿಲ್ಲ.",
			"22": "<sup>22</sup> ಅವರು - ಆ ಮನುಷ್ಯನು ಇಲ್ಲಿಗೆ ಬರುವನೋ ಎಂದು ಯೆಹೋವನನ್ನು ವಿಚಾರಿಸಿದಾಗ ಆತನು - ಬಂದಿದ್ದಾನೆ; ಇಗೋ, ಸಾಮಾನುಗಳಲ್ಲಿ ಅಡಗಿಕೊಂಡಿದ್ದಾನೆ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"23": "<sup>23</sup> ಅವರು ತಟ್ಟನೆ ಹೋಗಿ ಅವನನ್ನು ಹಿಡಿದುಕೊಂಡು ಬಂದು ಜನರ ಮಧ್ಯದಲ್ಲಿ ನಿಲ್ಲಿಸಿದರು. ಎಲ್ಲಾ ಜನರ ಗುಂಪಿನಲ್ಲಿ ಅವನ ಹೆಗಲೂ ತಲೆಯೂ ಕಾಣುವಷ್ಟು ಉನ್ನತಪುರುಷನಾಗಿದ್ದನು.",
			"24": "<sup>24</sup> ಆಗ ಸಮುವೇಲನು ಎಲ್ಲಾ ಜನರಿಗೆ - ನೋಡಿದಿರಾ, ಯೆಹೋವನಿಂದ ಆರಿಸಲ್ಪಟ್ಟವನು ಇವನೇ; ಸರ್ವಜನರಲ್ಲಿ ಇವನಿಗೆ ಸಮಾನರು ಇಲ್ಲವೇ ಇಲ್ಲ ಎನ್ನಲು ಜನರೆಲ್ಲರೂ - ಅರಸನು ಚಿರಂಜೀವಿಯಾಗಿರಲಿ ಎಂದು ಆರ್ಭಟಿಸಿದರು.",
			"25": "<sup>25</sup> ತರುವಾಯ ಸಮುವೇಲನು ಜನರಿಗೆ ರಾಜನೀತಿಯನ್ನು ಬೋಧಿಸಿ ಅದನ್ನು ಒಂದು ಪುಸ್ತಕದಲ್ಲಿ ಬರೆದು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿಟ್ಟನು; ಆಮೇಲೆ ಜನರನ್ನು ಅವರವರ ಮನೆಗಳಿಗೆ ಕಳುಹಿಸಿಬಿಟ್ಟನು.",
			"26": "<sup>26</sup> ಸೌಲನೂ ಗಿಬೆಯದಲ್ಲಿರುವ ತನ್ನ ಮನೆಗೆ ಹೋದನು; ಕೆಲವು ಮಂದಿ ಶೂರರು ದೇವರಿಂದ ಪ್ರೇರಿತರಾಗಿ ಅವನ ಜೊತೆಯಲ್ಲಿ ಹೋದರು.",
			"27": "<sup>27</sup> ಕೆಲವು ಮಂದಿ ಕಾಕಪೋಕರು - ಇವನೋ ನಮ್ಮನ್ನು ರಕ್ಷಿಸುವವನು ಎಂದು ತಿರಸ್ಕರಿಸಿ ಕಾಣಿಕೆಗಳನ್ನು ತರಲಿಲ್ಲ. ಆದರೂ ಸೌಲನು ಸುಮ್ಮನಿದ್ದನು."
		},
		"11": {
			"1": "<sup>1</sup> ಅಮ್ಮೋನಿಯನಾದ ನಾಹಾಷನು ಹೊರಟುಬಂದು ಯಾಬೇಷ್ ಗಿಲ್ಯಾದಿಗೆ ಮುತ್ತಿಗೆಹಾಕಲು ಯಾಬೇಷಿನವರು ಅವನನ್ನು - ನೀನು ನಮ್ಮ ಸಂಗಡ ಒಡಂಬಡಿಕೆ ಮಾಡಿಕೋ; ನಾವು ನಿನ್ನ ಸೇವಕರಾಗಿರುವೆವು ಎಂದು ಬೇಡಿಕೊಂಡರು.",
			"2": "<sup>2</sup> ಅದಕ್ಕೆ ಅವನು - ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಅವಮಾನಪಡಿಸುವದಕ್ಕಾಗಿ ನಾನು ಮೊದಲು ನಿಮ್ಮಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬರ ಬಲಗಣ್ಣನ್ನು ಕಿತ್ತುಹಾಕುತ್ತೇನೆ. ಇದಕ್ಕೆ ನೀವು ಒಪ್ಪುವಿರಾದರೆ ನಿಮ್ಮ ಸಂಗಡ ಒಡಂಬಡಿಕೆಮಾಡಿಕೊಳ್ಳುತ್ತೇನೆ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"3": "<sup>3</sup> ಆಗ ಯಾಬೇಷಿನ ಹಿರಿಯರು ಅವನಿಗೆ - ಏಳು ದಿವಸಗಳವರೆಗೆ ತಡೆ; ಅಷ್ಟರೊಳಗೆ ನಾವು ಇಸ್ರಾಯೇಲ್ಯರ ಎಲ್ಲಾ ಪ್ರಾಂತ್ಯಗಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸುತ್ತೇವೆ. ನಮ್ಮನ್ನು ಯಾರೂ ರಕ್ಷಿಸದಿದ್ದರೆ ನಾವು ನಿನ್ನ ಬಳಿಗೆ ಹೊರಟುಬರುವೆವು ಎಂದು ತಿಳಿಸಿದರು.",
			"4": "<sup>4</sup> ದೂತರು ಸೌಲನು ವಾಸವಾಗಿದ್ದ ಗಿಬೆಯಕ್ಕೆ ಬಂದು ಅಲ್ಲಿನವರಿಗೆ ಈ ವರ್ತಮಾನವನ್ನು ತಿಳಿಸಲು ಎಲ್ಲರೂ ಗಟ್ಟಿಯಾಗಿ ಅಳತೊಡಗಿದರು.",
			"5": "<sup>5</sup> ಅಷ್ಟರಲ್ಲಿ ಸೌಲನು ಹೊಲದಿಂದ ದನಗಳನ್ನು ಹೊಡೆದುಕೊಂಡು ಬಂದನು. ಅವನು - ಜನರು ಗೋಳಾಡುವದಕ್ಕೇನು ಕಾರಣವೆಂದು ಕೇಳಲು ಅವನಿಗೆ ಯಾಬೇಷಿನವರ ವರ್ತಮಾನವನ್ನು ತಿಳಿಸಿದರು.",
			"6": "<sup>6</sup> ಆಗ ಯೆಹೋವನ ಆತ್ಮವು ಅವನ ಮೇಲೆ ಬಂದಿತು. ಅವನು ಅತ್ಯಂತ ಕುಪಿತನಾಗಿ",
			"7": "<sup>7</sup> ಒಂದು ಜೋಡಿ ಎತ್ತುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ತುಂಡು ತುಂಡು ಮಾಡಿ ಆ ತುಂಡುಗಳನ್ನು ದೂತರ ಮುಖಾಂತರ ಇಸ್ರಾಯೇಲ್ಯರ ಎಲ್ಲಾ ಪ್ರಾಂತಗಳಿಗೆ ಕಳುಹಿಸಿ - ಯಾರು ಸೌಲ ಸಮುವೇಲರನ್ನು ಹಿಂಬಾಲಿಸುವದಿಲ್ಲವೋ ಅವರ ಎತ್ತುಗಳು ಹೀಗೆಯೇ ಕಡಿಯಲ್ಪಡುವವು ಎಂದು ಹೇಳಿಸಿದನು. ಯೆಹೋವನ ಭಯಪ್ರೇರಿತರಾಗಿ ಜನರೆಲ್ಲರೂ ಏಕಮನಸ್ಸಿನಿಂದ ಕೂಡಿ ಬಂದರು.",
			"8": "<sup>8</sup> ಅವರನ್ನು ಬೆಜೆಕಿನಲ್ಲಿ ಕ್ರಮಪಡಿಸಿ ಲೆಕ್ಕಿಸಿದಾಗ ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಮೂರು ಲಕ್ಷ ಸೈನಿಕರೂ ಯೆಹೂದ್ಯರಲ್ಲಿ ಮೂವತ್ತು ಸಾವಿರ ಸೈನಿಕರೂ ಇದ್ದರು.",
			"9": "<sup>9</sup> ಇವರು ಬಂದಿದ್ದ ದೂತರಿಗೆ - ನಾಳೆ ಮಧ್ಯಾಹ್ನದಲ್ಲಿ ನಿಮಗೆ ಸಹಾಯಸಿಕ್ಕುವದೆಂದು ಯಾಬೇಷ್ ಗಿಲ್ಯಾದಿನವರಿಗೆ ತಿಳಿಸಿರಿ ಎಂದು ಹೇಳಿ ಅವರನ್ನು ಕಳುಹಿಸಿದರು.",
			"10": "<sup>10</sup> ದೂತರು ಹೋಗಿ ಈ ವರ್ತಮಾನವನ್ನು ತಿಳಿಸಲು ಯಾಬೇಷಿನವರು ಬಹಳವಾಗಿ ಸಂತೋಷಪಟ್ಟು ಅಮ್ಮೋನಿಯರಿಗೆ - ನಾವು ನಾಳೆ ನಿಮ್ಮ ಬಳಿಗೆ ಬರುವೆವು; ನಿಮಗೆ ಸರಿಕಾಣುವ ಪ್ರಕಾರ ಮಾಡಿರಿ ಎಂದು ಹೇಳಿದರು.",
			"11": "<sup>11</sup> ಸೌಲನು ಬೆಳಗಿನ ಜಾವದಲ್ಲಿ ತನ್ನ ಜನರನ್ನು ಮೂರು ಪಾಲು ಮಾಡಿ ಅಮ್ಮೋನಿಯರ ಪಾಳೆಯದೊಳಕ್ಕೆ ನುಗ್ಗಿ ಅವರನ್ನು ಮಧ್ಯಾಹ್ನದವರೆಗೆ ಹತಮಾಡಿದನು; ಉಳಿದವರನ್ನು ಒಬ್ಬರನ್ನೊಬ್ಬರು ಕೂಡದ ಹಾಗೆ ಚದರಿಸಿಬಿಟ್ಟನು.",
			"12": "<sup>12</sup> ತರುವಾಯ ಜನರು ಸಮುವೇಲನಿಗೆ - ಸೌಲನು ನಮಗೆ ಅರಸನಾಗಬಾರದೆಂದು ಹೇಳಿದವರಾರು? ಅವರನ್ನು ನಮಗೆ ಒಪ್ಪಿಸಿರಿ; ನಾವು ಕೊಂದುಹಾಕುತ್ತೇವೆ ಎನ್ನಲು ಸೌಲನು ಅವರಿಗೆ -",
			"13": "<sup>13</sup> ಯೆಹೋವನು ಈ ಹೊತ್ತು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಜಯವನ್ನುಂಟುಮಾಡಿರುವದರಿಂದ ಯಾರನ್ನೂ ಕೊಲ್ಲಬಾರದು ಎಂದು ಹೇಳಿದನು.",
			"14": "<sup>14</sup> ಸಮುವೇಲನು ಜನರಿಗೆ - ಬನ್ನಿರಿ; ಗಿಲ್ಗಾಲಿಗೆ ಹೋಗಿ ಅರಸುತನವನ್ನು ಸ್ಥಿರಪಡಿಸೋಣ ಅನ್ನಲು",
			"15": "<sup>15</sup> ಅವರೆಲ್ಲರೂ ಅಲ್ಲಿಗೆ ಹೋಗಿ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸೌಲನ ಅರಸುತನವನ್ನು ಸ್ಥಿರಪಡಿಸಿ ಯೆಹೋವನಿಗೆ ಸಮಾಧಾನಯಜ್ಞಗಳನ್ನು ಸಮರ್ಪಿಸಿ ಸೌಲನೊಡನೆ ಬಹಳವಾಗಿ ಆನಂದಿಸಿದರು."
		},
		"12": {
			"1": "<sup>1</sup> ಸಮುವೇಲನು ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ - ನೋಡಿರಿ, ನಾನು ನಿಮ್ಮ ಬಿನ್ನಹಗಳನ್ನು ಲಾಲಿಸಿ ನಿಮಗೊಬ್ಬ ಅರಸನನ್ನು ನೇಮಿಸಿದೆನು.",
			"2": "<sup>2</sup> ಇನ್ನು ಮುಂದೆ ಇವನೇ ನಿಮ್ಮನ್ನು ನಡಿಸುವನು. ಚಿಕ್ಕಂದಿನಿಂದ ನಿಮ್ಮ ನಾಯಕನಾಗಿದ್ದ ನಾನು ಈಗ ತಲೆನರೆತ ಮುದುಕನಾಗಿದ್ದೇನೆ; ನನ್ನ ಮಕ್ಕಳು ನಿಮ್ಮ ಮಧ್ಯದಲ್ಲಿದ್ದಾರೆ.",
			"3": "<sup>3</sup> ಇಲ್ಲಿ ನಿಂತುಕೊಂಡಿರುವ ನಾನು ಯಾರ ಎತ್ತನ್ನಾಗಲಿ ಕತ್ತೆಯನ್ನಾಗಲಿ ತೆಗೆದುಕೊಂಡು ಯಾರನ್ನಾದರೂ ವಂಚಿಸಿ ಪೀಡಿಸಿದ್ದೂ ಲಂಚತೆಗೆದುಕೊಂಡು ಕುರುಡನಂತೆ ತೀರ್ಪುಮಾಡಿದ್ದೂ ಉಂಟೋ? ಇದ್ದರೆ ಯೆಹೋವನ ಮುಂದೆಯೂ ಆತನ ಅಭಿಷಿಕ್ತನ ಮುಂದೆಯೂ ಹೇಳಿರಿ; ನಾನು ಅದನ್ನು ಹಿಂದಕ್ಕೆ ಕೊಡುತ್ತೇನೆ ಅನ್ನಲು ಅವರು -",
			"4": "<sup>4</sup> ನೀನು ನಮ್ಮನ್ನು ವಂಚಿಸಿ ಪೀಡಿಸಿದ್ದಾಗಲಿ ನಮ್ಮಿಂದ ಏನಾದರೂ ಕಸಕೊಂಡದ್ದಾಗಲಿ ಇರುವದಿಲ್ಲ ಎಂದು ಉತ್ತರಕೊಟ್ಟರು.",
			"5": "<sup>5</sup> ಸಮುವೇಲನು ತಿರಿಗಿ - ನೀವು ನನ್ನಲ್ಲಿ ಇಂಥದನ್ನೇನೂ ಕಾಣಲಿಲ್ಲವೆಂಬದಕ್ಕೆ ಯೆಹೋವನೂ ಆತನ ಅಭಿಷಿಕ್ತನೂ ಸಾಕ್ಷಿಯಾಗಿದ್ದಾರೆ ಎನ್ನಲು ಅವರು - ಹೌದು, ಸಾಕ್ಷಿಯಾಗಿದ್ದಾರೆ ಎಂದು ನುಡಿದರು.",
			"6": "<sup>6</sup> ಆಗ ಸಮುವೇಲನು - ನಿಮ್ಮ ಪಿತೃಗಳನ್ನು ಐಗುಪ್ತದಿಂದ ಕರತರುವದಕ್ಕಾಗಿ ಮೋಶೆ ಆರೋನರನ್ನು ಏರ್ಪಡಿಸಿದ ಯೆಹೋವನೇ ಇದಕ್ಕೆ ಸಾಕ್ಷಿ.",
			"7": "<sup>7</sup> ಇಲ್ಲಿ ನಿಂತುಕೊಂಡು ಕೇಳಿರಿ; ಯೆಹೋವನು ನಿಮಗೋಸ್ಕರವೂ ನಿಮ್ಮ ಪಿತೃಗಳಿಗೋಸ್ಕರವೂ ನಡಿಸಿದ ನೀತಿಕಾರ್ಯಗಳನ್ನು ಕುರಿತು ಆತನೆದುರಿನಲ್ಲಿ ನಿಮಗೆ ನೆನಪು ಹುಟ್ಟಿಸಿ ನಿಮ್ಮನ್ನು ಎಚ್ಚರಿಸುತ್ತೇನೆ.",
			"8": "<sup>8</sup> ಐಗುಪ್ತಕ್ಕೆ ಬಂದ ಯಾಕೋಬನ ವಂಶದವರಾದ ನಮ್ಮ ಹಿರಿಯರು ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಟ್ಟಾಗ ಆತನು ಮೋಶೆ ಆರೋನರ ಮುಖಾಂತರ ಅವರನ್ನು ಐಗುಪ್ತದಿಂದ ಬಿಡಿಸಿ ಈ ದೇಶದಲ್ಲಿ ನೆಲೆಗೊಳಿಸಿದನು.",
			"9": "<sup>9</sup> ಅವರು ತಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಮರೆತುಬಿಡಲು ಆತನು ಅವರನ್ನು ಹಾಚೋರಿನ ಸೇನಾಪತಿಯಾದ ಸೀಸೆರನಿಗೂ ಫಿಲಿಷ್ಟಿಯರಿಗೂ ಮೋವಾಬ್‍ರಾಜನಿಗೂ ಮಾರಿಬಿಟ್ಟನು. ಇವರು ಬಂದು ಅವರೊಡನೆ ಯುದ್ಧಮಾಡಿದರು.",
			"10": "<sup>10</sup> ಆಗ ಅವರು ಆತನಿಗೆ - ನಾವು ಯೆಹೋವನಾದ ನಿನ್ನನ್ನು ಬಿಟ್ಟು ಬಾಳ್, ಅಷ್ಟೋರೆತ್ ಎಂಬ ದೇವತೆಗಳನ್ನು ಪೂಜಿಸಿ ಪಾಪಮಾಡಿದ್ದೇವೆ. ಈಗ ಕೃಪೆಮಾಡಿ ನಮ್ಮನ್ನು ಶತ್ರುಗಳ ಕೈಯಿಂದ ಬಿಡಿಸು; ನಾವು ಇನ್ನು ಮುಂದೆ ನಿನ್ನನ್ನೇ ಸೇವಿಸುವೆವು ಎಂದು ಮೊರೆಯಿಟ್ಟರು.",
			"11": "<sup>11</sup> ಯೆಹೋವನು ಯೆರುಬ್ಬಾಳ್, ಬಾರಾಕ್, ಯೆಫ್ತಾಹ, ಸಮುವೇಲ್ ಇವರನ್ನು ಕಳುಹಿಸಿ ನಿಮ್ಮನ್ನು ಎಲ್ಲಾ ಶತ್ರುಗಳ ಕೈಗೆ ತಪ್ಪಿಸಿ ಸುರಕ್ಷಿತವಾಗಿ ವಾಸಿಸುವಂತೆ ಮಾಡಿದನು.",
			"12": "<sup>12</sup> ಅಮ್ಮೋನಿಯರ ಅರಸನಾದ ನಾಹಾಷನು ನಿಮ್ಮ ಮೇಲೆ ಯುದ್ಧಕ್ಕೆ ಬರುತ್ತಾನೆಂದು ನಿಮಗೆ ಗೊತ್ತಾಗಲು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿಮ್ಮ ಅರಸನಾಗಿದ್ದರೂ ನೀವು - ನಮಗೊಬ್ಬ ಅರಸನನ್ನು ನೇಮಿಸೆಂದು ನನ್ನನ್ನು ಬೇಡಿಕೊಂಡಿರಿ.",
			"13": "<sup>13</sup> ಇಗೋ, ನೀವು ಅಪೇಕ್ಷಿಸಿ ಆರಿಸಿಕೊಂಡ ಅರಸನು ಇವನೇ; ಯೆಹೋವನು ಇವನನ್ನು ನಿಮ್ಮ ಮೇಲೆ ಅರಸನನ್ನಾಗಿ ನೇಮಿಸಿದ್ದಾನೆ.",
			"14": "<sup>14</sup> ನೀವೂ ನಿಮ್ಮ ಅರಸನೂ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿದ್ದುಕೊಂಡು ವಿಧೇಯರಾಗಿ ಆತನನ್ನು ಸೇವಿಸುತ್ತಾ ತಪ್ಪದೆ ಆತನ ಮಾತು ಕೇಳುವವರಾಗಿ ಆತನನ್ನೇ ಹೊಂದಿಕೊಂಡಿರುವದಾದರೆ ಎಷ್ಟೋ ಒಳ್ಳೇದು.",
			"15": "<sup>15</sup> ನೀವು ಯೆಹೋವನ ಮಾತನ್ನು ಕೇಳದೆಯೂ ಆತನ ಆಜ್ಞೆಗಳನ್ನು ಕೈಕೊಳ್ಳದೆಯೂ ಹೋದರೆ ಆತನ ಹಸ್ತವು ನಿಮ್ಮ ಹಿರಿಯರಿಗೆ ವಿರೋಧವಾಗಿದ್ದಂತೆ ನಿಮಗೂ ವಿರೋಧವಾಗಿಯೇ ಇರುವದು.",
			"16": "<sup>16</sup> ಈಗ ಯೆಹೋವನು ನಿಮ್ಮ ಕಣ್ಣುಮುಂದೆ ಮಾಡುವ ಮಹತ್ಕಾರ್ಯವನ್ನು ಹತ್ತಿರ ಬಂದು ನೋಡಿರಿ.",
			"17": "<sup>17</sup> ನಾನು ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಡುವೆನು; ಈಗ ಗೋದಿಯ ಸುಗ್ಗಿಯಿದ್ದರೂ ಆತನು ಗುಡುಗನ್ನೂ ಮಳೆಯನ್ನೂ ಕಳುಹಿಸುವದರ ಮೂಲಕ ನೀವು ಅರಸನನ್ನು ಕೇಳಿಕೊಂಡದ್ದು ತನ್ನ ದೃಷ್ಟಿಯಲ್ಲಿ ಎಷ್ಟೋ ಕೆಟ್ಟದ್ದಾಗಿದೆ ಎಂಬದನ್ನು ತೋರಿಸಿಕೊಡುವನು ಎಂದು ಅವರಿಗೆ ಹೇಳಿ ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಡಲು",
			"18": "<sup>18</sup> ಆತನು ಗುಡುಗು ಮಳೆಗಳನ್ನು ಕಳುಹಿಸಿದನು. ಎಲ್ಲಾ ಜನರೂ ಯೆಹೋವನಿಗೂ ಸಮುವೇಲನಿಗೂ ಬಹಳವಾಗಿ ಭಯಪಟ್ಟು ಸಮುವೇಲನನ್ನು -",
			"19": "<sup>19</sup> ನಮಗೊಬ್ಬ ಅರಸನು ಬೇಕೆಂದು ನಾವು ಬೇಡಿಕೊಂಡದರಿಂದ ನಮ್ಮ ಪಾಪಗಳಿಗೆ ಮತ್ತೊಂದು ಪಾಪವು ಕೂಡಿತು; ಆದದರಿಂದ ನಿನ್ನ ಸೇವಕರಾದ ನಾವು ಸಾಯದಂತೆ ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಬೇಡಿಕೋ ಎಂದು ವಿಜ್ಞಾಪಿಸಲು ಅವನು -",
			"20": "<sup>20</sup> ಭಯಪಡಬೇಡಿರಿ; ಇಷ್ಟು ಪಾಪಮಾಡಿದ ನೀವು ಇನ್ನು ಮುಂದೆಯಾದರೂ ಅದನ್ನು ಬಿಟ್ಟು ಯೆಹೋವನನ್ನು ಅಂಟಿಕೊಂಡು ಪೂರ್ಣಮನಸ್ಸಿನಿಂದ ಆತನೊಬ್ಬನನ್ನೇ ಸೇವಿಸಿರಿ.",
			"21": "<sup>21</sup> ದೇವರಲ್ಲದವುಗಳನ್ನು ಹಿಂಬಾಲಿಸಬೇಡಿರಿ; ಅವುಗಳಿಂದ ನಿಮಗೆ ಲಾಭವೂ ರಕ್ಷಣೆಯೂ ಸಿಕ್ಕುವದಿಲ್ಲ. ಅವು ವ್ಯರ್ಥವಾದವುಗಳೇ.",
			"22": "<sup>22</sup> ಯೆಹೋವನು ದಯದಿಂದ ನಿಮ್ಮನ್ನು ಸ್ವಕೀಯಜನವನ್ನಾಗಿ ಆರಿಸಿಕೊಂಡ ಮೇಲೆ ಆತನು ತನ್ನ ಮಹೋನ್ನತ ನಾಮದ ನಿಮಿತ್ತವಾಗಿ ನಿಮ್ಮನ್ನು ಕೈಬಿಡುವದೇ ಇಲ್ಲ.",
			"23": "<sup>23</sup> ನಾನಾದರೋ ನಿಮಗೋಸ್ಕರವಾಗಿ ಯೆಹೋವನನ್ನು ಪ್ರಾರ್ಥಿಸುತ್ತಾ ಆತನ ಉತ್ತಮ ನೀತಿಮಾರ್ಗವನ್ನು ನಿಮಗೆ ತೋರಿಸಿಕೊಡುವದನ್ನು ಬಿಡುವದೇ ಇಲ್ಲ; ಬಿಟ್ಟರೆ ಆತನ ದೃಷ್ಟಿಯಲ್ಲಿ ಪಾಪಿಯಾಗಿರುವೆನು.",
			"24": "<sup>24</sup> ನೀವಾದರೋ ಯೆಹೋವನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿದ್ದು ಆತನು ನಿಮಗೋಸ್ಕರವಾಗಿ ಮಾಡಿದ ಮಹತ್ಕಾರ್ಯಗಳನ್ನು ನೆನಸಿಕೊಂಡು ಆತನನ್ನು ಸತ್ಯದಿಂದಲೂ ಪೂರ್ಣಮನಸ್ಸಿನಿಂದಲೂ ಸೇವಿಸುತ್ತಾ ಬರಬೇಕು;",
			"25": "<sup>25</sup> ನೀವು ದ್ರೋಹಿಗಳಾಗಿಯೇ ಇದ್ದರೆ ನಿಮ್ಮ ಅರಸನೊಡನೆ ನಾಶವಾಗುವಿರಿ ಎಂದು ಹೇಳಿದನು."
		},
		"13": {
			"1": "<sup>1</sup> ಸೌಲನು ಅರಸನಾದಾಗ [ಮೂವತ್ತು] ವರುಷದವನಾಗಿದ್ದನು. ಅವನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಎರಡು ವರುಷ ಆಳಿದನಂತರ",
			"2": "<sup>2</sup> ಅವರಿಂದ ಮೂರು ಸಾವಿರ ಮಂದಿ ಸೈನಿಕರನ್ನು ಆರಿಸಿಕೊಂಡು ಅವರಲ್ಲಿ ಎರಡು ಸಾವಿರ ಜನರನ್ನು ಮಿಕ್ಮಾಷಿನಲ್ಲಿಯೂ ಬೇತೇಲಿನ ಗುಡ್ಡದಲ್ಲಿಯೂ ಇಟ್ಟುಕೊಂಡನು. ಉಳಿದ ಸಾವಿರ ಜನರನ್ನು ಬೆನ್ಯಾಮೀನ್ಯರ ಗಿಬೆಯದಲ್ಲಿದ್ದ ಯೋನಾತಾನನ ವಶಕ್ಕೆ ಕೊಟ್ಟನು.",
			"3": "<sup>3</sup> ಬೇರೆ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಅವರವರ ಮನೆಗಳಿಗೆ ಕಳುಹಿಸಿದನು. ಯೋನಾತಾನನು ಗೆಬದಲ್ಲಿದ್ದ ಫಿಲಿಷ್ಟಿಯರ ಠಾಣವನ್ನು ನಾಶಮಾಡಿದನು. ಈ ವರ್ತಮಾನವು ಫಿಲಿಷ್ಟಿಯರಿಗೆ ಮುಟ್ಟಿತು. ಈ ಸಂಗತಿಯು ಎಲ್ಲಾ ಇಬ್ರಿಯರಿಗೂ ಗೊತ್ತಾಗುವಂತೆ ಸೌಲನು ದೇಶದಲ್ಲೆಲ್ಲಾ ಕೊಂಬೂದಿಸಿದನು.",
			"4": "<sup>4</sup> ಸೌಲನು ಫಿಲಿಷ್ಟಿಯರ ಠಾಣವನ್ನು ನಾಶಮಾಡಿದ್ದರಿಂದ ತಾವು ಅವರ ದ್ವೇಷಕ್ಕೆ ಗುರಿಯಾದೆವೆಂದು ಇಸ್ರಾಯೇಲ್ಯರು ತಿಳಿದು ಗಿಲ್ಗಾಲಿಗೆ ಬಂದು ಸೌಲನನ್ನು ಕೂಡಿಕೊಂಡರು.",
			"5": "<sup>5</sup> ಆಗ ಫಿಲಿಷ್ಟಿಯರು ಇಸ್ರಾಯೇಲ್ಯರೊಡನೆ ಯುದ್ಧಮಾಡುವದಕ್ಕೋಸ್ಕರ ಮೂವತ್ತು ಸಾವಿರ ರಥಬಲವನ್ನೂ ಆರು ಸಾವಿರ ಅಶ್ವಬಲವನ್ನೂ ಸಮುದ್ರದ ಮರಳಿನಷ್ಟು ಅಸಂಖ್ಯವಾದ ಕಾಲ್ಬಲವನ್ನೂ ತೆಗೆದುಕೊಂಡು ಬಂದು ಬೇತಾವೆನಿನ ಪೂರ್ವದಿಕ್ಕಿನಲ್ಲಿರುವ ಮಿಕ್ಮಾಷಿನಲ್ಲಿ ಪಾಳೆಯ ಮಾಡಿಕೊಂಡರು.",
			"6": "<sup>6</sup> ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಕೇಡು ಬಂದಿತು. ತಾವು ಇಕ್ಕಟ್ಟಿನಲ್ಲಿದ್ದೇವೆಂದು ಅವರು ತಿಳಿದು ಗವಿ, ಕಾಡು, ಬಂಡೆ, ನೆಲಮನೆ, ಗುಂಡಿ ಇವುಗಳಲ್ಲಿ ಅಡಗಿಕೊಂಡರು.",
			"7": "<sup>7</sup> ಕೆಲವರು ಯೊರ್ದನ್ ಹೊಳೆದಾಟಿ ಗಾದ್ಯರ ದೇಶಕ್ಕೂ ಗಿಲ್ಯಾದ್ ಪ್ರಾಂತಕ್ಕೂ ಹೊರಟು ಹೋದರು. ಸೌಲನು ಇನ್ನೂ ಗಿಲ್ಗಾಲಿನಲ್ಲೇ ಇದ್ದನು. ಅವನ ಸಂಗಡ ಇದ್ದವರು ಭಯದಿಂದ ನಡುಗುತ್ತಿದ್ದರು.",
			"8": "<sup>8</sup> ಸೌಲನು ಸಮುವೇಲನ ಆಜ್ಞೆಯಂತೆ ಗಿಲ್ಗಾಲಿನಲ್ಲೇ ಏಳು ದಿವಸ ಇದ್ದರೂ ಸಮುವೇಲನು ಬರಲಿಲ್ಲ; ಅಷ್ಟರಲ್ಲಿ ಜನರು ಚದರಿಹೋದರು.",
			"9": "<sup>9</sup> ಆಗ ಸೌಲನು ಜನರಿಂದ ಸರ್ವಾಂಗಹೋಮಕ್ಕೂ ಸಮಾಧಾನಯಜ್ಞಕ್ಕೂ ಬೇಕಾದದ್ದನ್ನು ತರಿಸಿ ಸರ್ವಾಂಗಹೋಮವನ್ನು ಅರ್ಪಿಸಿದನು.",
			"10": "<sup>10</sup> ಅದು ತೀರುವಷ್ಟರಲ್ಲೇ ಸಮುವೇಲನು ಬಂದನು. ಸೌಲನು ಅವನನ್ನು ವಂದಿಸುವದಕ್ಕೋಸ್ಕರ ಎದುರಿಗೆ ಹೋಗಲು",
			"11": "<sup>11</sup> ಸಮುವೇಲನು ಅವನನ್ನು - ಇದೇನು ನೀನು ಮಾಡಿದ್ದು ಎಂದು ಕೇಳಲು ಅವನು - ಜನರು ಚದರಿಹೋಗುವದನ್ನೂ ನೀನು ನಿಯಮಿತಕಾಲದಲ್ಲಿ ಬಾರದಿರುವದನ್ನೂ ಫಿಲಿಷ್ಟಿಯರು ಮಿಕ್ಮಾಷಿನಲ್ಲಿ ನಮಗೆ ವಿರೋಧವಾಗಿ ಕೂಡಿಕೊಂಡಿರುವದನ್ನೂ ನೋಡಿ -",
			"12": "<sup>12</sup> ನಾನು ಯೆಹೋವನಿಗೆ ಶಾಂತ್ಯರ್ಪಣೆಯನ್ನು ಸಲ್ಲಿಸುವದಕ್ಕಿಂತ ಮೊದಲೇ ಫಿಲಿಷ್ಟಿಯರು ಗಿಲ್ಗಾಲಿಗೆ ಬಂದು ನನ್ನ ಮೇಲೆ ಬಿದ್ದಾರೆಂದು ಭಯಪಟ್ಟು ಸರ್ವಾಂಗಹೋಮವನ್ನು ಸಮರ್ಪಿಸುವದಕ್ಕೆ ಮುಂಗೊಂಡೆನು ಅಂದನು.",
			"13": "<sup>13</sup> ಆಗ ಸಮುವೇಲನು - ನೀನು ಬುದ್ಧಿಹೀನಕಾರ್ಯವನ್ನು ಮಾಡಿದೆ; ನೀನು ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನ ಆಜ್ಞೆಯನ್ನು ಕೈಕೊಳ್ಳಲಿಲ್ಲ. ಕೈಕೊಂಡಿದ್ದರೆ ಆತನು ನಿನ್ನ ರಾಜ್ಯವನ್ನು ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಶಾಶ್ವತವಾಗಿ ಸ್ಥಿರಪಡಿಸುತ್ತಿದ್ದನು.",
			"14": "<sup>14</sup> ಈಗಲಾದರೋ ನಿನ್ನ ಅರಸುತನವು ನಿಲ್ಲುವದಿಲ್ಲ; ನೀನು ಯೆಹೋವನ ಆಜ್ಞೆಯನ್ನು ಕೈಕೊಳ್ಳದೆಹೋದದರಿಂದ ಆತನು ತನಗೆ ಒಪ್ಪುವ ಬೇರೊಬ್ಬ ಪುರುಷನನ್ನು ತನ್ನ ಪ್ರಜೆಯ ಮೇಲೆ ಪ್ರಭುವಾಗಿ ನೇಮಿಸಿದ್ದಾನೆ ಎಂದು ಹೇಳಿದನು.",
			"15": "<sup>15</sup> ಅನಂತರ ಸಮುವೇಲನು ಗಿಲ್ಗಾಲನ್ನು ಬಿಟ್ಟು ಬೆನ್ಯಾಮೀನ್ಯರ ಗಿಬೆಯಕ್ಕೆ ಹೋದನು. ಸೌಲನು ತನ್ನ ಜೊತೆಯಲ್ಲಿದ್ದ ಸೈನಿಕರನ್ನು ಲೆಕ್ಕಿಸಿದಾಗ ಆರುನೂರು ಮಂದಿಯಿದ್ದರು.",
			"16": "<sup>16</sup> ಅವರೊಡನೆ ಅವನೂ ಅವನ ಮಗನಾದ ಯೋನಾತಾನನೂ ಬೆನ್ಯಾಮೀನ್ಯರ ಗೆಬದಲ್ಲಿ ಇಳುಕೊಂಡರು. ಫಿಲಿಷ್ಟಿಯರು ಮಿಕ್ಮಾಷಿನಲ್ಲಿ ಪಾಳೆಯಮಾಡಿಕೊಂಡಿದ್ದರು.",
			"17": "<sup>17</sup> ಸುಲಿಗೆ ಮಾಡುವದಕ್ಕೋಸ್ಕರ ಫಿಲಿಷ್ಟಿಯರ ಪಾಳೆಯದಿಂದ ಮೂರು ಗುಂಪುಗಳು ಹೊರಟು ಒಂದು ಗುಂಪು ಒಫ್ರದ ಮಾರ್ಗವಾಗಿ ಶುವಲ್‍ದೇಶಕ್ಕೂ",
			"18": "<sup>18</sup> ಇನ್ನೊಂದು ಬೇತ್‍ಹೋರೋನಿನ ಕಡೆಗೂ ಮತ್ತೊಂದು ಜೆಬೋಯೀಮ್ ಕಣಿವೆಯ ಕೆಳಗಿರುವ ಅರಣ್ಯವನ್ನು ನೋಡಬಹುದಾದ ಸ್ಥಳಕ್ಕೂ ಹೋದವು.",
			"19": "<sup>19</sup> ಇಬ್ರಿಯರು ಈಟಿಕತ್ತಿಗಳನ್ನು ಮಾಡಿಕೊಳ್ಳಕೂಡದೆಂದು ಫಿಲಿಷ್ಟಿಯರ ಆಜ್ಞೆಯಿದ್ದದರಿಂದ ಇಸ್ರಾಯೇಲ್ ದೇಶದಲ್ಲಿ ಅವುಗಳನ್ನು ಮಾಡುವ ಕಮ್ಮಾರರೇ ಇರಲಿಲ್ಲ.",
			"20": "<sup>20</sup> (ಅವರು ತಮ್ಮ ನೇಗಲಿನ ಗುಳ, ಸಲಿಕೆ, ಕೊಡಲಿ, ಗುದ್ದಲಿ ಇವುಗಳನ್ನು ಹಣಿಸುವದಕ್ಕೋಸ್ಕರ ಫಿಲಿಷ್ಟಿಯರ ಹತ್ತಿರವೇ ಹೋಗಬೇಕಿತ್ತು.",
			"21": "<sup>21</sup> ಗುದ್ದಲಿ, ಸಲಿಕೆ, ತ್ರಿಶೂಲ, ಕೊಡಲಿ, ಮುಳ್ಳುಗೋಲು ಇವುಗಳನ್ನು ಹದಮಾಡುವದಕ್ಕೋಸ್ಕರ ಅವರಲ್ಲಿ ಅರ ಮಾತ್ರ ಇತ್ತು.)",
			"22": "<sup>22</sup> ಹೀಗಿರುವದರಿಂದ ಯುದ್ಧಪ್ರಾರಂಭಿಸಿದಾಗ ಸೌಲ ಯೋನಾತಾನರ ಹೊರತು ಅವರ ಜೊತೆಯಲ್ಲಿದ್ದ ಜನರಲ್ಲಿ ಒಬ್ಬನ ಕೈಯಲ್ಲಾದರೂ ಕತ್ತಿಯಾಗಲಿ ಬರ್ಜಿಯಾಗಲಿ ಇರಲಿಲ್ಲ.",
			"23": "<sup>23</sup> ಫಿಲಿಷ್ಟಿಯರ ಕಾವಲುದಂಡು ಮಿಕ್ಮಾಷಿನ ಕಣಿವೆಯ ಕಡೆಗೆ ಹೊರಟಿತು."
		},
		"14": {
			"1": "<sup>1</sup> ಒಂದು ದಿವಸ ಸೌಲನ ಮಗನಾದ ಯೋನಾತಾನನು ತನ್ನ ಆಯುಧಗಳನ್ನು ಹೊರುವ ಸೇವಕನಿಗೆ - ಆಚೆ ಇರುವ ಫಿಲಿಷ್ಟಿಯರ ಕಾವಲುದಂಡಿಗೆ ವಿರೋಧವಾಗಿ ಹೋಗೋಣ ಬಾ ಎಂದು ಹೇಳಿದನು. ಆದರೆ ಈ ಸಂಗತಿಯನ್ನು ತನ್ನ ತಂದೆಗೆ ತಿಳಿಸಲಿಲ್ಲ.",
			"2": "<sup>2</sup> ಸೌಲನಾದರೋ ಗಿಬೆಯ ಪ್ರಾಂತದ ಅಂತ್ಯಭಾಗವಾಗಿರುವ ಮಿಗ್ರೋನಿನಲ್ಲಿನ ಒಂದು ದಾಳಿಂಬ ವೃಕ್ಷದ ಅಡಿಯಲ್ಲಿ ಇಳುಕೊಂಡಿದ್ದನು. ಅವನ ಜೊತೆಯಲ್ಲಿ ಸುಮಾರು ಆರುನೂರು ಮಂದಿ ಸೈನಿಕರೂ",
			"3": "<sup>3</sup> ಏಫೋದನ್ನು ಧರಿಸಿಕೊಂಡಿದ್ದ ಅಹೀಯನೂ ಇದ್ದರು. ಈ ಅಹೀಯನು ಶೀಲೋವಿನಲ್ಲಿ ಯೆಹೋವನ ಯಾಜಕನಾಗಿದ್ದ ಏಲಿಯ ಮರಿ ಮಗನೂ ಫೀನೆಹಾಸನ ಮೊಮ್ಮಗನೂ ಈಕಾಬೋದನ ಅಣ್ಣನಾದ ಅಹೀಟೂಬನ ಮಗನೂ ಆಗಿದ್ದನು. ಯೋನಾತಾನನು ಹೋದದ್ದು ಜನರಿಗೂ ಗೊತ್ತಿರಲಿಲ್ಲ.",
			"4": "<sup>4</sup> ಅವನು ಫಿಲಿಷ್ಟಿಯರ ಕಾವಲು ದಂಡಿಗೆ ವಿರೋಧವಾಗಿ ಹೊರಟ ಕಣಿವೆಯ ಮಾರ್ಗದ ಎರಡು ಕಡೆಗಳಲ್ಲಿ ಬೋಚೇಚ್, ಸೆನೆ ಎಂಬ ಎರಡು ಕೋಡುಗಲ್ಲುಗಳಿದ್ದವು.",
			"5": "<sup>5</sup> ಉತ್ತರದಿಕ್ಕಿನದು ಎತ್ತರವಾಗಿ ಮಿಕ್ಮಾಷಿಗೂ ದಕ್ಷಿಣದಿಕ್ಕಿನದು ಗೆಬಕ್ಕೂ ಎದುರಾಗಿದ್ದವು.",
			"6": "<sup>6</sup> ಯೋನಾತಾನನು ತನ್ನ ಆಯುಧಗಳನ್ನು ಹೊರುವವನಿಗೆ - ಸುನ್ನತಿಯಿಲ್ಲದ ಈ ಕಾವಲುದಂಡಿನವರಿಗೆ ವಿರೋಧವಾಗಿ ಹೋಗೋಣ ಬಾ; ಒಂದು ವೇಳೆ ಯೆಹೋವನು ತಾನೇ ನಮಗೋಸ್ಕರ ಕಾರ್ಯನಡಿಸುವನು. ಬಹುಜನರಿಂದಾದರೂ ಸ್ವಲ್ಪಜನರಿಂದಾದರೂ ರಕ್ಷಿಸುವದು ಯೆಹೋವನಿಗೆ ಅಸಾಧ್ಯವಲ್ಲ ಎಂದು ಹೇಳಲು",
			"7": "<sup>7</sup> ಆಯುಧಗಳನ್ನು ಹೊರುವವನು - ನಿನ್ನ ಮನಸ್ಸಿನಂತೆ ಮಾಡು; ನಿನ್ನ ಕೋರಿಕೆಯ ಹಾಗೆ ನಾನೂ ನಿನ್ನನ್ನು ಹಿಂಬಾಲಿಸುತ್ತೇನೆ ಅಂದನು.",
			"8": "<sup>8</sup> ಆಗ ಯೋನಾತಾನನು ಅವನಿಗೆ - ಅವರು ನಮ್ಮನ್ನು ಕಾಣುವಂತೆ ಸಮೀಪಕ್ಕೆ ಹೋಗೋಣ.",
			"9": "<sup>9</sup> ನಾವು ನಿಮ್ಮ ಬಳಿಗೆ ಬರುವ ತನಕ ಅಲ್ಲೇ ನಿಲ್ಲಿರಿ ಎಂದು ಅವರು ನಮಗೆ ಹೇಳಿದರೆ ಮೇಲೆ ಹತ್ತದೆ ಇದ್ದ ಸ್ಥಳದಲ್ಲೇ ಇರೋಣ.",
			"10": "<sup>10</sup> ಅದರೆ ಅವರು - ಮೇಲೆ ಹತ್ತಿ ನಮ್ಮ ಬಳಿಗೆ ಬನ್ನಿರಿ ಎಂದು ನಮ್ಮನ್ನು ಕರೆದರೆ ಯೆಹೋವನು ಅವರನ್ನು ನಮ್ಮ ಕೈಗೆ ಒಪ್ಪಿಸಿದ್ದಾನೆಂಬದಕ್ಕೆ ಇದೇ ಗುರುತೆಂದು ತಿಳಿದು ಮೇಲೆ ಹೋಗೋಣ ಎಂದನು.",
			"11": "<sup>11</sup> ಇವರಿಬ್ಬರೂ ಫಿಲಿಷ್ಟಿಯರ ಕಾವಲುದಂಡಿನವರಿಗೆ ಕಾಣಿಸಿಕೊಂಡಾಗ ಅವರು - ಇಗೋ, ಇಬ್ರಿಯರು ತಾವು ಅಡಗಿಕೊಂಡಿದ್ದ ಗುಹೆಗಳಿಂದ ಹೊರಗೆ ಬರುತ್ತಲಿದ್ದಾರೆ ಎಂದು ತಮ್ಮೊಳಗೆ ಮಾತಾಡಿಕೊಂಡು",
			"12": "<sup>12</sup> ಯೋನಾತಾನನಿಗೂ ಅವನ ಆಯುಧಗಳನ್ನು ಹೊರುವವನಿಗೂ - ಹತ್ತಿ ನಮ್ಮ ಬಳಿಗೆ ಬನ್ನಿರಿ; ನಿಮಗೆ ಕಾರ್ಯತೋರಿಸಿಕೊಡುತ್ತೇವೆ ಅನ್ನಲು ಯೋನಾತಾನನು ತನ್ನ ಆಯುಧಗಳನ್ನು ಹೊರುವ ಸೇವಕನಿಗೆ - ನೀನೂ ನನ್ನ ಸಂಗಡ ಹತ್ತಿ ಬಾ; ಯೆಹೋವನು ಅವರನ್ನು ಇಸ್ರಾಯೇಲ್ಯರ ಕೈಗೆ ಒಪ್ಪಿಸಿದ್ದಾನೆ ಎಂದು ಹೇಳಿದನು.",
			"13": "<sup>13</sup> ಯೋನಾತಾನನೂ ಅವನ ಆಯುಧಗಳನ್ನು ಹೊರುವ ಸೇವಕನೂ ತಮ್ಮ ಕೈ ಕಾಲುಗಳಿಂದ ಗಟ್ಟಾ ಹತ್ತಿದರು; ಯೋನಾತಾನನು ಫಿಲಿಷ್ಟಿಯರನ್ನು ನೆಲಕ್ಕುರುಳಿಸುತ್ತಾ ಹೋದನು; ಅವನ ಸೇವಕನು ಹಿಂದಿನಿಂದ ಅವರನ್ನು ಕೊಲ್ಲುತ್ತಾ ನಡೆದನು.",
			"14": "<sup>14</sup> ಅವರು ಒಂದು ಜೋಡಿ ಎತ್ತು ಒಪ್ಪೊತ್ತಿನಲ್ಲಿ ಉಳುವಷ್ಟು ಭೂಮಿಯಲ್ಲೆ ಸುಮಾರು ಇಪ್ಪತ್ತು ಮಂದಿಯನ್ನು ವಧಿಸಿದರು.",
			"15": "<sup>15</sup> ಇದರಿಂದ ಪಾಳೆಯದಲ್ಲಿದ್ದವರೂ ಕಾವಲುಗಾರರಾಗಿ ಠಾಣದಲ್ಲಿದ್ದವರೂ ಸುಲಿಗೆಗೋಸ್ಕರ ಹೊರಗೆ ಹೋಗಿದ್ದವರೂ ಉಳಿದ ಎಲ್ಲಾ ಜನರೂ ಭಯದಿಂದ ನಡುಗಹತ್ತಿದರು. ಇದಲ್ಲದೆ ದೇವರು ಭೂಕಂಪವನ್ನುಂಟುಮಾಡಿದ್ದರಿಂದ ಜನರಲ್ಲಿ ಮಹಾಭೀತಿಯುಂಟಾಯಿತು.",
			"16": "<sup>16</sup> ಬೆನ್ಯಾಮೀನ್ಯರ ಗಿಬೆಯದಲ್ಲಿ ಕಾವಲಿದ್ದ ಸೌಲನ ಸೈನಿಕರು ಫಿಲಿಷ್ಟಿಯರ ದಂಡು ಕಳವಳಗೊಂಡು ಚದರಿ ಹೋಗುತ್ತಿರುವದನ್ನು ಕಂಡರು.",
			"17": "<sup>17</sup> ಸೌಲನು ತನ್ನ ಜೊತೆಯಲ್ಲಿದ್ದವರಿಗೆ - ನಮ್ಮನ್ನು ಬಿಟ್ಟುಹೋದವರು ಯಾರೆಂಬದನ್ನು ಲೆಕ್ಕಮಾಡಿ ನೋಡಿರಿ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು. ಅವರು ಲೆಕ್ಕಮಾಡಿ ನೋಡುವಲ್ಲಿ ಯೋನಾತಾನನೂ ಅವನ ಆಯುಧಗಳನ್ನು ಹೊರುವ ಸೇವಕನೂ ಇರುವದಿಲ್ಲವೆಂದು ತಿಳಿದುಬಂದಿತು.",
			"18": "<sup>18</sup> ಅನಂತರ ಸೌಲನು ಅಹೀಯನಿಗೆ - ಏಫೋದನ್ನು ತೆಗೆದುಕೊಂಡು ಬಾ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು. ಆ ಕಾಲದಲ್ಲಿ ಇವನು ಇಸ್ರಾಯೇಲ್ಯರೊಳಗೆ ಏಫೋದನ್ನು ಧರಿಸುತ್ತಿದ್ದನಷ್ಟೆ.",
			"19": "<sup>19</sup> ಸೌಲನು ಯಾಜಕನೊಡನೆ ಮಾತಾಡುತ್ತಿರುವಷ್ಟರಲ್ಲಿ ಫಿಲಿಷ್ಟಿಯರ ಸೈನ್ಯದ ಗದ್ದಲವು ಬಲು ಹೆಚ್ಚಾಯಿತು. ಆದದರಿಂದ ಅವನು ಕೈ ನಿಲ್ಲಿಸೆಂದು ಯಾಜಕನಿಗೆ ಹೇಳಿ",
			"20": "<sup>20</sup> ತನ್ನ ಜೊತೆಯಲ್ಲಿದ್ದವರನ್ನು ಕೂಡಿಕೊಂಡು ಯುದ್ಧಕ್ಕೆ ಹೋದನು; ಅಷ್ಟರಲ್ಲಿ ಫಿಲಿಷ್ಟಿಯರ ಸೈನ್ಯದಲ್ಲಿ ಗಲಿಬಿಲಿಯುಂಟಾಗಿ ಒಬ್ಬನನ್ನೊಬ್ಬನು ಕೊಂದುಹಾಕುತ್ತಿದ್ದನು.",
			"21": "<sup>21</sup> ಇದಲ್ಲದೆ ಮುಂಚಿನಿಂದಲೇ ಫಿಲಿಷ್ಟಿಯರನ್ನು ಸೇರಿಕೊಂಡು ಅವರೊಡನೆ ಪಾಳೆಯದಲ್ಲಿದ್ದ ಇಬ್ರಿಯರು ಸೌಲ ಯೋನಾತಾನರ ಜೊತೆಯಲ್ಲಿ ಬಂದ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಕೂಡಿಕೊಂಡರು.",
			"22": "<sup>22</sup> ಎಫ್ರಾಯೀಮ್ ಪರ್ವತಪ್ರಾಂತದಲ್ಲಿ ಅಡಗಿಕೊಂಡಿದ್ದ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಫಿಲಿಷ್ಟಿಯರು ಓಡಿಹೋದರೆಂಬ ವರ್ತಮಾನವು ಮುಟ್ಟಿದಾಗ ಅವರೂ ಯುದ್ಧಕ್ಕೆ ಬಂದು ಅವರನ್ನು ಬೆನ್ನಟ್ಟಿದರು.",
			"23": "<sup>23</sup> ಹೀಗೆ ಯೆಹೋವನು ಆ ದಿನದಲ್ಲಿ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಜಯವನ್ನುಂಟುಮಾಡಿದನು. ಯುದ್ಧವು ಬೇತಾವೆನಿನ ಆಚೆಯ ತನಕ ನಡೆಯಿತು.",
			"24": "<sup>24</sup> ಆ ದಿವಸ ಸೌಲನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಕುರಿತು - ಶತ್ರುಗಳಿಗೆ ಮುಯ್ಯಿ ತೀರಿಸುವದಕ್ಕೋಸ್ಕರ ನಿಮ್ಮಲ್ಲಿ ಒಬ್ಬನಾದರೂ ಸಾಯಂಕಾಲದವರೆಗೆ ಊಟಮಾಡಬಾರದು; ಮಾಡಿದವನು ಶಾಪಗ್ರಸ್ತನಾಗಲಿ ಎಂದು ಆಣೆಯಿಟ್ಟಿದ್ದದರಿಂದ ಅಂದು ಅವರೆಲ್ಲರೂ ಬಹುವಾಗಿ ಬಳಲಿಹೋಗಿದ್ದರು. ಒಬ್ಬನಾದರೂ ಆಹಾರ ಪದಾರ್ಥವನ್ನು ರುಚಿನೋಡಲಿಲ್ಲ.",
			"25": "<sup>25</sup> ಜನರೆಲ್ಲರೂ ನೆಲಜೇನು ಇರುವ ಕಾಡಿಗೆ ಬಂದರು.",
			"26": "<sup>26</sup> ಅವರು ಅಲ್ಲಿ ಜೇನುತುಪ್ಪ ಹರಿಯುವದನ್ನು ಕಂಡರೂ ಶಾಪಕ್ಕೆ ಭಯಪಟ್ಟದರಿಂದ ಯಾವನೂ ಅದನ್ನು ಬಾಯೊಳಗೆ ಹಾಕಲಿಲ್ಲ.",
			"27": "<sup>27</sup> ಯೋನಾತಾನನಿಗೆ ತನ್ನ ತಂದೆಯು ಜನರಿಗೆ ಆಣೆಯಿಟ್ಟು ಹೇಳಿದ್ದು ಗೊತ್ತಿರಲಿಲ್ಲವಾದದರಿಂದ ಅವನು ತನ್ನ ಕೋಲನ್ನು ಜೇನು ತೊಟ್ಟಿಯಲ್ಲಿ ಚುಚ್ಚಿ ಕೈಯಿಂದ ತೆಗೆದು ತಿನ್ನಲು ಅವನ ಕಣ್ಣುಗಳು ಕಳೆಗೊಂಡವು.",
			"28": "<sup>28</sup> ಕೂಡಲೆ ಒಬ್ಬನು ಅವನಿಗೆ - ಈ ಹೊತ್ತು ಊಟಮಾಡುವಂಥವರು ಶಾಪಗ್ರಸ್ತರಾಗುವರೆಂದು ನಿನ್ನ ತಂದೆಯು ಆಣೆಯಿಟ್ಟು ಹೇಳಿದ್ದಾನೆ ಎಂದು ತಿಳಿಸಿದನು.",
			"29": "<sup>29</sup> ಜನರು ಬಹಳವಾಗಿ ಬಳಲಿಹೋದದನ್ನು ಕಂಡು ಯೋನಾತಾನನು ಆ ಮನುಷ್ಯನಿಗೆ - ನನ್ನ ತಂದೆಯು ದೇಶಕ್ಕೆ ನಷ್ಟವನ್ನುಂಟುಮಾಡಿದ್ದಾನೆ; ನಾನು ಸ್ವಲ್ಪ ಜೇನುತುಪ್ಪವನ್ನು ತಿಂದದರಿಂದ ನನ್ನ ಕಣ್ಣುಗಳು ಹೇಗೆ ಕಳೆಗೊಂಡಿರುತ್ತವೆ ನೋಡು.",
			"30": "<sup>30</sup> ಜನರು ತಾವು ಇಂದು ಶತ್ರುಗಳಿಂದ ಸುಲುಕೊಂಡ ಆಹಾರವನ್ನು ಯಥೇಚ್ಫವಾಗಿ ಊಟಮಾಡಿದ್ದರೆ ಅವರು ಇನ್ನಷ್ಟು ಬಲಗೊಳ್ಳುತ್ತಿದ್ದರು. ಫಿಲಿಷ್ಟಿಯರಲ್ಲಿ ಹತವಾಗದವರು ಇನ್ನೂ ಹೆಚ್ಚು ಮಂದಿ ಇದ್ದಾರೆ ಅಂದನು.",
			"31": "<sup>31</sup> ಆ ದಿವಸ ಜನರು ಮಿಕ್ಮಾಷಿನಿಂದ ಅಯ್ಯಾಲೋನಿನವರೆಗೆ ಫಿಲಿಷ್ಟಿಯರನ್ನು ಹೊಡೆದು ಬಹಳವಾಗಿ ದಣಿದಿದ್ದದರಿಂದ",
			"32": "<sup>32</sup> ಅವರು ತಾವು ಕೊಳ್ಳೆಮಾಡಿದ ಎತ್ತು, ಕುರಿ, ಕರು ಇವುಗಳನ್ನು ಹಿಡಿದು ನೆಲದ ಮೇಲೆಯೇ ಕೊಂದು ಮಾಂಸದೊಡನೆ ರಕ್ತವನ್ನೂ ತಿಂದರು.",
			"33": "<sup>33</sup> ಆಗ ಕೆಲವರು ಸೌಲನಿಗೆ - ನೋಡು, ಜನರು ರಕ್ತವನ್ನು ಭುಜಿಸಿ ಯೆಹೋವನಿಗೆ ವಿರುದ್ಧವಾಗಿ ಪಾಪಮಾಡುತ್ತಾರೆ ಎಂದು ತಿಳಿಸಲು ಅವನು ಅವರಿಗೆ - ಇದು ಮಹಾಪಾತಕವೇ ಸರಿ; ನೀವು ಬೇಗನೆ ನನ್ನ ಬಳಿಗೆ ದೊಡ್ಡ ಕಲ್ಲನ್ನು ಹೊರಳಿಸಿ ತಂದಿಟ್ಟು",
			"34": "<sup>34</sup> ಜನರ ಬಳಿಗೆ ಹೋಗಿ ಅವರಿಗೆ - ರಕ್ತವನ್ನು ಭುಜಿಸಿ ಯೆಹೋವನಿಗೆ ವಿರುದ್ಧವಾಗಿ ಪಾಪಮಾಡಬೇಡಿರಿ; ನಿಮ್ಮ ನಿಮ್ಮ ಎತ್ತು ಕುರಿಗಳನ್ನು ಇಲ್ಲಿ ತಂದು ಕೊಂದು ತಿನ್ನಬೇಕೆಂದು ಹೇಳಿರಿ ಅಂದನು. ಹಾಗೆಯೇ ಜನರೆಲ್ಲರೂ ತಮ್ಮ ತಮ್ಮ ಎತ್ತು ಕುರಿಗಳನ್ನು ಅದೇ ರಾತ್ರಿ ಅಲ್ಲಿಗೆ ತಂದು ಕೊಂದು ತಿಂದರು.",
			"35": "<sup>35</sup> ಸೌಲನು ಯೆಹೋವನಿಗೋಸ್ಕರ ಒಂದು ಯಜ್ಞವೇದಿಯನ್ನು ಕಟ್ಟಿಸಿದನು; ಅವನು ಕಟ್ಟಿಸಿದ ಯಜ್ಞವೇದಿಗಳಲ್ಲಿ ಇದೇ ಮೊದಲನೆಯದು.",
			"36": "<sup>36</sup> ಸೌಲನು ತನ್ನ ಸೈನಿಕರನ್ನು ಬನ್ನಿರಿ; ನಾವು ಈ ರಾತ್ರಿಯಲ್ಲೇ ಫಿಲಿಷ್ಟಿಯರನ್ನು ಬೆನ್ನಟ್ಟಿ ಬೆಳಗಾಗುವವರೆಗೆ ಅವರನ್ನು ಸುಲುಕೊಂಡು ಒಬ್ಬನನ್ನೂ ಉಳಿಸದೆ ಕೊಂದುಹಾಕೋಣ ಎಂದು ಕರೆಯಲು ಅವರು - ನಿನ್ನ ಇಷ್ಟದಂತೆ ಆಗಲಿ ಎಂದು ಹೇಳಿದರು. ಆದರೆ ಯಾಜಕನು - ಇತ್ತ ಬನ್ನಿರಿ; ನಾವು ದೇವರನ್ನು ವಿಚಾರಿಸೋಣ ಅನ್ನಲು ಸೌಲನು -",
			"37": "<sup>37</sup> ದೇವಾ, ನಾವು ಫಿಲಿಷ್ಟಿಯರನ್ನು ಬೆನ್ನಟ್ಟಬಹುದೋ? ನೀನು ಅವರನ್ನು ಇಸ್ರಾಯೇಲ್ಯರ ಕೈಗೆ ಒಪ್ಪಿಸಿಕೊಡುವಿಯೋ ಎಂದು ಕೇಳಿಕೊಂಡರೂ ಆತನು ಅವನಿಗೆ ಆ ದಿವಸ ಉತ್ತರ ಕೊಡಲೇ ಇಲ್ಲ.",
			"38": "<sup>38</sup> ಆಗ ಸೌಲನು ಪ್ರಜೆಯ ಮುಖಂಡರಿಗೆ - ನೀವೆಲ್ಲರೂ ಇಲ್ಲಿ ಬಂದು ಈ ಹೊತ್ತು ಪಾಪ ಯಾವದರಿಂದುಂಟಾಯಿತೋ ವಿಚಾರಿಸಿ ಗೊತ್ತುಮಾಡಿರಿ.",
			"39": "<sup>39</sup> ಇಸ್ರಾಯೇಲ್ಯರ ರಕ್ಷಕನಾದ ಯೆಹೋವನ ಆಣೆ, ಪಾಪಮಾಡಿದವನು ನನ್ನ ಮಗನಾದ ಯೋನಾತಾನನಾಗಿದ್ದರೂ ಸರಿಯೇ ಅವನು ಸಾಯಲೇಬೇಕು ಎಂದು ಹೇಳಿದನು; ಜನರು ಏನೂ ಮಾತಾಡದೆ ಮೌನವಾಗಿದ್ದರು.",
			"40": "<sup>40</sup> ಆಗ ಸೌಲನು ಅವರಿಗೆ - ನೀವೆಲ್ಲರೂ ಒಂದು ಕಡೆಯಲ್ಲಿರಿ; ನಾನೂ ನನ್ನ ಮಗನಾದ ಯೋನಾತಾನನೂ ಮತ್ತೊಂದು ಕಡೆಯಲ್ಲಿರುವೆವು ಅನ್ನಲು ಅವರು - ನಿನ್ನ ಇಷ್ಟದಂತೆ ಆಗಲಿ ಅಂದರು.",
			"41": "<sup>41</sup> ಅನಂತರ ಸೌಲನು - ಇಸ್ರಾಯೇಲ್ ದೇವರೇ ಸತ್ಯವನ್ನು ತಿಳಿಸು ಎಂದು ಯೆಹೋವನನ್ನು ಪ್ರಾರ್ಥಿಸಿ ಚೀಟು ಹಾಕಿದಲ್ಲಿ ಅದು ಅವನಿಗೂ ಯೋನಾತಾನನಿಗೂ ಬಂದಿತು; ಜನರು ಪಾರಾದರು.",
			"42": "<sup>42</sup> ಸೌಲನು ತಿರಿಗಿ - ನಮ್ಮಿಬ್ಬರೊಳಗೆ ಚೀಟುಹಾಕಿರಿ ಎಂದು ಹೇಳಿದನು. ಹಾಗೆಯೇ ಮಾಡಿದಲ್ಲಿ ಚೀಟು ಯೋನಾತಾನನಿಗೆ ಬಿದ್ದಿತು.",
			"43": "<sup>43</sup> ಆಗ ಸೌಲನು ಯೋನಾತಾನನನ್ನು - ನೀನು ಏನು ಮಾಡಿದ್ದೀ, ಹೇಳು ಎಂದು ಕೇಳಲು ಅವನು - ನಾನು ಕೋಲಿನಿಂದ ಸ್ವಲ್ಪ ಜೇನುತುಪ್ಪವನ್ನು ತೆಗೆದುಕೊಂಡು ತಿಂದೆನು; ಸಾಯುವದಕ್ಕೆ ಸಿದ್ಧನಾಗಿದ್ದೇನೆ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"44": "<sup>44</sup> ಅದಕ್ಕೆ ಸೌಲನು - ಯೋನಾತಾನನೇ, ನಾನು ನಿನ್ನನ್ನು ಕೊಲ್ಲದೆ ಬಿಟ್ಟರೆ ದೇವರು ನನಗೆ ಬೇಕಾದದ್ದನ್ನು ಮಾಡಲಿ ಅನ್ನಲು ಜನರು ಅವನಿಗೆ -",
			"45": "<sup>45</sup> ದೇವರ ಸಹಾಯದಿಂದ ಈ ಹೊತ್ತು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಮಹಾಜಯವನ್ನುಂಟುಮಾಡಿದ ಯೋನಾತಾನನು ಸಾಯಬೇಕೋ? ಕೂಡದು; ಯೆಹೋವನಾಣೆ, ಅವನ ತಲೆಗೂದಲುಗಳಲ್ಲಿ ಒಂದನ್ನೂ ನೆಲಕ್ಕೆ ಬೀಳಿಸಬಾರದು ಎಂದು ಹೇಳಿ ಪ್ರಾಯಶ್ಚಿತ್ತವನ್ನು ಕೊಟ್ಟು ಅವನನ್ನು ಬಿಡಿಸಿದರು.",
			"46": "<sup>46</sup> ಸೌಲನು ಆಮೇಲೆ ಫಿಲಿಷ್ಟಿಯರನ್ನು ಹಿಂದಟ್ಟದೆ ತನ್ನ ಮನೆಗೆ ಹೋದನು. ಫಿಲಿಷ್ಟಿಯರೂ ತಮ್ಮ ಪ್ರಾಂತಕ್ಕೆ ಹೋದರು.",
			"47": "<sup>47</sup> ಸೌಲನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾದ ಮೇಲೆ ಅವನು ಸುತ್ತಣ ವೈರಿಗಳಾದ ಮೋವಾಬ್ಯರು, ಅಮ್ಮೋನಿಯರು, ಎದೋಮ್ಯರು, ಚೋಬದ ಅರಸರು, ಫಿಲಿಷ್ಟಿಯರು ಇವರೊಡನೆ ಯುದ್ಧಮಾಡಿದನು. ಹೋದಕಡೆಯಲ್ಲೆಲ್ಲಾ ಜಯವನ್ನೇ ಹೊಂದಿದನು.",
			"48": "<sup>48</sup> ಇದಲ್ಲದೆ ಅವನು ಸುಲಿಗೆಮಾಡುವವರಾದ ಅಮಾಲೇಕ್ಯರಿಗೆ ವಿರೋಧವಾಗಿ ದಂಡೆತ್ತಿ ಹೋಗಿ ಅವರನ್ನು ಮುರಿಬಡಿದು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಅವರ ಕೈಗೆ ಸಿಕ್ಕದಂತೆ ತಪ್ಪಿಸಿದನು.",
			"49": "<sup>49</sup> ಸೌಲನ ಗಂಡು ಮಕ್ಕಳು - ಯೋನಾತಾನ್, ಇಷ್ವಿ, ಮಲ್ಕೀಷೂವ. ಅವನ ಹೆಣ್ಣು ಮಕ್ಕಳು - ಮೇರಬ್, ಮೀಕಲ್.",
			"50": "<sup>50</sup> ಅಹೀಮಾಚನ ಮಗಳಾದ ಅಹೀನೋವಮಳು ಅವನ ಹೆಂಡತಿ; ನೇರನ ಮಗನಾದ ಅಬ್ನೇರನು ಅವನ ಸೇನಾಪತಿಯು; ನೇರನು ಸೌಲನ ಚಿಕ್ಕಪ್ಪನು.",
			"51": "<sup>51</sup> ಸೌಲನ ತಂದೆಯಾದ ಕೀಷನೂ ಅಬ್ನೇರನ ತಂದೆಯಾದ ನೇರನೂ ಅಬೀಯೇಲನ ಮಕ್ಕಳು.",
			"52": "<sup>52</sup> ಸೌಲನ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ಫಿಲಿಷ್ಟಿಯರಿಗೂ ಇಸ್ರಾಯೇಲ್ಯರಿಗೂ ಘೋರ ಯುದ್ಧವಿದ್ದದರಿಂದ ಅವನು ಎಲ್ಲಿಯಾದರೂ ಒಬ್ಬ ಬಲಿಷ್ಠನೂ ಪರಾಕ್ರಮಶಾಲಿಯೂ ಆದ ಮನುಷ್ಯನನ್ನು ಕಂಡರೆ ಕೂಡಲೆ ಅವನನ್ನು ತನ್ನ ಹತ್ತಿರ ಇಟ್ಟುಕೊಳ್ಳುತ್ತಿದ್ದನು."
		},
		"15": {
			"1": "<sup>1</sup> ಸಮುವೇಲನು ಸೌಲನಿಗೆ - ಯೆಹೋವನು ನಿನ್ನನ್ನು ತನ್ನ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಾಯೇಲ್ಯರ ಮೇಲೆ ಅರಸನನ್ನಾಗಿ ಅಭಿಷೇಕಿಸುವದಕ್ಕೋಸ್ಕರ ನನ್ನನ್ನೇ ಕಳುಹಿಸಿದನಲ್ಲಾ; ಆತನು ಈಗ ಹೇಳುವದನ್ನು ಕೇಳು.",
			"2": "<sup>2</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ನಿನಗೆ - ಇಸ್ರಾಯೇಲ್ಯರು ಐಗುಪ್ತದಿಂದ ಬರುತ್ತಿರುವಾಗ ದಾರಿಯಲ್ಲಿ ಅಮಾಲೇಕ್ಯರು ಅವರಿಗೆ ವಿರೋಧವಾಗಿ ನಿಂತು ತೊಂದರೆಪಡಿಸಿದದರಿಂದ ನಾನು ಅವರಿಗೆ ಮುಯ್ಯಿತೀರಿಸುವೆನು.",
			"3": "<sup>3</sup> ಈಗ ನೀನು ಹೋಗಿ ಅವರನ್ನು ಸೋಲಿಸಿ ಅವರಿಗಿರುವದನ್ನೆಲ್ಲಾ ಸಂಪೂರ್ಣವಾಗಿ ಹಾಳುಮಾಡು. ಗಂಡಸರನ್ನೂ ಹೆಂಗಸರನ್ನೂ ಮಕ್ಕಳನ್ನೂ ಶಿಶುಗಳನ್ನೂ ಎತ್ತು, ಕುರಿ, ಕತ್ತೆ, ಒಂಟೆ, ಇವುಗಳನ್ನೂ ಕನಿಕರವಿಲ್ಲದೆ ಕೊಂದುಹಾಕು ಎಂದು ಹೇಳುತ್ತಾನೆ ಅಂದನು.",
			"4": "<sup>4</sup> ಸೌಲನು ತೆಲಾಯೀಮಿನಲ್ಲಿ ಸೈನ್ಯವನ್ನು ಕೂಡಿಸಿ ಲೆಕ್ಕಿಸಲು ಎರಡು ಲಕ್ಷ ಮಂದಿ ಕಾಲಾಳುಗಳು ಇದ್ದರು; ಯೆಹೂದ್ಯರು ಹತ್ತು ಸಾವಿರ ಮಂದಿ ಇದ್ದರು.",
			"5": "<sup>5</sup> ಅವನು ಇವರೊಡನೆ ಅಮಾಲೇಕ್ಯರ ರಾಜಧಾನಿಯ ಬಳಿಗೆ ಹೋಗಿ ಅಲ್ಲಿನ ಒಂದು ತಗ್ಗಿನಲ್ಲಿ ಹೊಂಚಿ ನೋಡುತ್ತಿದ್ದನು.",
			"6": "<sup>6</sup> ಆದರೆ ಅವನು ಕೇನ್ಯರಿಗೆ - ನೀವು ಅಮಾಲೇಕ್ಯರನ್ನು ಬಿಟ್ಟು ಹೋಗಿರಿ; ಇಲ್ಲವಾದರೆ ನೀವೂ ಅವರೊಡನೆ ನಾಶವಾದೀರಿ; ಇಸ್ರಾಯೇಲ್ಯರು ಐಗುಪ್ತದಿಂದ ಬರುತ್ತಿದ್ದಾಗ ನೀವು ಅವರಿಗೆ ದಯೆತೋರಿಸಿದಿರಲ್ಲಾ ಎಂದು ಹೇಳಿ ಕಳುಹಿಸಿದನು. ಅದರಂತೆಯೇ ಅವರು ಅಮಾಲೇಕ್ಯರನ್ನು ಬಿಟ್ಟುಹೋದರು.",
			"7": "<sup>7</sup> ಆಗ ಸೌಲನು ಅಮಾಲೇಕ್ಯರನ್ನು ಹವೀಲಾ ಪ್ರಾಂತದಿಂದ ಐಗುಪ್ತದ ಪೂರ್ವದಿಕ್ಕಿನಲ್ಲಿರುವ ಶೂರಿನವರೆಗೂ ಹೊಡೆಯುತ್ತಾ ಹೋದನು.",
			"8": "<sup>8</sup> ಅವರ ಅರಸನಾದ ಅಗಾಗನನ್ನು ಸಜೀವಿಯಾಗಿ ಹಿಡಿದು ಎಲ್ಲಾ ಜನರನ್ನು ಕತ್ತಿಯಿಂದ ಸಂಹರಿಸಿದನು.",
			"9": "<sup>9</sup> ಸೌಲನೂ ಇಸ್ರಾಯೇಲ್ಯರೂ ಅಗಾಗನನ್ನೂ ಮೇಲ್ತರದ ಕುರಿ ದನಗಳನ್ನೂ ಕುರಿಮರಿಗಳನ್ನೂ ಕೊಬ್ಬಿದ ಪಶುಗಳನ್ನೂ ಶ್ರೇಷ್ಠವಾದ ಎಲ್ಲಾ ಪದಾರ್ಥಗಳನ್ನೂ ನಾಶಮಾಡ ಮನಸ್ಸಿಲ್ಲದೆ ಉಳಿಸಿ ಪ್ರಯೋಜನವಿಲ್ಲದಂಥ ಹೀನವಾದವುಗಳನ್ನೆಲ್ಲಾ ನಾಶಮಾಡಿಬಿಟ್ಟರು.",
			"10": "<sup>10</sup> ಯೆಹೋವನು ಸಮುವೇಲನಿಗೆ - ಸೌಲನನ್ನು ಅರಸನನ್ನಾಗಿ ಮಾಡಿದ್ದಕ್ಕೆ ಈಗ ಪಶ್ಚಾತ್ತಾಪಪಡುತ್ತೇನೆ. ಅವನು ನನ್ನನ್ನು ಬಿಟ್ಟುಬಿಟ್ಟನು; ನನ್ನ ಆಜ್ಞೆಗಳನ್ನು ನೆರವೇರಿಸುವದಿಲ್ಲ ಎಂದು ಹೇಳಲು ಸಮುವೇಲನು ಕೋಪಗೊಂಡು ರಾತ್ರಿಯೆಲ್ಲಾ ಆತನಿಗೆ ಮೊರೆಯಿಟ್ಟನು.",
			"12": "<sup>12</sup> ಅವನು ಬೆಳಿಗ್ಗೆ ಎದ್ದು ಸೌಲನನ್ನು ನೋಡುವದಕ್ಕೆ ಹೊರಟಾಗ ಅವನಿಗೆ - ಸೌಲನು ಕರ್ಮೆಲಿಗೆ ಹೋಗಿ ಅಲ್ಲಿ ತನಗೋಸ್ಕರ ಒಂದು ಜ್ಞಾಪಕಸ್ತಂಭವನ್ನು ನಿಲ್ಲಿಸಿ ಅಲ್ಲಿಂದ ಗಟ್ಟಾ ಇಳಿದು ಗಿಲ್ಗಾಲಿಗೆ ಹೋದನೆಂಬ ವರ್ತಮಾನವು ಬಂದದರಿಂದ ಸಮುವೇಲನು ಅಲ್ಲಿಗೆ ಹೋದನು.",
			"13": "<sup>13</sup> ಸೌಲನು ಅವನನ್ನು ಕಂಡು - ಯೆಹೋವನು ನಿನ್ನನ್ನು ಆಶೀರ್ವದಿಸಲಿ; ನಾನು ಆತನ ಅಪ್ಪಣೆಯನ್ನು ನೆರವೇರಿಸಿದೆನು ಅಂದನು.",
			"14": "<sup>14</sup> ಅದಕ್ಕೆ ಸಮುವೇಲನು - ಹಾಗಾದರೆ ಇದೇನು? ಕುರಿಗಳ ಕೂಗು ನನ್ನ ಕಿವಿಗೆ ಬೀಳುತ್ತದೆ, ದನಗಳ ಶಬ್ದವು ನನಗೆ ಕೇಳಿಸುತ್ತದೆ ಎನ್ನಲು",
			"15": "<sup>15</sup> ಸೌಲನು - ಜನರು ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನಿಗೋಸ್ಕರ ಯಜ್ಞಸಲ್ಲಿಸುವದಕ್ಕಾಗಿ ಅಮಾಲೇಕ್ಯರ ಕುರಿದನಗಳಲ್ಲಿ ಮೇಲ್ತರದವುಗಳನ್ನು ಉಳಿಸಿ ತಂದಿದ್ದಾರೆ; ಮಿಕ್ಕಾದವುಗಳನ್ನೆಲ್ಲಾ ಸಂಪೂರ್ಣವಾಗಿ ನಾಶಮಾಡಿದೆವು ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"16": "<sup>16</sup> ಆಗ ಸಮುವೇಲನು ಅವನಿಗೆ - ಅದಿರಲಿ; ಯೆಹೋವನು ಕಳೆದ ರಾತ್ರಿಯಲ್ಲಿ ನನಗೆ ಹೇಳಿದ್ದನ್ನು ತಿಳಿಸುತ್ತೇನೆ ಕೇಳು ಎನ್ನಲು ಅವನು - ಹೇಳು ಅಂದನು.",
			"17": "<sup>17</sup> ಸಮುವೇಲನು - ನೀನು ನಿನ್ನ ದೃಷ್ಟಿಯಲ್ಲಿ ಅಲ್ಪನಾಗಿದ್ದರೂ ಯೆಹೋವನು ನಿನಗೆ ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ರಾಜ್ಯಾಭಿಷೇಕಮಾಡಿದ್ದರಿಂದ ನೀನು ಎಲ್ಲಾ ಕುಲಗಳಿಗೆ ಶಿರಸ್ಸಾದಿ.",
			"18": "<sup>18</sup> ಆತನು ನಿನಗೆ - ಹೊರಟು ಹೋಗಿ ದುಷ್ಟರಾದ ಅಮಾಲೇಕ್ಯರೊಡನೆ ಯುದ್ಧಮಾಡಿ ಅವರೆಲ್ಲರನ್ನೂ ಸಂಪೂರ್ಣವಾಗಿ ನಾಶಮಾಡು ಎಂದು ಹೇಳಿದನು.",
			"19": "<sup>19</sup> ಆದರೆ ನೀನು ಯೆಹೋವನ ಮಾತನ್ನು ಕೇಳದೆ ಕೊಳ್ಳೆಗಾಗಿ ಎರಗಿ ಆತನಿಗೆ ದ್ರೋಹಮಾಡಿದ್ದೇಕೆ ಅಂದನು.",
			"20": "<sup>20</sup> ಆಗ ಸೌಲನು ಸಮುವೇಲನಿಗೆ - ಏನು? ನಾನು ಯೆಹೋವನ ಮಾತನ್ನು ಕೇಳಲಿಲ್ಲವೋ? ಆತನು ಕಳುಹಿಸಿದಲ್ಲಿಗೆ ಹೋಗಿ ಅಮಾಲೇಕ್ಯರನ್ನೆಲ್ಲಾ ಸಂಹರಿಸಿ ಅವರ ಅರಸನಾದ ಅಗಾಗನನ್ನು ಹಿಡಿದು ತಂದೆನು.",
			"21": "<sup>21</sup> ಆದರೆ ಜನರು ಸಂಪೂರ್ಣವಾಗಿ ಸಂಹರಿಸಬೇಕಾದ ಕೊಳ್ಳೆಯಲ್ಲಿ ಉತ್ತಮವಾದ ಕುರಿದನಗಳನ್ನು ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಗಿಲ್ಗಾಲಿನಲ್ಲಿ ಯಜ್ಞಾರ್ಪಣೆಮಾಡುವದಕ್ಕಾಗಿ ಉಳಿಸಿ ತಂದರು ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"22": "<sup>22</sup> ಅದಕ್ಕೆ ಸಮುವೇಲನು - ಯೆಹೋವನು ವಿಧೇಯತ್ವಕ್ಕೆ ಮೆಚ್ಚುವಷ್ಟು ಯಜ್ಞಹೋಮಗಳಿಗೆ ಮೆಚ್ಚುತ್ತಾನೋ? ಯಜ್ಞವನ್ನರ್ಪಿಸುವದಕ್ಕಿಂತ ಮಾತು ಕೇಳುವದು ಉತ್ತಮವಾಗಿದೆ; ಟಗರುಗಳ ಕೊಬ್ಬಿಗಿಂತ ವಿಧೇಯತ್ವವು ವಿಶೇಷವಾಗಿದೆ.",
			"23": "<sup>23</sup> ಅವಿಧೇಯತ್ವವು ಮಂತ್ರತಂತ್ರಗಳಷ್ಟೇ ಕೆಟ್ಟದ್ದಾಗಿರುವದು; ಹಟವು ಮಿಥ್ಯಾಭಕ್ತಿಗೂ ವಿಗ್ರಹಾರಾಧನೆಗೂ ಸಮಾನವಾಗಿರುವದು. ನೀನು ಯೆಹೋವನ ಮಾತನ್ನು ತಳ್ಳಿಬಿಟ್ಟದ್ದರಿಂದ ಆತನು ನಿನ್ನನ್ನು ಅರಸುತನದಿಂದ ತಳ್ಳಿಬಿಟ್ಟಿದ್ದಾನೆ ಎಂದು ನುಡಿದನು.",
			"24": "<sup>24</sup> ಸೌಲನು ಸಮುವೇಲನಿಗೆ - ನಾನು ಯೆಹೋವನ ಮತ್ತು ನಿನ್ನ ಆಜ್ಞೆಗಳನ್ನು ಮೀರಿ ಪಾಪಮಾಡಿದ್ದೇನೆ; ಜನರಿಗೆ ಹೆದರಿ ಅವರ ಮಾತನ್ನು ಕೇಳಿದೆನು.",
			"25": "<sup>25</sup> ನೀನು ದಯವಿಟ್ಟು ನನ್ನ ಪಾಪವನ್ನು ಕ್ಷಮಿಸು; ನಾನು ಯೆಹೋವನನ್ನು ಆರಾಧಿಸುವಂತೆ ಹಿಂದಿರುಗಿ ನನ್ನ ಜೊತೆಯಲ್ಲಿ ಬಾ ಎಂದು ಅವನನ್ನು ಬೇಡಿಕೊಂಡನು.",
			"26": "<sup>26</sup> ಅದಕ್ಕೆ ಸಮುವೇಲನು - ನಾನು ಹಿಂದಿರುಗಿ ನಿನ್ನ ಜೊತೆಯಲ್ಲಿ ಬರುವದಿಲ್ಲ; ನೀನು ಯೆಹೋವನ ಮಾತನ್ನು ತಳ್ಳಿಬಿಟ್ಟದ್ದರಿಂದ ಆತನು ನಿನ್ನನ್ನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸುತನದಿಂದ ತಳ್ಳಿಬಿಟ್ಟಿದ್ದಾನೆ ಎಂದು ಹೇಳಿ ತಿರುಗಿಕೊಂಡು ಹೊರಡಲು",
			"27": "<sup>27</sup> ಸೌಲನು ಅವನ ಮೇಲಂಗಿಯ ಅಂಚನ್ನು ಹಿಡಿದು ಎಳೆದನು; ಅದು ಹರಿದುಹೋಯಿತು.",
			"28": "<sup>28</sup> ಆಗ ಸಮುವೇಲನು ಅವನಿಗೆ - ಯೆಹೋವನು ಈ ಹೊತ್ತು ಇಸ್ರಾಯೇಲ್‍ರಾಜ್ಯವನ್ನು ನಿನ್ನಿಂದ ಕಿತ್ತು ನಿನಗಿಂತ ಉತ್ತಮನಾದ ಇನ್ನೊಬ್ಬನಿಗೆ ಕೊಟ್ಟಿದ್ದಾನೆ.",
			"29": "<sup>29</sup> ಇಸ್ರಾಯೇಲ್ಯರ ನಿತ್ಯಾಧಾರನು ಸುಳ್ಳಾಡುವವನಲ್ಲ; ಪಶ್ಚಾತ್ತಾಪಪಡುವವನಲ್ಲ; ಯಾಕಂದರೆ ಅವನು ಪಶ್ಚಾತ್ತಾಪಪಡ ಮನುಷ್ಯನಲ್ಲ ಎಂದು ಹೇಳಿದನು.",
			"30": "<sup>30</sup> ಅದಕ್ಕೆ ಸೌಲನು - ನಾನು ಪಾಪ ಮಾಡಿದ್ದೇನೆ; ದಯವಿಟ್ಟು ಇಸ್ರಾಯೇಲ್ಯರ ಮುಂದೆಯೂ ಜನರ ಹಿರಿಯರ ಮುಂದೆಯೂ ನನ್ನ ಮಾನವನ್ನುಳಿಸು; ನಾನು ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಆರಾಧಿಸುವದಕ್ಕಾಗುವಂತೆ ಹಿಂದಿರುಗಿ ನನ್ನ ಜೊತೆಯಲ್ಲಿ ಬಾ ಎಂದು ಬೇಡಿಕೊಳ್ಳಲು ಅವನು ಹಿಂದಿರುಗಿ ಸೌಲನ ಜೊತೆಯಲ್ಲಿ ಹೋದನು.",
			"31": "<sup>31</sup> ಆಗ ಸೌಲನು ದೇವರನ್ನು ಆರಾಧಿಸಿದನು.",
			"32": "<sup>32</sup> ತರುವಾಯ ಸಮುವೇಲನು ಅಮಾಲೇಕ್ಯರ ಅರಸನಾದ ಅಗಾಗನನ್ನು ತನ್ನ ಬಳಿಗೆ ತರ ಹೇಳಿದನು. ಅಗಾಗನು ಮರಣಭಯ ತಪ್ಪಿತೆಂದು ನೆನಸಿಕೊಂಡು ಸಂತೋಷದಿಂದ ಅವನ ಹತ್ತಿರ ಬಂದನು.",
			"33": "<sup>33</sup> ಆದರೆ ಸಮುವೇಲನು ಅವನಿಗೆ - ನಿನ್ನ ಕತ್ತಿಯು ಅನೇಕ ಸ್ತ್ರೀಯರಿಗೆ ಪುತ್ರ ಶೋಕವನ್ನುಂಟುಮಾಡಿತು; ನಿನ್ನ ತಾಯಿಯು ಎಲ್ಲರಿಗಿಂತಲೂ ಹೆಚ್ಚಾಗಿ ಪುತ್ರಶೋಕವನ್ನು ಅನುಭವಿಸಲಿ ಎಂದು ಹೇಳಿ ಅವನನ್ನು ಗಿಲ್ಗಾಲಿನಲ್ಲಿ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲೇ ಕಡಿದು ಹಾಕಿದನು.",
			"34": "<sup>34</sup> ಅನಂತರ ಸಮುವೇಲನು ರಾಮಕ್ಕೆ ಹೋದನು; ಸೌಲನು ತನ್ನ ಊರಾದ ಗಿಬೆಯದಲ್ಲಿರುವ ಮನೆಗೆ ಹೋದನು.",
			"35": "<sup>35</sup> ಸಮುವೇಲನು ಜೀವದಿಂದಿರುವವರೆಗೂ ಸೌಲನನ್ನು ನೋಡುವದಕ್ಕೆ ಹೋಗಲಿಲ್ಲ; ಆದರೆ ಯೆಹೋವನು ಸೌಲನನ್ನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನನ್ನಾಗಿ ಮಾಡಿದ್ದಕ್ಕೋಸ್ಕರ ಪಶ್ಚಾತ್ತಾಪಪಟ್ಟದರಿಂದ ಸಮುವೇಲನು ಅವನ ವಿಷಯದಲ್ಲಿ ದುಃಖಪಡುತ್ತಿದ್ದನು."
		},
		"16": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಸಮುವೇಲನಿಗೆ - ನಾನು ಸೌಲನನ್ನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾಗಿರುವದಕ್ಕೆ ಅಯೋಗ್ಯನೆಂದು ತಳ್ಳಿ ಬಿಟ್ಟೆನಲ್ಲಾ; ನೀನು ಅವನಿಗೋಸ್ಕರ ಎಷ್ಟರವರೆಗೆ ದುಃಖಿಸುತ್ತಿರುವಿ? ಕೊಂಬನ್ನು ಎಣ್ಣೆಯಿಂದ ತುಂಬಿಸಿಕೊಂಡು ಬಾ; ನಾನು ನಿನ್ನನ್ನು ಬೇತ್ಲೆಹೇಮಿನವನಾದ ಇಷಯನ ಬಳಿಗೆ ಕಳುಹಿಸುತ್ತೇನೆ; ಅವನ ಮಕ್ಕಳಲ್ಲೊಬ್ಬನನ್ನು ಅರಸನನ್ನಾಗಿ ಆರಿಸಿಕೊಂಡಿದ್ದೇನೆ ಎಂದು ಹೇಳಿದನು.",
			"2": "<sup>2</sup> ಅದಕ್ಕೆ ಸಮುವೇಲನು - ನಾನು ಹೋಗುವದು ಹೇಗೆ? ಇದು ಸೌಲನಿಗೆ ಗೊತ್ತಾದರೆ ಅವನು ನನ್ನನ್ನು ಕೊಂದುಹಾಕುವನಲ್ಲವೇ ಅನ್ನಲು ಆತನು - ನೀನು ಒಂದು ಕಡಸನ್ನು ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ಯೆಹೋವನಿಗೋಸ್ಕರ ಯಜ್ಞಮಾಡುವದಕ್ಕೆ ಬಂದಿದ್ದೇನೆಂದು ಹೇಳಿ ಇಷಯನನ್ನು ಅದಕ್ಕೆ ಕರೆ.",
			"3": "<sup>3</sup> ಆಮೇಲೆ ನೀನು ಮಾಡಬೇಕಾದದ್ದನ್ನು ನಾನೇ ತಿಳಿಸುತ್ತೇನೆ; ನಾನು ಯಾವನನ್ನು ತೋರಿಸುತ್ತೇನೋ ಅವನನ್ನು ನೀನು ಅಭಿಷೇಕಿಸಬೇಕು ಎಂದನು.",
			"4": "<sup>4</sup> ಯೆಹೋವನು ಹೇಳಿದಂತೆಯೇ ಸಮುವೇಲನು ಬೇತ್ಲೆಹೇಮಿಗೆ ಹೋದನು. ಆ ಊರಿನ ಹಿರಿಯರು ನಡುಗುತ್ತಾ ಬಂದು ಅವನನ್ನು ಎದುರುಗೊಂಡು ನಿನ್ನ ಆಗಮನವು ನಮಗೆ ಶುಭಕರವಾಗಿದೆಯೇ ಎಂದು ಕೇಳಲು ಅವನು -",
			"5": "<sup>5</sup> ಹೌದು, ಶುಭಕರವಾಗಿದೆ; ಯೆಹೋವನಿಗೆ ಯಜ್ಞವನ್ನರ್ಪಿಸುವದಕ್ಕೆ ಬಂದಿದ್ದೇನೆ. ನೀವು ನಿಮ್ಮನ್ನು ಶುದ್ಧಪಡಿಸಿಕೊಂಡು ನನ್ನ ಜೊತೆಯಲ್ಲಿ ಯಜ್ಞಕ್ಕೆ ಬನ್ನಿರಿ ಎಂದು ಹೇಳಿ ಇಷಯನನ್ನೂ ಅವನ ಮಕ್ಕಳನ್ನೂ ಶುದ್ಧೀಕರಿಸಿ ಅವರನ್ನೂ ಯಜ್ಞಕ್ಕೆ ಕರೆದನು.",
			"6": "<sup>6</sup> ಅವರು ಬರಲು ಅವನು ಎಲೀಯಾಬನನ್ನು ನೋಡಿ - ನಿಶ್ಚಯವಾಗಿ ಯೆಹೋವನು ಅಭಿಷೇಕಕ್ಕೆ ಆರಿಸಿಕೊಂಡವನು ಆತನ ಮುಂದೆ ಇದ್ದಾನೆ ಎಂದುಕೊಂಡನು.",
			"7": "<sup>7</sup> ಆದರೆ ಯೆಹೋವನು ಸಮುವೇಲನಿಗೆ - ನೀನು ಅವನ ಚೆಲುವಿಕೆಯನ್ನೂ ನೀಳವನ್ನೂ ನೋಡಬೇಡ; ನಾನು ಅವನನ್ನು ತಳ್ಳಿಬಿಟ್ಟಿದ್ದೇನೆ. ಯೆಹೋವನು ಮನುಷ್ಯರಂತೆ ಹೊರಗಿನ ತೋರಿಕೆಯನ್ನು ನೋಡದೆ ಹೃದಯವನ್ನೇ ನೋಡುವವನಾಗಿದ್ದಾನೆ ಅಂದನು.",
			"8": "<sup>8</sup> ಇಷಯನು ಅಬೀನಾದಾಬನನ್ನು ಸಮುವೇಲನು ಬಳಿಗೆ ಬರಮಾಡಲು ಸಮುವೇಲನು - ಯೆಹೋವನು ಇವನನ್ನು ಆರಿಸಿಕೊಳ್ಳಲಿಲ್ಲ ಎಂದನು.",
			"9": "<sup>9</sup> ತರುವಾಯ ಇಷಯನು ಶಮ್ಮ ಎಂಬವನನ್ನು ಬರಮಾಡಲು ಸಮುವೇಲನು - ಯೆಹೋವನು ಇವನನ್ನು ಆರಿಸಿಕೊಳ್ಳಲಿಲ್ಲ ಎಂದು ಹೇಳಿದನು.",
			"10": "<sup>10</sup> ಹೀಗೆ ಇಷಯನು ತನ್ನ ಮಕ್ಕಳಲ್ಲಿ ಏಳು ಮಂದಿಯನ್ನು ಅವನ ಬಳಿಗೆ ಬರಮಾಡಿದರೂ ಅವನು - ಯೆಹೋವನು ಇವರನ್ನು ಆರಿಸಿಕೊಳ್ಳಲಿಲ್ಲ ಎಂದು ನುಡಿದನು.",
			"11": "<sup>11</sup> ಅನಂತರ ಸಮುವೇಲನು ಇಷಯನನ್ನು - ನಿನ್ನ ಮಕ್ಕಳೆಲ್ಲಾ ಇಷ್ಟೇ ಮಂದಿಯೋ ಎಂದು ಕೇಳಲು ಅವನು - ಇವರೆಲ್ಲರಿಗಿಂತಲೂ ಚಿಕ್ಕವನು ಒಬ್ಬನಿರುತ್ತಾನೆ; ಅವನು ಕುರಿಮೇಯಿಸುವದಕ್ಕೆ ಹೋಗಿದ್ದಾನೆ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು. ಆಗ ಸಮುವೇಲನು - ಅವನನ್ನು ಕರೇಕಳುಹಿಸು; ಅವನು ಬರುವವರೆಗೂ ನಾವು ಊಟಕ್ಕೆ ಕೂತುಕೊಳ್ಳಬಾರದು ಎಂದು ಹೇಳಲು ಇಷಯನು ಅವನನ್ನು ಕರತರಿಸಿದನು.",
			"12": "<sup>12</sup> ಅವನು ಕೆಂಬಣ್ಣದವನೂ ಸುಂದರನೇತ್ರನೂ ನೋಟಕ್ಕೆ ರಮಣೀಯನೂ ಆಗಿದ್ದನು. ಯೆಹೋವನು ಸಮುವೇಲನಿಗೆ - ಎದ್ದು ಇವನನ್ನು ಅಭಿಷೇಕಿಸು; ನಾನು ಆರಿಸಿಕೊಂಡವನು ಇವನೇ ಎಂದು ಆಜ್ಞಾಪಿಸಲು",
			"13": "<sup>13</sup> ಸಮುವೇಲನು ಎಣ್ಣೆಯ ಕೊಂಬನ್ನು ತೆಗೆದುಕೊಂಡು ಅವನನ್ನು ಅವನ ಸಹೋದರರ ಮಧ್ಯದಲ್ಲೇ ಅಭಿಷೇಕಿಸಿದನು. ಕೂಡಲೆ ಯೆಹೋವನ ಆತ್ಮವು ದಾವೀದನ ಮೇಲೆ ಬಂದು ನೆಲೆಗೊಂಡಿತು. ಅನಂತರ ಸಮುವೇಲನು ರಾಮಕ್ಕೆ ಹೊರಟು ಹೋದನು.",
			"14": "<sup>14</sup> ಯೆಹೋವನ ಆತ್ಮವು ಸೌಲನನ್ನು ಬಿಟ್ಟುಹೋಯಿತು; ಯೆಹೋವನು ಕಳುಹಿಸಿದ ದುರಾತ್ಮವು ಬಂದು ಅವನನ್ನು ಪೀಡಿಸುತ್ತಿದ್ದದರಿಂದ",
			"15": "<sup>15</sup> ಅವನ ಸೇವಕರು ಅವನಿಗೆ - ಇಗೋ, ದೇವರಿಂದ ಬಂದ ದುರಾತ್ಮವು ನಿನ್ನನ್ನು ಪೀಡಿಸುತ್ತಾ ಇದೆ.",
			"16": "<sup>16</sup> ಸ್ವಾಮೀ, ಅಪ್ಪಣೆಯಾಗಲಿ; ನಿನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನಿಂತಿರುವ ನಿನ್ನ ಸೇವಕರಾದ ನಾವು ಹೋಗಿ ಕಿನ್ನರಿಬಾರಿಸುವವರಲ್ಲಿ ನಿಪುಣನಾದ ಒಬ್ಬನನ್ನು ಹುಡುಕಿ ತರುತ್ತೇವೆ. ಯೆಹೋವನಿಂದ ಕಳುಹಿಸಲ್ಪಟ್ಟ ದುರಾತ್ಮವು ನಿನ್ನ ಮೇಲೆ ಬರುವಾಗ ಅವನು ಬಾರಿಸಿದರೆ ನಿನಗೆ ಉಪಶಮನವಾಗುವದು ಎಂದು ಹೇಳಿದರು.",
			"17": "<sup>17</sup> ಸೌಲನು ಅವರಿಗೆ - ಚೆನ್ನಾಗಿ ಬಾರಿಸಬಲ್ಲವನಾದ ಒಬ್ಬನನ್ನು ನನಗೋಸ್ಕರ ಹುಡುಕಿ ಕರಕೊಂಡು ಬನ್ನಿರಿ ಎಂದು ಆಜ್ಞಾಪಿಸಲು",
			"18": "<sup>18</sup> ಸೇವಕರಲ್ಲೊಬ್ಬನು ಅವನಿಗೆ - ಬೇತ್ಲೆಹೇಮಿನವನಾದ ಇಷಯನ ಮಗನನ್ನು ನೋಡಿದ್ದೇನೆ; ಅವನು ಚೆನ್ನಾಗಿ ಬಾರಿಸಬಲ್ಲವನೂ ಪರಾಕ್ರಮಶಾಲಿಯೂ ರಣಶೂರನೂ ವಾಕ್ಚತುರನೂ ಸುಂದರನೂ ಯೆಹೋವನ ಅನುಗ್ರಹವನ್ನು ಹೊಂದಿದವನೂ ಆಗಿದ್ದಾನೆಂದು ತಿಳಿಸಿದನು.",
			"19": "<sup>19</sup> ಕೂಡಲೆ ಸೌಲನು ಇಷಯನ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿ ಅವನಿಗೆ - ಕುರಿಗಳನ್ನು ಮೇಯಿಸುತ್ತಿರುವ ನಿನ್ನ ಮಗನಾದ ದಾವೀದನನ್ನು ನನ್ನ ಬಳಿಗೆ ಕಳುಹಿಸು ಎಂದು ಹೇಳಿಸಿದನು.",
			"20": "<sup>20</sup> ಇಷಯನು ರೊಟ್ಟಿಯನ್ನೂ ಒಂದು ಬುದ್ದಲಿ ದ್ರಾಕ್ಷಾರಸವನ್ನೂ ಒಂದು ಹೋತಮರಿಯನ್ನೂ ಕತ್ತೆಯ ಮೇಲೆ ಹೇರಿಸಿ ತನ್ನ ಮಗನಾದ ದಾವೀದನ ಮುಖಾಂತರ ಸೌಲನಿಗೆ ಕಳುಹಿಸಿದನು.",
			"21": "<sup>21</sup> ಹೀಗೆ ದಾವೀದನು ಸೌಲನ ಬಳಿಗೆ ಬಂದು ಅವನ ಸೇವಕನಾದನು; ಸೌಲನು ಅವನನ್ನು ಬಹಳವಾಗಿ ಪ್ರೀತಿಸಿ ತನ್ನ ಆಯುಧಗಳನ್ನು ಹೊರುವದಕ್ಕೆ ನೇಮಿಸಿಕೊಂಡನು.",
			"22": "<sup>22</sup> ತರುವಾಯ ಸೌಲನು ಇಷಯನ ಬಳಿಗೆ ದೂತರನ್ನು ಅಟ್ಟಿ ಅವನಿಗೆ - ನಾನು ದಾವೀದನನ್ನು ಮೆಚ್ಚಿದ್ದೇನೆ; ಅವನು ನನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸೇವೆಮಾಡಲಿ ಎಂದು ತಿಳಿಸಿದನು.",
			"23": "<sup>23</sup> ದೇವರಿಂದ ಕಳುಹಿಸಲ್ಪಟ್ಟ ದುರಾತ್ಮವು ಸೌಲನ ಮೇಲೆ ಬಂದಾಗ ದಾವೀದನು ಕಿನ್ನರಿಯನ್ನು ಬಾರಿಸುವನು; ಅದರಿಂದ ದುರಾತ್ಮವು ಸೌಲನನ್ನು ಬಿಟ್ಟುಹೋಗುವದು; ಅವನು ಉಪಶಮನಹೊಂದಿ ಚೆನ್ನಾಗಿರುವನು."
		},
		"17": {
			"1": "<sup>1</sup> ಫಿಲಿಷ್ಟಿಯರು ಯುದ್ಧಮಾಡುವದಕ್ಕೋಸ್ಕರ ಯೆಹೂದದೇಶದ ಸೋಕೋವಿನಲ್ಲಿ ಸೈನ್ಯವನ್ನು ಕೂಡಿಸಿಕೊಂಡು ಬಂದು ಸೋಕೋವಿಗೂ ಅಜೇಕಕ್ಕೂ ಮಧ್ಯದಲ್ಲಿರುವ ಎಫೆಸ್ದಮ್ಮೀಮಿನಲ್ಲಿ ಪಾಳೆಯ ಮಾಡಿಕೊಂಡರು.",
			"2": "<sup>2</sup> ಸೌಲನೂ ಇಸ್ರಾಯೇಲ್ಯರೂ ಫಿಲಿಷ್ಟಿಯರೊಡನೆ ಯುದ್ಧಮಾಡುವದಕ್ಕಾಗಿ ಸೈನ್ಯವನ್ನು ಕೂಡಿಸಿ ಏಲಾ ತಗ್ಗಿನಲ್ಲಿ ಪಾಳೆಯಮಾಡಿಕೊಂಡು ವ್ಯೂಹಕಟ್ಟಿದರು.",
			"3": "<sup>3</sup> ಫಿಲಿಷ್ಟಿಯರು ಒಂದು ಗುಡ್ಡದ ಪಕ್ಕದಲ್ಲಿಯೂ ಇಸ್ರಾಯೇಲ್ಯರು ಇನ್ನೊಂದು ಗುಡ್ಡದ ಪಕ್ಕದಲ್ಲಿಯೂ ನಿಂತರು.",
			"4": "<sup>4</sup> ಉಭಯರ ಮಧ್ಯದಲ್ಲಿ ಒಂದು ತಗ್ಗು ಇತ್ತು. ಫಿಲಿಷ್ಟಿಯರ ಪಾಳೆಯದಿಂದ ಗತ್ ಊರಿನವನಾದ ಗೊಲ್ಯಾತನೆಂಬ ಒಬ್ಬ ರಣಶೂರನು ಹೊರಟುಬಂದನು. ಅವನು ಆರುವರೆ ಮೊಳ ಎತ್ತರವಾಗಿದ್ದನು.",
			"5": "<sup>5</sup> ಅವನ ಶಿರಸ್ತ್ರಾಣವು ತಾಮ್ರದ್ದು; ಅದೇ ಲೋಹದ ಬಿಲ್ಲೆಗಳಿಂದ ಪರೆಪರೆಯಾಗಿ ಜೋಡಿಸಲ್ಪಟ್ಟ ಅವನ ಕವಚವು ಐದು ಸಾವಿರ ರೂಪಾಯಿ ತೂಕವಾಗಿತ್ತು.",
			"6": "<sup>6</sup> ಅವನ ಪಾದರಕ್ಷೆಗಳೂ ಹೆಗಲ ಮೇಲಣ ಈಟಿಯೂ ತಾಮ್ರದವು.",
			"7": "<sup>7</sup> ಅವನ ಬರ್ಜಿಯ ಹಿಡಿಕೆಯು ನೇಯಿಗಾರರ ಕುಂಟೆಯಷ್ಟು ಗಾತ್ರವಾಗಿತ್ತು. ಅದರ ಅಲಗು ಆರುನೂರು ತೊಲೆ ಕಬ್ಬಿಣದಿಂದ ಮಾಡಿದ್ದು. ಅವನ ಗುರಾಣಿಯನ್ನು ಹೊರುವವನು ಅವನ ಮುಂದೆ ಮುಂದೆ ಹೋಗುತ್ತಿದ್ದನು.",
			"8": "<sup>8</sup> ಅವನು ನಿಂತು ಇಸ್ರಾಯೇಲ್‍ಸೈನ್ಯದವರನ್ನು ಕೂಗಿ ಅವರಿಗೆ - ನೀವು ಯುದ್ಧಮಾಡಬಂದದ್ದೇಕೆ? ನಾನು ಫಿಲಿಷ್ಟಿಯನು, ನೀವು ಸೌಲನ ಸೇವಕರು. ನಿಮ್ಮಲ್ಲಿ ಒಬ್ಬನನ್ನು ಆರಿಸಿಕೊಂಡು ನನ್ನ ಬಳಿಗೆ ಕಳುಹಿಸಿರಿ;",
			"9": "<sup>9</sup> ಅವನು ನನ್ನೊಡನೆ ಯುದ್ಧಮಾಡಿ ನನ್ನನ್ನು ಸೋಲಿಸಿ ಕೊಂದರೆ ನಮ್ಮವರು ನಿಮ್ಮ ಸೇವಕರಾಗುವರು; ನಾನು ಅವನನ್ನು ಸೋಲಿಸಿ ಕೊಂದರೆ ನೀವು ನಮ್ಮ ದಾಸರಾಗಿ ನಮ್ಮನ್ನು ಸೇವಿಸಬೇಕು ಎಂದು ಹೇಳಿದನು.",
			"10": "<sup>10</sup> ಮತ್ತು ಅವನು - ಈ ಹೊತ್ತು ಇಸ್ರಾಯೇಲ್ ಸೈನ್ಯದವರನ್ನು ಹೀಯಾಳಿಸುತ್ತೇನೆ; ನನ್ನೊಡನೆ ಕಾಳಗಕ್ಕೆ ನಿಮ್ಮಲ್ಲಿಂದ ಒಬ್ಬ ಮನುಷ್ಯನನ್ನು ಕಳುಹಿಸಿರಿ ನೋಡೋಣ ಎಂದು ಕೊಚ್ಚಿಕೊಂಡನು.",
			"11": "<sup>11</sup> ಸೌಲನೂ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರೂ ಆ ಫಿಲಿಷ್ಟಿಯನ ಮಾತುಗಳನ್ನು ಕೇಳಿ ಬಹಳವಾಗಿ ಭಯಪಟ್ಟರು; ಅವರ ಎದೆಯೊಡೆದು ಹೋಯಿತು.",
			"12": "<sup>12</sup> ಯೆಹೂದದ ಬೇತ್ಲೆಹೇಮ್ ಊರಿನ ಎಫ್ರಾತ್ಯನಾದ ಇಷಯನೆಂಬವನು ಸೌಲನ ಕಾಲದಲ್ಲಿ ಬಹು ವೃದ್ಧನಾಗಿದ್ದನು. ಇವನ ಎಂಟು ಮಂದಿ ಮಕ್ಕಳಲ್ಲಿ ದಾವೀದನೆಂಬವನು ಒಬ್ಬನು.",
			"13": "<sup>13</sup> ಇವನ ಮೂರು ಮಂದಿ ಹಿರೀ ಮಕ್ಕಳು ಸೌಲನ ಜೊತೆಯಲ್ಲಿ ಯುದ್ಧಕ್ಕೆ ಹೋಗಿದ್ದರು. ಅವರಲ್ಲಿ ಚೊಚ್ಚಲ ಮಗನ ಹೆಸರು ಎಲೀಯಾಬ್; ಮರಚಲ ಮಗನ ಹೆಸರು ಅಬೀನಾದಾಬ್; ಮೂರನೆಯವನ ಹೆಸರು ಶಮ್ಮ. ದಾವೀದನೇ ಎಲ್ಲರಿಗಿಂತಲೂ ಕಿರಿಯವನು.",
			"14": "<sup>14</sup> ಮೂರು ಮಂದಿ ಹಿರೀ ಮಕ್ಕಳು ಸೌಲನ ಜೊತೆಯಲ್ಲಿ ಯುದ್ಧಕ್ಕೆ ಹೋದದರಿಂದ",
			"15": "<sup>15</sup> ದಾವೀದನು ಸೌಲನನ್ನು ಬಿಟ್ಟು ತನ್ನ ತಂದೆಯ ಕುರಿಗಳನ್ನು ಮೇಯಿಸುವದಕ್ಕೋಸ್ಕರ ಬೇತ್ಲೆಹೇಮಿಗೆ ಹೋದನು.",
			"16": "<sup>16</sup> ಆ ಫಿಲಿಷ್ಟಿಯನು ನಾಲ್ವತ್ತು ದಿವಸ ಹೊತ್ತಾರೆಯಲ್ಲಿಯೂ ಸಾಯಂಕಾಲದಲ್ಲಿಯೂ ಬಂದು ಬಂದು ಅದೇ ಪ್ರಕಾರ ಕೂಗುತ್ತಾ ನಿಲ್ಲುತ್ತಿದ್ದನು.",
			"17": "<sup>17</sup> ಒಂದು ದಿವಸ ಇಷಯನು ತನ್ನ ಮಗನಾದ ದಾವೀದನಿಗೆ - ಇಗೋ, ಇಲ್ಲಿ ಮೂವತ್ತು ಸೇರು ಹುರಿಗಾಳೂ ಹತ್ತು ರೊಟ್ಟಿಗಳೂ ಇರುತ್ತವೆ. ಇವುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಶೀಘ್ರವಾಗಿ ಪಾಳೆಯಕ್ಕೆ ಹೋಗಿ ನಿನ್ನ ಅಣ್ಣಂದಿರಿಗೆ ಕೊಡು.",
			"18": "<sup>18</sup> ಇದಲ್ಲದೆ ಇಲ್ಲಿ ಹತ್ತು ಗಿಣ್ಣಿನ ಗಡ್ಡೆಗಳಿರುತ್ತವೆ; ಇವುಗಳನ್ನು ಅವರ ಸಹಸ್ರಾಧಿಪತಿಗೆ ಕೊಡು. ಬರುವಾಗ ನಿನ್ನ ಅಣ್ಣಂದಿರ ಕ್ಷೇಮಸಮಾಚಾರವನ್ನು ವಿಚಾರಿಸಿಕೊಂಡು ಅವರಿಂದ ಒಂದು ಗುರುತನ್ನು ತೆಗೆದುಕೊಂಡು ಬಾ.",
			"19": "<sup>19</sup> ಸೌಲನೂ ಇಸ್ರಾಯೇಲ್ಯರೂ ಏಲಾ ತಗ್ಗಿನಲ್ಲಿ ಫಿಲಿಷ್ಟಿಯರೊಡನೆ ಯುದ್ಧಮಾಡುತ್ತಿದ್ದಾರಲ್ಲಾ ಎಂದು ಹೇಳಿದನು.",
			"20": "<sup>20</sup> ದಾವೀದನು ಮರುದಿನ ಬೆಳಿಗ್ಗೆ ಎದ್ದು ಕಾಯುವವನಿಗೆ ಕುರಿಗಳನ್ನು ಒಪ್ಪಿಸಿ ತನ್ನ ತಂದೆಯಾದ ಇಷಯನು ಹೇಳಿದವುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಸೈನ್ಯವು ಹೊರಟು ಯುದ್ಧಕ್ಕಾಗಿ ಆರ್ಭಟಿಸುವ ಹೊತ್ತಿನಲ್ಲಿ ಪಾಳೆಯದ ಬಂಡಿಗಳು ನಿಂತಿದ್ದ ಸ್ಥಳಕ್ಕೆ ಬಂದನು.",
			"21": "<sup>21</sup> ಇಸ್ರಾಯೇಲ್ಯರೂ ಫಿಲಿಷ್ಟಿಯರೂ ಎದುರೆದುರಾಗಿ ವ್ಯೂಹಕಟ್ಟುವಷ್ಟರಲ್ಲಿ",
			"22": "<sup>22</sup> ದಾವೀದನು ತಾನು ತಂದವುಗಳನ್ನು ಸಾಮಾನುಕಾಯುವವನ ಬಳಿಯಲ್ಲಿಟ್ಟು ರಣರಂಗಕ್ಕೆ ಓಡಿಹೋಗಿ ತನ್ನ ಸಹೋದರರ ಕ್ಷೇಮಸಮಾಚಾರವನ್ನು ವಿಚಾರಿಸುತ್ತಾ",
			"23": "<sup>23</sup> ಅವರೊಡನೆ ಮಾತಾಡುತ್ತಿರುವಷ್ಟರಲ್ಲಿ ಗತ್ ಊರಿನ ರಣವೀರನಾದ ಗೊಲ್ಯಾತನೆಂಬ ಆ ಫಿಲಿಷ್ಟಿಯನು ತನ್ನ ಸೈನ್ಯದಿಂದ ಹೊರಗೆ ಬಂದು ಮುಂಚಿನಂತೆಯೇ ಕೂಗಿದನು. ದಾವೀದನು ಅವನ ಮಾತುಗಳನ್ನು ಕೇಳಿದನು.",
			"24": "<sup>24</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಅವನನ್ನು ನೋಡಿ ಬಹಳವಾಗಿ ಭಯಪಟ್ಟು ಅಲ್ಲಿಂದ ಓಡಿಹೋದರು.",
			"25": "<sup>25</sup> ಅವರು - ಇಸ್ರಾಯೇಲ್ಯರಾದ ನಮ್ಮನ್ನು ಹೀಯಾಳಿಸುವದಕ್ಕೋಸ್ಕರ ಬಂದಿರುವ ಈ ಮನುಷ್ಯನನ್ನು ಕಂಡಿರಾ! ಯಾವನು ಇವನನ್ನು ಕೊಲ್ಲುವನೋ ಅಂಥವನಿಗೆ ಅರಸನು ಅಪಾರದ್ರವ್ಯದೊಡನೆ ತನ್ನ ಮಗಳನ್ನು ಕೊಟ್ಟು ಮದುವೆ ಮಾಡುವನು; ಇದಲ್ಲದೆ ಅವನ ಕುಟುಂಬಕ್ಕೆ ಸರ್ವಮಾನ್ಯವನ್ನು ಕೊಡುವನು ಎಂದು ಮಾತಾಡಿಕೊಳ್ಳುತ್ತಿದ್ದರು.",
			"26": "<sup>26</sup> ದಾವೀದನು - ಜೀವಸ್ವರೂಪನಾದ ದೇವರ ಸೈನ್ಯವನ್ನು ಹೀಯಾಳಿಸುವದಕ್ಕೆ ಸುನ್ನತಿಯಿಲ್ಲದ ಈ ಫಿಲಿಷ್ಟಿಯನು ಎಷ್ಟರವನು? ಇವನನ್ನು ಕೊಂದು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಬಂದಿರುವ ನಿಂದೆಯನ್ನು ತೆಗೆದುಹಾಕುವವನಿಗೆ ಸಿಕ್ಕುವದೇನೆಂದು ಹೇಳಿದಿರಿ ಎಂದು ತನ್ನ ಬಳಿಯಲ್ಲಿ ನಿಂತಿದ್ದ ಮನುಷ್ಯರನ್ನು ಕೇಳಲು",
			"27": "<sup>27</sup> ಅವರು ಮುಂಚಿನಂತೆ ಇಂಥಿಂಥದು ದೊರಕುವದೆಂದು ಉತ್ತರಕೊಟ್ಟರು.",
			"28": "<sup>28</sup> ದಾವೀದನು ಜನರ ಸಂಗಡ ಹೀಗೆ ಮಾತಾಡುತ್ತಿರುವದನ್ನು ಅವನ ಹಿರೀ ಅಣ್ಣನಾದ ಎಲೀಯಾಬನು ಕೇಳಿ ಕೋಪಗೊಂಡು - ನೀನು ಇಲ್ಲಿಗೆ ಬಂದದ್ದೇಕೆ? ಅಡವಿಯಲ್ಲಿರುವ ನಾಲ್ಕಾರು ಕುರಿಗಳನ್ನು ಯಾರಿಗೊಪ್ಪಿಸಿ ಬಂದಿ? ನಿನ್ನ ಸೊಕ್ಕೂ ತುಂಟತನವೂ ನನಗೆ ಗೊತ್ತದೆ; ನೀನು ಯುದ್ಧವನ್ನು ನೋಡುವದಕ್ಕೆ ಬಂದಿದ್ದೀ ಎಂದು ಅವನನ್ನು ಗದರಿಸಲು",
			"29": "<sup>29</sup> ದಾವೀದನು - ನಾನೇನು ತಪ್ಪು ಮಾಡಿದೆನು? ಬರೀ ಮಾತಾಡಿದೆನಷ್ಟೆ ಎಂದು ಹೇಳಿ ಅವನನ್ನು ಬಿಟ್ಟು ಬೇರೆ ಕಡೆಗೆ ಹೋಗಿ",
			"30": "<sup>30</sup> ಹಾಗೆಯೇ ವಿಚಾರಮಾಡಲು ಜನರು ಮುಂಚಿನಂತೆಯೇ ಉತ್ತರಕೊಟ್ಟರು.",
			"31": "<sup>31</sup> ದಾವೀದನು ಈ ಪ್ರಕಾರ ವಿಚಾರಿಸುತ್ತಿರುವದನ್ನು ಕೇಳಿದವರು ಸೌಲನಿಗೆ ತಿಳಿಸಿದರು; ಅವನು ಅವನನ್ನು ಕರೇ ಕಳುಹಿಸಿದನು.",
			"32": "<sup>32</sup> ದಾವೀದನು ಸೌಲನಿಗೆ - ಆ ಫಿಲಿಷ್ಟಿಯನ ನಿಮಿತ್ತವಾಗಿ ಯಾವನೂ ಎದೆಗೆಡಬಾರದು; ನಿನ್ನ ಸೇವಕನಾದ ನಾನು ಹೋಗಿ ಅವನೊಡನೆ ಯುದ್ಧಮಾಡುವೆನು ಅನ್ನಲು ಸೌಲನು ಅವನಿಗೆ -",
			"33": "<sup>33</sup> ನೀನು ಅವನೊಡನೆ ಕಾದಾಡಲಾರಿ; ನೀನು ಇನ್ನೂ ಹುಡುಗನು. ಅವನಾದರೋ ಚಿಕ್ಕಂದಿನಿಂದಲೇ ಯುದ್ಧವೀರನು ಎಂದು ಹೇಳಿದನು.",
			"34": "<sup>34</sup> ಆಗ ದಾವೀದನು ಅವನಿಗೆ - ನಿನ್ನ ಸೇವಕನಾದ ನಾನು ನನ್ನ ತಂದೆಯ ಕುರಿಗಳನ್ನು ಕಾಯುತ್ತಿದ್ದಾಗ ಸಿಂಹವಾಗಲಿ ಕರಡಿಯಾಗಲಿ ಬಂದು ಹಿಂಡಿನಲ್ಲಿನ ಕುರಿಮರಿಯನ್ನು ಹಿಡಿದುಕೊಂಡು ಹೋಗಲು",
			"35": "<sup>35</sup> ನಾನು ಒಡನೆ ಅದನ್ನು ಬೆನ್ನಟ್ಟಿ ಹೊಡೆದು ಕುರಿಮರಿಯನ್ನು ಕಿತ್ತುಕೊಳ್ಳುತ್ತಿದ್ದೆನು. ಅದು ಹಿಂದಿರುಗಿ ನನ್ನ ಮೇಲೆ ಬಿದ್ದರೆ ಅದನ್ನು ಗದ್ದಹಿಡಿದು ಬಡಿದು ಕೊಂದುಹಾಕುತ್ತಿದ್ದೆನು.",
			"36": "<sup>36</sup> ನಿನ್ನ ಸೇವಕನಿಂದ ಕೊಲ್ಲಲ್ಪಟ್ಟ ಸಿಂಹಕ್ಕೂ ಕರಡಿಗೂ ಆದ ಗತಿಯೇ ಜೀವಸ್ವರೂಪನಾದ ದೇವರ ಸೈನ್ಯವನ್ನು ನಿಂದಿಸುವಂಥ ಈ ಸುನ್ನತಿಯಿಲ್ಲದ ಫಿಲಿಷ್ಟಿಯನಿಗೂ ಆಗಬೇಕು.",
			"37": "<sup>37</sup> ನನ್ನನ್ನು ಅಂಥ ಸಿಂಹದ ಮತ್ತು ಕರಡಿಯ ಉಗುರುಗಳಿಗೆ ತಪ್ಪಿಸಿದ ಯೆಹೋವನು ಈ ಫಿಲಿಷ್ಟಿಯನ ಕೈಗೂ ತಪ್ಪಿಸುವನು ಎನ್ನಲು ಸೌಲನು ದಾವೀದನಿಗೆ - ಹೋಗು; ಯೆಹೋವನು ನಿನ್ನ ಸಂಗಡ ಇರಲಿ ಎಂದು ಹೇಳಿ",
			"38": "<sup>38</sup> ಅವನಿಗೆ ತನ್ನ ಯುದ್ಧವಸ್ತ್ರಗಳನ್ನೂ ಕವಚವನ್ನೂ ತೊಡಿಸಿ ತಲೆಗೊಂದು ತಾಮ್ರದ ಶಿರಸ್ತ್ರಾಣವನ್ನಿಟ್ಟನು;",
			"39": "<sup>39</sup> ದಾವೀದನು ಯುದ್ಧವಸ್ತ್ರದ ಮೇಲೆ ಕತ್ತಿಯನ್ನು ಬಿಗಿದುಕೊಂಡನಂತರ ನಡೆಯಬಹುದೋ ಎಂದು ಪರೀಕ್ಷಿಸಿದ ಮೇಲೆ ಸೌಲನಿಗೆ - ನನಗೆ ಅಭ್ಯಾಸವಿಲ್ಲದರಿಂದ ಇವುಗಳನ್ನು ಧರಿಸಿಕೊಂಡು ನಡೆಯಲಾರೆ ಎಂದು ಹೇಳಿ ಅವುಗಳನ್ನು ತೆಗೆದಿಟ್ಟು",
			"40": "<sup>40</sup> ತನ್ನ ಕೋಲನ್ನು ತೆಗೆದುಕೊಂಡು ಹೊರಟು ಹಳ್ಳದಲ್ಲಿ ಐದು ನುಣುಪುಕಲ್ಲುಗಳನ್ನು ಆರಿಸಿಕೊಂಡು ಅವುಗಳನ್ನು ಕುರುಬರ ಪದ್ಧತಿಯಂತೆ ತನಗಿರುವ ಸೊಂಟಚೀಲದಲ್ಲಿ ಹಾಕಿ ಕೈಯಲ್ಲಿ ಕವಣೆಯನ್ನು ಹಿಡಿದು ಫಿಲಿಷ್ಟಿಯನ ಬಳಿಗೆ ಹೋದನು.",
			"41": "<sup>41</sup> ಇತ್ತ ಫಿಲಿಷ್ಟಿಯನೂ ದಾವೀದನ ಸಮೀಪಕ್ಕೆ ಬಂದನು. ಗುರಾಣಿಹೊರುವವನು ಅವನ ಮುಂದೆ ಇದ್ದನು.",
			"42": "<sup>42</sup> ಫಿಲಿಷ್ಟಿಯನು ಕೆಂಬಣ್ಣದವನೂ ಸುಂದರನೂ ಯೌವನಸ್ಥನೂ ಆದ ದಾವೀದನನ್ನು ನೋಡಿ ತಿರಸ್ಕಾರದಿಂದ ಅವನಿಗೆ -",
			"43": "<sup>43</sup> ನೀನು ಕೋಲುಹಿಡಿದು ನನ್ನ ಬಳಿಗೆ ಬರುವದೇನು? ನಾನು ನಾಯಿಯೋ ಎಂದು ಹೇಳಿ ಅವನನ್ನು ತನ್ನ ದೇವರ ಹೆಸರಿನಲ್ಲಿ ಶಪಿಸಿ -",
			"44": "<sup>44</sup> ಇಲ್ಲಿ ಬಾ; ನಿನ್ನ ಮಾಂಸವನ್ನು ಮೃಗಪಕ್ಷಿಗಳಿಗೆ ಹಂಚಿಕೊಡುತ್ತೇನೆ ಅಂದನು.",
			"45": "<sup>45</sup> ಆಗ ದಾವೀದನು ಅವನಿಗೆ - ನೀನು ಈಟಿ ಕತ್ತಿ ಬರ್ಜಿಗಳೊಡನೆ ನನ್ನ ಬಳಿಗೆ ಬರುತ್ತೀ; ನಾನಾದರೋ ನೀನು ಹೀಯಾಳಿಸಿದಂಥ ಸೇನಾಧೀಶ್ವರನೂ ಇಸ್ರಾಯೇಲ್ಯರ ಯುದ್ಧಭಟರ ದೇವರೂ ಆಗಿರುವ ಯೆಹೋವನ ನಾಮದೊಡನೆ ನಿನ್ನ ಬಳಿಗೆ ಬರುತ್ತೇನೆ.",
			"46": "<sup>46</sup> ಆತನು ಈ ಹೊತ್ತು ನಿನ್ನನ್ನು ನನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸಿಕೊಡುವನು; ನಾನು ನಿನ್ನನ್ನು ಕೊಂದು ನಿನ್ನ ತಲೆಯನ್ನು ಕಡಿದುಹಾಕಿ ಫಿಲಿಷ್ಟಿಯಸೈನ್ಯದ ಶವಗಳನ್ನು ಮೃಗಪಕ್ಷಿಗಳಿಗೆ ಹಂಚಿಕೊಡುವೆನು. ಇದರಿಂದ ಇಸ್ರಾಯೇಲ್ಯರೊಳಗೆ ದೇವರಿರುತ್ತಾನೆಂಬದು ಭೂಲೋಕದವರಿಗೆಲ್ಲಾ ತಿಳಿದು ಬರುವದು;",
			"47": "<sup>47</sup> ಯೆಹೋವನು ಈಟಿ ಕತ್ತಿಗಳಿಲ್ಲದೆ ರಕ್ಷಿಸಬಲ್ಲನೆಂಬದು ಇಲ್ಲಿ ಕೂಡಿರುವವರಿಗೆಲ್ಲಾ ಗೊತ್ತಾಗುವದು; ಯಾಕಂದರೆ ಯುದ್ಧಫಲವು ಯೆಹೋವನ ಕೈಯಲ್ಲಿದೆ; ಆತನು ನಿಮ್ಮನ್ನು ನಮ್ಮ ಕೈಗೆ ಒಪ್ಪಿಸಿಕೊಡುವನು ಅಂದನು.",
			"48": "<sup>48</sup> ಕೂಡಲೆ ಆ ಫಿಲಿಷ್ಟಿಯನು ಹೊರಟು ದಾವೀದನೊಡನೆ ಯುದ್ಧಮಾಡುವದಕ್ಕೆ ಸಮೀಪಿಸಿದಾಗ ದಾವೀದನು ಫಿಲಿಷ್ಟಿಯ ಸೈನ್ಯದ ಕಡೆಗೆ ಓಡಿಹೋಗಿ ಆ ಫಿಲಿಷ್ಟಿಯನನ್ನು ಸಂಧಿಸಿ",
			"49": "<sup>49</sup> ಕೈಯನ್ನು ಚೀಲದಲ್ಲಿ ಹಾಕಿ ಒಂದು ಕಲ್ಲನ್ನು ತೆಗೆದು ಅವನ ಹಣೆಗೆ ಗುರಿಯಿಟ್ಟು ಕವಣೆಯನ್ನು ಬೀಸಿ ಹೊಡೆಯಲು ಕಲ್ಲು ಅವನ ಹಣೆಯೊಳಗೆ ಹೊಕ್ಕಿತು; ಅವನು ಬೋರಲಬಿದ್ದನು.",
			"50": "<sup>50</sup> ಕೂಡಲೆ ಅವನು ಹೋಗಿ ಆ ಫಿಲಿಷ್ಟಿಯನ ಮೇಲೆ ನಿಂತು ಅವನ ಕತ್ತಿಯನ್ನು ಹಿರಿದು ಅದರಿಂದಲೇ ಅವನ ತಲೆಯನ್ನು ಕಡಿದು ಅವನನ್ನು ಸಾಯಿಸಿದನು.",
			"51": "<sup>51</sup> ಈ ಪ್ರಕಾರ ದಾವೀದನು ಕತ್ತಿಯಿಂದಲ್ಲ, ಬರೀ ಒಂದು ಕವಣೆಯ ಕಲ್ಲಿನಿಂದ ಫಿಲಿಷ್ಟಿಯನನ್ನು ಸೋಲಿಸಿ ಕೊಂದುಹಾಕಿದನು. ಫಿಲಿಷ್ಟಿಯರು ತಮ್ಮ ರಣವೀರನು ಸತ್ತುಹೋದದ್ದನ್ನು ಕಂಡು ಓಡಿಹೋದರು.",
			"52": "<sup>52</sup> ಇಸ್ರಾಯೇಲ್ಯರೂ ಯೆಹೂದ್ಯರೂ ಎದ್ದು ಆರ್ಭಟಿಸಿ ಫಿಲಿಷ್ಟಿಯರನ್ನು ತಗ್ಗಿನ ದಾರಿಯವರೆಗೂ ಎಕ್ರೋನಿನ ಬಾಗಲುಗಳವರೆಗೂ ಹಿಂದಟ್ಟಿದರು. ಫಿಲಿಷ್ಟಿಯರ ಹೆಣಗಳು ಶಾರಯಿಮಿನಿಂದ ಗತ್ ಎಕ್ರೋನ್ ಪಟ್ಟಣಗಳವರೆಗೂ ಬಿದ್ದಿದ್ದವು.",
			"53": "<sup>53</sup> ಅನಂತರ ಇಸ್ರಾಯೇಲ್ಯರು ಹಿಂದಿರುಗಿ ಬಂದು ಫಿಲಿಷ್ಟಿಯರ ಪಾಳೆಯವನ್ನು ಸೂರೆಮಾಡಿದರು.",
			"54": "<sup>54</sup> ದಾವೀದನು ಆ ಫಿಲಿಷ್ಟಿಯನ ತಲೆಯನ್ನು ಯೆರೂಸಲೇಮಿಗೆ ಒಯ್ದನು; ಅವನ ಆಯುಧಗಳನ್ನು ತನ್ನ ಗುಡಾರದಲ್ಲಿಟ್ಟುಕೊಂಡನು.",
			"55": "<sup>55</sup> ದಾವೀದನು ಫಿಲಿಷ್ಟಿಯನೊಡನೆ ಯುದ್ಧಮಾಡುವದಕ್ಕೆ ಹೊರಟದ್ದನ್ನು ಸೌಲನು ಕಂಡು ತನ್ನ ಸೇನಾಪತಿಯಾದ ಅಬ್ನೇರನನ್ನು - ಅಬ್ನೇರನೇ, ಈ ಹುಡುಗನು ಯಾರ ಮಗನು ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ ಅವನು ಸೌಲನಿಗೆ - ಅರಸನೇ, ನಿನ್ನ ಜೀವದಾಣೆ ನನಗೆ ಗೊತ್ತಿಲ್ಲ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"56": "<sup>56</sup> ಆಗ ಅರಸನು ಅವನಿಗೆ - ಆ ಪ್ರಾಯಸ್ಥನು ಯಾರ ಮಗನೆಂದು ವಿಚಾರಿಸು ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"57": "<sup>57</sup> ದಾವೀದನು ಫಿಲಿಷ್ಟಿಯನನ್ನು ಕೊಂದು ಅವನ ತಲೆಯನ್ನು ಹಿಡಿದುಕೊಂಡು ಹಿಂದಿರುಗಿ ಬರುವಾಗ ಅಬ್ನೇರನು ಅವನನ್ನು ಕರೆದು ಸೌಲನ ಮುಂದೆ ನಿಲ್ಲಿಸಿದನು.",
			"58": "<sup>58</sup> ಸೌಲನು ಅವನನ್ನು - ಹುಡುಗನೇ, ನೀನು ಯಾರ ಮಗನೆಂದು ಕೇಳಿದ್ದಕ್ಕೆ ಅವನು - ನಾನು ಬೇತ್ಲೆಹೇಮಿನವನೂ ನಿನ್ನ ಸೇವಕನೂ ಆದ ಇಷಯನ ಮಗನು ಎಂದು ಉತ್ತರಕೊಟ್ಟನು."
		},
		"18": {
			"1": "<sup>1</sup> ದಾವೀದನು ಸೌಲನೊಡನೆ ಮಾತಾಡಿ ತೀರಿಸಿದ ಮೇಲೆ ಯೋನಾತಾನನ ಪ್ರಾಣವು ದಾವೀದನ ಪ್ರಾಣದೊಡನೆ ಒಂದಾಯಿತು. ಅವನು ಇವನನ್ನು ತನ್ನ ಪ್ರಾಣದಂತೆಯೇ ಪ್ರೀತಿಸತೊಡಗಿದನು.",
			"2": "<sup>2</sup> ಅಂದಿನಿಂದ ಸೌಲನು ದಾವೀದನನ್ನು ಅವನ ತಂದೆಯ ಮನೆಗೆ ಹೋಗಗೊಡದೆ ತನ್ನ ಬಳಿಯಲ್ಲೇ ಇಟ್ಟುಕೊಂಡನು.",
			"3": "<sup>3</sup> ಯೋನಾತಾನನು ದಾವೀದನನ್ನು ತನ್ನ ಪ್ರಾಣದಂತೆಯೇ ಪ್ರೀತಿಸಿದ್ದರಿಂದ ಅವನೊಡನೆ ಒಡಂಬಡಿಕೆಮಾಡಿಕೊಂಡನು.",
			"4": "<sup>4</sup> ಇದಲ್ಲದೆ ಅವನು ತನ್ನ ಮೈಮೇಲಿದ್ದ ನಿಲುವಂಗಿಯನ್ನೂ ತನ್ನ ಯುದ್ಧವಸ್ತ್ರಗಳನ್ನೂ ಕತ್ತಿ ಬಿಲ್ಲು ನಡುಕಟ್ಟುಗಳನ್ನೂ ದಾವೀದನಿಗೆ ಕೊಟ್ಟನು.",
			"5": "<sup>5</sup> ದಾವೀದನಾದರೋ ಸೌಲನು ಎಲ್ಲಿಗೆ ಕಳುಹಿಸಿದರೂ ಹೋಗಿ ಎಲ್ಲವನ್ನು ವಿವೇಕದಿಂದ ನಡಿಸುತ್ತಿದ್ದನು. ಸೌಲನು ಇವನನ್ನು ಸೇನಾಪತಿಯನ್ನಾಗಿ ನೇಮಿಸಿದನು; ಇದು ಅವನ ಸೇವಕರಿಗೂ ಎಲ್ಲಾ ಜನರಿಗೂ ಸಮ್ಮತವಾಯಿತು.",
			"6": "<sup>6</sup> ದಾವೀದನು ಫಿಲಿಷ್ಟಿಯನನ್ನು ಸಂಹರಿಸಿ ಸರ್ವಜನರೊಡನೆ ಹಿಂದಿರುಗಿ ಬರುವಾಗ ಇಸ್ರಾಯೇಲ್ಯರ ಎಲ್ಲಾ ಪಟ್ಟಣಗಳಿಂದ ಸ್ತ್ರೀಯರು ಹೊರಗೆ ಬಂದು ದಮ್ಮಡಿತಾಳಗಳನ್ನು ಹಿಡಿದು ಸಂತೋಷದಿಂದ ಹಾಡುತ್ತಾ ಕುಣಿಯುತ್ತಾ ಅರಸನಾದ ಸೌಲನನ್ನು ಎದುರುಗೊಂಡರು.",
			"7": "<sup>7</sup> ಅವರು ಪರಸ್ಪರವಾಗಿ - ಸೌಲನು ಸಾವಿರಗಟ್ಟಳೆಯಾಗಿ ಕೊಂದನು; ದಾವೀದನು ಹತ್ತು ಸಾವಿರಗಟ್ಟಳೆಯಾಗಿ ಕೊಂದನು ಎಂದು ಹಾಡಿದರು.",
			"8": "<sup>8</sup> ಈ ಮಾತುಗಳ ದೆಸೆಯಿಂದ ಸೌಲನಿಗೆ ಬಹುವ್ಯಸನವೂ ಕೋಪವೂ ಉಂಟಾಗಿ ಅವನು - ದಾವೀದನು ಹತ್ತು ಸಾವಿರ ಗಟ್ಟಳೆಯಾಗಿ ಕೊಂದನೆಂದೂ ನಾನು ಸಾವಿರಗಟ್ಟಳೆಯಾಗಿ ಕೊಂದೆನೆಂದೂ ಹಾಡುತ್ತಾರಲ್ಲಾ! ರಾಜತ್ವದ ಹೊರತು ಅವನಿಗೆ ಇನ್ನೇನು ಕಡಿಮೆಯಾದ ಹಾಗಾಯಿತು ಅಂದುಕೊಂಡು",
			"9": "<sup>9</sup> ಅಂದಿನಿಂದ ಅವನ ಮೇಲೆ ಕಣ್ಣಿಟ್ಟನು.",
			"10": "<sup>10</sup> ಮರುದಿನ ದೇವರಿಂದ ಕಳುಹಿಸಲ್ಪಟ್ಟ ದುರಾತ್ಮವು ಸೌಲನ ಮೇಲೆ ಬಂದದರಿಂದ ಅವನು ಬುದ್ಧಿಗೆಟ್ಟು ಮನೆಯೊಳಗೆ ಕೂಗುತ್ತಿದ್ದನು. ಅವನ ಕೈಯಲ್ಲಿ ಒಂದು ಈಟಿ ಇತ್ತು. ದಾವೀದನು ಕ್ರಮದ ಪ್ರಕಾರ ಕಿನ್ನರಿಯನ್ನು ಬಾರಿಸುತ್ತಾ ಇದ್ದನು.",
			"11": "<sup>11</sup> ಆಗ ಸೌಲನು - ದಾವೀದನನ್ನು ಗೋಡೆಗೆ ಹತ್ತಿಕೊಳ್ಳುವ ಹಾಗೆ ತಿವಿಯುವೆನೆಂದುಕೊಂಡು ಈಟಿಯನ್ನು ಎಸೆದನು. ದಾವೀದನು ಎರಡು ಸಾರಿ ತಪ್ಪಿಸಿಕೊಂಡನು.",
			"12": "<sup>12</sup> ಯೆಹೋವನು ಸೌಲನನ್ನು ಬಿಟ್ಟು ದಾವೀದನ ಸಂಗಡ ಇದ್ದದರಿಂದ ಸೌಲನು ದಾವೀದನಿಗೆ ಭಯಪಟ್ಟು",
			"13": "<sup>13</sup> ಅವನನ್ನು ತನ್ನ ಸಾನ್ನಿಧ್ಯಸೇವೆಯಿಂದ ತಪ್ಪಿಸಿ ಸೈನ್ಯದಲ್ಲಿ ಸಹಸ್ರಾಧಿಪತಿಯನ್ನಾಗಿ ನೇಮಿಸಿದನು; ಇವನು ದಳಪತಿಯಾಗಿ ಹೋಗುತ್ತಾ ಬರುತ್ತಾ ಇದ್ದನು.",
			"14": "<sup>14</sup> ಯೆಹೋವನು ಇವನ ಸಂಗಡ ಇದ್ದದರಿಂದ ಎಲ್ಲಾ ಕಾರ್ಯಗಳಲ್ಲಿ ಇವನು ವಿವೇಕದಿಂದ ನಡೆಯುತ್ತಿದ್ದನು.",
			"15": "<sup>15</sup> ಸೌಲನು ಇದನ್ನು ನೋಡಿ ಅವನಿಗೆ ಬಹಳವಾಗಿ ಹೆದರಿಕೊಂಡನು.",
			"16": "<sup>16</sup> ಇಸ್ರಾಯೇಲ್ಯರೂ ಯೆಹೂದ್ಯರೂ ತಮ್ಮ ಸಂಗಡ ಹೋಗುತ್ತಾ ಬರುತ್ತಾ ಇದ್ದ ದಾವೀದನನ್ನು ಬಹಳವಾಗಿ ಪ್ರೀತಿಸುತ್ತಿದ್ದರು.",
			"17": "<sup>17</sup> ಒಂದು ದಿವಸ ಸೌಲನು - ಇವನು ನನ್ನ ಕೈಯಿಂದಲ್ಲ, ಫಿಲಿಷ್ಟಿಯರ ಕೈಯಿಂದ ಸಾಯಲಿ ಎಂದು ಆಲೋಚಿಸಿಕೊಂಡು ದಾವೀದನಿಗೆ - ನೀನು ನನ್ನ ವೀರನಾಗಿ ಹೋಗಿ ಯೆಹೋವನ ಯುದ್ಧಗಳನ್ನು ನಡಿಸು; ನಾನು ನನ್ನ ಹಿರೀ ಮಗಳಾದ ಮೇರಬಳನ್ನು ನಿನಗೆ ಮದುವೆ ಮಾಡಿಕೊಡುತ್ತೇನೆ ಅಂದನು.",
			"18": "<sup>18</sup> ಅದಕ್ಕೆ ದಾವೀದನು - ಅರಸನ ಅಳಿಯನಾಗುವದಕ್ಕೆ ನಾನೆಷ್ಟರವನು? ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ನನ್ನ ಕುಲವೂ ಕುಟುಂಬವೂ ಎಷ್ಟರವು ಎಂದನು.",
			"19": "<sup>19</sup> ಸೌಲನು ತನ್ನ ಮಗಳಾದ ಮೇರಬಳನ್ನು ದಾವೀದನಿಗೆ ಕೊಡತಕ್ಕ ಕಾಲ ಬಂದಾಗ ಅವನು ಅವಳನ್ನು ಮೆಹೋಲದ ಅದ್ರೀಯೇಲ್ ಎಂಬವನಿಗೆ ಮದುವೆಮಾಡಿಕೊಟ್ಟನು.",
			"20": "<sup>20</sup> ಸೌಲನ ಮಗಳಾದ ಮೀಕಲಳು ದಾವೀದನನ್ನು ಪ್ರೀತಿಸಿದಳು. ಇದು ಸೌಲನಿಗೆ ತಿಳಿದಾಗ ಅವನಿಗೆ ಸಂತೋಷವಾಯಿತು.",
			"21": "<sup>21</sup> ಸೌಲನು - ದಾವೀದನಿಗೆ ಉರಲಾಗಿರುವಂತೆಯೂ ಅವನು ಫಿಲಿಷ್ಟಿಯರ ಕೈಗೆ ಸಿಕ್ಕಿಬೀಳುವಂತೆಯೂ ಇವಳನ್ನು ಅವನಿಗೆ ಕೊಡುವೆನು ಅಂದುಕೊಂಡು ಅವನಿಗೆ - ನೀನು ನನ್ನ ಅಳಿಯನಾಗುವದಕ್ಕೆ ಈಗ ಎರಡನೆಯ ಸಂದರ್ಭವುಂಟಾಯಿತು ಎಂದು ಹೇಳಿದನು.",
			"22": "<sup>22</sup> ಅವನು ತನ್ನ ಸೇವಕರಿಗೆ - ನೀವು ದಾವೀದನಿಗೆ ಗುಪ್ತವಾಗಿ - ಅರಸನು ನಿನಗೆ ಒಲಿಯುತ್ತಾನೆ; ಅವನ ಎಲ್ಲಾ ಸೇವಕರು ನಿನ್ನನ್ನು ಪ್ರೀತಿಸುತ್ತಾರೆ; ನೀನು ಅರಸನ ಅಳಿಯನಾಗು ಎಂದು ಪುಸಲಾಯಿಸಿರಿ ಅಂದನು.",
			"23": "<sup>23</sup> ಆ ಸೇವಕರು ದಾವೀದನಿಗೆ ಹಾಗೆಯೇ ಹೇಳಿದಾಗ ಅವನು - ಅರಸನ ಅಳಿಯನಾಗುವದು ಸಾಧಾರಣವಾದ ಸಂಗತಿಯೆಂದು ನೆನಸುತ್ತೀರೋ? ನಾನು ದರಿದ್ರನೂ ಅಲ್ಪನೂ ಆಗಿರುತ್ತೇನಲ್ಲಾ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"24": "<sup>24</sup> ದಾವೀದನು ಅಂದದ್ದನ್ನು ಸೇವಕರು ಸೌಲನಿಗೆ ತಿಳಿಸಿದರು.",
			"25": "<sup>25</sup> ಆಗ ಅವನು ಅವರಿಗೆ - ನೀವು ಹೋಗಿ ದಾವೀದನಿಗೆ - ಅರಸನು ತೆರವನ್ನು ಅಪೇಕ್ಷಿಸುವದಿಲ್ಲ; ತನ್ನ ಶತ್ರುಗಳಾದ ಫಿಲಿಷ್ಟಿಯರಿಗೆ ಮುಯ್ಯಿತೀರಿಸಿ ಅವರಲ್ಲಿನ ನೂರು ಮಂದಿಯ ಮುಂದೊಗಲನ್ನು ತಂದುಕೊಡಬೇಕು ಅನ್ನುತ್ತಾನೆಂದು ಹೇಳಿರಿ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು. ದಾವೀದನನ್ನು ಫಿಲಿಷ್ಟಿಯರಿಂದ ಕೊಲ್ಲಿಸಬೇಕೆಂಬದೇ ಇದಕ್ಕೆ ಕಾರಣ.",
			"26": "<sup>26</sup> ಸೇವಕರು ದಾವೀದನಿಗೆ ಈ ಮಾತುಗಳನ್ನು ತಿಳಿಸಿದಾಗ ಅವನು ಅರಸನ ಅಳಿಯನಾಗುವದಕ್ಕೆ ಒಪ್ಪಿದನು. ದಾವೀದನು ನೇಮಕವಾದ ದಿನಗಳು ಮುಗಿಯುವ ಮೊದಲೇ",
			"27": "<sup>27</sup> ತನ್ನ ಸೈನಿಕರೊಡನೆ ಹೊರಟು ಫಿಲಿಷ್ಟಿಯರ ದೇಶಕ್ಕೆ ಹೋಗಿ ಅಲ್ಲಿನ ಇನ್ನೂರು ಜನರನ್ನು ಕೊಂದು ಮುಂದೊಗಲುಗಳನ್ನು ತಂದು ಅರಸನ ಅಳಿಯನಾಗುವದಕ್ಕೋಸ್ಕರ ಅವುಗಳನ್ನು ಪೂರ್ಣವಾಗಿ ಅವನಿಗೆ ಒಪ್ಪಿಸಿದನು. ಆಗ ಸೌಲನು ತನ್ನ ಮಗಳಾದ ಮೀಕಲಳನ್ನು ಅವನಿಗೆ ಮದುವೆಮಾಡಿಕೊಟ್ಟನು;",
			"28": "<sup>28</sup> ಆಕೆಯು ಅವನನ್ನು ಬಹಳವಾಗಿ ಪ್ರೀತಿಸುವವಳಾದಳು. ಯೆಹೋವನು ದಾವೀದನ ಸಂಗಡ ಇದ್ದಾನೆಂದು ಸೌಲನು ತಿಳಿದು",
			"29": "<sup>29</sup> ಅವನಿಗೆ ಮತ್ತಷ್ಟು ಹೆದರುವವನಾಗಿ ಅವನ ನಿತ್ಯವೈರಿಯಾದನು.",
			"30": "<sup>30</sup> ಫಿಲಿಷ್ಟಿಯಪ್ರಭುಗಳು ಯುದ್ಧಕ್ಕೆ ಬಂದಾಗೆಲ್ಲಾ ಸೌಲನ ಸೇನಾಪತಿಗಳಲ್ಲಿ ದಾವೀದನೇ ಅಧಿಕಜಯಶೀಲನಾಗಿ ಬರುತ್ತಿದ್ದದರಿಂದ ಅವನ ಹೆಸರು ಬಹು ಪ್ರಸಿದ್ಧವಾಯಿತು."
		},
		"19": {
			"1": "<sup>1</sup> ಸೌಲನು ದಾವೀದನನ್ನು ಕೊಲ್ಲಬೇಕೆಂದು ತನ್ನ ಮಗನಾದ ಯೋನಾತಾನನಿಗೂ ಎಲ್ಲಾ ಪರಿವಾರದವರಿಗೂ ಆಜ್ಞಾಪಿಸಿದನು. ಆದರೆ ಯೋನಾತಾನನು ದಾವೀದನನ್ನು ಬಹಳವಾಗಿ ಪ್ರೀತಿಸುತ್ತಿದ್ದನು.",
			"2": "<sup>2</sup> ಆದದರಿಂದ ಅವನು ದಾವೀದನಿಗೆ - ನನ್ನ ತಂದೆಯಾದ ಸೌಲನು ನಿನ್ನನ್ನು ಕೊಲ್ಲಬೇಕೆಂದಿರುತ್ತಾನೆ. ನೀನು ಜಾಗರೂಕತೆಯಿಂದಿರು; ನಾಳೆ ಬೆಳಿಗ್ಗೆ ಒಂದು ಗುಪ್ತಸ್ಥಳದಲ್ಲಿ ಅಡಗಿಕೊಂಡಿರು.",
			"3": "<sup>3</sup> ನೀನು ಅಡಗಿಕೊಂಡಿರುವ ಹೊಲಕ್ಕೆ ನಾನೂ ಬಂದು ನನ್ನ ತಂದೆಯ ಬಳಿಯಲ್ಲಿ ನಿಂತು ನಿನ್ನ ವಿಷಯ ಅವನೊಡನೆ ಮಾತಾಡಿ ಗೊತ್ತಾಗುವದನ್ನು ನಿನಗೆ ತಿಳಿಸುವೆನು ಎಂದು ಹೇಳಿದನು.",
			"4": "<sup>4</sup> ತರುವಾಯ ಅವನು ತನ್ನ ತಂದೆಯ ಮುಂದೆ ದಾವೀದನನ್ನು ಹೊಗಳಿ - ಒಡೆಯನು ತನ್ನ ಸೇವಕನಾದ ದಾವೀದನಿಗೆ ಅನ್ಯಾಯಮಾಡದಿರಲಿ; ಅವನು ನಿನಗೆ ದ್ರೋಹಮಾಡಲಿಲ್ಲ; ಅವನು ಮಾಡಿದ್ದೆಲ್ಲವೂ ನಿನ್ನ ಹಿತಕ್ಕಾಗಿಯೇ.",
			"5": "<sup>5</sup> ತನ್ನ ಜೀವವನ್ನು ಕೈಯಲ್ಲಿ ಹಿಡಿದುಕೊಂಡು ಹೋಗಿ ಆ ಫಿಲಿಷ್ಟಿಯನನ್ನು ಕೊಂದನು; ಯೆಹೋವನು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಮಹಾಜಯವನ್ನುಂಟುಮಾಡಿದನು. ನೀನು ಅದನ್ನು ನೋಡಿ ಸಂತೋಷಪಟ್ಟಿಯಲ್ಲಾ; ದಾವೀದನನ್ನು ನಿಷ್ಕಾರಣವಾಗಿ ಕೊಂದು ನಿರ್ದೋಷಿಯ ರಕ್ತವನ್ನು ಸುರಿಸಿದ ಅಪರಾಧಕ್ಕೆ ನೀನೇಕೆ ಗುರಿಯಾಗುತ್ತೀ ಅಂದನು.",
			"6": "<sup>6</sup> ಸೌಲನು ಯೋನಾತಾನನ ಮಾತುಗಳನ್ನು ಲಾಲಿಸಿ - ಯೆಹೋವನಾಣೆ, ಅವನನ್ನು ಕೊಲ್ಲುವದಿಲ್ಲ ಎಂದು ಪ್ರಮಾಣಮಾಡಿದನು.",
			"7": "<sup>7</sup> ಆಗ ಯೋನಾತಾನನು ದಾವೀದನನ್ನು ಕರೆದು ಅವನಿಗೆ ಈ ಮಾತುಗಳನ್ನು ತಿಳಿಸಿ ಸೌಲನ ಬಳಿಗೆ ಕರತಂದನು. ದಾವೀದನು ಮುಂಚಿನಂತೆ ಸೌಲನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಇರುವವನಾದನು.",
			"8": "<sup>8</sup> ಫಿಲಿಷ್ಟಿಯರು ತಿರಿಗಿ ಯುದ್ಧಕ್ಕೆ ಬರುವಲ್ಲಿ ದಾವೀದನು ಹೋಗಿ ಅವರೊಡನೆ ಯುದ್ಧಮಾಡಿ ಅವರನ್ನು ಪೂರ್ಣವಾಗಿ ಸೋಲಿಸಿ ಓಡಿಸಿಬಿಟ್ಟನು.",
			"9": "<sup>9</sup> ಒಂದು ದಿವಸ ಸೌಲನು ಕೈಯಲ್ಲಿ ಈಟಿಯನ್ನು ಹಿಡಿದುಕೊಂಡು ತನ್ನ ಮನೆಯಲ್ಲಿ ಕೂತುಕೊಂಡಿದ್ದಾಗ ಯೆಹೋವನಿಂದ ಕಳುಹಿಸಲ್ಪಟ್ಟ ದುರಾತ್ಮವು ಅವನ ಮೇಲೆ ಬಂದಿತು.",
			"10": "<sup>10</sup> ಅವನು ಒಡನೆ ಕಿನ್ನರಿಯನ್ನು ಬಾರಿಸುತ್ತಿದ್ದ ದಾವೀದನನ್ನು ಗೋಡೆಗೆ ಹತ್ತಿಕೊಳ್ಳುವಂತೆ ತಿವಿಯಬೇಕೆಂದು ತನ್ನ ಈಟಿಯನ್ನು ಎಸೆದನು. ಆದರೆ ದಾವೀದನು ಫಕ್ಕನೆ ಸೌಲನೆದುರಿನಿಂದ ಸರಿದುಕೊಂಡ ಕಾರಣ ಅದು ಗೋಡೆಯೊಳಗೆ ನಾಟಿತು. ದಾವೀದನು ಆ ರಾತ್ರಿಯಲ್ಲೇ ತಪ್ಪಿಸಿಕೊಂಡು ಓಡಿಹೋದನು.",
			"11": "<sup>11</sup> ಸೌಲನು ಕೂಡಲೆ ದೂತರನ್ನು ಕರಿಸಿ - ದಾವೀದನ ಮನೆಯ ಸುತ್ತಲೂ ಹೊಂಚುಹಾಕಿ ಅವನನ್ನು ಬೆಳಗಾಗುವಷ್ಟರಲ್ಲಿ ಕೊಂದುಹಾಕಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿ ಕಳುಹಿಸಿದನು. ಮೀಕಲಳು ತನ್ನ ಗಂಡನಾದ ದಾವೀದನಿಗೆ - ನೀನು ಈ ರಾತ್ರಿಯೇ ತಪ್ಪಿಸಿಕೊಳ್ಳದಿದ್ದರೆ ನಾಳೆ ಬೆಳಿಗ್ಗೆ ಹತನಾಗುವಿ ಎಂದು ಹೇಳಿ",
			"12": "<sup>12</sup> ಅವನನ್ನು ಕಿಟಕಿಯಿಂದ ಕೆಳಕ್ಕಿಳಿಸಲು ಅವನು ಓಡಿಹೋಗಿ ತಪ್ಪಿಸಿಕೊಂಡನು.",
			"13": "<sup>13</sup> ಅನಂತರ ಆಕೆಯು ಪೂಜೆಯ ಬೊಂಬೆಯನ್ನು ತಂದು ಹಾಸಿಗೆಯ ಮೇಲೆ ಮಲಗಿಸಿ ಅದರ ಮುಖದ ಮೇಲೆ ಮೇಕೆಕೂದಲಿನಿಂದ ಹೆಣೆದ ಜಾಲರಿಯನ್ನು ಹಾಕಿ ಕಂಬಳಿಯನ್ನು ಹೊದಿಸಿದಳು.",
			"14": "<sup>14</sup> ಸೌಲನ ದೂತರು ದಾವೀದನನ್ನು ಹಿಡಿಯುವದಕ್ಕೆ ಬಂದಾಗ ಅವಳು - ಅವನಿಗೆ ಸ್ವಸ್ಥವಿಲ್ಲವೆಂದು ಹೇಳಿ ಕಳುಹಿಸಿದಳು.",
			"15": "<sup>15</sup> ಸೌಲನು ತಿರಿಗಿ ತನ್ನ ಆಳುಗಳಿಗೆ - ನೀವು ಹೋಗಿ ನೋಡಿ ದಾವೀದನನ್ನು ಹಾಸಿಗೆಯೊಡನೆ ತನ್ನ ಬಳಿಗೆ ತೆಗೆದುಕೊಂಡು ಬನ್ನಿರಿ, ಕೊಂದು ಹಾಕುತ್ತೇನೆ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"16": "<sup>16</sup> ಅವರು ಹೋಗಿ ನೋಡಲು ಮುಖದ ಮೇಲೆ ಮೇಕೆಕೂದಲಿನಿಂದ ಹೆಣೆದ ಜಾಲರಿಯುಳ್ಳದ್ದಾಗಿ ಹಾಸಿಗೆಯ ಮೇಲಿರುವದು ಒಂದು ಬೊಂಬೆಯೆಂದು ತಿಳಿದುಬಂದಾಗ",
			"17": "<sup>17</sup> ಸೌಲನು ಮೀಕಲಳಿಗೆ - ನೀನು ನನ್ನನ್ನು ವಂಚಿಸಿದ್ದೇಕೆ? ನನ್ನ ಶತ್ರುವನ್ನು ಕಳುಹಿಸಿ ಅವನು ತಪ್ಪಿಸಿಕೊಳ್ಳುವಂತೆ ಮಾಡಿದಿಯಲ್ಲಾ ಎನ್ನಲು ಆಕೆಯು ಅವನಿಗೆ - ನನ್ನನ್ನು ಕಳುಹಿಸದಿದ್ದರೆ ನಿನ್ನನ್ನು ಕೊಂದುಬಿಡುತ್ತೇನೆಂದು ಅವನು ನನಗೆ ಹೇಳಿದನು ಅಂದಳು.",
			"18": "<sup>18</sup> ದಾವೀದನು ತಪ್ಪಿಸಿಕೊಂಡು ರಾಮದಲ್ಲಿದ್ದ ಸಮುವೇಲನ ಬಳಿಗೆ ಬಂದು ಸೌಲನು ತನಗೆ ಮಾಡಿದ್ದೆಲ್ಲವನ್ನೂ ತಿಳಿಸಿದನು. ಅವರಿಬ್ಬರೂ ಹೋಗಿ ಅಲ್ಲಿನ ಮಠದಲ್ಲಿ ವಾಸವಾಗಿದ್ದರು.",
			"19": "<sup>19</sup> ದಾವೀದನು ರಾಮದ ಮಠದಲ್ಲಿರುವದು ಸೌಲನಿಗೆ ಗೊತ್ತಾಗಲು",
			"20": "<sup>20</sup> ಅವನು ಅವನನ್ನು ಹಿಡಿದು ತರುವದಕ್ಕಾಗಿ ಆಳುಗಳನ್ನು ಕಳುಹಿಸಿದನು. ಅವರು ಹೋಗಿ ಅಲ್ಲಿನ ಪ್ರವಾದಿ ಸಮೂಹವು ಪರವಶವಾಗಿ ಮಾತಾಡುವದನ್ನೂ ಸಮುವೇಲನು ಅವರ ನಾಯಕನಾಗಿ ನಿಂತಿರುವದನ್ನೂ ಕಂಡಾಗ ದೇವರ ಆತ್ಮವು ಅವರ ಮೇಲೆಯೂ ಬಂದಿತು; ಅವರೂ ಪರವಶರಾಗಿ ಮಾತಾಡತೊಡಗಿದರು.",
			"21": "<sup>21</sup> ಸೌಲನಿಗೆ ಈ ವರ್ತಮಾನವು ಮುಟ್ಟಿದಾಗ ಅವನು ಬೇರೆ ಆಳುಗಳನ್ನು ಕಳುಹಿಸಿದನು; ಇವರೂ ಪರವಶರಾದರು. ಮೂರನೆಯ ಸಾರಿ ಇನ್ನು ಕೆಲವು ಮಂದಿ ಆಳುಗಳನ್ನು ಕಳುಹಿಸಿದನು. ಇವರೂ ಪರವಶರಾದರು.",
			"22": "<sup>22</sup> ಆಗ ಸೌಲನು ತಾನೇ ರಾಮಕ್ಕೆ ಹೋಗಬೇಕೆಂದು ಹೊರಟು ಸೇಕೂವಿನಲ್ಲಿರುವ ದೊಡ್ಡ ಬಾವಿಯ ಬಳಿಗೆ ಬಂದು ಸಮುವೇಲ ದಾವೀದರು ಎಲ್ಲಿರುತ್ತಾರೆಂದು ವಿಚಾರಿಸಲು ಜನರು - ರಾಮದ ಮಠದಲ್ಲಿದ್ದಾರೆಂದು ಉತ್ತರಕೊಟ್ಟರು.",
			"23": "<sup>23</sup> ಅವನು ಅಲ್ಲಿಂದ ರಾಮದ ಮಠಕ್ಕೆ ಹೋಗುವಾಗ ದೇವರ ಆತ್ಮವು ಅವನ ಮೇಲೆಯೂ ಬಂದಿತು. ಅವನು ಆ ಊರನ್ನು ಮುಟ್ಟುವವರೆಗೆ ಪರವಶನಾಗಿ ಮಾತಾಡುತ್ತಾ ಹೋದನು.",
			"24": "<sup>24</sup> ಅಲ್ಲಿಗೆ ಸೇರಿದ ಮೇಲೆ ಅವನೂ ತನ್ನ ಬಟ್ಟೆಗಳನ್ನು ತೆಗೆದು ಹಾಕಿ ಸಮುವೇಲನ ಮುಂದೆ ಪರವಶನಾಗಿ ಮಾತಾಡುತ್ತಾ ಆ ದಿನ ಹಗಲಿರುಳು ಬೆತ್ತಲೆಯಾಗಿ ಬಿದ್ದುಕೊಂಡಿದ್ದನು. ಆದದರಿಂದ - ಸೌಲನೂ ಪ್ರವಾದಿಗಳಲ್ಲಿದ್ದಾನೋ ಎಂಬ ಗಾದೆಯುಂಟಾಯಿತು."
		},
		"20": {
			"1": "<sup>1</sup> ದಾವೀದನು ರಾಮದ ಮಠದಿಂದ ತಪ್ಪಿಸಿಕೊಂಡು ಯೋನಾತಾನನ ಬಳಿಗೆ ಬಂದು ಅವನಿಗೆ - ನಿನ್ನ ತಂದೆಯು ನನ್ನ ಪ್ರಾಣವನ್ನು ತೆಗೆಯಬೇಕೆಂದಿರುತ್ತಾನಲ್ಲಾ; ನಾನೇನು ಮಾಡಿದೆನು? ನನ್ನ ಅಪರಾಧವೇನು? ನಾನು ಅವನಿಗೆ ಮಾಡಿದ ದ್ರೋಹವು ಯಾವದು ಅನ್ನಲು ಯೋನಾತಾನನು ಅವನಿಗೆ -",
			"2": "<sup>2</sup> ಇದು ಆಗಲೇಬಾರದು; ನಿನಗೆ ಮರಣವಾಗಕೂಡದು, ನನ್ನ ತಂದೆಯು ಅಲ್ಪಕಾರ್ಯವನ್ನಾಗಲಿ ಅಧಿಕಕಾರ್ಯವನ್ನಾಗಲಿ ನನಗೆ ತಿಳಿಸದೆ ಮಾಡುವದಿಲ್ಲ; ಈ ಕಾರ್ಯವನ್ನು ನನಗೆ ಹೇಗೆ ಮರೆಮಾಡಾನು? ಆಗುವದಿಲ್ಲ ಎಂದು ಹೇಳಿದನು.",
			"3": "<sup>3</sup> ಅದಕ್ಕೆ ದಾವೀದನು - ನಿನ್ನ ಕಣ್ಣು ಮುಂದೆ ನನಗೆ ದಯೆ ದೊರಕುತ್ತದೆಂಬದನ್ನು ನಿನ್ನ ತಂದೆಯು ಬಲ್ಲನು. ಆದದರಿಂದ ಇದನ್ನು ಯೋನಾತಾನನಿಗೆ ತಿಳಿಸಿದರೆ ಅವನಿಗೆ ದುಃಖವಾಗುವದೆಂದು ಮರೆಮಾಡುತ್ತಾನೆ. ನಿನ್ನ ಜೀವದಾಣೆ, ಯೆಹೋವನಾಣೆ, ನನಗೂ ಮರಣಕ್ಕೂ ಒಂದು ಗೇಣು ಮಾತ್ರ ಅಂತರವಿದೆ ಎಂದು ಖಂಡಿತವಾಗಿ ಹೇಳಿದನು.",
			"4": "<sup>4</sup> ಆಗ ಯೋನಾತಾನನು ದಾವೀದನಿಗೆ - ನಾನು ನಿನಗೋಸ್ಕರ ಮಾಡಬೇಕಾದದ್ದನ್ನು ಹೇಳು ಮಾಡುತ್ತೇನೆ ಅಂದನು.",
			"5": "<sup>5</sup> ಅದಕ್ಕೆ ದಾವೀದನು - ನಾಳೆ ಶುದ್ಧ ಪಾಡ್ಯವು; ನಾನು ಅರಸನ ಪಂಕ್ತಿಯಲ್ಲಿ ಕೂತು ಊಟಮಾಡಬೇಕಾಗಿರುವದು. ನೀನು ಅಪ್ಪಣೆಕೊಟ್ಟರೆ ನಾನು ಈಗಲೇ ಹೋಗಿ ಮೂರನೆಯ ದಿನ ಸಾಯಂಕಾಲದವರೆಗೆ ಹೊಲದಲ್ಲಿ ಅಡಗಿಕೊಂಡಿರುವೆನು.",
			"6": "<sup>6</sup> ನಿನ್ನ ತಂದೆಯು ನನ್ನನ್ನು ಕುರಿತು ವಿಚಾರಮಾಡಿದರೆ ನೀನು - ದಾವೀದನು ತನ್ನ ಊರಾದ ಬೇತ್ಲೆಹೇಮಿಗೆ ಹೋಗಿ ಬರುವದಕ್ಕೆ ಅಪ್ಪಣೆಯಾಗಬೇಕೆಂದು ನನ್ನನ್ನು ಬಹಳವಾಗಿ ಬೇಡಿಕೊಂಡನು; ಅವನ ಗೋತ್ರದವರು ಅಲ್ಲಿ ವಾರ್ಷಿಕಯಜ್ಞ ಮಾಡುತ್ತಾರಂತೆ ಎಂದು ಹೇಳು.",
			"7": "<sup>7</sup> ಅವನು ಆಗಲಿ ಎಂದರೆ ನಿನ್ನ ಸೇವಕನಾದ ನಾನು ಸುರಕ್ಷಿತನಾಗಿರುವೆನು; ಸಿಟ್ಟುಮಾಡಿದರೆ ಅವನಿಂದ ನನಗೆ ಕೇಡು ಸಿದ್ಧವಾಗಿದೆಂದು ತಿಳಿದುಕೋ.",
			"8": "<sup>8</sup> ಸೇವಕನ ಮೇಲೆ ದಯವಿರಲಿ. ನೀನು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನನ್ನೊಡನೆ ಒಡಂಬಡಿಕೆ ಮಾಡಿಕೊಂಡಿಯಲ್ಲಾ; ನಾನು ಅಪರಾಧಿಯಾಗಿದ್ದರೆ ನನ್ನನ್ನು ನಿನ್ನ ತಂದೆಗೆ ಒಪ್ಪಿಸುವದೇಕೆ? ನೀನೇ ಕೊಂದುಹಾಕು ಎಂದು ಹೇಳಿದನು.",
			"9": "<sup>9</sup> ಯೋನಾತಾನನು ಅವನಿಗೆ - ಇದು ಆಗಲೇಬಾರದು; ನಿನಗೆ ನನ್ನ ತಂದೆಯಿಂದ ಕೇಡು ಸಿದ್ಧವಾಗಿದ್ದರೆ ಅದನ್ನು ನಿನಗೆ ತಿಳಿಸದಿರುವೆನೋ ಅಂದನು.",
			"10": "<sup>10</sup> ದಾವೀದನು - ನಿನ್ನ ತಂದೆಯು ಬಿರುನುಡಿಯಿಂದ ಉತ್ತರಕೊಟ್ಟರೆ ಅದನ್ನು ಯಾರ ಮುಖಾಂತರವಾಗಿ ತಿಳಿಸುವಿ ಎಂದು ಅವನನ್ನು ಕೇಳಲು",
			"11": "<sup>11</sup> ಯೋನಾತಾನನು ಅವನಿಗೆ - ನಾವು ಅಡವಿಗೆ ಹೋಗೋಣ ಬಾ ಎಂದು ಹೇಳಿದನು.",
			"12": "<sup>12</sup> ಅವರಿಬ್ಬರೂ ಅಡವಿಗೆ ಹೋದ ಮೇಲೆ ಯೋನಾತಾನನು ದಾವೀದನಿಗೆ - ಇಸ್ರಾಯೇಲ್‍ದೇವರಾದ ಯೆಹೋವನೇ ಸಾಕ್ಷಿ; ನಾನು ನಾಳೆ ಇಲ್ಲವೆ ನಾಡದು ನಿನ್ನ ವಿಷಯದಲ್ಲಿ ನನ್ನ ತಂದೆಯೊಡನೆ ಮಾತಾಡಿ ನಿನ್ನ ಮೇಲೆ ಅವನಿಗೆ ದಯವುಂಟೆಂದು ಗೊತ್ತಾದರೆ ಕೂಡಲೆ ನಿನಗೆ ತಿಳಿಯಪಡಿಸುವೆನು.",
			"13": "<sup>13</sup> ನನ್ನ ತಂದೆಯು ನಿನಗೆ ಕೇಡುಮಾಡ ಮನಸ್ಸುಳ್ಳವನಾಗಿದ್ದಾನೆಂದು ಗೊತ್ತಾದರೆ ಅದನ್ನೂ ನಿನಗೆ ತಿಳಿಸಿ ನೀನು ತಪ್ಪಿಸಿಕೊಂಡು ಸುರಕ್ಷಿತನಾಗುವ ಹಾಗೆ ನಿನ್ನನ್ನು ಕಳುಹಿಸಿಬಿಡುವೆನು. ಹಾಗೆ ಮಾಡದಿದ್ದರೆ ಯೆಹೋವನು ನನಗೆ ಬೇಕಾದದ್ದನ್ನು ಮಾಡಲಿ. ಆತನು ನನ್ನ ತಂದೆಯ ಸಂಗಡ ಇದ್ದ ಹಾಗೆ ನಿನ್ನ ಸಂಗಡಲೂ ಇರಲಿ.",
			"14": "<sup>14</sup> ನೀನು ಯೆಹೋವನನ್ನು ನೆನಸಿ ನನ್ನ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ನನಗೆ ಕೃಪೆತೋರಿಸು;",
			"15": "<sup>15</sup> ನಾನು ಸತ್ತನಂತರ ನನ್ನ ಮನೆಯ ಮೇಲೆಯೂ ಶಾಶ್ವತವಾಗಿ ನಿನ್ನ ದಯವಿರಲಿ. ಯೆಹೋವನು ನಿನ್ನ ಎಲ್ಲಾ ಶತ್ರುಗಳನ್ನು ಭೂಮಿಯಿಂದ ತೆಗೆದುಹಾಕಿದ ಮೇಲಾದರೂ ಅದು ನನ್ನ ಮನೆಯಿಂದ ಅಗಲದಿರಲಿ.",
			"16": "<sup>16</sup> ಅಗಲಿದರೆ ಯೆಹೋವನೇ ಶತ್ರುಗಳ ಕೈಯಿಂದ ದಾವೀದನಿಗೆ ಮುಯ್ಯಿತೀರಿಸಲಿ ಎಂದು ಹೇಳಿ ಅವನ ಮನೆಯವರೊಡನೆ ಒಡಂಬಡಿಕೆ ಮಾಡಿಕೊಂಡು ತಾನು ದಾವೀದನನ್ನು ಸ್ವಂತ ಪ್ರಾಣದಂತೆ ಪ್ರೀತಿಸುತ್ತಿದ್ದದರಿಂದ",
			"17": "<sup>17</sup> ಆ ಪ್ರೀತಿಸಾಕ್ಷಿಯಾಗಿ ಅವನಿಂದ ಪ್ರಮಾಣ ಮಾಡಿಸಿದನು.",
			"18": "<sup>18</sup> ಯೋನಾತಾನನು ದಾವೀದನಿಗೆ - ನಾಳೆ ಶುದ್ಧಪಾಡ್ಯವು; ನಿನ್ನ ಸ್ಥಳವು ಬರಿದಾಗಿರುವದನ್ನು ಅರಸನು ಕಂಡು ನಿನ್ನ ವಿಷಯವಾಗಿ ವಿಚಾರಿಸುವನು.",
			"19": "<sup>19</sup> ನೀನು ಮೂರನೆಯ ದಿನದಲ್ಲಿ ಮೊದಲು ಅಡಗಿಕೊಂಡಿದ್ದ ಸ್ಥಳಕ್ಕೆ ಬೇಗ ಬಂದು ಅಲ್ಲಿನ ಕಲ್ಲುಕುಪ್ಪೆಯ ಬಳಿಯಲ್ಲಿ ಕೂತುಕೊಂಡಿರು.",
			"20": "<sup>20</sup> ನಾನು ಗುರಿಯಿಟ್ಟವನೋ ಎಂಬಂತೆ ಅದರ ಕಡೆಗೆ ಮೂರು ಬಾಣಗಳನ್ನು ಎಸೆದು ಕೂಡಲೆ",
			"21": "<sup>21</sup> ಅವುಗಳನ್ನು ತರುವದಕ್ಕಾಗಿ ನನ್ನ ಆಳನ್ನು ಕಳುಹಿಸುವೆನು. ನಾನು ಅವನಿಗೆ - ಬಾಣಗಳು ಈಚೆ ಬಿದ್ದಿರುತ್ತವೆ; ಅವುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಬಾ ಎಂದು ಹೇಳಿದರೆ ನೀನು ಬಾ; ಯೆಹೋವನಾಣೆ, ಯಾವ ಕೇಡೂ ಸಂಭವಿಸದೆ ಸುರಕ್ಷಿತನಾಗಿರುವಿ.",
			"22": "<sup>22</sup> ಆದರೆ - ಬಾಣಗಳು ಆಚೆ ಬಿದ್ದಿರುತ್ತವೆ ಎಂದು ಹೇಳಿದರೆ ಹೊರಟುಹೋಗು; ನೀನು ಹೋಗುವದೇ ಯೆಹೋವನ ಚಿತ್ತವಾಗಿದೆ.",
			"23": "<sup>23</sup> ನಮ್ಮಿಬ್ಬರ ಒಡಂಬಡಿಕೆಗೆ ಯೆಹೋವನೇ ನಿತ್ಯ ಸಾಕ್ಷಿಯಾಗಿರುವನು ಅಂದನು.",
			"24": "<sup>24</sup> ದಾವೀದನು ಅಡವಿಯಲ್ಲಿ ಅಡಗಿಕೊಂಡನು.",
			"25": "<sup>25</sup> ಅರಸನು ಶುದ್ಧಪಾಡ್ಯದ ದಿವಸ ಪದ್ಧತಿಯ ಪ್ರಕಾರ ಗೋಡೆಯ ಬಳಿಯಲ್ಲಿರುವ ತನ್ನ ಆಸನದ ಮೇಲೆ ಭೋಜನಕ್ಕೆ ಕೂತುಕೊಂಡನು. ಅವನ ಎದುರಾಗಿ ಯೋನಾತಾನನೂ ಅವನ ಪಾರ್ಶ್ವದಲ್ಲಿ ಅಬ್ನೇರನೂ ಕೂತುಕೊಂಡರು. ಸೌಲನು ದಾವೀದನ ಸ್ಥಳವು ಬರಿದಾಗಿರುವದನ್ನು ಕಂಡರೂ",
			"26": "<sup>26</sup> ಆ ದಿವಸ ಅವನ ವಿಷಯ ಏನೂ ಹೇಳಲಿಲ್ಲ. ಅವನಿಗೆ ಏನಾದರೂ ಸಂಭವಿಸಿರಬೇಕು; ಅವನು ಹೊಲೆಯಾಗಿದ್ದಾನು, ತನ್ನನ್ನು ಇನ್ನೂ ಶುದ್ಧಪಡಿಸಿಕೊಳ್ಳದೆ ಇರಬಹುದು ಅಂದುಕೊಂಡನು.",
			"27": "<sup>27</sup> ಮರುದಿವಸದಲ್ಲಿಯೂ ಅಂದರೆ ತಿಂಗಳಿನ ಎರಡನೆಯ ದಿವಸದಲ್ಲಿಯೂ ದಾವೀದನ ಸ್ಥಾನವು ಬರಿದಾಗಿದ್ದದರಿಂದ ಸೌಲನು - ಇಷಯನ ಮಗನು ನಿನ್ನೆಯೂ ಈ ಹೊತ್ತೂ ಭೋಜನಕ್ಕೆ ಯಾಕೆ ಬರಲಿಲ್ಲ ಎಂದು ತನ್ನ ಮಗನಾದ ಯೋನಾತಾನನನ್ನು ಕೇಳಿದನು.",
			"28": "<sup>28</sup> ಅದಕ್ಕೆ ಯೋನಾತಾನನು - ದಾವೀದನು ಬೇತ್ಲೆಹೇಮಿನಲ್ಲಿ ನಮ್ಮ ಗೋತ್ರದವರು ಯಜ್ಞಮಾಡುತ್ತಾರೆ;",
			"29": "<sup>29</sup> ನನ್ನ ಅಣ್ಣನು ಅದಕ್ಕಾಗಿ ನನ್ನನ್ನು ಕರೆದಿದ್ದಾನೆ; ದಯವಿಟ್ಟು ನನಗೆ ಅಪ್ಪಣೆಕೊಡು. ನಾನು ಹೋಗಿ ನನ್ನ ಅಣ್ಣಂದಿರನ್ನು ನೋಡಿಬರುತ್ತೇನೆ ಎಂದು ನನ್ನನ್ನು ಬಹಳವಾಗಿ ಬೇಡಿಕೊಂಡು ಹೋದನು. ಆದದರಿಂದ ಅವನು ಅರಸನ ಪಂಕ್ತಿಗೆ ಬರಲಿಲ್ಲ ಎಂದು ಉತ್ತರ ಕೊಟ್ಟನು.",
			"30": "<sup>30</sup> ಆಗ ಸೌಲನು ಬಹಳವಾಗಿ ಉರಿಗೊಂಡು ಯೋನಾತಾನನಿಗೆ - ಎಲೋ, ದುಷ್ಟ ದಾಸಿಯ ಮಗನೇ, ನೀನು ಇಷಯನ ಮಗನೊಡನೆ ಸ್ನೇಹದಿಂದಿರುವದು ನನಗೆ ಗೊತ್ತಿಲ್ಲವೋ? ಇದರಿಂದ ನಿನಗೂ ನಿನ್ನನ್ನು ಹೆತ್ತವಳಿಗೂ ಮಾನಭಂಗವಾಗುವದು.",
			"31": "<sup>31</sup> ಆ ಇಷಯನ ಮಗನು ಭೂಲೋಕದಲ್ಲಿರುವವರೆಗೆ ನಿನಗಾದರೂ ನಿನ್ನ ರಾಜ್ಯಕ್ಕಾದರೂ ನೆಲೆಯಿಲ್ಲ; ಆದದರಿಂದ ಅವನನ್ನು ಕರಿಸಿ ನನ್ನ ಬಳಿಗೆ ತೆಗೆದುಕೊಂಡು ಬಾ; ಅವನು ಸಾಯಬೇಕು ಅಂದನು.",
			"32": "<sup>32</sup> ಅದಕ್ಕೆ ಯೋನಾತಾನನು - ಅವನು ಯಾಕೆ ಸಾಯಬೇಕು, ಅವನು ಏನು ಮಾಡಿದನು ಎಂದು ತನ್ನ ತಂದೆಯನ್ನು ಕೇಳಿದನು.",
			"33": "<sup>33</sup> ಕೂಡಲೆ ಸೌಲನು ಅವನನ್ನು ತಿವಿಯಬೇಕೆಂದು ಈಟಿಯನ್ನೆಸೆದನು. ತನ್ನ ತಂದೆಯು ದಾವೀದನನ್ನು ಕೊಲ್ಲುವದಕ್ಕೆ ನಿಶ್ಚಯಿಸಿದ್ದಾನೆಂಬದು ಯೋನಾತಾನನಿಗೆ ಗೊತ್ತಾಗಲು",
			"34": "<sup>34</sup> ಅವನು ಬಲು ಸಿಟ್ಟುಗೊಂಡು ಪಂಕ್ತಿಯಿಂದೆದ್ದು ಹೋದನು; ತನ್ನ ತಂದೆಯು ದಾವೀದನನ್ನು ಅಪಮಾನಪಡಿಸಿದದರಿಂದ ಯೋನಾತಾನನಿಗೆ ಬಹಳ ದುಃಖವುಂಟಾಗಿ ಅವನು ಆ ದಿವಸ ಊಟ ಮಾಡಲೇ ಇಲ್ಲ.",
			"35": "<sup>35</sup> ಅವನು ಮರುದಿವಸ ಮುಂಜಾನೆ ಒಬ್ಬ ಹುಡುಗನನ್ನು ಕರಕೊಂಡು ದಾವೀದನನ್ನು ನೋಡುವದಕ್ಕಾಗಿ ಅಡವಿಗೆ ಹೋಗಿ ಹುಡುಗನಿಗೆ -",
			"36": "<sup>36</sup> ಬೇಗ ಹೋಗಿ ನಾನು ಎಸೆದ ಬಾಣಗಳನ್ನು ಕೂಡಿಸಿಕೊಂಡು ಬಾ ಎಂದು ಹೇಳಿದನು. ಆ ಹುಡುಗನು ಹೋಗುತ್ತಿರುವಾಗ ಯೋನಾತಾನನು ಬಾಣವನ್ನು ಅವನ ಆಚೆಗೆ ಎಸೆದನು.",
			"37": "<sup>37</sup> ಹುಡುಗನು ಬಾಣಬಿದ್ದ ಸ್ಥಳಕ್ಕೆ ಬಂದಾಗ ಯೋನಾತಾನನು ಅವನಿಗೆ - ಬಾಣವು ನಿನ್ನ ಆಚೆ ಬಿದ್ದಿತಲ್ಲಾ;",
			"38": "<sup>38</sup> ಬೇಗ ಹೋಗು, ನಿಲ್ಲಬೇಡ ಎಂದು ಕೂಗಿ ಹೇಳಿದನು. ಹುಡುಗನು ಬಾಣಗಳನ್ನು ಕೂಡಿಸಿಕೊಂಡು ಯಜಮಾನನ ಬಳಿಗೆ ಬಂದನು.",
			"39": "<sup>39</sup> ಇದರ ರಹಸ್ಯವು ಯೋನಾತಾನ ದಾವೀದರಿಗೆ ಗೊತ್ತೇ ಹೊರತು ಆ ಹುಡುಗನಿಗೆ ಗೊತ್ತಿರಲಿಲ್ಲ.",
			"40": "<sup>40</sup> ಯೋನಾತಾನನು ತನ್ನ ಆಯುಧಗಳನ್ನು ಹುಡುಗನ ಕೈಯಲ್ಲಿ ಕೊಟ್ಟು ಅವನಿಗೆ ಅವುಗಳನ್ನು ಊರಿಗೆ ತೆಗೆದುಕೊಂಡು ಹೋಗು ಎಂದು ಹೇಳಿದನು.",
			"41": "<sup>41</sup> ಹುಡುಗನು ಹೋಗುತ್ತಲೆ ದಾವೀದನು ಕಲ್ಲುಕುಪ್ಪೆಯ ಬಳಿಯಿಂದೆದ್ದು ಬಂದು ಮೂರು ಸಾರಿ ಸಾಷ್ಟಾಂಗವಾಗಿ ಯೋನಾತಾನನನ್ನು ವಂದಿಸಿದನು. ಅವರು ಒಬ್ಬರಿಗೊಬ್ಬರು ಮುದ್ದಿಟ್ಟುಕೊಂಡು ಅತ್ತರು; ದಾವೀದನು ಬಹಳವಾಗಿ ಅತ್ತನು.",
			"42": "<sup>42</sup> ಅನಂತರ ಯೋನಾತಾನನು ದಾವೀದನಿಗೆ - ಸಮಾಧಾನದಿಂದ ಹೋಗು; ನಾವು ಯೆಹೋವನ ಹೆಸರಿನಲ್ಲಿ ಆಣೆಯಿಟ್ಟು ಒಡಂಬಡಿಕೆಯನ್ನು ಮಾಡಿಕೊಂಡಿದ್ದೇವಲ್ಲಾ; ಆತನೇ ನನಗೂ ನಿನಗೂ ನನ್ನ ಸಂತಾನಕ್ಕೂ ನಿನ್ನ ಸಂತಾನಕ್ಕೂ ಸದಾ ಸಂಬಂಧಸಾಕ್ಷಿಯಾಗಿರಲಿ ಎಂದು ಹೇಳಿದನು. ತರುವಾಯ ದಾವೀದನು ಹೊರಟುಹೋದನು; ಯೋನಾತಾನನು ಊರೊಳಕ್ಕೆ ಬಂದನು."
		},
		"21": {
			"1": "<sup>1</sup> ದಾವೀದನು ನೋಬ್ ಊರಲ್ಲಿದ್ದ ಯಾಜಕನಾದ ಅಹೀಮೆಲೆಕನ ಬಳಿಗೆ ಬಂದನು. ಅಹೀಮೆಲೆಕನು ದಾವೀದನನ್ನು ಭಯಭಕ್ತಿಯಿಂದ ಎದುರುಗೊಂಡು - ನಿನ್ನ ಜೊತೆಯಲ್ಲಿ ಒಬ್ಬನಾದರೂ ಇಲ್ಲವಲ್ಲಾ! ನೀನು ಒಬ್ಬನೇ ಬಂದದ್ದೇಕೆ ಎಂದು ಅವನನ್ನು ಕೇಳಲು ಅವನು -",
			"2": "<sup>2</sup> ಅರಸನು ಒಂದು ಕೆಲಸವನ್ನು ಆಜ್ಞಾಪಿಸಿ ಇದನ್ನು ಯಾರಿಗೂ ತಿಳಿಸಬಾರದೆಂದು ಕಟ್ಟುಮಾಡಿ ನನ್ನನ್ನು ಕಳುಹಿಸಿದ್ದಾನೆ. ನನ್ನ ಆಳುಗಳು ಇಂಥಿಂಥ ಸ್ಥಳದಲ್ಲಿ ನನ್ನನ್ನು ಸಂಧಿಸಬೇಕೆಂದು ಗೊತ್ತುಮಾಡಿದ್ದೇನೆ.",
			"3": "<sup>3</sup> ಈಗ ನಿನ್ನ ಕೈಯಲ್ಲಿ ಏನಾದರೂ ಉಂಟೋ? ನಾಲ್ಕೈದು ರೊಟ್ಟಿಗಳಾದರೂ ಬೇರೆ ಯಾವ ಆಹಾರಪದಾರ್ಥವಾದರೂ ಇದ್ದರೆ ನನಗೆ ಕೊಟ್ಟುಬಿಡು ಅಂದನು.",
			"4": "<sup>4</sup> ಯಾಜಕನು ದಾವೀದನಿಗೆ - ನನ್ನ ಬಳಿಯಲ್ಲಿ ಮೀಸಲ ರೊಟ್ಟಿಗಳ ಹೊರತು ಬೇರೆ ರೊಟ್ಟಿಗಳಿಲ್ಲ; ನಿನ್ನ ಆಳುಗಳು ಸ್ತ್ರೀಸಂಗ ಬಿಟ್ಟವರಾಗಿದ್ದರೆ ಅವುಗಳನ್ನು ಕೊಡಬಹುದು ಎನ್ನಲು ದಾವೀದನು -",
			"5": "<sup>5</sup> ನಮ್ಮ ಎಲ್ಲಾ ಯುದ್ಧಪ್ರಯಾಣಗಳಲ್ಲಿ ಸ್ತ್ರೀನಿಷೇಧ ಮಾಡುವಂತೆ ಈಗಲೂ ಮಾಡಿದ್ದೇವೆ. ನಾವು ಸಾಧಾರಣಕಾರ್ಯಕ್ಕೆ ಹೊರಟಾಗಲೂ ಆಳುಗಳ ಸಾಮಾನುಗಳು ಪರಿಶುದ್ಧವಾಗಿರುತ್ತಿದ್ದವು. ಈ ದಿನದಲ್ಲಿ ಅವು ಎಷ್ಟೋ ಹೆಚ್ಚಾಗಿ ಪರಿಶುದ್ಧವಾಗಿರುವವಲ್ಲವೇ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"6": "<sup>6</sup> ಬಿಸಿರೊಟ್ಟಿಗಳನ್ನು ಅರ್ಪಿಸಿದ ಆ ದಿನದಲ್ಲಿ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಿಂದ ತೆಗೆಯಲ್ಪಟ್ಟ ನೈವೇದ್ಯವಾದ ಮೊದಲ ರೊಟ್ಟಿಗಳ ಹೊರತು ಅಲ್ಲಿ ಬೇರೆ ರೊಟ್ಟಿಗಳಿರಲಿಲ್ಲವಾದದರಿಂದ ಯಾಜಕನು ಆ ಪರಿಶುದ್ಧವಾದ ರೊಟ್ಟಿಗಳನ್ನೇ ಕೊಟ್ಟುಬಿಟ್ಟನು.",
			"7": "<sup>7</sup> ಅದೇ ದಿವಸ ಸೌಲನ ಪಶುಪಾಲರಲ್ಲಿ ಪ್ರಧಾನನಾದ ದೋಯೇಗನೆಂಬ ಎದೋಮ್ಯನು ಯೆಹೋವನ ಆಲಯದಲ್ಲಿ ತಡೆಯಲ್ಪಟ್ಟಿದ್ದನು.",
			"8": "<sup>8</sup> ದಾವೀದನು ಅಹೀಮೆಲೆಕನನ್ನು - ಅರಸನ ಕಾರ್ಯವು ಅವಸರವಾಗಿದ್ದದರಿಂದ ಕತ್ತಿಯನ್ನಾಗಲಿ ಬೇರೆ ಆಯುಧವನ್ನಾಗಲಿ ತೆಗೆದುಕೊಂಡು ಬರಲು ಆಗಲಿಲ್ಲ; ನಿನ್ನ ಬಳಿಯಲ್ಲಿ ಬರ್ಜಿಯಾಗಲಿ ಕತ್ತಿಯಾಗಲಿ ಇಲ್ಲವೋ ಎಂದು ಕೇಳಲು ಅವನು -",
			"9": "<sup>9</sup> ನೀನು ಏಲಾ ತಗ್ಗಿನಲ್ಲಿ ಕೊಂದುಹಾಕಿದ ಫಿಲಿಷ್ಟಿಯನಾದ ಗೊಲ್ಯಾತನ ಕತ್ತಿಯು ಒಂದು ಬಟ್ಟೆಯಲ್ಲಿ ಸುತ್ತಲ್ಪಟ್ಟು ಏಫೋದಿನ ಹಿಂದೆ ಇಡಲ್ಪಟ್ಟಿದೆ; ಬೇಕಾದರೆ ಅದನ್ನು ತೆಗೆದುಕೋ. ಅದರ ಹೊರತು ನನ್ನ ಬಳಿಯಲ್ಲಿ ಬೇರೊಂದಿಲ್ಲ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು. ದಾವೀದನು - ಅದಕ್ಕೆ ಸಮಾನವಾದದ್ದು ಮತ್ತೊಂದಿಲ್ಲ, ಅದನ್ನೇ ಕೊಡು ಎಂದು ಹೇಳಿ ತೆಗೆದುಕೊಂಡನು.",
			"10": "<sup>10</sup> ದಾವೀದನು ಸೌಲನ ಭಯದಿಂದ ಅದೇ ದಿವಸ ಗತ್ ಊರಿನ ಅರಸನಾದ ಆಕೀಷನ ಬಳಿಗೆ ಓಡಿಹೋದನು.",
			"11": "<sup>11</sup> ಆಕೀಷನ ಸೇವಕರು - ಸೌಲನು ಸಾವಿರಗಟ್ಟಳೆಯಾಗಿ ಕೊಂದನು; ದಾವೀದನು ಹತ್ತು ಸಾವಿರಗಟ್ಟಳೆಯಾಗಿ ಕೊಂದನು ಎಂಬದಾಗಿ ಸ್ತ್ರೀಯರು ಕುಣಿಯುತ್ತಾ ಹಾಡಿದ್ದು ಇವನನ್ನು ಕುರಿತಲ್ಲವೋ? ಎಂದು ಮಾತಾಡಿಕೊಂಡರು.",
			"12": "<sup>12</sup> ದಾವೀದನು ಈ ಮಾತುಗಳನ್ನು ಕೇಳುತ್ತಲೆ ಗತ್ ಊರಿನ ಅರಸನಾದ ಆಕೀಷನಿಗೆ ಬಹಳವಾಗಿ ಹೆದರಿ",
			"13": "<sup>13</sup> ಅವನ ಸೇವಕರ ಮುಂದೆ ತನ್ನ ಬುದ್ಧಿಯನ್ನು ಮಾರ್ಪಡಿಸಿಕೊಂಡು ಗಡ್ಡದ ಮೇಲೆಲ್ಲಾ ಜೊಲ್ಲು ಸುರಿಸುತ್ತಾ ಕದಗಳನ್ನು ಕೆರೆಯುತ್ತಾ ತನ್ನನ್ನು ಹುಚ್ಚನಂತೆ ತೋರಿಸಿಕೊಂಡನು.",
			"14": "<sup>14</sup> ಆಗ ಆಕೀಷನು ತನ್ನ ಸೇವಕರಿಗೆ - ಈ ಮನುಷ್ಯನು ಹುಚ್ಚನೆಂದು ನಿಮಗೆ ಕಾಣುವದಿಲ್ಲವೋ? ಇವನನ್ನು ನನ್ನ ಹತ್ತಿರ ಯಾಕೆ ತಂದಿರಿ?",
			"15": "<sup>15</sup> ನನ್ನ ಬಳಿಯಲ್ಲಿ ಹುಚ್ಚರು ಕಡಿಮೆಯೆಂದು ನೆನಸಿ ನನ್ನನ್ನು ಮರುಳಾಟದಿಂದ ಬೇಸರಗೊಳಿಸುವದಕ್ಕಾಗಿ ಇವನನ್ನು ತಂದಿರೋ? ಇಂಥವನು ನನ್ನ ಮನೆಗೆ ಬರಬೇಕೋ ಅಂದನು."
		},
		"22": {
			"1": "<sup>1</sup> ದಾವೀದನು ಅಲ್ಲಿಂದ ತಪ್ಪಿಸಿಕೊಂಡು ಅದುಲ್ಲಾಮೆಂಬ ಗವಿಗೆ ಹೋದನು. ಈ ವರ್ತಮಾನವು ಅವನ ಅಣ್ಣಂದಿರಿಗೂ ಬೇರೆ ಎಲ್ಲಾ ಸಂಬಂಧಿಕರಿಗೂ ಮುಟ್ಟಲಾಗಿ ಅವರೂ ಬಂದು ಅವನನ್ನು ಸೇರಿಕೊಂಡರು.",
			"2": "<sup>2</sup> ಇದಲ್ಲದೆ ಕುಗ್ಗಿದವರೂ ಸಾಲಗಾರರೂ ಮನನೊಂದವರೂ ಆಗಿರುವ ಸರ್ವಜನರೂ ಬಂದು ಅವನನ್ನು ಆಶ್ರಯಿಸಿಕೊಳ್ಳಲು ಅವನು ಸುಮಾರು ನಾನೂರು ಜನರಿಗೆ ನಾಯಕನಾದನು.",
			"3": "<sup>3</sup> ಅನಂತರ ದಾವೀದನು ತನ್ನ ತಂದೆತಾಯಿಗಳನ್ನು ಮೋವಾಬ್‍ದೇಶದ ಮಿಚ್ಪೆ ಎಂಬಲ್ಲಿಗೆ ಕರಕೊಂಡು ಹೋಗಿ ಅಲ್ಲಿನ ಅರಸನನ್ನು - ದೇವರು ನನಗೆ ಮಾರ್ಗತೋರಿಸುವವರೆಗೂ ನನ್ನ ತಂದೆತಾಯಿಗಳು ನಿನ್ನ ಬಳಿಯಲ್ಲಿರುವದಕ್ಕೆ ಅಪ್ಪಣೆಯಾಗಬೇಕೆಂದು ಬೇಡಿಕೊಂಡು ಅವರನ್ನು ಅವನ ಬಳಿಯಲ್ಲಿ ಬಿಟ್ಟನು.",
			"4": "<sup>4</sup> ದಾವೀದನು ದುರ್ಗದಲ್ಲಿದ್ದ ಕಾಲವೆಲ್ಲಾ ಅವನ ತಂದೆತಾಯಿಗಳು ಈ ಅರಸನ ಬಳಿಯಲ್ಲಿ ವಾಸವಾಗಿದ್ದರು.",
			"5": "<sup>5</sup> ಗಾದ್‍ಪ್ರವಾದಿಯು ದಾವೀದನಿಗೆ - ನೀನು ಈ ದುರ್ಗದಲ್ಲಿರಬಾರದು; ಇದನ್ನು ಬಿಟ್ಟು ಯೆಹೂದ ದೇಶಕ್ಕೆ ಹೋಗು ಎಂದು ಹೇಳಿದದರಿಂದ ಅವನು ಹೆರೆತ್ ಅರಣ್ಯಕ್ಕೆ ಹೋದನು.",
			"6": "<sup>6</sup> ಒಂದು ದಿವಸ ಸೌಲನು ತನ್ನ ಎಲ್ಲಾ ಪರಿವಾರದವರ ನಡುವೆ ಕೈಯಲ್ಲಿ ಬರ್ಜಿಯನ್ನು ಹಿಡಿದುಕೊಂಡು ಗಿಬೆಯ ಗುಡ್ಡದ ಪಿಚುಲವೃಕ್ಷದ ಕೆಳಗೆ ಕೂತುಕೊಂಡಿರಲು ದಾವೀದನೂ ಅವನ ಜನರೂ ಅಡಗಿಕೊಂಡಿರುವ ಸ್ಥಳವು ಗೊತ್ತಾಗಿದೆ ಎಂಬ ಸುದ್ದಿಯು ಅವನಿಗೆ ಮುಟ್ಟಿತು.",
			"7": "<sup>7</sup> ಆಗ ಅವನು ತನ್ನ ಸುತ್ತಲೂ ನಿಂತಿದ್ದ ಪರಿವಾರದವರಿಗೆ - ಬೆನ್ಯಾಮೀನನ ಮಕ್ಕಳೇ, ಕೇಳಿರಿ; ನೀವೆಲ್ಲರೂ ನನಗೆ ವಿರೋಧವಾಗಿ ಒಳಸಂಚು ಮಾಡುವದೇಕೆ? ಇಷಯನ ಮಗನು ನಿಮ್ಮೆಲ್ಲರಿಗೂ ಹೊಲಗಳನ್ನೂ ದ್ರಾಕ್ಷೇತೋಟಗಳನ್ನೂ ಕೊಡುವನೋ ಎಲ್ಲರನ್ನೂ ಸಹಸ್ರಾಧಿಪತಿಗಳನ್ನಾಗಲಿ ಶತಾಧಿಪತಿಗಳನ್ನಾಗಲಿ ಮಾಡುವನೋ?",
			"8": "<sup>8</sup> ನನ್ನ ಮಗನು ಇಷಯನ ಮಗನೊಡನೆ ಒಡಂಬಡಿಕೆ ಮಾಡಿಕೊಂಡದ್ದನ್ನು ನಿಮ್ಮಲ್ಲಿ ಒಬ್ಬನಾದರೂ ನನಗೆ ತಿಳಿಸಲಿಲ್ಲವಲ್ಲಾ; ನನ್ನ ಸೇವಕನು ನನಗೆ ವಿರೋಧವಾಗಿ ಒಳಸಂಚುಮಾಡುವ ಹಾಗೆ ನನ್ನ ಮಗನೇ ಅವನನ್ನು ಎಬ್ಬಿಸಿದನೆಂದು ಒಬ್ಬನಾದರೂ ನನಗೆ ಯಾಕೆ ತಿಳಿಸಲಿಲ್ಲ; ನೀವು ನನ್ನ ಚಿಂತೆಯನ್ನೇ ಬಿಟ್ಟಿರಿ ಅಂದನು.",
			"9": "<sup>9</sup> ಆಗ ಸೌಲನ ಸೇವಕರ ಬಳಿಯಲ್ಲಿ ನಿಂತಿದ್ದ ಎದೋಮ್ಯನಾದ ದೋಯೇಗನು - ಇಷಯನ ಮಗನು ನೋಬದಲ್ಲಿರುವ ಅಹೀಟೂಬನ ಮಗನಾದ ಅಹೀಮೆಲೆಕನ ಬಳಿಗೆ ಬಂದದ್ದನ್ನು ಕಂಡೆನು.",
			"10": "<sup>10</sup> ಅವನು ಇವನಿಗೆ ಯೆಹೋವನ ಚಿತ್ತವನ್ನು ತಿಳಿಸಿ ಆಹಾರವನ್ನೂ ಫಿಲಿಷ್ಟಿಯನಾದ ಗೊಲ್ಯಾತನ ಕತ್ತಿಯನ್ನೂ ಕೊಟ್ಟನು ಎಂದು ಹೇಳಿದನು.",
			"11": "<sup>11</sup> ಕೂಡಲೆ ಸೌಲನು ಅಹೀಟೂಬನ ಮಗನೂ ಯಾಜಕನೂ ಆಗಿದ್ದ ಅಹೀಮೆಲೆಕನನ್ನು ಅವನ ಕುಟುಂಬಕ್ಕೆ ಸೇರಿದ ನೋಬದ ಬೇರೆ ಎಲ್ಲಾ ಯಾಜಕರೊಡನೆ ಕರೇ ಕಳುಹಿಸಿದನು. ಅವರೆಲ್ಲರೂ ಅರಸನ ಸನ್ನಿಧಿಗೆ ಬರಲು ಅರಸನು -",
			"12": "<sup>12</sup> ಅಹೀಟೂಬನ ಮಗನೇ, ಕೇಳು ಅಂದನು. ಅದಕ್ಕೆ ಅವನು - ಒಡೆಯಾ, ಅಪ್ಪಣೆಯಾಗಲಿ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"13": "<sup>13</sup> ಆಗ ಸೌಲನು ಅವನಿಗೆ - ನೀನು ನನಗೆ ವಿರೋಧವಾಗಿ ಇಷಯನ ಮಗನೊಡನೆ ಒಳಸಂಚುಮಾಡಿದ್ದೇಕೆ? ಅವನು ನನಗೆ ವಿರೋಧವಾಗಿ ಎದ್ದು ನನ್ನ ಜೀವಕ್ಕಾಗಿ ಹೊಂಚುಹಾಕುವಂತೆ ನೀನು ಅವನಿಗೆ ರೊಟ್ಟಿಕತ್ತಿಗಳನ್ನು ಕೊಟ್ಟು ದೈವೋತ್ತರವನ್ನು ತಿಳಿಸಿದ್ದೇಕೆ ಎಂದು ಕೇಳಲು ಅವನು -",
			"14": "<sup>14</sup> ನಿನ್ನ ಎಲ್ಲಾ ಸೇವಕರಲ್ಲಿ ಅರಸನ ಅಳಿಯನಾಗಿರುವ ದಾವೀದನಂತೆ ನಂಬಿಗಸ್ತನಾದವನು ಯಾವನಿದ್ದಾನೆ? ಅವನು ನಿನ್ನ ಮಂತ್ರಾಲೋಚಕರಲ್ಲೊಬ್ಬನೂ ನಿನ್ನ ಮನೆಯಲ್ಲಿ ಘನವಂತನೂ ಆಗಿದ್ದಾನಲ್ಲವೋ?",
			"15": "<sup>15</sup> ನಾನು ಅವನಿಗೋಸ್ಕರ ದೇವರ ಸನ್ನಿಧಿಯಲ್ಲಿ ವಿಚಾರಮಾಡಿದ್ದು ಇದೇ ಮೊದಲನೆಯ ಸಾರಿಯೋ? ಇದು ನನಗೆ ದೂರವಾಗಿರಲಿ; ಅರಸನು ತನ್ನ ಸೇವಕನ ಮೇಲೆಯೂ ಅವನ ಮನೆಯವರ ಮೇಲೆಯೂ ಇಂಥ ಮಾತನ್ನು ಹೊರಿಸಬಾರದು. ಈ ವಿಷಯದಲ್ಲಿ ನಿನ್ನ ಸೇವಕನಿಗೆ ಸ್ವಲ್ಪವೂ ಗೊತ್ತಿರಲಿಲ್ಲ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"16": "<sup>16</sup> ಆಗ ಅರಸನು ಅವನಿಗೆ - ಅಹೀಮೆಲೆಕನೇ, ನೀನೂ ನಿನ್ನ ಮನೆಯವರೂ ಸಾಯಲೇಬೇಕು ಎಂದು ಹೇಳಿ ತನ್ನ ಬಳಿಯಲ್ಲಿದ್ದ ಸಿಪಾಯಿಗಳಿಗೆ -",
			"17": "<sup>17</sup> ನೀವು ತಿರುಗಿಕೊಂಡು ಯೆಹೋವನ ಈ ಯಾಜಕರನ್ನು ಕೊಲ್ಲಿರಿ; ದಾವೀದನು ಓಡಿಹೋದದ್ದು ಗೊತ್ತಿದ್ದರೂ ನನಗೆ ತಿಳಿಸದೆ ಅವನಿಗೆ ಸಹಾಯಮಾಡಿದ್ದಾರೆ ಎಂದು ಹೇಳಿ ಆಜ್ಞಾಪಿಸಿದನು. ಆದರೆ ಅರಸನ ಸಿಪಾಯಿಗಳು ಯೆಹೋವನ ಯಾಜಕರ ಮೇಲೆ ಕೈಹಾಕುವದಕ್ಕೆ ಮನಸ್ಸುಮಾಡಲಿಲ್ಲ.",
			"18": "<sup>18</sup> ಆದದರಿಂದ ಸೌಲನು ಎದೋಮ್ಯನಾದ ದೋಯೇಗನಿಗೆ - ನೀನು ಹೋಗಿ ಅವರನ್ನು ಕೊಲ್ಲು ಎಂದು ಆಜ್ಞಾಪಿಸಲು ಅವನು ನಾರು ಬಟ್ಟೆಯ ಏಫೋದನ್ನು ಧರಿಸಿಕೊಂಡಿದ್ದ ಎಂಭತ್ತೈದು ಮಂದಿ ಯಾಜಕರನ್ನು ಆ ದಿವಸ ಕೊಂದುಹಾಕಿದನು.",
			"19": "<sup>19</sup> ಅನಂತರ ಅವನು ಯಾಜಕರ ಪಟ್ಟಣವಾದ ನೋಬಕ್ಕೆ ಹೋಗಿ ಅಲ್ಲಿನ ಸ್ತ್ರೀಪುರುಷರನ್ನೂ ಶಿಶುಬಾಲಕರನ್ನೂ ದನಕುರಿಕತ್ತೆಗಳನ್ನೂ ಕತ್ತಿಯಿಂದ ಸಂಹರಿಸಿದನು.",
			"20": "<sup>20</sup> ಅಹೀಟೂಬನ ಮಗನಾದ ಅಹೀಮೆಲೆಕನ ಮಕ್ಕಳಲ್ಲೊಬ್ಬನಾದ ಎಬ್ಯಾತಾರನೆಂಬವನು ತಪ್ಪಿಸಿಕೊಂಡು ದಾವೀದನ ಬಳಿಗೆ ಓಡಿಬಂದು",
			"21": "<sup>21</sup> ಸೌಲನು ಯೆಹೋವನ ಯಾಜಕರನ್ನು ಕೊಲ್ಲಿಸಿದ ಸಂಗತಿಯನ್ನು ಅವನಿಗೆ ತಿಳಿಸಲು ದಾವೀದನು -",
			"22": "<sup>22</sup> ಎದೋಮ್ಯನಾದ ದೋಯೇಗನನ್ನು ನಾನು ಅಲ್ಲಿ ಕಂಡಾಗಲೇ ಇವನು ಸೌಲನಿಗೆ ಹೇಗೂ ಈ ಸಂಗತಿಯನ್ನು ತಿಳಿಸುವನೆಂದು ನೆನಸಿಕೊಂಡೆನು. ನಿನ್ನ ಸಂಬಂಧಿಕರ ವಧೆಗೆ ನಾನೇ ಕಾರಣನಾದೆನಲ್ಲಾ!",
			"23": "<sup>23</sup> ನೀನಾದರೋ ಹೆದರದೆ ನನ್ನ ಸಂಗಡ ಇರು; ನನ್ನ ಪ್ರಾಣವನ್ನು ತೆಗೆಯಬೇಕೆಂದಿರುವವನೇ ನಿನ್ನ ಪ್ರಾಣವನ್ನೂ ತೆಗೆಯಬೇಕೆಂದಿರುತ್ತಾನೆ. ನೀನು ನನ್ನ ಜೊತೆಯಲ್ಲಿರುವದಾದರೆ ಸುರಕ್ಷಿತನಾಗಿರುವಿ ಅಂದನು."
		},
		"23": {
			"1": "<sup>1</sup> ಫಿಲಿಷ್ಟಿಯರು ಕೆಯೀಲಕ್ಕೆ ಮುತ್ತಿಗೆಹಾಕಿ ಅಲ್ಲಿನ ಕಣಗಳನ್ನು ಸೂರೆಮಾಡುತ್ತಿರುವ ವರ್ತಮಾನವು ದಾವೀದನಿಗೆ ಮುಟ್ಟಲು ಅವನು -",
			"2": "<sup>2</sup> ನಾನು ಹೋಗಿ ಆ ಫಿಲಿಷ್ಟಿಯರನ್ನು ಹೊಡಿಯಬಹುದೋ ಎಂದು ಯೆಹೋವನನ್ನು ಕೇಳಿದನು. ಆತನು ಅವನಿಗೆ - ಹೋಗಿ ಫಿಲಿಷ್ಟಿಯರನ್ನು ಹೊಡೆದು ಕೆಯೀಲಾ ಊರನ್ನು ರಕ್ಷಿಸು ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"3": "<sup>3</sup> ಆದರೆ ದಾವೀದನ ಸೈನಿಕರು ಅವನಿಗೆ - ನಮಗೆ ಯೆಹೂದ ಸೀಮೆಯಲ್ಲಿಯೇ ಇಷ್ಟು ಭಯವಿರುವಲ್ಲಿ ಫಿಲಿಷ್ಟಿಯ ಸೈನ್ಯದೊಡನೆ ಯುದ್ಧಮಾಡುವದಕ್ಕಾಗಿ ಕೆಯೀಲಕ್ಕೆ ಹೋದರೆ ಅಲ್ಲಿ ಎಷ್ಟೋ ಅಧಿಕವಾದ ಭಯವುಂಟಾಗುವದಲ್ಲವೇ ಅಂದರು.",
			"4": "<sup>4</sup> ಆದದರಿಂದ ಅವನು ತಿರಿಗಿ ಯೆಹೋವನನ್ನು ಕೇಳಿದನು. ಆತನು - ನೀನೆದ್ದು ಕೆಯೀಲಕ್ಕೆ ಹೋಗು; ನಾನು ಫಿಲಿಷ್ಟಿಯರನ್ನು ನಿನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸಿಕೊಡುವೆನು ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"5": "<sup>5</sup> ಆಗ ದಾವೀದನು ತನ್ನ ಜನರೊಡನೆ ಕೆಯೀಲಕ್ಕೆ ಹೋಗಿ ಫಿಲಿಷ್ಟಿಯರೊಡನೆ ಯುದ್ಧಮಾಡಿ ಅವರನ್ನು ಪೂರ್ಣವಾಗಿ ಸೋಲಿಸಿ ಅವರ ಪಶುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಕೆಯೀಲದವರನ್ನು ರಕ್ಷಿಸಿದನು.",
			"6": "<sup>6</sup> ದಾವೀದನ ಬಳಿಗೆ ಓಡಿಬಂದಿದ್ದ ಅಹೀಮೆಲೆಕನ ಮಗನಾದ ಎಬ್ಯಾತಾರನು ಕೆಯೀಲಕ್ಕೆ ಬರುವಾಗ ಏಫೋದನ್ನು ತಂದಿದ್ದನು. ದಾವೀದನು ಕೆಯೀಲದಲ್ಲಿದ್ದಾನೆಂಬ ವರ್ತಮಾನವು ಸೌಲನಿಗೆ ಮುಟ್ಟಿದಾಗ ಅವನು -",
			"7": "<sup>7</sup> ದೇವರು ದಾವೀದನನ್ನು ನನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸಿದನು. ಅವನು ಅಗುಳಿಕದಗಳಿಂದ ಭದ್ರವಾಗಿರುವ ಪಟ್ಟಣವನ್ನು ಪ್ರವೇಶಿಸಿದ್ದಾನಲ್ಲಾ; ಅವನು ಅಲ್ಲಿಂದ ತಪ್ಪಿಸಿಕೊಳ್ಳಲಾರನು ಅಂದುಕೊಂಡು",
			"8": "<sup>8</sup> ಕೆಯೀಲದಲ್ಲಿದ್ದ ದಾವೀದನನ್ನೂ ಅವನ ಜನರನ್ನೂ ಹಿಡಿಯ ಹೋಗುವದಕ್ಕೋಸ್ಕರ ಸೈನ್ಯವನ್ನು ಕೂಡಿಸಿದನು.",
			"9": "<sup>9</sup> ಸೌಲನು ತನಗೆ ಕೇಡುಮಾಡಬೇಕೆಂದಿದ್ದಾನೆಂದು ದಾವೀದನು ತಿಳಿದು ಯಾಜಕನಾದ ಎಬ್ಯಾತಾರನಿಗೆ - ಏಫೋದನ್ನು ಇಲ್ಲಿಗೆ ತೆಗೆದುಕೊಂಡು ಬಾ ಎಂದು ಹೇಳಿ ತರಿಸಿ -",
			"10": "<sup>10</sup> ಇಸ್ರಾಯೇಲ್‍ದೇವರಾದ ಯೆಹೋವನೇ, ಸೌಲನು ಕೆಯೀಲಕ್ಕೆ ಬಂದು ನನ್ನ ನಿಮಿತ್ತವಾಗಿ ಪಟ್ಟಣವನ್ನು ಹಾಳುಮಾಡಬೇಕೆಂದಿದ್ದಾನೆಂದು ನಿನ್ನ ಸೇವಕನಾದ ನನಗೆ ಸುದ್ದಿ ಬಂದಿತು.",
			"11": "<sup>11</sup> ನಾನು ಕೇಳಿದ ಸುದ್ದಿಯಂತೆ ಅವನು ನಿಜವಾಗಿ ಬರುವನೋ? ಕೆಯೀಲಾ ಊರಿನವರು ನನ್ನನ್ನು ಅವನ ಕೈಗೆ ಒಪ್ಪಿಸುವರೋ? ಇಸ್ರಾಯೇಲ್‍ದೇವರಾದ ಯೆಹೋವನೇ, ಈ ವಿಷಯದಲ್ಲಿ ಉತ್ತರ ದಯಪಾಲಿಸು ಎಂದು ಪ್ರಾರ್ಥಿಸಲು ಅವನು ಬರುವನೆಂಬದಾಗಿ ಯೆಹೋವನಿಂದ ಉತ್ತರಸಿಕ್ಕಿತು.",
			"12": "<sup>12</sup> ದಾವೀದನು ತಿರಿಗಿ ಯೆಹೋವನನ್ನು - ಕೆಯೀಲದವರು ನನ್ನನ್ನೂ ನನ್ನ ಜನರನ್ನೂ ಸೌಲನ ಕೈಗೆ ಒಪ್ಪಿಸುವರೋ ಎಂದು ಕೇಳಿದಾಗ ಆತನು - ಒಪ್ಪಿಸಿಕೊಡುವರು ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"13": "<sup>13</sup> ಆಗ ದಾವೀದನು ತನ್ನ ಸಂಗಡ ಇದ್ದ ಆರುನೂರು ಜನರ ಸಹಿತವಾಗಿ ಕೆಯೀಲಾ ಊರನ್ನು ಬಿಟ್ಟು ಎಲ್ಲೆಲ್ಲಿಯೋ ಅಲೆದಾಡಿದನು. ದಾವೀದನು ಕೆಯೀಲದಿಂದ ತಪ್ಪಿಸಿಕೊಂಡನೆಂದು ಸೌಲನಿಗೆ ಗೊತ್ತಾಗಲು ಅವನು ಹೊರಡುವದನ್ನು ನಿಲ್ಲಿಸಿಬಿಟ್ಟನು.",
			"14": "<sup>14</sup> ಅನಂತರ ದಾವೀದನು ಅರಣ್ಯದಲ್ಲಿರುವ ಆಯಾ ಆಶ್ರಯಗಿರಿಗಳಲ್ಲಿ ಇರುತ್ತಿದ್ದು ಕಡೆಗೆ ಜೀಫ್ ಅರಣ್ಯದ ಪರ್ವತ ಪ್ರಾಂತಕ್ಕೆ ಬಂದು ಅಲ್ಲಿ ವಾಸಮಾಡಿದನು. ಸೌಲನು ದಿನಂಪ್ರತಿಯೂ ಅವನನ್ನು ಹುಡುಕುತ್ತಿದ್ದರೂ ದೇವರು ಇವನ ಕೈಗೆ ಒಪ್ಪಿಸಲಿಲ್ಲ.",
			"15": "<sup>15</sup> ಸೌಲನು ತನ್ನನ್ನು ಕೊಲ್ಲ ಹೊರಟಿದ್ದಾನೆಂದು ದಾವೀದನು ತಿಳಿದು ಜೀಫ್ ಅರಣ್ಯದ ಹೋರೆಷದಲ್ಲಿ ಅಡಗಿಕೊಂಡಿದ್ದನು.",
			"16": "<sup>16</sup> ಆಗ ಸೌಲನ ಮಗನಾದ ಯೋನಾತಾನನು ಅಲ್ಲಿಗೆ ಹೋಗಿ ದಾವೀದನಿಗೆ -",
			"17": "<sup>17</sup> ಭಯಪಡಬೇಡ; ನೀನು ನನ್ನ ತಂದೆಯಾದ ಸೌಲನ ಕೈಗೆ ಸಿಕ್ಕಿಬೀಳುವದಿಲ್ಲ; ನೀನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾಗುವಿ; ನಾನು ನಿನಗೆ ಎರಡನೆಯವನಾಗಿರುವೆನು. ಹೀಗಾಗುವದೆಂದು ನನ್ನ ತಂದೆಯಾದ ಸೌಲನೂ ತಿಳಿದುಕೊಂಡಿದ್ದಾನೆ ಎಂದು ಹೇಳಿ ದೇವರಲ್ಲಿ ಅವನನ್ನು ಬಲಪಡಿಸಿದನು.",
			"18": "<sup>18</sup> ಅವರಿಬ್ಬರೂ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಒಡಂಬಡಿಕೆಮಾಡಿಕೊಂಡರು. ತರುವಾಯ ಯೋನಾತಾನನು ತನ್ನ ಮನೆಗೆ ಹೋದನು; ದಾವೀದನು ಹೋರೆಷದಲ್ಲಿಯೇ ಇದ್ದನು.",
			"19": "<sup>19</sup> ಜೀಫ್ಯರು ಗಿಬೆಯದಲ್ಲಿದ್ದ ಸೌಲನ ಬಳಿಗೆ ಬಂದು ಅವನಿಗೆ - ಒಡೆಯಾ, ಕೇಳು; ದಾವೀದನು ನಮ್ಮ ಪ್ರಾಂತಕ್ಕೆ ಸೇರಿದ ಹೋರೆಷದ ಗಿರಿಗಳಲ್ಲಿ ಅಂದರೆ ಅರಣ್ಯದ ದಕ್ಷಿಣದಲ್ಲಿರುವ ಹಕೀಲಾ ಬೆಟ್ಟದಲ್ಲಿ ಅಡಗಿಕೊಂಡಿದ್ದಾನೆ.",
			"20": "<sup>20</sup> ಅರಸನು ಕುತೂಹಲದಂತೆ ನಮ್ಮ ಜೊತೆಯಲ್ಲಿ ಬರೋಣವಾಗಲಿ; ಅವನನ್ನು ಅರಸನ ಕೈಗೆ ಒಪ್ಪಿಸುವ ಕೆಲಸ ನಮ್ಮದು ಎಂದು ಹೇಳಿದರು.",
			"21": "<sup>21</sup> ಆಗ ಸೌಲನು ಅವರಿಗೆ - ನೀವು ನನ್ನ ಮೇಲೆ ಕನಿಕರಪಟ್ಟದರಿಂದ ಯೆಹೋವನ ಆಶೀರ್ವಾದವು ನಿಮ್ಮ ಮೇಲಿರಲಿ.",
			"22": "<sup>22</sup> ನೀವು ದಯವಿಟ್ಟು ಹೋಗಿ ಅವನು ತಿರುಗಾಡುವ ಸ್ಥಳವನ್ನೂ ಅಲ್ಲಿ ಅವನ ಗುರುತು ಕಂಡಿರುವವರನ್ನೂ ಸೂಕ್ಷ್ಮವಾಗಿ ವಿಚಾರಿಸಿ ಗೊತ್ತುಮಾಡಿಕೊಳ್ಳಿರಿ. ಅವನು ಬಹುಯುಕ್ತಿವಂತನೆಂದು ಕೇಳಿದ್ದೇನೆ.",
			"23": "<sup>23</sup> ಆದದರಿಂದ ಅವನು ಅಡಗಿಕೊಳ್ಳತಕ್ಕ ಎಲ್ಲಾ ಗುಪ್ತಸ್ಥಳಗಳನ್ನು ಗೊತ್ತುಮಾಡಿಕೊಂಡು ನಿಜವಾದ ವರ್ತಮಾನವನ್ನು ನನಗೆ ಮುಟ್ಟಿಸಿರಿ. ನಾನು ನಿಮ್ಮ ಸಂಗಡ ಬರುವೆನು; ಅವನು ದೇಶದಲ್ಲಿರುವದಾದರೆ ಎಲ್ಲಾ ಯೆಹೂದ ಪ್ರಜೆಗಳ ಮಧ್ಯದಲ್ಲಾದರೂ ಹುಡುಕಿ ಅವನನ್ನು ಹಿಡಿಯುವೆನು ಅಂದನು.",
			"24": "<sup>24</sup> ಅವರು ಹೊರಟು ಸೌಲನ ಮುಂದಾಗಿ ಜೀಫಿಗೆ ಬಂದರು. ದಾವೀದನೂ ಅವನ ಜನರೂ ಮಾವೋನ್ ಅರಣ್ಯದ ದಕ್ಷಿಣದಿಕ್ಕಿನಲ್ಲಿರುವ ಅರಾಬಾ ಎಂಬ ತಗ್ಗಾದ ಪ್ರದೇಶದಲ್ಲಿದ್ದರು.",
			"25": "<sup>25</sup> ಸೌಲನು ತನ್ನ ಜನರೊಡನೆ ದಾವೀದನನ್ನು ಹಿಡಿಯುವದಕ್ಕೆ ಬಂದನು. ಈ ವರ್ತಮಾನವು ದಾವೀದನಿಗೆ ಮುಟ್ಟಲು ಅವನು ಮಾವೋನಿನಲ್ಲಿರುವ ಒಂದು ಕಡಿದಾದ ಬೆಟ್ಟಕ್ಕೆ ಹೋಗಿ ಅಡಗಿಕೊಂಡನು.",
			"26": "<sup>26</sup> ಸೌಲನು ಇದನ್ನು ಕೇಳಿ ಮಾವೋನ್ ಅರಣ್ಯಕ್ಕೆ ಬಂದು ಆ ಬೆಟ್ಟದ ಒಂದು ಕಡೆಯಲ್ಲಿ ಹೋಗುತ್ತಿದ್ದನು; ಅದರ ಇನ್ನೊಂದು ಕಡೆಯಲ್ಲಿ ದಾವೀದನೂ ಅವನ ಜನರೂ ಇದ್ದರು. ದಾವೀದನೂ ಅವನ ಜನರೂ ತಪ್ಪಿಸಿಕೊಳ್ಳುವದಕ್ಕೆ ತ್ವರೆಪಡುವಷ್ಟರಲ್ಲಿ ಸೌಲನೂ ಅವನ ಜನರೂ ಬಂದು ಅವರನ್ನು ಸುತ್ತಿ ಹಿಡಿಯುವದಕ್ಕಿದ್ದರು.",
			"27": "<sup>27</sup> ಆಗಲೇ ಒಬ್ಬ ದೂತನು ಸೌಲನ ಬಳಿಗೆ ಬಂದು ಅವನಿಗೆ - ಬೇಗ ಬಾ; ಫಿಲಿಷ್ಟಿಯರು ದೇಶದೊಳಕ್ಕೆ ನುಗ್ಗಿದ್ದಾರೆ ಎಂದು ತಿಳಿಸಿದ್ದರಿಂದ",
			"28": "<sup>28</sup> ಅವನು ದಾವೀದನನ್ನು ಹಿಡಿಯುವ ಕೆಲಸದಿಂದ ಹಿಂದಿರುಗಿ ಫಿಲಿಷ್ಟಿಯರಿಗೆ ವಿರೋಧವಾಗಿ ಹೊರಟನು. ಆದದರಿಂದ ಆ ಬೆಟ್ಟಕ್ಕೆ ತಡೆಬೆಟ್ಟ ಎಂದು ಹೆಸರಾಯಿತು.",
			"29": "<sup>29</sup> ದಾವೀದನು ಅಲ್ಲಿಂದ ಗಟ್ಟಾಹತ್ತಿ ಹೋಗಿ ಏಂಗೆದಿಯ ಗಿರಿಗಳಲ್ಲಿ ವಾಸಿಸಿದನು."
		},
		"24": {
			"1": "<sup>1</sup> ಸೌಲನು ಫಿಲಿಷ್ಟಿಯರನ್ನು ಓಡಿಸಿ ಹಿಂದಿರುಗಿದಾಗ ದಾವೀದನು ಏಂಗೆದಿಯ ಅರಣ್ಯದಲ್ಲಿದ್ದಾನೆಂದು ಅವನಿಗೆ ತಿಳಿದುಬಂದಿತು.",
			"2": "<sup>2</sup> ಆಗ ಅವನು ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಶ್ರೇಷ್ಠರಾದ ಮೂರು ಸಾವಿರ ಮಂದಿ ಸೈನಿಕರನ್ನು ಆರಿಸಿಕೊಂಡು ಕಾಡು ಗುರಿಬಂಡೆಗಳಲ್ಲಿದ್ದ ದಾವೀದನನ್ನೂ ಅವನ ಜನರನ್ನೂ ಹುಡುಕುವದಕ್ಕೋಸ್ಕರ ಹೊರಟನು.",
			"3": "<sup>3</sup> ಅವನು ಮಾರ್ಗದಲ್ಲಿ ಕುರಿಹಟ್ಟಿಗಳ ಬಳಿಯಲ್ಲಿ ಒಂದು ಗವಿಯನ್ನು ಕಂಡು ಶೌಚಕ್ಕೋಸ್ಕರ ಅದರೊಳಗೆ ಪ್ರವೇಶಿಸಿದನು. ದಾವೀದನೂ ಅವನ ಜನರೂ ಅದೇ ಗವಿಯ ಹಿಂಗಡೆಯಲ್ಲಿ ಅಡಗಿಕೊಂಡಿದ್ದರು.",
			"4": "<sup>4</sup> ಜನರು ದಾವೀದನಿಗೆ - ನಿನ್ನ ಶತ್ರುವನ್ನು ನಿನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸಿಕೊಡುವೆನು; ಆಗ ನೀನು ಅವನನ್ನು ಮನಸ್ಸಿಗೆ ಬಂದಂತೆ ನಡಿಸಬಹುದು ಎಂದು ಯೆಹೋವನು ನಿನಗೆ ಹೇಳಿದ ಮಾತು ನೆರವೇರುವ ದಿವಸ ಇದೇ ಅಂದಾಗ ಅವನೆದ್ದು ಮೆಲ್ಲಗೆ ಹೋಗಿ ಸೌಲನ ನಿಲುವಂಗಿಯ ಮೂಲೆಯನ್ನು ಕತ್ತರಿಸಿಕೊಂಡನು.",
			"5": "<sup>5</sup> ಅನಂತರದಲ್ಲಿ ಸೌಲನ ನಿಲುವಂಗಿಯ ಮೂಲೆಯನ್ನು ಕತ್ತರಿಸಿದ್ದಕ್ಕಾಗಿ ಅವನ ಮನಸ್ಸಾಕ್ಷಿಯು ಅವನನ್ನು ಹಂಗಿಸತೊಡಗಿತು.",
			"6": "<sup>6</sup> ಅವನು ತನ್ನ ಜನರಿಗೆ - ಅವನು ಯೆಹೋವನಿಂದ ಅಭಿಷೇಕಿಸಲ್ಪಟ್ಟವನೂ ನನ್ನ ಒಡೆಯನೂ ಆಗಿದ್ದಾನೆ; ನಾನು ನಿಮ್ಮ ಮಾತು ಕೇಳಿ ಯೆಹೋವನ ಅಭಿಷಿಕ್ತನಿಗೆ ವಿರೋಧವಾಗಿ ಕೈಯೆತ್ತದಂತೆ ಯೆಹೋವನೇ ನನಗೆ ಅಡ್ಡಿಮಾಡಲಿ ಎಂದು ಹೇಳಿದನು.",
			"7": "<sup>7</sup> ಈ ಮಾತುಗಳಿಂದ ಸೌಲನಿಗೆ ವಿರೋಧವಾಗಿ ಏಳದಂತೆ ತನ್ನ ಜನರನ್ನು ತಡೆದನು. ಸೌಲನು ಗವಿಯಿಂದ ಹೊರಗೆ ಬಂದು ಸ್ವಲ್ಪ ಮುಂದೆ ಹೋದ ಮೇಲೆ",
			"8": "<sup>8</sup> ದಾವೀದನೂ ಹೊರಗೆ ಬಂದು - ಅರಸನೇ, ನನ್ನ ಒಡೆಯನೇ ಎಂದು ಅವನನ್ನು ಕೂಗಿದನು. ಸೌಲನು ಹಿಂದಿರುಗಿ ನೋಡಲು ಅವನಿಗೆ ಸಾಷ್ಟಾಂಗ ನಮಸ್ಕಾರಮಾಡಿ -",
			"9": "<sup>9</sup> ದಾವೀದನು ನಿನಗೆ ಕೇಡುಮಾಡಬೇಕೆಂದಿರುತ್ತಾನೆಂದು ಹೇಳುವವರ ಮಾತಿಗೆ ನೀನೇಕೆ ಕಿವಿಗೊಡುತ್ತೀ?",
			"10": "<sup>10</sup> ಈಹೊತ್ತು ಯೆಹೋವನು ಗವಿಯಲ್ಲಿ ನಿನ್ನನ್ನು ನನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸಿಕೊಟ್ಟಿದ್ದನೆಂಬದು ಈಗ ನಿನಗೆ ಗೊತ್ತಾಯಿತಲ್ಲವೋ? ನಿನ್ನನ್ನು ಕೊಂದುಬಿಡಬೇಕೆಂದು ಕೆಲವರು ನನಗೆ ಹೇಳಿದರು; ಆದರೆ ನಾನು ಅವರಿಗೆ - ಯೆಹೋವನ ಅಭಿಷಿಕ್ತನಾದ ನನ್ನ ಒಡೆಯನ ಮೇಲೆ ಕೈಹಾಕುವದಿಲ್ಲ ಎಂದು ಹೇಳಿ ನಿನ್ನನ್ನು ಉಳಿಸಿದೆನು.",
			"11": "<sup>11</sup> ನನ್ನ ತಂದೆಯೇ, ಇಗೋ ನೋಡು; ನನ್ನ ಕೈಯಲ್ಲಿ ನಿನ್ನ ನಿಲುವಂಗಿಯ ತುಂಡು ಇದೆ; ನಾನು ನಿನ್ನನ್ನು ಕೊಲ್ಲದೆ ನಿನ್ನ ನಿಲುವಂಗಿಯ ಮೂಲೆಯನ್ನು ಕತ್ತರಿಸಿಕೊಂಡೆನಷ್ಟೆ. ಇದರಿಂದ ನನ್ನಲ್ಲಿ ಯಾವ ದೋಷವೂ ಅಪರಾಧವೂ ಇರುವದಿಲ್ಲ. ನಾನು ನಿನಗೆ ವಿರೋಧವಾಗಿ ದ್ರೋಹಮಾಡಲಿಲ್ಲ ಎಂದು ತಿಳಿದುಕೋ. ಆದರೂ ನೀನು ನನ್ನ ಪ್ರಾಣಕ್ಕೆ ಹೊಂಚುಹಾಕುತ್ತಿರುವಿಯಲ್ಲಾ;",
			"12": "<sup>12</sup> ಯೆಹೋವನೇ ನಮ್ಮ ಉಭಯರ ವ್ಯಾಜ್ಯವನ್ನು ತೀರಿಸಲಿ; ಆತನೇ ನನಗೋಸ್ಕರ ನಿನಗೆ ಮುಯ್ಯಿಸಲ್ಲಿಸಲಿ; ನಾನಂತೂ ನಿನಗೆ ವಿರೋಧವಾಗಿ ಕೈಯೆತ್ತುವದಿಲ್ಲ.",
			"13": "<sup>13</sup> ಕೆಟ್ಟವರಿಂದಲೇ ಕೇಡು ಎಂಬದಾಗಿ ಹಿರಿಯರ ಗಾದೆಯುಂಟಷ್ಟೆ; ನಾನು ನಿನಗೆ ವಿರೋಧವಾಗಿ ಕೈಯೆತ್ತುವದಿಲ್ಲ.",
			"14": "<sup>14</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸೇ, ಯಾರನ್ನು ಹಿಂದಟ್ಟ ಹೊರಟಿದ್ದೀ? ಯಾರನ್ನು ಹಿಡಿಯಬೇಕೆಂದಿರುತ್ತೀ? ಸತ್ತ ನಾಯಿಯನ್ನೇ, ಒಂದು ಬಡ ಸೊಳ್ಳೆಯನ್ನೇ.",
			"15": "<sup>15</sup> ಯೆಹೋವನು ನ್ಯಾಯಾಧಿಪತಿಯಾಗಿ ನಮ್ಮಿಬ್ಬರ ವ್ಯಾಜ್ಯವನ್ನು ತೀರಿಸಲಿ; ಆತನೇ ನೋಡಿ ನನಗೋಸ್ಕರ ವಾದಿಸಿ ನನ್ನನ್ನು ನಿನ್ನ ಕೈಗೆ ತಪ್ಪಿಸಲಿ ಎಂದು ಹೇಳಿದನು.",
			"16": "<sup>16</sup> ದಾವೀದನ ಮಾತುಗಳು ಮುಗಿದನಂತರ ಸೌಲನು - ದಾವೀದನೇ, ನನ್ನ ಮಗನೇ, ಇದು ನಿನ್ನ ಸ್ವರವೋ ಅಂದು ಗಟ್ಟಿಯಾಗಿ ಅತ್ತನು.",
			"17": "<sup>17</sup> ಇದಲ್ಲದೆ ಅವನು ದಾವೀದನಿಗೆ - ನೀನು ನನಗಿಂತ ನೀತಿವಂತನು; ನಾನು ನಿನಗೆ ಕೇಡುಮಾಡಿದರೂ ನೀನು ನನಗೆ ಒಳ್ಳೇದನ್ನೇ ಮಾಡಿದಿ.",
			"18": "<sup>18</sup> ಯೆಹೋವನು ನನ್ನನ್ನು ನಿನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸಿಕೊಟ್ಟರೂ ನೀನು ನನ್ನನ್ನು ಕೊಲ್ಲಲಿಲ್ಲ; ಇದರಿಂದ ನೀನು ನನಗೆ ಹಿತವನ್ನೇ ಮಾಡುವಂಥವನೆಂಬದು ಈ ಹೊತ್ತು ಪ್ರಕಟವಾಯಿತು.",
			"19": "<sup>19</sup> ಯಾವನಾದರೂ ತನ್ನ ಕೈಗೆ ಸಿಕ್ಕಿದ ವೈರಿಯನ್ನು ಸುಕ್ಷೇಮದಿಂದ ಕಳುಹಿಸಿಬಿಡುವನೋ? ನೀನು ಈಹೊತ್ತು ನನಗೆ ಉಪಕಾರಮಾಡಿದ್ದಕ್ಕಾಗಿ ಯೆಹೋವನು ನಿನಗೆ ಉಪಕಾರಮಾಡಲಿ.",
			"20": "<sup>20</sup> ಕೇಳು, ನೀನು ಹೇಗೂ ಅರಸನಾಗುವಿಯೆಂದೂ ಇಸ್ರಾಯೇಲ್ ರಾಜ್ಯವು ನಿನ್ನಲ್ಲಿ ಸ್ಥಿರವಾಗುವದೆಂದೂ ಬಲ್ಲೆನು.",
			"21": "<sup>21</sup> ಹೀಗಿರುವದರಿಂದ ನೀನು ನನ್ನ ಸಂತಾನವನ್ನು ನಿರ್ಮೂಲ ಮಾಡುವದಿಲ್ಲವೆಂದೂ ನಮ್ಮ ಕುಲದಿಂದ ನನ್ನ ಹೆಸರನ್ನು ತೆಗೆದುಹಾಕುವದಿಲ್ಲವೆಂದೂ ಯೆಹೋವನ ಹೆಸರಿನಲ್ಲಿ ಪ್ರಮಾಣಮಾಡು ಎಂದು ಬೇಡಿಕೊಂಡ ಹಾಗೆ ದಾವೀದನು ಪ್ರಮಾಣಮಾಡಿದನು;",
			"22": "<sup>22</sup> ಅನಂತರ ಸೌಲನು ತನ್ನ ಮನೆಗೆ ಹೊರಟನು; ದಾವೀದನು ತನ್ನ ಜನರೊಡನೆ ಆಶ್ರಯಗಿರಿಗೆ ಹೋದನು."
		},
		"25": {
			"1": "<sup>1</sup> ಸಮುವೇಲನು ಮೃತಿಹೊಂದಿದನು. ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರೂ ಕೂಡಿ ಬಂದು ಅವನಿಗೋಸ್ಕರ ಗೋಳಾಡುತ್ತಾ ಅವನ ಶವವನ್ನು ರಾಮದಲ್ಲಿದ್ದ ಅವನ ಮನೆಯ ನಿವೇಶನದಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ದಾವೀದನು ಹೊರಟು ಪಾರಾನ್ ಅರಣ್ಯಕ್ಕೆ ಹೋದನು.",
			"2": "<sup>2</sup> ಕರ್ಮೆಲಿನಲ್ಲಿ ಸ್ವಾಸ್ತ್ಯವಿದ್ದಂಥ ಒಬ್ಬ ಮನುಷ್ಯನು ಮಾವೋನಿನಲ್ಲಿ ವಾಸಿಸುತ್ತಿದ್ದನು; ಅವನ ಹೆಸರು ನಾಬಾಲ;",
			"3": "<sup>3</sup> ಅವನ ಹೆಂಡತಿಯ ಹೆಸರು ಅಬೀಗೈಲ್. ಆಕೆಯು ಬಹುಬುದ್ಧಿವಂತೆಯೂ ಸುಂದರಿಯೂ ಆಗಿದ್ದಳು; ಕಾಲೇಬನ ವಂಶದವನಾದ ಆ ಮನುಷ್ಯನು ನಿಷ್ಠುರನೂ ದುಷ್ಕರ್ಮಿಯೂ ಆಗಿದ್ದನು. ಅವನು ಬಹು ಧನವಂತನು. ಅವನಿಗೆ ಮೂರು ಸಾವಿರ ಕುರಿಗಳೂ ಸಾವಿರ ಆಡುಗಳೂ ಇದ್ದವು. ಅವನು ಒಂದು ಸಾರಿ ಕರ್ಮೆಲಿನಲ್ಲಿ ಕುರಿಗಳ ಉಣ್ಣೆಯನ್ನು ಕತ್ತರಿಸುತ್ತಿದ್ದನು.",
			"4": "<sup>4</sup> ನಾಬಾಲನು ಉಣ್ಣೆಕತ್ತರಿಸುತ್ತಿದ್ದಾನೆಂಬ ವರ್ತಮಾನವು ಅಲ್ಲಿ ಅರಣ್ಯದಲ್ಲಿದ್ದ ದಾವೀದನಿಗೆ ಮುಟ್ಟಿದಾಗ ಅವನು",
			"5": "<sup>5</sup> ಹತ್ತು ಮಂದಿ ಸೇವಕರನ್ನು ಕರೆದು ಅವರಿಗೆ - ನೀವು ಕರ್ಮೆಲಿನಲ್ಲಿರುವ ನಮ್ಮ ಸಂಬಂಧಿಕನಾದ ನಾಬಾಲನ ಬಳಿಗೆ ಹೋಗಿ ಅವನ ಕ್ಷೇಮ ಸಮಾಚಾರವನ್ನು ವಿಚಾರಿಸಿ ಅವನಿಗೆ ನನ್ನ ಹೆಸರಿನಲ್ಲಿ -",
			"6": "<sup>6</sup> ನಿನಗೂ ನಿನ್ನ ಕುಟುಂಬಕ್ಕೂ ನಿನ್ನ ಸರ್ವಸಂಪತ್ತಿಗೂ ಶುಭವಾಗಲಿ.",
			"7": "<sup>7</sup> ನಿನ್ನ ಕುರಿಗಳ ಉಣ್ಣೆಯನ್ನು ಕತ್ತರಿಸುವ ಕೆಲಸವು ನಡೆಯುತ್ತದೆಂಬ ವರ್ತಮಾನವನ್ನು ಕೇಳಿದ್ದೇನೆ. ನಿನ್ನ ಕುರುಬರು ನಮ್ಮೊಡನೆ ಕರ್ಮೆಲಿನಲ್ಲಿದ್ದ ಕಾಲವೆಲ್ಲಾ ನಾವು ಅವರನ್ನು ತೊಂದರೆಪಡಿಸಲಿಲ್ಲ; ಅವರಿಗೇನೂ ನಷ್ಟವಾಗಲಿಲ್ಲ.",
			"8": "<sup>8</sup> ನಿನ್ನ ಆಳುಗಳನ್ನು ಕೇಳು, ಅವರೇ ಹೇಳುವರು. ಹೀಗಿರುವದರಿಂದ ಶುಭಕಾಲದಲ್ಲಿ ನಿನ್ನ ಬಳಿಗೆ ಬಂದಿರುವ ನನ್ನ ಸೇವಕರಿಗೆ ನಿನ್ನ ದೃಷ್ಟಿಯಲ್ಲಿ ದಯೆದೊರಕಲಿ. ಕೃಪೆಮಾಡಿ ನಿನ್ನ ಸೇವಕರಿಗೂ ಮಗನಾದ ದಾವೀದನಿಗೂ ನಿನಗಿರುವದರಲ್ಲಿ ಕೊಡು ಎಂದು ಹೇಳಿರಿ ಅಂದು ಅವರನ್ನು ಕಳುಹಿಸಿದನು.",
			"9": "<sup>9</sup> ದಾವೀದನ ಸೇವಕರು ಹೋಗಿ ಅವನ ಹೆಸರಿನಲ್ಲಿ ಈ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ನಾಬಾಲನಿಗೆ ತಿಳಿಸಿ ಉತ್ತರಕಾಯುತ್ತಿರಲು",
			"10": "<sup>10</sup> ನಾಬಾಲನು ಅವರಿಗೆ - ದಾವೀದನಾರು? ಇಷಯನ ಮಗನಾರು? ಯಜಮಾನರನ್ನು ಬಿಟ್ಟು ಓಡಿಹೋದ ಸೇವಕರು ಈಗಿನ ಕಾಲದಲ್ಲಿ ಎಷ್ಟು ಮಂದಿಯಿಲ್ಲ!",
			"11": "<sup>11</sup> ಉಣ್ಣೆ ಕತ್ತರಿಸುವವರಿಗೋಸ್ಕರ ನಾನು ಸಿದ್ಧಮಾಡಿಸಿದ ಮಾಂಸವನ್ನೂ ಆಹಾರಪಾನಗಳನ್ನೂ ಎತ್ತಿ ಎಲ್ಲಿಂದಲೋ ಬಂದವರಿಗೆ ಕೊಟ್ಟುಬಿಡಬೇಕೋ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"12": "<sup>12</sup> ಆ ಸೇವಕರು ಹಿಂದಿರುಗಿ ತಮ್ಮ ದಾರಿ ಹಿಡಿದು ದಾವೀದನ ಬಳಿಗೆ ಬಂದು ಅವನಿಗೆ ಎಲ್ಲವನ್ನೂ ತಿಳಿಸಲಾಗಿ",
			"13": "<sup>13</sup> ಅವನು ತನ್ನ ಜನರಿಗೆ - ಕತ್ತಿಯನ್ನು ಸೊಂಟಕ್ಕೆ ಕಟ್ಟಿಕೊಳ್ಳಿರಿ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು; ಅವರೆಲ್ಲರೂ ಕಟ್ಟಿಕೊಂಡರು. ದಾವೀದನೂ ಕಟ್ಟಿಕೊಂಡನು. ಹೆಚ್ಚುಕಡಿಮೆ ನಾನೂರು ಮಂದಿ ದಾವೀದನ ಜೊತೆಯಲ್ಲಿ ಹೋದರು; ಇನ್ನೂರು ಮಂದಿ ಸಾಮಾನುಗಳ ಬಳಿಯಲ್ಲಿ ಇದ್ದರು.",
			"14": "<sup>14</sup> ನಾಬಾಲನ ಸೇವಕರಲ್ಲೊಬ್ಬನು ಅವನ ಹೆಂಡತಿಯಾದ ಅಬೀಗೈಲಳಿಗೆ - ಅರಣ್ಯದಲ್ಲಿರುವ ದಾವೀದನು ನಮ್ಮ ದಣಿಯ ಕ್ಷೇಮವಿಚಾರಕ್ಕಾಗಿ ದೂತರನ್ನು ಕಳುಹಿಸಲು ದಣಿಯು ಬಂದವರ ಮೇಲೆ ಬಿದ್ದು ಬೈದನು.",
			"15": "<sup>15</sup> ಆ ಜನರು ನಮಗೆ ಬಹಳ ಉಪಕಾರಮಾಡಿದವರು; ನಾವು ಅವರೊಡನೆ ಅಡವಿಯಲ್ಲಿದ್ದ ಕಾಲವೆಲ್ಲಾ ಏನೂ ತೊಂದರೆಪಡಲಿಲ್ಲ. ನಷ್ಟಹೊಂದಲಿಲ್ಲ.",
			"16": "<sup>16</sup> ನಾವು ಕುರಿಗಳನ್ನು ಮೇಯಿಸುವವರಾಗಿ ಅವರೊಡನೆ ಇದ್ದಾಗ ಅವರು ಹಗಲಿರುಳು ನಮಗೆ ಕಾವಲುಗೋಡೆಯಂತಿದ್ದರು.",
			"17": "<sup>17</sup> ಈಗ ಮಾಡಬೇಕಾದದ್ದನ್ನು ನೀನೇ ಆಲೋಚಿಸಿ ಗೊತ್ತುಮಾಡು. ನಮ್ಮ ಯಜಮಾನನಿಗೂ ಅವನ ಕುಟುಂಬದವರೆಲ್ಲರಿಗೂ ಕೇಡು ಹತ್ತಿರವಾಗಿದೆ; ಮೂರ್ಖನಾದ ಅವನೊಡನೆ ಮಾತಾಡುವದಸಾಧ್ಯ ಎಂದು ಹೇಳಿದನು.",
			"18": "<sup>18</sup> ಆಗ ಅಬೀಗೈಲಳು ಶೀಘ್ರವಾಗಿ ಇನ್ನೂರು ರೊಟ್ಟಿ, ಎರಡು ಬುದ್ದಲಿ ದ್ರಾಕ್ಷಾರಸ, ಸಿದ್ಧಮಾಡಿದ ಐದು ಕುರಿಗಳ ಮಾಂಸ, ಐವತ್ತು ಸೇರು ಹುರಿಗಾಳು, ಒಣಗಿದ ನೂರು ದ್ರಾಕ್ಷೇಗೊಂಚಲುಗಳು, ಅಂಜೂರ ಹಣ್ಣುಗಳ ಇನ್ನೂರು ಉಂಡೆಗಳು ಇವುಗಳನ್ನು ಕತ್ತೆಗಳ ಮೇಲೆ ಹೇರಿಸಿ ತನ್ನ ಸೇವಕರಿಗೆ -",
			"19": "<sup>19</sup> ನೀವು ಮುಂದೆ ನಡೆಯಿರಿ, ನಾನು ಹಿಂದೆ ಬರುತ್ತೇನೆ ಎಂದು ಹೇಳಿ ಅವರನ್ನು ಕಳುಹಿಸಿದಳು; ಗಂಡನಾದ ನಾಬಾಲನಿಗೆ ಏನೂ ತಿಳಿಸಲಿಲ್ಲ.",
			"20": "<sup>20</sup> ಆಕೆ ಕತ್ತೆಯ ಮೇಲೆ ಕೂತು ಗುಡ್ಡದ ಮರೆಯಲ್ಲಿ ಹೋಗುತ್ತಿರುವಾಗ ದಾವೀದನೂ ಅವನ ಜನರೂ ಆ ಕಡೆಯಿಂದ ಗಟ್ಟಾ ಇಳಿದುಬರಲು ಆಕೆಯು ಅವರನ್ನು ಎದುರುಗೊಂಡಳು.",
			"21": "<sup>21</sup> ಅಷ್ಟರಲ್ಲಿ ದಾವೀದನು - ನಾನು ಅಡವಿಯಲ್ಲಿ ಈ ಮನುಷ್ಯನ ಆಸ್ತಿಯಲ್ಲಿ ಏನೂ ನಷ್ಟವಾಗದಂತೆ ಕಾಪಾಡಿದ್ದು ವ್ಯರ್ಥವಾಯಿತು. ಅವನು ಉಪಕಾರಕ್ಕೆ ಅಪಕಾರಮಾಡಿದನು.",
			"22": "<sup>22</sup> ಬೆಳಗಾಗುವಷ್ಟರಲ್ಲಿ ಅವನ ಜನರೊಳಗೆ ಒಬ್ಬ ಗಂಡಸಾದರೂ ಉಳಿದರೆ ದೇವರು ದಾವೀದನಿಗೆ ಬೇಕಾದದ್ದನ್ನು ಮಾಡಲಿ ಎಂದು ಪ್ರತಿಜ್ಞೆಮಾಡಿದ್ದನು.",
			"23": "<sup>23</sup> ಅಬೀಗೈಲಳು ದಾವೀದನನ್ನು ಕಂಡ ಕೂಡಲೆ ಕತ್ತೆಯಿಂದಿಳಿದು ಅವನಿಗೆ ಸಾಷ್ಟಾಂಗವಾಗಿ ನಮಸ್ಕರಿಸಿದಳು.",
			"24": "<sup>24</sup> ಆಮೇಲೆ ಅವನ ಪಾದಗಳ ಮೇಲೆ ಬಿದ್ದು ಅವನಿಗೆ - ಸ್ವಾಮೀ, ಅಪರಾಧವು ನನ್ನ ಮೇಲಿರಲಿ; ನಿನ್ನ ದಾಸಿಯು ಮಾತಾಡುವದಕ್ಕೆ ಅಪ್ಪಣೆಯಾಗಲಿ; ಅವಳ ಮಾತನ್ನು ಲಾಲಿಸಬೇಕು.",
			"25": "<sup>25</sup> ಸ್ವಾಮಿಯು ಮೂರ್ಖನಾದ ಆ ನಾಬಾಲನನ್ನು ಲಕ್ಷಿಸದಿರಲಿ. ಅವನ ಹೆಸರು ನಾಬಾಲ್; ಅದಕ್ಕೆ ತಕ್ಕಂತೆ ಅವನು ಮೂರ್ಖನೇ ಆಗಿರುತ್ತಾನೆ. ನಿನ್ನ ದಾಸಿಯಾದ ನಾನು ಸ್ವಾಮಿಯು ಕಳುಹಿಸಿದ ಸೇವಕರನ್ನು ನೋಡಲಿಲ್ಲ.",
			"26": "<sup>26</sup> ಸ್ವಾಮೀ, ನೀನು ಸ್ವಹಸ್ತದಿಂದ ಮುಯ್ಯಿತೀರಿಸಿ ರಕ್ತಾಪರಾಧಕ್ಕೆ ಗುರಿಯಾಗದಂತೆ ಯೆಹೋವನು ನಿನ್ನನ್ನು ಕಾಪಾಡಿದ್ದಾನೆ. ಯೆಹೋವನ ಆಣೆ, ನಿನ್ನ ಜೀವದಾಣೆ, ನಿನ್ನ ವಿರೋಧಿಗಳೂ ನಿನಗೆ ಕೇಡು ಬಗೆಯುವವರೂ ನಾಬಾಲನ ಗತಿಯನ್ನು ಹೊಂದಲಿ.",
			"27": "<sup>27</sup> ನಿನ್ನ ದಾಸಿಯು ತಂದ ಈ ಕಾಣಿಕೆಯು ಸ್ವಾಮಿಯ ಸೇವಕರಿಗೆ ಸಲ್ಲಲಿ.",
			"28": "<sup>28</sup> ನಿನ್ನ ದಾಸಿಯ ಅಪರಾಧಕ್ಕೆ ಕ್ಷಮಾಪಣೆಯಾಗಲಿ; ಸ್ವಾಮಿಯೇ, ನೀನು ಯೆಹೋವನ ಶತ್ರುಗಳೊಡನೆ ಯುದ್ಧಮಾಡುವದರಿಂದ ಆತನು ನಿನ್ನ ಮನೆಯನ್ನು ಶಾಶ್ವತವಾಗಿ ಸ್ಥಿರಪಡಿಸುವನು. ಆದದರಿಂದ ನಿನ್ನ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ನಿನ್ನಲ್ಲಿ ಕೆಟ್ಟತನವು ಕಾಣದಿರಲಿ;",
			"29": "<sup>29</sup> ಯಾವನಾದರೂ ನನ್ನ ಸ್ವಾಮಿಯಾದ ನಿನ್ನನ್ನು ಹಿಂಸಿಸಿ ಜೀವತಗೆಯಬೇಕೆಂದಿರುವಾಗ ಆ ನಿನ್ನ ಜೀವವು ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನ ರಕ್ಷಣೆಯಲ್ಲಿರುವ ಜೀವನಿಕ್ಷೇಪದಲ್ಲಿ ಭದ್ರವಾಗಿರಲಿ; ಆದರೆ ಆತನು ನಿನ್ನ ಶತ್ರುಗಳ ಜೀವವನ್ನು ಕವಣೆಯ ಕಲ್ಲನ್ನೋ ಎಂಬಂತೆ ಎಸೆದುಬಿಡಲಿ.",
			"30": "<sup>30</sup> ಯೆಹೋವನು ನನ್ನ ಸ್ವಾಮಿಯಾದ ನಿನಗೆ ವಾಗ್ದಾನ ಮಾಡಿದ ಎಲ್ಲಾ ಸೌಭಾಗ್ಯವನ್ನು ದಯಪಾಲಿಸಿ ನಿನ್ನನ್ನು ಇಸ್ರಾಯೇಲ್ ಪ್ರಭುವನ್ನಾಗಿ ಮಾಡಿದ ಮೇಲೆ",
			"31": "<sup>31</sup> ನಿರಪರಾಧಿಯ ರಕ್ತವನ್ನು ಸುರಿಸಿ ಮುಯ್ಯಿತೀರಿಸಿದ್ದರಿಂದುಂಟಾಗುವ ಪಶ್ಚಾತ್ತಾಪ ಮನೋವ್ಯಥೆಗಳಿಗೆ ಕಾರಣವಿರುವದಿಲ್ಲ. ಆತನು ನಿನ್ನನ್ನು ಮಹಾಪದವಿಗೆ ತರುವಾಗ ನಿನ್ನ ದಾಸಿಯಾದ ನನ್ನನ್ನು ಮರೆತುಬಿಡಬೇಡ ಅಂದಳು.",
			"32": "<sup>32</sup> ಆಗ ದಾವೀದನು ಅಬೀಗೈಲಳಿಗೆ - ಈಹೊತ್ತು ನನ್ನನ್ನು ಎದುರುಗೊಳ್ಳುವದಕ್ಕಾಗಿ ನಿನ್ನನ್ನು ಕಳುಹಿಸಿದ ಇಸ್ರಾಯೇಲ್‍ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಸ್ತೋತ್ರವಾಗಲಿ.",
			"33": "<sup>33</sup> ಸ್ವಹಸ್ತದಿಂದ ಮುಯ್ಯಿತೀರಿಸಿ ರಕ್ತಾಪರಾಧಕ್ಕೆ ಗುರಿಯಾಗದಂತೆ ನನ್ನನ್ನು ತಡೆದ ನೀನೂ ನಿನ್ನ ಬುದ್ಧಿಯೂ ಸ್ತೋತ್ರಾರ್ಹವೇ ಸರಿ.",
			"34": "<sup>34</sup> ನಿನಗೆ ಕೇಡುಮಾಡದಂತೆ ನನ್ನನ್ನು ತಡೆದ ಇಸ್ರಾಯೇಲ್‍ದೇವರಾದ ಯೆಹೋವನ ಆಣೆ, ನೀನು ಬೇಗನೆ ಬಂದು ನನ್ನನ್ನು ಎದುರುಗೊಳ್ಳದಿದ್ದರೆ ನಿಜವಾಗಿ ನಾಳೆ ಬೆಳಗಾಗುವಷ್ಟರಲ್ಲಿ ನಾಬಾಲನವರಲ್ಲಿ ಒಬ್ಬ ಗಂಡಸಾದರೂ ಉಳಿಯುತ್ತಿದ್ದಿಲ್ಲ ಎಂದು ಹೇಳಿ",
			"35": "<sup>35</sup> ಆಕೆ ತಂದ ಪದಾರ್ಥಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಆಕೆಗೆ - ಸಮಾಧಾನದಿಂದ ಮನೆಗೆ ಹೋಗು; ನಿನ್ನನ್ನು ನೋಡಿ ನಿನ್ನ ವಿಜ್ಞಾಪನೆಯನ್ನು ಲಾಲಿಸಿದ್ದೇನೆ ಅಂದನು.",
			"36": "<sup>36</sup> ಅಬೀಗೈಲಳು ನಾಬಾಲನ ಬಳಿಗೆ ಬಂದಳು. ಅವನು ತನ್ನ ಮನೆಯಲ್ಲಿ ರಾಜರಂತೆ ಒಂದು ದೊಡ್ಡ ಔತಣವನ್ನು ಮಾಡಿಸಿ ಬಹಳವಾಗಿ ಕುಡಿದು ಬಹು ಸಂಭ್ರಮದಿಂದಿದ್ದ ಕಾರಣ ಈಕೆಯು ಮರುದಿವಸದವರೆಗೆ ಇದರ ವಿಷಯವಾಗಿ ಏನೂ ಹೇಳಲಿಲ್ಲ.",
			"37": "<sup>37</sup> ಮರುದಿನ ಅಮಲಿಳಿದನಂತರ ಈಕೆಯು ಈ ವರ್ತಮಾನವನ್ನು ಅವನಿಗೆ ತಿಳಿಸಲಾಗಿ ಅವನ ಹೃದಯವು ನಿಂತುಹೋಯಿತು, ಅವನು ಸ್ತಬ್ಧನಾದನು.",
			"38": "<sup>38</sup> ಸುಮಾರು ಹತ್ತು ದಿನಗಳಾದ ನಂತರ ನಾಬಾಲನು ಯೆಹೋವನಿಂದ ಹತನಾಗಿ ಸತ್ತನು.",
			"39": "<sup>39</sup> ನಾಬಾಲನು ಸತ್ತನೆಂಬ ವರ್ತಮಾನವನ್ನು ದಾವೀದನು ಕೇಳಿ - ನನಗೆ ಅಪಮಾನ ಮಾಡಿದ ನಾಬಾಲನಿಗೆ ಮುಯ್ಯಿತೀರಿಸಿದಂಥ ಯೆಹೋವನಿಗೆ ಸ್ತೋತ್ರವಾಗಲಿ. ಆತನು ನನ್ನನ್ನು ಕೆಟ್ಟತನಕ್ಕೆ ದೂರಮಾಡಿ ನಾಬಾಲನ ಕೆಟ್ಟತನವನ್ನು ಅವನ ತಲೆಯ ಮೇಲೆಯೇ ಹೊರಿಸಿದನಲ್ಲಾ ಅಂದನು. ಅನಂತರ ಅವನು ಅಬೀಗೈಲಳು ತನಗೆ ಹೆಂಡತಿಯಾಗಬೇಕೆಂದು ದೂತರನ್ನು ಕಳುಹಿಸಿದನು;",
			"40": "<sup>40</sup> ಅವರು ಕರ್ಮೆಲಿನಲ್ಲಿದ್ದ ಅಬೀಗೈಲಳ ಬಳಿಗೆ ಹೋಗಿ ಆಕೆಗೆ - ದಾವೀದನು ನಿನ್ನನ್ನು ತನಗೆ ಹೆಂಡತಿಯಾಗುವದಕ್ಕೆ ಕರಕೊಂಡು ಬರಬೇಕೆಂದು ನಮ್ಮನ್ನು ಕಳುಹಿಸಿದ್ದಾನೆ ಅಂದಾಗ",
			"41": "<sup>41</sup> ಆಕೆ ಎದ್ದು ನಮಸ್ಕಾರಮಾಡಿ ಮಾತಾಡಿಸಿದವನಿಗೆ - ನಿನ್ನ ದಾಸಿಯಾದ ನಾನು ನನ್ನ ಸ್ವಾಮಿಯ ಸೇವಕರ ಪಾದಗಳನ್ನು ತೊಳೆಯುವ ಸೇವಕಿಯಾಗುವದಕ್ಕೆ ಸಿದ್ಧಳಾಗಿದ್ದೇನೆ ಎಂದು ಉತ್ತರಕೊಟ್ಟು",
			"42": "<sup>42</sup> ಬೇಗನೆ ಒಂದು ಕತ್ತೆಯನ್ನು ಹತ್ತಿ ತನ್ನ ಐದು ಮಂದಿ ದಾಸಿಯರ ಸಹಿತವಾಗಿ ದಾವೀದನ ಸೇವಕರ ಜೊತೆಯಲ್ಲಿ ಹೊರಟುಹೋಗಿ ಅವನ ಹೆಂಡತಿಯಾದಳು.",
			"43": "<sup>43</sup> ಇದಲ್ಲದೆ ದಾವೀದನು ಇಜ್ರೇಲಿನವಳಾದ ಅಹೀನೋವಮಳನ್ನು ಮದುವೆಮಾಡಿಕೊಂಡಿದ್ದನು. ಅವರಿಬ್ಬರೂ ಅವನ ಹೆಂಡತಿಯರಾದರು.",
			"44": "<sup>44</sup> ಸೌಲನು ದಾವೀದನ ಹೆಂಡತಿಯಾಗಿದ್ದ ಮೀಕಲಳೆಂಬ ತನ್ನ ಮಗಳನ್ನು ಗಲ್ಲೀಮ್ ಊರಿನ ಲಯಿಷನ ಮಗನಾದ ಪಲ್ಟೀ ಎಂಬವನಿಗೆ ಮದುವೆಮಾಡಿಕೊಟ್ಟನು."
		},
		"26": {
			"1": "<sup>1</sup> ಜೀಫ್ಯರು ಗಿಬೆಯದಲ್ಲಿದ್ದ ಸೌಲನ ಬಳಿಗೆ ಬಂದು ಅವನಿಗೆ - ದಾವೀದನು ಅರಣ್ಯದ ಮೂಡಣ ದಿಕ್ಕಿನಲ್ಲಿರುವ ಹಕೀಲಾ ಬೆಟ್ಟದಲ್ಲಿ ಅಡಗಿಕೊಂಡಿರುವದು ನಿನಗೆ ಗೊತ್ತಿಲ್ಲವೋ ಅಂದರು.",
			"2": "<sup>2</sup> ಆಗ ಸೌಲನು ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಶ್ರೇಷ್ಠರಾದ ಮೂರು ಸಾವಿರ ಮಂದಿ ಸೈನಿಕರನ್ನು ಆರಿಸಿಕೊಂಡು ದಾವೀದನನ್ನು ಹುಡುಕುವದಕ್ಕೋಸ್ಕರ ಜೀಫ್ ಅರಣ್ಯಕ್ಕೆ ಹೋಗಿ",
			"3": "<sup>3</sup> ಅದರ ಮೂಡಣದಿಕ್ಕಿನಲ್ಲಿ ದಾರಿಯ ಬಳಿಯಲ್ಲಿರುವ ಹಕೀಲಾ ಬೆಟ್ಟದಲ್ಲಿ ಪಾಳೆಯಮಾಡಿಕೊಂಡನು. ಸೌಲನು ತನ್ನನ್ನು ಹಿಡಿಯುವದಕ್ಕೋಸ್ಕರ ಅರಣ್ಯಕ್ಕೆ ಬಂದಿದ್ದಾನೆಂಬ ವರ್ತಮಾನವು ಅಲ್ಲಿಯೇ ಇದ್ದ ದಾವೀದನಿಗೆ ಮುಟ್ಟಲು",
			"4": "<sup>4</sup> ಅವನು ಕೂಡಲೆ ಗೂಢಚಾರರನ್ನು ಕಳುಹಿಸಿ ಸೌಲನು ಇಳುಕೊಂಡಿದ್ದ ಸ್ಥಳವನ್ನು ಗೊತ್ತುಮಾಡಿಕೊಂಡನು.",
			"5": "<sup>5</sup> ಅನಂತರ ತಾನಾಗಿಯೇ ಸೌಲನ ಪಾಳೆಯದ ಹತ್ತಿರ ಹೋಗಿ ಸೌಲನೂ ನೇರನ ಮಗನಾದ ಅವನ ಸೇನಾಪತಿ ಅಬ್ನೇರನೂ ಮಲಗಿದ್ದ ಸ್ಥಳಗಳನ್ನು ಕಂಡುಹಿಡಿದನು. ಸೌಲನು ಬಂಡಿಗಳನ್ನು ಬಿಟ್ಟಿರುವ ಸ್ಥಳಮಧ್ಯದಲ್ಲಿ ಮಲಗಿದ್ದನು; ಅವನ ಸುತ್ತಲೂ ಸೈನಿಕರು ಇದ್ದರು.",
			"6": "<sup>6</sup> ಆಗ ದಾವೀದನು ಹಿತ್ತಿಯನಾದ ಅಹೀಮೆಲೆಕನನ್ನೂ ಚೆರೂಯಳ ಮಗನೂ ಯೋವಾಬನ ತಮ್ಮನೂ ಆದ ಅಬೀಷೈಯನ್ನೂ ಕುರಿತು - ನಿಮ್ಮಲ್ಲಿ ನನ್ನ ಸಂಗಡ ಸೌಲನ ಪಾಳೆಯಕ್ಕೆ ಯಾರು ಬರುವಿರಿ ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ ಅಬೀಷೈಯು - ನಾನು ಬರುತ್ತೇನೆ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"7": "<sup>7</sup> ದಾವೀದನೂ ಅಬೀಷೈಯೂ ರಾತ್ರಿಯಲ್ಲಿ ಆ ಪಾಳೆಯಕ್ಕೆ ಹೋದಾಗ ಸೌಲನು ಬಂಡಿಗಳ ಗುಂಪಿನ ಮಧ್ಯದಲ್ಲಿ ಮಲಗಿಕೊಂಡು ನಿದ್ರೆಮಾಡುತ್ತಿದ್ದನು. ಅವನ ಬರ್ಜಿಯು ಅವನ ತಲೆಯ ಹತ್ತಿರ ನೆಲದಲ್ಲಿ ನೆಡಲ್ಪಟ್ಟಿತ್ತು. ಅಬ್ನೇರನೂ ಸೈನಿಕರೂ ಅವನ ಸುತ್ತಲೂ ಮಲಗಿದ್ದರು.",
			"8": "<sup>8</sup> ಅಬೀಷೈಯು ದಾವೀದನಿಗೆ - ದೇವರು ಈ ಹೊತ್ತು ನಿನ್ನ ವೈರಿಯನ್ನು ನಿನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸಿಕೊಟ್ಟಿದ್ದಾನೆ; ಅಪ್ಪಣೆಯಾಗಲಿ, ನಾನು ಬರ್ಜಿಯಿಂದ ಒಂದೇ ಪೆಟ್ಟಿನಲ್ಲಿ ಅವನನ್ನು ನೆಲಕ್ಕೆ ಹತ್ತಿಕೊಳ್ಳುವಂತೆ ತಿವಿಯುವೆನು; ಎರಡನೆಯ ಸಾರಿ ಹೊಡೆಯುವದು ಅವಶ್ಯವಿಲ್ಲ ಎಂದು ಹೇಳಿದನು.",
			"9": "<sup>9</sup> ಆದರೆ ದಾವೀದನು - ಅವನನ್ನು ಕೊಲ್ಲಬೇಡ; ಯೆಹೋವನ ಅಭಿಷಿಕ್ತನಿಗೆ ವಿರೋಧವಾಗಿ ಕೈಯೆತ್ತುವ ಯಾವನಾದರೂ ನಿರಪರಾಧಿಯೆಂದು ಎಣಿಸಲ್ಪಡುವನೋ ಅಂದನು.",
			"10": "<sup>10</sup> ಇದಲ್ಲದೆ ದಾವೀದನು - ಯೆಹೋವನಾಣೆ, ಅವನು ಯೆಹೋವನಿಂದ ಸಾಯುವನು; ಇಲ್ಲವೆ ಕಾಲತುಂಬಿ ಮೃತಿಹೊಂದುವನು; ಅಥವಾ ಯುದ್ಧದಲ್ಲಿ ಮಡಿಯುವನು.",
			"11": "<sup>11</sup> ತನ್ನ ಅಭಿಷಿಕ್ತನಿಗೆ ವಿರೋಧವಾಗಿ ಕೈಯೆತ್ತದಂತೆ ಯೆಹೋವನೇ ನನ್ನನ್ನು ತಡೆಯಲಿ. ಈಗ ಅವನ ತಲೆಯ ಬಳಿಯಲ್ಲಿರುವ ಬರ್ಜಿಯನ್ನೂ ತಂಬಿಗೆಯನ್ನೂ ತೆಗೆದುಕೊಂಡು ಹೋಗೋಣ ಎಂದು ಹೇಳಿ",
			"12": "<sup>12</sup> ಸೌಲನ ತಲೆಗಿಂಬಿನ ಬಳಿಯಲ್ಲಿದ್ದ ಬರ್ಜಿ ತಂಬಿಗೆಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಹೋದನು. ಯಾರೂ ಕಾಣಲಿಲ್ಲ, ಯಾರಿಗೂ ಗೊತ್ತಾಗಲಿಲ್ಲ; ಯಾರೂ ಎಚ್ಚರವಾಗಲಿಲ್ಲ. ಯಾಕಂದರೆ ಯೆಹೋವನು ಅವರಿಗೆ ಗಾಢನಿದ್ರೆಯನ್ನು ಬರಮಾಡಿದ್ದನು; ಒಬ್ಬರಿಗೂ ಎಚ್ಚರವಿಲ್ಲದೆ ಎಲ್ಲರೂ ನಿದ್ರೆಮಾಡುತ್ತಿದ್ದರು.",
			"13": "<sup>13</sup> ದಾವೀದನು ತನಗೂ ಪಾಳೆಯಕ್ಕೂ ತಕ್ಕಷ್ಟು ಅಂತರವಿರುವ ಹಾಗೆ ಸ್ವಲ್ಪ ದೂರಕ್ಕೆ ಹೋಗಿ ಗುಡ್ಡವನ್ನೇರಿ ತುದಿಯಲ್ಲಿ ನಿಂತು -",
			"14": "<sup>14</sup> ಅಬ್ನೇರನೇ, ಕಿವಿಗೊಡುವದಿಲ್ಲವೋ ಎಂದು ನೇರನ ಮಗನಾದ ಅವನಿಗೂ ಸೈನಿಕರಿಗೂ ಕೇಳಿಸುವಂತೆ ಕೂಗಲು ಅಬ್ನೇರನು - ಅರಸನನ್ನು ಕೂಗುವ ನೀನಾರು ಎಂದು ಕೇಳಿದನು.",
			"15": "<sup>15</sup> ದಾವೀದನು ಅಬ್ನೇರನಿಗೆ - ನೀನು ಶೂರನಲ್ಲವೋ? ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ನಿನಗೆ ಸಮಾನರಾರು! ನೀನು ನಿನ್ನ ಒಡೆಯನಾದ ಅರಸನನ್ನು ಯಾಕೆ ಕಾಯಲಿಲ್ಲ? ಜನರಲ್ಲೊಬ್ಬನು ಒಳಗೆ ಹೊಕ್ಕು ನಿನ್ನ ಒಡೆಯನಾದ ಅರಸನನ್ನು ಕೊಲ್ಲಬೇಕೆಂದಿದ್ದನು.",
			"16": "<sup>16</sup> ನೀನು ಹೀಗೆ ಮಾಡುವದು ಸರಿಯಲ್ಲ. ಯೆಹೋವನಾಣೆ, ಆತನ ಅಭಿಷಿಕ್ತನಾಗಿರುವ ನಿಮ್ಮ ಒಡೆಯನನ್ನು ಕಾಯದಿರುವ ನೀವು ಮರಣಕ್ಕೆ ಪಾತ್ರರೇ ಹೌದು. ಅರಸನ ತಲೆಯ ಬಳಿಯಲ್ಲಿದ್ದ ಬರ್ಜಿಯೂ ತಂಬಿಗೆಯೂ ಏನಾದವೋ ನೋಡು ಎಂದು ಕೂಗಿ ಹೇಳಿದನು.",
			"17": "<sup>17</sup> ಸೌಲನು ದಾವೀದನ ಸ್ವರದ ಗುರುತು ಹಿಡಿದು ಅವನನ್ನು - ದಾವೀದನೇ, ನನ್ನ ಮಗನೇ, ಇದು ನಿನ್ನ ಸ್ವರವೋ ಎಂದು ಕೇಳಲು ಅವನು - ಅರಸನೇ, ನನ್ನ ಒಡೆಯನೇ, ಹೌದು ನನ್ನ ಸ್ವರವೇ;",
			"18": "<sup>18</sup> ನನ್ನ ಸ್ವಾಮಿಯು ತನ್ನ ಸೇವಕನನ್ನು ಈ ಪ್ರಕಾರ ಹಿಂಸಿಸುವದೇಕೆ? ನಾನೇನು ಮಾಡಿದೆನು? ಯಾವ ಪಾಪಕ್ಕೆ ಕೈಹಾಕಿದೆನು?",
			"19": "<sup>19</sup> ನನ್ನ ಅರಸನಾದ ಒಡೆಯನು ದಯವಿಟ್ಟು ತನ್ನ ಸೇವಕನ ಮಾತುಗಳನ್ನು ಲಾಲಿಸಬೇಕು. ನಿನ್ನನ್ನು ನನಗೆ ವಿರೋಧವಾಗಿ ಎಬ್ಬಿಸಿದವನು ಯೆಹೋವನೇ ಆಗಿರುವ ಪಕ್ಷದಲ್ಲಿ ಆತನಿಗೆ ಗಮಗಮಿಸುವ ನೈವೇದ್ಯವನ್ನು ಸಮರ್ಪಿಸಬೇಕು; ಮನುಷ್ಯರಾಗಿದ್ದರೆ ಈಗ ಅವರು ನನಗೆ ಯೆಹೋವನ ಸ್ವಾಸ್ತ್ಯದಲ್ಲಿ ಪಾಲುಸಿಕ್ಕದಂತೆ - ಹೋಗಿ ಅನ್ಯದೇವತೆಗಳನ್ನು ಸೇವಿಸು ಎಂದು ನನ್ನನ್ನು ತಳ್ಳಿಬಿಟ್ಟಿದ್ದಾರಾದದರಿಂದ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ಅವರು ಶಾಪಗ್ರಸ್ತರಾಗಿರಲಿ.",
			"20": "<sup>20</sup> ಯೆಹೋವನ ಸಾನ್ನಿಧ್ಯವಿಲ್ಲದಿರುವ ದೇಶದಲ್ಲಿ ನನ್ನ ರಕ್ತವು ಸುರಿಸಲ್ಪಡದಿರಲಿ. ಅಯ್ಯೋ, ಗುಡ್ಡಗಳಲ್ಲಿ ಕೌಜುಗವನ್ನು ಹಿಡಿಯ ಹೊರಟ ಬೇಟೆಗಾರನೋ ಎಂಬಂತೆ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನು ಹೊರಟುಬಂದು ನನ್ನ ಪ್ರಾಣವನ್ನು ತೆಗೆಯಬೇಕೆಂದಿರುತ್ತಾನಲ್ಲಾ ಅಂದನು.",
			"21": "<sup>21</sup> ಆಗ ಸೌಲನು ಅವನಿಗೆ - ನಾನು ಪಾಪಮಾಡಿದೆನು; ದಾವೀದನೇ, ನನ್ನ ಮಗನೇ, ಹಿಂದಿರುಗಿ ಬಾ; ಈಹೊತ್ತು ನನ್ನ ಜೀವವು ನಿನ್ನ ದೃಷ್ಟಿಯಲ್ಲಿ ಬಲು ಬೆಲೆಯುಳ್ಳದ್ದೆಂದು ಎಣಿಸಲ್ಪಟ್ಟಿತು. ಆದದರಿಂದ ನಾನು ಇನ್ನು ಮುಂದೆ ನಿನಗೆ ಕೇಡು ಮಾಡುವದಿಲ್ಲ.",
			"22": "<sup>22</sup> ಈವರೆಗೆ ನಾನು ಮಾಡಿದ್ದು ಹುಚ್ಚುತನವೂ ದೊಡ್ಡ ತಪ್ಪು ಆಗಿದೆ ಎಂದು ಹೇಳಲು ದಾವೀದನು ಅವನಿಗೆ - ಅರಸೇ, ಇಗೋ, ನಿನ್ನ ಬರ್ಜಿ ಇಲ್ಲಿ ಅದೆ; ಸೇವಕರಲ್ಲೊಬ್ಬನು ಬಂದು ತೆಗೆದುಕೊಂಡು ಹೋಗಲಿ.",
			"23": "<sup>23</sup> ಯೆಹೋವನು ಪ್ರತಿಯೊಬ್ಬನಿಗೂ ಅವನವನ ನೀತಿಸತ್ಯತೆಗಳಿಗನುಸಾರವಾದ ಫಲವನ್ನು ಕೊಡುವನು. ಆತನು ಈಹೊತ್ತು ನಿನ್ನನ್ನು ನನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸಿ ಕೊಟ್ಟಿದ್ದರೂ ನೀನು ಯೆಹೋವನ ಅಭಿಷಿಕ್ತನೆಂದು ನಾನು ನಿನ್ನ ಮೇಲೆ ಕೈಹಾಕಲಿಲ್ಲ.",
			"24": "<sup>24</sup> ನಾನು ಈಹೊತ್ತು ನಿನ್ನ ಜೀವವನ್ನು ಮಾನ್ಯವಾದದ್ದೆಂದೆಣಿಸಿದಂತೆ ಯೆಹೋವನು ನನ್ನ ಜೀವವನ್ನೂ ಮಾನ್ಯವಾದದ್ದೆಂದೆಣಿಸಿ ನನ್ನನ್ನು ಎಲ್ಲಾ ಇಕ್ಕಟ್ಟಿನಿಂದ ಬಿಡಿಸಲಿ ಎಂದನು.",
			"25": "<sup>25</sup> ಆಗ ಸೌಲನು ದಾವೀದನಿಗೆ - ನನ್ನ ಮಗನೇ, ದಾವೀದನೇ, ನೀನು ಆಶೀರ್ವಾದ ಹೊಂದು; ನೀನು ಹೇಗೂ ಮಹಾಕಾರ್ಯಗಳನ್ನು ನಡಿಸಿ ಸಫಲನಾಗುವಿ ಎಂದು ಹೇಳಿ ತನ್ನ ಊರಿಗೆ ಹೊರಟನು; ದಾವೀದನು ತನ್ನ ದಾರಿ ಹಿಡಿದನು."
		},
		"27": {
			"1": "<sup>1</sup> ದಾವೀದನು ತನ್ನ ಮನಸ್ಸಿನಲ್ಲಿ - ನಾನು ಇಲ್ಲಿದ್ದರೆ ಹೇಗೂ ಒಂದು ದಿನ ಸೌಲನ ಕೈಯಿಂದ ಮಡಿಯಲೇಬೇಕು. ಆದದರಿಂದ ಫಿಲಿಷ್ಟಿಯರ ದೇಶಕ್ಕೆ ಹೋಗಿ ತಪ್ಪಿಸಿಕೊಳ್ಳುವದೇ ಉತ್ತಮ. ಆಮೇಲೆ ಸೌಲನು ಇಸ್ರಾಯೇಲ್‍ಪ್ರಾಂತಗಳಲ್ಲಿ ನನ್ನನ್ನು ಹುಡುಕದೆ ಬಿಟ್ಟುಬಿಡುವನು, ನಾನು ಅವನ ಕೈಗೆ ತಪ್ಪಿಸಿಕೊಂಡವನಾಗಿರುವೆನು ಅಂದುಕೊಂಡು",
			"2": "<sup>2</sup> ತನ್ನ ಆರುನೂರು ಮಂದಿ ಸೈನಿಕರೊಡನೆ ಗತ್ ಊರಿನ ಅರಸನೂ ಮಾವೋಕನ ಮಗನೂ ಆದ ಆಕೀಷನ ಬಳಿಗೆ ಹೋದನು.",
			"3": "<sup>3</sup> ಅವನೂ ಅವನ ಜನರೂ ತಮ್ಮತಮ್ಮ ಕುಟುಂಬ ಸಹಿತವಾಗಿ ಆಕೀಷನ ಊರಾದ ಗತ್ ಊರಿನಲ್ಲಿ ವಾಸಿಸಿದರು. ದಾವೀದನ ಹೆಂಡತಿಯರಾಗಿರುವ ಇಜ್ರೇಲ್ಯಳಾದ ಅಹೀನೋವಮಳೂ ಕರ್ಮೆಲ್ಯನಾದ ನಾಬಾಲನ ಹೆಂಡತಿಯಾಗಿದ್ದ ಅಬೀಗೈಲಳೂ ಅವನ ಸಂಗಡ ಇದ್ದರು.",
			"4": "<sup>4</sup> ದಾವೀದನು ತಪ್ಪಿಸಿಕೊಂಡು ಗತ್ ಊರಿಗೆ ಹೋದನೆಂಬ ವರ್ತಮಾನವನ್ನು ಸೌಲನು ಕೇಳಿ ಆಮೇಲೆ ಅವನನ್ನು ಹುಡುಕಲೇ ಇಲ್ಲ.",
			"5": "<sup>5</sup> ಒಂದು ದಿನ ದಾವೀದನು ಆಕೀಷನಿಗೆ - ನಿನ್ನ ಸೇವಕನಾದ ನಾನು ನಿನ್ನ ಸಂಗಡ ರಾಜಧಾನಿಯಲ್ಲೇಕೆ ವಾಸಿಸಬೇಕು? ನಿನ್ನ ದೃಷ್ಟಿಯಲ್ಲಿ ನನಗೆ ದಯೆದೊರಕಿರುವದಾದರೆ ಯಾವದಾದರೊಂದು ಹಳ್ಳಿಯಲ್ಲಿ ನನಗೆ ಸ್ಥಳಕೊಡಿಸು, ನಾನು ಅಲ್ಲಿ ವಾಸಿಸುವೆನು ಅನ್ನಲು",
			"6": "<sup>6</sup> ಆಕೀಷನು ಕೂಡಲೆ ಅವನಿಗೆ ಚಿಕ್ಲಗ್ ಊರನ್ನು ಕೊಟ್ಟುಬಿಟ್ಟನು. ಆದದರಿಂದ ಚಿಕ್ಲಗ್ ಎಂಬದು ಇಂದಿನವರೆಗೂ ಯೆಹೂದರಾಜರಿಗೆ ಸೇರಿದೆ.",
			"7": "<sup>7</sup> ದಾವೀದನು ಫಿಲಿಷ್ಟಿಯರ ದೇಶದಲ್ಲಿ ಒಂದು ವರುಷವೂ ನಾಲ್ಕು ತಿಂಗಳೂ ವಾಸವಾಗಿದ್ದನು.",
			"8": "<sup>8</sup> ಈ ಕಾಲದಲ್ಲಿ ಅವನೂ ಅವನ ಜನರೂ ತೇಲಾಮಿನಿಂದ ಶೂರಿನವರೆಗೂ ಐಗುಪ್ತ ದೇಶದವರೆಗೂ ವಿಸ್ತರಿಸಿಕೊಂಡಿರುವ ಪ್ರಾಂತಗಳಲ್ಲಿ ವಾಸಿಸುತ್ತಿರುವ ಗೆಷೂರ್ಯರು, ಗಿಜ್ರೀಯರು, ಅಮಾಲೇಕ್ಯರು ಇವರ ಮೇಲೆ ಬಿದ್ದು ಸುಲುಕೊಳ್ಳುತ್ತಿದ್ದರು.",
			"9": "<sup>9</sup> ದಾವೀದನು ಸ್ತ್ರೀಪುರುಷರಲ್ಲಿ ಒಬ್ಬರನ್ನೂ ಉಳಿಸದೆ ದೇಶದ ಎಲ್ಲಾ ನಿವಾಸಿಗಳನ್ನು ಕೊಂದು ಕುರಿ, ದನ, ಕತ್ತೆ, ಒಂಟೆ, ಉಡುಗೊರೆ ಇವುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಆಕೀಷನ ಬಳಿಗೆ ಹೋಗುತ್ತಿದ್ದನು.",
			"10": "<sup>10</sup> ಆಕೀಷನು ಅವನನ್ನು - ಈಹೊತ್ತು ಯಾವ ಪ್ರಾಂತದವರನ್ನು ಸೂರೆಮಾಡಿಕೊಂಡು ಬಂದಿರಿ ಎಂದು ಕೇಳುವನು. ದಾವೀದನು ಅವನಿಗೆ - ದಕ್ಷಿಣಪ್ರಾಂತದಲ್ಲಿರುವ ಯೆಹೂದ್ಯರು, ಎರಹ್ಮೇಲ್ಯರು, ಕೇನ್ಯರು ಇವರಲ್ಲಿ ಯಾರ ಹೆಸರನ್ನಾದರೂ ಹೇಳುವನು.",
			"11": "<sup>11</sup> ದಾವೀದನು ತನ್ನ ಕೃತ್ಯವನ್ನು ಜನರು ಹೇಳಾರೆಂದು ನೆನಸಿ ಅವನು ಯಾರನ್ನೂ ಗತ್ ಊರಿಗೆ ತಾರದೆ ಎಲ್ಲಾ ಸ್ತ್ರೀಪುರುಷರನ್ನು ಅಲ್ಲೇ ಕೊಂದುಹಾಕುತ್ತಿದ್ದನು. ಅವನು ಫಿಲಿಷ್ಟಿಯರ ದೇಶದಲ್ಲಿ ವಾಸವಾಗಿದ್ದ ಕಾಲದಲ್ಲೆಲ್ಲಾ ಇದೇ ರೀತಿ ಮಾಡುತ್ತಿದ್ದನು.",
			"12": "<sup>12</sup> ದಾವೀದನ ಹೆಸರು ಸ್ವಜನರಾದ ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಅಸಹ್ಯವಾದದರಿಂದ ಇವನು ತನ್ನ ನಿತ್ಯ ದಾಸನಾಗಿರುವನೆಂದು ಆಕೀಷನು ನಂಬಿಕೊಂಡನು."
		},
		"28": {
			"1": "<sup>1</sup> ಆ ಕಾಲದಲ್ಲಿ ಫಿಲಿಷ್ಟಿಯರು ಇಸ್ರಾಯೇಲ್ಯರೊಡನೆ ಯುದ್ಧಮಾಡುವದಕ್ಕಾಗಿ ಸೈನ್ಯವನ್ನು ಕೂಡಿಸತೊಡಗಿದರು. ಆಗ ಆಕೀಷನು ದಾವೀದನಿಗೆ - ನೀನೂ ನಿನ್ನ ಜನರೂ ಯುದ್ಧಕ್ಕೆ ನನ್ನ ಜೊತೆಯಲ್ಲೇ ಬರಬೇಕೆಂದು ನಿನಗೆ ಗೊತ್ತಿರುತ್ತದಲ್ಲವೋ ಎನ್ನಲು ದಾವೀದನು -",
			"2": "<sup>2</sup> ಹೌದು, ನಿನ್ನ ದಾಸನ ಸಾಹಸವು ಗೊತ್ತಾಗುವದು ಅಂದನು. ಆಕೀಷನು ಅವನಿಗೆ - ಹಾಗಾದರೆ ಯುದ್ಧಕಾಲದಲ್ಲೆಲ್ಲಾ ನಿನ್ನನ್ನೇ ನನ್ನ ಮೈಗಾವಲಿಗೆ ನೇಮಿಸಿಕೊಳ್ಳುವೆನು ಅಂದನು.",
			"3": "<sup>3</sup> ಸಮುವೇಲನು ಮೃತಿಹೊಂದಿದ್ದನು. ಇಸ್ರಾಯೇಲ್ಯರು ಅವನಿಗೋಸ್ಕರ ಗೋಳಾಡಿ ಅವನ ಶವವನ್ನು ಅವನ ಸ್ವಂತ ಊರಾದ ರಾಮದಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದ್ದರು. ಸೌಲನು ಸತ್ತವರಲ್ಲಿ ವಿಚಾರಿಸುವವರನ್ನೂ ಬೇತಾಳಿಕರನ್ನೂ ದೇಶದಿಂದ ಹೊರಡಿಸಿಬಿಟ್ಟಿದ್ದನು.",
			"4": "<sup>4</sup> ಫಿಲಿಷ್ಟಿಯರು ಸೈನ್ಯಕೂಡಿಸಿಕೊಂಡು ಬಂದು ಶೂನೇಮಿನಲ್ಲಿ ಪಾಳೆಯಮಾಡಿಕೊಂಡಿದ್ದರು. ಸೌಲನು ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರೊಡನೆ ಬಂದು ಗಿಲ್ಬೋವದಲ್ಲಿ ಇಳುಕೊಂಡನು.",
			"5": "<sup>5</sup> ಅವನು ಫಿಲಿಷ್ಟಿಯರ ಪಾಳೆಯವನ್ನು ಕಂಡು ಬಹಳ ಭಯದಿಂದ ಎದೆಯೊಡೆದವನಾದನು.",
			"6": "<sup>6</sup> ಅವನು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ವಿಚಾರಿಸಿದರೂ ಆತನು ಅವನಿಗೆ ಕನಸುಗಳಿಂದಾಗಲಿ ಊರೀಮಿನಿಂದಾಗಲಿ ಪ್ರವಾದಿಗಳಿಂದಾಗಲಿ ಉತ್ತರಕೊಡಲೇ ಇಲ್ಲ.",
			"7": "<sup>7</sup> ಆದದರಿಂದ ಅವನು ತನ್ನ ಸೇವಕರಿಗೆ - ಸತ್ತವರಲ್ಲಿ ವಿಚಾರಿಸಬಲ್ಲ ಒಬ್ಬ ಸ್ತ್ರೀಯನ್ನು ಹುಡುಕಿರಿ; ನಾನು ಆಕೆಯ ಹತ್ತಿರ ಹೋಗಿ ವಿಚಾರಿಸುವೆನು ಎಂದು ಹೇಳಲಾಗಿ ಅವರು - ಅಂಥವಳೊಬ್ಬಳು ಏಂದೋರಿನಲ್ಲಿದ್ದಾಳೆ ಅಂದರು.",
			"8": "<sup>8</sup> ಆಗ ಸೌಲನು ವಸ್ತ್ರಾದಿಗಳಿಂದ ತನ್ನನ್ನು ಮಾರ್ಪಡಿಸಿಕೊಂಡು ಇಬ್ಬರು ಸೇವಕರೊಡನೆ ಹೊರಟು ರಾತ್ರಿಯಲ್ಲಿ ಆಕೆಯ ಮನೆ ಸೇರಿ - ದಯವಿಟ್ಟು ನನಗೋಸ್ಕರ ಸತ್ತವರಲ್ಲಿ ವಿಚಾರಿಸಿ ಕಣಿಹೇಳು; ನಾನು ಯಾವನ ಹೆಸರನ್ನು ಹೇಳುತ್ತೇನೋ ಅವನನ್ನು ಇಲ್ಲಿಗೆ ಬರಮಾಡು ಎಂದು ಆಕೆಯನ್ನು ಬೇಡಿಕೊಂಡನು.",
			"9": "<sup>9</sup> ಆಗ ಆಕೆಯು ಅವನಿಗೆ - ಸೌಲನು ಮಾಡಿದ್ದು ನಿನಗೆ ಗೊತ್ತುಂಟು; ಅವನು ಸತ್ತವರಲ್ಲಿ ವಿಚಾರಿಸುವವರನ್ನೂ ಬೇತಾಳಿಕರನ್ನೂ ದೇಶದಿಂದ ಹೊರಡಿಸಿಬಿಟ್ಟನಲ್ಲಾ; ಹೀಗಿದ್ದರೂ ನಾನು ಸಾಯುವಂತೆ ನೀನು ನನ್ನ ಪ್ರಾಣಕ್ಕೆ ಉರಲೊಡ್ಡುವದೇಕೆ ಅಂದಳು.",
			"10": "<sup>10</sup> ಅದಕ್ಕೆ ಸೌಲನು - ಯೆಹೋವನಾಣೆ, ಈ ವಿಷಯದಲ್ಲಿ ನೀನು ದಂಡನೆಗೆ ಗುರಿಯಾಗುವದಿಲ್ಲ ಎಂದು ಆಕೆಗೆ ಯೆಹೋವನ ಹೆಸರಿನಲ್ಲಿ ಪ್ರಮಾಣಮಾಡಿದನು.",
			"11": "<sup>11</sup> ಆಗ ಸ್ತ್ರೀಯು - ನಿನಗೆ ಯಾರನ್ನು ಬರಮಾಡಬೇಕು ಎನ್ನಲು - ಸಮುವೇಲನನ್ನು ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"12": "<sup>12</sup> ಆ ಸ್ತ್ರೀಯು ಸಮುವೇಲನನ್ನು ಕಂಡ ಕೂಡಲೆ ಗಟ್ಟಿಯಾಗಿ ಕೂಗಿ ಸೌಲನಿಗೆ - ನೀನು ನನ್ನನ್ನು ವಂಚಿಸಿದ್ದೇಕೆ? ನೀನು ಸೌಲನಲ್ಲವೋ ಅಂದಳು.",
			"13": "<sup>13</sup> ಅರಸನು - ಹೆದರಬೇಡ; ನಿನಗೇನು ಕಾಣಿಸುತ್ತದೆ ಎಂದು ಆಕೆಯನ್ನು ಕೇಳಲು - ಭೂಮಿಯೊಳಗಿಂದ ಒಬ್ಬ ದೇವನು ಬರುತ್ತಿರುವದನ್ನು ನೋಡುತ್ತೇನೆ ಎಂದು ಉತ್ತರಕೊಟ್ಟಳು.",
			"14": "<sup>14</sup> ಅವನು ತಿರಿಗಿ - ಅವನ ರೂಪ ಹೇಗಿದೆ ಎಂದು ಕೇಳಲು ಆಕೆಯು - ನಿಲುವಂಗಿಯನ್ನು ತೊಟ್ಟುಕೊಂಡ ಒಬ್ಬ ಮುದುಕನು ಬರುತ್ತಾನೆ ಎಂದು ಹೇಳಿದಳು. ಅವನು ಸಮುವೇಲನೇ ಎಂದು ಸೌಲನು ತಿಳಿದು ನೆಲದ ಮಟ್ಟಿಗೂ ಬೊಗ್ಗಿ ನಮಸ್ಕರಿಸಿದನು.",
			"15": "<sup>15</sup> ಆಗ ಸಮುವೇಲನು ಸೌಲನನ್ನು - ನೀನು ನನ್ನ ವಿಶ್ರಾಂತಿಯನ್ನು ಕೆಡಿಸಿದ್ದೇಕೆ? ನನ್ನನ್ನು ಇಲ್ಲಿಗೆ ಯಾಕೆ ಬರಮಾಡಿದಿ ಎಂದು ಕೇಳಿದನು. ಅದಕ್ಕೆ ಸೌಲನು - ನಾನು ಬಲು ಇಕ್ಕಟ್ಟಿನಲ್ಲಿದ್ದೇನೆ; ಫಿಲಿಷ್ಟಿಯರು ನನಗೆ ವಿರೋಧವಾಗಿ ಯುದ್ಧಕ್ಕೆ ಬಂದಿದ್ದಾರೆ; ದೇವರು ನನ್ನನ್ನು ಬಿಟ್ಟು ದೂರಹೋಗಿದ್ದಾನೆ; ಆತನು ನನಗೆ ಪ್ರವಾದಿಗಳಿಂದಾಗಲಿ ಕನಸುಗಳಿಂದಾಗಲಿ ಉತ್ತರಕೊಡಲೊಲ್ಲನು. ಆದದರಿಂದ ನಾನು ಮಾಡಬೇಕಾದದ್ದನ್ನು ನೀನು ತಿಳಿಸುವಿಯೆಂದು ನಿನ್ನನ್ನು ಇಲ್ಲಿಗೆ ಬರಮಾಡಿದೆನು ಅಂದನು.",
			"16": "<sup>16</sup> ಆಗ ಸಮುವೇಲನು - ಯೆಹೋವನು ನಿನ್ನನ್ನು ಬಿಟ್ಟು ನಿನಗೆ ವಿರೋಧಿಯಾದ ಮೇಲೆ ನೀನು ನನ್ನನ್ನು ವಿಚಾರಿಸುವದೇಕೆ?",
			"17": "<sup>17</sup> ಆತನು ನನ್ನ ಮುಖಾಂತರ ನಿನಗೆ ಹೇಳಿದ್ದನ್ನು ನೆರವೇರಿಸಿದ್ದಾನೆ; ರಾಜ್ಯವನ್ನು ನಿನ್ನಿಂದ ಕಿತ್ತುಕೊಂಡು ನಿನ್ನ ನೆರೆಯವನಾದ ದಾವೀದನಿಗೆ ಕೊಟ್ಟಿದ್ದಾನೆ.",
			"18": "<sup>18</sup> ನೀನು ಯೆಹೋವನ ಮಾತನ್ನು ಕೇಳಲಿಲ್ಲ; ಆತನು ಅಮಾಲೇಕ್ಯರ ಮೇಲೆ ಉಗ್ರಕೋಪಗೊಂಡು ಮಾಡಿದ ಕೋಪಾಜ್ಞೆಯನ್ನು ನೀನು ನೆರವೇರಿಸಲಿಲ್ಲ. ಆದದರಿಂದಲೇ ಯೆಹೋವನು ಈಗ ನಿನಗೆ ಹೀಗೆ ಮಾಡುತ್ತಾನೆ.",
			"19": "<sup>19</sup> ಆತನು ನಿನ್ನ ಕೂಡ ಇಸ್ರಾಯೇಲ್ಯರನ್ನೂ ಫಿಲಿಷ್ಟಿಯರ ಕೈಗೆ ಒಪ್ಪಿಸುವನು. ನಾಳೆ ನೀನೂ ನಿನ್ನ ಮಕ್ಕಳೂ ನಾನಿರುವಲ್ಲಿಗೆ ಬರುವಿರಿ; ಆತನು ಇಸ್ರಾಯೇಲ್ ಸೈನ್ಯವನ್ನು ಫಿಲಿಷ್ಟಿಯರ ಕೈಗೆ ಒಪ್ಪಿಸುವನು ಎಂದು ಹೇಳಿದನು.",
			"20": "<sup>20</sup> ಸೌಲನು ಸಮುವೇಲನ ಮಾತುಗಳನ್ನು ಕೇಳಿದ ಕೂಡಲೆ ಭಯಭ್ರಾಂತನಾಗಿ ನೆಲದ ಮೇಲೆ ಉದ್ದಕ್ಕೆ ಬಿದ್ದನು. ಅವನು ಇಡೀ ದಿವಸ ಊಟ ಮಾಡಿರಲಿಲ್ಲವಾದದರಿಂದ ಅವನಲ್ಲಿ ಬಲವೇ ಇರಲಿಲ್ಲ.",
			"21": "<sup>21</sup> ಆ ಸ್ತ್ರೀಯು ಸೌಲನ ಹತ್ತಿರ ಬಂದು ಅವನು ಬಹು ಭೀತನಾಗಿದ್ದಾನೆಂದು ಕಂಡು ಅವನಿಗೆ - ನಿನ್ನ ದಾಸಿಯಾದ ನಾನು ನಿನ್ನ ಮಾತು ಕೇಳಿದೆನು; ಕೈಯಲ್ಲಿ ಜೀವಹಿಡಿದವಳಾಗಿ ನಿನ್ನ ಅಪ್ಪಣೆಯನ್ನು ನೆರವೇರಿಸಿದೆನು.",
			"22": "<sup>22</sup> ಆದದರಿಂದ ಈಗ ನೀನು ದಯವಿಟ್ಟು ನಿನ್ನ ದಾಸಿಯ ಮಾತನ್ನು ಕೇಳಬೇಕು; ಸ್ವಲ್ಪ ಆಹಾರ ತರುತ್ತೇನೆ, ಪ್ರಯಾಣಕ್ಕೆ ಬಲಬರುವ ಹಾಗೆ ಅದನ್ನು ಊಟಮಾಡು ಎಂದು ಹೇಳಿದಳು.",
			"23": "<sup>23</sup> ಅವನು - ಒಲ್ಲೆನು; ಊಟಮಾಡುವದಿಲ್ಲ ಅಂದನು. ಆದರೆ ಅವನ ಸೇವಕರೂ ಆ ಸ್ತ್ರೀಯೂ ಬಹಳವಾಗಿ ಬೇಡಿಕೊಂಡದರಿಂದ ಅವನು ಕಡೆಗೆ ಅವರ ಮಾತಿಗೆ ಒಪ್ಪಿ ನೆಲದಿಂದೆದ್ದು ಮಂಚದ ಮೇಲೆ ಕೂತುಕೊಂಡನು.",
			"24": "<sup>24</sup> ಆ ಸ್ತ್ರೀಗೆ ಮನೆಯಲ್ಲಿ ಒಂದು ಕೊಬ್ಬಿದ ಕರುವಿತ್ತು. ಆಕೆಯು ಶೀಘ್ರವಾಗಿ ಅದನ್ನು ಕೊಯ್ದು ಪಕ್ವಮಾಡಿ ಹಿಟ್ಟನ್ನು ತೆಗೆದುಕೊಂಡು ನಾದಿ ಹುಳಿಯಿಲ್ಲದ ರೊಟ್ಟಿಗಳನ್ನು ಸುಟ್ಟು ಸೌಲನಿಗೂ ಅವನ ಸೇವಕರಿಗೂ ಬಡಿಸಿದಳು.",
			"25": "<sup>25</sup> ಅವರು ಊಟಮಾಡಿ ಅದೇ ರಾತ್ರಿ ಹೊರಟುಹೋದರು."
		},
		"29": {
			"1": "<sup>1</sup> ಫಿಲಿಷ್ಟಿಯರು ತಮ್ಮ ಸೈನ್ಯವನ್ನು ಕೂಡಿಸಿಕೊಂಡು ಬಂದು ಅಫೇಕಿನಲ್ಲಿ ಪಾಳೆಯಮಾಡಿಕೊಂಡರು. ಇಸ್ರಾಯೇಲ್ಯರು ಇಜ್ರೇಲ್ ಬೈಲಿನಲ್ಲಿರುವ ಬುಗ್ಗೆಯ ಬಳಿಯಲ್ಲಿ ಇಳುಕೊಂಡರು.",
			"2": "<sup>2</sup> ಫಿಲಿಷ್ಟಿಯ ಪ್ರಭುಗಳ ಸೈನಿಕರು ನೂರು ನೂರು ಮಂದಿಯಾಗಿಯೂ ಸಾವಿರ ಸಾವಿರ ಮಂದಿಯಾಗಿಯೂ ಬರುತ್ತಿರುವಾಗ ದಾವೀದನು ತನ್ನ ಜನರ ಸಹಿತವಾಗಿ ಆಕೀಷನ ಸಂಗಡ ಹಿಂದಳದಲ್ಲಿದ್ದನು.",
			"3": "<sup>3</sup> ಆಗ ಫಿಲಿಷ್ಟಿಯಪ್ರಭುಗಳು - ಈ ಇಬ್ರಿಯರು ಯಾಕೆ ಎಂದು ಆಕೀಷನನ್ನು ಕೇಳಲು ಅವನು ಅವರಿಗೆ - ಇವನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾದ ಸೌಲನ ಸೇವಕ ದಾವೀದನಲ್ಲವೋ? ಇವನು ಇಷ್ಟು ವರುಷ ಇಷ್ಟು ದಿವಸಗಳಿಂದ ನನ್ನ ಬಳಿಯಲ್ಲಿದ್ದಾನೆ; ಸೌಲನನ್ನು ಬಿಟ್ಟು ನನ್ನ ಹತ್ತಿರ ಬಂದ ದಿವಸ ಮೊದಲುಗೊಂಡು ಇಂದಿನವರೆಗೂ ನಾನು ಇವನಲ್ಲಿ ಯಾವ ಅಪರಾಧವನ್ನೂ ಕಾಣಲಿಲ್ಲ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"4": "<sup>4</sup> ಆದರೆ ಫಿಲಿಷ್ಟಿಯಪ್ರಭುಗಳು ಅವನ ಮೇಲೆ ಕೋಪಗೊಂಡು ಅವನಿಗೆ - ಈ ಮನುಷ್ಯನನ್ನು ಕಳುಹಿಸಿಬಿಡು, ಇವನು ಹಿಂದಿರುಗಿ ಹೋಗಿ ನೀನು ನೇಮಿಸಿದ ಸ್ಥಳದಲ್ಲೇ ವಾಸಿಸಲಿ; ನಮ್ಮ ಜೊತೆಯಲ್ಲಿ ಯುದ್ಧಕ್ಕೆ ಬರಬಾರದು. ಬಂದರೆ ನಮಗೆ ಶತ್ರುವಾಗಿ ನಿಂತಾನು. ಫಿಲಿಷ್ಟಿಯರ ತಲೆಗಳನ್ನು ಕಡಿಯುವದರಿಂದಲೇ ಇವನು ತನ್ನ ಯಜಮಾನನ ಮೆಚ್ಚಿಕೆಯನ್ನು ಪಡೆಯಬಹುದಲ್ಲವೇ!",
			"5": "<sup>5</sup> ಸೌಲನು ಸಾವಿರಗಟ್ಟಳೆಯಾಗಿ ಕೊಂದನು; ದಾವೀದನು ಹತ್ತು ಸಾವಿರಗಟ್ಟಳೆಯಾಗಿ ಕೊಂದನು ಎಂದು ಸ್ತ್ರೀಯರು ಕುಣಿಯುತ್ತಾ ಪರಸ್ಪರವಾಗಿ ಹಾಡಿದ್ದು ಈ ದಾವೀದನನ್ನೇ ಕುರಿತಲ್ಲವೋ ಅಂದರು.",
			"6": "<sup>6</sup> ಆಗ ಆಕೀಷನು ದಾವೀದನನ್ನು ಕರೆದು ಅವನಿಗೆ - ಯೆಹೋವನಾಣೆ, ನೀನು ಯಥಾರ್ಥನು; ನೀನು ನನ್ನ ಸಂಗಡ ಪಾಳೆಯದಲ್ಲಿದ್ದುಕೊಂಡು ಯುದ್ಧಕ್ಕೆ ಹೋಗುತ್ತಾ ಬರುತ್ತಾ ಇರುವದು ಯುಕ್ತವೆಂದು ನನಗೆ ತೋರಿತು. ನೀನು ಬಂದಂದಿನಿಂದ ಇಂದಿನವರೆಗೂ ನಿನ್ನಲ್ಲಿ ಯಾವ ದೋಷವನ್ನೂ ಕಾಣಲಿಲ್ಲ.",
			"7": "<sup>7</sup> ಆದರೆ ಪ್ರಭುಗಳು ನಿನ್ನನ್ನು ಮೆಚ್ಚುವದಿಲ್ಲ; ಆದದರಿಂದ ನೀನು ಸಮಾಧಾನದಿಂದ ಹಿಂದಿರುಗಿಹೋಗು; ಅವರನ್ನು ಸಿಟ್ಟುಗೊಳಿಸಬಾರದು ಎಂದು ಹೇಳಲು ದಾವೀದನು ಆಕೀಷನಿಗೆ -",
			"8": "<sup>8</sup> ನಾನೇನು ಮಾಡಿದೆನು? ನಿನ್ನ ದಾಸನಾದ ನಾನು ನಿನ್ನ ಬಳಿಗೆ ಬಂದಂದಿನಿಂದ ಇಂದಿನವರೆಗೂ ನನ್ನಲ್ಲಿ ಯಾವ ಅಪರಾಧವನ್ನು ಕಂಡಿ? ಅರಸನಾದ ನನ್ನ ಒಡೆಯನ ಜೊತೆಯಲ್ಲಿ ಹೋಗಿ ಅವನ ಶತ್ರುಗಳೊಡನೆ ನಾನೇಕೆ ಯುದ್ಧಮಾಡಬಾರದು ಅಂದನು.",
			"9": "<sup>9</sup> ಆಗ ಆಕೀಷನು ದಾವೀದನಿಗೆ - ನೀನು ಒಳ್ಳೆಯವನೆಂದು ನನಗೆ ಗೊತ್ತದೆ; ನನ್ನ ದೃಷ್ಟಿಯಲ್ಲಿ ನೀನು ದೇವದೂತನಂತಿರುತ್ತೀ; ಆದರೆ ಫಿಲಿಷ್ಟಿಯಪ್ರಭುಗಳು - ಇವನು ನಮ್ಮ ಜೊತೆಯಲ್ಲಿ ಯುದ್ಧಕ್ಕೆ ಹೊರಡಬಾರದೆಂದು ಹೇಳುತ್ತಾರಲ್ಲಾ;",
			"10": "<sup>10</sup> ಆದದರಿಂದ ನೀನೂ ನಿನ್ನ ಸಂಗಡ ಬಂದ ನಿನ್ನ ಒಡೆಯನ ಸೇವಕರೂ ನಾಳೆ ಬೆಳಿಗ್ಗೆ ಎದ್ದು ಹೊತ್ತುಮೂಡಿದ ಕೂಡಲೆ ಹಿಂದಿರುಗಿಹೋಗಿರಿ ಎಂದು ಹೇಳಿದನು.",
			"11": "<sup>11</sup> ದಾವೀದನು ತನ್ನ ಜನರೊಡನೆ ಬೆಳಿಗ್ಗೆ ಎದ್ದು ಹಿಂದಿರುಗಿ ಫಿಲಿಷ್ಟಿಯರ ದೇಶಕ್ಕೆ ಹೊರಟನು. ಫಿಲಿಷ್ಟಿಯರು ಇಜ್ರೇಲಿಗೆ ಹೋದರು."
		},
		"30": {
			"1": "<sup>1</sup> ದಾವೀದನೂ ಅವನ ಜನರೂ ಮೂರನೆಯ ದಿನ ಚಿಕ್ಲಗ್ ಊರನ್ನು ಮುಟ್ಟಿದರು. ಅಷ್ಟರೊಳಗೆ ಅಮಾಲೇಕ್ಯರು ದಂಡೆತ್ತಿ ದಕ್ಷಿಣಪ್ರಾಂತಕ್ಕೂ ಚಿಕ್ಲಗ್ ಊರಿಗೂ ಬಂದು ಚಿಕ್ಲಗ್ ಊರನ್ನು ಸುಟ್ಟು ಹಾಳುಮಾಡಿ",
			"2": "<sup>2</sup> ಅದರಲ್ಲಿದ್ದ ಎಲ್ಲಾ ಹೆಂಗಸರನ್ನೂ ಚಿಕ್ಕವರನ್ನೂ ದೊಡ್ಡವರನ್ನೂ ಸೆರೆಹಿಡಿದುಕೊಂಡು ಹೋಗಿದ್ದರು; ಆದರೆ ಯಾರನ್ನೂ ಕೊಂದಿರಲಿಲ್ಲ.",
			"3": "<sup>3</sup> ದಾವೀದನೂ ಅವನ ಜನರೂ ತಮ್ಮ ಊರಿಗೆ ಬಂದಾಗ ಅದು ಸುಡಲ್ಪಟ್ಟಿರುವದನ್ನೂ ತಮ್ಮ ಹೆಂಡತಿಯರೂ ಗಂಡುಹೆಣ್ಣು ಮಕ್ಕಳೂ ಸೆರೆಯಾಗಿ ಒಯ್ಯಲ್ಪಟ್ಟಿರುವದನ್ನೂ ಕಂಡು",
			"4": "<sup>4</sup> ಅವರು ತಮ್ಮಿಂದಾಗುವಷ್ಟು ಕಾಲ ಗಟ್ಟಿಯಾಗಿ ಅತ್ತರು.",
			"5": "<sup>5</sup> ದಾವೀದನ ಇಬ್ಬರು ಹೆಂಡತಿಯರಾದ ಇಜ್ರೇಲಿನವಳಾದ ಅಹೀನೋವಮಳೂ ಕರ್ಮೆಲ್ಯನಾದ ನಾಬಾಲನ ಹೆಂಡತಿಯಾಗಿದ್ದ ಅಬೀಗೈಲಳೂ ಸೆರೆಯಾಗಿ ಹೋಗಿದ್ದರು.",
			"6": "<sup>6</sup> ಇದಲ್ಲದೆ ಜನರು ತಮ್ಮ ಗಂಡುಹೆಣ್ಣು ಮಕ್ಕಳಿಗಾಗಿ ಮನಸ್ಸಿನಲ್ಲಿ ಬಹಳವಾಗಿ ನೊಂದುಕೊಂಡು ದಾವೀದನನ್ನು ಕಲ್ಲೆಸೆದು ಕೊಲ್ಲಬೇಕೆಂದಿದ್ದದರಿಂದ ಅವನು ಬಲು ಇಕ್ಕಟ್ಟಿನಲ್ಲಿ ಬಿದ್ದನು. ಆದರೂ ಅವನು ತನ್ನ ದೇವರಾದ ಯೆಹೋವನಲ್ಲಿ ತನ್ನನ್ನು ಬಲಪಡಿಸಿಕೊಂಡು",
			"7": "<sup>7</sup> ಅಹೀಮೆಲೆಕನ ಮಗನಾದ ಎಬ್ಯಾತಾರನಿಗೆ - ದಯವಿಟ್ಟು ಏಫೋದನ್ನು ಇಲ್ಲಿಗೆ ತೆಗೆದುಕೊಂಡು ಬಾ ಎಂದು ಹೇಳಲು ಅವನು ತಂದನು.",
			"8": "<sup>8</sup> ಆಗ ದಾವೀದನು ಯೆಹೋವನನ್ನು - ನಾನು ಆ ಗುಂಪನ್ನು ಹಿಂದಟ್ಟಬಹುದೋ? ಅದು ನನಗೆ ಸಿಕ್ಕುವದೋ ಎಂದು ಕೇಳಲು ಅತನು - ಹಿಂದಟ್ಟು, ಅದು ನಿನಗೆ ಸಿಕ್ಕುವದು; ನೀನು ಎಲ್ಲರನ್ನೂ ಬಿಡಿಸಿಕೊಂಡು ಬರುವಿ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"9": "<sup>9</sup> ದಾವೀದನೂ ಅವನ ಸಂಗಡ ಇದ್ದ ಆರುನೂರು ಮಂದಿ ಸೈನಿಕರೂ ಹೊರಟು ಬೆಸೋರ್ ಹಳ್ಳಕ್ಕೆ ಬಂದ ಮೇಲೆ",
			"10": "<sup>10</sup> ಆ ಹಳ್ಳದಿಂದ ಮೇಲೆ ಹತ್ತಲಾರದಷ್ಟು ಆಯಾಸಗೊಂಡ ಇನ್ನೂರು ಜನರು ಅಲ್ಲೇ ನಿಂತುಬಿಟ್ಟದರಿಂದ ದಾವೀದನು ಉಳಿದ ನಾನೂರು ಜನರನ್ನೇ ಕರೆದುಕೊಂಡು ಮುಂದೆ ನಡೆದನು.",
			"11": "<sup>11</sup> ಅವನ ಜನರು ಅಡವಿಯಲ್ಲಿ ಒಬ್ಬ ಐಗುಪ್ತ್ಯನು ಬಿದ್ದಿರುವದನ್ನು ಕಂಡು ಅವನನ್ನು ದಾವೀದನ ಬಳಿಗೆ ತಂದು ಅವನಿಗೆ ರೊಟ್ಟಿ ತಿನ್ನಿಸಿ ನೀರು ಕುಡಿಸಿ",
			"12": "<sup>12</sup> ಅಂಜೂರ ಹಣ್ಣಿನ ಉಂಡೆಯನ್ನೂ ಎರಡು ದ್ರಾಕ್ಷೇಗೊಂಚಲುಗಳನ್ನೂ ಕೊಟ್ಟರು. ಮೂರು ದಿವಸ ಹಗಲಿರುಳು ಅನ್ನಪಾನವಿಲ್ಲದ್ದರಿಂದ ಬಹಳವಾಗಿ ಬಳಲಿಹೋಗಿದ್ದ ಇವನು ಅವುಗಳನ್ನು ತಿನ್ನುತ್ತಲೇ ಚೇತರಿಸಿಕೊಂಡನು.",
			"13": "<sup>13</sup> ಆಗ ದಾವೀದನು ಅವನನ್ನು - ನೀನು ಯಾರ ಕಡೆಯವನು? ಎಲ್ಲಿಯವನು ಎಂದು ಕೇಳಲು ಅವನು - ನಾನು ಐಗುಪ್ತ್ಯನು; ಒಬ್ಬ ಅಮಾಲೇಕ್ಯನ ದಾಸನು. ನಾನು ಮೂರು ದಿನಗಳ ಹಿಂದೆ ಅಸ್ವಸ್ಥನಾಗಿ ಮಲಗಿಕೊಂಡದರಿಂದ ನನ್ನ ಯಜಮಾನನು ನನ್ನನ್ನು ಬಿಟ್ಟುಹೋದನು.",
			"14": "<sup>14</sup> ನಾವು ಕೆರೇತ್ಯರೂ ಯೆಹೂದ್ಯರೂ ಕಾಲೇಬ್ಯರೂ ಇರುವ ದಕ್ಷಿಣಪ್ರಾಂತವನ್ನು ಸೂರೆಮಾಡಿ ಚಿಕ್ಲಗ್ ಊರನ್ನು ಸುಟ್ಟುಬಿಟ್ಟು ಬಂದೆವು ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"15": "<sup>15</sup> ಅನಂತರ ದಾವೀದನು - ನೀನು ನನ್ನನ್ನು ಆ ಗುಂಪು ಇರುವ ಸ್ಥಳಕ್ಕೆ ಕರಕೊಂಡು ಹೋಗುವಿಯೋ ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ ಅವನು - ನೀನು ನನ್ನನ್ನು ಕೊಲ್ಲುವದಿಲ್ಲವೆಂದೂ ನನ್ನ ಯಜಮಾನನ ಕೈಗೆ ಒಪ್ಪಿಸುವದಿಲ್ಲವೆಂದೂ ದೇವರ ಹೆಸರಿನಲ್ಲಿ ಪ್ರಮಾಣಮಾಡಿದರೆ ನಿನ್ನನ್ನು ಆ ಗುಂಪಿನವರ ಬಳಿಗೆ ಕರಕೊಂಡು ಹೋಗುವೆನು ಅಂದನು.",
			"16": "<sup>16</sup> ಅವರು ಅಲ್ಲಿಗೆ ಹೋದಾಗ ಆ ಗುಂಪಿನವರು ತಮಗೆ ಫಿಲಿಷ್ಟಿಯ ಯೆಹೂದ ಪ್ರಾಂತಗಳಲ್ಲಿ ದೊರಕಿದ ದೊಡ್ಡ ಕೊಳ್ಳೆಯ ನಿಮಿತ್ತವಾಗಿ ಸಂತೋಷಿಸಿ ಉಂಡು ಕುಡಿದು ಕುಣಿದಾಡುತ್ತಾ ಅಲ್ಲಿನ ಬೈಲಿನಲ್ಲಿ ವ್ಯಾಪಿಸಿಕೊಂಡಿದ್ದರು.",
			"17": "<sup>17</sup> ದಾವೀದನು ಮರುದಿನ ಹೊತ್ತಾರೆಯಿಂದ ಸೂರ್ಯಾಸ್ತಮಾನದವರೆಗೂ ಅವರನ್ನು ಸಂಹರಿಸಿದನು. ಒಂಟೆಗಳನ್ನು ಹತ್ತಿ ಓಡಿಹೋದ ನಾನೂರು ಮಂದಿ ಪ್ರಾಯಸ್ಥರ ಹೊರತು ಬೇರೆ ಯಾರೂ ತಪ್ಪಿಸಿಕೊಳ್ಳಲಿಲ್ಲ.",
			"18": "<sup>18</sup> ದಾವೀದನು ತನ್ನ ಇಬ್ಬರು ಹೆಂಡತಿಯರನ್ನೂ ಅಮಾಲೇಕ್ಯರು ಒಯ್ದಿದ್ದ ಎಲ್ಲಾ ಕೊಳ್ಳೆಯನ್ನೂ ಬಿಡಿಸಿಕೊಂಡನು.",
			"19": "<sup>19</sup> ಅಮಾಲೇಕ್ಯರು ಒಯ್ದಿದ್ದ ಚಿಕ್ಕ ದೊಡ್ಡ ಒಡವೆಗಳಾಗಲಿ ಗಂಡುಹೆಣ್ಣು ಮಕ್ಕಳಾಗಲಿ ಒಂದನ್ನೂ ಬಿಡದೆ ಎಲ್ಲವನ್ನೂ ತಿರಿಗಿ ತೆಗೆದುಕೊಂಡು ಬಂದನು.",
			"20": "<sup>20</sup> ಇದಲ್ಲದೆ ದಾವೀದನು ಶತ್ರುಗಳ ದನಕುರಿಗಳನ್ನು ತೆಗೆದುಕೊಂಡನು. ಅವನ ಜನರು - ಇವು ದಾವೀದನ ಕೊಳ್ಳೆ ಎಂದು ಹೇಳಿ ಅವುಗಳನ್ನು ತಮ್ಮ ದನಗಳ ಮುಂದಾಗಿ ಹೊಡಕೊಂಡು ಬಂದರು.",
			"21": "<sup>21</sup> ಅನಂತರ ದಾವೀದನು ಮೊದಲು ಆಯಾಸದಿಂದ ತನ್ನ ಹಿಂದೆ ಬರಲಾರದೆ ಬೆಸೋರ್ ಹಳ್ಳದ ಹತ್ತಿರ ಇಳಿದಿದ್ದ ಇನ್ನೂರು ಜನರ ಬಳಿಗೆ ಹೋಗುತ್ತಿರುವಾಗ ಅವರು ಅವನನ್ನೂ ಅವನೊಂದಿಗಿದ್ದ ಜನರನ್ನೂ ಎದುರುಗೊಳ್ಳಬಂದರು. ದಾವೀದನು ಅವರನ್ನು ಸಂಧಿಸಿ ಅವರ ಕ್ಷೇಮಸಮಾಚಾರವನ್ನು ವಿಚಾರಿಸಿದನು.",
			"22": "<sup>22</sup> ಆಗ ದಾವೀದನ ಜೊತೆಯಲ್ಲಿ ಹೋಗಿದ್ದ ಜನರಲ್ಲಿ ದುಷ್ಟರೂ ಮುರ್ಖರೂ ಆದ ಕೆಲವು ಮಂದಿ - ಇವರು ನಮ್ಮ ಸಂಗಡ ಬಾರದ್ದರಿಂದ ನಾವು ಬಿಡಿಸಿಕೊಂಡು ಬಂದ ಕೊಳ್ಳೆಯಲ್ಲಿ ಇವರಿಗೆ ಏನೂ ಸಿಕ್ಕುವದಿಲ್ಲ. ತಮ್ಮತಮ್ಮ ಹೆಂಡರುಮಕ್ಕಳನ್ನು ಕರಕೊಂಡು ಹೋಗಿಬಿಡಲಿ ಅಂದರು.",
			"23": "<sup>23</sup> ಆಗ ದಾವೀದನು ಅವರಿಗೆ - ಸಹೋದರರೇ, ಯೆಹೋವನು ನಮ್ಮನ್ನು ಕಾಪಾಡಿ ನಮಗೆ ವಿರೋಧವಾಗಿ ಬಂದಿದ್ದ ಶತ್ರುಗಳ ಗುಂಪನ್ನು ನಮ್ಮ ಕೈಗೆ ಒಪ್ಪಿಸಿ ನಮಗೆ ಇಷ್ಟನ್ನೆಲ್ಲಾ ಕೊಟ್ಟ ಮೇಲೆ ನೀವು ಹೀಗೆ ಮಾಡಬಾರದು.",
			"24": "<sup>24</sup> ಈ ವಿಷಯದಲ್ಲಿ ನಿಮ್ಮ ಮಾತನ್ನು ಯಾರು ಕೇಳುವರು? ಯುದ್ಧಮಾಡಿದವನಿಗೆ ಸಿಕ್ಕುವಷ್ಟು ಪಾಲು ಸಾಮಾನು ಕಾಯ್ದವನಿಗೂ ಸಿಕ್ಕಬೇಕು. ಉಭಯರಿಗೂ ಸಮಭಾಗವಾಗಬೇಕು ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"25": "<sup>25</sup> ದಾವೀದನು ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಇದನ್ನು ಕಟ್ಟಳೆಯನ್ನಾಗಿ ನೇಮಿಸಿದ್ದರಿಂದ ಇಂದಿನವರೆಗೂ ಹಾಗೆಯೇ ನಡೆಯುತ್ತಾ ಬಂದಿದೆ.",
			"26": "<sup>26</sup> ದಾವೀದನು ಚಿಕ್ಲಗಿಗೆ ಬಂದನಂತರ ತನ್ನ ಸ್ನೇಹಿತರಾದ ಯೆಹೂದಹಿರಿಯರಿಗೆ ಕೊಳ್ಳೆಯ ಒಂದು ಭಾಗವನ್ನು ಕಳುಹಿಸಿ - ಇಗೋ, ಯೆಹೋವನ ಶತ್ರುಗಳನ್ನು ಸೂರೆಮಾಡಿ ತಂದವುಗಳಲ್ಲಿ ಇದು ನಿಮಗೆ ಬಹುಮಾನವಾಗಿರಲಿ ಎಂದು ಹೇಳಿಸಿದನು.",
			"27": "<sup>27</sup> ಬಹುಮಾನ ಹೊಂದಿದವರು ಯಾರಂದರೆ - ಬೇತೇಲಿನವರು, ದಕ್ಷಿಣಪ್ರಾಂತದ ರಾಮೋತಿನವರು, ಯತ್ತೀರಿನವರು,",
			"28": "<sup>28</sup> ಅರೋಯೇರಿನವರು, ಸಿಪ್ಮೋತಿನವರು, ಎಷ್ಟೆಮೋವದವರು,",
			"29": "<sup>29</sup> ರಾಕಾಲಿನವರು, ಎರಹ್ಮೇಲ್ಯರು, ಕೇನ್ಯರು,",
			"30": "<sup>30</sup> ಹೊರ್ಮದವರು, ಬೋರಾಷಾನಿನವರು, ಅತಾಕಿನವರು,",
			"31": "<sup>31</sup> ಹೆಬ್ರೋನಿನವರು ಮತ್ತು ದಾವೀದನೂ ಅವನ ಜನರೂ ಸಂಚರಿಸುತ್ತಿದ್ದ ಇತರ ಎಲ್ಲಾ ಸ್ಥಳಗಳವರು ಇವರೇ."
		},
		"31": {
			"1": "<sup>1</sup> ಫಿಲಿಷ್ಟಿಯರು ಇಸ್ರಾಯೇಲ್ಯರೊಡನೆ ಯುದ್ಧಮಾಡಿದರು. ಇಸ್ರಾಯೇಲ್ಯರು ಅವರಿಂದ ಅಪಜಯ ಹೊಂದಿ ಗಿಲ್ಬೋವ ಬೆಟ್ಟದಲ್ಲಿ ಹತರಾದರು.",
			"2": "<sup>2</sup> ಫಿಲಿಷ್ಟಿಯರು ಸೌಲನನ್ನೂ ಅವನ ಮಕ್ಕಳನ್ನೂ ಬಿಡದೆ ಹಿಂದಟ್ಟಿ ಅವನ ಮಕ್ಕಳಾದ ಯೋನಾತಾನನನ್ನೂ ಅಬೀನಾದಾಬನನ್ನೂ ಮಲ್ಕೀಷೂವನನ್ನೂ ಕೊಂದರು.",
			"3": "<sup>3</sup> ಸೌಲನಿದ್ದ ಕಡೆಯಲ್ಲಿ ಯುದ್ಧವು ಬಹುಘೋರವಾಗಿತ್ತು. ಬಿಲ್ಲುಗಾರರು ಅವನಿಗೆ ಗುರಿಯಿಟ್ಟರು.",
			"4": "<sup>4</sup> ಆದದರಿಂದ ಸೌಲನು ಬಹುಭೀತನಾಗಿ ತನ್ನ ಆಯುಧವಾಹಕನಿಗೆ - ನಿನ್ನ ಕತ್ತಿಯನ್ನು ಹಿರಿದು ನನ್ನನ್ನು ತಿವಿದು ಕೊಲ್ಲು; ಇಲ್ಲವಾದರೆ ಸುನ್ನತಿಯಿಲ್ಲದ ಈ ಜನರು ಬಂದು ನನ್ನನ್ನು ತಿವಿದು ಅಪಕೀರ್ತಿಯನ್ನುಂಟುಮಾಡಾರು ಎಂದು ಹೇಳಲು ಅವನು ಹೆದರಿ ಒಲ್ಲೆನು ಅಂದನು. ಆದದರಿಂದ ಸೌಲನು ತಾನೇ ಕತ್ತಿಯನ್ನು ಹಿರಿದು ಅದರ ಮೇಲೆ ಬಿದ್ದನು.",
			"5": "<sup>5</sup> ಸೌಲನು ಸತ್ತದ್ದನ್ನು ಆಯುಧವಾಹಕನು ಕಂಡು ತಾನೂ ತನ್ನ ಕತ್ತಿಯ ಮೇಲೆ ಬಿದ್ದು ಅಲ್ಲಿಯೇ ಸತ್ತನು.",
			"6": "<sup>6</sup> ಹೀಗೆ ಸೌಲನೂ ಅವನ ಮೂರು ಮಂದಿ ಮಕ್ಕಳೂ ಅವನ ಆಯುಧವಾಹಕನೂ ಎಲ್ಲಾ ಆಳುಗಳೂ ಅದೇ ದಿವಸದಲ್ಲಿ ಸತ್ತರು.",
			"7": "<sup>7</sup> ಇಸ್ರಾಯೇಲ್ ಭಟರು ಸೋತರು, ಸೌಲನೂ ಅವನ ಮಕ್ಕಳೂ ಸತ್ತರು ಎಂಬ ವರ್ತಮಾನವನ್ನು ತಗ್ಗಿನ ಆಚೆಯಲ್ಲಿಯೂ ಯೊರ್ದನಿನ ಆಚೆಯಲ್ಲಿಯೂ ವಾಸವಾಗಿದ್ದ ಇಸ್ರಾಯೇಲ್ಯರು ಕೇಳಿ ತಮ್ಮ ಪಟ್ಟಣಗಳನ್ನು ಬಿಟ್ಟು ಓಡಿಹೋದರು; ಫಿಲಿಷ್ಟಿಯರು ಬಂದು ಅವುಗಳಲ್ಲಿ ವಾಸಿಸಿದರು.",
			"8": "<sup>8</sup> ಮರುದಿನ ಬೆಳಿಗ್ಗೆ ಫಿಲಿಷ್ಟಿಯರು ಸತ್ತವರ ಒಡವೆಗಳನ್ನು ಸುಲುಕೊಳ್ಳುವದಕ್ಕೆ ಬಂದಾಗ ಸೌಲನೂ ಅವನ ಮೂರು ಮಂದಿ ಮಕ್ಕಳೂ ಗಿಲ್ಬೋವ ಬೆಟ್ಟದಲ್ಲಿ ಸತ್ತುಬಿದ್ದಿರುವದನ್ನು ಕಂಡು",
			"9": "<sup>9</sup> ಸೌಲನ ತಲೆಯನ್ನು ಕಡಿದು ಅವನ ಆಯುಧಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಅವುಗಳನ್ನು ತಮ್ಮ ದೇಶದ ಎಲ್ಲಾ ಕಡೆಗೂ ಕಳುಹಿಸಿ ತಮ್ಮ ಎಲ್ಲಾ ಜನರಿಗೂ ದೇವಸ್ಥಾನಗಳಿಗೂ ಜಯವಾರ್ತೆಯನ್ನು ಮುಟ್ಟಿಸಿದರು.",
			"10": "<sup>10</sup> ಅವನ ಆಯುಧಗಳನ್ನು ಅಷ್ಟೋರೆತ್ ದೇವತೆಯ ಗುಡಿಯಲ್ಲಿಟ್ಟರು; ಅವನ ಶವವನ್ನು ಬೇತ್ಷೆಯಾನಿನ ಕೋಟೆ ಗೋಡೆಗೆ ನೇತುಹಾಕಿದರು.",
			"11": "<sup>11</sup> ಫಿಲಿಷ್ಟಿಯರು ಸೌಲನಿಗೆ ಏನೇನು ಮಾಡಿದರೆಂಬ ವರ್ತಮಾನವು ಯಾಬೆಷ್ ಗಿಲ್ಯಾದಿನವರಿಗೆ ಮುಟ್ಟಿದಾಗ",
			"12": "<sup>12</sup> ಅವರಲ್ಲಿದ್ದ ಶೂರರೆಲ್ಲರೂ ಹೊರಟು ರಾತ್ರಿಯೆಲ್ಲಾ ನಡೆದುಹೋಗಿ ಸೌಲನ ಮತ್ತು ಅವನ ಮಕ್ಕಳ ಶವಗಳನ್ನು ಬೇತ್ಷೆಯಾನಿನ ಗೋಡೆಯಿಂದ ಇಳಿಸಿ ಯಾಬೇಷಿಗೆ ತೆಗೆದುಕೊಂಡು ಬಂದು ಅಲ್ಲಿ ಅವುಗಳನ್ನು ಸುಟ್ಟುಬಿಟ್ಟರು.",
			"13": "<sup>13</sup> ಅವರ ಎಲುಬುಗಳನ್ನು ಯಾಬೇಷಿನಲ್ಲಿದ್ದ ಪಿಚುಲ ವೃಕ್ಷದ ಕೆಳಗೆ ಸಮಾಧಿಮಾಡಿ ಏಳು ದಿವಸ ಉಪವಾಸವಾಗಿದ್ದರು."
		}
	}
};
module.exports = book;