var book = {
	"name": "2 ಸಮುವೇಲನು",
	"numChapters": 24,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ಸೌಲನು ಸತ್ತನಂತರ ಸಂಭವಿಸಿದ್ದು. ದಾವೀದನು ಅಮಾಲೇಕ್ಯರನ್ನು ಹೊಡೆದು ಬಂದು ಚಿಕ್ಲಗಿನಲ್ಲಿ ಎರಡು ದಿವಸ ಇದ್ದನು.",
			"2": "<sup>2</sup> ಮೂರನೆಯ ದಿನ ಸೌಲನ ಪಾಳೆಯದಿಂದ ಒಬ್ಬ ಮನುಷ್ಯನು ದಾವೀದನ ಬಳಿಗೆ ಬಂದು ನೆಲದ ಮಟ್ಟಿಗೆ ಬಾಗಿ ನಮಸ್ಕರಿಸಿದನು. ಅವನು ವಸ್ತ್ರಗಳನ್ನು ಹರಿದುಕೊಂಡು ತಲೆಯ ಮೇಲೆ ಮಣ್ಣು ಹಾಕಿಕೊಂಡಿದ್ದನು.",
			"3": "<sup>3</sup> ದಾವೀದನು ಅವನನ್ನು - ನೀನು ಎಲ್ಲಿಂದ ಬಂದಿ ಎಂದು ಕೇಳಲು ಅವನು - ನಾನು ಇಸ್ರಾಯೇಲ್ಯರ ಪಾಳೆಯದಿಂದ ತಪ್ಪಿಸಿಕೊಂಡು ಬಂದೆನು ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"4": "<sup>4</sup> ಆಗ ದಾವೀದನು - ದಯವಿಟ್ಟು ಕಾರ್ಯವೇನಾಯಿತೆಂದು ತಿಳಿಸು ಅನ್ನಲು ಆ ಮನುಷ್ಯನು - ಇಸ್ರಾಯೇಲ್ಯರು ರಣರಂಗದಿಂದ ಓಡಿಹೋದರು. ಅನೇಕರು ಮಡಿದರು. ಸೌಲನೂ ಅವನ ಮಗನಾದ ಯೋನಾತಾನನೂ ಮೃತಿ ಹೊಂದಿದರು ಎಂದು ತಿಳಿಸಿದನು.",
			"5": "<sup>5</sup> ದಾವೀದನು ವರ್ತಮಾನತಂದ ಆ ಪ್ರಾಯಸ್ಥನಿಗೆ - ಸೌಲನೂ ಅವನ ಮಗನಾದ ಯೋನಾತಾನನೂ ಸತ್ತಿರುವದು ನಿನಗೆ ಹೇಗೆ ಗೊತ್ತಾಯಿತು ಎಂದು ಪ್ರಶ್ನಿಸಲು ಅವನು -",
			"6": "<sup>6</sup> ನಾನು ಅಕಸ್ಮಾತ್ತಾಗಿ ಗಿಲ್ಬೋವಪರ್ವತ ಪ್ರದೇಶಕ್ಕೆ ಬಂದಾಗ ಸೌಲನು ತನ್ನ ಬರ್ಜಿಯನ್ನೂರಿಕೊಂಡು ನಿಂತಿರುವದನ್ನೂ ರಥಿಕರೂ ರಾಹುತರೂ ಅವನನ್ನು ಹಿಂದಟ್ಟಿ ಬರುತ್ತಿರುವದನ್ನೂ ಕಂಡೆನು.",
			"7": "<sup>7</sup> ಅವನು ತಿರುಗಿಕೊಂಡು ನನ್ನನ್ನು ಕಂಡು ಕರೆದನು.",
			"8": "<sup>8</sup> ನಾನು - ಇಗೋ, ಬಂದೆನು ಎಂದು ಹೇಳಿ ಹೋದಾಗ ಅವನು - ನೀನಾರು ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ - ಅಮಾಲೇಕ್ಯನೆಂದು ಉತ್ತರ ಕೊಟ್ಟೆನು.",
			"9": "<sup>9</sup> ಆಗ ಅವನು ನನಗೆ - ನೀನು ಕೃಪೆಮಾಡಿ ಹತ್ತಿರ ಬಂದು ನನ್ನನ್ನು ಕೊಂದುಹಾಕು; ಯಾಕಂದರೆ ನನ್ನಲ್ಲಿ ಜೀವವು ಇನ್ನೂ ಪೂರ್ಣವಾಗಿರುವದರಿಂದ ನನಗೆ ಸಂಕಟ ಹಿಡಿದದೆ ಎಂದು ಹೇಳಿದನು.",
			"10": "<sup>10</sup> ನಾನು ಹತ್ತಿರ ಹೋಗಿ ಅವನು ಬಿದ್ದು ಬಿಟ್ಟರೆ ಬದುಕಲಾರನೆಂದು ನೆನಸಿ ಅವನನ್ನು ಕೊಂದುಹಾಕಿ ಅವನ ತಲೆಯ ಮೇಲಣ ಕಿರೀಟವನ್ನೂ ರಟ್ಟೆಯಲ್ಲಿದ್ದ ಬಳೆಯನ್ನೂ ತೆಗೆದುಕೊಂಡು ಅವುಗಳನ್ನು ನನ್ನ ಒಡೆಯನಾದ ನಿನಗೆ ತಂದಿದ್ದೇನೆ ಅಂದನು.",
			"11": "<sup>11</sup> ಸೌಲನೂ ಅವನ ಮಗನಾದ ಯೋನಾತಾನನೂ ಯೆಹೋವನ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಾಯೇಲ್ಯರೂ ಕತ್ತಿಯಿಂದ ಸಂಹೃತರಾದದ್ದಕ್ಕಾಗಿ",
			"12": "<sup>12</sup> ದಾವೀದನೂ ಅವನ ಜನರೂ ತಮ್ಮ ಬಟ್ಟೆಗಳನ್ನು ಹರಿದುಕೊಂಡು ಗೋಳಾಡಿ ಅತ್ತು ಸಾಯಂಕಾಲದವರೆಗೆ ಉಪವಾಸ ಮಾಡಿದರು.",
			"13": "<sup>13</sup> ದಾವೀದನು ವರ್ತಮಾನ ತಂದ ಪ್ರಾಯಸ್ಥನನ್ನು - ನೀನೆಲ್ಲಿಯವನು ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ ಅವನು - ನಾನು ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಪ್ರವಾಸಿಯಾಗಿರುವ ಅಮಾಲೇಕ್ಯನ ಮಗನು ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"14": "<sup>14</sup> ದಾವೀದನು ಅವನಿಗೆ - ನೀನು ಕೈಯೆತ್ತಿ ಯೆಹೋವನ ಅಭಿಷಿಕ್ತನನ್ನು ಕೊಲ್ಲುವದಕ್ಕೆ ಏಕೆ ಭಯಪಡಲಿಲ್ಲ ಎಂದು ಹೇಳಿ",
			"15": "<sup>15</sup> ತನ್ನ ಆಳುಗಳಲ್ಲಿ ಒಬ್ಬನನ್ನು ಕರೆದು - ಇವನ ಮೇಲೆ ಬಿದ್ದು ಹೊಡಿ ಎಂದು ಆಜ್ಞಾಪಿಸಲು ಅವನು ಇವನನ್ನು ಹೊಡೆದು ಕೊಂದನು.",
			"16": "<sup>16</sup> ದಾವೀದನು - ರಕ್ತಾಪರಾಧವು ನಿನ್ನ ತಲೆಯ ಮೇಲೆಯೇ ಇರಲಿ; ಯೆಹೋವನ ಅಭಿಷಿಕ್ತನನ್ನು ಕೊಂದುಹಾಕಿದೆನೆಂದು ನಿನ್ನ ಬಾಯೇ ನಿನಗೆ ವಿರೋಧವಾಗಿ ಸಾಕ್ಷಿ ಹೇಳಿತು ಅಂದನು.",
			"17": "<sup>17</sup> ದಾವೀದನು ಸೌಲಯೋನಾತಾನರ ಮೇಲೆ ಬಿಲ್ಲೆಂಬ ಒಂದು ಶೋಕಗೀತವನ್ನು ರಚಿಸಿ",
			"18": "<sup>18</sup> ಅದನ್ನು ಯೆಹೂದ್ಯರಿಗೆ ಕಲಿಸಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿದನು. ಈ ಗೀತವು ಯಾಷಾರ್ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತದೆ -",
			"19": "<sup>19</sup> ಇಸ್ರಾಯೇಲ್ಯರೇ, ನಿಮ್ಮ ವೈಭವವು ನಿಮ್ಮ ಗುಡ್ಡಗಳಲ್ಲಿ ಮಣ್ಣುಪಾಲಾಗಿ ಹೋಯಿತು. ಅಯ್ಯೋ, ಪರಾಕ್ರಮಶಾಲಿಗಳೇ, ಹೇಗೆ ಹತರಾದಿರಿ!",
			"20": "<sup>20</sup> ಈ ಸಂಗತಿಯನ್ನು ಗತ್ ಊರಿನಲ್ಲಿ ತಿಳಿಸಬೇಡಿರಿ; ಅಷ್ಕೆಲೋನಿನ ಬೀದಿಗಳಲ್ಲಿ ಸಾರಬೇಡಿರಿ. ಫಿಲಿಷ್ಟಿಯರ ಹೆಂಗಸರು ಸಂತೋಷಿಸಾರು; ಸುನ್ನತಿಯಿಲ್ಲದವರ ಸ್ತ್ರೀಯರು ಉಲ್ಲಾಸಿಸಾರು.",
			"21": "<sup>21</sup> ಗಿಲ್ಬೋವಗುಡ್ಡಗಳೇ, ನಿಮ್ಮ ಮೇಲೆ ಮಂಜೂ ಮಳೆಯೂ ನೈವೇದ್ಯಕ್ಕಾಗಿ ಫಲಿಸುವ ಹೊಲಗಳೂ ಇಲ್ಲದೆ ಹೋಗಲಿ; ಅಲ್ಲಿ ಪರಾಕ್ರಮಶಾಲಿಗಳ ಗುರಾಣಿಗಳು ಬಿದ್ದಿರುತ್ತವೆ. ಸೌಲನ ಗುರಾಣಿಯೂ ಅಭಿಷಿಕ್ತನಲ್ಲದವನ ಗುರಾಣಿಯಂತೆ ಬಿದ್ದಿದೆ.",
			"22": "<sup>22</sup> ಯೋನಾತಾನನ ಬಿಲ್ಲು ಹತರಾದವರ ರಕ್ತವನ್ನು ಹೀರದೆಯೂ ಪರಾಕ್ರಮಶಾಲಿಗಳ ಕೊಬ್ಬನ್ನು ಭುಜಿಸದೆಯೂ ಬರುತ್ತಿರಲಿಲ್ಲ. ಸೌಲನ ಕತ್ತಿಯು ವ್ಯರ್ಥವಾಗಿ ಹಿಂದಿರುಗುತ್ತಿರಲಿಲ್ಲ.",
			"23": "<sup>23</sup> ಸೌಲಯೋನಾತಾನರು ಪ್ರಿಯರೂ ಮನೋಹರರೂ ಆಗಿದ್ದರು; ಅವರು ಜೀವಿಸುವಾಗಲೂ ಸಾಯುವಾಗಲೂ ಅಗಲಿದವರಲ್ಲ. ಅವರು ಹದ್ದುಗಳಿಗಿಂತಲೂ ವೇಗವುಳ್ಳವರು; ಸಿಂಹಗಳಿಗಿಂತಲೂ ಬಲವುಳ್ಳವರು.",
			"24": "<sup>24</sup> ಇಸ್ರಾಯೇಲ್ ಸ್ತ್ರೀಯರೇ, ಸೌಲನಿಗಾಗಿ ಗೋಳಾಡಿರಿ; ನಿಮಗೆ ಉಲ್ಲಾಸಕರವಾದ ರಕ್ತಾಂಬರಗಳನ್ನು ಉಡಿಸಿ ಅವುಗಳ ಮೇಲೆ ಸುವರ್ಣಾಭರಣಗಳನ್ನು ತೊಡಿಸಿದವನು ಆತನೇ ಅಲ್ಲವೋ?",
			"25": "<sup>25</sup> ಅಯ್ಯೋ, ಪರಾಕ್ರಮಶಾಲಿಗಳೇ, ಯುದ್ಧದಲ್ಲಿ ಹೇಗೆ ಮಡಿದುಹೋದಿರಿ! ಯೋನಾತಾನನು ನಿಮ್ಮ ಬೆಟ್ಟಗಳಲ್ಲಿ ಹತನಾಗಿ ಬಿದ್ದಿದ್ದಾನಲ್ಲಾ.",
			"26": "<sup>26</sup> ಯೋನಾತಾನನೇ, ನನ್ನ ಸಹೋದರನೇ, ನಿನಗೋಸ್ಕರ ನನ್ನಲ್ಲಿ ಬಹು ಸಂಕಟವುಂಟಾಗಿದೆ; ನೀನು ನನಗೆ ಬಹುಮನೋಹರನಾಗಿದ್ದಿ. ನನ್ನ ಮೇಲಿದ್ದ ನಿನ್ನ ಪ್ರೀತಿಯು ಆಶ್ಚರ್ಯಕರವಾದದ್ದೇ ಸರಿ; ಅದು ಸತೀಪ್ರೇಮಕ್ಕಿಂತ ಶ್ರೇಷ್ಠವಾದದ್ದು.",
			"27": "<sup>27</sup> ಅಯ್ಯೋ, ಪರಾಕ್ರಮಶಾಲಿಗಳು ಹೇಗೆ ಹತರಾದರು! ಯುದ್ಧಾಯುಧಗಳು ಹೇಗೆ ಹಾಳಾದವು!"
		},
		"2": {
			"1": "<sup>1</sup> ಅನಂತರ ದಾವೀದನು ಯೆಹೋವನನ್ನು - ನಾನು ಯೆಹೂದದೇಶದ ಯಾವದಾದರೊಂದು ಪಟ್ಟಣಕ್ಕೆ ಹೋಗಬಹುದೋ ಎಂದು ಕೇಳಲು ಆತನು - ಹೋಗಬಹುದು ಅಂದನು. ಅವನು ತಿರಿಗಿ - ಯಾವ ಊರಿಗೆ ಹೋಗಲಿ ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ - ಹೆಬ್ರೋನಿಗೆ ಹೋಗು ಎಂಬ ಉತ್ತರ ಸಿಕ್ಕಿತು.",
			"2": "<sup>2</sup> ಆಗ ದಾವೀದನು ತನ್ನ ಇಬ್ಬರು ಹೆಂಡತಿಯರಾದ ಇಜ್ರೇಲಿನ ಅಹೀನೋವಮಳನ್ನೂ ಕರ್ಮೆಲ್ಯನಾದ ನಾಬಾಲನ ಹೆಂಡತಿಯಾಗಿದ್ದ ಅಬೀಗೈಲಳನ್ನೂ ಕರಕೊಂಡು ಅಲ್ಲಿಗೆ ಹೋದನು.",
			"3": "<sup>3</sup> ದಾವೀದನ ಜನರೂ ತಮ್ಮತಮ್ಮ ಕುಟುಂಬಗಳನ್ನು ಕರಕೊಂಡು ಅವನ ಜೊತೆಯಲ್ಲಿ ಹೋಗಿ ಹೆಬ್ರೋನಿಗೆ ಸೇರಿದ ಊರುಗಳಲ್ಲಿ ವಾಸಿಸಿದರು.",
			"4": "<sup>4</sup> ತರುವಾಯ ಯೆಹೂದ್ಯರು ಅಲ್ಲಿಗೆ ಬಂದು ದಾವೀದನನ್ನು ಅಭಿಷೇಕಿಸಿ ತಮ್ಮ ಕುಲಕ್ಕೆ ಅರಸನನ್ನಾಗಿ ಮಾಡಿಕೊಂಡರು. ಸೌಲನ ಶವವನ್ನು ಸಮಾಧಿಮಾಡಿದವರು ಯಾಬೇಷ್ ಗಿಲ್ಯಾದಿನವರೇ ಎಂಬ ಸಂಗತಿಯು ದಾವೀದನಿಗೆ ಗೊತ್ತಾಯಿತು.",
			"5": "<sup>5</sup> ಆಗ ಅವನು ಅವರ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿ - ನೀವು ಕರುಣೆಯಿಂದ ನಿಮ್ಮ ಒಡೆಯನಾದ ಸೌಲನ ಶವವನ್ನು ಸಮಾಧಿಮಾಡಿದ್ದಕ್ಕಾಗಿ ನಿಮಗೆ ಯೆಹೋವನ ಅಶೀರ್ವಾದವುಂಟಾಗಲಿ.",
			"6": "<sup>6</sup> ಆತನ ಕೃಪಾಸತ್ಯತೆಗಳು ನಿಮ್ಮೊಂದಿಗಿರಲಿ; ಈ ಕಾರ್ಯಕ್ಕಾಗಿ ನಾನೂ ನಿಮಗೆ ಉಪಕಾರಮಾಡುವೆನು.",
			"7": "<sup>7</sup> ಯೆಹೂದ್ಯರು ನನ್ನನ್ನು ಅಭಿಷೇಕಿಸಿ ತಮ್ಮ ಅರಸನನ್ನಾಗಿ ಮಾಡಿಕೊಂಡಿರುವದರಿಂದ ಸೌಲನು ಸತ್ತು ಹೋದರೂ ಶೂರರಾಗಿರ್ರಿ, ನಿಮ್ಮ ಕೈಗಳು ಜೋಲು ಬೀಳದಿರಲಿ ಎಂದು ಹೇಳಿಸಿದನು.",
			"8": "<sup>8</sup> ಸೌಲನ ಸೇನಾಪತಿಯೂ ನೇರನ ಮಗನೂ ಆದ ಅಬ್ನೇರನು ಸೌಲನ ಮಗನಾದ ಈಷ್ಬೋಶೆತನನ್ನು ಹೊಳೆಯ ಆಚೆಯಲ್ಲಿರುವ ಮಹನಯಿಮಿಗೆ ಒಯ್ದು",
			"9": "<sup>9</sup> ಅವನನ್ನು ಗಿಲ್ಯಾದ್, ಆಶೇರ್, ಇಜ್ರೇಲ್, ಎಫ್ರಾಯೀಮ್, ಬೆನ್ಯಾಮೀನ್ ಇವುಗಳ ಜನರ ಮೇಲೆಯೂ ಬೇರೆ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರ ಮೇಲೆಯೂ ಅರಸನನ್ನಾಗಿ ಮಾಡಿದನು.",
			"10": "<sup>10</sup> (ಸೌಲನ ಮಗನಾದ ಈಷ್ಬೋಶೆತನು ನಾಲ್ವತ್ತು ವರುಷದವನಾದಾಗ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಆಳತೊಡಗಿ ಎರಡು ವರುಷ ರಾಜ್ಯಭಾರಮಾಡಿದನು.) ಯೆಹೂದ್ಯರು ಮಾತ್ರ ದಾವೀದನನ್ನು ಹಿಂಬಾಲಿಸಿದರು.",
			"11": "<sup>11</sup> ದಾವೀದನು ಹೆಬ್ರೋನಿನಲ್ಲಿ ಯೆಹೂದ್ಯರ ಅರಸನಾಗಿದ್ದದ್ದು ಏಳು ವರುಷವೂ ಆರು ತಿಂಗಳೂ.",
			"12": "<sup>12</sup> ಒಂದು ದಿವಸ ನೇರನ ಮಗನಾದ ಅಬ್ನೇರನೂ ಸೌಲನ ಮಗನಾದ ಈಷ್ಬೋಶೆತನ ಸೇವಕರೂ ಮಹನಯಿಮಿನಿಂದ ಹೊರಟು ಗಿಬ್ಯೋನಿಗೆ ಬಂದರು.",
			"13": "<sup>13</sup> ಚೆರೂಯಳ ಮಗನಾದ ಯೋವಾಬನೂ ದಾವೀದನ ಸೇವಕರೂ ಹೊರಟು ಗಿಬ್ಯೋನಿನ ಕೆರೆಯ ಹತ್ತಿರ ಸಂಧಿಸಿ ಇವರು ಕೆರೆಯ ಈಚೆಯಲ್ಲಿಯೂ ಅವರು ಆಚೆಯಲ್ಲಿಯೂ ಕೂತುಕೊಂಡರು.",
			"14": "<sup>14</sup> ಆಗ ಅಬ್ನೇರನು ಯೋವಾಬನಿಗೆ - ಪ್ರಾಯಸ್ಥರು ಎದ್ದು ನಮ್ಮ ಮುಂದೆ ಆಡಲಿ ಎಂದು ಹೇಳಲು ಯೋವಾಬನು ಆಗಲಿ ಅಂದನು.",
			"15": "<sup>15</sup> ಆಗ ಸೌಲನ ಮಗನಾದ ಈಷ್ಬೋಶೆತನ ಕಡೆಯವರಾದ ಹನ್ನೆರಡು ಮಂದಿ ಬೆನ್ಯಾಮೀನ್ಯರೂ ದಾವೀದನ ಸೇವಕರಲ್ಲಿ ಹನ್ನೆರಡು ಮಂದಿಯೂ ಸಮಸಂಖ್ಯೆಯಾಗಿ ಹೊರಟುಬಂದು",
			"16": "<sup>16</sup> ಅವರಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬನೂ ತನ್ನ ತನ್ನ ಎದುರಾಳಿಯ ತಲೆ ಹಿಡಿದು ಕತ್ತಿಯಿಂದ ಪಕ್ಕೆಯಲ್ಲಿ ತಿವಿದದರಿಂದ ಎಲ್ಲರೂ ಸತ್ತು ಬಿದ್ದರು. ಆದದರಿಂದ ಗಿಬ್ಯೋನಿನಲ್ಲಿರುವ ಆ ಸ್ಥಳಕ್ಕೆ ಹದಗತ್ತಿ ಹೊಲವೆಂದು ಹೆಸರಾಯಿತು.",
			"17": "<sup>17</sup> ಅನಂತರ ಆ ದಿವಸ ಘೋರಯುದ್ಧವಾಯಿತು. ಅಬ್ನೇರನೂ ಇಸ್ರಾಯೇಲ್ಯರೂ ದಾವೀದನ ಸೇವಕರಿಂದ ಸೋಲಿಸಲ್ಪಟ್ಟರು.",
			"18": "<sup>18</sup> ಚೆರೂಯಳ ಮೂರು ಮಂದಿ ಮಕ್ಕಳಾದ ಯೋವಾಬ್, ಅಬೀಷೈ, ಅಸಾಹೇಲ್ ಎಂಬವರು ಅಲ್ಲಿಗೆ ಬಂದಿದ್ದರು. ಅಸಾಹೇಲನು ಅಡವಿಯ ಜಿಂಕೆಯಂತೆ ಚುರುಕು ಕಾಲಿನವನಾಗಿದ್ದನು.",
			"19": "<sup>19</sup> ಇವನು ಎಡಕ್ಕಾದರೂ ಬಲಕ್ಕಾದರೂ ತಿರುಗದೆ ಅಬ್ನೇರನನ್ನು ಹಿಂದಟ್ಟಿದನು.",
			"20": "<sup>20</sup> ಅಬ್ನೇರನು ಹಿಂದಿರುಗಿ ನೋಡಿ - ನೀನು ಅಸಾಹೇಲನೋ ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ ಅವನು ಹೌದೆಂದು ಉತ್ತರ ಕೊಟ್ಟನು.",
			"21": "<sup>21</sup> ಆಗ ಅಬ್ನೇರನು ಅವನಿಗೆ - ನನ್ನನ್ನು ಬಿಟ್ಟು ಎಡಕ್ಕಾಗಲಿ ಬಲಕ್ಕಾಗಲಿ ತಿರುಗಿಕೊಂಡು ಪ್ರಾಯಸ್ಥರಲ್ಲೊಬ್ಬನನ್ನು ಹಿಡಿದು ಸುಲುಕೋ ಎಂದು ಹೇಳಿದನು; ಆದರೆ ಅಸಾಹೇಲನು ಒಪ್ಪಲಿಲ್ಲ.",
			"22": "<sup>22</sup> ಅಬ್ನೇರನು ತಿರಿಗಿ - ನನ್ನನ್ನು ಬಿಟ್ಟುಹೋಗು; ನಾನೇಕೆ ನಿನ್ನನ್ನು ನೆಲಕ್ಕುರುಳಿಸಬೇಕು? ಹಾಗೆ ಮಾಡಿದರೆ ನಿನ್ನ ಅಣ್ಣನಾದ ಯೋವಾಬನಿಗೆ ಹೇಗೆ ಮೋರೆತೋರಿಸಲಿ ಅಂದನು.",
			"23": "<sup>23</sup> ಅಸಾಹೇಲನು ಬಿಟ್ಟುಹೋಗುವದಕ್ಕೆ ಒಪ್ಪದ್ದರಿಂದ ಅಬ್ನೇರನು ಬರ್ಜಿಯನ್ನು ಹಿಂಗಡೆಯಿಂದ ಅವನ ಹೊಟ್ಟೆಯೊಳಗೆ ತಿವಿದನು. ಬರ್ಜಿಯು ಅವನ ಬೆನ್ನಿನಿಂದ ಹಾಯ್ದು ಬಂದಿತು. ಅವನು ಕೂಡಲೆ ಅಲ್ಲೇ ಬಿದ್ದು ಸತ್ತನು. ಅಸಾಹೇಲನು ಸತ್ತುಬಿದ್ದ ಸ್ಥಳಕ್ಕೆ ಬಂದವರೆಲ್ಲರೂ ಅಲ್ಲೇ ನಿಂತರು.",
			"24": "<sup>24</sup> ಆದರೆ ಯೋವಾಬನೂ ಅಬೀಷೈಯೂ ಅಬ್ನೇರನನ್ನು ಹಿಂದಟ್ಟಿದರು. ಹೀಗೆ ಅವರು ಅಮ್ಮಾ ಎಂಬ ಗುಡ್ಡಕ್ಕೆ ಬರುವಷ್ಟರಲ್ಲಿ ಸೂರ್ಯಾಸ್ತಮಾನವಾಯಿತು. ಆ ಗುಡ್ಡವು ಗಿಬ್ಯೋನಿನ ಅರಣ್ಯ ಮಾರ್ಗದ ಬಳಿಯಲ್ಲಿರುವ ಗೀಯದ ಎದುರಿನಲ್ಲಿರುತ್ತದೆ.",
			"25": "<sup>25</sup> ಬೆನ್ಯಾಮೀನ್ಯರು ಒಂದೇ ಗುಂಪಾಗಿ ಕೂಡಿಕೊಂಡು ಅಬ್ನೇರನ ಬಳಿಗೆ ಬಂದು ಗುಡ್ಡದ ಮೇಲೆ ನಿಂತರು.",
			"26": "<sup>26</sup> ಆಗ ಅಬ್ನೇರನು ಯೋವಾಬನನ್ನು - ಕತ್ತಿಯು ಯಾವಾಗಲೂ ತಿನ್ನುತ್ತಲೇ ಇರಬೇಕೋ? ಹಗೆತನವೇ ಇದರ ಅಂತ್ಯಫಲವೆಂದು ನಿನಗೆ ಗೊತ್ತಾಗಲಿಲ್ಲವೋ? ಸಹೋದರರನ್ನು ಹಿಂದಟ್ಟುವದು ಸಾಕೆಂದು ನಿನ್ನ ಜನರಿಗೆ ಯಾವಾಗ ಆಜ್ಞಾಪಿಸುವಿ ಎಂದನು.",
			"27": "<sup>27</sup> ಅದಕ್ಕೆ ಯೋವಾಬನು - ದೇವರ ಜೀವದಾಣೆ ಈಗ ನೀನು ಈ ಮಾತುಗಳನ್ನು ಹೇಳದಿದ್ದರೆ ಜನರು ನಾಳೆ ಬೆಳಗಾಗುವವರೆಗೆ ತಮ್ಮ ಬಂಧುಗಳನ್ನು ಹಿಂದಟ್ಟದೆ ಬಿಡುತ್ತಿದ್ದಿಲ್ಲ ಎಂದು ಉತ್ತರಕೊಟ್ಟು ಕೂಡಲೆ ತುತೂರಿಯನ್ನು ಊದಿಸಿದನು.",
			"28": "<sup>28</sup> ಆಗ ಜನರು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಹಿಂದಟ್ಟದೆ ಬಿಟ್ಟದರಿಂದ ಯುದ್ಧವು ನಿಂತುಹೋಯಿತು.",
			"29": "<sup>29</sup> ಅಬ್ನೇರನೂ ಅವನ ಜನರೂ ಅರಾಬಾ ತಗ್ಗಿನಲ್ಲಿ ರಾತ್ರಿಯೆಲ್ಲಾ ಪ್ರಯಾಣಮಾಡಿ ಯೊರ್ದನ್ ಹೊಳೆದಾಟಿ ಬಿಥ್ರೋನ್ ಕಣಿವೆಯನ್ನು ಹಾಯ್ದು ಮಹನಯಿಮಿಗೆ ಸೇರಿದರು.",
			"30": "<sup>30</sup> ಯೋವಾಬನು ಅಬ್ನೇರನನ್ನು ಹಿಂದಟ್ಟದೆ ಬಿಟ್ಟು ಹಿಂದಿರುಗಿ ಬಂದು ಜನರನ್ನು ಕೂಡಿಸಿ ಲೆಕ್ಕಿಸಿದಾಗ ಅವರಲ್ಲಿ ದಾವೀದನ ಹತ್ತೊಂಭತ್ತು ಮಂದಿ ಭಟರೂ ಅಸಾಹೇಲನೂ ಇರಲಿಲ್ಲ.",
			"31": "<sup>31</sup> ಆದರೆ ದಾವೀದನ ಭಟರು ಅಬ್ನೇರನ ಜನರಾಗಿರುವ ಬೆನ್ಯಾಮೀನ್ಯರಲ್ಲಿ ಮುನ್ನೂರರುವತ್ತು ಮಂದಿಯನ್ನು ಕೊಂದಿದ್ದರು.",
			"32": "<sup>32</sup> ಅವರು ಅಸಾಹೇಲನ ಶವವನ್ನು ತಂದು ಅದನ್ನು ಬೇತ್ಲೆಹೇಮಿನಲ್ಲಿರುವ ಅವನ ತಂದೆಯ ಶ್ಮಶಾನ ಭೂಮಿಯಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಅನಂತರ ಯೋವಾಬನೂ ಅವನ ಜನರೂ ರಾತ್ರಿಯೆಲ್ಲಾ ಪ್ರಯಾಣಮಾಡಿ ಹೆಬ್ರೋನಿಗೆ ಬಂದರು. ಆಗ ಸೂರ್ಯೋದಯವಾಯಿತು."
		},
		"3": {
			"1": "<sup>1</sup> ಬಹುದಿವಸಗಳವರೆಗೆ ಸೌಲನ ವಂಶದವರಿಗೂ ದಾವೀದನ ವಂಶದವರಿಗೂ ಯುದ್ಧನಡೆದಿತ್ತು. ದಾವೀದನು ಬಲಗೊಳ್ಳುತ್ತಾ ಬಂದನು, ಸೌಲನ ವಂಶವು ದುರ್ಬಲವಾಗುತ್ತಾ ಬಂದಿತು.",
			"2": "<sup>2</sup> ಹೆಬ್ರೋನಿನಲ್ಲಿರುವಾಗ ದಾವೀದನಿಗೆ ಹುಟ್ಟಿದ ಮಕ್ಕಳು - ಚೊಚ್ಚಲ ಮಗನು ಅಮ್ನೋನನು; ಇವನು ಇಜ್ರೇಲಿನವಳಾದ ಅಹೀನೋವಮಳಲ್ಲಿ ಹುಟ್ಟಿದವನು.",
			"3": "<sup>3</sup> ಎರಡನೆಯವನು ಕಿಲಾಬನು; ಇವನು ಕರ್ಮೆಲ್ಯನಾದ ನಾಬಾಲನ ಹೆಂಡತಿಯಾಗಿದ್ದ ಅಬೀಗೈಲಳಲ್ಲಿ ಹುಟ್ಟಿದವನು. ಮೂರನೆಯವನು ಅಬ್ಷಾಲೋಮನು; ಇವನು ಗೆಷೂರಿನ ಅರಸನಾದ ತಲ್ಮೈ ಎಂಬವನ ಮಗಳಾದ ಮಾಕಳ ಪುತ್ರನು.",
			"4": "<sup>4</sup> ನಾಲ್ಕನೆಯವನು ಅದೋನೀಯನು; ಇವನು ಹಗ್ಗೀತಳ ಮಗನು. ಐದನೆಯವನು ಶೆಫಟ್ಯನು; ಇವನು ಅಬೀಟಲಳ ಮಗನು.",
			"5": "<sup>5</sup> ಆರನೆಯವನು ಇತ್ರಾಮನು; ಇವನು ದಾವೀದನಿಗೆ ಅವನ ಹೆಂಡತಿಯಾದ ಎಗ್ಲಳಲ್ಲಿ ಹುಟ್ಟಿದವನು. ಹೆಬ್ರೋನಿನಲ್ಲಿರುವಾಗ ದಾವೀದನಿಗೆ ಹುಟ್ಟಿದ ಮಕ್ಕಳು ಇವರೇ.",
			"6": "<sup>6</sup> ಸೌಲನ ವಂಶದವರಿಗೂ ದಾವೀದನ ವಂಶದವರಿಗೂ ಯುದ್ಧನಡಿಯುತ್ತಿದ್ದಾಗ ಸೌಲನ ವಂಶದವರಲ್ಲಿ ಅಬ್ನೇರನೇ ಬಲಿಷ್ಠನಾಗಿದ್ದನು.",
			"7": "<sup>7</sup> ಅಯಾಹನ ಮಗಳಾದ ರಿಚ್ಪಳು ಸೌಲನ ಉಪಪತ್ನಿಯಾಗಿದ್ದಳು. ಒಂದು ದಿವಸ ಈಷ್ಬೋಶೆತನು ಅಬ್ನೇರನನ್ನು - ನೀನು ನನ್ನ ತಂದೆಯ ಉಪಪತ್ನಿಯನ್ನು ಕೂಡಿದ್ದೇಕೆ ಎಂದು ಕೇಳಿದನು.",
			"8": "<sup>8</sup> ಅಬ್ನೇರನು ಈ ಮಾತನ್ನು ಕೇಳಿ ಉರಿಗೊಂಡು ಈಷ್ಬೋಶೆತನಿಗೆ - ಈವರೆಗೆ ನಾನು ನಿನ್ನ ತಂದೆಯಾದ ಸೌಲನ ಕುಟುಂಬಕ್ಕೂ ಅವನ ಬಂಧು ಮಿತ್ರರಿಗೂ ದಯೆತೋರಿಸುತ್ತಿದ್ದೆನಲ್ಲಾ; ನಿನ್ನನ್ನು ದಾವೀದನ ಕೈಗೆ ಒಪ್ಪಿಸಿಕೊಡಲಿಲ್ಲವಷ್ಟೆ. ಈಗ ಈ ಹೆಂಗಸಿನ ದೆಸೆಯಿಂದ ನನ್ನಲ್ಲಿ ತಪ್ಪುಹಿಡಿಯುವದಕ್ಕೆ ನಾನೇನು ಯೆಹೂದನಾಯಿಯ ತಲೆಯೋ?",
			"9": "<sup>9</sup> ನಾನು ಅರಸುತನವನ್ನು ಸೌಲನ ಕುಟುಂಬದಿಂದ ತೆಗೆದು ದಾನಿನಿಂದ ಬೇರ್ಷೆಬದವರೆಗಿರುವ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿಯೂ ಯೆಹೂದ್ಯರಲ್ಲಿಯೂ ದಾವೀದನ ಸಿಂಹಾಸನವನ್ನು ಸ್ಥಿರಪಡಿಸಿ",
			"10": "<sup>10</sup> ಯೆಹೋವನಾದ ದೇವರು ದಾವೀದನಿಗೆ ಮಾಡಿದ ವಾಗ್ದಾನವನ್ನು ನೆರವೇರಿಸದೆಹೋದರೆ ಆತನು ನನಗೆ ಬೇಕಾದದ್ದನ್ನು ಮಾಡಲಿ ಅಂದನು.",
			"11": "<sup>11</sup> ಈಷ್ಬೋಶೆತನು ಅವನಿಗೆ ಹೆದರಿ ಏನೂ ಉತ್ತರ ಕೊಡಲಾರದೆ ಹೋದನು.",
			"12": "<sup>12</sup> ಅನಂತರ ಅಬ್ನೇರನು ದಾವೀದನ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿ ಅವನಿಗೆ - ದೇಶವು ಯಾರದು? ನೀನು ನನ್ನೊಡನೆ ಒಡಂಬಡಿಕೆ ಮಾಡಿಕೊಂಡರೆ ಇಸ್ರಾಯೇಲ್ಯರನ್ನೆಲ್ಲಾ ನಿನ್ನ ಕಡೆಗೆ ತಿರುಗಿಸುವ ಹಾಗೆ ನನ್ನ ಹಸ್ತವು ನಿನ್ನ ಸಂಗಡ ಇರುವದು ಎಂದು ಹೇಳಿಸಿದನು.",
			"13": "<sup>13</sup> ಅದಕ್ಕೆ ದಾವೀದನು - ಒಳ್ಳೇದು, ನಿನ್ನ ಸಂಗಡ ಒಡಂಬಡಿಕೆ ಮಾಡಿಕೊಳ್ಳುತ್ತೇನೆ; ಆದರೆ ನೀನು ಒಂದು ಮಾಡಬೇಕು. ನನ್ನನ್ನು ನೋಡುವದಕ್ಕೆ ಬರುವಾಗ ಸೌಲನ ಮಗಳಾದ ಮೀಕಲಳನ್ನು ಕರಕೊಂಡು ಬರಬೇಕು; ಹಾಗೆ ಕರಕೊಂಡು ಬಾರದಿದ್ದರೆ ನೀನು ನನ್ನ ಮೋರೆಯನ್ನು ನೋಡಕೂಡದು ಎಂದು ಉತ್ತರ ಕೊಟ್ಟನು.",
			"14": "<sup>14</sup> ಮತ್ತು ಅವನು ಸೌಲನ ಮಗನಾದ ಈಷ್ಬೋಶೆತನ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿ ಅವನಿಗೆ - ನಾನು ನೂರು ಮಂದಿ ಫಿಲಿಷ್ಟಿಯರ ಮುಂದೊಗಲನ್ನು ತಂದು ಕೊಟ್ಟು ವರಿಸಿದ ನನ್ನ ಹೆಂಡತಿಯಾದ ಮೀಕಲಳನ್ನು ನನಗೊಪ್ಪಿಸು ಎಂಬದಾಗಿ ಆಜ್ಞಾಪಿಸಿದನು.",
			"15": "<sup>15</sup> ಈಷ್ಬೋಶೆತನು ಲಯಿಷನ ಮಗನೂ ಆಕೆಯ ಗಂಡನೂ ಆದ ಪಲ್ಟೀಯೇಲನ ಮನೆಗೆ ಆಳುಗಳನ್ನು ಕಳುಹಿಸಿ ಆಕೆಯನ್ನು ತರಿಸಿದನು.",
			"16": "<sup>16</sup> ಆಗ ಪಲ್ಟೀಯೇಲನು ಬಹುರೀಮಿನವರೆಗೆ ಅಳುತ್ತಾ ಆಕೆಯ ಹಿಂದೆ ಬಂದನು. ಅನಂತರ ಅಬ್ನೇರನು ಅವನಿಗೆ - ಹಿಂದಿರುಗು, ನಡೆ ಎಂದು ಹೇಳಲು ಅವನು ಹಿಂದಿರುಗಿ ಹೋದನು.",
			"17": "<sup>17</sup> ಅಬ್ನೇರನು ಇಸ್ರಾಯೇಲ್ಯರ ಹಿರಿಯರಿಗೆ - ದಾವೀದನು ನಮ್ಮ ಅರಸನಾಗಬೇಕೆಂದು ನೀವು ಮುಂಚೆ ಅಪೇಕ್ಷಿಸಿದಂತೆಯೇ ಈಗ ಮಾಡಿರಿ.",
			"18": "<sup>18</sup> ಯೆಹೋವನು ದಾವೀದನ ವಿಷಯವಾಗಿ - ನನ್ನ ಸೇವಕನಾದ ದಾವೀದನ ಮುಖಾಂತರವಾಗಿ ನನ್ನ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಫಿಲಿಷ್ಟಿಯರ ಕೈಗೂ ಬೇರೆ ಎಲ್ಲಾ ಶತ್ರುಗಳ ಕೈಗೂ ತಪ್ಪಿಸುವೆನೆಂದು ನುಡಿದಿದ್ದಾನಲ್ಲಾ ಎಂದು ಹೇಳಿಸಿದನು.",
			"19": "<sup>19</sup> ಅವನು ಬೆನ್ಯಾಮೀನ್ಯರಿಗೂ ಇದೇ ಪ್ರಕಾರ ಹೇಳಿದನು. ತರುವಾಯ ಅವನು ಇಸ್ರಾಯೇಲ್ಯರೂ ಬೆನ್ಯಾಮೀನ್ಯರೆಲ್ಲರೂ ಒಪ್ಪಿ ಹೇಳಿದ ಮಾತುಗಳನ್ನು ದಾವೀದನಿಗೆ ತಿಳಿಸುವದಕ್ಕೋಸ್ಕರ ಹೆಬ್ರೋನಿಗೆ ಹೊರಟನು.",
			"20": "<sup>20</sup> ಅವನು ಹೆಬ್ರೋನಿಗೆ ಬಂದಾಗ ದಾವೀದನು ಅವನಿಗೂ ಅವನ ಸಂಗಡ ಬಂದಿದ್ದ ಇಪ್ಪತ್ತು ಜನರಿಗೂ ಔತಣಮಾಡಿಸಿದನು.",
			"21": "<sup>21</sup> ಅಬ್ನೇರನು ದಾವೀದನಿಗೆ - ನಾನು ಹೊರಟುಹೋಗಿ ಇಸ್ರಾಯೇಲ್ಯರನ್ನೆಲ್ಲಾ ಕೂಡಿಸಿ ನನ್ನ ಒಡೆಯನಾದ ಅರಸನ ಬಳಿಗೆ ಕರಕೊಂಡುಬರುವೆನು; ಅವರು ನಿನ್ನ ಸಂಗಡ ಒಡಂಬಡಿಕೆ ಮಾಡಿಕೊಳ್ಳುವರು; ನೀನು ನಿನ್ನ ಅಪೇಕ್ಷೆಯಂತೆ ಎಲ್ಲರ ಮೇಲೆ ದೊರೆತನಮಾಡಬಹುದು ಎಂದು ಹೇಳಲು ದಾವೀದನು ಅವನನ್ನು ಸುರಕ್ಷಿತವಾಗಿ ಕಳುಹಿಸಿಕೊಟ್ಟನು.",
			"22": "<sup>22</sup> ಸುಲಿಗೆಮಾಡುವದಕ್ಕಾಗಿ ಹೋಗಿದ್ದ ದಾವೀದನ ಭಟರೂ ಯೋವಾಬನೂ ದೊಡ್ಡ ಕೊಳ್ಳೆಯೊಡನೆ ಅಷ್ಟರಲ್ಲಿ ಹಿಂದಿರುಗಿ ಬಂದರು. ಅವರು ಹೆಬ್ರೋನಿಗೆ ಬಂದಾಗ ಅಬ್ನೇರನು ದಾವೀದನ ಹತ್ತಿರ ಇರಲಿಲ್ಲ. ದಾವೀದನು ಅವನನ್ನು ಸುರಕ್ಷಿತವಾಗಿ ಕಳುಹಿಸಿಬಿಟ್ಟಿದ್ದನು.",
			"23": "<sup>23</sup> ಸೈನ್ಯಸಹಿತವಾಗಿ ಹಿಂದಿರುಗಿ ಬಂದ ಯೋವಾಬನು - ನೇರನ ಮಗನಾದ ಅಬ್ನೇರನು ಬಂದಿದ್ದನೆಂದೂ ಅರಸನು ಅವನನ್ನು ಸುರಕ್ಷಿತವಾಗಿ ಕಳುಹಿಸಿದನೆಂದೂ ತಿಳಿದು",
			"24": "<sup>24</sup> ಅರಸನ ಬಳಿಗೆ ಹೋಗಿ - ನೀನು ಮಾಡಿದ್ದೇನು? ಅಬ್ನೇರನು ನಿನ್ನ ಬಳಿಗೆ ಬಂದಿದ್ದನಂತೆ; ನೀನು ಅವನನ್ನು ಸುರಕ್ಷಿತವಾಗಿ ಕಳುಹಿಸಿಬಿಟ್ಟದ್ದೇಕೆ?",
			"25": "<sup>25</sup> ನೇರನ ಮಗನಾದ ಅಬ್ನೇರನ ಸಂಗತಿಯು ನಿನಗೆ ಗೊತ್ತಿಲ್ಲವೋ? ಅವನು ನಿನ್ನನ್ನು ವಂಚಿಸುವದಕ್ಕೂ ನಿನ್ನ ಸ್ಥಿತಿಗತಿಗಳನ್ನು ತಿಳುಕೊಳ್ಳುವದಕ್ಕೂ ನೀನು ಏನೇನು ಮಾಡುತ್ತಿರುತ್ತೀ ಎಂಬದನ್ನು ಕಂಡು ಹಿಡಿಯುವದಕ್ಕೂ ಬಂದಿದ್ದನಷ್ಟೆ ಎಂದು ಹೇಳಿ",
			"26": "<sup>26</sup> ದಾವೀದನನ್ನು ಬಿಟ್ಟು ಹೊರಗೆ ಹೋಗಿ ಅಬ್ನೇರನನ್ನು ಕರತರುವಂತೆ ದೂತರನ್ನು ಅಟ್ಟಿದನು. ಇವರು ಹೋಗಿ ಸಿರಾ ಬಾವಿಯ ಬಳಿಯಿಂದ ಅವನನ್ನು ಕರಕೊಂಡುಬಂದರು. ಇದು ದಾವೀದನಿಗೆ ಗೊತ್ತಿರಲಿಲ್ಲ.",
			"27": "<sup>27</sup> ಅಬ್ನೇರನು ಹೆಬ್ರೋನಿಗೆ ಬಂದಾಗ ಯೋವಾಬನು ಅವನನ್ನು ಗುಪ್ತ ಸಂಭಾಷಣೆಗಾಗಿಯೋ ಎಂಬಂತೆ ಊರು ಬಾಗಲಿನೊಳಗೆ ಕರಕೊಂಡು ಹೋಗಿ ತನ್ನ ತಮ್ಮನಾದ ಅಸಾಹೇಲನನ್ನು ವಧಿಸಿದ್ದದಕ್ಕೆ ಪ್ರತಿಯಾಗಿ ಅವನನ್ನು ಹೊಟ್ಟೆಯಲ್ಲಿ ತಿವಿದು ಕೊಂದನು.",
			"28": "<sup>28</sup> ತುಸು ಹೊತ್ತಾದ ಮೇಲೆ ಈ ವರ್ತಮಾನವು ದಾವೀದನಗೆ ಮುಟ್ಟಿತು. ಆಗ ಅವನು - ನೇರನ ಮಗನಾದ ಅಬ್ನೇರನ ವಧೆಯಲ್ಲಿ ನಾನೂ ನನ್ನ ರಾಜ್ಯವೂ ಯೆಹೋವನ ಮುಂದೆ ಸದಾ ನಿರ್ದೋಷಿಗಳು.",
			"29": "<sup>29</sup> ಈ ಅಪರಾಧವು ಯೋವಾಬನ ಮೇಲೆಯೂ ಅವನ ಸಂತಾನದವರೆಲ್ಲರ ಮೇಲೆಯೂ ಇರಲಿ. ಅವನ ಮನೆಯಲ್ಲಿ ಮೇಹಸ್ರಾವವುಳ್ಳವರೂ ಕುಷ್ಠರೋಗಿಗಳೂ ಕುಂಟರೂ ಕತ್ತಿಯಿಂದ ಹತರಾಗುವವರೂ ಭಿಕ್ಷೆ ಬೇಡುವವರೂ ಇದ್ದೇ ಇರಲಿ ಅಂದನು.",
			"30": "<sup>30</sup> ಅಬ್ನೇರನು ಗಿಬ್ಯೋನಿನ ಸಮೀಪದಲ್ಲಾದ ಯುದ್ಧದಲ್ಲಿ ಯೋವಾಬ ಅಬೀಷೈಯರ ತಮ್ಮನಾದ ಅಸಾಹೇಲನನ್ನು ವಧಿಸಿದ್ದದರಿಂದ ಅವರು ಇವನನ್ನು ಕೊಂದುಹಾಕಿದರು.",
			"31": "<sup>31</sup> ದಾವೀದನು ಯೋವಾಬನಿಗೂ ಅವನ ಸಂಗಡ ಇದ್ದ ಜನರಿಗೂ - ನಿಮ್ಮ ಬಟ್ಟೆಗಳನ್ನು ಹರಕೊಂಡು ಗೋಣೀತಟ್ಟುಗಳನ್ನು ಕಟ್ಟಿಕೊಂಡು ಗೋಳಾಡುತ್ತಾ ಅಬ್ನೇರನ ಶವದ ಮುಂದೆ ನಡಿಯಿರಿ ಎಂದು ಆಜ್ಞಾಪಿಸಿ ತಾನು ಚಟ್ಟದ ಹಿಂದೆ ಹೋದನು.",
			"32": "<sup>32</sup> ಅಬ್ನೇರನ ಶವವನ್ನು ಹೆಬ್ರೋನಿನಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಅರಸನು ಅವನ ಸಮಾಧಿಯ ಹತ್ತಿರ ನಿಂತು ಗಟ್ಟಿಯಾಗಿ ಅತ್ತನು; ಜನರೆಲ್ಲರೂ ಅತ್ತರು.",
			"33": "<sup>33</sup> ದಾವೀದನು ಅಬ್ನೇರನನ್ನು ಕುರಿತು - ಅಬ್ನೇರನೇ, ನಿನಗೆ ಮೂರ್ಖರಿಗಾಗುವಂತೆ ದುರ್ಮರಣವಾಯಿತೇ!",
			"34": "<sup>34</sup> ನಿನ್ನ ಕೈಗಳು ಕಟ್ಟಲ್ಪಡಲಿಲ್ಲ, ನಿನ್ನ ಕಾಲಿಗೆ ಬೇಡಿಗಳು ಬರಲಿಲ್ಲ; ನೀನು ದುಷ್ಟರಿಂದಲೋ ಎಂಬಂತೆ ಹತನಾದಿ ಎಂದು ಶೋಕಗೀತವನ್ನು ಹಾಡಲು ಜನರು ತಿರಿಗಿ ಅತ್ತರು.",
			"35": "<sup>35</sup> ಸೂರ್ಯಾಸ್ತಮಾನಕ್ಕೆ ಮೊದಲೇ ಜನರೆಲ್ಲರೂ ದಾವೀದನ ಬಳಿಗೆ ಬಂದು ಅವನನ್ನು ಊಟಕ್ಕೆ ಎಬ್ಬಿಸಬೇಕೆಂದು ಪ್ರಯತ್ನಿಸಿದರು. ಆದರೆ ಅವನು - ಸೂರ್ಯನು ಮುಣುಗುವ ಮೊದಲು ನಾನು ರೊಟ್ಟಿಯನ್ನಾಗಲಿ ಬೇರೆ ಯಾವದನ್ನಾಗಲಿ ಬಾಯಲ್ಲಿ ಹಾಕಿದರೆ ದೇವರು ನನಗೆ ಬೇಕಾದದ್ದು ಮಾಡಲಿ ಎಂದು ಆಣೆಯಿಟ್ಟು ಹೇಳಿದನು.",
			"36": "<sup>36</sup> ಜನರು ಇದನ್ನು ಕಂಡು ಮೆಚ್ಚಿದರು; ಅರಸನು ಮಾಡಿದ್ದೆಲ್ಲವೂ ಅವರಿಗೆ ಒಪ್ಪಿಕೆಯಾಯಿತು.",
			"37": "<sup>37</sup> ನೇರನ ಮಗನಾದ ಅಬ್ನೇರನ ವಧೆಯಲ್ಲಿ ಅರಸನು ಕೈಹಾಕಲಿಲ್ಲವೆಂಬದು ಅವನ ಎಲ್ಲಾ ಜನರಿಗೂ ಇಸ್ರಾಯೇಲ್ಯರಿಗೂ ಅದೇ ದಿವಸ ಗೊತ್ತಾಯಿತು.",
			"38": "<sup>38</sup> ಅರಸನು ತನ್ನ ಸೇವಕರಿಗೆ - ಈ ಹೊತ್ತು ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಹತವಾದವನು ಪ್ರಭುವೂ ಮಹಾಪುರುಷನೂ ಆಗಿದ್ದಾನೆಂಬದು ನಿಮಗೆ ಗೊತ್ತುಂಟಲ್ಲಾ;",
			"39": "<sup>39</sup> ನಾನು ರಾಜ್ಯಾಭಿಷೇಕ ಹೊಂದಿದವನಾಗಿದ್ದರೂ ಈಗ ಏನೂ ಮಾಡಲಾರದವನಾಗಿದ್ದೇನೆ. ಚೆರೂಯಳ ಮಕ್ಕಳಾದ ಇವರು ನನ್ನ ಹತೋಟಿಗೆ ಬಾರದವರು. ಯೆಹೋವನೇ ಕೆಡುಕರಿಗೆ ಮುಯ್ಯಿತೀರಿಸಲಿ ಎಂದು ಹೇಳಿದನು."
		},
		"4": {
			"1": "<sup>1</sup> ಅಬ್ನೇರನು ಹೆಬ್ರೋನಿನಲ್ಲಿ ಸತ್ತನೆಂಬ ವರ್ತಮಾನವು ಸೌಲನ ಮಗನಾದ ಈಷ್ಬೋಶೆತನಿಗೆ ಮುಟ್ಟಿದಾಗ ಅವನ ಕೈಗಳು ಜೋಲುಬಿದ್ದವು. ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರೂ ಕಳವಳಗೊಂಡರು.",
			"2": "<sup>2</sup> ಸೌಲನ ಮಗನಿಗೆ ಬಾಣ, ರೇಕಾಬ್ ಎಂಬ ಇಬ್ಬರು ಸೇನಾಪತಿಗಳಿದ್ದರು. ಅವರು ಬೆನ್ಯಾಮೀನ್ ಕುಲದ ಬೇರೋತಿನವನಾದ ರಿಮ್ಮೋನನ ಮಕ್ಕಳು. (ಬೇರೋತ್ ಎಂಬದು ಬೆನ್ಯಾಮೀನ್ಯರಿಗೆ ಸೇರಿದ ಗ್ರಾಮ;",
			"3": "<sup>3</sup> ಆದರೆ ಬೇರೋತ್ಯರು ಗಿತ್ತಯಿಮಿಗೆ ಓಡಿಹೋಗಿ ಇಂದಿನವರೆಗೂ ಅಲ್ಲೇ ಪ್ರವಾಸಿಗಳಾಗಿದ್ದಾರೆ.)",
			"4": "<sup>4</sup> ಸೌಲನ ಮಗನಾದ ಯೋನಾತಾನನಿಗೆ ಮೆಫೀಬೋಶೆತನೆಂಬ ಒಬ್ಬ ಕುಂಟ ಮಗನಿದ್ದನು. ಅವನು ಕುಂಟನಾದದ್ದು ಹೇಗಂದರೆ - ಸೌಲಯೋನಾತಾನರು ಸತ್ತರೆಂಬ ವರ್ತಮಾನವು ಇಜ್ರೇಲಿನಿಂದ ಬಂದಾಗ ಐದು ವರುಷದವನಾಗಿದ್ದ ಅವನನ್ನು ಅವನ ದಾದಿಯು ತೆಗೆದುಕೊಂಡು ಅವಸರದಿಂದ ಓಡಿಹೋಗುವಾಗ ಅವನು ಬಿದ್ದು ಕುಂಟನಾದನು.",
			"5": "<sup>5</sup> ಈಷ್ಬೋಶೆತನು ಮಧ್ಯಾಹ್ನದ ಬಿಸಿಲು ಹೊತ್ತಿನಲ್ಲಿ ಮನೆಯೊಳಗೆ ಮಲಗಿರುವಾಗ ಬೇರೋತಿನ ರಿಮ್ಮೋನನ ಮಕ್ಕಳಾದ ರೇಕಾಬ್, ಬಾಣ ಎಂಬವರು ಅವನ ಮನೆಯ ಬಳಿಗೆ ಹೋದರು.",
			"6": "<sup>6</sup> ಆಗ ಬಾಗಲುಕಾಯುವವಳು ಗೋದಿ ಕೇರುತ್ತಿದ್ದು ಹಾಗೆಯೇ ತೂಗಡಿಸಿ ನಿದ್ರೆಮಾಡುತ್ತಿರುವದನ್ನು ಅವರು ಕಂಡು ರಹಸ್ಯವಾಗಿ ಒಳಗೆ ಬಂದು",
			"7": "<sup>7</sup> ಒಳಕೋಣೆಯಲ್ಲಿ ಮಂಚದ ಮೇಲೆ ಮಲಗಿದ್ದ ಅರಸನನ್ನು ಹೊಡೆದು ಕೊಂದು ತಲೆತೆಗೆದುಕೊಂಡು ರಾತ್ರಿಯೆಲ್ಲಾ ಅರಾಬಾ ತಗ್ಗಿನಲ್ಲಿ ಪ್ರಯಾಣಮಾಡಿ",
			"8": "<sup>8</sup> ಹೆಬ್ರೋನಿನಲ್ಲಿದ್ದ ಅರಸನಾದ ದಾವೀದನ ಬಳಿಗೆ ಬಂದು ಆ ತಲೆಯನ್ನು ಅವನಿಗೆ ತೋರಿಸಿ - ಇಗೋ, ನಿನ್ನ ಜೀವತೆಗೆಯಬೇಕೆಂದಿದ್ದ ನಿನ್ನ ಶತ್ರುವಾದ ಸೌಲನ ಮಗ ಈಷ್ಬೋಶೆತನ ತಲೆ! ಅರಸನಾದ ನಮ್ಮ ಒಡೆಯನಿಗಾಗಿ ಸೌಲನಿಗೂ ಅವನ ಸಂತಾನಕ್ಕೂ ಯೆಹೋವನು ಈ ಹೊತ್ತು ಮುಯ್ಯಿತೀರಿಸಿದ್ದಾನೆ ಅಂದರು.",
			"9": "<sup>9</sup> ದಾವೀದನು ಬೇರೋತಿನ ರಿಮ್ಮೋನನ ಮಕ್ಕಳಾದ ರೇಕಾಬ್, ಬಾಣ ಎಂಬವರಿಗೆ -",
			"10": "<sup>10</sup> ಸೌಲನ ಮರಣವಾರ್ತೆಯನ್ನು ತಿಳಿಸುವದು ಶುಭಕರವೆಂದು ಭಾವಿಸಿ ಬಂದು ನನಗೆ ಹೇಳಿದವನನ್ನು ಹಿಡಿದು ಅವನಿಗೆ ಚಿಕ್ಲಗಿನಲ್ಲಿ ಮರಣದಂಡನೆಯೆಂಬ ಬಹುಮಾನವನ್ನು ಸಲ್ಲಿಸಿದೆನಲ್ಲವೋ?",
			"11": "<sup>11</sup> ಮಹಾದುಷ್ಟರಾದ ನೀವು ನೀತಿವಂತನ ಮನೆಹೊಕ್ಕು ಅವನನ್ನು ಅವನ ಮಂಚದ ಮೇಲೆಯೇ ಕೊಂದು ಹಾಕಿದ ಮೇಲೆ ಆ ರಕ್ತಾಪರಾಧಕ್ಕಾಗಿ ನಿಮಗೆ ಮುಯ್ಯಿತೀರಿಸುವದು ಎಷ್ಟೋ ಅಗತ್ಯವಾಗಿದೆ. ನನ್ನ ಪ್ರಾಣವನ್ನು ಎಲ್ಲಾ ಇಕ್ಕಟ್ಟುಗಳಿಂದ ಬಿಡಿಸಿದ ಯೆಹೋವನಾಣೆ, ನಿಮ್ಮನ್ನು ಭೂಲೋಕದಿಂದ ತೆಗೆದೇ ಬಿಡುವೆನು ಎಂದು ಹೇಳಿ",
			"12": "<sup>12</sup> ತನ್ನ ಆಳುಗಳಿಗೆ ಆಜ್ಞಾಪಿಸಲು ಅವರು ಇವರನ್ನು ಕೊಂದು ಕೈಕಾಲುಗಳನ್ನು ಕತ್ತರಿಸಿ ಶವಗಳನ್ನು ಹೆಬ್ರೋನಿನ ಕೆರೆಯ ಬಳಿಯಲ್ಲಿ ನೇತುಹಾಕಿದರು. ಈಷ್ಬೋಶೆತನ ತಲೆಯನ್ನು ಹೆಬ್ರೋನಿನಲ್ಲಿದ್ದ ಅಬ್ನೇರನ ಸಮಾಧಿಯಲ್ಲಿ ಇಡಿಸಿದನು."
		},
		"5": {
			"1": "<sup>1</sup> ಅನಂತರ ಇಸ್ರಾಯೇಲ್ಯರ ಎಲ್ಲಾ ಕುಲಗಳವರು ಹೆಬ್ರೋನಿನಲ್ಲಿದ್ದ ದಾವೀದನ ಬಳಿಗೆ ಬಂದು ಅವನಿಗೆ -",
			"2": "<sup>2</sup> ನಾವು ನಿನ್ನ ರಕ್ತಸಂಬಂಧಿಗಳಾಗಿದ್ದೇವೆ; ಹಿಂದಿನ ದಿವಸಗಳಲ್ಲಿ ಅಂದರೆ ಸೌಲನ ಆಳಿಕೆಯಲ್ಲಿ ಇಸ್ರಾಯೇಲ್ಯರ ದಳಪತಿಯಾಗಿ ಹೋಗುತ್ತಾ ಬರುತ್ತಾ ಇದ್ದವನು ನೀನೇ. ನಿನ್ನನ್ನು ಕುರಿತು ಯೆಹೋವನು - ನೀನು ನನ್ನ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಾಯೇಲ್ಯರ ನಾಯಕನೂ ಪಾಲಕನೂ ಆಗಿರುವಿಯೆಂದು ಹೇಳಿದ್ದಾನಷ್ಟೆ ಅಂದರು.",
			"3": "<sup>3</sup> ಹೆಬ್ರೋನಿನಲ್ಲಿದ್ದ ಅರಸನಾದ ದಾವೀದನು ತನ್ನ ಬಳಿಗೆ ಬಂದಿದ್ದ ಇಸ್ರಾಯೇಲ್ಯರ ಹಿರಿಯರೆಲ್ಲರೊಡನೆ ಅಲ್ಲೇ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಒಡಂಬಡಿಕೆಮಾಡಿಕೊಂಡನು. ಅವರು ದಾವೀದನನ್ನು ಅಭಿಷೇಕಿಸಿ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನನ್ನಾಗಿ ಮಾಡಿದರು.",
			"4": "<sup>4</sup> ದಾವೀದನು ಮೂವತ್ತು ವರುಷದವನಾದಾಗ ಅರಸನಾಗಿ ನಾಲ್ವತ್ತು ವರುಷ ಆಳಿದನು.",
			"5": "<sup>5</sup> ಅವನು ಹೆಬ್ರೋನಿನಲ್ಲಿದ್ದುಕೊಂಡು ಯೆಹೂದಕುಲವೊಂದನ್ನೇ ಆಳಿದ್ದು ಏಳು ವರುಷವೂ ಆರು ತಿಂಗಳೂ; ಯೆರೂಸಲೇಮಿನಲ್ಲಿದ್ದುಕೊಂಡು ಯೆಹೂದ್ಯರನ್ನೂ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರನ್ನೂ ಆಳಿದ್ದು ಮೂವತ್ತುಮೂರು ವರುಷ.",
			"6": "<sup>6</sup> ದಾವೀದನು ತನ್ನ ಜನರನ್ನು ಕರಕೊಂಡು ಯೆರೂಸಲೇಮಿನಲ್ಲಿದ್ದ ದೇಶದ ಮೂಲನಿವಾಸಿಗಳಾದ ಯೆಬೂಸಿಯರಿಗೆ ವಿರೋಧವಾಗಿ ಹೊರಟನು. ಅವರು ಇವನು ಒಳಗೆ ಬರಲಾರನೆಂದು ನೆನಸಿ ದಾವೀದನಿಗೆ - ನೀನು ಒಳಗೆ ಬರಲಾರಿ; ಕುರುಡರು, ಕುಂಟರು ಇವರೇ ನಿನ್ನನ್ನು ಅಟ್ಟಿಬಿಡುವರು ಎಂದು ಹೇಳಿದರು.",
			"7": "<sup>7</sup> ಆದರೂ ಅವನು ಈಗ ದಾವೀದನಗರವೆನಿಸಿಕೊಳ್ಳುವ ಚೀಯೋನ್ ಕೋಟೆಯನ್ನು ಸ್ವಾಧೀನಪಡಿಸಿಕೊಂಡನು.",
			"8": "<sup>8</sup> ಆ ದಿವಸ ದಾವೀದನು - ಯೆಬೂಸಿಯರನ್ನು ಜಯಿಸಬೇಕೆಂದಿರುವವನು ಜಲದ್ವಾರದಲ್ಲಿ ಹತ್ತಿಹೋಗಿ ದಾವೀದನಿಗೆ ಅಸಹ್ಯರಾಗಿರುವ ಕುರುಡರನ್ನೂ ಕುಂಟರನ್ನೂ ಕಂದಕದಲ್ಲಿ ನಾಶಮಾಡಿಬಿಡಲಿ ಎಂದು ಹೇಳಿದನು. ಆದದರಿಂದ ಕುರುಡರೂ ಕುಂಟರೂ ಇದ್ದಾರೆ, ಒಳಗೆ ಹೋಗಲಾಗದು ಎಂದು ಗಾದೆಯುಂಟು.",
			"9": "<sup>9</sup> ದಾವೀದನು ಆ ಕೋಟೆಗೆ ದಾವೀದನಗರವೆಂದು ಹೆಸರಿಟ್ಟು ಅಲ್ಲೇ ವಾಸಿಸುತ್ತಾ ಅದರ ಸುತ್ತಲೂ ಮಿಲ್ಲೋವಿನಿಂದ ಪ್ರಾರಂಭಿಸಿ ಒಳಗಣ ಪೌಳಿಗೋಡೆಯನ್ನು ಕಟ್ಟಿಸಿದನು.",
			"10": "<sup>10</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವದೇವರು ಅವನ ಸಂಗಡ ಇದ್ದದರಿಂದ ಅವನು ಅಭಿವೃದ್ಧಿಹೊಂದುತ್ತಾ ಹೋದನು.",
			"11": "<sup>11</sup> ತೂರಿನ ಅರಸನಾದ ಹೀರಾಮನು ದಾವೀದನಿಗೆ ದೂತರನ್ನೂ ದೇವದಾರುಮರಗಳನ್ನೂ ಬಡಗಿಯವರನ್ನೂ ಶಿಲ್ಪಿಗಳನ್ನೂ ಕಳುಹಿಸಲು ಅವರು ಇವನಿಗೋಸ್ಕರ ಅರಮನೆಯನ್ನು ಕಟ್ಟಿದರು.",
			"12": "<sup>12</sup> ಇದರಿಂದ ದಾವೀದನು - ಯೆಹೋವನು ನನ್ನನ್ನು ಇಸ್ರಾಯೇಲ್ ರಾಜನನ್ನಾಗಿ ಸ್ಥಿರಪಡಿಸಿ ತನ್ನ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಾಯೇಲ್ಯರ ನಿಮಿತ್ತವಾಗಿ ನನ್ನ ರಾಜ್ಯವನ್ನು ವೃದ್ಧಿಪಡಿಸಿದನೆಂದು ತಿಳಿದುಕೊಂಡನು.",
			"13": "<sup>13</sup> ದಾವೀದನು ಹೆಬ್ರೋನಿನಿಂದ ಯೆರೂಸಲೇಮಿಗೆ ಬಂದ ಮೇಲೆ ಅಲ್ಲಿಯೂ ಅನೇಕ ಸ್ತ್ರೀಯರನ್ನು ಪತ್ನಿಯರನ್ನಾಗಿಯೂ ಉಪಪತ್ನಿಯರನ್ನಾಗಿಯೂ ಮಾಡಿಕೊಂಡದರಿಂದ ಅವನಿಗೆ ಇನ್ನೂ ಕೆಲವು ಮಂದಿ ಗಂಡುಹೆಣ್ಣುಮಕ್ಕಳು ಹುಟ್ಟಿದರು.",
			"14": "<sup>14</sup> ಅಲ್ಲಿ ಅವನಿಗೆ ಹುಟ್ಟಿದವರ ಹೆಸರುಗಳು - ಶಮ್ಮೂವ, ಶೋಬಾಬ್,",
			"15": "<sup>15</sup> ನಾತಾನ್, ಸೊಲೊಮೋನ್, ಇಬ್ಹಾರ್, ಎಲೀಷೂವ,",
			"16": "<sup>16</sup> ನೆಫೆಗ್, ಯಾಫೀಯ, ಎಲೀಷಾಮ, ಎಲ್ಯಾದ, ಎಲೀಫೆಲೆಟ್.",
			"17": "<sup>17</sup> ಇಸ್ರಾಯೇಲ್ಯರು ದಾವೀದನನ್ನು ಅಭಿಷೇಕಿಸಿ ತಮ್ಮ ಅರಸನನ್ನಾಗಿ ಮಾಡಿಕೊಂಡರೆಂಬ ವರ್ತಮಾನವು ಫಿಲಿಷ್ಟಿಯರಿಗೆ ಮುಟ್ಟಿದಾಗ ಅವರು ಅವನನ್ನು ಹಿಡಿಯುವದಕ್ಕೆ ಹೊರಟರು. ದಾವೀದನು ಇದನ್ನು ಕೇಳಿ ದುರ್ಗಕ್ಕೆ ಹೋದನು.",
			"18": "<sup>18</sup> ಫಿಲಿಷ್ಟಿಯರು ದೇಶದೊಳಗೆ ನುಗ್ಗಿ ರೆಫಾಯೀಮ್ ತಗ್ಗಿಗೆ ಬಂದು ಪಾಳೆಯ ಮಾಡಿಕೊಂಡಿದ್ದಾರೆಂದು ತಿಳಿದು",
			"19": "<sup>19</sup> ದಾವೀದನು ಯೆಹೋವನನ್ನು - ನಾನು ಫಿಲಿಷ್ಟಿಯರಿಗೆ ವಿರೋಧವಾಗಿ ಹೋಗಬಹುದೋ? ಅವರನ್ನು ನನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸಿ ಕೊಡುವಿಯೋ ಎಂದು ಕೇಳಲು ಆತನು - ಹೋಗು, ನಾನು ಹೇಗೂ ಅವರನ್ನು ನಿನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸುವೆನು ಎಂದು ಉತ್ತರಕೊಟ್ಟದರಿಂದ",
			"20": "<sup>20</sup> ಅವನು ಹೋಗಿ ಅವರನ್ನು ಸೋಲಿಸಿ ಯೆಹೋವನು ಕಟ್ಟೆಯೊಡೆದ ಪ್ರವಾಹದಂತೆ ತನ್ನ ಶತ್ರುಗಳ ಮೇಲೆ ಬಿದ್ದು ಅವರನ್ನು ನನ್ನ ಕಣ್ಣ ಮುಂದೆಯೇ ನಾಶಮಾಡಿದ್ದಾನೆಂದು ಹೇಳಿ ಆ ಯುದ್ಧಸ್ಥಳಕ್ಕೆ ಬಾಳ್‍ಪೆರಾಚೀಮೆಂದು ಹೆಸರಿಟ್ಟನು.",
			"21": "<sup>21</sup> ಫಿಲಿಷ್ಟಿಯರು ಅಲ್ಲಿ ಬಿಟ್ಟುಹೋಗಿದ್ದ ವಿಗ್ರಹಗಳನ್ನು ದಾವೀದನೂ ಅವನ ಜನರೂ ತೆಗೆದುಕೊಂಡು ಬಂದರು.",
			"22": "<sup>22</sup> ಫಿಲಿಷ್ಟಿಯರು ಇನ್ನೊಮ್ಮೆ ಹೊರಟು ಬಂದು ರೆಫಾಯೀಮ್ ತಗ್ಗಿನಲ್ಲಿ ಇಳುಕೊಂಡರು.",
			"23": "<sup>23</sup> ದಾವೀದನು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ವಿಚಾರಿಸಿದಾಗ ಆತನು ಅವನಿಗೆ - ನೀನು ನೆಟ್ಟಗೆ ಹೋಗಿ ಅವರ ಮೇಲೆ ಬೀಳಬೇಡ; ಅವರ ಹಿಂದಳವನ್ನು ಸುತ್ತಿಕೊಂಡು ಹೋಗಿ ಬಾಕಾ ಮರಗಳಿರುವ ಕಡೆಯಿಂದ ಅವರ ಮೇಲೆ ಬೀಳು.",
			"24": "<sup>24</sup> ಆ ಮರಗಳ ತುದಿಯಲ್ಲಿ ಹೆಜ್ಜೆಗಳ ಸಪ್ಪಳ ಕೇಳಿಸುವಾಗ ಯೆಹೋವನು ಫಿಲಿಷ್ಟಿಯರ ಸೈನ್ಯವನ್ನು ಸೋಲಿಸುವದಕ್ಕೋಸ್ಕರ ನಿನ್ನ ಮುಂದಾಗಿ ಹೊರಟನೆಂದು ತಿಳಿದುಕೊಂಡು ಅವರ ಮೇಲೆ ಬೀಳು ಅಂದನು.",
			"25": "<sup>25</sup> ದಾವೀದನು ಯೆಹೋವನ ಆಜ್ಞಾನುಸಾರವಾಗಿ ಮಾಡಿ ಫಿಲಿಷ್ಟಿಯರನ್ನು ಗೆಬದಿಂದ ಗೆಜೆರಿನವರೆಗೂ ಸಂಹರಿಸಿದನು."
		},
		"6": {
			"1": "<sup>1</sup> ಅನಂತರ ದಾವೀದನು ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಮೂವತ್ತುಸಾವಿರ ಮಂದಿ ಶ್ರೇಷ್ಠ ಸೈನಿಕರನ್ನು ಆರಿಸಿಕೊಂಡು",
			"2": "<sup>2</sup> ದೇವರ ಮಂಜೂಷವನ್ನು ತರುವದಕ್ಕಾಗಿ ಅವರೆಲ್ಲರ ಸಹಿತ ಯೆಹೂದದೇಶದ ಬಾಳಾ ಎಂಬಲ್ಲಿಗೆ ಹೋದನು. ಆ ಮಂಜೂಷವು ದೇವನಾಮದಿಂದ ಅಂದರೆ ಕೆರೂಬಿಯರ ನಡುವೆ ಆಸೀನನಾಗಿರುವ ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನ ನಾಮದಿಂದ ಪ್ರಸಿದ್ಧವಾದದ್ದು.",
			"3": "<sup>3</sup> ಅವರು ದೇವರ ಮಂಜೂಷವನ್ನು ಗುಡ್ಡದ ಮೇಲಿರುವ ಅಬೀನಾದಾಬನ ಮನೆಯಿಂದ ಹೊರಗೆ ತಂದು ಅದನ್ನು ಒಂದು ಹೊಸ ಬಂಡಿಯ ಮೇಲೆ ಏರಿಸಿದರು. ಅಬೀನಾದಾಬನ ಮಕ್ಕಳಾದ ಉಜ್ಜನೂ ಅಹಿಯೋವನೂ ಬಂಡಿಯನ್ನು ಹೊಡೆದರು.",
			"4": "<sup>4</sup> ಗುಡ್ಡದ ಮೇಲೆ ವಾಸವಾಗಿದ್ದ ಅಬೀನಾದಾಬನ ಮನೆಯಿಂದ ತೆಗೆಯಲ್ಪಟ್ಟ ದೇವರ ಮಂಜೂಷದ ಮುಂದೆ ಅಹಿಯೋವನು ಹೋಗುತ್ತಿದ್ದನು.",
			"5": "<sup>5</sup> ದಾವೀದನೂ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರೂ ಕಿನ್ನರಿ, ಸ್ವರಮಂಡಲ, ದಮ್ಮಡಿ, ಝಲ್ಲರಿ, ತಾಳ ಇವುಗಳನ್ನು ಬಾರಿಸಿಕೊಂಡು ಪೂರ್ಣಾಸಕ್ತಿಯಿಂದ ಗೀತಗಳನ್ನು ಹಾಡುತ್ತಾ ಯೆಹೋವನ ಮುಂದೆ ನರ್ತನಮಾಡುತ್ತಾ ಹೋದರು.",
			"6": "<sup>6</sup> ಅವರು ನಾಕೋನನ ಕಣಕ್ಕೆ ಬಂದಾಗ ಎತ್ತುಗಳು ಎಡವಿದ್ದರಿಂದ ಉಜ್ಜನು ಕೈಚಾಚಿ ದೇವರ ಮಂಜೂಷವನ್ನು ಹಿಡಿದನು.",
			"7": "<sup>7</sup> ಆಗ ಯೆಹೋವನು ಉಜ್ಜನ ಮೇಲೆ ಕೋಪಗೊಂಡು ಈ ತಪ್ಪಿನ ಸಲವಾಗಿ ಅವನನ್ನು ಹತಮಾಡಿದನು; ಅವನು ಅಲ್ಲೇ ದೇವರ ಮಂಜೂಷದ ಬಳಿಯಲ್ಲಿ ಸತ್ತನು.",
			"8": "<sup>8</sup> ಯೆಹೋವನು ಉಜ್ಜನನ್ನು ಮುರಿದುಬಿಟ್ಟದ್ದರಿಂದ ದಾವೀದನು ಉರಿಗೊಂಡು ಆ ಸ್ಥಳಕ್ಕೆ ಪೆರೆಚುಜ್ಜಾ ಎಂಬ ಹೆಸರಿಟ್ಟನು. ಅದಕ್ಕೆ ಇಂದಿನವರೆಗೂ ಇದೇ ಹೆಸರಿರುತ್ತದೆ.",
			"9": "<sup>9</sup> ಆ ದಿವಸ ದಾವೀದನು ಯೆಹೋವನಿಗೆ ಭಯಪಟ್ಟು - ಯೆಹೋವನ ಮಂಜೂಷವನ್ನು ನಾನಿರುವಲ್ಲಿಗೆ ತೆಗೆದುಕೊಂಡು ಹೋಗುವದು ಹೇಗೆ ಎಂದುಕೊಂಡು",
			"10": "<sup>10</sup> ಅದನ್ನು ದಾವೀದ ನಗರಕ್ಕೆ ತರಲೊಲ್ಲದೆ ಗತ್ ಊರಿನ ಓಬೇದೆದೋಮನ ಮನೆಗೆ ಕಳುಹಿಸಿದನು.",
			"11": "<sup>11</sup> ಯೆಹೋವನ ಮಂಜೂಷವು ಗತ್ ಊರಿನ ಓಬೇದೆದೋಮನ ಮನೆಯಲ್ಲಿ ಮೂರು ತಿಂಗಳು ಇತ್ತು. ಆ ಕಾಲದಲ್ಲಿ ಯೆಹೋವನು ಅವನನ್ನೂ ಅವನ ಮನೆಯವರನ್ನೂ ಆಶೀರ್ವದಿಸಿದನು.",
			"12": "<sup>12</sup> ಯೆಹೋವನು ಹೀಗೆ ತನ್ನ ಮಂಜೂಷದ ದೆಸೆಯಿಂದ ಓಬೇದೆದೋಮನನ್ನೂ ಅವನಿಗಿರುವದೆಲ್ಲವನ್ನೂ ಆಶೀರ್ವದಿಸಿದ್ದಾನೆಂಬ ವರ್ತಮಾನವು ದಾವೀದನಿಗೆ ಮುಟ್ಟಿದಾಗ ಅವನು ಹೋಗಿ ಓಬೇದೆದೋಮನ ಮನೆಯಲ್ಲಿದ್ದ ದೇವರ ಮಂಜೂಷವನ್ನು ಉತ್ಸವದಿಂದ ದಾವೀದನಗರಕ್ಕೆ ತಂದನು.",
			"13": "<sup>13</sup> ಯೆಹೋವನ ಮಂಜೂಷವನ್ನು ಹೊತ್ತವರು ಆರು ಹೆಜ್ಜೆ ನಡೆದ ನಂತರ ದಾವೀದನು ಒಂದು ಎತ್ತನ್ನೂ ಕೊಬ್ಬಿದ ಕರುವನ್ನೂ ಯಜ್ಞಮಾಡಿದನು.",
			"14": "<sup>14</sup> ದಾವೀದನು ನಾರಿನ ಏಫೋದನ್ನು ಧರಿಸಿಕೊಂಡವನಾಗಿ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಪೂರ್ಣಾಸಕ್ತಿಯಿಂದ ಕುಣಿದಾಡಿದನು.",
			"15": "<sup>15</sup> ಹೀಗೆ ದಾವೀದನೂ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರೂ ಆರ್ಭಟಿಸುತ್ತಾ ತುತೂರಿ ಊದುತ್ತಾ ಯೆಹೋವನ ಮಂಜೂಷವನ್ನು ತಂದರು.",
			"16": "<sup>16</sup> ಮಂಜೂಷವು ದಾವೀದನಗರದೊಳಗೆ ಬರುತ್ತಿರುವಾಗ ಸೌಲನ ಮಗಳಾದ ಮೀಕಲಳು ಕಿಟಕಿಯಿಂದ ಹಣಿಕಿ ನೋಡಿ ದಾವೀದನು ಯೆಹೋವನ ಮುಂದೆ ಜಿಗಿಯುತ್ತಾ ಕುಣಿಯುತ್ತಾ ಇರುವದನ್ನು ಕಂಡು ಮನಸ್ಸಿನಲ್ಲಿ ಅವನನ್ನು ತಿರಸ್ಕರಿಸಿದಳು.",
			"17": "<sup>17</sup> ಜನರು ಯೆಹೋವನ ಮಂಜೂಷವನ್ನು ತಂದು ದಾವೀದನು ಹಾಕಿಸಿದ ಗುಡಾರದೊಳಗೆ ನಿಯಮಿತ ಸ್ಥಳದಲ್ಲಿಟ್ಟರು; ಆಗ ದಾವೀದನು ಯೆಹೋವನಿಗೋಸ್ಕರ ಸರ್ವಾಂಗಹೋಮಗಳನ್ನೂ ಸಮಾಧಾನಯಜ್ಞಗಳನ್ನೂ ಸಮರ್ಪಿಸಿದನು.",
			"18": "<sup>18</sup> ಇದಾದ ಮೇಲೆ ಅವನು ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನ ಹೆಸರಿನಿಂದ ಎಲ್ಲರನ್ನೂ ಆಶೀರ್ವದಿಸಿ",
			"19": "<sup>19</sup> ಸಭೆಯಾಗಿ ನೆರೆದು ಬಂದಿರುವ ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬ ಗಂಡಸಿಗೂ ಹೆಂಗಸಿಗೂ ಒಂದು ರೊಟ್ಟಿಯನ್ನೂ ಒಂದು ತುಂಡು ಮಾಂಸವನ್ನೂ ದ್ರಾಕ್ಷೇ ಹಣ್ಣಿನ ಉಂಡೆಯನ್ನೂ ಕೊಡಿಸಿದನು. ತರುವಾಯ ಜನರೆಲ್ಲರೂ ತಮ್ಮತಮ್ಮ ಮನೆಗಳಿಗೆ ಹೋದರು.",
			"20": "<sup>20</sup> ಅನಂತರ ದಾವೀದನು ತನ್ನ ಮನೆಯವರನ್ನು ಆಶೀರ್ವದಿಸುವದಕ್ಕೆ ಹೋದಾಗ ಸೌಲನ ಮಗಳಾದ ಮೀಕಲಳು ಅವನೆದುರಿಗೆ ಬಂದು - ಈ ಹೊತ್ತು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನು ಎಂಥ ಗೌರವದಿಂದ ನಡೆದುಕೊಂಡನು! ನೀಚರಲ್ಲೊಬ್ಬನಂತೆ ತನ್ನ ಜನರ ದಾಸಿಯರ ಮುಂದೆ ಬೆತ್ತಲೆಯಾಗಿದ್ದನಲ್ಲಾ ಅಂದಳು. ಆಗ ದಾವೀದನು ಆಕೆಗೆ -",
			"21": "<sup>21</sup> ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿಯಷ್ಟೆ; ನಿನ್ನ ತಂದೆಯನ್ನೂ ಅವನ ಮನೆಯವರೆಲ್ಲರನ್ನೂ ಬಿಟ್ಟು ನನ್ನನ್ನೇ ಆರಿಸಿಕೊಂಡು ತನ್ನ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನನ್ನಾಗಿ ಮಾಡಿದ ಯೆಹೋವನ ಮುಂದೆ ಇನ್ನೂ ಕುಣಿದಾಡುವೆನು.",
			"22": "<sup>22</sup> ಇದಕ್ಕಿಂತ ಇನ್ನೂ ಹೆಚ್ಚಾಗಿ ತಿರಸ್ಕಾರಹೊಂದುವದಕ್ಕೂ ನನ್ನನ್ನು ಅಲ್ಪನೆಂದು ಭಾವಿಸಿಕೊಳ್ಳುವದಕ್ಕೂ ಸಿದ್ಧನಾಗಿದ್ದೇನೆ. ನೀನು ಹೇಳಿದ ದಾಸಿಯರಾದರೋ ಹೇಗೂ ನನ್ನನ್ನು ಸನ್ಮಾನಿಸುವರು ಅಂದನು.",
			"23": "<sup>23</sup> ಸೌಲನ ಮಗಳಾದ ಮೀಕಲಳಿಗೆ ಜೀವದಿಂದಿರುವವರೆಗೂ ಮಕ್ಕಳೇ ಆಗಲಿಲ್ಲ."
		},
		"7": {
			"1": "<sup>1</sup> ಯೆಹೋವನ ಅನುಗ್ರಹದಿಂದ ಸುತ್ತಣ ವೈರಿಗಳ ಭಯವೆಲ್ಲಾ ತಪ್ಪಿಹೋಗಿ ಅರಸನು ಅರಮನೆಯಲ್ಲಿ ಸಮಾಧಾನದಿಂದ ವಾಸಿಸುತ್ತಿದ್ದಾಗ",
			"2": "<sup>2</sup> ಒಂದು ದಿವಸ ಪ್ರವಾದಿಯಾದ ನಾತಾನನಿಗೆ - ನೋಡು, ನಾನು ದೇವದಾರುಮರದ ಮನೆಯಲ್ಲಿ ವಾಸವಾಗಿದ್ದೇನೆ; ದೇವರ ಮಂಜೂಷವಾದರೋ ಬಟ್ಟೆಯ ಮನೆಯಲ್ಲಿ ಇರುತ್ತದೆ ಎಂದು ಹೇಳಿದನು.",
			"3": "<sup>3</sup> ನಾತಾನನು ಅರಸನಿಗೆ - ಆಗಲಿ ಮನಸ್ಸಿದ್ದಂತೆ ಮಾಡು, ಯೆಹೋವನು ನಿನ್ನ ಸಂಗಡ ಇರುತ್ತಾನೆ ಅಂದನು.",
			"4": "<sup>4</sup> ಅದೇ ರಾತ್ರಿಯಲ್ಲಿ ಯೆಹೋವನು ನಾತಾನನಿಗೆ ಆಜ್ಞಾಪಿಸಿದ್ದೇನಂದರೆ -",
			"5": "<sup>5</sup> ಹೋಗಿ ನನ್ನ ಸೇವಕನಾದ ದಾವೀದನಿಗೆ - ನೀನು ನನಗೋಸ್ಕರ ಮನೆಯನ್ನು ಕಟ್ಟಬೇಕೋ?",
			"6": "<sup>6</sup> ನಾನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಐಗುಪ್ತ ದೇಶದಿಂದ ಬರಮಾಡಿದಂದಿನಿಂದ ಇಂದಿನವರೆಗೂ ಮನೆಯಲ್ಲಿ ವಾಸಮಾಡಲಿಲ್ಲ. ಗುಡಾರದಲ್ಲೇ ವಾಸಿಸುತ್ತಾ [ಅವರೊಡನೆ] ಸಂಚರಿಸಿದೆನು.",
			"7": "<sup>7</sup> ನಾನು ಇಸ್ರಾಯೇಲ್ಯರ ಮಧ್ಯದಲ್ಲಿ ಸಂಚರಿಸುತ್ತಿದ್ದಾಗ ನನ್ನ ಜನರಾದ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಪಾಲಿಸುವದಕ್ಕೋಸ್ಕರ ನನ್ನಿಂದ ನೇಮಿಸಲ್ಪಟ್ಟ ಯಾವ ಕುಲದವರನ್ನಾದರೂ - ನೀವು ನನಗೋಸ್ಕರ ದೇವದಾರುಮರದ ಮನೆಯನ್ನೇಕೆ ಕಟ್ಟಲಿಲ್ಲವೆಂದು ಕೇಳಿದೆನೋ ಎಂಬದಾಗಿ ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ ಎಂದು ಹೇಳು.",
			"8": "<sup>8</sup> ಇದಲ್ಲದೆ ನೀನು ನನ್ನ ಸೇವಕನಾದ ದಾವೀದನಿಗೆ ಹೇಳಬೇಕಾದದ್ದೇನಂದರೆ - ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ಕುರಿಗಳ ಹಿಂದೆ ಹೋಗುತ್ತಿದ್ದ ನಿನ್ನನ್ನು ಅಡವಿಯಿಂದ ತೆಗೆದುಕೊಂಡು ನನ್ನ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಾಯೇಲ್ಯರ ಮೇಲೆ ನಾಯಕನನ್ನಾಗಿ ನೇಮಿಸಿದೆನು.",
			"9": "<sup>9</sup> ನೀನು ಹೋದಲ್ಲೆಲ್ಲಾ ನಿನ್ನ ಸಂಗಡ ಇದ್ದೆನು; ನಿನ್ನ ಶತ್ರುಗಳನ್ನೆಲ್ಲಾ ನಿನ್ನೆದುರಿನಲ್ಲಿಯೇ ಸಂಹರಿಸಿಬಿಟ್ಟೆನು. ಲೋಕದ ಮಹಾಪುರುಷರ ಹೆಸರಿನಂತೆ ನಿನ್ನ ಹೆಸರನ್ನೂ ದೊಡ್ಡದುಮಾಡುವೆನು.",
			"10": "<sup>10</sup> ನನ್ನ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಒಂದು ಸ್ಥಳವನ್ನು ಕೊಟ್ಟು ಅದರಲ್ಲಿ ಅವರನ್ನು ನೆಲೆಗೊಳಿಸುವೆನು. ಅವರು ಇನ್ನು ಮೇಲೆ ಯಾವ ಭಯವೂ ಇಲ್ಲದೆ ಅಲ್ಲಿ ವಾಸಿಸುವರು.",
			"11": "<sup>11</sup> ಪೂರ್ವಕಾಲದಲ್ಲೂ ನಾನು ನನ್ನ ಜನರಾದ ಇಸ್ರಾಯೇಲ್ಯರ ಮೇಲೆ ನೇಮಿಸಿದ ನ್ಯಾಯಸ್ಥಾಪಕರ ಕಾಲದಿಂದೀಚೆಗೂ ದುಷ್ಟರು ಅವರನ್ನು ಕುಗ್ಗಿಸಿದಂತೆ ಇನ್ನು ಕುಗ್ಗಿಸುವದಿಲ್ಲ. ನೀನು ಶತ್ರು ಭಯವಿಲ್ಲದೆ ಸುಖದಿಂದಿರುವಂತೆ ಮಾಡುವೆನು. ಇದಲ್ಲದೆ ಯೆಹೋವನಾದ ನಾನು ನಿನಗೋಸ್ಕರ ಒಂದು ಮನೆಯನ್ನು ಕಟ್ಟುವೆನೆಂದು ಮಾತುಕೊಡುತ್ತೇನೆ.",
			"12": "<sup>12</sup> ನಿನ್ನ ಆಯುಷ್ಕಾಲವು ಮುಗಿದು ನೀನು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಿದ ಮೇಲೆ ನಿನ್ನಿಂದ ಹುಟ್ಟುವವನನ್ನು ನೆಲೆಗೊಳಿಸಿ ಅವನ ರಾಜ್ಯವನ್ನು ಸ್ಥಿರಪಡಿಸುವೆನು.",
			"13": "<sup>13</sup> ಅವನು ನನ್ನ ಹೆಸರಿಗಾಗಿ ಒಂದು ಮನೆಯನ್ನು ಕಟ್ಟುವನು; ನಾನು ಅವನ ರಾಜ್ಯಸಿಂಹಾಸನವನ್ನು ನಿರಂತರವಾಗಿ ಸ್ಥಿರಪಡಿಸುವೆನು.",
			"14": "<sup>14</sup> ನಾನು ಅವನಿಗೆ ತಂದೆಯಾಗಿರುವೆನು, ಅವನು ನನಗೆ ಮಗನಾಗಿರುವನು; ಅವನು ತಪ್ಪುಮಾಡಿದರೆ ಅವನಿಗೆ ಮನುಷ್ಯಶಿಕ್ಷೆಯನ್ನು ಕೊಡುವೆನು, ನರಪುತ್ರರಂತೆ ಅವನನ್ನು ದಂಡಿಸುವೆನು.",
			"15": "<sup>15</sup> ಆದರೂ ನನ್ನ ಕೃಪೆಯು ಸೌಲನನ್ನು ಬಿಟ್ಟುಹೊದ ಹಾಗೆ ಅವನನ್ನು ಬಿಟ್ಟುಹೋಗುವದಿಲ್ಲ. ನಾನು ಸೌಲನನ್ನು ನಿನ್ನೆದುರಿನಿಂದ ಹೊರಡಿಸಿಬಿಟ್ಟೆನಲ್ಲಾ!",
			"16": "<sup>16</sup> ನಿನ್ನ ಮನೆಯೂ ಅರಸುತನವೂ ಸದಾಕಾಲ ಸ್ಥಿರವಾಗಿರುವವು; ನಿನ್ನ ಸಿಂಹಾಸನವು ಶಾಶ್ವತವಾಗಿರುವದು ಎಂಬದೇ.",
			"17": "<sup>17</sup> ನಾತಾನನು ತನಗುಂಟಾದ ದರ್ಶನದ ಈ ಎಲ್ಲಾ ಮಾತುಗಳನ್ನು ದಾವೀದನಿಗೆ ಹೇಳಿದನು.",
			"18": "<sup>18</sup> ಅನಂತರ ದಾವೀದನು ಹೋಗಿ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಕೂತುಕೊಂಡು - ಕರ್ತನೇ, ಯೆಹೋವನೇ, ನಾನೆಷ್ಟರವನು? ನನ್ನ ಮನೆ ಎಷ್ಟರದು? ನೀನು ನನ್ನನ್ನು ಇಲ್ಲಿಯವರೆಗೂ ತಂದಿದ್ದೀಯಲ್ಲಾ;",
			"19": "<sup>19</sup> ಕರ್ತನೇ, ಯೆಹೋವನೇ, ಇದು ಸಾಲದೆಂದೆಣಿಸಿ ನಿನ್ನ ಸೇವಕನ ಬಹುದೂರಸಂತಾನದ ವಿಷಯದಲ್ಲಿಯೂ ವಾಗ್ದಾನಮಾಡಿದಿ. ಕರ್ತನೇ, ಯೆಹೋವನೇ, ನೀನು ನರಪ್ರಾಣಿಯನ್ನು ಹೀಗೆ ನಡಿಸಿದ್ದು ಆಶ್ಚರ್ಯ.",
			"20": "<sup>20</sup> ಕರ್ತನೇ, ಯೆಹೋವನೇ, ನೀನು ನಿನ್ನ ಸೇವಕನಾದ ದಾವೀದನನ್ನು ಬಲ್ಲೆ; ನಾನು ಇನ್ನೇನು ಹೇಳಲಿ?",
			"21": "<sup>21</sup> ನೀನು ನಿನ್ನ ವಾಗ್ದಾನಗಳನ್ನು ನೆರವೇರಿಸುವದಕ್ಕೋಸ್ಕರ ನಿನ್ನ ಇಷ್ಟಾನುಸಾರವಾಗಿ ಈ ಎಲ್ಲಾ ಮಹತ್ಕಾರ್ಯಗಳನ್ನು ನಡಿಸಿ ಅವುಗಳ ಅನುಭವವನ್ನು ನಿನ್ನ ಸೇವಕನಿಗೆ ಉಂಟುಮಾಡಿದ್ದೀ.",
			"22": "<sup>22</sup> ದೇವರೇ, ಯೆಹೋವನೇ, ನೀನೇ ದೊಡ್ಡವನು; ನಿನ್ನ ಸಮಾನರು ಯಾರೂ ಇಲ್ಲ. ನಾವು ಕೇಳಿದವುಗಳನ್ನೆಲ್ಲಾ ಆಲೋಚಿಸಿ ನೋಡಿದರೆ ನಿನ್ನ ಹೊರತು ದೇವರೇ ಇಲ್ಲವೆಂಬದು ನಿಶ್ಚಯ.",
			"23": "<sup>23</sup> ನಿನ್ನ ಇಸ್ರಾಯೇಲ್ ಪ್ರಜೆಗೆ ಸಮಾನವಾದ ಜನಾಂಗವು ಲೋಕದಲ್ಲಿ ಯಾವದಿರುತ್ತದೆ? ನೀನೇ ಹೋಗಿ ಅದನ್ನು ವಿಮೋಚಿಸಿ ಸ್ವಪ್ರಜೆಯನ್ನಾಗಿ ಮಾಡಿಕೊಂಡು ನಿನ್ನ ಹೆಸರನ್ನು ಪ್ರಸಿದ್ಧಪಡಿಸಿದಿ. ಐಗುಪ್ತ್ಯರು ಮೊದಲಾದ ಅನ್ಯಜನರ ಕೈಗೂ ಅವರ ದೇವತೆಗಳ ಕೈಗೂ ಸಿಕ್ಕದಂತೆ ತಪ್ಪಿಸಿ ರಕ್ಷಿಸಿ ಅದರ ಎದುರಿನಲ್ಲೇ ನಿನ್ನ ದೇಶಕ್ಕೋಸ್ಕರ ಭಯಂಕರವಾದ ಮಹತ್ಕಾರ್ಯಗಳನ್ನು ನಡಿಸಿದಿ.",
			"24": "<sup>24</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಸದಾಕಾಲವೂ ನಿನ್ನ ಪ್ರಜೆಗಳಾಗಿರಬೇಕೆಂದು ನಿರ್ಣಯಿಸಿದಿ; ಯೆಹೋವನೇ, ನೀನು ಅವರಿಗೆ ದೇವರಾದಿ.",
			"25": "<sup>25</sup> ದೇವರೇ, ಯೆಹೋವನೇ, ನೀನು ನಿನ್ನ ಸೇವಕನನ್ನೂ ಅವನ ಮನೆಯನ್ನೂ ಕುರಿತು ಮಾಡಿದ ವಾಗ್ದಾನವನ್ನು ನೆರವೇರಿಸು. ಅದು ಸದಾ ಸ್ಥಿರವಾಗಿರಲಿ.",
			"26": "<sup>26</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು, ಇಸ್ರಾಯೇಲ್‍ದೇವರು ಎಂಬ ನಿನ್ನ ನಾಮಧೇಯಗಳಿಗೆ ಸದಾಕಾಲವೂ ಮಹಿಮೆಯುಂಟಾಗಲಿ. ನಿನ್ನ ಸೇವಕನಾದ ದಾವೀದನ ಮನೆಯು ನಿನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸ್ಥಿರವಾಗಿರಲಿ.",
			"27": "<sup>27</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೇ, ಇಸ್ರಾಯೇಲ್‍ದೇವರೇ, ನೀನು ನಿನ್ನ ಸೇವಕನಿಗೆ - ನಾನು ನಿನಗೋಸ್ಕರ ಮನೆ ಕಟ್ಟುವೆನೆಂದು ವಾಗ್ದಾನ ಮಾಡಿದ್ದರಿಂದ ಅವನು ಈ ಪ್ರಕಾರ ನಿನ್ನನ್ನು ಪ್ರಾರ್ಥಿಸುವದಕ್ಕೆ ಧೈರ್ಯಗೊಂಡನು.",
			"28": "<sup>28</sup> ಕರ್ತನೇ, ಯೆಹೋವನೇ, ನಿನ್ನ ಸೇವಕನಿಗೆ ಈ ಶ್ರೇಷ್ಠ ವಾಗ್ದಾನಗಳನ್ನು ಮಾಡಿದ ನೀನು ದೇವರಾಗಿರುತ್ತೀ; ನಿನ್ನ ವಾಕ್ಯವು ಸತ್ಯವಾದದ್ದು.",
			"29": "<sup>29</sup> ಆದದರಿಂದ ನೀನು ನಿನ್ನ ಸೇವಕನ ಮನೆಯನ್ನು ಆಶೀರ್ವದಿಸು; ಸದಾಕಾಲವೂ ಅದರ ಮೇಲೆ ನಿನ್ನ ಕಟಾಕ್ಷವಿರಲಿ. ಕರ್ತನೇ, ಯೆಹೋವನೇ, ವಾಗ್ದಾನಮಾಡಿದವನು ನೀನೇ. ನಿನ್ನ ಆಶೀರ್ವಾದದಿಂದ ನಿನ್ನ ಸೇವಕನ ಮನೆಯಲ್ಲಿ ನಿತ್ಯ ಸೌಭಾಗ್ಯವಿರಲಿ ಎಂದನು."
		},
		"8": {
			"1": "<sup>1</sup> ಅನಂತರ ದಾವೀದನು ಹೋಗಿ ಫಿಲಿಷ್ಟಿಯರ ಮೇಲೆ ಬಿದ್ದು ಅವರನ್ನು ಸೋಲಿಸಿ ಅವರ ರಾಜಧಾನಿಯನ್ನು ತೆಗೆದುಕೊಂಡು ಆಡಳಿತವನ್ನು ತನ್ನ ಸ್ವಾಧೀನದಲ್ಲಿಟ್ಟುಕೊಂಡನು.",
			"2": "<sup>2</sup> ಇದಲ್ಲದೆ ಅವನು ಮೋವಾಬ್ಯರನ್ನು ಸೋಲಿಸಿ ಅವರನ್ನು ನೆಲಕ್ಕೆ ಹಾಕಿಸಿ ಹಗ್ಗದಿಂದ ಅಳತೆ ಮಾಡಿಸಿ ಎರಡು ಪಾಲು ಜನರನ್ನು ಕೊಲ್ಲಿಸಿ ಒಂದು ಪಾಲು ಉಳಿಸಿದನು. ಮೋವಾಬ್ಯರು ದಾವೀದನ ದಾಸರಾಗಿ ಅವನಿಗೆ ಕಪ್ಪಕೊಡಬೇಕಾಯಿತು.",
			"3": "<sup>3</sup> ದಾವೀದನು ಯೂಫ್ರೇಟೀಸ್ ನದಿಯ ಸುತ್ತಣ ಪ್ರದೇಶದಲ್ಲಿ ತನ್ನ ರಾಜ್ಯಾಧಿಕಾರವನ್ನು ಸ್ಥಾಪಿಸುವದಕ್ಕೆ ಹೋಗುತ್ತಿದ್ದ ರೆಹೋಬನ ಮಗನೂ ಚೋಬದ ಅರಸನೂ ಆದ ಹದದೆಜೆರನನ್ನು ಸೋಲಿಸಿ",
			"4": "<sup>4</sup> ಅವನ ಸಾವಿರದ ಏಳುನೂರು ಮಂದಿ ರಾಹುತರನ್ನೂ ಇಪ್ಪತ್ತುಸಾವಿರ ಮಂದಿ ಕಾಲಾಳುಗಳನ್ನೂ ಸೆರೆಹಿಡಿದು ನೂರು ಕುದುರೆಗಳನ್ನಿಟ್ಟುಕೊಂಡು ಮಿಕ್ಕಾದ ಕುದುರೆಗಳ ಹಿಂಗಾಲಿನ ನರಗಳನ್ನು ಕೊಯ್ದುಬಿಟ್ಟನು.",
			"5": "<sup>5</sup> ದಮಸ್ಕದ ಅರಾಮ್ಯರು ಚೋಬದ ಅರಸನಾದ ಹದದೆಜೆರನನ್ನು ರಕ್ಷಿಸಬಂದಾಗ ದಾವೀದನು ಅವರನ್ನೂ ಸೋಲಿಸಿ ಅವರಲ್ಲಿ ಇಪ್ಪತ್ತೆರಡು ಸಾವಿರ ಜನರನ್ನು ಹತಿಸಿದನು.",
			"6": "<sup>6</sup> ದಾವೀದನು ದಮಸ್ಕದ ಅರಾಮ್ ದೇಶದಲ್ಲಿ ಕಾವಲುದಂಡನ್ನಿರಿಸಿದನು. ಹೀಗೆ ಅರಾಮ್ಯರು ಅವನ ಸೇವಕರಾಗಿ ಅವನಿಗೆ ಕಪ್ಪಕೊಡುವವರಾದರು. ಯೆಹೋವನ ಅನುಗ್ರಹದಿಂದ ದಾವೀದನಿಗೆ ಎಲ್ಲಿ ಹೋದರೂ ಜಯದೊರಕಿತು.",
			"7": "<sup>7</sup> ಅವನು ಹದದೆಜೆರನ ಸೇವಕರಿಗಿದ್ದ ಬಂಗಾರದ ಗುರಾಣಿಗಳನ್ನು ಕಿತ್ತುಕೊಂಡು ಯೆರೂಸಲೇಮಿಗೆ ಒಯ್ದನು.",
			"8": "<sup>8</sup> ಇದಲ್ಲದೆ ಹದದೆಜೆರನ ಪಟ್ಟಣಗಳಾದ ಬೆಟಹದಿಂದಲೂ ಬೇರೋತೈಯಿಂದಲೂ ಬಹಳ ತಾಮ್ರವನ್ನು ತೆಗೆದುಕೊಂಡುಹೋದನು.",
			"9": "<sup>9</sup> ದಾವೀದನು ಹದದೆಜೆರನ ಸೈನ್ಯವನ್ನು ಸೋಲಿಸಿದನೆಂಬ ವರ್ತಮಾನವು ಹಮಾತಿನ ಅರಸನಾದ ತೋವಿಗೆ ಮುಟ್ಟಿತು.",
			"10": "<sup>10</sup> (ತೋವಿಗೂ ಹದದೆಜೆರನಿಗೂ ವಿರೋಧವಿತ್ತು.) ದಾವೀದನು ಹದದೆಜೆರನ ಮೇಲೆ ಬಿದ್ದು ಅವನನ್ನು ಸೋಲಿಸಿದದರಿಂದ ತೋವು ದಾವೀದನನ್ನು ವಂದಿಸುವದಕ್ಕೂ ಹರಸುವದಕ್ಕೂ ತನ್ನ ಮಗನಾದ ಯೋರಾಮನನ್ನು ಕಳುಹಿಸಿದನು. ಇವನು ಬರುವಾಗ ದಾವೀದನಿಗೋಸ್ಕರ ತಾಮ್ರ ಬೆಳ್ಳಿ ಬಂಗಾರದ ಪಾತ್ರೆಗಳನ್ನು ತಂದನು.",
			"11": "<sup>11</sup> ಅರಸನಾದ ದಾವೀದನು ಇವುಗಳನ್ನೂ ತನ್ನಿಂದ ಅಪಜಯಹೊಂದಿದ ಅರಾಮ್ಯರು, ಮೋವಾಬ್ಯರು, ಅಮ್ಮೋನಿಯರು, ಫಿಲಿಷ್ಟಿಯರು, ಅಮಾಲೇಕ್ಯರು ಎಂಬೀ ಸುತ್ತಣ ಜನಾಂಗಗಳಿಂದಲೂ ಚೋಬದ ಅರಸನಾದ ರೆಹೋಬನ ಮಗ ಹದದೆಜೆರನಿಂದಲೂ",
			"12": "<sup>12</sup> ಪಡಕೊಂಡ ಬೆಳ್ಳಿಬಂಗಾರವನ್ನೂ ಯೆಹೋವನಿಗೋಸ್ಕರ ಪ್ರತಿಷ್ಠಿಸಿದನು.",
			"13": "<sup>13</sup> ಇದಲ್ಲದೆ ದಾವೀದನು ಹೋಗಿ ಉಪ್ಪಿನ ತಗ್ಗಿನಲ್ಲಿ ಎದೋಮ್ಯರ ಹದಿನೆಂಟು ಸಾವಿರ ಮಂದಿ ಸೈನಿಕರನ್ನು ಹೊಡೆದು ಬಂದು ಅಂದಿನಿಂದ ಬಹುಖ್ಯಾತಿವಂತನಾದನು.",
			"14": "<sup>14</sup> ಅವನು ಎದೋಮಿನಲ್ಲೆಲ್ಲಾ ಕಾವಲುದಂಡುಗಳನ್ನಿರಿಸಿದನು; ಎದೋಮ್ಯರೆಲ್ಲರೂ ದಾವೀದನ ದಾಸರಾದರು. ಯೆಹೋವನ ಅನುಗ್ರಹದಿಂದ ದಾವೀದನಿಗೆ ಎಲ್ಲಿ ಹೋದರೂ ಜಯವುಂಟಾಯಿತು.",
			"15": "<sup>15</sup> ದಾವೀದನು ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರ ಅರಸನಾಗಿ ಎಲ್ಲಾ ಪ್ರಜೆಗಳಿಗೂ ಧರ್ಮತಪ್ಪದೆ ನ್ಯಾಯತೀರಿಸುತ್ತಿದ್ದನು.",
			"16": "<sup>16</sup> ಚೆರೂಯಳ ಮಗನಾದ ಯೋವಾಬನು ಅವನ ಸೇನಾಪತಿಯೂ ಅಹೀಲೂದನ ಮಗನಾದ ಯೆಹೋಷಾಫಾಟನು ಮಂತ್ರಿಯೂ ಆಗಿದ್ದರು.",
			"17": "<sup>17</sup> ಅಹೀಟೂಬನ ಮಗನಾದ ಚಾದೋಕನೂ ಅಹೀಮೆಲೆಕನ ಮಗನಾದ ಎಬ್ಯಾತಾರನೂ ಅವನ ಯಾಜಕರು;",
			"18": "<sup>18</sup> ಸೆರಾಯನು ಲೇಖಕನು. ಯೆಹೋಯಾದಾವನ ಮಗನಾದ ಬೆನಾಯನು ಕೆರೇತ್ಯ, ಪೆಲೇತ್ಯ ಎಂಬ ಕಾವಲು ದಂಡುಗಳ ಮುಖ್ಯಸ್ಥನು. ದಾವೀದನ ಮಕ್ಕಳೂ ಯಾಜಕರಾಗಿದ್ದರು."
		},
		"9": {
			"1": "<sup>1</sup> [ಒಂದು ದಿವಸ] ದಾವೀದನು - ಸೌಲನ ಮನೆಯವರಲ್ಲಿ ಯಾರಾದರೂ ಉಳಿದಿರುತ್ತಾರೋ? ನಾನು ಅವರಿಗೆ ಯೋನಾತಾನನ ಸಲುವಾಗಿ ದಯೆತೋರಿಸಬೇಕೆಂದಿರುತ್ತೇನೆ ಅನ್ನಲು",
			"2": "<sup>2</sup> ಸೌಲನ ಮನೆಯಲ್ಲಿ ದಾಸನಾಗಿದ್ದ ಚೀಬನೆಂಬ ಒಬ್ಬ ಮನುಷ್ಯನನ್ನು ಕರಕೊಂಡುಬಂದರು. ಅರಸನು ಅವನನ್ನು - ನೀನು ಚೀಬನೋ ಎಂದು ಕೇಳಲು ಅವನು - ನಿನ್ನ ಸೇವಕನಾದ ನಾನು ಚೀಬನೇ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"3": "<sup>3</sup> ಅರಸನು ಅವನಿಗೆ - ಸೌಲನ ಕುಟುಂಬದವರಲ್ಲಿ ಯಾರಾದರೂ ಉಳಿದಿದ್ದರೆ ಹೇಳು; ನಾನು ದೇವರನ್ನು ನೆನಸಿ ಅವರಿಗೆ ದಯೆತೋರಿಸುತ್ತೇನೆ ಅಂದನು. ಆಗ ಚೀಬನು ಅರಸನಿಗೆ - ಯೋನಾತಾನನಿಗೆ ಎರಡು ಕಾಲೂ ಕುಂಟಾದ ಒಬ್ಬ ಮಗನಿರುತ್ತಾನೆ ಎಂದು ಹೇಳಿದನು.",
			"4": "<sup>4</sup> ಅರಸನು - ಎಲ್ಲಿರುತ್ತಾನೆ ಎಂದು ಅವನನ್ನು ಕೇಳಲು ಅವನು - ಲೋದೆಬಾರಿನಲ್ಲಿರುವ ಅಮ್ಮೀಯೇಲನ ಮಗನಾದ ಮಾಕೀರನ ಮನೆಯಲ್ಲಿರುತ್ತಾನೆ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"5": "<sup>5</sup> ಕೂಡಲೆ ಅರಸನು ದೂತರನ್ನು ಕಳುಹಿಸಿ ಅವನನ್ನು ಲೋದೆಬಾರಿನಲ್ಲಿದ್ದ ಅಮ್ಮೀಯೇಲನ ಮಗನಾದ ಮಾಕೀರನ ಮನೆಯಿಂದ ಕರಿಸಿದನು.",
			"6": "<sup>6</sup> ಸೌಲನ ಮೊಮ್ಮಗನೂ ಯೋನಾತಾನನ ಮಗನೂ ಆದ ಮೆಫೀಬೋಶೆತನು ಬಂದಾಗ ದಾವೀದನಿಗೆ ಸಾಷ್ಟಾಂಗನಮಸ್ಕಾರ ಮಾಡಿದನು. ದಾವೀದನು - ಮೆಫೀಬೋಶೆತನೇ ಅನ್ನಲು ಅವನು - ನಿನ್ನ ಸೇವಕನಾದ ನಾನು ಇಲ್ಲಿದ್ದೇನೆ ಎಂದನು.",
			"7": "<sup>7</sup> ಆಗ ದಾವೀದನು ಅವನಿಗೆ - ಹೆದರಬೇಡ; ನಿನ್ನ ತಂದೆಯಾದ ಯೋನಾತಾನನನ್ನು ನೆನಸಿ ನಿನಗೆ ದಯೆತೋರಿಸುತ್ತೇನೆ. ನಿನ್ನ ಅಜ್ಜನಾದ ಸೌಲನ ಭೂಮಿಯನ್ನೆಲ್ಲಾ ನಿನಗೆ ಹಿಂದಕ್ಕೆ ಕೊಡುತ್ತೇನೆ; ಮತ್ತು ನೀನು ಪ್ರತಿದಿನ ನನ್ನ ಪಂಕ್ತಿಯಲ್ಲಿ ಊಟಮಾಡಬೇಕು ಎಂದು ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ಮೆಫೀಬೋಶೆತನು ಇದನ್ನು ಕೇಳಿ ಅರಸನಿಗೆ ಸಾಷ್ಟಾಂಗನಮಸ್ಕಾರ ಮಾಡಿ - ನೀನು ಸತ್ತ ನಾಯಿಯಂತಿರುವ ನನ್ನನ್ನು ಲಕ್ಷಿಸುವ ಹಾಗೆ ನಿನ್ನ ದಾಸನಾದ ನಾನು ಎಷ್ಟರವನು ಅಂದನು.",
			"9": "<sup>9</sup> ಅನಂತರ ದಾವೀದನು ಸೌಲನ ಸೇವಕನಾದ ಚೀಬನನ್ನು ಕರೆದು ಅವನಿಗೆ - ನಿನ್ನ ಯಜಮಾನನಾದ ಸೌಲನಿಗೂ ಅವನ ಕುಟುಂಬದವರಿಗೂ ಇದ್ದದ್ದನ್ನೆಲ್ಲಾ ಅವನ ಮೊಮ್ಮಗನಿಗೆ ಕೊಟ್ಟಿರುತ್ತೇನೆ.",
			"10": "<sup>10</sup> ನೀನೂ ನಿನ್ನ ಮಕ್ಕಳೂ ದಾಸರೂ ನಿನ್ನ ಯಜಮಾನನ ಮೊಮ್ಮಗನಿಗೋಸ್ಕರ ಆ ಭೂಮಿಯನ್ನು ವ್ಯವಸಾಯ ಮಾಡಿ ಅದರ ಬೆಳೆಯನ್ನು ಇವನ ಮನೆಯವರ ಅಶನಾರ್ಥವಾಗಿ ತಂದುಕೊಡಬೇಕು. ಇವನಿಗಾದರೋ ನಿತ್ಯವೂ ನನ್ನ ಪಂಕ್ತಿಯಲ್ಲಿ ಭೋಜನವಾಗುವದು ಎಂದು ಹೇಳಿದನು. ಚೀಬನಿಗೆ ಹದಿನೈದು ಮಂದಿ ಗಂಡುಮಕ್ಕಳೂ ಇಪ್ಪತ್ತು ಮಂದಿ ಸೇವಕರೂ ಇದ್ದರು.",
			"11": "<sup>11</sup> ಚೀಬನು ಅರಸನಿಗೆ - ಒಡೆಯನಾದ ಅರಸನ ಅಪ್ಪಣೆಯಂತೆ ಸೇವಕನು ನಡೆಯುವನು ಎಂದು ಉತ್ತರಕೊಟ್ಟನು. ಈ ಪ್ರಕಾರ ಮೆಫೀಬೋಶೆತನು ರಾಜಪುತ್ರರಂತೆ ಅರಸನ ಪಂಕ್ತಿಯಲ್ಲಿ ಊಟಮಾಡುವವನಾದನು.",
			"12": "<sup>12</sup> ಅವನಿಗೆ ಮೀಕನೆಂಬೊಬ್ಬ ಚಿಕ್ಕ ಮಗನಿದ್ದನು; ಚೀಬನ ಮನೆಯವರೆಲ್ಲರೂ ಅವನ ಸೇವಕರು.",
			"13": "<sup>13</sup> ಮೆಫೀಬೋಶೆತನಿಗೆ ಪ್ರತಿದಿನವೂ ರಾಜಪಂಕ್ತಿಯಲ್ಲಿ ಊಟವಾಗುತ್ತಿದ್ದದರಿಂದ ಅವನು ಯೆರೂಸಲೇಮಿನಲ್ಲೇ ವಾಸಿಸಿದನು. ಅವನ ಎರಡು ಕಾಲುಗಳೂ ಕುಂಟಾಗಿದ್ದವು."
		},
		"10": {
			"1": "<sup>1</sup> ಇದಾದನಂತರ ಅಮ್ಮೋನಿಯರ ಅರಸನು ಸತ್ತನು. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ಹಾನೂನನು ಅರಸನಾದನು.",
			"2": "<sup>2</sup> ದಾವೀದನು - ನಾಹಾಷನು ನನಗೆ ದಯೆತೋರಿಸಿದ್ದರಿಂದ ನಾನೂ ಅವನ ಮಗನಾದ ಹಾನೂನನಿಗೆ ದಯೆತೋರಿಸುವೆನು ಅಂದುಕೊಂಡು ಪಿತೃಶೋಕದಲ್ಲಿದ್ದ ಹಾನೂನನನ್ನು ಸಂತೈಸುವದಕ್ಕೋಸ್ಕರ ತನ್ನ ಸೇವಕರನ್ನು ಕಳುಹಿಸಿದನು. ದಾವೀದನ ಸೇವಕರು ಅಮ್ಮೋನಿಯರ ದೇಶಕ್ಕೆ ಬಂದಾಗ",
			"3": "<sup>3</sup> ಅಲ್ಲಿನ ಪ್ರಭುಗಳು ತಮ್ಮ ಒಡೆಯನಾದ ಹಾನೂನನಿಗೆ - ದಾವೀದನು ನಿನ್ನ ಬಳಿಗೆ ಸಂತೈಸುವವರನ್ನು ಕಳುಹಿಸಿದ್ದರಿಂದ ಅವನು ನಿನ್ನ ತಂದೆಯನ್ನು ಸನ್ಮಾನಿಸುವವನಾಗಿದ್ದಾನೆಂದು ತಿಳಿಯುತ್ತೀಯೋ? ಪಟ್ಟಣವನ್ನು ಸಂಚರಿಸಿ ನೋಡಿ ಅದನ್ನು ಸ್ವಾಧೀನ ಮಾಡಿಕೊಳ್ಳಬೇಕೆಂದು ತನ್ನ ಆಳುಗಳನ್ನು ಕಳುಹಿಸಿದ್ದಾನೆ ಎಂದು ಹೇಳಿದರು.",
			"4": "<sup>4</sup> ಆದದರಿಂದ ಹಾನೂನನು ದಾವೀದನ ಸೇವಕರನ್ನು ಹಿಡಿಸಿ ಗಡ್ಡದ ಅರ್ಧಭಾಗವನ್ನು ಬೋಳಿಸಿ ಅವರ ನಿಲುವಂಗಿಗಳನ್ನು ಕುಂಡಿಯಿಂದ ಕೆಳಗಿನ ಭಾಗವನ್ನೆಲ್ಲಾ ಕತ್ತರಿಸಿ ಕಳುಹಿಸಿಬಿಟ್ಟನು.",
			"5": "<sup>5</sup> ಅವರು ಈ ವರ್ತಮಾನವನ್ನು ದಾವೀದನಿಗೆ ಹೇಳಿ ಕಳುಹಿಸಿದಾಗ ಅವನು ಬಹಳವಾಗಿ ಅಪಮಾನಹೊಂದಿದ ಅವರಿಗೆ ಆಳುಗಳ ಮುಖಾಂತರವಾಗಿ - ನಿಮ್ಮ ಗಡ್ಡ ಬೆಳೆಯುವವರೆಗೆ ನೀವು ಯೆರಿಕೋವಿನಲ್ಲಿದ್ದು ಅನಂತರ ಬನ್ನಿರಿ ಎಂದು ಹೇಳಿಸಿದನು.",
			"6": "<sup>6</sup> ತಾವು ದಾವೀದನಿಗೆ ಅಸಹ್ಯರಾದೆವೆಂದು ಅಮ್ಮೋನಿಯರು ತಿಳಿದು ಹಣಕೊಟ್ಟು ಬೇತ್‍ರೆಹೋಬ್, ಚೋಬಾ ಎಂಬ ಪಟ್ಟಣಗಳಿಂದ ಅರಾಮ್ಯರ ಇಪ್ಪತ್ತು ಸಾವಿರ ಮಂದಿ ಕಾಲಾಳುಗಳನ್ನೂ ಮಾಕದ ರಾಜನಿಂದ ಸಾವಿರ ಮಂದಿ ಸೈನಿಕರನ್ನೂ ಟೋಬ್ ದೇಶದಿಂದ ಹನ್ನೆರಡು ಸಾವಿರ ಮಂದಿ ದಂಡಾಳುಗಳನ್ನೂ ತರಿಸಿದರು.",
			"7": "<sup>7</sup> ಈ ಸುದ್ದಿಯು ದಾವೀದನಿಗೆ ಮುಟ್ಟಿದಾಗ ಅವನು ಯೋವಾಬನನ್ನೂ ಎಲ್ಲಾ ಶೂರಸೈನಿಕರನ್ನೂ ಕಳುಹಿಸಿದನು.",
			"8": "<sup>8</sup> ಕೂಡಲೆ ಅಮ್ಮೋನಿಯರು ಹೊರಗೆ ಬಂದು ಊರುಬಾಗಲಿನ ಬಳಿಯಲ್ಲಿ ವ್ಯೂಹಕಟ್ಟಿದರು. ಚೋಬಾ, ರೆಹೋಬ್ ಎಂಬ ಸ್ಥಳಗಳ ಅರಾಮ್ಯರೂ ಟೋಬ್ ಮಾಕಾ ದೇಶಗಳವರೂ ಪ್ರತ್ಯೇಕವಾಗಿ ಮೈದಾನದಲ್ಲಿ ಇಳುಕೊಂಡಿದ್ದರು.",
			"9": "<sup>9</sup> ಯೋವಾಬನು ತನ್ನ ಮುಂದೆಯೂ ಹಿಂದೆಯೂ ಯುದ್ಧ ಪ್ರಾರಂಭವಾದದ್ದನ್ನು ಕಂಡು ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಶ್ರೇಷ್ಠರಾದ ಸೈನಿಕರನ್ನು ಆರಿಸಿಕೊಂಡು ಅವರನ್ನು ಅರಾಮ್ಯರಿಗೆ ವಿರೋಧವಾಗಿ ನಿಲ್ಲಿಸಿದನು.",
			"10": "<sup>10</sup> ಉಳಿದ ಜನರನ್ನು ತನ್ನ ತಮ್ಮನಾದ ಅಬೀಷೈಯ ವಶಕ್ಕೆ ಕೊಟ್ಟು ಅವನಿಗೆ -",
			"11": "<sup>11</sup> ಅರಾಮ್ಯರು ನನ್ನನ್ನು ಸೋಲಿಸುವಂತೆ ಕಂಡರೆ ನೀನು ನನ್ನ ಸಹಾಯಕ್ಕೆ ಬಾ; ಅಮ್ಮೋನಿಯರು ನಿನ್ನನ್ನು ಸೋಲಿಸುವಂತೆ ಕಂಡರೆ ನಾನು ನಿನ್ನ ಸಹಾಯಕ್ಕೆ ಬರುವೆನು;",
			"12": "<sup>12</sup> ಧೈರ್ಯದಿಂದಿರು, ನಮ್ಮ ಜನರಿಗೋಸ್ಕರವೂ ನಮ್ಮ ದೇವರ ಪಟ್ಟಣಗಳಿಗೋಸ್ಕರವೂ ನಮ್ಮ ಪೌರುಷವನ್ನು ತೋರಿಸೋಣ; ಯೆಹೋವನು ತನಗೆ ಸರಿಕಾಣುವಂಥದನ್ನು ಮಾಡಲಿ ಎಂದು ಹೇಳಿ ಅವನನ್ನು ಅಮ್ಮೋನಿಯರಿಗೆ ವಿರೋಧವಾಗಿ ಕಳುಹಿಸಿದನು.",
			"13": "<sup>13</sup> ಯೋವಾಬನೂ ಅವನ ಜನರೂ ಅರಾಮ್ಯರಿಗೆ ವಿರೋಧವಾಗಿ ಯುದ್ಧ ಪ್ರಾರಂಭಿಸಿದಾಗ ಅರಾಮ್ಯರು ಓಡಿಹೋದರು.",
			"14": "<sup>14</sup> ಇವರು ಓಡಿಹೋಗುವದನ್ನು ಅಮ್ಮೋನಿಯರು ಕಂಡು ಅವರೂ ಅಬೀಷೈಯ ಎದುರಿನಿಂದ ಓಡಿಹೋಗಿ ಪಟ್ಟಣವನ್ನು ಹೊಕ್ಕರು. ಯೋವಾಬನು ಅಮ್ಮೋನಿಯರೊಡನೆ ಯುದ್ಧಮಾಡುವದನ್ನು ಬಿಟ್ಟು ಯೆರೂಸಲೇಮಿಗೆ ಹೋದನು.",
			"15": "<sup>15</sup> ಅರಾಮ್ಯರಿಗೆ ತಮ್ಮ ಸೈನ್ಯವು ಇಸ್ರಾಯೇಲ್ಯರಿಂದ ಅಪಜಯಹೊಂದಿತೆಂದು ಗೊತ್ತಾದಾಗ ಅವರೆಲ್ಲರೂ ಕೂಡಿಬಂದರು.",
			"16": "<sup>16</sup> ಹದದೆಜೆರನು ಯೂಫ್ರೇಟೀಸ್ ನದಿಯ ಆಚೆಯಲ್ಲಿದ್ದ ಅರಾಮ್ಯರನ್ನು ಕರೇಕಳುಹಿಸಿದನು; ಅವರು ಹೇಲಾಮಿಗೆ ಬಂದರು. ಹದದೆಜೆರನ ಸೇನಾಪತಿಯಾದ ಶೋಬಕನು ಅವರ ನಾಯಕನಾದನು.",
			"17": "<sup>17</sup> ಈ ಸುದ್ದಿಯು ದಾವೀದನಿಗೆ ಮುಟ್ಟಿದಾಗ ಅವನು ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರನ್ನೂ ಕೂಡಿಸಿಕೊಂಡು ಯೊರ್ದನ್‍ಹೊಳೆದಾಟಿ ಹೇಲಾಮಿಗೆ ಬಂದನು. ಅರಾಮ್ಯರು ವ್ಯೂಹಕಟ್ಟಿ ದಾವೀದನೊಡನೆ ಯುದ್ಧಕ್ಕೆ ನಿಂತಾಗ",
			"18": "<sup>18</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಮುಂದೆ ಸೋತು ಓಡಿಹೋದರು; ದಾವೀದನು ಅರಾಮ್ಯರ ಏಳುನೂರು ರಥಗಳನ್ನು ಹಾಳುಮಾಡಿ ನಾಲ್ವತ್ತು ಸಾವಿರ ಮಂದಿ ರಾಹುತರನ್ನು ಹತಿಸಿಬಿಟ್ಟನು; ಸೇನಾಪತಿಯಾದ ಶೋಬಕನು ಗಾಯಹೊಂದಿ ಅಲ್ಲೇ ಸತ್ತನು.",
			"19": "<sup>19</sup> ಹದದೆಜೆರನಿಗೆ ದಾಸರಾಗಿದ್ದ ಅರಸರೆಲ್ಲರೂ ಇಸ್ರಾಯೇಲ್ಯರ ಮುಂದೆ ತಮ್ಮ ಕೈಸಾಗದೆಂದು ತಿಳಿದು ಅವರೊಡನೆ ಒಪ್ಪಂದಮಾಡಿಕೊಂಡು ಅವರ ದಾಸರಾದರು. ಅಂದಿನಿಂದ ಅರಾಮ್ಯರು ಅಮ್ಮೋನಿಯರಿಗೆ ಸಹಾಯ ಮಾಡುವದಕ್ಕೆ ಧೈರ್ಯಗೊಳ್ಳಲಿಲ್ಲ."
		},
		"11": {
			"1": "<sup>1</sup> ಮರುವರುಷ ಅರಸರು ಯುದ್ಧಕ್ಕೆ ಹೊರಡುವ ಸಮಯದಲ್ಲಿ ದಾವೀದನು ಯೋವಾಬನನ್ನೂ ತನ್ನ ಸೇವಕರನ್ನೂ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರನ್ನೂ ಯುದ್ಧಕ್ಕೆ ಕಳುಹಿಸಿದನು. ಇವರು ಹೋಗಿ ಅಮ್ಮೋನಿಯರ ಪ್ರಾಂತಗಳನ್ನು ಹಾಳುಮಾಡಿ ರಬ್ಬಕ್ಕೆ ಮುತ್ತಿಗೆಹಾಕಿದರು. ದಾವೀದನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿಯೇ ಇದ್ದನು.",
			"2": "<sup>2</sup> ಅವನು ಒಂದು ದಿವಸ ಸಂಜೇ ಹೊತ್ತಿನಲ್ಲಿ ಮಂಚದಿಂದೆದ್ದು ಅರಮನೆಯ ಮಾಳಿಗೆಯ ಮೇಲೆ ತಿರುಗಾಡುತ್ತಿರುವಾಗ ಅಲ್ಲಿಂದ ಬಹುಸುಂದರಿಯಾದ ಒಬ್ಬ ಸ್ತ್ರೀ ಸ್ನಾನಮಾಡುವದನ್ನು ಕಂಡನು.",
			"3": "<sup>3</sup> ಕೂಡಲೆ ದಾವೀದನು ಒಬ್ಬನನ್ನು ಕರೆದು ಆ ಸ್ತ್ರೀ ಯಾರೆಂದು ಕೇಳಲು ಅವನು - ಆಕೆಯು ಎಲೀಯಾಮನ ಮಗಳೂ ಹಿತ್ತಿಯನಾದ ಊರೀಯನ ಹೆಂಡತಿಯೂ ಆಗಿರುವ ಬತ್ಷೆಬೆಯಲ್ಲವೋ ಎಂದು ಉತ್ತರ ಕೊಟ್ಟನು.",
			"4": "<sup>4</sup> ಆಗ ದಾವೀದನು ಆಗಲೇ ಋತುಸ್ನಾನಮಾಡಿಕೊಂಡಿದ್ದ ಆಕೆಯನ್ನು ದೂತರ ಮುಖಾಂತರವಾಗಿ ಕರೇಕಳುಹಿಸಿದನು; ಆಕೆಯು ಬರಲು ಆಕೆಯನ್ನು ಕೂಡಿದನು.",
			"5": "<sup>5</sup> ಅನಂತರ ಆಕೆಯು ತನ್ನ ಮನೆಗೆ ಹೋದಳು. ಆಕೆಯು ತಾನು ಗರ್ಭಧರಿಸಿದ್ದು ಗೊತ್ತಾದಾಗ ದಾವೀದನಿಗೆ ವರ್ತಮಾನ ಕಳುಹಿಸಿದಳು.",
			"6": "<sup>6</sup> ಒಡನೆ ದಾವೀದನು ದೂತರ ಮುಖಾಂತರವಾಗಿ ಯೋವಾಬನಿಗೆ - ಹಿತ್ತಿಯನಾದ ಊರೀಯನನ್ನು ನನ್ನ ಬಳಿಗೆ ಕಳುಹಿಸು ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು. ಅವನು ಊರೀಯನನ್ನು ಕಳುಹಿಸಿದನು.",
			"7": "<sup>7</sup> ಊರೀಯನು ಬಂದಾಗ ದಾವೀದನು - ಯೋವಾಬನೂ ಸೈನ್ಯದವರೂ ಹೇಗಿರುತ್ತಾರೆ? ಯುದ್ಧವು ಹೇಗೆ ನಡೆದದೆ ಎಂದು ವಿಚಾರಿಸಿದನಂತರ ಅವನಿಗೆ -",
			"8": "<sup>8</sup> ಮನೆಗೆ ಹೋಗಿ ಕೈಕಾಲು ತೊಳೆದುಕೋ ಎಂದು ಹೇಳಿದನು. ಊರೀಯನು ಅರಮನೆಯಿಂದ ಹೊರಟ ಕೂಡಲೆ ಅವನ ಹಿಂದೆಯೇ ಅರಸನ ಭೋಜನ ಪದಾರ್ಥಗಳೂ ಹೋದವು.",
			"9": "<sup>9</sup> ಆದರೆ ಊರೀಯನು ತನ್ನ ಮನೆಗೆ ಹೋಗದೆ ಅರಸನ ಸೇವಕರೊಡನೆ ಅರಮನೆಯ ಬಾಗಲಲ್ಲೇ ಮಲಗಿಕೊಂಡನು.",
			"10": "<sup>10</sup> ಊರೀಯನು ತನ್ನ ಮನೆಗೆ ಹೋಗಲಿಲ್ಲ ಎಂಬ ವರ್ತಮಾನವು ದಾವೀದನಿಗೆ ಮುಟ್ಟಿದಾಗ ಇವನು ಅವನಿಗೆ - ನೀನು ಪ್ರಯಾಣಮಾಡಿಕೊಂಡು ಬಂದಿಯಲ್ಲಾ; ಯಾಕೆ ಮನೆಗೆ ಹೋಗಲಿಲ್ಲ ಅಂದನು.",
			"11": "<sup>11</sup> ಅದಕ್ಕೆ ಊರೀಯನು - ಒಡಂಬಡಿಕೆಯ ಮಂಜೂಷವೂ ಇಸ್ರಾಯೇಲ್ಯರೂ ಯೆಹೂದ್ಯರೂ ಗುಡಾರಗಳಲ್ಲಿ ವಾಸವಾಗಿದ್ದಾರೆ. ನನ್ನ ಒಡೆಯನಾದ ಯೋವಾಬನೂ ಅವನ ಸೇವಕರೂ ಬೈಲಿನಲ್ಲಿ ಪಾಳೆಯ ಮಾಡಿಕೊಂಡಿದ್ದಾರೆ. ಹೀಗಿರುವಲ್ಲಿ ನಾನು ನನ್ನ ಮನೆಗೆ ಹೋಗಿ ಉಂಡು ಕುಡಿದು ಹೆಂಡತಿಯೊಡನೆ ಮಲಗಿಕೊಳ್ಳುವದು ಹೇಗೆ? ನಿನ್ನಾಣೆ, ನಿನ್ನ ಜೀವದ ಆಣೆ, ನಾನು ಇಂಥದನ್ನು ಮಾಡುವದೇ ಇಲ್ಲ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"12": "<sup>12</sup> ಆಗ ದಾವೀದನು ಅವನಿಗೆ - ನೀನು ಈ ಹೊತ್ತು ಇಲ್ಲೇ ಇರು, ನಾಳೆ ನಿನ್ನನ್ನು ಕಳುಹಿಸುತ್ತೇನೆ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು. ಆದದರಿಂದ ಊರೀಯನು ಆ ದಿನ ಯೆರೂಸಲೇಮಿನಲ್ಲೇ ಇದ್ದನು.",
			"13": "<sup>13</sup> ಮರುದಿನ ದಾವೀದನು ಅವನನ್ನು ಅನ್ನಪಾನತೆಗೆದುಕೊಳ್ಳಬೇಕೆಂದು ತನ್ನ ಮನೆಗೆ ಕರಕೊಂಡುಹೋಗಿ ಮತ್ತನನ್ನಾಗಿ ಮಾಡಿದನು. ಆದರೆ ಆ ರಾತ್ರಿಯಲ್ಲಿಯೂ ಅವನು ಹೋಗಿ ತನ್ನ ಯಜಮಾನನ ಸೇವಕರ ಹತ್ತಿರ ಹಾಸಿಗೆ ಹಾಸಿ ಮಲಗಿಕೊಂಡನೇ ಹೊರತು ತನ್ನ ಮನೆಗೆ ಹೋಗಲಿಲ್ಲ.",
			"14": "<sup>14</sup> ಮರುದಿನ ಬೆಳಿಗ್ಗೆ ದಾವೀದನು ಯೋವಾಬನಿಗೆ ಒಂದು ಪತ್ರವನ್ನು ಬರೆದು ಅದನ್ನು ಊರೀಯನ ಕೈಯಲ್ಲಿ ಕೊಟ್ಟು ಕಳುಹಿಸಿದನು.",
			"15": "<sup>15</sup> ಅದರಲ್ಲಿದ್ದದ್ದೇನಂದರೆ - ಊರೀಯನು ಗಾಯಹೊಂದಿ ಸಾಯುವಂತೆ ಅವನನ್ನು ಘೋರಯುದ್ಧ ನಡೆಯುತ್ತಿರುವ ಕಡೆಗೆ ಮುಂಭಾಗದಲ್ಲಿ ನಿಲ್ಲಿಸಿ ನೀವು ಹಿಂದಕ್ಕೆ ಸರಿದುಕೊಳ್ಳಿರಿ ಎಂಬದೇ.",
			"16": "<sup>16</sup> ಯೋವಾಬನು ಪಟ್ಟಣಕ್ಕೆ ಮುತ್ತಿಗೆಹಾಕುವಾಗ ಶೂರರಾದ ಶತ್ರುಸೈನಿಕರೆಲ್ಲಿರುತ್ತಾರೆಂದು ಗೊತ್ತುಮಾಡಿಕೊಂಡು ಊರೀಯನನ್ನು ಅಲ್ಲಿ ನಿಲ್ಲಿಸಿದನು.",
			"17": "<sup>17</sup> ಆ ಊರಿನ ಜನರು ಹೊರಗೆ ಬಂದು ಯೋವಾಬನೊಡನೆ ಕಾದಿದಾಗ ದಾವೀದನ ಸೇವಕರಲ್ಲಿ ಕೆಲವರು ಸತ್ತರು. ಹಿತ್ತಿಯನಾದ ಊರೀಯನೂ ಮಡಿದನು.",
			"18": "<sup>18</sup> ಅನಂತರ ಯೋವಾಬನು ಒಬ್ಬ ದೂತನನ್ನು ಕರೆದು ಅವನಿಗೆ - ನೀನು ಹೋಗಿ ದಾವೀದನಿಗೆ ಯುದ್ಧ ವರ್ತಮಾನವನ್ನೆಲ್ಲಾ ತಿಳಿಸು.",
			"19": "<sup>19</sup> ನೀನು ಯುದ್ಧ ಸಮಾಚಾರವನ್ನು ಹೇಳಿ ಮುಗಿಸಿದನಂತರ ಅರಸನು ಮುನಿದು ನಿನ್ನನ್ನು -",
			"20": "<sup>20</sup> ನೀವು ಯುದ್ಧಮಾಡುವದಕ್ಕಾಗಿ ಪಟ್ಟಣದ ಸಮೀಪಕ್ಕೆ ಹೋದದ್ದೇಕೆ? ಶತ್ರುಗಳು ಊರುಗೋಡೆಯ ಮೇಲಣಿಂದ ಏನಾದರೂ ಬೀಸಾಡಬಹುದಲ್ಲವೋ?",
			"21": "<sup>21</sup> ಯೆರುಬ್ಬೆಷೆತನ ಮಗನಾದ ಅಬೀಮೆಲೆಕನನ್ನು ಕೊಂದವರಾರೆಂದು ನಿಮಗೆ ಗೊತ್ತಿಲ್ಲವೋ? ತೇಬೇಚಿನಲ್ಲಿ ಒಬ್ಬ ಹೆಂಗಸು ಗೋಡೆಯ ಮೇಲಣಿಂದ ಒಂದು ಬೀಸುವ ಕಲ್ಲನ್ನು ಅವನ ಮೇಲೆ ಎತ್ತಿಹಾಕಿ ಅವನನ್ನು ಕೊಂದಳಲ್ಲವೇ. ಹೀಗಿರುವದರಿಂದ ನೀವು ಗೋಡೆಯ ಸಮೀಪಕ್ಕೆ ಹೋದದ್ದೇನು ಎಂದು ಕೇಳಿದರೆ ನೀನು - ಹಿತ್ತಿಯನಾದ ನಿನ್ನ ಸೇವಕ ಊರೀಯನೂ ಮೃತಿಹೊಂದಿದನೆಂದು ಹೇಳು ಎಂಬದಾಗಿ",
			"22": "<sup>22</sup> ಆಜ್ಞಾಪಿಸಿ ಅವನನ್ನು ಕಳುಹಿಸಿದನು. ಆ ದೂತನು ಹೋಗಿ",
			"23": "<sup>23</sup> ಯೋವಾಬನ ಆಜ್ಞೆಯಂತೆ ದಾವೀದನಿಗೆ ತಿಳಿಸಿದ್ದೇನಂದರೆ - ಆ ಊರಿನವರು ಮಹಾಬಲದೊಡನೆ ಯುದ್ಧಮಾಡುವದಕ್ಕೆ ಬೈಲಿಗೆ ಬಂದರು; ನಾವು ಅವರನ್ನು ಊರುಬಾಗಲಿನವರೆಗೆ ಹಿಂದಟ್ಟಿದೆವು.",
			"24": "<sup>24</sup> ಆಗ ಬಿಲ್ಲುಗಾರರು ಗೋಡೆಯ ಮೇಲಣಿಂದ ನಿನ್ನ ಸೇವಕರ ಮೇಲೆ ಬಾಣಗಳನ್ನು ಎಸೆದದರಿಂದ ಅವರಲ್ಲಿ ಕೆಲವರು ಸತ್ತರು. ಹಿತ್ತಿಯನಾದ ನಿನ್ನ ಸೇವಕ ಊರೀಯನೂ ಮಡಿದನು ಎಂಬದೇ.",
			"25": "<sup>25</sup> ಆಗ ದಾವೀದನು ದೂತನಿಗೆ ನೀನು ಹೋಗಿ - ಇದಕ್ಕೋಸ್ಕರವಾಗಿ ವ್ಯಸನ ಪಡಬೇಡ; ಕತ್ತಿಯು ಈ ಹೊತ್ತು ಒಬ್ಬನನ್ನು ನುಂಗಿದರೆ ನಾಳೆ ಇನ್ನೊಬ್ಬನನ್ನು ನುಂಗುವದು. ಮತ್ತಷ್ಟು ಶೂರತನದಿಂದ ಯುದ್ಧಮಾಡಿ ಪಟ್ಟಣವನ್ನು ಕೆಡವಿಬಿಡಿರಿ ಎಂಬದಾಗಿ ಅರಸನು ಆಜ್ಞಾಪಿಸುತ್ತಾನೆಂದು ಯೋವಾಬನಿಗೆ ಹೇಳಿ ಅವನನ್ನು ಧೈರ್ಯಪಡಿಸು ಅಂದನು.",
			"26": "<sup>26</sup> ಊರೀಯನ ಮರಣವಾರ್ತೆಯನ್ನು ಅವನ ಹೆಂಡತಿಯು ಕೇಳಿ ಗಂಡನಿಗೋಸ್ಕರ ಗೋಳಾಡಿದಳು.",
			"27": "<sup>27</sup> ದುಃಖಕಾಲ ತೀರಿದನಂತರ ದಾವೀದನು ಆಕೆಯನ್ನು ತನ್ನ ಮನೆಗೆ ಕರತರಿಸಿ ತನ್ನ ಹೆಂಡತಿಯನ್ನಾಗಿ ಮಾಡಿಕೊಂಡನು; ಆಕೆಯು ಅವನಿಂದ ಒಬ್ಬ ಮಗನನ್ನು ಹೆತ್ತಳು. ದಾವೀದನ ಈ ಕೃತ್ಯವು ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ಕೆಟ್ಟದ್ದಾಗಿತ್ತು."
		},
		"12": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ನಾತಾನನನ್ನು ದಾವೀದನ ಬಳಿಗೆ ಕಳುಹಿಸಿದನು. ಅವನು ಇವನ ಹತ್ತಿರ ಬಂದು ಹೇಳಿದ್ದೇನಂದರೆ - ಒಂದು ಊರಲ್ಲಿ ಇಬ್ಬರು ಮನುಷ್ಯರಿದ್ದರು; ಒಬ್ಬನು ಐಶ್ವರ್ಯವಂತನು, ಇನ್ನೊಬ್ಬನು ಬಡವನು.",
			"2": "<sup>2</sup> ಐಶ್ವರ್ಯವಂತನಿಗೆ ಬಹಳ ಕುರಿದನಗಳಿದ್ದವು.",
			"3": "<sup>3</sup> ಬಡವನಿಗೆ ಒಂದು ಕುರಿಮರಿಯ ಹೊರತು ಬೇರೇನೂ ಇರಲಿಲ್ಲ. ಅವನು ಅದನ್ನು ಕೊಂಡುಕೊಂಡು ಸಾಕುತ್ತಿದ್ದನು. ಅದು ದೊಡ್ಡದಾಗುವವರೆಗೆ ಮಗಳಂತೆ ಅವನ ಸಂಗಡಲೂ ಅವನ ಮಕ್ಕಳ ಸಂಗಡಲೂ ಇದ್ದುಕೊಂಡು ಅವನೊಡನೆ ರೊಟ್ಟಿ ತಿನ್ನುತ್ತಾ ಅವನ ಪಾತ್ರೆಯಲ್ಲೇ ನೀರು ಕುಡಿಯುತ್ತಾ ಇತ್ತು; ಅವನ ಎದೆಯ ಮೇಲೆಯೇ ಒರಗಿಕೊಂಡು ನಿದ್ರೆ ಮಾಡುತ್ತಿತ್ತು.",
			"4": "<sup>4</sup> ಒಂದು ದಿವಸ ಐಶ್ವರ್ಯವಂತನ ಮನೆಗೆ ಒಬ್ಬ ಪ್ರಯಾಣಿಕನು ಬಂದನು. ಆಗ ಐಶ್ವರ್ಯವಂತನು ತನ್ನ ಕುರಿದನಗಳಿಂದ ಏನೂ ತೆಗೆದುಕೊಳ್ಳಮನಸ್ಸಿಲ್ಲದೆ ಆ ಬಡವನ ಕುರಿಮರಿಯನ್ನು ಹಿಡಿದು ಅತಿಥಿಗೋಸ್ಕರ ಅದನ್ನು ಅಡಿಗೆ ಮಾಡಿಸಿದನು ಎಂಬದೇ.",
			"5": "<sup>5</sup> ಇದನ್ನು ಕೇಳಿ ದಾವೀದನು ಆ ಮನುಷ್ಯನ ಮೇಲೆ ಬಹಳವಾಗಿ ಕೋಪಗೊಂಡು ನಾತಾನನಿಗೆ - ಯೆಹೋವನಾಣೆ, ಆ ಮನುಷ್ಯನು ಸಾಯಲೇ ಬೇಕು.",
			"6": "<sup>6</sup> ಅವನು ಕರುಣೆಯಿಲ್ಲದೆ ಹೀಗೆ ಮಾಡಿದ್ದರಿಂದ ಆ ಕುರಿಮರಿಗಾಗಿ ನಾಲ್ಕರಷ್ಟು ಹಿಂದಕ್ಕೆ ಕೊಡಬೇಕು ಅಂದನು.",
			"7": "<sup>7</sup> ಆಗ ನಾತಾನನು ದಾವೀದನನ್ನು ನೋಡಿ - ಆ ಮನುಷ್ಯನು ನೀನೇ. ಇಸ್ರಾಯೇಲ್ ದೇವರಾದ ಯೆಹೋವನು ನಿನಗೆ ಹೇಳುವದೇನಂದರೆ - ನಿನ್ನನ್ನು ಅಭಿಷೇಕಿಸಿ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನನ್ನಾಗಿ ಮಾಡಿದವನೂ ಸೌಲನ ಕೈಗೆ ಸಿಕ್ಕದಂತೆ ತಪ್ಪಿಸಿದವನೂ ನಾನೇ.",
			"8": "<sup>8</sup> ಇದಲ್ಲದೆ ನಾನು ನಿನಗೆ ನಿನ್ನ ಯಜಮಾನನ ಮನೆಯನ್ನು ಒಪ್ಪಿಸಿದೆನು; ಅವನ ಹೆಂಡತಿಯರನ್ನು ನಿನ್ನ ಮಗ್ಗಲಿಗೆ ಕೊಟ್ಟೆನು. ಇಸ್ರಾಯೇಲ್ ಮತ್ತು ಯೆಹೂದ ಕುಲಗಳನ್ನು ನಿನಗೆ ವಶಪಡಿಸಿದೆನು. ಇದು ಸಾಕಾಗಿರದಿದ್ದರೆ ನಾನು ನಿನಗೆ ಇನ್ನೂ ಬೇಕಾದದ್ದನ್ನು ಕೊಡುತ್ತಿದ್ದೆನು.",
			"9": "<sup>9</sup> ಹೀಗಿರಲು ನೀನು ಯೆಹೋವನಾದ ನನ್ನ ಆಜ್ಞೆಯನ್ನು ತಿರಸ್ಕರಿಸಿ ನನ್ನ ದೃಷ್ಟಿಯಲ್ಲಿ ಕೆಟ್ಟದ್ದಾಗಿರುವದನ್ನು ಮಾಡಿದ್ದೇನು? ನೀನು ಹಿತ್ತಿಯನಾದ ಊರೀಯನನ್ನು ಕತ್ತಿಯಿಂದ ಕೊಲ್ಲಿಸಿ ಅವನ ಹೆಂಡತಿಯನ್ನು ನಿನ್ನ ಹೆಂಡತಿಯನ್ನಾಗಿ ತೆಗೆದುಕೊಂಡಿ.",
			"10": "<sup>10</sup> ನೀನು ನನ್ನ ಆಜ್ಞೆಯನ್ನು ತಿರಸ್ಕರಿಸಿದದರಿಂದಲೂ ಹಿತ್ತಿಯನಾದ ಊರೀಯನನ್ನು ಅಮ್ಮೋನಿಯರ ಕತ್ತಿಯಿಂದ ಕೊಲ್ಲಿಸಿ ಅವನ ಹೆಂಡತಿಯನ್ನು ನಿನ್ನ ಹೆಂಡತಿಯನ್ನಾಗಿ ತೆಗೆದುಕೊಂಡದರಿಂದಲೂ ಕತ್ತಿಯು ನಿನ್ನ ಮನೆಯನ್ನು ಬಿಟ್ಟು ಹೋಗುವದಿಲ್ಲ.",
			"11": "<sup>11</sup> ಯೆಹೋವನಾದ ನನ್ನ ಮಾತನ್ನು ಕೇಳು - ನಾನು ನಿನ್ನ ಮನೆಯವರಿಂದಲೇ ನಿನಗೆ ಕೇಡು ಉಂಟಾಗುವಂತೆ ಮಾಡುವೆನು. ನಿನ್ನ ಹೆಂಡತಿಯರನ್ನು ತೆಗೆದು ನಿನ್ನೆದುರಿನಲ್ಲಿಯೇ ಇನ್ನೊಬ್ಬನಿಗೆ ಕೊಡುವೆನು. ಅವನು ಹಗಲಿನಲ್ಲಿಯೇ ಅವರ ಕೂಡ ಮಲಗುವನು. ನೀನು ಅದನ್ನು ಗುಪ್ತವಾಗಿ ಮಾಡಿದಿ;",
			"12": "<sup>12</sup> ನಾನು ಹೇಳುವ ಮಾತಾದರೋ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರ ಮುಂದೆ ಪ್ರಕಟವಾಗಿ ನೆರವೇರುವದು ಎಂಬದೇ ಅಂದನು.",
			"13": "<sup>13</sup> ಆಗ ದಾವೀದನು ನಾತಾನನಿಗೆ - ನಾನು ಯೆಹೋವನಿಗೆ ವಿರುದ್ಧವಾಗಿ ಪಾಪಮಾಡಿದ್ದೇನೆ ಎಂದು ಹೇಳಿದನು. ನಾತಾನನು ಅವನಿಗೆ - ಯೆಹೋವನು ನಿನ್ನ ಪಾಪವನ್ನು ಕ್ಷಮಿಸಿದ್ದಾನೆ, ನೀನು ಸಾಯುವದಿಲ್ಲ;",
			"14": "<sup>14</sup> ಆದರೂ ನೀನು ಈ ಕೃತ್ಯದಿಂದ ಯೆಹೋವನ ವೈರಿಗಳು ಆತನನ್ನು ಬಹಳವಾಗಿ ನಿಂದಿಸುವದಕ್ಕೆ ಆಸ್ಪದ ಕೊಟ್ಟದರಿಂದ ನಿನ್ನಿಂದ ಹುಟ್ಟಿರುವ ಮಗುವು ಸತ್ತೇ ಹೋಗುವದು",
			"15": "<sup>15</sup> ಎಂದು ಹೇಳಿ ಮನೆಗೆ ಹೊರಟು ಹೋದನು. ಊರೀಯನ ಹೆಂಡತಿಯಲ್ಲಿ ದಾವೀದನಿಗೆ ಹುಟ್ಟಿದ ಮಗುವು ಯೆಹೋವನ ಪೆಟ್ಟಿನಿಂದ ಬಹುವಾಗಿ ಅಸ್ವಸ್ಥವಾಯಿತು.",
			"16": "<sup>16</sup> ಆದದರಿಂದ ದಾವೀದನು ಮಗುವಿಗೋಸ್ಕರ ದೇವರನ್ನು ಪ್ರಾರ್ಥಿಸುತ್ತಾ ಉಪವಾಸಮಾಡುತ್ತಾ ಒಳಕೋಣೆಯಲ್ಲಿ ನೆಲದ ಮೇಲೆಯೇ ಬಿದ್ದುಕೊಂಡು ರಾತ್ರಿಕಳೆಯುತ್ತಿದ್ದನು.",
			"17": "<sup>17</sup> ಅವನ ಮನೆಯ ಹಿರೀಸೇವಕರು ಅವನನ್ನು ಎಬ್ಬಿಸುವದಕ್ಕೆ ಹೋದರು. ಆದರೆ ಅವನು ಏಳಲೂ ಇಲ್ಲ. ಅವರೊಡನೆ ಆಹಾರ ತೆಗೆದುಕೊಳ್ಳಲೂ ಇಲ್ಲ.",
			"18": "<sup>18</sup> ಏಳನೆಯ ದಿನದಲ್ಲಿ ಮಗುವು ಸತ್ತಿತು; ಅವನ ಸೇವಕರು ಭಯಪಟ್ಟು ಈ ವರ್ತಮಾನವನ್ನು ದಾವೀದನಿಗೆ ತಿಳಿಸಲಿಲ್ಲ. ಅವರು - ಹುಡುಗನು ಜೀವದಿಂದಿದ್ದಾಗಲೇ ಅವನು ನಮ್ಮ ಸಂತೈಸುವಿಕೆಯನ್ನು ಲಕ್ಷಿಸಲಿಲ್ಲ. ಹೀಗಿರುವಲ್ಲಿ ಮಗುವಿನ ಮರಣವಾರ್ತೆಯನ್ನು ಅವನಿಗೆ ತಿಳಿಸಿದರೆ ಅವನು ತನಗೆ ಏನಾದರೂ ಕೇಡುಮಾಡಿಕೊಂಡಾನಲ್ಲವೇ ಎಂದು ಮಾತಾಡಿಕೊಳ್ಳುತ್ತಿದ್ದರು.",
			"19": "<sup>19</sup> ಸೇವಕರು ಈ ಪ್ರಕಾರ ಗುಜುಗುಜು ಮಾತಾಡುವದನ್ನು ದಾವೀದನು ಕಂಡು ಮಗುವು ಮೃತಿಹೊಂದಿತೆಂದು ತಿಳಿದು ಸೇವಕರನ್ನು - ಮಗುವು ಸತ್ತಿತೋ ಎಂದು ಕೇಳಿದನು. ಅವರು - ಹೌದು, ಸತ್ತಿತು ಎಂದು ಉತ್ತರಕೊಟ್ಟರು.",
			"20": "<sup>20</sup> ಆಗ ದಾವೀದನು ನೆಲದಿಂದೆದ್ದು ಸ್ನಾನಮಾಡಿ ತೈಲ ಹಚ್ಚಿಕೊಂಡು ತನ್ನ ಬಟ್ಟೆಗಳನ್ನು ಬದಲಿಸಿ ಯೆಹೋವನ ಮಂದಿರಕ್ಕೆ ಹೋಗಿ ಆರಾಧನೆ ಮಾಡಿದನು. ಅನಂತರ ಮನೆಗೆ ಬಂದು ಆಹಾರತರಿಸಿ ಊಟಮಾಡಿದನು.",
			"21": "<sup>21</sup> ಇದನ್ನು ನೋಡಿ ಅವನ ಸೇವಕರು ಅವನಿಗೆ - ಇದೇನು ನೀನು ಮಾಡಿದ್ದು? ಹುಡುಗನು ಜೀವದಿಂದಿದ್ದಾಗ ಅಳುತ್ತಾ ಉಪವಾಸವಾಗಿದ್ದಿ; ಹುಡುಗನು ಸತ್ತನಂತರ ಎದ್ದು ಊಟಮಾಡಿದಿ ಅಂದರು.",
			"22": "<sup>22</sup> ದಾವೀದನು ಅವರಿಗೆ - ಹುಡುಗನು ಜೀವದಿಂದಿದ್ದಾಗ ಒಂದು ವೇಳೆ ಯೆಹೋವನು ಕೃಪೆಮಾಡಿ ಅವನನ್ನು ಉಳಿಸಾನು ಅಂದುಕೊಂಡು ಉಪವಾಸಮಾಡಿದೆನು, ಅತ್ತೆನು.",
			"23": "<sup>23</sup> ಈಗ ಸತ್ತಿದ್ದಾನಲ್ಲಾ, ನಾನೇಕೆ ಉಪವಾಸಮಾಡಬೇಕು? ಅವನನ್ನು ಹಿಂದಕ್ಕೆ ತರುವದು ನನ್ನಿಂದಾದೀತೇ? ನಾನಾಗಿ ಅವನ ಬಳಿಗೆ ಹೋಗಬೇಕೇ ಹೊರತು ಅವನು ನನ್ನ ಬಳಿಗೆ ಬರುವದಿಲ್ಲ ಎಂದು ಉತ್ತರ ಕೊಟ್ಟನು.",
			"24": "<sup>24</sup> ದಾವೀದನು ತನ್ನ ಹೆಂಡತಿಯಾದ ಬತ್ಷೆಬೆಯ ಬಳಿಗೆ ಹೋಗಿ ಆಕೆಯನ್ನು ಸಂತೈಸಿ ಆಕೆಯ ಸಂಗಡ ಮಲಗಿದನು. ಆಕೆ ಮಗನನ್ನು ಹೆತ್ತಾಗ ಅವನಿಗೆ ಸೊಲಮೋನನೆಂದು ಹೆಸರಿಟ್ಟನು. ಯೆಹೋವನು ಹುಡುಗನನ್ನು ಪ್ರೀತಿಸಿದನು;",
			"25": "<sup>25</sup> ಪ್ರವಾದಿಯಾದ ನಾತಾನನು ಯೆಹೋವನು ತನಗೆ ಆಜ್ಞಾಪಿಸಿದಂತೆ ಆತನಿಗೋಸ್ಕರವಾಗಿ ಆ ಹುಡುಗನಿಗೆ ಯೆದೀದ್ಯನು ಎಂದು ಹೆಸರಿಟ್ಟನು.",
			"26": "<sup>26</sup> ಯೋವಾಬನು ಅಮ್ಮೋನಿಯರ ಪಟ್ಟಣವಾದ ರಬ್ಬಕ್ಕೆ ಮುತ್ತಿಗೆಹಾಕಿ ಅರಮನೆಯಿದ್ದ ಭಾಗವನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡು",
			"27": "<sup>27</sup> ದಾವೀದನಿಗೆ ದೂತರ ಮುಖಾಂತರವಾಗಿ - ನಾನು ರಬ್ಬಕ್ಕೆ ಮುತ್ತಿಗೆಹಾಕಿ ಹೊಳೆಯ ಬಳಿಯಲ್ಲಿರುವ ಭಾಗವನ್ನು ಸ್ವಾಧೀನ ಮಾಡಿಕೊಂಡಿದ್ದೇನೆ.",
			"28": "<sup>28</sup> ನೀನು ಬೇಗನೆ ಉಳಿದ ಸೈನ್ಯವನ್ನು ಕೂಡಿಸಿಕೊಂಡು ಬಂದು ಪಟ್ಟಣಕ್ಕೆ ಮುತ್ತಿಗೆ ಹಾಕಿ ಉಳಿದ ಭಾಗವನ್ನು ಸ್ವಾಧೀನಪಡಿಸಿಕೋ; ಅದನ್ನು ನಾನೇ ಹಿಡಿದರೆ ಅದಕ್ಕೆ ನನ್ನ ಹೆಸರು ಬರುವದು ಎಂದು ಹೇಳಿ ಕಳುಹಿಸಿದನು.",
			"29": "<sup>29</sup> ಆಗ ದಾವೀದನು ಜನರೆಲ್ಲರನ್ನೂ ಕೂಡಿಸಿಕೊಂಡು ಹೋಗಿ ರಬ್ಬಕ್ಕೆ ಮುತ್ತಿಗೆಹಾಕಿ ಅದನ್ನು ವಶಮಾಡಿಕೊಂಡನು.",
			"30": "<sup>30</sup> ಇದಲ್ಲದೆ ಅವನು ಅವರ ಮಲ್ಕಾಮಮೂರ್ತಿಯ ತಲೆಯ ಮೇಲಿದ್ದ ಬಂಗಾರದ ಕಿರೀಟವನ್ನು ತೆಗೆದುಕೊಂಡನು; ಅದು ನಾಲ್ಕು ಸಾವಿರ ತೊಲೆ ತೂಕದ್ದು. ಅದರಲ್ಲಿದ್ದ ಮಣಿಯನ್ನು ದಾವೀದನು ತನ್ನ ಶಿರಸ್ಸಿನಲ್ಲಿ ಧರಿಸಿಕೊಂಡನು. ಆ ಪಟ್ಟಣದಿಂದ ದೊಡ್ಡ ಕೊಳ್ಳೆಯನ್ನು ಒಯ್ದನು.",
			"31": "<sup>31</sup> ಊರಿನ ನಿವಾಸಿಗಳನ್ನು ಹಿಡಿದು ಗರಗಸ ಗುದ್ದಲಿ ಕೊಡಲಿಗಳಿಂದ ಕೆಲಸ ಮಾಡುವದಕ್ಕೂ ಇಟ್ಟಿಗೆಗಳನ್ನು ಮಾಡುವದಕ್ಕೂ ಹಚ್ಚಿದನು. ಅಮ್ಮೋನಿಯರ ಎಲ್ಲಾ ಪಟ್ಟಣದವರಿಗೂ ಇದೇ ಗತಿಯಾಯಿತು. ಅನಂತರ ದಾವೀದನು ಎಲ್ಲಾ ಸೈನಿಕರೊಡನೆ ಯೆರೂಸಲೇಮಿಗೆ ಬಂದನು."
		},
		"13": {
			"1": "<sup>1</sup> ದಾವೀದನ ಮಗನಾದ ಅಬ್ಷಾಲೋಮನಿಗೆ ತಾಮಾರಳೆಂಬ ಬಹು ಸುಂದರಿಯಾದ ಒಬ್ಬ ತಂಗಿಯಿದ್ದಳು. ದಾವೀದನ ಮಗನಾದ ಅಮ್ನೋನನು ಆಕೆಯನ್ನು ಮೋಹಿಸಿದನು.",
			"2": "<sup>2</sup> ಅವನು ಆಕೆಯ ಮೇಲಣ ಮೋಹದಿಂದ ಪೀಡಿತನಾಗಿ ಅಸ್ವಸ್ಥನಾದನು. ಆಕೆಯು ಕನ್ಯೆಯಾಗಿದ್ದದರಿಂದ ಆಕೆಗೆ ಏನು ಮಾಡುವದೂ ಅಸಾಧ್ಯವೆಂದು ಅಮ್ನೋನನಿಗೆ ಕಂಡಿತು.",
			"3": "<sup>3</sup> ಅವನಿಗೆ ಯೋನಾದಾಬನೆಂಬ ಒಬ್ಬ ಗೆಳೆಯನಿದ್ದನು. ಇವನು ದಾವೀದನ ಅಣ್ಣನಾದ ಶಿಮ್ಮನ ಮಗನು.",
			"4": "<sup>4</sup> ಬಹು ಯುಕ್ತಿವಂತನಾದ ಇವನು ಒಂದು ದಿವಸ ಅಮ್ನೋನನನ್ನು - ರಾಜಪುತ್ರನೇ, ನೀನು ದಿನೇ ದಿನೇ ಕ್ಷೀಣನಾಗುತ್ತಾ ಬರುವದೇಕೆ? ನನಗೆ ತಿಳಿಸುವದಿಲ್ಲವೋ ಎಂದು ಕೇಳಲು ಅವನು - ನನ್ನ ತಮ್ಮನಾದ ಅಬ್ಷಾಲೋಮನ ತಂಗಿ ತಾಮಾರಳ ಮೇಲೆ ನನಗೆ ಮೋಹವುಂಟಾಗಿದೆ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"5": "<sup>5</sup> ಆಗ ಯೋನಾದಾಬನು ಅವನಿಗೆ - ನೀನು ಅಸ್ವಸ್ಥನಾದವನಂತೆ ಹಾಸಿಗೆಯ ಮೇಲೆ ಮಲಗಿಕೋ, ನಿನ್ನ ತಂದೆಯು ನಿನ್ನನ್ನು ನೋಡುವದಕ್ಕೆ ಬಂದಾಗ ಅವನಿಗೆ - ದಯವಿಟ್ಟು ನನ್ನ ತಂಗಿಯಾದ ತಾಮಾರಳನ್ನು ನನಗಾಗಿ ಆಹಾರ ಸಿದ್ಧಮಾಡುವದಕ್ಕೆ ಕಳುಹಿಸು. ಆಕೆ ನನ್ನೆದುರಿನಲ್ಲಿಯೇ ಸಿದ್ಧಮಾಡಿ ಕೊಡುವದಾದರೆ ಊಟಮಾಡೇನು ಎಂದು ಹೇಳು ಅಂದನು.",
			"6": "<sup>6</sup> ಅಮ್ನೋನನು ಅಸ್ವಸ್ಥವಾದವನೋ ಎಂಬಂತೆ ಮಲಗಿಕೊಂಡನು. ಅರಸನು ಅವನನ್ನು ನೋಡುವದಕ್ಕೆ ಬಂದಾಗ ಅವನು ಅರಸನಿಗೆ - ದಯವಿಟ್ಟು ನನ್ನ ತಂಗಿಯಾದ ತಾಮಾರಳನ್ನು ನನ್ನ ಬಳಿಗೆ ಕಳುಹಿಸು; ಆಕೆ ನನ್ನ ಕಣ್ಣುಮುಂದೆಯೇ ಎರಡು ಭಕ್ಷ್ಯಗಳನ್ನು ಮಾಡಿಕೊಡುವದಾದರೆ ಊಟಮಾಡೇನು ಎಂದು ಬೇಡಿಕೊಳ್ಳಲು",
			"7": "<sup>7</sup> ದಾವೀದನು ಅರಮನೆಗೆ ಆಳುಗಳನ್ನು ಕಳುಹಿಸಿ ಆಕೆಗೆ - ನೀನು ನಿನ್ನ ಅಣ್ಣನಾದ ಅಮ್ನೋನನ ಮನೆಗೆ ಹೋಗಿ ಅವನಿಗೋಸ್ಕರ ಆಹಾರವನ್ನು ಸಿದ್ಧಮಾಡು ಎಂದು ಹೇಳಿಸಿದನು.",
			"8": "<sup>8</sup> ಕೂಡಲೆ ಆಕೆಯು ತನ್ನ ಅಣ್ಣನಾದ ಅಮ್ನೋನನ ಮನೆಗೆ ಹೋದಳು; ಅವನು ಮಲಗಿದ್ದನು. ಆಕೆಯು ಹೋಗಿ ಹಿಟ್ಟು ತೆಗೆದುಕೊಂಡು ನಾದಿ ಅವನ ಕಣ್ಣು ಮುಂದೆಯೇ ಭಕ್ಷ್ಯಗಳನ್ನು ಮಾಡಿ ಸುಟ್ಟಳು.",
			"9": "<sup>9</sup> ಅನಂತರ ಆಕೆ ಅವುಗಳನ್ನು ಬಾಂಡ್ಲದಿಂದ ತೆಗೆದು ಅವನ ಮುಂದಿಟ್ಟಳು. ಅವನು ಊಟ ಮಾಡದೆ ಎಲ್ಲಾ ಜನರನ್ನು ತನ್ನ ಬಳಿಯಿಂದ ಹೊರಡಿಸಬೇಕೆಂದು ಅಪ್ಪಣೆ ಮಾಡಿದನು.",
			"10": "<sup>10</sup> ಎಲ್ಲರೂ ಹೋದ ಮೇಲೆ ಅವನು ತಾಮಾರಳಿಗೆ - ಆಹಾರವನ್ನು ಈ ಕೋಣೆಗೆ ತೆಗೆದುಕೊಂಡು ಬಾ; ನೀನಾಗಿ ತಂದುಕೊಟ್ಟರೆ ಊಟಮಾಡುತ್ತೇನೆ ಎಂದು ಹೇಳಿದನು. ಆಗ ತಾಮಾರಳು ತಾನು ಮಾಡಿದ ಭಕ್ಷ್ಯಗಳನ್ನು ಅಣ್ಣನಾದ ಅಮ್ನೋನನಿದ್ದ ಕೋಣೆಗೆ ತೆಗೆದುಕೊಂಡುಹೋಗಿ ಅವನ ಮುಂದಿಟ್ಟಳು.",
			"11": "<sup>11</sup> ಕೂಡಲೇ ಅವನು ಆಕೆಯನ್ನು ಹಿಡಿದು - ನನ್ನ ತಂಗಿಯೇ, ಬಂದು ನನ್ನ ಸಂಗಡ ಮಲಗಿಕೋ ಅಂದನು.",
			"12": "<sup>12</sup> ಆಕೆಯು - ಅಣ್ಣನೇ, ಬೇಡ; ನನ್ನನ್ನು ಕೆಡಿಸಬೇಡ; ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಇಂಥದು ನಡೆಯಬಾರದು.",
			"13": "<sup>13</sup> ಇಂಥ ಹುಚ್ಚುತನವನ್ನು ಮಾಡಬೇಡ. ಈ ಅಪಮಾನವನ್ನು ಮರೆಮಾಡುವದು ಹೇಗೆ? ನಿನಗಂತೂ ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ನೀಚನೆಂಬ ಹೆಸರು ತಪ್ಪದು. ಆದದರಿಂದ ದಯವಿಟ್ಟು ಅರಸನ ಸಂಗಡ ಮಾತಾಡು. ಅವನು ನನ್ನನ್ನು ನಿನಗೆ ಕೊಡದಿರಲಾರನು ಎಂದಳು.",
			"14": "<sup>14</sup> ಆದರೆ ಅವನು ಆಕೆಯ ಮಾತನ್ನು ಕೇಳದೆ ಬಲಾತ್ಕಾರದಿಂದ ಆಕೆಯನ್ನು ಕೆಡಿಸಿದನು.",
			"15": "<sup>15</sup> ಇದಾದ ಮೇಲೆ ಅವನಿಗೆ ಆಕೆಯಲ್ಲಿ ತುಂಬಾ ದ್ವೇಷಹುಟ್ಟಿತು. ಅವನ ಮುಂಚಿನ ಪ್ರೀತಿಗಿಂತ ದ್ವೇಷವೇ ಹೆಚ್ಚಾಯಿತು.",
			"16": "<sup>16</sup> ಅಮ್ನೋನನು ಆಕೆಗೆ - ಎದ್ದು ಹೋಗು ಎಂದು ಹೇಳಿದಾಗ ಆಕೆಯು - ಹಾಗೆ ಮಾಡಬೇಡ; ನನ್ನನ್ನು ಹೊರಡಿಸಿಬಿಡುವದು ನೀನು ಮಾಡಿದ ಮೊದಲನೆಯ ಅನ್ಯಾಯಕ್ಕಿಂತ ಹೆಚ್ಚಿನ ಅನ್ಯಾಯವಾಗಿದೆ ಅಂದಳು.",
			"17": "<sup>17</sup> ಆದರೆ ಅವನು ಆಕೆಯ ಮಾತನ್ನು ಕೇಳದೆ ಯಾವಾಗಲೂ ತನ್ನ ಹತ್ತಿರವೇ ಇರುವಂಥ ಆಳನ್ನು ಕರೆದು ಅವನಿಗೆ - ಇವಳನ್ನು ಹೊರಗೆ ತಳ್ಳಿ ಕದಮುಚ್ಚು ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"18": "<sup>18</sup> (ಆಕೆಯು ನಾನಾ ವರ್ಣಗಳುಳ್ಳ ಒಂದು ನಿಲುವಂಗಿಯನ್ನು ಧರಿಸಿಕೊಂಡಿದ್ದಳು. ಕನ್ಯೆಯರಾದ ರಾಜಪುತ್ರಿಯರು ಇದೇ ತರದ ವಸ್ತ್ರಗಳನ್ನು ಹಾಕಿಕೊಳ್ಳುತ್ತಿದ್ದರು.) ಸೇವಕನು ಆಕೆಯನ್ನು ಹೊರಗೆ ಕಳುಹಿಸಿ ಕದಮುಚ್ಚಿದನು.",
			"19": "<sup>19</sup> ತಾಮಾರಳು ತಲೆಯ ಮೇಲೆ ಬೂದಿ ಹಾಕಿಕೊಂಡು ನಿಲುವಂಗಿಯನ್ನು ಹರಿದುಕೊಂಡು ಕೈಗಳನ್ನು ತಲೆಯ ಮೇಲಿಟ್ಟು ಗೋಳಾಡುತ್ತಾ ಹೋದಳು.",
			"20": "<sup>20</sup> ಆಕೆಯ ಸಹೋದರನಾದ ಅಬ್ಷಾಲೋಮನು ಆಕೆಯನ್ನು ಕಂಡು - ನಿನ್ನ ಅಣ್ಣನಾದ ಅಮ್ನೋನನು ನಿನ್ನನ್ನು ಕೂಡಿದನೇನು? ನನ್ನ ತಂಗಿಯೇ, ಈಗ ಸುಮ್ಮನಿರು; ಅವನು ನಿನ್ನ ಅಣ್ಣನಲ್ಲವೋ? ಈ ಸಂಗತಿಯನ್ನು ಮನಸ್ಸಿಗೆ ಹಚ್ಚಿಕೊಳ್ಳಬೇಡ ಎಂದು ಹೇಳಿದನು. ತಾಮಾರಳು ಒಂಟಿಗಳಾಗಿ ತನ್ನ ಅಣ್ಣನಾದ ಅಬ್ಷಾಲೋಮನ ಮನೆಯಲ್ಲೇ ವಾಸಿಸಿದಳು.",
			"21": "<sup>21</sup> ಅರಸನಾದ ದಾವೀದನು ಈ ಸಂಗತಿಯನ್ನು ಕೇಳಿ ಬಹಳವಾಗಿ ಕೋಪಗೊಂಡನು.",
			"22": "<sup>22</sup> ಅಬ್ಷಾಲೋಮನು ಅಮ್ನೋನನ ಸಂಗಡ ಒಳ್ಳೇ ಮಾತನ್ನಾಗಲಿ ಕೆಟ್ಟ ಮಾತನ್ನಾಗಲಿ ಆಡಲಿಲ್ಲ. ತನ್ನ ತಂಗಿಯಾದ ತಾಮಾರಳನ್ನು ಕೆಡಿಸಿದ್ದಕ್ಕಾಗಿ ಅವನು ಅಮ್ನೋನನನ್ನು ದ್ವೇಷಿಸಿದನು.",
			"23": "<sup>23</sup> ಎರಡು ವರುಷಗಳಾದನಂತರ ಅಬ್ಷಾಲೋಮನು ಒಂದು ದಿನ ಎಫ್ರಾಯೀಮಿನ ಬಳಿಯಲ್ಲಿರುವ ಬಾಳ್ ಹಾಚೋರಿನಲ್ಲಿ ಕುರಿಗಳ ಉಣ್ಣೆಯನ್ನು ಕತ್ತರಿಸುವಾಗ ಎಲ್ಲಾ ರಾಜಪುತ್ರರನ್ನು ಔತಣಕ್ಕೆ ಕರೆದನು.",
			"24": "<sup>24</sup> ಅವನು ಅರಸನ ಬಳಿಗೂ ಹೋಗಿ ಅವನನ್ನು - ಸ್ವಾಮೀ, ನಿನ್ನ ಸೇವಕನು ಕುರಿಗಳ ಉಣ್ಣೆ ಕತ್ತರಿಸುವವರನ್ನು ಕರಿಸಿದ್ದಾನೆ. ಅರಸನು ತನ್ನ ಸೇವಕರೊಡನೆ ಅವನ ಬಳಿಗೆ ಚಿತ್ತಯಿಸೋಣವಾಗಲಿ ಎಂದು ಬೇಡಿಕೊಂಡನು.",
			"25": "<sup>25</sup> ಅರಸನು ಅವನಿಗೆ - ಮಗನೇ, ಬೇಡ; ನಾವೆಲ್ಲರೂ ಬಂದರೆ ನಿನಗೆ ಭಾರವಾದೀತು ಅನ್ನಲು ಅಬ್ಷಾಲೋಮನು ಅವನನ್ನು ಬಹಳವಾಗಿ ಒತ್ತಾಯಪಡಿಸಿದನು. ಆದರೂ ಅರಸನು ಒಪ್ಪಲಿಲ್ಲ, ಅವನನ್ನು ಆಶೀರ್ವದಿಸಿದನು, ಅಷ್ಟೆ.",
			"26": "<sup>26</sup> ಆಗ ಅಬ್ಷಾಲೋಮನು - ನೀನು ಬಾರದಿದ್ದರೆ ನನ್ನ ಅಣ್ಣನಾದ ಅಮ್ನೋನನನ್ನು ನಮ್ಮೊಡನೆ ಕಳುಹಿಸು ಅಂದನು. ಅದಕ್ಕೆ ಅರಸನು - ಅವನೇಕೆ ನಿನ್ನ ಸಂಗಡ ಬರಬೇಕು ಎಂದು ಕೇಳಿದನು.",
			"27": "<sup>27</sup> ಆದರೆ ಅಬ್ಷಾಲೋಮನು ಬಹಳವಾಗಿ ಒತ್ತಾಯಪಡಿಸಿದ್ದರಿಂದ ಅರಸನು ಅಮ್ನೋನನನ್ನೂ ತನ್ನ ಬೇರೆ ಎಲ್ಲಾ ಮಕ್ಕಳನ್ನೂ ಕಳುಹಿಸಿದನು.",
			"28": "<sup>28</sup> ಇದಕ್ಕೆ ಮೊದಲೇ ಅಬ್ಷಾಲೋಮನು ತನ್ನ ಆಳುಗಳಿಗೆ - ಕೇಳಿರಿ, ಅಮ್ನೋನನು ದ್ರಾಕ್ಷಾರಸ ಕುಡಿದು ಮತ್ತನಾಗಿರುವಾಗ ನಾನು ಹೊಡೆಯಬೇಕೆಂದು ಹೇಳಿದ ಕೂಡಲೆ ಅವನನ್ನು ಹೊಡೆದು ಕೊಂದುಹಾಕಿರಿ; ಹೆದರಬೇಡಿರಿ, ನಿಮಗೆ ಆಜ್ಞಾಪಿಸಿದವನು ನಾನಲ್ಲವೋ? ಧೈರ್ಯದಿಂದಿರಿ, ಶೂರರಾಗಿರಿ ಎಂದು ಹೇಳಿದ್ದನು.",
			"29": "<sup>29</sup> ಅಬ್ಷಾಲೋಮನ ಆಳುಗಳು ಅವನ ಆಜ್ಞೆಯಂತೆ ಅಮ್ನೋನನನ್ನು ಕೊಂದರು. ರಾಜಪುತ್ರರೆಲ್ಲರೂ ತಮ್ಮತಮ್ಮ ಹೇಸರಕತ್ತೆಗಳನ್ನು ಹತ್ತಿ ಓಡಿಹೋದರು.",
			"30": "<sup>30</sup> ಅವರು ಇನ್ನೂ ಮಾರ್ಗದಲ್ಲಿರುವಾಗಲೇ ದಾವೀದನಿಗೆ - ಅಬ್ಷಾಲೋಮನು ರಾಜಪುತ್ರರೆಲ್ಲರನ್ನೂ ಕೊಂದುಹಾಕಿದನು, ಒಬ್ಬನನ್ನೂ ಉಳಿಸಲಿಲ್ಲ ಎಂಬ ಸುದ್ದಿ ಮುಟ್ಟಿತು.",
			"31": "<sup>31</sup> ಆಗ ಅರಸನು ಎದ್ದು ತನ್ನ ಬಟ್ಟೆಗಳನ್ನು ಹರಿದುಕೊಂಡು ನೆಲಕ್ಕೆ ಬಿದ್ದನು. ಅವನ ಸೇವಕರೆಲ್ಲರೂ ತಮ್ಮತಮ್ಮ ಬಟ್ಟೆಗಳನ್ನು ಹರಕೊಂಡು ಅವನ ಹತ್ತಿರ ನಿಂತರು.",
			"32": "<sup>32</sup> ಆಗ ದಾವೀದನ ಅಣ್ಣನಾದ ಶಿಮ್ಮನ ಮಗ ಯೋನಾದಾಬನು ದಾವೀದನಿಗೆ - ರಾಜಪುತ್ರರೆಲ್ಲರನ್ನೂ ಕೊಂದು ಹಾಕಿದ್ದಾರೆಂದು ನನ್ನ ಒಡೆಯನು ನೆನಸದಿರಲಿ. ಅಮ್ನೋನನೊಬ್ಬನೇ ಸತ್ತಿರಬೇಕು. ಅವನು ಅಬ್ಷಾಲೋಮನ ತಂಗಿಯಾದ ತಾಮಾರಳನ್ನು ಕೆಡಿಸಿದಾಗಿನಿಂದಲೇ ಹೀಗಾಗುವದೆಂದು ಅಬ್ಷಾಲೋಮನ ಮುಖದಿಂದ ತೋರುತ್ತಿತ್ತು.",
			"33": "<sup>33</sup> ಆದದರಿಂದ ರಾಜಪುತ್ರರೆಲ್ಲರೂ ಸತ್ತರೆಂಬ ಸುದ್ದಿಗೆ ಅರಸನು ಲಕ್ಷ್ಯಕೊಡದಿರಲಿ; ಅಮ್ನೋನನೊಬ್ಬನೇ ಸತ್ತಿದ್ದಾನಷ್ಟೆ ಎಂದು ಹೇಳಿ ಸಂತೈಸಿದನು.",
			"34": "<sup>34</sup> (ಅಬ್ಷಾಲೋಮನು ತಪ್ಪಿಸಿಕೊಂಡನು.) ಅಷ್ಟರಲ್ಲಿ ದಾರಿ ನೋಡುತ್ತಿದ್ದ ಕಾವಲುಗಾರನು ಒಂದು ದೊಡ್ಡ ಗುಂಪು ಹೋರೋನಿನ ಮಾರ್ಗವಾಗಿ ಗುಡ್ಡ ಇಳಿದು ಬರುವದನ್ನು ಕಂಡನು.",
			"35": "<sup>35</sup> ಯೋನಾದಾಬನು ಅರಸನಿಗೆ - ಇಗೋ, ರಾಜಕುಮಾರರು ಬರುತ್ತಿದ್ದಾರೆ; ನಿನ್ನ ಸೇವಕನ ಮಾತಿನಂತೆಯೇ ಆಯಿತಲ್ಲವೋ ಎಂದು ಹೇಳಿ ಮುಗಿಸುವಷ್ಟರಲ್ಲೇ",
			"36": "<sup>36</sup> ರಾಜಕುಮಾರರು ಬಂದು ಗಟ್ಟಿಯಾಗಿ ಅಳತೊಡಗಿದರು. ಅರಸನೂ ಅವನ ಸೇವಕರೂ ಬಹಳವಾಗಿ ಗೋಳಾಡಿದರು.",
			"37": "<sup>37</sup> ದಾವೀದನು ತನ್ನ ಮಗನಿಗೋಸ್ಕರ ನಿತ್ಯವೂ ದುಃಖಪಡುತ್ತಿದ್ದನು. ಅಬ್ಷಾಲೋಮನು ತಪ್ಪಿಸಿಕೊಂಡು ಗೆಷೂರಿಗೆ ಓಡಿಹೋಗಿ ಅಲ್ಲಿನ ಅರಸನೂ ಅಮ್ಮೀಹೂದನ ಮಗನೂ ಆದ",
			"38": "<sup>38</sup> ತಲ್ಮೈಯ ಬಳಿಯಲ್ಲಿ ಮೂರು ವರುಷ ಇದ್ದನು.",
			"39": "<sup>39</sup> ಅರಸನಾದ ದಾವೀದನು ಅಮ್ನೋನನ ಮರಣದ ವಿಷಯ ಆದರಣೆಹೊಂದಿ ತನ್ನ ಮಗನಾದ ಅಬ್ಷಾಲೋಮನನ್ನು ತಿರಿಗಿ ನೋಡುವದಕ್ಕೆ ಬಹಳ ಆತುರಪಟ್ಟನು."
		},
		"14": {
			"1": "<sup>1</sup> ಅರಸನು ಅಬ್ಷಾಲೋಮನಿಗಾಗಿ ಹಂಬಲಿಸುತ್ತಿರುವದನ್ನು ಚೆರೂಯಳ ಮಗನಾದ ಯೋವಾಬನು ತಿಳಿದು",
			"2": "<sup>2</sup> ತೆಕೋವದಿಂದ ಒಬ್ಬ ಬುದ್ದಿವಂತೆಯಾದ ಸ್ತ್ರೀಯನ್ನು ಕರೇಕಳುಹಿಸಿ ಆಕೆಗೆ - ನೀನು ಪ್ರಿಯರನ್ನು ಕಳಕೊಂಡು ಬಹುದಿವಸಗಳಿಂದ ಶೋಕಪಡುತ್ತಿರುವ ಸ್ತ್ರೀಯೋ ಎಂಬಂತೆ ಶೋಕವಸ್ತ್ರಗಳನ್ನು ಧರಿಸಿಕೊಂಡು",
			"3": "<sup>3</sup> ಎಣ್ಣೆ ಹಚ್ಚಿಕೊಳ್ಳದೆ ಅರಸನ ಬಳಿಗೆ ಹೋಗಿ ಈ ಮಾತುಗಳನ್ನು ಅವನಿಗೆ ಹೇಳು ಎಂದು ಆಜ್ಞಾಪಿಸಿ ಹೇಳಬೇಕಾದ ಮಾತುಗಳನ್ನು ಆಕೆಗೆ ಕಲಿಸಿಕೊಟ್ಟನು.",
			"4": "<sup>4</sup> ತೆಕೋವದ ಸ್ತ್ರೀಯು ಅರಸನ ಹತ್ತಿರ ಹೋಗಿ ಅವನ ಮುಂದೆ ನೆಲಕ್ಕೆ ಬಿದ್ದು - ಅರಸೇ, ರಕ್ಷಿಸು ಎಂದು ಕೂಗಿದಳು.",
			"5": "<sup>5</sup> ಅರಸನು ನಿನಗೇನಾಯಿತು ಎಂದು ಆಕೆಯನ್ನು ಕೇಳಲು ಆಕೆಯು - ನಾನು ವಿಧವೆ; ಗಂಡನು ಸತ್ತುಹೋಗಿದ್ದಾನೆ.",
			"6": "<sup>6</sup> ನಿನ್ನ ದಾಸಿಯಾದ ನನಗೆ ಇಬ್ಬರು ಮಕ್ಕಳಿದ್ದರು. ಒಂದು ದಿವಸ ಅವರಿಬ್ಬರೂ ಹೊಲದಲ್ಲಿ ಜಗಳವಾಡಿದರು; ಅಲ್ಲಿ ಬಿಡಿಸುವವರಾರೂ ಇರಲಿಲ್ಲವಾದದರಿಂದ ಒಬ್ಬನು ಇನ್ನೊಬ್ಬನನ್ನು ಹೊಡೆದು ಕೊಂದನು.",
			"7": "<sup>7</sup> ಈಗ ನೋಡು, ಸಂಬಂಧಿಕರೆಲ್ಲಾ ನಿನ್ನ ದಾಸಿಯಾದ ನನಗೆ ವಿರೋಧವಾಗಿ ಎದ್ದು - ತಮ್ಮನನ್ನು ಕೊಂದವನೆಲ್ಲಿ? ಅವನನ್ನು ನಮಗೆ ಒಪ್ಪಿಸು; ತಮ್ಮನ ಪ್ರಾಣಕ್ಕಾಗಿ ಅವನ ಪ್ರಾಣವನ್ನೂ ತೆಗೆದುಬಿಟ್ಟು ನಿನ್ನನ್ನು ಬಾಧ್ಯಸ್ಥನಿಲ್ಲದ ಹಾಗೆ ಮಾಡಿಬಿಡುತ್ತೇವೆ ಅನ್ನುತ್ತಾರೆ. ಹೀಗೆ ಅವರು ನನಗುಳಿದಿರುವ ಒಂದು ಕೆಂಡವನ್ನೂ ಆರಿಸಿಬಿಟ್ಟು ನನ್ನ ಗಂಡನ ಹೆಸರನ್ನೂ ಸಂತಾನವನ್ನೂ ಭೂಲೋಕದ ಮೇಲಣಿಂದ ಅಳಿಸಬೇಕೆಂದಿದ್ದಾರೆ ಎಂದು ಉತ್ತರಕೊಟ್ಟಳು.",
			"8": "<sup>8</sup> ಆಗ ಅರಸನು - ಮನೆಗೆ ಹೋಗು, ನಿನ್ನ ವಿಷಯದಲ್ಲಿ ಆಜ್ಞಾಪಿಸುತ್ತೇನೆ ಎಂದು ಹೇಳಿದ್ದಕ್ಕೆ",
			"9": "<sup>9</sup> ಆ ತೆಕೋವದ ಸ್ತ್ರೀಯು - ಅರಸನೇ, ಒಡೆಯನೇ, ಅಪರಾಧವು ನನ್ನ ಮೇಲೆಯೂ ನನ್ನ ಕುಟುಂಬದ ಮೇಲೆಯೂ ಇರಲಿ; ಅರಸನಿಗೂ ಅವನ ಸಿಂಹಾಸನಕ್ಕೂ ದೋಷಹತ್ತದಿರಲಿ ಅಂದಳು.",
			"10": "<sup>10</sup> ಅರಸನು ಆಕೆಗೆ - ಹಾಗೆ ಅಂದವರನ್ನು ನನ್ನ ಬಳಿಗೆ ಕರಕೊಂಡು ಬಾ; ಅವರು ನಿನ್ನನ್ನು ಮುಟ್ಟದಂತೆ ಮಾಡುತ್ತೇನೆ ಅಂದನು.",
			"11": "<sup>11</sup> ಆಗ ಆ ಸ್ತ್ರೀಯು - ಸಮೀಪಬಂಧುವು ನನ್ನ ಎರಡನೆಯ ಮಗನನ್ನು ಕೊಂದು ನನ್ನನ್ನು ಪೂರ್ಣವಾಗಿ ಹಾಳುಮಾಡದಂತೆ ಅರಸನು ತಾನಾಗಿ ನೋಡಿಕೊಳ್ಳುವನೆಂಬದಾಗಿ ನನ್ನ ದೇವರಾದ ಯೆಹೋವನ ಮೇಲೆ ಆಣೆಯಿಟ್ಟು ಪ್ರಮಾಣಮಾಡಬೇಕು ಎಂದು ಬೇಡಿಕೊಂಡಳು. ಅದಕ್ಕೆ ಅರಸನು - ಯೆಹೋವನಾಣೆ, ನಿನ್ನ ಮಗನ ಕೂದಲುಗಳಲ್ಲಿ ಒಂದನ್ನೂ ನೆಲಕ್ಕೆ ಬೀಳಗೊಡುವದಿಲ್ಲ ಎಂದು ಹೇಳಿದನು.",
			"12": "<sup>12</sup> ಆಗ ಸ್ತ್ರೀಯು - ನನ್ನ ಒಡೆಯನಿಗೆ ಇನ್ನೊಂದು ಮಾತನ್ನು ಹೇಳಿಕೊಳ್ಳುವದಕ್ಕೆ ಅಪ್ಪಣೆಯಾಗಲಿ ಅನ್ನಲು ಅರಸನು - ಹೇಳು ಅಂದನು.",
			"13": "<sup>13</sup> ಆಕೆಯು - ಅರಸನು ಈ ತೀರ್ಪುಕೊಟ್ಟದ್ದರಿಂದ ತನ್ನನ್ನೇ ಅಪರಾಧಿಯೆಂದು ನಿರ್ಣಯಿಸಿದ ಹಾಗಾಯಿತು. ಅವನು ತಳ್ಳಲ್ಪಟ್ಟವನನ್ನು ಸೇರಿಸಿಕೊಳ್ಳಲೊಲ್ಲದೆ ಹೋಗುವದರಿಂದ ದೇವಪ್ರಜೆಗೆ ವಿರೋಧವಾಗಿ ಅದೇ ಆಲೋಚನೆ ಮಾಡಿದ ಹಾಗಾಯಿತು. ಅರಸನು ಹೀಗೇಕೆ ಮಾಡಬೇಕು?",
			"14": "<sup>14</sup> ನಾವು ಸಾಯುವವರು; ನೆಲದ ಮೇಲೆ ಚೆಲ್ಲಲ್ಪಟ್ಟು ತಿರಿಗಿ ಕೂಡಿಸಲ್ಪಡಲಾರದ ನೀರಿನಂತಿದ್ದೇವೆ. ಮನುಷ್ಯರ ಪ್ರಾಣವನ್ನು ತೆಗೆಯುವದಕ್ಕೆ ದೇವರಿಗೆ ಇಷ್ಟವಿಲ್ಲ; ತಳ್ಳಲ್ಪಟ್ಟವನು ತಿರಿಗಿ ತನ್ನ ಬಳಿಗೆ ಬರುವ ಹಾಗೆ ಆತನು ಸದುಪಾಯಗಳನ್ನು ಕಲ್ಪಿಸುವವನಾಗಿರುತ್ತಾನೆ.",
			"15": "<sup>15</sup> ಅದಿರಲಿ, ಜನರು ನಿನ್ನ ಸೇವಕಿಯಾದ ನನ್ನನ್ನು ಹೆದರಿಸಿದ್ದರಿಂದ ನಾನು ಈ ಸಂಗತಿಯನ್ನು ಅರಸನಿಗೆ ತಿಳಿಸಿದರೆ ಅವನು ತನ್ನ ಸೇವಕಿಯ ಬಿನ್ನಹವನ್ನು ಲಾಲಿಸಾನು ಎಂದುಕೊಂಡು ನನ್ನ ಒಡೆಯನಾದ ಅರಸನಿಗೆ ಇದನ್ನು ತಿಳಿಸುವದಕ್ಕೆ ಬಂದೆನು.",
			"16": "<sup>16</sup> ಅರಸನು ತನ್ನ ಸೇವಕಿಯಾದ ನನ್ನ ಬಿನ್ನಹವನ್ನು ಲಾಲಿಸಿ ನನ್ನನ್ನೂ ನನ್ನ ಮಗನನ್ನೂ ದೇವರ ಸ್ವಾಸ್ತ್ಯದಿಂದ ತೆಗೆದುಹಾಕಬೇಕೆಂದಿರುವವರ ಕೈಗೆ ಸಿಕ್ಕದಂತೆ ತಪ್ಪಿಸಿ ರಕ್ಷಿಸುವನೆಂದು ನಂಬಿಕೊಂಡಿದ್ದೇನೆ.",
			"17": "<sup>17</sup> ನನ್ನ ಒಡೆಯನಾದ ಅರಸನ ಮಾತು ಸಮಾಧಾನಕ್ಕೆ ಕಾರಣವಾಗುವದೆಂದು ನೆನಸಿ ನಿನ್ನ ದಾಸಿಯಾದ ನಾನು ಬಂದೆನು. ನ್ಯಾಯಾನ್ಯಾಯಗಳನ್ನು ಕಂಡುಹಿಡಿಯುವದರಲ್ಲಿ ನನ್ನ ಒಡೆಯನಾದ ಅರಸನು ದೇವದೂತನಂತಿರುತ್ತಾನೆ; ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನು ನಿನ್ನ ಸಂಗಡ ಇರಲಿ ಅಂದಳು.",
			"18": "<sup>18</sup> ಆಗ ಅರಸನು ಆ ಸ್ತ್ರೀಗೆ - ನಾನು ನಿನ್ನನ್ನು ಒಂದು ಮಾತು ಕೇಳಬೇಕೆಂದಿರುತ್ತೇನೆ; ನೀನು ಅದನ್ನು ಮರೆಮಾಡದೆ ತಿಳಿಸಬೇಕು ಎಂದು ಹೇಳಲು ಆಕೆಯು - ಅರಸನ ಅಪ್ಪಣೆಯಾಗಲಿ ಎಂದು ಉತ್ತರಕೊಟ್ಟಳು.",
			"19": "<sup>19</sup> ಅರಸನು - ಈ ಕಾರ್ಯದಲ್ಲಿ ಯೋವಾಬನ ಕೈಯಿರುತ್ತದಲ್ಲವೋ ಎಂದು ಕೇಳಿದನು. ಅದಕ್ಕೆ ಸ್ತ್ರೀಯು - ಅರಸನ ಜೀವದಾಣೆ, ಅರಸನು ಒಂದು ಮಾತು ಹೇಳಿದರೆ ನಾವು ತಪ್ಪಿಸಿಕೊಂಡು ಎಡಕ್ಕಾಗಲಿ ಬಲಕ್ಕಾಗಲಿ ಜಾರಿಕೊಳ್ಳುವದಕ್ಕೆ ಆಗುವದಿಲ್ಲ; ನಿನ್ನ ದಾಸಿಯಾದ ನನ್ನನ್ನು ಪ್ರೇರಿಸಿ ಈ ಎಲ್ಲಾ ಮಾತುಗಳನ್ನು ನನಗೆ ಕಲಿಸಿಕೊಟ್ಟವನು ನಿನ್ನ ಸೇವಕನಾದ ಯೋವಾಬನೇ ಹೌದು.",
			"20": "<sup>20</sup> ಅಬ್ಷಾಲೋಮನ ವಿಷಯವನ್ನು ರೂಪಾಂತರಪಡಿಸುವದಕ್ಕೋಸ್ಕರ ನಿನ್ನ ಸೇವಕನಾದ ಯೋವಾಬನೇ ಇದನ್ನು ಮಾಡಿದನು. ನನ್ನ ಒಡೆಯನು ದೇವದೂತನಂಥ ಜ್ಞಾನಿಯು; ಅವನು ಭೂಲೋಕದಲ್ಲಿ ನಡೆಯುವದನ್ನೆಲ್ಲಾ ತಿಳುಕೊಳ್ಳುವನು ಎಂದು ಉತ್ತರಕೊಟ್ಟಳು.",
			"21": "<sup>21</sup> ಅನಂತರ ಅರಸನು ಯೋವಾಬನಿಗೆ - ನೀನು ಕೇಳಿಕೊಂಡದ್ದನ್ನು ಅನುಗ್ರಹಿಸಿದ್ದೇನೆ; ಹೋಗಿ ಯೌವನಸ್ಥನಾದ ಅಬ್ಷಾಲೋಮನನ್ನು ಕರಕೊಂಡು ಬಾ ಅಂದನು.",
			"22": "<sup>22</sup> ಆಗ ಯೋವಾಬನು ನೆಲಕ್ಕೆ ಬಿದ್ದು ನಮಸ್ಕರಿಸಿ ಅರಸನನ್ನು ಹರಸಿ - ಅರಸನೇ, ನನ್ನ ಒಡೆಯನೇ, ನೀನು ನಿನ್ನ ಸೇವಕನಾದ ನನ್ನ ಬಿನ್ನಹವನ್ನು ಲಾಲಿಸಿದ್ದರಿಂದ ನಿನ್ನ ದೃಷ್ಟಿಯಲ್ಲಿ ನನಗೆ ದಯೆದೊರಕಿತೆಂದು ಈಗ ಗೊತ್ತಾಯಿತು ಎಂದು ಹೇಳಿ",
			"23": "<sup>23</sup> ಎದ್ದು ಗೆಷೂರಿಗೆ ಹೋಗಿ ಅಬ್ಷಾಲೋಮನನ್ನು ಯೆರೂಸಲೇಮಿಗೆ ಕರಕೊಂಡು ಬಂದನು.",
			"24": "<sup>24</sup> ಆದರೆ ಅರಸನು - ಅಬ್ಷಾಲೋಮನು ತನ್ನ ಮನೆಗೆ ಹೋಗಲಿ; ಅವನು ನನ್ನ ಮುಖವನ್ನು ನೋಡಬಾರದು ಎಂದು ಆಜ್ಞಾಪಿಸಿದ್ದರಿಂದ ಅವನು ತನ್ನ ಮನೆಗೆ ಹೋದನು; ಅರಸನ ಮುಖವನ್ನು ನೋಡಲಿಲ್ಲ.",
			"25": "<sup>25</sup> ಸೌಂದರ್ಯದಲ್ಲಿ ಅಬ್ಷಾಲೋಮನಂತೆ ಹೆಸರುಗೊಂಡ ಪುರುಷನು ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಒಬ್ಬನೂ ಇರಲಿಲ್ಲ. ಅವನಲ್ಲಿ ಅಂಗಾಲಿನಿಂದ ನಡುನೆತ್ತಿಯವರೆಗೆ ಒಂದು ದೋಷವಾದರೂ ಇರಲಿಲ್ಲ.",
			"26": "<sup>26</sup> ಅವನು ತಲೆಗೂದಲು ಬಹುಭಾರವಾಗುವದರಿಂದ ಪ್ರತಿ ವರುಷದ ಅಂತ್ಯದಲ್ಲಿ ಬೋಳಿಸಿಕೊಳ್ಳುವನು. ಆಗ ಅವನ ಕೂದಲು ಸರಕಾರೀ ತೂಕದ ಪ್ರಕಾರ ಇನ್ನೂರು ರೂಪಾಯಿ ತೂಕವಾಗುತ್ತಿತ್ತು.",
			"27": "<sup>27</sup> ಅವನಿಗೆ ಮೂರು ಮಂದಿ ಗಂಡುಮಕ್ಕಳೂ ಒಬ್ಬ ಮಗಳೂ ಇದ್ದರು. ಮಗಳ ಹೆಸರು ತಾಮಾರ್; ಈಕೆಯು ಬಹು ಸುಂದರಿಯಾಗಿದ್ದಳು.",
			"28": "<sup>28</sup> ಅಬ್ಷಾಲೋಮನು ಅರಸನ ಮೋರೆಯನ್ನು ನೋಡದೆ ಎರಡು ವರುಷ ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ವಾಸವಾಗಿದ್ದ ನಂತರ",
			"29": "<sup>29</sup> ಒಂದು ದಿವಸ ಅವನು ಅರಸನ ಬಳಿಗೆ ಕಳುಹಿಸುವದಕ್ಕಾಗಿ ಯೋವಾಬನನ್ನು ಕರೇಕಳುಹಿಸಿದನು; ಆದರೆ ಯೋವಾಬನು ಬರಲಿಲ್ಲ. ಎರಡನೆಯ ಸಾರಿ ಕರೇಕಳುಹಿಸಿದರೂ ಅವನು ಬರಲಿಲ್ಲ.",
			"30": "<sup>30</sup> ಆದದರಿಂದ ಅಬ್ಷಾಲೋಮನು ತನ್ನ ಸೇವಕರಿಗೆ - ನೋಡಿರಿ, ಸಮೀಪದಲ್ಲಿಯೇ ಯೋವಾಬನ ಜವೆಗೋದಿಹೊಲವುಂಟಲ್ಲಾ; ಹೋಗಿ ಅದಕ್ಕೆ ಬೆಂಕಿ ಹಚ್ಚಿರಿ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು; ಅವರು ಹಾಗೆಯೇ ಮಾಡಿದರು.",
			"31": "<sup>31</sup> ಆಗ ಯೋವಾಬನು ಅಬ್ಷಾಲೋಮನ ಮನೆಗೆ ಹೋಗಿ - ನಿನ್ನ ಸೇವಕರು ನನ್ನ ಹೊಲಕ್ಕೆ ಬೆಂಕಿಹಚ್ಚಿದ್ದೇಕೆ ಎಂದು ಅವನನ್ನು ಕೇಳಲು",
			"32": "<sup>32</sup> ಅಬ್ಷಾಲೋಮನು ಅವನಿಗೆ - ನಾನು ಗೆಷೂರಿನಿಂದ ಇಲ್ಲಿಗೆ ಬಂದದ್ದೇಕೆ? ಅಲ್ಲೇ ಇದ್ದರೆ ಒಳ್ಳೇದಾಗುತ್ತಿತ್ತಲ್ಲವೋ ಎಂದು ನಿನ್ನ ಮುಖಾಂತರ ಅರಸನಿಗೆ ತಿಳಿಸುವದಕ್ಕಾಗಿ ನಿನ್ನನ್ನು ಕರೇಕಳುಹಿಸಿದೆನು; ನಾನು ಹೇಗೂ ಅರಸನ ಮೋರೆಯನ್ನು ನೋಡಬೇಕು;",
			"33": "<sup>33</sup> ನಾನು ಅಪರಾಧಿಯಾಗಿದ್ದರೆ ಅವನು ನನ್ನನ್ನು ಕೊಲ್ಲಿಸಲಿ ಅಂದನು. ಯೋವಾಬನು ಹೋಗಿ ಇದನ್ನು ಅರಸನಿಗೆ ತಿಳಿಸಿದಾಗ ಅರಸನು ಅಬ್ಷಾಲೋಮನನ್ನು ಕರಿಸಿದನು. ಅವನು ಅರಸನ ಬಳಿಗೆ ಬಂದು ಸಾಷ್ಟಾಂಗನಮಸ್ಕಾರಮಾಡಿದನು. ಅರಸನು ಅವನನ್ನು ಮುದ್ದಿಟ್ಟನು."
		},
		"15": {
			"1": "<sup>1</sup> ಸ್ವಲ್ಪ ಕಾಲವಾದನಂತರ ಅಬ್ಷಾಲೋಮನು ತನಗೋಸ್ಕರ ಒಂದು ರಥವನ್ನೂ ಕುದುರೆಗಳನ್ನೂ ತೆಗೆದುಕೊಂಡು ತನ್ನ ಮುಂದೆ ಮೈಗಾವಲಾಗಿ ಓಡುವದಕ್ಕೆ ಐವತ್ತು ಮಂದಿಯನ್ನು ನೇಮಿಸಿದನು.",
			"2": "<sup>2</sup> ಅವನು ಹೊತ್ತಾರೆಯಲ್ಲೆದ್ದು ಊರುಬಾಗಲ ಬಳಿಯಲ್ಲಿ ನಿಂತುಕೊಳ್ಳುವನು. ಯಾವನಾದರೂ ತನ್ನ ವ್ಯಾಜ್ಯತೀರಿಸಿಕೊಳ್ಳುವದಕ್ಕಾಗಿ ಅರಸನ ಬಳಿಗೆ ಹೋಗುವದನ್ನು ಅಬ್ಷಾಲೋಮನು ಕಂಡರೆ ಅಂಥವನನ್ನು ತನ್ನ ಬಳಿಗೆ ಕರೆದು ನೀನು ಯಾವ ಊರಿನವನು ಎಂದು ಕೇಳುವನು. ಅವನು - ನಿನ್ನ ಸೇವಕನಾದ ನಾನು ಇಸ್ರಾಯೇಲ್ಯರ ಇಂಥ ಕುಲಕ್ಕೆ ಸೇರಿದವನು ಎಂದು ಉತ್ತರ ಕೊಡುವನು.",
			"3": "<sup>3</sup> ಆಗ ಅಬ್ಷಾಲೋಮನು - ನೋಡು, ನಿನ್ನ ಕಾರ್ಯವು ಒಳ್ಳೇದೂ ನ್ಯಾಯವಾದದ್ದೂ ಆಗಿದೆ; ಆದರೆ ವ್ಯಾಜ್ಯಗಳನ್ನು ವಿಚಾರಿಸುವದಕ್ಕೆ ಅರಸನಿಂದ ಒಬ್ಬನೂ ನೇಮಕವಾಗಿಲ್ಲ.",
			"4": "<sup>4</sup> ವ್ಯಾಜ್ಯವಾಗಲಿ ಬಿನ್ನಹವಾಗಲಿ ಉಳ್ಳವರು ಬಂದು ನ್ಯಾಯವನ್ನು ಪಡಕೊಳ್ಳುವ ಹಾಗೆ ನನ್ನನ್ನೇ ದೇಶದ ನ್ಯಾಯಾಧಿಪತಿಯನ್ನಾಗಿ ನೇಮಿಸಿದ್ದರೆ ಎಷ್ಟೋ ಒಳ್ಳೇದಾಗುತ್ತಿತ್ತು ಎಂದು ಹೇಳುವನು.",
			"5": "<sup>5</sup> ಯಾವನಾದರೂ ಅವನಿಗೆ ಸಾಷ್ಟಾಂಗನಮಸ್ಕಾರ ಮಾಡುವದಕ್ಕೆ ಬಂದರೆ ಅವನು ಕೂಡಲೆ ಕೈಚಾಚಿ ಅವನನ್ನು ಹಿಡಿದು ಮುದ್ದಿಡುವನು.",
			"6": "<sup>6</sup> ಅರಸನ ಬಳಿಗೆ ವ್ಯಾಜ್ಯಕ್ಕಾಗಿ ಬರುವ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಅಬ್ಷಾಲೋಮನು ಹೀಗೆಯೇ ಮಾಡಿ ಎಲ್ಲರ ಮನಸ್ಸುಗಳನ್ನು ತನ್ನ ಕಡೆಗೆ ತಿರುಗಿಸಿಕೊಂಡನು.",
			"7": "<sup>7</sup> ನಾಲ್ಕು ವರುಷಗಳಾದನಂತರ ಅಬ್ಷಾಲೋಮನು ಅರಸನಿಗೆ - ನಾನು ಯೆಹೋವನಿಗೆ ಹೊತ್ತ ಹರಕೆಗಳನ್ನು ಸಲ್ಲಿಸುವದಕ್ಕಾಗಿ ಹೆಬ್ರೋನಿಗೆ ಹೋಗಬೇಕಾಗಿದೆ, ಅಪ್ಪಣೆಯಾಗಲಿ.",
			"8": "<sup>8</sup> ನಿನ್ನ ಸೇವಕನಾದ ನಾನು ಅರಾಮ್‍ದೇಶದ ಗೆಷೂರಿನಲ್ಲಿದ್ದಾಗ - ಯೆಹೋವನು ನನ್ನನ್ನು ತಿರಿಗಿ ಯೆರೂಸಲೇಮಿಗೆ ಬರಮಾಡುವದಾದರೆ ಆತನಿಗೆ ಒಂದು ವಿಶೇಷವಾದ ಆರಾಧನೆ ಮಾಡಿಸುವೆನೆಂದು ಹರಕೆಮಾಡಿದ್ದೆನು ಎಂದು ವಿಜ್ಞಾಪಿಸಲು",
			"9": "<sup>9</sup> ಅರಸನು - ಹೋಗು, ನಿನಗೆ ಶುಭವಾಗಲಿ ಎಂದನು. ಅಬ್ಷಾಲೋಮನು ಹೆಬ್ರೋನಿಗೆ ಹೊರಟು ಹೋದನು.",
			"10": "<sup>10</sup> ಅವನು ಗೂಢಚಾರರನ್ನು ಕಳುಹಿಸಿ ಇಸ್ರಾಯೇಲ್ಯರ ಎಲ್ಲಾ ಕುಲದವರಿಗೆ - ನೀವು ತುತೂರಿಯ ಧ್ವನಿಯನ್ನು ಕೇಳುತ್ತಲೆ ಅಬ್ಷಾಲೋಮನು ಹೆಬ್ರೋನಿನಲ್ಲಿ ಅರಸನಾದನೆಂದು ಆರ್ಭಟಿಸಿರಿ ಎಂದು ಹೇಳಿಸಿದ್ದನು.",
			"11": "<sup>11</sup> ಅಬ್ಷಾಲೋಮನು ಯೆರೂಸಲೇಮಿನಿಂದ ಸಮಾರಾಧನೆಗೆಂದು ಇನ್ನೂರು ಜನರನ್ನು ಕರಕೊಂಡು ಹೋಗಿದ್ದನು. ಅವರು ಯಥಾರ್ಥ ಮನಸ್ಸಿನಿಂದ ಹೋದವರು; ಅವರಿಗೇನೂ ಗೊತ್ತಿರಲಿಲ್ಲ.",
			"12": "<sup>12</sup> ಇದಲ್ಲದೆ ಅವನು ಸಮಾಧಾನಯಜ್ಞವನ್ನು ಮಾಡುತ್ತಿರುವಾಗ ದಾವೀದನ ಮಂತ್ರಿಯಾಗಿದ್ದ ಗೀಲೋವಿನ ಅಹೀತೋಫೆಲನೆಂಬವನನ್ನು ಅವನ ಊರಾದ ಗೀಲೋವಿನಿಂದ ಕರೇಕಳುಹಿಸಿದನು. ಜನರು ಅಬ್ಷಾಲೋಮನನ್ನು ಕೂಡಿಕೊಳ್ಳುತ್ತಾ ಬಂದದರಿಂದ ಒಳಸಂಚು ಬಲವಾಗುತ್ತಾ ಹೋಯಿತು.",
			"13": "<sup>13</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಮನಸ್ಸು ಅಬ್ಷಾಲೋಮನ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡಿತೆಂಬ ವರ್ತಮಾನವು ದಾವೀದನಿಗೆ ಮುಟ್ಟಿದಾಗ",
			"14": "<sup>14</sup> ಅವನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿದ್ದ ತನ್ನ ಸೇವಕರಿಗೆ - ಏಳಿರಿ, ಓಡಿ ಹೋಗೋಣ; ಇಲ್ಲೇ ಇದ್ದರೆ ನಾವು ಅಬ್ಷಾಲೋಮನ ಕೈಯಿಂದ ತಪ್ಪಿಸಿಕೊಳ್ಳಲಾರೆವು. ಬೇಗ ಹೊರಡೋಣ; ಅವನು ಅಕಸ್ಮಾತ್ತಾಗಿ ನಮ್ಮ ಮೇಲೆ ಬಿದ್ದು ನಮಗೆ ದುರ್ಗತಿಯನ್ನುಂಟುಮಾಡಿ ಪಟ್ಟಣದವರನ್ನೆಲ್ಲಾ ಕತ್ತಿಯಿಂದ ಸಂಹರಿಸಾನು ಎಂದು ಹೇಳಿದನು.",
			"15": "<sup>15</sup> ಸೇವಕರು ಅರಸನಿಗೆ - ನಮ್ಮ ಒಡೆಯನಾದ ಅರಸನಿಗೆ ಸರಿತೋಚಿದ್ದನ್ನು ಮಾಡಸಿದ್ಧರಾಗಿದ್ದೇವೆ ಎಂದು ಉತ್ತರಕೊಟ್ಟರು.",
			"16": "<sup>16</sup> ಆಗ ಅರಸನು ಮನೆಕಾಯುವದಕ್ಕಾಗಿ ಹತ್ತು ಮಂದಿ ಉಪ ಪತ್ನಿಯರನ್ನು ಬಿಟ್ಟು ಉಳಿದವರನ್ನೆಲ್ಲಾ ಕರಕೊಂಡು ಹೊರಟನು.",
			"17": "<sup>17</sup> ಅರಸನೂ ಅವನ ಜೊತೆಯಲ್ಲಿ ಹೋದ ಜನರೆಲ್ಲರೂ ಪಟ್ಟಣದ ಕಡೇ ಮನೆಯ ಬಳಿಯಲ್ಲಿ ತುಸು ಹೊತ್ತು ನಿಂತರು.",
			"18": "<sup>18</sup> ದಾವೀದನ ಎಲ್ಲಾ ಸೇವಕರೂ ಕೆರೇತ್ಯ, ಪೆಲೇತ್ಯ ಎಂಬ ಕಾವಲುದಂಡುಗಳೂ ಗತ್ಊರಿನಿಂದ ಅರಸನ ಜೊತೆಯಲ್ಲಿ ಬಂದಿದ್ದ ಆರುನೂರು ಮಂದಿ ಗಿತ್ತೀಯರೂ ಅರಸನ ಮುಂದೆ ಹೋದರು.",
			"19": "<sup>19</sup> ಆಗ ಅರಸನು ಗಿತ್ತೀಯನಾದ ಇತ್ತೈ ಎಂಬವನಿಗೆ - ನೀನೂ ನಮ್ಮ ಸಂಗಡ ಯಾಕೆ ಬರಬೇಕು? ನೀನು ಸ್ವದೇಶವನ್ನು ಬಿಟ್ಟು ನನ್ನ ಆಶ್ರಯಕ್ಕೆ ಬಂದವನಲ್ಲವೇ. ಹಿಂದಿರುಗಿ ಹೋಗಿ ಅರಸನ ಬಳಿಯಲ್ಲಿ ವಾಸಮಾಡು.",
			"20": "<sup>20</sup> ನಾನು ಎಲ್ಲೆಲ್ಲಿಯೋ ಅಲೆಯುತ್ತಿರಬೇಕಾಗುವದು; ಹೀಗಿರುವದರಿಂದ ನಿನ್ನೆ ಬಂದಂಥ ನಿನ್ನನ್ನು ಕರಕೊಂಡು ಹೋಗಿ ಸುಮ್ಮನೆ ಯಾಕೆ ತಿರುಗಾಡಲಿಕ್ಕೆ ಹಚ್ಚಬೇಕು? ನಿನ್ನ ಸಹೋದರರನ್ನು ಕರಕೊಂಡು ಹಿಂದಿರುಗಿಹೋಗು; ಕೃಪಾಸತ್ಯತೆಗಳು ನಿನ್ನ ಸಂಗಡ ಇರಲಿ ಎಂದು ಹೇಳಿದನು.",
			"21": "<sup>21</sup> ಅದಕ್ಕೆ ಇತ್ತೈ - ಯೆಹೋವನಾಣೆ, ನನ್ನ ಒಡೆಯನಾದ ಅರಸನ ಜೀವದಾಣೆ, ಜೀವಹೋದರೂ ಉಳಿದರೂ ನನ್ನ ಒಡೆಯನಾದ ಅರಸನಿರುವಲ್ಲಿಗೆ ಹೋಗುವೆನು ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"22": "<sup>22</sup> ಆಗ ದಾವೀದನು ಅವನಿಗೆ - ಒಳ್ಳೇದು, ಮುಂದೆ ನಡೆ ಅಂದನು. ಗಿತ್ತೀಯನಾದ ಇತ್ತೈಯು ತನ್ನ ಎಲ್ಲಾ ಸೈನಿಕರನ್ನೂ ಪರಿಜನವನ್ನೂ ಕರಕೊಂಡು ಮುಂದೆ ನಡೆದನು.",
			"23": "<sup>23</sup> ಇವರೆಲ್ಲರೂ ಮುಂದೆ ನಡೆಯುವಾಗ ಸುತ್ತಣವರೆಲ್ಲರೂ ಬಹಳವಾಗಿ ಅತ್ತರು. ಅರಸನೂ ಎಲ್ಲಾ ಜನರೂ ಕಿದ್ರೋನ್‍ಹಳ್ಳವನ್ನು ದಾಟಿ ಅರಣ್ಯಮಾರ್ಗ ಹಿಡಿದರು.",
			"24": "<sup>24</sup> ಚಾದೋಕನೂ ಅವನ ವಶದಲ್ಲಿದ್ದ ಎಲ್ಲಾ ಲೇವಿಯರೂ ದೇವರ ಒಡಂಬಡಿಕೆಯ ಮಂಜೂಷವನ್ನು ಹೊತ್ತುಕೊಂಡು ಬಂದು ಅದನ್ನು ಜನರೆಲ್ಲರೂ ದಾಟಿಹೋಗುವವರೆಗೆ ಕೆಳಗಿಳಿಸಿದ್ದರು. ಎಬ್ಯಾತಾರನೂ ಬಂದಿದ್ದನು.",
			"25": "<sup>25</sup> ಅರಸನು ಚಾದೋಕನಿಗೆ - ನೀನು ದೇವರ ಒಡಂಬಡಿಕೆಯ ಮಂಜೂಷವನ್ನು ಹಿಂದಿರುಗಿ ತೆಗೆದುಕೊಂಡು ಹೋಗು; ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ನನಗೆ ದಯೆದೊರಕಿದರೆ ನಾನು ಆತನನ್ನೂ ಆತನ ಆಲಯವನ್ನೂ ನೋಡುವ ಹಾಗೆ ತಾನೇ ನನ್ನನ್ನು ಹಿಂದಕ್ಕೆ ಬರಮಾಡುವನು.",
			"26": "<sup>26</sup> ಆತನು - ನಿನ್ನಲ್ಲಿ ನನಗೆ ಇಷ್ಟವಿಲ್ಲವೆಂದು ತೋರಿಸಿದರೆ ತನಗೆ ಸರಿಕಂಡಂತೆ ಮಾಡಲಿ, ಆತನ ಚಿತ್ತ ಅಂದನು.",
			"27": "<sup>27</sup> ಇದಲ್ಲದೆ ಅವನು ಯಾಜಕನಾದ ಚಾದೋಕನಿಗೆ - ನೀನು ದೇವದರ್ಶಿ; ನೀನು ನಿನ್ನ ಮಗನಾದ ಅಹೀಮಾಚನು, ಎಬ್ಯಾತಾರನ ಮಗನಾದ ಯೋನಾತಾನನು ಎಂಬ ಈ ಇಬ್ಬರು ಹುಡುಗರನ್ನು ಕರಕೊಂಡು ಸುರಕ್ಷಿತವಾಗಿ ಪಟ್ಟಣಕ್ಕೆ ಹೋಗಬೇಕು.",
			"28": "<sup>28</sup> ನಿಮ್ಮಿಂದ ವರ್ತಮಾನಬರುವ ತನಕ ನಾನು ಅಡವಿಯಲ್ಲಿ ಹೊಳೆದಾಟುವ ಸ್ಥಳದ ಹತ್ತಿರ ಇರುವೆನು ಎಂದು ಹೇಳಿದನು.",
			"29": "<sup>29</sup> ಆಗ ಚಾದೋಕನೂ ಎಬ್ಯಾತಾರನೂ ದೇವರ ಮಂಜೂಷವನ್ನು ಯೆರೂಸಲೇಮಿಗೆ ಒಯ್ದು ಅಲ್ಲೇ ವಾಸಿಸಿದರು.",
			"30": "<sup>30</sup> ದಾವೀದನು ಮೋರೆಯನ್ನು ಮುಚ್ಚಿಕೊಂಡು ಅಳುತ್ತಾ ಬರಿಗಾಲಿನಿಂದ ಎಣ್ಣೇಮರಗಳ ಗುಡ್ಡವನ್ನೇರಿದನು. ಅವನ ಜೊತೆಯಲ್ಲಿದ್ದ ಜನರೂ ಮೋರೆಮುಚ್ಚಿಕೊಂಡು ಅಳುತ್ತಾ ಏರಿದರು.",
			"31": "<sup>31</sup> ಅಬ್ಷಾಲೋಮನ ಸಂಗಡ ಒಳಸಂಚುಮಾಡಿದವರಲ್ಲಿ ಅಹೀತೋಫೆಲನೂ ಇದ್ದಾನೆಂಬ ವರ್ತಮಾನವು ದಾವೀದನಿಗೆ ಮುಟ್ಟಿದಾಗ ಅವನು - ಯೆಹೋವನೇ, ಅಹೀತೋಫೆಲನ ಆಲೋಚನೆಗಳನ್ನು ನಿರರ್ಥಕಪಡಿಸು ಎಂದು ಪ್ರಾರ್ಥಿಸಿದನು.",
			"32": "<sup>32</sup> ಅವನು ಗುಡ್ಡದ ತುದಿಯಲ್ಲಿ ದೇವಾರಾಧನೆ ನಡೆಯುವ ಸ್ಥಳಕ್ಕೆ ಬಂದಾಗ ಅರ್ಕೀಯನಾದ ಹೂಷೈ ಎಂಬವನು ಅಂಗಿಯನ್ನು ಹರಕೊಂಡು ತಲೆಯ ಮೇಲೆ ಮಣ್ಣುಹಾಕಿಕೊಂಡು ಅರಸನ ಬಳಿಗೆ ಬಂದನು.",
			"33": "<sup>33</sup> ಅರಸನು ಅವನಿಗೆ - ನೀನು ನನ್ನ ಸಂಗಡ ಬರುವದಾದರೆ ನನಗೆ ಭಾರವಾಗುವಿಯಷ್ಟೆ.",
			"34": "<sup>34</sup> ಆದರೆ ನೀನು ಹಿಂದಿರುಗಿ ಪಟ್ಟಣಕ್ಕೆ ಹೋಗಿ ಅಬ್ಷಾಲೋಮನಿಗೆ - ಅರಸನೇ, ನಾನು ನಿನ್ನ ಸೇವಕನು; ಮುಂಚೆ ನಿನ್ನ ತಂದೆಯನ್ನು ಸೇವಿಸಿದಂತೆ ಈಗ ನಿನ್ನನ್ನು ಸೇವಿಸುತ್ತೇನೆ ಎಂದು ಹೇಳುವದಾದರೆ ನನಗೋಸ್ಕರ ಅಹೀತೋಫೆಲನ ಆಲೋಚನೆಯನ್ನು ವ್ಯರ್ಥಮಾಡುವದಕ್ಕೆ ನಿನಗೆ ಅನುಕೂಲವಾಗುವದು.",
			"35": "<sup>35</sup> ಅಲ್ಲಿ ನಿನ್ನ ಸಂಗಡ ಯಾಜಕರಾದ ಚಾದೋಕ್ ಎಬ್ಯಾತಾರರು ಇರುತ್ತಾರಲ್ಲಾ; ಅರಮನೆಯಲ್ಲಿ ನಿನಗೆ ಗೊತ್ತಾಗುವ ವರ್ತಮಾನವನ್ನೆಲ್ಲಾ ಆ ಇಬ್ಬರು ಯಾಜಕರಿಗೆ ತಿಳಿಸು.",
			"36": "<sup>36</sup> ಅವರ ಬಳಿಯಲ್ಲಿ ಇಬ್ಬರು ಹುಡುಗರಿದ್ದಾರೆ; ಒಬ್ಬನು ಚಾದೋಕನ ಮಗನಾದ ಅಹೀಮಾಚನು; ಇನ್ನೊಬ್ಬನು ಎಬ್ಯಾತಾರನ ಮಗನಾದ ಯೋನಾತಾನನು. ಇವರ ಮುಖಾಂತರವಾಗಿ ನೀನು ಎಲ್ಲಾ ವರ್ತಮಾನವನ್ನು ನನಗೆ ಮುಟ್ಟಿಸಬಹುದು ಎಂದು ಹೇಳಿ ಅವನನ್ನು ಕಳುಹಿಸಿದನು.",
			"37": "<sup>37</sup> ಅಬ್ಷಾಲೋಮನು ಯೆರೂಸಲೇಮನ್ನು ಪ್ರವೇಶಿಸುವಷ್ಟರಲ್ಲೇ ದಾವೀದನ ಸ್ನೇಹಿತನಾದ ಹೂಷೈಯು ಪಟ್ಟಣಕ್ಕೆ ಬಂದನು."
		},
		"16": {
			"1": "<sup>1</sup> ದಾವೀದನು ಗುಡ್ಡದ ತುದಿಯ ಆಚೆಗೆ ಹೋದ ಕೂಡಲೆ ಮೆಫೀಬೋಶೆತನ ಸೇವಕನಾದ ಚೀಬನು ತಡಿ ಹಾಕಿದ ಎರಡು ಕತ್ತೆಗಳ ಮೇಲೆ ಇನ್ನೂರು ರೊಟ್ಟಿಗಳನ್ನೂ ನೂರು ಒಣಗಿದ ದ್ರಾಕ್ಷೇ ಗೊಂಚಲುಗಳನ್ನೂ ನೂರು ಹಣ್ಣುಗಳನ್ನೂ ಒಂದು ಬುದ್ದಲಿ ದ್ರಾಕ್ಷಾರಸವನ್ನೂ ಹೇರಿಕೊಂಡು ಬಂದು ಅವನನ್ನು ಎದುರುಗೊಂಡನು.",
			"2": "<sup>2</sup> ಅರಸನು ಚೀಬನನ್ನು - ಇವುಗಳನ್ನು ಯಾಕೆ ತಂದಿ ಎಂದು ಕೇಳಲು ಅವನು - ಕತ್ತೆಗಳನ್ನು ಅರಸನ ಮನೆಯವರು ಸವಾರಿಮಾಡುವದಕ್ಕಾಗಿಯೂ ಹಣ್ಣು ರೊಟ್ಟಿಗಳನ್ನು ಆಳುಗಳು ತಿನ್ನುವದಕ್ಕಾಗಿಯೂ ದ್ರಾಕ್ಷಾರಸವನ್ನು ಅರಣ್ಯದಲ್ಲಿ ದಣಿದವರು ಕುಡಿಯುವದಕ್ಕಾಗಿಯೂ ತಂದಿದ್ದೇನೆ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"3": "<sup>3</sup> ಅರಸನು ತಿರಿಗಿ ಅವನನ್ನು - ನಿನ್ನ ಯಜಮಾನನ ಮಗನು ಎಲ್ಲಿದ್ದಾನೆಂದು ಕೇಳಲು ಅವನು - ತನ್ನ ತಂದೆಯ ರಾಜ್ಯವನ್ನು ಇಸ್ರಾಯೇಲ್ಯರು ಈಹೊತ್ತು ತನಗೆ ತಿರಿಗಿ ಕೊಡುವರೆಂದು ಹೇಳಿ ಅವನು ಯೆರೂಸಲೇಮಿನಲ್ಲೇ ಉಳುಕೊಂಡನು ಅಂದನು.",
			"4": "<sup>4</sup> ಆಗ ಅರಸನು ಅವನಿಗೆ - ಮೆಫೀಬೋಶೆತನ ಆಸ್ತಿಯೆಲ್ಲಾ ನಿನ್ನದೇ ಎಂದು ಹೇಳಲು ಅವನು - ಅರಸನೇ, ನಿನಗೆ ಸಾಷ್ಟಾಂಗ ನಮಸ್ಕಾರಮಾಡುತ್ತೇನೆ; ನನ್ನ ಒಡೆಯನೇ, ನಿನ್ನ ದೃಷ್ಟಿಯಲ್ಲಿ ನನಗೆ ದಯೆ ದೊರಕಲಿ ಎಂದನು.",
			"5": "<sup>5</sup> ಅರಸನಾದ ದಾವೀದನು ಬಹುರೀಮಿಗೆ ಬಂದಾಗ ಸೌಲನ ವಂಶದವನೂ ಗೇರನ ಮಗನೂ ಆದ ಶಿಮ್ಮೀ ಎಂಬವನು ದಾವೀದನನ್ನು ಶಪಿಸುತ್ತಾ ಆ ಊರಿನಿಂದ ಹೊರಗೆ ಬಂದನು.",
			"6": "<sup>6</sup> ಅವನು ದಾವೀದನಿಗೂ ಅವನ ಎಲ್ಲಾ ಸೇವಕರಿಗೂ ಎಡಬಲದಲ್ಲಿರುವ ಸೈನಿಕರಿಗೂ ಶೂರರಿಗೂ ಕಲ್ಲೆಸೆಯ ತೊಡಗಿದನು.",
			"7": "<sup>7</sup> ಅವನು ಶಪಿಸುತ್ತಾ -",
			"8": "<sup>8</sup> ನಡೆ, ಕೊಲೆಗಾರನೇ, ನೀಚನೇ, ನಡೆ; ಸೌಲನ ರಾಜ್ಯವನ್ನು ಕಸಕೊಂಡು ಅವನ ಮನೆಯವರನ್ನು ಕೊಂದದ್ದಕ್ಕಾಗಿ ಯೆಹೋವನು ನಿನಗೆ ಮುಯ್ಯಿ ತೀರಿಸಿದ್ದಾನೆ; ಆತನು ರಾಜ್ಯವನ್ನು ನಿನ್ನ ಮಗನಾದ ಅಬ್ಷಾಲೋಮನಿಗೆ ಕೊಟ್ಟು ಬಿಟ್ಟನು. ಕೊಲೆಗಾರನೇ, ಇಗೋ, ನಿನಗೆ ಈಗ ತಕ್ಕ ಆಪತ್ತು ಬಂದಿದೆ ಅಂದನು.",
			"9": "<sup>9</sup> ಆಗ ಚೆರೂಯಳ ಮಗನಾದ ಅಬೀಷೈಯು ಅರಸನಿಗೆ - ಈ ಸತ್ತ ನಾಯಿ ಅರಸನಾದ ನನ್ನ ಒಡೆಯನನ್ನು ಶಪಿಸುವದೇನು? ಅಪ್ಪಣೆಯಾಗಲಿ, ನಾನು ಅವನಿರುವಲ್ಲಿಗೆ ಹೋಗಿ ಅವನ ತಲೆ ಹಾರಿಸಿಕೊಂಡು ಬರುವೆನು ಅಂದನು.",
			"10": "<sup>10</sup> ಅದಕ್ಕೆ ಅರಸನು - ಚೆರೂಯಳ ಮಕ್ಕಳೇ, ನನಗೆ ನಿಮ್ಮ ಗೊಡವೆಯೇ ಬೇಡ; ಬಿಡಿರಿ, ಅವನು ಶಪಿಸಲಿ; ದಾವೀದನನ್ನು ಶಪಿಸೆಂದು ಯೆಹೋವನೇ ಅವನಿಗೆ ಆಜ್ಞಾಪಿಸಿದ ಮೇಲೆ ನೀನು ಹೀಗೇಕೆ ಮಾಡಿದಿ ಎಂದು ಅವನನ್ನು ಯಾರು ಕೇಳಬಹುದು ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"11": "<sup>11</sup> ಇದಲ್ಲದೆ ಅವನು ಅಬೀಷೈಗೂ ತನ್ನ ಎಲ್ಲಾ ಸೇವಕರಿಗೂ - ನೋಡಿರಿ, ನನ್ನಿಂದ ಹುಟ್ಟಿದ ನನ್ನ ಮಗನೇ ನನ್ನ ಪ್ರಾಣವನ್ನು ತೆಗೆಯಬೇಕೆಂದಿರುವಲ್ಲಿ ಈ ಬೆನ್ಯಾಮೀನ್ಯನು ಹೀಗೆ ಮಾಡುವದು ಯಾವ ದೊಡ್ಡ ಮಾತು! ಬಿಡಿರಿ, ಅವನು ಶಪಿಸಲಿ; ಹೀಗೆ ಮಾಡಬೇಕೆಂದು ಯೆಹೋವನೇ ಅವನಿಗೆ ಆಜ್ಞಾಪಿಸಿದ್ದಾನೆ.",
			"12": "<sup>12</sup> ಒಂದು ವೇಳೆ ಯೆಹೋವನು ನನ್ನ ಕಷ್ಟವನ್ನು ನೋಡಿ ಈಹೊತ್ತಿನ ಶಾಪಕ್ಕೆ ಬದಲಾಗಿ ನನಗೆ ಶುಭವನ್ನು ಅನುಗ್ರಹಿಸಾನು ಎಂದು ಹೇಳಿದನು.",
			"13": "<sup>13</sup> ದಾವೀದನೂ ಅವನ ಜನರೂ ದಾರಿಹಿಡಿದು ಹೋಗುವಾಗ ಶಿಮ್ಮಿಯು ಶಪಿಸುತ್ತಾ ಕಲ್ಲೆಸೆಯುತ್ತಾ ಧೂಳೆರಚುತ್ತಾ ಗುಡ್ಡದ ಓರೆಯಲ್ಲಿ ಸಮಾನಾಂತರದಲ್ಲೇ ನಡೆಯುತ್ತಿದ್ದನು.",
			"14": "<sup>14</sup> ಅರಸನೂ ಅವನ ಜೊತೆಯಲ್ಲಿ ಬಂದವರೆಲ್ಲರೂ ದಣಿದವರಾಗಿ ಹೋಗಬೇಕಾದ ಸ್ಥಳವನ್ನು ಸೇರಿ ಅಲ್ಲಿ ವಿಶ್ರಮಿಸಿಕೊಂಡರು.",
			"15": "<sup>15</sup> ಅಷ್ಟರಲ್ಲಿ ಅಬ್ಷಾಲೋಮನು ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರೊಡನೆ ಯೆರೂಸಲೇಮಿಗೆ ಬಂದನು. ಅಹೀತೋಫೆಲನೂ ಅವನ ಸಂಗಡ ಇದ್ದನು.",
			"16": "<sup>16</sup> ಅರ್ಕೀಯನೂ ದಾವೀದನ ಸ್ನೇಹಿತನೂ ಆಗಿದ್ದ ಹೂಷೈಯು ಅಬ್ಷಾಲೋಮನ ಬಳಿಗೆ ಬಂದು - ಅರಸನು ಚಿರಂಜೀವಿಯಾಗಿರಲಿ, ಅರಸನು ಚಿರಂಜೀವಿಯಾಗಿರಲಿ ಎಂದು ಕೂಗಿದನು.",
			"17": "<sup>17</sup> ಅಬ್ಷಾಲೋಮನು ಅವನಿಗೆ - ಸ್ನೇಹಿತನ ಮೇಲೆ ನಿನಗಿದ್ದ ಪ್ರೀತಿ ಇಷ್ಟೇಯೇನು? ನೀನು ನಿನ್ನ ಸ್ನೇಹಿತನೊಡನೆ ಯಾಕೆ ಹೋಗಲಿಲ್ಲ ಅಂದನು.",
			"18": "<sup>18</sup> ಅದಕ್ಕೆ ಹೂಷೈಯು - ಹಾಗಲ್ಲ, ಯೆಹೋವನೂ ಈ ಜನರೂ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರೂ ಯಾವನನ್ನು ಆರಿಸಿದ್ದಾರೋ ಅವನ ಪಕ್ಷದವನಾಗಿರುವೆನು; ಅವನ ಬಳಿಯಲ್ಲಿ ವಾಸಿಸುವೆನು.",
			"19": "<sup>19</sup> ಇದಲ್ಲದೆ ನಾನು ಈಗ ಸೇವಿಸಬೇಕೆಂದಿರುವದು ರಾಜಪುತ್ರನ ಸನ್ನಿಧಿಯಲ್ಲಿಯಲ್ಲವೇ. ನಿನ್ನ ತಂದೆಯ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸೇವಿಸಿದಂತೆ ನಿನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿಯೂ ಸೇವಿಸುವೆನು ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"20": "<sup>20</sup> ಅನಂತರ ಅಬ್ಷಾಲೋಮನು - ನಾವು ಈಗ ಮಾಡತಕ್ಕದ್ದೇನು? ಆಲೋಚನೆ ಹೇಳು ಎಂಬದಾಗಿ ಅಹೀತೋಫೆಲನನ್ನು ಕೇಳಲು ಅವನು -",
			"21": "<sup>21</sup> ಹೋಗಿ ನಿನ್ನ ತಂದೆಯು ಮನೆಕಾಯುವದಕ್ಕೆ ಬಿಟ್ಟಿರುವ ಅವನ ಉಪಪತ್ನಿಗಳೊಡನೆ ಸಂಗಮಿಸು. ಹೀಗೆ ಮಾಡುವದಾದರೆ ನೀನು ನಿನ್ನ ತಂದೆಗೆ ಅಸಹ್ಯವಾದಿಯೆಂದು ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ತಿಳಿಯುವದರಿಂದ ನಿನ್ನ ಪಕ್ಷದವರು ಬಲಗೊಳ್ಳುವರು ಎಂದು ಉತ್ತರ ಕೊಟ್ಟನು.",
			"22": "<sup>22</sup> ಆಗ ಅಬ್ಷಾಲೋಮನಿಗೋಸ್ಕರ ಮಾಳಿಗೆಯ ಮೇಲೆ ಗುಡಾರಹಾಕಿದರು. ಅವನು ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರ ಮುಂದೆಯೇ ತನ್ನ ತಂದೆಯ ಉಪಪತ್ನಿಗಳನ್ನು ಕೂಡಿದನು.",
			"23": "<sup>23</sup> ಆ ಕಾಲದಲ್ಲಿ ಅಹೀತೋಫೆಲನ ಆಲೋಚನೆಗಳಿಗೆ ದೈವೋತ್ತರಗಳಿಗಿರುವಷ್ಟು ಬೆಲೆಯಿತ್ತು. ದಾವೀದನೂ ಅಬ್ಷಾಲೋಮನೂ ಅವನ ಆಲೋಚನೆಗಳನ್ನು ಗೌರವಿಸುತ್ತಿದ್ದರು."
		},
		"17": {
			"1": "<sup>1</sup> ತರುವಾಯ ಅಹೀತೋಫೆಲನು ಅಬ್ಷಾಲೋಮನಿಗೆ - ಅಪ್ಪಣೆಯಾಗಲಿ, ನಾನು ಹನ್ನೆರಡು ಸಾವಿರ ಜನರನ್ನು ಆರಿಸಿಕೊಂಡು ಈ ರಾತ್ರಿಯೇ ದಾವೀದನನ್ನು ಹಿಂದಟ್ಟುವೆನು;",
			"2": "<sup>2</sup> ಅವನು ದಣಿದವನೂ ಧೈರ್ಯಗುಂದಿದವನೂ ಆಗಿರುವಾಗಲೇ ಫಕ್ಕನೆ ಅವನ ಮೇಲೆ ಬಿದ್ದು ಅವನನ್ನು ಬೆದರಿಸುವೆನು, ಅವನ ಜನರೆಲ್ಲರೂ ಓಡಿಹೋಗುವರು.",
			"3": "<sup>3</sup> ಅರಸನೊಬ್ಬನನ್ನೇ ಕೊಂದು ಎಲ್ಲಾ ಜನರನ್ನು ತಿರಿಗಿ ನಿನ್ನ ಬಳಿಗೆ ಬರಮಾಡುವೆನು. ನಿನ್ನ ಅಪೇಕ್ಷೆಯಂತೆ ಎಲ್ಲಾ ಜನರೂ ಹಿಂದಿರುಗಿ ಬಂದ ಮೇಲೆ ದೇಶದಲ್ಲೆಲ್ಲಾ ಸಮಾಧಾನವುಂಟಾಗುವದು ಎಂದು ಹೇಳಿದನು.",
			"4": "<sup>4</sup> ಈ ಮಾತು ಅಬ್ಷಾಲೋಮನಿಗೂ ಇಸ್ರಾಯೇಲ್ಯರ ಎಲ್ಲಾ ಹಿರಿಯರಿಗೂ ಸರಿಯಾಗಿ ಕಂಡಿತು.",
			"5": "<sup>5</sup> ಆಮೇಲೆ ಅಬ್ಷಾಲೋಮನು - ಅರ್ಕೀಯನಾದ ಹೂಷೈಯನ್ನು ಕರೆದು ಅವನ ಅಭಿಪ್ರಾಯವನ್ನು ಕೇಳೋಣ ಎಂದು ಹೇಳಿ",
			"6": "<sup>6</sup> ಅವನು ತನ್ನ ಬಳಿಗೆ ಬಂದಾಗ ಅವನನ್ನು - ಅಹೀತೋಫೆಲನು ಹೀಗೆ ಹೀಗೆ ಹೇಳಿದ್ದಾನೆ; ಇದರಂತೆ ಮಾಡಿದರೆ ಒಳ್ಳೇದಾಗುವದೋ? ಇಲ್ಲವಾದರೆ ನಿನ್ನ ಅಭಿಪ್ರಾಯವೇನು ಎಂದು ಕೇಳಲು",
			"7": "<sup>7</sup> ಹೂಷೈಯು ಅಬ್ಷಾಲೋಮನಿಗೆ - ಈ ಸಾರಿ ಅಹೀತೋಫೆಲನು ಹೇಳಿದ ಅಲೋಚನೆ ಒಳ್ಳೇದಲ್ಲ;",
			"8": "<sup>8</sup> ನಿನ್ನ ತಂದೆಯೂ ಅವನ ಜನರೂ ಶೂರರಾಗಿದ್ದಾರೆಂದೂ ಈಗ ಅವರು ಮರಿಯನ್ನು ಕಳಕೊಂಡ ಕರಡಿಯಂತೆ ರೋಷವುಳ್ಳವರಾಗಿದ್ದಾರೆಂದೂ ನಿನಗೆ ಗೊತ್ತುಂಟಲ್ಲಾ; ಇದಲ್ಲದೆ ಅವನು ಯುದ್ಧನಿಪುಣನು; ಜನರ ಮಧ್ಯದಲ್ಲಿ ರಾತ್ರಿ ಕಳೆಯುವದಿಲ್ಲ.",
			"9": "<sup>9</sup> ಅವನು ಈಗ ಒಂದು ಗುಹೆಯಲ್ಲಾಗಲಿ ಬೇರೆ ಯಾವದಾದರೊಂದು ಸ್ಥಳದಲ್ಲಾಗಲಿ ಅಡಗಿಕೊಂಡಿರುವನು. ಮೊದಲು ನಮ್ಮವರಲ್ಲೇ ಕೆಲವರು ಮಡಿದರೆ ಜನರು ಇದನ್ನು ಕೇಳಿ ಅಬ್ಷಾಲೋಮನ ಪಕ್ಷದವರಿಗೆ ಮಹಾಪಜಯವಾಯಿತೆಂದು ಸುದ್ದಿ ಹಬ್ಬಿಸುವರು.",
			"10": "<sup>10</sup> ಆಗ ಸಿಂಹಹೃದಯರಾದ ಶೂರರ ಎದೆಯೂ ಕರಗಿ ನೀರಾಗುವದು. ನಿನ್ನ ತಂದೆಯು ರಣವೀರನೆಂದೂ ಅವನ ಸಂಗಡ ಇದ್ದವರು ಪರಾಕ್ರಮಶಾಲಿಗಳೆಂದೂ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರು ಬಲ್ಲರಷ್ಟೆ.",
			"11": "<sup>11</sup> ಹೀಗಿರುವದರಿಂದ ನನ್ನ ಆಲೋಚನೆಯನ್ನು ಕೇಳು - ದಾನಿನಿಂದ ಬೇರ್ಷೆಬದವರೆಗೆ ವಾಸವಾಗಿರುವ ಇಸ್ರಾಯೇಲ್ಯರೊಳಗಿಂದ ಸಮುದ್ರದ ಮರಳಿನಷ್ಟು ಅಸಂಖ್ಯವಾದ ಸೈನ್ಯವನ್ನು ಕೂಡಿಸಿ ನೀನೂ ಅವರ ಜೊತೆಯಲ್ಲಿ ಯುದ್ಧಕ್ಕೆ ಹೋಗು.",
			"12": "<sup>12</sup> ನಾವು ಅವನಿರುವ ಸ್ಥಳವನ್ನು ಗೊತ್ತುಮಾಡಿಕೊಂಡು ಹೋಗಿ ಇಬ್ಬನಿಯು ನೆಲದ ಮೇಲೆ ಹೇಗೋ ಹಾಗೆಯೇ ನಾವು ಅವರ ಮೇಲೆ ಬೀಳೋಣ. ಆಗ ಅವನೂ ಅವನ ಜನರೂ ನಮ್ಮ ಕೈಗೆ ಸಿಕ್ಕುವರು; ಒಬ್ಬನೂ ತಪ್ಪಿಸಿಕೊಳ್ಳಲಾರನು.",
			"13": "<sup>13</sup> ಅವನು ಒಂದು ಪಟ್ಟಣವನ್ನು ಹೊಕ್ಕಿರುವದಾದರೆ ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರೂ ಹಗ್ಗಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಅಲ್ಲಿಗೆ ಬರಲಿ; ಆಗ ಆ ಊರನ್ನು ಒಂದು ಹರಳಾದರೂ ಉಳಿಯದಂತೆ ಹಗ್ಗಗಳಿಂದ ಎಳೆದುಕೊಂಡು ಹೋಗಿ ಹೊಳೆಯಲ್ಲಿ ಹಾಕಿ ಬಿಡೋಣ ಅಂದನು.",
			"14": "<sup>14</sup> ಇದನ್ನು ಕೇಳಿ ಅಬ್ಷಾಲೋಮನೂ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರೂ ಅರ್ಕೀಯನಾದ ಹೂಷೈಯ ಆಲೋಚನೆಯು ಅಹೀತೋಫೆಲನ ಆಲೋಚನೆಗಿಂತ ಒಳ್ಳೇದಾಗಿದೆ ಎಂದರು. ಯೆಹೋವನು ಅಬ್ಷಾಲೋಮನಿಗೆ ಕೇಡನ್ನುಂಟು ಮಾಡಬೇಕೆಂದು ಅಹೀತೋಫೆಲನ ಆಲೋಚನೆಯನ್ನು ವ್ಯರ್ಥಮಾಡಿದನು.",
			"15": "<sup>15</sup> ತರುವಾಯ ಹೂಷೈಯು ಯಾಜಕರಾದ ಚಾದೋಕ ಎಬ್ಯಾತಾರರಿಗೆ - ಅಹೀತೋಫೆಲನು ಅಬ್ಷಾಲೋಮನಿಗೂ ಇಸ್ರಾಯೇಲ್ಯರ ಹಿರಿಯರಿಗೂ ಇಂಥಿಂಥ ಆಲೋಚನೆಯನ್ನು ಹೇಳಿದನು; ನಾನು ಹೀಗೆ ಹೀಗೆ ಹೇಳಿದೆನು.",
			"16": "<sup>16</sup> ಆದದರಿಂದ ನೀವು ಬೇಗನೆ ಅರಸನಾದ ದಾವೀದನಿಗೆ - ನೀನು ಈ ರಾತ್ರಿ ಅಡವಿಯಲ್ಲಿ ಹೊಳೆದಾಟುವ ಸ್ಥಳದ ಹತ್ತಿರ ಇಳುಕೊಳ್ಳಬೇಡ; ಶೀಘ್ರವಾಗಿ ಹೊಳೆದಾಟಿ ಮುಂದೆ ಹೋಗಿಬಿಡು; ಇಲ್ಲವಾದರೆ ನೀನೂ ನಿನ್ನ ಜನರೂ ನಾಶವಾಗುವಿರಿ ಎಂದು ಹೇಳಿಕಳುಹಿಸಿರಿ ಅಂದನು.",
			"17": "<sup>17</sup> ಯೋನಾತಾನ್ ಅಹೀಮಾಚರು ರೋಗೆಲಿನ ಬುಗ್ಗೆಯ ಬಳಿಯಲ್ಲಿದ್ದರು. ಇವರ ಮನೆಯ ದಾಸಿಯು ಎಲ್ಲಾ ವರ್ತಮಾನಗಳನ್ನು ಇವರಿಗೂ ಇವರು ಅರಸನಾದ ದಾವೀದನಿಗೂ ಮುಟ್ಟಿಸುವಂತೆ ಗೊತ್ತು ಮಾಡಿಕೊಂಡಿದ್ದರು. ತಮ್ಮನ್ನು ಯಾರೂ ನೋಡಬಾರದೆಂದು ಇವರು ತಾವಾಗಿ ಊರೊಳಗೆ ಬರಲಿಲ್ಲ.",
			"18": "<sup>18</sup> ಆದರೂ ಒಬ್ಬ ಯೌವನಸ್ಥನು ಅವರನ್ನು ನೋಡಿ ಅಬ್ಷಾಲೋಮನಿಗೆ ತಿಳಿಸಿದನು. ಅಷ್ಟರಲ್ಲಿ ಅವರಿಬ್ಬರೂ ಬಹುರೀಮಿಗೆ ಓಡಿ ಹೋಗಿ ಅಲ್ಲಿ ಒಬ್ಬನ ಮನೆಯನ್ನು ಹೊಕ್ಕರು. ಆ ಮನೆಯ ಅಂಗಳದಲ್ಲಿ ಒಂದು ಬಾವಿಯಿತ್ತು. ಅವರು ಅದರಲ್ಲಿ ಇಳಿದರು.",
			"19": "<sup>19</sup> ಕೂಡಲೆ ಆ ಮನೆಯ ಹೆಂಗಸು ಅದರ ಮೇಲೆ ಒಂದು ಬಟ್ಟೆಯನ್ನು ಹಾಸಿ ಗೋದಿಯ ನುಚ್ಚನ್ನು ಹರವಿದಳು. ಆದದರಿಂದ ಸಂಗತಿಯು ಯಾರಿಗೂ ಗೊತ್ತಾಗಲಿಲ್ಲ.",
			"20": "<sup>20</sup> ಅಬ್ಷಾಲೋಮನ ಸೇವಕರು ಸ್ತ್ರೀಯ ಮನೆಗೆ ಹೋಗಿ - ಅಹೀಮಾಚ್ ಯೋನಾತಾನರೆಲ್ಲಿ ಎಂದು ಆಕೆಯನ್ನು ಕೇಳಿದ್ದಕ್ಕೆ ಆಕೆಯು - ಅವರು ಹಳ್ಳದಾಟಿ ಹೋಗಿ ಬಿಟ್ಟರು ಎಂದು ಉತ್ತರ ಕೊಟ್ಟಳು. ಅವರು ಅವರನ್ನು ಹುಡುಕುವದಕ್ಕೆ ಹೋಗಿ ಕಾಣದೆ ಯೆರೂಸಲೇಮಿಗೆ ಹಿಂದಿರುಗಿದರು.",
			"21": "<sup>21</sup> ಅವರು ಹೋದಕೂಡಲೆ ಇವರಿಬ್ಬರೂ ಬಾವಿಯಿಂದ ಮೇಲಕ್ಕೆ ಬಂದು ಅರಸನಾದ ದಾವೀದನ ಬಳಿಗೆ ಹೋಗಿ ಅವನಿಗೆ - ಅಹೀತೋಫೆಲನು ನಿನಗೆ ವಿರೋಧವಾಗಿ ಇಂಥಿಂಥ ಆಲೋಚನೆಯನ್ನು ಹೇಳಿದ್ದಾನೆ; ಆದದರಿಂದ ಬೇಗನೆ ಎದ್ದು ಹೊಳೆದಾಟಿ ಹೋಗು ಎಂದು ಹೇಳಿದರು.",
			"22": "<sup>22</sup> ಆಗ ದಾವೀದನೂ ಅವನ ಜೊತೆಯಲ್ಲಿದ್ದವರೆಲ್ಲರೂ ಯೊರ್ದನ್‍ಹೊಳೆಯನ್ನು ದಾಟಿದರು. ಉದಯವಾದಾಗ ದಾಟಬೇಕಾದವನೊಬ್ಬನೂ ಇರಲಿಲ್ಲ.",
			"23": "<sup>23</sup> ಅಹೀತೋಫೆಲನು ತನ್ನ ಆಲೋಚನೆಯು ನಡೆಯಲಿಲ್ಲವೆಂದು ತಿಳಿದು ಕತ್ತೆಗೆ ತಡಿಹಾಕಿಸಿ ಕೂತುಕೊಂಡು ತನ್ನ ಊರಿಗೆ ಹೋಗಿ ಮನೆಯಲ್ಲಿ ವ್ಯವಸ್ಥೆಮಾಡಿ ಉರ್ಲುಹಾಕಿಕೊಂಡು ಸತ್ತನು. ಅವನ ಶವವನ್ನು ಅವನ ತಂದೆಯ ಶ್ಮಶಾನಭೂಮಿಯಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು.",
			"24": "<sup>24</sup> ದಾವೀದನು ಮಹನಯಿಮಿಗೆ ಹೋದನು. ಇಸ್ರಾಯೇಲ್ಯರನ್ನೆಲ್ಲಾ ಕೂಡಿಸಿಕೊಂಡು ಅಬ್ಷಾಲೋಮನು ಯೊರ್ದನ್‍ಹೊಳೆ ದಾಟಿದನು.",
			"25": "<sup>25</sup> ಅವನು ಯೋವಾಬನಿಗೆ ಬದಲಾಗಿ ಅಮಾಸನನ್ನು ಸೇನಾಪತಿಯನ್ನಾಗಿ ನೇಮಿಸಿದನು. ಇಸ್ರಾಯೇಲ್ಯನಾದ ಇತ್ರನು ನಾಹಾಷನ ಮಗಳೂ ಯೋವಾಬನ ತಾಯಿಯಾದ ಚೆರೂಯಳ ತಂಗಿಯೂ ಆದ ಅಬೀಗಲ್ ಎಂಬವಳನ್ನು ಕೂಡಿದ್ದರಿಂದ ಈ ಅಮಾಸನು ಹುಟ್ಟಿದನು.",
			"26": "<sup>26</sup> ಇಸ್ರಾಯೇಲ್ಯರೂ ಅಬ್ಷಾಲೋಮನೂ ಗಿಲ್ಯಾದ್ ದೇಶದಲ್ಲಿ ಪಾಳೆಯಮಾಡಿಕೊಂಡರು.",
			"27": "<sup>27</sup> ದಾವೀದನು ಮಹನಯಿಮಿಗೆ ಬಂದಾಗ ಅಮ್ಮೋನಿಯರ ರಬ್ಬಾ ಊರಿನವನಾದ ನಾಹಾಷನ ಮಗ ಶೋಬಿ, ಲೋದೆಬಾರಿನ ಅಮ್ಮೀಯೇಲನ ಮಗನಾದ ಮಾಕೀರ್, ರೋಗೆಲೀಮ್ ಊರಿನ ಗಿಲ್ಯಾದ್ಯನಾದ",
			"28": "<sup>28</sup> ಬರ್ಜಿಲ್ಲೈ ಎಂಬವರು ದಾವೀದನಿಗೂ ಅವನ ಜನರಿಗೂ ಹಾಸಿಗೆ, ಬಟ್ಟಲು, ಮಡಕೆ ಇವುಗಳನ್ನೂ ಊಟಕ್ಕಾಗಿ ಗೋದಿ ಜವೇಗೋದಿ ಹಿಟ್ಟು ಹುರಿಗಾಳು ಅವರೆ ಅಲಸಂದಿ ಬೇಳೆ ಜೇನುತುಪ್ಪ",
			"29": "<sup>29</sup> ಬೆಣ್ಣೆ ಕುರಿ ಹಸುವಿನ ಗಿಣ್ಣು ಇವುಗಳನ್ನೂ ತಂದು ಕೊಟ್ಟರು. ಜನರು ಅರಣ್ಯಪ್ರಯಾಣದಿಂದ ಹಸಿದವರೂ ದಣಿದವರೂ ಬಾಯಾರಿದವರೂ ಆಗಿದ್ದಾರೆ ಅಂದುಕೊಂಡು ಇವುಗಳನ್ನು ತಂದರು."
		},
		"18": {
			"1": "<sup>1</sup> ತರುವಾಯ ಅರಸನಾದ ದಾವೀದನು ತನ್ನ ಹತ್ತಿರ ಇದ್ದ ಸೈನಿಕರನ್ನು ಲೆಕ್ಕಿಸಿ ಅವರ ಮೇಲೆ ಸಹಸ್ರಾಧಿಪತಿಗಳನ್ನೂ ಶತಾಧಿಪತಿಗಳನ್ನೂ ನೇಮಿಸಿದನು.",
			"2": "<sup>2</sup> ಅವನು ಸೈನ್ಯವನ್ನು ಮೂರು ಪಾಲು ಮಾಡಿ ಒಂದು ಪಾಲನ್ನು ಯೋವಾಬನಿಗೂ ಇನ್ನೊಂದನ್ನು ಚೆರೂಯಳ ಮಗನೂ ಯೋವಾಬನ ತಮ್ಮನೂ ಆದ ಅಬೀಷೈಗೂ ಮತ್ತೊಂದನ್ನು ಗಿತ್ತೀಯನಾದ ಇತ್ತೈಗೂ ಒಪ್ಪಿಸಿ - ನಾನೂ ನಿಮ್ಮ ಸಂಗಡ ಬರುತ್ತೇನೆ ಎಂದು ಹೇಳಿದನು.",
			"3": "<sup>3</sup> ಆಗ ಸೈನ್ಯದವರು ಅವನಿಗೆ - ನೀನು ಬರಬಾರದು; ನಾವು ಸೋತು ಓಡಿ ಹೋದರೆ ಶತ್ರುಗಳು ಲಕ್ಷಿಸುವದಿಲ್ಲ; ನಮ್ಮಲ್ಲಿ ಅರ್ಧ ಜನರು ಸತ್ತರೂ ಅದು ಅವರಿಗೆ ದೊಡ್ಡ ಲಾಭವಲ್ಲ. ನಮ್ಮಂಥ ಹತ್ತು ಸಾವಿರ ಜನರ ಬೆಲೆ ನಿನ್ನೊಬ್ಬನಿಗೇ ಉಂಟು. ನೀನು ಊರಲ್ಲಿದ್ದುಕೊಂಡು ಅಲ್ಲಿಂದಲೇ ನಮಗೆ ಬೇಕಾದ ಸಹಾಯ ಮಾಡಬಹುದು ಅಂದರು.",
			"4": "<sup>4</sup> ಅರಸನು ಅವರಿಗೆ - ನಿಮಗೆ ಸರಿಕಂಡ ಹಾಗೆ ಮಾಡುತ್ತೇನೆ ಎಂದು ಹೇಳಿ ಊರುಬಾಗಲಿನ ಒಂದು ಕಡೆಯಲ್ಲಿ ನಿಂತನು. ಸೈನಿಕರು ನೂರು ನೂರು ಮಂದಿಯಾಗಿಯೂ ಸಾವಿರ ಸಾವಿರ ಮಂದಿಯಾಗಿಯೂ ಹೊರಟರು.",
			"5": "<sup>5</sup> ಅರಸನು ಯೋವಾಬ್ ಅಬೀಷೈ ಇತ್ತೈ ಎಂಬವರಿಗೆ - ನನಗೋಸ್ಕರವಾಗಿ ಯೌವನಸ್ಥನಾದ ಅಬ್ಷಾಲೋಮನನ್ನು ಕರುಣಿಸಿರಿ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು. ದಾವೀದನು ಸೇನಾಪತಿಗಳಿಗೆ ಕೊಟ್ಟ ಈ ಅಪ್ಪಣೆಯು ಸೈನ್ಯದವರಿಗೆಲ್ಲಾ ಕೇಳಿಸಿತು.",
			"6": "<sup>6</sup> ಅನಂತರ ಸೈನಿಕರು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ವಿರೋಧವಾಗಿ ಹೊರಟು ಬೈಲಿಗೆ ಬಂದರು; ಎಫ್ರಾಯೀಮ್‍ಕಾಡಿನಲ್ಲಿ ಯುದ್ಧವಾಯಿತು.",
			"7": "<sup>7</sup> ಇಸ್ರಾಯೇಲ್ಯರು ದಾವೀದನ ಸೈನಿಕರಿಂದ ಪೂರ್ಣವಾಗಿ ಸೋಲಿಸಲ್ಪಟ್ಟರು. ಆ ದಿವಸ ಇಪ್ಪತ್ತುಸಾವಿರ ಜನರು ಹತರಾದರು.",
			"8": "<sup>8</sup> ಯುದ್ಧವು ಸುತ್ತಣ ಪ್ರದೇಶಗಳಲ್ಲಿ ಹಬ್ಬಿಕೊಂಡಿತು. ಆ ದಿವಸ ಕತ್ತಿಗಿಂತ ಕಾಡೇ ಹೆಚ್ಚಿನ ಸಂಹಾರಕ್ಕೆ ಕಾರಣವಾಯಿತು.",
			"9": "<sup>9</sup> ಅಬ್ಷಾಲೋಮನು ದಾವೀದನ ಸೇವಕರಿಗೆ ಸಿಕ್ಕಿದನು; ಹೇಗಂದರೆ - ಅವನು ಹತ್ತಿದ ಹೇಸರಕತ್ತೆಯು ಒಂದು ದೊಡ್ಡ ಏಲಾಮರದ ಕೆಳಗೆ ಹಾದುಹೋಗುವಾಗ ಅವನ ತಲೆಯು ನಿಬಿಡವಾದ ಅದರ ಕೊಂಬೆಗಳಲ್ಲಿ ಸಿಕ್ಕಿಕೊಂಡಿತು; ಹೇಸರಕತ್ತೆಯು ಅವನ ಕೆಳಗಿನಿಂದ ಓಡಿಹೋಯಿತು. ಅವನು ಭೂಮಿಗೂ ಆಕಾಶಕ್ಕೂ ಮಧ್ಯದಲ್ಲಿ ನೇತಾಡುವವನಾದನು.",
			"10": "<sup>10</sup> ಇದನ್ನು ನೋಡಿದ ಒಬ್ಬ ಮನುಷ್ಯನು ಯೋವಾಬನಿಗೆ - ಅಗೋ, ಆ ಏಲಾಮರದಲ್ಲಿ ಅಬ್ಷಾಲೋಮನು ನೇತಾಡುತ್ತಿರುವದನ್ನು ಕಂಡೆನು ಎಂದು ಹೇಳಲು ಯೋವಾಬನು ಅವನಿಗೆ -",
			"11": "<sup>11</sup> ನೀನು ಕಂಡ ಕೂಡಲೆ ಯಾಕೆ ಅವನನ್ನು ಕಡಿದು ನೆಲಕ್ಕುರುಳಿಸಲಿಲ್ಲ? ಹಾಗೆ ಮಾಡಿದ್ದರೆ ನಾನು ನಿನಗೆ ಹತ್ತು ರೂಪಾಯಿಗಳನ್ನೂ ಒಂದು ನಡುಕಟ್ಟನ್ನೂ ಕೊಡುತ್ತಿದ್ದೆನು ಅಂದನು.",
			"12": "<sup>12</sup> ಅದಕ್ಕೆ ಆ ಮನುಷ್ಯನು - ನೀನು ನನ್ನ ಕೈಯಲ್ಲಿ ಸಾವಿರ ರೂಪಾಯಿಗಳನ್ನಿಟ್ಟರೂ ಅರಸನ ಮಗನಿಗೆ ವಿರೋಧವಾಗಿ ಕೈಯೆತ್ತೆನು. ಅರಸನು ನಮ್ಮೆಲ್ಲರಿಗೆ ಕೇಳಿಸುವಂತೆ ನಿನಗೂ ಅಬೀಷೈಗೂ ಇತ್ತೈಗೂ - ಜಾಗರೂಕತೆಯಿಂದಿರ್ರಿ, ಯೌವನಸ್ಥನಾದ ಅಬ್ಷಾಲೋಮನನ್ನು ಯಾರೂ ಮುಟ್ಟಬಾರದು ಎಂದು ಆಜ್ಞಾಪಿಸಿದನಲ್ಲವೋ?",
			"13": "<sup>13</sup> ನಾನು ಅವನ ಪ್ರಾಣಕ್ಕೆ ಅಪಾಯವನ್ನುಂಟುಮಾಡಿದರೆ ಅದು ಅರಸನಿಗೆ ಮರೆಯಾಗುವದಿಲ್ಲ; ನೀನೂ ನನ್ನನ್ನು ಕೈಬಿಟ್ಟು ದೂರ ನಿಲ್ಲುವಿ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"14": "<sup>14</sup> ಆಗ ಯೋವಾಬನು - ಇಲ್ಲಿ ನಿಂತು ತಡ ಮಾಡುವದೇಕೆ ಎಂದು ಹೇಳಿ ಕೂಡಲೆ ಕೈಯಲ್ಲಿ ಮೂರು ಈಟಿಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ಅಬ್ಷಾಲೋಮನ ಎದೆಗೆ ತಿವಿದನು. ಅವನು ಇನ್ನೂ ಜೀವದಿಂದ ಮರದಲ್ಲಿ ನೇತಾಡುತ್ತಿರುವಾಗಲೇ",
			"15": "<sup>15</sup> ಯೋವಾಬನ ಆಯುಧಗಳನ್ನು ಹೊರುವ ಹತ್ತು ಮಂದಿ ಯೌವನಸ್ಥರು ಬಂದು ಅವನನ್ನು ಕೊಂದುಹಾಕಿದರು.",
			"16": "<sup>16</sup> ಕೂಡಲೆ ಯೋವಾಬನು ತನ್ನ ಜನರನ್ನು ತಡೆಯುವದಕ್ಕಾಗಿ ತುತೂರಿಯನ್ನು ಊದಿಸಲು ಸೈನ್ಯದವರೆಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಹಿಂದಟ್ಟುವದನ್ನು ಬಿಟ್ಟು ಹಿಂದಿರುಗಿದರು.",
			"17": "<sup>17</sup> ಅವರು ಅಬ್ಷಾಲೋಮನ ಶವವನ್ನು ಆ ಕಾಡಿನಲ್ಲಿದ್ದ ಒಂದು ದೊಡ್ಡ ಗುಂಡಿಯೊಳಗೆ ಹಾಕಿ ಅದರ ಮೇಲೆ ಬಲುದೊಡ್ಡ ಕಲ್ಲು ಕುಪ್ಪೆಯನ್ನು ಮಾಡಿದರು. ಇಸ್ರಾಯೇಲ್ಯರಾದರೋ ತಮ್ಮತಮ್ಮ ನಿವಾಸಗಳಿಗೆ ಓಡಿಹೋದರು.",
			"18": "<sup>18</sup> ಅಬ್ಷಾಲೋಮನು ಇನ್ನೂ ಜೀವದಿಂದಿದ್ದಾಗ ತನ್ನ ಹೆಸರನ್ನುಳಿಸುವ ಮಗನಿಲ್ಲದ್ದರಿಂದ ಒಂದು ಕಲ್ಲಿನ ಕಂಬವನ್ನು ತರಿಸಿ ಅದನ್ನು ಅರಸನ ತಗ್ಗಿನಲ್ಲಿ ತನ್ನ ಜ್ಞಾಪಕಾರ್ಥವಾಗಿ ನಿಲ್ಲಿಸಿ ಅದಕ್ಕೆ ತನ್ನ ಹೆಸರನ್ನಿಟ್ಟನು. ಅದು ಇಂದಿನವರೆಗೂ ಅಬ್ಷಾಲೋಮನ ಜ್ಞಾಪಕಸ್ತಂಭವೆಂದೆನಿಸಿಕೊಳ್ಳುತ್ತದೆ.",
			"19": "<sup>19</sup> ಚಾದೋಕನ ಮಗನಾದ ಅಹೀಮಾಚನು ಯೋವಾಬನಿಗೆ - ಅಪ್ಪಣೆಯಾಗಲಿ, ನಾನು ಅರಸನ ಬಳಿಗೆ ಓಡಿಹೋಗಿ ಯೆಹೋವನು ಅವನ ವೈರಿಗಳಿಗೆ ಮುಯ್ಯಿ ತೀರಿಸಿದ್ದಾನೆಂಬ ಶುಭವರ್ತಮಾನವನ್ನು ತಿಳಿಸುವೆನು ಅನ್ನಲು",
			"20": "<sup>20</sup> ಯೋವಾಬನು ಅವನಿಗೆ - ಈಹೊತ್ತಿನ ಸುದ್ದಿಯನ್ನು ತೆಗೆದುಕೊಂಡು ಹೋಗತಕ್ಕವನು ನೀನಲ್ಲ; ಇನ್ನೊಂದು ಸಾರಿ ನಿನ್ನನ್ನು ಕಳುಹಿಸುತ್ತೇನೆ. ಈಹೊತ್ತು ಅರಸನ ಮಗನು ಸತ್ತಿರುವದರಿಂದ ವರ್ತಮಾನವು ಶುಭವಾದದ್ದಲ್ಲ ಎಂದು ಹೇಳಿ",
			"21": "<sup>21</sup> ತನ್ನ ಬಳಿಯಲ್ಲಿದ್ದ ಒಬ್ಬ ಕೂಷ್ಯನನ್ನು ಕರೆದು ಅವನಿಗೆ - ನೀನು ಹೋಗಿ ಕಂಡದ್ದನ್ನು ಅರಸನಿಗೆ ತಿಳಿಸು ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು. ಅವನು ಯೋವಾಬನಿಗೆ ನಮಸ್ಕರಿಸಿ ಹೊರಟಹೋದನು.",
			"22": "<sup>22</sup> ಚಾದೋಕನ ಮಗನಾದ ಅಹೀಮಾಚನು ತಿರಿಗಿ ಯೋವಾಬನಿಗೆ - ಆಗುವದಾಗಲಿ, ದಯವಿಟ್ಟು ಆ ಕೂಷ್ಯನ ಹಿಂದೆ ಹೋಗುವದಕ್ಕೆ ನನಗೆ ಅಪ್ಪಣೆಕೊಡು ಎಂದು ಬೇಡಿಕೊಂಡನು. ಅದಕ್ಕೆ ಯೋವಾಬನು - ಮಗನೇ, ಯಾಕೆ ಹೋಗಬೇಕನ್ನುತ್ತೀ? ಅದಕ್ಕಾಗಿ ನಿನಗೆ ಬಹುಮಾನ ಸಿಕ್ಕುವದಿಲ್ಲವಲ್ಲಾ ಅಂದನು.",
			"23": "<sup>23</sup> ಆದರೆ ಅಹೀಮಾಚನು ತಿರಿಗಿ - ಚಿಂತೆಯಿಲ್ಲ, ನನ್ನನ್ನೂ ಕಳುಹಿಸು ಎಂದು ಒತ್ತಾಯಪಡಿಸಿದ್ದರಿಂದ ಯೋವಾಬನು ಹೋಗು ಎಂದು ಹೇಳಿದನು. ಆಗ ಅಹೀಮಾಚನು ಯೊರ್ದನ್ ತಗ್ಗಿನಲ್ಲಿರುವ ದಾರಿಯನ್ನು ಹಿಡಿದು ಆ ಕೂಷ್ಯನಿಗಿಂತ ಮುಂದಾಗಿ ಓಡಿದನು.",
			"24": "<sup>24</sup> ದಾವೀದನು ಎರಡು ಬಾಗಲುಗಳ ಮಧ್ಯದಲ್ಲಿ ಕೂತುಕೊಂಡಿದ್ದನು. ಅಷ್ಟರಲ್ಲಿ ಕಾವಲುಗಾರನು ಗೋಪುರದ ಮಾಳಿಗೆಯನ್ನು ಹತ್ತಿ ಪಾಗಾರದ ಬಳಿಯಲ್ಲಿ ನಿಂತು ನೋಡಲು ಒಂಟಿಗನಾಗಿ ಬರುವ ಒಬ್ಬ ಮನುಷ್ಯನನ್ನು ಕಂಡನು.",
			"25": "<sup>25</sup> ಅವನು ಕೂಡಲೆ ದಾವೀದನಿಗೆ ತಿಳಿಸಲು ದಾವೀದನು - ಅವನು ಒಬ್ಬನಾಗಿ ಇದ್ದರೆ ಸಮಾಚಾರ ತರುವವನಾಗಿರಬಹುದು ಅಂದನು. ಆ ಮನುಷ್ಯನು ಬರಬರುತ್ತಾ ಸಮೀಪವಾದನು.",
			"26": "<sup>26</sup> ಅಷ್ಟರಲ್ಲಿ ಕಾವಲುಗಾರನು ಓಡುತ್ತಾ ಬರುವ ಇನ್ನೊಬ್ಬನನ್ನು ಕಂಡು ದ್ವಾರಪಾಲಕನ ಮುಖಾಂತರ - ಇಗೋ, ಇನ್ನೊಬ್ಬನು ಒಂಟಿಗನಾಗಿ ಬರುತ್ತಿದ್ದಾನೆ ಎಂದು ಅರಸನಿಗೆ ತಿಳಿಸಿದನು. ಅರಸನು - ಹಾಗಾದರೆ ಅವನೂ ಸಮಾಚಾರ ತರುವವನು ಎಂದು ಹೇಳಿದನು.",
			"27": "<sup>27</sup> ಕಾವಲುಗಾರನು - ಮುಂದಾಗಿ ಬರುತ್ತಿರುವವನ ಓಟವು ಚಾದೋಕನ ಮಗನಾದ ಅಹೀಮಾಚನ ಓಟದ ಹಾಗೆ ಕಾಣುತ್ತದೆ ಎಂದನು. ಅದಕ್ಕೆ ಅರಸನು - ಅವನು ಒಳ್ಳೆಯವನು; ಶುಭವರ್ತಮಾನತರುವವನು ಅಂದನು.",
			"28": "<sup>28</sup> ಅಹೀಮಾಚನು ಹತ್ತಿರ ಬಂದು ಅರಸನಿಗೆ ಶುಭವಾಗಲಿ ಎಂದು ಹೇಳಿ ಸಾಷ್ಟಾಂಗನಮಸ್ಕಾರಮಾಡಿ - ಅರಸನಿಗೆ ವಿರೋಧವಾಗಿ ಕೈಯೆತ್ತಿದವರನ್ನು ಸ್ವಾಧೀನಪಡಿಸಿದ ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಸ್ತೋತ್ರವಾಗಲಿ ಅಂದನು.",
			"29": "<sup>29</sup> ಆಗ ಅರಸನು ಅವನನ್ನು - ಯೌವನಸ್ಥನಾದ ಅಬ್ಷಾಲೋಮನು ಸುರಕ್ಷಿತನಾಗಿದ್ದಾನೋ ಎಂದು ಕೇಳಿದನು. ಅದಕ್ಕೆ ಅಹೀಮಾಚನು ಯೋವಾಬನು ಒಡೆಯರ ಸೇವಕನಾದ ನನ್ನನ್ನು ಕಳುಹಿಸುವಾಗ ನಾನು ಒಂದು ದೊಡ್ಡ ದೊಂಬಿಯನ್ನು ಕಂಡೆನು. ಆದರೆ ಸಂಗತಿಯೇನೆಂಬದು ನನಗೆ ಗೊತ್ತಾಗಲಿಲ್ಲ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"30": "<sup>30</sup> ಆಗ ಅರಸನು ಅವನಿಗೆ - ನೀನು ಇತ್ತ ನಿಲ್ಲು ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು; ಅವನು ನಿಂತನು.",
			"31": "<sup>31</sup> ಅಷ್ಟರಲ್ಲಿ ಕೂಷ್ಯನೂ ಬಂದು - ನನ್ನ ಒಡೆಯನಾದ ಅರಸನಿಗೆ ಶುಭವರ್ತಮಾನ ತಂದಿದ್ದೇನೆ; ಯೆಹೋವನು ನಿನಗೆ ವಿರೋಧವಾಗಿ ಎದ್ದವರೆಲ್ಲರಿಗೂ ಮುಯ್ಯಿತೀರಿಸಿದ್ದಾನೆ ಅಂದನು.",
			"32": "<sup>32</sup> ಅರಸನು ಅವನನ್ನು - ಯೌವನಸ್ಥನಾದ ಅಬ್ಷಾಲೋಮನು ಸುರಕ್ಷಿತನಾಗಿದ್ದಾನೋ ಎಂದು ಕೇಳಲು ಅವನು - ನನ್ನ ಒಡೆಯನಾದ ಅರಸನ ಶತ್ರುಗಳಿಗೂ ಅವನಿಗೆ ಕೇಡು ಮಾಡುವದಕ್ಕೆ ಎದ್ದು ನಿಂತವರೆಲ್ಲರಿಗೂ ಆ ಯೌವನಸ್ಥನಿಗಾದ ಗತಿಯೇ ಆಗಲಿ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"33": "<sup>33</sup> ಇದನ್ನು ಕೇಳಿ ಅರಸನು ಎದೆಯೊಡೆದವನಾಗಿ - ನನ್ನ ಮಗನೇ, ಅಬ್ಷಾಲೋಮನೇ, ನನ್ನ ಮಗನೇ, ನನ್ನ ಮಗನಾದ ಅಬ್ಷಾಲೋಮನೇ, ನಿನಗೆ ಬದಲಾಗಿ ನಾನು ಸತ್ತಿದ್ದರೆ ಎಷ್ಟೋ ಒಳ್ಳೇದಾಗುತ್ತಿತ್ತು. ಅಬ್ಷಾಲೋಮನೇ, ನನ್ನ ಮಗನೇ, ಮಗನೇ ಎಂದು ಕೂಗಿ ಅಳುತ್ತಾ ಹೆಬ್ಬಾಗಲಿನ ಮೇಲಿರುವ ಕೋಣೆಗೆ ಹೋದನು."
		},
		"19": {
			"1": "<sup>1</sup> ಅರಸನು ಅಬ್ಷಾಲೋಮನಿಗಾಗಿ ಅಳುತ್ತಾ ಗೋಳಾಡುತ್ತಾ ಇದ್ದಾನೆಂಬ ವರ್ತಮಾನವು ಯೋವಾಬನಿಗೆ ಮುಟ್ಟಿತು.",
			"2": "<sup>2</sup> ಅರಸನು ಮಗನಿಗೋಸ್ಕರ ಪ್ರಲಾಪಿಸುತ್ತಿದ್ದಾನೆಂಬದು ಎಲ್ಲಾ ಜನರಿಗೂ ಗೊತ್ತಾದದರಿಂದ ಅವರ ಜಯಘೋಷವು ಗೋಳಾಟವಾಯಿತು.",
			"3": "<sup>3</sup> ಜನರು ಆ ದಿವಸ ಯುದ್ಧದಲ್ಲಿ ಸೋತು ಓಡಿ ಬಂದವರೋ ಎಂಬಂತೆ ನಾಚಿಕೆಯಿಂದ ಕಳ್ಳತನವಾಗಿ ಊರನ್ನು ಹೊಕ್ಕರು.",
			"4": "<sup>4</sup> ಅರಸನು ತನ್ನ ಮೋರೆಯನ್ನು ಮುಚ್ಚಿಕೊಂಡು - ನನ್ನ ಮಗನಾದ ಅಬ್ಷಾಲೋಮನೇ, ಅಬ್ಷಾಲೋಮನೇ, ನನ್ನ ಮಗನೇ, ಮಗನೇ ಎಂದು ಬಹಳವಾಗಿ ಗೋಳಾಡಿದನು.",
			"5": "<sup>5</sup> ಯೋವಾಬನು ಅರಸನ ಬಳಿಗೆ ಹೋಗಿ ಅವನಿಗೆ - ನಿನ್ನನ್ನೂ ನಿನ್ನ ಗಂಡುಹೆಣ್ಣು ಮಕ್ಕಳನ್ನೂ ಹೆಂಡತಿಯರನ್ನೂ ಉಪಪತ್ನಿಯರನ್ನೂ ರಕ್ಷಿಸಿದಂಥ ನಿನ್ನ ಸೇವಕರನ್ನು ಈಹೊತ್ತು ಅಪಮಾನಪಡಿಸಿದಿ.",
			"6": "<sup>6</sup> ನೀನು ಶತ್ರುಗಳನ್ನು ಪ್ರೀತಿಸುವವನೂ ಮಿತ್ರರನ್ನು ದ್ವೇಷಿಸುವವನೂ ಆಗಿದ್ದೀ; ನೀನು ನಿನ್ನ ಸೇನಾಧಿಪತಿಗಳನ್ನೂ ಸೈನಿಕರನ್ನೂ ಲಕ್ಷಿಸುವವನಲ್ಲವೆಂದು ಈಗ ತೋರಿಸಿಕೊಟ್ಟಿ. ಇದರಿಂದ ಈಹೊತ್ತು ನಾವೆಲ್ಲರೂ ಸತ್ತು ಅಬ್ಷಾಲೋಮನೊಬ್ಬನೇ ಉಳಿದಿದ್ದರೆ ನಿನಗೆ ಸಂತೋಷವಾಗುತ್ತಿತ್ತೆಂದು ತಿಳಿಯಬೇಕಷ್ಟೆ.",
			"7": "<sup>7</sup> ಈಗ ಎದ್ದು ಹೋಗಿ ನಿನ್ನ ಸೇವಕರನ್ನು ದಯಾಭಾವದಿಂದ ಮಾತಾಡಿಸು; ಯೆಹೋವನಾಣೆ, ನೀನು ಹೀಗೆ ಮಾಡದಿದ್ದರೆ ಸಾಯಂಕಾಲವಾಗುವಷ್ಟರಲ್ಲಿ ಎಲ್ಲರೂ ನಿನ್ನನ್ನು ಬಿಟ್ಟು ಹೋಗುವರು. ಯೌವನಕಾಲದಿಂದ ಈವರೆಗೆ ನಿನಗೆ ಬಂದ ಎಲ್ಲಾ ಕೇಡುಗಳಲ್ಲಿ ಇದೇ ಹೆಚ್ಚಿನದಾಗಿರುವದು ಎಂದು ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ಆಗ ಅರಸನು ಎದ್ದು ಬಂದು ಊರುಬಾಗಲಲ್ಲಿ ಕೂತುಕೊಂಡನು. ಇಗೋ, ಅರಸನು ಬಂದು ಊರುಬಾಗಲಲ್ಲಿ ಕೂತುಕೊಂಡಿದ್ದಾನೆಂಬ ಸುದ್ದಿಯು ಪ್ರಜೆಗಳಿಗೆ ಮುಟ್ಟಿದಾಗ ಅವರೆಲ್ಲರೂ ಅವನ ಮುಂದೆ ಕೂಡಿಬಂದರು.",
			"9": "<sup>9</sup> ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರೂ ತಮ್ಮತಮ್ಮ ನಿವಾಸಗಳಿಗೆ ಓಡಿಹೋಗಿದ್ದರು. ಇಸ್ರಾಯೆಲ್‍ಕುಲಗಳಲ್ಲೆಲ್ಲಾ ಜನರು ತಮ್ಮ ತಮ್ಮೊಳಗೆ ಜಗಳವಾಡಿ - ಅರಸನಾದ ದಾವೀದನು ನಮ್ಮನ್ನು ಫಿಲಿಷ್ಟಿಯರ ಕೈಗೂ ಬೇರೆ ಎಲ್ಲಾ ವೈರಿಗಳ ಕೈಗೂ ಸಿಕ್ಕದಂತೆ ತಪ್ಪಿಸಿ ಕಾಪಾಡಿದನು. ಆದರೆ ಅವನು ಅಬ್ಷಾಲೋಮನ ದೆಸೆಯಿಂದ ದೇಶವನ್ನು ಬಿಟ್ಟು ಓಡಿಹೋಗಬೇಕಾಯಿತು.",
			"10": "<sup>10</sup> ನಾವು ಅಭಿಷೇಕಿಸಿದ ಅಬ್ಷಾಲೋಮನು ಯುದ್ಧದಲ್ಲಿ ಸತ್ತನು. ಹೀಗಿರುವಲ್ಲಿ ಅರಸನಾದ ದಾವೀದನನ್ನು ತಿರಿಗಿ ಕರಕೊಂಡು ಬರಬಾರದೇ? ಸುಮ್ಮನೆ ಕೂತಿರುವದೇಕೆ ಎಂದು ಮಾತಾಡಿಕೊಂಡರು.",
			"11": "<sup>11</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಈ ಪ್ರಕಾರ ಮಾತಾಡಿಕೊಂಡ ವರ್ತಮಾನವು ದಾವೀದನಿಗೆ ಮುಟ್ಟಲು ಅವನು ಯಾಜಕರಾದ ಚಾದೋಕ್ ಎಬ್ಯಾತಾರರಿಗೆ - ನೀವು ನನ್ನ ಹೆಸರಿನಲ್ಲಿ ಯೆಹೂದ ಕುಲದ ಹಿರಿಯರಿಗೆ - ಅರಸನಾದ ನನ್ನನ್ನು ತಿರಿಗಿ ಅರಮನೆಗೆ ಕರಕೊಂಡು ಹೋಗುವದರಲ್ಲಿ ನೀವು ಹಿಂದುಳಿದಿರುವದೇಕೆ?",
			"12": "<sup>12</sup> ನೀವು ನನ್ನ ರಕ್ತಸಂಬಂಧಿಗಳಾದ ಸಹೋದರರಾಗಿರುತ್ತೀರಲ್ಲಾ; ಆದದರಿಂದ ನನ್ನನ್ನು ಕರಕೊಂಡು ಹೋಗುವದರಲ್ಲಿ ನೀವು ಹಿಂದುಳಿದಿರುವದು ಒಳ್ಳೇದಲ್ಲ ಎಂಬದಾಗಿಯೂ",
			"13": "<sup>13</sup> ಅಮಾಸನಿಗೆ - ನೀನೂ ನನ್ನ ರಕ್ತ ಸಂಬಂಧಿಯಾಗಿದ್ದೀ; ನಾನು ಯೋವಾಬನ ಬದಲಿಗೆ ನಿನ್ನನ್ನೇ ನಿತ್ಯ ಸೇನಾಪತಿಯನ್ನಾಗಿ ಮಾಡದಿದ್ದರೆ ಯೆಹೋವನು ನನಗೆ ಬೇಕಾದದ್ದನ್ನು ಮಾಡಲಿ ಎಂಬದಾಗಿಯೂ ತಿಳಿಸಿರಿ ಎಂದು ಹೇಳಿಕಳುಹಿಸಿದನು.",
			"14": "<sup>14</sup> ಆಗ ಯೆಹೂದ್ಯರೆಲ್ಲರೂ ಏಕ ಮನಸ್ಸಿನಿಂದ ಅರಸನ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡು ಅವನಿಗೆ - ನೀನೂ ನಿನ್ನ ಎಲ್ಲಾ ಸೇವಕರೂ ತಿರಿಗಿ ಬನ್ನಿರಿ ಎಂದು ಹೇಳಿಕಳುಹಿಸಿದರು.",
			"15": "<sup>15</sup> ಅರಸನು ಸ್ವದೇಶಕ್ಕೆ ಹೋಗಬೇಕೆಂದು ಹೊರಟು ಯೊರ್ದನಿಗೆ ಬಂದಾಗ ಯೆಹೂದಕುಲದವರು ಅವನನ್ನು ಎದುರುಗೊಳ್ಳುವದಕ್ಕೂ ಯೊರ್ದನ್ ಹೊಳೆಯನ್ನು ದಾಟಿಸುವದಕ್ಕೂ ಗಿಲ್ಗಾಲಿಗೆ ಬಂದರು.",
			"16": "<sup>16</sup> ಬೆನ್ಯಾಮೀನ್ ಕುಲದ ಗೇರನ ಮಗನೂ ಬಹುರೀಮಿನವನೂ ಆದ ಶಿಮ್ಮಿಯು ಅವಸರದಿಂದ ಬಂದು ಅರಸನಾದ ದಾವೀದನನ್ನು ಎದುರುಗೊಳ್ಳುವದಕ್ಕೆ ಯೆಹೂದ ಕುಲದವರನ್ನು ಕೂಡಿಕೊಂಡನು.",
			"17": "<sup>17</sup> ಅವನ ಜೊತೆಯಲ್ಲಿ ಸಾವಿರ ಮಂದಿ ಬೆನ್ಯಾಮೀನ್ಯರಿದ್ದರು. ಇದಲ್ಲದೆ ಸೌಲನ ಮನೆಯ ಸೇವಕನಾದ ಚೀಬನು ತನ್ನ ಹದಿನೈದು ಮಂದಿ ಮಕ್ಕಳನ್ನೂ ಇಪ್ಪತ್ತುಮಂದಿ ಸೇವಕರನ್ನೂ ಕರಕೊಂಡು ಅರಸನ ಮುಂದೆಯೇ ಪೂರ್ಣಾಸಕ್ತಿಯಿಂದ ಯೊರ್ದನ್ ಹೊಳೆಯಲ್ಲಿಳಿದು",
			"18": "<sup>18</sup> ಅರಸನ ಮನೆಯವರನ್ನು ದಾಟಿಸುವದಕ್ಕೋಸ್ಕರವೂ ಅವರಿಗೆ ಬೇಕಾದ ಸಹಾಯ ಮಾಡುವದಕ್ಕೋಸ್ಕರವೂ ದಡದಿಂದ ದಡಕ್ಕೆ ಹೋಗುತ್ತಾ ಬರುತ್ತಾ ಇದ್ದನು. ಅರಸನು ಯೊರ್ದನ್ ಹೊಳೆಯನ್ನು ದಾಟಿಬಂದ ಕೂಡಲೆ ಗೇರನ ಮಗನಾದ ಶಿಮ್ಮಿಯು ಸಾಷ್ಟಾಂಗನಮಸ್ಕಾರ ಮಾಡಿ ಅವನಿಗೆ -",
			"19": "<sup>19</sup> ನನ್ನ ಒಡೆಯನು ನನ್ನನ್ನು ಅಪರಾಧಿಯೆಂದೆಣಿಸದಿರಲಿ; ನನ್ನ ಅರಸನು ಯೆರೂಸಲೇಮನ್ನು ಬಿಟ್ಟು ಹೋಗುವಾಗ ನಾನು ಮೂರ್ಖತನದಿಂದ ಮಾಡಿದ್ದನ್ನೆಲ್ಲಾ ಅವನು ನೆನಸದಿರಲಿ, ಲಕ್ಷಿಸದಿರಲಿ.",
			"20": "<sup>20</sup> ಒಡೆಯನೇ, ನಿನ್ನ ಸೇವಕನಾದ ನಾನು ಪಾಪ ಮಾಡಿದೆನೆಂದು ಒಪ್ಪಿಕೊಳ್ಳುತ್ತೇನೆ. ಆದದರಿಂದಲೇ ನಾನು ಈಹೊತ್ತು ಅರಸನನ್ನು ಎದುರುಗೊಳ್ಳುವದಕ್ಕೆ ಎಲ್ಲಾ ಯೋಸೇಫ್ಯರಲ್ಲಿ ಮೊದಲಾಗಿ ಬಂದಿದ್ದೇನೆ ಎಂದು ಹೇಳಿದನು.",
			"21": "<sup>21</sup> ಚೆರೂಯಳ ಮಗನಾದ ಅಬೀಷೈಯು - ಯೆಹೋವನ ಅಭಿಷಿಕ್ತನನ್ನು ಶಪಿಸಿದ್ದಕ್ಕಾಗಿ ಶಿಮ್ಮಿಗೆ ಮರಣದಂಡನೆಯಾಗಬೇಕಲ್ಲವೇ ಅಂದನು.",
			"22": "<sup>22</sup> ಅದಕ್ಕೆ ದಾವೀದನು - ಚೆರೂಯಳ ಮಕ್ಕಳೇ, ನನಗೆ ನಿಮ್ಮ ಗೊಡವೆಯೇ ಬೇಡ; ನೀವು ಈಹೊತ್ತು ನನ್ನನ್ನು ಅಕೃತ್ಯಕ್ಕೆ ಪ್ರೇರಿಸುವವರಾಗಿದ್ದೀರಿ. ಇಂಥ ದಿವಸದಲ್ಲಿ ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಒಬ್ಬನಿಗೆ ಮರಣದಂಡನೆಯಾಗುವದು ಸರಿಯೋ? ನಾನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನೆಂಬದು ಈಹೊತ್ತು ಸ್ಪಷ್ಟವಾಗಿ ಗೊತ್ತಾಯಿತು ಎಂದು ನುಡಿದನು.",
			"23": "<sup>23</sup> ಅನಂತರ ಅರಸನು ಶಿಮ್ಮಿಗೆ - ನಿನಗೆ ಮರಣ ಶಿಕ್ಷೆಯಾಗುವದಿಲ್ಲ ಎಂದು ಪ್ರಮಾಣಮಾಡಿ ಹೇಳಿದನು.",
			"24": "<sup>24</sup> ತರುವಾಯ ಸೌಲನ ಮೊಮ್ಮಗನಾದ ಮೆಫೀಬೋಶೆತನು ಅರಸನನ್ನು ಎದುರುಗೊಳ್ಳುವದಕ್ಕೆ ಬಂದನು. ಅರಸನು ಯೆರೂಸಲೇಮನ್ನು ಬಿಟ್ಟಂದಿನಿಂದ ಸುರಕ್ಷಿತನಾಗಿ ಹಿಂದಿರುಗುವವರೆಗೂ ಇವನು ತನ್ನ ಕಾಲುಗಳನ್ನು ಕಟ್ಟಿಕೊಂಡಿದ್ದಿಲ್ಲ, ಮೀಸೆಯನ್ನು ಕತ್ತರಿಸಿದ್ದಿಲ್ಲ, ಬಟ್ಟೆಗಳನ್ನು ಒಗೆಸಿಕೊಂಡಿದ್ದಿಲ್ಲ,",
			"25": "<sup>25</sup> ಇವನು ಯೆರೂಸಲೇಮಿನಿಂದ ಅರಸನ ದರ್ಶನಕ್ಕೆ ಬಂದಾಗ ಅರಸನು - ಮೆಫೀಬೋಶೆತನೇ, ನೀನು ನನ್ನ ಸಂಗಡ ಯಾಕೆ ಬರಲಿಲ್ಲ ಎಂದು ಕೇಳಲು",
			"26": "<sup>26</sup> ಅವನು - ನನ್ನ ಒಡೆಯನೇ, ಅರಸನೇ, ನನ್ನ ಸೇವಕನು ನನ್ನನ್ನು ವಂಚಿಸಿದನು. ಕುಂಟನಾದ ನಾನು ಅವನಿಗೆ - ಕತ್ತೆಗೆ ತಡಿಹಾಕು, ನಾನು ಕೂತುಕೊಂಡು ಅರಸನ ಜೊತೆಯಲ್ಲಿ ಹೋಗಬೇಕು ಎಂದು ಆಜ್ಞಾಪಿಸಿದೆನು.",
			"27": "<sup>27</sup> ಅವನು ಹಾಗೆ ಮಾಡದೆ ನನ್ನ ಒಡೆಯನಾದ ಅರಸನ ಬಳಿಗೆ ಬಂದು ನನ್ನ ವಿಷಯದಲ್ಲಿ ಚಾಡಿಹೇಳಿದನು. ಅರಸನು ದೇವದೂತನ ಹಾಗಿರುತ್ತಾನೆ, ತನಗೆ ಸರಿಕಂಡದ್ದನ್ನು ಮಾಡಲಿ.",
			"28": "<sup>28</sup> ನನ್ನ ತಂದೆಯ ಮನೆಯವರೆಲ್ಲರೂ ಅರಸನಾದ ನನ್ನ ಒಡೆಯನ ದೃಷ್ಟಿಯಲ್ಲಿ ಮರಣಕ್ಕೆ ಪಾತ್ರರಾಗಿದ್ದರು. ಆದರೂ ಅವನು ತನ್ನ ಸೇವಕನಾದ ನನ್ನನ್ನು ಭೋಜನಕ್ಕೆ ತನ್ನ ಪಂಕ್ತಿಯಲ್ಲಿ ಕುಳ್ಳಿರಿಸಿಕೊಂಡನು. ಅರಸನಿಗೆ ಹೆಚ್ಚಾಗಿ ಮೊರೆಯಿಡುವದಕ್ಕೆ ನಾನೇನು ಯೋಗ್ಯನೋ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"29": "<sup>29</sup> ಆಗ ಅರಸನು ಅವನಿಗೆ - ಹೆಚ್ಚು ಮಾತು ಯಾಕೆ? ನೀನೂ ಚೀಬನೂ ಹೊಲವನ್ನು ಭಾಗಮಾಡಿಕೊಳ್ಳಬೇಕೆಂಬದೇ ನನ್ನ ತೀರ್ಪು ಎಂದು ಹೇಳಿದನು.",
			"30": "<sup>30</sup> ಅದಕ್ಕೆ ಮೆಫೀಬೋಶೆತನು - ಚೀಬನು ಎಲ್ಲವನ್ನು ತಾನೇ ತೆಗೆದುಕೊಂಡರೂ ತೆಗೆದುಕೊಳ್ಳಲಿ, ನನ್ನ ಒಡೆಯನಾದ ಅರಸನು ತನ್ನ ಮನೆಗೆ ಸುರಕ್ಷಿತವಾಗಿ ಬಂದದ್ದೇ ಸಾಕು ಅಂದನು.",
			"31": "<sup>31</sup> ಗಿಲ್ಯಾದ್ಯನಾದ ಬರ್ಜಿಲ್ಲೈ ಎಂಬವನು ಅರಸನನ್ನು ಸಾಗಕಳುಹಿಸುವದಕ್ಕಾಗಿ ರೋಗೆಲೀಮಿನಿಂದ ಯೊರ್ದನಿಗೆ ಬಂದಿದ್ದನು.",
			"32": "<sup>32</sup> ಇವನು ಎಂಭತ್ತು ವರುಷದ ಮುದುಕನು. ಇವನು ಬಹುಶ್ರೀಮಂತನಾಗಿದ್ದದರಿಂದ ದಾವೀದನು ಮಹನಯಿಮಿನಲ್ಲಿದ್ದ ಕಾಲದಲ್ಲೆಲ್ಲಾ ಅವನ ಜೀವನಕ್ಕೆ ಬೇಕಾದದ್ದನ್ನು ಇವನೇ ಒದಗಿಸಿಕೊಡುತ್ತಿದ್ದನು.",
			"33": "<sup>33</sup> ಅರಸನು ಬರ್ಜಿಲ್ಲೈಗೆ - ನನ್ನ ಜೊತೆಯಲ್ಲಿ ಹೊಳೆಯ ಆಚೆಗೆ ಬಾ; ನಾನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ನಿನ್ನನ್ನು ಹತ್ತಿರವೇ ಇಟ್ಟುಕೊಂಡು ಸಂರಕ್ಷಿಸುತ್ತೇನೆ ಅಂದನು.",
			"34": "<sup>34</sup> ಅದಕ್ಕೆ ಬರ್ಜಿಲ್ಲೈಯು - ಇಷ್ಟು ವಯಸ್ಸು ಕಳೆದವನಾದ ನಾನು ಅರಸನ ಸಂಗಡ ಯೆರೂಸಲೇಮಿಗೆ ಬರುವದು ಹೇಗೆ?",
			"35": "<sup>35</sup> ಈಗ ನಾನು ಎಂಭತ್ತು ವರುಷದವನು. ಒಳ್ಳೇದು, ಕೆಟ್ಟದ್ದು ಇವುಗಳ ಭೇದವು ನನಗಿನ್ನು ತಿಳಿಯುವದೋ? ನಿನ್ನ ಸೇವಕನಾದ ನನಗೆ ಅನ್ನಪಾನಗಳ ರುಚಿಯು ಗೊತ್ತಾಗುವದೋ? ಗಾಯನ ಮಾಡುವ ಸ್ತ್ರೀಪುರುಷರ ಸ್ವರಗಳು ನನಗೆ ಕೇಳಿಸುತ್ತವೋ? ನಾನು ನನ್ನ ಒಡೆಯನಾದ ಅರಸನಿಗೆ ಸುಮ್ಮನೆ ಯಾಕೆ ಭಾರವಾಗಿರಬೇಕು?",
			"36": "<sup>36</sup> ಹೊಳೆದಾಟಿ ಸ್ವಲ್ಪದೂರಕ್ಕೆ ನಿನ್ನ ಸಂಗಡ ಬರುತ್ತೇನೆ. ಅರಸನಾದ ನೀನು ನನಗೇಕೆ ಇಂಥ ಉಪಕಾರ ಮಾಡಬೇಕು?",
			"37": "<sup>37</sup> ದಯವಿಟ್ಟು ನಿನ್ನ ಸೇವಕನಾದ ನನಗೆ ಹಿಂದಿರುಗಿ ಹೋಗುವದಕ್ಕೆ ಅಪ್ಪಣೆಯಾಗಲಿ; ನಾನು ನನ್ನ ತಂದೆತಾಯಿಗಳ ಸಮಾಧಿಯಿರುವ ಸ್ವಂತ ಊರಿಗೆ ಹೋಗಿ ಅಲ್ಲೇ ಸಾಯುವೆನು. ಇಗೋ ಇಲ್ಲಿ ನಿನ್ನ ಸೇವಕನಾದ ಕಿಮ್ಹಾಮನಿರುತ್ತಾನೆ. ನನ್ನ ಒಡೆಯನಾದ ಅರಸನು ಇವನನ್ನು ಕರಕೊಂಡುಹೋಗಿ ತನ್ನ ಇಷ್ಟವಿದ್ದಂತೆ ಇವನಿಗೆ ದಯೆತೋರಿಸಲಿ ಎಂದನು.",
			"38": "<sup>38</sup> ಅದಕ್ಕೆ ಅರಸನು - ಕಿಮ್ಹಾಮನು ನನ್ನ ಸಂಗಡ ಬರಲಿ; ಅವನಿಗೆ ನಿನ್ನ ಇಷ್ಟದಂತೆ ದಯೆ ತೋರಿಸುವೆನು. ಇನ್ನೂ ಏನೇನು ಮಾಡಬೇಕನ್ನುತ್ತೀಯೋ ಅದೆಲ್ಲವನ್ನೂ ಮಾಡುತ್ತೇನೆ ಎಂದು ಹೇಳಿದನು.",
			"39": "<sup>39</sup> ಎಲ್ಲಾ ಜನರೂ ಹೊಳೆದಾಟಿದರು. ಅರಸನು ಹೊಳೆದಾಟಿದ ಮೇಲೆ ಬರ್ಜಿಲ್ಲೈಯನ್ನು ಮುದ್ದಿಟ್ಟು ಆಶೀರ್ವದಿಸಿದನು. ಅನಂತರ ಬರ್ಜಿಲ್ಲೈಯು ತನ್ನ ಊರಿಗೆ ಹೋದನು.",
			"40": "<sup>40</sup> ಅರಸನು ಕಿಮ್ಹಾಮನೊಡನೆ ಗಿಲ್ಗಾಲಿಗೆ ತೆರಳಿದನು. ಎಲ್ಲಾ ಯೆಹೂದ್ಯರೂ ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಅರ್ಧಜನರೂ ಅರಸನನ್ನು ಕರಕೊಂಡು ಹೋದರು.",
			"41": "<sup>41</sup> ಆಗ ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರೂ ಅರಸನ ಮುಂದೆ ಬಂದು ಅವನನ್ನು - ನಮ್ಮ ಸಹೋದರರಾದ ಯೆಹೂದ್ಯರು ನಮಗೆ ತಿಳಿಸದೆ ತಾವೇ ಹೋಗಿ ಅರಸನನ್ನೂ ಅವನ ಮನೆಯವರನ್ನೂ ಅವನೊಡನೆ ಇದ್ದ ಜನರನ್ನೂ ಯೊರ್ದನ್‍ಹೊಳೆ ದಾಟಿಸಿ ಕರಕೊಂಡು ಬಂದದ್ದೇಕೆ ಎಂದು ಕೇಳಿದರು.",
			"42": "<sup>42</sup> ಅದಕ್ಕೆ ಯೆಹೂದ್ಯರೆಲ್ಲರೂ - ಅರಸನು ನಮಗೆ ಸಮೀಪಬಂಧು; ನೀವು ಅದಕ್ಕಾಗಿ ಉರಿಗೊಳ್ಳುವದೇಕೆ? ನಾವೇನು ಅರಸನ ಖರ್ಚಿನಿಂದ ಊಟಮಾಡಿದೆವೋ? ಅವನು ನಮಗೆ ಏನಾದರೂ ಕೊಟ್ಟನೋ ಎಂದು ಉತ್ತರಕೊಟ್ಟರು.",
			"43": "<sup>43</sup> ಆಗ ಇಸ್ರಾಯೇಲ್ಯರು ಯೆಹೂದ್ಯರಿಗೆ - ಅರಸನಲ್ಲಿ ನಮಗೆ ಹತ್ತುಪಾಲು ಉಂಟಲ್ಲಾ; ದಾವೀದನ ವಿಷಯದಲ್ಲಿಯೂ ನಮಗೆ ಹೆಚ್ಚು ಹಕ್ಕು ಉಂಟು. ಹೀಗಿರುವದರಿಂದ ನೀವು ನಮ್ಮನ್ನು ತಿರಸ್ಕರಿಸಿದ್ದೇಕೆ? ನಮ್ಮ ಅರಸನನ್ನು ಕರಕೊಂಡು ಬರುವ ವಿಷಯದಲ್ಲಿ ಮೊದಲು ಮಾತಾಡಿದವರು ನಾವಲ್ಲವೋ ಅಂದರು. ಇಸ್ರಾಯೇಲ್ಯರ ಮಾತುಗಳಿಗಿಂತ ಯೆಹೂದ್ಯರ ಮಾತುಗಳು ಕಠಿಣವಾಗಿದ್ದವು."
		},
		"20": {
			"1": "<sup>1</sup> ಅಕಸ್ಮಾತ್ತಾಗಿ ಬೆನ್ಯಾಮೀನ್‍ಕುಲದವನೂ ಬಿಕ್ರೀಯ ಮಗನೂ ಆದ ಶೆಬನೆಂಬ ಒಬ್ಬ ದುಷ್ಟ ಮನುಷ್ಯನು ಅಲ್ಲಿ ಹೇಗೋ ಬಂದಿದ್ದನು. ಅವನು ತುತೂರಿಯನ್ನು ಊದಿ - ಇಸ್ರಾಯೇಲ್ಯರೇ, ದಾವೀದನಲ್ಲಿ ನಮಗೆ ಪಾಲಿಲ್ಲ; ಇಷಯನ ಮಗನಲ್ಲಿ ನಮಗೆ ಸ್ವಾಸ್ತ್ಯವಿಲ್ಲ. ಪ್ರತಿಯೊಬ್ಬನೂ ನಮ್ಮ ನಮ್ಮ ನಿವಾಸಗಳಿಗೆ ಹೋಗೋಣ ಅಂದನು.",
			"2": "<sup>2</sup> ಆಗ ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರೂ ದಾವೀದನನ್ನು ಬಿಟ್ಟು ಬಿಕ್ರೀಯ ಮಗನಾದ ಶೆಬನನ್ನು ಹಿಂಬಾಲಿಸಿದರು. ಆದರೆ ಯೆಹೂದ್ಯರು ತಮ್ಮ ಅರಸನನ್ನು ಅಂಟಿಕೊಂಡು ಯೊರ್ದನ್‍ತಗ್ಗಿನಿಂದ ಯೆರೂಸಲೇಮಿನವರೆಗೂ ಅವನ ಜೊತೆಯಲ್ಲಿ ಹೋದರು.",
			"3": "<sup>3</sup> ಅರಸನಾದ ದಾವೀದನು ಯೆರೂಸಲೇಮಿಗೆ ಬಂದ ಮೇಲೆ ತಾನು ಮನೆಕಾಯುವದಕ್ಕೋಸ್ಕರ ಇಟ್ಟಿದ್ದ ತನ್ನ ಹತ್ತು ಮಂದಿ ಉಪಪತ್ನಿಯರನ್ನು ಪ್ರತ್ಯೇಕವಾದ ಒಂದು ಮನೆಯಲ್ಲಿಟ್ಟು ಅವರಿಗೆ ಅನ್ನವಸ್ತ್ರಕೊಡುತ್ತಿದ್ದನು; ಆದರೆ ಅವರನ್ನು ತಿರಿಗಿ ಕೂಡಲಿಲ್ಲ. ಅವರು ಜೀವದಿಂದಿರುವವರೆಗೂ ವಿಧವೆಯರಂತಿದ್ದು ಕಾವಲಲ್ಲಿರಬೇಕಾಯಿತು.",
			"4": "<sup>4</sup> ಅನಂತರ ಅರಸನು ಅಮಾಸನಿಗೆ - ನೀನು ಮೂರು ದಿನಗಳೊಳಗಾಗಿ ಯೆಹೂದ್ಯರನ್ನು ಕೂಡಿಸಿಕೊಂಡು ಇಲ್ಲಿ ಸಿದ್ಧವಾಗಿರು ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"5": "<sup>5</sup> ಅವನು ಹೋಗಿ ಯೆಹೂದ್ಯರನ್ನು ಕೂಡಿಸಿದನು. ಆದರೆ ಅವನು ನೇಮಕವಾದ ಸಮಯಕ್ಕೆ ಬಾರದೆ ತಡಮಾಡಿದ್ದರಿಂದ ಅರಸನು ಅಬೀಷೈಗೆ -",
			"6": "<sup>6</sup> ಅಬ್ಷಾಲೋಮನಿಗಿಂತ ಬಿಕ್ರೀಯ ಮಗನಾದ ಶೆಬನು ನಮಗೆ ಹೆಚ್ಚಿನ ಕೇಡು ಮಾಡುವಂತಿದೆ. ಆದದರಿಂದ ನೀನು ನನ್ನ ಸ್ವಂತ ಆಳುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ಅವನನ್ನು ಹಿಂದಟ್ಟು; ಇಲ್ಲವಾದರೆ ಅವನು ನಮ್ಮ ಕಣ್ಣಿಗೆ ಮರೆಯಾಗಿ ಹೋಗಿ ಕೋಟೆ ಕೊತ್ತಲುಗಳುಳ್ಳ ಪಟ್ಟಣಗಳನ್ನು ಸೇರಿಕೊಂಡಾನು ಅಂದನು.",
			"7": "<sup>7</sup> ಆಗ ಅವನು ಯೋವಾಬನ ಸೈನಿಕರನ್ನೂ ಕೆರೇತ್ಯ, ಪೆಲೇತ್ಯ ಎಂಬ ಕಾವಲುದಂಡುಗಳನ್ನೂ ಎಲ್ಲಾ ಶೂರರನ್ನೂ ಕರಕೊಂಡು ಬಿಕ್ರೀಯ ಮಗನಾದ ಶೆಬನನ್ನು ಹಿಂದಟ್ಟುವದಕ್ಕೋಸ್ಕರ ಯೆರೂಸಲೇಮಿನಿಂದ ಹೊರಟನು.",
			"8": "<sup>8</sup> ಅವರು ಗಿಬ್ಯೋನಿನಲ್ಲಿದ್ದ ದೊಡ್ಡ ಕಲ್ಲನ್ನು ಸಮೀಪಿಸಿದಾಗ ಅಮಾಸನು ಅವರೆದುರಿಗೆ ಬಂದನು. ಯುದ್ಧ ವಸ್ತ್ರಗಳನ್ನು ಧರಿಸಿಕೊಂಡಿದ್ದ ಯೋವಾಬನು ಅವನನ್ನು ವಂದಿಸುವದಕ್ಕೆ ಹೋಗುತ್ತಿರುವಾಗ ಅವನ ಸೊಂಟಕ್ಕೆ ಕಟ್ಟಿದ್ದ ಕತ್ತಿಯು ಒರೆಯಿಂದ ಕೆಳಗೆ ಬಿದ್ದಿತು.",
			"9": "<sup>9</sup> ಅವನು ಅಮಾಸನನ್ನು - ಸಹೋದರನೇ, ಕ್ಷೇಮವೋ ಎಂದು ಕೇಳಿ ಮುದ್ದಿಡುವದಕ್ಕೋಸ್ಕರವೋ ಎಂಬಂತೆ ಬಲಗೈಯಿಂದ ಅವನ ಗಡ್ಡವನ್ನು ಹಿಡಿದು",
			"10": "<sup>10</sup> ಕೈಯಲ್ಲಿದ್ದ ಕತ್ತಿಯನ್ನು ಗಮನಿಸದಿದ್ದ ಅಮಾಸನನ್ನು ಹೊಟ್ಟೆಯೊಳಗೆ ತಿವಿದನು. ಅದರಿಂದ ಅವನ ಕರುಳುಗಳೆಲ್ಲಾ ಹೊರಗೆ ಬಂದವು. ಎರಡನೆಯ ಸಾರಿ ತಿವಿಯಲಿಲ್ಲ; ಅಷ್ಟಕ್ಕೆ ಅವನು ಸತ್ತನು. ಯೋವಾಬನೂ ಅವನ ತಮ್ಮನಾದ ಅಬೀಷೈಯೂ ಬಿಕ್ರೀಯ ಮಗನಾದ ಶೆಬನನ್ನು ಹಿಂದಟ್ಟುವದಕ್ಕೋಸ್ಕರ ಮುಂದೆ ನಡೆದಾಗ",
			"11": "<sup>11</sup> ಯೋವಾಬನ ಆಳುಗಳಲ್ಲೊಬ್ಬನು ಅಮಾಸನ ಹತ್ತಿರ ನಿಂತು - ದಾವೀದ ಯೋವಾಬರ ಕಡೆಯವರು ಯೋವಾಬನನ್ನು ಹಿಂಬಾಲಿಸಲಿ ಎಂದು ಕೂಗಿದನು.",
			"12": "<sup>12</sup> ಮಾರ್ಗದ ಮಧ್ಯದಲ್ಲಿ ರಕ್ತದೊಳಗೆ ಹೊರಳಾಡುತ್ತಿದ್ದ ಅಮಾಸನನ್ನು ನೋಡುತ್ತಾ ಎಲ್ಲಾ ಜನರೂ ನಿಂತರು. ಬಂದವರೆಲ್ಲರೂ ಅಲ್ಲೇ ನಿಲ್ಲುವದನ್ನು ಆ ಮನುಷ್ಯನು ಕಂಡು ಅಮಾಸನ ಶವವನ್ನು ದಾರಿಯಿಂದ ಎಳೆದು ಹೊಲದಲ್ಲಿ ಹಾಕಿ ಅದನ್ನು ಬಟ್ಟೆಯಿಂದ ಮುಚ್ಚಿದನು.",
			"13": "<sup>13</sup> ಶವವನ್ನು ಆಚೆ ಹಾಕಿದನಂತರ ಜನರೆಲ್ಲರೂ ಬಿಕ್ರೀಯ ಮಗನಾದ ಶೆಬನನ್ನು ಹಿಂದಟ್ಟುವದಕ್ಕೋಸ್ಕರ ಯೋವಾಬನನ್ನು ಹಿಂಬಾಲಿಸಿದರು.",
			"14": "<sup>14</sup> ಶೆಬನು ಇಸ್ರಾಯೇಲ್‍ಪ್ರಾಂತಗಳಲ್ಲಿ ಸಂಚರಿಸುತ್ತಾ ಆಬೇಲ್ಬೇತ್ಮಾಕಾ ಎಂಬಲ್ಲಿಗೆ ಬಂದನು. ಬೇರಿಯರೆಲ್ಲರೂ ಕೂಡಿಕೊಂಡು ಅವನೊಡನೆ ಆ ಊರನ್ನು ಸೇರಿದರು.",
			"15": "<sup>15</sup> ಯೋವಾಬನ ಕಡೆಯವರು ಆಬೇಲ್ಬೇತ್ಮಾಕಾ ಊರಿಗೆ ಮುತ್ತಿಗೆಹಾಕಿ ಅದಕ್ಕೆ ಎದುರಾಗಿ ಊರುಗೋಡೆಗೆ ತಗಲುವ ಒಂದು ಮಣ್ಣಿನ ದಿಬ್ಬವನ್ನು ಮಾಡಿ ಗೋಡೆಯನ್ನು ಕೆಡವಿಬಿಡುವದಕ್ಕೆ ಪ್ರಯತ್ನಿಸಿದರು.",
			"16": "<sup>16</sup> ಆಗ ಆ ಊರಿನ ಬುದ್ಧಿವಂತೆಯಾದ ಒಬ್ಬ ಹೆಂಗಸು ಅವರಿಗೆ - ಕೇಳಿರಿ, ದಯವಿಟ್ಟು ಕೇಳಿರಿ; ಯೋವಾಬನು ಮಾತಾಡುವದಕ್ಕೋಸ್ಕರ ನನ್ನ ಬಳಿಗೆ ಬರಬೇಕೆಂದು ಹೇಳಿರಿ ಎಂದು ಕೂಗಿದಳು.",
			"17": "<sup>17</sup> ಯೋವಾಬನು ಬರಲು ಆ ಸ್ತ್ರೀಯು - ನೀನು ಯೋವಾಬನೋ ಎಂದು ಅವನನ್ನು ಕೇಳಿದಳು. ಅವನು - ಹೌದು, ನಾನೇ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು. ಆಕೆಯು - ನಿನ್ನ ದಾಸಿಯ ಮಾತನ್ನು ಕೇಳು ಅನ್ನಲು ಅವನು - ಕೇಳುತ್ತೇನೆ ಅಂದನು.",
			"18": "<sup>18</sup> ಆಗ ಆಕೆಯು - ಪೂರ್ವಕಾಲದಲ್ಲಿ ಜನರು ಆಬೇಲಿನವರ ಆಲೋಚನೆಯನ್ನು ಕೇಳೋಣ ಅಂದುಕೊಂಡು ಅವರ ಆಲೋಚನೆಯ ಮೇರೆಗೆ ತೀರ್ಮಾನಮಾಡಿಕೊಳ್ಳುತ್ತಿದ್ದರು.",
			"19": "<sup>19</sup> ನಮ್ಮ ಪಟ್ಟಣವು ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಸಮಾಧಾನವುಳ್ಳದ್ದೂ ರಾಜನಿಷ್ಠೆಯುಳ್ಳದ್ದೂ ಆಗಿದೆ. ನೀನು ಇಸ್ರಾಯೇಲ್ಯರ ಪಟ್ಟಣಗಳಲ್ಲಿ ತಾಯಿಯೆಂದು ಹೆಸರುಗೊಂಡ ಈ ಪಟ್ಟಣವನ್ನು ಹಾಳುಮಾಡಬೇಕೆಂದಿದ್ದೀ; ನೀನು ಯೆಹೋವನ ಸ್ವಾಸ್ತ್ಯವನ್ನು ನುಂಗಿಬಿಡುವದೇಕೆ ಅಂದಳು.",
			"20": "<sup>20</sup> ಅದಕ್ಕೆ ಯೋವಾಬನು - ನುಂಗಿಬಿಡುವದಾಗಲಿ ಹಾಳುಮಾಡುವದಾಗಲಿ ನನಗೆ ದೂರವಾಗಿರಲಿ; ಅಂಥದು ಬೇಡವೇ ಬೇಡ.",
			"21": "<sup>21</sup> ಸಂಗತಿಯು ಹೀಗಿರುವದಿಲ್ಲ; ಎಫ್ರಾಯೀಮ್ ಪರ್ವತಪ್ರದೇಶದ ಬಿಕ್ರೀಯ ಮಗನಾದ ಶೆಬನೆಂಬವನು ಅರಸನಾದ ದಾವೀದನಿಗೆ ವಿರೋಧವಾಗಿ ಕೈಯೆತ್ತಿದ್ದಾನೆ. ಅವನನ್ನೇ ಒಪ್ಪಿಸಿರಿ; ನಾನು ಊರನ್ನು ಬಿಟ್ಟುಹೋಗುತ್ತೇನೆ ಎಂದು ಹೇಳಿದನು. ಅದಕ್ಕೆ ಸ್ತ್ರೀಯು - ನೋಡು, ಈಗಲೇ ಅವನ ತಲೆಯು ಗೋಡೆಯಕಂಡಿಯಿಂದ ನಿನ್ನ ಕಡೆಗೆ ಒಗೆಯಲ್ಪಡುವದು ಎಂದು ಉತ್ತರಕೊಟ್ಟು",
			"22": "<sup>22</sup> ತನ್ನ ಜನರ ಬಳಿಗೆ ಹೋಗಿ ಅವರಿಗೆ ವಿವೇಕದೊಡನೆ ಈ ಸಂಗತಿಯನ್ನು ತಿಳಿಸಿದಳು. ಅವರು ಬಿಕ್ರೀಯ ಮಗನಾದ ಶೆಬನ ತಲೆಯನ್ನು ಕಡಿದು ಯೋವಾಬನಿದ್ದಲ್ಲಿಗೆ ಬಿಸಾಡಿದರು. ಕೂಡಲೆ ಅವನು ತುತೂರಿಯನ್ನು ಊದಿಸಿದನು; ಜನರು ಆ ಪಟ್ಟಣವನ್ನು ಬಿಟ್ಟು ತಮ್ಮ ತಮ್ಮ ನಿವಾಸಗಳಿಗೆ ಹೋದರು. ಯೋವಾಬನು ಹಿಂದಿರುಗಿ ಯೆರೂಸಲೇಮಿನಲ್ಲಿದ್ದ ಅರಸನ ಬಳಿಗೆ ಬಂದನು.",
			"23": "<sup>23</sup> ಯೋವಾಬನು ಇಸ್ರಾಯೇಲ್ಯರ ಮುಖ್ಯ ಸೇನಾಪತಿ; ಯೆಹೋಯಾದಾವನ ಮಗನಾದ ಬೆನಾಯಾಯನು ಕೆರೇತ್ಯ, ಪೆಲೇತ್ಯ ಎಂಬ ಕಾವಲುದಂಡುಗಳ ಅಧಿಪತಿ.",
			"24": "<sup>24</sup> ಅದೋರಾಮನು ಬಿಟ್ಟೀಕೆಲಸ ಮಾಡಿಸುವವನು. ಅಹೀಲೂದನ ಮಗನಾದ ಯೆಹೋಷಾಫಾಟನು ಪ್ರಧಾನಮಂತ್ರಿಯು.",
			"25": "<sup>25</sup> ಶೆವನು ಲೇಖಕನು; ಚಾದೋಕ್ ಎಬ್ಯಾತಾರರು ಯಾಜಕರು;",
			"26": "<sup>26</sup> ಯಾಯೀರಿನವನಾದ ಈರನೂ ದಾವೀದನ ಯಾಜಕನು."
		},
		"21": {
			"1": "<sup>1</sup> ದಾವೀದನ ಕಾಲದಲ್ಲಿ ಮೂರು ವರುಷಗಳವರೆಗೂ ಬಿಡದೆ ಬರವಿತ್ತು. ದಾವೀದನು ಯೆಹೋವನನ್ನು ವಿಚಾರಿಸಲು ಆತನು - ಸೌಲನು ಗಿಬ್ಯೋನ್ಯರನ್ನು ಕೊಲ್ಲಿಸಿದ್ದರಿಂದ ಅವನ ಮೇಲೆಯೂ ಅವನ ಮನೆಯವರ ಮೇಲೆಯೂ ರಕ್ತಾಪರಾಧವಿರುತ್ತದೆ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"2": "<sup>2</sup> (ಗಿಬ್ಯೋನ್ಯರು ಇಸ್ರಾಯೇಲ್‍ಕುಲಗಳಿಗೆ ಸೇರಿದವರಲ್ಲ; ಸಂಹೃತರಾಗದೆ ಉಳಿದ ಅಮೋರಿಯರಷ್ಟೆ. ಇಸ್ರಾಯೇಲ್ಯರು ತಾವು ಇವರನ್ನು ಕೊಲ್ಲುವದಿಲ್ಲವೆಂದು ಪ್ರಮಾಣ ಮಾಡಿದ್ದರು. ಆದರೂ ಸೌಲನು ಇಸ್ರಾಯೇಲ್ ಮತ್ತು ಯೆಹೂದಕುಲಗಳ ಮೇಲೆ ತನಗಿದ್ದ ಅಭಿಮಾನದ ದೆಸೆಯಿಂದ ಇವರನ್ನೂ ನಿರ್ನಾಮಗೊಳಿಸಬೇಕೆಂದಿದ್ದನು.)",
			"3": "<sup>3</sup> ಆಗ ಅರಸನಾದ ದಾವೀದನು ಗಿಬ್ಯೋನ್ಯರನ್ನು ಕರೆದು - ನಾನು ನಿಮಗೋಸ್ಕರ ಏನು ಮಾಡಬೇಕೆನ್ನುತ್ತೀರಿ? ನಿಮಗೆ ಪ್ರಾಯಶ್ಚಿತ್ತವಾಗಿ ಯಾವದನ್ನು ಕೊಟ್ಟರೆ ನೀವು ಯೆಹೋವನ ಸ್ವಾಸ್ತ್ಯವನ್ನು ಆಶೀರ್ವದಿಸುವಿರಿ ಎಂದು ಕೇಳಿದನು.",
			"4": "<sup>4</sup> ಅದಕ್ಕೆ ಅವರು - ಸೌಲನ ಸಂತಾನದವರಿಗೂ ನಮಗೂ ಇರುವ ವ್ಯಾಜ್ಯವು ಬೆಳ್ಳಿಬಂಗಾರದಿಂದ ತೀರಲಾರದು; ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಕೊಲ್ಲುವ ಅಧಿಕಾರವು ನಮಗಿಲ್ಲ ಎಂದು ಉತ್ತರ ಕೊಡಲು ಅರಸನು ಅವರಿಗೆ - ಏನು ಮಾಡಬೇಕು ಹೇಳಿರಿ, ಮಾಡುತ್ತೇನೆ ಅಂದನು.",
			"5": "<sup>5</sup> ಅವರು - ನಮ್ಮನ್ನು ಇಸ್ರಾಯೇಲ್‍ಪ್ರಾಂತಗಳಿಂದ ಹೊರಡಿಸುವದಕ್ಕೂ ನಿರ್ನಾಮಗೊಳಿಸುವದಕ್ಕೂ ಪ್ರಯತ್ನಿಸಿದ",
			"6": "<sup>6</sup> ಆ ಮನುಷ್ಯನ ಮಕ್ಕಳಲ್ಲಿ ಏಳು ಮಂದಿಯನ್ನು ನಮಗೆ ಒಪ್ಪಿಸು. ಯೆಹೋವನಿಂದ ಆರಿಸಲ್ಪಟ್ಟ ಸೌಲನು ವಾಸವಾಗಿದ್ದ ಗಿಬೆಯದಲ್ಲಿ ನಾವು ಅವರನ್ನು ಕೊಂದು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನೇತುಹಾಕುವೆವು ಎಂದರು. ಅರಸನು - ಆಗಲಿ, ಒಪ್ಪಿಸುತ್ತೇನೆ ಅಂದನು.",
			"7": "<sup>7</sup> ದಾವೀದನು ತಾನು ಸೌಲನ ಮಗನಾದ ಯೋನಾತಾನನಿಗೆ ಯೆಹೋವನ ಹೆಸರಿನಲ್ಲಿ ಮಾಡಿದ ಪ್ರಮಾಣವನ್ನು ನೆನಸಿ ಸೌಲನ ಮೊಮ್ಮಗನೂ ಯೋನಾತಾನನ ಮಗನೂ ಆದ ಮೆಫೀಬೋಶೆತನನ್ನು ಉಳಿಸಿದನು.",
			"8": "<sup>8</sup> ಆದರೆ ಸೌಲನಿಗೆ ಅಯ್ಯಾಹನ ಮಗಳಾದ ರಿಚ್ಪಳಲ್ಲಿ ಹುಟ್ಟಿದ ಅರ್ಮೋನೀ, ಮೆಫೀಬೋಶೆತ್ ಎಂಬ ಇಬ್ಬರು ಮಕ್ಕಳನ್ನೂ ಮೆಹೋಲದ ಬರ್ಜಿಲ್ಲೈಯ ಮಗನಾದ ಅದ್ರೀಯೇಲನಿಗೆ ಸೌಲನ ಮಗಳಾದ ಮೇರಬಳಲ್ಲಿ ಹುಟ್ಟಿದ ಐದು ಮಂದಿ ಮಕ್ಕಳನ್ನೂ ತೆಗೆದುಕೊಂಡು ಅವರನ್ನು ಗಿಬ್ಯೋನ್ಯರಿಗೆ ಒಪ್ಪಿಸಿದನು.",
			"9": "<sup>9</sup> ಅವರು ಇವರನ್ನು ಕೊಂದು ಗುಡ್ಡದ ಮೇಲೆ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನೇತುಹಾಕಿದರು. ಈ ಏಳು ಮಂದಿಯೂ ಏಕಕಾಲದಲ್ಲಿ ಹತರಾದರು. ಇವರನ್ನು ಕೊಂದಾಗ ಜವೆಗೋದಿ ಸುಗ್ಗಿಯು ಆರಂಭವಾಗಿತ್ತು.",
			"10": "<sup>10</sup> ಆಗ ಅಯ್ಯಾಹನ ಮಗಳಾದ ರಿಚ್ಪಳು ಒಂದು ಗೋಣಿತಟ್ಟನ್ನು ತೆಗೆದುಕೊಂಡು ಅದನ್ನು ಬಂಡೆಯು ಮೇಲೆ ಹಾಸಿ ಸುಗ್ಗಿಯ ಆರಂಭದಿಂದ ಶವಗಳ ಮೇಲೆ ಮಳೆ ಬೀಳುವ ತನಕ ಅದರ ಮೇಲೆ ಕೂತುಕೊಂಡು ಹಗಲಿನಲ್ಲಿ ಆಕಾಶದ ಪಕ್ಷಿಗಳೂ ಇರುಳಿನಲ್ಲಿ ಕಾಡುಮೃಗಗಳೂ ಅವುಗಳನ್ನು ತಿನ್ನದಂತೆ ಕಾಯುತ್ತಿದ್ದಳು.",
			"11": "<sup>11</sup> ಅಯ್ಯಾಹನ ಮಗಳೂ ಸೌಲನ ಉಪಪತ್ನಿಯೂ ಆದ ರಿಚ್ಪಳು ಮಾಡಿದ ಈ ಕಾರ್ಯವು ದಾವೀದನಿಗೆ ತಿಳಿಸಲ್ಪಟ್ಟಿತು.",
			"12": "<sup>12</sup> ಆಗ ಅವನು ಸೌಲನ ಮತ್ತು ಅವನ ಮಗನಾದ ಯೋನಾತಾನನ ಎಲುಬುಗಳನ್ನು ತರುವದಕ್ಕೋಸ್ಕರ ಯಾಬೇಷ್‍ಗಿಲ್ಯಾದಿಗೆ ಹೋದನು. ಫಿಲಿಷ್ಟಿಯರು ಸೌಲನನ್ನು ಗಿಲ್ಬೋವದಲ್ಲಿ ಸೋಲಿಸಿದ ನಂತರ ಅವನ ಮತ್ತು ಯೋನಾತಾನನ ಶವಗಳನ್ನು ಬೇತ್ಷೆಯಾನಿನ ಬೀದಿಗಳಲ್ಲಿ ತೂಗಹಾಕಿದ್ದರು. ಯಾಬೇಷ್‍ಗಿಲ್ಯಾದಿನವರು ಅಲ್ಲಿಂದ ಅವುಗಳನ್ನು ಕದ್ದುಕೊಂಡು ಹೋಗಿದ್ದರು.",
			"13": "<sup>13</sup> ದಾವೀದನು ಯಾಬೇಷಿನಲ್ಲಿದ್ದ ಸೌಲಯೋನಾತಾನರ ಎಲುಬುಗಳನ್ನೂ ಗಿಬ್ಯೋನಿನಲ್ಲಿ ಹತರಾದ ಆ ಮನುಷ್ಯರ ಎಲುಬುಗಳನ್ನೂ ತೆಗೆದುಕೊಂಡು",
			"14": "<sup>14</sup> ಅವುಗಳನ್ನೆಲ್ಲಾ ಬೆನ್ಯಾಮೀನ್ ದೇಶದ ಚೇಲಾ ಊರಿನಲ್ಲಿ ಸೌಲನ ತಂದೆಯಾದ ಕೀಷನ ಶ್ಮಶಾನಭೂಮಿಯಲ್ಲಿ ಸಮಾಧಿಮಾಡಿಸಿದನು. ಅರಸನ ಅಪ್ಪಣೆಯಂತೆ ಇದೆಲ್ಲಾ ಆದನಂತರ ದೇಶದ ಮೇಲೆ ದೇವರಿಗಿದ್ದ ಕೋಪವು ಶಾಂತಿಯಾಯಿತು.",
			"15": "<sup>15</sup> ಒಂದು ಕಾಲದಲ್ಲಿ ಫಿಲಿಷ್ಟಿಯರಿಗೂ ಇಸ್ರಾಯೇಲ್ಯರಿಗೂ ತಿರಿಗಿ ಯುದ್ಧವಾಯಿತು. ದಾವೀದನು ತನ್ನ ಸೈನಿಕರನ್ನು ಕರಕೊಂಡು ಫಿಲಿಷ್ಟಿಯರಿಗೆ ವಿರೋಧವಾಗಿ ಯುದ್ಧಕ್ಕೆ ಹೋದಾಗ ಬಹಳವಾಗಿ ದಣಿದಂಥ ಅವನನ್ನು",
			"16": "<sup>16</sup> ರೆಫಾಯರಲ್ಲೊಬ್ಬನಾದ ಇಷ್ಬೀಬೆನೋಬ್ ಎಂಬವನು ಕೊಲ್ಲುವದಕ್ಕಿದ್ದನು. ಅವನ ಬರ್ಜಿಯ ತಾಮ್ರವು ಮುನ್ನೂರು ರೂಪಾಯಿ ತೂಕದ್ದು; ಅವನು ಸೊಂಟಕ್ಕೆ ಹೊಸ [ಕತ್ತಿಯನ್ನು] ಕಟ್ಟಿಕೊಂಡಿದ್ದನು.",
			"17": "<sup>17</sup> ಚೆರೂಯಳ ಮಗನಾದ ಅಬೀಷೈಯು ದಾವೀದನ ಸಹಾಯಕ್ಕೆ ಬಂದು ಫಿಲಿಷ್ಟಿಯನನ್ನು ಕೊಂದುಹಾಕಿದನು. ಆಗ ಜನರು ದಾವೀದನಿಗೆ - ಇಸ್ರಾಯೇಲ್ಯರ ದೀಪವು ಆರಿಹೋಗದಂತೆ ನೀನು ಇನ್ನು ಮುಂದೆ ನಮ್ಮ ಜೊತೆಯಲ್ಲಿ ಯುದ್ಧಕ್ಕೆ ಬರಬಾರದು ಎಂದು ಖಂಡಿತವಾಗಿ ಹೇಳಿದರು.",
			"18": "<sup>18</sup> ಅನಂತರ ಫಿಲಿಷ್ಟಿಯರ ಸಂಗಡ ಗೋಬಿನಲ್ಲಿ ಯುದ್ಧನಡೆಯಿತು. ಆಗ ಹುಷಾ ಊರಿನವನಾದ ಸಿಬ್ಬೆಕೈ ಎಂಬವನು ರೆಫಾಯನಾದ ಸಫ್ ಎಂಬವನನ್ನು ಕೊಂದನು.",
			"19": "<sup>19</sup> ಗೋಬಿನಲ್ಲಿ ಫಿಲಿಷ್ಟಿಯರೊಡನೆ ಇನ್ನೊಮ್ಮೆ ಯುದ್ಧ ನಡೆದಾಗ ಬೇತ್ಲೆಹೇಮಿನವನಾದ ಯಾರೇ ಯೋರೆಗೀಮ್ ಎಂಬವನ ಮಗನಾದ ಎಲ್ಹಾನಾನನು ಗಿತ್ತೀಯನಾದ ಗೊಲ್ಯಾತನನ್ನು ಕೊಂದನು. ಆ ಗೊಲ್ಯಾತನ ಬರ್ಜಿಯ ಹಿಡಿಕೆಯು ನೇಯಿಗಾರರ ಕುಂಟೆಯಷ್ಟು ಗಾತ್ರವಿತ್ತು.",
			"20": "<sup>20</sup> ಮತ್ತೊಂದು ಸಾರಿ ಗತ್ ಊರಿನಲ್ಲಿ ಯುದ್ಧ ನಡೆಯಿತು. ಅಲ್ಲಿ ಒಬ್ಬ ಎತ್ತರವಾದ ಪುರುಷನಿದ್ದನು. ಅವನ ಕೈಕಾಲುಗಳಿಗೆ ಆರಾರು ಬೆರಳುಗಳಂತೆ ಒಟ್ಟಿಗೆ ಇಪ್ಪತ್ತನಾಲ್ಕು ಬೆರಳುಗಳಿದ್ದವು. ಅವನೂ ರೆಫಾಯನು.",
			"21": "<sup>21</sup> ಅವನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ನಿಂದಿಸಿದಾಗ ದಾವೀದನ ಅಣ್ಣನಾದ ಶಿಮೆಯಾನನ ಮಗ ಯೋನಾತಾನನು ಅವನನ್ನು ಕೊಂದು ಹಾಕಿದನು.",
			"22": "<sup>22</sup> ಗತ್ ಊರಿನವರಾದ ಈ ನಾಲ್ಕು ಮಂದಿ ರೆಫಾಯರು ದಾವೀದನಿಂದಲೂ ಅವನ ಸೇವಕರಿಂದಲೂ ಹತರಾದರು."
		},
		"22": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ದಾವೀದನನ್ನು ಎಲ್ಲಾ ಶತ್ರುಗಳ ಕೈಗೂ ಸೌಲನ ಕೈಗೂ ಸಿಕ್ಕದಂತೆ ತಪ್ಪಿಸಿದಾಗ ಅವನು ಯೆಹೋವನ ಘನಕ್ಕಾಗಿ ಈ ಪದ್ಯವನ್ನು ರಚಿಸಿ ಹೇಳಿದನು -",
			"2": "<sup>2</sup> ಯೆಹೋವನು ನನ್ನ ಬಂಡೆಯೂ ನನ್ನ ಕೋಟೆಯೂ ನನ್ನ ವಿಮೋಚಕನೂ ಆಗಿದ್ದಾನೆ;",
			"3": "<sup>3</sup> ಆತನು ನನ್ನ ಆಶ್ರಯಗಿರಿಯಾಗಿರುವ ದೇವರೂ ನನ್ನ ಗುರಾಣಿಯೂ ನನ್ನ ರಕ್ಷಣೆಯ ಕೊಂಬೂ ನನ್ನ ದುರ್ಗವೂ ನನ್ನ ಶರಣನೂ ಬಲಾತ್ಕಾರಿಯಿಂದ ನನ್ನನ್ನು ರಕ್ಷಿಸುವವನೂ ಆಗಿದ್ದಾನೆ.",
			"4": "<sup>4</sup> ಯೆಹೋವನು ಸ್ತೋತ್ರಕ್ಕೆ ಅರ್ಹನು; ನಾನು ಆತನಿಗೆ ಮೊರೆಯಿಡಲು ನನ್ನನ್ನು ಶತ್ರುಗಳಿಂದ ರಕ್ಷಿಸುತ್ತಾನೆ.",
			"5": "<sup>5</sup> ಮರಣಪ್ರವಾಹದ ಅಲ್ಲಕಲ್ಲೋಲಗಳು ನನ್ನನ್ನು ಸುತ್ತಿಕೊಂಡವು. ನಾಶಪ್ರವಾಹವು ನನ್ನನ್ನು ನಡುಗಿಸಿತು.",
			"6": "<sup>6</sup> ಪಾತಾಳಪಾಶಗಳು ನನ್ನನ್ನು ಆವರಿಸಿಕೊಂಡವು; ಮರಣಕರವಾದ ಉರ್ಲುಗಳು ನನ್ನೆದುರಿನಲ್ಲಿದ್ದವು.",
			"7": "<sup>7</sup> ಅಂಥ ಕಷ್ಟದಲ್ಲಿ ನಾನು ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಟ್ಟೆನು; ನನ್ನ ದೇವರನ್ನು ಪ್ರಾರ್ಥಿಸಿದೆನು. ಆತನು ತನ್ನ ಮಂದಿರದಲ್ಲಿ ನನ್ನ ಶಬ್ದವನ್ನು ಕೇಳಿದನು; ನನ್ನ ಕೂಗು ಆತನ ಸನ್ನಿಧಿಗೆ ಕೇಳಿಸಿತು.",
			"8": "<sup>8</sup> ಆಗ ಆತನಿಗೆ ಸಿಟ್ಟೇರಿದದರಿಂದ ಭೂಮಿಯು ಗಡಗಡನೆ ಕಂಪಿಸಿತು; ಆಕಾಶದ ಆಧಾರಗಳು ನಡುಗಿ ಕದಲಿದವು.",
			"9": "<sup>9</sup> ಆತನ ಮೂಗಿನಿಂದ ಹೊಗೆಯು ಬಂತು; ಆತನ ಬಾಯಿಂದ ಅಗ್ನಿಜ್ವಾಲೆಹೊರಟು ಸಿಕ್ಕಿದ್ದೆಲ್ಲವನ್ನೂ ದಹಿಸಿ ಕೆಂಡವನ್ನಾಗಿ ಮಾಡಿತು.",
			"10": "<sup>10</sup> ಆತನು ಆಕಾಶವನ್ನು ತಗ್ಗಿಸಿ ಇಳಿದು ಬಂದನು; ಆತನ ಪಾದಗಳ ಕೆಳಗೆ ಕಾರ್ಗತ್ತಲಿತ್ತು.",
			"11": "<sup>11</sup> ಕೆರೂಬಿವಾಹನನಾಗಿ ಹಾರಿ ವಾಯುವೇ ಆತನ ರೆಕ್ಕೆಗಳೋ ಎಂಬಂತೆ ಇಳಿದು ಪ್ರತ್ಯಕ್ಷನಾದನು.",
			"12": "<sup>12</sup> ಕತ್ತಲನ್ನೂ ಜಲಮಯವಾಗಿರುವ ನೀಲಮೇಘಗಳನ್ನೂ ತನ್ನ ಸುತ್ತಲು ಗುಡಾರದಂತೆ ಕವಿಸಿಕೊಂಡನು.",
			"13": "<sup>13</sup> ಆತನ ಸಾನ್ನಿಧ್ಯಪ್ರಕಾಶದಿಂದ ಉರಿಗೆಂಡಗಳು ಹೊರಟವು.",
			"14": "<sup>14</sup> ಯೆಹೋವನು ಆಕಾಶದಿಂದ ಗುಡುಗಿದನು; ಪರಾತ್ಪರನು ಧ್ವನಿಗೊಟ್ಟನು.",
			"15": "<sup>15</sup> ಆತನು ಬಾಣಗಳನ್ನೆಸೆದು ಶತ್ರುಗಳನ್ನು ಚದರಿಸಿ ಬಿಟ್ಟನು; ಸಿಡಿಲಿನಿಂದ ಕಳವಳಗೊಳಿಸಿದನು.",
			"16": "<sup>16</sup> ಆಗ ಯೆಹೋವನ ಗದರಿಕೆಯಿಂದಲೂ ಆತನ ಶ್ವಾಸಭರದಿಂದಲೂ ಸಮುದ್ರದ ತಳವು ಕಾಣಿಸಿತು, ಭೂಮಂಡಲದ ಅಸ್ತಿವಾರಗಳು ತೋರಬಂದವು.",
			"17": "<sup>17</sup> ಆತನು ಮೇಲಣ ಲೋಕದಿಂದ ಕೈಚಾಚಿ ನನ್ನನ್ನು ಹಿಡಿದು ಮಹಾಜಲರಾಶಿಗಳೊಳಗಿಂದ ಎಳೆದನು.",
			"18": "<sup>18</sup> ನನಗಿಂತ ಬಲಿಷ್ಠರೂ ಪುಷ್ಟರೂ ಆಗಿ ದ್ವೇಷಿಸುತ್ತಿದ್ದ ಶತ್ರುಗಳಿಂದ ನನ್ನನ್ನು ಬಿಡಿಸಿ ರಕ್ಷಿಸಿದನು.",
			"19": "<sup>19</sup> ಅವರು ನನ್ನ ವಿಪತ್ಕಾಲದಲ್ಲಿ ನನ್ನ ಮೇಲೆ ಬಿದ್ದರು; ಆಗ ಯೆಹೋವನು ನನಗೆ ಉದ್ಧಾರಕನಾದನು.",
			"20": "<sup>20</sup> ಆತನು ನನ್ನನ್ನು ಬಿಡಿಸಿ ವಿಶಾಲಸ್ಥಳದಲ್ಲಿ ಸೇರಿಸಿದನು; ನನ್ನನ್ನು ಮೆಚ್ಚಿ ರಕ್ಷಿಸಿದನು.",
			"21": "<sup>21</sup> ಯೆಹೋವನು ನನ್ನ ನೀತಿಗೆ ಸರಿಯಾಗಿ ಮೇಲು ಮಾಡಿದನು; ನನ್ನ ಕೈಗಳ ಶುದ್ಧತ್ವಕ್ಕೆ ತಕ್ಕಂತೆ ಪ್ರತಿಫಲಕೊಟ್ಟನು.",
			"22": "<sup>22</sup> ಯೆಹೋವನ ಮಾರ್ಗವನ್ನೇ ಅನುಸರಿಸಿದೆನಲ್ಲಾ; ನನ್ನ ದೇವರನ್ನು ಬಿಟ್ಟು ದುಷ್ಟನಾಗಲಿಲ್ಲವಲ್ಲಾ.",
			"23": "<sup>23</sup> ನಾನು ಆತನ ಆಜ್ಞೆಗಳನ್ನು ಯಾವಾಗಲೂ ನನ್ನೆದುರಿನಲ್ಲಿ ಇಟ್ಟುಕೊಂಡೆನು; ಆತನ ವಿಧಿಗಳಿಂದ ತೊಲಗಿಹೋಗಲಿಲ್ಲ.",
			"24": "<sup>24</sup> ನಾನು ಆತನ ದೃಷ್ಟಿಯಲ್ಲಿ ನಿರ್ದೋಷಿಯು; ಪಾಪದಲ್ಲಿ ಬೀಳದಂತೆ ಜಾಗರೂಕತೆಯಿಂದ ನಡೆದುಕೊಂಡೆನು.",
			"25": "<sup>25</sup> ಆದದರಿಂದ ನಾನು ನೀತಿವಂತನೂ ನಿರಪರಾಧಿಯೂ ಎಂದು ನೋಡಿ ಯೆಹೋವನು ತಕ್ಕ ಪ್ರತಿಫಲವನ್ನು ಕೊಟ್ಟನು.",
			"26": "<sup>26</sup> ನೀನು ಕೃಪೆಯುಳ್ಳವನಿಗೆ ಕೃಪಾವಂತನೂ ದೋಷವಿಲ್ಲದವನಿಗೆ ನಿರ್ದೋಷಿಯೂ",
			"27": "<sup>27</sup> ಶುದ್ಧನಿಗೆ ಪರಿಶುದ್ಧನೂ ಮೂರ್ಖನಿಗೆ ವಕ್ರನೂ ಆಗಿರುವಿ.",
			"28": "<sup>28</sup> ದೀನರನ್ನು ಉದ್ಧರಿಸುತ್ತೀ; ಹಮ್ಮಿನವರನ್ನು ಕಂಡುಹಿಡಿದು ತಗ್ಗಿಸಿಬಿಡುತ್ತೀ.",
			"29": "<sup>29</sup> ಯೆಹೋವನೇ, ನನ್ನ ದೀಪವು ನೀನೇ; ಯೆಹೋವನು ನನಗೆ ಬೆಳಕನ್ನು ಕೊಟ್ಟು ಕತ್ತಲನ್ನು ಪರಿಹರಿಸುವನು.",
			"30": "<sup>30</sup> ನಿನ್ನ ಬಲದಿಂದ ದಂಡಿನ ಮೇಲೆ ಬೀಳುವೆನು; ನನ್ನ ದೇವರ ಸಹಾಯದಿಂದ ಪ್ರಾಕಾರವನ್ನು ಲಂಘಿಸುವೆನು.",
			"31": "<sup>31</sup> ದೇವರ ಮಾರ್ಗವು ಯಾವ ದೋಷವೂ ಇಲ್ಲದ್ದು; ಯೆಹೋವನ ವಚನವು ಶುದ್ಧವಾದದ್ದು. ಆತನು ಆಶ್ರಿತರೆಲ್ಲರಿಗೆ ಗುರಾಣಿಯಾಗಿದ್ದಾನೆ.",
			"32": "<sup>32</sup> ಯೆಹೋವನಲ್ಲದೆ ದೇವರು ಯಾರು? ನಮ್ಮ ದೇವರ ಹೊರತು ಶರಣನು ಎಲ್ಲಿ?",
			"33": "<sup>33</sup> ದೇವರು ನನ್ನ ಬಲವಾದ ಕೋಟೆಯಾಗಿದ್ದಾನೆ; ಆತನು ನನ್ನ ಮಾರ್ಗವನ್ನು ಸರಾಗಮಾಡುತ್ತಾನೆ.",
			"34": "<sup>34</sup> ನನ್ನ ಕಾಲನ್ನು ಜಿಂಕೆಯ ಕಾಲಿನಂತೆ ಚುರುಕು ಮಾಡುತ್ತಾನೆ. ನನ್ನನ್ನು ಉನ್ನತಪ್ರದೇಶಗಳಲ್ಲಿ ನಿಲ್ಲಿಸುತ್ತಾನೆ.",
			"35": "<sup>35</sup> ಆತನೇ ನನಗೆ ಯುದ್ಧ ವಿದ್ಯೆಯನ್ನು ಕಲಿಸಿದದರಿಂದ ನಾನು ತಾಮ್ರದ ಬಿಲ್ಲನ್ನಾದರೂ ಉಪಯೋಗಿಸಬಲ್ಲೆನು.",
			"36": "<sup>36</sup> ನೀನೇ ನನಗೋಸ್ಕರ ಗುರಾಣಿಯನ್ನು ಹಿಡಿದು ರಕ್ಷಿಸಿದ್ದೀ; ನಿನ್ನ ಕೃಪಾಕಟಾಕ್ಷವು ನನಗೆ ದೊಡ್ಡಸ್ತಿಕೆಯನ್ನು ಉಂಟುಮಾಡಿದೆ.",
			"37": "<sup>37</sup> ನೀನು ನನ್ನ ಕಾಲುಗಳಿಗೆ ವಿಶಾಲಸ್ಥಳವನ್ನು ಕೊಟ್ಟದರಿಂದ ನನ್ನ ಹರಡುಗಳು ಕದಲುವದಿಲ್ಲ.",
			"38": "<sup>38</sup> ನನ್ನ ಶತ್ರುಗಳನ್ನು ಹಿಂದಟ್ಟಿ ಸಂಹರಿಸುವೆನು; ಅವರನ್ನು ಇಲ್ಲದಂತೆ ಮಾಡುವವರೆಗೂ ಹಿಂದಿರುಗುವದಿಲ್ಲ.",
			"39": "<sup>39</sup> ಅವರನ್ನು ನಿರ್ನಾಮಗೊಳಿಸುವೆನು, ಹೊಡೆದು ಏಳಲಾರದಂತೆ ಮಾಡುವೆನು; ಅವರು ನನ್ನ ಪಾದದ ಕೆಳಗೆ ಬೀಳುವರು.",
			"40": "<sup>40</sup> ನೀನು ನನಗೆ ಯುದ್ಧಕ್ಕಾಗಿ ಶೌರ್ಯವೆಂಬ ನಡುಕಟ್ಟನ್ನು ಬಿಗಿದಿದ್ದೀ; ಎದುರಾಳಿಗಳನ್ನು ಕುಗ್ಗಿಸಿ ನನಗೆ ಅಧೀನ ಮಾಡಿದ್ದೀ.",
			"41": "<sup>41</sup> ನನ್ನ ಶತ್ರುಗಳನ್ನು ಬೆಂಗೊಟ್ಟು ಓಡಮಾಡಿದ್ದೀ; ನನ್ನ ಹಗೆಯವರನ್ನು ನಿರ್ಮೂಲ ಮಾಡುವೆನು.",
			"42": "<sup>42</sup> ಅವರು ಸುತ್ತಲೂ ನೋಡಿದರೂ ರಕ್ಷಿಸುವವನಿಲ್ಲ; ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಟ್ಟರೂ ಆತನು ಉತ್ತರವನ್ನು ಕೊಡಲೇ ಇಲ್ಲ.",
			"43": "<sup>43</sup> ಭೂಮಿಯ ಧೂಳನ್ನೋ ಎಂಬಂತೆ ಅವರನ್ನು ಪುಡಿಪುಡಿಮಾಡಿದೆನು; ಬೀದಿಯಲ್ಲಿರುವ ಕೆಸರನ್ನೋ ಎಂಬಂತೆ ಅವರನ್ನು ತುಳಿದು ಎಸೆದುಬಿಟ್ಟೆನು.",
			"44": "<sup>44</sup> ನನ್ನ ಜನರ ಕಲಹಗಳಿಗೆ ನನ್ನನ್ನು ತಪ್ಪಿಸಿದ್ದೀ; ಜನಾಂಗಗಳಿಗೆ ದೊರೆಯಾಗುವಂತೆ ನನ್ನನ್ನು ಉಳಿಸಿದ್ದೀ. ನಾನರಿಯದ ಜನಾಂಗದವರು ಸಹ ನನಗೆ ಅಧೀನರಾಗುವರು.",
			"45": "<sup>45</sup> ದೇಶಾಂತರದವರು ನನ್ನ ಮುಂದೆ ಮುದುರಿಕೊಳ್ಳುವರು. ನನ್ನ ಸುದ್ದಿಯನ್ನು ಕೇಳಿದ ಮಾತ್ರಕ್ಕೆ ನನಗೆ ವಿಧೇಯರಾಗುವರು.",
			"46": "<sup>46</sup> ಅವರು ಧೈರ್ಯಗುಂದಿದವರಾಗಿ ತಮ್ಮ ತಮ್ಮ ಕೋಟೆಗಳಿಂದ ನಡುಗುತ್ತಾ ಬರುವರು.",
			"47": "<sup>47</sup> ಯೆಹೋವನು ಚೈತನ್ಯಸ್ವರೂಪನು; ನನ್ನ ಶರಣನಿಗೆ ಸ್ತೋತ್ರ! ನನ್ನ ಆಶ್ರಯಗಿರಿಯಾಗಿರುವ ದೇವರಿಗೆ ಕೊಂಡಾಟ!",
			"48": "<sup>48</sup> ಆತನು ನನ್ನ ಶತ್ರುಗಳಿಗೆ ಪ್ರತಿದಂಡನೆ ಮಾಡುವ ದೇವರು; ಜನಾಂಗಗಳನ್ನು ನನಗೆ ಅಧೀನಪಡಿಸುತ್ತಾನೆ.",
			"49": "<sup>49</sup> ಶತ್ರುಗಳಿಂದ ನನ್ನನ್ನು ಬಿಡುಸುವಾತನೇ, ನೀನು ನನ್ನನ್ನು ನನ್ನ ಎದುರಾಳಿಗಳಿಗೆ ತಪ್ಪಿಸಿ ಉನ್ನತಪಡಿಸುತ್ತೀ; ಬಲಾತ್ಕಾರಿಗಳಿಂದ ನನ್ನನ್ನು ರಕ್ಷಿಸುತ್ತೀ.",
			"50": "<sup>50</sup> ಈ ಕಾರಣದಿಂದ ಯೆಹೋವನೇ, ಅನ್ಯಜನಗಳ ಮಧ್ಯದಲ್ಲಿ ನಿನ್ನನ್ನು ಸ್ತುತಿಸುವೆನು; ನಿನ್ನ ನಾಮವನ್ನು ಸಂಕೀರ್ತಿಸುವೆನು;",
			"51": "<sup>51</sup> ಆತನು ತಾನು ನೇಮಿಸಿದ ಅರಸನಿಗೋಸ್ಕರ ವಿಶೇಷರಕ್ಷಣೆಯನ್ನು ದಯಪಾಲಿಸುವವನಾಗಿದ್ದಾನೆ; ತಾನು ಅಭಿಷೇಕಿಸಿದ ದಾವೀದನಿಗೂ ಅವನ ಸಂತತಿಯವರಿಗೂ ಸದಾಕಾಲ ಕೃಪೆಯನ್ನು ಅನುಗ್ರಹಿಸುವವನಾಗಿದ್ದಾನೆ."
		},
		"23": {
			"1": "<sup>1</sup> ದಾವೀದನ ಕಡೇ ಮಾತುಗಳು. ಇಷಯನ ಮಗನಾದ ದಾವೀದನ ನುಡಿಗಳು - ಉನ್ನತಸ್ಥಾನಕ್ಕೆ ಏರಿಸಲ್ಪಟ್ಟವನೂ ಯಾಕೋಬನ ದೇವರಿಂದ ಅಭೀಷೇಕಿಸಲ್ಪಟ್ಟವನೂ ಇಸ್ರಾಯೇಲ್ಯರ ವರಕವಿಯೂ ಆಗಿರುವವನು ಹೇಳಿದ್ದು -",
			"2": "<sup>2</sup> ಯೆಹೋವನ ಆತ್ಮವು ನನ್ನಲ್ಲಿ ಉಸುರಿತು; ಆತನ ವಾಕ್ಯವು ನನ್ನ ಬಾಯಲ್ಲಿತ್ತು.",
			"3": "<sup>3</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಶರಣನೂ ದೇವರೂ ಆಗಿರುವಾತನು ಹೇಳಿದ್ದೇನಂದರೆ - ದೇವರಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವನಾಗಿ ಜನರನ್ನು ನೀತಿಯಿಂದ ಆಳುವವನು",
			"4": "<sup>4</sup> ಮೋಡಗಳಿಲ್ಲದ ಪ್ರಾತಃಕಾಲದಲ್ಲಿ ತೇಜೋಮಯನಾಗಿ ಉದಯಿಸಿ ಮೋಡಗಳನ್ನು ಚದರಿಸಿಬಿಟ್ಟು ಮಳೆಬಿದ್ದ ಭೂಮಿಯಿಂದ ಹುಲ್ಲನ್ನು ಮೊಳಿಸುವ ಸೂರ್ಯನಿಗೆ ಸಮಾನನಾಗಿದ್ದಾನೆ.",
			"5": "<sup>5</sup> ನನ್ನ ಮನೆಯು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸ್ಥಿರವಾಗಿರುತ್ತದಲ್ಲವೋ? ಆತನು ನನ್ನೊಡನೆ ಒಡಂಬಡಿಕೆಮಾಡಿಕೊಂಡಿದ್ದಾನೆ; ಅದು ಎಂದಿಗೂ ಬಿದ್ದು ಹೋಗುವದಿಲ್ಲ, ಎಲ್ಲಾ ವಿಷಯಗಳಲ್ಲಿ ಖಂಡಿತವಾಗಿದೆ. ನನ್ನ ರಕ್ಷಣೆಯ ಮೂಲವೂ ನನ್ನ ಅಭಿಲಾಷೆಯನ್ನು ಪೂರ್ತಿಗೊಳಿಸುವವನೂ ಆತನೇ ಅಲ್ಲವೋ?",
			"6": "<sup>6</sup> ದುಷ್ಟರು ದೂರಕ್ಕೆ ಬಿಸಾಡಲ್ಪಟ್ಟ ಮುಳ್ಳುಗಳಂತಿರುತ್ತಾರೆ; ಅವುಗಳನ್ನು ಹಿಡಿಯಬೇಕೆಂದಿರುವವನು ಕೈಯಿಂದ ಹಿಡಿಯಲಾರದೆ",
			"7": "<sup>7</sup> ಕಬ್ಬಿಣದ ಆಯುಧವನ್ನೂ ಬರ್ಜಿಯ ಹಿಡಿಯನ್ನೂ ಉಪಯೋಗಿಸಬೇಕು; ಅವು ಬಿದ್ದಲ್ಲಿಯೇ ಬೆಂಕಿಯಿಂದ ಸುಟ್ಟು ಹೋಗುವವು.",
			"8": "<sup>8</sup> ದಾವೀದನ ರಣವೀರರ ಪಟ್ಟಿ - ತಹ್ಕೆಮೋನ್ಯನಾದ ಯೋಷೆಬಷ್ಷೆಬೆತನು ಸರದಾರರಲ್ಲಿ ಮುಖ್ಯಸ್ಥನು. ಎಚ್ನೀಯನಾದ ಅದೀನೋ ಅನ್ನಿಸಿಕೊಳ್ಳುವ ಇವನು ಒಂದೇ ಸಾರಿ ಎಂಟುನೂರು ಮಂದಿಯನ್ನು ಕೊಂದನು.",
			"9": "<sup>9</sup> ಎರಡನೆಯವನು ಅಹೋಹ್ಯನಾದ ದೋದೋ ಎಂಬವನ ಮಗನಾಗಿರುವ ಎಲ್ಲಾಜಾರನು. ಫಿಲಿಷ್ಟಿಯರು ಅಲ್ಲಿ ಯುದ್ಧಕ್ಕೆ ಕೂಡಿಕೊಂಡಾಗ ಅವರನ್ನು ನಿಂದಿಸುವದಕ್ಕೆ ದಾವೀದನ ಜೊತೆಯಲ್ಲಿ ಹೋದ ಮೂರು ಮಂದಿ ಶೂರರಲ್ಲಿ ಇವನು ಒಬ್ಬನಾಗಿದ್ದನು.",
			"10": "<sup>10</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಹೋದನಂತರ ಇವನು ನಿಂತು ಕತ್ತಿ ಹಿಡಿದ ಕೈ ಸೋತು ಮರಗಟ್ಟಿ ಹೋಗುವ ತನಕ ಫಿಲಿಷ್ಟಿಯರನ್ನು ಕೊಲ್ಲುತ್ತಲೇ ಇದ್ದನು. ಆ ದಿನದಲ್ಲಿ ಯೆಹೋವನು ಮಹಾಜಯವನ್ನುಂಟುಮಾಡಲು ಇಸ್ರಾಯೇಲ್ಯರು ಸುಲುಕೊಳ್ಳುವದಕ್ಕೋಸ್ಕರವೇ ಹಿಂದಿರುಗಿದರು.",
			"11": "<sup>11</sup> ಮೂರನೆಯವನು ಹರಾರ್ಯನಾದ ಆಗೇಯನ ಮಗ ಶಮ್ಮ ಎಂಬವನು. ಫಿಲಿಷ್ಟಿಯರು ದೊಡ್ಡ ಗುಂಪಾಗಿ ಒಂದು ಅಲಸಂದಿಯ ಹೊಲಕ್ಕೆ ಬಂದರು.",
			"12": "<sup>12</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಅವರ ಎದುರಿನಿಂದ ಓಡಿಹೋದಾಗ ಇವನು ಆ ಹೊಲದ ಮಧ್ಯದಲ್ಲೇ ನಿಂತುಕೊಂಡು ಫಿಲಿಷ್ಟಿಯರನ್ನು ಕೊಂದು ಹೊಲವನ್ನು ಕಾಪಾಡಿದನು. ಹೀಗೆ ಯೆಹೋವನು ಮಹಾ ಜಯವನ್ನುಂಟುಮಾಡಿದನು.",
			"13": "<sup>13</sup> ಮೂವತ್ತು ಮಂದಿ ಪ್ರಸಿದ್ಧ ಶೂರರಲ್ಲಿ ದಾವೀದನು ಅದುಲ್ಲಾಮ್ ಗವಿಯಲ್ಲಿದ್ದಾಗ ಸುಗ್ಗೀಕಾಲದಲ್ಲಿ ಅವನ ಬಳಿಗೆ ಮೂರು ಮಂದಿ ಬಂದರು. ಫಿಲಿಷ್ಟಿಯರು ದಂಡೆತ್ತಿ ಬಂದು ರೆಫಾಯೀಮ್ ತಗ್ಗಿನಲ್ಲಿ ಪಾಳೆಯಮಾಡಿಕೊಂಡಾಗ",
			"14": "<sup>14</sup> ದಾವೀದನು ಆ ಅದುಲ್ಲಾಮ್ ದುರ್ಗದಲ್ಲಿದ್ದನು. ಫಿಲಿಷ್ಟಿಯರು ಬೇತ್ಲೆಹೇಮಿನಲ್ಲಿ ಕಾವಲುದಂಡನ್ನಿಟ್ಟಿದ್ದರು.",
			"15": "<sup>15</sup> ಅದೇ ಸಮಯದಲ್ಲಿ ದಾವೀದನು ಲವಲವಿಕೆಯಿಂದ - ಬೇತ್ಲೆಹೇಮ್ ಊರಿನ ಬಾಗಲ ಬಳಿಯಲ್ಲಿರುವ ಬಾವಿಯ ನೀರನ್ನು ಯಾರಾದರೂ ನನಗೆ ತಂದುಕೊಡುವದಾದರೆ ಎಷ್ಟೋ ಒಳ್ಳೇದು ಅಂದನು.",
			"16": "<sup>16</sup> ಕೂಡಲೆ ಆ ಮೂರು ಮಂದಿ ಪರಾಕ್ರಮಶಾಲಿಗಳು ಫಿಲಿಷ್ಟಿಯರ ದಂಡಿನೊಳಗೆ ನುಗ್ಗಿ ಹೋಗಿ ಬೇತ್ಲೆಹೇಮಿನ ಬಾಗಲ ಬಳಿಯಲ್ಲಿರುವ ಬಾವಿಯ ನೀರನ್ನು ಸೇದಿ ದಾವೀದನಿಗೆ ತಂದುಕೊಟ್ಟರು. ಆದರೆ ಅವನು ಅದನ್ನು ಕುಡಿಯಲೊಲ್ಲದೆ -",
			"17": "<sup>17</sup> ಯೆಹೋವನೇ, ಈ ನೀರನ್ನು ಕುಡಿಯುವದು ನನಗೆ ದೂರವಾಗಿರಲಿ. ಇದು ಜೀವದಾಶೆ ತೊರೆದವರ ರಕ್ತ! ಇದನ್ನು ಕುಡಿಯುವದೇ ಇಲ್ಲ ಎಂದು ಹೇಳಿ ಅದನ್ನು ಯೆಹೋವನ ಮುಂದೆ ಹೊಯ್ದನು. ಆ ಮೂರು ಮಂದಿಯ ಪರಾಕ್ರಮವು ಇದೇ.",
			"18": "<sup>18</sup> ಚೆರೂಯಳ ಮಗನೂ ಯೋವಾಬನ ತಮ್ಮನೂ ಆದ ಅಬೀಷೈಯು ಈ ಮೂರು ಮಂದಿಯಲ್ಲಿ ಮುಖ್ಯಸ್ಥನು. ಅವನು ತನ್ನ ಬರ್ಜಿಯನ್ನು ಬೀಸುತ್ತಾ ಹೋಗಿ ಮುನ್ನೂರು ಮಂದಿಯನ್ನು ಕೊಂದದರಿಂದ ಈ ಮೂವರಲ್ಲಿ ಕೀರ್ತಿಗೊಂಡನು.",
			"19": "<sup>19</sup> ಇವರಲ್ಲಿ ಘನವುಳ್ಳವನೂ ನಾಯಕನೂ ಇವನೇ. ಆದರೂ ಇವನು ಮೊದಲಿನ ಮೂರು ಮಂದಿಗೆ ಸಮಾನನಾಗಿರಲಿಲ್ಲ.",
			"20": "<sup>20</sup> ಯೆಹೋಯಾದಾವನ ಮಗನೂ ಕಬ್ಜಯೇಲಿನ ಪರಾಕ್ರಮಶಾಲಿಯೂ ಆದ ಬೆನಾಯನು ಇನ್ನೊಬ್ಬನು. ಇವನು ಅನೇಕ ಶೂರಕೃತ್ಯಗಳನ್ನು ನಡಿಸಿದನು. ದೃಷ್ಟಾಂತ - ಒಂದು ಸಾರಿ ಮೋವಾಬ್ಯನಾದ ಅರೀಯೇಲನ ಇಬ್ಬರು ಮಕ್ಕಳನ್ನು ಕೊಂದನು. ಇನ್ನೊಮ್ಮೆ ಹಿಮಕಾಲದಲ್ಲಿ ಒಂದು ಸಿಂಹವು ಗುಂಡಿಯಲ್ಲಿ ಬಿದ್ದಿರಲು ಇವನು ಆ ಗುಂಡಿಗೆ ಇಳಿದು ಹೋಗಿ ಅದನ್ನು ಕೊಂದನು.",
			"21": "<sup>21</sup> ಮತ್ತೊಮ್ಮೆ ಉನ್ನತನಾದ ಒಬ್ಬ ಐಗುಪ್ತ್ಯನನ್ನು ಕೊಂದನು. ಆ ಐಗುಪ್ತ್ಯನ ಕೈಯಲ್ಲಿ ಒಂದು ಬರ್ಜಿಯಿತ್ತು. ಇವನು ಕೈಯಲ್ಲಿ ಒಂದು ಕೋಲನ್ನು ಮಾತ್ರ ಹಿಡಿದುಕೊಂಡು ಹೋಗಿ ಅವನ ಕೈಯಲ್ಲಿದ್ದ ಬರ್ಜಿಯನ್ನು ಕಿತ್ತುಕೊಂಡು ಅದರಿಂದ ಅವನನ್ನು ಕೊಂದನು.",
			"22": "<sup>22</sup> ಈ ಪರಾಕ್ರಮ ಕೃತ್ಯದಿಂದ ಯೆಹೋಯಾದಾವನ ಮಗನಾದ ಬೆನಾಯನು ಈ ಮೂವರಲ್ಲಿ ಹೆಸರುಗೊಂಡನು.",
			"23": "<sup>23</sup> ಮೂವತ್ತು ಮಂದಿಯಲ್ಲಿ ಇವನು ವಿಶೇಷ ಕೀರ್ತಿಯನ್ನು ಹೊಂದಿದ್ದರೂ [ಮೊದಲಿನ] ಮೂವರಿಗೆ ಸಮಾನನಾಗಿರಲಿಲ್ಲ. ದಾವೀದನು ಇವನನ್ನು ತನ್ನ ಕಾವಲುದಂಡಿನ ಮುಖ್ಯಸ್ಥನನ್ನಾಗಿ ಮಾಡಿದನು.",
			"24": "<sup>24</sup> ಮೂವತ್ತು ಮಂದಿ ರಣವೀರರ ಪಟ್ಟಿ - ಯೋವಾಬನ ತಮ್ಮನಾದ ಅಸಾಹೇಲನು, ಬೇತ್ಲೆಹೇಮಿನ ದೋದೋವಿನ ಮಗನಾದ ಎಲ್ಹಾನಾನ್,",
			"25": "<sup>25</sup> ಹರೋದಿನವರಾದ ಶಮ್ಮ ಎಲೀಕರು, ಪೆಲೆಟಿನವನಾದ ಹೆಲೆಚ್, ತೆಕೋವದ ಇಕ್ಕೇಷನ ಮಗನಾದ ಈರಾ,",
			"27": "<sup>27</sup> ಅಣತೋತಿನವನಾದ ಅಬೀಯೆಜೆರ್, ಹುಷಾ ಊರಿನವನಾದ ಮೆಬುನ್ನೈ,",
			"28": "<sup>28</sup> ಅಹೋಹಿನವನಾದ ಚಲ್ಮೋನ್, ನೆಟೋಫದವನಾದ ಮಹರೈ,",
			"29": "<sup>29</sup> ನೆಟೋಫದವನಾದ ಬಾಣನ ಮಗ ಹೇಲೆಬ್, ಬೆನ್ಯಾಮೀನ್ ದೇಶದ ಗಿಬೆಯ ಊರಿನ ರೀಬೈ ಎಂಬವನ ಮಗನಾದ ಇತ್ತೈ,",
			"30": "<sup>30</sup> ಪಿರಾತೋನ್ಯನಾದ ಬೆನಾಯ, ನಹಲೇಗಾಷಿನವನಾದ ಹಿದ್ದೈ,",
			"31": "<sup>31</sup> ಅರಾಬಾ ತಗ್ಗಿನವನಾದ ಅಬೀ ಅಲ್ಬೋನ್,",
			"32": "<sup>32</sup> ಬರ್ಹುಮ್ಯನಾದ ಅಜ್ಮಾವೇತ್, ಶಾಲ್ಬೋನ್ಯನಾದ ಎಲೆಯಖ್ಬಾ, ಯಾಷೇನನ ಮಕ್ಕಳು,",
			"33": "<sup>33</sup> ಯೋನಾತಾನನು, ಹರಾರ್ಯನಾದ ಶಮ್ಮ, ಅರಾರ್ಯನಾದ ಶಾರಾರನ ಮಗ ಅಹೀಯಾಮ್,",
			"34": "<sup>34</sup> ಮಾಕಾ ಊರಿನ ಅಹಸ್ಬೈ ಎಂಬವನ ಮಗನಾದ ಎಲೀಫೆಲೆಟ್, ಗಿಲೋವಿನ ಅಹೀತೋಫೆಲ್ ಎಂಬವನ ಮಗನಾದ ಎಲೀಯಾಮ್,",
			"35": "<sup>35</sup> ಕರ್ಮೆಲ್ಯನಾದ ಹೆಚ್ರೋ, ಅರ್ಬೀಯನಾದ ಪಾರೈ,",
			"36": "<sup>36</sup> ಚೋಬ ಊರಿನ ನಾತಾನ್ ಎಂಬವನ ಮಗನಾದ ಇಗಾಲ್, ಗಾದ್ಯನಾದ ಬಾನೀ,",
			"37": "<sup>37</sup> ಅಮ್ಮೋನಿಯನಾದ ಚೆಲೆಕ್, ಬೇರೋತ್ಯನೂ ಚೆರೂಯಳ ಮಗನಾದ ಯೋವಾಬನ ಆಯುಧವಾಹಕನೂ ಆಗಿದ್ದ ನಹರೈ,",
			"38": "<sup>38</sup> ಇತ್ರೀಯರಾದ ಈರಾ ಗಾರೇಬರು,",
			"39": "<sup>39</sup> ಹಿತ್ತಿಯನಾದ ಊರೀಯ, ಒಟ್ಟಿಗೆ ಮೂವತ್ತೇಳು ಮಂದಿ."
		},
		"24": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ತಿರಿಗಿ ಇಸ್ರಾಯೇಲ್ಯರ ಮೇಲೆ ಕೋಪಗೊಂಡು ದಾವೀದನನ್ನು ಇಸ್ರಾಯೇಲ್ ಯೆಹೂದ್ಯ ಕುಲಗಳವರ ಖಾನೇಷುಮಾರಿಮಾಡುವದಕ್ಕೆ ಪ್ರೇರೇಪಿಸಿದನು.",
			"2": "<sup>2</sup> ಅರಸನು ತನ್ನ ಹತ್ತಿರದಲ್ಲಿದ್ದ ಸೇನಾಪತಿಯಾದ ಯೋವಾಬನಿಗೆ - ಜನಸಂಖ್ಯೆ ಗೊತ್ತಾಗುವ ಹಾಗೆ ನೀನು ದಾನ್ ಊರಿನಿಂದ ಬೇರ್ಷೆಬದವರೆಗೆ ಸಂಚರಿಸಿ ಇಸ್ರಾಯೇಲ್‍ಕುಲಗಳ ಎಲ್ಲಾ ಜನರನ್ನು ಲೆಕ್ಕಮಾಡಿಕೊಂಡು ಬಾ ಎಂದು ಅವನಿಗೆ ಆಜ್ಞಾಪಿಸಿದನು.",
			"3": "<sup>3</sup> ಆಗ ಯೋವಾಬನು ಅರಸನಿಗೆ - ನನ್ನ ಒಡೆಯನಾದ ನಿನ್ನ ಆಯುಷ್ಕಾಲದಲ್ಲೇ ದೇವರಾದ ಯೆಹೋವನು ನಿನ್ನ ಪ್ರಜೆಗಳನ್ನು ಈಗ ಇರುವದಕ್ಕಿಂತ ನೂರರಷ್ಟು ಹೆಚ್ಚಿಸಲಿ. ನನ್ನ ಒಡೆಯನಾದ ಅರಸನು ಈ ಕಾರ್ಯಕ್ಕೆ ಮನಸ್ಸು ಮಾಡಿದ್ದೇಕೆ ಅಂದನು.",
			"4": "<sup>4</sup> ಆದರೆ ಅರಸನು ಯೋವಾಬನಿಗೂ ಇತರ ಸೇನಾಪತಿಗಳಿಗೂ ಕಿವಿಗೊಡದೆ ತನ್ನ ಮಾತನ್ನೇ ಸಾಧಿಸಿದದರಿಂದ ಅವರು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಲೆಕ್ಕಿಸುವದಕ್ಕಾಗಿ ಅರಸನ ಸನ್ನಿಧಿಯಿಂದ ಹೊರಟು ಹೋದರು.",
			"5": "<sup>5</sup> ಅವರು ಯೊರ್ದನ್ ಹೊಳೆಯನ್ನು ದಾಟಿ ಗಾದ್ ತಗ್ಗಿನಲ್ಲಿರುವ ಪಟ್ಟಣದ ಬಲಪಾರ್ಶ್ವದಲ್ಲಿರುವ ಅರೋಯೇರಿನಿಂದ ತೊಡಗಿ",
			"6": "<sup>6</sup> ಯಗ್ಜೇರಿಗೂ ಅಲ್ಲಿಂದ ಗಿಲ್ಯಾದ್ ಪ್ರಾಂತ, ತಖ್ತೀಮ್ ಹೊಜೀಪ್ರದೇಶ ಇವುಗಳ ಮೇಲೆ ಯಾನಿನ ದಾನ್ ಎಂಬಲ್ಲಿಗೂ ಬಂದರು.",
			"7": "<sup>7</sup> ಅಲ್ಲಿಂದ ತಿರುಗಿಕೊಂಡು ಚೀದೋನ್, ತೂರ್‍ಕೋಟೆ, ಹಿವ್ವಿಯರ ಮತ್ತು ಕಾನಾನ್ಯರ ಪಟ್ಟಣಗಳು ಇವುಗಳ ಮೇಲೆ ಯೆಹೂದ ಪ್ರಾಂತದ ದಕ್ಷಿಣ ದಿಕ್ಕಿನಲ್ಲಿರುವ ಬೇರ್ಷೆಬಕ್ಕೆ ಬಂದರು.",
			"8": "<sup>8</sup> ಹೀಗೆ ಒಂಭತ್ತು ತಿಂಗಳು ಇಪ್ಪತ್ತು ದಿವಸಗಳಲ್ಲಿ ದೇಶವನ್ನೆಲ್ಲಾ ಸಂಚರಿಸಿ ಯೆರೂಸಲೇಮಿಗೆ ಹಿಂದಿರುಗಿ ಬಂದರು.",
			"9": "<sup>9</sup> ಯೋವಾಬನು ಅರಸನಿಗೆ ಒಪ್ಪಿಸಿದ ಖಾನೇಷುಮಾರಿಯ ಸಂಖ್ಯೆಯು - ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಯುದ್ಧಕ್ಕೆ ಹೋಗತಕ್ಕ ಸೈನಿಕರು ಎಂಟು ಲಕ್ಷ; ಯೆಹೂದ್ಯರಲ್ಲಿ ಐದು ಲಕ್ಷ.",
			"10": "<sup>10</sup> ಖಾನೇಷುಮಾರಿ ಮಾಡಿಸಿದನಂತರ ದಾವೀದನನ್ನು ಮನಸ್ಸಾಕ್ಷಿಯು ಹಂಗಿಸತೊಡಗಿತು. ಆದದರಿಂದ ಅವನು ಯೆಹೋವನನ್ನು - ಯೆಹೋವನೇ, ನಾನು ಬುದ್ಧಿಹೀನಕಾರ್ಯವನ್ನು ಮಾಡಿ ಪಾಪಿಯಾದೆನು; ದಯವಿಟ್ಟು ನಿನ್ನ ಸೇವಕನ ಅಪರಾಧವನ್ನು ಕ್ಷಮಿಸು ಎಂದು ಪ್ರಾರ್ಥಿಸಿದನು.",
			"11": "<sup>11</sup> ಅವನು ಮರುದಿನ ಬೆಳಿಗ್ಗೆ ಏಳುವಷ್ಟರಲ್ಲಿ ಅವನ ದರ್ಶಿಯಾದ ಗಾದ್ ಪ್ರವಾದಿಗೆ ಯೆಹೋವನು ದರ್ಶನದಲ್ಲಿ -",
			"12": "<sup>12</sup> ನೀನು ದಾವೀದನ ಹತ್ತಿರ ಹೋಗಿ ಅವನಿಗೆ - ನಾನು ಮೂರು ವಿಧವಾದ ಶಿಕ್ಷೆಗಳನ್ನು ನಿನ್ನ ಮುಂದೆ ಇಡುತ್ತೇನೆ; ಅವುಗಳಲ್ಲಿ ಯಾವದನ್ನು ನಿನ್ನ ಮೇಲೆ ಬರಮಾಡಬೇಕೋ ಆರಿಸಿಕೋ ಎಂದು ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ ಎಂಬದಾಗಿ ಹೇಳು ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"13": "<sup>13</sup> ಆಗ ಗಾದನು ದಾವೀದನ ಬಳಿಗೆ ಹೋಗಿ - ನಿನ್ನ ದೇಶದಲ್ಲಿ ಏಳು ವರುಷಗಳ ಬರವು ಉಂಟಾಗಬೇಕೋ, ಇಲ್ಲವೆ ನಿನ್ನ ಶತ್ರುಗಳು ನಿನ್ನನ್ನು ಸೋಲಿಸಿ ಮೂರು ತಿಂಗಳುಗಳವರೆಗೆ ಓಡಿಸಿ ಬಿಡಬೇಕೋ, ಇಲ್ಲವೆ ನಿನ್ನ ದೇಶದಲ್ಲಿ ಮೂರು ದಿನಗಳವರೆಗೂ ಘೋರವ್ಯಾಧಿ ಬರಬೇಕೋ? ನನ್ನನ್ನು ಕಳುಹಿಸಿದವನಿಗೆ ಯಾವ ಉತ್ತರವನ್ನು ತೆಗೆದುಕೊಂಡು ಹೋಗಬೇಕು, ಆಲೋಚಿಸಿ ಹೇಳು ಅಂದನು.",
			"14": "<sup>14</sup> ಅದಕ್ಕೆ ದಾವೀದನು - ನಾನು ಬಲು ಇಕ್ಕಟ್ಟಿನಲ್ಲಿರುತ್ತೇನೆ. ಯೆಹೋವನ ಕೈಯಲ್ಲಿ ಬೀಳುತ್ತೇನೆ; ಆತನು ಕೃಪಾಪೂರ್ಣನು. ಮನುಷ್ಯರ ಕೈಯಲ್ಲಿ ಬೀಳಲೊಲ್ಲೆನು ಎಂದು ಹೇಳಿದನು.",
			"15": "<sup>15</sup> ಆಗ ಯೆಹೋವನು ಇಸ್ರಾಯೇಲ್ಯರ ಮೇಲೆ ವ್ಯಾಧಿಯನ್ನು ಬರಮಾಡಿದನು. ಅದು ಹೊತ್ತಾರೆಯಿಂದ ನೇಮಕವಾದ ಹೊತ್ತಿನವರೆಗೂ ಇತ್ತು. ದಾನಿನಿಂದ ಬೇರ್ಷೆಬದವರೆಗೆ ವಾಸವಾಗಿರುವ ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಎಪ್ಪತ್ತು ಸಾವಿರ ಜನರು ಸತ್ತರು.",
			"16": "<sup>16</sup> ದೇವದೂತನು ಯೆರೂಸಲೇಮನ್ನೂ ಸಂಹರಿಸುವದಕ್ಕೆ ಕೈಚಾಚಿದಾಗ ಯೆಹೋವನು ಆ ಕೇಡಿನ ವಿಷಯದಲ್ಲಿ ಪಶ್ಚಾತ್ತಾಪಪಟ್ಟು ಸಂಹಾರಕದೂತನಿಗೆ - ಈಗ ಸಾಕು, ನಿನ್ನ ಕೈಯನ್ನು ಹಿಂದೆಗೆ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು. ಆಗ ಆ ದೂತನು ಯೆಬೂಸಿಯನಾದ ಅರೌನನ ಕಣದಲ್ಲಿದ್ದನು.",
			"17": "<sup>17</sup> ದಾವೀದನು ಜನಸಂಹಾರಕದೂತನನ್ನು ಕಂಡಾಗ ಯೆಹೋವನಿಗೆ - ಮೂರ್ಖತನದಿಂದ ಪಾಪಮಾಡಿದವನು ನಾನು; ಕುರಿಗಳಂತಿರುವ ಈ ಜನರು ಏನು ಮಾಡಿದ್ದಾರೆ? ನಿನ್ನ ಕೈ ನನಗೂ ನನ್ನ ಮನೆಯವರಿಗೂ ವಿರೋಧವಾಗಿರಲಿ ಎಂದು ಬೇಡಿಕೊಂಡನು.",
			"18": "<sup>18</sup> ಅದೇ ದಿನದಲ್ಲಿ ಗಾದನು ದಾವೀದನ ಬಳಿಗೆ ಬಂದು ಅವನಿಗೆ - ನೀನು ಹೋಗಿ ಯೆಬೂಸಿಯನಾದ ಅರೌನನ ಕಣದಲ್ಲಿ ಯೆಹೋವನಿಗೋಸ್ಕರ ಒಂದು ಯಜ್ಞವೇದಿಯನ್ನು ಕಟ್ಟಿಸು ಎಂದು ಹೇಳಿದನು.",
			"19": "<sup>19</sup> ದಾವೀದನು ಗಾದನ ಮುಖಾಂತರವಾಗಿ ತನಗಾದ ಯೆಹೋವನ ಅಪ್ಪಣೆಯಂತೆ ಹೊರಟನು.",
			"20": "<sup>20</sup> ದಾವೀದನೂ ಅವನ ಸೇವಕರೂ ತನ್ನ ಬಳಿಗೆ ಬರುವದನ್ನು ಅರೌನನು ಕಂಡು ಅವರ ಮುಂದೆ ಹೋಗಿ ಅರಸನಿಗೆ ಸಾಷ್ಟಾಂಗ ನಮಸ್ಕಾರಮಾಡಿ -",
			"21": "<sup>21</sup> ನನ್ನ ಒಡೆಯನಾದ ಅರಸನು ತನ್ನ ಸೇವಕನ ಬಳಿಗೆ ಬರುವದಕ್ಕೇನು ಕಾರಣ ಎಂದು ಕೇಳಿದನು. ದಾವೀದನು ಅವನಿಗೆ - ವ್ಯಾಧಿಯು ಜನರನ್ನು ಬಿಟ್ಟುಹೋಗುವಂತೆ ನಾನು ನಿನ್ನ ಕಣವನ್ನು ಕೊಂಡುಕೊಂಡು ಅದರಲ್ಲಿ ಯೆಹೋವನಿಗೋಸ್ಕರ ಒಂದು ವೇದಿಯನ್ನು ಕಟ್ಟುವದಕ್ಕೆ ಬಂದಿದ್ದೇನೆ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"22": "<sup>22</sup> ಆಗ ಅರೌನನು ದಾವೀದನಿಗೆ - ನನ್ನ ಒಡೆಯನಾದ ಅರಸನು ತನಗೆ ಇಷ್ಟವಾದದ್ದನ್ನು ತೆಗೆದುಕೊಂಡು ಅರ್ಪಿಸೋಣವಾಗಲಿ; ಇಲ್ಲಿ ಯಜ್ಞಕ್ಕೆ ಹೋರಿಗಳೂ ಸೌದೆಗೆ ಹಂತೀಕುಂಟೆ ಮೊದಲಾದ ಎತ್ತಿನ ಸಾಮಾನುಗಳೂ ಇರುತ್ತವೆ.",
			"23": "<sup>23</sup> ಇವುಗಳನ್ನೆಲ್ಲಾ ಅರಸನ ಸೇವಕನಾದ ನಾನು ಅರಸನಿಗೆ ಕೊಡುತ್ತೇನೆ. ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನು ನಿನಗೆ ಪ್ರಸನ್ನನಾಗಲಿ ಎಂದು ಹೇಳಿದನು.",
			"24": "<sup>24</sup> ಅರಸನು ಅರೌನನಿಗೆ - ಹಾಗಲ್ಲ, ನಾನು ನಿನ್ನಿಂದ ಕ್ರಯಕ್ಕೆ ತೆಗೆದುಕೊಳ್ಳುತ್ತೇನೆ. ನನ್ನ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಸುಮ್ಮನೆ ಸಿಕ್ಕಿದ್ದನ್ನು ಯಜ್ಞವಾಗಿ ಅರ್ಪಿಸಲೊಲ್ಲೆನು ಎಂದು ಹೇಳಿ ಆ ಕಣವನ್ನೂ ಹೋರಿಗಳನ್ನೂ ಐವತ್ತು ರೂಪಾಯಿಗೆ ಕೊಂಡುಕೊಂಡು",
			"25": "<sup>25</sup> ಅಲ್ಲಿ ಯೆಹೋವನಿಗೋಸ್ಕರ ಯಜ್ಞವೇದಿಯನ್ನು ಕಟ್ಟಿಸಿ ಅದರ ಮೇಲೆ ಆತನಿಗೆ ಸರ್ವಾಂಗ ಹೋಮಗಳನ್ನೂ ಸಮಾಧಾನಯಜ್ಞಗಳನ್ನೂ ಅರ್ಪಿಸಿದನು. ಹೀಗೆ ದೇಶದ ಮೇಲಿದ್ದ ಯೆಹೋವನ ಕೋಪವು ಶಾಂತವಾಯಿತು. ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿದ್ದ ಸಂಹಾರಕವ್ಯಾಧಿಯು ನಿಂತುಹೋಯಿತು."
		}
	}
};
module.exports = book;