var book = {
	"name": "1 ಅರಸುಗಳು",
	"numChapters": 22,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ಅರಸನಾದ ದಾವೀದನು ದಿನ ತುಂಬಿದ ಮುದುಕನಾಗಿದ್ದನು. ಸೇವಕರು ಎಷ್ಟು ಕಂಬಳಿಗಳನ್ನು ಹೊದಿಸಿದರೂ ಅವನಿಗೆ ಬೆಚ್ಚಗಾಗುತ್ತಿದ್ದಿಲ್ಲ.",
			"2": "<sup>2</sup> ಆದದರಿಂದ ಅವನ ಸೇವಕರು ಅವನಿಗೆ - ನಮ್ಮ ಒಡೆಯನಾದ ಅರಸನಿಗೋಸ್ಕರ ಒಬ್ಬ ಕನ್ಯೆಯನ್ನು ಹುಡುಕತಕ್ಕದ್ದು; ಆಕೆಯು ಅರಸನ ಸನ್ನಿಧಿಯಲ್ಲಿದ್ದುಕೊಂಡು ಅವನನ್ನು ಪರಾಂಬರಿಸಬೇಕು. ಆಕೆಯು ನಮ್ಮ ಒಡೆಯನಾದ ಅರಸನ ಮಗ್ಗುಲಲ್ಲಿ ಮಲಗುವದಾದರೆ ಅವನಿಗೆ ಬೆಚ್ಚಗಾಗುವದು ಎಂದು ಹೇಳಿ",
			"3": "<sup>3</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಎಲ್ಲಾ ಪ್ರಾಂತಗಳಲ್ಲಿ ಅತಿಸುಂದರಿಯಾದ ಹುಡುಗಿಯನ್ನು ಹುಡುಕುವದಕ್ಕೆ ಹೋಗಿ ಶೂನೇಮ್ ಊರಿನ ಅಬೀಷಗ್ ಎಂಬಾಕೆಯನ್ನು ಕಂಡು ಆಕೆಯನ್ನು ಅರಸನ ಬಳಿಗೆ ಕರಕೊಂಡು ಬಂದರು.",
			"4": "<sup>4</sup> ಬಹು ಸುಂದರಿಯಾದ ಈಕೆಯು ಅರಸನನ್ನು ಪರಾಂಬರಿಸುವ ಸೇವಕಿಯಾದಳು. ಅರಸನು ಈಕೆಯನ್ನು ಸಂಗಮಿಸಲಿಲ್ಲ.",
			"5": "<sup>5</sup> ಹಗ್ಗೀತಳ ಮಗನಾದ ಅದೋನೀಯನು ನಾನೇ ಅರಸನಾಗತಕ್ಕವನು ಎಂಬದಾಗಿ ಉಬ್ಬಿಕೊಂಡು ತನಗೋಸ್ಕರ ರಥರಥಾಶ್ವಗಳನ್ನೂ ಮುಂದೆ ಓಡುವದಕ್ಕಾಗಿ ಐವತ್ತು ಮಂದಿ ಸಿಪಾಯಿಗಳನ್ನೂ ಇಟ್ಟುಕೊಂಡನು.",
			"6": "<sup>6</sup> ಅವನ ತಂದೆಯು - ನೀನು ಹಾಗೆ ಹೀಗೆ ಮಾಡಿದ್ದೇಕೆ ಎಂದು ಅವನನ್ನು ಒಂದು ಸಾರಿಯಾದರೂ ಗದರಿಸಿದ್ದು ಇರಲಿಲ್ಲ. ಅವನು ಬಹುಸುಂದರನು. ಅಬ್ಷಾಲೋಮನ ತರುವಾಯ ಅವನೇ ಹಿರಿಯನು.",
			"7": "<sup>7</sup> ಅವನು ಚೆರೂಯಳ ಮಗನಾದ ಯೋವಾಬನನ್ನೂ ಯಾಜಕನಾದ ಎಬ್ಯಾತಾರನನ್ನೂ ತನ್ನ ಕಡೆಗೆ ಒಲಿಸಿಕೊಂಡನು. ಇವರು ಅವನಿಗೆ ಸಹಾಯಕರಾದರು.",
			"8": "<sup>8</sup> ಆದರೆ ಯಾಜಕನಾದ ಚಾದೋಕ್, ಯೆಹೋಯಾದಾವನ ಮಗನಾದ ಬೆನಾಯ, ಪ್ರವಾದಿಯಾದ ನಾತಾನ್, ಶಿಮ್ಮೀ, ರೇಗೀ ಎಂಬವರೂ ದಾವೀದನ ಭಟರೂ ಅವನ ಪಕ್ಷದಲ್ಲಿರಲಿಲ್ಲ.",
			"9": "<sup>9</sup> ಅದೋನೀಯನು ರೋಗೆಲ್ ಬುಗ್ಗೆಯ ಬಳಿಯಲ್ಲಿರುವ ಚೋಹೆಲೆತ್ ಬಂಡೆಯ ಮೇಲೆ ಕುರಿ, ಹೋರಿ ಮೊದಲಾದ ಕೊಬ್ಬಿದ ಪಶುಗಳನ್ನು ಯಜ್ಞಮಾಡಿಸಿ ಪ್ರವಾದಿಯಾದ ನಾತಾನ್, ಬೆನಾಯ, ಯುದ್ಧಭಟರು, ತಮ್ಮನಾದ ಸೊಲೊಮೋನ್ ಇವರನ್ನು ಬಿಟ್ಟು",
			"10": "<sup>10</sup> ರಾಜಪುತ್ರರಾದ ತನ್ನ ಉಳಿದ ಸಹೋದರರನ್ನೂ ಅರಸನ ಉದ್ಯೋಗಸ್ಥರಾದ ಎಲ್ಲಾ ಯೆಹೂದ್ಯರನ್ನೂ ಔತಣಕ್ಕೆ ಕರಿಸಿದನು.",
			"11": "<sup>11</sup> ಆಗ ನಾತಾನನು ಸೊಲೊಮೋನನ ತಾಯಿಯಾದ ಬತ್ಷೆಬೆಗೆ - ಹಗ್ಗೀತಳ ಮಗನಾದ ಅದೋನೀಯನು ನಮ್ಮ ಒಡೆಯನಾದ ದಾವೀದನಿಗೆ ತಿಳಿಯದೆ ಅರಸನಾದನೆಂಬದನ್ನು ನೀನು ಕೇಳಿದಿಯಲ್ಲವೋ?",
			"12": "<sup>12</sup> ಈಗ ನನ್ನ ಆಲೋಚನೆಯನ್ನು ಲಾಲಿಸಿ ನಿನ್ನ ಮತ್ತು ನಿನ್ನ ಮಗನಾದ ಸೊಲೊಮೋನನ ಜೀವವನ್ನು ಉಳಿಸಿಕೋ.",
			"13": "<sup>13</sup> ನೀನು ಅರಸನಾದ ದಾವೀದನ ಬಳಿಗೆ ಹೋಗಿ ಅವನಿಗೆ - ನನ್ನ ಒಡೆಯನಾದ ಅರಸನು ತನ್ನ ತರುವಾಯ ನನ್ನ ಮಗನಾದ ಸೊಲೊಮೋನನೇ ಆಳಬೇಕು; ಅವನೇ ತನ್ನ ಸಿಂಹಾಸನದ ಮೇಲೆ ಕೂತುಕೊಳ್ಳಬೇಕು ಎಂದು ತನ್ನ ದಾಸಿಯಾದ ನನಗೆ ಪ್ರಮಾಣಮಾಡಿ ಹೇಳಿದನಲ್ಲಾ! ಹೀಗಿದ್ದ ಮೇಲೆ ಅದೋನೀಯನು ಆಳುವದೇಕೆ ಎಂದು ಹೇಳು.",
			"14": "<sup>14</sup> ನೀನು ಅರಸನೊಡನೆ ಮಾತಾಡುತ್ತಿರುವಾಗ ನಾನೂ ಬಂದು ನಿನ್ನ ಮಾತನ್ನು ದೃಢಪಡಿಸುವೆನು ಅಂದನು.",
			"15": "<sup>15</sup> ಅರಸನು ಬಹು ವೃದ್ಧನಾಗಿದ್ದದರಿಂದ ಶೂನೇಮ್ಯಳಾದ ಅಬೀಷಗ್ ಎಂಬಾಕೆಯ ಪರಾಂಬರಿಕೆಯನ್ನು ಹೊಂದುತ್ತಾ ಒಳಗಿನ ಕೋಣೆಯಲ್ಲಿಯೇ ಇರುತ್ತಿದ್ದನು.",
			"16": "<sup>16</sup> ಬತ್ಷೆಬೆಯು ಅಲ್ಲಿಗೆ ಹೋಗಿ ಅರಸನಿಗೆ ಬೊಗ್ಗಿ ನಮಸ್ಕರಿಸಿದಳು. ಅರಸನು - ನಿನಗೇನು ಬೇಕು ಎಂದು ಆಕೆಯನ್ನು ಕೇಳಿದನು.",
			"17": "<sup>17</sup> ಆಗ ಆಕೆಯು - ನನ್ನ ಒಡೆಯನು ತನ್ನ ದೇವರಾದ ಯೆಹೋವನ ಮೇಲೆ ಆಣೆಯಿಟ್ಟು ತನ್ನ ದಾಸಿಯಾದ ನನಗೆ - ನನ್ನ ತರುವಾಯ ನಿನ್ನ ಮಗನಾದ ಸೊಲೊಮೋನನು ಅರಸನಾಗಬೇಕು; ನಿಜವಾಗಿ ಅವನೇ ನನ್ನ ಸಿಂಹಾಸನದ ಮೇಲೆ ಕೂತುಕೊಳ್ಳಬೇಕು ಎಂಬದಾಗಿ ಹೇಳಿದನಲ್ಲಾ!",
			"18": "<sup>18</sup> ಆದರೆ ಈಗ ಅದೋನೀಯನು ನನ್ನ ಒಡೆಯನೂ ಅರಸನೂ ಆದ ನಿನಗೆ ತಿಳಿಯದೆ",
			"19": "<sup>19</sup> ರಾಜನಾಗಿ ಹೋರಿಕುರಿ ಮೊದಲಾದ ಹಲವು ಕೊಬ್ಬಿದ ಪಶುಗಳನ್ನು ಯಜ್ಞಮಾಡಿಸಿ ನಿನ್ನ ಸೇವಕನಾದ ಸೊಲೊಮೋನನ ಹೊರತಾಗಿ ಎಲ್ಲಾ ರಾಜಪುತ್ರರನ್ನೂ ಯಾಜಕನಾದ ಎಬ್ಯಾತಾರನನ್ನೂ ಸೇನಾಪತಿಯಾದ ಯೋವಾಬನನ್ನೂ ಔತಣಕ್ಕೆ ಕರೆದಿದ್ದಾನೆ.",
			"20": "<sup>20</sup> ನನ್ನ ಒಡೆಯನೂ ಅರಸನೂ ಆದ ನಿನ್ನ ತರುವಾಯ ಸಿಂಹಾಸನದ ಮೇಲೆ ಕೂತುಕೊಳ್ಳತಕ್ಕವರಾರೆಂಬದನ್ನು ನೀನೇ ಗೊತ್ತುಮಾಡಬೇಕೆಂದು ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರೂ ಕಾದುಕೊಂಡಿರುತ್ತಾರೆ.",
			"21": "<sup>21</sup> ನನ್ನ ಒಡೆಯನೂ ಅರಸನೂ ಆದ ನೀನು ಹಾಗೆ ಮಾಡದೆ ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರುವದಾದರೆ ನಾನೂ ನನ್ನ ಮಗನೂ ದೋಷಾರೋಪಣೆಗೆ ಗುರಿಯಾಗುವೆವು ಎಂದು ಹೇಳಿದಳು.",
			"22": "<sup>22</sup> ಆಕೆಯು ಹೀಗೆ ಅರಸನ ಸಂಗಡ ಮಾತಾಡುತ್ತಿರುವಷ್ಟರಲ್ಲಿ ಪ್ರವಾದಿಯಾದ ನಾತಾನನು ಬಂದನು.",
			"23": "<sup>23</sup> ಸೇವಕರು ನಾತಾನನು ಬಂದಿರುವ ಸಂಗತಿಯನ್ನು ಅರಸನಿಗೆ ತಿಳಿಸಿದರು. ನಾತಾನನು ಅರಸನ ಮುಂದೆ ಹೋಗಿ ಸಾಷ್ಟಾಂಗನಮಸ್ಕಾರಮಾಡಿ -",
			"24": "<sup>24</sup> ನನ್ನ ಒಡೆಯನೂ ಅರಸನೂ ಆದ ನೀನು ಅದೋನೀಯನಿಗೆ ಅರಸನಾಗುವದಕ್ಕೂ ನಿನ್ನ ಸಿಂಹಾಸನದ ಮೇಲೆ ಕೂತುಕೊಳ್ಳುವದಕ್ಕೂ ಅಪ್ಪಣೆಕೊಟ್ಟಿರುವಂತೆ ಕಾಣುತ್ತದೆ.",
			"25": "<sup>25</sup> ಅವನು ಗಟ್ಟಾ ಇಳಿದು ಹೋಗಿ ಹೋರಿಕುರಿ ಮುಂತಾದ ಹಲವು ಕೊಬ್ಬಿದ ಪಶುಗಳನ್ನು ಯಜ್ಞಮಾಡಿಸಿ ಎಲ್ಲಾ ರಾಜಪುತ್ರರನ್ನೂ ಸೇನಾಪತಿಗಳನ್ನೂ ಯಾಜಕನಾದ ಎಬ್ಯಾತಾರನನ್ನೂ ಔತಣಕ್ಕೆ ಕರೆದಿದ್ದಾನೆ. ಅವರು ಅವನ ಪಂಕ್ತಿಯಲ್ಲಿ ಅನ್ನಪಾನಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಅರಸನಾದ ಅದೋನೀಯನು ಚಿರಂಜೀವಿಯಾಗಿರಲಿ ಎಂದು ಕೂಗುತ್ತಿರುತ್ತಾರೆ.",
			"26": "<sup>26</sup> ಆದರೆ ಅವನು ನಿನ್ನ ಸೇವಕನಾದ ನನ್ನನ್ನೂ ಯಾಜಕನಾದ ಚಾದೋಕ್, ಯೆಹೋಯಾದಾವನ ಮಗನಾದ ಬೆನಾಯ ಎಂಬವರನ್ನೂ ನಿನ್ನ ಸೇವಕನಾದ ಸೊಲೊಮೋನನನ್ನೂ ಔತಣಕ್ಕೆ ಕರೆಯಲಿಲ್ಲ.",
			"27": "<sup>27</sup> ಈ ಕಾರ್ಯವು ನನ್ನ ಒಡೆಯನೂ ಅರಸನೂ ಆದ ನಿನ್ನಿಂದಲೇ ನಡೆದಿರುವ ಪಕ್ಷದಲ್ಲಿ ನಿನ್ನ ತರುವಾಯ ಸಿಂಹಾಸನದ ಮೇಲೆ ಕೂತುಕೊಳ್ಳತಕ್ಕವರಾರೆಂಬದನ್ನು ನಿನ್ನ ಸೇವಕನಾದ ನನಗೆ ತಿಳಿಸಬಾರದಾಗಿತ್ತೇ ಎಂದು ಹೇಳಿದನು.",
			"28": "<sup>28</sup> ಅರಸನಾದ ದಾವೀದನು ಬತ್ಷೆಬೆಯನ್ನು ಕರೆಯಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿದನು. ಆಕೆಯು ಬಂದು ಅರಸನ ಮುಂದೆ ನಿಂತಾಗ ಅರಸನು ಆಕೆಗೆ -",
			"29": "<sup>29</sup> ನನ್ನ ಪ್ರಾಣವನ್ನು ಎಲ್ಲಾ ಇಕ್ಕಟ್ಟುಗಳಿಂದ ಬಿಡಿಸಿ ಕಾಪಾಡಿದ ಯೆಹೋವನಾಣೆ,",
			"30": "<sup>30</sup> ನನ್ನ ತರುವಾಯ ನಿನ್ನ ಮಗನಾದ ಸೊಲೊಮೋನನು ಅರಸನಾಗಿ ನನ್ನ ಸಿಂಹಾಸನದ ಮೇಲೆ ಕೂತುಕೊಳ್ಳಬೇಕೆಂದು ನಾನು ನಿನಗೆ ಇಸ್ರಾಯೇಲ್ ದೇವರಾದ ಯೆಹೋವನ ಹೆಸರಿನಲ್ಲಿ ಪ್ರಮಾಣಮಾಡಿದ್ದನ್ನು ಈಹೊತ್ತೇ ನೆರವೇರಿಸುವೆನು ಅಂದನು.",
			"31": "<sup>31</sup> ಆಗ ಬತ್ಷೆಬೆಯು ಅರಸನಿಗೆ ಸಾಷ್ಟಾಂಗನಮಸ್ಕಾರ ಮಾಡಿ - ನನ್ನ ಒಡೆಯನೂ ಅರಸನೂ ಆದ ದಾವೀದನು ಸದಾಕಾಲ ಬಾಳಲಿ ಎಂದಳು.",
			"32": "<sup>32</sup> ಅನಂತರ ಅರಸನಾದ ದಾವೀದನು ತನ್ನ ಸೇವಕರಿಗೆ - ಯಾಜಕನಾದ ಚಾದೋಕ್, ಪ್ರವಾದಿಯಾದ ನಾತಾನ್, ಯೆಹೋಯಾದಾವನ ಮಗನಾದ ಬೆನಾಯ ಎಂಬವರನ್ನು ಕರೆಯಿರಿ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು;",
			"33": "<sup>33</sup> ಅವರು ಅರಸನ ಸನ್ನಿಧಿಗೆ ಬರಲು ಅರಸನು ಅವರಿಗೆ - ನೀವು ನನ್ನ ಸೇವಕರನ್ನು ಕರಕೊಂಡು ನನ್ನ ಮಗನಾದ ಸೊಲೊಮೋನನನ್ನು ನನ್ನ ಹೇಸರಕತ್ತೆಯ ಮೇಲೆ ಕುಳ್ಳಿರಿಸಿಕೊಂಡು, ಗೀಹೋನ್ ಬುಗ್ಗೆಗೆ ಹೋಗಿರಿ.",
			"34": "<sup>34</sup> ಅಲ್ಲಿ ಯಾಜಕನಾದ ಚಾದೋಕನೂ ಪ್ರವಾದಿಯಾದ ನಾತಾನನೂ ಅವನನ್ನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನನ್ನಾಗಿ ಅಭಿಷೇಕಿಸಲಿ; ಅನಂತರ ಕೊಂಬನ್ನೂದಿರಿ; ಎಲ್ಲರೂ ಅರಸನಾದ ಸೊಲೊಮೋನನು ಚಿರಂಜೀವಿಯಾಗಿರಲಿ ಎಂದು ಆರ್ಭಟಿಸಲಿ.",
			"35": "<sup>35</sup> ಆಮೇಲೆ ನೀವು ಅವನ ಹಿಂದೆ ಬನ್ನಿರಿ; ಅವನು ಅರಮನೆಯನ್ನು ಪ್ರವೇಶಿಸಿ ನನ್ನ ಸಿಂಹಾಸನದ ಮೇಲೆ ಕೂತುಕೊಂಡು ನನಗೆ ಬದಲಾಗಿ ಆಳಬೇಕು. ನಾನು ಇಸ್ರಾಯೇಲ್ಯರ ಮತ್ತು ಯೆಹೂದ್ಯರ ಮೇಲೆ ಅವನನ್ನೇ ಪ್ರಭುವನ್ನಾಗಿ ನೇಮಿಸಿದ್ದೇನೆ ಅಂದನು.",
			"36": "<sup>36</sup> ಯೆಹೋಯಾದಾವನ ಮಗನಾದ ಬೆನಾಯನು ಅರಸನಿಗೆ - ಹಾಗಾಗಲಿ; ನನ್ನ ಒಡೆಯನಾದ ಅರಸನ ದೇವರಾದ ಯೆಹೋವನು ಅದನ್ನು ಸ್ಥಿರಪಡಿಸಲಿ.",
			"37": "<sup>37</sup> ಆತನು ನನ್ನ ಒಡೆಯನಾದ ಅರಸನ ಸಂಗಡ ಇದ್ದ ಪ್ರಕಾರ ಸೊಲೊಮೋನನ ಸಂಗಡಲೂ ಇದ್ದು ರಾಜ್ಯವನ್ನು ನನ್ನ ಒಡೆಯನೂ ಅರಸನೂ ಆದ ನಿನ್ನ ಕಾಲದಲ್ಲಿದ್ದದ್ದಕ್ಕಿಂತ ಅಭಿವೃದ್ಧಿಮಾಡಲಿ ಎಂದು ನುಡಿದನು.",
			"38": "<sup>38</sup> ಆಗ ಯಾಜಕನಾದ ಚಾದೋಕ್, ಪ್ರವಾದಿಯಾದ ನಾತಾನ್, ಯೆಹೋಯಾದಾವನ ಮಗನಾದ ಬೆನಾಯ ಎಂಬವರು ಕೆರೇತ್ಯ ಪೆಲೇತ್ಯ ಎಂಬ ಕಾವಲುದಂಡುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಸೊಲೊಮೋನನನ್ನು ಅರಸನಾದ ದಾವೀದನ ಹೇಸರಕತ್ತೆಯ ಮೇಲೆ ಕುಳ್ಳಿರಿಸಿ ಗೀಹೋನಿಗೆ ಕರಕೊಂಡು ಹೋದರು.",
			"39": "<sup>39</sup> ಯಾಜಕನಾದ ಚಾದೋಕನು ದೇವದರ್ಶನದ ಗುಡಾರದಲ್ಲಿರುವ ಎಣ್ಣೆಯ ಕೊಂಬನ್ನು ತೆಗೆದುಕೊಂಡು ಸೊಲೊಮೋನನನ್ನು ಅಭಿಷೇಕಿಸಿದನು. ಕೂಡಲೆ ಕೊಂಬನ್ನೂದಿ ಎಲ್ಲಾ ಜನರು - ಅರಸನಾದ ಸೊಲೊಮೋನನು ಚಿರಂಜೀವಿಯಾಗಿರಲಿ ಎಂದು ಆರ್ಭಟಿಸಿದರು.",
			"40": "<sup>40</sup> ತರುವಾಯ ಅವರೆಲ್ಲರೂ ಅವನನ್ನು ಹಿಂಬಾಲಿಸಿ ಕೊಳಲೂದುತ್ತಾ ಬಹುಸಂತೋಷದಿಂದ ಆರ್ಭಟಿಸುತ್ತಾ ತಿರಿಗಿ ಬಂದರು. ಅವರ ಕೂಗಿನಿಂದ ಭೂಮಿಯು ಸೀಳಿಹೋಗುತ್ತದೋ ಎಂಬಂತೆ ಆಯಿತು.",
			"41": "<sup>41</sup> ಅದೋನೀಯನೂ ಅವನ ಸಂಗಡ ಇದ್ದ ಅತಿಥಿಗಳೂ ಭೋಜನವನ್ನು ತೀರಿಸುವಷ್ಟರಲ್ಲೇ ಅವರಿಗೆ ಈ ಕೂಗು ಕೇಳಿಸಿತು. ಯೋವಾಬನು ಕೊಂಬಿನ ಧ್ವನಿಯನ್ನು ಕೇಳಿದಾಗ - ಪಟ್ಟಣದಲ್ಲಿ ಇಂಥ ಗದ್ದಲವೇಕೆ ಎಂದು ವಿಚಾರಿಸುವಾಗಲೇ",
			"42": "<sup>42</sup> ಯಾಜಕನಾದ ಎಬ್ಯಾತಾರನ ಮಗ ಯೋನಾತಾನನು ಅಲ್ಲಿಗೆ ಬಂದನು. ಅದೋನೀಯನು ಅವನಿಗೆ - ಬಾ, ನೀನು ಯೋಗ್ಯಪುರುಷನೂ ಶುಭವರ್ತಮಾನತರುವವನೂ ಆಗಿರುತ್ತೀ ಅಂದನು.",
			"43": "<sup>43</sup> ಯೋನಾತಾನನು ಅವನಿಗೆ - ಶುಭವಾರ್ತೆಯೆಲ್ಲಿ! ನಮ್ಮೊಡೆಯನೂ ಅರಸನೂ ಆದ ದಾವೀದನು ಸೊಲೊಮೋನನನ್ನು ಅರಸನನ್ನಾಗಿ ಮಾಡಿದ್ದಾನೆ.",
			"44": "<sup>44</sup> ಅರಸನು ಯಾಜಕನಾದ ಚಾದೋಕ್, ಪ್ರವಾದಿಯಾದ ನಾತಾನ್, ಯೆಹೋಯಾದಾವನ ಮಗನಾದ ಬೆನಾಯ ಎಂಬವರನ್ನೂ ಕೆರೇತ್ಯ ಪೆಲೇತ್ಯರನ್ನೂ ಅವನ ಜೊತೆಯಲ್ಲಿ ಕಳುಹಿಸಿದ್ದಾನೆ. ಅವರು ಅವನನ್ನು ಅರಸನ ಹೇಸರ ಕತ್ತೆಯ ಮೇಲೆ ಕುಳ್ಳಿರಿಸಿದ್ದಾರೆ.",
			"45": "<sup>45</sup> ಯಾಜಕನಾದ ಚಾದೋಕನೂ ಪ್ರವಾದಿಯಾದ ನಾತಾನನೂ ಗೀಹೋನಿನ ಬಳಿಯಲ್ಲಿ ಅವನಿಗೆ ರಾಜ್ಯಾಭಿಷೇಕಮಾಡಿದರು. ಅಲ್ಲಿಂದ ಅವರು ಮಹಾಸಂತೋಷದಿಂದ ಆರ್ಭಟಿಸುತ್ತಾ ಪಟ್ಟಣಕ್ಕೆ ಬಂದಿದ್ದಾರೆ. ಇದರಿಂದಲೇ ಪಟ್ಟಣದಲ್ಲಿ ಗದ್ದಲವುಂಟಾಗಿದೆ; ನೀವು ಕೇಳಿದ ಆರ್ಭಟವು ಇದೇ.",
			"46": "<sup>46</sup> ಇದಲ್ಲದೆ ಸೊಲೊಮೋನನು ರಾಜಸಿಂಹಾಸನದ ಮೇಲೆ ಕೂತುಕೊಂಡಿದ್ದಾನೆ.",
			"47": "<sup>47</sup> ಅರಸನಾದ ದಾವೀದನ ಸೇವಕರು ನಮ್ಮ ಒಡೆಯನೂ ಆರಸನೂ ಆದ ದಾವೀದನ ಮುಂದೆ ಬಂದು - ನಿನ್ನ ದೇವರು ನಿನ್ನ ಹೆಸರಿಗಿಂತಲೂ ಸೊಲೊಮೋನನ ಹೆಸರನ್ನು ಪ್ರಸಿದ್ಧಿಗೆ ತರಲಿ. ರಾಜ್ಯವನ್ನು ನಿನ್ನ ಕಾಲದಲ್ಲಿದ್ದದ್ದಕ್ಕಿಂತ ಅವನ ಕಾಲದಲ್ಲಿ ಹೆಚ್ಚು ಬಲಪಡಿಸಲಿ ಎಂದು ಅವನನ್ನು ಹರಸಿದ್ದಾರೆ; ಅರಸನು ತನ್ನ ಹಾಸಿಗೆಯ ಮೇಲೆ ಬಿದ್ದುಕೊಂಡು -",
			"48": "<sup>48</sup> ನಾನು ಕಣ್ಣಾರೆ ಕಾಣುವಂತೆ ಇಂದು ನನ್ನ ಸಿಂಹಾಸನಕ್ಕೆ ಒಬ್ಬ ಉತ್ತರಾಧಿಕಾರಿಯನ್ನು ದಯಪಾಲಿಸಿದ ಇಸ್ರಾಯೇಲ್ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಸ್ತೋತ್ರವಾಗಲಿ ಎಂದು ಹೇಳಿದ್ದಾನೆ ಅಂದನು.",
			"49": "<sup>49</sup> ಆಗ ಅದೋನೀಯನಿಂದ ಔತಣಕ್ಕೆ ಕರೆಯಲ್ಪಟ್ಟವರೆಲ್ಲರೂ ಭಯಪಟ್ಟು ತಮ್ಮತಮ್ಮ ಮನೆಗಳಿಗೆ ಹೊರಟುಹೋದರು.",
			"50": "<sup>50</sup> ಅದೋನೀಯನು ಸೊಲೊಮೋನನಿಗೆ ಹೆದರಿ ಹೊರಟುಹೋಗಿ ಯಜ್ಞವೇದಿಯ ಕೊಂಬುಗಳನ್ನು ಹಿಡಿದನು.",
			"51": "<sup>51</sup> ದೂತರು ಬಂದು ಸೊಲೊಮೋನನಿಗೆ - ಅದೋನೀಯನು ನಿನಗೆ ಹೆದರಿ ಯಜ್ಞವೇದಿಯ ಕೊಂಬುಗಳನ್ನು ಹಿಡಿದು - ಅರಸನಾದ ಸೊಲೊಮೋನನು ನನ್ನನ್ನು ಕತ್ತಿಯಿಂದ ಸಂಹರಿಸುವದಿಲ್ಲವೆಂಬದಾಗಿ ಈ ಹೊತ್ತು ನನಗೆ ಪ್ರಮಾಣಮಾಡಬೇಕೆಂದು ಬೇಡಿಕೊಳ್ಳುತ್ತಾನೆ ಎಂಬದನ್ನು ತಿಳಿಸಿದಾಗ",
			"52": "<sup>52</sup> ಅವನು - ಅದೋನೀಯನು ಸತ್ಪುರುಷನಾಗಿ ನಡೆಯುವದಾದರೆ ಅವನ ಕೂದಲುಗಳಲ್ಲಿ ಒಂದನ್ನೂ ನೆಲಕ್ಕೆ ಬೀಳಗೊಡುವದಿಲ್ಲ; ಅವನಲ್ಲಿ ಕೆಟ್ಟತನವು ಕಂಡುಬರುವದಾದರೆ ಅವನು ಸಾಯುವನು ಎಂದು ಹೇಳಿ",
			"53": "<sup>53</sup> ಆಳುಗಳನ್ನು ಕಳುಹಿಸಿ ಅವನನ್ನು ಯಜ್ಞವೇದಿಯ ಬಳಿಯಿಂದ ತರಿಸಿದನು. ಅವನು ಬಂದಾಗ ಅರಸನಾದ ಸೊಲೊಮೋನನಿಗೆ ಸಾಷ್ಟಾಂಗನಮಸ್ಕಾರ ಮಾಡಿದನು. ಸೊಲೊಮೋನನು ಅವನಿಗೆ ಮನೆಗೆ ಹೋಗ ಅಪ್ಪಣೆಕೊಟ್ಟನು."
		},
		"2": {
			"1": "<sup>1</sup> ದಾವೀದನ ಅವಸಾನಕಾಲವು ಸಮೀಪಿಸಿದಾಗ ಅವನು ತನ್ನ ಮಗನಾದ ಸೊಲೊಮೋನನಿಗೆ ಆಜ್ಞಾಪಿಸಿದ್ದೇನಂದರೆ -",
			"2": "<sup>2</sup> ಭೂಲೋಕದವರೆಲ್ಲರೂ ಹೋಗುವ ದಾರಿಯನ್ನು ನಾನೂ ಈಗ ಹಿಡಿಯಬೇಕು; ನೀನು ಧೈರ್ಯದಿಂದಿರು, ನಿನ್ನ ಪೌರುಷವನ್ನು ತೋರಿಸು.",
			"3": "<sup>3</sup> ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನ ಕಟ್ಟಳೆಯನ್ನು ಕೈಕೊಂಡು ಆತನ ಮಾರ್ಗದಲ್ಲೇ ನಡೆದುಕೋ; ಮೋಶೆಯ ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿ ಬರೆದಿರುವ ಆತನ ಆಜ್ಞಾನಿಯಮ ವಿಧಿನಿರ್ಣಯಗಳನ್ನು ಪಾಲಿಸು. ಹೀಗೆ ಮಾಡುವದಾದರೆ ನೀನು ಯಾವದನ್ನು ಮಾಡಿದರೂ ಎಲ್ಲಿಗೆ ಹೋದರೂ ಕೃತಾರ್ಥನಾಗುವಿ.",
			"4": "<sup>4</sup> ಮತ್ತು ಯೆಹೋವನು - ನಿನ್ನ ಸಂತಾನದವರು ನಂಬಿಗಸ್ತರಾಗಿ ಪೂರ್ಣಮನಸ್ಸಿನಿಂದಲೂ ಪೂರ್ಣಪ್ರಾಣದಿಂದಲೂ ನನಗೆ ನಡೆದುಕೊಳ್ಳುವದರಲ್ಲಿ ಜಾಗರೂಕರಾಗಿರುವದಾದರೆ ಅವರು ತಪ್ಪದೆ ಇಸ್ರಾಯೇಲ್ ಸಿಂಹಾಸನದ ಮೇಲೆ ಕೂತುಕೊಳ್ಳುವರು ಎಂಬದಾಗಿ ತಾನು ನನಗೆ ಮಾಡಿದ ವಾಗ್ದಾನವನ್ನು ಸ್ಥಿರಪಡಿಸುವನು.",
			"5": "<sup>5</sup> ಚೆರೂಯಳ ಮಗನಾದ ಯೋವಾಬನು ನನಗೆ ಮಾಡಿರುವದನ್ನು ಬಲ್ಲೆಯಲ್ಲಾ; ಅವನು ಇಬ್ಬರು ಇಸ್ರಾಯೇಲ್ ಸೇನಾಪತಿಗಳಾದ ನೇರನ ಮಗ ಅಬ್ನೇರನನ್ನೂ ಯೆತೆರನ ಮಗ ಅಮಾಸನನ್ನೂ ಕೊಂದುಹಾಕಿದನು. ಯುದ್ಧಕಾಲದಲ್ಲಿಯೋ ಎಂಬಂತೆ ಸಮಾಧಾನಕಾಲದಲ್ಲಿಯೂ ರಕ್ತ ಸುರಿಸಿ ಅದನ್ನು ತನ್ನ ನಡುಕಟ್ಟಿಗೂ ಪಾದರಕ್ಷೆಗಳಿಗೂ ಹಚ್ಚಿಕೊಂಡನು.",
			"6": "<sup>6</sup> ಅವನಿಗೇನು ಮಾಡಬೇಕೆಂಬದು ಬುದ್ಧಿವಂತನಾದ ನಿನಗೆ ಗೊತ್ತುಂಟಲ್ಲಾ; ಅವನ ಮುದಿ ತಲೆಯು ಸಮಾಧಿಯನ್ನು ಸಮಾಧಾನದಿಂದ ಸೇರದಂತೆ ಮಾಡು.",
			"7": "<sup>7</sup> ಆದರೆ ನೀನು ಗಿಲ್ಯಾದ್ಯನಾದ ಬರ್ಜಿಲ್ಲೈಯ ಮಕ್ಕಳಿಗೆ ಉಪಕಾರಮಾಡಬೇಕು; ಅವರು ನಿತ್ಯವೂ ನಿನ್ನ ಪಂಕ್ತಿಯಲ್ಲಿ ಊಟಮಾಡುತ್ತಿರಬೇಕು. ಯಾಕಂದರೆ ನಾನು ನಿನ್ನ ಸಹೋದರನಾದ ಅಬ್ಷಾಲೋಮನ ಬಳಿಯಿಂದ ಓಡಿಹೋದಾಗ ಅವರು ನನ್ನನ್ನು ಉಪಚರಿಸಿದರು.",
			"8": "<sup>8</sup> ಬೆನ್ಯಾಮೀನ್ಯನಾದ ಗೇರನ ಮಗನೂ ಬಹುರೀಮ್ ಊರಿನವನೂ ಆದ ಶಿಮ್ಮಿಯು ನಿನ್ನ ಬಳಿಯಲ್ಲಿರುತ್ತಾನಲ್ಲಾ; ನಾನು ಮಹನಯಿಮಿಗೆ ಹೋದಾಗ ಅವನು ನನ್ನನ್ನು ಬಹುಕ್ರೂರವಾಗಿ ಶಪಿಸಿದನು; ಅವನು ನನ್ನನ್ನು ಎದುರುಗೊಳ್ಳುವದಕ್ಕೆ ಯೊರ್ದನಿಗೆ ಬಂದಾಗ ನಾನು ಅವನಿಗೆ - ನಿನ್ನನ್ನು ಕೊಲ್ಲುವದಿಲ್ಲ ಎಂಬದಾಗಿ ಯೆಹೋವನ ಹೆಸರಿನಲ್ಲಿ ಪ್ರಮಾಣ ಮಾಡಿದೆನು.",
			"9": "<sup>9</sup> ನೀನಾದರೋ ಅವನನ್ನು ಶಿಕ್ಷಿಸದೆ ಬಿಡಬೇಡ; ನೀನು ಬುದ್ಧಿವಂತನಲ್ಲವೋ! ಅವನ ಮುದಿ ತಲೆಯು ರಕ್ತಮಯವಾಗಿ ಸಮಾಧಿ ಸೇರುವಂತೆ ಏನು ಮಾಡಬೇಕೆಂಬದು ನಿನಗೆ ಗೊತ್ತಿರುವದು ಎಂಬದೇ.",
			"10": "<sup>10</sup> ಅನಂತರ ದಾವೀದನು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಿದನು. ಅವನ ಶವವನ್ನು ದಾವೀದ ನಗರದಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು.",
			"11": "<sup>11</sup> ದಾವೀದನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಆಳಿದ್ದು ನಾಲ್ವತ್ತು ವರುಷ. ಹೇಗಂದರೆ - ಹೆಬ್ರೋನಿನಲ್ಲಿ ಏಳು ವರುಷ, ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಮೂವತ್ತ ಮೂರು ವರುಷ ಆಳಿದನು.",
			"12": "<sup>12</sup> ಸೊಲೊಮೋನನು ತನ್ನ ತಂದೆಯಾದ ದಾವೀದನ ಸಿಂಹಾಸನವನ್ನು ಏರಿದನು. ಅವನ ರಾಜ್ಯವು ಬಹಳವಾಗಿ ಬಲಗೊಂಡಿತು.",
			"13": "<sup>13</sup> ಹಗ್ಗೀತಳ ಮಗನಾದ ಅದೋನೀಯನು ಸೊಲೊಮೋನನ ತಾಯಿಯಾದ ಬತ್ಷೆಬೆಯ ದರ್ಶನಕ್ಕೆ ಬಂದನು. ಆಕೆಯು ಅವನನ್ನು - ನೀನು ಸಮಾಧಾನದಿಂದ ಬಂದಿರುತ್ತೀಯೋ ಎಂದು ಕೇಳಲು ಅವನು - ಹೌದು, ಸಮಾಧಾನದಿಂದ ಬಂದೆನು ಎಂದನು.",
			"14": "<sup>14</sup> ಆಮೇಲೆ ಅವನು - ನಿನಗೊಂದು ಮಾತು ಹೇಳಬೇಕೆಂದು ಬಂದಿದ್ದೇನೆ ಅನ್ನಲು ಆಕೆಯು - ಹೇಳು ಅಂದಳು.",
			"15": "<sup>15</sup> ಆಗ ಅವನು - ರಾಜ್ಯವು ನನಗೆ ಬರಬೇಕಾಗಿತ್ತು; ನಾನೇ ಅರಸನಾಗುವೆನೆಂದು ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರೂ ಎದುರು ನೋಡುತ್ತಿದ್ದರೆಂದು ನೀನು ಬಲ್ಲೆಯಷ್ಟೆ; ಆದರೆ ಅದು ತಪ್ಪಿ ನನ್ನ ತಮ್ಮನಿಗೆ ಹೋಯಿತು. ಅದು ಯೆಹೋವನಿಂದಲೇ ಅವನಿಗೆ ದೊರಕಿತು.",
			"16": "<sup>16</sup> ಅದಿರಲಿ, ಒಂದು ಬಿನ್ನಹವಿದೆ; ಅದನ್ನು ತಿರಸ್ಕರಿಸಬೇಡ ಎನ್ನಲು ಆಕೆಯು - ಅದೇನು, ಹೇಳು ಅಂದಾಗ",
			"17": "<sup>17</sup> ಅವನು - ದಯವಿಟ್ಟು ಶೂನೇಮ್ಯಳಾದ ಅಬೀಷಗ್ ಎಂಬಾಕೆಯನ್ನು ನನಗೆ ಹೆಂಡತಿಯನ್ನಾಗಿ ಕೊಡಬೇಕೆಂದು ಅರಸನಾದ ಸೊಲೊಮೋನನಿಗೆ ಹೇಳು; ಅವನು ನಿನ್ನ ಮಾತನ್ನು ತಳ್ಳುವದಿಲ್ಲ ಅಂದನು.",
			"18": "<sup>18</sup> ಬತ್ಷೆಬೆಯು - ಒಳ್ಳೇದು, ನಾನು ನಿನಗೋಸ್ಕರ ಸನ್ನಿಧಿಯಲ್ಲಿ ಅರಿಕೆಮಾಡುತ್ತೇನೆ ಎಂದು ಹೇಳಿದಳು.",
			"19": "<sup>19</sup> ಆಕೆಯು ಅದೋನೀಯನಿಗಾಗಿ ಮಾತಾಡುವದಕ್ಕೋಸ್ಕರ ಅರಸನಾದ ಸೊಲೊಮೋನನ ಬಳಿಗೆ ಬರುತ್ತಿರುವಾಗ ಅರಸನು ಎದ್ದುಹೋಗಿ ಆಕೆಯನ್ನು ಎದುರುಗೊಂಡು ನಮಸ್ಕರಿಸಿದನು. ಅನಂತರ ತಾನು ಸಿಂಹಾಸನದ ಮೇಲೆ ಕೂತು ರಾಜಮಾತೆಗೋಸ್ಕರ ಒಂದು ಆಸನವನ್ನು ತರಿಸಲು ಆಕೆಯು ಅವನ ಬಲಗಡೆಯಲ್ಲಿ ಕೂತುಕೊಂಡು -",
			"20": "<sup>20</sup> ಒಂದು ಸಣ್ಣ ಬಿನ್ನಹ ಉಂಟು, ಅದನ್ನು ತಿರಸ್ಕರಿಸಬೇಡ ಎಂದು ಹೇಳಿದಳು. ಅರಸನು - ನನ್ನ ತಾಯಿಯೇ, ಏನು ಬೇಕು, ಕೇಳು; ನಾನು ಆಗದು ಅನ್ನುವದಿಲ್ಲ ಎಂದನ್ನಲು",
			"21": "<sup>21</sup> ಆಕೆಯು - ಶೂನೇಮ್ಯಳಾದ ಅಬೀಷಗ್ ಎಂಬಾಕೆಯು ನಿನ್ನ ಅಣ್ಣನಾದ ಅದೋನೀಯನಿಗೆ ಹೆಂಡತಿಯಾಗಿ ಕೊಡಲ್ಪಡಲಿ ಅಂದಳು.",
			"22": "<sup>22</sup> ಆಗ ಅರಸನಾದ ಸೊಲೊಮೋನನು ತನ್ನ ತಾಯಿಗೆ - ಅದೋನೀಯನಿಗೋಸ್ಕರ ಶೂನೇಮ್ಯಳಾದ ಅಬೀಷಗ್ ಎಂಬಾಕೆಯನ್ನು ಮಾತ್ರ ಕೇಳುವದೇಕೆ? ಅವನಿಗೆ ರಾಜ್ಯವನ್ನೂ ಕೊಡಬೇಕೆಂದು ಕೇಳು; ಅವನು ನನ್ನ ಅಣ್ಣನಲ್ಲವೇ; ಯಾಜಕನಾದ ಎಬ್ಯಾತಾರನೂ ಚೆರೂಯಳ ಮಗನಾದ ಯೋವಾಬನೂ ಅವನ ಪಕ್ಷದವರಾಗಿದ್ದಾರೆ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"23": "<sup>23</sup> ಇದಲ್ಲದೆ ಅರಸನಾದ ಸೊಲೊಮೋನನು - ಯೆಹೋವನಾಣೆ, ಅದೋನೀಯನ ಈ ಮಾತಿಗೋಸ್ಕರ ನಾನು ಅವನನ್ನು ಮರಣಕ್ಕೆ ಪಾತ್ರನೆಂದು ಎಣಿಸದಿದ್ದರೆ ದೇವರು ನನಗೆ ಬೇಕಾದದ್ದನ್ನು ಮಾಡಲಿ.",
			"24": "<sup>24</sup> ನನ್ನ ತಂದೆಯಾದ ದಾವೀದನ ಸಿಂಹಾಸನದ ಮೇಲೆ ನನ್ನನ್ನು ಕುಳ್ಳಿರಿಸಿ ಬಲಪಡಿಸಿ ತಾನು ವಾಗ್ದಾನಮಾಡಿದಂತೆ ನನಗೆ ಮನೆಯನ್ನು ಕಟ್ಟಿದ ಯೆಹೋವನಾಣೆ, ಅದೋನೀಯನು ಈಹೊತ್ತೇ ಸಾಯಬೇಕು ಎಂದು ಹೇಳಿ",
			"25": "<sup>25</sup> ಯೆಹೋಯಾದಾವನ ಮಗನಾದ ಬೆನಾಯನಿಗೆ ಆಜ್ಞಾಪಿಸಿದದರಿಂದ ಅವನು ಹೋಗಿ ಅವನನ್ನು ಹೊಡೆದು ಕೊಂದುಹಾಕಿದನು.",
			"26": "<sup>26</sup> ತರುವಾಯ ಅರಸನು ಯಾಜಕನಾದ ಎಬ್ಯಾತಾರನಿಗೆ - ನೀನು ಅಣತೋತಿನಲ್ಲಿರುವ ನಿನ್ನ ಸ್ವಾಸ್ತ್ಯಕ್ಕೆ ಹೋಗು; ನೀನು ಮರಣಕ್ಕೆ ಪಾತ್ರನು. ಆದರೆ ನೀನು ಕರ್ತನಾದ ಯೆಹೋವನ ಮಂಜೂಷವನ್ನು ಹೊತ್ತುಕೊಂಡು ನನ್ನ ತಂದೆಯಾದ ದಾವೀದನೊಡನೆ ಸಂಚರಿಸುತ್ತಾ ಅವನ ಎಲ್ಲಾ ಕಷ್ಟಗಳಲ್ಲಿ ಪಾಲುಗಾರನಾಗಿದ್ದದರಿಂದ",
			"27": "<sup>27</sup> ಈಹೊತ್ತು ನಿನ್ನನ್ನು ಕೊಲ್ಲಿಸುವದಿಲ್ಲ ಎಂದು ಹೇಳಿ ಅವನನ್ನು ಯೆಹೋವನ ಯಾಜಕೋದ್ಯೋಗದಿಂದ ತಳ್ಳಿಬಿಟ್ಟನು. ಹೀಗೆ ಯೆಹೋವನು ಶೀಲೋವಿನಲ್ಲಿದ್ದ ಏಲಿಯ ಮನೆಯನ್ನು ಕುರಿತು ಹೇಳಿದ ಮಾತು ನೆರವೇರಿತು.",
			"28": "<sup>28</sup> ಈ ವರ್ತಮಾನವು ಯೋವಾಬನಿಗೆ ಮುಟ್ಟಿದ ಕೂಡಲೆ ಅವನು ಯೆಹೋವನ ಗುಡಾರಕ್ಕೆ ಓಡಿಹೋಗಿ ಯಜ್ಞವೇದಿಯ ಕೊಂಬುಗಳನ್ನು ಹಿಡಿದನು. (ಅವನು ಅಬ್ಷಾಲೋಮನ ಪಕ್ಷವನ್ನು ಹಿಡಿಯದಿದ್ದರೂ ಅದೋನೀಯನ ಪಕ್ಷವನ್ನು ಹಿಡಿದಿದ್ದನು.)",
			"29": "<sup>29</sup> ಯೋವಾಬನು ಯೆಹೋವನ ಗುಡಾರಕ್ಕೆ ಓಡಿಹೋಗಿ ಯಜ್ಞವೇದಿಯ ಹತ್ತಿರ ಇದ್ದಾನೆ ಎಂಬ ಸುದ್ದಿಯು ಅರಸನಾದ ಸೊಲೊಮೋನನಿಗೆ ತಲುಪಿದಾಗ ಅವನು ಯೆಹೋಯಾದಾವನ ಮಗನಾದ ಬೆನಾಯನಿಗೆ - ಹೋಗಿ ಅವನ ಮೇಲೆ ಬೀಳು ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"30": "<sup>30</sup> ಬೆನಾಯನು ಯೆಹೋವನ ಗುಡಾರಕ್ಕೆ ಹೋಗಿ ಯೋವಾಬನಿಗೆ - ಅರಸನು ನಿನಗೆ ಹೊರಗೆ ಬರಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸುತ್ತಾನೆ ಎಂದು ಹೇಳಲು ಅವನು - ಬರುವದಿಲ್ಲ, ನಾನು ಇಲ್ಲಿಯೇ ಸಾಯುತ್ತೇನೆ ಎಂದು ಉತ್ತರ ಕೊಟ್ಟನು. ಬೆನಾಯನು ಅರಸನ ಬಳಿಗೆ ಹೋಗಿ ಯೋವಾಬನು ಅಂದದ್ದನ್ನು ತಿಳಿಸಲು",
			"31": "<sup>31</sup> ಅರಸನು ಅವನಿಗೆ - ಅವನು ಹೇಳಿದಂತೆ ಮಾಡು; ಅವನನ್ನು ಕೊಂದು ಅವನ ಶವವನ್ನು ಸಮಾಧಿಮಾಡು. ಯೋವಾಬನು ನಿಷ್ಕಾರಣವಾಗಿ ರಕ್ತಸುರಿಸಿದದರಿಂದ ನನಗೂ ನನ್ನ ತಂದೆಯ ಮನೆಯವರಿಗೂ ಹತ್ತಿರುವ ದೋಷವನ್ನು ಈ ಪ್ರಕಾರ ಪರಿಹರಿಸು.",
			"32": "<sup>32</sup> ಯೆಹೋವನು ಈ ರಕ್ತಾಪರಾಧವನ್ನು ಅವನ ತಲೆಯ ಮೇಲೆಯೇ ಬರಮಾಡಲಿ. ಅವನು ನನ್ನ ತಂದೆಯಾದ ದಾವೀದನಿಗೆ ತಿಳಿಯದೆ ತನಗಿಂತ ಉತ್ತಮರೂ ನೀತಿವಂತರೂ ಆದ ಇಬ್ಬರು ಮನುಷ್ಯರನ್ನು ಅಂದರೆ ಇಸ್ರಾಯೇಲ್ ಸೇನಾಪತಿಯೂ ನೇರನ ಮಗನೂ ಆದ ಅಬ್ನೇರನನ್ನೂ ಯೆಹೂದಸೇನಾಪತಿಯೂ ಯೆತೆರೆನ ಮಗನೂ ಆದ ಅಮಾಸನನ್ನೂ ಕೊಂದನಲ್ಲಾ.",
			"33": "<sup>33</sup> ಈ ಇಬ್ಬರನ್ನೂ ವಧಿಸಿದ ಪಾಪವು ಯೋವಾಬನ ತಲೆಯ ಮೇಲೆಯೂ ಅವನ ಸಂತಾನದವರ ತಲೆಯ ಮೇಲೆಯೂ ಇರಲಿ. ಆದರೆ ದಾವೀದನಿಗೂ ಅವನ ಸಿಂಹಾಸನ ಕುಟುಂಬ ಸಂತಾನಗಳಿಗೂ ಯೆಹೋವನಿಂದ ನಿತ್ಯ ಸೌಭಾಗ್ಯವು ದೊರಕಲಿ ಅಂದನು.",
			"34": "<sup>34</sup> ಯೆಹೋಯಾದಾವನ ಮಗನಾದ ಬೆನಾಯನು ಹೋಗಿ ಯೋವಾಬನನ್ನು ಕೊಂದನು. ಅವನ ಶವಕ್ಕೆ ಅರಣ್ಯದಲ್ಲಿರುವ ಅವನ ಮನೆಯ ನಿವೇಶನದಲ್ಲಿ ಸಮಾಧಿಯಾಯಿತು.",
			"35": "<sup>35</sup> ಅರಸನು ಯೋವಾಬನಿಗೆ ಬದಲಾಗಿ ಯೆಹೋಯಾದಾವನ ಮಗನಾದ ಬೆನಾಯನನ್ನು ಸೈನ್ಯಾಧಿಪತಿಯನ್ನಾಗಿಯೂ ಎಬ್ಯಾತಾರನಿಗೆ ಬದಲಾಗಿ ಚಾದೋಕನನ್ನು ಯಾಜಕನನ್ನಾಗಿಯೂ ನೇಮಿಸಿದನು.",
			"36": "<sup>36</sup> ತರುವಾಯ ಅರಸನು ಶಿಮ್ಮಿಯನ್ನು ಕರಿಸಿ ಅವನಿಗೆ - ನೀನು ಒಂದು ಮನೆಯನ್ನು ಕಟ್ಟಿಸಿಕೊಂಡು ಯೆರೂಸಲೇಮಿನಲ್ಲೇ ವಾಸಿಸಬೇಕು. ಇದನ್ನು ಬಿಟ್ಟು ಎಲ್ಲಿಗೂ ಹೋಗಬಾರದು.",
			"37": "<sup>37</sup> ನೀನು ಇದನ್ನು ಬಿಟ್ಟು ಕಿದ್ರೋನ್‍ಹಳ್ಳದ ಆಚೆಗೆ ಹೋದಿಯಾದರೆ ಅದೇ ದಿವಸದಲ್ಲಿ ನಿನಗೆ ಮರಣಶಿಕ್ಷೆಯಾಗುವದೆಂದು ತಿಳಿದುಕೋ; ಮತ್ತು ಆ ರಕ್ತಾಪರಾಧವು ನಿನ್ನ ತಲೆಯ ಮೇಲೆಯೇ ಇರುವದು ಎಂದು ಹೇಳಿದನು.",
			"38": "<sup>38</sup> ಅದಕ್ಕೆ ಶಿಮ್ಮಿಯು - ಒಳ್ಳೇದು; ನನ್ನ ಒಡೆಯನೂ ಅರಸನೂ ಆದ ನೀನು ಹೇಳಿದಂತೆಯೇ ಸೇವಕನಾದ ನಾನು ಮಾಡುವೆನು ಎಂದು ಉತ್ತರಕೊಟ್ಟು ಬಹುದಿನಗಳವರೆಗೂ ಯೆರೂಸಲೇಮಿನಲ್ಲೆ ವಾಸಿಸುತ್ತಿದ್ದನು.",
			"39": "<sup>39</sup> ಮೂರು ವರುಷಗಳಾದನಂತರ ಶಿಮ್ಮಿಯ ಇಬ್ಬರು ದಾಸರು ಗತ್ ಊರಿನ ಅರಸನೂ ಮಾಕನ ಮಗನೂ ಆದ ಆಕೀಷನ ಬಳಿಗೆ ಓಡಿಹೋದರು.",
			"40": "<sup>40</sup> ದಾಸರು ಗತ್ ಊರಿನಲ್ಲಿರುವದು ಶಿಮ್ಮಿಗೆ ಗೊತ್ತಾದಾಗ ಅವನು ಕತ್ತೆಗೆ ತಡಿಹಾಕಿಸಿ ತನ್ನ ದಾಸರನ್ನು ಹುಡುಕುವದಕ್ಕೋಸ್ಕರ ಗತ್ ಊರಿನ ಅರಸನಾದ ಆಕೀಷನ ಬಳಿಗೆ ಹೊರಟುಹೋಗಿ ಅವರನ್ನು ಗತ್ ಊರಿನಿಂದ ಹಿಡಿದುಕೊಂಡು ಬಂದನು.",
			"41": "<sup>41</sup> ಶಿಮ್ಮಿಯು ಯೆರೂಸಲೇಮಿನಿಂದ ಗತ್ ಊರಿಗೆ ಹೋಗಿ ಬಂದದ್ದು ಅರಸನಾದ ಸೊಲೊಮೋನನಿಗೆ ತಿಳಿದುಬಂದಾಗ ಅವನು ಅವನನ್ನು ಕರಿಸಿ ಅವನಿಗೆ -",
			"42": "<sup>42</sup> ನೀನು ಈ ಊರನ್ನು ಬಿಟ್ಟು ಎಲ್ಲಿಗಾದರೂ ಹೋದರೆ ಅದೇ ದಿನದಲ್ಲಿ ನಿನಗೆ ಮರಣಶಿಕ್ಷೆಯಾಗುವದೆಂದು ಖಂಡಿತವಾಗಿ ಹೇಳಿ ಯೆಹೋವನ ಹೆಸರಿನಲ್ಲಿ ನಿನ್ನಿಂದ ಪ್ರಮಾಣತೆಗೆದುಕೊಂಡೆನಲ್ಲವೋ?",
			"43": "<sup>43</sup> ಆಗ ನೀನು - ಒಳ್ಳೇದು, ಹಾಗೆಯೇ ಮಾಡುತ್ತೇನೆ ಎಂದು ಹೇಳಿದಿಯಲ್ಲಾ. ಹಾಗಾದರೆ ಯೆಹೋವನ ಹೆಸರಿನಲ್ಲಿ ನೀನು ಮಾಡಿದ ಪ್ರಮಾಣವನ್ನೂ ನಾನು ನಿನಗೆ ಕೊಟ್ಟ ಅಪ್ಪಣೆಯನ್ನೂ ಯಾಕೆ ಕೈಕೊಳ್ಳಲಿಲ್ಲ ಅಂದನು.",
			"44": "<sup>44</sup> ಇದಲ್ಲದೆ ಅರಸನು ಅವನಿಗೆ - ನೀನು ನನ್ನ ತಂದೆಯಾದ ದಾವೀದನಿಗೆ ವಿರೋಧವಾಗಿ ಮಾಡಿದ ಪಾಪಕ್ಕೆ ಮನವೇ ಸಾಕ್ಷಿ; ಯೆಹೋವನು ಅದರ ಫಲವನ್ನು ನಿನ್ನ ತಲೆಯ ಮೇಲೆ ಬರಮಾಡುವನು.",
			"45": "<sup>45</sup> ಆದರೆ ಸೊಲೊಮೋನ ರಾಜನಿಗೆ ಆಶೀರ್ವಾದವಾಗಲಿ; ದಾವೀದನ ಸಿಂಹಾಸನವು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸದಾಕಾಲವೂ ಇರಲಿ ಎಂದು ಹೇಳಿ",
			"46": "<sup>46</sup> ಯೆಹೋಯಾದಾವನ ಮಗನಾದ ಬೆನಾಯನಿಗೆ ಅವನನ್ನು ಸಂಹರಿಸೆಂದು ಆಜ್ಞಾಪಿಸಲು ಇವನು ಹೊರಗೆ ಹೋಗಿ ಅವನ ಮೇಲೆ ಬಿದ್ದು ಅವನನ್ನು ಕೊಂದುಹಾಕಿದನು. ಹೀಗೆ ಸೊಲೊಮೋನನ ರಾಜ್ಯಾಧಿಕಾರವು ಸ್ಥಿರವಾಯಿತು."
		},
		"3": {
			"1": "<sup>1</sup> ಸೊಲೊಮೋನನು ಐಗುಪ್ತದ ಅರಸನಾದ ಫರೋಹನ ಅಳಿಯನಾದನು. ಅವನು ಫರೋಹನ ಮಗಳನ್ನು ಮದುವೆಮಾಡಿಕೊಂಡು ತನ್ನ ಅರಮನೆಯನ್ನೂ ಯೆಹೋವನ ಆಲಯವನ್ನೂ ಯೆರೂಸಲೇಮಿನ ಸುತ್ತಣ ಗೋಡೆಯನ್ನೂ ಕಟ್ಟಿ ತೀರಿಸುವ ತನಕ ಆಕೆಯನ್ನು ದಾವೀದನ ನಗರದಲ್ಲಿ ಇರಿಸಿದನು.",
			"2": "<sup>2</sup> ಆವರೆಗೂ ಯೆಹೋವನಾಮಕ್ಕೋಸ್ಕರ ಆಲಯವಿರಲಿಲ್ಲವಾದದರಿಂದ ಜನರು ಪೂಜಾಸ್ಥಳಗಳಲ್ಲಿ ಯಜ್ಞಮಾಡುತ್ತಿದ್ದರು.",
			"3": "<sup>3</sup> ಸೊಲೊಮೋನನು ಯೆಹೋವನನ್ನು ಪ್ರೀತಿಸಿ ತನ್ನ ತಂದೆಯಾದ ದಾವೀದನ ವಿಧಿಗಳನ್ನು ಕೈಕೊಳ್ಳುವವನಾಗಿದ್ದರೂ ಆ ಪೂಜಾಸ್ಥಳಗಳಲ್ಲಿಯೇ ಯಜ್ಞವರ್ಪಿಸುತ್ತಿದ್ದನು; ಅಲ್ಲಿಯೇ ಧೂಪಸುಡುತ್ತಿದ್ದನು.",
			"4": "<sup>4</sup> ಅರಸನಾದ ಸೊಲೊಮೋನನು ಯಜ್ಞಮಾಡುವದಕ್ಕೋಸ್ಕರ ಪೂಜಾಸ್ಥಳಗಳಲ್ಲಿ ವಿಶೇಷವಾಗಿದ್ದ ಗಿಬ್ಯೋನಿಗೆ ಹೋಗಿ ಅಲ್ಲಿನ ವೇದಿಯ ಮೇಲೆ ಸಹಸ್ರ ಯಜ್ಞಗಳನ್ನು ಸಮರ್ಪಿಸಿದಾಗ",
			"5": "<sup>5</sup> ದೇವರಾದ ಯೆಹೋವನು ಆ ರಾತ್ರಿ ಕನಸಿನಲ್ಲಿ ಅವನಿಗೆ ಕಾಣಿಸಿಕೊಂಡು - ನಿನಗೆ ಯಾವ ವರ ಬೇಕು ಕೇಳಿಕೋ ಎಂದು ಹೇಳಲು ಸೊಲೊಮೋನನು -",
			"6": "<sup>6</sup> ನಿನಗೆ ನಂಬಿಗಸ್ತನಾಗಿ ನೀತಿಯಿಂದಲೂ ಯಥಾರ್ಥಚಿತ್ತದಿಂದಲೂ ನಡೆದುಕೊಂಡ ನಿನ್ನ ಸೇವಕನೂ ನನ್ನ ತಂದೆಯೂ ಆದ ದಾವೀದನಿಗೆ ನೀನು ಮಹಾಕೃಪೆಯನ್ನು ತೋರಿಸಿದಿ; ಅವನ ಮೇಲೆ ಬಹಳವಾಗಿ ಕೃಪೆಮಾಡಿ ಈಗಿರುವಂತೆ ಅವನ ಸಿಂಹಾಸನಕ್ಕೆ ಒಬ್ಬ ಮಗನನ್ನು ಅನುಗ್ರಹಿಸಿದ್ದರಲ್ಲಿ ಅದನ್ನು ಪೂರ್ತಿಗೊಳಿಸಿದಿ.",
			"7": "<sup>7</sup> ನನ್ನ ದೇವರಾದ ಯೆಹೋವನೇ, ನನ್ನ ತಂದೆಗೆ ಬದಲಾಗಿ ನಿನ್ನಿಂದ ಅರಸನಾಗಿ ನೇಮಿಸಲ್ಪಟ್ಟ ನಿನ್ನ ಸೇವಕನಾದ ನಾನು ಇನ್ನೂ ಚಿಕ್ಕವನು;",
			"8": "<sup>8</sup> ವ್ಯವಹಾರಜ್ಞಾನವಿಲ್ಲದವನು, ನಿನ್ನ ಸೇವಕನಾದ ನಾನು ಲೆಕ್ಕಿಸಲಾಗದಂಥ ಮಹಾ ಜನಾಂಗವಾಗಿರುವ ನಿನ್ನ ಸ್ವಕೀಯ ಪ್ರಜೆಯ ಮಧ್ಯದಲ್ಲಿದ್ದೇನೆ.",
			"9": "<sup>9</sup> ಆದದರಿಂದ ಅದನ್ನು ಆಳುವದಕ್ಕೂ ನ್ಯಾಯಾನ್ಯಾಯಗಳನ್ನು ಕಂಡುಹಿಡಿಯುವದಕ್ಕೂ ನನಗೆ ವಿವೇಕವನ್ನು ದಯಪಾಲಿಸು. ಈ ಮಹಾ ಜನಾಂಗವನ್ನು ಆಳಲು ಸಮರ್ಥರು ಯಾರು ಎಂದು ಬೇಡಿಕೊಂಡನು.",
			"10": "<sup>10</sup> ಸೊಲೊಮೋನನ ಈ ಬಿನ್ನಹವನ್ನು ಕರ್ತನಾದ ದೇವರು ಮೆಚ್ಚಿದನು.",
			"11": "<sup>11</sup> ಆತನು ಅವನಿಗೆ - ನೀನು ನಿನಗೋಸ್ಕರ ದೀರ್ಘಾಯುಷ್ಯವನ್ನಾಗಲಿ, ಐಶ್ವರ್ಯವನ್ನಾಗಲಿ, ವೈರಿಗಳ ಪ್ರಾಣವನ್ನಾಗಲಿ ಕೇಳಿಕೊಳ್ಳದೆ ನ್ಯಾಯ ನಿರ್ಣಯಿಸುವದಕ್ಕೋಸ್ಕರ",
			"12": "<sup>12</sup> ವಿವೇಕವನ್ನು ಬೇಡಿಕೊಂಡದರಿಂದ ನಿನ್ನ ಬಿನ್ನಹವನ್ನು ನೆರವೇರಿಸಿದ್ದೇನೆ. ನೋಡು, ನಿನಗೆ ಜ್ಞಾನವನ್ನೂ ವಿವೇಕವನ್ನೂ ಅನುಗ್ರಹಿಸಿದ್ದೇನೆ; ನಿನ್ನಂಥ ಜ್ಞಾನಿಯು ಮುಂಚೆ ಇರಲಿಲ್ಲ; ಮುಂದೆಯೂ ಇರುವದಿಲ್ಲ.",
			"13": "<sup>13</sup> ಇದಲ್ಲದೆ ನೀನು ಕೇಳದಂಥದನ್ನೂ ನಿನಗೆ ಅನುಗ್ರಹಿಸಿದ್ದೇನೆ. ನಿನ್ನ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ಐಶ್ವರ್ಯದಲ್ಲಿಯೂ ಘನದಲ್ಲಿಯೂ ನಿನಗೆ ಸಮಾನನಾದ ಅರಸನು ಇನ್ನೊಬ್ಬನಿರುವದಿಲ್ಲ.",
			"14": "<sup>14</sup> ನೀನು ನಿನ್ನ ತಂದೆಯಾದ ದಾವೀದನಂತೆ ನನ್ನ ಮಾರ್ಗದಲ್ಲಿ ನಡೆದು ನನ್ನ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ಕೈಕೊಳ್ಳುವದಾದರೆ ನಿನ್ನ ಆಯುಷ್ಯವನ್ನು ಹೆಚ್ಚಿಸುವೆನು ಅಂದನು.",
			"15": "<sup>15</sup> ಸೊಲೊಮೋನನು ನಿದ್ರೆಯಿಂದ ಎಚ್ಚತ್ತಾಗ ಅದು ಕನಸೆಂದು ತಿಳಿದುಕೊಂಡನು. ಅವನು ಯೆರೂಸಲೇಮಿಗೆ ಬಂದ ನಂತರ ಯೆಹೋವನ ಒಡಂಬಡಿಕೆಯ ಮಂಜೂಷದ ಮುಂದೆ ನಿಂತು ಸರ್ವಾಂಗಹೋಮಗಳನ್ನೂ ಸಮಾಧಾನ ಯಜ್ಞಗಳನ್ನೂ ಸಮರ್ಪಿಸಿ ತನ್ನ ಎಲ್ಲಾ ಸೇವಕರಿಗೋಸ್ಕರ ಔತಣಮಾಡಿಸಿದನು.",
			"16": "<sup>16</sup> ಅದೇ ಕಾಲದಲ್ಲಿ ಇಬ್ಬರು ಸೂಳೆಯರು ಅರಸನ ಸನ್ನಿಧಿಗೆ ಬಂದರು.",
			"17": "<sup>17</sup> ಅವರಲ್ಲಿ ಒಬ್ಬಳು ಅರಸನಿಗೆ - ನನ್ನ ಒಡೆಯಾ, ಕೇಳು; ನಾನೂ ಈ ಹೆಂಗಸೂ ಒಂದೇ ಮನೆಯಲ್ಲಿ ವಾಸಿಸುತ್ತೇವೆ. ಇವಳು ಮನೆಯಲ್ಲಿದ್ದಾಗಲೇ ನಾನು ಮಗುವನ್ನು ಹೆತ್ತೆನು.",
			"18": "<sup>18</sup> ಮೂರನೆಯ ದಿನದಲ್ಲಿ ಇವಳೂ ಹೆತ್ತಳು. ನಾವಿಬ್ಬರೂ ಒಟ್ಟಿಗೆ ಇದ್ದೆವು; ನಮ್ಮಿಬ್ಬರ ಹೊರತು ಆ ಮನೆಯಲ್ಲಿ ಯಾರೂ ಇರಲಿಲ್ಲ.",
			"19": "<sup>19</sup> ಇವಳು ರಾತ್ರಿಯಲ್ಲಿ ತನ್ನ ಕೂಸಿನ ಮೇಲೆ ಹೊರಳಿದ್ದರಿಂದ ಅದು ಸತ್ತಿತು.",
			"20": "<sup>20</sup> ಮಧ್ಯರಾತ್ರಿಯಲ್ಲಿ ಇವಳೆದ್ದು ನಿನ್ನ ದಾಸಿಯಾದ ನಾನು ಗಾಢನಿದ್ರೆಯಲ್ಲಿದ್ದಾಗ ನನ್ನ ಮಗುವನ್ನು ನನ್ನ ಬಳಿಯಿಂದ ತೆಗೆದು ತನ್ನ ಮಗ್ಗುಲಲ್ಲಿಟ್ಟುಕೊಂಡಳು. ಸತ್ತುಹೋದ ತನ್ನ ಮಗುವನ್ನು ನನ್ನ ಮಗ್ಗುಲಲ್ಲಿಟ್ಟಳು.",
			"21": "<sup>21</sup> ನಾನು ಹೊತ್ತಾರೆ ಎದ್ದು ಮಗುವಿಗೆ ಮೊಲೆ ಕುಡಿಸಬೇಕೆಂದಿರುವಾಗ ಅದು ಸತ್ತಿತ್ತು. ಆದರೆ ಅದನ್ನು ಬೆಳಕಿನಲ್ಲಿ ನೋಡಿದಾಗ ಅದು ನಾನು ಹೆತ್ತ ಕೂಸಾಗಿರಲಿಲ್ಲ ಎಂದು ಹೇಳಿದಳು.",
			"22": "<sup>22</sup> ಆಗ ಎರಡನೆಯವಳು - ಹಾಗಲ್ಲ, ಬದುಕಿರುವವನು ನನ್ನ ಮಗನು; ಸತ್ತಿರುವವನು ನಿನ್ನ ಮಗನು ಎಂದು ನುಡಿದಳು. ಮೊದಲನೆಯವಳು ತಿರಿಗಿ - ಅಲ್ಲ, ಸತ್ತಿರುವವನು ನಿನ್ನ ಮಗನು; ಬದುಕಿರುವವನು ನನ್ನ ಮಗನು ಅಂದಳು. ಹೀಗೆ ಅವರು ಅರಸನ ಮುಂದೆ ವಾದಿಸುತ್ತಾ ಇದ್ದರು.",
			"23": "<sup>23</sup> ಆಗ ಅರಸನು - ಜೀವದಿಂದಿರುವ ಕೂಸು ನನ್ನದು, ಸತ್ತಿರುವದು ನಿನ್ನದು ಎಂದು ಒಬ್ಬಳು ಹೇಳುತ್ತಾಳೆ; ಇನ್ನೊಬ್ಬಳು - ಅಲ್ಲ, ಸತ್ತಿರುವದು ನಿನ್ನದು, ಜೀವದಿಂದಿರುವದು ನನ್ನದು ಅನ್ನುತ್ತಾಳೆ.",
			"24": "<sup>24</sup> ನನಗೊಂದು ಕತ್ತಿಯನ್ನು ತಂದುಕೊಡಿರಿ ಎಂದು ಸೇವಕರಿಗೆ ಹೇಳಿದನು.",
			"25": "<sup>25</sup> ಅವರು ತರಲು ಅರಸನು ಅವರಿಗೆ - ಜೀವದಿಂದಿರುವ ಕೂಸನ್ನು ಕಡಿದು ಎರಡು ಭಾಗಮಾಡಿ ಅರ್ಧವನ್ನು ಇವಳಿಗೂ ಅರ್ಧವನ್ನು ಅವಳಿಗೂ ಕೊಡಿರಿ ಎಂದು ಅಪ್ಪಣೆಮಾಡಿದನು.",
			"26": "<sup>26</sup> ಆಗ ಜೀವದಿಂದಿದ್ದ ಕೂಸಿನ ನಿಜವಾದ ತಾಯಿ ಕೂಸಿನ ವಿಷಯದಲ್ಲಿ ಕರುಳುಮರುಗಿ - ನನ್ನ ಒಡೆಯಾ, ಬೇಡ; ಬದುಕಿರುವ ಕೂಸನ್ನು ಅವಳಿಗೇ ಕೊಟ್ಟುಬಿಡು; ಅದನ್ನು ಕೊಲ್ಲಿಸಬೇಡ ಎಂದು ಬೇಡಿಕೊಳ್ಳುವಲ್ಲಿ ಎರಡನೆಯವಳು - ಅದು ನನಗೂ ಬೇಡ, ನಿನಗೂ ಬೇಡ, ಕಡಿಯಲಿ ಎಂದು ಕೂಗಿದಳು.",
			"27": "<sup>27</sup> ಕೂಡಲೆ ಅರಸನು - ಬದುಕಿರುವ ಕೂಸನ್ನು ಕೊಲ್ಲಬೇಡಿರಿ, ಆ ಸ್ತ್ರೀಗೆ ಕೊಡಿರಿ; ಅವಳೇ ಅದರ ತಾಯಿ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"28": "<sup>28</sup> ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರೂ ಅರಸನ ಈ ತೀರ್ಪನ್ನು ಕೇಳಿ ನ್ಯಾಯನಿರ್ಣಯಿಸುವದಕ್ಕೆ ಈತನಲ್ಲಿ ದೇವಜ್ಞಾನವಿದೆ ಎಂದು ತಿಳಿದು ಅವನಿಗೆ ಬಹಳವಾಗಿ ಭಯಪಟ್ಟರು."
		},
		"4": {
			"1": "<sup>1</sup> ಸೊಲೊಮೋನರಾಜನು ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರ ಮೇಲೆ ಅರಸನಾಗಿದ್ದನು.",
			"2": "<sup>2</sup> ಅವನ ಮುಖ್ಯ ಉದ್ಯೋಗಸ್ಥರು - ಚಾದೋಕನ ಮಗನಾದ ಅಜರ್ಯನು ಯಾಜಕನು;",
			"3": "<sup>3</sup> ಶೀಷನ ಮಕ್ಕಳಾದ ಎಲೀಹೋರೆಫ್, ಅಹೀಯಾಹು ಎಂಬವರು ಲೇಖಕರು; ಅಹೀಲೂದನ ಮಗನಾದ ಯೆಹೋಷಾಫಾಟನು ಅವನ ಮಂತ್ರಿಯು;",
			"4": "<sup>4</sup> ಯೆಹೋಯಾದಾವನ ಮಗನಾದ ಬೆನಾಯನು ಸೇನಾಪತಿ; ಎಬ್ಯಾತಾರ ಚಾದೋಕರು ಯಾಜಕರು;",
			"5": "<sup>5</sup> ನಾತಾನನ ಮಗನಾದ ಅಜರ್ಯನು ಪ್ರದೇಶಾಧಿಪತಿಗಳ ಮುಖ್ಯಸ್ಥನು; ನಾತಾನನ ಮಗನಾದ ಜಾಬೂದನು ಯಾಜಕನೂ ಅರಸನ ಮಿತ್ರನೂ ಆಗಿದ್ದನು;",
			"6": "<sup>6</sup> ಅಹೀಷಾರನು ರಾಜಗೃಹಾಧಿಪತಿಯು; ಅಬ್ದನ ಮಗನಾದ ಅದೋನೀರಾಮನು ಬಿಟ್ಟೀ ಕೆಲಸಮಾಡಿಸುವವರ ಮುಖ್ಯಸ್ಥನು.",
			"7": "<sup>7</sup> ಸೊಲೊಮೋನನು ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರ ಮೇಲೆ ಹನ್ನೆರಡು ಮಂದಿ ಪ್ರದೇಶಾಧಿಪತಿಗಳನ್ನು ನೇಮಿಸಿದನು. ಅವರಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬನು ಒಂದೊಂದು ತಿಂಗಳು ಅರಸನಿಗೂ ಅವನ ಮನೆಯವರಿಗೂ ಬೇಕಾದ ಆಹಾರಪದಾರ್ಥಗಳನ್ನು ಪ್ರತಿವರುಷವೂ ಒದಗಿಸಿ ಕೊಡಬೇಕಾಗಿತ್ತು.",
			"8": "<sup>8</sup> ಅವರು ಯಾರಂದರೆ - ಎಫ್ರಾಯೀಮ್ ಪರ್ವತಪ್ರದೇಶದಲ್ಲಿದ್ದ ಹೂರನ ಮಗನು.",
			"9": "<sup>9</sup> ಮಾಕಚ್, ಶಾಲ್ಬೀಮ್, ಬೇತ್‍ಷೆಮೆಷ್, ಏಲೋನ್, ಬೇತ್‍ಹಾನಾನ್ ಎಂಬ ಊರುಗಳ ಒಡೆಯನಾದ ದೆಕೆರನ ಮಗನು.",
			"10": "<sup>10</sup> ಸೋಕೋ, ಹೇಫೆರ್ ಎಂಬ ಪ್ರದೇಶಗಳಿಗೆ ಅಧಿಪತಿಯಾಗಿದ್ದು ಅರುಬ್ಬೋತಿನಲ್ಲಿ ವಾಸವಾಗಿದ್ದ ಹೆಸೆದನ ಮಗನು.",
			"11": "<sup>11</sup> ದೋರ್‍ಗುಡ್ಡದ ಪ್ರದೇಶದಲ್ಲಿದ್ದ ಅಬೀನಾದಾಬನ ಮಗನು; ಸೊಲೊಮೋನನ ಮಗಳಾದ ಟಾಫತಳು ಇವನ ಹೆಂಡತಿಯಾಗಿದ್ದಳು.",
			"12": "<sup>12</sup> ತಾಣಕ್, ಮೆಗಿದ್ದೋ, ಚಾರೆತಾನಿನ ಬಳಿಯಲ್ಲಿಯೂ ಇಜ್ರೇಲಿನ ಅಡಿಯಲ್ಲಿಯೂ ಬೇತ್‍ಷೆಯಾನಿನಿಂದ ಆಬೇಲ್ ಮೆಹೋಲದವರೆಗೂ ಇರುವಂಥ ಯೊಕ್ಮೆಯಾನಿನ ಆಚೆಗೆ ವಿಸ್ತರಿಸಿಕೊಂಡಿರುವಂಥ ಬೇತ್‍ಷೆಯಾನಿನ ಎಲ್ಲಾ ಪ್ರದೇಶ ಇವುಗಳಿಗೆ ಅಧಿಪತಿಯಾದ ಅಹೀಲೂದನ ಮಗನಾದ ಬಾಣಾ.",
			"13": "<sup>13</sup> ಗಿಲ್ಯಾದಿನಲ್ಲಿ ಮನಸ್ಸೆಯ ಮಗನಾದ ಯಾಯೀರನ ಗ್ರಾಮಗಳಿಗೂ ಬಾಷಾನಿನಲ್ಲಿ ಪೌಳಿಗೋಡೆಗಳಿಂದಲೂ ಕಬ್ಬಿಣದ ಅಗುಳಿಗಳಿಂದಲೂ ಭದ್ರಮಾಡಲ್ಪಟ್ಟ ಅರುವತ್ತು ಪಟ್ಟಣಗಳಿರುವ ಅರ್ಗೋಬ್ ಪ್ರದೇಶಕ್ಕೂ ಅಧಿಪತಿಯಾಗಿದ್ದು ರಾಮೋತ್ ಗಿಲ್ಯಾದಿನಲ್ಲಿ ವಾಸವಾಗಿದ್ದ ಗೆಬೆರನ ಮಗನು.",
			"14": "<sup>14</sup> ಮಹನಯಿಮಿನಲ್ಲಿದ್ದ ಇದ್ದೋವಿನ ಮಗನಾದ ಅಹೀನಾದಾಬನು.",
			"15": "<sup>15</sup> ನಫ್ತಾಲಿ ಪ್ರದೇಶದಲ್ಲಿದ್ದ ಅಹೀಮಾಚನು; ಇವನು ಸೊಲೊಮೋನನ ಮಗಳಾದ ಬಾಸೆಮತಳನ್ನು ಮದುವೆಮಾಡಿ ಕೊಂಡವನು.",
			"16": "<sup>16</sup> ಆಶೇರಿಗೂ ಆಲೋತಿಗೂ ಒಡೆಯನಾದ ಹೂಷೈಯ ಮಗನಾದ ಬಾಣನು.",
			"17": "<sup>17</sup> ಇಸ್ಸಾಕಾರ್ ಪ್ರಾಂತದಲ್ಲಿದ್ದ ಫಾರೂಹನ ಮಗನಾದ ಯೆಹೋಷಾಫಾಟನು.",
			"18": "<sup>18</sup> ಬೆನ್ಯಾಮೀನಿನಲ್ಲಿದ್ದ ಏಲನ ಮಗನಾದ ಶಿಮ್ಮೀ.",
			"19": "<sup>19</sup> ಅಮೋರಿಯರ ಅರಸನಾದ ಸೀಹೋನ್, ಬಾಷಾನಿನ ಅರಸನಾದ ಓಗ್ ಇವರ ರಾಜ್ಯವಾಗಿದ್ದ ಗಿಲ್ಯಾದ್ ಪ್ರದೇಶಕ್ಕೆಲ್ಲಾ ಒಬ್ಬನೇ ಅಧಿಪತಿಯಾಗಿದ್ದ ಉರೀಯ ಮಗನಾದ ಗೆಬೆರ್ ಇವರೇ.",
			"20": "<sup>20</sup> ಇಸ್ರಾಯೇಲ್ ಯೆಹೂದ್ಯರು ಸಮುದ್ರತೀರದ ಉಸುಬಿನಷ್ಟು ಅಸಂಖ್ಯರಾಗಿದ್ದರು; ಅವರು ಅನ್ನಪಾನಗಳಲ್ಲಿ ತೃಪ್ತರಾಗಿ ಸಂತೋಷದಿಂದಿದ್ದರು.",
			"21": "<sup>21</sup> ಸೊಲೊಮೋನನು ಯೂಫ್ರೇಟೀಸ್‍ನದಿ ಮೊದಲುಗೊಂಡು ಫಿಲಿಷ್ಟಿಯರ ಮತ್ತು ಐಗುಪ್ತ್ಯರ ದೇಶಗಳವರೆಗೂ ಇರುವ ಎಲ್ಲಾ ರಾಜ್ಯಗಳವರನ್ನು ಆಳುತ್ತಿದ್ದನು. ಅವರು ಅವನ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ಅವನಿಗೆ ಅಧೀನರಾಗಿ ಕಪ್ಪಕೊಡುತ್ತಿದ್ದರು.",
			"22": "<sup>22</sup> ಸೊಲೊಮೋನನ ಅರಮನೆಗೆ ದಿನಂಪ್ರತಿ ಬೇಕಾಗುವ ಆಹಾರಪದಾರ್ಥಗಳು - ಮೂವತ್ತು ಕೋರ್ ಗೋದಿಯ ಹಿಟ್ಟು, ಅರುವತ್ತು ಕೋರ್ ಜವೆ ಗೋದಿಯ ಹಿಟ್ಟು,",
			"23": "<sup>23</sup> ಹತ್ತು ಕಟ್ಟಿ ಮೇಯಿಸಿದ ಎತ್ತುಗಳು, ಇಪ್ಪತ್ತು ಬಿಟ್ಟು ಮೇಯಿಸಿದ ಎತ್ತುಗಳು, ನೂರು ಕುರಿಗಳು; ಇವುಗಳ ಹೊರತು ದುಪ್ಪಿ, ಜಿಂಕೆ, ಸಾರಗ, ಕೊಬ್ಬಿದ ಕೋಳಿಗಳು ಇವೇ.",
			"24": "<sup>24</sup> ಅವನು ಯೂಫ್ರೇಟೀಸ್ ನದಿಯ ಈಚೆಯಲ್ಲಿ ತಿಪ್ಸಹು ಮೊದಲುಗೊಂಡು ಗಾಜದವರೆಗಿರುವ ಈಚೆಯ ಎಲ್ಲಾ ಅರಸರಿಗೂ ದೊರೆಯಾಗಿದ್ದನು. ಅವನು ಸುತ್ತಣ ರಾಜರೊಡನೆ ಸಮಾಧಾನದಿಂದಿದ್ದನು.",
			"25": "<sup>25</sup> ಸೊಲೊಮೋನನ ಆಳಿಕೆಯಲ್ಲೆಲ್ಲಾ ದಾನ್‍ಪಟ್ಟಣ ಮೊದಲುಗೊಂಡು ಬೇರ್ಷೆಬದವರೆಗಿರುವ ಸಮಸ್ತ ಇಸ್ರಾಯೇಲ್ಯರೂ ಯೆಹೂದ್ಯರೂ ತಮ್ಮತಮ್ಮ ದ್ರಾಕ್ಷಾಲತೆ ಅಂಜೂರಗಿಡ ಇವುಗಳ ನೆರಳಿನಲ್ಲಿ ವಾಸಿಸುತ್ತಾ ಸುರಕ್ಷಿತರಾಗಿದ್ದರು.",
			"26": "<sup>26</sup> ಸೊಲೊಮೋನನ ಲಾಯಗಳಲ್ಲಿ ನಾಲ್ವತ್ತು ಸಾವಿರ ರಥಾಶ್ವಗಳಿಗೆ ಸ್ಥಳವಿತ್ತು; ಅವನಿಗೆ ಹನ್ನೆರಡು ಸಾವಿರ ಮಂದಿ ರಾಹುತರಿದ್ದರು.",
			"27": "<sup>27</sup> ಮೇಲೆ ಕಂಡ ಪ್ರದೇಶಾಧಿಪತಿಗಳು ತಮತಮಗೆ ನೇಮಕವಾದ ತಿಂಗಳಲ್ಲಿ ಅರಸನಾದ ಸೊಲೊಮೋನನಿಗೂ ಅವನ ಪಂಕ್ತಿಯಲ್ಲಿ ಊಟಕ್ಕೆ ಕೂತುಕೊಳ್ಳುವವರೆಲ್ಲರಿಗೂ ಬೇಕಾಗುವ ಆಹಾರಪದಾರ್ಥಗಳನ್ನು ಯಾವ ಕೊರತೆಯೂ ಇಲ್ಲದೆ ಒದಗಿಸಿಕೊಡುತ್ತಿದ್ದರು.",
			"28": "<sup>28</sup> ಇದಲ್ಲದೆ ಅವರು ರಥಾಶ್ವಗಳಿಗೋಸ್ಕರವೂ ಸವಾರಿಕುದುರೆಗಳಿಗೋಸ್ಕರವೂ ನೇಮಕವಾದಷ್ಟು ಜವೆಗೋದಿಯನ್ನೂ ಹುಲ್ಲನ್ನೂ ಕುದುರೆಗಳಿದ್ದ ಸ್ಥಳಕ್ಕೆ ತಂದೊಪ್ಪಿಸುತ್ತಿದ್ದರು.",
			"29": "<sup>29</sup> ದೇವರು ಸೊಲೊಮೋನನಿಗೆ ಸಮುದ್ರತೀರದ ಉಸುಬಿನಷ್ಟು ಅಪರಿಮಿತವಾದ ಜ್ಞಾನವಿವೇಕಗಳನ್ನೂ ಮನೋವಿಶಾಲತೆಯನ್ನೂ ಅನುಗ್ರಹಿಸಿದನು.",
			"30": "<sup>30</sup> ಅವನ ಜ್ಞಾನವು ಮೂಡಣದೇಶದವರೆಲ್ಲರ ಜ್ಞಾನಕ್ಕಿಂತಲೂ ಐಗುಪ್ತ್ಯರ ಜ್ಞಾನಕ್ಕಿಂತಲೂ ಮಿಗಿಲಾದದ್ದು.",
			"31": "<sup>31</sup> ಅವನು ಜೆರಹನ ಮಗನಾದ ಏತಾನ್, ಮಾಹೋಲನ ಮಕ್ಕಳಾದ ಹೇಮಾನ್, ಕಲ್ಕೋಲ್, ದರ್ದ ಮೊದಲಾದ ಎಲ್ಲರಿಗಿಂತಲೂ ಜ್ಞಾನಿಯಾಗಿದ್ದನು. ಸುತ್ತಣ ಎಲ್ಲಾ ಜನಾಂಗಗಳಲ್ಲಿ ಅವನ ಹೆಸರು ಪ್ರಸಿದ್ಧವಾಯಿತು.",
			"32": "<sup>32</sup> ಅವನ ಜ್ಞಾನೋಕ್ತಿಗಳು ಮೂರು ಸಾವಿರ; ಗೀತಗಳು ಸಾವಿರದ ಐದು.",
			"33": "<sup>33</sup> ಅವನು ಲೆಬನೋನಿನ ದೇವದಾರುವೃಕ್ಷ ಮೊದಲುಗೊಂಡು ಗೋಡೆಯಲ್ಲಿ ಬೆಳೆಯುವ ಹಿಸ್ಸೋಪ್ ಗಿಡದವರೆಗಿರುವ ಎಲ್ಲಾ ವನಸ್ಪತಿಗಳನ್ನೂ ಎಲ್ಲಾ ಪಶುಪಕ್ಷಿಜಲಜಂತು ಕ್ರಿಮಿಕೀಟಗಳನ್ನೂ ಕುರಿತು ಪ್ರಸ್ತಾಪಿಸಿದನು.",
			"34": "<sup>34</sup> ಎಲ್ಲಾ ಜನಾಂಗಗಳಲ್ಲಿಯೂ ಸೊಲೊಮೋನನ ಜ್ಞಾನವಿಶೇಷವನ್ನು ಕುರಿತು ಕೇಳಿದ ಭೂಪಾಲರಲ್ಲಿಯೂ ಅನೇಕರು ಅವನ ಜ್ಞಾನವಾಕ್ಯಗಳನ್ನು ಕೇಳುವದಕ್ಕೋಸ್ಕರ ಬಂದರು."
		},
		"5": {
			"1": "<sup>1</sup> ದಾವೀದನ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ಅವನ ಮಿತ್ರನಾಗಿದ್ದ ತೂರಿನ ಅರಸನಾದ ಹೀರಾಮನು ಸೊಲೊಮೋನನು ತಂದೆಗೆ ಬದಲಾಗಿ ಅಭಿಷೇಕಿಸಲ್ಪಟ್ಟನೆಂದು ಕೇಳಿದಾಗ ಅವನ ಬಳಿಗೆ ತನ್ನ ಸೇವಕರನ್ನು ಕಳುಹಿಸಿದನು.",
			"2": "<sup>2</sup> ಅನಂತರ ಸೊಲೊಮೋನನು ದೂತರ ಮುಖಾಂತರವಾಗಿ ಹೀರಾಮನಿಗೆ -",
			"3": "<sup>3</sup> ಯೆಹೋವನ ಅನುಗ್ರಹದಿಂದ ತಮ್ಮ ತಂದೆಯಾದ ದಾವೀದನಿಗೆ ಎಲ್ಲಾ ಶತ್ರುಗಳು ವಶವಾಗುವವರೆಗೆ ಅವನು ಸುತ್ತಲೂ ಯುದ್ಧಗಳನ್ನು ನಡಿಸಬೇಕಾಗಿದ್ದದರಿಂದ ತನ್ನ ದೇವರಾದ ಯೆಹೋವನ ಹೆಸರಿಗೆ ಆಲಯವನ್ನು ಕಟ್ಟಿಸಲಾರದೆ ಹೋದನೆಂದು ನೀನು ಬಲ್ಲೆ.",
			"4": "<sup>4</sup> ನನಗಾದರೋ ನನ್ನ ದೇವರಾದ ಯೆಹೋವನು ಎಲ್ಲಾ ಕಡೆಗಳಲ್ಲಿಯೂ ಸಮಾಧಾನವನ್ನು ಅನುಗ್ರಹಿಸಿದ್ದಾನೆ; ನನ್ನನ್ನು ವಿರೋಧಿಸುವವನು ಒಬ್ಬನೂ ಇಲ್ಲ; ಆಪತ್ತು ನನಗೆ ದೂರವಾಗಿದೆ.",
			"5": "<sup>5</sup> ಯೆಹೋವನು ನನ್ನ ತಂದೆಯಾದ ದಾವೀದನಿಗೆ - ನಾನು ನಿನಗೆ ಬದಲಾಗಿ ನಿನ್ನ ಸಿಂಹಾಸನದ ಮೇಲೆ ಕುಳ್ಳಿರಿಸುವ ನಿನ್ನ ಮಗನು ನನ್ನ ಹೆಸರಿಗೆ ಒಂದು ಆಲಯವನ್ನು ಕಟ್ಟಲಿ ಎಂದು ಹೇಳಿದ್ದರಿಂದ ನಾನು ನನ್ನ ದೇವರಾದ ಯೆಹೋವನ ಹೆಸರಿಗೆ ಒಂದು ಆಲಯವನ್ನು ಕಟ್ಟಬೇಕೆಂದಿರುತ್ತೇನೆ.",
			"6": "<sup>6</sup> ಆದದರಿಂದ ನೀನು ನನಗೋಸ್ಕರ ಲೆಬನೋನಿನ ದೇವದಾರು ವೃಕ್ಷಗಳನ್ನು ಕೊಯ್ಯಬೇಕೆಂದು ನಿನ್ನ ಆಳುಗಳಿಗೆ ಆಜ್ಞಾಪಿಸು; ಅವರ ಜೊತೆಯಲ್ಲಿ ನನ್ನ ಆಳುಗಳನ್ನೂ ಕಳುಹಿಸುವೆನು. ಮತ್ತು ನಿನ್ನ ಆಳುಗಳಿಗೆ ನೀನು ಹೇಳುವಷ್ಟು ಸಂಬಳವನ್ನು ಕೊಡುತ್ತೇನೆ. ಚೀದೋನ್ಯರಂತೆ ಮರ ಕೊಯ್ಯುವದರಲ್ಲಿ ಜಾಣರು ನಮ್ಮಲ್ಲಿರುವದಿಲ್ಲವೆಂದು ನಿನಗೆ ಗೊತ್ತುಂಟಲ್ಲಾ ಎಂದು ಹೇಳಿಸಿದನು.",
			"7": "<sup>7</sup> ಹೀರಾಮನು ಸೊಲೊಮೋನನ ಮಾತುಗಳನ್ನು ಕೇಳಿದಾಗ ಬಹಳವಾಗಿ ಸಂತೋಷಪಟ್ಟು - ಈ ಮಹಾಜನಾಂಗವನ್ನು ಆಳುವದಕ್ಕಾಗಿ ದಾವೀದನಿಗೆ ಒಬ್ಬ ಬುದ್ಧಿವಂತನಾದ ಮಗನನ್ನು ಅನುಗ್ರಹಿಸಿದ ಯೆಹೋವನಿಗೆ ಈಗ ಸ್ತೋತ್ರವಾಗಲಿ ಎಂದು ನುಡಿದನು.",
			"8": "<sup>8</sup> ಇದಲ್ಲದೆ ಅವನು ಸೊಲೊಮೋನನಿಗೆ - ನೀನು ಹೇಳಿಕಳುಹಿಸಿದ್ದನ್ನು ಕೇಳಿದೆನು; ದೇವದಾರು ವೃಕ್ಷಗಳನ್ನೂ ತುರಾಯಿಮರಗಳನ್ನೂ ಕುರಿತು ನಿನ್ನ ಅಪೇಕ್ಷೆಯನ್ನು ನೆರವೇರಿಸುವೆನು.",
			"9": "<sup>9</sup> ನನ್ನ ಸೇವಕರು ಅವುಗಳನ್ನು ಲೆಬನೋನಿನಿಂದ ಸಮುದ್ರಕ್ಕೆ ತೆಗೆದುಕೊಂಡು ಹೋಗುವರು. ನಾನು ಅವುಗಳನ್ನು ತೆಪ್ಪವನ್ನಾಗಿ ಕಟ್ಟಿಸಿ ಸಮುದ್ರಮಾರ್ಗವಾಗಿ ನೀನು ನೇಮಿಸುವ ಸ್ಥಳಕ್ಕೆ ಕಳುಹಿಸಿ ಅಲ್ಲಿ ಬಿಚ್ಚಿಸುವೆನು; ನೀನು ಅವುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಅವುಗಳಿಗೆ ಬದಲಾಗಿ ನನ್ನ ಮನೆಯವರಿಗೆ ಆಹಾರಪದಾರ್ಥಗಳನ್ನು ಕೊಡಬೇಕೆಂಬದೇ ನನ್ನ ಅಪೇಕ್ಷೆಯಾಗಿದೆ ಎಂದು ಹೇಳಿ ಕಳುಹಿಸಿದನು.",
			"10": "<sup>10</sup> ಹೀಗೆ ಹೀರಾಮನು ಸೊಲೊಮೋನನಿಗೆ ಬೇಕಾದಷ್ಟು ದೇವದಾರಿನ ಮರಗಳನ್ನೂ ತುರಾಯಿಮರಗಳನ್ನೂ ಕೊಡುತ್ತಿದ್ದನು.",
			"11": "<sup>11</sup> ಸೊಲೊಮೋನನಾದರೋ ಪ್ರತಿವರುಷ ಅವನ ಮನೆಯವರ ಆಹಾರಕ್ಕಾಗಿ ಇಪ್ಪತ್ತು ಸಾವಿರ ಕೋರ್ ಗೋದಿಯನ್ನೂ ಇಪ್ಪತ್ತು ಕೋರ್ ನಿರ್ಮಲವಾದ ಎಣ್ಣೆಯನ್ನೂ ಕೊಡುತ್ತಿದ್ದನು.",
			"12": "<sup>12</sup> ಯೆಹೋವನು ತಾನು ವಾಗ್ದಾನಮಾಡಿದಂತೆ ಸೊಲೊಮೋನನಿಗೆ ಜ್ಞಾನವನ್ನು ಅನುಗ್ರಹಿಸಿದನು. ಸೊಲೊಮೋನನಿಗೂ ಹೀರಾಮನಿಗೂ ಸಮಾಧಾನವಿತ್ತು; ಅವರು ಒಡಂಬಡಿಕೆ ಮಾಡಿಕೊಂಡರು.",
			"13": "<sup>13</sup> ಅರಸನಾದ ಸೊಲೊಮೋನನು ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಮೂವತ್ತು ಸಾವಿರ ಮಂದಿಯನ್ನು ಬಿಟ್ಟಿಹಿಡಿದು",
			"14": "<sup>14</sup> ಅವರಲ್ಲಿ ತಿಂಗಳೊಂದಕ್ಕೆ ಹತ್ತುಸಾವಿರ ಮಂದಿಯಂತೆ ಲೆಬನೋನಿಗೆ ಕಳುಹಿಸುತ್ತಿದ್ದನು. ಅವರು ಒಂದು ತಿಂಗಳು ಲೆಬನೋನಿನಲ್ಲಿದ್ದರೆ ಎರಡು ತಿಂಗಳು ಮನೆಯಲ್ಲಿರುತ್ತಿದ್ದರು. ಅದೋನೀರಾಮನು ಇವರ ಮುಖ್ಯಸ್ಥನಾಗಿದ್ದನು.",
			"15": "<sup>15</sup> ಇದಲ್ಲದೆ ಸೊಲೊಮೋನನಿಗೆ ಎಪ್ಪತ್ತು ಸಾವಿರ ಮಂದಿ ಹೊರೆಹೊರುವವರೂ ಎಂಭತ್ತು ಸಾವಿರ ಮಂದಿ ಕಲ್ಲುಗಣಿಯಲ್ಲಿ ಕೆಲಸ ಮಾಡುವವರೂ ಇದ್ದರು.",
			"16": "<sup>16</sup> ಈ ಕೆಲಸದವರ ಮೇಲ್ವಿಚಾರಣೆಗಾಗಿ ಅವನು ಮೂರು ಸಾವಿರದ ಮುನ್ನೂರು ಮಂದಿ ಮೇಸ್ತ್ರಿಗಳನ್ನು ನೇಮಿಸಿದನು.",
			"17": "<sup>17</sup> ಕೆತ್ತಿದ ಕಲ್ಲುಗಳಿಂದ ಹಾಕಬೇಕಾದ ದೇವಾಲಯದ ಅಸ್ತಿವಾರಕ್ಕಾಗಿ ಆಳುಗಳು ಸೊಲೊಮೋನನ ಅಪ್ಪಣೆಯಂತೆ ಶ್ರೇಷ್ಠವಾದ ದೊಡ್ಡಕಲ್ಲುಗಳನ್ನು ಕೊರೆಯುತ್ತಿದ್ದರು.",
			"18": "<sup>18</sup> ಸೊಲೊಮೋನನ ಮತ್ತು ಹೀರಾಮನ ಶಿಲ್ಪಿಗಳೂ ಗೆಬಾಲ್ಯರೂ ಅವುಗಳನ್ನು ಕೆತ್ತುತ್ತಿದ್ದರು. ದೇವಾಲಯ ಕಟ್ಟುವದಕ್ಕಾಗಿ ಬೇಕಾದ ಕಲ್ಲು ಮರಗಳನ್ನು ಸಿದ್ಧಪಡಿಸಿದವರು ಇವರೇ."
		},
		"6": {
			"1": "<sup>1</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಐಗುಪ್ತದೇಶವನ್ನು ಬಿಟ್ಟ ನಾನೂರ ಎಂಭತ್ತನೆಯ ವರುಷದ ಅಂದರೆ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾದ ಸೊಲೊಮೋನನ ಆಳಿಕೆಯ ನಾಲ್ಕನೆಯ ವರುಷದ ಎರಡನೆಯ ತಿಂಗಳಾದ ವೈಶಾಖಮಾಸದಲ್ಲಿ ಅರಸನಾದ ಸೊಲೊಮೋನನು ಯೆಹೋವನ ಆಲಯವನ್ನು ಕಟ್ಟುವದಕ್ಕೆ ಪ್ರಾರಂಭಿಸಿದನು.",
			"2": "<sup>2</sup> ಅವನು ಯೆಹೋವನಿಗೋಸ್ಕರ ಕಟ್ಟಿಸಿದ ದೇವಾಲಯದ ಉದ್ದವು ಅರುವತ್ತು ಮೊಳ; ಅಗಲವು ಇಪ್ಪತ್ತು ಮೊಳ; ಎತ್ತರವು ಮೂವತ್ತು ಮೊಳ.",
			"3": "<sup>3</sup> ದೇವಾಲಯದ ಪರಿಶುದ್ಧ ಸ್ಥಳದ ಮುಂದುಗಡೆಯಲ್ಲಿ ಒಂದು ಮಂಟಪವಿತ್ತು; ಅದರ ಉದ್ದವು ದೇವಾಲಯದ ಅಗಲಕ್ಕೆ ಸರಿಯಾಗಿ ಇಪ್ಪತ್ತು ಮೊಳ, ಅಗಲವು ಹತ್ತು ಮೊಳ.",
			"4": "<sup>4</sup> ಇದಲ್ಲದೆ ಅವನು ಆಲಯಕ್ಕೆ ತೆರೆಯಲಾರದ ಜಾಳಂಧರ ಕಿಟಕಿಗಳನ್ನಿಡಿಸಿದನು.",
			"5": "<sup>5</sup> ದೇವಾಲಯದ ಗೋಡೆಯ ಸುತ್ತಲೂ ಅಂದರೆ ಪರಿಶುದ್ಧಸ್ಥಳ, ಮಹಾಪರಿಶುದ್ಧಸ್ಥಳ ಇವುಗಳ ಗೋಡೆಯ ಸುತ್ತಲೂ ಇನ್ನೊಂದು ಗೋಡೆಯನ್ನು ಕಟ್ಟಿಸಿ ಇವುಗಳ ನಡುವೆ ಅಂತಸ್ತುಗಳುಳ್ಳ ಕೊಠಡಿಗಳನ್ನು ಮಾಡಿಸಿದನು.",
			"6": "<sup>6</sup> ಕೆಳಗಿನ ಕೊಠಡಿಗಳ ಅಗಲವು ಐದು ಮೊಳ; ಮೊದಲನೆಯ ಅಂತಸ್ತಿನ ಕೊಠಡಿಗಳ ಅಗಲವು ಆರು ಮೊಳ; ಎರಡನೆಯ ಅಂತಸ್ತಿನ ಕೊಠಡಿಗಳ ಅಗಲವು ಏಳು ಮೊಳ. ಅಂತಸ್ತಿನ ತೊಲೆಗಳನ್ನಿಡುವಾಗ ದೇವಾಲಯದ ಗೋಡೆಯಲ್ಲಿ ತೂತಾಗಬಾರದೆಂದು ಗೋಡೆಯ ಹೊರಮೈಯನ್ನು ಸೋಪಾನಾಕಾರವಾಗಿ ಮಾಡಿಸಿದನು.",
			"7": "<sup>7</sup> ದೇವಾಲಯವನ್ನು ಕಟ್ಟುತ್ತಿರುವಾಗ ಕಲ್ಲುಗಳನ್ನು ಕಲ್ಲುಗಣಿಯಲ್ಲಿಯೇ ಸಿದ್ಧಮಾಡಿಕೊಂಡು ಬಂದು ಕಟ್ಟಿದ್ದರಿಂದ ಕಟ್ಟುವ ದಿವಸಗಳಲ್ಲೆಲ್ಲಾ ಸುತ್ತಿಗೆ, ಉಳಿ ಮುಂತಾದ ಕಬ್ಬಿಣದ ಸಾಮಾನುಗಳ ಶಬ್ದವು ಕೇಳಿಸಲಿಲ್ಲ.",
			"8": "<sup>8</sup> ಕೆಳಗಿನ ಕೊಠಡಿಗಳಿಗೆ ಹೋಗುವ ಬಾಗಲು ಆಲಯದ ಬಲಪಾರ್ಶ್ವದಲ್ಲಿತ್ತು. ಅಲ್ಲಿಂದ ಮೊದಲನೆಯ ಅಂತಸ್ತಿಗೂ ಎರಡನೆಯ ಅಂತಸ್ತಿಗೂ ಹೋಗಬೇಕಾದರೆ ವಕ್ರಸೋಪಾನಗಳಿದ್ದವು.",
			"9": "<sup>9</sup> ಈ ಪ್ರಕಾರ ಅವನು ಮನೆಯನ್ನು ಕಟ್ಟಿಸಿ ದೇವದಾರಿನ ತೊಲೆಹಲಿಗೆಗಳಿಂದ ಮಾಳಿಗೆಯನ್ನು ಮಾಡಿಸಿ ಕೆಲಸ ತೀರಿಸಿದನು.",
			"10": "<sup>10</sup> ಅವನು ದೇವಾಲಯದ ಸುತ್ತಲೂ ಒಂದರ ಮೇಲೊಂದು ಐದೈದು ಮೊಳ ಎತ್ತರವಾದ ಕೊಠಡಿಗಳನ್ನು ಕಟ್ಟಿಸಿದನು. ಇವು ದೇವದಾರಿನ ತೊಲೆಗಳಿಂದ ದೇವಾಲಯಕ್ಕೆ ಸಂಬಂಧಿಸಲ್ಪಟ್ಟಿದ್ದವು.",
			"11": "<sup>11</sup> ಯೆಹೋವನು ಸೊಲೊಮೋನನಿಗೆ - ನೀನು ಈಗ ಒಂದು ಆಲಯವನ್ನು ಕಟ್ಟಿಸುತ್ತಿರುತ್ತೀಯಷ್ಟೆ. ನೀನು ನನ್ನ ನಿಯಮ ವಿಧಿಗಳನ್ನು ಅನುಸರಿಸಿ ನನ್ನ ಆಜ್ಞೆಗಳನ್ನು ಕೈಕೊಂಡು ನಡೆಯುವದಾದರೆ ನಾನು ನಿನ್ನನ್ನು ಕುರಿತು ನಿನ್ನ ತಂದೆಯಾದ ದಾವೀದನಿಗೆ ಮಾಡಿದ ವಾಗ್ದಾನವನ್ನು ನೆರವೇರಿಸುವೆನು.",
			"13": "<sup>13</sup> ನನ್ನ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಕೈಬಿಡದೆ ಅವರ ಮಧ್ಯದಲ್ಲೇ ವಾಸಿಸುವೆನು ಎಂದು ಹೇಳಿದನು.",
			"14": "<sup>14</sup> ಸೊಲೊಮೋನನು ದೇವಾಲಯವನ್ನು ಕಟ್ಟಿಸಿದ ಮೇಲೆ",
			"15": "<sup>15</sup> ಅದರ ಗೋಡೆಗಳ ಒಳಮೈಯನ್ನು ನೆಲದಿಂದ ಮಾಳಿಗೆಯವರೆಗೆ ದೇವದಾರಿನ ಹಲಿಗೆಗಳಿಂದ ಹೊದಿಸಿದನು. ನೆಲಕ್ಕೆ ತುರಾಯಿಮರದ ಹಲಿಗೆಗಳನ್ನು ಹಾಕಿಸಿದನು.",
			"16": "<sup>16</sup> ಇದಲ್ಲದೆ ಅವನು ದೇವಾಲಯದೊಳಗೆ ಹಿಂದಿನ ಇಪ್ಪತ್ತು ಮೊಳ ಸ್ಥಳವನ್ನು ಬಿಟ್ಟು ನೆಲದಿಂದ ತೊಲೆಗಳವರೆಗೆ ದೇವದಾರಿನ ಹಲಿಗೆಗಳಿಂದ ಒಂದು ಗೋಡೆಯನ್ನು ಮಾಡಿಸಿ ಅದರ ಹಿಂದಿನ ಭಾಗವನ್ನು ಗರ್ಭಗೃಹ ಅಥವಾ ಮಹಾಪರಿಶುದ್ಧ ಸ್ಥಳವೆಂದು ಪ್ರತ್ಯೇಕಿಸಿದನು.",
			"17": "<sup>17</sup> ದೇವಾಲಯದ ಪರಿಶುದ್ಧಸ್ಥಳವೆನಿಸಿಕೊಳ್ಳುವ ಮುಂದಿನ ಭಾಗವು ನಾಲ್ವತ್ತು ಮೊಳ ಉದ್ದವಿತ್ತು.",
			"18": "<sup>18</sup> ದೇವಾಲಯದೊಳಗೆ ಕಲ್ಲು ಕಾಣಿಸದಂತೆ ಗೋಡೆಯನ್ನೆಲ್ಲಾ ಬಳ್ಳಿಗಳೂ ಹೂವುಗಳೂ ಕೆತ್ತಿರುವ ದೇವದಾರಿನ ಹಲಿಗೆಗಳಿಂದ ಹೊದಿಸಿದನು.",
			"19": "<sup>19</sup> ಯೆಹೋವನ ಒಡಂಬಡಿಕೆಯ ಮಂಜೂಷವನ್ನು ಇಡುವದಕ್ಕೋಸ್ಕರ ದೇವಾಲಯದೊಳಗೆ ಒಂದು ಭಾಗವನ್ನು ಗರ್ಭಗೃಹವೆಂದು ಪ್ರತ್ಯೇಕಿಸಿದನು.",
			"20": "<sup>20</sup> ಆ ಗರ್ಭಗೃಹವು ಇಪ್ಪತ್ತು ಮೊಳ ಉದ್ದವೂ ಇಪ್ಪತ್ತು ಮೊಳ ಅಗಲವೂ ಇಪ್ಪತ್ತು ಮೊಳ ಎತ್ತರವೂ ಇತ್ತು. ಅದನ್ನು ಚೊಕ್ಕ ಬಂಗಾರದ ತಗಡಿನಿಂದಲೂ ವೇದಿಯನ್ನು ದೇವದಾರುಮರದಿಂದಲೂ ಹೊದಿಸಿದನು.",
			"21": "<sup>21</sup> ಸೊಲೊಮೋನನು ದೇವಾಲಯದ ಒಳಮೈಯನ್ನು ಚೊಕ್ಕ ಬಂಗಾರದ ತಗಡಿನಿಂದ ಹೊದಿಸಿದನು. ಬಂಗಾರದ ತಗಡಿನಿಂದ ಹೊದಿಸಲ್ಪಟ್ಟ ಮಹಾಪರಿಶುದ್ಧಸ್ಥಳದ ಎದುರಿನಲ್ಲಿ ಒಂದು ಗೋಡೆಯಿಂದ ಇನ್ನೊಂದು ಗೋಡೆಗೆ ಬಂಗಾರದ ಸರಪಣಿಗಳನ್ನು ಕಟ್ಟಿಸಿದನು.",
			"22": "<sup>22</sup> ದೇವಾಲಯದ ಎಲ್ಲಾ ಗೋಡೆಗಳನ್ನೂ ಮಹಾಪರಿಶುದ್ಧಸ್ಥಳಕ್ಕೆ ಸೇರಿದ ವೇದಿಯನ್ನೂ ಪೂರ್ಣವಾಗಿ ಬಂಗಾರದ ತಗಡಿನಿಂದ ಹೊದಿಸಿದನು.",
			"23": "<sup>23</sup> ಇದಲ್ಲದೆ ಅವನು ಎಣ್ಣೇಮರದಿಂದ ಹತ್ತು ಮೊಳ ಎತ್ತರವಾದ ಎರಡು ಕೆರೂಬಿಗಳನ್ನು ಮಾಡಿಸಿ ಅವುಗಳನ್ನು ಗರ್ಭಗೃಹದಲ್ಲಿಡಿಸಿದನು.",
			"24": "<sup>24</sup> ಪ್ರತಿಯೊಂದು ಕೆರೂಬಿಯ ರೆಕ್ಕೆಗಳು ಐದೈದು ಮೊಳ ಉದ್ದವಿದ್ದವು. ಎರಡು ರೆಕ್ಕೆಗಳ ಅಂತರವು ತುದಿಯಿಂದ ತುದಿಗೆ ಹತ್ತು ಮೊಳ.",
			"25": "<sup>25</sup> ಇನ್ನೊಂದು ಕೆರೂಬಿಯ ರೆಕ್ಕೆಗಳ ಅಂತರವೂ ಹತ್ತು ಮೊಳ. ಎರಡೂ ಕೆರೂಬಿಗಳ ಅಳತೆಯೂ ಆಕಾರವೂ ಒಂದೇ.",
			"26": "<sup>26</sup> ಅವೆರಡೂ ಹತ್ತತ್ತು ಮೊಳ ಎತ್ತರವಾಗಿದ್ದವು.",
			"27": "<sup>27</sup> ಅವನು ಅವುಗಳನ್ನು ದೇವಾಲಯದ ಗರ್ಭಗೃಹದಲ್ಲಿಡಿಸಿದನು. ಅವುಗಳ ರೆಕ್ಕೆಗಳು ಚಾಚಿದವುಗಳಾಗಿದ್ದದರಿಂದ ಮೊದಲನೆಯ ಕೆರೂಬಿಯ ಒಂದು ರೆಕ್ಕೆಯು ಈಚೆಯ ಗೋಡೆಗೂ ಎರಡನೆಯ ಕೆರೂಬಿಯ ಒಂದು ರೆಕ್ಕೆಯು ಆಚೆಯ ಗೋಡೆಗೂ ತಗುಲಿದ್ದವು; ಅವುಗಳ ಬೇರೆ ಎರಡು ರೆಕ್ಕೆಗಳು ಮನೆಯ ಮಧ್ಯದಲ್ಲಿ ಒಂದನ್ನೊಂದು ತಗುಲಿದ್ದವು.",
			"28": "<sup>28</sup> ಈ ಕೆರೂಬಿಗಳು ಬಂಗಾರದ ತಗಡುಗಳಿಂದ ಹೊದಿಸಲ್ಪಟ್ಟಿದ್ದವು.",
			"29": "<sup>29</sup> ಅವನು ದೇವಾಲಯದ ಎಲ್ಲಾ ಗೋಡೆಗಳಲ್ಲಿ ಹೊರಗೂ ಒಳಗೂ ಕೆರೂಬಿ, ಖರ್ಜೂರವೃಕ್ಷ, ಹೂವು ಇವುಗಳ ಚಿತ್ರಗಳನ್ನು ಕೆತ್ತಿಸಿದನು.",
			"30": "<sup>30</sup> ದೇವಾಲಯದ ಒಳಗಣ ಮತ್ತು ಹೊರಗಣ ನೆಲವನ್ನು ಬಂಗಾರದ ತಗಡುಗಳಿಂದ ಹೊದಿಸಿದನು.",
			"31": "<sup>31</sup> ಗರ್ಭಗೃಹದ ಬಾಗಲಿಗೆ ಎಣ್ಣೇಮರದ ಕದಗಳನ್ನು ಹಚ್ಚಿಸಿದನು.",
			"32": "<sup>32</sup> ಚೌಕಟ್ಟು ಪಂಚಕೋಣಗಳುಳ್ಳದಾಗಿತ್ತು. ಎಣ್ಣೇಮರದ ಆ ಎರಡು ಕದಗಳಲ್ಲಿಯೂ ಕೆರೂಬಿ, ಖರ್ಜೂರವೃಕ್ಷ, ಹೂವು ಇವುಗಳ ಚಿತ್ರಗಳನ್ನು ಕೆತ್ತಿಸಿ ಕದಗಳಿಗೆ ಬಂಗಾರದ ತಗಡನ್ನು ಹೊದಿಸಿ ಚಿತ್ರಗಳಿದ್ದಲ್ಲಿ ಆ ತಗಡನ್ನು ಬಡಿಸಿದನು.",
			"33": "<sup>33</sup> ಇದಲ್ಲದೆ ಅವನು ಪರಿಶುದ್ಧಸ್ಥಳದ ಬಾಗಲಿಗೆ ಎಣ್ಣೇಮರದಿಂದ ಚತುಷ್ಕೋಣದ ಚೌಕಟ್ಟನ್ನು ಮಾಡಿಸಿ",
			"34": "<sup>34</sup> ಅದಕ್ಕೆ ತುರಾಯಿ ಮರದ ಎರಡು ಕದಗಳನ್ನು ಹಚ್ಚಿಸಿದನು. ಪ್ರತಿಯೊಂದು ಕದವು ಎರಡು ಭಾಗವುಳ್ಳದಾಗಿ ಮಡಚುವದಕ್ಕೆ ಬರುತ್ತಿತ್ತು.",
			"35": "<sup>35</sup> ಈ ಕದಗಳಲ್ಲಿ ಕೆರೂಬಿ, ಖರ್ಜೂರವೃಕ್ಷ, ಹೂವು ಇವುಗಳ ಚಿತ್ರಗಳನ್ನು ಕೆತ್ತಿಸಿ ಅವುಗಳಿಗೆ ಬಂಗಾರದ ತಗಡನ್ನು ಹೊದಿಸಿ ಕೆತ್ತನೆಯಿದ್ದಲ್ಲಿ ಆ ತಗಡನ್ನು ಬಡಿಸಿದನು.",
			"36": "<sup>36</sup> ದೇವಾಲಯದ ಪ್ರಾಕಾರದಲ್ಲಿ ಮೂರು ಸಾಲು ಕಲ್ಲಿನ ಕಂಬಗಳನ್ನೂ ಒಂದು ಸಾಲು ದೇವದಾರುವಿನ ಕಂಬಗಳನ್ನೂ ಇರಿಸಿದನು.",
			"37": "<sup>37</sup> ಸೊಲೊಮೋನನ ಆಳಿಕೆಯ ನಾಲ್ಕನೆಯ ವರುಷದ ವೈಶಾಖ ಮಾಸದಲ್ಲಿ ಯೆಹೋವನ ಆಲಯದ ಅಸ್ತಿವಾರವನ್ನು ಹಾಕಿದರು.",
			"38": "<sup>38</sup> ಹನ್ನೊಂದನೆಯ ವರುಷದ ಎಂಟನೆಯ ತಿಂಗಳಾದ ಕಾರ್ತಿಕ ಮಾಸದಲ್ಲಿ ದೇವಾಲಯವೂ ಅದರ ಎಲ್ಲಾ ಕಟ್ಟಡಗಳೂ ನೇಮದ ಪ್ರಕಾರ ಸಿದ್ಧವಾದವು. ದೇವಾಲಯವನ್ನು ಕಟ್ಟುವದಕ್ಕೆ ಏಳು ವರುಷ ಹಿಡಿಯಿತು."
		},
		"7": {
			"1": "<sup>1</sup> ಸೊಲೊಮೋನನು ತನ್ನ ಅರಮನೆಯನ್ನು ಹದಿಮೂರು",
			"2": "<sup>2</sup> ವರುಷಗಳಲ್ಲಿ ಕಟ್ಟಿಸಿ ತೀರಿಸಿದನು. ಅವನು ಕಟ್ಟಿಸಿದ ಲೇಬನೋನಿನ ತೋಪು ಎನಿಸಿಕೊಳ್ಳುವ ಮಂದಿರವು ನೂರು ಮೊಳ ಉದ್ದ, ಐವತ್ತು ಮೊಳ ಅಗಲ, ಮೂವತ್ತು ಮೊಳ ಎತ್ತರವಾಗಿತ್ತು. ಅದರ ಮಾಳಿಗೆಗೆ ದೇವದಾರಿನ ಮೂರು ಸಾಲು ಕಂಬಗಳೂ ಅವುಗಳ ಮೇಲೆ ಇಡಲ್ಪಟ್ಟ ದೇವದಾರಿನ ತೊಲೆಗಳೂ ಆಧಾರವಾಗಿದ್ದವು.",
			"3": "<sup>3</sup> ಸ್ತಂಭಗಳ ಮೇಲಿರುವ ತೊಲೆಗಳ ಮೇಲೆ ದೇವದಾರಿನ ಹಲಿಗೆಗಳಿಂದ ಮಾಡಿದ ಮಾಳಿಗೆಯಿತ್ತು. ಸ್ತಂಭಗಳು ಪ್ರತಿಯೊಂದು ಸಾಲಿನಲ್ಲಿ ಹದಿನೈದರಂತೆ ಒಟ್ಟು ನಾಲ್ವತ್ತೈದು ಇದ್ದವು.",
			"4": "<sup>4</sup> ಬೆಳಕು ಎದುರೆದುರಾಗಿ ಬೀಳುವಂತೆ ಎರಡು ಗೋಡೆಗಳಿಗೆ ಮೂರು ಮೂರು ಸಾಲು ಬೆಳಕು ಕಂಡಿಗಳಿದ್ದವು.",
			"5": "<sup>5</sup> ಎಲ್ಲಾ ದ್ವಾರಗಳೂ ಬೆಳಕು ಕಂಡಿಗಳೂ ಚತುಷ್ಕೋಣಾಕಾರವುಳ್ಳವುಗಳು. ಮೂರು ಮೂರು ಸಾಲು ಬೆಳಕುಕಂಡಿಗಳು ಎದುರೆದುರಾಗಿದ್ದವು.",
			"6": "<sup>6</sup> ಇದಲ್ಲದೆ ಸೊಲೊಮೋನನು ಒಂದು ಕಂಬ ಮಂಟಪವನ್ನು ಕಟ್ಟಿಸಿದನು. ಅದರ ಉದ್ದ ಐವತ್ತು ಮೊಳ; ಅಗಲ ಮೂವತ್ತು ಮೊಳ. ಅದರ ಮುಂದುಗಡೆಯಲ್ಲಿ ಒಂದು ಪಡಸಾಲೆಯಿತ್ತು; ಆ ಪಡಸಾಲೆಗೆ ಕಂಬಗಳೂ ಮೆಟ್ಲುಗಳೂ ಇದ್ದವು.",
			"7": "<sup>7</sup> ಇದಲ್ಲದೆ ಅವನು ಸಿಂಹಾಸನ ಮಂದಿರವನ್ನು ಕಟ್ಟಿಸಿದನು. ಅವನು ಅಲ್ಲಿ ನ್ಯಾಯತೀರಿಸುತ್ತಿದ್ದದರಿಂದ ಅದಕ್ಕೆ ನ್ಯಾಯಮಂದಿರವೆಂತಲೂ ಹೆಸರಾಯಿತು. ಇದರ ಎಲ್ಲಾ ಗೋಡೆಗಳು ನೆಲದಿಂದ ಮಾಳಿಗೆಯವರೆಗೆ ದೇವದಾರುವಿನ ಹಲಿಗೆಗಳಿಂದ ಹೊದಿಸಲ್ಪಟ್ಟಿದ್ದವು.",
			"8": "<sup>8</sup> ನ್ಯಾಯಮಂದಿರದ ಹಿಂದಿನ ಪ್ರಾಕಾರಗಳಲ್ಲಿ ತನಗೋಸ್ಕರ ಅರಮನೆಯನ್ನೂ ತನ್ನ ಹೆಂಡತಿಯಾದ ಫರೋಹನ ಮಗಳಿಗೋಸ್ಕರ ಇನ್ನೊಂದು ಮನೆಯನ್ನೂ ಕಟ್ಟಿಸಿದನು. ನ್ಯಾಯಮಂದಿರದ ಹಾಗೆಯೇ ಇವುಗಳನ್ನು ಕಟ್ಟಿಸಿದನು.",
			"9": "<sup>9</sup> ಈ ಎಲ್ಲಾ ಮಂದಿರಗಳನ್ನೂ ಅವುಗಳ ಸುತ್ತುಗೋಡೆಯನ್ನೂ ದೊಡ್ಡ ಪ್ರಾಕಾರದ ಸುತ್ತು ಗೋಡೆಯನ್ನೂ ಅಸ್ತಿವಾರದ ಮೇಲಣಿಂದ ಕಡೇ ವರಸೆಯವರೆಗೆ ಅಳತೆಯ ಮೇರೆಗೆ ಕೆತ್ತಲ್ಪಟ್ಟಂಥ ಎರಡು ಮಗ್ಗಲಿಗೂ ಗರಗಸದಿಂದ ಕೊಯ್ಯಲ್ಪಟ್ಟಂಥ ಶ್ರೇಷ್ಠವಾದ ಕಲ್ಲುಗಳಿಂದ ಕಟ್ಟಿದರು.",
			"10": "<sup>10</sup> ಅಸ್ತಿವಾರಕ್ಕೆ ಪ್ರಯೋಗಿಸಿದ ಶ್ರೇಷ್ಠವಾದ ದೊಡ್ಡ ಕಲ್ಲುಗಳು ಎಂಟು ಹತ್ತು ಮೊಳ ಉದ್ದವಾಗಿದ್ದವು.",
			"11": "<sup>11</sup> ಅಸ್ತಿವಾರದ ಮೇಲಣ ಭಾಗಕ್ಕೆ ಅಳತೆಯ ಮೇರೆಗೆ ಕೆತ್ತಿದ ಶ್ರೇಷ್ಠವಾದ ಕಲ್ಲುಗಳನ್ನೂ ದೇವದಾರಿನ ಮರವನ್ನೂ ಉಪಯೋಗಿಸಿದರು.",
			"12": "<sup>12</sup> ಒಳಗಣ ಪ್ರಾಕಾರವೆನಿಸಿಕೊಳ್ಳುವ ಯೆಹೋವನ ಆಲಯದ ಪ್ರಾಕಾರಕ್ಕೂ ಅರಮನೆಯ ಪ್ರಾಕಾರಕ್ಕೂ ಹೇಗೋ ಹಾಗೆಯೇ ಎಲ್ಲಾ ಮಂದಿರಗಳ ಸುತ್ತಲಿರುವ ದೊಡ್ಡ ಪ್ರಾಕಾರಕ್ಕೆ ಮೂರು ಸಾಲು ಕಲ್ಲಿನ ಕಂಬಗಳೂ ಒಂದು ಸಾಲು ದೇವದಾರುವಿನ ಮರದ ಕಂಬಗಳೂ ಇದ್ದವು.",
			"13": "<sup>13</sup> ಅರಸನಾದ ಸೊಲೊಮೋನನು ತೂರ್ ಪಟ್ಟಣದಿಂದ ಹೀರಾಮ್ ಎಂಬವನನ್ನು ಕರಿಸಿದನು.",
			"14": "<sup>14</sup> ಇವನು ನಫ್ತಾಲಿಕುಲದ ಒಬ್ಬ ವಿಧವೆಯಲ್ಲಿ ತೂರ್‍ಪಟ್ಟಣದ ಒಬ್ಬ ಕಂಚುಗಾರನಿಗೆ ಹುಟ್ಟಿದವನು. ಜಾಣನೂ ತಾಮ್ರದ ಕೆಲಸದಲ್ಲಿ ನಿಪುಣನೂ ಅನುಭವಶಾಲಿಯೂ ಆಗಿದ್ದ ಇವನು ಅರಸನ ಬಳಿಗೆ ಬಂದು ಅವನ ಎಲ್ಲಾ ಕೆಲಸವನ್ನು ಮಾಡಿದನು.",
			"15": "<sup>15</sup> ಅವನು ಎರಡು ತಾಮ್ರದ ಕಂಬಗಳನ್ನು ಮಾಡಿದನು. ಅವು ಹದಿನೆಂಟು ಮೊಳ ಎತ್ತರವಿದ್ದವು.",
			"16": "<sup>16</sup> ಅವುಗಳ ಸುತ್ತಳತೆಯು ಹನ್ನೆರಡು ಮೊಳ. ಇದಲ್ಲದೆ ಅವನು ತಾಮ್ರವನ್ನು ಎರಕಹೊಯ್ದು ಕಂಬಗಳ ಮೇಲೆ ಇಡುವದಕ್ಕೋಸ್ಕರ ಐದೈದು ಮೊಳ ಎತ್ತರವಾದ ಎರಡು ಕುಂಭಗಳನ್ನೂ",
			"17": "<sup>17</sup> ಒಂದೊಂದಕ್ಕೆ ಏಳೇಳರಂತೆ ಕುಂಭದ ಮೇಲೆ ಹಾಕುವದಕ್ಕೋಸ್ಕರ ತಾಮ್ರದ ಸರಿಗೆಯ ಜಾಲರಿಗಳನ್ನೂ ಮಾಡಿದನು;",
			"18": "<sup>18</sup> ಇದಲ್ಲದೆ ಅವನು ತಾಮ್ರದ ದಾಳಿಂಬಹಣ್ಣುಗಳನ್ನು ಮಾಡಿ ಅವುಗಳನ್ನು ಕಂಬಗಳ ಮೇಲಣ ಪ್ರತಿಯೊಂದು ಕುಂಭದ ಜಾಲರಿಯ ಸುತ್ತಲೂ ಎರಡೆರಡು ಸಾಲಾಗಿ ಸಿಕ್ಕಿಸಿದನು.",
			"19": "<sup>19</sup> ಕಂಬಗಳ ಮೇಲಿರುವ ಕುಂಭಗಳ ಒಡಲು ನಾಲ್ಕು ಮೊಳಗಳವರೆಗೆ ಕಮಲಾಕಾರವುಳ್ಳದ್ದಾಗಿತ್ತು.",
			"20": "<sup>20</sup> ಆ ಕುಂಭಗಳ ಮೇಲಿನ ಭಾಗದಲ್ಲಿ ಒಡಲಿನ ಸುತ್ತಲೂ ಜಾಲರಿಯ ಹೊರಗೆ ದಾಳಿಂಬಹಣ್ಣಿನ ಆಕಾರವುಳ್ಳ ಗುಂಡುಗಳಿದ್ದವು. ಪ್ರತಿಯೊಂದು ಕುಂಭದ ಸುತ್ತಲೂ ಇನ್ನೂರು ಗುಂಡುಗಳನ್ನು ಸಾಲಾಗಿ ಸಿಕ್ಕಿಸಿದನು.",
			"21": "<sup>21</sup> ತರುವಾಯ ಅವನು ಆ ಎರಡು ಕಂಬಗಳನ್ನೂ ದೇವಾಲಯದ ಮಂಟಪದ ಬಳಿಯಲ್ಲಿ ನಿಲ್ಲಿಸಿ ಬಲಗಡೆಯ ಕಂಬಕ್ಕೆ ಯಾಕೀನ್ ಎಂದೂ ಎಡಗಡೆಯ ಕಂಬಕ್ಕೆ ಬೋವಜ್ ಎಂದೂ ಹೆಸರಿಟ್ಟನು.",
			"22": "<sup>22</sup> ಅವನು ಕಂಬಗಳ ಮೇಲೆ ಕಮಲಾಲಂಕಾರವನ್ನು ಮಾಡಿ ಅವುಗಳ ಕೆಲಸವನ್ನು ತೀರಿಸಿದನು.",
			"23": "<sup>23</sup> ಅನಂತರ ಅವನು ಸಮುದ್ರವೆನಿಸಿಕೊಳ್ಳುವ ಒಂದು ಎರಕದ ಪಾತ್ರೆಯನ್ನು ಮಾಡಿದನು. ಅದರ ಬಾಯಿ ಚಕ್ರಾಕಾರವಾಗಿಯೂ ಅಂಚಿನಿಂದ ಅಂಚಿಗೆ ಹತ್ತು ಮೊಳವಾಗಿಯೂ ಇತ್ತು. ಅದರ ಎತ್ತರ ಐದು ಮೊಳ, ಸುತ್ತಳತೆ ಮೂವತ್ತು ಮೊಳ.",
			"24": "<sup>24</sup> ಅದನ್ನು ಎರಕಹೊಯ್ಯುವಾಗ ಅದರ ಅಂಚಿನ ಕೆಳಗೆ ಸುತ್ತಲೂ ಹತ್ತತ್ತು ಮೊಳ ಉದ್ದವಾದ ಬಳ್ಳಿಗಳನ್ನು ಎರಡು ಸಾಲಾಗಿ ಎರಕಹೊಯ್ದಿದ್ದನು.",
			"25": "<sup>25</sup> ಹನ್ನೆರಡು ಎರಕದ ಹೋರಿಗಳು ಅದನ್ನು ಹೊತ್ತಿದ್ದವು. ಅವುಗಳಲ್ಲಿ ಮೂರು ಉತ್ತರದಿಕ್ಕಿಗೂ ಮೂರು ಪಶ್ಚಿಮದಿಕ್ಕಿಗೂ ಮೂರು ದಕ್ಷಿಣದಿಕ್ಕಿಗೂ ಮೂರು ಪೂರ್ವದಿಕ್ಕಿಗೂ ಮುಖಮಾಡಿಕೊಂಡಿದ್ದವು. ಸಮುದ್ರವು ಅವುಗಳ ಬೆನ್ನಿನ ಮೇಲೆ ಇತ್ತು; ಅವುಗಳ ಹಿಂಭಾಗವು ಒಳಗಡೆ ಇತ್ತು.",
			"26": "<sup>26</sup> ಪಾತ್ರೆಯು ನಾಲ್ಕು ಬೆರಳು ದಪ್ಪವಾಗಿತ್ತು. ಅದರ ಅಂಚು ಕಮಲಾಕಾರದ ಬಟ್ಟಲನ್ನು ಹೋಲುತ್ತಿತ್ತು. ಅದು ಎರಡು ಸಾವಿರ ಬತ್ ನೀರನ್ನು ಹಿಡಿಯುವದು.",
			"27": "<sup>27</sup> ಇದಲ್ಲದೆ ಅವನು ಹತ್ತು ತಾಮ್ರದ ಪೀಠಗಳನ್ನು ಮಾಡಿದನು. ಪ್ರತಿಯೊಂದು ನಾಲ್ಕು ಮೊಳ ಉದ್ದ, ನಾಲ್ಕು ಮೊಳ ಅಗಲ, ಮೂರು ಮೊಳ ಎತ್ತರ ಇತ್ತು.",
			"28": "<sup>28</sup> ಅವುಗಳನ್ನು ಮಾಡಿದ ವಿಧಾನವು ಹೇಗಂದರೆ - ಅವುಗಳಲ್ಲಿ ನಿಲುವುಪಟ್ಟಿಗಳಿಂದ ಕೂಡಿದ ಅಡ್ಡಪಟ್ಟಿಗಳಿದ್ದವು.",
			"29": "<sup>29</sup> ಆ ಅಡ್ಡಪಟ್ಟಿಗಳ ಮೇಲೆ ಸಿಂಹ, ಹೋರಿ, ಕೆರೂಬಿ ಇವುಗಳ ಚಿತ್ರಗಳಿದ್ದವು; ನಿಲುವುಪಟ್ಟಿಗಳಿಗೂ ಇದೇ ಅಲಂಕಾರವಿತ್ತು; ಸಿಂಹ, ಹೋರಿ ಇವುಗಳ ಮೇಲೂ ಕೆಳಗೂ ತೋರಣಚಿತ್ರಗಳಿದ್ದವು.",
			"30": "<sup>30</sup> ಪ್ರತಿಯೊಂದು ಪೀಠಕ್ಕೆ ನಾಲ್ಕು ತಾಮ್ರದ ಗಾಲಿಗಳೂ ಅಚ್ಚುಗಳೂ ಇದ್ದವು. ಪ್ರತಿಯೊಂದು ನಿಲುವುಪಟ್ಟಿಯ ಮೇಲಣ ತುದಿಯಲ್ಲಿ ಎರಕಹೊಯ್ದ ತಾಮ್ರದ ಹಿಡಿಗಳಿದ್ದವು; ಅವು ಪೀಠದ ಮೇಲಣ ಗಂಗಾಳವನ್ನು ಹಿಡಿಯುತ್ತಿದ್ದವು. ಅವುಗಳ ಹೊರಮೈಗೆ ತೋರಣಚಿತ್ರಗಳಿದ್ದವು.",
			"31": "<sup>31</sup> ಗಂಗಾಳವನ್ನು ಇಡುವದಕ್ಕೋಸ್ಕರ ಪೀಠದ ಮೇಲಿನ ಭಾಗದಲ್ಲಿ ಒಂದು ಅಥವಾ ಒಂದುವರೆ ಮೊಳ ಎತ್ತರವಾದ ಬಾಯಿತ್ತು. ಚಕ್ರಾಕಾರವಾಗಿರುವ ಈ ಬಾಯಿಯು ಒಂದುವರೆ ಮೊಳ ಅಗಲವಾಗಿತ್ತು. ಇದರ ಮೇಲೆಯೂ ಚಿತ್ರಗಳಿದ್ದವು. ಇದಕ್ಕೆ ಆಧಾರವಾದ ಅಡ್ಡಪಟ್ಟಿಗಳು ಚಕ್ರಾಕಾರವಾಗಿರದೆ ಚತುಷ್ಕೋಣದವುಗಳಾಗಿದ್ದವು.",
			"32": "<sup>32</sup> ಮೇಲೆ ಕಂಡ ನಾಲ್ಕು ಗಾಲಿಗಳು ಅಡ್ಡಪಟ್ಟಿಗಳ ಕೆಳಗಿದ್ದವು. ಇರಸೂ ಪೀಠವೂ ಅಖಂಡವಾಗಿದ್ದವು; ಪ್ರತಿಯೊಂದು ಗಾಲಿಯು ಒಂದುವರೆ ಮೊಳ ಎತ್ತರವಿತ್ತು.",
			"33": "<sup>33</sup> ಅವು ಸಾಧಾರಣವಾದ ಬಂಡಿಗಳ ಗಾಲಿಗಳಂತೆಯೇ ಇದ್ದವು. ಆದರೆ ಅವುಗಳ ಕಟ್ಟು, ಅರ, ಕುಂಭ ಇವುಗಳೂ ಅವುಗಳಿಗೆ ಸಂಬಂಧಪಟ್ಟ ಇರಸೂ ತಾಮ್ರದವುಗಳು.",
			"34": "<sup>34</sup> ಪ್ರತಿಯೊಂದು ಪೀಠದ ತುದಿಯಲ್ಲಿದ್ದ ನಾಲ್ಕು ಹಿಡಿಗಳು ಪೀಠದೊಡನೆ ಅಖಂಡವಾಗಿದ್ದವು.",
			"35": "<sup>35</sup> ಪೀಠದ ಮೇಲಣ ಭಾಗದಲ್ಲಿದ್ದ ಚಕ್ರಾಕಾರವಾದ ಬಾಯಿಯು ಅರ್ಧ ಮೊಳ ಎತ್ತರವಾಗಿತ್ತು. ಪೀಠದ ಮೇಲಿನ ಭಾಗದಲ್ಲಿದ್ದ ಹಿಡಿಗಳೂ ಅಡ್ಡಪಟ್ಟಿಗಳೂ ಅದರೊಡನೆ ಅಖಂಡವಾಗಿದ್ದವು;",
			"36": "<sup>36</sup> ಅಡ್ಡಪಟ್ಟಿಗಳಿಗೂ ಹಿಡಿಗಳ ಹೊರ ಮೈಗೂ ಸ್ಥಳವಿದ್ದ ಪ್ರಕಾರ ಕೆರೂಬಿ, ಸಿಂಹ, ಖರ್ಜೂರವೃಕ್ಷ ಇವುಗಳ ಚಿತ್ರಗಳನ್ನೂ ಈ ಚಿತ್ರಗಳ ಸುತ್ತಲೂ ತೋರಣಚಿತ್ರಗಳನ್ನೂ ಮಾಡಿದನು.",
			"37": "<sup>37</sup> ಲೋಹಗಳನ್ನು ಒಂದೇ ಸಾರಿ ಕರಗಿಸಿ ಆ ಹತ್ತು ಪೀಠಗಳನ್ನು ಮಾಡಿದನು. ಅವುಗಳ ಆಕಾರವೂ ಅಳತೆಯೂ ಒಂದೇ.",
			"38": "<sup>38</sup> ಇದಲ್ಲದೆ ಅವನು ನಾಲ್ವತ್ತು ಬತ್ ನೀರು ಹಿಡಿಯುವ ನಾಲ್ಕು ಮೊಳ ಅಗಲವಾದ ಹತ್ತು ಗಂಗಾಳಗಳನ್ನು ಮಾಡಿ ಪ್ರತಿಯೊಂದು ಪೀಠದ ಮೇಲೆ ಒಂದೊಂದನ್ನು ಇಟ್ಟನು.",
			"39": "<sup>39</sup> ಐದು ಪೀಠಗಳನ್ನು ದೇವಾಲಯದ ಬಲಗಡೆಯಲ್ಲಿಯೂ ಐದು ಪೀಠಗಳನ್ನು ಎಡಗಡೆಯಲ್ಲಿಯೂ ಇಟ್ಟನು. ಸಮುದ್ರವೆನಿಸಿಕೊಳ್ಳುವ ಪಾತ್ರೆಯನ್ನು ದೇವಾಲಯದ ಬಲಗಡೆಯಲ್ಲಿ ಅಂದರೆ ಪ್ರಾಕಾರದ ಆಗ್ನೇಯ ದಿಕ್ಕಿನಲ್ಲಿರಿಸಿದನು.",
			"40": "<sup>40</sup> ಇದಲ್ಲದೆ ಹೀರಾಮನು ಬಟ್ಟಲುಗಳನ್ನೂ ಸಲಿಕೆಗಳನ್ನೂ ಬೋಗುಣಿಗಳನ್ನೂ ಮಾಡಿದನು. ಅವನು ಸೊಲೊಮೋನನ ಅಪ್ಪಣೆಯಂತೆ ದೇವಾಲಯಕ್ಕೋಸ್ಕರ ಮಾಡಿದ ಒಟ್ಟು ಸಾಮಾನುಗಳು ಯಾವವಂದರೆ -",
			"41": "<sup>41</sup> ಎರಡು ಕಂಬಗಳು, ಅವುಗಳ ಮೇಲಿನ ಎರಡು ಕುಂಭಗಳು, ಕುಂಭಗಳ ಮೇಲೆ ಹಾಕುವದಕ್ಕೋಸ್ಕರ ಎರಡು ಜಾಲರಿಗಳು,",
			"42": "<sup>42</sup> ಜಾಲರಿಗಳ ಮೇಲೆ ಎರಡೆರಡು ಸಾಲಾಗಿ ಸಿಕ್ಕಿಸುವದಕ್ಕೋಸ್ಕರ ತಾಮ್ರದ ನಾನೂರು ದಾಳಿಂಬಹಣ್ಣುಗಳು,",
			"43": "<sup>43</sup> ಹತ್ತು ಪೀಠಗಳು, ಅವುಗಳ ಮೇಲಿದ್ದ ಹತ್ತು ಗಂಗಾಳಗಳು,",
			"44": "<sup>44</sup> ಸಮುದ್ರವೆನಿಸಿಕೊಳ್ಳುವ ಪಾತ್ರೆಯು, ಅದನ್ನು ಹೊರುವ ಹನ್ನೆರಡು ಎರಕದ ಹೋರಿಗಳು, ಹಂಡೆ ಸಲಿಕೆ ಬೋಗುಣಿಗಳು ಇವೇ.",
			"45": "<sup>45</sup> ಹೀರಾಮನು ಅರಸನಾದ ಸೊಲೊಮೋನನ ಅಪ್ಪಣೆಯಂತೆ ಯೆಹೋವನ ಆಲಯಕ್ಕೋಸ್ಕರ ಈ ಎಲ್ಲಾ ಸಾಮಾನುಗಳನ್ನು ಒಪ್ಪ ಹಾಕಿದ ತಾಮ್ರದಿಂದ ಮಾಡಿದನು.",
			"46": "<sup>46</sup> ಅರಸನು ಯೊರ್ದನಿನ ತಗ್ಗಿನಲ್ಲಿ ಸುಕ್ಕೋತಿಗೂ ಚಾರೆತಾನಿಗೂ ಮಧ್ಯದಲ್ಲಿರುವ ಮಣ್ಣುನೆಲದಲ್ಲಿ ಎರಕಹೊಯ್ಸಿದನು.",
			"47": "<sup>47</sup> ಸೊಲೊಮೋನನು ತಾನು ಮಾಡಿಸಿದ ಸಾಮಾನುಗಳು ಅನೇಕವಾಗಿದ್ದದರಿಂದ ಅವುಗಳ ತಾಮ್ರದ ತೂಕವು ಎಷ್ಟೆಂಬದನ್ನು ಗೊತ್ತು ಮಾಡಲಾರದವನಾದನು.",
			"48": "<sup>48</sup> ಸೊಲೊಮೋನನು ಯೆಹೋವನ ಆಲಯಕ್ಕೋಸ್ಕರ ಮಾಡಿಸಿದ ಒಳಗಿನ ಸಾಮಾನುಗಳು ಯಾವವಂದರೆ -",
			"49": "<sup>49</sup> ಬಂಗಾರದ ಧೂಪವೇದಿಯು, ನೈವೇದ್ಯವಾದ ರೊಟ್ಟಿಗಳನ್ನಿಡುವದಕ್ಕಾಗಿ ಬಂಗಾರದ ಮೇಜು, ಮಹಾಪರಿಶುದ್ಧಸ್ಥಳದ ಎದುರಿಗೆ ಎಡಗಡೆಯಲ್ಲಿಯೂ ಬಲಗಡೆಯಲ್ಲಿಯೂ ಐದೈದರಂತೆ ಇಡುವದಕ್ಕೋಸ್ಕರ ಚೊಕ್ಕ ಬಂಗಾರದಿಂದ ಮಾಡಿದ ಪುಷ್ಪಾಲಂಕಾರವುಳ್ಳ ಹತ್ತು ದೀಪಸ್ತಂಭಗಳು, ಬಂಗಾರದ ಹಣತೆತಂಡಸುಗಳು,",
			"50": "<sup>50</sup> ಚೊಕ್ಕ ಬಂಗಾರದ ಬಟ್ಟಲುಗಳು, ಕತ್ತರಿಗಳು, ಬೋಗುಣಿಗಳು, ಧೂಪಾರತಿಗಳು, ಅಗ್ಗಿಷ್ಟಿಗೆಗಳು, ದೇವಾಲಯದ ಮಹಾಪರಿಶುದ್ಧ ಸ್ಥಳವೆನಿಸಿಕೊಳ್ಳುವ ಗರ್ಭಗೃಹದ ಕದಗಳಿಗೂ ಪರಿಶುದ್ಧಸ್ಥಳದ ಕದಗಳಿಗೂ ಇರುವ ಬಂಗಾರದ ತಿರುಗಣಿಗಳು ಇವೇ.",
			"51": "<sup>51</sup> ಅರಸನಾದ ಸೊಲೊಮೋನನು ಯೆಹೋವನ ಆಲಯಕ್ಕೋಸ್ಕರ ಬೇಕಾದ ಎಲ್ಲಾ ಸಾಮಾನುಗಳನ್ನು ಸಿದ್ಧಮಾಡಿಸಿದನಂತರ ತನ್ನ ತಂದೆಯಾದ ದಾವೀದನು ಯೆಹೋವನಿಗೆ ಹರಕೆಹೊತ್ತು ಪ್ರತಿಷ್ಠಿಸಿದ ಬೆಳ್ಳಿ ಬಂಗಾರವನ್ನೂ ಸಾಮಾನುಗಳನ್ನೂ ದೇವಾಲಯದ ಭಂಡಾರಕ್ಕೆ ಸೇರಿಸಿದನು."
		},
		"8": {
			"1": "<sup>1</sup> ಅನಂತರ ಅರಸನಾದ ಸೊಲೊಮೋನನು ದಾವೀದನಗರವಾದ ಚೀಯೋನಿನಿಂದ ಯೆಹೋವನ ಒಡಂಬಡಿಕೆಯ ಮಂಜೂಷವನ್ನು ಮೇಲೆ ತರುವದಕ್ಕಾಗಿ ಇಸ್ರಾಯೇಲ್ಯರ ಹಿರಿಯರು ಕುಲಾಧಿಪತಿಗಳು ಇವರೇ ಮೊದಲಾದ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ ಪ್ರಧಾನ ಪುರುಷರನ್ನು ಯೆರೂಸಲೇಮಿಗೆ ತನ್ನ ಹತ್ತಿರಕ್ಕೆ ಕರಿಸಿಕೊಂಡನು.",
			"2": "<sup>2</sup> ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರೂ ಏಳನೆಯ ತಿಂಗಳಾದ ಆಶ್ವೀಜಮಾಸದಲ್ಲಿ ಜಾತ್ರೆಗೋಸ್ಕರ ಅರಸನಾದ ಸೊಲೊಮೋನನ ಬಳಿಗೆ ಕೂಡಿಬಂದರು.",
			"3": "<sup>3</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಎಲ್ಲಾ ಹಿರಿಯರು ಕೂಡಿಬಂದಿರಲಾಗಿ ಯಾಜಕರು ಯೆಹೋವನ ಮಂಜೂಷವನ್ನು ಹೊತ್ತುಕೊಂಡರು.",
			"4": "<sup>4</sup> ಉಳಿದ ಯಾಜಕರೂ ಲೇವಿಯರೂ ದೇವದರ್ಶನದ ಗುಡಾರ, ಅದರಲ್ಲಿದ್ದ ಪರಿಶುದ್ಧವಸ್ತು ಇವುಗಳನ್ನು ಹೊತ್ತರು.",
			"5": "<sup>5</sup> ಅರಸನಾದ ಸೊಲೊಮೋನನೂ ಅಲ್ಲಿ ಕೂಡಿಬಂದಿದ್ದ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರೂ ಮಂಜೂಷದ ಮುಂದೆ ಅಸಂಖ್ಯವಾದ ಕುರಿದನಗಳನ್ನು ಯಜ್ಞಮಾಡಿದರು.",
			"6": "<sup>6</sup> ಯಾಜಕರು ಯೆಹೋವನ ಒಡಂಬಡಿಕೆಯ ಮಂಜೂಷವನ್ನು ತೆಗೆದುಕೊಂಡು ಬಂದು ಮಹಾಪರಿಶುದ್ಧ ಸ್ಥಳವೆನಿಸುವ ಗರ್ಭಗೃಹದಲ್ಲಿ ಕೆರೂಬಿಗಳ ರೆಕ್ಕೆಗಳ ಕೆಳಗೆ ಅದಕ್ಕೆ ನೇಮಕವಾದ ಸ್ಥಳದಲ್ಲಿ ಇಟ್ಟರು.",
			"7": "<sup>7</sup> ಕೆರೂಬಿಗಳ ರೆಕ್ಕೆಗಳು ಮಂಜೂಷವಿದ್ದ ಸ್ಥಳದ ಮೇಲೆ ಚಾಚಿದವುಗಳಾಗಿದ್ದದರಿಂದ ಮಂಜೂಷವೂ ಅದರ ಕೋಲುಗಳೂ ಪೂರ್ಣವಾಗಿ ಅವುಗಳ ನೆರಳಿನಲ್ಲಿದ್ದವು.",
			"8": "<sup>8</sup> ಆ ಕೋಲುಗಳು ಬಹಳ ಉದ್ದವಾಗಿದ್ದದರಿಂದ ಅವುಗಳ ತುದಿಗಳು ಗರ್ಭಗೃಹದ ಎದುರಿನಲ್ಲಿರುವ ಪರಿಶುದ್ಧಸ್ಥಳದಲ್ಲಿ ನಿಂತವರಿಗೆ ಕಾಣಿಸಿದರೂ ಹೊರಗೆ ನಿಂತವರಿಗೆ ಕಾಣಿಸುತ್ತಿರಲಿಲ್ಲ. ಅವು ಇಂದಿನವರೆಗೂ ಅಲ್ಲೇ ಇರುತ್ತವೆ.",
			"9": "<sup>9</sup> ಮಂಜೂಷದಲ್ಲಿ ಎರಡು ಕಲ್ಲಿನ ಹಲಿಗೆಗಳ ಹೊರತಾಗಿ ಬೇರೇನೂ ಇರಲಿಲ್ಲ. ಯೆಹೋವನು ಐಗುಪ್ತದಿಂದ ಬಂದ ಇಸ್ರಾಯೇಲ್ಯರೊಡನೆ ಹೋರೇಬ್ ಬೆಟ್ಟದ ಬಳಿಯಲ್ಲಿ ಒಡಂಬಡಿಕೆ ಮಾಡಿಕೊಂಡ ಮೇಲೆ ಮೋಶೆಯು ಅವುಗಳನ್ನು ಅದರಲ್ಲಿಟ್ಟಿದ್ದನು.",
			"10": "<sup>10</sup> ಯಾಜಕರು ಪರಿಶುದ್ಧಸ್ಥಳದಿಂದ ಹೊರಗೆ ಬಂದ ಕೂಡಲೆ ಮೇಘವು ಯೆಹೋವನ ಆಲಯವನ್ನು ತುಂಬಿಕೊಂಡಿತು.",
			"11": "<sup>11</sup> ಯೆಹೋವನ ತೇಜಸ್ಸಿನಿಂದ ವ್ಯಾಪ್ತವಾಗಿದ್ದ ಮೇಘವು ಆಲಯವನ್ನು ತುಂಬಿಕೊಂಡದರಿಂದ ಯಾಜಕರು ಅಲ್ಲಿ ನಿಂತು ಸೇವೆ ಮಾಡಲಾರದವರಾದರು.",
			"12": "<sup>12</sup> ತರುವಾಯ ಸೊಲೊಮೋನನು - ಯೆಹೋವನೇ, ಕಾರ್ಗತ್ತಲಲ್ಲಿ ವಾಸಿಸುತ್ತೇನೆಂದು ಹೇಳಿದ್ದೀ;",
			"13": "<sup>13</sup> ನಾನು ನಿನಗೋಸ್ಕರ ಒಂದು ಮಂದಿರವನ್ನು ಕಟ್ಟಿಸಿದ್ದೇನೆ; ಅದು ನಿನಗೆ ಶಾಶ್ವತವಾದ ವಾಸಸ್ಥಳವಾಗಿರಲಿ ಎಂದು ಪ್ರಾರ್ಥನೆಮಾಡಿದನು.",
			"14": "<sup>14</sup> ಇದಾದ ಮೇಲೆ ಅರಸನು ಎದ್ದುನಿಂತಿದ್ದ ಇಸ್ರಾಯೇಲ್ ಸಮೂಹದವರ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡು ಅವರನ್ನು ಆಶೀರ್ವದಿಸಿ -",
			"15": "<sup>15</sup> ಇಸ್ರಾಯೇಲ್‍ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಸ್ತೋತ್ರವಾಗಲಿ; ನನ್ನ ತಂದೆಯಾದ ದಾವೀದನಿಗೆ ಆತನ ಬಾಯಿ ನುಡಿದದ್ದನ್ನು ಆತನ ಹಸ್ತವು ಈಗ ನೆರವೇರಿಸಿಯದೆ.",
			"16": "<sup>16</sup> ಆತನು - ನಾನು ನನ್ನ ಜನರಾದ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಐಗುಪ್ತದಿಂದ ಬರಮಾಡಿದಂದಿನಿಂದ ನನ್ನ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಆಳುವದಕ್ಕೋಸ್ಕರ ದಾವೀದನನ್ನು ಆರಿಸಿಕೊಂಡೆನೇ ಹೊರತು ನನ್ನ ನಾಮದ ನಿವಾಸಕ್ಕೋಸ್ಕರ ಆಲಯ ಸ್ಥಾನವನ್ನಾಗಿ ಇಸ್ರಾಯೇಲ್‍ಕುಲಗಳ ಯಾವ ಪಟ್ಟಣವನ್ನೂ ಆರಿಸಿಕೊಳ್ಳಲಿಲ್ಲ ಎಂದು ಹೇಳಿದ್ದನು.",
			"17": "<sup>17</sup> ನನ್ನ ತಂದೆಯಾದ ದಾವೀದನು ಇಸ್ರಾಯೇಲ್‍ದೇವರಾದ ಯೆಹೋವನ ಹೆಸರಿಗೋಸ್ಕರ ಆಲಯವನ್ನು ಕಟ್ಟಬೇಕೆಂದು ಮನಸ್ಸುಮಾಡಿದಾಗ",
			"18": "<sup>18</sup> ಆತನು ಅವನಿಗೆ - ನೀನು ನನ್ನ ಹೆಸರಿಗೋಸ್ಕರ ಒಂದು ಮನೆಯನ್ನು ಕಟ್ಟುವದಕ್ಕೆ ಮನಸ್ಸುಮಾಡಿದ್ದು ಒಳ್ಳೇದೇ ಸರಿ.",
			"19": "<sup>19</sup> ಆದರೂ ನೀನು ಅದನ್ನು ಕಟ್ಟಬಾರದು; ನಿನ್ನಿಂದ ಹುಟ್ಟುವ ಮಗನು ನನ್ನ ಹೆಸರಿಗಾಗಿ ಒಂದು ಆಲಯವನ್ನು ಕಟ್ಟಬೇಕು ಎಂದು ಹೇಳಿದನು.",
			"20": "<sup>20</sup> ಯೆಹೋವನು ತಾನು ಕೊಟ್ಟ ಮಾತನ್ನು ನೆರವೇರಿಸಿದನು. ಆತನ ವಾಗ್ದಾನದಂತೆ ನಾನು ನನ್ನ ತಂದೆಯಾದ ದಾವೀದನಿಗೆ ಬದಲಾಗಿ ಇಸ್ರಾಯೇಲ್‍ಸಿಂಹಾಸನದ ಮೇಲೆ ಅರಸನಾಗಿ ಕೂತುಕೊಂಡು ಇಸ್ರಾಯೇಲ್‍ದೇವರಾದ ಯೆಹೋವನ ಹೆಸರಿಗೋಸ್ಕರ ಈ ಆಲಯವನ್ನು ಕಟ್ಟಿದೆನು;",
			"21": "<sup>21</sup> ಇದಲ್ಲದೆ ಐಗುಪ್ತದಿಂದ ಬಿಡುಗಡೆ ಹೊಂದಿದ ನಮ್ಮ ಪಿತೃಗಳಿಗೆ ಯೆಹೋವನಿಂದ ದೊರಕಿದ ನಿಬಂಧನಶಾಸನಗಳಿರುವ ಮಂಜೂಷಕ್ಕೆ ಸ್ಥಳವನ್ನು ಪ್ರತ್ಯೇಕಿಸಿ ಅಲ್ಲಿ ಅದನ್ನು ಇಟ್ಟೆನು ಎಂದು ಹೇಳಿದನು.",
			"22": "<sup>22</sup> ತರುವಾಯ ಸೊಲೊಮೋನನು ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರ ಎದುರಿನಲ್ಲಿ ಯೆಹೋವನ ಯಜ್ಞವೇದಿಯ ಮುಂದೆ ನಿಂತು ಆಕಾಶದ ಕಡೆಗೆ ಕೈಗಳನ್ನೆತ್ತಿ ಹೀಗೆ ಪ್ರಾರ್ಥಿಸಿದನು -",
			"23": "<sup>23</sup> ಇಸ್ರಾಯೇಲ್‍ದೇವರಾದ ಯೆಹೋವನೇ, ಭೂಲೋಕದಲ್ಲಿಯೂ ಪರಲೋಕದಲ್ಲಿಯೂ ನಿನಗೆ ಸಮಾನನಾದ ದೇವರಿಲ್ಲ; ಪೂರ್ಣಮನಸ್ಸಿನಿಂದ ನಿನಗೆ ನಡೆದುಕೊಳ್ಳುವಂಥ ಸೇವಕರ ವಿಷಯದಲ್ಲಿ ನೀನು ನಿನ್ನ ಒಡಂಬಡಿಕೆಯನ್ನೂ ಕೃಪೆಯನ್ನೂ ನೆರವೇರಿಸುವವನು.",
			"24": "<sup>24</sup> ನಿನ್ನ ಸೇವಕನೂ ನನ್ನ ತಂದೆಯೂ ಆದ ದಾವೀದನಿಗೆ ಕೊಟ್ಟ ಮಾತನ್ನು ಕೈಕೊಂಡಿ; ನಿನ್ನ ಬಾಯಿ ನುಡಿದದ್ದನ್ನು ನಿನ್ನ ಹಸ್ತವು ಈಗ ನೆರವೇರಿಸಿತು.",
			"25": "<sup>25</sup> ಇಸ್ರಾಯೇಲ್‍ದೇವರಾದ ಯೆಹೋವನೇ, ನೀನು ನನ್ನ ತಂದೆಯಾದ ದಾವೀದನಿಗೆ - ನಿನ್ನ ಸಂತಾನದವರು ನಿನ್ನಂತೆ ಜಾಗರೂಕತೆಯಿಂದ ನನ್ನ ಮಾರ್ಗದಲ್ಲೇ ನಡೆದುಕೊಳ್ಳುವದಾದರೆ ಅವರು ತಪ್ಪದೆ ಇಸ್ರಾಯೇಲ್‍ಸಿಂಹಾಸನದ ಮೇಲೆ ಕೂತುಕೊಳ್ಳುವಂತೆ ಮಾಡುವೆನು ಎಂಬದಾಗಿ ವಾಗ್ದಾನ ಮಾಡಿದಿಯಲ್ಲಾ; ಅದನ್ನು ನೆರವೇರಿಸು.",
			"26": "<sup>26</sup> ಇಸ್ರಾಯೇಲ್‍ದೇವರೇ, ನೀನು ನಿನ್ನ ಸೇವಕನೂ ನನ್ನ ತಂದೆಯೂ ಆದ ದಾವೀದನಿಗೆ ನುಡಿದದ್ದೆಲ್ಲವೂ ಸಾರ್ಥಕವಾಗಲಿ.",
			"27": "<sup>27</sup> ದೇವರು ನಿಜವಾಗಿ ಭೂಲೋಕದಲ್ಲಿ ವಾಸಿಸುವನೋ? ಆಕಾಶವೂ ಉನ್ನತೋನ್ನತವಾದ ಆಕಾಶವೂ ನಿನ್ನ ವಾಸಕ್ಕೆ ಸಾಲವು; ಹೀಗಿರುವಲ್ಲಿ ನಾನು ಕಟ್ಟಿಸಿದ ಈ ಮಂದಿರವು ಹೇಗೆ ಸಾಕಾದೀತು!",
			"28": "<sup>28</sup> ಆದರೂ ನನ್ನ ದೇವರೇ, ಯೆಹೋವನೇ, ನಿನ್ನ ಸೇವಕನ ಪ್ರಾರ್ಥನೆಗೂ ವಿಜ್ಞಾಪನೆಗೂ ಕಿವಿಗೊಡು; ಈಹೊತ್ತು ನಿನ್ನನ್ನು ಪ್ರಾರ್ಥಿಸುತ್ತಿರುವ ನಿನ್ನ ಸೇವಕನ ಮೊರೆಯನ್ನು ಲಾಲಿಸು.",
			"29": "<sup>29</sup> ಈ ಸ್ಥಳವನ್ನು ಕುರಿತು - ನನ್ನ ನಾಮಪ್ರಭಾವವು ಇಲ್ಲಿ ವಾಸಿಸುವದು ಎಂದು ಹೇಳಿದವನೇ, ನಿನ್ನ ಕಟಾಕ್ಷವು ಹಗಲಿರುಳು ಈ ಮಂದಿರದ ಮೇಲಿರಲಿ; ಇಲ್ಲಿ ನಿನ್ನ ಸೇವಕನು ನಿನ್ನನ್ನು ಪ್ರಾರ್ಥಿಸುವಾಗೆಲ್ಲಾ ಅವನಿಗೆ ಸದುತ್ತರವನ್ನು ದಯಪಾಲಿಸು.",
			"30": "<sup>30</sup> ನಿನ್ನ ಸೇವಕನಾದ ನಾನಾಗಲಿ ನಿನ್ನ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಾಯೇಲ್ಯರಾಗಲಿ ಈ ಸ್ಥಳದ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡು ನಿನ್ನನ್ನು ಪ್ರಾರ್ಥಿಸುವದಾದರೆ ನಿನ್ನ ನಿವಾಸವಾಗಿರುವ ಪರಲೋಕದಿಂದ ನಮ್ಮ ಬಿನ್ನಹವನ್ನು ಕೇಳಿ ಕ್ಷಮೆಯನ್ನು ಅನುಗ್ರಹಿಸು.",
			"31": "<sup>31</sup> ನೆರೆಯವನಿಗೆ ವಿರೋಧವಾಗಿ ತಪ್ಪುಮಾಡಿದವನೆಂಬ ಸಂಶಯಕ್ಕೆ ಗುರಿಯಾದ ಮನುಷ್ಯನು ತಾನು ನಿರ್ದೋಷಿಯೆಂದು ಪ್ರಮಾಣಮಾಡಬೇಕಾದಾಗ ಅಂಥವನು ಈ ಆಲಯಕ್ಕೆ ಬಂದು ನಿನ್ನ ವೇದಿಯ ಮುಂದೆ ನಿಂತು ಪ್ರಮಾಣಮಾಡುವದಾದರೆ",
			"32": "<sup>32</sup> ಪರಲೋಕದಲ್ಲಿರುವ ನೀನು ಅದನ್ನು ಕೇಳಿ ನಿನ್ನ ಸೇವಕರ ವ್ಯಾಜ್ಯವನ್ನು ತೀರಿಸು; ದುಷ್ಟನಿಗೆ ಅವನ ದುಷ್ಟತ್ವವನ್ನು ಅವನ ತಲೆಯ ಮೇಲೆಯೇ ಬರಮಾಡಿ ಅವನು ಅಪರಾಧಿಯೆಂದೂ ನೀತಿವಂತನಿಗೆ ಅವನ ನೀತಿಯ ಫಲವನ್ನು ಅನುಗ್ರಹಿಸಿ ಅವನು ನೀತಿವಂತನೆಂದೂ ತೋರಿಸಿಕೊಡು.",
			"33": "<sup>33</sup> ತಮ್ಮ ಪಾಪಗಳ ಸಲುವಾಗಿ ಶತ್ರುಗಳಿಂದ ತಗ್ಗಿಸಲ್ಪಟ್ಟ ನಿನ್ನ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಾಯೇಲ್ಯರು ನಿನ್ನ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡು ನಿನ್ನ ನಾಮವನ್ನು ಎತ್ತಿ ಈ ಆಲಯದಲ್ಲಿ ನಿನಗೆ ವಿಜ್ಞಾಪನೆಯನ್ನೂ ಪ್ರಾರ್ಥನೆಯನ್ನೂ ಮಾಡುವದಾದರೆ",
			"34": "<sup>34</sup> ಪರಲೋಕದಲ್ಲಿರುವ ನೀನು ಲಾಲಿಸಿ ಅವರ ಪಾಪಗಳನ್ನು ಕ್ಷಮಿಸಿ ಅವರ ಪಿತೃಗಳಿಗೆ ನೀನು ಕೊಟ್ಟ ದೇಶಕ್ಕೆ ಅವರನ್ನು ತಿರಿಗಿ ಬರಮಾಡು.",
			"35": "<sup>35</sup> ಅವರ ಪಾಪಗಳ ನಿಮಿತ್ತವಾಗಿ ಆಕಾಶವು ಮಳೆಗರೆಯದಂತೆ ಮುಚ್ಚಿಕೊಂಡಾಗ ಅವರು ತಮ್ಮ ಪಾಪಗಳನ್ನು ಬಿಟ್ಟು ತಮ್ಮನ್ನು ತಗ್ಗಿಸಿದವನು ನೀನೇ ಎಂದು ನಿನ್ನ ನಾಮವನ್ನು ಎತ್ತಿ ಈ ಆಲಯದ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡು ಪ್ರಾರ್ಥಿಸುವದಾದರೆ",
			"36": "<sup>36</sup> ಪರಲೋಕದಲ್ಲಿರುವ ನೀನು ಲಾಲಿಸಿ ನಿನ್ನ ಸೇವಕರೂ ಪ್ರಜೆಗಳೂ ಆದ ಇಸ್ರಾಯೇಲ್ಯರ ಪಾಪಗಳನ್ನು ಕ್ಷಮಿಸಿ ಅವರು ನಡೆಯಬೇಕಾದ ಮಾರ್ಗವನ್ನು ತೋರಿಸಿ ನಿನ್ನ ಪ್ರಜೆಗೆ ಸ್ವಾಸ್ತ್ಯವಾಗಿ ಕೊಟ್ಟ ದೇಶಕ್ಕೆ ಮಳೆಯನ್ನು ಅನುಗ್ರಹಿಸು.",
			"37": "<sup>37</sup> ದೇಶಕ್ಕೆ ಕ್ಷಾಮ, ಘೋರವ್ಯಾಧಿ, ಬಿಸಿಗಾಳಿ, ಬೂದಿ, ಮಿಡಿತೆ, ಜಿಟ್ಟೇಹುಳ, ಪಟ್ಟಣಗಳಿಗೆ ಶತ್ರುಗಳ ಮುತ್ತಿಗೆ, ಅಂತು ಯಾವ ಉಪದ್ರವದಿಂದಾಗಲಿ",
			"38": "<sup>38</sup> ವ್ಯಾಧಿಯಿಂದಾಗಲಿ ಬಾಧೆಯುಂಟಾಗುವಲ್ಲಿ ಮನಸ್ಸಾಕ್ಷಿ ಪೀಡಿತರಾದ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರಾಗಲಿ ಅವರಲ್ಲಿ ಒಬ್ಬನಾಗಲಿ ಈ ಆಲಯದ ಕಡೆಗೆ ಕೈಯೆತ್ತಿ ನಿನಗೆ ಪ್ರಾರ್ಥನೆಯನ್ನೂ ವಿಜ್ಞಾಪನೆಯನ್ನೂ ಮಾಡುವದಾದರೆ",
			"39": "<sup>39</sup> ಮನುಷ್ಯರ ಹೃದಯಗಳನ್ನು ಬಲ್ಲಂಥ ನೀನು ನಿನ್ನ ನಿವಾಸವಾಗಿರುವ ಪರಲೋಕದಿಂದ ಲಾಲಿಸಿ ಅವರಿಗೆ ಪಾಪಪರಿಹಾರವನ್ನು ಅನುಗ್ರಹಿಸು; ನೀನೊಬ್ಬನೇ ಎಲ್ಲಾ ಮನುಷ್ಯರ ಹೃದಯಗಳನ್ನು ಬಲ್ಲವನಾಗಿರುವದರಿಂದ ಪ್ರತಿಯೊಬ್ಬನಿಗೂ ಅವನವನು ಹಿಡಿಯುವ ಮಾರ್ಗಕ್ಕೆ ತಕ್ಕ ಹಾಗೆ ಫಲವನ್ನು ಕೊಡು.",
			"40": "<sup>40</sup> ಆಗ ನಮ್ಮ ಪಿತೃಗಳಿಗೆ ನೀನು ಕೊಟ್ಟ ದೇಶದಲ್ಲಿ ಅವರು ವಾಸಿಸುವ ಕಾಲದಲ್ಲೆಲ್ಲಾ ನಿನ್ನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿರುವರು.",
			"41": "<sup>41</sup> ನಿನ್ನ ನಾಮಮಹತ್ತು, ಭುಜಬಲ, ಶಿಕ್ಷಾಹಸ್ತ ಇವುಗಳ ವರ್ತಮಾನವು ಪರರಾಜ್ಯಗಳವರಿಗೂ ಗೊತ್ತಾಗುವದು.",
			"42": "<sup>42</sup> ನಿನ್ನ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಸೇರದವನಾದ ಇಂಥ ಒಬ್ಬ ಪರದೇಶಿಯು ನಿನ್ನ ನಾಮಕ್ಕೋಸ್ಕರ ದೂರದೇಶದಿಂದ ಬಂದು ಈ ಆಲಯದ ಮುಂದೆ ನಿಂತು ನಿನ್ನನ್ನು ಪ್ರಾರ್ಥಿಸುವದಾದರೆ",
			"43": "<sup>43</sup> ನಿನ್ನ ನಿವಾಸವಾಗಿರುವ ಪರಲೋಕದಿಂದ ಅವನ ಪ್ರಾರ್ಥನೆಯನ್ನು ಕೇಳಿ ಅವನು ಬೇಡಿಕೊಂಡದ್ದನ್ನು ಅನುಗ್ರಹಿಸು; ಆಗ ಲೋಕದ ಎಲ್ಲಾ ಜನರೂ ನಿನ್ನ ನಾಮಮಹತ್ತನ್ನು ತಿಳಿದು ನಿನ್ನ ಜನರಾದ ಇಸ್ರಾಯೇಲ್ಯರಂತೆ ನಿನ್ನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿ ನಾನು ನಿನ್ನ ಹೆಸರಿಗಾಗಿ ಈ ಆಲಯವನ್ನು ಕಟ್ಟಿಸಿದ್ದೇನೆಂದು ತಿಳಿದುಕೊಳ್ಳುವರು.",
			"44": "<sup>44</sup> ನೀನು ನಿನ್ನ ಜನರನ್ನು ಶತ್ರುಗಳೊಡನೆ ಯುದ್ಧಮಾಡುವದಕ್ಕೋಸ್ಕರ ಎಲ್ಲಿಗಾದರೂ ಕಳುಹಿಸಿದಾಗ ಅವರು ಅಲ್ಲಿಂದ ನೀನು ಆರಿಸಿಕೊಂಡ ಪಟ್ಟಣದ ಕಡೆಗೂ ನಾನು ನಿನ್ನ ಹೆಸರಿಗಾಗಿ ಕಟ್ಟಿಸಿದ ಈ ಆಲಯದ ಕಡೆಗೂ ತಿರುಗಿಕೊಂಡು ಯೆಹೋವನಾದ ನಿನ್ನನ್ನು ಪ್ರಾರ್ಥಿಸುವದಾದರೆ",
			"45": "<sup>45</sup> ಪರಲೋಕದಲ್ಲಿರುವ ನೀನು ಅವರ ವಿಜ್ಞಾಪನೆಯನ್ನೂ ಪ್ರಾರ್ಥನೆಯನ್ನೂ ಕೇಳಿ ಅವರ ನ್ಯಾಯವನ್ನು ಸ್ಥಾಪಿಸು.",
			"46": "<sup>46</sup> ಅವರು ನಿನಗೆ ವಿರುದ್ಧವಾಗಿ ಪಾಪಮಾಡಾರು. ಪಾಪಮಾಡದ ಮನುಷ್ಯನು ಒಬ್ಬನೂ ಇಲ್ಲವಲ್ಲಾ. ನೀನು ಅವರ ಮೇಲೆ ಕೋಪಗೊಂಡು ಅವರನ್ನು ಶತ್ರುಗಳ ಕೈಗೆ ಒಪ್ಪಿಸಿದಾಗ ಮತ್ತು ಆ ಶತ್ರುಗಳು ಅವರನ್ನು ಸೆರೆಹಿಡಿದು ದೂರದಲ್ಲಾಗಲಿ ಸಮೀಪದಲ್ಲಾಗಲಿ ಇರುವ ತಮ್ಮ ದೇಶಕ್ಕೆ ಅವರನ್ನು ಒಯ್ದಾಗ",
			"47": "<sup>47</sup> ತಾವು ಸೆರೆಯವರಾಗಿ ಬಿದ್ದುಕೊಂಡಿರುವ ದೇಶದಲ್ಲಿ ಅವರು ಪಶ್ಚಾತ್ತಾಪಪಟ್ಟು",
			"48": "<sup>48</sup> ನಿನ್ನ ಅನುಗ್ರಹದಿಂದ ತಮ್ಮ ಪಿತೃಗಳಿಗೆ ದೊರಕಿದ ದೇಶದ ಕಡೆಗೂ ನೀನು ಆರಿಸಿಕೊಂಡ ಪಟ್ಟಣದ ಕಡೆಗೂ ನಾನು ನಿನ್ನ ಹೆಸರಿಗೋಸ್ಕರ ಕಟ್ಟಿಸಿದ ಈ ಆಲಯದ ಕಡೆಗೂ ತಿರುಗಿಕೊಂಡು - ನಾವು ನಿನ್ನ ಆಜ್ಞೆಗಳನ್ನು ಮೀರಿ ಪಾಪಮಾಡಿ ದ್ರೋಹಿಗಳಾದೆವು ಎಂದು ಒಪ್ಪಿ ಪೂರ್ಣಮನಸ್ಸಿನಿಂದಲೂ ಪೂರ್ಣಪ್ರಾಣದಿಂದಲೂ ನಿನ್ನನ್ನು ಪ್ರಾರ್ಥಿಸುವದಾದರೆ",
			"49": "<sup>49</sup> ನೀನು ನಿನ್ನ ನಿವಾಸವಾಗಿರುವ ಪರಲೋಕದಿಂದ ಅವರ ವಿಜ್ಞಾಪನೆಯನ್ನೂ ಪ್ರಾರ್ಥನೆಯನ್ನೂ ಲಾಲಿಸಿ ಅವರ ನ್ಯಾಯವನ್ನು ಸ್ಥಾಪಿಸು.",
			"50": "<sup>50</sup> ಕಬ್ಬಿಣ ಕರಗಿಸುವ ಕುಲಿಮೆಯೋಪಾದಿಯಲ್ಲಿದ್ದ ಐಗುಪ್ತ ದೇಶದಿಂದ ನೀನು ಬಿಡಿಸಿದ ಈ ಜನರು",
			"51": "<sup>51</sup> ನಿನ್ನ ಸ್ವಕೀಯ ಜನರಾಗಿದ್ದಾರೆಂಬದನ್ನು ನೆನಸಿ ಅವರು ನಿನಗೆ ವಿರುದ್ಧವಾಗಿ ಮಾಡಿದ ಎಲ್ಲಾ ಅಪರಾಧ ದ್ರೋಹಗಳನ್ನು ಕ್ಷಮಿಸಿ ಅವರನ್ನು ಸೆರೆಯೊಯ್ದವರ ಮನಸ್ಸಿನಲ್ಲಿ ಅವರ ಮೇಲೆ ದಯೆ ಹುಟ್ಟಿಸು.",
			"52": "<sup>52</sup> ನಿನ್ನ ಸೇವಕನ ಮೇಲೆಯೂ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಾಯೇಲ್ಯರ ಮೇಲೆಯೂ ಕಟಾಕ್ಷವಿಟ್ಟು ಅವರು ಪ್ರಾರ್ಥಿಸುವಾಗೆಲ್ಲಾ ಅವರ ವಿಜ್ಞಾಪನೆಗಳನ್ನು ಲಾಲಿಸು.",
			"53": "<sup>53</sup> ಕರ್ತನೇ, ಯೆಹೋವನೇ, ನೀನು ನಮ್ಮ ಪಿತೃಗಳನ್ನು ಐಗುಪ್ತದೇಶದಿಂದ ಬಿಡಿಸುವಾಗ ಅವರಿಗೆ - ನಾನು ನಿಮ್ಮನ್ನು ಎಲ್ಲಾ ಜನಾಂಗಗಳೊಳಗಿಂದ ಪ್ರತ್ಯೇಕಿಸಿ ಸ್ವಕೀಯ ಜನರನ್ನಾಗಿ ಮಾಡಿಕೊಂಡಿದ್ದೇನೆಂದು ಮೋಶೆಯ ಮುಖಾಂತರವಾಗಿ ಹೇಳಿದಿಯಲ್ಲವೇ ಎಂದು ದೇವರನ್ನು ಪ್ರಾರ್ಥಿಸಿದನು.",
			"54": "<sup>54</sup> ಆಕಾಶದ ಕಡೆಗೆ ಕೈಯೆತ್ತಿ ಯೆಹೋವನ ಯಜ್ಞವೇದಿಯ ಮುಂದೆ ಮೊಣಕಾಲೂರಿದ್ದ ಸೊಲೊಮೋನನು ಈ ಪ್ರಕಾರವಾಗಿ ಯೆಹೋವನನ್ನು ಪ್ರಾರ್ಥಿಸಿದ ನಂತರ ಎದ್ದು ನಿಂತು",
			"55": "<sup>55</sup> ಇಸ್ರಾಯೇಲ್ ಸರ್ವಸಮೂಹದವರನ್ನು ಆಶೀರ್ವದಿಸಿ ಗಟ್ಟಿಯಾಗಿ ಹೇಳಿದ್ದೇನಂದರೆ -",
			"56": "<sup>56</sup> ತಾನು ವಾಗ್ದಾನಮಾಡಿದಂತೆ ತನ್ನ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ವಿಶ್ರಾಂತಿಯನ್ನು ಅನುಗ್ರಹಿಸಿದ ಯೆಹೋವನಿಗೆ ಸ್ತೋತ್ರವಾಗಲಿ. ಆತನು ತನ್ನ ಸೇವಕನಾದ ಮೋಶೆಯ ಮುಖಾಂತರವಾಗಿ ಮಾಡಿದ ಅತಿಶ್ರೇಷ್ಠವಾಗ್ದಾನಗಳಲ್ಲಿ ಒಂದೂ ತಪ್ಪಿಹೋಗಲಿಲ್ಲ.",
			"57": "<sup>57</sup> ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಮ್ಮ ಪಿತೃಗಳ ಸಂಗಡ ಇದ್ದ ಹಾಗೆ ನಮ್ಮ ಸಂಗಡಲೂ ಇರಲಿ; ಆತನು ನಮ್ಮನ್ನು ಕೈಬಿಡದಿರಲಿ, ತಳ್ಳದಿರಲಿ.",
			"58": "<sup>58</sup> ನಾವು ನಮ್ಮ ಪಿತೃಗಳಿಗೆ ಕೊಡಲ್ಪಟ್ಟ ಆಜ್ಞಾನಿಯಮ ವಿಧಿಗಳನ್ನು ಕೈಕೊಂಡು ಆತನ ಮಾರ್ಗದಲ್ಲಿ ನಡೆಯುವಂತೆ ನಮ್ಮ ಮನಸ್ಸನ್ನು ತನ್ನ ಕಡೆಗೆ ತಿರುಗಿಸಿಕೊಳ್ಳಲಿ.",
			"59": "<sup>59</sup> ನಾನು ಈಗ ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಮಾಡಿದ ಪ್ರಾರ್ಥನೆಯು ಹಗಲಿರುಳು ಆತನ ಜ್ಞಾಪಕದಲ್ಲಿರಲಿ.",
			"60": "<sup>60</sup> ಭೂಲೋಕದ ಜನರೆಲ್ಲರೂ ಯೆಹೋವನ ಹೊರತು ಬೇರೆ ದೇವರಿಲ್ಲವೆಂಬದನ್ನು ತಿಳಿದುಕೊಳ್ಳುವಂತೆ ಆತನು ಅವಶ್ಯವಿರುವಾಗೆಲ್ಲಾ ತನ್ನ ಸೇವಕನ ಮತ್ತು ಪ್ರಜೆಗಳಾದ ಇಸ್ರಾಯೇಲ್ಯರ ನ್ಯಾಯವನ್ನು ಸ್ಥಾಪಿಸುತ್ತಾ ಬರಲಿ.",
			"61": "<sup>61</sup> ನೀವಾದರೋ ಈಗಿನಂತೆ ಮುಂದೆಯೂ ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಲ್ಲಿ ಪೂರ್ಣಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿ ಆತನ ವಿಧಿಗಳನ್ನು ಅನುಸರಿಸಿ ಆತನ ಆಜ್ಞೆಗಳನ್ನು ಕೈಕೊಳ್ಳಿರಿ ಎಂಬದೇ.",
			"62": "<sup>62</sup> ಅರಸನೂ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರೂ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಯಜ್ಞಮಾಡಿದರು.",
			"63": "<sup>63</sup> ಸೊಲೊಮೋನನು ಯೆಹೋವನಿಗೋಸ್ಕರ ಮಾಡಿದ ಸಮಾಧಾನಯಜ್ಞಕ್ಕಾಗಿ ವಧಿಸಿದ ಹೋರಿಗಳು ಇಪ್ಪತ್ತೆರಡು ಸಾವಿರ; ಕುರಿಗಳು ಲಕ್ಷದ ಇಪ್ಪತ್ತು ಸಾವಿರ. ಈ ಪ್ರಕಾರ ಅರಸನೂ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರೂ ಯೆಹೋವನ ಆಲಯವನ್ನು ಪ್ರತಿಷ್ಠಿಸಿದರು.",
			"64": "<sup>64</sup> ಯೆಹೋವನ ಆಲಯದ ಮುಂದಿರುವ ತಾಮ್ರದ ಯಜ್ಞವೇದಿಯು ಈ ಎಲ್ಲಾ ಸರ್ವಾಂಗಹೋಮದ್ರವ್ಯಗಳನ್ನೂ ಧಾನ್ಯನೈವೇದ್ಯಗಳನ್ನೂ ಸಮಾಧಾನಯಜ್ಞದ ಕೊಬ್ಬನ್ನೂ ಹಿಡಿಯಲಾರದಷ್ಟು ಚಿಕ್ಕದಾಗಿದ್ದದರಿಂದ ಸೊಲೊಮೋನನು ಆ ದಿವಸ ದೇವಾಲಯದ ಮುಂದಿನ ಪ್ರಾಕಾರದ ಮಧ್ಯಸ್ಥಳವನ್ನು ಪ್ರತಿಷ್ಠಿಸಿ ಅಲ್ಲಿ ಇವುಗಳನ್ನೆಲ್ಲಾ ಸಮರ್ಪಿಸಿದನು.",
			"65": "<sup>65</sup> ಈ ಪ್ರಕಾರ ಸೊಲೊಮೋನನೂ ಹಮಾತಿನ ದಾರಿಯಿಂದ ಐಗುಪ್ತದ ಹಳ್ಳದವರೆಗಿರುವ ಪ್ರಾಂತಗಳಿಂದ ಮಹಾ ಸಮೂಹವಾಗಿ ಕೂಡಿಬಂದಿದ್ದ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರೂ ಎರಡು ವಾರ ಅಂದರೆ ಹದಿನಾಲ್ಕು ದಿವಸಗಳವರೆಗೆ ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಹಬ್ಬಮಾಡಿದರು.",
			"66": "<sup>66</sup> ಎಂಟನೆಯ ದಿವಸ ಜನರಿಗೆ ಹೋಗುವದಕ್ಕೆ ಅಪ್ಪಣೆಯಾಗಲು ಅವರು ಅರಸನನ್ನು ವಂದಿಸಿ ಯೆಹೋವನು ತನ್ನ ಸೇವಕನಾದ ದಾವೀದನಿಗೂ ತನ್ನ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಾಯೇಲ್ಯರಿಗೂ ಮಾಡಿದ ಸರ್ವೋಪಕಾರಗಳನ್ನು ನೆನಸಿ ಆನಂದಚಿತ್ತರಾಗಿ ಹರ್ಷಿಸುತ್ತಾ ತಮ್ಮತಮ್ಮ ನಿವಾಸಗಳಿಗೆ ತೆರಳಿದರು."
		},
		"9": {
			"1": "<sup>1</sup> ಸೊಲೊಮೋನನು ಯೆಹೋವನ ಆಲಯವನ್ನೂ ತನ್ನ ಅರಮನೆಯನ್ನೂ ತನಗೆ ಇಷ್ಟವಾದ ಬೇರೆ ಎಲ್ಲಾ ಮಂದಿರಗಳನ್ನೂ ಕಟ್ಟಿಸಿದನಂತರ",
			"2": "<sup>2</sup> ಯೆಹೋವನು ಅವನಿಗೆ ಗಿಬ್ಯೋನಿನಲ್ಲಿ ಪ್ರತ್ಯಕ್ಷನಾದಂತೆ ಎರಡನೆಯ ಸಾರಿ ಪ್ರತ್ಯಕ್ಷನಾದನು.",
			"3": "<sup>3</sup> ಆತನು ಅವನಿಗೆ - ನೀನು ನನಗೆ ಮಾಡಿದ ಪ್ರಾರ್ಥನೆಯನ್ನೂ ವಿಜ್ಞಾಪನೆಯನ್ನೂ ಕೇಳಿದ್ದೇನೆ. ನನ್ನ ನಾಮಮಹತ್ತು ನೀನು ಕಟ್ಟಿಸಿದ ಆಲಯದಲ್ಲಿ ಸದಾ ಇರುವಂತೆ ಅದನ್ನು ನನಗೋಸ್ಕರ ಪ್ರತಿಷ್ಠಿಸಿಕೊಂಡಿದ್ದೇನೆ; ನನ್ನ ದೃಷ್ಟಿಯೂ ಮನಸ್ಸೂ ಪ್ರತಿದಿನವೂ ಅದರ ಮೇಲಿರುವವು.",
			"4": "<sup>4</sup> ನೀನು ನಿನ್ನ ತಂದೆಯಾದ ದಾವೀದನಂತೆ ಪೂರ್ಣಮನಸ್ಸಿನಿಂದಲೂ ಯಥಾರ್ಥಚಿತ್ತದಿಂದಲೂ ನನಗೆ ನಡೆದುಕೊಂಡು ನನ್ನ ಆಜ್ಞಾವಿಧಿನ್ಯಾಯಗಳನ್ನು ಕೈಕೊಳ್ಳುತ್ತಾ ಬರುವದಾದರೆ",
			"5": "<sup>5</sup> ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ನಿನ್ನ ಸಿಂಹಾಸನವನ್ನು ಸದಾ ಸ್ಥಿರಪಡಿಸುವೆನು; ಇಸ್ರಾಯೇಲ್‍ಸಿಂಹಾಸನದ ಮೇಲೆ ನಿನ್ನ ಸಂತಾನದವರು ತಪ್ಪದೆ ಕೂತುಕೊಳ್ಳುವರು ಎಂಬದಾಗಿ ನಿನ್ನ ತಂದೆಯಾದ ದಾವೀದನಿಗೆ ನಾನು ಮಾಡಿದ ವಾಗ್ದಾನವನ್ನು ನೆರವೇರಿಸುವೆನು.",
			"6": "<sup>6</sup> ಆದರೆ ನೀವಾಗಲಿ ನಿಮ್ಮ ಮಕ್ಕಳಾಗಲಿ ನನ್ನ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ಕೈಕೊಳ್ಳದೆ ನನ್ನನ್ನು ಬಿಟ್ಟು ಅನ್ಯದೇವತೆಗಳನ್ನು ಹಿಂಬಾಲಿಸಿ ಅವುಗಳಿಗೆ ಕೈ ಮುಗಿಯುವದಾದರೆ",
			"7": "<sup>7</sup> ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ನಾನು ಅವರಿಗೆ ಕೊಟ್ಟ ದೇಶದಿಂದ ತೆಗೆದುಹಾಕಿ ನನ್ನ ಹೆಸರಿಗೋಸ್ಕರ ಪ್ರತಿಷ್ಠಿಸಿಕೊಂಡ ಆಲಯವನ್ನು ನಿರಾಕರಿಸಿಬಿಡುವೆನು. ಇಸ್ರಾಯೇಲ್ಯರು ಎಲ್ಲಾ ಜನಾಂಗಗಳವರ ಗಾದೆಗೂ ನಿಂದೆಗೂ ಆಸ್ಪದರಾಗುವರು.",
			"8": "<sup>8</sup> ಉನ್ನತವಾದ ಈ ಮಂದಿರದ ಮಾರ್ಗವಾಗಿ ಹಾದುಹೋಗುವವರು ಅದನ್ನು ನೋಡಿ ವಿಸ್ಮಿತರಾಗಿ - ಅಬ್ಬಬ್ಬಾ, ಇದೇನು! ಯೆಹೋವನು ಈ ದೇಶವನ್ನೂ ಈ ಆಲಯವನ್ನೂ ಹೀಗೇಕೆ ಮಾಡಿದನು ಎಂದು ಕೇಳುವರು.",
			"9": "<sup>9</sup> ಆಗ ಅವರಿಗೆ - ಈ ದೇಶದವರು ತಮ್ಮ ಪಿತೃಗಳನ್ನು ಐಗುಪ್ತದಿಂದ ಬರಮಾಡಿದ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಬಿಟ್ಟು ಅನ್ಯದೇವತೆಗಳನ್ನು ಅವಲಂಬಿಸಿ ಅವುಗಳಿಗೆ ಅಡ್ಡಬಿದ್ದು ಸೇವಿಸಿದದರಿಂದಲೇ ಯೆಹೋವನು ಈ ಎಲ್ಲಾ ಆಪತ್ತನ್ನು ಬರಮಾಡಿದ್ದಾನೆ ಎಂಬ ಉತ್ತರವು ಸಿಕ್ಕುವದು ಅಂದನು.",
			"10": "<sup>10</sup> ಅರಸನಾದ ಸೊಲೊಮೋನನು ಇಪ್ಪತ್ತು ವರುಷಗಳಲ್ಲಿ ಯೆಹೋವನ ಆಲಯವನ್ನೂ ತನ್ನ ಅರಮನೆಯನ್ನೂ ಕಟ್ಟಿಸಿ ತೀರಿಸಿದ ನಂತರ",
			"11": "<sup>11</sup> ತನಗೆ ಬೇಕಾದಷ್ಟು ದೇವದಾರು ಮರಗಳನ್ನೂ ತುರಾಯಿ ಮರಗಳನ್ನೂ ಬಂಗಾರವನ್ನೂ ಕೊಟ್ಟಂಥ ತೂರಿನ ಅರಸನಾದ ಹೀರಾಮನಿಗೆ ಗಲಿಲಾಯ ಪ್ರಾಂತದಲ್ಲಿನ ಇಪ್ಪತ್ತು ಪಟ್ಟಣಗಳನ್ನು ಕೊಟ್ಟನು.",
			"12": "<sup>12</sup> ಹೀರಾಮನು ತೂರಿನಿಂದ ಬಂದು ಆ ಪಟ್ಟಣಗಳನ್ನು ನೋಡಲು ಅವು ಅವನ ಮನಸ್ಸಿಗೆ ಬರಲಿಲ್ಲ.",
			"13": "<sup>13</sup> ಆದದರಿಂದ ಅವನು ಸೊಲೊಮೋನನಿಗೆ - ಸಹೋದರನೇ, ನೀನು ನನಗೆ ಎಂಥ ಪಟ್ಟಣಗಳನ್ನು ಕೊಟ್ಟಿ ಎಂದು ಹೇಳಿ ಅವುಗಳಿಗೆ ಕಾಬೂಲ್‍ದೇಶವೆಂದು ಹೆಸರಿಟ್ಟನು.",
			"14": "<sup>14</sup> ಅವುಗಳಿಗೆ ಇಂದಿನವರೆಗೂ ಇದೇ ಹೆಸರಿರುತ್ತದೆ. ಹೀರಾಮನು ಸೊಲೊಮೋನನಿಗೆ ನೂರಿಪ್ಪತ್ತು ತಲಾಂತು ಬಂಗಾರವನ್ನು ಕೊಟ್ಟನು.",
			"15": "<sup>15</sup> ಸೊಲೊಮೋನನ ಬಿಟ್ಟೀಕೆಲಸದವರು ಕಟ್ಟಿದವುಗಳು ಯಾವವಂದರೆ - ಯೆಹೋವನ ಆಲಯ, ಅರಮನೆ, ಮಿಲ್ಲೋಕೋಟೆ, ಯೆರೂಸಲೇಮಿನ ಪೌಳಿಗೋಡೆ, ಹಾಚೋರ್, ಮೆಗಿದ್ದೋ, ಗೆಜೆರ್ ಇವೇ.",
			"16": "<sup>16</sup> ಐಗುಪ್ತದ ಅರಸನಾದ ಫರೋಹನು ಗೆಜೆರ್‍ಪಟ್ಟಣವನ್ನು ಹಿಡಿದು ಅದಕ್ಕೆ ಬೆಂಕಿಹೊತ್ತಿಸಿ ಅದರಲ್ಲಿದ್ದ ಕಾನಾನ್ಯರನ್ನು ಹತಿಸಿ ಅದನ್ನು ವಶಮಾಡಿಕೊಂಡು ಸೊಲೊಮೋನನ ಹೆಂಡತಿಯಾದ ತನ್ನ ಮಗಳಿಗೆ ಬಳುವಳಿಯಾಗಿ ಕೊಟ್ಟಿದ್ದನು.",
			"17": "<sup>17</sup> ಸೊಲೊಮೋನನು ಗೆಜೆರ್‍ಪಟ್ಟಣದ ಹೊರತಾಗಿ ಕೆಳಗಿನ ಬೇತ್‍ಹೋರೋನ್,",
			"18": "<sup>18</sup> ಬಾಲಾತ್, ಅರಣ್ಯಪ್ರದೇಶದಲ್ಲಿರುವ ತಾಮಾರ್, ತನ್ನ ಎಲ್ಲಾ ಉಗ್ರಾಣ ಪಟ್ಟಣಗಳು,",
			"19": "<sup>19</sup> ಯುದ್ಧರಥಗಳನ್ನಿರಿಸುವ ಪಟ್ಟಣಗಳು, ರಾಹುತರ ಪಟ್ಟಣಗಳು, ಇವುಗಳನ್ನೂ ಕಟ್ಟಿಸಿದನು. ಇದಲ್ಲದೆ ಯೆರೂಸಲೇಮಿನಲ್ಲಿಯೂ ಲೆಬನೋನಿನಲ್ಲಿಯೂ ತನ್ನ ರಾಜ್ಯದ ಎಲ್ಲಾ ಪ್ರಾಂತಗಳಲ್ಲಿಯೂ ಮನಸ್ಸಿಗೆ ಬಂದವುಗಳನ್ನೆಲ್ಲಾ ಕಟ್ಟಿಸಿದನು.",
			"20": "<sup>20</sup> ಇದಲ್ಲದೆ ಅವನು ಇಸ್ರಾಯೇಲ್ಯರಿಂದ ಸಂಹೃತರಾಗದೆ ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲದವರೂ ಕಾನಾನ್‍ದೇಶದಲ್ಲಿ ಉಳಿದ ಅಮೋರಿಯ, ಹಿತ್ತಿಯ,",
			"21": "<sup>21</sup> ಪೆರಿಜ್ಜೀಯ, ಹಿವ್ವೀಯ, ಯೆಬೂಸಿಯ ಮೊದಲಾದ ಅನ್ಯಜನಾಂಗಗಳ ಸಂತಾನದವರೆಲ್ಲರನ್ನು ತನ್ನ ಕೆಲಸಕ್ಕಾಗಿ ಬಿಟ್ಟಿಹಿಡಿದನು. ಇಂದಿನವರೆಗೂ ಅವರು ಬಿಟ್ಟೀ ಕೆಲಸದವರಾಗಿರುತ್ತಾರೆ.",
			"22": "<sup>22</sup> ಆದರೆ ಅವನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಬಿಟ್ಟಿಹಿಡಿಯಲಿಲ್ಲ; ಅವರನ್ನು ಸೈನಿಕರನ್ನಾಗಿಯೂ ಪರಿವಾರದವರನ್ನಾಗಿಯೂ ಅಧಿಪತಿಗಳನ್ನಾಗಿಯೂ ಸರದಾರರನ್ನಾಗಿಯೂ ರಥಾಶ್ವಬಲಗಳ ನಾಯಕರನ್ನಾಗಿಯೂ ನೇಮಿಸಿಕೊಂಡನು.",
			"23": "<sup>23</sup> ಇದಲ್ಲದೆ ಅವನು ಬಿಟ್ಟೀಕೆಲಸ ಮಾಡುವವರ ಮೇಲೆ ಐನೂರೈವತ್ತು ಮಂದಿ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಅಧಿಕಾರಿಗಳನ್ನಾಗಿ ನೇಮಿಸಿದನು.",
			"24": "<sup>24</sup> ಫರೋಹನ ಮಗಳು ದಾವೀದನಗರದಿಂದ ತನಗೋಸ್ಕರ ಕಟ್ಟಿಸಿದ್ದ ಮಂದಿರಕ್ಕೆ ಬಂದಳು;",
			"25": "<sup>25</sup> ಅನಂತರ ಸೊಲೊಮೋನನು ಮಿಲ್ಲೋಕೋಟೆಯನ್ನು ಕಟ್ಟಿಸಿದನು. ಸೊಲೊಮೋನನು ಯೆಹೋವನ ಆಲಯದ ಮುಂದೆ ತಾನು ಯೆಹೋವನಿಗೋಸ್ಕರ ಕಟ್ಟಿಸಿದ ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ವರುಷಕ್ಕೆ ಮೂರು ಸಾರಿ ಸರ್ವಾಂಗಹೋಮ, ಸಮಾಧಾನಯಜ್ಞ ಇವುಗಳನ್ನು ಸಮರ್ಪಿಸಿ ಧೂಪಸುಡುತ್ತಿದ್ದನು. ದೇವಾಲಯವನ್ನು ಕಟ್ಟಿಸಿ ತೀರಿಸಿದನು.",
			"26": "<sup>26</sup> ಇದಲ್ಲದೆ ಅರಸನಾದ ಸೊಲೊಮೋನನು ಎದೋಮ್‍ ದೇಶದಲ್ಲಿ ಏಲೋತಿನ ಹತ್ತಿರ ಕೆಂಪುಸಮುದ್ರದ ತೀರದಲ್ಲಿರುವ ಎಚ್ಯೋನ್ಗೆಬೆರಿನಲ್ಲಿ ಹಡಗುಗಳನ್ನು ಕಟ್ಟಿಸಿದನು.",
			"27": "<sup>27</sup> ಹೀರಾಮನು ಸಮುದ್ರ ಪ್ರಯಾಣದಲ್ಲಿ ನಿಪುಣರಾದ ತನ್ನ ನಾವಿಕರನ್ನು ಸೊಲೊಮೋನನ ಸೇವಕರೊಡನೆ ಆ ಹಡಗುಗಳಲ್ಲಿ ಕಳುಹಿಸಿದನು.",
			"28": "<sup>28</sup> ಅವರು ಓಫೀರಿಗೆ ಪ್ರಯಾಣಮಾಡಿ ಅಲ್ಲಿಂದ ಅರಸನಾದ ಸೊಲೊಮೋನನಿಗೆ ನಾನೂರಿಪ್ಪತ್ತು ತಲಾಂತು ಬಂಗಾರವನ್ನು ತೆಗೆದುಕೊಂಡು ಬಂದರು."
		},
		"10": {
			"1": "<sup>1</sup> ಯೆಹೋವನಾಮ ಮಹತ್ತಿನಿಂದ ಸೊಲೊಮೋನನಿಗುಂಟಾದ ಕೀರ್ತಿಯನ್ನು ಕುರಿತು ಶೆಬದ ರಾಣಿಯು ಕೇಳಿ ಅವನನ್ನು ಒಗಟುಗಳಿಂದ ಪರೀಕ್ಷಿಸುವದಕ್ಕೆ ಬಂದಳು.",
			"2": "<sup>2</sup> ಆಕೆಯು ಸುಗಂಧದ್ರವ್ಯ, ಅಪರಿಮಿತವಾದ ಬಂಗಾರ, ರತ್ನ ಇವುಗಳನ್ನು ಒಂಟೆಗಳ ಮೇಲೆ ಹೇರಿಸಿಕೊಂಡು ಮಹಾ ಪರಿವಾರದೊಡನೆ ಯೆರೂಸಲೇಮಿಗೆ ಬಂದು ತನ್ನ ಮನಸ್ಸಿನಲ್ಲಿ ಗೊತ್ತುಮಾಡಿಕೊಂಡಿದ್ದ ಎಲ್ಲಾ ವಿಷಯಗಳನ್ನು ಕುರಿತು ಸೊಲೊಮೋನನೊಡನೆ ಸಂಭಾಷಿಸಿದಳು.",
			"3": "<sup>3</sup> ಸೊಲೊಮೋನನು ಆಕೆಯ ಎಲ್ಲಾ ಪ್ರಶ್ನೆಗಳಿಗೆ ಉತ್ತರಕೊಟ್ಟನು; ಅರಸನಾದ ಅವನಿಗೆ ತಿಳಿಯದಂಥದು ಅವುಗಳಲ್ಲಿ ಒಂದೂ ಇರಲಿಲ್ಲವಾದದರಿಂದ ಎಲ್ಲವುಗಳಿಗೂ ಉತ್ತರ ಕೊಟ್ಟನು.",
			"4": "<sup>4</sup> ಶೆಬದ ರಾಣಿಯು ಸೊಲೊಮೋನನ ಎಲ್ಲಾ ಜ್ಞಾನ, ಅವನು ಕಟ್ಟಿಸಿದ ಅರಮನೆ,",
			"5": "<sup>5</sup> ಅವನ ಭೋಜನಪೀಠದ ಆಹಾರ, ಆ ಪೀಠದ ಸುತ್ತಲಿರುವ ಉದ್ಯೋಗಸ್ಥರ ಆಸನಗಳು, ಅವನ ಪರಿಚಾರಕರ ಸೇವಾಕ್ರಮ, ಅವರ ಉಡುಪುಗಳು, ಅವನ ಪಾನಕಗಳು ಇವುಗಳನ್ನೂ ಅವನು ಯೆಹೋವನ ಆಲಯದಲ್ಲಿ ಸಮರ್ಪಿಸುತ್ತಿದ್ದ ಸರ್ವಾಂಗಹೋಮಗಳನ್ನೂ ನೋಡಿದಾಗ ವಿಸ್ಮಿತಳಾಗಿ ಅರಸನಿಗೆ -",
			"6": "<sup>6</sup> ನಾನು ನನ್ನ ದೇಶದಲ್ಲಿ ನಿನ್ನ ಜ್ಞಾನವನ್ನೂ ಕೃತ್ಯಗಳನ್ನೂ ಕುರಿತು ಕೇಳಿದ್ದು ಸತ್ಯವಾಗಿದೆ.",
			"7": "<sup>7</sup> ನಾನಾಗಿ ಇಲ್ಲಿಗೆ ಬಂದು ಕಣ್ಣಾರೆ ನೋಡುವದಕ್ಕಿಂತ ಮೊದಲು ಜನರು ಹೇಳಿದ ಸುದ್ದಿಯನ್ನು ನಂಬಲಿಲ್ಲ; ಈಗ ನೋಡಿದರೆ ನಿನ್ನ ಜ್ಞಾನ ವೈಭವಗಳು ನಾನು ಕೇಳಿದ್ದಕ್ಕಿಂತ ಹೆಚ್ಚಾಗಿವೆ; ಜನರು ನನಗೆ ಇದರಲ್ಲಿ ಅರ್ಧವನ್ನಾದರೂ ಹೇಳಲಿಲ್ಲ.",
			"8": "<sup>8</sup> ನಿನ್ನ ಪ್ರಜೆಗಳೂ ಸದಾ ನಿನ್ನ ಮುಂದೆ ನಿಂತುಕೊಂಡು ನಿನ್ನ ಜ್ಞಾನವಾಕ್ಯಗಳನ್ನು ಕೇಳುವ ನಿನ್ನ ಸೇವಕರೂ ಧನ್ಯರು.",
			"9": "<sup>9</sup> ನಿನ್ನನ್ನು ಮೆಚ್ಚಿ ಇಸ್ರಾಯೇಲ್ ಸಿಂಹಾಸನದ ಮೇಲೆ ಕುಳ್ಳಿರಿಸಿದ ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಸ್ತೋತ್ರವಾಗಲಿ; ಆತನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಸದಾ ಪ್ರೀತಿಸುವವನಾಗಿರುವದರಿಂದ ಅವರ ನೀತಿನ್ಯಾಯಗಳನ್ನು ಸ್ಥಾಪಿಸುವದಕ್ಕೋಸ್ಕರ ನಿನ್ನನ್ನೇ ಅರಸನನ್ನಾಗಿ ನೇಮಿಸಿದ್ದಾನೆ ಎಂದು ಹೇಳಿದಳು.",
			"10": "<sup>10</sup> ಆಕೆಯು ಅರಸನಿಗೆ ನೂರಿಪ್ಪತ್ತು ತಲಾಂತು ಬಂಗಾರವನ್ನೂ ಅಪರಿಮಿತ ಸುಗಂಧದ್ರವ್ಯವನ್ನೂ ರತ್ನಗಳನ್ನೂ ಕೊಟ್ಟಳು. ಶೆಬದ ರಾಣಿಯು ಅರಸನಾದ ಸೊಲೊಮೋನನಿಗೆ ಕೊಟ್ಟಷ್ಟು ಸುಗಂಧದ್ರವ್ಯವು ದೇಶಕ್ಕೆ ತಿರಿಗಿ ಬರಲೇ ಇಲ್ಲ.",
			"11": "<sup>11</sup> ಹೀರಾಮನ ಹಡಗುಗಳು ಓಫೀರ್‍ದೇಶದಿಂದ ಬಂಗಾರದ ಹೊರತಾಗಿ ಸುಗಂಧದ ಮರವನ್ನೂ ರತ್ನಗಳನ್ನೂ ರಾಶಿ ರಾಶಿಯಾಗಿ ತಂದವು.",
			"12": "<sup>12</sup> ಅರಸನು ಸುಗಂಧದ ಮರದಿಂದ ದೇವಾಲಯ, ಅರಮನೆ ಇವುಗಳಿಗೆ ಕಟಕಟೆಗಳನ್ನೂ ಗಾಯಕರಿಗೋಸ್ಕರ ಕಿನ್ನರಿಗಳನ್ನೂ ಸ್ವರಮಂಡಲಗಳನ್ನೂ ಮಾಡಿಸಿದನು. ಆಗ ಬಂದಷ್ಟು ಸುಗಂಧದ ಮರವು ಇಂದಿನವರೆಗೂ ಬರಲಿಲ್ಲ, ಮತ್ತು ಕಾಣಲಿಲ್ಲ.",
			"13": "<sup>13</sup> ಅರಸನಾದ ಸೊಲೊಮೋನನು ಶೆಬದ ರಾಣಿಗೆ ತಾನಾಗಿ ರಾಜಮರ್ಯಾದೆಯಿಂದ ಕೊಟ್ಟ ವಸ್ತುಗಳಲ್ಲದೆ ಆಕೆಯು ಕೇಳಿದವುಗಳನ್ನೆಲ್ಲಾ ಕೊಟ್ಟುಬಿಟ್ಟನು. ಅನಂತರ ಆಕೆಯು ತನ್ನ ಪರಿವಾರದವರೊಡನೆ ಸ್ವದೇಶಕ್ಕೆ ಹೊರಟುಹೋದಳು.",
			"14": "<sup>14</sup> ಸೊಲೊಮೋನನಿಗೆ ದೇಶಾಂತರ ವ್ಯಾಪಾರಿಗಳೂ ವರ್ತಕರೂ ಅರಬಿಯ ಅರಸರೂ ಪ್ರದೇಶಾಧಿಪತಿಗಳೂ ತೆರುತ್ತಿದ್ದ ಬಂಗಾರದ ಹೊರತಾಗಿ",
			"15": "<sup>15</sup> ಪ್ರತಿವರುಷ ಆರುನೂರರುವತ್ತಾರು ತಲಾಂತು ಬಂಗಾರವು ದೊರಕುತ್ತಿತ್ತು.",
			"16": "<sup>16</sup> ಅರಸನಾದ ಸೊಲೊಮೋನನು ಬಂಗಾರದ ತಗಡಿನಿಂದ ಇನ್ನೂರು ಗುರಾಣಿಗಳನ್ನು ಮಾಡಿಸಿದನು; ಪ್ರತಿಯೊಂದು ಗುರಾಣಿಗೆ ಆರುನೂರು ತೊಲೆ ಬಂಗಾರ ಹಿಡಿಯಿತು.",
			"17": "<sup>17</sup> ಇದಲ್ಲದೆ ಅವನು ಬಂಗಾರದ ತಗಡಿನಿಂದ ಮುನ್ನೂರು ಖೇಡ್ಯಗಳನ್ನು ಮಾಡಿಸಿದನು. ಪ್ರತಿಯೊಂದು ಖೇಡ್ಯಕ್ಕೆ ನೂರೈವತ್ತು ತೊಲೆ ಬಂಗಾರ ಹಿಡಿಯಿತು. ಅವನು ಇವುಗಳನ್ನು ಲೆಬನೋನಿನ ತೋಪು ಎನಿಸಿಕೊಳ್ಳುವ ಮಂದಿರದಲ್ಲಿ ಇರಿಸಿದನು.",
			"18": "<sup>18</sup> ಇದಲ್ಲದೆ ಅರಸನು ದೊಡ್ಡದಾದ ಒಂದು ದಂತ ಸಿಂಹಾಸನವನ್ನು ಮಾಡಿಸಿ ಅದನ್ನು ಚೊಕ್ಕಬಂಗಾರದ ತಗಡಿನಿಂದ ಹೊದಿಸಿದನು;",
			"19": "<sup>19</sup> ಅದಕ್ಕೆ ಆರು ಮೆಟ್ಲುಗಳಿದ್ದವು. ಅದರ ಹಿಂಭಾಗದ ತುದಿಯು ಚಕ್ರಾಕಾರವಾಗಿತ್ತು. ಆಸನಕ್ಕೆ ಎರಡು ಕೈಗಳೂ ಅವುಗಳ ಹತ್ತಿರ ಎರಡು ಸಿಂಹಗಳೂ ಇದ್ದವು.",
			"20": "<sup>20</sup> ಆರು ಮೆಟ್ಲುಗಳ ಎರಡು ಕಡೆಗಳಲ್ಲೂ ಒಟ್ಟು ಹನ್ನೆರಡು ಸಿಂಹಗಳು ನಿಂತಿದ್ದವು; ಇಂಥ ಸಿಂಹಾಸನವು ಬೇರೆ ಯಾವ ರಾಜ್ಯದಲ್ಲಿಯೂ ಇರಲಿಲ್ಲ.",
			"21": "<sup>21</sup> ಅರಸನಾದ ಸೊಲೊಮೋನನ ಎಲ್ಲಾ ಪಾನಪಾತ್ರೆಗಳು ಬಂಗಾರದವುಗಳು. ಲೆಬನೋನಿನ ತೋಪು ಎನಿಸಿಕೊಳ್ಳುವ ಮಂದಿರದ ಸಾಮಾನುಗಳು ಚೊಕ್ಕ ಬಂಗಾರದವುಗಳು. ಸೊಲೊಮೋನನ ಕಾಲದಲ್ಲಿ ಬೆಳ್ಳಿಗೆ ಬೆಲೆಯಿರಲಿಲ್ಲವಾದದರಿಂದ ಅವನಲ್ಲಿ ಬೆಳ್ಳಿಯ ಸಾಮಾನು ಒಂದಾದರೂ ಕಾಣಿಸಲಿಲ್ಲ.",
			"22": "<sup>22</sup> ಹೀರಾಮನ ನಾವೆಗಳ ಜೊತೆಯಲ್ಲಿ ಸೊಲೊಮೋನನ ತಾರ್ಷೀಷ್ ಹಡಗುಗಳು ಹೋಗಿ ಮೂರು ವರುಷಕ್ಕೊಮ್ಮೆ ಬಂಗಾರ, ಬೆಳ್ಳಿ, ದಂತ, ಕಪಿ, ನವಿಲು ಇವುಗಳನ್ನು ತರುತ್ತಿದ್ದವು.",
			"23": "<sup>23</sup> ಈ ಪ್ರಕಾರ ಅರಸನಾದ ಸೊಲೊಮೋನನು ಐಶ್ವರ್ಯದಲ್ಲಿಯೂ ಜ್ಞಾನದಲ್ಲಿಯೂ ಭೂಲೋಕದ ಎಲ್ಲಾ ಅರಸರಿಗಿಂತ ಮಿಗಿಲಾಗಿದ್ದನು.",
			"24": "<sup>24</sup> ಭೂಲೋಕದವರೆಲ್ಲರೂ ದೇವರು ಅವನಿಗೆ ಅನುಗ್ರಹಿಸಿದ ಜ್ಞಾನವಾಕ್ಯಗಳನ್ನು ಕೇಳುವದಕ್ಕೋಸ್ಕರ ಅವನ ದರ್ಶನಕ್ಕೆ ಬಂದರು.",
			"25": "<sup>25</sup> ಅವರೆಲ್ಲರೂ ಅವನಿಗೆ ವರುಷ ವರುಷ ಬೆಳ್ಳಿಬಂಗಾರದ ಸಾಮಾನು, ಉಡುಪು, ಯುದ್ಧಾಯುಧ, ಸುಗಂದದ್ರವ್ಯ, ಕುದುರೆ, ಹೇಸರಕತ್ತೆ ಇವುಗಳನ್ನು ಕಾಣಿಕೆಯಾಗಿ ಕೊಡುತ್ತಿದ್ದರು.",
			"26": "<sup>26</sup> ಸೊಲೊಮೋನನು ರಥಗಳನ್ನೂ ರಾಹುತರನ್ನೂ ಸಂಗ್ರಹಿಸಿದನು. ಅವನ ರಥಗಳು ಸಾವಿರದ ನಾನೂರು; ರಾಹುತರು ಹನ್ನೆರಡು ಸಾವಿರ. ಇವುಗಳಲ್ಲಿ ಕೆಲವನ್ನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ತನ್ನ ಬಳಿಯಲ್ಲಿಯೂ ಉಳಿದವುಗಳನ್ನು ರಥಗಳಿಗೋಸ್ಕರ ನೇಮಿಸಿದ ಪಟ್ಟಣಗಳಲ್ಲಿಯೂ ಇರಿಸಿದನು.",
			"27": "<sup>27</sup> ಅವನ ಮುಖಾಂತರವಾಗಿ ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಬೆಳ್ಳಿಯು ಕಲ್ಲಿನ ಹಾಗೂ ದೇವದಾರುಮರಗಳು ಇಳಕಲಿನ ಪ್ರದೇಶದಲ್ಲಿ ಬೆಳೆಯುವ ಅತ್ತಿಮರಗಳ ಹಾಗೂ ಹೇರಳವಾದವು.",
			"28": "<sup>28</sup> ಅರಸನಾದ ಸೊಲೊಮೋನನ ಕುದುರೆಗಳು ಐಗುಪ್ತದೇಶದವುಗಳು. ಅವನ ವರ್ತಕರು ಅವುಗಳನ್ನು ಹಿಂಡುಗಟ್ಟಳೆಯಾಗಿ ಕೊಂಡುಕೊಂಡು ಬರುತ್ತಿದ್ದರು.",
			"29": "<sup>29</sup> ಐಗುಪ್ತದೇಶದಿಂದ ರಥಗಳನ್ನೂ ಕುದುರೆಗಳನ್ನೂ ತರಿಸಬೇಕಾದರೆ ಪ್ರತಿಯೊಂದು ರಥಕ್ಕೆ ಆರುನೂರು ರೂಪಾಯಿಗಳನ್ನೂ ಕುದುರೆಗೆ ನೂರೈವತ್ತು ರೂಪಾಯಿಗಳನ್ನೂ ಕೊಡಬೇಕಾಯಿತು. ಅವು ಹಿತ್ತಿಯರ ಮತ್ತು ಅರಾಮ್ಯರ ಎಲ್ಲಾ ಅರಸರಿಗೆ ಇವರ ಮುಖಾಂತರವಾಗಿಯೇ ಸಿಕ್ಕುತ್ತಿದ್ದವು."
		},
		"11": {
			"1": "<sup>1</sup> ಅರಸನಾದ ಸೊಲೊಮೋನನು ಫರೋಹನ ಮಗಳಲ್ಲದೆ ಮೋವಾಬ್ಯರು, ಅಮ್ಮೋನಿಯರು, ಎದೋಮ್ಯರು, ಚೀದೋನ್ಯರು, ಹಿತ್ತಿಯರು ಎಂಬೀ ಜನಾಂಗಗಳ ಸ್ತ್ರೀಯರನ್ನೂ ಮೋಹಿಸಿದನು.",
			"2": "<sup>2</sup> ಯೆಹೋವನು ಈ ಜನಾಂಗಗಳ ವಿಷಯದಲ್ಲಿ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ - ನಿಮಗೂ ಅವರಿಗೂ ಕೊಟ್ಟುಕೊಳ್ಳುವದು ನಡೆಯಬಾರದು; ಅದು ನಡೆಯುವದಾದರೆ ಅವರು ನಿಮ್ಮ ಮನಸ್ಸನ್ನು ತಮ್ಮ ದೇವತೆಗಳ ಕಡೆಗೆ ತಿರುಗಿಸಾರು ಎಂದು ಆಜ್ಞಾಪಿಸಿದ್ದರೂ ಸೊಲೊಮೋನನು ಅವರ ಸ್ತ್ರೀಯರಲ್ಲಿ ಅನುರಕ್ತನಾಗಿದ್ದನು.",
			"3": "<sup>3</sup> ಅವನಿಗೆ ರಾಜವಂಶದವರಾದ ಏಳುನೂರು ಮಂದಿ ಪತ್ನಿಯರಲ್ಲದೆ ಮುನ್ನೂರು ಮಂದಿ ಉಪಪತ್ನಿಯರಿದ್ದರು. ಈ ಸ್ತ್ರೀಯರು ಅವನ ಮನಸ್ಸನ್ನು ಕೆಡಿಸಿಬಿಟ್ಟರು.",
			"4": "<sup>4</sup> ಅವನು ವೃದ್ಧನಾದಾಗ ಇವರು ಅವನ ಹೃದಯವನ್ನು ಅನ್ಯದೇವತೆಗಳ ಕಡೆಗೆ ತಿರುಗಿಸಿದರು. ಈ ಕಾರಣದಿಂದ ಅವನು ತನ್ನ ದೇವರಾದ ಯೆಹೋವನಲ್ಲಿಟ್ಟಿದ್ದ ಯಥಾರ್ಥಭಕ್ತಿಯನ್ನು ಕಳೆದುಕೊಂಡನು. ತನ್ನ ತಂದೆಯಾದ ದಾವೀದನಂತೆ ನಡಿಯಲಿಲ್ಲ.",
			"5": "<sup>5</sup> ಅವನು ಚೀದೋನ್ಯರ ಅಷ್ಟೋರೆತ್ ದೇವತೆಯನ್ನೂ ಅಮ್ಮೋನಿಯರ ಮಿಲ್ಕೋಮ್ ಎಂಬ ಅಸಹ್ಯ ವಿಗ್ರಹವನ್ನೂ ಆರಾಧಿಸತೊಡಗಿ",
			"6": "<sup>6</sup> ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾದನು; ಅವನ ತಂದೆಯಾದ ದಾವೀದನು ಪೂರ್ಣಮನಸ್ಸಿನಿಂದ ಯೆಹೋವನನ್ನು ಸೇವಿಸಿದಂತೆ ಅವನು ಸೇವಿಸಲಿಲ್ಲ.",
			"7": "<sup>7</sup> ಅವನು ಯೆರೂಸಲೇಮಿನ ಮೂಡಲಲ್ಲಿರುವ ಗುಡ್ಡದಲ್ಲಿ ಮೋವಾಬ್ಯರ ಕೆಮೋಷ್ ಎಂಬ ಅಸಹ್ಯ ವಿಗ್ರಹಕ್ಕೂ ಅಮ್ಮೋನಿಯರ ಮೋಲೆಕ್ ಎಂಬ ಅಸಹ್ಯ ವಿಗ್ರಹಕ್ಕೂ ಪೂಜಾಸ್ಥಳಗಳನ್ನು ಏರ್ಪಡಿಸಿದನು.",
			"8": "<sup>8</sup> ಈ ಪ್ರಕಾರವಾಗಿ ಅನ್ಯಜನಾಂಗಗಳಿಂದ ಬಂದ ತನ್ನ ಹೆಂಡತಿಯರು ತಮ್ಮ ದೇವತೆಗಳಿಗೆ ಧೂಪಸುಡುವದಕ್ಕೂ ಯಜ್ಞವರ್ಪಿಸುವದಕ್ಕೂ ಅನುಕೂಲಮಾಡಿಕೊಟ್ಟನು.",
			"9": "<sup>9</sup> ಇಸ್ರಾಯೇಲ್‍ದೇವರಾದ ಯೆಹೋವನು ಸೊಲೊಮೋನನಿಗೆ ಎರಡು ಸಾರಿ ಕಾಣಿಸಿಕೊಂಡು",
			"10": "<sup>10</sup> ಅನ್ಯದೇವತೆಗಳನ್ನು ಸೇವಿಸಲೇ ಬಾರದೆಂದು ಆಜ್ಞಾಪಿಸಿದ್ದರೂ ಅವನು ಆತನ ಆಜ್ಞೆಗಳನ್ನು ಮೀರಿದ್ದರಿಂದ ಆತನು ಅವನ ಮೇಲೆ ಕೋಪಗೊಂಡು",
			"11": "<sup>11</sup> ಅವನಿಗೆ - ನೀನು ಈ ಪ್ರಕಾರ ಮಾಡಿ ನನ್ನ ವಿಧಿನಿಬಂಧನೆಗಳನ್ನು ಮೀರಿದ್ದರಿಂದ ನಿನ್ನ ರಾಜ್ಯವನ್ನು ನಿನ್ನಿಂದ ಕಿತ್ತುಕೊಂಡು ನಿನ್ನ ಸೇವಕನಿಗೆ ಕೊಡುವೆನು.",
			"12": "<sup>12</sup> ಆದರೂ ನಾನು ನಿನ್ನ ತಂದೆಯಾದ ದಾವೀದನನ್ನು ನೆನಸಿ ನಿನ್ನ ಕಾಲದಲ್ಲಿ ಇದನ್ನು ಮಾಡುವದಿಲ್ಲ.",
			"13": "<sup>13</sup> ನಿನ್ನ ಮಗನ ಕೈಯಿಂದ ಅದನ್ನು ಕಿತ್ತುಕೊಳ್ಳುವೆನು. ಆದರೆ ರಾಜ್ಯವನ್ನೆಲ್ಲಾ ಕಿತ್ತು ಕೊಳ್ಳುವದಿಲ್ಲ; ನನ್ನ ಸೇವಕನಾದ ದಾವೀದನಿಗೋಸ್ಕರವೂ ನಾನು ಆರಿಸಿಕೊಂಡ ಯೆರೂಸಲೇಮ್‍ಪಟ್ಟಣಕ್ಕೋಸ್ಕರವೂ ನಿನ್ನ ಮಗನಿಗೆ ಒಂದು ಕುಲವನ್ನು ಉಳಿಸುವೆನು ಎಂದು ಹೇಳಿದನು.",
			"14": "<sup>14</sup> ಯೆಹೋವನು ಎದೋಮ್ಯನೂ ರಾಜಪುತ್ರನೂ ಆದ ಹದದನನ್ನು ಸೊಲೊಮೋನನಿಗೆ ವಿರೋಧವಾಗಿ ಏಳಮಾಡಿದನು.",
			"15": "<sup>15</sup> ದಾವೀದನಿಗೂ ಎದೋಮ್ಯರಿಗೂ ಯುದ್ಧನಡಿಯುತ್ತಿರುವಾಗ ಸೇನಾಪತಿಯಾದ ಯೋವಾಬನು ಇಸ್ರಾಯೇಲ್ ಸರ್ವಸೈನ್ಯದೊಡನೆ ಅಲ್ಲಿಗೆ ಹೋಗಿ ಹತರಾದ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಹೂಣಿಟ್ಟು",
			"16": "<sup>16</sup> ಆರು ತಿಂಗಳು ಅಲ್ಲಿಯೇ ಇದ್ದು ಎದೋಮ್ಯರ ಎಲ್ಲಾ ಗಂಡಸರನ್ನು ಸಂಹರಿಸಿದನು.",
			"17": "<sup>17</sup> ಆ ಸಮಯದಲ್ಲಿ ಬಾಲಕನಾದ ಹದದನು ತನ್ನ ತಂದೆಯ ಸೇವಕರಾದ ಕೆಲವು ಮಂದಿ ಎದೋಮ್ಯರ ಜೊತೆಯಲ್ಲಿ ಐಗುಪ್ತಕ್ಕೆ ಓಡಿಹೋಗಬೇಕೆಂದು ಹೊರಟನು.",
			"18": "<sup>18</sup> ಅವರು ಮೊದಲು ಮಿದ್ಯಾನಿಗೂ ಆಮೇಲೆ ಪಾರಾನಿಗೂ ಬಂದರು. ಅಲ್ಲಿಂದ ಕೆಲವು ಮಂದಿ ಪಾರಾನಿನ ಜನರನ್ನು ತಮ್ಮ ಜೊತೆಯಲ್ಲಿ ಕರಕೊಂಡು ಐಗುಪ್ತದ ಅರಸನಾದ ಫರೋಹನ ಬಳಿಗೆ ಬಂದರು. ಫರೋಹನು ಹದದನಿಗೆ ಅಶನವನ್ನು ನೇಮಿಸಿ ಮನೆಯನ್ನೂ ಭೂಮಿಯನ್ನೂ ಕೊಟ್ಟನು.",
			"19": "<sup>19</sup> ಹದದನು ಫರೋಹನ ವಿಶೇಷ ದಯೆಗೆ ಪಾತ್ರನಾದದರಿಂದ ಫರೋಹನು ತನ್ನ ಹೆಂಡತಿಯೂ ರಾಣಿಯೂ ಆದ ತಖ್ಪೆನೇಸ್ ಎಂಬಾಕೆಯ ತಂಗಿಯನ್ನು ಇವನಿಗೆ ಮದುವೆಮಾಡಿ ಕೊಟ್ಟನು.",
			"20": "<sup>20</sup> ಆಕೆಯಲ್ಲಿ ಇವನಿಗೆ ಗೆನುಬತ್ ಎಂಬ ಮಗನು ಹುಟ್ಟಿದನು. ತಖ್ಬೆನೇಸಳು ಈ ಹುಡುಗನನ್ನು ಫರೋಹನ ಮನೆಯಲ್ಲೇ ಸಾಕಿದಳು; ಅಲ್ಲಿ ಇವನು ಫರೋಹನ ಕುಮಾರರ ಜೊತೆಯಲ್ಲಿದ್ದನು.",
			"21": "<sup>21</sup> ದಾವೀದನು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಿದನೆಂದೂ ಸೇನಾಪತಿಯಾದ ಯೋವಾಬನು ಮೃತನಾದನೆಂದೂ ಐಗುಪ್ತದಲ್ಲಿದ್ದ ಹದದನು ಕೇಳಿ ಫರೋಹನಿಗೆ - ನಾನು ಸ್ವದೇಶಕ್ಕೆ ಹೋಗಬೇಕೆಂದಿರುತ್ತೇನೆ; ನನಗೆ ಅಪ್ಪಣೆಕೊಡು ಅಂದನು.",
			"22": "<sup>22</sup> ಫರೋಹನು ಅವನನ್ನು - ನನ್ನ ಬಳಿಯಲ್ಲಿ ನಿನಗೇನು ಕೊರತೆಯಾಯಿತು? ಸ್ವದೇಶಕ್ಕೆ ಹೋಗಬೇಕೆಂದು ಯಾಕೆ ಆಶಿಸುತ್ತೀ ಎಂದು ಕೇಳಲು ಅವನು - ಏನೂ ಕೊರತೆಯಾಗಿರುವದಿಲ್ಲ; ಆದರೂ ನನಗೆ ಹೋಗುವದಕ್ಕೆ ಅಪ್ಪಣೆಯಾಗಬೇಕು ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"23": "<sup>23</sup> ದೇವರು ಸೊಲೊಮೋನನಿಗೆ ವಿರೋಧವಾಗಿ ಎಬ್ಬಿಸಿದ ಎರಡನೆಯ ವೈರಿಯು ಎಲ್ಯಾದಾವನ ಮಗನಾದ ರೆಜೋನ್ ಎಂಬವನು.",
			"24": "<sup>24</sup> ದಾವೀದನು ಚೋಬದವರನ್ನು ಸಂಹರಿಸಿದಾಗ ಇವನು ಚೋಬದ ಅರಸನೂ ತನ್ನ ಒಡೆಯನೂ ಆದ ಹದದೆಜೆರನನ್ನು ಬಿಟ್ಟು ಓಡಿಹೋಗಿ ಕೆಲವು ಮಂದಿಯನ್ನು ಕೂಡಿಸಿಕೊಂಡು ತಾನು ಅವರ ನಾಯಕನಾಗಿ ಅವರೊಡನೆ ದಮಸ್ಕಕ್ಕೆ ಬಂದು ಅಲ್ಲಿ ಅರಸನಾದನು.",
			"25": "<sup>25</sup> ಸೊಲೊಮೋನನ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಮಹಾಬಾಧಕನಾದ ಹದದನಲ್ಲದೆ ಇವನೂ ವೈರಿಯಾಗಿದ್ದನು. ಇವನು ಅರಾಮ್‍ದೇಶದಲ್ಲಿ ಆಳುವವನಾಗಿ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಬಹಳವಾಗಿ ದ್ವೇಷಿಸಿದನು.",
			"26": "<sup>26</sup> ಅರಸನಾದ ಸೊಲೊಮೋನನಿಗೆ ವಿರೋಧವಾಗಿ ಎದ್ದವರಲ್ಲಿ ಅವನ ಸೇವಕನೂ ಚರೇದ ಊರಿನ ಎಫ್ರಾಯೀಮ್ಯನಾದ ನೆಬಾಟನ ಮಗನೂ ಆಗಿದ್ದ ಯಾರೊಬ್ಬಾಮನೆಂಬವನು ಮತ್ತೊಬ್ಬನು; ಚೆರೂಗಳೆಂಬ ವಿಧವೆಯು ಅವನ ತಾಯಿ.",
			"27": "<sup>27</sup> ಅವನು ತಿರುಗಿ ಬಿದ್ದದ್ದು ಹೇಗಂದರೆ - ಸೊಲೊಮೋನನು ಮಿಲ್ಲೋ ಕೋಟೆಯನ್ನು ಕಟ್ಟಿಸಿ ತನ್ನ ತಂದೆಯಾದ ದಾವೀದನ ಪಟ್ಟಣದ ದುರ್ಬಲಸ್ಥಳವನ್ನು ಭದ್ರಪಡಿಸುತ್ತಿರುವಾಗ",
			"28": "<sup>28</sup> ಯೌವನಸ್ಥನಾದ ಯಾರೊಬ್ಬಾಮನು ಬಹುಸಾಮರ್ಥ್ಯದಿಂದ ಕೆಲಸಮಾಡುತ್ತಿರುವದನ್ನು ಕಂಡು ಅವನನ್ನು ಯೋಸೇಫ್ಯರ ಎಲ್ಲಾ ಆಳುಗಳ ಮೇಲೆ ಮೇಸ್ತ್ರಿಯನ್ನಾಗಿ ನೇಮಿಸಿದನು.",
			"29": "<sup>29</sup> ಒಂದು ದಿವಸ ಯಾರೊಬ್ಬಾಮನು ಯೆರೂಸಲೇಮಿನಿಂದ ಎಲ್ಲಿಗೋ ಹೋಗುತ್ತಿರುವಾಗ ಅಡವಿಯಲ್ಲಿ ಶೀಲೋವಿನವನಾದ ಅಹೀಯನೆಂಬ ಪ್ರವಾದಿಯು ಅವನಿಗೆ ಎದುರಾದನು. ಇವನು ಹೊಸ ಬಟ್ಟೆಯನ್ನು ಹೊದ್ದುಕೊಂಡಿದ್ದನು. ಅಲ್ಲಿ ಇವರಿಬ್ಬರ ಹೊರತಾಗಿ ಬೇರೆ ಯಾರೂ ಇರಲಿಲ್ಲ.",
			"30": "<sup>30</sup> ಆಗ ಅಹೀಯನು ತಾನು ಹೊದ್ದುಕೊಂಡಿದ್ದ ಹೊಸ ಬಟ್ಟೆಯನ್ನು ತೆಗೆದುಕೊಂಡು ಅದನ್ನು ಹರಿದು ಹನ್ನೆರಡು ತುಂಡುಮಾಡಿ ಯಾರೊಬ್ಬಾಮನಿಗೆ -",
			"31": "<sup>31</sup> ನೀನು ಇವುಗಳಲ್ಲಿ ಹತ್ತು ತುಂಡುಗಳನ್ನು ತೆಗೆದುಕೋ, ಇಸ್ರಾಯೇಲ್‍ದೇವರಾದ ಯೆಹೋವನು ರಾಜ್ಯವನ್ನು ಸೊಲೊಮೋನನಿಂದ ಕಿತ್ತುಕೊಂಡು",
			"32": "<sup>32</sup> ತನ್ನ ಸೇವಕನಾದ ದಾವೀದನ ನಿಮಿತ್ತವಾಗಿಯೂ ತಾನು ಇಸ್ರಾಯೇಲ್ಯರ ಎಲ್ಲಾ ಕುಲಗಳಿಂದ ಆರಿಸಿಕೊಂಡ ಯೆರೂಸಲೇಮ್‍ಪಟ್ಟಣದ ನಿಮಿತ್ತವಾಗಿಯೂ ಅವನಿಗೆ ಒಂದೇ ಕುಲವನ್ನು ಉಳಿಸಿ ಬೇರೆ ಹತ್ತು ಕುಲಗಳನ್ನು ನಿನಗೆ ಕೊಡುತ್ತೇನೆಂದು ಹೇಳಿದ್ದಾನೆ.",
			"33": "<sup>33</sup> ಇದಲ್ಲದೆ ಆತನು ನಿನಗೆ - ಸೊಲೊಮೋನನೂ ಅವನ ಮನೆಯವರೂ ನನ್ನನ್ನು ಬಿಟ್ಟು ಚೀದೋನ್ಯರ ದೇವತೆಯಾದ ಅಷ್ಟೋರೆತ್, ಮೋವಾಬ್ಯರ ದೇವತೆಯಾದ ಕೆಮೋಷ್, ಅಮ್ಮೋನಿಯರ ದೇವತೆಯಾದ ಮಿಲ್ಕೋಮ್ ಇವುಗಳನ್ನು ಆರಾಧಿಸಿದ್ದೂ ಅವರ ತಂದೆಯಾದ ದಾವೀದನು ನನ್ನ ನೇಮವಿಧಿಗಳನ್ನು ಕೈಕೊಂಡು ನನ್ನನ್ನು ಮೆಚ್ಚಿಸಿ ನಡೆದಂತೆ ಅವರು ನಡಿಯದೆ ಹೋದದ್ದೂ ಇದಕ್ಕೆ ಕಾರಣ.",
			"34": "<sup>34</sup> ಆದರೂ ನಾನು ಸೊಲೊಮೋನನ ಕೈಯಿಂದ ರಾಜ್ಯವನ್ನೆಲ್ಲಾ ಕಿತ್ತುಕೊಳ್ಳುವದಿಲ್ಲ; ನನ್ನ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ಕೈಕೊಂಡವನೂ ನಾನು ಆರಿಸಿಕೊಂಡ ಸೇವಕನೂ ಆದ ದಾವೀದನ ನಿಮಿತ್ತವಾಗಿ ಅವನನ್ನು ಅವನ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ಅರಸನನ್ನಾಗಿಯೇ ಇರಿಸುವೆನು.",
			"35": "<sup>35</sup> ಆದರೆ ಅವನ ಮಗನ ಕೈಯಿಂದ ಅದನ್ನು ಕಿತ್ತುಕೊಂಡು ನಿನಗೆ ಹತ್ತು ಕುಲಗಳನ್ನು ಕೊಡುವೆನು.",
			"36": "<sup>36</sup> ನಾನು ನನ್ನ ಹೆಸರಿಗೋಸ್ಕರ ಆರಿಸಿಕೊಂಡ ಯೆರೂಸಲೇಮ್‍ಪಟ್ಟಣದಲ್ಲಿ ನನ್ನ ಸೇವಕನಾದ ದಾವೀದನ ದೀಪವು ನನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಉರಿಯುತ್ತಲೇ ಇರುವಂತೆ ಅವನ ಮಗನಿಗೆ ಒಂದು ಕುಲವನ್ನು ಉಳಿಸುವೆನು.",
			"37": "<sup>37</sup> ನಾನು ನಿನ್ನನ್ನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನನ್ನಾಗಿ ನೇಮಿಸುವೆನು; ನೀನು ಅವರನ್ನು ನಿನ್ನ ಇಷ್ಟದ ಪ್ರಕಾರ ಆಳಬಹುದು.",
			"38": "<sup>38</sup> ನನ್ನ ಸೇವಕನಾದ ದಾವೀದನು ನನ್ನ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ಕೈಕೊಂಡಂತೆ ನೀನೂ ನನ್ನ ಆಜ್ಞೆಗಳನ್ನು ಕೈಕೊಂಡು ನನ್ನ ಮಾರ್ಗದಲ್ಲಿ ನಡೆದು ನನ್ನನ್ನು ಮೆಚ್ಚಿಸುವದಾದರೆ ನಾನು ನಿನ್ನ ಸಂಗಡ ಇದ್ದು ದಾವೀದನ ಸಂತಾನದಂತೆ ನಿನ್ನ ಸಂತಾನವನ್ನೂ ಸ್ಥಿರಪಡಿಸುವೆನು; ಇಸ್ರಾಯೇಲ್‍ರಾಜ್ಯವು ನಿನ್ನ ಪಾಲಾಗುವದು.",
			"39": "<sup>39</sup> ದಾವೀದನ ಸಂತಾನದವರನ್ನು ಅವರ ಪಾಪದ ದೆಸೆಯಿಂದ ಕುಗ್ಗಿಸುವೆನು; ಆದರೆ ಸದಾಕಾಲವು ಅಲ್ಲ ಎಂದು ಹೇಳಿದ್ದಾನೆ ಅಂದನು.",
			"40": "<sup>40</sup> ಸೊಲೊಮೋನನು ಯಾರೊಬ್ಬಾಮನನ್ನು ಕೊಲ್ಲುವದಕ್ಕೆ ಪ್ರಯತ್ನಿಸಿದಾಗ ಅವನು ತಪ್ಪಿಸಿಕೊಂಡು ಐಗುಪ್ತದ ಅರಸನಾದ ಶೀಶಕನ ಬಳಿಗೆ ಹೋಗಿ ಸೊಲೊಮೋನನು ಜೀವದಿಂದಿರುವವರೆಗೆ ಅಲ್ಲಿಯೇ ಇದ್ದನು.",
			"41": "<sup>41</sup> ಸೊಲೊಮೋನನ ಉಳಿದ ಎಲ್ಲಾ ಕೃತ್ಯಗಳನ್ನೂ ಅವನ ಜ್ಞಾನವನ್ನೂ ಕುರಿತು ಸೊಲೊಮೋನನ ಚರಿತ್ರ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತದೆ.",
			"42": "<sup>42</sup> ಅವನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿದ್ದುಕೊಂಡು ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ನಾಲ್ವತ್ತು ವರುಷ ಆಳಿದನಂತರ",
			"43": "<sup>43</sup> ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಿದನು; ಅವನ ಶವವನ್ನು ತಂದೆಯಾದ ದಾವೀದನ ಪಟ್ಟಣದಲ್ಲಿ ಸಮಾಧಿ ಮಾಡಿದರು. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ರೆಹಬ್ಬಾಮನು ಅರಸನಾದನು."
		},
		"12": {
			"1": "<sup>1</sup> ರೆಹಬ್ಬಾಮನು ಶೆಕೆಮಿಗೆ ಹೋದನು; ಅಲ್ಲಿ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರೂ ಅವನನ್ನು ಅರಸನನ್ನಾಗಿ ಮಾಡುವದಕ್ಕೆ ಕೂಡಿ ಬಂದಿದ್ದರು.",
			"2": "<sup>2</sup> ಇದಲ್ಲದೆ ಅರಸನಾದ ಸೊಲೊಮೋನನಿಗೆ ತಪ್ಪಿಸಿಕೊಂಡು ಐಗುಪ್ತಕ್ಕೆ ಓಡಿಹೋಗಿದ್ದ ನೆಬಾಟನ ಮಗನಾದ ಯಾರೊಬ್ಬಾಮನನ್ನು ಕರಿಸಿಕೊಂಡಿದ್ದರು.",
			"3": "<sup>3</sup> ಯಾರೊಬ್ಬಾಮನು ನಡೆದ ಸಂಗತಿಗಳ ವರ್ತಮಾನವನ್ನು ಕೇಳಿ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರೊಡನೆ ರೆಹಬ್ಬಾಮನ ಬಳಿಗೆ ಹೋಗಿ ಅವನಿಗೆ -",
			"4": "<sup>4</sup> ನಿನ್ನ ತಂದೆಯು ನಮ್ಮ ಮೇಲೆ ಭಾರವಾದ ನೊಗವನ್ನು ಹೇರಿದನು; ನಿನ್ನ ತಂದೆಯು ನೇಮಿಸಿದ ಬಿಟ್ಟೀಕೆಲಸವನ್ನು ನೀನು ಕಡಿಮೆಮಾಡಿ ನಮ್ಮ ಮೇಲಿರುವ ಭಾರವಾದ ನೊಗವನ್ನು ಹಗುರ ಮಾಡುವದಾದರೆ ನಾವು ನಿನ್ನನ್ನು ಸೇವಿಸುವೆವು ಎಂದು ಹೇಳಲು ಅವನು -",
			"5": "<sup>5</sup> ನೀವು ಈಗ ಹೋಗಿ ಮೂರು ದಿನಗಳಾದ ನಂತರ ತಿರಿಗಿ ಬನ್ನಿರಿ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು; ಜನರು ಹೋದರು.",
			"6": "<sup>6</sup> ಆಗ ಅರಸನಾದ ರೆಹಬ್ಬಾಮನು ತನ್ನ ತಂದೆಯಾದ ಸೊಲೊಮೋನನ ಕಾಲದಲ್ಲಿ ಮಂತ್ರಿಗಳಾಗಿದ್ದ ಹಿರಿಯರನ್ನು ಕೂಡಿಸಿ ತಾನು ಜನರಿಗೆ ಕೊಡತಕ್ಕ ಉತ್ತರದ ವಿಷಯದಲ್ಲಿ ಅವರ ಆಲೋಚನೆಯನ್ನು ಕೇಳಿದನು.",
			"7": "<sup>7</sup> ಅವರು ಅವನಿಗೆ - ನೀನು ಈ ಹೊತ್ತು ಈ ಜನರ ಮಾತನ್ನು ಕೇಳಿ ಅವರಿಗೆ ಒಳಗಾಗಿ ಒಳ್ಳೇ ಉತ್ತರಕೊಡುವದಾದರೆ ಅವರು ಯಾವಾಗಲೂ ನಿನಗೆ ಒಳಗಾಗಿರುವರು ಅಂದರು.",
			"8": "<sup>8</sup> ಆದರೆ ಅವನು ಹಿರಿಯರ ಆಲೋಚನೆಯನ್ನು ನಿರಾಕರಿಸಿ ತನ್ನ ಸಂಗಡ ಬೆಳೆದು ತನ್ನ ಮಂತ್ರಿಗಳಾದ ಯೌವನಸ್ಥರನ್ನು ಕರಿಸಿ -",
			"9": "<sup>9</sup> ನನ್ನ ತಂದೆಯು ಹೇರಿದ ನೊಗವನ್ನು ಹಗುರ ಮಾಡಬೇಕೆಂದು ಬೇಡಿಕೊಳ್ಳುವ ಈ ಜನರಿಗೆ ಯಾವ ಉತ್ತರವನ್ನು ಕೊಡತಕ್ಕದ್ದು? ಈ ವಿಷಯದಲ್ಲಿ ನಿಮ್ಮ ಆಲೋಚನೆ ಏನು ಎಂದು ಕೇಳಲು",
			"10": "<sup>10</sup> ಅವರು ಅವನಿಗೆ - ನಿನ್ನ ತಂದೆಯು ನಮ್ಮ ಮೇಲೆ ಹೇರಿರುವ ನೊಗವನ್ನು ಹಗುರಮಾಡು ಎಂಬದಾಗಿ ನಿನ್ನನ್ನು ಬೇಡಿಕೊಂಡ ಜನರಿಗೆ ನೀನು - ನನ್ನ ತಂದೆಯ ನಡುವಿಗಿಂತ ನನ್ನ ಕಿರಿಬೆರಳು ದಪ್ಪವಾಗಿದೆ.",
			"11": "<sup>11</sup> ನನ್ನ ತಂದೆಯು ನಿಮ್ಮ ಮೇಲೆ ಭಾರವಾದ ನೊಗವನ್ನು ಹಾಕಿರುವದು ನಿಜ; ಆದರೆ ನಾನು ಅದಕ್ಕೆ ಇನ್ನೂ ಹೆಚ್ಚಿನ ಭಾರವನ್ನು ಕೂಡಿಸುತ್ತೇನೆ; ಅವನು ನಿಮ್ಮನ್ನು ಬಾರುಕೋಲುಗಳಿಂದ ಹೊಡೆದದ್ದು ನಿಜ; ನಾನಾದರೋ ನಿಮ್ಮನ್ನು ಮುಳ್ಳು ಕೊರಡೆಗಳಿಂದ ದಂಡಿಸುವೆನು ಎಂಬದಾಗಿ ಹೇಳಬೇಕು ಎಂದು ಉತ್ತರಕೊಟ್ಟರು.",
			"12": "<sup>12</sup> ಯಾರೊಬ್ಬಾಮನೂ ಎಲ್ಲಾ ಜನರೂ ಅರಸನಾದ ರೆಹಬ್ಬಾಮನ ಅಪ್ಪಣೆಯಂತೆ ಮೂರನೆಯ ದಿನದಲ್ಲಿ ತಿರಿಗಿ ಅವನ ಬಳಿಗೆ ಬಂದರು.",
			"13": "<sup>13</sup> ಅರಸನು ಅವರಿಗೆ ಕಠಿಣವಾದ ಉತ್ತರವನ್ನು ಕೊಟ್ಟನು; ಅವನು ಹಿರಿಯರ ಆಲೋಚನೆಯನ್ನು ನಿರಾಕರಿಸಿ",
			"14": "<sup>14</sup> ಯೌವನಸ್ಥರ ಆಲೋಚನೆಗನುಸಾರವಾಗಿ ಜನರಿಗೆ - ನನ್ನ ತಂದೆಯು ನಿಮ್ಮ ಮೇಲೆ ಭಾರವಾದ ನೊಗವನ್ನು ಹೇರಿರುವದು ನಿಜ; ಆದರೆ ನಾನು ಅದಕ್ಕೆ ಇನ್ನೂ ಹೆಚ್ಚಿನ ಭಾರವನ್ನು ಕೂಡಿಸುವೆನು; ಅವನು ನಿಮ್ಮನ್ನು ಬಾರುಕೋಲುಗಳಿಂದ ಹೊಡೆದದ್ದು ನಿಜ; ನಾನಾದರೋ ಮುಳ್ಳುಕೊರಡೆಗಳಿಂದ ದಂಡಿಸುವೆನು ಎಂದು ನುಡಿದನು.",
			"15": "<sup>15</sup> ಅವನು ಜನರ ಮಾತನ್ನು ಕೇಳದೆಹೋದದ್ದು ಯೆಹೋವನಿಂದಲೇ. ಈ ಪ್ರಕಾರ ಯೆಹೋವನು ಶೀಲೋವಿನವನಾದ ಅಹೀಯನ ಮುಖಾಂತರವಾಗಿ ನೆಬಾಟನ ಮಗನಾದ ಯಾರೊಬ್ಬಾಮನಿಗೆ ಹೇಳಿಸಿದ ಮಾತು ನೆರವೇರಿತು.",
			"16": "<sup>16</sup> ಅರಸನು ತಮ್ಮ ಮಾತನ್ನು ಲಕ್ಷಿಸಲಿಲ್ಲವೆಂದು ನೋಡಿ ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರೂ ಅವನಿಗೆ - ದಾವೀದನಲ್ಲಿ ನಮಗೇನು ಪಾಲು? ಇಷಯನ ಮಗನಲ್ಲಿ ನಮಗೇನು ಬಾಧ್ಯತೆ? ಇಸ್ರಾಯೇಲ್ಯರೇ, ನಿಮ್ಮ ನಿಮ್ಮ ನಿವಾಸಗಳಿಗೆ ಹೋಗಿರಿ; ದಾವೀದನವರೇ, ನಿಮ್ಮ ಕುಲವನ್ನು ನೀವೇ ನೋಡಿಕೊಳ್ಳಿರಿ ಎಂದು ಹೇಳಿ ತಮ್ಮ ತಮ್ಮ ನಿವಾಸಗಳಿಗೆ ಹೋದರು.",
			"17": "<sup>17</sup> ಯೆಹೂದ ಪ್ರಾಂತದಲ್ಲಿದ್ದ ಇಸ್ರಾಯೇಲ್ಯರಾದರೋ ರೆಹಬ್ಬಾಮನ ಅಧೀನದಲ್ಲಿದ್ದರು.",
			"18": "<sup>18</sup> ರೆಹಬ್ಬಾಮನು ಬಿಟ್ಟೀಕೆಲಸದವರ ಮೇಲ್ವಿಚಾರಕನಾದ ಅದೋರಾಮನನ್ನು ಇಸ್ರಾಯೇಲ್ಯರ ಬಳಿಗೆ ಕಳುಹಿಸಲು ಅವರು ಇವನನ್ನು ಕಲ್ಲೆಸೆದು ಕೊಂದರು. ಅರಸನಾದ ರೆಹಬ್ಬಾಮನು ಬೇಗನೇ ರಥವನ್ನೇರಿ ಯೆರೂಸಲೇಮಿಗೆ ಓಡಿ ಹೋದನು.",
			"19": "<sup>19</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಅಂದಿನಿಂದ ಇಂದಿನವರೆಗೂ ದಾವೀದನ ಕುಟುಂಬದವರೊಡನೆ ಸಮಾಧಾನವಾಗಲೇ ಇಲ್ಲ.",
			"20": "<sup>20</sup> ಯಾರೊಬ್ಬಾಮನು ಹಿಂದಿರುಗಿ ಬಂದಿದ್ದಾನೆಂಬ ವರ್ತಮಾನವು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಗೊತ್ತಾದಾಗ ಅವರೆಲ್ಲರೂ ಅವನನ್ನು ನೆರೆದ ಸಭೆಯ ಮುಂದೆ ಕರಿಸಿ ತಮ್ಮ ಅರಸನನ್ನಾಗಿ ನೇಮಿಸಿಕೊಂಡರು. ದಾವೀದನ ಕುಟುಂಬದವರನ್ನು ಯೆಹೂದ ಕುಲವೇ ಹೊರತು ಬೇರೆ ಯಾವ ಕುಲವೂ ಹಿಂಬಾಲಿಸಲಿಲ್ಲ.",
			"21": "<sup>21</sup> ಸೊಲೊಮೋನನ ಮಗನಾದ ರೆಹಬ್ಬಾಮನು ಯೆರೂಸಲೇಮನ್ನು ಮುಟ್ಟಿದನಂತರ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ವಿರೋಧವಾಗಿ ಯುದ್ಧಮಾಡುವದಕ್ಕೂ ರಾಜ್ಯವನ್ನು ತಿರಿಗಿ ತನಗೆ ವಶಪಡಿಸಿಕೊಳ್ಳುವದಕ್ಕೂ ಯೆಹೂದ ಬೆನ್ಯಾಮೀನ್‍ಕುಲಗಳಿಂದ ಲಕ್ಷದ ಎಂಭತ್ತುಸಾವಿರ ಮಂದಿ ಶ್ರೇಷ್ಠ ಸೈನಿಕರನ್ನು ಕೂಡಿಸಿದನು.",
			"22": "<sup>22</sup> ಆಗ ದೇವರ ಮನುಷ್ಯನಾದ ಶೆಮಾಯನಿಗೆ -",
			"23": "<sup>23</sup> ನೀನು ಹೋಗಿ ಸೊಲೊಮೋನನ ಮಗನೂ ಯೆಹೂದದ ಅರಸನೂ ಆದ ರೆಹಬ್ಬಾಮನಿಗೂ ಯೆಹೂದ ಬೆನ್ಯಾಮೀನ್ ಕುಲಗಳವರಿಗೂ",
			"24": "<sup>24</sup> ಉಳಿದ ಜನರಿಗೂ - ಯೆಹೋವನ ಮಾತನ್ನು ಕೇಳಿರಿ, ನೀವು ನಿಮ್ಮ ಸಹೋದರರಾದ ಇಸ್ರಾಯೇಲ್ಯರೊಡನೆ ಯುದ್ಧಮಾಡುವದಕ್ಕೆ ಹೋಗಬಾರದು; ಎಲ್ಲರೂ ಹಿಂದಿರುಗಿ ಹೋಗಿರಿ; ಈ ಕಾರ್ಯವು ಯೆಹೋವನಿಂದಾಗಿದೆ ಎಂದು ಹೇಳಬೇಕು ಎಂಬ ದೈವೋತ್ತರ ಉಂಟಾಗಲು ಅವರು ಯೆಹೋವನ ಮಾತನ್ನು ಕೇಳಿ ಹಿಂದಿರುಗಿಹೋದರು.",
			"25": "<sup>25</sup> ಯಾರೊಬ್ಬಾಮನು ಎಫ್ರಾಯೀಮ್ ಬೆಟ್ಟದ ಸೀಮೆಯಲ್ಲಿನ ಶೆಕೆಮ್‍ಪಟ್ಟಣವನ್ನು ಭದ್ರಪಡಿಸಿ ಅಲ್ಲಿ ವಾಸಿಸಿದನು. ಅಲ್ಲಿಂದ ಪೆನೂವೇಲಿಗೆ ಹೋಗಿ ಅದನ್ನೂ ಭದ್ರಪಡಿಸಿದನು;",
			"26": "<sup>26</sup> ಅವನು ತನ್ನ ಮನಸ್ಸಿನಲ್ಲಿ - ರಾಜ್ಯವು ತಿರಿಗಿ ದಾವೀದನ ಕುಟುಂಬದವರಿಗೆ ಆಗುವದೋ ಏನೋ;",
			"27": "<sup>27</sup> ಜನರು ಯೆರೂಸಲೇಮಿನಲ್ಲಿರುವ ಯೆಹೋವನ ಆಲಯಕ್ಕೆ ಯಜ್ಞಾರ್ಪಣೆಗಾಗಿ ಹೋಗುವದಾದರೆ ಅವರ ಮನಸ್ಸು ಅವರ ಒಡೆಯನೂ ಯೆಹೂದದ ಅರಸನೂ ಆದ ರೆಹಬ್ಬಾಮನ ಕಡೆಗೆ ತಿರುಗೀತು; ಅವರು ನನ್ನನ್ನು ಕೊಂದು ಅವನ ಬಳಿಗೆ ಹೋದಾರು ಅಂದುಕೊಂಡು",
			"28": "<sup>28</sup> ಬಹಳವಾಗಿ ಆಲೋಚಿಸಿ ಕಡೆಯಲ್ಲಿ ಬಂಗಾರದ ಎರಡು ಬಸವ ಮೂರ್ತಿಗಳನ್ನು ಮಾಡಿಸಿ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ - ನೀವು ಜಾತ್ರೆಗಾಗಿ ಯೆರೂಸಲೇಮಿಗೆ ಹೋದದ್ದು ಸಾಕಾಯಿತು; ಇಗೋ, ನಿಮ್ಮನ್ನು ಐಗುಪ್ತದಿಂದ ಕರತಂದ ದೇವರುಗಳು ಇಲ್ಲಿರುತ್ತವೆ ಎಂದು ಹೇಳಿ",
			"29": "<sup>29</sup> ಅವುಗಳಲ್ಲಿ ಒಂದನ್ನು ಬೇತೇಲಿನಲ್ಲಿರಿಸಿ ಇನ್ನೊಂದನ್ನು ದಾನಿಗೆ ಕಳುಹಿಸಿದನು.",
			"30": "<sup>30</sup> (ಇದು ಪಾಪಕ್ಕೆ ಕಾರಣವಾಯಿತು.) ಜನರು ಈ ಎರಡನೆಯ ವಿಗ್ರಹವನ್ನು ಮೆರವಣಿಗೆಯಿಂದ ದಾನಿಗೆ ಒಯ್ದರು.",
			"31": "<sup>31</sup> ಇದಲ್ಲದೆ ಅವನು ಪೂಜಾಗಿರಿಗಳ ಮೇಲೆ ಗುಡಿಗಳನ್ನು ಕಟ್ಟಿಸಿ ಲೇವಿಯರಲ್ಲದ ಕನಿಷ್ಠ ಜನರನ್ನೂ ಯಾಜಕರನ್ನಾಗಿ ನೇಮಿಸಿದನು.",
			"32": "<sup>32</sup> ಅವನು ಎಂಟನೆಯ ತಿಂಗಳ ಹದಿನೈದನೆಯ ದಿವಸ ಬೇತೇಲಿನಲ್ಲಿ ಯೆಹೂದದೇಶದ ಜಾತ್ರೆಗೆ ಸರಿಯಾದ ಜಾತ್ರೆಯು ನಡೆಯಬೇಕೆಂದು ಅಪ್ಪಣೆಮಾಡಿ ತಾನೂ ಅಲ್ಲಿಗೆ ಹೋಗಿ ಅಲ್ಲಿ ನಿಲ್ಲಿಸಿದ ಬಸವನ ಮೂರ್ತಿಗಳಿಗೋಸ್ಕರ ವೇದಿಯ ಮೇಲೆ ಯಜ್ಞಮಾಡಿದನು. ತಾನು ಏರ್ಪಡಿಸಿದ ಪೂಜಾಸ್ಥಳಗಳ ಯಾಜಕರನ್ನು ಬೇತೇಲಿನ ದೇವಸ್ಥಾನದ ಸೇವೆಗೆ ನೇಮಿಸಿದನು.",
			"33": "<sup>33</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಎಂಟನೆಯ ತಿಂಗಳ ಹದಿನೈದನೆಯ ದಿವಸದಲ್ಲಿ ಹಬ್ಬವನ್ನಾಚರಿಸಬೇಕೆಂದು ಯಾರೊಬ್ಬಾಮನು ಸ್ವೇಚ್ಫೆಯಿಂದ ಗೊತ್ತುಮಾಡಿ ಆ ಸಮಯಕ್ಕೆ ತಾನೂ ಬೇತೇಲಿನ ಯಜ್ಞವೇದಿಯ ಬಳಿಗೆ ಹೋಗಿ ಧೂಪಸುಡುವದಕ್ಕಾಗಿ ಅದರ ಮೆಟ್ಲುಗಳನ್ನು ಹತ್ತುತ್ತಿರುವಾಗ."
		},
		"13": {
			"1": "<sup>1</sup> ಯೆಹೂದ ದೇಶದವನಾದ ಒಬ್ಬ ದೇವರ ಮನುಷ್ಯನು ಯೆಹೋವನಿಂದ ಕಳುಹಿಸಲ್ಪಟ್ಟವನಾಗಿ ಬೇತೇಲಿಗೆ ಬಂದನು. ಯಾರೊಬ್ಬಾಮನು ಧೂಪ ಸುಡುವದಕ್ಕಾಗಿ ವೇದಿಯ ಹತ್ತಿರ ನಿಂತುಕೊಳ್ಳಲು",
			"2": "<sup>2</sup> ಆ ಮನುಷ್ಯನು ಯೆಹೋವನ ಅಪ್ಪಣೆಯಿಂದ ವೇದಿಯನ್ನು ಕುರಿತು - ವೇದಿಯೇ, ವೇದಿಯೇ, ದಾವೀದನ ಸಂತಾನದಲ್ಲಿ ಯೋಷೀಯನು ಎಂಬೊಬ್ಬನು ಹುಟ್ಟುವನು; ಅವನು ನಿನ್ನ ಮೇಲೆ ಧೂಪಸುಡುವ ಪೂಜಾಸ್ಥಳಗಳ ಯಾಜಕರನ್ನು ಹಿಡಿದು ಅವರನ್ನು ನಿನ್ನ ಮೇಲೆಯೇ ಯಜ್ಞಮಾಡುವನು. ಮನುಷ್ಯರ ಎಲುಬುಗಳು ನಿನ್ನ ಮೇಲೆ ಸುಡಲ್ಪಡುವವು ಎಂದು ಯೆಹೋವನು ಹೇಳುತ್ತಾನೆ ಅಂದನು.",
			"3": "<sup>3</sup> ಇದಲ್ಲದೆ ಅವನು - ನನ್ನ ಮಾತು ಯೆಹೋವನದೆಂಬದಕ್ಕೆ ವೇದಿಯು ಸೀಳಿ ಅದರ ಮೇಲಣ ಬೂದಿಯು ಬಿದ್ದು ಹೋಗುವದೇ ಗುರುತಾಗಿರುವದು ಎಂದು ಹೇಳಿದನು.",
			"4": "<sup>4</sup> ಬೇತೇಲಿನ ವೇದಿಯ ಬಳಿಯಲ್ಲಿ ನಿಂತಿದ್ದ ಅರಸನು ಆ ದೇವರ ಮನುಷ್ಯನು ವೇದಿಗೆ ವಿರೋಧವಾಗಿ ನುಡಿದದ್ದನ್ನು ಕೇಳಿ ಕೈಚಾಚಿ - ಅವನನ್ನು ಹಿಡಿಯಿರಿ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು. ಕೂಡಲೆ ಅವನ ಕೈ ಒಣಗಿಹೋಯಿತು.",
			"5": "<sup>5</sup> ಅವನು ಅದನ್ನು ಹಿಂದೆಗೆಯಲಾರದವನಾದನು. ಯಜ್ಞವೇದಿಯು ಸೀಳಿ ಅದರ ಮೇಲಣ ಬೂದಿಯು ಬಿದ್ದುಹೋಯಿತು; ಹೀಗೆ ಆ ದೇವರ ಮನುಷ್ಯನು ಯೆಹೋವನ ಅಪ್ಪಣೆಯಿಂದ ಹೇಳಿದ ಗುರುತು ನೆರವೇರಿತು.",
			"6": "<sup>6</sup> ಅರಸನು ಆ ಮನುಷ್ಯನಿಗೆ - ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನು ಪ್ರಸನ್ನನಾಗುವಂತೆ ಬೇಡಿಕೊಂಡು ನಾನು ನನ್ನ ಕೈಯನ್ನು ಹಿಂದೆಗೆಯುವದಕ್ಕಾಗುವ ಹಾಗೆ ಆತನನ್ನು ನನಗೋಸ್ಕರ ಪ್ರಾರ್ಥಿಸು ಎಂದು ಹೇಳಿದನು. ದೇವರ ಮನುಷ್ಯನು ಯೆಹೋವನನ್ನು ಬೇಡಿಕೊಂಡದರಿಂದ ಅರಸನ ಕೈ ವಾಸಿಯಾಗಿ ಮುಂಚಿನಂತೆ ಆಯಿತು.",
			"7": "<sup>7</sup> ಆಗ ಅರಸನು ಅವನಿಗೆ - ನನ್ನ ಮನೆಗೆ ಬಾ; ಸ್ವಲ್ಪ ಊಟಮಾಡಿ ಬಲಹೊಂದು; ನಾನು ನಿನಗೆ ಬಹುಮಾನವನ್ನು ಕೊಡುತ್ತೇನೆ ಅನ್ನಲು",
			"8": "<sup>8</sup> ದೇವರ ಮನುಷ್ಯನು ಅರಸನಿಗೆ - ನೀನು ನನಗೆ ನಿನ್ನ ಆಸ್ತಿಯಲ್ಲಿ ಅರ್ಧವನ್ನು ಕೊಟ್ಟರೂ ನಾನು ನಿನ್ನ ಸಂಗಡ ಬರುವದಿಲ್ಲ; ಇಲ್ಲಿ ಅನ್ನಪಾನಗಳನ್ನು ತೆಗೆದುಕೊಳ್ಳುವದಿಲ್ಲ.",
			"9": "<sup>9</sup> ಇಲ್ಲಿ ಅನ್ನಪಾನಗಳನ್ನು ತೆಗೆದುಕೊಳ್ಳಬಾರದೆಂದೂ ಹಿಂದಿರುಗಿಹೋಗುವಾಗ ಬೇರೆ ದಾರಿಯಿಂದ ಹೋಗಬೇಕೆಂದೂ ನನಗೆ ಯೆಹೋವನ ಅಪ್ಪಣೆಯಾಗಿದೆ ಎಂದು ಹೇಳಿ",
			"10": "<sup>10</sup> ಬಂದ ದಾರಿಯನ್ನು ಬಿಟ್ಟು ಇನ್ನೊಂದು ದಾರಿಯನ್ನು ಹಿಡಿದು ಹೊರಟನು.",
			"11": "<sup>11</sup> ಬೇತೇಲಿನಲ್ಲಿ ಒಬ್ಬ ಮುದುಕನಾದ ಪ್ರವಾದಿಯು ವಾಸವಾಗಿದ್ದನು. ಅವನ ಮಕ್ಕಳು ಬಂದು ದೇವರ ಮನುಷ್ಯನು ಆ ದಿವಸ ಬೇತೇಲಿನಲ್ಲಿ ಮಾಡಿದ್ದನ್ನೂ ಅವನು ಅರಸನಿಗೆ ಅಂದದ್ದನ್ನೂ ತಂದೆಗೆ ತಿಳಿಸಿದರು.",
			"12": "<sup>12</sup> ತಂದೆಯು ಅವರನ್ನು - ಅವನು ಯಾವ ದಾರಿಯಿಂದ ಹೋದನು ಎಂದು ಕೇಳಲು ಅವರು ಯೆಹೂದ ದೇಶದವನಾದ ಆ ದೇವರ ಮನುಷ್ಯನು ಹೋದ ದಾರಿಯನ್ನು ತೋರಿಸಿದರು.",
			"13": "<sup>13</sup> ಆಗ ಅವನು ತನ್ನ ಮಕ್ಕಳಿಗೆ - ಕತ್ತೆಗೆ ತಡಿಹಾಕಿರಿ ಎಂದು ಆಜ್ಞಾಪಿಸಲು ಅವರು ಹಾಕಿದರು. ಅವನು ಕತ್ತೆಯ ಮೇಲೆ ಕೂತುಕೊಂಡು ದೇವರ ಮನುಷ್ಯನ ಹಿಂದೆ ಹೋಗಿ",
			"14": "<sup>14</sup> ಏಲಾವೃಕ್ಷದ ಕೆಳಗೆ ಕೂತಿರುವ ಅವನನ್ನು ಕಂಡು - ಯೆಹೂದದಿಂದ ಬಂದ ದೇವರ ಮನುಷ್ಯನು ನೀನೋ ಎಂದು ಕೇಳಿದನು;",
			"15": "<sup>15</sup> ಅವನು ಹೌದೆಂದನು. ಆಗ ಆ ಮುದುಕನು ಅವನಿಗೆ - ನೀನು ನನ್ನ ಮನೆಗೆ ಬಂದು ಊಟಮಾಡು ಎಂದು ಹೇಳಿದನು.",
			"16": "<sup>16</sup> ಅದಕ್ಕೆ ಅವನು - ನಾನು ನಿನ್ನ ಸಂಗಡ ಬರಕೂಡದು; ಈ ಸ್ಥಳದಲ್ಲಿ ಅನ್ನಪಾನಗಳನ್ನು ತೆಗೆದುಕೊಳ್ಳಕೂಡದು.",
			"17": "<sup>17</sup> ಇಲ್ಲಿ ಅನ್ನಪಾನಗಳನ್ನು ತೆಗೆದುಕೊಳ್ಳಬಾರದೆಂದೂ ಬಂದ ದಾರಿಯನ್ನು ಬಿಟ್ಟು ಬೇರೆ ದಾರಿಯಿಂದ ಹಿಂದಿರುಗಿ ಹೋಗಬೇಕೆಂದೂ ನನಗೆ ಯೆಹೋವನ ಅಪ್ಪಣೆಯಾಗಿದೆ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"18": "<sup>18</sup> ಆಗ ಮುದುಕನು - ನಾನೂ ನಿನ್ನಂತೆ ಪ್ರವಾದಿಯಾಗಿದ್ದೇನೆ; ಒಬ್ಬ ದೇವದೂತನು ನನಗೆ ಕಾಣಿಸಿಕೊಂಡು ನಿನ್ನನ್ನು ಕರಕೊಂಡು ಬರಬೇಕೆಂದೂ ನಿನಗೆ ಅನ್ನಪಾನಗಳನ್ನು ಕೊಡಬೇಕೆಂದೂ ಯೆಹೋವನ ಹೆಸರಿನಲ್ಲಿ ಆಜ್ಞಾಪಿಸಿದ್ದಾನೆ ಎಂದು ಹೇಳಿದನು; ಆದರೆ ಈ ಮಾತು ಸುಳ್ಳು.",
			"19": "<sup>19</sup> ದೇವರ ಮನುಷ್ಯನು ಹಿಂದಿರುಗಿಕೊಂಡು ಅವನ ಮನೆಗೆ ಹೋಗಿ ಅನ್ನಪಾನಗಳನ್ನು ತೆಗೆದುಕೊಂಡನು.",
			"20": "<sup>20</sup> ಅವರು ಊಟಮಾಡುತ್ತಿರುವಾಗ ದೇವರ ಮನುಷ್ಯನನ್ನು ಹಿಂದಕ್ಕೆ ಕರತಂದ ಪ್ರವಾದಿಗೆ ಯೆಹೋವನು ಒಂದು ವಾಕ್ಯವನ್ನು ದಯಪಾಲಿಸಿದನು.",
			"21": "<sup>21</sup> ಅವನು ಯೆಹೂದ ದೇಶದವನಾದ ಆ ದೇವರ ಮನುಷ್ಯನಿಗೆ - ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನು ನಿನಗೆ ಈ ಸ್ಥಳದಲ್ಲಿ ಅನ್ನಪಾನಗಳನ್ನು ತೆಗೆದುಕೊಳ್ಳಬಾರದೆಂದು ಆಜ್ಞಾಪಿಸಿದರೂ ನೀನು ಆತನ ಆಜ್ಞೆಯನ್ನು ಮೀರಿ ಅವಿಧೇಯನಾಗಿ",
			"22": "<sup>22</sup> ಅನ್ನಪಾನಗಳನ್ನು ತೆಗೆದುಕೊಳ್ಳುವದಕ್ಕೋಸ್ಕರ ಹಿಂದಿರುಗಿ ಬಂದದರಿಂದ ನಿನ್ನ ಶವವು ನಿನ್ನ ಕುಟುಂಬ ಶ್ಮಶಾನಭೂಮಿಯನ್ನು ಸೇರುವದೇ ಇಲ್ಲವೆಂದು ಯೆಹೋವನು ಹೇಳುತ್ತಾನೆ ಅಂದನು.",
			"23": "<sup>23</sup> ಅನ್ನಪಾನಗಳನ್ನು ತೆಗೆದುಕೊಂಡನಂತರ ಅವನು ತಾನು ಕರತಂದ ಪ್ರವಾದಿಗೋಸ್ಕರ ಕತ್ತೆಗೆ ತಡಿಹಾಕಿಸಿದನು.",
			"24": "<sup>24</sup> ದೇವರ ಮನುಷ್ಯನು ಸ್ವಲ್ಪ ದೂರ ಹೋದನಂತರ ಒಂದು ಸಿಂಹವು ಅವನಿಗೆ ಎದುರಾಗಿ ಬಂದು ಅವನನ್ನು ಕೊಂದುಹಾಕಿತು. ಅವನ ಶವವು ದಾರಿಯಲ್ಲಿಯೇ ಬಿದ್ದಿತ್ತು; ಕತ್ತೆಯೂ ಸಿಂಹವೂ ಶವದ ಹತ್ತಿರ ನಿಂತುಕೊಂಡಿದ್ದವು.",
			"25": "<sup>25</sup> ಹಾದುಹೋಗುವವರು ಶವವು ದಾರಿಯಲ್ಲಿ ಬಿದ್ದಿರುವದನ್ನೂ ಸಿಂಹವು ಅದರ ಬಳಿಯಲ್ಲಿ ನಿಂತಿರುವದನ್ನೂ ಕಂಡು ಮುದುಕನಾದ ಪ್ರವಾದಿಯ ಊರಿಗೆ ಬಂದು ಅದನ್ನು ತಿಳಿಸಿದರು.",
			"26": "<sup>26</sup> ಅವನನ್ನು ಹಿಂದಕ್ಕೆ ಕರಕೊಂಡು ಹೋಗಿದ್ದ ಪ್ರವಾದಿಯು ಈ ಸುದ್ದಿಯನ್ನು ಕೇಳಿ - ಆ ಶವವು ದೇವರ ಮನುಷ್ಯನದೇ ಆಗಿರಬೇಕು; ಅವನು ಯೆಹೋವನ ಆಜ್ಞೆಗೆ ಅವಿಧೇಯನಾದದರಿಂದ ಯೆಹೋವನು ಅವನ ವಿಷಯದಲ್ಲಿ ತಾನು ಹೇಳಿದ ಮಾತು ನೆರವೇರುವಂತೆ ಅವನನ್ನು ಸಿಂಹದ ಬಾಯಿಗೆ ಒಪ್ಪಿಸಿದನು. ಅದು ಅವನ ಮೇಲೆ ಹಾರಿ ಅವನನ್ನು ಕೊಂದುಬಿಟ್ಟಿತು ಎಂದು ಹೇಳಿ",
			"27": "<sup>27</sup> ಕತ್ತೆಗೆ ತಡಿಹಾಕಬೇಕೆಂದು ಮಕ್ಕಳಿಗೆ ಆಜ್ಞಾಪಿಸಿದನು; ಅವರು ಹಾಕಿದರು.",
			"28": "<sup>28</sup> ಅವನು ಹೋಗಿ ಶವವು ದಾರಿಯಲ್ಲಿ ಬಿದ್ದಿರುವದನ್ನೂ ಕತ್ತೆಯೂ ಸಿಂಹವೂ ಅದರ ಬಳಿಯಲ್ಲಿ ನಿಂತಿರುವದನ್ನೂ ಕಂಡನು. ಸಿಂಹವು ಶವವನ್ನು ತಿನ್ನಲಿಲ್ಲ, ಕತ್ತೆಯನ್ನು ಕೊಲ್ಲಲೂ ಇಲ್ಲ.",
			"29": "<sup>29</sup> ಪ್ರವಾದಿಯು ದೇವರ ಮನುಷ್ಯನ ಶವವನ್ನು ಹೂಳಿ ಗೋಳಾಡಬೇಕೆಂದು ಕತ್ತೆಯ ಮೇಲೆ ಹಾಕಿ",
			"30": "<sup>30</sup> ತನ್ನ ಊರಿಗೆ ತೆಗೆದುಕೊಂಡು ಬಂದು ತನ್ನ ಶ್ಮಶಾನಭೂಮಿಯಲ್ಲಿ ಅದನ್ನು ಸಮಾಧಿ ಮಾಡಿದನು. ಅವರು - ಅಯ್ಯೋ, ನನ್ನ ಸಹೋದರನೇ, ಎಂದು ಗೋಳಾಡಿದರು.",
			"31": "<sup>31</sup> ಅನಂತರ ಅವನು ತನ್ನ ಮಕ್ಕಳಿಗೆ - ನಾನು ಸತ್ತಾಗ ನನ್ನ ಶವವನ್ನು ದೇವರ ಮನುಷ್ಯನ ಶವವಿರುವಲ್ಲೇ ಸಮಾಧಿಮಾಡಿರಿ; ನನ್ನ ಎಲುಬುಗಳನ್ನು ಅವನ ಎಲುಬುಗಳಿರುವಲ್ಲೇ ಇಡಿರಿ.",
			"32": "<sup>32</sup> ಅವನು ಯೆಹೋವನ ಅಪ್ಪಣೆಯಿಂದ ಬೇತೇಲಿನ ಯಜ್ಞವೇದಿಗೆ ವಿರೋಧವಾಗಿಯೂ ಸಮಾರ್ಯದ ಪಟ್ಟಣಗಳಲ್ಲಿರುವ ಎಲ್ಲಾ ಪೂಜಾಸ್ಥಳಗಳಿಗೆ ವಿರೋಧವಾಗಿಯೂ ಹೇಳಿದ ಮಾತುಗಳು ಸಂದೇಹವಿಲ್ಲದೆ ನೆರವೇರುವವು ಅಂದನು.",
			"33": "<sup>33</sup> ಇಷ್ಟಾದರೂ ಯಾರೊಬ್ಬಾಮನು ತನ್ನ ದುರ್ಮಾರ್ಗವನ್ನು ಬಿಡದೆ ತಿರಿಗಿ ತಿರಿಗಿ ಕನಿಷ್ಠರಾದ ಜನರನ್ನು ಪೂಜಾಸ್ಥಳಗಳ ಯಾಜಕರನ್ನಾಗಿ ನೇಮಿಸಿದನು; ಮನಸ್ಸಿಗೆ ಬಂದವರನ್ನು ಪೂಜಾಸ್ಥಳಗಳ ಯಾಜಕರನ್ನಾಗಿ ಪ್ರತಿಷ್ಠಿಸಿದನು.",
			"34": "<sup>34</sup> ಈ ಕಾರಣದಿಂದ ಯಾರೊಬ್ಬಾಮನ ಕುಟುಂಬದವರು ಪಾಪಿಗಳಾಗಿ ಭೂಲೋಕದಿಂದ ತೆಗೆದುಹಾಕಲ್ಪಟ್ಟು ನಿರ್ನಾಮವಾದರು."
		},
		"14": {
			"1": "<sup>1</sup> ಆ ಕಾಲದಲ್ಲಿ ಯಾರೊಬ್ಬಾಮನ ಮಗನಾದ ಅಬೀಯನು ಅಸ್ವಸ್ಥನಾದನು.",
			"2": "<sup>2</sup> ಆಗ ಯಾರೊಬ್ಬಾಮನು ತನ್ನ ಹೆಂಡತಿಗೆ - ನೀನು ಎದ್ದು ನನ್ನ ಹೆಂಡತಿಯೆಂದು ಯಾರಿಗೂ ಗೊತ್ತಾಗದಂತೆ ವೇಷ ಮಾರ್ಪಡಿಸಿಕೊಂಡು ಶೀಲೋವಿಗೆ ಹೋಗು. ನಾನು ಈ ಜನರ ಮೇಲೆ ಅರಸನಾಗಬೇಕೆಂದು ನನಗೆ ಮುಂತಿಳಿಸಿದ ಪ್ರವಾದಿಯಾದ ಅಹೀಯನು ಅಲ್ಲಿರುತ್ತಾನೆ.",
			"3": "<sup>3</sup> ನೀನು ಹತ್ತು ರೊಟ್ಟಿಗಳನ್ನೂ ಮಿಠಾಯಿಯನ್ನೂ ಒಂದು ಕುಪ್ಪಿ ಜೇನುತುಪ್ಪವನ್ನೂ ತೆಗೆದುಕೊಂಡು ಅವನ ಬಳಿಗೆ ಹೋಗು; ಹುಡುಗನಿಗೆ ಏನಾಗುವದೆಂದು ಅವನು ತಿಳಿಸುವನು ಎಂದು ಹೇಳಿದನು.",
			"4": "<sup>4</sup> ಆಕೆಯು ಅವನು ಹೇಳಿದಂತೆಯೇ ಮಾಡಿ ಶೀಲೋವಿನಲ್ಲಿದ್ದ ಅಹೀಯನ ಮನೆಗೆ ಹೋದಳು. ಮುಪ್ಪಿನ ದೆಸೆಯಿಂದ ಅಹೀಯನ ಕಣ್ಣುಗಳು ಮೊಬ್ಬಾಗಿದ್ದದ್ದರಿಂದ ಕಾಣಿಸುತ್ತಿರಲಿಲ್ಲ.",
			"5": "<sup>5</sup> ಆದರೆ ಯೆಹೋವನು ಅವನಿಗೆ - ಯಾರೊಬ್ಬಾಮನ ಹೆಂಡತಿಯು ಅಸ್ವಸ್ಥನಾದ ತನ್ನ ಮಗನ ವಿಷಯದಲ್ಲಿ ದೈವೋತ್ತರವನ್ನು ಕೇಳುವದಕ್ಕೆ ತನ್ನನ್ನು ಅನ್ಯಳೆಂದು ತೋರ್ಪಡಿಸಿಕೊಂಡು ಬರುತ್ತಾಳೆಂದೂ ಆಕೆಗೆ ಇಂಥಿಂಥ ಉತ್ತರ ಕೊಡಬೇಕೆಂದೂ ತಿಳಿಸಿದ್ದನು.",
			"6": "<sup>6</sup> ಆಕೆಯು ಅಹೀಯನ ಮನೆಯನ್ನು ಪ್ರವೇಶಿಸುವಾಗಲೇ ಅಹೀಯನು ಆಕೆಯ ಕಾಲುಸಪ್ಪಳವನ್ನು ಕೇಳಿ - ಯಾರೊಬ್ಬಾಮನ ಹೆಂಡತಿಯೇ, ಬಾ; ಯಾಕೆ ನಿನ್ನನ್ನು ಅನ್ಯಳೆಂದು ತೋರ್ಪಡಿಸಿಕೊಳ್ಳುತ್ತೀ? ನಿನಗೆ ಕಠಿಣವಾದ ಉತ್ತರವನ್ನು ಕೊಡಬೇಕೆಂದು ನನಗೆ ಅಪ್ಪಣೆಯಾಗಿದೆ.",
			"7": "<sup>7</sup> ನೀನು ಹೋಗಿ ಯಾರೊಬ್ಬಾಮನಿಗೆ ಇಸ್ರಾಯೇಲ್ ದೇವರಾದ ಯೆಹೋವನ ಅಪ್ಪಣೆಯನ್ನು ತಿಳಿಸು. ಆತನು ಅವನಿಗೆ - ನಾನು ನಿನ್ನನ್ನು ಜನರ ಮಧ್ಯದಿಂದ ಉನ್ನತಸ್ಥಾನಕ್ಕೆ ತಂದೆನು; ನಿನ್ನನ್ನು ನನ್ನ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಾಯೇಲ್ಯರ ನಾಯಕನನ್ನಾಗಿ ಮಾಡಿದೆನು.",
			"8": "<sup>8</sup> ರಾಜ್ಯವನ್ನು ದಾವೀದನ ಕುಟುಂಬದವರಿಂದ ಕಿತ್ತುಕೊಂಡು ನಿನಗೆ ಕೊಟ್ಟೆನು. ಆದರೂ ನೀನು ನನ್ನ ಆಜ್ಞೆಗಳನ್ನು ಕೈಕೊಂಡು ಪೂರ್ಣಮನಸ್ಸಿನಿಂದ ನನ್ನನ್ನು ಹಿಂಬಾಲಿಸಿ ಮೆಚ್ಚಿಸಿದಂಥ ನನ್ನ ಸೇವಕನಾದ ದಾವೀದನಂತೆ ನಡೆಯಲಿಲ್ಲ.",
			"9": "<sup>9</sup> ನೀನು ನಿನ್ನ ಎಲ್ಲಾ ಪೂರ್ವಾಧಿಕಾರಿಗಳಿಗಿಂತಲೂ ದುಷ್ಟನಾದಿ; ನನ್ನನ್ನು ಉಲ್ಲಂಘಿಸಿ ಅನ್ಯದೇವತೆಗಳನ್ನೂ ಎರಕದ ವಿಗ್ರಹಗಳನ್ನೂ ಪೂಜಿಸಿ ನನಗೆ ಕೋಪವನ್ನೆಬ್ಬಿಸಿದಿ.",
			"10": "<sup>10</sup> ಆದದರಿಂದ ಯಾರೊಬ್ಬಾಮನೇ, ಕೇಳು; ನಾನು ನಿನ್ನ ಮನೆಯವರ ಮೇಲೆ ಕೇಡನ್ನು ಬರಮಾಡುವೆನು; ನಿನ್ನ ಕುಟುಂಬದ ಗಂಡಸರಲ್ಲಿ ಸ್ವತಂತ್ರರಾಗಲಿ ಪರತಂತ್ರರಾಗಲಿ, ಎಲ್ಲರನ್ನೂ ಇಸ್ರಾಯೇಲ್ಯರೊಳಗಿಂದ ಸಂಹರಿಸಿಬಿಡುವೆನು. ಒಬ್ಬನು ಕಸವನ್ನು ಗುಡಿಸಿ ತೆಗೆದುಹಾಕುವಂತೆ ನಾನು ನಿನ್ನ ಮನೆಯವರನ್ನು ತೆಗೆದುಹಾಕುವೆನು; ಅವರು ನಿರ್ನಾಮವಾಗುವರು.",
			"11": "<sup>11</sup> ಅವರಲ್ಲಿ ಪಟ್ಟಣದೊಳಗೆ ಸಾಯುವಂಥವರನ್ನು ನಾಯಿಗಳೂ ಅಡವಿಯಲ್ಲಿ ಸಾಯುವಂಥವರನ್ನು ಪಕ್ಷಿಗಳೂ ತಿಂದುಬಿಡುವವು ಎಂದು ಹೇಳುತ್ತಾನೆ. ಇದು ಯೆಹೋವನ ಮಾತು.",
			"12": "<sup>12</sup> ನೀನು ನಿನ್ನ ಮನೆಗೆ ಹೋಗು; ನೀನು ಪಟ್ಟಣದೊಳಗೆ ಕಾಲಿಡುವಷ್ಟರಲ್ಲಿ ಹುಡುಗನು ಸಾಯುವನು.",
			"13": "<sup>13</sup> ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರೂ ಅವನಿಗೋಸ್ಕರ ಗೋಳಾಡಿ ಅವನ ಶವವನ್ನು ಸಮಾಧಿಮಾಡುವರು. ಯಾರೊಬ್ಬಾಮನ ಮನೆಯವರೊಳಗೆ ಇಸ್ರಾಯೇಲ್ ದೇವರಾದ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ಒಳ್ಳೆಯವನಾಗಿರುವದರಿಂದ ಇವನೊಬ್ಬನೇ ಸಮಾಧಿಹೊಂದುವನು.",
			"14": "<sup>14</sup> ಯೆಹೋವನು ಇಸ್ರಾಯೇಲ್ಯರ ಮೇಲೆ ಬೇರೊಬ್ಬ ಅರಸನನ್ನು ನೇಮಿಸುವನು. ಅವನು ಕೂಡಲೆ ಯಾರೊಬ್ಬಾಮನ ಮನೆಯವರನ್ನು ಸಂಹರಿಸಿಬಿಡುವನು.",
			"15": "<sup>15</sup> ಇನ್ನು ಏನೇನು ಸಂಭವಿಸುವದೋ! ಯೆಹೋವನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಹೊಡಿಯುವನು. ಆಗ ಅವರು ನೀರಿನಲ್ಲಿರುವ ಆಪುಹುಲ್ಲೋ ಎಂಬಂತೆ ಹೊಯ್ದಾಡುವರು. ಅವರು ಅಶೇರವಿಗ್ರಹಸ್ತಂಭಗಳನ್ನು ಮಾಡಿಕೊಂಡು ಆತನಿಗೆ ಕೋಪವನ್ನೆಬ್ಬಿಸಿದದರಿಂದ ಆತನು ಅವರನ್ನು ಅವರ ಪಿತೃಗಳಿಗೆ ಕೊಟ್ಟ ದೇಶದಿಂದ ಕಿತ್ತುಹಾಕಿ ಯೂಫ್ರೇಟೀಸ್ ನದಿಯ ಆಚೆಯಲ್ಲಿರುವ ದೇಶದೊಳಗೆ ಚದರಿಸಿಬಿಡುವನು.",
			"16": "<sup>16</sup> ಯೆಹೋವನು ಯಾರೊಬ್ಬಾಮನ ಪಾಪಗಳ ನಿಮಿತ್ತವಾಗಿಯೂ ಅವನ ಪ್ರೇರಣೆಯಿಂದ ಇಸ್ರಾಯೇಲ್ಯರು ಮಾಡಿದ ಪಾಪಗಳ ನಿಮಿತ್ತವಾಗಿಯೂ ಅವರನ್ನು ಶತ್ರುಗಳಿಗೆ ಒಪ್ಪಿಸುವನು ಎಂದು ಆಕೆಗೆ ಹೇಳಿದನು.",
			"17": "<sup>17</sup> ಯಾರೊಬ್ಬಾಮನ ಹೆಂಡತಿಯು ತಿರ್ಚಾ ಊರಿಗೆ ಹೊರಟುಹೋಗಿ ತನ್ನ ಮನೆಯ ಹೊಸ್ತಿಲಲ್ಲಿ ಕಾಲಿಟ್ಟ ಕೂಡಲೆ ಹುಡುಗನು ಸತ್ತನು.",
			"18": "<sup>18</sup> ಯೆಹೋವನು ಪ್ರವಾದಿಯಾದ ಅಹೀಯನ ಮುಖಾಂತರವಾಗಿ ತಿಳಿಸಿದ ಪ್ರಕಾರ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರು ಅವನಿಗೋಸ್ಕರ ಗೋಳಾಡಿ ಅವನ ಶವವನ್ನು ಸಮಾಧಿಮಾಡಿದರು.",
			"19": "<sup>19</sup> ಯಾರೊಬ್ಬಾಮನ ಇತರ ಕೃತ್ಯಗಳೂ ಅವನ ಯುದ್ಧ ರಾಜ್ಯಭಾರಗಳ ವಿವರವೂ ಇಸ್ರಾಯೇಲ್ ರಾಜಕಾಲವೃತ್ತಾಂತ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"20": "<sup>20</sup> ಅವನು ಇಪ್ಪತ್ತೆರಡು ವರುಷ ಆಳಿ ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಿದನು. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ನಾದಾಬನು ಅರಸನಾದನು.",
			"21": "<sup>21</sup> ಸೊಲೊಮೋನನ ಮಗನಾದ ರೆಹಬ್ಬಾಮನು ಯೆಹೂದದ ಅರಸನಾದನು. ಅವನು ಪಟ್ಟಕ್ಕೆ ಬಂದಾಗ ನಾಲ್ವತ್ತೊಂದು ವರುಷದವನಾಗಿದ್ದು ಯೆಹೋವನು ತನ್ನ ಹೆಸರಿಗೋಸ್ಕರ ಇಸ್ರಾಯೇಲ್ಯರ ಎಲ್ಲಾ ಕುಲಗಳಿಂದ ಆರಿಸಿಕೊಂಡ ಯೆರೂಸಲೇಮ್ ಪಟ್ಟಣದಲ್ಲಿ ಹದಿನೇಳು ವರುಷ ಆಳಿದನು. ಅಮ್ಮೋನಿಯಳಾದ ನಯಮಾ ಎಂಬಾಕೆಯು ಅವನ ತಾಯಿ.",
			"22": "<sup>22</sup> ಯೆಹೂದ್ಯರು ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಗಳಾದರು; ಅವರು ತಮ್ಮ ಪಿತೃಗಳಿಗಿಂತಲೂ ದುಷ್ಟರಾಗಿ ಆತನನ್ನು ರೇಗಿಸಿದರು.",
			"23": "<sup>23</sup> ಪಿತೃಗಳಂತೆಯೇ ಅವರೂ ತಮಗೋಸ್ಕರ ಪೂಜಾಸ್ಥಳಗಳನ್ನು ಏರ್ಪಡಿಸಿಕೊಂಡರು. ಪ್ರತಿಯೊಂದು ದಿನ್ನೆಯ ಮೇಲೆಯೂ ಚೆನ್ನಾಗಿ ಬೆಳೆದಿರುವ ಪ್ರತಿಯೊಂದು ಮರದ ಕೆಳಗೆಯೂ ಕಲ್ಲಿನ ಕಂಬಗಳನ್ನೂ ಅಶೇರ ವಿಗ್ರಹಸ್ತಂಭಗಳನ್ನೂ ನಿಲ್ಲಿಸಿದರು.",
			"24": "<sup>24</sup> ಇದಲ್ಲದೆ ಅವರ ದೇಶದಲ್ಲಿ ವೇಶ್ಯಾವೃತ್ತಿಯನ್ನನುಸರಿಸುತ್ತಿರುವ ದೇವದಾಸ ದೇವದಾಸಿಯರು ಇದ್ದರು. ಯೆಹೋವನು ಅವರ ಎದುರಿನಿಂದ ಹೊರಡಿಸಿಬಿಟ್ಟ ಅನ್ಯಜನಾಂಗಗಳಲ್ಲಿದ್ದ ದುರಾಚಾರಗಳನ್ನೆಲ್ಲಾ ಅವರೂ ನಡಿಸುವವರಾದರು.",
			"25": "<sup>25</sup> ರೆಹಬ್ಬಾಮನ ಆಳಿಕೆಯ ಐದನೆಯ ವರುಷದಲ್ಲಿ ಐಗುಪ್ತದ ಅರಸನಾದ ಶೀಶಕನು ಯೆರೂಸಲೇಮಿಗೆ ವಿರೋಧವಾಗಿ ಬಂದು",
			"26": "<sup>26</sup> ಯೆಹೋವನ ಆಲಯದ ಮತ್ತು ಅರಮನೆಯ ಎಲ್ಲಾ ದ್ರವ್ಯವನ್ನೂ ಸೊಲೊಮೋನನು ಮಾಡಿಸಿದ ಬಂಗಾರದ ಗುರಾಣಿಗಳನ್ನೂ ತೆಗೆದುಕೊಂಡು ಹೋದನು.",
			"27": "<sup>27</sup> ಅರಸನಾದ ರೆಹಬ್ಬಾಮನು ಅವುಗಳಿಗೆ ಬದಲಾಗಿ ತಾಮ್ರದ ಗುರಾಣಿಗಳನ್ನು ಮಾಡಿಸಿ ಅವುಗಳನ್ನು ಅರಮನೆಯ ದ್ವಾರಪಾಲಕರಾಗಿದ್ದ ಮೈಗಾವಲಿನವರ ದಳವಾಯಿಗಳಿಗೆ ಒಪ್ಪಿಸಿದನು.",
			"28": "<sup>28</sup> ಅರಸನು ಯೆಹೋವನ ಆಲಯಕ್ಕೆ ಹೋಗುವಾಗೆಲ್ಲಾ ಅವರು ಅವುಗಳನ್ನು ಹಿಡಿದುಕೊಳ್ಳುವರು; ಅಲ್ಲಿಂದ ತಿರಿಗಿ ಬಂದ ಮೇಲೆ ಅವುಗಳನ್ನು ತಮ್ಮ ಕೋಣೆಯಲ್ಲಿಡುವರು.",
			"29": "<sup>29</sup> ರೆಹಬ್ಬಾಮನ ಉಳಿದ ಚರಿತ್ರೆಯೂ ಅವನ ಎಲ್ಲಾ ಕೃತ್ಯಗಳೂ ಯೆಹೂದರಾಜಕಾಲ ವೃತ್ತಾಂತ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"30": "<sup>30</sup> ರೆಹಬ್ಬಾಮನಿಗೂ ಯಾರೊಬ್ಬಾಮನಿಗೂ ಯಾವಾಗಲೂ ಯುದ್ಧ ನಡೆಯುತ್ತಿತ್ತು.",
			"31": "<sup>31</sup> ರೆಹಬ್ಬಾಮನು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಲು ಅವನ ಶವವನ್ನು ದಾವೀದನಗರದೊಳಗೆ ಅವನ ಹಿರಿಯರ ಶ್ಮಶಾನ ಭೂಮಿಯಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಅಮ್ಮೋನಿಯಳಾದ ನಯಮಾ ಎಂಬಾಕೆಯು ಅವನ ತಾಯಿ. ಅನಂತರ ಅವನ ಮಗನಾದ ಅಬೀಯಾಮನು ಅವನಿಗೆ ಬದಲಾಗಿ ಅರಸನಾದನು."
		},
		"15": {
			"1": "<sup>1</sup> ನೆಬಾಟನ ಮಗನಾದ ಯಾರೊಬ್ಬಾಮನ ಆಳಿಕೆಯ ಹದಿನೆಂಟನೆಯ ವರುಷದಲ್ಲಿ ಅಬೀಯಾಮನು ಯೆಹೂದದ ಅರಸನಾಗಿ",
			"2": "<sup>2</sup> ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಮೂರು ವರುಷ ಆಳಿದನು; ಅಬೀಷಾಲೋಮನ ಮಗಳಾದ ಮಾಕಾ ಎಂಬಾಕೆಯು ಅವನ ತಾಯಿ.",
			"3": "<sup>3</sup> ತನ್ನ ತಂದೆಯು ಮಾಡಿದ ಪಾಪಕೃತ್ಯಗಳನ್ನು ಇವನೂ ಮಾಡಿದನು. ಇವನ ಪೂರ್ವಿಕನಾದ ದಾವೀದನು ದೇವರಾದ ಯೆಹೋವನನ್ನು ಯಥಾರ್ಥಮನಸ್ಸಿನಿಂದ ಸೇವಿಸಿದಂತೆ ಇವನು ಸೇವಿಸಲಿಲ್ಲ.",
			"4": "<sup>4</sup> ಆದರೂ ಹಿತ್ತಿಯನಾದ ಊರೀಯನ ವಿಷಯ ಹೊರತಾಗಿ ಬೇರೆ ಯಾವ ವಿಷಯದಲ್ಲಿಯೂ ತನ್ನ ಆಜ್ಞೆಯನ್ನು ಮೀರದೆ ಜೀವದಿಂದಿರುವವರೆಗೂ ತನ್ನ ಚಿತ್ತಕ್ಕನುಸಾರನಾಗಿ ನಡೆದ ದಾವೀದನ ನಿಮಿತ್ತವಾಗಿ ಯೆಹೋವನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿನ ಇವನ ದೀಪವನ್ನು ಆರಿಸಲಿಲ್ಲ.",
			"5": "<sup>5</sup> ಯೆರೂಸಲೇಮನ್ನೂ ಇವನ ಸಂತಾನವನ್ನೂ ಉಳಿಸಿದನು.",
			"6": "<sup>6</sup> (ರೆಹಬ್ಬಾಮನಿಗೂ ಯಾರೊಬ್ಬಾಮನಿಗೂ ಯಾವಾಗಲೂ ಯುದ್ಧ ನಡೆಯುತ್ತಿತ್ತು.)",
			"7": "<sup>7</sup> ಅಬೀಯಾಮನ ಉಳಿದ ಚರಿತ್ರೆಯೂ ಅವನ ಎಲ್ಲಾ ಕೃತ್ಯಗಳೂ ಯೆಹೂದರಾಜಕಾಲವೃತ್ತಾಂತ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ. ಇವನಿಗೂ ಯಾರೊಬ್ಬಾಮನಿಗೂ ಯುದ್ಧ ನಡೆಯುತ್ತಿತ್ತು.",
			"8": "<sup>8</sup> ಅಬೀಯಾಮನು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಲು ಅವನ ಶವವನ್ನು ದಾವೀದನಗರದಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ಆಸನು ಅರಸನಾದನು.",
			"9": "<sup>9</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾದ ಯಾರೊಬ್ಬಾಮನ ಆಳಿಕೆಯ ಇಪ್ಪತ್ತನೆಯ ವರುಷದಲ್ಲಿ ಆಸನು ಯೆಹೂದದ ಅರಸನಾದನು.",
			"10": "<sup>10</sup> ಇವನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ನಾಲ್ವತ್ತೊಂದು ವರುಷ ಆಳಿದನು. ಅಬೀಷಾಲೋಮನ ಮಗಳಾದ ಮಾಕಾ ಎಂಬಾಕೆಯು ಇವನ ತಾಯಿ.",
			"11": "<sup>11</sup> ಇವನು ತನ್ನ ಪೂರ್ವಿಕನಾದ ದಾವೀದನಂತೆ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ಒಳ್ಳೆಯವನಾಗಿ ನಡೆದನು.",
			"12": "<sup>12</sup> ದೇವದಾಸ ದೇವದಾಸಿಯರನ್ನು ದೇಶದಿಂದ ಹೊರಡಿಸಿ ತನ್ನ ಹಿರಿಯರು ಮಾಡಿಸಿಟ್ಟಿದ್ದ ವಿಗ್ರಹಗಳನ್ನೆಲ್ಲಾ ತೆಗೆದು ಹಾಕಿದನು.",
			"13": "<sup>13</sup> ಇವನ ತಾಯಿಯಾದ ಮಾಕಳು ಅಶೇರದೇವತೆಯ ಒಂದು ಅಸಹ್ಯವಾದ ಮೂರ್ತಿಯನ್ನು ಮಾಡಿಸಿದದರಿಂದ ಆಸನು ಆಕೆಯನ್ನು ಗದ್ದುಗೆಯಿಂದ ತಳ್ಳಿ ಆ ಮೂರ್ತಿಯನ್ನು ಕಡಿದು ಕಿದ್ರೋನ್‍ಹಳ್ಳದ ಹತ್ತಿರ ಸುಡಿಸಿದನು.",
			"14": "<sup>14</sup> ಇವನು ದೇಶದಲ್ಲಿದ್ದ ಪೂಜಾಸ್ಥಳಗಳನ್ನು ಹಾಳುಮಾಡದಿದ್ದರೂ ತನ್ನ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ಯಥಾರ್ಥಮನಸ್ಸಿನಿಂದ ಯೆಹೋವನಿಗೆ ನಡೆದುಕೊಂಡನು.",
			"15": "<sup>15</sup> ಇದಲ್ಲದೆ ತನ್ನ ತಂದೆಯೂ ತಾನೂ ಪ್ರತಿಷ್ಠಿಸಿದ ಬೆಳ್ಳಿಬಂಗಾರವನ್ನೂ ಪಾತ್ರೆಗಳನ್ನೂ ಯೆಹೋವನ ಆಲಯದಲ್ಲಿ ತಂದಿಟ್ಟನು.",
			"16": "<sup>16</sup> ಇವನಿಗೂ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾದ ಬಾಷನಿಗೂ ಯಾವಾಗಲೂ ಯುದ್ಧ ನಡೆಯುತ್ತಿತ್ತು.",
			"17": "<sup>17</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾದ ಬಾಷನು ಯೆಹೂದ್ಯರಿಗೆ ವಿರೋಧವಾಗಿ ಯುದ್ಧಮಾಡಿ ಯಾರೂ ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಆಸನ ಬಳಿಗೆ ಹೋಗಿ ಬರುವದಕ್ಕಾಗದಂತೆ ರಾಮ ಎಂಬ ಕೋಟೆಯನ್ನು ಕಟ್ಟಿಸಿದನು.",
			"18": "<sup>18</sup> ಆಗ ಆಸನು ಯೆಹೋವನ ಆಲಯದ ಮತ್ತು ಅರಮನೆಯ ಭಂಡಾರಗಳಲ್ಲಿ ಉಳಿದಿದ್ದ ಬೆಳ್ಳಿಬಂಗಾರವನ್ನು ತೆಗೆದುಕೊಂಡು ಅದನ್ನು ದೂತರ ಮುಖಾಂತರವಾಗಿ ದಮಸ್ಕದಲ್ಲಿದ್ದ ಅರಾಮ್ಯರ ಅರಸನೂ ಹೆಜ್ಯೋನನ ಮೊಮ್ಮಗನೂ ಟಬ್ರಿಮ್ಮೋನನ ಮಗನೂ ಆದ ಬೆನ್ಹದದನಿಗೆ ಕೊಟ್ಟು ಕಳುಹಿಸಿ ಅವನಿಗೆ -",
			"19": "<sup>19</sup> ನನಗೂ ನಿನಗೂ ನನ್ನ ತಂದೆಗೂ ನಿನ್ನ ತಂದೆಗೂ ಒಡಂಬಡಿಕೆಯಿರುತ್ತದಲ್ಲಾ; ಇಗೋ, ನಿನಗೆ ಬೆಳ್ಳಿಬಂಗಾರವನ್ನು ಕಾಣಿಕೆಯಾಗಿ ಕಳುಹಿಸಿದ್ದೇನೆ. ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾದ ಬಾಷನು ನನ್ನನ್ನು ಬಿಟ್ಟುಹೋಗುವಂತೆ ಅವನ ಸಂಗಡ ನೀನು ಮಾಡಿಕೊಂಡಿರುವ ಒಡಂಬಡಿಕೆಯನ್ನು ಮುರಿದುಬಿಡು ಎಂದು ಹೇಳಿಸಿದನು.",
			"20": "<sup>20</sup> ಬೆನ್ಹದದನು ಅರಸನಾದ ಆಸನ ಮಾತನ್ನು ಕೇಳಿ ತನ್ನ ಸೈನ್ಯಾಧಿಪತಿಗಳನ್ನು ಯುದ್ಧಕ್ಕಾಗಿ ಇಸ್ರಾಯೇಲ್ಯರ ಪಟ್ಟಣಗಳಿಗೆ ಕಳುಹಿಸಿದನು. ಇವರು ಇಯ್ಯೋನ್, ದಾನ್, ಆಬೇಲ್ಬೇತ್ಮಾಕಾ, ಕಿನ್ನೆರೋತ್‍ಪ್ರದೇಶ, ನಫ್ತಾಲ್ಯರ ಎಲ್ಲಾ ಪ್ರಾಂತ ಇವುಗಳನ್ನು ಹಾಳುಮಾಡಿದರು.",
			"21": "<sup>21</sup> ಬಾಷನು ಈ ಸುದ್ದಿಯನ್ನು ಕೇಳಿದಾಗ ರಾಮ ಕೋಟೆಯನ್ನು ಕಟ್ಟಿಸುವದನ್ನು ಬಿಟ್ಟು ತಿರ್ಚಕ್ಕೆ ಹೋಗಿ ಅಲ್ಲೇ ವಾಸಿಸಿದನು.",
			"22": "<sup>22</sup> ಅನಂತರ ಅರಸನಾದ ಆಸನು ಯೆಹೂದ್ಯರಲ್ಲಿ ಒಬ್ಬನನ್ನೂ ಬಿಡದೆ ಎಲ್ಲರನ್ನೂ ಕರಿಸಿ ಬಾಷನು ರಾಮಕೋಟೆಗಾಗಿ ತರಿಸಿದ್ದ ಕಲ್ಲುಮರಗಳನ್ನು ಇವರ ಮುಖಾಂತರವಾಗಿ ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ಬೆನ್ಯಾಮೀನ್ಯರ ಗೆಬ, ಮಿಚ್ಬೆ ಎಂಬ ಪಟ್ಟಣಗಳನ್ನು ಭದ್ರಪಡಿಸಿದನು.",
			"23": "<sup>23</sup> ಆಸನ ಉಳಿದ ಚರಿತ್ರೆ, ಅವನ ಎಲ್ಲಾ ಪರಾಕ್ರಮಕೃತ್ಯಗಳು, ಪಟ್ಟಣಗಳನ್ನು ಕಟ್ಟಿಸಿದ ಸಂಗತಿ ಇವುಗಳೂ ಅವನ ಇತರ ಕ್ರಿಯೆಗಳೂ ಯೆಹೂದರಾಜಕಾಲವೃತ್ತಾಂತ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ. ಮುಪ್ಪಿನಲ್ಲಿ ಅವನ ಕಾಲುಗಳಿಗೆ ರೋಗವುಂಟಾಯಿತು.",
			"24": "<sup>24</sup> ಅವನು ತನ್ನ ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಿದನು. ಅವನ ಶವವನ್ನು ಅವನ ಪೂರ್ವಿಕನಾದ ದಾವೀದನ ಪಟ್ಟಣದೊಳಗೆ ಕುಟುಂಬ ಶ್ಮಶಾನ ಭೂಮಿಯಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ಯೆಹೋಷಾಫಾಟನು ಅರಸನಾದನು.",
			"25": "<sup>25</sup> ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಆಸನ ಆಳಿಕೆಯ ಎರಡನೆಯ ವರುಷದಲ್ಲಿ ಯಾರೊಬ್ಬಾಮನ ಮಗನಾದ ನಾದಾಬನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾಗಿ ಎರಡು ವರುಷ ಆಳಿದನು.",
			"26": "<sup>26</sup> ಇವನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಪಾಪಕ್ಕೆ ಪ್ರೇರಿಸಿದ ತನ್ನ ತಂದೆಯ ಮಾರ್ಗದಲ್ಲಿ ನಡೆದು ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾದನು.",
			"27": "<sup>27</sup> ಇವನು ಇಸ್ರಾಯೇಲ್ಯರೊಡನೆ ಫಿಲಿಷ್ಟಿಯರ ವಶದಲ್ಲಿದ್ದ ಗಿಬ್ಬೆತೋನ್ ಎಂಬ ಪಟ್ಟಣಕ್ಕೆ ಮುತ್ತಿಗೆ ಹಾಕಿದಾಗ ಇಸ್ಸಾಕಾರನ ಕುಲದವನೂ ಅಹೀಯನ ಮಗನೂ ಆದ ಬಾಷನೆಂಬವನು ಇವನಿಗೆ ವಿರೋಧವಾಗಿ ಒಳಸಂಚು ಮಾಡಿ ಇವನನ್ನು ಕೊಂದನು.",
			"28": "<sup>28</sup> ಬಾಷನು ಯೆಹೂದದ ಅರಸನಾದ ಆಸನ ಆಳಿಕೆಯ ಮೂರನೆಯ ವರುಷದಲ್ಲಿ ಇವನನ್ನು ಕೊಂದು ಇವನಿಗೆ ಬದಲಾಗಿ ತಾನು ಅರಸನಾದನು;",
			"29": "<sup>29</sup> ಅರಸನಾದ ಕೂಡಲೆ ಯಾರೊಬ್ಬಾಮನ ಮನೆಯವರಲ್ಲಿ ಒಬ್ಬನನ್ನೂ ಜೀವದಿಂದುಳಿಸದೆ ಎಲ್ಲರನ್ನೂ ಸಂಹರಿಸಿದನು. ಹೀಗೆ ಯೆಹೋವನು ಶೀಲೋವಿನ ಪ್ರವಾದಿಯಾದ ಅಹೀಯನ ಮುಖಾಂತರವಾಗಿ ಹೇಳಿಸಿದ ಮಾತು ನೆರವೇರಿತು.",
			"30": "<sup>30</sup> ಯಾರೊಬ್ಬಾಮನು ತಾನು ಪಾಪಮಾಡಿದ್ದಲ್ಲದೆ ಇಸ್ರಾಯೇಲ್ಯರನ್ನೂ ಪಾಪಕ್ಕೆ ಪ್ರೇರಿಸಿ ಇಸ್ರಾಯೇಲ್‍ದೇವರಾದ ಯೆಹೋವನನ್ನು ರೇಗಿಸಿದ್ದರಿಂದ ಹೀಗಾಯಿತು.",
			"31": "<sup>31</sup> ನಾದಾಬನ ಉಳಿದ ಚರಿತ್ರೆಯೂ ಅವನ ಎಲ್ಲಾ ಕೃತ್ಯಗಳೂ ಇಸ್ರಾಯೇಲ್ ರಾಜಕಾಲವೃತ್ತಾಂತ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"32": "<sup>32</sup> ಆಸನಿಗೂ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾದ ಬಾಷನಿಗೂ ಯಾವಾಗಲೂ ಯುದ್ಧ ನಡೆಯುತ್ತಿತ್ತು.",
			"33": "<sup>33</sup> ಯೆಹೂದದ ಅರಸನಾದ ಆಸನ ಆಳಿಕೆಯ ಮೂರನೆಯ ವರುಷದಲ್ಲಿ ಅಹೀಯನ ಮಗನಾದ ಬಾಷನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾಗಿ ತಿರ್ಚದಲ್ಲಿದ್ದುಕೊಂಡು ಇಪ್ಪತ್ತನಾಲ್ಕು ವರುಷ ಆಳಿದನು.",
			"34": "<sup>34</sup> ಇವನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಪಾಪಕ್ಕೆ ಪ್ರೇರಿಸಿದ ಯಾರೊಬ್ಬಾಮನ ಮಾರ್ಗದಲ್ಲಿ ನಡೆದು ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾದನು."
		},
		"16": {
			"1": "<sup>1</sup> ಆಗ ಯೆಹೋವನು ಹನಾನೀಯ ಮಗನಾದ ಯೇಹುವಿನ ಮುಖಾಂತರವಾಗಿ ಅವನಿಗೆ ಹೇಳಿಸಿದ್ದೇನಂದರೆ -",
			"2": "<sup>2</sup> ನಾನು ನಿನ್ನನ್ನು ಧೂಳಿನಿಂದ ಎತ್ತಿ ನನ್ನ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಾಯೇಲ್ಯರ ನಾಯಕನನ್ನಾಗಿ ಮಾಡಿದರೂ ನೀನು ಯಾರೊಬ್ಬಾಮನ ಮಾರ್ಗದಲ್ಲಿ ನಡೆದು ನನ್ನ ಜನರಾದ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಪಾಪಕ್ಕೆ ಪ್ರೇರಿಸಿ ಅವರ ಪಾಪದ ಮೂಲಕವಾಗಿ ನನಗೆ ಕೋಪವನ್ನೆಬ್ಬಿಸಿದ್ದರಿಂದ",
			"3": "<sup>3</sup> ನಿನ್ನನ್ನೂ ನಿನ್ನ ಮನೆಯವರನ್ನೂ ಅಳಿಸಿಬಿಡುವೆನು; ನೆಬಾಟನ ಮಗನಾದ ಯಾರೊಬ್ಬಾಮನ ಮನೆಗಾದ ಗತಿಯು ನಿನ್ನ ಮನೆಗೂ ಆಗುವದು.",
			"4": "<sup>4</sup> ನಿನ್ನ ಕುಟುಂಬದವರಲ್ಲಿ ಪಟ್ಟಣದೊಳಗೆ ಸಾಯುವವರನ್ನು ನಾಯಿಗಳೂ ಅಡವಿಯಲ್ಲಿ ಸಾಯುವವರನ್ನು ಪಕ್ಷಿಗಳೂ ತಿಂದುಬಿಡುವವು ಎಂಬದೇ.",
			"5": "<sup>5</sup> ಬಾಷನ ಉಳಿದ ಚರಿತ್ರೆಯೂ ಅವನ ಪರಾಕ್ರಮಕೃತ್ಯಗಳೂ ಇಸ್ರಾಯೇಲ್ ರಾಜ ಕಾಲವೃತ್ತಾಂತ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"6": "<sup>6</sup> ಅವನು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಲು ಅವನ ಶವವನ್ನು ತಿರ್ಚದಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ಏಲನು ಅರಸನಾದನು.",
			"7": "<sup>7</sup> ಬಾಷನು ಯಾರೊಬ್ಬಾಮನ ಮನೆಯವರಂತೆ ತನ್ನ ದುಷ್ಕೃತ್ಯಗಳಿಂದ ಯೆಹೋವನನ್ನು ರೇಗಿಸಿ ಆತನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾದದರಿಂದಲೂ ಅವನು ಯಾರೊಬ್ಬಾಮನ ಮನೆಯವರನ್ನು ನಿರ್ನಾಮಗೊಳಿಸಿದ್ದದರಿಂದಲೂ ಯೆಹೋವನು ಹನಾನೀಯ ಮಗನಾದ ಯೇಹುವಿನ ಮುಖಾಂತರವಾಗಿ ಅವನಿಗೂ ಅವನ ಕುಟುಂಬಕ್ಕೂ ದುರ್ಗತಿಯನ್ನು ಮುಂತಿಳಿಸಿದನು.",
			"8": "<sup>8</sup> ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಆಸನ ಆಳಿಕೆಯ ಇಪ್ಪತ್ತಾರನೆಯ ವರುಷದಲ್ಲಿ ಬಾಷನ ಮಗನಾದ ಏಲನು ತಿರ್ಚದಲ್ಲಿ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾಗಿ ಎರಡು ವರುಷ ಆಳಿದನು.",
			"9": "<sup>9</sup> ಅವನ ಸೇವಕನೂ ಅವನ ರಥಬಲದ ಅರ್ಧಭಾಗಕ್ಕೆ ಅಧಿಪತಿಯೂ ಆಗಿದ್ದ ಜಿಮ್ರಿ ಎಂಬವನು ಅವನಿಗೆ ವಿರೋಧವಾಗಿ ಒಳಸಂಚುಮಾಡಿದನು. ಅರಸನು ತಿರ್ಚದಲ್ಲಿ ತನ್ನ ಮನೆಯ ಉಗ್ರಾಣಿಕನಾದ ಅರ್ಚನೆಂಬವನ ಮನೆಯಲ್ಲಿ ಕುಡಿದು ಮತ್ತನಾಗಿರುವಾಗ",
			"10": "<sup>10</sup> ಜಿಮ್ರಿಯು ಅಲ್ಲಿಗೆ ಹೋಗಿ ಅವನ ಮೇಲೆ ಬಿದ್ದು ಅವನನ್ನು ಕೊಂದುಹಾಕಿ ಅವನಿಗೆ ಬದಲಾಗಿ ತಾನು ಅರಸನಾದನು. ಇದು ಯೆಹೂದದ ಅರಸನಾದ ಆಸನ ಆಳಿಕೆಯ ಇಪ್ಪತ್ತೇಳನೆಯ ವರುಷದಲ್ಲಿ ನಡೆಯಿತು.",
			"11": "<sup>11</sup> ಇವನು ಸಿಂಹಾಸನದ ಮೇಲೆ ಕೂತು ಆಳತೊಡಗಿದ ಕೂಡಲೆ ಬಾಷನ ಮನೆಯವರನ್ನೂ ಅವನ ಬಂಧು ಮಿತ್ರರನ್ನೂ ಸಂಹರಿಸಿದನು; ಅವರಲ್ಲಿ ಒಬ್ಬ ಗಂಡಸನ್ನೂ ಉಳಿಸಲಿಲ್ಲ.",
			"12": "<sup>12</sup> ಬಾಷನೂ ಅವನ ಮಗನಾದ ಏಲನೂ ತಾವು ಪಾಪಮಾಡಿದ್ದಲ್ಲದೆ ಇಸ್ರಾಯೇಲ್ಯರನ್ನೂ ಪಾಪಕ್ಕೆ ಪ್ರೇರಿಸಿ ತಮ್ಮ ವಿಗ್ರಹಗಳಿಂದ ಇಸ್ರಾಯೇಲ್ ದೇವರಾದ ಯೆಹೋವನನ್ನು ರೇಗಿಸಿದ್ದರಿಂದ",
			"13": "<sup>13</sup> ಆತನು ಪ್ರವಾದಿಯಾದ ಯೇಹುವಿನ ಮುಖಾಂತರವಾಗಿ ಬಾಷನ ಮನೆಗೆ ದುರ್ಗತಿಯನ್ನು ಮುಂತಿಳಿಸಿದ್ದನು. ಜಿಮ್ರಿಯು ಬಾಷನ ಮನೆಯವರೆಲ್ಲರನ್ನೂ ನಿರ್ನಾಮಗೊಳಿಸಿದಾಗ ಆ ಮಾತು ನೆರವೇರಿತು.",
			"14": "<sup>14</sup> ಏಲನ ಉಳಿದ ಚರಿತ್ರೆಯೂ ಅವನ ಎಲ್ಲಾ ಕೃತ್ಯಗಳೂ ಇಸ್ರಾಯೇಲ್‍ರಾಜಕಾಲವೃತ್ತಾಂತ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"15": "<sup>15</sup> ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಆಸನ ಆಳಿಕೆಯ ಇಪ್ಪತ್ತೇಳನೆಯ ವರುಷದಲ್ಲಿ ಜಿಮ್ರಿಯು ತಿರ್ಚದಲ್ಲಿ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾಗಿ ಏಳು ದಿವಸ ಆಳಿದನು. ಆ ಸಮಯದಲ್ಲಿ ಇಸ್ರಾಯೇಲ್ಯರು ಫಿಲಿಷ್ಟಿಯರ ವಶದಲ್ಲಿದ್ದ ಗಿಬ್ಬೆತೋನ್ ಎಂಬ ಪಟ್ಟಣಕ್ಕೆ ಮುತ್ತಿಗೆ ಹಾಕಿದ್ದರು.",
			"16": "<sup>16</sup> ಜಿಮ್ರಿಯು ಅರಸನಿಗೆ ವಿರೋಧವಾಗಿ ಒಳಸಂಚುಮಾಡಿ ಅವನನ್ನು ಕೊಂದುಹಾಕಿದನೆಂಬ ವರ್ತಮಾನವು ಪಾಳೆಯದಲ್ಲಿದ್ದ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಮುಟ್ಟಲಾಗಿ ಅವರು ಅದೇ ದಿವಸ ಅಲ್ಲಿಯೇ ಸೈನ್ಯಾಧಿಪತಿಯಾದ ಒಮ್ರಿಯನ್ನು ತಮ್ಮ ಅರಸನನ್ನಾಗಿ ನೇಮಿಸಿಕೊಂಡರು.",
			"17": "<sup>17</sup> ಇವನೂ ಇವನ ಜೊತೆಯಲ್ಲಿದ್ದ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರೂ ಗಿಬ್ಬೆತೋನನ್ನು ಬಿಟ್ಟುಹೋಗಿ ತಿರ್ಚಕ್ಕೆ ಮುತ್ತಿಗೆ ಹಾಕಿದರು.",
			"18": "<sup>18</sup> ಪಟ್ಟಣವು ಅವರ ವಶವಾದದ್ದನ್ನು ಜಿಮ್ರಿಯು ಕಂಡು ಅರಮನೆಯ ಗರ್ಭಗೃಹವನ್ನು ಹೊಕ್ಕು ಅರಮನೆಗೆ ಬೆಂಕಿಹೊತ್ತಿಸಿ ಸುಟ್ಟುಕೊಂಡು ಸತ್ತನು.",
			"19": "<sup>19</sup> ಅವನು ಯಾರೊಬ್ಬಾಮನಂತೆ ತಾನು ಪಾಪಮಾಡಿದ್ದಲ್ಲದೆ ಇಸ್ರಾಯೇಲ್ಯರನ್ನೂ ಪಾಪಕ್ಕೆ ಪ್ರೇರಿಸಿ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾದದರಿಂದ ಹೀಗಾಯಿತು.",
			"20": "<sup>20</sup> ಜಿಮ್ರಿಯ ಉಳಿದ ಚರಿತ್ರೆಯೂ ಅವನ ಒಳಸಂಚೂ ಇಸ್ರಾಯೇಲ್ ರಾಜಕಾಲವೃತ್ತಾಂತ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"21": "<sup>21</sup> ಆ ಕಾಲದಲ್ಲಿ ಇಸ್ರಾಯೇಲ್ಯರೊಳಗೆ ಎರಡು ಪಕ್ಷಗಳುಂಟಾದವು; ಒಂದು ಪಕ್ಷದವರು ಗೀನತನ ಮಗನಾದ ತಿಬ್ನಿಯನ್ನು ಹಿಂಬಾಲಿಸಿ ಅವನನ್ನು ಅರಸನನ್ನಾಗಿ ಮಾಡಿಕೊಳ್ಳಬೇಕೆಂದಿದ್ದರು; ಇನ್ನೊಂದು ಪಕ್ಷದವರು ಒಮ್ರಿಯನ್ನು ಹಿಂಬಾಲಿಸಿದರು.",
			"22": "<sup>22</sup> ಆದರೆ ಒಮ್ರಿಯ ಪಕ್ಷದವರು ಗೀನತನ ಮಗನಾದ ತಿಬ್ನಿಯ ಪಕ್ಷದವರನ್ನು ಸೋಲಿಸಿದರು. ತಿಬ್ನಿಯು ಸಾಯಲಾಗಿ ಒಮ್ರಿಯೇ ಆಳುವವನಾದನು.",
			"23": "<sup>23</sup> ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಆಸನ ಆಳಿಕೆಯ ಮೂವತ್ತೊಂದನೆಯ ವರುಷದಲ್ಲಿ ಒಮ್ರಿಯು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾಗಿ ಹನ್ನೆರಡು ವರುಷ ಆಳಿದನು. ಆರು ವರುಷಗಳವರೆಗೆ ತಿರ್ಚವೇ ಅವನ ರಾಜಧಾನಿಯಾಗಿತ್ತು.",
			"24": "<sup>24</sup> ಅನಂತರ ಶೆಮೆರ್ ಎಂಬವನಿಗೆ ಎರಡು ತಲಾಂತು ಬೆಳ್ಳಿಯನ್ನು ಕೊಟ್ಟು ಅವನಿಂದ ಸಮಾರ್ಯವೆಂಬ ಗುಡ್ಡವನ್ನು ಕೊಂಡುಕೊಂಡು ಅದರ ಮೇಲೆ ಒಂದು ಪಟ್ಟಣವನ್ನು ಕಟ್ಟಿಸಿ ಅದಕ್ಕೆ ಆ ಭೂಮಿಯ ಒಡೆಯನಾಗಿದ್ದ ಶೆಮೆರನ ಜ್ಞಾಪಕಾರ್ಥವಾಗಿ ಸಮಾರ್ಯವೆಂಬ ಹೆಸರಿಟ್ಟನು.",
			"25": "<sup>25</sup> ಇವನು ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯೂ ತನ್ನ ಪೂರ್ವಾಧಿಕಾರಿಗಳೆಲ್ಲರಿಗಿಂತ ದುಷ್ಟನೂ ಆಗಿದ್ದನು.",
			"26": "<sup>26</sup> ಇಸ್ರಾಯೇಲ್ಯರು ವಿಗ್ರಹಗಳಿಂದ ತಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ರೇಗಿಸುವಂತೆ ಅವರನ್ನು ಪಾಪಕ್ಕೆ ಪ್ರೇರಿಸಿದ ನೆಬಾಟನ ಮಗನಾದ ಯಾರೊಬ್ಬಾಮನ ಮಾರ್ಗದಲ್ಲಿ ಇವನೂ ನಡೆದನು.",
			"27": "<sup>27</sup> ಇವನ ಉಳಿದ ಚರಿತ್ರೆಯೂ ಪರಾಕ್ರಮಕೃತ್ಯಗಳೂ ಇಸ್ರಾಯೇಲ್‍ರಾಜಕಾಲವೃತ್ತಾಂತ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"28": "<sup>28</sup> ಒಮ್ರಿಯು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಲು ಇವನ ಶವವನ್ನು ಸಮಾರ್ಯ ಪಟ್ಟಣದಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಇವನಿಗೆ ಬದಲಾಗಿ ಇವನ ಮಗನಾದ ಅಹಾಬನು ಅರಸನಾದನು.",
			"29": "<sup>29</sup> ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಆಸನ ಆಳಿಕೆಯ ಮೂವತ್ತೆಂಟನೆಯ ವರುಷದಲ್ಲಿ ಒಮ್ರಿಯ ಮಗನಾದ ಅಹಾಬನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾಗಿ ಸಮಾರ್ಯಪಟ್ಟಣದಲ್ಲಿ ಇಪ್ಪತ್ತೆರಡು ವರುಷ ಆಳಿದನು.",
			"30": "<sup>30</sup> ಇವನು ತನ್ನ ಪೂರ್ವಾಧಿಕಾರಿಗಳೆಲ್ಲರಿಗಿಂತಲೂ ದುಷ್ಟನಾಗಿ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾದನು.",
			"31": "<sup>31</sup> ಇವನು ನೆಬಾಟನ ಮಗನಾದ ಯಾರೊಬ್ಬಾಮನ ಮಾರ್ಗದಲ್ಲಿ ನಡೆಯುವದು ಅಲ್ಪಪಾಪವೆಂದು ತಿಳಿದವನೋ ಎಂಬಂತೆ ಚೀದೋನ್ಯರ ಅರಸನಾದ ಎತ್ಬಾಳನ ಮಗಳು ಈಜೆಬೆಲ್ ಎಂಬಾಕೆಯನ್ನು ಮದುವೆ ಮಾಡಿಕೊಂಡು ಬಾಳ್‍ದೇವರನ್ನು ಪೂಜಿಸತೊಡಗಿದನು.",
			"32": "<sup>32</sup> ಬಾಳ್‍ದೇವರಿಗೋಸ್ಕರ ಸಮಾರ್ಯದಲ್ಲಿ ಒಂದು ಗುಡಿ ಕಟ್ಟಿಸಿ ಅದರಲ್ಲಿ ಯಜ್ಞವೇದಿಯನ್ನು ಮಾಡಿಸಿದನು.",
			"33": "<sup>33</sup> ಇದಲ್ಲದೆ ಅಶೇರವಿಗ್ರಹಸ್ತಂಭವನ್ನು ನಿಲ್ಲಿಸಿದನು; ಹೀಗೆ ಇನ್ನು ಎಷ್ಟೋ ಕೆಟ್ಟ ಕೆಲಸಗಳನ್ನು ಮಾಡಿ ಮುಂಚೆ ಇದ್ದ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ ರಾಜರಿಗಿಂತಲೂ ಹೆಚ್ಚಾಗಿ ಇಸ್ರಾಯೇಲ್ ದೇವರಾದ ಯೆಹೋವನನ್ನು ರೇಗಿಸಿದನು.",
			"34": "<sup>34</sup> ಇವನ ಕಾಲದಲ್ಲಿ ಬೇತೇಲಿನವನಾದ ಹೀಯೇಲನು ಯೆರಿಕೋಪಟ್ಟಣವನ್ನು ಕಟ್ಟಿಸಿದನು. ಅವನು ಅದಕ್ಕೆ ಅಸ್ತಿವಾರ ಹಾಕುವಾಗ ಹಿರೀಮಗನಾದ ಅಬೀರಾಮನನ್ನೂ ಬಾಗಲುಗಳನ್ನಿಡುವಾಗ ಕಿರೀಮಗನಾದ ಸೆಗೂಬನನ್ನೂ ಕಳಕೊಂಡನು. ಹೀಗೆ ಯೆಹೋವನು ನೂನನ ಮಗನಾದ ಯೆಹೋಶುವನಿಂದ ಹೇಳಿಸಿದ ಮಾತು ನೆರವೇರಿತು."
		},
		"17": {
			"1": "<sup>1</sup> ಗಿಲ್ಯಾದಿನ ಪ್ರವಾಸಿಗಳಲ್ಲಿ ತಿಷ್ಬೀಯನಾದ ಎಲೀಯ ಎಂಬವನು ಅಹಾಬನಿಗೆ - ನಾನು ಸನ್ನಿಧಿಸೇವೆ ಮಾಡುತ್ತಿರುವ ಇಸ್ರಾಯೇಲ್‍ದೇವರಾದ ಯೆಹೋವನಾಣೆ, ನಾನು ಸೂಚಿಸಿದ ಹೊರತು ಇಂದಿನಿಂದ ಕೆಲವು ವರುಷಗಳವರೆಗೆ ಮಳೆಯಾಗಲಿ ಮಂಜಾಗಲಿ ಬೀಳುವದಿಲ್ಲ ಅಂದನು.",
			"2": "<sup>2</sup> ತರುವಾಯ ಯೆಹೋವನು ಎಲೀಯನಿಗೆ - ನೀನು ಈ ಸ್ಥಳವನ್ನು ಬಿಟ್ಟು ಪೂರ್ವದಿಕ್ಕಿಗೆ ಹೋಗಿ ಯೊರ್ದನ್‍ಹೊಳೆಯ ಆಚೆಯಲ್ಲಿರುವ ಕೆರೀತ್‍ಹಳ್ಳದಲ್ಲಿ ಅಡಗಿಕೋ.",
			"4": "<sup>4</sup> ಆ ಹಳ್ಳದ ನೀರು ನಿನಗೆ ಪಾನವಾಗಿರುವದು; ನಿನಗೆ ಆಹಾರತಂದುಕೊಡಬೇಕೆಂದು ಕಾಗೆಗಳಿಗೆ ಆಜ್ಞಾಪಿಸಿದ್ದೇನೆ ಎಂದು ಹೇಳಿದನು.",
			"5": "<sup>5</sup> ಅವನು ಯೆಹೋವನ ಅಪ್ಪಣೆಯಂತೆ ಯೊರ್ದನಿನ ಪೂರ್ವಕ್ಕಿರುವ ಕೆರೀತ್‍ಹಳ್ಳದಲ್ಲಿ ವಾಸಿಸಿದನು.",
			"6": "<sup>6</sup> ಕಾಗೆಗಳು ಅವನಿಗೆ ಪ್ರಾತಃಕಾಲದಲ್ಲಿಯೂ ಸಾಯಂಕಾಲದಲ್ಲಿಯೂ ರೊಟ್ಟಿ, ಮಾಂಸ ಇವುಗಳನ್ನು ತಂದುಕೊಡುತ್ತಿದ್ದವು. ಅವನು ಇವುಗಳನ್ನು ತಿನ್ನುತ್ತಿದ್ದನು; ಹಳ್ಳದ ನೀರು ಅವನಿಗೆ ಪಾನವಾಗಿತ್ತು.",
			"7": "<sup>7</sup> ದೇಶದಲ್ಲಿ ಮಳೆಯಿಲ್ಲದ್ದರಿಂದ ಕೆಲವು ದಿವಸಗಳಾದನಂತರ ಹಳ್ಳವು ಬತ್ತಿಹೋಯಿತು.",
			"8": "<sup>8</sup> ಆಗ ಯೆಹೋವನು ಅವನಿಗೆ - ನೀನು ಇಲ್ಲಿಂದ ಚೀದೋನ್ಯರ ಚಾರೆಪ್ತಾ ಊರಿಗೆ ಹೊರಟುಹೋಗಿ ಅಲ್ಲಿ ವಾಸಿಸು. ನಿನ್ನನ್ನು ಸಾಕಬೇಕೆಂದು ಅಲ್ಲಿನ ಒಬ್ಬ ವಿಧವೆಗೆ ಆಜ್ಞಾಪಿಸಿದ್ದೇನೆ ಅಂದನು.",
			"10": "<sup>10</sup> ಅವನು ಅಲ್ಲಿಂದ ಹೊರಟು ಚಾರೆಪ್ತದ ಊರುಬಾಗಲಿನ ಸಮೀಪಕ್ಕೆ ಬಂದಾಗ ಸೌದೆಯನ್ನು ಕೂಡಿಸುತ್ತಿರುವ ಒಬ್ಬ ವಿಧವೆಯನ್ನು ಕಂಡು ಆಕೆಯನ್ನು ಕೂಗಿ - ದಯವಿಟ್ಟು ಕುಡಿಯುವದಕ್ಕೆ ಒಂದು ತಂಬಿಗೆಯಲ್ಲಿ ನೀರು ತೆಗೆದುಕೊಂಡು ಬಾ ಎಂದು ಹೇಳಿದನು.",
			"11": "<sup>11</sup> ಆಕೆಯು ಹೋಗುತ್ತಿರುವಾಗ ತಿರಿಗಿ ಆಕೆಯನ್ನು ಕರೆದು - ನೀನು ಬರುವಾಗ ನನಗೋಸ್ಕರ ಒಂದು ತುಂಡು ರೊಟ್ಟಿಯನ್ನೂ ತೆಗೆದುಕೊಂಡು ಬಾ ಅಂದನು.",
			"12": "<sup>12</sup> ಆಕೆಯು ಅವನಿಗೆ - ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನಾಣೆ, ನನ್ನ ಹತ್ತಿರ ರೊಟ್ಟಿಯಿರುವದಿಲ್ಲ. ಮಡಕೆಯಲ್ಲಿ ಒಂದು ಹಿಡಿ ಹಿಟ್ಟು, ಮೊಗೆಯಲ್ಲಿ ಸ್ವಲ್ಪ ಎಣ್ಣೆ ಇವುಗಳ ಹೊರತಾಗಿ ಬೇರೇನೂ ಇರುವದಿಲ್ಲ. ಈಗ ಎರಡು ಕಟ್ಟಿಗೆಗಳನ್ನು ಹೆಕ್ಕಿ ನನಗೋಸ್ಕರವೂ ನನ್ನ ಮಗನಿಗೋಸ್ಕರವೂ ರೊಟ್ಟಿ ಮಾಡುತ್ತೇನೆ. ಅದನ್ನು ತಿಂದ ಮೇಲೆ ನಾವು ಸಾಯಬೇಕೇ ಹೊರತು ಬೇರೆ ಗತಿಯಿಲ್ಲ ಎಂದು ಉತ್ತರಕೊಟ್ಟಳು.",
			"13": "<sup>13</sup> ಆಗ ಎಲೀಯನು ಆಕೆಗೆ - ಹೆದರಬೇಡ; ನೀನು ಹೇಳಿದಂತೆ ಮಾಡು, ಆದರೆ ಮೊದಲು ಅದರಿಂದ ನನಗೋಸ್ಕರ ಒಂದು ಚಿಕ್ಕ ರೊಟ್ಟಿಯನ್ನು ಮಾಡಿಕೊಂಡು ಬಾ; ತರುವಾಯ ನಿನಗೂ ನಿನ್ನ ಮಗನಿಗೂ ಮಾಡಿಕೋ.",
			"14": "<sup>14</sup> ಇಸ್ರಾಯೇಲ್ ದೇವರಾದ ಯೆಹೋವನು ನಿನಗೆ - ನಾನು ದೇಶಕ್ಕೆ ಮಳೆಯನ್ನು ಕಳುಹಿಸುವವರೆಗೆ ನಿನ್ನ ಮಡಕೆಯಲ್ಲಿರುವ ಹಿಟ್ಟು ತೀರುವದಿಲ್ಲ, ಮೊಗೆಯಲ್ಲಿರುವ ಎಣ್ಣೆಯು ಮುಗಿದುಹೋಗುವದಿಲ್ಲ ಎಂದು ಹೇಳುತ್ತಾನೆ ಅಂದನು.",
			"15": "<sup>15</sup> ಆಕೆಯು ಹೋಗಿ ಅವನು ಹೇಳಿದಂತೆಯೇ ಮಾಡಿದಳು; ಆಕೆಯೂ ಆಕೆಯ ಮನೆಯವರೂ ಎಲೀಯನೂ ಅದನ್ನು ಅನೇಕ ದಿವಸಗಳವರೆಗೆ ಊಟಮಾಡಿದರು.",
			"16": "<sup>16</sup> ಯೆಹೋವನು ಎಲೀಯನ ಮುಖಾಂತರವಾಗಿ ಹೇಳಿದಂತೆ ಮಡಕೆಯಲ್ಲಿದ್ದ ಹಿಟ್ಟು ತೀರಲಿಲ್ಲ; ಮೊಗೆಯಲ್ಲಿದ್ದ ಎಣ್ಣೆಯು ಮುಗಿದುಹೋಗಲಿಲ್ಲ.",
			"17": "<sup>17</sup> ಕೆಲವು ದಿನಗಳಾದನಂತರ ಮನೆಯ ಯಜಮಾನಿಯಾದ ಆ ಸ್ತ್ರೀಯ ಮಗನು ಅಸ್ವಸ್ಥನಾದನು; ಹುಡುಗನಿಗೆ ರೋಗವು ಹೆಚ್ಚಾದದರಿಂದ ಉಸಿರಾಡುವುದು ನಿಂತು ಹೋಯಿತು.",
			"18": "<sup>18</sup> ಆಗ ಆ ಸ್ತ್ರೀಯು ಎಲೀಯನಿಗೆ - ದೇವರ ಮನುಷ್ಯನೇ, ನನ್ನ ಗೊಡವೆ ನಿನಗೇಕೆ? ನೀನು ನನ್ನ ಪಾಪವನ್ನು [ದೇವರ] ನೆನಪಿಗೆ ತಂದು ನನ್ನ ಮಗನನ್ನು ಸಾಯಿಸುವದಕ್ಕೆ ಬಂದಿಯೋ ಎನ್ನಲು ಅವನು ಆಕೆಗೆ -",
			"19": "<sup>19</sup> ನಿನ್ನ ಮಗನನ್ನು ನನಗೆ ಕೊಡು ಎಂದು ಹೇಳಿ ಅವನನ್ನು ಆಕೆಯ ಉಡಿಲಿನಿಂದ ತೆಗೆದುಕೊಂಡು ತಾನು ವಾಸವಾಗಿದ್ದ ಮೇಲಿನ ಕೋಣೆಗೆ ಹೋಗಿ ತನ್ನ ಮಂಚದ ಮೇಲೆ ಮಲಗಿಸಿದನು. ಅನಂತರ ಅವನು ಯೆಹೋವನಿಗೆ -",
			"20": "<sup>20</sup> ನನ್ನ ದೇವರಾದ ಯೆಹೋವನೇ, ನನಗೆ ಸ್ಥಳಕೊಟ್ಟ ಈ ವಿಧವೆಯ ಮಗನನ್ನು ನೀನು ಸಾಯಿಸಿ ಆಕೆಗೂ ಕೇಡನ್ನುಂಟುಮಾಡಿದ್ದೇನು ಎಂದು ಹೇಳಿ",
			"21": "<sup>21</sup> ಹುಡುಗನ ಮೇಲೆ ಮೂರು ಸಾರಿ ಬೋರ್ಲಬಿದ್ದು - ನನ್ನ ದೇವರಾದ ಯೆಹೋವನೇ, ಈ ಹುಡುಗನ ಪ್ರಾಣವು ತಿರಿಗಿ ಬರುವಂತೆ ಮಾಡು ಎಂಬದಾಗಿ ಆತನಿಗೆ ಮೊರೆಯಿಟ್ಟನು.",
			"22": "<sup>22</sup> ಯೆಹೋವನು ಅವನ ಪ್ರಾರ್ಥನೆಯನ್ನು ಕೇಳಿದದರಿಂದ ಹುಡುಗನ ಪ್ರಾಣವು ತಿರಿಗಿ ಬಂದು ಅವನು ಉಜ್ಜೀವಿಸಿದನು. ಎಲೀಯನು ಆ ಹುಡುಗನನ್ನು ಅಲ್ಲಿಂದ ಕೆಳಗೆ ತೆಗೆದುಕೊಂಡುಹೋಗಿ",
			"23": "<sup>23</sup> ಅವನ ತಾಯಿಗೆ ಒಪ್ಪಿಸಿ - ಇಗೋ, ನೋಡು; ನಿನ್ನ ಮಗನು ಜೀವಿಸುತ್ತಾನೆ ಅಂದನು.",
			"24": "<sup>24</sup> ಆಗ ಆಕೆಯು ಅವನಿಗೆ - ನೀನು ದೇವರ ಮನುಷ್ಯನೆಂದೂ ನಿನ್ನ ಬಾಯಿಂದ ಬಂದ ಯೆಹೋವನ ಮಾತು ಸತ್ಯವೆಂದೂ ಈಗ ನನಗೆ ಗೊತ್ತಾಯಿತು ಎಂದು ಹೇಳಿದಳು."
		},
		"18": {
			"1": "<sup>1</sup> ಅನೇಕ ದಿವಸಗಳಾದನಂತರ ಎಲೀಯನಿಗೆ ಯೆಹೋವನ ವಾಕ್ಯವುಂಟಾಯಿತು. ಬರಗಾಲದ ಮೂರನೆಯ ವರುಷದಲ್ಲಿ ಆತನು ಅವನಿಗೆ - ನೀನು ಹೋಗಿ ಅಹಾಬನನ್ನು ಕಾಣು; ನಾನು ದೇಶಕ್ಕೆ ಮಳೆ ಕೊಡುತ್ತೇನೆ ಅಂದನು.",
			"2": "<sup>2</sup> ಎಲೀಯನು ಅಹಾಬನನ್ನು ನೋಡುವದಕ್ಕೆ ಹೊರಟನು. ಆಗ ಸಮಾರ್ಯದಲ್ಲಿ ಬರವು ಬಹುಘೋರವಾಗಿದ್ದದರಿಂದ",
			"3": "<sup>3</sup> ಅಹಾಬನು ತನ್ನ ಉಗ್ರಾಣಿಕನಾದ ಓಬದ್ಯ ಎಂಬವನನ್ನು ಕರೇಕಳುಹಿಸಿದನು.",
			"4": "<sup>4</sup> (ಈಜೆಬೆಲಳು ಯೆಹೋವನ ಪ್ರವಾದಿಗಳನ್ನು ಸಂಹರಿಸುತ್ತಿರುವಾಗ ಯೆಹೋವನಲ್ಲಿ ಬಹುಭಯಭಕ್ತಿಯುಳ್ಳವನಾದ ಈ ಓಬದ್ಯನು ನೂರುಮಂದಿ ಪ್ರವಾದಿಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಅವರನ್ನು ಐವತ್ತೈವತ್ತು ಮಂದಿಯನ್ನಾಗಿ ಗವಿಗಳಲ್ಲಿ ಅಡಗಿಸಿಟ್ಟು ಅವರಿಗೆ ಅನ್ನಪಾನಗಳನ್ನು ಕೊಟ್ಟು ಸಾಕುತ್ತಿದ್ದನು.)",
			"5": "<sup>5</sup> ಅಹಾಬನು ಅವನಿಗೆ - ನಾವು ದೇಶಸಂಚಾರಮಾಡುತ್ತಾ ಎಲ್ಲಾ ಬುಗ್ಗೆ ಹಳ್ಳಗಳಿಗೆ ಹೋಗಿ ಅವುಗಳಲ್ಲಿ ಹುಲ್ಲು ಸಿಕ್ಕುತ್ತದೋ ನೋಡೋಣ; ಸಿಕ್ಕುವದಾದರೆ ನಮ್ಮ ಕುದುರೆಗಳೂ ಹೇಸರಕತ್ತೆಗಳೂ ಉಳಿಯುವವು; ಇಲ್ಲವಾದರೆ ಎಲ್ಲಾ ಸತ್ತುಹೋಗುವವು ಎಂದು ಹೇಳಿ",
			"6": "<sup>6</sup> ಸಂಚಾರಕ್ಕಾಗಿ ದೇಶವನ್ನು ಎರಡು ಭಾಗಮಾಡಿ ಒಂದು ಭಾಗಕ್ಕೆ ಓಬದ್ಯನನ್ನು ಕಳುಹಿಸಿ ಇನ್ನೊಂದು ಭಾಗಕ್ಕೆ ತಾನೇ ಹೊರಟುಹೋದನು.",
			"7": "<sup>7</sup> ಓಬದ್ಯನು ಪ್ರಯಾಣಮಾಡುತ್ತಿರುವಾಗ ಎಲೀಯನು ಫಕ್ಕನೆ ಅವನಿಗೆ ಎದುರಾದನು. ಓಬದ್ಯನು ಅವನ ಗುರುತು ಹಿಡಿದು ಸಾಷ್ಟಾಂಗನಮಸ್ಕಾರ ಮಾಡಿ - ನೀನು ನನ್ನ ಸ್ವಾಮಿಯಾದ ಎಲೀಯನೋ ಎಂದು ಕೇಳಲು ಅವನು -",
			"8": "<sup>8</sup> ಹೌದು; ನೀನು ಹೋಗಿ ನಿನ್ನ ಒಡೆಯನಿಗೆ ಎಲೀಯನು ಬಂದಿದ್ದಾನೆಂದು ಹೇಳು ಅಂದನು.",
			"9": "<sup>9</sup> ಅದಕ್ಕೆ ಓಬದ್ಯನು - ಅಹಾಬನು ನನ್ನನ್ನು ಕೊಲ್ಲುವ ಹಾಗೆ ನೀನು ನನ್ನನ್ನು ಅವನ ಕೈಗೆ ಒಪ್ಪಿಸುವದೇಕೆ? ನಾನೇನು ಪಾಪಮಾಡಿದೆನು?",
			"10": "<sup>10</sup> ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನಾಣೆ, ನನ್ನ ಒಡೆಯನು ಸೇವಕರನ್ನು ಅಟ್ಟಿ ನಿನ್ನನ್ನು ಹುಡುಕದ ಜನಾಂಗವೂ ರಾಜ್ಯವೂ ಒಂದೂ ಇರುವದಿಲ್ಲ. ಆ ಜನಾಂಗರಾಜ್ಯಗಳವರು ಎಲೀಯನು ನಮ್ಮಲ್ಲಿರುವದಿಲ್ಲವೆಂದು ಹೇಳಿದಾಗ ಅವನು ಅವರಿಂದ ಪ್ರಮಾಣಮಾಡಿಸಿದನು.",
			"11": "<sup>11</sup> ಹೀಗಿದ್ದರೂ ನೀನು ನನಗೆ - ಎಲೀಯನು ಬಂದಿದ್ದಾನೆಂದು ನಿನ್ನ ಒಡೆಯನಿಗೆ ತಿಳಿಸು ಎಂಬದಾಗಿ ಆಜ್ಞಾಪಿಸುವದೇನು?",
			"12": "<sup>12</sup> ನಾನು ನಿನ್ನನ್ನು ಬಿಟ್ಟು ಹೊರಟ ಕೂಡಲೆ ಯೆಹೋವನ ಆತ್ಮವು ನಿನ್ನನ್ನು ನನಗೆ ಗೊತ್ತಿಲ್ಲದ ಬೇರೊಂದು ಸ್ಥಳಕ್ಕೆ ಒಯ್ಯುವದು. ನಾನು ಹೋಗಿ ಅಹಾಬನಿಗೆ ಹೇಳುವಲ್ಲಿ ನೀನು ಅವನಿಗೆ ಸಿಕ್ಕದೆಹೋದರೆ ಅವನು ನನ್ನನ್ನು ಕೊಂದುಹಾಕುವನು. ನಾನು ಬಾಲ್ಯಾರಭ್ಯ ಯೆಹೋವನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವನಲ್ಲವೇ.",
			"13": "<sup>13</sup> ಈಜೆಬೆಲಳು ಯೆಹೋವನ ಪ್ರವಾದಿಗಳನ್ನು ಸಂಹರಿಸುತ್ತಿರುವಾಗ ನಾನು ಮಾಡಿದ್ದು ನನ್ನ ಸ್ವಾಮಿಯಾದ ನಿನಗೆ ತಿಳಿಸಲ್ಪಡಲಿಲ್ಲವೋ? ಅವರಲ್ಲಿ ನೂರು ಮಂದಿಯನ್ನು ತೆಗೆದುಕೊಂಡು ಐವತ್ತೈವತ್ತು ಮಂದಿಯನ್ನಾಗಿ ಗವಿಗಳಲ್ಲಿ ಅಡಗಿಸಿಟ್ಟು ಅನ್ನಪಾನಗಳನ್ನು ಕೊಟ್ಟು ಸಾಕಿದೆನಲ್ಲವೇ.",
			"14": "<sup>14</sup> ಹೀಗಿದ್ದರೂ ನೀನು ನನಗೆ - ಎಲೀಯನು ಬಂದಿದ್ದಾನೆಂಬದಾಗಿ ನಿನ್ನ ಒಡೆಯನಿಗೆ ತಿಳಿಸು ಎಂದು ಆಜ್ಞಾಪಿಸುತ್ತೀ; ಅವನು ನನ್ನನ್ನು ಕೊಲ್ಲುವನಲ್ಲವೋ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"15": "<sup>15</sup> ಆಗ ಎಲೀಯನು ಅವನಿಗೆ - ನಾನು ಸನ್ನಿಧಿ ಸೇವೆಮಾಡುತ್ತಿರುವ ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನಾಣೆ, ಈಹೊತ್ತು ಅಹಾಬನಿಗೆ ಹೇಗೂ ಕಾಣಿಸಿಕೊಳ್ಳುವೆನು ಅಂದನು.",
			"16": "<sup>16</sup> ಓಬದ್ಯನು ಅಹಾಬನ ಬಳಿಗೆ ಹೋಗಿ ಅವನಿಗೆ ಈ ಮಾತನ್ನು ತಿಳಿಸಿದನು. ಅಹಾಬನು ಎಲೀಯನನ್ನು ಎದುರುಗೊಳ್ಳುವದಕ್ಕಾಗಿ ಹೊರಟುಹೋಗಿ",
			"17": "<sup>17</sup> ಅವನನ್ನು ಕಂಡ ಕೂಡಲೆ - ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಆಪತ್ತನ್ನು ಬರಮಾಡಿದವನೇ, ನೀನು ಬಂದಿಯಾ ಅಂದನು.",
			"18": "<sup>18</sup> ಅದಕ್ಕೆ ಅವನು - ಅವರಿಗೆ ಆಪತ್ತನ್ನು ಬರಮಾಡಿದವನು ನಾನಲ್ಲ; ಯೆಹೋವನ ಆಜ್ಞೆಗಳನ್ನು ಉಲ್ಲಂಘಿಸಿ ಬಾಳನ ವಿಗ್ರಹಗಳನ್ನು ಪೂಜಿಸಿದ ನೀನೂ ನಿನ್ನ ಮನೆಯವರೂ ಅದಕ್ಕೆ ಕಾರಣರು.",
			"19": "<sup>19</sup> ನೀನು ಈಗ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರನ್ನೂ ಈಜೆಬೆಲಳಿಂದ ಪೋಷಣೆ ಹೊಂದುವ ಬಾಳನ ನಾನೂರೈವತ್ತು ಮಂದಿ ಪ್ರವಾದಿಗಳನ್ನೂ ಅಶೇರದೇವತೆಯ ನಾನೂರು ಮಂದಿ ಪ್ರವಾದಿಗಳನ್ನೂ ಕರ್ಮೆಲ್‍ಬೆಟ್ಟಕ್ಕೆ ಕರಿಸು ಎಂದು ಹೇಳಿದನು.",
			"20": "<sup>20</sup> ಅಹಾಬನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನೂ ಎಲ್ಲಾ ಪ್ರವಾದಿಗಳನ್ನೂ ಅಲ್ಲಿಗೆ ಕರಿಸಿದನು.",
			"21": "<sup>21</sup> ಎಲೀಯನು ಎಲ್ಲಾ ಜನರ ಹತ್ತಿರ ಹೋಗಿ ಅವರಿಗೆ - ನೀವು ಎಷ್ಟರವರೆಗೆ ಎರಡು ಮನಸ್ಸುಳ್ಳವರಾಗಿರುವಿರಿ? ಯೆಹೋವನು ದೇವರಾಗಿದ್ದರೆ ಆತನನ್ನೇ ಹಿಂಬಾಲಿಸಿರಿ; ಬಾಳನು ದೇವರಾಗಿದ್ದರೆ ಅವನನ್ನೇ ಹಿಂಬಾಲಿಸಿರಿ ಎಂದು ಹೇಳಿದನು. ಜನರು ಇದಕ್ಕೆ ಏನೂ ಉತ್ತರಕೊಡದೆ ಸುಮ್ಮನಿರುವದನ್ನು ಕಂಡು",
			"22": "<sup>22</sup> ಅವನು ತಿರಿಗಿ ಅವರಿಗೆ - ಯೆಹೋವನ ಪ್ರವಾದಿಗಳಲ್ಲಿ ನಾನೊಬ್ಬನೇ ಉಳಿದಿದ್ದೇನೆ; ಬಾಳನ ಪ್ರವಾದಿಗಳಲ್ಲಿ ನಾನೂರೈವತ್ತು ಮಂದಿ ಇದ್ದಾರೆ.",
			"23": "<sup>23</sup> ಅವರು ಎರಡು ಹೋರಿಗಳನ್ನು ನಮ್ಮ ಬಳಿಗೆ ತರಲಿ, ಅವುಗಳಲ್ಲೊಂದನ್ನು ಆರಿಸಿಕೊಂಡು ಕಡಿದು ತುಂಡುಮಾಡಿ ಕಟ್ಟಿಗೆಯ ಮೇಲಿಡಲಿ; ಆದರೆ ಬೆಂಕಿಹೊತ್ತಿಸಬಾರದು. ನಾನೂ ಇನ್ನೊಂದನ್ನು ಹಾಗೆಯೇ ಮಾಡಿ ಬೆಂಕಿ ಹೊತ್ತಿಸದೆ ಕಟ್ಟಿಗೆಯ ಮೇಲಿಡುವೆನು.",
			"24": "<sup>24</sup> ನೀವು ನಿಮ್ಮ ದೇವರ ಹೆಸರು ಹೇಳಿ ಪ್ರಾರ್ಥಿಸಿರಿ. ಅನಂತರ ನಾನು ಯೆಹೋವನ ಹೆಸರು ಹೇಳಿ ಪ್ರಾರ್ಥಿಸುವೆನು. ಆ ಇಬ್ಬರಲ್ಲಿ ಯಾವನು ಲಾಲಿಸಿ ಬೆಂಕಿಯನ್ನು ಕಳುಹಿಸುವನೋ ಅವನೇ ದೇವರೆಂದು ನಿಶ್ಚಯಿಸೋಣ ಅಂದನು. ಎಲ್ಲಾ ಜನರೂ - ಸರಿ, ನೀನು ಹೇಳಿದಂತೆಯೇ ಆಗಲಿ ಎಂದು ಉತ್ತರಕೊಟ್ಟರು.",
			"25": "<sup>25</sup> ಆಗ ಅವನು ಬಾಳನ ಪ್ರವಾದಿಗಳಿಗೆ - ನೀವು ಹೆಚ್ಚು ಮಂದಿ ಇರುವದರಿಂದ ಮೊದಲು ನೀವೇ ಒಂದು ಹೋರಿಯನ್ನು ಆರಿಸಿಕೊಂಡು ಅದನ್ನು ಸಿದ್ಧಪಡಿಸಿ ನಿಮ್ಮ ದೇವರ ಹೆಸರು ಹೇಳಿ ಪ್ರಾರ್ಥಿಸಿರಿ; ಆದರೆ ಬೆಂಕಿಯನ್ನು ಹೊತ್ತಿಸಕೂಡದು ಅಂದನು.",
			"26": "<sup>26</sup> ಅವರು ತರಲ್ಪಟ್ಟ ಹೋರಿಗಳಲ್ಲೊಂದನ್ನು ತೆಗೆದುಕೊಂಡು ಸಿದ್ಧಪಡಿಸಿ ತಮ್ಮ ದೇವರಾದ ಬಾಳನ ಹೆಸರು ಹೇಳಿ - ಬಾಳನೇ, ನಮಗೆ ಕಿವಿಗೊಡು ಎಂದು ಹೊತ್ತಾರೆಯಿಂದ ಮಧ್ಯಾಹ್ನದವರೆಗೆ ಕೂಗಿದರೂ ಆಕಾಶವಾಣಿಯಾಗಲಿಲ್ಲ; ಅವರು ವೇದಿಯ ಸುತ್ತಲು ಕುಣಿದಾಡಿದರೂ ಯಾರೂ ಉತ್ತರ ದಯಪಾಲಿಸಲಿಲ್ಲ.",
			"27": "<sup>27</sup> ಮಧ್ಯಾಹ್ನವಾದನಂತರ ಎಲೀಯನು ಅವರನ್ನು ಪರಿಹಾಸ್ಯಮಾಡಿ - ಗಟ್ಟಿಯಾಗಿ ಕೂಗಿರಿ; ಅವನು ದೇವರಾಗಿರುತ್ತಾನಲ್ಲಾ! ಈಗ ಒಂದು ವೇಳೆ ಅವನು ಧ್ಯಾನದಲ್ಲಿರಬಹುದು; ಇಲ್ಲವೆ ಯಾವದೋ ಕೆಲಸದಲ್ಲಿ ಅಥವಾ ಪ್ರಯಾಣದಲ್ಲಿ ಇರಬೇಕು. ಅದೂ ಇಲ್ಲದಿದ್ದರೆ ನಿದ್ರೆಮಾಡುತ್ತಿದ್ದಾನು, ಎಚ್ಚರವಾಗಬೇಕು ಎಂದು ಹೇಳಿದನು.",
			"28": "<sup>28</sup> ಅವರು ಗಟ್ಟಿಯಾಗಿ ಕೂಗಿ ತಮ್ಮ ಪದ್ಧತಿಯ ಪ್ರಕಾರ ಈಟಿಕತ್ತಿಗಳಿಂದ ರಕ್ತಸೋರುವಷ್ಟು ಗಾಯ ಮಾಡಿಕೊಂಡರು.",
			"29": "<sup>29</sup> ಮಧ್ಯಾಹ್ನದಿಂದ ನೈವೇದ್ಯ ಸಮರ್ಪಣೆಯ ಹೊತ್ತಿನವರೆಗೂ ಪರವಶರಾಗಿ ಕೂಗುತ್ತಾ ಇದ್ದರು. ಆದರೂ ಆಕಾಶವಾಣಿಯಾಗಲಿಲ್ಲ; ಯಾವನೂ ಅವರಿಗೆ ಉತ್ತರಕೊಡಲಿಲ್ಲ, ಅವರನ್ನು ಲಕ್ಷಿಸಲಿಲ್ಲ.",
			"30": "<sup>30</sup> ಅನಂತರ ಎಲೀಯನು ಎಲ್ಲಾ ಜನರನ್ನು ಹತ್ತಿರಕ್ಕೆ ಕರೆಯಲು ಅವರು ಬಂದರು. ಅವನು ಹಾಳಾಗಿದ್ದ ಅಲ್ಲಿನ ಯೆಹೋವವೇದಿಯನ್ನು ತಿರಿಗಿ ಕಟ್ಟಿಸಿದನು.",
			"31": "<sup>31</sup> ಯೆಹೋವನಿಂದ ಇಸ್ರಾಯೇಲನೆಂಬ ಹೆಸರು ಹೊಂದಿದ ಯಾಕೋಬನ ಮಕ್ಕಳಿಂದ ಉತ್ಪತ್ತಿಯಾದ ಕುಲಗಳ ಸಂಖ್ಯೆಗೆ ಸರಿಯಾಗಿ ಹನ್ನೆರಡು ಕಲ್ಲುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು",
			"32": "<sup>32</sup> ಯೆಹೋವನ ಹೆಸರಿಗೋಸ್ಕರ ಒಂದು ವೇದಿಯನ್ನು ಕಟ್ಟಿಸಿ ಅದರ ಸುತ್ತಲೂ ಇಪ್ಪತ್ತು ಸೇರು ಬೀಜವರೀ ನೆಲವನ್ನು ಅಗೆಸಿ ಕಾಲುವೆಮಾಡಿಸಿದನು.",
			"33": "<sup>33</sup> ಇದಲ್ಲದೆ ಕಟ್ಟಿಗೆಯನ್ನು ವೇದಿಯ ಮೇಲೆ ಕ್ರಮಪಡಿಸಿ ಹೋರಿಯನ್ನು ವಧಿಸಿ ತುಂಡುಮಾಡಿ ಅದರ ಮೇಲಿಟ್ಟನು. ಅನಂತರ ಜನರಿಗೆ - ನಾಲ್ಕು ಕೊಡ ನೀರು ತಂದು ಯಜ್ಞಮಾಂಸದ ಮೇಲೆಯೂ ಕಟ್ಟಿಗೆಯ ಮೇಲೆಯೂ ಹೊಯ್ಯಿರಿ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"34": "<sup>34</sup> ಅವರು ತೆಗೆದುಕೊಂಡು ಬರಲು ಇನ್ನೊಂದು ಸಾರಿ ತನ್ನಿರಿ ಎಂದು ಹೇಳಿದನು. ತಿರಿಗಿ ತಂದರು. ಅವನು ಮೂರನೆಯ ಸಾರಿ ಅದೇ ಪ್ರಕಾರವಾಗಿ ಆಜ್ಞಾಪಿಸಲು ಅವರು ಮತ್ತೊಮ್ಮೆ ತಂದು ಸುರಿದರು.",
			"35": "<sup>35</sup> ನೀರು ವೇದಿಯ ಸುತ್ತಲೂ ಹರಿಯಿತು; ಇದಲ್ಲದೆ ಅವನು ಕಾಲುವೆಯನ್ನು ನೀರಿನಿಂದ ತುಂಬಿಸಿದನು.",
			"36": "<sup>36</sup> ಸಂಧ್ಯಾನೈವೇದ್ಯದ ಹೊತ್ತಿಗೆ ಪ್ರವಾದಿಯಾದ ಎಲೀಯನು ವೇದಿಯ ಹತ್ತಿರ ಬಂದು - ಅಬ್ರಹಾಮ್ ಇಸಾಕ್ ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರೇ, ಯೆಹೋವನೇ, ನೀನೋಬ್ಬನೇ ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರಾಗಿರುತ್ತೀ ಎಂಬದನ್ನೂ ನಾನು ನಿನ್ನ ಸೇವಕನಾಗಿರುತ್ತೇನೆಂಬದನ್ನೂ ಇದನ್ನೆಲ್ಲಾ ನಿನ್ನ ಅಪ್ಪಣೆಯ ಮೇರೆಗೆ ಮಾಡಿದೆನೆಂಬದನ್ನೂ ಈಹೊತ್ತು ತೋರಿಸಿ ಕೊಡು.",
			"37": "<sup>37</sup> ಕಿವಿಗೊಡು; ಯೆಹೋವನೇ, ಕಿವಿಗೊಡು; ಯೆಹೋವನಾದ ನೀನೊಬ್ಬನೇ ದೇವರೂ ಈ ಜನರ ಮನಸ್ಸನ್ನು ನಿನ್ನ ಕಡೆಗೆ ತಿರುಗಿಸಿಕೊಳ್ಳುವವನೂ ಆಗಿರುತ್ತೀ ಎಂಬದನ್ನು ಇವರಿಗೆ ತಿಳಿಯಪಡಿಸು ಎಂದು ಪ್ರಾರ್ಥಿಸಿದನು.",
			"38": "<sup>38</sup> ಕೂಡಲೆ ಯೆಹೋವನ ಕಡೆಯಿಂದ ಬೆಂಕಿಬಿದ್ದು ಯಜ್ಞಮಾಂಸವನ್ನೂ ಕಟ್ಟಿಗೆಕಲ್ಲು ಮಣ್ಣುಗಳನ್ನೂ ದಹಿಸಿಬಿಟ್ಟು ಕಾಲುವೆಯಲ್ಲಿದ್ದ ನೀರನ್ನೆಲ್ಲಾ ಹೀರಿಬಿಟ್ಟಿತು.",
			"39": "<sup>39</sup> ಜನರೆಲ್ಲರೂ ಅದನ್ನು ಕಂಡು ಬೋರ್ಲಬಿದ್ದು - ಯೆಹೋವನೇ ದೇವರು, ಯೆಹೋವನೇ ದೇವರು ಎಂದು ಕೂಗಿದರು.",
			"40": "<sup>40</sup> ಆಗ ಎಲೀಯನು ಅವರಿಗೆ - ಬಾಳನ ಪ್ರವಾದಿಗಳೆಲ್ಲರನ್ನೂ ಹಿಡಿಯಿರಿ; ಅವರಲ್ಲಿ ಒಬ್ಬನೂ ತಪ್ಪಿಸಿಕೊಳ್ಳಬಾರದು ಎಂದು ಆಜ್ಞಾಪಿಸಲು ಅವರು ಹಿಡಿದರು. ಅವನು ಅವರನ್ನು ಕೀಷೋನ್‍ಹಳ್ಳಕ್ಕೆ ಒಯ್ದು ಅಲ್ಲಿ ಕೊಲ್ಲಿಸಿದನು.",
			"41": "<sup>41</sup> ಅನಂತರ ಅವನು ಅಹಾಬನಿಗೆ - ನೀನು ಮೇಲೆ ಹೋಗಿ ಅನ್ನಪಾನಗಳನ್ನು ತೆಗೆದುಕೋ; ದೊಡ್ಡಮಳೆಯ ಶಬ್ದವು ಕೇಳಿಸುತ್ತದೆ ಅಂದನು.",
			"42": "<sup>42</sup> ಅಹಾಬನು ಅನ್ನಪಾನಗಳನ್ನು ತೆಗೆದುಕೊಳ್ಳಲು ಹೋದಾಗ ಎಲೀಯನು ಕರ್ಮೆಲಿನ ತುದಿಗೆ ಹೋಗಿ ಅಲ್ಲಿ ನೆಲದ ಮೇಲೆ ಬಿದ್ದುಕೊಂಡು ಮೊಣಕಾಲಿನ ಮೇಲೆ ತಲೆಯನ್ನಿಟ್ಟನು.",
			"43": "<sup>43</sup> ಅವನು ತನ್ನ ಸೇವಕನಿಗೆ - ನೀನು ಮೇಲೆ ಹೋಗಿ ಸಮುದ್ರದ ಕಡೆಗೆ ನೋಡು ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು. ಸೇವಕನು ಹೋಗಿ ನೋಡಿ ಬಂದು ಏನೂ ತೋರುವದಿಲ್ಲ ಎಂದು ಹೇಳಿದನು. ಹೀಗೆ ಅವನನ್ನು ಏಳು ಸಾರಿ ಕಳುಹಿಸಿದನು.",
			"44": "<sup>44</sup> ಏಳನೆಯ ಸಾರಿ ಸೇವಕನು - ಅಂಗೈಯಷ್ಟು ಚಿಕ್ಕದಾದ ಮೋಡವು ಸಮುದ್ರದಿಂದ ಏರಿ ಬರುತ್ತಲಿದೆ ಎಂದು ತಿಳಿಸಿದನು. ಆಗ ಎಲೀಯನು ಅವನಿಗೆ - ನೀನು ಹೋಗಿ ಅಹಾಬನಿಗೆ - ಬೇಗನೆ ರಥವನ್ನು ಹೂಡಿಸಿಕೊಂಡು ಮಳೆಯು ನಿನ್ನನ್ನು ತಡೆಯದಂತೆ ಬೆಟ್ಟವನ್ನಿಳಿದು ಹೋಗು ಎಂದು ಹೇಳು ಅಂದನು.",
			"45": "<sup>45</sup> ತುಸುಹೊತ್ತಿನಲ್ಲಿಯೇ ಆಕಾಶವು ಮೋಡ ಗಾಳಿಗಳಿಂದ ಕಪ್ಪಾಗಿ ದೊಡ್ಡ ಮಳೆಯು ಪ್ರಾರಂಭವಾಯಿತು. ಅಹಾಬನು ರಥದಲ್ಲಿ ಕೂತುಕೊಂಡು ಇಜ್ರೇಲಿಗೆ ಹೋದನು.",
			"46": "<sup>46</sup> ಯೆಹೋವನ ಹಸ್ತವು ಎಲೀಯನ ಸಂಗಡ ಇದ್ದದರಿಂದ ಅವನು ನಡುಕಟ್ಟಿಕೊಂಡು ಅಹಾಬನ ಮುಂದೆ ಓಡುತ್ತಾ ಇಜ್ರೇಲನ್ನು ಸೇರಿದನು."
		},
		"19": {
			"1": "<sup>1</sup> ಎಲೀಯನು ಎಲ್ಲಾ ಪ್ರವಾದಿಗಳನ್ನು ಕತ್ತಿಯಿಂದ ಸಂಹರಿಸಿದ್ದನ್ನೂ ಅವನು ಮಾಡಿದ ಬೇರೆ ಎಲ್ಲಾ ಕಾರ್ಯಗಳನ್ನೂ ಅಹಾಬನು ಈಜೆಬೆಲಳಿಗೆ ತಿಳಿಸಿದಾಗ",
			"2": "<sup>2</sup> ಆಕೆಯು ಎಲೀಯನ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿ ಅವನಿಗೆ - ನೀನು ಪ್ರವಾದಿಗಳ ಪ್ರಾಣವನ್ನು ತೆಗೆದಂತೆ ನಾಳೆ ಇಷ್ಟು ಹೊತ್ತಿಗೆ ನಾನು ನಿನ್ನ ಪ್ರಾಣವನ್ನು ತೆಗೆಯದೆ ಹೋದರೆ ದೇವತೆಗಳು ನನಗೆ ಬೇಕಾದದ್ದನ್ನು ಮಾಡಲಿ ಎಂದು ಹೇಳಿಕಳುಹಿಸಿದಳು.",
			"3": "<sup>3</sup> ಅವನು ಇದನ್ನು ಕೇಳಿದೊಡನೆ ತನ್ನ ಪ್ರಾಣರಕ್ಷಣೆಗಾಗಿ ಅಲ್ಲಿಂದ ಹೊರಟು ಯೆಹೂದದ ಬೇರ್ಷೆಬಕ್ಕೆ ಬಂದು ಅಲ್ಲಿ ತನ್ನ ಸೇವಕನನ್ನು ಬಿಟ್ಟನು.",
			"4": "<sup>4</sup> ತರುವಾಯ ತಾನೊಬ್ಬನಾಗಿ ಅರಣ್ಯದೊಳಗೆ ಒಂದು ದಿವಸದ ಪ್ರಯಾಣದಷ್ಟು ದೂರ ಹೋಗಿ ಒಂದು ಜಾಲೀಗಿಡದ ಕೆಳಗೆ ಕೂತುಕೊಂಡು ಮರಣವನ್ನು ಅಪೇಕ್ಷಿಸಿದನು. ಅವನು - ಯೆಹೋವನೇ, ನನಗೆ ಸಾಕಾಯಿತು; ನನ್ನ ಪ್ರಾಣವನ್ನು ತೆಗೆದುಬಿಡು; ನಾನು ನನ್ನ ಪಿತೃಗಳಿಗಿಂತ ಉತ್ತಮನಲ್ಲ ಎಂದು ದೇವರನ್ನು ಪ್ರಾರ್ಥಿಸಿ",
			"5": "<sup>5</sup> ಅದೇ ಗಿಡದ ಕೆಳಗೆ ಮಲಗಿಕೊಂಡು ನಿದ್ರೆಮಾಡಿದನು. ಫಕ್ಕನೆ ಒಬ್ಬ ದೇವದೂತನು ಅವನನ್ನು ತಟ್ಟಿ - ಎದ್ದು ಊಟಮಾಡು ಎಂದು ಹೇಳಿದನು.",
			"6": "<sup>6</sup> ಎಲೀಯನು ಎದ್ದು ನೋಡಲಾಗಿ ಕೆಂಡದ ಮೇಲೆ ಸುಟ್ಟ ರೊಟ್ಟಿಯೂ ಒಂದು ತಂಬಿಗೆ ನೀರೂ ತನ್ನ ತಲೆಯ ಹತ್ತಿರ ಇರುವದನ್ನು ಕಂಡು ಅವುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ತಿಂದು ಕುಡಿದು ತಿರಿಗಿ ಮಲಗಿದನು.",
			"7": "<sup>7</sup> ಯೆಹೋವನ ದೂತನು ಎರಡನೆಯ ಸಾರಿ ಬಂದು ಅವನನ್ನು ತಟ್ಟಿ ಅವನಿಗೆ - ಎದ್ದು ಊಟಮಾಡು; ನೀನು ನಿನ್ನ ಶಕ್ತಿ ಮೀರುವಷ್ಟು ಪ್ರಯಾಣಮಾಡಬೇಕಾಗಿದೆ ಅಂದನು.",
			"8": "<sup>8</sup> ಅವನು ಎದ್ದು ತಿಂದು ಕುಡಿದು ಅದರ ಬಲದಿಂದ ನಾಲ್ವತ್ತು ದಿವಸ ಹಗಲಿರುಳು ಪ್ರಯಾಣಮಾಡಿ ದೇವಗಿರಿಯಾದ ಹೋರೇಬಿಗೆ ಮುಟ್ಟಿ",
			"9": "<sup>9</sup> ಅಲ್ಲಿನ ಒಂದು ಗವಿಯಲ್ಲಿ ಇಳುಕೊಂಡನು. ಆಗ ಅವನಿಗೆ ಯೆಹೋವನಿಂದ - ಎಲೀಯನೇ, ನೀನು ಇಲ್ಲೇನು ಮಾಡುತ್ತೀ ಎಂಬ ವಾಣಿಯು ಕೇಳಿಸಿತು.",
			"10": "<sup>10</sup> ಅವನು ಅದಕ್ಕೆ - ಸೇನಾಧೀಶ್ವರನಾದ ದೇವರೇ, ಯೆಹೋವನೇ, ಇಸ್ರಾಯೇಲ್ಯರು ನಿನ್ನ ನಿಬಂಧನೆಯನ್ನು ಮೀರಿದ್ದಾರೆ, ಯಜ್ಞವೇದಿಗಳನ್ನು ಕೆಡವಿಹಾಕಿದ್ದಾರೆ, ಪ್ರವಾದಿಗಳನ್ನು ಕತ್ತಿಯಿಂದ ಸಂಹರಿಸಿದ್ದಾರೆ, ನಾನೊಬ್ಬನೇ ಉಳಿದು ನಿನ್ನ ಗೌರವವನ್ನು ಕಾಪಾಡುವದರಲ್ಲಿ ಆಸಕ್ತನಾಗಿದ್ದೆನು; ಆದರೆ ಅವರು ನನ್ನ ಪ್ರಾಣವನ್ನೂ ತೆಗೆಯಬೇಕೆಂದಿದ್ದಾರೆ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"11": "<sup>11</sup> ಆಗ ತಿರಿಗಿ - ನೀನು ಹೊರಗೆ ಬಂದು ಬೆಟ್ಟದ ಮೇಲೆ ಯೆಹೋವನ ಮುಂದೆ ನಿಲ್ಲು ಎಂಬ ವಾಣಿಯಾಯಿತು. ಆಹಾ, ಯೆಹೋವನು ಅಲ್ಲಿ ಹಾದುಹೋದನು. ಆತನ ಮುಂದೆ ಪರ್ವತಗಳನ್ನು ಭೇದಿಸಿ ಬಂಡೆಗಳನ್ನು ಪುಡಿಪುಡಿಮಾಡುವಂಥ ದೊಡ್ಡ ಬಿರುಗಾಳಿಯು ಬೀಸಿತು; ಯೆಹೋವನು ಅದರಲ್ಲಿ ಇರಲಿಲ್ಲ. ತರುವಾಯ ಭೂಕಂಪವುಂಟಾಯಿತು; ಅದರಲ್ಲಿಯೂ ಆತನಿರಲಿಲ್ಲ.",
			"12": "<sup>12</sup> ಭೂಕಂಪವಾದನಂತರ ಸಿಡಿಲುಂಟಾಯಿತು; ಅದರಲ್ಲಿಯೂ ಯೆಹೋವನಿರಲಿಲ್ಲ.",
			"13": "<sup>13</sup> ಕಡೆಗೆ ಮಂದಮಾರುತ ಶಬ್ದ! ಅದನ್ನು ಕೇಳಿದ ಕೂಡಲೆ ಎಲೀಯನು ತನ್ನ ಕಂಬಳಿಯಿಂದ ಮೋರೆಯನ್ನು ಮುಚ್ಚಿಕೊಂಡು ಹೋಗಿ ಗವಿಯ ದ್ವಾರದಲ್ಲಿ ನಿಂತನು. ಆಗ - ಎಲೀಯನೇ, ನೀನು ಇಲ್ಲೇನು ಮಾಡುತ್ತೀ ಎಂಬ ವಾಣಿಯು ಕೇಳಿಸಿತು.",
			"14": "<sup>14</sup> ಅದಕ್ಕೆ ಅವನು - ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೇ, ಇಸ್ರಾಯೇಲ್ಯರು ನಿನ್ನ ನಿಬಂಧನೆಯನ್ನು ಮೀರಿದ್ದಾರೆ, ಯಜ್ಞವೇದಿಗಳನ್ನು ಕೆಡವಿಹಾಕಿದ್ದಾರೆ, ಪ್ರವಾದಿಗಳನ್ನು ಕತ್ತಿಯಿಂದ ಸಂಹರಿಸಿದ್ದಾರೆ, ನಾನೊಬ್ಬನೇ ಉಳಿದು ನಿನ್ನ ಗೌರವವನ್ನು ಕಾಪಾಡುವದರಲ್ಲಿ ಆಸಕ್ತನಾಗಿದ್ದೆನು; ಆದರೆ ಅವರು ನನ್ನ ಪ್ರಾಣವನ್ನೂ ತೆಗೆಯಬೇಕೆಂದಿದ್ದಾರೆ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"15": "<sup>15</sup> ಆಗ ಯೆಹೋವನು ಅವನಿಗೆ - ನೀನು ಬಂದ ದಾರಿಯಿಂದ ಹಿಂದಿರುಗಿ ದಮಸ್ಕದ ಅರಣ್ಯಕ್ಕೆ ಹೋಗು; ಅಲ್ಲಿಂದ ಪಟ್ಟಣದೊಳಗೆ ಪ್ರವೇಶಿಸಿ ಹಜಾಯೇಲನನ್ನು ಅರಾಮ್ಯರ ಅರಸನನ್ನಾಗಿ ಅಭಿಷೇಕಿಸು;",
			"16": "<sup>16</sup> ನಿಂಷಿಯ ಮಗನಾದ ಯೇಹುವನ್ನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನನ್ನಾಗಿಯೂ ಅಬೇಲ್‍ಮೆಹೋಲದವನೂ ಶಾಫಾಟನ ಮಗನೂ ಆದ ಎಲೀಷನನ್ನು ನಿನಗೆ ಬದಲಾಗಿ ಪ್ರವಾದಿಯನ್ನಾಗಿಯೂ ಅಭೀಷೇಕಿಸು.",
			"17": "<sup>17</sup> ಹಜಾಯೇಲನ ಕತ್ತಿಗೆ ತಪ್ಪಿಸಿಕೊಂಡವರನ್ನು ಯೇಹುವು ಕೊಲ್ಲುವನು; ಇವನ ಕತ್ತಿಗೆ ತಪ್ಪಿಸಿಕೊಂಡವರನ್ನು ಎಲೀಷನು ಕೊಲ್ಲುವನು.",
			"18": "<sup>18</sup> ಆದರೆ ಬಾಳನ ವಿಗ್ರಹಕ್ಕೆ ಅಡ್ಡಬೀಳದೆಯೂ ಅದನ್ನು ಮುದ್ದಿಡದೆಯೂ ಇರುವ ಏಳು ಸಾವಿರ ಮಂದಿ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಉಳಿಸುವೆನು ಎಂದು ಹೇಳಿದನು.",
			"19": "<sup>19</sup> ಎಲೀಯನು ಅಲ್ಲಿಂದ ಹೊರಟುಹೋಗಿ ಶಾಫಾಟನ ಮಗನಾದ ಎಲೀಷನನ್ನು ಕಂಡನು. ಅವನು ಹೊಲವನ್ನು ಉಳುವದಕ್ಕೆ ಹನ್ನೆರಡು ಜೋಡಿ ಎತ್ತುಗಳನ್ನು ತಂದು ಹನ್ನೆರಡನೆಯ ಜೋಡಿಯಿಂದ ತಾನಾಗಿ ಉಳುತ್ತಿದ್ದನು. ಎಲೀಯನು ಅಲ್ಲಿಂದ ಹಾದುಹೋಗುವಾಗ ತನ್ನ ಕಂಬಳಿಯನ್ನು ಅವನ ಮೇಲೆ ಹಾಕಿದನು.",
			"20": "<sup>20</sup> ಕೂಡಲೆ ಅವನು ಎತ್ತುಗಳನ್ನು ಬಿಟ್ಟು ಓಡುತ್ತಾ ಬಂದು ಎಲೀಯನಿಗೆ - ನನ್ನ ತಂದೆತಾಯಿಗಳನ್ನು ಮುದ್ದಿಟ್ಟು ಬರುವದಕ್ಕೆ ಅಪ್ಪಣೆಯಾಗಲಿ; ಅನಂತರ ನಿನ್ನನ್ನು ಹಿಂಬಾಲಿಸುವೆನು ಅಂದನು. ಅದಕ್ಕೆ ಎಲೀಯನು - ಹೋಗು; ನಾನು ನಿನಗೆ ಮಾಡಿದ್ದೇನು ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"21": "<sup>21</sup> ಎಲೀಷನು ಹಿಂದಿರುಗಿಹೋಗಿ ತಾನು ಉಳುತ್ತಿದ್ದ ಜೋಡಿ ಎತ್ತುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಅವುಗಳನ್ನು ವಧಿಸಿ ಮಾಂಸವನ್ನು ರಂಟೆಯ ಕಟ್ಟಿಗೆಯಿಂದ ಬೇಯಿಸಿ ಜನರಿಗೆ ಔತಣಮಾಡಿಸಿದನು. ಅನಂತರ ಅವನು ಎದ್ದು ಎಲೀಯನನ್ನು ಹಿಂಬಾಲಿಸಿ ಅವನ ಸೇವಕನಾದನು."
		},
		"20": {
			"1": "<sup>1</sup> ಅರಾಮ್ಯರ ಅರಸನಾದ ಬೆನ್ಹದದನು ತನ್ನ ಎಲ್ಲಾ ಸೈನ್ಯವನ್ನು ಕೂಡಿಸಿಕೊಂಡು ರಥಾಶ್ವಬಲಗಳಿಂದ ಕೂಡಿದ ಮೂವತ್ತೆರಡು ಮಂದಿ ಅರಸರೊಡನೆ ಹೊರಟು ಬಂದು ಸಮಾರ್ಯಪಟ್ಟಣಕ್ಕೆ ಮುತ್ತಿಗೆ ಹಾಕಿ ಯುದ್ಧಮಾಡಿದನು.",
			"2": "<sup>2</sup> ಅವನು ಆ ಪಟ್ಟಣದಲ್ಲಿದ್ದ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾದ ಅಹಾಬನ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿ ಅವನಿಗೆ -",
			"3": "<sup>3</sup> ನಿನ್ನ ಬೆಳ್ಳಿಬಂಗಾರವು ನನ್ನದು; ನಿನ್ನ ಪ್ರಿಯ ಸತಿಸುತರೂ ನನ್ನವರೇ ಎಂದು ತಿಳಿದುಕೋ ಎಂಬದಾಗಿ ಹೇಳಿಸಿದನು.",
			"4": "<sup>4</sup> ಅದಕ್ಕೆ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನು - ನನ್ನ ಒಡೆಯನಾದ ಅರಸನೇ, ನೀನು ಹೇಳಿದಂತೆ ನಾನು ನಿನ್ನವನೇ; ನನಗಿರುವದೆಲ್ಲವೂ ನಿನ್ನದೇ ಎಂದು ಉತ್ತರಕೊಟ್ಟು ಕಳುಹಿಸಿದನು.",
			"5": "<sup>5</sup> ಆ ದೂತರು ತಿರಿಗಿ ಅಹಾಬನ ಬಳಿಗೆ ಬಂದು ಅವನಿಗೆ - ನಿನ್ನ ಬೆಳ್ಳಿಬಂಗಾರವನ್ನೂ ಸತಿಸುತರನ್ನೂ ನನಗೆ ಒಪ್ಪಿಸಬೇಕೆಂದು ನಿನಗೆ ಹೇಳಿ ಕಳುಹಿಸಿದೆನಲ್ಲಾ.",
			"6": "<sup>6</sup> ನಾಳೆ ಇಷ್ಟುಹೊತ್ತಿಗೆ ನನ್ನ ಆಳುಗಳನ್ನು ಕಳುಹಿಸುವೆನು; ಅವರು ನಿನ್ನ ಮತ್ತು ನಿನ್ನ ಸೇವಕರ ಮನೆಗಳನ್ನು ಶೋಧಿಸಿ ನಿಮಗೆ ಇಷ್ಟವಾಗಿರುವವುಗಳನ್ನೆಲ್ಲಾ ತೆಗೆದುಕೊಳ್ಳುವರು ಎಂಬದಾಗಿ ಬೆನ್ಹದದನು ಅನ್ನುತ್ತಾನೆ ಎಂದು ತಿಳಿಸಿದರು.",
			"7": "<sup>7</sup> ಆಗ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನು ದೇಶದ ಎಲ್ಲಾ ಹಿರಿಯರನ್ನು ಕರಿಸಿ ಅವರಿಗೆ - ನೋಡಿದಿರಾ, ಅವನು ನಮಗೆ ಕೇಡು ಬಗೆಯುತ್ತಾನೆ; ನಿನ್ನ ಬೆಳ್ಳಿಬಂಗಾರವನ್ನೂ ಸತಿಸುತರನ್ನೂ ನನಗೆ ಕೊಡು ಎಂದು ಅವನು ಹೇಳಿಕಳುಹಿಸಿದ್ದಕ್ಕೆ ನಾನು ಒಪ್ಪಿದರೂ ಅವನಿಗೆ ತೃಪ್ತಿಯಾಗಲಿಲ್ಲ ಎಂದು ಹೇಳಲು",
			"8": "<sup>8</sup> ಎಲ್ಲಾ ಹಿರಿಯರೂ ಜನರೂ ಅವನಿಗೆ - ನೀನು ಈ ಮಾತಿಗೆ ಒಪ್ಪಬೇಡ ಅಂದರು.",
			"9": "<sup>9</sup> ಆದದರಿಂದ ಅವನು ಬಂದಂಥ ದೂತರ ಮುಖಾಂತರವಾಗಿ ಬೆನ್ಹದದನಿಗೆ - ನನ್ನ ಒಡೆಯನೇ, ಅರಸನೇ, ನಾನು ನಿನ್ನ ಮೊದಲನೆಯ ಮಾತಿಗೆ ಒಪ್ಪಿ ಅದರಂತೆ ಮಾಡಲು ಸಿದ್ಧನಾಗಿರುತ್ತೇನೆ; ಆದರೆ ಎರಡನೆಯ ಮಾತಿಗೆ ಒಪ್ಪಲಾರೆನು ಎಂದು ಹೇಳಿ ಕಳುಹಿಸಿದನು.",
			"10": "<sup>10</sup> ದೂತರು ಬಂದು ಈ ಮಾತುಗಳನ್ನು ಬೆನ್ಹದದನಿಗೆ ತಿಳಿಸಿದಾಗ ಅವನು ಅಹಾಬನಿಗೆ - ಸಮಾರ್ಯಪಟ್ಟಣದ ನಾಶನದಿಂದುಂಟಾಗುವ ಧೂಳು ಲೆಕ್ಕವಿಲ್ಲದ ನನ್ನ ಸೈನಿಕರ ಹಿಡಿತುಂಬವಷ್ಟಿದ್ದರೆ ದೇವತೆಗಳು ನನಗೆ ಬೇಕಾದದ್ದನ್ನು ಮಾಡಲಿ ಎಂದು ಹೇಳಿಕಳುಹಿಸಿದನು.",
			"11": "<sup>11</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನು ಬಂದ ದೂತರಿಗೆ - ಯುದ್ಧಕ್ಕಾಗಿ ನಡುಕಟ್ಟನ್ನು ಬಿಗಿದುಕೊಳ್ಳುವವನು ಅದನ್ನು ಬಿಚ್ಚಿಡುವ ಜಯಶಾಲಿಯಂತೆ ಹೆಚ್ಚಳಪಡಬಾರದು ಎಂದು ಅವನಿಗೆ ಹೇಳಿರಿ ಅಂದನು.",
			"12": "<sup>12</sup> ತನ್ನ ಅರಸುಗಳ ಸಂಗಡ ಡೇರೆಗಳಲ್ಲಿ ಮದ್ಯಪಾನಮಾಡುತ್ತಿದ್ದ ಬೆನ್ಹದದನು ಈ ಉತ್ತರವನ್ನು ಕೇಳುತ್ತಲೆ ಪಟ್ಟಣಕ್ಕೆ ಲಗ್ಗೆ ಹತ್ತುವಂತೆ ಸೈನಿಕರಿಗೆ ಆಜ್ಞಾಪಿಸಿದನು. ಅವರು ಹಾಗೆಯೇ ಮಾಡಿದರು.",
			"13": "<sup>13</sup> ಆಗ ಒಬ್ಬ ಪ್ರವಾದಿಯು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾದ ಅಹಾಬನ ಬಳಿಗೆ ಬಂದು ಅವನಿಗೆ - ಈ ಮಹಾ ಸಮೂಹವನ್ನು ನೋಡಿದಿಯಾ; ನಾನು ಯೆಹೋವನೇ ಎಂಬದು ನಿನಗೆ ಗೊತ್ತಾಗುವಂತೆ ಈಹೊತ್ತೇ ಇವರನ್ನೆಲ್ಲಾ ನಿನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸುವೆನು ಎಂಬದಾಗಿ ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ ಎಂದು ಹೇಳಿದನು.",
			"14": "<sup>14</sup> ಅಹಾಬನು ಅವನನ್ನು - ಇದು ಯಾರ ಮುಖಾಂತರವಾಗಿ ಆಗುವದು ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ ಅವನು - ಪ್ರದೇಶಾಧಿಪತಿಗಳ ಸೇವಕರ ಮುಖಾಂತರವಾಗಿ ಆಗುವದೆಂದು ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು. ಅಹಾಬನು ತಿರಿಗಿ ಅವನನ್ನು - ಯುದ್ಧಪ್ರಾರಂಭಮಾಡತಕ್ಕವರಾರು ಎಂದು ಕೇಳಲು ಅವನು - ನೀನೇ ಎಂದು ಹೇಳಿದನು.",
			"15": "<sup>15</sup> ಅಹಾಬನು ಪ್ರದೇಶಾಧಿಪತಿಗಳ ಆಳುಗಳನ್ನು ಲೆಕ್ಕಿಸಿದಾಗ ಇನ್ನೂರ ಮೂವತ್ತೆರಡು ಮಂದಿಯಿದ್ದರು; ಇಸ್ರಾಯೇಲ್ಯರ ಸೈನ್ಯವನ್ನು ಲೆಕ್ಕಿಸಿದಾಗ ಏಳು ಸಾವಿರ ಮಂದಿಯಿದ್ದರು.",
			"16": "<sup>16</sup> ಮಧ್ಯಾಹ್ನದಲ್ಲಿ ಬೆನ್ಹದದನು ತನ್ನ ಸಹಾಯಕ್ಕಾಗಿ ಬಂದ ಮೂವತ್ತೆರಡು ಮಂದಿ ಅರಸರ ಸಂಗಡ ಮದ್ಯಪಾನಮಾಡಿ ಮತ್ತನಾಗಿ ಡೇರೆಯಲ್ಲಿ ಕೂತುಕೊಂಡಿದ್ದಾಗ ಇವರು ಪಟ್ಟಣದಿಂದ ಹೊರಗೆ ಬಂದರು.",
			"17": "<sup>17</sup> ಪ್ರದೇಶಾಧಿಪತಿಗಳ ಆಳುಗಳು ಮೊದಲು ಬಂದರು. ಸಮಾರ್ಯದ ಜನರು ಹೊರಗೆ ಬಂದಿರುತ್ತಾರೆಂದು ಬೆನ್ಹದದನ ಕಾವಲುಗಾರರು ಅವನಿಗೆ ತಿಳಿಸಲು ಅವನು ಅವರಿಗೆ -",
			"18": "<sup>18</sup> ಅವರು ಯುದ್ಧಕ್ಕಾಗಿ ಬಂದಿದ್ದರೂ ಸಮಾಧಾನಕ್ಕಾಗಿ ಬಂದಿದ್ದರೂ ಅವರನ್ನು ಸಜೀವಿಗಳನ್ನಾಗಿ ಹಿಡಿದು ತನ್ನಿರಿ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"19": "<sup>19</sup> ಮೊದಲಾಗಿ ಹೊರಟುಬಂದ ಪ್ರದೇಶಾಧಿಪತಿಗಳ ಆಳುಗಳ ಹಿಂದೆ ಇಸ್ರಾಯೇಲ್‍ಸೈನ್ಯದವರೂ ಬಂದರು.",
			"20": "<sup>20</sup> ಅವರಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬನು ತನ್ನ ಎದುರಿಗೆ ಬಂದ ವಿರೋಧಿಯನ್ನು ಕೊಲ್ಲುತ್ತಾ ಮುಂದರಿದನು; ಅರಾಮ್ಯರು ಓಡಿಹೋಗಲು ಇಸ್ರಾಯೇಲ್ಯರು ಅವರನ್ನು ಹಿಂದಟ್ಟಿದರು. ಅವರ ಅರಸನಾದ ಬೆನ್ಹದದನೂ ಕೆಲವು ಮಂದಿ ರಾಹುತರೂ ಕುದುರೆಗಳನ್ನೇರಿ ಓಡಿಹೋಗಿ ತಪ್ಪಿಸಿಕೊಂಡರು.",
			"21": "<sup>21</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನು ಹೊರಟುಬಂದು ಅರಾಮ್ಯರ ರಥಾಶ್ವಬಲಗಳನ್ನು ಸೋಲಿಸಿ ಮಹಾಸಮೂಹವನ್ನು ಸಂಹರಿಸಿದನು.",
			"22": "<sup>22</sup> ಆಗ ಪ್ರವಾದಿಯು ತಿರಿಗಿ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನ ಬಳಿಗೆ ಬಂದು ಅವನಿಗೆ - ಅರಾಮ್ಯರ ಅರಸನು ಮುಂದಿನ ವರುಷದಲ್ಲಿ ಇನ್ನೊಮ್ಮೆ ನಿನಗೆ ವಿರೋಧವಾಗಿ ಬರುತ್ತಾನೆ; ಆದದರಿಂದ ನೀನು ಹೋಗಿ ನಿನ್ನನ್ನು ಬಲಪಡಿಸಿಕೋ; ಜಾಗರೂಕನಾಗಿದ್ದು ನೀನು ಮಾಡತಕ್ಕದ್ದೇನೆಂಬದನ್ನು ಪರ್ಯಾಲೋಚಿಸು ಅಂದನು.",
			"23": "<sup>23</sup> ಅರಾಮ್ಯರ ಅರಸನ ಮಂತ್ರಿಗಳು ತಮ್ಮ ಒಡೆಯನಿಗೆ - ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರು ಬೆಟ್ಟಗಳ ದೇವರಾಗಿರುವದರಿಂದ ಅವರು ನಮ್ಮನ್ನು ಸೊಲಿಸಿದರು; ನಾವು ಅವರೊಡನೆ ಬೈಲಿನಲ್ಲಿ ಯುದ್ಧಮಾಡುವದಾದರೆ ಹೇಗೂ ಜಯಹೊಂದುವೆವು.",
			"24": "<sup>24</sup> ನೀನು ಆಯಾಸ್ಥಳಗಳಲ್ಲಿರುವ ಅರಸರನ್ನು ತೆಗೆದುಹಾಕಿ ಅವರಿಗೆ ಬದಲಾಗಿ ಪ್ರದೇಶಾಧಿಪತಿಗಳನ್ನು ನೇಮಿಸು.",
			"25": "<sup>25</sup> ಅನಂತರ ನಮ್ಮ ಸೈನ್ಯದಿಂದ ನಷ್ಟವಾಗಿ ಹೋದಷ್ಟು ಜನರನ್ನೂ ಕುದುರೆಗಳನ್ನೂ ರಥಗಳನ್ನೂ ತಿರಿಗಿ ಕೂಡಿಸಿ ಅವರೊಡನೆ ಬೈಲಿನಲ್ಲಿ ಯುದ್ಧಮಾಡೋಣ; ನಮಗೆ ಹೇಗೂ ಜಯಸಿಕ್ಕುವದು ಎಂದು ಹೇಳಿದರು. ಅವನು ಅದರಂತೆಯೇ ಮಾಡಿದನು.",
			"26": "<sup>26</sup> ಒಂದು ವರುಷ ದಾಟಿದ ನಂತರ ಬೆನ್ಹದದನು ಅರಾಮ್ಯರನ್ನು ಕೂಡಿಸಿಕೊಂಡು ಇಸ್ರಾಯೇಲ್ಯರೊಡನೆ ಯುದ್ಧಮಾಡುವದಕ್ಕಾಗಿ ಅಫೇಕಕ್ಕೆ ಹೋದನು.",
			"27": "<sup>27</sup> ಇಸ್ರಾಯೇಲ್ಯರೂ ಕೂಡಿ ಆಹಾರ ಸಾಮಗ್ರಿಗಳನ್ನು ಒದಗಿಸಿಕೊಂಡು ಅವರಿಗೆ ವಿರೋಧವಾಗಿ ಬಂದರು. ಅರಾಮ್ಯರು ದೇಶದಲ್ಲೆಲ್ಲಾ ವ್ಯಾಪಿಸಿಕೊಂಡಿದ್ದರು. ಆಡುಮರಿಗಳ ಎರಡು ಹಿಂಡುಗಳಂತಿರುವ ಇಸ್ರಾಯೇಲ್ಯರು ಅವರ ಎದುರಿನಲ್ಲಿ ಪಾಳೆಯ ಮಾಡಿಕೊಂಡರು.",
			"28": "<sup>28</sup> ಆಗ ದೇವರ ಮನುಷ್ಯನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನ ಬಳಿಗೆ ಬಂದು ಅವನಿಗೆ - ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ಯೆಹೋವನು ತಗ್ಗುಗಳ ದೇವರಲ್ಲ, ಬೆಟ್ಟಗಳ ದೇವರಾಗಿರುತ್ತಾನೆ ಅಂದುಕೊಂಡು ಬಂದಿರುವ ಈ ಅರಾಮ್ಯರ ಮಹಾಸಮೂಹವನ್ನು ನಿನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸುವೆನು; ಇದರಿಂದ ನಾನು ಯೆಹೋವನೇ ಎಂದು ನಿನಗೆ ಗೊತ್ತಾಗುವದು ಎಂದು ಹೇಳಿದನು.",
			"29": "<sup>29</sup> ಈ ಎರಡು ಸೈನ್ಯಗಳೂ ಏಳು ದಿನಗಳವರೆಗೆ ಎದುರುಬದುರಾಗಿ ಪಾಳೆಯಮಾಡಿಕೊಂಡಿದ್ದವು. ಏಳನೆಯ ದಿನದಲ್ಲಿ ಯುದ್ಧ ಪ್ರಾರಂಭವಾಗಲು ಇಸ್ರಾಯೇಲ್ಯರು ಆ ಒಂದೇ ದಿನದಲ್ಲಿ ಅರಾಮ್ಯರ ಒಂದು ಲಕ್ಷ ಮಂದಿ ಕಾಲಾಳುಗಳನ್ನು ವಧಿಸಿದರು.",
			"30": "<sup>30</sup> ಉಳಿದ ಇಪ್ಪತ್ತೇಳು ಸಾವಿರ ಮಂದಿ ಸೈನಿಕರು ಅಫೇಕ ಪಟ್ಟಣವನ್ನು ಹೊಕ್ಕರು; ಆದರೆ ಆ ಪಟ್ಟಣದ ಗೋಡೆಯು ಅವರ ಮೇಲೆ ಬಿದ್ದದರಿಂದ ಅವರೂ ಸತ್ತರು. ಬೆನ್ಹದದನೂ ಆ ಪಟ್ಟಣವನ್ನು ಹೊಕ್ಕು ಒಂದು ಮನೆಯ ಒಳಕೋಣೆಯಲ್ಲಿ ಅಡಗಿಕೊಂಡನು.",
			"31": "<sup>31</sup> ಆಗ ಅವನ ಸೇವಕರು ಅವನಿಗೆ - ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸರು ದಯೆಯುಳ್ಳವರೆಂದು ನಾವು ಕೇಳಿದ್ದೇವೆ; ಆದದರಿಂದ ಸೊಂಟಕ್ಕೆ ಗೋಣೀತಟ್ಟು ಕಟ್ಟಿಕೊಂಡು ತಲೆಯ ಮೇಲೆ ಹಗ್ಗವನ್ನಿಟ್ಟುಕೊಂಡು ಆ ಅರಸನ ಬಳಿಗೆ ಹೋಗುವದಕ್ಕೆ ನಮಗೆ ಅಪ್ಪಣೆಯಾಗಲಿ; ಹೀಗೆ ಮಾಡುವದಾದರೆ ಅವನು ನಿನ್ನ ಜೀವವನ್ನು ಉಳಿಸಾನು ಎಂದು ಹೇಳಿದರು.",
			"32": "<sup>32</sup> ಅವರು ಸೊಂಟಕ್ಕೆ ಗೋಣೀತಟ್ಟು ಕಟ್ಟಿಕೊಂಡು ತಲೆಯ ಮೇಲೆ ಹಗ್ಗವನ್ನಿಟ್ಟುಕೊಂಡು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನ ಬಳಿಗೆ ಬಂದು ಅವನಿಗೆ - ನನಗೆ ಜೀವದಾನಮಾಡಬೇಕೆಂದು ನಿನ್ನ ಸೇವಕನಾದ ಬೆನ್ಹದದನು ವಿಜ್ಞಾಪಿಸುತ್ತಾನೆ ಅಂದರು. ಆಗ ಅವನು - ಬೆನ್ಹದದನು ಇನ್ನೂ ಜೀವದಿಂದಿರುತ್ತಾನೋ? ಅವನು ನನ್ನ ಸಹೋದರನು ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"33": "<sup>33</sup> ಅವರು ಈ ಮಾತು ಶುಭವಚನವೆಂದು ನೆನಸಿ ಕೂಡಲೆ ಅವನ ಮಾತನ್ನೇ ಹಿಡಿದು - ಬೆನ್ಹದದನು ನಿನ್ನ ಸಹೋದರನೇ ಹೌದು ಅಂದರು. ಅವನು ಅವರಿಗೆ - ಹೋಗಿ ಬೆನ್ಹದದನನ್ನು ಕರಕೊಂಡು ಬನ್ನಿರಿ ಎಂದು ಆಜ್ಞಾಪಿಸಲು ಅವನು ಬಂದಾಗ ಅವನನ್ನು ತನ್ನ ರಥದಲ್ಲಿ ಕುಳ್ಳಿರಿಸಿಕೊಂಡನು.",
			"34": "<sup>34</sup> ಬೆನ್ಹದದನು ಅವನಿಗೆ - ನನ್ನ ತಂದೆಯು ನಿನ್ನ ತಂದೆಯಿಂದ ಕಿತ್ತುಕೊಂಡ ಪಟ್ಟಣಗಳನ್ನು ನಾನು ಹಿಂದಕ್ಕೆ ಕೊಡುತ್ತೇನೆ; ಅವನು ಸಮಾರ್ಯದಲ್ಲಿ ಮಾಡಿದಂತೆ ನೀನೂ ನಿನಗೋಸ್ಕರ ದಮಸ್ಕದಲ್ಲಿ ಕೆಲವು ಕೇರಿಗಳನ್ನು ಮಾಡಿಸಿಕೊಳ್ಳಬಹುದು ಎಂದು ಹೇಳಲು ಅಹಾಬನು - ನಾನು ಈ ಒಪ್ಪಂದದ ಮೇಲೆ ನಿನ್ನನ್ನು ಬಿಟ್ಟುಬಿಡುತ್ತೇನೆ ಎಂದು ಹೇಳಿ ಅವನಿಂದ ಪ್ರಮಾಣಮಾಡಿಸಿ ಕಳುಹಿಸಿಬಿಟ್ಟನು.",
			"35": "<sup>35</sup> ಪ್ರವಾದಿಮಂಡಲಿಯವರಲ್ಲಿ ಒಬ್ಬನು ಯೆಹೋವ ಪ್ರೇರಿತನಾಗಿ ತನ್ನ ಜೊತೆಗಾರನಿಗೆ - ನನ್ನನ್ನು ಹೊಡಿ ಎಂದು ಹೇಳಿದನು. ಅದಕ್ಕೆ ಅವನು ಒಪ್ಪಲಿಲ್ಲ.",
			"36": "<sup>36</sup> ಆಗ ಪ್ರವಾದಿಯು ಅವನಿಗೆ - ನೀನು ಯೆಹೋವನ ಮಾತನ್ನು ಕೇಳದೆಹೋದದರಿಂದ ನನ್ನನ್ನು ಬಿಟ್ಟು ಹೊರಟ ಕೂಡಲೆ ಒಂದು ಸಿಂಹವು ಬಂದು ನಿನ್ನನ್ನು ಕೊಲ್ಲುವದು ಅಂದನು. ಅವನು ಇವನನ್ನು ಬಿಟ್ಟು ಹೋದ ಕೂಡಲೆ ಒಂದು ಸಿಂಹವು ಬಂದು ಅವನನ್ನು ಕೊಂದುಹಾಕಿತು.",
			"37": "<sup>37</sup> ಅನಂತರ ಆ ಪ್ರವಾದಿಯು ಇನ್ನೊಬ್ಬನನ್ನು ನೋಡಿ - ನನ್ನನ್ನು ಹೊಡಿ ಎಂದು ಅವನನ್ನು ಬೇಡಿಕೊಂಡನು. ಅವನು ಇವನನ್ನು ಗಾಯವಾಗುವಷ್ಟು ಹೊಡೆದನು.",
			"38": "<sup>38</sup> ಆಗ ಇವನು ತನ್ನ ಗುರುತು ತಿಳಿಯಬಾರದೆಂದು ಮುಂಡಾಸದಿಂದ ಕಣ್ಣುಗಳನ್ನು ಮುಚ್ಚಿಕೊಂಡು ಅರಸನು ಬರುವ ದಾರಿಯಲ್ಲಿ ನಿಂತು",
			"39": "<sup>39</sup> ಅವನು ಹಾದುಹೋಗುವಾಗ ಅವನಿಗೆ - ನಿನ್ನ ಸೇವಕನಾದ ನಾನು ಯುದ್ಧಕ್ಕೆ ಹೋದಾಗ ರಣರಂಗದಲ್ಲಿ ಒಬ್ಬನು ಇನ್ನೊಬ್ಬನನ್ನು ಹಿಡಿದುಕೊಂಡು ನನ್ನ ಬಳಿಗೆ ಬಂದು - ನೀನು ಇವನನ್ನು ಕಾಯಬೇಕು; ಇವನು ತಪ್ಪಿಸಿಕೊಂಡರೆ ಇವನ ಪ್ರಾಣಕ್ಕೆ ಬದಲಾಗಿ ನಿನ್ನ ಪ್ರಾಣವು ನನ್ನದಾಗಿರುವದು; ಅಥವಾ ನೀನು ನನಗೆ ಒಂದು ತಲಾಂತು ಬೆಳ್ಳಿಯನ್ನು ಕೊಡಬೇಕು ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"40": "<sup>40</sup> ನಾನು ಕೆಲಸದಲ್ಲಿದ್ದಾಗ ಅವನು ತಪ್ಪಿಸಿಕೊಂಡು ಹೋದನು ಎಂದು ಮೊರೆಯಿಟ್ಟನು. ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನು ಇವನಿಗೆ - ನಿನಗೆ ಆ ತೀರ್ಪು ಸರಿಯಾಗಿದೆ; ತೀರ್ಮಾನಿಸಿದವನು ನೀನೇ ಅಲ್ಲವೋ ಎಂದು ನುಡಿದನು.",
			"41": "<sup>41</sup> ಕೂಡಲೇ ಆ ಪ್ರವಾದಿಯು ಕಣ್ಣುಗಳ ಮೇಲಿನಿಂದ ಮುಂಡಾಸವನ್ನು ತೆಗೆದದರಿಂದ ಅವನು ಪ್ರವಾದಿಗಳಲ್ಲೊಬ್ಬನೆಂದು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಿಗೆ ಗೊತ್ತಾಯಿತು.",
			"42": "<sup>42</sup> ಪ್ರವಾದಿಯು ಅರಸನಿಗೆ - ಸಂಹರಿಸಬೇಕೆಂದು ಹೇಳಿ ನಾನು ನಿನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸಿದ ಮನುಷ್ಯನನ್ನು ನೀನು ಹೋಗಗೊಟ್ಟದ್ದರಿಂದ ಅವನ ಪ್ರಾಣಕ್ಕೆ ಬದಲಾಗಿ ನಿನ್ನ ಪ್ರಾಣವು ಹೋಗುವದು; ನಿನ್ನ ಪ್ರಜೆಗಳು ಅವನ ಪ್ರಜೆಗಳಾಗುವರು ಎಂಬದಾಗಿ ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ ಎಂದು ಹೇಳಿದನು.",
			"43": "<sup>43</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನು ಸಿಟ್ಟಿನಿಂದ ಗಂಟುಮೋರೆಮಾಡಿಕೊಂಡು ಸಮಾರ್ಯದಲ್ಲಿದ್ದ ತನ್ನ ಮನೆಗೆ ಹೋದನು."
		},
		"21": {
			"1": "<sup>1</sup> ಅನಂತರ ಸಂಭವಿಸಿದ್ದೇನಂದರೆ - ಇಜ್ರೇಲಿನಲ್ಲಿ ಸಮಾರ್ಯದ ಅರಸನಾದ ಅಹಾಬನ ಅರಮನೆಯ ಹತ್ತಿರ ಇಜ್ರೇಲಿನವನಾದ ನಾಬೋತನೆಂಬ ಮನುಷ್ಯನ ದ್ರಾಕ್ಷೇತೋಟವಿತ್ತು.",
			"2": "<sup>2</sup> ಅಹಾಬನು ನಾಬೋತನಿಗೆ - ನಿನ್ನ ದ್ರಾಕ್ಷೇತೋಟವನ್ನು ನನಗೆ ಕೊಡು; ಅದು ನನ್ನ ಅರಮನೆಯ ಹತ್ತಿರವಿರುವದರಿಂದ ಅದನ್ನು ಕಾಯಿಪಲ್ಯದ ತೋಟವನ್ನಾಗಿ ಮಾಡಿಕೊಳ್ಳುವೆನು; ಅದಕ್ಕೆ ಬದಲಾಗಿ ನಿನಗೆ ಅದಕ್ಕಿಂತ ಉತ್ತಮವಾದ ತೋಟವನ್ನು ಕೊಡುತ್ತೇನೆ; ಅದು ಬೇಡವಾದರೆ ಕ್ರಯವನ್ನು ಕೊಡುತ್ತೇನೆ ಎಂದು ಹೇಳಿದನು.",
			"3": "<sup>3</sup> ಅದಕ್ಕೆ ನಾಬೋತನು - ನಾನು ಪಿತ್ರಾರ್ಜಿತ ಸ್ವಾಸ್ತ್ಯವನ್ನು ನಿನಗೆ ಮಾರದಂತೆ ಯೆಹೋವನು ನನ್ನನ್ನು ತಡೆಯಲಿ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"4": "<sup>4</sup> ನನ್ನ ಪಿತ್ರಾರ್ಜಿತಸ್ವಾಸ್ತ್ಯವನ್ನು ನಿನಗೆ ಕೊಡುವದಿಲ್ಲವೆಂದು ನಾಬೋತನು ಹೇಳಿದದರಿಂದ ಅಹಾಬನು ಸಿಟ್ಟಿನಿಂದ ಗಂಟುಮೋರೆ ಮಾಡಿಕೊಂಡು ಮನೆಗೆ ಹೋಗಿ ಊಟಮಾಡಲೊಲ್ಲದೆ ಮಂಚದ ಮೇಲೆ ಮಲಗಿ ಗೋಡೆಯ ಕಡೆಗೆ ಮುಖ ತಿರುಗಿಸಿಕೊಂಡನು.",
			"5": "<sup>5</sup> ಆಗ ಅವನ ಹೆಂಡತಿಯಾದ ಈಜೆಬೆಲಳು ಅವನ ಬಳಿಗೆ ಬಂದು ಅವನನ್ನು - ನೀನು ಯಾಕೆ ಊಟಮಾಡುವದಿಲ್ಲ? ನಿನಗೆ ಯಾವ ಚಿಂತೆ ಇರುತ್ತದೆ ಎಂದು ಕೇಳಲು ಅವನು -",
			"6": "<sup>6</sup> ನಾನು ಇಜ್ರೇಲಿನವನಾದ ನಾಬೋತನಿಗೆ - ನಿನ್ನ ದ್ರಾಕ್ಷೇತೋಟವನ್ನು ನನಗೆ ಮಾರಿಬಿಡು; ಹಣ ಬೇಡವಾದರೆ ನಿನಗೆ ಬೇರೊಂದು ದ್ರಾಕ್ಷೇತೋಟವನ್ನು ಕೊಡುತ್ತೇನೆ ಎಂಬದಾಗಿ ಹೇಳಿದೆನು; ಆದರೆ ಅವನು ಕೊಡುವದಿಲ್ಲ ಅಂದನು ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"7": "<sup>7</sup> ಆಗ ಅವನ ಹೆಂಡತಿಯಾದ ಈಜೆಬೆಲಳು ಅವನಿಗೆ - ನೀನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನೋ; ಎದ್ದು ಊಟಮಾಡಿ ಸಂತೋಷದಿಂದಿರು; ನಾನು ನಿನಗೆ ಇಜ್ರೇಲಿನವನಾದ ನಾಬೋತನ ದ್ರಾಕ್ಷೇತೋಟವನ್ನು ಕೊಡುತ್ತೇನೆ ಎಂದು ನುಡಿದು",
			"8": "<sup>8</sup> ಅಹಾಬನ ಹೆಸರಿನಲ್ಲಿ ಒಂದು ಪತ್ರವನ್ನು ಬರೆದು ಅದಕ್ಕೆ ಅವನ ಮುದ್ರೆಹಾಕಿ ಅದನ್ನು ನಾಬೋತನ ಊರಿನಲ್ಲಿದ್ದ ಎಲ್ಲಾ ಪ್ರಧಾನಪುರುಷರಿಗೂ ಹಿರಿಯರಿಗೂ ಕಳುಹಿಸಿದಳು.",
			"9": "<sup>9</sup> ಅದರಲ್ಲಿ - ಎಲ್ಲರೂ ಉಪವಾಸ ಮಾಡಬೇಕೆಂದು ಪ್ರಕಟಿಸಿ ನಾಬೋತನನ್ನು ನೆರೆದ ಸಭೆಯ ಮುಂದೆ ನಿಲ್ಲಿಸಿ",
			"10": "<sup>10</sup> ಅವನು ದೇವರನ್ನೂ ಅರಸನನ್ನೂ ಶಪಿಸಿದವನು ಎಂಬದಾಗಿ ಇಬ್ಬರು ದುಷ್ಟ ಮನುಷ್ಯರಿಂದ ಅವನಿಗೆ ವಿರೋಧವಾಗಿ ಸಾಕ್ಷಿ ಹೇಳಿಸಿ ಅವನನ್ನು ಹೊರಗೆ ಒಯ್ದು ಕಲ್ಲೆಸೆದು ಕೊಲ್ಲಿರಿ ಎಂದು ಬರೆದಿದ್ದಳು.",
			"11": "<sup>11</sup> ಅವನ ಊರಿನ ಹಿರಿಯರೂ ಪ್ರಧಾನ ಪುರುಷರೂ ಈಜೆಬೆಲಳು ಕಳುಹಿಸಿದ ಪತ್ರದಲ್ಲಿ ಬರೆದಿದ್ದಂತೆಯೇ ಮಾಡಿದರು.",
			"12": "<sup>12</sup> ಅವರು ಉಪವಾಸವನ್ನು ಪ್ರಕಟಿಸಿ ನೆರೆದ ಸಭೆಯ ಮುಂದೆ ನಾಬೋತನನ್ನು ನಿಲ್ಲಿಸಿದರು.",
			"13": "<sup>13</sup> ತರುವಾಯ ಇಬ್ಬರು ದುಷ್ಟರು ಬಂದು ಅವನ ಮುಂದೆ ಕೂತು ಇವನು ದೇವರನ್ನೂ ಅರಸನನ್ನೂ ಶಪಿಸಿದ್ದಾನೆಂಬದಾಗಿ ಜನರ ಎದುರಿನಲ್ಲಿ ಅವನಿಗೆ ವಿರೋಧವಾಗಿ ಸಾಕ್ಷಿ ಹೇಳಿದರು. ಜನರು ಅವನನ್ನು ಊರಿನ ಹೊರಗೆ ಒಯ್ದು ಕಲ್ಲೆಸೆದು ಕೊಂದರು.",
			"14": "<sup>14</sup> ನಾಬೋತನು ಕಲ್ಲೆಸೆಯಲ್ಪಟ್ಟು ಸತ್ತ ವರ್ತಮಾನವನ್ನು ಹಿರಿಯರು ಈಜೆಬೆಲಳಿಗೆ ಮುಟ್ಟಿಸಿದರು.",
			"15": "<sup>15</sup> ಆಕೆಯು ಇದನ್ನು ಕೇಳಿ ಅಹಾಬನಿಗೆ - ನೀನು ಹೋಗಿ ನಾಬೋತನು ಮಾರುವದಿಲ್ಲವೆಂದು ಹೇಳಿದ ದ್ರಾಕ್ಷೇತೋಟವನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೋ; ಅವನು ಜೀವದಿಂದಿಲ್ಲ, ಸತ್ತನು ಎಂದು ಹೇಳಿದಳು.",
			"16": "<sup>16</sup> ಇಜ್ರೇಲಿನವನಾದ ನಾಬೋತನ ಮರಣವಾರ್ತೆಯನ್ನು ಅಹಾಬನು ಕೇಳಿ ದ್ರಾಕ್ಷೇತೋಟವನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವದಕ್ಕೋಸ್ಕರ ಇಳಿದು ಹೋದನು.",
			"17": "<sup>17</sup> ಆಗ ತಿಷ್ಬೀಯನಾದ ಎಲೀಯನಿಗೆ ಯೆಹೋವನ ವಾಕ್ಯವುಂಟಾಯಿತು.",
			"18": "<sup>18</sup> ಆತನು ಅವನಿಗೆ - ನೀನು ಹೋಗಿ ಸಮಾರ್ಯದಲ್ಲಿ ವಾಸಿಸುವ ಇಸ್ರಾಯೇಲ್ ರಾಜನನ್ನು ಕಾಣು; ಅವನು ಈಗ ನಾಬೋತನ ದ್ರಾಕ್ಷೇತೋಟವನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವದಕ್ಕೋಸ್ಕರ ಅಲ್ಲಿಗೆ ಹೋಗಿದ್ದಾನೆ.",
			"19": "<sup>19</sup> ಅವನಿಗೆ - ನೀನು ಕೊಲೆ ಮಾಡಿ ಸ್ವಾಸ್ತ್ಯವನ್ನು ಸಂಪಾದಿಸಿಕೊಂಡಿಯಲ್ಲವೋ? ನಾಯಿಗಳು ನಾಬೋತನ ರಕ್ತವನ್ನು ನೆಕ್ಕಿದ ಸ್ಥಳದಲ್ಲೇ ನಿನ್ನ ರಕ್ತವನ್ನೂ ನೆಕ್ಕುವವು ಎಂಬದಾಗಿ ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ ಎಂದು ಅವನಿಗೆ ಹೇಳು ಎಂಬದೇ.",
			"20": "<sup>20</sup> ಅಹಾಬನು ಎಲೀಯನನ್ನು ಕಂಡು ಅವನನ್ನು - ನನ್ನ ವೈರಿಯೇ, ನೀನು ನನ್ನನ್ನು ಕಂಡುಕೊಂಡಿಯಾ ಎಂದು ಕೇಳಲು ಅವನು - ಹೌದು, ಕಂಡುಕೊಂಡೆನು; ನೀನು ನಿನ್ನನ್ನು ಪಾಪಕ್ಕೆ ಮಾರಿಬಿಟ್ಟು ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾದಿಯಲ್ಲಾ.",
			"21": "<sup>21</sup> ಯೆಹೋವನು ನಿನ್ನನ್ನು ಕುರಿತು - ನಾನು ನಿನ್ನ ಮೇಲೆ ಕೇಡನ್ನು ಬರಮಾಡಿ ನಿನ್ನನ್ನು ಕಸವನ್ನೋ ಎಂಬಂತೆ ತೆಗೆದುಹಾಕುವೆನು; ನಿನ್ನ ಸಂತಾನದ ಗಂಡಸರಲ್ಲಿ ಸ್ವತಂತ್ರರಾಗಲಿ, ಪರತಂತ್ರರಾಗಲಿ ಎಲ್ಲರನ್ನೂ ಇಸ್ರಾಯೇಲ್ಯರೊಳಗಿಂದ ಕಡಿದುಬಿಡುವೆನು.",
			"22": "<sup>22</sup> ನೀನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಪಾಪಕ್ಕೆ ಪ್ರೇರಿಸಿ ನನಗೆ ಕೋಪವನ್ನೆಬ್ಬಿಸಿದದರಿಂದ ನೆಬಾಟನ ಮಗನಾದ ಯಾರೊಬ್ಬಾಮನ ಮನೆಗೂ ಅಹೀಯನ ಮಗನಾದ ಬಾಷನ ಮನೆಗೂ ಆದ ಗತಿ ನಿನ್ನ ಮನೆಗೂ ಆಗುವದು ಅನ್ನುತ್ತಾನೆ.",
			"23": "<sup>23</sup> ಇದಲ್ಲದೆ ಆತನು ಈಜೆಬೆಲಳನ್ನು ಕುರಿತು - ನಾಯಿಗಳು ಇಜ್ರೇಲ್ ಪಟ್ಟಣದ ಗೋಡೆಯ ಬಳಿಯಲ್ಲಿ ಆಕೆಯ ಶವವನ್ನು ತಿನ್ನುವವು;",
			"24": "<sup>24</sup> ಅಹಾಬನ ಮನೆಯವರಲ್ಲಿ ಊರೊಳಗೆ ಸಾಯುವಂಥವರನ್ನು ನಾಯಿಗಳೂ ಅಡವಿಯಲ್ಲಿ ಸಾಯುವಂಥವರನ್ನು ಪಕ್ಷಿಗಳೂ ತಿಂದುಬಿಡುವವು ಎಂದು ಹೇಳುತ್ತಾನೆ ಅಂದನು.",
			"25": "<sup>25</sup> ಹೆಂಡತಿಯಾದ ಈಜೆಬೆಲಳಿಂದ ಪ್ರೇರಿತನಾಗಿ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ಕೆಟ್ಟದ್ದಾಗಿರುವದನ್ನು ಮಾಡುವದಕ್ಕೆ ತನ್ನನ್ನೇ ಮಾರಿಬಿಟ್ಟ ಅಹಾಬನಂಥ ದುಷ್ಟನು ಇನ್ನೊಬ್ಬನಿರಲಿಲ್ಲ.",
			"26": "<sup>26</sup> ಯೆಹೋವನು ಇಸ್ರಾಯೇಲ್ಯರ ಎದುರಿನಿಂದ ಓಡಿಸಿಬಿಟ್ಟ ಅಮೋರಿಯರಂತೆ ವಿಗ್ರಹಗಳನ್ನು ಪೂಜಿಸಿದ ಅಹಾಬನ ನಡತೆಯು ಕೇವಲ ಅಸಹ್ಯವಾಗಿತ್ತು.",
			"27": "<sup>27</sup> ಅಹಾಬನು ಎಲೀಯನ ಮಾತುಗಳನ್ನು ಕೇಳಿ ತನ್ನ ಬಟ್ಟೆಯನ್ನು ಹರಿದುಕೊಂಡು ಹಗಲಿರುಳು ಮೈಮೇಲೆ ಗೋಣೀತಟ್ಟನ್ನು ಹಾಕಿಕೊಂಡು ಉಪವಾಸ ಮಾಡುತ್ತಾ ದೀನಮನಸ್ಸಿನಿಂದ ಪ್ರವರ್ತಿಸಿದನು.",
			"28": "<sup>28</sup> ಆದದರಿಂದ ಯೆಹೋವನು ತಿಷ್ಬೀಯನಾದ ಎಲೀಯನಿಗೆ ಅಹಾಬನು ನನ್ನ ಮುಂದೆ ತಗ್ಗಿಹೋದದ್ದನ್ನು ಕಂಡಿಯಾ?",
			"29": "<sup>29</sup> ಅವನು ನನ್ನ ಮುಂದೆ ತನ್ನನ್ನು ತಗ್ಗಿಸಿಕೊಂಡದರಿಂದ ನಾನು ಮುಂತಿಳಿಸಿದ ಕೇಡನ್ನು ಅವನ ಜೀವಮಾನದಲ್ಲಿ ಬರಗೊಡದೆ ಅವನ ಮಗನ ಕಾಲದಲ್ಲಿ ಅವನ ಮನೆಯವರ ಮೇಲೆ ಬರಮಾಡುವೆನು ಎಂದು ಹೇಳಿದನು."
		},
		"22": {
			"1": "<sup>1</sup> ಮೂರು ವರುಷಗಳವರೆಗೆ ಇಸ್ರಾಯೇಲ್ಯರಿಗೂ ಅರಾಮ್ಯರಿಗೂ ಯುದ್ಧ ನಡೆಯಲಿಲ್ಲ.",
			"2": "<sup>2</sup> ಮೂರನೆಯ ವರುಷದಲ್ಲಿ ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಯೆಹೋಷಾಫಾಟನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನ ಬಳಿಗೆ ಬಂದನು.",
			"3": "<sup>3</sup> ಆಗ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನು ತನ್ನ ಸೇವಕರಿಗೆ - ಗಿಲ್ಯಾದಿನ ರಾಮೋತ್ ಪಟ್ಟಣವು ನಮ್ಮದೆಂದು ನಿಮಗೆ ಗೊತ್ತುಂಟಲ್ಲಾ; ಅದನ್ನು ಇಷ್ಟರವರೆಗೂ ಅರಾಮ್ಯರ ಅರಸನಿಂದ ತಿರಿಗಿ ತೆಗೆದುಕೊಳ್ಳದೆ ಸುಮ್ಮನೆ ಕೂತಿರುತ್ತೇವೆ ಎಂದು ಹೇಳಿ",
			"4": "<sup>4</sup> ಯೆಹೋಷಾಫಾಟನನ್ನು - ಯುದ್ಧಮಾಡುವದಕ್ಕಾಗಿ ನೀನೂ ನಮ್ಮ ಜೊತೆಯಲ್ಲಿ ರಾಮೋತ್‍ಗಿಲ್ಯಾದಿಗೆ ಬರುತ್ತೀಯೋ ಎಂದು ಕೇಳಲು ಅವನು - ನಾನೂ ನೀನೂ, ನನ್ನ ಜನರೂ ನಿನ್ನ ಜನರೂ, ನನ್ನ ಕುದುರೆಗಳೂ ನಿನ್ನ ಕುದುರೆಗಳೂ ಒಂದೇಯಲ್ಲವೋ ಎಂದು ಉತ್ತರಕೊಟ್ಟು",
			"5": "<sup>5</sup> ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಈಗ ವಿಚಾರಿಸು ಎಂದು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನನ್ನು ಬೇಡಿಕೊಂಡನು.",
			"6": "<sup>6</sup> ಆಗ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನು ತನ್ನ ರಾಜ್ಯದಲ್ಲಿದ್ದ ಸುಮಾರು ನಾನೂರು ಮಂದಿ ಪ್ರವಾದಿಗಳನ್ನು ಕೂಡಿಸಿ ಅವರನ್ನು - ನಾನು ರಾಮೋತ್‍ಗಿಲ್ಯಾದಿನ ಮೇಲೆ ಯುದ್ಧಕ್ಕಾಗಿ ಹೋಗಬಹುದೋ ಬಾರದೋ ಎಂದು ಕೇಳಲು ಅವರು - ಹೋಗಬಹುದು, ಕರ್ತನು ಅದನ್ನು ಅರಸನ ಕೈಗೆ ಒಪ್ಪಿಸುವನು ಅಂದರು.",
			"7": "<sup>7</sup> ಆದರೆ ಯೆಹೋಷಾಫಾಟನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನನ್ನು - ಯೆಹೋವನ ಪ್ರವಾದಿಗಳಲ್ಲಿ ಇವರ ಹೊರತಾಗಿ ನಾವು ವಿಚಾರಿಸಬಹುದಾದ ಬೇರೆ ಪ್ರವಾದಿಯಿರುವದಿಲ್ಲವೋ ಎಂದು ಕೇಳಲು ಅವನು -",
			"8": "<sup>8</sup> ನಮಗೋಸ್ಕರ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ವಿಚಾರಿಸಬಲ್ಲವನಾದ ಇನ್ನೊಬ್ಬ ಪ್ರವಾದಿಯಿರುತ್ತಾನೆ; ಅವನು ಇಮ್ಲನ ಮಗನಾದ ಮೀಕಾಯೆಹು ಎಂಬವನು. ಆದರೆ ನಾನು ಅವನನ್ನು ಹಗೆಮಾಡುತ್ತೇನೆ; ಅವನು ನನ್ನನ್ನು ಕುರಿತು ಯಾವಾಗಲೂ ಶುಭವನ್ನಲ್ಲ, ಅಶುಭವನ್ನೇ ಮುಂತಿಳಿಸುತ್ತಾನೆ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು. ಅದಕ್ಕೆ ಯೆಹೋಷಾಫಾಟನು - ಅರಸನು ಹಾಗನ್ನದಿರಲಿ ಅಂದನು.",
			"9": "<sup>9</sup> ಆಗ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನು ಒಬ್ಬ ಕಂಚುಕಿಯನ್ನು ಕರೆದು ಅವನಿಗೆ - ಬೇಗ ಹೋಗಿ ಇಮ್ಲನ ಮಗನಾದ ಮೀಕಾಯೆಹುವನ್ನು ಕರೆದುಕೊಂಡು ಬಾ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"10": "<sup>10</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನೂ ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಯೆಹೋಷಾಫಾಟನೂ ರಾಜವಸ್ತ್ರಗಳನ್ನು ಧರಿಸಿಕೊಂಡು ಸಮಾರ್ಯಪಟ್ಟಣದ ಹೆಬ್ಬಾಗಲಿನ ಹತ್ತಿರವಿರುವ ಬೈಲಿನಲ್ಲಿ ತಮ್ಮ ತಮ್ಮ ಆಸನಗಳ ಮೇಲೆ ಕೂತುಕೊಂಡಿರಲು ಎಲ್ಲಾ ಪ್ರವಾದಿಗಳು ಅವರ ಮುಂದೆ ಪರವಶರಾಗಿ ಮಾತಾಡುತ್ತಾ ಇದ್ದರು.",
			"11": "<sup>11</sup> ಅವರಲ್ಲಿ ಕೆನಾನನ ಮಗನಾದ ಚಿದ್ಕೀಯ ಎಂಬವನು ಕಬ್ಬಿಣದ ಕೊಂಬುಗಳನ್ನು ಮಾಡಿಸಿ ತಲೆಗೆ [ಕಟ್ಟಿಕೊಂಡು] ಬಂದು - ಕೊಂಬುಗಳಿಂದಲೋ ಎಂಬಂತೆ ನೀನು ಅರಾಮ್ಯರನ್ನು ಇರಿದು ಕೊಂದುಹಾಕುವಿ ಎಂದನು.",
			"12": "<sup>12</sup> ಉಳಿದ ಪ್ರವಾದಿಗಳೂ ಇದೇ ತರದ ಮಾತುಗಳನ್ನು ನುಡಿದು - ರಾಮೋತ್‍ಗಿಲ್ಯಾದಿಗೆ ಹೋಗು; ನೀನು ಕೃತಾರ್ಥನಾಗಿ ಬರುವಿ; ಯೆಹೋವನು ಅದನ್ನು ಅರಸನ ಕೈಗೆ ಒಪ್ಪಿಸುವನು ಅಂದರು.",
			"13": "<sup>13</sup> ಮೀಕಾಯೆಹುವನ್ನು ಕರೆಯುವದಕ್ಕೆ ಹೋದ ದೂತನು ಅವನಿಗೆ - ಎಲ್ಲಾ ಪ್ರವಾದಿಗಳೂ ಏಕಮನಸ್ಸಿನಿಂದ ಅರಸನಿಗೆ ಶುಭವನ್ನೇ ಮುಂತಿಳಿಸುತ್ತಿರುತ್ತಾರೆ; ದಯವಿಟ್ಟು ನೀನೂ ಅವರಂತೆ ಶುಭವನ್ನೇ ಮುಂತಿಳಿಸು ಎಂದು ಹೇಳಿದನು.",
			"14": "<sup>14</sup> ಅದಕ್ಕೆ ಮೀಕಾಯೆಹುವು - ಯೆಹೋವನಾಣೆ, ಯೆಹೋವನು ಹೇಳುವದನ್ನೇ ನುಡಿಯುತ್ತೇನೆ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"15": "<sup>15</sup> ಅವನು ಅರಸನ ಬಳಿಗೆ ಬಂದಾಗ ಅರಸನು - ಮೀಕಾಯೆಹುವೇ, ನಾವು ರಾಮೋತ್‍ಗಿಲ್ಯಾದಿನ ಮೇಲೆ ಯುದ್ಧಕ್ಕಾಗಿ ಹೋಗಬಹುದೋ ಬಾರದೋ ಎಂದು ಕೇಳಲು ಅವನು - ಹೋಗಬಹುದು, ಕೃತಾರ್ಥನಾಗಿ ಬರುವಿ; ಯೆಹೋವನು ಪಟ್ಟಣವನ್ನು ಅರಸನ ಕೈಗೆ ಒಪ್ಪಿಸುವನು ಎಂದು ಹೇಳಿದನು.",
			"16": "<sup>16</sup> ಆಗ ಅರಸನು ಅವನಿಗೆ - ಸತ್ಯವನ್ನೇ ತಿಳಿಸಬೇಕೆಂದು ಯೆಹೋವನ ಹೆಸರಿನಲ್ಲಿ ನಿನ್ನಿಂದ ಎಷ್ಟು ಸಾರಿ ಪ್ರಮಾಣಮಾಡಿಸಬೇಕು ಅನ್ನಲು ಅವನು -",
			"17": "<sup>17</sup> ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರೂ ಕುರುಬನಿಲ್ಲದ ಕುರಿಗಳಂತೆ ಬೆಟ್ಟಗಳಲ್ಲಿ ಚದರಿಹೋದದ್ದನ್ನು ಕಂಡೆನು; ಆಗ ಯೆಹೋವನು - ಇವರು ಒಡೆಯನಿಲ್ಲದವರಾಗಿರುತ್ತಾರೆ; ಸಮಾಧಾನದಿಂದ ತಮ್ಮ ತಮ್ಮ ಮನೆಗಳಿಗೆ ಹೋಗಲಿ ಅಂದನು ಎಂಬದಾಗಿ ಉತ್ತರಕೊಟ್ಟನು.",
			"18": "<sup>18</sup> ಆಗ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನು ಯೆಹೋಷಾಫಾಟನಿಗೆ - ಇವನು ನನಗೆ ಯಾವಾಗಲೂ ಶುಭವನ್ನಲ್ಲ, ಅಶುಭವನ್ನೇ ಮುಂತಿಳಿಸುತ್ತಾನೆಂದು ನಾನು ನಿನಗೆ ಹೇಳಲಿಲ್ಲವೋ ಎಂದನು.",
			"19": "<sup>19</sup> ಅದಕ್ಕೆ ಮೀಕಾಯೆಹುವು - ಅದಿರಲಿ, ಯೆಹೋವನ ವಾಕ್ಯವನ್ನು ಕೇಳು. ಯೆಹೋವನು ತನ್ನ ಸಿಂಹಾಸನದ ಮೇಲೆ ಕೂತುಕೊಂಡದ್ದನ್ನೂ ಪರಲೋಕ ಸೈನ್ಯಗಳು ಆತನ ಎಡಬಲಗಡೆಗಳಲ್ಲಿ ನಿಂತದ್ದನ್ನೂ ಕಂಡೆನು.",
			"20": "<sup>20</sup> ಯೆಹೋವನು ತನ್ನ ಹತ್ತಿರ ನಿಂತವರನ್ನು - ಅಹಾಬನು ಹತನಾಗಿ ಬೀಳುವಂತೆ ಅವನನ್ನು ರಾಮೋತ್‍ಗಿಲ್ಯಾದಿನ ಯುದ್ಧಕ್ಕೆ ಯಾರು ಪ್ರೇರಿಸುವಿರಿ ಎಂದು ಕೇಳಿದಾಗ ಒಬ್ಬನು ಈ ತರವಾಗಿಯೂ ಇನ್ನೊಬ್ಬನು ಆ ತರವಾಗಿಯೂ ಉತ್ತರ ಕೊಟ್ಟರು.",
			"21": "<sup>21</sup> ಕಡೆಯಲ್ಲಿ ಒಂದು ಆತ್ಮವು ಯೆಹೋವನ ಮುಂದೆ ಬಂದು ನಿಂತು ಆತನಿಗೆ - ನಾನು ಹೋಗಿ ಅವನನ್ನು ಪ್ರೇರಿಸುವೆನು ಅಂದಿತು.",
			"22": "<sup>22</sup> ಹೇಗೆ ಪ್ರೇರಿಸುವಿ ಎಂದು ಯೆಹೋವನು ಕೇಳಲು ಅದು - ನಾನು ಅಸತ್ಯವನ್ನಾಡುವ ಆತ್ಮವಾಗಿ ಅವನ ಎಲ್ಲಾ ಪ್ರವಾದಿಗಳಲ್ಲಿ ಸೇರುವೆನು ಎಂದು ಉತ್ತರಕೊಟ್ಟಿತು. ಆಗ ಆತನು ಅದಕ್ಕೆ - ಹೋಗಿ ಅದರಂತೆ ಮಾಡು; ಅವನನ್ನು ಪ್ರೇರಿಸಿ ಸಫಲನಾಗುವಿ ಅಂದನು.",
			"23": "<sup>23</sup> ನೋಡು, ಯೆಹೋವನು ನಿನ್ನ ವಿಷಯದಲ್ಲಿ ಕೇಡುನುಡಿದು ನಿನ್ನ ಎಲ್ಲಾ ಪ್ರವಾದಿಗಳಲ್ಲಿ ಅಸತ್ಯವನ್ನಾಡುವ ಆತ್ಮವನ್ನು ಕಳುಹಿಸಿದ್ದಾನೆ ಎಂದು ಹೇಳಿದನು.",
			"24": "<sup>24</sup> ಆಗ ಕೆನಾನನ ಮಗನಾದ ಚಿದ್ಕೀಯನು ಮೀಕಾಯೆಹುವಿನ ಬಳಿಗೆ ಹೋಗಿ ಅವನ ಕೆನ್ನೆಗೆ ಒಂದು ಏಟು ಹಾಕಿ - ಯೆಹೋವನ ಆತ್ಮವು ನನ್ನನ್ನು ಬಿಟ್ಟು ನಿನ್ನೊಂದಿಗೆ ಮಾತಾಡುವದಕ್ಕೋಸ್ಕರ ಯಾವ ಮಾರ್ಗವಾಗಿ ಬಂದಿತು ಅನ್ನಲು ಮೀಕಾಯೆಹುವು -",
			"25": "<sup>25</sup> ನೀನು ಅಡಗಿಕೊಳ್ಳುವದಕ್ಕೆ ಒಳಗಿನ ಕೋಣೆಗೆ ಹೋಗುವ ದಿನದಲ್ಲಿ ಅದು ನಿನಗೆ ಗೊತ್ತಾಗುವದು ಎಂದು ಉತ್ತರ ಕೊಟ್ಟನು.",
			"26": "<sup>26</sup> ಆಗ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನು [ಸೇವಕರಿಗೆ] - ಮೀಕಾಯೆಹುವನ್ನು ಹಿಡಿದುಕೊಂಡು ಹೋಗಿ ಪುರಾಧಿಕಾರಿಯಾದ ಆಮೋನನಿಗೂ ರಾಜಪುತ್ರನಾದ ಯೋವಾಷನಿಗೂ ಒಪ್ಪಿಸಿ ಅವರಿಗೆ -",
			"27": "<sup>27</sup> ನಾನು ಸುರಕ್ಷಿತನಾಗಿ ಹಿಂದಿರುಗುವವರೆಗೂ ಇವನನ್ನು ಸೆರೆಯಲ್ಲಿಟ್ಟು ಸೆರೆಮನೆಯ ಅನ್ನಪಾನಗಳನ್ನೇ ಕೊಟ್ಟು ಕುಗ್ಗಿಸಬೇಕೆಂದು ಅರಸನು ಅನ್ನುತ್ತಾನೆ ಎಂದು ಹೇಳಿರಿ ಎಂಬದಾಗಿ ಆಜ್ಞಾಪಿಸಿದನು.",
			"28": "<sup>28</sup> ಮೀಕಾಯೆಹುವು ಅರಸನಿಗೆ - ನೀನು ಸುರಕ್ಷಿತನಾಗಿ ಬರುವದಾದರೆ ನಾನು ನುಡಿದದ್ದು ಯೆಹೋವನ ಮಾತಲ್ಲವೆಂದು ತಿಳಿದುಕೋ ಎಂದು ಹೇಳಿ - ಎಲ್ಲಾ ಜನರೇ, ಇದನ್ನು ಕೇಳಿರಿ ಎಂದು ಕೂಗಿದನು.",
			"29": "<sup>29</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನೂ ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಯೆಹೋಷಾಫಾಟನೂ ರಾಮೋತ್‍ಗಿಲ್ಯಾದಿಗೆ ವಿರೋಧವಾಗಿ ಯುದ್ಧಕ್ಕೆ ಹೊರಟರು.",
			"30": "<sup>30</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನು ಯೆಹೋಷಾಫಾಟನಿಗೆ - ನಾನು ವೇಷಹಾಕಿಸಿಕೊಂಡು ರಣರಂಗಕ್ಕೆ ಬರುವೆನು; ನೀನಾದರೋ ನಿನ್ನ ರಾಜವಸ್ತ್ರಗಳನ್ನು ಧರಿಸಿಕೊಂಡೇ ಬಾ ಎಂದು ಹೇಳಿ ವೇಷಹಾಕಿಕೊಂಡು ರಣರಂಗಕ್ಕೆ ಹೋದನು.",
			"31": "<sup>31</sup> ಅರಾಮ್ಯರ ಅರಸನು ತನ್ನ ರಥಬಲದ ಮೂವತ್ತೆರಡು ಮಂದಿ ಅಧಿಪತಿಗಳಿಗೆ - ನೀವು ಶತ್ರುಗಳ ಸಾಧಾರಣ ಸೈನಿಕರನ್ನೂ ಅಧಿಪತಿಗಳನ್ನೂ ಬಿಟ್ಟು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಿಗೇ ಗುರಿಯಿಡಿರಿ ಎಂದು ಆಜ್ಞಾಪಿಸಿದ್ದನು.",
			"32": "<sup>32</sup> ಅವರು ಯೆಹೋಷಾಫಾಟನನ್ನು ಕಂಡಾಗ ಅವನೇ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನೆಂದು ನೆನಸಿ ಅವನಿಗೆ ವಿರೋಧವಾಗಿ ಯುದ್ಧಮಾಡ ಬಂದರು.",
			"33": "<sup>33</sup> ಅವನು ಕೂಗಿಕೊಂಡನು; ಆಗ ಇವನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಲ್ಲವೆಂಬದು ರಥಬಲದ ಅಧಿಪತಿಗಳಿಗೆ ಗೊತ್ತಾದಾಗ ಅವರು ಅವನನ್ನು ಬಿಟ್ಟು ಹಿಂದಿರುಗಿದರು.",
			"34": "<sup>34</sup> ಅರಾಮ್ಯರ ಒಬ್ಬ ಸೈನಿಕನು ಯಾರಿಗೂ ಗುರಿಯಿಡದೆ ಬಾಣವನ್ನೆಸೆಯಲು ಆ ಬಾಣವು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಿಗೆ ಅವನ ಕವಚದ ಸಂದಿನಲ್ಲಿ ತಾಗಿತು. ಆಗ ಅವನು ತನ್ನ ಸಾರಥಿಗೆ - ರಥವನ್ನು ತಿರುಗಿಸಿ ನನ್ನನ್ನು ರಣರಂಗದಿಂದ ಆಚೆಗೆ ತೆಗೆದುಕೊಂಡು ಹೋಗು; ನನಗೆ ದೊಡ್ಡ ಗಾಯವಾಗಿದೆ ಎಂದು ಹೇಳಿದನು.",
			"35": "<sup>35</sup> ಆದರೂ ಆ ದಿವಸ ಯುದ್ಧವು ಬಹುಘೋರವಾಗಿದ್ದದರಿಂದ ಅರಸನು ಅರಾಮ್ಯರ ಎದುರಾಗಿ ತನ್ನ ರಥದಲ್ಲೇ ಆತುಕೊಂಡು ನಿಂತಿದ್ದು ಸಾಯಂಕಾಲವಾದಾಗ ಸತ್ತನು. ಗಾಯದಿಂದ ರಕ್ತವು ಸುರಿದು ಸುರಿದು ರಥದ ಅಡಿಯಲ್ಲಿ ನಿಂತಿತು.",
			"36": "<sup>36</sup> ಸೂರ್ಯಾಸ್ತಮಾನವಾದ ಕೂಡಲೆ - ಪ್ರತಿಯೊಬ್ಬನು ತನ್ನ ಪ್ರಾಂತಕ್ಕೂ ಪಟ್ಟಣಕ್ಕೂ ಹೋಗಲಿ ಎಂಬ ಕೂಗು ಇಸ್ರಾಯೇಲ್‍ಸೈನ್ಯದಲ್ಲಿ ಹಬ್ಬಿಕೊಂಡಿತು.",
			"37": "<sup>37</sup> ಅರಸನು ಸಾಯಲು ಅವನನ್ನು ಸಮಾರ್ಯಕ್ಕೆ ತಂದು ಅಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು.",
			"38": "<sup>38</sup> ದೇವದಾಸಿಯರು ಸ್ನಾನಮಾಡುವ ಸಮಾರ್ಯದ ಕೆರೆಯಲ್ಲಿ ಅವನ ರಥವನ್ನು ತೊಳೆಯುತ್ತಿದ್ದಾಗ ನಾಯಿಗಳು ಬಂದು ಅದರಲ್ಲಿದ್ದ ರಕ್ತವನ್ನು ನೆಕ್ಕಿದವು; ಹೀಗೆ ಯೆಹೋವನು ಹೇಳಿದ ಮಾತು ನೆರವೇರಿತು.",
			"39": "<sup>39</sup> ಅಹಾಬನ ಉಳಿದ ಚರಿತ್ರೆಯೂ ಅವನು ಕಟ್ಟಿಸಿದ ದಂತಮಂದಿರ, ಪಟ್ಟಣಗಳು ಇವುಗಳ ವಿವರವೂ ಅವನ ಬೇರೆ ಎಲ್ಲಾ ಕೃತ್ಯಗಳೂ ಇಸ್ರಾಯೇಲ್‍ರಾಜಕಾಲವೃತ್ತಾಂತ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"40": "<sup>40</sup> ಅಹಾಬನು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಲು ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ಅಹಜ್ಯ ಎಂಬವನು ಅರಸನಾದನು.",
			"41": "<sup>41</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾದ ಅಹಾಬನ ಆಳಿಕೆಯ ನಾಲ್ಕನೆಯ ವರುಷದಲ್ಲಿ ಆಸನ ಮಗನಾದ ಯೆಹೋಷಾಫಾಟನು ಯೆಹೂದ್ಯರ ಅರಸನಾದನು.",
			"42": "<sup>42</sup> ಅವನು ಅರಸನಾದಾಗ ಮೂವತ್ತೈದು ವರುಷದವನಾಗಿದ್ದು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಇಪ್ಪತ್ತೈದು ವರುಷ ಆಳಿದನು. ಶಿಲ್ಹಿಯ ಮಗಳಾದ ಅಜೂಬಳೆಂಬಾಕೆಯು ಇವನ ತಾಯಿ.",
			"43": "<sup>43</sup> ಇವನು ತಪ್ಪದೆ ತನ್ನ ತಂದೆಯಾದ ಆಸನ ಮಾರ್ಗದಲ್ಲಿ ನಡೆಯುತ್ತಾ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ಒಳ್ಳೆಯವನಾಗಿದ್ದರೂ ಪೂಜಾಸ್ಥಳಗಳನ್ನು ಹಾಳು ಮಾಡಲಿಲ್ಲ; ಜನರು ಆ ಸ್ಥಳಗಳಲ್ಲಿ ಯಜ್ಞಮಾಡುತ್ತಾ ಧೂಪಸುಡುತ್ತಾ ಇದ್ದರು.",
			"44": "<sup>44</sup> ಇವನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನೊಡನೆ ಸಮಾಧಾನದಿಂದಿದ್ದನು.",
			"45": "<sup>45</sup> ಇವನ ಉಳಿದ ಚರಿತ್ರೆಯೂ ಇವನು ಯುದ್ಧಗಳಲ್ಲಿ ನಡಿಸಿದ ಶೂರ ಕೃತ್ಯಗಳ ವಿವರವೂ ಯೆಹೂದರಾಜಕಾಲವೃತ್ತಾಂತ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"46": "<sup>46</sup> ಇವನು ತನ್ನ ತಂದೆಯಾದ ಆಸನ ಕಾಲದಲ್ಲಿ ತಪ್ಪಿಸಿಕೊಂಡ ದೇವದಾಸ ದೇವದಾಸಿಯರನ್ನು ದೇಶದಿಂದ ತೆಗೆದುಹಾಕಿದನು.",
			"47": "<sup>47</sup> ಆ ಕಾಲದಲ್ಲಿ ಎದೋಮ್ ದೇಶದಲ್ಲಿ ಅರಸನಿರಲಿಲ್ಲ; ಇವನ ಪ್ರತಿನಿಧಿಯೇ ಅದನ್ನು ಆಳುತ್ತಿದ್ದನು.",
			"48": "<sup>48</sup> ಇದಲ್ಲದೆ ಯೆಹೋಷಾಫಾಟನು ಓಫೀರಿನಿಂದ ಬಂಗಾರ ತರುವದಕ್ಕಾಗಿ ತಾರ್ಷೀಷ್ ಹಡಗುಗಳನ್ನು ಮಾಡಿಸಿದನು.",
			"49": "<sup>49</sup> ಆಗ ಅಹಾಬನ ಮಗನಾದ ಅಹಜ್ಯನು ಅವನನ್ನು - ನಿನ್ನ ಜನರ ಜೊತೆಯಲ್ಲಿ ನನ್ನ ಜನರೂ ಸಮುದ್ರ ಪ್ರಯಾಣಮಾಡುವದಕ್ಕೆ ಅಪ್ಪಣೆಯಾಗಲಿ ಎಂದು ಬೇಡಿಕೊಂಡನು; ಅವನು ಅದಕ್ಕೆ ಒಪ್ಪಲಿಲ್ಲ. ಆದರೆ ಆ ಹಡಗುಗಳು ಎಚ್ಯೋನ್ಗೆಬೆರಿನಲ್ಲಿ ಒಡೆದುಹೋದದರಿಂದ ಓಫೀರನ್ನು ಮುಟ್ಟಲಿಲ್ಲ.",
			"50": "<sup>50</sup> ಯೆಹೋಷಾಫಾಟನು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಲು ಅವನ ಶವವನ್ನು ಅವನ ಪೂರ್ವಿಕನಾದ ದಾವೀದನ ಪಟ್ಟಣದೊಳಗೆ ಕುಟುಂಬ ಶ್ಮಶಾನಭೂಮಿಯಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ಯೆಹೋರಾಮನು ಅರಸನಾದನು.",
			"51": "<sup>51</sup> ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಯೆಹೋಷಾಫಾಟನ ಆಳಿಕೆಯ ಹದಿನೇಳನೆಯ ವರುಷದಲ್ಲಿ ಅಹಾಬನ ಮಗನಾದ ಅಹಜ್ಯ ಎಂಬವನು ಅರಸನಾಗಿ ಸಮಾರ್ಯದಲ್ಲಿ ಎರಡು ವರುಷ ಆಳಿದನು.",
			"52": "<sup>52</sup> ಇವನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಪಾಪಕ್ಕೆ ಪ್ರೇರಿಸಿದ ನೆಬಾಟನ ಮಗನಾದ ಯಾರೊಬ್ಬಾಮನ ಮತ್ತು ತನ್ನ ತಂದೆತಾಯಿಗಳ ಮಾರ್ಗದಲ್ಲಿ ನಡೆದು ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾದನು.",
			"53": "<sup>53</sup> ತನ್ನ ತಂದೆಯಂತೆ ಬಾಳ್ ದೇವರನ್ನು ಪೂಜಿಸಿ ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರಾದ ಯೆಹೋವನನ್ನು ರೇಗಿಸಿದನು."
		}
	}
};
module.exports = book;