var book = {
	"name": "2 ಅರಸುಗಳು",
	"numChapters": 25,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ಅಹಾಬನು ಮರಣಹೊಂದಿದನಂತರ ಮೋವಾಬ್ಯರು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ವಿರೋಧವಾಗಿ ತಿರುಗಿಬಿದ್ದರು.",
			"2": "<sup>2</sup> ಅಹಜ್ಯನು ಸಮಾರ್ಯದಲ್ಲಿ ತನ್ನ ಮೇಲುಪ್ಪರಿಗೆಯ ಜಾಳಾಂಧರ ಕಿಟಕಿಯಿಂದ ಬಿದ್ದು ಅಸ್ವಸ್ಥನಾದಾಗ ತನ್ನ ಸೇವಕರನ್ನು ಕರೆದು ಅವರಿಗೆ - ನೀವು ಎಕ್ರೋನಿನ ದೇವರಾದ ಬಾಳ್ಜೆಬೂಬನ ಸನ್ನಿಧಿಗೆ ಹೋಗಿ ನಾನು ಈ ಅಸ್ವಸ್ಥತೆಯಿಂದ ವಾಸಿಯಾಗುವೆನೋ ಇಲ್ಲವೋ ಎಂಬದನ್ನು ವಿಚಾರಿಸಿರಿ ಎಂದು ಹೇಳಿಕಳುಹಿಸಿದನು.",
			"3": "<sup>3</sup> ಆಗ ಯೆಹೋವನ ದೂತನು ತಿಷ್ಬೀಯನಾದ ಎಲೀಯನಿಗೆ - ನೀನು ಹೋಗಿ ಸಮಾರ್ಯದ ಅರಸನ ಸೇವಕರನ್ನು ಎದುರುಗೊಂಡು ಅವರಿಗೆ - ನೀವು ಎಕ್ರೋನಿನ ದೇವರಾದ ಬಾಳ್ವೆಬೂಬನನ್ನು ವಿಚಾರಿಸುವದಕ್ಕೆ ಹೋಗುವದೇನು? ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ದೇವರಿಲ್ಲವೋ?",
			"4": "<sup>4</sup> ಅಹಜ್ಯನು ತಾನು ಹತ್ತಿದ ಮಂಚದಿಂದಿಳಿಯದೆ ಸಾಯಲೇಬೇಕು ಎಂಬದಾಗಿ ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ ಎಂದು ಹೇಳು ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು. ಎಲೀಯನು ಹಾಗೆಯೇ ಮಾಡಿದನು.",
			"5": "<sup>5</sup> ದೂತರು ಅರಸನ ಬಳಿಗೆ ಹಿಂದಿರುಗಿ ಬಂದಾಗ ಅರಸನು - ನೀವು ಹಿಂದಿರುಗಿದ್ದೇಕೆ ಎಂದು ಕೇಳಿದನು.",
			"6": "<sup>6</sup> ಅದಕ್ಕೆ ಅವರು - ಒಬ್ಬ ಮನುಷ್ಯನು ನಮ್ಮನ್ನು ಎದುರುಗೊಂಡು ನಮಗೆ - ನಿಮ್ಮನ್ನು ಕಳುಹಿಸಿದ ಅರಸನ ಬಳಿಗೆ ಹೋಗಿ ಯೆಹೋವನ ಹೆಸರಿನಲ್ಲಿ ಅವನಿಗೆ - ನೀನು ಎಕ್ರೋನಿನ ದೇವರಾದ ಬಾಳ್ಜೆಬೂಬನನ್ನು ವಿಚಾರಿಸಿಸುವದಕ್ಕೆ ಕಳುಹಿಸುವದೇನು? ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ದೇವರಿಲ್ಲವೋ? ನೀನು ಹೀಗೆ ಮಾಡಿದ್ದರಿಂದ ಹತ್ತಿದ ಮಂಚದಿಂದಿಳಿಯದೆ ಸಾಯಲೇಬೇಕು ಎಂಬದಾಗಿ ಹೇಳಿರಿ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು ಎಂದು ಉತ್ತರಕೊಟ್ಟರು.",
			"7": "<sup>7</sup> ಅರಸನು ತಿರಿಗಿ ಅವರನ್ನು - ನಿಮಗೆ ಎದುರಾಗಿ ಹೀಗೆ ಹೇಳಿದ ಮನುಷ್ಯನು ಹೇಗಿದ್ದನು ಎಂದು ಕೇಳಲು ಅವರು -",
			"8": "<sup>8</sup> ಅವನು ಕಂಬಳಿಯನ್ನು ಹೊದ್ದುಕೊಂಡಿದ್ದನು; ಅವನ ಸೊಂಟಕ್ಕೆ ತೊಗಲಿನ ನಡುಕಟ್ಟು ಇತ್ತು ಎಂದು ಉತ್ತರಕೊಟ್ಟರು. ಅದಕ್ಕೆ ಅರಸನು - ಆ ಮನುಷ್ಯನು ತಿಷ್ಬೀಯನಾದ ಎಲೀಯನೇ ಆಗಿರಬೇಕು ಅಂದನು.",
			"9": "<sup>9</sup> ಆಗ ಅರಸನು ಎಲೀಯನನ್ನು ಕರತರುವದಕ್ಕಾಗಿ ಪಂಚದಶಾಧಿಪತಿಯನ್ನು ಅವನ ಐವತ್ತು ಮಂದಿ ಸಿಪಾಯಿಗಳೊಡನೆ ಕಳುಹಿಸಿದನು. ಇವನು ಹೋಗಿ ಎಲೀಯನು ಬೆಟ್ಟದ ತುದಿಯಲ್ಲಿ ಕೂತಿರುವದನ್ನು ಕಂಡು ಅವನಿಗೆ - ದೇವರ ಮನುಷ್ಯನೇ ಇಳಿದು ಬಾ; ಅರಸನು ನಿನ್ನನ್ನು ಕರೆಯುತ್ತಾನೆ ಎಂದು ಹೇಳಿದನು.",
			"10": "<sup>10</sup> ಅದಕ್ಕೆ ಎಲೀಯನು - ನಾನು ದೇವರ ಮನುಷ್ಯನಾಗಿರುವದಾದರೆ ಆಕಾಶದಿಂದ ಬೆಂಕಿಬಿದ್ದು ನಿನ್ನನ್ನೂ ನಿನ್ನ ಐವತ್ತು ಮಂದಿ ಸಿಪಾಯಿಗಳನ್ನೂ ದಹಿಸಿಬಿಡಲಿ ಅಂದನು. ಕೂಡಲೆ ಆಕಾಶದಿಂದ ಬೆಂಕಿಬಿದ್ದು ಅವನನ್ನೂ ಅವನ ಐವತ್ತು ಮಂದಿ ಸಿಪಾಯಿಗಳನ್ನೂ ದಹಿಸಿಬಿಟ್ಟಿತು.",
			"11": "<sup>11</sup> ತರುವಾಯ ಅರಸನು ಇನ್ನೊಬ್ಬ ಪಂಚದಶಾಧಿಪತಿಯನ್ನು ಅವನ ಐವತ್ತು ಮಂದಿ ಸಿಪಾಯಿಗಳೊಡನೆ ಕಳುಹಿಸಿದನು. ಇವನು ಹೋಗಿ ಎಲೀಯನಿಗೆ - ದೇವರ ಮನುಷ್ಯನೇ, ಬೇಗನೆ ಇಳಿದು ಬಾ; ಅರಸನು ನಿನ್ನನ್ನು ಕರೆಯುತ್ತಾನೆ ಎಂದು ಹೇಳಿದನು.",
			"12": "<sup>12</sup> ಆಗ ಎಲೀಯನು ಅವನಿಗೆ - ನಾನು ದೇವರ ಮನುಷ್ಯನಾಗಿರುವದಾದರೆ ಆಕಾಶದಿಂದ ಬೆಂಕಿಬಿದ್ದು ನಿನ್ನನ್ನೂ ನಿನ್ನ ಐವತ್ತು ಮಂದಿ ಸಿಪಾಯಿಗಳನ್ನೂ ದಹಿಸಿಬಿಡಲಿ ಎಂದನು. ಕೂಡಲೆ ದೇವರು ಆಕಾಶದಿಂದ ಕಳುಹಿಸಿದ ಬೆಂಕಿಯು ಬಂದು ಅವನನ್ನೂ ಅವನ ಐವತ್ತು ಮಂದಿ ಸಿಪಾಯಿಗಳನ್ನೂ ಸುಟ್ಟುಬಿಟ್ಟಿತು.",
			"13": "<sup>13</sup> ಅರಸನು ಮತ್ತೊಬ್ಬ ಪಂಚದಶಾಧಿಪತಿಯನ್ನು ಅವನ ಐವತ್ತು ಮಂದಿ ಸಿಪಾಯಿಗಳೊಡನೆ ಕಳುಹಿಸಿದನು. ಇವನು ಬಂದು ಎಲೀಯನ ಮುಂದೆ ಮೊಣಕಾಲೂರಿ ಅವನನ್ನು - ದೇವರ ಮನುಷ್ಯನೇ, ನನ್ನ ಮತ್ತು ನಿನ್ನ ಸೇವಕರಾದ ಈ ಐವತ್ತು ಮಂದಿಯ ಪ್ರಾಣವು ನಿನ್ನ ದೃಷ್ಟಿಯಲ್ಲಿ ಬೆಲೆಯುಳ್ಳದ್ದಾಗಿರಲಿ.",
			"14": "<sup>14</sup> ಆಕಾಶದಿಂದ ಬೆಂಕಿಬಿದ್ದು ಮುಂಚೆ ಬಂದ ಇಬ್ಬರು ಪಂಚದಶಾಧಿಪತಿಗಳನ್ನೂ ಅವರ ಸಿಪಾಯಿಗಳನ್ನೂ ದಹಿಸಿಬಿಟ್ಟಿತಲ್ಲಾ; ನನ್ನ ಪ್ರಾಣವಾದರೋ ನಿನ್ನ ದೃಷ್ಟಿಯಲ್ಲಿ ಬೆಲೆಯುಳ್ಳದ್ದೆಂದು ಎಣಿಸಲ್ಪಡಲಿ ಎಂದು ಬೇಡಿಕೊಂಡನು.",
			"15": "<sup>15</sup> ಆಗ ಯೆಹೋವನ ದೂತನು ಎಲೀಯನಿಗೆ - ನೀನು ಇವನ ಸಂಗಡ ಹೋಗು; ಇವನಿಗೆ ಹೆದರಬೇಡ ಎಂದು ಹೇಳಿದ್ದರಿಂದ ಅವನು ಎದ್ದು ಇವನ ಜೊತೆಯಲ್ಲಿ ಅರಸನ ಬಳಿಗೆ ಹೋಗಿ ಅವನಿಗೆ -",
			"16": "<sup>16</sup> ನೀನು ಎಕ್ರೋನಿನ ದೇವರಾದ ಬಾಳ್ಜೆಬೂಬನನ್ನು ವಿಚಾರಿಸುವದಕ್ಕೆ ದೂತರನ್ನು ಕಳುಹಿಸಿದ್ದೇನು? ಅಂಥ ವಿಚಾರಮಾಡುವದಕ್ಕೆ ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ದೇವರಿಲ್ಲವೋ? ನೀನು ಹೀಗೆ ಮಾಡಿದ್ದರಿಂದ ಹತ್ತಿದ ಮಂಚದಿಂದಿಳಿಯದೆ ಸಾಯಲೇಬೇಕು ಎಂಬದಾಗಿ ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ ಎಂದು ಹೇಳಿದನು.",
			"17": "<sup>17</sup> ಯೆಹೋವನು ಎಲೀಯನ ಮುಖಾಂತರವಾಗಿ ಮುಂತಿಳಿಸಿದಂತೆ ಅರಸನು ಸತ್ತನು. ಅವನಿಗೆ ಮಗನಿಲ್ಲದ್ದರಿಂದ ಅವನಿಗೆ ಬದಲಾಗಿ [ಅವನ ತಮ್ಮನಾದ] ಯೋರಾಮನೆಂಬವನು ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಯೆಹೋಷಾಫಾಟನ ಮಗ ಯೆಹೋರಾಮನ ಆಳಿಕೆಯ ಎರಡನೆಯ ವರುಷದಲ್ಲಿ ಅರಸನಾದನು.",
			"18": "<sup>18</sup> ಅಹಜ್ಯನ ಉಳಿದ ಚರಿತ್ರೆಯೂ ಅವನ ಕೃತ್ಯಗಳೂ ಇಸ್ರಾಯೇಲ್ಯರ ರಾಜಕಾಲವೃತ್ತಾಂತ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ."
		},
		"2": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಎಲೀಯನನ್ನು ಸುಳಿಗಾಳಿಯ ಮುಖಾಂತರವಾಗಿ ಪರಲೋಕಕ್ಕೆ ಸೇರಿಸುವ ಸಮಯ ಬಂದಾಗ ಎಲೀಯನು ಎಲೀಷನೊಡನೆ ಗಿಲ್ಗಾಲನ್ನು ಬಿಟ್ಟು ಹೊರಟನು.",
			"2": "<sup>2</sup> ಆಗ ಎಲೀಯನು ಎಲೀಷನಿಗೆ - ದಯವಿಟ್ಟು ನೀನು ಇಲ್ಲೇ ಇರು; ಯೆಹೋವನು ನನಗೆ ಬೇತೇಲಿಗೆ ಹೋಗಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿದ್ದಾನೆ ಅನ್ನಲು ಎಲೀಷನು - ಯೆಹೋವನಾಣೆ, ನಿನ್ನ ಜೀವದಾಣೆ, ನಾನು ನಿನ್ನನ್ನು ಬಿಟ್ಟುಹೋಗುವದಿಲ್ಲ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"3": "<sup>3</sup> ತರುವಾಯ ಅವರಿಬ್ಬರೂ ಬೇತೇಲಿಗೆ ಹೋದರು. ಬೇತೇಲಿನ ಪ್ರವಾದಿಮಂಡಲಿಯವರು ಎಲೀಷನನ್ನು ಎದುರುಗೊಂಡು ಅವನನ್ನು - ಯೆಹೋವನು ಈಹೊತ್ತು ನಿನ್ನ ಯಜಮಾನನನ್ನು ನಿನ್ನ ಬಳಿಯಿಂದ ಮೇಲಕ್ಕೆ ತೆಗೆದುಕೊಳ್ಳುವನೆಂಬದು ನಿನಗೆ ಗೊತ್ತುಂಟೋ ಎಂದು ಕೇಳಿದರು. ಅದಕ್ಕೆ ಅವನು -",
			"4": "<sup>4</sup> ಗೊತ್ತುಂಟು, ಸುಮ್ಮನಿರ್ರಿ ಅಂದನು. ಎಲೀಯನು ಎಲೀಷನಿಗೆ - ದಯವಿಟ್ಟು ಇಲ್ಲೇ ಇರು; ಯೆಹೋವನು ನನಗೆ ಯೆರಿಕೋವಿಗೆ ಹೋಗಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿದ್ದಾನೆ ಎಂದು ಹೇಳಿದನು. ಆದರೆ ಎಲೀಷನು - ಯೆಹೋವನಾಣೆ, ನಿನ್ನ ಜೀವದಾಣೆ, ನಾನು ನಿನ್ನನ್ನು ಬಿಟ್ಟುಹೋಗುವದಿಲ್ಲ ಎಂದು ಉತ್ತರಕೊಟ್ಟು ಅವನ ಜೊತೆಯಲ್ಲಿ ಯೆರಿಕೋವಿಗೆ ಹೋದನು.",
			"5": "<sup>5</sup> ಅಲ್ಲಿನ ಪ್ರವಾದಿಮಂಡಲಿಯವರು ಅವನನ್ನು ಎದುರುಗೊಂಡು - ಯೆಹೋವನು ಈಹೊತ್ತು ನಿನ್ನ ಯಜಮಾನನನ್ನು ನಿನ್ನ ಬಳಿಯಿಂದ ಮೇಲಕ್ಕೆ ಕರೆದುಕೊಳ್ಳುವನೆಂದು ನಿನಗೆ ಗೊತ್ತುಂಟೋ ಎಂದು ಕೇಳಿದರು. ಅದಕ್ಕೆ ಅವನು - ಗೊತ್ತುಂಟು, ಸುಮ್ಮನಿರ್ರಿ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"6": "<sup>6</sup> ಎಲೀಯನು ತಿರಿಗಿ ಅವನಿಗೆ - ದಯವಿಟ್ಟು ನೀನು ಇಲ್ಲೇ ಇರು; ಯೆಹೋವನು ನನಗೆ ಯೊರ್ದನಿಗೆ ಹೋಗಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿದ್ದಾನೆ ಎಂದು ಹೇಳಿದನು. ಆಗ ಅವನು - ಯೆಹೋವನಾಣೆ, ನಿನ್ನ ಜೀವದಾಣೆ, ನಾನು ನಿನ್ನನ್ನು ಬಿಟ್ಟು ಹೋಗುವದಿಲ್ಲ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು; ಆದದರಿಂದ ಇಬ್ಬರೂ ಹೊರಟು ಯೊರ್ದನ್ ಹೊಳೆಯ ದಡಕ್ಕೆ ಬಂದರು.",
			"7": "<sup>7</sup> ಪ್ರವಾದಿ ಮಂಡಲಿಯವರಲ್ಲಿ ಐವತ್ತು ಜನರು ಇವರ ಹಿಂದಿನಿಂದಲೇ ಬಂದು ಸ್ವಲ್ಪ ದೂರದಲ್ಲಿ ನಿಂತುಕೊಂಡರು.",
			"8": "<sup>8</sup> ಅನಂತರ ಎಲೀಯನು ತನ್ನ ಕಂಬಳಿಯನ್ನು ಮಡಚಿ ಅದರಿಂದ ನೀರನ್ನು ಹೊಡೆಯಲು ನೀರು ಎರಡು ಭಾಗವಾಯಿತು; ಇಬ್ಬರೂ ಒಣನೆಲದ ಮೇಲೆ ದಾಟಿ ಹೋದರು.",
			"9": "<sup>9</sup> ಅವರು ಆಚೇದಡಕ್ಕೆ ಸೇರಿದ ಮೇಲೆ ಎಲೀಯನು ಎಲೀಷನನ್ನು - ನಿನ್ನನ್ನು ಬಿಟ್ಟುಹೋಗುವ ಮೊದಲು ನಾನು ನಿನಗೋಸ್ಕರ ಏನು ಮಾಡಬೇಕನ್ನುತ್ತೀ ಹೇಳು ಎಂದು ಕೇಳಿದನು. ಅದಕ್ಕೆ ಎಲೀಷನು - ನಿನಗಿರುವ ಆತ್ಮದಲ್ಲಿ ನನಗೆ ಎರಡು ಪಾಲನ್ನು ಅನುಗ್ರಹಿಸು ಎಂದು ಬೇಡಿಕೊಂಡನು.",
			"10": "<sup>10</sup> ಆಗ ಎಲೀಯನು ಅವನಿಗೆ - ನೀನು ದುರ್ಲಭವಾದದ್ದನ್ನು ಕೇಳಿಕೊಂಡಿ; ಆದರೂ ನಾನು ನಿನ್ನ ಬಳಿಯಿಂದ ತೆಗೆಯಲ್ಪಡುವಾಗ ನೀನು ನನ್ನನ್ನು ನೋಡುವದಾದರೆ ಅದು ದೊರಕುವದು; ಇಲ್ಲವಾದರೆ ದೊರಕುವದಿಲ್ಲ ಅಂದನು.",
			"11": "<sup>11</sup> ಅವರು ಮಾತಾಡುತ್ತಾ ಮುಂದೆ ಹೋಗುತ್ತಿರುವಾಗ ಫಕ್ಕನೆ ಅಗ್ನಿಮಯವಾದ ರಥರಥಾಶ್ವಗಳು ನಡುವೆ ಬಂದು ಅವರನ್ನು ವಿಂಗಡಿಸಿದವು. ಎಲೀಯನು ಸುಳಿಗಾಳಿಯ ಮುಖಾಂತರವಾಗಿ ಪರಲೋಕಕ್ಕೆ ಹೋದನು.",
			"12": "<sup>12</sup> ಎಲೀಷನು ಅದನ್ನು ನೋಡುತ್ತಾ - ನನ್ನ ತಂದೆಯೇ, ನನ್ನ ತಂದೆಯೇ, ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ರಥರಥಾಶ್ವಗಳಾಗಿದ್ದವನೇ ಎಂದು ಕೂಗಿ ಎಲೀಯನು ತನಗೆ ಕಾಣಿಸದೆ ಹೋದ ಮೇಲೆ ತನ್ನ ಬಟ್ಟೆಗಳನ್ನು ಹರಿದು ಎರಡು ತುಂಡುಮಾಡಿದನು.",
			"13": "<sup>13</sup> ಅನಂತರ ಅವನು ಮೇಲಣಿಂದ ಬಿದ್ದ ಎಲೀಯನ ಕಂಬಳಿಯನ್ನು ತೆಗೆದುಕೊಂಡು ಯೊರ್ದನ್ ನದೀ ತೀರಕ್ಕೆ ಬಂದು",
			"14": "<sup>14</sup> ಎಲೀಯನ ದೇವರಾದ ಯೆಹೋವನೆಲ್ಲಿ ಅಂದುಕೊಂಡು ಆ ಕಂಬಳಿಯಿಂದ ನೀರನ್ನು ಹೊಡೆಯಲು ಅದು ಎರಡು ಭಾಗವಾಯಿತು; ಅವನು ದಾಟಿಹೋದನು.",
			"15": "<sup>15</sup> ದೂರದಲ್ಲಿ ನಿಂತಿದ್ದ ಯೆರಿಕೋವಿನ ಪ್ರವಾದಿ ಮಂಡಲಿಯವರು ಇದನ್ನು ಕಂಡು ಎಲೀಯನಿಗಿದ್ದ ಆತ್ಮವು ಎಲೀಷನ ಮೇಲೆ ಬಂದಿತೆಂದು ತಿಳಿದುಕೊಂಡು ಅವನನ್ನು ಎದುರುಗೊಂಡು ಅವನಿಗೆ ಸಾಷ್ಟಾಂಗ ನಮಸ್ಕಾರಮಾಡಿದರು.",
			"16": "<sup>16</sup> ಅನಂತರ ಅವರು ಅವನಿಗೆ - ನಿನ್ನ ಸೇವಕರಾದ ನಮ್ಮಲ್ಲಿ ಐವತ್ತು ಮಂದಿ ಬಲಿಷ್ಠ ಜನರಿದ್ದಾರೆ; ನಿನ್ನ ಯಜಮಾನನನ್ನು ಹುಡುಕಹೋಗುವದಕ್ಕೆ ಅವರಿಗೆ ಅಪ್ಪಣೆಯಾಗಲಿ. ಯೆಹೋವನ ಆತ್ಮವು ಅವನನ್ನು ಎತ್ತಿಕೊಂಡು ಹೋಗಿ ಒಂದು ಬೆಟ್ಟದ ಮೇಲಾಗಲಿ ತಗ್ಗಿನಲ್ಲಾಗಲಿ ಇಟ್ಟಿರಬಹುದು ಎಂದು ಹೇಳಿದರು; ಅವನು ಅವರಿಗೆ - ನೀವು ಕಳುಹಿಸಬೇಡಿರಿ ಅಂದನು.",
			"17": "<sup>17</sup> ಆದರೆ ಅವರು ಅವನನ್ನು ಒತ್ತಾಯಪಡಿಸಿದ್ದರಿಂದ ಅವನು ಬೇಸರಗೊಂಡು ಕಳುಹಿಸುವದಕ್ಕೆ ಅಪ್ಪಣೆಕೊಟ್ಟನು. ಅವರು ಐವತ್ತು ಮಂದಿಯನ್ನು ಕಳುಹಿಸಿದರು. ಇವರು ಮೂರು ದಿವಸ ಹುಡುಕಿದರೂ ಎಲೀಯನನ್ನು ಕಾಣದೆ",
			"18": "<sup>18</sup> ಇನ್ನೂ ಯೆರಿಕೋವಿನಲ್ಲೇ ಇದ್ದ ಎಲೀಷನ ಬಳಿಗೆ ಬಂದರು; ಅವನು ಅವರಿಗೆ - ಹೋಗಬೇಡಿರೆಂದು ನಾನು ಹೇಳಲಿಲ್ಲವೋ ಅಂದನು.",
			"19": "<sup>19</sup> ಯೆರಿಕೋವಿನ ಜನರು ಎಲೀಷನಿಗೆ - ನಮ್ಮ ಒಡೆಯನಾದ ನಿನಗೆ ಗೊತ್ತಿರುವಂತೆ ಈ ಊರು ಕಟ್ಟಲ್ಪಟ್ಟಿರುವ ಸ್ಥಳವು ಒಳ್ಳೇದಾಗಿದೆ; ಆದರೆ ನೀರು ಕೆಟ್ಟದ್ದಾಗಿರುವದರಿಂದ ಬಂಜೆತನ ಹೆಚ್ಚು ಎಂದು ಹೇಳಿದರು.",
			"20": "<sup>20</sup> ಆಗ ಎಲೀಷನು ಅವರಿಗೆ - ಒಂದು ಹೊಸ ಮಡಕೆಯಲ್ಲಿ ಉಪ್ಪು ಹಾಕಿ ಅದನ್ನು ತಂದುಕೊಡಿರಿ ಅಂದನು.",
			"21": "<sup>21</sup> ಅವರು ತಂದುಕೊಡಲು ಅವನು ಬುಗ್ಗೆಗೆ ಹೋಗಿ ಆ ನೀರಿನಲ್ಲಿ ಉಪ್ಪು ಹಾಕಿ - ಇನ್ನು ಮುಂದೆ ಈ ನೀರಿನಿಂದ ಮರಣವೂ ಬಂಜೆತನವೂ ಉಂಟಾಗದಂತೆ ಇದರಲ್ಲಿದ್ದ ದೋಷವನ್ನೆಲ್ಲಾ ಪರಿಹರಿಸಿದ್ದೇನೆ ಎಂಬದಾಗಿ ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ ಎಂದು ಹೇಳಿದನು.",
			"22": "<sup>22</sup> ಕೂಡಲೆ ನೀರಿನಲ್ಲಿದ್ದ ದೋಷವೆಲ್ಲಾ ಪರಿಹಾರವಾಯಿತು; ಎಲೀಷನ ವಾಕ್ಯಬಲದಿಂದ ಅದು ಇಂದಿನವರೆಗೂ ಹಾಗೆಯೇ ಇರುತ್ತದೆ.",
			"23": "<sup>23</sup> ಎಲೀಷನು ಅಲ್ಲಿಂದ ಬೇತೇಲಿಗೆ ಹೊರಟು ಅಲ್ಲಿನ ಗುಡ್ಡವನ್ನು ಹತ್ತುತ್ತಿರುವಾಗ ಆ ಊರಿನ ಹುಡುಗರು ಹೊರಗೆ ಬಂದು - ಬೋಳಮಂಡೆಯವನೇ, ಏರು; ಬೋಳಮಂಡೆಯವನೇ, ಏರು ಎಂದು ಕೂಗಿ ಅವನನ್ನು ಪರಿಹಾಸ್ಯಮಾಡಿದರು.",
			"24": "<sup>24</sup> ಅವನು ಅವರ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡು ಯೆಹೋವನ ಹೆಸರಿನಲ್ಲಿ ಅವರನ್ನು ಶಪಿಸಿದನು. ಕೂಡಲೆ ಕಾಡಿನಿಂದ ಎರಡು ಹೆಣ್ಣು ಕರಡಿಗಳು ಬಂದು ಆ ಹುಡುಗರಲ್ಲಿ ನಾಲ್ವತ್ತೆರಡು ಮಂದಿಯನ್ನು ಹರಿದುಬಿಟ್ಟವು.",
			"25": "<sup>25</sup> ಎಲೀಷನು ಅಲ್ಲಿಂದ ಕರ್ಮೆಲ್‍ಬೆಟ್ಟಕ್ಕೆ ಹೋಗಿ ಅನಂತರ ಸಮಾರ್ಯಕ್ಕೆ ಬಂದನು."
		},
		"3": {
			"1": "<sup>1</sup> ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಯೆಹೋಷಾಫಾಟನ ಆಳಿಕೆಯ ಹದಿನೆಂಟನೆಯ ವರುಷದಲ್ಲಿ ಅಹಾಬನ ಮಗನಾದ ಯೋರಾಮನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾಗಿ ಸಮಾರ್ಯದಲ್ಲಿ ಹನ್ನೆರಡು ವರುಷ ಆಳಿದನು.",
			"2": "<sup>2</sup> ಇವನು ತನ್ನ ತಂದೆತಾಯಿಗಳಷ್ಟು ದುಷ್ಟನಾಗಿರಲಿಲ್ಲವಾದರೂ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾಗಿದ್ದನು. ತನ್ನ ತಂದೆಯು ಬಾಳನಿಗೋಸ್ಕರ ನಿಲ್ಲಿಸಿದ ಕಲ್ಲಿನ ಕಂಬವನ್ನು ತೆಗೆದುಹಾಕಿದನು.",
			"3": "<sup>3</sup> ಆದರೂ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಪಾಪಕ್ಕೆ ಪ್ರೇರಿಸಿದ ನೆಬಾಟನ ಮಗನಾದ ಯಾರೊಬ್ಬಾಮನ ಮಾರ್ಗವನ್ನು ಬಿಡಲೇ ಇಲ್ಲ.",
			"4": "<sup>4</sup> ಅನೇಕ ಕುರಿಹಿಂಡುಗಳುಳ್ಳವನಾಗಿದ್ದ ಮೋವಾಬ್ಯರ ಅರಸನಾದ ಮೇಷನೆಂಬವನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಿಗೆ ಲಕ್ಷ ಕುರಿಗಳ ಮತ್ತು ಲಕ್ಷ ಟಗರುಗಳ ಉಣ್ಣೆಯನ್ನು ಕಪ್ಪವಾಗಿ ಕೊಡುತ್ತಿದ್ದನು.",
			"5": "<sup>5</sup> ಆದರೆ ಅಹಾಬನು ಮರಣಹೊಂದಿದನಂತರ ಇವನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಿಗೆ ವಿರೋಧವಾಗಿ ತಿರುಗಿಬಿದ್ದನು.",
			"6": "<sup>6</sup> ಆದದರಿಂದ ಅರಸನಾದ ಯೋರಾಮನು ಒಡನೆ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಕೂಡಿಸಿಕೊಂಡು ಸಮಾರ್ಯದಿಂದ ಹೊರಟು",
			"7": "<sup>7</sup> ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಯೆಹೋಷಾಫಾಟನಿಗೆ ದೂತರ ಮುಖಾಂತರವಾಗಿ - ಮೋವಾಬ್ಯರ ಅರಸನು ನನಗೆ ವಿರೋಧವಾಗಿ ತಿರುಗಿಬಿದ್ದಿದ್ದಾನೆ; ಮೋವಾಬ್ಯರೊಡನೆ ಯುದ್ಧಮಾಡುವದಕ್ಕೆ ನೀನು ನನ್ನ ಜೊತೆಯಲ್ಲಿ ಬರುತ್ತೀಯೋ ಎಂದು ಹೇಳಿಕಳುಹಿಸಿದನು. ಅದಕ್ಕೆ ಯೆಹೋಷಾಫಾಟನು - ಬರುತ್ತೇನೆ; ನಾನೂ ನೀನೂ, ನನ್ನ ಜನರೂ ನಿನ್ನ ಜನರೂ, ನನ್ನ ಕುದುರೆಗಳೂ ನಿನ್ನ ಕುದುರೆಗಳೂ ಒಂದೇಯಲ್ಲವೋ ಎಂದು ಉತ್ತರಕೊಟ್ಟು",
			"8": "<sup>8</sup> ಯಾವ ಮಾರ್ಗವಾಗಿ ಹೋಗಬೇಕೆಂದು ವಿಚಾರಿಸಿದಾಗ ಯೋರಾಮನು ಎದೋಮ್ಯರ ಅರಣ್ಯಮಾರ್ಗವಾಗಿ ಅಂದನು.",
			"9": "<sup>9</sup> ಆಗ ಇಸ್ರಾಯೇಲ್ ಯೆಹೂದ ಎದೋಮ್ ರಾಜ್ಯಗಳ ಅರಸರು ಹೊರಟು ಸುತ್ತಾದ ದಾರಿಯಿಂದ ಏಳು ದಿವಸ ಪ್ರಯಾಣಮಾಡಿದರು. ಮಾರ್ಗದಲ್ಲಿ ಅವರ ಸೈನಿಕರಿಗೂ ಅವರು ತಂದ ಪಶುಗಳಿಗೂ ನೀರು ಸಿಕ್ಕದೆಹೋದದರಿಂದ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನು -",
			"10": "<sup>10</sup> ಅಯ್ಯೋ! ಯೆಹೋವನು ಮೂರು ಮಂದಿ ಅರಸರಾದ ನಮ್ಮನ್ನು ಮೋವಾಬ್ಯರ ಕೈಗೆ ಒಪ್ಪಿಸುವದಕ್ಕೋಸ್ಕರ ಇಲ್ಲಿಗೆ ಬರಮಾಡಿದ್ದಾನೆ ಎಂದು ಗೋಳಾಡಿದನು.",
			"11": "<sup>11</sup> ಆದರೆ ಯೇಹೋಷಾಫಾಟನು - ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನಮಗೋಸ್ಕರ ವಿಚಾರಿಸಬಲ್ಲವನಾದ ಒಬ್ಬ ಪ್ರವಾದಿಯು ಇಲ್ಲಿರುವದಿಲ್ಲವೋ ಎಂದು ಕೇಳಿದನು. ಆಗ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನ ಸೇವಕರಲ್ಲೊಬ್ಬನು ಅವನಿಗೆ - ಶಾಫಾಟನ ಮಗನೂ ಎಲೀಯನ ಕೈಗೆ ನೀರು ಕೊಡುತ್ತಿದ್ದವನೂ ಆದ ಎಲೀಷನೆಂಬ ಪ್ರವಾದಿಯು ಇಲ್ಲಿರುತ್ತಾನೆ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"12": "<sup>12</sup> ಯೆಹೋಷಾಫಾಟನು - ಅವನು ಹೇಗೂ ಯೆಹೋವನ ಉತ್ತರವನ್ನು ತಿಳಿಸುವನು ಅಂದದರಿಂದ ಇಸ್ರಾಯೇಲ್ ಎದೋಮ್ ರಾಜ್ಯಗಳ ಅರಸರು ಯೆಹೋಷಾಫಾಟನೊಡನೆ ಎಲೀಷನ ಬಳಿಗೆ ಹೋದರು.",
			"13": "<sup>13</sup> ಎಲೀಷನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಿಗೆ - ನನಗೂ ನಿನಗೂ ಗೊಡವೆಯೇನು? ನಿನ್ನ ತಂದೆತಾಯಿಗಳ ಪ್ರವಾದಿಗಳ ಬಳಿಗೆ ಹೋಗು ಎಂದು ಹೇಳಿದನು. ಅದಕ್ಕೆ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನು - ಹಾಗನ್ನಬೇಡ; ಯೆಹೋವನು ಈ ಮೂರು ಮಂದಿ ಅರಸರಾದ ನಮ್ಮನ್ನು ಮೋವಾಬ್ಯರ ಕೈಗೆ ಒಪ್ಪಿಸುವದಕ್ಕೋಸ್ಕರ ಇಲ್ಲಿಗೆ ಬರಮಾಡಿದನಲ್ಲವೋ ಅಂದನು.",
			"14": "<sup>14</sup> ಆಗ ಎಲೀಷನು - ನಾನು ಸನ್ನಿಧಿಸೇವೆ ಮಾಡುತ್ತಿರುವ ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನಾಣೆ, ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಯೆಹೋಷಾಫಾಟನು ನಿನ್ನ ಜೊತೆಯಲ್ಲಿರದಿದ್ದರೆ ನಾನು ನಿನ್ನನ್ನು ನೋಡುತ್ತಿದ್ದಿಲ್ಲ, ಲಕ್ಷಿಸುತ್ತಿದ್ದಿಲ್ಲ.",
			"15": "<sup>15</sup> ಈಗ ಒಬ್ಬ ವಾದ್ಯಗಾರನನ್ನು ನನ್ನ ಹತ್ತಿರ ಕರಕೊಂಡು ಬನ್ನಿರಿ ಎಂದು ಹೇಳಿದನು. (ವಾದ್ಯಗಳ ಸ್ವರವನ್ನು ಕೇಳುವಾಗೆಲ್ಲಾ ಎಲೀಷನಲ್ಲಿ ಯೆಹೋವನ ಬಲವು ಬರುತ್ತಿತ್ತು.)",
			"16": "<sup>16</sup> ಅನಂತರ ಎಲೀಷನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಿಗೆ - ಈ ಹಳ್ಳದಲ್ಲಿ ತುಂಬಾ ಗುಂಡಿಗಳನ್ನು ಮಾಡಿರಿ;",
			"17": "<sup>17</sup> ಗಾಳಿ ಮಳೆಗಳು ಕಾಣದಿದ್ದರೂ ಇದು ನೀರಿನಿಂದ ತುಂಬುವದು. ನೀವೂ ನಿಮ್ಮ ಕುರಿದನಗಳೂ ಎಲ್ಲಾ ಪಶುಗಳೂ ಕುಡಿಯಬಹುದು ಎಂಬದಾಗಿ ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ.",
			"18": "<sup>18</sup> ಆತನು ತನ್ನ ದೃಷ್ಟಿಯಲ್ಲಿ ಅಲ್ಪವಾಗಿರುವ ಈ ಕಾರ್ಯದ ಹೊರತಾಗಿ ಮೋವಾಬ್ಯರನ್ನೂ ನಿಮ್ಮ ಕೈಗೆ ಒಪ್ಪಿಸುವನು.",
			"19": "<sup>19</sup> ನೀವು ಅವರ ಕೋಟೆಕೊತ್ತಲುಗಳುಳ್ಳ ಪಟ್ಟಣಗಳನ್ನೂ ವಿಶೇಷವಾದ ಎಲ್ಲಾ ಊರುಗಳನ್ನೂ ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡು ಎಲ್ಲಾ ಹಣ್ಣಿನ ಮರಗಳನ್ನು ಕಡಿದುಹಾಕಿ ಬಾವಿಗಳನ್ನು ಮುಚ್ಚಿಬಿಟ್ಟು ಹೊಲಗಳನ್ನು ಕಲ್ಲು ತುಂಬಿಸಿ ಹಾಳು ಮಾಡುವಿರಿ ಎಂದು ನುಡಿದನು.",
			"20": "<sup>20</sup> ಪ್ರಾತಃಕಾಲನೈವೇದ್ಯವನ್ನು ಸಮರ್ಪಿಸುವ ಹೊತ್ತಿನಲ್ಲಿ ಫಕ್ಕನೆ ಎದೋಮಿನ ಕಡೆಯಿಂದ ನೀರು ಬಂದು ದೇಶದಲ್ಲೆಲ್ಲಾ ತುಂಬಿಕೊಂಡಿತು.",
			"21": "<sup>21</sup> ಅರಸುಗಳು ತಮಗೆ ವಿರೋಧವಾಗಿ ಯುದ್ಧಕ್ಕೆ ಬಂದಿದ್ದಾರೆಂಬದನ್ನು ಮೋವಾಬ್ಯರು ಕೇಳಿ ಆಯುಧಗಳನ್ನು ಧರಿಸಶಕ್ತರಾದ ಎಲ್ಲಾ ಯೌವನಸ್ಥರನ್ನೂ ಪ್ರಾಯಸ್ಥರನ್ನೂ ಕೂಡಿಸಿಕೊಂಡು ತಮ್ಮ ದೇಶದ ಮೇರೆಗೆ ಬಂದು ಬೆಳಿಗ್ಗೆ ಎದ್ದು ನೋಡಲು",
			"22": "<sup>22</sup> ಎದುರಿಗಿದ್ದ ನೀರು ಇವರ ದೃಷ್ಟಿಗೆ ಬಿದ್ದಿತು. ಸೂರ್ಯ ಪ್ರಕಾಶದಿಂದ ಆ ನೀರು ರಕ್ತದಂತೆ ಕೆಂಪಾಗಿ ಕಾಣಿಸುತ್ತಿದ್ದದರಿಂದ ಇವರು -",
			"23": "<sup>23</sup> ಅದು ರಕ್ತ; ಅರಸರು ತಮ್ಮ ತಮ್ಮೊಳಗೆ ಜಗಳವಾಡಿ ಸಂಹೃತರಾಗಿರಬೇಕು; ಮೋವಾಬ್ಯರೇ, ಏಳಿರಿ, ಸುಲಿಗೆಗೆ ಹೋಗೋಣ ಎಂದು ಕೂಗಿಕೊಂಡು",
			"24": "<sup>24</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಪಾಳೆಯಕ್ಕೆ ಹೋದರು. ಆದರೆ ಇಸ್ರಾಯೇಲ್ಯರು ಎದ್ದು ಮೋವಾಬ್ಯರನ್ನು ಸೋಲಿಸಿ ಓಡಿಸಿಬಿಟ್ಟರು. ಇದಲ್ಲದೆ ಅವರು ಮೋವಾಬ್ಯರನ್ನು ಸಂಹರಿಸುತ್ತಾ",
			"25": "<sup>25</sup> ಅವರ ದೇಶದೊಳಗೆ ನುಗ್ಗಿ ಎಲ್ಲಾ ಪಟ್ಟಣಗಳನ್ನು ಕೆಡವಿ ಹೊಲಗಳನ್ನು ಕಲ್ಲುಗಳಿಂದ ತುಂಬಿಸಿ ಬಾವಿಗಳನ್ನು ಮುಚ್ಚಿ ಹಣ್ಣಿನ ಮರಗಳನ್ನು ಕಡಿದುಹಾಕಿದರು. ಕೀರ್ಹರೆಷೆತ್ ಎಂಬ ಒಂದೇ ದುರ್ಗ ಉಳಿಯಿತು. ಕವಣೆಹೊಡೆಯುವವರು ಅದನ್ನೂ ಸುತ್ತಿಕೊಂಡು ಕಲ್ಲೆಸೆದರು.",
			"26": "<sup>26</sup> ಮೋವಾಬ್ಯರ ಅರಸನು ತಾನು ಯುದ್ಧದಲ್ಲಿ ಗೆಲ್ಲಲಾರೆನೆಂದು ತಿಳಿದು ಏಳು ನೂರು ಮಂದಿ ಭಟರೊಡನೆ ಸೈನ್ಯದೊಳಗೆ ನುಗ್ಗಿ ಎದೋಮ್ಯರ ಅರಸನ ಕಡೆಗೆ ಹೋಗ ಪ್ರಯತ್ನಿಸಿದನು; ಆದರೆ ಆಗದೆಹೋಯಿತು.",
			"27": "<sup>27</sup> ಆಗ ಅವನು ತನಗೆ ಬದಲಾಗಿ ಅರಸನಾಗತಕ್ಕ ತನ್ನ ಚೊಚ್ಚಲಮಗನನ್ನು ಹಿಡಿದು ವಧಿಸಿ ಗೋಡೆಯ ಮೇಲೆ ಸರ್ವಾಂಗಹೋಮ ಮಾಡಿದನು. ಇದರಿಂದ ಮೋವಾಬ್ಯರಿಗೆ ಇಸ್ರಾಯೇಲ್ಯರ ಮೇಲೆ ಕೋಪೋದ್ರೇಕವುಂಟಾಗಲು ಅವರು ಅವನನ್ನು ಬಿಟ್ಟು ತಮ್ಮ ದೇಶಕ್ಕೆ ಹೊರಟುಹೋದರು."
		},
		"4": {
			"1": "<sup>1</sup> ಒಂದು ದಿವಸ ಪ್ರವಾದಿಮಂಡಲಿಯವರಲ್ಲೊಬ್ಬನ ಹೆಂಡತಿಯು ಎಲೀಷನ ಹತ್ತಿರ ಬಂದು ಅವನಿಗೆ - ನಿನ್ನ ಸೇವಕನಾದ ನನ್ನ ಗಂಡನು ಮರಣಹೊಂದಿದನು; ಅವನು ಯೆಹೋವನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವನಾಗಿದ್ದನೆಂಬದು ನಿನಗೆ ಗೊತ್ತುಂಟಲ್ಲಾ; ಸಾಲಕೊಟ್ಟವನು ನನ್ನ ಇಬ್ಬರು ಮಕ್ಕಳನ್ನು ದಾಸರನ್ನಾಗಿ ತೆಗೆದುಕೊಂಡು ಹೋಗುವದಕ್ಕೆ ಬಂದಿದ್ದಾನೆ ಎಂದು ಮೊರೆಯಿಟ್ಟಳು.",
			"2": "<sup>2</sup> ಎಲೀಷನು ಆಕೆಗೆ - ನಾನು ನಿನಗೇನು ಮಾಡಬೇಕನ್ನುತ್ತೀ? ನಿನ್ನ ಮನೆಯಲ್ಲಿ ಏನಿರುತ್ತದೆ, ಹೇಳು ಅಂದನು. ಅದಕ್ಕೆ ಆಕೆಯು - ನಿನ್ನ ದಾಸಿಯ ಮನೆಯಲ್ಲಿ ಒಂದು ಮೊಗೆ ಎಣ್ಣೆ ಹೊರತಾಗಿ ಏನೂ ಇಲ್ಲ ಎಂದು ಉತ್ತರಕೊಟ್ಟಳು.",
			"3": "<sup>3</sup> ಆಗ ಎಲೀಷನು ಆಕೆಗೆ - ಹೋಗಿ ನಿನ್ನ ನೆರೆಯವರಿಂದ ಸಿಕ್ಕುವಷ್ಟು ಬರೀ ಪಾತ್ರೆಗಳನ್ನು ಕೇಳಿಕೊಂಡು ಬಾ.",
			"4": "<sup>4</sup> ಅನಂತರ ನಿನ್ನ ಮಕ್ಕಳನ್ನು ಒಳಗೆ ಕರಕೊಂಡು ಬಾಗಲನ್ನು ಮುಚ್ಚಿ ಎಣ್ಣೆಯನ್ನು ಪಾತ್ರೆಗಳಲ್ಲಿ ಹೊಯ್ದು ತುಂಬಿದವುಗಳನ್ನೆಲ್ಲಾ ಒತ್ತಟ್ಟಿಗಿಡು ಎಂದು ಹೇಳಿದನು.",
			"5": "<sup>5</sup> ಆಕೆಯು ಹೋಗಿ ಮಕ್ಕಳನ್ನು ಒಳಗೆ ಕರಕೊಂಡು ಬಾಗಲನ್ನು ಮುಚ್ಚಿ ಅವರು ಮುಂದಿಟ್ಟ ಪಾತ್ರೆಗಳಲ್ಲೆಲ್ಲಾ ಎಣ್ಣೆ ಹೊಯ್ದಳು.",
			"6": "<sup>6</sup> ಪಾತ್ರೆಗಳು ತುಂಬಿದಾಗ ಆಕೆಯು ಮಗನಿಗೆ - ಇನ್ನೊಂದು ಪಾತ್ರೆಯನ್ನು ತಂದಿಡು ಅನ್ನಲು ಅವನು - ಪಾತ್ರೆಗಳು ತೀರಿದವೆಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"7": "<sup>7</sup> ಕೂಡಲೆ ಎಣ್ಣೆಯುಕ್ಕುವದು ನಿಂತು ಹೋಯಿತು. ತರುವಾಯ ಆಕೆಯು ದೇವರ ಮನುಷ್ಯನ ಹತ್ತಿರ ಬಂದು ನಡೆದದ್ದನ್ನೆಲ್ಲಾ ತಿಳಿಸಿದಳು. ಅವನು ಆಕೆಗೆ - ಹೋಗಿ ಎಣ್ಣೆಯನ್ನು ಮಾರಿ ಸಾಲತೀರಿಸು; ಉಳಿದ ಹಣದಿಂದ ನೀನೂ ನಿನ್ನ ಮಕ್ಕಳೂ ಜೀವನ ಮಾಡಿರಿ ಅಂದನು.",
			"8": "<sup>8</sup> ಒಂದು ಸಾರಿ ಎಲೀಷನು ಶೂನೇಮಿಗೆ ಹೋದನು. ಅಲ್ಲಿನ ಒಬ್ಬ ಕುಲೀನ ಸ್ತ್ರೀಯು ಅವನನ್ನು ತನ್ನ ಮನೆಯಲ್ಲಿ ಊಟಮಾಡಬೇಕೆಂದು ಒತ್ತಾಯಪಡಿಸಿದಳು. ಅಂದಿನಿಂದ ಅವನು ಆ ಮಾರ್ಗದಿಂದ ಹೋಗುವಾಗೆಲ್ಲಾ ಆ ಮನೆಯಲ್ಲೇ ಊಟಮಾಡುತ್ತಿದ್ದನು.",
			"9": "<sup>9</sup> ಆ ಸ್ತ್ರೀಯು ತನ್ನ ಗಂಡನಿಗೆ - ಯಾವಾಗಲೂ ಈ ದಾರಿಯಿಂದ ಹೋಗುತ್ತಾ ಬರುತ್ತಾ ಇರುವ ಆ ಮನುಷ್ಯನು ಪರಿಶುದ್ಧನೂ ದೇವರ ಮನುಷ್ಯನೂ ಆಗಿರುತ್ತಾನೆಂದು ನನಗೆ ಗೊತ್ತಾಯಿತು.",
			"10": "<sup>10</sup> ಆದದರಿಂದ ನಾವು ಅವನಿಗೋಸ್ಕರ ಮಾಳಿಗೆಯ ಮೇಲೆ ಒಂದು ಸಣ್ಣ ಕೋಣೆಯನ್ನು ಕಟ್ಟಿಸಿ ಅದರಲ್ಲಿ ಒಂದು ಮಂಚ, ಮೇಜು, ಕುರ್ಚಿ, ದೀಪಸ್ತಂಭ, ಇವುಗಳನ್ನು ಇಡೋಣ; ಅವನು ಇಲ್ಲಿ ಬಂದಾಗೆಲ್ಲಾ ಅದರಲ್ಲಿ ವಾಸಮಾಡಲಿ ಎಂದು ಹೇಳಿದಳು.",
			"11": "<sup>11</sup> ಒಂದು ದಿವಸ ಎಲೀಷನು ಅಲ್ಲಿಗೆ ಬಂದು ಆ ಕೋಣೆಯೊಳಗೆ ಪ್ರವೇಶಿಸಿ ದಣುವಾರಿಸಿಕೊಂಡನಂತರ",
			"12": "<sup>12</sup> ಆ ಶೂನೇಮ್ಯಳನ್ನು ಕರೆಯಬೇಕೆಂದು ತನ್ನ ಸೇವಕನಾದ ಗೇಹಜಿಗೆ ಆಜ್ಞಾಪಿಸಿದನು. ಅವನು ಹೋಗಿ ಆಕೆಯನ್ನು ಕರೆಯಲು ಆಕೆಯು ಬಂದು ಮುಂದೆ ನಿಂತಳು.",
			"13": "<sup>13</sup> ಆಗ ಎಲೀಷನು ಗೇಹಜಿಯ ಮುಖಾಂತರವಾಗಿ ಆಕೆಯನ್ನು - ನೀನು ಕಷ್ಟಪಟ್ಟು ನಮಗೆ ಇಷ್ಟೆಲ್ಲಾ ಉಪಕಾರಮಾಡಿದಿ; ನಾನು ನಿನಗೆ ಯಾವ ಪ್ರತ್ಯುಪಕಾರ ಮಾಡಲಿ? ನಿನಗೋಸ್ಕರವಾಗಿ ಅರಸನ ಮುಂದಾಗಲಿ ಸೇನಾಪತಿಯ ಮುಂದಾಗಲಿ ಮಾತಾಡಬೇಕೋ ಎಂದು ಕೇಳಲು ಆಕೆಯು - ನಾನು ಸ್ವಕುಲದವರ ಮಧ್ಯದಲ್ಲಿ ಸುಖವಾಗಿದ್ದೇನೆ ಎಂದು ಉತ್ತರಕೊಟ್ಟಳು.",
			"14": "<sup>14</sup> ಆಮೇಲೆ ಎಲೀಷನು ಗೇಹಜಿಯನ್ನು - ನಾವು ಆಕೆಗೆ ಯಾವ ಉಪಕಾರವನ್ನು ಮಾಡಬಹುದು ಎಂದು ಕೇಳಲು ಅವನು - ಆಕೆಗೆ ಮಗನಿಲ್ಲ, ಗಂಡನು ಮುದುಕನಾಗಿದ್ದಾನೆ ಅಂದನು.",
			"15": "<sup>15</sup> ಇದನ್ನು ಕೇಳಿ ಎಲೀಷನು ಆ ಸ್ತ್ರೀಯನ್ನು ಕರೆಯಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿದನು. ಅವನು ಹೋಗಿ ಕರೆಯಲು ಆಕೆಯು ಬಂದು ಬಾಗಲಿನ ಹತ್ತಿರ ನಿಂತಳು.",
			"16": "<sup>16</sup> ಎಲೀಷನು ಆಕೆಗೆ - ನೀನು ಬರುವ ವರುಷ ಇದೇ ಕಾಲದಲ್ಲಿ ಒಬ್ಬ ಮಗನನ್ನು ಅಪ್ಪಿಕೊಂಡಿರುವಿ ಅಂದನು. ಅದಕ್ಕೆ ಆಕೆಯು - ದೇವರ ಮನುಷ್ಯನೇ, ಒಡೆಯನೇ, ಹೀಗೆ ಹೇಳಿ ನಿನ್ನ ದಾಸಿಯನ್ನು ವಂಚಿಸಬೇಡ ಎಂದು ನುಡಿದಳು.",
			"17": "<sup>17</sup> ಆ ಸ್ತ್ರೀಯು ಗರ್ಭಿಣಿಯಾಗಿ ಎಲೀಷನು ಹೇಳಿದಂತೆ ಮುಂದಿನ ವರುಷ ಅದೇ ಕಾಲದಲ್ಲಿ ಒಬ್ಬ ಮಗನನ್ನು ಹೆತ್ತಳು.",
			"18": "<sup>18</sup> ಹುಡುಗನು ದೊಡ್ಡವನಾದ ಮೇಲೆ ಒಂದು ದಿವಸ ಹೊಲದಲ್ಲಿ ಕೊಯ್ಯುವವರ ಸಂಗಡ ಇದ್ದ ತನ್ನ ತಂದೆಯ ಬಳಿಗೆ ಹೋದನು.",
			"19": "<sup>19</sup> ಅಲ್ಲಿದ್ದಾಗ ಅವನು ಫಕ್ಕನೆ - ಅಪ್ಪಾ, ನನ್ನ ತಲೆ, ನನ್ನ ತಲೆ ಎಂದು ಕೂಗಲು ತಂದೆಯು ಒಬ್ಬ ಸೇವಕನನ್ನು ಕರೆದು ಅವನಿಗೆ - ಇವನನ್ನು ತಾಯಿಯ ಹತ್ತಿರ ತೆಗೆದುಕೊಂಡು ಹೋಗು ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"20": "<sup>20</sup> ಸೇವಕನು ಹುಡುಗನನ್ನು ಅವನ ತಾಯಿಗೆ ತಂದೊಪ್ಪಿಸಿದನು. ಹುಡುಗನು ಮಧ್ಯಾಹ್ನದವರೆಗೂ ತಾಯಿಯ ತೊಡೆಯ ಮೇಲೆಯೇ ಇದ್ದುಕೊಂಡು ಅನಂತರ ಸತ್ತನು.",
			"21": "<sup>21</sup> ಕೂಡಲೆ ಆಕೆಯು ಅವನನ್ನು ದೇವರ ಮನುಷ್ಯನ ಕೋಣೆಗೆ ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ಅವನ ಮಂಚದ ಮೇಲೆ ಮಲಗಿಸಿ",
			"22": "<sup>22</sup> ಬಾಗಲು ಮುಚ್ಚಿ ಗಂಡನನ್ನು ಕರಿಸಿ ಅವನಿಗೆ - ದಯವಿಟ್ಟು ನನಗೋಸ್ಕರ ಒಬ್ಬ ಸೇವಕನನ್ನೂ ಒಂದು ಕತ್ತೆಯನ್ನೂ ಕಳುಹಿಸು; ನಾನು ಬೇಗನೆ ದೇವರ ಮನುಷ್ಯನ ಬಳಿಗೆ ಹೋಗಿ ಬರುತ್ತೇನೆ ಎಂದು ಹೇಳಿದಳು.",
			"23": "<sup>23</sup> ಅದಕ್ಕೆ ಅವನು - ಇಂದು ಅಮಾವಾಸ್ಯೆಯಲ್ಲ, ಸಬ್ಬತ್ತಲ್ಲ, ಈಹೊತ್ತು ಯಾಕೆ ಹೋಗುತ್ತೀ ಅಂದನು.",
			"24": "<sup>24</sup> ಆಕೆಯು ಚಿಂತೆಯಿಲ್ಲ ಎಂದು ಹೇಳಿ ಕತ್ತೆಗೆ ತಡಿಹಾಕಿಸಿ ಕೂತುಕೊಂಡು ಸೇವಕನಿಗೆ - ಹೊಡೆ, ಓಡಿಸುತ್ತಾ ಬಾ; ನಾನು ಅಪ್ಪಣೆ ಮಾಡುವವರೆಗೂ ನಿಲ್ಲಿಸಬೇಡ ಎಂದು ಆಜ್ಞಾಪಿಸಿದಳು.",
			"25": "<sup>25</sup> ಹೀಗೆ ಆಕೆಯು ಪ್ರಯಾಣಮಾಡಿ ಕರ್ಮೆಲ್‍ಬೆಟ್ಟದ ಮೇಲಿದ್ದ ದೇವರ ಮನುಷ್ಯನ ಬಳಿಗೆ ಬಂದಳು. ಅವನು ಈಕೆಯನ್ನು ದೂರದಿಂದಲೇ ಕಂಡು ತನ್ನ ಸೇವಕನಾದ ಗೇಹಜಿಗೆ -",
			"26": "<sup>26</sup> ಅಗೋ, ಅಲ್ಲಿ ಶೂನೇಮ್ಯಳು! ನೀನು ಓಡಿಹೋಗಿ ಆಕೆಯನ್ನು ಎದುರುಗೊಂಡು - ನಿನಗೂ ನಿನ್ನ ಗಂಡನಿಗೂ ಮಗನಿಗೂ ಕ್ಷೇಮವೋ ಎಂದು ವಿಚಾರಿಸು ಎಂಬದಾಗಿ ಆಜ್ಞಾಪಿಸಿ ಕಳುಹಿಸಿದನು. ಆಕೆಯು ಆ ಸೇವಕನಿಗೆ - ಕ್ಷೇಮ ಎಂದು ಉತ್ತರಕೊಟ್ಟು",
			"27": "<sup>27</sup> ಬೆಟ್ಟದ ಮೇಲಿದ್ದ ದೇವರ ಮನುಷ್ಯನ ಬಳಿಗೆ ಬಂದು ಅವನ ಪಾದಗಳನ್ನು ಹಿಡಿದಳು. ಗೇಹಜಿಯು ಆಕೆಯನ್ನು ದೂಡುವದಕ್ಕಾಗಿ ಹತ್ತಿರ ಬರಲು ದೇವರ ಮನುಷ್ಯನು ಅವನಿಗೆ - ಬಿಡು, ಆಕೆಯ ಮನಸ್ಸಿನಲ್ಲಿ ಬಹುದುಃಖವಿರುವ ಹಾಗೆ ತೋರುತ್ತದೆ; ಯೆಹೋವನು ಆಕೆಯ ದುಃಖವನ್ನು ನನಗೆ ಪ್ರಕಟಿಸಲಿಲ್ಲ, ಮರೆ ಮಾಡಿದ್ದಾನೆ ಎಂದು ಹೇಳಿದನು.",
			"28": "<sup>28</sup> ಆಕೆಯು ದೇವರ ಮನುಷ್ಯನಿಗೆ - ನನಗೆ ಮಗನು ಬೇಕೆಂದು ಸ್ವಾಮಿಯಾದ ನಿನ್ನನ್ನು ಬೇಡಿಕೊಂಡೆನೋ? ನನ್ನನ್ನು ವಂಚಿಸಬಾರದೆಂದು ಮೊರೆಯಿಟ್ಟೆನಲ್ಲಾ ಅಂದಳು.",
			"29": "<sup>29</sup> ಆಗ ಎಲೀಷನು ಗೇಹಜಿಗೆ - ನೀನು ನಡು ಕಟ್ಟಿಕೊಂಡು ನನ್ನ ಕೋಲನ್ನು ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ಹುಡುಗನ ಮೋರೆಯ ಮೇಲಿಡು; ಹೋಗುವಾಗ ದಾರಿಯಲ್ಲಿ ಯಾರನ್ನೂ ವಂದಿಸಬೇಡ; ಅವರ ವಂದನೆಯನ್ನೂ ಸ್ವೀಕರಿಸಬೇಡ ಎಂದು ಹೇಳಿ ಕಳುಹಿಸಿದನು.",
			"30": "<sup>30</sup> ಆದರೂ ಹುಡುಗನ ತಾಯಿ - ಯೆಹೋವನಾಣೆ, ನಿನ್ನ ಜೀವದಾಣೆ, ನಾನು ನಿನ್ನನ್ನು ಬಿಟ್ಟು ಹೋಗುವದಿಲ್ಲ ಎಂದು ಹೇಳಿದ್ದರಿಂದ ಎಲೀಷನು ಆಕೆಯ ಜೊತೆಯಲ್ಲಿ ಹೊರಟನು.",
			"31": "<sup>31</sup> ಅವರ ಮುಂದಾಗಿ ಹೋದ ಗೇಹಜಿಯು ಕೋಲನ್ನು ಹುಡುಗನ ಮುಖದ ಮೇಲಿಟ್ಟರೂ ಶಬ್ದವಿಲ್ಲ, ಜೀವದ ಲಕ್ಷಣವಿಲ್ಲ. ಆದದರಿಂದ ಅವನು ಹಿಂದಿರುಗಿ ಹೋಗಿ ಎಲೀಷನನ್ನು ಎದುರುಗೊಂಡು ಅವನಿಗೆ - ಹುಡುಗನು ಎಚ್ಚರವಾಗಲಿಲ್ಲ ಅಂದನು.",
			"32": "<sup>32</sup> ಎಲೀಷನು ಆ ಮನೆಯನ್ನು ಮುಟ್ಟಿದಾಗ ಸತ್ತ ಹುಡುಗನು ತನ್ನ ಮಂಚದ ಮೇಲೆ ಇಡಲ್ಪಟ್ಟಿರುವದನ್ನು ಕಂಡು",
			"33": "<sup>33</sup> ಯಾರೂ ಒಳಗೆ ಬಾರದಂತೆ ಆ ಕೋಣೆಯ ಬಾಗಲನ್ನು ಮುಚ್ಚಿಕೊಂಡು ಯೆಹೋವನನ್ನು ಪ್ರಾರ್ಥಿಸಿದನು.",
			"34": "<sup>34</sup> ಅನಂತರ ಹುಡುಗನ ಮೇಲೆ ಬೋರ್ಲಬಿದ್ದು ತನ್ನ ಬಾಯಿ ಕಣ್ಣು ಕೈಗಳನ್ನು ಅವನ ಬಾಯಿ ಕಣ್ಣು ಕೈಗಳಿಗೆ ಮುಟ್ಟಿಸಿದ್ದರಿಂದ ಹುಡುಗನ ದೇಹವು ಬೆಚ್ಚಗಾಯಿತು.",
			"35": "<sup>35</sup> ಆಮೇಲೆ ಹುಡುಗನನ್ನು ಬಿಟ್ಟು ಎದ್ದು ಮನೆಯಲ್ಲಿ ತುಸುಹೊತ್ತು ಅತ್ತಿತ್ತ ಅಡ್ಡಾಡಿ ತಿರಿಗಿ ಅವನ ಮೇಲೆ ಬೋರ್ಲ ಬೀಳಲು ಹುಡುಗನು ಏಳು ಸಾರಿ ಸೀತು ಕಣ್ದೆರೆದನು.",
			"36": "<sup>36</sup> ಆಗ ಎಲೀಷನು ಗೇಹಜಿಗೆ - ಶೂನೇಮ್ಯಳನ್ನು ಕರೆ ಎಂದು ಆಜ್ಞಾಪಿಸಲು ಅವನು ಆಕೆಯನ್ನು ಕರೆದನು. ಆಕೆಯು ಬಂದಾಗ ಎಲೀಷನು ಆಕೆಗೆ - ನಿನ್ನ ಮಗನನ್ನು ತೆಗೆದುಕೋ ಎಂದು ಹೇಳಿದನು.",
			"37": "<sup>37</sup> ಆಕೆಯು ಹತ್ತಿರ ಬಂದು ಅವನ ಪಾದಗಳಿಗೆ ಬಿದ್ದು ಸಾಷ್ಟಾಂಗನಮಸ್ಕಾರಮಾಡಿ ಮಗನನ್ನು ತೆಗೆದುಕೊಂಡು ಹೋದಳು.",
			"38": "<sup>38</sup> ಎಲೀಷನು ತಿರಿಗಿ ಗಿಲ್ಗಾಲಿಗೆ ಹೋದನು. ಆಗ ದೇಶದಲ್ಲಿ ಬರವಿದ್ದಿತು. ಪ್ರವಾದಿ ಮಂಡಲಿಯವರು ತನ್ನ ಮುಂದೆ ಕೂಡಿ ಬರಲು ಅವನು ತನ್ನ ಸೇವಕನಿಗೆ - ದೊಡ್ಡ ಹಂಡೆಯನ್ನು ತೆಗೆದುಕೊಂಡು ಇವರಿಗೋಸ್ಕರ ಅಡಿಗೆಮಾಡು ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"39": "<sup>39</sup> ಒಬ್ಬನು ಯಾವದಾದರೊಂದು ಪಲ್ಯ ಸಿಕ್ಕೀತೆಂದು ಕಾಡಿಗೆ ಹೋಗಿ ಅಲ್ಲಿ ಒಂದು ಕಾಡುಬಳ್ಳಿಯನ್ನು ಕಂಡು ಉಡಿತುಂಬಾ ಅದರ ಕಾಯಿಗಳನ್ನು ಕಿತ್ತುಕೊಂಡು ಬಂದು ತುಂಡುತುಂಡುಮಾಡಿ ಆ ಪಾತ್ರೆಯೊಳಗೆ ಹಾಕಿದನು. ಅದು ಎಂಥ ಕಾಯಿಯೆಂಬದು ಯಾರಿಗೂ ಗೊತ್ತಿರಲಿಲ್ಲ.",
			"40": "<sup>40</sup> ಜನರಿಗೆ ಅದನ್ನೇ ಊಟಕ್ಕೆ ಬಡಿಸಿದರು; ಅವರು ಅದನ್ನು ಬಾಯೊಳಗೆ ಹಾಕಿದ ಕೂಡಲೆ ತಿನ್ನಲಾರದೆ - ದೇವರ ಮನುಷ್ಯನೇ, ಪಾತ್ರೆಯಲ್ಲಿ ವಿಷ ಎಂದು ಕೂಗಿದರು.",
			"41": "<sup>41</sup> ಆಗ ಅವನು - ಹಿಟ್ಟನ್ನು ತಂದುಕೊಡಿರಿ ಎಂದು ಹೇಳಿದನು; ಅವರು ತರಲು ಅದನ್ನು ಆ ಪಾತ್ರೆಯೊಳಗೆ ಹಾಕಿ ಈಗ ಇದನ್ನು ಬಡಿಸಬಹುದು ಅಂದನು. ಕೂಡಲೆ ಪಾತ್ರೆಯಲ್ಲಿದ್ದ ವಿಷವೆಲ್ಲಾ ಹೋಯಿತು.",
			"42": "<sup>42</sup> ಒಂದು ದಿವಸ ಬಾಳ್‍ಷಾಲಿಷಾ ಊರಿನ ಒಬ್ಬ ಮನುಷ್ಯನು ಇಪ್ಪತ್ತು ಜವೆಗೋದಿಯ ರೊಟ್ಟಿಗಳನ್ನೂ ಒಂದು ಚೀಲತುಂಬಾ ಹಸೀ ತೆನೆಗಳನ್ನೂ ಪ್ರಥಮ ಫಲದ ಕಾಣಿಕೆಯಾಗಿ ತೆಗೆದುಕೊಂಡು ಬಂದು ದೇವರ ಮನುಷ್ಯನಿಗೆ ಸಮರ್ಪಿಸಿದನು. ಎಲೀಷನು ತನ್ನ ಸೇವಕನಿಗೆ - ಇದನ್ನು ಜನರಿಗೆ ಕೊಡು; ಅವರು ಊಟಮಾಡಲಿ ಎಂದು ಹೇಳಿದನು.",
			"43": "<sup>43</sup> ಅದಕ್ಕೆ ಅವನು - ಇಷ್ಟನ್ನು ನೂರು ಮಂದಿಗೆ ಹೇಗೆ ಬಡಿಸಲಿ ಅಂದನು. ಎಲೀಷನು ಅವನಿಗೆ - ಇರಲಿ, ಕೊಡು; ಅವರು ಊಟ ಮಾಡಲಿ. ಅವರು ಅದನ್ನು ಊಟಮಾಡಿ ತೃಪ್ತರಾಗಿ ಇನ್ನೂ ಉಳಿಸಿಕೊಳ್ಳುವರೆಂದು ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ ಎಂದು ಹೇಳಿದನು.",
			"44": "<sup>44</sup> ಸೇವಕನು ಬಡಿಸಲು ಯೆಹೋವನು ಹೇಳಿದಂತೆ ಜನರು ಊಟಮಾಡಿ ತೃಪ್ತರಾಗಿ ಇನ್ನೂ ಉಳಿಸಿಕೊಂಡರು."
		},
		"5": {
			"1": "<sup>1</sup> ಅರಾಮ್ಯರ ಅರಸನಿಗೆ ನಾಮಾನನೆಂಬೊಬ್ಬ ಸೇನಾಪತಿಯಿದ್ದನು. ಯೆಹೋವನು ಇವನ ಮುಖಾಂತರವಾಗಿ ಅರಾಮ್ಯರಿಗೆ ಜಯವನ್ನು ಅನುಗ್ರಹಿಸಿದದರಿಂದ ಅರಸನು ಇವನನ್ನು ಮಹಾಪುರುಷನೆಂದೂ ಸನ್ಮಾನಯೋಗ್ಯನೆಂದೂ ಎಣಿಸುತ್ತಿದ್ದನು. ಆದರೆ ಪರಾಕ್ರಮಶಾಲಿಯಾದ ಇವನು ಕುಷ್ಠರೋಗಿಯಾಗಿದ್ದನು.",
			"2": "<sup>2</sup> ಅರಾಮ್ಯರು ಒಂದು ಸಾರಿ ಸುಲಿಗೆಮಾಡುವದಕ್ಕೋಸ್ಕರ ಇಸ್ರಾಯೇಲ್ಯರ ಪ್ರಾಂತಕ್ಕೆ ಹೋಗಿ ಬರುವಾಗ ಅಲ್ಲಿನ ಒಬ್ಬ ಹುಡುಗಿಯನ್ನು ಹಿಡಿದುಕೊಂಡು ಬಂದರು; ಆಕೆಯು ನಾಮಾನನ ಹೆಂಡತಿಗೆ ದಾಸಿಯಾದಳು.",
			"3": "<sup>3</sup> ಆಕೆಯು ಒಂದು ದಿವಸ ತನ್ನ ಯಜಮಾನಿಗೆ - ನಮ್ಮ ದಣಿಯು ಸಮಾರ್ಯದಲ್ಲಿರುವ ಪ್ರವಾದಿಯ ಹತ್ತಿರ ಇರುತ್ತಿದ್ದರೆ ಎಷ್ಟೋ ಒಳ್ಳೇದಾಗುತ್ತಿತ್ತು. ಅವನು ಇವನನ್ನು ಕುಷ್ಠರೋಗದಿಂದ ವಾಸಿಮಾಡುತ್ತಿದ್ದನು ಎಂದು ಹೇಳಿದಳು.",
			"4": "<sup>4</sup> ನಾಮಾನನು ಅರಸನ ಸನ್ನಿಧಿಗೆ ಹೋಗಿ ಇಸ್ರಾಯೇಲ್‍ದೇಶದ ಹುಡುಗಿಯು ಹೇಳಿದ್ದನ್ನು ತಿಳಿಸಲು ಅವನು -",
			"5": "<sup>5</sup> ನೀನು ಅಲ್ಲಿಗೆ ಹೋಗಿ ಬಾ; ನಾನು ನಿನ್ನ ಕೈಯಲ್ಲಿ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಿಗೆ ಒಂದು ಪತ್ರವನ್ನು ಕೊಡುತ್ತೇನೆ ಅಂದನು. ನಾಮಾನನು ಹತ್ತು ತಲಾಂತು ಬೆಳ್ಳಿ, ಆರು ಸಾವಿರ ತೊಲೆ ಬಂಗಾರ, ಹತ್ತು ದುಸ್ತು ಬಟ್ಟೆಗಳು ಇವುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು",
			"6": "<sup>6</sup> ಸಮಾರ್ಯಕ್ಕೆ ಹೊರಟುಹೋಗಿ ಪತ್ರವನ್ನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಿಗೆ ಕೊಟ್ಟನು. ಅದರಲ್ಲಿ - ನನ್ನ ಸೇವಕನಾದ ನಾಮಾನನನ್ನು ನಿನ್ನ ಬಳಿಗೆ ಕಳುಹಿಸಿದ್ದೇನೆ; ನೀನು ಅವನ ಕುಷ್ಠರೋಗವನ್ನು ವಾಸಿಮಾಡತಕ್ಕದೆಂದು ಈ ಪತ್ರದಿಂದ ತಿಳಿದುಕೋ ಎಂಬದಾಗಿ ಬರೆದಿತ್ತು.",
			"7": "<sup>7</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನು ಪತ್ರವನ್ನು ಓದಿದ ಕೂಡಲೆ ಬಟ್ಟೆಗಳನ್ನು ಹರಿದುಕೊಂಡು ತನ್ನ ಪರಿವಾರದವರಿಗೆ - ತಾನು ಕಳುಹಿಸಿದ ಮನುಷ್ಯನನ್ನು ಕುಷ್ಠರೋಗದಿಂದ ವಾಸಿಮಾಡಬೇಕಂತೆ; ಇದು ಎಂಥ ಅಪ್ಪಣೆ! ನಾನೇನು ದೇವರೋ? ಜೀವದಾನ ಮಾಡುವದಕ್ಕಾಗಲಿ ಸಾಯಿಸುವದಕ್ಕಾಗಲಿ ನನಗೆ ಸಾಮರ್ಥ್ಯವುಂಟೋ? ಇವನು ನನ್ನೊಡನೆ ಜಗಳವಾಡುವದಕ್ಕೆ ಕಾರಣ ಹುಡುಕುತ್ತಾನಲ್ಲದೆ ಮತ್ತೇನು? ನೀವೇ ಆಲೋಚಿಸಿ ನೋಡಿರಿ ಎಂದು ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನು ತನ್ನ ಬಟ್ಟೆಗಳನ್ನು ಹರಿದುಕೊಂಡನೆಂಬದನ್ನು ದೇವರ ಮನುಷ್ಯನಾದ ಎಲೀಷನು ಕೇಳಿ ದೂತರ ಮುಖಾಂತರವಾಗಿ ಅವನಿಗೆ - ನೀನು ನಿನ್ನ ಬಟ್ಟೆಗಳನ್ನು ಹರಿದುಕೊಂಡದ್ದೇಕೆ? ಅವನನ್ನು ನನ್ನ ಬಳಿಗೆ ಕಳುಹಿಸು; ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಒಬ್ಬ ಪ್ರವಾದಿಯಿದ್ದಾನೆಂಬದು ಅವನಿಗೆ ಗೊತ್ತಾಗಲಿ ಎಂದು ಹೇಳಿ ಕಳುಹಿಸಿದನು.",
			"9": "<sup>9</sup> ನಾಮಾನನು ರಥರಥಾಶ್ವಗಳೊಡನೆ ಎಲೀಷನ ಮನೆಗೆ ಹೋಗಿ ಬಾಗಲಿನ ಮುಂದೆ ನಿಂತನು.",
			"10": "<sup>10</sup> ಎಲೀಷನು ಅವನಿಗೆ - ಹೋಗಿ ಯೊರ್ದನ್ ಹೊಳೆಯಲ್ಲಿ ಏಳು ಸಾರಿ ಸ್ನಾನಮಾಡು; ಆಗ ನಿನ್ನ ದೇಹವು ಮುಂಚಿನಂತಾಗುವದು; ನೀನು ಶುದ್ಧನಾಗುವಿ ಎಂದು ಹೇಳಿಕಳುಹಿಸಿದನು.",
			"11": "<sup>11</sup> ನಾಮಾನನು ಇದನ್ನು ಕೇಳಿ ಕೋಪಗೊಂಡು - ಇದೇನು! ಅವನು ಹೇಗೂ ಹೊರಗೆ ಬಂದು ನಿಂತು ತನ್ನ ದೇವರಾದ ಯೆಹೋವನ ಹೆಸರು ಹೇಳಿ ಕುಷ್ಠದ ಮೇಲೆ ಕೈಯಾಡಿಸಿ ವಾಸಿಮಾಡುವನೆಂದು ನೆನಸಿದೆನು.",
			"12": "<sup>12</sup> ದಮಸ್ಕದ ಅಬಾನಾ, ಪರ್ಪರ್ ಎಂಬ ಹೊಳೆಗಳು ಇಸ್ರಾಯೇಲ್ಯರ ಎಲ್ಲಾ ಹೊಳೆಹಳ್ಳಗಳಿಗಿಂತ ಉತ್ತಮವಾಗಿವೆಯಲ್ಲವೋ? ಸ್ನಾನದಿಂದ ವಾಸಿಯಾಗುವ ಹಾಗಿದ್ದರೆ ನಾನು ಅವುಗಳಲ್ಲಿಯೇ ಸ್ನಾನಮಾಡಬಹುದಲ್ಲವೇ ಎಂದು ಹೇಳಿ ಬಲು ಸಿಟ್ಟಿನಿಂದ ಹೊರಟುಹೋದನು.",
			"13": "<sup>13</sup> ಆಗ ಅವನ ಸೇವಕರು ಹತ್ತಿರ ಬಂದು ಅವನಿಗೆ - ಅಪ್ಪನವರೇ, ಪ್ರವಾದಿಯು ಒಂದು ಕಠಿಣವಾದ ಕೆಲಸವನ್ನು ಹೇಳಿದ್ದರೆ ಅದನ್ನು ಮಾಡುತ್ತಿದ್ದಿರಲ್ಲಾ; ಹಾಗಾದರೆ ಸ್ನಾನಮಾಡಿ ಶುದ್ಧನಾಗು ಎಂದು ಹೇಳಿದರೆ ಯಾಕೆ ಅದರಂತೆ ಮಾಡಬಾರದು ಅಂದರು.",
			"14": "<sup>14</sup> ಅವನು ಯೊರ್ದನಿಗೆ ಹೋಗಿ ಏಳು ಸಾರಿ ಅದರಲ್ಲಿ ಮುಣುಗಿ ಎದ್ದನು. ಕೂಡಲೆ ದೇವರ ಮನುಷ್ಯನ ಮಾತಿಗನುಸಾರವಾಗಿ ಅವನ ದೇಹವು ಕೂಸಿನ ದೇಹದಂತೆ ಶುದ್ಧವಾಯಿತು.",
			"15": "<sup>15</sup> ಅನಂತರ ಅವನು ತನ್ನ ಪರಿವಾರದವರೊಡನೆ ಹಿಂದಿರುಗಿ ದೇವರ ಮನುಷ್ಯನ ಹತ್ತಿರ ಹೋಗಿ ಅವನ ಮುಂದೆ ನಿಂತು - ಇಸ್ರಾಯೇಲ್ ದೇಶದಲ್ಲಿರುವ ದೇವರ ಹೊರತಾಗಿ ಲೋಕದಲ್ಲಿ ಬೇರೆ ದೇವರು ಇರುವದೇ ಇಲ್ಲವೆಂಬದು ಈಗ ನನಗೆ ಗೊತ್ತಾಯಿತು; ನೀನು ದಯವಿಟ್ಟು ಕಾಣಿಕೆಯನ್ನು ಅಂಗೀಕರಿಸಬೇಕು ಎಂದು ಹೇಳಿದನು.",
			"16": "<sup>16</sup> ಅದಕ್ಕೆ ಎಲೀಷನು - ನಾನು ಸನ್ನಿಧಿ ಸೇವೆಮಾಡುತ್ತಿರುವ ಯೆಹೋವನಾಣೆ, ನಿನ್ನಿಂದ ಏನೂ ತೆಗೆದುಕೊಳ್ಳುವದಿಲ್ಲ ಅಂದನು. ನಾಮಾನನು ಎಷ್ಟು ಒತ್ತಾಯಪಡಿಸಿದರೂ ಅವನು ತೆಗೆದುಕೊಳ್ಳಲೇ ಇಲ್ಲ.",
			"17": "<sup>17</sup> ಆಗ ಅವನು ಎಲೀಷನಿಗೆ - ನೀನು ನನ್ನದನ್ನು ಸ್ವೀಕರಿಸದಿದ್ದರೆ ನಿನ್ನ ಸೇವಕನಾದ ನನಗೆ ಎರಡು ಹೇಸರಕತ್ತೆಗಳು ಹೊರುವಷ್ಟು ಮಣ್ಣನ್ನು ಕೊಡಿಸಬೇಕೆಂದು ಬೇಡಿಕೊಳ್ಳುತ್ತೇನೆ. ನಾನು ಇನ್ನು ಮುಂದೆ ಎಲ್ಲಾ ದೇವತೆಗಳನ್ನು ಬಿಟ್ಟು ಯೆಹೋವನೊಬ್ಬನಿಗೇ ಸರ್ವಾಂಗಹೋಮಯಜ್ಞಗಳನ್ನು ಸಮರ್ಪಿಸಬೇಕೆಂದಿರುತ್ತೇನೆ.",
			"18": "<sup>18</sup> ಆದರೆ ಈ ವಿಷಯದಲ್ಲಿ ಯೆಹೋವನು ನನ್ನನ್ನು ಕ್ಷಮಿಸಲಿ; ನನ್ನ ಒಡೆಯನು ಆರಾಧನೆಗೋಸ್ಕರ ರಿಮ್ಮೋನನ ದೇವಸ್ಥಾನಕ್ಕೆ ಹೋಗಿ ನನ್ನ ಕೈಹಿಡಿದು ಆ ದೇವತೆಗೆ ನಮಸ್ಕಾರ ಮಾಡುವಾಗ ನಾನೂ ನಮಸ್ಕಾರ ಮಾಡಬೇಕಾಗುವದು. ಈ ಒಂದು ವಿಷಯದಲ್ಲಿ ಮಾತ್ರ ಯೆಹೋವನು ನನಗೆ ಕ್ಷಮೆಯನ್ನು ಅನುಗ್ರಹಿಸಬೇಕು ಅನ್ನಲು",
			"19": "<sup>19</sup> ಎಲೀಷನು ಅವನಿಗೆ - ಸಮಾಧಾನದಿಂದ ಹೋಗು ಎಂದು ಉತ್ತರಕೊಟ್ಟನು. ಅವನು ಹೊರಟು ಹೋದನು.",
			"20": "<sup>20</sup> ನಾಮಾನನು ಸ್ವಲ್ಪದೂರಕ್ಕೆ ಹೋದನಂತರ ದೇವರ ಮನುಷ್ಯನಾದ ಎಲೀಷನ ಸೇವಕ ಗೇಹಜಿಯು ಮನಸ್ಸಿನಲ್ಲಿ - ನನ್ನ ಯಜಮಾನನು ಆ ಅರಾಮ್ಯನಾದ ನಾಮಾನನಿಂದ ಏನೂ ತೆಗೆದುಕೊಳ್ಳದೆ ಅವನನ್ನು ಹಾಗೆಯೇ ಕಳುಹಿಸಿಬಿಟ್ಟನಲ್ಲಾ; ಯೆಹೋವನಾಣೆ, ನಾನು ಅವನ ಹಿಂದೆ ಓಡುತ್ತಾ ಹೋಗಿ ಅವನಿಂದ ಸ್ವಲ್ಪವನ್ನಾದರೂ ಬಾಚಿಕೊಂಡು ಬರುವೆನು ಅಂದುಕೊಂಡು ಹೊರಟನು.",
			"21": "<sup>21</sup> ನಾಮಾನನು ತನ್ನ ಹಿಂದೆ ಓಡುತ್ತಾ ಬರುವ ಗೇಹಜಿಯನ್ನು ಕಂಡು ರಥದಿಂದಿಳಿದು ಅವನನ್ನು ಎದುರುಗೊಂಡು - ಕ್ಷೇಮವೋ ಎಂದು ಕೇಳಿದನು.",
			"22": "<sup>22</sup> ಅವನು - ಕ್ಷೇಮ, ಎಫ್ರಾಯೀಮ್ ಪರ್ವತ ಪ್ರದೇಶದಿಂದ ಪ್ರವಾದಿಮಂಡಲಿಯವರಾದ ಇಬ್ಬರು ಯೌವನಸ್ಥರು ಬಂದಿರುತ್ತಾರೆ. ಆದದರಿಂದ ಅವರಿಗೋಸ್ಕರ ಬೇಗನೆ ಒಂದು ತಲಾಂತು ಬೆಳ್ಳಿಯನ್ನೂ ಎರಡು ದುಸ್ತುಬಟ್ಟೆಗಳನ್ನೂ ತೆಗೆದುಕೊಂಡು ಬರಬೇಕೆಂಬದಾಗಿ ನನ್ನ ಯಜಮಾನನು ನನ್ನನ್ನು ನಿನ್ನ ಬಳಿಗೆ ಕಳುಹಿಸಿದನು ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"23": "<sup>23</sup> ನಾಮಾನನು - ಎರಡು ತಲಾಂತನ್ನಾದರೂ ತೆಗೆದುಕೊಳ್ಳಬಾರದೇ ಎಂದು ಅವನನ್ನು ಒತ್ತಾಯಪಡಿಸಿ ಅದನ್ನು ಎರಡು ಚೀಲಗಳಲ್ಲಿ ಹಾಕಿಸಿ ಆ ಚೀಲಗಳನ್ನೂ ಎರಡು ದುಸ್ತುಬಟ್ಟೆಗಳನ್ನೂ ಇಬ್ಬರು ಸೇವಕರ ಮೇಲೆ ಹೊರಿಸಿ ಅವರನ್ನು ಗೇಹಜಿಯ ಸಂಗಡ ಕಳುಹಿಸಿದನು. ಅವರು ಹೊತ್ತುಕೊಂಡು ಇವನ ಮುಂದೆ ನಡೆದರು.",
			"24": "<sup>24</sup> ಗುಡ್ಡವನ್ನು ಮುಟ್ಟಿದಾಗ ಗೇಹಜಿಯು ಅವುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಮನೆಯಲ್ಲಿ ಅಡಗಿಸಿಟ್ಟು ಆಳುಗಳನ್ನು ಕಳುಹಿಸಿಬಿಟ್ಟನು.",
			"25": "<sup>25</sup> ಅವರು ಹೋದನಂತರ ಇವನು ತನ್ನ ಯಜಮಾನನ ಬಳಿಗೆ ಬಂದನು. ಎಲೀಷನು ಅವನನ್ನು - ಗೇಹಜಿಯೇ, ಎಲ್ಲಿಗೆ ಹೋಗಿ ಬಂದಿ ಎಂದು ಕೇಳಿದನು. ಅದಕ್ಕೆ ಅವನು - ನಿನ್ನ ಸೇವಕನಾದ ನಾನು ಎಲ್ಲಿಯೂ ಹೋಗಲಿಲ್ಲ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"26": "<sup>26</sup> ಆಗ ಎಲೀಷನು ಅವನಿಗೆ - ಒಬ್ಬನು ರಥದಿಂದಿಳಿದು ಬಂದು ನಿನ್ನನ್ನು ಎದುರುಗೊಂಡದು ನನ್ನ ಜ್ಞಾನ ದೃಷ್ಟಿಗೆ ಕಾಣಿಸಲಿಲ್ಲವೆಂದು ನೆನಸುತ್ತೀಯೋ? ದ್ರವ್ಯ, ಬಟ್ಟೆಗಳು, ಎಣ್ಣೇ ಮರದ ತೋಪುಗಳು, ದ್ರಾಕ್ಷೇತೋಟಗಳು, ಕುರಿದನಗಳು, ದಾಸದಾಸೀ ಜನವು ಇವುಗಳನ್ನು ಸಂಪಾದಿಸುವದಕ್ಕೆ ಇದು ಸಮಯವೋ?",
			"27": "<sup>27</sup> ನಾಮಾನನ ಕುಷ್ಠವು ನಿನ್ನನ್ನೂ ನಿನ್ನ ಸಂತಾನದವರನ್ನೂ ಸದಾಕಾಲ ಹಿಡಿದಿರುವದು ಅಂದನು. ಕೂಡಲೆ ಅವನಿಗೆ ಕುಷ್ಠ ಹತ್ತಿತು. ಅವನು ಹಿಮದಂತೆ ಬಿಳುಪಾಗಿ ಅವನ ಸನ್ನಿಧಿಯಿಂದ ಹೊರಟುಹೋದನು."
		},
		"6": {
			"1": "<sup>1</sup> ಒಂದು ಸಾರಿ ಪ್ರವಾದಿಮಂಡಲಿಯವರು ಎಲೀಷನಿಗೆ - ನಾವು ನಿನ್ನ ಜೊತೆಯಲ್ಲಿ ವಾಸಿಸುತ್ತಿರವ ಈ ಸ್ಥಳವು ಬಲು ಇಕ್ಕಟ್ಟಾಗಿದೆ.",
			"2": "<sup>2</sup> ಆದದರಿಂದ ನಾವು ಯೊರ್ದನಿಗೆ ಹೋಗಿ ಅಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬನೂ ಒಂದೊಂದು ತೊಲೆಯನ್ನು ಕಡಿದುಕೊಂಡು ಬಂದು ನಮಗೋಸ್ಕರ ಒಂದು ಮನೆಯನ್ನು ಮಾಡಿಕೊಳ್ಳೋಣ ಅಂದರು; ಅವನು ಅವರಿಗೆ ಹೋಗಬಹುದು ಎಂದು ಹೇಳಲು ಅವರಲ್ಲೊಬ್ಬನು -",
			"3": "<sup>3</sup> ದಯವಿಟ್ಟು ನೀನೂ ನಿನ್ನ ಸೇವಕರಾದ ನಮ್ಮ ಜೊತೆಯಲ್ಲಿ ಬರಬೇಕು ಎಂದು ಬೇಡಿಕೊಂಡನು. ಎಲೀಷನು - ಬರುತ್ತೇನೆ ಎಂದು ಹೇಳಿ ಅವರೊಡನೆ ಹೊರಟನು.",
			"4": "<sup>4</sup> ಅವರು ಯೊರ್ದನನ್ನು ಮುಟ್ಟಿ ಮರಗಳನ್ನು ಕಡಿಯುತ್ತಿರುವಾಗ",
			"5": "<sup>5</sup> ಒಬ್ಬನ ಕೊಡಲಿಯು ಕಾವು ತಪ್ಪಿ ನೀರೊಳಗೆ ಬಿದ್ದಿತು. ಆದದರಿಂದ ಅವನು - ಅಯ್ಯೋ, ಸ್ವಾಮೀ, ನಾನು ಅದನ್ನು ಎರವಾಗಿ ತಂದಿದ್ದೆನು ಎಂದು ಕೂಗಿದನು.",
			"6": "<sup>6</sup> ದೇವರ ಮನುಷ್ಯನು ಅವನನ್ನು - ಅದು ಎಲ್ಲಿ ಬಿದ್ದಿತು ಎಂದು ಕೇಳಲು ಅದು ಬಿದ್ದ ಸ್ಥಳವನ್ನು ಅವನು ತೋರಿಸಿದನು. ಆಗ ದೇವರ ಮನುಷ್ಯನು ಮರದಿಂದ ಒಂದು ತುಂಡು ಕಟ್ಟಿಗೆಯನ್ನು ಕಡಿದು ಅಲ್ಲಿ ಹಾಕಿದನು; ಕೂಡಲೆ ಕೊಡಲಿಯು ತೇಲಾಡಿತು.",
			"7": "<sup>7</sup> ಎಲೀಷನು ಅದನ್ನು ತೆಗೆದುಕೋ ಎಂದು ಆಜ್ಞಾಪಿಸಲು ಆ ಮನುಷ್ಯನು ಕೈಚಾಚಿ ಅದನ್ನು ತೆಗೆದುಕೊಂಡನು.",
			"8": "<sup>8</sup> ಅರಾಮ್ಯರ ಅರಸನು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ವಿರೋಧವಾಗಿ ಯುದ್ಧಕ್ಕೆ ಬಂದನು. ಅವನು ಯುದ್ಧದಲ್ಲಿ ಹೊಂಚುಹಾಕತಕ್ಕ ಸ್ಥಳವನ್ನು ತನ್ನ ಸೇನಾಪತಿಗಳೊಡನೆ ಗೊತ್ತು ಮಾಡುವಷ್ಟು ಸಾರಿ",
			"9": "<sup>9</sup> ದೇವರ ಮನುಷ್ಯನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಿಗೆ - ಜಾಗರೂಕತೆಯಿಂದಿರ್ರಿ; ನೀವು ಇಂಥಿಂಥ ಸ್ಥಳದಲ್ಲಿ ಹಾಯಬಾರದು, ಅಲ್ಲಿ ಅರಾಮ್ಯರು ಅಡಗಿಕೊಂಡಿದ್ದಾರೆ ಎಂದು ತಿಳಿಸುತ್ತಿದ್ದನು.",
			"10": "<sup>10</sup> ದೇವರ ಮನುಷ್ಯನು ಸೂಚಿಸುವ ಎಲ್ಲಾ ಸ್ಥಳಗಳಿಗೂ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನು ಹೇಳಿಕಳುಹಿಸುತ್ತಿದ್ದನು. ಹೀಗೆ ಅವನು ಹಲವು ಸಾರಿ ಅರಾಮ್ಯರ ಅರಸನ ಕೈಗೆ ಸಿಕ್ಕದಂತೆ ತಪ್ಪಿಸಿಕೊಂಡನು.",
			"11": "<sup>11</sup> ಇದರ ದೆಸೆಯಿಂದ ಅರಾಮ್ಯರ ಅರಸನು ಕಳವಳಗೊಂಡು ತನ್ನ ಸೇನಾಪತಿಗಳನ್ನು ಕರೆದು ಅವರನ್ನು - ನಮ್ಮವರಲ್ಲಿ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನ ಪಕ್ಷದವರು ಯಾರು? ನನಗೆ ತಿಳಿಸುವದಿಲ್ಲವೋ ಎಂದು ಕೇಳಿದನು.",
			"12": "<sup>12</sup> ಆಗ ಅವರಲ್ಲೊಬ್ಬನು ಅವನಿಗೆ - ನನ್ನ ಒಡೆಯನಾದ ಅರಸೇ, ಹಾಗಲ್ಲ; ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಎಲೀಷನೆಂಬೊಬ್ಬ ಪ್ರವಾದಿಯಿರುತ್ತಾನೆ; ನೀನು ನಿನ್ನ ಮಲಗುವ ಕೋಣೆಯಲ್ಲಿ ಆಡುವ ಮಾತುಗಳನ್ನು ಸಹ ಅವನು ಅರಿತುಕೊಂಡು ಎಲ್ಲವನ್ನೂ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಿಗೆ ತಿಳಿಸುತ್ತಾನೆ ಅಂದನು.",
			"13": "<sup>13</sup> ಅರಸನು ಇದನ್ನು ಕೇಳಿ - ಅವನು ಎಲ್ಲಿರುತ್ತಾನೆಂದು ವಿಚಾರಿಸಿರಿ, ನಾನು ಅವನನ್ನು ಹಿಡಿಸುತ್ತೇನೆ ಎಂದು ಹೇಳಿದನು. ಪ್ರವಾದಿಯು ದೋತಾನಿನಲ್ಲಿರುತ್ತಾನೆಂದು ತಿಳಿದುಕೊಂಡು",
			"14": "<sup>14</sup> ರಥರಥಾಶ್ವಸಹಿತವಾದ ಮಹಾಸೈನ್ಯವನ್ನು ಅಲ್ಲಿಗೆ ಕಳುಹಿಸಿದನು. ಸೈನ್ಯದವರು ರಾತ್ರಿಯಲ್ಲಿ ಆ ಪಟ್ಟಣವನ್ನು ಸಮೀಪಿಸಿ ಅದಕ್ಕೆ ಮುತ್ತಿಗೆಹಾಕಿದರು.",
			"15": "<sup>15</sup> ದೇವರ ಮನುಷ್ಯನ ಸೇವಕನು ಬೆಳಿಗ್ಗೆ ಎದ್ದು ಹೊರಗೆ ಹೋದಾಗ ರಥರಥಾಶ್ವಸಹಿತವಾದ ಮಹಾಸೈನ್ಯವು ಬಂದು ಪಟ್ಟಣದ ಸುತ್ತಲೂ ನಿಂತಿರುವದನ್ನು ಕಂಡು ತನ್ನ ಯಜಮಾನನಿಗೆ - ಅಯ್ಯೋ, ಸ್ವಾಮೀ, ಏನು ಮಾಡೋಣ ಎಂದನು.",
			"16": "<sup>16</sup> ಆಗ ಎಲೀಷನು ಅವನಿಗೆ - ಹೆದರಬೇಡ; ಅವರ ಕಡೆಯಲ್ಲಿರುವವರಿಗಿಂತಲೂ ನಮ್ಮ ಕಡೆಯಲ್ಲಿರುವವರು ಹೆಚ್ಚಾಗಿದ್ದಾರೆ ಎಂದು ಹೇಳಿ -",
			"17": "<sup>17</sup> ಯೆಹೋವನೇ, ಇವನು ನೋಡುವಂತೆ ಇವನ ಕಣ್ಣುಗಳನ್ನು ತೆರೆ ಎಂದು ಪ್ರಾರ್ಥಿಸಲು ಯೆಹೋವನು ಅವನ ಕಣ್ಣುಗಳನ್ನು ತೆರೆದನು. ಆಗ ಎಲೀಷನ ರಕ್ಷಣೆಗಾಗಿ ಸುತ್ತಣ ಗುಡ್ಡಗಳಲ್ಲಿ ಬಂದು ನಿಂತಿದ್ದ ಅಗ್ನಿಮಯವಾದ ರಥರಥಾಶ್ವಗಳು ಆ ಸೇವಕನಿಗೆ ಕಂಡವು.",
			"18": "<sup>18</sup> ಶತ್ರುಗಳು ಸಮೀಪಕ್ಕೆ ಬಂದಾಗ ಎಲೀಷನು - ಸ್ವಾಮೀ, ಈ ಜನರನ್ನು ಕುರುಡರನ್ನಾಗಿಮಾಡು ಎಂದು ಯೆಹೋವನನ್ನು ಪ್ರಾರ್ಥಿಸಿದನು. ಆತನು ಅವನ ಮೊರೆಯನ್ನು ಲಾಲಿಸಿ ಅವರನ್ನು ಕುರುಡರನ್ನಾಗಿ ಮಾಡಿದನು.",
			"19": "<sup>19</sup> ಆಗ ಎಲೀಷನು ಅವರಿಗೆ - ನೀವು ಹೋಗಬೇಕೆಂದಿದ್ದ ಪಟ್ಟಣವೂ ದಾರಿಯೂ ಇದಲ್ಲ; ನನ್ನ ಜೊತೆಯಲ್ಲಿ ಬನ್ನಿರಿ, ನೀವು ಹುಡುಕುತ್ತಿರುವ ಮನುಷ್ಯನ ಬಳಿಗೆ ನಿಮ್ಮನ್ನು ಕರಕೊಂಡು ಹೋಗುತ್ತೇನೆ ಎಂದು ಹೇಳಿ ಅವರನ್ನು ಸಮಾರ್ಯಕ್ಕೆ ಕರಕೊಂಡು ಹೋದನು.",
			"20": "<sup>20</sup> ಅವರು ಸಮಾರ್ಯದೊಳಗೆ ಬಂದಾಗ ಎಲೀಷನು - ಇವರು ನೋಡುವಂತೆ ಇವರ ಕಣ್ಣುಗಳನ್ನು ತೆರೆ ಎಂದು ಯೆಹೋವನನ್ನು ಬೇಡಿಕೊಂಡದರಿಂದ ಆತನು ಅವರ ಕಣ್ಣುಗಳನ್ನು ತೆರೆದನು. ಆಗ ಅವರಿಗೆ ತಾವು ಸಮಾರ್ಯದಲ್ಲಿದ್ದೇವೆಂದು ತಿಳಿದುಬಂತು.",
			"21": "<sup>21</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನು ಅವರನ್ನು ಕಂಡು ಎಲೀಷನಿಗೆ - ಅಪ್ಪಾ, ನಾನು ಇವರನ್ನು ಸಂಹರಿಸಲೋ ಸಂಹರಿಸಲೋ ಎಂದು ಕೇಳಲು ಅವನು - ಬೇಡ;",
			"22": "<sup>22</sup> ಕತ್ತಿಬಿಲ್ಲುಗಳಿಂದ ಸ್ವಾಧೀನಪಡಿಸಿಕೊಂಡು ಸೆರೆಯಾಗಿ ತಂದವರನ್ನು ನೀನು ಸಂಹರಿಸುವಿಯೋ? ಇವರಿಗೆ ಅನ್ನಪಾನಗಳನ್ನು ಕೊಡು; ಉಂಡು ಕುಡಿದು ತಮ್ಮ ಯಜಮಾನನ ಬಳಿಗೆ ಹಿಂದಿರುಗಿ ಹೋಗಲಿ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"23": "<sup>23</sup> ಆಗ ಅರಸನು ಅವರಿಗೋಸ್ಕರ ದೊಡ್ಡ ಔತಣವನ್ನು ಮಾಡಿಸಿ ಅವರು ಅನ್ನಪಾನಗಳನ್ನು ತೆಗೆದುಕೊಂಡ ಮೇಲೆ ಅವರನ್ನು ಅವರ ಯಜಮಾನನ ಬಳಿಗೆ ಕಳುಹಿಸಿದನು. ಅಂದಿನಿಂದ ಸುಲಿಗೆಮಾಡುವ ಅರಾಮ್ಯರ ಗುಂಪುಗಳು ಇಸ್ರಾಯೇಲ್ಯರ ಪ್ರಾಂತದೊಳಗೆ ತಿರಿಗಿ ಬರಲಿಲ್ಲ.",
			"24": "<sup>24</sup> ಆಮೇಲೆ ಅರಾಮ್ಯರ ಅರಸನಾದ ಬೆನ್ಹದದನು ತನ್ನ ಎಲ್ಲಾ ಸೈನ್ಯವನ್ನು ಕೂಡಿಸಿಕೊಂಡು ಬಂದು ಸಮಾರ್ಯಕ್ಕೆ ಮುತ್ತಿಗೆಹಾಕಿದನು.",
			"25": "<sup>25</sup> ಆಗ ಸಮಾರ್ಯ ಪಟ್ಟಣದಲ್ಲಿ ಘೋರವಾದ ಬರವಿದ್ದಿತು. ಮುತ್ತಿಗೆಯ ದೆಸೆಯಿಂದ ಅದು ಮತ್ತಷ್ಟು ಘೋರವಾಗಿ ಕತ್ತೆಯ ತಲೆಯು ಎಂಭತ್ತು ರೂಪಾಯಿಗಳಿಗೂ ಅರೆಸೇರು ಪಾರಿವಾಳದ ಮಲವು ಐದು ರೂಪಾಯಿಗಳಿಗೂ ಮಾರಲ್ಪಟ್ಟವು.",
			"26": "<sup>26</sup> ಒಂದು ದಿವಸ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನು ಪೌಳಿಗೋಡೆಯ ಮೇಲೆ ಹಾದುಹೋಗುತ್ತಿರುವಾಗ ಒಬ್ಬ ಸ್ತ್ರೀಯು - ಅರಸೇ, ನನ್ನ ಒಡೆಯನೇ, ನನ್ನನ್ನು ರಕ್ಷಿಸು ಎಂದು ಮೊರೆಯಿಟ್ಟಳು.",
			"27": "<sup>27</sup> ಅರಸನು ಆಕೆಗೆ - ಯೆಹೋವನು ನಿನ್ನನ್ನು ರಕ್ಷಿಸದಿದ್ದರೆ ನಾನು ಹೇಗೆ ರಕ್ಷಿಸಬಹುದು? ಕಣದಲ್ಲಾಗಲಿ ದ್ರಾಕ್ಷೇ ಆಲೆಯಲ್ಲಾಗಲಿ ಏನಾದರೂ ಉಂಟೋ ಎಂದು ಹೇಳಿ",
			"28": "<sup>28</sup> ನಿನಗಿರುವ ದುಃಖ ಯಾವದು ಎಂದು ಕೇಳಿದನು. ಆಗ ಆಕೆಯು - ಈ ಸ್ತ್ರೀಯು ನನಗೆ - ನಿನ್ನ ಮಗನನ್ನು ತೆಗೆದುಕೊಂಡು ಬಾ; ನಾವು ಈ ಹೊತ್ತು ಅವನನ್ನು ತಿಂದುಬಿಡೋಣ; ನಾಳೆ ನನ್ನ ಮಗನನ್ನು ತಿನ್ನೋಣ ಎಂದು ಹೇಳಿದ್ದರಿಂದ",
			"29": "<sup>29</sup> ನಾವಿಬ್ಬರೂ ನನ್ನ ಮಗನನ್ನು ಕೊಂದು ಬೇಯಿಸಿ ತಿಂದುಬಿಟ್ಟೆವು. ಮರುದಿವಸ ನಾನು ಈಕೆಗೆ - ಈಹೊತ್ತು ನಿನ್ನ ಮಗನನ್ನು ತೆಗೆದುಕೊಂಡು ಬಾ; ಅವನನ್ನು ಕೊಂದು ತಿನ್ನೋಣ ಅಂದಾಗ ಈಕೆಯು ಅವನನ್ನು ಎಲ್ಲಿಯೋ ಅಡಗಿಸಿಟ್ಟಳು ಎಂದು ಹೇಳಿದಳು.",
			"30": "<sup>30</sup> ಗೋಡೆಯ ಮೇಲೆ ಹೋಗುತ್ತಿದ್ದ ಅರಸನು ಆ ಸ್ತ್ರೀಯ ಮಾತನ್ನು ಕೇಳಿದೊಡನೆ ತನ್ನ ಬಟ್ಟೆಗಳನ್ನು ಹರಿದುಕೊಂಡನು. ಆಗ ಅವನ ಮೈಮೇಲೆ ಗೋಣೀತಟ್ಟು ಇರುವದು ಜನರಿಗೆ ಕಂಡಿತು.",
			"31": "<sup>31</sup> ಇದಲ್ಲದೆ ಅವನು - ನಾನು ಈಹೊತ್ತು ಶಾಫಾಟನ ಮಗನಾದ ಎಲೀಷನ ತಲೆಯನ್ನು ಹಾರಿಸದಿದ್ದರೆ ದೇವರು ನನಗೆ ಬೇಕಾದದ್ದನ್ನು ಮಾಡಲಿ ಎಂದು ಆಣೆಯಿಟ್ಟುಕೊಂಡನು.",
			"32": "<sup>32</sup> ಎಲೀಷನು ಊರಿನ ಹಿರಿಯರೊಡನೆ ತನ್ನ ಮನೆಯಲ್ಲಿ ಕೂತುಕೊಂಡಿದ್ದನು; ಅರಸನು ತನ್ನ ಹತ್ತಿರವಿದ್ದ ಒಬ್ಬ ಆಳನ್ನು ಕಳುಹಿಸಿದನು. ಅವನು ಇನ್ನೂ ದೂರದಲ್ಲಿರುವಾಗಲೇ ಎಲೀಷನು ಹಿರಿಯರಿಗೆ - ನೋಡಿರಿ, ಆ ಕೊಲೆಗಾರನ ಮಗನು ನನ್ನ ತಲೆಯನ್ನು ಹಾರಿಸುವದಕ್ಕೆ ಆಳನ್ನು ಕಳುಹಿಸಿದ್ದಾನೆ. ಆಳು ಇಲ್ಲಿಗೆ ಬಂದ ಕೂಡಲೆ ನೀವು ಬಾಗಲನ್ನು ಮುಚ್ಚಿ ಅವನು ಒಳಗೆ ಬಾರದಂತೆ ಅದನ್ನು ಒತ್ತಿಹಿಡಿಯಿರಿ; ಅವನ ಹಿಂದೆಯೇ ಬರುತ್ತಿರುವ ಅವನೊಡೆಯನ ಕಾಲು ಸಪ್ಪಳವು ಕೇಳಿಸುತ್ತದಲ್ಲಾ ಎಂದು ಹೇಳಿದನು.",
			"33": "<sup>33</sup> ಅವನು ಅವರೊಡನೆ ಮಾತಾಡುತ್ತಿರುವಾಗಲೇ ಆಳೂ [ಅವನ ಹಿಂದಿನಿಂದ ಅರಸನೂ] ಅಲ್ಲಿಗೆ ಬಂದರು. ಅರಸನು ಎಲೀಷನಿಗೆ - ನೋಡು, ಈ ಆಪತ್ತು ಯೆಹೋವನಿಂದಲೇ ಬಂದದ್ದು. ಇನ್ನು ಮುಂದೆ ನಾನೇಕೆ ಆತನನ್ನು ನಿರೀಕ್ಷಿಸಿಕೊಂಡಿರಬೇಕು ಅಂದನು."
		},
		"7": {
			"1": "<sup>1</sup> ಆಗ ಎಲೀಷನು ಅವನಿಗೆ - ಯೆಹೋವನ ವಾಕ್ಯವನ್ನು ಕೇಳು; ಆತನು - ನಾಳೆ ಇಷ್ಟು ಹೊತ್ತಿಗೆ ಸಮಾರ್ಯ ಪಟ್ಟಣದ ಬಾಗಲಿನಲ್ಲಿ ಗೋದಿಹಿಟ್ಟು ರೂಪಾಯಿಗೆ ಹನ್ನೆರಡು ಸೇರಿನಂತೆಯೂ ಜವೆಗೋದಿಯು ಇಪ್ಪತ್ತನಾಲ್ಕು ಸೇರಿನಂತೆಯೂ ಮಾರಲ್ಪಡುವವು ಅನ್ನುತ್ತಾನೆ ಎಂದು ಹೇಳಿದನು.",
			"2": "<sup>2</sup> ಇದನ್ನು ಕೇಳಿ ಅರಸನ ಹಸ್ತಕನಾದ ಸರದಾರನು ದೇವರ ಮನುಷ್ಯನಿಗೆ - ಯೆಹೋವನು ಆಕಾಶದಲ್ಲಿ ದ್ವಾರಗಳನ್ನು ಮಾಡಿದರೂ ಇದು ಸಂಭವಿಸಲಾರದು ಅನ್ನಲು ಎಲೀಷನು ಅವನಿಗೆ - ಅದು ಸಂಭವಿಸುವದನ್ನು ನೀನು ಕಣ್ಣಾರೆ ಕಾಣುವಿ, ಆದರೆ ಅದನ್ನು ಅನುಭವಿಸುವದಿಲ್ಲ ಎಂದು ಹೇಳಿದನು.",
			"3": "<sup>3</sup> ಊರುಬಾಗಲಿನ ಹತ್ತಿರ ನಾಲ್ಕು ಮಂದಿ ಕುಷ್ಠರೋಗಿಗಳಿದ್ದರು. ಅವರು ತಮ್ಮೊಳಗೆ - ನಾವು ಸಾಯುವ ತನಕ ಇಲ್ಲೇ ಕೂತಿರಬೇಕೋ?",
			"4": "<sup>4</sup> ಪಟ್ಟಣದೊಳಗೆ ಬರವಿರುವದರಿಂದ ಅಲ್ಲಿ ಹೋದರೂ ಸಾಯಬೇಕು. ಇಲ್ಲಿಯೇ ಕೂತಿದ್ದರೂ ಸಾಯಬೇಕು. ಆದದರಿಂದ ಹೊರಟುಹೋಗಿ ಅರಾಮ್ಯರ ಪಾಳೆಯವನ್ನು ಸೇರೋಣ. ಅವರು ನಮ್ಮನ್ನು ಉಳಿಸುವದಾದರೆ ಉಳಿಸಲಿ, ಕೊಲ್ಲುವದಾದರೆ ಕೊಲ್ಲಲಿ ಎಂದು ಮಾತಾಡಿಕೊಂಡು",
			"5": "<sup>5</sup> ಸಾಯಂಕಾಲವಾಗಲು ಅರಾಮ್ಯರ ಪಾಳೆಯಕ್ಕೆ ಹೊರಟರು. ಅವರು ಪಾಳೆಯದ ಅಂಚಿಗೆ ಬಂದಾಗ ಅಲ್ಲಿ ಒಬ್ಬನೂ ಕಾಣಿಸಲಿಲ್ಲ.",
			"6": "<sup>6</sup> ಕರ್ತನು ಅರಾಮ್ಯರ ಪಾಳೆಯದವರಿಗೆ ರಥರಥಾಶ್ವಸಹಿತವಾದ ಮಹಾಸೈನ್ಯ ಘೋಷವು ಕೇಳಿಸುವಂತೆ ಮಾಡಿದ್ದರಿಂದ ಅವರು - ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನು ಹಿತ್ತಿಯ, ಐಗುಪ್ತ ಇವುಗಳ ಅರಸರಿಗೆ ಹಣಕೊಟ್ಟು ಅವರನ್ನು ನಮಗೆ ವಿರೋಧವಾಗಿ ಕರತಂದಿದ್ದಾನೆ ಅಂದುಕೊಂಡು",
			"7": "<sup>7</sup> ತಮ್ಮ ಗುಡಾರಗಳನ್ನೂ ಕತ್ತೆಕುದುರೆಗಳನ್ನೂ ಪಾಳೆಯದಲ್ಲಿದ್ದದ್ದೆಲ್ಲವನ್ನೂ ಬಿಟ್ಟು ತಮ್ಮ ಜೀವರಕ್ಷಣೆಗಾಗಿ ಅದೇ ಸಾಯಂಕಾಲದಲ್ಲಿ ಓಡಿಹೋಗಿದ್ದರು.",
			"8": "<sup>8</sup> ಆ ಕುಷ್ಠರೋಗಿಗಳು ಪಾಳೆಯದ ಅಂಚಿಗೆ ಬಂದಾಗ ಮೊದಲನೆಯ ಡೇರೆಯನ್ನು ಹೊಕ್ಕು ಉಂಡು ಕುಡಿದು ಅದರಲ್ಲಿದ್ದ ಬೆಳ್ಳಿಬಂಗಾರವನ್ನೂ ಬಟ್ಟೆಗಳನ್ನೂ ತೆಗೆದುಕೊಂಡು ಅಡಗಿಸಿಟ್ಟರು. ಇನ್ನೊಂದು ಡೇರೆಯನ್ನು ಹೊಕ್ಕು ಅಲ್ಲಿಂದಲೂ ತೆಗೆದುಕೊಂಡು ಅಡಗಿಸಿಟ್ಟರು.",
			"9": "<sup>9</sup> ಅನಂತರ ಅವರು - ಇದು ಶುಭವಾರ್ತೆಯ ದಿವಸವಾಗಿದೆ; ನಾವು ಇದನ್ನು ಪ್ರಕಟಿಸದಿರುವದು ಒಳ್ಳೇದಲ್ಲ. ಬೆಳಗಾಗುವವರೆಗೆ ತಡಮಾಡಿದರೆ ಶಿಕ್ಷೆಗೆ ಪಾತ್ರರಾದೇವು. ಆದದರಿಂದ ಹೋಗಿ ಅರಮನೆಯವರಿಗೆ ಈ ಸಂಗತಿಯನ್ನು ತಿಳಿಸೋಣ ಎಂದು ಮಾತಾಡಿಕೊಂಡು",
			"10": "<sup>10</sup> ಅಲ್ಲಿಂದ ಹೊರಟು ಊರುಬಾಗಲಿಗೆ ಬಂದು ಕಾಯುವವರಿಗೆ - ನಾವು ಅರಾಮ್ಯರ ಪಾಳೆಯಕ್ಕೆ ಹೋಗಿದ್ದೆವು. ಅಲ್ಲಿ ನಮಗೆ ಯಾರೂ ಕಾಣಿಸಲಿಲ್ಲ, ಮನುಷ್ಯ ಶಬ್ದವೇ ಕೇಳಿಸಲಿಲ್ಲ. ಕತ್ತೆಕುದುರೆಗಳು ಅಲ್ಲಲ್ಲಿ ಕಟ್ಟಲ್ಪಟ್ಟಿರುತ್ತವೆ, ಡೇರೆಗಳು ಇದ್ದ ಹಾಗೆಯೇ ಇರುತ್ತವೆ ಎಂದು ಕೂಗಿ ತಿಳಿಸಿದರು.",
			"11": "<sup>11</sup> ಬಾಗಲು ಕಾಯುವವರು ಕೂಡಲೆ ಅರಮನೆಯವರನ್ನು ಕೂಗಿ ಅವರಿಗೆ ಈ ವರ್ತಮಾನವನ್ನು ಮುಟ್ಟಿಸಿದರು.",
			"12": "<sup>12</sup> ಅರಸನು ರಾತ್ರಿಯಲ್ಲೇ ಎದ್ದು ತನ್ನ ಪರಿವಾರದವರಿಗೆ - ಅರಾಮ್ಯರ ಹಂಚಿಕೆಯನ್ನು ಹೇಳುತ್ತೇನೆ ಕೇಳಿರಿ; ನಾವು ಹಸಿವೆಯಿಂದ ಸಾಯುವವರಾಗಿದ್ದೇವೆಂಬದನ್ನು ಅವರು ಬಲ್ಲರು. ಆದದರಿಂದ ಪಾಳೆಯವನ್ನು ಬಿಟ್ಟು ಹೋಗಿ ಅಡವಿಯಲ್ಲಿ ಅಡಗಿಕೊಂಡಿದ್ದಾರೆ; ನಾವು ಪಟ್ಟಣದಿಂದ ಹೊರಗೆ ಹೋದ ಕೂಡಲೆ ನಮ್ಮನ್ನು ಸಜೀವಿಗಳನ್ನಾಗಿಯೇ ಹಿಡಿದು ಪಟ್ಟಣವನ್ನು ಪ್ರವೇಶಿಸಬೇಕೆಂದಿದ್ದಾರೆ ಅಂದನು.",
			"13": "<sup>13</sup> ಆಗ ಅವರಲ್ಲೊಬ್ಬನು ಅರಸನಿಗೆ - ರಾಹುತರು ಪಟ್ಟಣದಲ್ಲುಳಿದಿರುವ ಕುದುರೆಗಳಲ್ಲಿ ನಾಲ್ಕೈದು ಕುದುರೆಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ನೋಡುವದಕ್ಕೆ ಹೋಗಲಿ; ಅವರಿಗೆ ಪಟ್ಟಣದಲ್ಲುಳಿದಿರುವ ಇಸ್ರಾಯೇಲ್ಯರ ಗತಿಯಾಗಲಿ ಸತ್ತುಹೋದವರ ಗತಿಯಾಗಲಿ ಸಂಭವಿಸುವದಷ್ಟೆ ಎಂದು ಹೇಳಿದನು.",
			"14": "<sup>14</sup> ಕೂಡಲೆ ಅರಸನು ಎರಡು ಜೋಡಿ ಕುದುರೆಗಳನ್ನು ತರಿಸಿ ಅರಾಮ್ಯರ ಸೈನ್ಯವೆಲ್ಲಿರುತ್ತದೆಂಬದನ್ನು ನೋಡಿ ಬರುವದಕ್ಕಾಗಿ ರಾಹುತರನ್ನು ಕಳುಹಿಸಿದನು.",
			"15": "<sup>15</sup> ಅವರು ಯೊರ್ದನ್‍ಹೊಳೆಯವರೆಗೂ ಹೋಗಿ ಅವಸರದಿಂದ ಓಡಿಹೋದ ಅರಾಮ್ಯರು ದಾರಿಯಲ್ಲೆಲ್ಲಾ ತಮ್ಮ ಬಟ್ಟೆಗಳನ್ನೂ ಸಾಮಾನುಗಳನ್ನೂ ಬಿಸಾಡಿದ್ದನ್ನು ಕಂಡು ಹಿಂದಿರುಗಿ ಬಂದು ಅರಸನಿಗೆ ತಿಳಿಸಿದರು.",
			"16": "<sup>16</sup> ಆಗ ಜನರು ಹೊರಗೆ ಹೋಗಿ ಅರಾಮ್ಯರ ಪಾಳೆಯವನ್ನು ಸೂರೆಮಾಡಿಬಿಟ್ಟರು. ಯೆಹೋವನ ಮಾತಿನಂತೆ ಗೋದಿಹಿಟ್ಟು ರೂಪಾಯಿಗೆ ಹನ್ನೆರಡು ಸೇರಿನಂತೆಯೂ ಜವೆಗೋದಿಯು ಇಪ್ಪತ್ತನಾಲ್ಕು ಸೇರಿನಂತೆಯೂ ಮಾರಲ್ಪಟ್ಟವು.",
			"17": "<sup>17</sup> ಅರಸನು ತನ್ನ ಹಸ್ತಕನಾದ ಸರದಾರನನ್ನು ಊರು ಬಾಗಲು ಕಾಯುವದಕ್ಕಾಗಿ ಕಳುಹಿಸಿದಲ್ಲಿ ಅವನು ಜನರಿಂದ ತುಳಿಯಲ್ಪಟ್ಟು ಸತ್ತನು. ಹೀಗೆ ದೇವರ ಮನುಷ್ಯನು ಅರಸನ ಜೊತೆಯಲ್ಲಿ ತನ್ನ ಬಳಿಗೆ ಬಂದಿದ್ದ ಆ ಸರದಾರನಿಗೆ ಹೇಳಿದ ಮಾತು ನೆರವೇರಿತು.",
			"18": "<sup>18</sup> ಅವನು ಅರಸನಿಗೆ - ನಾಳೆ ಇಷ್ಟು ಹೊತ್ತಿಗೆ ಸಮಾರ್ಯ ಪಟ್ಟಣದ ಬಾಗಲಿನಲ್ಲಿ ಜವೆಗೋದಿಯು ರೂಪಾಯಿಗೆ ಇಪ್ಪತ್ತನಾಲ್ಕು ಸೇರಿನಂತೆಯೂ ಗೋದಿಹಿಟ್ಟು ಹನ್ನೆರಡು ಸೇರಿನಂತೆಯೂ ಮಾರಲ್ಪಡುವವು ಎಂದು ಹೇಳಿದಾಗ ಆ ಸರದಾರನು -",
			"19": "<sup>19</sup> ಯೆಹೋವನು ಆಕಾಶದಲ್ಲಿ ದ್ವಾರಗಳನ್ನು ಮಾಡಿದರೂ ಇದು ಸಂಭವಿಸಲಾರದು ಅಂದದರಿಂದ ದೇವರ ಮನುಷ್ಯನು ಅವನಿಗೆ - ನೀನು ಅದನ್ನು ಕಣ್ಣಾರೆ ಕಾಣುವಿ; ಆದರೆ ಅನುಭವಿಸುವದಿಲ್ಲ ಎಂದು ನುಡಿದಿದ್ದನು.",
			"20": "<sup>20</sup> ಅವನು ಊರುಬಾಗಲಿನಲ್ಲಿ ಜನರಿಂದ ತುಳಿಯಲ್ಪಟ್ಟು ಸತ್ತದರಿಂದ ಈ ಮಾತು ನೆರವೇರಿತು."
		},
		"8": {
			"1": "<sup>1</sup> ಎಲೀಷನು ತಾನು ಬದುಕಿಸಿದ ಹುಡುಗನ ತಾಯಿಗೆ - ಯೆಹೋವನು ಈ ದೇಶಕ್ಕೆ ಏಳು ವರುಷಗಳ ಬರವನ್ನು ಕಳುಹಿಸುವದಕ್ಕಿರುತ್ತಾನೆ. ಆದದರಿಂದ ನೀನು ನಿನ್ನ ಮನೆಯವರೊಡನೆ ಯಾವದಾದರೊಂದು ಪರದೇಶಕ್ಕೆ ಹೋಗಿ ಅಲ್ಲಿ ವಾಸಿಸು ಎಂದು ಹೇಳಿದನು.",
			"2": "<sup>2</sup> ಆಕೆಯು ದೇವರ ಮನುಷ್ಯನ ಅಪ್ಪಣೆಯಂತೆ ತನ್ನ ಮನೆಯವರೊಡನೆ ಸ್ವದೇಶವನ್ನು ಬಿಟ್ಟು ಹೋಗಿ ಫಿಲಿಷ್ಟಿಯರ ದೇಶದಲ್ಲಿ ಏಳು ವರುಷ ವಾಸವಾಗಿದ್ದಳು.",
			"3": "<sup>3</sup> ಏಳು ವರುಷಗಳಾದನಂತರ ಆಕೆಯು ಅಲ್ಲಿಂದ ಸ್ವದೇಶಕ್ಕೆ ಬಂದು ಪರಾಧೀನವಾಗಿದ್ದ ತನ್ನ ಹೊಲಮನೆಗಳಿಗಾಗಿ ಮೊರೆಯಿಡುವದಕ್ಕೋಸ್ಕರ ಅರಸನ ಬಳಿಗೆ ಹೋದಳು.",
			"4": "<sup>4</sup> ಆ ಸಮಯದಲ್ಲಿ ಅರಸನು ದೇವರ ಮನುಷ್ಯನ ಸೇವಕನಾದ ಗೇಹಜಿಗೆ - ಎಲೀಷನು ಮಾಡಿದ ಎಲ್ಲಾ ಮಹತ್ಕಾರ್ಯಗಳನ್ನು ನನಗೆ ವಿವರಿಸು ಎಂದು ಆಜ್ಞಾಪಿಸಿದ್ದನು.",
			"5": "<sup>5</sup> ಎಲೀಷನು ಸತ್ತ ಹುಡುಗನಿಗೆ ಜೀವದಾನಮಾಡಿದ ಸಂಗತಿಯನ್ನು ಗೇಹಜಿಯು ವಿವರಿಸುತ್ತಿರುವಾಗಲೇ ಜೀವದಾನಹೊಂದಿದ ಹುಡುಗನ ತಾಯಿಯು ತನ್ನ ಹೊಲಮನೆಗಳಿಗಾಗಿ ಮೊರೆಯಿಡುವದಕ್ಕೋಸ್ಕರ ಅರಸನ ಬಳಿಗೆ ಬಂದಳು. ಕೂಡಲೆ ಗೇಹಜಿಯು - ಅರಸನೇ, ನನ್ನ ಒಡೆಯನೇ, ಎಲೀಷನು ಬದುಕಿಸಿದ ಹುಡುಗನು ಇವನೇ; ಈಕೆಯು ಇವನ ತಾಯಿ ಎಂದು ಹೇಳಿದನು.",
			"6": "<sup>6</sup> ಅರಸನು ಈ ಸಂಗತಿಯ ವಿಷಯವಾಗಿ ಆ ಸ್ತ್ರೀಯನ್ನು ವಿಚಾರಿಸಲು ಆಕೆಯು ಅದರಂತೆಯೇ ಹೇಳಿದಳು. ಆಗ ಅರಸನು ಒಬ್ಬ ಕಂಚುಕಿಯನ್ನು ಕರೆದು ಅವನಿಗೆ - ಹೋಗಿ ಈಕೆಯ ಹೊಲಮನೆಗಳನ್ನೂ ಈಕೆಯು ದೇಶವನ್ನು ಬಿಟ್ಟಂದಿನಿಂದ ಇಂದಿನವರೆಗೂ ಈಕೆಗೆ ಸಲ್ಲತಕ್ಕ ಹೊಲದ ಆದಾಯವನ್ನೂ ಕೊಡಿಸು ಎಂದು ಆಜ್ಞಾಪಿಸಿ ಅವನನ್ನು ಆಕೆಯ ಜೊತೆಯಲ್ಲಿ ಕಳುಹಿಸಿದನು.",
			"7": "<sup>7</sup> ಒಂದಾನೊಂದು ಸಮಯದಲ್ಲಿ ಎಲೀಷನು ದಮಸ್ಕಕ್ಕೆ ಹೋದನು. ಆಗ ಅರಾಮ್ಯರ ಅರಸನಾದ ಬೆನ್ಹದದನು ಅಸ್ವಸ್ಥನಾಗಿದ್ದನು. ದೇವರ ಮನುಷ್ಯನು ಬಂದಿದ್ದಾನೆಂಬ ವರ್ತಮಾನವನ್ನು ಕೇಳಿ ಅವನು ಹಜಾಯೇಲನನ್ನು ಕರೆದು",
			"8": "<sup>8</sup> ಅವನಿಗೆ - ನೀನು ಕಾಣಿಕೆಯನ್ನು ತೆಗೆದುಕೊಂಡು ದೇವರ ಮನುಷ್ಯನ ಬಳಿಗೆ ಹೋಗಿ ನನಗೆ ವಾಸಿಯಾಗುವದೋ ಇಲ್ಲವೋ ಎಂಬದನ್ನು ಅವನ ಮುಖಾಂತರವಾಗಿ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ವಿಚಾರಿಸು ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"9": "<sup>9</sup> ಹಜಾಯೇಲನು ದಮಸ್ಕದ ಶ್ರೇಷ್ಠವಸ್ತುಗಳಲ್ಲಿ ನಾಲ್ವತ್ತು ಒಂಟೆಗಳು ಹೊರುವಷ್ಟನ್ನು ತೆಗೆದುಕೊಂಡು ಎಲೀಷನ ಬಳಿಗೆ ಹೋಗಿ ಅವನ ಮುಂದೆ ನಿಂತು - ನಿನ್ನ ಮಗನೂ ಅರಾಮ್ಯರ ಅರಸನೂ ಆದ ಬೆನ್ಹದದನು ತನಗೆ ಸ್ವಸ್ಥವಾಗುವದೋ ಇಲ್ಲವೋ ಎಂಬದನ್ನು ವಿಚಾರಿಸುವದಕ್ಕಾಗಿ ನನ್ನನ್ನು ನಿನ್ನ ಹತ್ತಿರ ಕಳುಹಿಸಿದ್ದಾನೆ ಎಂದು ಹೇಳಿದನು.",
			"10": "<sup>10</sup> ಆಗ ದೇವರ ಮನುಷ್ಯನಾದ ಎಲೀಷನು ಅವನಿಗೆ - ಹೇಗೂ ನಿನಗೆ ವಾಸಿಯಾಗುವದು ಎಂಬದಾಗಿ ಬೆನ್ಹದದನಿಗೆ ಹೇಳು; ಆದರೂ ಅವನು ಸತ್ತೇ ಹೋಗುವನೆಂದು ಯೆಹೋವನು ನನಗೆ ತಿಳಿಸಿದ್ದಾನೆ ಎಂದು ಹೇಳಿ",
			"11": "<sup>11</sup> ಅವನು ನಾಚಿಕೊಳ್ಳುವ ತನಕ ಸ್ಥಿರದೃಷ್ಟಿಯಿಂದ ಅವನನ್ನು ನೋಡಿ ಅಳತೊಡಗಿದನು.",
			"12": "<sup>12</sup> ಹಜಾಯೇಲನು ಅವನನ್ನು - ನನ್ನ ಒಡೆಯಾ, ಯಾಕೆ ಅಳುತ್ತೀ ಎಂದು ಕೇಳಿದನು. ಅದಕ್ಕೆ ಅವನು - ನೀನು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಎಷ್ಟು ಕೇಡು ಮಾಡುವಿಯೆಂಬದು ನನಗೆ ಪ್ರಕಟವಾಯಿತು. ನೀನು ಅವರ ಕೋಟೆಗಳಿಗೆ ಬೆಂಕಿ ಹೊತ್ತಿಸುವಿ; ಯೌವನಸ್ಥರನ್ನು ಕತ್ತಿಯಿಂದ ಸಂಹರಿಸುವಿ; ಮಕ್ಕಳನ್ನು ಬಂಡೆಗೆ ಅಪ್ಪಳಿಸುವಿ; ಗರ್ಭಿಣಿಯರ ಹೊಟ್ಟೆಯನ್ನು ಸೀಳುವಿ ಎಂದು ಉತ್ತರ ಕೊಟ್ಟನು.",
			"13": "<sup>13</sup> ಹಜಾಯೇಲನು ಎಲೀಷನನ್ನು - ನಿನ್ನ ಸೇವಕನಾದ ನಾನು ನಾಯಿಯಂತಿರುತ್ತೇನಷ್ಟೆ; ನನ್ನಿಂದ ಇಂಥ ಮಹಾಕಾರ್ಯವಾಗುವದು ಹೇಗೆ ಎಂದು ಕೇಳಲು ಅವನು - ನೀನು ಅರಾಮ್ಯರ ಅರಸನಾಗುವಿಯೆಂದು ಯೆಹೋವನು ನನಗೆ ತಿಳಿಸಿದ್ದಾನೆ ಅಂದನು.",
			"14": "<sup>14</sup> ಹಜಾಯೇಲನು ಎಲೀಷನನ್ನು ಬಿಟ್ಟು ತನ್ನ ಯಜಮಾನನ ಬಳಿಗೆ ಹೋದಾಗ ಅವನು ಇವನನ್ನು - ಎಲೀಷನು ಏನು ಹೇಳಿದನು ಎಂದು ಕೇಳಿದನು. ಅದಕ್ಕೆ ಇವನು - ನಿನಗೆ ಹೇಗೂ ಸ್ವಸ್ಥವಾಗುವದೆಂದು ಹೇಳಿದನು ಎಂಬದಾಗಿ ಉತ್ತರಕೊಟ್ಟನು.",
			"15": "<sup>15</sup> ಮರುದಿನ ಬೆಳಿಗ್ಗೆ ಇವನು ಕವುದಿಯನ್ನು ತೆಗೆದುಕೊಂಡು ನೀರಿನಲ್ಲಿ ತೋಯಿಸಿ ಅರಸನ ಮುಖದ ಮೇಲೆ ಹಾಕಿದದರಿಂದ ಅವನು ಸತ್ತನು. ಹಜಾಯೇಲನು ಅವನಿಗೆ ಬದಲಾಗಿ ಅರಸನಾದನು.",
			"16": "<sup>16</sup> ಅಹಾಬನ ಮಗನೂ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನೂ ಆದ ಯೋರಾಮನ ಆಳಿಕೆಯ ಐದನೆಯ ವರುಷದಲ್ಲಿ ಯೆಹೂದರಾಜನಾದ ಯೆಹೋಷಾಫಾಟನ ಮಗ ಯೆಹೋರಾಮನು ಆಳತೊಡಗಿದನು.",
			"17": "<sup>17</sup> ಇವನು ಪಟ್ಟಕ್ಕೆ ಬಂದಾಗ ಮೂವತ್ತೆರಡು ವರುಷದವನಾಗಿದ್ದು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಎಂಟು ವರುಷ ಆಳಿದನು.",
			"18": "<sup>18</sup> ಇವನು ಅಹಾಬನ ಮಗಳನ್ನು ಮದುವೆಮಾಡಿಕೊಂಡದರಿಂದ ಅಹಾಬನ ಕುಟುಂಬದವರಾದ ಇಸ್ರಾಯೇಲ್ ರಾಜರ ಮಾರ್ಗವನ್ನು ಅನುಸರಿಸಿ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾದನು.",
			"19": "<sup>19</sup> ಆದರೂ ಯೆಹೋವನು ಯೆಹೂದರಾಜ್ಯವನ್ನು ನಾಶಮಾಡಲಿಲ್ಲ. ನಿನ್ನ ಮತ್ತು ನಿನ್ನ ಸಂತಾನದವರ ದೀಪವನ್ನು ಎಂದೂ ನಂದಿಸುವದಿಲ್ಲ ಎಂಬದಾಗಿ ತನ್ನ ಸೇವಕನಾದ ದಾವೀದನಿಗೆ ಮಾಡಿದ ಪ್ರಮಾಣವನ್ನು ನೆನಸಿ ಅದನ್ನು ಉಳಿಸಿದನು.",
			"20": "<sup>20</sup> ಇವನ ಕಾಲದಲ್ಲಿ ಎದೋಮ್ಯರು ಯೆಹೂದ್ಯರಿಗೆ ವಿರೋಧವಾಗಿ ತಿರುಗಿಬಿದ್ದು ಸ್ವತಂತ್ರರಾಗಿ ತಮಗೆ ಒಬ್ಬ ಅರಸನನ್ನು ನೇಮಿಸಿಕೊಂಡರು.",
			"21": "<sup>21</sup> ಆಗ ಯೆಹೋರಾಮನು ತನ್ನ ಎಲ್ಲಾ ರಥಬಲಸಹಿತವಾಗಿ ಯೊರ್ದನ್ ಹೊಳೆಯನ್ನು ದಾಟಿ ಚಾಯೀರಿಗೆ ಹೋದನು. ಎದೋಮ್ಯರು ಬಂದು ಅವನನ್ನು ಸುತ್ತಿಕೊಳ್ಳಲು ಅವನು ರಾತ್ರಿಯಲ್ಲೆದ್ದು ಅವರನ್ನೂ ಅವರ ರಥಬಲದ ಅಧಿಪತಿಗಳನ್ನೂ ಸೋಲಿಸಿ ಪಾರಾದನು. ಅವನ ಸೈನ್ಯದವರೂ ತಮ್ಮತಮ್ಮ ನಿವಾಸಗಳಿಗೆ ಓಡಿಹೋದರು.",
			"22": "<sup>22</sup> ಹೀಗೆ ಎದೋಮ್ಯರು ಯೆಹೂದ್ಯರಿಗೆ ವಿರೋಧವಾಗಿ ತಿರುಗಿಬಿದ್ದರು. ಅವರು ಅಂದಿನಿಂದ ಇಂದಿನವರೆಗೂ ಸ್ವತಂತ್ರರಾಗಿರುತ್ತಾರೆ. ಅದೇ ಕಾಲದಲ್ಲಿ ಲಿಬ್ನದವರೂ ಸ್ವತಂತ್ರರಾದರು.",
			"23": "<sup>23</sup> ಯೆಹೋರಾಮನ ಉಳಿದ ಚರಿತ್ರೆಯೂ ಅವನ ಕೃತ್ಯಗಳೂ ಯೆಹೂದರಾಜಕಾಲವೃತ್ತಾಂತ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"24": "<sup>24</sup> ಅವನು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಲು ಅವನ ಶವವನ್ನು ದಾವೀದ ನಗರದೊಳಗೆ ಕುಟುಂಬ ಶ್ಮಶಾನಭೂಮಿಯಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ಅಹಜ್ಯನು ಅರಸನಾದನು.",
			"25": "<sup>25</sup> ಅಹಾಬನ ಮಗನೂ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನೂ ಆದ ಯೋರಾಮನ ಆಳಿಕೆಯ ಹನ್ನೆರಡನೆಯ ವರುಷದಲ್ಲಿ ಯೆಹೂದದ ಅರಸನಾದ ಯೆಹೋರಾಮನ ಮಗ ಅಹಜ್ಯನು ಅರಸನಾದನು.",
			"26": "<sup>26</sup> ಇವನು ಪಟ್ಟಕ್ಕೆ ಬಂದಾಗ ಇಪ್ಪತ್ತೆರಡು ವರುಷದವನಾಗಿದ್ದು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಒಂದು ವರುಷ ಆಳಿದನು. ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾದ ಒವ್ರಿುಯ ಮೊಮ್ಮಗಳಾದ ಅತಲ್ಯ ಎಂಬಾಕೆಯು ಇವನ ತಾಯಿ.",
			"27": "<sup>27</sup> ಇವನಿಗೂ ಅಹಾಬನ ಕುಟುಂಬದವರಿಗೂ ಬೀಗತನವಿದ್ದದರಿಂದ ಇವನು ಅವರ ಮಾರ್ಗದಲ್ಲೇ ನಡೆದು ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾದನು.",
			"28": "<sup>28</sup> ಇದಲ್ಲದೆ ಇವನು ಅರಾಮ್ಯರ ಅರಸನಾದ ಹಜಾಯೇಲನೊಡನೆ ಯುದ್ಧಮಾಡುವದಕ್ಕಾಗಿ ಅಹಾಬನ ಮಗನಾದ ಯೋರಾಮನ ಜೊತೆಯಲ್ಲಿ ರಾಮೋತ್ ಗಿಲ್ಯಾದಿಗೆ ಹೋದನು.",
			"29": "<sup>29</sup> ಅರಾಮ್ಯರು ಯೋರಾಮನನ್ನು ಗಾಯಪಡಿಸಲು ಅವನು ಯುದ್ಧದಲ್ಲಾದ ಗಾಯಗಳನ್ನು ಮಾಯಿಸಿಕೊಳ್ಳುವದಕ್ಕಾಗಿ ಇಜ್ರೇಲಿಗೆ ಬಂದನು. ಅವನು ಅಸ್ವಸ್ಥನಾಗಿದ್ದದರಿಂದ ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಯೆಹೋರಾಮನ ಮಗ ಅಹಜ್ಯನು ಅವನನ್ನು ನೋಡುವದಕ್ಕೋಸ್ಕರ ಅಲ್ಲಿಗೆ ಹೋದನು."
		},
		"9": {
			"1": "<sup>1</sup> ಪ್ರವಾದಿಯಾದ ಎಲೀಷನು ಪ್ರವಾದಿ ಮಂಡಲಿಯವರಲ್ಲಿ ಒಬ್ಬನನ್ನು ಕರೆದು ಅವನಿಗೆ - ನಡುಕಟ್ಟಿ ಈ ಎಣ್ಣೆಯ ಕುಪ್ಪಿಯನ್ನು ತೆಗೆದುಕೊಂಡು ರಾಮೋತ್ ಗಿಲ್ಯಾದಿಗೆ ಹೋಗು.",
			"2": "<sup>2</sup> ಊರನ್ನು ಮುಟ್ಟಿದ ನಂತರ ನಿಂಷಿಯ ಮೊಮ್ಮಗನೂ ಯೆಹೋಷಾಫಾಟನ ಮಗನೂ ಆದ ಯೇಹುವು ಎಲ್ಲಿರುತ್ತಾನೆಂದು ವಿಚಾರಿಸಿ ಅವನು ಸಿಕ್ಕಿದಾಗ ಅವನನ್ನು ಅವನ ಜೊತೆಗಾರರ ಗುಂಪಿನಿಂದ ಒಳಗಿನ ಕೋಣೆಗೆ ಕರಕೊಂಡು ಹೋಗು.",
			"3": "<sup>3</sup> ತರುವಾಯ ಕುಪ್ಪಿಯಲ್ಲಿರುವ ಎಣ್ಣೆಯನ್ನು ಅವನ ತಲೆಯ ಮೇಲೆ ಹೊಯ್ದು - ನಾನು ನಿನ್ನನ್ನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾಗುವದಕ್ಕೆ ಅಭಿಷೇಕಿಸಿದ್ದೇನೆಂದು ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ ಎಂಬದಾಗಿ ಹೇಳಿ ಕೂಡಲೆ ಕದತೆರೆದು ಓಡಿಹೋಗು ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"4": "<sup>4</sup> ಆಗ ಪ್ರವಾದಿಯ ಸೇವಕನಾದ ಆ ಯೌವನಸ್ಥನು ರಾಮೋತ್‍ಗಿಲ್ಯಾದಿಗೆ ಹೋಗಿ",
			"5": "<sup>5</sup> ಸೈನ್ಯಾಧಿಪತಿಗಳೆಲ್ಲಾ ಒಂದು ಕಡೆಯಲ್ಲಿ ಕೂಡಿರುವದನ್ನು ಕಂಡು ಅವರನ್ನು ಸಮೀಪಿಸಿ - ಸೇನಾಪತಿಯೇ, ನಿನಗೊಂದು ಮಾತು ಹೇಳುವದದೆ ಅಂದನು. ಯೇಹುವು ಅವನನ್ನು - ನಮ್ಮಲ್ಲಿ ಯಾರಿಗೆ ಎಂದು ಕೇಳಲು ಅವನು - ಸೇನಾಪತಿಯಾದ ನಿನಗೇ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"6": "<sup>6</sup> ಯೇಹುವು ಎದ್ದು ಒಳಗಿನ ಕೋಣೆಗೆ ಹೋಗಲು ಆ ಯೌವನಸ್ಥನು ಇವನ ತಲೆಯ ಮೇಲೆ ಎಣ್ಣೆಯನ್ನು ಹೊಯ್ದು - ಇಸ್ರಾಯೇಲ್‍ದೇವರಾದ ಯೆಹೋವನ ಮಾತನ್ನು ಕೇಳು - ಆತನು ನಿನಗೆ - ನಾನು ನಿನ್ನನ್ನು ನನ್ನ ಪ್ರಜೆಯಾದ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾಗುವದಕ್ಕೆ ಅಭಿಷೇಕಿಸಿದ್ದೇನೆ.",
			"7": "<sup>7</sup> ನೀನು ನಿನ್ನ ಯಜಮಾನನಾದ ಅಹಾಬನ ಮನೆಯವರನ್ನು ಸಂಹರಿಸಿಬಿಡು; ಆಗ ನನ್ನ ಸೇವಕರಾದ ಪ್ರವಾದಿಗಳೇ ಮೊದಲಾದ ಯೆಹೋವಭಕ್ತರ ರಕ್ತವನ್ನು ಸುರಿಸಿದ್ದಕ್ಕಾಗಿ ನಾನು ಈಜೆಬೆಲಳಿಗೆ ಮುಯ್ಯಿತೀರಿಸಿದಂತಾಗುವದು.",
			"8": "<sup>8</sup> ಅಹಾಬನ ಕುಟುಂಬದವರೆಲ್ಲಾ ನಿರ್ನಾಮವಾಗಬೇಕು. ಅವನ ಸಂತಾನದ ಗಂಡಸರಲ್ಲಿ ಸ್ವತಂತ್ರರಾಗಿರಲಿ ಪರತಂತ್ರರಾಗಿರಲಿ ಎಲ್ಲರನ್ನೂ ಇಸ್ರಾಯೇಲ್ಯರೊಳಗಿಂದ ತೆಗೆದುಹಾಕುವೆನು.",
			"9": "<sup>9</sup> ನೆಬಾಟನ ಮಗನಾದ ಯಾರೊಬ್ಬಾಮನ ಮನೆಗೂ ಅಹೀಯನ ಮಗನಾದ ಬಾಷನ ಮನೆಗೂ ಆದ ಗತಿಯು ಅಹಾಬನ ಮನೆಗೂ ಆಗುವದು.",
			"10": "<sup>10</sup> ಈಜೆಬೆಲಳ ಶವವು ಸಮಾಧಿಯನ್ನು ಸೇರುವದಿಲ್ಲ; ನಾಯಿಗಳು ಅದನ್ನು ಇಜ್ರೇಲಿನ ಹೊಲದಲ್ಲಿ ತಿಂದುಬಿಡುವವು ಅನ್ನುತ್ತಾನೆ ಎಂದು ಹೇಳಿ ಕದತೆರೆದು ಓಡಿಹೋದನು.",
			"11": "<sup>11</sup> ಯೇಹುವು ತಿರಿಗಿ ತನ್ನ ಒಡೆಯನ ಸೇವಕರ ಹತ್ತಿರ ಬರಲು ಅವರು ಅವನನ್ನು - ಶುಭವಾರ್ತೆಯೋ? ಆ ಹುಚ್ಚನು ನಿನ್ನ ಬಳಿಗೆ ಬಂದದ್ದೇಕೆ ಎಂದು ಕೇಳಿದರು. ಅದಕ್ಕೆ ಅವನು - ಆ ಮನುಷ್ಯನು ಎಂಥವನು, ಅವನ ಮಾತು ಎಂಥದು ಎಂಬದು ನಿಮಗೆ ಗೊತ್ತುಂಟಲ್ಲಾ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"12": "<sup>12</sup> ಆದರೆ ಅವರು - ಅದು ಸುಳ್ಳು; ಅವನು ಹೇಳಿದ್ದನ್ನು ತಿಳಿಸು ಎಂದು ಅವನನ್ನು ಒತ್ತಾಯಪಡಿಸಿದದರಿಂದ ಅವನು - ನಿನ್ನನ್ನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾಗುವದಕ್ಕೆ ಅಭಿಷೇಕಿಸಿದ್ದೇನೆಂಬದಾಗಿ ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ ಎಂಬೀ ಮೊದಲಾದ ಮಾತುಗಳನ್ನು ಹೇಳಿದನು ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"13": "<sup>13</sup> ಕೂಡಲೆ ಅವರು ಅವನಿಗೋಸ್ಕರ ಮೆಟ್ಲುಗಳ ಮೇಲೆ ತಮ್ಮ ಬಟ್ಟೆಗಳನ್ನು ಹಾಸಿ ತುತೂರಿಯನ್ನು ಊದಿಸಿ ಯೇಹುವು ಅರಸನಾಗಿದ್ದಾನೆಂದು ಆರ್ಭಟಿಸಿದರು.",
			"14": "<sup>14</sup> ಹೀಗೆ ನಿಂಷಿಯ ಮೊಮ್ಮಗನೂ ಯೆಹೋಶಾಫಾಟನ ಮಗನೂ ಆದ ಯೆಹುವು ಯೋರಾಮನಿಗೆ ವಿರೋಧವಾಗಿ ಒಳಸಂಚುಮಾಡಿದನು. (ಅರಾಮ್ಯರ ಅರಸನಾದ ಹಜಾಯೇಲನು ರಾಮೋತ್‍ಗಿಲ್ಯಾದಿಗೆ ವಿರೋಧವಾಗಿ ಬರಲು ಯೋರಾಮನು ಅದನ್ನು ಕಾಪಾಡುವದಕ್ಕೋಸ್ಕರ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರ ಸಹಿತವಾಗಿ ಹೋಗಿ ಹಜಾಯೇಲನೊಡನೆ",
			"15": "<sup>15</sup> ಯುದ್ಧಮಾಡುತ್ತಿರುವಾಗ ಗಾಯ ಹೊಂದಿದದರಿಂದ ಅದನ್ನು ಮಾಯಿಸಿಕೊಳ್ಳುವದಕ್ಕಾಗಿ ಹಿಂದಿರುಗಿ ಇಜ್ರೇಲಿಗೆ ಬಂದಿದ್ದನು.) ಯೇಹುವು ತನ್ನ ಜೊತೆಗಾರರಿಗೆ - ನೀವು ನನ್ನವರಾಗಿದ್ದರೆ ಈ ಸುದ್ದಿಯು ಇಜ್ರೇಲನ್ನು ಮುಟ್ಟದಂತೆ ಒಬ್ಬನನ್ನೂ ಈ ಪಟ್ಟಣದಿಂದ ಹೊರಗೆ ಬಿಡಬೇಡಿರಿ ಎಂದು ಹೇಳಿದನು.",
			"16": "<sup>16</sup> ಅನಂತರ ಅವನು ರಥದಲ್ಲಿ ಕೂತು ಇಜ್ರೇಲಿಗೆ ಹೋಗುವದಕ್ಕಾಗಿ ಹೊರಟನು. ಅಲ್ಲಿ ಅಸ್ವಸ್ಥನಾದ ಯೋರಾಮನೂ ಅವನನ್ನು ನೋಡುವದಕ್ಕೆ ಬಂದಿದ್ದ ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಅಹಜ್ಯನೂ ಇದ್ದರು.",
			"17": "<sup>17</sup> ಇಜ್ರೇಲಿನ ಬುರುಜಿನಲ್ಲಿದ್ದ ಕಾವಲುಗಾರನು ಯೇಹುವಿನ ಗುಂಪಿನವರನ್ನು ಕಂಡು - ಜನರ ಒಂದು ಗುಂಪು ಕಾಣಿಸುತ್ತದೆ ಎಂದು ಯೋರಾಮನಿಗೆ ತಿಳಿಸಿದನು. ಯೋರಾಮನು ಅವನಿಗೆ - ನೀನು ಒಬ್ಬ ರಾಹುತನನ್ನು ಕರೆದು ಬರುತ್ತಿರುವವರು ಶುಭವಾರ್ತೆಯನ್ನು ತರುತ್ತಾರೋ ಎಂದು ಕೇಳುವದಕ್ಕಾಗಿ ಕಳುಹಿಸು ಎಂಬದಾಗಿ ಆಜ್ಞಾಪಿಸಿದನು.",
			"18": "<sup>18</sup> ರಾಹುತನು ಯೇಹುವನ್ನು ಎದುರುಗೊಂಡು - ಅರಸನು ಶುಭವಾರ್ತೆಯುಂಟೋ ಎಂದು ಕೇಳುತ್ತಾನೆ ಅನ್ನಲು ಅವನು - ಶುಭವಾರ್ತೆ ನಿನಗೇನು? ನನ್ನ ಹಿಂದೆ ಬಾ ಎಂದು ಹೇಳಿದನು. ಕಾವಲುಗಾರನು ಅರಸನಿಗೆ - ರಾಹುತನು ಆ ಗುಂಪನ್ನು ಮುಟ್ಟಿದನು; ಆದರೆ ತಿರಿಗಿ ಬರುವದು ಕಾಣಿಸುವದಿಲ್ಲ ಎಂದು ತಿಳಿಸಿದಾಗ ಅರಸನು ಇನ್ನೊಬ್ಬ ರಾಹುತನನ್ನು ಕಳುಹಿಸಿದನು.",
			"19": "<sup>19</sup> ಇವನು ಹೋಗಿ ಯೇಹುವನ್ನು ಎದುರುಗೊಂಡು - ಅರಸನು ಶುಭವಾರ್ತೆಯುಂಟೋ ಎಂದು ಕೇಳುತ್ತಾನೆ ಅನ್ನಲು ಅವನು - ಶುಭವಾರ್ತೆ ನಿನಗೇನು? ನನ್ನ ಹಿಂದೆ ಬಾ ಎಂದನು.",
			"20": "<sup>20</sup> ಕಾವಲುಗಾರನು ತಿರಿಗಿ ಅರಸನಿಗೆ - ಎರಡನೆಯವನೂ ಆ ಗುಂಪನ್ನು ಮುಟ್ಟಿದನು; ಆದರೆ ಹಿಂದಿರುಗಿ ಬರುವದು ಕಾಣಿಸುವದೇ ಇಲ್ಲ. ರಥದಲ್ಲಿ ಕೂತಿರುವವನು ಕುದುರೆಗಳನ್ನು ಹುಚ್ಚು ಹಿಡಿದವನಂತೆ ಓಡಿಸುವದನ್ನು ನೋಡಿದರೆ ಅವನು ನಿಂಷಿಯ ಮೊಮ್ಮಗನಾದ ಯೇಹುವಾಗಿರಬೇಕು ಎಂದು ಹೇಳಿದನು.",
			"21": "<sup>21</sup> ಯೋರಾಮನು ರಥಹೂಡಬೇಕೆಂದು ತನ್ನ ಸೇವಕರಿಗೆ ಆಜ್ಞಾಪಿಸಿದನು. ಅವರು ಹೂಡಿದರು. ಆಗ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾದ ಇವನೂ ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಅಹಜ್ಯನೂ ತಮ್ಮತಮ್ಮ ರಥಗಳಲ್ಲಿ ಕೂತುಕೊಂಡು ಯೇಹುವನ್ನು ಎದುರುಗೊಳ್ಳುವದಕ್ಕಾಗಿ ಹೊರಟು ಇಜ್ರೇಲಿನವನಾದ ನಾಬೋತನ ಹೊಲದಲ್ಲಿ ಅವನನ್ನು ಸಂಧಿಸಿದರು.",
			"22": "<sup>22</sup> ಯೋರಾಮನು ಅವನನ್ನು ಕಂಡ ಕೂಡಲೆ - ಯೇಹುವೇ, ಶುಭವೋ ಎಂದು ಅವನನ್ನು ಕೇಳಿದನು. ಅದಕ್ಕೆ ಅವನು - ನಿನ್ನ ತಾಯಿಯ ದೇವದ್ರೋಹವೂ ಮಂತ್ರತಂತ್ರವೂ ಪ್ರಬಲವಾಗಿರುವಲ್ಲಿ ಶುಭವೆಲ್ಲಿಂದ ಬರುವದು ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"23": "<sup>23</sup> ಒಡನೆ ಯೋರಾಮನು ತನ್ನ ರಥವನ್ನು ತಿರುಗಿಸಿ ಅಹಜ್ಯನಿಗೆ - ಅಹಜ್ಯನೇ, ಇದು ಒಳಸಂಚು ಎಂದು ಹೇಳಿ ಓಡಿಹೋದನು.",
			"24": "<sup>24</sup> ಯೇಹುವು ಪೂರ್ಣಬಲದಿಂದ ಬಿಲ್ಲನ್ನು ಬೊಗ್ಗಿಸಿ ಯೋರಾಮನ ಬೆನ್ನಿಗೆ ಬಾಣವನ್ನೆಸೆದನು; ಅದು ಅವನ ಹೃದಯದಿಂದ ಹೊರಗೆ ಬಂದಿತು. ಅವನು ಮುದುರಿಕೊಂಡು ರಥದಲ್ಲಿ ಬಿದ್ದನು.",
			"25": "<sup>25</sup> ಆಗ ಯೇಹುವು ತನ್ನ ಜೊತೆಯಲ್ಲಿದ್ದ ಬಿದ್ಕರನೆಂಬ ಸೇನಾಪತಿಗೆ - ಯೋರಾಮನ ಶವವನ್ನು ಎತ್ತಿ ಇಜ್ರೇಲಿನವನಾದ ನಾಬೋತನ ಹೊಲದಲ್ಲಿ ಹಾಕು. ಒಂದು ದಿವಸ ನಾವಿಬ್ಬರೂ ಕುದುರೆಹತ್ತಿ ಇವನ ತಂದೆಯಾದ ಅಹಾಬನ ಹಿಂದಿನಿಂದ ಹೋಗುತ್ತಿರುವಾಗ ಯೆಹೋವನು ಅಹಾಬನಿಗೆ -",
			"26": "<sup>26</sup> ನನ್ನ ಮಾತನ್ನು ಕೇಳು, ನೀನು ನಿನ್ನೆ ಸುರಿಸಿದ ನಾಬೋತನ ಮತ್ತು ಅವನ ಮಕ್ಕಳ ರಕ್ತವನ್ನು ನಿಶ್ಚಯವಾಗಿ ನೋಡಿದ್ದೇನೆ. ನೀನು ಅವರ ರಕ್ತವನ್ನು ಸುರಿಸಿದ ಹೊಲದಲ್ಲೇ ನಿನಗೆ ಮುಯ್ಯಿತೀರಿಸುವೆನು ಎಂದು ಪ್ರವಾದಿಯಿಂದ ಹೇಳಿಸಿದ್ದು ನಿನಗೆ ನೆನಪಿರುತ್ತದಲ್ಲಾ; ಯೆಹೋವನ ಆ ಮಾತು ನೆರವೇರುವಂತೆ ಇವನ ಶವವನ್ನು ಆ ಹೊಲದಲ್ಲಿ ಹಾಕಿಬಿಡು ಎಂದು ಹೇಳಿದನು.",
			"27": "<sup>27</sup> ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಅಹಜ್ಯನು ಇದನ್ನೆಲ್ಲಾ ಕಂಡು ಬೇತ್‍ಹಗ್ಗನಿನ ಮಾರ್ಗವಾಗಿ ಓಡಿಹೋದನು. ಯೇಹುವು ಅವನನ್ನು ಹಿಂದಟ್ಟಿ - ಅವನನ್ನೂ ರಥದಲ್ಲಿಯೇ ಹೊಡಿಯಿರಿ ಎಂದು ಕೂಗಲು ಅವನ ಜನರು ಇಬ್ಲೆಯಾಮಿನ ಬಳಿಯಲ್ಲಿರುವ ಗೂರ್ ಗಟ್ಟದ ಮೇಲೆ ಅವನನ್ನು ಗಾಯಪಡಿಸಿದರು. ಅಹಜ್ಯನು ಮೆಗಿದ್ದೋವಿಗೆ ಓಡಿಹೋಗಿ ಅಲ್ಲಿ ಸತ್ತನು.",
			"28": "<sup>28</sup> ಅವನ ಸೇವಕರು ಅವನ ಶವವನ್ನು ರಥದಲ್ಲಿ ಹಾಕಿಕೊಂಡು ಯೆರೂಸಲೇಮಿನಲ್ಲಿರುವ ದಾವೀದ ನಗರಕ್ಕೆ ತಂದು ಅದನ್ನು ಅವನ ಕುಟುಂಬ ಶ್ಮಶಾನಭೂಮಿಯಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು.",
			"29": "<sup>29</sup> ಅಹಜ್ಯನು ಅಹಾಬನ ಮಗನಾದ ಯೋರಾಮನ ಆಳಿಕೆಯ ಹನ್ನೊಂದನೆಯ ವರುಷದಲ್ಲಿ ಯೆಹೂದ್ಯರ ಅರಸನಾಗಿದ್ದನು.",
			"30": "<sup>30</sup> ಯೇಹುವು ಇಜ್ರೇಲಿಗೆ ಬಂದನು. ಈಜೆಬೆಲಳು ಅದನ್ನು ಕೇಳಿ ತಲೆಯನ್ನು ಆಭರಣಗಳಿಂದ ಅಲಂಕರಿಸಿಕೊಂಡು ಕಣ್ಣಿಗೆ ಕಾಡಿಗೆಹಚ್ಚಿಕೊಂಡು ಕಿಟಕಿಯಿಂದ ನೋಡುತ್ತಾ ನಿಂತಳು.",
			"31": "<sup>31</sup> ಅನಂತರ ಅರಮನೆಯ ಪ್ರಾಕಾರದೊಳಗೆ ಬಂದ ಯೇಹುವನ್ನು ಕಂಡು ಅವನನ್ನು - ಯಜಮಾನನನ್ನು ಕೊಂದ ಜಿವ್ರಿುಗೆ ಸಮಾನನೇ, ಕ್ಷೇಮವೋ ಎಂದು ಕೇಳಿದಳು.",
			"32": "<sup>32</sup> ಅವನು ಕಣ್ಣೆತ್ತಿ ಕಿಟಕಿಯ ಕಡೆಗೆ ನೋಡಿ - ಅಲ್ಲಿ ನನ್ನ ಪಕ್ಷದವರು ಯಾರು ಎಂದು ಕೂಗಿದನು. ಕೂಡಲೆ ಆ ಕಿಟಕಿಯಿಂದ ಇಬ್ಬರು ಮೂವರು ಕಂಚುಕಿಗಳು ಅವನ ಕಡೆಗೆ ನೋಡಿದರು.",
			"33": "<sup>33</sup> ಅವನು ಅವರಿಗೆ - ಆಕೆಯನ್ನು ಕೆಳಗೆ ದೊಬ್ಬಿರಿ ಎಂದು ಆಜ್ಞಾಪಿಸಲು ಅವರು ದೊಬ್ಬಿದರು. ಆಕೆಯ ರಕ್ತವು ಗೋಡೆಗೂ ಕುದುರೆಗಳಿಗೂ ಸಿಡಿಯಿತು. ಅವನು ಆಕೆಯ ಶವವನ್ನು ತುಳಿಸಿಬಿಟ್ಟನು.",
			"34": "<sup>34</sup> ಅನಂತರ ಅವನು ಅರಮನೆಯೊಳಗೆ ಹೋಗಿ ಅನ್ನಪಾನಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ತನ್ನ ಸೇವಕರಿಗೆ - ಆ ಶಾಪಗ್ರಸ್ತ ಸ್ತ್ರೀಯ ಶವವನ್ನು ನೋಡಿಕೊಂಡು ಸಮಾಧಿಮಾಡಿರಿ; ಆಕೆಯು ರಾಜಪುತ್ರಿಯಾಗಿರುತ್ತಾಳಷ್ಟೆ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"35": "<sup>35</sup> ಸೇವಕರು ಶವವನ್ನು ಸಮಾಧಿಮಾಡುವದಕ್ಕಾಗಿ ಹೋದರು; ಆದರೆ ಅವರಿಗೆ ಆಕೆಯ ತಲೆಬುರುಡೆ, ಕೈಕಾಲುಗಳು ಹೊರತಾಗಿ ಬೇರೇನೂ ಸಿಕ್ಕಲಿಲ್ಲ.",
			"36": "<sup>36</sup> ಅವರು ಹಿಂದಿರುಗಿ ಯೇಹುವಿನ ಹತ್ತಿರ ಬಂದು ಆ ಸಂಗತಿಯನ್ನು ತಿಳಿಸಲು ಅವನು ಅವರಿಗೆ - ನಾಯಿಗಳು ಈಜೆಬೆಲಳ ದೇಹಮಾಂಸವನ್ನು ಇಜ್ರೇಲಿನ ಹೊಲದಲ್ಲಿ ತಿಂದು ಬಿಡುವವು;",
			"37": "<sup>37</sup> ಆಕೆಯ ಶವವು ಆ ಹೊಲಕ್ಕೆ ಗೊಬ್ಬರವಾಗುವದು; ಇದು ಈಜೆಬೆಲಳ ಶವವೆಂದು ಯಾರಿಗೂ ಗುರುತು ಸಿಕ್ಕದ ಹಾಗಾಗುವದು ಎಂಬದಾಗಿ ಯೆಹೋವನು ತಿಷ್ಬೀಯನೂ ತನ್ನ ಸೇವಕನೂ ಆದ ಎಲೀಯನ ಮುಖಾಂತರವಾಗಿ ಹೇಳಿಸಿದ ಮಾತು ಈಗ ನೆರವೇರಿತು ಅಂದನು."
		},
		"10": {
			"1": "<sup>1</sup> ಸಮಾರ್ಯಪಟ್ಟಣದಲ್ಲಿ ಅಹಾಬನ ಸಂತಾನದ ಎಪ್ಪತ್ತುಮಂದಿ ರಾಜಪುತ್ರರಿದ್ದರು. ಯೇಹುವು ಆ ಪಟ್ಟಣದಲ್ಲಿದ್ದ ಇಜ್ರೇಲಿನ ಅಧಿಕಾರಿಗಳಾದ ಹಿರಿಯರಿಗೂ ರಾಜಪುತ್ರಪಾಲಕರಿಗೂ ಪತ್ರಗಳನ್ನು ಬರೆದು ಅವುಗಳನ್ನು ದೂತರ ಮುಖಾಂತರವಾಗಿ ಕಳುಹಿಸಿದನು.",
			"2": "<sup>2</sup> ಅವುಗಳಲ್ಲಿ - ನಿಮ್ಮ ವಶದಲ್ಲಿ ನಿಮ್ಮ ಯಜಮಾನನ ಮಕ್ಕಳೂ ರಥಾಶ್ವಬಲಗಳೂ ಕೋಟೆಕೊತ್ತಲುಗಳುಳ್ಳ ಪಟ್ಟಣವೂ ಯುದ್ಧಾಯುಧಗಳೂ ಇರುತ್ತವಷ್ಟೆ.",
			"3": "<sup>3</sup> ಈ ಪತ್ರವು ನಿಮಗೆ ಮುಟ್ಟಿದೊಡನೆ ಆ ರಾಜಪುತ್ರರಲ್ಲಿ ಉತ್ತಮನೂ ಸಮರ್ಥನೂ ಆದ ಒಬ್ಬನನ್ನು ಆರಿಸಿಕೊಂಡು ಅವನನ್ನು ಅವನ ತಂದೆಯ ಸಿಂಹಾಸನದ ಮೇಲೆ ಕುಳ್ಳಿರಿಸಿ ನಿಮ್ಮ ಯಜಮಾನನ ಕುಟುಂಬದವರಿಗೋಸ್ಕರ ಯುದ್ಧಮಾಡಿರಿ ಎಂಬದಾಗಿ ಬರೆದಿತ್ತು.",
			"4": "<sup>4</sup> ಅವರು ಬಹಳವಾಗಿ ಭಯಪಟ್ಟು - ಇಬ್ಬರು ರಾಜರೂ ಅವನ ಮುಂದೆ ನಿಲ್ಲಲಾರದೆ ಹೋದರು; ನಾವು ನಿಲ್ಲುವದು ಹೇಗೆ ಅಂದುಕೊಂಡರು.",
			"5": "<sup>5</sup> ಅನಂತರ ರಾಜಗೃಹಾಧಿಪತಿ, ಪುರಾಧಿಕಾರಿ, ಹಿರಿಯರು, ರಾಜಪುತ್ರಪಾಲಕರು ಇವರೆಲ್ಲಾ ಕೂಡಿ ಯೇಹುವಿಗೆ - ನಾವು ನಿನ್ನ ಆಜ್ಞಾಧಾರಕರಾದ ಸೇವಕರು; ನಾವಾಗಿ ನಮಗೊಬ್ಬ ಅರಸನನ್ನು ನೇಮಿಸಿಕೊಳ್ಳುವದಿಲ್ಲ; ನಿನ್ನ ಚಿತ್ತವೇ ನೆರವೇರಲಿ ಎಂದು ಹೇಳಿಕಳುಹಿಸಿದರು.",
			"6": "<sup>6</sup> ಆಗ ಯೇಹುವು ಇನ್ನೊಂದು ಪತ್ರದ ಮೂಲಕವಾಗಿ ಅವರಿಗೆ - ನೀವು ನನ್ನ ಪಕ್ಷದವರಾಗಿದ್ದು ನನ್ನ ಆಜ್ಞಾನುಸಾರವಾಗಿ ನಡೆದುಕೊಳ್ಳಮನಸ್ಸುಳ್ಳವರಾಗಿದ್ದರೆ ನಾಳೆ ಇಷ್ಟು ಹೊತ್ತಿಗೆ ನಿಮ್ಮ ಯಜಮಾನನ ಸಂತಾನದವರ ತಲೆಗಳೊಡನೆ ಬನ್ನಿರಿ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು. ಎಪ್ಪತ್ತು ಮಂದಿ ರಾಜಪುತ್ರರು ಪಟ್ಟಣದ ಪ್ರಧಾನಪುರುಷರ ಪರಾಂಬರಿಕೆಯಲ್ಲಿದ್ದರು.",
			"7": "<sup>7</sup> ಪ್ರಧಾನ ಪುರುಷರು ಪತ್ರವನ್ನು ಓದಿದೊಡನೆ ಆ ಎಪ್ಪತ್ತು ಮಂದಿಯನ್ನು ಹಿಡಿದು ಕೊಂದು ಅವರ ತಲೆಗಳನ್ನು ಪುಟ್ಟಿಗಳಲ್ಲಿ ಹಾಕಿ ಇಜ್ರೇಲಿನಲ್ಲಿದ್ದ ಯೇಹುವಿಗೆ ಕಳುಹಿಸಿದರು.",
			"8": "<sup>8</sup> ರಾಜಪುತ್ರರ ತಲೆಗಳನ್ನು ತಂದಿದ್ದಾರೆಂಬ ವರ್ತಮಾನವು ಯೇಹುವಿಗೆ ಮುಟ್ಟಿದಾಗ ಅವನು - ಬೆಳಗಾಗುವ ತನಕ ಅವುಗಳನ್ನು ಹೆಬ್ಬಾಗಲಿನ ಹತ್ತಿರ ಎರಡು ರಾಶಿಗಳಾಗಿ ಹಾಕಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"9": "<sup>9</sup> ಮರುದಿನ ಬೆಳಿಗ್ಗೆ ಅವನು ಅವುಗಳ ಹತ್ತಿರ ಹೋಗಿ ಎಲ್ಲಾ ಜನರ ಎದುರಿಗೆ ನಿಂತು ಅವರಿಗೆ - ನೀವು ನಿರಪರಾಧಿಗಳು; ನಾನಾದರೋ ನನ್ನ ಯಜಮಾನನಿಗೆ ವಿರೋಧವಾಗಿ ಒಳಸಂಚುಮಾಡಿ ಅವನನ್ನು ಕೊಂದವನು; ಆದರೆ ಇವರನ್ನೆಲ್ಲಾ ಹತಿಸಿದವರಾರು?",
			"10": "<sup>10</sup> ಇಗೋ ಯೆಹೋವನು ಅಹಾಬನ ಮನೆಯವರನ್ನು ಕುರಿತು ಹೇಳಿದ ಮಾತುಗಳೆಲ್ಲಾ ತಪ್ಪದೆ ನೆರವೇರುವವೆಂದು ತಿಳಿದುಕೊಳ್ಳಿರಿ. ಯೆಹೋವನು ತನ್ನ ಸೇವಕನಾದ ಎಲೀಯನ ಮುಖಾಂತರವಾಗಿ ಹೇಳಿದ್ದನ್ನು ಮಾಡಿದ್ದಾನಷ್ಟೆ ಎಂದು ಹೇಳಿದನು.",
			"11": "<sup>11</sup> ಅನಂತರ ಅವನು ಇಜ್ರೇಲಿನಲ್ಲಿ ಉಳಿದ ಅಹಾಬನ ಕುಟುಂಬದವರನ್ನೂ ಅವನ ಸರದಾರರು, ಆಪ್ತಮಿತ್ರರು, ಪುರೋಹಿತರು ಇವರನ್ನೂ ಸಂಹರಿಸಿದನು; ಒಬ್ಬನನ್ನೂ ಉಳಿಸಲಿಲ್ಲ.",
			"12": "<sup>12</sup> ಆಮೇಲೆ ಯೇಹುವು ಸಮಾರ್ಯಕ್ಕೆ ಹೊರಟು ಹೋದನು. ಅವನು ದಾರಿಯಲ್ಲಿ ಕುರುಬರ ಛತ್ರಕ್ಕೆ ಬಂದಾಗ",
			"13": "<sup>13</sup> ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಅಹಜ್ಯನ ಸಹೋದರರನ್ನು ಕಂಡು ಅವರನ್ನು - ನೀವು ಯಾರು ಎಂದು ಕೇಳಿದನು. ಅದಕ್ಕೆ ಅವರು - ನಾವು ಅಹಜ್ಯನ ಸಹೋದರರು; ರಾಜಪುತ್ರರನ್ನೂ ರಾಜಮಾತೆಯ ಮಕ್ಕಳನ್ನೂ ವಂದಿಸುವದಕ್ಕೋಸ್ಕರ ಇಲ್ಲಿಗೆ ಬಂದಿದ್ದೇವೆ ಎಂದು ಉತ್ತರಕೊಟ್ಟರು.",
			"14": "<sup>14</sup> ಆಗ ಯೇಹುವು ತನ್ನ ಸೇವಕರಿಗೆ - ಅವರನ್ನು ಸಜೀವಿಗಳಾಗಿ ಹಿಡಿಯಿರಿ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು. ಅವರು ಅವರನ್ನು ಹಿಡಿದು ವಧಿಸಿ ಆ ಛತ್ರದ ಬಾವಿಯಲ್ಲಿ ಹಾಕಿದರು. ಅವರು ನಾಲ್ವತ್ತೆರಡು ಮಂದಿಯಿದ್ದರು; ಅವರಲ್ಲಿ ಒಬ್ಬನನ್ನೂ ಬಿಡಲಿಲ್ಲ.",
			"15": "<sup>15</sup> ಅವನು ಅಲ್ಲಿಂದ ಮುಂದೆ ಹೋಗುತ್ತಿರುವಾಗ ತನ್ನನ್ನು ಎದುರುಗೊಳ್ಳುವದಕ್ಕಾಗಿ ಬಂದ ರೇಕಾಬನ ಮಗನಾದ ಯೆಹೋನಾದಾಬನನ್ನು ಕಂಡು ವಂದಿಸಿ - ನಿನ್ನ ವಿಷಯದಲ್ಲಿ ನನ್ನ ಮನಸ್ಸು ಯಥಾರ್ಥವಾಗಿರುವಂತೆ ನಿನ್ನ ಮನಸ್ಸೂ ಯಥಾರ್ಥವಾಗಿರುತ್ತದೋ ಎಂದು ಕೇಳಿದನು. ಅವನು - ಹೌದು ಎಂದು ಉತ್ತರಕೊಟ್ಟನು. ಆಗ ಯೇಹುವು - ಹಾಗಾದರೆ ನಿನ್ನ ಕೈಕೊಡು ಅನ್ನಲು ಅವನು ಕೈಕೊಟ್ಟನು.",
			"16": "<sup>16</sup> ಯೇಹುವು ಅವನನ್ನು ತನ್ನ ರಥದಲ್ಲಿ ಕುಳ್ಳಿರಿಸಿಕೊಂಡು ನನ್ನ ಜೊತೆಯಲ್ಲಿ ಬಂದು ಯೆಹೋವನಲ್ಲಿ ನನಗಿರುವ ಆಸಕ್ತಿಯನ್ನು ನೋಡು ಎಂದು ಹೇಳಿ ಅವನನ್ನು ಕೈ ಹಿಡಿದು ತನ್ನ ರಥದಲ್ಲಿ ಕರಕೊಂಡು ಹೋದನು.",
			"17": "<sup>17</sup> ಸಮಾರ್ಯವನ್ನು ಸೇರಿದನಂತರ ಅಲ್ಲಿ ಉಳಿದಿದ್ದ ಅಹಾಬನ ಸಂತಾನದವರನ್ನೆಲ್ಲಾ ಸಂಹರಿಸಿಬಿಟ್ಟನು. ಹೀಗೆ ಯೆಹೋವನು ಎಲೀಯನ ಮುಖಾಂತರವಾಗಿ ಹೇಳಿಸಿದ ಮಾತು ನೆರವೇರಿತು.",
			"18": "<sup>18</sup> ತರುವಾಯ ಅವನು ಎಲ್ಲಾ ಜನರನ್ನು ಕೂಡಿಸಿ ಅವರಿಗೆ - ಅಹಾಬನು ಬಾಳನನ್ನು ಸ್ವಲ್ಪ ಮಾತ್ರ ಸೇವಿಸಿದನು; ಯೇಹುವಾದರೋ ಅಧಿಕವಾಗಿ ಸೇವಿಸಬೇಕೆಂದಿರುತ್ತಾನೆ.",
			"19": "<sup>19</sup> ಆದದರಿಂದ ನೀವು ಬಾಳನ ಎಲ್ಲಾ ಪ್ರವಾದಿಗಳನ್ನೂ ಭಕ್ತರನ್ನೂ ಯಾಜಕರನ್ನೂ ನನ್ನ ಬಳಿಗೆ ಕರಕೊಂಡು ಬನ್ನಿರಿ; ನಾನು ಬಾಳನಿಗೋಸ್ಕರ ಮಹಾಯಜ್ಞವನ್ನು ಸಮರ್ಪಿಸಬೇಕೆಂದಿರುತ್ತೇನಾದದರಿಂದ ಅವರಲ್ಲಿ ಒಬ್ಬನೂ ಬಾರದಿರಕೂಡದು; ಬಾರದವನು ಸಾಯಬೇಕು ಎಂದು ಹೇಳಿದನು. ಅವನು ಬಾಳನ ಭಕ್ತರನ್ನು ಸಂಹರಿಸಬೇಕೆಂದು ಈ ಯುಕ್ತಿಯನ್ನು ಮಾಡಿದನು.",
			"20": "<sup>20</sup> ಇದಲ್ಲದೆ ಅವನು ದೂತರ ಮುಖಾಂತರವಾಗಿ - ಬಾಳನ ಎಲ್ಲಾ ಭಕ್ತರೂ ತಮ್ಮನ್ನು ಶುದ್ಧಪಡಿಸಿಕೊಂಡು ಜಾತ್ರೆಗೆ ಬರಬೇಕೆಂದು ಇಸ್ರಾಯೇಲ್ಯರ ಸಮಸ್ತ ಪ್ರಾಂತಗಳಲ್ಲಿ ಪ್ರಕಟಿಸಿದನು.",
			"21": "<sup>21</sup> ಆಗ ಬಾಳನ ಭಕ್ತರಲ್ಲಿ ಒಬ್ಬನೂ ತಪ್ಪದೆ ಎಲ್ಲರೂ ಕೂಡಿ ಬಂದದರಿಂದ ಬಾಳನ ದೇವಸ್ಥಾನವೂ ಪೂರ್ಣವಾಗಿ ತುಂಬಿತು.",
			"22": "<sup>22</sup> ಯೇಹುವು ವಸ್ತ್ರಾಗಾರದ ಅಧಿಪತಿಗೆ - ಬಾಳನ ಭಕ್ತರೆಲ್ಲರಿಗೂ ಬಟ್ಟೆಗಳನ್ನು ಕೊಡು ಎಂದು ಆಜ್ಞಾಪಿಸಲು ಅವನು ತಂದುಕೊಟ್ಟನು.",
			"23": "<sup>23</sup> ಅನಂತರ ಯೇಹುವು ರೇಕಾಬನ ಮಗನಾದ ಯೆಹೋನಾದಾಬನೊಡನೆ ದೇವಸ್ಥಾನವನ್ನು ಪ್ರವೇಶಿಸಿ ಬಾಳನ ಭಕ್ತರಿಗೆ - ಯೆಹೋವನ ಭಕ್ತರು ನಿಮ್ಮ ಮಧ್ಯದಲ್ಲಿ ಬಂದಿರುತ್ತಾರೋ ವಿಚಾರಿಸಿ ನೋಡಿರಿ; ಬಾಳನ ಭಕ್ತರು ಮಾತ್ರ ಅಲ್ಲಿರಬೇಕು ಎಂದು ಹೇಳಿದನು.",
			"24": "<sup>24</sup> ಆಮೇಲೆ ಅವರು ಸರ್ವಾಂಗಹೋಮಯಜ್ಞಗಳನ್ನು ಸಮರ್ಪಿಸುವದಕ್ಕೆ ಹೋದರು. ಇದಕ್ಕೆ ಮೊದಲೇ ಅವನು ದೇವಸ್ಥಾನದ ಹೊರಗೆ ಎಂಭತ್ತು ಮಂದಿಯನ್ನು ಕಾಯುವದಕ್ಕಾಗಿ ಇರಿಸಿ ಅವರಿಗೆ - ನಾನು ಯಾರನ್ನು ನಿಮ್ಮ ಕೈಗೆ ಸಿಕ್ಕುವ ಹಾಗೆ ಮಾಡುವೆನೋ ಅವರಲ್ಲಿ ಒಬ್ಬನನ್ನೂ ಬಿಡಬೇಡಿರಿ; ಬಿಟ್ಟರೆ ಅವನ ಪ್ರಾಣಕ್ಕೆ ಬದಲಾಗಿ ನಿಮ್ಮ ಪ್ರಾಣವು ಹೋಗುವದು ಎಂಬದಾಗಿ ಆಜ್ಞಾಪಿಸಿದ್ದನು.",
			"25": "<sup>25</sup> ಸರ್ವಾಂಗಹೋಮವನ್ನು ಅರ್ಪಿಸಿದ ಕೂಡಲೆ ಯೇಹುವು ಕಾವಲುಗಾರರಿಗೂ ಸೇನಾಪತಿಗಳಿಗೂ - ಒಳಗೆ ಹೋಗಿ ಅವರನ್ನೆಲ್ಲಾ ಸಂಹರಿಸಿರಿ; ಒಬ್ಬನೂ ತಪ್ಪಿಸಿಕೊಳ್ಳಕೂಡದು ಎಂದು ಹೇಳಿದನು. ಅವರು ಒಳಗೆ ಹೋಗಿ ಅವರನ್ನು ಕತ್ತಿಯಿಂದ ಕೊಂದು ಅವರ ಶವಗಳನ್ನು ಹೊರಗೆ ಹಾಕಿ ಬಾಳನ ದೇವಸ್ಥಾನಕ್ಕೆ ಸೇರಿದ ಕೇರಿಯನ್ನು ಪ್ರವೇಶಿಸಿ",
			"26": "<sup>26</sup> ಅಲ್ಲಿನ ವಿಗ್ರಹ ಸ್ತಂಭಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಬಂದು ಸುಟ್ಟುಬಿಟ್ಟರು.",
			"27": "<sup>27</sup> ಇದಲ್ಲದೆ ಬಾಳನ ವಿಗ್ರಹಸ್ತಂಭವನ್ನೂ ದೇವಸ್ಥಾನವನ್ನೂ ಕೆಡವಿ ಹಾಳುಮಾಡಿದರು. ಅಂದಿನಿಂದ ಇಂದಿನವರೆಗೂ ಆ ಸ್ಥಳವನ್ನು ಶೌಚಕ್ಕಾಗಿ ಉಪಯೋಗಿಸುತ್ತಾರೆ.",
			"28": "<sup>28</sup> ಹೀಗೆ ಯೇಹುವು ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಬಾಳನ ಪೂಜೆಯನ್ನು ನಿಲ್ಲಿಸಿದನು.",
			"29": "<sup>29</sup> ಆದರೂ ಅವನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಪಾಪಕ್ಕೆ ಪ್ರೇರಿಸಿದ ನೆಬಾಟನ ಮಗನಾದ ಯಾರೊಬ್ಬಾಮನ ಮಾರ್ಗವನ್ನು ಬಿಡದೆ ಬೇತೇಲ್, ದಾನ್ ಎಂಬ ಊರುಗಳಲ್ಲಿದ್ದ ಚಿನ್ನದ ಬಸವಗಳನ್ನು ಪೂಜಿಸುತ್ತಿದ್ದನು.",
			"30": "<sup>30</sup> ಯೆಹೋವನು ಯೇಹುವಿಗೆ - ನೀನು ನನ್ನ ದೃಷ್ಟಿಗೆ ಸರಿಯಾದದ್ದನ್ನು ನಡಿಸಿ ನನ್ನ ಮೆಚ್ಚಿಕೆಯನ್ನು ಪಡೆದಿ; ಅಹಾಬನ ಮನೆಯವರನ್ನು ಕುರಿತು ನನ್ನ ಚಿತ್ತವನ್ನು ನೆರವೇರಿಸಿದಿ; ಆದದರಿಂದ ನಿನ್ನ ಸಂತಾನದವರು ನಾಲ್ಕನೆಯ ತಲೆಯವರೆಗೂ ಇಸ್ರಾಯೇಲ್ ಸಿಂಹಾಸನದ ಮೇಲೆ ಕೂತುಕೊಳ್ಳುವರು ಎಂದು ಹೇಳಿದನು.",
			"31": "<sup>31</sup> ಆದರೆ ಅವನು ಇಸ್ರಾಯೇಲ್‍ದೇವರಾದ ಯೆಹೋವನ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಪೂರ್ಣಮನಸ್ಸಿನಿಂದ ಕೈಕೊಳ್ಳುವದಕ್ಕೆ ಪ್ರಯತ್ನಿಸಲಿಲ್ಲ; ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಪಾಪಕ್ಕೆ ಪ್ರೇರಿಸಿದ ಯಾರೊಬ್ಬಾಮನ ಮಾರ್ಗವನ್ನು ಬಿಡಲಿಲ್ಲ.",
			"32": "<sup>32</sup> ಯೆಹೋವನು ಈ ಕಾಲದಲ್ಲಿ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಕುಗ್ಗಿಸತೊಡಗಿದನು. ಹಜಾಯೇಲನು ಬಂದು ಯೊರ್ದನಿನ ಪೂರ್ವದಿಕ್ಕಿಗೂ",
			"33": "<sup>33</sup> ಅರ್ನೊನ್‍ತಗ್ಗಿನ ಅರೋಯೇರ್ ಪಟ್ಟಣದ ಉತ್ತರದಿಕ್ಕಿಗೂ ಇರುವ ಗಿಲ್ಯಾದ್ ಬಾಷಾನ್ ಪ್ರಾಂತಗಳಲ್ಲಿ ವಾಸವಾಗಿದ್ದ ಗಾದ್ ರೂಬೇನ್ ಮನಸ್ಸೆ ಕುಲಗಳವರನ್ನೂ ಬೇರೆ ಎಲ್ಲಾ ಪ್ರಾಂತಗಳಲ್ಲಿರುವ ಇಸ್ರಾಯೇಲ್ಯರನ್ನೂ ಬಾಧಿಸಿದನು.",
			"34": "<sup>34</sup> ಯೇಹುವಿನ ಉಳಿದ ಚರಿತ್ರೆಯೂ ಅವನ ಶೂರಕೃತ್ಯಗಳ ವಿವರವೂ ಇಸ್ರಾಯೇಲ್‍ರಾಜಕಾಲ ವೃತ್ತಾಂತ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"35": "<sup>35</sup> ಅವನು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಲು ಅವನ ಶವವನ್ನು ಸಮಾರ್ಯದಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ಯೆಹೋವಾಹಾಜನು ಅರಸನಾದನು. ಯೇಹುವು ಸಮಾರ್ಯಪಟ್ಟಣದಲ್ಲಿದ್ದುಕೊಂಡು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಆಳಿದ್ದು ಇಪ್ಪತ್ತೆಂಟು ವರುಷ."
		},
		"11": {
			"1": "<sup>1</sup> ಅಹಜ್ಯನು ಮರಣಹೊಂದಿದನೆಂಬದನ್ನು ಅವನ ತಾಯಿಯಾದ ಅತಲ್ಯಳು ಕೇಳಿದಾಗ ಫಕ್ಕನೆ ರಾಜಸಂತಾನದವರನ್ನೆಲ್ಲಾ ಸಂಹರಿಸಿಬಿಟ್ಟಳು.",
			"2": "<sup>2</sup> ಆದರೆ ಅರಸನಾದ ಯೆಹೋರಾಮನ ಮಗಳೂ ಅಹಜ್ಯನ ತಂಗಿಯೂ ಆದ ಯೆಹೋಷೆಬಳೆಂಬಾಕೆಯು ಯಾರಿಗೂ ತಿಳಿಯದಂತೆ ತನ್ನ ಅಣ್ಣನ ಮಗನಾದ ಯೆಹೋವಾಷನನ್ನು ಹತವಾಗುವದಕ್ಕಿದ್ದ ರಾಜಪುತ್ರರ ಮಧ್ಯದಿಂದ ಅವನ ದಾದಿಯೊಡನೆ ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ಮಲಗುವ ಕೋಣೆಯಲ್ಲಿ ಅಡಗಿಸಿಟ್ಟಳು.",
			"3": "<sup>3</sup> ಹೀಗೆ ಅವನು ಅತಲ್ಯಳಿಂದ ಹತವಾಗದಂತೆ ತಪ್ಪಿಸಲ್ಪಟ್ಟು ಆರು ವರುಷಗಳವರೆಗೂ ಯೆಹೋಷೆಬಳೊಡನೆ ಗುಪ್ತವಾಗಿ ಯೆಹೋವನ ಆಲಯದಲ್ಲಿದ್ದನು. ಈ ಆರು ವರುಷಗಳಲ್ಲಿ ಅತಲ್ಯಳೇ ದೇಶವನ್ನು ಆಳುತ್ತಿದ್ದಳು.",
			"4": "<sup>4</sup> ಏಳನೆಯ ವರುಷದಲ್ಲಿ ಯೆಹೋಯಾದಾವನು ಕಾರಿ ಎಂಬ ಸಿಪಾಯಿಗಳ ಮತ್ತು ಕಾವಲುದಂಡಿನವರ ಶತಾಧಿಪತಿಗಳನ್ನು ತನ್ನ ಹತ್ತಿರ ಯೆಹೋವನ ಆಲಯಕ್ಕೆ ಕರಿಸಿದನು. ಅಲ್ಲಿ ಅವನು ಅವರ ಸಂಗಡ ಪ್ರಮಾಣಪೂರ್ವಕವಾದ ಒಡಂಬಡಿಕೆಯನ್ನು ಮಾಡಿಕೊಂಡನಂತರ",
			"5": "<sup>5</sup> ರಾಜಕುಮಾರನನ್ನು ತೋರಿಸಿ ಅವರಿಗೆ - ಸಬ್ಬತ್‍ದಿನದಲ್ಲಿ ಮನೆಗೆ ಹೋಗುವ ಸೈನ್ಯದ ಮೂರರಲ್ಲೊಂದು ಭಾಗವು ಅರಮನೆಯನ್ನು ಕಾಯಬೇಕು;",
			"6": "<sup>6</sup> ಇನ್ನೊಂದು ಭಾಗವು ಸೂರ್ ಬಾಗಲಿನಲ್ಲಿಯೂ ಮತ್ತೊಂದು ಭಾಗವು ಕಾವಲುದಂಡಿನ ಹಿಂದಣ ಬಾಗಲಿನಲ್ಲಿಯೂ ಇರಬೇಕು. ಈ ಪ್ರಕಾರ ನೀವು ಅರಮನೆಯನ್ನು ಭದ್ರವಾಗಿ ಕಾಯಬೇಕು.",
			"7": "<sup>7</sup> ಇದಲ್ಲದೆ ಸಬ್ಬತ್‍ದಿನದಲ್ಲಿ ಕಾಯುವದಕ್ಕಾಗಿ ಬರುವ ನಿಮ್ಮ ಇಮ್ಮಡಿಯಾದ ಸೈನ್ಯವು ಯೆಹೋವನ ಆಲಯದಲ್ಲಿರುವ ಅರಸನನ್ನು ಕಾಯಬೇಕು.",
			"8": "<sup>8</sup> ನೀವು ಕೈಯಲ್ಲಿ ಆಯುಧಗಳನ್ನು ಹಿಡಿದುಕೊಂಡು ಅವನ ಸುತ್ತಲೂ ನಿಂತು ನಿಮ್ಮ ಗುಂಪಿನಲ್ಲಿ ನುಗ್ಗುವಂಥವನನ್ನು ಸಂಹರಿಸಬೇಕು. ಅರಸನು ಹೊರಗೆ ಹೋಗಿ ಬರುವಾಗೆಲ್ಲಾ ನೀವು ಅವನ ಜೊತೆಯಲ್ಲೇ ಇರಬೇಕು, ಇದೇ ನನ್ನ ಅಪ್ಪಣೆ ಎಂದು ಹೇಳಿದನು.",
			"9": "<sup>9</sup> ಯಾಜಕನಾದ ಯೆಹೋಯಾದಾವನು ಆಜ್ಞಾಪಿಸಿದಂತೆ ಶತಾಧಿಪತಿಗಳು ಸಬ್ಬತ್‍ದಿನದಲ್ಲಿ ಮನೆಗೆ ಹೋಗತಕ್ಕ ಮತ್ತು ಕಾಯುವದಕ್ಕಾಗಿ ಬರತಕ್ಕ ಸಿಪಾಯಿಗಳನ್ನು ಕರಕೊಂಡು ಅವನ ಬಳಿಗೆ ಬಂದರು.",
			"10": "<sup>10</sup> ಅವನು ಆ ಶತಾಧಿಪತಿಗಳಿಗೆ ಯೆಹೋವನ ಆಲಯದಲ್ಲಿ ಇಡಲ್ಪಟ್ಟಿದ್ದ ದಾವೀದನ ಬರ್ಜಿಯನ್ನೂ ಗುರಾಣಿಗಳನ್ನೂ ಕೊಟ್ಟನು.",
			"11": "<sup>11</sup> ಕಾವಲುದಂಡಿನವರು ಆಯುಧ ಹಿಡಿದುಕೊಂಡು ಅರಸನನ್ನು ಕಾಯುವದಕ್ಕೋಸ್ಕರ ದೇವಾಲಯದ ದಕ್ಷಿಣದಿಕ್ಕಿನ ಮೂಲೆಯಿಂದ ಯಜ್ಞವೇದಿಯವರೆಗೂ ಅಲ್ಲಿಂದ ಉತ್ತರದಿಕ್ಕಿನ ಮೂಲೆಯವರೆಗೂ ಸಾಲಾಗಿ ನಿಂತರು.",
			"12": "<sup>12</sup> ಯೆಹೋಯಾದಾವನು ರಾಜಪುತ್ರನನ್ನು ಹೊರಗೆ ಕರಕೊಂಡು ಬಂದು ಅವನ ತಲೆಯ ಮೇಲೆ ಕಿರೀಟವನ್ನಿಟ್ಟು ಕೈಯಲ್ಲಿ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಕೊಟ್ಟು ಅವನಿಗೆ ರಾಜ್ಯಾಭಿಷೇಕಮಾಡಿದನು. ಕೂಡಲೆ ಜನರು ಚಪ್ಪಾಳೆಹೊಡೆದು ಅರಸನು ಚಿರಂಜೀವಿಯಾಗಿರಲಿ ಎಂದು ಕೂಗಿದರು.",
			"13": "<sup>13</sup> ಅತಲ್ಯಳು ಕಾವಲುದಂಡಿನವರ ಮತ್ತು ಇತರ ಜನರ ಗದ್ದಲವನ್ನು ಕೇಳಿ ಅವರು ಕೂಡಿದ್ದ ಯೆಹೋವನ ಆಲಯಕ್ಕೆ ಬಂದಳು.",
			"14": "<sup>14</sup> ಅಲ್ಲಿ ಅರಸನು ಪದ್ಧತಿಯ ಪ್ರಕಾರ ಸ್ತಂಭದ ಬಳಿಯಲ್ಲಿ ನಿಂತಿದ್ದನು; ಅಧಿಪತಿಗಳೂ ತುತೂರಿಗಳನ್ನು ಊದುವವರೂ ಅರಸನ ಹತ್ತಿರ ಇದ್ದರು. ಸಾಧಾರಣ ಜನರೆಲ್ಲರೂ ಸಂತೋಷದಿಂದ ಕೊಂಬೂದುತ್ತಿದ್ದರು. ಇದನ್ನು ಕಂಡ ಕೂಡಲೆ ಆಕೆಯು ಬಟ್ಟೆಗಳನ್ನು ಹರಿದುಕೊಂಡು - ದ್ರೋಹ, ದ್ರೋಹ ಎಂದು ಕೂಗಿದಳು.",
			"15": "<sup>15</sup> ಆಗ ಯಾಜಕನಾದ ಯೆಹೋಯಾದಾವನು ಸೇನಾನಿಗಳಾದ ಶತಾಧಿಪತಿಗಳಿಗೆ - ಈಕೆಯನ್ನು ಯೆಹೋವನ ಆಲಯದಲ್ಲಿ ಕೊಲ್ಲಬೇಡಿರಿ; ಎರಡು ಸಾಲು ಸಿಪಾಯಿಗಳು ಈಕೆಯನ್ನು ನಡುವೆ ಮಾಡಿ ಹೊರಗೆ ಕರಕೊಂಡು ಹೋಗಲಿ; ಮತ್ತು ಈಕೆಯನ್ನು ಹಿಂಬಾಲಿಸುವಂಥವರನ್ನು ಕತ್ತಿಯಿಂದ ಕೊಲ್ಲಿರಿ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"16": "<sup>16</sup> ಅವರು ಆಕೆಯನ್ನು ಹಿಡಿದು ಕುದುರೆಗಳ ಬಾಗಲಿನಿಂದ ಅರಮನೆಗೆ ಒಯ್ದು ಅಲ್ಲಿ ಹತಿಸಿದರು.",
			"17": "<sup>17</sup> ಅನಂತರ ಯೆಹೋಯಾದಾವನ ಪ್ರೇರಣೆಯಿಂದ ಅರಸನೂ ಪ್ರಜೆಗಳೂ ತಾವು ಯೆಹೋವನ ಪ್ರಜೆಗಳಾಗಿರುವದಾಗಿ ಆತನಿಗೆ ಪ್ರಮಾಣಮಾಡಿದರು. ಇದಲ್ಲದೆ ರಾಜಪ್ರಜೆಗಳಿಗೂ ಒಡಂಬಡಿಕೆಯಾಯಿತು.",
			"18": "<sup>18</sup> ಯೆಹೂದ್ಯರೆಲ್ಲರೂ ಬಾಳನ ದೇವಸ್ಥಾನಕ್ಕೆ ಹೋಗಿ ಬಾಳನ ಪೂಜಾರಿಯಾದ ಮತ್ತಾನನನ್ನು ವೇದಿಗಳ ಮುಂದೆಯೇ ಕೊಂದು ದೇವಸ್ಥಾನವನ್ನೂ ಅದರಲ್ಲಿದ್ದ ವೇದಿ ವಿಗ್ರಹಗಳನ್ನೂ ಸಂಪೂರ್ಣವಾಗಿ ಹಾಳು ಮಾಡಿದರು.",
			"19": "<sup>19</sup> ಯೆಹೋಯಾದಾವನು ದೇವಾಲಯಕ್ಕೆ ಕಾವಲಿಟ್ಟು ಶತಾಧಿಪತಿಗಳು, ಕಾರಿ ಎನಿಸಿಕೊಳ್ಳುವ ಸೈನ್ಯ, ಕಾವಲುದಂಡು, ಸಾಧಾರಣ ಜನರು ಇವರೊಡನೆ ಅರಸನನ್ನು ಯೆಹೋವನ ಆಲಯದಿಂದ ಕಾವಲುದಂಡಿನವರ ಬಾಗಲಿನ ಮಾರ್ಗವಾಗಿ ಅರಮನೆಗೆ ಕರಕೊಂಡು ಹೋಗಿ ರಾಜಸಿಂಹಾಸನದ ಮೇಲೆ ಕುಳ್ಳಿರಿಸಿದನು. ದೇಶದವರೆಲ್ಲರೂ ಬಹಳವಾಗಿ ಸಂತೋಷಪಟ್ಟರು;",
			"20": "<sup>20</sup> ಪಟ್ಟಣದಲ್ಲಿ ಶಾಂತಿಯಿತ್ತು; ಅತಲ್ಯಳನ್ನು ಅರಮನೆಯಲ್ಲಿ ಕತ್ತಿಯಿಂದ ಸಂಹರಿಸಿದರು.",
			"21": "<sup>21</sup> ಯೆಹೋವಾಷನು ಅರಸನಾದಾಗ ಏಳು ವರುಷದವನಾಗಿದ್ದನು."
		},
		"12": {
			"1": "<sup>1</sup> ಯೆಹೋವಾಷನು ಯೇಹುವಿನ ಆಳಿಕೆಯ ಏಳನೆಯ ವರುಷದಲ್ಲಿ ಅರಸನಾಗಿ ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ನಾಲ್ವತ್ತು ವರುಷ ಆಳಿದನು. ಬೇರ್ಷೆಬದವಳಾದ ಚಿಬ್ಯಳು ಅವನ ತಾಯಿ.",
			"2": "<sup>2</sup> ಯಾಜಕನಾದ ಯೆಹೋಯಾದಾವನು ಅವನಿಗೆ ಬೋಧಕನಾಗಿದ್ದ ಕಾಲದಲ್ಲಿ ಅವನು ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ಒಳ್ಳೆಯವನಾಗಿಯೇ ನಡೆದುಕೊಳ್ಳುತ್ತಿದ್ದನು.",
			"3": "<sup>3</sup> ಆದರೆ ಅವನು ಪೂಜಾಸ್ಥಳಗಳನ್ನು ತೆಗೆದುಹಾಕಲಿಲ್ಲವಾದದರಿಂದ ಜನರು ಅವುಗಳ ಮೇಲೆಯೇ ಯಜ್ಞಧೂಪಗಳನ್ನು ಅರ್ಪಿಸುತ್ತಿದ್ದರು.",
			"4": "<sup>4</sup> ಯೆಹೋವಾಷನು ಯಾಜಕರಿಗೆ - ನೀವು ಯೆಹೋವನ ಆಲಯಕ್ಕೆ ಸೇರುವ ಎಲ್ಲಾ ಪರಿಶುದ್ಧದ್ರವ್ಯವನ್ನು ಅಂದರೆ ಖಾನೇಷುಮಾರಿಯಲ್ಲಿ ಲೆಕ್ಕಿಸಲ್ಪಟ್ಟ ಪ್ರತಿಯೊಬ್ಬನು ತಂದುಕೊಡುವ ಹಣ, ದೇವರಿಗೆ ಪ್ರತಿಷ್ಠಿತನಾದ ಮನುಷ್ಯನು ತನ್ನ ಪ್ರಾಣವನ್ನು ಬಿಡಿಸಿಕೊಳ್ಳುವದಕ್ಕಾಗಿ ತರುವ ಹಣ, ಜನರು ಯೆಹೋವನ ಆಲಯಕ್ಕೆ ಸ್ವೇಚ್ಫೆಯಿಂದ ತಂದೊಪ್ಪಿಸುವ ಹಣ ಇವುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು",
			"5": "<sup>5</sup> ದೇವಾಲಯವು ಎಲ್ಲೆಲ್ಲಿ ಶಿಥಿಲವಾಗಿರುತ್ತದೆಂದು ನೋಡಿ ಅದನ್ನು ಸರಿಮಾಡುವದಕ್ಕೋಸ್ಕರ ವಿನಿಯೋಗಿಸಿರಿ; ಪ್ರತಿಯೊಬ್ಬನೂ ತನ್ನ ತನ್ನ ಪರಿಚಿತರಿಂದಲೇ ಹಣ ತೆಗೆದುಕೊಳ್ಳಬೇಕು ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"6": "<sup>6</sup> ಯಾಜಕರು ಅರಸನಾದ ಯೆಹೋವಾಷನ ಆಳಿಕೆಯ ಇಪ್ಪತ್ತಮೂರನೆಯ ವರುಷದವರೆಗೂ ದೇವಾಲಯವನ್ನು ಜೀರ್ಣೋದ್ಧಾರಮಾಡಿರಲಿಲ್ಲ.",
			"7": "<sup>7</sup> ಆದದರಿಂದ ಅವನು ಯೆಹೋಯಾದಾವ ಮೊದಲಾದ ಯಾಜಕರನ್ನು ಕರೆದು ಅವರಿಗೆ - ನೀವು ಈ ವರೆಗೂ ದೇವಾಲಯವನ್ನೇಕೆ ಜೀರ್ಣೋದ್ಧಾರ ಮಾಡಲಿಲ್ಲ? ಇನ್ನು ಮುಂದೆ ನಿಮ್ಮ ಪರಿಚಿತರು ತಂದು ಕೊಡುವ ಹಣವನ್ನು ನಿಮ್ಮ ಕೈಯಲ್ಲಿಟ್ಟುಕೊಳ್ಳದೆ ಕೂಡಲೆ ಅದನ್ನು ದೇವಾಲಯದ ಜೀರ್ಣೋದ್ಧಾರಕ್ಕಾಗಿ ಕೊಡಿರಿ ಎಂದು ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ಆಗ ಯಾಜಕರು ಅವನಿಗೆ - ನಾವು ಇನ್ನು ಮುಂದೆ ಜನರಿಂದ ಹಣ ತೆಗೆದುಕೊಳ್ಳುವದಿಲ್ಲ. ದೇವಾಲಯದ ಜೀರ್ಣೋದ್ಧಾರಕ್ಕೆ ಕೈಹಾಕುವದಿಲ್ಲ ಎಂಬದಾಗಿ ಮಾತುಕೊಟ್ಟರು.",
			"9": "<sup>9</sup> ಯಾಜಕನಾದ ಯೆಹೋಯಾದಾವನು ಒಂದು ಪೆಟ್ಟಿಗೆಯನ್ನು ತೆಗೆದುಕೊಂಡು ಬಂದು ಅದರ ಮುಚ್ಚಳದಲ್ಲಿ ತೂತುಮಾಡಿ ಅದನ್ನು ಯೆಹೋವನ ಆಲಯದ ಬಾಗಲಿನ ಬಲಗಡೆಯಲ್ಲಿ ಯಜ್ಞವೇದಿಯ ಹತ್ತಿರ ಇಟ್ಟನು. ದ್ವಾರಪಾಲಕರಾದ ಯಾಜಕರು ಯೆಹೋವನ ಆಲಯಕ್ಕೆ ತರಲ್ಪಟ್ಟ ಹಣವನ್ನೆಲ್ಲಾ ಅದರಲ್ಲಿಯೇ ಹಾಕಿಸುತ್ತಿದ್ದರು.",
			"10": "<sup>10</sup> ಪೆಟ್ಟಿಗೆಯಲ್ಲಿ ತಕ್ಕಷ್ಟು ಹಣಕೂಡಿದೆಯೆಂದು ಕಂಡು ಬಂದಾಗೆಲ್ಲಾ ರಾಜಲೇಖಕನೂ ಮಹಾಯಾಜಕನೂ ಬಂದು ಅದನ್ನು ಚೀಲಗಳಲ್ಲಿ ಹಾಕಿ ತೂಕಮಾಡಿ ಯೆಹೋವನ ಆಲಯದ ಹಣವು ಇಷ್ಟಿಷ್ಟು ಆಯಿತೆಂದು ಬರೆದುಕೊಂಡು",
			"11": "<sup>11</sup> ಅದನ್ನು ಆಲಯದ ಕೆಲಸವನ್ನು ನಡಿಸುವ ಮೊಕ್ತೇಸರರಿಗೆ ಒಪ್ಪಿಸುವರು; ಇವರು ಅದನ್ನು ಅಲ್ಲಿ ಕೆಲಸಮಾಡುವ ಬಡಗಿ, ಶಿಲ್ಪಿ, ಉಪ್ಪಾರ, ಕಲ್ಲುಕುಟಿಕ",
			"12": "<sup>12</sup> ಇವರ ಸಂಬಳಕ್ಕಾಗಿಯೂ ಮರ, ಕೆತ್ತಿದ ಕಲ್ಲು ಇವುಗಳನ್ನು ಕೊಂಡುಕೊಳ್ಳುವದಕ್ಕಾಗಿಯೂ ಆಲಯದ ಜೀರ್ಣೋದ್ಧಾರದ ಬೇರೆ ಎಲ್ಲಾ ವೆಚ್ಚಕ್ಕಾಗಿಯೂ ಉಪಯೋಗಿಸುವರು.",
			"13": "<sup>13</sup> ಯೆಹೋವನ ಆಲಯಕ್ಕೆ ತರಲ್ಪಟ್ಟ ಹಣವನ್ನು ಜೀರ್ಣೋದ್ಧಾರಕಾರ್ಯದವರ ಸಂಬಳಕ್ಕಾಗಿ ಉಪಯೋಗಿಸಿದರೇ ಹೊರತು",
			"14": "<sup>14</sup> ಯೆಹೋವನ ಆಲಯದ ಬೆಳ್ಳಿಯ ಬಟ್ಟಲು, ಕತ್ತರಿ, ಬೋಗುಣಿ, ತುತೂರಿ, ಬೆಳ್ಳಿಬಂಗಾರದ ಪಾತ್ರೆ ಇವುಗಳನ್ನು ಮಾಡುವದಕ್ಕಾಗಿ ಉಪಯೋಗಿಸಲಿಲ್ಲ.",
			"15": "<sup>15</sup> ಕೆಲಸಗಾರರಿಗೆ ಸಂಬಳ ಕೊಡುವ ಮೊಕ್ತೇಸರರು ನಂಬಿಗಸ್ತರಾಗಿದ್ದದರಿಂದ ಅವರ ವಶಕ್ಕೆ ಕೊಡಲ್ಪಟ್ಟ ಹಣದ ಲೆಕ್ಕವನ್ನು ಯಾರೂ ಕೇಳಲಿಲ್ಲ.",
			"16": "<sup>16</sup> ಅಪರಾಧ ಪ್ರಾಯಶ್ಚಿತ್ತಕ್ಕಾಗಿಯೂ ದೋಷಪರಿಹಾರಕ್ಕಾಗಿಯೂ ತರಲ್ಪಟ್ಟ ಹಣವು ಯಾಜಕರ ಭಾಗವಾಗಿತ್ತು; ಅದು ಯೆಹೋವನ ಆಲಯಕ್ಕೆ ಸೇರಲಿಲ್ಲ.",
			"17": "<sup>17</sup> ಇದೇ ಕಾಲದಲ್ಲಿ ಅರಾಮ್ಯರ ಅರಸನಾದ ಹಜಾಯೇಲನು ಬಂದು ಗತ್ ಊರಿಗೆ ಮುತ್ತಿಗೆಹಾಕಿ ಅದನ್ನು ಸ್ವಾಧೀನಪಡಿಸಿಕೊಂಡು ಅಲ್ಲಿಂದ ಯೆರೂಸಲೇಮಿಗೆ ವಿರೋಧವಾಗಿ ಹೊರಟನು.",
			"18": "<sup>18</sup> ಯೆಹೂದದ ಅರಸನಾದ ಯೆಹೋವಾಷನು ಇದನ್ನು ಕೇಳಿ ತಾನೂ ತನ್ನ ಪೂರ್ವಿಕರಾದ ಯೆಹೋಷಾಫಾಟ್, ಯೆಹೋರಾಮ್, ಅಹಜ್ಯ ಎಂಬ ಯೆಹೂದರಾಜರೂ ದೇವಸ್ಥಾನಕ್ಕೆ ಕಾಣಿಕೆಯಾಗಿ ಕೊಟ್ಟ ಒಡವೆಗಳು, ಯೆಹೋವನ ಆಲಯದ ಮತ್ತು ಅರಮನೆಯ ಭಂಡಾರಗಳಲ್ಲಿದ್ದ ಬಂಗಾರ ಇವುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಅರಾಮ್ಯರ ಅರಸನಾದ ಹಜಾಯೇಲನಿಗೆ ಕಳುಹಿಸಿದನು. ಆಗ ಅವನು ಯೆರೂಸಲೇಮನ್ನು ಬಿಟ್ಟು ಹೋದನು.",
			"19": "<sup>19</sup> ಯೆಹೋವಾಷನ ಉಳಿದ ಚರಿತ್ರೆಯೂ ಅವನ ಎಲ್ಲಾ ಕೃತ್ಯಗಳೂ ಯೆಹೂದರಾಜಕಾಲವೃತ್ತಾಂತ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"20": "<sup>20</sup> ಯೆಹೋವಾಷನ ಉದ್ಯೋಗಸ್ಥರು ಅವನಿಗೆ ವಿರೋಧವಾಗಿ ಒಳಸಂಚು ಮಾಡಿದರು.",
			"21": "<sup>21</sup> ಅವರಲ್ಲಿ ಶಿಮೆಯಾತನ ಮಗನಾದ ಯೋಜಾಕಾರ್, ಶೋಮೇರನ ಮಗನಾದ ಯೆಹೋಜಾಬಾದ್ ಎಂಬವರು ಸಿಲ್ಲಾ ಊರಿಗೆ ಹೋಗುವ ಗಟ್ಟದ ದಾರಿಯಲ್ಲಿರುವ ಮಿಲ್ಲೋಕೋಟೆಯಲ್ಲಿ ಅವನನ್ನು ಕೊಂದರು. ಅವನ ಜನರು ಅವನ ಶವವನ್ನು ದಾವೀದ ನಗರದೊಳಗೆ ಅವನ ಕುಟುಂಬಶ್ಮಶಾನ ಭೂಮಿಯಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ಅಮಚ್ಯನು ಅರಸನಾದನು."
		},
		"13": {
			"1": "<sup>1</sup> ಯೆಹೂದದ ಅರಸನೂ ಅಹಜ್ಯನ ಮಗನೂ ಆದ ಯೆಹೋವಾಷನ ಆಳಿಕೆಯ ಇಪ್ಪತ್ತಮೂರನೆಯ ವರುಷದಲ್ಲಿ ಯೇಹುವಿನ ಮಗನಾದ ಯೆಹೋವಾಹಾಜನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾಗಿ ಸಮಾರ್ಯದಲ್ಲಿ ಹದಿನೇಳು ವರುಷ ಆಳಿದನು.",
			"2": "<sup>2</sup> ಅವನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಪಾಪಕ್ಕೆ ಪ್ರೇರಿಸಿದ ನೆಬಾಟನ ಮಗನಾದ ಯಾರೊಬ್ಬಾಮನ ಮಾರ್ಗವನ್ನು ಬಿಡದೆ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾದನು.",
			"3": "<sup>3</sup> ಆದದರಿಂದ ಯೆಹೋವನು ಇಸ್ರಾಯೇಲ್ಯರ ಮೇಲೆ ಕೋಪಗೊಂಡು ಅವರನ್ನು ಅರಾಮ್ಯರ ಅರಸನಾದ ಹಜಾಯೇಲನ ಕೈಗೂ ಅವನ ಮಗನಾದ ಬೆನ್ಹದದನ ಕೈಗೂ ಒಪ್ಪಿಸಿದನು. ಅವರು ಯೆಹೋವಾಹಾಜನ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ಇವರ ಕೈಯಲ್ಲಿದ್ದರು.",
			"4": "<sup>4</sup> (ಯೆಹೋವಾಹಾಜನು ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಟ್ಟನು. ಆತನು ಅವನ ಮೊರೆಯನ್ನು ಲಾಲಿಸಿ ಅರಾಮ್ಯರ ಅರಸನಿಂದ ಬಹಳವಾಗಿ ಬಾಧಿಸಲ್ಪಟ್ಟ ಇಸ್ರಾಯೇಲ್ಯರ ಮೇಲೆ ಕಟಾಕ್ಷವಿಟ್ಟು",
			"5": "<sup>5</sup> ಅವರಿಗೆ ಒಬ್ಬ ರಕ್ಷಕನನ್ನು ಅನುಗ್ರಹಿಸಿದನು. ಇವನ ಮುಖಾಂತರವಾಗಿ ಅವರು ಅರಾಮ್ಯರ ಕೈಯಿಂದ ಬಿಡುಗಡೆಯಾಗಿ ಮುಂಚಿನಂತೆ ತಮ್ಮ ತಮ್ಮ ಮನೆಗಳಲ್ಲಿ ಸುರಕ್ಷಿತರಾಗಿ ವಾಸಿಸುವವರಾದರು.",
			"6": "<sup>6</sup> ಆದರೂ ಅವರು ತಮ್ಮನ್ನು ಪಾಪಕ್ಕೆ ಪ್ರೇರಿಸಿದ ಯಾರೊಬ್ಬಾಮನ ಮನೆಯವರ ಮಾರ್ಗದಲ್ಲಿ ನಡೆದರು; ಅದನ್ನು ಬಿಡಲೇ ಇಲ್ಲ. ಸಮಾರ್ಯದಲ್ಲಿ ಅಶೇರವಿಗ್ರಹಸ್ತಂಭವು ಇದ್ದೇ ಇತ್ತು.)",
			"7": "<sup>7</sup> ಯೆಹೋವಾಹಾಜನಿಗೆ ಐವತ್ತು ಮಂದಿ ರಾಹುತರು, ಹತ್ತು ರಥಗಳು, ಹತ್ತುಸಾವಿರ ಮಂದಿ ಕಾಲಾಳುಗಳು ಮಾತ್ರ ಉಳಿದರು. ಅರಾಮ್ಯರ ಅರಸನು ಬೇರೆ ಎಲ್ಲರನ್ನೂ ಸಂಹರಿಸಿ ಕಣದ ಧೂಳಿನಂತೆ ಮಾಡಿದನು.",
			"8": "<sup>8</sup> ಯೆಹೋವಾಹಾಜನ ಉಳಿದ ಚರಿತ್ರೆಯೂ ಅವನ ಶೂರಕೃತ್ಯಗಳ ವಿವರವೂ ಇಸ್ರಾಯೇಲ್‍ರಾಜಕಾಲವೃತ್ತಾಂತ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"9": "<sup>9</sup> ಅವನು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಲು ಅವನ ಶವವನ್ನು ಸಮಾರ್ಯದಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ಯೋವಾಷನು ಅರಸನಾದನು.",
			"10": "<sup>10</sup> ಯೆಹೂದದ ಅರಸನಾದ ಯೆಹೋವಾಷನ ಆಳಿಕೆಯ ಮೂವತ್ತೇಳನೆಯ ವರುಷದಲ್ಲಿ ಯೆಹೋವಾಹಾಜನ ಮಗನಾದ ಯೋವಾಷನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾಗಿ ಸಮಾರ್ಯದಲ್ಲಿ ಹದಿನಾರು ವರುಷ ಆಳಿದನು.",
			"11": "<sup>11</sup> ಇವನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಪಾಪಕ್ಕೆ ಪ್ರೇರಿಸಿದ ನೆಬಾಟನ ಮಗನಾದ ಯಾರೊಬ್ಬಾಮನ ಮಾರ್ಗವನ್ನು ಬಿಡದೆ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾದನು.",
			"12": "<sup>12</sup> ಇವನ ಉಳಿದ ಚರಿತ್ರೆಯೂ ಎಲ್ಲಾ ಶೂರ ಕೃತ್ಯಗಳೂ ಇವನು ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಅಮಚ್ಯನು ಎಂಬವನೊಡನೆ ನಡಿಸಿದ ಯುದ್ಧದ ವಿವರವೂ ಇಸ್ರಾಯೇಲ್‍ರಾಜಕಾಲವೃತ್ತಾಂತ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"13": "<sup>13</sup> ಇವನು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಲು ಇವನ ಶವವನ್ನು ಸಮಾರ್ಯದೊಳಗೆ ಇಸ್ರಾಯೇಲ್ ರಾಜಶ್ಮಶಾನದಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಇವನ ತರುವಾಯ ಯಾರೊಬ್ಬಾಮನು ಸಿಂಹಾಸನವನ್ನೇರಿದನು.",
			"14": "<sup>14</sup> ಎಲೀಷನು ಮರಣಕರ ರೋಗದಲ್ಲಿ ಬಿದ್ದನು. ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾದ ಯೋವಾಷನು ಇದನ್ನು ಕೇಳಿ ಅವನ ಬಳಿಗೆ ಹೋಗಿ - ನನ್ನ ತಂದೆಯೇ, ನನ್ನ ತಂದೆಯೇ, ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ರಥರಥಾಶ್ವಬಲಗಳಾಗಿದ್ದವನೇ ಎಂದು ಕೂಗುತ್ತಾ ಅವನ ಮುಖದ ಮೇಲೆ ಬಿದ್ದು ಬಹಳವಾಗಿ ಅತ್ತನು.",
			"15": "<sup>15</sup> ಆಗ ಎಲೀಷನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಿಗೆ - ಬಿಲ್ಲನ್ನೂ ಬಾಣಗಳನ್ನೂ ತರಿಸು ಎಂದು ಹೇಳಿದನು; ಅವನು ತರಿಸಿದನು.",
			"16": "<sup>16</sup> ಆಗ ಅವನಿಗೆ - ಬಿಲ್ಲನ್ನು ಹಿಡಿದುಕೋ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು. ಅವನು ಹಿಡಿದುಕೊಳ್ಳಲು ತನ್ನ ಕೈಯನ್ನು ಅವನ ಕೈ ಮೇಲೆ ಇಟ್ಟು -",
			"17": "<sup>17</sup> ಮೂಡಣ ದಿಕ್ಕಿಗಿರುವ ಕಿಟಕಿಯನ್ನು ತೆರೆ ಎಂದನು. ಅವನು ತೆರೆದನು. ಅನಂತರ - ಬಾಣವನ್ನೆಸೆ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು. ಅವನು ಎಸೆಯಲು - ಇದು ಜಯಪ್ರದವಾದ ಯೆಹೋವನ ಬಾಣ; ಅರಾಮ್ಯರನ್ನು ಜಯಿಸುವ ಬಾಣ. ನೀನು ಅರಾಮ್ಯರನ್ನು ಅಫೇಕದಲ್ಲಿ ಸೋಲಿಸಿ ಸಂಹರಿಸಿಬಿಡುವಿ ಎಂದು ಹೇಳಿದನು.",
			"18": "<sup>18</sup> ಇದಲ್ಲದೆ ಎಲೀಷನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಿಗೆ - ಬಾಣಗಳನ್ನು ತೆಗೆದುಕೋ ಎಂದು ಆಜ್ಞಾಪಿಸಿದಾಗ ಅವನು ತೆಗೆದುಕೊಳ್ಳಲು - ಇವುಗಳಿಂದ ನೆಲವನ್ನು ಹೊಡಿ ಅಂದನು. ಅವನು ಮೂರು ಸಾರಿ ಹೊಡೆದು ಸುಮ್ಮನೆ ನಿಂತನು.",
			"19": "<sup>19</sup> ಆಗ ದೇವರ ಮನುಷ್ಯನು ಅವನ ಮೇಲೆ ಕೋಪಗೊಂಡು - ನೀನು ಐದಾರು ಸಾರಿ ಹೊಡೆಯಬೇಕಾಗಿತ್ತು. ಹಾಗೆ ಮಾಡಿದ್ದರೆ ಅರಾಮ್ಯರು ನಿರ್ನಾಮವಾಗಿ ಹೋಗುವವರೆಗೂ ಅವರ ಮೇಲೆ ನಿನಗೆ ಜಯದೊರಕುತ್ತಿತ್ತು. ನೀನು ಮೂರೇ ಸಾರಿ ಹೊಡೆದದರಿಂದ ಅವರನ್ನು ಮೂರು ಸಾರಿ ಮಾತ್ರ ಸೋಲಿಸುವಿ ಎಂದು ಹೇಳಿದನು.",
			"20": "<sup>20</sup> ಎಲೀಷನು ಮೃತಿಹೊಂದಲು ಅವನ ಶವವನ್ನು ಸಮಾಧಿಮಾಡಿದರು. ಮೋವಾಬ್ಯರು ಪ್ರತಿವರುಷದ ಪ್ರಾರಂಭದಲ್ಲಿ ಸುಲಿಗೆ ಮಾಡುವದಕ್ಕೋಸ್ಕರ ಗುಂಪು ಗುಂಪಾಗಿ ಬರುತ್ತಿದ್ದರು.",
			"21": "<sup>21</sup> ಒಂದು ದಿವಸ ಜನರು ಒಬ್ಬ ಸತ್ತವನನ್ನು ಸಮಾಧಿಮಾಡುವದಕ್ಕೆ ಹೋದಾಗ ಮೋವಾಬ್ಯರ ಗುಂಪು ಬರುತ್ತಿರುವದನ್ನು ಕಂಡು ಶವವನ್ನು ಎಲೀಷನ ಸಮಾಧಿಯಲ್ಲಿ ಬಿಸಾಡಿ ಓಡಿ ಹೋದರು. ಸತ್ತ ಮನುಷ್ಯನು ಎಲೀಷನ ಎಲುಬುಗಳಿಗೆ ತಗುಲಿದ ಕೂಡಲೆ ಉಜ್ಜೀವಿಸಿ ಎದ್ದು ನಿಂತನು.",
			"22": "<sup>22</sup> ಅರಾಮ್ಯರ ಅರಸನಾದ ಹಜಾಯೇಲನು ಯೆಹೋವಾಹಾಜನ ಆಳಿಕೆಯಲ್ಲೆಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಪೀಡಿಸುತ್ತಿದ್ದನು.",
			"23": "<sup>23</sup> ಆದರೂ ಯೆಹೋವನು ಅವರನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ಹಾಳುಮಾಡಗೊಡಲಿಲ್ಲ. ಅವರನ್ನು ಈ ಕಾಲದಲ್ಲಿಯೂ ತನ್ನ ಸನ್ನಿಧಿಯಿಂದ ತಳ್ಳಿಬಿಡಲಿಲ್ಲ. ತಾನು ಅಬ್ರಹಾಮ್ ಇಸಾಕ್ ಯಾಕೋಬರೊಡನೆ ಮಾಡಿಕೊಂಡ ಒಡಂಬಡಿಕೆಯನ್ನು ನೆನಸಿ ಅವರಿಗೆ ದಯೆತೋರಿಸಿದನು; ಕರುಳು ಮರುಗಿದವನಾಗಿ ಅವರಿಗೆ ಪ್ರಸನ್ನನಾದನು.",
			"24": "<sup>24</sup> ಅರಾಮ್ಯರ ಅರಸನಾದ ಹಜಾಯೇಲನು ಮರಣಹೊಂದಲು ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ಬೆನ್ಹದದನು ಅರಸನಾದನು.",
			"25": "<sup>25</sup> ಯೆಹೋವಾಹಾಜನ ಮಗನಾದ ಯೋವಾಷನು ತನ್ನ ತಂದೆಯ ಕಾಲದಲ್ಲಿ ಹಜಾಯೇಲನು ಯುದ್ಧಮಾಡಿ ಕಿತ್ತುಕೊಂಡಿದ್ದ ಪಟ್ಟಣಗಳನ್ನು ಬೆನ್ಹದದನಿಂದ ತಿರಿಗಿ ತೆಗೆದುಕೊಂಡನು. ಅವನನ್ನು ಮೂರು ಸಾರಿ ಸೋಲಿಸಿ ಇಸ್ರಾಯೇಲ್ಯರ ಎಲ್ಲಾ ಪಟ್ಟಣಗಳನ್ನು ಹಿಂದಕ್ಕೆ ತೆಗೆದುಕೊಂಡನು."
		},
		"14": {
			"1": "<sup>1</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾದ ಯೆಹೋವಾಹಾಜನ ಮಗ ಯೋವಾಷನ ಆಳಿಕೆಯ ಎರಡನೆಯ ವರುಷದಲ್ಲಿ ಯೆಹೂದದ ಅರಸನಾದ ಯೆಹೋವಾಷನ ಮಗ ಅಮಚ್ಯ ಎಂಬವನು ಅರಸನಾದನು.",
			"2": "<sup>2</sup> ಇವನು ಪಟ್ಟಕ್ಕೆ ಬಂದಾಗ ಇಪ್ಪತ್ತೈದು ವರುಷದವನಾಗಿದ್ದು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಇಪ್ಪತ್ತೊಂಭತ್ತು ವರುಷ ಆಳಿದನು. ಯೆರೂಸಲೇಮಿನವಳಾದ ಯೆಹೋವದ್ದೀನ್ ಎಂಬಾಕೆಯು ಇವನ ತಾಯಿ.",
			"3": "<sup>3</sup> ಇವನು ತನ್ನ ತಂದೆಯಾದ ಯೆಹೋವಾಷನ ಮಾರ್ಗದಲ್ಲಿ ತಪ್ಪದೆ ನಡೆದು ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ಒಳ್ಳೆಯವನಾದನು; ಆದರೂ ತನ್ನ ಪೂರ್ವಿಕನಾದ ದಾವೀದನಷ್ಟು ಉತ್ತಮನಾಗಿರಲಿಲ್ಲ.",
			"4": "<sup>4</sup> ಇವನ ಕಾಲದಲ್ಲಿ ಪೂಜಾಸ್ಥಳಗಳು ಇನ್ನೂ ತೆಗೆಯಲ್ಪಡಲಿಲ್ಲವಾದದರಿಂದ ಜನರು ಅವುಗಳಲ್ಲಿ ಯಜ್ಞಧೂಪಗಳನ್ನು ಅರ್ಪಿಸುತ್ತಿದ್ದರು.",
			"5": "<sup>5</sup> ಇವನು ರಾಜ್ಯವನ್ನು ಭದ್ರಪಡಿಸಿಕೊಂಡ ಕೂಡಲೆ ತನ್ನ ತಂದೆಯನ್ನು ಕೊಂದ ಉದ್ಯೋಗಸ್ಥರಿಗೆ ಮರಣ ವಿಧಿಸಿದನು.",
			"6": "<sup>6</sup> ಆದರೆ ಮಕ್ಕಳ ಪಾಪದ ದೆಸೆಯಿಂದ ತಂದೆಗೂ ತಂದೆಯ ಪಾಪದ ದೆಸೆಯಿಂದ ಮಕ್ಕಳಿಗೂ ಮರಣಶಿಕ್ಷೆಯಾಗಬಾರದು; ಪ್ರತಿಯೊಬ್ಬನೂ ತನ್ನ ಪಾಪಫಲವನ್ನು ತಾನೇ ಅನುಭವಿಸಬೇಕು ಎಂಬದಾಗಿ ಮೋಶೆಯ ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿರುವ ಯೆಹೋವನ ಅಪ್ಪಣೆಯನ್ನು ನೆನಪುಮಾಡಿಕೊಂಡು ಅವರ ಮಕ್ಕಳನ್ನು ಕೊಲ್ಲಿಸಲಿಲ್ಲ.",
			"7": "<sup>7</sup> ಇದಲ್ಲದೆ ಉಪ್ಪಿನತಗ್ಗಿನಲ್ಲಿ ಎದೋಮ್ಯರ ಹತ್ತು ಸಾವಿರ ಮಂದಿ ಸೈನಿಕರನ್ನು ಹೊಡೆದು ಅವರಿಂದ ಸೆಲ ದುರ್ಗವನ್ನು ಕಿತ್ತುಕೊಂಡು ಅದಕ್ಕೆ ಯೊಕ್ತೆಯೇಲ್ ಎಂಬ ಹೆಸರಿಟ್ಟನು. ಅದಕ್ಕೆ ಇಂದಿನವರೆಗೂ ಇದೇ ಹೆಸರಿರುತ್ತದೆ.",
			"8": "<sup>8</sup> ಅನಂತರ ಅಮಚ್ಯನು ಯೇಹುವಿನ ಮೊಮ್ಮಗನೂ ಯೆಹೋವಾಹಾಜನ ಮಗನೂ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನೂ ಆದ ಯೋವಾಷನ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿ ಅವನಿಗೆ - ನಾವು ಒಬ್ಬರನ್ನೊಬ್ಬರು ಎದುರುಗೊಳ್ಳೋಣ ಬಾ ಎಂದು ಹೇಳಿಸಿದನು.",
			"9": "<sup>9</sup> ಆಗ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾದ ಯೋವಾಷನು ಯೆಹೂದದ ಅರಸನಾದ ಅಮಚ್ಯನಿಗೆ - ಲೆಬನೋನಿನ ಮುಳ್ಳುಗಿಡವು ಅಲ್ಲಿನ ದೇವದಾರುಮರಕ್ಕೆ - ನನ್ನ ಮಗನಿಗೆ ನಿನ್ನ ಮಗಳನ್ನು ಕೊಡು ಎಂದು ಹೇಳಿಕಳುಹಿಸಿತು. ತುಸುಹೊತ್ತಾದ ಮೇಲೆ ಲೆಬನೋನಿನ ಒಂದು ಕಾಡು ಮೃಗವು ಅಲ್ಲಿಂದ ಹಾದುಹೋಗುತ್ತಿರುವಾಗ ಅದನ್ನು ತುಳಿದು ಬಿಟ್ಟಿತು.",
			"10": "<sup>10</sup> ನೀನು ಎದೋಮ್ಯರನ್ನು ಸೋಲಿಸಿದದರಿಂದ ಬಹಳವಾಗಿ ಉಬ್ಬಿಕೊಂಡಿದ್ದೀ; ಆ ಕೀರ್ತಿಯು ಸಾಕೆಂದು ನೆನಸಿ ಸುಮ್ಮನೆ ಮನೆಯಲ್ಲಿ ಕೂತುಕೋ. ನನ್ನನ್ನು ಕೆಣಕಿ ನಿನ್ನ ಮೇಲೆಯೂ ನಿನ್ನ ರಾಜ್ಯದ ಮೇಲೆಯೂ ಯಾಕೆ ಕೇಡನ್ನು ಬರಮಾಡಿಕೊಳ್ಳುತ್ತೀ ಎಂದು ಉತ್ತರಕೊಟ್ಟು ಕಳುಹಿಸಿದನು.",
			"11": "<sup>11</sup> ಅಮಚ್ಯನು ಇದನ್ನು ಲಕ್ಷಿಸಲಿಲ್ಲವಾದದರಿಂದ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾದ ಯೋವಾಷನು ಯುದ್ಧಕ್ಕೆ ಹೊರಟನು; ಅವರು ಯೆಹೂದಪ್ರಾಂತದ ಬೇತ್ಷೆಮೆಷಿನಲ್ಲಿ ಒಬ್ಬರನ್ನೊಬ್ಬರು ಎದುರುಗೊಂಡರು.",
			"12": "<sup>12</sup> ಯೆಹೂದ್ಯರು ಇಸ್ರಾಯೇಲ್ಯರಿಂದ ಸೋಲಿಸಲ್ಪಟ್ಟು ತಮ್ಮ ತಮ್ಮ ನಿವಾಸಗಳಿಗೆ ಓಡಿಹೋದರು.",
			"13": "<sup>13</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾದ ಯೋವಾಷನು ಅಹಜ್ಯನ ಮೊಮ್ಮಗನೂ ಯೆಹೋವಾಷನ ಮಗನೂ ಯೆಹೂದದ ಅರಸನೂ ಆದ ಅಮಚ್ಯನನ್ನು ಬೇತ್ಷೆಮೆಷಿನಲ್ಲಿ ಹಿಡಿದು ಅವನನ್ನು ಯೆರೂಸಲೇಮಿಗೆ ಕರಕೊಂಡುಬಂದು ಎಫ್ರಾಯೀಮ್ ಬಾಗಲಿಗೂ ಮೂಲೆಯ ಬಾಗಲಿಗೂ ಮಧ್ಯದಲ್ಲಿದ್ದ ಯೆರೂಸಲೇಮಿನ ನಾನೂರು ಮೊಳ ಗೋಡೆಯನ್ನು ಕೆಡವಿಬಿಟ್ಟನು.",
			"14": "<sup>14</sup> ಇದಲ್ಲದೆ ಅವನು ಯೆಹೋವನ ಆಲಯದ ಮತ್ತು ಅರಮನೆಯ ಭಂಡಾರಗಳಲ್ಲಿ ಸಿಕ್ಕಿದ ಎಲ್ಲಾ ಬೆಳ್ಳಿ ಬಂಗಾರವನ್ನೂ ಪಾತ್ರೆಗಳನ್ನೂ ತೆಗೆದುಕೊಂಡು ಹೊಣೆಗಾಗಿ ಕೆಲವರನ್ನು ಸೆರೆಹಿಡಿದು ಸಮಾರ್ಯಕ್ಕೆ ಹಿಂದಿರುಗಿದನು.",
			"15": "<sup>15</sup> ಯೋವಾಷನ ಉಳಿದ ಚರಿತ್ರೆಯೂ ಅವನ ಶೂರಕೃತ್ಯಗಳ ಮತ್ತು ಯೆಹೂದ ರಾಜನಾದ ಅಮಚ್ಯನೊಡನೆ ನಡಿಸಿದ ಯುದ್ಧದ ವಿವರವೂ ಇಸ್ರಾಯೇಲ್‍ರಾಜಕಾಲ ವೃತ್ತಾಂತ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"16": "<sup>16</sup> ಅವನು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಲು ಅವನ ಶವವನ್ನು ಸಮಾರ್ಯದೊಳಗೆ ಇಸ್ರಾಯೇಲ್ಯರ ರಾಜಶ್ಮಶಾನಭೂಮಿಯಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ಯಾರೊಬ್ಬಾಮನು ಅರಸನಾದನು.",
			"17": "<sup>17</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನೂ ಯೆಹೋವಾಹಾಜನ ಮಗನೂ ಆದ ಯೋವಾಷನು ಸತ್ತನಂತರ ಯೆಹೂದದ ಅರಸನೂ ಯೆಹೋವಾಷನ ಮಗನೂ ಆದ ಅಮಚ್ಯನು ಇನ್ನೂ ಹದಿನೈದು ವರುಷ ಬದುಕಿದನು.",
			"18": "<sup>18</sup> ಅವನ ಉಳಿದ ಚರಿತ್ರೆಯು ಯೆಹೂದರಾಜಕಾಲವೃತ್ತಾಂತ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತದೆ.",
			"19": "<sup>19</sup> ಯೆರೂಸಲೇಮಿನವರು ಅವನಿಗೆ ವಿರೋಧವಾಗಿ ಒಳಸಂಚು ಮಾಡಿದ್ದರಿಂದ ಅವನು ಲಾಕೀಷಿಗೆ ಓಡಿಹೋದನು; ಆದರೆ ಅವರು ಅಲ್ಲಿಗೆ ಆಳುಗಳನ್ನು ಕಳುಹಿಸಿ ಅವನನ್ನು ಕೊಲ್ಲಿಸಿದರು.",
			"20": "<sup>20</sup> ಅವನ ಜನರು ಅವನ ಶವವನ್ನು ಕುದುರೆಗಳಿಂದ ಯೆರೂಸಲೇಮಿಗೆ ತಂದು ದಾವೀದನಗರದೊಳಗೆ ಅವನ ಕುಟುಂಬ ಶ್ಮಶಾನಭೂಮಿಯಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು.",
			"21": "<sup>21</sup> ಅನಂತರ ಎಲ್ಲಾ ಯೆಹೂದ್ಯರು ಕೂಡಿ ಹದಿನಾರು ವರುಷದವನಾದ ಅಜರ್ಯನನ್ನು ಅವನ ತಂದೆಯಾದ ಅಮಚ್ಯನಿಗೆ ಬದಲಾಗಿ ಅರಸನನ್ನಾಗಿ ಮಾಡಿದರು.",
			"22": "<sup>22</sup> ಅರಸನು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಿದ ಮೇಲೆ ಇವನು ಏಲತ್ ಎಂಬ ಪಟ್ಟಣವನ್ನು ತಿರಿಗಿ ಯೆಹೂದರಾಜ್ಯಕ್ಕೆ ಸೇರಿಸಿ ಭದ್ರಪಡಿಸಿದನು.",
			"23": "<sup>23</sup> ಯೆಹೂದದ ಅರಸನೂ ಯೆಹೋವಾಷನ ಮಗನೂ ಆದ ಅಮಚ್ಯನ ಆಳಿಕೆಯ ಹದಿನೈದನೆಯ ವರುಷದಲ್ಲಿ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾದ ಯೋವಾಷನ ಮಗ ಯಾರೊಬ್ಬಾಮನು ಅರಸನಾಗಿ ಸಮಾರ್ಯದಲ್ಲಿ ನಾಲ್ವತ್ತೊಂದು ವರುಷ ಆಳಿದನು.",
			"24": "<sup>24</sup> ಇವನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಪಾಪಕ್ಕೆ ಪ್ರೇರಿಸಿದ ನೆಬಾಟನ ಮಗನಾದ ಯಾರೊಬ್ಬಾಮನ ಮಾರ್ಗವನ್ನು ಬಿಡದೆ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾದನು.",
			"25": "<sup>25</sup> ಇಸ್ರಾಯೇಲ್‍ದೇವರಾದ ಯೆಹೋವನು ತನ್ನ ಸೇವಕನೂ ಗತ್‍ಹೇಫೆರಿನವನಾದ ಅಮಿತ್ತೈಯ ಮಗನೂ ಆದ ಯೋನಾ ಎಂಬ ಪ್ರವಾದಿಯ ಮುಖಾಂತರವಾಗಿ ಮುಂತಿಳಿಸಿದಂತೆ ಹಮಾತಿನ ದಾರಿಯಿಂದ ಅರಾಬಾ ತಗ್ಗಿನ ಸಮುದ್ರದವರೆಗಿದ್ದ ಇಸ್ರಾಯೇಲ್ಯರ ಮೇರೆಯನ್ನು ತಿರಿಗಿ ತೆಗೆದುಕೊಂಡವನು ಇವನೇ.",
			"26": "<sup>26</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಘೋರಕಷ್ಟವನ್ನು ಅನುಭವಿಸುತ್ತಿರುತ್ತಾರೆ, ಅವರಲ್ಲಿದ್ದ ಸ್ವತಂತ್ರರೂ ಪರತಂತ್ರರೂ ನಾಶವಾಗಿದ್ದಾರೆ, ಅವರನ್ನು ರಕ್ಷಿಸುವವನು ಒಬ್ಬನೂ ಇಲ್ಲ ಎಂಬದನ್ನು ಯೆಹೋವನು ನೋಡಿ",
			"27": "<sup>27</sup> ಅವರನ್ನು ಭೂಲೋಕದಿಂದ ನಿರ್ನಾಮ ಮಾಡುವದಕ್ಕೆ ಮನಸ್ಸಿಲ್ಲದೆ ಯೋವಾಷನ ಮಗನಾದ ಯಾರೊಬ್ಬಾಮನ ಮುಖಾಂತರವಾಗಿ ರಕ್ಷಿಸಿದನು.",
			"28": "<sup>28</sup> ಯಾರೊಬ್ಬಾಮನ ಉಳಿದ ಚರಿತ್ರೆಯೂ ಅವನು ಯುದ್ಧದಲ್ಲಿ ನಡಿಸಿದ ಶೂರಕೃತ್ಯಗಳೂ ಮೊದಲು ಯೆಹೂದ್ಯರ ಸ್ವಾಧೀನದಲ್ಲಿದ್ದ ದಮಸ್ಕ ಹಮಾತ್ ಎಂಬ ಪಟ್ಟಣಗಳು ಅವನಿಂದ ಇಸ್ರಾಯೇಲ್ ರಾಜ್ಯಕ್ಕೆ ಸೇರಿಸಲ್ಪಟ್ಟ ವಿವರವೂ ಇಸ್ರಾಯೇಲ್‍ರಾಜಕಾಲವೃತ್ತಾಂತ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"29": "<sup>29</sup> ಅವನು ಇಸ್ರಾಯೇಲ್‍ರಾಜರಾಗಿದ್ದ ತನ್ನ ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಲು ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ಜೆಕರ್ಯನು ಅರಸನಾದನು."
		},
		"15": {
			"1": "<sup>1</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾದ ಯಾರೊಬ್ಬಾಮನ ಆಳಿಕೆಯ ಇಪ್ಪತ್ತೇಳನೆಯ ವರುಷದಲ್ಲಿ ಅಮಚ್ಯನ ಮಗನಾದ ಅಜರ್ಯನು ಯೆಹೂದದ ಅರಸನಾದನು.",
			"2": "<sup>2</sup> ಇವನು ಪಟ್ಟಕ್ಕೆ ಬಂದಾಗ ಹದಿನಾರು ವರುಷದವನಾಗಿದ್ದು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಐವತ್ತೆರಡು ವರುಷ ಆಳಿದನು. ಯೆರೂಸಲೇಮಿನವಳಾದ ಯೆಕೊಲ್ಯ ಎಂಬಾಕೆಯು ಇವನ ತಾಯಿ.",
			"3": "<sup>3</sup> ಇವನು ತನ್ನ ತಂದೆಯಾದ ಅಮಚ್ಯನಂತೆ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ಒಳ್ಳೆಯವನಾಗಿ ನಡೆದನು.",
			"4": "<sup>4</sup> ಆದರೆ ಇವನ ಕಾಲದಲ್ಲಿಯೂ ಪೂಜಾಸ್ಥಳಗಳು ತೆಗೆಯಲ್ಪಡಲಿಲ್ಲವಾದದರಿಂದ ಜನರು ಅವುಗಳಲ್ಲಿ ಯಜ್ಞಧೂಪಗಳನ್ನು ಅರ್ಪಿಸುತ್ತಿದ್ದರು.",
			"5": "<sup>5</sup> ಯೆಹೋವನು ಇವನನ್ನು ಕುಷ್ಠ ರೋಗದಿಂದ ಬಾಧಿಸಿದನು. ಆದದರಿಂದ ಇವನು ಜೀವದಿಂದಿರುವವರೆಗೂ ಪ್ರತ್ಯೇಕವಾದ ಮನೆಯಲ್ಲಿ ವಾಸಿಸಬೇಕಾಯಿತು; ರಾಜಗೃಹಾಧಿಪತ್ಯವನ್ನೂ ಪ್ರಜಾಪಾಲನೆಯನ್ನೂ ಇವನ ಮಗನಾದ ಯೋತಾಮನು ನೋಡಿಕೊಳ್ಳುತ್ತಿದ್ದನು.",
			"6": "<sup>6</sup> ಅಜರ್ಯನ ಉಳಿದ ಚರಿತ್ರೆಯೂ ಅವನ ಎಲ್ಲಾ ಕೃತ್ಯಗಳೂ ಯೆಹೂದರಾಜಕಾಲವೃತ್ತಾಂತ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"7": "<sup>7</sup> ಅವನು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಲು ಅವನ ಶವವನ್ನು ದಾವೀದನಗರದೊಳಗೆ ರಾಜಕುಟುಂಬದ ಶ್ಮಶಾನಭೂಮಿಯಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ಯೋತಾಮನು ಅರಸನಾದನು.",
			"8": "<sup>8</sup> ಯೆಹೂದದ ಅರಸನಾದ ಅಜರ್ಯನ ಆಳಿಕೆಯ ಮೂವತ್ತೆಂಟನೆಯ ವರುಷದಲ್ಲಿ ಯಾರೊಬ್ಬಾಮನ ಮಗನಾದ ಜೆಕರ್ಯ ಎಂಬವನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾಗಿ ಸಮಾರ್ಯದಲ್ಲಿ ಆರು ತಿಂಗಳು ಆಳಿದನು.",
			"9": "<sup>9</sup> ಅವನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಪಾಪಕ್ಕೆ ಪ್ರೇರಿಸಿದ ನೆಬಾಟನ ಮಗನಾದ ಯಾರೊಬ್ಬಾಮನ ಮಾರ್ಗವನ್ನು ಬಿಡದೆ ತನ್ನ ಪೂರ್ವಿಕರಂತೆ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾದನು.",
			"10": "<sup>10</sup> ಯಾಬೇಷನ ಮಗನಾದ ಶಲ್ಲೂಮನು ಅವನಿಗೆ ವಿರೋಧವಾಗಿ ಒಳಸಂಚು ಮಾಡಿ ಪ್ರಜೆಗಳ ಮುಂದೆಯೇ ಅವನನ್ನು ಕೊಂದು ಅವನಿಗೆ ಬದಲಾಗಿ ತಾನು ಅರಸನಾದನು.",
			"11": "<sup>11</sup> ಜೆಕರ್ಯನ ಉಳಿದ ಚರಿತ್ರೆಯು ಇಸ್ರಾಯೇಲ್‍ರಾಜಕಾಲವೃತ್ತಾಂತ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತದೆ.",
			"12": "<sup>12</sup> ನಿನ್ನ ಸಂತಾನದವರು ನಾಲ್ಕನೆಯ ತಲೆಯವರೆಗೂ ಇಸ್ರಾಯೇಲ್‍ಸಿಂಹಾಸನದ ಮೇಲೆ ಕೂತುಕೊಳ್ಳುವರು ಎಂಬದಾಗಿ ಯೆಹೋವನು ಯೇಹುವಿಗೆ ಮುಂತಿಳಿಸಿದ ಮಾತು ನೆರವೇರಿತು.",
			"13": "<sup>13</sup> ಯೆಹೂದದ ಅರಸನಾದ ಉಜ್ಜೀಯನ ಆಳಿಕೆಯ ಮೂವತ್ತೊಂಭತ್ತನೆಯ ವರುಷದಲ್ಲಿ ಯಾಬೇಷನ ಮಗನಾದ ಶಲ್ಲೂಮನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾಗಿ ಸಮಾರ್ಯದಲ್ಲಿ ಒಂದು ತಿಂಗಳು ಆಳಿದನು.",
			"14": "<sup>14</sup> ಗಾದಿಯ ಮಗನಾದ ಮೆನಹೇಮನೆಂಬವನು ತಿರ್ಚದಿಂದ ಸಮಾರ್ಯಕ್ಕೆ ಬಂದು ಅಲ್ಲಿದ್ದ ಯಾಬೇಷನ ಮಗನಾದ ಶಲ್ಲೂಮನನ್ನು ಕೊಂದು ಅವನಿಗೆ ಬದಲಾಗಿ ತಾನು ಅರಸನಾದನು.",
			"15": "<sup>15</sup> ಶಲ್ಲೂಮನ ಉಳಿದ ಚರಿತ್ರೆಯೂ ಅವನ ಒಳಸಂಚಿನ ವಿವರವೂ ಇಸ್ರಾಯೇಲ್‍ರಾಜಕಾಲವೃತ್ತಾಂತ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"16": "<sup>16</sup> ಮೆನಹೇಮನು ತಿರ್ಚದಿಂದ ತಿಪ್ಸಹುವಿಗೆ ಹೋದಾಗ ಅಲ್ಲಿಯವರು ತಮ್ಮ ಊರುಬಾಗಲನ್ನು ಅವನಿಗೆ ತೆರೆದುಕೊಡಲಿಲ್ಲವಾದದರಿಂದ ಅವನು ಅದನ್ನೂ ಅದಕ್ಕೆ ಸೇರಿದ ಪ್ರದೇಶವನ್ನೂ ಹಾಳುಮಾಡಿ ಅದರ ನಿವಾಸಿಗಳನ್ನು ಸಂಹರಿಸಿದನು; ಅದರಲ್ಲಿದ್ದ ಎಲ್ಲಾ ಗರ್ಭಿಣಿಗಳ ಹೊಟ್ಟೆಸೀಳಿಸಿದನು.",
			"17": "<sup>17</sup> ಯೆಹೂದದ ಅರಸನಾದ ಅಜರ್ಯನ ಆಳಿಕೆಯ ಮೂವತ್ತೊಂಭತ್ತನೆಯ ವರುಷದಲ್ಲಿ ಗಾದಿಯ ಮಗನಾದ ಮೆನಹೇಮನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾಗಿ ಸಮಾರ್ಯದಲ್ಲಿ ಹತ್ತು ವರುಷ ಆಳಿದನು.",
			"18": "<sup>18</sup> ಇವನು ಜೀವದಿಂದಿರುವವರೆಗೂ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಪಾಪಕ್ಕೆ ಪ್ರೇರಿಸಿದ ನೆಬಾಟನ ಮಗನಾದ ಯಾರೊಬ್ಬಾಮನ ಮಾರ್ಗವನ್ನು ಬಿಡದೆ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾದನು.",
			"19": "<sup>19</sup> ಅಶ್ಶೂರ್‍ದೇಶದ ಅರಸನಾದ ಪೂಲನೆಂಬವನು ಇಸ್ರಾಯೇಲ್‍ದೇಶಕ್ಕೆ ವಿರೋಧವಾಗಿ ಬಂದಾಗ ಮೆನಹೇಮನು ಅವನ ಮುಖಾಂತರವಾಗಿ ತನ್ನ ಅರಸುತನವನ್ನು ದೃಢಪಡಿಸಿಕೊಳ್ಳುವದಕ್ಕೋಸ್ಕರ ಅವನಿಗೆ ಸಾವಿರ ತಲಾಂತು ಬೆಳ್ಳಿಯನ್ನು ಕೊಟ್ಟನು.",
			"20": "<sup>20</sup> ಅಶ್ಶೂರದ ಅರಸನಿಗೆ ಆ ಹಣವನ್ನು ಕೊಡುವದಕ್ಕಾಗಿ ಇಸ್ರಾಯೇಲ್ಯರ ಪ್ರತಿಯೊಬ್ಬ ಐಶ್ವರ್ಯವಂತನಿಂದ ಐವತ್ತು ರೂಪಾಯಿಗಳನ್ನು ತೆಗೆದುಕೊಂಡನು. ಆ ಹಣವು ಸಿಕ್ಕಿದನಂತರ ಅಶ್ಶೂರದ ಅರಸನು ಅಲ್ಲಿ ನಿಲ್ಲದೆ ಹಿಂದಿರುಗಿ ಹೋದನು.",
			"21": "<sup>21</sup> ಮೆನಹೇಮನ ಉಳಿದ ಚರಿತ್ರೆಯೂ ಅವನ ಎಲ್ಲಾ ಕೃತ್ಯಗಳೂ ಇಸ್ರಾಯೇಲ್‍ರಾಜಕಾಲವೃತ್ತಾಂತ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"22": "<sup>22</sup> ಅವನು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಲು ಅವನ ಮಗನಾದ ಪೆಕಹ್ಯನು ಅರಸನಾದನು.",
			"23": "<sup>23</sup> ಯೆಹೂದದ ಅರಸನಾದ ಅಜರ್ಯನ ಆಳಿಕೆಯ ಐವತ್ತನೆಯ ವರುಷದಲ್ಲಿ ಮೆನಹೇಮನ ಮಗನಾದ ಪೆಕಹ್ಯ ಎಂಬವನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾಗಿ ಸಮಾರ್ಯದಲ್ಲಿ ಎರಡು ವರುಷ ಆಳಿದನು.",
			"24": "<sup>24</sup> ಇವನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಪಾಪಕ್ಕೆ ಪ್ರೇರಿಸಿದ ನೆಬಾಟನ ಮಗನಾದ ಯಾರೊಬ್ಬಾಮನ ಮಾರ್ಗವನ್ನು ಬಿಡದೆ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾದನು.",
			"25": "<sup>25</sup> ಇವನ ಸರದಾರನೂ ರೆಮಲ್ಯನ ಮಗನೂ ಆಗಿದ್ದ ಪೆಕಹನು ಗಿಲ್ಯಾದಿನ ಐವತ್ತು ಮಂದಿ ಜನರನ್ನು ಕೂಡಿಸಿಕೊಂಡು ಇವನಿಗೆ ವಿರೋಧವಾಗಿ ಒಳಸಂಚುಮಾಡಿ ಸಮಾರ್ಯದಲ್ಲಿರುವ ಅರಮನೆಯ ಗರ್ಭಗೃಹದಲ್ಲಿ ಇವನನ್ನೂ ಅರ್ಗೋಬ್, ಅರ್ಯೇ ಎಂಬವರನ್ನೂ ಕೊಂದು ಇವನಿಗೆ ಬದಲಾಗಿ ತಾನು ಅರಸನಾದನು.",
			"26": "<sup>26</sup> ಪೆಕಹ್ಯನ ಉಳಿದ ಚರಿತ್ರೆಯೂ ಅವನ ಎಲ್ಲಾ ಕೃತ್ಯಗಳೂ ಇಸ್ರಾಯೇಲ್‍ರಾಜಕಾಲವೃತ್ತಾಂತ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"27": "<sup>27</sup> ಯೆಹೂದದ ಅರಸನಾದ ಅಜರ್ಯನ ಆಳಿಕೆಯ ಐವತ್ತೆರಡನೆಯ ವರುಷದಲ್ಲಿ ರೆಮಲ್ಯನ ಮಗನಾದ ಪೆಕಹನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾಗಿ ಸಮಾರ್ಯದಲ್ಲಿ ಇಪ್ಪತ್ತು ವರುಷ ಆಳಿದನು.",
			"28": "<sup>28</sup> ಇವನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಪಾಪಕ್ಕೆ ಪ್ರೇರಿಸಿದ ನೆಬಾಟನ ಮಗನಾದ ಯಾರೊಬ್ಬಾಮನ ಮಾರ್ಗವನ್ನು ಬಿಡದೆ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾದನು.",
			"29": "<sup>29</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾದ ಪೆಕಹನ ಕಾಲದಲ್ಲಿ ಅಶ್ಶೂರ್ ದೇಶದ ಅರಸನಾದ ತಿಗ್ಲತ್ಪಿಲೆಸೆರನೆಂಬವನು ಬಂದು ಇಯ್ಯೋನ್, ಆಬೇಲ್ಬೇತ್ಮಾಕಾ, ಯಾನೋಹ, ಕೆದೆಷ್, ಹಾಚೋರ್ ಮೊದಲಾದ ನಫ್ತಾಲಿಯ ಊರುಗಳನ್ನೂ ಗಿಲ್ಯಾದ್, ಗಲಿಲಾಯ ಪ್ರಾಂತಗಳನ್ನೂ ಸ್ವಾಧೀನಪಡಿಸಿಕೊಂಡು ಅವುಗಳ ನಿವಾಸಿಗಳನ್ನು ಅಶ್ಶೂರ್ ದೇಶಕ್ಕೆ ಸೆರೆಯಾಗಿ ತೆಗೆದುಕೊಂಡುಹೋದನು.",
			"30": "<sup>30</sup> ಆಗ ಏಲನ ಮಗನಾದ ಹೋಶೇಯನು ರೆಮಲ್ಯನ ಮಗನಾದ ಪೆಕಹನಿಗೆ ವಿರೋಧವಾಗಿ ಒಳಸಂಚುಮಾಡಿ ಇವನನ್ನು ಉಜ್ಜೀಯನ ಮಗನಾದ ಯೋತಾಮನ ಆಳಿಕೆಯ ಇಪ್ಪತ್ತನೆಯ ವರುಷದಲ್ಲಿ ಕೊಂದು ಇವನಿಗೆ ಬದಲಾಗಿ ತಾನು ಅರಸನಾದನು.",
			"31": "<sup>31</sup> ಪೆಕಹನ ಉಳಿದ ಚರಿತ್ರೆಯೂ ಅವನ ಎಲ್ಲಾ ಕೃತ್ಯಗಳೂ ಇಸ್ರಾಯೇಲ್‍ರಾಜಕಾಲವೃತ್ತಾಂತ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"32": "<sup>32</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನೂ ರೆಮಲ್ಯನ ಮಗನೂ ಆದ ಪೆಕಹನ ಆಳಿಕೆಯ ಎರಡನೆಯ ವರುಷದಲ್ಲಿ ಯೆಹೂದ ರಾಜನಾದ ಉಜ್ಜೀಯನ ಮಗ ಯೋತಾಮನು ಆಳತೊಡಗಿದನು.",
			"33": "<sup>33</sup> ಇವನು ಪಟ್ಟಕ್ಕೆ ಬಂದಾಗ ಇಪ್ಪತ್ತೈದು ವರುಷದವನಾಗಿದ್ದನು. ಇವನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಹದಿನಾರು ವರುಷ ಆಳಿದನು. ಚಾದೋಕನ ಮಗಳಾದ ಯೆರೂಷಾ ಇವನ ತಾಯಿ.",
			"34": "<sup>34</sup> ಇವನು ತನ್ನ ತಂದೆಯಾದ ಉಜ್ಜೀಯನಂತೆ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ಒಳ್ಳೆಯವನಾಗಿದ್ದನು;",
			"35": "<sup>35</sup> ಆದರೆ ಇವನ ಕಾಲದಲ್ಲಿ ಪೂಜಾಸ್ಥಳಗಳು ಇನ್ನೂ ತೆಗೆಯಲ್ಪಡಲಿಲ್ಲವಾದದರಿಂದ ಜನರು ಅವುಗಳಲ್ಲಿ ಯಜ್ಞಧೂಪಗಳನ್ನು ಸಮರ್ಪಿಸುತ್ತಿದ್ದರು. ಯೆಹೋವನ ಆಲಯಕ್ಕೆ ಮೇಲಣ ಹೆಬ್ಬಾಗಲನ್ನು ಇಡಿಸಿದವನು ಇವನೇ.",
			"36": "<sup>36</sup> ಯೋತಾಮನ ಉಳಿದ ಚರಿತ್ರೆಯೂ ಅವನ ಎಲ್ಲಾ ಕೃತ್ಯಗಳೂ ಯೆಹೂದರಾಜಕಾಲವೃತ್ತಾಂತ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"37": "<sup>37</sup> ಯೆಹೋವನು ಆ ಕಾಲದಲ್ಲಿ ಅರಾಮ್ಯರ ಅರಸನಾದ ರೆಚೀನನನ್ನೂ ರೆಮಲ್ಯನ ಮಗನಾದ ಪೆಕಹನನ್ನೂ ಯೆಹೂದ್ಯರಿಗೆ ವಿರೋಧವಾಗಿ ಕಳುಹಿಸತೊಡಗಿದನು.",
			"38": "<sup>38</sup> ಯೋತಾಮನು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಲು ಅವನ ಶವವನ್ನು ಅವನ ಪೂರ್ವಿಕನಾದ ದಾವೀದನ ಪಟ್ಟಣದೊಳಗೆ ಕುಟುಂಬಶ್ಮಶಾನದಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ಆಹಾಜನು ಅರಸನಾದನು."
		},
		"16": {
			"1": "<sup>1</sup> ರೆಮಲ್ಯನ ಮಗನಾದ ಪೆಕಹನ ಆಳಿಕೆಯ ಹದಿನೇಳನೆಯ ವರುಷದಲ್ಲಿ ಯೆಹೂದರಾಜನಾದ ಯೋತಾಮನ ಮಗ ಆಹಾಜನು ಆಳತೊಡಗಿದನು.",
			"2": "<sup>2</sup> ಇವನು ಪಟ್ಟಕ್ಕೆ ಬಂದಾಗ ಇಪ್ಪತ್ತು ವರುಷದವನಾಗಿದ್ದು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಹದಿನಾರು ವರುಷ ಆಳಿದನು. ಇವನು ತನ್ನ ದೇವರಾದ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ಒಳ್ಳೆಯವನಾಗಿರಲಿಲ್ಲ. ತನ್ನ ಪೂರ್ವಿಕನಾದ ದಾವೀದನ ಮಾರ್ಗವನ್ನು ಬಿಟ್ಟು ಇಸ್ರಾಯೇಲ್ ರಾಜರ ಮಾರ್ಗದಲ್ಲಿ ನಡೆದನು;",
			"3": "<sup>3</sup> ಇದಲ್ಲದೆ ಯೆಹೋವನು ಇಸ್ರಾಯೇಲ್ಯರ ಎದುರಿನಿಂದ ಓಡಿಸಿಬಿಟ್ಟ ಜನಾಂಗಗಳ ಹೇಸಿಗೆ ಕೆಲಸಗಳನ್ನು ಅನುಸರಿಸಿ ತನ್ನ ಮಗನನ್ನು ಆಹುತಿಕೊಟ್ಟನು.",
			"4": "<sup>4</sup> ಪೂಜಾಸ್ಥಳಗಳಲ್ಲಿಯೂ ದಿನ್ನೆಗಳ ಮೇಲೆಯೂ ಎಲ್ಲಾ ಹಸುರು ಮರಗಳ ಕೆಳಗೆಯೂ ಯಜ್ಞಧೂಪಗಳನ್ನು ಸಮರ್ಪಿಸಿದನು.",
			"5": "<sup>5</sup> ಇವನ ಕಾಲದಲ್ಲಿ ಅರಾಮ್ಯರ ಅರಸನಾದ ರೆಚೀನ್, ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನೂ ರೆಮಲ್ಯನ ಮಗನೂ ಆದ ಪೆಕಹ ಎಂಬವರು ಕೂಡಿಕೊಂಡು ಯೆರೂಸಲೇಮಿಗೆ ವಿರೋಧವಾಗಿ ಬಂದು ಆಹಾಜನನ್ನು ಸುತ್ತಿಕೊಂಡರು. ಆದರೆ ಅವನನ್ನು ಜಯಿಸುವದು ಅವರಿಂದ ಆಗಲಿಲ್ಲ.",
			"6": "<sup>6</sup> ಇದೇ ಕಾಲದಲ್ಲಿ ಅರಾಮ್ಯರ ಅರಸನಾದ ರೆಚೀನನು ಏಲತ್ ಪಟ್ಟಣವನ್ನು ಅರಾಮ್‍ರಾಜ್ಯಕ್ಕೆ ಸೇರಿಸಿಕೊಂಡು ಅದರಲ್ಲಿದ್ದ ಯೆಹೂದ್ಯರನ್ನೆಲ್ಲಾ ಓಡಿಸಿದನು. ಎದೋಮ್ಯರು ಬಂದು ಅಲ್ಲಿ ವಾಸಿಸಿದರು. ಇಂದಿನವರೆಗೂ ಅದರಲ್ಲಿ ಅವರೇ ವಾಸವಾಗಿದ್ದಾರೆ.",
			"7": "<sup>7</sup> ಆಹಾಜನು ಅಶ್ಶೂರ್‍ದೇಶದ ಅರಸನಾದ ತಿಗ್ಲತ್ಪಿಲೆಸೆರನ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿ ಅವನಿಗೆ - ನಾನು ನಿನ್ನ ದಾಸನೂ ಮಗನೂ ಆಗಿದ್ದೇನೆ; ನೀನು ಬಂದು ನನ್ನನ್ನು ನನಗೆ ವಿರೋಧವಾಗಿ ಎದ್ದಿರುವ ಅರಾಮ್ಯರ ಮತ್ತು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸುಗಳ ಕೈಗೆ ಸಿಕ್ಕದಂತೆ ತಪ್ಪಿಸು ಎಂದು ಹೇಳಿಸಿದನು.",
			"8": "<sup>8</sup> ಇದಲ್ಲದೆ ಆಹಾಜನು ಯೆಹೋವನ ಆಲಯದಲ್ಲಿಯೂ ಅರಮನೆಯ ಭಂಡಾರದಲ್ಲಿಯೂ ಇದ್ದ ಬೆಳ್ಳಿಬಂಗಾರವನ್ನು ತೆಗೆದುಕೊಂಡು ಅಶ್ಶೂರದ ಅರಸನಿಗೆ ಕಾಣಿಕೆಯಾಗಿ ಕಳುಹಿಸಿದನು.",
			"9": "<sup>9</sup> ಅವನು ಇವನ ಮಾತಿಗೆ ಒಪ್ಪಿ ದಮಸ್ಕ ಪಟ್ಟಣಕ್ಕೆ ವಿರೋಧವಾಗಿ ಹೋಗಿ ರೆಚೀನನನ್ನು ಕೊಂದು ನಿವಾಸಿಗಳನ್ನು ಸೆರೆಹಿಡಿದು ಕೀರ್ ಪ್ರಾಂತಕ್ಕೆ ಒಯ್ದನು.",
			"10": "<sup>10</sup> ಅರಸನಾದ ಆಹಾಜನು ಅಶ್ಶೂರದ ಅರಸನಾದ ತಿಗ್ಲತ್ಪಿಲೆಸೆರನ ದರ್ಶನಕ್ಕಾಗಿ ದಮಸ್ಕಕ್ಕೆ ಹೋದನು. ಅವನು ಅಲ್ಲಿದ್ದ ಯಜ್ಞವೇದಿಯನ್ನು ಕಂಡು ಅದರ ಒಂದು ಚಿತ್ರವನ್ನೂ ಅದರ ಎಲ್ಲಾ ಅಲಂಕಾರದ ನಕ್ಷೆಯನ್ನೂ ಬರೆಯಿಸಿ ಯಾಜಕನಾದ ಊರೀಯನಿಗೆ ಕಳುಹಿಸಿದನು.",
			"11": "<sup>11</sup> ಅರಸನಾದ ಆಹಾಜನು ದಮಸ್ಕದಿಂದ ಬರುವಷ್ಟರಲ್ಲಿ ಊರೀಯನು ಅರಸನಿಂದ ತನಗೆ ಬಂದ ಮಾದರಿಯ ಪ್ರಕಾರ ಒಂದು ವೇದಿಯನ್ನು ಮಾಡಿಸಿಟ್ಟಿದ್ದನು.",
			"12": "<sup>12</sup> ಅರಸನು ಹಿಂದಿರುಗಿ ಬಂದ ಮೇಲೆ ಆ ಯಜ್ಞವೇದಿಯನ್ನು ಕಂಡು ಅದರ ಸಮೀಪಕ್ಕೆ ಹೋಗಿ",
			"13": "<sup>13</sup> ತಾನು ತಂದ ಸರ್ವಾಂಗಹೋಮದ್ರವ್ಯ, ಧಾನ್ಯನೈವೇದ್ಯ, ಪಾನದ್ರವ್ಯ ಇವುಗಳನ್ನು ಸಮರ್ಪಿಸಿ ಸಮಾಧಾನಯಜ್ಞಕ್ಕಾಗಿ ವಧಿಸಿದ ಪಶುಗಳ ರಕ್ತವನ್ನು ಅದರ ಮೇಲೆ ಚಿಮಿಕಿಸಿದನು.",
			"14": "<sup>14</sup> ಇದಲ್ಲದೆ ಯೆಹೋವನ ಆಲಯಕ್ಕೂ ತನ್ನ ವೇದಿಗೂ ಮಧ್ಯದಲ್ಲಿದ್ದು ಆವರೆಗೂ ಯೆಹೋವನ ಸೇವೆಗೆ ಉಪಯೋಗಿಸಲ್ಪಡುತ್ತಿದ್ದ ತಾಮ್ರವೇದಿಯನ್ನು ಆಲಯದ ಎದುರಿನಿಂದ ತೆಗೆಯಿಸಿ ತನ್ನ ವೇದಿಯ ಉತ್ತರ ದಿಕ್ಕಿನಲ್ಲಿಡಿಸಿ",
			"15": "<sup>15</sup> ಯಾಜಕನಾದ ಊರೀಯನಿಗೆ - ಪ್ರಾತಃಕಾಲದ ಸರ್ವಾಂಗಹೋಮವನ್ನೂ ಸಾಯಂಕಾಲದ ನೈವೇದ್ಯವನ್ನೂ ಅರಸನು ತರುವ ಸರ್ವಾಂಗಹೋಮದ್ರವ್ಯ ಧಾನ್ಯನೈವೇದ್ಯ ಇವುಗಳನ್ನೂ ಜನರು ತರುವ ಸರ್ವಾಂಗಹೋಮದ್ರವ್ಯ ಧಾನ್ಯನೈವೇದ್ಯ ಪಾನದ್ರವ್ಯ ಇವುಗಳನ್ನೂ ಈ ಮಹಾವೇದಿಯ ಮೇಲೆ ಸಮರ್ಪಿಸಬೇಕು; ಮತ್ತು ಸರ್ವಾಂಗ ಹೋಮಯಜ್ಞಗಳಿಗಾಗಿ ವಧಿಸುವ ಪಶುಗಳ ರಕ್ತವನ್ನು ಇದರ ಮೇಲೆಯೇ ಚಿಮಿಕಿಸಬೇಕು. ದೈವೋತ್ತರಗಳನ್ನು ತಿಳಿದುಕೊಳ್ಳುವದಕ್ಕಾಗಿ ತಾಮ್ರವೇದಿಯನ್ನು ಉಪಯೋಗಿಸುವೆನು ಎಂದು ಹೇಳಿದನು.",
			"16": "<sup>16</sup> ಯಾಜಕನಾದ ಊರೀಯನು ಅರಸನು ಹೇಳಿದಂತೆಯೇ ಮಾಡಿದನು.",
			"17": "<sup>17</sup> ಅರಸನಾದ ಆಹಾಜನು ಅಶ್ಶೂರದ ಅರಸನ ನಿಮಿತ್ತವಾಗಿ ಪೀಠಗಳ ಪಟ್ಟಿಗಳನ್ನು ಕತ್ತರಿಸಿದನು; ಅವುಗಳ ಮೇಲಿದ್ದ ಗಂಗಾಳಗಳನ್ನು ತೆಗೆದಿಟ್ಟನು; ಸಮುದ್ರವೆನಿಸುವ ಪಾತ್ರೆಯನ್ನು ತಾಮ್ರದ ಹೋರಿಗಳ ಮೇಲಣಿಂದ ಇಳಿಸಿ ಕಲ್ಲುಕಟ್ಟೆಗಳ ಮೇಲೆ ಇಡಿಸಿದನು.",
			"18": "<sup>18</sup> ಯೆಹೋವನ ಆಲಯದ ಪ್ರಾಕಾರದೊಳಗೆ ಸಬ್ಬತ್‍ದಿನದ ಆರಾಧನೆಗಾಗಿ ರಾಜರಿಗೆ ಕಟ್ಟಲ್ಪಟ್ಟಿದ್ದ ಮಂಟಪವನ್ನೂ ಅರಮನೆಯಿಂದ ದೇವಸ್ಥಾನಕ್ಕೆ ಹೋಗುವ ಹೊರಗಿನ ಬಾಗಲನ್ನೂ ಬದಲಾಯಿಸಿದನು.",
			"19": "<sup>19</sup> ಆಹಾಜನ ಉಳಿದ ಚರಿತ್ರೆಯೂ ಅವನ ಕೃತ್ಯಗಳೂ ಯೆಹೂದರಾಜಕಾಲವೃತ್ತಾಂತ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"20": "<sup>20</sup> ಅವನು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಲು ಅವನ ಶವವನ್ನು ದಾವೀದನಗರದೊಳಗೆ ಅವನ ಕುಟುಂಬ ಶ್ಮಶಾನದಲ್ಲಿ ಸಮಾಧಿ ಮಾಡಿದರು. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ಹಿಜ್ಕೀಯ ಎಂಬವನು ಅರಸನಾದನು."
		},
		"17": {
			"1": "<sup>1</sup> ಯೆಹೂದದ ಅರಸನಾದ ಆಹಾಜನ ಆಳಿಕೆಯ ಹನ್ನೆರಡನೆಯ ವರುಷದಲ್ಲಿ ಏಲನ ಮಗನಾದ ಹೋಶೇಯನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾಗಿ ಸಮಾರ್ಯದಲ್ಲಿ ಒಂಭತ್ತು ವರುಷ ಆಳಿದನು.",
			"2": "<sup>2</sup> ಇವನು ತನ್ನ ಪೂರ್ವಿಕರಾದ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸರಷ್ಟು ಕೆಟ್ಟವನಾಗಿರಲಿಲ್ಲವಾದರೂ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾಗಿದ್ದನು.",
			"3": "<sup>3</sup> ಅಶ್ಶೂರದ ಅರಸನಾದ ಶಲ್ಮನೆಸೆರನು ಇವನಿಗೆ ವಿರೋಧವಾಗಿ ಬಂದಾಗ ಇವನು ಅವನಿಗೆ ಅಧೀನನಾಗಿ ಕಪ್ಪಕೊಡುವವನಾದನು.",
			"4": "<sup>4</sup> ಕೆಲವು ವರುಷಗಳಾದನಂತರ ಇವನು ಪ್ರತಿವರ್ಷವೂ ಕೊಡಬೇಕಾದ ಕಪ್ಪವನ್ನು ಕೊಡದೆ ಹೋದದರಿಂದಲೂ ಐಗುಪ್ತದ ಅರಸನಾದ ಸೋ ಎಂಬವನ ಬಳಿಗೆ ರಾಯಭಾರಿಗಳನ್ನು ಕಳುಹಿಸಿದದರಿಂದಲೂ ಅಶ್ಶೂರದ ಅರಸನು ಇವನನ್ನು ದ್ರೋಹಿಯೆಂದು ತಿಳಿದು ಬಂಧಿಸಿ ಸೆರೆಯಲ್ಲಿಟ್ಟನು.",
			"5": "<sup>5</sup> ಇದಲ್ಲದೆ ಅವನು ದೇಶವನ್ನೆಲ್ಲಾ ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡು ಸಮಾರ್ಯಕ್ಕೆ ಬಂದು ಮೂರು ವರುಷಗಳವರೆಗೂ ಅದಕ್ಕೆ ಮುತ್ತಿಗೆಹಾಕಿದನು.",
			"6": "<sup>6</sup> ಅದು ಹೋಶೇಯನ ಆಳಿಕೆಯ ಒಂಭತ್ತನೆಯ ವರುಷದಲ್ಲಿ ಅವನ ಸ್ವಾಧೀನವಾಗಲು ಅವನು ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಅಶ್ಶೂರ್‍ದೇಶಕ್ಕೆ ಒಯ್ದು ಹಲಹು ಪ್ರಾಂತದಲ್ಲಿಯೂ ಹಾಬೋರ್ ಹೊಳೆಯಿರುವ ಗೋಜಾನ್ ಪ್ರಾಂತದಲ್ಲಿಯೂ ಮೇದ್ಯರ ಪಟ್ಟಣಗಳಲ್ಲಿಯೂ ಇರಿಸಿದನು.",
			"7": "<sup>7</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಈ ದುರ್ಗತಿಗೆ ಅವರ ದುರ್ನಡತೆಯೇ ಕಾರಣ; ಹೇಗಂದರೆ - ಅವರು ತಮ್ಮನ್ನು ಐಗುಪ್ತದ ಅರಸನಾದ ಫರೋಹನ ಕೈಯಿಂದ ಬಿಡಿಸಿ ಅವನ ರಾಜ್ಯದಿಂದ ಹೊರತಂದ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ವಿರುದ್ಧವಾಗಿ ಪಾಪಮಾಡಿ ಅನ್ಯದೇವತೆಗಳನ್ನು ಸೇವಿಸಿದರು.",
			"8": "<sup>8</sup> ಯೆಹೋವನು ತಮ್ಮ ಎದುರಿನಿಂದ ಹೊರಡಿಸಿಬಿಟ್ಟ ಜನಾಂಗಗಳ ಮತ್ತು ಇಸ್ರಾಯೇಲ್‍ರಾಜರ ದುರಾಚಾರಗಳನ್ನು ಅನುಸರಿಸಿದರು;",
			"9": "<sup>9</sup> ಗುಪ್ತವಾಗಿ ತಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ವಿರೋಧವಾದ ಕೃತ್ಯಗಳನ್ನು ನಡಿಸಿದರು; ಕಾವಲುಗಾರರ ಬುರುಜುಳ್ಳ ಚಿಕ್ಕ ಊರು ಮೊದಲುಗೊಂಡು ಕೋಟೆಕೊತ್ತಲುಗಳುಳ್ಳ ಪಟ್ಟಣದವರೆಗಿರುವ ಎಲ್ಲಾ ಊರುಗಳಲ್ಲಿ ತಮಗೋಸ್ಕರ ಪೂಜಾಸ್ಥಳಗಳನ್ನು ಏರ್ಪಡಿಸಿಕೊಂಡರು;",
			"10": "<sup>10</sup> ಎಲ್ಲಾ ದಿನ್ನೆಗಳ ಮೇಲೆ ಮತ್ತು ಎಲ್ಲಾ ಹಸುರು ಮರಗಳ ಕೆಳಗೆ ಕಲ್ಲುಕಂಬ, ಅಶೇರವಿಗ್ರಹ ಸ್ತಂಭ ಇವುಗಳನ್ನು ನಿಲ್ಲಿಸಿದರು;",
			"11": "<sup>11</sup> ಯೆಹೋವನು ತಮ್ಮ ಎದುರಿನಿಂದ ಹೊರಡಿಸಿಬಿಟ್ಟ ಅನ್ಯಜನಾಂಗಗಳಂತೆ ಎಲ್ಲಾ ಪೂಜಾಸ್ಥಳಗಳಲ್ಲಿ ಧೂಪಸುಟ್ಟು ತಮ್ಮ ದುಷ್ಕೃತ್ಯಗಳಿಂದ ಆತನನ್ನು ರೇಗಿಸಿದರು;",
			"12": "<sup>12</sup> ಯೆಹೋವನ ಆಜ್ಞೆಯನ್ನು ಮೀರಿ ವಿಗ್ರಹಗಳನ್ನು ಆರಾಧಿಸಿದರು;",
			"13": "<sup>13</sup> ಯೆಹೋವನು ದರ್ಶಿಗಳೆನಿಸಿಕೊಂಡ ತನ್ನ ಪ್ರವಾದಿಗಳ ಮುಖಾಂತರವಾಗಿ ಇಸ್ರಾಯೇಲ್ಯ ಯೆಹೂದ್ಯರಿಗೆ - ನೀವು ನಿಮ್ಮ ದುರಾಚಾರವನ್ನು ಬಿಟ್ಟು ನಾನು ನಿಮ್ಮ ಪಿತೃಗಳಿಗೂ ನನ್ನ ಸೇವಕರಾದ ಪ್ರವಾದಿಗಳ ಮುಖಾಂತರವಾಗಿ ನಿಮಗೂ ಕೊಟ್ಟಂಥ ನನ್ನ ಎಲ್ಲಾ ಆಜ್ಞಾನಿಯಮವಿಧಿಗಳನ್ನು ಕೈಕೊಂಡು ನಡೆಯಿರಿ ಎಂದು ಖಂಡಿತವಾಗಿ ಹೇಳಿದಾಗ",
			"14": "<sup>14</sup> ಅವರು ದೇವರಾದ ಯೆಹೋವನನ್ನು ನಂಬದೆ ಆತನ ಆಜ್ಞೆಗಳಿಗೆ ಮಣಿಯದೆ ಇದ್ದ ತಮ್ಮ ಹಿರಿಯರಂತೆ ಆತನಿಗೆ ಕಿವಿಗೊಡದೆ ಹೋದರು;",
			"15": "<sup>15</sup> ಆತನು ತಮ್ಮ ಪಿತೃಗಳಿಗೆ ಕೊಟ್ಟ ವಿಧಿನಿಬಂಧನೆಗಳನ್ನೂ ತಮಗೆ ಹೇಳಿಸಿದ ಬುದ್ಧಿ ವಾಕ್ಯಗಳನ್ನೂ ತಿರಸ್ಕರಿಸಿ ಮಿಥ್ಯಾದೇವತೆಗಳನ್ನು ಸೇವಿಸಿ ನಿಷ್ಪ್ರಯೋಜಕರಾದರು; ಸುತ್ತಣ ಜನಾಂಗಗಳನ್ನು ಅನುಸರಿಸಬಾರದೆಂದು ಯೆಹೋವನು ಆಜ್ಞಾಪಿಸಿದರೂ ಅವರು ಕೇಳದೆ ಅವರನ್ನು ಅನುಸರಿಸಿದರು;",
			"16": "<sup>16</sup> ತಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಆಜ್ಞೆಗಳನ್ನೆಲ್ಲಾ ಮೀರಿ ಎರಡು ಎರಕದ ಬಸವಮೂರ್ತಿಗಳು, ಅಶೇರವಿಗ್ರಹಸ್ತಂಭ ಇವುಗಳನ್ನು ಮಾಡಿಕೊಂಡರು; ಆಕಾಶಸೈನ್ಯ ಬಾಳ್‍ದೇವತೆ ಇವುಗಳನ್ನು ಪೂಜಿಸಿದರು;",
			"17": "<sup>17</sup> ತಮ್ಮ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ಆಹುತಿಕೊಟ್ಟರು; ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ಕೆಟ್ಟವುಗಳಾಗಿರುವ ಕಣಿಹೇಳುವದು, ಯಂತ್ರಮಂತ್ರಗಳನ್ನು ಮಾಡುವದು ಮೊದಲಾದ ದುಷ್ಕೃತ್ಯಗಳಿಗೆ ತಮ್ಮನ್ನು ಮಾರಿಬಿಟ್ಟು ಆತನಿಗೆ ಕೋಪವನ್ನೆಬ್ಬಿಸಿದರು.",
			"18": "<sup>18</sup> ಅವರು ಈ ಪ್ರಕಾರ ನಡೆದದರಿಂದ ಯೆಹೋವನು ಅವರ ಮೇಲೆ ಬಹಳವಾಗಿ ಕೋಪಗೊಂಡು ಯೆಹೂದಕುಲದವರ ಹೊರತು ಉಳಿದ ಎಲ್ಲಾ ಕುಲಗಳವರನ್ನು ತನ್ನ ಸನ್ನಿಧಿಯಿಂದ ತಳ್ಳಿಬಿಟ್ಟನು.",
			"19": "<sup>19</sup> ಯೆಹೂದ್ಯರು ಸಹ ತಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಆಜ್ಞೆಯನ್ನು ಕೈಕೊಳ್ಳದೆ ಇಸ್ರಾಯೇಲ್ಯರ ಪದ್ಧತಿಯನ್ನು ಅನುಸರಿಸಿದದರಿಂದ",
			"20": "<sup>20</sup> ಆತನು ಇಸ್ರಾಯೇಲ್‍ಕುಲಗಳಲ್ಲಿ ಉಳಿದಿದ್ದ ಇವರನ್ನೂ ಹೇಸಿ ತನ್ನ ಸನ್ನಿಧಿಯಿಂದ ತಳ್ಳಿಬಿಟ್ಟು ಸೂರೆಮಾಡುವವರಿಗೆ ಒಪ್ಪಿಸಿ ಬಾಧಿಸಿದನು.",
			"21": "<sup>21</sup> ಆತನು ಇಸ್ರಾಯೇಲ್ ರಾಜ್ಯವನ್ನು ದಾವೀದಸಂತಾನದವರ ಕೈಯಿಂದ ಕಿತ್ತುಕೊಂಡನಂತರ ಇಸ್ರಾಯೇಲ್ಯರು ನೆಬಾಟನ ಮಗನಾದ ಯಾರೊಬ್ಬಾಮನನ್ನು ಅರಸನನ್ನಾಗಿ ಮಾಡಿಕೊಳ್ಳಲು ಅವನು ಅವರನ್ನು",
			"22": "<sup>22</sup> ಯೆಹೋವನ ಭಕ್ತಿಯಿಂದ ಬೀಳಿಸಿ ಮಹಾ ಪಾಪಕ್ಕೆ ಒಳಪಡಿಸಿದನು. ಅವರು ಯಾರೊಬ್ಬಾಮನ",
			"23": "<sup>23</sup> ದುರ್ಮಾರ್ಗದಲ್ಲಿ ನಡೆಯುವದನ್ನು ಬಿಡಲಿಲ್ಲವಾದದರಿಂದ ಯೆಹೋವನು ತನ್ನ ಸೇವಕರಾದ ಎಲ್ಲಾ ಪ್ರವಾದಿಗಳ ಮುಖಾಂತರವಾಗಿ ಮುಂತಿಳಿಸಿದಂತೆ ಅವರನ್ನು ತನ್ನ ಸನ್ನಿಧಿಯಿಂದ ತಳ್ಳಿಬಿಟ್ಟನು. ಅವರು ಸೆರೆಯವರಾಗಿ ತಮ್ಮ ದೇಶದಿಂದ ಅಶ್ಶೂರ್ ದೇಶಕ್ಕೆ ಒಯ್ಯಲ್ಪಟ್ಟು ಇಂದಿನವರೆಗೂ ಅಲ್ಲೇ ಇರುತ್ತಾರೆ.",
			"24": "<sup>24</sup> ಅಶ್ಶೂರದ ಅರಸನು ಸಮಾರ್ಯಪಟ್ಟಣಗಳಲ್ಲಿ ವಾಸವಾಗಿದ್ದ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಸೆರೆಯಾಗಿ ಒಯ್ದನಂತರ ಅವರಿಗೆ ಬದಲಾಗಿ ಆ ಪಟ್ಟಣಗಳಿಗೆ ಬಾಬೆಲ್, ಕೂತಾ, ಅವ್ವಾ, ಹಮಾತ್, ಸೆಫರ್ವಯಿಮ್ ಎಂಬ ಊರುಗಳವರನ್ನು ಕಳುಹಿಸಿದನು. ಅವರು ಸಮಾರ್ಯದೇಶವನ್ನು ಸ್ವತಂತ್ರಿಸಿಕೊಂಡು ಅದರ ಪಟ್ಟಣಗಳಲ್ಲಿ ವಾಸಿಸಿದರು.",
			"25": "<sup>25</sup> ಅವರಿಗೆ ಆರಂಭದಲ್ಲಿ ಯೆಹೋವನ ಭಯಭಕ್ತಿ ಇರಲಿಲ್ಲವಾದದರಿಂದ ಆತನು ಸಿಂಹಗಳನ್ನು ಕಳುಹಿಸಿದನು; ಅವು ಅವರಲ್ಲಿ ಅನೇಕರನ್ನು ಕೊಂದವು.",
			"26": "<sup>26</sup> ಅಶ್ಶೂರದ ಅರಸನ ಸೇವಕರು ತಮ್ಮ ಅರಸನಿಗೆ - ನೀನು ಸಮಾರ್ಯಪಟ್ಟಣಗಳಿಗೆ ಕಳುಹಿಸಿದ ಆಯಾ ಊರುಗಳ ಜನರು ಆ ದೇಶದ ದೇವರಿಗೆ ನಡೆಯತಕ್ಕ ರೀತಿಯನ್ನು ಅರಿಯದವರಾಗಿದ್ದಾರೆ. ಆದದರಿಂದ ಅಲ್ಲಿನ ದೇವರು ಸಿಂಹಗಳನ್ನು ಕಳುಹಿಸಿ ಅವರನ್ನು ಸಂಹರಿಸುತ್ತಿರುತ್ತಾನೆ ಎಂದು ತಿಳಿಸಿದರು.",
			"27": "<sup>27</sup> ಆಗ ಅವನು ಅವರಿಗೆ - ಅಲ್ಲಿಂದ ಇಲ್ಲಿಗೆ ಸೆರೆಯವರಾಗಿ ಬಂದಿರುವ ಯಾಜಕರಲ್ಲೊಬ್ಬನನ್ನು ಅಲ್ಲಿಗೆ ಕಳುಹಿಸಿರಿ. ಅವನು ಹೋಗಿ ಅಲ್ಲಿ ವಾಸವಾಗಿದ್ದು ಆ ದೇಶದ ದೇವರಿಗೆ ನಡೆಯತಕ್ಕ ರೀತಿಯನ್ನು ಅವರಿಗೆ ಕಲಿಸಲಿ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"28": "<sup>28</sup> ಅವರು ಸಮಾರ್ಯದಿಂದ ಸೆರೆಯವನಾಗಿ ಬಂದಿದ್ದ ಒಬ್ಬ ಯಾಜಕನನ್ನು ಅಲ್ಲಿಗೆ ಕಳುಹಿಸಿದರು. ಇವನು ಬೇತೇಲಿನಲ್ಲಿ ವಾಸವಾಗಿದ್ದು ಜನರಿಗೆ ಯೆಹೋವಭಕ್ತಿಯನ್ನು ಬೋಧಿಸಿದನು.",
			"29": "<sup>29</sup> ಆದರೂ ಆ ಜನಾಂಗಗಳವರು ತಮ್ಮ ದೇವತೆವಿಗ್ರಹಗಳನ್ನು ಮಾಡಿಕೊಂಡು ತಾವು ವಾಸಿಸುವ ಊರುಗಳೊಳಗೆ ಸಮಾರ್ಯದವರು ಏರ್ಪಡಿಸಿದ್ದ ಪೂಜಾಸ್ಥಳಗಳಲ್ಲಿ ಅವುಗಳನ್ನಿಟ್ಟು ಪೂಜಿಸುತ್ತಿದ್ದರು.",
			"30": "<sup>30</sup> ಬಾಬೆಲಿನವರು ಸುಕ್ಕೋತ್ಬೆನೋತ್, ಕೂತದವರು ನೇರ್ಗಲ್,",
			"31": "<sup>31</sup> ಹಮಾತಿನವರು ಅಷೀಮಾ, ಅವ್ವೀಯರು ನಿಭಜ್ ಮತ್ತು ತರ್ತಕ್ ಎಂಬ ದೇವತೆಗಳನ್ನು ಪೂಜಿಸುತ್ತಿದ್ದರು. ಸೆಫರ್ವಯಿಮಿನವರು ತಮ್ಮ ಕುಲದೇವತೆಗಳಾಗಿರುವ ಅದ್ರಮ್ಮೆಲೆಕ್, ಅನಮ್ಮೆಲೆಕ್ ಎಂಬವುಗಳಿಗೋಸ್ಕರ ತಮ್ಮ ಮಕ್ಕಳನ್ನು ಆಹುತಿಕೊಡುತ್ತಿದ್ದರು.",
			"32": "<sup>32</sup> ಅವರು ಯೆಹೋವನ ಭಕ್ತರಾಗಿ ಕನಿಷ್ಠಜನರಲ್ಲಿ ಕೆಲವರನ್ನು ಪೂಜಾಸ್ಥಳಗಳ ಯಾಜಕರನ್ನಾಗಿ ನೇಮಿಸಿಕೊಂಡು ಅವರ ಮುಖಾಂತರವಾಗಿ ಪೂಜಾಸ್ಥಳದ ಗುಡಿಗಳಲ್ಲಿ ಆರಾಧನೆ ನಡಿಸುತ್ತಿದ್ದರು.",
			"33": "<sup>33</sup> ಯೆಹೋವನ ಭಕ್ತರಾಗಿದ್ದರೂ ತಾವು ಬಿಟ್ಟುಬಂದ ದೇಶಗಳ ಪದ್ಧತಿಯ ಪ್ರಕಾರ ತಮ್ಮ ಕುಲದೇವತೆಗಳ ಆರಾಧನೆಯನ್ನೂ ಮಾಡುತ್ತಿದ್ದರು.",
			"34": "<sup>34</sup> ಅವರು ಆ ಕಾಲದಲ್ಲಿ ರೂಢಿಯಾದ ಪದ್ಧತಿಯಂತೆ ಇಂದಿನವರೆಗೂ ನಡೆಯುತ್ತಾರೆ. ಅವರು ಯೆಹೋವನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಲ್ಲ; ಆತನಿಂದ ಇಸ್ರಾಯೇಲನೆಂಬ ಹೆಸರು ಹೊಂದಿದ ಯಾಕೋಬನ ಸಂತಾನದವರಿಗೆ ಕೊಡಲ್ಪಟ್ಟ ಆಜ್ಞಾನಿಯಮ ವಿಧಿಧರ್ಮಶಾಸ್ತ್ರಗಳನ್ನು ಅನುಸರಿಸುವದಿಲ್ಲ.",
			"35": "<sup>35</sup> ಯೆಹೋವನು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ನೀವು ಅನ್ಯದೇವತೆಗಳನ್ನು ಆರಾಧಿಸಬಾರದು, ಅವುಗಳಿಗೆ ಕೈಮುಗಿಯಲೂ ಬಾರದು, ಸೇವೆಮಾಡಲೂ ಯಜ್ಞವರ್ಪಿಸಲೂಬಾರದು.",
			"36": "<sup>36</sup> ನಿಮ್ಮನ್ನು ಮಹಾಶಕ್ತಿ ಭುಜಪರಾಕ್ರಮ ಇವುಗಳ ಮೂಲಕವಾಗಿ ಐಗುಪ್ತದೇಶದಿಂದ ಕರಕೊಂಡುಬಂದ ಯೆಹೋವನಾದ ನನ್ನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿ ನನಗೊಬ್ಬನಿಗೇ ಕೈ ಮುಗಿದು ಯಜ್ಞವರ್ಪಿಸಬೇಕು,",
			"37": "<sup>37</sup> ನಾನು ನಿಮಗೆ ಬರೆಯಿಸಿಕೊಟ್ಟ ಆಜ್ಞಾನಿಯಮವಿಧಿ ಧರ್ಮಶಾಸ್ತ್ರಗಳನ್ನು ತಪ್ಪದೆ ಕೈಕೊಳ್ಳಬೇಕು,",
			"38": "<sup>38</sup> ಅನ್ಯದೇವತೆಗಳನ್ನು ಪೂಜಿಸಬಾರದು, ನನ್ನ ನಿಬಂಧನೆಯನ್ನು ಮರೆತು ಅನ್ಯದೇವತೆಗಳನ್ನು ಪೂಜಿಸಬಾರದು.",
			"39": "<sup>39</sup> ನಿಮ್ಮ ದೇವರಾಗಿರುವ ಯೆಹೋವನಾದ ನನ್ನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿರಬೇಕು; ಆಗ ನಿಮ್ಮನ್ನು ಎಲ್ಲಾ ಶತ್ರುಗಳ ಕೈಗೆ ಸಿಕ್ಕದಂತೆ ತಪ್ಪಿಸುವೆನು ಎಂದು ಹೇಳಿ ಅವರೊಡನೆ ಒಡಂಬಡಿಕೆ ಮಾಡಿಕೊಂಡನು;",
			"40": "<sup>40</sup> ಅವರು ಅದನ್ನು ಲಕ್ಷಿಸದೆ ತಮ್ಮ ಮುಂಚಿನ ಪದ್ಧತಿಗಳನ್ನೇ ಅನುಸರಿಸಿದರು.",
			"41": "<sup>41</sup> ಆ ಜನಾಂಗಗಳವರು ಯೆಹೋವ ಭಕ್ತರಾಗಿದ್ದರೂ ತಮ್ಮ ವಿಗ್ರಹಗಳನ್ನೂ ಆರಾಧಿಸುತ್ತಿದ್ದರು. ಅವರ ಸಂತಾನದವರು ಇಂದಿನವರೆಗೂ ಹಾಗೆಯೇ ಮಾಡುತ್ತಿರುತ್ತಾರೆ."
		},
		"18": {
			"1": "<sup>1</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನೂ ಏಲನ ಮಗನೂ ಆದ ಹೋಶೇಯನ ಆಳಿಕೆಯ ಮೂರನೆಯ ವರುಷದಲ್ಲಿ ಯೆಹೂದರಾಜನಾದ ಆಹಾಜನ ಮಗ ಹಿಜ್ಕೀಯನು ಆಳತೊಡಗಿದನು.",
			"2": "<sup>2</sup> ಇವನು ಪಟ್ಟಕ್ಕೆ ಬಂದಾಗ ಇಪ್ಪತ್ತೈದು ವರುಷದವನಾಗಿದ್ದು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಇಪ್ಪತ್ತೊಂಭತ್ತು ವರುಷ ಆಳಿದನು. ಜೆಕರ್ಯನ ಮಗಳಾದ ಅಬೀ ಎಂಬಾಕೆಯು ಇವನ ತಾಯಿ.",
			"3": "<sup>3</sup> ಇವನು ಎಲ್ಲಾ ವಿಷಯಗಳಲ್ಲಿಯೂ ತನ್ನ ಪೂರ್ವಿಕನಾದ ದಾವೀದನಂತೆ ಯೆಹೋವನ ಚಿತ್ತಾನುಸಾರವಾಗಿ ನಡೆದನು.",
			"4": "<sup>4</sup> ಪೂಜಾಸ್ಥಳಗಳನ್ನು ಹಾಳುಮಾಡಿ ಕಲ್ಲುಕಂಬಗಳನ್ನು ಒಡೆದು ಅಶೇರವಿಗ್ರಹ ಸ್ತಂಭಗಳನ್ನು ಕಡಿದುಹಾಕಿದನು; ಮೋಶೆಯು ಮಾಡಿಸಿದ ತಾಮ್ರಸರ್ಪವನ್ನು ಚೂರು ಚೂರು ಮಾಡಿದನು. ಇಸ್ರಾಯೇಲ್ಯರು ಅದಕ್ಕೆ ಆವರೆಗೂ ಧೂಪಸುಡುತ್ತಿದ್ದರು. ಅದಕ್ಕೆ ನೆಹುಷ್ಟಾನ್ ಎಂಬ ಹೆಸರಿತ್ತು;",
			"5": "<sup>5</sup> ಇವನು ಇಸ್ರಾಯೇಲ್ ದೇವರಾದ ಯೆಹೋವನಲ್ಲಿ ಭರವಸವಿಟ್ಟಿದ್ದನು. ಯೆಹೂದ್ಯರಲ್ಲಿ ಇವನಿಗೆ ಸಮಾನನಾದ ಅರಸನು ಮುಂಚೆಯೂ ತರುವಾಯವೂ ಇರಲಿಲ್ಲ.",
			"6": "<sup>6</sup> ಇದಲ್ಲದೆ ಇವನು ಯೆಹೋವನನ್ನೇ ಹೊಂದಿಕೊಂಡು ಆತನನ್ನು ಬಿಡದೆ ಹಿಂಬಾಲಿಸಿ ಆತನು ಮೋಶೆಯ ಮುಖಾಂತರವಾಗಿ ಅನುಗ್ರಹಿಸಿದ ಆಜ್ಞೆಯನ್ನು ಕೈಕೊಂಡನು.",
			"7": "<sup>7</sup> ಯೆಹೋವನು ಇವನ ಸಂಗಡ ಇದ್ದದರಿಂದ ಎಲ್ಲಿಗೆ ಹೋದರೂ ಕೃತಾರ್ಥನಾಗಿ ಬರುತ್ತಿದ್ದನು. ಇವನು ಅಶ್ಶೂರದ ಅರಸನಿಗೆ ವಿರೋಧವಾಗಿ ತಿರುಗಿಬಿದ್ದು ಸ್ವತಂತ್ರನಾದನು.",
			"8": "<sup>8</sup> ಗಾಜಪ್ರಾಂತದವರೆಗೆ ವಾಸವಾಗಿದ್ದ ಫಿಲಿಷ್ಟಿಯರನ್ನು ಸೋಲಿಸಿ ಕಾವಲುಗಾರರ ಬುರುಜಿರುವ ಚಿಕ್ಕ ಹಳ್ಳಿ ಮೊದಲುಗೊಂಡು ಕೋಟೆಕೊತ್ತಲುಗಳುಳ್ಳ ಮಹಾನಗರಗಳವರೆಗೂ ಎಲ್ಲಾ ಊರುಗಳನ್ನು ಹಾಳುಮಾಡಿದನು.",
			"9": "<sup>9</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನೂ ಏಲನ ಮಗನೂ ಆದ ಹೋಶೇಯನ ಆಳಿಕೆಯ ಏಳನೆಯ ವರುಷದಲ್ಲಿ ಅಂದರೆ ಹಿಜ್ಕೀಯನ ಆಳಿಕೆಯ ನಾಲ್ಕನೆಯ ವರುಷದಲ್ಲಿ ಅಶ್ಶೂರದ ಅರಸನಾದ ಶಲ್ಮನೆಸೆರನು ಸಮಾರ್ಯಕ್ಕೆ ವಿರೋಧವಾಗಿ ಬಂದು",
			"10": "<sup>10</sup> ಮೂರು ವರುಷಗಳವರೆಗೂ ಅದಕ್ಕೆ ಮುತ್ತಿಗೆಹಾಕಿ ಅದು ತನಗೆ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾದ ಹೋಶೇಯನ ಆಳಿಕೆಯ ಒಂಭತ್ತನೆಯ ವರುಷದಲ್ಲಿ ಅಂದರೆ ಹಿಜ್ಕೀಯನ ಆಳಿಕೆಯ",
			"11": "<sup>11</sup> ಆರನೆಯ ವರುಷದಲ್ಲಿ ಸ್ವಾಧೀನವಾಗಲು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಸೆರೆಹಿಡಿದು ಹಲಹು, ಹಾಬೋರ್ ಹೊಳೆಯಿರುವ ಗೋಜಾನ್ ಪ್ರಾಂತ, ಮೇದ್ಯರ ಪಟ್ಟಣಗಳು ಇವುಗಳಿಗೆ ಕಳುಹಿಸಿದನು.",
			"12": "<sup>12</sup> ಅವರು ತಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಮಾತನ್ನು ಕೇಳದೆಹೋದದ್ದೂ ಆತನ ನಿಬಂಧನೆಯನ್ನು ಉಲ್ಲಂಘಿಸಿದ್ದೂ ಆತನ ಸೇವಕನಾದ ಮೋಶೆಯ ಆಜ್ಞೆಗಳಿಗೆ ಅವಿಧೇಯರಾಗಿ ನಡೆದದ್ದೂ ಇದಕ್ಕೆ ಕಾರಣ.",
			"13": "<sup>13</sup> ಅರಸನಾದ ಹಿಜ್ಕೀಯನ ಆಳಿಕೆಯ ಹದಿನಾಲ್ಕನೆಯ ವರುಷದಲ್ಲಿ ಅಶ್ಶೂರದ ಅರಸನಾದ ಸನ್ಹೇರೀಬನು ಬಂದು ಯೆಹೂದ ಪ್ರಾಂತದ ಕೋಟೆಕೊತ್ತಲುಗಳುಳ್ಳ ಎಲ್ಲಾ ಪಟ್ಟಣಗಳನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡನು.",
			"14": "<sup>14</sup> ಆಗ ಯೆಹೂದದ ಅರಸನಾದ ಹಿಜ್ಕೀಯನು ಲಾಕೀಷಿನಲ್ಲಿದ್ದ ಅಶ್ಶೂರದ ಅರಸನ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿ ಅವನಿಗೆ - ನಾನು ಅಪರಾಧಿ; ನೀನು ನನ್ನನ್ನು ಬಿಟ್ಟುಹೋಗು, ನೀನು ನನಗೆ ವಿಧಿಸುವಷ್ಟನ್ನು ಕೊಡುತ್ತೇನೆ ಎಂದು ಹೇಳಿ ಕಳುಹಿಸಿದನು. ಅಶ್ಶೂರದ ಅರಸನು ಮುನ್ನೂರು ತಲಾಂತು ಬೆಳ್ಳಿಯನ್ನೂ ಮೂವತ್ತು ತಲಾಂತು ಬಂಗಾರವನ್ನೂ ಕೊಡಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"15": "<sup>15</sup> ಆಗ ಹಿಜ್ಕೀಯನು ಯೆಹೋವನ ಆಲಯದಲ್ಲಿಯೂ ಅರಮನೆಯ ಭಂಡಾರದಲ್ಲಿಯೂ ಸಿಕ್ಕಿದ ಬೆಳ್ಳಿಯನ್ನೂ",
			"16": "<sup>16</sup> ತಾನು ಯೆಹೋವನ ಆಲಯದ ಕದಗಳಿಗೂ ಕಂಬಗಳಿಗೂ ಹೊದಿಸಿದ್ದ ಬಂಗಾರದ ತಗಡನ್ನೂ ತೆಗೆದುಕೊಂಡು ಅಶ್ಶೂರದ ಅರಸನಿಗೆ ಕಳುಹಿಸಿದನು.",
			"17": "<sup>17</sup> ಅಶ್ಶೂರದ ಅರಸನು ಲಾಕೀಷಿನಿಂದ ಮಹಾಸೈನ್ಯ ಸಹಿತರಾದ ತರ್ತಾನ್, ರಬ್ಸಾರೀಸ್, ರಬ್ಷಾಕೆ ಎಂಬವರನ್ನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿದ್ದ ಹಿಜ್ಕೀಯನ ಬಳಿಗೆ ಕಳುಹಿಸಿದನು. ಇವರು ಗಟ್ಟಾ ಹತ್ತಿ ಯೆರೂಸಲೇಮಿಗೆ ಬಂದು ಮಡಿವಾಳರ ಹೊಲದ ಮೇಲೆ ಹೋಗುವ ರಾಜಮಾರ್ಗದ ಹತ್ತಿರ ಅಲ್ಲಿನ ಕೆರೆಯ ಕಾಲುವೆಯ ಬಳಿಯಲ್ಲಿ ಪಾಳೆಯಮಾಡಿಕೊಂಡು ಅರಸನನ್ನು ಕೂಗಿ ಕರೆದರು.",
			"18": "<sup>18</sup> ಆಗ ಹಿಲ್ಕೀಯನ ಮಗನೂ ರಾಜಗೃಹಾಧಿಪತಿಯೂ ಆದ ಎಲ್ಯಾಕೀಮ್, ಲೇಖಕನಾದ ಶೆಬ್ನ, ಆಸಾಫನ ಮಗನೂ ಮಂತ್ರಿಯೂ ಆಗಿದ್ದ ಯೋವ ಎಂಬವರು ಬಂದರು.",
			"19": "<sup>19</sup> ರಬ್ಷಾಕೆಯು ಅವರಿಗೆ - ನೀವು ಹೋಗಿ ಹಿಜ್ಕೀಯನಿಗೆ ಮಹಾರಾಜನಾದ ಅಶ್ಶೂರದ ಅರಸನ ಮಾತುಗಳನ್ನು ತಿಳಿಸಿರಿ.",
			"20": "<sup>20</sup> ಅವನು - ನಿನ್ನ ಭರವಸಕ್ಕೆ ಯಾವ ಆಧಾರವುಂಟು? ಬರೀ ಒಂದು ಬಾಯಿಮಾತಿನ ಮೇಲೆ ಯುದ್ಧಕ್ಕೆ ಬೇಕಾದ ವಿವೇಕವೂ ಬಲವೂ ಉಂಟೆಂದು ಅಂದುಕೊಳ್ಳುತ್ತೀ; ನೀನು ಯಾರನ್ನು ನಂಬಿಕೊಂಡು ನನಗೆ ವಿರೋಧವಾಗಿ ತಿರುಗಿ ಬಿದ್ದಿದ್ದೀ?",
			"21": "<sup>21</sup> ಜಜ್ಜಿದ ದಂಟಿಗೆ ಸಮಾನವಾಗಿರುವ ಐಗುಪ್ತದಲ್ಲಿ ಭರವಸವಿಟ್ಟಿರುತ್ತೀಯಷ್ಟೆ. ಒಬ್ಬನು ಅಂಥ ದಂಟಿನ ಮೇಲೆ ಕೈಯೂರಿಕೊಳ್ಳುವದಾದರೆ ಅದು ಅವನ ಕೈಯನ್ನು ತಿವಿದು ಒಳಗೆ ಹೋಗುತ್ತದಲ್ಲವೇ! ಐಗುಪ್ತದ ಅರಸನಾದ ಫರೋಹನಲ್ಲಿ ಭರವಸವಿಟ್ಟವರಿಗೆ ಅದೇ ಗತಿಯಾಗುವದು.",
			"22": "<sup>22</sup> ಒಂದು ವೇಳೆ ನೀವು - ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ನಂಬಿಕೊಂಡಿದ್ದೇವೆಂದು ಹೇಳಬಹುದು. ಹಿಜ್ಕೀಯನು ಯೆರೂಸಲೇಮಿನ ಯಜ್ಞವೇದಿಯ ಮುಂದೆಯೇ ಆರಾಧನೆ ಮಾಡಬೇಕೆಂಬದಾಗಿ ಯೆಹೂದ್ಯರಿಗೂ ಯೆರೂಸಲೇಮಿನವರಿಗೂ ಆಜ್ಞಾಪಿಸಿ ಆ ಯೆಹೋವನ ಪೂಜಾಸ್ಥಳಗಳನ್ನೂ ಬೇರೆ ಎಲ್ಲಾ ಯಜ್ಞವೇದಿಗಳನ್ನೂ ಹಾಳುಮಾಡಿದನಲ್ಲಾ!",
			"23": "<sup>23</sup> ನನ್ನ ಒಡೆಯನಾದ ಅಶ್ಶೂರದ ಅರಸನೊಂದಿಗೆ ಪಂಥಹಾಕುವದಕ್ಕೆ ನಿನಗೆ ಮನಸ್ಸುಂಟೋ? ಹಾಗಾದರೆ ಅವನು ನಿನಗೆ ಎರಡು ಸಾವಿರ ಕುದುರೆಗಳನ್ನು ಕೊಡುತ್ತಾನೆ; ನೀನು ಎಷ್ಟು ಮಂದಿ ಸವಾರರನ್ನು ಅವುಗಳ ಮೇಲೆ ಕುಳ್ಳಿರಿಸುವಿಯೋ?",
			"24": "<sup>24</sup> ಇದೂ ನಿನಗೆ ಅಸಾಧ್ಯವಾಗುವದಾದರೆ ನನ್ನ ಒಡೆಯನ ಸೇನಾಪತಿಗಳಲ್ಲಿ ಅತ್ಯಲ್ಪನನ್ನಾದರೂ ಸೋಲಿಸುವದು ಹೇಗೆ? ರಥಾಶ್ವಬಲಗಳ ವಿಷಯವಾಗಿ ಐಗುಪ್ತ್ಯರನ್ನು ನಂಬಿರುತ್ತೀಯೆಂದು ಕಾಣುತ್ತದೆ.",
			"25": "<sup>25</sup> ಈ ದೇಶವನ್ನು ಹಾಳುಮಾಡುವದಕ್ಕೆ ಯೆಹೋವನ ಚಿತ್ತವಿಲ್ಲದೆ ಬಂದೆನೆಂದು ನೆನಸುತ್ತೀಯೋ? ಇಲ್ಲಿಗೆ ಬಂದು ಇದನ್ನು ಹಾಳುಮಾಡಿಬಿಡು ಎಂದು ಯೆಹೋವನೇ ನನಗೆ ಆಜ್ಞಾಪಿಸಿದನು ಅನ್ನುತ್ತಾನೆ ಎಂದನು.",
			"26": "<sup>26</sup> ಆಗ ಹಿಲ್ಕೀಯನ ಮಗನಾದ ಎಲ್ಯಾಕೀಮ್, ಶೆಬ್ನ, ಯೋವ ಎಂಬವರು ರಬ್ಷಾಕೆಗೆ - ನೀನು ಮಾತಾಡುವದು ಪೌಳಿಗೋಡೆಯ ಮೇಲಿರುವವರಿಗೆ ಕೇಳಿಸುತ್ತದೆ. ಆದದರಿಂದ ದಯವಿಟ್ಟು ನಿನ್ನ ಸೇವಕರಾದ ನಮ್ಮೊಡನೆ ಅರಾಮ್ಯ ಭಾಷೆಯಲ್ಲಿ ಮಾತಾಡು; ಅದು ನಮಗೆ ತಿಳಿಯುತ್ತದೆ. ಆದರೆ ಯೂದಾಯ ಭಾಷೆಯಲ್ಲಿ ಮಾತಾಡಬೇಡ ಎಂದು ಹೇಳಿದರು.",
			"27": "<sup>27</sup> ಅದಕ್ಕೆ ರಬ್ಷಾಕೆಯು - ನನ್ನ ಯಜಮಾನನು ನಿಮ್ಮ ಸಂಗಡವಾಗಲಿ ನಿಮ್ಮ ಒಡೆಯನ ಸಂಗಡವಾಗಲಿ ಮಾತಾಡಬೇಕೆಂದು ನನ್ನನ್ನು ಕಳುಹಿಸಲಿಲ್ಲ. ಈ ಗೋಡೆಯ ಮೇಲೆ ಕೂತಿರುವ ಜನರ ಸಂಗಡ ಮಾತಾಡುವದಕ್ಕೋಸ್ಕರ ಕಳುಹಿಸಿದ್ದಾನೆ. ಅವರು ನಿಮ್ಮೊಡನೆ ಇದ್ದರೆ ಸ್ವಂತ ಮಲವನ್ನು ತಿಂದು ಸ್ವಂತ ಮೂತ್ರವನ್ನು ಕುಡಿಯಬೇಕಾಗುವದು ಎಂದು ಉತ್ತರಕೊಟ್ಟು ಎದ್ದು",
			"28": "<sup>28</sup> ಗೋಡೆಯ ಮೇಲೆ ನಿಂತವರಿಗೆ ಯೂದಾಯ ಭಾಷೆಯಲ್ಲಿ - ಅಶ್ಶೂರದ ಮಹಾರಾಜನ ಮಾತನ್ನು ಕೇಳಿರಿ;",
			"29": "<sup>29</sup> ಅವನು ನಿಮಗೆ - ಹಿಜ್ಕೀಯನಿಂದ ಮೋಸಹೋಗಬೇಡಿರಿ. ಅವನು ನಿಮ್ಮನ್ನು ನನ್ನ ಕೈಯಿಂದ ಬಿಡಿಸಲಾರನು.",
			"30": "<sup>30</sup> ಹಿಜ್ಕೀಯನು ನಿಮಗೆ - ಯೆಹೋವನನ್ನು ನಂಬಿರಿ; ಆತನು ನಮ್ಮನ್ನು ಹೇಗೂ ರಕ್ಷಿಸುವನು; ಈ ಪಟ್ಟಣವು ಅಶ್ಶೂರದ ಅರಸನ ವಶವಾಗುವದಿಲ್ಲ ಎಂಬದಾಗಿ ಹೇಳಿದರೆ ಅವನಿಗೆ ಕಿವಿಗೊಡಬೇಡಿರಿ, ಒಪ್ಪಬೇಡಿರಿ.",
			"31": "<sup>31</sup> ಅಶ್ಶೂರದ ಅರಸನಾದ ನನ್ನ ಮಾತನ್ನು ಕೇಳಿರಿ; ನನ್ನೊಡನೆ ಒಡಂಬಡಿಕೆಮಾಡಿಕೊಂಡು ನನ್ನ ಆಶ್ರಯದಲ್ಲಿ ಸೇರಿರಿ. ನಿಮ್ಮಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬನೂ ತನ್ನ ತನ್ನ ಅಂಜೂರಮರ, ದ್ರಾಕ್ಷಾಲತೆ ಇವುಗಳ ಹಣ್ಣುಗಳನ್ನು ತಿಂದು ತನ್ನ ತನ್ನ ಬಾವಿಯ ನೀರನ್ನು ಕುಡಿಯುವನು.",
			"32": "<sup>32</sup> ಸ್ವಲ್ಪ ಕಾಲವಾದನಂತರ ನಾನು ಬಂದು ನಿಮ್ಮನ್ನು ಧಾನ್ಯ ದ್ರಾಕ್ಷಾರಸ ಆಹಾರ ದ್ರಾಕ್ಷೇತೋಟ ಎಣ್ಣೇಮರ ಜೇನುತುಪ್ಪ ಇವು ಸಮೃದ್ಧಿಯಾಗಿರುವ ನಿಮ್ಮ ದೇಶಕ್ಕೆ ಸಮಾನವಾದ ಇನ್ನೊಂದು ದೇಶಕ್ಕೆ ಕರಕೊಂಡುಹೋಗುವೆನು; ನೀವು ಸಾಯುವದಿಲ್ಲ, ಬದುಕುವಿರಿ. ಯೆಹೋವನು ನಮ್ಮನ್ನು ರಕ್ಷಿಸುವನು ಎಂದು ನಂಬಿಕೆಯನ್ನು ನಿಮ್ಮಲ್ಲಿ ಹುಟ್ಟಿಸುವದಕ್ಕೆ ಪ್ರಯತ್ನಿಸುವ ಹಿಜ್ಕೀಯನಿಗೆ ಕಿವಿಗೊಡಬೇಡಿರಿ.",
			"33": "<sup>33</sup> ಯಾವ ಜನಾಂಗದ ದೇವತೆಯು ತನ್ನ ದೇಶವನ್ನು ಅಶ್ಶೂರದ ಅರಸನ ಕೈಗೆ ಸಿಕ್ಕದಂತೆ ತಪ್ಪಿಸಿ ಕಾಪಾಡಿತು?",
			"34": "<sup>34</sup> ಹಮಾತ್, ಅರ್ಪಾದ್, ಸೆಫರ್ವಯಿಮ್, ಹೇನ, ಇವ್ವಾ ಎಂಬ ಪಟ್ಟಣಗಳ ದೇವತೆಗಳೇನಾದವು? ಅವು ಸಮಾರ್ಯವನ್ನು ನನ್ನ ಕೈಗೆ ಸಿಕ್ಕದಂತೆ ತಪ್ಪಿಸಿದವೋ?",
			"35": "<sup>35</sup> ಯಾವ ಜನಾಂಗದ ದೇವತೆಯಾದರೂ ತನ್ನ ದೇಶವನ್ನು ನನ್ನ ಕೈಯಿಂದ ಬಿಡಿಸಲಾರದೆ ಹೋದ ಮೇಲೆ ಯೆಹೋವನು ಯೆರೂಸಲೇಮನ್ನು ನನ್ನ ಕೈಗೆ ಸಿಕ್ಕದಂತೆ ತಪ್ಪಿಸಿ ಕಾಪಾಡುವನೋ ಅನ್ನುತ್ತಾನೆ ಎಂದು ಹೇಳಿದನು.",
			"36": "<sup>36</sup> ಆ ಸೇನಾಪತಿಗಳಿಗೆ ಯಾವ ಉತ್ತರವನ್ನೂ ಕೊಡಬಾರದೆಂದು ಅರಸನು ತನ್ನ ಪ್ರಜೆಗಳಿಗೆ ಆಜ್ಞಾಪಿಸಿದ್ದರಿಂದ ಅವರು ಸುಮ್ಮನಿದ್ದರು; ಏನೂ ಅನ್ನಲಿಲ್ಲ.",
			"37": "<sup>37</sup> ಹಿಲ್ಕೀಯನ ಮಗನೂ ರಾಜಗೃಹಾಧಿಪತಿಯೂ ಆಗಿದ್ದ ಎಲ್ಯಾಕೀಮ್, ಲೇಖಕನಾದ ಶೆಬ್ನ, ಆಸಾಫನ ಮಗನೂ ಮಂತ್ರಿಯೂ ಆಗಿದ್ದ ಯೋವ ಎಂಬವರು ತಮ್ಮ ಬಟ್ಟೆಗಳನ್ನು ಹರಕೊಂಡು ಅರಸನಾದ ಹಿಜ್ಕೀಯನ ಬಳಿಗೆ ಬಂದು ಅವನಿಗೆ ರಬ್ಷಾಕೆಯ ಮಾತುಗಳನ್ನು ತಿಳಿಸಿದರು."
		},
		"19": {
			"1": "<sup>1</sup> ಹಿಜ್ಕೀಯನು ಅದನ್ನು ಕೇಳಿ ಬಟ್ಟೆಗಳನ್ನು ಹರಿದುಕೊಂಡು ಗೋಣೀತಟ್ಟನ್ನು ಕಟ್ಟಿಕೊಂಡು ಯೆಹೋವನ ಆಲಯಕ್ಕೆ ಹೋದನು.",
			"2": "<sup>2</sup> ಇದಲ್ಲದೆ ಅವನು ರಾಜಗೃಹಾಧಿಪತಿಯಾದ ಎಲ್ಯಾಕೀಮ್, ಲೇಖಕನಾದ ಶೆಬ್ನ, ವೃದ್ಧಯಾಜಕರು ಇವರನ್ನು ಕರಿಸಿ ಅವರಿಗೆ ಆಜ್ಞಾಪಿಸಿದ್ದೇನಂದರೆ - ನೀವು ಗೋಣೀತಟ್ಟನ್ನು ಕಟ್ಟಿಕೊಂಡು ಆಮೋಚನ ಮಗನೂ ಪ್ರವಾದಿಯೂ ಆಗಿರುವ ಯೆಶಾಯನ ಬಳಿಗೆ ಹೋಗಿ ಅವನಿಗೆ -",
			"3": "<sup>3</sup> ಈ ದಿವಸದಲ್ಲಿ ನಮಗೆ ಮಹಾಕಷ್ಟವು ಸಂಭವಿಸಿರುತ್ತದೆ; ನಾವು ಶಿಕ್ಷೆಯನ್ನೂ ನಿಂದೆಯನ್ನೂ ಅನುಭವಿಸಬೇಕಾಯಿತು. ಹೆರಿಗೆಯ ಕಾಲ ಬಂದದೆ; ಆದರೆ ಹೆರುವದಕ್ಕೆ ಬಲ ಸಾಲದು.",
			"4": "<sup>4</sup> ಜೀವಸ್ವರೂಪನಾದ ದೇವರನ್ನು ದೂಷಿಸುವದಕ್ಕಾಗಿ ತನ್ನ ಯಜಮಾನನಾದ ಅಶ್ಶೂರದ ಅರಸನಿಂದ ಕಳುಹಿಸಲ್ಪಟ್ಟ ರಬ್ಷಾಕೆಯ ನಿಂದಾವಾಕ್ಯಗಳನ್ನು ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನು ಕೇಳಿರುವನು; ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನು ತಾನು ಹೇಳಿದ ವಾಕ್ಯಗಳ ನಿಮಿತ್ತ ಮುಯ್ಯಿತೀರಿಸಾನು. ಆದದರಿಂದ ಉಳಿದಿರುವ ಸ್ವಲ್ಪ ಜನರಿಗೋಸ್ಕರ ಆತನನ್ನು ಪ್ರಾರ್ಥಿಸು ಎಂಬದಾಗಿ ಹಿಜ್ಕೀಯನು ಅನ್ನುತ್ತಾನೆ ಎಂದು ಹೇಳಿರಿ ಎಂಬದೇ.",
			"5": "<sup>5</sup> ಅರಸನಾದ ಹಿಜ್ಕೀಯನ ಸೇವಕರು ಯೆಶಾಯನ ಬಳಿಗೆ ಬಂದಾಗ ಅವನು ಅವರಿಗೆ -",
			"6": "<sup>6</sup> ನೀನು ಕೇಳಿದ ಮಾತುಗಳ ದೆಸೆಯಿಂದ ಹೆದರಬೇಡ; ಅಶ್ಶೂರದ ಅರಸನ ಸೇವಕರು ಆ ಮಾತುಗಳಿಂದ ನನ್ನನ್ನೇ ದೂಷಿಸಿದ್ದಾರೆ.",
			"7": "<sup>7</sup> ಆದದರಿಂದ ನಾನು ಅವನ ಮೇಲೆ [ಭಯದ] ಆತ್ಮವನ್ನು ಬರಮಾಡುವೆನು. ಅವನು ಒಂದು ಸುದ್ದಿಯನ್ನು ಕೇಳಿ ತಿರುಗಿಕೊಂಡು ಸ್ವದೇಶಕ್ಕೆ ಹೋಗಿ ಅಲ್ಲಿ ಕತ್ತಿಯಿಂದ ಸಂಹೃತನಾಗುವಂತೆ ಮಾಡುವೆನು ಎಂಬದಾಗಿ ಯೆಹೋವನು ನಿಮ್ಮ ಯಜಮಾನನಿಗೆ ಹೇಳುತ್ತಾನೆ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"8": "<sup>8</sup> ರಬ್ಷಾಕೆಯು ಹಿಂದಿರುಗಿ ಹೋಗುವಾಗ ದಾರಿಯಲ್ಲಿ ಅಶ್ಶೂರದ ಅರಸನು ಲಾಕೀಷನ್ನು ಬಿಟ್ಟು ಹೋದನೆಂಬ ವರ್ತಮಾನ ಕೇಳಿ ಲಿಬ್ನಕ್ಕೆ ಹೋಗಿ ಅಲ್ಲಿ ಅವನನ್ನು ಕಂಡನು. ಆಗ ಅವನು ಆ ಪಟ್ಟಣಕ್ಕೆ ವಿರೋಧವಾಗಿ ಯುದ್ಧಮಾಡುತ್ತಾ ಇದ್ದನು.",
			"9": "<sup>9</sup> ಅಷ್ಟರಲ್ಲಿ ಕೂಷಿನ ಅರಸನಾದ ತಿರ್ಹಾಕನು ತನಗೆ ವಿರೋಧವಾಗಿ ಹೊರಟಿದ್ದಾನೆಂಬ ಸುದ್ದಿಯನ್ನು ಕೇಳಿ ಯೆಹೂದದ ಅರಸನಾದ ಹಿಜ್ಕೀಯನ ಬಳಿಗೆ ತಿರಿಗಿ ದೂತರನ್ನು ಕಳುಹಿಸಿ",
			"10": "<sup>10</sup> ಅವರ ಮುಖಾಂತರವಾಗಿ ಅವನಿಗೆ - ನೀನು ನಂಬುವ ದೇವರು - ನಾನು ಯೆರೂಸಲೇಮನ್ನು ಅಶ್ಶೂರದ ಅರಸನ ಕೈಗೆ ಒಪ್ಪಿಸುವದಿಲ್ಲವೆಂದು ಹೇಳಿ ನಿನ್ನನ್ನು ಮೋಸಗೊಳಿಸಾನು.",
			"11": "<sup>11</sup> ಅಶ್ಶೂರದ ಅರಸರು ಎಲ್ಲಾ ರಾಜ್ಯಗಳನ್ನು ಪೂರ್ಣವಾಗಿ ನಾಶಮಾಡಿದರೆಂದು ಕೇಳಿದಿಯಲ್ಲಾ; ಹೀಗಿದ್ದ ಮೇಲೆ ನೀನು ಉಳಿಯುವಿಯೋ?",
			"12": "<sup>12</sup> ನನ್ನ ತಂದೆ ತಾತಂದಿರು ಗೋಜಾನ್, ಖಾರಾನ್, ರೆಚೆಫ್ ಎಂಬ ಪಟ್ಟಣಗಳ ಜನರನ್ನೂ ತೆಲಸ್ಸಾರ್ ಪ್ರಾಂತದಲ್ಲಿರುವ ಎದೆನಿನ ಜನರನ್ನೂ ನಾಶಮಾಡುವದಕ್ಕೆ ಹೋದಾಗ ಅವರ ದೇವತೆಗಳು ಅವರನ್ನು ಕಾಪಾಡಿದವೋ?",
			"13": "<sup>13</sup> ಹಮಾತ್, ಅರ್ಪಾದ್, ಸೆಫರ್ವಯಿಮ್, ಹೇನ, ಇವ್ವಾ ಎಂಬ ಪಟ್ಟಣಗಳ ಅರಸರು ಏನಾದರು ಎಂದು ಹೇಳಿಸಿದನು.",
			"14": "<sup>14</sup> ಹಿಜ್ಕೀಯನು ಆ ದೂತರು ತಂದ ಪತ್ರವನ್ನು ತೆಗೆದುಕೊಂಡು ಓದಿದನಂತರ ಯೆಹೋವನ ಆಲಯಕ್ಕೆ ಹೋಗಿ ಅದನ್ನು ಯೆಹೋವನ ಮುಂದೆ ತೆರೆದಿಟ್ಟು ಆತನಿಗೆ -",
			"15": "<sup>15</sup> ಕೆರೂಬಿಗಳ ಮೇಲೆ ಆಸೀನನಾಗಿರುವಾತನೇ, ಇಸ್ರಾಯೇಲ್‍ದೇವರೇ, ಯೆಹೋವನೇ, ಎಲ್ಲಾ ಭೂರಾಜ್ಯಗಳನ್ನು ಆಳುವ ದೇವರು ನೀನೊಬ್ಬನೇ; ಪರಲೋಕ ಭೂಲೋಕಗಳನ್ನುಂಟು ಮಾಡಿದವನು ನೀನೇ.",
			"16": "<sup>16</sup> ಯೆಹೋವನೇ, ಕಿವಿಗೊಟ್ಟು ಕೇಳು; ಯೆಹೋವನೇ, ಕಣ್ಣಿಟ್ಟು ನೋಡು. ಸನ್ಹೇರೀಬನು ಜೀವಸ್ವರೂಪದೇವರಾದ ನಿನ್ನನ್ನು ನಿಂದಿಸುವದಕ್ಕೋಸ್ಕರ ಹೇಳಿಕಳುಹಿಸಿದ ಮಾತುಗಳನ್ನು ಮನಸ್ಸಿಗೆ ತಂದುಕೋ.",
			"17": "<sup>17</sup> ಯೆಹೋವನೇ, ಅಶ್ಶೂರದ ಅರಸರು ದೇಶಜನಾಂಗಗಳನ್ನು ಹಾಳುಮಾಡಿ",
			"18": "<sup>18</sup> ಅವರ ದೇವತೆಗಳನ್ನು ಬೆಂಕಿಯಲ್ಲಿ ಹಾಕಿದ್ದು ನಿಜ. ಅವು ದೇವತೆಗಳಲ್ಲ, ಮನುಷ್ಯರು ಕೆತ್ತಿದ ಕಲ್ಲು ಮರಗಳ ಬೊಂಬೆಗಳಷ್ಟೆ; ಆದದರಿಂದಲೇ ಅವುಗಳನ್ನು ಹಾಳು ಮಾಡುವದು ಅವರಿಗೆ ಸಾಧ್ಯವಾಯಿತು.",
			"19": "<sup>19</sup> ಯೆಹೋವನೇ, ನಮ್ಮ ದೇವರೇ, ನೀನೊಬ್ಬನೇ ದೇವರೆಂಬದನ್ನು ಭೂರಾಜ್ಯಗಳೆಲ್ಲವೂ ತಿಳಿದುಕೊಳ್ಳುವಂತೆ ನಮ್ಮನ್ನು ಇವನ ಕೈಯಿಂದ ಬಿಡಿಸು ಎಂದು ಪ್ರಾರ್ಥಿಸಿದನು.",
			"20": "<sup>20</sup> ಆಗ ಆಮೋಚನ ಮಗನಾದ ಯೆಶಾಯನು ಹಿಜ್ಕೀಯನಿಗೆ ಹೇಳಿಕಳುಹಿಸಿದ್ದೇನಂದರೆ - ನೀನು ಅಶ್ಶೂರದ ಅರಸನಾದ ಸನ್ಹೇರೀಬನ ವಿಷಯವಾಗಿ ಮಾಡಿದ ಬಿನ್ನಹವನ್ನು ಕೇಳಿದ್ದೇನೆಂಬದಾಗಿ ಇಸ್ರಾಯೇಲ್‍ದೇವರಾದ ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ.",
			"21": "<sup>21</sup> ಯೆಹೋವನು ಅವನನ್ನು ಕುರಿತು - ಕನ್ನಿಕೆಯಾಗಿರುವ ಚೀಯೋನ್ ಕುಮಾರ್ತೆಯು ನಿನ್ನನ್ನು ತಿರಸ್ಕರಿಸಿ ಪರಿಹಾಸ್ಯ ಮಾಡುತ್ತಾಳೆ; ಯೆರೂಸಲೇಮ್ ಕುಮಾರ್ತೆಯು ನಿನ್ನ ಹಿಂದಿನಿಂದ ತಲೆಯಾಡಿಸುತ್ತಾಳೆ.",
			"22": "<sup>22</sup> ನೀನು ಯಾರನ್ನು ನಿಂದಿಸಿ ಯಾರನ್ನು ದೂಷಿಸಿದಿ? ಯಾರಿಗೆ ವಿರೋಧವಾಗಿ ಬಾಯ್ದೆರೆದು ಒದರಿದಿ? ನೀನು ಸೊಕ್ಕಿನಿಂದ ನೋಡಿದ್ದು ಯಾರನ್ನು? ಇಸ್ರಾಯೇಲ್ಯರ ಸದಮಲಸ್ವಾಮಿಯನ್ನಲ್ಲವೇ!",
			"23": "<sup>23</sup> ನೀನು ನಿನ್ನ ದೂತರ ಮುಖಾಂತರವಾಗಿ ಕರ್ತನನ್ನು ನಿಂದಿಸಿ - ನನ್ನ ರಥಸಮೂಹದೊಡನೆ ಪರ್ವತ ಶಿಖರಗಳನ್ನು ಹತ್ತಿದ್ದೇನೆ; ಲೆಬನೋನಿನ ದುರ್ಗಮಸ್ಥಳಗಳಿಗೆ ಹೋಗಿದ್ದೇನೆ; ಅದರ ಎತ್ತರವಾದ ದೇವದಾರುವೃಕ್ಷಗಳನ್ನೂ ಶ್ರೇಷ್ಠವಾದ ತುರಾಯಿಮರಗಳನ್ನೂ ಕಡಿದುಬಿಟ್ಟಿದ್ದೇನೆ; ಅಲ್ಲಿನ ಬಹುದೂರ ನಿವಾಸಸ್ಥಳವನ್ನೂ ಉದ್ಯಾನ ವನಗಳನ್ನೂ ಪ್ರವೇಶಿಸಿದ್ದೇನೆ;",
			"24": "<sup>24</sup> ಪರದೇಶಗಳಲ್ಲಿ ಅಗೆದು ನೀರುತೆಗೆದು ಕುಡಿದಿದ್ದೇನೆ; ನನ್ನ ಪಾದಗಳಿಂದ ಐಗುಪ್ತದ ಎಲ್ಲಾ ಹೊಳೆಗಳನ್ನು ಬತ್ತಿಸಿದ್ದೇನೆ ಎಂಬದಾಗಿ ನೀನು ಕೊಚ್ಚಿಕೊಂಡಿದ್ದೀ.",
			"25": "<sup>25</sup> ಕೇಳು, ಹೀಗಾಗಬೇಕೆಂದು ಬಹುಕಾಲದ ಹಿಂದೆಯೇ ಗೊತ್ತುಮಾಡಿದೆನು; ಪೂರ್ವಕಾಲದಲ್ಲಿ ನಿರ್ಣಯಿಸಿದ್ದನ್ನು ಈಗ ನೆರವೇರಿಸಿದ್ದೇನೆ. ಆದದರಿಂದ ಕೋಟೆಕೊತ್ತಲುಗಳುಳ್ಳ ಪಟ್ಟಣಗಳನ್ನು ಹಾಳುದಿಬ್ಬಗಳನ್ನಾಗಿ ಮಾಡುವದು ನಿನಗೆ ಸಾಧ್ಯವಾಯಿತು.",
			"26": "<sup>26</sup> ಅವುಗಳ ನಿವಾಸಿಗಳು ಬಲವಿಲ್ಲದವರಾಗಿ ಆಶಾಭಂಗಪಟ್ಟು ಕಳವಳಗೊಂಡರು; ಅವರು ಹೊಲದ ಕಾಯಿಪಲ್ಯಕ್ಕೂ ಎಳೆಗರಿಕೆಗೂ ಮಾಳಿಗೆಯ ಮೇಲಣ ಹುಲ್ಲಿಗೂ ಹೊಡೆಹಾಯುವದಕ್ಕಿಂತ ಮೊದಲೇ ಒಣಗಿಹೋದ ಪೈರಿಗೂ ಸಮಾನರಾದರು.",
			"27": "<sup>27</sup> ನೀನು ಕೂತುಕೊಳ್ಳುವದೂ ಹೋಗುವದೂ ಬರುವದೂ ನನಗೆ ಗೊತ್ತುಂಟು; ನೀನು ನನ್ನ ಮೇಲೆ ರೌದ್ರಾವೇಶನಾಗಿರುವದನ್ನು ಬಲ್ಲೆನು.",
			"28": "<sup>28</sup> ನೀನು ನನಗೆ ವಿರೋಧವಾಗಿ ಉಬ್ಬಿಕೊಂಡಿರುವದೂ ಕುಪಿತನಾಗಿರುವದೂ ನನಗೆ ತಿಳಿದು ಬಂತು. ಆದದರಿಂದ ನಿನಗೆ ಮೂಗುದಾಣವನ್ನೂ ಕಡಿವಾಣವನ್ನೂ ಹಾಕಿ ಬಂದ ದಾರಿಯಿಂದಲೇ ನಿನ್ನನ್ನು ಹಿಂದಕ್ಕೆ ಎಳೆದುಕೊಂಡು ಹೋಗುವೆನು ಎಂಬದಾಗಿ ಹೇಳಿದ್ದಾನೆ.",
			"29": "<sup>29</sup> ಈ ಮಾತುಗಳು ನೆರವೇರುವವೆಂಬದಕ್ಕೆ ನೀವು ಈ ವರುಷದಲ್ಲಿ ಕೂಳೆ ಬೆಳೆಯನ್ನೂ ಮುಂದಿನ ವರುಷದಲ್ಲಿ ತನ್ನಷ್ಟಕ್ಕೆ ತಾನೇ ಬೆಳೆದದ್ದನ್ನೂ ಮೂರನೆಯ ವರುಷ ಹೊಲಗಳಲ್ಲಿ ಬಿತ್ತಿ ಕೊಯ್ದದ್ದನ್ನೂ ದ್ರಾಕ್ಷೇತೋಟಗಳಲ್ಲಿ ವ್ಯವಸಾಯಮಾಡಿ ಕೂಡಿಸಿದ್ದನ್ನೂ ಅನುಭವಿಸುವದೇ ಗುರುತಾಗಿರುವದು.",
			"30": "<sup>30</sup> ತಪ್ಪಿಸಿಕೊಂಡು ಉಳಿದ ಯೆಹೂದ್ಯರು ದೇಶದಲ್ಲಿ ನೆಲೆಗೊಂಡು ಅಭಿವೃದ್ಧಿಯಾಗುವರು.",
			"31": "<sup>31</sup> ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಉಳಿದವರು ಹರಡಿಕೊಳ್ಳುವರು; ಚೀಯೋನ್ ಪರ್ವತದಲ್ಲಿ ತಪ್ಪಿಸಿಕೊಂಡವರು ಅಭಿವೃದ್ಧಿ ಹೊಂದುವರು; ಯೆಹೋವನ ಆಗ್ರಹವೇ ಇದನ್ನು ನೆರವೇರಿಸುವದು.",
			"32": "<sup>32</sup> ಯೆಹೋವನು ಅಶ್ಶೂರದ ಅರಸನ ವಿಷಯವಾಗಿ - ನನ್ನ ಮಾತನ್ನು ಕೇಳು, ಅವನು ಪಟ್ಟಣವನ್ನು ಸಮೀಪಿಸುವದಿಲ್ಲ, ಅದಕ್ಕೆ ಬಾಣವನ್ನೆಸೆಯುವದಿಲ್ಲ, ಗುರಾಣಿಹಿಡಿದಿರುವವರನ್ನು ಕಳುಹಿಸುವದಿಲ್ಲ, ಅದನ್ನು ಕೆಡವಿಬಿಡುವದಕ್ಕೋಸ್ಕರ ಅದರ ಎದುರಾಗಿ ಮಣ್ಣಿನ ದಿಬ್ಬವನ್ನು ಮಾಡುವದಿಲ್ಲ.",
			"33": "<sup>33</sup> ಅವನು ಬಂದ ದಾರಿಯಿಂದಲೇ ಹಿಂದಿರುಗಿ ಹೋಗುವನು; ಈ ಪಟ್ಟಣಕ್ಕೆ ಬರುವದೇ ಇಲ್ಲ.",
			"34": "<sup>34</sup> ನನಗೋಸ್ಕರವಾಗಿಯೂ ನನ್ನ ಸೇವಕನಾದ ದಾವೀದನಿಗೋಸ್ಕರವಾಗಿಯೂ ಇದನ್ನು ಉಳಿಸಿ ಕಾಪಾಡುವೆನು ಎಂದು ಹೇಳುತ್ತಾನೆ ಎಂಬದೇ.",
			"35": "<sup>35</sup> ಅದೇ ರಾತ್ರಿಯಲ್ಲಿ ಯೆಹೋವನ ದೂತನು ಹೊರಟುಬಂದು ಅಶ್ಶೂರ್ಯರ ಪಾಳೆಯದಲ್ಲಿ ಲಕ್ಷದ ಎಂಭತ್ತೈದು ಸಾವಿರಮಂದಿ ಸೈನಿಕರನ್ನು ಸಂಹರಿಸಿದನು. ಅಶ್ಶೂರ್ಯರು ಬೆಳಿಗ್ಗೆ ಎದ್ದು ನೋಡುವಲ್ಲಿ ಪಾಳೆಯತುಂಬಾ ಹೆಣಗಳಿದ್ದವು.",
			"36": "<sup>36</sup> ಆಗ ಅವರ ಅರಸನಾದ ಸನ್ಹೇರೀಬನು ಹಿಂದಿರುಗಿ ನಿನೆವೆಪಟ್ಟಣಕ್ಕೆ ಹೋಗಿ ಅಲ್ಲಿ ವಾಸಿಸಿದನು.",
			"37": "<sup>37</sup> ಅವನು ಒಂದು ದಿವಸ ಗುಡಿಗೆ ಹೋಗಿ ತನ್ನ ದೇವರಾದ ನಿಸ್ರೋಕನನ್ನು ಆರಾಧಿಸುತ್ತಿರುವಾಗ ಅದ್ರಮ್ಮೆಲೆಕ್, ಸರೆಚೆರ್ ಎಂಬವರು ಅವನನ್ನು ಕತ್ತಿಯಿಂದ ಕೊಂದು ಅರಾರಾಟ್ ದೇಶಕ್ಕೆ ಓಡಿಹೋದರು. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ಏಸರ್‍ಹದ್ದೋನನು ಅರಸನಾದನು."
		},
		"20": {
			"1": "<sup>1</sup> ಆ ಕಾಲದಲ್ಲಿ ಹಿಜ್ಕೀಯನು ಮರಣಕರ ರೋಗದಲ್ಲಿ ಬಿದ್ದನು. ಆಗ ಆಮೋಚನ ಮಗನೂ ಪ್ರವಾದಿಯೂ ಆದ ಯೆಶಾಯನು ಅವನ ಬಳಿಗೆ ಬಂದು ಅವನಿಗೆ - ನಿನ್ನ ಮನೆಯ ವಿಷಯವಾಗಿ ವ್ಯವಸ್ಥೆಮಾಡು; ಯಾಕಂದರೆ ನೀನು ಉಳಿಯುವ ಹಾಗಿಲ್ಲ, ಸಾಯಬೇಕಾಗಿದೆ ಎಂಬದಾಗಿ ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ ಎಂದು ಹೇಳಿದನು.",
			"2": "<sup>2</sup> ಇದನ್ನು ಕೇಳಿದೊಡನೆ ಹಿಜ್ಕೀಯನು ಮೋರೆಯನ್ನು ಗೋಡೆಯ ಕಡೆಗೆ ತಿರುಗಿಸಿ -",
			"3": "<sup>3</sup> ಯೆಹೋವನೇ, ನಾನು ನಂಬಿಗಸ್ತನಾಗಿಯೂ ಯಥಾರ್ಥಚಿತ್ತನಾಗಿಯೂ ನಿನಗೆ ನಡೆದುಕೊಂಡು ನಿನ್ನ ದೃಷ್ಟಿಯಲ್ಲಿ ಒಳ್ಳೆಯವನಾಗಿದ್ದದ್ದನ್ನು ನೆನಪು ಮಾಡಿಕೋ ಎಂದು ಪ್ರಾರ್ಥಿಸಿ ಬಹಳವಾಗಿ ಅತ್ತನು.",
			"4": "<sup>4</sup> ಯೆಶಾಯನು ಅರಮನೆಯ ಮಧ್ಯಪ್ರಾಕಾರವನ್ನು ದಾಟುವದಕ್ಕೆ ಮೊದಲು ಯೆಹೋವನು ಅವನಿಗೆ -",
			"5": "<sup>5</sup> ನೀನು ಹಿಂದಿರುಗಿ ಹೋಗಿ ನನ್ನ ಪ್ರಜೆಗಳ ಪ್ರಭುವಾಗಿರುವ ಹಿಜ್ಕೀಯನಿಗೆ - ನಾನು ನಿನ್ನ ಪ್ರಾರ್ಥನೆಯನ್ನು ಕೇಳಿದ್ದೇನೆ; ನಿನ್ನ ಕಣ್ಣೀರನ್ನು ನೋಡಿದ್ದೇನೆ; ನೀನು ಗುಣಹೊಂದಿ ನಾಡದು ನನ್ನ ಆಲಯಕ್ಕೆ ಬರುವಿ.",
			"6": "<sup>6</sup> ಇದಲ್ಲದೆ ನಿನ್ನ ಆಯುಷ್ಯಕ್ಕೆ ಹದಿನೈದು ವರುಷಗಳನ್ನು ಕೂಡಿಸುತ್ತೇನೆ. ನಿನ್ನನ್ನೂ ಈ ಪಟ್ಟಣವನ್ನೂ ಅಶ್ಶೂರದ ಅರಸನ ಕೈಗೆ ಬೀಳದಂತೆ ತಪ್ಪಿಸುವೆನು; ನನಗೋಸ್ಕರವಾಗಿಯೂ ನನ್ನ ಸೇವಕನಾದ ದಾವೀದನಿಗೋಸ್ಕರವಾಗಿಯೂ ಈ ಪಟ್ಟಣವನ್ನು ಉಳಿಸಿ ಕಾಪಾಡುವೆನು ಎಂಬದಾಗಿ ನಿನ್ನ ಪೂರ್ವಿಕನಾದ ದಾವೀದನ ದೇವರಾಗಿರುವ ಯೆಹೋವನು ಹೇಳುತ್ತಾನೆ ಎಂದು ತಿಳಿಸು ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"7": "<sup>7</sup> ಯೆಶಾಯನು ಒಂದು ಅಂಜೂರಹಣ್ಣಿನ ಉಂಡೆಯನ್ನು ತರಿಸಿ ಅದನ್ನು ಕುರುವಿನ ಮೇಲೆ ಇಡಿಸಿದಾಗ ಹಿಜ್ಕೀಯನು ಗುಣಹೊಂದಿದನು.",
			"8": "<sup>8</sup> ಹಿಜ್ಕೀಯನು ಯೆಶಾಯನನ್ನು - ಯೆಹೋವನು ನನ್ನನ್ನು ಗುಣಪಡಿಸುವನೆಂಬದಕ್ಕೂ ನಾನು ನಾಡದು ಆತನ ಆಲಯಕ್ಕೆ ಹೋಗುವೆನೆಂಬದಕ್ಕೂ ಗುರುತೇನು ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ ಅವನು -",
			"9": "<sup>9</sup> ಯೆಹೋವನು ನುಡಿದದ್ದನ್ನು ನೆರವೇರಿಸುವನು ಎಂಬದಕ್ಕೆ ಯೆಹೋವನಿಂದ ಗುರುತು ಕೇಳುತ್ತೀ; ಹಾಗಾದರೆ ನೆರಳು ಹತ್ತು ಮೆಟ್ಲು ಮುಂದೆ ಹೋಗಬೇಕೋ ಹಿಂದೆ ಬರಬೇಕೋ ಹೇಳು ಅಂದನು.",
			"10": "<sup>10</sup> ಹಿಜ್ಕೀಯನು - ನೆರಳು ಮುಂದೆ ಹೋಗುವದು ಸುಲಭ; ಆದದರಿಂದ ಹತ್ತು ಮೆಟ್ಲು ಹಿಂದೆ ಬರುವಂತೆ ಮಾಡು ಎಂದು ಹೇಳಿದನು.",
			"11": "<sup>11</sup> ಆಗ ಪ್ರವಾದಿಯಾದ ಯೆಶಾಯನು ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಡಲು ಆತನು ಆಹಾಜನ ಸೋಪಾನಪಂಕ್ತಿಯಲ್ಲಿ ಹತ್ತು ಮೆಟ್ಲು ಮುಂದೆ ಹೋಗಿದ್ದ ನೆರಳನ್ನು ಹಿಂದೆ ಬರಮಾಡಿದನು.",
			"12": "<sup>12</sup> ಅದೇ ಕಾಲದಲ್ಲಿ ಬಲದಾನನ ಮಗನೂ ಬಾಬೆಲಿನ ಅರಸನೂ ಆದ ಬೆರೋದಕಬಲದಾನನೆಂಬವನು ಹಿಜ್ಕೀಯನು ಅಸ್ವಸ್ಥನಾಗಿದ್ದನೆಂದು ಕೇಳಿ ದೂತರ ಮುಖಾಂತರವಾಗಿ ಅವನಿಗೆ ಪತ್ರವನ್ನೂ ಬಹುಮಾನವನ್ನೂ ಕಳುಹಿಸಿದನು.",
			"13": "<sup>13</sup> ಹಿಜ್ಕೀಯನು ಬಂದಂಥ ದೂತರ ಮಾತನ್ನು ಕೇಳಿ ಅವರಿಗೆ ಬೆಳ್ಳಿ ಬಂಗಾರ ಸುಗಂಧದ್ರವ್ಯ ಪರಿಮಳತೈಲ ಮೊದಲಾದ ಪದಾರ್ಥಗಳಿರುವ ಮನೆಯನ್ನೂ ಆಯುಧಶಾಲೆಯನ್ನೂ ತನ್ನ ಭಂಡಾರದಲ್ಲಿದ್ದದ್ದೆಲ್ಲವನ್ನೂ ತೋರಿಸಿದನು. ಅವನ ಅರಮನೆಯಲ್ಲಿಯೂ ರಾಜ್ಯದಲ್ಲಿಯೂ ಅವರಿಗೆ ತೋರಿಸದಿದ್ದ ವಸ್ತುವು ಒಂದೂ ಇರಲಿಲ್ಲ.",
			"14": "<sup>14</sup> ಆಗ ಪ್ರವಾದಿಯಾದ ಯೆಶಾಯನು ಹಿಜ್ಕೀಯನ ಬಳಿಗೆ ಬಂದು ಅವನನ್ನು - ಆ ಮನುಷ್ಯರು ಎಲ್ಲಿಯವರು, ಏನು ಹೇಳಿದರು ಎಂದು ಕೇಳಲು ಅವನು - ಅವರು ಬಹುದೂರ ದೇಶವಾದ ಬಾಬೆಲಿನಿಂದ ಬಂದವರು ಎಂದು ಉತ್ತರ ಕೊಟ್ಟನು.",
			"15": "<sup>15</sup> ಯೆಶಾಯನು ತಿರಿಗಿ ಅವನನ್ನು - ಅವರು ನಿನ್ನ ಅರಮನೆಯಲ್ಲಿ ಏನು ನೋಡಿದರು ಎಂದು ಕೇಳಲು ಅವನು - ಅರಮನೆಯಲ್ಲಿರುವದೆಲ್ಲವನ್ನೂ ನೋಡಿದರು. ನನ್ನ ಭಂಡಾರದಲ್ಲಿ ನಾನು ಅವರಿಗೆ ತೋರಿಸದಿದ್ದ ವಸ್ತುವು ಒಂದೂ ಇರುವದಿಲ್ಲ ಅಂದನು.",
			"16": "<sup>16</sup> ಆಗ ಅವನು ಹಿಜ್ಕೀಯನಿಗೆ - ಯೆಹೋವನ ಮಾತನ್ನು ಕೇಳು; ಆತನು -",
			"17": "<sup>17</sup> ನಿನ್ನ ಪೂರ್ವಿಕರ ಕಾಲದಿಂದ ಇಂದಿನವರೆಗೂ ಅರಮನೆಯಲ್ಲಿ ಸಂಗ್ರಹವಾದದ್ದೆಲ್ಲವೂ ಬಾಬೆಲಿಗೆ ಒಯ್ಯಲ್ಪಡುವ ದಿನ ಬರುವದು; ಇಲ್ಲೇನೂ ಉಳಿಯುವದಿಲ್ಲ.",
			"18": "<sup>18</sup> ಬಾಬೆಲಿನ ಅರಸರು ಬಂದು ನೀನು ಪಡೆದ ಮಕ್ಕಳನ್ನು ತೆಗೆದುಕೊಂಡುಹೋಗಿ ಅವರನ್ನು ತಮ್ಮ ಅರಮನೆಯಲ್ಲಿ ಕಂಚುಕಿಗಳನ್ನಾಗಿ ನೇಮಿಸಿಕೊಳ್ಳುವರು ಅನ್ನುತ್ತಾನೆ ಎಂದು ಹೇಳಿದನು.",
			"19": "<sup>19</sup> ನನ್ನ ಜೀವಮಾನದಲ್ಲಿ ಹೇಗೂ ತಪ್ಪದೆ ಸೌಭಾಗ್ಯವಿರುವದು ಅಂದುಕೊಂಡು ಹಿಜ್ಕೀಯನು ಯೆಶಾಯನಿಗೆ - ನೀನು ತಿಳಿಸಿದ ಯೆಹೋವನ ಮಾತು ಒಳ್ಳೇದಾಗಿದೆ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"20": "<sup>20</sup> ಹಿಜ್ಕೀಯನ ಉಳಿದ ಚರಿತ್ರೆಯೂ ಅವನ ಪರಾಕ್ರಮಕೃತ್ಯಗಳೂ ಅವನು ಕೆರೆಕಾಲುವೆಗಳನ್ನು ಮಾಡಿಸಿ ಊರೊಳಗೆ ನೀರನ್ನು ತಂದ ವಿವರವೂ ಯೆಹೂದರಾಜಕಾಲವೃತ್ತಾಂತ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"21": "<sup>21</sup> ಅವನು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಲು ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ಮನಸ್ಸೆಯು ಅರಸನಾದನು."
		},
		"21": {
			"1": "<sup>1</sup> ಮನಸ್ಸೆಯು ಅರಸನಾದಾಗ ಹನ್ನೆರಡು ವರುಷದವನಾಗಿದ್ದು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಐವತ್ತೈದು ವರುಷ ಆಳಿದನು; ಅವನ ತಾಯಿಯ ಹೆಸರು ಹೆಫ್ಚಿಬಾ.",
			"2": "<sup>2</sup> ಯೆಹೋವನು ಇಸ್ರಾಯೇಲ್ಯರ ಎದುರಿನಿಂದ ಹೊರಡಿಸಿಬಿಟ್ಟ ಅನ್ಯಜನಾಂಗಗಳ ಅಸಹ್ಯಕಾರ್ಯಗಳನ್ನು ಅವನು ಅನುಸರಿಸಿ ಆತನ ಚಿತ್ತಕ್ಕೆ ವಿರುದ್ಧವಾಗಿ ನಡೆದನು.",
			"3": "<sup>3</sup> ತನ್ನ ತಂದೆಯಾದ ಹಿಜ್ಕೀಯನು ತೆಗೆದುಹಾಕಿದ ಪೂಜಾಸ್ಥಾನಗಳನ್ನು ತಿರಿಗಿ ಸ್ಥಾಪಿಸಿ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾದ ಅಹಾಬನಂತೆ ಬಾಳನಿಗೋಸ್ಕರ ಯಜ್ಞವೇದಿಗಳನ್ನು ಕಟ್ಟಿಸಿ ಅಶೇರ ವಿಗ್ರಹಸ್ತಂಭವನ್ನು ನಿಲ್ಲಿಸಿ ನಕ್ಷತ್ರಮಂಡಲಕ್ಕೆ ಕೈಮುಗಿದು ಆರಾಧಿಸಿದನು.",
			"4": "<sup>4</sup> ಯೆಹೋವನು ತನ್ನ ಹೆಸರನ್ನು ಸ್ಥಾಪಿಸುವದಕ್ಕಾಗಿ ಆರಿಸಿಕೊಂಡ ಯೆರೂಸಲೇಮಿನ ದೇವಾಲಯದ ಎರಡು ಪ್ರಾಕಾರಗಳಲ್ಲಿಯೂ",
			"5": "<sup>5</sup> ಸರ್ವನಕ್ಷತ್ರಮಂಡಲಕ್ಕೋಸ್ಕರ ಯಜ್ಞವೇದಿಗಳನ್ನು ಕಟ್ಟಿಸಿದನು.",
			"6": "<sup>6</sup> ಇದಲ್ಲದೆ ಅವನು ತನ್ನ ಮಗನನ್ನು ಆಹುತಿಕೊಟ್ಟನು. ಕಣಿಹೇಳಿಸುವದು, ಯಂತ್ರಮಂತ್ರಗಳನ್ನು ಮಾಡಿಸುವದು, ಸತ್ತವರಲ್ಲಿ ವಿಚಾರಿಸುವವರ ಮತ್ತು ಬೈತಾಳಿಕರ ಬಳಿಕೆ ಮಾಡುವದು ಇವೇ ಮೊದಲಾದ ದುಷ್ಕೃತ್ಯಗಳಿಂದ ಯೆಹೋವನಿಗೆ ಕೋಪವನ್ನೆಬ್ಬಿಸಿದನು.",
			"7": "<sup>7</sup> ತಾನು ಮಾಡಿಸಿದ ಅಶೇರವಿಗ್ರಹಸ್ತಂಭವನ್ನು ದೇವಾಲಯದಲ್ಲಿ ಇಡಿಸಿದನು. ಯೆಹೋವನು ಆ ಆಲಯದ ವಿಷಯದಲ್ಲಿ ದಾವೀದನಿಗೂ ಅವನ ಮಗನಾದ ಸೊಲೊಮೋನನಿಗೂ - ಇಸ್ರಾಯೇಲ್ಯರು ತಮಗೆ ಮೋಶೆಯ ಮುಖಾಂತರವಾಗಿ ಕೊಡಲ್ಪಟ್ಟ ನನ್ನ ಧರ್ಮಶಾಸ್ತ್ರದ ನಿಬಂಧನೆಗಳನ್ನು ಅನುಸರಿಸಿ ನಡೆಯುವದಾದರೆ",
			"8": "<sup>8</sup> ನನ್ನ ನಾಮ ಮಹತ್ತು ಈ ದೇವಾಲಯದಲ್ಲಿಯೂ ಇಸ್ರಾಯೇಲ್ಯರ ಎಲ್ಲಾ ಊರುಗಳಲ್ಲಿ ನನಗೆ ಇಷ್ಟವಾಗಿರುವ ಯೆರೂಸಲೇಮಿನಲ್ಲಿಯೂ ಸದಾಕಾಲವಿರುವದು. ಇಸ್ರಾಯೇಲ್ಯರು ಇನ್ನು ಮುಂದೆ ದೇಶಭ್ರಷ್ಟರಾಗಿ ಅಲೆದಾಡದೆ ತಮ್ಮ ಪಿತೃಗಳಿಗೆ ಕೊಡಲ್ಪಟ್ಟ ದೇಶದಲ್ಲಿಯೇ ವಾಸವಾಗಿರುವಂತೆ ಮಾಡುವೆನು ಎಂದು ಹೇಳಿದ್ದನು.",
			"9": "<sup>9</sup> ಆದರೆ ಇಸ್ರಾಯೇಲ್ಯರು ಆತನ ಮಾತನ್ನು ಕೇಳದೆ ಮನಸ್ಸೆಯಿಂದ ಪ್ರೇರಿತರಾಗಿ ತಮ್ಮ ಮುಂದೆಯೇ ಯೆಹೋವನಿಂದ ಸಂಹೃತರಾದ ಅನ್ಯಜನಾಂಗಗಳಿಗಿಂತಲೂ ದುಷ್ಟರಾದರು.",
			"10": "<sup>10</sup> ಆಗ ಯೆಹೋವನು ತನ್ನ ಸೇವಕರಾದ ಪ್ರವಾದಿಗಳ ಮುಖಾಂತರವಾಗಿ -",
			"11": "<sup>11</sup> ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಮನಸ್ಸೆಯು ಮುಂಚೆ ಇದ್ದ ಅಮೋರಿಯರ ಕೃತ್ಯಗಳಿಗಿಂತ ಅಸಹ್ಯವಾದ ಕೃತ್ಯಗಳನ್ನು ನಡಿಸಿದದರಿಂದಲೂ ವಿಗ್ರಹಗಳಿಂದ ಯೆಹೂದ್ಯರನ್ನು ಪಾಪಕ್ಕೆ ಪ್ರೇರಿಸಿದದರಿಂದಲೂ",
			"12": "<sup>12</sup> ಇಸ್ರಾಯೇಲ್‍ದೇವರಾದ ಯೆಹೋವನೆಂಬ ನಾನು ಯೆರೂಸಲೇಮಿನವರ ಮೇಲೆಯೂ ಯೆಹೂದದವರ ಮೇಲೆಯೂ ಕೇಡನ್ನು ಬರಮಾಡುವೆನು; ಅದನ್ನು ಕೇಳುವವರ ಎರಡು ಕಿವಿಗಳೂ ಮೊರ್ರೆನ್ನುವವು.",
			"13": "<sup>13</sup> ನೂಲು ಮಟ್ಟಗೋಲು ಇವುಗಳಿಂದಲೋ ಎಂಬಂತೆ ಸಮಾರ್ಯವನ್ನೂ ಅಹಾಬನ ಮನೆಯನ್ನೂ ನೆಲಸಮಮಾಡಿಬಿಟ್ಟೆನಲ್ಲಾ; ಯೆರೂಸಲೇಮನ್ನೂ ಅವುಗಳಂತೆಯೇ ಮಾಡುವೆನು. ಒಬ್ಬನು ಪಾತ್ರೆಯನ್ನು ಒರಸಿ ಡಬ್ಬುಹಾಕುವ ಮೇರೆಗೆ ನಾನು ಯೆರೂಸಲೇಮನ್ನು ಒರಸಿ ಡಬ್ಬು ಹಾಕುವೆನು.",
			"14": "<sup>14</sup> ನನ್ನ ಪ್ರಜೆಗಳಲ್ಲಿ ಉಳಿದಿರುವವರನ್ನು ತಳ್ಳಿಬಿಟ್ಟು ಶತ್ರುಗಳ ಕೈಗೆ ಒಪ್ಪಿಸುವೆನು. ಅವರ ಎಲ್ಲಾ ಶತ್ರುಗಳು ಬಂದು ಅವರನ್ನು ಸುಲಿದು ಸೂರೆಮಾಡುವರು.",
			"15": "<sup>15</sup> ಅವರು ಐಗುಪ್ತದೇಶವನ್ನು ಬಿಟ್ಟು ಬಂದ ಪಿತೃಗಳ ಕಾಲ ಮೊದಲುಗೊಂಡು ಇಂದಿನವರೆಗೂ ತಮ್ಮ ಪಾಪಗಳಿಂದ ನನ್ನನ್ನು ರೇಗಿಸುತ್ತಾ ಬಂದದ್ದೇ ಇದಕ್ಕೆ ಕಾರಣ ಎಂದು ಹೇಳಿಸಿದನು.",
			"16": "<sup>16</sup> ಮನಸ್ಸೆಯು ಯೆಹೂದ್ಯರನ್ನು ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ಕೆಟ್ಟದ್ದಾಗಿರುವದನ್ನು ಮಾಡ ಪ್ರೇರಿಸಿದ್ದಲ್ಲದೆ ಯೆರೂಸಲೇಮನ್ನೆಲ್ಲಾ ನಿರಪರಾಧ ರಕ್ತದಿಂದ ತುಂಬಿಸಿದನು.",
			"17": "<sup>17</sup> ಮನಸ್ಸೆಯ ಉಳಿದ ಚರಿತ್ರೆಯೂ ಅವನ ಎಲ್ಲಾ ಕ್ರಿಯೆಗಳೂ ಅವನು ಮಾಡಿದ ಪಾಪಕೃತ್ಯಗಳೂ ಯೆಹೂದರಾಜಕಾಲವೃತ್ತಾಂತ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"18": "<sup>18</sup> ಅವನು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಲು ಅವನ ಶವವನ್ನು ಅವನ ಅರಮನೆಯಿರುವ ಉಜ್ಜನ ವನದಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ಆಮೋನನು ಅರಸನಾದನು.",
			"19": "<sup>19</sup> ಆಮೋನನು ಅರಸನಾದಾಗ ಇಪ್ಪತ್ತೆರಡು ವರುಷದವನಾಗಿದ್ದು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಎರಡು ವರುಷ ಆಳಿದನು. ಯೊಟ್ಟಾ ಊರಿನವಳೂ ಹಾರೂಚನ ಮಗಳೂ ಆದ ಮೆಷುಲ್ಲೆಮೆತ್ ಎಂಬಾಕೆಯು ಅವನ ತಾಯಿ.",
			"20": "<sup>20</sup> ಅವನು ತನ್ನ ತಂದೆಯಾದ ಮನಸ್ಸೆಯ ಮಾರ್ಗದಲ್ಲಿಯೇ ನಡೆದು ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾದನು.",
			"21": "<sup>21</sup> ತನ್ನ ಪೂರ್ವಿಕರ ದೇವರಾದ ಯೆಹೋವನನ್ನೂ ಆತನು ಮೆಚ್ಚುವ ಮಾರ್ಗವನ್ನೂ ಬಿಟ್ಟು",
			"22": "<sup>22</sup> ತನ್ನ ತಂದೆಯ ಮಾರ್ಗದಲ್ಲಿ ನಡೆದು ಅವನು ಪೂಜಿಸಿದ ವಿಗ್ರಹಗಳನ್ನು ತಾನೂ ಪೂಜಿಸಿ ಅವುಗಳಿಗೆ ಅಡ್ಡಬಿದ್ದನು.",
			"23": "<sup>23</sup> ಅವನ ಸೇವಕರು ಅವನಿಗೆ ವಿರೋಧವಾಗಿ ಒಳಸಂಚುಮಾಡಿ ಅವನನ್ನು ಅರಮನೆಯಲ್ಲಿಯೇ ಕೊಂದರು.",
			"24": "<sup>24</sup> ಆದರೆ ದೇಶದ ಜನರು ಒಳಸಂಚುಮಾಡಿದವರನ್ನೆಲ್ಲಾ ಕೊಂದುಹಾಕಿ ಆಮೋನನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ಯೋಷೀಯನನ್ನು ಅರಸನನ್ನಾಗಿ ಮಾಡಿದರು.",
			"25": "<sup>25</sup> ಆಮೋನನ ಉಳಿದ ಚರಿತ್ರೆಯೂ ಅವನ ಕೃತ್ಯಗಳೂ ಯೆಹೂದರಾಜಕಾಲವೃತ್ತಾಂತ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"26": "<sup>26</sup> ಅವನ ಶವವನ್ನು ಉಜ್ಜನ ವನದಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಅಲ್ಲಿ ಅವನು ಮೊದಲೇ ತನಗೋಸ್ಕರ ಒಂದು ಸಮಾಧಿಯನ್ನು ಸಿದ್ಧಪಡಿಸಿಟ್ಟಿದ್ದನು. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ಯೋಷೀಯನು ಅರಸನಾದನು."
		},
		"22": {
			"1": "<sup>1</sup> ಯೋಷೀಯನು ಅರಸನಾದಾಗ ಎಂಟು ವರುಷದವನಾಗಿದ್ದು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಮೂವತ್ತೊಂದು ವರುಷ ಆಳಿದನು. ಬೊಚ್ಕತೂರಿನವಳೂ ಅದಾಯನ ಮಗಳೂ ಆದ ಯೆದೀದಾ ಎಂಬಾಕೆಯು ಅವನ ತಾಯಿ.",
			"2": "<sup>2</sup> ಅವನು ಎಡಕ್ಕಾಗಲಿ ಬಲಕ್ಕಾಗಲಿ ತಿರುಗಿಕೊಳ್ಳದೆ ತನ್ನ ಪೂರ್ವಿಕನಾದ ದಾವೀದನ ಮಾರ್ಗದಲ್ಲಿಯೇ ನಡೆದು ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ಒಳ್ಳೆಯವನಾದನು.",
			"3": "<sup>3</sup> ಯೋಷೀಯನು ತನ್ನ ಆಳಿಕೆಯ ಹದಿನೆಂಟನೆಯ ವರುಷದಲ್ಲಿ ಅಚಲ್ಯನ ಮಗನೂ ಮೆಷುಲ್ಲಾಮನ ಮೊಮ್ಮಗನೂ ಆದ ಶಾಫಾನನೆಂಬ ಲೇಖಕನನ್ನು ಕರೆದು ಅವನಿಗೆ -",
			"4": "<sup>4</sup> ನೀನು ಮಹಾಯಾಜಕನಾದ ಹಿಲ್ಕೀಯನ ಬಳಿಗೆ ಹೋಗಿ ಅವನಿಗೆ - ದ್ವಾರಪಾಲಕರು ಯೆಹೋವನ ಆಲಯಕ್ಕೆ ಬರುವ ಜನರಿಂದ ಕೂಡಿಸಿದ ಹಣವನ್ನು ಲೆಕ್ಕಿಸಿ",
			"5": "<sup>5</sup> ಆಲಯದ ಕೆಲಸಮಾಡಿಸುವ ಮೊಕ್ತೇಸರರಿಗೆ ಅದನ್ನು ಒಪ್ಪಿಸು ಎಂದು ಹೇಳು.",
			"6": "<sup>6</sup> ಮೊಕ್ತೇಸರರು ಅದನ್ನು ಯೆಹೋವನ ಆಲಯದ ಜೀರ್ಣೋದ್ಧಾರಮಾಡುವ ಬಡಗಿ ಶಿಲ್ಪಿ ಉಪ್ಪಾರ ಮೊದಲಾದವರ ಸಂಬಳಕ್ಕಾಗಿಯೂ ದೇವಾಲಯಕ್ಕೆ ಬೇಕಾದ ಮರ, ಕೆತ್ತಿದ ಕಲ್ಲು ಇವುಗಳನ್ನು ಕೊಂಡುಕೊಳ್ಳುವದಕ್ಕಾಗಿಯೂ ಉಪಯೋಗಿಸಲಿ.",
			"7": "<sup>7</sup> ಅವರು ನಂಬಿಗಸ್ತರಾಗಿರುವದರಿಂದ ಅವರ ವಶಕ್ಕೆ ಕೊಡಲ್ಪಡುವ ಹಣದ ಲೆಕ್ಕವನ್ನು ಕೇಳಬಾರದು ಎಂದು ಆಜ್ಞಾಪಿಸಿ ಅವನನ್ನು ಯೆಹೋವನ ಆಲಯಕ್ಕೆ ಕಳುಹಿಸಿದನು.",
			"8": "<sup>8</sup> ಮಹಾಯಾಜಕನಾದ ಹಿಲ್ಕೀಯನು ಲೇಖಕನಾದ ಶಾಫಾನನಿಗೆ - ಯೆಹೋವನ ಆಲಯದಲ್ಲಿ ನನಗೆ ಧರ್ಮೋಪದೇಶ ಗ್ರಂಥವು ಸಿಕ್ಕಿರುತ್ತದೆ ಎಂದು ಹೇಳಿ ಅದನ್ನು ಅವನ ಕೈಯಲ್ಲಿ ಕೊಟ್ಟನು; ಶಾಫಾನನು ಓದಿದನು.",
			"9": "<sup>9</sup> ಅನಂತರ ಅವನು ಹಿಂದಿರುಗಿ ಅರಸನ ಬಳಿಗೆ ಹೋಗಿ - ನಿನ್ನ ಸೇವಕರು ದೇವಾಲಯದಲ್ಲಿ ಸಂಗ್ರಹವಾದ ಹಣವನ್ನು ತೆಗೆದು ದೇವಾಲಯದ ಕೆಲಸವನ್ನು ಮಾಡಿಸುವ ಮೊಕ್ತೇಸರರಿಗೆ ಒಪ್ಪಿಸಿದರು;",
			"10": "<sup>10</sup> ಇದಲ್ಲದೆ ಯಾಜಕನಾದ ಹಿಲ್ಕೀಯನು ನನಗೆ ಒಂದು ಗ್ರಂಥವನ್ನು ಕೊಟ್ಟಿದ್ದಾನೆ ಎಂದು ಹೇಳಿ ಅದನ್ನು ಅರಸನ ಮುಂದೆ ಓದಿದನು.",
			"11": "<sup>11</sup> ಅರಸನು ಧರ್ಮೋಪದೇಶಗ್ರಂಥವಾಕ್ಯಗಳನ್ನು ಕೇಳಿದಾಗ",
			"12": "<sup>12</sup> ಬಟ್ಟೆಗಳನ್ನು ಹರಿದುಕೊಂಡು ಯಾಜಕನಾದ ಹಿಲ್ಕೀಯನು, ಶಾಫಾನನ ಮಗನಾದ ಅಹೀಕಾಮ್, ಮೀಕಾಯನ ಮಗನಾದ ಅಕ್ಬೋರ್, ಲೇಖಕನಾದ ಶಾಫಾನ್, ತನ್ನ ಹಸ್ತಕನಾದ ಅಸಾಯ ಎಂಬವರಿಗೆ -",
			"13": "<sup>13</sup> ನಮ್ಮ ಪೂರ್ವಿಕರು ನಮಗೆ ಸಿಕ್ಕಿರುವ ಈ ಗ್ರಂಥವಾಕ್ಯಗಳಿಗೆ ಕಿವಿಗೊಡದೆಯೂ ಅವುಗಳನ್ನು ಕೈಕೊಳ್ಳದೆಯೂ ಹೋದದರಿಂದ ನಾವು ಯೆಹೋವನ ಉಗ್ರಕೋಪಕ್ಕೆ ಪಾತ್ರರಾಗಿದ್ದೇವೆ. ಆದದರಿಂದ ನೀವು ನನಗೋಸ್ಕರವೂ ಜನರಿಗೋಸ್ಕರವೂ ಎಲ್ಲಾ ಯೆಹೂದ್ಯರಿಗೋಸ್ಕರವೂ ಯೆಹೋವನ ಬಳಿಗೆ ಹೋಗಿ ಈ ಗ್ರಂಥವಾಕ್ಯಗಳ ವಿಷಯವಾಗಿ ವಿಚಾರಿಸಿರಿ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"14": "<sup>14</sup> ಆಗ ಯಾಜಕನಾದ ಹಿಲ್ಕೀಯನು, ಅಹೀಕಾಮ್, ಅಕ್ಬೋರ್, ಶಾಫಾನ್, ಅಸಾಯ ಎಂಬವರು ಹುಲ್ದ ಎಂಬ ಪ್ರವಾದಿನಿಯ ಬಳಿಗೆ ಹೋಗಿ ಆಕೆಯ ಹತ್ತಿರ ವಿಚಾರಿಸಿದರು. ಹರ್ಹಸನ ಮೊಮ್ಮಗನೂ ತಿಕ್ವನ ಮಗನೂ ರಾಜ ವಸ್ತ್ರಾಗಾರದ ಅಧಿಪತಿಯೂ ಆಗಿದ್ದ ಶಲ್ಲೂಮನು ಈಕೆಯ ಗಂಡನು. ಈಕೆಯು ಯೆರೂಸಲೇಮಿನ ಎರಡನೆಯ ಕೇರಿಯಲ್ಲಿ ವಾಸವಾಗಿದ್ದಳು.",
			"15": "<sup>15</sup> ಹುಲ್ದಳು ಅವರಿಗೆ - ನಿಮ್ಮನ್ನು ಕಳುಹಿಸಿದವನಿಗೆ ಇಸ್ರಾಯೇಲ್‍ದೇವರಾದ ಯೆಹೋವನ ಮಾತುಗಳನ್ನು ತಿಳಿಸಿರಿ.",
			"16": "<sup>16</sup> ಆತನು - ಈ ದೇಶದ ಮೇಲೆಯೂ ಜನರ ಮೇಲೆಯೂ ಯೆಹೂದದ ಅರಸನು ಓದಿಸಿದ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುವ ಎಲ್ಲಾ ಶಿಕ್ಷೆಯನ್ನು ಬರಮಾಡುವೆನು.",
			"17": "<sup>17</sup> ಅವರು ನನ್ನನ್ನು ಬಿಟ್ಟು ಅನ್ಯದೇವತೆಗಳಿಗೆ ಧೂಪಸುಟ್ಟು ತಮ್ಮ ದುಷ್ಕೃತ್ಯಗಳಿಂದ ನನ್ನನ್ನು ರೇಗಿಸಿದ್ದರಿಂದ ನನ್ನ ಕೋಪಾಗ್ನಿಯು ಈ ದೇಶದ ಮೇಲೆ ಉರಿಯಹತ್ತಿ ಆರಿ ಹೋಗುವದೇ ಇಲ್ಲ ಅನ್ನುತ್ತಾನೆ.",
			"18": "<sup>18</sup> ತಾನು ಕೇಳಿದ ವಾಕ್ಯಗಳ ವಿಷಯವಾಗಿ ಯೆಹೋವನ ಬಳಿಯಲ್ಲಿ ವಿಚಾರಿಸುವದಕ್ಕೆ ನಿಮ್ಮನ್ನು ಕಳುಹಿಸಿದ ಯೆಹೂದದ ಅರಸನಿಗೆ ನೀವು ತಿಳಿಸಬೇಕಾದ ಇಸ್ರಾಯೇಲ್‍ದೇವರಾದ ಯೆಹೋವನ ಮಾತೇನಂದರೆ -",
			"19": "<sup>19</sup> ನಾನು ಈ ದೇಶವನ್ನೂ ನಿವಾಸಿಗಳನ್ನೂ ಶಾಪ ವಿಸ್ಮಯಗಳಿಗೆ ಗುರಿಮಾಡುವೆನೆಂಬದನ್ನು ನೀನು ಕೇಳಿದಾಗ ದುಃಖಪಟ್ಟು ನನ್ನ ಮುಂದೆ ತಗ್ಗಿಸಿಕೊಂಡದ್ದರಿಂದಲೂ ಬಟ್ಟೆಗಳನ್ನು ಹರಿದುಕೊಂಡು ಕಣ್ಣೀರು ಸುರಿಸಿದ್ದರಿಂದಲೂ ನಿನ್ನನ್ನು ಲಕ್ಷಿಸಿದೆನು.",
			"20": "<sup>20</sup> ನಾನು ಈ ದೇಶಕ್ಕೆ ಬರಮಾಡುವ ಶಿಕ್ಷೆಗಳಲ್ಲಿ ನೀನು ಒಂದನ್ನೂ ನೋಡದೆ ಸಮಾಧಾನದಿಂದ ಮೃತಿಹೊಂದಿ ಸಮಾಧಿಸೇರುವಂತೆ ಅನುಗ್ರಹಿಸುವೆನು ಎಂಬದೇ ಎಂದು ಹೇಳಿದಳು. ಅವರು ಹಿಂದಿರುಗಿ ಬಂದು ಅರಸನಿಗೆ ಆ ಮಾತುಗಳನ್ನು ತಿಳಿಸಿದರು."
		},
		"23": {
			"1": "<sup>1</sup> ಅನಂತರ ಅರಸನು ದೂತರ ಮುಖಾಂತರವಾಗಿ ಯೆರೂಸಲೇಮಿನ ಮತ್ತು ಯೆಹೂದ ಪ್ರಾಂತದ ಬೇರೆ ಎಲ್ಲಾ ಊರುಗಳ ಹಿರಿಯರನ್ನು ತನ್ನ ಬಳಿಗೆ ಕರಿಸಿ",
			"2": "<sup>2</sup> ಯೆರೂಸಲೇಮಿನವರನ್ನೂ ಬೇರೆ ಎಲ್ಲಾ ಯೆಹೂದ್ಯರನ್ನೂ ಯಾಜಕರು ಪ್ರವಾದಿಗಳು ಇವರನ್ನೂ ಕರಕೊಂಡು ಯೆಹೋವನ ಆಲಯಕ್ಕೆ ಹೋದನು; ಚಿಕ್ಕವರು ಮೊದಲುಗೊಂಡು ದೊಡ್ಡವರವರೆಗೆ ಎಲ್ಲರೂ ಅವನನ್ನು ಹಿಂಬಾಲಿಸಿದರು. ಅಲ್ಲಿ ಅವನು ಎಲ್ಲರಿಗೂ ಕೇಳಿಸುವಂತೆ ಯೆಹೋವನ ಆಲಯದಲ್ಲಿ ಸಿಕ್ಕಿದ ನಿಬಂಧನಗ್ರಂಥವನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ಓದಿಸಿ",
			"3": "<sup>3</sup> ಕಂಬದ ಬಳಿಯಲ್ಲಿ ನಿಂತು ತಾನು ಯೆಹೋವನ ಮಾರ್ಗದಲ್ಲಿ ನಡೆಯುವದಾಗಿಯೂ ಆತನ ಆಜ್ಞಾನಿಯಮವಿಧಿಗಳನ್ನು ಪೂರ್ಣಮನಸ್ಸಿನಿಂದಲೂ ಪೂರ್ಣಪ್ರಾಣದಿಂದಲೂ ಕೈಕೊಳ್ಳುವದಾಗಿಯೂ ನಿಬಂಧನಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುವ ಎಲ್ಲಾ ವಾಕ್ಯಗಳನ್ನು ನೆರವೇರಿಸುವದಾಗಿಯೂ ಯೆಹೋವನಿಗೆ ಪ್ರಮಾಣಮಾಡಿದನು. ಎಲ್ಲಾ ಜನರೂ ಹಾಗೆಯೇ ಪ್ರಮಾಣಮಾಡಿದರು.",
			"4": "<sup>4</sup> ತರುವಾಯ ಅವನು ಮಹಾಯಾಜಕನಾದ ಹಿಲ್ಕೀಯನು, ಎರಡನೆಯ ತರಗತಿಯ ಯಾಜಕರು, ದ್ವಾರಪಾಲಕರು ಇವರ ಮುಖಾಂತರವಾಗಿ ಬಾಳ್, ಅಶೇರ ಎಂಬ ದೇವತೆಗಳಿಗೋಸ್ಕರವೂ ಆಕಾಶ ಸೈನ್ಯಕ್ಕೋಸ್ಕರವೂ ಉಪಯೋಗಿಸುತ್ತಿದ್ದ ಎಲ್ಲಾ ಸಾಮಾನುಗಳನ್ನು ಯೆಹೋವನ ಆಲಯದಿಂದ ತರಿಸಿ ಯೆರೂಸಲೇಮಿನ ಹೊರಗಿರುವ ಕಿದ್ರೋನ್ ಬೈಲಿನಲ್ಲಿ ಅವುಗಳನ್ನು ಸುಡಿಸಿ ಬೂದಿಯನ್ನು ಬೇತೇಲಿಗೆ ಕಳುಹಿಸಿದನು.",
			"5": "<sup>5</sup> ಇದಲ್ಲದೆ ಯೆಹೂದ ಪ್ರಾಂತದ ಪಟ್ಟಣಗಳಲ್ಲಿಯೂ ಯೆರೂಸಲೇಮಿನ ಸುತ್ತಣ ಪ್ರದೇಶದಲ್ಲಿಯೂ ಇದ್ದ ಪೂಜಾಸ್ಥಳಗಳ ವಿಗ್ರಹಗಳಿಗೂ ಬಾಳನಿಗೂ ಸೂರ್ಯಚಂದ್ರನಕ್ಷತ್ರಗಳೆನಿಸಿಕೊಳ್ಳುವ ಆಕಾಶಸೈನ್ಯಕ್ಕೂ ಧೂಪಸುಡುವದಕ್ಕಾಗಿ ಯೆಹೂದರಾಜರಿಂದ ನೇಮಿಸಲ್ಪಟ್ಟ ಎಲ್ಲಾ ಪೂಜಾರಿಗಳನ್ನು ತೆಗೆದುಹಾಕಿದನು.",
			"6": "<sup>6</sup> ಯೆಹೋವನ ಆಲಯದಲ್ಲಿದ್ದ ಅಶೇರವಿಗ್ರಹಸ್ತಂಭವನ್ನು ತರಿಸಿ ಯೆರೂಸಲೇಮಿನ ಹೊರಗಿರುವ ಕಿದ್ರೋನ್‍ಹಳ್ಳಕ್ಕೆ ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ಅಲ್ಲಿ ಅದನ್ನು ಸುಡಿಸಿ ಪುಡಿಪುಡಿ ಮಾಡಿಸಿ ಆ ಪುಡಿಯನ್ನು ಕನಿಷ್ಠಜನರ ಶ್ಮಶಾನದಲ್ಲಿ ಹಾಕಿಸಿದನು.",
			"7": "<sup>7</sup> ಯೆಹೋವನ ಆಲಯದ ಪ್ರಾಕಾರದೊಳಗಿದ್ದ ದೇವದಾಸ ದೇವದಾಸಿಯರ ಮನೆಗಳನ್ನು ಕೆಡವಿಸಿದನು. ಆ ಮನೆಗಳಲ್ಲಿ ಸ್ತ್ರೀಯರು ಅಶೇರ ದೇವತೆಗೋಸ್ಕರ ಗುಡಾರಗಳನ್ನು ನೇಯುತ್ತಿದ್ದರು.",
			"8": "<sup>8</sup> ಅನಂತರ ಗೆಬದಿಂದ ಬೇರ್ಷೆಬದವರೆಗಿರುವ ಯೆಹೂದ ಪಟ್ಟಣಗಳ ಎಲ್ಲಾ ಯಾಜಕರನ್ನು ಕರಿಸಿಕೊಂಡು ಅವರು ಧೂಪಸುಡುತ್ತಿದ್ದ ಪೂಜಾಸ್ಥಳಗಳನ್ನು ಹೊಲೆಮಾಡಿದನು. ಪುರಾಧಿಕಾರಿಯಾದ ಯೆಹೋಶುವನ ಬಾಗಲಿನ ಎಡಗಡೆಯಲ್ಲಿದ್ದ ದ್ವಾರಗಳ ದೇವತೆಯ ಪೂಜಾಸ್ಥಳಗಳನ್ನು ಕೆಡವಿಬಿಟ್ಟನು.",
			"9": "<sup>9</sup> ಪೂಜಾಸ್ಥಳಗಳಿಂದ ಬಂದ ಯಾಜಕರಿಗೆ ಇತರ ಯಾಜಕರೊಡನೆ ಹುಳಿಯಿಲ್ಲದ ರೊಟ್ಟಿಗಳಲ್ಲಿ ಪಾಲುಸಿಕ್ಕಿದರೂ ಅವರಿಗೆ ಯೆರೂಸಲೇಮಿನಲ್ಲಿದ್ದ ಯೆಹೋವನ ಯಜ್ಞವೇದಿಯ ಸಮೀಪಕ್ಕೆ ಹೋಗುವದಕ್ಕೆ ಅಪ್ಪಣೆಯಿರಲಿಲ್ಲ.",
			"10": "<sup>10</sup> ತಮ್ಮ ಗಂಡುಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ಯಾರೂ ಮೋಲೆಕನಿಗೋಸ್ಕರ ಆಹುತಿ ಕೊಡದಂತೆ ಬೇನ್‍ಹಿನ್ನೋಮ್ ತಗ್ಗಿನಲ್ಲಿದ್ದ ತೋಫೆತ್ ಎಂಬ ಸ್ಥಳವನ್ನು ಹೊಲೆಮಾಡಿದನು.",
			"11": "<sup>11</sup> ಯೆಹೂದರಾಜರು ಸೂರ್ಯದೇವತೆಗೋಸ್ಕರ ಪ್ರತಿಷ್ಠಿಸಿದ ಕುದುರೆಗಳನ್ನು ತೆಗೆದುಹಾಕಿ ರಥಗಳನ್ನು ಸುಡಿಸಿಬಿಟ್ಟನು. ಅವು ಯೆಹೋವನ ಆಲಯದ ಹೆಬ್ಬಾಗಲಿನ ಹೊರಗೆ ಪರ್ವರೀಮೆಂಬ ಸ್ಥಳದಲ್ಲಿ ಕಂಚುಕಿಯಾದ ನಾತಾನ್ ಮೆಲೆಕನ ಕೋಣೆಯ ಹತ್ತಿರ ಇಡಲ್ಪಟ್ಟಿದ್ದವು.",
			"12": "<sup>12</sup> ಇದಲ್ಲದೆ ಯೆಹೂದ್ಯರ ಅರಸರು ದೇವಸ್ಥಾನದ ಮಾಳಿಗೆಯ ಮೇಲೆ ಆಹಾಜನ ಉಪ್ಪರಿಗೆಯ ಹತ್ತಿರ ಕಟ್ಟಿಸಿದ್ದ ಯಜ್ಞವೇದಿಗಳನ್ನೂ ಮನಸ್ಸೆಯು ಯೆಹೋವನ ಆಲಯದ ಎರಡು ಪ್ರಾಕಾರಗಳಲ್ಲಿ ಕಟ್ಟಿಸಿದ್ದ ಯಜ್ಞವೇದಿಗಳನ್ನೂ ಕೆಡವಿ ಪುಡಿಪುಡಿಮಾಡಿ ಅವುಗಳ ಧೂಳನ್ನು ಕಿದ್ರೋನ್‍ಹಳ್ಳದಲ್ಲಿ ಹಾಕಿಸಿದನು.",
			"13": "<sup>13</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾದ ಸೊಲೊಮೋನನು ಚೀದೋನ್ಯರ ಅಷ್ಟೋರೆತ್, ಮೋವಾಬ್ಯರ ಕೆಮೋಷ್, ಅಮ್ಮೋನಿಯರ ಮಿಲ್ಕೋಮ್ ಎಂಬ ಅಸಹ್ಯವಿಗ್ರಹಗಳಿಗೋಸ್ಕರ ಯೆರೂಸಲೇಮಿನ ಎದುರಿನಲ್ಲಿಯೂ ವಿಘ್ನಪರ್ವತದ ದಕ್ಷಿಣದಿಕ್ಕಿನಲ್ಲಿಯೂ ಸ್ಥಾಪಿಸಿದ ಪೂಜಾಸ್ಥಳಗಳನ್ನು ಇವನು ಹೊಲೆಮಾಡಿದನು.",
			"14": "<sup>14</sup> ಕಲ್ಲುಕಂಬಗಳನ್ನು ಒಡೆದುಹಾಕಿ ಅಶೇರ ವಿಗ್ರಹಸ್ತಂಭಗಳನ್ನು ಕಡಿದುಬಿಟ್ಟು ಅವುಗಳಿದ್ದ ಸ್ಥಳವನ್ನು ಮನುಷ್ಯರ ಎಲುಬುಗಳಿಂದ ತುಂಬಿಸಿದನು.",
			"15": "<sup>15</sup> ಇದಲ್ಲದೆ ಅವನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಪಾಪಕ್ಕೆ ಪ್ರೇರಿಸಿದ ನೆಬಾಟನ ಮಗನಾದ ಯಾರೊಬ್ಬಾಮನು ಬೇತೇಲಿನಲ್ಲಿ ಏರ್ಪಡಿಸಿದ ಪೂಜಾಸ್ಥಳವನ್ನೂ ಅದರಲ್ಲಿದ್ದ ಯಜ್ಞವೇದಿಯನ್ನೂ ಕೆಡವಿಬಿಟ್ಟನು. ಪೂಜಾಸ್ಥಳವನ್ನೂ ಅದರಲ್ಲಿದ್ದ ಅಶೇರವಿಗ್ರಹಸ್ತಂಭವನ್ನೂ ಸುಟ್ಟು ಬೂದಿಮಾಡಿದನು.",
			"16": "<sup>16</sup> ಅವನು ಹಿಂದಿರುಗಿ ನೋಡಿದಾಗ ಅಲ್ಲಿನ ಗುಡ್ಡದಲ್ಲಿ ಸಮಾಧಿಗಳನ್ನು ಕಂಡು ಅವುಗಳಲ್ಲಿದ್ದ ಎಲುಬುಗಳನ್ನು ತರಿಸಿ ಆ ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಸುಡಿಸಿ ಅದನ್ನು ಹೊಲೆಮಾಡಿದನು; ಹೀಗೆ ದೇವರ ಮನುಷ್ಯನೊಬ್ಬನು ಪ್ರಕಟಿಸಿದ ಯೆಹೋವನ ವಾಕ್ಯವು ನೆರವೇರಿತು.",
			"17": "<sup>17</sup> ಇದಲ್ಲದೆ ಅವನು ಒಂದು ಸಮಾಧಿಶಿಲೆಯನ್ನು ಕಂಡು ಅದೇನೆಂದು ವಿಚಾರಿಸಲು ಆ ಊರಿನವರು - ನೀನು ಈಗ ಬೇತೇಲಿನ ಯಜ್ಞವೇದಿಗೆ ಮಾಡಿದ್ದನ್ನು ಮುಂತಿಳಿಸಿದ ಯೆಹೂದ ದೇಶದವನಾದ ದೇವರ ಮನುಷ್ಯನ ಸಮಾಧಿಯೆಂದು ಉತ್ತರಕೊಟ್ಟರು.",
			"18": "<sup>18</sup> ಆಗ ಅವನು - ಬಿಡಿರಿ, ಆ ಮನುಷ್ಯನ ಎಲುಬುಗಳನ್ನು ಯಾರೂ ಮುಟ್ಟಬಾರದು ಎಂದು ಹೇಳಿದನು. ಆದದರಿಂದ ಅವರು ಅವನ ಮತ್ತು ಸಮಾರ್ಯದೇಶದ ಪ್ರವಾದಿಯ ಎಲುಬುಗಳನ್ನು ಮುಟ್ಟಲಿಲ್ಲ.",
			"19": "<sup>19</sup> ಇಸ್ರಾಯೇಲ್ ರಾಜರು ಸಮಾರ್ಯದ ಪಟ್ಟಣಗಳಲ್ಲಿ ಕಟ್ಟಿಸಿದಂಥ ಯೆಹೋವನ ಕೋಪಕ್ಕೆ ಕಾರಣವಾಗಿದ್ದಂಥ ಪೂಜಾಸ್ಥಳಗಳ ಗುಡಿಗಳಿಗೆ ಬೇತೇಲಿಗಾದ ಗತಿಯೇ ಆಯಿತು.",
			"20": "<sup>20</sup> ಯೋಷೀಯನು ಅವುಗಳನ್ನು ಕೆಡವಿಬಿಟ್ಟು ಅವುಗಳ ಪೂಜಾರಿಗಳನ್ನು ಯಜ್ಞವೇದಿಗಳ ಮೇಲೆ ವಧಿಸಿ ಮನುಷ್ಯರ ಎಲುಬುಗಳನ್ನು ಸುಡಿಸಿ ಯೆರೂಸಲೇಮಿಗೆ ಹಿಂದಿರುಗಿ ಹೋದನು.",
			"21": "<sup>21</sup> ಅನಂತರ ಅರಸನು ಎಲ್ಲಾ ಜನರಿಗೆ - ಈ ನಿಬಂಧನಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುವ ಪ್ರಕಾರ ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೋಸ್ಕರ ಪಸ್ಕಹಬ್ಬವನ್ನು ಆಚರಿಸಬೇಕು ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"22": "<sup>22</sup> ಇಂಥ ಪಸ್ಕ ಹಬ್ಬವು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಪಾಲಿಸಿದ ನ್ಯಾಯಸ್ಥಾಪಕರ ಕಾಲದಲ್ಲಾಗಲಿ ಇಸ್ರಾಯೇಲ್ಯರ ಮತ್ತು ಯೆಹೂದ್ಯರ ಅರಸರ ಕಾಲದಲ್ಲಾಗಲಿ ನಡೆಯಲೇ ಇಲ್ಲ.",
			"23": "<sup>23</sup> ಈ ಹಬ್ಬವು ಅರಸನಾದ ಯೋಷೀಯನ ಆಳಿಕೆಯ ಹದಿನೆಂಟನೆಯ ವರುಷದಲ್ಲಿ ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಯೆಹೋವನಿಗೋಸ್ಕರ ಆಚರಿಸಲ್ಪಟ್ಟಿತು.",
			"24": "<sup>24</sup> ಇದಲ್ಲದೆ ಅವನು ಸತ್ತವರಲ್ಲಿ ವಿಚಾರಿಸುವವರನ್ನೂ ಬೈತಾಳಿಕರನ್ನೂ ಯೆರೂಸಲೇಮ್ ಯೆಹೂದಪ್ರಾಂತ ಇವುಗಳಲ್ಲಿದ್ದ ಎಲ್ಲಾ ತೆರಾಫೀಮ್ ಎಂಬ ಬೊಂಬೆಗಳನ್ನೂ ಮೂರ್ತಿಗಳನ್ನೂ ಎಲ್ಲಾ ಅಸಹ್ಯ ವಿಗ್ರಹಗಳನ್ನೂ ತೆಗೆದುಹಾಕಿ ಯಾಜಕನಾದ ಹಿಲ್ಕೀಯನಿಗೆ ಯೆಹೋವನ ಆಲಯದಲ್ಲಿ ಸಿಕ್ಕಿದ ಧರ್ಮೋಪದೇಶಗ್ರಂಥದ ವಾಕ್ಯಗಳನ್ನು ನೆರವೇರಿಸಿದನು.",
			"25": "<sup>25</sup> ಯೆಹೋವನ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡು ಪೂರ್ಣಮನಸ್ಸಿನಿಂದಲೂ ಪೂರ್ಣಪ್ರಾಣದಿಂದಲೂ ಪೂರ್ಣಬಲದಿಂದಲೂ ಮೋಶೆಯ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಅನುಸರಿಸಿದ ಯೋಷೀಯನಿಗೆ ಸಮಾನನಾದ ಅರಸನು ಮುಂಚೆಯೂ ತರುವಾಯವೂ ಇರಲಿಲ್ಲ.",
			"26": "<sup>26</sup> ಆದರೂ ಮನಸ್ಸೆಯ ದುಷ್ಕೃತ್ಯಗಳ ದೆಸೆಯಿಂದ ಯೆಹೂದ್ಯರ ಮೇಲಿದ್ದ ಯೆಹೋವನ ಉಗ್ರಕೋಪವು ಇಳಿಯಲಿಲ್ಲ.",
			"27": "<sup>27</sup> ಆತನು - ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ತೆಗೆದುಹಾಕಿದಂತೆ ಯೆಹೂದ್ಯರನ್ನೂ ನನ್ನ ಸನ್ನಿಧಿಯಿಂದ ತೆಗೆದುಹಾಕುವೆನು; ನಾನು ಆರಿಸಿಕೊಂಡ ಯೆರೂಸಲೇಮ್ ಪಟ್ಟಣವನ್ನೂ ನನ್ನ ನಾಮಮಹತ್ತಿಗೋಸ್ಕರ ಸ್ವೀಕರಿಸಿಕೊಂಡ ದೇವಾಲಯವನ್ನೂ ತಿರಸ್ಕರಿಸುವೆನು ಎಂದು ಹೇಳಿದನು.",
			"28": "<sup>28</sup> ಯೋಷೀಯನ ಉಳಿದ ಚರಿತ್ರೆಯೂ ಅವನ ಎಲ್ಲಾ ಕೃತ್ಯಗಳೂ ಯೆಹೂದ ರಾಜಕಾಲವೃತ್ತಾಂತ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"29": "<sup>29</sup> ಅವನ ಕಾಲದಲ್ಲಿ ಐಗುಪ್ತದ ಅರಸನಾದ ಫರೋಹ ನೆಕೋ ಎಂಬವನು ಅಶ್ಶೂರದ ಅರಸನಿಗೆ ವಿರೋಧವಾಗಿ ಯುದ್ಧಮಾಡುವದಕ್ಕೋಸ್ಕರ ಯೂಫ್ರೇಟೀಸ್ ನದಿಯ ಬಳಿಗೆ ಹೋದನು. ಅವನು ಅಲ್ಲಿಗೆ ಹೋಗುತ್ತಿರುವಾಗ ಯೋಷೀಯನು ಮೆಗಿದ್ದೋವಿನಲ್ಲಿ ಅವನನ್ನು ಎದುರುಗೊಂಡು ಕೂಡಲೆ ಅವನಿಂದ ಹತನಾದನು.",
			"30": "<sup>30</sup> ಅವನ ಸೇವಕರು ಅವನ ಶವವನ್ನು ರಥದಲ್ಲಿ ಹಾಕಿಕೊಂಡು ಮೆಗಿದ್ದೋವಿನಿಂದ ಯೆರೂಸಲೇಮಿಗೆ ತಂದು ಅವನ ಸ್ವಂತ ಸಮಾಧಿಯಲ್ಲಿ ಇಟ್ಟರು. ಜನರು ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ಯೆಹೋವಾಹಾಜನನ್ನು ಅಭಿಷೇಕಿಸಿ ಅರಸನನ್ನಾಗಿಮಾಡಿದರು.",
			"31": "<sup>31</sup> ಯೆಹೋವಾಹಾಜನು ಅರಸನಾದಾಗ ಇಪ್ಪತ್ತ ಮೂರು ವರುಷದವನಾಗಿದ್ದು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಮೂರು ತಿಂಗಳು ಆಳಿದನು. ಲಿಬ್ನದವಳೂ ಯೆರೆಮೀಯನ ಮಗಳೂ ಅದ ಹಮೂಟಲ್ ಎಂಬಾಕೆಯು ಇವನ ತಾಯಿ.",
			"32": "<sup>32</sup> ಇವನು ತನ್ನ ಪೂರ್ವಿಕರಂತೆ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾದನು.",
			"33": "<sup>33</sup> ಫರೋಹ ನೆಕೋವನು ಇವನನ್ನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಬಹು ದಿವಸಗಳವರೆಗೆ ಆಳಗೊಡಲಿಲ್ಲ. ಅವನು ಹಮಾತ್ ದೇಶದ ರಿಬ್ಲಾ ಎಂಬ ಊರಲ್ಲಿ ಇವನನ್ನು ಸೆರೆಯಲ್ಲಿಟ್ಟನು. ಯೆಹೂದ್ಯರು ಅವನಿಗೆ ನೂರು ತಲಾಂತು ಬೆಳ್ಳಿಯನ್ನೂ ಒಂದು ತಲಾಂತು ಬಂಗಾರವನ್ನೂ ದಂಡ ತೆರಬೇಕಾಯಿತು.",
			"34": "<sup>34</sup> ಫರೋಹ ನೆಕೋವನು ಯೋಷೀಯನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ಎಲ್ಯಾಕೀಮನನ್ನು ಅರಸನನ್ನಾಗಿ ನೇಮಿಸಿ ಅವನಿಗೆ ಯೆಹೋಯಾಕೀಮನೆಂಬ ಹೊಸ ಹೆಸರನ್ನಿಟ್ಟು ಯೆಹೋವಾಹಾಜನನ್ನು ಐಗುಪ್ತ ದೇಶಕ್ಕೆ ತೆಗೆದುಕೊಂಡು ಹೋದನು. ಯೆಹೋವಾಹಾಜನು ಅಲ್ಲಿ ಮೃತಿಹೊಂದಿದನು.",
			"35": "<sup>35</sup> ಫರೋಹನು ಗೊತ್ತುಮಾಡಿದಷ್ಟು ಬೆಳ್ಳಿಬಂಗಾರವನ್ನು ಕೊಡುವದಕ್ಕಾಗಿ ಯೆಹೋಯಾಕೀಮನು ತನ್ನ ದೇಶದಲ್ಲಿ ಕರವನ್ನು ನೇಮಿಸಿದನು. ಕರಕೊಡಬೇಕಾದ ಪ್ರತಿಯೊಬ್ಬನು ತನಗೆ ನೇಮಕವಾದಷ್ಟು ಬೆಳ್ಳಿ ಬಂಗಾರವನ್ನು ದೇಶದ ಜನರಿಂದ ಕೂಡಿಸಿ ಫರೋಹನಿಗೋಸ್ಕರ ತಂದೊಪ್ಪಿಸಿದನು.",
			"36": "<sup>36</sup> ಯೆಹೋಯಾಕೀಮನು ಅರಸನಾದಾಗ ಇಪ್ಪತ್ತೈದು ವರುಷದವನಾಗಿದ್ದು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಹನ್ನೊಂದು ವರುಷ ಆಳಿದನು. ರೂಮದವಳೂ ಪೆದಾಯನ ಮಗಳೂ ಆದ ಜೆಬೂದಾ ಎಂಬಾಕೆಯು ಅವನ ತಾಯಿ.",
			"37": "<sup>37</sup> ಅವನು ತನ್ನ ಪೂರ್ವಿಕರಂತೆ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾಗಿದ್ದನು."
		},
		"24": {
			"1": "<sup>1</sup> ಅವನ ಕಾಲದಲ್ಲಿ ಬಾಬೆಲಿನ ಅರಸನಾದ ನೆಬೂಕದ್ನೆಚ್ಚರನು ಬಂದನು; ಯೆಹೋಯಾಕೀಮನು ಅವನಿಗೆ ಅಧೀನನಾಗಿ ಮೂರು ವರುಷಗಳಾದನಂತರ ಅವನಿಗೆ ವಿರೋಧವಾಗಿ ತಿರುಗಿಬಿದ್ದನು.",
			"2": "<sup>2</sup> ಯೆಹೋವನು ತನ್ನ ಸೇವಕರಾದ ಪ್ರವಾದಿಗಳ ಮುಖಾಂತರವಾಗಿ ಮುಂತಿಳಿಸಿದ ಪ್ರಕಾರ ಯೆಹೂದರಾಜ್ಯವನ್ನು ಹಾಳುಮಾಡುವದಕ್ಕೋಸ್ಕರ ಕಸ್ದೀಯರ, ಅರಾಮ್ಯರ, ಮೋವಾಬ್ಯರ, ಅಮ್ಮೋನಿಯರ ಸುಲಿಗೆಯ ಗುಂಪುಗಳನ್ನು ಕಳುಹಿಸಿದನು.",
			"3": "<sup>3</sup> ಯೆರೂಸಲೇಮನ್ನು ನಿರಪರಾಧರಕ್ತದಿಂದ ತುಂಬಿಸಿದ ಮನಸ್ಸೆಯ ದುಷ್ಕೃತ್ಯಗಳನ್ನು ಯೆಹೋವನು ಕ್ಷಮಿಸದೆ ಹೋದನು;",
			"4": "<sup>4</sup> ಅವುಗಳಿಗಾಗಿ ಆತನು ಯೆಹೂದ್ಯರನ್ನು ತನ್ನ ಸನ್ನಿಧಿಯಿಂದ ತೆಗೆದುಹಾಕಬೇಕೆಂದಿದ್ದದರಿಂದ ಆತನ ಅಪ್ಪಣೆಯ ಪ್ರಕಾರ ಈ ಶಿಕ್ಷೆಯು ಅವರಿಗೆ ಆಯಿತು.",
			"5": "<sup>5</sup> ಯೆಹೋಯಾಕೀಮನ ಉಳಿದ ಚರಿತ್ರೆಯೂ ಅವನ ಎಲ್ಲಾ ಕೃತ್ಯಗಳೂ ಯೆಹೂದರಾಜಕಾಲವೃತ್ತಾಂತವೆಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"6": "<sup>6</sup> ಅವನು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಲು ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ಯೆಹೋಯಾಖೀನನು ಅರಸನಾದನು.",
			"7": "<sup>7</sup> ಬಾಬೆಲಿನ ಅರಸನು ಐಗುಪ್ತದ ಹಳ್ಳಕ್ಕೂ ಯೂಫ್ರೇಟೀಸ್ ನದಿಗೂ ಮಧ್ಯದಲಿದ್ದ ಐಗುಪ್ತ ರಾಜನ ಎಲ್ಲಾ ದೇಶವನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡನು. ಅಂದಿನಿಂದ ಐಗುಪ್ತದ ಅರಸನು ತನ್ನ ದೇಶದಿಂದ ಈಚೆಗೆ ಬರಲೇ ಇಲ್ಲ.",
			"8": "<sup>8</sup> ಯೆಹೋಯಾಖೀನನು ಅರಸನಾದಾಗ ಹದಿನೆಂಟು ವರುಷದವನಾಗಿದ್ದು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಮೂರು ತಿಂಗಳು ಆಳಿದನು. ಯೆರೂಸಲೇಮಿನ ಎಲ್ನಾತಾನನ ಮಗಳಾದ ನೆಹುಷ್ಟಾ ಎಂಬಾಕೆಯು ಇವನ ತಾಯಿ.",
			"9": "<sup>9</sup> ಇವನು ತನ್ನ ತಂದೆಯಂತೆ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾಗಿದ್ದನು.",
			"10": "<sup>10</sup> ಇವನ ಕಾಲದಲ್ಲಿ ಬಾಬೆಲಿನ ಅರಸನಾದ ನೆಬೂಕದ್ನೆಚ್ಚರನ ಸೈನ್ಯದವರು ಯೆರೂಸಲೇಮಿಗೆ ವಿರೋಧವಾಗಿ ಬಂದು ಅದಕ್ಕೆ ಮುತ್ತಿಗೆ ಹಾಕಿದರು.",
			"11": "<sup>11</sup> ಅಷ್ಟರಲ್ಲಿ ನೆಬೂಕದ್ನೆಚ್ಚರನೂ ಬಂದನು.",
			"12": "<sup>12</sup> ಆಗ ಯೆಹೂದದ ಅರಸನಾದ ಯೆಹೋಯಾಖೀನನು ತನ್ನ ತಾಯಿ ಪರಿವಾರದವರು ಸೇನಾಪತಿಗಳು ಕಂಚುಕಿಗಳು ಇವರೊಡನೆ ಅವನ ಬಳಿಗೆ ಹೋದನು; ಅವನು ಇವನನ್ನು ತನ್ನ ಆಳಿಕೆಯ ಎಂಟನೆಯ ವರುಷದಲ್ಲಿ ಸೆರೆಹಿಡಿದನು.",
			"13": "<sup>13</sup> ಇದಲ್ಲದೆ ಯೆಹೋವನು ಮುಂತಿಳಿಸಿದ ಪ್ರಕಾರ ಅವನು ಯೆಹೋವನ ಆಲಯದ ಮತ್ತು ಅರಮನೆಯ ಭಂಡಾರಗಳಲ್ಲಿದ್ದ ಎಲ್ಲಾ ದ್ರವ್ಯವನ್ನು ತೆಗೆದುಕೊಂಡುಹೋದನು. ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾದ ಸೊಲೊಮೋನನು ಯೆಹೋವನ ಆಲಯಕ್ಕಾಗಿ ಮಾಡಿಸಿದ್ದ ಬಂಗಾರದ ಎಲ್ಲಾ ಸಾಮಾನುಗಳನ್ನು ಮುರಿದುಬಿಟ್ಟನು.",
			"14": "<sup>14</sup> ಯೆರೂಸಲೇಮಿನಲ್ಲಿದ್ದ ಎಲ್ಲಾ ಪ್ರಭುಗಳನ್ನೂ ಭಟರನ್ನೂ ಕಮ್ಮಾರರು ಮೊದಲಾದ ಕೈಗಾರಿಕೆಯವರನ್ನೂ ಒಟ್ಟಾಗಿ ಹತ್ತು ಸಾವಿರ ಜನರನ್ನು ಸೆರೆಯೊಯ್ದನು. ದೇಶದ ಜನರಲ್ಲಿ ಕೇವಲ ಬಡವರು ಹೊರತಾಗಿ ಯಾರನ್ನೂ ಬಿಡಲಿಲ್ಲ.",
			"15": "<sup>15</sup> ಅವನು ಯೆಹೋಯಾಖೀನನನ್ನೂ ಅವನ ತಾಯಿ ಹೆಂಡತಿಯರು ಕಂಚುಕಿಗಳು",
			"16": "<sup>16</sup> ದೇಶದ ಪ್ರಧಾನಪುರುಷರು ಇವರನ್ನೂ ಏಳು ಸಾವಿರ ಮಂದಿ ಭಟರನ್ನೂ ಸಾವಿರ ಮಂದಿ ಕಮ್ಮಾರರು ಮೊದಲಾದ ಕೈಗಾರಿಕೆಯವರನ್ನೂ ಯೆರೂಸಲೇಮಿನಿಂದ ಬಾಬೆಲಿಗೆ ಒಯ್ದನು; ಅವರೆಲ್ಲರೂ ಪುಷ್ಟರೂ ರಣವೀರರೂ ಆಗಿದ್ದರು.",
			"17": "<sup>17</sup> ಬಾಬೆಲಿನ ಅರಸನು ಯೆಹೋಯಾಖೀನನಿಗೆ ಬದಲಾಗಿ ಅವನ ಚಿಕ್ಕಪ್ಪನಾದ ಮತ್ತನ್ಯ ಎಂಬವನನ್ನು ಅರಸನನ್ನಾಗಿ ನೇಮಿಸಿ ಅವನಿಗೆ ಚಿದ್ಕೀಯ ಎಂಬ ಹೆಸರಿಟ್ಟನು.",
			"18": "<sup>18</sup> ಚಿದ್ಕೀಯನು ತನ್ನ ಇಪ್ಪತ್ತೊಂದನೆಯ ವರುಷ ತುಂಬಿದ ಮೇಲೆ ಅರಸನಾಗಿ ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಹನ್ನೊಂದು ವರುಷ ಆಳಿದನು. ಲಿಬ್ನದವಳೂ ಯೆರೆಮಿಯಾನ ಮಗಳೂ ಆದ ಹಮೂಟಲ್ ಎಂಬಾಕೆಯು ಇವನ ತಾಯಿ.",
			"19": "<sup>19</sup> ಇವನು ಯೆಹೋಯಾಕೀಮನಂತೆ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾಗಿದ್ದನು.",
			"20": "<sup>20</sup> ಯೆಹೋವನು ಯೆರೂಸಲೇಮಿನವರ ಮೇಲೆಯೂ ಬೇರೆ ಎಲ್ಲಾ ಯೆಹೂದ್ಯರ ಮೇಲೆಯೂ ಇದನ್ನೆಲ್ಲಾ ಬರಮಾಡಿ ಕಡೆಯಲ್ಲಿ ಅವರನ್ನು ತನ್ನ ಸನ್ನಿಧಿಯಿಂದ ತಳ್ಳಿಬಿಡುವಷ್ಟು ರೋಷವುಳ್ಳವನಾದನು. ಚಿದ್ಕೀಯನು ಬಾಬೆಲಿನ ಅರಸನಾದ ನೆಬೂಕದ್ನೆಚ್ಚರನಿಗೆ ವಿರೋಧವಾಗಿ ತಿರುಗಿಬಿದ್ದದರಿಂದ ಅವನು ಚಿದ್ಕೀಯನ ಆಳಿಕೆಯ."
		},
		"25": {
			"1": "<sup>1</sup> ಒಂಭತ್ತನೆಯ ವರುಷದ ಹತ್ತನೆಯ ತಿಂಗಳಿನ ಹತ್ತನೆಯ ದಿನದಲ್ಲಿ ಸರ್ವಸೈನ್ಯಸಹಿತನಾಗಿ ಯೆರೂಸಲೇಮಿಗೆ ಬಂದು ಅಲ್ಲಿ ಪಾಳೆಯಮಾಡಿಕೊಂಡು ಅದರ ಸುತ್ತಲೂ ಮಣ್ಣಿನ ದಿಬ್ಬವನ್ನು ಮಾಡಿ",
			"2": "<sup>2</sup> ಚಿದ್ಕೀಯನ ಆಳಿಕೆಯ ಹನ್ನೊಂದನೆಯ ವರುಷದವರೆಗೆ ಅದಕ್ಕೆ ಮುತ್ತಿಗೆ ಹಾಕಿದನು.",
			"3": "<sup>3</sup> ಘೋರಕ್ಷಾಮದ ದೆಸೆಯಿಂದ ಪಟ್ಟಣದಲ್ಲಿರುವವರಿಗೆ ಆಹಾರಸಿಕ್ಕದೆ ಹೋಯಿತು. ಪಟ್ಟಣಕ್ಕೆ ಮುತ್ತಿಗೆ ಹಾಕಿದ್ದ ಕಸ್ದೀಯರು ಹನ್ನೊಂದನೆಯ ವರುಷದ ನಾಲ್ಕನೆಯ ತಿಂಗಳಿನ",
			"4": "<sup>4</sup> ಒಂಭತ್ತನೆಯ ದಿವಸ ಪೌಳಿಗೋಡೆಯಲ್ಲಿ ಒಂದು ದ್ವಾರವನ್ನು ಮಾಡಿದ್ದರಿಂದ ಒಳಗಿದ್ದ ಅರಸನೂ ಅವನ ಎಲ್ಲಾ ಸೈನಿಕರೂ ಅದೇ ರಾತ್ರಿಯಲ್ಲಿ ಅರಸನ ತೋಟದ ಬಳಿಯಲ್ಲಿರುವ ಬಾಗಲಿನಿಂದ ಓಡಿಹೋದರು; ಅದು ಎರಡು ಗೋಡೆಗಳ ಮಧ್ಯದಲ್ಲಿತ್ತು. ಅರಸನು ಅರಾಬಾ ಎಂಬ ತಗ್ಗಾದ ಪ್ರದೇಶದ ಮಾರ್ಗವಾಗಿ ಓಡಿಹೋಗುತ್ತಿರುವಾಗ",
			"5": "<sup>5</sup> ಕಸ್ದೀಯರ ಸೈನ್ಯದವರು ಅವನನ್ನು ಹಿಂದಟ್ಟಿ ಯೆರಿಕೋವಿನ ಬೈಲಿನಲ್ಲಿ ಹಿಡಿದರು. ಅಷ್ಟರಲ್ಲಿ ಅವನ ಎಲ್ಲಾ ಸೈನಿಕರು ಅವನನ್ನು ಬಿಟ್ಟು ಚದರಿಹೋಗಿದ್ದರು.",
			"6": "<sup>6</sup> ಅನಂತರ ಕಸ್ದೀಯರು ಅವನನ್ನು ರಿಬ್ಲದಲ್ಲಿದ್ದ ತಮ್ಮ ಅರಸನ ಬಳಿಗೆ ತೆಗೆದುಕೊಂಡು ಬಂದು ಅವನಿಗೆ ಶಿಕ್ಷೆಯನ್ನು ವಿಧಿಸಿದರು.",
			"7": "<sup>7</sup> ಅವನ ಇಬ್ಬರು ಮಕ್ಕಳನ್ನು ಅವನ ಎದುರಿನಲ್ಲಿಯೇ ವಧಿಸಿ ಅವನಿಗೆ ಬೇಡಿ ಹಾಕಿ ಎರಡು ಕಣ್ಣುಗಳನ್ನೂ ಕಿತ್ತು ಬಾಬೆಲಿಗೆ ತೆಗೆದುಕೊಂಡು ಹೋದರು.",
			"8": "<sup>8</sup> ಐದನೆಯ ತಿಂಗಳಿನ ಏಳನೆಯ ದಿನದಲ್ಲಿ ಅಂದರೆ ಬಾಬೆಲಿನ ಅರಸನಾದ ನೆಬೂಕದ್ನೆಚ್ಚರನ ಆಳಿಕೆಯ ಹತ್ತೊಂಭತ್ತನೆಯ ವರುಷದಲ್ಲಿ ಬಾಬೆಲಿನ ಅರಸನ ಸೇವಕನೂ ಕಾವಲುದಂಡಿನ ಅಧಿಪತಿಯೂ ಆಗಿದ್ದ ನೆಬೂಜರದಾನನೆಂಬವನು ಯೆರೂಸಲೇಮಿಗೆ ಬಂದು",
			"9": "<sup>9</sup> ಯೆಹೋವನ ಆಲಯವನ್ನೂ ಅರಮನೆಯನ್ನೂ ಯೆರೂಸಲೇಮಿನ ಎಲ್ಲಾ ದೊಡ್ಡ ಮನೆಗಳನ್ನೂ ಸುಟ್ಟುಬಿಟ್ಟನು.",
			"10": "<sup>10</sup> ಅವನ ಜೊತೆಯಲ್ಲಿ ಬಂದಿದ್ದ ಕಸ್ದೀಯ ಸೈನ್ಯದವರು ಯೆರೂಸಲೇಮಿನ ಸುತ್ತುಗೋಡೆಗಳನ್ನು ಕೆಡವಿದರು.",
			"11": "<sup>11</sup> ಕಾವಲುದಂಡಿನ ಅಧಿಪತಿಯಾದ ನೆಬೂಜರದಾನನು ಪಟ್ಟಣದಲ್ಲಿ ಉಳಿದವರನ್ನೂ ಮೊದಲೇ ಬಾಬೆಲಿನ ಅರಸನ ಮರೆಹೊಕ್ಕವರನ್ನೂ ಬೇರೆ ಎಲ್ಲಾ ಜನರನ್ನೂ ಸೆರೆಯೊಯ್ದನು.",
			"12": "<sup>12</sup> ಹೊಲಗಳನ್ನೂ ದ್ರಾಕ್ಷೇತೋಟಗಳನ್ನೂ ವ್ಯವಸಾಯಮಾಡುವದಕ್ಕಾಗಿ ದೇಶದ ಜನರಲ್ಲಿ ಕೇವಲ ಬಡವರನ್ನು ಮಾತ್ರ ಬಿಟ್ಟುಹೋದನು.",
			"13": "<sup>13</sup> ಕಸ್ದೀಯರು ಯೆಹೋವನ ಆಲಯದ ಬಳಿಯಲ್ಲಿದ್ದ ತಾಮ್ರದ ಕಂಬಗಳನ್ನೂ ಪೀಠಗಳನ್ನೂ ಸಮುದ್ರವೆನಿಸಿಕೊಂಡ ಪಾತ್ರೆಯನ್ನೂ ಒಡೆದು",
			"14": "<sup>14</sup> ಅವುಗಳ ತಾಮ್ರವನ್ನೂ ದೇವಾಲಯದ ಸೇವೆಗಾಗಿ ಉಪಯೋಗಿಸುತ್ತಿದ್ದ ಬೋಗುಣಿ ಸಲಿಕೆ ಕತ್ತರಿ ಧೂಪಾರತಿ ಮೊದಲಾದ ತಾಮ್ರದ ಸಾಮಾನುಗಳನ್ನೂ ಬಾಬೆಲಿಗೆ ತೆಗೆದುಕೊಂಡು ಹೋದರು.",
			"15": "<sup>15</sup> ಕಾವಲುದಂಡಿನ ಅಧಿಪತಿಯು ಬೆಳ್ಳಿಬಂಗಾರದ ಅಗ್ಗಿಷ್ಟಿಗೆ ಬಟ್ಟಲು ಮೊದಲಾದವುಗಳನ್ನು ತೆಗೆದುಕೊಂಡನು.",
			"16": "<sup>16</sup> ಸೊಲೊಮೋನನು ಯೆಹೋವನ ಆಲಯಕ್ಕೋಸ್ಕರ ಮಾಡಿಸಿದ ಎರಡು ಕಂಬಗಳು, ಸಮುದ್ರವೆನಿಸಿಕೊಂಡ ಪಾತ್ರೆಯು, ಪೀಠಗಳು, ಇವುಗಳಿಗೆ ಲೆಕ್ಕವಿಲ್ಲದಷ್ಟು ತಾಮ್ರ ಹಿಡಿದಿತ್ತು.",
			"17": "<sup>17</sup> ಪ್ರತಿಯೊಂದು ಕಂಬವು ಹದಿನೆಂಟು ಮೊಳ ಎತ್ತರವಿತ್ತು; ಪ್ರತಿಯೊಂದರ ಮೇಲೆ ಮೂರು ಮೊಳ ಎತ್ತರವಾದ ಒಂದು ತಾಮ್ರದ ಕುಂಭವಿತ್ತು. ಆ ಕುಂಭಗಳ ಮೇಲೆ ಸುತ್ತಲೂ ತಾಮ್ರದ ಜಾಲರಿಯೂ ದಾಳಿಂಬಹಣ್ಣುಗಳೂ ಇದ್ದವು.",
			"18": "<sup>18</sup> ಕಾವಲುದಂಡಿನ ಅಧಿಪತಿಯಾದ ನೆಬೂಜರದಾನನು ಹಿಡಿದುಕೊಂಡುಹೋದ ಜನರಲ್ಲಿ ಮಹಾಯಾಜಕನಾದ ಸೆರಾಯನು, ಎರಡನೆಯ ತರಗತಿಯ ಯಾಜಕನಾದ ಚೆಫನ್ಯನು,",
			"19": "<sup>19</sup> ಮೂರು ಮಂದಿ ದ್ವಾರಪಾಲಕರು, ಸೈನ್ಯ ಸಂಬಂಧವಾದ ಎಲ್ಲಾ ವ್ಯವಸ್ಥೆಯನ್ನು ಮಾಡುವ ಕಂಚುಕಿ, ಪಟ್ಟಣದಲ್ಲಿ ಉಳಿದಿದ್ದ ಅರಸನಿಗೆ ಆಪ್ತರಾಗಿದ್ದ ಐದು ಮಂದಿ ಮಂತ್ರಿಗಳು, ಯುದ್ಧಕ್ಕೆ ಹೋಗತಕ್ಕವರ ಪಟ್ಟಿಯನ್ನು ಮಾಡುವ ಸೇನಾಪತಿಯ ಲೇಖಕನು, ಪಟ್ಟಣದಲ್ಲಿದ್ದ ಅರುವತ್ತು ಮಂದಿ ರೈತರು ಇದ್ದರು.",
			"20": "<sup>20</sup> ಅವನು ಇವರನ್ನೆಲ್ಲಾ ತೆಗೆದುಕೊಂಡುಹೋಗಿ ಹಮಾತ್ ಪ್ರದೇಶದ ರಿಬ್ಲದಲ್ಲಿದ್ದ ಬಾಬೆಲಿನ ಅರಸನಿಗೆ ಒಪ್ಪಿಸಲು",
			"21": "<sup>21</sup> ಅವನು ಇವರನ್ನು ಅಲ್ಲಿಯೇ ಕೊಲ್ಲಿಸಿದನು. ಹೀಗೆ ಯೆಹೂದ್ಯರು ಸೆರೆಯವರಾಗಿ ತಮ್ಮ ದೇಶವನ್ನು ಬಿಟ್ಟು ಹೋಗಬೇಕಾಯಿತು.",
			"22": "<sup>22</sup> ಬಾಬೆಲಿನ ಅರಸನಾದ ನೆಬೂಕದ್ನೆಚ್ಚರನು ತಾನು ಯೆಹೂದದೇಶದಲ್ಲಿ ಉಳಿಸಿದ ಜನರ ಮೇಲೆ ಅಹೀಕಾಮನ ಮಗನೂ ಶಾಫಾನನ ಮೊಮ್ಮಗನೂ ಆದ ಗೆದಲ್ಯ ಎಂಬವನನ್ನು ಅಧಿಪತಿಯನ್ನಾಗಿ ನೇಮಿಸಿದನು.",
			"23": "<sup>23</sup> [ತಪ್ಪಿಸಿಕೊಂಡು ಉಳಿದಿದ್ದ] ಯೆಹೂದ ಸೇನಾಪತಿಗಳು ಗೆದಲ್ಯನು ಬಾಬೆಲಿನ ಅರಸನಿಂದ ಅಧಿಪತಿಯಾಗಿ ನೇಮಿಸಲ್ಪಟ್ಟನೆಂಬ ವರ್ತಮಾನವನ್ನು ಕೇಳಿ ತಮ್ಮ ಜನರೊಡನೆ ಮಿಚ್ಪದಲ್ಲಿ ವಾಸವಾಗಿದ್ದ ಅವನ ಬಳಿಗೆ ಹೋದರು. ಅವರು ಯಾರಂದರೆ - ನೆತನ್ಯನ ಮಗನಾದ ಇಷ್ಮಾಯೇಲನು, ಕಾರೇಹನ ಮಗನಾದ ಯೋಹಾನಾನ್, ನೆಟೋಫದವನೂ ತನ್ಹುಮೆತನ ಮಗನೂ ಆದ ಸೆರಾಯನು, ಮಾಕಾ ಊರಿನವನಾದ ಯಾಜನ್ಯನು ಇವರೇ.",
			"24": "<sup>24</sup> ಗೆದಲ್ಯನು ಇವರಿಗೂ ಇವರ ಜನರಿಗೂ - ಕಸ್ದೀಯರ ಸೈನ್ಯದವರಿಗೆ ಹೆದರಬೇಡಿರಿ; ಬಾಬೆಲಿನ ಅರಸನಿಗೆ ಒಳಗಾಗಿದ್ದು ಇಲ್ಲಿಯೇ ವಾಸಿಸಿರಿ; ಆಗ ನಿಮಗೆ ಸುಖವಾಗುವದು ಎಂದು ಆಣೆಯಿಟ್ಟು ಹೇಳಿದನು.",
			"25": "<sup>25</sup> ಆದರೆ ಏಳನೆಯ ತಿಂಗಳಿನಲ್ಲಿ ರಾಜವಂಶದವನೂ ಎಲೀಷಾಮನ ಮೊಮ್ಮಗನೂ ನೆತನ್ಯನ ಮಗನೂ ಆದ ಇಷ್ಮಾಯೇಲನು ಹತ್ತು ಮಂದಿ ಆಳುಗಳೊಡನೆ ಬಂದು ಗೆದಲ್ಯನನ್ನೂ ಅವನ ಸಂಗಡ ಮಿಚ್ಪದಲ್ಲಿದ್ದ ಯೆಹೂದ್ಯರನ್ನೂ ಕಸ್ದೀಯರನ್ನೂ ಸಂಹರಿಸಿದನು.",
			"26": "<sup>26</sup> ಆಗ ಎಲ್ಲಾ ಚಿಕ್ಕವರೂ ದೊಡ್ಡವರೂ ಸೇನಾಪತಿಗಳೂ ಕಸ್ದೀಯರಿಗೆ ಹೆದರಿ ಐಗುಪ್ತಕ್ಕೆ ಓಡಿಹೋದರು.",
			"27": "<sup>27</sup> ಎವೀಲ್ಮೆರೋದಕನು ತಾನು ಪಟ್ಟಕ್ಕೆ ಬಂದ ಮೊದಲನೆಯ ವರುಷದಲ್ಲಿ ಅಂದರೆ ಯೆಹೂದದ ಅರಸನಾದ ಯೆಹೋಯಾಖೀನನ ಸೆರೆಯ ಮೂವತ್ತೇಳನೆಯ ವರುಷದ ಹನ್ನೆರಡನೆಯ ತಿಂಗಳಿನ ಇಪ್ಪತ್ತೇಳನೆಯ ದಿನದಲ್ಲಿ ಯೆಹೋಯಾಖೀನನನ್ನು ಸೆರೆಮನೆಯಿಂದ ಬಿಡಿಸಿ ಅವನನ್ನು ಮೇಲಕ್ಕೆ ಎತ್ತಿದನು.",
			"28": "<sup>28</sup> ಅವನೊಡನೆ ಪ್ರೀತಿಯಿಂದ ಮಾತಾಡಿ ತನ್ನ ಸಂಗಡ ಬಾಬೆಲಿನಲ್ಲಿದ್ದ ಎಲ್ಲಾ ಅರಸುಗಳಲ್ಲಿ ಅವನಿಗೆ ಉನ್ನತಸ್ಥಾನವನ್ನು ಕೊಟ್ಟನು.",
			"29": "<sup>29</sup> ಯೆಹೋಯಾಖೀನನು ಸೆರೆಯ ಬಟ್ಟೆಗಳನ್ನು ತೆಗೆದುಹಾಕಿ ಜೀವದಿಂದಿರುವವರೆಗೂ ಅರಸನ ಪಂಕ್ತಿಯಲ್ಲಿ ಊಟ ಮಾಡಿದನು.",
			"30": "<sup>30</sup> ಅವನು ಬದುಕಿರುವವರೆಗೂ ಅವನಿಗೆ ಬೇಕಾಗುವ ಎಲ್ಲಾ ಪದಾರ್ಥಗಳು ಪ್ರತಿ ನಿತ್ಯವೂ ಅರಸನಿಂದಲೇ ದೊರಕುತ್ತಿದ್ದವು."
		}
	}
};
module.exports = book;