var book = {
	"name": "2 ಪೂರ್ವಕಾಲವೃತ್ತಾಂತ",
	"numChapters": 36,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ದಾವೀದನ ಮಗನಾದ ಸೊಲೊಮೋನನು ತನ್ನ ದೇವರಾದ ಯೆಹೋವನಿಂದ ಸಹಾಯವನ್ನೂ ವಿಶೇಷವೃದ್ಧಿಯನ್ನೂ ಹೊಂದಿದವನಾಗಿ ರಾಜ್ಯಾಧಿಕಾರವನ್ನು ಸ್ಥಿರಪಡಿಸಿಕೊಂಡನು.",
			"2": "<sup>2</sup> ಅವನು ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಅಂದರೆ ಅವರ ಶತಾಧಿಪತಿ ಸಹಸ್ರಾಧಿಪತಿಗಳನ್ನೂ ನ್ಯಾಯಾಧಿಪತಿಗಳನ್ನೂ ಗೋತ್ರಪ್ರಧಾನರಾದ ಇಸ್ರಾಯೇಲ್ ಪ್ರಭುಗಳನ್ನೂ ಕರಿಸಿಕೊಂಡು",
			"3": "<sup>3</sup> ಆ ಸರ್ವಸಮೂಹದೊಡನೆ ಗಿಬ್ಯೋನಿನಲ್ಲಿದ್ದ ಪೂಜಾಸ್ಥಳಕ್ಕೆ ಹೋದನು. ಯೆಹೋವನ ಸೇವಕನಾದ ಮೋಶೆಯು ಅರಣ್ಯದಲ್ಲಿ ಮಾಡಿಸಿದ ದೇವದರ್ಶನದ ಗುಡಾರವು ಅಲ್ಲಿತ್ತಷ್ಟೆ.",
			"4": "<sup>4</sup> ದಾವೀದನು ದೇವರ ಮಂಜೂಷವನ್ನು ಕಿರ್ಯತ್ಯಾರೀಮಿನಿಂದ ತಾನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಅದಕ್ಕೋಸ್ಕರ ಸಿದ್ಧಮಾಡಿಟ್ಟ ಡೇರೆಗೆ ತೆಗೆದುಕೊಂಡು ಬಂದಿದ್ದರೂ",
			"5": "<sup>5</sup> ಊರೀಯ ಮಗನೂ ಹೂರನ ಮೊಮ್ಮಗನೂ ಆದ ಬೆಚಲೇಲನು ಮಾಡಿದ ತಾಮ್ರಯಜ್ಞವೇದಿಯು ಅಲ್ಲೇ ಯೆಹೋವನ ಗುಡಾರದ ಮುಂದಿತ್ತು. ಆ ವೇದಿಗಾಗಿಯೇ ಸೊಲೊಮೋನನೂ ಆ ಸಮೂಹದವರೂ ಅಲ್ಲಿಗೆ ಹೋದರು.",
			"6": "<sup>6</sup> ಸೊಲೊಮೋನನು ದೇವದರ್ಶನದ ಗುಡಾರದ ಬಳಿಯಲ್ಲಿ ಯೆಹೋವನ ಮುಂದಿದ್ದ ಆ ತಾಮ್ರವೇದಿಯ ಮೇಲೆ ಸಾವಿರ ಸರ್ವಾಂಗಹೋಮಗಳನ್ನು ಸಮರ್ಪಿಸಿದನು.",
			"7": "<sup>7</sup> ಅದೇ ರಾತ್ರಿಯಲ್ಲಿ ದೇವರು ಸೊಲೊಮೋನನಿಗೆ ದರ್ಶನಕೊಟ್ಟು - ನಿನಗೆ ಯಾವ ವರ ಬೇಕು, ಕೇಳಿಕೋ ಎಂದು ಹೇಳಲು ಸೊಲೊಮೋನನು ಆತನಿಗೆ -",
			"8": "<sup>8</sup> ನೀನು ನನ್ನ ತಂದೆಯಾದ ದಾವೀದನಿಗೆ ಮಹಾಕೃಪೆಯನ್ನು ತೋರಿಸಿ ಅವನಿಗೆ ಬದಲಾಗಿ ನನ್ನನ್ನು ಅರಸನನ್ನಾಗಿ ನೇಮಿಸಿದ್ದೀ.",
			"9": "<sup>9</sup> ಹೀಗಿರಲಾಗಿ ದೇವರಾದ ಯೆಹೋವನೇ, ನೀನು ನನ್ನ ತಂದೆಯಾದ ದಾವೀದನಿಗೆ ನುಡಿದದ್ದು ಸಾರ್ಥಕವಾಗಲಿ; ಭೂಮಿಯ ಧೂಳಿನಷ್ಟು ಅಸಂಖ್ಯವಾದ ಜನಾಂಗದ ಮೇಲೆ ನನ್ನನ್ನು ಅರಸನನ್ನಾಗಿ ನೇಮಿಸಿದ್ದೀ.",
			"10": "<sup>10</sup> ನಿನ್ನ ಪ್ರಜೆಯಾದ ಈ ಮಹಾಜನಾಂಗವನ್ನು ಆಳುವದಕ್ಕೆ ಸಮರ್ಥರಾರು! ನಾನು ಈ ಜನರ ನಾಯಕನಾಗಿ ಹೋಗುತ್ತಾ ಬರುತ್ತಾ ಇರುವದಕ್ಕೋಸ್ಕರ ನನಗೆ ಜ್ಞಾನವಿವೇಕಗಳನ್ನು ಅನುಗ್ರಹಿಸಬೇಕು ಎಂದು ದೇವರನ್ನು ಬೇಡಿಕೊಂಡನು.",
			"11": "<sup>11</sup> ಆಗ ದೇವರು ಸೊಲೊಮೋನನಿಗೆ - ನೀನು ಘನಧನೈಶ್ವರ್ಯಗಳನ್ನಾಗಲಿ ವೈರಿಗಳ ಪ್ರಾಣಹರಣವನ್ನಾಗಲಿ ದೀರ್ಘಾಯುಷ್ಯವನ್ನಾಗಲಿ ಕೇಳಿಕೊಳ್ಳದೆ ನಾನು ಯಾರ ಮೇಲೆ ನಿನ್ನನ್ನು ಅರಸನನ್ನಾಗಿ ಮಾಡಿದೆನೋ ಆ ನನ್ನ ಪ್ರಜೆಗಳನ್ನು ಪಾಲಿಸುವದಕ್ಕೋಸ್ಕರ ಜ್ಞಾನವಿವೇಕಗಳ ಮೇಲೆ ಮನಸ್ಸಿಟ್ಟು ಅವುಗಳನ್ನು ಬೇಡಿಕೊಂಡದರಿಂದ ಅವು ನಿನಗೆ ದೊರಕುವವು.",
			"12": "<sup>12</sup> ಇದಲ್ಲದೆ ನಾನು ನಿನಗೆ ಘನಧನೈಶ್ವರ್ಯಗಳನ್ನೂ ಅನುಗ್ರಹಿಸುತ್ತೇನೆ. ಇಂಥ ಘನಧನೈಶ್ವರ್ಯಗಳು ನಿನಗಿಂತ ಮೊದಲಿದ್ದ ಅರಸರಲ್ಲಿ ಯಾರಿಗೂ ಇರಲಿಲ್ಲ, ನಿನ್ನ ಅನಂತರದವರಿಗೂ ಇರುವದಿಲ್ಲ ಅಂದನು.",
			"13": "<sup>13</sup> ಆಮೇಲೆ ಸೊಲೊಮೋನನು ಗಿಬ್ಯೋನಿನ ಪೂಜಾಸ್ಥಳದಲ್ಲಿದ್ದ ದೇವದರ್ಶನದ ಗುಡಾರದಿಂದ ಯೆರೂಸಲೇಮಿಗೆ ಬಂದು ಇಸ್ರಾಯೇಲ್ ರಾಜ್ಯವನ್ನು ಆಳಿದನು.",
			"14": "<sup>14</sup> ಅರಸನಾದ ಸೊಲೊಮೋನನು ರಥಗಳನ್ನೂ ರಾಹುತರನ್ನೂ ಸಂಗ್ರಹಿಸಿದನು. ಅವನ ರಥಗಳು ಸಾವಿರದ ನಾನೂರು, ರಾಹುತರು ಹನ್ನೆರಡು ಸಾವಿರ. ಇವುಗಳಲ್ಲಿ ಕೆಲವನ್ನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿಟ್ಟುಕೊಂಡು ಉಳಿದವುಗಳನ್ನು ರಥಗಳಿಗೋಸ್ಕರ ನೇಮಿಸಿದ ಪಟ್ಟಣಗಳಲ್ಲಿರಿಸಿದನು.",
			"15": "<sup>15</sup> ಅವನ ಮುಖಾಂತರವಾಗಿ ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಬೆಳ್ಳಿಬಂಗಾರವು ಕಲ್ಲಿನ ಹಾಗೂ ದೇವದಾರುಮರಗಳು ಇಳಕಲಿನ ಪ್ರದೇಶದಲ್ಲಿ ಬೆಳೆಯುವ ಅತ್ತಿಮರಗಳ ಹಾಗೂ ಹೇರಳವಾದವು.",
			"16": "<sup>16</sup> ಅರಸನಾದ ಸೊಲೊಮೋನನ ಕುದುರೆಗಳು ಐಗುಪ್ತದೇಶದವುಗಳು. ಅವನ ವರ್ತಕರು ಅವುಗಳನ್ನು ಹಿಂಡುಗಟ್ಟಳೆಯಾಗಿ ಕೊಂಡುಕೊಂಡು ಬರುತ್ತಿದ್ದರು.",
			"17": "<sup>17</sup> ರಥಕ್ಕೆ ಆರುನೂರು ರೂಪಾಯಿಗಳಂತೆಯೂ ಕುದುರೆಗೆ ನೂರೈವತ್ತು ರೂಪಾಯಿಗಳಂತೆಯೂ ಕೊಟ್ಟು ರಥಗಳನ್ನೂ ಕುದುರೆಗಳನ್ನೂ ಐಗುಪ್ತದೇಶದಿಂದ ತರಿಸುತ್ತಿದ್ದರು. ಹೀಗೆಯೇ ಹಿತ್ತಿಯರ ಮತ್ತು ಅರಾಮ್ಯರ ಎಲ್ಲಾ ಅರಸರು ಇವರ ಮುಖಾಂತರವಾಗಿಯೇ ತರಿಸುತ್ತಿದ್ದರು."
		},
		"2": {
			"1": "<sup>1</sup> ಸೊಲೊಮೋನನು ಯೆಹೋವನ ಹೆಸರಿಗೋಸ್ಕರ ಆಲಯವನ್ನೂ ತನಗೋಸ್ಕರ ಅರಮನೆಯನ್ನೂ ಕಟ್ಟಿಸಬೇಕೆಂದು ಮನಸ್ಸುಳ್ಳವನಾಗಿ",
			"2": "<sup>2</sup> ಎಪ್ಪತ್ತುಸಾವಿರ ಮಂದಿ ಹೊರೆಹೊರುವವರನ್ನೂ ಎಂಭತ್ತು ಸಾವಿರ ಮಂದಿ ಕಲ್ಲುಗಣಿಯಲ್ಲಿ ಕೆಲಸಮಾಡುವವರನ್ನೂ ಇವರ ಮೇಲ್ವಿಚಾರಣೆಗಾಗಿ ಮೂರು ಸಾವಿರದ ಆರು ನೂರು ಮಂದಿ ಮೇಸ್ತ್ರಿಗಳನ್ನೂ ಲೆಕ್ಕಿಸಿ ನೇಮಿಸಿದನು.",
			"3": "<sup>3</sup> ಇದಲ್ಲದೆ ಅವನು ದೂತರ ಮುಖಾಂತರವಾಗಿ ತೂರಿನ ಅರಸನಾದ ಹೂರಾಮನಿಗೆ - ನನ್ನ ತಂದೆಯಾದ ದಾವೀದನು ತನ್ನ ವಾಸಕ್ಕೋಸ್ಕರ ಅರಮನೆಯನ್ನು ಕಟ್ಟಿಸಿಕೊಳ್ಳುವದಕ್ಕಾಗಿ ನೀನು ಅವನಿಗೆ ದೇವದಾರುಮರಗಳನ್ನು ಕಳುಹಿಸಿದಿಯಲ್ಲಾ.",
			"4": "<sup>4</sup> ಈಗ ನಾನು ನನ್ನ ದೇವರಾದ ಯೆಹೋವನ ಹೆಸರಿಗಾಗಿ ಒಂದು ಆಲಯವನ್ನು ಕಟ್ಟಿಸಿ ಇಸ್ರಾಯೇಲ್ಯರಿಗಿರುವ ಶಾಶ್ವತನಿಯಮದ ಪ್ರಕಾರ ಆತನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸುಗಂಧದ್ರವ್ಯಗಳಿಂದ ಧೂಪಹಾಕುವದಕ್ಕೂ ಪ್ರತಿನಿತ್ಯ ನೈವೇದ್ಯದ ರೊಟ್ಟಿಗಳನ್ನಿಡುವದಕ್ಕೂ ಪ್ರಾತಃಕಾಲ, ಸಾಯಂಕಾಲ, ಸಬ್ಬತ್‍ದಿನ, ಅಮಾವಾಸ್ಯೆ, ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಜಾತ್ರೆ ಇವುಗಳಲ್ಲಿ ಸರ್ವಾಂಗ ಹೋಮವನ್ನರ್ಪಿಸುವದಕ್ಕೂ ಆ ಆಲಯವನ್ನು ಆತನಿಗೋಸ್ಕರ ಪ್ರತಿಷ್ಠಿಸಬೇಕೆಂದಿದ್ದೇನೆ.",
			"5": "<sup>5</sup> ನಮ್ಮ ದೇವರು ಎಲ್ಲಾ ದೇವರುಗಳಿಗಿಂತ ದೊಡ್ಡವನು; ನಾನು ಕಟ್ಟಿಸುವ ಆಲಯವೂ ದೊಡ್ಡದಾಗಿರಬೇಕು.",
			"6": "<sup>6</sup> ಆತನಿಗೋಸ್ಕರ ಆಲಯವನ್ನು ಕಟ್ಟುವದಕ್ಕೆ ಶಕ್ತರಾರು! ಆಕಾಶವೂ ಉನ್ನತೋನ್ನತವಾದ ಆಕಾಶವೂ ಆತನ ವಾಸಕ್ಕೆ ಸಾಲದಿರುವಲ್ಲಿ ಆತನಿಗೋಸ್ಕರ ಆಲಯವನ್ನು ಕಟ್ಟುವದಕ್ಕೆ ಶಕ್ತರಾರು! ನಾನು ಆತನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಧೂಪಹಾಕುವ ಉದ್ದೇಶದಿಂದ ಹೊರತು ಆತನ ವಾಸಕ್ಕಾಗಿ ಆಲಯವನ್ನು ಕಟ್ಟಿಸುವದಕ್ಕೆ ಎಷ್ಟರವನು!",
			"7": "<sup>7</sup> ಹೀಗಿರಲು ಬಂಗಾರ ಬೆಳ್ಳಿ ತಾಮ್ರ ಕಬ್ಬಿಣ ಇವುಗಳ ಕೆಲಸಮಾಡುವದರಲ್ಲಿಯೂ ಧೂಮ್ರ ರಕ್ತ ನೀಲವರ್ಣಗಳ ಬಟ್ಟೆಯನ್ನು ನೇಯುವದರಲ್ಲಿಯೂ ಚಿತ್ರ ಕೆತ್ತುವದರಲ್ಲಿಯೂ ಜಾಣನಾದ ಮನುಷ್ಯನನ್ನು ನನ್ನ ಬಳಿಗೆ ಕಳುಹಿಸು; ನನ್ನ ತಂದೆಯು ಗೊತ್ತು ಮಾಡಿದಂಥ ಮತ್ತು ನನ್ನೊಡನೆ ಯೆಹೂದದಲ್ಲಿಯೂ ಯೆರೂಸಲೇಮಿನಲ್ಲಿಯೂ ಇರುವಂಥ ಜಾಣರೊಂದಿಗೆ ಅವನು ಕೆಲಸಮಾಡಲಿ.",
			"8": "<sup>8</sup> ಮತ್ತು ಲೆಬನೋನಿನಿಂದ ದೇವದಾರುಮರವನ್ನೂ ತುರಾಯಿಮರವನ್ನೂ ಸುಗಂಧದಮರವನ್ನೂ ನನಗೆ ಕಳುಹಿಸಬೇಕು. ನಿನ್ನ ಆಳುಗಳು ಲೆಬನೋನಿನ ಮರಗಳನ್ನು ಕಡಿಯುವದರಲ್ಲಿ ಜಾಣರೆಂದು ನನಗೆ ಗೊತ್ತಿದೆ. ನಿನ್ನ ಆಳುಗಳ ಜೊತೆಯಲ್ಲಿ ನನ್ನ ಆಳುಗಳೂ ಇರುವರು.",
			"9": "<sup>9</sup> ನಾನು ದೊಡ್ಡದಾಗಿಯೂ ಸೋಜಿಗವಾಗಿಯೂ ಇರುವ ಆಲಯವನ್ನು ಕಟ್ಟಿಸುವದರಿಂದ ನನಗೋಸ್ಕರ ಹೆಚ್ಚು ಮರವನ್ನು ಸಿದ್ಧಪಡಿಸಬೇಕು.",
			"10": "<sup>10</sup> ಮರಕಡಿಯುವ ನಿನ್ನ ಆಳುಗಳಿಗಾಗಿ ಇಪ್ಪತ್ತು ಸಾವಿರ ಕೋರ್ ಗೋದಿ, ಇಪ್ಪತ್ತು ಸಾವಿರ ಕೋರ್ ಜವೆಗೋದಿ, ಇಪ್ಪತ್ತು ಸಾವಿರ ಬತ್ ದ್ರಾಕ್ಷಾರಸ, ಇಪ್ಪತ್ತು ಸಾವಿರ ಬತ್ ಎಣ್ಣೆ ಇವುಗಳನ್ನು ಕೊಡುವೆನೆಂದು ಹೇಳಿಸಿದನು.",
			"11": "<sup>11</sup> ತೂರಿನ ಅರಸನಾದ ಹೂರಾಮನು ಪತ್ರದ ಮೂಲಕವಾಗಿ ಸೊಲೊಮೋನನಿಗೆ - ಯೆಹೋವನು ತನ್ನ ಪ್ರಜೆಗಳನ್ನು ಪ್ರೀತಿಸುವವನಾಗಿರುವದರಿಂದ ನಿನ್ನನ್ನು ಅವರ ಅರಸನನ್ನಾಗಿ ನೇಮಿಸಿದ್ದಾನೆ.",
			"12": "<sup>12</sup> ಭೂಮ್ಯಾಕಾಶಗಳನ್ನುಂಟುಮಾಡಿದ ಇಸ್ರಾಯೇಲ್‍ದೇವರಾದ ಯೆಹೋವನು ತನಗೋಸ್ಕರ ದೇವಾಲಯವನ್ನೂ ರಾಜನಿಗೋಸ್ಕರ ಅರಮನೆಯನ್ನೂ ಕಟ್ಟಿಸತಕ್ಕ ಬುದ್ಧಿ ಜ್ಞಾನವಿವೇಕಗಳುಳ್ಳ ಮಗನನ್ನು ಅರಸನಾದ ದಾವೀದನಿಗೆ ಕೊಟ್ಟದರಿಂದ ಆತನಿಗೆ ಸ್ತೋತ್ರವಾಗಲಿ.",
			"13": "<sup>13</sup> ನಾನು ಈಗ ಜ್ಞಾನವಿವೇಕಗಳುಳ್ಳ ಪುರುಷನನ್ನು ಕಳುಹಿಸುತ್ತೇನೆ; ಅವನ ಹೆಸರು ಹೂರಾಮಾಬೀ.",
			"14": "<sup>14</sup> ಅವನು ದಾನ್‍ಕುಲದ ಸ್ತ್ರೀಯ ಮಗನು; ಅವನ ತಂದೆ ತೂರಿನವನು. ಅವನು ಬಂಗಾರ ಬೆಳ್ಳಿ ತಾಮ್ರ ಕಬ್ಬಿಣ ಕಲ್ಲು ಮರ ಇವುಗಳ ಕೆಲಸವನ್ನು ಮಾಡುವದಕ್ಕೂ ಧೂಮ್ರ ನೀಲರಕ್ತವರ್ಣಗಳುಳ್ಳ ನಾರುಬಟ್ಟೆಗಳನ್ನು ನೇಯುವದಕ್ಕೂ ಎಲ್ಲಾ ತರದ ಕೆತ್ತನೆಯ ಕೆಲಸವನ್ನು ಮಾಡುವದಕ್ಕೂ ತನಗೆ ಒಪ್ಪಿಸಲ್ಪಟ್ಟ ಎಲ್ಲಾ ಚಮತ್ಕಾರವಾದ ಕೆಲಸಗಳನ್ನು ನಡಿಸುವದಕ್ಕೂ ಸಮರ್ಥನು. ನೀನೂ ನಿನ್ನ ತಂದೆಯಾದ ದಾವೀದನೂ ಗೊತ್ತುಮಾಡಿದ ಜಾಣರೊಡನೆ ಅವನು ಕೆಲಸಮಾಡಲಿ.",
			"15": "<sup>15</sup> ಈಗ ನನ್ನ ಒಡೆಯನು ತಾನು ಮಾತುಕೊಟ್ಟ ಮೇರೆಗೆ ಗೋದಿ ಜವೆಗೋದಿ ಎಣ್ಣೆ ದ್ರಾಕ್ಷಾರಸ ಇವುಗಳನ್ನು ತನ್ನ ದಾಸರಿಗೆ ಕಳುಹಿಸಬೇಕು.",
			"16": "<sup>16</sup> ನಾವಾದರೋ ನಿನಗೆ ಬೇಕಾದ ಎಲ್ಲಾ ಮರಗಳನ್ನು ಲೆಬನೋನಿನಲ್ಲಿ ಕಡಿದು ತೆಪ್ಪಗಳಾಗಿ ಕಟ್ಟಿ ಸಮುದ್ರಮಾರ್ಗವಾಗಿ ಯೊಪ್ಪಕ್ಕೆ ಕಳುಹಿಸುವೆವು; ಅಲ್ಲಿಂದ ನೀನು ಅವುಗಳನ್ನು ಯೆರೂಸಲೇಮಿಗೆ ತರಿಸಿಕೊಳ್ಳಬಹುದು ಎಂಬದಾಗಿ ಉತ್ತರಕೊಟ್ಟನು.",
			"17": "<sup>17</sup> ಆಮೇಲೆ ಸೊಲೊಮೋನನು ತನ್ನ ತಂದೆಯಾದ ದಾವೀದನು ಮಾಡಿಸಿದ ಖಾನೇಷುಮಾರಿಯ ಆಧಾರದಿಂದ ಇಸ್ರಾಯೇಲ್ ದೇಶದಲ್ಲಿ ಪ್ರವಾಸಿಗಳಾಗಿದ್ದ ಎಲ್ಲಾ ಅನ್ಯಜನರನ್ನು ಲೆಕ್ಕಿಸಲು ಒಂದು ಲಕ್ಷ ಐವತ್ತಮೂರು ಸಾವಿರದ ಆರುನೂರು ಮಂದಿ ಸಿಕ್ಕಿದರು.",
			"18": "<sup>18</sup> ಅವರಲ್ಲಿ ಹೊರೆಹೊರುವದಕ್ಕಾಗಿ ಎಪ್ಪತ್ತು ಸಾವಿರ ಮಂದಿಯನ್ನೂ ಕಲ್ಲುಗಣಿಗಳಲ್ಲಿ ಕೆಲಸಮಾಡುವದಕ್ಕಾಗಿ ಎಂಭತ್ತು ಸಾವಿರ ಮಂದಿಯನ್ನೂ ಕೆಲಸದವರ ಮೇಲ್ವಿಚಾರಣೆಗಾಗಿ ಮೂರು ಸಾವಿರದ ಆರುನೂರು ಮಂದಿಯನ್ನೂ ನೇಮಿಸಿದನು."
		},
		"3": {
			"1": "<sup>1</sup> ಸೊಲೊಮೋನನು ಯೆರೂಸಲೇಮ್ ಪಟ್ಟಣದೊಳಗೆ ತನ್ನ ತಂದೆಯಾದ ದಾವೀದನಿಗೆ ದೇವದರ್ಶನವುಂಟಾದ ಮೋರೀಯಾ ಗುಡ್ಡದಲ್ಲಿ ಯೆಹೋವನ ಆಲಯವನ್ನು ಕಟ್ಟಿಸ ತೊಡಗಿದನು. ಮುಂಚೆ ಯೆಬೂಸಿಯನಾದ ಒರ್ನಾನನ ಕಣವಾಗಿದ್ದ ಆ ಸ್ಥಳವನ್ನು ದಾವೀದನು ಇದಕ್ಕೋಸ್ಕರ ಸಿದ್ಧಮಾಡಿದ್ದನು.",
			"2": "<sup>2</sup> ಸೊಲೊಮೋನನು ತನ್ನ ಆಳಿಕೆಯ ನಾಲ್ಕನೆಯ ವರುಷದ ಎರಡನೆಯ ತಿಂಗಳಿನ ಎರಡನೆಯ ದಿನದಲ್ಲಿ ಕಟ್ಟಿಸುವದಕ್ಕೆ ಪ್ರಾರಂಭಿಸಿದನು.",
			"3": "<sup>3</sup> ಅವನು ಕಟ್ಟಿಸಿದ ದೇವಾಲಯದ ಅಸ್ತಿವಾರದ ಉದ್ದವು ಮುಂಚಿನ ಮೊಳದ ಪ್ರಕಾರ ಅರುವತ್ತು ಮೊಳ,",
			"4": "<sup>4</sup> ಅಗಲವು ಇಪ್ಪತ್ತು ಮೊಳ, ದೇವಾಲಯದ ಮುಂದುಗಡೆಯಲ್ಲಿದ್ದ ಮಂಟಪದ ಉದ್ದವು ಆಲಯದ ಅಗಲಕ್ಕೆ ಸರಿಯಾಗಿ ಇಪ್ಪತ್ತು ಮೊಳ, ಎತ್ತರವು ಇಪ್ಪತ್ತು ಮೊಳ. ಸೊಲೊಮೋನನು ಅದರ ಒಳಮೈಯನ್ನು ಚೊಕ್ಕಬಂಗಾರದ ತಗಡಿನಿಂದ ಹೊದಿಸಿದನು.",
			"5": "<sup>5</sup> ದೊಡ್ಡ ಕೋಣೆಯನ್ನು ತುರಾಯಿ ಮರದ ಹಲಿಗೆಗಳಿಂದಲೂ ಆ ಹಲಿಗೆಗಳನ್ನು ತೋರಣ ಖರ್ಜೂರಗಳ ಚಿತ್ರಗಳುಳ್ಳ ಚೊಕ್ಕಬಂಗಾರದ ತಗಡಿನಿಂದಲೂ ಹೊದಿಸಿ",
			"6": "<sup>6</sup> ಅದನ್ನು ರತ್ನಗಳಿಂದ ಅಲಂಕರಿಸಿದನು. ಆ ಬಂಗಾರವು ಪರ್ವಯಿಮ್ ದೇಶದ್ದು.",
			"7": "<sup>7</sup> ಹೀಗೆ ಆಲಯದಲ್ಲಿನ ತೊಲೆ ಹೊಸ್ತಲು ಗೋಡೆ ಕದ ಇವುಗಳನ್ನು ಬಂಗಾರದ ತಗಡಿನಿಂದ ಹೊದಿಸಿ ಗೋಡೆಗಳ ಮೇಲೆ ಕೆರೂಬಿಗಳ ಚಿತ್ರಗಳನ್ನು ಕೆತ್ತಿಸಿದನು.",
			"8": "<sup>8</sup> ಇದಲ್ಲದೆ ಅವನು ಮಹಾಪರಿಶುದ್ಧಸ್ಥಳವನ್ನು ಮಾಡಿಸಿದನು. ಅದರ ಉದ್ದವು ಆಲಯದ ಅಗಲಕ್ಕೆ ಸರಿಯಾಗಿ ಇಪ್ಪತ್ತು ಮೊಳ; ಅದರ ಅಗಲವೂ ಇಪ್ಪತ್ತು ಮೊಳ. ಅದನ್ನು ಆರುನೂರು ತಲಾಂತು ಚೊಕ್ಕ ಬಂಗಾರದ ತಗಡಿನಿಂದ ಹೊದಿಸಿದನು.",
			"9": "<sup>9</sup> ಬಂಗಾರದ ಮೊಳೆಗಳ ತೂಕ ಐವತ್ತು ತೊಲೆ. ಮೇಲುಪ್ಪರಿಗೆಗಳನ್ನು ಬಂಗಾರದ ತಗಡಿನಿಂದ ಹೊದಿಸಿದನು.",
			"10": "<sup>10</sup> ಮಹಾ ಪರಿಶುದ್ಧಸ್ಥಳದಲ್ಲಿ ಎರಡು ಕೆರೂಬಿಗಳ ಪ್ರತಿಮೆಗಳನ್ನು ಮಾಡಿಸಿಟ್ಟು ಅವುಗಳನ್ನೂ ಬಂಗಾರದ ತಗಡಿನಿಂದ ಹೊದಿಸಿದನು.",
			"11": "<sup>11</sup> ಕೆರೂಬಿಗಳ ರೆಕ್ಕೆಗಳು ಒಟ್ಟಾಗಿ ಇಪ್ಪತ್ತು ಮೊಳ ಉದ್ದವಿದ್ದವು. [ಮೊದಲನೆಯ ಕೆರೂಬಿಯ] ಒಂದು ರೆಕ್ಕೆಯು ಐದು ಮೊಳ ಉದ್ದವಾಗಿದ್ದು ಮನೆಯ ಗೋಡೆಗೆ ತಗುಲಿತು. ಐದು ಮೊಳ ಉದ್ದವಾದ ಇನ್ನೊಂದು ರೆಕ್ಕೆಯು ಎರಡನೆಯ ಕೆರೂಬಿಯ ರೆಕ್ಕೆಗೆ ತಗುಲಿತು.",
			"12": "<sup>12</sup> ಅದರಂತೆಯೇ ಎರಡನೆಯ ಕೆರೂಬಿಯ ಐದು ಮೊಳ ಉದ್ದವಾದ ಒಂದು ರೆಕ್ಕೆಯು ಮನೆಯ ಗೋಡೆಗೂ ಇನ್ನೊಂದು ರೆಕ್ಕೆಯು ಮೊದಲನೆಯ ಕೆರೂಬಿಯ ರೆಕ್ಕೆಗೂ ತಗುಲಿದ್ದವು.",
			"13": "<sup>13</sup> ಈ ಕೆರೂಬಿಗಳು ತಮ್ಮ ರೆಕ್ಕೆಗಳನ್ನು ಇಪ್ಪತ್ತು ಮೊಳ ಉದ್ದಕ್ಕೆ ಚಾಚಿ ಕಾಲೂರಿ ನಿಂತುಕೊಂಡು ಪರಿಶುದ್ಧಸ್ಥಳದ ಕಡೆಗೆ ಮುಖಮಾಡಿದ್ದವು.",
			"14": "<sup>14</sup> ಸೊಲೊಮೋನನು ಪರದೆಯನ್ನು ನೀಲಧೂಮ್ರರಕ್ತವರ್ಣಗಳುಳ್ಳ ನಾರಿನಿಂದ ಮಾಡಿಸಿದನು. ಅದರಲ್ಲಿ ಕೆರೂಬಿಗಳನ್ನು ಕಸೂತಿ ಹಾಕಿಸಿದನು.",
			"15": "<sup>15</sup> ಇದಲ್ಲದೆ ದೇವಾಲಯದ ಮುಂದೆ ಎರಡು ಕಂಬಗಳನ್ನು ನಿಲ್ಲಿಸಿದನು. ಅವು ಮೂವತ್ತೈದು ಮೊಳ ಎತ್ತರವಿದ್ದವು. ಅವುಗಳ ತಲೆಗಳ ಮೇಲಣ ಕುಂಭಗಳು ಐದು ಮೊಳ ಎತ್ತರವಿದ್ದವು.",
			"16": "<sup>16</sup> ಅವನು ಗರ್ಭಗೃಹದ ಸರಪಣಿಯಂತೆ ಸರಪಣಿಗಳನ್ನು ಮಾಡಿಸಿ ಕಂಬಗಳ ಮೇಲಣ ಕುಂಭಗಳಿಗೆ ಸಿಕ್ಕಿಸಿ ಆ ಸರಪಣಿಗಳಲ್ಲಿ ನೂರು ನೂರು ತಾಮ್ರದ ದಾಳಿಂಬಹಣ್ಣುಗಳನ್ನು ಕಟ್ಟಿಸಿದನು.",
			"17": "<sup>17</sup> ಆ ಕಂಬಗಳನ್ನು ದೇವಾಲಯದ ಮುಂಭಾಗದಲ್ಲಿ ಒಂದನ್ನು ಬಲಗಡೆಗೂ ಇನ್ನೊಂದನ್ನು ಎಡಗಡೆಗೂ ನಿಲ್ಲಿಸಿ ಬಲಗಡೆಯದಕ್ಕೆ ಯಾಕೀನ್ ಎಂದೂ ಎಡಗಡೆಯದಕ್ಕೆ ಬೋವಜ್ ಎಂದೂ ಹೆಸರಿಟ್ಟನು."
		},
		"4": {
			"1": "<sup>1</sup> ಇದಲ್ಲದೆ ಅವನು ತಾಮ್ರದ ಯಜ್ಞವೇದಿಯನ್ನು ಮಾಡಿಸಿದನು. ಅದರ ಉದ್ದ ಇಪ್ಪತ್ತು ಮೊಳ, ಅಗಲ ಇಪ್ಪತ್ತು ಮೊಳ, ಎತ್ತರ ಹತ್ತು ಮೊಳ.",
			"2": "<sup>2</sup> ಅನಂತರ ಅವನು ಸಮುದ್ರವೆನಿಸಿಕೊಳ್ಳುವ ಒಂದು ಎರಕದ ಪಾತ್ರೆಯನ್ನು ಮಾಡಿಸಿದನು. ಅದರ ಬಾಯಿ ಚಕ್ರಾಕಾರವಾಗಿಯೂ ಅಂಚಿನಿಂದ ಅಂಚಿಗೆ ಹತ್ತು ಮೊಳವಾಗಿಯೂ ಇತ್ತು. ಅದರ ಎತ್ತರ ಐದು ಮೊಳ, ಸುತ್ತಳತೆ ಮೂವತ್ತು ಮೊಳ.",
			"3": "<sup>3</sup> ಅದನ್ನು ಎರಕಹೊಯ್ಯುವಾಗ ಅದರ [ಅಂಚಿನ] ಕೆಳಗೆ ಸುತ್ತಲೂ ಮೊಳಕ್ಕೆ ಹತ್ತರಂತೆ ಹೋರಿಗಳ ಚಿತ್ರಗಳನ್ನು ಎರಡು ಸಾಲಾಗಿ ಎರಕ ಹೊಯ್ಸಿದನು.",
			"4": "<sup>4</sup> ಹನ್ನೆರಡು ಎರಕದ ಹೋರಿಗಳು ಅದನ್ನು ಹೊತ್ತಿದ್ದವು. ಅವುಗಳಲ್ಲಿ ಮೂರು ಉತ್ತರ ದಿಕ್ಕಿಗೂ ಮೂರು ಪಶ್ಚಿಮದಿಕ್ಕಿಗೂ ಮೂರು ದಕ್ಷಿಣ ದಿಕ್ಕಿಗೂ ಮೂರು ಪೂರ್ವದಿಕ್ಕಿಗೂ ಮುಖ ಮಾಡಿಕೊಂಡಿದ್ದವು. ಸಮುದ್ರವು ಅವುಗಳ ಬೆನ್ನಿನ ಮೇಲಿತ್ತು.",
			"5": "<sup>5</sup> ಅವುಗಳ ಹಿಂಭಾಗವು ಒಳಗಡೆಗಿತ್ತು. ಪಾತ್ರೆಯು ನಾಲ್ಕು ಬೆರಳು ದಪ್ಪವಾಗಿತ್ತು. ಅದರ ಅಂಚು ಕಮಲಾಕಾರದ ಬಟ್ಟಲನ್ನು ಹೋಲುತ್ತಿತ್ತು. ಅದು ಮೂರು ಸಾವಿರ ಬತ್ ನೀರನ್ನು ಹಿಡಿಯುವದು.",
			"6": "<sup>6</sup> ಇದಲ್ಲದೆ ಅವನು ಹತ್ತು ಗಂಗಾಳಗಳನ್ನು ಮಾಡಿಸಿ ಸರ್ವಾಂಗಹೋಮಸಮರ್ಪಣೆಯ ಸಾಮಾನು ತೊಳೆದು ಶುದ್ಧಮಾಡುವದಕ್ಕಾಗಿ ಐದನ್ನು ಬಲಗಡೆಯಲ್ಲಿಯೂ ಐದನ್ನು ಎಡಗಡೆಯಲ್ಲಿಯೂ ಇಡಿಸಿದನು; ಸಮುದ್ರವೆನಿಸಿಕೊಳ್ಳುವ ಪಾತ್ರೆಯು ಯಾಜಕರ ಸ್ನಾನಕ್ಕಾಗಿ ಇತ್ತು.",
			"7": "<sup>7</sup> ಮತ್ತು ನೇಮಕವಾದ ಮಾದರಿಯಂತೆ ಬಂಗಾರದ ಹತ್ತು ದೀಪಸ್ತಂಭಗಳನ್ನು ಮಾಡಿಸಿ ಐದನ್ನು ದೇವಾಲಯದೊಳಗೆ ಬಲಗಡೆಯಲ್ಲಿಯೂ ಐದನ್ನು ಎಡಗಡೆಯಲ್ಲಿಯೂ ಇಡಿಸಿದನು.",
			"8": "<sup>8</sup> ಹತ್ತು ಮೇಜುಗಳನ್ನು ಮಾಡಿಸಿ ದೇವಾಲಯದೊಳಗೆ ಬಲಗಡೆಯಲ್ಲಿ ಐದನ್ನೂ ಎಡಗಡೆಯಲ್ಲಿ ಐದನ್ನೂ ಇಡಿಸಿ ನೂರು ಬಂಗಾರದ ಬೋಗುಣಿಗಳನ್ನು ಮಾಡಿಸಿದನು.",
			"9": "<sup>9</sup> ಇದಲ್ಲದೆ ಯಾಜಕರ ಪ್ರಾಕಾರವನ್ನೂ ಮಹಾಪ್ರಾಕಾರವನ್ನೂ ಮಹಾಪ್ರಾಕಾರಕ್ಕೆ ಬಾಗಲುಗಳನ್ನೂ ಮಾಡಿಸಿದನು; ಅವುಗಳ ಕದಗಳನ್ನು ತಾಮ್ರದ ತಗಡಿನಿಂದ ಹೊದಿಸಿದನು.",
			"10": "<sup>10</sup> ಸಮುದ್ರವೆನಿಸಿಕೊಳ್ಳುವ ಪಾತ್ರೆಯನ್ನು [ದೇವಾಲಯದ] ಬಲಗಡೆಯಲ್ಲಿ ಅಂದರೆ ಪ್ರಾಕಾರದ ಆಗ್ನೇಯ ದಿಕ್ಕಿನಲ್ಲಿ ಇರಿಸಿದನು.",
			"11": "<sup>11</sup> ಇದಲ್ಲದೆ ಹೂರಾಮನು ಹಂಡೆಗಳನ್ನೂ ಸಲಿಕೆಗಳನ್ನೂ ಬೋಗುಣಿಗಳನ್ನೂ ಮಾಡಿದನು. ಹೂರಾಮನು ಸೊಲೊಮೋನನ ಅಪ್ಪಣೆಯಂತೆ ದೇವಾಲಯಕ್ಕೋಸ್ಕರ ಮಾಡಿದ ಸಾಮಾನುಗಳು ಯಾವವಂದರೆ -",
			"12": "<sup>12</sup> ಎರಡು ಕಂಬಗಳು, ಅವುಗಳ ಮೇಲಣ ಕುಂಭಾಕಾರದ ಎರಡು ತಲೆಗಳು, ಕುಂಭಗಳ ಮೇಲೆ ಹಾಕುವದಕ್ಕೋಸ್ಕರ ಎರಡು ಜಾಲರಿಗಳು,",
			"13": "<sup>13</sup> ಕಂಬಗಳ ತುದಿಯಲ್ಲಿರುವ ಕುಂಭಗಳ ಜಾಲರಿಗಳ ಮೇಲೆ ಎರಡೆರಡು ಸಾಲಾಗಿ ಸಿಕ್ಕಿಸುವದಕ್ಕೋಸ್ಕರ ತಾಮ್ರದ ನಾನೂರು ದಾಳಿಂಬದ ಹಣ್ಣುಗಳು,",
			"14": "<sup>14</sup> ಪೀಠಗಳು, ಅವುಗಳ ಮೇಲಣ ಗಂಗಾಳಗಳು,",
			"15": "<sup>15</sup> ಸಮುದ್ರವೆನಿಸಿಕೊಳ್ಳುವ ಪಾತ್ರೆಯು, ಅದನ್ನು ಹೊರುವ ಹನ್ನೆರಡು ಎರಕದ ಹೋರಿಗಳು, ಹಂಡೆ, ಸಲಿಕೆ, ಮುಳ್ಳು ಇವೇ.",
			"16": "<sup>16</sup> ಹೂರಾಮಾಬೀವನು ಅರಸನಾದ ಸೊಲೊಮೋನನ ಅಪ್ಪಣೆಯ ಮೇರೆಗೆ ಯೆಹೋವನ ಆಲಯಕ್ಕೋಸ್ಕರ ಅದರ ಎಲ್ಲಾ ಸಾಮಾನುಗಳನ್ನು ಒಪ್ಪಹಾಕಿದ ತಾಮ್ರದಿಂದ ಮಾಡಿ ತೀರಿಸಿದನು.",
			"17": "<sup>17</sup> ಅರಸನು ಯೊರ್ದನ್ ತಗ್ಗಿನಲ್ಲಿ ಸುಕ್ಕೋತಿಗೂ ಚೆರೇದಕ್ಕೂ ಮಧ್ಯದಲ್ಲಿರುವ ಮಣ್ಣುನೆಲದಲ್ಲಿ ಎರಕ ಹೊಯ್ಸಿದನು.",
			"18": "<sup>18</sup> ಸೊಲೊಮೋನನು ಮಾಡಿಸಿದ ಸಾಮಾನುಗಳು ಅನೇಕವಾಗಿದ್ದದರಿಂದ ಅವುಗಳ ತಾಮ್ರದ ತೂಕ ಎಷ್ಟೆಂಬದು ಗೊತ್ತಾಗದೆ ಹೋಯಿತು.",
			"19": "<sup>19</sup> ಅವನು ದೇವಾಲಯಕ್ಕೋಸ್ಕರ ಮಾಡಿಸಿದ ಒಳಗಿನ ಸಾಮಾನುಗಳು ಯಾವವಂದರೆ - ಬಂಗಾರದ ಧೂಪವೇದಿ, ನೈವೇದ್ಯವಾದ ರೊಟ್ಟಿಗಳನ್ನಿಡತಕ್ಕ ಮೇಜುಗಳು,",
			"20": "<sup>20</sup> ನಿಯಮದ ಪ್ರಕಾರ ಮಹಾಪರಿಶುದ್ಧಸ್ಥಳದ ಮುಂದೆ ಉರಿಯುತ್ತಿರುವದಕ್ಕಾಗಿ ಪುಷ್ಪಾಲಂಕಾರವುಳ್ಳ ಬಂಗಾರದ ದೀಪಸ್ತಂಭಗಳು,",
			"21": "<sup>21</sup> ಉತ್ಕೃಷ್ಟವಾದ ಬಂಗಾರದಿಂದ ಮಾಡಿದ ಅವುಗಳ ಹಣತೆ, ತಂಡಸ,",
			"22": "<sup>22</sup> ಚೊಕ್ಕಬಂಗಾರದ ಕತ್ತರಿ, ಬೋಗುಣಿ, ಧೂಪಾರತಿ, ಅಗ್ಗಿಷ್ಟಿಗೆ, ದೇವಾಲಯದ ಮಹಾಪರಿಶುದ್ಧಸ್ಥಳದೊಳಗಣ ಬಾಗಲಿನ ಬಂಗಾರಕದಗಳು, ಪರಿಶುದ್ಧಸ್ಥಳದ ಬಂಗಾರ ಕದಗಳು ಇವೇ."
		},
		"5": {
			"1": "<sup>1</sup> ಸೊಲೊಮೋನನು ಯೆಹೋವನ ಆಲಯಕ್ಕೋಸ್ಕರ ಬೇಕಾದ ಎಲ್ಲಾ ಸಾಮಾನುಗಳನ್ನು ಸಿದ್ಧಪಡಿಸಿದನಂತರ ತನ್ನ ತಂದೆಯಾದ ದಾವೀದನು ಯೆಹೋವನಿಗೆ ಹರಕೆಹೊತ್ತು ಮುಡುಪಾಗಿಟ್ಟ ಬೆಳ್ಳಿ ಬಂಗಾರವನ್ನೂ ಎಲ್ಲಾ ಸಾಮಾನುಗಳನ್ನೂ ದೇವಾಲಯದ ಭಂಡಾರಕ್ಕೆ ಸೇರಿಸಿದನು.",
			"2": "<sup>2</sup> ಅನಂತರ ಸೊಲೊಮೋನನು ದಾವೀದ ನಗರವಾದ ಚೀಯೋನಿನಿಂದ ಯೆಹೋವನ ಒಡಂಬಡಿಕೆಯ ಮಂಜೂಷವನ್ನು ಮೇಲೆ ತರುವದಕ್ಕಾಗಿ ಇಸ್ರಾಯೇಲ್ಯರ ಹಿರಿಯರು ಕುಲಾಧಿಪತಿಗಳು ಇವರೇ ಮೊದಲಾದ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ ಪ್ರಧಾನಪುರುಷರನ್ನು ಯೆರೂಸಲೇಮಿಗೆ ಕರಿಸಿಕೊಂಡನು.",
			"3": "<sup>3</sup> ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರೂ ಏಳನೆಯ ತಿಂಗಳಿನ ಜಾತ್ರೆಗೋಸ್ಕರ ಅರಸನ ಬಳಿಗೆ ಕೂಡಿಬಂದರು.",
			"4": "<sup>4</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಹಿರಿಯರೆಲ್ಲರೂ ಕೂಡಿಬಂದಿರಲಾಗಿ ಲೇವಿಯರು ಯೆಹೋವನ ಮಂಜೂಷವನ್ನು ಹೊತ್ತುಕೊಂಡರು.",
			"5": "<sup>5</sup> ಇದಲ್ಲದೆ ಯಾಜಕರಾದ ಲೇವಿಯರು ದೇವದರ್ಶನದ ಗುಡಾರ, ಅದರಲ್ಲಿದ್ದ ಎಲ್ಲಾ ಪರಿಶುದ್ಧವಸ್ತುಗಳು ಇವುಗಳನ್ನೂ ಹೊತ್ತುಕೊಂಡುಹೋದರು.",
			"6": "<sup>6</sup> ಅರಸನಾದ ಸೊಲೊಮೋನನೂ ಅಲ್ಲಿ ಕೂಡಿಬಂದಿದ್ದ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರೂ ಮಂಜೂಷದ ಮುಂದೆ ಅಸಂಖ್ಯವಾದ ಕುರಿದನಗಳನ್ನು ಯಜ್ಞಮಾಡಿದರು.",
			"7": "<sup>7</sup> ಯಾಜಕರು ಯೆಹೋವನ ಒಡಂಬಡಿಕೆಯ ಮಂಜೂಷವನ್ನು ತೆಗೆದುಕೊಂಡು ಬಂದು ಮಹಾಪರಿಶುದ್ಧಸ್ಥಳವೆನಿಸುವ ಗರ್ಭಗೃಹದಲ್ಲಿ ಕೆರೂಬಿಗಳ ರೆಕ್ಕೆಗಳ ಕೆಳಗೆ ಅದಕ್ಕೆ ನೇಮಕವಾದ ಸ್ಥಳದಲ್ಲಿಟ್ಟರು.",
			"8": "<sup>8</sup> ಕೆರೂಬಿಗಳ ರೆಕ್ಕೆಗಳು ಮಂಜೂಷವಿದ್ದ ಸ್ಥಳದ ಮೇಲೆ ಚಾಚಿದವುಗಳಾಗಿದ್ದದರಿಂದ ಮಂಜೂಷವೂ ಅದರ ಕೋಲುಗಳೂ ಪೂರ್ಣವಾಗಿ ಅವುಗಳ ನೆರಳಿನಲ್ಲಿದ್ದವು.",
			"9": "<sup>9</sup> ಮಂಜೂಷಕ್ಕಿಂತಲೂ ಉದ್ದವಾಗಿದ್ದ ಆ ಕೋಲುಗಳ ತುದಿಗಳು ಗರ್ಭಗೃಹದ ಎದುರಿನಲ್ಲಿ ನಿಂತವರಿಗೆ ಕಾಣಿಸಿದರೂ ಹೊರಗೆ ನಿಂತವರಿಗೆ ಕಾಣಿಸುತ್ತಿರಲಿಲ್ಲ; ಮಂಜೂಷವು ಇಂದಿನವರೆಗೂ ಅಲ್ಲೇ ಇರುತ್ತದೆ.",
			"10": "<sup>10</sup> ಅದರಲ್ಲಿ ಎರಡು ಕಲ್ಲಿನ ಹಲಿಗೆಗಳು ಹೊರತಾಗಿ ಬೇರೇನೂ ಇರಲಿಲ್ಲ; ಯೆಹೋವನು ಐಗುಪ್ತದೇಶದಿಂದ ಬಂದ ಇಸ್ರಾಯೇಲ್ಯರೊಡನೆ ಹೋರೇಬ್ ಬೆಟ್ಟದ ಬಳಿಯಲ್ಲಿ ಒಡಂಬಡಿಕೆಮಾಡಿಕೊಂಡ ಮೇಲೆ ಮೋಶೆಯು ಅವುಗಳನ್ನು ಅದರಲ್ಲಿಟ್ಟಿದ್ದನು.",
			"11": "<sup>11</sup> ಕೂಡಿಬಂದಿದ್ದ ಯಾಜಕರು ವರ್ಗವ್ಯತ್ಯಾಸವಿಲ್ಲದೆ ಎಲ್ಲರೂ ತಮ್ಮನ್ನು ಶುದ್ಧಪಡಿಸಿಕೊಂಡಿದ್ದರು.",
			"12": "<sup>12</sup> ನಾರು ಬಟ್ಟೆಗಳನ್ನು ಧರಿಸಿಕೊಂಡು ತಾಳ ಸ್ವರಮಂಡಲ ಕಿನ್ನರಿ ಇವುಗಳನ್ನು ಹಿಡಿದುಕೊಂಡ ಲೇವಿಯ ಗಾಯಕರಾದ ಆಸಾಫ್‍ಹೇಮಾನ್ ಯೆದುತೂನರೂ ಇವರ ಮಕ್ಕಳೂ ಸಹೋದರರೂ ತುತೂರಿಗಳನ್ನು ಊದತಕ್ಕ ನೂರಿಪ್ಪತ್ತು ಮಂದಿ ಯಾಜಕರೂ ಯಜ್ಞವೇದಿಯ ಪೂರ್ವದಿಕ್ಕಿನಲ್ಲಿ ನಿಂತುಕೊಂಡರು.",
			"13": "<sup>13</sup> [ಮಂಜೂಷವನ್ನು ಒಳಗಿಟ್ಟ] ಯಾಜಕರು ದೇವಾಲಯದಿಂದ ಹೊರಗೆ ಬಂದಕೂಡಲೆ ಒಬ್ಬನೋ ಎಂಬಂತೆ ಸ್ವರವೆತ್ತಿ ಯೆಹೋವನನ್ನು ಕೀರ್ತಿಸುವದಕ್ಕಾಗಿ ತುತೂರಿ ಊದುವವರೂ ಗಾಯನ ಮಾಡುವವರೂ ಅಲ್ಲಿ ನಿಂತಿದ್ದರು. ತುತೂರಿ ತಾಳ ಮೊದಲಾದ ವಾದ್ಯಗಳ ಧ್ವನಿಯೂ ಯೆಹೋವನು ಒಳ್ಳೆಯವನು, ಆತನ ಕೃಪೆಯು ಶಾಶ್ವತವಾಗಿರುವದು ಎಂದು ಕೃತಜ್ಞತಾಸ್ತುತಿ ಮಾಡುವವರ ಸ್ವರವೂ ಕೇಳಿಸಿದೊಡನೆ ಮೇಘವು ಯೆಹೋವನ ಆಲಯದಲ್ಲಿ ತುಂಬಿಕೊಂಡಿತು.",
			"14": "<sup>14</sup> ಯೆಹೋವನ ತೇಜಸ್ಸಿನಿಂದ ವ್ಯಾಪ್ತವಾಗಿದ್ದ ಮೇಘವು ದೇವಾಲಯದಲ್ಲಿ ತುಂಬಿಕೊಂಡದರಿಂದ ಯಾಜಕರು ಅಲ್ಲಿ ನಿಂತು ಸೇವೆಮಾಡಲಾರದೆ ಹೋದರು."
		},
		"6": {
			"1": "<sup>1</sup> ತರುವಾಯ ಸೊಲೊಮೋನನು - ಯೆಹೋವನೇ ಕಾರ್ಗತ್ತಲಲ್ಲಿ ವಾಸಿಸುತ್ತೇನೆಂದು ಹೇಳಿದ್ದೀ;",
			"2": "<sup>2</sup> ನಾನು ನಿನಗೋಸ್ಕರ ಒಂದು ಮಂದಿರವನ್ನು ಕಟ್ಟಿಸಿದ್ದೇನೆ; ಅದು ನಿನಗೆ ಶಾಶ್ವತವಾದ ವಾಸಸ್ಥಳವಾಗಿರಲಿ ಎಂದು ಪ್ರಾರ್ಥನೆ ಮಾಡಿದನು.",
			"3": "<sup>3</sup> ಇದಾದ ಮೇಲೆ ಅರಸನು ಎದ್ದು ನಿಂತಿದ್ದ ಇಸ್ರಾಯೇಲ್ಸಮೂಹದವರ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡು ಅವರನ್ನು ಆಶೀರ್ವದಿಸಿ -",
			"4": "<sup>4</sup> ಇಸ್ರಾಯೇಲ್‍ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಸ್ತೋತ್ರವಾಗಲಿ; ನನ್ನ ತಂದೆಯಾದ ದಾವೀದನಿಗೆ ಆತನ ಬಾಯಿ ನುಡಿದದ್ದನ್ನು ಆತನ ಹಸ್ತವು ಈಗ ನೆರವೇರಿಸಿಯದೆ.",
			"5": "<sup>5</sup> ಆತನು - ನನ್ನ ಜನರಾದ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಐಗುಪ್ತದೇಶದಿಂದ ನಾನು ಬರಮಾಡಿದ್ದು ಮೊದಲುಗೊಂಡು ಇಸ್ರಾಯೇಲ್ ಕುಲಗಳಲ್ಲಿ ಯಾವ ಪಟ್ಟಣವನ್ನೂ ನನ್ನ ನಾಮದ ನಿವಾಸಕ್ಕೋಸ್ಕರ ಆಲಯಸ್ಥಾನವನ್ನಾಗಿ ಆರಿಸಿಕೊಂಡಿರಲಿಲ್ಲ; ನನ್ನ ಪ್ರಜೆಯಾದ ಇಸ್ರಾಯೇಲ್ಯರ ಪ್ರಭುವಾಗುವದಕ್ಕಾಗಿ ಯಾವನೊಬ್ಬ ಪುರುಷನನ್ನು ಆರಿಸಿಕೊಂಡದ್ದೂ ಇಲ್ಲ.",
			"6": "<sup>6</sup> ಆದರೆ ಈಗ ನನ್ನ ನಾಮಸ್ಥಾಪನೆಗೋಸ್ಕರ ಯೆರೂಸಲೇಮನ್ನೂ ನನ್ನ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಆಳುವದಕ್ಕೋಸ್ಕರ ದಾವೀದನನ್ನೂ ಆರಿಸಿಕೊಂಡಿದ್ದೇನೆ ಎಂದು ಹೇಳಿದನು.",
			"7": "<sup>7</sup> ನನ್ನ ತಂದೆಯಾದ ದಾವೀದನು ಇಸ್ರಾಯೇಲ್ ದೇವರಾದ ಯೆಹೋವನ ಹೆಸರಿನದಾಗಿ ಆಲಯವನ್ನು ಕಟ್ಟಬೇಕೆಂದು ಮನಸ್ಸು ಮಾಡಿದಾಗ ಆತನು ಅವನಿಗೆ -",
			"8": "<sup>8</sup> ನೀನು ನನ್ನ ಹೆಸರಿನದಾಗಿ ಒಂದು ಮನೆಯನ್ನು ಕಟ್ಟುವದಕ್ಕೆ ಮನಸ್ಸುಮಾಡಿದ್ದು ಒಳ್ಳೇದೇ ಸರಿ.",
			"9": "<sup>9</sup> ಆದರೂ ನೀನು ಅದನ್ನು ಕಟ್ಟಬಾರದು; ನಿನ್ನಿಂದ ಹುಟ್ಟುವ ಮಗನು ಅದನ್ನು ನನ್ನ ಹೆಸರಿನದಾಗಿ ಕಟ್ಟಬೇಕು ಎಂದು ಹೇಳಿದನು.",
			"10": "<sup>10</sup> ಯೆಹೋವನು ತಾನು ಕೊಟ್ಟ ಮಾತನ್ನು ನೆರವೇರಿಸಿದನು. ಆತನ ವಾಗ್ದಾನದಂತೆ ನಾನು ನನ್ನ ತಂದೆಯಾದ ದಾವೀದನಿಗೆ ಬದಲಾಗಿ ಇಸ್ರಾಯೇಲ್ ಸಿಂಹಾಸನದ ಮೇಲೆ ಅರಸನಾಗಿ ಕೂತುಕೊಂಡು ಇಸ್ರಾಯೇಲ್ ದೇವರಾದ ಯೆಹೋವನ ಹೆಸರಿನದಾಗಿ ಈ ಆಲಯವನ್ನು ಕಟ್ಟಿಸಿದ್ದೇನೆ.",
			"11": "<sup>11</sup> ಇದಲ್ಲದೆ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಯೆಹೋವನಿಂದ ದೊರಕಿದ ನಿಬಂಧನಶಾಸನಗಳಿರುವ ಮಂಜೂಷವನ್ನು ಅಲ್ಲಿಟ್ಟಿದ್ದೇನೆ ಎಂದು ಹೇಳಿದನು.",
			"12": "<sup>12</sup> ತರುವಾಯ ಸೊಲೊಮೋನನು ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರ ಎದುರಿನಲ್ಲಿ ಯೆಹೋವನ ಯಜ್ಞವೇದಿಯ ಮುಂದೆ ಕೈಗಳನ್ನೆತ್ತಿಕೊಂಡು ನಿಂತನು.",
			"13": "<sup>13</sup> ಅವನು ಐದು ಮೊಳ ಉದ್ದ, ಐದು ಮೊಳ ಅಗಲ, ಮೂರು ಮೊಳ ಎತ್ತರವಾಗಿರುವ ಒಂದು ತಾಮ್ರಪೀಠವನ್ನು ಮಾಡಿಸಿ ಪ್ರಾಕಾರದ ಮಧ್ಯದಲ್ಲಿಡಿಸಿದ್ದನು. ಅವನು ಇಸ್ರಾಯೇಲ್ ಸರ್ವಸಮೂಹದ ಎದುರಿನಲ್ಲಿ ಅದನ್ನು ಹತ್ತಿ ಅದರ ಮೇಲೆ ಮೊಣಕಾಲೂರಿಕೊಂಡು ಆಕಾಶದ ಕಡೆಗೆ ಕೈಗಳನ್ನೆತ್ತಿ ಹೀಗೆ ಪ್ರಾರ್ಥಿಸಿದನು -",
			"14": "<sup>14</sup> ಇಸ್ರಾಯೇಲ್‍ದೇವರಾದ ಯೆಹೋವನೇ, ಭೂಲೋಕದಲ್ಲಿಯೂ ಪರಲೋಕದಲ್ಲಿಯೂ ನಿನಗೆ ಸಮಾನನಾದ ದೇವರಿಲ್ಲ; ಪೂರ್ಣಮನಸ್ಸಿನಿಂದ ನಿನಗೆ ನಡೆದುಕೊಳ್ಳುವಂಥ ಸೇವಕರ ವಿಷಯದಲ್ಲಿ ನೀನು ನಿನ್ನ ಒಡಂಬಡಿಕೆಯನ್ನೂ ಕೃಪೆಯನ್ನೂ ನೆರವೇರಿಸುವವನು.",
			"15": "<sup>15</sup> ನಿನ್ನ ಸೇವಕನೂ ನನ್ನ ತಂದೆಯೂ ಆದ ದಾವೀದನಿಗೆ ಕೊಟ್ಟ ಮಾತನ್ನು ಕೈಕೊಂಡಿ; ನಿನ್ನ ಬಾಯಿ ನುಡಿದದ್ದನ್ನು ನಿನ್ನ ಹಸ್ತವು ಈಗ ನೆರವೇರಿಸಿತು.",
			"16": "<sup>16</sup> ಇಸ್ರಾಯೇಲ್‍ದೇವರಾದ ಯೆಹೋವನೇ, ನೀನು ನನ್ನ ತಂದೆಯಾದ ದಾವೀದನಿಗೆ - ನಿನ್ನ ಸಂತಾನದವರು ನಿನ್ನಂತೆ ಜಾಗರೂಕತೆಯಿಂದ ನನ್ನ ಕಟ್ಟಳೆಯನ್ನು ಅನುಸರಿಸಿ ನಡೆದುಕೊಳ್ಳುವದಾದರೆ ಅವರು ತಪ್ಪದೆ ಇಸ್ರಾಯೇಲ್ ಸಿಂಹಾಸನದ ಮೇಲೆ ಕೂತುಕೊಳ್ಳುವಂತೆ ಮಾಡುವೆನು ಎಂಬದಾಗಿ ವಾಗ್ದಾನಮಾಡಿದಿಯಲ್ಲಾ, ಅದನ್ನು ನೆರವೇರಿಸು.",
			"17": "<sup>17</sup> ಇಸ್ರಾಯೇಲ್ ದೇವರೇ, ಯೆಹೋವನೇ, ನೀನು ನಿನ್ನ ಸೇವಕನಾದ ದಾವೀದನಿಗೆ ನುಡಿದದ್ದೆಲ್ಲವೂ ಸಾರ್ಥಕವಾಗಲಿ.",
			"18": "<sup>18</sup> ದೇವರು ನಿಜವಾಗಿ ಮನುಷ್ಯರೊಡನೆ ಭೂಲೋಕದಲ್ಲಿ ವಾಸಿಸುವನೋ? ಆಕಾಶವೂ ಉನ್ನತೋನ್ನತವಾದ ಆಕಾಶವೂ ನಿನ್ನ ವಾಸಕ್ಕೆ ಸಾಲವು; ಹೀಗಿರುವಲ್ಲಿ ನಾನು ಕಟ್ಟಿಸಿದ ಈ ಮಂದಿರವು ಹೇಗೆ ಸಾಕಾದೀತು?",
			"19": "<sup>19</sup> ಆದರೂ ನನ್ನ ದೇವರೇ, ಯೆಹೋವನೇ, ನಿನ್ನ ಸೇವಕನ ಪ್ರಾರ್ಥನೆಗೂ ವಿಜ್ಞಾಪನೆಗೂ ಕಿವಿಗೊಡು; ನಿನ್ನನ್ನು ಪ್ರಾರ್ಥಿಸುತ್ತಿರುವ ನಿನ್ನ ಸೇವಕನ ಮೊರೆಯನ್ನು ಲಾಲಿಸು.",
			"20": "<sup>20</sup> ಈ ಸ್ಥಳವನ್ನು ಕುರಿತು - ನನ್ನ ನಾಮಪ್ರಭಾವವು ಇಲ್ಲಿ ವಾಸಿಸುವದು ಎಂದು ಹೇಳಿದವನೇ, ನಿನ್ನ ಕಟಾಕ್ಷವು ಹಗಲಿರುಳೂ ಈ ಮಂದಿರದ ಮೇಲಿರಲಿ; ಇಲ್ಲಿ ನಿನ್ನ ಸೇವಕನು ಪ್ರಾರ್ಥಿಸುವಾಗೆಲ್ಲಾ ಅವನಿಗೆ ಸದುತ್ತರವನ್ನು ದಯಪಾಲಿಸು.",
			"21": "<sup>21</sup> ನಿನ್ನ ಸೇವಕನಾದ ನಾನಾಗಲಿ ನಿನ್ನ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಾಯೇಲ್ಯರಾಗಲಿ ಈ ಸ್ಥಳದ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡು ನಿನ್ನನ್ನು ಪ್ರಾರ್ಥಿಸುವಾಗ ನಿನ್ನ ನಿವಾಸವಾಗಿರುವ ಪರಲೋಕದಿಂದ ನಮ್ಮ ಬಿನ್ನಹಗಳನ್ನು ಕೇಳಿ ಕ್ಷಮೆಯನ್ನು ಅನುಗ್ರಹಿಸು.",
			"22": "<sup>22</sup> ನೆರೆಯವನಿಗೆ ವಿರೋಧವಾಗಿ ತಪ್ಪುಮಾಡಿದವನೆಂಬ ಸಂಶಯಕ್ಕೆ ಗುರಿಯಾದ ಮನುಷ್ಯನು ತಾನು ನಿರ್ದೋಷಿಯೆಂದು ಪ್ರಮಾಣಮಾಡಬೇಕಾದಾಗ ಅವನು ಈ ಆಲಯಕ್ಕೆ ಬಂದು ನಿನ್ನ ವೇದಿಯ ಮುಂದೆ ನಿಂತು ಪ್ರಮಾಣಮಾಡುವದಾದರೆ",
			"23": "<sup>23</sup> ನೀನು ಪರಲೋಕದಿಂದ ಅದನ್ನು ಕೇಳಿ ನಿನ್ನ ಸೇವಕರ ವ್ಯಾಜ್ಯವನ್ನು ತೀರಿಸು; ದುಷ್ಟನಿಗೆ ಅವನ ದುಷ್ಟತ್ವವನ್ನು ಅವನ ತಲೆಯ ಮೇಲೆಯೇ ಬರಮಾಡಿ ಮುಯ್ಯಿತೀರಿಸು; ನೀತಿವಂತನಿಗೆ ಅವನ ನೀತಿಯ ಫಲವನ್ನು ಅನುಗ್ರಹಿಸಿ ಅವನು ನೀತಿವಂತನೆಂದು ತೋರಿಸಿಕೊಡು.",
			"24": "<sup>24</sup> ತಮ್ಮ ಪಾಪಗಳ ಸಲುವಾಗಿ ಶತ್ರುಗಳಿಂದ ಕುಗ್ಗಿಸಲ್ಪಟ್ಟ ನಿನ್ನ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಾಯೇಲ್ಯರು ತಿರುಗಿಕೊಂಡು ನಿನ್ನ ನಾಮವನ್ನು ಎತ್ತಿ ಈ ಆಲಯಕ್ಕೆ ಬಂದು ನಿನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ವಿಜ್ಞಾಪನೆಯನ್ನೂ ಪ್ರಾರ್ಥನೆಯನ್ನೂ ಮಾಡುವದಾದರೆ",
			"25": "<sup>25</sup> ನೀನು ಪರಲೋಕದಿಂದ ಲಾಲಿಸಿ ಅವರ ಪಾಪಗಳನ್ನು ಕ್ಷಮಿಸಿ ಅವರಿಗೂ ಅವರ ಪಿತೃಗಳಿಗೂ ನೀನು ಕೊಟ್ಟ ದೇಶಕ್ಕೆ ಅವರನ್ನು ತಿರಿಗಿ ಬರಮಾಡು.",
			"26": "<sup>26</sup> ಅವರ ಪಾಪಗಳ ನಿಮಿತ್ತವಾಗಿ ನೀನು ಆಕಾಶವನ್ನು ಮಳೆಗರೆಯದಂತೆ ಮುಚ್ಚಿ ಅವರನ್ನು ತಗ್ಗಿಸಿದಾಗ ಅವರು ತಮ್ಮ ಪಾಪಗಳನ್ನು ಬಿಟ್ಟು ತಮ್ಮನ್ನು ತಗ್ಗಿಸಿದವನು ನೀನೇ ಎಂದು ನಿನ್ನ ನಾಮವನ್ನು ಎತ್ತಿ ಈ ಆಲಯದ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡು ನಿನ್ನನ್ನು ಪ್ರಾರ್ಥಿಸುವದಾದರೆ",
			"27": "<sup>27</sup> ಪರಲೋಕದಲ್ಲಿರುವ ನೀನು ಲಾಲಿಸಿ ನಿನ್ನ ಸೇವಕರೂ ಪ್ರಜೆಗಳೂ ಆದ ಇಸ್ರಾಯೇಲ್ಯರ ಪಾಪಗಳನ್ನು ಕ್ಷಮಿಸಿ ಅವರು ನಡೆಯಬೇಕಾದ ಸನ್ಮಾರ್ಗವನ್ನು ತೋರಿಸಿ ನಿನ್ನ ಪ್ರಜೆಗೆ ಸ್ವಾಸ್ತ್ಯವಾಗಿ ಕೊಟ್ಟ ದೇಶಕ್ಕೆ ಮಳೆಯನ್ನು ಅನುಗ್ರಹಿಸು.",
			"28": "<sup>28</sup> ದೇಶಕ್ಕೆ ಕ್ಷಾಮ, ಘೋರವ್ಯಾದಿ, [ಬೆಳೆಗೆ] ಬಿಸಿಗಾಳಿ, ಬೂದಿ, ಮಿಡಿತೆ, ಜಿಟ್ಟೇಹುಳ, ಪಟ್ಟಣಗಳಿಗೆ ಶತ್ರುಗಳ ಮುತ್ತಿಗೆ ಅಂತು ಯಾವ ಉಪದ್ರವದಿಂದಾಗಲಿ ವ್ಯಾಧಿಯಿಂದಾಗಲಿ ಬಾಧೆಯುಂಟಾಗುವಲ್ಲಿ",
			"29": "<sup>29</sup> ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರಾಗಲಿ ಅವರಲ್ಲೊಬ್ಬನಾಗಲಿ ತಾವು ಅನುಭವಿಸುತ್ತಿರುವ ಉಪದ್ರವ ದುಃಖಗಳ ನಿಮಿತ್ತವಾಗಿ ಈ ಆಲಯದ ಕಡೆಗೆ ಕೈಯೆತ್ತಿ ನಿನಗೆ ಪ್ರಾರ್ಥನೆಯನ್ನೂ ವಿಜ್ಞಾಪನೆಯನ್ನೂ ಮಾಡುವದಾದರೆ",
			"30": "<sup>30</sup> ಮನುಷ್ಯರ ಹೃದಯಗಳನ್ನು ಬಲ್ಲಂಥ ನೀನು ನಿನ್ನ ನಿವಾಸವಾಗಿರುವ ಪರಲೋಕದಿಂದ ಲಾಲಿಸಿ ಅವರಿಗೆ ಪಾಪ ಪರಿಹಾರವನ್ನು ಅನುಗ್ರಹಿಸು. ನೀನೊಬ್ಬನೇ ಮನುಷ್ಯರ ಹೃದಯಗಳನ್ನು ಬಲ್ಲವನಾಗಿರುವದರಿಂದ ಪ್ರತಿಯೊಬ್ಬನಿಗೂ ಅವನವನು ಹಿಡಿಯುವ ಮಾರ್ಗಕ್ಕೆ ತಕ್ಕ ಹಾಗೆ ಫಲವನ್ನು ಕೊಡು.",
			"31": "<sup>31</sup> ಆಗ ನಮ್ಮ ಪಿತೃಗಳಿಗೆ ನೀನು ಕೊಟ್ಟ ದೇಶದಲ್ಲಿ ಅವರು ವಾಸಿಸುವ ಕಾಲದಲ್ಲೆಲ್ಲಾ ನಿನ್ನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿ ನಿನ್ನ ಮಾರ್ಗಗಳಲ್ಲಿ ನಡೆಯುವರು.",
			"32": "<sup>32</sup> ನಿನ್ನ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಸೇರದಂಥ ಒಬ್ಬೊಬ್ಬ ಪರದೇಶಿಯೂ ನಿನ್ನ ನಾಮಮಹತ್ತು ಭುಜಬಲ ಶಿಕ್ಷಾಹಸ್ತ ಇವುಗಳ ವರ್ತಮಾನ ಕೇಳಿ ದೂರದೇಶದಿಂದ ಬಂದು ಈ ಆಲಯದ ಮುಂದೆ ನಿಂತು ನಿನ್ನನ್ನು ಪ್ರಾರ್ಥಿಸುವದಾದರೆ",
			"33": "<sup>33</sup> ನಿನ್ನ ನಿವಾಸವಾಗಿರುವ ಪರಲೋಕದಿಂದ ಅವರ ಪ್ರಾರ್ಥನೆಯನ್ನು ಕೇಳಿ ಅವರು ಬೇಡಿಕೊಂಡದ್ದನ್ನು ಅನುಗ್ರಹಿಸು. ಆಗ ಲೋಕದ ಎಲ್ಲಾ ಜನರೂ ನಿನ್ನ ನಾಮಮಹತ್ತನ್ನು ತಿಳಿದು ನಿನ್ನ ಜನರಾದ ಇಸ್ರಾಯೇಲ್ಯರಂತೆ ನಿನ್ನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿ ನಾನು ಈ ಆಲಯವನ್ನು ನಿನ್ನ ಹೆಸರಿನದಾಗಿ ಕಟ್ಟಿಸಿದ್ದೇನೆಂದು ತಿಳಿದುಕೊಳ್ಳುವರು.",
			"34": "<sup>34</sup> ನೀನು ನಿನ್ನ ಜನರನ್ನು ಶತ್ರುಗಳೊಡನೆ ಯುದ್ಧಮಾಡುವದಕ್ಕೋಸ್ಕರ ಎಲ್ಲಿಗಾದರೂ ಕಳುಹಿಸಿದಾಗ ಅವರು ಅಲ್ಲಿಂದ ನೀನು ಆರಿಸಿಕೊಂಡ ಪಟ್ಟಣದ ಕಡೆಗೂ ನಾನು ನಿನ್ನ ಹೆಸರಿಗಾಗಿ ಕಟ್ಟಿಸಿದ ಈ ಆಲಯದ ಕಡೆಗೂ ತಿರುಗಿಕೊಂಡು ನಿನ್ನನ್ನು ಪ್ರಾರ್ಥಿಸುವದಾದರೆ",
			"35": "<sup>35</sup> ನೀನು ಪರಲೋಕದಿಂದ ಅವರ ವಿಜ್ಞಾಪನೆಯನ್ನೂ ಪ್ರಾರ್ಥನೆಯನ್ನೂ ಕೇಳಿ ಅವರ ನ್ಯಾಯವನ್ನು ಸ್ಥಾಪಿಸು.",
			"36": "<sup>36</sup> ಅವರು ನಿನಗೆ ವಿರುದ್ಧವಾಗಿ ಪಾಪಮಾಡಾರು; ಪಾಪಮಾಡದ ಮನುಷ್ಯನು ಒಬ್ಬನೂ ಇಲ್ಲವಲ್ಲಾ. ನೀನು ಅವರ ಮೇಲೆ ಕೋಪಗೊಂಡು ಅವರನ್ನು ಶತ್ರುಗಳ ಕೈಗೆ ಒಪ್ಪಿಸಿದಾಗ ಮತ್ತು ಆ ಶತ್ರುಗಳು ಅವರನ್ನು ಸೆರೆಹಿಡಿದು ದೂರದಲ್ಲಾಗಲಿ ಸಮೀಪದಲ್ಲಾಗಲಿ ಇರುವ ದೇಶಕ್ಕೆ ಒಯ್ದಾಗ",
			"37": "<sup>37</sup> ತಾವು ಸೆರೆಯವರಾಗಿ ಬಿದ್ದುಕೊಂಡಿರುವ ದೇಶದಲ್ಲಿ ಅವರು ಪಶ್ಚಾತ್ತಾಪಪಟ್ಟು",
			"38": "<sup>38</sup> ನಿನ್ನ ಅನುಗ್ರಹದಿಂದ ತಮ್ಮ ಪಿತೃಗಳಿಗೆ ದೊರಕಿದ ದೇಶದ ಕಡೆಗೂ ನೀನು ಆರಿಸಿಕೊಂಡ ಪಟ್ಟಣದ ಕಡೆಗೂ ನಾನು ನಿನ್ನ ಹೆಸರಿಗೋಸ್ಕರ ಕಟ್ಟಿಸಿದ ಈ ಆಲಯದ ಕಡೆಗೂ ತಿರುಗಿಕೊಂಡು - ನಾವು ನಿನ್ನ ಆಜ್ಞೆಗಳನ್ನು ಮೀರಿ ಪಾಪಮಾಡಿ ದ್ರೋಹಿಗಳಾದೆವು ಎಂದು ಒಪ್ಪಿ ಪೂರ್ಣಮನಸ್ಸಿನಿಂದಲೂ ಪೂರ್ಣಪ್ರಾಣದಿಂದಲೂ ನಿನ್ನನ್ನು ಪ್ರಾರ್ಥಿಸುವದಾದರೆ",
			"39": "<sup>39</sup> ನೀನು ನಿನ್ನ ನಿವಾಸವಾಗಿರುವ ಪರಲೋಕದಿಂದ ಅವರ ವಿಜ್ಞಾಪನೆಯನ್ನೂ ಪ್ರಾರ್ಥನೆಯನ್ನೂ ಲಾಲಿಸಿ ಅವರ ನ್ಯಾಯವನ್ನು ಸ್ಥಾಪಿಸಿ ನಿನಗೆ ವಿರುದ್ಧವಾಗಿ ಪಾಪಮಾಡಿದ ನಿನ್ನ ಪ್ರಜೆಗೆ ಕ್ಷಮೆಯನ್ನು ಅನುಗ್ರಹಿಸು.",
			"40": "<sup>40</sup> ನನ್ನ ದೇವರೇ, ಕಟಾಕ್ಷವಿಟ್ಟು ಈ ಸ್ಥಳದಲ್ಲಿ ಮಾಡುವ ಪ್ರಾರ್ಥನೆಗೆ ಕಿವಿಗೊಡು.",
			"41": "<sup>41</sup> ದೇವರಾದ ಯೆಹೋವನೇ, ಎದ್ದು ನಿನ್ನ ಪ್ರತಾಪಯುಕ್ತವಾದ ಮಂಜೂಷದೊಡನೆ ನಿನ್ನ ವಾಸಸ್ಥಾನದೊಳಗೆ ಸೇರೋಣವಾಗಲಿ. ದೇವರಾದ ಯೆಹೋವನೇ, ನಿನ್ನ ಯಾಜಕರು ರಕ್ಷಣೆಯೆಂಬ ವಸ್ತ್ರವನ್ನು ಹೊದ್ದುಕೊಳ್ಳಲಿ; ನಿನ್ನ ಭಕ್ತರು ಸೌಭಾಗ್ಯದಲ್ಲಿ ಹರ್ಷಿಸಲಿ.",
			"42": "<sup>42</sup> ದೇವರಾದ ಯೆಹೋವನೇ, ನಿನ್ನಿಂದ ಅಭಿಷಿಕ್ತನಾದವನನ್ನು ತಳ್ಳಿಬಿಡಬೇಡ; ನಿನ್ನ ಸೇವಕನಾದ ದಾವೀದನಿಗೆ ಅನುಗ್ರಹಿಸಿದ ಕೃಪೆಯನ್ನು ನೆನಪುಮಾಡಿಕೋ."
		},
		"7": {
			"1": "<sup>1</sup> ಸೊಲೊಮೋನನು ಪ್ರಾರ್ಥಿಸುವದನ್ನು ಮುಗಿಸಿದ ಕೂಡಲೆ ಆಕಾಶದಿಂದ ಬೆಂಕಿಬಿದ್ದು ಸರ್ವಾಂಗ ಹೋಮದ್ರವ್ಯಗಳನ್ನೂ ಯಜ್ಞಮಾಂಸವನ್ನೂ ದಹಿಸಿಬಿಟ್ಟಿತು, ಯೆಹೋವನ ತೇಜಸ್ಸು ಆಲಯದಲ್ಲಿ ತುಂಬಿಕೊಂಡಿತು.",
			"2": "<sup>2</sup> ಯೆಹೋವನ ತೇಜಸ್ಸು ಆತನ ಆಲಯದಲ್ಲಿ ತುಂಬಿಕೊಂಡದ್ದರಿಂದ ಯಾಜಕರು ಒಳಗೆ ಪ್ರವೇಶಿಸಲಾರದೆ ಹೋದರು.",
			"3": "<sup>3</sup> ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರೂ ಬೆಂಕಿ ಇಳಿದುಬರುವದನ್ನೂ ಯೆಹೋವನ ತೇಜಸ್ಸು ಆಲಯದಲ್ಲಿರುವದನ್ನೂ ನೋಡಿ ನೆಲಗಟ್ಟಿನ ಮೇಲೆ ಸಾಷ್ಟಾಂಗವೆರಗಿ ಆರಾಧಿಸಿ - ಯೆಹೋವನು ಒಳ್ಳೆಯವನು, ಆತನ ಕೃಪೆಯು ಶಾಶ್ವತವಾಗಿದೆ ಎಂದು ಕೃತಜ್ಞತಾಸ್ತುತಿಮಾಡಿದರು.",
			"4": "<sup>4</sup> ಅರಸನೂ ಎಲ್ಲಾ ಜನರೂ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಯಜ್ಞಮಾಡಿದರು.",
			"5": "<sup>5</sup> ಅರಸನಾದ ಸೊಲೊಮೋನನು ಯಜ್ಞಕ್ಕಾಗಿ ವಧಿಸಿದ ಹೋರಿಗಳು ಇಪ್ಪತ್ತೆರಡು ಸಾವಿರ, ಕುರಿಗಳು ಲಕ್ಷದ ಇಪ್ಪತ್ತು ಸಾವಿರ. ಈ ಪ್ರಕಾರ ಅರಸನೂ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರೂ ದೇವಾಲಯವನ್ನು ಪ್ರತಿಷ್ಠಿಸಿದರು.",
			"6": "<sup>6</sup> ಯಾಜಕರೂ ಲೇವಿಯರೂ ತಮ್ಮ ತಮ್ಮ ಸ್ಥಳಗಳಲ್ಲಿ ನಿಂತರು. ದಾವೀದನು ಯೆಹೋವನ ಗಾಯನಸೇವೆಗೋಸ್ಕರ ಮಾಡಿಸಿದ ವಾದ್ಯಗಳು ಈ ಲೇವಿಯರ ಕೈಯಲ್ಲಿದ್ದವು. ಇವರು ರಾಜ ಸೇವೆಯಲ್ಲಿದ್ದುಕೊಂಡು ದೇವಾರಾಧನೆ ನಡಿಸುತ್ತಾ - ಯೆಹೋವನಿಗೆ ಕೃತಜ್ಞತಾಸ್ತುತಿಯನ್ನು ಸಮರ್ಪಿಸಿರಿ, ಆತನ ಕೃಪೆಯು ಶಾಶ್ವತವಾಗಿದೆ ಎಂಬದಾಗಿ ಆ ವಾದ್ಯಗಳೊಡನೆ ಭಜಿಸುತ್ತಿದ್ದರು. ಯಾಜಕರು ಇವರ ಎದುರಾಗಿ ನಿಂತು ತುತೂರಿಯೂದುತ್ತಿದ್ದರು. ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರೂ ನಿಂತಿದ್ದರು.",
			"7": "<sup>7</sup> ಸೊಲೊಮೋನನು ಮಾಡಿಸಿದ ತಾಮ್ರದ ಯಜ್ಞವೇದಿಯು ಸಮರ್ಪಣೆಯಾಗುವ ಸರ್ವಾಂಗ ಹೋಮದ್ರವ್ಯಗಳನ್ನೂ ಧಾನ್ಯನೈವೇದ್ಯಗಳನ್ನೂ ಸಮಾಧಾನಯಜ್ಞದ ಕೊಬ್ಬನ್ನೂ ಹಿಡಿಯಲಾರದ್ದರಿಂದ ಅವನು ಯೆಹೋವನ ಆಲಯದ ಮುಂದಿನ ಪ್ರಾಕಾರದ ಮಧ್ಯಸ್ಥಳವನ್ನು ಪ್ರತಿಷ್ಠಿಸಿ ಅಲ್ಲಿ ಇವುಗಳನ್ನೆಲ್ಲಾ ಸಮರ್ಪಿಸಿದನು.",
			"8": "<sup>8</sup> ಈ ಪ್ರಕಾರ ಸೊಲೊಮೋನನೂ ಹಮಾತಿನ ದಾರಿಯಿಂದ ಐಗುಪ್ತದ ಹಳ್ಳದವರೆಗಿರುವ ಪ್ರಾಂತಗಳಿಂದ ಮಹಾಸಮೂಹವಾಗಿ ಕೂಡಿಬಂದಿದ್ದ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರೂ ಅದೇ ಕಾಲದಲ್ಲಿ ಏಳು ದಿನಗಳವರೆಗೂ [ಪರ್ಣಶಾಲೆಗಳ] ಜಾತ್ರೆ ನಡಿಸಿದರು.",
			"9": "<sup>9</sup> ತರುವಾಯ ಎಂಟನೆಯ ದಿನದಲ್ಲಿ ಸಭೆ ನೆರೆಯಿತು. ಹೀಗೆ ಏಳು ದಿವಸ ಯಜ್ಞವೇದಿ ಪ್ರತಿಷ್ಠೆಯನ್ನೂ ಇನ್ನು ಏಳು ದಿವಸ ಜಾತ್ರೆಯನ್ನೂ ನಡಿಸಿದರು.",
			"10": "<sup>10</sup> ಏಳನೆಯ ತಿಂಗಳಿನ ಇಪ್ಪತ್ತಮೂರನೆಯ ದಿನದಲ್ಲಿ ಅರಸನು ಜನರಿಗೆ ಅಪ್ಪಣೆಕೊಡಲು ಅವರು ಯೆಹೋವನಿಂದ ದಾವೀದ ಸೊಲೊಮೋನರಿಗೂ ಆತನ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಾಯೇಲ್ಯರಿಗೂ ಉಂಟಾದ ಉಪಕಾರಗಳನ್ನು ನೆನಸಿಕೊಂಡು ಆನಂದಭರಿತರಾಗಿ ಹರ್ಷಿಸುತ್ತಾ ತಮ್ಮ ತಮ್ಮ ನಿವಾಸಗಳಿಗೆ ಹೋದರು.",
			"11": "<sup>11</sup> ಸೊಲೊಮೋನನು ಯೆಹೋವನ ಆಲಯವನ್ನೂ ತನ್ನ ಅರಮನೆಯನ್ನೂ ಕಟ್ಟಿಸಿ ಆ ಕಟ್ಟಡಗಳ ವಿಷಯವಾಗಿ ತನ್ನ ಮನಸ್ಸಿನಲ್ಲಿದ್ದದನ್ನೆಲ್ಲಾ ಕೊನೆಗಾಣಿಸಿದ ನಂತರ",
			"12": "<sup>12</sup> ಯೆಹೋವನು ಅವನಿಗೆ ರಾತ್ರಿಯಲ್ಲಿ ಪ್ರತ್ಯಕ್ಷನಾಗಿ - ನೀನು ನನಗೆ ಮಾಡಿದ ಪ್ರಾರ್ಥನೆಯನ್ನು ಲಾಲಿಸಿ ಈ ಮನೆಯನ್ನು ನನ್ನ ಯಜ್ಞಾಲಯಕ್ಕಾಗಿ ಆರಿಸಿಕೊಂಡಿದ್ದೇನೆ.",
			"13": "<sup>13</sup> ನಾನು ಆಕಾಶವನ್ನು ಮಳೆಗರೆಯದಂತೆ ಮುಚ್ಚುವಾಗಲೂ ದೇಶವನ್ನು ತಿಂದುಬಿಡುವದಕ್ಕೆ ಮಿಡಿತೆಗಳನ್ನು ಕಳುಹಿಸುವಾಗಲೂ ನನ್ನ ಪ್ರಜೆಯ ಮೇಲೆ ಘೋರ ವ್ಯಾಧಿಯನ್ನು ಬರಮಾಡುವಾಗಲೂ",
			"14": "<sup>14</sup> ನನ್ನವರೆಂದು ಹೆಸರುಗೊಂಡ ನನ್ನ ಪ್ರಜೆಗಳು ತಮ್ಮನ್ನು ತಗ್ಗಿಸಿಕೊಂಡು ತಮ್ಮ ಕೆಟ್ಟ ನಡತೆಯನ್ನು ಬಿಟ್ಟು ತಿರುಗಿಕೊಂಡು ನನ್ನನ್ನು ಪ್ರಾರ್ಥಿಸಿ ನನ್ನ ದರ್ಶನವನ್ನು ಬಯಸುವದಾದರೆ ನಾನು ಪರಲೋಕದಿಂದ ಲಾಲಿಸಿ ಅವರ ಪಾಪಗಳನ್ನು ಕ್ಷಮಿಸಿ ಅವರ ದೇಶದಲ್ಲಿ ಆರೋಗ್ಯವನ್ನು ದಯಪಾಲಿಸುವೆನು.",
			"15": "<sup>15</sup> ಇನ್ನು ಮುಂದೆ ಈ ಸ್ಥಳದಲ್ಲಿ ಪ್ರಾರ್ಥಿಸುವವರನ್ನು ಕಟಾಕ್ಷಿಸಿ ಅವರ ವಿಜ್ಞಾಪನೆಗೆ ಕಿವಿಗೊಡುವೆನು.",
			"16": "<sup>16</sup> ನನ್ನ ನಾಮಮಹತ್ತು ಈ ಆಲಯದಲ್ಲಿ ಶಾಶ್ವತವಾಗಿರುವಂತೆ ಇದನ್ನು ನನಗೋಸ್ಕರ ಆರಿಸಿ ಪ್ರತಿಷ್ಠಿಸಿಕೊಂಡಿದ್ದೇನೆ; ನನ್ನ ದೃಷ್ಟಿಯೂ ಮನಸ್ಸೂ ಸದಾ ಅಲ್ಲೇ ಇರುವವು.",
			"17": "<sup>17</sup> ನೀನು ನಿನ್ನ ತಂದೆಯಾದ ದಾವೀದನಂತೆ ನನಗೆ ವಿಧೇಯನಾಗಿ ನಡೆದುಕೊಂಡು ನನ್ನ ಆಜ್ಞಾವಿಧಿನ್ಯಾಯಗಳನ್ನು ಕೈಕೊಳ್ಳುವದಾದರೆ",
			"18": "<sup>18</sup> ನಿನ್ನ ರಾಜ್ಯಸಿಂಹಾಸನವನ್ನು ಸ್ಥಿರಪಡಿಸುವೆನು; ಮತ್ತು ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ನಿನ್ನ ಸಂತಾನದವರು ತಪ್ಪದೆ ರಾಜ್ಯಾಧಿಕಾರ ನಡಿಸುವರು ಎಂಬದಾಗಿ ನಿನ್ನ ತಂದೆಯಾದ ದಾವೀದನಿಗೆ ಮಾಡಿದ ವಾಗ್ದಾನವನ್ನು ನೆರವೇರಿಸುವೆನು.",
			"19": "<sup>19</sup> ಆದರೆ ನೀವು ದ್ರೋಹಿಗಳಾಗಿ ನನ್ನ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ಉಲ್ಲಂಘಿಸಿ ಅನ್ಯದೇವತೆಗಳನ್ನು ಹಿಂಬಾಲಿಸಿ",
			"20": "<sup>20</sup> ಅವುಗಳಿಗೆ ಕೈಮುಗಿಯುವದಾದರೆ ನಾನು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಕೊಟ್ಟ ನನ್ನ ದೇಶದಿಂದ ಅವರನ್ನು ಕಿತ್ತುಹಾಕಿ ನನ್ನ ಹೆಸರಿಗೋಸ್ಕರ ಪ್ರತಿಷ್ಠಿಸಿಕೊಂಡ ಈ ಆಲಯವನ್ನು ನಿರಾಕರಿಸಿಬಿಟ್ಟು ಎಲ್ಲಾ ಜನಾಂಗಗಳವರು ಗಾದೆಗಳನ್ನು ಕಲ್ಪಿಸುವದಕ್ಕೂ ನಿಂದಿಸುವದಕ್ಕೂ ಅದು ಆಸ್ಪದವಾಗುವಂತೆ ಮಾಡುವೆನು.",
			"21": "<sup>21</sup> ಉನ್ನತವಾದ ಈ ಮಂದಿರದ ಮಾರ್ಗವಾಗಿ ಹೋಗುವವರು ಅದನ್ನು ನೋಡಿ ವಿಸ್ಮಿತರಾಗಿ - ಯೆಹೋವನು ಈ ದೇಶವನ್ನೂ ಆಲಯವನ್ನೂ ಹೀಗೇಕೆ ಮಾಡಿದನು ಎಂದು ಕೇಳುವರು.",
			"22": "<sup>22</sup> ಆಗ ಅವರಿಗೆ - ಈ ದೇಶದವರು ತಮ್ಮನ್ನು ಐಗುಪ್ತದೇಶದಿಂದ ಬರಮಾಡಿದ ತಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಬಿಟ್ಟು ಅನ್ಯದೇವತೆಗಳನ್ನು ಅವಲಂಬಿಸಿ ಅವುಗಳಿಗೆ ಅಡ್ಡಬಿದ್ದು ಸೇವಿಸಿದ್ದರಿಂದಲೇ ಆತನು ಈ ಎಲ್ಲಾ ಆಪತ್ತನ್ನು ಅವರಿಗೆ ಬರಮಾಡಿದ್ದಾನೆ ಎಂಬ ಉತ್ತರ ಸಿಕ್ಕುವದು ಅಂದನು."
		},
		"8": {
			"1": "<sup>1</sup> ಸೊಲೊಮೋನನು ಇಪ್ಪತ್ತು ವರುಷಗಳಲ್ಲಿ ಯೆಹೋವನ ಆಲಯವನ್ನೂ ತನ್ನ ಅರಮನೆಯನ್ನೂ ಕಟ್ಟಿಸಿ ತೀರಿಸಿದನು.",
			"2": "<sup>2</sup> ಮತ್ತು ಹೂರಾಮನಿಂದ ತನಗೆ ದೊರಕಿದ ಪಟ್ಟಣಗಳನ್ನು ಭದ್ರಪಡಿಸಿ ಅವುಗಳಲ್ಲಿ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಇರಿಸಿದನು.",
			"3": "<sup>3</sup> ಅನಂತರ ಅವನು ಚೋಬರಾಜ್ಯದ ಹಮಾತಿಗೆ ಹೊರಟು ಅದನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡು",
			"4": "<sup>4</sup> ಅರಣ್ಯದಲ್ಲಿರುವ ತದ್ಮೋರ್ ಪಟ್ಟಣವನ್ನೂ ಹಮಾತಿನಲ್ಲಿ ತಾನು ಕಟ್ಟಿಸಿದ್ದ ಎಲ್ಲಾ ಉಗ್ರಾಣದ ಪಟ್ಟಣಗಳನ್ನೂ ಭದ್ರಪಡಿಸಿದನು.",
			"5": "<sup>5</sup> ಇದಲ್ಲದೆ ಮೇಲಿನ ಮತ್ತು ಕೆಳಗಿನ ಬೇತ್ಹೋರೋನ್ ಎಂಬ ಊರುಗಳನ್ನು ಪೌಳಿಗೋಡೆ ಕದ ಅಗುಳಿ ಇವುಗಳನ್ನು ಏರ್ಪಡಿಸಿ ಕೋಟೆಗಳನ್ನಾಗಿ ಮಾಡಿದನು.",
			"6": "<sup>6</sup> ಬಾಲಾತೂರು, ತನ್ನ ಎಲ್ಲಾ ಉಗ್ರಾಣದ ಪಟ್ಟಣ, ಯುದ್ಧರಥಗಳನ್ನಿರಿಸುವ ಪಟ್ಟಣ, ರಾಹುತರ ಪಟ್ಟಣ ಇವುಗಳನ್ನೂ ಕಟ್ಟಿಸಿದನು. ಮತ್ತು ಯೆರೂಸಲೇಮಿನಲ್ಲಿಯೂ ಲೆಬನೋನಿನಲ್ಲಿಯೂ ತನ್ನ ರಾಜ್ಯದ ಎಲ್ಲಾ ಪ್ರಾಂತಗಳಲ್ಲಿಯೂ ತನ್ನ ಮನಸ್ಸಿಗೆ ಬಂದವುಗಳನ್ನೆಲ್ಲಾ ಕಟ್ಟಿಸಿದನು.",
			"7": "<sup>7</sup> ಇದಲ್ಲದೆ ಇಸ್ರಾಯೇಲ್ಯರಿಂದ ಸಂಹೃತರಾಗದೆ ಕಾನಾನ್ ದೇಶದಲ್ಲಿ ಉಳಿದ ಹಿತ್ತಿಯ ಅಮೋರಿಯ ಪೆರಿಜೀಯ ಹಿವ್ವಿಯ ಯೆಬೂಸಿಯ ಮೊದಲಾದ ಅನ್ಯಜನಾಂಗಗಳ ಸಂತಾನದವರೆಲ್ಲರನ್ನೂ [ತನ್ನ ಕೆಲಸಕ್ಕಾಗಿ] ಬಿಟ್ಟಿ ಹಿಡಿದನು.",
			"8": "<sup>8</sup> ಅವರು ಇಂದಿನವರೆಗೂ ಬಿಟ್ಟೀ ಕೆಲಸದವರಾಗಿರುತ್ತಾರೆ.",
			"9": "<sup>9</sup> ಆದರೆ ಸೊಲೊಮೋನನು ತನ್ನ ಕೆಲಸಕ್ಕಾಗಿ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಬಿಟ್ಟಿಹಿಡಿಯಲಿಲ್ಲ. ಅವರನ್ನು ಸೈನಿಕರನ್ನಾಗಿಯೂ ಅಧಿಪತಿಗಳನ್ನಾಗಿಯೂ ಸರದಾರರನ್ನಾಗಿಯೂ ರಥಾಶ್ವಬಲಗಳ ನಾಯಕರನ್ನಾಗಿಯೂ ನೇಮಿಸಿಕೊಂಡನು.",
			"10": "<sup>10</sup> ಈ ಇನ್ನೂರೈವತ್ತು ಮಂದಿ ಮುಖ್ಯಾಧಿಪತಿಗಳು ಅರಸನಾದ ಸೊಲೊಮೋನನ ಆಳುಗಳ ಮೇಲೆ ಅಧಿಕಾರಿಗಳಾಗಿದ್ದರು.",
			"11": "<sup>11</sup> ಸೊಲೊಮೋನನು - ಯಾವ ಯಾವ ಸ್ಥಳಗಳಲ್ಲಿ ಯೆಹೋವನ ಮಂಜೂಷವಿತ್ತೋ ಅವು ಪವಿತ್ರಸ್ಥಾನಗಳೇ; ಆದದರಿಂದ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾದ ದಾವೀದನ ಮನೆಯಲ್ಲಿ ಫರೋಹನ ಮಗಳಾದ ನನ್ನ ಹೆಂಡತಿಯು ವಾಸಿಸಬಾರದು ಅಂದುಕೊಂಡು ಆಕೆಯನ್ನು ದಾವೀದನಗರದಿಂದ ತಾನು ಆಕೆಗೋಸ್ಕರ ಕಟ್ಟಿಸಿದ ಮಂದಿರಕ್ಕೆ ಕರಕೊಂಡು ಬಂದನು.",
			"12": "<sup>12</sup> ಆ ಕಾಲದಿಂದ ಸೊಲೊಮೋನನು, ತಾನು ಯೆಹೋವನಿಗೋಸ್ಕರ ಮಂಟಪದ ಮುಂದೆ ಕಟ್ಟಿಸಿದ",
			"13": "<sup>13</sup> ಯೆಹೋವನ ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಮೋಶೆಯ ಆಜ್ಞೆಯಂತೆ ಸಬ್ಬತ್ ದಿವಸ, ಅಮಾವಾಸ್ಯೆ ಇವುಗಳಲ್ಲಿಯೂ ಹುಳಿಯಿಲ್ಲದ ರೊಟ್ಟಿಗಳ ಜಾತ್ರೆ, ಪಂಚಾಶತ್ತಮದಿನದ ಜಾತ್ರೆ, ಪರ್ಣಶಾಲೆಗಳ ಜಾತ್ರೆ ಎಂಬೀ ಮೂರು ವಾರ್ಷಿಕಜಾತ್ರೆಗಳಲ್ಲಿಯೂ ಆಯಾ ದಿವಸಗಳಿಗೆ ನೇಮಕವಾದ ಸರ್ವಾಂಗಹೋಮಗಳನ್ನು ಸಮರ್ಪಿಸುತ್ತಿದ್ದನು.",
			"14": "<sup>14</sup> ಇದಲ್ಲದೆ ತನ್ನ ತಂದೆಯಾದ ದಾವೀದನ ವಿಧಿಗನುಸಾರವಾಗಿ ಯಾಜಕವರ್ಗಗಳವರನ್ನೂ ಲೇವಿಯರನ್ನೂ ಅವರವರ ಸೇವಾವೃತ್ತಿಗೆ ನೇಮಿಸಿದನು. ಲೇವಿಯರು ಆಯಾ ದಿನದ ನೇಮದ ಪ್ರಕಾರ ದೇವಭಜನೆಮಾಡಿ ಯಾಜಕರ ಕೈಕೆಳಗೆ ಸೇವೆಮಾಡುತ್ತಲೂ ದ್ವಾರಪಾಲಕರು ತಮ್ಮ ತಮ್ಮ ವರ್ಗಗಳ ಸರತಿಯ ಮೇಲೆ ಆಯಾ ಬಾಗಲುಗಳನ್ನು ಕಾಯುತ್ತಲೂ ಇರಬೇಕಾಯಿತು. ಇದೇ ದೇವರ ಮನುಷ್ಯನಾದ ದಾವೀದ ರಾಜನ ಅಪ್ಪಣೆ.",
			"15": "<sup>15</sup> ಅವನು ಯಾಜಕರನ್ನೂ ಲೇವಿಯರನ್ನೂ ಭಂಡಾರಗಳನ್ನೂ ಕುರಿತು ಕೊಟ್ಟ ಅಪ್ಪಣೆಯನ್ನು ಅವರು ಸ್ವಲ್ಪವಾದರೂ ಮೀರಲಿಲ್ಲ.",
			"16": "<sup>16</sup> ಈ ಪ್ರಕಾರ ಅಸ್ತಿವಾರ ಮೊದಲುಗೊಂಡು ಸಮಾಪ್ತಿಯವರೆಗೂ ಸೊಲೊಮೋನನ ಎಲ್ಲಾ ಕೆಲಸವೂ ಮುಗಿದು ಯೆಹೋವನ ಆಲಯವು ಸಿದ್ಧವಾಯಿತು.",
			"17": "<sup>17</sup> ಆಮೇಲೆ ಸೊಲೊಮೋನನು ಎದೋಮ್ ದೇಶದ ಸಮುದ್ರತೀರದಲ್ಲಿರುವ ಎಚ್ಯೋನ್ಗೆಬೆರ್, ಏಲೋತ್ ಎಂಬ ಊರುಗಳಿಗೆ ಹೋದನು.",
			"18": "<sup>18</sup> ಹೂರಾಮನು ಅವನಿಗೆ ತನ್ನ ಆಳುಗಳ ಮುಖಾಂತರ ಹಡಗುಗಳನ್ನು ಕಳುಹಿಸಿದ್ದಲ್ಲದೆ ಸಮುದ್ರಪ್ರಯಾಣದಲ್ಲಿ ನಿಪುಣರಾದ ನಾವಿಕರನ್ನೂ ಕಳುಹಿಸಿದನು. ಅವರು ಸೊಲೊಮೋನನ ಸೇವಕರೊಡನೆ ಓಫೀರಿಗೆ ಪ್ರಯಾಣಮಾಡಿ ಅಲ್ಲಿಂದ ಅರಸನಾದ ಸೊಲೊಮೋನನಿಗೆ ನಾನೂರೈವತ್ತು ತಲಾಂತು ಬಂಗಾರವನ್ನು ತಂದುಕೊಟ್ಟರು."
		},
		"9": {
			"1": "<sup>1</sup> ಶೆಬದ ರಾಣಿಯು ಸೊಲೊಮೋನನ ಕೀರ್ತಿಯನ್ನು ಕೇಳಿ ಅವನನ್ನು ಒಗಟುಗಳಿಂದ ಪರೀಕ್ಷಿಸುವದಕ್ಕೆ ಯೆರೂಸಲೇಮಿಗೆ ಬಂದಳು. ಆಕೆಯು ಸುಗಂಧದ್ರವ್ಯ, ಅಪರಿಮಿತವಾದ ಬಂಗಾರ, ರತ್ನ ಇವುಗಳನ್ನು ಒಂಟೆಗಳ ಮೇಲೆ ಹೇರಿಸಿಕೊಂಡು ಮಹಾ ಪರಿವಾರದೊಡನೆ ಸೊಲೊಮೋನನ ಬಳಿಗೆ ಬಂದು ತನ್ನ ಮನಸ್ಸಿನಲ್ಲಿ ಗೊತ್ತುಮಾಡಿಕೊಂಡಿದ್ದ ಎಲ್ಲಾ ವಿಷಯಗಳನ್ನು ಕುರಿತು ಅವನೊಡನೆ ಸಂಭಾಷಿಸಿದಳು.",
			"2": "<sup>2</sup> ಸೊಲೊಮೋನನು ಆಕೆಯ ಪ್ರಶ್ನೆಗಳಿಗೆಲ್ಲಾ ಉತ್ತರಕೊಟ್ಟನು; ಅವುಗಳಲ್ಲಿ ಅವನಿಗೆ ತಿಳಿಯದಂಥದು ಒಂದೂ ಇರಲಿಲ್ಲವಾದದರಿಂದ ಎಲ್ಲವುಗಳಿಗೂ ಉತ್ತರಕೊಟ್ಟನು.",
			"3": "<sup>3</sup> ಶೆಬದ ರಾಣಿಯು ಸೊಲೊಮೋನನ ಜ್ಞಾನ, ಅವನು ಕಟ್ಟಿಸಿದ ಅರಮನೆ,",
			"4": "<sup>4</sup> ಅವನ ಭೋಜನ ಪೀಠದ ಆಹಾರ, ಆ ಪೀಠದ ಸುತ್ತಲಿರುವ ಉದ್ಯೋಗಸ್ಥರ ಆಸನಗಳು, ಅವನ ಪರಿಚಾರಕರ ಸೇವಾಕ್ರಮ ಮತ್ತು ಅವರ ಉಡುಪುಗಳು, ಅವನ ಪಾನಸೇವಕರು, ಅವರ ಉಡುಪುಗಳು ಇವುಗಳನ್ನೂ ಅವನು ಯೆಹೋವನ ಆಲಯದಲ್ಲಿ ಸಮರ್ಪಿಸುತ್ತಿದ್ದ ಸರ್ವಾಂಗಹೋಮಗಳನ್ನೂ ನೋಡಿದಾಗ",
			"5": "<sup>5</sup> ವಿಸ್ಮಿತಳಾಗಿ ಅವನಿಗೆ - ನಾನು ನನ್ನ ದೇಶದಲ್ಲಿ ನಿನ್ನ ಜ್ಞಾನವನ್ನೂ ಕೃತ್ಯಗಳನ್ನೂ ಕುರಿತು ಕೇಳಿದ್ದು ಸತ್ಯವಾಗಿದೆ.",
			"6": "<sup>6</sup> ನಾನಾಗಿ ಇಲ್ಲಿಗೆ ಬಂದು ಕಣ್ಣಾರೆ ನೋಡುವದಕ್ಕಿಂತ ಮೊದಲು ಜನರು ಹೇಳಿದ ಸುದ್ದಿಯನ್ನು ನಂಬಲಿಲ್ಲ; ಈಗ ನೋಡಿದರೆ ನಿನ್ನ ಜ್ಞಾನಾತಿಶಯವು ನಾನು ಕೇಳಿದ್ದಕ್ಕಿಂತ ಹೆಚ್ಚಾಗಿದೆ; ಜನರು ನನಗೆ ಇದರಲ್ಲಿ ಅರ್ಧವನ್ನಾದರೂ ಹೇಳಲಿಲ್ಲ.",
			"7": "<sup>7</sup> ನಿನ್ನ ಪ್ರಜೆಗಳೂ ಸದಾ ನಿನ್ನ ಮುಂದೆ ನಿಂತುಕೊಂಡು ನಿನ್ನ ಜ್ಞಾನವಾಕ್ಯಗಳನ್ನು ಕೇಳುವ ನಿನ್ನ ಸೇವಕರೂ ಧನ್ಯರು.",
			"8": "<sup>8</sup> ನಿನ್ನನ್ನು ಮೆಚ್ಚಿ ತನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಅರಸನಾಗುವದಕ್ಕೆ ನಿನ್ನನ್ನು ತನ್ನ ಸಿಂಹಾಸನದ ಮೇಲೆ ಕುಳ್ಳಿರಿಸಿದ ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಸ್ತೋತ್ರವಾಗಲಿ; ನಿನ್ನ ದೇವರು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಪ್ರೀತಿಸಿ ಅವರನ್ನು ನಿರಂತರವಾಗಿ ಸ್ಥಿರಗೊಳಿಸಬೇಕೆಂದಿರುವದರಿಂದ ಅವರ ನೀತಿನ್ಯಾಯಗಳನ್ನು ಸ್ಥಾಪಿಸುವದಕ್ಕೋಸ್ಕರ ನಿನ್ನನ್ನೇ ಅರಸನನ್ನಾಗಿ ನೇಮಿಸಿದ್ದಾನೆ ಎಂದು ಹೇಳಿದಳು.",
			"9": "<sup>9</sup> ಆಕೆಯು ಅರಸನಿಗೆ ನೂರಿಪ್ಪತ್ತು ತಲಾಂತು ಬಂಗಾರವನ್ನೂ ಅಪರಿಮಿತಸುಗಂಧದ್ರವ್ಯವನ್ನೂ ರತ್ನಗಳನ್ನೂ ಕೊಟ್ಟಳು. ಶೆಬದ ರಾಣಿಯು ಅರಸನಾದ ಸೊಲೊಮೋನನಿಗೆ ಕೊಟ್ಟ ಸುಗಂಧದ್ರವ್ಯಕ್ಕೆ ಸಮಾನವಾದದ್ದು ಇಲ್ಲ.",
			"10": "<sup>10</sup> ಹೂರಾಮನ ಮತ್ತು ಸೊಲೊಮೋನನ ಆಳುಗಳು ಓಫೀರ್ದೇಶದ ಬಂಗಾರವನ್ನಲ್ಲದೆ ಸುಗಂಧದ ಮರವನ್ನೂ ರತ್ನಗಳನ್ನೂ ತಂದರು.",
			"11": "<sup>11</sup> ಅರಸನು ಸುಗಂಧದ ಮರದಿಂದ ದೇವಾಲಯ, ಅರಮನೆ ಇವುಗಳಿಗೆ ಸೋಪಾನಗಳನ್ನೂ ಗಾಯಕರಿಗೋಸ್ಕರ ಕಿನ್ನರಿಗಳನ್ನೂ ಸ್ವರಮಂಡಲಗಳನ್ನೂ ಮಾಡಿಸಿದನು. ಇಂಥ [ಸುಗಂಧದ ಮರವು] ಮುಂಚೆ ಯೆಹೂದ ದೇಶದಲ್ಲಿ ಕಾಣಲಿಲ್ಲ.",
			"12": "<sup>12</sup> ಅರಸನಾದ ಸೊಲೊಮೋನನು ಶೆಬದ ರಾಣಿಯಿಂದ ತನಗೆ ದೊರಕಿದ ಬಹುಮಾನಕ್ಕೆ ಪ್ರತಿಯಾಗಿ ಆಕೆಗೆ ತಾನು ಕೊಟ್ಟ ವಸ್ತುಗಳನ್ನಲ್ಲದೆ ಆಕೆಯು ಕೇಳಿದವುಗಳನ್ನೆಲ್ಲಾ ಕೊಟ್ಟುಬಿಟ್ಟನು. ಅನಂತರ ಆಕೆಯು ತನ್ನ ಪರಿವಾರದವರೊಡನೆ ಸ್ವದೇಶಕ್ಕೆ ಹೊರಟುಹೋದಳು.",
			"13": "<sup>13</sup> ಸೊಲೊಮೋನನಿಗೆ ದೇಶಾಂತರವ್ಯಾಪಾರಿಗಳೂ ವರ್ತಕರೂ ಅರಬಸ್ಥಾನದ ಅರಸರೂ ದೇಶಾಧಿಪತಿಗಳೂ ತೆರುತ್ತಿದ್ದ ಬೆಳ್ಳಿಬಂಗಾರವಲ್ಲದೆ",
			"14": "<sup>14</sup> ಪ್ರತಿವರುಷ ಆರುನೂರರುವತ್ತಾರು ತಲಾಂತು ಬಂಗಾರವು ದೊರಕುತ್ತಿತ್ತು.",
			"15": "<sup>15</sup> ಅರಸನಾದ ಸೊಲೊಮೋನನು ಬಂಗಾರದ ತಗಡಿನಿಂದ ಇನ್ನೂರು ಗುರಾಣಿಗಳನ್ನು ಮಾಡಿಸಿದನು; ಪ್ರತಿಯೊಂದು ಗುರಾಣಿಗೆ ಆರುನೂರು ತೊಲೆ ಬಂಗಾರ ಹಿಡಿಯಿತು.",
			"16": "<sup>16</sup> ಇದಲ್ಲದೆ ಅವನು ಬಂಗಾರದ ತಗಡಿನಿಂದ ಮುನ್ನೂರು ಖೇಡ್ಯಗಳನ್ನು ಮಾಡಿಸಿದನು; ಪ್ರತಿಯೊಂದು ಖೇಡ್ಯಕ್ಕೆ ಮುನ್ನೂರು ತೊಲೆ ಬಂಗಾರ ಹಿಡಿಯಿತು. ಅವನು ಇವುಗಳನ್ನು ಲೆಬನೋನಿನ ತೋಪು ಎನಿಸಿಕೊಳ್ಳುವ ಮಂದಿರದಲ್ಲಿರಿಸಿದನು.",
			"17": "<sup>17</sup> ಇದಲ್ಲದೆ ಅರಸನು ದೊಡ್ಡದಾದ ಒಂದು ದಂತ ಸಿಂಹಾಸನವನ್ನು ಮಾಡಿಸಿ ಅದನ್ನು ಚೊಕ್ಕ ಬಂಗಾರದ ತಗಡಿನಿಂದ ಹೊದಿಸಿದನು.",
			"18": "<sup>18</sup> ಅದಕ್ಕೆ ಹೊಂದಿಕೆಯಾಗಿ ಆರು ಮೆಟ್ಲುಗಳೂ ಬಂಗಾರದ ಪಾದಪೀಠವೂ ಇದ್ದವು; ಆಸನಕ್ಕೆ ಎರಡು ಕೈಗಳೂ ಅವುಗಳ ಹತ್ತಿರ ಎರಡು ಸಿಂಹಗಳೂ ಇದ್ದವು.",
			"19": "<sup>19</sup> ಆರು ಮೆಟ್ಲುಗಳ ಎರಡು ಕಡೆಗಳಲ್ಲಿಯೂ ಒಟ್ಟು ಹನ್ನೆರಡು ಸಿಂಹಗಳು ನಿಂತಿದ್ದವು. ಇಂಥ ಸಿಂಹಾಸನವು ಬೇರೆ ಯಾವ ರಾಜ್ಯದಲ್ಲಿಯೂ ಇರಲಿಲ್ಲ. ಅರಸನಾದ ಸೊಲೊಮೋನನ ಎಲ್ಲಾ ಪಾನಪಾತ್ರೆಗಳು ಬಂಗಾರದವುಗಳು.",
			"20": "<sup>20</sup> ಲೆಬನೋನಿನ ತೋಪು ಎನಿಸಿಕೊಳ್ಳುವ ಮಂದಿರದ ಎಲ್ಲಾ ಸಾಮಾನುಗಳು ಚೊಕ್ಕ ಬಂಗಾರದವುಗಳು. ಸೊಲೊಮೋನನ ಕಾಲದಲ್ಲಿ ಬೆಳ್ಳಿಗೆ ಬೆಲೆಯೇ ಇರಲಿಲ್ಲ.",
			"21": "<sup>21</sup> ಅರಸನ ಹಡಗುಗಳು ಹೂರಾಮನ ನಾವಿಕರ ಜೊತೆಯಲ್ಲಿ ತಾರ್ಷೀಷಿಗೆ ಹೋಗಿ ಮೂರು ವರುಷಕ್ಕೊಮ್ಮೆ ಬಂಗಾರ ಬೆಳ್ಳಿ ದಂತ ಕಪಿ ನವಿಲು ಇವುಗಳನ್ನು ತರುತ್ತಿದ್ದವು.",
			"22": "<sup>22</sup> ಈ ಪ್ರಕಾರ ಅರಸನಾದ ಸೊಲೊಮೋನನು ಐಶ್ವರ್ಯದಲ್ಲಿಯೂ ಜ್ಞಾನದಲ್ಲಿಯೂ ಭೂಲೋಕದ ಎಲ್ಲಾ ಅರಸರಿಗಿಂತ ಮಿಗಿಲಾಗಿದ್ದನು.",
			"23": "<sup>23</sup> ಭೂರಾಜರೆಲ್ಲರೂ ದೇವರು ಅವನಿಗೆ ಅನುಗ್ರಹಿಸಿದ ಜ್ಞಾನ ವಾಕ್ಯಗಳನ್ನು ಕೇಳುವದಕ್ಕೋಸ್ಕರ ಅವನ ದರ್ಶನಕ್ಕೆ ಬಂದರು.",
			"24": "<sup>24</sup> ಅವರೆಲ್ಲರೂ ಅವನಿಗೆ ವರುಷ ವರುಷ ಬೆಳ್ಳಿ ಬಂಗಾರದ ಸಾಮಾನು, ಉಡುಪು, ಯುದ್ಧಾಯುಧ, ಸುಗಂಧದ್ರವ್ಯ, ಕುದುರೆ, ಹೇಸರಕತ್ತೆ ಇವುಗಳನ್ನು ಕಾಣಿಕೆಯಾಗಿ ಕೊಡುತ್ತಿದ್ದರು.",
			"25": "<sup>25</sup> ಸೊಲೊಮೋನನ ಅಶ್ವಸಹಿತವಾದ ರಥಗಳು ನಾಲ್ಕು ಸಾವಿರ, ರಾಹುತರು ಹನ್ನೆರಡು ಸಾವಿರ. ಇವುಗಳನ್ನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ತನ್ನ ಬಳಿಯಲ್ಲಿಯೂ ರಥಗಳಿಗೋಸ್ಕರ ನೇಮಿಸಿದ ಪಟ್ಟಣಗಳಲ್ಲಿಯೂ ಇರಿಸಿದನು.",
			"26": "<sup>26</sup> ಅವನು ಯೂಫ್ರೇಟೀಸ್ ನದಿ ಮೊದಲುಗೊಂಡು ಫಿಲಿಷ್ಟಿಯರ ಮತ್ತು ಐಗುಪ್ತ್ಯರ ದೇಶಗಳವರೆಗೂ ಇರುವ ಎಲ್ಲಾ ರಾಜರನ್ನು ಆಳುತ್ತಿದ್ದನು.",
			"27": "<sup>27</sup> ಅರಸನ ಮುಖಾಂತರವಾಗಿ ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಬೆಳ್ಳಿಯು ಕಲ್ಲಿನ ಹಾಗೂ ದೇವದಾರು ಮರಗಳು ಇಳಕಲಿನ ಪ್ರದೇಶದಲ್ಲಿ ಬೆಳೆಯುವ ಅತ್ತಿಮರಗಳ ಹಾಗೂ ಹೇರಳವಾದವು.",
			"28": "<sup>28</sup> ಸೊಲೊಮೋನನಿಗೋಸ್ಕರ ಐಗುಪ್ತ ಮುಂತಾದ ಎಲ್ಲಾ ದೇಶಗಳಿಂದ ಕುದುರೆಗಳನ್ನು ತರುತ್ತಿದ್ದರು.",
			"29": "<sup>29</sup> ಸೊಲೊಮೋನನ ಉಳಿದ ಪೂರ್ವೋತ್ತರ ಚರಿತ್ರೆಯು ಪ್ರವಾದಿಯಾದ ನಾತಾನನ ಚರಿತ್ರೆ, ಶೀಲೋವಿನವನಾದ ಅಹೀಯನ ಪ್ರವಾದನೆ, ದಿವ್ಯದರ್ಶಿಯಾದ ಇದ್ದೋವನು ನೆಬಾಟನ ಮಗನಾದ ಯಾರೊಬ್ಬಾಮನನ್ನು ಕುರಿತು ನುಡಿದ ದರ್ಶನೋಕ್ತಿ ಎಂಬ ಗ್ರಂಥಗಳಲ್ಲಿ ಬರೆದಿರುತ್ತದೆ.",
			"30": "<sup>30</sup> ಸೊಲೊಮೋನನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿದ್ದುಕೊಂಡು ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ನಾಲ್ವತ್ತು ವರುಷ ಆಳಿದನಂತರ ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಿದನು;",
			"31": "<sup>31</sup> ಅವನ ಶವವನ್ನು ಅವನ ತಂದೆಯಾದ ದಾವೀದನ ಪಟ್ಟಣದಲ್ಲಿ ಸಮಾಧಿ ಮಾಡಿದರು; ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ರೆಹಬ್ಬಾಮನು ಅರಸನಾದನು."
		},
		"10": {
			"1": "<sup>1</sup> ರೆಹಬ್ಬಾಮನು ಶೆಕೆಮಿಗೆ ಹೋದನು. ಅಲ್ಲಿ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರೂ ಅವನನ್ನು ಅರಸನನ್ನಾಗಿ ಮಾಡುವದಕ್ಕೆ ಕೂಡಿಬಂದಿದ್ದರು.",
			"2": "<sup>2</sup> ಇದಲ್ಲದೆ ಅರಸನಾದ ಸೊಲೊಮೋನನಿಗೆ ತಪ್ಪಿಸಿಕೊಂಡು ಐಗುಪ್ತಕ್ಕೆ ಓಡಿಹೋಗಿದ್ದ ನೆಬಾಟನ ಮಗನಾದ ಯಾರೊಬ್ಬಾಮನು ನಡೆದ ಸಂಗತಿಗಳ ವರ್ತಮಾನವನ್ನು ಕೇಳಿದೊಡನೆ ಐಗುಪ್ತದಿಂದ ಹಿಂದಿರುಗಿ ಬಂದಿದ್ದನು.",
			"3": "<sup>3</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಅವನನ್ನೂ ತಮ್ಮ ಬಳಿಗೆ ಕರಿಸಿಕೊಂಡರು. ಅವನು ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರೊಡನೆ ರೆಹಬ್ಬಾಮನ ಬಳಿಗೆ ಹೋಗಿ",
			"4": "<sup>4</sup> ಅವನಿಗೆ ನಿನ್ನ ತಂದೆಯು ನಮ್ಮ ಮೇಲೆ ಭಾರವಾದ ನೊಗವನ್ನು ಹೇರಿದನು; ನಿನ್ನ ತಂದೆಯು ನೇಮಿಸಿದ ಬಿಟ್ಟೀ ಕೆಲಸವನ್ನು ನೀನು ಕಡಿಮೆ ಮಾಡಿ ನಮ್ಮ ಮೇಲಿರುವ ಭಾರವಾದ ನೊಗವನ್ನು ಹಗುರಮಾಡುವದಾದರೆ ನಾವು ನಿನ್ನನ್ನು ಸೇವಿಸುವೆವು ಎಂದು ಹೇಳಲು ಅವನು -",
			"5": "<sup>5</sup> ನೀವು ಮೂರು ದಿನಗಳಾದ ನಂತರ ತಿರಿಗಿ ಬನ್ನಿರಿ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು; ಜನರು ಹೋದರು.",
			"6": "<sup>6</sup> ಆಗ ಅರಸನಾದ ರೆಹಬ್ಬಾಮನು ತನ್ನ ತಂದೆಯಾದ ಸೊಲೊಮೋನನ ಕಾಲದಲ್ಲಿ ಮಂತ್ರಿಗಳಾಗಿದ್ದ ಹಿರಿಯರನ್ನು ಕೂಡಿಸಿ ತಾನು ಜನರಿಗೆ ಕೊಡತಕ್ಕ ಉತ್ತರದ ವಿಷಯದಲ್ಲಿ ಅವರ ಆಲೋಚನೆಯನ್ನು ಕೇಳಿದನು.",
			"7": "<sup>7</sup> ಅವರು ಅವನಿಗೆ - ನೀನು ಈ ಜನರಿಗೆ ದಯತೋರಿಸಿ ಅವರನ್ನು ಮೆಚ್ಚಿಸುವ ಒಳ್ಳೇ ಉತ್ತರ ಕೊಡುವದಾದರೆ ಅವರು ಯಾವಾಗಲೂ ನಿನಗೆ ಒಳಗಾಗಿರುವರು ಅಂದರು.",
			"8": "<sup>8</sup> ಆದರೆ ಅವನು ಹಿರಿಯರ ಆಲೋಚನೆಯನ್ನು ನಿರಾಕರಿಸಿ ತನ್ನ ಸಂಗಡಲೇ ಬೆಳೆದು ತನಗೆ ಮಂತ್ರಿಗಳೂ ಆದ ಯೌವನಸ್ಥರನ್ನು ಕರಿಸಿ -",
			"9": "<sup>9</sup> ನನ್ನ ತಂದೆಯು ಹೇರಿದ ನೊಗವನ್ನು ಹಗುರಮಾಡಬೇಕೆಂದು ಬೇಡಿಕೊಳ್ಳುವ ಈ ಜನರಿಗೆ ಯಾವ ಉತ್ತರವನ್ನು ಕೊಡತಕ್ಕದ್ದು? ಈ ವಿಷಯದಲ್ಲಿ ನಿಮ್ಮ ಆಲೋಚನೆ ಏನು ಎಂದು ಕೇಳಲು",
			"10": "<sup>10</sup> ಅವರು ಅವನಿಗೆ - ನಿನ್ನ ತಂದೆಯು ನಮ್ಮ ಮೇಲೆ ಹೇರಿರುವ ಭಾರವಾದ ನೊಗವನ್ನು ಹಗುರಮಾಡು ಎಂಬದಾಗಿ ನಿನ್ನನ್ನು ಬೇಡಿಕೊಂಡ ಜನರಿಗೆ ನೀನು - ನನ್ನ ತಂದೆಯ ನಡುವಿಗಿಂತ ನನ್ನ ಕಿರಿಬೆರಳು ದಪ್ಪವಾಗಿದೆ;",
			"11": "<sup>11</sup> ನನ್ನ ತಂದೆಯು ನಿಮ್ಮ ಮೇಲೆ ಭಾರವಾದ ನೊಗವನ್ನು ಹೇರಿರುವದು ನಿಜ; ಆದರೆ ನಾನು ಅದಕ್ಕೆ ಇನ್ನೂ ಹೆಚ್ಚಿನ ಭಾರವನ್ನು ಕೂಡಿಸುತ್ತೇನೆ; ಅವನು ನಿಮ್ಮನ್ನು ಬಾರುಕೋಲುಗಳಿಂದ ಹೊಡೆದದ್ದು ನಿಜ; ನಾನಾದರೋ ನಿಮ್ಮನ್ನು ಮುಳ್ಳುಕೊರಡೆಗಳಿಂದ ದಂಡಿಸುವೆನು ಎಂಬದಾಗಿ ಹೇಳಬೇಕು ಎಂದು ಉತ್ತರಕೊಟ್ಟರು.",
			"12": "<sup>12</sup> ಯಾರೊಬ್ಬಾಮನೂ ಎಲ್ಲಾ ಜನರೂ ಅರಸನಾದ ರೆಹಬ್ಬಾಮನ ಅಪ್ಪಣೆಯಂತೆ ಮೂರನೆಯ ದಿನದಲ್ಲಿ ತಿರಿಗಿ ಅವನ ಬಳಿಗೆ ಬಂದರು.",
			"13": "<sup>13</sup> ಅರಸನು ಅವರಿಗೆ ಕಠಿನವಾದ ಉತ್ತರವನ್ನು ಕೊಟ್ಟನು; ಅವನು ಹಿರಿಯರ ಆಲೋಚನೆಯನ್ನು ನಿರಾಕರಿಸಿ",
			"14": "<sup>14</sup> ಯೌವನಸ್ಥರ ಆಲೋಚನೆಗನುಸಾರವಾಗಿ ಜನರಿಗೆ - ನನ್ನ ತಂದೆಯು ನಿಮ್ಮ ಮೇಲೆ ಭಾರವಾದ ನೊಗವನ್ನು ಹೇರಿರುವದು ನಿಜ; ಆದರೆ ನಾನು ಅದಕ್ಕೆ ಇನ್ನೂ ಹೆಚ್ಚಿನ ಭಾರವನ್ನು ಕೂಡಿಸುವೆನು. ಅವನು ನಿಮ್ಮನ್ನು ಬಾರು ಕೋಲುಗಳಿಂದ ಹೊಡೆದದ್ದು ನಿಜ; ನಾನಾದರೋ ನಿಮ್ಮನ್ನು ಮುಳ್ಳು ಕೊರಡೆಗಳಿಂದ ದಂಡಿಸುವೆನು ಎಂದು ನುಡಿದನು.",
			"15": "<sup>15</sup> ಅರಸನು ಜನರ ಮಾತನ್ನು ಕೇಳದೆಹೋದದ್ದು ದೈವಯೋಗದಿಂದಲೇ. ಈ ಪ್ರಕಾರ ಯೆಹೋವನು ಶೀಲೋವಿನವನಾದ ಅಹೀಯನ ಮುಖಾಂತರವಾಗಿ ನೆಬಾಟನ ಮಗನಾದ ಯಾರೊಬ್ಬಾಮನಿಗೆ ಹೇಳಿಸಿದ ಮಾತು ನೆರವೇರಿತು.",
			"16": "<sup>16</sup> ಅರಸನು ತಮ್ಮ ಮಾತನ್ನು ಲಕ್ಷಿಸಲಿಲ್ಲವೆಂದು ತಿಳಿದು ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರೂ ಅವನಿಗೆ - ದಾವೀದನಲ್ಲಿ ನಮಗೇನು ಪಾಲು? ಇಷಯನ ಮಗನಲ್ಲಿ ನಮಗೇನು ಬಾಧ್ಯತೆ? ಇಸ್ರಾಯೇಲ್ಯರೇ, ನಿಮ್ಮ ನಿಮ್ಮ ನಿವಾಸಗಳಿಗೆ ಹೋಗಿರಿ; ದಾವೀದನವರೇ, ನಿಮ್ಮ ಕುಲವನ್ನು ನೀವೇ ನೋಡಿಕೊಳ್ಳಿರಿ ಎಂದು ಹೇಳಿ ತಮ್ಮ ತಮ್ಮ ನಿವಾಸಗಳಿಗೆ ಹೋದರು.",
			"17": "<sup>17</sup> ಯೆಹೂದ ಪ್ರಾಂತದಲ್ಲಿದ್ದ ಇಸ್ರಾಯೇಲ್ಯರಾದರೋ ಅರಸನಾದ ರೆಹಬ್ಬಾಮನ ಅಧೀನದಲ್ಲಿದ್ದರು.",
			"18": "<sup>18</sup> ರೆಹಬ್ಬಾಮನು ಬಿಟ್ಟೀಕೆಲಸದವರ ಮೇಲ್ವಿಚಾರಕನಾದ ಹದೋರಾಮನನ್ನು ಇಸ್ರಾಯೇಲ್ಯರ ಬಳಿಗೆ ಕಳುಹಿಸಲು ಅವರು ಇವನನ್ನು ಕಲ್ಲೆಸೆದು ಕೊಂದರು. ಆಗ ಅರಸನಾದ ರೆಹಬ್ಬಾಮನು ಬೇಗನೆ ರಥವನ್ನೇರಿ ಯೆರೂಸಲೇಮಿಗೆ ಓಡಿ ಹೋದನು.",
			"19": "<sup>19</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಅಂದಿನಿಂದ ಇಂದಿನವರೆಗೂ ದಾವೀದನ ಕುಟುಂಬದವರೊಡನೆ ಸಮಾಧಾನವಾಗಲೇ ಇಲ್ಲ."
		},
		"11": {
			"1": "<sup>1</sup> ರೆಹಬ್ಬಾಮನು ಯೆರೂಸಲೇಮನ್ನು ಮುಟ್ಟಿದ ನಂತರ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ವಿರೋಧವಾಗಿ ಯುದ್ಧ ಮಾಡುವದಕ್ಕೂ ರಾಜ್ಯವನ್ನು ತಿರಿಗಿ ತನ್ನ ವಶಪಡಿಸಿಕೊಳ್ಳುವದಕ್ಕೂ ಯೆಹೂದ ಬೆನ್ಯಾಮೀನ್ ಕುಲಗಳಿಂದ ಲಕ್ಷದ ಎಂಭತ್ತು ಸಾವಿರಮಂದಿ ಶ್ರೇಷ್ಠ ಸೈನಿಕರನ್ನು ಕೂಡಿಸಿದನು.",
			"2": "<sup>2</sup> ಆಗ ದೇವರ ಮನುಷ್ಯನಾದ ಶೆಮಾಯನಿಗೆ -",
			"3": "<sup>3</sup> ನೀನು ಹೋಗಿ ಸೊಲೊಮೋನನ ಮಗನೂ ಯೆಹೂದದ ಅರಸನೂ ಆದ ರೆಹಬ್ಬಾಮನಿಗೂ ಯೆಹೂದ ಬೆನ್ಯಾಮೀನ್ ಕುಲಗಳ ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರಿಗೂ -",
			"4": "<sup>4</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ, ನೀವು ನಿಮ್ಮ ಸಹೋದರರೊಡನೆ ಯುದ್ಧಮಾಡುವದಕ್ಕೆ ಹೋಗಬಾರದು. ಎಲ್ಲರೂ ಹಿಂದಿರುಗಿ ಹೋಗಿರಿ; ಈ ಕಾರ್ಯವು ಯೆಹೋವನಿಂದಾಗಿದೆ ಎಂದು ಹೇಳಬೇಕು ಎಂಬ ಯೆಹೋವನ ವಾಕ್ಯವುಂಟಾಗಲು ಅವರು ಯೆಹೋವನ ಮಾತನ್ನು ಕೇಳಿ ಯಾರೊಬ್ಬಾಮನಿಗೆ ವಿರೋಧವಾಗಿ ಹೊರಡದೆ ಹಿಂದಿರುಗಿ ಹೋದರು.",
			"5": "<sup>5</sup> ರೆಹಬ್ಬಾಮನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ವಾಸವಾಗಿದ್ದು ಯೆಹೂದದ ಪಟ್ಟಣಗಳನ್ನು ಭದ್ರಪಡಿಸಿದನು.",
			"6": "<sup>6</sup> ಅವು ಯಾವವಂದರೆ - ಬೇತ್ಲೆಹೆಮ್, ಏತಾಮ್, ತೆಕೋವ,",
			"7": "<sup>7</sup> ಬೇತ್ಚೂರ್, ಸೋಕೋ, ಅದುಲ್ಲಾಮ್, ಗತ್ ಊರು,",
			"9": "<sup>9</sup> ಮಾರೇಷ, ಜೀಫ್, ಅದೋರೈಮ್, ಲಾಕೀಷ್,",
			"10": "<sup>10</sup> ಅಜೇಕ, ಚೊರ್ಗ, ಅಯ್ಯಾಲೋನ್, ಹೆಬ್ರೋನ್ ಇವೇ. ಕೋಟೆಕೊತ್ತಲುಗಳುಳ್ಳ ಈ ಪಟ್ಟಣಗಳು ಯೆಹೂದ ಬೆನ್ಯಾಮೀನ್ ಪ್ರಾಂತದವುಗಳು.",
			"11": "<sup>11</sup> ಅವನು ಈ ಕೋಟೆಗಳನ್ನು ಬಲಪಡಿಸಿ ನಾಯಕರ ವಶಕ್ಕೆ ಕೊಟ್ಟು ಪ್ರತಿಯೊಂದರಲ್ಲಿ ಆಹಾರಪದಾರ್ಥ ಎಣ್ಣೆ ದ್ರಾಕ್ಷಾರಸ ಇವುಗಳನ್ನೂ ಗುರಾಣಿಬರ್ಜಿಗಳನ್ನೂ ಸಂಗ್ರಹಿಸಿಟ್ಟನು;",
			"12": "<sup>12</sup> ಈ ಪ್ರಕಾರ ಇವುಗಳನ್ನು ಅತ್ಯಧಿಕವಾಗಿ ಭದ್ರಪಡಿಸಿದನು.",
			"13": "<sup>13</sup> ಯೆಹೂದ ಬೆನ್ಯಾಮೀನ್ ಕುಲಗಳವರು ಅವನ ಸ್ವಾಧೀನದಲ್ಲಿದ್ದದ್ದಲ್ಲದೆ ಬೇರೆ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ ಪ್ರಾಂತಗಳಲ್ಲಿದ್ದ ಯಾಜಕರೂ ಲೇವಿಯರೂ ಅವನೊಡನೆ ಕೂಡಿಕೊಂಡರು.",
			"14": "<sup>14</sup> ಲೇವಿಯರು ಯೆಹೋವನಿಗೆ ಯಾಜಕಸೇವೆಮಾಡದಂತೆ ಯಾರೊಬ್ಬಾಮನೂ ಅವನ ಮಕ್ಕಳೂ ಅವರನ್ನು ತಳ್ಳಿಬಿಟ್ಟು",
			"15": "<sup>15</sup> ತಾವು ಏರ್ಪಡಿಸಿದ ಪೂಜಾಸ್ಥಳ ಅಜದೇವತೆ ಬಸವಮೂರ್ತಿ ಇವುಗಳಿಗೋಸ್ಕರ ಬೇರೆ ಪೂಜಾರಿಗಳನ್ನು ನೇಮಿಸಿಕೊಂಡದ್ದರಿಂದ ಅವರು ಗೋಮಾಳಗಳುಳ್ಳ ತಮ್ಮ ಸ್ವಾಸ್ತ್ಯಗಳನ್ನು ಬಿಟ್ಟು ಯೆಹೂದ ದೇಶಕ್ಕೂ ಯೆರೂಸಲೇಮಿಗೂ ಬಂದರು.",
			"16": "<sup>16</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಎಲ್ಲಾ ಕುಲಗಳಲ್ಲಿ ಇಸ್ರಾಯೇಲ್‍ದೇವರಾದ ಯೆಹೋವನ ದರ್ಶನವನ್ನು ಬಯಸುವವರು ತಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಯಜ್ಞವರ್ಪಿಸುವದಕ್ಕಾಗಿ ಆ ಲೇವಿಯರನ್ನು ಹಿಂಬಾಲಿಸಿ ಯೆರೂಸಲೇಮಿಗೆ ಬಂದರು.",
			"17": "<sup>17</sup> ಇವರೆಲ್ಲರೂ ಮೊದಲನೆಯ ಮೂರು ವರುಷಗಳಲ್ಲಿ ದಾವೀದಸೊಲೊಮೋನರ ಮಾರ್ಗದಲ್ಲಿ ನಡೆಯುತ್ತಿದ್ದದರಿಂದ ಯೆಹೂದರಾಜ್ಯವು ಆ ವರುಷಗಳಲ್ಲಿ ವೃದ್ಧಿಯಾಗಿ ಸೊಲೊಮೋನನ ಮಗನಾದ ರೆಹಬ್ಬಾಮನು ಬಲಗೊಂಡನು.",
			"18": "<sup>18</sup> ರೆಹಬ್ಬಾಮನು ದಾವೀದನ ಮಗನಾದ ಯೆರೀಮೋತನಿಗೆ ಇಷಯನ ಮೊಮ್ಮಗಳೂ ಎಲೀಯಾಬನ ಮಗಳೂ ಆದ ಅಬೀಹೈಲಳಲ್ಲಿ ಹುಟ್ಟಿದ ಮಹ್ಲತ್ ಎಂಬಾಕೆಯನ್ನು ಹೆಂಡತಿಯನ್ನಾಗಿ ತೆಗೆದುಕೊಂಡನು.",
			"19": "<sup>19</sup> ಆಕೆಯು ಅವನಿಂದ ಯೆಗೂಷ್, ಶೆಮರ್ಯ, ಜಾಹಮ್ ಎಂಬ ಗಂಡು ಮಕ್ಕಳನ್ನು ಹೆತ್ತಳು.",
			"20": "<sup>20</sup> ಅನಂತರ ಅವನು ಅಬ್ಷಾಲೋಮನ ಮಗಳಾದ ಮಾಕ ಎಂಬಾಕೆಯನ್ನು ಮದುವೆಮಾಡಿಕೊಂಡನು; ಆಕೆಯು ಅವನಿಂದ ಅಬೀಯ, ಅತ್ತೈ, ಜೀಜ, ಶೆಲೋಮೀತ್ ಎಂಬವರನ್ನು ಹೆತ್ತಳು.",
			"21": "<sup>21</sup> ರೆಹಬ್ಬಾಮನು ತನ್ನ ಎಲ್ಲಾ ಪತ್ನಿಯರಲ್ಲಿಯೂ ಉಪಪತ್ನಿಯರಲ್ಲಿಯೂ ಅಬ್ಷಾಲೋಮನ ಮಗಳಾದ ಮಾಕಳನ್ನು ಹೆಚ್ಚಾಗಿ ಪ್ರೀತಿಸುತ್ತಿದ್ದನು. ಅವನಿಗೆ ಹದಿನೆಂಟು ಮಂದಿ ಪತ್ನಿಯರೂ ಅರುವತ್ತು ಮಂದಿ ಉಪಪತ್ನಿಯರೂ ಇದ್ದರು. ಇವರಲ್ಲಿ ಅವನಿಗೆ ಇಪ್ಪತ್ತೆಂಟು ಮಂದಿ ಗಂಡುಮಕ್ಕಳೂ ಅರುವತ್ತು ಮಂದಿ ಹೆಣ್ಣುಮಕ್ಕಳೂ ಹುಟ್ಟಿದರು.",
			"22": "<sup>22</sup> ರೆಹಬ್ಬಾಮನು ಮಾಕಳ ಮಗನಾದ ಅಬೀಯನನ್ನು ಅರಸನನ್ನಾಗಿ ಮಾಡಬೇಕೆಂದು ಅವನನ್ನು ಅವನ ಅಣ್ಣತಮ್ಮಂದಿರಲ್ಲಿ ಪ್ರಧಾನನನ್ನಾಗಿಯೂ ಪ್ರಭುವನ್ನಾಗಿಯೂ ನೇಮಿಸಿದನು.",
			"23": "<sup>23</sup> ತನ್ನ ಉಳಿದ ಎಲ್ಲಾ ಮಕ್ಕಳನ್ನು ಜಾಣತನದಿಂದ ವಿಂಗಡಿಸಿ ಯೆಹೂದ ಬೆನ್ಯಾಮೀನ್ ಪ್ರದೇಶಗಳಲ್ಲಿದ್ದ ಕೋಟೆಕೊತ್ತಲುಗಳುಳ್ಳ ಆಯಾ ಪಟ್ಟಣಗಳಲ್ಲಿಟ್ಟು ಅವರಿಗೆ ಬೇಕಾದಷ್ಟು ಆಹಾರವನ್ನು ಏರ್ಪಡಿಸಿ ಅನೇಕ ಮಂದಿ ಹೆಂಡತಿಯರನ್ನೂ ಗೊತ್ತು ಮಾಡಿಕೊಟ್ಟನು."
		},
		"12": {
			"1": "<sup>1</sup> ರೆಹಬ್ಬಾಮನು ತನ್ನ ರಾಜ್ಯಾಧಿಕಾರವನ್ನು ಸ್ಥಿರಪಡಿಸಿಕೊಂಡು ಬಲಗೊಂಡ ಮೇಲೆ ಅವನೂ ಅವನ ಪ್ರಜೆಗಳಾದ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರೂ ಯೆಹೋವನ ಧರ್ಮೋಪದೇಶವನ್ನು ಬಿಟ್ಟುಬಿಟ್ಟರು;",
			"2": "<sup>2</sup> ಅವರು ಯೆಹೋವನಿಗೆ ದ್ರೋಹಿಗಳಾದದರಿಂದ ಅರಸನಾದ ರೆಹಬ್ಬಾಮನ ಆಳಿಕೆಯ ಐದನೆಯ ವರುಷದಲ್ಲಿ",
			"3": "<sup>3</sup> ಐಗುಪ್ತದ ಅರಸನಾದ ಶೀಶಕನು ಸಾವಿರದ ಇನ್ನೂರು ರಥಗಳನ್ನೂ ಅರುವತ್ತು ಸಾವಿರ ಮಂದಿ ರಾಹುತರನ್ನೂ ಐಗುಪ್ತದ ಅಸಂಖ್ಯರಾದ ಲೂಬ್ಯ ಸುಕ್ಕೀಯ ಕೂಷ್ಯ ಸೈನಿಕರನ್ನೂ ಕೂಡಿಸಿಕೊಂಡು ಯೆರೂಸಲೇಮಿಗೆ ವಿರೋಧವಾಗಿ ಹೊರಟು",
			"4": "<sup>4</sup> ಯೆಹೂದದೇಶದ ಕೋಟೆ ಕೊತ್ತಲುಗಳುಳ್ಳ ಪಟ್ಟಣಗಳನ್ನು ಸ್ವಾಧೀನ ಮಾಡಿಕೊಂಡನಂತರ ಯೆರೂಸಲೇಮನ್ನು ಸಮೀಪಿಸಿದನು.",
			"5": "<sup>5</sup> ಆಗ ಪ್ರವಾದಿಯಾದ ಶೆಮಾಯನು ರೆಹಬ್ಬಾಮನ ಬಳಿಗೂ ಶೀಶಕನಿಗೆ ಹೆದರಿ ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಕೂಡಿದ್ದ ಯೆಹೂದ್ಯಪ್ರಧಾನರ ಬಳಿಗೂ ಬಂದು ಅವರಿಗೆ - ನೀವು ನನ್ನನ್ನು ಬಿಟ್ಟದ್ದರಿಂದ ನಾನು ನಿಮ್ಮನ್ನು ಶೀಶಕನ ಕೈಯಲ್ಲಿ ಬಿಟ್ಟು ಬಿಟ್ಟಿದ್ದೇನೆಂಬದಾಗಿ ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ ಎಂದು ಹೇಳಿದನು.",
			"6": "<sup>6</sup> ಆಗ ಅರಸನೂ ಇಸ್ರಾಯೇಲ್ ಪ್ರಧಾನರೂ ಯೆಹೋವನು ನ್ಯಾಯವಂತನೆಂದು ಒಪ್ಪಿ ತಮ್ಮನ್ನು ತಗ್ಗಿಸಿಕೊಂಡರು.",
			"7": "<sup>7</sup> ಯೆಹೋವನು ಇದನ್ನು ನೋಡಿ ಶೆಮಾಯನಿಗೆ - ಇವರು ತಮ್ಮನ್ನು ತಗ್ಗಿಸಿಕೊಂಡದ್ದರಿಂದ ಇವರನ್ನು ಸಂಹರಿಸುವದಿಲ್ಲ; ಸ್ವಲ್ಪ ಕಾಲದಲ್ಲಿಯೇ ಇವರಿಗೆ ರಕ್ಷಣೆಯನ್ನು ಅನುಗ್ರಹಿಸುವೆನು; ಶೀಶಕನ ಮುಖಾಂತರವಾಗಿ ನನ್ನ ರೌದ್ರವನ್ನು ಯೆರೂಸಲೇಮಿನ ಮೇಲೆ ಸುರಿದುಬಿಡುವದಿಲ್ಲ.",
			"8": "<sup>8</sup> ಆದರೂ ನನ್ನ ಸೇವೆಗೂ ಅನ್ಯರಾಜ್ಯಗಳ ಸೇವೆಗೂ ಇರುವ ವ್ಯತ್ಯಾಸವು ಇವರಿಗೆ ಗೊತ್ತಾಗುವಂತೆ ಇವರು ಶೀಶಕನಿಗೆ ದಾಸರಾಗಬೇಕು ಎಂದು ಹೇಳಿದನು.",
			"9": "<sup>9</sup> ಹೀಗೆ ಯೆರೂಸಲೇಮಿಗೆ ವಿರೋಧವಾಗಿ ಬಂದ ಐಗುಪ್ತ್ಯ ರಾಜನಾದ ಶೀಶಕನು ಯೆಹೋವನ ಆಲಯದ ಮತ್ತು ಅರಮನೆಯ ಎಲ್ಲಾ ದ್ರವ್ಯವನ್ನೂ ಸೊಲೊಮೋನನು ಮಾಡಿಸಿದ ಬಂಗಾರದ ಗುರಾಣಿಗಳನ್ನೂ ತೆಗೆದುಕೊಂಡು ಹೋದನು.",
			"10": "<sup>10</sup> ಅರಸನಾದ ರೆಹಬ್ಬಾಮನು ಅವುಗಳಿಗೆ ಬದಲಾಗಿ ತಾಮ್ರದ ಗುರಾಣಿಗಳನ್ನು ಮಾಡಿಸಿ ಅವುಗಳನ್ನು ಅರಮನೆಯ ದ್ವಾರಪಾಲಕರಾಗಿದ್ದ ಮೈಗಾವಲಿನವರ ದಳವಾಯಿಗಳಿಗೆ ಒಪ್ಪಿಸಿದನು.",
			"11": "<sup>11</sup> ಅರಸನು ಯೆಹೋವನ ಆಲಯಕ್ಕೆ ಹೋಗುವಾಗೆಲ್ಲಾ ಮೈಗಾವಲಿನವರು ಅವುಗಳನ್ನು ಹಿಡಿದುಕೊಂಡು ಹೋಗುವರು; ಅಲ್ಲಿಂದ ತಿರಿಗಿ ಬಂದ ಮೇಲೆ ತಮ್ಮ ಕೋಣೆಯಲ್ಲಿಡುವರು.",
			"12": "<sup>12</sup> ರೆಹಬ್ಬಾಮನು ತನ್ನನ್ನು ತಗ್ಗಿಸಿಕೊಂಡದರಿಂದಲೂ ಯೆಹೂದ್ಯರಲ್ಲಿ ಕೆಲವು ಸುಲಕ್ಷಣಗಳು ತೋರುತ್ತಿದ್ದದರಿಂದಲೂ ಯೆಹೋವನು ರೆಹಬ್ಬಾಮನ ಮೇಲಣ ಕೋಪವನ್ನು ಬಿಟ್ಟನು; ಅವನನ್ನು ಪೂರ್ಣವಾಗಿ ಹಾಳುಮಾಡಲಿಲ್ಲ.",
			"13": "<sup>13</sup> ಅರಸನಾದ ರೆಹಬ್ಬಾಮನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ತನ್ನ ರಾಜ್ಯಾಧಿಕಾರವನ್ನು ಸ್ಥಿರಪಡಿಸಿಕೊಂಡು ಆಳುವವನಾದನು. ಅವನು ತನ್ನ ನಾಲ್ವತ್ತೊಂದನೆಯ ವರುಷ ತುಂಬಿದ ಮೇಲೆ ಪಟ್ಟಕ್ಕೆ ಬಂದು ಯೆಹೋವನು ತನ್ನ ಹೆಸರಿಗೋಸ್ಕರ ಇಸ್ರಾಯೇಲ್ಯರ ಎಲ್ಲಾ ಕುಲಗಳಿಂದ ಆರಿಸಿಕೊಂಡ ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಹದಿನೇಳು ವರುಷ ಆಳಿದನು. ಅಮ್ಮೋನಿಯಳಾದ ನಯಮಾ ಎಂಬಾಕೆಯು ಅವನ ತಾಯಿ.",
			"14": "<sup>14</sup> ಅವನು ಯೆಹೋವನನ್ನು ಅನುಸರಿಸುವದಕ್ಕೆ ಮನಸ್ಸುಮಾಡದೆ ದ್ರೋಹಿಯಾದನು.",
			"15": "<sup>15</sup> ರೆಹಬ್ಬಾಮನ ಪೂರ್ವೋತ್ತರಕೃತ್ಯಗಳು ಪ್ರವಾದಿಯಾದ ಶೆಮಾಯ, ದರ್ಶಿಯಾದ ಇದ್ದೋ ಎಂಬವರ ಚರಿತ್ರೆಗಳ ವಂಶಾವಳಿಭಾಗದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ. ರೆಹಬ್ಬಾಮನಿಗೂ ಯಾರೊಬ್ಬಾಮನಿಗೂ ಯಾವಾಗಲೂ ಯುದ್ಧನಡೆಯುತ್ತಿತ್ತು.",
			"16": "<sup>16</sup> ರೆಹಬ್ಬಾಮನು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಲು ಅವನ ಶವಕ್ಕೆ ದಾವೀದನಗರದಲ್ಲಿ ಸಮಾಧಿಯಾಯಿತು. ಅವನ ಮಗನಾದ ಅಬೀಯನು ಅವನಿಗೆ ಬದಲಾಗಿ ಅರಸನಾದನು."
		},
		"13": {
			"1": "<sup>1</sup> ಅರಸನಾದ ಯಾರೊಬ್ಬಾಮನ ಆಳಿಕೆಯ ಹದಿನೆಂಟನೆಯ ವರುಷದಲ್ಲಿ ಅಬೀಯನು ಯೆಹೂದದ ಅರಸನಾಗಿ",
			"2": "<sup>2</sup> ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಮೂರು ವರುಷ ಆಳಿದನು; ಗಿಬೆಯದ ಊರೀಯೇಲನ ಮಗಳಾದ ಮೀಕಾಯ ಎಂಬಾಕೆಯು ಇವನ ತಾಯಿ. ಇವನಿಗೂ ಯಾರೊಬ್ಬಾಮನಿಗೂ ಯುದ್ಧನಡೆಯಿತು.",
			"3": "<sup>3</sup> ಅಬೀಯನು ನಾಲ್ಕು ಲಕ್ಷ ಮಂದಿ ರಣವೀರರನ್ನು ಆರಿಸಿಕೊಂಡು ಬಂದು ಯುದ್ಧ ಪ್ರಾರಂಭಿಸಿದನು; ಯಾರೊಬ್ಬಾಮನೂ ಎಂಟು ಲಕ್ಷ ಶ್ರೇಷ್ಠರಾದ ಭಟರೊಡನೆ ಅವನಿಗೆ ವಿರೋಧವಾಗಿ ಬಂದು ವ್ಯೂಹಕಟ್ಟಿದನು.",
			"4": "<sup>4</sup> ಆಗ ಅಬೀಯನು ಎಫ್ರಾಯೀಮ್ ಪರ್ವತಪ್ರದೇಶದಲ್ಲಿರುವ ಚೆಮಾರೈಮೆಂಬ ಬೆಟ್ಟದಲ್ಲಿ ನಿಂತು - ಯಾರೊಬ್ಬಾಮನೇ, ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರೇ, ನನ್ನ ಮಾತನ್ನು ಕೇಳಿರಿ.",
			"5": "<sup>5</sup> ಯೆಹೋವನು ಉಪ್ಪಿನ ಒಡಂಬಡಿಕೆಯಿಂದ ದಾವೀದನಿಗೂ ಅವನ ಸಂತಾನದವರಿಗೂ ಇಸ್ರಾಯೇಲ್ಯರ ಮೇಲೆ ಶಾಶ್ವತವಾದ ಅರಸುತನವನ್ನು ಕೊಟ್ಟಿದ್ದಾನೆಂದು ನಿಮಗೆ ಗೊತ್ತಿಲ್ಲವೋ?",
			"6": "<sup>6</sup> ಹೀಗಿದ್ದರೂ ದಾವೀದನ ಮಗನಾದ ಸೊಲೊಮೋನನ ಸೇವೆಯಲ್ಲಿದ್ದ ನೆಬಾಟನ ಮಗನಾದ ಯಾರೊಬ್ಬಾಮನು ಎದ್ದು ತನ್ನ ದಣಿಗೆ ವಿರೋಧವಾಗಿ ತಿರುಗಿ ಬೀಳುವಲ್ಲಿ",
			"7": "<sup>7</sup> ಕಾಕಪೋಕರೂ ದುಷ್ಟರೂ ಆಗಿರುವ ಜನರು ಅವನನ್ನು ಕೂಡಿಕೊಂಡು ಆ ಸಮಯದಲ್ಲಿ ಎಳೇ ಪ್ರಾಯದವನೂ ಮೃದುಸ್ವಭಾವವುಳ್ಳವನೂ ತಮ್ಮೆದುರಿನಲ್ಲಿ ನಿಲ್ಲಲಾರದವನೂ ಆದ ಸೊಲೊಮೋನನ ಮಗ ರೆಹಬ್ಬಾಮನಿಗೆ ಮಾರ್ಮಲೆತು ಬಲಗೊಂಡರು.",
			"8": "<sup>8</sup> ಈಗ ನೀವು ದೊಡ್ಡ ಗುಂಪಾಗಿರುವದರಿಂದಲೂ ಯಾರೊಬ್ಬಾಮನು ನಿಮಗೆ ದೇವರುಗಳೆಂದು ಮಾಡಿಕೊಟ್ಟ ಬಂಗಾರದ ಬಸವ ಮೂರ್ತಿಗಳು ನಿಮ್ಮಲ್ಲಿರುವದರಿಂದಲೂ ದಾವೀದನ ಸಂತಾನದವರ ಕೈಯಲ್ಲಿರುವ ಯೆಹೋವನ ರಾಜ್ಯಕ್ಕೆ ವಿರೋಧವಾಗಿ ನಿಂತು ಗೆಲ್ಲಬಹುದೆಂದು ನೆನಸುತ್ತೀರೋ?",
			"9": "<sup>9</sup> ನೀವು ಯೆಹೋವನ ಯಾಜಕರಾದ ಆರೋನನ ಸಂತಾನದವರನ್ನೂ ಲೇವಿಯರನ್ನೂ ಓಡಿಸಿಬಿಟ್ಟು ಅನ್ಯದೇಶಗಳವರಂತೆ ನಿಮಗೋಸ್ಕರ ಪೂಜಾರಿಗಳನ್ನು ಮಾಡಿಕೊಂಡಿರಲ್ಲಾ; ತಾನು ಪೂಜಾರಿಯಾಗಿ ಪ್ರತಿಷ್ಠಿತನಾಗಬೇಕೆಂದು ಒಂದು ಎಳೇ ಹೋರಿಯನ್ನೂ ಏಳು ಟಗರುಗಳನ್ನೂ ತೆಗೆದುಕೊಂಡು ಬಂದ ಪ್ರತಿಯೊಬ್ಬನೂ ದೇವರಲ್ಲದವುಗಳಿಗೆ ಪೂಜಾರಿಯಾದನಷ್ಟೆ.",
			"10": "<sup>10</sup> ನಮಗಾದರೋ ಯೆಹೋವನು ದೇವರಾಗಿರುತ್ತಾನೆ; ನಾವು ಆತನನ್ನು ಬಿಡಲಿಲ್ಲ. ಯೆಹೋವನ ಆರಾಧನೆ ನಡಿಸುವ ಆರೋನನ ಸಂತಾನದವರಾದ ಯಾಜಕರೂ ತಮಗೆ ನೇಮಕವಾದ ಕೆಲಸದಲ್ಲಿ ನಿರತರಾದ ಲೇವಿಯರೂ ನಮಗಿರುತ್ತಾರೆ.",
			"11": "<sup>11</sup> ಆ ಯಾಜಕರು ಪ್ರತಿದಿನ ಪ್ರಾತಃಸ್ಸಾಯಂಕಾಲಗಳಲ್ಲಿ ಯೆಹೋವನಿಗೋಸ್ಕರ ಸರ್ವಾಂಗಹೋಮಗಳನ್ನು ಸಮರ್ಪಿಸುತ್ತಾ ಸುಗಂಧ ದ್ರವ್ಯಧೂಪವನ್ನು ಹಾಕುತ್ತಾ ಚೊಕ್ಕ [ಬಂಗಾರದ] ಮೇಜಿನ ಮೇಲೆ ನೈವೇದ್ಯವಾದ ರೊಟ್ಟಿಗಳನ್ನಿಡುತ್ತಾ ಪ್ರತಿ ಸಾಯಂಕಾಲದಲ್ಲಿ ಬಂಗಾರದ ದೀಪಸ್ತಂಭದ ದೀಪಗಳನ್ನು ಹಚ್ಚುತ್ತಾ ಇರುತ್ತಾರೆ. ಹೀಗೆ ನಾವು ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಕಟ್ಟಳೆಗಳನ್ನು ಕೈಕೊಳ್ಳುತ್ತೇವೆ; ನೀವಾದರೋ ಆತನನ್ನು ಬಿಟ್ಟವರು.",
			"12": "<sup>12</sup> ಇಗೋ, ದೇವರು ನಾಯಕನಾಗಿ ನಮ್ಮೊಂದಿಗಿರುತ್ತಾನೆ; ನಿಮಗೆ ವಿರೋಧವಾಗಿ ಆರ್ಭಟದಿಂದ ಊದತಕ್ಕ ತುತೂರಿಗಳನ್ನು ಹಿಡಿದಿರುವ ಯಾಜಕರೂ ನಮ್ಮೊಂದಿಗಿರುತ್ತಾರೆ. ಇಸ್ರಾಯೇಲ್ಯರೇ, ನಿಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಯೆಹೋವನೊಡನೆ ಯುದ್ಧ ಮಾಡಬೇಡಿರಿ, ನೀವು ಸಫಲರಾಗಲಿಕ್ಕಿಲ್ಲ ಎಂದು ಕೂಗಿ ಹೇಳಿದನು.",
			"13": "<sup>13</sup> ಯಾರೊಬ್ಬಾಮನು ಯೆಹೂದ್ಯರ ಹಿಂದೆ ಹೊಂಚುಹಾಕುವದಕ್ಕೋಸ್ಕರ ತನ್ನ ಸೈನ್ಯದ ಒಂದು ಗುಂಪನ್ನು ಕಳುಹಿಸಿದನು. ಹೀಗೆ ಸೈನ್ಯವು ಯೆಹೂದ್ಯರ ಎದುರಿನಲ್ಲಿಯೂ ಹೊಂಚುಹಾಕುವವರು ಅವರ ಹಿಂದೆಯೂ ಇದ್ದರು.",
			"14": "<sup>14</sup> ಯೆಹೂದ್ಯರು ತಿರುಗಿಕೊಂಡು ತಮ್ಮ ಮುಂದೆಯೂ ಹಿಂದೆಯೂ ಯುದ್ಧಪ್ರಾರಂಭವಾಗಿರುವದನ್ನು ಕಂಡು ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಟ್ಟರು, ಯಾಜಕರು ತುತೂರಿಗಳನ್ನು ಊದಿದರು.",
			"15": "<sup>15</sup> ಅನಂತರ ಜನರು ಆರ್ಭಟಿಸಿದರು. ಅವರು ಆರ್ಭಟಿಸಿದ ಕೂಡಲೆ ದೇವರು ಯಾರೊಬ್ಬಾಮನನ್ನೂ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರನ್ನೂ ಅಬೀಯನಿಂದಲೂ ಯೆಹೂದ್ಯರಿಂದಲೂ ಅಪಜಯಪಡಿಸಿದ್ದರಿಂದ",
			"16": "<sup>16</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಯೆಹೂದ್ಯರ ಎದುರಿನಿಂದ ಓಡಿಹೋದರು. ದೇವರು ಅವರನ್ನು ಇವರ ಕೈಗೆ ಒಪ್ಪಿಸಿದನು.",
			"17": "<sup>17</sup> ಅಬೀಯನೂ ಅವನ ಜನರೂ ಅವರಲ್ಲಿ ಅನೇಕಾನೇಕರನ್ನು ಸಂಹರಿಸಿಬಿಟ್ಟರು. ಇಸ್ರಾಯೇಲ್ ಭಟರಲ್ಲಿ ಹತರಾಗಿ ಬಿದ್ದವರು ಐದು ಲಕ್ಷ ಮಂದಿ.",
			"18": "<sup>18</sup> ಹೀಗೆ ಇಸ್ರಾಯೇಲ್ಯರು ಆ ಕಾಲದಲ್ಲಿ ತಗ್ಗಿಸಲ್ಪಟ್ಟರು; ಯೆಹೂದ್ಯರು ತಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಯೆಹೋವನಲ್ಲಿ ಭರವಸವಿಟ್ಟದರಿಂದ ಬಲಗೊಂಡರು.",
			"19": "<sup>19</sup> ಅಬೀಯನು ಯಾರೊಬ್ಬಾಮನನ್ನು ಹಿಂದಟ್ಟಿ ಅವನಿಂದ ಬೇತೇಲ್, ಯೆಷಾನಾ, ಎಫ್ರೋನ್ ಎಂಬ ಪಟ್ಟಣಗಳನ್ನೂ ಅವುಗಳಿಗೆ ಸೇರಿದ ಗ್ರಾಮಗಳನ್ನೂ ಕಿತ್ತುಕೊಂಡನು.",
			"20": "<sup>20</sup> ಯಾರೊಬ್ಬಾಮನು ಅಬೀಯನ ಜೀವಮಾನದಲ್ಲಿ ತಿರಿಗಿ ಬಲಗೊಳ್ಳಲೇ ಇಲ್ಲ. ಯೆಹೋವನು ಅವನನ್ನು ಹೊಡೆದದರಿಂದ ಅವನು ಸತ್ತನು.",
			"21": "<sup>21</sup> ಅಬೀಯನು ಬಲಗೊಂಡನು; ಅವನು ಹದಿನಾಲ್ಕು ಮಂದಿ ಹೆಂಡತಿಯರನ್ನು ಮಾಡಿಕೊಂಡು ಇಪ್ಪತ್ತೆರಡು ಮಂದಿ ಗಂಡುಮಕ್ಕಳನ್ನೂ ಹದಿನಾರು ಮಂದಿ ಹೆಣ್ಣುಮಕ್ಕಳನ್ನೂ ಪಡೆದನು.",
			"22": "<sup>22</sup> ಅಬೀಯನ ಉಳಿದ ಚರಿತ್ರೆಯೂ ಅವನ ನಡೆನುಡಿಗಳೂ ಪ್ರವಾದಿಯಾದ ಇದ್ದೋವಿನ ವ್ಯಾಖ್ಯಾನದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ."
		},
		"14": {
			"1": "<sup>1</sup> ಅಬೀಯನು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಲು ಅವನ ಶವವನ್ನು ದಾವೀದನಗರದಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ಆಸನು ಅರಸನಾದನು. ಇವನ ಕಾಲದಲ್ಲಿ ದೇಶದಲ್ಲಿ ಹತ್ತು ವರುಷ ಸಮಾಧಾನವಿತ್ತು.",
			"2": "<sup>2</sup> ಆಸನು ತನ್ನ ದೇವರಾದ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ಒಳ್ಳೆಯವನಾಗಿಯೂ ನೀತಿವಂತನಾಗಿಯೂ ನಡೆದನು.",
			"3": "<sup>3</sup> ಇವನು ಅನ್ಯದೇವತೆಗಳ ಯಜ್ಞವೇದಿಗಳನ್ನೂ ಪೂಜಾಸ್ಥಳಗಳನ್ನೂ ತೆಗೆದುಹಾಕಿ ಕಲ್ಲುಕಂಬಗಳನ್ನು ಒಡಿಸಿ ಅಶೇರವಿಗ್ರಹಸ್ತಂಭಗಳನ್ನು ಕಡಿಸಿಬಿಟ್ಟು ಯೆಹೂದ್ಯರಿಗೆ -",
			"4": "<sup>4</sup> ನೀವು ನಿಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಯೆಹೋವನನ್ನೇ ಆಶ್ರಯಿಸಿಕೊಂಡು ಧರ್ಮಶಾಸ್ತ್ರವಿಧಿಗಳನ್ನು ಕೈಕೊಳ್ಳಿರಿ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"5": "<sup>5</sup> ಯೆಹೂದದ ಎಲ್ಲಾ ಪಟ್ಟಣಗಳೊಳಗಿಂದ ಪೂಜಾಸ್ಥಳಗಳನ್ನೂ ಸೂರ್ಯಸ್ತಂಭಗಳನ್ನೂ ತೆಗೆದುಹಾಕಿದನು. ಇವನ ಆಳಿಕೆಯಲ್ಲಿ ರಾಜ್ಯದೊಳಗೆ ಸಮಾಧಾನವಿತ್ತು.",
			"6": "<sup>6</sup> ಯೆಹೋವನ ಅನುಗ್ರಹದಿಂದ ಶತ್ರುಭಯ ತಪ್ಪಿ ಆ ವರುಷಗಳಲ್ಲಿ ಯಾವ ಯುದ್ಧವೂ ಇಲ್ಲದೆ ದೇಶದಲ್ಲಿ ಸಮಾಧಾನವಿದ್ದದರಿಂದ ಇವನು ಯೆಹೂದದಲ್ಲಿ ಕೋಟೆಕೊತ್ತಲುಗಳುಳ್ಳ ಪಟ್ಟಣಗಳನ್ನು ಕಟ್ಟಿಸಿದನು.",
			"7": "<sup>7</sup> ಇವನು ಯೆಹೂದ್ಯರಿಗೆ - ನಾವು ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಆಶ್ರಯಿಸಿಕೊಂಡ ಕಾರಣ ಆತನ ಅನುಗ್ರಹದಿಂದ ಸುತ್ತಣ ವೈರಿಗಳ ಭಯ ತಪ್ಪಿ ದೇಶವು ಇನ್ನೂ ನಿರಾತಂಕವಾಗಿರುತ್ತದೆ. ಆದದರಿಂದ ನಾವು ಪಟ್ಟಣಗಳನ್ನು ಕಟ್ಟಿ ಅವುಗಳನ್ನು ಸುತ್ತಣಗೋಡೆ ಬುರುಜು ಬಾಗಲು ಅಗುಳಿ ಇವುಗಳಿಂದ ಭದ್ರಪಡಿಸೋಣ ಅನ್ನಲಾಗಿ ಅವರು ಅವುಗಳನ್ನು ಚೆನ್ನಾಗಿ ಕಟ್ಟಿತೀರಿಸಿದರು.",
			"8": "<sup>8</sup> ಆಸನಿಗೆ ಸೈನ್ಯವಿತ್ತು; ಅದರಲ್ಲಿ ಗುರಾಣಿಬರ್ಜಿಗಳನ್ನು ಹಿಡಿದಿರುವ ಮೂರು ಲಕ್ಷ ಮಂದಿ ಯೆಹೂದ್ಯರೂ ಖೇಡ್ಯ ಹಿಡಿದವರೂ ಬಿಲ್ಲುಗಾರರೂ ಆಗಿರುವ ಎರಡು ಲಕ್ಷದ ಎಂಭತ್ತು ಸಾವಿರ ಮಂದಿ ಬೆನ್ಯಾಮೀನ್ಯರೂ ಇದ್ದರು. ಇವರೆಲ್ಲರೂ ರಣವೀರರು.",
			"9": "<sup>9</sup> ಕೂಷ್ಯನಾದ ಜೆರಹನು ಹತ್ತು ಲಕ್ಷ ಸೈನ್ಯವನ್ನೂ ಮುನ್ನೂರು ರಥಗಳನ್ನೂ ತೆಗೆದುಕೊಂಡು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ವಿರುದ್ಧವಾಗಿ ಯುದ್ಧಕ್ಕೆ ಹೊರಟು ಮಾರೇಷಕ್ಕೆ ಬಂದನು.",
			"10": "<sup>10</sup> ಆಸನು ಅವನೆದುರಿಗೆ ಹೋದನು. ಮಾರೇಷದ ಬಳಿಯಲ್ಲಿರುವ ಚೆಫಾತಾ ಬೈಲಿನಲ್ಲಿ ಉಭಯರೂ ವ್ಯೂಹಕಟ್ಟಿದರು.",
			"11": "<sup>11</sup> ಆಸನು ತನ್ನ ದೇವರಾದ ಯೆಹೋವನಿಗೆ - ಯೆಹೋವನೇ, ಬಲಿಷ್ಠನು ಬಲಹೀನನ ಮೇಲೆ ಬೀಳುವಲ್ಲಿ ನಿನ್ನ ಹೊರತು ರಕ್ಷಕನಿಲ್ಲ. ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನೇ, ನಮ್ಮನ್ನು ರಕ್ಷಿಸು. ನಿನ್ನಲ್ಲಿ ಭರವಸವಿಟ್ಟು ನಿನ್ನ ಹೆಸರಿನಲ್ಲಿ ಈ ಮಹಾ ಸಮೂಹಕ್ಕೆ ವಿರೋಧವಾಗಿ ಯುದ್ಧಕ್ಕೆ ಬಂದೆವಲ್ಲಾ. ಯೆಹೋವನೇ, ನಮ್ಮ ದೇವರು ನೀನು. ನರರು ನಿನ್ನನ್ನು ಎದುರಿಸಿ ಗೆಲ್ಲಬಾರದು ಎಂದು ಮೊರೆಯಿಡಲು",
			"12": "<sup>12</sup> ಯೆಹೋವನು ಕೂಷ್ಯರನ್ನು ಆಸನಿಂದಲೂ ಯೆಹೂದ್ಯರಿಂದಲೂ ಅಪಜಯಪಡಿಸಿದನು.",
			"13": "<sup>13</sup> ಕೂಷ್ಯರು ಓಡಿಹೋದಾಗ ಆಸನೂ ಅವನ ಜನರೂ ಗೆರಾರಿನವರೆಗೂ ಅವರನ್ನು ಹಿಂದಟ್ಟಿ ಸಂಹರಿಸಿದರು; ಅವರಲ್ಲಿ ಯಾರೂ ಜೀವದಿಂದುಳಿಯಲಿಲ್ಲ. ಯೆಹೋವನ ಮತ್ತು ಆತನ ಸೈನ್ಯದ ಮುಂದೆ ಚೂರು ಚೂರಾದರು. ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಅಪರಿಮಿತವಾದ ಕೊಳ್ಳೆ ಸಿಕ್ಕಿತು.",
			"14": "<sup>14</sup> ಇದಲ್ಲದೆ ಅವರು ಯೆಹೋವನ ಭಯಕ್ಕೆ ಒಳಗಾದ ಗೆರಾರಿನ ಸುತ್ತಣ ಪಟ್ಟಣಗಳನ್ನು ವಶಮಾಡಿಕೊಂಡು ಅವುಗಳನ್ನೆಲ್ಲಾ ಸುಲಿಗೆಮಾಡಿದರು. ಅವುಗಳಲ್ಲಿ ದೊಡ್ಡ ಕೊಳ್ಳೆ ಸಿಕ್ಕಿತು.",
			"15": "<sup>15</sup> ದನಕಾಯುವವರ ಗುಡಾರಗಳ ಮೇಲೆ ಬಿದ್ದು ಎಷ್ಟೋ ಒಂಟೆ ಕುರಿಗಳನ್ನು ಯೆರೂಸಲೇಮಿಗೆ ಹೊಡೆದುಕೊಂಡು ಬಂದರು."
		},
		"15": {
			"1": "<sup>1</sup> ಆಗ ಓಬೇದನ ಮಗನಾದ ಅಜರ್ಯನ ಮೇಲೆ ದೇವರ ಆತ್ಮವು ಬಂದದರಿಂದ",
			"2": "<sup>2</sup> ಅವನು ಆಸನನ್ನು ಎದುರುಗೊಳ್ಳುವದಕ್ಕೆ ಹೋಗಿ ಅವನಿಗೆ - ಆಸನೇ, ಎಲ್ಲಾ ಯೆಹೂದ ಬೆನ್ಯಾಮೀನ್ ಕುಲಗಳವರೇ, ಕಿವಿಗೊಡಿರಿ. ನೀವು ಯೆಹೋವನನ್ನು ಹೊಂದಿಕೊಂಡಿರುವ ತನಕ ಆತನೂ ನಿಮ್ಮೊಂದಿಗಿರುವನು; ನೀವು ಆತನನ್ನು ಹುಡುಕಿದರೆ ನಿಮಗೆ ಸಿಕ್ಕುವನು; ಆತನನ್ನು ಬಿಟ್ಟರೆ ಆತನೂ ನಿಮ್ಮನ್ನು ಬಿಟ್ಟು ಬಿಡುವನು.",
			"3": "<sup>3</sup> ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಬಹುಕಾಲದವರೆಗೆ ನಿಜವಾದ ದೇವರೂ ಬೋಧಿಸುವ ಯಾಜಕರೂ ಧರ್ಮಶಾಸ್ತ್ರವೂ ಇರಲಿಲ್ಲ.",
			"4": "<sup>4</sup> ಅವರು ತಮ್ಮ ಇಕ್ಕಟ್ಟಿನಲ್ಲಿ ಇಸ್ರಾಯೇಲ್ ದೇವರಾದ ಯೆಹೋವನ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡರು. ಅವರು ಆತನನ್ನು ಹುಡುಕಿದಾಗ ಆತನು ಅವರಿಗೆ ಸಿಕ್ಕಿದನು.",
			"5": "<sup>5</sup> ಆ ಕಾಲದಲ್ಲಿ ಯಾರಿಗೂ ನಿರ್ಭಯವಾಗಿ ತಿರುಗಾಡುವದಕ್ಕೆ ಆಗುತ್ತಿರಲಿಲ್ಲ; ದೇಶ ನಿವಾಸಿಗಳೆಲ್ಲರೂ ಕಳವಳಗೊಂಡಿದ್ದರು.",
			"6": "<sup>6</sup> ಒಂದು ಜನಾಂಗವು ಇನ್ನೊಂದು ಜನಾಂಗವನ್ನೂ ಒಂದು ಪಟ್ಟಣದವರು ಇನ್ನೊಂದು ಪಟ್ಟಣದವರನ್ನೂ ಹಾಳು ಮಾಡುತ್ತಿದ್ದರು. ದೇವರು ಎಲ್ಲಾ ತರದ ಕಷ್ಟದಿಂದಲೂ ಅವರನ್ನು ತಳಮಳಗೊಳಿಸಿದನು. ನೀವಾದರೋ ಸ್ಥಿರಚಿತ್ತರಾಗಿರಿ;",
			"7": "<sup>7</sup> ನಿಮ್ಮ ಕೈಗಳು ಜೋಲು ಬೀಳದಿರಲಿ. ನಿಮ್ಮ ಪ್ರಯತ್ನಕ್ಕೆ ಫಲತಪ್ಪದು ಎಂದು ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ಆಸನು ಓಬೇದನ [ಮಗನಾದ ಅಜರ್ಯನ] ಈ ಪ್ರವಾದನೋಕ್ತಿಗಳನ್ನು ಕೇಳಿದಾಗ ಧೈರ್ಯಗೊಂಡು ಯೆಹೂದ ಬೆನ್ಯಾಮೀನ್ ಪ್ರಾಂತಗಳಲ್ಲಿಯೂ ತಾನು ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡ ಎಫ್ರಾಯೀಮ್ ಪರ್ವತ ಪ್ರದೇಶದ ಪಟ್ಟಣಗಳಲ್ಲಿಯೂ ಇದ್ದ ಅಸಹ್ಯ ಪ್ರತಿಮೆಗಳನ್ನು ತೆಗೆದುಹಾಕಿ ಯೆಹೋವನ ಆಲಯಕ್ಕೆ ಸೇರಿದ ಮಂಟಪದ ಮುಂದಿದ್ದ ಆತನ ಯಜ್ಞವೇದಿಯನ್ನು ಜೀರ್ಣೋದ್ಧಾರಮಾಡಿಸಿದನು.",
			"9": "<sup>9</sup> ಆಮೇಲೆ ಆಸನು ಎಲ್ಲಾ ಯೆಹೂದ ಬೆನ್ಯಾಮೀನ್ ಕುಲಗಳವರನ್ನೂ ತನ್ನ ದೇವರಾದ ಯೆಹೋವನು ತನ್ನ ಸಂಗಡ ಇರುವದನ್ನು ನೋಡಿ ತನ್ನೊಡನೆ ಗುಂಪುಗುಂಪಾಗಿ ಕೂಡಿಕೊಂಡು ಯೆಹೂದದೇಶದಲ್ಲಿ ಇಳಕೊಂಡಿದ್ದ ಎಫ್ರಾಯೀಮ್ ಮನಸ್ಸೆ ಸಿಮೆಯೋನ್ ಕುಲಗಳವರನ್ನೂ ಕರಿಸಿಕೊಂಡನು.",
			"10": "<sup>10</sup> ಅವರು ಆಸನ ಆಳಿಕೆಯ ಹದಿನೈದನೆಯ ವರುಷದ ಮೂರನೆಯ ತಿಂಗಳಲ್ಲಿ ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಕೂಡಿಬಂದು",
			"11": "<sup>11</sup> ಯೆಹೋವನಿಗೋಸ್ಕರ ಆ ದಿವಸ ತಾವು ತಂದ ಕೊಳ್ಳೆಯಿಂದ ಏಳು ನೂರು ಹೋರಿಗಳನ್ನೂ ಏಳು ಸಾವಿರ ಕುರಿಗಳನ್ನೂ ಯಜ್ಞ ಮಾಡಿದರು.",
			"12": "<sup>12</sup> ಇದಲ್ಲದೆ ಅವರು ತಾವು ಪೂರ್ಣಹೃದಯದಿಂದಲೂ ಪೂರ್ಣಪ್ರಾಣದಿಂದಲೂ ತಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಯೆಹೋವನ ಭಕ್ತರಾಗಿರುವೆವೆಂದೂ",
			"13": "<sup>13</sup> ತಮ್ಮಲ್ಲಿ ಇಸ್ರಾಯೇಲ್‍ದೇವರಾದ ಯೆಹೋವನ ಭಕ್ತಿಯನ್ನು ಬಿಟ್ಟವರು ಚಿಕ್ಕವರಾಗಲಿ ದೊಡ್ಡವರಾಗಲಿ, ಗಂಡಸರಾಗಲಿ ಹೆಂಗಸರಾಗಲಿ, ಅವರೆಲ್ಲರನ್ನೂ ಕೊಲ್ಲುವೆವೆಂದೂ",
			"14": "<sup>14</sup> ತುತೂರಿಕೊಂಬುಗಳನ್ನೂದಿಸಿ ದೊಡ್ಡ ಆರ್ಭಟದೊಡನೆ ಯೆಹೋವನಿಗೆ ಪ್ರಮಾಣಮಾಡಿದರು.",
			"15": "<sup>15</sup> ಈ ಪ್ರಮಾಣದ ನಿಮಿತ್ತವಾಗಿ ಯೆಹೂದ್ಯರೆಲ್ಲರಿಗೂ ಸಂತೋಷವಾಯಿತು. ಅವರು ಪೂರ್ಣಮನಸ್ಸಿನಿಂದ ಪ್ರಮಾಣಮಾಡಿ ತುಂಬಾ ಲವಲವಿಕೆಯಿಂದ ಯೆಹೋವನನ್ನು ಬಯಸಿದ ಕಾರಣ ಆತನು ಅವರಿಗೆ ಪ್ರಸನ್ನನಾಗಿ ಎಲ್ಲಾಕಡೆಗಳಲ್ಲಿಯೂ ಸಮಾಧಾನವನ್ನನುಗ್ರಹಿಸಿದನು.",
			"16": "<sup>16</sup> ಅರಸನಾದ ಆಸನ ತಾಯಿ ಮಾಕಳು ಅಶೇರದೇವತೆಯ ಒಂದು ಅಸಹ್ಯ ಮೂರ್ತಿಯನ್ನು ಮಾಡಿಸಿದದರಿಂದ ಅವನು ಆಕೆಯನ್ನು ಗದ್ದುಗೆಯಿಂದ ತಳ್ಳಿ ಆ ಮೂರ್ತಿಯನ್ನು ಕಡಿಸಿ ಕಿದ್ರೋನ್ ಹಳ್ಳದ ಬಳಿಯಲ್ಲಿ ಚೂರುಚೂರು ಮಾಡಿ ಸುಡಿಸಿದನು.",
			"17": "<sup>17</sup> ಇಸ್ರಾಯೇಲ್‍ಪ್ರಾಂತದಲ್ಲಿದ್ದ ಪೂಜಾಸ್ಥಳಗಳು ತೆಗೆದುಹಾಕಲ್ಪಡದಿದ್ದರೂ ಆಸನು ತನ್ನ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ಯಥಾರ್ಥಚಿತ್ತನಾಗಿ ನಡೆದುಕೊಂಡನು.",
			"18": "<sup>18</sup> ತನ್ನ ತಂದೆಯೂ ತಾನೂ ಪ್ರತಿಷ್ಠಿಸಿದ ಬೆಳ್ಳಿಬಂಗಾರವನ್ನೂ ಪಾತ್ರೆಗಳನ್ನೂ ದೇವಾಲಯದಲ್ಲಿ ತಂದಿಟ್ಟನು.",
			"19": "<sup>19</sup> ಅವನ ಆಳಿಕೆಯ ಮೂವತ್ತೈದನೆಯ ವರುಷದವರೆಗೂ ಯುದ್ಧವಿರಲಿಲ್ಲ."
		},
		"16": {
			"1": "<sup>1</sup> ಆಸನ ಆಳಿಕೆಯ ಮೂವತ್ತಾರನೆಯ ವರುಷದಲ್ಲಿ ಇಸ್ರಾಯೇಲ್‍ರಾಜನಾದ ಬಾಷನು ಯೆಹೂದ್ಯರಿಗೆ ವಿರೋಧವಾಗಿ ಬಂದು ಯಾರೂ ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಆಸನ ಬಳಿಗೆ ಹೋಗಿ ಬರುವದಕ್ಕಾಗದಂತೆ ರಾಮಕೋಟೆಯನ್ನು ಕಟ್ಟಿಸಿದನು.",
			"2": "<sup>2</sup> ಆಗ ಆಸನು ಯೆಹೋವನ ಆಲಯದ ಮತ್ತು ಅರಮನೆಯ ಭಂಡಾರಗಳಿಂದ ಬೆಳ್ಳಿಬಂಗಾರವನ್ನು ತೆಗೆದುಕೊಂಡು ದಮಸ್ಕದಲ್ಲಿದ್ದ ಅರಾಮ್ಯರ ಅರಸನಾದ ಬೆನ್ಹದದನಿಗೆ ಕಳುಹಿಸಿ ಅವನಿಗೆ -",
			"3": "<sup>3</sup> ನನಗೂ ನಿನಗೂ ನನ್ನ ತಂದೆಗೂ ನಿನ್ನ ತಂದೆಗೂ ಒಡಂಬಡಿಕೆಯಿರುತ್ತದಲ್ಲಾ; ಇಗೋ, ನಿನಗೆ ಬೆಳ್ಳಿಬಂಗಾರವನ್ನು ಕಳುಹಿಸಿದ್ದೇನೆ; ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾದ ಬಾಷನು ನನ್ನನ್ನು ಬಿಟ್ಟುಹೋಗುವಂತೆ ಅವನ ಸಂಗಡ ನೀನು ಮಾಡಿಕೊಂಡಿರುವ ಒಡಂಬಡಿಕೆಯನ್ನು ಮುರಿದುಬಿಡು ಎಂದು ಹೇಳಿಕಳುಹಿಸಿದನು.",
			"4": "<sup>4</sup> ಬೆನ್ಹದದನು ಅರಸನಾದ ಆಸನ ಮಾತನ್ನು ಕೇಳಿ ತನ್ನ ಸೈನ್ಯಾಧಿಪತಿಗಳನ್ನು ಇಸ್ರಾಯೇಲ್ಯರ ಪಟ್ಟಣಗಳಿಗೆ ವಿರೋಧವಾಗಿ ಕಳುಹಿಸಿದನು; ಅವರು ಇಯ್ಯೋನ್, ದಾನ್, ಆಬೇಲ್ಮಯಿಮ್ ಇವುಗಳನ್ನೂ ನಫ್ತಾಲ್ಯರ ಪಟ್ಟಣಗಳ ಎಲ್ಲಾ ಉಗ್ರಾಣಗಳನ್ನೂ ಹಾಳು ಮಾಡಿದರು.",
			"5": "<sup>5</sup> ಬಾಷನು ಈ ಸುದ್ದಿಯನ್ನು ಕೇಳಿದಾಗ ರಾಮಕೋಟೆ ಕಟ್ಟಿಸುವ ಕೆಲಸವನ್ನು ನಿಲ್ಲಿಸಿಬಿಟ್ಟನು.",
			"6": "<sup>6</sup> ಅನಂತರ ಅರಸನಾದ ಆಸನು ಎಲ್ಲಾ ಯೆಹೂದ್ಯರನ್ನು ಕರಿಸಿ ಬಾಷನು ರಾಮಕೋಟೆಗಾಗಿ ತರಿಸಿದ್ದ ಕಲ್ಲುಮರಗಳನ್ನು ಇವರ ಮುಖಾಂತರವಾಗಿ ತೆಗೆದುಕೊಂಡುಹೋಗಿ ಗೆಬ, ಮಿಚ್ಪ ಎಂಬ ಪಟ್ಟಣಗಳನ್ನು ಭದ್ರಪಡಿಸಿದನು.",
			"7": "<sup>7</sup> ಆ ಕಾಲದಲ್ಲಿ ದರ್ಶಿಯಾದ ಹನಾನಿಯು ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಆಸನ ಬಳಿಗೆ ಬಂದು ಅವನಿಗೆ - ನೀನು ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನ ಮೇಲೆ ಆತುಕೊಳ್ಳದೆ ಅರಾಮ್ಯರ ಅರಸನ ಮೇಲೆ ಆತುಕೊಂಡದರಿಂದ ಅರಾಮ್‍ರಾಜನ ಸೈನ್ಯವು ನಿನ್ನ ಕೈಗೆ ಬೀಳದಂತೆ ತಪ್ಪಿಸಿಕೊಂಡಿತು.",
			"8": "<sup>8</sup> ಕೂಷ್ಯಲೂಬ್ಯರ ಸೈನ್ಯವು ಅಪರಿಮಿತ ರಥಾಶ್ವಬಲಗಳುಳ್ಳ ಮಹಾಸೈನ್ಯವಲ್ಲವೋ! ನೀನು ಯೆಹೋವನ ಮೇಲೆ ಆತುಕೊಂಡದರಿಂದ ಆತನು ಅದನ್ನು ನಿನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸಿಕೊಟ್ಟನು.",
			"9": "<sup>9</sup> ಯೆಹೋವನು ಭೂಲೋಕದ ಎಲ್ಲಾ ಕಡೆಗಳಲ್ಲಿಯೂ ದೃಷ್ಟಿಯನ್ನು ಪ್ರಸರಿಸುತ್ತಾ ತನ್ನ ಕಡೆಗೆ ಯಥಾರ್ಥಮನಸ್ಸುಳ್ಳವರ ರಕ್ಷಣೆಗಾಗಿ ತನ್ನ ಪ್ರತಾಪವನ್ನು ತೋರ್ಪಡಿಸುತ್ತಾನೆ. ನೀನು ಈ ಕಾರ್ಯದಲ್ಲಿ ಬುದ್ಧಿಹೀನನಾಗಿ ನಡೆದುಕೊಂಡಿದ್ದೀ; ಇಂದಿನಿಂದ ನಿನಗೆ ಯುದ್ಧಗಳು ಇದ್ದೇ ಇರುವವು ಎಂದು ಹೇಳಿದನು.",
			"10": "<sup>10</sup> ಆಸನು ಈ ಮಾತುಗಳ ದೆಸೆಯಿಂದ ಬೇಸರಗೊಂಡು ದರ್ಶಿಯ ಮೇಲೆ ಕೋಪಗೊಂಡು ಅವನನ್ನು ಸೆರೆಮನೆಯಲ್ಲಿಡಿಸಿ ಕೋಳಹಾಕಿಸಿದನು. ಇದಲ್ಲದೆ ಜನರಲ್ಲಿ ಕೆಲವರನ್ನು ಪೀಡಿಸಿದನು.",
			"11": "<sup>11</sup> ಆಸನ ಪೂರ್ವೋತ್ತರಚರಿತ್ರೆಯು ಯೆಹೂದ ಮತ್ತು ಇಸ್ರಾಯೇಲ್ ರಾಜರ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತದೆ.",
			"12": "<sup>12</sup> ಅವನ ಆಳಿಕೆಯ ಮೂವತ್ತೊಂಭತ್ತನೆಯ ವರುಷದಲ್ಲಿ ಅವನ ಕಾಲುಗಳಿಗೆ ಬಹುಕಠಿಣವಾದ ರೋಗವುಂಟಾಯಿತು. ಈ ರೋಗದಲ್ಲಿಯೂ ಅವನು ಯೆಹೋವನ ಸಹಾಯವನ್ನು ಕೋರದೆ ವೈದ್ಯರ ಸಹಾಯವನ್ನೇ ಕೋರಿದನು.",
			"13": "<sup>13</sup> ಅವನು ತನ್ನ ಆಳಿಕೆಯ ನಾಲ್ವತ್ತೊಂದನೆಯ ವರುಷದಲ್ಲಿ ತೀರಿಕೊಂಡು",
			"14": "<sup>14</sup> ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಲು ಅವನ ಶವವನ್ನು ಬುಕ್ಕಿಟ್ಟುಗಾರರ ಕಸಬಿನ ಮೇರೆಗೆ ಮಿಶ್ರಮಾಡಲ್ಪಟ್ಟ ತರತರದ ಸುಗಂಧದ್ರವ್ಯಗಳಿಂದ ತುಂಬಿರುವ ಹಾಸಿಗೆಯ ಮೇಲಿಟ್ಟು ಅವನು ತನಗೋಸ್ಕರ ದಾವೀದನಗರದಲ್ಲಿ ತೋಡಿಸಿದ್ದ ಸಮಾಧಿಯಲ್ಲಿ ಅದನ್ನು ಇಟ್ಟರು. ಅವನಿಗೋಸ್ಕರ ಬಹುಧೂಪವನ್ನು ಸುಟ್ಟರು."
		},
		"17": {
			"1": "<sup>1</sup> ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ಯೆಹೋಷಾಫಾಟನು ಅರಸನಾಗಿ ಇಸ್ರಾಯೇಲ್ಯರಿಂದ ಅಪಾಯವಾಗದಂತೆ ತನ್ನನ್ನು ಭದ್ರಪಡಿಸಿಕೊಂಡನು.",
			"2": "<sup>2</sup> ಇವನು ಯೆಹೂದದ ಕೋಟೆಕೊತ್ತಲಗಳುಳ್ಳ ಎಲ್ಲಾ ಪಟ್ಟಣಗಳಲ್ಲಿಯೂ ಸೈನ್ಯವನ್ನಿರಿಸಿ ಯೆಹೂದ ದೇಶದಲ್ಲಿಯೂ ತನ್ನ ತಂದೆಯಾದ ಆಸನು ಕಿತ್ತುಕೊಂಡಿದ್ದ ಎಫ್ರಾಯೀಮ್ಯರ ಪಟ್ಟಣದಲ್ಲಿಯೂ ಅಧಿಕಾರಿಗಳನ್ನು ನೇಮಿಸಿದನು.",
			"3": "<sup>3</sup> ಇವನು ಬಾಳ್‍ದೇವರುಗಳನ್ನು ಅವಲಂಬಿಸದೆ ತನ್ನ ಪೂರ್ವಿಕನಾದ ದಾವೀದನ ಮೊದಲಿನ ನಡತೆಯ ಪ್ರಕಾರ ನಡೆಯುತ್ತಿದ್ದದರಿಂದ ಯೆಹೋವನು ಇವನ ಸಂಗಡ ಇದ್ದನು.",
			"4": "<sup>4</sup> ಇವನು ಇಸ್ರಾಯೇಲ್ಯರಂತೆ ನಡೆಯದೆ ತನ್ನ ತಂದೆಯ ದೇವರ ಭಕ್ತನಾಗಿ ಆತನ ಆಜ್ಞೆಗಳನ್ನು ಅನುಸರಿಸಿದನು.",
			"5": "<sup>5</sup> ಆದದರಿಂದ ಯೆಹೋವನು ಇವನ ರಾಜ್ಯಾಧಿಕಾರವನ್ನು ಸ್ಥಿರಪಡಿಸಿದನು; ಯೆಹೂದ್ಯರೆಲ್ಲರೂ ಯೆಹೋಷಾಫಾಟನಿಗೆ ಕಾಣಿಕೆ ಕೊಡುತ್ತಿದ್ದದರಿಂದ ಇವನ ಧನಘನತೆಗಳು ಹೆಚ್ಚಾದವು.",
			"6": "<sup>6</sup> ಇವನು ಯೆಹೋವನ ಮಾರ್ಗದಲ್ಲಿ ನಡೆದು ಧೈರ್ಯಗೊಂಡನು; ಇದಲ್ಲದೆ ಯೆಹೂದದೇಶದಲ್ಲಿದ್ದ ಪೂಜಾಸ್ಥಳಗಳನ್ನೂ ಅಶೇರವಿಗ್ರಹಸ್ತಂಭಗಳನ್ನೂ ತೆಗೆದುಹಾಕಿಸಿದನು.",
			"7": "<sup>7</sup> ಇವನು ತನ್ನ ಆಳಿಕೆಯ ಮೂರನೆಯ ವರುಷದಲ್ಲಿ ತನ್ನ ಸರದಾರರಾದ ಬೆನ್ಹೈಲ್, ಓಬದ್ಯ, ಜೆಕರ್ಯ, ನೆತನೇಲ್, ಮೀಕಾಯ ಇವರನ್ನೂ",
			"8": "<sup>8</sup> ಇವರ ಜೊತೆಯಲ್ಲಿ ಶೆಮಾಯ, ನೆತನ್ಯ, ಜೆಬದ್ಯ, ಅಸಾಹೇಲ್, ಶೆಮೀರಾಮೋತ್, ಯೆಹೋನಾತಾನ್, ಅದೋನೀಯ, ಟೋಬೀಯ, ಟೋಬದೋನೀಯ ಎಂಬ ಲೇವಿಯರನ್ನೂ ಎಲೀಷಾಮಾ, ಯೆಹೋರಾಮ್ ಎಂಬ ಯಾಜಕರನ್ನೂ ಧರ್ಮೋಪದೇಶ ಮಾಡುವದಕ್ಕೋಸ್ಕರ ಯೆಹೂದ ದೇಶದ ಪಟ್ಟಣಗಳಿಗೆ ಕಳುಹಿಸಿದನು.",
			"9": "<sup>9</sup> ಇವರು ಯೆಹೋವಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ತೆಗೆದುಕೊಂಡು ಯೆಹೂದ ದೇಶದಲ್ಲೆಲ್ಲಾ ಸಂಚಾರಮಾಡಿ ಜನರಿಗೆ ಬೋಧಿಸಿದರು.",
			"10": "<sup>10</sup> ಯೆಹೂದದ ಸುತ್ತಣ ದೇಶಗಳ ರಾಜ್ಯಗಳವರಿಗೆ ಯೆಹೋವನ ಭಯವಿದ್ದದರಿಂದ ಅವರು ಯೆಹೋಷಾಫಾಟನೊಡನೆ ಯುದ್ಧಕ್ಕೆ ಬರಲಿಲ್ಲ.",
			"11": "<sup>11</sup> ಫಿಲಿಷ್ಟಿಯರಲ್ಲಿ ಕೆಲವರು ಯೆಹೋಷಾಫಾಟನಿಗೆ ಕಾಣಿಕೆಯನ್ನೂ ಕಪ್ಪವನ್ನಾಗಿ ಬೆಳ್ಳಿಯನ್ನೂ ತಂದುಕೊಡುತ್ತಿದ್ದರು. ಅರಬಿಯರು ತಮ್ಮ ಪಶುಗಳಲ್ಲಿ ಏಳು ಸಾವಿರದ ಏಳು ನೂರು ಟಗರುಗಳನ್ನೂ ಏಳು ಸಾವಿರದ ಏಳು ನೂರು ಹೋತಗಳನ್ನೂ ಕೊಡುತ್ತಿದ್ದರು.",
			"12": "<sup>12</sup> ಹೀಗೆ ಯೆಹೋಷಾಫಾಟನು ಅತ್ಯಧಿಕವಾಗಿ ಅಭಿವೃದ್ಧಿಹೊಂದಿ ಯೆಹೂದದೇಶದಲ್ಲಿ ಕೋಟೆಗಳನ್ನೂ ಉಗ್ರಾಣದ ಪಟ್ಟಣಗಳನ್ನೂ ಕಟ್ಟಿಸಿದನು.",
			"13": "<sup>13</sup> ಯೆಹೂದದ ಪಟ್ಟಣಗಳಲ್ಲಿ ದೊಡ್ಡ ಪದಾರ್ಥಸಂಗ್ರಹವೂ ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ರಣವೀರರಾದ ಭಟರೂ ಇದ್ದರು.",
			"14": "<sup>14</sup> ಗೋತ್ರಗಳ ಪ್ರಕಾರ ಲೆಕ್ಕಿಸಲ್ಪಟ್ಟ ಇವರ ಗೋತ್ರಪ್ರಧಾನರು ಯಾರಂದರೆ - ಯೆಹೂದ್ಯರಲ್ಲಿ ಮೂರು ಲಕ್ಷ ಮಂದಿ ರಣವೀರರ ಅಧಿಪತಿಯಾದ ಅದ್ನ;",
			"15": "<sup>15</sup> ಇವನ ತರುವಾಯ ಎರಡು ಲಕ್ಷದ ಎಂಭತ್ತು ಸಾವಿರ ಮಂದಿ ರಣವೀರರ ಅಧಿಪತಿಯಾದ ಯೆಹೋಹಾನಾನ್;",
			"16": "<sup>16</sup> ತರುವಾಯ ಎರಡು ಲಕ್ಷ ಮಂದಿ ರಣವೀರರೊಡನೆ ಸ್ವೇಚ್ಫೆಯಿಂದ ತನ್ನನ್ನು ಯೆಹೋವನಿಗೆ ಪ್ರತಿಷ್ಠಿಸಿಕೊಂಡ ಜಿಕ್ರಿಯ ಮಗನಾದ ಅಮಸ್ಯ;",
			"17": "<sup>17</sup> ಬೆನ್ಯಾಮೀನ್ಯರಲ್ಲಿ ಬಿಲ್ಲುಗುರಾಣಿಗಳನ್ನು ಹಿಡಿದುಕೊಂಡ ಎರಡು ಲಕ್ಷ ಸೈನಿಕರ ನಾಯಕನೂ ರಣವೀರನೂ ಆದ ಎಲ್ಯಾದ;",
			"18": "<sup>18</sup> ಇವನ ತರುವಾಯ ಒಂದು ಲಕ್ಷದ ಎಂಭತ್ತು ಸಾವಿರ ಮಂದಿ ಯುದ್ಧಸನ್ನದ್ಧರ ನಾಯಕನಾದ ಯೆಹೋಜಾಬಾದ್ ಇವರೇ.",
			"19": "<sup>19</sup> ಅರಸನಿಗೆ ತನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸೇವೆಮಾಡುತ್ತಿರುವ ಇವರಲ್ಲದೆ ಕೋಟೆಕೊತ್ತಲಗಳುಳ್ಳ ಪಟ್ಟಣಗಳಲ್ಲಿಯೂ ಸೈನಿಕರಿದ್ದರು."
		},
		"18": {
			"1": "<sup>1</sup> ಯೆಹೋಷಾಫಾಟನಿಗೆ ಬಹಳ ಧನಘನತೆಗಳುಂಟಾದವು. ಅವನು ಅಹಾಬನೊಡನೆ ಬೀಗತನಮಾಡಿ",
			"2": "<sup>2</sup> ಕೆಲವು ವರುಷಗಳಾದನಂತರ ಅವನ ಬಳಿಗೆ ಸಮಾರ್ಯಕ್ಕೆ ಹೋದಾಗ ಅಹಾಬನು ಅವನಿಗೋಸ್ಕರವೂ ಅವನೊಡನೆ ಬಂದ ಜನರಿಗೋಸ್ಕರವೂ ಅನೇಕಾನೇಕ ದನಕುರಿಗಳನ್ನು ವಧಿಸಿ ಔತಣಮಾಡಿಸಿ ತನ್ನ ಸಂಗಡ ಯುದ್ಧಕ್ಕಾಗಿ ರಾಮೋತ್‍ಗಿಲ್ಯಾದಿಗೆ ಬರಬೇಕೆಂದು ಅವನನ್ನು ಪ್ರೇರಿಸಿದನು.",
			"3": "<sup>3</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾದ ಅಹಾಬನು ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಯೆಹೋಷಾಫಾಟನನ್ನು - ನನ್ನ ಜೊತೆಯಲ್ಲಿ ರಾಮೋತ್ ಗಿಲ್ಯಾದಿಗೆ ಬರುತ್ತೀಯೋ ಎಂದು ಕೇಳಲು ಅವನು - ನಾನೂ ನೀನೂ ನನ್ನ ಜನರೂ ನಿನ್ನ ಜನರೂ ಒಂದೇಯಲ್ಲವೋ. ನಿನ್ನ ಜೊತೆಯಲ್ಲಿ ಯುದ್ಧಕ್ಕೆ ಬರುವೆನು ಎಂದು ಉತ್ತರಕೊಟ್ಟು",
			"4": "<sup>4</sup> ಅನಂತರ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಈಗ ವಿಚಾರಿಸು ಎಂದು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನನ್ನು ಬೇಡಿಕೊಂಡನು.",
			"5": "<sup>5</sup> ಆಗ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನು ತನ್ನ ರಾಜ್ಯದಲ್ಲಿದ್ದ ನಾನೂರು ಮಂದಿ ಪ್ರವಾದಿಗಳನ್ನು ಕೂಡಿಸಿ - ನಾವು ರಾಮೋತ್ ಗಿಲ್ಯಾದಿನ ಮೇಲೆ ಯುದ್ಧಕ್ಕಾಗಿ ಹೋಗಬಹುದೋ ಬಾರದೋ ಎಂದು ಕೇಳಲು ಅವರು - ಹೋಗಬಹುದು, ದೇವರು ಅದನ್ನು ಅರಸನ ಕೈಗೆ ಒಪ್ಪಿಸುವನು ಅಂದರು.",
			"6": "<sup>6</sup> ಆದರೆ ಯೆಹೋಷಾಫಾಟನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನನ್ನು - ಯೆಹೋವನ ಪ್ರವಾಧಿಗಳಲ್ಲಿ ಇವರ ಹೊರತಾಗಿ ನಾವು ವಿಚಾರಿಸಬಹುದಾದ ಬೇರೆ ಪ್ರವಾದಿಯಿರುವದಿಲ್ಲವೋ ಎಂದು ಕೇಳಲು ಅವನು -",
			"7": "<sup>7</sup> ನಮಗೋಸ್ಕರ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ವಿಚಾರಿಸಬಲ್ಲವನಾದ ಇನ್ನೊಬ್ಬ ಪ್ರವಾದಿಯಿರುತ್ತಾನೆ; ಅವನು ಇಮ್ಲನ ಮಗನಾದ ಮೀಕಾಯೆಹು ಎಂಬವನು. ಆದರೆ ನಾನು ಅವನನ್ನು ಹಗೆಮಾಡುತ್ತೇನೆ; ಅವನು ನನ್ನನ್ನು ಕುರಿತು ಯಾವಾಗಲೂ ಶುಭವನ್ನಲ್ಲ, ಅಶುಭವನ್ನೇ ಮುಂತಿಳಿಸುತ್ತಾನೆ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು. ಅದಕ್ಕೆ ಯೆಹೋಷಾಫಾಟನು - ಅರಸನು ಹಾಗೆ ಹೇಳಬಾರದು ಅಂದನು.",
			"8": "<sup>8</sup> ಆಗ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನು ಒಬ್ಬ ಕಂಚುಕಿಯನ್ನು ಕರೆದು ಅವನಿಗೆ - ಬೇಗ ಹೋಗಿ ಇಮ್ಲನ ಮಗನಾದ ಮೀಕಾಯೆಹುವನ್ನು ಕರೆದುಕೊಂಡು ಬಾ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"9": "<sup>9</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನೂ ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಯೆಹೋಷಾಫಾಟನೂ ರಾಜವಸ್ತ್ರಗಳನ್ನು ಧರಿಸಿಕೊಂಡು ಸಮಾರ್ಯಪಟ್ಟಣದ ಹೆಬ್ಬಾಗಲಿನ ಹತ್ತಿರವಿರುವ ಬೈಲಿನಲ್ಲಿ ತಮ್ಮ ತಮ್ಮ ಆಸನಗಳ ಮೇಲೆ ಕೂತುಕೊಂಡಿರಲು ಎಲ್ಲಾ ಪ್ರವಾದಿಗಳು ಅವರ ಮುಂದೆ ಪರವಶರಾಗಿ ಮಾತಾಡುತ್ತಾ ಇದ್ದರು.",
			"10": "<sup>10</sup> ಅವರಲ್ಲಿ ಕೆನಾನನ ಮಗನಾದ ಚಿದ್ಕೀಯ ಎಂಬವನು ಕಬ್ಬಿಣದ ಕೊಂಬುಗಳನ್ನು ಮಾಡಿಸಿ [ತಲೆಗೆ ಕಟ್ಟಿಕೊಂಡು] ಬಂದು - ಕೊಂಬುಗಳಿಂದಲೋ ಎಂಬಂತೆ ನೀನು ಅರಾಮ್ಯರನ್ನು ಇರಿದು ಕೊಂದುಹಾಕುವಿ ಎಂಬದಾಗಿ ಯೆಹೋವನು ಹೇಳುತ್ತಾನೆ ಎಂದನು.",
			"11": "<sup>11</sup> ಉಳಿದ ಪ್ರವಾದಿಗಳೂ ಇದೇ ತರದ ಮಾತುಗಳನ್ನು ನುಡಿದು - ರಾಮೋತ್‍ಗಿಲ್ಯಾದಿಗೆ ಹೋಗು; ನೀನು ಕೃತಾರ್ಥನಾಗಿ ಬರುವಿ; ಯೆಹೋವನು ಅದನ್ನು ಅರಸನ ಕೈಗೆ ಒಪ್ಪಿಸುವನು ಅಂದರು.",
			"12": "<sup>12</sup> ಮೀಕಾಯೆಹುವನ್ನು ಕರೆಯುವದಕ್ಕೆ ಹೋದ ದೂತನು ಅವನಿಗೆ - ಎಲ್ಲಾ ಪ್ರವಾದಿಗಳೂ ಏಕ ಮನಸ್ಸಿನಿಂದ ಅರಸನಿಗೆ ಶುಭವನ್ನೇ ಮುಂತಿಳಿಸುತ್ತಿರುತ್ತಾರೆ; ದಯವಿಟ್ಟು ನೀನೂ ಅವರಂತೆ ಶುಭವನ್ನೇ ಮುಂತಿಳಿಸು ಎಂದು ಹೇಳಿದನು.",
			"13": "<sup>13</sup> ಅದಕ್ಕೆ ಮೀಕಾಯೆಹುವು - ಯೆಹೋವನಾಣೆ, ನನ್ನ ದೇವರು ಹೇಳುವದನ್ನೇ ನುಡಿಯುತ್ತೇನೆ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"14": "<sup>14</sup> ಅವನು ಅರಸನ ಬಳಿಗೆ ಬಂದಾಗ ಅರಸನು - ಮೀಕಾಯೆಹುವೇ, ನಾವು ರಾಮೋತ್‍ಗಿಲ್ಯಾದಿನ ಮೇಲೆ ಯುದ್ಧಕ್ಕಾಗಿ ಹೋಗಬಹುದೋ ಬಾರದೋ ಎಂದು ಕೇಳಲು ಅವನು - ಹೋಗಬಹುದು, ಕೃತಾರ್ಥರಾಗಿ ಬರುವಿರಿ; ವೈರಿಗಳು ನಿಮ್ಮ ಕೈವಶರಾಗುವರು ಎಂದು ಹೇಳಿದನು.",
			"15": "<sup>15</sup> ಆಗ ಅರಸನು ಅವನಿಗೆ - ಸತ್ಯವನ್ನೇ ತಿಳಿಸಬೇಕೆಂದು ಯೆಹೋವನ ಹೆಸರಿನಲ್ಲಿ ನಿನ್ನಿಂದ ಎಷ್ಟುಸಾರಿ ಪ್ರಮಾಣ ಮಾಡಿಸಬೇಕು ಅನ್ನಲು",
			"16": "<sup>16</sup> ಅವನು - ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರೂ ಕುರುಬನಿಲ್ಲದ ಕುರಿಗಳಂತೆ ಬೆಟ್ಟಗಳಲ್ಲಿ ಚದರಿಹೋದದ್ದನ್ನು ಕಂಡೆನು; ಆಗ ಯೆಹೋವನು - ಇವರು ಒಡೆಯನಿಲ್ಲದವರಾಗಿರುತ್ತಾರೆ; ಸಮಾಧಾನದಿಂದ ತಮ್ಮ ತಮ್ಮ ಮನೆಗಳಿಗೆ ಹೋಗಲಿ ಅಂದನು ಎಂಬದಾಗಿ ಉತ್ತರ ಕೊಟ್ಟನು.",
			"17": "<sup>17</sup> ಆಗ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನು ಯೆಹೋಷಾಫಾಟನಿಗೆ - ಇವನು ನನಗೆ ಯಾವಾಗಲೂ ಶುಭವನ್ನಲ್ಲ, ಅಶುಭವನ್ನೇ ಮುಂತಿಳಿಸುತ್ತಾನೆಂದು ನಾನು ನಿನಗೆ ಹೇಳಲಿಲ್ಲವೋ? ಎಂದನು.",
			"18": "<sup>18</sup> ಅದಕ್ಕೆ ಮೀಕಾಯೆಹುವು - ಅದಿರಲಿ, ಯೆಹೋವನ ವಾಕ್ಯವನ್ನು ಕೇಳಿರಿ. ಯೆಹೋವನು ತನ್ನ ಸಿಂಹಾಸನದ ಮೇಲೆ ಕೂತುಕೊಂಡದ್ದನ್ನೂ ಪರಲೋಕಸೈನ್ಯಗಳು ಆತನ ಎಡಬಲಗಡೆಗಳಲ್ಲಿ ನಿಂತದ್ದನ್ನೂ ಕಂಡೆನು.",
			"19": "<sup>19</sup> ಯೆಹೋವನು ತನ್ನ ಹತ್ತಿರ ನಿಂತವರನ್ನು - ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾದ ಅಹಾಬನು ಹತನಾಗಿ ಬೀಳುವಂತೆ ಅವನನ್ನು ರಾಮೋತ್‍ಗಿಲ್ಯಾದಿನ ಯುದ್ಧಕ್ಕೆ ಯಾರು ಪ್ರೇರಿಸುವಿರಿ ಎಂದು ಕೇಳಿದಾಗ ಒಬ್ಬನು ಈ ತರವಾಗಿಯೂ ಇನ್ನೊಬ್ಬನು ಆ ತರವಾಗಿಯೂ ಉತ್ತರಕೊಟ್ಟರು.",
			"20": "<sup>20</sup> ಕಡೆಯಲ್ಲಿ ಒಂದು ಆತ್ಮವು ಯೆಹೋವನ ಮುಂದೆ ಬಂದು ನಿಂತು ಆತನಿಗೆ - ನಾನು ಹೋಗಿ ಅವನನ್ನು ಪ್ರೇರಿಸುವೆನು ಅಂದಿತು. ಹೇಗೆ ಪ್ರೇರಿಸುವಿ ಎಂದು ಯೆಹೋವನು ಕೇಳಲು ಅದು -",
			"21": "<sup>21</sup> ನಾನು ಅಸತ್ಯವನ್ನಾಡುವ ಆತ್ಮವಾಗಿ ಅವನ ಎಲ್ಲಾ ಪ್ರವಾದಿಗಳಲ್ಲಿ ಸೇರುವೆನು ಎಂದು ಉತ್ತರಕೊಟ್ಟಿತು. ಆಗ ಆತನು ಅದಕ್ಕೆ - ಹೋಗಿ ಅದರಂತೆ ಮಾಡು; ಅವನನ್ನು ಪ್ರೇರಿಸಿ ಸಫಲನಾಗುವಿ ಅಂದನು.",
			"22": "<sup>22</sup> ನೋಡು, ಯೆಹೋವನು ನಿನ್ನ ವಿಷಯದಲ್ಲಿ ಕೇಡು ನುಡಿದು ನಿನ್ನ ಪ್ರವಾದಿಗಳಲ್ಲಿ ಅಸತ್ಯವನ್ನಾಡುವ ಆತ್ಮವನ್ನು ಕಳುಹಿಸಿದ್ದಾನೆ ಎಂದು ಹೇಳಿದನು.",
			"23": "<sup>23</sup> ಆಗ ಕೆನಾನನ ಮಗನಾದ ಚಿದ್ಕೀಯನು ಮೀಕಾಯೆಹುವಿನ ಬಳಿಗೆ ಹೋಗಿ ಅವನ ಕೆನ್ನೆಗೆ ಒಂದು ಏಟು ಹಾಕಿ - ಯೆಹೋವನ ಆತ್ಮವು ನನ್ನನ್ನು ಬಿಟ್ಟು ನಿನ್ನೊಂದಿಗೆ ಮಾತಾಡುವದಕ್ಕೋಸ್ಕರ ಯಾವ ಮಾರ್ಗವಾಗಿ ಬಂದಿತು ಅನ್ನಲು ಮೀಕಾಯೆಹುವು -",
			"24": "<sup>24</sup> ನೀನು ಅಡಗಿಕೊಳ್ಳುವದಕ್ಕೆ ಒಳಗಿನ ಕೋಣೆಗೆ ಹೋಗುವ ದಿನದಲ್ಲಿ ಅದು ನಿನಗೆ ಗೊತ್ತಾಗುವದು ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"25": "<sup>25</sup> ಆಗ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನು [ಸೇವಕರಿಗೆ] - ಮೀಕಾಯೆಹುವನ್ನು ಹಿಡಿದುಕೊಂಡು ಹೋಗಿ ಪುರಾಧಿಕಾರಿಯಾದ ಆಮೋನನಿಗೂ ರಾಜಪುತ್ರನಾದ ಯೋವಾಷನಿಗೂ ಒಪ್ಪಿಸಿ ಅವರಿಗೆ -",
			"26": "<sup>26</sup> ನಾನು ಸುರಕ್ಷಿತನಾಗಿ ಹಿಂದಿರುಗುವವರೆಗೂ ಇವನನ್ನು ಸೆರೆಯಲ್ಲಿಟ್ಟು ಸೆರೆಮನೆಯ ಅನ್ನಪಾನಗಳನ್ನೇ ಕೊಟ್ಟು ಕುಗ್ಗಿಸಬೇಕು ಎಂದು ಅರಸನು ಅನ್ನುತ್ತಾನೆ ಎಂದು ಹೇಳಿರಿ ಎಂಬದಾಗಿ ಆಜ್ಞಾಪಿಸಿದನು.",
			"27": "<sup>27</sup> ಮೀಕಾಯೆಹುವು ಅರಸನಿಗೆ - ನೀನು ಸುರಕ್ಷಿತನಾಗಿ ಬರುವದಾದರೆ ನಾನು ನುಡಿದದ್ದು ಯೆಹೋವನ ಮಾತಲ್ಲ ಎಂದು ಹೇಳಿ - ಎಲ್ಲಾ ಜನರೇ, ಇದನ್ನು ಕೇಳಿರಿ ಎಂದು ಕೂಗಿದನು.",
			"28": "<sup>28</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನೂ ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಯೆಹೋಷಾಫಾಟನೂ ರಾಮೋತ್‍ಗಿಲ್ಯಾದಿಗೆ ವಿರೋಧವಾಗಿ ಯುದ್ಧಕ್ಕೆ ಹೊರಟರು.",
			"29": "<sup>29</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನು ಯೆಹೋಷಾಫಾಟನಿಗೆ - ನಾನು ವೇಷಹಾಕಿಕೊಂಡು ರಣರಂಗಕ್ಕೆ ಬರುವೆನು; ನೀನಾದರೋ ನಿನ್ನ ರಾಜವಸ್ತ್ರಗಳನ್ನು ಧರಿಸಿಕೊಂಡೇ ಬಾ ಎಂದು ಹೇಳಿ ವೇಷಹಾಕಿಕೊಂಡನು; ತರುವಾಯ ರಣರಂಗಕ್ಕೆ ಹೋದರು.",
			"30": "<sup>30</sup> ಅರಾಮ್ಯರ ಅರಸನು ತನ್ನ ರಥಬಲದ ಅಧಿಪತಿಗಳಿಗೆ - ನೀವು ಶತ್ರುಗಳ ಸಾಧಾರಣ ಸೈನಿಕರನ್ನೂ ಅಧಿಪತಿಗಳನ್ನೂ ಬಿಟ್ಟು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಿಗೇ ಗುರಿಯಿಡಿರಿ ಎಂದು ಆಜ್ಞಾಪಿಸಿದ್ದನು.",
			"31": "<sup>31</sup> ರಥಬಲದ ಅಧಿಪತಿಗಳು ಯೆಹೋಷಾಫಾಟನನ್ನು ಕಂಡಾಗ ಅವನೇ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನೆಂದು ನೆನಸಿ ಅವನಿಗೆ ವಿರೋಧವಾಗಿ ಯುದ್ಧಮಾಡುವದಕ್ಕೆ ಅವನನ್ನು ಸುತ್ತಿಕೊಂಡರು. ಯೆಹೋಷಾಫಾಟನು ಕೂಗಿಕೊಳ್ಳಲು ದೇವರಾದ ಯೆಹೋವನು ಅವನಿಗೆ ನೆರವಾಗಿ ಶತ್ರುಗಳನ್ನು ಅವನ ಕಡೆಯಿಂದ ತೊಲಗಿಸಿದನು.",
			"32": "<sup>32</sup> ಅವನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಲ್ಲವೆಂಬದು ರಥಬಲದ ಅಧಿಪತಿಗಳಿಗೆ ಗೊತ್ತಾದಾಗ ಅವರು ಅವನನ್ನು ಬಿಟ್ಟು ಹಿಂದಿರುಗಿದರು.",
			"33": "<sup>33</sup> ಅರಾಮ್ಯರ ಒಬ್ಬ ಸೈನಿಕನು ಯಾರಿಗೂ ಗುರಿಯಿಡದೆ ಬಾಣವನ್ನೆಸೆಯಲು ಆ ಬಾಣವು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಿಗೆ ಅವನ ಕವಚದ ಸಂದಿನಲ್ಲಿ ತಾಗಿತು. ಆಗ ಅವನು ತನ್ನ ಸಾರಥಿಗೆ - ರಥವನ್ನು ತಿರುಗಿಸಿ ನನ್ನನ್ನು ರಣರಂಗದಿಂದ ಆಚೆಗೆ ತೆಗೆದುಕೊಂಡು ಹೋಗು; ನನಗೆ ದೊಡ್ಡ ಗಾಯವಾಗಿದೆ ಎಂದು ಹೇಳಿದನು.",
			"34": "<sup>34</sup> ಆದರೂ ಆ ದಿವಸ ಯುದ್ಧವು ಬಹು ಘೋರವಾಗಿದ್ದದರಿಂದ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನು ಸಾಯಂಕಾಲದವರೆಗೂ ಅರಾಮ್ಯರ ಎದುರಾಗಿ ತನ್ನ ರಥದಲ್ಲೇ ಆತುಕೊಂಡಿದ್ದು ಸೂರ್ಯಾಸ್ತಮಾನವಾದಾಗ ಸತ್ತನು."
		},
		"19": {
			"1": "<sup>1</sup> ಯೆಹೂದದ ಅರಸನಾದ ಯೆಹೋಷಾಫಾಟನು ಸುರಕ್ಷಿತವಾಗಿ ತನ್ನ ಮನೆಗೆ ಹಿಂದಿರುಗಿದನು.",
			"2": "<sup>2</sup> ಅವನು ಯೆರೂಸಲೇಮಿಗೆ ಬರಲು ಹನಾನೀಯನ ಮಗನಾದ ಯೇಹೂ ಎಂಬ ದರ್ಶಿಯು ಅವನನ್ನು ಎದುರುಗೊಂಡು ಅವನಿಗೆ - ನೀನು ಕೆಟ್ಟವನಿಗೆ ಸಹಾಯಮಾಡತಕ್ಕದ್ದೋ? ಯೆಹೋವನ ಹಗೆಗಾರರನ್ನು ಪ್ರೀತಿಸುವದೋ! ನೀನು ಹೀಗೆ ಮಾಡಿದ್ದರಿಂದ ಯೆಹೋವನ ಕೋಪವು ನಿನ್ನ ಮೇಲಿರುತ್ತದೆ.",
			"3": "<sup>3</sup> ಆದರೂ ನೀನು ದೇಶದೊಳಗಿಂದ ಅಶೇರವಿಗ್ರಹಸ್ತಂಭಗಳನ್ನು ತೆಗೆದುಹಾಕಿ ಯೆಹೋವನ ಭಕ್ತಿಯಲ್ಲಿ ಮನಸ್ಸಿಟ್ಟದ್ದರಿಂದ ನಿನ್ನಲ್ಲಿ ಸುಶೀಲತೆಯೂ ಉಂಟೆಂದು ತಿಳಿದುಬಂತು ಎಂಬದಾಗಿ ಹೇಳಿದನು.",
			"4": "<sup>4</sup> ಯೆಹೋಷಾಫಾಟನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ವಾಸವಾಗಿದ್ದು ಇನ್ನೊಮ್ಮೆ ಬೇರ್ಷೆಬದಿಂದ ಎಫ್ರಾಯೀಮ್ ಪರ್ವತದವರೆಗೂ ಸಂಚರಿಸಿ ತನ್ನ ಪ್ರಜೆಗಳನ್ನು ಅವರ ಪಿತೃಗಳ ದೇವರಾದ ಯೆಹೋವನ ಕಡೆಗೆ ತಿರುಗಿಸಿದನು.",
			"5": "<sup>5</sup> ಅವನು ಯೆಹೂದದೇಶದ ಪ್ರತಿಯೊಂದು ಕೋಟೆಕೊತ್ತಲಗಳುಳ್ಳ ಪಟ್ಟಣದಲ್ಲಿ ನ್ಯಾಯಾಧಿಪತಿಗಳನ್ನು ನೇಮಿಸಿ ಅವರಿಗೆ -",
			"6": "<sup>6</sup> ನೀವು ಹೇಗೆ ಕೆಲಸನಡಿಸುತ್ತೀರಿ ಎಂಬದರ ವಿಷಯ ನೋಡಿಕೊಳ್ಳಿರಿ. ನೀವು ನ್ಯಾಯತೀರಿಸುವದು ಮನುಷ್ಯರಿಗೋಸ್ಕರವಲ್ಲ, ಯೆಹೋವನಿಗೋಸ್ಕರವೇ. ನ್ಯಾಯವಿಚಾರಣೆ ನಡೆಯುವಾಗ ಆತನು ನಿಮ್ಮ ಮಧ್ಯದಲ್ಲಿರುತ್ತಾನೆ.",
			"7": "<sup>7</sup> ಹೀಗಿರುವಲ್ಲಿ ನಿಮಗೆ ಯೆಹೋವನ ಭಯವು ಇರಲಿ. ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಲ್ಲಿ ಅನ್ಯಾಯವೂ ಮುಖದಾಕ್ಷಿಣ್ಯವೂ ಲಂಚತಿನ್ನುವದೂ ಇಲ್ಲವಾದದರಿಂದ ಜಾಗರೂಕತೆಯಿಂದ ಕೆಲಸಮಾಡಿರಿ ಎಂದು ಎಚ್ಚರಿಸಿದನು.",
			"8": "<sup>8</sup> ಇದಲ್ಲದೆ ಯೆಹೋಷಾಫಾಟನು ತನ್ನ ಪರಿವಾರದವರೊಡನೆ ಯೆರೂಸಲೇಮಿಗೆ ಹಿಂದಿರುಗಿ ಬಂದು ಲೇವಿಯರಿಂದಲೂ ಯಾಜಕರಿಂದಲೂ ಇಸ್ರಾಯೇಲ್‍ಗೋತ್ರ ಪ್ರಧಾನರಿಂದಲೂ ಕೆಲವರನ್ನು ಆರಿಸಿ ಯೆಹೋವ ಧರ್ಮಶಾಸ್ತ್ರಕ್ಕೆ ಸಂಬಂಧಪಟ್ಟ ವ್ಯಾಜ್ಯಗಳನ್ನೂ ಬೇರೆ ವ್ಯಾಜ್ಯಗಳನ್ನೂ ವಿಚಾರಿಸಿ ತೀರಿಸುವದಕ್ಕೋಸ್ಕರ ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಇರಿಸಿ ಅವರಿಗೆ -",
			"9": "<sup>9</sup> ನೀವು ಯೆಹೋವನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿ ನಂಬಿಕೆಯಿಂದಲೂ ಯಥಾರ್ಥಮನಸ್ಸಿನಿಂದಲೂ ಮಾಡತಕ್ಕ ಕೆಲಸವು ಯಾವದಂದರೆ -",
			"10": "<sup>10</sup> ಯೆಹೂದದ ಪಟ್ಟಣಗಳಲ್ಲಿ ವಾಸಿಸುವ ನಿಮ್ಮ ಸಹೋದರರೊಳಗೆ ಜೀವಹತ್ಯ ಸಂಬಂಧದಲ್ಲಿಯಾಗಲಿ ಧರ್ಮಶಾಸ್ತ್ರದ ಆಯಾ ಆಜ್ಞಾವಿಧಿನ್ಯಾಯಗಳ ಸಂಬಂಧದಲ್ಲಿಯಾಗಲಿ ವ್ಯಾಜ್ಯವುಂಟಾಗಿ ಅದು ನಿಮ್ಮ ಮುಂದೆ ಬರುವದಾದರೆ ನಿಮ್ಮ ಸಹೋದರರು ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ಅಪರಾಧಿಗಳಾಗದಂತೆಯೂ ನೀವು ಅವರೊಂದಿಗೆ ದೇವಕೋಪಕ್ಕೆ ಗುರಿಯಾಗದಂತೆಯೂ ಅವರನ್ನು ಎಚ್ಚರಿಸಿರಿ. ಹೀಗೆ ಮಾಡುವದಾದರೆ ನೀವು ನಿರ್ದೋಷಿಗಳಾಗಿರುವಿರಿ.",
			"11": "<sup>11</sup> ಇಗೋ, ಯೆಹೋವನ ಕಾರ್ಯಸಂಬಂಧವಾದ ಎಲ್ಲಾ ವಿಚಾರಣೆಗಳಲ್ಲಿಯೂ ಮಹಾಯಾಜಕನಾದ ಅಮರ್ಯನು ನಿಮ್ಮ ಅಧ್ಯಕ್ಷನು; ರಾಜಕೀಯಕಾರ್ಯ ಸಂಬಂಧವಾದ ಎಲ್ಲಾ ವಿಚಾರಣೆಗಳಲ್ಲಿಯೂ ಇಷ್ಮಾಯೇಲನ ಮಗನೂ ಯೆಹೂದಕುಲಪ್ರಭುವೂ ಆದ ಜೆಬದ್ಯನು ಅಧ್ಯಕ್ಷನು; ಲೇವಿಯರು ನ್ಯಾಯಸ್ಥಾನ ಲೇಖಕರು; ಧೈರ್ಯದಿಂದ ಕೆಲಸಮಾಡಿರಿ, ಯೆಹೋವನು ಸಜ್ಜನರ ಸಹಾಯಕನು ಎಂದು ಹೇಳಿದನು."
		},
		"20": {
			"1": "<sup>1</sup> ಇದಾದ ಮೇಲೆ ಮೋವಾಬ್ಯರೂ ಅಮ್ಮೋನಿಯರೂ ಮೆಗೂನ್ಯರಲ್ಲಿ ಕೆಲವರೂ ಯೆಹೋಷಾಫಾಟನಿಗೆ ವಿರೋಧವಾಗಿ ಯುದ್ಧಕ್ಕೆ ಬಂದರು.",
			"2": "<sup>2</sup> ದೂತರು ಬಂದು ಯೆಹೋಷಾಫಾಟನಿಗೆ - ಮಹಾಸಮೂಹವು ನಿನಗೆ ವಿರೋಧವಾಗಿ ಲವಣಸಮುದ್ರದ ಆಚೆಯಲ್ಲಿರುವ ಅರಾಮ್ ಪ್ರಾಂತದ ಕಡೆಯಿಂದ ಬಂದು ಈಗ ಏಂಗೆದಿಯೆನಿಸಿಕೊಳ್ಳುವ ಹಚೆಚೋನ್‍ತಾಮಾರಿನಲ್ಲಿರುತ್ತಾರೆಂದು ತಿಳಿಸಿದಾಗ",
			"3": "<sup>3</sup> ಯೆಹೋಷಾಫಾಟನು ಹೆದರಿ ಯೆಹೋವನನ್ನೇ ಆಶ್ರಯಿಸಿಕೊಳ್ಳಬೇಕೆಂದು ನಿರ್ಣಯಿಸಿಕೊಂಡು ಯೆಹೂದ್ಯರೆಲ್ಲರೂ ಉಪವಾಸಮಾಡಬೇಕೆಂದು ಪ್ರಕಟಿಸಿದನು.",
			"4": "<sup>4</sup> ಆಗ ಯೆಹೂದ್ಯರು ಯೆಹೋವನ ಸಹಾಯವನ್ನು ಕೇಳಿಕೊಳ್ಳುವದಕ್ಕಾಗಿ ತಮ್ಮ ಎಲ್ಲಾ ಪಟ್ಟಣಗಳಿಂದ ಆತನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಕೂಡಿಬಂದರು.",
			"5": "<sup>5</sup> ಯೆಹೂದ್ಯರೂ ಯೆರೂಸಲೇಮಿನವರೂ ಯೆಹೋವನ ಆಲಯದ ಹೊಸ ಪ್ರಾಕಾರದಲ್ಲಿ ಸಭೆಯಾಗಿ ನೆರೆದು ಬಂದಾಗ ಯೆಹೋಷಾಫಾಟನು ಅದರ ಮುಂಗಡೆಯಲ್ಲಿ ನಿಂತು -",
			"6": "<sup>6</sup> ಯೆಹೋವನೇ, ನಮ್ಮ ಪಿತೃಗಳ ದೇವರೇ, ಪರಲೋಕದಲ್ಲಿ ದೇವರಾಗಿರುವಾತನು ನೀನಲ್ಲವೋ? ನೀನು ಜನಾಂಗಗಳ ಎಲ್ಲಾ ರಾಜ್ಯಗಳನ್ನು ಆಳುವವನಾಗಿರುತ್ತೀ. ನಿನ್ನ ಹಸ್ತದಲ್ಲಿ ಬಲಪರಾಕ್ರಮಗಳಿರುತ್ತವೆ; ನಿನ್ನೆದುರಿನಲ್ಲಿ ಯಾರೂ ನಿಲ್ಲಲಾರರು.",
			"7": "<sup>7</sup> ನಮ್ಮ ದೇವರಾದ ನೀನು ನಿನ್ನ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಾಯೇಲ್ಯರ ಎದುರಿನಿಂದ ಈ ದೇಶದ ನಿವಾಸಿಗಳನ್ನು ಹೊರಡಿಸಿ ದೇಶವನ್ನು ನಿನ್ನ ಸ್ನೇಹಿತನಾದ ಅಬ್ರಹಾಮನ ಸಂತಾನದವರಿಗೆ ಶಾಶ್ವತ ಸ್ವಾಸ್ತ್ಯವನ್ನಾಗಿ ಕೊಟ್ಟಿಯಲ್ಲಾ.",
			"8": "<sup>8</sup> ಅವರು ಈ ದೇಶದಲ್ಲಿ ಇಳುಕೊಂಡು ಇದರಲ್ಲಿ ನಿನ್ನ ಹೆಸರಿಗೋಸ್ಕರ ಪವಿತ್ರಾಲಯವನ್ನು ಕಟ್ಟಿ -",
			"9": "<sup>9</sup> ತಮ್ಮ ಮೇಲೆ ಖಡ್ಗವಿಧಿ ಘೋರವ್ಯಾಧಿ ಕ್ಷಾಮ ಮೊದಲಾದ ಆಪತ್ತುಗಳು ಬರುವಾಗ ತಾವು ನಿನ್ನ ನಾಮಮಹತ್ತು ಇರುವ ಈ ಆಲಯದ ಮುಂದೆಯೂ ನಿನ್ನ ಮುಂದೆಯೂ ನಿಂತು ತಮ್ಮ ಇಕ್ಕಟ್ಟಿನಲ್ಲಿ ನಿನಗೆ ಮೊರೆಯಿಡುವದಾದರೆ ನೀನು ಕೇಳಿ ರಕ್ಷಿಸುವಿ ಎಂದುಕೊಂಡರಷ್ಟೆ.",
			"10": "<sup>10</sup> ಈ ಅಮ್ಮೋನಿಯರನ್ನೂ ಮೋವಾಬ್ಯರನ್ನೂ ಸೇಯೀರ್ ಪರ್ವತಪ್ರದೇಶದವರನ್ನೂ ನೋಡು; ಇಸ್ರಾಯೇಲ್ಯರು ಐಗುಪ್ತದೇಶದಿಂದ ಬರುತ್ತಿದ್ದಾಗ ಇವರ ದೇಶದಲ್ಲಿ ನುಗ್ಗಬಾರದೆಂದು ನಿನ್ನಿಂದ ಅಪ್ಪಣೆಹೊಂದಿ ಇವರನ್ನು ಸಂಹರಿಸದೆ ವಾರೆಯಾಗಿ ಹೋದರಲ್ಲಾ.",
			"11": "<sup>11</sup> ಈಗ ಇವರು ಉಪಕಾರಕ್ಕೆ ಅಪಕಾರಮಾಡಿ ನೀನು ನಮಗೆ ಅನುಗ್ರಹಿಸಿದ ಸ್ವಾಸ್ತ್ಯದೊಳಗಿಂದ ನಮ್ಮನ್ನು ಹೊರಪಡಿಸುವದಕ್ಕೋಸ್ಕರ ನಮ್ಮ ಮೇಲೆ ಯುದ್ಧಕ್ಕೆ ಬಂದಿರುತ್ತಾರೆ.",
			"12": "<sup>12</sup> ನಮ್ಮ ದೇವರೇ, ಅವರನ್ನು ದಂಡಿಸದೆ ಬಿಡುವಿಯೋ? ನಮ್ಮ ಮೇಲೆ ಬಂದ ಈ ಮಹಾ ಸಮೂಹದ ಮುಂದೆ ನಿಲ್ಲುವದಕ್ಕೆ ನಮ್ಮಲ್ಲಿ ಬಲವಿಲ್ಲ, ಏನು ಮಾಡಬೇಕೆಂಬದೂ ತಿಳಿಯದು; ನಮ್ಮ ಕಣ್ಣುಗಳು ನಿನ್ನನ್ನೇ ನೋಡುತ್ತವೆ ಎಂದು ಪ್ರಾರ್ಥಿಸಿದನು.",
			"13": "<sup>13</sup> ಎಲ್ಲಾ ಯೆಹೂದ್ಯರೂ ಅವರ ಹೆಂಡತಿಮಕ್ಕಳೂ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನಿಂತಿದ್ದರು.",
			"14": "<sup>14</sup> ಆಗ ಆ ಸಮೂಹದೊಳಗೆ ಆಸಾಫನ ವಂಶದವನಾದ ಲೇವಿಯನೂ ಮತ್ತನ್ಯನಿಗೆ ಹುಟ್ಟಿದ ಯೆಗೀಯೇಲನ ಮರಿಮಗನೂ ಬೆನಾಯನ ಮೊಮ್ಮಗನೂ ಜೆಕರ್ಯನ ಮಗನೂ ಆದ ಯಹಜೀಯೇಲನ ಮೇಲೆ ಯೆಹೋವನ ಆತ್ಮವು ಬಂದಿತು.",
			"15": "<sup>15</sup> ಅವನು - ಎಲ್ಲಾ ಯೆಹೂದ್ಯರೇ, ಯೆರೂಸಲೇಮಿನವರೇ, ಅರಸನಾದ ಯೆಹೋಷಾಫಾಟನೇ, ಯೆಹೋವನು ಹೇಳುವದನ್ನು ಕೇಳಿರಿ! ಈ ಮಹಾಸಮೂಹದ ನಿಮಿತ್ತವಾಗಿ ಕಳವಳಗೊಳ್ಳಬೇಡಿರಿ, ಹೆದರಬೇಡಿರಿ, ಯುದ್ಧವು ನಿಮ್ಮದಲ್ಲ, ದೇವರದೇ.",
			"16": "<sup>16</sup> ನಾಳೆ ಬೆಳಿಗ್ಗೆ ಅವರಿಗೆ ವಿರೋಧವಾಗಿ ಹೊರಡಿರಿ; ಇಗೋ ಅವರು ಹಚ್ಚೀಚ್ ಗಟ್ಟದ ಮಾರ್ಗವಾಗಿ ಬರುತ್ತಾರೆ. ಯೆರೂವೇಲ್ ಅರಣ್ಯದ ಮುಂದಿರುವ ಕಣಿವೆಯ ತುದಿಯಲ್ಲಿ ಅವರನ್ನು ಸಂಧಿಸುವಿರಿ.",
			"17": "<sup>17</sup> ಈ ಸಾರಿ ನೀವು ಯುದ್ಧಮಾಡುವದು ಅವಶ್ಯವಿಲ್ಲ. ಯೆಹೂದ್ಯರೇ, ಯೆರೂಸಲೇಮಿನವರೇ, ಸುಮ್ಮನೆ ನಿಂತುಕೊಂಡು ಯೆಹೋವನು ನಿಮಗೋಸ್ಕರ ನಡಿಸುವ ರಕ್ಷಣಾಕಾರ್ಯವನ್ನು ನೋಡಿರಿ; ಹೆದರಬೇಡಿರಿ, ಕಳವಳಗೊಳ್ಳಬೇಡಿರಿ. ನಾಳೆ ಅವರೆದುರಿಗೆ ಹೊರಡಿರಿ, ಯೆಹೋವನು ನಿಮ್ಮ ಸಂಗಡ ಇರುವನು ಎಂದು ಹೇಳಿದನು.",
			"18": "<sup>18</sup> ಆಗ ಯೆಹೋಷಾಫಾಟನು ನೆಲದ ಮಟ್ಟಿಗೂ ತಲೆಬಾಗಿಸಿದನು. ಎಲ್ಲಾ ಯೆಹೂದ್ಯರೂ ಯೆರೂಸಲೇಮಿನವರೂ ಯೆಹೋವನ ಮುಂದೆ ಅಡ್ಡ ಬಿದ್ದು ನಮಸ್ಕರಿಸಿದರು.",
			"19": "<sup>19</sup> ಆಮೇಲೆ ಲೇವಿಯರಲ್ಲಿ ಕೆಹಾತ್ಯರೂ ಕೋರಹಿಯರೂ ಎದ್ದು ಇಸ್ರಾಯೇಲ್‍ದೇವರಾದ ಯೆಹೋವನನ್ನು ಮಹಾಸ್ವರದಿಂದ ಕೀರ್ತಿಸಿದರು.",
			"20": "<sup>20</sup> ಅವರು ಮರುದಿನ ಬೆಳಿಗ್ಗೆ ಎದ್ದು ತೆಕೋವ ಅರಣ್ಯಕ್ಕೆ ಹೊರಟರು. ಅವರು ಹೊರಡುವಾಗ ಯೆಹೋಷಾಫಾಟನು ನಿಂತು ಅವರಿಗೆ - ಯೆಹೂದ್ಯರೇ, ಯೆರೂಸಲೇಮಿನವರೇ, ನನ್ನ ಮಾತನ್ನು ಕೇಳಿರಿ. ಯೆಹೋವನಲ್ಲಿ ಭರವಸವಿಡಿರಿ, ಆಗ ಸುರಕ್ಷಿತರಾಗಿರುವಿರಿ; ಆತನ ಪ್ರವಾದಿಗಳನ್ನು ನಂಬಿರಿ, ಆಗ ಸಾರ್ಥಕರಾಗುವಿರಿ ಎಂದು ಹೇಳಿದನು.",
			"21": "<sup>21</sup> ಆಮೇಲೆ ಅವನು ಜನರ ಸಮ್ಮತಿಯಿಂದ ಯೆಹೋವನಿಗೋಸ್ಕರ ಗಾಯನಮಾಡುವದಕ್ಕಾಗಿ ಕೆಲವರನ್ನು ಆರಿಸಿಕೊಂಡು ಅವರಿಗೆ - ಪರಿಶುದ್ಧತ್ವವೆಂಬ ಭೂಷಣದೊಡನೆ ಭಟರ ಮುಂದೆ ಹೋಗುತ್ತಾ - ಯೆಹೋವನಿಗೆ ಕೃತಜ್ಞತಾಸ್ತುತಿ ಮಾಡಿರಿ, ಆತನ ಕೃಪೆಯು ಶಾಶ್ವತವಾಗಿದೆ ಎಂದು ಭಜಿಸಿರಿ ಎಂಬದಾಗಿ ಆಜ್ಞಾಪಿಸಿದನು.",
			"22": "<sup>22</sup> ಅವರು ಉತ್ಸಾಹ ಧ್ವನಿಯಿಂದ ಕೀರ್ತಿಸುವದಕ್ಕೆ ಪ್ರಾರಂಭಿಸಲು ಯೆಹೋವನು ಯೆಹೂದ್ಯರಿಗೆ ವಿರೋಧವಾಗಿ ಬಂದ ಅಮ್ಮೋನಿಯರನ್ನೂ ಮೋವಾಬ್ಯರನ್ನೂ ಸೇಯೀರ್ ಪರ್ವತದವರನ್ನೂ ನಶಿಸುವದಕ್ಕೋಸ್ಕರ ಅವರಲ್ಲಿ ಹೊಂಚುಹಾಕುವವರನ್ನು ಇರಿಸಿದ್ದರಿಂದ ಅಮ್ಮೋನಿಯರೂ ಮೋವಾಬ್ಯರೂ ಸೇಯೀರ್ ಪರ್ವತದವರ ಮೇಲೆ ಬಿದ್ದು ಅವರನ್ನು ಪೂರ್ಣವಾಗಿ ಸಂಹರಿಸಿಬಿಟ್ಟರು;",
			"23": "<sup>23</sup> ಅವರನ್ನು ಮುಗಿಸಿಬಿಟ್ಟ ಮೇಲೆ ತಾವೇ ಒಬ್ಬರನ್ನೊಬ್ಬರು ಕೊಲ್ಲುವದಕ್ಕೆ ಪ್ರಾರಂಭಿಸಿದರು.",
			"24": "<sup>24</sup> ಯೆಹೂದ್ಯರು ಅರಣ್ಯದಲ್ಲಿನ ಬುರುಜಿಗೆ ಬಂದು ಆ ಸಮೂಹವಿದ್ದ ಕಡೆಗೆ ನೋಡಿದಾಗ ನೆಲದ ಮೇಲೆ ಬಿದ್ದಿರುವ ಹೆಣಗಳು ಹೊರತಾಗಿ ಜೀವದಿಂದುಳಿದವರು ಯಾರೂ ಕಾಣಿಸಲಿಲ್ಲ.",
			"25": "<sup>25</sup> ಯೆಹೋಷಾಫಾಟನೂ ಅವನ ಜನರೂ ಸುಲಿಗೆಮಾಡುವದಕ್ಕೋಸ್ಕರ ಅಲ್ಲಿಗೆ ಹೋಗಲು ಅವರಿಗೆ ದ್ರವ್ಯ ವಸ್ತ್ರ ಶ್ರೇಷ್ಠಾಯುಧ ಇವುಗಳು ರಾಶಿರಾಶಿಯಾಗಿ ಸಿಕ್ಕಿದವು. ಅವರು ಹೊರಲಾರದಷ್ಟು ಸುಲುಕೊಂಡರು. ಕೊಳ್ಳೆಯು ಬಲುಹೆಚ್ಚಾಗಿದ್ದದರಿಂದ ಅವರು ಮೂರು ದಿನಗಳವರೆಗೂ ಕೂಡಿಸುತ್ತಿದ್ದರು.",
			"26": "<sup>26</sup> ನಾಲ್ಕನೆಯ ದಿನದಲ್ಲಿ ಬೆರಾಕ ತಗ್ಗಿನಲ್ಲಿ ಕೂಡಿಬಂದರು. ಅವರು ಅಲ್ಲಿ ಯೆಹೋವನನ್ನು ಸ್ತುತಿಸಿದದರಿಂದ ಆ ಸ್ಥಳಕ್ಕೆ ಇಂದಿನವರೆಗೂ ಬೆರಾಕ ತಗ್ಗು ಎಂಬ ಹೆಸರಿದೆ.",
			"27": "<sup>27</sup> ಆಮೇಲೆ ಯೆಹೂದ್ಯರೂ ಯೆರೂಸಲೇಮಿನವರೂ ಅವರ ಮುಂಗಡೆಯಲ್ಲಿ ಯೆಹೋಷಾಫಾಟನೂ ಯೆಹೋವನು ತಮಗೆ ಶತ್ರುಗಳ ಮೇಲೆ ಜಯವನ್ನು ಅನುಗ್ರಹಿಸಿದ್ದಾನೆಂದು ಜಯಘೋಷ ಮಾಡುತ್ತಾ ಯೆರೂಸಲೇಮಿಗೆ ಹಿಂದಿರುಗಿದರು.",
			"28": "<sup>28</sup> ಅವರು ಸ್ವರಮಂಡಲ ಕಿನ್ನರಿ ತುತೂರಿ ಇವುಗಳೊಡನೆ ಯೆರೂಸಲೇಮಿನಲ್ಲಿರುವ ಯೆಹೋವನ ಆಲಯಕ್ಕೆ ಬಂದರು.",
			"29": "<sup>29</sup> ಯೆಹೋವನು ತಾನೇ ಇಸ್ರಾಯೇಲ್ಯರ ಶತ್ರುಗಳೊಡನೆ ಯುದ್ಧಮಾಡಿದನೆಂಬ ಸುದ್ದಿಯು ಅನ್ಯದೇಶಗಳ ರಾಜ್ಯಗಳವರಿಗೆ ಮುಟ್ಟಿದಾಗ ಅವರೆಲ್ಲರೂ ಬಹುಭೀತರಾದರು.",
			"30": "<sup>30</sup> ಹೀಗೆ ದೇವಾನುಗ್ರಹದಿಂದ ಸುತ್ತಣ ವೈರಿಗಳ ಭಯತಪ್ಪಿ ಯೆಹೋಷಾಫಾಟನ ರಾಜ್ಯಕ್ಕೆ ಸಮಾಧಾನವುಂಟಾಯಿತು.",
			"31": "<sup>31</sup> ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಯೆಹೋಷಾಫಾಟನು ಪಟ್ಟಕ್ಕೆ ಬಂದಾಗ ಮೂವತ್ತೈದು ವರುಷದವನಾಗಿದ್ದನು; ಇವನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಇಪ್ಪತ್ತೈದು ವರುಷ ಆಳಿದನು. ಶಿಲ್ಹಿಯ ಮಗಳಾದ ಅಜೂಬಳೆಂಬಾಕೆಯು ಇವನ ತಾಯಿ.",
			"32": "<sup>32</sup> ಇವನು ತಪ್ಪದೆ ತಂದೆಯಾದ ಆಸನ ಮಾರ್ಗದಲ್ಲಿ ನಡೆಯುತ್ತಾ ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ಒಳ್ಳೆಯವನಾಗಿದ್ದರೂ",
			"33": "<sup>33</sup> ಪೂಜಾಸ್ಥಳಗಳನ್ನು ಹಾಳುಮಾಡಲಿಲ್ಲ. ಜನರು ಇನ್ನೂ ತಮ್ಮ ಪಿತೃಗಳ ದೇವರ ಮೇಲೆ ಮನಸ್ಸಿಟ್ಟಿರಲಿಲ್ಲ.",
			"34": "<sup>34</sup> ಯೆಹೋಷಾಫಾಟನ ಉಳಿದ ಪೂರ್ವೋತ್ತರ ಚರಿತ್ರೆಯು ಇಸ್ರಾಯೇಲ್ ರಾಜರ ಗ್ರಂಥದಲ್ಲಿ ಅಡಕವಾಗಿರುವ ಹನಾನೀಯ ಮಗನಾದ ಯೇಹುವಿನ ವೃತ್ತಾಂತದಲ್ಲಿ ಬರೆದಿರುತ್ತದೆ.",
			"35": "<sup>35</sup> ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಯೆಹೋಷಾಫಾಟನು ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನೂ ಬಹುದುರಾಚಾರಿಯೂ ಆದ ಅಹಜ್ಯನೊಡನೆ ಒಡಂಬಡಿಕೆಮಾಡಿಕೊಂಡನು.",
			"36": "<sup>36</sup> ಅವರು ತಾರ್ಷೀಷಿಗೆ ಹೋಗುವದಕ್ಕಾಗಿ ಹಡಗುಗಳನ್ನು ಕಟ್ಟಬೇಕೆಂದು ಒಡಂಬಡಿಕೆಮಾಡಿಕೊಂಡು ಅವುಗಳನ್ನು ಎಚ್ಯೋನ್ಗೆಬೆರಿನಲ್ಲಿ ಕಟ್ಟಿಸಿದರು.",
			"37": "<sup>37</sup> ಆಗ ಮಾರೇಷಾ ಊರಿನ ದೋದಾವಾಹುವಿನ ಮಗನಾದ ಎಲೀಯೆಜರನು ಯೆಹೋಷಾಫಾಟನಿಗೆ ವಿರೋಧವಾಗಿ - ನೀನು ಅಹಜ್ಯನೊಡನೆ ಒಡಂಬಡಿಕೆ ಮಾಡಿಕೊಂಡದ್ದರಿಂದ ಯೆಹೋವನು ನಿನ್ನ ಕೆಲಸವನ್ನು ಹಾಳುಮಾಡುವನು ಎಂದು ಪ್ರವಾದಿಸಿದನು. ಆ ಹಡಗುಗಳು ಒಡೆದುಹೋದದ್ದರಿಂದ ತಾರ್ಷೀಷಿಗೆ ಹೊರಡಲೇ ಇಲ್ಲ."
		},
		"21": {
			"1": "<sup>1</sup> ಯೆಹೋಷಾಫಾಟನು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಿ ದಾವೀದನಗರದಲ್ಲಿ ಪೂರ್ವಿಕರ ಶ್ಮಶಾನಭೂಮಿಯಲ್ಲಿ ಸಮಾಧಿಹೊಂದಿದ ಮೇಲೆ ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ಯೆಹೋರಾಮನು ಅರಸನಾದನು.",
			"2": "<sup>2</sup> ಇವನಿಗೆ ಅಜರ್ಯ, ಯೆಹೀಯೇಲ್, ಜೆಕರ್ಯ, ಅಜರ್ಯ, ಮೀಕಾಯೇಲ್, ಶೆಫಟ್ಯ ಎಂಬ ತಮ್ಮಂದಿರಿದ್ದರು. ಇವರೆಲ್ಲರೂ ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಯೆಹೋಷಾಫಾಟನ ಮಕ್ಕಳು.",
			"3": "<sup>3</sup> ಇವರ ತಂದೆಯು ಇವರಿಗೆ ಯೆಹೂದದ ಕೋಟೆಕೊತ್ತಲಗಳುಳ್ಳ ಪಟ್ಟಣಗಳನ್ನಲ್ಲದೆ ಬೆಳ್ಳಿ ಬಂಗಾರ ಶ್ರೇಷ್ಠವಸ್ತು ಇತ್ಯಾದಿ ವಿಶೇಷದಾನಗಳನ್ನು ಕೊಟ್ಟನು; ಆದರೆ ಯೆಹೋರಾಮನು ಹಿರಿಯ ಮಗನಾಗಿದ್ದದರಿಂದ ಅವನಿಗೆ ರಾಜ್ಯವನ್ನು ಕೊಟ್ಟನು.",
			"4": "<sup>4</sup> ಯೆಹೋರಾಮನು ತನ್ನ ತಂದೆಯ ರಾಜ್ಯವನ್ನು ಪಡಕೊಂಡು ತನ್ನ ಆಳಿಕೆಯನ್ನು ಸ್ಥಿರಗೊಳಿಸಿಕೊಂಡ ಮೇಲೆ ತನ್ನ ಎಲ್ಲಾ ಸಹೋದರರನ್ನೂ ಕೆಲವು ಮಂದಿ ಇಸ್ರಾಯೇಲ್ ಪ್ರಧಾನರನ್ನೂ ಕತ್ತಿಯಿಂದ ಸಂಹರಿಸಿದನು.",
			"5": "<sup>5</sup> ಇವನು ಪಟ್ಟಕ್ಕೆ ಬಂದಾಗ ಮೂವತ್ತೆರಡು ವರುಷದವನಾಗಿದ್ದನು;",
			"6": "<sup>6</sup> ಇವನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಎಂಟು ವರುಷ ಆಳಿದನು. ಇವನು ಅಹಾಬನ ಮಗಳನ್ನು ಮದುವೆಮಾಡಿಕೊಂಡದ್ದರಿಂದ ಅಹಾಬನ ಕುಟುಂಬದವರಾದ ಇಸ್ರಾಯೇಲ್‍ರಾಜರ ಮಾರ್ಗವನ್ನು ಅನುಸರಿಸಿ ಯೆಹೋವನ ಚಿತ್ತಕ್ಕೆ ವಿರುದ್ಧವಾಗಿ ನಡೆದನು.",
			"7": "<sup>7</sup> ಆದರೂ ಯೆಹೋವನು ದಾವೀದನಿಗೆ - ನಿನ್ನ ಮತ್ತು ನಿನ್ನ ಸಂತಾನದವರ ದೀಪವನ್ನು ಎಂದೂ ನಂದಿಸುವದಿಲ್ಲ ಎಂದು ಪ್ರಮಾಣಮಾಡಿ ಹೇಳಿದ್ದದರಿಂದ ದಾವೀದನ ಮನೆತನದವರನ್ನು ನಾಶಮಾಡುವದಕ್ಕೆ ಇಷ್ಟಪಡಲಿಲ್ಲ.",
			"8": "<sup>8</sup> ಯೆಹೋರಾಮನ ಕಾಲದಲ್ಲಿ ಎದೋಮ್ಯರು ಯೆಹೂದ್ಯರಿಗೆ ವಿರೋಧವಾಗಿ ತಿರುಗಿಬಿದ್ದು ಸ್ವತಂತ್ರರಾಗಿದ್ದು ತಾವೇ ತಮಗೊಬ್ಬ ಅರಸನನ್ನು ನೇಮಿಸಿಕೊಂಡರು.",
			"9": "<sup>9</sup> ಆಗ ಯೆಹೋರಾಮನು ತನ್ನ ಸರದಾರರನ್ನೂ ಎಲ್ಲಾ ರಥಬಲವನ್ನೂ ಕೂಡಿಸಿಕೊಂಡು [ಯೊರ್ದನ್ ಹೊಳೆಯನ್ನು] ದಾಟಿದನು. ಎದೋಮ್ಯರು ಬಂದು ಅವನನ್ನು ಸುತ್ತಿಕೊಳ್ಳಲು ಅವನು ರಾತ್ರಿಯಲ್ಲೇ ದಂಡೆತ್ತಿ ಹೋಗಿ ಅವರನ್ನೂ ಅವರ ರಥಬಲದ ಅಧಿಪತಿಗಳನ್ನೂ ಸೋಲಿಸಿ ಪಾರಾದನು.",
			"10": "<sup>10</sup> ಯೆಹೂದ್ಯರಿಗೆ ವಿರೋಧವಾಗಿ ತಿರುಗಿಬಿದ್ದ ಎದೋಮ್ಯರು ಅಂದಿನಿಂದ ಇಂದಿನವರೆಗೂ ಸ್ವತಂತ್ರರಾಗಿದ್ದಾರೆ. ಆ ಕಾಲದಲ್ಲಿಯೇ ಲಿಬ್ನದವರೂ ಅವನ ಕೈಯಿಂದ ತಪ್ಪಿಸಿಕೊಂಡು ಸ್ವತಂತ್ರರಾದರು. ಯೆಹೋರಾಮನು ತನ್ನ ಪಿತೃಗಳ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಬಿಟ್ಟದ್ದೇ ಇದಕ್ಕೆ ಕಾರಣ.",
			"11": "<sup>11</sup> ಮತ್ತು ಅವನು ಯೆಹೂದದ ಗುಡ್ಡಗಳಲ್ಲಿ ಪೂಜಾಸ್ಥಳಗಳನ್ನು ಏರ್ಪಡಿಸಿ ಯೆರೂಸಲೇಮಿನವರನ್ನು ದೇವದ್ರೋಹಮಾಡುವಂತೆ ಪ್ರೇರಿಸಿ ಯೆಹೂದ್ಯರನ್ನು ಸನ್ಮಾರ್ಗದಿಂದ ತಪ್ಪಿಸಿದನು.",
			"12": "<sup>12</sup> ಹೀಗಿರಲು ಪ್ರವಾದಿಯಾದ ಎಲೀಯನು ಕಾಗದದ ಮೂಲಕವಾಗಿ ಅವನಿಗೆ - ನಿನ್ನ ಪೂರ್ವಿಕನಾದ ದಾವೀದನ ದೇವರಾಗಿರುವ ಯೆಹೋವನ ಮಾತನ್ನು ಕೇಳು. ನೀನು ನಿನ್ನ ತಂದೆಯಾದ ಯೆಹೋಷಾಫಾಟನ ಮಾರ್ಗದಲ್ಲಿಯೂ ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಆಸನ ಮಾರ್ಗದಲ್ಲಿಯೂ ನಡೆಯಲಿಲ್ಲ;",
			"13": "<sup>13</sup> ಇಸ್ರಾಯೇಲ್ ರಾಜರ ಮಾರ್ಗದಲ್ಲಿ ನಡೆದು ಅಹಾಬನ ಮನೆಯವರಲ್ಲಿ ಪ್ರಬಲವಾಗಿದ್ದ ದೇವದ್ರೋಹಮಾಡುವಂತೆ ಯೆಹೂದ್ಯರನ್ನೂ ಯೆರೂಸಲೇಮಿನವರನ್ನೂ ಪ್ರೇರಿಸಿದಿ; ಮತ್ತು ನಿನ್ನ ತಂದೆಯ ಕುಟುಂಬಕ್ಕೆ ಸೇರಿ ನಿನಗಿಂತ ಉತ್ತಮರೂ ನಿನ್ನ ಸಹೋದರರೂ ಆಗಿದ್ದವರನ್ನು ವಧಿಸಿದಿ;",
			"14": "<sup>14</sup> ಆದದರಿಂದ ಯೆಹೋವನು ನಿನ್ನ ಪ್ರಜೆಯನ್ನೂ ಹೆಂಡರುಮಕ್ಕಳನ್ನೂ ಸರ್ವಸ್ವವನ್ನೂ ಮಹಾಪತ್ತಿಗೆ ಗುರಿಮಾಡುವನು.",
			"15": "<sup>15</sup> ನಿನಗಾದರೋ ಕರುಳುಬೇನೆಯೆಂಬ ಕಠಿನರೋಗವು ಬಂದು ಬಹುದಿನಗಳವರೆಗೂ ಇದ್ದು ಕಡೆಯಲ್ಲಿ ಅದರಿಂದ ನಿನ್ನ ಕರುಳುಗಳು ಹೊರಗೆ ಬೀಳುವವು ಎಂದು ತಿಳಿಸಿದನು.",
			"16": "<sup>16</sup> ಯೆಹೋವನು ಫಿಲಿಷ್ಟಿಯರನ್ನೂ ಕೂಷ್ಯರ ನೆರೆಯವರಾದ ಅರಬಿಯರನ್ನೂ ಯೆಹೋರಾಮನಿಗೆ ವಿರೋಧಿಗಳಾಗುವಂತೆ ಎಬ್ಬಿಸಿದ್ದರಿಂದ",
			"17": "<sup>17</sup> ಅವರು ಯೆಹೂದದ ಮೇಲೆ ಯುದ್ಧಕ್ಕೆ ಬಂದು ದೇಶದೊಳಗೆ ನುಗ್ಗಿ ಅರಮನೆಯಲ್ಲಿ ಸಿಕ್ಕಿದ ಎಲ್ಲಾ ಸೊತ್ತನ್ನೂ ಅರಸನ ಹೆಂಡರುಮಕ್ಕಳನ್ನೂ ಸೆರೆಯೊಯ್ದರು. ಅವನಿಗೆ ಕಿರಿಯ ಮಗನಾದ ಯೆಹೋವಾಹಾಜನ ಹೊರತು ಮಕ್ಕಳೇ ಉಳಿಯಲಿಲ್ಲ.",
			"18": "<sup>18</sup> ಇಷ್ಟೆಲ್ಲಾ ಆದ ಮೇಲೆ ಯೆಹೋವನು ಅವನ ಕರುಳುಗಳಲ್ಲಿ ವಾಸಿಯಾಗಲಾರದ ರೋಗವನ್ನು ಉಂಟುಮಾಡಿದನು.",
			"19": "<sup>19</sup> ಕಾಲಾಂತರದಲ್ಲಿ ಅಂದರೆ ಸುಮಾರು ಎರಡು ವರುಷಗಳಾದ ಮೇಲೆ ಆ ರೋಗದಿಂದ ಕರುಳುಗಳು ಹೊರಬಿದ್ದವು. ಅವನು ಕಡುಬೇನೆಯಿಂದ ಸತ್ತನು. ಅವನ ಪ್ರಜೆಗಳು ಅವನ ಪೂರ್ವಿಕರಿಗೋಸ್ಕರ ಧೂಪಹಾಕಿದ ಪ್ರಕಾರ ಅವನಿಗೆ ಹಾಕಲಿಲ್ಲ.",
			"20": "<sup>20</sup> ಅವನು ಪಟ್ಟಕ್ಕೆ ಬಂದಾಗ ಮೂವತ್ತೆರಡು ವರುಷದವನಾಗಿದ್ದನು; ಅವನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಎಂಟು ವರುಷ ಆಳಿದನು. ಅವನು ತೀರಿಹೋದಾಗ ಅವನನ್ನು ಯಾರೂ ಹಂಬಲಿಸಲಿಲ್ಲ. ಅವನ ಶವವನ್ನು ದಾವೀದನಗರದಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು; ಆದರೆ ರಾಜಶ್ಮಶಾನದಲ್ಲಿ ಅಲ್ಲ."
		},
		"22": {
			"1": "<sup>1</sup> ಯೆರೂಸಲೇಮಿನವರು ಯೆಹೋರಾಮನಿಗೆ ಬದಲಾಗಿ ಅವನ ಕಿರಿಯ ಮಗನಾದ ಅಹಜ್ಯನನ್ನು ಅರಸನನ್ನಾಗಿ ಮಾಡಿದರು. ಅರಬಿಯರೊಡನೆ [ಯೆಹೋರಾಮನ] ಪಾಳೆಯಕ್ಕೆ ಬಂದ ಕೊಳ್ಳೆಗಾರರು ಅವನ ಹಿರಿಯ ಮಕ್ಕಳನ್ನೆಲ್ಲಾ ಕೊಂದುಬಿಟ್ಟಿದ್ದರು. ಆದದರಿಂದ ಯೆಹೂದದ ಅರಸನಾದ ಯೆಹೋರಾಮನ ಮಗ ಅಹಜ್ಯನು ಅರಸನಾದನು.",
			"2": "<sup>2</sup> ಇವನು ಪಟ್ಟಕ್ಕೆ ಬಂದಾಗ ನಾಲ್ವತ್ತೆರಡು ವರುಷದವನಾಗಿದ್ದನು; ಇವನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಒಂದು ವರುಷ ಆಳಿದನು; ಒವ್ರಿುಯ ಮೊಮ್ಮಗಳಾದ ಅತಲ್ಯ ಎಂಬಾಕೆಯು ಇವನ ತಾಯಿ.",
			"3": "<sup>3</sup> ತನ್ನ ತಾಯಿಯ ದುರ್ಬೋಧೆಯನ್ನು ಆಲೈಸಿ ಇವನೂ ದುರಾಚಾರಿಯಾಗಿ ಅಹಾಬನ ಮನೆಯವರ ಮಾರ್ಗದಲ್ಲೇ ನಡೆದನು.",
			"4": "<sup>4</sup> ಇವನ ತಂದೆ ತೀರಿಹೋದ ಮೇಲೆ ಅಹಾಬನ ಕುಟುಂಬದವರೇ ಇವನಿಗೆ ಸಲಹೆಕೊಟ್ಟು ಇವನನ್ನು ಕೆಡಿಸುತ್ತಾ ಬಂದದರಿಂದ ಇವನು ಯೆಹೋವನಿಗೆ ವಿರುದ್ಧವಾಗಿ ನಡೆದನು.",
			"5": "<sup>5</sup> ಅವರ ಪ್ರೇರಣೆಯಿಂದ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನೂ ಅಹಾಬನ ಮಗನೂ ಆದ ಯೋರಾಮನ ಜೊತೆಯಲ್ಲಿ ಅರಾಮ್ಯರ ಅರಸನಾದ ಹಜಾಯೇಲನಿಗೆ ವಿರುದ್ಧವಾಗಿ ಯುದ್ಧಮಾಡುವದಕ್ಕೆ ರಾಮೋತ್‍ಗಿಲ್ಯಾದಿಗೆ ಹೋದನು. ಅರಾಮ್ಯರು ಯೋರಾಮನನ್ನು ಯುದ್ಧದಲ್ಲಿ ಗಾಯಪಡಿಸಲು ಅವನು ಆ ಗಾಯಗಳನ್ನು ಮಾಯಿಸಿಕೊಳ್ಳುವದಕ್ಕಾಗಿ ಇಜ್ರೇಲಿಗೆ ಬಂದನು.",
			"6": "<sup>6</sup> ಅವನು ಅಸ್ವಸ್ಥನಾಗಿದ್ದದರಿಂದ ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಯೆಹೋರಾಮನಿಗೆ ಮಗನಾಗಿರುವ ಅಹಜ್ಯನು ಅವನನ್ನು ನೋಡುವದಕ್ಕೋಸ್ಕರ ಅಲ್ಲಿಗೆ ಹೋದನು.",
			"7": "<sup>7</sup> ಅಹಜ್ಯನು ಯೋರಾಮನ ಬಳಿಗೆ ಹೋಗಿ ನಾಶವಾದದ್ದು ದೈವಯೋಗದಿಂದಲೇ. ಅವನು ಅಲ್ಲಿ ಮುಟ್ಟಿದ ಕೂಡಲೆ ಯೋರಾಮನ ಜೊತೆಯಲ್ಲಿ ನಿಂಷಿಯ ಮಗನಾದ ಯೇಹುವನ್ನು ಎದುರುಗೊಳ್ಳುವದಕ್ಕೆ ಹೋದನು. ಯೆಹೋವನು ಅಹಾಬನ ಮನೆಯನ್ನು ನಿರ್ನಾಮಮಾಡುವದಕ್ಕೋಸ್ಕರ ಯೇಹುವಿಗೆ ರಾಜ್ಯಾಭಿಷೇಕ ಮಾಡಿಸಿದ್ದನು.",
			"8": "<sup>8</sup> ಯೇಹುವು ಅಹಾಬನ ಮನೆಯವರ ಮೇಲೆ ಆ ನಿರ್ಣಯವನ್ನು ನೆರವೇರಿಸುತ್ತಿರುವಾಗ ತನಗೆ ಸಿಕ್ಕಿದ ಯೆಹೂದ್ಯ ಪ್ರಧಾನರನ್ನೂ ಅಹಜ್ಯನಿಗೆ ಸೇವೆಮಾಡುತ್ತಿದ್ದ ಅವನ ಅಣ್ಣತಮ್ಮಂದಿರ ಮಕ್ಕಳನ್ನೂ ಕೊಲ್ಲಿಸಿದನು.",
			"9": "<sup>9</sup> ಆಮೇಲೆ ಅಹಜ್ಯನನ್ನು ಹುಡುಕಿಸಿದನು; ಅವನು ಸಮಾರ್ಯದಲ್ಲಿ ಅಡಗಿಕೊಂಡಿದ್ದನು. ಜನರು ಅವನನ್ನು ಯೇಹುವಿನ ಬಳಿಗೆ ಹಿಡಿದು ತಂದು ವಧಿಸಿ - ಇವನು ಯೆಹೋವನ ಯಥಾರ್ಥಭಕ್ತನಾದ ಯೆಹೋಷಾಫಾಟನ ಮೊಮ್ಮಗನಾಗಿರುತ್ತಾನಲ್ಲವೇ ಅಂದುಕೊಂಡು ಅವನನ್ನು ಸಮಾಧಿಮಾಡಿದರು. ರಾಜ್ಯಾಧಿಕಾರವನ್ನು ವಹಿಸಿಕೊಳ್ಳುವದಕ್ಕೆ ಅಹಜ್ಯನ ಮನೆಯವರಲ್ಲಿ ಸಮರ್ಥರಾರೂ ಉಳಿಯಲಿಲ್ಲ.",
			"10": "<sup>10</sup> ಅಹಜ್ಯನು ಮರಣಹೊಂದಿದನೆಂಬದನ್ನು ಅವನ ತಾಯಿಯಾದ ಅತಲ್ಯಳು ಕೇಳಿದ ಕೂಡಲೆ ಯೆಹೂದ ರಾಜಸಂತಾನದವರನ್ನೆಲ್ಲಾ ಸಂಹರಿಸಿಬಿಟ್ಟಳು.",
			"11": "<sup>11</sup> ಆದರೆ ರಾಜಪುತ್ರಿಯಾದ ಯೆಹೋಷಬತ್ ಎಂಬಾಕೆಯು ಹತರಾಗುವದಕ್ಕಿದ್ದ ರಾಜಪುತ್ರರ ಮಧ್ಯದಿಂದ ಅಹಜ್ಯನ ಮಗನಾದ ಯೆಹೋವಾಷನನ್ನು ಯಾರಿಗೂ ತಿಳಿಯದಂತೆ ಅವನ ದಾದಿಯೊಡನೆ ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ಮಲಗುವ ಕೋಣೆಯಲ್ಲಿ ಅಡಗಿಸಿಟ್ಟಳು. ಅರಸನಾದ ಯೆಹೋರಾಮನ ಮಗಳೂ ಅಹಜ್ಯನ ಸಹೋದರಿಯೂ ಯಾಜಕನಾದ ಯೆಹೋಯಾದನ ಹೆಂಡತಿಯೂ ಆಗಿದ್ದ ಯೆಹೋಷಬತಳು ಹೀಗೆ ಅವನನ್ನು ಅತಲ್ಯಳಿಂದ ಹತನಾಗದಂತೆ ರಕ್ಷಿಸಿದಳು.",
			"12": "<sup>12</sup> ಅವನು ಆರು ವರುಷಗಳವರೆಗೂ ಅವರೊಡನೆ ಗುಪ್ತವಾಗಿ ದೇವಾಲಯದಲ್ಲಿದ್ದನು. ಆಗ ಅತಲ್ಯಳೇ ದೇಶವನ್ನಾಳುತ್ತಿದ್ದಳು."
		},
		"23": {
			"1": "<sup>1</sup> ಏಳನೆಯ ವರುಷದಲ್ಲಿ ಯೆಹೋಯಾದನು ಧೈರ್ಯತಂದುಕೊಂಡು ಯೆರೋಹಾಮನ ಮಗನಾದ ಅಜರ್ಯ, ಯೆಹೋಹಾನಾನನ ಮಗನಾದ ಇಷ್ಮಾಯೇಲ್, ಓಬೇದನ ಮಗನಾದ ಅಜರ್ಯ, ಅದಾಯನ ಮಗನಾದ ಮಾಸೇಯ, ಜಿಕ್ರಿಯ ಮಗನಾದ ಎಲೀಷಾಫಾಟ್ ಎಂಬ ಶತಾಧಿಪತಿಗಳೊಡನೆ ಒಡಂಬಡಿಕೆಯನ್ನು ಮಾಡಿಕೊಂಡನು.",
			"2": "<sup>2</sup> ಇವರು ಯೆಹೂದದಲ್ಲೆಲ್ಲಾ ಸಂಚರಿಸಿ ಎಲ್ಲಾ ಪಟ್ಟಣಗಳಿಂದ ಲೇವಿಯರನ್ನೂ ಇಸ್ರಾಯೇಲ್ಯರ ಗೋತ್ರಪ್ರಧಾನರನ್ನೂ ಕೂಡಿಸಲು ಅವರು ಯೆರೂಸಲೇಮಿಗೆ ಬಂದರು.",
			"3": "<sup>3</sup> ಕೂಡಿಬಂದವರೆಲ್ಲರೂ ದೇವಾಲಯದಲ್ಲಿ ಅರಸನೊಂದಿಗೆ ಒಡಂಬಡಿಕೆ ಮಾಡಿಕೊಂಡರು. ಯೆಹೋಯಾದನು ಅವರಿಗೆ - ಇಗೋ, ರಾಜಪುತ್ರನು! ಯೆಹೋವನು ದಾವೀದನ ಸಂತಾನದವರಿಗೆ ಮಾಡಿದ ವಾಗ್ದಾನಕ್ಕನುಸಾರವಾಗಿ ಅರಸನಾಗತಕ್ಕವನು ಇವನೇ.",
			"4": "<sup>4</sup> ಈಗ ನೀವು ಮಾಡತಕ್ಕದ್ದೇನಂದರೆ - ಸಬ್ಬತ್‍ದಿವಸ ದೇವಾಲಯಕ್ಕೆ ಬರತಕ್ಕ ಲೇವಿಯರಾಗಲಿ ಯಾಜಕರಾಗಲಿ ಆದ ನಿಮ್ಮಲ್ಲಿ ಮೂರರಲ್ಲೊಂದು ಭಾಗವು ದೇವಸ್ಥಾನದ ದ್ವಾರಪಾಲಕರಾಗಿ ನಿಲ್ಲಬೇಕು.",
			"5": "<sup>5</sup> ಇನ್ನೊಂದು ಭಾಗವು ಅರಮನೆಯನ್ನೂ ಮತ್ತೊಂದು ಭಾಗವು ಯೆಸೋದ್‍ಬಾಗಲನ್ನೂ ಕಾಯಬೇಕು. ಇತರ ಜನರೆಲ್ಲರೂ ಯೆಹೋವನ ಆಲಯದ ಪ್ರಾಕಾರದಲ್ಲಿರಬೇಕು.",
			"6": "<sup>6</sup> ಯಾಜಕರೂ ಸೇವೆಯಲ್ಲಿರುವ ಲೇವಿಯರೂ ಹೊರತಾಗಿ ಯಾರೂ ಯೆಹೋವನ ಆಲಯದೊಳಗೆ ಬರಬಾರದು. ಇವರು ಪರಿಶುದ್ಧರಾಗಿರುವದರಿಂದ ಬರಬಹುದು. ಉಳಿದ ಜನರೆಲ್ಲರೂ ಯೆಹೋವನ ಆಜ್ಞಾನುಸಾರವಾಗಿ ಹೊರಗಿರಬೇಕು.",
			"7": "<sup>7</sup> ಲೇವಿಯರು ಕೈಯಲ್ಲಿ ಆಯುಧಗಳನ್ನು ಹಿಡಿದುಕೊಂಡು ಅರಸನ ಸುತ್ತಲೂ ನಿಂತು ಆಲಯದೊಳಕ್ಕೆ ನುಗ್ಗುವವರನ್ನು ಸಂಹರಿಸಬೇಕು. ಅರಸನು ಹೋಗಿ ಬರುವಾಗೆಲ್ಲಾ ಅವರು ಅವನ ಜೊತೆಯಲ್ಲೇ ಇರಬೇಕು ಎಂದು ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ಲೇವಿಯರೂ ಎಲ್ಲಾ ಯೆಹೂದ್ಯರೂ ಯಾಜಕನಾದ ಯೆಹೋಯಾದನ ಆಜ್ಞೆಯಂತೆ ನಡೆದುಕೊಂಡರು. ಅವನು [ಸಬ್ಬತ್ ದಿವಸದಲ್ಲಿ ಸೇವಾ ವಿಮುಕ್ತರಾದ] ವರ್ಗಗಳವರನ್ನು ಕಳುಹಿಸಿಬಿಡಲಿಲ್ಲ; ಆದದರಿಂದ ಪ್ರತಿಯೊಬ್ಬನು ಸಬ್ಬತ್‍ದಿವಸ ಮನೆಗೆ ಹೋಗಬೇಕಾಗಿದ್ದ ಮತ್ತು ಬರಬೇಕಾಗಿದ್ದ ತನ್ನ ಕೈಕೆಳಗಿನವರನ್ನು ಕೂಡಿಸಿಕೊಂಡನು.",
			"9": "<sup>9</sup> ದೇವಾಲಯದಲ್ಲಿಟ್ಟಿದ್ದ ಅರಸನಾದ ದಾವೀದನ ಬರ್ಜಿ ಗುರಾಣಿ ಖೇಡ್ಯಗಳೇ ಮುಂತಾದ ಆಯುಧಗಳನ್ನು ಯಾಜಕನಾದ ಯೆಹೋಯಾದನು ಶತಾಧಿಪತಿಗಳಿಗೆ ಕೊಟ್ಟು",
			"10": "<sup>10</sup> ಆಯುಧಪಾಣಿಗಳಾದ ಎಲ್ಲಾ ಜನರನ್ನು ಅರಸನ ಸುತ್ತಲೂ ದೇವಾಲಯದ ದಕ್ಷಿಣ ದಿಕ್ಕಿನ ಮೂಲೆಯಿಂದ ಯಜ್ಞವೇದಿಯವರೆಗೂ ಅಲ್ಲಿಂದ ಉತ್ತರದಿಕ್ಕಿನ ಮೂಲೆಯವರೆಗೂ ಸಾಲಾಗಿ ನಿಲ್ಲಿಸಿದನು.",
			"11": "<sup>11</sup> ಆಮೇಲೆ ಅವನೂ ಅವನ ಮಕ್ಕಳೂ ರಾಜಪುತ್ರನನ್ನು ಹೊರಗೆ ಕರಕೊಂಡು ಬಂದು ಅವನ ತಲೆಯ ಮೇಲೆ ಕಿರೀಟವನ್ನಿಟ್ಟು ಕೈಯಲ್ಲಿ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಕೊಟ್ಟು ಅವನಿಗೆ ರಾಜ್ಯಾಭಿಷೇಕಮಾಡಿದರು. ಕೂಡಲೆ ಜನರು - ಅರಸನು ಚಿರಂಜೀವಿಯಾಗಿರಲಿ ಎಂದು ಹರಸಿದರು.",
			"12": "<sup>12</sup> ಜನರು ಅತ್ತಿತ್ತ ಓಡಾಡುತ್ತಾ ಅರಸನನ್ನು ಹರಸುವ ಗದ್ದಲವನ್ನು ಅತಲ್ಯಳು ಕೇಳಿ ಅವರು ಕೂಡಿದ್ದ ಯೆಹೋವನ ಆಲಯಕ್ಕೆ ಬಂದಳು.",
			"13": "<sup>13</sup> ಅಲ್ಲಿ ಅರಸನು ಬಾಗಲಿನ ಬಳಿಯಲ್ಲಿರುವ ರಾಜಸ್ತಂಭದ ಹತ್ತಿರ ನಿಂತಿದ್ದನು. ಅಧಿಪತಿಗಳೂ ತುತೂರಿಗಳನ್ನು ಊದುವವರೂ ಅರಸನ ಹತ್ತಿರ ಇದ್ದರು; ಸಾಧಾರಣ ಜನರೆಲ್ಲರೂ ಸಂತೋಷದಿಂದ ಕೊಂಬೂದುತ್ತಿದ್ದರು. ಗಾಯಕರು ವಾದ್ಯಗಳನ್ನು ಬಾರಿಸುತ್ತಾ ಜನರ ಕೀರ್ತನೆಗಳನ್ನು ನುಡಿಸುತ್ತಿದ್ದರು. ಇದನ್ನು ಕಂಡಕೂಡಲೆ ಆಕೆಯು ಬಟ್ಟೆಗಳನ್ನು ಹರಿದುಕೊಂಡು - ದ್ರೋಹ, ದ್ರೋಹ ಎಂದು ಕೂಗಿದಳು.",
			"14": "<sup>14</sup> ಆಗ ಯಾಜಕನಾದ ಯೆಹೋಯಾದನು ಸೇನಾನಿಗಳಾದ ಶತಾಧಿಪತಿಗಳನ್ನು ಹೊರಗೆ ಕರಕೊಂಡು ಹೋಗಿ ಅವರಿಗೆ - ಈಕೆಯನ್ನು ಯೆಹೋವನ ಆಲಯದಲ್ಲಿ ಕೊಲ್ಲಬೇಡಿರಿ; ಎರಡು ಸಾಲು ಸಿಪಾಯಿಗಳು ಈಕೆಯನ್ನು ನಡುವೆ ಸಾಗಿಸಿಕೊಂಡು ಹೊರಗೆ ತಳ್ಳಲಿ; ಮತ್ತು ಈಕೆಯನ್ನು ಹಿಂಬಾಲಿಸುವಂಥವರನ್ನು ಕತ್ತಿಯಿಂದ ಕೊಲ್ಲಿರಿ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"15": "<sup>15</sup> ಅವರು ಆಕೆಯನ್ನು ಹಿಡಿದು ಅರಮನೆಗೆ ಹೋಗುವ ಕುದುರೆಬಾಗಲಿನ ತನಕ ಒಯ್ದು ಅಲ್ಲಿ ಕೊಂದರು.",
			"16": "<sup>16</sup> ಅನಂತರ ಯೆಹೋಯಾದನು ಎಲ್ಲಾ ಜನರನ್ನೂ ಅರಸನನ್ನೂ ಪ್ರೇರಿಸಿ ತಾವೆಲ್ಲರೂ ಯೆಹೋವನ ಪ್ರಜೆಗಳಾಗಿರುವೆವೆಂದು ಅವರೊಡನೆ ಪ್ರಮಾಣಮಾಡಿದನು.",
			"17": "<sup>17</sup> ತರುವಾಯ ಜನರೆಲ್ಲರೂ ಬಾಳ್‍ದೇವಸ್ಥಾನಕ್ಕೆ ಹೋಗಿ ಪೂಜಾರಿಯಾದ ಮತ್ತಾನನನ್ನು ಯಜ್ಞವೇದಿಗಳ ಮುಂದೆಯೇ ಕೊಂದು ದೇವಸ್ಥಾನವನ್ನೂ ಅದರಲ್ಲಿದ್ದ ವೇದಿವಿಗ್ರಹಗಳನ್ನೂ ಹಾಳುಮಾಡಿಬಿಟ್ಟರು.",
			"18": "<sup>18</sup> ಯೆಹೋಯಾದನು ಲೇವಿಯರಾದ ಯಾಜಕರ ಕೈಕೆಳಗೆ ಯೆಹೋವನ ಆಲಯದ ಕಾವಲುಗಾರರನ್ನು ನೇಮಿಸಿದನು. ದಾವೀದನು ಆ ಯಾಜಕರನ್ನು ಯೆಹೋವನ ಆಲಯದ ಸೇವೆಗೋಸ್ಕರ ವರ್ಗವರ್ಗಗಳಾಗಿ ವಿಭಾಗಿಸಿದ್ದನು. ದಾವೀದನು ನೇಮಿಸಿದ ಉತ್ಸಾಹ ಗಾಯನದೊಡನೆ ಮೋಶೆಯ ಧರ್ಮಶಾಸ್ತ್ರಾನುಸಾರ ಯೆಹೋವನಿಗೆ ಸರ್ವಾಂಗಹೋಮಸಮರ್ಪಣೆ ಮಾಡುವದೇ ಅವರ ಕರ್ತವ್ಯ.",
			"19": "<sup>19</sup> ಯಾವ ವಿಷಯದಲ್ಲಾದರೂ ಅಶುದ್ಧನಾದವನು ಯೆಹೋವನ ಆಲಯದಲ್ಲಿ ಸೇರದಂತೆ ಯೆಹೋಯಾದನು ಆಯಾ ಬಾಗಲುಗಳಲ್ಲಿ ದ್ವಾರಪಾಲಕರನ್ನಿರಿಸಿದನು.",
			"20": "<sup>20</sup> ಆಮೇಲೆ ಅವನು ಶತಾಧಿಪತಿಗಳು ಶ್ರೀಮಂತರು ಜನನಾಯಕರು ಸಾಧಾರಣಜನರು ಇವರೊಡನೆ ಅರಸನನ್ನು ಯೆಹೋವನ ಆಲಯದಿಂದ ಮೇಲಣ ಬಾಗಲಿನ ಮಾರ್ಗವಾಗಿ ಅರಮನೆಗೆ ಕರಕೊಂಡು ಹೋಗಿ ರಾಜಸಿಂಹಾಸನದ ಮೇಲೆ ಕುಳ್ಳಿರಿಸಿದನು.",
			"21": "<sup>21</sup> ದೇಶದವರೆಲ್ಲರೂ ಬಹಳವಾಗಿ ಸಂತೋಷಪಟ್ಟರು; ಪಟ್ಟಣವು ಶಾಂತವಾಯಿತು; ಅತಲ್ಯಳನ್ನು ಕತ್ತಿಯಿಂದ ಸಂಹರಿಸಿದರು."
		},
		"24": {
			"1": "<sup>1</sup> ಯೆ‍ಹೋವಾಷನು ಅರಸನಾದಾಗ ಏಳು ವರುಷದವನಾಗಿದ್ದನು. ಅವನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ನಾಲ್ವತ್ತು ವರುಷ ಆಳಿದನು. ಬೇರ್ಷೆಬದವಳಾದ ಚಿಬ್ಯಳು ಅವನ ತಾಯಿ.",
			"2": "<sup>2</sup> ಅವನು ಯಾಜಕನಾದ ಯೆಹೋಯಾದನ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ಯೆಹೋವನ ಚಿತ್ತಾನುಸಾರವಾಗಿ ನಡೆದುಕೊಳ್ಳುತ್ತಿದ್ದನು.",
			"3": "<sup>3</sup> ಯೆಹೋಯಾದನು ಅವನಿಗೆ ಇಬ್ಬರು ಕನ್ಯೆಯರನ್ನು ಮದುವೆ ಮಾಡಿಕೊಟ್ಟನು; ಅವನು ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ಪಡೆದನು.",
			"4": "<sup>4</sup> ಕಾಲಾಂತರದಲ್ಲಿ ಯೆಹೋವಾಷನು ಯೆಹೋವನ ಆಲಯವನ್ನು ಜೀರ್ಣೋದ್ಧಾರಮಾಡುವದಕ್ಕೆ ಮನಸ್ಸುಮಾಡಿ",
			"5": "<sup>5</sup> ಯಾಜಕರನ್ನೂ ಲೇವಿಯರನ್ನೂ ಕೂಡಿಸಿ ಅವರಿಗೆ - ನೀವು ಯೆಹೂದದ ಪಟ್ಟಣಗಳಿಗೆ ಹೋಗಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಆಲಯವನ್ನು ವರುಷ ವರುಷ ಜೀರ್ಣೋದ್ಧಾರ ಮಾಡುವದಕ್ಕಾಗಿ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರಿಂದ ಹಣಕೂಡಿಸಿರಿ; ಈ ಕಾರ್ಯವನ್ನು ಶೀಘ್ರವಾಗಿ ನೆರವೇರಿಸಿರಿ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"6": "<sup>6</sup> ಆದರೂ ಲೇವಿಯರು ಅವಸರ ಪಡಲಿಲ್ಲ. ಆದದರಿಂದ ಅರಸನು ಅವರ ಮುಖ್ಯಸ್ಥನಾದ ಯೆಹೋಯಾದನನ್ನು ಕರಿಸಿ ಅವನಿಗೆ - ಯೆಹೋವನ ಸೇವಕನಾದ ಮೋಶೆಯ ವಿಧಿಗನುಸಾರವಾಗಿ ಇಸ್ರಾಯೇಲ್ ಸಮೂಹದವರೆಲ್ಲಾ ದೇವದರ್ಶನದ ಗುಡಾರಕ್ಕೋಸ್ಕರ ಕೊಡಬೇಕಾದ ಕಾಣಿಕೆಯನ್ನು ಲೇವಿಯರು ಯೆಹೂದ್ಯರಿಂದಲೂ ಯೆರೂಸಲೇಮಿನವರಿಂದಲೂ ಕೂಡಿಸುವ ಹಾಗೆ ನೀನೇಕೆ ನೋಡಿಕೊಳ್ಳಲಿಲ್ಲ?",
			"7": "<sup>7</sup> ಅತಿ ದುಷ್ಟಳಾದ ಅತಲ್ಯಳ ಮನೆಯವರು ದೇವಾಲಯವನ್ನು ಹಾಳುಮಾಡಿ ಯೆಹೋವನ ಆಲಯದ ಪ್ರತಿಷ್ಠಿತ ವಸ್ತುಗಳನ್ನು ಬಾಳ್‍ದೇವತೆಗಳಿಗೋಸ್ಕರ ಕೊಟ್ಟುಬಿಟ್ಟರಲ್ಲಾ ಎಂದು ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ಅನಂತರ ಅರಸನು ಒಂದು ಪೆಟ್ಟಿಗೆಯನ್ನು ಮಾಡಿಸಿ ಅದನ್ನು ಯೆಹೋವನ ಆಲಯದ ಬಾಗಲಿನ ಹೊರಗೆ ಇಡಿಸಿ -",
			"9": "<sup>9</sup> ದೇವರ ಸೇವಕನಾದ ಮೋಶೆಯು ಅರಣ್ಯದಲ್ಲಿ ವಿಧಿಸಿದಂತೆ ಇಸ್ರಾಯೇಲ್ಯರು ಯೆಹೋವನಿಗೋಸ್ಕರ ಕೊಡಬೇಕಾದ ಕಾಣಿಕೆಯನ್ನು ತಂದುಕೊಡಬೇಕು ಎಂಬದಾಗಿ ಯೆಹೂದದಲ್ಲಿಯೂ ಯೆರೂಸಲೇಮಿನಲ್ಲಿಯೂ ಪ್ರಕಟಿಸಿದನು.",
			"10": "<sup>10</sup> ಎಲ್ಲಾ ಪ್ರಭುಗಳೂ ಜನರೂ ಸಂತೋಷದಿಂದ ಬೇಕಾಗುವಷ್ಟು ಹಣವನ್ನು ತಂದು ಪೆಟ್ಟಿಗೆಯಲ್ಲಿ ಹಾಕಿದರು.",
			"11": "<sup>11</sup> ಪೆಟ್ಟಿಗೆಯಲ್ಲಿ ತುಂಬಾ ಹಣವಿರುತ್ತದೆಂದು ಕಂಡುಬಂದಾಗೆಲ್ಲಾ ಲೇವಿಯರು ಪೆಟ್ಟಿಗೆಯನ್ನು ರಾಜಕೀಯ ಅಧ್ಯಕ್ಷರ ಬಳಿಗೆ ತರುವರು. ಆಗ ಅರಸನ ಲೇಖಕನೂ ಮಹಾಯಾಜಕನ ಉದ್ಯೋಗಸ್ಥನೂ ಬಂದು ಅದನ್ನು ತೆರವುಮಾಡಿ ತಿರಿಗಿ ಅದರ ಸ್ಥಳದಲ್ಲಿಡುವರು. ಅವರು ಪ್ರತಿದಿನವೂ ಈ ಪ್ರಕಾರ ಮಾಡುತ್ತಿದ್ದದರಿಂದ ಬಹಳ ಹಣವನ್ನು ಕೂಡಿಸಿದರು.",
			"12": "<sup>12</sup> ಅರಸನೂ ಯೆಹೋಯಾದನೂ ಅದನ್ನು ಯೆಹೋವನ ಆಲಯದ ಕೆಲಸವನ್ನು ನಡಿಸುವವರಿಗೆ ಒಪ್ಪಿಸಿದರು. ಇವರು ಯೆಹೋವನ ಆಲಯದ ಜೀರ್ಣೋದ್ಧಾರಕ್ಕಾಗಿ ಶಿಲ್ಪಿಗರನ್ನೂ ಬಡಗಿಯರನ್ನೂ ಅದನ್ನು ಭದ್ರಪಡಿಸುವದಕ್ಕಾಗಿ ಕಮ್ಮಾರರನ್ನೂ ಕಂಚುಗಾರರನ್ನೂ ಹಚ್ಚಿದರು.",
			"13": "<sup>13</sup> ಕೆಲಸಮಾಡುವವರು ಬಹುಪ್ರಯಾಸ ಪಡುತ್ತಿದ್ದದರಿಂದ ಜೀರ್ಣೋದ್ಧಾರದ ಕೆಲಸವು ಮುಂದರಿಯಿತು. ಅವರು ದೇವಾಲಯವನ್ನು ಭದ್ರಪಡಿಸಿ ಪೂರ್ವಸ್ಥಿತಿಗೆ ತಂದರು.",
			"14": "<sup>14</sup> ಕೆಲಸ ತೀರಿದಾಗ ಅವರು ಉಳಿದ ಹಣವನ್ನು ಅರಸನಿಗೂ ಯೆಹೋಯಾದನಿಗೂ ಒಪ್ಪಿಸಿದರು. ಇವರು ಅದರಿಂದ ಯೆಹೋವನ ಆಲಯದ ಆರಾಧನೆಗಾಗಿಯೂ ಯಜ್ಞಾರ್ಪಣೆಗಾಗಿಯೂ ಉಪಯೋಗವಾಗುವ ಧೂಪಾರತಿ, ಬೆಳ್ಳಿಬಂಗಾರದ ಪಾತ್ರೆ ಮೊದಲಾದ ಸಾಮಾನುಗಳನ್ನು ಮಾಡಿಸಿದರು. ಯೆಹೋಯಾದನ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ಯೆಹೋವನ ಆಲಯದಲ್ಲಿ ಪ್ರತಿನಿತ್ಯವೂ ಸರ್ವಾಂಗಹೋಮ ಸಮರ್ಪಣೆಯಾಗುತ್ತಿತ್ತು.",
			"15": "<sup>15</sup> ಅವನು ಮುಪ್ಪಿನ ಮುದುಕನಾಗಿ ಮರಣಹೊಂದಿದನು; ಅವನು ಸಾಯುವಾಗ ನೂರಮೂವತ್ತು ವರುಷದವನಾಗಿದ್ದನು.",
			"16": "<sup>16</sup> ಅವನು ಇಸ್ರಾಯೇಲ್ಯರ ವಿಷಯದಲ್ಲಿಯೂ ದೇವರ ಮತ್ತು ದೇವಾಲಯದ ವಿಷಯದಲ್ಲಿಯೂ ಸತ್ಕಾರ್ಯಮಾಡಿದ್ದರಿಂದ ಅವನ ಶವವನ್ನು ದಾವೀದನಗರದೊಳಗೆ ರಾಜಶ್ಮಶಾನದಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು.",
			"17": "<sup>17</sup> ಯೆಹೋಯಾದನು ಮೃತನಾದ ಮೇಲೆ ಯೆಹೂದಪ್ರಭುಗಳು ಅರಸನ ಬಳಿಗೆ ಬಂದು ಅವನಿಗೆ ಅಡ್ಡಬಿದ್ದು ಅವನನ್ನು ಒಲಿಸಿಕೊಂಡರು;",
			"18": "<sup>18</sup> ಅಂದಿನಿಂದ ಅವರು ತಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಯೆಹೋವನ ಆಲಯವನ್ನು ನಿರಾಕರಿಸಿ ಅಶೇರಸ್ತಂಭಗಳನ್ನೂ ವಿಗ್ರಹಗಳನ್ನೂ ಪೂಜಿಸುವವರಾದರು. ಅವರ ಈ ಅಪರಾಧದ ದೆಸೆಯಿಂದ ಯೆಹೂದದ ಮೇಲೆಯೂ ಯೆರೂಸಲೇಮಿನ ಮೇಲೆಯೂ ದೇವಕೋಪವುಂಟಾಯಿತು.",
			"19": "<sup>19</sup> ಯೆಹೋವನು ಅವರನ್ನು ತನ್ನ ಕಡೆಗೆ ತಿರುಗಿಸಿಕೊಳ್ಳುವದಕ್ಕಾಗಿ ಅವರ ಬಳಿಗೆ ಪ್ರವಾದಿಗಳನ್ನು ಕಳುಹಿಸಿ ಇವರ ಮುಖಾಂತರ ಎಷ್ಟು ಎಚ್ಚರಿಸಿದರೂ ಅವರು ಕಿವಿಗೊಡಲಿಲ್ಲ.",
			"20": "<sup>20</sup> ಆಗ ಯಾಜಕನಾದ ಯೆಹೋಯಾದನ ಮಗ ಜೆಕರ್ಯನು ದೇವಾತ್ಮಾವೇಶವುಳ್ಳವನಾದನು; ಆಗ ಅವನು ಜನರ ಎದುರಿಗೆ ಉನ್ನತಸ್ಥಾನದಲ್ಲಿ ನಿಂತು ಅವರಿಗೆ - ದೇವರ ಮಾತನ್ನು ಕೇಳಿರಿ; ನೀವು ಯೆಹೋವನ ಆಜ್ಞೆಗಳನ್ನು ಮೀರಿ ನಿಮ್ಮನ್ನು ಕೆಡಿಸಿಕೊಳ್ಳುವದೇಕೆ? ನೀವು ಯೆಹೋವನನ್ನು ಬಿಟ್ಟದ್ದರಿಂದ ಆತನೂ ನಿಮ್ಮನ್ನು ಬಿಟ್ಟಿದ್ದಾನೆ ಅಂದನು.",
			"21": "<sup>21</sup> ಆಗ ಅವರು ಅವನಿಗೆ ವಿರೋಧವಾಗಿ ಒಳಸಂಚುಮಾಡಿ ಯೆಹೋವನ ಆಲಯದ ಪ್ರಾಕಾರದಲ್ಲಿ ಅವನನ್ನು ಕಲ್ಲೆಸೆದು ಕೊಂದರು. ಇದು ಅರಸನಾದ ಯೆಹೋವಾಷನ ಅಪ್ಪಣೆಯಿಂದಲೇ ಆಯಿತು.",
			"22": "<sup>22</sup> ಇವನು ಜೆಕರೀಯನ ತಂದೆಯಾದ ಯೆಹೋಯಾದನಿಂದ ತನಗುಂಟಾದ ಕೃಪೆಯನ್ನು ನೆನಪುಮಾಡಿಕೊಳ್ಳದೆ ಅವನ ಮಗನನ್ನು ಕೊಲ್ಲಿಸಿದನು. ಜೆಕರೀಯನು ಸಾಯುವಾಗ - ಯೆಹೋವನೇ ನೋಡಿ ವಿಚಾರಿಸಲಿ ಅಂದನು.",
			"23": "<sup>23</sup> ವರುಷಾಂತ್ಯದಲ್ಲಿ ಅರಾಮ್ಯರ ಸೈನ್ಯವು ಯೆಹೋವಾಷನಿಗೆ ವಿರೋಧವಾಗಿ ಹೊರಟಿತು. ಆ ಸೈನ್ಯದವರು ಯೆಹೂದದೇಶದೊಳಗೆ ನುಗ್ಗಿ ಯೆರೂಸಲೇಮಿಗೆ ಬಂದು ಇಸ್ರಾಯೇಲ್ಯರ ಎಲ್ಲಾ ಜನಾಧಿಪತಿಗಳನ್ನು ನಿರ್ನಾಮಮಾಡಿ ಅವರಲ್ಲಿ ಸಿಕ್ಕಿದ ಕೊಳ್ಳೆಯನ್ನೆಲ್ಲಾ ದಮಸ್ಕದ ಅರಸನಿಗೆ ಕಳುಹಿಸಿದರು.",
			"24": "<sup>24</sup> ಅರಾಮ್ಯರ ಸೈನ್ಯದಿಂದ ಬಂದು ಗುಂಪು ಚಿಕ್ಕದಾಗಿದ್ದರೂ ಯೆಹೂದ್ಯರು ತಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಬಿಟ್ಟುಬಿಟ್ಟದ್ದರಿಂದ ಮಹಾಸೈನ್ಯವಾಗಿದ್ದ ಅವರನ್ನು ಆತನು ಅರಾಮ್ಯರ ಕೈಯಲ್ಲಿ ಬೀಳುವಂತೆ ಮಾಡಿ ಯೆಹೋವಾಷನನ್ನು ಇವರ ಮುಖಾಂತರ ಶಿಕ್ಷಿಸಿದನು.",
			"25": "<sup>25</sup> ಇವರು ಅವನನ್ನು ಬಿಟ್ಟುಹೋದಕೂಡಲೆ ಅವನ ಸೇವಕರು ಯಾಜಕನಾದ ಯೆಹೋಯಾದನ ಮಗನ ವಧೆಯ ನಿಮಿತ್ತ ಅವನಿಗೆ ವಿರೋಧವಾಗಿ ಒಳಸಂಚುಮಾಡಿ ಕಠಿನ ರೋಗದಲ್ಲಿದ್ದ ಅವನನ್ನು ಹಾಸಿಗೆಯಲ್ಲೇ ಕೊಂದುಹಾಕಿದರು. ಅವನ ಶವವನ್ನು ದಾವೀದನಗರದೊಳಗೆ ಸಮಾಧಿಮಾಡಿದರು; ಆದರೆ ರಾಜಶ್ಮಶಾನದಲ್ಲಿ ಅಲ್ಲ.",
			"26": "<sup>26</sup> ಅಮ್ಮೋನ್ ದೇಶದ ಶಿಮ್ಗಾತೆಂಬಾಕೆಯ ಮಗನಾದ ಜಾಬಾದ್, ಮೋವಾಬ್ ದೇಶದ ಶಿಮ್ರೀತೆಂಬಾಕೆಯ ಮಗನಾದ ಯೆಹೋಜಾಬಾದ್ ಎಂಬವರೇ ಅವನಿಗೆ ವಿರೋಧವಾಗಿ ಒಳಸಂಚುಮಾಡಿದವರು.",
			"27": "<sup>27</sup> ಅವನ ಮಕ್ಕಳ ವಿವರವೂ ಅವನಿಗೆ ವಿರುದ್ಧವಾಗಿ ಹೇಳಲ್ಪಟ್ಟ ಅನೇಕ ದೈವೋಕ್ತಿಗಳೂ ದೇವಾಲಯದ ಜೀರ್ಣೋದ್ಧಾರವೃತ್ತಾಂತವೂ ರಾಜರ ಗ್ರಂಥದ ವ್ಯಾಖ್ಯಾನದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ಅಮಚ್ಯನು ಅರಸನಾದನು."
		},
		"25": {
			"1": "<sup>1</sup> ಅಮಚ್ಯನು ಪಟ್ಟಕ್ಕೆ ಬಂದಾಗ ಇಪ್ಪತ್ತೈದು ವರುಷದವನಾಗಿದ್ದನು. ಅವನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಇಪ್ಪತ್ತೊಂಭತ್ತು ವರುಷ ಆಳಿದನು. ಯೆರೂಸಲೇಮಿನವಳಾದ ಯೆಹೋವದ್ದಾನ್ ಎಂಬಾಕೆಯು ಅವನ ತಾಯಿ.",
			"2": "<sup>2</sup> ಅವನು ಯೆಹೋವನ ಚಿತ್ತಾನುಸಾರವಾಗಿ ನಡೆದನು; ಆದರೆ ಯಥಾರ್ಥಚಿತ್ತನಾಗಿರಲಿಲ್ಲ.",
			"3": "<sup>3</sup> ಅವನು ರಾಜ್ಯವನ್ನು ಭದ್ರಪಡಿಸಿಕೊಂಡ ಕೂಡಲೆ ತನ್ನ ತಂದೆಯನ್ನು ಕೊಂದ ಸೇವಕರನ್ನು ಕೊಲ್ಲಿಸಿದನು.",
			"4": "<sup>4</sup> ಆದರೆ ಮಕ್ಕಳ ಪಾಪದ ದೆಸೆಯಿಂದ ತಂದೆಗೂ ತಂದೆಯ ಪಾಪದ ದೆಸೆಯಿಂದ ಮಕ್ಕಳಿಗೂ ಮರಣಶಿಕ್ಷೆಯಾಗಬಾರದು; ಪ್ರತಿಯೊಬ್ಬನೂ ತನ್ನ ಪಾಪದ ಫಲವನ್ನು ತಾನೇ ಅನುಭವಿಸಬೇಕು ಎಂಬದಾಗಿ ಮೋಶೆಯ ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿರುವ ಯೆಹೋವನ ಅಪ್ಪಣೆಯನ್ನು ನೆನಪುಮಾಡಿಕೊಂಡು ಅವರ ಮಕ್ಕಳನ್ನು ಕೊಲ್ಲಿಸಲಿಲ್ಲ.",
			"5": "<sup>5</sup> ಆಮೇಲೆ ಅಮಚ್ಯನು ಯೆಹೂದ್ಯರನ್ನು ಕೂಡಿಸಿ ಸಹಸ್ರಾಧಿಪತಿ ಶತಾಧಿಪತಿಗಳ ಕೈಕೆಳಗೆ ಕೊಟ್ಟು ಯೆಹೂದ ಬೆನ್ಯಾಮೀನ್ ಕುಲಗಳಲ್ಲಿನ ಆಯಾ ಗೋತ್ರಗಳ ಪ್ರಕಾರ ಅವರನ್ನು ನಿಲ್ಲಿಸಿ ಇಪ್ಪತ್ತು ವರುಷದವರು ಮೊದಲುಗೊಂಡು ಹೆಚ್ಚಾದ ವಯಸ್ಸುಳ್ಳವರನ್ನು ಲೆಕ್ಕಮಾಡಲು ಅವರಲ್ಲಿ ಬರ್ಜಿ ಗುರಾಣಿಗಳನ್ನು ಹಿಡಿದುಕೊಂಡು ಯುದ್ಧಕ್ಕೆ ಹೊರಡತಕ್ಕ ಶೂರರು ಮೂರು ಲಕ್ಷ ಮಂದಿ ಸಿಕ್ಕಿದರು.",
			"6": "<sup>6</sup> ಇದಲ್ಲದೆ ಅವನು ನೂರು ತಲಾಂತು ಬೆಳ್ಳಿಯನ್ನು ಕೊಟ್ಟು ಇಸ್ರಾಯೇಲ್ಯರಿಂದಲೂ ಲಕ್ಷ ಮಂದಿ ಭಟರನ್ನು ತರಿಸಿದನು.",
			"7": "<sup>7</sup> ಆಗ ದೇವರ ಮನುಷ್ಯನೊಬ್ಬನು ಅವನ ಬಳಿಗೆ ಬಂದು ಅವನಿಗೆ - ಅರಸನೇ, ಇಸ್ರಾಯೇಲ್ ಸೈನ್ಯದವರು ನಿನ್ನ ಜೊತೆಯಲ್ಲಿ ಯುದ್ಧಕ್ಕೆ ಹೋಗಬಾರದು. ಯೆಹೋವನು ಎಫ್ರಾಯೀಮ್ಯರಾದ ಇಸ್ರಾಯೇಲ್ಯರನ್ನೆಲ್ಲಾ ಕೈಬಿಟ್ಟಿದ್ದಾನೆ.",
			"8": "<sup>8</sup> ನೀನೇ ಹೋಗಿ ಕಾರ್ಯವನ್ನು ನಡಿಸು; ಧೈರ್ಯದಿಂದ ಯುದ್ಧಮಾಡು. [ಹಾಗೆ ಮಾಡದಿದ್ದರೆ] ದೇವರು ಶತ್ರುಗಳಿಂದ ನಿನ್ನನ್ನು ಅಪಜಯಪಡಿಸುವನು. ಜಯಾಪಜಯಗಳನ್ನುಂಟುಮಾಡುವದಕ್ಕೆ ಆತನು ಶಕ್ತನಾಗಿರುತ್ತಾನಲ್ಲಾ ಅಂದನು.",
			"9": "<sup>9</sup> ಅಮಚ್ಯನು ದೇವರ ಮನುಷ್ಯನನ್ನು - ಹಾಗಾದರೆ ನಾನು ಇಸ್ರಾಯೇಲ್ ಸೈನ್ಯಕ್ಕೋಸ್ಕರ ನೂರು ತಲಾಂತುಗಳನ್ನು ಕೊಟ್ಟ ವಿಷಯದಲ್ಲಿ ಮಾಡುವದೇನು ಎಂದು ಕೇಳಲು ಅವನು ಯೆಹೋವನು ಅದಕ್ಕಿಂತ ಎಷ್ಟೋ ಹೆಚ್ಚಾಗಿ ನಿನಗೆ ಕೊಡಬಲ್ಲನು ಅಂದನು.",
			"10": "<sup>10</sup> ಆಗ ಅಮಚ್ಯನು ಎಫ್ರಾಯೀಮ್ಯರಿಂದ ಬಂದ ಸೈನಿಕರನ್ನು ಬೇರೆ ಮಾಡಿ ಅವರನ್ನು ಅವರ ದೇಶಕ್ಕೆ ಕಳುಹಿಸಿದನು. ಅವರು ಯೆಹೂದ್ಯರ ಮೇಲೆ ಬಹುಕೋಪಗೊಂಡು ಬಲುಸಿಟ್ಟಿನಿಂದ ತಮ್ಮ ದೇಶಕ್ಕೆ ಹೋದರು.",
			"11": "<sup>11</sup> ಅಮಚ್ಯನಾದರೋ ಧೈರ್ಯದಿಂದ ತನ್ನ ಸೈನ್ಯವನ್ನು ಕರಕೊಂಡು ಉಪ್ಪಿನ ತಗ್ಗಿಗೆ ಹೋಗಿ ಸೇಯೀರ್ಯರಲ್ಲಿ ಹತ್ತು ಸಾವಿರ ಮಂದಿಯನ್ನು ವಧಿಸಿದನು.",
			"12": "<sup>12</sup> ಯೆಹೂದ್ಯರು ಬೇರೆ ಹತ್ತು ಸಾವಿರ ಮಂದಿಯನ್ನು ಜೀವಸಹಿತವಾಗಿ ಹಿಡಿದು ಕಡಿದಾದ ಬಂಡೆಯ ತುದಿಗೆ ಒಯ್ದು ಅವರೆಲ್ಲರೂ ಚೂರುಚೂರಾಗುವ ಹಾಗೆ ಅವರನ್ನು ಕೆಳಕ್ಕೆ ದೊಬ್ಬಿದರು.",
			"13": "<sup>13</sup> ಅಷ್ಟರಲ್ಲಿ ಅಮಚ್ಯನು ತನ್ನ ಜೊತೆಯಲ್ಲಿ ಯುದ್ಧಕ್ಕೆ ಬರಬಾರದೆಂದು ಹಿಂದಕ್ಕೆ ಕಳುಹಿಸಿದ ಗುಂಪಿನವರು ಸಮಾರ್ಯಕ್ಕೂ ಬೇತ್ಹೋರೋನಿಗೂ ಮಧ್ಯದಲ್ಲಿರುವ ಯೆಹೂದಪಟ್ಟಣಗಳ ಮೇಲೆ ಬಿದ್ದು ಮೂರು ಸಾವಿರ ಜನರನ್ನು ಕೊಂದು ದೊಡ್ಡ ಕೊಳ್ಳೆಯನ್ನು ಕೂಡಿಸಿಕೊಂಡು ಹೋದರು.",
			"14": "<sup>14</sup> ಅಮಚ್ಯನು ಎದೋಮ್ಯರನ್ನು ಸೋಲಿಸಿ ಮನೆಗೆ ಬಂದ ಮೇಲೆ ತಾನು ತೆಗೆದುಕೊಂಡು ಬಂದಿದ್ದ ಸೇಯೀರನವರ ದೇವತಾಪ್ರತಿಮೆಗಳನ್ನು ತನ್ನ ದೇವರುಗಳೆಂದು ನಿಲ್ಲಿಸಿ ಅವುಗಳಿಗೆ ಅಡ್ಡಬಿದ್ದು ಧೂಪಹಾಕುತ್ತಿದ್ದನು.",
			"15": "<sup>15</sup> ಆಗ ಯೆಹೋವನು ಅಮಚ್ಯನ ಮೇಲೆ ಬಹಳವಾಗಿ ಕೋಪಗೊಂಡು ಅವನ ಬಳಿಗೆ ಪ್ರವಾದಿಯನ್ನು ಕಳುಹಿಸಿ ಅವನಿಗೆ - ನಿನ್ನ ಕೈಯಿಂದ ತಮ್ಮ ಜನರನ್ನು ತಪ್ಪಿಸಲಾರದೆಹೋದ ಅನ್ಯದೇವತೆಗಳಲ್ಲಿ ಭಕ್ತಿಯನ್ನೇಕೆ ಇಟ್ಟಿದ್ದೀ ಎಂದು ಹೇಳಿಸಿದನು.",
			"16": "<sup>16</sup> ಈ ಮಾತನ್ನು ಹೇಳಿದ ಪ್ರವಾದಿಗೆ ಅರಸನು - ನಿನ್ನನ್ನು ರಾಜಮಂತ್ರಿಯನ್ನಾಗಿ ನೇಮಿಸಿದ್ದೇವೋ? ಸುಮ್ಮನಿರು, ನಿನಗೆ ಪೆಟ್ಟು ಬೇಕೋ ಅನ್ನಲು ಅವನು - ನೀನು ನನ್ನ ಬುದ್ಧಿವಾದವನ್ನು ಲಾಲಿಸದೆ ಹೀಗೆ ಮಾಡುವದರಿಂದ ದೇವರು ನಿನ್ನನ್ನು ನಾಶಮಾಡಬೇಕೆಂದು ನಿಶ್ಚಯಿಸಿಕೊಂಡಿದ್ದಾನೆಂಬದಾಗಿ ನನಗೆ ತಿಳಿಯಬಂತು ಎಂದು ಹೇಳಿ ಸುಮ್ಮನಾದನು.",
			"17": "<sup>17</sup> ಅನಂತರ ಯೆಹೂದದ ಅರಸನಾದ ಅಮಚ್ಯನು ಮಂತ್ರಾಲೋಚನೆ ಕೇಳಿ ಯೇಹುವಿನ ಮೊಮ್ಮಗನೂ ಯೆಹೋವಾಹಾಜನ ಮಗನೂ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನೂ ಆದ ಯೋವಾಷನಿಗೆ - ನಾವು ಒಬ್ಬರನ್ನೊಬ್ಬರು ಎದುರುಗೊಳ್ಳೋಣ ಎಂದು ದೂತರ ಮುಖಾಂತರವಾಗಿ ಹೇಳಿಸಿದನು.",
			"18": "<sup>18</sup> ಆಗ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾದ ಯೋವಾಷನು ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಅಮಚ್ಯನಿಗೆ - ಲೆಬನೋನಿನ ಮುಳ್ಳುಗಿಡವು ಅಲ್ಲಿನ ದೇವದಾರುಮರಕ್ಕೆ - ನನ್ನ ಮಗನಿಗೆ ನಿನ್ನ ಮಗಳನ್ನು ಕೊಡು ಎಂದು ಹೇಳಿಕಳುಹಿಸಿತು. ತುಸುಹೊತ್ತಾದ ಮೇಲೆ ಲೆಬನೋನಿನ ಒಂದು ಕಾಡುಮೃಗವು ಆ ಮಾರ್ಗವಾಗಿ ಹಾದುಹೋಗುತ್ತಿರುವಾಗ ಅದನ್ನು ತುಳಿದು ಬಿಟ್ಟಿತು.",
			"19": "<sup>19</sup> ಅಹಹ, ಎದೋಮ್ಯರನ್ನು ಸೋಲಿಸಿ ಬಹುಕೀರ್ತಿಯನ್ನು ಪಡಕೊಂಡೆನಲ್ಲಾ ಎಂದು ನೀನು ಹೆಮ್ಮೆಗೊಂಡಿದ್ದೀ; ಸುಮ್ಮನೆ ಮನೆಯಲ್ಲಿ ಕೂತುಕೊಳ್ಳಬಾರದೇ; ನನ್ನನ್ನು ಕೆಣಕಿ ನಿನಗೂ ನಿನ್ನ ರಾಜ್ಯಕ್ಕೂ ಯಾಕೆ ಕೇಡನ್ನು ತಂದುಕೊಳ್ಳುತ್ತೀ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"20": "<sup>20</sup> ಅಮಚ್ಯನು ಆ ಮಾತನ್ನು ಲಕ್ಷಿಸಲಿಲ್ಲ; ಹಾಗಾಗಬೇಕೆಂಬದೇ ದೇವರ ಸಂಕಲ್ಪವಾಗಿತ್ತು. ಯೆಹೂದ್ಯರು ಎದೋಮ್ಯರ ದೇವತೆಗಳಲ್ಲಿ ಭಕ್ತಿಯನ್ನಿಟ್ಟದ್ದರಿಂದ ಅವರನ್ನು ಪರಾಧೀನಮಾಡಬೇಕೆಂದು ಆತನು ನಿರ್ಣಯಿಸಿದ್ದನು.",
			"21": "<sup>21</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾದ ಯೋವಾಷನು ಯುದ್ಧಕ್ಕೆ ಹೊರಟನು; ಅವನೂ ಯೆಹೂದದ ಅರಸನಾದ ಅಮಚ್ಯನೂ ಯೆಹೂದಪ್ರಾಂತದ ಬೇತ್ಷೆಮೆಷಿನಲ್ಲಿ ಒಬ್ಬರನ್ನೊಬ್ಬರು ಎದುರುಗೊಂಡರು.",
			"22": "<sup>22</sup> ಯೆಹೂದ್ಯರು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಸೋತು ತಮ್ಮ ತಮ್ಮ ನಿವಾಸಗಳಿಗೆ ಓಡಿಹೋದರು.",
			"23": "<sup>23</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾದ ಯೋವಾಷನು ಯೆಹೋವಾಹಾಜನ ಮೊಮ್ಮಗನೂ ಯೆಹೋವಾಷನ ಮಗನೂ ಯೆಹೂದದ ಅರಸನೂ ಆದ ಅಮಚ್ಯನನ್ನು ಬೇತ್ಷೆಮೆಷಿನಲ್ಲಿ ಹಿಡಿದು ಅವನನ್ನು ಯೆರೂಸಲೇಮಿಗೆ ಕರಕೊಂಡು ಬಂದು ಎಫ್ರಾಯೀಮ್ ಬಾಗಲಿಗೂ ಮೂಲೆಯ ಬಾಗಲಿಗೂ ಮಧ್ಯದಲ್ಲಿದ್ದ ಯೆರೂಸಲೇಮಿನ ಗೋಡೆಯನ್ನು ನಾನೂರು ಮೊಳದಷ್ಟು ಕೆಡವಿಬಿಟ್ಟನು.",
			"24": "<sup>24</sup> ಇದಲ್ಲದೆ ಅವನು ಓಬೇದೆದೋಮನ ವಶದಲ್ಲಿದ್ದ ದೇವಾಲಯದ ಎಲ್ಲಾ ಬೆಳ್ಳಿಬಂಗಾರವನ್ನೂ ಪಾತ್ರೆಗಳನ್ನೂ ಅರಮನೆಯ ಭಂಡಾರಗಳಲ್ಲಿದ್ದದ್ದೆಲ್ಲವನ್ನೂ ತೆಗೆದುಕೊಂಡು ಹೊಣೆಗಾರರನ್ನಾಗಿ ಕೆಲವರನ್ನು ಸೆರೆಹಿಡಿದು ಸಮಾರ್ಯಕ್ಕೆ ಹಿಂದಿರುಗಿದನು.",
			"25": "<sup>25</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನೂ ಯೆಹೋವಾಹಾಜನ ಮಗನೂ ಆದ ಯೋವಾಷನು ಸತ್ತ ನಂತರ ಯೆಹೂದದ ಅರಸನಾದ ಯೆಹೋವಾಷನ ಮಗ ಅಮಚ್ಯನು ಇನ್ನೂ ಹದಿನೈದು ವರುಷ ಬದುಕಿದನು.",
			"26": "<sup>26</sup> ಅವನ ಉಳಿದ ಪೂರ್ವೋತ್ತರಚರಿತ್ರೆಯು ಯೆಹೂದ್ಯರ ಮತ್ತು ಇಸ್ರಾಯೇಲ್ಯರ ರಾಜರ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತದೆ.",
			"27": "<sup>27</sup> ಅಮಚ್ಯನು ಯೆಹೋವ ಭಕ್ತಿಯನ್ನು ಬಿಟ್ಟಂದಿನಿಂದ ಯೆರೂಸಲೇಮಿನವರು ಅವನಿಗೆ ವಿರೋಧವಾಗಿ ಒಳಸಂಚು ಮಾಡುತ್ತಿದ್ದರು. ಆದದರಿಂದ ಅವನು ಲಾಕೀಷಿಗೆ ಓಡಿಹೋದನು; ಆದರೆ ಅವರು ಆಳುಗಳನ್ನು ಕಳುಹಿಸಿ ಅವನನ್ನು ಅಲ್ಲಿಯೇ ಕೊಲ್ಲಿಸಿದರು.",
			"28": "<sup>28</sup> ಅವನ ಜನರು ಆ ಶವವನ್ನು ಕುದುರೆಯ ಮೇಲೆ ಯೆರೂಸಲೇಮಿಗೆ ತಂದು ಯೆಹೂದದ ರಾಜಧಾನಿಯೊಳಗೆ ಅವನ ಪಿತೃಶ್ಮಶಾನದಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು."
		},
		"26": {
			"1": "<sup>1</sup> ಅನಂತರ ಎಲ್ಲಾ ಯೆಹೂದ್ಯರೂ ಕೂಡಿ ಹದಿನಾರು ವರುಷದವನಾಗಿದ್ದ ಉಜ್ಜೀಯನನ್ನು ಅವನ ತಂದೆಯಾದ ಅಮಚ್ಯನಿಗೆ ಬದಲಾಗಿ ಅರಸನನ್ನಾಗಿ ಮಾಡಿದರು.",
			"2": "<sup>2</sup> ಅರಸನು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಿದ ಮೇಲೆ ಇವನು ಏಲೋತ್ ಪಟ್ಟಣವನ್ನು ತಿರಿಗಿ ಯೆಹೂದರಾಜ್ಯಕ್ಕೆ ಸೇರಿಸಿಕೊಂಡು ಭದ್ರಪಡಿಸಿದನು.",
			"3": "<sup>3</sup> ಉಜ್ಜೀಯನು ಪಟ್ಟಕ್ಕೆ ಬಂದಾಗ ಹದಿನಾರು ವರುಷದವನಾಗಿದ್ದನು; ಅವನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಐವತ್ತೆರಡು ವರುಷ ಆಳಿದನು. ಯೆರೂಸಲೇಮಿನವಳಾದ ಯೆಕೊಲ್ಯ ಎಂಬಾಕೆಯು ಅವನ ತಾಯಿ.",
			"4": "<sup>4</sup> ಅವನು ತನ್ನ ತಂದೆಯಾದ ಅಮಚ್ಯನಂತೆ ಯೆಹೋವನ ಚಿತ್ತಾನುಸಾರವಾಗಿ ನಡೆದನು.",
			"5": "<sup>5</sup> ದೇವಭಕ್ತಿಯನ್ನು ಬೋಧಿಸುತ್ತಿದ್ದ ಜೆಕರ್ಯನ ಜೀವಮಾನದಲ್ಲಿ ಅವನು ದೇವರನ್ನು ಅವಲಂಬಿಸಿಕೊಂಡಿದ್ದನು; ಹೀಗೆ ಅವಲಂಬಿಸಿದ್ದ ಕಾಲದಲ್ಲೆಲ್ಲಾ ದೇವರಾದ ಯೆಹೋವನು ಅವನನ್ನು ಅಭಿವೃದ್ಧಿಗೆ ತಂದನು.",
			"6": "<sup>6</sup> ಅವನು ಫಿಲಿಷ್ಟಿಯರ ಮೇಲೆ ಯುದ್ಧಕ್ಕಾಗಿ ಹೊರಟು ಗತ್ ಊರು, ಯಬ್ನೆ, ಅಷ್ಡೋದ್ ಎಂಬ ಪಟ್ಟಣಗಳ ಗೋಡೆಗಳನ್ನು ಕೆಡವಿಬಿಟ್ಟು ಅಷ್ಡೋದಿನ ಮತ್ತು ಫಿಲಿಷ್ಟಿಯರ ಪ್ರಾಂತಗಳಲ್ಲಿ ಪಟ್ಟಣಗಳನ್ನು ಕಟ್ಟಿಸಿದನು.",
			"7": "<sup>7</sup> ಫಿಲಿಷ್ಟಿಯರೊಡನೆಯೂ ಗೂರ್ ಬಾಳಿನಲ್ಲಿರುವ ಅರಬಿಯರೊಡನೆಯೂ ಮೆಗೂನ್ಯರೊಡನೆಯೂ ಯುದ್ಧಮಾಡುವಾಗ ಅವನಿಗೆ ದೇವರ ಸಹಾಯವು ದೊರೆಯಿತು.",
			"8": "<sup>8</sup> ಅಮ್ಮೋನಿಯರೂ ಉಜ್ಜೀಯನಿಗೆ ಕಪ್ಪವನ್ನು ಕೊಡುವವರಾದರು. ಅವನು ಅಧಿಕಬಲವುಳ್ಳವನಾದದರಿಂದ ಅವನ ಹೆಸರು ಐಗುಪ್ತದ ಮೇರೆಯವರೆಗೂ ಹಬ್ಬಿತು.",
			"9": "<sup>9</sup> ಅವನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಮೂಲೆಬಾಗಲು, ತಗ್ಗಿನ ಬಾಗಲು, ಗೋಡೆ ತಿರುಗುವ ಭಾಗ ಇವುಗಳ ಮೇಲೆ ಬುರುಜುಗಳನ್ನು ಕಟ್ಟಿಸಿದನು.",
			"10": "<sup>10</sup> ಅವನಿಗೆ ಇಳಕಲಿನ ಪ್ರದೇಶದಲ್ಲಿಯೂ ತಪ್ಪಲ ಸೀಮೆಯಲ್ಲಿಯೂ ಅಲ್ಲದೆ ಅಡವಿಯಲ್ಲಿಯೂ ಪಶುಗಳ ದೊಡ್ಡ ಮಂದೆಗಳಿದ್ದದರಿಂದ ಆ ಅಡವಿಯಲ್ಲಿ ಬುರುಜುಗಳನ್ನು ಕಟ್ಟಿಸಿ ಹಲವು ಬಾವಿಗಳನ್ನು ತೋಡಿಸಿದನು. ಅವನು ವ್ಯವಸಾಯದಲ್ಲಿ ಇಷ್ಟವುಳ್ಳವನಾಗಿದ್ದದರಿಂದ ಗುಡ್ಡ, ಫಲವತ್ತಾದ ಬೈಲು ಇವುಗಳಲ್ಲಿ ಹೊಲ ದ್ರಾಕ್ಷೇತೋಟಗಳ ಕೆಲಸಮಾಡುವವರನ್ನು ನೇಮಿಸಿದನು.",
			"11": "<sup>11</sup> ಇದಲ್ಲದೆ ಉಜ್ಜೀಯನಿಗೆ ಯುದ್ಧಕ್ಕೋಸ್ಕರ ಸೈನ್ಯವೂ ಇತ್ತು. ಲೇಖಕನಾದ ಯೆಗೀಯೇಲ್, ಅಧಿಕಾರಿಯಾದ ಮಾಸೇಯ ಇವರು ಅರಸನ ಸರದಾರರಲ್ಲೊಬ್ಬನಾದ ಹನನ್ಯನ ಆಜ್ಞಾನುಸಾರವಾಗಿ ಅವರ ಪಟ್ಟಿಯನ್ನು ಬರೆದಿದ್ದರು. ಆಯಾ ಗುಂಪುಗಳು ಪಟ್ಟಿಯ ಕ್ರಮಾನುಸಾರ ಯುದ್ಧಕ್ಕೆ ಹೊರಡುತ್ತಿದ್ದವು.",
			"12": "<sup>12</sup> ರಣವೀರರಾಗಿರುವ ಎಲ್ಲಾ ಗೋತ್ರಪ್ರಧಾನರ ಸಂಖ್ಯೆಯು ಎರಡು ಸಾವಿರದ ಆರು ನೂರು.",
			"13": "<sup>13</sup> ಇವರ ಕೈಕೆಳಗಿರುವ ಭಟರ ಸಂಖ್ಯೆಯು ಮೂರು ಲಕ್ಷದ ಏಳು ಸಾವಿರದ ಐನೂರು; ಇವರು ಅರಸನ ಸೇವೆಯಲ್ಲಿದ್ದು ಅವನ ವೈರಿಗಳಿಗೆ ವಿರೋಧವಾಗಿ ಪರಾಕ್ರಮದಿಂದ ಯುದ್ಧಮಾಡುತ್ತಿದ್ದರು.",
			"14": "<sup>14</sup> ಉಜ್ಜೀಯನು ಈ ಎಲ್ಲಾ ಸೈನ್ಯದವರಿಗೋಸ್ಕರ ಬೇಕಾದ ಗುರಾಣಿ ಬರ್ಜಿ ಶಿರಸ್ತ್ರಾಣ ಕವಚ ಬಿಲ್ಲು ಕವಣೆಯಕಲ್ಲು ಇವುಗಳನ್ನು ಒದಗಿಸಿಕೊಟ್ಟನು.",
			"15": "<sup>15</sup> ಇದಲ್ಲದೆ ಅವನು ಬಾಣಗಳನ್ನೂ ದೊಡ್ಡ ಕಲ್ಲುಗಳನ್ನೂ ಎಸೆಯುವದಕ್ಕಾಗಿ ಜಾಣರ ಕಲ್ಪನೆಯ ಮೇರೆಗೆ ಯಂತ್ರಗಳನ್ನು ಮಾಡಿಸಿ ಅವುಗಳನ್ನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಬುರುಜುಗಳ ಮೇಲೆಯೂ ಆಳುವೇರಿಗಳ ಮೇಲೆಯೂ ಇಡಿಸಿದನು. ಅವನು ಬಲಿಷ್ಠನಾಗುವವರೆಗೂ ದೇವರ ಆಶ್ಚರ್ಯವಾದ ಸಹಾಯವನ್ನು ಹೊಂದಿದನು; ಅವನ ಕೀರ್ತಿಯು ದೂರದವರೆಗೆ ಹಬ್ಬಿತು.",
			"16": "<sup>16</sup> ಆದರೆ ಅವನು ಬಲಿಷ್ಠನಾದ ಮೇಲೆ ಗರ್ವಿಷ್ಠನಾಗಿ ಭ್ರಷ್ಟನಾದನು; ತನ್ನ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ದ್ರೋಹಿಯಾಗಿ ಧೂಪವೇದಿಯ ಮೇಲೆ ತಾನೇ ಧೂಪ ಹಾಕಬೇಕೆಂದು ಯೆಹೋವನ ಆಲಯದೊಳಕ್ಕೆ ಹೋದನು.",
			"17": "<sup>17</sup> ಆಗ ಮಹಾಯಾಜಕನಾದ ಅಜರ್ಯನೂ ಧೈರ್ಯಶಾಲಿಗಳಾದ ಎಂಭತ್ತು ಮಂದಿ ಇತರ ಯಾಜಕರೂ ಅರಸನಾದ ಉಜ್ಜೀಯನ ಹಿಂದೆಯೇ ಹೋಗಿ ಅವನೆದುರಿಗೆ ನಿಂತು",
			"18": "<sup>18</sup> ಅವನಿಗೆ - ಉಜ್ಜೀಯನೇ, ಯೆಹೋವನಿಗೋಸ್ಕರ ಧೂಪ ಹಾಕುವದು ನಿನ್ನ ಕೆಲಸವಲ್ಲ; ಅದಕ್ಕೋಸ್ಕರ ಆರೋನನ ಸಂತಾನದವರಾದ ಯಾಜಕರು ಪ್ರತಿಷ್ಠಿತರಾಗಿದ್ದಾರೆ. ಪವಿತ್ರಾಲಯವನ್ನು ಬಿಟ್ಟುಹೋಗು; ನೀನು ದ್ರೋಹಿ. ಇದಕ್ಕಾಗಿ ದೇವರಾದ ಯೆಹೋವನಿಂದ ನಿನಗೆ ಮಾನವುಂಟಾಗಲಾರದು ಅನ್ನಲು",
			"19": "<sup>19</sup> ಧೂಪಹಾಕಬೇಕೆಂದು ಕೈಯಲ್ಲಿ ಧೂಪಾರತಿಯನ್ನು ಹಿಡಿದಿದ್ದ ಉಜ್ಜೀಯನು ಕೋಪವುಳ್ಳವನಾದನು. ಅವನು ಯೆಹೋವನ ಆಲಯದ ಧೂಪವೇದಿಯ ಬಳಿಯಲ್ಲಿ ನಿಂತು ಯಾಜಕರೊಡನೆ ಸಿಟ್ಟಿನಿಂದ ಮಾತಾಡುತ್ತಿರುವಾಗ ಯಾಜಕರ ಸಮಕ್ಷದಲ್ಲೇ ಅವನ ಹಣೆಯಲ್ಲಿ ಕುಷ್ಠ ಹುಟ್ಟಿತು.",
			"20": "<sup>20</sup> ಮಹಾಯಾಜಕನಾದ ಅಜರ್ಯನೂ ಎಲ್ಲಾ ಯಾಜಕರೂ ಅವನನ್ನು ದೃಷ್ಟಿಸಿ ನೋಡಿದಾಗ ಅವನ ಹಣೆಯಲ್ಲಿ ಕುಷ್ಠವು ಕಂಡುಬಂದಕಾರಣ ಅವನನ್ನು ಶೀಘ್ರವಾಗಿ ಅಲ್ಲಿಂದ ಹೊರಡಿಸಿದರು. ಯೆಹೋವನು ತನ್ನನ್ನು ಬಾಧಿಸಿದ್ದಾನೆಂದು ಅವನಿಗೆ ತಿಳಿದದರಿಂದ ಅವನೂ ಶೀಘ್ರವಾಗಿ ಹೊರಗೆ ಹೋದನು.",
			"21": "<sup>21</sup> ಉಜ್ಜೀಯನು ಜೀವದಿಂದಿರುವವರೆಗೂ ಕುಷ್ಠರೋಗಿಯಾಗಿದ್ದು ಯೆಹೋವನ ಆಲಯಕ್ಕೆ ಬಾರದಂತೆ ಬಹಿಷ್ಕೃತನಾದನು. ಅವನು ಕುಷ್ಠದ ದೆಸೆಯಿಂದ ಪ್ರತ್ಯೇಕವಾದ ಮನೆಯಲ್ಲಿ ವಾಸಿಸಬೇಕಾಯಿತು. ರಾಜ ಗೃಹಾಧಿಪತ್ಯವನ್ನೂ ಪ್ರಜಾಪಾಲನೆಯನ್ನೂ ಅವನ ಮಗನಾದ ಯೋತಾಮನು ನೋಡಿಕೊಳ್ಳುತ್ತಿದ್ದನು.",
			"22": "<sup>22</sup> ಉಜ್ಜೀಯನ ಉಳಿದ ಪೂರ್ವೋತ್ತರ ಚರಿತ್ರೆಯನ್ನು ಆಮೋಚನ ಮಗನಾದ ಯೆಶಾಯನೆಂಬ ಪ್ರವಾದಿಯು ಬರೆದಿದ್ದಾನೆ.",
			"23": "<sup>23</sup> ಉಜ್ಜೀಯನು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಲು ಅವನ ಶವವನ್ನು ಕುಷ್ಠರೋಗಿಯದೆಂದು ರಾಜಕುಟುಂಬಶ್ಮಶಾನವಿದ್ದ ಹೊಲದಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ಯೋತಾಮನು ಅರಸನಾದನು."
		},
		"27": {
			"1": "<sup>1</sup> ಯೋತಾಮನು ಪಟ್ಟಕ್ಕೆ ಬಂದಾಗ ಇಪ್ಪತ್ತೈದು ವರುಷದವನಾಗಿದ್ದನು. ಅವನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಹದಿನಾರು ವರುಷ ಆಳಿದನು. ಚಾದೋಕನ ಮಗಳಾದ ಯೆರೂಷ ಎಂಬಾಕೆಯು ಇವನ ತಾಯಿ.",
			"2": "<sup>2</sup> ಇವನು ತನ್ನ ತಂದೆಯಾದ ಉಜ್ಜೀಯನಂತೆ ಯೆಹೋವನ ಚಿತ್ತಾನುಸಾರವಾಗಿ ನಡೆದನು; ಆದರೆ [ಅವನಂತೆ] ಯೆಹೋವನ ಆಲಯವನ್ನು ಪ್ರವೇಶಿಸಲಿಲ್ಲ. ಪ್ರಜೆಗಳಾದರೋ ದುರಾಚಾರಿಗಳಾಗಿಯೇ ಇದ್ದರು.",
			"3": "<sup>3</sup> ಯೆಹೋವನ ಆಲಯಕ್ಕೆ ಮೇಲಣ ಹೆಬ್ಬಾಗಲನ್ನು ಇಡಿಸಿ ಓಫೇಲ್ ಗೋಡೆಯ ಹೆಚ್ಚಿನ ಭಾಗವನ್ನು ಕಟ್ಟಿಸಿದವನು ಇವನೇ.",
			"4": "<sup>4</sup> ಇದಲ್ಲದೆ ಇವನು ಯೆಹೂದ ಪರ್ವತಪ್ರದೇಶದಲ್ಲಿ ಪಟ್ಟಣಗಳನ್ನೂ ಕಾಡುಗಳಲ್ಲಿ ದುರ್ಗಗಳನ್ನೂ ಬುರುಜುಗಳನ್ನೂ ಕಟ್ಟಿಸಿದನು.",
			"5": "<sup>5</sup> ಮತ್ತು ಅಮ್ಮೋನಿಯರ ಅರಸನೊಡನೆ ಯುದ್ಧಮಾಡಿ ಅವರನ್ನು ಜಯಿಸಿದನು; ಅಮ್ಮೋನಿಯರು ಆ ವರುಷದಲ್ಲಿ ಇವನಿಗೆ ನೂರು ತಲಾಂತು ಬೆಳ್ಳಿ, ಹತ್ತುಸಾವಿರ ಕೋರ್ ಗೋದಿ, ಹತ್ತು ಸಾವಿರ ಕೋರ್ ಜವೆಗೋದಿ ಇವುಗಳನ್ನು ಕೊಡಬೇಕಾಯಿತು. ಅಮ್ಮೋನಿಯರು ಅವನಿಗೆ ಎರಡನೆಯ ಮತ್ತು ಮೂರನೆಯ ವರುಷಗಳಲ್ಲಿಯೂ ಹೀಗೆಯೇ ಕೊಟ್ಟರು.",
			"6": "<sup>6</sup> ಯೋತಾಮನು ತನ್ನ ದೇವರಾದ ಯೆಹೋವನ ಚಿತ್ತಾನುಸಾರವಾಗಿ ನಡೆದುಕೊಂಡದ್ದರಿಂದ ಬಲವುಳ್ಳವನಾದನು.",
			"7": "<sup>7</sup> ಯೋತಾಮನ ಉಳಿದ ಚರಿತ್ರೆಯೂ ಅವನ ಯುದ್ಧಗಳೂ ಪ್ರವರ್ತನೆಗಳೂ ಇಸ್ರಾಯೇಲ್ಯರ ಮತ್ತು ಯೆಹೂದ್ಯರ ರಾಜಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"8": "<sup>8</sup> ಅವನು ತನ್ನ ಇಪ್ಪತ್ತೈದನೆಯ ವರುಷ ತುಂಬಿದ ಮೇಲೆ ಪಟ್ಟಕ್ಕೆ ಬಂದು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಹದಿನಾರು ವರುಷ ಆಳಿದನು.",
			"9": "<sup>9</sup> ಅವನು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಲು ಅವನ ಶವವನ್ನು ದಾವೀದನಗರದಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ಆಹಾಜನು ಅರಸನಾದನು."
		},
		"28": {
			"1": "<sup>1</sup> ಆಹಾಜನು ಪಟ್ಟಕ್ಕೆ ಬಂದಾಗ ಇಪ್ಪತ್ತು ವರುಷದವನಾಗಿದ್ದನು; ಅವನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಹದಿನಾರು ವರುಷ ಆಳಿದನು. ಅವನು ಯೆಹೋವನ ಚಿತ್ತಾನುಸಾರವಾಗಿ ನಡೆಯಲಿಲ್ಲ.",
			"2": "<sup>2</sup> ತನ್ನ ಪೂರ್ವಿಕನಾದ ದಾವೀದನ ಮಾರ್ಗವನ್ನು ಬಿಟ್ಟು ಇಸ್ರಾಯೇಲ್ ರಾಜರ ಮಾರ್ಗದಲ್ಲಿ ನಡೆದು ಅವರಂತೆ ಬಾಳ್ ದೇವರುಗಳ ಎರಕದ ವಿಗ್ರಹಗಳನ್ನು ಮಾಡಿಸಿದನು.",
			"3": "<sup>3</sup> ಅವನು ಬೆನ್‍ಹಿನ್ನೋಮ್ ಎಂಬ ತಗ್ಗಿನಲ್ಲಿ ಧೂಪ ಹಾಕಿಸಿದ್ದಲ್ಲದೆ ಯೆಹೋವನು ಇಸ್ರಾಯೇಲ್ಯರ ಎದುರಿನಿಂದ ಓಡಿಸಿಬಿಟ್ಟ ಜನಾಂಗಗಳ ಅಸಹ್ಯಕಾರ್ಯಗಳನ್ನು ಅನುಸರಿಸಿ ತನ್ನ ಮಕ್ಕಳನ್ನು ಆಹುತಿಕೊಟ್ಟನು.",
			"4": "<sup>4</sup> ಪೂಜಾಸ್ಥಳಗಳಲ್ಲಿಯೂ ದಿನ್ನೆಗಳ ಮೇಲೂ ಎಲ್ಲಾ ಹಸುರು ಮರಗಳ ಕೆಳಗೂ ಯಜ್ಞಗಳನ್ನೂ ಧೂಪಗಳನ್ನೂ ಸಮರ್ಪಿಸಿದನು.",
			"5": "<sup>5</sup> ಆದದರಿಂದ ಅವನ ದೇವರಾದ ಯೆಹೋವನು ಅವನನ್ನು ಅರಾಮ್ಯರ ಅರಸನ ಕೈಗೆ ಒಪ್ಪಿಸಿದನು; ಅರಾಮ್ಯರು ಅವನನ್ನು ಸೋಲಿಸಿ ಅವನ ಜನರಲ್ಲಿ ದೊಡ್ಡ ಗುಂಪನ್ನು ಸೆರೆಹಿಡಿದು ದಮಸ್ಕಕ್ಕೆ ಒಯ್ದರು. ಇದಲ್ಲದೆ ಅವನು ಇಸ್ರಾಯೇಲ್ ರಾಜನ ಕೈಗೂ ಒಪ್ಪಿಸಲ್ಪಟ್ಟು ಅವನಿಂದಲೂ ಪೂರ್ಣವಾಗಿ ಅಪಜಯಹೊಂದಿದನು.",
			"6": "<sup>6</sup> ರೆಮಲ್ಯನ ಮಗನಾದ ಪೆಕಹ ಎಂಬವನು ಒಂದೇ ದಿವಸ ಯೆಹೂದ್ಯರಲ್ಲಿ ಲಕ್ಷದ ಇಪ್ಪತ್ತು ಸಾವಿರ ಮಂದಿಯನ್ನು ಕೊಲ್ಲಿಸಿದನು. ಇವರೆಲ್ಲರೂ ರಣವೀರರು. ಯೆಹೂದ್ಯರು ತಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಬಿಟ್ಟದ್ದೇ ಇದಕ್ಕೆ ಕಾರಣ.",
			"7": "<sup>7</sup> ರಾಜಪುತ್ರನಾದ ಮಾಸೇಯ, ರಾಜಗೃಹಾಧಿಪತಿಯಾದ ಅಜ್ರೀಕಾಮ್, ರಾಜಪ್ರತಿನಿಧಿಯಾದ ಎಲ್ಕಾನ ಎಂಬವರನ್ನು ಎಫ್ರಾಯೀಮ್ ದೇಶದ ಜಿಕ್ರಿಯೆಂಬ ಶೂರನು ಕೊಂದನು.",
			"8": "<sup>8</sup> ಇದಲ್ಲದೆ ಇಸ್ರಾಯೇಲ್ಯರು ತಮ್ಮ ಬಂಧುಗಳಾದ ಯೆಹೂದ್ಯರಲ್ಲಿ ಎರಡು ಲಕ್ಷ ಮಂದಿ ಹೆಂಗಸರನ್ನೂ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನೂ ಸೆರೆಹಿಡಿದು ದೊಡ್ಡ ಕೊಳ್ಳೆಯನ್ನೂ ಕೂಡಿಸಿಕೊಂಡು ಸಮಾರ್ಯಕ್ಕೆ ಒಯ್ದರು.",
			"9": "<sup>9</sup> ಅಲ್ಲಿ ಯೆಹೋವನ ಒಬ್ಬ ಪ್ರವಾದಿಯಿದ್ದನು; ಅವನ ಹೆಸರು ಓದೇದ್. ಅವನು ಸಮಾರ್ಯಕ್ಕೆ ಬರುತ್ತಿದ್ದ ಸೈನ್ಯದವರನ್ನು ಎದುರುಗೊಂಡು ಅವರಿಗೆ - ನಿಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಯೆಹೋವನು ಯೆಹೂದ್ಯರ ಮೇಲೆ ಕೋಪಗೊಂಡು ಅವರನ್ನು ನಿಮ್ಮ ಕೈಗೆ ಒಪ್ಪಿಸಿಕೊಟ್ಟನಷ್ಟೆ. ಅವರನ್ನು ಸಂಹರಿಸುವದರಲ್ಲಿ ನಿಮ್ಮ ರೌದ್ರವು ಆಕಾಶವನ್ನು ಮುಟ್ಟಿತು.",
			"10": "<sup>10</sup> ಇದು ಸಾಲದೆ ನೀವು ಯೆಹೂದ್ಯರನ್ನೂ ಯೆರೂಸಲೇಮಿನವರನ್ನೂ ಬಲಾತ್ಕರಿಸಿ ದಾಸರನ್ನಾಗಿಯೂ ದಾಸಿಯರನ್ನಾಗಿಯೂ ಮಾಡಿಕೊಳ್ಳಬೇಕೆಂದಿದ್ದೀರಿ. ದೇವರಾದ ಯೆಹೋವನಿಗೆ ವಿರುದ್ಧವಾದ ಅಪರಾಧಗಳು ನಿಮ್ಮಲ್ಲಿಯೂ ಇಲ್ಲವೋ?",
			"11": "<sup>11</sup> ಈಗ ನೀವು ನನ್ನ ಮಾತಿಗೆ ಕಿವಿಗೊಟ್ಟು ನಿಮ್ಮ ಸಹೋದರರಿಂದ ಸೆರೆಯಾಗಿ ತಂದವರನ್ನು ಹಿಂದಕ್ಕೆ ಕಳುಹಿಸಿರಿ; ಯೆಹೋವನ ಕೋಪಾಗ್ನಿಯು ನಿಮ್ಮ ಮೇಲೆ ಉರಿಯುತ್ತಾ ಇದೆ ಅಂದನು.",
			"12": "<sup>12</sup> ಆಗ ಎಫ್ರಾಯೀಮ್ಯರಲ್ಲಿ ಮುಖಂಡರಾಗಿದ್ದ ಯೆಹೋಹಾನಾನನ ಮಗನಾದ ಅಜರ್ಯ, ಮೆಷಿಲ್ಲೇಮೋತನ ಮಗನಾದ ಬೆರೆಕ್ಯ, ಶಲ್ಲೂಮನ ಮಗನಾದ ಹಿಜ್ಕೀಯ, ಹದ್ಲೈಯನ ಮಗನಾದ ಅಮಾಸ ಎಂಬವರು ಯುದ್ಧದಿಂದ ಬರುವವರ ಮುಂದೆ ನಿಂತು ಅವರಿಗೆ -",
			"13": "<sup>13</sup> ನೀವು ಸೆರೆಹಿಡಿದವರನ್ನು ಇಲ್ಲಿಗೆ ತರಬೇಡಿರಿ; ನೀವು ನಮ್ಮ ಪಾಪಾಪರಾಧಗಳ ಜೊತೆಯಲ್ಲಿ ಯೆಹೋವನಿಗೆ ವಿರುದ್ಧವಾದ ಮತ್ತೊಂದು ಅಪರಾಧವನ್ನು ಸೇರಿಸಬೇಕೆಂದಿರುತ್ತೀರಾ? ನಮ್ಮ ಅಪರಾಧವು ದೊಡ್ಡದು; ಇಸ್ರಾಯೇಲ್ಯರ ಮೇಲಿರುವ ದೇವಕೋಪವು ಉಗ್ರವಾಗಿದೆ ಎಂದು ಹೇಳಿದರು.",
			"14": "<sup>14</sup> ಭಟರು ಇದನ್ನು ಕೇಳಿ ಸೆರೆಯವರನ್ನೂ ಕೊಳ್ಳೆಯನ್ನೂ ಅಧಿಪತಿಗಳ ಮತ್ತು ಸಮೂಹದವರ ಮುಂದೆಯೇ ಬಿಟ್ಟು ಬಿಟ್ಟರು.",
			"15": "<sup>15</sup> ಆಗ ಮೇಲೆ ಹೇಳಿದ ಪುರುಷರು ಎದ್ದುಬಂದು ಸೆರೆಯವರಲ್ಲಿ ಬೆತ್ತಲೆಯಾದವರೆಲ್ಲರಿಗೆ ಕೊಳ್ಳೆಯಿಂದ ಉಡುವದಕ್ಕೆ ಬಟ್ಟೆಗಳನ್ನೂ ಕಾಲಿಗೆ ಕೆರಗಳನ್ನೂ ಕೊಟ್ಟು ಅನ್ನಪಾನಗಳನ್ನಿತ್ತು ತೈಲಹಚ್ಚಿ ಬಳಲಿ ಹೋದವರನ್ನು ಕತ್ತೆಗಳ ಮೇಲೆ ಕುಳ್ಳಿರಿಸಿ ಎಲ್ಲರನ್ನೂ ಯೆರಿಕೋವೆಂಬ ಖರ್ಜೂರ ನಗರಕ್ಕೆ ಕರಕೊಂಡುಹೋಗಿ ಅವರ ಬಂಧುಗಳ ಹತ್ತಿರ ಬಿಟ್ಟು ಸಮಾರ್ಯಕ್ಕೆ ಹಿಂದಿರುಗಿದರು.",
			"16": "<sup>16</sup> ಆ ಕಾಲದಲ್ಲಿ ಅರಸನಾದ ಆಹಾಜನು ತನಗೆ ಸಹಾಯಮಾಡಬೇಕೆಂದು ಅಶ್ಶೂರದ ರಾಜರನ್ನು ದೂತರ ಮುಖಾಂತರ ಬೇಡಿಕೊಂಡನು.",
			"17": "<sup>17</sup> ಯಾಕಂದರೆ ಎದೋಮ್ಯರು ಯೆಹೂದ್ಯರ ಮೇಲೆ ತಿರಿಗಿ ಯುದ್ಧಕ್ಕೆ ಬಂದು ಅವರನ್ನು ಸೋಲಿಸಿ ಅನೇಕಾನೇಕರನ್ನು ಸೆರೆಯೊಯ್ದಿದ್ದರು.",
			"18": "<sup>18</sup> ಫಿಲಿಷ್ಟಿಯರು ಯೆಹೂದದ ಇಳಕಲಿನ ಪ್ರದೇಶದ ಮತ್ತು ದಕ್ಷಿಣಪ್ರಾಂತದ ಪಟ್ಟಣಗಳ ಮೇಲೆ ಬಿದ್ದು ಬೇತ್ಷೆಮೆಷ್, ಅಯ್ಯಾಲೋನ್, ಗೆದೇರೋತ್ ಇವುಗಳನ್ನೂ ಸೋಕೋ, ತಿಮ್ನಾ, ಗಿಮ್ಜೋ ಇವುಗಳನ್ನೂ ಇವುಗಳಿಗೆ ಸೇರಿದ ಗ್ರಾಮಗಳನ್ನೂ ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡು ಅವುಗಳಲ್ಲಿ ವಾಸವಾಗಿದ್ದರು.",
			"19": "<sup>19</sup> ಯೆಹೂದ್ಯರನ್ನು ಅಧರ್ಮಕ್ಕೆ ಪ್ರೇರಿಸಿ ಯೆಹೋವನಿಗೆ ದ್ರೋಹಮಾಡಿದ ಇಸ್ರಾಯೇಲ್‍ರಾಜನಾದ ಆಹಾಜನ ನಿಮಿತ್ತವಾಗಿ ಯೆಹೋವನು ಯೆಹೂದ್ಯರನ್ನು ಈ ಪ್ರಕಾರ ತಗ್ಗಿಸಿದನು.",
			"20": "<sup>20</sup> ಅಶ್ಶೂರದ ಅರಸನಾದ ತಿಗ್ಲತ್ಪಿಲೆಸರನು ಬಂದು ಅವನಿಗೆ ನೆರವಾಗುವದಕ್ಕೆ ಬದಲಾಗಿ ಅವನನ್ನು ಕುಗ್ಗಿಸಿದನು.",
			"21": "<sup>21</sup> ಆಹಾಜನು ಯೆಹೋವನ ಆಲಯದ ಅರಮನೆಯ ಪ್ರಭುಗಳ ಆಸ್ತಿಯನ್ನು ಸೂರೆಮಾಡಿಕೊಂಡು ಹೋಗಿ ಅಶ್ಶೂರದ ಅರಸನಿಗೆ ಕಾಣಿಕೆಯನ್ನಾಗಿ ಕೊಟ್ಟರೂ ಅವನಿಂದ ಏನೂ ಸಹಾಯವಾಗಲಿಲ್ಲ.",
			"22": "<sup>22</sup> ಕಷ್ಟ ಬಂದಾಗ ಅರಸನಾದ ಆಹಾಜನು ಯೆಹೋವನಿಗೆ ಮತ್ತಷ್ಟು ದ್ರೋಹಿಯಾದನು.",
			"23": "<sup>23</sup> ಹೇಗಂದರೆ - ಅರಾಮ್‍ರಾಜರ ದೇವತೆಗಳು ಅವರಿಗೆ ಜಯವನ್ನನುಗ್ರಹಿಸಿದವಲ್ಲಾ; ನಾನೂ ಅವುಗಳಿಗೆ ಯಜ್ಞಸಮರ್ಪಿಸುವೆನು, ಆಗ ನನಗೂ ಜಯವಾಗುವದು ಎಂದುಕೊಂಡು ಅವನು ತನ್ನ ಅಪಜಯಕ್ಕೆ ಕಾರಣವಾಗಿದ್ದ ದಮಸ್ಕದ ದೇವತೆಗಳಿಗೆ ಯಜ್ಞಸಮರ್ಪಿಸುವವನಾದನು. ಆ ದೇವತೆಗಳ ದೆಸೆಯಿಂದ ಅವನಿಗೂ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರಿಗೂ ಕೇಡುಂಟಾಯಿತು.",
			"24": "<sup>24</sup> ಇದಲ್ಲದೆ ಆಹಾಜನು ದೇವಾಲಯದ ಸಾಮಾನುಗಳನ್ನು ಕೂಡಿಸಿ ಅವುಗಳನ್ನು ಕತ್ತರಿಸಿಬಿಟ್ಟನು; ಯೆಹೋವನ ಆಲಯದ ಬಾಗಲುಗಳನ್ನು ಮುಚ್ಚಿ ಯೆರೂಸಲೇಮಿನ ಪ್ರತಿಯೊಂದು ಮೂಲೆಯಲ್ಲಿಯೂ ಯಜ್ಞವೇದಿಗಳನ್ನು ಕಟ್ಟಿಸಿದನು.",
			"25": "<sup>25</sup> ಅನ್ಯದೇವತೆಗಳಿಗೆ ಧೂಪಹಾಕುವದಕ್ಕಾಗಿ ಯೆಹೂದದ ಎಲ್ಲಾ ಪಟ್ಟಣಗಳಲ್ಲಿಯೂ ಪೂಜಾಸ್ಥಳಗಳನ್ನು ಏರ್ಪಡಿಸಿದನು. ಹೀಗೆ ತನ್ನ ಪಿತೃಗಳ ದೇವರಾದ ಯೆಹೋವನನ್ನು ರೇಗಿಸಿದನು.",
			"26": "<sup>26</sup> ಆಹಾಜನ ಉಳಿದ ಪೂರ್ವೋತ್ತರ ಚರಿತ್ರೆಯೂ ಅವನ ಎಲ್ಲಾ ಪ್ರವರ್ತನೆಗಳೂ ಯೆಹೂದ್ಯರ ಮತ್ತು ಇಸ್ರಾಯೇಲ್ಯರ ರಾಜಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"27": "<sup>27</sup> ಅವನು ತನ್ನ ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಲು ಅವನನ್ನು ಇಸ್ರಾಯೇಲ್‍ರಾಜಶ್ಮಶಾನದಲ್ಲಿ ಹೂಣಿಡದೆ ಯೆರೂಸಲೇಮ್ ಪಟ್ಟಣದೊಳಗಣ ಒಂದು ಸ್ಥಳದಲ್ಲಿ ಹೂಣಿಟ್ಟರು. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ಹಿಜ್ಕೀಯನು ಅರಸನಾದನು."
		},
		"29": {
			"1": "<sup>1</sup> ಹಿಜ್ಕೀಯನು ಪಟ್ಟಕ್ಕೆ ಬಂದಾಗ ಇಪ್ಪತ್ತೈದು ವರುಷದವನಾಗಿದ್ದನು; ಅವನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಇಪ್ಪತ್ತೊಂಭತ್ತು ವರುಷ ಆಳಿದನು. ಜೆಕರ್ಯನ ಮಗಳಾದ ಅಬೀಯ ಎಂಬಾಕೆಯು ಅವನ ತಾಯಿ.",
			"2": "<sup>2</sup> ಅವನು ಎಲ್ಲಾ ವಿಷಯಗಳಲ್ಲಿಯೂ ತನ್ನ ಪೂರ್ವಿಕನಾದ ದಾವೀದನಂತೆ ಯೆಹೋವನ ಚಿತ್ತಾನುಸಾರವಾಗಿ ನಡೆದನು.",
			"3": "<sup>3</sup> ಅವನು ತನ್ನ ಆಳಿಕೆಯ ಮೊದಲನೆಯ ವರುಷದ ಮೊದಲನೆಯ ತಿಂಗಳಲ್ಲಿ ಯೆಹೋವನ ಆಲಯದ ಬಾಗಲುಗಳನ್ನು ತೆರೆಯಿಸಿ ಅವುಗಳನ್ನು ಭದ್ರಪಡಿಸಿದನು.",
			"4": "<sup>4</sup> ಆಮೇಲೆ ಯಾಜಕರನ್ನೂ ಲೇವಿಯರನ್ನೂ [ದೇವಾಲಯದ] ಮೂಡಣದಿಕ್ಕಿನ ಬೈಲಿನಲ್ಲಿ ಕೂಡಿಸಿ ಅವರಿಗೆ -",
			"5": "<sup>5</sup> ಲೇವಿಯರೇ, ನನ್ನ ಮಾತನ್ನು ಕೇಳಿರಿ; ಈಗ ನಿಮ್ಮನ್ನು ಶುದ್ಧಪಡಿಸಿಕೊಂಡು ನಿಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಯೆಹೋವನ ಪವಿತ್ರಾಲಯದಲ್ಲಿರುವ ಹೊಲಸನ್ನೆಲ್ಲಾ ಹೊರಗೆ ಹಾಕಿ ಆಲಯವನ್ನು ಶುದ್ಧೀಕರಿಸಿರಿ.",
			"6": "<sup>6</sup> ನಮ್ಮ ಪಿತೃಗಳು ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ದ್ರೋಹ ಮಾಡಿ ಆತನ ಚಿತ್ತಕ್ಕೆ ವಿರುದ್ಧವಾಗಿ ನಡೆದು ಆತನನ್ನು ಬಿಟ್ಟರು. ಅವರು ಆತನ ನಿವಾಸ ಸ್ಥಳಕ್ಕೆ ಬೆನ್ನುಕೊಟ್ಟು ವಿಮುಖರಾದರು.",
			"7": "<sup>7</sup> ಅದರ ಮಂಟಪದ ಬಾಗಲುಗಳನ್ನು ಮುಚ್ಚಿ ದೀಪಗಳನ್ನು ನಂದಿಸಿ ಇಸ್ರಾಯೇಲ್‍ದೇವರ ಪವಿತ್ರಸ್ಥಾನದಲ್ಲಿ ಧೂಪಹಾಕುವದನ್ನೂ ಸರ್ವಾಂಗಹೋಮ ಸಮರ್ಪಿಸುವದನ್ನೂ ನಿಲ್ಲಿಸಿಬಿಟ್ಟರು.",
			"8": "<sup>8</sup> ಆದದರಿಂದ ಯೆಹೋವನು ಯೆಹೂದದೇಶದ ಮೇಲೆಯೂ ಯೆರೂಸಲೇಮಿನ ಮೇಲೆಯೂ ಕೋಪವುಳ್ಳವನಾಗಿ ಅವುಗಳನ್ನು ಭಯವಿಸ್ಮಯ ಪರಿಹಾಸಗಳಿಗೆ ಆಸ್ಪದಮಾಡಿದ್ದಾನೆ; ಇದಕ್ಕೆ ನೀವೇ ಸಾಕ್ಷಿಗಳು.",
			"9": "<sup>9</sup> ಅದೇ ಕಾರಣದಿಂದ ನಮ್ಮ ಹಿರಿಯರು ಕತ್ತಿಯಿಂದ ಹತರಾದರು; ನಮ್ಮ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳೂ ಹೆಂಡತಿಯರೂ ಸೆರೆಯವರಾಗಿ ಹೋಗಬೇಕಾಯಿತು.",
			"10": "<sup>10</sup> ಈಗ ನಮ್ಮ ಮೇಲಿರುವ ಆತನ ಕೋಪಾಗ್ನಿಯು ತೊಲಗಿಹೋಗುವಂತೆ ಇಸ್ರಾಯೇಲ್‍ದೇವರಾದ ಯೆಹೋವನೊಡನೆ ಒಡಂಬಡಿಕೆ ಮಾಡಿಕೊಳ್ಳುವದಕ್ಕೆ ಮನಸ್ಸುಳ್ಳವನಾಗಿದ್ದೇನೆ.",
			"11": "<sup>11</sup> ಹೀಗಿರಲಾಗಿ ನನ್ನ ಮಕ್ಕಳೇ, ಉದಾಸೀನರಾಗಿರಬೇಡಿರಿ; ಯೆಹೋವನು ತನ್ನನ್ನು ಆರಾಧಿಸುವದಕ್ಕೂ ಧೂಪಹಾಕುವದಕ್ಕೂ ನಿಮ್ಮನ್ನು ತನ್ನ ಸಾನ್ನಿಧ್ಯಸೇವಕರನ್ನಾಗಿ ಆರಿಸಿಕೊಂಡನಲ್ಲಾ ಎಂದು ಹೇಳಿದನು.",
			"12": "<sup>12</sup> ಕೂಡಲೆ ಕೆಹಾತ್ಯರಲ್ಲಿ ಅಮಾಸೈಯ ಮಗನಾದ ಮಹತ್, ಅಜರ್ಯನ ಮಗನಾದ ಯೋವೇಲ್, ಮೆರಾರೀಯರಲ್ಲಿ ಅಬ್ದೀಯ ಮಗನಾದ ಕೀಷ್, ಯೆಹಲ್ಲೆಲೇಲನ ಮಗನಾದ ಅಜರ್ಯ, ಗೆರ್ಷೋನ್ಯರಲ್ಲಿ ಜಿಮ್ಮನ ಮಗನಾದ ಯೋವಾಹ, ಯೋವಾಹನ ಮಗನಾದ ಏದೆನ್,",
			"13": "<sup>13</sup> ಎಲೀಚಾಫಾನ್ಯರಲ್ಲಿ ಶಿಮ್ರೀ, ಯೆಗೀಯೇಲ್, ಆಸಾಫ್ಯರಲ್ಲಿ ಜೆಕರ್ಯ, ಮತ್ತನ್ಯ,",
			"14": "<sup>14</sup> ಹೇಮಾನ್ಯರಲ್ಲಿ ಯೆಹೀಯೇಲ್, ಶಿಮ್ಮೀ, ಯೆದೂತೂನ್ಯರಲ್ಲಿ ಶೆಮಾಯ, ಉಜ್ಜೀಯೇಲ್ ಎಂಬ ಲೇವಿಯರು ಎದ್ದು ತಮ್ಮ ಸಹೋದರರನ್ನು ಕೂಡಿಸಿದರು.",
			"15": "<sup>15</sup> ಅವರು ಅರಸನ ಆಜ್ಞಾಧಾರಕರಾಗಿ ಯೆಹೋವನ ಧರ್ಮಶಾಸ್ತ್ರ ವಿಧಿಯ ಮೇರೆಗೆ ತಮ್ಮನ್ನು ಶುದ್ಧಪಡಿಸಿಕೊಂಡು ಯೆಹೋವನ ಆಲಯವನ್ನು ಶುದ್ಧೀಕರಿಸುವದಕ್ಕೆ ಬಂದರು.",
			"16": "<sup>16</sup> ಯಾಜಕರು ಯೆಹೋವನ ಆಲಯವನ್ನು ಶುದ್ಧಮಾಡುವದಕ್ಕೋಸ್ಕರ ಒಳಗೆ ಹೋಗಿ ಪವಿತ್ರಸ್ಥಾನದಲ್ಲಿದ್ದ ಹೊಲಸನ್ನೆಲ್ಲಾ ತಂದು ಪ್ರಾಕಾರದಲ್ಲಿ ಹಾಕಿದರು. ಲೇವಿಯರು ಅದನ್ನು ಕೂಡಿಸಿ ಹೊರಗೆ ಒಯ್ದು ಕಿದ್ರೋನ್ ಹಳ್ಳದಲ್ಲಿ ಹಾಕಿದರು.",
			"17": "<sup>17</sup> ಅವರು ಮೊದಲನೆಯ ತಿಂಗಳಿನ ಮೊದನೆಯ ದಿನದಲ್ಲಿ ಶುದ್ಧೀಕರಣವನ್ನು ಪ್ರಾರಂಭಿಸಿದರು. ಎಂಟನೆಯ ದಿನದಲ್ಲಿ ಯೆಹೋವನ ಆಲಯದ ಮಂಟಪಕ್ಕೆ ಕೈಹಾಕಿದರು. ಯೆಹೋವನ ಆಲಯವನ್ನು ಶುದ್ಧಪಡಿಸುವದರಲ್ಲಿ ಎಂಟು ದಿನಗಳು ಕಳೆದುಹೋದವು. ಮೊದಲನೆಯ ತಿಂಗಳಿನ ಹದಿನಾರನೆಯ ದಿನದಲ್ಲಿ ಕೆಲಸವೆಲ್ಲಾ ತೀರಿತು.",
			"18": "<sup>18</sup> ಆಮೇಲೆ ಅವರು ಅರಮನೆಯೊಳಕ್ಕೆ ಹೋಗಿ ಅರಸನಾದ ಹಿಜ್ಕೀಯನಿಗೆ - ನಾವು ಯೆಹೋವನ ಸಮಸ್ತಾಲಯವನ್ನೂ ಯಜ್ಞವೇದಿಯನ್ನೂ ಅದರ ಉಪಕರಣಗಳನ್ನೂ ನೈವೇದ್ಯವಾದ ರೊಟ್ಟಿಗಳನ್ನಿಡತಕ್ಕ ಮೇಜನ್ನೂ ಅದರ ಸಾಮಾನುಗಳನ್ನೂ ಶುದ್ಧಮಾಡಿದೆವು.",
			"19": "<sup>19</sup> ಅರಸನಾದ ಆಹಾಜನು ತನ್ನ ಆಳಿಕೆಯಲ್ಲಿ ದೇವದ್ರೋಹಿಯಾಗಿ ತಳ್ಳಿಬಿಟ್ಟ ಪಾತ್ರೆಗಳನ್ನು ತಿರಿಗಿ ತಂದಿಟ್ಟು ಪ್ರತಿಷ್ಠಿಸಿದ್ದೇವೆ; ಅವು ಈಗ ಯೆಹೋವನ ಯಜ್ಞವೇದಿಯ ಮುಂದಿರುತ್ತವೆ ಅಂದರು.",
			"20": "<sup>20</sup> ಅರಸನಾದ ಹಿಜ್ಕೀಯನು [ಮರುದಿನ] ಬೆಳಗಿನ ಜಾವದಲ್ಲೆದ್ದು ಪಟ್ಟಣದ ಮುಖಂಡರನ್ನು ಕೂಡಿಸಿ ಅವರೊಡನೆ ಯೆಹೋವನ ಆಲಯಕ್ಕೆ ಹೋದನು.",
			"21": "<sup>21</sup> ಅವರು ಸರಕಾರ ದೇವಾಲಯ ಯೆಹೂದಪ್ರಜೆ ಇವುಗಳಿಗೋಸ್ಕರ ದೋಷಪರಿಹಾರಕಯಜ್ಞಮಾಡುವದಕ್ಕಾಗಿ ಹೋರಿ ಟಗರು ಕುರಿಮರಿ ಹೋತ ಇವುಗಳಲ್ಲಿ ಏಳೇಳನ್ನು ತಂದರು. ಅರಸನು ಯಾಜಕರಾದ ಆರೋನನ ಸಂತಾನದವರಿಗೆ ಯೆಹೋವನ ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಸರ್ವಾಂಗಹೋಮಮಾಡಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"22": "<sup>22</sup> ಹೋರಿಗಳನ್ನು ವಧಿಸಿದ ಮೇಲೆ ಯಾಜಕರು ರಕ್ತವನ್ನು ಕೂಡಿಸಿ ಯಜ್ಞವೇದಿಗೆ ಎರಚಿದರು. ಅನಂತರ ಟಗರುಗಳನ್ನು ವಧಿಸಿ ಅವುಗಳ ರಕ್ತವನ್ನು ಯಜ್ಞವೇದಿಗೆ ಎರಚಿದರು. ಆಮೇಲೆ ಕುರಿಮರಿಗಳನ್ನು ವಧಿಸಿ ಅವುಗಳ ರಕ್ತವನ್ನೂ ಯಜ್ಞವೇದಿಗೆ ಎರಚಿದರು.",
			"23": "<sup>23</sup> ಕಡೆಯಲ್ಲಿ ದೋಷಪರಿಹಾರಕಯಜ್ಞಕ್ಕಾಗಿ ತಂದ ಹೋತಗಳನ್ನು ಅರಸನ ಮುಂದೆಯೂ ಸಮೂಹದವರ ಮುಂದೆಯೂ ನಿಲ್ಲಿಸಿದರು. ಅವರು ಅವುಗಳ ಮೇಲೆ ತಮ್ಮ ಕೈಗಳನ್ನಿಟ್ಟನಂತರ",
			"24": "<sup>24</sup> ಯಾಜಕರು ಅವುಗಳನ್ನು ವಧಿಸಿ ಅವುಗಳ ರಕ್ತವನ್ನು ಸಮಸ್ತ ಇಸ್ರಾಯೇಲ್ಯರ ದೋಷಪರಿಹಾರಕ್ಕಾಗಿ ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಸುರಿದರು. ಆ ಸರ್ವಾಂಗಹೋಮಗಳೂ ದೋಷಪರಿಹಾರಕ ಯಜ್ಞಗಳೂ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರಿಗೋಸ್ಕರ ಆಗಬೇಕೆಂದು ಅರಸನು ಆಜ್ಞಾಪಿಸಿದ್ದನು.",
			"25": "<sup>25</sup> ಇದಲ್ಲದೆ ಅವನು ತಾಳ ಸ್ವರಮಂಡಲ ಕಿನ್ನರಿ ಇವುಗಳಿಂದ ಭಜಿಸುವದಕ್ಕೋಸ್ಕರ ಲೇವಿಯರನ್ನು ದಾವೀದ, ರಾಜದರ್ಶಿಯಾದ ಗಾದ್, ಪ್ರವಾದಿಯಾದ ನಾತಾನ್ ಇವರ ಆಜ್ಞಾನುಸಾರವಾಗಿ ಯೆಹೋವನ ಆಲಯದಲ್ಲಿ ಇರಿಸಿದನು. ಯೆಹೋವನು ತಾನೇ ಪ್ರವಾದಿಗಳ ಮುಖಾಂತರ ಹೀಗೆ ಆಜ್ಞಾಪಿಸಿದ್ದನು.",
			"26": "<sup>26</sup> ಆ ಲೇವಿಯರು ದಾವೀದನ ವಾದ್ಯಗಳನ್ನೂ ಯಾಜಕರು ತುತೂರಿಗಳನ್ನೂ ಹಿಡಿದು ನಿಂತಿರಲು ಹಿಜ್ಕೀಯನು ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಸರ್ವಾಂಗಹೋಮವನ್ನು ಸಮರ್ಪಿಸುವದಕ್ಕೆ ಆಜ್ಞಾಪಿಸಿದನು.",
			"27": "<sup>27</sup> ಸರ್ವಾಂಗಹೋಮಸಮರ್ಪಣೆಯು ಪ್ರಾರಂಭಿಸಿದೊಡನೆ ಇಸ್ರಾಯೇಲ್‍ರಾಜನಾದ ದಾವೀದನ ವಾದ್ಯಗಳಿಂದ ಯೆಹೋವನನ್ನು ಭಜಿಸುವದೂ ತುತೂರಿಯೂದುವದೂ ಆರಂಭವಾಯಿತು.",
			"28": "<sup>28</sup> ಸರ್ವಾಂಗಹೋಮ ಸಮರ್ಪಣೆಯು ತೀರುವವರೆಗೆ ಸರ್ವಸಮೂಹದವರು ಸಾಷ್ಟಾಂಗನಮಸ್ಕಾರ ಮಾಡುತ್ತಲೂ ಗಾಯಕರು ಗಾನಮಾಡುತ್ತಲೂ ತುತೂರಿಯೂದುವವರು ತುತೂರಿಯೂದುತ್ತಲೂ ಇದ್ದರು.",
			"29": "<sup>29</sup> ಸರ್ವಾಂಗಹೋಮಸಮರ್ಪಣೆಯು ತೀರಿದ ಮೇಲೆ ಅರಸನೂ ಅವನ ಜೊತೆಯಲ್ಲಿದ್ದವರೆಲ್ಲರೂ ಸಾಷ್ಟಾಂಗನಮಸ್ಕಾರಮಾಡಿದರು.",
			"30": "<sup>30</sup> ಅರಸನಾದ ಹಿಜ್ಕೀಯನೂ ಪ್ರಭುಗಳೂ ದಾವೀದನ ಮತ್ತು ದರ್ಶಿಯಾದ ಆಸಾಫನ ಕೀರ್ತನೆಗಳಿಂದ ಯೆಹೋವನನ್ನು ಸ್ತುತಿಸಬೇಕೆಂದು ಲೇವಿಯರಿಗೆ ಆಜ್ಞಾಪಿಸಲು ಅವರು ಉತ್ಸಾಹದಿಂದ ಸ್ತುತಿಸುತ್ತಾ ಬೊಗ್ಗಿಕೊಂಡು ನಮಸ್ಕರಿಸಿದರು.",
			"31": "<sup>31</sup> ಆಮೇಲೆ ಹಿಜ್ಕೀಯನು ಸಮೂಹದವರಿಗೆ - ನೀವು ಈಗ ಯೆಹೋವನಿಗೋಸ್ಕರ ನಿಮ್ಮನ್ನು ಪ್ರತಿಷ್ಠಿಸಿಕೊಂಡಿರಲ್ಲಾ; ಹತ್ತಿರ ಬಂದು ಯೆಹೋವನ ಆಲಯಕ್ಕೋಸ್ಕರ ಸಮಾಧಾನಯಜ್ಞಗಳನ್ನೂ ಕೃತಜ್ಞತಾ ಯಜ್ಞಗಳನ್ನೂ ಸಮರ್ಪಿಸಿರಿ ಅನ್ನಲು ಸಮೂಹದವರು ಸಮಾಧಾನಯಜ್ಞಗಳನ್ನೂ ಕೃತಜ್ಞತಾಯಜ್ಞಗಳನ್ನೂ ಸಮರ್ಪಿಸಿದ್ದಲ್ಲದೆ ಅನೇಕರು ಸ್ವೇಚ್ಫೆಯಿಂದ ಸರ್ವಾಂಗಹೋಮಗಳನ್ನೂ ಸಮರ್ಪಿಸಿದರು.",
			"32": "<sup>32</sup> ಸಮೂಹದವರು ಸರ್ವಾಂಗಹೋಮಕ್ಕೋಸ್ಕರ ತಂದೊಪ್ಪಿಸಿದ ಹೋರಿಗಳು ಎಪ್ಪತ್ತು, ಟಗರುಗಳು ನೂರು, ಕುರಿಮರಿಗಳು ಇನ್ನೂರು. ಇವೆಲ್ಲಾ ಯೆಹೋವನಿಗೆ ಸರ್ವಾಂಗಹೋಮಸಮರ್ಪಣೆಗಾಗಿಯೇ.",
			"33": "<sup>33</sup> ದೇವರಿಗೆ ಕಾಣಿಕೆಯಾಗಿ ತರಲ್ಪಟ್ಟ ಹೋರಿಗಳು ಆರು ನೂರು, ಕುರಿಗಳು ಮೂರು ಸಾವಿರ.",
			"34": "<sup>34</sup> ಚರ್ಮವನ್ನು ಸುಲಿಯುವದು [ಶುದ್ಧಿಪಡಿಸಿಕೊಂಡ] ಕೆಲವು ಮಂದಿ ಯಾಜಕರಿಂದ ಆಗದೆ ಹೋಯಿತು. ಆದದರಿಂದ ಮಿಕ್ಕ ಯಾಜಕರು ತಮ್ಮನ್ನು ಶುದ್ಧಿಪಡಿಸಿಕೊಳ್ಳುವವರೆಗೂ ಆ ಕೆಲಸ ತೀರುವ ತನಕ ಅವರ ಬಂಧುಗಳಾದ ಲೇವಿಯರು ಅವರಿಗೆ ಸಹಾಯಮಾಡುತ್ತಿದ್ದರು.",
			"35": "<sup>35</sup> ತಮ್ಮನ್ನು ಶುದ್ಧಪಡಿಸಿಕೊಳ್ಳುವದರಲ್ಲಿ ಯಾಜಕರಿಗಿಂತ ಲೇವಿಯರೇ ಹೆಚ್ಚು ಶ್ರದ್ಧೆಯುಳ್ಳವರಾಗಿದ್ದರು. ಇದಲ್ಲದೆ ಯಾಜಕರು ಸಮರ್ಪಿಸಬೇಕಾಗಿದ್ದ ಸರ್ವಾಂಗಹೋಮ, ಸಮಾಧಾನಯಜ್ಞಗಳ ಕೊಬ್ಬು, ಸರ್ವಾಂಗಹೋಮ ಸಂಬಂಧವಾದ ಪಾನದ್ರವ್ಯ ಇವು ಅಪಾರವಾಗಿದ್ದವು.",
			"36": "<sup>36</sup> ಹೀಗೆ ಯೆಹೋವನ ಆಲಯದ ಸೇವಾಕ್ರಮವು ಪುನಃಸ್ಥಾಪಿತವಾಯಿತು. ಯಾರೂ ನೆನಸದೆ ಇರುವಲ್ಲಿ ದೇವರು ತಾನೇ ತನ್ನ ಪ್ರಜೆಗಳಿಗೋಸ್ಕರ ಈ ಕಾರ್ಯವನ್ನು ನೆರವೇರಿಸಿದ್ದರಿಂದ ಹಿಜ್ಕೀಯನೂ ಎಲ್ಲಾ ಜನರೂ ಸಂತೋಷಿಸಿದರು."
		},
		"30": {
			"1": "<sup>1</sup> ತರುವಾಯ ಹಿಜ್ಕೀಯನು - ಇಸ್ರಾಯೇಲ್ ದೇವರಾದ ಯೆಹೋವನ ಪಸ್ಕವನ್ನಾಚರಿಸುವದಕ್ಕಾಗಿ ಯೆರೂಸಲೇಮಿನಲ್ಲಿರುವ ಯೆಹೋವನ ಆಲಯಕ್ಕೆ ಬರಬೇಕೆಂದು ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರಿಗೂ ಯೆಹೂದ್ಯರಿಗೂ ಎಫ್ರಾಯೀಮ್ಯರಿಗೂ ಮನಸ್ಸೆಯರಿಗೂ ದೂತರ ಮುಖಾಂತರವಾಗಿ ಮತ್ತು ಪತ್ರಗಳ ಮೂಲಕವಾಗಿ ತಿಳಿಯಪಡಿಸಿದನು.",
			"2": "<sup>2</sup> ಯಾಜಕರಲ್ಲಿ ತಕ್ಕಷ್ಟು ಮಂದಿ ತಮ್ಮನ್ನು ಶುದ್ಧಪಡಿಸಿಕೊಂಡಿರಲಿಲ್ಲವಾದದರಿಂದಲೂ ಜನರು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಇನ್ನೂ ಕೂಡಿರಲಿಲ್ಲವಾದದರಿಂದಲೂ ಕೂಡಲೆ ಪಸ್ಕವನ್ನು ಆಚರಿಸಲಾಗದೆಂದು ಅರಸನೂ ಅವನ ಸರದಾರರೂ ಯೆರೂಸಲೇಮಿನ ಸರ್ವಸಂಘದವರೂ ತಿಳಿದು",
			"3": "<sup>3</sup> ಎರಡನೆಯ ತಿಂಗಳಿನಲ್ಲಿ ಅದನ್ನು ಆಚರಿಸಬೇಕೆಂದು ಅಭಿಪ್ರಾಯಪಟ್ಟರು.",
			"4": "<sup>4</sup> ಈ ಅಭಿಪ್ರಾಯವು ಅರಸನಿಗೂ ಸರ್ವಸಮೂಹಕ್ಕೂ ಸರಿಕಂಡದರಿಂದ ಅವರು -",
			"5": "<sup>5</sup> ಜನರು ಇಸ್ರಾಯೇಲ್‍ದೇವರಾದ ಯೆಹೋವನಿಗೋಸ್ಕರ ಪಸ್ಕವನ್ನಾಚರಿಸುವದಕ್ಕಾಗಿ ಯೆರೂಸಲೇಮಿಗೆ ಬರಬೇಕು ಎಂಬದಾಗಿ ಬೇರ್ಷೆಬದಿಂದ ದಾನಿನವರೆಗೂ ವಾಸಿಸುವ ಇಸ್ರಾಯೇಲ್ಯರೊಳಗೆ ಡಂಗುರಹೊಡಿಸಬೇಕೆಂದು ನಿರ್ಣಯಿಸಿದರು. ಜನರಲ್ಲಿ ಹೆಚ್ಚು ಮಂದಿ ಆವರೆಗೂ ಧರ್ಮಶಾಸ್ತ್ರವಿಧಿಯ ಮೇರೆಗೆ ಪಸ್ಕವನ್ನು ಆಚರಿಸಿರಲಿಲ್ಲ.",
			"6": "<sup>6</sup> ಅರಸನ ಮತ್ತು ಅವನ ಸರದಾರರ ಪತ್ರಗಳನ್ನು ತೆಗೆದುಕೊಂಡುಹೋದ ದೂತರು ಇಸ್ರಾಯೇಲ್ ಯೆಹೂದದೇಶಗಳಲ್ಲೆಲ್ಲಾ ಸಂಚರಿಸಿ ರಾಜಾಜ್ಞೆಯಂತೆ ಪ್ರಕಟಿಸಿದ್ದೇನಂದರೆ - ಇಸ್ರಾಯೇಲ್ಯರೇ, ಅಬ್ರಹಾಮ್ ಇಸಾಕ್ ಇಸ್ರಾಯೇಲರ ದೇವರಾದ ಯೆಹೋವನ ಕಡೆಗೆ ತಿರುಗಿಕೊಳ್ಳಿರಿ. ಆಗ ಆತನೂ ನಿಮ್ಮಲ್ಲಿ ಅಶ್ಶೂರದ ಅರಸನ ಕೈಗೆ ಸಿಕ್ಕದಂತೆ ತಪ್ಪಿಸಿಕೊಂಡು ಉಳಿದವರ ಕಡೆಗೆ ತಿರುಗಿಕೊಳ್ಳುವನು.",
			"7": "<sup>7</sup> ನಿಮ್ಮ ತಂದೆಗಳೂ ಸಹೋದರರೂ ನಡೆದಂತೆ ನೀವೂ ನಡೆಯಬೇಡಿರಿ. ಅವರು ತಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ವಿರೋಧವಾಗಿ ದ್ರೋಹಿಗಳಾದದರಿಂದ ಆತನು ಅವರನ್ನು ನಾಶನಕ್ಕೆ ಒಪ್ಪಿಸಿದನು. ಅದಕ್ಕೆ ನೀವೇ ಸಾಕ್ಷಿಗಳು.",
			"8": "<sup>8</sup> ಈಗ ನಿಮ್ಮ ಪಿತೃಗಳಂತೆ ಆಜ್ಞೆಗೆ ಮಣಿಯದವರಾಗಿರಬೇಡಿರಿ, ಯೆಹೋವನಿಗೆ ಅಧೀನರಾಗಿರಿ. ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಸದಾಕಾಲಕ್ಕೂ ಪ್ರತಿಷ್ಠಿಸಿಕೊಂಡ ಪವಿತ್ರಾಲಯಕ್ಕೆ ಬಂದು ಆತನನ್ನು ಸೇವಿಸಿರಿ. ಆಗ ನಿಮ್ಮ ಮೇಲಿರುವ ಆತನ ಉಗ್ರಕೋಪವು ತೊಲಗಿಹೋಗುವದು.",
			"9": "<sup>9</sup> ನೀವು ಯೆಹೋವನ ಕಡೆಗೆ ತಿರುಗಿಕೊಳ್ಳುವದಾದರೆ ನಿಮ್ಮ ಸಹೋದರರೂ ಮಕ್ಕಳೂ ತಮ್ಮನ್ನು ಸೆರೆಯೊಯ್ದವರ ದೃಷ್ಟಿಯಲ್ಲಿ ದಯೆಗೆ ಪಾತ್ರರಾಗಿ ತಿರಿಗಿ ಸ್ವದೇಶಕ್ಕೆ ಬರುವರು. ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ದಯೆಯೂ ಕನಿಕರವೂ ಉಳ್ಳವನಾಗಿರುತ್ತಾನೆ. ಆತನು ತನ್ನ ಕಡೆಗೆ ತಿರುಗಿಕೊಳ್ಳುವ ನಿಮ್ಮನ್ನು ಕಟಾಕ್ಷಿಸದೆ ಇರುವದಿಲ್ಲ ಎಂಬದೇ.",
			"10": "<sup>10</sup> ದೂತರು ಎಫ್ರಾಯೀಮ್ ಮನಸ್ಸೆ ಜೆಬುಲೂನ್ ಪ್ರಾಂತಗಳಲ್ಲಿ ಪಟ್ಟಣದಿಂದ ಪಟ್ಟಣಕ್ಕೆ ಹೋಗುತ್ತಿರಲು ಜನರು ಅವರನ್ನು ಕಂಡು ನಕ್ಕು ಗೇಲಿಮಾಡಿದರು.",
			"11": "<sup>11</sup> ಕೆಲವು ಮಂದಿ ಆಶೇರ್ಯರೂ ಮನಸ್ಸೆಯವರೂ ಜೆಬುಲೂನ್ಯರೂ ಮಾತ್ರ ತಮ್ಮನ್ನು ತಗ್ಗಿಸಿಕೊಂಡು ಯೆರೂಸಲೇಮಿಗೆ ಬಂದರು.",
			"12": "<sup>12</sup> ಯೆಹೂದ್ಯರಾದರೋ ದೇವರ ಕೃಪಾಹಸ್ತ ಪ್ರೇರಣೆಯಿಂದ ಏಕಮನಸ್ಸುಳ್ಳವರಾಗಿ ಯೆಹೋವನ ಧರ್ಮಶಾಸ್ತ್ರನುಸಾರ ಅರಸನಿಂದಲೂ ಪ್ರಭುಗಳಿಂದಲೂ ಹೊರಟ ಆಜ್ಞೆಯನ್ನು ಕೈಕೊಂಡರು.",
			"13": "<sup>13</sup> ಹೀಗೆ ಅನೇಕಾನೇಕ ಜನರು ಎರಡನೆಯ ತಿಂಗಳಿನಲ್ಲಿ ಹುಳಿಯಿಲ್ಲದ ರೊಟ್ಟಿಗಳ ಹಬ್ಬವನ್ನಾಚರಿಸುವದಕ್ಕಾಗಿ ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಕೂಡಿದರು; ಆ ಕೂಟವು ಮಹಾಸಮೂಹವಾಗಿತ್ತು.",
			"14": "<sup>14</sup> ಅವರು ಯೆರೂಸಲೇಮಿನಲ್ಲಿದ್ದ ಯಜ್ಞವೇದಿಗಳನ್ನೂ ಎಲ್ಲಾ ಧೂಪವೇದಿಗಳನ್ನೂ ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ಕಿದ್ರೋನ್‍ಹಳ್ಳದಲ್ಲಿ ಬೀಸಾಟುಬಿಟ್ಟರು.",
			"15": "<sup>15</sup> ಆಮೇಲೆ ಎರಡನೆಯ ತಿಂಗಳಿನ ಹದಿನಾಲ್ಕನೆಯ ದಿನದಲ್ಲಿ ಪಸ್ಕಹಬ್ಬಕ್ಕಾಗಿ ಕುರಿಮರಿಗಳನ್ನು ವಧಿಸಿದರು. ಯಾಜಕರೂ ಲೇವಿಯರೂ [ಮೊದಲನೆಯ ಸಾರಿ ತಪ್ಪಿದ್ದಕ್ಕಾಗಿ] ನಾಚಿಕೆಪಟ್ಟು ತಮ್ಮನ್ನು ಶುದ್ಧಿಪಡಿಸಿಕೊಂಡು ಯೆಹೋವನ ಆಲಯದಲ್ಲಿ ಸರ್ವಾಂಗಹೋಮಗಳನ್ನು ಸಮರ್ಪಿಸುತ್ತಾ",
			"16": "<sup>16</sup> ತಮ್ಮ ತಮ್ಮ ಸ್ಥಳಗಳಲ್ಲಿ ನಿಂತು ದೇವರ ಮನುಷ್ಯನಾದ ಮೋಶೆಯ ಧರ್ಮಶಾಸ್ತ್ರದಿಂದ ತಮಗೆ ನೇಮಕವಾದ ಸೇವೆಯನ್ನು ಮಾಡುತ್ತಿದ್ದರು.",
			"17": "<sup>17</sup> ಯಾಜಕರು ಲೇವಿಯರ ಕೈಯಿಂದ ರಕ್ತವನ್ನು ತೆಗೆದುಕೊಂಡು ಅದನ್ನು ಪ್ರೋಕ್ಷಿಸಿದರು. ಸಮೂಹದಲ್ಲಿ ಅನೇಕರು ತಮ್ಮನ್ನು ಶುದ್ಧಿಪಡಿಸಿಕೊಳ್ಳದೆ ಇದ್ದದರಿಂದ ಅಶುದ್ಧರಾದವರೆಲ್ಲರ ಯಜ್ಞಪಶುಗಳು ಯೆಹೋವನಿಗೆ ಮೀಸಲಾಗುವಂತೆ ಅವುಗಳನ್ನು ಲೇವಿಯರೇ ವಧಿಸಿದರು.",
			"18": "<sup>18</sup> ಜನರಲ್ಲಿ ಅನೇಕಾನೇಕರು ಅಂದರೆ ಹೆಚ್ಚಾಗಿ ಎಫ್ರಾಯೀಮ್ ಮನಸ್ಸೆ ಇಸ್ಸಾಕಾರ್ ಜೆಬುಲೂನ್ ಪ್ರಾಂತಗಳವರು ತಮ್ಮನ್ನು ಶುದ್ಧಿಪಡಿಸಿಕೊಳ್ಳದೆ ಧರ್ಮಶಾಸ್ತ್ರಕ್ಕೆ ವಿರುದ್ಧವಾದ ರೀತಿಯಿಂದ ಪಸ್ಕಭೋಜನವನ್ನು ಮಾಡಿದರು. ಆದರೆ ಹಿಜ್ಕೀಯನು - ಯೆಹೋವನೇ, ದಯಾಪರನಾದ ದೇವರೇ, ದೇವಾಲಯಯಾತ್ರಿಕರಿಗಿರಬೇಕಾದ ಪರಿಶುದ್ಧತ್ವವು ಅನೇಕರಲ್ಲಿ ಇಲ್ಲದಿದ್ದರೂ",
			"19": "<sup>19</sup> ಪಿತೃಗಳ ದೇವರಾದ ನಿನ್ನನ್ನು ಹುಡುಕಬೇಕೆಂದು ಮನಸ್ಸು ಮಾಡಿರುವವರಿಗೆಲ್ಲಾ ಕ್ಷಮೆಯನ್ನು ಅನುಗ್ರಹಿಸು ಎಂದು ವಿಜ್ಞಾಪನೆ ಮಾಡಿದ್ದರಿಂದ",
			"20": "<sup>20</sup> ಯೆಹೋವನು ಹಿಜ್ಕೀಯನ ಪ್ರಾರ್ಥನೆಯನ್ನು ಲಾಲಿಸಿ ಜನರನ್ನು ಸ್ವಸ್ಥಗೊಳಿಸಿದನು.",
			"21": "<sup>21</sup> ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಕೂಡಿಬಂದ ಇಸ್ರಾಯೇಲ್ಯರು ಏಳು ದಿನಗಳವರೆಗೂ ಮಹಾ ಸಂತೋಷದಿಂದ ಹುಳಿಯಿಲ್ಲದ ರೊಟ್ಟಿಗಳ ಹಬ್ಬವನ್ನು ಆಚರಿಸುತ್ತಾ ಇದ್ದರು. ಲೇವಿಯರೂ ಯಾಜಕರೂ ಮಹಾವಾದ್ಯದೊಡನೆ ಯೆಹೋವನನ್ನು ಪ್ರತಿದಿನವೂ ಕೀರ್ತಿಸುತ್ತಾ ಇದ್ದರು.",
			"22": "<sup>22</sup> ಹಿಜ್ಕೀಯನು ಯೆಹೋವನ [ಭಜನೆಯಲ್ಲಿ] ಗಟ್ಟಿಗರಾಗಿದ್ದ ಎಲ್ಲಾ ಲೇವಿಯರೊಡನೆ ಪ್ರೀತಿಯಿಂದ ಮಾತಾಡಿದನು. ನೇಮಕವಾದ ಏಳು ದಿವಸಗಳವರೆಗೂ ಜನರು ಸಮಾಧಾನಯಜ್ಞಗಳನ್ನರ್ಪಿಸುತ್ತಾ ತಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಯೆಹೋವನನ್ನು ತಮ್ಮ ದೇವರೆಂದು ಕೊಂಡಾಡುತ್ತಾ ಔತಣ ಮಾಡಿದರು.",
			"23": "<sup>23</sup> ಯೆಹೂದದ ಅರಸನಾದ ಹಿಜ್ಕೀಯನು ಸಾವಿರ ಹೋರಿಗಳನ್ನೂ ಏಳು ಸಾವಿರ ಕುರಿಗಳನ್ನೂ ಪ್ರಭುಗಳು ಸಾವಿರ ಹೋರಿಗಳನ್ನೂ ಹತ್ತು ಸಾವಿರ ಕುರಿಗಳನ್ನೂ ತಮಗೆ ಯಜ್ಞಕ್ಕಾಗಿ ದಾನಮಾಡಿದ್ದರಿಂದಲೂ ಯಾಜಕರಲ್ಲಿ ಬಹು ಮಂದಿ ತಮ್ಮನ್ನು ಶುದ್ಧಿಪಡಿಸಿಕೊಂಡಿದ್ದರಿಂದಲೂ",
			"24": "<sup>24</sup> ಸಮೂಹದವರು ಇನ್ನೂ ಏಳು ದಿವಸ ಹಬ್ಬಮಾಡಬೇಕೆಂದು ಗೊತ್ತುಮಾಡಿಕೊಂಡು ಆ ಏಳು ದಿನಗಳಲ್ಲಿಯೂ ಬಹಳವಾಗಿ ಸಂತೋಷದಿಂದ ಹಬ್ಬವನ್ನು ಆಚರಿಸಿದರು.",
			"25": "<sup>25</sup> ಯೆಹೂದ್ಯರ ಸರ್ವಸಮೂಹದವರೂ ಯಾಜಕರೂ ಲೇವಿಯರೂ ಇಸ್ರಾಯೇಲ್‍ಪ್ರಾಂತಗಳಿಂದ ನೆರೆದುಬಂದವರೆಲ್ಲರೂ ಇಸ್ರಾಯೇಲ್ ಯೆಹೂದ ಪ್ರಾಂತಗಳಲ್ಲಿದ್ದ ಪ್ರವಾಸಿಗಳೂ ಈ ಉತ್ಸವದಲ್ಲಿ ಪಾಲುಗಾರರಾಗಿದ್ದರು.",
			"26": "<sup>26</sup> ದಾವೀದನ ಮಗನೂ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನೂ ಆದ ಸೊಲೊಮೋನನ ಕಾಲದಿಂದ ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಅಂಥ ಉತ್ಸವವು ನಡೆದಿರಲಿಲ್ಲವಾದದರಿಂದ ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಬಹುಸಂತೋಷವುಂಟಾಯಿತು.",
			"27": "<sup>27</sup> ಆಮೇಲೆ ಯಾಜಕರಾಗಿದ್ದ ಲೇವಿಯರು ಎದ್ದು ನಿಂತು ಜನರನ್ನು ಆಶೀರ್ವದಿಸಿದರು. ಅವರ ಸ್ವರವು [ದೇವರಿಗೆ] ಕೇಳಿಸಿತು; ಅವರ ಪ್ರಾರ್ಥನೆಯು ಪರಲೋಕದಲ್ಲಿರುವ ಆತನ ಪರಿಶುದ್ಧ ನಿವಾಸಕ್ಕೆ ಮುಟ್ಟಿತು."
		},
		"31": {
			"1": "<sup>1</sup> ಇದಾದನಂತರ ನೆರೆದುಬಂದ ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರೂ ಯೆಹೂದ ದೇಶದ ಪಟ್ಟಣಗಳಿಗೆ ಹೋಗಿ ಕಲ್ಲು ಕಂಬಗಳನ್ನು ಒಡೆದು ಅಶೇರವಿಗ್ರಹಸ್ತಂಭಗಳನ್ನು ಕಡಿದುಹಾಕಿ ಪೂಜಾಸ್ಥಳಗಳನ್ನೂ ಯಜ್ಞವೇದಿಗಳನ್ನೂ ಹಾಳುಮಾಡಿಬಿಟ್ಟರು. ಯೆಹೂದ ಬೆನ್ಯಾಮೀನ್ ಪ್ರಾಂತಗಳಲ್ಲಲ್ಲದೆ ಎಫ್ರಾಯೀಮ್ ಮನಸ್ಸೆ ಪ್ರಾಂತಗಳಲ್ಲಿಯೂ ಯಾವದೊಂದನ್ನೂ ಉಳಿಸಲಿಲ್ಲ. ಆಮೇಲೆ ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರೂ ತಮ್ಮ ತಮ್ಮ ಸ್ವಾಸ್ತ್ಯವಿರುವ ಪಟ್ಟಣಗಳಿಗೆ ಹೋದರು.",
			"2": "<sup>2</sup> ಹಿಜ್ಕೀಯನು ಯಾಜಕರ ಮತ್ತು ಲೇವಿಯರ ಆಯಾ ವರ್ಗಗಳವರನ್ನು ಅವರವರಿಗೆ ನೇಮಕವಾದ ಸೇವೆಗೆ ಅಂದರೆ ಸರ್ವಾಂಗಹೋಮ ಸಮಾಧಾನಯಜ್ಞ ಇವುಗಳನ್ನು ಸಮರ್ಪಿಸುವದಕ್ಕೂ ಯೆಹೋವನ ಪಾಳೆಯದ ದ್ವಾರಗಳಲ್ಲಿ ಸೇರಿ ಯೆಹೋವನನ್ನು ಆರಾಧಿಸುತ್ತಾ ಕೀರ್ತಿಸುತ್ತಾ ಕೃತಜ್ಞತಾಸ್ತುತಿ ಮಾಡುತ್ತಾ ಇರುವದಕ್ಕೂ ನೇಮಿಸಿದನು.",
			"3": "<sup>3</sup> ಇದಲ್ಲದೆ ಅವನು ಯೆಹೋವನ ಧರ್ಮಶಾಸ್ತ್ರವಿಧಿಗನುಸಾರವಾಗಿ ಪ್ರಾತಃಕಾಲ ಸಾಯಂಕಾಲ ಸಬ್ಬತ್‍ದಿನ ಅಮಾವಾಸ್ಯೆ ಜಾತ್ರೆ ಇವುಗಳಲ್ಲಿ ಸಮರ್ಪಣೆಯಾಗತಕ್ಕ ಸರ್ವಾಂಗಹೋಮಗಳಿಗೋಸ್ಕರ ಆಗಬೇಕಾದದ್ದೆಲ್ಲಾ ತನ್ನ ಆಸ್ತಿಯಿಂದ ಸಲ್ಲಿಸಬೇಕೆಂತಲೂ",
			"4": "<sup>4</sup> ಯಾಜಕರು ಮತ್ತು ಲೇವಿಯರು ಯೆಹೋವ ಧರ್ಮದಲ್ಲಿ ಆಸಕ್ತರಾಗಿರುವದಕ್ಕಾಗಿ ಅವರಿಗೆ ಸಲ್ಲತಕ್ಕ ಜೀವನಾಂಶವನ್ನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ವಾಸಿಸುವ ಜನರು ಕೊಡಬೇಕೆಂತಲೂ ಗೊತ್ತುಮಾಡಿದನು.",
			"5": "<sup>5</sup> ಈ ರಾಜಾಜ್ಞೆಯು ಪ್ರಕಟವಾದ ಕೂಡಲೆ ಇಸ್ರಾಯೇಲ್ಯರು ಧಾನ್ಯ ದ್ರಾಕ್ಷಾರಸ ಎಣ್ಣೆ ಜೇನು ಮುಂತಾದ ಎಲ್ಲಾ ಹುಟ್ಟುವಳಿಯಲ್ಲಿಯೂ ಪ್ರಥಮಫಲವನ್ನು ರಾಶಿರಾಶಿಯಾಗಿ ತಂದುಕೊಟ್ಟರು; ಮತ್ತು ತಮ್ಮ ಎಲ್ಲಾ ಆದಾಯದ ದಶಮಾಂಶವನ್ನೂ ಉದಾರವಾಗಿ ಸಲ್ಲಿಸಿದರು.",
			"6": "<sup>6</sup> ಯೆಹೂದದ ಪಟ್ಟಣಗಳಲ್ಲಿ ವಾಸಿಸುತ್ತಿರುವ ಇಸ್ರಾಯೇಲ್ಯರೂ ಯೆಹೂದ್ಯರೂ ಕುರಿದನಗಳಲ್ಲಿ ದಶಮಾಂಶವನ್ನು ಕೊಟ್ಟದ್ದಲ್ಲದೆ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಣೆಯಾಗತಕ್ಕ ಪರಿಶುದ್ಧದ್ರವ್ಯಗಳಲ್ಲಿಯೂ ದಶಮಾಂಶವನ್ನು ತಂದು ರಾಶಿಮಾಡಿದರು.",
			"7": "<sup>7</sup> ರಾಶಿಮಾಡುವದು ಮೂರನೆಯ ತಿಂಗಳಲ್ಲಿ ಪ್ರಾರಂಭವಾಯಿತು, ಏಳನೆಯ ತಿಂಗಳಲ್ಲಿ ತೀರಿತು.",
			"8": "<sup>8</sup> ಹಿಜ್ಕೀಯನೂ ಪ್ರಭುಗಳೂ ಬಂದು ಆ ರಾಶಿಗಳನ್ನು ನೋಡಿದಾಗ ಯೆಹೋವನನ್ನು ಕೊಂಡಾಡಿ ಆತನ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಹರಸಿದರು.",
			"9": "<sup>9</sup> ಹಿಜ್ಕೀಯನು ಯಾಜಕರ ಮತ್ತು ಲೇವಿಯರ ಹತ್ತಿರ ಆ ರಾಶಿಗಳ ವಿಷಯದಲ್ಲಿ ವಿಚಾರಿಸಲು",
			"10": "<sup>10</sup> ಚಾದೋಕ್ ಸಂತಾನದ ಮಹಾಯಾಜಕನಾದ ಅಜರ್ಯನು ಅವನಿಗೆ - ಜನರು ದೇವರಾದ ಯೆಹೋವನಿಗೋಸ್ಕರ ಪ್ರತ್ಯೇಕಿಸತಕ್ಕದ್ದನ್ನು ಆತನ ಆಲಯಕ್ಕೆ ತಂದಂದಿನಿಂದ ಯೆಹೋವನು ತನ್ನ ಪ್ರಜೆಗೆ ಸಮೃದ್ಧಿಯನ್ನನುಗ್ರಹಿಸಿದ್ದಾನೆ. ನಾವು ಉಂಡು ತೃಪ್ತರಾಗಿ ಇನ್ನೂ ಇಷ್ಟು ದೊಡ್ಡ ರಾಶಿಯನ್ನು ಉಳಿಸಿಕೊಂಡಿರುತ್ತೇವೆ ಅಂದನು.",
			"11": "<sup>11</sup> ಆಗ ಹಿಜ್ಕೀಯನು ಯೆಹೋವನ ಆಲಯದಲ್ಲಿ ಕೊಠಡಿಗಳನ್ನು ಸಿದ್ಧಮಾಡಲಾಜ್ಞಾಪಿಸಿದನು. ಅವು ಸಿದ್ಧವಾದ ಮೇಲೆ",
			"12": "<sup>12</sup> ಜನರು ಕಾಣಿಕೆಗಳನ್ನೂ ತಮ್ಮ ಆದಾಯದ ದಶಮಾಂಶವನ್ನೂ ದೇವರಿಗೋಸ್ಕರ ಪ್ರತ್ಯೇಕಿಸಿದ ಮುಡುಪನ್ನೂ ನಿರ್ವಂಚನೆಯಿಂದ ತಂದು ಅವುಗಳಲ್ಲಿ ಹಾಕುತ್ತಿದ್ದರು. ಈ ಎಲ್ಲಾ ಉಗ್ರಾಣಗಳ ಮೇಲ್ವಿಚಾರಣೆಯಲ್ಲಿ ಲೇವಿಯನಾದ ಕೋನನ್ಯನು ಮುಖ್ಯಸ್ಥನು; ಅವನ ತಮ್ಮನಾದ ಶಿಮ್ಮಿಯು ದ್ವಿತೀಯ ಸ್ಥಾನದವನು.",
			"13": "<sup>13</sup> ಅರಸನಾದ ಹಿಜ್ಕೀಯನ ಮತ್ತು ಯೆಹೋವನ ಆಲಯದ ಅಧಿಪತಿಯಾದ ಅಜರ್ಯನ ನಿಯಮದಿಂದ ಯೆಹೀಯೇಲ್, ಅಜಜ್ಯ, ನಹತ್, ಅಸಾಹೇಲ್, ಯೆರೀಮೋತ್, ಯೋಜಾಬಾದ್, ಎಲೀಯೇಲ್, ಇಸ್ಮಕ್ಯ, ಮಹತ್, ಬೆನಾಯ ಇವರು ಕೋನನ್ಯನ ಮತ್ತು ಅವನ ತಮ್ಮನಾದ ಶಿಮ್ಮಿಯ ಕೈಕೆಳಗಿನ ಪಾರುಪತ್ಯಗಾರರು.",
			"14": "<sup>14</sup> ಮೂಡಣದಿಕ್ಕಿನ ದ್ವಾರಪಾಲಕನಾಗಿದ್ದ ಇಮ್ನನ ಮಗನಾದ ಕೋರೆ ಎಂಬ ಲೇವಿಯನು ಜನರು ಸ್ವೇಚ್ಫೆಯಿಂದ ದೇವರಿಗೆ ಕಾಣಿಕೆಯಾಗಿ ಸಮರ್ಪಿಸುತ್ತಿದ್ದ ವಸ್ತುಗಳ ಮೇಲ್ವಿಚಾರಕನೂ ಯೆಹೋವನಿಗೋಸ್ಕರ ಪ್ರತ್ಯೇಕಿಸಲ್ಪಟ್ಟ ದ್ರವ್ಯ, ಮಹಾಪರಿಶುದ್ಧವಸ್ತು ಇವುಗಳನ್ನು ಹಂಚಿಕೊಡುವವನೂ ಆಗಿದ್ದನು.",
			"15": "<sup>15</sup> ಇವನ ಕೈಕೆಳಗೆ ಏದೆನ್, ಮಿನ್ಯಾಮಿನ್, ಯೇಷೂವ, ಶೆಮಾಯ, ಅಮರ್ಯ, ಶೆಕನ್ಯ ಎಂಬವರಿದ್ದರು. ಇವರು ಯಾಜಕರ ಪಟ್ಟಣಗಳಲ್ಲಿದ್ದು ತಮ್ಮ ಬಂಧುಗಳಿಗೆ ದೊಡ್ಡವರು ಚಿಕ್ಕವರು ಎಂಬ ವ್ಯತ್ಯಾಸ ಮಾಡದೆ ವರ್ಗಗಳ ಪ್ರಕಾರ ಪಾಲುಕೊಡುತ್ತಿದ್ದರು.",
			"16": "<sup>16</sup> (ಆಯಾ ಉದ್ಯೋಗವರ್ಗಗಳಿಗನುಸಾರವಾಗಿ ಅನುದಿನದ ಸೇವೆಗೋಸ್ಕರ ಯೆಹೋವನ ಆಲಯಕ್ಕೆ ಬರತಕ್ಕ ಗಂಡಸರಲ್ಲಿ ಮೂರು ವರುಷ ಮೊದಲುಗೊಂಡು ಹೆಚ್ಚು ವಯಸ್ಸುಳ್ಳವರ ಪಟ್ಟಿಯಲ್ಲಿ ಬರೆಯಲ್ಪಟ್ಟವರಿಗೆ ಮಾತ್ರ ಕೊಡುತ್ತಿರಲಿಲ್ಲ.",
			"17": "<sup>17</sup> ಆ ಪಟ್ಟಿಯಲ್ಲಿ ಗೋತ್ರಗಳಿಗನುಸಾರವಾಗಿಯೂ ಆಯಾ ಉದ್ಯೋಗವರ್ಗಗಳಿಗನುಸಾರವಾಗಿಯೂ ಇಪ್ಪತ್ತು ವರುಷ ಮೊದಲುಗೊಂಡು ಹೆಚ್ಚು ವಯಸ್ಸುಳ್ಳ ಯಾಜಕರು,",
			"18": "<sup>18</sup> ಲೇವಿಯರು, ಅವರವರ ಹೆಂಡತಿಯರು, ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳು ಅಂತು ತಮ್ಮ ಉದ್ಯೋಗದ ನಿಮಿತ್ತ ತಮ್ಮನ್ನು ದೇವರಿಗೆ ಪ್ರತಿಷ್ಠಿಸಿಕೊಂಡ ಸಮೂಹದವರೂ ಅವರಿಗೆ ಸೇರಿದವರೆಲ್ಲರೂ ಲಿಖಿತರಾಗಿದ್ದರು.)",
			"19": "<sup>19</sup> ಇದಲ್ಲದೆ ಆರೋನನ ಸಂತಾನದವರಾದ ಯಾಜಕರ ಆಯಾ ಪಟ್ಟಣಗಳಿಗೆ ಸೇರಿದ ಗೋಮಾಳಗಳಲ್ಲಿದ್ದವರಿಗೆ ಪಾಲುಕೊಡುತ್ತಿದ್ದರು. ಇವರು ಯಾಜಕ ಸಂತಾನದ ಎಲ್ಲಾ ಗಂಡಸರಿಗೂ ಪಟ್ಟಿಯಲ್ಲಿ ಲಿಖಿತರಾದ ಎಲ್ಲಾ ಲೇವಿಯರಿಗೂ ಸಿಕ್ಕತಕ್ಕ ಭಾಗಗಳನ್ನು ಹಂಚಿಕೊಡುವದಕ್ಕೆ ಹೆಸರು ಹೆಸರಾಗಿ ನೇಮಿಸಲ್ಪಟ್ಟ ಪುರುಷರಿದ್ದರು.",
			"20": "<sup>20</sup> ಹಿಜ್ಕೀಯನು ಯೆಹೂದದಲ್ಲೆಲ್ಲಾ ಇದೇ ವ್ಯವಸ್ಥೆಯನ್ನು ಮಾಡಿದನು. ಅವನು ತನ್ನ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಒಳ್ಳೆಯವನೂ ನೀತಿವಂತನೂ ನಂಬಿಗಸ್ತನೂ ಆಗಿ ನಡೆದನು.",
			"21": "<sup>21</sup> ಅವನು ತನ್ನ ದೇವರ ಒಲುಮೆಯನ್ನು ಪಡಕೊಳ್ಳುವದಕ್ಕೋಸ್ಕರ ದೇವಾಲಯದ ಸೇವಾಸಂಬಂಧದಲ್ಲಿಯೂ ಧರ್ಮಶಾಸ್ತ್ರವಿಧಿಗಳ ಸಂಬಂಧದಲ್ಲಿಯೂ ತಾನು ಪ್ರಾರಂಭಿಸಿದ ಎಲ್ಲಾ ಕಾರ್ಯಗಳನ್ನು ಯಥಾರ್ಥಚಿತ್ತದಿಂದ ಮಾಡಿ ಕೊನೆಗಾಣಿಸಿದನು."
		},
		"32": {
			"1": "<sup>1</sup> ಈ ಭಕ್ತಿಕಾರ್ಯಗಳಾದನಂತರ ಅಶ್ಶೂರದ ಅರಸನಾದ ಸನ್ಹೇರೀಬನು ಯುದ್ಧಕ್ಕೆ ಹೊರಟು ಯೆಹೂದದಲ್ಲಿ ನುಗ್ಗಿ ಕೋಟೆಕೊತ್ತಲುಗಳುಳ್ಳ ಪಟ್ಟಣಗಳನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳಬೇಕೆಂದು ಅವುಗಳಿಗೆ ಮುತ್ತಿಗೆಹಾಕಿದನು.",
			"2": "<sup>2</sup> ದೇಶದೊಳಗೆ ನುಗ್ಗಿದ ಸನ್ಹೇರೀಬನು ಯೆರೂಸಲೇಮಿನ ಮೇಲೆಯೂ ಯುದ್ಧಕ್ಕಾಗಿ ಬರಬೇಕೆಂದಿರುವದು ಹಿಜ್ಕೀಯನಿಗೆ ಗೊತ್ತಾಗಲು",
			"3": "<sup>3</sup> ಅವನು ಪಟ್ಟಣದ ಹೊರಗಿರುವ ಎಲ್ಲಾ ಬಾವಿಗಳನ್ನು ಮುಚ್ಚಿಸಬೇಕೆಂದು ಮನಸ್ಸುಮಾಡಿ ತನ್ನ ಸರದಾರರ ಮತ್ತು ಶೂರರ ಮುಂದೆ ಆ ಪ್ರಸ್ತಾಪವನ್ನಿಟ್ಟು ಅವರ ಸಹಾಯವನ್ನು ಪಡಕೊಂಡನು.",
			"4": "<sup>4</sup> ಆಗ ಅನೇಕರು ಸೇರಿ - ಇಲ್ಲಿಗೆ ಬರುವ ಅಶ್ಶೂರದ ರಾಜರಿಗೆ ಬೇಕಾದಷ್ಟು ನೀರು ಯಾಕೆ ಸಿಕ್ಕಬೇಕು ಎಂದುಕೊಂಡು ಎಲ್ಲಾ ಬಾವಿಗಳನ್ನೂ ದೇಶಮಧ್ಯದಲ್ಲಿ ಹರಿಯುವ ಹಳ್ಳವನ್ನೂ ಮುಚ್ಚಿಬಿಟ್ಟರು.",
			"5": "<sup>5</sup> ಇದಲ್ಲದೆ ಅರಸನು ಧೈರ್ಯಗೊಂಡು ಅಲ್ಲಲ್ಲಿ ಬಿದ್ದುಹೋದ ಪೌಳಿಗೋಡೆಯನ್ನು ಜೀರ್ಣೋದ್ಧಾರಮಾಡಿಸಿ ಅದರ ಮೇಲೆ ಬುರುಜುಗಳನ್ನೂ ಹೊರಗೆ ಇನ್ನೊಂದು ಗೋಡೆಯನ್ನೂ ಕಟ್ಟಿಸಿ ದಾವೀದ ನಗರದ ಮಿಲ್ಲೋಕೋಟೆಯನ್ನು ಭದ್ರಪಡಿಸಿ ಅನೇಕಾಯುಧಗಳನ್ನೂ ಗುರಾಣಿಗಳನ್ನೂ ಮಾಡಿಸಿದನು.",
			"6": "<sup>6</sup> ಅನಂತರ ಅವನು ಜನರ ಮೇಲೆ ಸೇನಾಪತಿಗಳನ್ನು ನೇಮಿಸಿ ಅವರನ್ನು ಊರುಬಾಗಲಿನ ಬೈಲಿನಲ್ಲಿ ಕೂಡಿಸಿಕೊಂಡು ಅವರಿಗೆ -",
			"7": "<sup>7</sup> ಶೂರರಾಗಿರಿ, ಧೈರ್ಯದಿಂದಿರಿ; ಅಶ್ಶೂರದ ಅರಸನಿಗೂ ಅವನೊಂದಿಗಿರುವ ಆ ದೊಡ್ಡ ಗುಂಪಿಗೂ ಅಂಜಬೇಡಿರಿ, ಕಳವಳಪಡಬೇಡಿರಿ. ಅವನಿಗಿರುವ ಸಹಾಯಕ್ಕಿಂತ ನಮಗಿರುವ ಸಹಾಯವು ದೊಡ್ಡದು.",
			"8": "<sup>8</sup> ಅವನಿಗಿರುವ ಸಹಾಯವು ಮಾಂಸದ ತೋಳು; ನಮಗಾದರೋ ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನೇ. ಆತನು ನಮಗೆ ನೆರವಾಗಿ ಯುದ್ಧಗಳಲ್ಲಿ ನಮಗೋಸ್ಕರ ಕಾದಾಡುವನು ಎಂದು ಹೇಳಿ ಧೈರ್ಯಪಡಿಸಿದನು. ಜನರು ಯೆಹೂದದ ಅರಸನಾದ ಹಿಜ್ಕೀಯನ ಮಾತುಗಳನ್ನು ಕೇಳಿ ಭರವಸವುಳ್ಳವರಾದರು.",
			"9": "<sup>9</sup> ಇದಾದ ಮೇಲೆ ಸರ್ವಸೇನೆಯೊಡನೆ ಲಾಕೀಷಿನ ಎದುರಿನಲ್ಲಿ ಪಾಳೆಯಮಾಡಿಕೊಂಡಿದ್ದ ಅಶ್ಶೂರದ ಅರಸನಾದ ಸನ್ಹೇರೀಬನು ತನ್ನ ಸೇವಕರನ್ನು ಯೆರೂಸಲೇಮಿಗೆ ಕಳುಹಿಸಿ ಅವರಿಂದ ಯೆಹೂದದ ಅರಸನಾದ ಹಿಜ್ಕೀಯನಿಗೂ ಯೆರೂಸಲೇಮಿನಲ್ಲಿದ್ದ ಎಲ್ಲಾ ಯೆಹೂದ್ಯರಿಗೂ -",
			"10": "<sup>10</sup> ಅಶ್ಶೂರದ ಅರಸನಾದ ಸನ್ಹೇರೀಬನ ಮಾತನ್ನು ಕೇಳಿರಿ. ನೀವು ಯಾವದರ ಮೇಲೆ ಭರವಸವಿಟ್ಟು ಮುತ್ತಲ್ಪಡುವದಕ್ಕಿರುವ ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ನಿಂತಿದ್ದೀರಿ?",
			"11": "<sup>11</sup> ಹಿಜ್ಕೀಯನು - ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಅಶ್ಶೂರದ ಅರಸನ ಕೈಯಿಂದ ನಮ್ಮನ್ನು ರಕ್ಷಿಸುವನು ಎಂಬ ನಂಬಿಕೆಯನ್ನು ನಿಮ್ಮಲ್ಲಿ ಹುಟ್ಟಿಸುವದಕ್ಕೆ ಪ್ರಯತ್ನಿಸುತ್ತಿರುವದು ನಿಮ್ಮನ್ನು ಹಸಿವೆ ನೀರಡಿಕೆಗಳಿಂದ ಸಾಯಿಸುವದಕ್ಕೇ ಅಲ್ಲವೋ?",
			"12": "<sup>12</sup> ಆ ಹಿಜ್ಕೀಯನು ಒಂದೇ ಯಜ್ಞವೇದಿಯ ಮುಂದೆ ಆರಾಧನೆಮಾಡಿ ಅದರ ಮೇಲೆಯೇ ಧೂಪಹಾಕಬೇಕೆಂಬದಾಗಿ ಯೆಹೂದ್ಯರಿಗೂ ಯೆರೂಸಲೇಮಿನವರಿಗೂ ಆಜ್ಞಾಪಿಸಿ ಆತನ ಪೂಜಾಸ್ಥಳಗಳನ್ನೂ ಯಜ್ಞವೇದಿಗಳನ್ನೂ ಹಾಳುಮಾಡಿದನಲ್ಲಾ!",
			"13": "<sup>13</sup> ನಾನೂ ನನ್ನ ತಂದೆತಾತಂದಿರೂ ಎಲ್ಲಾ ದೇಶಗಳ ಜನಾಂಗಗಳಿಗೆ ಮಾಡಿದ್ದನ್ನು ಕೇಳಲಿಲ್ಲವೋ? ಆ ಜನಾಂಗಗಳ ದೇವತೆಗಳಿಗೆ ಅವರ ದೇಶವನ್ನು ನನ್ನ ಕೈಗೆ ಸಿಕ್ಕದಂತೆ ತಪ್ಪಿಸುವದಕ್ಕೆ ಶಕ್ತಿಯಿತ್ತೋ?",
			"14": "<sup>14</sup> ನನ್ನ ತಂದೆತಾತಂದಿರು ನಾಶಮಾಡಿಬಿಟ್ಟ ಆ ಜನಾಂಗಗಳ ದೇವರುಗಳಲ್ಲಿ ಯಾವನು ತನ್ನ ಪ್ರಜೆಯನ್ನು ನನ್ನ ಕೈಯಿಂದ ಬಿಡಿಸುವದಕ್ಕೆ ಸಮರ್ಥನಾಗಿದ್ದನು? ಹಾಗಾದರೆ ನಿಮ್ಮ ದೇವರು ನಿಮ್ಮನ್ನು ನನ್ನ ಕೈಗೆ ಸಿಕ್ಕದಂತೆ ತಪ್ಪಿಸಿ ಕಾಪಾಡುವದಕ್ಕೆ ಶಕ್ತನಾಗಿರುವನೋ?",
			"15": "<sup>15</sup> ನೀವು ಹಿಜ್ಕೀಯನಿಂದ ಮರುಳಾಗಿ ಈ ಪ್ರಕಾರ ಮೋಸಹೋಗಬೇಡಿರಿ. ಅವನನ್ನು ನಂಬಲೂಬೇಡಿರಿ. ಯಾವ ರಾಜ್ಯ ಜನಾಂಗಗಳ ದೇವತೆಯಾದರೂ ತನ್ನ ಪ್ರಜೆಯನ್ನು ನನ್ನ ಮತ್ತು ನನ್ನ ಪೂರ್ವಿಕರ ಕೈಯಿಂದ ಬಿಡಿಸಲಾರದೆ ಹೋದ ಮೇಲೆ ನಿಮ್ಮ ದೇವರಂಥವನು ನಿಮ್ಮನ್ನು ನನ್ನ ಕೈಗೆ ಸಿಕ್ಕದಂತೆ ತಪ್ಪಿಸಬಲ್ಲನೇ ಎಂದು ಹೇಳಿಸಿದನು.",
			"16": "<sup>16</sup> ಸನ್ಹೇರೀಬನು ದೇವರಾದ ಯೆಹೋವನಿಗೂ ಆತನ ಸೇವಕನಾದ ಹಿಜ್ಕೀಯನಿಗೂ ವಿರೋಧವಾದ ಈ ಮೊದಲಾದ ಮಾತುಗಳನ್ನು ತನ್ನ ಸೇವಕರ ಮುಖಾಂತರ ಹೇಳಿ ಕಳುಹಿಸಿದ್ದಲ್ಲದೆ",
			"17": "<sup>17</sup> ಇಸ್ರಾಯೇಲ್ ದೇವರಾದ ಯೆಹೋವನನ್ನು ನಿಂದಿಸಿ ದೂಷಿಸುವದಕ್ಕಾಗಿ - ಅನ್ಯದೇಶಗಳವರ ದೇವರುಗಳು ತಮ್ಮ ಜನರನ್ನು ನನ್ನ ಕೈಯಿಂದ ಬಿಡಿಸಲಾರದೆ ಹೋದಂತೆ ಹಿಜ್ಕೀಯನ ದೇವರೂ ತನ್ನ ಪ್ರಜೆಯನ್ನು ನನ್ನ ಕೈಯಿಂದ ಬಿಡಿಸಲಾರನು ಎಂಬದಾಗಿ ಪತ್ರದಲ್ಲಿ ಬರೆದು ಕಳುಹಿಸಿದನು.",
			"18": "<sup>18</sup> [ಯೆರೂಸಲೇಮಿಗೆ ಬಂದ ಆ ಅಶ್ಶೂರ್ಯರು] ಗೋಡೆಯ ಮೇಲಿದ್ದ ಜನರನ್ನು ಬೆದರಿಸಿ ಅಂಜಿಸಿ ಪಟ್ಟಣವನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವದಕ್ಕಾಗಿ",
			"19": "<sup>19</sup> ಯೆರೂಸಲೇಮಿನ ದೇವರು ಮನುಷ್ಯರ ಕೈಕೆಲಸವಾಗಿರುವ ಅನ್ಯದೇಶಗಳವರ ದೇವತೆಗಳಿಗೆ ಸಮಾನನಾಗಿರುವನೋ ಎಂಬಂತೆ ಆತನ ವಿಷಯದಲ್ಲಿ ಗಟ್ಟಿಯಾಗಿ ಕೂಗುತ್ತಾ ಅವರೊಡನೆ ಯೂದಾಯ ಭಾಷೆಯಲ್ಲಿ ಮಾತಾಡಿದರು.",
			"20": "<sup>20</sup> ಈ ಕಾರಣದಿಂದ ಅರಸನಾದ ಹಿಜ್ಕೀಯನೂ ಆಮೋಚನ ಮಗನಾದ ಯೆಶಾಯನೆಂಬ ಪ್ರವಾದಿಯೂ ಪರಲೋಕಕ್ಕೆ ಮೊರೆಯಿಟ್ಟು ಪ್ರಾರ್ಥಿಸಲು",
			"21": "<sup>21</sup> ಯೆಹೋವನು ದೂತನನ್ನು ಕಳುಹಿಸಿ ಅಶ್ಶೂರದ ಅರಸನ ದಂಡಿನಲ್ಲಿದ್ದ ಎಲ್ಲಾ ಶೂರರನ್ನೂ ನಾಯಕರನ್ನೂ ಅಧಿಪತಿಗಳನ್ನೂ ಸಂಹರಿಸಿದನು; ಅರಸನು ನಾಚಿಕೆಯಿಂದ ತನ್ನ ದೇಶಕ್ಕೆ ಹಿಂದಿರುಗಬೇಕಾಯಿತು. ಅಲ್ಲಿ ಅವನು ತನ್ನ ದೇವರ ಗುಡಿಗೆ ಹೋಗಿದ್ದಾಗ ಅವನ ಸ್ವಂತ ಮಕ್ಕಳೇ ಅವನನ್ನು ಕತ್ತಿಯಿಂದ ಕೊಂದರು.",
			"22": "<sup>22</sup> ಹೀಗೆ ಯೆಹೋವನು ಹಿಜ್ಕೀಯನನ್ನೂ ಯೆರೂಸಲೇಮಿನವರನ್ನೂ ಅಶ್ಶೂರದ ಅರಸನಾದ ಸನ್ಹೇರೀಬನ ಕೈಗೂ ಸುತ್ತಣ ಎಲ್ಲಾ ಶತ್ರುಗಳ ಕೈಗೂ ಸಿಕ್ಕದಂತೆ ತಪ್ಪಿಸಿ ಕಾಪಾಡುತ್ತಿದ್ದನು.",
			"23": "<sup>23</sup> ಅನೇಕರು ಯೆಹೋವನಿಗೋಸ್ಕರ ಕಾಣಿಕೆಗಳನ್ನೂ ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಹಿಜ್ಕೀಯನಿಗೋಸ್ಕರ ಶ್ರೇಷ್ಠ ವಸ್ತುಗಳನ್ನೂ ತೆಗೆದುಕೊಂಡು ಬಂದರು. ಅಂದಿನಿಂದ ಎಲ್ಲಾ ಜನಾಂಗಗಳವರು ಹಿಜ್ಕೀಯನನ್ನು ಬಲು ದೊಡ್ಡವನೆಂದು ಎಣಿಸುತ್ತಿದ್ದರು.",
			"24": "<sup>24</sup> ಆ ಕಾಲದಲ್ಲಿ ಹಿಜ್ಕೀಯನು ಮರಣಕರ ರೋಗದಲ್ಲಿ ಬಿದ್ದನು. ಆಗ ಅವನು ದೇವರನ್ನು ಪ್ರಾರ್ಥಿಸಲು ಆತನು ಸದುತ್ತರವನ್ನು ದಯಪಾಲಿಸಿ ಅವನಿಗೆ ಒಂದು ಗುರುತನ್ನು ಅನುಗ್ರಹಿಸಿದನು.",
			"25": "<sup>25</sup> ಆದರೆ ಹಿಜ್ಕೀಯನು ಈ ಉಪಕಾರಕ್ಕೆ ತಕ್ಕಂತೆ ನಡೆಯದೆ ಅಹಂಕಾರಿಯಾದದರಿಂದ ಯೆಹೂದದ ಮೇಲೆಯೂ ಯೆರೂಸಲೇಮಿನ ಮೇಲೆಯೂ ದೇವಕೋಪವುಂಟಾಯಿತು.",
			"26": "<sup>26</sup> ಹಿಜ್ಕೀಯನು ತನ್ನ ಗರ್ವವನ್ನು ಬಿಟ್ಟು ಯೆರೂಸಲೇಮಿನವರೊಡನೆ ತನ್ನನ್ನು ತಗ್ಗಿಸಿಕೊಂಡದ್ದರಿಂದ ಅವನ ಜೀವಮಾನದಲ್ಲಿ ಯೆಹೋವನ ಕೋಪವು ಅವರ ಮೇಲೆ ಬರಲಿಲ್ಲ.",
			"27": "<sup>27</sup> ಹಿಜ್ಕೀಯನಿಗೆ ಅತ್ಯಧಿಕವಾದ ಧನ ಘನತೆಗಳು ಒದಗಿದವು; ಅವನು ಬೆಳ್ಳಿ ಬಂಗಾರ, ರತ್ನ, ಪರಿಮಳ ದ್ರವ್ಯ, ಗುರಾಣಿ ಮುಂತಾದ ಶ್ರೇಷ್ಠಾಯುಧ ಇವುಗಳನ್ನಿಡುವದಕ್ಕೋಸ್ಕರ ಭಂಡಾರಗಳನ್ನೂ ಧಾನ್ಯ ದ್ರಾಕ್ಷಾರಸ ಎಣ್ಣೆ ಇವುಗಳನ್ನು ಸಂಗ್ರಹಿಸುವದಕ್ಕೋಸ್ಕರ ಉಗ್ರಾಣಗಳನ್ನೂ",
			"28": "<sup>28</sup> ಆಯಾ ಜಾತಿಯ ಪಶುಗಳಿಗೋಸ್ಕರ ಕೊಟ್ಟಿಗೆಗಳನ್ನೂ ಆಡುಕುರಿಗಳಿಗೋಸ್ಕರ ಹಟ್ಟಿಗಳನ್ನೂ ಸಿದ್ಧಮಾಡಿಸಿಕೊಂಡನು.",
			"29": "<sup>29</sup> ಇದಲ್ಲದೆ ಪಟ್ಟಣಗಳನ್ನು ಕಟ್ಟಿಸಿ ದನಕುರಿಗಳ ದೊಡ್ಡ ಹಿಂಡುಗಳನ್ನು ಸಂಪಾದಿಸಿಕೊಂಡನು. ದೇವರು ಅವನಿಗೆ ಕೊಟ್ಟ ಸಂಪತ್ತು ಅಪರಿಮಿತವಾದದ್ದು.",
			"30": "<sup>30</sup> ಮೇಲಣ ಗೀಹೋನ್ ಎಂಬ ಬುಗ್ಗೆಗೆ ಕಟ್ಟೆಕಟ್ಟಿಸಿ ಅದರ ನೀರು ನೆಟ್ಟಗೆ ದಾವೀದನಗರದ ಪಡುವಣ ತಗ್ಗಿಗೆ ಹರಿಯುವಂತೆ ಮಾಡಿದವನು ಹಿಜ್ಕೀಯನೇ. ಅವನು ತನ್ನ ಎಲ್ಲಾ ಕಾರ್ಯಗಳಲ್ಲಿಯೂ ಸಾರ್ಥಕನಾದನು.",
			"31": "<sup>31</sup> ಆದದರಿಂದ ಅವನ ದೇಶದಲ್ಲುಂಟಾದ ಅದ್ಭುತದ ವಿಷಯವನ್ನು ವಿಚಾರಿಸುವದಕ್ಕಾಗಿ ಬಾಬೆಲಿನ ಪ್ರಭುಗಳ ರಾಯಭಾರಿಗಳು ಅವನ ಬಳಿಗೆ ಬಂದಾಗ ದೇವರು ಅವನನ್ನು ಪರೀಕ್ಷಿಸುವದಕ್ಕೂ ಅವನ ಸರ್ವಾಂತರ್ಯವನ್ನು ತಿಳುಕೊಳ್ಳುವದಕ್ಕೂ ಅವನನ್ನು ಕೈಬಿಟ್ಟನು.",
			"32": "<sup>32</sup> ಹಿಜ್ಕೀಯನ ಉಳಿದ ಚರಿತ್ರೆಯೂ ಅವನ ಭಕ್ತಿಕಾರ್ಯಗಳೂ ಆಮೋಚನ ಮಗನಾದ ಯೆಶಾಯನ ದರ್ಶನಗ್ರಂಥದಲ್ಲಿಯೂ ಯೆಹೂದ್ಯರ ಮತ್ತು ಇಸ್ರಾಯೇಲ್ಯರ ರಾಜಗ್ರಂಥದಲ್ಲಿಯೂ ಬರೆದಿರುತ್ತವೆ.",
			"33": "<sup>33</sup> ಹಿಜ್ಕೀಯನು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಲು ಅವನ ಶವವನ್ನು ದಾವೀದವಂಶದವರ ಕುಟುಂಬಶ್ಮಶಾನ ಭೂಮಿಯ ದಿಬ್ಬದ ಮೇಲೆ ಸಮಾಧಿಮಾಡಿದರು. ಅವನ ಉತ್ತರಕ್ರಿಯೆ ನಡೆಯುವಾಗ ಎಲ್ಲಾ ಯೆಹೂದ್ಯರೂ ಯೆರೂಸಲೇಮಿನವರೂ ಅವನನ್ನು ಬಹಳವಾಗಿ ಸನ್ಮಾನಿಸಿದರು. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ಮನಸ್ಸೆಯು ಅರಸನಾದನು."
		},
		"33": {
			"1": "<sup>1</sup> ಮನಸ್ಸೆಯು ಅರಸನಾದಾಗ ಹನ್ನೆರಡು ವರುಷದವನಾಗಿದ್ದನು; ಅವನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಐವತ್ತೈದು ವರುಷ ಆಳಿದನು.",
			"2": "<sup>2</sup> ಯೆಹೋವನು ಇಸ್ರಾಯೇಲ್ಯರ ಎದುರಿನಿಂದ ಹೊರಡಿಸಿಬಿಟ್ಟ ಅನ್ಯಜನಾಂಗಗಳ ಅಸಹ್ಯಕಾರ್ಯಗಳನ್ನು ಅವನು ಅನುಸರಿಸಿ ಆತನ ಚಿತ್ತಕ್ಕೆ ವಿರುದ್ಧವಾಗಿ ನಡೆದನು.",
			"3": "<sup>3</sup> ತನ್ನ ತಂದೆಯಾದ ಹಿಜ್ಕೀಯನು ತೆಗೆದುಹಾಕಿದ ಪೂಜಾಸ್ಥಳಗಳನ್ನು ತಿರಿಗಿ ಸ್ಥಾಪಿಸಿ ಬಾಳ್‍ದೇವತೆಗಳಿಗೋಸ್ಕರ ಯಜ್ಞವೇದಿಗಳನ್ನು ಕಟ್ಟಿಸಿ ಅಶೇರವಿಗ್ರಹ ಸ್ತಂಭಗಳನ್ನು ನಿಲ್ಲಿಸಿ ನಕ್ಷತ್ರಮಂಡಲಕ್ಕೆ ಕೈಮುಗಿದು ಆರಾಧಿಸಿದನು.",
			"4": "<sup>4</sup> ಯೆಹೋವನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ತನ್ನ ಹೆಸರನ್ನು ಸದಾಕಾಲ ಸ್ಥಾಪಿಸುವದಕ್ಕಾಗಿ ಆರಿಸಿಕೊಂಡ",
			"5": "<sup>5</sup> ಆಲಯದ ಎರಡು ಪ್ರಾಕಾರಗಳಲ್ಲಿಯೂ ಸರ್ವನಕ್ಷತ್ರಮಂಡಲಕ್ಕೋಸ್ಕರ ಯಜ್ಞವೇದಿಗಳನ್ನು ಕಟ್ಟಿಸಿದನು.",
			"6": "<sup>6</sup> ಇದಲ್ಲದೆ ಅವನು ತನ್ನ ಮಕ್ಕಳನ್ನು ಬೆನ್‍ಹಿನ್ನೋಮ್ ತಗ್ಗಿನಲ್ಲಿ ಆಹುತಿಕೊಟ್ಟನು. ಕಣಿಹೇಳಿಸುವದು, ಶಕುನ ನೋಡಿಸುವದು, ಯಂತ್ರಮಂತ್ರಗಳನ್ನು ಮಾಡಿಸುವದು, ಸತ್ತವರಲ್ಲಿ ವಿಚಾರಿಸುವವರ ಮತ್ತು ಬೇತಾಳಿಕರ ಬಳಿಕೆಮಾಡುವದು ಇವೇ ಮೊದಲಾದ ದುಷ್ಕೃತ್ಯಗಳಿಂದ ಯೆಹೋವನಿಗೆ ಕೋಪವನ್ನೆಬ್ಬಿಸಿದನು.",
			"7": "<sup>7</sup> ತಾನು ಮಾಡಿಸಿದ ವಿಗ್ರಹಸ್ತಂಭವನ್ನು ದೇವಾಲಯದಲ್ಲಿಡಿಸಿದನು; ಯೆಹೋವನು ಆ ಆಲಯದ ವಿಷಯದಲ್ಲಿ ದಾವೀದನಿಗೂ ಅವನ ಮಗನಾದ ಸೊಲೊಮೋನನಿಗೂ - ಇಸ್ರಾಯೇಲ್ಯರು ಮೋಶೆಯ ಮುಖಾಂತರ ತಮಗೆ ಕೊಡಲ್ಪಟ್ಟ ನನ್ನ ಎಲ್ಲಾ ಧರ್ಮಶಾಸ್ತ್ರವಿಧಿ ನ್ಯಾಯಗಳನ್ನು ಅನುಸರಿಸಿ ನಡೆಯುವದಾದರೆ ನನ್ನ ನಾಮಮಹತ್ತು ಈ ದೇವಾಲಯದಲ್ಲಿಯೂ ಇಸ್ರಾಯೇಲ್ಯರ ಎಲ್ಲಾ ಊರುಗಳೊಳಗೆ ನನಗೆ ಇಷ್ಟವಾಗಿರುವ ಯೆರೂಸಲೇಮಿನಲ್ಲಿಯೂ ಸದಾಕಾಲವಿರುವದು;",
			"8": "<sup>8</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಇನ್ನು ಮುಂದೆ ದೇಶಭ್ರಷ್ಟರಾಗಿರದೆ ತಮ್ಮ ಪಿತೃಗಳಿಗೆ ನೇಮಿಸಲ್ಪಟ್ಟ ದೇಶದಲ್ಲಿಯೇ ವಾಸವಾಗಿರುವಂತೆ ಮಾಡುವೆನು ಎಂದು ಹೇಳಿದ್ದನು.",
			"9": "<sup>9</sup> ಆದರೆ ಯೆಹೂದ್ಯರೂ ಯೆರೂಸಲೇಮಿನವರೂ ಮನಸ್ಸೆಯಿಂದ ಪ್ರೇರಿತರಾಗಿ ಇಸ್ರಾಯೇಲ್ಯರ ಮುಂದೆಯೇ ಯೆಹೋವನಿಂದ ನಾಶಹೊಂದಿದ ಅನ್ಯಜನಾಂಗಗಳಿಗಿಂತಲೂ ದುಷ್ಟರಾದರು.",
			"10": "<sup>10</sup> ಯೆಹೋವನು ಮನಸ್ಸೆಯನ್ನೂ ಅವನ ಪ್ರಜೆಗಳನ್ನೂ ಎಚ್ಚರಿಸಿದರೂ ಅವರು ಲಕ್ಷಿಸಲಿಲ್ಲ.",
			"11": "<sup>11</sup> ಆದದರಿಂದ ಆತನು ಅಶ್ಶೂರದ ಅರಸನ ಸೈನ್ಯಾಧಿಪತಿಗಳನ್ನು ಅವರ ಮೇಲೆ ಬರಮಾಡಿದನು. ಅವರು ಮನಸ್ಸೆಯನ್ನು ಹಿಡಿದು ಅವನಿಗೆ ಕೊಂಡಿಗಳನ್ನು ಸಿಕ್ಕಿಸಿ ಬೇಡಿಹಾಕಿ ಬಾಬೆಲಿಗೆ ಒಯ್ದರು.",
			"12": "<sup>12</sup> ಆದರೆ ಆ ಕಷ್ಟದಲ್ಲಿ ಮನಸ್ಸೆಯು ತನ್ನ ದೇವರಾದ ಯೆಹೋವನ ಪ್ರಸನ್ನತೆಯನ್ನು ಬೇಡಿಕೊಂಡನು. ಅವನು ತನ್ನ ಪಿತೃಗಳ ದೇವರ ಮುಂದೆ ತನ್ನನ್ನು ಬಹಳವಾಗಿ ತಗ್ಗಿಸಿಕೊಂಡು ಆತನನ್ನು ಪ್ರಾರ್ಥಿಸಲು",
			"13": "<sup>13</sup> ಆತನು ಲಾಲಿಸಿ ಅವನಿಗೆ ಸದುತ್ತರವನ್ನು ದಯಪಾಲಿಸಿ ಅವನನ್ನು ತಿರಿಗಿ ಯೆರೂಸಲೇಮಿಗೆ ಬರಮಾಡಿ ಅರಸುತನವನ್ನು ಕೊಟ್ಟನು. ಆಗ ಯೆಹೋವನೇ ದೇವರೆಂಬದು ಮನಸ್ಸೆಗೆ ಮಂದಟ್ಟಾಯಿತು.",
			"14": "<sup>14</sup> ಇದಾದ ಮೇಲೆ ಅವನು ಗೀಹೋನ್ ಬುಗ್ಗೆಯ ಪಡುವಣ ಕಡೆಯಿಂದ ಮೀನು ಬಾಗಲಿನ ಮಾರ್ಗದವರೆಗಿರುವ ತಗ್ಗಿನಲ್ಲಿ ಓಫೆಲ್ ಗುಡ್ಡದ ಸುತ್ತಲೂ ದಾವೀದ ನಗರದ ಹೊರಗಣ ಬಲು ಎತ್ತರವಾದ ಗೋಡೆಯನ್ನು ಕಟ್ಟಿಸಿ ಯೆಹೂದದೇಶದ ಕೋಟೆ ಕೊತ್ತಲುಗಳುಳ್ಳ ಎಲ್ಲಾ ಪಟ್ಟಣಗಳಲ್ಲಿ ಸೇನಾಪತಿಗಳನ್ನಿರಿಸಿದನು.",
			"15": "<sup>15</sup> ಅನ್ಯದೇವತಾಪ್ರತಿಮೆಗಳನ್ನೂ ತಾನು ಮಾಡಿಸಿದ್ದ ಸ್ತಂಭವನ್ನೂ ಯೆಹೋವನ ಆಲಯದಿಂದ ತೆಗಿಸಿ ಯೆಹೋವಮಂದಿರವಿರುವ ಗುಡ್ಡದಲ್ಲಿಯೂ ಯೆರೂಸಲೇಮಿನಲ್ಲಿಯೂ ಕಟ್ಟಿಸಿದ್ದ ಎಲ್ಲಾ ಯಜ್ಞವೇದಿಗಳನ್ನು ಕೆಡವಿ ಪಟ್ಟಣದ ಹೊರಗೆ ಹಾಕಿಸಿದನು.",
			"16": "<sup>16</sup> ಯೆಹೋವನ ಯಜ್ಞವೇದಿಯನ್ನು ತಿರಿಗಿ ಕಟ್ಟಿಸಿ ಅದರ ಮೇಲೆ ಸಮಾಧಾನಯಜ್ಞಗಳನ್ನೂ ಕೃತಜ್ಞತಾಯಜ್ಞಗಳನ್ನೂ ಅರ್ಪಿಸಿ ಇಸ್ರಾಯೇಲ್‍ದೇವರಾದ ಯೆಹೋವನನ್ನೇ ಸೇವಿಸಬೇಕೆಂದು ಯೆಹೂದ್ಯರಿಗೆ ಆಜ್ಞಾಪಿಸಿದನು.",
			"17": "<sup>17</sup> ಅವರು ಈ ಪ್ರಕಾರ ದೇವರಾದ ಯೆಹೋವನೊಬ್ಬನಿಗೇ ಯಜ್ಞವನ್ನು ಅರ್ಪಿಸುವವರಾದರೂ ಆ ಯಜ್ಞಗಳನ್ನು ಇನ್ನೂ ಪೂಜಾಸ್ಥಳಗಳಲ್ಲಿಯೇ ಸಮರ್ಪಿಸುತ್ತಿದ್ದರು.",
			"18": "<sup>18</sup> ಮನಸ್ಸೆಯ ಉಳಿದ ಚರಿತ್ರೆಯೂ ಅವನು ತನ್ನ ದೇವರಿಗೆ ಮಾಡಿದ ಪ್ರಾರ್ಥನೆಯೂ ದರ್ಶಿಗಳು ಇಸ್ರಾಯೇಲ್‍ದೇವರಾದ ಯೆಹೋವನ ಹೆಸರಿನಲ್ಲಿ ಅವನಿಗೆ ಹೇಳಿದ ಮಾತುಗಳೂ ಇಸ್ರಾಯೇಲ್‍ರಾಜರ ವೃತ್ತಾಂತದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"19": "<sup>19</sup> ಅವನ ದೇವಪ್ರಾರ್ಥನೆಯೂ ಅವನಿಗೆ ದೊರಕಿದ ಸದುತ್ತರವೂ ಅವನು ತನ್ನನ್ನು ತಗ್ಗಿಸಿಕೊಳ್ಳುವದಕ್ಕಿಂತ ಮುಂಚೆ ಮಾಡಿದ ಅಪರಾಧದ್ರೋಹಗಳೂ ಅವನು ಅಲ್ಲಲ್ಲಿ ಪೂಜಾಸ್ಥಳಗಳನ್ನು ಏರ್ಪಡಿಸಿ ಅಶೇರಸ್ತಂಭ ವಿಗ್ರಹ ಇವುಗಳನ್ನು ನಿಲ್ಲಿಸಿದ್ದೂ ದರ್ಶಿಗಳ ಚರಿತ್ರೆಯಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"20": "<sup>20</sup> ಮನಸ್ಸೆಯು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಲು ಅವನ ಶವವನ್ನು ಅರಮನೆಯ ನಿವೇಶನದಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ಆಮೋನನು ಅರಸನಾದನು.",
			"21": "<sup>21</sup> ಆಮೋನನು ಅರಸನಾದಾಗ ಇಪ್ಪತ್ತೆರಡು ವರುಷದವನಾಗಿದ್ದನು; ಅವನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಎರಡು ವರುಷ ಆಳಿದನು.",
			"22": "<sup>22</sup> ಅವನು ತನ್ನ ತಂದೆಯಾದ ಮನಸ್ಸೆಯ ಹಾಗೆಯೇ ಯೆಹೋವನ ಚಿತ್ತಕ್ಕೆ ವಿರುದ್ಧವಾಗಿ ನಡೆದನು. ತನ್ನ ತಂದೆಯಾದ ಮನಸ್ಸೆಯು ಮಾಡಿಸಿದ್ದ ಎಲ್ಲಾ ವಿಗ್ರಹಗಳಿಗೆ ಯಜ್ಞವನ್ನರ್ಪಿಸಿ ಅವುಗಳನ್ನು ಪೂಜಿಸಿದನು.",
			"23": "<sup>23</sup> ಆಮೋನನ ತಂದೆಯಾದ ಮನಸ್ಸೆಯು ಯೆಹೋವನ ಮುಂದೆ ತನ್ನನ್ನು ತಗ್ಗಿಸಿಕೊಂಡಿದ್ದನಷ್ಟೆ. ಆಮೋನನಾದರೋ ತನ್ನನ್ನು ತಗ್ಗಿಸಿಕೊಳ್ಳದೆ ಮಹಾಪರಾಧಿಯಾದನು.",
			"24": "<sup>24</sup> ಅವನ ಸೇವಕರು ಅವನಿಗೆ ವಿರೋಧವಾಗಿ ಒಳಸಂಚುಮಾಡಿ ಅವನನ್ನು ಅರಮನೆಯಲ್ಲಿಯೇ ಕೊಂದರು.",
			"25": "<sup>25</sup> ಆದರೆ ದೇಶದ ಜನರು ಅರಸನಾದ ಆಮೋನನಿಗೆ ವಿರೋಧವಾಗಿ ಒಳಸಂಚುಮಾಡಿದವರನ್ನೆಲ್ಲಾ ಕೊಂದುಹಾಕಿ ಆಮೋನನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ಯೋಷೀಯನನ್ನು ಅರಸನನ್ನಾಗಿ ಮಾಡಿದರು."
		},
		"34": {
			"1": "<sup>1</sup> ಯೋಷೀಯನು ಅರಸನಾದಾಗ ಎಂಟು ವರುಷದವನಾಗಿದ್ದನು; ಅವನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಮೂವತ್ತೊಂದು ವರುಷ ಆಳಿದನು.",
			"2": "<sup>2</sup> ಅವನು ಎಡಕ್ಕಾಗಲಿ ಬಲಕ್ಕಾಗಲಿ ತಿರುಗಿಕೊಳ್ಳದೆ ತನ್ನ ಪೂರ್ವಿಕನಾದ ದಾವೀದನ ಮಾರ್ಗದಲ್ಲೇ ಯೆಹೋವನ ಚಿತ್ತಾನುಸಾರವಾಗಿ ನಡೆದನು.",
			"3": "<sup>3</sup> ಅವನು ತನ್ನ ಆಳಿಕೆಯ ಎಂಟನೆಯ ವರುಷದಲ್ಲಿ ಇನ್ನೂ ಯೌವನಸ್ಥನಾಗಿರುವಾಗಲೇ ತನ್ನ ಪೂರ್ವಿಕನಾದ ದಾವೀದನ ದೇವರನ್ನು ಹುಡುಕುವವನಾದನು. ಹನ್ನೆರಡನೆಯ ವರುಷ ಯೆಹೂದದಲ್ಲಿಯೂ ಯೆರೂಸಲೇಮಿನಲ್ಲಿಯೂ ಇದ್ದ ಪೂಜಾಸ್ಥಳಗಳನ್ನೂ ಅಶೇರ ಸ್ತಂಭಗಳನ್ನೂ ಕೆತ್ತಿದ ಮತ್ತು ಎರಕದ ವಿಗ್ರಹಗಳನ್ನೂ ತೆಗೆದುಹಾಕಿ ದೇಶವನ್ನು ಶುದ್ಧಪಡಿಸಿದನು.",
			"4": "<sup>4</sup> ಇದಲ್ಲದೆ ಅವನು ಬಾಳ್‍ದೇವತೆಗಳ ಯಜ್ಞವೇದಿಗಳನ್ನು ತನ್ನೆದುರಿನಲ್ಲಿಯೇ ಹಾಳು ಮಾಡಿಸಿ ಅವುಗಳ ಮೇಲಿದ್ದ ಸೂರ್ಯಸ್ತಂಭಗಳನ್ನು ಕಡಿಸಿ ಅಶೇರಸ್ತಂಭ ಕೆತ್ತಿದ ಮತ್ತು ಎರಕದ ವಿಗ್ರಹ ಇವುಗಳನ್ನು ಒಡಿಸಿ ಪುಡಿಪುಡಿಮಾಡಿಸಿ ಅವುಗಳಿಗೆ ಯಜ್ಞವನ್ನರ್ಪಿಸಿದವರ ಸಮಾಧಿಗಳ ಮೇಲೆ ಆ ದೂಳನ್ನು ಚೆಲ್ಲಿಸಿದನು.",
			"5": "<sup>5</sup> ಅವುಗಳ ಪೂಜಾರಿಗಳ ಎಲುಬುಗಳನ್ನು ಆ ಯಜ್ಞವೇದಿಗಳ ಮೇಲೆ ಸುಡಿಸಿ ಯೆಹೂದದ ಮತ್ತು ಯೆರೂಸಲೇಮಿನ ಅಶುದ್ಧತ್ವವನ್ನೆಲ್ಲಾ ಪರಿಹರಿಸಿದನು.",
			"6": "<sup>6</sup> ಮನಸ್ಸೆ ಎಫ್ರಾಯೀಮ್ ಸಿಮೆಯೋನ್ ನಫ್ತಾಲಿ ಪ್ರಾಂತಗಳ ಊರುಗಳ ಸುತ್ತಣ ಹಾಳುಬೀಳುಗಳಲ್ಲಿದ್ದ ಯಜ್ಞವೇದಿಗಳನ್ನು ಕೆಡವಿಸಿ",
			"7": "<sup>7</sup> ಅಶೇರಸ್ತಂಭಗಳನ್ನೂ ವಿಗ್ರಹಗಳನ್ನೂ ಒಡಿಸಿ ಪುಡಿಪುಡಿಮಾಡಿಸಿ ಇಸ್ರಾಯೇಲ್‍ದೇಶದಲ್ಲಿದ್ದ ಸೂರ್ಯಸ್ತಂಭಗಳನ್ನು ಕಡಿಸಿ ಯೆರೂಸಲೇಮಿಗೆ ಹಿಂದಿರುಗಿದನು.",
			"8": "<sup>8</sup> ಅವನು ತನ್ನ ಆಳಿಕೆಯ ಹದಿನೆಂಟನೆಯ ವರುಷದಲ್ಲಿ ದೇಶವನ್ನೂ ದೇವಾಲಯವನ್ನೂ ಶುದ್ಧಿಪಡಿಸಿದ ಮೇಲೆ ತನ್ನ ದೇವರಾದ ಯೆಹೋವನ ಆಲಯವನ್ನು ಜೀರ್ಣೋದ್ಧಾರ ಮಾಡಿಸುವದಕ್ಕೋಸ್ಕರ ಅಚಲ್ಯನ ಮಗನಾದ ಶಾಫಾನನನ್ನೂ ಪುರಾಧಿಕಾರಿಯಾದ ಮಾಸೇಯನನ್ನೂ ಪ್ರಧಾನಮಂತ್ರಿಯಾಗಿದ್ದ ಯೋವಾಹಾಜನ ಮಗನಾದ ಯೋವಾಹನನ್ನೂ ಕಳುಹಿಸಿದನು.",
			"9": "<sup>9</sup> ಇವರು ಮಹಾಯಾಜಕನಾದ ಹಿಲ್ಕೀಯನ ಬಳಿಗೆ ಹೋಗಿ ದೇವಾಲಯಕ್ಕೋಸ್ಕರ ಸಂಗ್ರಹವಾದ ಹಣವನ್ನು ಅಂದರೆ ದ್ವಾರಪಾಲಕರಾದ ಲೇವಿಯರು ಮನಸ್ಸೆ ಎಫ್ರಾಯೀಮ್ ಮುಂತಾದ ಇಸ್ರಾಯೇಲ್ ಕುಲಗಳಲ್ಲಿ ಉಳಿದವರಿಂದಲೂ ಎಲ್ಲಾ ಯೆಹೂದ ಬೆನ್ಯಾಮೀನ್ಯರಿಂದಲೂ ಯೆರೂಸಲೇಮಿನವರಿಂಲೂ ಕೂಡಿಸಿದ್ದ ಹಣವನ್ನು",
			"10": "<sup>10</sup> ದೇವಾಲಯದ ಕೆಲಸಮಾಡಿಸುವವರಿಗೆ ಕೊಡುವದಕ್ಕೋಸ್ಕರ ಅವನಿಗೆ ಒಪ್ಪಿಸಿದರು.",
			"11": "<sup>11</sup> ದೇವಾಲಯದ ಕೆಲಸಮಾಡಿಸುತ್ತಿದ್ದವರು ಆ ಹಣವನ್ನು ಕೆತ್ತಿದ ಕಲ್ಲು, ಜೋಡಿಸತಕ್ಕ ತೊಲೆ ಇವುಗಳನ್ನು ಕೊಂಡುಕೊಳ್ಳುವದಕ್ಕಾಗಿಯೂ ಯೆಹೂದರಾಜರ ಅಲಕ್ಷ್ಯದಿಂದ ಹಾಳಾಗಿದ್ದ ಕಟ್ಟಡಗಳಿಗೆ ಮಾಳಿಗೆ ಹಾಕುವದಕ್ಕಾಗಿಯೂ ಅಂತು ದೇವಾಲಯವನ್ನು ಜೀರ್ಣೋದ್ಧಾರಮಾಡಿ ಭದ್ರಪಡಿಸುವದಕ್ಕೆ ಬಡಗಿಯವರಿಗೂ ಶಿಲ್ಪಿಯವರಿಗೂ ಕೊಟ್ಟರು.",
			"12": "<sup>12</sup> ಕೆಲಸಮಾಡುವವರು ನಂಬಿಗಸ್ತರಾಗಿದ್ದರು; ಮೆರಾರಿಯರಲ್ಲಿ ಯಹತ್, ಓಬದ್ಯ ಎಂಬ ಲೇವಿಯರೂ ಕೆಹಾತ್ಯರಲ್ಲಿ ಜೆಕರ್ಯ, ಮೆಷುಲ್ಲಾಮ್ ಎಂಬ ಲೇವಿಯರೂ ಅವರ ಮೇಲ್ವಿಚಾರಕರಾಗಿದ್ದರು.",
			"13": "<sup>13</sup> ಗಾಯನ ನಿಪುಣರಾದ ಲೇವಿಯರೆಲ್ಲರೂ ಹೊರೆಹೊರುವವರ ಮೇಲ್ವಿಚಾರಕರಾಗಿದ್ದು ಎಲ್ಲಾ ವಿಧವಾದ ಕೆಲಸಮಾಡುವವರನ್ನು ನಡಿಸುತ್ತಿದ್ದರು; ಎಲ್ಲಾ ಲೇಖಕರೂ ಅಧಿಕಾರಿಗಳೂ ದ್ವಾರಪಾಲಕರೂ ಲೇವಿಯರೇ.",
			"14": "<sup>14</sup> ಯೆಹೋವನ ಆಲಯದಲ್ಲಿ ಸಂಗ್ರಹವಾಗಿದ್ದ ಹಣವನ್ನು ಹೊರಗೆ ತೆಗೆಯುವಾಗ ಮೋಶೆಯ ಮುಖಾಂತರವಾಗಿ ಕೊಡಲ್ಪಟ್ಟ ಯೆಹೋವನ ಧರ್ಮೋಪದೇಶದ ಗ್ರಂಥವು ಯಾಜಕನಾದ ಹಿಲ್ಕೀಯನಿಗೆ ಸಿಕ್ಕಿತು.",
			"15": "<sup>15</sup> ಹಿಲ್ಕೀಯನು ಲೇಖಕನಾದ ಶಾಫಾನನಿಗೆ - ಯೆಹೋವನ ಆಲಯದಲ್ಲಿ ನನಗೆ ಧರ್ಮೋಪದೇಶಗ್ರಂಥವು ಸಿಕ್ಕಿರುತ್ತದೆ ಎಂದು ಹೇಳಿ ಅದನ್ನು ಅವನ ಕೈಯಲ್ಲಿಕೊಟ್ಟನು.",
			"16": "<sup>16</sup> ಶಾಫಾನನು ಆ ಗ್ರಂಥವನ್ನು ಅರಸನ ಬಳಿಗೆ ತೆಗೆದುಕೊಂಡು ಹೋಗಿ - ನೀನು ಆಜ್ಞಾಪಿಸಿದ್ದೆಲ್ಲವನ್ನೂ ನಿನ್ನ ಸೇವಕರು ಮಾಡುತ್ತಿದ್ದಾರೆ.",
			"17": "<sup>17</sup> ದೇವಾಲಯದಲ್ಲಿ ಸಂಗ್ರಹವಾದ ಹಣವನ್ನು ತೆಗೆದು ಕೆಲಸಮಾಡಿಸುವವರಿಗೂ ಮಾಡುವವರಿಗೂ ಒಪ್ಪಿಸಿದರು ಎಂದು ತಿಳಿಸಿದನು.",
			"18": "<sup>18</sup> ಇದಲ್ಲದೆ ಲೇಖಕನಾದ ಶಾಫಾನನು ಅರಸನಿಗೆ - ಯಾಜಕನಾದ ಹಿಲ್ಕೀಯನು ನನಗೊಂದು ಗ್ರಂಥವನ್ನು ಕೊಟ್ಟಿದ್ದಾನೆ ಎಂದು ಹೇಳಿ ಅವನ ಮುಂದೆ ಅದನ್ನು ಓದಿದನು.",
			"19": "<sup>19</sup> ಅರಸನು ಧರ್ಮಶಾಸ್ತ್ರದ ವಾಕ್ಯಗಳನ್ನು ಕೇಳಿದಾಗ ಬಟ್ಟೆಗಳನ್ನು ಹರಿದುಕೊಂಡು",
			"20": "<sup>20</sup> ಹಿಲ್ಕೀಯ, ಶಾಫಾನನ ಮಗನಾದ ಅಹೀಕಾಮ್, ಮೀಕನ ಮಗನಾದ ಅಬ್ದೋನ್, ಲೇಖಕನಾದ ಶಾಫಾನ್, ಸಹಕಾರಿಯಾದ ಅಸಾಯ ಎಂಬವರಿಗೆ -",
			"21": "<sup>21</sup> ನಮ್ಮ ಪೂರ್ವಿಕರು ಈ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುವ ಯೆಹೋವನ ಆಜ್ಞೆಗಳನ್ನೆಲ್ಲಾ ಕೈಕೊಳ್ಳದೆ ಹೋದದರಿಂದ ಯೆಹೋವನು ತನ್ನ ಮಹಾರೌದ್ರವನ್ನು ನಮ್ಮ ಮೇಲೆ ಸುರಿದಿದ್ದಾನೆ; ಆದದರಿಂದ ನೀವು ನನಗೋಸ್ಕರವೂ ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಮತ್ತು ಯೆಹೂದ್ಯರಲ್ಲಿ ಉಳಿದಿರುವವರಿಗೋಸ್ಕರವೂ ಯೆಹೋವನ ಬಳಿಗೆ ಹೋಗಿ ನಮಗೆ ಸಿಕ್ಕಿರುವ ಈ ಗ್ರಂಥದ ವಾಕ್ಯಗಳ ವಿಷಯವಾಗಿ ವಿಚಾರಿಸಿರಿ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"22": "<sup>22</sup> ಆಗ ಹಿಲ್ಕೀಯನೂ ಅರಸನ ಜನರೂ ಹುಲ್ದ ಎಂಬ ಪ್ರವಾದಿನಿಯ ಬಳಿಗೆ ಹೋಗಿ ಅಪ್ಪಣೆಯಾದಂತೆ ಆಕೆಯ ಹತ್ತಿರ ವಿಚಾರಿಸಿದರು. ಹಸ್ರನ ಮೊಮ್ಮಗನೂ ತೊಕ್ಹತನ ಮಗನೂ ರಾಜವಸ್ತ್ರಾಗಾರದ ಅಧಿಪತಿಯೂ ಆಗಿದ್ದ ಶಲ್ಲೂಮನು ಆಕೆಯ ಗಂಡನು. ಆಕೆಯು ಯೆರೂಸಲೇಮಿನ ಎರಡನೆಯ ಕೇರಿಯಲ್ಲಿ ವಾಸವಾಗಿದ್ದಳು.",
			"23": "<sup>23</sup> ಹುಲ್ದಳು ಅವರಿಗೆ - ನಿಮ್ಮನ್ನು ಕಳುಹಿಸಿದವನಿಗೆ ಇಸ್ರಾಯೇಲ್‍ದೇವರಾದ ಯೆಹೋವನ ಮಾತುಗಳನ್ನು ತಿಳಿಸಿರಿ.",
			"24": "<sup>24</sup> ಆತನು - ಈ ದೇಶದ ಮೇಲೆಯೂ ಜನರ ಮೇಲೆಯೂ ಯೆಹೂದದ ಅರಸನ ಮುಂದೆ ಪಾರಾಯಣವಾದ ಆ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುವ ಎಲ್ಲಾ ಶಾಪಗಳನ್ನು ಬರಮಾಡುವೆನು;",
			"25": "<sup>25</sup> ಅವರು ನನ್ನನ್ನು ಬಿಟ್ಟು ಅನ್ಯದೇವತೆಗಳಿಗೆ ಧೂಪಹಾಕಿ ತಮ್ಮ ದುಷ್ಕೃತ್ಯಗಳಿಂದ ನನ್ನನ್ನು ರೇಗಿಸಿದ್ದರಿಂದ ನನ್ನ ಕೋಪಾಗ್ನಿಯು ಈ ದೇಶದ ಮೇಲೆ ಉರಿಯುತ್ತಿರುವದು, ಆರಿಹೋಗುವದಿಲ್ಲ ಅನ್ನುತ್ತಾನೆ.",
			"26": "<sup>26</sup> ತಾನು ಕೇಳಿದ ವಾಕ್ಯಗಳ ವಿಷಯವಾಗಿ ಯೆಹೋವನ ಬಳಿಯಲ್ಲಿ ವಿಚಾರಿಸುವದಕ್ಕೆ ನಿಮ್ಮನ್ನು ಕಳುಹಿಸಿದ ಯೆಹೂದದ ಅರಸನಿಗೆ ನೀವು ತಿಳಿಸಬೇಕಾದ ಮಾತೇನಂದರೆ - ಇಸ್ರಾಯೇಲ್‍ದೇವರಾದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ,",
			"27": "<sup>27</sup> ಈ ದೇಶದ ಮತ್ತು ಅದರ ನಿವಾಸಿಗಳ ವಿಷಯ ನಾನು ಹೇಳಿದ ಮಾತುಗಳನ್ನು ಕೇಳಿದಾಗ ನೀನು ದುಃಖಪಟ್ಟು ದೇವರಾದ ನನ್ನ ಮುಂದೆ ತಗ್ಗಿಸಿಕೊಂಡದ್ದರಿಂದಲೂ ದೀನಮನಸ್ಸಿನಿಂದ ಬಟ್ಟೆಗಳನ್ನು ಹರಿದುಕೊಂಡು ಕಣ್ಣೀರುಸುರಿಸಿದ್ದರಿಂದಲೂ ನಿನ್ನನ್ನು ಲಕ್ಷಿಸಿದೆನು.",
			"28": "<sup>28</sup> ನಾನು ಈ ದೇಶದ ಮೇಲೆಯೂ ಅದರ ನಿವಾಸಿಗಳ ಮೇಲೆಯೂ ಬರಮಾಡುವ ಶಿಕ್ಷೆಗಳಲ್ಲಿ ನೀನು ಒಂದನ್ನೂ ನೋಡದೆ ಸಮಾಧಾನದಿಂದ ಮೃತಿಹೊಂದಿ ಸಮಾಧಿಸೇರುವಂತೆ ಅನುಗ್ರಹಿಸುವೆನು ಎಂಬದೇ ಎಂದು ಹೇಳಿದಳು. ಅವರು ಹಿಂದಿರುಗಿ ಬಂದು ಅರಸನಿಗೆ ಆ ಮಾತುಗಳನ್ನು ತಿಳಿಸಿದರು.",
			"29": "<sup>29</sup> ಅನಂತರ ಅರಸನು ದೂತರ ಮುಖಾಂತರ ಯೆರೂಸಲೇಮಿನ ಮತ್ತು ಯೆಹೂದದ ಎಲ್ಲಾ ಹಿರಿಯರನ್ನು ಕೂಡಿಸಿ",
			"30": "<sup>30</sup> ಯೆರೂಸಲೇಮಿನವರನ್ನೂ ಬೇರೆ ಎಲ್ಲಾ ಯೆಹೂದ್ಯರನ್ನೂ ಯಾಜಕರನ್ನೂ ಲೇವಿಯರನ್ನೂ ಕರಕೊಂಡು ಯೆಹೋವನ ಆಲಯಕ್ಕೆ ಹೋದನು. ದೊಡ್ಡವರು ಮೊದಲುಗೊಂಡು ಚಿಕ್ಕವರವರೆಗೆ ಎಲ್ಲರೂ ಅವನನ್ನು ಹಿಂಬಾಲಿಸಿದರು. ಅಲ್ಲಿ ಅವನು ಎಲ್ಲರಿಗೂ ಕೇಳಿಸುವಂತೆ ಯೆಹೋವನ ಆಲಯದಲ್ಲಿ ಸಿಕ್ಕಿದ ನಿಬಂಧನಗ್ರಂಥವನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ಓದಿಸಿ",
			"31": "<sup>31</sup> ತನ್ನ ಸ್ಥಳದಲ್ಲಿ ನಿಂತು ತಾನು ಯೆಹೋವನ ಮಾರ್ಗದಲ್ಲಿ ನಡೆಯುವದಾಗಿಯೂ ಆತನ ಆಜ್ಞಾನಿಯಮವಿಧಿಗಳನ್ನು ಪೂರ್ಣಮನಸ್ಸಿನಿಂದಲೂ ಪೂರ್ಣಪ್ರಾಣದಿಂದಲೂ ಕೈಕೊಳ್ಳುವದಾಗಿಯೂ ನಿಬಂಧನ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುವ ಎಲ್ಲಾ ವಾಕ್ಯಗಳನ್ನು ನೆರವೇರಿಸುವದಾಗಿಯೂ ಯೆಹೋವನಿಗೆ ಪ್ರಮಾಣ ಮಾಡಿದನು.",
			"32": "<sup>32</sup> ಆಮೇಲೆ ಎಲ್ಲಾ ಯೆರೂಸಲೇಮಿನವರಿಂದಲೂ ಬೆನ್ಯಾಮೀನ್ಯರಿಂದಲೂ ಪ್ರಮಾಣಮಾಡಿಸಿದನು. ಯೆರೂಸಲೇಮಿನವರು ಏಕದೇವರಾದ ತಮ್ಮ ಪಿತೃಗಳ ದೇವರ ನಿಬಂಧನೆಯನ್ನು ಕೈಕೊಳ್ಳುವವರಾದರು.",
			"33": "<sup>33</sup> ಯೋಷೀಯನು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಸೇರಿದ ಎಲ್ಲಾ ಪ್ರಾಂತಗಳೊಳಗಿನ ಅಸಹ್ಯಮೂರ್ತಿಗಳನ್ನೆಲ್ಲಾ ತೆಗೆದುಹಾಕಿಸಿ ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಉಳಿದಿರುವವರು ತಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನೇ ಸೇವಿಸುವಂತೆ ಮಾಡಿದನು. ಅವನ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ಅವರು ತಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಬಿಡದೆ ಹಿಂಬಾಲಿಸಿದರು."
		},
		"35": {
			"1": "<sup>1</sup> ಯೋಷೀಯನು ಯೆಹೋವನಿಗೋಸ್ಕರ ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಪಸ್ಕಹಬ್ಬವನ್ನು ಆಚರಿಸುವಂತೆ ಮಾಡಿದನು; ಮೊದಲನೆಯ ತಿಂಗಳಿನ ಹದಿನಾಲ್ಕನೆಯ ದಿನದಲ್ಲಿ ಪಸ್ಕದ ಕುರಿಗಳನ್ನು ವಧಿಸಿದರು.",
			"2": "<sup>2</sup> ಯೋಷೀಯನು ಯಾಜಕರನ್ನು ಅವರವರ ಕೆಲಸಕ್ಕೆ ನೇಮಿಸಿ ಯೆಹೋವನ ಆಲಯದ ಸೇವೆಗಾಗಿ ಅವರನ್ನು ಪ್ರೋತ್ಸಾಹಪಡಿಸಿ",
			"3": "<sup>3</sup> ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಉಪದೇಶಿಸತಕ್ಕವರೂ ಯೆಹೋವನಿಗೆ ಪ್ರತಿಷ್ಠಿತರೂ ಆದ ಲೇವಿಯರಿಗೆ - ದಾವೀದನ ಮಗನೂ ಇಸ್ರಾಯೇಲ್‍ರಾಜನೂ ಆದ ಸೊಲೊಮೋನನು ಕಟ್ಟಿಸಿದ ದೇವಾಲಯದಲ್ಲಿ ಪವಿತ್ರ ಮಂಜೂಷವನ್ನು ಇಟ್ಟುಬಿಡಿರಿ; ಅದನ್ನು ಹೆಗಲಿನ ಮೇಲೆ ಹೊತ್ತುಕೊಳ್ಳುವದು ಅವಶ್ಯವಿಲ್ಲ; ಇನ್ನು ಮುಂದೆ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನೂ ಆತನ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಾಯೇಲ್ಯರನ್ನೂ ಸೇವಿಸಿರಿ.",
			"4": "<sup>4</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾದ ದಾವೀದನೂ ಅವನ ಮಗನಾದ ಸೊಲೊಮೋನನೂ ಬರೆದು ನೇಮಿಸಿದಂತೆ ನಿಮ್ಮ ನಿಮ್ಮ ಗೋತ್ರ ವರ್ಗಗಳ ಪ್ರಕಾರ ಸಿದ್ಧರಾಗಿದ್ದು",
			"5": "<sup>5</sup> ನಿಮ್ಮ ಸಹೋದರರಾದ ಇಸ್ರಾಯೇಲ್ಯರ ಒಂದೊಂದು ಗೋತ್ರಶಾಖೆಗೋಸ್ಕರ ನಿಮ್ಮಲ್ಲಿ ಒಂದೊಂದು ಗುಂಪಿನವರು ಪವಿತ್ರಸ್ಥಾನದಲ್ಲಿ ನಿಂತು ಪಸ್ಕದ ಕುರಿಯನ್ನು ವಧಿಸಲಿ.",
			"6": "<sup>6</sup> ಮೋಶೆಯಿಂದ ಪ್ರಕಟವಾದ ಯೆಹೋವನ ವಿಧಿಗನುಸಾರವಾಗಿ ನಿಮ್ಮ ಸಹೋದರರು ಪಸ್ಕಭೋಜನ ಮಾಡುವಂತೆ ನೀವು ನಿಮ್ಮನ್ನು ಶುದ್ಧಿಪಡಿಸಿಕೊಂಡು ಅವರಿಗೋಸ್ಕರ ಎಲ್ಲವನ್ನೂ ಸಿದ್ಧಮಾಡಿರಿ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"7": "<sup>7</sup> ಯೋಷೀಯನು ಹಬ್ಬಕ್ಕೆ ಬಂದ ಜನರಿಗೆ ಪಸ್ಕ ಯಜ್ಞಕ್ಕಾಗಿ ರಾಜಕೀಯ ಸೊತ್ತಿನಿಂದ ಮೂವತ್ತು ಸಾವಿರ ಆಡುಮರಿಗಳನ್ನೂ ಕುರಿಮರಿಗಳನ್ನೂ ಮೂರು ಸಾವಿರ ಹೋರಿಗಳನ್ನೂ ದಾನಮಾಡಿದನು.",
			"8": "<sup>8</sup> ಅವನ ಸರದಾರರು ಜನರಿಗೂ ಯಾಜಕರಿಗೂ ಲೇವಿಯರಿಗೂ [ಯಜ್ಞಪಶುಗಳನ್ನು] ಸಂತೋಷವಾಗಿ ಕೊಟ್ಟರು. ದೇವಾಲಯದ ಪ್ರಧಾನರಾದ ಹಿಲ್ಕೀಯ, ಜೆಕರ್ಯ, ಯೆಹೀಯೇಲ್ ಎಂಬವರು ಯಾಜಕರಿಗೆ ಎರಡು ಸಾವಿರದ ಆರು ನೂರು ಪಸ್ಕದ ಕುರಿಮರಿಗಳನ್ನೂ ಮುನ್ನೂರು ಹೋರಿಗಳನ್ನೂ ಕೊಟ್ಟರು.",
			"9": "<sup>9</sup> ಲೇವಿಯರಲ್ಲಿ ಮುಖ್ಯಸ್ಥರಾದ ಕೋನನ್ಯ, ಸೆಮಾಯ, ನೆತನೇಲ್ ಎಂಬ ಅಣ್ಣತಮ್ಮಂದಿರೂ ಹಷಲ್ಯ, ಯೆಗೀಯೇಲ್, ಯೋಜಾಬಾದ್ ಎಂಬವರೂ ಲೇವಿಯರಿಗೆ ಐದು ಸಾವಿರ ಪಸ್ಕದ ಕುರಿಮರಿಗಳನ್ನೂ ಐನೂರು ಹೋರಿಗಳನ್ನೂ ದಾನಮಾಡಿದರು.",
			"10": "<sup>10</sup> ಸೇವೆಯ ಸಂಬಂಧವಾದದ್ದೆಲ್ಲವನ್ನೂ ಏರ್ಪಡಿಸಿದನಂತರ ಯಾಜಕರು ತಮ್ಮ ತಮ್ಮ ಸ್ಥಳಗಳಲ್ಲಿಯೂ ಲೇವಿಯರು ಅರಸನ ಅಪ್ಪಣೆಯಂತೆ ವರ್ಗವರ್ಗಗಳಾಗಿಯೂ ನಿಂತರು;",
			"11": "<sup>11</sup> ಲೇವಿಯರು ಪಸ್ಕದ ಪಶುಗಳನ್ನು ವಧಿಸಿದರು. ಯಾಜಕರು ಇವರ ಕೈಯಿಂದ ರಕ್ತವನ್ನು ತೆಗೆದುಕೊಂಡು ಪ್ರೋಕ್ಷಿಸಿದರು.",
			"12": "<sup>12</sup> ಲೇವಿಯರು ಚರ್ಮಸುಲಿದು ಹೋಮಕ್ಕಾಗಿ ಪ್ರತ್ಯೇಕಿಸತಕ್ಕದ್ದನ್ನು ಪ್ರತ್ಯೇಕಿಸಿ ಜನರಿಗೆ ಆಯಾ ಗೋತ್ರ ಶಾಖೆಗಳಿಗನುಸಾರವಾಗಿ ಕೊಟ್ಟರು. ಇವರು ಅದನ್ನು ಮೋಶೆಯ ಧರ್ಮಶಾಸ್ತ್ರವಿಧಿಯ ಮೇರೆಗೆ ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಿಸಬೇಕಾಯಿತು. ಹೋರಿಗಳ ವಿಷಯದಲ್ಲಿಯೂ ಅದೇ ಕ್ರಮವನ್ನು ಅನುಸರಿಸಿದರು.",
			"13": "<sup>13</sup> ಆಮೇಲೆ ಲೇವಿಯರು ಪಸ್ಕದ ಮಾಂಸವನ್ನು ನಿಯಮದ ಪ್ರಕಾರ ಬೆಂಕಿಯಲ್ಲಿ ಸುಟ್ಟು ದೇವರಿಗೆ ಕಾಣಿಕೆಯಾಗಿ ಸಮರ್ಪಿತವಾದ ಪಶುಗಳ ಮಾಂಸವನ್ನು ಕೊಪ್ಪರಿಗೆ ಬಾನೆ ಹಂಡೆಗಳಲ್ಲಿ ಬೇಯಿಸಿ ಬೇಗನೆ ತಂದು ಜನರಿಗೆ ಬಡಿಸಿದರು.",
			"14": "<sup>14</sup> ತರುವಾಯ ತಮಗೋಸ್ಕರವೂ ಯಾಜಕರಿಗೋಸ್ಕರವೂ ಸಿದ್ಧಮಾಡಿದರು. ಆರೋನನ ಮಕ್ಕಳಾದ ಯಾಜಕರು ರಾತ್ರಿಯವರೆಗೂ ಸರ್ವಾಂಗಹೋಮಗಳನ್ನೂ ಕೊಬ್ಬನ್ನೂ ಸಮರ್ಪಿಸುತ್ತಾ ಇದ್ದದರಿಂದ ಲೇವಿಯರು ತಮಗೋಸ್ಕರ ಹೇಗೋ ಹಾಗೆ ಆರೋನನ ಮಕ್ಕಳಾದ ಯಾಜಕರಿಗೋಸ್ಕರವೂ ಸಿದ್ಧಮಾಡಿದರು.",
			"15": "<sup>15</sup> ಆಸಾಫ್ಯರಾದ ಗಾಯಕರು ಅರಸನಾದ ದಾವೀದನ ಮತ್ತು ಆಸಾಫ್, ಹೇಮಾನ್, ಅರಸನ ದರ್ಶಿಯಾದ ಯೆದುತೂನ್ ಇವರ ಆಜ್ಞಾನುಸಾರ ತಮ್ಮ ಸ್ಥಳದಲ್ಲಿ ನಿಂತಿದ್ದರು; ದ್ವಾರಪಾಲಕರು ಆಯಾ ಬಾಗಲುಗಳನ್ನು ಕಾಯುತ್ತಿದ್ದರು. ಅವರ ಸಹೋದರರಾದ ಲೇವಿಯರು ಅವರ ಭೋಜನಕ್ಕೆ ಸಿದ್ಧಮಾಡುತ್ತಿದ್ದದರಿಂದ ಅವರು ತಮ್ಮ ತಮ್ಮ ಸೇವೆಯನ್ನು ಬಿಡುವದಕ್ಕೆ ಕಾರಣವಿರಲಿಲ್ಲ.",
			"16": "<sup>16</sup> ಈ ಪ್ರಕಾರ ಅರಸನಾದ ಯೋಷೀಯನ ಅಪ್ಪಣೆಯಂತೆ ಪಸ್ಕವನ್ನು ಆಚರಿಸುವದಕ್ಕೂ ಯೆಹೋವನ ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಸರ್ವಾಂಗಹೋಮಗಳನ್ನರ್ಪಿಸುವದಕ್ಕೂ ಯೆಹೋವನ ಸೇವೆಯ ಸಂಬಂಧವಾದದ್ದೆಲ್ಲವೂ ಆ ದಿವಸವೇ ಸಿದ್ಧವಾಯಿತು.",
			"17": "<sup>17</sup> ಕೂಡಿಬಂದಿದ್ದ ಇಸ್ರಾಯೇಲ್ಯರು ಆ ಕಾಲದಲ್ಲಿ ಪಸ್ಕಹಬ್ಬವನ್ನೂ ಏಳು ದಿನಗಳವರೆಗೆ ಹುಳಿಯಿಲ್ಲದ ರೊಟ್ಟಿಗಳ ಜಾತ್ರೆಯನ್ನೂ ಆಚರಿಸಿದರು.",
			"18": "<sup>18</sup> ಇಂಥಾ ಪಸ್ಕಹಬ್ಬವು ಪ್ರವಾದಿಯಾದ ಸಮುವೇಲನ ದಿವಸಗಳಿಂದ ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ನಡೆಯಲೇ ಇಲ್ಲ. ಯೋಷೀಯನೂ ಯಾಜಕರೂ ಲೇವಿಯರೂ ಕೂಡಿಬಂದಿದ್ದ ಇಸ್ರಾಯೇಲ್ಯ ಯೆಹೂದ್ಯರೂ ಯೆರೂಸಲೇಮಿನವರೂ ಪಸ್ಕಹಬ್ಬವನ್ನು ಆಚರಿಸಿದಂತೆ ಇಸ್ರಾಯೇಲ್ ರಾಜರಲ್ಲಿ ಒಬ್ಬನೂ ಆಚರಿಸಲಿಲ್ಲ.",
			"19": "<sup>19</sup> ಈ ಪಸ್ಕಹಬ್ಬವು ಯೋಷೀಯನ ಆಳಿಕೆಯ ಹದಿನೆಂಟನೆಯ ವರುಷದಲ್ಲಿ ನಡೆಯಿತು.",
			"20": "<sup>20</sup> ಯೋಷೀಯನು ದೇವಾಲಯದ ವಿಷಯದಲ್ಲಿ ಈ ಎಲ್ಲಾ ವ್ಯವಸ್ಥೆಯನ್ನು ಮಾಡಿದನಂತರ ಐಗುಪ್ತದ ಅರಸನಾದ ನೆಕೋ ಎಂಬವನು ಯುದ್ಧಮಾಡುವದಕ್ಕೋಸ್ಕರ ಯೂಫ್ರೇಟೀಸ್ ನದಿಯ ತೀರದಲ್ಲಿದ್ದ ಕರ್ಕೆಮೀಷಿಗೆ ಹೋದನು.",
			"21": "<sup>21</sup> ಯೋಷೀಯನು ಅವನಿಗೆ ವಿರೋಧವಾಗಿ ಹೊರಡಲು ನೆಕೋವನು ಅವನ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿ - ಯೆಹೂದದ ಅರಸನೇ, ನನ್ನ ಗೊಡವೆ ನಿನಗೇಕೆ? ನಾನು ಈ ಸಾರಿ ಯುದ್ಧಕ್ಕೆ ಹೊರಟದ್ದು ನಿನಗೆ ವಿರೋಧವಾಗಿ ಅಲ್ಲ, ನನ್ನ ಶತ್ರುವಂಶಕ್ಕೆ ವಿರೋಧವಾಗಿಯೇ. ನನ್ನನ್ನು ಮುಂದರಿಸಬೇಕೆಂದು ದೇವರ ಅಪ್ಪಣೆಯಾಗಿದೆ. ನನ್ನೊಂದಿಗಿರುವ ದೇವರಿಗೆ ವಿರೋಧವಾಗಿ ಕೈಯೆತ್ತುವದನ್ನು ಬಿಡು. ಇಲ್ಲವಾದರೆ ಆತನು ನಿನ್ನನ್ನು ನಾಶಮಾಡುವನು ಎಂದು ಹೇಳಿಸಿದನು.",
			"22": "<sup>22</sup> ಆದರೆ ಯೋಷೀಯನು ಅವನನ್ನು ಬಿಟ್ಟು ಹಿಂದಿರುಗಲಿಲ್ಲ. ದೇವೋಕ್ತಿಯಾಗಿದ್ದ ನೆಕೋವಿನ ಮಾತಿಗೆ ಕಿವಿಗೊಡದೆ ವೇಷಹಾಕಿಕೊಂಡು ಅವನೊಡನೆ ಕಾದುವದಕ್ಕೆ ಮೆಗಿದ್ದೋ ಬೈಲಿಗೆ ಹೋದನು.",
			"23": "<sup>23</sup> ಅಲ್ಲಿ ಬಿಲ್ಲುಗಾರರು ಅರಸನಾದ ಯೋಷೀಯನಿಗೆ ಬಾಣವನ್ನೆಸೆದಾಗ ಅವನು ತನ್ನ ಸೇವಕರಿಗೆ - ನನಗೆ ದೊಡ್ಡ ಗಾಯವಾಯಿತು. ನನ್ನನ್ನು ಆಚೆಗೆ ತೆಗೆದುಕೊಂಡು ಹೋಗಿರಿ ಅಂದನು.",
			"24": "<sup>24</sup> ಸೇವಕರು ಅವನನ್ನು ಯುದ್ಧ ರಥದಿಂದಿಳಿಸಿ ಅವನ ಎರಡನೆಯ ರಥದಲ್ಲಿ ಮಲಗಿಸಿ ಯೆರೂಸಲೇಮಿಗೆ ತೆಗೆದುಕೊಂಡು ಹೋಗಲು ಅಲ್ಲಿ ಸತ್ತನು. ಅವನ ಶವವನ್ನು ಅವನ ಪೂರ್ವಿಕರ ಶ್ಮಶಾನದಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಎಲ್ಲಾ ಯೆಹೂದ್ಯರೂ ಯೆರೂಸಲೇಮಿನವರೂ ಯೋಷೀಯನ ವಿಷಯವಾಗಿ ಗೋಳಾಡಿದರು.",
			"25": "<sup>25</sup> ಯೆರೆಮೀಯನು ಯೋಷೀಯನನ್ನು ಕುರಿತು ಶೋಕಗೀತವನ್ನು ರಚಿಸಿದನು. ಎಲ್ಲಾ ಗಾಯಕರೂ ಗಾಯಕಿಯರೂ ಅವನನ್ನು ಇಂದಿನವರೆಗೆ ತಮ್ಮ ಶೋಕಗೀತಗಳಲ್ಲಿ ವರ್ಣಿಸುತ್ತಿರುತ್ತಾರೆ. ಆ ಗೀತಗಳನ್ನು ಹಾಡುವದು ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಒಂದು ಪದ್ಧತಿಯಾಯಿತು.",
			"26": "<sup>26</sup> ಅವು ಶೋಕಗೀತಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ. ಯೋಷೀಯನ ಉಳಿದ ಚರಿತ್ರೆಯೂ ಯೆಹೋವನ ಧರ್ಮಶಾಸ್ತ್ರಾನುಸಾರವಾದ ಅವನ ಭಕ್ತಿಕಾರ್ಯಗಳೂ",
			"27": "<sup>27</sup> ಅವನ ಪೂರ್ವೋತ್ತರವೃತ್ತಾಂತವೂ ಇಸ್ರಾಯೇಲ್ಯರ ಮತ್ತು ಯೆಹೂದ್ಯರ ರಾಜಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ."
		},
		"36": {
			"1": "<sup>1</sup> ದೇಶದ ಜನರು ಯೋಷೀಯನ ಮಗನಾದ ಯೆಹೋವಾಹಾಜನನ್ನು ತೆಗೆದುಕೊಂಡು ಅವನ ತಂದೆಗೆ ಬದಲಾಗಿ ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಅರಸನನ್ನಾಗಿ ಮಾಡಿದರು.",
			"2": "<sup>2</sup> ಯೆಹೋವಾಹಾಜನು ಪಟ್ಟಕ್ಕೆ ಬಂದಾಗ ಇಪ್ಪತ್ತಮೂರು ವರುಷದವನಾಗಿದ್ದನು; ಅವನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಮೂರು ತಿಂಗಳು ಆಳಿದನು.",
			"3": "<sup>3</sup> ಐಗುಪ್ತದ ಅರಸನು ಯೆರೂಸಲೇಮಿಗೆ ಬಂದು ಅವನನ್ನು ಪಟ್ಟದಿಂದ ತೆಗೆದುಹಾಕಿ ಯೆಹೂದ್ಯರು ತನಗೆ ನೂರು ತಲಾಂತು ಬೆಳ್ಳಿಯನ್ನೂ ಒಂದು ತಲಾಂತು ಬಂಗಾರವನ್ನೂ ದಂಡ ತೆರುವಂತೆ ಮಾಡಿದನು.",
			"4": "<sup>4</sup> ಐಗುಪ್ತದ ಅರಸನಾದ ನೆಕೋವನು ಯೆಹೋವಾಹಾಜನ ಸಹೋದರನಾದ ಎಲ್ಯಾಕೀಮನನ್ನು ಯೆಹೂದದ ಮತ್ತು ಯೆರೂಸಲೇಮಿನ ಮೇಲೆ ಅರಸನನ್ನಾಗಿ ನೇಮಿಸಿ ಅವನಿಗೆ ಯೆಹೋಯಾಕೀಮನೆಂಬ ಹೆಸರಿಟ್ಟನು; ಮತ್ತು ಅವನ ಸಹೋದರನಾದ ಯೆಹೋವಾಹಾಜನನ್ನು ಐಗುಪ್ತದೇಶಕ್ಕೆ ತೆಗೆದುಕೊಂಡು ಹೋದನು.",
			"5": "<sup>5</sup> ಯೆಹೋಯಾಕೀಮನು ಅರಸನಾದಾಗ ಇಪ್ಪತ್ತೈದು ವರುಷದವನಾಗಿದ್ದನು; ಅವನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಹನ್ನೊಂದು ವರುಷ ಆಳಿದನು. ಅವನು ತನ್ನ ದೇವರಾದ ಯೆಹೋವನ ಚಿತ್ತಕ್ಕೆ ವಿರುದ್ಧವಾಗಿ ನಡೆದನು.",
			"6": "<sup>6</sup> ಬಾಬೆಲಿನ ಅರಸನಾದ ನೆಬೂಕದ್ನೆಚ್ಚರನು ಅವನ ಮೇಲೆ ಯುದ್ಧಕ್ಕೆ ಬಂದು ಬಾಬೆಲಿಗೆ ಒಯ್ಯುವದಕ್ಕೋಸ್ಕರ",
			"7": "<sup>7</sup> ಅವನಿಗೆ ಬೇಡಿಹಾಕಿ ಯೆಹೋವನ ಆಲಯದ ಹಲವು ಸಾಮಾನುಗಳನ್ನು ಬಾಬೆಲಿಗೆ ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ತನ್ನ ದೇವಾಲಯದಲ್ಲಿಟ್ಟನು. ಯೆಹೋಯಾಕೀಮನ ಉಳಿದ ಚರಿತ್ರೆಯೂ",
			"8": "<sup>8</sup> ಅವನು ನಡಿಸಿದ ಅಸಹ್ಯಕೃತ್ಯಗಳೂ ಅವನಲ್ಲಿ ಕಂಡುಬಂದದ್ದೆಲ್ಲವೂ ಇಸ್ರಾಯೇಲ್ಯರ ಮತ್ತು ಯೆಹೂದ್ಯರ ರಾಜಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ಯೆಹೋಯಾಕೀನನು ಅರಸನಾದನು.",
			"9": "<sup>9</sup> ಯೆಹೋಯಾಕೀನನು ಅರಸನಾದಾಗ ಎಂಟು ವರುಷದವನಾಗಿದ್ದನು; ಅವನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಮೂರು ತಿಂಗಳು ಹತ್ತು ದಿವಸ ಆಳಿದನು. ಅವನು ಯೆಹೋವನ ಚಿತ್ತಕ್ಕೆ ವಿರುದ್ಧವಾಗಿ ನಡೆದನು.",
			"10": "<sup>10</sup> ನೆಬೂಕದ್ನೆಚ್ಚರನು ವರ್ಷಾರಂಭದಲ್ಲಿ ಅವನನ್ನೂ ದೇವಾಲಯದ ಬೆಲೆಯುಳ್ಳಸಾಮಾನುಗಳನ್ನೂ ಬಾಬೆಲಿಗೆ ತರಿಸಿಕೊಂಡು ಅವನ ಸಹೋದರನಾದ ಚಿದ್ಕೀಯನನ್ನು ಯೆಹೂದದ ಮತ್ತು ಯೆರೂಸಲೇಮಿನ ಮೇಲೆ ಅರಸನನ್ನಾಗಿ ಮಾಡಿದನು.",
			"11": "<sup>11</sup> ಚಿದ್ಕೀಯನು ಅರಸನಾದಾಗ ಇಪ್ಪತ್ತೊಂದು ವರುಷದವನಾಗಿದ್ದನು; ಅವನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಹನ್ನೊಂದು ವರುಷ ಆಳಿದನು.",
			"12": "<sup>12</sup> ಅವನು ತನ್ನ ದೇವರಾದ ಯೆಹೋವನ ಚಿತ್ತಕ್ಕೆ ವಿರುದ್ಧವಾಗಿ ನಡೆದು ಯೆಹೋವನ ಮಾತುಗಳನ್ನು ತನಗೆ ತಿಳಿಸುತ್ತಿದ್ದ ಪ್ರವಾದಿಯಾದ ಯೆರೆಮೀಯನ ಮುಂದೆ ತನ್ನನ್ನು ತಗ್ಗಿಸಿಕೊಳ್ಳಲಿಲ್ಲ.",
			"13": "<sup>13</sup> ಇದಲ್ಲದೆ ನೆಬೂಕದ್ನೆಚ್ಚರನಿಗೆ ಒಳಗಾಗುತ್ತೇನೆಂದು ದೇವರ ಮೇಲೆ ಆಣೆಯಿಟ್ಟು ಪ್ರಮಾಣಮಾಡಿದ್ದರೂ ಅವನಿಗೆ ವಿರುದ್ಧವಾಗಿ ತಿರುಗಿಬಿದ್ದು ಇಸ್ರಾಯೇಲ್‍ದೇವರಾದ ಯೆಹೋವನ ಕಡೆಗೆ ತಿರುಗಿಕೊಳ್ಳಲೊಲ್ಲದೆ ಹಟಹಿಡಿದು ಮನಸ್ಸನ್ನು ಕಠಿನಮಾಡಿಕೊಂಡನು.",
			"14": "<sup>14</sup> ಪ್ರಧಾನಯಾಜಕರೂ ಪ್ರಜೆಗಳೂ ಕೂಡಾ ಮಹಾದ್ರೋಹಿಗಳಾಗಿ ಅನ್ಯಜನಾಂಗಗಳ ಅಸಹ್ಯಕೃತ್ಯಗಳನ್ನು ಅನುಸರಿಸಿ ಯೆಹೋವನು ತನಗೆ ಪ್ರತಿಷ್ಠಿಸಿಕೊಂಡಿದ್ದ ಯೆರೂಸಲೇಮಿನ ದೇವಾಲಯವನ್ನು ಹೊಲೆಮಾಡಿದರು.",
			"15": "<sup>15</sup> ಅವರ ಪಿತೃಗಳ ದೇವರಾದ ಯೆಹೋವನು ತನ್ನ ಪ್ರಜೆಯನ್ನೂ ನಿವಾಸಸ್ಥಾನವನ್ನೂ ಕನಿಕರಿಸಿ ಸಾವಕಾಶಮಾಡದೆ ತನ್ನ ದೂತರ ಮುಖಾಂತರವಾಗಿ ಅವರನ್ನು ಎಚ್ಚರಿಸುತ್ತಾ ಇದ್ದರೂ",
			"16": "<sup>16</sup> ಅವರು ದೇವಪ್ರೇಷಿತರನ್ನು ಗೇಲಿಮಾಡಿ ಆತನ ಮಾತುಗಳನ್ನು ತುಚ್ಫೀಕರಿಸಿ ಆತನ ಪ್ರವಾದಿಗಳನ್ನು ಹೀಯಾಳಿಸಿದ್ದರಿಂದ ಆತನ ಕೋಪಾಗ್ನಿಯು ಆತನ ಪ್ರಜೆಯ ಮೇಲೆ ಉರಿಯಹತ್ತಿತು, ಅದರ ತಾಪವು ಆರಿಹೋಗಲೇ ಇಲ್ಲ.",
			"17": "<sup>17</sup> ಆತನು ಕಸ್ದೀಯರ ಅರಸನನ್ನು ಅವರ ಮೇಲೆ ಬರಮಾಡಿ ಎಲ್ಲರನ್ನೂ ಅವನ ಕೈಗೆ ಒಪ್ಪಿಸಿದನು. ಅವನು ಅವರ ಯುವಭಟರನ್ನು ಅವರ ಪವಿತ್ರಾಲಯದಲ್ಲೇ ಕತ್ತಿಯಿಂದ ಸಂಹರಿಸಿ ಯುವಕರನ್ನೂ ಕನ್ಯೆಯರನ್ನೂ ಮುದುಕರನ್ನೂ ಅತಿವೃದ್ಧರನ್ನೂ ಕನಿಕರಿಸದೆ ಎಲ್ಲರನ್ನೂ ಕೊಲ್ಲಿಸಿದನು.",
			"18": "<sup>18</sup> ದೇವಾಲಯದ ಎಲ್ಲಾ ಚಿಕ್ಕ ದೊಡ್ಡ ಸಾಮಾನುಗಳನ್ನೂ ಯೆಹೋವನ ಮಂದಿರದ ಭಂಡಾರ, ಅರಸನ ಮತ್ತು ಅವನ ಸರದಾರರ ಭಂಡಾರ ಇವುಗಳ ದ್ರವ್ಯವನ್ನೂ ಬಾಬೆಲಿಗೆ ಒಯ್ದನು.",
			"19": "<sup>19</sup> [ಅವನ ಜನರು] ಯೆರೂಸಲೇಮಿನ ಪೌಳಿಗೋಡೆಗಳನ್ನು ಕೆಡವಿ ಅವರ ಎಲ್ಲಾ ರಾಜಮಂದಿರಗಳನ್ನೂ ದೇವಾಲಯವನ್ನೂ ಸುಟ್ಟುಬಿಟ್ಟು ಅಮೂಲ್ಯ ವಸ್ತುಗಳನ್ನು ನಾಶಮಾಡಿದರು.",
			"20": "<sup>20</sup> ಕತ್ತಿಗೆ ತಪ್ಪಿಸಿಕೊಂಡವರನ್ನು ಅರಸನು ಬಾಬೆಲಿಗೆ ಸೆರೆಯೊಯ್ದನು. ಪಾರಸಿಯ ಪ್ರಭುತ್ವವು ಅಲ್ಲಿ ಸ್ಥಾಪಿತವಾಗುವವರೆಗೆ ಅವರು ಅವನಿಗೂ ಅವನ ಮಕ್ಕಳಿಗೂ ಸೇವಕರಾಗಿದ್ದರು.",
			"21": "<sup>21</sup> ಈ ಪ್ರಕಾರ ಯೆಹೋವನು ಯೆರೆಮೀಯನ ಮುಖಾಂತರ ಹೇಳಿಸಿದ ಮಾತು ನೆರವೇರಿ ದೇಶವು ತನ್ನ ಸಬ್ಬತ್‍ಕಾಲವನ್ನು ಅನುಭವಿಸುವದಕ್ಕೆ ಮಾರ್ಗವಾಯಿತು. ದೇಶವು ಎಪ್ಪತ್ತು ವರುಷ ಹಾಳುಬಿದ್ದು ಆ ಕಾಲದಲ್ಲೆಲ್ಲಾ ವಿಶ್ರಾಂತಿಯನ್ನು ಅನುಭವಿಸುತ್ತಿತ್ತು.",
			"22": "<sup>22</sup> ಪಾರಸಿಯ ಅರಸನಾದ ಕೋರೆಷನ ಮೊದಲನೆಯ ವರುಷದಲ್ಲಿ ಯೆಹೋವನು ತಾನು ಯೆರೆಮೀಯನ ಮುಖಾಂತರ ಹೇಳಿಸಿದ ವಾಕ್ಯವನ್ನು ನೆರವೇರಿಸುವದಕ್ಕಾಗಿ ಆ ಪಾರಸಿಯ ರಾಜನಾದ ಕೋರೆಷನ ಮನಸ್ಸನ್ನು ಪ್ರೇರಿಸಿದ್ದರಿಂದ ಅವನು ತನ್ನ ರಾಜ್ಯದಲ್ಲೆಲ್ಲಾ ಡಂಗುರದಿಂದಲೂ ಪತ್ರಗಳಿಂದಲೂ -",
			"23": "<sup>23</sup> ಪಾರಸಿಯ ರಾಜನಾದ ಕೋರೆಷನೆಂಬ ನನ್ನ ಮಾತನ್ನು ಕೇಳಿರಿ - ಪರಲೋಕದೇವರಾದ ಯೆಹೋವನು ನನಗೆ ಭೂಲೋಕದ ಎಲ್ಲಾ ರಾಜ್ಯಗಳನ್ನು ಒಪ್ಪಿಸಿ ತನಗೋಸ್ಕರ ಯೆಹೂದದೇಶದ ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಆಲಯವನ್ನು ಕಟ್ಟಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿದ್ದಾನೆ. ನಿಮ್ಮಲ್ಲಿ ಯಾರು ಆತನ ಪ್ರಜೆಗಳಾಗಿರುತ್ತಾರೋ ಅವರು ಸ್ವದೇಶಕ್ಕೆ ಹೋಗಲಿ; ಅವರ ದೇವರಾದ ಯೆಹೋವನು ಅವರ ಸಂಗಡ ಇರುವನು ಎಂದು ಪ್ರಕಟಿಸಿದನು."
		}
	}
};
module.exports = book;