var book = {
	"name": "ನೆಹೆಮೀಯ",
	"numChapters": 13,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ಹಕಲ್ಯನ ಮಗನಾದ ನೆಹೆಮೀಯನ ಮಾತುಗಳು - ಇಪ್ಪತ್ತನೆಯ ವರುಷದ ಮಾರ್ಗಶೀರ್ಷ ಮಾಸದಲ್ಲಿ ನಾನು ಶೂಷನ್ ಪಟ್ಟಣದ ಅರಮನೆಯಲ್ಲಿದ್ದಾಗ",
			"2": "<sup>2</sup> ನನ್ನ ಸಹೋದರರಲ್ಲಿ ಒಬ್ಬನಾದ ಹನಾನೀಯನೂ ಬೇರೆ ಕೆಲವು ಜನರೂ ಯೆಹೂದದಿಂದ ನನ್ನ ಬಳಿಗೆ ಬಂದರು. ಸೆರೆಯವರೊಳಗೆ ತಪ್ಪಿಸಿಕೊಂಡ ಯೆಹೂದ್ಯರ ಮತ್ತು ಯೆರೂಸಲೇಮಿನ ಸಮಾಚಾರವನ್ನು ನಾನು ಅವರ ಹತ್ತಿರ ವಿಚಾರಿಸುವಲ್ಲಿ",
			"3": "<sup>3</sup> ಅವರು ನನಗೆ - ಸೆರೆಯವರೊಳಗೆ ತಪ್ಪಿಸಿಕೊಂಡು ಆ ಸಂಸ್ಥಾನದಲ್ಲಿ ವಾಸಿಸುತ್ತಿರುವವರು ಮಹಾ ಕಷ್ಟನಿಂದೆಗಳಿಗೆ ಒಳಗಾಗಿದ್ದಾರೆ; ಯೆರೂಸಲೇಮಿನ ಪೌಳಿಗೋಡೆಯು ಕೆಡವಲ್ಪಟ್ಟಿರುತ್ತದೆ; ಅದರ ಬಾಗಲುಗಳು ಬೆಂಕಿಯಿಂದ ಸುಡಲ್ಪಟ್ಟಿರುತ್ತವೆ ಎಂದು ಹೇಳಿದರು.",
			"4": "<sup>4</sup> ನಾನು ಈ ವರ್ತಮಾನವನ್ನು ಕೇಳಿದಾಗ ನೆಲದ ಮೇಲೆ ಕೂತುಕೊಂಡು ಅತ್ತೆನು; ಕೆಲವು ದಿವಸಗಳವರೆಗೂ ಶೋಕಿಸುತ್ತಾ ಉಪವಾಸವಾಗಿದ್ದು ಪರಲೋಕ ದೇವರ ಮುಂದೆ ವಿಜ್ಞಾಪನೆ ಮಾಡುತ್ತಾ ಇದ್ದೆನು.",
			"5": "<sup>5</sup> ಆ ವಿಜ್ಞಾಪನೆಗಳಲ್ಲಿ ನಾನು - ಯೆಹೋವನೇ, ಮಹೋನ್ನತನೂ ಭಯಂಕರನೂ ಆಗಿರುವ ಪರಲೋಕದೇವರೇ, ನಿನ್ನನ್ನು ಪ್ರೀತಿಸಿ ನಿನ್ನ ಆಜ್ಞೆಗಳನ್ನು ಅನುಸರಿಸುವವರಿಗೆ ಮಾಡಿದ ಕೃಪಾವಾಗ್ದಾನಗಳನ್ನು ನೆರವೇರಿಸುವವನೇ,",
			"6": "<sup>6</sup> ಕೃಪೆಮಾಡಿ ಕಿವಿಗೊಡಬೇಕು, ಕಟಾಕ್ಷಿಸಬೇಕು; ನಿನ್ನ ಸೇವಕನ ಪ್ರಾರ್ಥನೆಯನ್ನು ಲಾಲಿಸಬೇಕು. ನಾನು ಈಗ ಹಗಲಿರುಳೂ ನಿನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನಿನ್ನ ಸೇವಕರಾದ ಇಸ್ರಾಯೇಲ್ಯರಿಗೋಸ್ಕರ ಪ್ರಾರ್ಥಿಸುತ್ತಾ ಅವರು ನಿನಗೆ ವಿರುದ್ಧವಾಗಿ ಮಾಡಿದ ಪಾಪಗಳನ್ನು ಅರಿಕೆಮಾಡುತ್ತಾ ಇದ್ದೇನೆ; ನಾನೂ ನನ್ನ ಪೂರ್ವಿಕರೂ ಆ ಪಾಪಗಳಲ್ಲಿ ಪಾಲುಗಾರರು.",
			"7": "<sup>7</sup> ನಾವು ನಿನಗೆ ದ್ರೋಹಿಗಳಾಗಿ ನಡೆಯುತ್ತಾ ಬಂದೆವು; ನೀನು ನಿನ್ನ ಸೇವಕನಾದ ಮೋಶೆಯ ಮುಖಾಂತರ ಕೊಟ್ಟ ಆಜ್ಞಾವಿಧಿನ್ಯಾಯಗಳನ್ನು ನಾವು ಕೈಕೊಳ್ಳಲಿಲ್ಲ.",
			"8": "<sup>8</sup> ನೀನು ಅವನ ಮುಖಾಂತರವಾಗಿ - ನೀವು ಅವಿಧೇಯರಾದರೆ ನಿಮ್ಮನ್ನು ಜನಾಂಗಗಳೊಳಗೆ ಚದರಿಸಿ ಬಿಡುವೆನು;",
			"9": "<sup>9</sup> ನನ್ನ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡು ನನ್ನ ಆಜ್ಞೆಗಳನ್ನು ಅನುಸರಿಸಿ ನಡೆಯುವದಾದರೆ ನಿಮ್ಮವರು ಆಕಾಶದ ಕೊನೆಯವರೆಗೆ ಒಯ್ಯಲ್ಪಟ್ಟರೂ ನಾನು ಅವರನ್ನು ಅಲ್ಲಿಂದ ಕೂಡಿಸಿ ನನ್ನ ನಾಮಸ್ಥಾಪನೆಗೋಸ್ಕರ ಆರಿಸಿದ ಸ್ಥಳಕ್ಕೆ ತಿರಿಗಿ ಬರಮಾಡುವೆನು ಎಂದು ಹೇಳಿದಿಯಲ್ಲಾ; ಆ ಮಾತನ್ನು ನೆನಪು ಮಾಡಿಕೊಳ್ಳಬೇಕು, ಸ್ವಾಮೀ.",
			"10": "<sup>10</sup> ಅವರು ನಿನ್ನ ಸೇವಕರೂ ನೀನು ನಿನ್ನ ಮಹಾಶಕ್ತಿಭುಜಪರಾಕ್ರಮಗಳಿಂದ ವಿಮೋಚಿಸಿದ ನಿನ್ನ ಪ್ರಜೆಗಳೂ ಆಗಿರುತ್ತಾರಲ್ಲಾ.",
			"11": "<sup>11</sup> ಸ್ವಾಮೀ, ಕೃಪೆಮಾಡು; ನಿನ್ನ ಸೇವಕನಾದ ನನ್ನ ಪ್ರಾರ್ಥನೆಗೂ ನಿನ್ನ ನಾಮಸ್ಮರಣೆಯಲ್ಲಿ ಆನಂದಿಸುವ ನಿನ್ನ ಭಕ್ತರ ಪ್ರಾರ್ಥನೆಗೂ ಕಿವಿಗೊಡು. ನಿನ್ನ ಸೇವಕನು ಈಹೊತ್ತು ಆ ಮನುಷ್ಯನ ದಯೆಗೆ ಪಾತ್ರನಾಗಿ ಕೃತಾರ್ಥನಾಗುವಂತೆ ಅನುಗ್ರಹಿಸಬೇಕು ಎಂದು ಪ್ರಾರ್ಥಿಸಿದೆನು."
		},
		"2": {
			"1": "<sup>1</sup> ನಾನು ಅರಸನ ಪಾನಸೇವಕನಾಗಿದ್ದೆನು. ಅರಸನಾದ ಅರ್ತಷಸ್ತನ ಇಪ್ಪತ್ತನೆಯ ವರುಷದ ಚೈತ್ರಮಾಸದಲ್ಲಿ ಅರಸನು ದ್ರಾಕ್ಷಾರಸಪಾನಮಾಡುವ ಹೊತ್ತಿನಲ್ಲಿ ನಾನು ದ್ರಾಕ್ಷಾರಸವನ್ನು ತಂದು ಅವನಿಗೆ ಕೊಟ್ಟೆನು. ನಾನು ಅವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಹಿಂದೆ ಎಂದೂ ಖಿನ್ನನಾಗಿರಲಿಲ್ಲ.",
			"2": "<sup>2</sup> ಅರಸನು ನನಗೆ - ನೀನು ಕಳೆಗುಂದಿದವನಾಗಿ ಕಾಣುವದೇಕೆ? ನಿನಗೆ ದೇಹಾಲಸ್ಯವಿಲ್ಲವಲ್ಲಾ; ಇದಕ್ಕೆ ಮನೋವೇದನೆಯೇ ಹೊರತು ಬೇರೇನೂ ಕಾರಣವಲ್ಲ ಎಂದು ಹೇಳಲು ನನಗೆ ಮಹಾಭೀತಿಯುಂಟಾಯಿತು.",
			"3": "<sup>3</sup> ನಾನು ಅರಸನಿಗೆ - ಅರಸನು ಚಿರಂಜೀವಿಯಾಗಿರಲಿ. ನನ್ನ ಪೂರ್ವಿಕರ ಸಮಾಧಿಗಳಿರುವ ಪಟ್ಟಣವು ಹಾಳಾಗಿ ಅದರ ಬಾಗಲುಗಳು ಬೆಂಕಿಯಿಂದ ಸುಟ್ಟು ಹೋಗಿವೆ; ಹೀಗಿರುವಲ್ಲಿ ನನ್ನ ಮುಖವು ಕಳೆಗುಂದದೆ ಇರುವದು ಹೇಗೆ ಎಂದು ಹೇಳಿದೆನು.",
			"4": "<sup>4</sup> ಆಗ ಅರಸನು ನನ್ನನ್ನು - ನಿನ್ನ ಅಪೇಕ್ಷೆಯೇನು ಎಂದು ಕೇಳಿದನು. ನಾನು ಪರಲೋಕದ ದೇವರನ್ನು ಪ್ರಾರ್ಥಿಸಿ ಅರಸನಿಗೆ - ರಾಜರ ಚಿತ್ತವಿರುವದಾದರೆ ಮತ್ತು",
			"5": "<sup>5</sup> ತಮ್ಮ ಸೇವಕನು ತಮ್ಮ ದೃಷ್ಟಿಯಲ್ಲಿ ದಯೆಗೆ ಪಾತ್ರನಾಗಿದ್ದರೆ ನನ್ನ ಪಿತೃಗಳ ಸಮಾಧಿಗಳಿರುವ ಪಟ್ಟಣವನ್ನು ತಿರಿಗಿ ಕಟ್ಟುವದಕ್ಕೋಸ್ಕರ ಯೆಹೂದದೇಶಕ್ಕೆ ಹೋಗಲು ನನಗೆ ಅಪ್ಪಣೆಯಾಗಬೇಕು ಎಂದು ಹೇಳಿದೆನು.",
			"6": "<sup>6</sup> ರಾಣಿಯು ಅರಸನ ಹತ್ತಿರದಲ್ಲೇ ಕೂತುಕೊಂಡಿದ್ದಳು. ಅರಸನು - ಪ್ರಯಾಣಕ್ಕೆ ನಿನಗೆಷ್ಟು ಕಾಲಬೇಕು? ಯಾವಾಗ ಹಿಂದಿರುಗುವಿ ಎಂದು ವಿಚಾರಿಸಿದಾಗ ನಾನು ಕಾಲವನ್ನು ಸೂಚಿಸಲು ಅವನು ಒಪ್ಪಿಕೊಂಡು ಅಪ್ಪಣೆಕೊಟ್ಟನು.",
			"7": "<sup>7</sup> ತರುವಾಯ ನಾನು ಅರಸನಿಗೆ - ಹೊಳೆಯಾಚೆಯ ದೇಶಾಧಿಪತಿಗಳು ತಮ್ಮ ಪ್ರಾಂತಗಳಲ್ಲಿ ಹಾದು ಯೆಹೂದ ಸೀಮೆಗೆ ಹೋಗುವದಕ್ಕೆ ನನಗೆ ಅಪ್ಪಣೆಕೊಡುವ ಹಾಗೂ",
			"8": "<sup>8</sup> ರಾಜವನಪಾಲಕನಾದ ಆಸಾಫನು ದೇವಾಲಯದ ಕೋಟೆಯ ಬಾಗಲುಗಳು, ಪಟ್ಟಣದ ಪೌಳಿಗೋಡೆ, ನಾನು ಸೇರುವ ಮನೆ ಇವುಗಳಿಗೆ ಬೇಕಾಗುವ ತೊಲೆಗಳಿಗಾಗಿ ಮರಗಳನ್ನು ಕೊಡುವ ಹಾಗೂ ರಾಜರು ನನ್ನ ಕೈಯಲ್ಲಿ ಪತ್ರಗಳನ್ನು ದಯಮಾಡಬೇಕು ಎಂದು ಬಿನ್ನವಿಸಲು ನನ್ನ ದೇವರ ಕೃಪಾಹಸ್ತಪಾಲನೆ ನನಗಿದ್ದದ್ದರಿಂದ",
			"9": "<sup>9</sup> ಅರಸನು ಅವುಗಳನ್ನು ನನಗೆ ಕೊಟ್ಟದ್ದಲ್ಲದೆ ನನ್ನನ್ನು ಸಾಗಕಳುಹಿಸುವದಕ್ಕೋಸ್ಕರ ಸೇನಾಧಿಪತಿಗಳನ್ನೂ ರಾಹುತರನ್ನೂ ಕಳುಹಿಸಿದನು. ಹೀಗೆ ನಾನು ಹೊಳೆಯಾಚೆಯ ದೇಶಾಧಿಪತಿಗಳ ಬಳಿಗೆ ಬಂದು ಅರಸನ ಪತ್ರಗಳನ್ನು ಅವರಿಗೆ ಕೊಟ್ಟೆನು.",
			"10": "<sup>10</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಹಿತಚಿಂತಕನೊಬ್ಬನು ಬಂದನೆಂಬ ವರ್ತಮಾನವು ಹೋರೋನಿನ ಸನ್ಬಲ್ಲಟನಿಗೂ ಅಮ್ಮೋನ್‍ದೇಶದವನಾದ ಟೋಬೀಯ ಎಂಬ ದಾಸನಿಗೂ ಮುಟ್ಟಿದಾಗ ಅವರು ಬಹಳವಾಗಿ ಹೊಟ್ಟೆಕಿಚ್ಚುಪಟ್ಟರು.",
			"11": "<sup>11</sup> ಅನಂತರ ಯೆರೂಸಲೇಮಿಗೆ ಬಂದೆನು. ಅಲ್ಲಿ ಮೂರು ದಿನಗಳಿದ್ದ ಮೇಲೆ",
			"12": "<sup>12</sup> ಯೆರೂಸಲೇಮಿಗೋಸ್ಕರ ಮಾಡತಕ್ಕ ಕಾರ್ಯದ ವಿಷಯವಾಗಿ ನನ್ನ ದೇವರು ನನ್ನಲ್ಲಿ ಯಾವ ಆಲೋಚನೆಯನ್ನು ಹುಟ್ಟಿಸಿದ್ದಾನೆಂದು ಯಾರಿಗೂ ತಿಳಿಸದೆ ರಾತ್ರಿಯಲ್ಲೆದ್ದು ಕೆಲವು ಜನರನ್ನು ಮಾತ್ರ ಕರಕೊಂಡು ಹೊರಟೆನು. ನನ್ನ ವಾಹನಪಶುವಿನ ಹೊರತು ಬೇರೆ ಯಾವ ಪಶುವೂ ನನ್ನೊಂದಿಗಿರಲಿಲ್ಲ.",
			"13": "<sup>13</sup> ನಾನು ರಾತ್ರಿವೇಳೆಯಲ್ಲಿ ತಗ್ಗಿನ ಬಾಗಲಿನಿಂದ ಹೊರಟು ಹಾಳುಬಿದ್ದ ಯೆರೂಸಲೇಮಿನ ಗೋಡೆಗಳನ್ನೂ ಬೆಂಕಿಯಿಂದ ಸುಡಲ್ಪಟ್ಟ ಅದರ ಬಾಗಲುಗಳನ್ನೂ ನೋಡುತ್ತಾ ಹೆಬ್ಬಾವು ಬುಗ್ಗೆಯ ಮಾರ್ಗವಾಗಿ ತಿಪ್ಪೆಬಾಗಲಿಗೆ ಹೋದೆನು.",
			"14": "<sup>14</sup> ಅಲ್ಲಿಂದ ಬುಗ್ಗೆಬಾಗಲನ್ನು ಹಾದು ಅರಸನ ಕೊಳಕ್ಕೆ ಹೋದೆನು.",
			"15": "<sup>15</sup> ನನ್ನ ವಾಹನಪಶುವಿಗೆ ಅಲ್ಲಿಂದ ಮುಂದೆ ಹೋಗುವದಕ್ಕೆ ಮಾರ್ಗವಿಲ್ಲದ್ದರಿಂದ ನಾನು ರಾತ್ರಿಯಲ್ಲಿ ಹಳ್ಳದ ಮಾರ್ಗದಿಂದ ಹತ್ತುತ್ತಾ ಗೋಡೆಯನ್ನು ಪರೀಕ್ಷಿಸಿದೆನು. ಆಮೇಲೆ ಪುನಃ ತಗ್ಗಿನ ಬಾಗಲಿನಿಂದ ಮನೆಗೆ ಬಂದೆನು.",
			"16": "<sup>16</sup> ನಾನು ಆವರೆಗೆ ಯೆಹೂದ್ಯರಿಗೂ ಯಾಜಕರಿಗೂ ಶ್ರೀಮಂತರಿಗೂ ಅಧಿಕಾರಿಗಳಿಗೂ ಕೆಲಸನಡಿಸತಕ್ಕ ಇತರರಿಗೂ ಏನೂ ತಿಳಿಸದೆ ಇದ್ದದರಿಂದ ನಾನು ಎಲ್ಲಿ ಹೋಗಿದ್ದೆನು, ಏನು ಮಾಡಿದೆನು ಎಂಬದು ಅಧಿಕಾರಿಗಳಿಗೆ ಗೊತ್ತಿರಲಿಲ್ಲ.",
			"17": "<sup>17</sup> ಆಗ ನಾನು ಅವರಿಗೆ - ನಮ್ಮ ದುರವಸ್ಥೆ ನಿಮ್ಮ ಕಣ್ಣ ಮುಂದಿರುತ್ತದಲ್ಲವೋ; ಯೆರೂಸಲೇಮ್ ಪಟ್ಟಣವು ಹಾಳುಬಿದ್ದದೆ, ಅದರ ಬಾಗಲುಗಳು ಬೆಂಕಿಯಿಂದ ಸುಡಲ್ಪಟ್ಟಿರುತ್ತವೆ; ಬನ್ನಿರಿ, ಯೆರೂಸಲೇಮಿನ ಗೋಡೆಯನ್ನು ಕಟ್ಟೋಣ. ಹೀಗೆ ಮಾಡಿದರೆ ನಮ್ಮ ಮೇಲಣ ನಿಂದೆಯು ಹೋಗುವದು ಎಂದು ಹೇಳಿದೆನು.",
			"18": "<sup>18</sup> ಇದಲ್ಲದೆ ನನ್ನ ದೇವರ ಕೃಪಾಹಸ್ತವು ನನ್ನನ್ನು ಪಾಲಿಸಿದ್ದನ್ನೂ ಅರಸನು ನನಗೆ ಹೇಳಿದ್ದನ್ನೂ ಅವರಿಗೆ ವಿವರಿಸಿದೆನು. ಆಗ ಅವರು - ಬನ್ನಿರಿ, ಕಟ್ಟೋಣ ಎಂದು ಹೇಳಿ ಆ ಒಳ್ಳೇ ಕೆಲಸಕ್ಕೆ ಕೈಹಾಕಲು ಧೈರ್ಯಗೊಂಡರು.",
			"19": "<sup>19</sup> ಹೋರೋನಿನವನಾದ ಸನ್ಬಲ್ಲಟನೂ ಅಮ್ಮೋನ್ ದೇಶದವನಾದ ಟೋಬೀಯ ಎಂಬ ದಾಸನೂ ಅರಬಿಯನಾದ ಗೆಷೆಮನೂ ಈ ವರ್ತಮಾನವನ್ನು ಕೇಳಿದಾಗ ಅವರು ನಮ್ಮನ್ನು ತಿರಸ್ಕರಿಸಿ ಗೇಲಿಮಾಡಿ - ನೀವು ಇಲ್ಲಿ ಮಾಡುವದೇನು? ಅರಸನಿಗೆ ವಿರೋಧವಾಗಿ ತಿರುಗಿಬೀಳಬೇಕೆಂದಿರುತ್ತೀರೋ ಎಂದು ಕೇಳಿದರು.",
			"20": "<sup>20</sup> ನಾನು ಅವರಿಗೆ - ಪರಲೋಕದೇವರು ನಮಗೆ ಸಾಫಲ್ಯವನ್ನನುಗ್ರಹಿಸುವನು. ಆದದರಿಂದ ಆತನ ಸೇವಕರಾದ ನಾವು ಕಟ್ಟುವದಕ್ಕೆ ಮನಸ್ಸು ಮಾಡಿದ್ದೇವೆ. ನಿಮಗಾದರೋ ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಪಾಲೂ ಹಕ್ಕೂ ಹೆಸರೂ ಇರುವದಿಲ್ಲ ಎಂದು ಉತ್ತರಕೊಟ್ಟೆನು."
		},
		"3": {
			"1": "<sup>1</sup> ಆಗ ಮಹಾಯಾಜಕನಾದ ಎಲ್ಯಾಷೀಬನೂ ಅವನ ಕುಟುಂಬದ ಯಾಜಕರೂ ಕುರಿಬಾಗಲನ್ನು ಕಟ್ಟಿ ಅದನ್ನು ಪ್ರತಿಷ್ಠಿಸಿ ಅದಕ್ಕೆ ಕದಗಳನ್ನು ಇಟ್ಟರು. ಅಲ್ಲಿಂದ ಹಮ್ಮೆಯಾ ಬುರುಜಿನವರೆಗೆ ಗೋಡೆಕಟ್ಟಿ ಅದನ್ನೂ ಪ್ರತಿಷ್ಠಿಸಿದರು. ಅಲ್ಲಿಂದ ಹನನೇಲ್ ಬುರುಜಿನವರೆಗೂ ಕಟ್ಟಿದರು.",
			"2": "<sup>2</sup> ಅಲ್ಲಿಂದ ಆಚೆಗೆ ಕಟ್ಟುತ್ತಿದ್ದವರು ಯೆರಿಕೋವಿನವರು; ಆಚೆಗೆ ಇವ್ರಿುಯ ಮಗನಾದ ಜಕ್ಕೂರನು.",
			"3": "<sup>3</sup> ಮೀನುಬಾಗಲನ್ನು ಕಟ್ಟಿದವರು ಹಸ್ಸೆನಾಹನ ಮನೆಯವರು. ಇವರು ಅದರ ಮೇಲೆ ತೊಲೆಗಳನ್ನಿಟ್ಟು ಅದಕ್ಕೆ ಕದ ತಿರುಗಣಿ ಅಗುಳಿಗಳನ್ನು ಹಚ್ಚಿದರು.",
			"4": "<sup>4</sup> ಅಲ್ಲಿಂದ ಮುಂದಕ್ಕೆ ಗೋಡೆಯನ್ನು ಜೀರ್ಣೋದ್ಧಾರಮಾಡಿಸಿದವನು ಊರೀಯನ ಮಗನೂ ಹಕ್ಕೋಚನ ಮೊಮ್ಮಗನೂ ಆಗಿರುವ ಮೆರೇಮೋತ್; ಆಮೇಲೆ ಬೆರೆಕ್ಯನ ಮಗನೂ ಮೆಷೇಜಬೇಲನ ಮೊಮ್ಮಗನೂ ಆಗಿರುವ ಮೆಷುಲ್ಲಾಮ್; ಆಚೆಗೆ ಬಾನನ ಮಗನಾದ ಚಾದೋಕ್; ಮುಂದಕ್ಕೆ ತೆಕೋವದವರು.",
			"5": "<sup>5</sup> ತೆಕೋವದ ಶ್ರೀಮಂತರಾದರೋ ತಮ್ಮ ಒಡೆಯರ ಸೇವೆಗಾಗಿ ಹೆಗಲನ್ನು ಕೊಡಲಿಲ್ಲ.",
			"6": "<sup>6</sup> ಹಳೆಯದು ಎಂಬ ಬಾಗಲನ್ನು ಜೀರ್ಣೋದ್ಧಾರ ಮಾಡಿದವರು ಪಾಸೇಹನ ಮಗನಾದ ಯೋಯಾದನೂ ಬೆಸೋದ್ಯನ ಮಗನಾದ ಮೆಷುಲ್ಲಾಮನೂ. ಇವರು ಅದರ ಮೇಲೆ ತೊಲೆಗಳನ್ನಿಟ್ಟು ಅದಕ್ಕೆ ಕದ ತಿರುಗಣಿ ಅಗುಳಿಗಳನ್ನು ಹಚ್ಚಿದರು.",
			"7": "<sup>7</sup> ಅಲ್ಲಿಂದ ಹೊಳೆಯಾಚೆಯ ದೇಶಾಧಿಪತಿಯ ನ್ಯಾಯಸ್ಥಾನದವರೆಗೆ ಗಿಬ್ಯೋನಿನವನಾದ ಮೆಲೆಟ್ಯ, ಮೇರೋನೋತಿನವನಾದ ಯಾದೋನ್ ಇವರೂ ಗಿಬ್ಯೋನ್ ಮತ್ತು ಮಿಚ್ಪ ಊರುಗಳವರೂ ಜೀರ್ಣೋದ್ಧಾರಮಾಡಿದರು.",
			"8": "<sup>8</sup> ಇದರ ಆಚೆಗೆ ಹರ್ಹಯನ ಮಗನಾದ ಅಕ್ಕಸಾಲಿಗರ ಉಜ್ಜೀಯೇಲ್; ಆಮೇಲೆ ಬುಕ್ಕಿಟ್ಟುಗಾರರ ಹನನ್ಯ; ಇವರು ಅಗಲ ಗೋಡೆಯೆನಿಸಿಕೊಳ್ಳುವ ಗೋಡೆಯ ಈಚೆಗಿರುವ ಯೆರೂಸಲೇಮಿನ ಭಾಗವನ್ನು ಗೋಡೆಯೊಳಗೆ ಸೇರಿಸಲಿಲ್ಲ.",
			"9": "<sup>9</sup> ಇದರ ಆಚೆಗೆ ಯೆರೂಸಲೇಮಿನ ಅರೆನಾಡೊಡೆಯನೂ ಹೂರನ ಮಗನೂ ಆದ ರೆಫಾಯ.",
			"10": "<sup>10</sup> ಇವನ ಮುಂದಕ್ಕೆ ಹರೂಮಫನ ಮಗನಾದ ಯೆದಾಯ; ಇವನು ತನ್ನ ಮನೆಯ ಎದುರಿಗಿರುವ ಗೋಡೆಯನ್ನು ಜೀರ್ಣೋದ್ಧಾರಮಾಡಿದನು. ಇವನ ಆಚೆಗೆ ಹಷಬ್ನೆಯನ ಮಗನಾದ ಹಟ್ಟೂಷ್.",
			"11": "<sup>11</sup> ಗೋಡೆಯ ಇನ್ನೊಂದು ಭಾಗವನ್ನೂ ಒಲೆಬುರುಜನ್ನೂ ಜೀರ್ಣೋದ್ಧಾರ ಮಾಡಿದವರು ಹಾರೀಮನ ಮಗನಾದ ಮಲ್ಕೀಯನೂ ಪಹತ್ ಮೋವಾಬ್ಯನಾದ ಹಷ್ಷೂಬನೂ.",
			"12": "<sup>12</sup> ಇವನ ಸಮೀಪದಲ್ಲಿ ಗೋಡೆಯನ್ನು ಜೀರ್ಣೋದ್ಧಾರ ಮಾಡಿದವರು ಯೆರೂಸಲೇಮಿನ ಅರೆನಾಡೊಡೆಯನೂ ಹಲ್ಲೊಹೇಷನ ಮಗನೂ ಆದ ಶಲ್ಲೂಮನೂ ಅವನ ಕುಮಾರಿಯರೂ.",
			"13": "<sup>13</sup> ತಗ್ಗಿನ ಬಾಗಲನ್ನು ಜೀರ್ಣೋದ್ಧಾರಮಾಡಿದವರು ಹಾನೂನನೂ ಜಾನೋಹ ಊರಿನವರೂ. ಇವರು ಅದರ ಗೋಡೆಯನ್ನು ಕಟ್ಟಿ ಅದಕ್ಕೆ ಕದ ತಿರುಗಣಿ ಅಗುಳಿಗಳನ್ನು ಹಚ್ಚಿ ಅಲ್ಲಿಂದ ತಿಪ್ಪೆಬಾಗಲಿನವರೆಗೂ ಸಾವಿರ ಮೊಳದ ಗೋಡೆಯನ್ನು ಕಟ್ಟಿದರು.",
			"14": "<sup>14</sup> ತಿಪ್ಪೆಬಾಗಲನ್ನು ಜೀರ್ಣೋದ್ಧಾರಮಾಡಿದವನು ಬೇತ್‍ಹಕ್ಕೆರೆಮಿನ ನಾಡೊಡೆಯನೂ ರೇಕಾಬನ ಮಗನೂ ಆದ ಮಲ್ಕೀಯನು; ಇವನು ಅದರ ಗೋಡೆಯನ್ನು ಕಟ್ಟಿ ಅದಕ್ಕೆ ಕದ ತಿರುಗಣಿ ಅಗುಳಿಗಳನ್ನು ಹಚ್ಚಿದನು.",
			"15": "<sup>15</sup> ಬುಗ್ಗೆಬಾಗಲನ್ನು ಜೀರ್ಣೋದ್ಧಾರ ಮಾಡಿದವನು ಮಿಚ್ಪದ ನಾಡೊಡೆಯನೂ ಕೊಲ್ಹೋಜೆಯ ಮಗನೂ ಆದ ಶಲ್ಲೂನ್; ಇವನು ಅದರ ಗೋಡೆಯನ್ನು ಕಟ್ಟಿ ಅದಕ್ಕೆ ಮಾಳಿಗೆಯನ್ನು ಹಾಕಿ ಕದ ತಿರುಗಣಿ ಅಗುಳಿಗಳನ್ನು ಹಚ್ಚಿ ದಾವೀದನಗರದಿಂದಿಳಿದು ಬರುವ ಸೋಪಾನಗಳ ಈಚೆ ಅರಸನ ತೋಟದ ಬಳಿಯಲ್ಲಿರುವ ಸಿಲೋವ ಕೊಳದ ಗೋಡೆಯನ್ನು ಕಟ್ಟಿದನು.",
			"16": "<sup>16</sup> ಇವನ ಆಚೆ ಅಂದರೆ ದಾವೀದನ ಸಮಾಧಿಯ ಎದುರಿನಲ್ಲಿರುವ ಕೊಳ, ಸಿಪಾಯರ ಠಾಣ ಇವುಗಳವರೆಗೂ ಜೀರ್ಣೋದ್ಧಾರ ಮಾಡಿದವನು ಬೇತ್ಚೂರಿನ ಅರೆನಾಡೊಡೆಯನೂ ಅಜ್ಬೂಕನ ಮಗನೂ ಆದ ನೆಹೆಮೀಯನು.",
			"17": "<sup>17</sup> ಇವನ ಆಚೆ ಜೀರ್ಣೋದ್ಧಾರಮಾಡಿದವರು ಲೇವಿಯರು. ಅವರಲ್ಲಿ ಮೊದಲು ಬಾನಿಯ ಮಗನಾದ ರೆಹೂಮ್, ಇವನ ಸಮೀಪದಲ್ಲಿ ತನ್ನ ನಾಡಿನವರೊಡನೆ ಕಟ್ಟುತ್ತಿದ್ದ ಕೆಯೀಲದ ಅರೆನಾಡೊಡೆಯನಾದ ಹಷಬ್ಯ ಇವರು.",
			"18": "<sup>18</sup> ಇವರ ಆಚೆ ಇವರ ಬಂಧುಗಳಲ್ಲಿ ಕೆಯೀಲದ [ಎರಡನೆಯ] ಅರೆನಾಡೊಡೆಯನೂ ಹೇನಾದಾದನ ಮಗನೂ ಆದ ಬವ್ವೈ.",
			"19": "<sup>19</sup> ಇವನ ಸಮೀಪದಲ್ಲಿ ಗೋಡೆಯ ಇನ್ನೊಂದು ಭಾಗವನ್ನು ಅಂದರೆ ಗೋಡೆಯ ಮೂಲೆಯಲ್ಲಿರುವ ಆಯುಧಶಾಲೆಗೆ ಹೋಗುವ ಮೆಟ್ಲುಗಳ ಎದುರಿಗಿರುವ ಭಾಗವನ್ನು ಮಿಚ್ಪದ ಅಧಿಕಾರಿಯೂ ಯೇಷೂವನ ಮಗನೂ ಆದ ಏಜೆರನು ಜೀರ್ಣೋದ್ಧಾರ ಮಾಡಿದನು.",
			"20": "<sup>20</sup> ಇವನ ಆಚೆ ಗೋಡೆಯ ಇನ್ನೊಂದು ಭಾಗವನ್ನು ಅಂದರೆ ಮೂಲೆಯಿಂದ ಮಹಾಯಾಜಕನಾದ ಎಲ್ಯಾಷೀಬನ ಮನೆಯ ಬಾಗಲಿನವರೆಗೆ ಜಕ್ಕೈಯ ಮಗನಾದ ಬಾರೂಕನು ಆಸಕ್ತಿಯಿಂದ ಭದ್ರಪಡಿಸಿದನು.",
			"21": "<sup>21</sup> ಇವನ ಆಚೆ ಗೋಡೆಯ ಇನ್ನೊಂದು ಭಾಗವನ್ನು ಅಂದರೆ ಎಲ್ಯಾಷೀಬನ ಮನೆಯ ಬಾಗಲಿನಿಂದ ಅದೇ ಮನೆಯ ಕೊನೆಯವರೆಗೆ ಹಕ್ಕೋಚನ ಮೊಮ್ಮಗನೂ ಊರೀಯನ ಮಗನೂ ಆದ ಮೆರೇಮೋತನು ಜೀರ್ಣೋದ್ಧಾರ ಮಾಡಿದನು.",
			"22": "<sup>22</sup> ಮುಂದಿನ ಭಾಗವನ್ನು ಜೀರ್ಣೋದ್ಧಾರಮಾಡಿದವರು ಯೊರ್ದನ್ ಹೊಳೆಯ ತಗ್ಗಿನಲ್ಲಿ ವಾಸಿಸುತ್ತಿದ್ದ ಯಾಜಕರು.",
			"23": "<sup>23</sup> ಇವರ ಆಚೆ ಬೆನ್ಯಾಮೀನ್ ಹಷ್ಷೂಬರು; ಇವರು ತಮ್ಮ ತಮ್ಮ ಮನೆಗಳಿಗೆ ಎದುರಾಗಿರುವ ಗೋಡೆಯನ್ನು ಜೀರ್ಣೋದ್ಧಾರಮಾಡಿದರು. ಇವರ ಆಚೆ ಅನನ್ಯನ ಮೊಮ್ಮಗನೂ ಮಾಸೇಯನ ಮಗನೂ ಆದ ಅಜರ್ಯ; ಇವನು ತನ್ನ ಮನೆಯ ಹತ್ತಿರದಲ್ಲಿರುವ ಗೋಡೆಯನ್ನು ಜೀರ್ಣೋದ್ಧಾರ ಮಾಡಿದನು.",
			"24": "<sup>24</sup> ಇವನ ಆಚೆ ಅಜರ್ಯನ ಮನೆಯಿಂದ ಮೂಲೆಯವರೆಗೆ ಇರುವ ಗೋಡೆಯ ಇನ್ನೊಂದು ಭಾಗವನ್ನು ಜೀರ್ಣೋದ್ಧಾರಮಾಡಿದವನು ಹೇನಾದಾದನ ಮಗನಾದ ಬಿನ್ನೂಯ್.",
			"25": "<sup>25</sup> ಸೆರೆಮನೆಯ ಅಂಗಳದ ಹತ್ತಿರ ಮೇಲಣ ಅರಮನೆಯ ಗೋಡೆಯನ್ನು ಮೀರಿಬರುವ ಮೂಲೆಬುರುಜಿನ ಎದುರಿನ ಭಾಗವನ್ನು ಊಜೈಯ ಮಗನಾದ ಪಾಲಾಲನೂ ಇವನ ಆಚೆಯಲ್ಲಿ ಪರೋಷನ ಮಗನಾದ ಪೆದಾಯನೂ ಜೀರ್ಣೋದ್ಧಾರ ಮಾಡಿದರು.",
			"26": "<sup>26</sup> ಆ ಮೂಲೆ ಬುರುಜಿನ ಮತ್ತು ನೀರುಬಾಗಲಿನ ಪೂರ್ವದಿಕ್ಕಿಗಿರುವ ಓಫೇಲ್ ಗುಡ್ಡವು ದೇವಸ್ಥಾನದಾಸರ ನಿವಾಸಸ್ಥಾನವು.",
			"27": "<sup>27</sup> ಆ ದೊಡ್ಡ ಮೂಲೆಬುರುಜಿನ ಎದುರಿನಿಂದ ಓಫೇಲ್ ಗೋಡೆಯವರೆಗಿರುವ ಗೋಡೆಯ ಇನ್ನೊಂದು ಭಾಗವನ್ನು ತೆಕೋವದವರು ಜೀರ್ಣೋದ್ಧಾರಮಾಡಿದರು.",
			"28": "<sup>28</sup> ಯಾಜಕರು ಕುದುರೆಬಾಗಲಿನ ಆಚೆ ದಿನ್ನೆಯ ಮೇಲಿರುವ ಗೋಡೆಯಲ್ಲಿ ತಮ್ಮ ತಮ್ಮ ಮನೆಗಳ ಎದುರಿನ ಭಾಗಗಳನ್ನು ಜೀರ್ಣೋದ್ಧಾರಮಾಡಿದರು.",
			"29": "<sup>29</sup> ಇವರ ಆಚೆ ಇಮ್ಮೇರನ ಮಗನಾದ ಚಾದೋಕನು ತನ್ನ ಮನೆಯ ಎದುರಿಗಿರುವ ಗೋಡೆಯನ್ನು ಜೀರ್ಣೋದ್ಧಾರಮಾಡಿದನು. ಇವನ ಆಚೆ ಪೂರ್ವದಿಕ್ಕಿನ ದ್ವಾರಪಾಲಕನಾದ ಶೆಕನ್ಯನ ಮಗ ಶೆಮಾಯನು.",
			"30": "<sup>30</sup> ಇವನ ಆಚೆ ಶೆಲೆಮ್ಯನ ಮಗನಾದ ಹನನ್ಯ; ಗೋಡೆಯ ಇನ್ನೊಂದು ಭಾಗವನ್ನು ಜೀರ್ಣೋದ್ಧಾರಮಾಡಿದವನು ಚಾಲಾಫನ ಆರನೆಯ ಮಗನಾದ ಹಾನೂನ್; ಇವನ ಆಚೆ ಅಂದರೆ ತನ್ನ ಸ್ವಂತ ಕೊಠಡಿಯ ಮುಂದೆ ಬೆರೆಕ್ಯನ ಮಗನಾದ ಮೆಷುಲ್ಲಾಮ್.",
			"31": "<sup>31</sup> ಇನ್ನೂ ಮುಂದಕ್ಕೆ ಅಕ್ಕಸಾಲಿಗರ ಮಲ್ಕೀಯ; ಇವನು ದಂಡುಕೂಡುವ ಬಾಗಲಿನೆದುರಿನಲ್ಲಿರುವ ದೇವಸ್ಥಾನದಾಸರ ಮತ್ತು ವರ್ತಕರ ಕೇರಿಯವರೆಗೂ ಮೂಲೆಯುಪ್ಪರಿಗೆಯವರೆಗೂ ಜೀರ್ಣೋದ್ಧಾರ ಮಾಡಿದನು.",
			"32": "<sup>32</sup> ಈ ಮೂಲೆಯುಪ್ಪರಿಗೆಗೂ ಕುರಿಬಾಗಲಿಗೂ ಮಧ್ಯದಲ್ಲಿದ್ದ ಗೋಡೆಯನ್ನು ಜೀರ್ಣೋದ್ಧಾರಮಾಡಿದವರು ಅಕ್ಕಸಾಲಿಗರು, ವರ್ತಕರು ಇವರೇ."
		},
		"4": {
			"1": "<sup>1</sup> ನಾವು ಪೌಳಿಗೋಡೆಯನ್ನು ಕಟ್ಟುತ್ತಿದ್ದ ಸುದ್ದಿಯು ಸನ್ಬಲ್ಲಟನಿಗೆ ಮುಟ್ಟಿದಾಗ ಅವನು ಬಲು ಹೊಟ್ಟೆಕಿಚ್ಚೂ ಸಿಟ್ಟೂ ಉಳ್ಳವನಾಗಿ",
			"2": "<sup>2</sup> ತನ್ನ ಸಹೋದರರ ಮುಂದೆಯೂ ಸಮಾರ್ಯದ ದಂಡಿನವರ ಮುಂದೆಯೂ - ನಿತ್ರಾಣಿಗಳಾದ ಈ ಯೆಹೂದ್ಯರು ಮಾಡುವದೇನು? ಇವರು ತಮ್ಮನ್ನು ಬಲಪಡಿಸಿಕೊಳ್ಳಬೇಕೆಂದಿರುವರೋ? ಯಜ್ಞವನ್ನರ್ಪಿಸುವರೋ? ಈ ಹೊತ್ತೇ ಈ ಕೆಲಸವನ್ನು ತೀರಿಸುವರೇನು? ಸುಟ್ಟು ಹೋದ ಪಟ್ಟಣದ ಧೂಳಿನ ರಾಶಿಯೊಳಗೆ ಹುಗಿದುಹೋದ ಕಲ್ಲುಗಳನ್ನು ಬದುಕಿಸುವರೇನೋ ಎಂದು ಹೇಳಿ ಯೆಹೂದ್ಯರನ್ನು ಗೇಲಿಮಾಡಿದನು.",
			"3": "<sup>3</sup> ಅವನ ಬಳಿಯಲ್ಲಿ ನಿಂತಿದ್ದ ಅಮ್ಮೋನಿಯನಾದ ಟೋಬೀಯನು - ಅವರು ಕಟ್ಟುವ ಕಲ್ಲುಗೋಡೆಯ ಮೇಲೆ ನರಿ ಹಾರಿದರೆ ಅದು ಬಿದ್ದುಹೋಗುವದು ಅಂದನು.",
			"4": "<sup>4</sup> ನಮ್ಮ ದೇವರೇ ಕೇಳು; ಅವರು ನಮ್ಮನ್ನು ಎಷ್ಟು ಹೀಯಾಳಿಸುತ್ತಾರೆ! ಈ ನಿಂದೆಯನ್ನು ಅವರ ತಲೆಯ ಮೇಲೆಯೇ ಬರಮಾಡು. ಅವರು ದೇಶಭ್ರಷ್ಟರಾಗಿ ಸೂರೆ ಹೋಗುವಂತೆ ಮಾಡು.",
			"5": "<sup>5</sup> ಅವರ ಅಪರಾಧವನ್ನು ಕ್ಷಮಿಸಬೇಡ; ನಿನ್ನೆದುರಿಗಿರುವ ಅವರ ಪಾಪವನ್ನು ಅಳಿಸಿಬಿಡಬೇಡ. ಕಟ್ಟುವವರ ಮುಂದೆಯೇ ಅವರು ನಿನ್ನನ್ನು ಕೆಣಕಿದ್ದಾರಲ್ಲಾ.",
			"6": "<sup>6</sup> ಆದರೂ ನಾವು ಕಟ್ಟುವ ಕೆಲಸವನ್ನು ಮುಂದರಿಸಿದೆವು. ಜನರು ಅದಕ್ಕೆ ಮನಸ್ಸು ಕೊಟ್ಟದ್ದರಿಂದ ಗೋಡೆಯೆಲ್ಲಾ ಅರ್ಧ ಎತ್ತರದವರೆಗೂ ಜೋಡನೆಯಾಯಿತು.",
			"7": "<sup>7</sup> ಸನ್ಬಲ್ಲಟನೂ ಟೋಬೀಯನೂ ಅರಬಿಯರೂ ಅಮ್ಮೋನಿಯರೂ ಅಷ್ಡೋದಿನವರೂ ಯೆರೂಸಲೇಮಿನ ಗೋಡೆಯ ಜೀರ್ಣೋದ್ಧಾರಕಾರ್ಯವು ಮುಂದರಿದು ಅದರ ಸಂದುಗಳು ತಿರಿಗಿ ಕೂಡುತ್ತಾ ಬರುವದನ್ನು ಕೇಳಿ ಬಹುಕೋಪವುಳ್ಳವರಾಗಿ -",
			"8": "<sup>8</sup> ನಾವು ಒಟ್ಟಾಗಿ ಯೆರೂಸಲೇಮಿನವರಿಗೆ ವಿರುದ್ಧವಾಗಿ ಯುದ್ಧಕ್ಕೆ ಹೋಗಿ ಅವರನ್ನು ತಳಮಳಗೊಳಿಸೋಣ ಎಂದು ಒಳಸಂಚು ಮಾಡಿಕೊಂಡರು.",
			"9": "<sup>9</sup> ನಾವಾದರೋ ನಮ್ಮ ದೇವರಿಗೆ ಮೊರೆಯಿಟ್ಟು ಅವರಿಗೆ ವಿರುದ್ಧವಾಗಿ ಅವರು ಬರುವ ದಾರಿಯಲ್ಲಿ ಹಗಲಿರುಳು ಕಾವಲಿಟ್ಟೆವು.",
			"10": "<sup>10</sup> ಇತ್ತ ಯೆಹೂದ್ಯರು - ಹೊರೆಹೊರುವವರ ಬಲವು ಕುಂದಿ ಹೋಯಿತು, ಧೂಳಿನರಾಶಿಯು ವಿಪರೀತವಾಗಿದೆ; ಈ ಗೋಡೆಯನ್ನು ಕಟ್ಟುವದು ನಮ್ಮಿಂದಾಗದು ಅಂದರು.",
			"11": "<sup>11</sup> ಅತ್ತ ನಮ್ಮ ವಿರೋಧಿಗಳು - ಅವರಿಗೆ ತಿಳಿಯದೆಯೂ ಗೋಚರವಾಗದೆಯೂ ಫಕ್ಕನೆ ಅವರೊಳಗೆ ನುಗ್ಗಿ ಅವರನ್ನು ಕೊಂದು ಕೆಲಸವನ್ನು ನಿಲ್ಲಿಸಿಬಿಡೋಣ ಅಂದುಕೊಳ್ಳುತ್ತಿದ್ದರು.",
			"12": "<sup>12</sup> ಅವರ ನೆರೆಯೂರುಗಳಲ್ಲಿ ವಾಸಿಸುತ್ತಿದ್ದ ಯೆಹೂದ್ಯರು ಬಂದು ಬಂದು ನಮಗೆ - ನೀವು ಯಾವ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡರೂ ಆ ಎಲ್ಲಾ ಕಡೆಗಳಿಂದ ನಮಗೆ ವಿರೋಧವಾಗಿ ಯುದ್ಧಕ್ಕೆ ಬರುತ್ತಾರೆ ಎಂದು ಪದೇ ಪದೇ ತಿಳಿಸಿದ್ದರಿಂದ",
			"13": "<sup>13</sup> ನಾನು ಜನರಿಗೆ - ಕತ್ತಿ ಬಿಲ್ಲು ಬರ್ಜಿಗಳನ್ನು ಹಿಡಿದುಕೊಂಡು ಗೋಡೆಯ ಹಿಂದಣ ತಗ್ಗಾದ ಬೈಲುಗಳಲ್ಲಿ ಗೋತ್ರಗೋತ್ರಗಳಾಗಿ ನಿಲ್ಲಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿ",
			"14": "<sup>14</sup> ಅವರನ್ನು ಸಂದರ್ಶಿಸಿ ಅವರ ಮುಂದೆ ನಿಂತು ಶ್ರೀಮಂತರನ್ನೂ ಅಧಿಕಾರಿಗಳನ್ನೂ ಉಳಿದ ಜನರನ್ನೂ ಸಂಬೋಧಿಸಿ - ನಿಮ್ಮ ಹಗೆಗಳಿಗೆ ಹೆದರಬೇಡಿರಿ; ಮಹೋನ್ನತನೂ ಭಯಂಕರನೂ ಆಗಿರುವ ಕರ್ತನನ್ನು ನೆನಪು ಮಾಡಿಕೊಂಡು ನಿಮ್ಮ ಸಹೋದರರಿಗೋಸ್ಕರವೂ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳಿಗೋಸ್ಕರವೂ ಹೆಂಡತಿಯರಿಗೋಸ್ಕರವೂ ನಿಮ್ಮ ಮನೆಗಳಿಗೋಸ್ಕರವೂ ಕಾದಾಡಿರಿ ಎಂದು ಹೇಳಿದೆನು.",
			"15": "<sup>15</sup> ನಮ್ಮ ಶತ್ರುಗಳು ತಮ್ಮ ಹಂಚಿಕೆಯು ನಮಗೆ ಗೊತ್ತಾಯಿತೆಂದೂ ದೇವರು ಅದನ್ನು ವ್ಯರ್ಥ ಮಾಡಿದನೆಂದೂ ತಿಳಿದುಕೊಂಡಾಗ [ಅವರು ನಮ್ಮನ್ನು ಬಿಟ್ಟು ಹೋದರು;] ನಾವೆಲ್ಲರೂ ನಮ್ಮ ನಮ್ಮ ಗೋಡೆಯ ಕೆಲಸಕ್ಕೆ ತಿರಿಗಿ ಹೋದೆವು.",
			"16": "<sup>16</sup> ಅಂದಿನಿಂದ ನನ್ನ ಸ್ವಂತ ಆಳುಗಳಲ್ಲಿ ಅರ್ಧ ಮಂದಿ ಕೆಲಸದಲ್ಲಿ ಸಹಾಯಮಾಡಿದರು; ಅರ್ಧ ಮಂದಿ ಕವಚವನ್ನು ಧರಿಸಿಕೊಂಡು ಬರ್ಜಿ ಗುರಾಣಿ ಬಿಲ್ಲುಗಳನ್ನು ಹಿಡಿದುಕೊಂಡು ನಿಂತರು.",
			"17": "<sup>17</sup> ಅದೇ ಪ್ರಕಾರ ಪ್ರಭುಗಳೂ ಗೋಡೆಕಟ್ಟುವ ಯೆಹೂದ್ಯರ ಹಿಂದೆ ನಿಂತರು. ಹೊರೆ ಹೊರುವವರು ಒಂದು ಕೈಯಿಂದ ಹೊರೆಹೊತ್ತುಕೊಳ್ಳುತ್ತಾ ಇನ್ನೊಂದು ಕೈಯಿಂದ ಈಟಿ ಹಿಡಿದುಕೊಳ್ಳುತ್ತಿದ್ದರು.",
			"18": "<sup>18</sup> ಕಟ್ಟುವವರಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬನು ತನ್ನ ತನ್ನ ಕತ್ತಿಯನ್ನು ಸೊಂಟಕ್ಕೆ ಬಿಗಿದುಕೊಂಡು ಕಟ್ಟುತ್ತಿದ್ದನು. ನಾನಂತೂ ಕೊಂಬೂದುವವನೊಬ್ಬನನ್ನು ನನ್ನ ಬಳಿಯಲ್ಲಿರಿಸಿಕೊಂಡು",
			"19": "<sup>19</sup> ಶ್ರೀಮಂತರಿಗೂ ಅಧಿಕಾರಿಗಳಿಗೂ ಉಳಿದ ಜನರಿಗೂ - ನಾವು ಮಾಡುವ ಕೆಲಸವು ಬಲುದೊಡ್ಡದೂ ಅನೇಕ ಸ್ಥಳಗಳಲ್ಲಿ ನಡೆಯುವಂಥದೂ ಆಗಿದೆ; ನಾವು ಗೋಡೆಯ ಮೇಲೆ ಚದರಿ ಒಬ್ಬರಿಗೊಬ್ಬರು ದೂರವಾಗಿರುತ್ತೇವೆ.",
			"20": "<sup>20</sup> ನಿಮಗೆ ಕೊಂಬಿನ ಧ್ವನಿಯು ಯಾವ ಸ್ಥಳದಿಂದ ಕೇಳಬರುವದೋ ಆ ಸ್ಥಳಕ್ಕೆ ನಮ್ಮ ಹತ್ತಿರ ಕೂಡಿಬನ್ನಿರಿ. ನಮ್ಮ ದೇವರು ನಮಗೋಸ್ಕರ ಯುದ್ಧಮಾಡುವನು ಎಂದು ಹೇಳಿದೆನು.",
			"21": "<sup>21</sup> ಹೀಗೆ ನಾವು ಅರುಣೋದಯದಿಂದ ನಕ್ಷತ್ರಗಳು ಮೂಡುವವರೆಗೂ ಕೆಲಸ ಮಾಡುತ್ತಿದ್ದೆವು; [ನನ್ನ ಸೇವಕರಲ್ಲಿ] ಅರ್ಧ ಮಂದಿ ಬರ್ಜಿ ಹಿಡಿದುಕೊಂಡಿದ್ದರು.",
			"22": "<sup>22</sup> ಇದಲ್ಲದೆ ನಾನು ಆ ಸಮಯದಲ್ಲಿ ಜನರಿಗೆ - ನಿಮ್ಮ ಆಳುಗಳು ರಾತ್ರಿಯಲ್ಲಿ ನಮಗೆ ಕಾವಲಾಗಿರುವ ಹಾಗೂ ಹಗಲಿನಲ್ಲಿ ಕೆಲಸ ನಡಿಸುವ ಹಾಗೂ ನೀವು ನಿಮ್ಮ ನಿಮ್ಮ ಆಳುಗಳೊಡನೆ ಯೆರೂಸಲೇಮಿನಲ್ಲೇ ವಾಸಮಾಡಬೇಕು ಎಂದು ಅಪ್ಪಣೆ ಮಾಡಿದೆನು.",
			"23": "<sup>23</sup> ನಾನೂ ನನ್ನ ಸಹೋದರರೂ ಸೇವಕರೂ ಮೈಗಾವಲಿನವರೂ ಸ್ನಾನಸಮಯದಲ್ಲಿ ಹೊರತು ಬೇರೆ ಸಮಯದಲ್ಲಿ ನಮ್ಮ ವಸ್ತ್ರಗಳನ್ನು ತೆಗೆದುಹಾಕಲಿಲ್ಲ."
		},
		"5": {
			"1": "<sup>1</sup> ತರುವಾಯ ಸಾಧಾರಣಜನರೂ ಅವರ ಹೆಂಡತಿಯರೂ ತಮ್ಮ ಬಂಧುಗಳಾದ ಯೆಹೂದ್ಯರಿಗೆ ವಿರುದ್ಧವಾಗಿ ಬಹಳ ಗುಣುಗುಟ್ಟತೊಡಗಿದರು.",
			"2": "<sup>2</sup> ಕೆಲವರು - ನಾವೂ ನಮ್ಮ ಗಂಡುಹೆಣ್ಣು ಮಕ್ಕಳೂ ಬಹುಮಂದಿ ಇದ್ದೇವೆ; ನಮ್ಮ ಜೀವನಕ್ಕಾಗಿ ಕಾಳುಕಡ್ಡಿಗಳನ್ನು ಹೇಗಾದರೂ ಸಂಪಾದಿಸಿಕೊಳ್ಳೋಣ;",
			"3": "<sup>3</sup> ಇನ್ನು ಕೆಲವರು - ನಮ್ಮ ಹೊಲ ತೋಟಮನೆಗಳನ್ನು ಒತ್ತೆ ಹಾಕಿ ಆಯಿತು; ಈ ಬರಗಾಲದಲ್ಲಿ ಧಾನ್ಯವನ್ನು [ಹೇಗಾದರೂ] ಸಂಪಾದಿಸಿಕೊಳ್ಳೋಣ;",
			"4": "<sup>4</sup> ಮತ್ತು ಕೆಲವರು - ಸರಕಾರಕ್ಕೆ ಕಂದಾಯಕೊಡುವದಕ್ಕಾಗಿ ನಾವು ನಮ್ಮ ಹೊಲ ತೋಟಗಳ ಮೇಲೆ ಸಾಲ ತೆಗೆದುಕೊಳ್ಳಬೇಕಾಯಿತು;",
			"5": "<sup>5</sup> ಆ ನಮ್ಮ ಸಹೋದರರ ಕುಲಕ್ಕೂ ನಮ್ಮ ಕುಲಕ್ಕೂ ಅವರ ಮಕ್ಕಳಿಗೂ ನಮ್ಮ ಮಕ್ಕಳಿಗೂ ಏನು ಹೆಚ್ಚುಕಡಿಮೆ? ನೋಡಿರಿ, ನಾವು ನಮ್ಮ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ಪರರಿಗೆ ದಾಸರನ್ನಾಗಿ ಕೊಡಬೇಕಾಯಿತು; ನಮ್ಮ ಹೆಣ್ಣು ಮಕ್ಕಳಲ್ಲಿ ಕೆಲವರನ್ನು ಇಷ್ಟರಲ್ಲೇ ಅಪಹರಿಸಿದರು; ನಮ್ಮ ಯತ್ನವೇನೂ ಸಾಗುವದಿಲ್ಲ. ನಮ್ಮ ಹೊಲತೋಟಗಳು ಪರಾಧೀನವಾದವು ಎಂದು ಗುಣುಗುಟ್ಟಿದರು.",
			"6": "<sup>6</sup> ಅವರ ಬೊಬ್ಬೆಯನ್ನೂ ಮಾತುಗಳನ್ನೂ ಕೇಳಿದಾಗ ನನಗೆ ಬಲು ಸಿಟ್ಟಾಯಿತು.",
			"7": "<sup>7</sup> ನಾನು ತುಸು ಆಲೋಚಿಸಿಕೊಂಡು ಶ್ರೀಮಂತರಿಗೂ ಅಧಿಕಾರಿಗಳಿಗೂ - ನೀವು ನಿಮ್ಮ ಸಹೋದರರಿಂದ ಬಡ್ಡಿ ತೆಗೆದುಕೊಳ್ಳುವದೆಂದರೇನು ಎಂದು ಹೇಳಿ ಅವರೊಡನೆ ಬಹಳವಾಗಿ ಹೆಣಗಾಡಿ ಅವರಿಗೆ ವಿರೋಧವಾಗಿ ಮಹಾಸಭೆಯನ್ನು ಕೂಡಿಸಿ -",
			"8": "<sup>8</sup> ನಾವು ನಮ್ಮಿಂದಾಗುವಷ್ಟು ಮಟ್ಟಿಗೆ ನಮ್ಮ ಸಹೋದರರಾದ ಯೆಹೂದ್ಯರಲ್ಲಿ ಅನ್ಯಜನರಿಗೆ ಮಾರಲ್ಪಟ್ಟವರನ್ನು ಹಣಕೊಟ್ಟು ಬಿಡಿಸುತ್ತಿದ್ದೆವು. ಈಗ ನೀವು ನಿಮ್ಮ ಸಹೋದರರನ್ನು ಮಾರಿಬಿಡುತ್ತೀರಿ; ಆಮೇಲೆ ಅವರನ್ನು ನಾವು ಕೊಂಡುಕೊಳ್ಳಬೇಕೇನು ಅನ್ನಲು ಅವರು ಉತ್ತರಕೊಡದೆ ಸುಮ್ಮನಿದ್ದರು.",
			"9": "<sup>9</sup> ಆಮೇಲೆ ನಾನು ಅವರಿಗೆ - ನೀವು ಮಾಡಿದ್ದು ಒಳ್ಳೇದಲ್ಲ; ನಾವು ನಮ್ಮ ವಿರೋಧಿಗಳಾಗಿರುವ ಅನ್ಯಜನರ ನಿಂದೆಗೆ ಗುರಿಯಾಗದಂತೆ ದೇವರಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿ ನಡೆದುಕೊಳ್ಳಬೇಕಲ್ಲವೇ.",
			"10": "<sup>10</sup> ನಾನೂ ನನ್ನ ಸಹೋದರರೂ ಸೇವಕರೂ ಸಹ ಅವರಿಗೆ ಹಣವನ್ನೂ ಧಾನ್ಯವನ್ನೂ ಬಡ್ಡಿಗೆ ಕೊಟ್ಟಿದ್ದೇವೆ. ಬಡ್ಡಿ ತೆಗೆದುಕೊಳ್ಳುವ ಪದ್ಧತಿಯನ್ನು ಬಿಟ್ಟುಬಿಡೋಣ.",
			"11": "<sup>11</sup> ಅವರ ಹೊಲ, ದ್ರಾಕ್ಷೇತೋಟ, ಎಣ್ಣೆಮರಗಳ ತೋಪು, ಮನೆ ಇವುಗಳನ್ನೂ ನೀವು ಕೊಟ್ಟ ಹಣ, ಧಾನ್ಯ, ದ್ರಾಕ್ಷಾರಸ, ಎಣ್ಣೆ ಇವುಗಳಿಗೋಸ್ಕರ ಶೇಕಡಾ ಇಷ್ಟೆಂದು ತೆಗೆದುಕೊಂಡಿರುವ ಬಡ್ಡಿಯನ್ನೂ ದಯವಿಟ್ಟು ಈಹೊತ್ತೇ ಹಿಂದಕ್ಕೆ ಕೊಡಿರಿ ಎಂದು ಹೇಳಿದೆನು.",
			"12": "<sup>12</sup> ಅವರು - ಹಿಂದಕ್ಕೆ ಕೊಡುತ್ತೇವೆ; ಅವರಿಂದ ಏನೂ ಕೇಳುವದಿಲ್ಲ. ನೀನು ಹೇಳಿದಂತೆಯೇ ಮಾಡುತ್ತೇವೆ ಎಂದು ಉತ್ತರಕೊಟ್ಟರು. ಆಗ ನಾನು ಯಾಜಕರನ್ನು ಕರಿಸಿ ಈ ಮಾತಿನಂತೆ ನಡೆಯುವದಾಗಿ ಅವರಿಂದ ಪ್ರಮಾಣ ಮಾಡಿಸಿದೆನು.",
			"13": "<sup>13</sup> ಇದಲ್ಲದೆ ನನ್ನ ಉಡಿಲನ್ನು ಝಾಡಿಸಿ ಅವರಿಗೆ - ಈಗ ಕೊಟ್ಟ ಮಾತನ್ನು ಕೈಕೊಳ್ಳದ ಪ್ರತಿಯೊಬ್ಬನನ್ನು ದೇವರು ಅವನ ಮನೆಯಿಂದಲೂ ಸ್ವಾಸ್ತ್ಯದಿಂದಲೂ ಈ ಪ್ರಕಾರವೇ ಝಾಡಿಸಿ ಬಿಡಲಿ; ಅವನು ಉಡಿಲಿನಂತೆಯೇ ಝಾಡಿಸಲ್ಪಟ್ಟು ಬರಿದಾಗಲಿ ಅಂದೆನು. ಕೂಡಲೆ ಸಭೆಯವರೆಲ್ಲಾ ಹಾಗೆಯೇ ಆಗಲಿ ಎಂದು ಹೇಳಿ ಯೆಹೋವನನ್ನು ಕೊಂಡಾಡಿ ಕೊಟ್ಟ ಮಾತನ್ನು ಕೈಕೊಂಡರು.",
			"14": "<sup>14</sup> ಅರಸನಾದ ಅರ್ತಷಸ್ತನು ನನ್ನನ್ನು ಯೆಹೂದ ದೇಶದ ಅಧಿಪತಿಯನ್ನಾಗಿ ನೇಮಿಸಿದಂದಿನಿಂದ ಹನ್ನೆರಡು ವರುಷಗಳವರೆಗೆ ಅಂದರೆ ಅವನ ಆಳಿಕೆಯ ಇಪ್ಪತ್ತನೆಯ ವರುಷದಿಂದ ಮೂವತ್ತೆರಡನೆಯ ವರುಷದವರೆಗೆ ನಾನಾಗಲಿ ನನ್ನ ಸಹೋದರರಾಗಲಿ ದೇಶಾಧಿಪತಿಗೆ ಸಲ್ಲತಕ್ಕ ಆಯದಿಂದ ಜೀವನಮಾಡಲಿಲ್ಲ.",
			"15": "<sup>15</sup> ನನಗಿಂತ ಮುಂಚೆಯಿದ್ದ ದೇಶಾಧಿಪತಿಗಳು ಜನರ ಮೇಲೆ ಬಹಳ ಭಾರಹಾಕಿ ಅವರಿಂದ [ದಿನಕ್ಕೆ] ನಾಲ್ವತ್ತು ರೂಪಾಯಿಯ ಆಹಾರವನ್ನೂ ದ್ರಾಕ್ಷಾರಸವನ್ನೂ ತೆಗೆದುಕೊಂಡದ್ದಲ್ಲದೆ ಅವರ ಸೇವಕರೂ ಜನರ ಮೇಲೆ ದೊರೆತನ ನಡಿಸಿದರು. ನಾನಾದರೋ ದೇವರಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವನಾಗಿ ಇರುವದರಿಂದ ಹಾಗೆ ಮಾಡದೆ ಆ ಗೋಡೆ ಕಟ್ಟುವದರಲ್ಲಿ ನಿರತನಾಗಿದ್ದೆನು.",
			"16": "<sup>16</sup> ಮತ್ತು ನನ್ನ ಎಲ್ಲಾ ಸೇವಕರೂ ಪ್ರತಿದಿನ ಆ ಕೆಲಸಕ್ಕೆ ಬರುವಂತೆ ನೋಡುತ್ತಿದ್ದೆನು. ನಾವು ಅಲ್ಲಿ ಭೂಸ್ಥಿತಿಯನ್ನು ಸಂಪಾದಿಸಿಕೊಂಡಿರಲಿಲ್ಲ.",
			"17": "<sup>17</sup> ಇದಲ್ಲದೆ ಯೆಹೂದ್ಯರಲ್ಲಿ ನೂರೈವತ್ತು ಮಂದಿ ಅಧಿಕಾರಿಗಳೂ ಸುತ್ತಣ ಜನಾಂಗಗಳ ಮಧ್ಯದಿಂದ ನಮ್ಮ ಬಳಿಗೆ ಬರುತ್ತಿದ್ದವರೂ ನನ್ನ ಪಂಕ್ತಿಯಲ್ಲಿ ಊಟಮಾಡುತ್ತಿದ್ದರು.",
			"18": "<sup>18</sup> ಪ್ರತಿದಿನ ಒಂದು ಹೋರಿ ಆರು ಕೊಬ್ಬಿದ ಕುರಿ ಕೆಲವು ಕೋಳಿಗಳು ನಮ್ಮ ಭೋಜನಕ್ಕಾಗಿ ಸಿದ್ಧವಾಗುತ್ತಿದ್ದವು. ಹತ್ತು ದಿವಸಕ್ಕೊಮ್ಮೆ ನಾನು ಎಲ್ಲಾ ತರದ ದ್ರಾಕ್ಷಾರಸವನ್ನು ಬೇಕಾಗುವಷ್ಟು ಒದಗಿಸಿಕೊಡುತ್ತಿದ್ದೆನು. ನನಗೆ ಇಷ್ಟು ವೆಚ್ಚವಾಗುತ್ತಿದ್ದರೂ ಆ ಜನರು ಮಾಡತಕ್ಕ ಕೆಲಸವು ಬಹು ಭಾರವಾಗಿದ್ದದರಿಂದ ನಾನು ದೇಶಾಧಿಪತಿಗೆ ಸಲ್ಲತಕ್ಕ ಆಯವನ್ನು ಕೇಳಲೇ ಇಲ್ಲ.",
			"19": "<sup>19</sup> ನನ್ನ ದೇವರೇ, ನಾನು ಆ ಜನರಿಗೋಸ್ಕರ ಮಾಡಿದ್ದೆಲ್ಲವನ್ನೂ ನನ್ನ ಹಿತಕ್ಕಾಗಿ ನೆನಪು ಮಾಡಿಕೋ."
		},
		"6": {
			"1": "<sup>1</sup> ನಾನು ಗೋಡೆಯನ್ನು ಕಟ್ಟಿಸಿ ತೀರಿಸಿ ಬಾಗಲುಗಳಿಗೆ ಕದಗಳನ್ನು ಇನ್ನೂ ಹಚ್ಚಿಸದಿದ್ದರೂ ಎಲ್ಲಾ ಸಂದುಗಳನ್ನು ಕೂಡಿಸಿದ ಸಂಗತಿಯು ಸನ್ಬಲ್ಲಟ್, ಟೋಬೀಯ, ಅರಬಿಯನಾದ ಗೆಷೆಮ್ ಇವರಿಗೂ",
			"2": "<sup>2</sup> ನಮ್ಮ ಬೇರೆ ವಿರೋಧಿಗಳಿಗೂ ಗೊತ್ತಾದಾಗ ಅವರಲ್ಲಿ ಸನ್ಬಲ್ಲಟ್ ಗೆಷೆಮರು ನನಗೆ - ಓನೋ ತಗ್ಗಿನ ಹಕ್ಕೆಫಿರೀಮಿನಲ್ಲಿ ಒಬ್ಬರನ್ನೊಬ್ಬರು ಎದುರುಗೊಳ್ಳೋಣ ಬಾ ಎಂದು ಹೇಳಿಕಳುಹಿಸಿದರು.",
			"3": "<sup>3</sup> ಅವರು ನನಗೆ ಕೇಡು ಬಗೆಯುವವರು ಎಂದು ತಿಳಿದು ನಾನು ದೂತರ ಮುಖಾಂತರವಾಗಿ ಅವರಿಗೆ - ನನಗೆ ದೊಡ್ಡ ಜಂಬರವುಂಟು; ಅದನ್ನು ಬಿಟ್ಟು ನಿಮ್ಮ ಬಳಿಗೆ ಬಂದರೆ ಕೆಲಸವು ನಿಂತುಹೋಗುವದು; ನಾನು ಬರಲಾರೆನು ಎಂದು ಹೇಳಿಸಿದೆನು.",
			"4": "<sup>4</sup> ಅವರು ನಾಲ್ಕು ಸಾರಿ ನನಗೆ ಅದೇ ಪ್ರಕಾರ ಹೇಳಿ ಕಳುಹಿಸಿದರೂ ನಾನು ಅದೇ ಉತ್ತರಕೊಟ್ಟೆನು.",
			"5": "<sup>5</sup> ಐದನೆಯ ಸಾರಿ ಸನ್ಬಲ್ಲಟನು ಅದೇ ಉದ್ದೇಶದಿಂದ ತನ್ನ ಸ್ವಂತ ಸೇವಕನ ಮುಖಾಂತರವಾಗಿ ಮುಚ್ಚದಿರುವ ಒಂದು ಪತ್ರವನ್ನು ಕಳುಹಿಸಿದನು.",
			"6": "<sup>6</sup> ಅದರಲ್ಲಿ - ಗೆಷೆಮ್ ಎಂಬವನು ನನಗೆ ತಿಳಿಸಿದ ಪ್ರಕಾರ ನೀನೂ ಯೆಹೂದ್ಯರೂ ತಿರುಗಿಬೀಳಬೇಕೆಂದಿದ್ದೀರಿ; ಆದದರಿಂದಲೇ ನೀನು ಆ ಗೋಡೆಯನ್ನು ಕಟ್ಟಿಸುತ್ತೀ. ಮತ್ತು ನೀನು ಅವರ ಅರಸನಾಗುವಿಯೆಂಬ ಸುದ್ದಿಯು ಜನರಲ್ಲಿ ಹಬ್ಬಿರುತ್ತದೆ.",
			"7": "<sup>7</sup> ಇದಲ್ಲದೆ ಯೆಹೂದದಲ್ಲಿ ಒಬ್ಬ ಅರಸನಿರುತ್ತಾನೆಂದು ನಿನ್ನನ್ನು ಕುರಿತು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಪ್ರಕಟಿಸುವದಕ್ಕೋಸ್ಕರ ಪ್ರವಾದಿಗಳನ್ನು ನೇಮಿಸಿದ್ದಿ ಎಂಬದಾಗಿ ಹೇಳುತ್ತಾರೆ. ಈ ಸುದ್ದಿ ರಾಜನಿಗೂ ಮುಟ್ಟುವದು; ಆದದರಿಂದ ಕೂಡಿ ಮಾತಾಡೋಣ ಬಾ ಎಂದು ಬರೆದಿತ್ತು.",
			"8": "<sup>8</sup> ಅದಕ್ಕೆ - ನೀನು ಬರೆದಂಥದು ಏನೂ ನಡೆದಿಲ್ಲ; ಅದನ್ನು ನೀನೇ ನಿನ್ನ ಮನಸ್ಸಿನಲ್ಲಿ ಕಲ್ಪಿಸಿಕೊಂಡಿದ್ದಿ ಎಂದು ಹೇಳಿ ಕಳುಹಿಸಿದೆನು.",
			"9": "<sup>9</sup> ಹೀಗೆ ಎಲ್ಲರೂ - ಇವರ ಕೈಗಳು ಜೋಲು ಬಿದ್ದು ಕೆಲಸವನ್ನು ತೀರಿಸದೆ ಬಿಡಲಿ ಅಂದುಕೊಂಡು ನಮ್ಮನ್ನು ಹೆದರಿಸುವದಕ್ಕೆ ಪ್ರಯತ್ನಿಸಿದರು. [ನನ್ನ ದೇವರೇ,] ನನ್ನ ಕೈಗಳನ್ನು ಬಲಪಡಿಸು.",
			"10": "<sup>10</sup> ಒಂದು ದಿವಸ ನಾನು ಮೆಹೇಟಬೇಲನ ಮೊಮ್ಮಗನೂ ದೆಲಾಯನ ಮಗನೂ ಆದ ಶೆಮಾಯನ ಮನೆಗೆ ಹೋದೆನು. ಅವನು ಅಲ್ಲಿ ಅಡಗಿಕೊಂಡಿದ್ದನು. ಅವನು ನನಗೆ - ನಿನ್ನನ್ನು ಕೊಲ್ಲುವದಕ್ಕೆ ಬರುತ್ತಾರೆ, ಈ ರಾತ್ರಿಯೇ ಬರುತ್ತಾರೆ. ಆದದರಿಂದ ನಾವಿಬ್ಬರೂ ದೇವಾಲಯಕ್ಕೆ ಹೋಗಿ ಪವಿತ್ರಸ್ಥಾನವನ್ನು ಪ್ರವೇಶಿಸಿ ಅದರ ಕದಗಳನ್ನು ಮುಚ್ಚಿಕೊಳ್ಳೋಣ ಬಾ ಎಂದು ಹೇಳಿದನು.",
			"11": "<sup>11</sup> ನಾನು - ನನ್ನಂಥ ಪುರುಷನು ಓಡಿಹೋಗುವದು ಯೋಗ್ಯವೋ? ಇದಲ್ಲದೆ ನನ್ನಂಥವನು ಪ್ರಾಣ ರಕ್ಷಣೆಗಾಗಿ ಪವಿತ್ರಸ್ಥಾನವನ್ನು ಪ್ರವೇಶಿಸುವನೇ? ನಾನು ಬರುವದಿಲ್ಲ ಎಂದು ಉತ್ತರಕೊಟ್ಟೆನು.",
			"12": "<sup>12</sup> ನಾನು ಸೂಕ್ಷ್ಮವಾಗಿ ವಿಚಾರಮಾಡಿದಾಗ ನನ್ನ ವಿಷಯವಾಗಿ ಆ ಕಾಲಜ್ಞಾನವಚನವನ್ನು ಉಚ್ಚರಿಸುವದಕ್ಕೆ ಇವನನ್ನು ಪ್ರೇರಿಸಿದವನು ದೇವರಲ್ಲ, ಟೋಬೀಯನೂ ಸನ್ಬಲ್ಲಟನೂ ಇವನಿಗೆ ಲಂಚಕೊಟ್ಟು ಹಾಗೆ ಹೇಳಿಸಿದರು ಎಂದು ನನಗೆ ಗೊತ್ತಾಯಿತು.",
			"13": "<sup>13</sup> ನಾನು ಅಂಜಿಕೊಂಡು ಇವನ ಮಾತಿನಂತೆ ನಡೆದು ದೋಷಿಯಾಗಿ ತಮ್ಮ ನಿಂದೆಗೂ ಕೆಟ್ಟ ಹೆಸರಿಗೂ ಒಳಗಾಗಲಿ ಎಂದು ಅವರು ಇವನಿಗೆ ಲಂಚಕೊಟ್ಟಿದ್ದರು.",
			"14": "<sup>14</sup> ನನ್ನ ದೇವರೇ, ಟೋಬೀಯ ಸನ್ಬಲ್ಲಟರು ಮಾಡಿದ ಈ ದುಷ್ಕೃತ್ಯಗಳು ನಿನ್ನ ನೆನಪಿನಲ್ಲಿರಲಿ; ನನ್ನನ್ನು ಹೆದರಿಸುವದಕ್ಕೆ ಪ್ರಯತ್ನಿಸಿದ ಪ್ರವಾದಿನಿಯಾದ ನೋವದ್ಯ ಮುಂತಾದ ಪ್ರವಾದಿಗಳನ್ನು ಮರೆಯಬೇಡ.",
			"15": "<sup>15</sup> ಗೋಡೆಯು ಭಾದ್ರಪದ ಮಾಸದ ಇಪ್ಪತ್ತೈದನೆಯ ದಿನದಲ್ಲಿ ತೀರಿತು. ಅದನ್ನು ಕಟ್ಟುವದಕ್ಕೆ ಒಟ್ಟು ಐವತ್ತೆರಡು ದಿನಗಳು ಹಿಡಿದವು.",
			"16": "<sup>16</sup> ಈ ಸುದ್ದಿಯು ನಮ್ಮ ವಿರೋಧಿಗಳಾದ ಸುತ್ತಣ ಎಲ್ಲಾ ಜನಾಂಗಗಳವರಿಗೆ ಮುಟ್ಟಿದಾಗ ಅವರು ಭಯವುಳ್ಳವರಾಗಿ ಸೊಕ್ಕನ್ನು ಬಿಟ್ಟು ಬಹಳ ಮನಗುಂದಿದವರಾದರು. ಈ ಕಾರ್ಯವು ನಮ್ಮ ದೇವರಿಂದಲೇ ಪೂರೈಸಿತು ಎಂದು ಅವರಿಗೆ ಮಂದಟ್ಟಾಯಿತು.",
			"17": "<sup>17</sup> ಅದೇ ಸಮಯದಲ್ಲಿ ಯೆಹೂದ್ಯ ಶ್ರೀಮಂತರಿಗೂ ಟೋಬೀಯನಿಗೂ ಬಹಳ ಪತ್ರವ್ಯವಹಾರ ನಡೆಯುತ್ತಿತ್ತು.",
			"18": "<sup>18</sup> ಯಾಕಂದರೆ ಅವನು ಆರಹನ ಮಗನಾದ ಶೆಕನ್ಯನ ಅಳಿಯನೂ ಯೆಹೋಹಾನಾನನೆಂಬ ಅವನ ಮಗನು ಬೆರೆಕ್ಯನ ಮಗನಾದ ಮೆಷುಲ್ಲಾಮನ ಮಗಳನ್ನು ಮದುವೆಮಾಡಿಕೊಂಡವನೂ ಆಗಿದ್ದದರಿಂದ ಯೆಹೂದ್ಯರಲ್ಲಿ ಅನೇಕರು ಅವನಿಗೆ ಪ್ರಮಾಣಮಾಡಿ ವಶರಾಗಿದ್ದರು.",
			"19": "<sup>19</sup> ಅವರು ಅವನ ಒಳ್ಳೆತನವನ್ನೇ ನನ್ನ ಮುಂದೆ ವರ್ಣಿಸುತ್ತಾ ನನ್ನ ಮಾತುಗಳನ್ನು ಅವನಿಗೆ ತಿಳಿಸುತ್ತಾ ಇದ್ದರು. ಟೋಬೀಯನೂ ನನ್ನಲ್ಲಿ ಭಯಹುಟ್ಟಿಸುವದಕ್ಕೋಸ್ಕರ ನನಗೆ ಪತ್ರಗಳನ್ನು ಕಳುಹಿಸುತ್ತಿದ್ದನು."
		},
		"7": {
			"1": "<sup>1</sup> ಗೋಡೆಯನ್ನು ಕಟ್ಟಿ ತೀರಿಸಿದ ನಂತರ ನಾನು ಕದಗಳನ್ನು ಹಚ್ಚಿಸಿದೆನು; ದ್ವಾರಪಾಲಕರೂ ಗಾಯಕರೂ ಲೇವಿಯರೂ ನೇಮಿಸಲ್ಪಟ್ಟರು.",
			"2": "<sup>2</sup> ಆಮೇಲೆ ನನ್ನ ತಮ್ಮನಾದ ಹನಾನಿಗೂ ಬಲು ನಂಬಿಗಸ್ತನೂ ದೇವರಲ್ಲಿ ವಿಶೇಷ ಭಯಭಕ್ತಿಯುಳ್ಳವನೂ ದುರ್ಗಾಧಿಕಾರಿಯೂ ಆದ ಹನನ್ಯನಿಗೂ ಯೆರೂಸಲೇಮಿನ ಮೇಲ್ವಿಚಾರಣೆಯನ್ನು ಒಪ್ಪಿಸಿ",
			"3": "<sup>3</sup> ಅವರಿಗೆ - ಬಿಸಿಲು ಬಲಿಯುವದಕ್ಕಿಂತ ಮೊದಲು ಯೆರೂಸಲೇಮಿನ ಬಾಗಲುಗಳನ್ನು ತೆರೆಯಬಾರದು; ಕಾವಲುಗಾರರು ಇನ್ನೂ ಇರುವಷ್ಟರಲ್ಲೇ ಕದಗಳನ್ನು ಮುಚ್ಚಿ ಭದ್ರಪಡಿಸಬೇಕು. ಇದಲ್ಲದೆ ಯೆರೂಸಲೇಮಿನ ನಿವಾಸಿಗಳೊಳಗೆ ಕಾವಲುಗಾರರನ್ನು ಗೊತ್ತುಮಾಡಿರಿ. ಅವರಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬನನ್ನು ಅವನವನ ಮನೆಯ ಎದುರಿನಲ್ಲಿ ಕಾವಲಿರುವಂತೆ ನೇಮಿಸಬೇಕು ಎಂದು ಆಜ್ಞಾಪಿಸಿದೆನು.",
			"4": "<sup>4</sup> ಪಟ್ಟಣವು ಎಲ್ಲಾ ಕಡೆಗಳಲ್ಲಿ ವಿಸ್ತಾರವಾಗಿದ್ದರೂ ಅದರೊಳಗೆ ಬಹು ಸ್ವಲ್ಪಜನರಿದ್ದರು. ಮನೆಗಳನ್ನು ಇನ್ನೂ ಕಟ್ಟಿರಲಿಲ್ಲ.",
			"5": "<sup>5</sup> ಹೀಗಿರಲಾಗಿ ನಾನು ನನ್ನ ದೇವರ ಪ್ರೇರಣೆಯಿಂದ ಶ್ರೀಮಂತರನ್ನೂ ಅಧಿಕಾರಿಗಳನ್ನೂ ಸಾಧಾರಣಜನರನ್ನೂ ಖಾನೇಷುಮಾರಿಗೋಸ್ಕರ ಕೂಡಿಸಿದೆನು. ಆಗ [ಯೆರೂಸಲೇಮಿಗೆ] ಮೊದಲು ಬಂದವರ ಹೆಸರುಗಳ ಪಟ್ಟಿಯು ನನಗೆ ಸಿಕ್ಕಿತು. ಅದರಲ್ಲಿ ಬರೆದಿದ್ದದ್ದೇನಂದರೆ -",
			"6": "<sup>6</sup> ಸೆರೆಯಿಂದ ತಿರಿಗಿ ಬಂದ [ಯೆಹೂದ] ಸಂಸ್ಥಾನದವರ ಖಾನೇಷುಮಾರಿ. ಬಾಬೆಲಿನ ಅರಸನಾದ ನೆಬೂಕದ್ನೆಚ್ಚರನಿಂದ ಸೆರೆ ಒಯ್ಯಲ್ಪಟ್ಟವರಲ್ಲಿ ಯೆರೂಸಲೇಮಿಗೂ ಯೆಹೂದಪ್ರಾಂತದ ಸ್ವಂತ ಪಟ್ಟಣಗಳಿಗೂ",
			"7": "<sup>7</sup> ಜೆರುಬ್ಬಾಬೆಲ್, ಯೇಷೂವ, ನೆಹೆಮೀಯ, ಅಜರ್ಯ, ರಗಮ್ಯ, ನಹಮಾನೀ, ಮೊರ್ದೆಕೈ, ಬಿಲ್ಷಾನ್, ಮಿಸ್ಪೆರೆತ್, ಬಿಗ್ವೈ, ನೆಹೂಮ್, ಬಾಣ ಎಂಬ ನಾಯಕರೊಡನೆ ತಿರಿಗಿ ಬಂದವರು ಯಾರಂದರೆ -",
			"8": "<sup>8</sup> ಪರೋಷಿನವರು 2172",
			"9": "<sup>9</sup> ಶೆಫಟ್ಯನವರು 372",
			"10": "<sup>10</sup> ಆರಹನವರು 652",
			"11": "<sup>11</sup> ಪಹತ್‍ಮೋವಾಬಿನವರಾದ ಯೇಷೂವ ಮತ್ತು ಯೋವಾಬ್ ಸಂತಾನದವರು 2818",
			"12": "<sup>12</sup> ಏಲಾಮಿನವರು 1254",
			"13": "<sup>13</sup> ಜತ್ತೂವಿನವರು 845",
			"14": "<sup>14</sup> ಜಕ್ಕೈಯವರು 760",
			"15": "<sup>15</sup> ಬಿನ್ನೂಯವರು 648",
			"16": "<sup>16</sup> ಬೇಬೈಯವರು 628",
			"17": "<sup>17</sup> ಅಜ್ಗಾದಿನವರು 2322",
			"18": "<sup>18</sup> ಅದೋನೀಕಾಮಿನವರು 667",
			"19": "<sup>19</sup> ಬಿಗ್ವೈಯವರು 2067",
			"20": "<sup>20</sup> ಆದೀನನವರು 655",
			"21": "<sup>21</sup> ಆಟೇರಿನವರಾದ ಹಿಜ್ಕೀಯನ ಸಂತಾನದವರು 98",
			"22": "<sup>22</sup> ಹಾಷುಮಿನವರು 328",
			"23": "<sup>23</sup> ಬೇಚೈಯವರು 324",
			"24": "<sup>24</sup> ಹಾರಿಫಿನವರು 112",
			"25": "<sup>25</sup> ಗಿಬ್ಯೋನಿನವರು 95",
			"26": "<sup>26</sup> ಬೇತ್ಲೆಹೇಮ್ ನೆಟೋಫ ಊರುಗಳವರು 188",
			"27": "<sup>27</sup> ಅನಾತೋತ್ ಊರಿನವರು 128",
			"28": "<sup>28</sup> ಬೇತಜ್ಮಾವೇತಿನವರು 42",
			"29": "<sup>29</sup> ಕಿರ್ಯತ್ಯಾರೀಮ್ ಕೆಫೀರಾ ಬೇರೋತ್ ಊರುಗಳವರು 743",
			"30": "<sup>30</sup> ರಾಮಾ ಗೆಬ ಊರುಗಳವರು 621",
			"31": "<sup>31</sup> ಮಿಕ್ಮಾಸಿನವರು 122",
			"32": "<sup>32</sup> ಬೇತೇಲ್ ಆಯಿ ಎಂಬ ಊರುಗಳವರು 123",
			"33": "<sup>33</sup> ಎರಡನೆಯ ನೆಬೋವಿನವರು 52",
			"34": "<sup>34</sup> ಎರಡನೆಯ ಏಲಾಮಿನವರು 1254",
			"35": "<sup>35</sup> ಹಾರಿಮನವರು 320",
			"36": "<sup>36</sup> ಯೆರಿಕೋವಿನವರು 345",
			"37": "<sup>37</sup> ಲೋದ್, ಹಾದೀದ್, ಓನೋ ಎಂಬ ಊರುಗಳವರು 721",
			"38": "<sup>38</sup> ಸೆನಾಹನವರು 3930",
			"39": "<sup>39</sup> ಯಾಜಕರಲ್ಲಿ - ಯೆದಾಯನವರಾದ ಯೇಷೂವನ ಮನೆಯವರು 973",
			"40": "<sup>40</sup> ಇಮ್ಮೇರನವರು 1052",
			"41": "<sup>41</sup> ಪಷ್ಹೂರನವರು 1247",
			"42": "<sup>42</sup> ಹಾರಿಮನವರು 1017",
			"43": "<sup>43</sup> ಲೇವಿಯರಲ್ಲಿ - ಹೋದವ್ಯನವರಾದ ಯೇಷೂವ, ಕದ್ಮೀಯೇಲ್ ಇವರ ಸಂತಾನದವರು 74",
			"44": "<sup>44</sup> ಗಾಯಕರಲ್ಲಿ - ಆಸಾಫ್ಯರು 148",
			"45": "<sup>45</sup> ದ್ವಾರಪಾಲಕರಲ್ಲಿ - ಶಲ್ಲೂಮ್, ಆಟೇರ್, ಟಲ್ಮೋನ್, ಅಕ್ಕೂಬ್, ಹಟೀಟಾ, ಶೋಬೈ ಇವರ ಸಂತಾನದವರು ಒಟ್ಟು 138",
			"46": "<sup>46</sup> ದೇವಸ್ಥಾನದಾಸರಲ್ಲಿ - ಜೀಹ, ಹಸೂಫ ಟಬ್ಬಾವೋತ್,",
			"47": "<sup>47</sup> ಕೇರೋಸ್, ಸೀಯ, ಪಾದೋನ್, ಲೆಬಾನ,",
			"48": "<sup>48</sup> ಹಗಾಬ, ಸಲ್ಮೈ, ಹಾನಾನ್, ಗಿದ್ದೇಲ್,",
			"50": "<sup>50</sup> ಗಹರ್, ರೆವಾಯ, ರೆಚೀನ್, ನೆಕೋದ,",
			"52": "<sup>52</sup> ಗಜ್ಜಾಮ್, ಉಜ್ಜ, ಪಾಸೇಹ, ಬೇಸೈ, ಮೆಯನೀಮ್,",
			"53": "<sup>53</sup> ನೆಫೀಷೆಸೀಮ್, ಬಕ್ಬೂಕ್, ಹಕ್ಕೂಫ, ಹರ್ಹೂರ್,",
			"54": "<sup>54</sup> ಬಚ್ಲೂತ್, ಮೆಹೀದ, ಹರ್ಷ, ಬರ್ಕೋಸ್, ಸೀಸೆರ,",
			"56": "<sup>56</sup> ತೆಮಹ, ನೆಚೀಹ, ಹಟೀಫ ಇವರ ಸಂತಾನದವರೂ.",
			"57": "<sup>57</sup> ಸೊಲೊಮೋನನ ದಾಸರಲ್ಲಿ - ಸೋಟೈ,",
			"58": "<sup>58</sup> ಸೋಫೆರೆತ್, ಪೆರೀದ, ಯಾಲ, ದರ್ಕೋನ್,",
			"59": "<sup>59</sup> ಗಿದ್ದೇಲ್, ಶೆಫಟ್ಯ, ಹಟ್ಟೀಲ್, ಪೋಕೆರೆತ್, ಹಚ್ಚೆಬಾಯೀಮ್, ಆಮೋನ್ ಇವರ ಸಂತಾನದವರೂ.",
			"60": "<sup>60</sup> ಎಲ್ಲಾ ದೇವಸ್ಥಾನದಾಸರೂ ಸೊಲೊಮೋನನ ದಾಸರೂ ಒಟ್ಟು 392 ಮಂದಿ.",
			"61": "<sup>61</sup> ತೇಲ್ಮೆಲಹ, ತೇಲ್ಹರ್ಷ, ಕೆರೂಬದ್ದೋನ್, ಇಮ್ಮೇರ್ ಎಂಬ ಊರುಗಳಿಂದ ಬಂದವರಾಗಿ ತಮ್ಮ ಗೋತ್ರವಂಶಾವಳಿಗಳನ್ನು ತೋರಿಸಿ ತಾವು",
			"62": "<sup>62</sup> ಇಸ್ರಾಯೇಲ್ಯರೆಂಬದನ್ನು ಸ್ಥಾಪಿಸಲಾರದವರಾದ ದೆಲಾಯ, ಟೋಬೀಯ, ನೆಕೋದ ಇವರ ಸಂತಾನದವರು 642 ಮಂದಿ.",
			"63": "<sup>63</sup> ಯಾಜಕರಲ್ಲಿ ಹೋಬಾಯ, ಹಕ್ಕೋಚ್, ಬರ್ಜಿಲ್ಲೈ ಇವರ ಸಂತಾನದವರು ತಮ್ಮ ವಂಶಾವಳಿಯನ್ನು ತೋರಿಸಲಾರದೆ ಹೋದರು. (ಈ ಬರ್ಜಿಲ್ಲೈ ಎಂಬವನು ಗಿಲ್ಯಾದ್ಯನಾದ ಬರ್ಜಿಲ್ಲೈಯ ಹೆಣ್ಣು ಮಕ್ಕಳಲ್ಲೊಬ್ಬಾಕೆಯನ್ನು ಮದುವೆಮಾಡಿಕೊಂಡು ಅವನ ಹೆಸರನ್ನೂ ಇಟ್ಟುಕೊಂಡಿದ್ದನು.)",
			"64": "<sup>64</sup> ಇವರು ತಮ್ಮ ವಂಶಾವಳಿ ಪತ್ರಗಳನ್ನು ಹುಡುಕಿದರೂ ಅವು ಸಿಕ್ಕದ ಕಾರಣ ಅಶುದ್ಧರೆಂದು ಯಾಜಕೋದ್ಯೋಗದಿಂದ ತಳ್ಳಲ್ಪಟ್ಟರು.",
			"65": "<sup>65</sup> ಊರೀಮ್ ತುಮ್ಮೀಮುಗಳ ಮೂಲಕ ದೈವನಿರ್ಣಯವನ್ನು ತಿಳಿಸಬಲ್ಲವನಾದ ಯಾಜಕನು ಬರುವ ತನಕ ಇವರು ಮಹಾಪರಿಶುದ್ಧ ಪದಾರ್ಥಗಳಲ್ಲಿ ಭೋಜನಮಾಡಬಾರದೆಂಬದಾಗಿ ದೇಶಾಧಿಪತಿಯು ತೀರ್ಪು ಮಾಡಿದನು.",
			"66": "<sup>66</sup> ಸರ್ವಸಮೂಹದವರ ಒಟ್ಟು ಸಂಖ್ಯೆಯು ನಾಲ್ವತ್ತೆರಡು ಸಾವಿರದ ಮುನ್ನೂರರುವತ್ತು.",
			"67": "<sup>67</sup> ಈ ಸಂಖ್ಯೆಯಲ್ಲಿ ಲೆಕ್ಕಿಸಲ್ಪಡದ ಅವರ ದಾಸದಾಸಿಯರು ಏಳು ಸಾವಿರದ ಮುನ್ನೂರ ಮೂವತ್ತೇಳು ಮಂದಿ. ಅವರ ಗಾಯಕರೂ ಗಾಯಕಿಗಳೂ ಇನ್ನೂರ ನಾಲ್ವತ್ತೈದು ಮಂದಿ.",
			"68": "<sup>68</sup> ಅವರಿಗೆ ಏಳುನೂರ ಮೂವತ್ತಾರು ಕುದುರೆಗಳೂ ಇನ್ನೂರ ನಾಲ್ವತ್ತೈದು ಹೇಸರಕತ್ತೆಗಳೂ",
			"69": "<sup>69</sup> ನಾನೂರ ಮೂವತ್ತೈದು ಒಂಟೆಗಳೂ ಆರು ಸಾವಿರದ ಏಳುನೂರಿಪ್ಪತ್ತು ಕತ್ತೆಗಳೂ ಇದ್ದವು.",
			"70": "<sup>70</sup> ಗೋತ್ರಪ್ರಧಾನರಲ್ಲಿ ಕೆಲವರು ಕೆಲಸಕ್ಕೋಸ್ಕರ ದ್ರವ್ಯಸಹಾಯಮಾಡಿದರು. ತಿರ್ಷಾತಾ ಅನ್ನಿಸಿಕೊಳ್ಳುವ ದೇಶಾಧಿಪತಿಯು ಭಂಡಾರಕ್ಕೆ ಕೊಟ್ಟದ್ದು - ಸಾವಿರ ಬಂಗಾರದ ಪವನುಗಳು, ಐವತ್ತು ಬೋಗುಣಿಗಳು, ಐನೂರಮೂವತ್ತು ಯಾಜಕವಸ್ತ್ರಗಳು.",
			"71": "<sup>71</sup> ಬೇರೆ ಕೆಲವು ಮಂದಿ ಗೋತ್ರ ಪ್ರಧಾನರು ಕಟ್ಟಡದ ಭಂಡಾರಕ್ಕೆ ಕೊಟ್ಟದ್ದು - ಇಪ್ಪತ್ತು ಸಾವಿರ ಬಂಗಾರದ ಪವನುಗಳು, ಒಂದು ಲಕ್ಷ ಹತ್ತು ಸಾವಿರ ತೊಲೆ ಬೆಳ್ಳಿ;",
			"72": "<sup>72</sup> ಉಳಿದ ಜನರು ಕೊಟ್ಟದ್ದು - ಇಪ್ಪತ್ತು ಸಾವಿರ ಬಂಗಾರದ ಪವನುಗಳು, ಒಂದು ಲಕ್ಷ ತೊಲೆ ಬೆಳ್ಳಿ, ಅರುವತ್ತೇಳು ಯಾಜಕವಸ್ತ್ರಗಳು.",
			"73": "<sup>73</sup> ಯಾಜಕರು, ಲೇವಿಯರು, ದ್ವಾರಪಾಲಕರು, ಗಾಯಕರು, ಸಾಧಾರಣಜನರಿಗೆ ಸೇರಿದವರು, ದೇವಸ್ಥಾನದಾಸರು ಅಂತೂ ಇಸ್ರಾಯೇಲ್ಯರೆಲ್ಲರೂ ತಮ್ಮ ತಮ್ಮ ಪಟ್ಟಣಗಳಲ್ಲಿ ವಾಸಿಸುತ್ತಿದ್ದರು."
		},
		"8": {
			"1": "<sup>1</sup> ತಮ್ಮ ತಮ್ಮ ಪಟ್ಟಣಗಳಲ್ಲಿ ವಾಸಿಸುತ್ತಿದ್ದ ಇಸ್ರಾಯೇಲ್ ಸರ್ವಸಮೂಹದವರು ಏಳನೆಯ ತಿಂಗಳಲ್ಲಿ ನೀರುಬಾಗಲಿನ ಮುಂದಿನ ಬೈಲಿನಲ್ಲಿ ಒಟ್ಟಾಗಿ ಕೂಡಿಬಂದು - ಯೆಹೋವನು ಮೋಶೆಯ ಮುಖಾಂತರ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಕೊಟ್ಟ ಧರ್ಮಶಾಸ್ತ್ರದ ಗ್ರಂಥವನ್ನು ತೆಗೆದುಕೊಂಡು ಬರಬೇಕು ಎಂದು ಧರ್ಮೋಪದೇಶಕನಾದ ಎಜ್ರನನ್ನು ಕೇಳಿಕೊಂಡರು.",
			"2": "<sup>2</sup> ಯಾಜಕನಾದ ಎಜ್ರನು ಗ್ರಹಿಸಶಕ್ತರಾದ ಸ್ತ್ರೀಪುರುಷರು ನೆರೆದಿದ್ದ ಸಮೂಹದವರ ಮುಂದೆ ಏಳನೆಯ ತಿಂಗಳಿನ ಪ್ರಥಮ ದಿನದಲ್ಲಿ",
			"3": "<sup>3</sup> ಆ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ತಂದು ಪ್ರಾತಃಕಾಲದಿಂದ ಮಧ್ಯಾಹ್ನದವರೆಗೂ ಓದಿದನು. ನೀರುಬಾಗಲಿನ ಮುಂದಣ ಬೈಲಿನಲ್ಲಿ ಕೂಡಿಬಂದಿದ್ದ ಸ್ತ್ರೀಪುರುಷರಲ್ಲಿ ಗ್ರಹಿಸಶಕ್ತರಾದವರೆಲ್ಲರೂ ಧರ್ಮಶಾಸ್ತ್ರಪಾರಾಯಣವನ್ನು ಲಾಲಿಸುತ್ತಾ ಇದ್ದರು.",
			"4": "<sup>4</sup> ಧರ್ಮೋಪದೇಶಕನಾದ ಎಜ್ರನು ಪಾರಾಯಣಕ್ಕಾಗಿ ಮಾಡಲ್ಪಟ್ಟ ಮರದ ಪೀಠದ ಮೇಲೆ ನಿಂತನು. ಅವನ ಬಲಗಡೆಯಲ್ಲಿ ಮತ್ತಿತ್ಯ, ಶೆಮ, ಅನಾಯ, ಊರೀಯ, ಹಿಲ್ಕೀಯ, ಮಾಸೇಯ ಎಂಬವರೂ ಎಡಗಡೆಯಲ್ಲಿ ಪೆದಾಯ, ಮೀಷಾಯೇಲ್, ಮಲ್ಕೀಯ, ಹಾಷುಮ್, ಹಷ್ಬದ್ದಾನ, ಜೆಕರ್ಯ, ಮೆಷುಲ್ಲಾಮ್ ಎಂಬವರೂ ನಿಂತರು.",
			"5": "<sup>5</sup> ಸರ್ವಸಮೂಹಕ್ಕಿಂತಲೂ ಎತ್ತರವಾದ ಸ್ಥಳದಲ್ಲಿ ನಿಂತಿದ್ದ ಎಜ್ರನು ಜನರ ಸಮಕ್ಷದಲ್ಲಿ ಗ್ರಂಥವನ್ನು ತೆರೆದ ಕೂಡಲೆ ಎಲ್ಲರೂ ಎದ್ದು ನಿಂತರು.",
			"6": "<sup>6</sup> ಎಜ್ರನು ಪರಾತ್ಪರ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಸ್ತುತಿಸಿದ್ದಕ್ಕೆ ಸರ್ವಜನರೂ ತಮ್ಮ ಕೈಗಳನ್ನೆತ್ತಿ ಆಮೆನ್, ಆಮೆನ್ ಎಂದು ಹೇಳಿ ನೆಲದ ಮಟ್ಟಿಗೂ ತಲೆಬಾಗಿ ಯೆಹೋವನನ್ನು ಆರಾಧಿಸಿದರು.",
			"7": "<sup>7</sup> ಆಮೇಲೆ ಯೇಷೂವ, ಬಾನೀ, ಶೇರೇಬ್ಯ, ಯಾಮೀನ್, ಅಕ್ಕೂಬ್, ಶಬ್ಬೆತೈ, ಹೋದೀಯ, ಮಾಸೇಯ, ಕೆಲೀಟ, ಅಜರ್ಯ, ಯೋಜಾಬಾದ್, ಹಾನಾನ್, ಪೆಲಾಯ ಎಂಬ ಲೇವಿಯರು ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಜನರು ಗ್ರಹಿಸುವಂತೆ ಮಾಡಿದರು.",
			"8": "<sup>8</sup> ಜನರು ಎದ್ದು ನಿಂತನಂತರ ಅವರು ದೇವರ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಸ್ಪಷ್ಟವಾಗಿ ಓದುತ್ತಾ ಅದರ ತಾತ್ಪರ್ಯವನ್ನು ವಿವರಿಸಿಲು ಜನರು ಗ್ರಹಿಸಿದರು.",
			"9": "<sup>9</sup> ಜನರೆಲ್ಲರೂ ಧರ್ಮೋಪದೇಶವಾಕ್ಯಗಳನ್ನು ಕೇಳುತ್ತಾ ಅಳುತ್ತಿದ್ದದರಿಂದ ದೇಶಾಧಿಪತಿಯಾದ ನೆಹೆಮೀಯನೂ ಧರ್ಮೋಪದೇಶಮಾಡುವ ಯಾಜಕನಾದ ಎಜ್ರನೂ ಜನರನ್ನು ಬೋಧಿಸುತ್ತಿದ್ದ ಲೇವಿಯರೂ ಅವರಿಗೆ - ಈ ದಿನವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಪರಿಶುದ್ಧದಿನವಾಗಿರುವದರಿಂದ ನೀವು ದುಃಖಿಸುತ್ತಾ ಅಳುತ್ತಾ ಇರಬೇಡಿರಿ ಎಂದು ಹೇಳಿದರು.",
			"10": "<sup>10</sup> ಇದಲ್ಲದೆ ಎಜ್ರನು ಅವರಿಗೆ - ಹೋಗಿ ಮೃಷ್ಟಾನ್ನವನ್ನೂ ಮಧುರಪಾನವನ್ನೂ ತೆಗೆದುಕೊಳ್ಳಿರಿ; ತಮಗೋಸ್ಕರ ಏನೂ ಸಿದ್ಧಮಾಡದವರಿಗೆ ಭಾಗಗಳನ್ನು ಕಳುಹಿಸಿರಿ. ಈ ದಿನವು ನಮ್ಮ ಕರ್ತನಿಗೆ ಪ್ರತಿಷ್ಠಿತ ದಿನವಾಗಿರುವದರಿಂದ ವ್ಯಸನಪಡಬೇಡಿರಿ; ಯೆಹೋವನ ಆನಂದವೇ ನಿಮ್ಮ ಆಶ್ರಯವಾಗಿದೆ ಅಂದನು.",
			"11": "<sup>11</sup> ಲೇವಿಯರೂ - ಸುಮ್ಮನಿರಿ, ಇದು ದೇವರ ದಿವಸ, ದುಃಖಿಸಬೇಡಿರಿ ಎಂದು ಹೇಳಿ ಜನರನ್ನು ಸಂತೈಸಿದರು.",
			"12": "<sup>12</sup> ತಮಗೆ ತಿಳಿಯಪಡಿಸಿದ ಮಾತುಗಳನ್ನು ಜನರೆಲ್ಲರೂ ಗಮನಿಸಿ ಹೋಗಿ ಅನ್ನಪಾನಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಏನೂ ಇಲ್ಲದವರಿಗೆ ಭಾಗಗಳನ್ನು ಕಳುಹಿಸಿ ಬಹಳವಾಗಿ ಸಂತೋಷಪಟ್ಟರು.",
			"13": "<sup>13</sup> ಮರುದಿನ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ ಗೋತ್ರಪ್ರಧಾನರೂ ಯಾಜಕರೂ ಲೇವಿಯರೂ ಧರ್ಮಶಾಸ್ತ್ರದ ಅಧ್ಯಯನಕ್ಕಾಗಿ ಧರ್ಮೋಪದೇಶಕನಾದ ಎಜ್ರನ ಬಳಿಗೆ ಕೂಡಿಬಂದಾಗ",
			"14": "<sup>14</sup> ಏಳನೆಯ ತಿಂಗಳಿನ ಜಾತ್ರೆಯಲ್ಲಿ ಇಸ್ರಾಯೇಲ್ಯರು ಪರ್ಣಶಾಲೆಗಳಲ್ಲಿ ವಾಸಿಸಬೇಕು;",
			"15": "<sup>15</sup> ಈ ನಿಯಮದ ಪ್ರಕಾರ ಆ ಪರ್ಣಶಾಲೆಗಳಿಗಾಗಿ ಜನರು ಗುಡ್ಡಕ್ಕೆ ಹೋಗಿ ಒಲೀವ, ಕಾಡು ಒಲೀವ, ಸುಗಂಧ, ಖರ್ಜೂರ ಮುಂತಾದ ಮರಗಳ ದಟ್ಟವಾದ ಎಲೆಗಳುಳ್ಳ ಕೊಂಬೆಗಳನ್ನು ತರುವ ಹಾಗೆ ಅವರ ಎಲ್ಲಾ ಪಟ್ಟಣಗಳಲ್ಲಿಯೂ ಯೆರೂಸಲೇಮಿನಲ್ಲಿಯೂ ಡಂಗುರದಿಂದ ಪ್ರಕಟಿಸಬೇಕು ಎಂಬದಾಗಿ ಬರೆದಿರುವ ಶಾಸನವು ಮೋಶೆಗೆ ಯೆಹೋವನಿಂದ ದೊರಕಿದ ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿ ಸಿಕ್ಕಿತು.",
			"16": "<sup>16</sup> ಈ ಆಜ್ಞಾನುಸಾರವಾಗಿ ಜನರು ಹೋಗಿ ಕೊಂಬೆಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಬಂದು ತಮ್ಮ ತಮ್ಮ ಅಂಗಳಮಾಳಿಗೆಗಳಲ್ಲಿಯೂ ದೇವಾಲಯದ ಪ್ರಾಕಾರಗಳಲ್ಲಿಯೂ ನೀರು ಬಾಗಲು, ಎಫ್ರಾಯೀಮ್ ಬಾಗಲು ಇವುಗಳ ಮುಂದಣ ಬೈಲುಗಳಲ್ಲಿಯೂ ಪರ್ಣಶಾಲೆಗಳನ್ನು ಮಾಡಿಕೊಂಡರು.",
			"17": "<sup>17</sup> ನೂನನ ಮಗನಾದ ಯೆಹೋಶುವನ ಕಾಲದಿಂದ ಆವರೆಗೂ ಇಸ್ರಾಯೇಲ್ಯರು ಹೀಗೆ ಮಾಡಿದ್ದಿಲ್ಲ. ಸೆರೆಯಿಂದ ತಿರಿಗಿ ಬಂದ ಸರ್ವಸಮೂಹದವರಾದರೋ ಪರ್ಣಶಾಲೆಗಳನ್ನು ಮಾಡಿಕೊಂಡು ಅವುಗಳಲ್ಲಿ ವಾಸಿಸುತ್ತಾ ಬಹು ಸಂತೋಷಪಟ್ಟರು.",
			"18": "<sup>18</sup> ಎಜ್ರನು ಮೊದಲನೆಯ ದಿನದಿಂದ ಕಡೆಯ ದಿನದವರೆಗೆ ಪ್ರತಿದಿನವೂ ದೇವರ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು [ಜನರಿಗೋಸ್ಕರ] ಪಾರಾಯಣ ಮಾಡುತ್ತಿದ್ದನು. ಏಳು ದಿನಗಳವರೆಗೂ ಜಾತ್ರೆ ನಡೆಯಿತು. ನೇಮದ ಪ್ರಕಾರ ಎಂಟನೆಯ ದಿನದಲ್ಲಿ ದೇವಾರಾಧನೆಗಾಗಿ ಸಭೆಕೂಡಿತು."
		},
		"9": {
			"1": "<sup>1</sup> ಅದೇ ತಿಂಗಳಿನ ಇಪ್ಪತ್ತನಾಲ್ಕನೆಯ ದಿನದಲ್ಲಿ ಇಸ್ರಾಯೇಲ್ಯರು ಉಪವಾಸವಾಗಿದ್ದು ಗೋಣೀತಟ್ಟನ್ನು ಕಟ್ಟಿಕೊಂಡು ತಲೆಯ ಮೇಲೆ ಮಣ್ಣುಹಾಕಿಕೊಂಡು ನೆರೆದು ಬಂದರು.",
			"2": "<sup>2</sup> ಇಸ್ರಾಯೇಲ್‍ಸಂತಾನದವರು ಎಲ್ಲಾ ಅನ್ಯಕುಲದವರಿಂದ ತಮ್ಮನ್ನು ಬೇರ್ಪಡಿಸಿಕೊಂಡು ನಿಂತು ತಮ್ಮ ಪಾಪಗಳನ್ನೂ ತಮ್ಮ ಪಿತೃಗಳ ಪಾಪಗಳನ್ನೂ ಆತನಿಗೆ ಅರಿಕೆಮಾಡಿದರು.",
			"3": "<sup>3</sup> ಅವರು ಮೂರು ತಾಸುಗಳವರೆಗೂ ತಮ್ಮ ತಮ್ಮ ಸ್ಥಳಗಳಲ್ಲಿ ನಿಂತು ತಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಧರ್ಮಶಾಸ್ತ್ರ ಗ್ರಂಥವನ್ನು ಪಾರಾಯಣ ಮಾಡಿಸಿದರು. ತರುವಾಯ ಇನ್ನೂ ಮೂರು ತಾಸು ತಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಅಡ್ಡ ಬಿದ್ದು ಪಾಪಗಳನ್ನು ಅರಿಕೆಮಾಡಿದರು.",
			"4": "<sup>4</sup> ಯೇಷೂವ, ಬಾನೀ, ಕದ್ಮೀಯೇಲ್, ಶೆಬನ್ಯ, ಬುನ್ನೀ, ಶೇರೇಬ್ಯ, ಬಾನೀ, ಕೆನಾನೀ ಎಂಬವರು ಲೇವಿಯರ ಮೆಟ್ಲುಗಳ ಮೇಲೆ ನಿಂತು ಮಹಾಶಬ್ದದಿಂದ ತಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಟ್ಟರು.",
			"5": "<sup>5</sup> ಆಮೇಲೆ ಲೇವಿಯರಾದ ಯೇಷೂವ, ಕದ್ಮೀಯೇಲ್, ಬಾನೀ, ಹಷಬ್ನೆಯ, ಶೇರೇಬ್ಯ, ಹೋದೀಯ, ಶೆಬನ್ಯ, ಪೆತಹ್ಯ ಎಂಬವರು - ಏಳಿರಿ, ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಯುಗಯುಗಕ್ಕೂ ಸ್ತೋತ್ರವಾಗಲಿ ಅನ್ನಿರಿ ಎಂದು ಹೇಳಿ - ಯೆಹೋವನೇ, ಸರ್ವಸ್ತುತಿಕೀರ್ತನೆಗಳಿಗೆ ಮಿಗಿಲಾಗಿರುವ ನಿನ್ನ ಮಹಾನಾಮಕ್ಕೆ ಕೊಂಡಾಟವಾಗಲಿ,",
			"6": "<sup>6</sup> ನೀನೊಬ್ಬನೇ ದೇವರು; ನೀನು ಉನ್ನತೋನ್ನತವಾದ ಆಕಾಶವನ್ನೂ ಅದರ ಸೈನ್ಯವನ್ನೂ ಭೂಮಿಯನ್ನೂ ಅದರ ಮೇಲಿರುವದನ್ನೂ ಸಮುದ್ರಗಳನ್ನೂ ಎಲ್ಲಾ ಜಲಚರಗಳನ್ನೂ ಉಂಟು ಮಾಡಿ ಸಮಸ್ತ ಪ್ರಾಣಿಗಳ ಜೀವಾಧಾರನಾಗಿರುತ್ತೀ.",
			"7": "<sup>7</sup> ಆಕಾಶ ಸೈನ್ಯದವರು ನಿನಗೆ ಅಡ್ಡಬೀಳುತ್ತಾರೆ. ಯೆಹೋವನೇ, ಅಬ್ರಾಮನನ್ನು ಆರಿಸಿಕೊಂಡು ಕಸ್ದೀಯರ ಊರ್ ಎಂಬ ಪಟ್ಟಣದಿಂದ ಬರಮಾಡಿ ಅವನಿಗೆ ಅಬ್ರಹಾಮನೆಂಬ ಹೆಸರನ್ನು ಕೊಟ್ಟ ದೇವರು ನೀನೇ.",
			"8": "<sup>8</sup> ಅವನು ಯಥಾರ್ಥಚಿತ್ತನೆಂದು ಕಂಡು ಅವನಿಗೆ - ಕಾನಾನ್ಯರು, ಹಿತ್ತಿಯರು, ಅಮೋರಿಯರು, ಪೆರಿಜ್ಜೀಯರು, ಯೆಬೂಸಿಯರು, ಗಿರ್ಗಾಷಿಯರು ಇವರ ದೇಶವನ್ನು ನಿನ್ನ ಸಂತಾನದವರಿಗೆ ಕೊಡುತ್ತೇನೆಂದು ವಾಗ್ದಾನಮಾಡಿ ಅದನ್ನು ನೆರವೇರಿಸಿದ್ದೀ; ನೀನು ಧರ್ಮಸ್ವರೂಪನೇ.",
			"9": "<sup>9</sup> ಐಗುಪ್ತದೇಶದಲ್ಲಿ ನಮ್ಮ ಪಿತೃಗಳಿಗಿದ್ದ ಕಷ್ಟವನ್ನು ನೋಡಿದಿ; ಕೆಂಪುಸಮುದ್ರದ ಬಳಿಯಲ್ಲಿ ಅವರು ಮೊರೆಯಿಟ್ಟಾಗ ನೀನು ಲಾಲಿಸಿದಿ.",
			"10": "<sup>10</sup> ಐಗುಪ್ತ್ಯರು ಗರ್ವಿಗಳಾಗಿ ಅವರನ್ನು ಕುಗ್ಗಿಸಿದಾಗ ನೀನು ಫರೋಹನಲ್ಲಿಯೂ ಅವನ ಸೇವಕರಲ್ಲಿಯೂ ಅವನ ದೇಶದ ಜನರಲ್ಲಿಯೂ ಅದ್ಭುತಕಾರ್ಯಗಳನ್ನೂ ಸೂಚಕಕಾರ್ಯಗಳನ್ನೂ ನಡಿಸಿ ಈಗ ನಿನಗಿರುವ ಕೀರ್ತಿಯನ್ನು ಸ್ಥಾಪಿಸಿದಿ.",
			"11": "<sup>11</sup> ನಮ್ಮ ಪಿತೃಗಳು ಸಮುದ್ರ ಮಧ್ಯದಲ್ಲಿ ಒಣನೆಲದಲ್ಲೇ ಹಾದುಹೋಗುವಂತೆ ಅವರ ಮುಂದೆ ಸಮುದ್ರವನ್ನು ಭೇದಿಸಿದಿ. ಅವರನ್ನು ಹಿಂದಟ್ಟುವವರನ್ನು ಕಲ್ಲಿನಂತೆ ಮಹಾಜಲರಾಶಿಯ ತಳದಲ್ಲಿ ಮುಳುಗಿಸಿ ಬಿಟ್ಟಿ.",
			"12": "<sup>12</sup> ಹಗಲುಹೊತ್ತಿನಲ್ಲಿ ಮೇಘಸ್ತಂಭದಲ್ಲಿಯೂ ರಾತ್ರಿವೇಳೆಯಲ್ಲಿ ಅವರು ನಡೆಯ ತಕ್ಕ ದಾರಿಯಲ್ಲಿ ಬೆಳಕುಕೊಡುವದಕ್ಕೆ ಅಗ್ನಿ ಸ್ತಂಭದಲ್ಲಿಯೂ ಅವರ ಮುಂದೆ ಹೋದಿ.",
			"13": "<sup>13</sup> ಸೀನಾಯಿ ಬೆಟ್ಟದ ಮೇಲೆ ಇಳಿದು ಬಂದು ಆಕಾಶದ ಕಡೆಯಿಂದ ಅವರೊಡನೆ ಮಾತಾಡಿ ಅವರಿಗೆ ನೀತಿನಿಯಮಗಳನ್ನೂ ಯಥಾರ್ಥ ಧರ್ಮೋಪದೇಶವನ್ನೂ ಶ್ರೇಷ್ಠವಾದ ಆಜ್ಞಾವಿಧಿಗಳನ್ನೂ ಅನುಗ್ರಹಿಸಿದಿ.",
			"14": "<sup>14</sup> ನಿನ್ನ ಸೇವಕನಾದ ಮೋಶೆಯ ಮುಖಾಂತರ ನಿನಗೆ ಮೀಸಲಾದ ಸಬ್ಬತೆಂಬ ವಿಶ್ರಾಂತಿ ದಿನವನ್ನು ಅವರಿಗೆ ಪ್ರಕಟಿಸಿ ಆಜ್ಞಾವಿಧಿಧರ್ಮಗಳನ್ನು ಕಲಿಸಿದಿ.",
			"15": "<sup>15</sup> ಅವರು ಹಸಿದಾಗ ಆಕಾಶದಿಂದ ಆಹಾರವನ್ನು ಕೊಟ್ಟು ಬಾಯಾರಿದಾಗ ಬಂಡೆಯೊಳಗಿಂದ ನೀರನ್ನು ಬರಮಾಡಿ ಪ್ರಮಾಣಪೂರ್ವಕವಾಗಿ ವಾಗ್ದಾನಮಾಡಿದ ದೇಶದಲ್ಲಿ ನೀವು ಸೇರಿ ಅದನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳಿರಿ ಎಂದು ಅವರಿಗೆ ಆಜ್ಞಾಪಿಸಿದಿ.",
			"16": "<sup>16</sup> ಆದರೂ ನಮ್ಮ ಪಿತೃಗಳು ಗರ್ವಿಗಳಾಗಿ ಹಟಹಿಡಿದು ನಿನ್ನ ಆಜ್ಞೆಗಳಿಗೆ ಅವಿಧೇಯರಾದರು.",
			"17": "<sup>17</sup> ನೀನು ಅವರ ಮಧ್ಯದಲ್ಲಿ ಮಾಡಿದ ಮಹತ್ಕಾರ್ಯಗಳನ್ನು ಅವರು ಮರೆತು ಮಾತುಕೇಳದೆ ಹಟಹಿಡಿದು ತಮಗೊಬ್ಬ ನಾಯಕನನ್ನು ನೇಮಿಸಿಕೊಂಡು ಮುಂಚಿನಂತೆ ದಾಸರಾಗಿರುವದಕ್ಕಾಗಿ ಐಗುಪ್ತಕ್ಕೆ ಹಿಂದಿರುಗಬೇಕೆಂದಿದ್ದರು. ನೀನಾದರೋ ಪಾಪಗಳನ್ನು ಕ್ಷಮಿಸುವವನೂ ಕನಿಕರದಯೆಗಳುಳ್ಳವನೂ ದೀರ್ಘಶಾಂತನೂ ಕೃಪಾಳುವೂ ಆಗಿರುವ ದೇವರು; ನೀನು ಅವರನ್ನು ಕೈಬಿಡಲಿಲ್ಲ.",
			"18": "<sup>18</sup> ಅವರು ತಮಗೆ ಎರಕದ ಬಸವನನ್ನು ಮಾಡಿಕೊಂಡು - [ಇಸ್ರಾಯೇಲ್ಯರೇ,] ನಿಮ್ಮನ್ನು ಐಗುಪ್ತದಿಂದ ಕರಕೊಂಡು ಬಂದ ದೇವರು ಇದೇ ಎಂದು ಹೇಳಿ ನಿನ್ನನ್ನು ಅಸಡ್ಡೆ ಮಾಡಿದಾಗಲೂ",
			"19": "<sup>19</sup> ಕರುಣಾನಿಧಿಯಾದ ನೀನು ಅವರನ್ನು ಅರಣ್ಯದಲ್ಲಿ ಕೈಬಿಡಲಿಲ್ಲ, ಹಗಲಿನಲ್ಲಿ ಅವರಿಗೆ ದಾರಿ ತೋರಿಸುತ್ತಿದ್ದ ಮೇಘ ಸ್ತಂಭವೂ ರಾತ್ರಿವೇಳೆಯಲ್ಲಿ ಅವರು ನಡೆಯತಕ್ಕ ದಾರಿಯಲ್ಲಿ ಬೆಳಕು ಕೊಡುತ್ತಿದ್ದ ಅಗ್ನಿಸ್ತಂಭವೂ ಅವರನ್ನು ಬಿಟ್ಟುಹೋಗಲಿಲ್ಲ.",
			"20": "<sup>20</sup> ಅವರಿಗೆ ಉಪದೇಶಿಸಲು ನಿನ್ನ ಒಳ್ಳೇ ಆತ್ಮನನ್ನು ಕೊಟ್ಟಿ. ಅವರ ಬಾಯಿಂದ ಮನ್ನವನ್ನು ಹಿಂದೆಗೆಯಲಿಲ್ಲ.",
			"21": "<sup>21</sup> ಬಾಯಾರಿದಾಗ ಅವರಿಗೆ ನೀರನ್ನು ಕೊಟ್ಟಿ. ನಾಲ್ವತ್ತು ವರುಷ ಅವರನ್ನು ಅರಣ್ಯದಲ್ಲಿ ಸಾಕುತ್ತಾ ಇದ್ದಿ; ಅವರಿಗೆ ಯಾವ ಕೊರತೆಯೂ ಇರಲಿಲ್ಲ. ಅವರ ಬಟ್ಟೆಗಳು ಜೀರ್ಣವಾಗಲಿಲ್ಲ, ಕಾಲುಗಳು ಬಾತುಹೋಗಲಿಲ್ಲ.",
			"22": "<sup>22</sup> ರಾಜ್ಯಜನಾಂಗಗಳನ್ನು ಅವರಿಗೆ ವಶಪಡಿಸಿ ಆ ರಾಜ್ಯಗಳನ್ನು ಅವರಿಗೆ ಸ್ವಾಸ್ತ್ಯವನ್ನಾಗಿ ಹಂಚಿಕೊಟ್ಟಿ. ಹೀಗೆ ಅವರು ಹೆಷ್ಬೋನಿನ ಅರಸನಾದ ಸೀಹೋನ್, ಬಾಷಾನಿನ ಅರಸನಾದ ಓಗ್ ಇವರ ಪ್ರಾಂತಗಳನ್ನು ಸ್ವಾಧೀನಪಡಿಸಿಕೊಂಡರು.",
			"23": "<sup>23</sup> ಅವರ ಮಕ್ಕಳನ್ನು ಆಕಾಶದ ನಕ್ಷತ್ರಗಳಷ್ಟು ಹೆಚ್ಚಿಸಿ - ನೀವು ಈ ದೇಶದೊಳಕ್ಕೆ ಪ್ರವೇಶಿಸಿ ಸ್ವಾಧೀನ ಮಾಡಿಕೊಳ್ಳುವಿರಿ ಎಂದು ಅವರ ಪಿತೃಗಳಿಗೆ ವಾಗ್ದಾನಮಾಡಿದ್ದ ದೇಶಕ್ಕೆ ಅವರನ್ನು ಬರಮಾಡಿದಿ.",
			"24": "<sup>24</sup> ಅವರು ಆ ದೇಶದಲ್ಲಿ ಸೇರಿ ಅದನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡರು. ನೀನು ಅವರ ಮುಂದೆ ದೇಶನಿವಾಸಿಗಳಾಗಿದ್ದ ಕಾನಾನ್ಯರನ್ನು ಕುಗ್ಗಿಸಿ ಮನಸ್ಸಿಗೆ ಬಂದಂತೆ ನಡಿಸುವ ಹಾಗೆ ಆ ದೇಶದ ರಾಜರನ್ನೂ ಪ್ರಜೆಗಳನ್ನೂ ಅವರ ಕೈಗೆ ಒಪ್ಪಿಸಿದಿ.",
			"25": "<sup>25</sup> ಅವರು ಕೋಟೆ ಕೊತ್ತಲುಗಳುಳ್ಳ ಪಟ್ಟಣಗಳನ್ನೂ ಸಾರವುಳ್ಳ ಭೂಮಿಯನ್ನೂ ಸಮಸ್ತ ವಿಧವಾದ ಉತ್ತಮವಸ್ತುಗಳಿಂದ ತುಂಬಿದ ಮನೆಗಳನ್ನೂ ತೋಡಿದ ಬಾವಿಗಳನ್ನೂ ದ್ರಾಕ್ಷೇತೋಟ, ಎಣ್ಣೇಮರಗಳ ತೋಪು, ಎಷ್ಟೋ ಹಣ್ಣಿನ ಮರ ಇವುಗಳನ್ನೂ ವಶಮಾಡಿಕೊಂಡು ಚೆನ್ನಾಗಿ ತಿಂದು ತೃಪ್ತರಾಗಿ ಕೊಬ್ಬಿ ನೀನು ಕೊಟ್ಟ ಸಮೃದ್ಧಿಯಲ್ಲಿ ಆನಂದಿಸುತ್ತಿದ್ದರು.",
			"26": "<sup>26</sup> ಆಗ ಅವರು ನಿನಗೆ ಅವಿಧೇಯರಾಗಿ ತಿರುಗಿ ಬಿದ್ದು ನಿನ್ನ ಧರ್ಮೋಪದೇಶವನ್ನು ಉಲ್ಲಂಘಿಸಿ ತಮ್ಮನ್ನು ಎಚ್ಚರಿಸುವದಕ್ಕೂ ನಿನ್ನ ಕಡೆಗೆ ತಿರುಗಿಸುವದಕ್ಕೂ ಪ್ರಯತ್ನಿಸುತ್ತಿದ್ದ ನಿನ್ನ ಪ್ರವಾದಿಗಳನ್ನು ಕೊಂದುಹಾಕಿ ನಿನ್ನನ್ನು ಅಸಡ್ಡೆಮಾಡಿದರು.",
			"27": "<sup>27</sup> ಆದದರಿಂದ ನೀನು ಅವರನ್ನು ಬಾಧಿಸುವ ವಿರೋಧಿಗಳ ಕೈಗೆ ಒಪ್ಪಿಸಿದಿ. ಅವರು ಆ ಕಷ್ಟಕಾಲದಲ್ಲಿ ನಿನಗೆ ಮೊರೆಯಿಡಲು ಪರಲೋಕದಿಂದ ನೀನು ಅವರಿಗೆ ಕಿವಿಗೊಟ್ಟು ನಿನ್ನ ಕರುಣಾತಿಶಯದಿಂದ ರಕ್ಷಕರನ್ನು ಕಳುಹಿಸಿ ವಿರೋಧಿಗಳ ಕೈಯಿಂದ ಅವರನ್ನು ಬಿಡಿಸಿದಿ.",
			"28": "<sup>28</sup> ಉಪಶಮನವನ್ನು ಪಡೆದ ಮೇಲೆ ಅವರು ತಿರಿಗಿ ದ್ರೋಹಿಗಳಾಗಿ ನಡೆಯುತ್ತಿರುವದನ್ನು ನೀನು ಕಂಡು ಅವರ ಮೇಲೆ ದೊರೆತನ ನಡಿಸತಕ್ಕ ವೈರಿಗಳ ಕೈಗೆ ಅವರನ್ನು ಒಪ್ಪಿಸಿದಿ. ಆಗ ಅವರು ಪಶ್ಚಾತ್ತಾಪಪಟ್ಟು ಕೂಗಿಕೊಳ್ಳಲು ಪರಲೋಕದಿಂದ ನೀನು ಕೇಳಿ ನಿನ್ನ ಕರುಣಾತಿಶಯದಿಂದ ಅವರನ್ನು ಅನೇಕಾವರ್ತಿ ರಕ್ಷಿಸಿ -",
			"29": "<sup>29</sup> ನನ್ನ ಧರ್ಮೋಪದೇಶಕ್ಕೆ ಪುನಃ ಮನಗೊಟ್ಟು ಬನ್ನಿರಿ ಎಂದು ಎಷ್ಟೋ ಖಂಡಿತವಾಗಿ ಎಚ್ಚರಿಸಿದಿ. ಆದರೂ ಅವರು ಲಾಲಿಸದೆ ಗರ್ವಿಗಳೂ ನಿನ್ನ ಆಜ್ಞೆಗಳಿಗೆ ಅವಿಧೇಯರೂ ಆದರು; ಕೈಕೊಳ್ಳುವ ಮನುಷ್ಯನಿಗೆ ಜೀವಾಧಾರವಾಗಿರುವ ನಿನ್ನ ವಿಧಿ ನ್ಯಾಯಗಳನ್ನು ಮೀರಿ ಪಾಪಮಾಡಿ ಹೆಗಲು ಕೊಡದೆ ಮೊಂಡಬಿದ್ದು ನಿನ್ನ ಮಾತನ್ನು ಕೇಳದೆ ಹೋದರು.",
			"30": "<sup>30</sup> ನೀನು ಅನೇಕವರ್ಷಗಳ ತನಕ ಅವರ ವಿಷಯದಲ್ಲಿ ತಾಳಿಕೊಂಡು ಪ್ರವಾದಿಗಳ ಮುಖಾಂತರ ಮಾತಾಡುತ್ತಿದ್ದ ನಿನ್ನ ಆತ್ಮನಿಂದ ಅವರನ್ನು ಎಷ್ಟು ಎಚ್ಚರಿಸುತ್ತಿದ್ದರೂ ಅವರು ಕಿವಿಗೊಡದೆ ಹೋದ ಮೇಲೆ ನೀನು ಅವರನ್ನು ಅನ್ಯದೇಶಗಳವರ ಕೈಗೆ ಒಪ್ಪಿಸಿಬಿಟ್ಟಿ.",
			"31": "<sup>31</sup> ಆದರೆ ನೀನು ದಯೆಯೂ ಕನಿಕರವೂ ಉಳ್ಳ ದೇವರಾಗಿರುವದರಿಂದ ನಿನ್ನ ಮಹಾ ಕೃಪಾನುಸಾರವಾಗಿ ಅವರನ್ನು ನಾಶಮಾಡಿಬಿಡಲಿಲ್ಲ, ಕೈಬಿಡಲೂ ಇಲ್ಲ.",
			"32": "<sup>32</sup> ನಮ್ಮ ದೇವರೇ, ಮಹೋನ್ನತನೂ ಪರಾಕ್ರಮಿಯೂ ಭಯಂಕರನೂ ಆಗಿರುವಾತನೇ, ಕೃಪಾವಾಗ್ದಾನಗಳನ್ನು ನೆರವೇರಿಸುವ ದೇವರೇ, ಅಶ್ಶೂರದ ರಾಜರ ಕಾಲದಿಂದ ಇಂದಿನವರೆಗೆ ನಮ್ಮ ಅರಸರಿಗೂ ಪ್ರಭುಗಳಿಗೂ ಯಾಜಕರಿಗೂ ಪ್ರವಾದಿಗಳಿಗೂ ಹಿರಿಯರಿಗೂ ನಿನ್ನ ಎಲ್ಲಾ ಪ್ರಜೆಗಳಿಗೂ ಪ್ರಾಪ್ತವಾದ ಕಷ್ಟವು ಅಲ್ಪವು ಎಂದೆಣಿಸಬೇಡ.",
			"33": "<sup>33</sup> ನೀನು ನಮ್ಮ ಮೇಲೆ ಎಷ್ಟು ಕೇಡನ್ನು ಬರಮಾಡಿದರೂ ನೀನು ಧರ್ಮಸ್ವರೂಪನೇ; ನೀನು ಸತ್ಯವನ್ನೇ ನಡಿಸಿದಿ, ನಾವಾದರೋ ದುಷ್ಟರು.",
			"34": "<sup>34</sup> ನಮ್ಮ ಅರಸರೂ ಪ್ರಭುಗಳೂ ಯಾಜಕರೂ ಹಿರಿಯರೂ ನಿನ್ನ ಧರ್ಮೋಪದೇಶವನ್ನು ಅನುಸರಿಸಲಿಲ್ಲ, ನಿನ್ನ ಆಜ್ಞೆಗಳಿಗೆ ಕಿವಿಗೊಡಲಿಲ್ಲ; ನೀನು ಎಚ್ಚರಿಸಿ ಹೇಳಿದ ಮಾತುಗಳನ್ನು ಲಕ್ಷಿಸಲಿಲ್ಲ.",
			"35": "<sup>35</sup> ಅವರು ಸ್ವರಾಜ್ಯದಲ್ಲಿದ್ದುಕೊಂಡು ನೀನು ದಯಪಾಲಿಸಿದ ಸಮೃದ್ಧಿಯನ್ನು ಅನುಭವಿಸುತ್ತಾ ನಿನ್ನ ಅನುಗ್ರಹದಿಂದ ದೊರಕಿದ ಸಾರವುಳ್ಳ ವಿಸ್ತಾರದೇಶದಲ್ಲಿ ವಾಸಿಸುತ್ತಾ ಇರುವಾಗ ನಿನ್ನನ್ನು ಸೇವಿಸದೆಯೂ ತಮ್ಮ ದುಷ್ಕೃತ್ಯಗಳನ್ನು ಬಿಡದೆಯೂ ಇದ್ದದರಿಂದಲೇ ನಾವು ಈಗ ದಾಸರಾಗಿದ್ದೇವೆ.",
			"36": "<sup>36</sup> ನೀನು ನಮ್ಮ ಪಿತೃಗಳಿಗೆ - ಇದರ ಉತ್ಪನ್ನವನ್ನೂ ಸಮೃದ್ಧಿಯನ್ನೂ ಅನುಭವಿಸಿರಿ ಎಂದು ಹೇಳಿ ಕೊಟ್ಟ ದೇಶದಲ್ಲೇ ನಾವು ದಾಸರಾಗಿರಬೇಕಾಗಿದೆ. ನಮ್ಮ ಪಾಪಗಳ ನಿಮಿತ್ತವಾಗಿ ನಿನ್ನಿಂದ ನಮ್ಮ ಮೇಲೆ ನೇಮಿಸಲ್ಪಟ್ಟ ರಾಜರಿಗೆ ಈ ದೇಶದ ಹೇರಳವಾದ ಹುಟ್ಟುವಳಿಯು ಹೋಗುತ್ತದೆ.",
			"37": "<sup>37</sup> ನಮ್ಮ ಕಾಯಗಳ ಮೇಲೆಯೂ ಪಶುಗಳ ಮೇಲೆಯೂ ಅವರು ಅಧಿಕಾರ ನಡಿಸುತ್ತಾರೆ. ನಾವು ಮಹಾಸಂಕಟದಲ್ಲಿರುತ್ತೇವೆ ಅಯ್ಯೋ ಎಂದು ಪ್ರಾರ್ಥಿಸಿದರು.",
			"38": "<sup>38</sup> ಈ ಎಲ್ಲಾ ಕಾರಣಗಳಿಂದ ನಾವು ಲೇಖನ ರೂಪವಾದ ಪ್ರತಿಜ್ಞೆಯನ್ನು ಮಾಡಿದೆವು. ಅದಕ್ಕೆ ನಮ್ಮ ಪ್ರಭುಗಳೂ ಲೇವಿಯರೂ ಯಾಜಕರೂ ಸಹಿಮಾಡಿ ಮುದ್ರೆಹಾಕಿದರು."
		},
		"10": {
			"1": "<sup>1</sup> ಸಹಿಮಾಡಿದವರು - ಹಕಲ್ಯನ ಮಗನಾದ ನೆಹೆಮೀಯನೆಂಬ ದೇಶಾಧಿಪತಿಯೂ",
			"2": "<sup>2</sup> ಚಿದ್ಕೀಯ, ಸೆರಾಯ, ಅಜರ್ಯ,",
			"3": "<sup>3</sup> ಯೆರೆಮೀಯ, ಪಷ್ಹೂರ್, ಅಮರ್ಯ, ಮಲ್ಕೀಯ,",
			"4": "<sup>4</sup> ಹಟ್ಟೂಷ್, ಶೆಬನ್ಯ, ಮಲ್ಲೂಕ್, ಹಾರಿಮ್,",
			"6": "<sup>6</sup> ಮೆರೇಮೋತ್, ಓಬದ್ಯ, ದಾನೀಯೇಲ್, ಗಿನ್ನೆತೋನ್,",
			"7": "<sup>7</sup> ಬಾರೂಕ್, ಮೆಷುಲ್ಲಾಮ್, ಅಬೀಯ,",
			"8": "<sup>8</sup> ಮಿಯ್ಯಾಮೀನ್, ಮಾಜ್ಯ, ಬಿಲ್ಗೈ, ಶೆಮಾಯ ಎಂಬ ಯಾಜಕರೂ.",
			"9": "<sup>9</sup> ಲೇವಿಯರಲ್ಲಿ ಅಜನ್ಯನ ಮಗನಾದ ಯೇಷೂವ, ಹೇನಾದಾದ್ ವಂಶದವನಾದ ಬಿನ್ನೂಯ್, ಕದ್ಮೀಯೇಲ್ ಎಂಬವರೂ",
			"10": "<sup>10</sup> ಇವರ ಬಂಧುಗಳಾದ ಶೆಬನ್ಯ,",
			"11": "<sup>11</sup> ಹೋದೀಯ, ಕೆಲೀಟ, ಪೆಲಾಯ, ಹಾನಾನ್, ಮೀಕ,",
			"12": "<sup>12</sup> ರೆಹೋಬ್, ಹಷಬ್ಯ, ಜಕ್ಕೂರ್, ಶೇರೇಬ್ಯ, ಶೆಬನ್ಯ,",
			"13": "<sup>13</sup> ಹೋದೀಯ, ಬಾನೀ, ಬೆನೀನೂ ಎಂಬವರೂ.",
			"14": "<sup>14</sup> ಜನಪ್ರಧಾನರಲ್ಲಿ ಪರೋಷ್, ಪಹತ್",
			"15": "<sup>15</sup> ಮೋವಾಬ್, ಏಲಾಮ್, ಜತ್ತೂ, ಬಾನೀ, ಬುನ್ನೀ,",
			"16": "<sup>16</sup> ಅಜ್ಗಾದ್, ಬೇಬೈ, ಅದೋನೀಯ, ಬಿಗ್ವೈ,",
			"18": "<sup>18</sup> ಆದೀನ್, ಆಟೇರ್, ಹಿಜ್ಕೀಯ, ಅಜ್ಜೂರ್,",
			"19": "<sup>19</sup> ಹೋದೀಯ, ಹಾಷುಮ್, ಬೇಚೈ, ಹಾರೀಫ್,",
			"21": "<sup>21</sup> ಅನಾತೋತ್, ನೇಬೈ, ಮಗ್ಪೀಯಾಷ್, ಮೆಷುಲ್ಲಾಮ್,",
			"22": "<sup>22</sup> ಹೇಜೀರ್, ಮೆಷೇಜಬೇಲ್, ಚಾದೋಕ್,",
			"23": "<sup>23</sup> ಯದ್ದೂವ, ಪೆಲಟ್ಯ, ಹಾನಾನ್, ಆನಾಯ,",
			"24": "<sup>24</sup> ಹೋಷೇಯ, ಹನನ್ಯ, ಹಷ್ಷೂಬ್, ಹಲೋಹೇಷ್,",
			"25": "<sup>25</sup> ಪಿಲ್ಹ, ಶೋಬೇಕ್, ರೆಹೂಮ್, ಹಷಬ್ನ, ಮಾಸೇಯ,",
			"26": "<sup>26</sup> ಅಹೀಯ, ಹಾನಾನ್, ಆನಾನ್, ಮಲ್ಲೂಕ್,",
			"27": "<sup>27</sup> ಹಾರಿಮ್, ಬಾನ ಎಂಬವರೂ.",
			"28": "<sup>28</sup> ಯಾಜಕರು, ಲೇವಿಯರು, ದ್ವಾರಪಾಲಕರು, ಗಾಯಕರು, ದೇವಸ್ಥಾನದಾಸರು, ಉಳಿದ ಜನರು ಇವರಲ್ಲಿ ದೇವರ ಧರ್ಮೋಪದೇಶದ ನಿಮಿತ್ತವಾಗಿ ಅನ್ಯದೇಶದವರ ಗೊಡವೆಯನ್ನು ತೊರೆದುಬಿಟ್ಟವರು ತಮ್ಮ ತಮ್ಮ ಹೆಂಡತಿಯರು, ಗ್ರಹಿಸಶಕ್ತರಾದ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳು ಇವರೊಡನೆ ಬಂದು ಮುಖಂಡರಾದ ತಮ್ಮ ಸಹೋದರರನ್ನು ಕೂಡಿಕೊಂಡು",
			"29": "<sup>29</sup> ತಾವು ದೇವರ ಸೇವಕನಾದ ಮೋಶೆಯ ಮುಖಾಂತರ ಕೊಡಲ್ಪಟ್ಟ ದೇವರ ಧರ್ಮೋಪದೇಶವನ್ನು ಅನುಸರಿಸಿ ತಮ್ಮ ಕರ್ತನಾದ ಯೆಹೋವನ ಎಲ್ಲಾ ಆಜ್ಞಾನಿಯಮವಿಧಿಗಳನ್ನು ಕೈಕೊಂಡು ನಡೆಯುವದಾಗಿ ಆಣೆಯಿಟ್ಟು ಪ್ರಮಾಣಮಾಡಿದರು. ಹೇಗಂದರೆ -",
			"30": "<sup>30</sup> ದೇಶನಿವಾಸಿಗಳಿಗೆ ನಮ್ಮ ಹೆಣ್ಣುಗಳನ್ನು ಕೊಡುವದಿಲ್ಲ. ನಮ್ಮ ಮಕ್ಕಳಿಗೋಸ್ಕರ ಅವರಿಂದ ಹೆಣ್ಣು ತೆಗೆದುಕೊಳ್ಳುವದಿಲ್ಲ;",
			"31": "<sup>31</sup> ದೇಶನಿವಾಸಿಗಳು ಮಾರುವದಕ್ಕೋಸ್ಕರ ತರುವ ಯಾವ ಸರಕುಗಳನ್ನೂ ಧಾನ್ಯವನ್ನೂ ಸಬ್ಬತ್ ಮೊದಲಾದ ಪರಿಶುದ್ಧ ದಿನಗಳಲ್ಲಿ ಕೊಂಡುಕೊಳ್ಳುವದಿಲ್ಲ; ಪ್ರತಿಯೊಂದು ಏಳನೆಯ ವರುಷ [ಭೂಮಿಯ ಸಾಗುವಳಿಯನ್ನೂ ] ಹೆರರು ಕೊಡಬೇಕಾದ ಸಾಲವನ್ನೂ ಬಿಟ್ಟು ಬಿಡುತ್ತೇವೆ;",
			"32": "<sup>32</sup> ವರುಷಕ್ಕೆ ಒಂದು ಶೆಕೆಲಿನ ಮೂರನೆಯ ಭಾಗವನ್ನು ದೇವಾಲಯ ಸೇವೆಗಾಗಿ ಕೊಡಬೇಕೆಂಬ ಕರ್ತವ್ಯವನ್ನು ವಹಿಸಿಕೊಳ್ಳುತ್ತೇವೆ;",
			"33": "<sup>33</sup> ಆ ಹಣವು ದೇವರ ಸನ್ನಿಧಿಯಲ್ಲಿಡತಕ್ಕ ರೊಟ್ಟಿ, ನಿತ್ಯಧಾನ್ಯ ನೈವೇದ್ಯ ಇವುಗಳಿಗೋಸ್ಕರವೂ ನಿತ್ಯಸರ್ವಾಂಗಹೋಮ ಮತ್ತು ಸಬ್ಬತ್ ದಿನ, ಅಮಾವಾಸ್ಯೆ, ಜಾತ್ರೆ ಇವುಗಳಲ್ಲಿ ಮಾಡತಕ್ಕ ಸರ್ವಾಂಗಹೋಮ, ಸಮಾಧಾನಯಜ್ಞ, ಇಸ್ರಾಯೇಲ್ಯರ ದೋಷಪರಿಹಾರಾರ್ಥವಾದ ಯಜ್ಞ ಇವುಗಳಿಗೋಸ್ಕರವೂ ನಮ್ಮ ದೇವಾಲಯ ಸಂಬಂಧವಾದ ಬೇರೆ ಎಲ್ಲಾ ಕೆಲಸಕ್ಕೋಸ್ಕರವೂ ವೆಚ್ಚವಾಗಬೇಕು;",
			"34": "<sup>34</sup> ಧರ್ಮಶಾಸ್ತ್ರವಿಧಿಗನುಸಾರವಾಗಿ ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಬೆಂಕಿಯುರಿಸುವದಕ್ಕಾಗಿ ವರುಷ ವರುಷ ನೇಮಿತವಾದ ಕಾಲಗಳಲ್ಲಿ ನಮ್ಮ ದೇವಾಲಯಕ್ಕೆ ಕಟ್ಟಿಗೆ ದೊರಕುವ ಹಾಗೆ ಆಯಾಗೋತ್ರಾನುಸಾರ ಕಾಷ್ಠದಾನ ಮಾಡತಕ್ಕವರು ಇಂಥವರು ಎಂಬದನ್ನು ಯಾಜಕರೂ ಲೇವಿಯರೂ ಸಾಧಾರಣ ಜನರೂ ಆಗಿರುವ ನಮ್ಮಲ್ಲಿ ಚೀಟುಹಾಕಿ ಗೊತ್ತುಮಾಡುತ್ತೇವೆ;",
			"35": "<sup>35</sup> ನಮ್ಮ ಭೂಮಿಯ ಮತ್ತು ಎಲ್ಲಾ ಹಣ್ಣಿನ ಮರಗಳ ಪ್ರಥಮ ಫಲಗಳನ್ನು ಪ್ರತಿವರುಷವೂ ಯೆಹೋವನ ಆಲಯಕ್ಕೆ ತಂದುಕೊಡುವೆವು;",
			"36": "<sup>36</sup> ನಮ್ಮ ಚೊಚ್ಚಲಮಕ್ಕಳ ವಿಷಯದಲ್ಲೂ ನಮ್ಮ ಪಶುಗಳ ಚೊಚ್ಚಲಮರಿಗಳ ವಿಷಯದಲ್ಲೂ ಧರ್ಮಶಾಸ್ತ್ರ ವಿಧಿಗನುಸಾರವಾಗಿ ನಡೆಯುವೆವು; ನಮ್ಮ ಚೊಚ್ಚಲ ಕರುಗಳನ್ನೂ ಚೊಚ್ಚಲ ಆಡುಕುರಿಮರಿಗಳನ್ನೂ ನಮ್ಮ ದೇವಾಲಯಕ್ಕೆ ತಂದು ಅಲ್ಲಿ ಕೆಲಸ ನಡಿಸುತ್ತಿರುವ ಯಾಜಕರಿಗೆ ಕೊಡುವೆವು;",
			"37": "<sup>37</sup> ನಾವು ದೇವರಿಗಾಗಿ ಪ್ರತ್ಯೇಕಿಸಿಡತಕ್ಕ ಪ್ರಥಮಫಲದ ಹಿಟ್ಟು, ಹಣ್ಣುಹಂಪಲು, ದ್ರಾಕ್ಷಾರಸ, ಎಣ್ಣೆ ಮೊದಲಾದವುಗಳನ್ನು ಯಾಜಕರಿಗೋಸ್ಕರ ನಮ್ಮ ದೇವರ ಆಲಯದ ಕೊಠಡಿಗಳಲ್ಲಿ ತಂದಿಡುವೆವು; ನಮ್ಮ ಭೂಮಿಯ ಹುಟ್ಟುವಳಿಯ ದಶಮಾಂಶವು ಲೇವಿಯರದಾಗಬೇಕು; ಲೇವಿಯರು ತಾವೇ ಬಂದು ನಮ್ಮ ಸಾಗುವಳಿಯ ಊರುಗಳಲ್ಲಿ ಅದನ್ನು ಕೂಡಿಸಬೇಕು;",
			"38": "<sup>38</sup> ಅವರು ದಶಮಾಂಶವನ್ನು ಕೂಡಿಸುವಾಗ ಆರೋನನ ವಂಶದವನಾದ ಯಾಜಕನೊಬ್ಬನು ಅವರ ಸಂಗಡ ಇರಬೇಕು. ಲೇವಿಯರು ತಮಗೆ ದೊರಕಿದ ಭಾಗದ ದಶಮಾಂಶವನ್ನು ದೇವಾಲಯದ ಬಂಡಾರದ ಕೊಠಡಿಗಳಲ್ಲಿಡಬೇಕು,",
			"39": "<sup>39</sup> ಇಸ್ರಾಯೇಲ್ಯರೂ ಲೇವಿಯರೂ ಧಾನ್ಯ, ದ್ರಾಕ್ಷಾರಸ, ಎಣ್ಣೆ ಇವುಗಳಲ್ಲಿ ದೇವರಿಗೋಸ್ಕರ ಪ್ರತ್ಯೇಕಿಸತಕ್ಕ ಭಾಗಗಳನ್ನು ತಂದು ಸೇವೆನಡಿಸುತ್ತಿರುವ ಯಾಜಕರು, ದ್ವಾರಪಾಲಕರು, ಗಾಯಕರು ಇವರೂ ಪವಿತ್ರಾಲಯದ ಪಾತ್ರೆಗಳೂ ಇರುವ ಕೊಠಡಿಗಳಲ್ಲಿ ಇಡಬೇಕು. ನಮ್ಮ ದೇವರ ಆಲಯವನ್ನು ಎಂದೂ ಅಲಕ್ಷ್ಯ ಮಾಡುವದಿಲ್ಲ ಎಂದು ಬರೆದುಕೊಟ್ಟರು."
		},
		"11": {
			"1": "<sup>1</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಪ್ರಭುಗಳು ಮಾತ್ರ ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ವಾಸಿಸುತ್ತಿದ್ದರು. ಉಳಿದ ಜನರೊಳಗೆ ಹತ್ತತ್ತು ಮಂದಿಯಲ್ಲಿ ಒಂಭತ್ತು ಮಂದಿ ತಮ್ಮ ತಮ್ಮ ಊರುಗಳಲ್ಲಿದ್ದುಕೊಂಡು ಒಬ್ಬನು ಪವಿತ್ರನಗರವಾಗಿರುವ ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ವಾಸಿಸುವದಕ್ಕಾಗಿ ಅಲ್ಲಿಗೆ ಬರುವಂತೆ ಚೀಟುಹಾಕಿ ಗೊತ್ತು ಮಾಡಿದರು.",
			"2": "<sup>2</sup> ಮತ್ತು ಸ್ವೇಚ್ಫೆಯಿಂದ ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ವಾಸಿಸುವದಕ್ಕೆ ಮನಸ್ಸುಮಾಡಿದಂಥವರನ್ನು ಜನರು ಆಶೀರ್ವದಿಸಿದರು.",
			"3": "<sup>3</sup> ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ವಾಸಿಸುತ್ತಿದ್ದ [ಯೆಹೂದ] ಸಂಸ್ಥಾನಪ್ರಧಾನರ ಪಟ್ಟಿ. (ಸಾಧಾರಣ ಇಸ್ರಾಯೇಲ್ಯರೂ ಯಾಜಕರೂ ಲೇವಿಯರೂ ದೇವಸ್ಥಾನದಾಸರೂ ಸೊಲೊಮೋನನ ದಾಸರ ವಂಶದವರೂ ತಮ್ಮ ತಮ್ಮ ಸ್ವಾಸ್ತ್ಯಗಳಿರುವ ಯೆಹೂದ ದೇಶದ ಪಟ್ಟಣಗಳಲ್ಲಿ ವಾಸಿಸುತ್ತಿದ್ದರು.",
			"4": "<sup>4</sup> ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಯೆಹೂದ ಬೆನ್ಯಾಮೀನ್ ಕುಲಗಳವರಲ್ಲಿ ಕೆಲವರು ವಾಸವಾಗಿದ್ದರು.) ಯೆಹೂದ ಕುಲದವರಲ್ಲಿ - ಒಬ್ಬನು ಅತಾಯ; ಇವನು ಉಜ್ಜೀಯನ ಮಗನು; ಇವನು ಜೆಕರ್ಯನ ಮಗನು; ಇವನು ಅಮರ್ಯನ ಮಗನು; ಇವನು ಶೆಫಟ್ಯನ ಮಗನು; ಇವನು ಮಹಲಲೇಲನ ಮಗನು; ಇವನು ಪೆರೆಚ್ ಸಂತಾನದವನು.",
			"5": "<sup>5</sup> ಇನ್ನೊಬ್ಬನು ಮಾಸೇಯ; ಇವನು ಬಾರೂಕನ ಮಗನು; ಇವನು ಕೊಲ್ಹೋಜೆಯ ಮಗನು; ಇವನು ಹಜಾಯನ ಮಗನು; ಇವನು ಅದಾಯನ ಮಗನು; ಇವನು ಯೋಯಾರೀಬನ ಮಗನು; ಇವನು ಜೆಕರ್ಯನ ಮಗನು; ಇವನು ಶೇಲಹನ ಸಂತಾನದವನು.",
			"6": "<sup>6</sup> ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ವಾಸಿಸುತ್ತಿದ್ದ ಪೆರೆಚ್ ಸಂತಾನದ ರಣವೀರರು ನಾನೂರರುವತ್ತೆಂಟು ಮಂದಿ.",
			"7": "<sup>7</sup> ಬೆನ್ಯಾಮೀನ್ ಕುಲದವರಲ್ಲಿ - ಸಲ್ಲು; ಇವನು ಮೆಷುಲ್ಲಾಮನ ಮಗನು; ಇವನು ಯೋವೇದನ ಮಗನು; ಇವನು ಪೆದಾಯನ ಮಗನು; ಇವನು ಕೋಲಾಯನ ಮಗನು; ಇವನು ಮಾಸೇಯನ ಮಗನು; ಇವನು ಈತೀಯೇಲನ ಮಗನು; ಇವನು ಯೆಶಾಯನ ಮಗನು.",
			"8": "<sup>8</sup> ರಣವೀರರಾದ ಇವನ ಬಂಧುಜನರು ಒಂಭೈನೂರಿಪ್ಪತ್ತೆಂಟು ಮಂದಿ.",
			"9": "<sup>9</sup> ಜಿಕ್ರಿಯ ಮಗನಾದ ಯೋವೇಲನು ಇವರ ನಾಯಕನು; ಹಸ್ಸೆನೂವನ ಮಗನಾದ ಯೆಹೂದನು ಎರಡನೆಯ ಪುರಾಧಿಕಾರಿ.",
			"10": "<sup>10</sup> ಯಾಜಕರಲ್ಲಿ - ಯೆದಾಯ, ಯೋಯಾರೀಬ್, ಯಾಕೀನ್,",
			"11": "<sup>11</sup> ಅಹೀಟೂಬನ ಸಂತಾನದವನಾದ ಮೆರಾಯೋತನಿಂದ ಹುಟ್ಟಿದ ಚಾದೋಕನ ಮರಿಮಗನೂ ಮೆಷುಲ್ಲಾಮನ ಮೊಮ್ಮಗನೂ ಹಿಲ್ಕೀಯನ ಮಗನೂ ದೇವಾಲಯದ ಅಧಿಪತಿಯೂ ಆದ ಸೆರಾಯ ಇವರು.",
			"12": "<sup>12</sup> ಇವರೂ ದೇವಾಲಯದ ಸೇವೆ ನಡಿಸುತ್ತಿದ್ದ ಇವರ ಬಂಧುಗಳೂ ಎಂಟುನೂರಿಪ್ಪತ್ತೆರಡು ಮಂದಿ. ಇವರಲ್ಲದೆ ಅದಾಯನೆಂಬವನು ಇನ್ನೊಬ್ಬನು. ಇವನು ಯೆರೋಹಾಮನ ಮಗನು; ಇವನು ಪೆಲಲ್ಯನ ಮಗನು; ಇವನು ಅವ್ಚಿುೕಯ ಮಗನು; ಇವನು ಜೆಕರ್ಯನ ಮಗನು; ಇವನು ಪಷ್ಹೂರನ ಮಗನು; ಇವನು ಮಲ್ಕೀಯನ ಮಗನು.",
			"13": "<sup>13</sup> ಗೋತ್ರಪ್ರಧಾನರಾದ ಈ ಅದಾಯನ ಬಂಧುಗಳು ಇನ್ನೂರ ನಾಲ್ವತ್ತೆರಡು ಮಂದಿ. ಅಮಷ್ಷೈ ಎಂಬವನು ಮತ್ತೊಬ್ಬನು. ಇವನು ಅಜರೇಲನ ಮಗನು; ಇವನು ಅಹಜೈಯ ಮಗನು; ಇವನು ಮೆಷಿಲ್ಲೇಮೋತನ ಮಗನು; ಇವನು ಇಮ್ಮೇರನ ಮಗನು.",
			"14": "<sup>14</sup> ರಣವೀರರಾಗಿದ್ದ ಈ ಅಮಷ್ಷೈಯ ಬಂಧುಗಳು ನೂರಿಪ್ಪತ್ತೆಂಟು ಮಂದಿ. ಹಗ್ಗೆದೋಲೀಮನ ಮಗನಾದ ಜಬ್ದೀಯೇಲನು ಇವರ ನಾಯಕನು.",
			"15": "<sup>15</sup> ಲೇವಿಯರಲ್ಲಿ - ಹಷ್ಷೂಬನ ಮಗನೂ ಅಜ್ರೀಕಾಮನ ಮೊಮ್ಮಗನೂ ಹಷಬ್ಯನ ಮರಿಮಗನೂ ಬುನ್ನೀವಂಶದವನೂ ಆದ ಶೆಮಾಯನೂ",
			"16": "<sup>16</sup> ಲೇವಿಗೋತ್ರಪ್ರಧಾನರಲ್ಲಿ ದೇವಾಲಯದ ಲೌಕಿಕಕಾರ್ಯಗಳ ಮೇಲ್ವಿಚಾರಕರಾದ ಶಬ್ಬೆತೈ, ಯೋಜಾಬಾದ್ ಎಂಬವರೂ",
			"17": "<sup>17</sup> ಮೀಕನ ಮಗನೂ ಜಬ್ದೀಯ ಮೊಮ್ಮಗನೂ ಆಸಾಫನ ಮರಿಮಗನೂ ಪ್ರಾರ್ಥನಾಸಮಯದಲ್ಲಿ - ಕೃತಜ್ಞತಾಸ್ತುತಿಮಾಡಿರಿ ಎಂದು ಹೇಳಿ ಭಜಕರನ್ನು ನಡಿಸುತ್ತಿದ್ದ ಮತ್ತನ್ಯ, ತನ್ನ ಬಂಧುಗಳಲ್ಲಿ ದ್ವಿತೀಯ ಸ್ಥಾನದವನಾದ ಬಕ್ಬುಕ್ಯ, ಶಮ್ಮೂವನ ಮಗನೂ ಗಾಲಾಲನ ಮೊಮ್ಮಗನೂ ಯೆದೂತೂನನ ಮರಿಮಗನೂ ಆದ ಅಬ್ದ ಎಂಬವರೂ.",
			"18": "<sup>18</sup> ಪರಿಶುದ್ಧ ನಗರದಲ್ಲಿದ್ದ ಎಲ್ಲಾ ಲೇವಿಯರು ಇನ್ನೂರೆಂಭತ್ತನಾಲ್ಕು ಮಂದಿ.",
			"19": "<sup>19</sup> ದ್ವಾರಪಾಲಕರಲ್ಲಿ - ಅಕ್ಕೂಬ್ ಟಲ್ಮೋನರೂ ಬಾಗಲುಗಳನ್ನು ಕಾಯುತ್ತಿದ್ದ ಇವರ ಬಂಧುಗಳೂ; ಒಟ್ಟು ನೂರೆಪ್ಪತ್ತೆರಡು ಮಂದಿ.",
			"20": "<sup>20</sup> ಉಳಿದ ಇಸ್ರಾಯೇಲ್ಯರೂ ಯಾಜಕರೂ ಲೇವಿಯರೂ ತಮ್ಮ ತಮ್ಮ ಸ್ವಾಸ್ತ್ಯಗಳಿರುವ ಯೆಹೂದದ ಎಲ್ಲಾ ಪಟ್ಟಣಗಳಲ್ಲಿ ವಾಸಿಸುತ್ತಿದ್ದರು.",
			"21": "<sup>21</sup> ದೇವಸ್ಥಾನದಾಸರು ಓಪೇಲ್ ಗುಡ್ಡದಲ್ಲಿ ವಾಸಿಸುತ್ತಿದ್ದರು; ಚೀಹ, ಗಿಷ್ಪ ಎಂಬವರು ಇವರ ನಾಯಕರು.",
			"22": "<sup>22</sup> ದೇವಾಲಯ ಸೇವಾ ಸಂಬಂಧದಲ್ಲಿ ಗಾಯಕರಾದ ಆಸಾಫ್ಯರಿಗೆ ಸೇರಿದ ಬಾನೀಯ ಮಗನೂ ಹಷಬ್ಯನ ಮೊಮ್ಮಗನೂ ಮತ್ತನ್ಯನ ಮರಿಮಗನೂ ಮೀಕನ ವಂಶದವನೂ ಆದ ಉಜ್ಜೀಯು ಯೆರೂಸಲೇಮಿನಲ್ಲಿದ್ದ ಲೇವಿಯರ ನಾಯಕನು.",
			"23": "<sup>23</sup> ಇವರ ಮತ್ತು ಗಾಯಕರ ಅನುದಿನದ ಕರ್ತವ್ಯಾಂಶಗಳ ವಿಷಯವಾಗಿ ರಾಜನಿಯಮವಿತ್ತು.",
			"24": "<sup>24</sup> ಮೆಷೇಜಬೇಲನ ಮಗನೂ ಜೆರಹನ ಸಂತಾನದವನೂ ಯೆಹೂದ ಕುಲದವನೂ ಆದ ಪೆತಹ್ಯನು ಪ್ರಜೆಗಳ ಎಲ್ಲಾ ಕಾರ್ಯಗಳ ಸಂಬಂಧದಲ್ಲಿ ರಾಜನ ಕಾರಭಾರಿಯಾಗಿದ್ದನು.",
			"25": "<sup>25</sup> ಭೂಸ್ವಾಸ್ತ್ಯವಿದ್ದವರು ವಾಸಿಸುತ್ತಿದ್ದ ಊರುಗಳು - ಕಿರ್ಯತರ್ಬ, ದೀಬೋನ್, ಯೆಕಬ್ಜೆಯೇಲ್, ಇವುಗಳೂ",
			"26": "<sup>26</sup> ಇವುಗಳ ಗ್ರಾಮಗಳೂ ಯೇಷೂವ,",
			"27": "<sup>27</sup> ಮೋಲಾದ, ಬೇತ್ಪೆಲೆಟ್, ಹಚರ್ಷೂವಲ್ ಇವುಗಳೂ",
			"28": "<sup>28</sup> ಬೇರ್ಷೆಬವೂ ಅದರ ಗ್ರಾಮಗಳೂ ಚಿಕ್ಲಗ್ ಊರೂ",
			"29": "<sup>29</sup> ಮೆಕೋನವೂ ಅದರ ಗ್ರಾಮಗಳೂ ಏನ್ರಿಮ್ಮೋನ್, ಚೊರ್ರ, ಯರ್ಮೂತ್ ಇವುಗಳೂ",
			"30": "<sup>30</sup> ಜನೋಹ, ಅದುಲ್ಲಾಮ್ ಇವುಗಳೂ ಇವುಗಳ ಗ್ರಾಮಗಳೂ ಲಾಕೀಷ್ ಊರೂ ಇದರ ಪ್ರಾಂತಗಳೂ ಅಜೇಕವೂ ಅದರ ಗ್ರಾಮಗಳೂ ಯೆಹೂದ ಕುಲದವರ ನಿವಾಸಸ್ಥಾನಗಳು. ಅವರು ಬೇರ್ಷೆಬದಿಂದ ಹಿನ್ನೋಮ್ ತಗ್ಗಿನವರೆಗೂ ವಾಸಿಸುತ್ತಿದ್ದರು.",
			"31": "<sup>31</sup> ಗೆಬ, ಮಿಕ್ಮಾಷ್, ಅಯ್ಯಾ, ಗ್ರಾಮ ಸಹಿತವಾದ",
			"32": "<sup>32</sup> ಬೇತೇಲ್ ಎಂಬ ಊರುಗಳೂ, ಅನಾತೋತ್,",
			"33": "<sup>33</sup> ನೋಬ್, ಅನನ್ಯ, ಹಾಚೋರ್, ರಾಮಾ, ಗಿತ್ತಯಿಮ್,",
			"34": "<sup>34</sup> ಹಾದೀದ್, ಚೆಬೋಯೀಮ್, ನೆಬಲ್ಲಾಟ್,",
			"35": "<sup>35</sup> ಲೋದ್, ಓನೋ, ಗೇಹರಾಷೀಮ್ ಎಂಬ ಊರುಗಳೂ ಬೆನ್ಯಾಮೀನ್ ಕುಲದವರ ನಿವಾಸಸ್ಥಾನಗಳು.",
			"36": "<sup>36</sup> ಲೇವಿಯರಲ್ಲಿ ಕೆಲವು ವರ್ಗಗಳವರು ಯೆಹೂದ್ಯರಲ್ಲಿಯೂ ಕೆಲವರು ಬೆನ್ಯಾಮೀನ್ಯರಲ್ಲಿಯೂ ವಾಸಿಸುತ್ತಿದ್ದರು."
		},
		"12": {
			"1": "<sup>1</sup> ಶೆಯಲ್ತಿಯೇಲನ ಮಗನಾದ ಜೆರುಬ್ಬಾಬೆಲ್, ಯೇಷೂವ ಇವರೊಡನೆ [ಯೆಹೂದ ದೇಶಕ್ಕೆ] ಬಂದ ಯಾಜಕರೂ ಲೇವಿಯರೂ.",
			"2": "<sup>2</sup> ಸೆರಾಯ, ಯೆರೆಮೀಯ, ಎಜ್ರ, ಅಮರ್ಯ,",
			"3": "<sup>3</sup> ಮಲ್ಲೂಕ್, ಹಟ್ಟೂಷ್, ಶೆಕನ್ಯ, ರೆಹುಮ್,",
			"4": "<sup>4</sup> ಮೆರೇಮೋತ್, ಇದ್ದೋ, ಗಿನ್ನೆತೋನ್, ಅಬೀಯ,",
			"5": "<sup>5</sup> ಮಿಯ್ಯಾಮೀನ್, ಮಾದ್ಯ, ಬಿಲ್ಗ, ಶೆಮಾಯ,",
			"7": "<sup>7</sup> ಯೋಯಾರೀಬ್, ಯೆದಾಯ, ಸಲ್ಲೂ, ಆಮೋಕ್, ಹಿಲ್ಕೀಯ, ಯೆದಾಯ ಇವರು ಯೇಷೂವನ ಕಾಲದಲ್ಲಿ ಯಾಜಕರ ಮತ್ತು ಅವರ ಬಂಧುಗಳ ಪ್ರಧಾನರು.",
			"8": "<sup>8</sup> ಲೇವಿಯರು - ಯೇಷೂವ, ಬಿನ್ನೂಯ್, ಕದ್ಮೀಯೇಲ್, ಶೇರೇಬ್ಯ, ಯೆಹೂದ, ಮತ್ತನ್ಯ ಇವರು. ಮತ್ತನ್ಯನೂ ಅವನ ಸಹೋದರರೂ ಕೃತಜ್ಞತಾಭಜನ ನಾಯಕರು.",
			"9": "<sup>9</sup> ಇವರ ಸಹೋದರರಾದ ಬಕ್ಬುಕ್ಯ, ಉನ್ನೀ ಎಂಬವರು ಭಜನಸೇವೆಯಲ್ಲಿ ಇವರೆದುರಿಗೆ ನಿಂತು ಹಾಡುವವರು.",
			"10": "<sup>10</sup> ಯೇಷೂವನು ಯೋಯಾಕೀಮನನ್ನೂ ಯೋಯಾಕೀಮನು ಎಲ್ಯಾಷೀಬನನ್ನೂ ಎಲ್ಯಾಷೀಬನು ಯೋಯಾದನನ್ನೂ",
			"11": "<sup>11</sup> ಯೋಯಾದನು ಯೋನಾತಾನನನ್ನೂ ಯೋನಾತಾನನು ಯದ್ದೂವನನ್ನೂ ಪಡೆದರು.",
			"12": "<sup>12</sup> ಯೋಯಾಕೀಮನ ಕಾಲದಲ್ಲಿ ಯಾಜಕರಾಗಿದ್ದ ಗೋತ್ರಪ್ರಧಾನರು - ಸೆರಾಯನ ಗೋತ್ರದ ಮೆರಾಯ,",
			"13": "<sup>13</sup> ಯೆರೆಮೀಯನ ಗೋತ್ರದ ಹನನ್ಯ, ಎಜ್ರನ ಗೋತ್ರದ ಮೆಷುಲ್ಲಾಮ್, ಅಮರ್ಯನ ಗೋತ್ರದ ಯೆಹೋಹಾನಾನ್,",
			"14": "<sup>14</sup> ಮಲ್ಲೂಕಿಯ ಗೋತ್ರದ ಯೋನಾತಾನ್, ಶೆಬನ್ಯನ ಗೋತ್ರದ ಯೋಸೇಫ್,",
			"15": "<sup>15</sup> ಹಾರಿಮನ ಗೋತ್ರದ ಅದ್ನ, ಮೆರಾಯೋತನ ಗೋತ್ರದ ಹೆಲ್ಕೈ,",
			"16": "<sup>16</sup> ಇದ್ದೋವಿನ ಗೋತ್ರದ ಜೆಕರ್ಯ, ಗಿನ್ನೆತೋನನ ಗೋತ್ರದ ಮೆಷುಲ್ಲಾಮ್,",
			"17": "<sup>17</sup> ಅಬೀಯನ ಗೋತ್ರದ ಜಿಕ್ರೀ, ಮಿನ್ಯಾಮೀನನ ಮತ್ತು ಮೋವದ್ಯನಗೋತ್ರದ ಪಿಲ್ಟೈ,",
			"18": "<sup>18</sup> ಬಿಲ್ಗಾಗೋತ್ರದ ಶಮ್ಮೂವ, ಶೆಮಾಯನ ಗೋತ್ರದ ಯೆಹೋನಾತಾನ್,",
			"19": "<sup>19</sup> ಯೋಯಾರೀಬನ ಗೋತ್ರದ ಮತ್ತೆನೈ, ಯೆದಾಯನ ಗೋತ್ರದ ಉಜ್ಜೀ,",
			"20": "<sup>20</sup> ಸಲ್ಲೈ ಗೋತ್ರದ ಕಲ್ಲೈ, ಆಮೋಕನ ಗೋತ್ರದ ಏಬೆರ್,",
			"21": "<sup>21</sup> ಹಿಲ್ಕೀಯನ ಗೋತ್ರದ ಹಷಬ್ಯ, ಯೆದಾಯನ ಗೋತ್ರದ ನೆತನೇಲ್ ಇವರು.",
			"22": "<sup>22</sup> ಎಲ್ಯಾಷೀಬ್, ಯೋಯಾದ, ಯೋಹಾನಾನ್, ಯದ್ದೂವ ಇವರ ಕಾಲಗಳಲ್ಲಿದ್ದ ಲೇವಿಯ ಗೋತ್ರ ಪ್ರಧಾನರ ಪಟ್ಟಿಯೂ ಪಾರಸಿಯನಾದ ದಾರ್ಯಾವೇಷ ರಾಜನ ಆಳಿಕೆಯವರೆಗಿದ್ದ ಯಾಜಕರ ಪಟ್ಟಿಯೂ ಉಂಟು.",
			"23": "<sup>23</sup> ಎಲ್ಯಾಷೀಬನ ಮೊಮ್ಮಗನಾದ ಯೋಹಾನಾನನ ಕಾಲದವರೆಗಿದ್ದ ಲೇವಿಯ ಗೋತ್ರಪ್ರಧಾನರ ಪಟ್ಟಿಯು ಪೂರ್ವಕಾಲವೃತ್ತಾಂತವೆಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತದೆ.",
			"24": "<sup>24</sup> ಈ ಲೇವಿಗೋತ್ರಪ್ರಧಾನರಲ್ಲಿ ಯೇಷೂವನ ಮಗನೂ ಯೋಚಾದಾಕನ ಮೊಮ್ಮಗನೂ ಆದ ಯೋಯಾಕೀಮ್, ದೇಶಾಧಿಪತಿಯಾದ ನೆಹೆಮೀಯ, ಯಾಜಕನೂ ಧರ್ಮೋಪದೇಶಕನೂ ಆದ ಎಜ್ರ",
			"25": "<sup>25</sup> ಇವರ ಕಾಲದಲ್ಲಿದ್ದವರು - ಹಷಬ್ಯ, ಶೇರೇಬ್ಯ, ಯೇಷೂವ, ಬಿನ್ನೂಯ್, ಕದ್ಮೀಯೇಲ್ ಇವರು. (ದೇವರ ಮನುಷ್ಯನಾದ ದಾವೀದನ ಅಪ್ಪಣೆಯ ಮೇರೆಗೆ ಇವರೂ ಇವರ ಸಹೋದರರೂ ಸರತಿಗೆ ಎರಡೆರಡು ವರ್ಗಗಳಾಗಿ ಪರಸ್ಪರ ಹಾಡುತ್ತಾ ಕೀರ್ತನೆಯನ್ನೂ ಕೃತಜ್ಞತಾಸ್ತುತಿಯನ್ನೂ ಮಾಡುತ್ತಿದ್ದರು.)",
			"26": "<sup>26</sup> ಇವರಲ್ಲದೆ ಬಾಗಲುಗಳ ಬಳಿಯಲ್ಲಿದ್ದ ಉಗ್ರಾಣಗಳನ್ನು ಕಾಯತಕ್ಕ ದ್ವಾರಪಾಲಕರಾದ ಮತ್ತನ್ಯ, ಬಕ್ಬುಕ್ಯ, ಓಬದ್ಯ, ಮೆಷುಲ್ಲಾಮ್, ಟಲ್ಮೋನ್, ಅಕ್ಕೂಬ್ ಎಂಬವರು.",
			"27": "<sup>27</sup> ಯೆರೂಸಲೇಮಿನ ಗೋಡೆಯನ್ನು ಪ್ರತಿಷ್ಠಿಸುವ ಸಮಯ ಬಂದಾಗ ಆಯಾ ಸ್ಥಳಗಳಲ್ಲಿದ್ದ ಲೇವಿಯರನ್ನು ಕೀರ್ತನಗಾಯನಗಳಿಂದಲೂ ತಾಳ ಸ್ವರಮಂಡಲ ಕಿನ್ನರಿ ಇವುಗಳಿಂದಲೂ ಪ್ರತಿಷ್ಠೆಯ ಉತ್ಸವವನ್ನು ಆಚರಿಸುವದಕ್ಕಾಗಿ ಯೆರೂಸಲೇಮಿಗೆ ಕರೆಯಿಸಿದರು.",
			"28": "<sup>28</sup> ಆಗ ಯೆರೂಸಲೇಮಿನ ಸುತ್ತಣ ಪ್ರಾಂತಗಳಲ್ಲಿ ತಮಗೋಸ್ಕರ ಹಳ್ಳಿಗಳನ್ನು ಕಟ್ಟಿಕೊಂಡಿದ್ದ ಗಾಯಕ ಮಂಡಲಿಯವರು ಯೊರ್ದನ್ ಹೊಳೆಯ ತಗ್ಗು, ಯೆರೂಸಲೇಮಿನ ಸುತ್ತಣಪ್ರದೇಶ, ನೆಟೋಫಾತ್ಯರ ಗ್ರಾಮ,",
			"29": "<sup>29</sup> ಬೇತ್ಹಗಿಲ್ಗಾಲ್, ಗೆಬ ಮತ್ತು ಅಜ್ಮಾವೇತ್ ಊರುಗಳಿಗೆ ಸೇರಿರುವ ಹಳ್ಳಿಗಳು ಇವುಗಳಿಂದ ಕೂಡಿ ಬಂದರು.",
			"30": "<sup>30</sup> ಯಾಜಕರೂ ಲೇವಿಯರೂ ತಮ್ಮನ್ನು ಶುದ್ಧಿಪಡಿಸಿಕೊಂಡ ಮೇಲೆ ಜನರನ್ನೂ ಊರುಬಾಗಲುಗಳನ್ನೂ ಗೋಡೆಯನ್ನೂ ಶುದ್ಧಪಡಿಸಿದರು.",
			"31": "<sup>31</sup> ಆಮೇಲೆ ನಾನು ಯೆಹೂದ ಪ್ರಭುಗಳನ್ನು ಗೋಡೆಯ ಮೇಲೆ ಕರಕೊಂಡು ಬಂದು ಭಜನ ಮಂಡಲಿಯವರಿಗೆ - ಎರಡು ದೊಡ್ಡ ಗುಂಪುಗಳಾಗಿ ಇಲ್ಲಿ ನಿಲ್ಲಬೇಕೆಂದು ಅಪ್ಪಣೆಮಾಡಿದೆನು. ಒಂದು ಗುಂಪಿನವರು ಮೆರವಣಿಗೆಯಿಂದ ಗೋಡೆಯ ಮೇಲೆ ತಿಪ್ಪೆಬಾಗಲಿನಿಂದ ಬಲಗಡೆಗೆ ಹೋದರು.",
			"32": "<sup>32</sup> ಅವರ ಹಿಂದೆ ಯೆಹೂದ ಪ್ರಭುಗಳ ಅರ್ಧ ಜನರೊಡನೆ ಹೋಷಾಯನೂ",
			"33": "<sup>33</sup> ತುತೂರಿಹಿಡಿದಿರುವ ಯಾಜಕರೊಡನೆ ಅಜರ್ಯ,",
			"34": "<sup>34</sup> ಎಜ್ರ, ಮೆಷುಲ್ಲಾಮ್, ಯೆಹೂದ, ಬೆನ್ಯಾಮೀನ್, ಶೆಮಾಯ, ಯೆರೆಮೀಯ ಎಂಬವರೂ",
			"35": "<sup>35</sup> ಆಸಾಫ್ ಸಂತಾನದ ಜಕ್ಕೂರನಿಗೆ ಹುಟ್ಟಿದ ಮೀಕಾಯನ ವಂಶದವನೂ ಮತ್ತನ್ಯನ ಮರಿಮಗನೂ ಶೆಮಾಯನ ಮೊಮ್ಮಗನೂ ಯೋನಾತಾನನ ಮಗನೂ ಆದ ಜೆಕರ್ಯನೂ",
			"36": "<sup>36</sup> ದೇವರ ಮನುಷ್ಯನಾದ ದಾವೀದನ ವಾದ್ಯಗಳನ್ನು ಹಿಡಿದಿರುವ ಇವನ ಬಂಧುಗಳಾದ ಶೆಮಾಯ, ಅಜರೇಲ್, ಮಿಲಲೈ, ಗಿಲಲೈ, ಮಾಯೈ, ನೆತನೇಲ್, ಯೆಹೂದ, ಹನಾನೀ ಎಂಬವರೂ ಹೋದರು. ಧರ್ಮೋಪದೇಶಕನಾದ ಎಜ್ರನು ಇವರ ಮುಂದಾಳು.",
			"37": "<sup>37</sup> ಇವರು ಬುಗ್ಗೆ ಬಾಗಲನ್ನು ಹಾದು ನೆಟ್ಟಗೆ ದಾವೀದನಗರದ ಸೋಪಾನಗಳ ಮಾರ್ಗವಾಗಿ ಗೋಡೆಯನ್ನನುಸರಿಸಿ ಅರಮನೆಯ ಮೇಲಣ ದಿನ್ನೆಯನ್ನು ಹತ್ತಿ ಪೂರ್ವದಿಕ್ಕಿನಲ್ಲಿರುವ ನೀರು ಬಾಗಲಿನವರೆಗೆ ಹೋದರು.",
			"38": "<sup>38</sup> ಅವರನ್ನು ಎದುರುಗೊಳ್ಳುವ ಹಾಗೆ ಭಜನಮಂಡಲಿಯ ಎರಡನೆಯ ಗುಂಪಿನವರೂ ನಾನೂ ಉಳಿದ ಅರ್ಧಜನರೂ ಗೋಡೆಯ ಮೇಲೆ ಒಲೆಬುರುಜಿನಿಂದ ಅಗಲಗೋಡೆಯವರೆಗೂ",
			"39": "<sup>39</sup> ಅಲ್ಲಿಂದ ಎಫ್ರಾಯೀಮ್ ಬಾಗಲು, ಯೆಷಾನಾ ಬಾಗಲು, ಮೀನು ಬಾಗಲು, ಹನನೇಲ್ ಬುರುಜು, ಹಮ್ಮೇಯಾ ಬುರುಜು ಇವುಗಳ ಮಾರ್ಗವಾಗಿ ಕುರಿಬಾಗಲಿನವರೆಗೂ ಹೋಗಿ ಸೆರೆಮನೆಯ ಬಾಗಲಿನ ಬಳಿಯಲ್ಲಿ ನಿಂತೆವು.",
			"40": "<sup>40</sup> ಎರಡು ಭಜನಮಂಡಲಿಯ ಗುಂಪಿನವರೂ ನಾನೂ ನನ್ನ ಜೊತೆಯಲ್ಲಿದ್ದ ಅರ್ಧಮಂದಿ ಅಧಿಕಾರಿಗಳೂ",
			"41": "<sup>41</sup> ತುತೂರಿ ಹಿಡಿದಿರುವ ಯಾಜಕರಾದ ಎಲ್ಯಾಕೀಮ್, ಮಾಸೇಯ, ಮಿನ್ಯಾಮೀನ್, ಮೀಕಾಯ, ಎಲ್ಯೋವೇನೈ, ಜೆಕರ್ಯ, ಹನನ್ಯ ಇವರೂ",
			"42": "<sup>42</sup> ಮಾಸೇಯ, ಶೆಮಾಯ, ಎಲ್ಲಾಜಾರ್, ಉಜ್ಜೀ, ಯೆಹೋಹಾನಾನ್, ಮಲ್ಕೀಯ, ಏಲಾಮ್, ಎಜೆರ್ ಎಂಬವರೂ ದೇವಾಲಯದ ಬಳಿಯಲ್ಲಿ ನಿಂತಿರಲು ಗಾಯಕರು ಹಾಡತೊಡಗಿದರು. ಇಜ್ರಹ್ಯನು ಇವರ ನಾಯಕನು.",
			"43": "<sup>43</sup> ದೇವರು ತಮಗೆ ವಿಶೇಷಾನಂದವನ್ನುಂಟುಮಾಡಿದ್ದರಿಂದ ಜನರು ಆ ದಿನದಲ್ಲಿ ಅನೇಕ ಯಜ್ಞಗಳನ್ನು ಸಮರ್ಪಿಸಿ ತಮ್ಮ ಹೆಂಡತಿ ಮಕ್ಕಳೊಡನೆ ಮಹೋತ್ಸವ ಮಾಡಿದರು. ಯೆರೂಸಲೇಮಿನ ಉತ್ಸವದ ಹರ್ಷಧ್ವನಿಯು ಬಹುದೂರದವರೆಗೂ ಕೇಳಿಸಿತು.",
			"44": "<sup>44</sup> ಸೇವೆಮಾಡುತ್ತಿದ್ದ ಯಾಜಕರ ಮತ್ತು ಲೇವಿಯರ ವಿಷಯದಲ್ಲಿ ಯೆಹೂದ್ಯರಿಗೆ ಬಹು ಸಂತೋಷವುಂಟಾದದರಿಂದ ಆ ದಿನದಲ್ಲಿ ಅವರು ಧರ್ಮವಿಧಿಯ ಪ್ರಕಾರ ಆಯಾ ಊರುಗಳ ಭೂಮಿಯಿಂದ ಯಾಜಕರಿಗೂ ಲೇವಿಯರಿಗೂ ಸಿಕ್ಕತಕ್ಕ ಭಾಗಗಳನ್ನು ಅಂದರೆ ದೇವರಿಗೋಸ್ಕರ ಪ್ರತ್ಯೇಕಿಸತಕ್ಕ ವಸ್ತು, ಪ್ರಥಮಫಲ, ದಶಮಾಂಶ ಇವುಗಳನ್ನು ಸಂಗ್ರಹಮಾಡತಕ್ಕ ಕೊಠಡಿಗಳ ಮೇಲೆ ಪಾರುಪತ್ಯಗಾರರನ್ನು ನೇಮಿಸಿದರು.",
			"45": "<sup>45</sup> ಆ ಯಾಜಕರೂ ಲೇವಿಯರೂ ತಮ್ಮ ದೇವರ ಸೇವಾಸಂಬಂಧದಲ್ಲಿಯೂ ಶುದ್ಧೀಕರಣಸಂಬಂಧದಲ್ಲಿಯೂ ತಮ್ಮ ಕರ್ತವ್ಯವನ್ನು ಜಾಗರೂಕತೆಯಿಂದ ನೆರವೇರಿಸುತ್ತಿದ್ದರು. ಅವರಂತೆ ಗಾಯಕರೂ ದ್ವಾರಪಾಲಕರೂ ಸಹ ದಾವೀದನಿಂದಲೂ ಅವನ ಮಗನಾದ ಸೊಲೊಮೋನನಿಂದಲೂ ಸ್ಥಾಪಿತವಾದ ಕ್ರಮಾನುಸಾರ ನಡೆಯುತ್ತಿದ್ದರು.",
			"46": "<sup>46</sup> ಗಾಯನ ಮಂಡಲಿಗಳೂ ಕೃತಜ್ಞತಾಸ್ತುತಿಕೀರ್ತನೆಗಳ ದೇವಸೇವೆಯೂ ಪೂರ್ವಕಾಲದಲ್ಲಿ ಅಂದರೆ ದಾವೀದನ ಮತ್ತು ಆಸಾಫನ ಕಾಲದಲ್ಲಿ ಇದ್ದವಷ್ಟೆ.",
			"47": "<sup>47</sup> ಜೆರುಬ್ಬಾಬೆಲ್, ನೆಹೆಮೀಯ ಇವರ ಕಾಲಗಳಲ್ಲಿ ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರು ಗಾಯಕರಿಗೂ ದ್ವಾರಪಾಲಕರಿಗೂ ದಿನದಿನಕ್ಕೆ ಸಿಕ್ಕತಕ್ಕ ಭಾಗಗಳನ್ನು ಕೊಡುತ್ತಿದ್ದರು. ಇವರು ಅವುಗಳಲ್ಲಿ ದಶಮಾಂಶವನ್ನು ಲೇವಿಯರಿಗೂ ಲೇವಿಯರು ತಮ್ಮದರಲ್ಲಿ ದಶಮಾಂಶವನ್ನು ಆರೋನನ ಮಕ್ಕಳಿಗೂ ಕೊಡುತ್ತಿದ್ದರು."
		},
		"13": {
			"1": "<sup>1</sup> ಆ ಕಾಲದಲ್ಲಿ ಜನರ ಮುಂದೆ ಮೋಶೆಯ ಧರ್ಮಶಾಸ್ತ್ರಪಾರಾಯಣವು ನಡೆಯುತ್ತಿರಲಾಗಿ ಅದರಲ್ಲಿ - ಅಮ್ಮೋನಿಯರಾಗಲಿ ಮೋವಾಬ್ಯರಾಗಲಿ ದೇವರ ಸಭೆಯಲ್ಲಿ ಎಂದಿಗೂ ಸೇರಬಾರದು;",
			"2": "<sup>2</sup> ಯಾಕಂದರೆ ಅವರು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಅನ್ನಪಾನಗಳೊಡನೆ ಎದುರುಗೊಳ್ಳಲಿಲ್ಲ; ಅವರನ್ನು ಶಪಿಸುವದಕ್ಕಾಗಿ ಬಿಳಾಮನಿಗೆ ಹಣಕೊಟ್ಟು ಅವನನ್ನು ಕರಿಸಿದರು. ನಮ್ಮ ದೇವರಾದರೋ ಅವನಿಂದ ಶಾಪವನ್ನಲ್ಲ, ಅಶೀರ್ವಾದವನ್ನೇ ಹೇಳಿಸಿದನು ಎಂಬ ಮಾತು ಬರೆದಿರುವದಾಗಿ ಕಂಡು ಬಂತು.",
			"3": "<sup>3</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಈ ಧರ್ಮವಿಧಿಯನ್ನು ಕೇಳಿದೊಡನೆ ಎಲ್ಲಾ ಮಿಶ್ರಜಾತಿಯವರನ್ನು ತಮ್ಮ ಮಧ್ಯದಿಂದ ಬೇರ್ಪಡಿಸಿದರು.",
			"4": "<sup>4</sup> ಇದಕ್ಕಿಂತ ಮೊದಲು ಯಾಜಕನಾದ ಎಲ್ಯಾಷೀಬನು ನಮ್ಮ ದೇವಾಲಯದ ಕೊಠಡಿಗಳ ಮೇಲ್ವಿಚಾರಕನಾಗಿ ನೇಮಿಸಲ್ಪಟ್ಟಿದ್ದನು.",
			"5": "<sup>5</sup> ಇವನು ಟೋಬೀಯನ ಬೀಗನಾಗಿದ್ದದರಿಂದ ನೈವೇದ್ಯದ್ರವ್ಯ ಧೂಪ ಪಾತ್ರೆ ಇವುಗಳನ್ನೂ ಧಾನ್ಯ ದ್ರಾಕ್ಷಾರಸ ಎಣ್ಣೆ ಇವುಗಳ ದಶಮಾಂಶವನ್ನೂ ಲೇವಿಯ ಗಾಯಕ ದ್ವಾರಪಾಲಕ ಇವರಿಗೆ ಸಲ್ಲತಕ್ಕ ಪದಾರ್ಥಗಳನ್ನೂ ಯಾಜಕರಿಗೋಸ್ಕರ ಪ್ರತ್ಯೇಕಿಸತಕ್ಕ ಪದಾರ್ಥಗಳನ್ನೂ ಇಡುವದಕ್ಕಾಗಿ ಉಪಯೋಗವಾಗುತ್ತಿದ್ದ ಕೊಠಡಿಯನ್ನು ಅವನಿಗೋಸ್ಕರ ಸಿದ್ಧಮಾಡಿಸಿ ಕೊಟ್ಟಿದ್ದನು.",
			"6": "<sup>6</sup> ಬಾಬೆಲಿನ ಅರಸನಾದ ಅರ್ತಷಸ್ತನ ಮೂವತ್ತೆರಡನೆಯ ವರುಷದಲ್ಲಿ ಅರಸನ ಬಳಿಗೆ ಹೋಗಿದ್ದೆನಾದದರಿಂದ ಇದೆಲ್ಲಾ ನಡೆಯುವಾಗ ನಾನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿರಲಿಲ್ಲ. ಕಾಲಾಂತರದಲ್ಲಿ ನಾನು ಅರಸನಿಂದ ಅಪ್ಪಣೆಪಡಕೊಂಡು",
			"7": "<sup>7</sup> ತಿರಿಗಿ ಯೆರೂಸಲೇಮಿಗೆ ಬಂದಾಗ ಎಲ್ಯಾಷೀಬನು ಟೋಬೀಯನಿಗೋಸ್ಕರ ದೇವಾಲಯ ಪ್ರಾಕಾರದೊಳಗೆ ಕೊಠಡಿಯನ್ನು ಸಿದ್ಧಮಾಡಿಕೊಟ್ಟಿದ್ದನ್ನು ನೋಡಿ",
			"8": "<sup>8</sup> ಈ ದುಷ್ಕೃತ್ಯದ ದೆಸೆಯಿಂದ ಬಹಳವಾಗಿ ವ್ಯಸನಗೊಂಡು ಟೋಬೀಯನ ಮನೆಯ ಸಾಮಾನುಗಳನ್ನು ಆ ಕೊಠಡಿಯಿಂದ ಹೊರಗೆ ಹಾಕಿಸಿ",
			"9": "<sup>9</sup> ಕೊಠಡಿಯನ್ನು ಶುದ್ಧಿಮಾಡುವದಕ್ಕೆ ಅಪ್ಪಣೆಕೊಟ್ಟು ಅದು ಶುದ್ಧವಾದ ಮೇಲೆ ದೇವಾಲಯದ ಪಾತ್ರೆ, ನೈವೇದ್ಯ ದ್ರವ್ಯ, ಧೂಪ ಇವುಗಳನ್ನು ತಿರಿಗಿ ಅಲ್ಲಿಡಿಸಿದೆನು.",
			"10": "<sup>10</sup> ಇದಲ್ಲದೆ ಲೇವಿಯರಿಗೆ ಸಲ್ಲತಕ್ಕ ಭಾಗಗಳು ಅವರಿಗೆ ಸಿಕ್ಕಲಿಲ್ಲವೆಂದೂ ಆರಾಧನೆ ನಡಿಸತಕ್ಕ ಲೇವಿಯರೂ ಗಾಯಕರೂ ತಮ್ಮ ತಮ್ಮ ಭೂಸ್ವಾಸ್ತ್ಯಗಳಿಗೆ ಹೋಗಿಬಿಟ್ಟರೆಂದೂ ನನಗೆ ಗೊತ್ತಾಯಿತು.",
			"11": "<sup>11</sup> ಇದರ ದೆಸೆಯಿಂದ ನಾನು ಅಧಿಕಾರಿಗಳೊಡನೆ - ದೇವಾಲಯವನ್ನು ಅಲಕ್ಷ್ಯಮಾಡಿದ್ದೇಕೆ ಎಂಬದಾಗಿ ವಾದಿಸಿ ಬಿಟ್ಟುಹೋದವರನ್ನು ತಿರಿಗಿ ಕರಿಸಿ ಅವರನ್ನು ಅವರವರ ಉದ್ಯೋಗಗಳಲ್ಲಿರಿಸಿದೆನು.",
			"12": "<sup>12</sup> ಆಗ ಎಲ್ಲಾ ಯೆಹೂದ್ಯರು ತಮ್ಮ ತಮ್ಮ ಧಾನ್ಯ ದ್ರಾಕ್ಷಾರಸ ಎಣ್ಣೆ ಇವುಗಳಲ್ಲಿ ದಶಮಾಂಶವನ್ನು ತಂದು ಭಂಡಾರಗಳಲ್ಲಿ ಹಾಕಿದರು.",
			"13": "<sup>13</sup> ಆಮೇಲೆ ಉಗ್ರಾಣಗಳನ್ನು ನೋಡುವದಕ್ಕೋಸ್ಕರ ಯಾಜಕನಾದ ಶೆಲೆಮ್ಯ, ಲೇಖಕನಾದ ಚಾದೋಕ್, ಲೇವಿಕುಲದವನಾದ ಪೆದಾಯ ಇವರನ್ನೂ ಇವರ ಸಹಾಯಕ್ಕಾಗಿ ಜಕ್ಕೂರನ ಮಗನೂ ಮತ್ತನ್ಯನ ಮೊಮ್ಮಗನೂ ಆದ ಹಾನಾನನನ್ನೂ ನೇಮಿಸಿದೆನು; ಇವರೆಲ್ಲರೂ ನಂಬಿಗಸ್ತರೆಂದು ಹೆಸರುಗೊಂಡವರು. ತಮ್ಮ ಸಹೋದರರಿಗೆ ಸಿಕ್ಕತಕ್ಕದ್ದನ್ನು ಹಂಚಿಕೊಡುವದು ಇವರ ಕರ್ತವ್ಯ.",
			"14": "<sup>14</sup> ನನ್ನ ದೇವರೇ, ಇದನ್ನು ನನ್ನ ಹಿತಕ್ಕಾಗಿ ನೆನಪುಮಾಡಿಕೋ. ನಾನು ನನ್ನ ದೇವರ ಆಲಯದ ಮತ್ತು ಅದರ ಸೇವೆಯ ಸಂಬಂಧವಾಗಿ ಮಾಡಿದ ಭಕ್ತಿಕಾರ್ಯಗಳನ್ನು [ನಿನ್ನ ಪುಸ್ತಕದಿಂದ] ಅಳಿಸಿಬಿಡಬೇಡ.",
			"15": "<sup>15</sup> ಆ ಕಾಲದಲ್ಲಿ ಜನರು ಸಬ್ಬತ್‍ದಿವಸ ಯೆಹೂದ ದೇಶದಲ್ಲಿ ತೊಟ್ಟಿಯೊಳಗೆ ದ್ರಾಕ್ಷೆತುಳಿಯುವದನ್ನೂ ಕಣದ ಕಾಳನ್ನು ಕೂಡಿಸಿ ಆ ಕಾಳು ದ್ರಾಕ್ಷಾರಸ ದ್ರಾಕ್ಷೆ ಅಂಜೂರದ ಹಣ್ಣು ಈ ಮುಂತಾದವುಗಳನ್ನು ಕತ್ತೆಗಳ ಮೇಲೆ ಹೇರಿಕೊಂಡು ಆ ದಿನದಲ್ಲೇ ಯೆರೂಸಲೇಮಿಗೆ ತರುವದನ್ನೂ ಕಂಡು ಅವರು ಆ ಆಹಾರಪದಾರ್ಥಗಳನ್ನು ಮಾರುವದಕ್ಕೆ ಬಂದಾಗ ಅವರನ್ನು ಗದರಿಸಿದೆನು.",
			"16": "<sup>16</sup> ಇದಲ್ಲದೆ ದೇಶದಲ್ಲಿ ವಾಸಿಸುತ್ತಿದ್ದ ತೂರ್ಯರೂ ಮೀನು ಮುಂತಾದ ಸರಕುಗಳನ್ನು ತಂದು ಯೆಹೂದ್ಯರಿಗೆ ಸಬ್ಬತ್‍ದಿನದಲ್ಲಿ ಯೆರೂಸಲೇಮಿನಲ್ಲೇ ಮಾರುತ್ತಿದ್ದರು.",
			"17": "<sup>17</sup> ಆಗ ನಾನು ಯೆಹೂದ ಶ್ರೀಮಂತರಿಗೆ - ನೀವು ಮಾಡುವದು ಎಂಥ ದುಷ್ಕೃತ್ಯ! ನೀವು ಸಬ್ಬತ್‍ದಿನವನ್ನು ಅಶುದ್ಧಮಾಡುತ್ತೀರಿ.",
			"18": "<sup>18</sup> ನಿಮ್ಮ ಪೂರ್ವಿಕರು ಹೀಗೆ ಮಾಡಿದ್ದರಿಂದ ನಮ್ಮ ದೇವರು ಈ ಎಲ್ಲಾ ಕೇಡನ್ನು ನಮ್ಮ ಮೇಲೆಯೂ ನಮ್ಮ ಪಟ್ಟಣದ ಮೇಲೆಯೂ ಬರಮಾಡಿದನಲ್ಲವೇ. ಹೀಗಿರಲಾಗಿ ನೀವೂ ಸಬ್ಬತ್ ದಿನವನ್ನು ಅಶುದ್ಧಮಾಡಿ ಇಸ್ರಾಯೇಲ್ಯರ ಮೇಲಿದ್ದ ದೇವಕೋಪವನ್ನು ಹೆಚ್ಚಿಸುತ್ತೀರೇನು ಎಂದು ಹೇಳಿ ಅವರನ್ನು ಗದರಿಸಿದೆನು.",
			"19": "<sup>19</sup> ಇದಲ್ಲದೆ ಸಬ್ಬತ್‍ದಿನ ಪ್ರಾರಂಭವಾಗುವದಕ್ಕಿಂತ ಮೊದಲು ಯೆರೂಸಲೇಮಿನ ಬಾಗಲುಗಳಲ್ಲಿ ಕತ್ತಲಾಗಿರುವಾಗಲೇ ಕದಗಳನ್ನು ಮುಚ್ಚಿಸಿ ಸಬ್ಬತ್ ದಿನವು ಗತಿಸುವದಕ್ಕಿಂತ ಮುಂಚೆ ಅವುಗಳನ್ನು ತೆರೆಯಬಾರದೆಂದು ಆಜ್ಞಾಪಿಸಿ ಯಾವ ಹೊರೆಯೂ ಒಳಗೆ ಬಾರದಂತೆ ನನ್ನ ಸೇವಕರಲ್ಲಿ ಕೆಲವರನ್ನು ಬಾಗಲುಗಳ ಬಳಿಯಲ್ಲಿ ಕಾವಲಿರಿಸಿದೆನು.",
			"20": "<sup>20</sup> ಆದದರಿಂದ ವರ್ತಕರೂ ಎಲ್ಲಾ ತರದ ದಿನಸುಗಳನ್ನು ಮಾರುವದಕ್ಕೆ ಬಂದವರೂ ಒಂದೆರಡು ಸಾರಿ ಯೆರೂಸಲೇಮಿನ ಹೊರಗೆ ಇಳುಕೊಂಡರು.",
			"21": "<sup>21</sup> ಕಡೆಯಲ್ಲಿ ನಾನು ಅವರನ್ನು - ನೀವು ಗೋಡೆಯ ಹೊರಗೇಕೆ ಇಳುಕೊಂಡಿದ್ದೀರಿ? ನೀವು ತಿರಿಗಿ ಹೀಗೆ ಮಾಡಿದರೆ ನಿಮ್ಮನ್ನು ಹಿಡಿಸುವೆನು ಎಂದು ಗದರಿಸಲು ಅವರು ಅಂದಿನಿಂದ ಸಬ್ಬತ್‍ದಿನದಲ್ಲಿ ತಿರಿಗಿ ಬರಲಿಲ್ಲ.",
			"22": "<sup>22</sup> ಆಮೇಲೆ ನಾನು ಲೇವಿಯರಿಗೆ - ಸಬ್ಬತ್‍ದಿನವನ್ನು ಪರಿಶುದ್ಧದಿನವೆಂದು ಆಚರಿಸುವ ಹಾಗೆ ನೀವು ನಿಮ್ಮನ್ನು ಶುದ್ಧಿಪಡಿಸಿಕೊಂಡು ಬಂದು ಬಾಗಲುಗಳನ್ನು ಕಾಯಬೇಕು ಎಂದು ಆಜ್ಞಾಪಿಸಿದೆನು. ನನ್ನ ದೇವರೇ ಇದನ್ನೂ ನನ್ನ ಹಿತಕ್ಕಾಗಿ ನೆನಪುಮಾಡಿಕೊಂಡು ನಿನ್ನ ಮಹಾಕೃಪೆಗನುಸಾರವಾಗಿ ನನ್ನನ್ನು ಕನಿಕರಿಸು.",
			"23": "<sup>23</sup> ಅದೇ ಕಾಲದಲ್ಲಿ ನಾನು ಅಷ್ಡೋದ್ ಅಮ್ಮೋನಿಯ ಮೋವಾಬ್ ಸ್ತ್ರೀಯರನ್ನು ಮದುವೆಮಾಡಿಕೊಂಡ ಯೆಹೂದ್ಯರನ್ನು ಕುರಿತು ವಿಚಾರಮಾಡಿ",
			"24": "<sup>24</sup> ಅವರ ಮಕ್ಕಳಲ್ಲಿ ಅರ್ಧಮಂದಿ ಯೂದಾಯ ಭಾಷೆಯಲ್ಲಿ ಮಾತಾಡಲರಿಯದೆ ಅಷ್ಡೋದ್ಯ ಮುಂತಾದ ಜನರ ಭಾಷೆಯನ್ನಾಡುತ್ತಾರೆಂದು ತಿಳಿಯಲು",
			"25": "<sup>25</sup> ಕೋಪಗೊಂಡು ಅವರನ್ನು ಶಪಿಸಿ ಅವರಲ್ಲಿ ಕೆಲವರನ್ನು ಹೊಡೆದು ಅವರ ಕೂದಲುಗಳನ್ನು ಕಿತ್ತು -",
			"26": "<sup>26</sup> ಅಂಥ ಸ್ತ್ರೀಯರ ದೆಸೆಯಿಂದ ಇಸ್ರಾಯೇಲ್‍ರಾಜನಾದ ಸೊಲೊಮೋನನು ದೋಷಿಯಾದನಲ್ಲವೇ. ಅನೇಕಾನೇಕ ಜನಾಂಗಗಳಲ್ಲಿ ಅವನಿಗೆ ಸಮಾನನಾದ ಅರಸನು ಇರಲಿಲ್ಲ; ಅವನು ತನ್ನ ದೇವರಿಗೆ ವಿಶೇಷ ಪ್ರಿಯನು; ಆದದರಿಂದ ದೇವರು ಅವನನ್ನು ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರ ಮೇಲೆ ಅರಸನನ್ನಾಗಿ ನೇಮಿಸಿದನು. ಆದರೂ ಅನ್ಯದೇಶಸ್ತ್ರೀಯರು ಅವನನ್ನು ಪಾಪದಲ್ಲಿ ಬೀಳಿಸಿದರು.",
			"27": "<sup>27</sup> ಹೀಗಿರಲಾಗಿ ನೀವು ಅನ್ಯಸ್ತ್ರೀಯರನ್ನು ಮದುವೆಮಾಡಿಕೊಂಡು ಅದೇ ಘೋರವಾದ ದುಷ್ಟತ್ವವನ್ನು ನಡಿಸಿ ದೇವರಿಗೆ ದ್ರೋಹಿಗಳಾಗುವದನ್ನು ನಾವು ಒಪ್ಪಿಕೊಳ್ಳಬೇಕೋ ಎಂದು ಅವರನ್ನು ಗದರಿಸಿ ಅವರಿಂದ - ಅನ್ಯರ ಮಕ್ಕಳಿಗೆ ಹೆಣ್ಣುಕೊಡಬಾರದು, ನಿಮಗಾಗಲಿ ನಿಮ್ಮ ಮಕ್ಕಳಿಗಾಗಲಿ ಅವರಿಂದ ತರಲೂಬಾರದು ಎಂಬ ಧರ್ಮವಿಧಿಯನ್ನು ಕೈಕೊಳ್ಳುವದಾಗಿ ದೇವರ ಹೆಸರಿನಲ್ಲಿ ಪ್ರಮಾಣಮಾಡಿಸಿದೆನು.",
			"28": "<sup>28</sup> ಮಹಾಯಾಜಕ ಎಲ್ಯಾಷೀಬನ ಮಗನಾದ ಯೋಯಾದನ ಮಕ್ಕಳಲ್ಲೊಬ್ಬನು ಹೊರೋನ್ಯನಾದ ಸನ್ಬಲ್ಲಟನಿಗೆ ಅಳಿಯನಾದದರಿಂದ ಅವನನ್ನು ನನ್ನ ಸನ್ನಿಧಿಯಿಂದ ಓಡಿಸಿಬಿಟ್ಟೆನು.",
			"29": "<sup>29</sup> ನನ್ನ ದೇವರೇ, ಅವರು ಯಾಜಕತ್ವವನ್ನೂ ಯಾಜಕರ ಮತ್ತು ಲೇವಿಯರ ಪ್ರತಿಜ್ಞೆಯನ್ನೂ ಹೊಲೆಮಾಡಿದ್ದಾರಲ್ಲಾ; ಇದನ್ನು ನೆನಪು ಮಾಡಿಕೋ.",
			"30": "<sup>30</sup> ನಾನು ಈ ಪ್ರಕಾರ ಅನ್ಯವಾದದ್ದನ್ನೆಲ್ಲಾ ತೆಗಿಸಿ ಅವರನ್ನು ಶುದ್ಧಪಡಿಸಿದೆನಲ್ಲದೆ ಯಾಜಕರಲ್ಲಿಯೂ ಲೇವಿಯರಲ್ಲಿಯೂ ಪ್ರತಿಯೊಬ್ಬನಿಗಿರುವ ಕೆಲಸದ ವಿಷಯವಾಗಿಯೂ",
			"31": "<sup>31</sup> ನೇಮಿತ ಕಾಲಗಳಲ್ಲಿ ಸಲ್ಲತಕ್ಕ ಕಾಷ್ಠದಾನದ ಮತ್ತು ಪ್ರಥಮ ಫಲದ ವಿಷಯವಾಗಿಯೂ ಕ್ರಮಗಳನ್ನು ಏರ್ಪಡಿಸಿದೆನು. ನನ್ನ ದೇವರೇ, ನನ್ನ ಹಿತಕ್ಕಾಗಿ ಇದನ್ನು ನೆನಪು ಮಾಡಿಕೋ."
		}
	}
};
module.exports = book;