var book = {
	"name": "ಎಸ್ತೇರಳು",
	"numChapters": 10,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ಹಿಂದುಸ್ಥಾನ ಮೊದಲುಗೊಂಡು ಕೂಷಿನವರೆಗೂ ಇರುವ ನೂರಿಪ್ಪತ್ತೇಳು ಸಂಸ್ಥಾನಗಳನ್ನು ಆಳುತ್ತಿದ್ದ ಅಹಷ್ವೇರೋಷನ ಕಾಲದಲ್ಲಿ ನಡೆದ ಚರಿತ್ರೆ.",
			"2": "<sup>2</sup> ಅಹಷ್ವೇರೋಷನು ತನ್ನ ಆಳಿಕೆಯ ಮೂರನೆಯ ವರುಷ ಶೂಷನ್ ಕೋಟೆಯಲ್ಲಿನ ತನ್ನ ರಾಜಸಿಂಹಾಸನವನ್ನೇರಿದಾಗ",
			"3": "<sup>3</sup> ಎಲ್ಲಾ ಸರದಾರರಿಗೋಸ್ಕರವೂ ಪರಿವಾರದವರಿಗೋಸ್ಕರವೂ ಔತಣಮಾಡಿಸಿದನು. ಪಾರಸಿಯ ಮತ್ತು ಮೇದ್ಯ ಸೇನಾಧಿಪತಿಗಳೂ ಪ್ರಧಾನರೂ ಸಂಸ್ಥಾನಾಧಿಕಾರಿಗಳೂ ಅವನ ಸನ್ನಿಧಿಗೆ ಬಂದಿದ್ದರು.",
			"4": "<sup>4</sup> ಅವನು ಅನೇಕ ದಿವಸ ಅಂದರೆ ನೂರೆಂಭತ್ತು ದಿವಸಗಳವರೆಗೂ ಅವರಿಗೆ ತನ್ನ ಘನವಾದ ರಾಜ್ಯದ ಐಶ್ವರ್ಯವನ್ನೂ ಮಹಾಮಹಿಮೆಯ ವೈಭವಗಳನ್ನೂ ತೋರಿಸಿದನು.",
			"5": "<sup>5</sup> ಈ ದಿನಗಳಾದನಂತರ ಅರಸನು ಶೂಷನ್ ಕೋಟೆಯಲ್ಲಿದ್ದ ಎಲ್ಲಾ ಶ್ರೇಷ್ಠರಿಗೂ ಕನಿಷ್ಠರಿಗೂ ಅರಮನೆಯ ತೋಟದ ಬೈಲಿನಲ್ಲಿ ಏಳು ದಿನಗಳವರೆಗೂ ಔತಣಮಾಡಿಸಿದನು.",
			"6": "<sup>6</sup> ಅಲ್ಲಿ ಬಿಳೀ ನೂಲಿನ ಬಟ್ಟೆಗಳೂ ನೀಲಿಬಟ್ಟೆಗಳೂ ಧೂಮ್ರವರ್ಣವುಳ್ಳ ನಾರಿನ ದಾರದಿಂದ ಸಂಗಮೀರ ಕಲ್ಲು ಕಂಬಗಳಲ್ಲಿನ ಬೆಳ್ಳಿಯ ಉಂಗುರಗಳಿಗೆ ಕಟ್ಟಲ್ಪಟ್ಟಿದ್ದವು. ಜರತಾರೆಯ ಕಸೂತಿ ಹಾಕಿರುವ ಲೋಡುಗಳು ಕೆಂಪು ಬಿಳಿ ಹಳದಿ ಕಪ್ಪು ಬಣ್ಣಗಳುಳ್ಳ ಸಂಗಮೀರ ಕಲ್ಲುಗಳಿಂದ ರಚಿತವಾದ ನೆಲಗಟ್ಟಿನ ಮೇಲೆ ಇಡಲ್ಪಟ್ಟಿದ್ದವು.",
			"7": "<sup>7</sup> ಪಾನಪಾತ್ರೆಗಳು ಬಂಗಾರದವುಗಳೂ ನಾನಾ ಆಕಾರದವುಗಳೂ ಆಗಿದ್ದವು. ರಾಜದ್ರಾಕ್ಷಾರಸವು ಅರಸರ ಔದಾರ್ಯಕ್ಕೆ ತಕ್ಕಂತೆ ಧಾರಾಳವಾಗಿತ್ತು.",
			"8": "<sup>8</sup> ಪಾನಮಾಡುವದರಲ್ಲಿ ಯಾರಿಗೂ ಒತ್ತಾಯಮಾಡಬಾರದೆಂದು ಕ್ರಮವಿತ್ತು. ಪ್ರತಿಯೊಬ್ಬನಿಗೆ ಅವನವನ ಇಷ್ಟದಂತೆ ಕೊಡಬೇಕೆಂದು ರಾಜನು ತನ್ನ ಎಲ್ಲಾ ಮನೆವಾರ್ತೆಯವರಿಗೆ ಆಜ್ಞಾಪಿಸಿದ್ದನು.",
			"9": "<sup>9</sup> ವಷ್ಟಿರಾಣಿಯೂ ಅಹಷ್ವೇರೋಷ್‍ರಾಜನ ಅರಮನೆಯಲ್ಲಿ ಸ್ತ್ರೀಯರಿಗೋಸ್ಕರ ಔತಣ ಮಾಡಿಸಿದಳು.",
			"10": "<sup>10</sup> ಏಳನೆಯ ದಿನದಲ್ಲಿ ಅರಸನಾದ ಅಹಷ್ವೇರೋಷನು ದ್ರಾಕ್ಷಾರಸಪಾನಮಾಡಿ ಆನಂದಲಹರಿಯಲ್ಲಿದ್ದಾಗ",
			"11": "<sup>11</sup> ಬಹು ಸುಂದರಿಯಾದ ರಾಣಿಯ ಸೌಂದರ್ಯವನ್ನು ಜನರಿಗೂ ಸರದಾರರಿಗೂ ತೋರಿಸಬೇಕೆಂದು ತನ್ನ ಸಾನ್ನಿಧ್ಯಸೇವಕರಾದ ಮೆಹೂಮಾನ್, ಬಿಜೆತಾ, ಹರ್ಬೋನಾ, ಬಿಗೆತಾ, ಅಬಗೆತಾ, ಜೇತರ್, ಕರ್ಕಸ್ ಎಂಬ ಏಳು ಮಂದಿ ಕಂಚುಕಿಗಳಿಗೆ - ವಷ್ಟಿರಾಣಿಯು ರಾಜ ಮುಕುಟವನ್ನು ಧರಿಸಿಕೊಂಡು ರಾಜಸನ್ನಿಧಿಗೆ ಬರಬೇಕೆಂಬದಾಗಿ ಹೇಳಿರಿ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"12": "<sup>12</sup> ಕಂಚುಕಿಗಳು ತಿಳಿಸಿದ ರಾಜಾಜ್ಞೆಗೆ ವಷ್ಟಿರಾಣಿಯು - ಬರುವದಿಲ್ಲ ಎಂದು ಉತ್ತರಕೊಟ್ಟಳು. ಇದರಿಂದ ಅರಸನು ಬಹುಕೋಪಗೊಂಡು ರೌದ್ರಾವೇಶನಾದನು.",
			"13": "<sup>13</sup> ವಿಧಿನ್ಯಾಯಗಳನ್ನು ಬಲ್ಲವರೆಲ್ಲರ ಮುಂದೆ ಅರಮನೆಯ ಸಂಗತಿಗಳನ್ನು ಇಡುವ ಪದ್ಧತಿಯಿತ್ತು.",
			"14": "<sup>14</sup> ಅರಸನಾದ ಅಹಷ್ವೇರೋಷನ ಒಡ್ಡೋಲಗದಲ್ಲಿ ರಾಜಸಾನಿಧ್ಯಸೇವಕರೂ ರಾಜಸಭಾಪ್ರಧಾನರೂ ಆಗಿದ್ದ ಕರ್ಷೆನಾ, ಶೇತಾರ್, ಅದ್ಮಾತಾ, ತರ್ಷೀಷ್, ಮೆರೆಸ್, ಮರ್ಸೆನಾ, ಮೆಮೂಕಾನ್ ಎಂಬ ಏಳು ಮಂದಿ ಪಾರಸಿಯ ಮತ್ತು ಮೇದ್ಯ ಪ್ರಭುಗಳಿದ್ದರು. ಅವನು ಸಮಯೋಚಿತಜ್ಞಾನವುಳ್ಳವರಾದ ಇವರನ್ನು -",
			"15": "<sup>15</sup> ಕಂಚುಕಿಗಳ ಮುಖಾಂತರ ತನಗುಂಟಾದ ರಾಜಾಜ್ಞೆಯನ್ನು ಕೈಕೊಳ್ಳದೆಹೋದ ವಷ್ಟಿರಾಣಿಗೆ ನಿಯಮದ ಪ್ರಕಾರ ಮಾಡತಕ್ಕದ್ದೇನು ಎಂದು ಕೇಳಲು ಮೆಮೂಕಾನನು ಅರಸನ ಮತ್ತು ಸರದಾರರ ಮುಂದೆ -",
			"16": "<sup>16</sup> ವಷ್ಟಿರಾಣಿಯ ಅಪರಾಧವು ಅರಸನೊಬ್ಬನಿಗೇ ವಿರುದ್ಧವಾದದ್ದಲ್ಲ; ಅರಸನಾದ ಅಹಷ್ವೇರೋಷನಿಗೆ ಸೇರಿರುವ ಎಲ್ಲಾ ಸಂಸ್ಥಾನಗಳ ಸರದಾರರಿಗೂ ಜನರಿಗೂ ವಿರುದ್ಧವಾದದ್ದು.",
			"17": "<sup>17</sup> ರಾಣಿಯ ನಡತೆಯು ಎಲ್ಲಾ ಸ್ತ್ರೀಯರಿಗೂ ಗೊತ್ತಾಗಿ ಅವರೂ ತಮ್ಮ ಗಂಡಂದಿರನ್ನು ತಿರಸ್ಕರಿಸುವದಕ್ಕೆ ಕಾರಣವಾಗುವದು. ಅರಸನಾದ ಅಹಷ್ವೇರೋಷನು ವಷ್ಟಿ ರಾಣಿಗೆ ಬರಬೇಕೆಂದು ಹೇಳಿಕಳುಹಿಸಿದಾಗ ಆಕೆಯು ಹೋಗಲಿಲ್ಲವಲ್ಲಾ ಅನ್ನುವರು.",
			"18": "<sup>18</sup> ರಾಣಿಯ ಮಾತನ್ನು ಕೇಳಿದ ಪಾರಸಿಯ ಮತ್ತು ಮೇದ್ಯ ಕುಲೀನಸ್ತ್ರೀಯರು ಈ ಹೊತ್ತೇ ಅದನ್ನು ಅರಸನ ಎಲ್ಲಾ ಸರದಾರರಿಗೂ ಹೇಳುವರು. ಹೀಗೆ ಎಷ್ಟೋ ತಿರಸ್ಕಾರವೂ ಕೋಪವೂ ಉಂಟಾಗುವದು.",
			"19": "<sup>19</sup> ಅರಸನು ಸಮ್ಮತಿಸುವದಾದರೆ ವಷ್ಟಿಯು ತಿರಿಗಿ ಅಹಷ್ವೇರೋಷ್‍ರಾಜನ ಸನ್ನಿಧಿಗೆ ಬರಲೇಬಾರದೆಂಬ ರಾಜಾಜ್ಞೆಯು ಪ್ರಕಟವಾಗಿ ಅದು ಎಂದಿಗೂ ರದ್ದಾಗದ ಹಾಗೆ ಪಾರಸಿಯ ಮತ್ತು ಮೇದ್ಯಶಾಸನಗಳಲ್ಲಿ ಲಿಖಿತವಾಗಲಿ; ಅರಸನು ಆಕೆಯ ಪಟ್ಟವನ್ನು ಆಕೆಗಿಂತ ಉತ್ತಮಳಾದ ಇನ್ನೊಬ್ಬಾಕೆಗೆ ಕೊಡಲಿ.",
			"20": "<sup>20</sup> ಈ ರಾಜನಿರ್ಣಯವು ಬಹು ವಿಸ್ತಾರವಾದ ಅವನ ರಾಜ್ಯದಲ್ಲೆಲ್ಲಾ ಗೊತ್ತಾದಾಗ ಶ್ರೇಷ್ಠರೂ ಕನಿಷ್ಠರೂ ಆದ ಎಲ್ಲಾ ಸ್ತ್ರೀಯರು ತಮ್ಮ ಗಂಡಂದಿರಿಗೆ ಮಾನ ಸಲ್ಲಿಸುವರು ಅಂದನು.",
			"21": "<sup>21</sup> ಈ ಮಾತು ಅರಸನಿಗೂ ಅವನ ಸರದಾರರಿಗೂ - ಒಳ್ಳೇದೆಂದು ಕಂಡದರಿಂದ ಅರಸನು ಮೆಮೂಕಾನನ ಮಾತಿನಂತೆಯೇ ಮಾಡಿದನು.",
			"22": "<sup>22</sup> ಅವನು - ಪ್ರತಿಯೊಂದು ಕುಟುಂಬದಲ್ಲಿ ಪುರುಷನೇ ಒಡೆಯನಾಗಿದ್ದು ಅವನ ಸ್ವಜನರ ಭಾಷೆಯೇ ಉಪಯೋಗವಾಗಬೇಕೆಂಬದಾಗಿ ಎಲ್ಲಾ ರಾಜಸಂಸ್ಥಾನಗಳಲ್ಲಿ ಪತ್ರಗಳ ಮೂಲಕ ಪ್ರಕಟಿಸಿದನು. ಈ ಪತ್ರಗಳು ಆಯಾ ಸಂಸ್ಥಾನಗಳ ಬರಹಗಳಲ್ಲಿಯೂ ಆಯಾ ಜನಾಂಗಗಳ ಭಾಷೆಗಳಲ್ಲಿಯೂ ಬರೆಯಲ್ಪಟ್ಟವು."
		},
		"2": {
			"1": "<sup>1</sup> ಇದಾದನಂತರ ಅರಸನಾದ ಅಹಷ್ವೇರೋಷನು ಕೋಪವನ್ನು ಬಿಟ್ಟು ವಷ್ಟಿಯನ್ನೂ ಆಕೆ ಮಾಡಿದ್ದನ್ನೂ ಆಕೆಯ ವಿಷಯವಾದ ತೀರ್ಪನ್ನೂ ನೆನಪುಮಾಡಿಕೊಳ್ಳುತ್ತಿರಲು",
			"2": "<sup>2</sup> ಅವನ ಸೇವೆಮಾಡುತ್ತಿರುವ ರಾಜಪರಿವಾರದವರು ಅವನಿಗೆ ಅರಸನಿಗೋಸ್ಕರ ಸುಂದರಿಯರಾದ ಕನ್ಯೆಯರನ್ನು ಹುಡುಕುವದಕ್ಕೆ ಅಪ್ಪಣೆಮಾಡಬೇಕು.",
			"3": "<sup>3</sup> ಅರಸನು ತನ್ನ ರಾಜ್ಯದ ಎಲ್ಲಾ ಸಂಸ್ಥಾನಗಳಿಗೆ ಕಾರಭಾರಿಗಳನ್ನು ಕಳುಹಿಸಬೇಕು; ಅವರು ಸುಂದರಿಯರಾದ ಎಲ್ಲಾ ಕನ್ಯೆಯರನ್ನು ಶೂಷನ್ ಕೋಟೆಯ ಅಂತಃಪುರದಲ್ಲಿ ಕೂಡಿಸಿ ಅಂತಃಪುರಪಾಲಕನೂ ರಾಜ ಕಂಚುಕಿಯೂ ಆದ ಹೇಗೈಯ ವಶಕ್ಕೆ ಕೊಡಲಿ; ಅವನು ಅವರಿಗೆ ಕಾಂತಿಯನ್ನು ಹೆಚ್ಚಿಸುವ ಲೇಪನ ದ್ರವ್ಯಗಳನ್ನು ಹಂಚಲಿ.",
			"4": "<sup>4</sup> ಮತ್ತು ಅರಸನಿಗೆ ಇಷ್ಟಳಾಗಿ ಕಾಣುವ ಕನ್ಯೆಯು ವಷ್ಟಿಗೆ ಬದಲಾಗಿ ರಾಣಿಯಾಗಲಿ ಎಂದು ಹೇಳಿದರು. ಅರಸನು ಈ ಅಭಿಪ್ರಾಯಕ್ಕೆ ಸಮ್ಮತಿಸಿ ಅದರಂತೆಯೇ ಮಾಡಿದನು.",
			"5": "<sup>5</sup> ಶೂಷನ್ ಕೋಟೆಯಲ್ಲಿ ಮೊರ್ದೆಕೈ ಎಂಬೊಬ್ಬ ಯೆಹೂದಿ ಮನುಷ್ಯನು ವಾಸಿಸುತ್ತಿದ್ದನು. ಇವನು ಬೆನ್ಯಾಮೀನ್‍ಕುಲದ ಕೀಷನ ಮರಿಮಗನೂ ಶಿಮ್ಗೀಯ ಮೊಮ್ಮಗನೂ ಯಾಯೀರನ ಮಗನೂ ಆಗಿದ್ದನು.",
			"6": "<sup>6</sup> ಬಾಬೆಲಿನ ಅರಸನಾದ ನೆಬೂಕದ್ನೆಚ್ಚರನಿಂದ ಯೆಹೂದರಾಜನಾದ ಯೆಹೋಯಾಕೀನನೊಡನೆ ಸೆರೆಯೊಯ್ಯಲ್ಪಟ್ಟವರಲ್ಲಿ ಇವನೊಬ್ಬನು.",
			"7": "<sup>7</sup> ಇವನು ಎಸ್ತೇರಳೆಂಬ ಹೆಸರನ್ನು ಪಡೆದ ಹದೆಸ್ಸಾ ಎಂಬ ತನ್ನ ಚಿಕ್ಕಪ್ಪನ ಅನಾಥಳಾದ ಮಗಳನ್ನು ತಂದೆ ತಾಯಿಗಳು ಸತ್ತಂದಿನಿಂದ ಪುತ್ರಿಯನ್ನಾಗಿ ಸ್ವೀಕರಿಸಿ ಸಾಕುತ್ತಿದ್ದನು. ಈಕೆಯು ರೂಪವತಿಯೂ ಲಾವಣ್ಯವತಿಯೂ ಆಗಿದ್ದಳು.",
			"8": "<sup>8</sup> ಅರಸನ ನಿರ್ಣಯವೂ ಆಜ್ಞೆಯೂ ಪ್ರಕಟಿಸಲ್ಪಟ್ಟು [ಕಾರಭಾರಿಗಳು] ಅನೇಕಾನೇಕ ಕನ್ಯೆಯರನ್ನು ಕೂಡಿಸಿ ಶೂಷನ್ ಕೋಟೆಯಲ್ಲಿ ಹೇಗೈಯ ವಶಕ್ಕೆ ಒಪ್ಪಿಸಿದಾಗ ಎಸ್ತೇರಳೂ ಅರಮನೆಗೆ ತರಲ್ಪಟ್ಟು ಅಂತಃಪುರಪಾಲಕನಾದ ಹೇಗೈಯ ಪರಾಂಬರಿಕೆಯಲ್ಲಿದ್ದಳು.",
			"9": "<sup>9</sup> ಹೇಗೈಯು ಆಕೆಯನ್ನು ಮೆಚ್ಚಿ ತನ್ನ ದಯೆಗೆ ಪಾತ್ರಳಾದ ಆಕೆಗೋಸ್ಕರ ತಕ್ಕ ಲೇಪನ ದ್ರವ್ಯಗಳನ್ನೂ ಭೋಜನಾಂಶವನ್ನೂ ಆರಿಸಿದ ಏಳು ಮಂದಿ ಸೇವಕಿಯರನ್ನೂ ಅರಮನೆಯಿಂದ ಬೇಗನೆ ಏರ್ಪಡಿಸಿ ಆಕೆಯನ್ನೂ ಆಕೆಯ ಸೇವಕಿಯರನ್ನೂ ಅಂತಃಪುರದ ಉತ್ತಮಭಾಗದಲ್ಲಿರಿಸಿದನು.",
			"10": "<sup>10</sup> ಎಸ್ತೇರಳು ತಾನು ಇಂಥ ಜನಾಂಗದವಳು, ಇಂಥ ಕುಲದವಳು ಎಂಬದನ್ನು ತೋರ್ಪಡಿಸಿಕೊಳ್ಳಲಿಲ್ಲ; ಮೊರ್ದೆಕೈಯು ಹೀಗೆ ಅಪ್ಪಣೆಕೊಟ್ಟಿದ್ದನು.",
			"11": "<sup>11</sup> ಅವನು ಆಕೆಯ ಈಗಿನ ಸ್ಥಿತಿಯನ್ನೂ ಮುಂದಿನ ಗತಿಯನ್ನೂ ವಿಚಾರಿಸುವದಕ್ಕಾಗಿ ಪ್ರತಿದಿನವೂ ಅಂತಃಪುರದ ಪ್ರಾಕಾರದ ಮುಂದೆ ತಿರುಗಾಡುತ್ತಿದ್ದನು.",
			"12": "<sup>12</sup> ಕನ್ಯೆಯರ ಲೇಪನಕಾಲವು ಪೂರೈಸುವದಕ್ಕೆ ಗಂಧರಸಪ್ರಯೋಗದಲ್ಲಿ ಆರು ತಿಂಗಳೂ ಪರಿಮಳ ದ್ರವ್ಯ ಕಾಂತಿವರ್ಧಕ ದ್ರವ್ಯಗಳ ಪ್ರಯೋಗದಲ್ಲಿ ಆರು ತಿಂಗಳೂ ಅಂತೂ ಹನ್ನೆರಡು ತಿಂಗಳು ಕಳೆದವು. ಈ ಕಾಲ ಮುಗಿದನಂತರ ರಾಜಸ್ತ್ರೀಯರ ವಿಷಯವಾದ ನಿಯಮದ ಮೇರೆಗೆ ತನ್ನನ್ನು ಸಿದ್ಧಪಡಿಸಿಕೊಂಡ ಪ್ರತಿಯೊಬ್ಬ ಕನ್ಯೆಯು ಅರಸನಾದ ಅಹಷ್ವೇರೋಷನ ಬಳಿಗೆ ಹೋಗುವಳು.",
			"13": "<sup>13</sup> ಅರಸನ ಬಳಿಗೆ ಹೋಗುವ ಸರತಿ ಯಾವಾಕೆಗೆ ಬರುವದೋ ಆಕೆಯು ಅಂತಃಪುರದಿಂದ ಅರಮನೆಗೆ ತೆಗೆದುಕೊಂಡು ಹೋಗುವದಕ್ಕೋಸ್ಕರ ಏನು ಕೇಳಿದರೂ ಕೊಡಲ್ಪಡುವದು.",
			"14": "<sup>14</sup> ಆಕೆಯು ಅಲ್ಲಿಗೆ ಸಾಯಂಕಾಲ ಹೋದವಳು [ಮರುದಿನ] ಬೆಳಿಗ್ಗೆ ಅರಸನ ಉಪಪತ್ನಿಗಳ ಪಾಲಕನಾದ ಶವಷ್ಗಜನೆಂಬ ರಾಜಕಂಚುಕಿಯ ವಶದಲ್ಲಿದ್ದ ಎರಡನೆಯ ಅಂತಃಪುರಕ್ಕೆ ಬರುವಳು. ಅರಸನು ಅವಳನ್ನು ಮೆಚ್ಚಿ ಹೆಸರು ಹೇಳಿ ಕರಿಸಿದರೆ ಹೊರತಾಗಿ ಒಬ್ಬಳೂ ಅರಸನ ಬಳಿಗೆ ತಿರಿಗಿ ಹೋಗುವ ಹಾಗಿರಲಿಲ್ಲ.",
			"15": "<sup>15</sup> ಅರಸನ ಬಳಿಗೆ ಹೋಗುವದಕ್ಕೆ ಮೊರ್ದೆಕೈಯ ದತ್ತಪುತ್ರಿಯೂ ಅವನ ಚಿಕ್ಕಪ್ಪನಾದ ಅಬೀಹೈಲನ ಮಗಳೂ ಆದ ಎಸ್ತೇರಳ ಸರತಿ ಬಂದಾಗ ಅಂತಃಪುರ ಪಾಲಕನಾದ ಹೇಗೈ ಎಂಬ ರಾಜಕಂಚುಕಿಯು ನೇಮಿಸಿದ್ದನ್ನೇ ಹೊರತು ಆಕೆ ಬೇರೇನೂ ಕೇಳಲಿಲ್ಲ. ನೋಡುವವರೆಲ್ಲರೂ ಆಕೆಯನ್ನು ಮೆಚ್ಚುತ್ತಿದ್ದರು.",
			"16": "<sup>16</sup> ಅರಸನಾದ ಅಹಷ್ವೇರೋಷನ ಬಳಿಗೆ ಅವನ ಆಳಿಕೆಯ ಏಳನೆಯ ವರುಷದ ಹತ್ತನೆಯ ತಿಂಗಳಾದ ಪುಷ್ಯ ಮಾಸದಲ್ಲಿ ಆಕೆಯನ್ನು ರಾಜಗೃಹಕ್ಕೆ ಕರತಂದರು. ಅರಸನು ಎಲ್ಲಾ ಸ್ತ್ರೀಯರಲ್ಲಿ ಎಸ್ತೇರಳನ್ನು ಮೆಚ್ಚಿದನು.",
			"17": "<sup>17</sup> ಎಲ್ಲಾ ಕನ್ಯೆಯರಲ್ಲಿ ಆಕೆಯು ಅವನ ದಯೆಗೂ ಪ್ರೀತಿಗೂ ಪಾತ್ರಳಾದದರಿಂದ ಅವನು ರಾಜಮುಕುಟವನ್ನು ಆಕೆಯ ತಲೆಯ ಮೇಲಿಟ್ಟು ಆಕೆಯನ್ನು ವಷ್ಟಿಗೆ ಬದಲಾಗಿ ರಾಣಿಯನ್ನಾಗಿ ಮಾಡಿಕೊಂಡು",
			"18": "<sup>18</sup> ತನ್ನ ಎಲ್ಲಾ ಸರದಾರರಿಗೂ ಪರಿವಾರದವರಿಗೂ ಎಸ್ತೇರಳ ಔತಣವೆಂದು ದೊಡ್ಡ ಔತಣವನ್ನು ಮಾಡಿಸಿ ಎಲ್ಲಾ ಸಂಸ್ಥಾನಗಳಲ್ಲಿಯೂ ಸೆರೆಯವರನ್ನು ಬಿಡಿಸಿ ರಾಜರಿಗೆ ತಕ್ಕಂತೆ ಉದಾರವಾಗಿ ದಾನಧರ್ಮಗಳನ್ನು ಮಾಡಿದನು.",
			"19": "<sup>19</sup> ಕನ್ಯೆಯರು ಎರಡನೆಯ ಸಾರಿ ಕೂಡಿಸಲ್ಪಡುತ್ತಿರುವಾಗ ಮೊರ್ದೆಕೈಯು ಅರಮನೆಯ ಹೆಬ್ಬಾಗಲಲ್ಲಿ ಕೂತುಕೊಂಡಿದ್ದನು.",
			"20": "<sup>20</sup> ಎಸ್ತೇರಳಾದರೋ ಮೊರ್ದೆಕೈಯ ಪರಾಂಬರಿಕೆಯಲ್ಲಿದ್ದ ದಿನಗಳಲ್ಲಿ ಹೇಗೋ ಹಾಗೆ ಈಗಲೂ ಅವನ ಆಜ್ಞೆಯನ್ನು ಅನುಸರಿಸುವವಳಾಗಿ ತಾನು ಇಂಥ ಕುಲದವಳು, ಇಂಥ ಜನಾಂಗದವಳು ಎಂಬದನ್ನು ತೋರ್ಪಡಿಸಲಿಲ್ಲ.",
			"21": "<sup>21</sup> ಆ ದಿನಗಳಲ್ಲಿ ಮೊರ್ದೆಕೈಯು ಅರಮನೆಯ ಹೆಬ್ಬಾಗಲಿನಲ್ಲಿ ಕೂತುಕೊಂಡಿದ್ದಾಗ ಅರಮನೆಯ ದ್ವಾರಪಾಲಕರಾದ ಬಿಗೆತಾನ್ ತೆರೆಷ್ ಎಂಬ ಇಬ್ಬರು ರಾಜಕಂಚುಕಿಯರು ಕೋಪವುಳ್ಳವರಾಗಿ ಅರಸನಾದ ಅಹಷ್ವೇರೋಷನಿಗೆ ವಿರೋಧವಾಗಿ ಕೈಯೆತ್ತಬೇಕೆಂದು ಒಳಸಂಚುಮಾಡಿದರು.",
			"22": "<sup>22</sup> ಈ ಸಂಗತಿಯು ಮೊರ್ದೆಕೈಗೆ ಗೊತ್ತಾದಾಗ ಅವನು ಅದನ್ನು ಎಸ್ತೇರ್‍ರಾಣಿಗೆ ತಿಳಿಸಿದನು. ಆಕೆಯು ಅವನ ಹೆಸರನ್ನು ಅರಸನಿಗೆ ಹೇಳಿ ಆ ಸಂಗತಿಯನ್ನು ತಿಳಿಸಿದಳು.",
			"23": "<sup>23</sup> ಈ ವಿಷಯವು ವಿಚಾರಣೆಗೆ ಬಂದಾಗ ಅದು ನಿಜವೆಂದು ಸ್ಥಾಪನೆಯಾದದರಿಂದ ಆ ಇಬ್ಬರನ್ನೂ ಗಲ್ಲಿಗೆ ಹಾಕಿದರು. ಆ ಸಂಗತಿಯು ರಾಜನ ದಿನಚರ್ಯಪುಸ್ತಕದಲ್ಲಿ ಬರೆಯಲ್ಪಟ್ಟಿತು."
		},
		"3": {
			"1": "<sup>1</sup> ಇದಾದನಂತರ ಅರಸನಾದ ಅಹಷ್ವೇರೋಷನು ಅಗಾಗನ ವಂಶದವನೂ ಹಮ್ಮೆದಾತನ ಮಗನೂ ಆದ ಹಾಮಾನನನ್ನು ಪದವಿಯಲ್ಲಿ ಹೆಚ್ಚಿಸಿ ಅವನಿಗೆ ದೊಡ್ಡ ಅಧಿಕಾರವನ್ನು ಕೊಟ್ಟು ತನ್ನ ಒಡ್ಡೋಲಗದ ಎಲ್ಲಾ ಸರದಾರರಲ್ಲಿ ಅವನಿಗೆ ಪ್ರಥಮಾಸನವನ್ನು ಅನುಗ್ರಹಿಸಿದನು.",
			"2": "<sup>2</sup> ಅರಸನು - ರಾಜನಿವೇಶನದಲ್ಲಿರುವ ರಾಜಸೇವಕರೆಲ್ಲರೂ ಹಾಮಾನನಿಗೆ ಸಾಷ್ಟಾಂಗ ನಮಸ್ಕಾರಮಾಡಬೇಕೆಂದು ಅಪ್ಪಣೆಮಾಡಿದ್ದರಿಂದ ಎಲ್ಲರೂ ಹಾಗೆ ಮಾಡುತ್ತಿದ್ದರು; ಆದರೆ ಮೊರ್ದೆಕೈಯು ಅವನಿಗೆ ಸಾಷ್ಟಾಂಗನಮಸ್ಕಾರ ಮಾಡಲಿಲ್ಲ.",
			"3": "<sup>3</sup> ರಾಜಗೃಹನಿವೇಶನದಲ್ಲಿದ್ದ ರಾಜಸೇವಕರು - ನೀನು ರಾಜಾಜ್ಞೆಯನ್ನು ಮೀರುವದೇಕೆ ಎಂದು ಹೇಳಿ",
			"4": "<sup>4</sup> ದಿನಂಪ್ರತಿ ಅವನನ್ನು ಎಚ್ಚರಿಸಿದರೂ ಅವನು ಮಣಿಯಲಿಲ್ಲ. ಆದದರಿಂದ ಅವರು - ತಾನು ಯೆಹೂದ್ಯನೆನ್ನುವ ಮೊರ್ದೆಕೈಯ ಹೆಮ್ಮೆಯ ಹಟವು ಸಾಗುವದೇನೋ ನೋಡೋಣ ಎಂದುಕೊಂಡು ಅದನ್ನು ಹಾಮಾನನಿಗೆ ತಿಳಿಸಿದರು.",
			"5": "<sup>5</sup> ಮೊರ್ದೆಕೈಯು ತನಗೆ ಸಾಷ್ಟಾಂಗನಮಸ್ಕಾರ ಮಾಡುವದಿಲ್ಲವೆಂದು ಹಾಮಾನನು ನೋಡಿ ಬಹು ಕೋಪವುಳ್ಳವನಾದನು.",
			"6": "<sup>6</sup> ಅವನು ಮೊರ್ದೆಕೈಯೊಬ್ಬನ ಮೇಲೆಯೇ ಕೈಯೆತ್ತುವದು ಸಾಲದೆಂದೆಣಿಸಿ ಇವನು ಯೆಹೂದಜನಾಂಗದವನೆಂದು ಕೇಳಿದ ಮೇಲೆ ಅಹಷ್ವೇರೋಷನ ರಾಜ್ಯದಲ್ಲಿದ್ದ ಮೊರ್ದೆಕೈಯ ಬಂಧುಗಳನ್ನು ಅಂದರೆ ಎಲ್ಲಾ ಯೆಹೂದ್ಯರನ್ನು ಸಂಹರಿಸಬೇಕೆಂದು ನಿರ್ಣಯಿಸಿಕೊಂಡನು.",
			"7": "<sup>7</sup> ಅರಸನಾದ ಅಹಷ್ವೇರೋಷನ [ಆಳಿಕೆಯ] ಹನ್ನೆರಡನೆಯ ವರುಷದ ಮೊದಲನೆಯ ತಿಂಗಳಾದ ಚೈತ್ರಮಾಸದಲ್ಲಿ ಹಾಮಾನನ ಮುಂದೆ ಶುಭಮಾಸವೂ ಶುಭದಿವಸವೂ ಯಾವವೆಂದು ತಿಳಿದುಕೊಳ್ಳುವದಕ್ಕೆ ಪೂರನ್ನು ಅಂದರೆ ಚೀಟನ್ನು ಹಾಕಲು ಚೀಟು ಹನ್ನೆರಡನೆಯ ತಿಂಗಳಾದ ಫಾಲ್ಗುನಮಾಸದ ಹದಿಮೂರನೆಯ ದಿನಕ್ಕೆ ಬಿತ್ತು.",
			"8": "<sup>8</sup> ಹಾಮಾನನು ಅರಸನಾದ ಅಹಷ್ವೇರೋಷನಿಗೆ - ನಿನ್ನ ರಾಜ್ಯದ ಎಲ್ಲಾ ಸಂಸ್ಥಾನಗಳಲ್ಲಿ ಒಂದು ಜನಾಂಗವಿರುತ್ತದೆ; ಅದು ಇತರ ಜನಾಂಗಗಳವರ ಮಧ್ಯದಲ್ಲಿ ಹರಡಿದ್ದರೂ ಅವರಿಂದ ಪ್ರತ್ಯೇಕವಾಗಿಯೇ ಇರುತ್ತದೆ. ಆ ಜನಾಂಗದವರ ನಿಯಮಗಳು ಇತರ ಎಲ್ಲಾ ಜನಾಂಗಗಳವರ ನಿಯಮಗಳಿಗೆ ಬೇರೆಯಾಗಿರುತ್ತವೆ. ಅರಸನ ನಿಯಮಗಳನ್ನಂತೂ ಅವರು ಅನುಸರಿಸುವದೇ ಇಲ್ಲ. ಅರಸನು ಅವರನ್ನು ಸುಮ್ಮನೆ ಬಿಡುವದು ಉಚಿತವಲ್ಲ.",
			"9": "<sup>9</sup> ಅರಸನ ಚಿತ್ತ ಬಂದರೆ ಅವರನ್ನು ಸಂಹರಿಸಬೇಕು ಎಂಬ ರಾಯಸವು ಹೊರಡಲಿ; ಹಾಗೆ ಮಾಡಿದರೆ ನಾನು ರಾಜ ಭಂಡಾರಗಳಿಗೆ ಸೇರಿಸುವದಕ್ಕೋಸ್ಕರ ಹತ್ತು ಸಾವಿರ ತಲಾಂತು ಬೆಳ್ಳಿಯನ್ನು ತೂಕ ಮಾಡಿ ಉದ್ಯೋಗಸ್ಥರ ಕೈಗೆ ಒಪ್ಪಿಸುವೆನು ಎಂದು ಹೇಳಿದನು.",
			"10": "<sup>10</sup> ಆಗ ಅರಸನು ತನ್ನ ಕೈಯಲ್ಲಿದ್ದ ಮುದ್ರೆಯುಂಗುರವನ್ನು ತೆಗೆದು ಅಗಾಗನ ವಂಶದವನೂ ಹಮ್ಮೆದಾತನ ಮಗನೂ ಯೆಹೂದದ್ವೇಷಿಯೂ ಆದ ಹಾಮಾನನಿಗೆ ಒಪ್ಪಿಸಿ ಅವನಿಗೆ -",
			"11": "<sup>11</sup> ಆ ಬೆಳ್ಳಿ ನಿನ್ನಲ್ಲೇ ಇರಲಿ; ನೀನು ಆ ಜನರನ್ನು ಏನು ಬೇಕಾದರೂ ಮಾಡಬಹುದು ಎಂದು ಹೇಳಿದನು.",
			"12": "<sup>12</sup> ಮೊದಲನೆಯ ತಿಂಗಳಿನ ಹದಿಮೂರನೆಯ ದಿನದಲ್ಲಿ ರಾಜಲೇಖಕರು ಕೂಡಿ ಬರಬೇಕು ಎಂದು ಅಪ್ಪಣೆಯಾಯಿತು. ಅವರು ಹಾಮಾನನ ಆಜ್ಞಾನುಸಾರ ಉಪರಾಜರಿಗೂ ಆಯಾ ಸಂಸ್ಥಾನಗಳ ಅಧಿಕಾರಿಗಳಿಗೂ ಆಯಾ ಜನಾಂಗಗಳ ಅಧಿಪತಿಗಳಿಗೂ ಪತ್ರಗಳನ್ನು ಬರೆದರು. ಆಯಾ ಸಂಸ್ಥಾನಗಳ ಬರಹದಲ್ಲಿಯೂ ಆಯಾ ಜನಾಂಗಗಳ ಭಾಷೆಯಲ್ಲಿಯೂ ಇದ್ದ ಆ ಪತ್ರಗಳು ಅರಸನಾದ ಅಹಷ್ವೇರೋಷನ ಹೆಸರಿನಲ್ಲೇ ಲಿಖಿತವಾಗಿದ್ದವು; ಅವುಗಳಿಗೆ ರಾಜಮುದ್ರೆಯೂ ಇತ್ತು.",
			"13": "<sup>13</sup> ಪತ್ರಗಳು ಅಂಚೆಯವರ ಮುಖಾಂತರ ಎಲ್ಲಾ ರಾಜಸಂಸ್ಥಾನಗಳಿಗೆ ಕಳುಹಿಸಲ್ಪಟ್ಟವು. ಅವುಗಳಲ್ಲಿ - ಒಂದೇ ದಿನದಲ್ಲಿ ಅಂದರೆ ಹನ್ನೆರಡನೆಯ ತಿಂಗಳಾದ ಫಾಲ್ಗುನ ಮಾಸದ ಹದಿಮೂರನೆಯ ದಿನದಲ್ಲಿ ಹುಡುಗರು, ಮುದುಕರು, ಹೆಂಗಸರು, ಮಕ್ಕಳು ಎಂದು ನೋಡದೆ ಎಲ್ಲಾ ಯೆಹೂದ್ಯರನ್ನು ಕೊಲ್ಲಿರಿ, ಸಂಹರಿಸಿರಿ, ನಿರ್ನಾಮಗೊಳಿಸಿರಿ, ಅವರ ಸೊತ್ತನ್ನು ಸೂರೆಮಾಡಿರಿ ಎಂದೂ",
			"14": "<sup>14</sup> ಎಲ್ಲರೂ ಆ ದಿನದಲ್ಲಿ ಸಿದ್ಧರಾಗಿರುವ ಹಾಗೆ ಈ ಪತ್ರದ ಒಂದೊಂದು ಪ್ರತಿಯು ಪ್ರತಿಯೊಂದು ಸಂಸ್ಥಾನದಲ್ಲಿ ರಾಜವಿಧಿಯೆಂದು ಎಲ್ಲಾ ಜನರಲ್ಲಿಯೂ ಪ್ರಕಟಿಸಲ್ಪಡಬೇಕು ಎಂದೂ ಬರೆದಿತ್ತು.",
			"15": "<sup>15</sup> ಈ ನಿರ್ಣಯವು ಶೂಷನ್ ಕೋಟೆಯಲ್ಲಿ ಪ್ರಕಟವಾದಾಗಲೇ ಅಂಚೆಯವರು ರಾಜಾಜ್ಞೆಯ ಮೇರೆಗೆ ಶೀಘ್ರವಾಗಿ ಹೊರಟರು. ಅರಸನಾದರೋ ಹಾಮಾನನೊಡನೆ ಸುರಾಪಾನ ಮಾಡುವದಕ್ಕೆ ಕೂತುಕೊಂಡನು. ಅತ್ತ ಶೂಷನ್ ಪಟ್ಟಣದಲ್ಲಿ ತಳಮಳ ಉಂಟಾಯಿತು."
		},
		"4": {
			"1": "<sup>1</sup> ಮೊರ್ದೆಕೈಯು ನಡೆದದ್ದನ್ನೆಲ್ಲಾ ಕೇಳಿದಾಗ ಬಟ್ಟೆಗಳನ್ನು ಹರಕೊಂಡು ಗೋಣಿತಟ್ಟನ್ನು ಕಟ್ಟಿಕೊಂಡು ಬೂದಿಯನ್ನು ಹಾಕಿಕೊಂಡು ಪಟ್ಟಣದ ಮಧ್ಯದಲ್ಲಿ ಹೋಗುತ್ತಾ ದುಃಖಾತಿಶಯದಿಂದ ಗೋಳಾಡಿ",
			"2": "<sup>2</sup> ಅರಮನೆಯ ಹೆಬ್ಬಾಗಲಿನ ಮುಂದೆ ಬಂದನು; ಗೋಣಿತಟ್ಟು ಕಟ್ಟಿಕೊಂಡವರು ಒಳಗೆ ಹೋಗಬಾರದೆಂದು ಅಪ್ಪಣೆಯಾಗಿತ್ತು.",
			"3": "<sup>3</sup> ಯಾವ ಯಾವ ಸಂಸ್ಥಾನಗಳಲ್ಲಿ ಅರಸನ ಆಜ್ಞೆಯೂ ನಿರ್ಣಯವೂ ಪ್ರಕಟವಾದವೋ ಅಲ್ಲೆಲ್ಲಾ ಯೆಹೂದ್ಯರೊಳಗೆ ಮಹಾದುಃಖವೂ ಉಪವಾಸರೋದನ ಪ್ರಲಾಪಗಳೂ ಉಂಟಾದವು. ಅನೇಕರು ಗೋಣಿತಟ್ಟನ್ನು ಹಾಸಿ ಬೂದಿಹಾಕಿಕೊಂಡು ಅದರ ಮೇಲೆ ಕೂತರು.",
			"4": "<sup>4</sup> ಎಸ್ತೇರಳ ಸೇವಕಿಯರೂ ಕಂಚುಕಿಗಳೂ ರಾಣಿಯಾದ ಆಕೆಯ ಬಳಿಗೆ ಬಂದು ಅದನ್ನು ತಿಳಿಸಿದಾಗ ಆಕೆಯು ಬಹಳವಾಗಿ ಕಳವಳಗೊಂಡು ಮೊರ್ದೆಕೈಗೆ ವಸ್ತ್ರಗಳನ್ನು ಕಳುಹಿಸಿ - ಗೋಣಿತಟ್ಟನ್ನು ತೆಗೆದುಬಿಟ್ಟು ಇವುಗಳನ್ನು ಧರಿಸಿಕೋ ಎಂದು ಹೇಳಿಸಿದಳು; ಆದರೆ ಅವನು ಅವುಗಳನ್ನು ಸ್ವೀಕರಿಸಲಿಲ್ಲ.",
			"5": "<sup>5</sup> ಆಗ ಆಕೆ ಅರಸನಿಂದ ತನ್ನ ಸೇವೆಗೋಸ್ಕರ ನೇಮಿಸಲ್ಪಟ್ಟಿದ್ದ ಹತಾಕನೆಂಬ ರಾಜಕಂಚುಕಿಯನ್ನು ಕರಿಸಿ - ನೀನು ಮೊರ್ದೆಕೈಯ ಬಳಿಗೆ ಹೋಗಿ ಇದೇನು, ಯಾಕೆ ಎಂದು ವಿಚಾರಿಸಿಕೊಂಡು ಬಾ ಎಂದು ಹೇಳಿಕಳುಹಿಸಿದಳು.",
			"6": "<sup>6</sup> ಹತಾಕನು ಅರಮನೆಯ ಬಾಗಲಿನ ಮುಂದಿರುವ ಪಟ್ಟಣದ ಬೈಲಿಗೆ ಮೊರ್ದೆಕೈಯ ಹತ್ತಿರ ಹೋದಾಗ",
			"7": "<sup>7</sup> ಮೊರ್ದೆಕೈಯು ತನಗೆ ಸಂಭವಿಸಿದದ್ದೆಲ್ಲವನ್ನೂ ಹಾಮಾನನು ಯೆಹೂದ್ಯರನ್ನು ನಾಶಮಾಡಲು ಅಪ್ಪಣೆಪಡಕೊಳ್ಳುವದಕ್ಕಾಗಿ ರಾಜಭಂಡಾರಕ್ಕೆ ತೂಕಮಾಡಿ ಕೊಡುತ್ತೇನೆಂದು ವಾಗ್ದಾನಮಾಡಿದ ಸರಿಯಾದ ಹಣದ ಗಂಟು ಇಷ್ಟೆಂಬದನ್ನೂ ಅವನಿಗೆ ತಿಳಿಸಿದ್ದಲ್ಲದೆ",
			"8": "<sup>8</sup> ತಮ್ಮನ್ನು ಸಂಹರಿಸುವದಕ್ಕೋಸ್ಕರ ಶೂಷನಿನಲ್ಲಿ ಪ್ರಕಟವಾದ ರಾಜಶಾಸನದ ಒಂದು ಪ್ರತಿಯನ್ನು ಕೊಟ್ಟು - ಇದನ್ನು ಎಸ್ತೇರಳಿಗೆ ತೋರಿಸಿ ವಿವರಿಸು; ಮತ್ತು ಆಕೆಯು ಅರಸನ ಬಳಿಗೆ ಹೋಗಿ ಅವನು ಕೃಪೆ ತೋರಿಸುವಂತೆ ಅವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ತನ್ನ ಜನರಿಗೋಸ್ಕರ ಬಿನ್ನಹ ಮಾಡಲಿ ಎಂದು ಹೇಳಿಕಳುಹಿಸಿದನು.",
			"9": "<sup>9</sup> ಹತಾಕನು ಹಿಂದಿರುಗಿ ಬಂದು ಎಸ್ತೇರಳಿಗೆ ಮೊರ್ದೆಕೈಯ ಮಾತುಗಳನ್ನು ತಿಳಿಸಲು",
			"10": "<sup>10</sup> ಎಸ್ತೇರಳು ಅವನನ್ನು ತಿರಿಗಿ ಮೊರ್ದೆಕೈಯ ಹತ್ತಿರ ಕಳುಹಿಸಿ -",
			"11": "<sup>11</sup> ಅರಸನು ಕರಿಸಿದರೆ ಹೊರತು ಅವನ ಬಳಿಗೆ ಒಳಗಣ ಪ್ರಾಕಾರಕ್ಕೆ ಹೋಗುವವರು ಮರಣದಂಡನೆಗೆ ಪಾತ್ರರಾಗುವರು ಎಂಬ ಒಂದೇ ನಿಯಮವು ಎಲ್ಲಾ ಸ್ತ್ರೀ ಪುರುಷರಿಗುಂಟು; ಯಾರ ಕಡೆಗೆ ಅವನು ತನ್ನ ಸುವರ್ಣದಂಡವನ್ನು ಚಾಚುವನೋ ಅವರು ಮಾತ್ರ ಜೀವದಿಂದುಳಿಯುವರೆಂಬದು ಅರಸನ ಎಲ್ಲಾ ಸೇವಕರಿಗೂ ಸಂಸ್ಥಾನಗಳ ಎಲ್ಲಾ ಜನರಿಗೂ ಗೊತ್ತುಂಟಲ್ಲಾ; ನನಗಂತೂ ಮೂವತ್ತು ದಿನಗಳಿಂದ ಅರಸನ ಬಳಿಗೆ ಹೋಗುವದಕ್ಕೆ ಆಮಂತ್ರಣವಾಗಲಿಲ್ಲ ಎಂದು ಹೇಳಿಸಿದಳು.",
			"12": "<sup>12</sup> ಹತಾಕನು ಎಸ್ತೇರಳ ಮಾತುಗಳನ್ನು ಮೊರ್ದೆಕೈಗೆ ತಿಳಿಸಿದಾಗ",
			"13": "<sup>13</sup> ಅವನು ತಿರಿಗಿ ಎಸ್ತೇರಳಿಗೆ - ಯೆಹೂದ್ಯರೆಲ್ಲಾ ನಾಶವಾದರೂ ನಾನೊಬ್ಬಳು ಅರಮನೆಯಲ್ಲಿರುವದರಿಂದ ಉಳಿಯುವೆನು ಎಂದು ಭಾವಿಸಿಕೊಳ್ಳಬೇಡ.",
			"14": "<sup>14</sup> ನೀನು ಈಗ ಸುಮ್ಮನಿದ್ದು ಬಿಟ್ಟರೆ ಬೇರೆ ಕಡೆಯಿಂದ ಯೆಹೂದ್ಯರಿಗೆ ಸಹಾಯವೂ ವಿಮೋಚನೆಯೂ ಉಂಟಾದಾವು; ನೀನಾದರೋ ನಿನ್ನ ತಂದೆಯ ಮನೆಯವರೊಡನೆ ನಾಶವಾಗುವಿ. ಇದಲ್ಲದೆ ನೀನು ಇಂಥ ಸಂದರ್ಭಕ್ಕಾಗಿಯೇ ಪಟ್ಟಕ್ಕೆ ಬಂದಿರಬಹುದು, ಯಾರು ಬಲ್ಲರು ಎಂದು ಹೇಳಿ ಕಳುಹಿಸಿದನು.",
			"15": "<sup>15</sup> ಆಗ ಎಸ್ತೇರಳು - ನೀನು ಹೋಗಿ ಶೂಷನಿನಲ್ಲಿ ಸಿಕ್ಕುವ ಎಲ್ಲಾ ಯೆಹೂದ್ಯರನ್ನು ಕೂಡಿಸು; ನೀವೆಲ್ಲರೂ ಮೂರು ದಿನ ಹಗಲಿರುಳು ಅನ್ನಪಾನಗಳನ್ನು ಬಿಟ್ಟು ನನಗೋಸ್ಕರ ಉಪವಾಸ ಮಾಡಿರಿ; ಅದರಂತೆ ನಾನೂ ನನ್ನ ಸೇವಕಿಯರೊಡನೆ ಉಪವಾಸದಿಂದಿರುವೆನು. ಅನಂತರ ನಾನು ವಿಧಿ ಮೀರಿ ಅರಸನ ಬಳಿಗೆ ಹೋಗುವೆನು, ಸತ್ತರೆ ಸಾಯುತ್ತೇನೆ ಎಂದು ಮೊರ್ದೆಕೈಗೆ ಮರಳಿ ಹೇಳಿಸಿದಳು.",
			"17": "<sup>17</sup> ಮೊರ್ದೆಕೈಯು ಹಿಂದಿರುಗಿ ಹೋಗಿ ಎಸ್ತೇರಳು ಹೇಳಿದಂತೆಯೇ ಮಾಡಿದನು."
		},
		"5": {
			"1": "<sup>1</sup> ಮೂರನೆಯ ದಿನದಲ್ಲಿ ಎಸ್ತೇರಳು ರಾಜವಸ್ತ್ರ ಭೂಷಿತಳಾಗಿ ಅರಮನೆಯ ಒಳಗಣ ಪ್ರಾಕಾರವನ್ನು ಪ್ರವೇಶಿಸಿ ರಾಜ ಮಂದಿರದ ಎದುರಿನಲ್ಲಿ ನಿಂತಳು. ಅರಸನು ಆ ಮಂದಿರದಲ್ಲಿ ರಾಜಸಿಂಹಾಸನದ ಮೇಲೆ ಬಾಗಲಿಗೆದುರಾಗಿ ಕೂತುಕೊಂಡಿದ್ದನು.",
			"2": "<sup>2</sup> ಅವನು ಪ್ರಾಕಾರದಲ್ಲಿ ನಿಂತಿರುವ ಎಸ್ತೇರ್ ರಾಣಿಯನ್ನು ಕಂಡ ಕೂಡಲೆ ಆಕೆಯ ಮೇಲೆ ದಯವಿಟ್ಟು ತನ್ನ ಕೈಯಲ್ಲಿದ್ದ ಸುವರ್ಣದಂಡವನ್ನು ಆಕೆಯ ಕಡೆಗೆ ಚಾಚಿದನು. ಎಸ್ತೇರಳು ಸಮೀಪಕ್ಕೆ ಹೋಗಿ ದಂಡದ ತುದಿಯನ್ನು ಮುಟ್ಟಲು",
			"3": "<sup>3</sup> ಅರಸನು ಆಕೆಗೆ ಎಸ್ತೇರ್ ರಾಣಿಯೇ, ನಿನಗೇನು ಬೇಕು? ನಿನ್ನ ವಿಜ್ಞಾಪನೆ ಯಾವದು? ನನ್ನ ಅರ್ಧ ರಾಜ್ಯವನ್ನು ಕೇಳಿದರೂ ನಿನಗೆ ಕೊಡುತ್ತೇನೆ ಅಂದನು.",
			"4": "<sup>4</sup> ಅದಕ್ಕೆ ಎಸ್ತೇರಳು - ಅರಸನ ಚಿತ್ತಕ್ಕೆ ಬಂದರೆ ನಾನು ಈಹೊತ್ತು ತಮಗೋಸ್ಕರ ಸಿದ್ಧಮಾಡಿಸಿರುವ ಔತಣಕ್ಕೆ ಹಾಮಾನನೊಡನೆ ಬರೋಣವಾಗಲಿ ಎಂದು ಅರಿಕೆಮಾಡಿದಳು.",
			"5": "<sup>5</sup> ಕೂಡಲೆ ಅರಸನು - ಎಸ್ತೇರಳ ವಿಜ್ಞಾಪನೆಯನ್ನು ನೆರವೇರಿಸುವದಕ್ಕೆ ಹಾಮಾನನನ್ನು ತ್ವರೆಪಡಿಸಿರಿ ಎಂದು ಅಪ್ಪಣೆಮಾಡಿದನು; ಮತ್ತು ಎಸ್ತೇರಳು ಮಾಡಿಸಿದ್ದ ಔತಣಕ್ಕೆ ಹಾಮಾನನ ಜೊತೆಯಲ್ಲಿ ಹೋದನು.",
			"6": "<sup>6</sup> ದ್ರಾಕ್ಷಾರಸಪಾನ ಮಾಡುವಾಗ ಅರಸನು ತಿರಿಗಿ ಎಸ್ತೇರಳಿಗೆ - ನಿನ್ನ ವಿಜ್ಞಾಪನೆ ಯಾವದು, ಹೇಳು; ಅದನ್ನು ನೆರವೇರಿಸುವೆನು. ನೀನು ನನ್ನ ಅರ್ಧರಾಜ್ಯವನ್ನು ಕೇಳಿಕೊಂಡರೂ ಸರಿಯೇ, ಅದನ್ನು ನಿನಗೆ ಕೊಡುವೆನು ಅನ್ನಲು ಎಸ್ತೇರಳು -",
			"7": "<sup>7</sup> ಅರಸರು ನನ್ನ ಮೇಲೆ ಕಟಾಕ್ಷವಿಟ್ಟು ನಾನು ಕೇಳುವದನ್ನು ಕೊಡುವದಕ್ಕೂ ನನ್ನ ವಿಜ್ಞಾಪನೆಯನ್ನು ನೆರವೇರಿಸುವದಕ್ಕೂ ಮನಸ್ಸುಳ್ಳವರಾಗಿದ್ದರೆ",
			"8": "<sup>8</sup> ನಾನು ತಮಗೋಸ್ಕರ ಸಿದ್ಧಮಾಡಿಸುವ ಔತಣಕ್ಕೆ ನಾಳೆಯೂ ಹಾಮಾನನ ಜೊತೆಯಲ್ಲಿ ಬರೋಣವಾಗಲಿ; ಆಗ ಅರಸರು ಹೇಳಿದಂತೆ ಮಾಡುವೆನು. ಇದೇ ನನ್ನ ಮನವಿ ಎಂದು ಉತ್ತರಕೊಟ್ಟಳು.",
			"9": "<sup>9</sup> ಆ ದಿನ ಹಾಮಾನನು ಆನಂದಲಹರಿಯಲ್ಲಿ ಮನೆಗೆ ಹೋಗುತ್ತಿರುವಾಗ ಮೊರ್ದೆಕೈಯು ತನಗೆ ಭಯಪಡದೆ ತನ್ನ ಮುಂದೆ ಏಳದೆ ಅರಮನೆಯ ಹೆಬ್ಬಾಗಲಿನಲ್ಲಿ ಕೂತಿರುವದನ್ನು ಕಂಡು ಅವನ ಮೇಲೆ ಕೋಪಭರಿತನಾದನು.",
			"10": "<sup>10</sup> ಆದರೂ ತನ್ನನ್ನು ಬಿಗಿಹಿಡಿದುಕೊಂಡು ಮನೆಗೆ ಹೋಗಿ ತನ್ನ ಆಪ್ತರನ್ನೂ ಪತ್ನಿಯಾದ ಜೆರೆಷಳನ್ನೂ ಕರಿಸಿ",
			"11": "<sup>11</sup> ಅವರ ಮುಂದೆ ತನ್ನ ಘನೈಶ್ವರ್ಯಗಳನ್ನೂ ಪುತ್ರಲಾಭಾತಿಶಯವನ್ನೂ ಅರಸನು ತನ್ನನ್ನು ಸನ್ಮಾನಿಸಿ ಎಲ್ಲಾ ಸರದಾರರಲ್ಲಿಯೂ ರಾಜ ಸೇವಕರಲ್ಲಿಯೂ ತನಗೆ ಶ್ರೇಷ್ಠಸ್ಥಾನವನ್ನು ಅನುಗ್ರಹಿಸಿದ್ದನ್ನೂ ವರ್ಣಿಸಿದ ಮೇಲೆ -",
			"12": "<sup>12</sup> ಅಹಹ! ಎಸ್ತೇರ್ ರಾಣಿಯು ತಾನು ಸಿದ್ಧಮಾಡಿಸಿದ ಔತಣಕ್ಕೆ ಅರಸನ ಜೊತೆಯಲ್ಲಿ ನನ್ನನ್ನು ಹೊರತಾಗಿ ಇನ್ನಾರನ್ನೂ ಕರಿಸಲಿಲ್ಲ. ನಾಳೆಯೂ ಅರಸನ ಜೊತೆಯಲ್ಲಿ ಬರಬೇಕೆಂದು ನನಗೆ ಹೇಳಿದ್ದಾಳೆ.",
			"13": "<sup>13</sup> ಆದರೂ ಮೊರ್ದೆಕೈ ಎಂಬ ಯೆಹೂದ್ಯನು ಅರಮನೆಯ ಬಾಗಲಿನಲ್ಲಿ ನನ್ನ ಕಣ್ಣು ಮುಂದೆ ಕೂತಿರುವವರೆಗೂ ಇದೆಲ್ಲದರಿಂದಲೂ ನನಗೆ ತೃಪ್ತಿಯಾಗುವದಿಲ್ಲ ಅಂದನು.",
			"14": "<sup>14</sup> ಅದಕ್ಕೆ ಅವನು ಪತ್ನಿಯಾದ ಜೆರೆಷಳೂ ಅವನ ಎಲ್ಲಾ ಆಪ್ತರೂ - ಐವತ್ತು ಮೊಳ ಎತ್ತರವಾದ ಒಂದು ಗಲ್ಲುಮರವನ್ನು ಸಿದ್ಧಮಾಡಿಸಿ ನಾಳೆ ಅರಸನ ಅಪ್ಪಣೆಯನ್ನು ಪಡಕೊಂಡು ಮೊರ್ದೆಕೈಯನ್ನು ಅದಕ್ಕೆ ನೇತುಹಾಕಿಸಬೇಕು; ಆಮೇಲೆ ನೀನು ಸಂತೋಷದಿಂದ ಅರಸನ ಜೊತೆಯಲ್ಲಿ ಔತಣಕ್ಕೆ ಹೋಗಬಹುದು ಎಂದು ಹೇಳಿದರು. ಹಾಮಾನನು ಈ ಮಾತಿಗೆ ಮೆಚ್ಚಿ ಗಲ್ಲುಮರವನ್ನು ಸಿದ್ಧಮಾಡಿಸಿದನು."
		},
		"6": {
			"1": "<sup>1</sup> ಆ ರಾತ್ರಿಯಲ್ಲಿ ಅರಸನಿಗೆ ನಿದ್ರೆ ಬರಲಿಲ್ಲ; ಆದದರಿಂದ ಸ್ಮರಿಸತಕ್ಕ ಪೂರ್ವವೃತ್ತಾಂತಗಳ ಗ್ರಂಥವನ್ನು ಅವನು ತರಿಸಿ ಪಾರಾಯಣಮಾಡಿಸುತ್ತಿರುವಾಗ",
			"2": "<sup>2</sup> ಅಹಷ್ವೇರೋಷ್ ರಾಜನನ್ನು ಕೊಲ್ಲಬೇಕೆಂದು ದ್ವಾರಪಾಲಕರಾದ ಬಿಗೆತಾನ್ ತೆರೆಷ್ ಎಂಬಿಬ್ಬರು ರಾಜಕಂಚುಕಿಗಳು ಒಳಸಂಚುಮಾಡಿದ ಸಂಗತಿಯೂ ಅದು ಮೊರ್ದೆಕೈಯ ಮುಖಾಂತರ ಬೈಲಿಗೆ ಬಂದ ಸಂಗತಿಯೂ ಅದರಲ್ಲಿ ಸಿಕ್ಕಿದವು.",
			"3": "<sup>3</sup> ಆಗ ಅರಸನು - ಇದಕ್ಕಾಗಿ ಮೊರ್ದೆಕೈಗೆ ಯಾವ ಸ್ಥಾನಮಾನಗಳು ದೊರಕಿದವೆಂದು ವಿಚಾರಿಸಿದಾಗ ಅವನ ಸಾನ್ನಿಧ್ಯಸೇವಕರಾದ ಪರಿವಾರದವರು - ಏನೂ ದೊರಕಲಿಲ್ಲ ಎಂದು ಹೇಳಿದರು.",
			"4": "<sup>4</sup> ಅರಸನು - ಪ್ರಾಕಾರದಲ್ಲಿ ಯಾರಿದ್ದಾರೆ ಎಂದು ಕೇಳಿದನು. ಅಷ್ಟರಲ್ಲಿ ಹಾಮಾನನೇ ತಾನು ಸಿದ್ಧಮಾಡಿಸಿದ ಗಲ್ಲಿಗೆ ಮೊರ್ದೆಕೈಯನ್ನು ನೇತುಹಾಕಿಸುವದಕ್ಕೆ ಅರಸನ ಅಪ್ಪಣೆಪಡಕೊಳ್ಳಬೇಕೆಂದು ಅರಮನೆಯ ಹೊರಗಣ ಪ್ರಾಕಾರದಲ್ಲಿ ಬಂದಿದ್ದನು.",
			"5": "<sup>5</sup> ಆದದರಿಂದ ರಾಜಸೇವಕರು - ಇಗೋ, ಪ್ರಾಕಾರದಲ್ಲಿ ಹಾಮಾನನಿರುತ್ತಾನೆ ಎಂದು ಬಿನ್ನವಿಸಿದರು. ಆಗ ಅರಸನು - ಅವನು ಒಳಗೆ ಬರಲಿ ಎಂದನು.",
			"6": "<sup>6</sup> ಹಾಮಾನನು ಬಂದಾಗ - ಅರಸನು ಯಾವನನ್ನು ಸನ್ಮಾನಿಸಬೇಕೆಂದು ಇಷ್ಟವುಳ್ಳವನಾಗಿರುತ್ತಾನೋ ಅಂಥವನಿಗೆ ಮಾಡತಕ್ಕದ್ದೇನು ಎಂದು ಅವನನ್ನು ಕೇಳಿದನು. ಹಾಮಾನನು - ಅರಸನು ನನ್ನನ್ನಲ್ಲದೆ ಇನ್ನಾರನ್ನು ಸನ್ಮಾನಿಸುವದಕ್ಕೆ ಇಷ್ಟವುಳ್ಳವನಾಗಿರುವನು ಅಂದುಕೊಂಡು",
			"7": "<sup>7</sup> ಅರಸನಿಗೆ - ಒಬ್ಬನನ್ನು ಸನ್ಮಾನಿಸಬೇಕೆಂದು ಅರಸನಿಗೆ ಇಷ್ಟವಿದ್ದರೆ",
			"8": "<sup>8</sup> ತಾನು ಧರಿಸಿಕೊಳ್ಳುವ ರಾಜವಸ್ತ್ರಗಳನ್ನೂ ಸವಾರಿಮಾಡುವ ಪಟ್ಟದ ಕುದುರೆಯನ್ನೂ ತರಿಸಿ",
			"9": "<sup>9</sup> ಆ ವಸ್ತ್ರಗಳನ್ನೂ ಕುದುರೆಯನ್ನೂ ಪ್ರಧಾನರಾದ ಅರಸನ ಸರದಾರರಲ್ಲೊಬ್ಬನ ವಶಕ್ಕೆ ಕೊಟ್ಟು ಅರಸನು ಸನ್ಮಾನಿಸಬೇಕೆಂದಿರುವ ಪುರುಷನಿಗೆ ಆ ರಾಜವಸ್ತ್ರಗಳನ್ನು ಧಾರಣೆಮಾಡಿಸಿ ಅವನನ್ನು ಆ ಕುದುರೆಯ ಮೇಲೆ ಕುಳ್ಳಿರಿಸಿ ಪಟ್ಟಣದ ಮಧ್ಯದಲ್ಲಿ ಮೆರವಣಿಗೆಮಾಡಿಸುತ್ತಾ ಅವನ ಮುಂದೆ - ಅರಸನು ಸನ್ಮಾನಿಸಬೇಕೆಂದಿರುವವನನ್ನು ಗೌರವಿಸುವ ರೀತಿಯು ಇದೇ ಎಂದು ಪ್ರಕಟಣೆಮಾಡಿಸಬೇಕು ಎಂದು ಹೇಳಿದನು.",
			"10": "<sup>10</sup> ಕೂಡಲೆ ಅರಸನು ಹಾಮಾನನಿಗೆ - ನೀನು ಹೇಳಿದ ವಸ್ತ್ರಗಳನ್ನೂ ಕುದುರೆಯನ್ನೂ ಬೇಗನೆ ತೆಗೆದುಕೊಂಡು ಬಂದು ಅರಮನೆಯ ಬಾಗಲಿನಲ್ಲಿ ಕೂತಿರುವ ಮೊರ್ದೆಕೈ ಎಂಬ ಯೆಹೂದ್ಯನಿಗೆ ಹಾಗೆಯೇ ಮಾಡು; ನೀನು ಹೇಳಿದವುಗಳಲ್ಲಿ ಒಂದನ್ನೂ ನೆರವೇರಿಸದೆ ಬಿಡಬೇಡ ಎಂದು ಅಪ್ಪಣೆಮಾಡಿದನು.",
			"11": "<sup>11</sup> ಹಾಮಾನನು ಆ ವಸ್ತ್ರಗಳನ್ನೂ ಕುದುರೆಯನ್ನೂ ತೆಗೆದುಕೊಂಡು ಬಂದು ಮೊರ್ದೆಕೈಯನ್ನು ಭೂಷಿಸಿ ಪಟ್ಟಣದ ಮಧ್ಯದಲ್ಲಿ ಮೆರವಣಿಗೆಮಾಡಿಸಿ ಅವನ ಮುಂದೆ - ಅರಸನು ಸನ್ಮಾನಿಸಬೇಕೆಂದಿರುವವನನ್ನು ಗೌರವಿಸುವ ರೀತಿಯು ಇದೇ ಎಂದು ಪ್ರಕಟಣೆ ಮಾಡಿಸಿದನು.",
			"12": "<sup>12</sup> ಮೊರ್ದೆಕೈಯು ತಿರಿಗಿ ಅರಮನೆಯ ಬಾಗಲಿಗೆ ಹೋದನು. ಹಾಮಾನನಾದರೋ ಶೋಕಾರ್ತನಾಗಿ ಮೋರೆಮುಚ್ಚಿಕೊಂಡು ಶೀಘ್ರವಾಗಿ ಮನೆಗೆ ಹೋಗಿ",
			"13": "<sup>13</sup> ತನ್ನ ಹೆಂಡತಿಯಾದ ಜೆರೆಷಳ ಮತ್ತು ಎಲ್ಲಾ ಆಪ್ತರ ಮುಂದೆ ತನಗೆ ಸಂಭವಿಸಿದ್ದನ್ನೆಲ್ಲಾ ತಿಳಿಸಿದನು. ಆಗ ಅವನ ಪಂಡಿತರೂ ಹೆಂಡತಿಯೂ ಅವನಿಗೆ - ಯಾವನ ಮುಂದೆ ನಿನ್ನ ಪತನವು ಪ್ರಾರಂಭವಾಯಿತೋ ಆ ಮನುಷ್ಯನು ಯೆಹೂದಸಂತಾನದವನಾಗಿದ್ದರೆ ನೀನು ಗೆಲ್ಲಲಾರಿ, ಅವನ ಮುಂದೆ ಬಿದ್ದು ಹಾಳಾಗುವಿ ಅಂದರು.",
			"14": "<sup>14</sup> ಅವರು ಇನ್ನೂ ಅವನೊಡನೆ ಮಾತಾಡುತ್ತಿರುವಾಗಲೇ ರಾಜಕಂಚುಕಿಗಳು ಬಂದು ಎಸ್ತೇರಳು ಮಾಡಿಸಿರುವ ಔತಣಕ್ಕೆ ಬೇಗಬರಬೇಕೆಂದು ಹಾಮಾನನನ್ನು ಕರೆದರು."
		},
		"7": {
			"1": "<sup>1</sup> ಅರಸನು ಹಾಮಾನನೊಡನೆ ಎಸ್ತೇರಳ ಗೃಹಕ್ಕೆ ಔತಣಕ್ಕೋಸ್ಕರ ಬಂದನು.",
			"2": "<sup>2</sup> ಅವನು ಈ ಎರಡನೆಯ ದಿನದಲ್ಲಿಯೂ ದ್ರಾಕ್ಷಾರಸಪಾನಮಾಡುತ್ತಿರುವಾಗ ಎಸ್ತೇರಳಿಗೆ - ಎಸ್ತೇರ್‍ರಾಣಿಯೇ, ನಿನ್ನ ವಿಜ್ಞಾಪನೆ ಯಾವದು, ಹೇಳು; ಅದನ್ನು ನೆರವೇರಿಸುವೆನು. ನೀನು ನನ್ನ ಅರ್ಧರಾಜ್ಯವನ್ನು ಕೇಳಿಕೊಂಡರೂ ಸರಿಯೇ, ಅದನ್ನು ನಿನಗೆ ಕೊಡುವೆನು ಎಂದು ಹೇಳಿದನು.",
			"3": "<sup>3</sup> ಆಗ ಎಸ್ತೇರ್‍ರಾಣಿಯು - ಅರಸನು ನನ್ನ ಮೇಲೆ ಕಟಾಕ್ಷವಿಟ್ಟು ಸಮ್ಮತಿಸುವದಾದರೆ ನನ್ನ ವಿಜ್ಞಾಪನೆಯನ್ನೂ ಪ್ರಾರ್ಥನೆಯನ್ನೂ ಲಾಲಿಸಿ ನನ್ನ ಜೀವವನ್ನೂ ಜನಾಂಗವನ್ನೂ ಉಳಿಯಗೊಡಿಸಬೇಕು.",
			"4": "<sup>4</sup> ಜನರು ನಮ್ಮನ್ನು ಕೊಂದು ಸಂಹರಿಸಿ ನಿರ್ನಾಮಗೊಳಿಸುವ ಹಾಗೆ ನಾವು ಮಾರಲ್ಪಟ್ಟೆವು. ದಾಸದಾಸಿಯರಾಗುವದಕ್ಕೆ ಮಾರಲ್ಪಟ್ಟಿದ್ದರೆ ಆ ಕಷ್ಟದ ವಿಷಯದಲ್ಲಿ ರಾಜನನ್ನು ತೊಂದರೆ ಪಡಿಸುವದು ಯೋಗ್ಯವಲ್ಲವೆಂದು ಸುಮ್ಮನಿರುತ್ತಿದ್ದೆನು ಎಂದು ಉತ್ತರಕೊಟ್ಟಳು.",
			"5": "<sup>5</sup> ಅಹಷ್ವೇರೋಷ್‍ರಾಜನು ಎಸ್ತೇರ್‍ರಾಣಿಯನ್ನು - ಇಂಥ ದುಷ್ಕೃತ್ಯದ ಮೇಲೆ ಮನಸ್ಸಿಟ್ಟವನು ಯಾವನು? ಅವನೆಲ್ಲಿ ಎಂದು ಕೇಳಲು",
			"6": "<sup>6</sup> ಆಕೆಯು - ನಮ್ಮನ್ನು ಬಾಧಿಸಬೇಕೆಂದಿರುವ ಹಗೆಗಾರನು ಈ ದುಷ್ಟ ಹಾಮಾನನೇ ಅಂದಳು. ಇದನ್ನು ಕೇಳಿದೊಡನೆ ಹಾಮಾನನು ಅರಸನ ಮತ್ತು ರಾಣಿಯ ಮುಂದೆ ಬೆಪ್ಪಾದನು.",
			"7": "<sup>7</sup> ಅರಸನು ರೌದ್ರಾವೇಶನಾಗಿ ದ್ರಾಕ್ಷಾರಸಪಾನಮಾಡುವದನ್ನು ಬಿಟ್ಟೆದ್ದು ಅರಮನೆಯ ತೋಟಕ್ಕೆ ಹೋದನು. ಹಾಮಾನನಾದರೋ ಅರಸನಿಂದ ತನಗೆ ಕೇಡು ಸಿದ್ಧವಾಯಿತೆಂದು ತಿಳಿದು ಎಸ್ತೇರಳ ಹತ್ತಿರ ತನ್ನ ಪ್ರಾಣ ರಕ್ಷಣೆಯ ನಿಮಿತ್ತವಾಗಿ ಬಿನ್ನಹಮಾಡಲು ನಿಂತುಕೊಂಡನು.",
			"8": "<sup>8</sup> ಅರಸನು ಅರಮನೆಯ ತೋಟದಿಂದ ತಾನು ದ್ರಾಕ್ಷಾರಸಪಾನ ಮಾಡಿದ ಗೃಹಕ್ಕೆ ತಿರಿಗಿ ಬಂದು ಎಸ್ತೇರಳು ಒರಗಿಕೊಳ್ಳುವ ಲೋಡಿನ ಮೇಲೆ ಹಾಮಾನನು ಬಿದ್ದುಕೊಂಡಿರುವದನ್ನು ಕಂಡು - ಇವನು ರಾಣಿಯನ್ನು ನನ್ನ ಮುಂದೆಯೇ ಅರಮನೆಯಲ್ಲಿ ಭಂಗಪಡಿಸಲೂ ಬೇಕೆಂದಿರುತ್ತಾನೋ ಅಂದನು. ಅರಸನ ಬಾಯಿಂದ ಈ ಮಾತು ಹೊರಟ ಕೂಡಲೇ ಸೇವಕರು ಹಾಮಾನನ ಮೋರೆಗೆ ಮುಸುಕು ಹಾಕಿದರು.",
			"9": "<sup>9</sup> ಇದಲ್ಲದೆ ಅರಸನ ಸೇವೆಮಾಡುತ್ತಿದ್ದ ಕಂಚುಕಿಗಳಲ್ಲೊಬ್ಬನಾದ ಹರ್ಬೋನನು - ಇಗೋ ಈ ಹಾಮಾನನ ಮನೆಯ ಹತ್ತಿರ ಅರಸನ ಪ್ರಾಣರಕ್ಷಣೆಗಾಗಿ ಸಮಾಚಾರವನ್ನು ತಿಳಿಸಿದ ಮೊರ್ದೆಕೈಯನ್ನು ನೇತುಹಾಕಿಸುವದಕ್ಕೆ ಇವನಿಂದ ಸಿದ್ಧಮಾಡಲ್ಪಟ್ಟ ಐವತ್ತು ಮೊಳ ಎತ್ತರವಾದ ಗಲ್ಲು ಮರವಿರುತ್ತದಲ್ಲಾ ಅನ್ನಲು ಅರಸನು - ಇವನನ್ನು ಅದಕ್ಕೆ ನೇತುಹಾಕಿರಿ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"10": "<sup>10</sup> ಹಾಮಾನನನ್ನು ಮೊರ್ದೆಕೈಗೋಸ್ಕರ ಸಿದ್ಧಮಾಡಿದ ಗಲ್ಲಿಗೆ ಹಾಕಿದರು. ಅರಸನ ಕೋಪವೂ ಶಾಂತವಾಯಿತು."
		},
		"8": {
			"1": "<sup>1</sup> ಆ ದಿನದಲ್ಲಿ ಅರಸನಾದ ಅಹಷ್ವೇರೋಷನು ಯೆಹೂದ್ಯರ ವೈರಿಯಾದ ಹಾಮಾನನ ಮನೆಯನ್ನು ಎಸ್ತೇರ್‍ರಾಣಿಗೆ ಕೊಟ್ಟನು. ಆಕೆಯು ಮೊರ್ದೆಕೈಗೂ ತನಗೂ ಇರುವ ಸಂಬಂಧವನ್ನು ತಿಳಿಸಿದ್ದರಿಂದ ಅರಸನು ಮೊರ್ದೆಕೈಯನ್ನು ಒಡ್ಡೋಲಗದಲ್ಲಿ ಸೇರಿಸಿ",
			"2": "<sup>2</sup> ತಾನು ಹಾಮಾನನಿಂದ ತೆಗಿಸಿದ್ದ ತನ್ನ ಮುದ್ರೆಯುಂಗರವನ್ನು ಅವನಿಗೆ ಕೊಟ್ಟನು; ಎಸ್ತೇರಳು ಹಾಮಾನನ ಮನೆಯ ಆಡಳಿತವನ್ನು ಅವನಿಗೆ ಒಪ್ಪಿಸಿದಳು.",
			"3": "<sup>3</sup> ಎಸ್ತೇರಳು ತಿರಿಗಿ ಅರಸನನ್ನು ಮಾತಾಡಿಸುವದಕ್ಕೆ ಹೋಗಿ ಅವನ ಪಾದಗಳಿಗೆ ಬಿದ್ದು ಅತ್ತು - ಅಗಾಗನ ವಂಶದವನಾದ ಹಾಮಾನನು ಯೆಹೂದ್ಯರ ಹಾನಿಗಾಗಿ ಕಲ್ಪಿಸಿದ ಅಪಾಯಗಳನ್ನು ನಿವಾರಿಸಬೇಕೆಂದು ವಿಜ್ಞಾಪಿಸಲು",
			"4": "<sup>4</sup> ಅರಸನು ಸುವರ್ಣರಾಜದಂಡವನ್ನು ಆಕೆಯ ಕಡೆಗೆ ಚಾಚಿದ್ದರಿಂದ ಆಕೆಯು ಎದ್ದು ಅರಸನ ಮುಂದೆ ನಿಂತು ಅವನಿಗೆ -",
			"5": "<sup>5</sup> ಅರಸನು ನನ್ನ ಮೇಲೆ ಕಟಾಕ್ಷವಿಟ್ಟು ನನ್ನನ್ನು ಮೆಚ್ಚಿ ನಾನು ಹೇಳುವ ಮಾತು ಉಚಿತವಾದದ್ದೆಂದೆಣಿಸಿ ಅದಕ್ಕೆ ಸಮ್ಮತಿಸುವದಾದರೆ ಅರಸನ ಎಲ್ಲಾ ಸಂಸ್ಥಾನಗಳಲ್ಲಿರುವ ಯೆಹೂದ್ಯರನ್ನು ಸಂಹರಿಸುವದರ ವಿಷಯವಾಗಿ ಅಗಾಗನ ವಂಶದವನೂ ಹಮ್ಮೆದಾತನ ಮಗನೂ ಆದ ಹಾಮಾನನು ಬರಿಸಿದ ಪತ್ರಗಳನ್ನು ರದ್ದುಮಾಡುವದಕ್ಕಾಗಿ ರಾಯಸವನ್ನು ಪ್ರಕಟಿಸಬೇಕು.",
			"6": "<sup>6</sup> ನನ್ನ ಜನರಿಗೆ ಕೇಡು ಸಂಭವಿಸುವದನ್ನು ನೋಡಿ ನಾನು ಸಹಿಸುವದು ಹೇಗೆ? ನನ್ನ ಕುಲನಾಶನವನ್ನು ನೋಡುತ್ತಾ ಸುಮ್ಮನಿರುವದು ಹೇಗೆ ಎಂದು ಬಿನ್ನವಿಸಿದಳು.",
			"7": "<sup>7</sup> ಆಗ ಅಹಷ್ವೇರೋಷ್ ರಾಜನು ರಾಣಿಯಾದ ಎಸ್ತೇರಳಿಗೂ ಯೆಹೂದ್ಯನಾದ ಮೊರ್ದೆಕೈಗೂ - ಹಾಮಾನನು ಯೆಹೂದ್ಯರಿಗೆ ವಿರೋಧವಾಗಿ ಕೈಯೆತ್ತಿದದರಿಂದ ಅವನನ್ನು ಗಲ್ಲಿಗೆ ಹಾಕಿಸಿ ಅವನ ಸೊತ್ತನ್ನು ಎಸ್ತೇರಳಿಗೆ ದಾನಮಾಡಿದ್ದೇನಲ್ಲಾ!",
			"8": "<sup>8</sup> ಅರಸನ ಹೆಸರಿನಲ್ಲಿ ಬರೆಯಲ್ಪಟ್ಟ ರಾಜಮುದ್ರೆಯಿರುವ ಲೇಖನವನ್ನು ಯಾರೂ ರದ್ದುಮಾಡಕೂಡದಷ್ಟೆ. ಹೀಗಿರುವದರಿಂದ ಯೆಹೂದ್ಯರ ವಿಷಯವಾಗಿ ನಿಮಗೆ ಸರಿತೋರುವದನ್ನು ಅರಸನ ಹೆಸರಿನಲ್ಲಿ ನೀವು ಬರೆಯಿಸಿ ರಾಜ ಮುದ್ರೆಯನ್ನು ಹಾಕಿರಿ ಎಂದು ಹೇಳಿದನು.",
			"9": "<sup>9</sup> ಆಗ ರಾಜಲೇಖಕರು ಕರೆಯಲ್ಪಟ್ಟರು. ಅವರು ಮೂರನೆಯ ತಿಂಗಳಾದ ಜೇಷ್ಠಮಾಸದ ಇಪ್ಪತ್ತ ಮೂರನೆಯ ದಿನದಲ್ಲಿ ಬಂದು ಮೊರ್ದೆಕೈಯ ಆಜ್ಞಾನುಸಾರ ಯೆಹೂದ್ಯರಿಗೂ ಹಿಂದುಸ್ಥಾನ ಮೊದಲುಗೊಂಡು ಕೂಷಿನವರೆಗೂ ಇರುವ ನೂರಿಪ್ಪತ್ತೇಳು ಸಂಸ್ಥಾನಗಳ ಉಪರಾಜರಿಗೂ ದೇಶಾಧಿಪತಿಗಳಿಗೂ ಅಧಿಕಾರಿಗಳಿಗೂ ಪತ್ರಗಳನ್ನು ಬರೆದರು. ಆ ಪತ್ರಗಳು ಆಯಾ ಸಂಸ್ಥಾನಗಳ ಬರಹಗಳಲ್ಲಿಯೂ ಆಯಾ ಜನಾಂಗಗಳ ಭಾಷೆಗಳಲ್ಲಿಯೂ ಇದ್ದವು. ಯೆಹೂದ್ಯರಿಗೆ ಬರೆದ ಪತ್ರಗಳು ಯೆಹೂದ್ಯ ಬರಹದಲ್ಲಿಯೂ ಭಾಷೆಯಲ್ಲಿಯೂ ಲಿಖಿತವಾದವು.",
			"10": "<sup>10</sup> ಮೊರ್ದೆಕೈಯು ಅರಸನ ಹೆಸರಿನಲ್ಲಿ ಬರೆಯಲ್ಪಟ್ಟ ಅವುಗಳಿಗೆ ರಾಜಮುದ್ರೆಯನ್ನು ಹಾಕಿ ಅರಮನೆಯ ಅಶ್ವಾಲಯಗಳಲ್ಲಿ ಹುಟ್ಟಿ ಬೆಳೆದ ಸವಾರಿಕುದುರೆಗಳನ್ನು ಹತ್ತಿಕೊಂಡಿದ್ದ ಅಂಚೆಯವರ ಮುಖಾಂತರ ಕಳುಹಿಸಿದನು.",
			"11": "<sup>11</sup> ಆ ಪತ್ರಗಳಿಂದ ಅರಸನು - ಅಹಷ್ವೇರೋಷ್‍ರಾಜನ ಎಲ್ಲಾ ಸಂಸ್ಥಾನಗಳ ಆಯಾ ಪಟ್ಟಣಗಳಲ್ಲಿರುವ ಯೆಹೂದ್ಯರು ಒಂದೇ ದಿನದಲ್ಲಿ ಅಂದರೆ ಹನ್ನೆರಡನೆಯ ತಿಂಗಳಾದ ಫಾಲ್ಗುನಮಾಸದ",
			"12": "<sup>12</sup> ಹದಿಮೂರನೆಯ ದಿನದಲ್ಲಿ ಕೂಡಿಕೊಂಡು ತಮ್ಮ ಪ್ರಾಣರಕ್ಷಣೆಗಾಗಿ ಎದ್ದು ತಮಗೆ ವಿರೋಧವಾಗಿ ಆಯುಧಗಳೊಡನೆ ನೆರೆದು ಬರುವ ಎಲ್ಲಾ ಜನಾಂಗಗಳವರನ್ನೂ ಸಂಸ್ಥಾನಗಳವರನ್ನೂ ಅವರ ಹೆಂಡತಿಯರು ಮಕ್ಕಳು ಸಹಿತವಾಗಿ ಕೊಂದು ಸಂಹರಿಸಿ ನಿರ್ನಾಮಗೊಳಿಸಿ ಅವರ ಸೊತ್ತನ್ನು ಸೂರೆಮಾಡಬೇಕೆಂದೂ",
			"13": "<sup>13</sup> ಈ ಪತ್ರದ ಒಂದೊಂದು ಪ್ರತಿಯನ್ನು ಪ್ರತಿಯೊಂದು ಸಂಸ್ಥಾನದಲ್ಲಿ ರಾಜವಿಧಿಯೆಂದು ಎಲ್ಲಾ ಜನರಲ್ಲಿ ಪ್ರಕಟಿಸಬೇಕೆಂದೂ ಆ ದಿನದಲ್ಲಿ ಯೆಹೂದ್ಯರು ತಮ್ಮ ಶತ್ರುಗಳಿಗೆ ಮುಯ್ಯಿತೀರಿಸುವದಕ್ಕೆ ಸಿದ್ಧರಾಗಿರಬೇಕೆಂದೂ ಅಪ್ಪಣೆಮಾಡಿದನು.",
			"14": "<sup>14</sup> ಈ ರಾಜಾಜ್ಞೆಯು ಶೂಷನ್ ಕೋಟೆಯಲ್ಲಿ ಪ್ರಕಟವಾದಾಗಲೇ ಅರಮನೆಯ ಸವಾರಿ ಕುದುರೆಗಳನ್ನು ಹತ್ತಿಕೊಂಡಿದ್ದ ಅಂಚೆಯವರು ಅರಸನ ಅಪ್ಪಣೆಯಿಂದ ಪ್ರೇರಿತರಾಗಿ ಅತಿಶೀಘ್ರವಾಗಿ ಹೊರಟರು.",
			"15": "<sup>15</sup> ಮೊರ್ದೆಕೈಯಾದರೋ ನೀಲ ಶುಭ್ರವರ್ಣಗಳುಳ್ಳ ರಾಜವಸ್ತ್ರಗಳನ್ನು ಧರಿಸಿ ಬಂಗಾರದ ದೊಡ್ಡ ಪಟ್ಟಿಯನ್ನು ಹಣೆಗೆ ಕಟ್ಟಿಕೊಂಡು ರಕ್ತವರ್ಣದ ನಾರಿನ ಶಾಲನ್ನು ಹೊದ್ದು ರಾಜಸನ್ನಿಧಿಯಿಂದ ಹೊರಟನು. ಶೂಷನ್ ಪಟ್ಟಣದಲ್ಲಿ ಸಂತೋಷದ ಆರ್ಭಟವು ಕೇಳಿಸಿತು.",
			"16": "<sup>16</sup> ಅಲ್ಲಿನ ಯೆಹೂದ್ಯರು ಸಂತೋಷ ಹರ್ಷ ಪ್ರಭಾವ ತೇಜಸ್ಸುಗಳುಳ್ಳವರಾದರು.",
			"17": "<sup>17</sup> ರಾಜನಿರ್ಣಯ ಶಾಸನಗಳು ಪ್ರಕಟಿಸಲ್ಪಟ್ಟ ಪ್ರತಿಯೊಂದು ಸಂಸ್ಥಾನದಲ್ಲಿಯೂ ಪಟ್ಟಣದಲ್ಲಿಯೂ ಇದ್ದ ಯೆಹೂದ್ಯರಿಗೆ ಶುಭದಿನವುಂಟಾಯಿತು; ಅವರು ಸಂತೋಷದಿಂದಲೂ ಉಲ್ಲಾಸದಿಂದಲೂ ಭಕ್ಷ್ಯಭೋಜನಮಾಡಿದರು. ದೇಶದ ಜನರಲ್ಲಿ ಅನೇಕರು ಯೆಹೂದ್ಯರಿಗೆ ಭಯಪಟ್ಟು ಅವರ ಮತಕ್ಕೆ ಸೇರಿದರು."
		},
		"9": {
			"1": "<sup>1</sup> ಅರಸನ ಆಜ್ಞಾನಿರ್ಣಯಗಳನ್ನು ನೆರವೇರಿಸತಕ್ಕ ಹನ್ನೆರಡನೆಯ ತಿಂಗಳಾದ ಪಾಲ್ಗುನಮಾಸದ ಹದಿಮೂರನೆಯ ದಿನವು ಬಂತು. ಆ ದಿವಸದಲ್ಲಿ ಯೆಹೂದ್ಯರನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳಬಹುದೆಂದು ಅವರ ವೈರಿಗಳು ನಿರೀಕ್ಷಿಸಿಕೊಂಡಿದ್ದರು. ಆದರೆ ಅದಕ್ಕೆ ಬದಲಾಗಿ ಯೆಹೂದ್ಯರೇ ತಮ್ಮ ಹಗೆಗಳನ್ನು ಸ್ವಾಧೀನ ಮಾಡಿಕೊಂಡರು.",
			"2": "<sup>2</sup> ಆದದರಿಂದ ಅರಸನಾದ ಅಹಷ್ವೇರೋಷನ ಸಮಸ್ತಸಂಸ್ಥಾನಗಳ ಆಯಾ ಪಟ್ಟಣಗಳಲ್ಲಿದ್ದ ಯೆಹೂದ್ಯರು ತಮಗೆ ಕೇಡುಬಯಸಿದವರಿಗೆ ವಿರೋಧವಾಗಿ ಕೈಯೆತ್ತುವದಕ್ಕೆ ಕೂಡಿಕೊಂಡಾಗ ಯಾರೂ ಅವರೆದುರಿನಲ್ಲಿ ನಿಲ್ಲಲಿಲ್ಲ.",
			"3": "<sup>3</sup> ಅವರಿಂದ ಎಲ್ಲಾ ಜನಾಂಗದವರಿಗೆ ಭಯವುಂಟಾಗಿತ್ತು; ಇದಲ್ಲದೆ ಅರಮನೆಯಲ್ಲಿ ಮೊರ್ದೆಕೈಯ ಪ್ರಾಬಲ್ಯವು ಹೆಚ್ಚಾಗುತ್ತಾ ಬಂದಹಾಗೆಲ್ಲಾ ಅವನ ಸುದ್ದಿಯೂ ಸಕಲ ಸಂಸ್ಥಾನಗಳಲ್ಲಿಯೂ ಹಬ್ಬಿದ್ದರಿಂದ",
			"4": "<sup>4</sup> ಸರ್ವಸಂಸ್ಥಾನಾಧಿಕಾರಿಗಳೂ ಉಪರಾಜರೂ ದೇಶಾಧಿಪತಿಗಳೂ ಇತರ ರಾಜೋದ್ಯೋಗಸ್ಥರೂ ಅವನಿಗೆ ಹೆದರಿ ಯೆಹೂದ್ಯರಿಗೆ ಸಹಾಯಕರಾದರು.",
			"5": "<sup>5</sup> ಹೀಗೆ ಯೆಹೂದ್ಯರು ತಮ್ಮ ಎಲ್ಲಾ ವೈರಿಗಳನ್ನು ಸೋಲಿಸಿ ಕತ್ತಿಯಿಂದ ಹೊಡೆದು ಕೊಂದು ನಿರ್ನಾಮಗೊಳಿಸಿದರು; ತಮ್ಮ ಹಗೆಗಳನ್ನು ಇಷ್ಟವಿದ್ದಂತೆ ಮಾಡಿದರು.",
			"6": "<sup>6</sup> ಅವರು ಶೂಷನ್ ಕೋಟೆಯಲ್ಲೇ ಐನೂರು ಮಂದಿಯನ್ನು ಕೊಂದುಬಿಟ್ಟರು.",
			"7": "<sup>7</sup> ಹತರಾದವರಲ್ಲಿ ಪರ್ಷಂದಾತ, ದಲ್ಫೋನ್, ಅಸ್ಪಾತ,",
			"8": "<sup>8</sup> ಪೋರಾತ, ಅದಲ್ಯ, ಅರೀದಾತ, ಪರ್ಮಷ್ಟ, ಅರೀಸೈ,",
			"9": "<sup>9</sup> ಅರಿದೈ, ವೈಜಾತ ಎಂಬವರಿದ್ದರು.",
			"10": "<sup>10</sup> ಇವರು ಯೆಹೂದ್ಯರನ್ನು ದ್ವೇಷಿಸುತ್ತಿದ್ದ ಹಮ್ಮೆದಾತನ ಮಗನಾದ ಹಾಮಾನನ ಹತ್ತು ಮಂದಿ ಮಕ್ಕಳು. ಇಷ್ಟು ಮಂದಿಯನ್ನು ಕೊಂದರೂ ಯೆಹೂದ್ಯರು ಸುಲಿಗೆಗೆ ಕೈಹಾಕಲಿಲ್ಲ.",
			"11": "<sup>11</sup> ಆ ದಿವಸ ಶೂಷನ್ ಕೋಟೆಯಲ್ಲಿ ಹತರಾದವರ ಸಂಖ್ಯೆಯು ಅರಸನಿಗೆ ತಿಳಿದುಬಂದಾಗ",
			"12": "<sup>12</sup> ಅರಸನು ಎಸ್ತೇರ್‍ರಾಣಿಗೆ - ಯೆಹೂದ್ಯರು ಶೂಷನ್‍ಕೋಟೆಯಲ್ಲೇ ಐನೂರು ಮಂದಿಯನ್ನೂ ಹಾಮಾನನ ಹತ್ತು ಮಂದಿ ಮಕ್ಕಳನ್ನೂ ಸಂಹರಿಸಿಬಿಟ್ಟಿದ್ದಾರೆ; ಉಳಿದ ರಾಜಸಂಸ್ಥಾನಗಳಲ್ಲಿ ಎಷ್ಟೋ ಮಂದಿ ಹತರಾಗಿರಬಹುದು. ನಿನ್ನ ವಿಜ್ಞಾಪನೆ ಇನ್ನು ಯಾವದಿದ್ದರೂ ನೆರವೇರುವದು, ನೀನು ಏನು ಕೇಳಿಕೊಂಡರೂ ಕೊಡುವೆನು ಎಂದು ಹೇಳಿದನು.",
			"13": "<sup>13</sup> ಅದಕ್ಕೆ ಎಸ್ತೇರಳು - ಅರಸನ ಇಷ್ಟವಿದ್ದರೆ ನಾಳೆಯೂ ಇಂದಿನ ರಾಜಾಜ್ಞೆಯ ಪ್ರಕಾರ ಮಾಡುವದಕ್ಕೂ ಹಾಮಾನನ ಹತ್ತು ಮಂದಿ ಮಕ್ಕಳ ಶವಗಳನ್ನು ಗಲ್ಲಿಗೆ ಹಾಕುವದಕ್ಕೂ ಯೆಹೂದ್ಯರಿಗೆ ಅಪ್ಪಣೆಯಾಗಬೇಕು ಎಂದು ಬೇಡಿಕೊಳ್ಳಲು ಅರಸನು ಹಾಗೆ ಮಾಡುವದಕ್ಕೆ ಅಪ್ಪಣೆಕೊಟ್ಟನು.",
			"14": "<sup>14</sup> ಕೂಡಲೆ ಈ ಸಂಬಂಧವಾದ ರಾಜಾಜ್ಞೆಯು ಶೂಷನ್‍ಕೋಟೆಯಲ್ಲಿ ಪ್ರಕಟವಾಯಿತು. ಮತ್ತು ಹಾಮಾನನ ಹತ್ತು ಮಂದಿ ಮಕ್ಕಳನ್ನು ಗಲ್ಲಿಗೆ ಹಾಕಿದರು.",
			"15": "<sup>15</sup> ಶೂಷನಿನ ಯೆಹೂದ್ಯರು ಫಾಲ್ಗುನಮಾಸದ ಹದಿನಾಲ್ಕನೆಯ ದಿನದಲ್ಲೂ ಕೂಡಿಕೊಂಡು ಅಲ್ಲಿ ಮುನ್ನೂರು ಮಂದಿಯನ್ನು ಕೊಂದರು. ಆದರೆ ಕೊಳ್ಳೆಗೆ ಕೈಹಾಕಲಿಲ್ಲ.",
			"16": "<sup>16</sup> ಅದೇ ಮೇರೆಗೆ ರಾಜಸಂಸ್ಥಾನಗಳಲ್ಲಿದ್ದ ಯೆಹೂದ್ಯರು ತಮ್ಮ ವಿರೋಧಿಗಳ ಕಾಟವನ್ನು ನಿಲ್ಲಿಸಿ ಪ್ರಾಣರಕ್ಷಿಸಿಕೊಳ್ಳುವದಕ್ಕಾಗಿ ಕೂಡಿಕೊಂಡು ಎದ್ದು ತಮ್ಮ ಹಗೆಗಳಲ್ಲಿ ಎಪ್ಪತ್ತೈದುಸಾವಿರ ಮಂದಿಯನ್ನು ಸಂಹರಿಸಿದರು; ಆದರೆ ಸುಲಿಗೆಮಾಡುವದಕ್ಕೆ ಕೈ ಹಾಕಲಿಲ್ಲ.",
			"17": "<sup>17</sup> ಇದು ಫಾಲ್ಗುನಮಾಸದ ಹದಿಮೂರನೆಯ ದಿನದಲ್ಲಿ ನಡೆಯಿತು. ಅವರು ಹದಿನಾಲ್ಕನೆಯ ದಿನದಲ್ಲಿ ವಿಶ್ರಮಿಸಿಕೊಂಡು ಆ ದಿವಸವನ್ನು ಉತ್ಸವಭೋಜನ ದಿನವನ್ನಾಗಿ ಆಚರಿಸಿದರು.",
			"18": "<sup>18</sup> ಆದರೆ ಶೂಷನಿನ ಯೆಹೂದ್ಯರು ಹದಿಮೂರನೆಯ ಮತ್ತು ಹದಿನಾಲ್ಕನೆಯ ದಿನಗಳಲ್ಲಿ [ವೈರಿಗಳನ್ನು ವಧಿಸುವದಕ್ಕೆ] ಕೂಡಿಕೊಂಡಿದ್ದದರಿಂದ ಹದಿನೈದನೆಯ ದಿನದಲ್ಲಿ ವಿಶ್ರಮಿಸಿಕೊಂಡು ಆ ದಿನವನ್ನು ಉತ್ಸವಭೋಜನದಿನವನ್ನಾಗಿ ಆಚರಿಸಿದರು.",
			"19": "<sup>19</sup> ಆದದರಿಂದ ಪೌಳಿಗೋಡೆಯಿಲ್ಲದ ಹಳ್ಳಿಪಳ್ಳಿಯ ಯೆಹೂದ್ಯರು ಫಾಲ್ಗುನಮಾಸದ ಹದಿನಾಲ್ಕನೆಯ ದಿನವನ್ನೂ ನಗರಗಳ ಯೆಹೂದ್ಯರು ಹದಿನೈದನೆಯ ದಿನವನ್ನೂ ಶುಭದಿವಸವೆಂದು ಆಚರಿಸಿ ಉತ್ಸವಭೋಜನಮಾಡಿ ಒಬ್ಬರಿಗೊಬ್ಬರು ಭೋಜನಭಾಗಗಳನ್ನು ಕಳುಹಿಸುತ್ತಾರೆ.",
			"20": "<sup>20</sup> ಮೊರ್ದೆಕೈಯು ಸಮೀಪದಲ್ಲಿಯೂ ದೂರದಲ್ಲಿಯೂ ಇದ್ದ ಅಹಷ್ವೇರೋಷನ ಎಲ್ಲಾ ಸಂಸ್ಥಾನಗಳ ಯೆಹೂದ್ಯರಿಗೆ ಪತ್ರಗಳ ಮುಖಾಂತರ ತಿಳಿಯಪಡಿಸಿದ ಸಂಗತಿಗಳು -",
			"21": "<sup>21</sup> ಪ್ರತಿವರುಷವೂ ಫಾಲ್ಗುನಮಾಸದ ಹದಿನಾಲ್ಕನೆಯ ಮತ್ತು ಹದಿನೈದನೆಯ ದಿನಗಳನ್ನು ಉತ್ಸವದಿನಗಳನ್ನಾಗಿ ಆಚರಿಸುವದು ಶಾಶ್ವತನಿಯಮ ಎಂದೆಣಿಸಬೇಕು.",
			"22": "<sup>22</sup> ಆ ದಿನಗಳಲ್ಲಿ ಯೆಹೂದ್ಯರಿಗೆ ಶತ್ರುಪೀಡೆ ತಪ್ಪಿ ವಿಶ್ರಾಂತಿಯುಂಟಾಯಿತು; ಆ ತಿಂಗಳಿನಲ್ಲಿ ಸಂತಾಪವು ಪರಿಹಾರವಾಗಿ ಅವರಿಗೆ ಸಂತೋಷವುಂಟಾಯಿತು; ದುಃಖವು ಹೋಗಿ ಸುಖಕಾಲವು ಬಂತು. ಆದದರಿಂದ ಅವರು ಆ ದಿನಗಳಲ್ಲಿ ಉತ್ಸವಭೋಜನ ಮಾಡಿ ಒಬ್ಬರಿಗೊಬ್ಬರು ಭೋಜನಭಾಗಗಳನ್ನು ಕಳುಹಿಸಿ ಬಡವರಿಗೆ ದಾನಧರ್ಮಮಾಡಬೇಕು.",
			"23": "<sup>23</sup> ಯೆಹೂದ್ಯರು ಮೊರ್ದೆಕೈಯ ಪತ್ರದಲ್ಲಿ ನಿರೂಪಿತವಾದದ್ದನ್ನು ಸ್ವೀಕರಿಸಿ ಆ ವರುಷದಲ್ಲಿ ನಡೆದ ಉತ್ಸವವು ತಮ್ಮಲ್ಲಿ ವಾರ್ಷಿಕೋತ್ಸವವಾಗಬೇಕೆಂದು ಗೊತ್ತುಮಾಡಿಕೊಂಡರು.",
			"24": "<sup>24</sup> ಎಲ್ಲಾ ಯೆಹೂದ್ಯರನ್ನು ದ್ವೇಷಿಸುತ್ತಿದ್ದ ಅಗಾಗನ ವಂಶದವನೂ ಹಮ್ಮೆದಾತನ ಮಗನೂ ಆದ ಹಾಮಾನನು ಯೆಹೂದ್ಯರನ್ನು ಸಂಹರಿಸಬೇಕೆಂದು ನಿರ್ಣಯಿಸಿಕೊಂಡು ಅವರನ್ನು ತಳಮಳಗೊಳಿಸಿ ಹಾಳು ಮಾಡುವದರ ವಿಷಯವಾಗಿ ಪೂರನ್ನು ಅಂದರೆ ಚೀಟನ್ನು ಹಾಕಿಸಿದನಲ್ಲಾ.",
			"25": "<sup>25</sup> (ಇದು ಅರಸನ ಮುಂದೆ ಬಂದಾಗ ಅವನು - ಹಾಮಾನನು ಯೆಹೂದ್ಯರಿಗೆ ವಿರೋಧವಾಗಿ ಕಲ್ಪಿಸಿದ ಕುತಂತ್ರವು ಅವನ ತಲೆಯ ಮೇಲೆಯೇ ಬರಲಿ; ಅವನನ್ನೂ ಅವನ ಮಕ್ಕಳನ್ನೂ ಗಲ್ಲಿಗೆ ಹಾಕಬೇಕೆಂದು ಅಪ್ಪಣೆ ಮಾಡಿದನಷ್ಟೆ. )",
			"26": "<sup>26</sup> ಆ ಪೂರ್ ಎಂಬ ಶಬ್ದದ ಆಧಾರದಿಂದ ಆ ದಿನಗಳಿಗೆ ಪೂರೀಮ್ ಎಂಬ ಹೆಸರಾಯಿತು. ಯೆಹೂದ್ಯರು ಆ ಪತ್ರದ ಮಾತುಗಳನ್ನೂ ಅವುಗಳ ಸಂಬಂಧವಾಗಿ ತಾವೇ ಅನುಭವಿಸಿದ್ದನ್ನೂ ತಮಗೆ ಸಂಭವಿಸಿದ್ದನ್ನೂ ಮನಸ್ಸಿಗೆ ತೆಗೆದುಕೊಂಡು -",
			"27": "<sup>27</sup> ಪ್ರತಿವರುಷವೂ ಆ ಎರಡು ದಿವಸಗಳನ್ನು ಅವುಗಳ ವಿಷಯವಾದ ಶಾಸನದ ಪ್ರಕಾರ ನೇಮಿತವಾದ ಕಾಲದಲ್ಲಿ ಆಚರಿಸುವದು ತಮ್ಮಲ್ಲಿಯೂ ತಮ್ಮ ಸಂತಾನದವರಲ್ಲಿಯೂ ತಮ್ಮೊಂದಿಗೆ ಕೂಡಿಕೊಳ್ಳುವವರಲ್ಲಿಯೂ ಮೀರಕೂಡದ ಪದ್ಧತಿನಿಯಮಗಳಾಗಬೇಕೆಂದೂ",
			"28": "<sup>28</sup> ಯುಗಯುಗಾಂತರಗಳಲ್ಲಿ ಎಲ್ಲಾ ಗೋತ್ರಸಂಸ್ಥಾನ ನಗರಗಳವರು ಈ ದಿನಗಳನ್ನು ನೆನಪುಮಾಡಿಕೊಂಡು ಆಚರಿಸಬೇಕೆಂದೂ ಈ ಪೂರೀಮ್ ಹಬ್ಬವು ಯೆಹೂದ್ಯರಲ್ಲಿ ಎಂದೂ ನಿಂತುಹೋಗಬಾರದು, ಅದರ ಜ್ಞಾಪಕವು ಅವರ ಸಂತಾನದವರಲ್ಲಿ ಅಳಿದುಹೋಗಲೇಬಾರದು ಎಂದೂ ಗೊತ್ತುಮಾಡಿಕೊಂಡರು.",
			"29": "<sup>29</sup> ಇದಲ್ಲದೆ ಅಬೀಹೈಲನ ಮಗಳಾದ ಎಸ್ತೇರ್‍ರಾಣಿಯು ಅಧಿಕಾರಯುಕ್ತಳಾಗಿ ಪೂರೀಮ್ ಸಂಬಂಧವಾದ ಆ ಎರಡನೆಯ ಶಾಸನವನ್ನು ದೃಢಪಡಿಸುವದಕ್ಕೋಸ್ಕರ ಯೆಹೂದ್ಯನಾದ ಮೊರ್ದೆಕೈಯ ಸಹಾಯದಿಂದ ಪತ್ರಗಳನ್ನು ಬರೆದಳು.",
			"30": "<sup>30</sup> ಅವನು ಅಹಷ್ವೇರೋಷನ ರಾಜ್ಯದ ನೂರಿಪ್ಪತ್ತೇಳು ಸಂಸ್ಥಾನಗಳಲ್ಲಿದ್ದ ಯೆಹೂದ್ಯರೆಲ್ಲರಿಗೂ ಕಳುಹಿಸಿದನು.",
			"31": "<sup>31</sup> ಆಕೆಯು ದಯಾಸತ್ಯತೆಗಳುಳ್ಳ ಮಾತುಗಳಿಂದ ಆ ಪತ್ರಗಳಲ್ಲಿ - ಯೆಹೂದ್ಯರು ತಮ್ಮಲ್ಲಿಯೂ ತಮ್ಮ ಸಂತಾನದವರಲ್ಲಿಯೂ ಉಪವಾಸಪ್ರಲಾಪ ದಿನಗಳನ್ನು ಪ್ರತಿವರುಷ ತಪ್ಪದೆ ಆಚರಿಸಬೇಕೆಂದು ತಾವೇ ಗೊತ್ತುಮಾಡಿಕೊಂಡ ಪ್ರಕಾರ ಈ ಪೂರೀಮ್ ದಿನಗಳನ್ನೂ ನನ್ನ ಮತ್ತು ಯೆಹೂದ್ಯನಾದ ಮೊರ್ದೆಕೈಯ ನಿರೂಪದಂತೆ ಪ್ರತಿವರುಷ ತಪ್ಪದೆ ನೇಮಿತವಾದ ಕಾಲದಲ್ಲಿ ಆಚರಿಸಬೇಕು ಎಂದು ಬರೆದಿದ್ದಳು.",
			"32": "<sup>32</sup> ಎಸ್ತೇರಳ ಆ ನಿರೂಪವು ಪೂರೀಮ್ ಹಬ್ಬವನ್ನು ಸ್ಥಾಪಿಸಿತು. ಆ ನಿರೂಪವು ಗ್ರಂಥದಲ್ಲಿ ಬರೆಯಲ್ಪಟ್ಟಿತು."
		},
		"10": {
			"1": "<sup>1</sup> ಅಹಷ್ವೇರೋಷ್ ರಾಜನು ತನ್ನ ರಾಜ್ಯಕ್ಕೆ ಸೇರಿರುವ ದೇಶಗಳಲ್ಲಿಯೂ ಸಮುದ್ರದ್ವೀಪಗಳಲ್ಲಿಯೂ ತೆರಿಗೆಯನ್ನು ಎತ್ತಿದನು.",
			"2": "<sup>2</sup> ಅವನ ಬಲ ಪ್ರತಾಪಯುಕ್ತವಾದ ಕೃತ್ಯಗಳೂ ಅವನ ಅನುಗ್ರಹದಿಂದ ಮೊರ್ದೆಕೈಗೆ ಪ್ರಾಪ್ತವಾದ ದೊಡ್ಡಸ್ತಿಕೆಯ ವಿವರವೂ ಮೇದ್ಯ ಮತ್ತು ಪಾರಸಿಯ ರಾಜಕಾಲ ವೃತ್ತಾಂತಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"3": "<sup>3</sup> ಯೆಹೂದ್ಯನಾದ ಆ ಮೊರ್ದೆಕೈಯಾದರೋ ರಾಜ್ಯದಲ್ಲಿ ಅರಸನಾದ ಅಹಷ್ವೇರೋಷನಿಗೆ ದ್ವಿತೀಯಸ್ಥಾನದವನೂ ಯೆಹೂದ್ಯರಲ್ಲಿ ಸನ್ಮಾನಿತನೂ ತನ್ನ ಬಂಧುಜನ ಸಮೂಹಕ್ಕೆ ಪ್ರೀತಿಪಾತ್ರನೂ ಸ್ವಜನರ ಹಿತಚಿಂತಕನೂ ಸ್ವಕುಲದವರೆಲ್ಲರಿಗೋಸ್ಕರ ಸುಖಪ್ರಾರ್ಥನಾಪರನೂ ಆಗಿದ್ದನು."
		}
	}
};
module.exports = book;