var book = {
	"name": "ಕೀರ್ತನೆಗಳು",
	"numChapters": 150,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ಯಾವನು ದುಷ್ಟರ ಆಲೋಚನೆಯಂತೆ ನಡೆಯದೆ ಪಾಪಾತ್ಮರ ಮಾರ್ಗದಲ್ಲಿ ನಿಂತುಕೊಳ್ಳದೆ ಧರ್ಮನಿಂದಕರೊಡನೆ ಕೂತುಕೊಳ್ಳದೆ",
			"2": "<sup>2</sup> ಯೆಹೋವನ ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿ ಆನಂದ ಪಡುವವನಾಗಿ ಅದನ್ನೇ ಹಗಲಿರುಳು ಧ್ಯಾನಿಸುತ್ತಿರುವನೋ ಅವನು ಎಷ್ಟೋ ಧನ್ಯನು.",
			"3": "<sup>3</sup> ಅವನು ನೀರಿನ ಕಾಲುವೆಗಳ ಬಳಿಯಲ್ಲಿ ಬೆಳೆದಿರುವ ಮರದ ಹಾಗಿರುವನು. ಅಂಥ ಮರವು ತಕ್ಕ ಕಾಲದಲ್ಲಿ ಫಲಕೊಡುತ್ತದಲ್ಲಾ; ಅದರ ಎಲೆ ಬಾಡುವದೇ ಇಲ್ಲ. ಅದರಂತೆ ಅವನ ಕಾರ್ಯವೆಲ್ಲವೂ ಸಫಲವಾಗುವದು.",
			"4": "<sup>4</sup> ದುಷ್ಟರೋ ಹಾಗಲ್ಲ; ಗಾಳಿ ಬಡುಕೊಂಡು ಹೋಗುವ ಹೊಟ್ಟಿನಂತಿದ್ದಾರೆ.",
			"5": "<sup>5</sup> ಆದದರಿಂದ ದುಷ್ಟರು ನ್ಯಾಯವಿಚಾರಣೆಯಲ್ಲೂ ಪಾಪಾತ್ಮರು ನೀತಿವಂತರ ಸಭೆಯಲ್ಲೂ ನಿಲ್ಲುವದಿಲ್ಲ.",
			"6": "<sup>6</sup> ಯೆಹೋವನು ನೀತಿವಂತರ ಮಾರ್ಗವನ್ನು ಲಕ್ಷಿಸುವನು; ದುಷ್ಟರ ಮಾರ್ಗವೋ ನಾಶವಾಗುವದು."
		},
		"2": {
			"1": "<sup>1</sup> ಅನ್ಯಜನಗಳು ದೊಂಬಿಮಾಡುವದೂ ಜನಾಂಗಗಳವರು ವ್ಯರ್ಥಕಾರ್ಯಗಳನ್ನು ಯೋಚಿಸುವದೂ ಯಾಕೆ?",
			"2": "<sup>2</sup> ಯೆಹೋವನಿಗೂ ಆತನು ಅಭಿಷೇಕಿಸಿದವನಿಗೂ ವಿರೋಧವಾಗಿ ಭೂಪತಿಗಳು ಸನ್ನದ್ಧರಾಗಿ ನಿಂತಿದ್ದಾರೆ, ಅಧಿಕಾರಿಗಳು ಒಟ್ಟಾಗಿ ಸೇರಿ ಆಲೋಚಿಸುತ್ತಿದ್ದಾರೆ.",
			"3": "<sup>3</sup> ನಮಗೆ ಅವರು ಹಾಕಿದ ಬಂಧನಗಳನ್ನು ಕಿತ್ತು ಬೇಡಿಗಳನ್ನು ಮುರಿದು ಬೀಸಾಡೋಣ ಎಂದು ಮಾತಾಡಿಕೊಳ್ಳುತ್ತಾರಲ್ಲಾ.",
			"4": "<sup>4</sup> ಪರಲೋಕದಲ್ಲಿ ಆಸನಾರೂಢನಾಗಿರುವಾತನು ಅದಕ್ಕೆ ನಗುವನು; ಕರ್ತನು ಅವರನ್ನು ಪರಿಹಾಸ್ಯಮಾಡುವನು.",
			"5": "<sup>5</sup> ಅನಂತರ ಆತನು ಸಿಟ್ಟಾಗಿ - ನಾನು ನೇಮಿಸಿದ ಅರಸನನ್ನು ಚೀಯೋನೆಂಬ ನನ್ನ ಪರಿಶುದ್ಧಪರ್ವತದಲ್ಲಿಯೇ ಸ್ಥಾಪಿಸಿದ್ದಾಯಿತು ಎಂದು ಹೇಳಿ",
			"6": "<sup>6</sup> ಕೋಪಾವೇಶದಿಂದ ಅವರನ್ನು ಕಳವಳಗೊಳಿಸುವನು.",
			"7": "<sup>7</sup> ನಾನು ಯೆಹೋವನ ಆಜ್ಞೆಯನ್ನು ತಿಳಿಸುತ್ತೇನೆ, ಕೇಳಿರಿ; ಆತನು ನನಗೆ - ನನಗೆ ನೀನು ಮಗನು; ನಾನೇ ಈಹೊತ್ತು ನಿನ್ನನ್ನು ಪಡೆದಿದ್ದೇನೆ.",
			"8": "<sup>8</sup> ನೀನು ಕೇಳಿಕೊಂಡರೆ ನಾನು ಅನ್ಯಜನಗಳನ್ನೆಲ್ಲಾ ನಿನಗೆ ಅಧೀನ ಮಾಡುವೆನು; ಭೂಮಿಯ ಕಟ್ಟಕಡೆಯವರೆಗೂ ಇರುವ ಎಲ್ಲಾ ದೇಶಗಳನ್ನೂ ನಿನಗೆ ಸ್ವಾಸ್ತ್ಯವಾಗಿ ಕೊಡುವೆನು.",
			"9": "<sup>9</sup> ಕಬ್ಬಿಣದ ಗದೆಯಿಂದ ಅವರನ್ನು ನಾಶಮಾಡುವಿ; ಮಣ್ಣಿನ ಮಡಿಕೆಗಳನ್ನೋ ಎಂಬಂತೆ ಅವರನ್ನು ಒಡೆದುಹಾಕುವಿ ಎಂದು ಹೇಳಿದನು.",
			"10": "<sup>10</sup> ಆದದರಿಂದ ಅರಸುಗಳಿರಾ, ವಿವೇಕಿಗಳಾಗಿರ್ರಿ; ದೇಶಾಧಿಪತಿಗಳಿರಾ, ಬುದ್ಧಿಮಾತುಗಳಿಗೆ ಕಿವಿಗೊಡಿರಿ.",
			"11": "<sup>11</sup> ಯೆಹೋವನನ್ನು ಭಯಭಕ್ತಿಯಿಂದ ಸೇವಿಸಿರಿ; ನಡುಗುತ್ತಾ ಉಲ್ಲಾಸಪಡಿರಿ.",
			"12": "<sup>12</sup> ಆತನ ಮಗನಿಗೆ ಮುದ್ದಿಡಿರಿ; ಇಲ್ಲವಾದರೆ ಆತನ ಕೋಪವು ಬೇಗನೆ ಪ್ರಜ್ವಲಿಸಲು ನೀವು ದಾರಿಯಲ್ಲೇ ನಾಶವಾದೀರಿ. ಆತನ ಮರೆಹೊಕ್ಕವರೆಲ್ಲರು ಧನ್ಯರು."
		},
		"3": {
			"1": "<sup>1</sup> ಯೆಹೋವನೇ, ನನ್ನ ವಿರೋಧಿಗಳು ಎಷ್ಟೋ ಹೆಚ್ಚಾಗಿದ್ದಾರೆ; ನನಗೆ ವೈರಿಗಳಾಗಿ ನಿಂತವರು ಬಹಳ ಮಂದಿ.",
			"2": "<sup>2</sup> ಅನೇಕರು ನನ್ನ ವಿಷಯದಲ್ಲಿ - ಅವನಿಗೆ ದೇವರಿಂದ ಸಹಾಯವು ಆಗುವದೇ ಇಲ್ಲವೆಂದು ಹೇಳಿಕೊಳ್ಳುತ್ತಾರೆ. ಸೆಲಾ.",
			"3": "<sup>3</sup> ಆದರೂ ಯೆಹೋವನೇ, ನೀನು ನನ್ನನ್ನು ಕಾಯುವ ಗುರಾಣಿ; ನೀನು ನನ್ನ ಗೌರವಕ್ಕೆ ಆಧಾರನೂ ನನ್ನ ತಲೆಯನ್ನು ಎತ್ತುವಂತೆ ಮಾಡುವವನೂ ಆಗಿದ್ದೀ.",
			"4": "<sup>4</sup> ನಾನು ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಡುವಾಗ ಆತನು ತನ್ನ ಪರಿಶುದ್ಧಪರ್ವತದಿಂದ ಸದುತ್ತರವನ್ನು ಅನುಗ್ರಹಿಸುತ್ತಾನೆ. ಸೆಲಾ.",
			"5": "<sup>5</sup> ಯೆಹೋವನು ನನ್ನನ್ನು ಕಾಪಾಡುವವನಾದ್ದರಿಂದ ನಾನು ಮಲಗಿಕೊಂಡು ನಿದ್ದೆಮಾಡಿ ಸುಖವಾಗಿ ಎಚ್ಚರಗೊಂಡೆನು.",
			"6": "<sup>6</sup> ನನ್ನ ಸುತ್ತಲು ಸನ್ನದ್ಧರಾಗಿ ನಿಂತಿರುವ ಸಾವಿರಾರು ವೈರಿಗಳಿಗಾದರೂ ನಾನು ಹೆದರೆನು.",
			"7": "<sup>7</sup> ಯೆಹೋವನೇ, ನನ್ನ ದೇವರೇ, ನನ್ನ ಎಲ್ಲಾ ಶತ್ರುಗಳ ದವಡೆಯ ಮೇಲೆ ಬಡಿದು ಅವರ ಹಲ್ಲುಗಳನ್ನು ಉದುರಿಸಿ ಬಿಟ್ಟವನೇ, ಎದ್ದು ಬಂದು ನನ್ನನ್ನು ರಕ್ಷಿಸು.",
			"8": "<sup>8</sup> ರಕ್ಷಣೆಯು ಯೆಹೋವನಿಂದಲೇ ಉಂಟಾಗುವದು. [ಯೆಹೋವನೇ,] ನಿನ್ನ ಆಶೀರ್ವಾದವು ನಿನ್ನ ಪ್ರಜೆಯ ಮೇಲೆ ಇರಲಿ. ಸೆಲಾ."
		},
		"4": {
			"1": "<sup>1</sup> ನ್ಯಾಯವನ್ನು ಸ್ಥಾಪಿಸುವ ನನ್ನ ದೇವರೇ, ನಿನಗೆ ಮೊರೆಯಿಡುತ್ತೇನೆ; ಸದುತ್ತರವನ್ನು ದಯಪಾಲಿಸು. ನನ್ನನ್ನು ಇಕ್ಕಟ್ಟಿನಿಂದ ಬಿಡಿಸಿ ಇಂಬಾದ ಸ್ಥಳದಲ್ಲಿ ಸೇರಿಸಿದಾತನೇ, ನನ್ನನ್ನು ಕರುಣಿಸಿ ನನ್ನ ಪ್ರಾರ್ಥನೆಯನ್ನು ಅಂಗೀಕರಿಸು.",
			"2": "<sup>2</sup> ಮಹನೀಯರೇ, ನೀವು ಎಷ್ಟರವರೆಗೆ ನನ್ನ ಗೌರವವನ್ನು ಕೆಡಿಸಿ ನನ್ನನ್ನು ಅವಮಾನ ಪಡಿಸುವಿರಿ? ಯಾಕೆ ನಿರರ್ಥಕವಾದದ್ದನ್ನು ಪ್ರೀತಿಸಿ ಸುಳ್ಳನ್ನು ಹಿಂಬಾಲಿಸುವಿರಿ? ಸೆಲಾ.",
			"3": "<sup>3</sup> ಯೆಹೋವನು ತನ್ನ ಭಕ್ತನನ್ನು ತನಗೋಸ್ಕರ ಪ್ರತ್ಯೇಕಿಸಿಕೊಂಡಿದ್ದಾನೆಂದು ತಿಳಿದುಕೊಳ್ಳಿರಿ. ನಾನು ಆತನಿಗೆ ಮೊರೆಯಿಡುವಾಗೆಲ್ಲಾ ಆತನು ಕೇಳುತ್ತಾನೆ.",
			"4": "<sup>4</sup> ಭಯಪಡಿರಿ, ಪಾಪಮಾಡಬೇಡಿರಿ, ಮೌನವಾಗಿರಿ; ಹಾಸಿಗೆಯ ಮೇಲೆ ಇರುವಾಗ ಹೃದಯದಲ್ಲೇ ಆಲೋಚಿಸಿಕೊಳ್ಳಿರಿ. ಸೆಲಾ.",
			"5": "<sup>5</sup> ನ್ಯಾಯವಾದ ಯಜ್ಞಗಳನ್ನು ಸಮರ್ಪಿಸಿರಿ; ಯೆಹೋವನಲ್ಲಿಯೇ ಭರವಸವಿಡಿರಿ.",
			"6": "<sup>6</sup> ನಮಗೆ ಒಳ್ಳೆಯದನ್ನು ಮಾಡುವವರು ಯಾರಿದ್ದಾರೆಂದು ಅನೇಕರು ಹೇಳಿಕೊಳ್ಳುತ್ತಾರೆ. ಯೆಹೋವನೇ, ನೀನು ಪ್ರಸನ್ನಮುಖದಿಂದ ನಮ್ಮನ್ನು ನೋಡಬೇಕು.",
			"7": "<sup>7</sup> ಧಾನ್ಯದ್ರಾಕ್ಷೆಗಳು ಸಮೃದ್ಧಿಯಾಗಿ ಬೆಳೆದ ಸುಗ್ಗಿಕಾಲದಲ್ಲಿ ಅವರಿಗಿರುವ ಸಂತೋಷಕ್ಕಿಂತಲೂ ನೀನು ನನ್ನ ಹೃದಯದಲ್ಲಿ ಹೆಚ್ಚಾದ ಆನಂದವನ್ನು ಉಂಟುಮಾಡಿದ್ದೀ.",
			"8": "<sup>8</sup> ನಾನು ನಿರ್ಭಯವಾಗಿರುವದರಿಂದ ಮಲಗಿಕೊಂಡು ಕೂಡಲೆ ನಿದ್ದೆ ಮಾಡುವೆನು; ಯಾಕಂದರೆ ಯೆಹೋವನೇ, ನಾನು ಯಾವ ಅಪಾಯವೂ ಇಲ್ಲದೆ ಸುರಕ್ಷಿತನಾಗಿರುವಂತೆ ನೀನು ಕಾಪಾಡುತ್ತೀ."
		},
		"5": {
			"1": "<sup>1</sup> ಯೆಹೋವನೇ, ನನ್ನ ಮಾತುಗಳಿಗೆ ಕಿವಿಗೊಡು; ನನ್ನ ಧ್ಯಾನವನ್ನು ಲಕ್ಷ್ಯಕ್ಕೆ ತಂದುಕೋ.",
			"2": "<sup>2</sup> ನನ್ನ ಅರಸೇ, ನನ್ನ ದೇವರೇ, ನಿನ್ನನ್ನೇ ಪ್ರಾರ್ಥಿಸುವೆನು; ನನ್ನ ಮೊರೆಯನ್ನು ಆಲೈಸು.",
			"3": "<sup>3</sup> ಯೆಹೋವನೇ, ಉದಯಕಾಲದಲ್ಲಿ ನನ್ನ ಸ್ವರವು ನಿನಗೆ ಕೇಳಿಸುವದು; ಉದಯಕಾಲದಲ್ಲಿಯೇ [ಪ್ರಾರ್ಥನೆಯನ್ನು] ಸಮರ್ಪಿಸಿ ಸದುತ್ತರವನ್ನು ಎದುರುನೋಡುತ್ತಿರುವೆನು.",
			"4": "<sup>4</sup> ನೀನು ದುಷ್ಟತ್ವದಲ್ಲಿ ಸಂತೋಷಿಸುವ ದೇವರಲ್ಲ; ಕೆಟ್ಟದ್ದು ನಿನ್ನ ಬಳಿಯಲ್ಲಿ ತಂಗಲಾರದು.",
			"5": "<sup>5</sup> ಸೊಕ್ಕಿನವರು ನಿನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನಿಲ್ಲಲಾರರು; ಅಧರ್ಮಿಗಳೆಲ್ಲರನ್ನು ನೀನು ಹಗೆಮಾಡುವಿ.",
			"6": "<sup>6</sup> ಸುಳ್ಳು ಹೇಳುವವರನ್ನು ನಾಶಮಾಡುವಿ; ಯೆಹೋವನೇ, ನರಹತ್ಯಮಾಡುವವರೂ, ಕಪಟಿಗಳೂ ನಿನಗೆ ಹೇಯವಾಗಿದ್ದಾರೆ.",
			"7": "<sup>7</sup> ನಾನಂತು ನಿನ್ನ ಮಹಾಕೃಪೆಯನ್ನು ಹೊಂದಿದವನಾಗಿ ನಿನ್ನ ಮಂದಿರದೊಳಕ್ಕೆ ಪ್ರವೇಶಿಸುವೆನು; ನಿನ್ನಲ್ಲಿಯೇ ಭಯಭಕ್ತಿಯುಳ್ಳವನಾಗಿ ನಿನ್ನ ಪರಿಶುದ್ಧ ಆಲಯದ ಕಡೆಗೆ ಅಡ್ಡಬೀಳುವೆನು.",
			"8": "<sup>8</sup> ಯೆಹೋವನೇ, ವಿರೋಧಿಗಳು ನನ್ನ ಕೇಡನ್ನೇ ಹಾರೈಸುತ್ತಿರುವದರಿಂದ ನಿನ್ನ ನೀತಿಗೆ ಸರಿಯಾಗಿ ನನ್ನನ್ನು ನಡಿಸು; ನನ್ನ ಮುಂದೆ ನಿನ್ನ ಮಾರ್ಗವನ್ನು ಸರಾಗಮಾಡು.",
			"9": "<sup>9</sup> ಅವರ ಬಾಯಲ್ಲಿ ಯಥಾರ್ಥತ್ವವಿಲ್ಲ; ಅವರು ನಾಲಿಗೆಯಿಂದ ಸವಿಮಾತಾಡಿದರೂ ಅವರ ಹೃದಯವು ನಾಶಕರವಾದ ಗುಂಡಿ, ಅವರ ಗಂಟಲು ತೆರೆದಿರುವ ಸಮಾಧಿ.",
			"10": "<sup>10</sup> ದೇವರೇ, ಅವರು ನಿನಗೆ ವಿರೋಧವಾಗಿ ತಿರುಗಿಬಿದ್ದವರು; ಆದದರಿಂದ ಅವರನ್ನು ಅಪರಾಧಿಗಳೆಂದು ನಿರ್ಣಯಿಸು; ಅವರು ತಮ್ಮ ಕುಯುಕ್ತಿಯಿಂದಲೇ ಮೋಸ ಹೋಗಲಿ; ಅವರ ದ್ರೋಹವು ಅಪಾರವಾಗಿರುವದರಿಂದ ಅವರನ್ನು ದೊಬ್ಬಿಬಿಡು.",
			"11": "<sup>11</sup> ಯೆಹೋವನೇ, ನೀತಿವಂತನನ್ನು ಆಶೀರ್ವದಿಸುವವನು ನೀನೇ; ನಿನ್ನ ದಯವು ದೊಡ್ಡ ಗುರಾಣಿಯಂತೆ ಅವನನ್ನು ಆವರಿಸಿಕೊಳ್ಳುವದು.",
			"12": "<sup>12</sup> ಆದಕಾರಣ ನಿನ್ನನ್ನು ಮರೆಹೊಕ್ಕವರೆಲ್ಲರು ಸಂತೋಷಪಡುವರು; ನೀನು ಕಾಪಾಡುವವನೆಂದು ಅವರು ಯಾವಾಗಲೂ ಆನಂದಧ್ವನಿಮಾಡುವರು. ನಿನ್ನ ನಾಮವನ್ನು ಪ್ರೀತಿಸುವವರು ನಿನ್ನಲ್ಲಿ ಉಲ್ಲಾಸಗೊಳ್ಳುವರು."
		},
		"6": {
			"1": "<sup>1</sup> ಯೆಹೋವನೇ, ಕೋಪದಿಂದ ನನ್ನನ್ನು ಶಿಕ್ಷಿಸಬೇಡ; ರೋಷದಿಂದ ದಂಡಿಸಬೇಡ.",
			"2": "<sup>2</sup> ಯೆಹೋವನೇ, ಕನಿಕರಿಸು; ನಾನು ನಿಶ್ಶಕ್ತನಾಗಿ ಹೋಗಿದ್ದೇನೆ. ಯೆಹೋವನೇ, ವಾಸಿಮಾಡು; ನನ್ನ ಎಲುಬುಗಳೆಲ್ಲಾ ತತ್ತರಿಸುತ್ತವೆ.",
			"3": "<sup>3</sup> ನನ್ನ ಪ್ರಾಣವು ಸಹ ಬಹಳವಾಗಿ ತತ್ತರಿಸುತ್ತದೆ; ಯೆಹೋವನೇ, ಎಷ್ಟರವರೆಗೂ [ನನ್ನ ಕೈ ಬಿಟ್ಟಿರುವಿ]?",
			"4": "<sup>4</sup> ಯೆಹೋವನೇ, ಅಭಿಮುಖನಾಗಿ ನನ್ನನ್ನು ಬಿಡಿಸು; ನಿನ್ನ ಕೃಪೆಯ ನಿಮಿತ್ತವೇ ನನ್ನನ್ನು ರಕ್ಷಿಸು.",
			"5": "<sup>5</sup> ಮೃತರಿಗೆ ನಿನ್ನ ಜ್ಞಾಪಕವಿರುವದಿಲ್ಲವಲ್ಲಾ; ಪಾತಾಳದಲ್ಲಿ ನಿನ್ನನ್ನು ಸ್ತುತಿಸುವವರು ಯಾರು?",
			"6": "<sup>6</sup> ನಾನು ನರನರಳಿ ದಣಿದಿದ್ದೇನೆ; ಪ್ರತಿರಾತ್ರಿಯೂ ನನ್ನ ಕಣ್ಣೀರಿನಿಂದ ಮಂಚವು ತೇಲಾಡುತ್ತದೆ. ಹಾಸಿಗೆಯು ಕಣ್ಣೀರಿನಿಂದ ನೆನೆದು ಹೋಗುತ್ತದೆ.",
			"7": "<sup>7</sup> ದುಃಖದಿಂದ ನನ್ನ ಕಣ್ಣು ಡೊಗರಾಯಿತು; ವಿರೋಧಿಗಳ ಬಾಧೆಯ ದೆಸೆಯಿಂದಲೇ ಮೊಬ್ಬಾಯಿತು.",
			"8": "<sup>8</sup> ಧರ್ಮವನ್ನು ಮೀರಿ ನಡೆಯುವವರೇ, ನೀವೆಲ್ಲರು ನನ್ನಿಂದ ತೊಲಗಿಹೋಗಿರಿ; ಯೆಹೋವನು ನನ್ನ ಗೋಳಾಟಕ್ಕೆ ಕಿವಿಗೊಟ್ಟು",
			"9": "<sup>9</sup> ನನ್ನ ವಿಜ್ಞಾಪನೆಯನ್ನು ಕೇಳಿದ್ದಾನಲ್ಲಾ; ನನ್ನ ಪ್ರಾರ್ಥನೆಯನ್ನು ಅಂಗೀಕರಿಸುವನು.",
			"10": "<sup>10</sup> ನನ್ನ ವಿರೋಧಿಗಳೆಲ್ಲರು ಆಶೆಗೆಟ್ಟು ಕಳವಳಗೊಳ್ಳುವರು; ಅವರು ಫಕ್ಕನೆ ಆಶಾಭಂಗಪಟ್ಟು ಹಿಂದಿರುಗುವರು."
		},
		"7": {
			"1": "<sup>1</sup> ಯೆಹೋವನೇ, ನನ್ನ ದೇವರೇ, ನಿನ್ನ ಮರೆಹೊಕ್ಕಿದ್ದೇನೆ; ಹಿಂದಟ್ಟುವವರೆಲ್ಲರಿಂದ ತಪ್ಪಿಸಿ ನನ್ನನ್ನು ಕಾಪಾಡು.",
			"2": "<sup>2</sup> ಇಲ್ಲವಾದರೆ ನನಗೆ ರಕ್ಷಕನಿಲ್ಲವೆಂದು ತಿಳಿದು ಶತ್ರುವು ಸಿಂಹದಂತೆ ಮೇಲೆ ಬಿದ್ದು ನನ್ನನ್ನು ಹರಿದುಬಿಟ್ಟಾನು.",
			"3": "<sup>3</sup> ಯೆಹೋವನೇ, ನನ್ನ ದೇವರೇ, ನಾನು ಕೈಗಳಲ್ಲಿ ಅನ್ಯಾಯವುಳ್ಳವನೂ",
			"4": "<sup>4</sup> ಮಿತ್ರದ್ರೋಹಿಯೂ ಆಗಿದ್ದರೆ ಶತ್ರುವು ಹಿಂದಟ್ಟಿ ಬಂದು ನನ್ನನ್ನು ಹಿಡಿದು ನೆಲಕ್ಕೆ ಕೆಡವಿ ತುಳಿಯಲಿ;",
			"5": "<sup>5</sup> ನನ್ನ ಮಾನವನ್ನು ಮಣ್ಣುಪಾಲು ಮಾಡಲಿ. ನಾನಂಥವನಲ್ಲ; ನಿಷ್ಕಾರಣವೈರಿಯನ್ನೂ ರಕ್ಷಿಸಿದೆನಲ್ಲಾ. ಸೆಲಾ.",
			"6": "<sup>6</sup> ಯೆಹೋವನೇ, ನ್ಯಾಯಸ್ಥಾಪಕನೇ, ನನಗೋಸ್ಕರ ಎಚ್ಚರವಾಗು. ಮಹಾಕೋಪದಿಂದ ಎದ್ದು ಬಂದು ನನ್ನ ವಿರೋಧಿಗಳ ಕ್ರೋಧವನ್ನು ಭಂಗಪಡಿಸು.",
			"7": "<sup>7</sup> ಎಲ್ಲಾ ಜನಾಂಗಗಳು ನಿನ್ನ ಸುತ್ತಲು ಸಭೆಯಾಗಿ ಕೂಡಿರುವಲ್ಲಿ ನೀನು ಪುನಹ ಆರೋಹಣಮಾಡು.",
			"8": "<sup>8</sup> ಯೆಹೋವನು ಎಲ್ಲಾ ಜನಾಂಗಗಳಿಗೂ ನ್ಯಾಯತೀರಿಸುವವನಾಗಿದ್ದಾನೆ. ಯೆಹೋವನೇ, ನಿರಪರಾಧಿಯೂ ನೀತಿವಂತನೂ ಆಗಿರುವ ನನಗೋಸ್ಕರ ನ್ಯಾಯತೀರಿಸು.",
			"9": "<sup>9</sup> ಮನುಷ್ಯರ ಹೃದಯವನ್ನೂ ಅಂತರಿಂದ್ರಿಯವನ್ನೂ ಪರಿಶೋಧಿಸುವ ನೀತಿಸ್ವರೂಪನಾದ ದೇವರೇ, ದುಷ್ಟರ ಕೆಟ್ಟತನವು ಇಲ್ಲದೆ ಹೋಗುವಂತೆ ಮಾಡು; ನೀತಿವಂತರನ್ನು ದೃಢಪಡಿಸು.",
			"10": "<sup>10</sup> ನನ್ನನ್ನು ರಕ್ಷಿಸುವ ಗುರಾಣಿಯು ದೇವರೇ; ಆತನು ಯಥಾರ್ಥರನ್ನು ಕಾಪಾಡುತ್ತಾನೆ.",
			"11": "<sup>11</sup> ದೇವರು ನ್ಯಾಯಕ್ಕೆ ಸರಿಯಾಗಿ ತೀರ್ಪುಕೊಡುವವನು; ಯಾವಾಗಲೂ [ದುಷ್ಟರ ವಿಷಯದಲ್ಲಿ] ಕೋಪವುಳ್ಳವನು.",
			"12": "<sup>12</sup> ದೋಷಿಯು ಮನಸ್ಸನ್ನು ಬೇರೆಮಾಡಿಕೊಳ್ಳದೆ ಹೋದರೆ ಆತನು ತನ್ನ ಕತ್ತಿಯನ್ನು ಮಸೆಯುವನು. ತನ್ನ ಬಿಲ್ಲನ್ನು ಬೊಗ್ಗಿಸಿ ಸಿದ್ಧಮಾಡಿದ್ದಾನೆ.",
			"13": "<sup>13</sup> ಆತನು ಅದಕ್ಕೆ ಮರಣಕರವಾದ ಅಗ್ನಿ ಬಾಣಗಳನ್ನು ಹೂಡಿ ಅವನ ಮೇಲೆ ಪ್ರಯೋಗಿಸುವದಕ್ಕೆ ಗುರಿಯಿಟ್ಟು ನಿಂತಿದ್ದಾನೆ.",
			"14": "<sup>14</sup> ನನ್ನ ಶತ್ರುವು ಕೆಡುಕನ್ನು ಹೆರಬೇಕೆಂದು ಪ್ರಸವ ವೇದನೆಯಲ್ಲಿದ್ದಾನೆ; ಅವನು ಹಾನಿಯನ್ನು ಹಡೆಯುವೆನೆಂದು ನೆನಸಿಕೊಂಡರೂ ಶೂನ್ಯವನ್ನೇ ಹೆತ್ತನು ನೋಡಿರಿ.",
			"15": "<sup>15</sup> ಅವನು ಅಗೆದು ಅಗೆದು ಗುಂಡಿಯನ್ನು ತೋಡಿ ಅದರೊಳಗೆ ತಾನೇ ಬಿದ್ದುಹೋದನಲ್ಲಾ.",
			"16": "<sup>16</sup> ಅವನು ಮಾಡಿದ ಕುಯುಕ್ತಿ ಅವನ ತಲೆಯ ಮೇಲೆಯೇ ಬರುವದು; ಅವನು ಮಾಡಬೇಕೆಂದಿದ್ದ ಅನ್ಯಾಯವು ಸ್ವಂತ ಬುರುಡೆಯ ಮೇಲೆ ಬೀಳುವದು.",
			"17": "<sup>17</sup> ಯೆಹೋವನು ಮಾಡಿದ ನ್ಯಾಯವಾದ ತೀರ್ಪಿಗಾಗಿ ನಾನು ಆತನನ್ನು ಕೊಂಡಾಡುವೆನು. ಪರಾತ್ಪರದೇವನಾದ ಯೆಹೋವನ ನಾಮವನ್ನು ಸಂಕೀರ್ತಿಸುವೆನು."
		},
		"8": {
			"1": "<sup>1</sup> ನಮ್ಮ ಕರ್ತನಾದ ಯೆಹೋವನೇ, ನಿನ್ನ ನಾಮವು ಭೂಲೋಕದಲ್ಲೆಲ್ಲಾ ಎಷ್ಟೋ ಮಹಿಮೆಯುಳ್ಳದ್ದು. ಆಕಾಶಮಂಡಲದಲ್ಲಿ ನಿನ್ನ ವೈಭವವನ್ನು ಪ್ರಕಾಶಪಡಿಸಿದ್ದೀ.",
			"2": "<sup>2</sup> ನಿನಗೆ ವಿರೋಧವಾಗಿ ನಿಂತು ಮುಯ್ಯಿತೀರಿಸುವ ವೈರಿಗಳ ಬಾಯನ್ನು ಕಟ್ಟುವದಕ್ಕೋಸ್ಕರ ನೀನು ಬಾಲಕರ ಮತ್ತು ಮೊಲೆಕೂಸುಗಳ ಬಾಯಿಂದ ಬಲವಾದ ಸಾಕ್ಷಿ ಉಂಟಾಗುವಂತೆ ಮಾಡಿದ್ದೀ.",
			"3": "<sup>3</sup> ನಿನ್ನ ಕೈಕೆಲಸವಾಗಿರುವ ಆಕಾಶಮಂಡಲವನ್ನೂ ನೀನು ಉಂಟುಮಾಡಿದ ಚಂದ್ರನಕ್ಷತ್ರಗಳನ್ನೂ ನಾನು ನೋಡುವಾಗ -",
			"4": "<sup>4</sup> ಮನುಷ್ಯನು ಎಷ್ಟು ಮಾತ್ರದವನು, ಅವನನ್ನು ನೀನು ಯಾಕೆ ನೆನಸಬೇಕು? ಮಾನವನು ಎಷ್ಟರವನು, ಅವನಲ್ಲಿ ಯಾಕೆ ಲಕ್ಷ್ಯವಿಡಬೇಕು?",
			"5": "<sup>5</sup> ಅವನನ್ನು ದೇವದೂತರಿಗಿಂತ ಸ್ವಲ್ಪವೇ ಕಡಿಮೆಯಾಗಿ ಮಾಡಿದಿಯಲ್ಲಾ; ಪ್ರಭಾವವನ್ನೂ ಮಾನವನ್ನೂ ಅವನಿಗೆ ಕಿರೀಟವಾಗಿ ಇಟ್ಟಿ.",
			"6": "<sup>6</sup> ನೀನು ಸೃಷ್ಟಿಸಿದ ಎಲ್ಲಾ ವಸ್ತುಗಳ ಮೇಲೆ ಪ್ರಭುತ್ವವನ್ನು ಅವನಿಗೆ ಅನುಗ್ರಹಿಸಿದಿ;",
			"7": "<sup>7</sup> ನೀನು ಎಲ್ಲಾ ಕುರಿದನಗಳನ್ನು ಮಾತ್ರವೇ ಅಲ್ಲದೆ ಕಾಡುಮೃಗಗಳು,",
			"8": "<sup>8</sup> ಆಕಾಶಪಕ್ಷಿಗಳು, ಸಮುದ್ರದ ಮೀನುಗಳು, ಅದರಲ್ಲಿ ಸಂಚರಿಸುವ ಸಕಲವಿಧವಾದ ಜೀವಜಂತುಗಳು ಇವೆಲ್ಲವನ್ನೂ ಅವನಿಗೆ ಅಧೀನಮಾಡಿದ್ದೀಯಲ್ಲವೇ.",
			"9": "<sup>9</sup> ನಮ್ಮ ಕರ್ತನಾದ ಯೆಹೋವನೇ, ನಿನ್ನ ನಾಮವು ಭೂಲೋಕದಲ್ಲೆಲ್ಲಾ ಎಷ್ಟೋ ಮಹಿಮೆಯುಳ್ಳದ್ದು."
		},
		"9": {
			"1": "<sup>1</sup> ಯೆಹೋವನೇ, ಮನಃಪೂರ್ವಕವಾಗಿ ನಿನ್ನನ್ನು ಕೊಂಡಾಡುವೆನು; ನೀನು ಮಾಡಿದ ಅದ್ಭುತಕಾರ್ಯಗಳನ್ನೆಲ್ಲಾ ವರ್ಣಿಸುವೆನು.",
			"2": "<sup>2</sup> ಪರಾತ್ಪರನೇ, ನಿನ್ನಲ್ಲಿ ನಾನು ಸಂತೋಷಿಸಿ ಉತ್ಸಾಹಪಡುವೆನು; ನಿನ್ನ ಹೆಸರನ್ನು ಕೀರ್ತಿಸುವೆನು.",
			"3": "<sup>3</sup> ನನ್ನ ಹಗೆಗಳು ನಿನ್ನ ಎದುರಿನಿಂದ ಹಿಂದಿರುಗಿ ಎಡವಿಬಿದ್ದು ಹಾಳಾದರಲ್ಲಾ.",
			"4": "<sup>4</sup> ನನ್ನ ವ್ಯಾಜ್ಯದಲ್ಲಿ ನೀನು ನ್ಯಾಯವನ್ನು ಸ್ಥಾಪಿಸಿದಿ; ನೀನು ಆಸನಾರೂಢನಾಗಿ ನೀತಿಯಿಂದ ನ್ಯಾಯವನ್ನು ನಿರ್ಣಯಿಸುತ್ತೀ.",
			"5": "<sup>5</sup> ನೀನು ಜನಾಂಗಗಳನ್ನು ಭಂಗಿಸಿ ದುಷ್ಟರನ್ನು ನಾಶಮಾಡಿದ್ದೀ; ಅವರ ಹೆಸರನ್ನು ಯುಗಯುಗಾಂತರಕ್ಕೂ ಅಳಿಸಿಬಿಟ್ಟಿದ್ದೀ;",
			"6": "<sup>6</sup> ಶತ್ರುಗಳು ನಿಶ್ಶೇಷವಾದರು; ನೀನು ಕೆಡವಿಸಿದ ಅವರ ಕೋಟೆಗಳು ಸಂಪೂರ್ಣವಾಗಿ ಹಾಳಾದವು. ಅವರ ಸ್ಮರಣೆಯೇ ಇಲ್ಲವಾಯಿತು.",
			"7": "<sup>7</sup> ಯೆಹೋವನಾದರೋ ಯಾವಾಗಲೂ ನೆಲೆಗೊಂಡಿದ್ದು ನ್ಯಾಯವಿಚಾರಿಸುವದಕ್ಕಾಗಿ ತನ್ನ ಸಿಂಹಾಸನವನ್ನು ಸ್ಥಾಪಿಸಿದ್ದಾನೆ.",
			"8": "<sup>8</sup> ಆತನೇ ನೀತಿಗನುಸಾರವಾಗಿ ಲೋಕಕ್ಕೆ ನ್ಯಾಯತೀರಿಸುವವನು; ಆತನು ಜನಾಂಗಗಳಿಗೆ ಸತ್ಯವಾಗಿಯೇ ತೀರ್ಪುಕೊಡುವನು.",
			"9": "<sup>9</sup> ಯೆಹೋವನು ಕುಗ್ಗಿಹೋದವರಿಗೆ ಆಶ್ರಯವೂ ಆಪತ್ಕಾಲದಲ್ಲಿ ದುರ್ಗವೂ ಆಗಿರುವನು.",
			"10": "<sup>10</sup> ಯೆಹೋವನೇ, ನಿನ್ನ ನಾಮಮಹಿಮೆಯನ್ನು ಬಲ್ಲವರು ನಿನ್ನಲ್ಲಿ ಭರವಸವಿಡುವರು; ಯಾಕಂದರೆ ನಿನ್ನ ಮರೆಹೊಗುವವರನ್ನು ನೀನು ಕೈಬಿಡುವವನಲ್ಲ.",
			"11": "<sup>11</sup> ಚೀಯೋನಿನಲ್ಲಿ ವಾಸಿಸುವ ಯೆಹೋವನನ್ನು ಕೀರ್ತಿಸಿರಿ; ಆತನ ಮಹತ್ಕಾರ್ಯಗಳನ್ನು ಜನಾಂಗಗಳಲ್ಲಿ ಪ್ರಕಟಿಸಿರಿ.",
			"12": "<sup>12</sup> ಆತನು ಕುಗ್ಗಿದವರ ಮೊರೆಯನ್ನು ಮರೆಯದೆ ಪ್ರಾಣಾಪರಾಧಕ್ಕೆ ಮುಯ್ಯಿತೀರಿಸುವವನಾಗಿ ಅವರನ್ನು ಜ್ಞಾಪಕಮಾಡಿಕೊಂಡನು.",
			"13": "<sup>13</sup> ಯೆಹೋವನೇ, ಮರಣದ್ವಾರದೊಳಗೆ ಸೇರದಂತೆ ನನ್ನನ್ನು ಉದ್ಧರಿಸುವವನೇ, ಕನಿಕರಿಸು; ಹಗೆಗಳಿಂದ ನನಗುಂಟಾದ ಬಾಧೆಯನ್ನು ಲಕ್ಷ್ಯಕ್ಕೆ ತಂದುಕೋ.",
			"14": "<sup>14</sup> ಆಗ ನಾನು ನಿನ್ನ ಸ್ತೋತ್ರವನ್ನು ಪ್ರಸಿದ್ಧಪಡಿಸುವೆನು; ನಿನ್ನಿಂದಾದ ರಕ್ಷಣೆಗಾಗಿ ಚೀಯೋನೆಂಬ ಕುಮಾರಿಯ ಬಾಗಲುಗಳಲ್ಲಿ ಹರ್ಷಿಸುವೆನು.",
			"15": "<sup>15</sup> ಜನಾಂಗಗಳವರು ತಾವು ಮಾಡಿದ ಕುಣಿಯಲ್ಲಿ ತಾವೇ ಬಿದ್ದುಹೋದರು; ಅವರು ಹಾಸಿದ ಬಲೆಯಲ್ಲಿ ಅವರ ಕಾಲೇ ಸಿಕ್ಕಿಕೊಂಡಿತು.",
			"16": "<sup>16</sup> ಯೆಹೋವನು ನ್ಯಾಯತೀರಿಸುವವನಾಗಿ ತನ್ನನ್ನು ಪ್ರಕಟಿಸಿಕೊಂಡಿದ್ದಾನೆ; ದುಷ್ಟರು ತಾವೇ ಕಲ್ಪಿಸಿದ ಕುಯುಕ್ತಿಯಲ್ಲಿ ಸಿಕ್ಕಿಕೊಂಡಿದ್ದಾರಲ್ಲಾ. ಹಿಗ್ಗಾಯೋನ್ ಸೆಲಾ.",
			"17": "<sup>17</sup> ದುಷ್ಟರು ಅಂದರೆ ದೇವರನ್ನು ಅಲಕ್ಷ್ಯಮಾಡುವ ಜನಾಂಗಗಳವರೆಲ್ಲಾ ಪಾತಾಳಕ್ಕೆ ಇಳಿದುಹೋಗುವರು.",
			"18": "<sup>18</sup> ದಿಕ್ಕಿಲ್ಲದವರು ಕಡೆಯವರೆಗೆ ಮರೆಯಲ್ಪಡುವದಿಲ್ಲ; ದೀನರ ನಿರೀಕ್ಷಣೆಯು ಕೆಡುವದೇ ಇಲ್ಲ.",
			"19": "<sup>19</sup> ಯೆಹೋವನೇ, ಏಳು; ಮನುಷ್ಯಮಾತ್ರದವರು ಬಲಗೊಳ್ಳಬಾರದು. ಜನಾಂಗಗಳಿಗೆ ನಿನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ತೀರ್ಪು ಉಂಟಾಗಲಿ.",
			"20": "<sup>20</sup> ಯೆಹೋವನೇ, ಅವರಿಗೆ ಭಯವನ್ನು ಹುಟ್ಟಿಸು; ಜನಾಂಗಗಳು ತಾವು ಮನುಷ್ಯಮಾತ್ರದವರೆಂದು ತಿಳಿದುಕೊಳ್ಳಲಿ. ಸೆಲಾ."
		},
		"10": {
			"1": "<sup>1</sup> ಯೆಹೋವನೇ, ನೀನು ಯಾಕೆ ದೂರವಾಗಿ ನಿಂತಿದ್ದೀ; ಕಷ್ಟಕಾಲದಲ್ಲಿ ಯಾಕೆ ಮರೆಯಾಗುತ್ತೀ?",
			"2": "<sup>2</sup> ದುಷ್ಟರು ಗರ್ವಿಷ್ಠರಾಗಿ ದೀನರನ್ನು ಬಹಳವಾಗಿ ಹಿಂಸಿಸುತ್ತಾರೆ; ಅವರು ಕಲ್ಪಿಸಿದ ಕುಯುಕ್ತಿಯಲ್ಲಿ ತಾವೇ ಸಿಕ್ಕಿಬೀಳಲಿ.",
			"3": "<sup>3</sup> ದುಷ್ಟನು ತನ್ನ ಮನೋರಥವು ನೆರವೇರಿತೆಂದು ಕೊಚ್ಚಿಕೊಳ್ಳುತ್ತಾನೆ; ಪರರ ಸೊತ್ತನ್ನು ಅಪಹರಿಸಿದವನು ಯೆಹೋವನನ್ನು ಅಲ್ಲಗಳೆದು ಬಿಟ್ಟುಬಿಡುತ್ತಾನೆ.",
			"4": "<sup>4</sup> ಯೆಹೋವನು ವಿಚಾರಿಸುವದಿಲ್ಲ ಎಂದು ದುಷ್ಟನು ಸೊಕ್ಕಿನ ಮುಖದಿಂದ ಹೇಳಿಕೊಂಡು ದೇವರಿಲ್ಲವೆಂಬದಾಗಿ ಸದಾ ಯೋಚಿಸುತ್ತಾನೆ.",
			"5": "<sup>5</sup> ಅವನ ಪ್ರಯತ್ನಗಳು ಯಾವಾಗಲೂ ಕೈಗೂಡುತ್ತವೆ; ನಿನ್ನ ನ್ಯಾಯತೀರ್ಪು ಮಹೋನ್ನತವಾಗಿರುವದರಿಂದ ಅವನ ಗ್ರಹಿಕೆಗೆ ಬರುವದಿಲ್ಲ; ವೈರಿಗಳ ಗುಂಪನ್ನಾದರೋ ತಾತ್ಸಾರ ಮಾಡುತ್ತಾನೆ.",
			"6": "<sup>6</sup> ತಾನು ಕದಲುವದೇ ಇಲ್ಲ; ತನಗೆ ವಿಪತ್ತು ಎಂದೆಂದಿಗೂ ಸಂಭವಿಸುವದಿಲ್ಲ ಅಂದುಕೊಂಡಿದ್ದಾನೆ.",
			"7": "<sup>7</sup> ಅವನ ಬಾಯಿಯು ಶಾಪಬಲಾತ್ಕಾರವಂಚನೆಗಳಿಂದ ತುಂಬಿದೆ; ಅವನ ನಾಲಿಗೆಯ ಕೆಳಗೆ ಹಾನಿಯೂ ನಾಶವೂ ಕೂತಿವೆ.",
			"8": "<sup>8</sup> ಅವನು ಗ್ರಾಮಗಳ ಸಂದುಗೊಂದುಗಳಲ್ಲಿ ಹೊಂಚಿಕೊಂಡಿದ್ದು ಮರೆಯಾದ ಸ್ಥಳಗಳಲ್ಲಿ ನಿರಪರಾಧಿಗಳನ್ನು ಕೊಲ್ಲುತ್ತಾನೆ. ಅವನು ಗತಿಹೀನನನ್ನು ಹಿಡಿಯುವದಕ್ಕೆ ಸಮಯ ನೋಡುತ್ತಾನೆ.",
			"9": "<sup>9</sup> ಗವಿಯಲ್ಲಿ ಅಡಗಿಕೊಂಡಿರುವ ಸಿಂಹದಂತೆ ಅವನು ಮರೆಯಾಗಿ ಹೊಂಚಿಕೊಂಡಿರುವನು. ಅವನು ಬಲೆಯೊಡ್ಡಿ ಕಾದಿದ್ದು ಕುಗ್ಗಿದವನನ್ನು ಹಿಡಿದು ಎಳೆದುಕೊಂಡು ಹೋಗುತ್ತಾನೆ.",
			"10": "<sup>10</sup> [ಕುಗ್ಗಿದವನು] ಜಜ್ಜಲ್ಪಟ್ಟನು ಉರುಳಿಕೊಳ್ಳುತ್ತಾನೆ, ಗತಿಯಿಲ್ಲದವನು ಅವನ ಬಲಾತ್ಕಾರದಿಂದ ಬಿದ್ದುಹೋಗುತ್ತಾನೆ.",
			"11": "<sup>11</sup> [ಆ ದುಷ್ಟನು] ತನ್ನೊಳಗೆ ದೇವರು ಇವನನ್ನು ಬಿಟ್ಟು ವಿಮುಖನಾಗಿದ್ದಾನೆ; ಆತನು ನೋಡುವದೇ ಇಲ್ಲ ಅಂದುಕೊಳ್ಳುತ್ತಾನೆ.",
			"12": "<sup>12</sup> ಯೆಹೋವನೇ, ಏಳು; ದೇವರೇ, ಕುಗ್ಗಿದವನನ್ನು ಮರೆಯಬೇಡ; ಅವನನ್ನು ರಕ್ಷಿಸುವದಕ್ಕೆ ಕೈಚಾಚು.",
			"13": "<sup>13</sup> ದುಷ್ಟನು ನಿನ್ನನ್ನು ಯಾಕೆ ಅಲಕ್ಷ್ಯಮಾಡಬೇಕು? ದೇವರು ವಿಚಾರಿಸುವದೇ ಇಲ್ಲವೆಂದು ಹೇಳಿಕೊಳ್ಳುವದೇಕೆ?",
			"14": "<sup>14</sup> ನೀನು ಅವರ ಅನ್ಯಾಯಬಲಾತ್ಕಾರಗಳನ್ನು ನೋಡಿಯೇ ಇದ್ದೀ; ಅವುಗಳನ್ನು ವಿಚಾರಿಸುತ್ತೀ. ಗತಿಯಿಲ್ಲದವನು ತನ್ನನ್ನು ನಿನಗೇ ಒಪ್ಪಿಸುವನು; ದಿಕ್ಕಿಲ್ಲದವನಿಗೆ ನೀನೇ ದಿಕ್ಕು.",
			"15": "<sup>15</sup> ದುಷ್ಟನ ಭುಜಬಲವನ್ನು ಮುರಿದುಹಾಕು; ಕೆಡುಕನ ದುಷ್ಟತ್ವವನ್ನು ಶೋಧಿಸಿ ನಿರ್ಮೂಲ ಮಾಡಿಬಿಡು.",
			"16": "<sup>16</sup> ಯೆಹೋವನು ಯುಗಯುಗಾಂತರಗಳಲ್ಲಿಯೂ ಅರಸನಾಗಿರುವನು; ಆತನ ದೇಶದಲ್ಲಿ ಅನ್ಯಜನಗಳು ನಿಶ್ಶೇಷರಾದರು.",
			"17": "<sup>17</sup> ಯೆಹೋವನೇ, ನೀನು ದೀನರ ಕೋರಿಕೆಯನ್ನು ನೆರವೇರಿಸುವವನೇ ಆಗಿದ್ದೀ; ಅವರ ಹೃದಯವನ್ನು ಧೈರ್ಯಪಡಿಸುತ್ತೀ; ಅವರ ಮೊರೆಗೆ ಕಿವಿಗೊಡುತ್ತೀ.",
			"18": "<sup>18</sup> ನೀನು ದಿಕ್ಕಿಲ್ಲದವರ ಮತ್ತು ಕುಗ್ಗಿದವರ ನ್ಯಾಯವನ್ನು ವಿಚಾರಿಸುತ್ತೀ. ಹೀಗಿರುವಲ್ಲಿ ಇನ್ನು ಮುಂದೆ ಮಣ್ಣಿನಿಂದಾದ ಮನುಷ್ಯರಿಂದ ಅವರಿಗೆ ಹೆದರಿಕೆ ಉಂಟಾಗುವದಿಲ್ಲ."
		},
		"11": {
			"1": "<sup>1</sup> ಯೆಹೋವನನ್ನು ಆಶ್ರಯಿಸಿಕೊಂಡಿದ್ದೇನೆ.",
			"2": "<sup>2</sup> ನೀವು - ದುಷ್ಟರು ಕತ್ತಲಲ್ಲಿ ಯಥಾರ್ಥ ಹೃದಯವುಳ್ಳವರನ್ನು ಕೊಲ್ಲಬೇಕೆಂದು ಬಿಲ್ಲುಬೊಗ್ಗಿಸಿ ಹೆದೆಗೆ ಬಾಣವನ್ನು ಹೂಡಿದ್ದಾರೆ ನೋಡಿರಿ;",
			"3": "<sup>3</sup> ಪಕ್ಷಿಗಳಂತೆ ನಿಮ್ಮ ಬೆಟ್ಟಗಳಿಗೆ ಓಡಿಹೋಗಿರಿ. ಆಧಾರಗಳೇ ಕೆಡವಲ್ಪಟ್ಟ ಮೇಲೆ ನೀತಿವಂತನ ಗತಿ ಏನಾದೀತು ಎಂದು ನನಗೆ ಹೇಳುವದೇಕೆ?",
			"4": "<sup>4</sup> ಯೆಹೋವನು ತನ್ನ ಪರಿಶುದ್ಧ ಮಂದಿರದಲ್ಲಿದ್ದಾನೆ; ಆತನು ತನ್ನ ಸಿಂಹಾಸನವನ್ನು ಪರಲೋಕದಲ್ಲಿ ಸ್ಥಾಪಿಸಿದ್ದಾನೆ. ಆತನ ಕಣ್ಣುಗಳು ಮಾನವರನ್ನು ನೋಡುತ್ತವೆ; ಆತನು ಅವರನ್ನು ಬಹು ಸೂಕ್ಷ್ಮವಾಗಿ ಪರಿಶೋಧಿಸುತ್ತಾನೆ.",
			"5": "<sup>5</sup> ಯೆಹೋವನು ನೀತಿವಂತರನ್ನೂ ಅನೀತಿವಂತರನ್ನೂ ಪರೀಕ್ಷಿಸುತ್ತಾನೆ; ಬಲಾತ್ಕಾರಿಗಳನ್ನು ದ್ವೇಷಿಸುತ್ತಾನೆ.",
			"6": "<sup>6</sup> ಆತನು ದುಷ್ಟರ ಮೇಲೆ ಪಾಶಗಳನ್ನು ಸುರಿಸಲಿ. ಬೆಂಕಿ ಗಂಧಕ ಉರಿಗಾಳಿ ಇವುಗಳನ್ನು ಅವರ ಪಾನವಾಗಮಾಡಲಿ.",
			"7": "<sup>7</sup> ಯಾಕಂದರೆ ನೀತಿಸ್ವರೂಪನಾದ ಯೆಹೋವನು ನೀತಿಯನ್ನು ಮೆಚ್ಚುವವನಾಗಿದ್ದಾನೆ. ಸಜ್ಜನರು ಆತನ ಸಾನ್ನಿಧ್ಯದಲ್ಲಿ ಇರುವರು."
		},
		"12": {
			"1": "<sup>1</sup> ಯೆಹೋವನೇ, ರಕ್ಷಿಸು; ಭಕ್ತರು ಮುಗಿದು ಹೋಗಿದ್ದಾರೆ. ಜನರೊಳಗೆ ನಂಬಿಕೆಯುಳ್ಳವರು ಕಾಣಿಸುವದೇ ಇಲ್ಲ.",
			"2": "<sup>2</sup> ಪ್ರತಿಯೊಬ್ಬರು ನೆರೆಯವರೊಡನೆ ಹುಸಿಯನ್ನಾಡುತ್ತಾರೆ. ಅವರು ವಂಚನೆಯ ತುಟಿಗಳಿಂದ ಹೊರಗೊಂದು ಒಳಗೊಂದು ಮಾತಾಡುತ್ತಾರೆ.",
			"3": "<sup>3</sup> ಯೆಹೋವನು ವಂಚನೆಯ ತುಟಿಗಳನ್ನೂ ಬಡಾಯಿ ನಾಲಿಗೆಯನ್ನೂ ಕಡಿದುಬಿಡಲಿ.",
			"4": "<sup>4</sup> ಅವರು - ನಮ್ಮ ಮಾತುಗಳಿಗೆ ತಡೆಯಿಲ್ಲವಲ್ಲಾ; ನಮ್ಮ ತುಟಿಗಳು ನಮ್ಮವೇ; ನಮಗೆ ಒಡೆಯನು ಯಾವನು ಎಂದು ಹೇಳಿಕೊಳ್ಳುತ್ತಾರಲ್ಲಾ.",
			"5": "<sup>5</sup> ಯೆಹೋವನು - ಕುಗ್ಗಿದವರ ಬಾಧೆಯನ್ನು ನೋಡಿದ್ದೇನೆ; ಗತಿಯಿಲ್ಲದವರ ನರಳುವಿಕೆಯು ನನಗೆ ಕೇಳಿಸಿತು. ಈಗ ಎದ್ದು ಬಂದು ಅವರ ಇಷ್ಟಾರ್ಥವನ್ನು ನೆರವೇರಿಸಿ ಅವರನ್ನು ಸುರಕ್ಷಿತವಾಗಿ ಇರಿಸುವೆನು ಎಂದು ಹೇಳುತ್ತಾನೆ.",
			"6": "<sup>6</sup> ಯೆಹೋವನ ಮಾತುಗಳು ಯಥಾರ್ಥವಾದವುಗಳೇ; ಅವು ಏಳು ಸಾರಿ ಪುಟಕ್ಕೆ ಹಾಕಿದ ಚೊಕ್ಕ ಬೆಳ್ಳಿಯೋಪಾದಿಯಲ್ಲಿವೆ.",
			"7": "<sup>7</sup> ಯೆಹೋವನೇ, ದುಷ್ಟತ್ವಕ್ಕೆ ಆಳಿಕೆ ಬಂದು ಎಲ್ಲೆಲ್ಲೂ ನೀಚರೇ ತುಂಬಿಕೊಂಡಿದ್ದರೂ",
			"8": "<sup>8</sup> ನೀನು ನಮ್ಮನ್ನು ಅವರಿಂದ ತಪ್ಪಿಸಿ ಕಾಪಾಡುವಿ; ಸದಾಕಾಲವೂ ನಮ್ಮನ್ನು ಕಾಯುವಿ."
		},
		"13": {
			"1": "<sup>1</sup> ಯೆಹೋವನೇ, ನೀನು ಇನ್ನೆಷ್ಟರವರೆಗೆ ನನ್ನನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ಮರೆತಿರುವಿ? ಇನ್ನೆಲ್ಲಿಯ ತನಕ ನನಗೆ ವಿಮುಖನಾಗಿರುವಿ?",
			"2": "<sup>2</sup> ನಾನು ಇನ್ನೆಷ್ಟರವರೆಗೆ ಹಗಲೆಲ್ಲಾ ದುಃಖಾಕ್ರಾಂತನಾಗಿ ಆಲೋಚಿಸಿಕೊಳ್ಳುತ್ತಾ ಇರಬೇಕು? ಇನ್ನೆಷ್ಟರವರೆಗೆ ವಿರೋಧಿಯು ನನಗೆ ದೊರೆಯಾಗಿರಬೇಕು?",
			"3": "<sup>3</sup> ಯೆಹೋವನೇ, ನನ್ನ ದೇವರೇ, ನನ್ನಲ್ಲಿ ದೃಷ್ಟಿಯಿಟ್ಟು ಸದುತ್ತರವನ್ನು ದಯಪಾಲಿಸು; ನನಗೆ ಮರಣನಿದ್ರೆಯು ಉಂಟಾಗದಂತೆ ನನ್ನ ಕಣ್ಣುಗಳನ್ನು ಕಳೆಗೊಳಿಸು.",
			"4": "<sup>4</sup> ನನ್ನ ಶತ್ರುವು - ಅವನನ್ನು ಜಯಿಸಿದೆನೆಂದು ಹೇಳಿಕೊಳ್ಳಬಾರದು; ವೈರಿಗಳು ಜಾರಿಬಿದ್ದನೆಂದು ಹಿಗ್ಗಬಾರದು.",
			"5": "<sup>5</sup> ನಾನಂತೂ ನಿನ್ನ ಕೃಪೆಯಲ್ಲಿ ಭರವಸವಿಟ್ಟಿದ್ದೇನೆ; ನೀನು ನನ್ನನ್ನು ರಕ್ಷಿಸಿದ್ದರಿಂದ ನನ್ನ ಹೃದಯವು ಹರ್ಷಗೊಳ್ಳುವದು.",
			"6": "<sup>6</sup> ಯೆಹೋವನ ಮಹೋಪಕಾರಕ್ಕಾಗಿ ಆತನಿಗೆ ಹಾಡುವೆನು."
		},
		"14": {
			"1": "<sup>1</sup> ದುರ್ಮತಿಗಳು - ದೇವರಿಲ್ಲವೆಂದು ಮನಸ್ಸಿನಲ್ಲಿ ಅಂದುಕೊಳ್ಳುತ್ತಾರೆ; ಅವರು ಕೆಟ್ಟುಹೋದವರು; ಹೇಯ ಕೃತ್ಯಗಳನ್ನು ನಡಿಸುತ್ತಾರೆ. ಅವರಲ್ಲಿ ಒಳ್ಳೆಯದನ್ನು ಮಾಡುವವರೇ ಇಲ್ಲ.",
			"2": "<sup>2</sup> ಮನುಷ್ಯರಲ್ಲಿ ತನ್ನ ಸಾನ್ನಿಧ್ಯವನ್ನು ಅಪೇಕ್ಷಿಸುವ ಬುದ್ಧಿವಂತರು ಇದ್ದಾರೋ ಎಂದು ಯೆಹೋವನು ಆಕಾಶದಿಂದ ಮನುಷ್ಯರನ್ನು ನೋಡಲಾಗಿ",
			"3": "<sup>3</sup> ಒಳ್ಳೆಯದನ್ನು ಮಾಡುವವನು ಇಲ್ಲ, ಒಬ್ಬನಾದರೂ ಇಲ್ಲ. ಪ್ರತಿಯೊಬ್ಬನು ದಾರಿತಪ್ಪಿದವನು, ಎಲ್ಲರೂ ಕೆಟ್ಟುಹೋದವರೇ.",
			"4": "<sup>4</sup> ದುಷ್ಟತ್ವವನ್ನು ನಡಿಸುವವರೆಲ್ಲರು ತಿಳಿಯುವದಿಲ್ಲವೋ? ಅವರು ನನ್ನ ಜನರನ್ನು ಆಹಾರವನ್ನೋ ಎಂಬಂತೆ ನುಂಗಿಬಿಡುತ್ತಾರೆ; ಯೆಹೋವನನ್ನು ಸ್ಮರಿಸುವದಿಲ್ಲ.",
			"5": "<sup>5</sup> ಫಕ್ಕನೆ ಅವರು ಭಯಭ್ರಾಂತರಾಗುವರು; ಯಾಕಂದರೆ ದೇವರು ನೀತಿವಂತರ ಪಕ್ಷದಲ್ಲಿದ್ದಾನೆ.",
			"6": "<sup>6</sup> ನೀವು ಕುಗ್ಗಿದವರ ಸಂಕಲ್ಪವನ್ನು ಭಂಗಪಡಿಸಿದರೂ ಯೆಹೋವನು ಅವರ ಆಶ್ರಯವಲ್ಲವೇ.",
			"7": "<sup>7</sup> ಚೀಯೋನಿನಿಂದ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ರಕ್ಷಣೆಯು ಬೇಗನೆ ಬರಲಿ. ಯೆಹೋವನು ದುರವಸ್ಥೆಯಿಂದ ತಪ್ಪಿಸಿದಾಗ ಆತನ ಪ್ರಜೆಗಳಾಗಿರುವ ಯಾಕೋಬವಂಶದವರು ಉಲ್ಲಾಸಗೊಳ್ಳುವರು; ಇಸ್ರಾಯೇಲ್ಯರು ಹರ್ಷಿಸುವರು."
		},
		"15": {
			"1": "<sup>1</sup> ಯೆಹೋವನೇ, ನಿನ್ನ ಗುಡಾರದಲ್ಲಿ ಇಳುಕೊಂಡಿರುವದಕ್ಕೆ ಯೋಗ್ಯನು ಯಾವನು? ನಿನ್ನ ಪರಿಶುದ್ಧಪರ್ವತದಲ್ಲಿ ವಾಸಿಸತಕ್ಕವನು ಎಂಥವನಾಗಿರಬೇಕು?",
			"2": "<sup>2</sup> ಅವನು ಸಜ್ಜನನೂ ನೀತಿವಂತನೂ ಮನಃಪೂರ್ವಕವಾಗಿ ಸತ್ಯವಚನವನ್ನಾಡುವವನೂ ಆಗಿರಬೇಕು.",
			"3": "<sup>3</sup> ಅವನು ಚಾಡಿಯನ್ನು ಹೇಳದವನೂ ಮತ್ತೊಬ್ಬರಿಗೆ ಅನ್ಯಾಯಮಾಡದವನೂ ಯಾರನ್ನೂ ನಿಂದಿಸದವನೂ ಆಗಿರಬೇಕು.",
			"4": "<sup>4</sup> ಅವನು ಭ್ರಷ್ಟರನ್ನು ಜರಿದುಬಿಟ್ಟವನೂ ಯೆಹೋವನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರನ್ನು ಸನ್ಮಾನಿಸುವವನೂ ನಷ್ಟವಾದರೂ ಪ್ರಮಾಣತಪ್ಪದವನೂ ಆಗಿರಬೇಕು.",
			"5": "<sup>5</sup> ಅವನು ಸಾಲಕ್ಕೆ ಬಡ್ಡಿ ಕೇಳದವನೂ ನಿರಪರಾಧಿಯ ಕೇಡಿಗಾಗಿ ಲಂಚತೆಗೆದುಕೊಳ್ಳದವನೂ ಆಗಿರಬೇಕು. ಇಂಥವನು ಎಂದಿಗೂ ಕದಲುವದಿಲ್ಲ."
		},
		"16": {
			"1": "<sup>1</sup> ದೇವರೇ, ನಾನು ನಿನ್ನ ಶರಣಾಗತನು; ನನ್ನನ್ನು ಕಾಪಾಡು.",
			"2": "<sup>2</sup> ನಾನು ಯೆಹೋವನನ್ನು ಕುರಿತು - ನೀನೇ ನನ್ನ ಒಡೆಯನು; ನನ್ನ ಸುಖವು ನಿನ್ನಲ್ಲಿಯೇ ಹೊರತು ಬೇರೆ ಇಲ್ಲ ಎಂತಲೂ",
			"3": "<sup>3</sup> ಭೂಲೋಕದಲ್ಲಿರುವ ದೇವಜನರ ವಿಷಯದಲ್ಲಿ - ಇವರೇ ಶ್ರೆಷ್ಠರು, ಇವರೇ ನನಗೆ ಇಷ್ಟರಾದವರು ಎಂತಲೂ ಹೇಳುವೆನು.",
			"4": "<sup>4</sup> ಇತರ ದೇವರುಗಳನ್ನು ಅವಲಂಬಿಸಿದವರಿಗೆ ಬಹಳ ಕಷ್ಟನಷ್ಟಗಳು ಉಂಟಾಗುವವು. ಅವರಂತೆ ನಾನು ರಕ್ತವನ್ನು ಪಾನದ್ರವ್ಯವಾಗಿ ಸಮರ್ಪಿಸುವದೇ ಇಲ್ಲ; ಅವರ ನಾಮಗಳನ್ನಾದರೂ ಉಚ್ಚರಿಸುವದಿಲ್ಲ.",
			"5": "<sup>5</sup> ನನ್ನ ಪಾಲೂ ಪಾನವೂ ಯೆಹೋವನೇ; ನೀನೇ ನನ್ನ ಸ್ವಾಸ್ತ್ಯವನ್ನು ಭದ್ರಗೊಳಿಸುತ್ತೀ.",
			"6": "<sup>6</sup> ನನಗೆ ಪ್ರಾಪ್ತವಾಗಿರುವ ಸ್ವಾಸ್ತ್ಯವು ರಮಣೀಯವಾದದ್ದು; ಅದು ನನಗೆ ಸಂತೃಪ್ತಿಕರವಾಗಿದೆ.",
			"7": "<sup>7</sup> ಯೆಹೋವನು ನನಗೆ ಆಲೋಚನೆಯನ್ನು ಹೇಳಿ ಕೊಡುತ್ತಾನೆ; ಆತನನ್ನು ಕೊಂಡಾಡುವೆನು. ರಾತ್ರಿ ಸಮಯಗಳಲ್ಲಿ ನನ್ನ ಅಂತರಾತ್ಮವು ನನ್ನನ್ನು ಬೋಧಿಸುತ್ತದೆ.",
			"8": "<sup>8</sup> ನಾನು ಯೆಹೋವನನ್ನು ಯಾವಾಗಲೂ ನನ್ನೆದುರಿಗೇ ಇಟ್ಟುಕೊಂಡಿದ್ದೇನೆ; ಆತನು ನನ್ನ ಬಲಗಡೆಯಲ್ಲಿ ಇರುವದರಿಂದ ನಾನು ಎಂದಿಗೂ ಕದಲುವದಿಲ್ಲ.",
			"9": "<sup>9</sup> ಆದಕಾರಣ ನನ್ನ ಹೃದಯವು ಹರ್ಷಿಸುತ್ತದೆ; ನನ್ನ ಮನವು ಉಲ್ಲಾಸಗೊಳ್ಳುತ್ತದೆ; ನನ್ನ ಶರೀರವೂ ಸುರಕ್ಷಿತವಾಗಿರುವದು.",
			"10": "<sup>10</sup> ಯಾಕಂದರೆ ನೀನು ನನ್ನ ಜೀವಾತ್ಮವನ್ನು ಪಾತಾಳದಲ್ಲಿ ಬಿಡುವದಿಲ್ಲ; ನಿನ್ನ ಪ್ರಿಯನಿಗೆ ಅಧೋಲೋಕವನ್ನು ನೋಡಗೊಡಿಸುವದಿಲ್ಲ.",
			"11": "<sup>11</sup> ನೀನು ನನಗೆ ಜೀವಮಾರ್ಗವನ್ನು ತಿಳಿಯಪಡಿಸುವಿ; ನಿನ್ನ ಸಮ್ಮುಖದಲ್ಲಿ ಪರಿಪೂರ್ಣ ಸಂತೋಷವಿದೆ; ನಿನ್ನ ಬಲಗೈಯಲ್ಲಿ ಶಾಶ್ವತಭಾಗ್ಯವಿದೆ."
		},
		"17": {
			"1": "<sup>1</sup> ಯೆಹೋವನೇ, ನ್ಯಾಯವಾದದ್ದನ್ನು ಆಲೈಸು; ನನ್ನ ಮೊರೆಗೆ ಲಕ್ಷ್ಯವಿಡು. ನನ್ನ ಪ್ರಾರ್ಥನೆಗೆ ಕಿವಿಗೊಡು, ಅದು ಕಪಟವಾದ ಬಾಯಿಂದ ಬಂದದ್ದಲ್ಲ.",
			"2": "<sup>2</sup> ನಿನ್ನಿಂದ ನನಗೆ ನ್ಯಾಯವಾದ ತೀರ್ಪುಉಂಟಾಗಲಿ; ನೀನು ನೀತಿಗನುಸಾರವಾಗಿ ನೋಡುವವನಲ್ಲವೇ.",
			"3": "<sup>3</sup> ನೀನು ನನ್ನ ಹೃದಯವನ್ನು ಪರೀಕ್ಷಿಸಿದರೂ ರಾತ್ರಿ ವೇಳೆ ವಿಚಾರಿಸಿದರೂ ನನ್ನನ್ನು ಪುಟಕ್ಕೆ ಹಾಕಿ ಶೋಧಿಸಿದರೂ ಯಾವ ದುರಾಲೋಚನೆಯಾದರೂ ನನ್ನಲ್ಲಿ ದೊರೆಯುವದಿಲ್ಲ; ನನ್ನ ಬಾಯಲ್ಲಿಯೂ ತಪ್ಪಿಲ್ಲ.",
			"4": "<sup>4</sup> ನಾನಂತೂ ಲೋಕವ್ಯವಹಾರಗಳಲ್ಲಿ ಬಲಾತ್ಕಾರಿಗಳಂತೆ ನಡೆಯದೆ ನಿನ್ನ ಮಾತನ್ನೇ ಅನುಸರಿಸಿದ್ದೇನೆ.",
			"5": "<sup>5</sup> ನಿನ್ನ ಮಾರ್ಗದಲ್ಲಿ ಹೆಜ್ಜೆಯಿಟ್ಟು ನಡೆಯುತ್ತಾ ಇದ್ದೇನೆ; ನನ್ನ ಕಾಲು ಜಾರಲಿಲ್ಲ.",
			"6": "<sup>6</sup> ದೇವರೇ, ನನಗೆ ಸದುತ್ತರವನ್ನು ದಯಪಾಲಿಸುವಿಯೆಂದು ಮೊರೆಯಿಡುತ್ತೇನೆ, ಕಿವಿಗೊಟ್ಟು ಕೇಳು.",
			"7": "<sup>7</sup> ಭುಜಬಲವನ್ನು ಪ್ರಯೋಗಿಸಿ ಶರಣಾಗತರನ್ನು ವಿರೋಧಿಗಳಿಂದ ರಕ್ಷಿಸುವಾತನೇ, ನಿನ್ನ ಪ್ರೀತಿಯನ್ನು ವಿಶೇಷವಾಗಿ ತೋರಿಸು.",
			"8": "<sup>8</sup> ನನ್ನನ್ನು ಸುತ್ತಿಕೊಂಡು ಬಾಧಿಸುತ್ತಿರುವ ದುಷ್ಟರಿಂದಲೂ ಪ್ರಾಣವೈರಿಗಳಿಂದಲೂ ತಪ್ಪಿಸಿ",
			"9": "<sup>9</sup> ನಿನ್ನ ರೆಕ್ಕೆಗಳ ಮರೆಯಲ್ಲಿಟ್ಟುಕೊಂಡು ನನ್ನನ್ನು ಕಾಯಿ; ಕಣ್ಣುಗುಡ್ಡಿನಂತೆಯೇ ಕಾಪಾಡು.",
			"10": "<sup>10</sup> ಅವರು ತಮ್ಮ ಹೃದಯವನ್ನು ಬಿಗಿಮಾಡಿದ್ದಾರೆ. ಅಹಂಕಾರದಿಂದ ಮಾತಾಡುತ್ತಾರೆ.",
			"11": "<sup>11</sup> ನಾವು ಎಲ್ಲಿ ಕಾಲಿಟ್ಟರೂ ಅಲ್ಲೆಲ್ಲಾ ನಮ್ಮನ್ನು ಸುತ್ತಿಕೊಳ್ಳುತ್ತಾರೆ; ನಮ್ಮನ್ನು ನೆಲಕ್ಕೆ ಹಾಕಬೇಕೆಂದು ಸಮಯನೋಡುತ್ತಾರೆ.",
			"12": "<sup>12</sup> ನನ್ನ ಶತ್ರುವು ಸೀಳಿಬಿಡಲಾಶಿಸುವ ಸಿಂಹದಂತೆಯೂ ಮರೆಯಲ್ಲಿ ಹೊಂಚುಹಾಕಿರುವ ಪ್ರಾಯದ ಸಿಂಹದಂತೆಯೂ ಇದ್ದಾನೆ.",
			"13": "<sup>13</sup> ಯೆಹೋವನೇ, ನೀನು ಅವನಿಗೆ ಎದುರಾಗಿ ನಿಂತು ಅವನನ್ನು ಕೆಡವಿಬಿಡು; ನಿನ್ನ ಕತ್ತಿಯಿಂದ ನನ್ನ ಪ್ರಾಣವನ್ನು ದುಷ್ಟರಿಗೆ ತಪ್ಪಿಸಿ ಕಾಪಾಡು.",
			"14": "<sup>14</sup> ಯೆಹೋವನೇ, ಇಹಲೋಕವೇ ತಮ್ಮ ಪಾಲೆಂದು ನಂಬಿದ ನರರಿಗೆ ಸಿಕ್ಕದಂತೆ ನಿನ್ನ ಕೈಯಿಂದ ನನ್ನನ್ನು ತಪ್ಪಿಸು; ನಿನ್ನ ಐಶ್ವರ್ಯದಿಂದ ಅವರ ಹೊಟ್ಟೆಯನ್ನು ತುಂಬಿಸಿದ್ದೀಯಲ್ಲಾ. ಅವರು ಸಂತಾನವೃದ್ಧಿಹೊಂದಿ ಮಿಕ್ಕ ಆಸ್ತಿಯನ್ನು ತನ್ನ ಮಕ್ಕಳಿಗೆ ಬಿಡುತ್ತಾರೆ.",
			"15": "<sup>15</sup> ನಾನಾದರೋ ನಿರಪರಾಧಿಯು; ನಿನ್ನ ಸಾನ್ನಿಧ್ಯವನ್ನು ಸೇರುವೆನು. ಎಚ್ಚತ್ತಾಗ ನಿನ್ನ ಸ್ವರೂಪದರ್ಶನದಿಂದ ತೃಪ್ತನಾಗಿರುವೆನು."
		},
		"18": {
			"1": "<sup>1</sup> ನನ್ನ ಬಲವಾಗಿರುವ ಯೆಹೋವನೇ, ನಿನ್ನಲ್ಲಿಯೇ ಮಮತೆಯಿಡುತ್ತೇನೆ.",
			"2": "<sup>2</sup> ಯೆಹೋವನು ನನ್ನ ಬಂಡೆಯೂ ನನ್ನ ಕೋಟೆಯೂ ನನ್ನ ವಿಮೋಚಕನೂ ನನ್ನ ದೇವರೂ ನನ್ನ ಆಶ್ರಯಗಿರಿಯೂ ನನ್ನ ಗುರಾಣಿಯೂ ನನ್ನ ರಕ್ಷಣೆಯ ಕೊಂಬೂ ನನ್ನ ದುರ್ಗವೂ ಆಗಿದ್ದಾನೆ.",
			"3": "<sup>3</sup> ಯೆಹೋವನು ಸ್ತೋತ್ರಕ್ಕೆ ಅರ್ಹನು; ಆತನಿಗೆ ನಾನು ಮೊರೆಯಿಡಲು ಆತನು ನನ್ನನ್ನು ಶತ್ರುಗಳಿಂದ ರಕ್ಷಿಸುತ್ತಾನೆ.",
			"4": "<sup>4</sup> ಮೃತ್ಯುಪಾಶಗಳು ನನಗೆ ಸುತ್ತಿಕೊಂಡವು; ನಾಶಪ್ರವಾಹವು ನನ್ನನ್ನು ನಡುಗಿಸಿತು.",
			"5": "<sup>5</sup> ಪಾತಾಳಪಾಶಗಳು ನನ್ನನ್ನು ಆವರಿಸಿಕೊಂಡವು; ಮರಣಕರವಾದ ಉರುಲುಗಳು ನನ್ನೆದುರಿನಲ್ಲಿದ್ದವು.",
			"6": "<sup>6</sup> ಅಂಥ ಕಷ್ಟದಲ್ಲಿ ನಾನು ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಟ್ಟೆನು; ನನ್ನ ದೇವರನ್ನು ಪ್ರಾರ್ಥಿಸಿದೆನು. ಆತನು ತನ್ನ ಮಂದಿರದಲ್ಲಿ ನನ್ನ ಶಬ್ದವನ್ನು ಕೇಳಿದನು; ನನ್ನ ಕೂಗು ಆತನ ಸನ್ನಿಧಿಗೆ ಸೇರಿ ಕೇಳಿಸಿತು.",
			"7": "<sup>7</sup> ಆಗ ಆತನಿಗೆ ಸಿಟ್ಟೇರಿದ್ದರಿಂದ ಭೂಮಿಯು ಗಡಗಡನೆ ಕಂಪಿಸಿತು; ಪರ್ವತಗಳ ಬುಡಗಳು ನಡುಗಿ ಕದಲಿದವು.",
			"8": "<sup>8</sup> ಆತನ ಮೂಗಿನಿಂದ ಹೊಗೆಯು ಬಂತು; ಆತನ ಬಾಯಿಂದ ಅಗ್ನಿಜ್ವಾಲೆ ಹೊರಟು ಸಿಕ್ಕಿದ್ದೆಲ್ಲವನ್ನು ದಹಿಸಿ ಕೆಂಡವನ್ನಾಗಿ ಮಾಡಿತು.",
			"9": "<sup>9</sup> ಆತನು ಆಕಾಶವನ್ನು ತಗ್ಗಿಸಿ ಇಳಿದು ಬಂದನು; ಆತನ ಪಾದಗಳ ಕೆಳಗೆ ಕಾರ್ಗತ್ತಲು ಇತ್ತು.",
			"10": "<sup>10</sup> ಕೆರೂಬಿವಾಹನನಾಗಿ ಹಾರಿ ವಾಯುವೇ ಆತನ ರೆಕ್ಕೆಗಳೋ ಎಂಬಂತೆ ಇಳಿದು ಬಂದನು.",
			"11": "<sup>11</sup> ಕತ್ತಲನ್ನು ತನ್ನ ಸುತ್ತಲು ಗುಡಾರದಂತೆ ಕವಿಸಿಕೊಂಡು ಜಲಮಯವಾಗಿರುವ ನೀಲಮೇಘಗಳ ಮಧ್ಯದಲ್ಲಿ ಮರೆಯಾದನು.",
			"12": "<sup>12</sup> ಆತನ ಸನ್ನಿಧಿಯ ಪ್ರಕಾಶದಿಂದ ಕಲ್ಮಳೆಯೂ ಉರಿಗೆಂಡಗಳೂ ಹೊರಟು ಆತನ ಸುತ್ತಲಿದ್ದ ಕರೀಮೋಡಗಳನ್ನು ದಾಟಿ ಸುರಿದವು.",
			"13": "<sup>13</sup> ಯೆಹೋವನು ಆಕಾಶದಲ್ಲಿ ಗುಡುಗಿದನು; ಪರಾತ್ಪರನು ಧ್ವನಿಕೊಟ್ಟನು. ಇಗೋ, ಕಲ್ಮಳೆಯೂ ಉರಿಗೆಂಡಗಳೂ.",
			"14": "<sup>14</sup> ಆತನು ಬಾಣಗಳನ್ನೆಸೆದು ಶತ್ರುಗಳನ್ನು ಚದರಿಸಿಬಿಟ್ಟನು; ಸಿಡಿಲುಗಳಿಂದ ಕಳವಳಗೊಳಿಸಿದನು.",
			"15": "<sup>15</sup> ಆಗ ಯೆಹೋವನೇ, ನಿನ್ನ ಗದರಿಕೆಯಿಂದಲೂ ನಿನ್ನ ಶ್ವಾಸಭರದಿಂದಲೂ ಸಮುದ್ರದ ತಳವು ಕಾಣಿಸಿತು. ಭೂಮಂಡಲದ ಅಸ್ತಿವಾರಗಳು ತೋರಿಬಂದವು.",
			"16": "<sup>16</sup> ಆತನು ಮೇಲಣ ಲೋಕದಿಂದ ಕೈಚಾಚಿ ನನ್ನನ್ನು ಹಿಡಿದು ಮಹಾಜಲರಾಶಿಗಳೊಳಗಿಂದ ಎಳೆದನು.",
			"17": "<sup>17</sup> ನನಗಿಂತ ಬಲಿಷ್ಠರೂ ಪುಷ್ಟರೂ ಆಗಿ ದ್ವೇಷಿಸುತ್ತಿದ್ದ ಶತ್ರುಗಳಿಂದ ನನ್ನನ್ನು ಬಿಡಿಸಿ ರಕ್ಷಿಸಿದನು.",
			"18": "<sup>18</sup> ಅವರು ನನ್ನ ವಿಪತ್ಕಾಲದಲ್ಲಿ ನನ್ನ ಮೇಲೆ ಬಿದ್ದರು; ಆಗ ಯೆಹೋವನು ನನಗೆ ಉದ್ಧಾರಕನಾದನು.",
			"19": "<sup>19</sup> ಆತನು ನನ್ನನ್ನು ಬಿಡಿಸಿ ವಿಶಾಲಸ್ಥಳದಲ್ಲಿ ಸೇರಿಸಿದನು; ನನ್ನನ್ನು ಮೆಚ್ಚಿ ರಕ್ಷಿಸಿದನು.",
			"20": "<sup>20</sup> ಯೆಹೋವನು ನನ್ನ ನೀತಿಗೆ ಸರಿಯಾಗಿ ಮೇಲು ಮಾಡಿದನು; ನನ್ನ ಕೈಗಳ ಶುದ್ಧತ್ವಕ್ಕೆ ತಕ್ಕಂತೆ ಪ್ರತಿಫಲ ಕೊಟ್ಟನು.",
			"21": "<sup>21</sup> ಯೆಹೋವನ ಮಾರ್ಗವನ್ನೇ ಅನುಸರಿಸಿದೆನಲ್ಲಾ; ನನ್ನ ದೇವರನ್ನು ಬಿಟ್ಟು ದುಷ್ಟನಾಗಲಿಲ್ಲವಲ್ಲಾ.",
			"22": "<sup>22</sup> ನಾನು ಆತನ ಆಜ್ಞೆಗಳನ್ನು ಯಾವಾಗಲೂ ನನ್ನ ಎದುರಿನಲ್ಲಿ ಇಟ್ಟುಕೊಂಡೆನು; ಆತನ ವಿಧಿಗಳನ್ನು ಅಲಕ್ಷ್ಯಮಾಡಲೇ ಇಲ್ಲ.",
			"23": "<sup>23</sup> ನಾನು ಆತನ ದೃಷ್ಟಿಯಲ್ಲಿ ನಿರ್ದೋಷಿಯು; ಪಾಪದಲ್ಲಿ ಬೀಳದಂತೆ ಜಾಗರೂಕತೆಯಿಂದ ನಡೆದುಕೊಂಡೆನು.",
			"24": "<sup>24</sup> ಆದದರಿಂದ ನಾನು ನೀತಿವಂತನೂ ನಿರಪರಾಧಿಯೂ ಎಂದು ನೋಡಿ ಯೆಹೋವನು ತಕ್ಕ ಪ್ರತಿಫಲವನ್ನು ಕೊಟ್ಟನು.",
			"25": "<sup>25</sup> ನೀನು ಕೃಪೆಯುಳ್ಳವನಿಗೆ ಕೃಪಾವಂತನೂ ದೋಷವಿಲ್ಲದವನಿಗೆ ನಿರ್ದೋಷಿಯೂ",
			"26": "<sup>26</sup> ಶುದ್ಧನಿಗೆ ಪರಿಶುದ್ಧನೂ ಮೂರ್ಖನಿಗೆ ವಕ್ರನೂ ಆಗಿರುವಿ.",
			"27": "<sup>27</sup> ದೀನರನ್ನು ಉದ್ಧರಿಸುವವನೂ ಹಮ್ಮಿನ ಕಣ್ಣುಳ್ಳವರನ್ನು ತಗ್ಗಿಸಿಬಿಡುವವನೂ ನೀನಲ್ಲವೋ?",
			"28": "<sup>28</sup> ನೀನೇ ನನ್ನ ದೀಪವನ್ನು ಹೊತ್ತಿಸುವವನಲ್ಲವೇ, ನನ್ನ ದೇವರಾದ ಯೆಹೋವನು ನನಗೆ ಬೆಳಕನ್ನು ಕೊಟ್ಟು ಕತ್ತಲನ್ನು ಪರಿಹರಿಸುವವನು.",
			"29": "<sup>29</sup> ನಿನ್ನ ಬಲದಿಂದ ದಂಡಿನ ಮೇಲೆ ಬೀಳುವೆನು; ನನ್ನ ದೇವರ ಸಹಾಯದಿಂದ ಪ್ರಾಕಾರವನ್ನು ಹಾರುವೆನು.",
			"30": "<sup>30</sup> ದೇವರ ಮಾರ್ಗವು ಯಾವ ದೋಷವೂ ಇಲ್ಲದ್ದು; ಯೆಹೋವನ ವಚನವು ಶುದ್ಧವಾದದ್ದು. ಆತನು ಆಶ್ರಿತರೆಲ್ಲರಿಗೆ ಗುರಾಣಿಯಾಗಿದ್ದಾನೆ.",
			"31": "<sup>31</sup> ಯೆಹೋವನಲ್ಲದೆ ದೇವರು ಯಾರು? ನಮ್ಮ ದೇವರ ಹೊರತು ಶರಣನು ಎಲ್ಲಿ?",
			"32": "<sup>32</sup> ನನಗೆ ಶೌರ್ಯವೆಂಬ ನಡುಕಟ್ಟನ್ನು ಬಿಗಿಯುವವನೂ ನನ್ನ ಮಾರ್ಗವನ್ನು ಸರಾಗಮಾಡುವವನೂ ಆತನೇ.",
			"33": "<sup>33</sup> ನನ್ನ ಕಾಲನ್ನು ಜಿಂಕೆಯ ಕಾಲಿನಂತೆ ಚುರುಕು ಮಾಡುತ್ತಾನೆ; ನನ್ನನ್ನು ಉನ್ನತಪ್ರದೇಶಗಳಲ್ಲಿ ನಿಲ್ಲಿಸುತ್ತಾನೆ.",
			"34": "<sup>34</sup> ಆತನೇ ನನಗೆ ಯುದ್ಧ ವಿದ್ಯೆಯನ್ನು ಕಲಿಸಿದ್ದರಿಂದ ನಾನು ತಾಮ್ರದ ಬಿಲ್ಲನ್ನಾದರೂ ಉಪಯೋಗಿಸಬಲ್ಲೆನು.",
			"35": "<sup>35</sup> ನೀನೇ ನನಗೋಸ್ಕರ ಗುರಾಣಿಯನ್ನು ಹಿಡಿದು ರಕ್ಷಿಸಿದ್ದೀ. ನಿನ್ನ ಬಲಗೈ ನನಗೆ ಆಧಾರ; ನಿನ್ನ ಕೃಪಾಕಟಾಕ್ಷವು ನನಗೆ ದೊಡ್ಡಸ್ತಿಕೆಯನ್ನು ಉಂಟುಮಾಡಿದೆ.",
			"36": "<sup>36</sup> ನೀನು ನನ್ನ ಕಾಲುಗಳಿಗೆ ವಿಶಾಲಸ್ಥಳವನ್ನು ಕೊಟ್ಟದ್ದರಿಂದ ನನ್ನ ಹರಡುಗಳು ಕದಲುವದಿಲ್ಲ.",
			"37": "<sup>37</sup> ನನ್ನ ಶತ್ರುಗಳನ್ನು ಹಿಂದಟ್ಟಿ ಹಿಡಿದುಕೊಳ್ಳುವೆನು; ಅವರನ್ನು ಇಲ್ಲದಂತೆ ಮಾಡುವವರೆಗೂ ಹಿಂದಿರುಗುವದಿಲ್ಲ.",
			"38": "<sup>38</sup> ಅವರನ್ನು ಹೊಡೆದು ಏಳಲಾರದಂತೆ ಮಾಡುವೆನು; ನನ್ನ ಪಾದದ ಕೆಳಗೆ ಬೀಳುವರು.",
			"39": "<sup>39</sup> ನೀನು ನನಗೆ ಯುದ್ಧಕ್ಕಾಗಿ ಶೌರ್ಯವೆಂಬ ನಡುಕಟ್ಟನ್ನು ಬಿಗಿದಿದ್ದೀ; ಎದುರಾಳಿಗಳನ್ನು ಕುಗ್ಗಿಸಿ ನನಗೆ ಅಧೀನ ಮಾಡಿದ್ದೀ.",
			"40": "<sup>40</sup> ನನ್ನ ಶತ್ರುಗಳನ್ನು ಬೆಂಗೊಟ್ಟು ಓಡಮಾಡಿದ್ದೀ; ನನ್ನ ಹಗೆಯವರನ್ನು ನಿರ್ಮೂಲಮಾಡುವೆನು.",
			"41": "<sup>41</sup> ಅವರು ಕೂಗಿಕೊಂಡರೂ ರಕ್ಷಿಸುವವನಿಲ್ಲ; ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಟ್ಟರೂ ಆತನು ಉತ್ತರವನ್ನು ಕೊಡಲೇ ಇಲ್ಲ.",
			"42": "<sup>42</sup> ಗಾಳಿಯಿಂದ ಬಡಿಸಿಕೊಂಡು ಹೋಗುವ ದೂಳನ್ನೋ ಎಂಬಂತೆ ಅವರನ್ನು ಪುಡಿಪುಡಿಮಾಡಿದೆನು. ಬೀದಿಯಲ್ಲಿರುವ ಕೆಸರನ್ನೋ ಎಂಬಂತೆ ಅವರನ್ನು ಎಸೆದುಬಿಟ್ಟೆನು.",
			"43": "<sup>43</sup> ಜನರ ಕಲಹಗಳಿಗೆ ನನ್ನನ್ನು ತಪ್ಪಿಸಿ ಜನಾಂಗಗಳಿಗೆ ದೊರೆಯಾಗುವಂತೆ ಮಾಡಿದ್ದೀ; ನಾನರಿಯದ ಜನಾಂಗದವರು ಸಹ ನನಗೆ ಅಧೀನರಾಗುವರು.",
			"44": "<sup>44</sup> ನನ್ನ ಸುದ್ದಿಯನ್ನು ಕೇಳಿದ ಮಾತ್ರಕ್ಕೆ ನನಗೆ ವಿಧೇಯರಾಗುವರು; ದೇಶಾಂತರದವರು ನನ್ನ ಮುಂದೆ ಮುದುರಿಕೊಳ್ಳುವರು.",
			"45": "<sup>45</sup> ಅವರು ಧೈರ್ಯಗುಂದಿದವರಾಗಿ ತಮ್ಮ ತಮ್ಮ ಕೋಟೆಗಳಿಂದ ನಡುಗುತ್ತಾ ಬರುವರು.",
			"46": "<sup>46</sup> ಯೆಹೋವನು ಚೈತನ್ಯಸ್ವರೂಪನು; ನನ್ನ ಶರಣನಿಗೆ ಸ್ತೋತ್ರ; ನನ್ನನ್ನು ರಕ್ಷಿಸುವ ದೇವರಿಗೆ ಕೊಂಡಾಟ.",
			"47": "<sup>47</sup> ಆತನು ನನ್ನ ಶತ್ರುಗಳಿಗೆ ಪ್ರತಿದಂಡನೆ ಮಾಡುವ ದೇವರು; ಜನಾಂಗಗಳನ್ನು ನನಗೆ ಅಧೀನಪಡಿಸುತ್ತಾನೆ.",
			"48": "<sup>48</sup> ಶತ್ರುಗಳಿಂದ ನನ್ನನ್ನು ಬಿಡಿಸುವಾತನೇ, ನೀನು ನನ್ನನ್ನು ನನ್ನ ಎದುರಾಳಿಗಳಿಗೆ ತಪ್ಪಿಸಿ ಉನ್ನತಪಡಿಸುತ್ತೀ; ಬಲಾತ್ಕಾರಿಗಳಿಂದ ನನ್ನನ್ನು ರಕ್ಷಿಸುತ್ತೀ.",
			"49": "<sup>49</sup> ಈ ಕಾರಣದಿಂದ ಯೆಹೋವನೇ, ಅನ್ಯಜನಗಳ ಮಧ್ಯದಲ್ಲಿ ನಿನ್ನನ್ನು ಸ್ತುತಿಸುವೆನು; ನಿನ್ನ ನಾಮವನ್ನು ಸಂಕೀರ್ತಿಸುವೆನು.",
			"50": "<sup>50</sup> ಆತನು ತಾನು ನೇಮಿಸಿದ ಅರಸನಿಗೋಸ್ಕರ ವಿಶೇಷ ರಕ್ಷಣೆಯನ್ನು ದಯಪಾಲಿಸುವವನಾಗಿದ್ದಾನೆ; ತಾನು ಅಭಿಷೇಕಿಸಿದ ದಾವೀದನಿಗೂ ಅವನ ಸಂತತಿಯವರಿಗೂ ಸದಾಕಾಲ ಕೃಪೆಯನ್ನು ಅನುಗ್ರಹಿಸುವವನಾಗಿದ್ದಾನೆ."
		},
		"19": {
			"1": "<sup>1</sup> ಆಕಾಶವು ದೇವರ ಪ್ರಭಾವವನ್ನು ಪ್ರಚುರಪಡಿಸುತ್ತದೆ; ಗಗನವು ಆತನ ಕೈಕೆಲಸವನ್ನು ತಿಳಿಸುತ್ತದೆ.",
			"2": "<sup>2</sup> ದಿನವು ದಿನಕ್ಕೆ ಪ್ರಕಟಿಸುತ್ತಿರುವದು; ರಾತ್ರಿಯು ರಾತ್ರಿಗೆ ಅರುಹುತ್ತಿರುವದು.",
			"3": "<sup>3</sup> ಶಬ್ದವಿಲ್ಲ, ಮಾತಿಲ್ಲ, ಅವುಗಳ ಸ್ವರ ಕೇಳಿಸುವದಿಲ್ಲ.",
			"4": "<sup>4</sup> ಆದರೂ ಅವುಗಳ ಪ್ರಭುತ್ವವು ಭೂಮಿಯಲ್ಲೆಲ್ಲಾ ಪ್ರಸರಿಸಿದೆ; ಅವುಗಳ ನುಡಿಗಳು ಲೋಕದ ಕಟ್ಟಕಡೆಯವರೆಗೂ ವ್ಯಾಪಿಸಿರುತ್ತವೆ. ಅಲ್ಲಿ ದೇವರು ಸೂರ್ಯನಿಗೋಸ್ಕರ ಗುಡಾರವನ್ನು ಏರ್ಪಡಿಸಿದ್ದಾನೆ.",
			"5": "<sup>5</sup> ಅವನು ತನ್ನ ಮಂಟಪದೊಳಗಿಂದ ಬರುವ ಮದಲಿಂಗನೋ ಎಂಬಂತೆ ಹೊರಟು ಬರುವನು. ಅವನು ಶೂರನಂತೆ ತನಗೆ ನೇಮಕವಾದ ಮಾರ್ಗದಲ್ಲಿ ಓಡುವದಕ್ಕೆ ಉಲ್ಲಾಸಗೊಂಡಿದ್ದಾನೆ.",
			"6": "<sup>6</sup> ಆಕಾಶದ ಒಂದು ಕಡೆಯಿಂದ ಹೊರಟು ಮತ್ತೊಂದು ಕಡೆಗೆ ಬರುತ್ತಾನೆ; ಅವನ ಬಿಸಲಿಗೆ ಮರೆಯಾದದ್ದು ಒಂದೂ ಇಲ್ಲ.",
			"7": "<sup>7</sup> ಯೆಹೋವನ ಧರ್ಮಶಾಸ್ತ್ರವು ಲೋಪವಿಲ್ಲದ್ದು; ಅದು ಪ್ರಾಣವನ್ನು ಉಜ್ಜೀವಿಸಮಾಡುವಂಥದ್ದು. ಯೆಹೋವನ ಕಟ್ಟಳೆ ನಂಬಿಕೆಗೆ ಯೋಗ್ಯವಾದದ್ದು; ಬುದ್ಧಿಹೀನರಿಗೆ ವಿವೇಕಪ್ರದವಾಗಿದೆ.",
			"8": "<sup>8</sup> ಯೆಹೋವನ ನಿಯಮಗಳು ನೀತಿಯುಳ್ಳವುಗಳಾಗಿವೆ; ಮನಸ್ಸನ್ನು ಹರ್ಷಪಡಿಸುತ್ತವೆ. ಯೆಹೋವನ ಆಜ್ಞೆ ಪವಿತ್ರವಾದದ್ದು; ಕಣ್ಣುಗಳನ್ನು ಕಳೆಗೊಳಿಸುತ್ತದೆ.",
			"9": "<sup>9</sup> ಯೆಹೋವನ ಭಯ ಪರಿಶುದ್ಧವಾಗಿದೆ; ಅದು ಶಾಶ್ವತವಾದದ್ದೇ. ಯೆಹೋವನ ವಿಧಿಗಳು ಯಥಾರ್ಥವಾದವುಗಳು; ಅವು ಕೇವಲ ನ್ಯಾಯವಾಗಿವೆ.",
			"10": "<sup>10</sup> ಅವು ಬಂಗಾರಕ್ಕಿಂತಲೂ ಅಪರಂಜಿರಾಶಿಗಿಂತಲೂ ಅಪೇಕ್ಷಿಸತಕ್ಕವುಗಳು. ಅವು ಜೇನಿಗಿಂತಲೂ ಶೋಧಿಸಿದ ಜೇನು ತುಪ್ಪಕ್ಕಿಂತಲೂ ಸಿಹಿಯಾಗಿವೆ.",
			"11": "<sup>11</sup> ಅವುಗಳ ಮೂಲಕ ನಿನ್ನ ದಾಸನಿಗೆ ಎಚ್ಚರಿಕೆಯಾಗುತ್ತದೆ; ಅವುಗಳನ್ನು ಕೈಕೊಳ್ಳುವದರಿಂದ ಬಹಳ ಫಲ ಉಂಟಾಗುತ್ತದೆ.",
			"12": "<sup>12</sup> ತನ್ನ ತಪ್ಪುಗಳನ್ನೆಲ್ಲಾ ತಿಳಿದುಕೊಳ್ಳುವವನು ಯಾವನು? ಮರೆಯಾದವುಗಳಿಂದ ನನ್ನನ್ನು ನಿರ್ಮಲಮಾಡು.",
			"13": "<sup>13</sup> ಅದಲ್ಲದೆ ಬೇಕೆಂದು ಪಾಪಮಾಡದಂತೆ ನಿನ್ನ ದಾಸನನ್ನು ಕಾಪಾಡು. ಅಂಥ ಪಾಪಗಳು ನನ್ನನ್ನು ಆಳದಿರಲಿ. ಆಗ ನಾನು ತಪ್ಪಿಲ್ಲದವನಾಗಿ ಮಹಾ ದ್ರೋಹಕ್ಕೆ ಒಳಗಾಗುವದಿಲ್ಲ.",
			"14": "<sup>14</sup> ಯೆಹೋವನೇ, ನನ್ನ ಶರಣನೇ, ನನ್ನ ವಿಮೋಚಕನೇ, ನನ್ನ ಮಾತುಗಳೂ ನನ್ನ ಹೃದಯದ ಧ್ಯಾನವೂ ನಿನಗೆ ಸಮರ್ಪಕವಾಗಿರಲಿ."
		},
		"20": {
			"1": "<sup>1</sup> ಇಕ್ಕಟ್ಟಿನಲ್ಲಿ ಯೆಹೋವನು ನಿನ್ನ ಪ್ರಾರ್ಥನೆಯನ್ನು ಕೇಳಲಿ; ಯಾಕೋಬನ ದೇವರ ನಾಮವು ನಿನ್ನನ್ನು ಉದ್ಧಾರ ಮಾಡಲಿ.",
			"2": "<sup>2</sup> ತನ್ನ ಪರಿಶುದ್ಧ ನಿವಾಸದಿಂದ ನಿನಗೆ ಸಹಾಯಮಾಡಲಿ; ಚೀಯೋನಿನಿಂದ ನಿನಗೆ ಆಧಾರ ದಯಪಾಲಿಸಲಿ.",
			"3": "<sup>3</sup> ನೀನು ಸಮರ್ಪಿಸಿದ ನೈವೇದ್ಯಗಳು ಆತನ ನೆನಪಿಗೆ ಬರಲಿ; ನೀನು ಮಾಡಿದ ಸರ್ವಾಂಗಹೋಮಗಳು ಆತನಿಗೆ ಮೆಚ್ಚಿಕೆಯಾಗಲಿ. ಸೆಲಾ.",
			"4": "<sup>4</sup> ಆತನು ನಿನ್ನ ಇಷ್ಟಾರ್ಥವನ್ನು ನೆರವೇರಿಸಲಿ; ನಿನ್ನ ಎಲ್ಲಾ ಸಂಕಲ್ಪಗಳನ್ನು ಸಫಲಮಾಡಲಿ.",
			"5": "<sup>5</sup> ನಿನ್ನ ಜಯದಲ್ಲಿ ಉತ್ಸಾಹಧ್ವನಿಮಾಡುವೆವು; ನಮ್ಮ ದೇವರ ಹೆಸರಿನಲ್ಲಿ ಧ್ವಜ ಎತ್ತುವೆವು. ಯೆಹೋವನು ನಿನ್ನ ಎಲ್ಲಾ ವಿಜ್ಞಾಪನೆಗಳನ್ನು ನೆರವೇರಿಸಲಿ.",
			"6": "<sup>6</sup> ಯೆಹೋವನು ತಾನು ಅಭಿಷೇಕಿಸಿದ ಅರಸನಿಗೆ ಜಯವನ್ನು ಅನುಗ್ರಹಿಸುವನೆಂದು ಈಗ ನನಗೆ ಗೊತ್ತಾಯಿತು. ಆತನು ತನ್ನ ಪವಿತ್ರಲೋಕದಿಂದ ಅವನ ಪ್ರಾರ್ಥನೆಗೆ ಸದುತ್ತರವನ್ನು ಕೊಡುವನು; ತನ್ನ ಭುಜಬಲದಿಂದ ಅವನಿಗೆ ವಿಜಯವನ್ನುಂಟು ಮಾಡುವನು.",
			"7": "<sup>7</sup> ಕೆಲವರು ರಥಬಲದಲ್ಲಿ, ಕೆಲವರು ಅಶ್ವ ಬಲದಲ್ಲಿ ಹೆಚ್ಚಳಪಡುತ್ತಾರೆ; ನಾವಾದರೋ ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಲ್ಲಿಯೇ.",
			"8": "<sup>8</sup> ಅವರು ಬಿದ್ದು ಹೋಗಿದ್ದಾರೆ; ನಾವಾದರೋ ಎದ್ದು ನಿಂತಿದ್ದೇವೆ.",
			"9": "<sup>9</sup> ಯೆಹೋವನೇ, ನಮ್ಮ ಅರಸನಿಗೆ ಜಯವನ್ನುಂಟುಮಾಡು; ನಾವು ಮೊರೆಯಿಡುವಾಗ ಸದುತ್ತರವನ್ನು ದಯಪಾಲಿಸು."
		},
		"21": {
			"1": "<sup>1</sup> ಯೆಹೋವನೇ, ನಿನ್ನ ಪರಾಕ್ರಮದಲ್ಲಿ ಅರಸನು ಸಂತೋಷಿಸುತ್ತಾನೆ; ನೀನು ಉಂಟುಮಾಡಿದ ಜಯಕ್ಕಾಗಿ ಎಷ್ಟೋ ಆನಂದಪಡುತ್ತಾನೆ.",
			"2": "<sup>2</sup> ನೀನು ಅವನ ಪ್ರಾರ್ಥನೆಯನ್ನು ನಿರಾಕರಿಸದೆ ಅವನ ಇಷ್ಟಾರ್ಥವನ್ನು ನೆರವೇರಿಸಿದಿಯಲ್ಲಾ. ಸೆಲಾ.",
			"3": "<sup>3</sup> ನಾನಾ ಶುಭಗಳೊಡನೆ ನೀನೇ ಅವನನ್ನು ಎದುರುಗೊಂಡಿಯಲ್ಲಾ; ಅವನ ತಲೆಯ ಮೇಲೆ ಸುವರ್ಣಕಿರೀಟವನ್ನು ಇಟ್ಟಿದ್ದೀ.",
			"4": "<sup>4</sup> ಅವನು ದೀರ್ಘಾಯುಷ್ಯವನ್ನು ಬೇಡಿಕೊಳ್ಳಲು ನೀನು ಯುಗಯುಗಾಂತರಗಳ ಆಯುಷ್ಯವನ್ನು ಅನುಗ್ರಹಿಸಿದ್ದೀ.",
			"5": "<sup>5</sup> ನಿನ್ನ ರಕ್ಷಣಕಾರ್ಯದಿಂದ ಅವನ ಘನತೆಯು ಬಹು ವೃದ್ಧಿಯಾಯಿತು; ಮಹಿಮಪ್ರಭಾವಗಳನ್ನು ಅವನಿಗೆ ಬರಮಾಡಿದ್ದೀ.",
			"6": "<sup>6</sup> ಅವನಿಗೆ ಶಾಶ್ವತವಾದ ಸೌಭಾಗ್ಯನಿಧಿಯನ್ನು ದಯಪಾಲಿಸಿದ್ದೀ; ಅವನನ್ನು ನಿನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಅತ್ಯಾನಂದ ಪಡಿಸಿದ್ದೀ.",
			"7": "<sup>7</sup> ಅರಸನು ಯೆಹೋವನಲ್ಲಿಯೇ ಭರವಸವಿಟ್ಟಿದ್ದಾನೆ; ಪರಾತ್ಪರನ ಕೃಪೆಯ ದೆಸೆಯಿಂದ ಅವನು ಕದಲುವದೇ ಇಲ್ಲ.",
			"8": "<sup>8</sup> ಶತ್ರುಗಳೆಲ್ಲರು ನಿನಗೆ ಸಿಕ್ಕುವರು; ಭುಜಬಲದಿಂದ ನಿನ್ನ ಹಗೆಗಳನ್ನು ಹಿಡಿಯುವಿ.",
			"9": "<sup>9</sup> ನೀನು ಪ್ರತ್ಯಕ್ಷನಾಗುವಾಗ ಉರಿಯುವ ಕುಲುಮೆಯಂತಿದ್ದು ಅವರನ್ನು ಬೂದಿಮಾಡುವಿ. ಯೆಹೋವನು ಮಹಾಕೋಪದಿಂದ ಅವರನ್ನು ನಾಶಮಾಡುವನು; ಆತನ ಕೋಪಾಗ್ನಿ ಅವರನ್ನು ದಹಿಸಿಬಿಡುವದು.",
			"10": "<sup>10</sup> ನೀನು ಅವರನ್ನು ಸಂಹರಿಸಿ ಭೂಮಿಯ ಮೇಲೆ ಅವರ ಸಂತತಿಯೇ ಇಲ್ಲದಂತೆ ಮಾಡುವಿ; ಅವರ ವಂಶದವರು ಮನುಷ್ಯರೊಳಗೆ ಉಳಿಯುವದೇ ಇಲ್ಲ.",
			"11": "<sup>11</sup> ಅವರು ತಂತ್ರೋಪಾಯಗಳನ್ನು ಕಲ್ಪಿಸಿ ನಿನಗೆ ಕೇಡನ್ನು ಮಾಡಬೇಕೆಂದು ಆಲೋಚಿಸಿದರೂ ಅದು ನಡೆಯುವದಿಲ್ಲ.",
			"12": "<sup>12</sup> ನೀನು ನಿನ್ನ ಬಾಣಗಳನ್ನು ಬಿಲ್ಲಿಗೆ ಹೂಡಿ ಅವರ ಮುಖಗಳ ಮೇಲೆ ಎಸೆದು ಅವರು ಬೆನ್ನು ತೋರಿಸಿ ಓಡಿಹೋಗುವಂತೆ ಮಾಡುವಿ.",
			"13": "<sup>13</sup> ಯೆಹೋವನೇ, ಪರಾಕ್ರಮದಿಂದ ನಿನ್ನನ್ನು ಪ್ರಸಿದ್ಧಿಪಡಿಸಿಕೋ; ನಾವು ಗಾಯನಮಾಡುತ್ತಾ ನಿನ್ನ ಶೂರತ್ವವನ್ನು ಕೊಂಡಾಡುವೆವು."
		},
		"22": {
			"1": "<sup>1</sup> ನನ್ನ ದೇವರೇ, ನನ್ನ ದೇವರೇ, ಯಾಕೆ ನನ್ನನ್ನು ಕೈಬಿಟ್ಟಿದ್ದೀ? ನೀನು ಯಾಕೆ ನನಗೆ ಸಹಾಯಮಾಡದೆಯೂ ನನ್ನ ಕೂಗನ್ನು ಕೇಳದೆಯೂ ದೂರವಾಗಿದ್ದೀ?",
			"2": "<sup>2</sup> ನನ್ನ ದೇವರೇ, ಹಗಲಿನಲ್ಲಿ ಮೊರೆಯಿಡುತ್ತೇನೆ; ಪ್ರತ್ಯುತ್ತರವೇ ಇಲ್ಲ; ಇರುಳಿನಲ್ಲಿಯೂ ನನಗೆ ಉಪಶಮನವಿಲ್ಲ.",
			"3": "<sup>3</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಸ್ತೋತ್ರಸಿಂಹಾಸನದಲ್ಲಿರುವಾತನೇ, ನೀನು ಪವಿತ್ರಸ್ವರೂಪನು.",
			"4": "<sup>4</sup> ನಮ್ಮ ಪೂರ್ವಿಕರು ನಿನ್ನಲ್ಲಿ ಭರವಸವಿಟ್ಟರು; ನಿನ್ನನ್ನು ನಂಬಿ ಉದ್ಧಾರವಾದರು.",
			"5": "<sup>5</sup> ನಿನಗೆ ಮೊರೆಯಿಟ್ಟು ವಿಮುಕ್ತರಾದರು; ನಿನ್ನಲ್ಲಿ ವಿಶ್ವಾಸವಿಟ್ಟು ಆಶಾಭಂಗಪಡಲಿಲ್ಲ.",
			"6": "<sup>6</sup> ನಾನಾದರೋ ಹುಳದಂಥವನೇ ಹೊರತು ಮನುಷ್ಯನಲ್ಲ; ಮನುಷ್ಯರಿಂದ ನಿಂದಿಸಲ್ಪಟ್ಟು ಜನರಿಂದ ತಿರಸ್ಕಾರ ಹೊಂದಿದ್ದೇನೆ.",
			"7": "<sup>7</sup> ನನ್ನನ್ನು ನೋಡುವವರೆಲ್ಲರೂ ಹಾಸ್ಯಮಾಡುತ್ತಾರೆ; ಅವರು ಓರೇ ತುಟಿಮಾಡಿ ತಲೆ ಆಡಿಸುತ್ತಾ -",
			"8": "<sup>8</sup> ಯೆಹೋವನು ತನ್ನನ್ನು ರಕ್ಷಿಸುವನೆಂದು ಆತನಲ್ಲಿ ಭರವಸವಿಟ್ಟಿದ್ದಾನಲ್ಲಾ; ಆತನು ಇವನನ್ನು ಮೆಚ್ಚಿದ್ದಾದರೆ ರಕ್ಷಿಸಲಿ ಎಂದು ಹೇಳುತ್ತಾರೆ.",
			"9": "<sup>9</sup> ತಾಯಿಯ ಗರ್ಭದೊಳಗಿಂದ ನನ್ನನ್ನು ಬರಮಾಡಿದವನು ನೀನೇ ಅಲ್ಲವೇ. ತಾಯಿಯ ಎದೆಯಲ್ಲಿ ನನ್ನನ್ನು ನಿಶ್ಚಿಂತೆಯಿಂದ ಇರಿಸಿದವನು ನೀನೇ ಅಲ್ಲವೇ.",
			"10": "<sup>10</sup> ಹುಟ್ಟಿದಂದಿನಿಂದ ನೀನೇ ನನಗೆ ಆಧಾರ; ತಾಯಿ ಹೆತ್ತಂದಿನಿಂದ ನನ್ನ ದೇವರು ನೀನೇ.",
			"11": "<sup>11</sup> ನನಗೀಗ ಕಷ್ಟವು ಪ್ರಾಪ್ತವಾಗಿದೆ; ಸಹಾಯಕರು ಯಾರೂ ಇಲ್ಲ, ದೂರವಾಗಿರಬೇಡ.",
			"12": "<sup>12</sup> ಬಹಳ ಗೂಳಿಗಳು ನನ್ನನ್ನು ಸುತ್ತಿಕೊಂಡಿವೆ; ಬಾಷಾನಿನ ಬಲವುಳ್ಳ ಹೋರಿಗಳು ನನ್ನನ್ನು ಮುತ್ತಿಕೊಂಡಿವೆ.",
			"13": "<sup>13</sup> ಅವರು ಗರ್ಜಿಸುವ ಉಗ್ರಸಿಂಹಗಳಂತೆ ನನ್ನನ್ನು ನುಂಗುವದಕ್ಕೆ ಬಾಯಿ ತೆರೆದಿದ್ದಾರೆ.",
			"14": "<sup>14</sup> ನಾನು ಹೊಯ್ಯಲ್ಪಟ್ಟ ನೀರಿನಂತಿದ್ದೇನೆ; ನನ್ನ ಎಲುಬುಗಳೆಲ್ಲಾ ಸಡಲಿಹೋಗಿವೆ. ನನ್ನ ಗುಂಡಿಗೆ ಮೇಣದಂತೆ ನನ್ನಲ್ಲಿ ಕರಗಿಹೋಗಿದೆ.",
			"15": "<sup>15</sup> ನನ್ನ ಶಕ್ತಿಯು ಬೋಕಿಯ ಹಾಗೆ ಒಣಗಿಹೋಗಿದೆ; ನನ್ನ ನಾಲಿಗೆಯು ಅಂಗುಳಕ್ಕೆ ಹತ್ತಿಹೋಗಿದೆ. ನೀನು ನನ್ನನ್ನು ಮಣ್ಣಿಗೆ ಸೇರಿಸುತ್ತೀ.",
			"16": "<sup>16</sup> ನಾಯಿಗಳು ನನ್ನನ್ನು ಸುತ್ತಿಕೊಂಡಿವೆ; ದುಷ್ಟರ ಗುಂಪು ನನ್ನನ್ನು ಆವರಿಸಿಕೊಂಡಿದೆ. ನನ್ನ ಕೈಕಾಲುಗಳನ್ನು ತಿವಿದಿದ್ದಾರೆ.",
			"17": "<sup>17</sup> ನನ್ನ ಎಲುಬುಗಳನ್ನೆಲ್ಲಾ ಎಣಿಸುವದಕ್ಕಾಗುವದು; ಅವರೋ ನನ್ನನ್ನು ನೋಡಿ ನೋಡಿ ಹಿಗ್ಗುತ್ತಾರೆ.",
			"18": "<sup>18</sup> ನನ್ನ ಮೇಲ್ಹೊದಿಕೆಯನ್ನು ತಮ್ಮಲ್ಲಿ ಪಾಲುಮಾಡಿಕೊಳ್ಳುತ್ತಾರೆ; ನನ್ನ ಅಂಗಿಗೋಸ್ಕರ ಚೀಟು ಹಾಕುತ್ತಾರೆ.",
			"19": "<sup>19</sup> ಯೆಹೋವನೇ, ನನ್ನ ರಕ್ಷಕನೇ, ನೀನಾದರೋ ದೂರವಾಗಿರಬೇಡ; ಬೇಗ ಬಂದು ಸಹಾಯಮಾಡು.",
			"20": "<sup>20</sup> ಕತ್ತಿಗೆ ಸಿಕ್ಕದಂತೆ ನನ್ನನ್ನು ತಪ್ಪಿಸು; ನನ್ನ ಪ್ರಿಯ ಪ್ರಾಣವು ನಾಯಿಯ ವಶವಾಗದಂತೆ ಕಾಪಾಡು.",
			"21": "<sup>21</sup> ಸಿಂಹಗಳ ಬಾಯಿಂದಲೂ ಕಾಡುಕೋಣಗಳ ಕೊಂಬುಗಳಿಂದಲೂ ನನ್ನನ್ನು ತಪ್ಪಿಸು. ನನಗೆ ಸದುತ್ತರವನ್ನು ಕೊಟ್ಟಿದ್ದೀಯಲ್ಲಾ.",
			"22": "<sup>22</sup> ನಿನ್ನ ನಾಮ ಮಹಿಮೆಯನ್ನು ನನ್ನ ಸಹೋದರರಿಗೆ ತಿಳಿಸುವೆನು; ಸಭಾಮಧ್ಯದಲ್ಲಿ ನಿನ್ನನ್ನು ಸ್ತುತಿಸುವೆನು.",
			"23": "<sup>23</sup> ಯೆಹೋವನ ಭಕ್ತರೇ, ಆತನನ್ನು ಸ್ತುತಿಸಿರಿ; ಯಾಕೋಬವಂಶದವರೇ, ನೀವೆಲ್ಲರೂ ಆತನನ್ನು ಕೊಂಡಾಡಿರಿ. ಇಸ್ರಾಯೇಲ ವಂಶಸ್ಥರೇ, ನೀವೆಲ್ಲರೂ ಆತನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿರ್ರಿ.",
			"24": "<sup>24</sup> ಆತನು ಕುಗ್ಗಿಹೋದವನ ದುರವಸ್ಥೆಯನ್ನು ತಿರಸ್ಕರಿಸಲಿಲ್ಲ, ಅದಕ್ಕೆ ಅಸಹ್ಯಪಡಲಿಲ್ಲ; ತನ್ನ ಮುಖವನ್ನು ಅವನಿಗೆ ಮರೆಮಾಡದೆ ಅವನ ಪ್ರಾರ್ಥನೆಗೆ ಕಿವಿಗೊಟ್ಟನು.",
			"25": "<sup>25</sup> ನಾನು ಮಹಾಸಭೆಯಲ್ಲಿ ಮಾಡುವ ಸ್ತೋತ್ರಕ್ಕೆ ನೀನೇ ಆಧಾರನು; ನನ್ನ ಹರಕೆಗಳನ್ನು ನಿನ್ನ ಭಕ್ತರ ಮುಂದೆ ಸಲ್ಲಿಸುವೆನು.",
			"26": "<sup>26</sup> ದೀನರು ಉಂಡು ತೃಪ್ತರಾಗುವರು; ಯೆಹೋವನ ಭಕ್ತರೆಲ್ಲರೂ ಆತನನ್ನು ಕೊಂಡಾಡುವರು. ನಿಮ್ಮ ಅಂತರಾತ್ಮವು ಯಾವಾಗಲೂ ಚೈತನ್ಯವುಳ್ಳದ್ದಾಗಿರಲಿ.",
			"27": "<sup>27</sup> ಭೂಮಂಡಲದವರೆಲ್ಲರು ಎಚ್ಚರಗೊಂಡು ಯೆಹೋವನ ಕಡೆಗೆ ತಿರುಗಿಕೊಳ್ಳುವರು; [ಯೆಹೋವನೇ,] ಎಲ್ಲಾ ಜನಾಂಗಗಳವರು ನಿನಗೆ ಅಡ್ಡಬೀಳುವರು.",
			"28": "<sup>28</sup> ರಾಜ್ಯವು ಯೆಹೋವನದೇ; ಎಲ್ಲಾ ಜನಾಂಗಗಳಿಗೂ ಆತನೇ ಒಡೆಯನು.",
			"29": "<sup>29</sup> ಭೂಲೋಕದಲ್ಲಿರುವ ಪುಷ್ಟರೆಲ್ಲರೂ ಉಂಡು ಆರಾಧಿಸುವರು; ತಮ್ಮ ಜೀವವನ್ನು ಉಳಿಸಿಕೊಳ್ಳಲಾರದೆ ಮಣ್ಣುಪಾಲಾಗುವವರೆಲ್ಲರೂ ಆತನಿಗೆ ಅಡ್ಡಬೀಳುವರು.",
			"30": "<sup>30</sup> ಆತನನ್ನು ಸೇವಿಸುವವರ ಸಂತಾನದವರು ಯೆಹೋವನ ಸಂತಾನದವರೆಂದು ಎಣಿಸಲ್ಪಡುವರು.",
			"31": "<sup>31</sup> ಅವರು ಬಂದು ಆತನ ನೀತಿಯನ್ನೂ ಆತನು ನಡಿಸಿದ ಕಾರ್ಯಗಳನ್ನೂ ಮುಂದೆ ಹುಟ್ಟುವ ಜನಕ್ಕೆ ತಿಳಿಸುವರು."
		},
		"23": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ನನಗೆ ಕುರುಬನು; ಕೊರತೆಪಡೆನು.",
			"2": "<sup>2</sup> ಆತನು ಹಸುರುಗಾವಲುಗಳಲ್ಲಿ ನನ್ನನ್ನು ತಂಗಿಸುತ್ತಾನೆ; ವಿಶ್ರಾಂತಿಕರವಾದ ನೀರುಗಳ ಬಳಿಗೆ ಬರಮಾಡುತ್ತಾನೆ.",
			"3": "<sup>3</sup> ನನ್ನ ಪ್ರಾಣವನ್ನು ಉಜ್ಜೀವಿಸಮಾಡಿ ತನ್ನ ಹೆಸರಿಗೆ ತಕ್ಕಂತೆ ನೀತಿಮಾರ್ಗದಲ್ಲಿ ನನ್ನನ್ನು ನಡಿಸುತ್ತಾನೆ.",
			"4": "<sup>4</sup> ನಾನು ಕಾರ್ಗತ್ತಲಿನ ಕಣಿವೆಯಲ್ಲಿ ನಡೆಯುವಾಗಲೂ ನೀನು ಹತ್ತಿರವಿರುವದರಿಂದ ಕೇಡಿಗೆ ಹೆದರೆನು; ನಿನ್ನ ದೊಣ್ಣೆಯೂ ನಿನ್ನ ಕೋಲೂ ನನಗೆ ಧೈರ್ಯ ಕೊಡುತ್ತವೆ.",
			"5": "<sup>5</sup> ವೈರಿಗಳ ಮುಂದೆಯೇ ನೀನು ನನಗೋಸ್ಕರ ಔತಣವನ್ನು ಸಿದ್ಧಪಡಿಸುತ್ತೀ; ನನ್ನ ತಲೆಗೆ ತೈಲವನ್ನು ಹಚ್ಚಿಸುತ್ತೀ. ನನ್ನ ಪಾತ್ರೆಯು ತುಂಬಿ ಹೊರಸೂಸುತ್ತದೆ.",
			"6": "<sup>6</sup> ನಿಶ್ಚಯವಾಗಿ ನನ್ನ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ಶುಭವೂ ಕೃಪೆಯೂ ನನ್ನನ್ನು ಹಿಂಬಾಲಿಸುವವು. ನಾನು ಸದಾಕಾಲವೂ ಯೆಹೋವನ ಮಂದಿರದಲ್ಲಿ ವಾಸಿಸುವೆನು."
		},
		"24": {
			"1": "<sup>1</sup> ಭೂಮಿಯೂ ಅದರಲ್ಲಿರುವ ಸಮಸ್ತವೂ ಯೆಹೋವನದು; ಲೋಕವೂ ಅದರ ನಿವಾಸಿಗಳೂ ಆತನವೇ.",
			"2": "<sup>2</sup> ಆತನೇ ಅದರ ಅಸ್ತಿವಾರವನ್ನು ಸಾಗರದ ಮೇಲೆ ಹಾಕಿದವನು; ಅದನ್ನು ಜಲರಾಶಿಗಳ ಮೇಲೆ ಸ್ಥಿರಪಡಿಸಿದವನು ಆತನೇ.",
			"3": "<sup>3</sup> ಯೆಹೋವನ ಪರ್ವತವನ್ನು ಹತ್ತತಕ್ಕವನು ಯಾರು? ಆತನ ಪವಿತ್ರಸ್ಥಾನದಲ್ಲಿ ನಿಲ್ಲುವದಕ್ಕೆ ಎಂಥವನು ಯೋಗ್ಯನು?",
			"4": "<sup>4</sup> ಯಾವನು ಅಯೋಗ್ಯಕಾರ್ಯಗಳಲ್ಲಿ ಮನಸ್ಸಿಡದೆ ಮೋಸಪ್ರಮಾಣಮಾಡದೆ ಶುದ್ಧಹಸ್ತವೂ ನಿರ್ಮಲಮನಸ್ಸೂ ಉಳ್ಳವನಾಗಿದ್ದಾನೋ",
			"5": "<sup>5</sup> ಅವನೇ ಯೆಹೋವನಿಂದ ಶುಭವನ್ನು ಹೊಂದುವನು; ತನ್ನ ರಕ್ಷಕನಾದ ದೇವರಿಂದ ನೀತಿಫಲವನ್ನು ಪಡೆಯುವನು.",
			"6": "<sup>6</sup> ಇಂಥವರೇ ಆತನ ದರ್ಶನವನ್ನು ಬಯಸುವವರು. ಯಾಕೋಬ್ಯರ ದೇವರೇ, ನಿನ್ನ ಸಾನ್ನಿಧ್ಯವನ್ನು ಸೇರುವವರು ಇಂಥವರೇ. ಸೆಲಾ.",
			"7": "<sup>7</sup> ದ್ವಾರಗಳೇ, ಉನ್ನತವಾಗಿರ್ರಿ; ಪುರಾತನವಾದ ಕದಗಳೇ, ತೆರೆದುಕೊಂಡಿರ್ರಿ; ಮಹಾಪ್ರಭಾವವುಳ್ಳ ಅರಸನು ಆಗಮಿಸುತ್ತಾನೆ.",
			"8": "<sup>8</sup> ಮಹಾಪ್ರಭಾವವುಳ್ಳ ಈ ಅರಸನು ಯಾರು? ಮಹಾ ಬಲಿಷ್ಠನೂ ವಿಶೇಷ ಪರಾಕ್ರಮಿಯೂ ಆಗಿರುವ ಯೆಹೋವ, ಯುದ್ಧವೀರನಾಗಿರುವ ಯೆಹೋವ.",
			"9": "<sup>9</sup> ದ್ವಾರಗಳೇ, ಉನ್ನತವಾಗಿರ್ರಿ; ಪುರಾತನವಾದ ಕದಗಳೇ, ತೆರೆದುಕೊಂಡಿರ್ರಿ; ಮಹಾಪ್ರಭಾವವುಳ್ಳ ಅರಸನು ಆಗಮಿಸುತ್ತಾನೆ.",
			"10": "<sup>10</sup> ಮಹಾಪ್ರಭಾವವುಳ್ಳ ಈ ಅರಸನು ಯಾರು? ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೇ; ಮಹಾಪ್ರಭಾವವುಳ್ಳ ಅರಸನು ಈತನೇ. ಸೆಲಾ."
		},
		"25": {
			"1": "<sup>1</sup> ಯೆಹೋವನೇ, ನಿನ್ನಲ್ಲಿ ಮನಸ್ಸಿಟ್ಟಿದ್ದೇನೆ; ನನ್ನ ದೇವರೇ, ನಿನ್ನನ್ನೇ ನಂಬಿದ್ದೇನೆ,",
			"2": "<sup>2</sup> ನನಗೆ ಆಶಾಭಂಗಪಡಿಸಬೇಡ. ಶತ್ರುಗಳ ಉತ್ಸಾಹಕ್ಕೆ ಆಸ್ಪದಮಾಡಬೇಡ.",
			"3": "<sup>3</sup> ವೃಥಾದ್ರೋಹಿಗಳಿಗೆ ಆಶಾಭಂಗವಾಗಬೇಕೇ ಹೊರತು ನಿನ್ನನ್ನು ನಿರೀಕ್ಷಿಸಿದವರಿಗೆ ಎಂದಿಗೂ ಆಗಬಾರದು.",
			"4": "<sup>4</sup> ಯೆಹೋವನೇ, ನಿನ್ನ ಮಾರ್ಗವನ್ನು ನನಗೆ ತಿಳಿಸು; ನೀನು ಒಪ್ಪುವ ದಾರಿಯನ್ನು ತೋರಿಸು.",
			"5": "<sup>5</sup> ನಿನ್ನ ಸತ್ಯಾನುಸಾರವಾಗಿ ನನ್ನನ್ನು ನಡಿಸುತ್ತಾ ಉಪದೇಶಿಸು; ನೀನೇ ನನ್ನನ್ನು ರಕ್ಷಿಸುವ ದೇವರು; ಹಗಲೆಲ್ಲಾ ನಿನ್ನನ್ನೇ ನಿರೀಕ್ಷಿಸುವವನಾಗಿದ್ದೇನೆ.",
			"6": "<sup>6</sup> ಯೆಹೋವನೇ, ನೀನು ಆದಿಯಿಂದಲೂ ನನ್ನನ್ನು ಕರುಣಿಸುವವನಾಗಿ ನನಗೆ ಮಾಡಿದ ಮಹೋಪಕಾರಗಳನ್ನು ನೆನಸಿಕೋ.",
			"7": "<sup>7</sup> ಯೆಹೋವನೇ, ನನ್ನ ಯೌವನದ ತಪ್ಪುಗಳನ್ನೂ ದ್ರೋಹಗಳನ್ನೂ ಮನಸ್ಸಿನಲ್ಲಿಡದೆ ನಿನ್ನ ಕೃಪೆಗೆ ತಕ್ಕಂತೆ ದಯಾಪೂರ್ವಕವಾಗಿ ನನ್ನನ್ನು ನೆನಸು.",
			"8": "<sup>8</sup> ಯೆಹೋವನು ದಯಾಳುವೂ ಸತ್ಯಸ್ವರೂಪನೂ ಆಗಿದ್ದಾನೆ; ದಾರಿತಪ್ಪಿದವರನ್ನು ಬೋಧಿಸಿ ಸನ್ಮಾರ್ಗದಲ್ಲಿ ನಡಿಸುವನು.",
			"9": "<sup>9</sup> ಆತನು ದೀನರನ್ನು ತನ್ನ ವಿಧಿಗನುಗುಣವಾಗಿ ನಡಿಸುವನು; ಅವರಿಗೆ ತನ್ನ ಮಾರ್ಗವನ್ನು ತೋರಿಸುವನು.",
			"10": "<sup>10</sup> ಯೆಹೋವನ ವಿಧಿನಿಬಂಧನೆಗಳನ್ನು ಕೈಕೊಂಡು ನಡೆಯುವವರಿಗೆ ಆತನ ಎಲ್ಲಾ ಮಾರ್ಗಗಳು ಕೃಪೆಯೂ ಸತ್ಯತೆಯೂ ಉಳ್ಳವು.",
			"11": "<sup>11</sup> ಯೆಹೋವನೇ, ನನ್ನ ಪಾಪವು ಬಹುಘೋರವಾಗಿದೆ; ಆದರೂ ನಿನ್ನ ಹೆಸರಿನ ನಿಮಿತ್ತ ಅದನ್ನು ಕ್ಷಮಿಸು.",
			"12": "<sup>12</sup> ಯಾವನು ಯೆಹೋವನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವನೋ ಅವನಿಗೆ ಉಚಿತವಾದ ಮಾರ್ಗವನ್ನು ಆತನು ಬೋಧಿಸುವನು.",
			"13": "<sup>13</sup> ಅವನು ಸುಖದಿಂದಲೇ ಇರುವನು; ಅವನ ಸಂತತಿಯವರು ದೇಶವನ್ನು ಅನುಭವಿಸುವರು.",
			"14": "<sup>14</sup> ಯೆಹೋವನು ತನ್ನ ಸದ್ಭಕ್ತರಿಗೆ ಆಪ್ತಮಿತ್ರನಂತಿರುವನು; ಅವರಿಗೇ ತನ್ನ ಒಡಂಬಡಿಕೆಯ ಅನುಭವವನ್ನು ದಯಪಾಲಿಸುವನು.",
			"15": "<sup>15</sup> ನನ್ನ ದೃಷ್ಟಿ ಯಾವಾಗಲೂ ಯೆಹೋವನಲ್ಲಿದೆ; ಆತನೇ ನನ್ನ ಕಾಲುಗಳನ್ನು ಬಲೆಯಿಂದ ಬಿಡಿಸುವವನು.",
			"16": "<sup>16</sup> ನಾನು ಒಬ್ಬೊಂಟಿಗನೂ ಬಾಧೆಪಡುವವನೂ ಆಗಿದ್ದೇನೆ; ನೀನು ಕಟಾಕ್ಷವಿಟ್ಟು ನನ್ನನ್ನು ಕರುಣಿಸು.",
			"17": "<sup>17</sup> ನನ್ನ ಮನೋವ್ಯಥೆಗಳನ್ನು ನಿವಾರಿಸು; ಸಂಕಟಗಳಿಂದ ನನ್ನನ್ನು ಬಿಡಿಸು.",
			"18": "<sup>18</sup> ನಾನು ಕುಗ್ಗಿರುವದನ್ನೂ ಕಷ್ಟಪಡುವದನ್ನೂ ನೋಡಿ ನನ್ನ ಎಲ್ಲಾ ಪಾಪಗಳನ್ನು ಪರಿಹರಿಸು.",
			"19": "<sup>19</sup> ನನಗೆ ಎಷ್ಟೋ ಮಂದಿ ಶತ್ರುಗಳು ಇದ್ದಾರಲ್ಲಾ; ಅವರು ಕಡು ವೈರದಿಂದ ನನ್ನನ್ನು ಹಗೆಮಾಡುತ್ತಾರೆ.",
			"20": "<sup>20</sup> ನನ್ನ ಪ್ರಾಣವನ್ನು ಕಾಪಾಡಿ ಉಳಿಸು; ನಿನ್ನನ್ನೇ ಆಶ್ರಯಿಸಿಕೊಂಡಿದ್ದೇನೆ, ನನಗೆ ಆಶಾಭಂಗವಾಗಬಾರದು.",
			"21": "<sup>21</sup> ನಿನ್ನನ್ನೇ ನಿರೀಕ್ಷಿಸಿಕೊಂಡಿದ್ದೇನೆ; ನಿರ್ದೋಷಭಕ್ತಿಯೂ ಯಥಾರ್ಥತ್ವವೂ ನನ್ನನ್ನು ಕಾಯಲಿ.",
			"22": "<sup>22</sup> ದೇವರೇ, ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಎಲ್ಲಾ ಬಾಧೆಗಳಿಂದ ವಿಮೋಚಿಸು."
		},
		"26": {
			"1": "<sup>1</sup> ಯೆಹೋವನೇ, ನನಗೋಸ್ಕರ ನ್ಯಾಯವನ್ನು ನಿರ್ಣಯಿಸು. ನಾನಾದರೋ ನಿರ್ದೋಷಿಯಾಗಿಯೇ ನಡೆದುಕೊಂಡಿದ್ದೇನೆ. ನಾನು ಕದಲದೆ ಯೆಹೋವನಲ್ಲೇ ಭರವಸವಿಟ್ಟಿದ್ದೇನೆ.",
			"2": "<sup>2</sup> ಯೆಹೋವನೇ, ನನ್ನನ್ನು ಪರೀಕ್ಷಿಸು, ಪರಿಶೀಲಿಸು; ನನ್ನ ಅಂತರಿಂದ್ರಿಯವನ್ನೂ ಹೃದಯವನ್ನೂ ಪರಿಶೋಧಿಸು.",
			"3": "<sup>3</sup> ನಿನ್ನ ಕೃಪೆಯನ್ನು ನನ್ನ ದೃಷ್ಟಿಯಲ್ಲೇ ಇಟ್ಟುಕೊಂಡಿದ್ದೇನೆ; ನಿನ್ನ ಸತ್ಯದಲ್ಲಿ ನಡೆದುಕೊಂಡಿದ್ದೇನೆ.",
			"4": "<sup>4</sup> ನಾನು ಕುಟಿಲಸ್ವಭಾವಿಗಳ ಸಹವಾಸಮಾಡುವವನಲ್ಲ; ಕಪಟಿಗಳನ್ನು ಸೇರುವವನಲ್ಲ.",
			"5": "<sup>5</sup> ನನಗೆ ದುರ್ಜನರ ಕೂಟವು ಅಸಹ್ಯ; ದುಷ್ಟರ ಸಂಗವು ಬೇಕಿಲ್ಲ.",
			"6": "<sup>6</sup> ಯೆಹೋವನೇ, ನಾನು ನಿಷ್ಕಳಂಕನೆಂದು ಕೈಗಳನ್ನು ತೊಳೆದುಕೊಂಡವನಾಗಿ",
			"7": "<sup>7</sup> ನಿನ್ನ ಅದ್ಭುತಕೃತ್ಯವರ್ಣನೆಯ ಪದಗಳನ್ನು ಹಾಡುತ್ತಾ ನಿನ್ನ ಯಜ್ಞವೇದಿಯನ್ನು ಪ್ರದಕ್ಷಿಣೆಮಾಡುವೆನು.",
			"8": "<sup>8</sup> ಯೆಹೋವನೇ, ನಿನ್ನ ನಿವಾಸವು ನನಗೆ ಎಷ್ಟೋ ಪ್ರಿಯ; ನಿನ್ನ ಪ್ರಭಾವಸ್ಥಾನವು ನನಗೆ ಇಷ್ಟ.",
			"9": "<sup>9</sup> ಪಾಪಿಷ್ಠರ ಪ್ರಾಣದ ಸಂಗಡ ನನ್ನ ಪ್ರಾಣವನ್ನೂ ತೆಗೆಯಬೇಡ; ಕೊಲೆಪಾತಕರ ಜೀವದೊಂದಿಗೆ ನನ್ನ ಜೀವವನ್ನೂ ತೆಗೆಯಬೇಡ.",
			"10": "<sup>10</sup> ಅವರ ಕೈಗಳು ಬಲಾತ್ಕಾರ ನಡಿಸುತ್ತವೆ; ಅವರ ಬಲಗೈ ಲಂಚದಿಂದ ತುಂಬಿದೆ.",
			"11": "<sup>11</sup> ನಾನಾದರೋ ನಿರ್ದೋಷಿಯಾಗಿಯೇ ನಡೆದುಕೊಳ್ಳುವವನು; ಯೆಹೋವನೇ, ಅವರಿಂದ ನನ್ನನ್ನು ವಿಮೋಚಿಸಿ ಪ್ರಸನ್ನನಾಗಿರು.",
			"12": "<sup>12</sup> ನನ್ನ ಪಾದವು ಸಮಭೂಮಿಯಲ್ಲಿ ನಿಂತಿದೆ; ಕೂಡಿದ ಸಭೆಗಳಲ್ಲಿ ಯೆಹೋವನನ್ನು ಕೊಂಡಾಡುವೆನು."
		},
		"27": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ನನಗೆ ಬೆಳಕೂ ರಕ್ಷಕನೂ ಆಗಿದ್ದಾನೆ; ನಾನು ಯಾರಿಗೆ ಭಯಪಟ್ಟೇನು? ಯೆಹೋವನು ನನ್ನ ಪ್ರಾಣದ ಆಧಾರವು; ನಾನು ಯಾರಿಗೆ ಹೆದರೇನು?",
			"2": "<sup>2</sup> ನನ್ನನ್ನು ಬಾಧಿಸುತ್ತಿರುವ ದುರ್ವೈರಿಗಳು ನನ್ನನ್ನು ನುಂಗಿಬಿಡಬೇಕೆಂದು ಬಂದು ತಾವೇ ನೆಲಕ್ಕೆ ಬಿದ್ದುಹೋದರು.",
			"3": "<sup>3</sup> ನನಗೆ ವಿರೋಧವಾಗಿ ದಂಡುಬಂದಿಳಿದರೂ ನನಗೇನೂ ಭಯವಿಲ್ಲ; ಚತುರಂಗಬಲವು ಯುದ್ಧಸನ್ನದ್ಧವಾಗಿ ನಿಂತರೂ ಭರವಸವುಳ್ಳವನಾಗಿಯೇ ಇರುವೆನು.",
			"4": "<sup>4</sup> ನನ್ನ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ಯೆಹೋವನ ಮನೆಯಲ್ಲಿ ವಾಸಮಾಡುತ್ತಾ ಆತನ ಪ್ರಸನ್ನತೆಯನ್ನು ನೋಡುವದಕ್ಕೂ ಆತನ ಮಂದಿರದಲ್ಲಿ ಧ್ಯಾನಮಾಡುವದಕ್ಕೂ ನನಗೆ ಅಪ್ಪಣೆಯಾಗಬೇಕೆಂಬ ಒಂದೇ ವರವನ್ನು ಯೆಹೋವನಿಂದ ಕೇಳಿಕೊಂಡು ಅದನ್ನೇ ಎದುರುನೋಡುತ್ತಿರುವೆನು.",
			"5": "<sup>5</sup> ಅಪಾಯಕಾಲದಲ್ಲಿ ಆತನು ನನ್ನನ್ನು ಗುಪ್ತಸ್ಥಳದಲ್ಲಿ ಅಡಗಿಸುವನು; ತನ್ನ ಗುಡಾರವೆಂಬ ಆಶ್ರಯಸ್ಥಾನದಲ್ಲಿ ನನ್ನನ್ನು ಭದ್ರಪಡಿಸುವನು; ಪರ್ವತಾಗ್ರದಲ್ಲಿ ನನ್ನನ್ನು ಸುರಕ್ಷಿತವಾಗಿ ಇರಿಸುವನು.",
			"6": "<sup>6</sup> ಹೀಗಿರುವದರಿಂದ ನನ್ನ ಸುತ್ತಲಿರುವ ವೈರಿಗಳ ಮೇಲೆ ನನ್ನ ತಲೆ ಎತ್ತಲ್ಪಟ್ಟಿರುವದು; ನಾನು ಯೆಹೋವನ ಗುಡಾರದಲ್ಲಿ ಉತ್ಸಾಹಧ್ವನಿಯೊಡನೆ ಯಜ್ಞಗಳನ್ನು ಸಮರ್ಪಿಸುವೆನು. ಆತನನ್ನು ಹಾಡುತ್ತಾ ವಾದ್ಯಬಾರಿಸುವೆನು.",
			"7": "<sup>7</sup> ಯೆಹೋವನೇ, ನಿನಗೆ ಗಟ್ಟಿಯಾಗಿ ಮೊರೆಯಿಡುತ್ತೇನೆ; ನನ್ನನ್ನು ಕರುಣಿಸಿ ಸದುತ್ತರವನ್ನು ದಯಪಾಲಿಸು.",
			"8": "<sup>8</sup> ನನ್ನ ಸಾನ್ನಿಧ್ಯಕ್ಕೆ ಬಾ ಎಂಬ ನಿನ್ನ ಮಾತಿಗೆ ನಾನು - ಯೆಹೋವನೇ, ನಿನ್ನ ಸಾನ್ನಿಧ್ಯಕ್ಕೆ ಬರಲೇ ಬರುವೆನು ಎಂದು ಉತ್ತರ ಕೊಟ್ಟೆನು.",
			"9": "<sup>9</sup> ನನಗೆ ವಿಮುಖನಾಗಿರಬೇಡ; ನಿನ್ನ ಸೇವಕನನ್ನು ಕೋಪದಿಂದ ತಳ್ಳಬೇಡ. ನೀನೇ ನನಗೆ ಸಹಾಯಕನಾಗಿದ್ದೆಯಲ್ಲವೇ; ನನ್ನನ್ನು ರಕ್ಷಿಸಿದ ದೇವರೇ, ಕೈಬಿಡಬೇಡ, ತೊರೆದು ಬಿಡಬೇಡ.",
			"10": "<sup>10</sup> ತಂದೆತಾಯಿಗಳು ನನ್ನನ್ನು ತೊರೆದುಬಿಟ್ಟರೇನು; ಯೆಹೋವನು ನನ್ನನ್ನು ಸೇರಿಸಿಕೊಳ್ಳುವನು.",
			"11": "<sup>11</sup> ಯೆಹೋವನೇ, ನಿನ್ನ ಮಾರ್ಗವನ್ನು ನನಗೆ ಬೋಧಿಸು; ಹೊಂಚುಹಾಕಿರುವವರಿಗೆ ಸಿಕ್ಕದ ಹಾಗೆ ನನ್ನನ್ನು ಸಮವಾದ ದಾರಿಯಲ್ಲಿ ನಡಿಸು.",
			"12": "<sup>12</sup> ಸುಳ್ಳುಸಾಕ್ಷಿಗಳೂ ಬೆದರಿಸುವವರೂ ನನಗೆ ವಿರೋಧವಾಗಿ ಎದ್ದಿದ್ದಾರೆ; ಇಂಥ ವೈರಿಗಳ ವಶಕ್ಕೆ ನನ್ನನ್ನು ಕೊಡಬೇಡ.",
			"13": "<sup>13</sup> ಜೀವಲೋಕದಲ್ಲಿಯೇ ಯೆಹೋವನ ದಯೆಯನ್ನು ಅನುಭವಿಸುವೆನೆಂದು ದೃಢವಾಗಿ ನಂಬಿದ್ದೇನೆ.",
			"14": "<sup>14</sup> ಯೆಹೋವನನ್ನು ನಿರೀಕ್ಷಿಸಿಕೊಂಡಿರು; ದೃಢವಾಗಿರು; ನಿನ್ನ ಹೃದಯವು ಧೈರ್ಯದಿಂದಿರಲಿ; ಯೆಹೋವನನ್ನು ನಿರೀಕ್ಷಿಸಿಕೊಂಡೇ ಇರು."
		},
		"28": {
			"1": "<sup>1</sup> ಯೆಹೋವನೇ, ನನ್ನ ಶರಣನೇ, ನಿನಗೆ ಮೊರೆಯಿಡುತ್ತೇನೆ; ಕೇಳದೆ ಇರಬೇಡ. ನೀನು ಕಿವಿಗೊಡದೆ ಹೋದರೆ ನಾನು ಸತ್ತವರಿಗೆ ಸಮಾನನಾಗುವೆನಲ್ಲವೇ.",
			"2": "<sup>2</sup> ನೀನು ವಾಸಿಸುವ ಮಹಾಪರಿಶುದ್ಧ ಸ್ಥಾನದ ಕಡೆಗೆ ನಾನು ಕೈಯೆತ್ತಿಕೊಂಡು ಮೊರೆಯಿಡುತ್ತೇನಲ್ಲಾ; ನನ್ನ ವಿಜ್ಞಾಪನೆಯನ್ನು ಲಾಲಿಸು.",
			"3": "<sup>3</sup> ನೀನು ದುಷ್ಟರೊಡನೆಯೂ ಪಾತಕಿಗಳ ಸಂಗಡಲೂ ನನ್ನನ್ನೂ ಎಳೆದುಕೊಂಡು ಹೋಗಬೇಡ. ಅವರು ಹೊರಗೆ ಒಳ್ಳೇದಾಗಲಿ ಎಂದು ಹೇಳಿದರೂ ಒಳಗೆ ಕೇಡಾಗಲಿ ಎಂದು ಯೋಚಿಸುವವರು.",
			"4": "<sup>4</sup> ಅವರ ದುಷ್ಕೃತ್ಯಗಳಿಗೂ ಕೆಡುಕುಗಳಿಗೂ ಸರಿಯಾದ ಪ್ರತಿಫಲವನ್ನು ಅವರಿಗೆ ಕೊಡು; ಅವರು ಮತ್ತೊಬ್ಬರಿಗೆ ಮಾಡಿದಂತೆಯೇ ಅವರಿಗೆ ಮಾಡು.",
			"5": "<sup>5</sup> ಅವರು ಯೆಹೋವನ ಕಾರ್ಯಗಳನ್ನೂ ಆತನ ಕೈಕೆಲಸಗಳನ್ನೂ ವಿವೇಚಿಸಿ ತಿಳಿದುಕೊಳ್ಳದೆ ಹೋದರು; ಆದದರಿಂದ ಆತನು ಅವರನ್ನು ಹಾಳು ಮಾಡುವನೇ ಹೊರತು ವೃದ್ಧಿಪಡಿಸುವದಿಲ್ಲ.",
			"6": "<sup>6</sup> ಯೆಹೋವನು ನನ್ನ ವಿಜ್ಞಾಪನೆಗಳನ್ನು ಕೇಳಿದ್ದಾನೆ; ಆತನಿಗೆ ಸ್ತೋತ್ರವಾಗಲಿ.",
			"7": "<sup>7</sup> ಯೆಹೋವನು ನನಗೆ ಬಲವೂ ಗುರಾಣಿಯೂ ಆಗಿದ್ದಾನೆ; ನಾನು ಆತನಲ್ಲಿ ಭರವಸವಿಟ್ಟೆನು, ನನಗೆ ಸಹಾಯವು ಉಂಟಾಯಿತು. ಆದಕಾರಣ ನನ್ನ ಹೃದಯವು ಹರ್ಷಿಸುವದು; ಕೀರ್ತನಾರೂಪವಾಗಿ ಆತನನ್ನು ಸ್ತುತಿಸುವೆನು.",
			"8": "<sup>8</sup> ಯೆಹೋವನು ತನ್ನ ಜನರಿಗೆ ಬಲವೂ ತಾನು ಅಭಿಷೇಕಿಸಿದವನಿಗೆ ಆಶ್ರಯದುರ್ಗವೂ ಆಗಿದ್ದಾನೆ.",
			"9": "<sup>9</sup> [ಯೆಹೋವನೇ,] ನಿನ್ನ ಜನರನ್ನು ರಕ್ಷಿಸು; ನಿನ್ನ ಸ್ವಕೀಯ ಪ್ರಜೆಯನ್ನು ಆಶೀರ್ವದಿಸು. ಅವರನ್ನು ಯಾವಾಗಲೂ ಪರಿಪಾಲಿಸುತ್ತಾ ಆಧಾರವಾಗಿರು."
		},
		"29": {
			"1": "<sup>1</sup> ದೇವದೂತರುಗಳಿರಾ, ಪರಮಪ್ರಭಾವವು ಯೆಹೋವನದೇ ಯೆಹೋವನದೇ ಎಂದು ಹೇಳಿ ಕೊಂಡಾಡಿರಿ.",
			"2": "<sup>2</sup> ಯೆಹೋವನ ನಾಮಕ್ಕೆ ಯೋಗ್ಯವಾದ ಘನವನ್ನು ಸಲ್ಲಿಸಿರಿ; ಪರಿಶುದ್ಧತ್ವವೆಂಬ ಭೂಷಣದೊಡನೆ ಯೆಹೋವನಿಗೆ ಅಡ್ಡಬೀಳಿರಿ.",
			"3": "<sup>3</sup> ಯೆಹೋವನ ಮಹಾಧ್ವನಿಯು ಮೇಘಮಂಡಲದಲ್ಲಿ ಕೇಳಿಸುತ್ತದೆ; ಪ್ರಭಾವಸ್ವರೂಪನಾದ ದೇವರು ಗುಡುಗುತ್ತಾನೆ; ಯೆಹೋವನು ಆಕಾಶದ ಜಲರಾಶಿಗಳ ಮೇಲೆ ಇದ್ದಾನೆ.",
			"4": "<sup>4</sup> ಯೆಹೋವನ ಧ್ವನಿಯು ಎಷ್ಟೋ ಬಲವುಳ್ಳದ್ದು, ಎಷ್ಟೋ ಗಂಭೀರವಾದದ್ದು.",
			"5": "<sup>5</sup> ಯೆಹೋವನ ಗರ್ಜನೆಗೆ ದೇವದಾರುವೃಕ್ಷಗಳು ದಡಲ್ ಎಂದು ಮುರಿದು ಬೀಳುತ್ತವೆ; ಯೆಹೋವನು ಲೆಬನೋನ್ ಪರ್ವತದಲ್ಲಿನ ಮಹಾ ದೇವದಾರುಗಳನ್ನೂ ಮುರಿದುಬಿಡುತ್ತಾನೆ.",
			"6": "<sup>6</sup> ಲೆಬನೋನ್ ಪರ್ವತವು ಕರುವಿನೋಪಾದಿಯಲ್ಲಿಯೂ ಸಿರ್ಯೋನ್ ಬೆಟ್ಟವು ಕಾಡುಕೋಣದ ಮರಿಯಂತೆಯೂ ಹಾರಾಡುವ ಹಾಗೆ ಮಾಡುತ್ತಾನೆ.",
			"7": "<sup>7</sup> ಯೆಹೋವನ ಗರ್ಜನೆಗೆ ಮಿಂಚುಗಳು ಥಳಥಳನೆ ಹೊಳೆಯುತ್ತವೆ.",
			"8": "<sup>8</sup> ಯೆಹೋವನ ಗರ್ಜನೆಗೆ ಕಾಡು ಹೊರಳಾಡುತ್ತದೆ; ಕಾದೇಶ್ ಅರಣ್ಯವು ಕಂಪಿಸುತ್ತದೆ.",
			"9": "<sup>9</sup> ಯೆಹೋವನ ಗರ್ಜನೆಗೆ ಗರ್ಭದ ಜಿಂಕೆಗಳು ಈಯುತ್ತವೆ; ಕಾಡಿನ ಮರಗಳು ಬರೀದಾಗುತ್ತವೆ. ಆಗ ಆತನ ಮಂದಿರದಲ್ಲಿರುವವರೆಲ್ಲರು - ಎಷ್ಟೋ ಪ್ರಭಾವ! ಎನ್ನುತ್ತಾರೆ.",
			"10": "<sup>10</sup> ಯೆಹೋವನು ಜಲಪ್ರಳಯದಲ್ಲಿ ಆಸೀನನಾಗಿದ್ದನು; ಆತನು ಸದಾಕಾಲವೂ ಅರಸನಾಗಿ ಕೂತಿರುವನು.",
			"11": "<sup>11</sup> ಯೆಹೋವನು ತನ್ನ ಜನರಿಗೆ ಬಲವನ್ನು ಅನುಗ್ರಹಿಸುವನು; ಆತನು ತನ್ನ ಪ್ರಜೆಗೆ ಸುಕ್ಷೇಮವನ್ನು ದಯಪಾಲಿಸುವನು."
		},
		"30": {
			"1": "<sup>1</sup> ಯೆಹೋವನೇ, ನನ್ನ ಶತ್ರುಗಳು ಸಂತೋಷಿಸುವದಕ್ಕೆ ಅವಕಾಶಕೊಡದೆ ನನ್ನನ್ನು ಉದ್ಧರಿಸಿದ್ದರಿಂದ ನಿನ್ನನ್ನು ಕೊಂಡಾಡುವೆನು.",
			"2": "<sup>2</sup> ಯೆಹೋವನೇ, ನನ್ನ ದೇವರೇ, ನಿನಗೆ ಮೊರೆಯಿಡಲು ನನ್ನನ್ನು ಸ್ವಸ್ಥಮಾಡಿದಿ.",
			"3": "<sup>3</sup> ಯೆಹೋವನೇ, ನನ್ನ ಪ್ರಾಣವನ್ನು ಪಾತಾಳದಿಂದಲೂ ಎತ್ತಿದಿಯಲ್ಲಾ; ನನ್ನನ್ನು ಸಮಾಧಿಯಲ್ಲಿ ಸೇರಗೊಡದೆ ಬದುಕಿಸಿದಿಯಲ್ಲಾ.",
			"4": "<sup>4</sup> ಯೆಹೋವನ ಭಕ್ತರೇ, ಆತನನ್ನು ಕೀರ್ತಿಸಿರಿ; ಆತನ ಪರಿಶುದ್ಧನಾಮವನ್ನು ಕೊಂಡಾಡಿರಿ.",
			"5": "<sup>5</sup> ಆತನ ಕೋಪವು ಕ್ಷಣಮಾತ್ರವೇ; ಆತನ ಅನುಗ್ರಹವೋ ಜೀವಮಾನವೆಲ್ಲಾ ಇರುವದು; ಸಂಜೆಗೆ ದುಃಖವೆಂಬದು ಬಂದು ನಮ್ಮ ಬಳಿಯಲ್ಲಿ ಇಳಿದುಕೊಂಡರೂ ಮುಂಜಾನೆ ಹರ್ಷಧ್ವನಿಯು ಕೇಳಿಸುವದು.",
			"6": "<sup>6</sup> ನಾನಂತೂ ಸುಖದಿಂದಿದ್ದಾಗ - ನನಗೆ ಎಂದಿಗೂ ಚಲನವೇ ಇಲ್ಲವೆಂದು ಹೇಳಿಕೊಂಡಿದ್ದೆನು.",
			"7": "<sup>7</sup> ಯೆಹೋವನೇ, ಕೃಪೆಮಾಡಿ ನಾನಿರುವ ಬೆಟ್ಟಕ್ಕೆ ಸ್ಥಿರವಾದ ಬಲವನ್ನು ಅನುಗ್ರಹಿಸಿದಿಯಲ್ಲಾ. ಆದರೂ ನೀನು ನಿನ್ನ ಮುಖವನ್ನು ಮರೆಮಾಡಿಕೊಂಡಾಗ ನಾನು ಕಳವಳಗೊಂಡು",
			"8": "<sup>8</sup> ಯೆಹೋವನೇ, ನಿನಗೆ ಮೊರೆಯಿಟ್ಟೆನು; ನನ್ನ ಒಡೆಯನಿಗೆ ಬಿನ್ನವಿಸಿದೆನು.",
			"9": "<sup>9</sup> ನನ್ನನ್ನು ಕೊಂದುಹಾಕಿ ಸಮಾಧಿಗೆ ಸೇರಿಸಿದರೆ ನಿನಗೆ ಲಾಭವೇನು? ಮಣ್ಣು ನಿನ್ನನ್ನು ಸ್ತುತಿಸುವದೋ? ಅದು ನಿನ್ನ ನಂಬಿಕೆಯನ್ನು ಹೊಗಳುವದೇನು?",
			"10": "<sup>10</sup> ಯೆಹೋವನೇ, ನನ್ನ ಪ್ರಾರ್ಥನೆಯನ್ನು ಕೇಳಿ ಕರುಣಿಸು; ಯೆಹೋವನೇ, ನನ್ನ ಸಹಾಯಕ್ಕೆ ಬಾ ಎಂದು ಹೇಳಿದೆನು.",
			"11": "<sup>11</sup> ಆಗ ನೀನು ನನ್ನ ಗೋಳಾಟವನ್ನು ತಪ್ಪಿಸಿ ಸಂತೋಷದಿಂದ ಕುಣಿದಾಡುವಂತೆ ಮಾಡಿದಿ; ನಾನು ಕಟ್ಟಿಕೊಂಡಿದ್ದ ಗೋಣಿತಟ್ಟನ್ನು ತೆಗೆದುಬಿಟ್ಟು ಹರ್ಷವಸ್ತ್ರವನ್ನು ನನಗೆ ಧಾರಣೆಮಾಡಿಸಿದಿ.",
			"12": "<sup>12</sup> ಇದರಿಂದ ಯೆಹೋವನೇ, ನನ್ನ ಮನಸ್ಸು ಎಡೆಬಿಡದೆ ನಿನ್ನನ್ನು ಕೀರ್ತಿಸುತ್ತಿರುವದು; ನನ್ನ ದೇವರೇ, ನಿನ್ನನ್ನು ಸದಾಕಾಲವೂ ಸ್ತುತಿಸುವೆನು."
		},
		"31": {
			"1": "<sup>1</sup> ಯೆಹೋವನೇ, ನಿನ್ನ ಮರೆಹೊಕ್ಕಿದ್ದೇನೆ; ನನಗೆ ಎಂದಿಗೂ ಆಶಾಭಂಗಪಡಿಸಬೇಡ. ನಿನ್ನ ನೀತಿಗನುಸಾರವಾಗಿ ನನ್ನನ್ನು ರಕ್ಷಿಸು.",
			"2": "<sup>2</sup> ಕಿವಿಗೊಟ್ಟು ಕೇಳಿ ಬೇಗ ನನ್ನನ್ನು ಬಿಡಿಸು; ನನ್ನನ್ನು ರಕ್ಷಿಸುವ ಆಶ್ರಯಗಿರಿಯೂ ದುರ್ಗಸ್ಥಾನವೂ ಆಗಿರು.",
			"3": "<sup>3</sup> ನೀನೇ ನನ್ನ ಬಂಡೆಯೂ ಕೋಟೆಯೂ ಆಗಿದ್ದೀಯಲ್ಲಾ; ಆದದರಿಂದ ನಿನ್ನ ಹೆಸರಿನ ನಿಮಿತ್ತ ದಾರಿತೋರಿಸಿ ನನ್ನನ್ನು ನಡಿಸು.",
			"4": "<sup>4</sup> ಶತ್ರುಗಳು ನನಗೆ ರಹಸ್ಯವಾಗಿ ಒಡ್ಡಿದ ಬಲೆಯೊಳಗೆ ಸಿಕ್ಕಿಬೀಳದಂತೆ ನನ್ನನ್ನು ಕಾಪಾಡು;",
			"5": "<sup>5</sup> ನೀನೇ ನನಗೆ ಆಧಾರವಲ್ಲವೇ; ನನ್ನ ಆತ್ಮವನ್ನು ನಿನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸಿಕೊಟ್ಟಿದ್ದೇನೆ. ಯೆಹೋವನೇ, ನಂಬಿಕೆಯುಳ್ಳ ದೇವರೇ, ನನ್ನನ್ನು ವಿಮೋಚಿಸಿದ್ದೀ.",
			"6": "<sup>6</sup> ಸುಳ್ಳುವಿಗ್ರಹಗಳನ್ನು ಅವಲಂಬಿಸಿದವರನ್ನು ನಾನು ದ್ವೇಷಿಸುತ್ತೇನೆ; ನಾನಾದರೋ ಯೆಹೋವನಲ್ಲಿ ಭರವಸವಿಟ್ಟಿದ್ದೇನೆ.",
			"7": "<sup>7</sup> ನಿನ್ನ ಕೃಪೆಯಲ್ಲಿ ಸಂತೋಷಿಸಿ ಉಲ್ಲಾಸದಿಂದಿರುವೆನು; ನಾನು ಕುಗ್ಗಿಹೋಗಿರುವದನ್ನು ನೋಡಿ ನನ್ನ ಬಾಧೆಗಳನ್ನು ನೀನು ಲಕ್ಷ್ಯಕ್ಕೆ ತೆಗೆದುಕೊಂಡಿಯಲ್ಲಾ.",
			"8": "<sup>8</sup> ನೀನು ನನ್ನನ್ನು ಶತ್ರುಗಳ ಕೈಕೆಳಗೆ ಬೀಳಿಸಲಿಲ್ಲ; ನಿರಾತಂಕಸ್ಥಾನದಲ್ಲಿ ನನ್ನನ್ನು ನಿಲ್ಲಿಸಿದಿ.",
			"9": "<sup>9</sup> ಯೆಹೋವನೇ, ಕರುಣಿಸು; ಕಷ್ಟದಲ್ಲಿ ಬಿದ್ದಿದ್ದೇನೆ. ನನಗುಂಟಾದ ಬಾಧೆಯ ದೆಸೆಯಿಂದ ನನ್ನ ಕಣ್ಣುಗುಡ್ಡು ಸೇದಿಕೊಂಡಿದೆ; ದೇಹಾತ್ಮಗಳು ಕುಗ್ಗಿಹೋದವು.",
			"10": "<sup>10</sup> ನನ್ನ ಜೀವಮಾನವೆಲ್ಲಾ ದುಃಖದಲ್ಲಿಯೂ ನಿಟ್ಟುಸಿರಿನಲ್ಲಿಯೂ ಕಳೆದುಹೋಗುತ್ತಾ ಇದೆ; ನನ್ನ ಅಪರಾಧದಿಂದ ನನ್ನ ಶಕ್ತಿಯು ಕುಂದಿಹೋಯಿತು; ನನ್ನ ಎಲುಬುಗಳು ಸವೆದುಹೋಗಿವೆ.",
			"11": "<sup>11</sup> ನನ್ನ ಎಲ್ಲಾ ಹಿಂಸಕರ ದೆಸೆಯಿಂದ ದೂಷಣೆಗೆ ಗುರಿಯಾದೆನು; ವಿಶೇಷವಾಗಿ ನೆರೆಯವರು ನನ್ನನ್ನು ನಿಂದಿಸುತ್ತಾರೆ. ನನ್ನ ಪರಿಚಿತರಿಗೆ ನನ್ನಲ್ಲಿ ಭೀತಿಯುಂಟಾಯಿತು; ನನ್ನನ್ನು ದಾರಿಯಲ್ಲಿ ಕಂಡವರೆಲ್ಲರು ಓರೆಯಾಗಿ ಹೋಗುತ್ತಾರೆ.",
			"12": "<sup>12</sup> ನಾನು ಸತ್ತವರಂತೆ ಯಾರ ಜ್ಞಾಪಕಕ್ಕೂ ಬಾರದವನಾದೆನು; ಒಡೆದ ಬೋಕಿಯಂತಿದ್ದೇನೆ.",
			"13": "<sup>13</sup> ಅನೇಕರು ನನ್ನ ಪ್ರಾಣ ತೆಗೆಯಬೇಕೆಂದು ಒಳಸಂಚು ಮಾಡುತ್ತಿದ್ದಾರೆ; ಅವರು ಪಿಸುಗುಟ್ಟುವ ಮಾತುಗಳು ನನ್ನ ಕಿವಿಗೆ ಬಿದ್ದವೆ; ಎಲ್ಲಾ ಕಡೆಯಲ್ಲಿಯೂ ನನಗೆ ಭೀತಿಯುಂಟಾಗಿದೆ.",
			"14": "<sup>14</sup> ನಾನಾದರೋ ಯೆಹೋವನೇ, ನಿನ್ನಲ್ಲೇ ಭರವಸವಿಟ್ಟಿದ್ದೇನೆ; ನೀನೇ ನನ್ನ ದೇವರೆಂದು ಹೇಳಿಕೊಂಡಿದ್ದೇನೆ.",
			"15": "<sup>15</sup> ನನ್ನ ಆಯುಷ್ಕಾಲವು ನಿನ್ನ ಕೈಯಲ್ಲಿದೆ; ಹಿಂದಟ್ಟುವ ಶತ್ರುಗಳಿಗೆ ನನ್ನನ್ನು ತಪ್ಪಿಸಿಕಾಪಾಡು.",
			"16": "<sup>16</sup> ನಿನ್ನ ಸೇವಕನನ್ನು ಪ್ರಸನ್ನಮುಖದಿಂದ ನೋಡು; ಪ್ರೀತಿಯಿಂದ ನನ್ನನ್ನು ರಕ್ಷಿಸು.",
			"17": "<sup>17</sup> ಯೆಹೋವನೇ, ಮೊರೆಯಿಟ್ಟಿದ್ದೇನೆ; ನನಗೆ ಆಶಾಭಂಗಪಡಿಸಬೇಡ. ದುಷ್ಟರಿಗೇ ಆಶಾಭಂಗವಾಗಲಿ; ಅವರು ಸ್ತಬ್ಧರಾಗಿ ಪಾತಾಳಕ್ಕೆ ಬೀಳಲಿ.",
			"18": "<sup>18</sup> ಯಾರು ನೀತಿವಂತರನ್ನು ತಾತ್ಸಾರಮಾಡಿ ಗರ್ವದಿಂದಲೂ ಕೊಬ್ಬಿನಿಂದಲೂ ಅವರಿಗೆ ವಿರೋಧವಾಗಿ ಸುಳ್ಳು ಹೇಳುತ್ತಾರೋ ಅವರ ತುಟಿಗಳು ಮುಚ್ಚಿಹೋಗಲಿ.",
			"19": "<sup>19</sup> ಸದ್ಭಕ್ತರಿಗೋಸ್ಕರ ನೀನು ಇಟ್ಟುಕೊಂಡಿರುವ ಮೇಲೂ ಆಶ್ರಿತರಿಗೋಸ್ಕರ ನೀನು ಎಲ್ಲರ ಮುಂದೆ ಮಾಡಿದ ಉಪಕಾರಗಳೂ ಎಷ್ಟೋ ವಿಶೇಷವಾಗಿವೆ.",
			"20": "<sup>20</sup> ಮನುಷ್ಯರ ಒಳಸಂಚುಗಳಿಂದ ಹಾನಿ ಉಂಟಾಗದಂತೆ ಅವರನ್ನು ನಿನ್ನ ಸಾನ್ನಿಧ್ಯದಲ್ಲಿಯೇ ಮರೆಮಾಡುತ್ತೀ; ವಿವಾದಿಸುವ ನಾಲಿಗೆಗಳಿಂದ ಕೇಡಾಗದಂತೆ ಅವರನ್ನು ಗೋಪ್ಯಸ್ಥಳದಲ್ಲಿ ಅಡಗಿಸುತ್ತೀ.",
			"21": "<sup>21</sup> ಮುತ್ತಿಗೆ ಹಾಕಲ್ಪಟ್ಟಿರುವ ನಗರದಲ್ಲಿದ್ದ ನನಗೆ ತನ್ನ ಕೃಪೆಯನ್ನು ಆಶ್ಚರ್ಯಕರವಾಗಿ ತೋರಿಸಿದ ಯೆಹೋವನಿಗೆ ಸ್ತೋತ್ರ.",
			"22": "<sup>22</sup> ನಾನಂತೂ ಭಯಭ್ರಾಂತನಾಗಿ - ನಿನ್ನ ಸಾನ್ನಿಧ್ಯದಿಂದ ತೆಗೆದುಹಾಕಲ್ಪಟ್ಟೆನೆಂದು ಹೇಳಿಕೊಂಡಿದ್ದೆನು; ಆದರೂ ನಾನು ಮೊರೆಯಿಡಲು ನೀನು ಕೇಳಿದಿ.",
			"23": "<sup>23</sup> ಭಕ್ತರೇ, ನೀವೆಲ್ಲರೂ ಯೆಹೋವನನ್ನು ಪ್ರೀತಿಸಿರಿ. ಆತನು ನಂಬಿಗಸ್ತರನ್ನು ಕಾಪಾಡುತ್ತಾನೆ; ಅಹಂಕಾರಿಗಳಿಗೆ ಚೆನ್ನಾಗಿ ಮುಯ್ಯಿ ತೀರಿಸುತ್ತಾನೆ.",
			"24": "<sup>24</sup> ಯೆಹೋವನನ್ನು ನಿರೀಕ್ಷಿಸುವವರೇ, ದೃಢವಾಗಿರ್ರಿ; ನಿಮ್ಮ ಹೃದಯವು ಧೈರ್ಯದಿಂದಿರಲಿ."
		},
		"32": {
			"1": "<sup>1</sup> ಯಾವನ ದ್ರೋಹವು ಪರಿಹಾರವಾಗಿದೆಯೋ ಯಾವನ ಪಾಪವು ಕ್ಷಮಿಸಲ್ಪಟ್ಟಿದೆಯೋ ಅವನೇ ಧನ್ಯನು.",
			"2": "<sup>2</sup> ಯೆಹೋವನು ಯಾವನ ಲೆಕ್ಕಕ್ಕೆ ಅಪರಾಧವನ್ನು ಎಣಿಸುವದಿಲ್ಲವೋ ಯಾವನ ಹೃದಯದಲ್ಲಿ ಕಪಟವಿರುವದಿಲ್ಲವೋ ಅವನು ಧನ್ಯನು.",
			"3": "<sup>3</sup> ನಾನು [ನನ್ನ ಪಾಪವನ್ನು] ಅರಿಕೆಮಾಡದೆ ಇದ್ದಾಗ ದಿನವೆಲ್ಲಾ ನರಳುವದರಿಂದ ನನ್ನ ಎಲುಬುಗಳು ಸವೆದುಹೋಗುತ್ತಿದ್ದವು.",
			"4": "<sup>4</sup> ಹಗಲಿರುಳು ನಿನ್ನ ಶಿಕ್ಷಾಹಸ್ತವು ನನ್ನ ಮೇಲೆ ಭಾರವಾಗಿತ್ತು; ಬೇಸಿಗೆಯ ನೀರಿನಂತೆ ನನ್ನ ಶರೀರದ ಸಾರವೆಲ್ಲಾ ಬತ್ತಿಹೋಯಿತು. ಸೆಲಾ.",
			"5": "<sup>5</sup> [ಹೀಗಿರುವಲ್ಲಿ] ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನನ್ನ ದ್ರೋಹವನ್ನು ಒಪ್ಪಿಕೊಳ್ಳುವೆನು ಅಂದುಕೊಂಡು ನನ್ನ ಪಾಪವನ್ನು ಮರೆಮಾಡದೆ ನಿನಗೆ ನನ್ನ ದೋಷವನ್ನು ತಿಳಿಸಿದೆನು. ನೀನು ನನ್ನ ಅಪರಾಧಪಾಪಗಳನ್ನು ಪರಿಹರಿಸಿಬಿಟ್ಟಿ. ಸೆಲಾ.",
			"6": "<sup>6</sup> ಇದರಿಂದ ಭಕ್ತರೆಲ್ಲರು ಸಕಾಲದಲ್ಲಿ ನಿನ್ನನ್ನು ಪ್ರಾರ್ಥಿಸಲಿ; ಮಹಾಪ್ರವಾಹವು ಅಂಥವರನ್ನು ಮುಟ್ಟುವದೇ ಇಲ್ಲ.",
			"7": "<sup>7</sup> ನೀನೇ ನನಗೆ ಮರೆಯು; ನಿರಪಾಯವಾಗಿ ನನ್ನನ್ನು ಕಾಯುವಿ; ವಿಮೋಚನಧ್ವನಿಯಿಂದ ನನ್ನನ್ನು ಆವರಿಸಿಕೊಳ್ಳುತ್ತೀ. ಸೆಲಾ.",
			"8": "<sup>8</sup> [ಯೆಹೋವನು] - ನಿನ್ನನ್ನು ಉಪದೇಶಿಸಿ ನಡೆಯಬೇಕಾದ ಮಾರ್ಗವನ್ನು ತಿಳಿಸುವೆನು; ನಿನ್ನನ್ನು ಕಟಾಕ್ಷಿಸಿ ಆಲೋಚನೆ ಹೇಳುವೆನು [ಅನ್ನುತ್ತಾನಲ್ಲಾ].",
			"9": "<sup>9</sup> [ಆದದರಿಂದ] ನೀವು ವಿವೇಕಹೀನರಾಗಿ ಕುದುರೆಯಂತಾಗಲಿ ಹೇಸರಕತ್ತೆಯಂತಾಗಲಿ ಇರಬೇಡಿರಿ; ಅವುಗಳು ಬಾರುಕಡಿವಾಣಗಳಿಂದಲ್ಲದೆ ಸ್ವಾಧೀನವಾಗುವದಿಲ್ಲವಷ್ಟೆ.",
			"10": "<sup>10</sup> ದುಷ್ಟರಿಗೆ ಅನೇಕ ಕಷ್ಟನಷ್ಟಗಳು ಉಂಟಾಗುವವು; ಆದರೆ ಯೆಹೋವನಲ್ಲಿ ಭರವಸವಿಟ್ಟವರನ್ನು ಆತನ ಕೃಪೆಯು ಆವರಿಸಿಕೊಳ್ಳುವದು.",
			"11": "<sup>11</sup> ನೀತಿವಂತರೇ, ಯೆಹೋವನಲ್ಲಿ ಸಂತೋಷಿಸುತ್ತಾ ಉಲ್ಲಾಸವಾಗಿರ್ರಿ; ಯಥಾರ್ಥಚಿತ್ತರೇ, ಆತನ ವಿಷಯದಲ್ಲಿ ಉತ್ಸಾಹಧ್ವನಿ ಮಾಡಿರಿ."
		},
		"33": {
			"1": "<sup>1</sup> ನೀತಿವಂತರೇ, ಯೆಹೋವನ ವಿಷಯದಲ್ಲಿ ಉಲ್ಲಾಸ ಧ್ವನಿಮಾಡಿರಿ; ಯಥಾರ್ಥಚಿತ್ತರು ಆತನನ್ನು ಸ್ತುತಿಸುವದು ಯುಕ್ತವಾಗಿದೆ.",
			"2": "<sup>2</sup> ಕಿನ್ನರಿಯನ್ನು ನುಡಿಸುತ್ತಾ ಯೆಹೋವನನ್ನು ಕೊಂಡಾಡಿರಿ; ಹತ್ತುತಂತಿಗಳ ಸ್ವರಮಂಡಲವನ್ನು ಬಾರಿಸುತ್ತಾ ಸಂಕೀರ್ತಿಸಿರಿ.",
			"3": "<sup>3</sup> ಆತನ ಘನಕ್ಕಾಗಿ ನೂತನಕೀರ್ತನೆಯನ್ನು ಹಾಡಿರಿ; ಉತ್ಸಾಹಧ್ವನಿಯೊಡನೆ ಇಂಪಾಗಿ ಬಾರಿಸಿರಿ.",
			"4": "<sup>4</sup> ಯೆಹೋವನ ವಚನವು ಯಥಾರ್ಥವಾದದ್ದು; ಆತನ ಕೃತ್ಯವೆಲ್ಲಾ ನಂಬಿಕೆಯಾಗಿದೆ.",
			"5": "<sup>5</sup> ಆತನು ನೀತಿನ್ಯಾಯಗಳನ್ನು ಪ್ರೀತಿಸುವವನು; ಭೂಲೋಕವೆಲ್ಲಾ ಯೆಹೋವನ ಕೃಪೆಯಿಂದ ತುಂಬಿದೆ.",
			"6": "<sup>6</sup> ಯೆಹೋವನ ಅಪ್ಪಣೆಯಿಂದಲೇ ಆಕಾಶವು ಉಂಟಾಯಿತು; ಅದರಲ್ಲಿರುವದೆಲ್ಲವೂ ಆತನ ಶ್ವಾಸದಿಂದ ನಿರ್ಮಿತವಾಯಿತು.",
			"7": "<sup>7</sup> ನೀರುಗಳನ್ನು ಒಟ್ಟುಗೂಡಿಸಿ ಸಮುದ್ರವನ್ನು ಮಾಡಿದವನು ಆತನೇ; ಭೂಮಿಯ ಕೆಳಗೆ ಜಲಾಶಯಗಳನ್ನು ಇಟ್ಟವನು ಆತನೇ.",
			"8": "<sup>8</sup> ಭೂಲೋಕದವರೆಲ್ಲರೂ ಯೆಹೋವನಿಗೆ ಭಯಪಡಲಿ; ಭೂನಿವಾಸಿಗಳೆಲ್ಲರೂ ಆತನಿಗೆ ಹೆದರಲಿ.",
			"9": "<sup>9</sup> ಆತನು ನುಡಿದ ಮಾತ್ರಕ್ಕೆ ಸಮಸ್ತವೂ ಉಂಟಾಯಿತು; ಆತನು ಆಜ್ಞಾಪಿಸುತ್ತಲೇ ಸರ್ವವೂ ಸ್ಥಾಪನೆಯಾಯಿತು.",
			"10": "<sup>10</sup> ಯೆಹೋವನು ಅನ್ಯಜನರ ಸಂಕಲ್ಪಗಳನ್ನು ವ್ಯರ್ಥಮಾಡುತ್ತಾನೆ; ಅವರ ಯೋಚನೆಗಳನ್ನು ನಿಷ್ಫಲಮಾಡುತ್ತಾನೆ.",
			"11": "<sup>11</sup> ಯೆಹೋವನ ಆಲೋಚನೆಯೋ ಶಾಶ್ವತವಾಗಿಯೇ ನಿಲ್ಲುವದು; ಆತನ ಸಂಕಲ್ಪವು ಎಂದಿಗೂ ಕದಲುವದಿಲ್ಲ.",
			"12": "<sup>12</sup> ಯಾವ ಜನರಿಗೆ ಯೆಹೋವನೇ ದೇವರಾಗಿದ್ದಾನೋ ಯಾವ ಜನಾಂಗದವರನ್ನು ಸ್ವಕೀಯರನ್ನಾಗಿ ಆದುಕೊಂಡಿದ್ದಾನೋ ಅವರೇ ಧನ್ಯರು.",
			"13": "<sup>13</sup> ಯೆಹೋವನು ಆಕಾಶದಿಂದ ಮನುಷ್ಯರೆಲ್ಲರನ್ನು ನೋಡುತ್ತಾನೆ;",
			"14": "<sup>14</sup> ತಾನಿರುವ ಸ್ಥಾನದಿಂದ ಭೂನಿವಾಸಿಗಳೆಲ್ಲರನ್ನು ದೃಷ್ಟಿಸುತ್ತಾನೆ.",
			"15": "<sup>15</sup> ಅವರೆಲ್ಲರ ಹೃದಯಗಳನ್ನು ನಿರ್ಮಿಸಿದವನೂ ಅವರ ಕೃತ್ಯಗಳನ್ನೆಲ್ಲಾ ವಿವೇಚಿಸುವವನೂ ಆತನೇ.",
			"16": "<sup>16</sup> ಮಹಾಸೇನಾಬಲದಿಂದಲೇ ಯಾವ ಅರಸನಿಗೂ ಜಯವಾಗುವದಿಲ್ಲ; ಯಾವ ಶೂರನೂ ಅಧಿಕವಾದ ಭುಜಬಲದ ದೆಸೆಯಿಂದ ಸುರಕ್ಷಿತನಾಗುವದಿಲ್ಲ.",
			"17": "<sup>17</sup> ಜೀವರಕ್ಷಣೆಗಾಗಿ ಕುದುರೆಯು ಪ್ರಯೋಜನವಿಲ್ಲ; ಅದು ತನ್ನ ವಿಶೇಷಬಲದಿಂದ ಯಾರನ್ನೂ ರಕ್ಷಿಸಲಾರದು.",
			"18": "<sup>18</sup> ಯೆಹೋವನಾದರೋ ತನ್ನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರನ್ನು ಕಟಾಕ್ಷಿಸುತ್ತಾನೆ; ತನ್ನ ಕೃಪೆಯನ್ನು ನಿರೀಕ್ಷಿಸುವವರನ್ನು ಲಕ್ಷಿಸುತ್ತಾನೆ.",
			"19": "<sup>19</sup> ಅವರ ಪ್ರಾಣವನ್ನು ಮರಣದಿಂದ ತಪ್ಪಿಸುವನು; ಬರಗಾಲದಲ್ಲಿ ಅವರ ಜೀವವನ್ನು ಉಳಿಸುವನು.",
			"20": "<sup>20</sup> ನಮ್ಮ ಮನಸ್ಸು ಯೆಹೋವನಿಗೋಸ್ಕರ ಕಾದಿದೆ; ನಮ್ಮ ಸಹಾಯವೂ ಗುರಾಣಿಯೂ ಆತನೇ.",
			"21": "<sup>21</sup> ಆತನಲ್ಲಿ ನಮ್ಮ ಹೃದಯವು ಸಂತೋಷಿಸುವದು; ಆತನ ಪರಿಶುದ್ಧನಾಮದಲ್ಲಿ ಭರವಸವಿಟ್ಟಿದ್ದೇವೆ.",
			"22": "<sup>22</sup> ಯೆಹೋವನೇ, ನಾವು ನಿನ್ನನ್ನೇ ನಿರೀಕ್ಷಿಸಿಕೊಂಡಿದ್ದೇವೆ; ನಿನ್ನ ಕೃಪೆಯು ನಮ್ಮ ಮೇಲೆ ಇರಲಿ."
		},
		"34": {
			"1": "<sup>1</sup> ನಾನು ಯೆಹೋವನನ್ನು ಎಡೆಬಿಡದೆ ಕೊಂಡಾಡುವೆನು; ಆತನ ಸ್ತೋತ್ರವು ಯಾವಾಗಲೂ ನನ್ನ ಬಾಯಲ್ಲಿ ಇರುವದು.",
			"2": "<sup>2</sup> ನನ್ನ ಮನಸ್ಸು ಯೆಹೋವನಲ್ಲಿ ಹಿಗ್ಗುತ್ತಿರುವದು; ಇದನ್ನು ದೀನರು ಕೇಳಿ ಸಂತೋಷಿಸುವರು.",
			"3": "<sup>3</sup> ನನ್ನೊಡನೆ ಯೆಹೋವನನ್ನು ಕೊಂಡಾಡಿರಿ; ನಾವು ಒಟ್ಟಾಗಿ ಆತನ ಹೆಸರನ್ನು ಘನಪಡಿಸೋಣ.",
			"4": "<sup>4</sup> ನಾನು ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಬೇಡಿಕೊಳ್ಳಲು ಆತನು ಸದುತ್ತರವನ್ನು ಕೊಟ್ಟು ಎಲ್ಲಾ ಭೀತಿಯಿಂದ ನನ್ನನ್ನು ತಪ್ಪಿಸಿದನು.",
			"5": "<sup>5</sup> ಆತನನ್ನೇ ದೃಷ್ಟಿಸಿದವರು ಪ್ರಕಾಶವನ್ನು ಹೊಂದಿದರು; ಅವರ ಮುಖವು ಲಜ್ಜೆಯಿಂದ ಕೆಡುವದೇ ಇಲ್ಲ.",
			"6": "<sup>6</sup> ಕಷ್ಟದಲ್ಲಿದ್ದ ಈ ಮನುಷ್ಯನು ಮೊರೆಯಿಡಲು ಯೆಹೋವನು ಕೇಳಿ ಎಲ್ಲಾ ಬಾಧೆಗಳಿಂದ ಬಿಡಿಸಿದನು.",
			"7": "<sup>7</sup> ಯೆಹೋವನ ಭಯಭಕ್ತಿಯುಳ್ಳವರ ಸುತ್ತಲು ಆತನ ದೂತನು ದಂಡಿಳಿಸಿ ಕಾವಲಾಗಿದ್ದು ಕಾಪಾಡುತ್ತಾನೆ.",
			"8": "<sup>8</sup> ಯೆಹೋವನು ಸರ್ವೋತ್ತಮನೆಂದು ಅನುಭವದಿಂದ ತಿಳಿದುಕೊಳ್ಳಿರಿ; ಆತನನ್ನು ಆಶ್ರಯಿಸಿಕೊಂಡವರು ಎಷ್ಟೋ ಧನ್ಯರು.",
			"9": "<sup>9</sup> ಯೆಹೋವನ ಜನರೇ, ಆತನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿರ್ರಿ; ಆತನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಿಗೆ ಯಾವ ಕೊರತೆಯೂ ಇರುವದಿಲ್ಲ.",
			"10": "<sup>10</sup> ಪ್ರಾಯದ ಸಿಂಹಗಳಾದರೋ ಹೊಟ್ಟೆಗಿಲ್ಲದೆ ಹಸಿದಾವು; ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಬೇಡಿಕೊಳ್ಳುವವರಿಗೆ ಯಾವ ಮೇಲಿಗೂ ಕಡಿಮೆಯಿಲ್ಲ.",
			"11": "<sup>11</sup> ಮಕ್ಕಳಿರಾ, ಬನ್ನಿರಿ, ನನ್ನ ಮಾತನ್ನು ಕೇಳಿರಿ; ನಾನು ಯೆಹೋವನ ಭಯವನ್ನು ನಿಮಗೆ ಕಲಿಸುವೆನು.",
			"12": "<sup>12</sup> ದೀರ್ಘಾಯುಷ್ಯವು ಬೇಕೋ? ಬಹುದಿವಸ ಬದುಕಿ ಸುಖವನ್ನು ಅನುಭವಿಸಬೇಕೋ?",
			"13": "<sup>13</sup> ಹಾಗಾದರೆ ಕೆಟ್ಟದ್ದಕ್ಕೆ ಹೋಗದಂತೆ ನಾಲಿಗೆಯನ್ನು ಕಾದುಕೋ; ವಂಚನೆಯ ಮಾತುಗಳಿಗೆ ಬಿಡದೆ ತುಟಿಗಳನ್ನು ಇಟ್ಟುಕೋ.",
			"14": "<sup>14</sup> ಕೆಟ್ಟದ್ದನ್ನು ಬಿಟ್ಟು ಒಳ್ಳೆಯದನ್ನೇ ಮಾಡು; ಸಮಾಧಾನವನ್ನು ಹಾರೈಸಿ ಅದಕ್ಕಾಗಿ ಪ್ರಯತ್ನಪಡು.",
			"15": "<sup>15</sup> ಯೆಹೋವನು ನೀತಿವಂತರನ್ನು ಕಟಾಕ್ಷಿಸುತ್ತಾನೆ; ಅವರು ಮೊರೆಯಿಡುವಾಗ ಕಿವಿಗೊಡುತ್ತಾನೆ.",
			"16": "<sup>16</sup> ಕೆಡುಕರಿಗೋ ಯೆಹೋವನು ಕೋಪದ ಮುಖವುಳ್ಳವನಾಗಿರುವನು; ಲೋಕದಲ್ಲಿ ಅವರ ನೆನಪೇ ಉಳಿಯದಂತೆ ತೆಗೆದುಹಾಕುವನು.",
			"17": "<sup>17</sup> ಯೆಹೋವನು ನೀತಿವಂತರ ಕೂಗನ್ನು ಕೇಳಿ ಎಲ್ಲಾ ಕಷ್ಟಗಳಿಂದ ಅವರನ್ನು ಬಿಡಿಸುತ್ತಾನೆ.",
			"18": "<sup>18</sup> ಮುರಿದ ಮನಸ್ಸುಳ್ಳವರಿಗೆ ಯೆಹೋವನು ನೆರವಾಗುತ್ತಾನೆ; ಕುಗ್ಗಿಹೋದವರನ್ನು ಉದ್ಧಾರಮಾಡುತ್ತಾನೆ.",
			"19": "<sup>19</sup> ನೀತಿವಂತನಿಗೆ ಸಂಭವಿಸುವ ಕಷ್ಟಗಳು ಅನೇಕವಿದ್ದರೂ ಯೆಹೋವನು ಅವೆಲ್ಲವುಗಳಿಂದ ಅವನನ್ನು ಬಿಡಿಸುತ್ತಾನೆ.",
			"20": "<sup>20</sup> ಆತನು ಅವನ ಎಲುಬುಗಳನ್ನೆಲ್ಲಾ ಕಾಪಾಡುತ್ತಾನೆ; ಅವುಗಳಲ್ಲಿ ಒಂದಾದರೂ ಮುರಿದುಹೋಗುವದಿಲ್ಲ.",
			"21": "<sup>21</sup> ದುಷ್ಟನ ಕೆಡುಕು ಅವನನ್ನೇ ಕೊಲ್ಲುವದು; ನೀತಿವಂತನನ್ನು ದ್ವೇಷಿಸುವವರು ಅಪರಾಧಿಗಳೆಂದು ಎಣಿಸಲ್ಪಡುವರು.",
			"22": "<sup>22</sup> ಯೆಹೋವನು ತನ್ನ ಸೇವಕರ ಪ್ರಾಣವನ್ನು ವಿಮೋಚಿಸುತ್ತಾನೆ; ಆತನ ಆಶ್ರಿತರಲ್ಲಿ ಒಬ್ಬರಾದರೂ ಅಪರಾಧಿಯೆಂದು ಎಣಿಸಲ್ಪಡುವದಿಲ್ಲ."
		},
		"35": {
			"1": "<sup>1</sup> ಯೆಹೋವನೇ, ನನ್ನ ಸಂಗಡ ವ್ಯಾಜ್ಯವಾಡುವವರೊಡನೆ ವ್ಯಾಜ್ಯವಾಡು; ನನ್ನ ಮೇಲೆ ಯುದ್ಧಮಾಡುವವರ ಸಂಗಡ ಯುದ್ಧಮಾಡು.",
			"2": "<sup>2</sup> ಖೇಡ್ಯಗುರಾಣಿಗಳನ್ನು ಹಿಡಿದುಕೊಂಡು ನನಗೆ ಸಹಾಯಕನಾಗಿ ನಿಲ್ಲು.",
			"3": "<sup>3</sup> ನೀನು ಭಲ್ಲೆಯನ್ನೂ ಯುದ್ಧದ ಕೊಡಲಿಯನ್ನೂ ಹಿಡಿದು ನನ್ನನ್ನು ಹಿಂದಟ್ಟುವ ವೈರಿಗಳನ್ನು ಪ್ರತಿಭಟಿಸು; ನಾನೇ ನಿನ್ನನ್ನು ರಕ್ಷಿಸುವೆನೆಂದು ಅಭಯಕೊಡು.",
			"4": "<sup>4</sup> ನನ್ನ ಪ್ರಾಣವನ್ನು ತೆಗೆಯಬೇಕೆಂದಿರುವವರು ಆಶಾಭಂಗಪಟ್ಟು ಅವಮಾನಹೊಂದಲಿ; ನನಗೆ ಕೇಡನ್ನು ಯೋಚಿಸುವವರು ಮಾನಭಂಗದಿಂದ ಹಿಂದಿರುಗಿ ಓಡಲಿ.",
			"5": "<sup>5</sup> ಅವರು ಗಾಳಿ ಹಾರಿಸುವ ಹೊಟ್ಟಿನಂತಾಗಲಿ; ಯೆಹೋವನ ದೂತನು ಅವರನ್ನು ನೂಕಿಬಿಡಲಿ.",
			"6": "<sup>6</sup> ಕತ್ತಲೆಯೂ ಜಾರಿಕೆಯೂ ಇರುವ ದಾರಿಯಲ್ಲಿ ಯೆಹೋವನ ದೂತನು ಅವರನ್ನು ಹಿಂದಟ್ಟಲಿ.",
			"7": "<sup>7</sup> ಅವರು ನಿಷ್ಕಾರಣವಾಗಿ ನನಗೆ ಬಲೆಯೊಡ್ಡಿದ್ದಾರೆ; ಅಕಾರಣವಾಗಿ ನನ್ನ ಪ್ರಾಣವನ್ನು ತೆಗೆಯಬೇಕೆಂದು ಗುಂಡಿಯನ್ನು ತೋಡಿದ್ದಾರೆ.",
			"8": "<sup>8</sup> ಅವನಿಗೆ ನಾಶನವು ಅಕಸ್ಮಾತ್ತಾಗಿ ಬರಲಿ; ತಾನು ಒಡ್ಡಿದ ಬಲೆಯಲ್ಲಿ ತಾನೇ ಸಿಕ್ಕಿಬೀಳಲಿ. ತಾನೇ ತೋಡಿದ ಕುಣಿಯಲ್ಲಿ ತಾನೇ ಬಿದ್ದುಹೋಗಲಿ.",
			"9": "<sup>9</sup> ಆಗ ನನ್ನ ಮನಸ್ಸಿಗೆ ಯೆಹೋವನ ದೆಸೆಯಿಂದ ಹರ್ಷವುಂಟಾಗುವದು; ಆತನಿಂದಾದ ರಕ್ಷಣೆಯ ನಿಮಿತ್ತ ಆನಂದಪಡುವೆನು.",
			"10": "<sup>10</sup> ಯೆಹೋವನೇ, ಕುಗ್ಗಿದವನನ್ನು ಬಲಾತ್ಕಾರಿಯಿಂದಲೂ ದಿಕ್ಕಿಲ್ಲದ ಬಡವನನ್ನು ಸೂರೆಮಾಡುವವನಿಂದಲೂ ತಪ್ಪಿಸಿ ರಕ್ಷಿಸುವಾತನೇ, ನಿನಗೆ ಸಮಾನರು ಯಾರಿದ್ದಾರೆಂದು ನನ್ನ ಎಲುಬುಗಳೆಲ್ಲಾ ಹೇಳುವವು.",
			"11": "<sup>11</sup> ನ್ಯಾಯವಿರುದ್ಧಸಾಕ್ಷಿಗಳು ನನಗೆ ವಿರೋಧವಾಗಿ ಎದ್ದಿದ್ದಾರೆ; ನಾನರಿಯದ ಸಂಗತಿಗಳ ವಿಷಯದಲ್ಲಿ ನನ್ನನ್ನು ವಿಚಾರಿಸುತ್ತಾರೆ.",
			"12": "<sup>12</sup> ಅವರು ಉಪಕಾರಕ್ಕೆ ಅಪಕಾರವನ್ನೇ ಮಾಡುತ್ತಾರೆ; ನಾನು ದಿಕ್ಕಿಲ್ಲದವನಾದೆನು.",
			"13": "<sup>13</sup> ನಾನಾದರೋ ಅವರ ಅಸ್ವಸ್ಥಕಾಲದಲ್ಲಿ ಗೋಣಿತಟ್ಟನ್ನೇ ಕಟ್ಟಿಕೊಂಡಿದ್ದೆನು; ಉಪವಾಸದಿಂದ ನನ್ನ ಆತ್ಮವನ್ನು ನೋಯಿಸಿದೆನು. ನನ್ನ ಪ್ರಾರ್ಥನೆಯು ನನ್ನ ಎದೆಗೆ ತಿರುಗಿತು.",
			"14": "<sup>14</sup> ಅಸ್ವಸ್ಥನಾದವನನ್ನು ಸ್ನೇಹಿತನೂ ಅಣ್ಣನೂ ಎಂದು ಭಾವಿಸಿ ನಡೆದುಕೊಂಡೆನು; ತಾಯಿ ಸತ್ತದ್ದಕ್ಕಾಗಿ ದುಃಖಿಸುವವನಂತೆ ನಾನು ತಲೆಬಾಗಿ ಅಳುತ್ತಿದ್ದೆನು.",
			"15": "<sup>15</sup> ಆದರೂ ನನಗೆ ಆಪತ್ತು ಬಂದಾಗ ಅವರು ಸಂತೋಷಿಸುತ್ತಾ ಕೂಡಿಕೊಂಡರು; ಅಕಾರಣವಾಗಿ ಈ ಭ್ರಷ್ಟರು ನನಗೆ ವಿರೋಧವಾಗಿ ಕೂಡಿಕೊಂಡು ಸೂರೆಮಾಡುವದನ್ನು ಬಿಡಲೇ ಇಲ್ಲ.",
			"16": "<sup>16</sup> ದೋಸೆಗೋಸ್ಕರ ಗೇಲಿಮಾಡುವ ಮೂರ್ಖರ ಸಂಗಡ ನನ್ನ ಮೇಲೆ ಹಲ್ಲುಕಡಿಯುತ್ತಾರೆ.",
			"17": "<sup>17</sup> ಕರ್ತನೇ, ಇನ್ನೆಷ್ಟರವರೆಗೆ ಸುಮ್ಮನೆ ನೋಡುತ್ತಾ ಇರುವಿ? ಅವರ ಅಪಾಯದಿಂದ ನನ್ನ ಪ್ರಾಣವನ್ನು ಬಿಡಿಸು; ನನ್ನ ಪರಮಪ್ರಿಯಪ್ರಾಣವನ್ನು ಆ ಸಿಂಹಗಳ ಬಾಯಿಗೆ ಸಿಕ್ಕದಂತೆ ತಪ್ಪಿಸು.",
			"18": "<sup>18</sup> ಆಗ ನಾನು ಮಹಾಸಭೆಯಲ್ಲಿ ನಿನ್ನನ್ನು ಕೊಂಡಾಡುವೆನು; ಬಹುಜನರ ಮುಂದೆ ಸ್ತುತಿಸುವೆನು.",
			"19": "<sup>19</sup> ನಿರ್ನಿಮಿತ್ತ ವಿರೋಧಿಗಳು ನನ್ನ ವಿಷಯದಲ್ಲಿ ಹಿಗ್ಗುವದಕ್ಕೆ ಅವಕಾಶಕೊಡಬೇಡ; ನಿಷ್ಕಾರಣವೈರಿಗಳ ಕಣ್ಣುಸನ್ನೆಗೆ ಆಸ್ಪದ ಕೊಡಬೇಡ.",
			"20": "<sup>20</sup> ಅವರ ಮಾತುಗಳು ಸಮಾಧಾನಕರವಾದವುಗಳಲ್ಲ; ದೇಶದ ಸಾಧುಜನರನ್ನು ಕೆಡಿಸುವದಕ್ಕೆ ಮೋಸವನ್ನು ಕಲ್ಪಿಸುತ್ತಾರೆ.",
			"21": "<sup>21</sup> ಅವರು ನನ್ನನ್ನು ನೋಡಿ ಬಾಯಿಕಿಸಿದು - ಆಹಾ, ಆಹಾ, ನಮ್ಮ ಕಣ್ಣು ಕಂಡಿತಲ್ಲಾ ಅನ್ನುತ್ತಾರೆ.",
			"22": "<sup>22</sup> ಯೆಹೋವನೇ, ನೀನೇ ನೋಡಿದಿಯಲ್ಲವೇ? ಸುಮ್ಮನಿರಬೇಡ; ನನ್ನ ಒಡೆಯನೇ, ನನಗೆ ದೂರವಾಗಿರುವದೇಕೆ?",
			"23": "<sup>23</sup> ನನ್ನ ದೇವರೇ, ಎದ್ದು ನನಗಾಗಿ ನ್ಯಾಯವನ್ನು ನಿರ್ಣಯಿಸು; ನನ್ನ ಕರ್ತನೇ, ಎಚ್ಚತ್ತು ನನ್ನ ವಿವಾದವನ್ನು ವಿಚಾರಿಸು.",
			"24": "<sup>24</sup> ಯೆಹೋವನೇ, ನನ್ನ ದೇವರೇ, ನಿನ್ನ ನೀತಿಗನುಸಾರವಾಗಿ ನನಗೆ ತೀರ್ಪುಕೊಡು; ನನ್ನ ವಿಷಯದಲ್ಲಿ ಹಿಗ್ಗುವದಕ್ಕೆ ಶತ್ರುಗಳಿಗೆ ಆಸ್ಪದವಿರಬಾರದು.",
			"25": "<sup>25</sup> ಆಹಾ, ನಮ್ಮ ಆಶೆ ನೆರವೇರಿತು ಅಂದುಕೊಳ್ಳುವದಕ್ಕೆ ಅವರಿಗೆ ಅವಕಾಶವಿರಬಾರದು; ಅವನನ್ನು ನುಂಗಿಬಿಟ್ಟಿದ್ದೇವೆಂದು ಅವರು ಕೊಚ್ಚಿಕೊಳ್ಳಬಾರದು.",
			"26": "<sup>26</sup> ನನ್ನ ಕೇಡಿನಲ್ಲಿ ಹಿಗ್ಗುವವರೆಲ್ಲರು ಆಶಾಭಂಗಪಟ್ಟು ಅವಮಾನಹೊಂದಲಿ; ನನ್ನನ್ನು ಹೀಯಾಳಿಸಿ ಆತ್ಮಸ್ತುತಿಮಾಡಿಕೊಳ್ಳುವವರು ಮಾನಭಂಗವನ್ನೂ ದುಷ್ಕೀರ್ತಿಯನ್ನೂ ಹೊದ್ದುಕೊಳ್ಳಲಿ.",
			"27": "<sup>27</sup> ನನ್ನ ನ್ಯಾಯಸ್ಥಾಪನೆಯನ್ನು ಬಯಸುವವರು ಆನಂದದೊಡನೆ ಜಯಧ್ವನಿ ಮಾಡಲಿ; ತನ್ನ ಸೇವಕನ ಹಿತವನ್ನು ಕೋರುವ ಯೆಹೋವನಿಗೆ ಸ್ತೋತ್ರ ಎಂದು ಯಾವಾಗಲೂ ಹೇಳುವವರಾಗಲಿ.",
			"28": "<sup>28</sup> ನನ್ನ ನಾಲಿಗೆಯು ನಿನ್ನ ನೀತಿಯನ್ನೂ ಮಹಿಮೆಯನ್ನೂ ದಿನವೆಲ್ಲಾ ವರ್ಣಿಸುವದು."
		},
		"36": {
			"1": "<sup>1</sup> ಪಾಪವು ದುಷ್ಟನ ಮನಸ್ಸಿನೊಳಗೆ ನುಡಿಯುತ್ತಿರುವದರಿಂದ ಅವನ ಕಣ್ಣೆದುರಿಗೆ ದೇವರ ಭಯವೇ ಇಲ್ಲ.",
			"2": "<sup>2</sup> ಅದು ಅವನನ್ನು ವಂಚಿಸಿ - ನಿನ್ನ ತಪ್ಪು ಬೈಲಿಗೆ ಬರುವದಿಲ್ಲ. ಹೇಯವಾಗುವದಿಲ್ಲ ಎಂದು ಊದಿಬಿಡುತ್ತದೆ.",
			"3": "<sup>3</sup> ಅವನ ಬಾಯಿಂದ ಕೆಡುಕೂ ವಂಚನೆಯೂ ಬರುತ್ತವೆ; ವಿವೇಕಮಾರ್ಗವನ್ನೂ ಪರಹಿತವನ್ನೂ ಬಿಟ್ಟೇ ಬಿಟ್ಟಿದ್ದಾನೆ.",
			"4": "<sup>4</sup> ಹಾಸಿಗೆಯ ಮೇಲೆ ಇರುವಾಗಲೂ ಕೆಡುಕನ್ನೇ ಯೋಚಿಸುತ್ತಿರುವನು; ಅಲ್ಲದ ದಾರಿಯಲ್ಲಿ ನಿಂತುಕೊಂಡಿದ್ದಾನೆ; ಎಂಥ ದುಷ್ಕೃತ್ಯಕ್ಕೂ ಹೇಸುವದಿಲ್ಲ.",
			"5": "<sup>5</sup> ಯೆಹೋವನೇ, ನಿನ್ನ ಪ್ರೀತಿಯು ಆಕಾಶದಷ್ಟು ಉನ್ನತವಾಗಿದೆ; ನಿನ್ನ ನಂಬಿಗಸ್ತಿಕೆಯು ಮೇಘಮಾರ್ಗವನ್ನು ಮುಟ್ಟುತ್ತದೆ.",
			"6": "<sup>6</sup> ನಿನ್ನ ನೀತಿಯು ದಿವ್ಯಪರ್ವತಗಳಂತೆಯೂ ನಿನ್ನ ನ್ಯಾಯವು ಮಹಾಸಾಗರದಂತೆಯೂ ಇವೆ; ಯೆಹೋವನೇ, ನೀನು ಮನುಷ್ಯರನ್ನೂ ಮೃಗಗಳನ್ನೂ ಸಂರಕ್ಷಿಸುತ್ತೀ.",
			"7": "<sup>7</sup> ದೇವರೇ, ನಿನ್ನ ಪ್ರೀತಿ ಎಷ್ಟೋ ಅಮೂಲ್ಯವಾದದ್ದು; ಮಾನವರು ನಿನ್ನ ರೆಕ್ಕೆಗಳ ಮರೆಯನ್ನು ಆಶ್ರಯಿಸಿಕೊಳ್ಳುತ್ತಾರೆ.",
			"8": "<sup>8</sup> ನಿನ್ನ ಮಂದಿರದ ಸಮೃದ್ಧಿಯಿಂದ ಅವರನ್ನು ಸಂತೃಪ್ತಿಪಡಿಸುತ್ತೀ; ನಿನ್ನ ಸಂಭ್ರಮಪ್ರವಾಹದಲ್ಲಿ ಅವರಿಗೆ ಪಾನಮಾಡಿಸುತ್ತೀ.",
			"9": "<sup>9</sup> ನಿನ್ನ ಬಳಿಯಲ್ಲಿ ಜೀವದ ಬುಗ್ಗೆ ಉಂಟಲ್ಲಾ; ನಿನ್ನ ತೇಜಸ್ಸು ನಮಗೆ ಬೆಳಕು ಕೊಡುತ್ತದೆ.",
			"10": "<sup>10</sup> ನಿನ್ನನ್ನು ಅರಿತವರಿಗೆ ನಿನ್ನ ಪ್ರೀತಿಯನ್ನು ಯಾವಾಗಲೂ ದಯಪಾಲಿಸು; ಯಥಾರ್ಥಚಿತ್ತರಿಗೆ ನಿನ್ನ ನ್ಯಾಯವನ್ನು ನಿರಂತರವೂ ಅನುಗ್ರಹಿಸು.",
			"11": "<sup>11</sup> ಗರ್ವಿಷ್ಠರ ಕಾಲಕೆಳಗೆ ನಮ್ಮನ್ನು ಬೀಳಲೀಸಬೇಡ; ದುರ್ಜನರಿಂದ ನಾವು ಭ್ರಷ್ಟರಾಗದಂತೆ ಮಾಡು.",
			"12": "<sup>12</sup> ಇಗೋ, ಕೆಡುಕರು ಕೆಡವಲ್ಪಟ್ಟು ಏಳಲಾರದೆ ಬಿದ್ದಿದ್ದಾರೆ."
		},
		"37": {
			"1": "<sup>1</sup> ಕೆಟ್ಟ ನಡತೆಯುಳ್ಳವರನ್ನು ನೋಡಿ ಉರಿಗೊಳ್ಳಬೇಡ; ದುರಾಚಾರಿಗಳಿಗೋಸ್ಕರ ಹೊಟ್ಟೆಕಿಚ್ಚು ಪಡಬೇಡ.",
			"2": "<sup>2</sup> ಅವರು ಹುಲ್ಲಿನಂತೆ ಬೇಗ ಒಣಗಿ ಹೋಗುವರು; ಸೊಪ್ಪಿನ ಪಲ್ಯದಂತೆ ಬಾಡಿಹೋಗುವರು.",
			"3": "<sup>3</sup> ಯೆಹೋವನಲ್ಲಿ ಭರವಸವಿಟ್ಟು ಒಳ್ಳೆಯದನ್ನು ಮಾಡು; ದೇಶದಲ್ಲಿ ವಾಸವಾಗಿದ್ದು ನಂಬಿಕೆಯನ್ನು ಅನುಸರಿಸು.",
			"4": "<sup>4</sup> ಆಗ ಯೆಹೋವನಲ್ಲಿ ಸಂತೋಷಿಸುವಿ; ಮತ್ತು ಆತನು ನಿನ್ನ ಇಷ್ಟಾರ್ಥಗಳನ್ನು ನೆರವೇರಿಸುವನು.",
			"5": "<sup>5</sup> ನಿನ್ನ ಭೂಯಾತ್ರೆಯ ಚಿಂತೆಯನ್ನು ಯೆಹೋವನಿಗೆ ವಹಿಸಿಬಿಟ್ಟು ಭರವಸದಿಂದಿರು; ಆತನೇ ಅದನ್ನು ಸಾಗಿಸುವನು.",
			"6": "<sup>6</sup> ಆತನು ನಿನ್ನ ನೀತಿಯನ್ನು ಉದಯದ ಬೆಳಕಿನಂತೆಯೂ ನಿನ್ನ ನ್ಯಾಯವನ್ನು ಮಧ್ಯಾಹ್ನದ ತೇಜಸ್ಸಿನಂತೆಯೂ ಪ್ರಕಾಶಗೊಳಿಸುವನು.",
			"7": "<sup>7</sup> ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಶಾಂತನಾಗಿ ಆತನಿಗೋಸ್ಕರ ಕಾದಿರು; ಕುಯುಕ್ತಿಗಳನ್ನು ನೆರವೇರಿಸಿಕೊಂಡು ಅಭಿವೃದ್ಧಿ ಹೊಂದುವವನನ್ನು ನೋಡಿ ಉರಿಗೊಳ್ಳಬೇಡ.",
			"8": "<sup>8</sup> ಕೋಪವನ್ನು ಅಣಗಿಸಿಕೋ; ರೋಷವನ್ನು ಬಿಡು. ಉರಿಗೊಳ್ಳಬೇಡ; ಕೆಡುಕಿಗೆ ಕಾರಣವಾದೀತು.",
			"9": "<sup>9</sup> ಕೆಡುಕರು ತೆಗೆದುಹಾಕಲ್ಪಡುವರು; ಯೆಹೋವನನ್ನು ನಿರೀಕ್ಷಿಸುವವರೇ ದೇಶವನ್ನು ಅನುಭವಿಸುವರು.",
			"10": "<sup>10</sup> ಇನ್ನು ಸ್ವಲ್ಪಕಾಲದೊಳಗೆ ದುಷ್ಟನು ಕಾಣಿಸದೆ ಹೋಗುವನು; ಅವನಿದ್ದ ಸ್ಥಳದಲ್ಲಿ ಎಷ್ಟು ವಿಚಾರಿಸಿದರೂ ಅವನು ಸಿಕ್ಕುವದೇ ಇಲ್ಲ.",
			"11": "<sup>11</sup> ಆದರೆ ದೀನರು ದೇಶವನ್ನು ಅನುಭವಿಸುವರು; ಅವರು ಮಹಾಸೌಖ್ಯದಿಂದ ಆನಂದಿಸುವರು.",
			"12": "<sup>12</sup> ದುಷ್ಟನು ನೀತಿವಂತನಿಗೆ ವಿರೋಧವಾಗಿ ಆಲೋಚಿಸುತ್ತಾನೆ; ಅವನನ್ನು ನೋಡಿ ಹಲ್ಲುಕಡಿಯುತ್ತಾನೆ.",
			"13": "<sup>13</sup> ಆದರೆ ಕರ್ತನು ಅವನಿಗೆ ಶಿಕ್ಷಾಕಾಲ ಬರುತ್ತದೆಂದು ನಗುತ್ತಾನೆ.",
			"14": "<sup>14</sup> ದುಷ್ಟರು ದೀನದರಿದ್ರರನ್ನು ಕಡಿದುಬಿಡಬೇಕೆಂದು ಕತ್ತಿಯನ್ನು ಹಿರಿದಿದ್ದಾರೆ; ಯಥಾರ್ಥರನ್ನು ಕೊಂದುಹಾಕಬೇಕೆಂದು ಬಿಲ್ಲನ್ನು ಎತ್ತಿದ್ದಾರೆ.",
			"15": "<sup>15</sup> ಆದರೆ ಅವರ ಕತ್ತಿ ಅವರ ಎದೆಯನ್ನೇ ಇರಿದುಬಿಡುವದು; ಅವರ ಬಿಲ್ಲುಗಳು ಸಿಡಿದು ಮುರಿದುಹೋಗುವವು.",
			"16": "<sup>16</sup> ದುಷ್ಟರ ಮಹಾಸಮೃದ್ಧಿಗಿಂತಲೂ ನೀತಿವಂತನ ಬಡತನವೇ ಲೇಸು.",
			"17": "<sup>17</sup> ದುಷ್ಟರ ತೋಳುಗಳು ಮುರಿದುಹೋಗುವವು; ನೀತಿವಂತರನ್ನು ಯೆಹೋವನೇ ಉದ್ಧರಿಸುವನು.",
			"18": "<sup>18</sup> ಯೆಹೋವನು ಸದ್ಭಕ್ತರ ಜೀವಮಾನವನ್ನು ಲಕ್ಷಿಸುತ್ತಾನೆ; ಅವರ ಸ್ವಾಸ್ತ್ಯವು ಶಾಶ್ವತವಾಗಿ ನಿಲ್ಲುವದು.",
			"19": "<sup>19</sup> ಅವರಿಗೆ ವಿಪತ್ಕಾಲದಲ್ಲಿಯೂ ಆಶಾಭಂಗವಾಗುವದಿಲ್ಲ; ಬರಗಾಲದಲ್ಲಿಯೂ ಅವರಿಗೆ ಕೊರತೆಯಿಲ್ಲ.",
			"20": "<sup>20</sup> ದುಷ್ಟರೋ ನಾಶವಾಗಿ ಹೋಗುವರು; ಯೆಹೋವನ ವೈರಿಗಳು ಹುಲ್ಲುಗಾವಲುಗಳ ಸೊಗಸಿನಂತಿದ್ದು ಮಾಯವಾಗುವರು. ಅವರು ಹಬೆಯಂತೆ ತೋರಿ ಅಡಗಿಹೋಗುವರು.",
			"21": "<sup>21</sup> ದುಷ್ಟನು ಸಾಲಮಾಡಿಕೊಂಡು ತೀರಿಸಲಾರದೆ ಹೋಗುವನು; ನೀತಿವಂತನು ಪರೋಪಕಾರಿಯಾಗಿ ಧರ್ಮಕೊಡುವನು.",
			"22": "<sup>22</sup> ಯೆಹೋವನ ಆಶೀರ್ವಾದವು ಯಾರಿಗಿರುವದೋ ಅವರು ದೇಶವನ್ನು ಅನುಭವಿಸುವರು; ಆತನ ಶಾಪವು ಯಾರಿಗಿರುವದೋ ಅವರು ತೆಗೆದುಹಾಕಲ್ಪಡುವರು.",
			"23": "<sup>23</sup> ಸತ್ಪುರುಷನ ಗತಿಸ್ಥಾಪನೆಯು ಯೆಹೋವನಿಂದಲೇ ಆಗಿದೆ, ಆತನು ಅವನ ಪ್ರವರ್ತನೆಯನ್ನು ಮೆಚ್ಚುತ್ತಾನೆ.",
			"24": "<sup>24</sup> ಅವನು ಕೆಳಗೆ ಬಿದ್ದರೂ ಏಳದೆ ಹೋಗುವದಿಲ್ಲ; ಯೆಹೋವನು ಅವನನ್ನು ಕೈಹಿಡಿದು ಉದ್ಧಾರಮಾಡುವನು.",
			"25": "<sup>25</sup> ನಾನು ಬಾಲಕನಾಗಿದ್ದೆನು, ಈಗ ವೃದ್ಧನಾಗಿದ್ದೇನೆ; ಈವರೆಗೂ ನೀತಿವಂತನು ದಿಕ್ಕಿಲ್ಲದೆ ಬಿದ್ದಿರುವದನ್ನಾಗಲಿ ಅವನ ಸಂತತಿಯವರು ಭಿಕ್ಷೆಬೇಡಿ ತಿನ್ನುವದನ್ನಾಗಲಿ ನೋಡಲಿಲ್ಲ.",
			"26": "<sup>26</sup> ಅವನು ಯಾವಾಗಲೂ ಧರ್ಮಿಷ್ಠನಾಗಿ ಹಣ ಸಹಾಯವನ್ನು ಮಾಡುತ್ತಾನೆ; ಅವನ ಸಂತತಿಯವರು ಆಶೀರ್ವಾದ ಹೊಂದುವರು.",
			"27": "<sup>27</sup> ಕೆಟ್ಟದ್ದಕ್ಕೆ ಹೋಗದೆ ಒಳ್ಳೆಯದನ್ನೇ ಮಾಡು; ಆಗ ನೀನು ಯಾವಾಗಲೂ [ದೇಶದಲ್ಲಿ] ವಾಸವಾಗಿರುವಿ.",
			"28": "<sup>28</sup> ಯೆಹೋವನು ನ್ಯಾಯವನ್ನು ಮೆಚ್ಚುವವನು; ತನ್ನ ಭಕ್ತರನ್ನು ಎಂದಿಗೂ ಕೈಬಿಡುವವನಲ್ಲ. ಅವರು ಸದಾಕಾಲವೂ ಸುರಕ್ಷಿತರಾಗಿರುವರು. ದುಷ್ಟರ ಸಂತತಿ ತೆಗೆದುಹಾಕಲ್ಪಡುವದು.",
			"29": "<sup>29</sup> ನೀತಿವಂತರೋ ದೇಶವನ್ನು ಅನುಭವಿಸುವವರಾಗಿ ಅದರಲ್ಲಿ ಶಾಶ್ವತವಾಗಿ ವಾಸಿಸುವರು.",
			"30": "<sup>30</sup> ನೀತಿವಂತನ ಬಾಯಿಯು ಸುಜ್ಞಾನವನ್ನು ನುಡಿಯುತ್ತದೆ; ಅವನ ನಾಲಿಗೆ ನ್ಯಾಯವನ್ನೇ ಹೇಳುತ್ತದೆ.",
			"31": "<sup>31</sup> ದೇವರ ಧರ್ಮೋಪದೇಶವು ಅವನ ಹೃದಯದಲ್ಲಿರುವದು; ಅವನು ಜಾರುವದೇ ಇಲ್ಲ.",
			"32": "<sup>32</sup> ದುಷ್ಟನು ಹೊಂಚುಹಾಕಿ ನೀತಿವಂತನನ್ನು ಕೊಲ್ಲಬೇಕೆಂದು ಸಮಯನೋಡುತ್ತಾನೆ.",
			"33": "<sup>33</sup> ಆದರೆ ಯೆಹೋವನು ಅವನನ್ನು ಸಿಕ್ಕಗೊಡಿಸುವದಿಲ್ಲ; ಅವನನ್ನು ನ್ಯಾಯವಿಚಾರಣೆಯಲ್ಲಿ ಅಪರಾಧಿಯೆಂದು ಎಣಿಸುವದಿಲ್ಲ.",
			"34": "<sup>34</sup> ಯೆಹೋವನನ್ನು ನಿರೀಕ್ಷಿಸುವವನಾಗಿ ಆತನ ಮಾರ್ಗವನ್ನೇ ಅನುಸರಿಸು; ಆಗ ಆತನು ನಿನ್ನನ್ನು ಮುಂದಕ್ಕೆ ತಂದು ದೇಶವನ್ನು ಅನುಭವಿಸುವಂತೆ ಮಾಡುವನು; ದುಷ್ಟರು ತೆಗೆದುಹಾಕಲ್ಪಡುವದನ್ನು ನೀನು ನೋಡುವಿ.",
			"35": "<sup>35</sup> ದುಷ್ಟನು ಭೀಕರನಾಗಿ ಸ್ವಸ್ಥಳದಲ್ಲಿ ಹಸುರಾಗಿ ಬೆಳೆದ ಮರದಂತೆ ವಿಸ್ತರಿಸಿಕೊಂಡಿರುವದನ್ನು ನೋಡಿದ್ದೆನು.",
			"36": "<sup>36</sup> ತರುವಾಯ ನಾನು ಆ ದಾರಿಯಲ್ಲಿ ಹೋಗಿ ನೋಡಲಾಗಿ ಅವನು ಇಲ್ಲದೆ ಹೋಗಿದ್ದನು; ಅವನನ್ನು ಹುಡುಕಿದರೂ ಸಿಕ್ಕಲೇ ಇಲ್ಲ.",
			"37": "<sup>37</sup> ಒಳ್ಳೇ ನಡತೆಯುಳ್ಳವನನ್ನು ನೋಡು, ಯಥಾರ್ಥನನ್ನು ಲಕ್ಷಿಸು; ಶಾಂತನಿಗೆ ಸಂತಾನವೃದ್ಧಿ ಆಗುವದು.",
			"38": "<sup>38</sup> ದ್ರೋಹಿಗಳೆಲ್ಲರೂ ನಾಶವಾಗುವರು; ದುಷ್ಟರ ಸಂತಾನವು ತೆಗೆದುಹಾಕಲ್ಪಡುವದು.",
			"39": "<sup>39</sup> ನೀತಿವಂತರ ರಕ್ಷಣೆ ಯೆಹೋವನಿಂದಲೇ; ಇಕ್ಕಟ್ಟಿನಲ್ಲಿ ಆತನೇ ಅವರಿಗೆ ದುರ್ಗಸ್ಥಾನ.",
			"40": "<sup>40</sup> ಯೆಹೋವನು ಸಹಾಯಕನಾಗಿ ಅವರನ್ನು ತಪ್ಪಿಸಿಬಿಡುವನು; ಅವರು ಆತನ ಆಶ್ರಿತರಾದ್ದರಿಂದ ದುಷ್ಟರ ಕೈಯಿಂದ ತಪ್ಪಿಸಿ ರಕ್ಷಿಸುವನು."
		},
		"38": {
			"1": "<sup>1</sup> ಯೆಹೋವನೇ, ಕೋಪದಿಂದ ನನ್ನನ್ನು ಶಿಕ್ಷಿಸಬೇಡ; ರೋಷದಿಂದ ದಂಡಿಸಬೇಡ.",
			"2": "<sup>2</sup> ನಿನ್ನ ಬಾಣಗಳು ನನ್ನೊಳಗೆ ಹೊಕ್ಕಿವೆ; ನಿನ್ನ ಶಿಕ್ಷಾಹಸ್ತವು ನನ್ನ ಮೇಲೆ ಭಾರವಾಗಿದೆ.",
			"3": "<sup>3</sup> ನಿನ್ನ ಸಿಟ್ಟಿನಿಂದ ನನ್ನ ಶರೀರದಲ್ಲಿ ಸ್ವಸ್ಥವೇ ತಪ್ಪಿಹೋಗಿದೆ; ನನ್ನ ಪಾಪದಿಂದ ನನ್ನ ಎಲುಬುಗಳಲ್ಲಿ ಸ್ವಲ್ಪವೂ ಕ್ಷೇಮವಿಲ್ಲ.",
			"4": "<sup>4</sup> ನನ್ನ ಅಪರಾಧಗಳು ನನ್ನನ್ನು ಮುಣುಗಿಸಿ ಬಿಟ್ಟವೆ; ಅವು ಹೊರಲಾರದಷ್ಟು ಭಾರವಾದ ಹೊರೆಯಂತೆ ನನ್ನನ್ನು ಅದಿಮಿಬಿಟ್ಟವೆ.",
			"5": "<sup>5</sup> ನನ್ನ ಮೂರ್ಖತನದ ಫಲವಾದ ಬಾಸುಂಡೆಗಳು ಕೀವು ಸೋರಿ ದುರ್ವಾಸನೆಯಾಗಿವೆ.",
			"6": "<sup>6</sup> ನಾನು ಬಹಳವಾಗಿ ಬಾಗಿ ಕುಗ್ಗಿದ್ದೇನೆ; ಯಾವಾಗಲೂ ದುಃಖದಿಂದ ವಿಕಾರಿಯಾಗಿ ಅಲೆಯುತ್ತೇನೆ.",
			"7": "<sup>7</sup> ನನ್ನ ಸೊಂಟಕ್ಕೆ ಉರಿಬಡಿದಂತಿದೆ; ನನ್ನ ದೇಹದಲ್ಲಿ ಸ್ವಲ್ಪವಾದರೂ ಕ್ಷೇಮವಿಲ್ಲ.",
			"8": "<sup>8</sup> ನನಗೆ ಜೋಮುಹಿಡಿದಂತಿದೆ; ಬಹಳ ಮನಗುಂದಿದವನಾದೆನು. ಹೃದಯದ ಸಂಕಟದ ದೆಸೆಯಿಂದ ಅರಚಿಕೊಳ್ಳುತ್ತಾ ಇದ್ದೇನೆ.",
			"9": "<sup>9</sup> ಕರ್ತನೇ, ನನ್ನ ಅಪೇಕ್ಷೆ ನಿನಗೆ ಗೊತ್ತುಂಟು; ನನ್ನ ನಿಟ್ಟುಸುರು ನಿನಗೆ ಮರೆಯಾಗಿಲ್ಲ.",
			"10": "<sup>10</sup> ನನ್ನ ಗುಂಡಿಗೆ ಬಡುಕೊಳ್ಳುತ್ತದೆ; ನನ್ನ ಚೈತನ್ಯವು ಕುಗ್ಗಿಹೋಯಿತು;",
			"11": "<sup>11</sup> ನನ್ನ ಕಣ್ಣುಗಳೂ ಮೊಬ್ಬಾಗಿಹೋದವು. ನನ್ನ ಆಪ್ತಸ್ನೇಹಿತರು ನನ್ನ ಜಾಡ್ಯವನ್ನು ನೋಡಿ ಓರೆಯಾಗಿ ಹೋಗುತ್ತಾರೆ; ನನ್ನ ಬಂಧುಗಳು ದೂರ ನಿಲ್ಲುತ್ತಾರೆ.",
			"12": "<sup>12</sup> ನನ್ನ ಪ್ರಾಣವನ್ನು ತೆಗೆಯಬೇಕೆಂಬವರು ನನಗೆ ಉರುಲುಗಳನ್ನು ಒಡ್ಡಿದ್ದಾರೆ; ನನ್ನ ವಿಪತ್ತನ್ನು ಕೋರುವವರು ನನಗೆ ನಾಶನವನ್ನೇ ನಿಶ್ಚಯಿಸಿಕೊಂಡು ಯಾವಾಗಲೂ ಮೋಸವನ್ನು ಕಲ್ಪಿಸುತ್ತಿದ್ದಾರೆ.",
			"13": "<sup>13</sup> ನಾನಂತೂ ಕಿವುಡನಂತೆ ಕೇಳದವನಾಗಿದ್ದೇನೆ; ಮೂಕನಂತೆ ಬಾಯಿತೆರೆಯುವದೇ ಇಲ್ಲ.",
			"14": "<sup>14</sup> ನಾನು ಕಿವಿಕೇಳಿಸದವನಂತೆಯೂ ಪ್ರತ್ಯುತ್ತರಕೊಡಲಾರದವನಂತೆಯೂ ಆದೆನು.",
			"15": "<sup>15</sup> ಯೆಹೋವನೇ, ನಿನ್ನನ್ನೇ ನಿರೀಕ್ಷಿಸಿಕೊಂಡಿದ್ದೇನೆ; ನನ್ನ ಕರ್ತನೇ, ನನ್ನ ದೇವರೇ, ನೀನೇ ಸದುತ್ತರವನ್ನು ಕೊಡುವವನು.",
			"16": "<sup>16</sup> ನನ್ನ ವಿಷಯದಲ್ಲಿ ಶತ್ರುಗಳಿಗೆ ಸಂತೋಷವಾಗಬಾರದು; ನಾನು ಜಾರಿಬಿದ್ದರೆ ಹಿಗ್ಗುವರಲ್ಲಾ ಅಂದುಕೊಂಡೆನು.",
			"17": "<sup>17</sup> ನನಗೆ ಆಪತ್ತೇ ಸಿದ್ಧವಾಗಿದೆ; ಯಾವಾಗಲೂ ನನಗೆ ಸಂಕಟವದೆ.",
			"18": "<sup>18</sup> ನಾನು ಅಪರಾಧಿಯೇ ಹೌದೆಂದು ಒಪ್ಪಿಕೊಳ್ಳುತ್ತೇನೆ; ನನ್ನ ಪಾಪದ ದೆಸೆಯಿಂದಲೇ ನನಗೆ ವ್ಯಸನವುಂಟಾಯಿತು.",
			"19": "<sup>19</sup> ನನ್ನ ಶತ್ರುಗಳು ಚುರುಕಾದವರೂ ಬಲಿಷ್ಠರೂ ಆಗಿದ್ದಾರೆ; ನನ್ನನ್ನು ಅನ್ಯಾಯವಾಗಿ ದ್ವೇಷಿಸುವವರು ಬಹುಮಂದಿ.",
			"20": "<sup>20</sup> ಉಪಕಾರಕ್ಕೆ ಪ್ರತಿಯಾಗಿ ಅಪಕಾರ ಸಲ್ಲಿಸುವವರು ಒಳ್ಳೆಯದನ್ನು ಅನುಸರಿಸುವ ನನ್ನನ್ನು ಎದುರಿಸುತ್ತಾರೆ.",
			"21": "<sup>21</sup> ಯೆಹೋವನೇ, ಕೈ ಬಿಡಬೇಡ; ನನ್ನ ದೇವರೇ, ದೂರವಾಗಿರಬೇಡ.",
			"22": "<sup>22</sup> ನನ್ನ ಕರ್ತನೇ, ನನ್ನ ರಕ್ಷಕನೇ, ಬೇಗ ಬಂದು ಸಹಾಯಮಾಡು."
		},
		"39": {
			"1": "<sup>1</sup> ನನ್ನ ನಾಲಿಗೆ ಪಾಪಕ್ಕೆ ಹೋಗದಂತೆ ಜಾಗರೂಕನಾಗಿರುವೆನು; ದುಷ್ಟರು ನನ್ನ ಮುಂದೆ ಇರುವಾಗ ಬಾಯಿಗೆ ಕುಕ್ಕೆಹಾಕಿಕೊಂಡಿರುವೆನು ಅಂದುಕೊಂಡೆನು.",
			"2": "<sup>2</sup> ನಾನು ಮೌನವಾಗಿದ್ದೆನು; ಒಳ್ಳೆಯದನ್ನಾದರೂ ಆಡದೆ ಸುಮ್ಮನಿದ್ದೆನು; ಆದರೆ ನನ್ನ ಸಂಕಟವು ಹೆಚ್ಚಿತು.",
			"3": "<sup>3</sup> ನನ್ನ ಹೃದಯದಲ್ಲಿ ಸಂತಾಪ ಉಕ್ಕಿತು; ನಾನು ಯೋಚಿಸುತ್ತಿರುವಲ್ಲಿ ಬೆಂಕಿಯುರಿಯಿತು. ಆಗ ನಾನು ಬಾಯಿಬಿಟ್ಟು -",
			"4": "<sup>4</sup> ಯೆಹೋವನೇ, ನನಗೆ ಅವಸಾನವುಂಟೆಂದೂ ನನ್ನ ಜೀವಮಾನವು ಅತ್ಯಲ್ಪವೆಂದೂ ನಾನು ಎಷ್ಟೋ ಅಸ್ಥಿರನೆಂದೂ ನನಗೆ ತಿಳಿಯಪಡಿಸು.",
			"5": "<sup>5</sup> ನನ್ನ ಆಯುಸ್ಸನ್ನು ಗೇಣುದ್ದವಾಗಿ ಮಾಡಿದ್ದೀಯಲ್ಲಾ; ನನ್ನ ಜೀವಿತಕಾಲ ನಿನ್ನ ಎಣಿಕೆಯಲ್ಲಿ ಏನೂ ಅಲ್ಲ ಅಂದೆನು. ಮನುಷ್ಯನೆಂಬವನು ಎಷ್ಟು ಸ್ಥಿರನೆಂದು ಕಂಡರೂ ಬರೀ ಉಸಿರೇ. ಸೆಲಾ.",
			"6": "<sup>6</sup> ನರರು ನಿಜವಲ್ಲದ ಮಾಯಾರೂಪದಿಂದ ಸಂಚರಿಸುವವರು; ಅವರು ಸುಮ್ಮ ಸುಮ್ಮನೆ ಗಡಿಬಿಡಿಮಾಡುವವರು; ಆಸ್ತಿಯನ್ನು ಕೂಡಿಸಿಟ್ಟುಕೊಳ್ಳುತ್ತಾರೆ, ಆದರೆ ಅದು ಯಾರ ಪಾಲಾಗುವದೋ ತಾವೇ ತಿಳಿಯರು.",
			"7": "<sup>7</sup> ಹೀಗಿರಲಾಗಿ ಕರ್ತನೇ, ಇನ್ನು ಯಾವದಕ್ಕೆ ಕಾದುಕೊಳ್ಳಲಿ? ನೀನೇ ನನ್ನ ನಿರೀಕ್ಷೆ.",
			"8": "<sup>8</sup> ಎಲ್ಲಾ ದ್ರೋಹಗಳಿಂದ ನನ್ನನ್ನು ಬಿಡುಗಡೆ ಮಾಡು; ಮೂರ್ಖರ ನಿಂದೆಗೆ ಗುರಿಮಾಡಬೇಡ.",
			"9": "<sup>9</sup> ನೀನೇ ಇದನ್ನು ಬರಮಾಡಿದ್ದರಿಂದ ನಾನು ಏನೂ ಹೇಳದೆ ಮೌನವಾಗಿರುವೆನು.",
			"10": "<sup>10</sup> ನಿನ್ನ ದಂಡನೆಯನ್ನು ಪರಿಹರಿಸು; ನಿನ್ನ ಕೈಹೊಡೆತದಿಂದ ಸಾಯುವ ಹಾಗಿದ್ದೇನಲ್ಲಾ.",
			"11": "<sup>11</sup> ನೀನು ನರನನ್ನು ಪಾಪದ ನಿಮಿತ್ತ ಗದರಿಸಿ ಶಿಕ್ಷಿಸುವಾಗ ಅವನ ಚೆಲುವಿಕೆಯು ನುಸಿಹತ್ತಿತೋ ಎಂಬಂತೆ ಹಾಳಾಗಿ ಹೋಗುತ್ತದೆ. ಮನುಷ್ಯನೆಂಬವನು ಬರೀ ಉಸಿರೇ. ಸೆಲಾ.",
			"12": "<sup>12</sup> ಯೆಹೋವನೇ, ನನ್ನ ಪ್ರಾರ್ಥನೆಯನ್ನು ಲಾಲಿಸು; ನನ್ನ ಮೊರೆಗೆ ಕಿವಿಗೊಡು. ನನ್ನ ಕಣ್ಣೀರನ್ನು ನೋಡು, ಸುಮ್ಮನಿರಬೇಡ. ನನ್ನ ಪೂರ್ವಿಕರಂತೆಯೇ ನಾನು ನಿನ್ನ ಮರೆಹೊಕ್ಕಿರುವ ಪ್ರವಾಸಿಯೂ ಪರದೇಶದವನೂ ಆಗಿದ್ದೇನಲ್ಲಾ.",
			"13": "<sup>13</sup> ನಾನು ಬಿಟ್ಟುಹೋಗಿ ಇಲ್ಲವಾಗುವದಕ್ಕಿಂತ ಮುಂಚೆ ಸ್ವಲ್ಪ ಸಂತೋಷಪಡುವಂತೆ ನಿನ್ನ ಕೋಪದೃಷ್ಟಿಯನ್ನು ನನ್ನ ಕಡೆಯಿಂದ ತಿರುಗಿಸಿಕೊಳ್ಳಬೇಕು, ದೇವಾ."
		},
		"40": {
			"1": "<sup>1</sup> ನಾನು ಯೆಹೋವನಿಗೋಸ್ಕರ ನಿರೀಕ್ಷಿಸಿಯೇ ನಿರೀಕ್ಷಿಸಿದೆನು; ಆತನು ನನ್ನ ಮೊರೆಗೆ ಕಿವಿಗೊಟ್ಟು ಲಕ್ಷಿಸಿದನು.",
			"2": "<sup>2</sup> ನನ್ನನ್ನು ನಾಶನದ ಗುಂಡಿಯೊಳಗಿಂದ ಎತ್ತಿದನು; ಕೆಸರಿನೊಳಗಿಂದ ನನ್ನನ್ನು ತೆಗೆದು ಬಂಡೆಯ ಮೇಲೆ ನಿಲ್ಲಿಸಿ ನಾನು ದೃಢವಾಗಿ ಹೆಜ್ಜೆಯಿಡುವಂತೆ ಮಾಡಿದನು.",
			"3": "<sup>3</sup> ಆತನು ನನ್ನ ಬಾಯಲ್ಲಿ ನೂತನಕೀರ್ತನೆಯನ್ನು ಹುಟ್ಟಿಸಿದ್ದಾನೆ; ಅದು ನಮ್ಮ ದೇವರ ಸ್ತೋತ್ರವೇ. ಆತನ ಮಹತ್ಕಾರ್ಯಗಳನ್ನು ನೋಡಿದ ಅನೇಕರು ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿ ಯೆಹೋವನಲ್ಲಿ ನಂಬಿಕೆಯಿಡುವರು.",
			"4": "<sup>4</sup> ಯಾವನು ಅಹಂಕಾರಿಗಳಲ್ಲಿಯೂ ಸುಳ್ಳನ್ನು ಹಿಂಬಾಲಿಸುವವರಲ್ಲಿಯೂ ಸೇರದೆ ಯೆಹೋವನನ್ನೇ ನಂಬುತ್ತಾನೋ ಅವನೇ ಧನ್ಯನು.",
			"5": "<sup>5</sup> ಯೆಹೋವನೇ, ನನ್ನ ದೇವರೇ, ನಿನಗೆ ಸಮಾನನಾದ ದೇವರು ಯಾರು? ನಮ್ಮ ಹಿತಕ್ಕಾಗಿ ನೀನು ಮಾಡಿದ ಆಲೋಚನೆಗಳೂ ಅದ್ಭುತಕಾರ್ಯಗಳೂ ಎಷ್ಟೋ ವಿಶೇಷವಾಗಿವೆ; ಅವುಗಳನ್ನು ವಿವರಿಸಿ ಹೇಳೋಣವೆಂದರೆ ಅಸಾಧ್ಯವು; ಅವು ಅಸಂಖ್ಯಾತವಾಗಿವೆ.",
			"6": "<sup>6</sup> ಯಜ್ಞನೈವೇದ್ಯಗಳು ನಿನಗೆ ಇಷ್ಟವಲ್ಲ; ಸರ್ವಾಂಗಹೋಮಗಳನ್ನಾಗಲಿ ದೋಷಪರಿಹಾರಕ ಯಜ್ಞಗಳನ್ನಾಗಲಿ ನೀನು ಅಪೇಕ್ಷಿಸಲಿಲ್ಲ. ಆದರೆ ಶ್ರವಣಶಕ್ತಿಯನ್ನು ನನಗೆ ಅನುಗ್ರಹಿಸಿದಿ.",
			"7": "<sup>7</sup> ಆಗ ನಾನು - ಇಗೋ ಇದ್ದೇನೆ; ನನ್ನ ಕರ್ತವ್ಯವು ಗ್ರಂಥದ ಸುರಳಿಯಲ್ಲಿ ಬರೆದದೆ.",
			"8": "<sup>8</sup> ನನ್ನ ದೇವರೇ, ನಿನ್ನ ಚಿತ್ತವನ್ನು ಅನುಸರಿಸುವದೇ ನನ್ನ ಸಂತೋಷವು; ನಿನ್ನ ಧರ್ಮೋಪದೇಶವು ನನ್ನ ಅಂತರಂಗದಲ್ಲಿದೆ ಅಂದೆನು.",
			"9": "<sup>9</sup> ನೀನು ನೀತಿಯನ್ನು ಸಾಧಿಸಿದ ಶುಭಸಮಾಚಾರವನ್ನು ಧಾರಾಳವಾಗಿ ಮಹಾಸಭೆಯಲ್ಲಿ ಪ್ರಕಟಿಸಿದೆನು; ಯೆಹೋವನೇ, ನೀನೇ ಬಲ್ಲೆ.",
			"10": "<sup>10</sup> ನಿನ್ನ ನೀತಿಸಾಧನೆಯನ್ನು ಮರೆಮಾಜಲಿಲ್ಲ; ನಿನ್ನ ನಂಬಿಕೆಯನ್ನೂ ರಕ್ಷಣೆಯನ್ನೂ ಪ್ರಸಿದ್ಧಪಡಿಸಿದೆನು; ನಿನ್ನ ಕೃಪಾಸತ್ಯತೆಗಳನ್ನು ಮಹಾಸಮುದಾಯದಲ್ಲಿ ತಿಳಿಸದೆ ಇರಲಿಲ್ಲ.",
			"11": "<sup>11</sup> ಯೆಹೋವನೇ, ನೀನಂತೂ ನಿನ್ನ ಕರುಣೆಯನ್ನು ನನ್ನಿಂದ ಅಗಲಿಸಬೇಡ; ನಿನ್ನ ಕೃಪಾಸತ್ಯತೆಗಳು ನನ್ನನ್ನು ಯಾವಾಗಲೂ ಕಾಪಾಡಲಿ.",
			"12": "<sup>12</sup> ಯಾಕಂದರೆ ಲೆಕ್ಕವಿಲ್ಲದ ಆಪತ್ತುಗಳು ನನ್ನನ್ನು ಸುತ್ತಿಕೊಂಡಿವೆ; ನನ್ನ ಪಾಪಗಳು ನನ್ನನ್ನು ಹಿಂದಟ್ಟಿ ಹಿಡಿದಿರುತ್ತವೆ, ನನಗೆ ದಿಕ್ಕೇ ತೋರುವದಿಲ್ಲ. ಅವು ನನ್ನ ತಲೇ ಕೂದಲುಗಳಿಗಿಂತಲೂ ಹೆಚ್ಚಾಗಿವೆ, ನಾನು ಎದೆಗುಂದಿ ಹೋದೆನು.",
			"13": "<sup>13</sup> ಯೆಹೋವನೇ, ದಯವಿಟ್ಟು ನನ್ನನ್ನು ರಕ್ಷಿಸು; ಯೆಹೋವನೇ, ಬೇಗ ಬಂದು ಸಹಾಯಮಾಡು.",
			"14": "<sup>14</sup> ನನ್ನ ಪ್ರಾಣವನ್ನು ತೆಗೆಯುವದಕ್ಕೆ ಸಮಯ ನೋಡುವವರು ಆಶಾಭಂಗಪಟ್ಟು ಅವಮಾನಹೊಂದಲಿ; ನನ್ನ ಆಪತ್ತಿನಲ್ಲಿ ಸಂತೋಷಿಸುವವರು ಮಾನಭಂಗದಿಂದ ಹಿಂದಿರುಗಿ ಓಡಲಿ.",
			"15": "<sup>15</sup> ಆಹಾ, ಆಹಾ ಎಂದು ನನ್ನನ್ನು ಪರಿಹಾಸ್ಯ ಮಾಡುವವರು ತಮಗೆ ಆಗುವ ಅವಮಾನದಿಂದ ವಿಸ್ಮಯಗೊಳ್ಳಲಿ.",
			"16": "<sup>16</sup> ನಿನ್ನ ದರ್ಶನವನ್ನು ಕೋರುವವರೆಲ್ಲರು ನಿನ್ನಲ್ಲಿ ಉಲ್ಲಾಸದಿಂದ ಸಂತೋಷಿಸಲಿ; ನಿನ್ನ ಜಯದಲ್ಲಿ ಆನಂದಿಸುವವರು ಯೆಹೋವನಿಗೆ ಸ್ತೋತ್ರವೆಂದು ಯಾವಾಗಲೂ ಹೇಳುವವರಾಗಲಿ.",
			"17": "<sup>17</sup> ನಾನಾದರೋ ಕುಗ್ಗಿದವನೂ ದಿಕ್ಕಿಲ್ಲದವನೂ ಆಗಿದ್ದೇನೆ; ಕರ್ತನೇ ನನ್ನ ಹಿತಚಿಂತಕನು. ನನ್ನ ದೇವರೇ, ನೀನೇ ನನಗೆ ಸಹಾಯಕನೂ ರಕ್ಷಕನೂ ಆಗಿದ್ದೀ; ತಡಮಾಡಬೇಡ."
		},
		"41": {
			"1": "<sup>1</sup> ದಿಕ್ಕಿಲ್ಲದವನನ್ನು ಪರಾಂಬರಿಸುವವನು ಧನ್ಯನು; ಯೆಹೋವನು ಅವನನ್ನು ಆಪತ್ಕಾಲದಲ್ಲಿ ರಕ್ಷಿಸುವನು.",
			"2": "<sup>2</sup> ಯೆಹೋವನು ಅವನ ಪ್ರಾಣವನ್ನು ಕಾಪಾಡಿ ಉಳಿಸುವನು; ಅವನು ದೇಶದಲ್ಲಿ ಧನ್ಯನೆನಿಸಿಕೊಳ್ಳುವನು; ಯೆಹೋವನೇ, ಅವನನ್ನು ಶತ್ರುಗಳ ಅಧೀನಕ್ಕೆ ಕೊಡಬೇಡ!",
			"3": "<sup>3</sup> ಅವನು ಅಸ್ವಸ್ಥನಾಗಿ ಬಿದ್ದುಕೊಂಡಿರುವಾಗ ಯೆಹೋವನು ಅವನನ್ನು ಉದ್ಧರಿಸುವನು; ಅವನ ರೋಗವನ್ನೆಲ್ಲಾ ಪರಿಹರಿಸಿ ಆರೋಗ್ಯವನ್ನುಂಟುಮಾಡಿದಿಯಲ್ಲಾ, ಸ್ವಾಮೀ.",
			"4": "<sup>4</sup> ನಾನಂತೂ - ಯೆಹೋವನೇ, ನಿನ್ನ ಆಜ್ಞೆಯನ್ನು ಮೀರಿ ಪಾಪಮಾಡಿದ್ದೇನೆ; ನನ್ನನ್ನು ಕರುಣಿಸಿ ಸ್ವಸ್ಥಮಾಡು ಅಂದೆನು.",
			"5": "<sup>5</sup> ನನ್ನ ಹಾನಿಯನ್ನು ಅಪೇಕ್ಷಿಸುವ ಶತ್ರುಗಳು - ಅವನು ಯಾವಾಗ ಸತ್ತಾನು? ಅವನ ಹೆಸರು ಯಾವಾಗ ಇಲ್ಲದೆಹೋದೀತು ಎಂದು ಹೇಳಿಕೊಳ್ಳುತ್ತಾರೆ.",
			"6": "<sup>6</sup> ಅವರಲ್ಲೊಬ್ಬನು ನನ್ನನ್ನು ನೋಡುವದಕ್ಕೆ ಬಂದರೆ ಕಪಟದ ಮಾತನ್ನಾಡುವನು; ಮನಸ್ಸಿನಲ್ಲಿ ಅಶುಭಗಳನ್ನು ಸಂಗ್ರಹಿಸಿಕೊಂಡು ಹೋಗಿ ಹೊರಗೆ ಪ್ರಕಟಿಸುತ್ತಾನೆ.",
			"7": "<sup>7</sup> ನನ್ನ ಹಗೆಯವರೆಲ್ಲರು ನನಗೆ ವಿರೋಧವಾಗಿ ಕಿವಿಗಳಲ್ಲಿ ಗುಜುಗುಜು ಮಾತಾಡಿಕೊಳ್ಳುತ್ತಾರೆ; ನನಗೆ ಕೇಡನ್ನು ಕಲ್ಪಿಸುತ್ತಾರೆ.",
			"8": "<sup>8</sup> ಅವನನ್ನು ಅಸಾಧ್ಯರೋಗ ಹಿಡಿದದೆ; ಅವನು ಹಾಸಿಗೆಯನ್ನು ಬಿಟ್ಟು ತಿರಿಗಿ ಏಳುವದೇ ಇಲ್ಲವೆಂದು ಹೇಳಿಕೊಳ್ಳುತ್ತಾರೆ.",
			"9": "<sup>9</sup> ನಾನು ಯಾವನನ್ನು ನಂಬಿದ್ದೆನೋ ಯಾವನು ನನ್ನ ಮನೆಯಲ್ಲಿ ಅನ್ನಪಾನಗಳನ್ನು ತೆಗೆದುಕೊಂಡನೋ ಅಂಥ ಆಪ್ತಸ್ನೇಹಿತನು ನನಗೆ ಕಾಲನ್ನು ಅಡ್ಡಗೊಟ್ಟಿದ್ದಾನೆ.",
			"10": "<sup>10</sup> ಯೆಹೋವನೇ, ನೀನಾದರೋ ಕರುಣಿಸಿ ನನ್ನನ್ನು ಏಳಮಾಡು; ಆಗ ನಾನು ಅವರಿಗೆ ಮುಯ್ಯಿತೀರಿಸುವೆನು.",
			"11": "<sup>11</sup> ಶತ್ರುಗಳ ಜಯಧ್ವನಿ ಇಲ್ಲದ್ದರಿಂದಲೇ ನಿನ್ನ ಒಲುಮೆ ನನಗುಂಟೆಂದು ತಿಳಿದುಕೊಳ್ಳುವೆನು.",
			"12": "<sup>12</sup> ನಿರ್ದೋಷಿಯಾದ ನನ್ನನ್ನಾದರೋ ನೀನು ಉದ್ಧಾರಮಾಡಿ ನಿನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಶಾಶ್ವತವಾಗಿ ನಿಲ್ಲಿಸುವಿ.",
			"13": "<sup>13</sup> ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಯುಗಯುಗಾಂತರಗಳವರೆಗೂ ಕೊಂಡಾಟವಾಗಲಿ. ಆಮೆನ್. ಆಮೆನ್."
		},
		"42": {
			"1": "<sup>1</sup> ದೇವರೇ, ಬಾಯಾರಿದ ಜಿಂಕೆಯು ನೀರಿನ ತೊರೆಗಳನ್ನು ಹೇಗೋ ಹಾಗೆಯೇ ನನ್ನ ಮನವು ನಿನ್ನನ್ನು ಬಯಸುತ್ತದೆ.",
			"2": "<sup>2</sup> ನನ್ನ ಮನಸ್ಸು ದೇವರಿಗಾಗಿ, ಚೈತನ್ಯ ಸ್ವರೂಪನಾದ ದೇವರಿಗಾಗಿ ಹಾರೈಸುತ್ತದೆ; ನಾನು ಯಾವಾಗ ಹೋಗಿ ದೇವರ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸೇರುವೆನೋ?",
			"3": "<sup>3</sup> ನಿನ್ನ ದೇವರು ಎಲ್ಲಿ ಎಂದು ಜನರು ಯಾವಾಗಲೂ ನನ್ನನ್ನು ಗೇಲಿ ಮಾಡುವದರಿಂದ ಹಗಲಿರುಳು ನನಗೆ ಕಣ್ಣೀರೇ ಆಹಾರವಾಯಿತು.",
			"4": "<sup>4</sup> ನಾನು ಭಜನೋತ್ಸವದಲ್ಲಿ ಸೇರಿ ಜನಸಮೂಹದೊಡನೆ ಹರ್ಷಧ್ವನಿಮಾಡುತ್ತಾ ಸ್ತೋತ್ರಪದಗಳನ್ನು ಹಾಡುತ್ತಾ ದೇವಾಲಯಕ್ಕೆ ಹೋಗುತ್ತಿದ್ದದ್ದನ್ನೇ ನೆನಪಿಗೆ ತಂದುಕೊಂಡು ನನ್ನೊಳಗೆ ನಾನೇ ಹಂಬಲಿಸುತ್ತಿರುವೆನು.",
			"5": "<sup>5</sup> ನನ್ನ ಮನವೇ, ನೀನು ಕುಗ್ಗಿಹೋಗಿರುವದೇನು? ಹೀಗೆ ವ್ಯಥೆಪಡುವದೇಕೆ? ದೇವರನ್ನು ನಿರೀಕ್ಷಿಸು; ಆತನೇ ನನಗೆ ರಕ್ಷಕನೂ ದೇವರೂ ಆಗಿದ್ದಾನೆ. ನಾನು ಇನ್ನೂ ಆತನನ್ನು ಸ್ತುತಿಸುತ್ತಿರುವೆನು.",
			"6": "<sup>6</sup> ನನ್ನ ದೇವರೇ, ನನ್ನ ಪ್ರಾಣವು ಕುಗ್ಗಿಹೋಗಿದೆ; ಆದದರಿಂದ ಯೊರ್ದನ್ ಹೊಳೆಯ ದೇಶ, ಹೆರ್ಮೊನ್ ಪರ್ವತ, ಮಿಸಾರ್ ಬೆಟ್ಟ ಇವುಗಳಲ್ಲಿರುವವನಾಗಿ ನಿನ್ನನ್ನು ಸ್ಮರಿಸುತ್ತೇನೆ.",
			"7": "<sup>7</sup> ನಿನ್ನ ಜಲಪಾತಗಳಿಂದುಂಟಾಗುವ ಮಹಾಘೋಷವು ಒಂದು ಪ್ರವಾಹವು ಮತ್ತೊಂದು ಪ್ರವಾಹವನ್ನು ಕೂಗುತ್ತದೋ ಎಂಬಂತಿರುವದು. ಹಾಗೆಯೇ ನೀನು ಅಲ್ಲಕಲ್ಲೋಲವಾದ [ದುಃಖ ಪ್ರವಾಹದ] ತೆರೆಗಳನ್ನು ನನ್ನ ತಲೆಯ ಮೇಲೆ ದಾಟಿಸಿದಿಯಲ್ಲಾ.",
			"8": "<sup>8</sup> ಯೆಹೋವನು ಹಗಲಿನಲ್ಲಿ ತನ್ನ ಪ್ರೀತಿಯನ್ನು ನನಗೆ ಅನುಗ್ರಹಿಸುವನು; ರಾತ್ರಿವೇಳೆಯಲ್ಲಿಯೂ ಆತನ ಕೀರ್ತನೆ ನನ್ನಲ್ಲಿರುವದು. ನನ್ನ ಜೀವಾಧಾರಕನಾದ ದೇವರನ್ನು ಪ್ರಾರ್ಥಿಸುವೆನು.",
			"9": "<sup>9</sup> ನಾನು ನನ್ನ ಶರಣನಿಗೆ - ದೇವರೇ, ಯಾಕೆ ನನ್ನನ್ನು ಮರೆತುಬಿಟ್ಟಿ? ನಾನು ಯಾಕೆ ಶತ್ರುಬಾಧೆಯಿಂದ ದುಃಖಿಸುತ್ತಾ ವಿಕಾರಿಯಾಗಿ ಅಲೆಯಬೇಕು ಎಂದು ಮೊರೆಯಿಡುವೆನು.",
			"10": "<sup>10</sup> ನನ್ನ ವಿರೋಧಿಗಳು - ನಿನ್ನ ದೇವರು ಎಲ್ಲಿ ಎಂದು ಯಾವಾಗಲೂ ನನ್ನನ್ನು ಗೇಲಿಮಾಡುವದರಿಂದ ನನ್ನ ಎಲುಬುಗಳೆಲ್ಲಾ ಮುರಿದ ಹಾಗಿವೆ.",
			"11": "<sup>11</sup> ನನ್ನ ಮನವೇ, ನೀನು ಕುಗ್ಗಿಹೋಗಿರುವದೇನು? ಹೀಗೆ ವ್ಯಥೆಪಡುವದೇಕೆ? ದೇವರನ್ನು ನಿರೀಕ್ಷಿಸು; ಆತನೇ ನನಗೆ ರಕ್ಷಕನೂ ದೇವರೂ ಆಗಿದ್ದಾನೆ; ನಾನು ಇನ್ನೂ ಆತನನ್ನು ಸ್ತುತಿಸುತ್ತಿರುವೆನು."
		},
		"43": {
			"1": "<sup>1</sup> ದೇವರೇ, ನ್ಯಾಯವನ್ನು ನಿರ್ಣಯಿಸು; ನನಗೋಸ್ಕರ ವ್ಯಾಜ್ಯವನ್ನು ನಡಿಸಿ ನಿರ್ದಯವಾದ ಜನಾಂಗದಿಂದ ತಪ್ಪಿಸು, ದುರಾಚಾರಿಗಳಾದ ಮೋಸಗಾರರಿಂದ ನನ್ನನ್ನು ಬಿಡಿಸು.",
			"2": "<sup>2</sup> ದೇವರೇ, ನೀನು ನನಗೆ ದುರ್ಗಸ್ಥಾನವಲ್ಲವೇ; ಯಾಕೆ ನನ್ನನ್ನು ತಳ್ಳಿಬಿಟ್ಟಿ? ನಾನು ಯಾಕೆ ಶತ್ರುಬಾಧೆಯಿಂದ ದುಃಖಿಸುತ್ತಾ ವಿಕಾರಿಯಾಗಿ ಅಲೆಯಬೇಕು?",
			"3": "<sup>3</sup> ನಿನ್ನ ಸತ್ಯಪ್ರಸನ್ನತೆಗಳನ್ನು [ದೂತರನ್ನೋ ಎಂಬಂತೆ] ಕಳುಹಿಸು; ಅವೇ ನಿನ್ನ ಪರಿಶುದ್ಧಪರ್ವತಕ್ಕೆ ನನ್ನನ್ನು ಕರೆದುಕೊಂಡು ಹೋಗಿ ನಿನ್ನ ನಿವಾಸಕ್ಕೆ ಸೇರಿಸಲಿ.",
			"4": "<sup>4</sup> ದೇವರೇ, ನನ್ನ ದೇವರೇ, ಆಗ ನಾನು ನಿನ್ನ ಯಜ್ಞವೇದಿಯ ಬಳಿಗೆ, ನನ್ನ ಆನಂದನಿಧಿಯಾಗಿರುವ ನಿನ್ನ ಹತ್ತಿರಕ್ಕೆ, ಸೇರಿ ಕಿನ್ನರಿಯನ್ನು ಬಾರಿಸುತ್ತಾ ನಿನ್ನನ್ನು ಕೊಂಡಾಡುವೆನು.",
			"5": "<sup>5</sup> ನನ್ನ ಮನವೇ, ನೀನು ಕುಗ್ಗಿಹೋಗಿರುವದೇನು? ಹೀಗೆ ವ್ಯಥೆಪಡುವದೇಕೆ? ದೇವರನ್ನು ನಿರೀಕ್ಷಿಸು; ಆತನೇ ನನಗೆ ರಕ್ಷಕನೂ ದೇವರೂ ಆಗಿದ್ದಾನೆ; ನಾನು ಇನ್ನೂ ಆತನನ್ನು ಸ್ತುತಿಸುತ್ತಿರುವೆನು."
		},
		"44": {
			"1": "<sup>1</sup> ದೇವರೇ, ಪೂರ್ವಕಾಲದಲ್ಲಿ ನಮ್ಮ ಪಿತೃಗಳ ದಿನದಲ್ಲಿ ನೀನು ನಡಿಸಿದ ಮಹತ್ಕಾರ್ಯಗಳ ವಿಷಯವನ್ನು ಕೇಳಿದ್ದೇವೆ; ಅವರೇ ನಮಗೆ ತಿಳಿಸಿದರು.",
			"2": "<sup>2</sup> ನಿನ್ನ ಹಸ್ತವೇ ಈ ದೇಶದಲ್ಲಿದ್ದ ಜನಾಂಗಗಳನ್ನು ಹೊರಡಿಸಿ ನಮ್ಮ ಪಿತೃಗಳನ್ನೇ ನೆಲೆಗೊಳ್ಳಿಸಿದೆ; ನೀನು ಆ ಅನ್ಯಜನಗಳನ್ನು ತೆಗೆದುಬಿಟ್ಟು ನಮ್ಮವರನ್ನು ಹಬ್ಬಿಸಿದಿ.",
			"3": "<sup>3</sup> ನಮ್ಮ ಪಿತೃಗಳಿಗೆ ಕತ್ತಿಯೇ ಈ ದೇಶವನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಡಲಿಲ್ಲ; ಅವರ ಭುಜಬಲವೇ ಅವರಿಗೆ ಜಯಕೊಡಲಿಲ್ಲ. ನಿನ್ನ ಭುಜಬಲ, ಹಸ್ತ, ಪ್ರಸನ್ನತೆ ಇವೇ ಅವರಿಗೆ ಜಯವನ್ನುಂಟುಮಾಡಿದವು; ನಿನ್ನ ಒಲುಮೆ ಅವರಿಗಿತ್ತಲ್ಲಾ.",
			"4": "<sup>4</sup> ದೇವರೇ, ನೀನೇ ನನ್ನ ಅರಸನು; ಯಾಕೋಬವಂಶಕ್ಕೆ ಜಯಪ್ರದನಾಗು.",
			"5": "<sup>5</sup> ನಿನ್ನ ಸಹಾಯದಿಂದಲೇ ವೈರಿಗಳನ್ನು ಕೆಡವಿಬಿಡುವೆವು; ನಿನ್ನ ನಾಮಧೇಯದ ಬಲದಿಂದ ಎದುರಾಳಿಗಳನ್ನು ತುಳಿದುಬಿಡುವೆವು.",
			"6": "<sup>6</sup> ನಾನು ಬಿಲ್ಲಿನಲ್ಲಿ ಭರವಸವಿಡುವದಿಲ್ಲ; ಕತ್ತಿಯು ನನ್ನನ್ನು ರಕ್ಷಿಸಲಾರದು.",
			"7": "<sup>7</sup> ಹಿಂಸಕರಿಂದ ಬಿಡಿಸಿದವನು ನೀನೇ; ನೀನೇ ಹಗೆಗಳಿಗೆ ಆಶಾಭಂಗಪಡಿಸಿದವನು.",
			"8": "<sup>8</sup> ದೇವರೇ ನಿನ್ನಲ್ಲಿಯೇ ಯಾವಾಗಲೂ ಹಿಗ್ಗುತ್ತಿದ್ದೇವೆ; ನಿನ್ನ ನಾಮವನ್ನೇ ಸದಾಕಾಲವೂ ಕೀರ್ತಿಸುವೆವು. ಸೆಲಾ.",
			"9": "<sup>9</sup> ಆದರೆ ಈಗ ನೀನು ಕೈಬಿಟ್ಟದ್ದರಿಂದ ಅವಮಾನಕ್ಕೆ ಒಳಗಾದೆವಲ್ಲಾ; ನಮ್ಮ ಸೈನ್ಯಗಳ ಸಂಗಡ ನೀನು ಹೊರಡಲಿಲ್ಲ.",
			"10": "<sup>10</sup> ನಾವು ಹಗೆಗಳಿಗೆ ಬೆನ್ನುಕೊಟ್ಟು ಓಡಿಹೋಗುವಂತೆ ಮಾಡಿದ್ದೀ; ವೈರಿಗಳು ನಮ್ಮ ಸೊತ್ತನ್ನು ಬೇಕಾದ ಹಾಗೆ ಸೂರೆಮಾಡುತ್ತಾರೆ.",
			"11": "<sup>11</sup> ಕುರಿಗಳನ್ನೋ ಎಂಬಂತೆ ನಮ್ಮನ್ನು ಘಾತುಕರಿಗೆ ಒಪ್ಪಿಸಿದ್ದೀ; ಜನಾಂಗಗಳಲ್ಲಿ ನಮ್ಮನ್ನು ಚದರಿಸಿಬಿಟ್ಟಿದ್ದೀ.",
			"12": "<sup>12</sup> ನೀನು ಯಾವ ಲಾಭವನ್ನೂ ಹೊಂದದೆ ನಿಷ್ಪ್ರಯೋಜನವಾಗಿ ನಿನ್ನ ಪ್ರಜೆಯನ್ನು ಮಾರಿಬಿಟ್ಟಿದ್ದೀ.",
			"13": "<sup>13</sup> ನಮ್ಮನ್ನು ನೆರೆಯವರ ನಿಂದೆಗೆ ಗುರಿಮಾಡಿದ್ದೀ; ಸುತ್ತಣ ಜನಾಂಗಗಳವರ ಪರಿಹಾಸ್ಯಕ್ಕೂ ಕುಚೋದ್ಯಕ್ಕೂ ಈಡಾದೆವು.",
			"14": "<sup>14</sup> ನಮ್ಮನ್ನು ಅನ್ಯದೇಶೀಯರ ಗಾದೆಗೆ ಆಸ್ಪದ ಮಾಡಿದ್ದೀ; ಅವರು ನಮ್ಮನ್ನು ನೋಡಿ ತಲೆ ಆಡಿಸುತ್ತಾರೆ.",
			"15": "<sup>15</sup> ದೂಷಕರ ನಿಂದಾವಚನಗಳಿಂದಲೂ ಮುಯ್ಯಿತೀರಿಸುವ ವೈರಿಗಳ ದೃಷ್ಟಿಯಿಂದಲೂ",
			"16": "<sup>16</sup> ನನಗುಂಟಾಗಿರುವ ಅವಮಾನವು ಯಾವಾಗಲೂ ನನ್ನ ಮುಂದೇ ಇದೆ; ನಾಚಿಕೆಯು ನನ್ನ ಮುಖವನ್ನು ಕವಿದದೆ.",
			"17": "<sup>17</sup> ನಾವು ನಿನ್ನನ್ನು ಮರೆಯದೆಯೂ ನಿನ್ನ ನಿಬಂಧನೆಗಳನ್ನು ಮೀರದೆಯೂ ಇದ್ದರೂ ಇದೆಲ್ಲಾ ನಮ್ಮ ಮೇಲೆ ಬಂದಿದೆಯಲ್ಲಾ!",
			"18": "<sup>18</sup> ನಮ್ಮ ಹೃದಯವು ಹಿಂದಿರುಗಲಿಲ್ಲ; ನಮ್ಮ ಕಾಲು ನಿನ್ನ ದಾರಿಯಿಂದ ಅಗಲಲಿಲ್ಲ.",
			"19": "<sup>19</sup> ನೀನು ನಮ್ಮನ್ನು ಪರಾಜಯಪಡಿಸಿ ನಮ್ಮ ದೇಶವನ್ನು ನರಿಗಳಿರುವ ಕಾಡನ್ನಾಗಿ ಮಾಡಿದ್ದೇಕೆ? ಕಾರ್ಗತ್ತಲು ನಮ್ಮನ್ನು ಕವಿಯುವಂತೆ ಮಾಡಿದ್ದೇಕೆ?",
			"20": "<sup>20</sup> ನಾವು ನಮ್ಮ ದೇವರ ಹೆಸರನ್ನು ಮರೆತು ಅನ್ಯದೇವತೆಗಳಿಗೆ ಕೈಯೆತ್ತಿದ್ದರೆ",
			"21": "<sup>21</sup> ಹೃದಯರಹಸ್ಯಗಳನ್ನು ಬಲ್ಲವನಾದ ದೇವರು ವಿಚಾರಿಸುತ್ತಿರಲಿಲ್ಲವೋ?",
			"22": "<sup>22</sup> ದೇವರೇ, ನಾವು ನಿನ್ನ ನಿಮಿತ್ತ ದಿನವೆಲ್ಲಾ ಕೊಲೆಗೆ ಗುರಿಯಾಗಿದ್ದೇವೆ; ಜನರು ನಮ್ಮನ್ನು ಕೊಯ್ಗುರಿಗಳಂತೆ ಎಣಿಸಿದ್ದಾರೆ.",
			"23": "<sup>23</sup> ಕರ್ತನೇ, ಯಾಕೆ ನಿದ್ರಿಸುತ್ತೀ? ಎಚ್ಚರವಾಗು; ಏಳು, ನಮ್ಮನ್ನು ಬಿಟ್ಟೇಬಿಡಬೇಡ.",
			"24": "<sup>24</sup> ಯಾಕೆ ವಿಮುಖನಾಗಿದ್ದೀ? ನಮಗಿರುವ ಬಾಧೆಯನ್ನೂ ಹಿಂಸೆಯನ್ನೂ ಯಾಕೆ ಲಕ್ಷಿಸುವದಿಲ್ಲ?",
			"25": "<sup>25</sup> ನಮ್ಮ ಪ್ರಾಣವು ಧೂಳಿನಲ್ಲಿ ಅಣಗಿಹೋಗಿದೆ; ಶರೀರವು ನೆಲಕ್ಕೆ ಹತ್ತಿಕೊಂಡಿದೆ.",
			"26": "<sup>26</sup> ಎದ್ದು ಬಂದು ಸಹಾಯಮಾಡು; ನಿನ್ನ ಕೃಪೆಯ ನಿಮಿತ್ತ ನಮ್ಮನ್ನು ವಿಮೋಚಿಸು."
		},
		"45": {
			"1": "<sup>1</sup> ಒಂದು ದಿವ್ಯ ವಿಷಯವನ್ನು ಹೇಳುವದಕ್ಕೆ ನನ್ನ ಹೃದಯವು ತವಕಪಡುತ್ತದೆ; ನಾನು ರಾಜನನ್ನು ಕುರಿತು ಈ ಪದ್ಯವನ್ನು ರಚಿಸುವೆನು. ನನ್ನ ನಾಲಿಗೆಯು ಒಳ್ಳೇ ಬರವಣಿಗಸ್ತನ ಲೇಖನಿಯಂತೆ ಸಿದ್ಧವಾಗಿದೆ.",
			"2": "<sup>2</sup> ನೀನು ಎಲ್ಲಾ ಮನುಷ್ಯರಿಗಿಂತ ಅತಿಸುಂದರನು; ನಿನ್ನ ಮಾತುಗಳು ಬಹಳ ಮಧುರ; ಇದರಿಂದಲೇ ದೈವಾನುಗ್ರಹವು ಯಾವಾಗಲೂ ನಿನ್ನ ಮೇಲಿದೆ ಎಂಬದು ನಿನ್ನ ಬಾಯ ಮಾತುಗಳಿಂದ ಸ್ಪಷ್ಟವಾಗುತ್ತದೆ.",
			"3": "<sup>3</sup> ಶೂರನೇ ಮಹಿಮಪ್ರಭಾವಗಳನ್ನು ಧರಿಸಿಕೊಂಡು ಸೊಂಟಕ್ಕೆ ಪಟ್ಟದ ಕತ್ತಿಯನ್ನು ಕಟ್ಟಿಕೋ.",
			"4": "<sup>4</sup> ಸತ್ಯತೆದೈನ್ಯನೀತಿಗಳನ್ನು ಸ್ಥಾಪಿಸುವದಕ್ಕಾಗಿ ಆಡಂಬರದಿಂದ ವಾಹನಾರೂಢನಾಗಿ ವಿಜಯೋತ್ಸವದೊಡನೆ ಹೊರಡೋಣವಾಗಲಿ. ನಿನ್ನ ಭುಜವೀರ್ಯದಿಂದ ಭಯಂಕರಕೃತ್ಯಗಳು ನಡೆಯುವವು.",
			"5": "<sup>5</sup> ನಿನ್ನ ಬಾಣಗಳು ಮಹಾತೀಕ್ಷ್ಣವಾಗಿರುವವು; ಅವು ರಾಜವಿರೋಧಿಗಳ ಎದೆಯನ್ನು ಭೇದಿಸುವವು; ಶತ್ರುಜನಾಂಗಗಳು ನಿನ್ನ ಪಾದದ ಕೆಳಗೆ ಬೀಳುವವು.",
			"6": "<sup>6</sup> ದೇವರೇ, ನಿನ್ನ ಸಿಂಹಾಸನವು ಯುಗಯುಗಾಂತರಗಳಲ್ಲಿಯೂ ಇರುವದು; ನಿನ್ನ ರಾಜದಂಡವು ನ್ಯಾಯಸ್ಥಾಪಕವಾದದ್ದೇ.",
			"7": "<sup>7</sup> ನೀನು ಧರ್ಮವನ್ನು ಪ್ರೀತಿಸಿದಿ, ಅಧರ್ಮವನ್ನು ದ್ವೇಷಿಸಿದಿ; ಆದದರಿಂದ ದೇವರು, ನಿನ್ನ ದೇವರೇ, ನಿನ್ನನ್ನು ನಿನ್ನ ಜೊತೆಗಾರರಿಗಿಂತ ಉನ್ನತಸ್ಥಾನಕ್ಕೆ ಏರಿಸಿ ಪರಮಾನಂದತೈಲದಿಂದ ಅಭಿಷೇಕಿಸಿದ್ದಾನೆ.",
			"8": "<sup>8</sup> ನಿನ್ನ ದಿವ್ಯಾಂಬರಗಳು ರಕ್ತಬೋಳ ಅಗರು ಚಂದನಗಳಿಂದ ಎಷ್ಟೋ ಸುವಾಸನೆಯುಳ್ಳವುಗಳಾಗಿವೆ; ಗಜದಂತಮಂದಿರಗಳೊಳಗೆ ಉತ್ಕೃಷ್ಟವಾದ್ಯಗಳು ನಿನ್ನನ್ನು ಆನಂದಗೊಳಿಸುವವು.",
			"9": "<sup>9</sup> ನಿನ್ನ ಸ್ತ್ರೀಪರಿವಾರದಲ್ಲಿ ರಾಜಕುಮಾರಿಯರೂ ಇದ್ದಾರೆ; ಪಟ್ಟದ ರಾಣಿಯು ಓಫಿರ್ ದೇಶದ ಬಂಗಾರದ ಆಭರಣಗಳಿಂದ ಭೂಷಿತಳಾಗಿ ನಿನ್ನ ಬಲಭಾಗದಲ್ಲಿ ನಿಂತಿರುವಳು.",
			"10": "<sup>10</sup> ಎಲೌ ಕುಮಾರಿಯೇ, ನನ್ನ ಮಾತನ್ನು ಕೇಳಿ ಆಲೋಚಿಸು; ಸ್ವಜನರನ್ನೂ ತವರುಮನೆಯನ್ನೂ ಮರೆತುಬಿಡು.",
			"11": "<sup>11</sup> ಆಗ ರಾಜನು ನಿನ್ನ ಲಾವಣ್ಯವನ್ನು ನೋಡಲಪೇಕ್ಷಿಸುವನು. ಆತನೇ ನಿನಗೆ ಒಡೆಯನು; ಆತನಿಗೆ ನಮಸ್ಕರಿಸು.",
			"12": "<sup>12</sup> ತೂರ್ ಸಂಸ್ಥಾನದವರು ಕಾಣಿಕೆಗಳಿಂದ ನಿನ್ನನ್ನು ಸನ್ಮಾನಿಸುವರು; ಪ್ರಜೆಗಳಲ್ಲಿ ಸ್ಥಿತಿವಂತರು ನಿನ್ನ ದಯೆಯನ್ನು ಕೋರುವರು.",
			"13": "<sup>13</sup> ಅಂತಃಪುರದಲ್ಲಿ ರಾಜಕುಮಾರಿಯು ಎಷ್ಟೋ ವೈಭವದಿಂದಿದ್ದಾಳೆ; ಆಕೆಯು ಜರತಾರಿಯ ವಸ್ತ್ರವನ್ನು ಧರಿಸಿಕೊಂಡಿದ್ದಾಳೆ.",
			"14": "<sup>14</sup> ಆಕೆಯು ಬೂಟೇದಾರೀ ಕಸೂತಿಕೆಲಸದ ವಸ್ತ್ರಗಳನ್ನು ಧರಿಸಿಕೊಂಡು ಸಖಿಯರಾದ ಕನ್ಯೆಯರ ಪರಿವಾರದೊಡನೆ ಅರಸನ ಬಳಿಗೆ ಬರುವಳು.",
			"15": "<sup>15</sup> ಅವರು ಸಂಭ್ರಮೋತ್ಸವದಿಂದ ಬಂದು ಅರಮನೆಯೊಳಕ್ಕೆ ಪ್ರವೇಶಿಸುವರು.",
			"16": "<sup>16</sup> [ಅರಸನೇ,] ವಂಶಪಾರಂಪರ್ಯವಾಗಿ ಬಂದ ಸ್ಥಾನದಲ್ಲಿರುವದಕ್ಕೆ ನಿನಗೆ ಮಕ್ಕಳು ಹುಟ್ಟುವರು; ಅವರನ್ನು ದೇಶದಲ್ಲೆಲ್ಲಾ ಅಧಿಕಾರಿಗಳನ್ನಾಗಿ ನೇಮಿಸುವಿ.",
			"17": "<sup>17</sup> ನಿನ್ನ ಹೆಸರು ಯಾವಾಗಲೂ ಜ್ಞಾಪಕದಲ್ಲಿರುವಂತೆ ನಾನು ಮಾಡುವೆನು; ಆದದರಿಂದ ಎಲ್ಲಾ ಜನಗಳು ನಿನ್ನನ್ನು ಯುಗಯುಗಾಂತರಗಳಲ್ಲಿಯೂ ಹೊಗಳುವರು."
		},
		"46": {
			"1": "<sup>1</sup> ದೇವರು ನಮಗೆ ಆಶ್ರಯದುರ್ಗವಾಗಿದ್ದಾನೆ; ಆತನು ಇಕ್ಕಟ್ಟಿನಲ್ಲಿ ನಮಗೆ ವಿಶೇಷ ಸಹಾಯಕನು.",
			"2": "<sup>2</sup> ಆದದರಿಂದ ಭೂಮಿಯು ಮಾರ್ಪಟ್ಟರೂ ಬೆಟ್ಟಗಳು ಸಮುದ್ರದಲ್ಲಿ ಮುಣುಗಿಹೋದರೂ ನಮಗೇನೂ ಭಯವಿಲ್ಲ.",
			"3": "<sup>3</sup> ಸಮುದ್ರವು ಘೋಷಿಸುತ್ತಾ ನೊರೆಯನ್ನು ಕಾರಿದರೇನು? ಅದರ ಅಲ್ಲಕಲ್ಲೋಲಗಳಿಂದ ಪರ್ವತಗಳು ಚಲಿಸಿದರೇನು? ಸೆಲಾ.",
			"4": "<sup>4</sup> ಒಂದು ನದಿ ಅದೆ; ಅದರ ಕಾಲುವೆಗಳು ಪರಾತ್ಪರನ ಪರಿಶುದ್ಧ ನಿವಾಸಸ್ಥಾನವಾಗಿರುವ ದೇವನಗರವನ್ನು ಸಂತೋಷಪಡಿಸುತ್ತವೆ.",
			"5": "<sup>5</sup> ದೇವರು ಇದರಲ್ಲಿದ್ದಾನೆ; ಇದಕ್ಕೆ ಚಲನವೇ ಇಲ್ಲ. ಉದಯಕಾಲದಲ್ಲಿಯೇ ದೇವರು ಇದರ ಸಹಾಯಕ್ಕೆ ಬರುವನು.",
			"6": "<sup>6</sup> ಜನಾಂಗಗಳು ಕಳವಳಗೊಂಡವು; ರಾಜ್ಯಗಳು ತತ್ತರಿಸಿದವು. ಆತನು ಗರ್ಜಿಸಲು ಭೂಲೋಕವೆಲ್ಲಾ ಕರಗಿಹೋಯಿತು.",
			"7": "<sup>7</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ನಮ್ಮ ಸಂಗಡ ಇದ್ದಾನೆ; ಯಾಕೋಬವಂಶದವರ ದೇವರು ನಮಗೆ ಆಶ್ರಯದುರ್ಗವಾಗಿದ್ದಾನೆ. ಸೆಲಾ.",
			"8": "<sup>8</sup> ಬನ್ನಿರಿ, ಯೆಹೋವನ ಕಾರ್ಯವನ್ನು ನೋಡಿರಿ; ಆತನು ಭೂಲೋಕದಲ್ಲಿ ಎಂಥಾ ಪ್ರಳಯವನ್ನು ನಡಿಸಿದ್ದಾನೆ.",
			"9": "<sup>9</sup> ಲೋಕದ ಎಲ್ಲಾ ಭಾಗದಲ್ಲೂ ಯುದ್ಧವನ್ನು ನಿಲ್ಲಿಸಿಬಿಟ್ಟಿದ್ದಾನೆ; ಬಿಲ್ಲುಗಳನ್ನೂ ಭಲ್ಲೆಯಗಳನ್ನೂ ಮುರಿದುಹಾಕಿದ್ದಾನೆ; ರಥಗಳನ್ನು ದಹಿಸಿಬಿಟ್ಟಿದ್ದಾನೆ.",
			"10": "<sup>10</sup> ಬಿಡಿರಿ, ನಾನೇ ದೇವರು; ಲೋಕದ ಸಮಸ್ತ ಜನಗಳಿಗೂ ಸರ್ವಾಧಿಪತಿಯು ನಾನೇ ಎಂದು ತಿಳಿದುಕೊಳ್ಳಿರಿ.",
			"11": "<sup>11</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ನಮ್ಮ ಸಂಗಡ ಇದ್ದಾನೆ; ಯಾಕೋಬವಂಶದವರ ದೇವರು ನಮಗೆ ಆಶ್ರಯದುರ್ಗವಾಗಿದ್ದಾನೆ. ಸೆಲಾ."
		},
		"47": {
			"1": "<sup>1</sup> ಸರ್ವಜನಾಂಗಗಳಿರಾ, ಚಪ್ಪಾಳೆ ಹೊಡೆಯಿರಿ; ಆರ್ಭಟದಿಂದ ದೇವರಿಗೆ ಜಯಧ್ವನಿ ಮಾಡಿರಿ.",
			"2": "<sup>2</sup> ಪರಾತ್ಪರನಾದ ಯೆಹೋವನು ಭಯಂಕರನಾಗಿದ್ದಾನೆ; ಆತನು ಭೂಲೋಕಕ್ಕೆಲ್ಲಾ ಅಧಿರಾಜನಾಗಿದ್ದಾನೆ.",
			"3": "<sup>3</sup> ಆತನು ಜನಾಂಗಗಳನ್ನು ನಮಗೆ ಅಧೀನಪಡಿಸಿ ಪರಕುಲದವರನ್ನು ನಮ್ಮ ಕಾಲಕೆಳಗೆ ಹಾಕಿದ್ದಾನೆ.",
			"4": "<sup>4</sup> ಆತನು ಯಾಕೋಬವಂಶದವರಾದ ನಮ್ಮನ್ನು ಪ್ರೀತಿಸಿ ನಾವು ಅಭಿಮಾನಿಸುವ ಸ್ವಾಸ್ತ್ಯವನ್ನು ನಿರ್ಣಯಿಸಿಕೊಟ್ಟಿದ್ದಾನೆ. ಸೆಲಾ.",
			"5": "<sup>5</sup> ದೇವರು ಜಯಘೋಷದಿಂದ ಏರಿದ್ದಾನೆ; ಯೆಹೋವನು ತುತೂರಿಯ ಧ್ವನಿಯೊಡನೆ ಆರೋಹಣಮಾಡಿದ್ದಾನೆ.",
			"6": "<sup>6</sup> ದೇವರನ್ನು ಸಂಕೀರ್ತಿಸಿರಿ, ಕೀರ್ತಿಸಿರಿ; ನಮ್ಮ ಅರಸನನ್ನು ಕೀರ್ತಿಸಿರಿ, ಕೀರ್ತಿಸಿರಿ.",
			"7": "<sup>7</sup> ದೇವರು ಭೂಲೋಕಕ್ಕೆಲ್ಲಾ ರಾಜನು; ಆತನನ್ನು ಜ್ಞಾನಯುಕ್ತರಾಗಿ ಕೀರ್ತಿಸಿರಿ.",
			"8": "<sup>8</sup> ದೇವರು ಸರ್ವಾಧಿಪತ್ಯವನ್ನು ವಹಿಸಿಕೊಂಡಿದ್ದಾನೆ; ತನ್ನ ಪರಿಶುದ್ಧ ಸಿಂಹಾಸನದಲ್ಲಿ ಕೂತಿದ್ದಾನೆ.",
			"9": "<sup>9</sup> ಜನಾಂಗಗಳಲ್ಲಿ ಶ್ರೇಷ್ಠರಾದವರು ಅಬ್ರಹಾಮನ ದೇವರ ಪ್ರಜೆಯಾಗಿರುವದಕ್ಕೆ ಕೂಡಿಬರುತ್ತಾರೆ; ಯಾಕಂದರೆ ಭೂಪಾಲಕರೆಲ್ಲಾ ದೇವರಿಗೇ ಅಧೀನರಾಗಿದ್ದಾರೆ. ಆತನೇ ಸರ್ವೋನ್ನತನು."
		},
		"48": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಮಹೋನ್ನತನು; ನಮ್ಮ ದೇವರು ತನ್ನ ಪರಿಶುದ್ಧ ಪರ್ವತನಗರದಲ್ಲಿ ಸರ್ವಸ್ತುತಿಪಾತ್ರನಾಗಿದ್ದಾನೆ.",
			"2": "<sup>2</sup> ರಾಜಾಧಿರಾಜನ ಪಟ್ಟಣವಿರುವ ಚೀಯೋನ್ ಪರ್ವತವು ಉತ್ತರದಿಕ್ಕಿನಲ್ಲಿ ಉನ್ನತವಾಗಿಯೂ ರಮ್ಯವಾಗಿಯೂ ಭೂಲೋಕದಲ್ಲೆಲ್ಲಾ ಕಂಗೊಳಿಸುತ್ತಿರುವದು.",
			"3": "<sup>3</sup> ದೇವರು ಅದರ ಕೊತ್ತಲುಗಳಲ್ಲಿ ತಾನೇ ಭದ್ರವಾದ ಬುರುಜೆಂದು ಕೀರ್ತಿಪಡೆದನು.",
			"4": "<sup>4</sup> ರಾಜರು ಕೂಡಿಕೊಂಡರು; ಅವರು ಒಗ್ಗಟ್ಟಾಗಿ ಬಂದರು.",
			"5": "<sup>5</sup> ನೋಡಿ ಬೆರಗಾದರು; ತಲ್ಲಣಿಸಿ ಓಡಿ ಹೋದರು.",
			"6": "<sup>6</sup> ಕಂಪನವೂ ಹೆರುವವಳಂತೆ ವೇದನೆಯೂ ಅವರಿಗುಂಟಾಯಿತು.",
			"7": "<sup>7</sup> ದೇವರೇ, ನೀನು ಬಿರುಗಾಳಿಯಿಂದ ತಾರ್ಷಿಷ್ ದೇಶದ ದೊಡ್ಡ ದೊಡ್ಡ ಹಡಗುಗಳನ್ನು ಒಡೆದುಬಿಟ್ಟಂತಾಯಿತು.",
			"8": "<sup>8</sup> ನಾವು ಕಿವಿಯಿಂದ ಕೇಳಿದಂತೆಯೇ ಈಗ ಕಣ್ಣಾರೆ ಕಂಡೆವು; ನಮ್ಮ ದೇವರೂ ಸೇನಾಧೀಶನೂ ಆಗಿರುವ ಯೆಹೋವನ ಸಂಸ್ಥಾನದಲ್ಲಿ ಆತನ ಮಹತ್ತನ್ನು ನೋಡಿದ್ದೇವೆ.",
			"9": "<sup>9</sup> ದೇವರು ಅದನ್ನು ಶಾಶ್ವತವಾಗಿಯೇ ಸ್ಥಾಪಿಸುವನು. ಸೆಲಾ.",
			"10": "<sup>10</sup> ದೇವರೇ, ನಾವು ನಿನ್ನ ಆಲಯದಲ್ಲಿ ನಿನ್ನ ಪ್ರೀತಿಯನ್ನು ಸ್ಮರಿಸುತ್ತೇವೆ; ದೇವರೇ, ನಿನ್ನ ನಾಮದ ಘನತೆಗೆ ತಕ್ಕಂತೆಯೇ ಲೋಕವೆಲ್ಲಾ ನಿನ್ನನ್ನು ಹೊಗಳುವದು. ನಿನ್ನ ಬಲಗೈ ನೀತಿಯನ್ನು ನೆರವೇರಿಸಿತು.",
			"11": "<sup>11</sup> ಚೀಯೋನ್ ಪಟ್ಟಣದವರು ಹರ್ಷಿಸಲಿ; ನೀನು ನ್ಯಾಯವನ್ನು ನಿರ್ಣಯಿಸಿದ್ದರಿಂದ ಯೆಹೂದಸೀಮೆಯ ನಿವಾಸಿಗಳು ಆನಂದಪಡಲಿ.",
			"12": "<sup>12</sup> ಚೀಯೋನ್ ಸಂಸ್ಥಾನದ ಸುತ್ತಲು ಸಂಚರಿಸಿ ಅದರ ಬುರುಜುಗಳನ್ನು ಲೆಕ್ಕಿಸಿರಿ.",
			"13": "<sup>13</sup> ಅದರ ಪ್ರಾಕಾರಗಳನ್ನು ಚೆನ್ನಾಗಿ ನೋಡಿರಿ; ಅದರ ಕೊತ್ತಲುಗಳನ್ನು ಸೂಕ್ಷ್ಮವಾಗಿ ಗಮನಿಸಿರಿ.",
			"14": "<sup>14</sup> ಆಗ ನೀವು ನಿಮ್ಮ ಮುಂದಣ ಸಂತತಿಯವರಿಗೆ - ದೇವರು ಇಂಥವನು; ಆತನು ಯುಗಯುಗಾಂತರಗಳಲ್ಲಿಯೂ ನಮ್ಮ ದೇವರು, ನಿರಂತರವೂ ನಮ್ಮನ್ನು ನಡಿಸುವವನಾಗಿದ್ದಾನೆ ಎಂದು ತಿಳಿಸುವಿರಿ."
		},
		"49": {
			"1": "<sup>1</sup> ಸಕಲ ದೇಶಗಳವರೇ, ಕೇಳಿರಿ; ಭೂಲೋಕದ ನಿವಾಸಿಗಳಿರಾ, ಕಿವಿಗೊಡಿರಿ.",
			"2": "<sup>2</sup> ಜನಗಳೇ ಜನಾಧಿಪತಿಗಳೇ, ಬಡವರೇ ಬಲ್ಲಿದರೇ, ನೀವೆಲ್ಲರೂ ಒಂದಾಗಿ ಬಂದು ಆಲೈಸಿರಿ.",
			"3": "<sup>3</sup> ನನ್ನ ಬಾಯಿ ಸುಜ್ಞಾನವನ್ನು ಬೋಧಿಸುವದು; ನನ್ನ ಹೃದಯವು ವಿವೇಕವನ್ನು ಫಲಿಸುವದು.",
			"4": "<sup>4</sup> ನಾನು ಧರ್ಮರಹಸ್ಯವನ್ನು ಕಿವಿಗೊಟ್ಟು ಕೇಳಿ ಕಿನ್ನರಿಯನ್ನು ಬಾರಿಸುತ್ತಾ ಅದರ ಗೂಡಾರ್ಥವನ್ನು ಪ್ರಕಟಿಸುವೆನು.",
			"5": "<sup>5</sup> ಆಪತ್ತಿನಲ್ಲಿ ಯಾಕೆ ಭಯಪಡಬೇಕು? ದ್ವೇಷಿಗಳು ಮೋಸದಿಂದ ಸುತ್ತಿಕೊಂಡಿರುವಾಗ ನಾನು ಹೆದರುವದೇಕೆ?",
			"6": "<sup>6</sup> ಅವರು ತಮ್ಮ ಐಶ್ವರ್ಯವನ್ನೇ ನಂಬಿದ್ದಾರೆ; ತಾವು ಬಹಳ ಆಸ್ತಿವಂತರೆಂದು ಉಬ್ಬಿದ್ದಾರೆ.",
			"7": "<sup>7</sup> ಆದರೆ ಯಾವನಾದರೂ ತನ್ನ ಸಹೋದರನು ಸಮಾಧಿಯಲ್ಲಿ ಸೇರದೆ ಶಾಶ್ವತವಾಗಿ ಬದುಕಿರುವದಕ್ಕಾಗಿ",
			"8": "<sup>8</sup> ದೇವರಿಗೆ ಈಡನ್ನು ಕೊಟ್ಟು ಅವನ ಪ್ರಾಣವನ್ನು ಬಿಡಿಸಲಾರನು.",
			"9": "<sup>9</sup> ಮರಣವನ್ನು ತಪ್ಪಿಸಿಕೊಳ್ಳುವದಕ್ಕೆ ಎಷ್ಟು ಹಣಕೊಟ್ಟರೂ ಸಾಲುವದೇ ಇಲ್ಲ; ಅಂಥ ಪ್ರಯತ್ನವನ್ನು ನಿಷ್ಫಲವೆಂದು ಬಿಟ್ಟೇಬಿಡಬೇಕು.",
			"10": "<sup>10</sup> ಸುಜ್ಞಾನಿಗಳು ಸಾಯುವದನ್ನು ನೋಡುತ್ತೇವಲ್ಲಾ; ಹಾಗೆಯೇ ಪಶುಗಳಂತಿರುವ ಜ್ಞಾನಹೀನರೂ ನಾಶವಾಗುತ್ತಾರೆ. ಅವರು ನಂಬಿದ್ದ ಆಸ್ತಿಯು ಇತರರ ಪಾಲಾಗುತ್ತದೆ.",
			"11": "<sup>11</sup> ಯಾರು ವಿಸ್ತಾರವಾದ ಭೂಸ್ಥಿತಿಗಳಿಗೆ ತಮ್ಮ ಹೆಸರುಗಳನ್ನು ಕೊಟ್ಟಿದ್ದಾರೋ ಅಂಥವರಿಗೂ ಸಮಾಧಿಯೇ ಶಾಶ್ವತಮಂದಿರವು; ಅದೇ ಅವರ ನಿತ್ಯನಿವಾಸಸ್ಥಾನ.",
			"12": "<sup>12</sup> ಮನುಷ್ಯನು ಎಷ್ಟು ಘನವಾದ ಪದವಿಯಲ್ಲಿದ್ದರೂ ಸ್ಥಿರವಿಲ್ಲ; ಪಶುಗಳಂತೆಯೇ ಇಲ್ಲವಾಗುತ್ತಾನೆ.",
			"13": "<sup>13</sup> ಮೂರ್ಖರಿಗೂ ಅವರ ಮಾತಿನಂತೆ ನಡೆಯುವವರಿಗೂ ಇದೇ ಗತಿ. ಸೆಲಾ.",
			"14": "<sup>14</sup> ಅವರು ಕುರಿಗಳಂತೆ ಪಾತಾಳದಲ್ಲಿ ದಟ್ಟವಾಗಿ ಸೇರಿಸಲ್ಪಡುವರು; ಮೃತ್ಯುವೇ ಅವರ ಪಾಲಕನು. ಅವರಿಗೆ ನಿವಾಸವಿಲ್ಲದ ಹಾಗೆ ಪಾತಾಳವು ಅವರ ರೂಪವನ್ನು ನಾಶಮಾಡುವದು; ಉದಯಕಾಲದಲ್ಲಿ ಯಥಾರ್ಥರು ಅವರ ಮೇಲೆ ದೊರೆತನ ನಡಿಸುವರು.",
			"15": "<sup>15</sup> ಆದರೆ ದೇವರು ನನ್ನ ಪ್ರಾಣವನ್ನು ಮೃತ್ಯುಹಸ್ತದಿಂದ ತಪ್ಪಿಸಿ ಸ್ವೀಕಾರ ಮಾಡುವನು. ಸೆಲಾ.",
			"16": "<sup>16</sup> ಒಬ್ಬನ ಐಶ್ವರ್ಯವೂ ಗೃಹವೈಭವವೂ ವೃದ್ಧಿಯಾದಾಗ ಕಳವಳಿಸಬೇಡ.",
			"17": "<sup>17</sup> ಅವನು ಸಾಯುವಾಗ ಏನೂ ತೆಗೆದುಕೊಂಡು ಹೋಗಲಾರನಷ್ಟೆ; ಅವನ ವೈಭವವು ಅವನೊಡನೆ ಹೋಗುವದಿಲ್ಲ.",
			"18": "<sup>18</sup> ಸಿರಿ ಬಂದಾಗ ಹೆರರ ಹೊಗಳಿಕೆ ತಪ್ಪದು ಎಂಬಂತೆ ಅವನು ಜೀವಮಾನದಲ್ಲಿ ಆತ್ಮಸ್ತುತಿಯಿಂದಲೂ ಜನಸ್ತುತಿಯಿಂದಲೂ ಕೂಡಿದವನಾದರೂ",
			"19": "<sup>19</sup> ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಿ ಅವರಂತೆಯೇ ಎಂದಿಗೂ ಬೆಳಕನ್ನು ಕಾಣದೆ ಇರುವನು.",
			"20": "<sup>20</sup> ವಿವೇಕಹೀನ ಮನುಷ್ಯನು ಎಷ್ಟು ಘನವಾದ ಪದವಿಯಲ್ಲಿದ್ದರೂ ಪಶುಗಳಂತೆಯೇ ಇಲ್ಲವಾಗುತ್ತಾನೆ."
		},
		"50": {
			"1": "<sup>1</sup> ದೇವದೇವನಾದ ಯೆಹೋವನು ಪೂರ್ವದಿಂದ ಪಶ್ಚಿಮದಿಕ್ಕಿನವರೆಗೂ ಇರುವ ಭೂನಿವಾಸಿಗಳೆಲ್ಲರನ್ನೂ ತನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಬರಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸುತ್ತಾನೆ.",
			"2": "<sup>2</sup> ಅತ್ಯಂತ ರಮಣೀಯವಾದ ಚೀಯೋನಿನಲ್ಲಿರುವ ದೇವರು ಪ್ರಕಾಶಿಸುತ್ತಾನೆ.",
			"3": "<sup>3</sup> ನಮ್ಮ ದೇವರು ಪ್ರತ್ಯಕ್ಷನಾಗುವನು, ಎಷ್ಟು ಮಾತ್ರವೂ ಸುಮ್ಮನಿರುವದಿಲ್ಲ; ಆತನ ಮುಂಭಾಗದಲ್ಲಿ ಬೆಂಕಿ ಪ್ರಜ್ವಲಿಸುತ್ತದೆ; ಆತನ ಸುತ್ತಲು ಬಿರುಗಾಳಿ ಬೀಸುತ್ತದೆ.",
			"4": "<sup>4</sup> ಆತನು ತನ್ನ ಪ್ರಜೆಗಳ ನ್ಯಾಯವಿಚಾರಣೆಯಲ್ಲಿ ಭೂಮ್ಯಾಕಾಶಗಳನ್ನು ಸಾಕ್ಷಿಗಳಾಗಿ ನೇಮಿಸುತ್ತಾನೆ.",
			"5": "<sup>5</sup> ನನ್ನೊಡನೆ ಯಜ್ಞದ ಮೂಲಕ ಒಡಂಬಡಿಕೆಯನ್ನು ಮಾಡಿಕೊಂಡ ನನ್ನ ಭಕ್ತರನ್ನು ನನ್ನ ಬಳಿಗೆ ಕರಕೊಂಡು ಬನ್ನಿರಿ ಎಂದು ಆಜ್ಞಾಪಿಸುತ್ತಾನೆ.",
			"6": "<sup>6</sup> ದೇವರು ತಾನೇ ನ್ಯಾಯಾಧಿಪತಿ; ಆತನು ನೀತಿಸ್ವರೂಪನೆಂದು ಆಕಾಶಮಂಡಲವು ಘೋಷಿಸುತ್ತದೆ. ಸೆಲಾ.",
			"7": "<sup>7</sup> [ಆತನು ಆಜ್ಞಾಪಿಸುವದೇನಂದರೆ - ] ಪ್ರಜೆಗಳಿರಾ, ಇಸ್ರಾಯೇಲ್ಯರೇ, ದೇವರಾದ ನಾನೇ ನಿಮ್ಮ ದೇವರು; ನಿಮಗೆ ಖಂಡಿತವಾಗಿ ಹೇಳುತ್ತೇನೆ, ಕೇಳಿರಿ.",
			"8": "<sup>8</sup> ನಾನು ನಿಮ್ಮ ಯಜ್ಞಗಳ ವಿಷಯದಲ್ಲಿ ತಪ್ಪೆಣಿಸುವದಿಲ್ಲ; ಸರ್ವಾಂಗಹೋಮಗಳನ್ನು ನೀವು ನಿತ್ಯವೂ ನನಗೆ ಸಮರ್ಪಿಸುತ್ತೀರಲ್ಲಾ.",
			"9": "<sup>9</sup> ಆದರೂ ನಿಮ್ಮ ಮನೆಗಳಿಂದ ನನಗೆ ಹೋರಿ ಬೇಕಾಗಿಲ್ಲ; ನಿಮ್ಮ ದೊಡ್ಡಿಗಳಿಂದ ಹೋತ ಬೇಕಿಲ್ಲ.",
			"10": "<sup>10</sup> ಕಾಡಿನಲ್ಲಿರುವ ಸರ್ವಮೃಗಗಳೂ ಗುಡ್ಡಗಳಲ್ಲಿರುವ ಸಾವಿರಾರು ಪಶುಗಳೂ ನನ್ನವೇ.",
			"11": "<sup>11</sup> ಬೆಟ್ಟಗಳ ಪಕ್ಷಿಗಳನ್ನೆಲ್ಲಾ ಬಲ್ಲೆ; ಅಡವಿಯ ಜೀವಜಂತುಗಳೆಲ್ಲಾ ನನಗೆ ಗೊತ್ತುಂಟು.",
			"12": "<sup>12</sup> ನನಗೆ ಹಸಿವೆಯಿದ್ದರೆ ನಿಮಗೆ ತಿಳಿಸುವದಿಲ್ಲ; ಲೋಕವೂ ಅದರಲ್ಲಿರುವದೆಲ್ಲವೂ ನನ್ನದಲ್ಲವೇ.",
			"13": "<sup>13</sup> ನಾನು ಹೋರಿಗಳ ಮಾಂಸವನ್ನು ತಿನ್ನುವದೂ ಹೋತಗಳ ರಕ್ತವನ್ನು ಕುಡಿಯುವದೂ ಉಂಟೇ?",
			"14": "<sup>14</sup> ಸ್ತುತಿಯಜ್ಞವನ್ನೇ ದೇವರಿಗೆ ಸಮರ್ಪಿಸಿರಿ; ಮಾಡಿದ ಹರಕೆಗಳನ್ನು ಪರಾತ್ಪರನಿಗೆ ಸಲ್ಲಿಸಿರಿ.",
			"15": "<sup>15</sup> ಕಷ್ಟಕಾಲದಲ್ಲಿ ನನಗೆ ಮೊರೆಯಿಡಿರಿ; ಬಿಡಿಸುವೆನು; ಆಗ ನನ್ನನ್ನು ಕೊಂಡಾಡುವಿರಿ.",
			"16": "<sup>16</sup> ದುಷ್ಟರಿಗಾದರೋ ದೇವರು ಹೇಳುವದೇನಂದರೆ - ನನ್ನ ವಿಧಿಗಳನ್ನು ಪಠಿಸುವದಕ್ಕೆ ನಿಮಗೇನು ಬಾಧ್ಯತೆ ಉಂಟು? ನನ್ನ ನಿಬಂಧನೆಯನ್ನು ಉಚ್ಚರಿಸುವದು ನಿಮಗೇನು ಕೆಲಸ?",
			"17": "<sup>17</sup> ನೀವೋ ನನ್ನ ಸುಶಿಕ್ಷಣವನ್ನು ಹಗೆ ಮಾಡುತ್ತೀರಲ್ಲಾ; ನನ್ನ ಆಜ್ಞೆಯನ್ನು ಉಲ್ಲಂಘಿಸುತ್ತೀರಿ.",
			"18": "<sup>18</sup> ನೀವು ಚೋರರೊಡನೆ ಸಂತೋಷದಿಂದ ಸೇರುತ್ತೀರಿ; ಜಾರರ ಒಡನಾಟವೇ ನಿಮಗೆ ಇಷ್ಟ.",
			"19": "<sup>19</sup> ನಿಮ್ಮ ಬಾಯನ್ನು ಕೇಡಿಗೆ ಒಪ್ಪಿಸುತ್ತೀರಿ; ನಾಲಿಗೆಯು ಮೋಸವನ್ನು ನೇಯುತ್ತದೆ;",
			"20": "<sup>20</sup> ಸಹೋದರನನ್ನು ಕ್ಷುದ್ರಮಾಡುತ್ತಾ ಕೂತುಕೊಳ್ಳುತ್ತೀರಿ; ಒಡಹುಟ್ಟಿದವರನ್ನು ದೂರುತ್ತೀರಿ.",
			"21": "<sup>21</sup> ನೀವು ಹೀಗೆ ಮಾಡಿದರೂ ನಾನು ಸುಮ್ಮನೆ ಇದ್ದದರಿಂದ ದೇವರೂ ನಮ್ಮಂಥವನೇ ಎಂದು ನೆನಸಿಕೊಂಡಿರಿ. ಈಗಲಾದರೋ ನಾನು ಎಲ್ಲವನ್ನು ನಿಮ್ಮ ಮುಂದೆ ವಿವರಿಸಿ ನಿಮ್ಮನ್ನು ಅಪರಾಧಿಗಳೆಂದು ಸ್ಥಾಪಿಸುವೆನು.",
			"22": "<sup>22</sup> ದೇವರನ್ನು ಬಿಟ್ಟವರೇ, ಇದನ್ನು ಮನಸ್ಸಿಗೆ ತಂದುಕೊಳ್ಳಿರಿ; ಇಲ್ಲವಾದರೆ ನಿಮ್ಮನ್ನು ಹರಿದುಬಿಟ್ಟೇನು. ನಿಮ್ಮನ್ನು ತಪ್ಪಿಸುವವರು ಯಾರೂ ಇರುವದಿಲ್ಲ.",
			"23": "<sup>23</sup> ಯಾರು ಸ್ತುತಿಯಜ್ಞವನ್ನು ಸಮರ್ಪಿಸುತ್ತಾರೋ ಅವರೇ ನನ್ನನ್ನು ಗೌರವಿಸುವವರು; ತಮ್ಮ ನಡತೆಯನ್ನು ಸರಿಪಡಿಸಿಕೊಳ್ಳುವವರಿಗೆ ನನ್ನ ವಿಶೇಷವಾದ ರಕ್ಷಣೆಯನ್ನು ತೋರಿಸುವೆನು."
		},
		"51": {
			"1": "<sup>1</sup> ಪ್ರೀತಿಸ್ವರೂಪನಾದ ದೇವರೇ, ನನ್ನನ್ನು ಕರುಣಿಸು; ಕರುಣಾನಿಧಿಯೇ, ನನ್ನ ದ್ರೋಹವನ್ನೆಲ್ಲಾ ಅಳಿಸಿಬಿಡು.",
			"2": "<sup>2</sup> ನನ್ನ ಪಾಪವನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ತೊಳೆದುಬಿಡು; ನನ್ನ ದೋಷವನ್ನು ಪರಿಹರಿಸಿ ನನ್ನನ್ನು ಶುದ್ಧಗೊಳಿಸು.",
			"3": "<sup>3</sup> ನಾನು ದ್ರೋಹಿ ಎಂದು ನಾನೇ ಒಪ್ಪಿಕೊಂಡಿದ್ದೇನೆ; ನನ್ನ ಪಾಪವು ಯಾವಾಗಲೂ ನನ್ನ ಮುಂದೆ ಇದೆ.",
			"4": "<sup>4</sup> ನಿನಗೇ ಕೇವಲ ನಿನಗೇ ತಪ್ಪುಮಾಡಿದ್ದೇನೆ; ನಿನ್ನ ದೃಷ್ಟಿಗೆ ಕೆಟ್ಟದ್ದಾಗಿರುವದನ್ನೇ ಮಾಡಿದ್ದೇನೆ. ನಿನ್ನ ನಿರ್ಣಯ ನ್ಯಾಯವಾಗಿಯೂ ನಿನ್ನ ತೀರ್ಪು ನಿಷ್ಕಳಂಕವಾಗಿಯೂ ಇರುತ್ತದಲ್ಲಾ.",
			"5": "<sup>5</sup> ಹುಟ್ಟಿದಂದಿನಿಂದ ನಾನು ಪಾಪಿಯೇ; ಮಾತೃಗರ್ಭವನ್ನು ಹೊಂದಿದ ದಿನದಿಂದ ದ್ರೋಹಿಯೇ.",
			"6": "<sup>6</sup> ಯಥಾರ್ಥಚಿತ್ತವೇ ನಿನಗೆ ಸಂತೋಷ; ಸುಜ್ಞಾನದ ರಹಸ್ಯಗಳನ್ನು ನನಗೆ ತಿಳಿಯಪಡಿಸು.",
			"7": "<sup>7</sup> ಹಿಸ್ಸೋಪ್ ಗಿಡದ ಬರಲಿನಿಂದ ನೀರನ್ನು ಚಿಮುಕಿಸಿಯೋ ಎಂಬಂತೆ ನನ್ನ ಅಶುದ್ಧತ್ವವನ್ನು ತೆಗೆದುಬಿಡು, ಆಗ ಶುದ್ಧನಾಗಿರುವೆನು; ನನ್ನನ್ನು ತೊಳೆ, ಆಗ ಹಿಮಕ್ಕಿಂತಲೂ ಬೆಳ್ಳಗಾಗುವೆನು.",
			"8": "<sup>8</sup> ನನ್ನಲ್ಲಿ ಸಂಭ್ರಮೋತ್ಸವದ ಧ್ವನಿ ಉಂಟಾಗುವಂತೆ ಮಾಡು; ಆಗ ನೀನು ಜಜ್ಜಿದ ಎಲುಬುಗಳು ಆನಂದ ಪಡುವವು.",
			"9": "<sup>9</sup> ನನ್ನ ದೋಷಕ್ಕೆ ವಿಮುಖನಾಗು; ನನ್ನ ಪಾಪಗಳನ್ನೆಲ್ಲಾ ಅಳಿಸಿಬಿಡು.",
			"10": "<sup>10</sup> ದೇವರೇ, ನನ್ನಲ್ಲಿ ಶುದ್ಧಹೃದಯವನ್ನು ನಿರ್ಮಿಸು; ನನಗೆ ಸ್ಥಿರಚಿತ್ತವನ್ನು ಅನುಗ್ರಹಿಸಿ ನನ್ನನ್ನು ನೂತನ ಪಡಿಸು.",
			"11": "<sup>11</sup> ನಿನ್ನ ಸನ್ನಿಧಿಯಿಂದ ನನ್ನನ್ನು ತಳ್ಳಬೇಡ; ನಿನ್ನ ಪರಿಶುದ್ಧಾತ್ಮವನ್ನು ನನ್ನಿಂದ ತೆಗೆಯಬೇಡ.",
			"12": "<sup>12</sup> ನಿನ್ನ ರಕ್ಷಣಾನಂದವನ್ನು ನಾನು ತಿರಿಗಿ ಅನುಭವಿಸುವಂತೆ ಮಾಡು; ನನ್ನಲ್ಲಿ ಸಿದ್ಧಮನಸ್ಸನ್ನು ಹುಟ್ಟಿಸಿ ನನಗೆ ಆಧಾರನಾಗು.",
			"13": "<sup>13</sup> ಆಗ ನಿನ್ನ ಮಾರ್ಗವನ್ನು ದ್ರೋಹಿಗಳಿಗೆ ಬೋಧಿಸುವೆನು; ಪಾಪಿಗಳು ನಿನ್ನ ಕಡೆಗೆ ತಿರುಗಿಕೊಳ್ಳುವರು.",
			"14": "<sup>14</sup> ದೇವರೇ, ನನ್ನ ರಕ್ಷಣಕರ್ತನೇ, ಪ್ರಾಣಹತ್ಯದ ಅಪರಾಧದಿಂದ ನನ್ನನ್ನು ಬಿಡಿಸು; ಆಗ ನನ್ನ ನಾಲಿಗೆಯು ಉತ್ಸಾಹದಿಂದ ನಿನ್ನ ಸತ್ಯಸಂಧತೆಯನ್ನು ಕೊಂಡಾಡುವದು.",
			"15": "<sup>15</sup> ಕರ್ತನೇ, ನನ್ನ ಬಾಯಿ ನಿನ್ನನ್ನು ಸ್ತೋತ್ರ ಮಾಡುವಂತೆ ನನ್ನ ತುಟಿಗಳನ್ನು ತೆರೆಯಮಾಡು.",
			"16": "<sup>16</sup> ನಿನಗೆ ಯಜ್ಞಗಳಲ್ಲಿ ಅಪೇಕ್ಷೆಯಿಲ್ಲ, ಇದ್ದರೆ ಸಮರ್ಪಿಸೇನು; ಸರ್ವಾಂಗಹೋಮಗಳಲ್ಲಿ ನಿನಗೆ ಸಂತೋಷವಿಲ್ಲ.",
			"17": "<sup>17</sup> ಕುಗ್ಗಿದ ಮನಸ್ಸೇ ದೇವರಿಗೆ ಇಷ್ಟಯಜ್ಞ; ದೇವರೇ, ಪಶ್ಚಾತ್ತಾಪದಿಂದ ಜಜ್ಜಿಹೋದ ಮನಸ್ಸನ್ನು ನೀನು ತಿರಸ್ಕರಿಸುವದಿಲ್ಲ.",
			"18": "<sup>18</sup> ಚೀಯೋನ್ ಪಟ್ಟಣವನ್ನು ಕಟಾಕ್ಷಿಸಿ ಅದಕ್ಕೆ ಶುಭವನ್ನುಂಟುಮಾಡು; ಯೆರೂಸಲೇಮಿನ ಪೌಳಿಗೋಡೆಯನ್ನು ಕಟ್ಟಿಸು.",
			"19": "<sup>19</sup> ಜನರು ನ್ಯಾಯವಾದ ಯಜ್ಞಗಳನ್ನೂ ಸರ್ವಾಂಗ ಹೋಮಗಳನ್ನೂ ಸಮರ್ಪಿಸಿ ನಿನಗೆ ಸಂತೋಷವನ್ನುಂಟುಮಾಡುವರು. ನಿನ್ನ ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಹೋರಿಗಳನ್ನು ಸಮರ್ಪಿಸುವರು."
		},
		"52": {
			"1": "<sup>1</sup> ದುಷ್ಟಾಧಿಕಾರಿಯೇ, ನೀನು ಕೆಡುಕು ಮಾಡಿ ಹಿಗ್ಗುವದೇನು? ದೇವರ ಕೃಪೆಯು ಯಾವಾಗಲೂ ಇರುವದು.",
			"2": "<sup>2</sup> ಮೋಸಗಾರನೇ, ನಿನ್ನ ನಾಲಿಗೆಯು ಹದವಾದ ಕ್ಷೌರಕತ್ತಿಯಂತೆ ಅಪಾಯಗಳನ್ನೇ ಕಲ್ಪಿಸುತ್ತದೆ.",
			"3": "<sup>3</sup> ಉಪಕಾರಕ್ಕಿಂತಲೂ ಅಪಕಾರವೇ ನಿನಗೆ ಇಷ್ಟ; ನೀತಿಯನ್ನು ಬಿಟ್ಟು ಸುಳ್ಳನ್ನು ಸ್ಥಾಪಿಸುವದೇ ನಿನಗೆ ಸಂತೋಷ. ಸೆಲಾ.",
			"4": "<sup>4</sup> ಮೋಸದ ನಾಲಿಗೆಯೇ, ಹಾನಿಕರವಾದ ಮಾತುಗಳೇ ನಿನಗೆ ಇಷ್ಟ.",
			"5": "<sup>5</sup> ದೇವರು ನಿನ್ನನ್ನು ಎಂದಿಗೂ ಏಳದಂತೆ ಕೆಡವಿ ಬಿಡುವನು; ನಿನ್ನನ್ನು ಹಿಡಿದು ನಿವಾಸದೊಳಗಿಂದ ಕಿತ್ತುಬೀಸಾಡುವನು; ಆತನು ನಿನ್ನನ್ನು ಜೀವಲೋಕದಿಂದ ಬೇರು ಸಹಿತವಾಗಿ ಕಿತ್ತುಹಾಕುವನು. ಸೆಲಾ.",
			"6": "<sup>6</sup> ನೀತಿವಂತರು ಅದನ್ನು ನೋಡಿ ಭಯಪಡುವರು; ಅವರು ಪರಿಹಾಸ್ಯಮಾಡುತ್ತಾ -",
			"7": "<sup>7</sup> ನೋಡಿರಿ, ದೇವರನ್ನು ಆಶ್ರಯಿಸಿಕೊಳ್ಳದೆ ತನ್ನ ಅಧಿಕವಾದ ಐಶ್ವರ್ಯದಲ್ಲಿ ಭರವಸವಿಟ್ಟು ತನ್ನ ದುಷ್ಟತ್ವವೇ ತನಗೆ ಬಲವೆಂದು ನಂಬಿಕೊಂಡ ಮೂಢನು ಇವನೇ ಎಂದು ಹೇಳುವರು.",
			"8": "<sup>8</sup> ಆದರೆ ನಾನು ದೇವಾಲಯದ ಸೊಗಸಾದ ಎಣ್ಣೇ ಮರದಂತಿರುವೆನು; ದೇವರ ಕೃಪೆಯನ್ನು ಯುಗಯುಗಾಂತರಗಳಲ್ಲಿಯೂ ನಂಬಿಕೊಂಡಿರುವೆನು.",
			"9": "<sup>9</sup> [ದೇವರೇ] ನಿನ್ನ ಉಪಕಾರಕ್ಕಾಗಿ ಯಾವಾಗಲೂ ನಿನ್ನನ್ನು ಸ್ತುತಿಸುವೆನು; ನಿನ್ನ ನಾಮವು ಸರ್ವೋತ್ತಮವೆಂದು ನಿನ್ನ ಭಕ್ತರ ಮುಂದೆ ಅದನ್ನೇ ನಿರೀಕ್ಷಿಸಿಕೊಂಡಿರುವೆನು."
		},
		"53": {
			"1": "<sup>1</sup> ದುರ್ಮತಿಗಳು - ದೇವರಿಲ್ಲವೆಂದು ಮನಸ್ಸಿನಲ್ಲಿ ಅಂದುಕೊಳ್ಳುತ್ತಾರೆ; ಅವರು ಕೆಟ್ಟುಹೋದವರು; ಹೇಯಕೃತ್ಯಗಳನ್ನು ನಡಿಸುತ್ತಾರೆ; ಅವರಲ್ಲಿ ಒಳ್ಳೆಯದನ್ನು ಮಾಡುವವರೇ ಇಲ್ಲ.",
			"2": "<sup>2</sup> ಮನುಷ್ಯರಲ್ಲಿ ತನ್ನ ಸಾನ್ನಿಧ್ಯವನ್ನು ಅಪೇಕ್ಷಿಸುವ ಬುದ್ಧಿವಂತರು ಇದ್ದಾರೋ ಎಂದು ದೇವರು ಆಕಾಶದಿಂದ ಮನುಷ್ಯರನ್ನು ನೋಡಲಾಗಿ",
			"3": "<sup>3</sup> ಒಳ್ಳೆಯದನ್ನು ನಡಿಸುವವರು ಒಬ್ಬರಾದರೂ ಇಲ್ಲ; ಅವರೆಲ್ಲರೂ ದ್ರೋಹಿಗಳಾಗಿ ಕೆಟ್ಟುಹೋದವರೇ.",
			"4": "<sup>4</sup> ದುಷ್ಟತ್ವವನ್ನು ನಡಿಸುವವರು ತಿಳಿಯುವದಿಲ್ಲವೋ? ಅವರು ನನ್ನ ಜನರನ್ನು ಆಹಾರವನ್ನೋ ಎಂಬಂತೆ ನುಂಗಿಬಿಡುತ್ತಾರೆ; ದೇವರನ್ನು ಸ್ಮರಿಸುವದಿಲ್ಲ.",
			"5": "<sup>5</sup> ಭಯಪಡುವದಕ್ಕೆ ಕಾರಣವಿಲ್ಲದಿದ್ದರೂ ಅವರು ಫಕ್ಕನೆ ಭಯಭ್ರಾಂತರಾದರು; ನಿಮಗೆ ಮುತ್ತಿಗೆಹಾಕಿದವರನ್ನು ದೇವರು ಸಂಹರಿಸಿ ಅವರ ಎಲುಬುಗಳನ್ನು ಚದರಿಸಿಬಿಟ್ಟನಲ್ಲಾ. ದೇವರು ಅವರನ್ನು ಕೈಬಿಟ್ಟದ್ದರಿಂದ ನೀವು ಅವರನ್ನು ಅಪಜಯಪಡಿಸಿದಿರಿ.",
			"6": "<sup>6</sup> ಚೀಯೋನಿನಿಂದ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ರಕ್ಷಣೆಯು ಬೇಗನೆ ಬರಲಿ. ದೇವರು ದುರವಸ್ಥೆಯಿಂದ ತಪ್ಪಿಸಿದಾಗ ಆತನ ಪ್ರಜೆಗಳಾಗಿರುವ ಯಾಕೋಬ ವಂಶದವರು ಉಲ್ಲಾಸಗೊಳ್ಳುವರು; ಇಸ್ರಾಯೇಲ್ಯರು ಹರ್ಷಿಸುವರು."
		},
		"54": {
			"1": "<sup>1</sup> ದೇವರೇ, ನಿನ್ನ ನಾಮಮಹತ್ತಿನಿಂದ ನನ್ನನ್ನು ರಕ್ಷಿಸು; ನಿನ್ನ ಪರಾಕ್ರಮದಿಂದ ನನ್ನ ನ್ಯಾಯವನ್ನು ಸ್ಥಾಪಿಸು.",
			"2": "<sup>2</sup> ದೇವರೇ, ನನ್ನ ಪ್ರಾರ್ಥನೆಯನ್ನು ಲಾಲಿಸು; ನನ್ನ ಮಾತುಗಳಿಗೆ ಕಿವಿಗೊಡು.",
			"3": "<sup>3</sup> ಅನ್ಯರು ನನಗೆ ವಿರೋಧವಾಗಿ ನಿಂತಿದ್ದಾರೆ; ಬಲಾತ್ಕಾರಿಗಳು ನನ್ನ ಪ್ರಾಣವನ್ನು ತೆಗೆಯಬೇಕೆಂದಿದ್ದಾರೆ. ಅವರು ದೇವರನ್ನು ಲಕ್ಷಿಸುವದೇ ಇಲ್ಲ. ಸೆಲಾ.",
			"4": "<sup>4</sup> ಇಗೋ, ದೇವರೇ ನನಗೆ ಸಹಾಯಕನು; ಕರ್ತನೇ ನನ್ನ ಪ್ರಾಣವನ್ನು ಕಾಪಾಡುವವನು.",
			"5": "<sup>5</sup> ನನ್ನ ಕೆಡುಕಿಗೆ ಸಮಯನೋಡುವವರು ತಾವೇ ಕೇಡನ್ನು ಅನುಭವಿಸಲಿ. ನೀನು ನಂಬಿಗಸ್ತನಲ್ಲವೇ; ಅವರನ್ನು ನಿರ್ಮೂಲ ಮಾಡು.",
			"6": "<sup>6</sup> ಯೆಹೋವನೇ, ನೀನು ನನ್ನನ್ನು ಸರ್ವಾಪತ್ತುಗಳಿಂದ ಬಿಡಿಸಿ ನನ್ನ ಕಣ್ಣ ಮುಂದೆಯೇ ನನ್ನ ವೈರಿಗಳನ್ನು ಶಿಕ್ಷಿಸಿದ್ದರಿಂದ",
			"7": "<sup>7</sup> ನಾನು ಸಂತೋಷದಿಂದ ನಿನಗೆ ಯಜ್ಞವನ್ನು ಸಮರ್ಪಿಸುವೆನು; ಉತ್ತಮೋತ್ತಮವಾಗಿರುವ ನಿನ್ನ ಹೆಸರನ್ನು ಕೊಂಡಾಡುವೆನು."
		},
		"55": {
			"1": "<sup>1</sup> ದೇವರೇ, ನನ್ನ ಮೊರೆಯನ್ನು ಲಾಲಿಸು; ನನ್ನ ವಿಜ್ಞಾಪನೆಗೆ ಕಿವಿಮುಚ್ಚಿಕೊಳ್ಳಬೇಡ.",
			"2": "<sup>2</sup> ನನ್ನ ಕಡೆಗೆ ಲಕ್ಷ್ಯಕೊಟ್ಟು ಸದುತ್ತರವನ್ನು ದಯಪಾಲಿಸು.",
			"3": "<sup>3</sup> ಶತ್ರುಗಳ ಅಬ್ಬರ, ದುಷ್ಟರ ಹಿಂಸೆ ಇವುಗಳ ದೆಸೆಯಿಂದ ಪ್ರಲಾಪಿಸುವವನಾಗಿ ಹೊಯ್ದಾಡುತ್ತಾ ನರಳುತ್ತೇನೆ. ಅವರು ನನ್ನ ಮೇಲೆ ಅಪಾಯವನ್ನು ಬರಮಾಡಿ ಕೋಪದಿಂದ ನನ್ನನ್ನು ದ್ವೇಷಿಸುತ್ತಾರೆ.",
			"4": "<sup>4</sup> ನನ್ನ ಹೃದಯವು ನೊಂದು ಬೆಂದದೆ; ಮರಣಭಯವು ಆವರಿಸಿಕೊಂಡದೆ.",
			"5": "<sup>5</sup> ಅಂಜಿ ನಡುಗುತ್ತೇನೆ; ದಿಗಿಲು ನನ್ನನ್ನು ಹಿಡಿದದೆ.",
			"6": "<sup>6</sup> ಆಹಾ, ನನಗೆ ರೆಕ್ಕೆಗಳಿದ್ದರೆ ಪಾರಿವಾಳದಂತೆ ಹಾರಿಹೋಗಿ ಆಶ್ರಯಸೇರಿಕೊಳ್ಳುತ್ತಿದ್ದೆನು.",
			"7": "<sup>7</sup> ಅವಸರದಿಂದ ಹಾನಿಕರವಾದ ಬಿರುಗಾಳಿಗೆ ತಪ್ಪಿಸಿಕೊಂಡು",
			"8": "<sup>8</sup> ದೂರ ಹೋಗಿ ಅರಣ್ಯಸ್ಥಳದಲ್ಲಿ ಪ್ರವಾಸಿಯಾಗಿರುತ್ತಿದ್ದೆನು ಅಂದುಕೊಂಡೆನು. ಸೆಲಾ.",
			"9": "<sup>9</sup> ಕರ್ತನೇ, ಅವರ ಭಾಷೆಯನ್ನು ತಾರು ಮಾರುಮಾಡಿ ಅವರನ್ನು ಭ್ರಾಂತಿಗೊಳಿಸು. ಪಟ್ಟಣದಲ್ಲಿ ಕಲಹಬಲಾತ್ಕಾರಗಳು ಕಾಣಬರುತ್ತವೆ.",
			"10": "<sup>10</sup> ಅವೇ ಅದರ ಪೌಳಿಗೋಡೆಗಳ ಮೇಲೆ ಹಗಲಿರುಳು ಸುತ್ತುತ್ತಿರುವ ಕಾವಲುಗಾರರು; ಊರೊಳಗೆ ಕೇಡುತೊಂದರೆಗಳು ಪ್ರಬಲವಾಗಿವೆ.",
			"11": "<sup>11</sup> ಅದರೊಳಗೆಲ್ಲಾ ನಾಶನವೇ; ತಿಪ್ಪಲಾಟವೂ ವಂಚನೆಯೂ ಅದರ ಬೀದಿಗಳಿಂದ ತೊಲಗವು.",
			"12": "<sup>12</sup> ನನ್ನನ್ನು ದೂಷಿಸುವವನು ವೈರಿಯಾಗಿದ್ದರೆ ತಾಳಿಕೊಂಡೇನು; ನನ್ನನ್ನು ತಿರಸ್ಕರಿಸಿ ಉಬ್ಬಿಕೊಳ್ಳುವವನು ದ್ವೇಷಿಯಾಗಿದ್ದರೆ ಅಡಗಿಕೊಂಡೇನು.",
			"13": "<sup>13</sup> ಆದರೆ ನೀನು ನನಗೆ ಸ್ವಕೀಯನೂ ಆಪ್ತಮಿತ್ರನೂ ಅಲ್ಲವೇ.",
			"14": "<sup>14</sup> ನಾವು ಪರಸ್ಪರವಾಗಿ ರಸಭರಿತ ಸಂಭಾಷಣೆ ಮಾಡುತ್ತಾ ಭಕ್ತಸಮೂಹದೊಡನೆ ದೇವಾಲಯಕ್ಕೆ ಹೋಗುತ್ತಿದ್ದೆವಲ್ಲಾ.",
			"15": "<sup>15</sup> ಆ ದುಷ್ಟರಿಗೆ ಮರಣವು ತಟ್ಟನೆ ಬರಲಿ; ಸಜೀವರಾಗಿಯೇ ಪಾತಾಳಕ್ಕೆ ಇಳಿದು ಹೋಗಲಿ. ಅವರ ಮನೆಯಲ್ಲಿಯೂ ಮನಸ್ಸಿನಲ್ಲಿಯೂ ಕೆಟ್ಟತನವೇ.",
			"16": "<sup>16</sup> ನಾನಂತೂ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಡುವೆನು; ಆತನು ನನ್ನನ್ನು ರಕ್ಷಿಸುವನು.",
			"17": "<sup>17</sup> ತ್ರಿಕಾಲದಲ್ಲಿಯೂ ಹಂಬಲಿಸುತ್ತಾ ಮೊರೆಯಿಡುವೆನು. ಆತನು ಹೇಗೂ ನನ್ನ ಮೊರೆಯನ್ನು ಕೇಳಿ",
			"18": "<sup>18</sup> ನನ್ನ ವಿರೋಧಿಗಳು ಅನೇಕರಿದ್ದರೂ ಅವರು ನನ್ನನ್ನು ಮುಟ್ಟದಂತೆ ಸುರಕ್ಷಿತವಾಗಿ ಇಡುವನು.",
			"19": "<sup>19</sup> ಅನಾದಿಕಾಲದಿಂದ ಆಸನಾರೂಢನಾಗಿರುವ ದೇವರು ಲಕ್ಷ್ಯವಿಟ್ಟು ಅವರನ್ನು ತಗ್ಗಿಸಿಬಿಡುವನು. ಸೆಲಾ. ಅವರು ತಮ್ಮ ಸುಖವು ಕದಲದೆಂದುಕೊಂಡು ದೇವರಿಗೆ ಹೆದರುವದಿಲ್ಲ.",
			"20": "<sup>20</sup> ಆ ದ್ರೋಹಿಯಾದರೋ ತನ್ನೊಡನೆ ಸಮಾಧಾನದಿಂದಿದ್ದವರ ಮೇಲೆ ವಿರೋಧವಾಗಿ ಕೈಯೆತ್ತಿ ತಾನು ಮಾಡಿಕೊಂಡ ಒಡಂಬಡಿಕೆಯನ್ನು ಭಂಗಪಡಿಸಿದ್ದಾನೆ.",
			"21": "<sup>21</sup> ಅವನ ಬಾಯಿ ಬೆಣ್ಣೆಯಂತೆ ನುಣುಪು; ಹೃದಯವೋ ಕಲಹಮಯವೇ. ಅವನ ನುಡಿಗಳು ಎಣ್ಣೆಗಿಂತ ನಯವಾಗಿದ್ದರೂ ಬಿಚ್ಚುಕತ್ತಿಗಳೇ ಸರಿ.",
			"22": "<sup>22</sup> ನಿನ್ನ ಚಿಂತಾಭಾರವನ್ನು ಯೆಹೋವನ ಮೇಲೆ ಹಾಕು; ಆತನು ನಿನ್ನನ್ನು ಉದ್ಧಾರಮಾಡುವನು. ನೀತಿವಂತನನ್ನು ಎಂದಿಗೂ ಕದಲಗೊಡಿಸನು.",
			"23": "<sup>23</sup> ದೇವರೇ, ನೀನು ದುಷ್ಟರನ್ನು ಪಾತಾಳದ ಕೆಳಕ್ಕೆ ದೊಬ್ಬಿಬಿಡುವಿ. ಕೊಲೆಪಾತಕರೂ ವಂಚಕರೂ ನರಾಯುಷ್ಯದ ಅರ್ಧಾಂಶವಾದರೂ ಬದುಕರು. ನಾನಂತೂ ನಿನ್ನನ್ನೇ ನಂಬಿಕೊಂಡಿರುವೆನು."
		},
		"56": {
			"1": "<sup>1</sup> ದೇವರೇ, ಕರುಣಿಸು; ನರರು ನನ್ನನ್ನು ನುಂಗಿಬಿಡಬೇಕೆಂದು ಎದ್ದಿದ್ದಾರೆ. ಹಗಲೆಲ್ಲಾ ಯುದ್ಧಕ್ಕೆ ನಿಂತು ಬಾಧಿಸುತ್ತಾರೆ.",
			"2": "<sup>2</sup> ಹೊಂಚುಹಾಕಿ ನನ್ನನ್ನು ನುಂಗಿಬಿಡಬೇಕೆಂದು ಯಾವಾಗಲೂ ಬಾಯ್ದೆರೆದಿದ್ದಾರೆ; ಸೊಕ್ಕಿನಿಂದ ನನ್ನ ಮೇಲೆ ಯುದ್ಧಕ್ಕೆ ನಿಂತವರು ಎಷ್ಟೋ ಮಂದಿ.",
			"3": "<sup>3</sup> ನನಗೆ ಹೆದರಿಕೆಯುಂಟಾದಾಗ ನಿನ್ನನ್ನೇ ಆಶ್ರಯಿಸಿಕೊಳ್ಳುವೆನು.",
			"4": "<sup>4</sup> ದೇವರ ವಾಗ್ದಾನಕ್ಕೋಸ್ಕರ ಆತನಲ್ಲಿಯೇ ಹೆಚ್ಚಳಪಡುವೆನು; ದೇವರನ್ನು ನಂಬಿ ನಿರ್ಭಯದಿಂದಿರುವೆನು. ನರಪ್ರಾಣಿಗಳು ನನಗೆ ಮಾಡುವದೇನು?",
			"5": "<sup>5</sup> ಹಗಲೆಲ್ಲಾ ನನ್ನ ಮಾತುಗಳಿಗೆ ಅಪಾರ್ಥ ಮಾಡುತ್ತಾರೆ; ಅವರು ಬಗೆಯುವದೆಲ್ಲ ನನಗೆ ಕೇಡೇ.",
			"6": "<sup>6</sup> ಅವರು ಒಟ್ಟುಗೂಡಿ ನನ್ನ ಜೀವ ತೆಗೆಯಬೇಕೆಂದು ಹೊಂಚುಹಾಕಿ ನನ್ನ ಹೆಜ್ಜೆ ಹಿಡಿದು ಬರುತ್ತಾರೆ.",
			"7": "<sup>7</sup> ಇಂಥ ಅನ್ಯಾಯಗಾರರು ತಪ್ಪಿಸಿಕೊಳ್ಳಬಹುದೋ? ದೇವರೇ, ರೌದ್ರದಿಂದ ಜನಾಂಗಗಳನ್ನು ಉರುಳಿಸಿಬಿಡು.",
			"8": "<sup>8</sup> ನಾನು ದೇಶಭ್ರಷ್ಟನಾಗಿ ಅಲೆದಾಡಿದ್ದನ್ನು ನೀನೇ ಬಲ್ಲೆ. ನನ್ನ ಕಣ್ಣೀರು ನಿನ್ನ ಬುದ್ದಲಿಯಲ್ಲಿ ತುಂಬಿದೆ; ಅದರ ವಿಷಯ ನಿನ್ನ ಪುಸ್ತಕದಲ್ಲಿ ಬರೆದದೆಯಲ್ಲಾ.",
			"9": "<sup>9</sup> ದೇವರು ನನ್ನ ಸಂಗಡ ಇರುವದು ನಿಶ್ಚಯ; ನಾನು ಆತನಿಗೆ ಮೊರೆಯಿಡುವಾಗಲೇ ನನ್ನ ಶತ್ರುಗಳು ಫಕ್ಕನೆ ಪಲಾಯನ ಮಾಡುವರು.",
			"10": "<sup>10</sup> ದೇವರಲ್ಲಿ ಹೆಚ್ಚಳಪಟ್ಟು ಹಾಡಿ ಹರಸುತ್ತೇನೆ. ಯೆಹೋವನಲ್ಲಿ ಹೆಚ್ಚಳಪಟ್ಟು ಹಾಡಿಹರಸುತ್ತೇನೆ.",
			"11": "<sup>11</sup> ದೇವರನ್ನು ನಂಬಿ ನಿರ್ಭಯದಿಂದಿರುವೆನು; ನರಪ್ರಾಣಿಗಳು ನನಗೆ ಮಾಡುವದೇನು?",
			"12": "<sup>12</sup> ದೇವರೇ, ನಾನು ಜೀವದಿಂದ ಬೆಳಕಿನಲ್ಲಿದ್ದು ನಿನಗೆ ನಡೆದುಕೊಳ್ಳಬೇಕೆಂದು ನೀನು ನನ್ನ ಪ್ರಾಣವನ್ನು ಮರಣಕ್ಕೆ ತಪ್ಪಿಸಿ ನನ್ನ ಪಾದಗಳನ್ನು ಎಡವದಂತೆ ಕಾದಿದ್ದೀ.",
			"13": "<sup>13</sup> ನಿನಗೆ ಹೊತ್ತ ಹರಕೆಗಳನ್ನು ಸಲ್ಲಿಸುವೆನು; ಕೃತಜ್ಞತಾಯಜ್ಞಗಳನ್ನು ಸಮರ್ಪಿಸುವೆನು."
		},
		"57": {
			"1": "<sup>1</sup> ದೇವರೇ, ಕರುಣಿಸು, ನನ್ನನ್ನು ಕರುಣಿಸು. ನೀನೇ ನನ್ನ ಆಶ್ರಯಸ್ಥಾನವಲ್ಲವೇ! ಆಪತ್ತುಗಳು ಕಳೆದುಹೋಗುವ ತನಕ ನಿನ್ನ ರೆಕ್ಕೆಗಳ ಮರೆಯನ್ನು ಆಶ್ರಯಿಸಿಕೊಳ್ಳುವೆನು.",
			"2": "<sup>2</sup> ಪರಾತ್ಪರನಾದ ದೇವರಿಗೆ ಮೊರೆಯಿಡುವೆನು, ನನ್ನ ಕಾರ್ಯವನ್ನು ಸಿದ್ಧಿಗೆ ತರುವ ದೇವರನ್ನು ಕೂಗಿಕೊಳ್ಳುವೆನು.",
			"3": "<sup>3</sup> ಆತನು ಪರಲೋಕದಿಂದ ಸಹಾಯಮಾಡಿ ನಿಂದಕರ ಬಾಯಿಗೆ ನನ್ನನ್ನು ತಪ್ಪಿಸುವನು; ಸೆಲಾ. ತನ್ನ ಕಾರುಣ್ಯವನ್ನೂ ಸತ್ಯತೆಯನ್ನೂ ತೋರ್ಪಡಿಸುವನು.",
			"4": "<sup>4</sup> ಸಿಂಹಗಳ ಮಧ್ಯದಲ್ಲಿ ಸಿಕ್ಕಿದ್ದೇನೆ; ಬೆಂಕಿಕಾರುವ ಮನುಷ್ಯರ ನಡುವೆ ಬಿದ್ದಿದ್ದೇನೆ. ಅವರ ಹಲ್ಲುಗಳು ಶಸ್ತ್ರಬಾಣಗಳು; ನಾಲಿಗೆಗಳು ಹದವಾದ ಕತ್ತಿಗಳೇ.",
			"5": "<sup>5</sup> ದೇವರೇ, ಮೇಲಣ ಲೋಕಗಳಲ್ಲಿ ನಿನ್ನ ಪ್ರಭಾವವು ಮೆರೆಯಲಿ; ಭೂಮಂಡಲದ ಮೇಲೆಲ್ಲಾ ನಿನ್ನ ಮಹತ್ವವು ಹಬ್ಬಲಿ.",
			"6": "<sup>6</sup> ನನ್ನ ಕಾಲಿಗೆ ಬಲೆಯನ್ನು ಹಾಸಿದ್ದಾರೆ; ನನ್ನ ಪ್ರಾಣವು ಕುಂದಿಹೋಯಿತು. ನನ್ನ ದಾರಿಯಲ್ಲಿ ಕುಣಿಯನ್ನು ಅಗೆದರು; ತಾವೇ ಅದರಲ್ಲಿ ಬಿದ್ದುಹೋದರು. ಸೆಲಾ.",
			"7": "<sup>7</sup> ದೇವರೇ, ನನ್ನ ಹೃದಯವು ಸ್ಥಿರವಾಗಿದೆ, ಸ್ಥಿರವಾಗಿದೆ. ನಾನು ಬಾರಿಸುತ್ತಾ ಹಾಡುವೆನು.",
			"8": "<sup>8</sup> ನನ್ನ ಮನವೇ, ಚುರುಕಾಗು; ಸ್ವರಮಂಡಲವೇ, ಕಿನ್ನರಿಯೇ, ಎಚ್ಚರವಾಗಿರಿ. ಸಂಕೀರ್ತನೆಯಿಂದ ಉದಯವನ್ನು ಮುಂಗೊಳ್ಳುವೆನು.",
			"9": "<sup>9</sup> ಕರ್ತನೇ, ಜನಾಂಗಗಳಲ್ಲಿ ನಿನ್ನನ್ನು ಸ್ತುತಿಸುವೆನು; ಸರ್ವದೇಶದವರೊಳಗೆ ನಿನ್ನನ್ನು ಕೊಂಡಾಡುವೆನು.",
			"10": "<sup>10</sup> ಯಾಕಂದರೆ ನಿನ್ನ ಕೃಪೆಯು ಆಕಾಶವನ್ನೂ ನಿನ್ನ ಸತ್ಯತೆಯು ಮುಗಿಲನ್ನೂ ನಿಲುಕುವಷ್ಟು ದೊಡ್ಡವಾಗಿವೆ.",
			"11": "<sup>11</sup> ದೇವರೇ, ಮೇಲಣ ಲೋಕಗಳಲ್ಲಿ ನಿನ್ನ ಪ್ರಭಾವವು ಮೆರೆಯಲಿ; ಭೂಮಂಡಲದ ಮೇಲೆಲ್ಲಾ ನಿನ್ನ ಮಹತ್ವವು ಹಬ್ಬಲಿ."
		},
		"58": {
			"1": "<sup>1</sup> ಅಧಿಕಾರಿಗಳೇ, ನೀವು ಕೊಡುವ ತೀರ್ಪುಗಳು ನೀತಿಗನುಸಾರವಾಗಿವೆಯೋ? ಜನರ ವ್ಯಾಜ್ಯಗಳನ್ನು ಯಥಾರ್ಥವಾಗಿ ಬಗೆಹರಿಸುವದುಂಟೋ?",
			"2": "<sup>2</sup> ನಿಮ್ಮ ಮನಸ್ಸಿನ ಕಲ್ಪನೆಯೆಲ್ಲಾ ಕೆಟ್ಟತನವೇ; ನೀವು ದೇಶದವರಿಗೆ ಅನ್ಯಾಯವನ್ನೇ ತೂಗಿಕೊಡುವವರಾಗಿದ್ದೀರಲ್ಲಾ.",
			"3": "<sup>3</sup> ದುಷ್ಟರು ಜನ್ಮದಿಂದಲೇ ಧರ್ಮ ಭ್ರಷ್ಟರು; ಅವರು ಹುಟ್ಟಿದಂದಿನಿಂದ ಸುಳ್ಳುಗಾರರಾಗಿ ದಾರಿ ಬಿಟ್ಟವರು.",
			"4": "<sup>4</sup> ಅವರು ಸರ್ಪದಂತೆ ವಿಷಭರಿತರು.",
			"5": "<sup>5</sup> ಜಾಣತನದಿಂದ ಮಂತ್ರಿಸುವ ಹಾವಾಡಿಗರ ನಾಗಸ್ವರಕ್ಕೂ ಮರುಳಾಗದೆ ಕಿವಿಗೊಡದ ಕಳ್ಳಹಾವಿನಂತೆ ಇರುತ್ತಾರೆ.",
			"6": "<sup>6</sup> ದೇವರೇ, ಅವರ ಹಲ್ಲುಗಳನ್ನು ಮುರಿದುಬಿಡು; ಯೆಹೋವನೇ, ಪ್ರಾಯದ ಸಿಂಹಗಳ ಕೋರೇದಾಡೆಗಳನ್ನು ಕಿತ್ತುಹಾಕು.",
			"7": "<sup>7</sup> ಕ್ಷಣದಲ್ಲಿ ಹರಿದು ಕಾಣದೆಹೋಗುವ ನೀರಿನಂತೆ ಅವರು ಮಾಯವಾಗಲಿ; ಬಾಣ ತಾಗಿತೋ ಎಂಬಂತೆ ಅವರು ಬೀಳಲಿ.",
			"8": "<sup>8</sup> ಒಣಗಿ ಸತ್ತ ಬಸವನಹುಳದಂತೆ ನಿರ್ನಾಮವಾಗಲಿ; ಗರ್ಭವಿಳಿದ ಪಿಂಡದಂತೆ ಸೂರ್ಯನನ್ನು ನೋಡದೆ ಹೋಗಲಿ.",
			"9": "<sup>9</sup> ಇನ್ನೂ ಬೇಯದಿರುವಾಗಲೇ ಒಲೆಗಿಕ್ಕಿದ ಮುಳ್ಳಿನ ಕೊಂಪೆಯನ್ನು ಬಿರುಗಾಳಿಯು ಹಾರಿಸಿಬಿಡುವಂತೆ ದೇವರ ಉಗ್ರಕೋಪವು ಅವರನ್ನು ನಿಶ್ಶೇಷಮಾಡಲಿ.",
			"10": "<sup>10</sup> ದುಷ್ಟರಿಗಾಗುವ ಪ್ರತಿದಂಡನೆಯನ್ನು ಭಕ್ತರು ನೋಡಿ ಹರ್ಷಿಸಿ ಅವರ ರಕ್ತದಲ್ಲಿ ಕಾಲಾಡಿಸುವರು.",
			"11": "<sup>11</sup> ನೀತಿವಂತನಿಗೆ ಫಲವುಂಟೆಂತಲೂ ಲೋಕದಲ್ಲಿ ನ್ಯಾಯಸ್ಥಾಪಕನಾದ ದೇವರು ಇದ್ದಾನೆಂತಲೂ ಸರ್ವರು ಒಪ್ಪಿಕೊಳ್ಳುವರು."
		},
		"59": {
			"1": "<sup>1</sup> ನನ್ನ ದೇವರೇ, ಶತ್ರುಗಳ ಕೈಯಿಂದ ನನ್ನನ್ನು ಬಿಡಿಸು; ನನಗೆ ವಿರೋಧವಾಗಿ ಎದ್ದಿರುವವರಿಗೆ ನನ್ನನ್ನು ತಪ್ಪಿಸಿ ಭದ್ರಸ್ಥಳದಲ್ಲಿರಿಸು.",
			"2": "<sup>2</sup> ಕೆಡುಕರಿಂದ ಬಿಡಿಸು; ಕೊಲೆಪಾತಕರಿಂದ ನನ್ನನ್ನು ರಕ್ಷಿಸು.",
			"3": "<sup>3</sup> ಇಗೋ, ನನ್ನ ಜೀವಕ್ಕೆ ಹೊಂಚುಹಾಕುತ್ತಾರೆ; ಬಲಿಷ್ಠರು ನನಗೆ ವಿರೋಧವಾಗಿ ಗುಂಪು ಕೂಡಿದ್ದಾರೆ. ಯೆಹೋವನೇ, ನಾನು ನಿರ್ದೋಷಿಯೂ ನಿರಪರಾಧಿಯೂ ಅಲ್ಲವೇ!",
			"4": "<sup>4</sup> ನಿಷ್ಕಾರಣವಾಗಿ ನನ್ನ ಮೇಲೆ ಬೀಳಲು ಓಡಿಬಂದು ನಿಂತಿದ್ದಾರೆ; ಎದ್ದು ಬಂದು ಪರಾಂಬರಿಸಿ ನನಗೆ ಸಹಾಯ ಮಾಡು.",
			"5": "<sup>5</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೇ, ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರೇ, ನೀನು ಎಚ್ಚರವಾಗಿ ಎಲ್ಲಾ ಅನ್ಯಜನಾಂಗಗಳನ್ನು ದಂಡಿಸು. ದುಷ್ಟದ್ರೋಹಿಗಳಲ್ಲಿ ಒಬ್ಬನಿಗೂ ದಯೆತೋರಿಸಬೇಡ. ಸೆಲಾ.",
			"6": "<sup>6</sup> ಅವರು ಪ್ರತಿಸಾಯಂಕಾಲವೂ ಬಂದು ಬಂದು ನಾಯಿಗಳಂತೆ ಗುರುಗುಟ್ಟುತ್ತಾ ಪಟ್ಟಣವನ್ನೆಲ್ಲಾ ಸುತ್ತುತ್ತಾರೆ.",
			"7": "<sup>7</sup> ಇಗೋ, ಅವರ ಬಾಯಿಗಳು ಎಷ್ಟೋ ಮಾತುಗಳನ್ನು ಕಕ್ಕುತ್ತವೆ; ಅವೆಲ್ಲಾ ಕತ್ತಿಗಳೇ. ನಮ್ಮನ್ನು ಕೇಳುವವರು ಯಾರು ಅಂದುಕೊಳ್ಳುತ್ತಾರೆ.",
			"8": "<sup>8</sup> ಯೆಹೋವನೇ, ನೀನಾದರೋ ನೋಡಿ ನಗುವಿ; ಎಲ್ಲಾ ಅನ್ಯಜನಾಂಗಗಳನ್ನು ಪರಿಹಾಸ್ಯಮಾಡುವಿ.",
			"9": "<sup>9</sup> ನನ್ನ ಬಲವೇ, ನಿನ್ನನ್ನು ನಿರೀಕ್ಷಿಸುತ್ತಿದ್ದೇನೆ. ನನ್ನ ಆಶ್ರಯದುರ್ಗವು ದೇವರೇ.",
			"10": "<sup>10</sup> ನನ್ನ ದೇವರು ಪ್ರಸನ್ನನಾಗಿ ನನಗೆ ಸಹಾಯಮಾಡುವನು; ನನ್ನ ವಿರೋಧಿಗಳಿಗುಂಟಾದ ಶಿಕ್ಷೆಯನ್ನು ನಾನು ನೋಡುವಂತೆ ಮಾಡುವನು.",
			"11": "<sup>11</sup> ಕರ್ತನೇ, ನಮ್ಮ ಗುರಾಣಿಯೇ, ಅವರನ್ನು ಫಕ್ಕನೆ ಸಂಹರಿಸಬೇಡ; ನನ್ನ ಜನರು ಮರೆತುಬಿಡದ ಹಾಗೆ ಅವರು ಉಳಿಯಲಿ. ನಿನ್ನ ಸೇನಾಬಲದಿಂದ ಚದರಿಸಿ ಭ್ರಾಂತಿಯಿಂದ ಅಲೆದಾಡಿಸಿ ಅವರನ್ನು ಕೆಡವಿಬಿಡು.",
			"12": "<sup>12</sup> ಅವರ ಬಾಯಿಂದ ಬರುವದೆಲ್ಲಾ ಪಾಪವೇ. ಅವರ ಅಹಂಕಾರದಿಂದಲೇ ಅವರು ಸಿಕ್ಕಿ ಬೀಳಲಿ.",
			"13": "<sup>13</sup> ಅವರು ನುಡಿಯುವ ಶಾಪಕ್ಕಾಗಿಯೂ ಸುಳ್ಳಿಗಾಗಿಯೂ ಅವರನ್ನು ರೌದ್ರದಿಂದ ಸಂಹರಿಸಿ ನಿರ್ನಾಮಗೊಳಿಸು. ಯಾಕೋಬನ ವಂಶದವರನ್ನು ಆಳುವವನು ದೇವರೇ ಎಂಬದು ಭೂಲೋಕದಲ್ಲೆಲ್ಲಾ ಗೊತ್ತಾಗಲಿ. ಸೆಲಾ.",
			"14": "<sup>14</sup> ಅವರು ಪ್ರತಿಸಾಯಂಕಾಲವೂ ಬಂದು ಬಂದು ನಾಯಿಗಳಂತೆ ಗುರುಗುಟ್ಟುತ್ತಾ ಪಟ್ಟಣವನ್ನೆಲ್ಲಾ ಸುತ್ತುತ್ತಾರೆ.",
			"15": "<sup>15</sup> ಆಹಾರಕ್ಕಾಗಿ ಅತ್ತಿತ್ತ ತಿರುಗುತ್ತಾರೆ; ಹೊಟ್ಟೆಗಿಲ್ಲದಿದ್ದರೆ ಬೊಗಳುತ್ತಾರೆ.",
			"16": "<sup>16</sup> ನಾನಾದರೋ, ಇಕ್ಕಟ್ಟಿನ ಕಾಲದಲ್ಲಿ ಆಶ್ರಯವೂ ದುರ್ಗವೂ ಆಗಿರುವ ನಿನ್ನ ಬಲವನ್ನು ಹಾಡಿ ಹೊಗಳುವೆನು; ಪ್ರಾತಃಕಾಲದಲ್ಲಿ ನಿನ್ನ ಪ್ರೇಮವನ್ನು ಉಲ್ಲಾಸದಿಂದ ಕೊಂಡಾಡುವೆನು.",
			"17": "<sup>17</sup> ನನ್ನ ಬಲವೇ, ನಿನ್ನನ್ನು ಹಾಡಿಹರಸುವೆನು. ನನ್ನ ಆಶ್ರಯದುರ್ಗವೂ ಕೃಪಾನಿಧಿಯೂ ದೇವರೇ."
		},
		"60": {
			"1": "<sup>1</sup> ದೇವರೇ, ಕೋಪದಿಂದ ನಮ್ಮನ್ನು ತಳ್ಳಿಕೆಡವಿ ಬಿಟ್ಟಿದ್ದೀ; ನಮ್ಮನ್ನು ಎತ್ತಿ ನಿಲ್ಲಿಸು.",
			"2": "<sup>2</sup> ನೀನು ದೇಶವನ್ನು ಕಂಪನಗೊಳಿಸಿ ಒಡೆದು ಬಿಟ್ಟಿದ್ದೀ. ಅದರ ಒಡಕುಗಳನ್ನು ಸರಿಮಾಡು; ಅದು ನಡುಗುತ್ತಿರುವದಲ್ಲಾ.",
			"3": "<sup>3</sup> ನಿನ್ನ ಜನರನ್ನು ಸಂಕಟಕ್ಕೆ ಗುರಿಪಡಿಸಿದ್ದೀ; ನೀನು ನಮಗೆ ಭ್ರಮಣಮದ್ಯವನ್ನು ಕುಡಿಸಿದ್ದೀ.",
			"4": "<sup>4</sup> ನೀನು ನಿನ್ನ ಭಕ್ತರಿಗೆ ಧ್ವಜವನ್ನು ಕೊಟ್ಟದ್ದು ಅವರು ಬಿಲ್ಲುಗಾರರಿಗೆ ಹೆದರಿ ಓಡುವದಕ್ಕೋ? ಸೆಲಾ.",
			"5": "<sup>5</sup> ನಿನ್ನ ಪ್ರಿಯರಾದ ನಮ್ಮ ಮೊರೆಯನ್ನು ಲಾಲಿಸಿ ರಕ್ಷಿಸು. ನಿನ್ನ ಭುಜಬಲದಿಂದ ನಮ್ಮನ್ನು ಜಯಗೊಳಿಸು.",
			"6": "<sup>6</sup> ದೇವರು ತನ್ನ ಪವಿತ್ರತ್ವವನ್ನು ಸಾಕ್ಷಿಮಾಡಿ ನುಡಿದಿದ್ದಾನೆ. ಜಯಘೋಷಮಾಡುವೆನು; ಶೆಖೆಮ್ ಪ್ರದೇಶವನ್ನು ಹಂಚುವೆನು. ಸುಖೋತ್ ಬೈಲನ್ನು ಅಳೆದುಕೊಡುವೆನು.",
			"7": "<sup>7</sup> ಗಿಲ್ಯಾದ್ ಸೀಮೆಯೂ ಮನಸ್ಸೆಯ ದೇಶವೂ ನನ್ನವು; ಎಫ್ರಾಯೀಮ್ ಗೋತ್ರವು ನನಗೆ ಶಿರಸ್ತ್ರಾಣವು. ನನ್ನ ರಾಜದಂಡವು ಯೆಹೂದಕುಲವೇ.",
			"8": "<sup>8</sup> ಮೋವಾಬ್ ಪ್ರದೇಶವು ನನ್ನ ಸ್ನಾನಪಾತ್ರೆಯು; ಎದೋಮ್ ಸೀಮೆಯು ನನ್ನ ಕೆರಗಳನ್ನು ಬಿಡುವ ಸ್ಥಳ. ಫಿಲಿಷ್ಟಿಯರು ನನ್ನ ವಿಷಯವಾಗಿ ಕೂಗಿಕೊಳ್ಳಲಿ.",
			"9": "<sup>9</sup> ಕೋಟೆಕೊತ್ತಲುಗಳುಳ್ಳ ನಗರಕ್ಕೆ ನನ್ನನ್ನು ಕರೆದುಕೊಂಡು ಹೋಗುವವರು ಯಾರು? ಎದೋಮ್ ಪ್ರಾಂತದೊಳಗೆ ನನ್ನನ್ನು ಸೇರಿಸುವವರು ಯಾರು?",
			"10": "<sup>10</sup> ದೇವರೇ, ನೀನು ನಮ್ಮ ಸೈನ್ಯಗಳ ಸಂಗಡ ಬರಲಿಲ್ಲವಲ್ಲಾ! ದೇವರೇ, ನಮ್ಮನ್ನು ಕೈಬಿಟ್ಟಿಯಾ?",
			"11": "<sup>11</sup> ನಮಗೆ ಕೈಕೊಟ್ಟು ಶತ್ರುಬಾಧೆಯಿಂದ ಪಾರುಮಾಡು; ಮನುಷ್ಯರ ಸಹಾಯವು ವ್ಯರ್ಥ.",
			"12": "<sup>12</sup> ದೇವರಿಂದ ಶೂರಕೃತ್ಯಗಳನ್ನು ನಡಿಸುವೆವು; ನಮ್ಮ ವೈರಿಗಳನ್ನು ತುಳಿದುಬಿಡುವವನು ಆತನೇ."
		},
		"61": {
			"1": "<sup>1</sup> ದೇವರೇ, ನನ್ನ ಕೂಗನ್ನು ಕೇಳಿ ನನ್ನ ಪ್ರಾರ್ಥನೆಗೆ ಕಿವಿಗೊಡು.",
			"2": "<sup>2</sup> ನಾನು ಎದೆಗುಂದಿದವನಾಗಿ ಭೂಮಿಯ ಕಡೇ ಭಾಗದಿಂದ ನಿನಗೆ ಮೊರೆಯಿಡುತ್ತೇನೆ; ನಾನು ಹತ್ತಲಾರದ ಆಶ್ರಯಗಿರಿಯ ಮೇಲೆ ನನ್ನನ್ನು ಹತ್ತಿಸು.",
			"3": "<sup>3</sup> ನೀನು ನನಗೆ ಶರಣನೂ ಶತ್ರುಗಳಿಂದ ತಪ್ಪಿಸುವ ಭದ್ರವಾದ ಬುರುಜೂ ಆಗಿದ್ದೀ.",
			"4": "<sup>4</sup> ನನಗೆ ನಿರಂತರವೂ ನಿನ್ನ ಗುಡಾರದಲ್ಲಿ ಬಿಡಾರವಾಗಲಿ; ನಿನ್ನ ರೆಕ್ಕೆಗಳ ಮರೆಯನ್ನು ಆಶ್ರಯಿಸಿಕೊಳ್ಳುವಂತೆ ಅನುಗ್ರಹಿಸು. ಸೆಲಾ.",
			"5": "<sup>5</sup> ದೇವರೇ, ನೀನು ನನ್ನ ಹರಕೆಗಳಿಗೆ ಲಕ್ಷ್ಯಕೊಟ್ಟಿದ್ದೀ; ನಿನ್ನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಿಗೆ ಸಿಕ್ಕತಕ್ಕ ಬಾಧ್ಯತೆಯನ್ನು ನನಗೂ ದಯಪಾಲಿಸಿದ್ದೀ.",
			"6": "<sup>6</sup> ಅರಸನಿಗೆ ದೀರ್ಘಕಾಲದ ಬಾಳಿಕೆಯನ್ನು ಅನುಗ್ರಹಿಸು; ಅವನ ಆಯುಸ್ಸು ಅನೇಕ ತಲೆಗಳವರೆಗೆ ವೃದ್ಧಿಯಾಗಲಿ.",
			"7": "<sup>7</sup> ಅವನು ನಿನ್ನ ಸಾನ್ನಿಧ್ಯವನ್ನು ಪಡೆದು ಸದಾಕಾಲವೂ ಸಿಂಹಾಸನಾರೂಢನಾಗಿರಲಿ; ನಿನ್ನ ಪ್ರೇಮಸತ್ಯತೆಗಳು ಅವನ ಬೆಂಗಾವಲಾಗಿರಲಿ.",
			"8": "<sup>8</sup> ಹೀಗಾದರೆ ನಾನು ಪ್ರತಿದಿನವೂ ನನ್ನ ಹರಕೆಗಳನ್ನು ಸಲ್ಲಿಸುವವನಾಗಿ ನಿನ್ನ ನಾಮವನ್ನು ಸದಾ ಸ್ಮರಿಸಿ ಕೀರ್ತಿಸುತ್ತಿರುವೆನು."
		},
		"62": {
			"1": "<sup>1</sup> ನನ್ನ ಮನಸ್ಸು ದೇವರನ್ನೇ ನಂಬಿ ಶಾಂತವಾಗಿರುವದು. ನನ್ನ ರಕ್ಷಣೆಯು ಆತನಿಂದಲೇ.",
			"2": "<sup>2</sup> ಆತನೇ ನನಗೆ ಶರಣನೂ ರಕ್ಷಕನೂ ದುರ್ಗವೂ; ನಾನು ಕದಲಿದರೂ ಬೀಳೆನು.",
			"3": "<sup>3</sup> ನೀವೆಲ್ಲರೂ ಒಬ್ಬ ಪುರುಷನ ಮೇಲೆ ಬಿದ್ದು ಅವನು ಬಾಗಿದ ಗೋಡೆಯೋ ಕುಸಿದ ಪ್ರಾಕಾರವೋ ಎಂಬಂತೆ ಅವನನ್ನು ಹೊಡೆದು ಕೆಡವಬೇಕೆಂದಿರುವದು ಇನ್ನೆಷ್ಟರವರೆಗೆ?",
			"4": "<sup>4</sup> ಅವನನ್ನು ಉನ್ನತಸ್ಥಾನದಿಂದ ದೊಬ್ಬುವದೇ ಅವರ ಆಲೋಚನೆ. ಸುಳ್ಳಾಡುವದು ಅವರಿಗೆ ಅತಿಸಂತೋಷ; ಬಾಯಿಂದ ಹರಸಿ ಮನಸ್ಸಿನಿಂದ ಶಪಿಸುತ್ತಾರೆ. ಸೆಲಾ.",
			"5": "<sup>5</sup> ನನ್ನ ಮನವೇ, ದೇವರನ್ನೇ ನಂಬಿ ಶಾಂತವಾಗಿರು. ನನ್ನ ನಿರೀಕ್ಷೆಯು ನೆರವೇರುವದು ಆತನಿಂದಲೇ.",
			"6": "<sup>6</sup> ಆತನೇ ನನಗೆ ಶರಣನೂ ರಕ್ಷಕನೂ ದುರ್ಗವೂ; ನಾನು ಕದಲುವದಿಲ್ಲ.",
			"7": "<sup>7</sup> ನನ್ನ ರಕ್ಷಣೆಗೂ ಮಾನಕ್ಕೂ ದೇವರೇ ಆಧಾರ; ನನಗೆ ಬಲವಾದ ದುರ್ಗವೂ ಆಶ್ರಯವೂ ದೇವರಲ್ಲಿಯೇ.",
			"8": "<sup>8</sup> ಜನರೇ, ಯಾವಾಗಲೂ ಆತನನ್ನೇ ನಂಬಿ ನಿಮ್ಮ ಹೃದಯವನ್ನು ಆತನ ಮುಂದೆ ಬಿಚ್ಚಿರಿ; ದೇವರು ನಮ್ಮ ಆಶ್ರಯವು. ಸೆಲಾ.",
			"9": "<sup>9</sup> ನರರು ಬರೀ ಉಸಿರೇ; ನರಾಧಿಪತಿಗಳು ಬರೀ ಮಾಯವೇ. ಅವರೆಲ್ಲರನ್ನು ತ್ರಾಸಿನಲ್ಲಿ ತೂಗಿ ನೋಡಿದರೆ ಉಸಿರಿಗಿಂತಲೂ ಲಘು.",
			"10": "<sup>10</sup> ಅನ್ಯಾಯದಿಂದ ಸಂಪಾದಿಸಿದ್ದನ್ನು ನೆಚ್ಚಬೇಡಿರಿ; ಸುಲಿಗೆಯಿಂದ ಗಳಿಸಿ ಸೊಕ್ಕಿರಬೇಡಿರಿ; ಹೆಚ್ಚಿದ ಆಸ್ತಿಯಲ್ಲಿ ಮನಸ್ಸಿಡಬೇಡಿರಿ.",
			"11": "<sup>11</sup> ಸರ್ವಾಧಿಕಾರವು ದೇವರದೇ ಎಂದು ದೇವರು ಒಮ್ಮೆ ಅಲ್ಲ, ಎರಡಾವರ್ತಿ ಹೇಳಿದ್ದನ್ನು ಕೇಳಿದ್ದೇನೆ.",
			"12": "<sup>12</sup> ಕರ್ತನೇ, ನೀನು ಪ್ರೀತಿಸ್ವರೂಪನಾಗಿ ಪ್ರತಿಯೊಬ್ಬನಿಗೆ ಅವನವನ ಕೃತ್ಯಗಳಿಗೆ ತಕ್ಕ ಪ್ರತಿಫಲವನ್ನು ಕೊಡುವಂಥವನಲ್ಲವೇ."
		},
		"63": {
			"1": "<sup>1</sup> ದೇವರೇ, ನೀನೇ ನನ್ನ ದೇವರು; ನಾನು ನಿನ್ನ ದರ್ಶನವನ್ನು ಕುತೂಹಲದಿಂದ ಎದುರುನೋಡುತ್ತೇನೆ. ನೀರಿಲ್ಲದೆ ಒಣಗಿದ ಭೂಮಿಯಲ್ಲಿದ್ದವನು ನೀರಿಗಾಗಿಯೋ ಎಂಬಂತೆ ನನ್ನ ಆತ್ಮವು ನಿನಗಾಗಿ ಆತುರಗೊಳ್ಳುತ್ತದೆ; ಶರೀರವು ಕಂದಿಹೋಗುತ್ತದೆ.",
			"2": "<sup>2</sup> ನಿನ್ನ ಮಂದಿರದಲ್ಲಿ ನಾನು ನಿನ್ನ ಮಹತ್ತನ್ನೂ ಪ್ರಭಾವವನ್ನೂ ಕಂಡ ಪ್ರಕಾರ ಈಗಲೂ ಕಾಣಬೇಕೆಂದು ಅಪೇಕ್ಷಿಸುತ್ತೇನೆ.",
			"3": "<sup>3</sup> ನಿನ್ನ ಪ್ರೇಮಾನುಭವವು ಜೀವಕ್ಕಿಂತಲೂ ಶ್ರೇಷ್ಠ; ನನ್ನ ಬಾಯಿ ನಿನ್ನನ್ನು ಕೀರ್ತಿಸುವದು.",
			"4": "<sup>4</sup> ನನ್ನ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ಹೀಗೆಯೇ ನಿನ್ನನ್ನು ಹಾಡಿಹರಸುತ್ತಾ ನಿನ್ನ ಹೆಸರೆತ್ತಿ ಕೈಮುಗಿಯುವೆನು.",
			"5": "<sup>5</sup> ಮೃಷ್ಟಭೋಜನದಿಂದಲೋ ಎಂಬಂತೆ ನನ್ನ ಮನಸ್ಸು ಸಂತುಷ್ಟವಾಗಿರುವದು; ನನ್ನ ಬಾಯಿ ಉತ್ಸಾಹಧ್ವನಿಮಾಡುತ್ತಾ ನಿನ್ನನ್ನು ಕೊಂಡಾಡುವದು.",
			"6": "<sup>6</sup> ನಾನು ಹಾಸಿಗೆಯ ಮೇಲಿದ್ದುಕೊಂಡು ನಿನ್ನನ್ನು ಸ್ಮರಿಸುವಾಗ ರಾತ್ರಿಯ ಜಾವಗಳಲ್ಲಿ ನಿನ್ನನ್ನು ಧ್ಯಾನಿಸುತ್ತಿರುವೆನು.",
			"7": "<sup>7</sup> ನೀನು ನನಗೆ ಸಹಾಯಕನಾಗಿದ್ದಿಯಲ್ಲಾ; ನಿನ್ನ ರೆಕ್ಕೆಗಳ ಮರೆಯಲ್ಲಿ ಸುರಕ್ಷಿತನಾಗಿದ್ದುಕೊಂಡು ಆನಂದಘೋಷ ಮಾಡುತ್ತಿರುವೆನು.",
			"8": "<sup>8</sup> ನನ್ನ ಆತ್ಮವು ನಿನ್ನನ್ನು ಅಂಟಿಕೊಂಡು ಹಿಂಬಾಲಿಸಿರುವದು; ನಿನ್ನ ಬಲಗೈ ನನಗೆ ಆಧಾರವಾಗಿರುವದು.",
			"9": "<sup>9</sup> ನನ್ನ ಜೀವಕ್ಕೆ ಕೇಡುಬಗೆಯುವವರೋ ಅಧೋಲೋಕಕ್ಕೆ ಇಳಿದುಹೋಗುವರು.",
			"10": "<sup>10</sup> ಕತ್ತಿಗೆ ಬಲಿಯಾಗುವರು; ನರಿಗಳ ಪಾಲಾಗುವರು.",
			"11": "<sup>11</sup> ಅರಸನೋ ದೇವರಲ್ಲಿ ಆನಂದಿಸುವನು. ದೇವರ ಮೇಲೆ ಆಣೆಯಿಡುವವರೆಲ್ಲರು ಸುಳ್ಳುಬಾಯಿ ಮುಚ್ಚಿಹೋಗುವದನ್ನು ಕಂಡು ಹಿಗ್ಗುವರು."
		},
		"64": {
			"1": "<sup>1</sup> ದೇವರೇ, ನನ್ನ ಆರ್ತಸ್ವರವನ್ನು ಕೇಳಿ ಶತ್ರುಭಯದಿಂದ ನನ್ನ ಜೀವವನ್ನು ರಕ್ಷಿಸು.",
			"2": "<sup>2</sup> ದುಷ್ಟರ ಒಳಸಂಚಿಗೂ ಕೆಡುಕರ ದೊಂಬಿಗೂ ಸಿಕ್ಕದಂತೆ ನನ್ನನ್ನು ತಪ್ಪಿಸಿ ಭದ್ರಪಡಿಸು.",
			"3": "<sup>3</sup> ಅವರು ನಾಲಿಗೆಯೆಂಬ ಕತ್ತಿಯನ್ನು ಮಸೆದಿದ್ದಾರೆ; ವಿಷವಚನವೆಂಬ ಬಾಣವನ್ನು ಹೂಡಿ",
			"4": "<sup>4</sup> ಗುಪ್ತಸ್ಥಳಗಳಲ್ಲಿದ್ದು ಸಜ್ಜನನಿಗೆ ಗುರಿಯಿಟ್ಟಿದ್ದಾರೆ. ಸ್ವಲ್ಪವೂ ಹೆದರದೆ ಅವನ ಮೇಲೆ ಫಕ್ಕನೆ ಎಸೆಯುತ್ತಾರೆ.",
			"5": "<sup>5</sup> ದುಷ್ಕೃತ್ಯಕ್ಕಾಗಿ ಮನಸ್ಸನ್ನು ದೃಢಪಡಿಸಿಕೊಂಡು ತಮ್ಮೊಳಗೆ - ರಹಸ್ಯವಾಗಿ ಬಲೆಗಳನ್ನು ಒಡ್ಡೋಣ; ನಮ್ಮನ್ನು ನೋಡುವವರು ಯಾರು ಅಂದುಕೊಳ್ಳುತ್ತಾರೆ.",
			"6": "<sup>6</sup> ಅವರು ಕೆಡುಕನ್ನು ಕಲ್ಪಿಸಿ ಒಳ್ಳೇ ಉಪಾಯವನ್ನು ಕಂಡುಕೊಂಡಿದ್ದೇವೆ ಅಂದುಕೊಳ್ಳುತ್ತಾರೆ; ಅವರ ಹೃದಯವೂ ಅಂತರಂಗದ ಆಲೋಚನೆಯೂ ಅಶೋಧ್ಯವಾಗಿವೆ.",
			"7": "<sup>7</sup> ಆದರೆ ದೇವರು ಬಾಣವನ್ನು ಎಸೆಯಲು ಫಕ್ಕನೆ ಅವರಿಗೆ ಗಾಯವಾಗುವದು.",
			"8": "<sup>8</sup> ಅವರ ನಾಲಿಗೆಗಳೇ ಅವರಿಗೆ ವಿಘ್ನವಾಗಿ ಎಡವಿ ಬೀಳುವರು; ಆಗ ನೋಡುವವರೆಲ್ಲರು ತಲೆ ಅಲ್ಲಾಡಿಸಿ ಅಣಕಿಸುವರು.",
			"9": "<sup>9</sup> ಎಲ್ಲಾ ಮನುಷ್ಯರು ಭಯಪಟ್ಟು ದೇವರ ಕೆಲಸವೆಂದು ಹೇಳಿ ಆತನ ಕೃತ್ಯಗಳನ್ನು ಆಲೋಚಿಸಿಕೊಳ್ಳುವರು.",
			"10": "<sup>10</sup> ಸದ್ಭಕ್ತರು ಯೆಹೋವನಲ್ಲಿ ಆನಂದಪಟ್ಟು ಆತನನ್ನೇ ಆಶ್ರಯಿಸಿಕೊಳ್ಳುವರು; ಸರಳ ಹೃದಯರೆಲ್ಲರೂ ಹಿಗ್ಗುವರು."
		},
		"65": {
			"1": "<sup>1</sup> ದೇವರೇ, ಚೀಯೋನಿನಲ್ಲಿ ನಿನಗೋಸ್ಕರ ಸ್ತೋತ್ರವು ಸಿದ್ಧವಾಗಿದೆ; ಹರಕೆಗಳು ನಿನಗೆ ಸಲ್ಲುತ್ತವೆ.",
			"2": "<sup>2</sup> ಪ್ರಾರ್ಥನೆಯನ್ನು ಕೇಳುವವನೇ, ನರರೆಲ್ಲರು ನಿನ್ನ ಬಳಿಗೆ ಬರುವರು.",
			"3": "<sup>3</sup> ನನ್ನ ಪಾಪಗಳನ್ನು ನಿವಾರಿಸಲು ನನ್ನಿಂದಾಗುವದಿಲ್ಲ. ಆದರೆ ನಮ್ಮ ದೋಷಪರಿಹಾರಕನು ನೀನೇ.",
			"4": "<sup>4</sup> ನಿನ್ನ ಅಂಗಳದಲ್ಲಿ ವಾಸಿಸುವವರಾಗಿ ನಿನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸೇವೆಮಾಡುವದಕ್ಕೋಸ್ಕರ ಯಾರನ್ನು ಆರಿಸಿಕೊಳ್ಳುತ್ತೀಯೋ ಅವರೇ ಧನ್ಯರು. ನೀನು ವಾಸಿಸುವ ಮಹಾಪವಿತ್ರಾಲಯದ ಸೌಭಾಗ್ಯದಿಂದ ನಮಗೆ ಸಂತೃಪ್ತಿಯಾಗಲಿ.",
			"5": "<sup>5</sup> ನಮ್ಮ ರಕ್ಷಕನಾದ ದೇವರೇ, ನೀನು ಭಯಂಕರ ಮಹತ್ಕಾರ್ಯಗಳನ್ನು ನಡಿಸಿ ನಿನ್ನ ನೀತಿಗನುಸಾರವಾಗಿ ನಮಗೆ ಸದುತ್ತರವನ್ನು ದಯಪಾಲಿಸುವಿ. ಭೂಮಿಯ ಎಲ್ಲಾ ಕಡೆಯವರ, ಬಹುದೂರ ಸಮುದ್ರದ ಆಚೆ ಎಲ್ಲಾ ನಿವಾಸಿಗಳ ನಂಬಿಕೆಗೆ ಆಧಾರನು ನೀನೇ.",
			"6": "<sup>6</sup> ನೀನು ಶೌರ್ಯವೆಂಬ ನಡುಕಟ್ಟನ್ನು ಬಿಗಿದುಕೊಂಡು ಬಲದಿಂದ ಪರ್ವತಗಳನ್ನು ಸ್ಥಿರವಾಗಿ ನಿಲ್ಲಿಸಿದವನೂ",
			"7": "<sup>7</sup> ಸಮುದ್ರತರಂಗಗಳ ಘೋಷವನ್ನು ತಡೆಯುವವನೂ ಜನಾಂಗಗಳ ದೊಂಬಿಯನ್ನು ಶಾಂತಿಪಡಿಸುವವನೂ ಆಗಿದ್ದೀ.",
			"8": "<sup>8</sup> ಭೂಮಿಯ ಕಟ್ಟಕಡೆಗಳಲ್ಲಿ ವಾಸಿಸುವವರೂ ನಿನ್ನ ಅದ್ಭುತಕೃತ್ಯಗಳಿಗಾಗಿ ಭಯಪಡುತ್ತಾರೆ; ಮೂಡಣದಿಂದ ಪಡುವಣದವರೆಗೂ ಇರುವವರನ್ನು ಹರ್ಷಗೊಳಿಸುವಿ.",
			"9": "<sup>9</sup> ನೀನು ನಮ್ಮ ದೇಶವನ್ನು ಕಟಾಕ್ಷಿಸಿ ಅದರ ಮೇಲೆ ಮಳೆಸುರಿಸಿ ಚೆನ್ನಾಗಿ ಹದಗೊಳಿಸುತ್ತೀ; ದೇವರೇ, ನಿನ್ನ ಕಾಲುವೆಗಳಲ್ಲಿ ನೀರಿಗೆ ಕೊರತೆ ಇಲ್ಲ. ಹೀಗೆ ಭೂಮಿಯನ್ನು ಸಿದ್ಧಮಾಡಿ ಮನುಷ್ಯರಿಗೆ ಧಾನ್ಯವನ್ನು ಒದಗಿಸುತ್ತೀ.",
			"10": "<sup>10</sup> ನೇಗಿಲಗೆರೆಗಳನ್ನು ತ್ಯಾವಿಸಿ ಮಳೆಯಿಂದ ಅದರ ಹೆಂಟೆಗಳನ್ನು ಕರಗಿಸಿ ಸಮಮಾಡುತ್ತೀ; ಅದರ ಬೆಳೆಯನ್ನು ವೃದ್ಧಿಪಡಿಸುತ್ತೀ.",
			"11": "<sup>11</sup> ನಿನ್ನ ಕೃಪೆಯಿಂದ ಸಂವತ್ಸರಕ್ಕೆ ಸುಭಿಕ್ಷ ಕಿರೀಟವನ್ನು ಇಟ್ಟಿದ್ದೀ; ನೀನು ಹಾದುಹೋಗುವ ಮಾರ್ಗದಲ್ಲೆಲ್ಲಾ ಸಮೃದ್ಧಿಕರವಾದ ವೃಷ್ಟಿಯು ಸುರಿಯುವದು.",
			"12": "<sup>12</sup> ಮೇವುಗಾಡುಗಳು ಹಚ್ಚಗೆ ಕಂಗೊಳಿಸುತ್ತವೆ; ಗುಡ್ಡಗಳು ಹರ್ಷವೆಂಬ ನಡುಕಟ್ಟನ್ನು ಬಿಗಿದುಕೊಂಡಂತಿವೆ.",
			"13": "<sup>13</sup> ಹುಲ್ಲುಗಾವಲುಗಳು ಕುರಿಹಿಂಡುಗಳೆಂಬ ವಸ್ತ್ರವನ್ನು ಹೊದೆದುಕೊಂಡಿವೆ; ತಗ್ಗುಗಳು ಬೆಳೆಯಿಂದ ಶೋಭಿಸುತ್ತವೆ; ಅವು ಆನಂದಘೋಷಮಾಡಿ ಹಾಡುತ್ತವೋ ಎಂಬಂತಿವೆ."
		},
		"66": {
			"1": "<sup>1</sup> ಸರ್ವಭೂನಿವಾಸಿಗಳೇ, ದೇವರಿಗೆ ಜಯಧ್ವನಿ ಮಾಡಿರಿ.",
			"2": "<sup>2</sup> ಆತನ ನಾಮದ ಮಹತ್ತನ್ನು ಕೀರ್ತಿಸಿರಿ; ಆತನ ಪ್ರಭಾವವನ್ನು ವರ್ಣಿಸುತ್ತಾ ಕೊಂಡಾಡಿರಿ.",
			"3": "<sup>3</sup> ನೀವು ದೇವರಿಗೆ - ನಿನ್ನ ಕೃತ್ಯಗಳು ಎಷ್ಟೋ ಭಯಂಕರವಾಗಿವೆ; ನಿನ್ನ ಪರಾಕ್ರಮದ ಮಹತ್ತಿನ ದೆಸೆಯಿಂದ ನಿನ್ನ ಶತ್ರುಗಳು ನಿನ್ನ ಮುಂದೆ ಮುದುರಿಕೊಳ್ಳುವರು;",
			"4": "<sup>4</sup> ಸರ್ವಭೂನಿವಾಸಿಗಳು ನಿನಗೆ ಎರಗಿ ಭಜಿಸುತ್ತಾ ನಿನ್ನ ನಾಮವನ್ನು ಕೀರ್ತಿಸುವರು ಎಂದು ಬಿನ್ನೈಸಿರಿ. ಸೆಲಾ.",
			"5": "<sup>5</sup> ಬನ್ನಿರಿ, ದೇವರ ಕಾರ್ಯಗಳನ್ನು ನೋಡಿರಿ; ಆತನ ಆಳಿಕೆ ನರರಲ್ಲಿ ಭಯಹುಟ್ಟಿಸತಕ್ಕದ್ದಾಗಿದೆ.",
			"6": "<sup>6</sup> ಸಮುದ್ರವನ್ನು ಒಣನೆಲವಾಗುವಂತೆ ಮಾಡಿದನು; ಜನರು ನದಿಯನ್ನು ಕಾಲಿನಿಂದ ದಾಟಿದರು; ಅದಕ್ಕಾಗಿ ನಾವು ಆತನಲ್ಲಿ ಆನಂದಪಡೋಣ.",
			"7": "<sup>7</sup> ಆತನು ಪರಾಕ್ರಮದಿಂದ ಸದಾಕಾಲವೂ ಆಳುತ್ತಾನೆ; ಜನಾಂಗಗಳನ್ನು ನೋಡಿಕೊಳ್ಳುತ್ತಾನೆ. ದಂಗೆಖೋರರು ಏಳದಿರಲಿ. ಸೆಲಾ.",
			"8": "<sup>8</sup> ಜನಾಂಗಗಳೇ, ನಮ್ಮ ದೇವರನ್ನು ವಂದಿಸಿರಿ; ಆತನನ್ನು ಸ್ತುತಿಸುವ ಧ್ವನಿ ಕೇಳಿಸಲಿ.",
			"9": "<sup>9</sup> ಆತನು ನಮ್ಮನ್ನು ಜೀವದಿಂದುಳಿಸಿ ನಮ್ಮ ಕಾಲುಗಳನ್ನು ಜಾರದಂತೆ ಕಾಪಾಡಿದನು.",
			"10": "<sup>10</sup> ದೇವರೇ, ನಮ್ಮನ್ನು ಶೋಧಿಸಿದಿ; ಬೆಳ್ಳಿಯನ್ನು ಪುಟಕ್ಕೆ ಹಾಕುವ ಮೇರೆಗೆ ಶುದ್ಧಿಮಾಡಿದಿ.",
			"11": "<sup>11</sup> ನಮ್ಮನ್ನು ಬಲೆಯಲ್ಲಿ ಸಿಕ್ಕಿಸಿದಿ; ನಮ್ಮ ಸೊಂಟಕ್ಕೆ ಭಾರವಾದ ಕೊರಡನ್ನು ಕಟ್ಟಿದಿ;",
			"12": "<sup>12</sup> ಮನುಷ್ಯರು ನಮ್ಮ ತಲೆಗಳ ಮೇಲೆಯೇ ತಮ್ಮ ರಥಗಳನ್ನು ಹಾಯಿಸುವಂತೆ ಮಾಡಿದಿ. ನಾವು ಬೆಂಕಿಯನ್ನೂ ನೀರನ್ನೂ ದಾಟಬೇಕಾಯಿತು; ಆದರೂ ಸುಸ್ಥಿತಿಗೆ ನಮ್ಮನ್ನು ಪಾರುಗಾಣಿಸಿದ್ದೀ.",
			"13": "<sup>13</sup> ನಾನು ನಿನ್ನ ಆಲಯಕ್ಕೆ ಬಂದು ಸರ್ವಾಂಗ ಹೋಮಗಳನ್ನು ಸಮರ್ಪಿಸುವೆನು.",
			"14": "<sup>14</sup> ಇಕ್ಕಟ್ಟಿನ ವೇಳೆಯಲ್ಲಿ ನನ್ನ ತುಟಿಗಳು ಬಿಚ್ಚಿ ಬಾಯಿ ಉಚ್ಚರಿಸಿದ ಹರಕೆಗಳನ್ನು ನಿನಗೆ ಸಲ್ಲಿಸುವೆನು.",
			"15": "<sup>15</sup> ನಿನಗೆ ಟಗರು ಮುಂತಾದ ಪುಷ್ಟಪಶುಗಳನ್ನು ಯಜ್ಞರೂಪವಾಗಿ ಸಮರ್ಪಿಸಿ ಸುವಾಸನೆಯನ್ನುಂಟುಮಾಡುವೆನು; ಯಜ್ಞಕ್ಕಾಗಿ ಹೋತಹೋರಿಗಳನ್ನು ತರುವೆನು. ಸೆಲಾ.",
			"16": "<sup>16</sup> ಎಲ್ಲಾ ದೇವಭಕ್ತರೇ, ಬಂದು ಕೇಳಿರಿ; ಆತನು ನನಗಾಗಿ ಮಾಡಿದ್ದೆಲ್ಲವನ್ನು ನಿಮಗೆ ತಿಳಿಸುವೆನು.",
			"17": "<sup>17</sup> ನನ್ನ ಮೊರೆಯೊಡನೆ ಕೃತಜ್ಞತಾಸ್ತುತಿಯೂ ನನ್ನ ನಾಲಿಗೆಯ ಮೇಲಿತ್ತು.",
			"18": "<sup>18</sup> ನಾನು ಕೆಟ್ಟತನದ ಮೇಲೆ ಮನಸ್ಸಿಟ್ಟಿದ್ದರೆ ಸ್ವಾಮಿಯು ನನ್ನ ವಿಜ್ಞಾಪನೆಯನ್ನು ಕೇಳುತ್ತಿದ್ದಿಲ್ಲ.",
			"19": "<sup>19</sup> ಆದರೆ ದೇವರು ನನ್ನ ಮೊರೆಯನ್ನು ಲಕ್ಷಿಸಿ ಕೇಳಿದ್ದಾನಲ್ಲಾ.",
			"20": "<sup>20</sup> ಆತನು ನನ್ನ ಬಿನ್ನಹವನ್ನು ತಿರಸ್ಕರಿಸಲಿಲ್ಲ; ನನ್ನ ಮೇಲಿನ ತನ್ನ ದಯೆಯನ್ನು ತಪ್ಪಿಸಿಬಿಡಲಿಲ್ಲ. ದೇವರಿಗೆ ಸ್ತೋತ್ರವಾಗಲಿ."
		},
		"67": {
			"1": "<sup>1</sup> ದೇವರೇ, ನಮ್ಮನ್ನು ಕಟಾಕ್ಷಿಸಿ ಆಶೀರ್ವದಿಸು; ಪ್ರಸನ್ನ ಮುಖದಿಂದ ನಮ್ಮನ್ನು ನೋಡು. ಸೆಲಾ.",
			"2": "<sup>2</sup> ಇದರಿಂದ ಭೂಲೋಕದಲ್ಲಿ ನಿನ್ನ ಪರಿಪಾಲನ ಮಾರ್ಗವೂ ಎಲ್ಲಾ ಜನಾಂಗಗಳಲ್ಲಿ ನಿನ್ನ ರಕ್ಷಣೆಯೂ ಪ್ರಸಿದ್ಧವಾಗುವವು.",
			"3": "<sup>3</sup> ದೇವರೇ, ಜನಾಂಗಗಳು ನಿನ್ನನ್ನು ಕೀರ್ತಿಸಲಿ; ಸರ್ವಜನಾಂಗಗಳು ನಿನ್ನನ್ನು ಕೀರ್ತಿಸಲಿ.",
			"4": "<sup>4</sup> ನೀನು ಸಮಸ್ತ ದೇಶಗಳವರನ್ನು ನೀತಿಯಿಂದ ಆಳಿ ಭೂಪ್ರಜೆಗಳನ್ನೆಲ್ಲಾ ನಡಿಸುವಾತನಾಗಿರುವದರಿಂದ ಜನಾಂಗಗಳು ಹರ್ಷಿಸಿ ಆನಂದಘೋಷ ಮಾಡಲಿ. ಸೆಲಾ.",
			"5": "<sup>5</sup> ದೇವರೇ, ಜನಾಂಗಗಳು ನಿನ್ನನ್ನು ಕೀರ್ತಿಸಲಿ; ಸರ್ವಜನಾಂಗಗಳು ನಿನ್ನನ್ನು ಕೀರ್ತಿಸಲಿ.",
			"6": "<sup>6</sup> ಭೂಮಿಯು ಒಳ್ಳೇ ಬೆಳೆಯನ್ನು ಕೊಟ್ಟಿರುತ್ತದೆ. ದೇವರು, ನಮ್ಮ ದೇವರೇ ನಮ್ಮನ್ನು ಆಶೀರ್ವದಿಸುವನು;",
			"7": "<sup>7</sup> ಆತನು ನಮ್ಮನ್ನು ಆಶೀರ್ವದಿಸುವನು. ಭೂಮಂಡಲದವರೆಲ್ಲರೂ ಆತನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿರುವರು."
		},
		"68": {
			"1": "<sup>1</sup> ದೇವರು ಎದ್ದು ಹೊರಡುವಾಗ ಆತನ ವೈರಿಗಳು ಚದರಿಹೋಗುವರು; ಆತನ ಹಗೆಗಾರರು ಬೆಂಗೊಟ್ಟು ಓಡಿಹೋಗುವರು.",
			"2": "<sup>2</sup> ಹೊಗೆಯು [ಗಾಳಿಯಿಂದ] ಹೇಗೋ ಹಾಗೆ ಅವರು ಆತನಿಂದ ಹಾರಿಹೋಗುವರು; ಬೆಂಕಿಯ ಮುಂದೆ ಮೇಣವು ಕರಗಿ ಲಯವಾಗಿ ಹೋಗುವಂತೆ ದೇವರ ಎದುರಿನಲ್ಲಿ ದುಷ್ಟರು ನಾಶವಾಗಿ ಹೋಗುವರು.",
			"3": "<sup>3</sup> ನೀತಿವಂತರಾದರೋ ದೇವರ ಸನ್ನಿಧಿಯಲ್ಲಿ ಹರ್ಷಿಸುವರು; ಉಲ್ಲಾಸಿಸುವರು, ಆನಂದಧ್ವನಿಮಾಡುವರು.",
			"4": "<sup>4</sup> ದೇವರಿಗೆ ಗಾಯನಮಾಡಿರಿ; ಆತನ ನಾಮವನ್ನು ಭಜಿಸಿರಿ; ಅರಣ್ಯದಲ್ಲಿ ಸವಾರಿಮಾಡುತ್ತಾ ಬರುವಾತನಿಗೆ ರಾಜಮಾರ್ಗವನ್ನು ಸಿದ್ಧಮಾಡಿರಿ. ಆತನ ನಾಮಧೇಯ ಯಾಹು; ಆತನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಉಲ್ಲಾಸಿಸಿರಿ.",
			"5": "<sup>5</sup> ಪರಿಶುದ್ಧ ವಾಸಸ್ಥಾನದಲ್ಲಿರುವ ದೇವರು ದಿಕ್ಕಿಲ್ಲದವರಿಗೆ ತಂದೆಯೂ ವಿಧವೆಯರಿಗೆ ಸಹಾಯಕನೂ ಆಗಿದ್ದಾನೆ.",
			"6": "<sup>6</sup> ಒಬ್ಬೊಂಟಿಗರನ್ನು ಸಂಸಾರಿಕರಾಗುವಂತೆ ಮಾಡುತ್ತಾನೆ; ಸೆರೆಯಲ್ಲಿರುವವರನ್ನು ಬಿಡಿಸಿ ಸುಖಾವಸ್ಥೆಗೆ ತರುತ್ತಾನೆ. ದ್ರೋಹಿಗಳಾದರೋ ಮರುಭೂಮಿಯಲ್ಲಿ ಉಳಿಯಬೇಕಾಗುವದು.",
			"7": "<sup>7</sup> ದೇವರೇ, ನೀನು ನಿನ್ನ ಪ್ರಜೆಯ ಮುಂಗಡೆಯಲ್ಲಿ ಹೊರಟು ಅರಣ್ಯಮಾರ್ಗವಾಗಿ ಪಯಣಮಾಡುತ್ತಾ ಬರುವಾಗ ಸೆಲಾ.",
			"8": "<sup>8</sup> ದೇವರು ಪ್ರತ್ಯಕ್ಷನಾಗಿದ್ದಾನೆಂದು ಭೂಮಿಯು ಕಂಪಿಸಿತು; ಮೇಘಮಂಡಲವು ಮಳೆಗರಿಯಿತು. ಇಸ್ರಾಯೇಲ್ಯರ ದೇವನಾದ ದೇವರು ಪ್ರತ್ಯಕ್ಷನಾಗಿದ್ದಾನೆಂದು ಆ ಸೀನಾಯಿಬೆಟ್ಟವು ಕದಲಿತು.",
			"9": "<sup>9</sup> ದೇವರೇ, ನೀನು ಹೇರಳ ಮಳೆಸುರಿಸಿ ಬಾಯ್ದೆರೆದಿದ್ದ ನಿನ್ನ ಸ್ವಾಸ್ತ್ಯವನ್ನು ಶಾಂತಿಪಡಿಸಿದಿ.",
			"10": "<sup>10</sup> ನಿನ್ನ ಪ್ರಜಾಮಂಡಲಿಯು ಅದರಲ್ಲಿ ವಾಸಿಸಿಕೊಂಡಿತು. ದೇವರೇ, ನೀನು ದಯಾಪರನಾಗಿ ದರಿದ್ರರಿಗೆ ಬೇಕಾದದ್ದೆಲ್ಲವನ್ನು ಸವರಿಸಿ ಕೊಟ್ಟಿ.",
			"11": "<sup>11</sup> ಕರ್ತನು ನುಡಿದನು; ಶುಭವಾರ್ತೆಯನ್ನು ಪ್ರಸಿದ್ಧಪಡಿಸುವ ಸ್ತ್ರೀ ಸಮೂಹವು ಎಷ್ಟೋ ದೊಡ್ಡದು.",
			"12": "<sup>12</sup> ಓಡಿ ಹೋಗುತ್ತಾರೆ; ಸೈನ್ಯದೊಡನೆ ಅರಸುಗಳು ಓಡಿ ಹೋಗುತ್ತಾರೆ; ಮನೆಯಲ್ಲಿದ್ದ ಸ್ತ್ರೀಯರು ಕೊಳ್ಳೆಯನ್ನು ಹಂಚುತ್ತಾರೆ.",
			"13": "<sup>13</sup> ನೀವು ಕುರಿಹಟ್ಟಿಗಳಲ್ಲಿ ಮಲಗಿಕೊಂಡಿರುವದೇನು? ಪಾರಿವಾಳದ ರೆಕ್ಕೆಗಳು ಬೆಳ್ಳಿಯಿಂದಲೂ ಅದರ ಗರಿಗಳು ಬಂಗಾರದಿಂದಲೂ ಥಳಥಳಿಸುತ್ತವೆ.",
			"14": "<sup>14</sup> ಸರ್ವಶಕ್ತನು ಅಲ್ಲಿ ರಾಜರನ್ನು ಚದರಿಸಿದಾಗ ಸಲ್ಮೋನಿನ ಮೇಲೆ ಹಿಮವು ಬಿದ್ದಿತು.",
			"15": "<sup>15</sup> ಬಾಷಾನಿನ ಪರ್ವತವು ಮಹೋನ್ನತವಾಗಿದೆ; ಆ ಬಾಷಾನ್ ಗಿರಿಯು ಶಿಖರಗಳುಳ್ಳದ್ದೇ.",
			"16": "<sup>16</sup> ಎಲೈ ಶಿಖರೋನ್ನತಪರ್ವತಗಳೇ, ದೇವರು ತನ್ನ ನಿವಾಸಾರ್ಥವಾಗಿ ಆರಿಸಿಕೊಂಡಿರುವ ಈ ಪರ್ವತವನ್ನು ನೀವು ಓರೆಗಣ್ಣಿನಿಂದ ನೋಡುವದೇಕೆ? ಯೆಹೋವನು ಸದಾಕಾಲವೂ ಇದರಲ್ಲೇ ವಾಸಿಸುವನು.",
			"17": "<sup>17</sup> ದೇವರಿಗೆ ಸಹಸ್ರಾರು ಮಾತ್ರವಲ್ಲ, ಲಕ್ಷಾಂತರ ರಥಗಳು ಇವೆ. ಕರ್ತನಾದ ಯೆಹೋವನು ಅವುಗಳ ಸಮೇತವಾಗಿ ಸೀನಾಯಿಬೆಟ್ಟದಲ್ಲಿದ್ದ ಹಾಗೆ ಪವಿತ್ರಾಲಯದಲ್ಲಿದ್ದಾನೆ.",
			"18": "<sup>18</sup> ನೀನು ಜಯಿಸಿದ ಬಹು ಜನರನ್ನು ಸೆರೆಹಿಡುಕೊಂಡು ಹೋಗಿ ನಿನಗೆ ದ್ರೋಹಿಗಳಾದ ಮನುಷ್ಯರಿಂದಲೇ ಕಪ್ಪಗಳನ್ನು ಸಂಗ್ರಹಿಸಿ ಉನ್ನತಸ್ಥಾನಕ್ಕೆ ಏರಿದ್ದೀ. ದೇವನಾದ ಯಾಹುವೇ, ಅಲ್ಲೇ ವಾಸಿಸುವಿ.",
			"19": "<sup>19</sup> ಅನುದಿನವೂ ನಮ್ಮ ಭಾರವನ್ನು ಹೊರುವ ಕರ್ತನಿಗೆ ಸ್ತೋತ್ರವಾಗಲಿ. ನಮ್ಮನ್ನು ರಕ್ಷಿಸುವ ದೇವರು ಆತನೇ. ಸೆಲಾ.",
			"20": "<sup>20</sup> ನಮ್ಮ ದೇವರು ನಮ್ಮನ್ನು ವಿಮೋಚಿಸುವದಕ್ಕೋಸ್ಕರ ದೇವರಾಗಿದ್ದಾನೆ; ಕರ್ತನಾದ ಯೆಹೋವನು ಮರಣಕ್ಕೆ ತಪ್ಪಿಸ ಶಕ್ತನಾಗಿದ್ದಾನೆ.",
			"21": "<sup>21</sup> ಆಹಾ, ಆತನು ತನ್ನ ವೈರಿಗಳ ಶಿರಸ್ಸುಗಳನ್ನೂ ಸ್ವೇಚ್ಫೆಯಿಂದ ಪಾಪದಲ್ಲಿ ಪ್ರವರ್ತಿಸುವವರ ತುಂಬುಗೂದಲಿನ ತಲೆಗಳನ್ನೂ ಒಡೆದು ಬಿಡುವನು.",
			"22": "<sup>22</sup> ಕರ್ತನು - ನಾನು ಅವರನ್ನು ಬಾಷಾನಿನಿಂದಲೂ ಸಮುದ್ರತಲದಿಂದಲೂ ಹಿಡಿದುತರುವೆನು.",
			"23": "<sup>23</sup> ಆಗ ನೀನು ನಿನ್ನ ಶತ್ರುಗಳ ರಕ್ತದಲ್ಲಿ ಕಾಲಾಡಿಸುವಿ. ನಿನ್ನ ನಾಯಿಗಳ ನಾಲಿಗೆಗಳಿಗೆ ವೈರಿಗಳ ದೇಹದಲ್ಲಿ ಪಾಲುಸಿಕ್ಕುವದು ಎಂದು ನುಡಿದನು.",
			"24": "<sup>24</sup> ದೇವರೇ, ನಿನ್ನ ಮೆರವಣಿಗೆ ಶೋಭಿಸುತ್ತದೆ; ನನ್ನ ಅರಸನಾದ ದೇವರು ತನ್ನ ಪರಿಶುದ್ಧಾಲಯಕ್ಕೆ ಮೆರವಣಿಗೆಯಾಗಿ ಚಿತ್ತೈಸುತ್ತಾನೆ.",
			"25": "<sup>25</sup> ಮುಂಗಡೆಯಲ್ಲಿ ಹಾಡುವವರೂ ಹಿಂಗಡೆಯಲ್ಲಿ ವಾದ್ಯಬಾರಿಸುವವರೂ ಸುತ್ತಲೂ ದಮ್ಮಡಿಬಡಿಯುವ ಸ್ತ್ರೀಯರೂ ಹೋಗುತ್ತಾ -",
			"26": "<sup>26</sup> ಇಸ್ರಾಯೇಲ್‍ಸಂಭವರೇ, ಸಮ್ಮೇಳವಾಗಿ ದೇವನಾದ ಕರ್ತನನ್ನು ಕೊಂಡಾಡಿರಿ ಎಂದು ಹಾಡುತ್ತಾರೆ.",
			"27": "<sup>27</sup> ಅಲ್ಲಿ ಎಲ್ಲರಿಗೆ ನಾಯಕರಾಗಿರುವ ಬೆನ್ಯಾಮೀನೆಂಬ ಸಣ್ಣ ಗೋತ್ರದವರೂ ಗುಂಪಾಗಿ ಬರುವ ಯೆಹೂದಕುಲ ಪ್ರಭುಗಳೂ ಜೆಬುಲೂನ ನಫ್ತಾಲಿ ಗೋತ್ರಗಳ ಪ್ರಧಾನರೂ ಇದ್ದಾರೆ.",
			"28": "<sup>28</sup> ದೇವರೇ, ನಿನ್ನ ಪ್ರತಾಪವನ್ನು ತೋರ್ಪಡಿಸು. ನಿನ್ನ ಮಂದಿರದಲ್ಲಿ ಆಸೀನನಾಗಿ ನಮಗೋಸ್ಕರ ಮಹತ್ಕಾರ್ಯಗಳನ್ನು ನಡಿಸಿದ ದೇವರೇ, ನಿನ್ನ ಬಲವನ್ನು ಪ್ರಕಟಿಸು.",
			"29": "<sup>29</sup> ಅರಸುಗಳು ಯೆರೂಸಲೇಮ್ ಪಟ್ಟಣಕ್ಕೆ ನಿನಗೋಸ್ಕರ ಕಾಣಿಕೆಗಳನ್ನು ತಂದು ಸಮರ್ಪಿಸಲಿ.",
			"30": "<sup>30</sup> ಆಪಿನೊಳಗೆ ವಾಸಿಸುವ ನೀರಾನೆ, ಕರುಗಳ ಸಹಿತವಾದ ಹೋರಿಗಳ ಗುಂಪು, ಇವುಗಳಂತಿರುವ ಶತ್ರುಜನಾಂಗಗಳನ್ನು ಬೆದರಿಸು. ಅವು ಬೆಳ್ಳೀಗಟ್ಟಿಗಳನ್ನು ತಂದು ನಿನಗೆ ಅಡ್ಡಬೀಳಲಿ. ಯುದ್ಧಾಸಕ್ತ ಜನಾಂಗಗಳನ್ನು ಚದರಿಸಿಬಿಡು.",
			"31": "<sup>31</sup> ಐಗುಪ್ತ ದೇಶದಿಂದ ರಾಯಭಾರಿಗಳು ಬರುವರು; ಕೂಷ್ ದೇಶದವರ ಕೈಗಳು ದೇವರಿಗೆ ಕಾಣಿಕೆಗಳನ್ನು ನೀಡಲಿಕ್ಕೆ ಅವಸರ ಪಡುವವು.",
			"32": "<sup>32</sup> ಭೂರಾಜ್ಯಗಳೇ, ದೇವರಿಗೆ ಗಾಯನಮಾಡಿರಿ; ಕರ್ತನನ್ನು ಸಂಕೀರ್ತಿಸಿರಿ. ಸೆಲಾ.",
			"33": "<sup>33</sup> ಅನಾದಿಕಾಲದಿಂದಿರುವ ಮಹೋನ್ನತಾಕಾಶದಲ್ಲಿ ವಾಹನಾರೂಢನಾಗಿರುವಾತನನ್ನು ಸ್ತುತಿಸಿರಿ. ಕೇಳಿರಿ; ಆತನ ಗರ್ಜನೆಯು ಮಹಾಘೋರವಾದದ್ದು.",
			"34": "<sup>34</sup> ದೇವರ ಪ್ರತಾಪವನ್ನು ಕೊಂಡಾಡಿರಿ. ಆತನ ಗಾಂಭೀರ್ಯವು ಇಸ್ರಾಯೇಲ್ಯರ ಆಶ್ರಯವಾಗಿದೆ; ಆತನ ಶಕ್ತಿಯು ಮೇಘಮಾರ್ಗದಲ್ಲೆಲ್ಲಾ ವ್ಯಾಪಿಸಿದೆ.",
			"35": "<sup>35</sup> ದೇವರೇ, ಪರಿಶುದ್ಧಾಲಯದಲ್ಲಿರುವ ನೀನು ಮಹಾಭಯಂಕರನು. ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರು ತನ್ನ ಪ್ರಜೆಗೆ ಬಲಪರಾಕ್ರಮಗಳನ್ನು ದಯಪಾಲಿಸುವನು. ದೇವರಿಗೆ ಸ್ತೋತ್ರ."
		},
		"69": {
			"1": "<sup>1</sup> ದೇವರೇ, ರಕ್ಷಿಸು; ಪ್ರಾಣಾಪಾಯವಾಗುವಷ್ಟು ಜಲವು ಏರಿಬಂದದೆ.",
			"2": "<sup>2</sup> ಆಳವಾದ ಕಳ್ಳುಸುಬಿನಲ್ಲಿ ಮುಳುಗುತ್ತಿದ್ದೇನೆ; ನೆಲೆ ಸಿಕ್ಕುವದಿಲ್ಲ. ದೊಡ್ಡ ಮಡುವಿನೊಳಕ್ಕೆ ಬಂದಿದ್ದೇನೆ; ಪ್ರವಾಹವು ನನ್ನನ್ನು ಬಡಕೊಂಡು ಹೋಗುತ್ತದೆ.",
			"3": "<sup>3</sup> ಮೊರೆಯಿಟ್ಟಿಟ್ಟು ಬೇಸತ್ತು ಹೋಗಿದ್ದೇನೆ; ಗಂಟಲು ಒಣಗಿಹೋಯಿತು. ನನ್ನ ದೇವರನ್ನು ನಿರೀಕ್ಷಿಸುತ್ತಾ ದೃಷ್ಟಿ ಮಂದವಾಯಿತು.",
			"4": "<sup>4</sup> ನಿಷ್ಕಾರಣದ್ವೇಷಿಗಳು ನನ್ನ ತಲೆಗೂದಲುಗಳಿಗಿಂತ ಹೆಚ್ಚಾಗಿದ್ದಾರೆ; ನಿರ್ನಿಮಿತ್ತವಾಗಿ ನನ್ನನ್ನು ಮುಗಿಸಿಬಿಡಬೇಕೆಂದಿರುವ ವೈರಿಗಳು ಬಲಿಷ್ಠರಾಗಿದ್ದಾರೆ. ನಾನು ಅಪಹರಿಸದಿದ್ದರೂ ನನ್ನಿಂದ ದಂಡತೆಗೆದುಕೊಂಡರಲ್ಲಾ.",
			"5": "<sup>5</sup> ದೇವರೇ, ನನ್ನ ಮೂರ್ಖತನವು ನಿನಗೆ ಗೊತ್ತು; ನನ್ನ ಅಪರಾಧಗಳು ನಿನಗೆ ಮರೆಯಾಗಿಲ್ಲ.",
			"6": "<sup>6</sup> ಸೇನಾಧೀಶ್ವರನೇ, ಯೆಹೋವನೇ, ನಿನ್ನನ್ನು ನಂಬಿದವರಿಗೆ ನನ್ನಿಂದ ನಿರಾಶೆಯಾಗದಿರಲಿ; ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರೇ, ನಿನ್ನ ದರ್ಶನಾರ್ಥಿಗಳು ನನ್ನಿಂದ ಅಪಮಾನಕ್ಕೆ ಗುರಿಯಾಗದಿರಲಿ.",
			"7": "<sup>7</sup> ನಿನಗೋಸ್ಕರವಾಗಿ ನಿಂದೆಗೆ ಒಳಗಾದೆನಲ್ಲಾ; ನಾಚಿಕೆಯು ನನ್ನ ಮುಖವನ್ನು ಕವಿದದೆ.",
			"8": "<sup>8</sup> ನನ್ನ ಅಣ್ಣತಮ್ಮಂದಿರಿಗೆ ಹೆರನಂತಾದೆನು; ಒಡಹುಟ್ಟಿದವರಿಗೆ ಪರಕೀಯನಂತಿದ್ದೇನೆ.",
			"9": "<sup>9</sup> ನಿನ್ನ ಆಲಯಾಭಿಮಾನವು ನನ್ನನ್ನು ಬೆಂಕಿಯಂತೆ ದಹಿಸಿದೆ. ನಿನ್ನನ್ನು ದೂಷಿಸುವವರ ದೂಷಣೆಗಳು ನನ್ನ ಮೇಲೆ ಬಂದಿವೆ.",
			"10": "<sup>10</sup> ನಾನು ಅತ್ತತ್ತು ಉಪವಾಸ ಬಿದ್ದದ್ದೆಲ್ಲಾ ಪರಿಹಾಸ್ಯಕ್ಕೆ ಕಾರಣವಾಯಿತು.",
			"11": "<sup>11</sup> ನಾನು ಗೋಣೀತಟ್ಟು ಕಟ್ಟಿಕೊಂಡದ್ದು ಅವರ ಗಾದೆಗೆ ಆಸ್ಪದವಾಯಿತು.",
			"12": "<sup>12</sup> ಊರುಬಾಗಲಲ್ಲಿ ಕೂತುಕೊಳ್ಳುವವರ ಆಡಿಕೆಗೆ ಗುರಿಯಾಗಿದ್ದೇನೆ. ಕುಡಿಕರು ನನ್ನ ವಿಷಯ ಹಾಡಿ ಗೇಲಿಮಾಡುತ್ತಾರೆ.",
			"13": "<sup>13</sup> ಯೆಹೋವನೇ, ನಾನಾದರೋ ನಿನಗೆ ಮೊರೆಯಿಟ್ಟಿದ್ದೇನೆ; ಇದು ನಿನ್ನ ಪ್ರಸನ್ನತೆಗೆ ಸಕಾಲ. ಪ್ರೇಮಪೂರ್ಣನಾದ ದೇವನೇ, ಸತ್ಯಸಂಧನಾದ ರಕ್ಷಕನೇ, ಸದುತ್ತರವನ್ನು ದಯಪಾಲಿಸು.",
			"14": "<sup>14</sup> ನಾನು ಕಳ್ಳುಸುಬಿನಲ್ಲಿ ಮುಳುಗಿಹೋಗದಂತೆ ಮೇಲೆತ್ತು; ವೈರಿಗಳ ಕೈಯೊಳಗಿಂದ ಬಿಡಿಸು; ಮಹಾಜಲ ರಾಶಿಯಿಂದ ಎಳೆದುಕೋ.",
			"15": "<sup>15</sup> ಪ್ರವಾಹವು ನನ್ನನ್ನು ಬಡಕೊಂಡು ಹೋಗದಿರಲಿ; ಮಡುವು ನನ್ನನ್ನು ಒಳಗೆ ಎಳಕೊಳ್ಳದಿರಲಿ; ಪಾತಾಳವು ನನ್ನನ್ನು ನುಂಗದಿರಲಿ.",
			"16": "<sup>16</sup> ಯೆಹೋವನೇ, ನನ್ನ ಮೊರೆಯನ್ನು ಲಾಲಿಸು; ನಿನ್ನ ಕೃಪೆಯು ಶುಭಕರವಾಗಿದೆಯಲ್ಲಾ. ಕರುಣಾನಿಧಿಯೇ, ನನ್ನನ್ನು ಕಟಾಕ್ಷಿಸು.",
			"17": "<sup>17</sup> ನಿನ್ನ ದಾಸನಿಗೆ ವಿಮುಖನಾಗಬೇಡ. ಇಕ್ಕಟ್ಟಿನಲ್ಲಿದ್ದೇನೆ; ತಡೆಮಾಡದೆ ಸಹಾಯಕೊಡು.",
			"18": "<sup>18</sup> ಸಮೀಪಿಸಿ ನನ್ನ ಪ್ರಾಣವನ್ನು ವಿಮೋಚಿಸು. ಶತ್ರುನಿಮಿತ್ತವಾಗಿ ನನ್ನನ್ನು ರಕ್ಷಿಸು.",
			"19": "<sup>19</sup> ನನಗುಂಟಾದ ನಿಂದೆ, ಲಜ್ಜೆ, ಅಪಮಾನ ಇವು ನಿನಗೇ ಗೊತ್ತು; ನನ್ನ ವಿರೋಧಿಗಳು ನಿನಗೆ ಮರೆಯಾಗಿಲ್ಲವಲ್ಲಾ.",
			"20": "<sup>20</sup> ನಿಂದೆಯಿಂದ ಖಿನ್ನನಾಗಿ ಕುಂದಿಹೋಗಿದ್ದೇನೆ. ಕರುಣಾಳುಗಳನ್ನು ನಿರೀಕ್ಷಿಸಿದೆ; ದೊರೆಯಲಿಲ್ಲ. ಸಂತೈಸುವವರನ್ನು ಹಾರೈಸಿದೆ; ಸಿಕ್ಕಲಿಲ್ಲ.",
			"21": "<sup>21</sup> ನನಗೆ ಉಣ್ಣುವದಕ್ಕೆ ಕಹಿಯಾದ ವಸ್ತುವನ್ನೂ ಬಾಯಾರಿದಾಗ ಹುಳಿತ ದ್ರಾಕ್ಷಾರಸವನ್ನೂ ಕೊಟ್ಟರು.",
			"22": "<sup>22</sup> ಅವರ ಊಟವೇ ಅವರಿಗೆ ಉರುಲಾಗಲಿ; ನಿಶ್ಚಿಂತರಾಗಿರುವಾಗಲೇ ಅದು ಅವರಿಗೆ ಬೋನಾಗಲಿ.",
			"23": "<sup>23</sup> ಅವರ ಕಣ್ಣು ಮೊಬ್ಬಾಗಿ ಕಾಣದೆಹೋಗಲಿ; ಅವರ ನಡುವು ಯಾವಾಗಲೂ ಬಳುಕುತ್ತಿರಲಿ.",
			"24": "<sup>24</sup> ನಿನ್ನ ರೌದ್ರವನ್ನು ಅವರ ಮೇಲೆ ಸುರಿದುಬಿಡು; ನಿನ್ನ ಕೋಪಾಗ್ನಿಯು ಅವರನ್ನು ಆಹುತಿಗೊಳ್ಳಲಿ.",
			"25": "<sup>25</sup> ಅವರ ಪಾಳೆಯವು ಹಾಳುಬೀಳಲಿ; ಅವರ ನಿವಾಸಗಳು ಜನಶೂನ್ಯವಾಗಲಿ.",
			"26": "<sup>26</sup> ನೀನು ಹೊಡೆದವನನ್ನು ಅವರು ಹಿಂಸಿಸುತ್ತಾರೆ; ನೀನು ಗಾಯಮಾಡಿದವರ ನೋವು ಅವರ ಆಡಿಕೆಗೆ ಕಾರಣವಾಗಿದೆ.",
			"27": "<sup>27</sup> ಅವರ ಅಪರಾಧಗಳು ಒಂದೊಂದಾಗಿ ಹೆಚ್ಚುತ್ತಾ ಬರಲಿ. ನಿನ್ನ ನೀತಿಯಲ್ಲಿ ಅವರಿಗೆ ಪಾಲುಕೊಡಬೇಡ.",
			"28": "<sup>28</sup> ಜೀವಿತರ ಪಟ್ಟಿಯಿಂದ ಅವರ ಹೆಸರು ತೆಗೆಯಲ್ಪಡಲಿ; ಸದ್ಭಕ್ತರ ಹೆಸರಿನ ಸಂಗಡ ಅವರದು ಬರೆಯಲ್ಪಡದಿರಲಿ.",
			"29": "<sup>29</sup> ದೇವರೇ, ನೊಂದು ಕುಗ್ಗಿದವನಾದ ನನ್ನನ್ನಾದರೋ ನಿನ್ನ ರಕ್ಷಣೆಯು ಭದ್ರಸ್ಥಳದಲ್ಲಿರಿಸುವದು.",
			"30": "<sup>30</sup> ನಾನು ಸಂಕೀರ್ತಿಸುತ್ತಾ ದೇವರ ನಾಮವನ್ನು ಕೊಂಡಾಡುವೆನು; ಕೃತಜ್ಞತಾಸ್ತುತಿಯಿಂದ ಘನಪಡಿಸುವೆನು.",
			"31": "<sup>31</sup> ಇದು ಯೆಹೋವನಿಗೆ ಕೊಂಬುಗೊರಸುಗಳುಳ್ಳ ಎಳೇ ಹೋರಿಗಳ ಯಜ್ಞಕ್ಕಿಂತ ಬಹು ಪ್ರಿಯವಾದದ್ದು.",
			"32": "<sup>32</sup> ದೀನರು ಇದನ್ನು ನೋಡಿ ಹರ್ಷಿಸುವರು. ದೇವದರ್ಶನವನ್ನು ಅಪೇಕ್ಷಿಸುವವರೇ, ನಿಮ್ಮ ಆತ್ಮವು ಉಜ್ಜೀವಿಸಲಿ.",
			"33": "<sup>33</sup> ಯೆಹೋವನು ಬಡವರ ಮೊರೆಗೆ ಲಕ್ಷ್ಯಕೊಡುವನು; ಸೆರೆಯಲ್ಲಿರುವ ತನ್ನವರನ್ನು ತಿರಸ್ಕರಿಸುವದಿಲ್ಲ.",
			"34": "<sup>34</sup> ಭೂಮ್ಯಾಕಾಶಗಳೂ ಸಮುದ್ರಗಳೂ ಜಲಚರಗಳೂ ಆತನನ್ನು ಕೊಂಡಾಡಲಿ.",
			"35": "<sup>35</sup> ದೇವರು ಚೀಯೋನ್ ಪಟ್ಟಣವನ್ನು ರಕ್ಷಿಸಿ ಯೆಹೂದದೇಶದ ನಗರಗಳನ್ನು ಕಟ್ಟಿಸುವನು; ಆತನ ಪ್ರಜೆಗಳು ಅಲ್ಲಿ ವಾಸಮಾಡುತ್ತಾ ಸ್ವದೇಶವಾಗಿ ಮಾಡಿಕೊಳ್ಳುವರು.",
			"36": "<sup>36</sup> ಅವರ ಸಂತತಿಗೇ ಅದರ ಹಕ್ಕಿರುವದು; ಆತನ ನಾಮವನ್ನು ಪ್ರೀತಿಸುವವರು ಅದರಲ್ಲಿ ವಾಸಿಸುವರು."
		},
		"70": {
			"1": "<sup>1</sup> ದೇವರೇ, ದಯವಿಟ್ಟು ನನ್ನನ್ನು ರಕ್ಷಿಸು. ಯೆಹೋವನೇ, ಬೇಗ ಬಂದು ಸಹಾಯಮಾಡು.",
			"2": "<sup>2</sup> ನನ್ನ ಪ್ರಾಣಕ್ಕಾಗಿ ಸಮಯನೋಡುವವರು ಆಶಾಭಂಗಪಟ್ಟು ಅಪಮಾನಹೊಂದಲಿ; ನನ್ನ ಆಪತ್ತಿನಲ್ಲಿ ಸಂತೋಷಿಸುವವರು ಮಾನಭಂಗದಿಂದ ಹಿಂದಿರುಗಿ ಓಡಲಿ.",
			"3": "<sup>3</sup> ಆಹಾ, ಆಹಾ ಎಂದು ಪರಿಹಾಸ್ಯ ಮಾಡುವವರು ತಮಗಾಗುವ ಅಪಮಾನದಿಂದ ಬೆಂಗೊಟ್ಟು ಓಡಿಹೋಗಲಿ.",
			"4": "<sup>4</sup> ನಿನ್ನ ದರ್ಶನವನ್ನು ಕೋರುವವರೆಲ್ಲರು ನಿನ್ನಲ್ಲಿ ಉಲ್ಲಾಸದಿಂದ ಸಂತೋಷಿಸಲಿ; ನಿನ್ನ ಜಯದಲ್ಲಿ ಆನಂದಿಸುವವರು - ದೇವರಿಗೆ ಸ್ತೋತ್ರವೆಂದು ಯಾವಾಗಲೂ ಅನ್ನುವವರಾಗಲಿ.",
			"5": "<sup>5</sup> ನಾನಾದರೋ ಕುಗ್ಗಿದವನೂ ದಿಕ್ಕಿಲ್ಲದವನೂ ಆಗಿದ್ದೇನೆ; ದೇವರೇ, ಶೀಘ್ರವಾಗಿ ಬಾ. ಯೆಹೋವನೇ, ನೀನೇ ನನಗೆ ಸಹಾಯಕನೂ ರಕ್ಷಕನೂ ಆಗಿದ್ದೀ; ತಡಮಾಡಬೇಡ."
		},
		"71": {
			"1": "<sup>1</sup> ಯೆಹೋವನೇ, ನಿನ್ನ ಮರೆ ಹೊಕ್ಕಿದ್ದೇನೆ; ಎಂದಿಗೂ ಆಶಾಭಂಗಪಡಿಸಬೇಡ.",
			"2": "<sup>2</sup> ನೀತಿಸ್ಥಾಪಕನಾದ ನೀನು ನನ್ನನ್ನು ಬಿಡಿಸಿ ಪಾರುಮಾಡು; ನನ್ನ ಪ್ರಾರ್ಥನೆಗೆ ಕಿವಿಗೊಟ್ಟು ಉದ್ಧರಿಸು.",
			"3": "<sup>3</sup> ನಾನು ಯಾವಾಗಲೂ ಮರೆಹೊಗುವ ಆಶ್ರಯಗಿರಿಯಾಗಿರು; ನನ್ನ ರಕ್ಷಣೆಗೋಸ್ಕರ ಆಜ್ಞಾಪಿಸಿದ್ದೀಯಲ್ಲವೇ. ನೀನೇ ನನ್ನ ಬಂಡೆಯೂ ಕೋಟೆಯೂ ಆಗಿದ್ದೀಯಲ್ಲಾ.",
			"4": "<sup>4</sup> ದೇವರೇ, ನನ್ನನ್ನು ದುಷ್ಟನ ಕೈಯಿಂದಲೂ ಅನ್ಯಾಯ ಬಲಾತ್ಕಾರಿಯ ವಶದಿಂದಲೂ ತಪ್ಪಿಸು.",
			"5": "<sup>5</sup> ಕರ್ತನಾದ ಯೆಹೋವನೇ, ಬಾಲ್ಯಾರಭ್ಯ ನನ್ನ ನಿರೀಕ್ಷೆಯೂ ಭರವಸವೂ ನೀನಲ್ಲವೋ?",
			"6": "<sup>6</sup> ನಾನು ಹುಟ್ಟಿದಂದಿನಿಂದ ನಿನ್ನನ್ನೇ ಆತುಕೊಂಡಿದ್ದೇನೆ; ತಾಯಿ ಹೆತ್ತಂದಿನಿಂದ ನನ್ನ ಉದ್ಧಾರಕನು ನೀನೇ. ನಾನು ಯಾವಾಗಲೂ ಹಿಗ್ಗುತ್ತಿರುವದು ನಿನ್ನಲ್ಲಿಯೇ.",
			"7": "<sup>7</sup> ಅನೇಕರು ನನ್ನ ದುಃಸ್ಥಿತಿಗೆ ಬೆರಗಾಗುತ್ತಾರೆ; ಆದರೂ ನೀನು ನನಗೆ ಬಲವಾದ ಆಶ್ರಯಸ್ಥಾನವಾಗಿರುತ್ತೀ.",
			"8": "<sup>8</sup> ನನ್ನ ಬಾಯಲ್ಲಿ ನಿನ್ನ ಸ್ತೋತ್ರವಲ್ಲದೆ ಮತ್ತೊಂದಿಲ್ಲ; ದಿನವೆಲ್ಲಾ ನಿನ್ನ ಘನತೆಯನ್ನು ವರ್ಣಿಸುತ್ತಿರುವೆನು.",
			"9": "<sup>9</sup> ವೃದ್ಧಾಪ್ಯದಲ್ಲಿ ನನ್ನನ್ನು ಧಿಕ್ಕರಿಸಬೇಡ; ನನ್ನ ಬಲವು ಕುಂದಿದಾಗ ಕೈಬಿಡಬೇಡ.",
			"10": "<sup>10</sup> ನನ್ನ ಜೀವಕ್ಕೆ ಹೊಂಚುಹಾಕುವ ವೈರಿಗಳು ಒಟ್ಟಾಗಿ ಆಲೋಚಿಸುತ್ತಾ -",
			"11": "<sup>11</sup> ದೇವರು ಅವನನ್ನು ಕೈಬಿಟ್ಟಿದ್ದಾನೆ; ಬೆನ್ನಟ್ಟಿ ಹಿಡಿಯಿರಿ; ಬಿಡಿಸುವವರು ಯಾರೂ ಇಲ್ಲ ಎಂದು ಹೇಳಿಕೊಳ್ಳುತ್ತಾರೆ.",
			"12": "<sup>12</sup> ದೇವರೇ, ದೂರವಾಗಿರಬೇಡ. ನನ್ನ ದೇವರೇ, ಬೇಗ ಸಹಾಯಮಾಡು.",
			"13": "<sup>13</sup> ನನ್ನ ಪ್ರಾಣಕ್ಕೆ ಕಂಟಕರಾದವರು ಆಶಾಭಂಗಪಟ್ಟು ನಾಶವಾಗಲಿ; ನನಗೆ ಕೇಡುಬಗೆಯುವವರನ್ನು ನಿಂದಾಪಮಾನಗಳು ಕವಿಯಲಿ.",
			"14": "<sup>14</sup> ನಾನಂತೂ ನಿರೀಕ್ಷಿಸಿಕೊಂಡೇ ಇರುವೆನು; ನಿನ್ನನ್ನು ಅಧಿಕಾಧಿಕವಾಗಿ ಹೊಗಳುತ್ತಿರುವೆನು.",
			"15": "<sup>15</sup> ನನ್ನ ಬಾಯಿ ನಿನ್ನ ನೀತಿಯನ್ನೂ ರಕ್ಷಣೆಯನ್ನೂ ಹಗಲೆಲ್ಲಾ ವರ್ಣಿಸುತ್ತಿರುವದು; ಆದರೂ ಅವುಗಳ ವಿವರಣೆ ನನಗೆ ಅಸಾಧ್ಯ.",
			"16": "<sup>16</sup> ಕರ್ತನಾದ ಯೆಹೋವನೇ, ನಾನು ನಿನ್ನ ಮಹತ್ಕೃತ್ಯಗಳನ್ನು ಸ್ಮರಿಸುವವನಾಗಿ ನಿನ್ನೊಬ್ಬನ ನೀತಿಯನ್ನೇ ಪ್ರಕಟಪಡಿಸುವೆನು.",
			"17": "<sup>17</sup> ದೇವರೇ, ನೀನು ಬಾಲ್ಯಾರಭ್ಯ ನನ್ನನ್ನು ಉಪದೇಶಿಸುತ್ತಾ ಬಂದಿದ್ದೀ; ನಾನು ನಿನ್ನ ಅದ್ಭುತಕೃತ್ಯಗಳನ್ನು ಇಂದಿನವರೆಗೂ ಪ್ರಚುರಪಡಿಸುತ್ತಿದ್ದೇನೆ.",
			"18": "<sup>18</sup> ದೇವರೇ, ನಾನು ನರೆಯ ಮುದುಕನಾದಾಗಲೂ ಕೈಬಿಡಬೇಡ; ಆಗ ಮುಂದಿನ ತಲೆಯವರಿಗೆ ನಿನ್ನ ಭುಜಬಲವನ್ನು ಸಾರುವೆನು, ತಲತಲಾಂತರದವರಿಗೆಲ್ಲಾ ನಿನ್ನ ಪ್ರತಾಪವನ್ನು ಪ್ರಕಟಿಸುವೆನು.",
			"19": "<sup>19</sup> ದೇವರೇ, ನಿನ್ನ ನೀತಿಯು ಆಕಾಶವನ್ನು ನಿಲುಕುವಷ್ಟು ಮಹೋನ್ನತವಾಗಿದೆ. ಮಹತ್ಕೃತ್ಯಗಳನ್ನು ನಡಿಸಿದ ದೇವರೇ, ನಿನಗೆ ಸಮಾನರು ಯಾರು?",
			"20": "<sup>20</sup> ನಮ್ಮನ್ನು ಅನೇಕ ಕಷ್ಟನಷ್ಟಗಳಿಗೆ ಗುರಿಮಾಡಿದ ನೀನೇ ಪುನಃ ಉಜ್ಜೀವಿಸಮಾಡು; ನಮ್ಮನ್ನು ಭೂಮಿಯ ಅಧೋಭಾಗದಿಂದ ಮೇಲೆತ್ತು.",
			"21": "<sup>21</sup> ನನ್ನ ಗೌರವವನ್ನು ಹೆಚ್ಚಿಸು; ನನಗೆ ಅಭಿಮುಖನಾಗಿ ಸಂತೈಸು.",
			"22": "<sup>22</sup> ನನ್ನ ದೇವರೇ, ನಿನ್ನ ಸತ್ಯತೆಯನ್ನು ಸ್ಮರಿಸುವೆನು; ಸ್ವರಮಂಡಲದಿಂದ ನಿನ್ನನ್ನು ಸಂಕೀರ್ತಿಸುವೆನು. ಇಸ್ರಾಯೇಲ್ಯರ ಸದಮಲಸ್ವಾಮಿಯೇ, ಕಿನ್ನರಿಯನ್ನು ನುಡಿಸುತ್ತಾ ನಿನ್ನನ್ನು ಭಜಿಸುವೆನು.",
			"23": "<sup>23</sup> ನನ್ನ ತುಟಿಗಳೂ ನೀನು ರಕ್ಷಿಸಿದ ನನ್ನ ಪ್ರಾಣವೂ ನಿನ್ನನ್ನು ಹಾಡಿಹರಸುವವು.",
			"24": "<sup>24</sup> ನನ್ನ ಕೇಡಿಗೆ ಪ್ರಯತ್ನಿಸಿದವರು ಆಶಾಭಂಗಪಟ್ಟು ಅಪಮಾನಹೊಂದಿದ್ದಾರೆ. ಆದುದರಿಂದ ನನ್ನ ನಾಲಿಗೆಯು ದಿನವೆಲ್ಲಾ ನಿನ್ನ ನೀತಿಸಾಧನೆಯನ್ನು ವರ್ಣಿಸುತ್ತಿರುವದು."
		},
		"72": {
			"1": "<sup>1</sup> ದೇವರೇ, ಅರಸನಿಗೆ ನಿನ್ನ ನ್ಯಾಯವನ್ನು ಕಲಿಸಿಕೊಡು; ರಾಜಕುಮಾರನಾದ ಅವನಿಗೆ ನಿನ್ನ ನೀತಿಯನ್ನು ಅನುಗ್ರಹಿಸು.",
			"2": "<sup>2</sup> ಅವನು ನಿನ್ನ ಜನರಿಗೆ ನೀತಿಯಿಂದಲೂ ಕುಗ್ಗಿಹೋದ ನಿನ್ನವರಿಗೆ ನ್ಯಾಯವಾಗಿಯೂ ತೀರ್ಪುಕೊಡಲಿ.",
			"3": "<sup>3</sup> ಗುಡ್ಡದಿನ್ನೆಗಳೆಲ್ಲವು ನಿನ್ನ ಜನರಿಗೋಸ್ಕರ ನೀತಿಗನುಸಾರವಾಗಿ ಸುಕ್ಷೇಮವನ್ನು ಫಲಿಸಲಿ.",
			"4": "<sup>4</sup> ಅವನು ಬಡವರ ನ್ಯಾಯವನ್ನು ಸ್ಥಾಪಿಸಲಿ; ದೀನರ ಮಕ್ಕಳನ್ನು ಉದ್ಧರಿಸಲಿ; ಪ್ರಜಾಹಿಂಸಕರನ್ನು ಖಂಡಿಸಿಬಿಡಲಿ.",
			"5": "<sup>5</sup> ಆಚಂದ್ರಾರ್ಕವಾಗಿ ತಲತಲಾಂತರಗಳೆಲ್ಲಾ ನಿನಗೆ ಭಯಪಡಲಿ.",
			"6": "<sup>6</sup> ಹುಲ್ಲು ಕೊಯಿದ ಮೇಹುಗಾಡಿನ ಮೇಲೆ ಸುರಿಯುವ ವೃಷ್ಟಿಯಂತೆಯೂ ಭೂಮಿಯನ್ನು ನೆನಸುವ ಹದಮಳೆಯಂತೆಯೂ ಅವನು ಇರಲಿ.",
			"7": "<sup>7</sup> ಅವನ ದಿವಸಗಳಲ್ಲಿ ನೀತಿಯು ವೃದ್ಧಿಯಾಗಲಿ; ಚಂದ್ರನಿರುವವರೆಗೂ ಪರಿಪೂರ್ಣ ಸೌಭಾಗ್ಯವಿರಲಿ.",
			"8": "<sup>8</sup> ಅವನು ಸಮುದ್ರದಿಂದ ಸಮುದ್ರದವರೆಗೂ [ಯೂಫ್ರೇಟೀಸ್] ನದಿಯಿಂದ ಭೂಮಿಯ ಕಟ್ಟಕಡೆಯವರೆಗೂ ಆಳಲಿ.",
			"9": "<sup>9</sup> ಅರಣ್ಯವಾಸಿಗಳು ಅವನಿಗೆ ಅಡ್ಡ ಬೀಳಲಿ; ಅವನ ವೈರಿಗಳು ಮಣ್ಣುಮುಕ್ಕಲಿ.",
			"10": "<sup>10</sup> ತಾರ್ಷೀಷ್‍ಪ್ರಾಂತದ ಮತ್ತು ಸಮುದ್ರದ ಕರಾವಳಿಯ ಅರಸರು ಕಪ್ಪಗಳನ್ನು ಸಲ್ಲಿಸಲಿ; ಶೆಬಾ ಮತ್ತು ಸೆಬಾ ಎಂಬ ಪ್ರದೇಶಗಳ ರಾಜರೂ ಕಾಣಿಕೆಗಳನ್ನು ತಂದೊಪ್ಪಿಸಲಿ.",
			"11": "<sup>11</sup> ಎಲ್ಲಾ ಅರಸರೂ ಅವನಿಗೆ ಸಾಷ್ಟಾಂಗವೆರಗಲಿ; ಸರ್ವಜನಾಂಗಗಳು ಅವನನ್ನು ಸೇವಿಸಲಿ.",
			"12": "<sup>12</sup> ಯಾಕಂದರೆ ಅವನು ಮೊರೆಯಿಡುವ ಬಡವರನ್ನೂ ದಿಕ್ಕಿಲ್ಲದೆ ಕುಗ್ಗಿದವರನ್ನೂ ಉದ್ಧರಿಸುವನು.",
			"13": "<sup>13</sup> ದೀನದರಿದ್ರರ ಮೇಲೆ ಕರುಣೆಯುಳ್ಳವನಾಗಿ ಅವರ ಪ್ರಾಣಗಳನ್ನು ಸಂತೈಸುವನು.",
			"14": "<sup>14</sup> ಕುಯುಕ್ತಿ ಬಲಾತ್ಕಾರಗಳಿಗೆ ತಪ್ಪಿಸಿ ಅವರನ್ನು ಕಾಯುವನು; ಅವರ ಜೀವವು ಅವನ ದೃಷ್ಟಿಯಲ್ಲಿ ಅಮೂಲ್ಯವಾಗಿರುವದು.",
			"15": "<sup>15</sup> ಅವನು ಬಾಳಲಿ; ಶೆಬಾ ಪ್ರಾಂತದ ಬಂಗಾರವು ಅವನಿಗೆ ಸಮರ್ಪಣೆಯಾಗಲಿ. ಅವನಿಗೋಸ್ಕರ ಯಾವಾಗಲೂ ದೇವರಲ್ಲಿ ವಿಜ್ಞಾಪನೆಯು ನಡೆಯಲಿ; ದಿನದಿನವೂ ಅವನಿಗೆ ಆಶೀರ್ವಾದಗಳುಂಟಾಗಲಿ.",
			"16": "<sup>16</sup> ದೇಶದಲ್ಲಿ ಬೆಟ್ಟಗಳ ಮೇಲೆಲ್ಲಾ ಬೆಳೆಯು ಸಮೃದ್ಧಿಯಾಗಲಿ; ಪೈರುಗಳ ಶಬ್ದವು ಲೆಬನೋನಿನ ಮರಗಳ ಸಪ್ಪಳದಂತಿರಲಿ. ಹೊಲದಲ್ಲಿ ಕಾಯಿಪಲ್ಯದಂತೆ ಪಟ್ಟಣಗಳಲ್ಲಿ ಜನರು ಹೆಚ್ಚಲಿ.",
			"17": "<sup>17</sup> ಅವನ ನಾಮವು ಸ್ಥಿರವಾಗಿರಲಿ; ಅವನ ಹೆಸರು ಸೂರ್ಯನಿರುವವರೆಗೆ ಇರಲಿ. ಎಲ್ಲಾ ಜನಾಂಗಗಳವರು ಅವನನ್ನು ಧನ್ಯನೆಂದು ಹೇಳಿ ಅವನಿಗಿದ್ದ ಆಶೀರ್ವಾದವು ತಮಗೂ ಆಗಬೇಕೆಂದು ಕೋರುವವರಾಗಲಿ.",
			"18": "<sup>18</sup> ಮಹತ್ಕಾರ್ಯಗಳನ್ನು ನಡಿಸುವದರಲ್ಲಿ ಅದ್ವಿತೀಯನೂ ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರೂ ಆದ ಯೆಹೋವ ದೇವರಿಗೆ ಸ್ತೋತ್ರವು.",
			"19": "<sup>19</sup> ಆತನ ಪ್ರಭಾವವುಳ್ಳ ನಾಮಕ್ಕೆ ಸದಾಕಾಲವೂ ಪ್ರಣಾಮವಿರಲಿ; ಭೂಮಂಡಲವೆಲ್ಲಾ ಆತನ ಪ್ರಭಾವದಿಂದ ತುಂಬಿರಲಿ. ಆಮೆನ್, ಆಮೆನ್.",
			"20": "<sup>20</sup> ಇಲ್ಲಿಗೆ ಇಷಯನ ಮಗನಾದ ದಾವೀದನ ಪ್ರಾರ್ಥನೆಗಳ ಸಮಾಪ್ತಿ."
		},
		"73": {
			"1": "<sup>1</sup> ದೇವರು ನಿರ್ಮಲಚಿತ್ತರಾದ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ದಯಾಪರನೇ ಹೌದು.",
			"2": "<sup>2</sup> ಆದರೆ ನಾನು ದುಷ್ಟರ ಸೌಭಾಗ್ಯವನ್ನು ಕಂಡು ಸೊಕ್ಕಿನವರ ಮೇಲೆ ಉರಿಗೊಂಡೆನು.",
			"3": "<sup>3</sup> ನನ್ನ ಕಾಲುಗಳು ಜಾರಿದವುಗಳೇ; ನನ್ನ ಹೆಜ್ಜೆಗಳು ತಪ್ಪಿದವುಗಳೇ.",
			"4": "<sup>4</sup> ಅವರ ಮರಣವು ನಿರ್ಬಾಧಕವಾಗಿದೆ; ಅವರ ಕಾಯವು ಕೊಬ್ಬುಳ್ಳದ್ದಾಗಿದೆ.",
			"5": "<sup>5</sup> ಮನುಷ್ಯರ ಕಷ್ಟದಲ್ಲಿ ಅವರು ಭಾಗಿಗಳಾಗುವದಿಲ್ಲ; ಇತರರಿಗೆ ತಗುಲುವಂತೆ ಅವರಿಗೆ ಅಂಟುರೋಗವೂ ತಗುಲುವದಿಲ್ಲ.",
			"6": "<sup>6</sup> ಆದದರಿಂದ ಅವರಿಗೆ ಗರ್ವವು ಕಂಠಮಾಲೆಯಾಗಿದೆ; ಬಲಾತ್ಕಾರವು ಉಡುಪಾಗಿದೆ.",
			"7": "<sup>7</sup> ಕೊಬ್ಬಿನಿಂದ ಅವರ ಕಣ್ಣುಗಳು ಮೇಲೆ ಅವೆ; ಅವರ ದುಷ್ಕಲ್ಪನೆಗಳು ತುಂಬಿತುಳುಕುತ್ತವೆ.",
			"8": "<sup>8</sup> ಹಾಸ್ಯಮಾಡುವವರಾಗಿ ಕೆಡುಕಿನ ವಿಷಯ ಮಾತಾಡಿಕೊಳ್ಳುತ್ತಾರೆ; ಬಲಾತ್ಕಾರನಡಿಸಬೇಕೆಂದು ಹೆಮ್ಮೆ ಕೊಚ್ಚುತ್ತಾರೆ.",
			"9": "<sup>9</sup> ತಾವು ಮೇಲುಲೋಕದವರೋ ಎಂಬಂತೆ ದೊಡ್ಡ ಬಾಯಿಮಾಡುತ್ತಾರೆ. ಭೂಲೋಕದಲ್ಲೆಲ್ಲಾ ಅವರ ಮಾತೇ ಮುಂದು.",
			"10": "<sup>10</sup> ಆದದರಿಂದ ಜನರು ಅವರ ಪಕ್ಷವನ್ನು ಹಿಡಿಯುತ್ತಾರೆ; ಅವರಿಗೆ ಪಾನವು ಯಥೇಚ್ಫವಾಗಿ ದೊರಕುವದು.",
			"11": "<sup>11</sup> ದೇವರು ವಿಚಾರಿಸುವದೆಲ್ಲಿ? ಪರಾತ್ಪರನು ಚಿಂತಿಸುವದುಂಟೋ ಅಂದುಕೊಳ್ಳುತ್ತಾರೆ.",
			"12": "<sup>12</sup> ನೋಡಿರಿ, ದುಷ್ಟರು ಇಂಥವರೇ; ಅವರು ಸದಾ ಸುಖದಿಂದಿದ್ದು ಸ್ಥಿತಿವಂತರಾಗಿ ಹೋಗುತ್ತಾರೆ.",
			"13": "<sup>13</sup> ನನ್ನ ಮನಸ್ಸನ್ನು ನಿರ್ಮಲಮಾಡಿಕೊಂಡದ್ದೂ ಶುದ್ಧತ್ವದಲ್ಲಿ ಕೈತೊಳಕೊಂಡದ್ದೂ ವ್ಯರ್ಥವೇ ಸರಿ.",
			"14": "<sup>14</sup> ನಾನು ಯಾವಾಗಲೂ ವ್ಯಾಧಿಪೀಡಿತನಾಗಿದ್ದು ಪ್ರತಿದಿನವೂ ದಂಡಿಸಲ್ಪಡುತ್ತಾ ಇದ್ದೇನಲ್ಲಾ.",
			"15": "<sup>15</sup> ನಾನು ಈ ಪ್ರಕಾರ ಬಾಯಿಬಿಡುವದಕ್ಕೆ ಮನಸ್ಸು ಮಾಡಿಕೊಂಡಿದ್ದರೆ ನಿನ್ನ ಭಕ್ತಕುಲಕ್ಕೆ ದ್ರೋಹಿಯಾಗುತ್ತಿದ್ದೆನು.",
			"16": "<sup>16</sup> ನಾನು ಇದನ್ನು ಗ್ರಹಿಸಿಕೊಳ್ಳಬೇಕೆಂದು ಎಷ್ಟು ಚಿಂತಿಸಿದರೂ ಅದು ಒಂದು ಕಷ್ಟಕರವಾದ ಮರ್ಮವೆಂದು ತೋಚಿತು.",
			"17": "<sup>17</sup> ಆದರೆ ದೇವಾಲಯಕ್ಕೆ ಹೋಗಿ ಅವರ ಅಂತ್ಯಾವಸ್ಥೆಯನ್ನು ಆಲೋಚಿಸಿದಾಗ ನನಗೆ ಗೊತ್ತಾಯಿತು.",
			"18": "<sup>18</sup> ಹೌದು, ನೀನು ಅವರನ್ನು ಅಪಾಯಕರ ಸ್ಥಳದಲ್ಲಿಟ್ಟು ಬೀಳಿಸಿ ನಾಶಮಾಡಿಬಿಡುತ್ತೀ.",
			"19": "<sup>19</sup> ಅವರು ನಿಮಿಷಮಾತ್ರದಲ್ಲಿಯೇ ಹಾಳಾಗಿ ಹೋಗುತ್ತಾರೆ; ಭಯಂಕರರೀತಿಯಿಂದ ಸಂಹಾರವಾಗಿ ಮುಗಿದು ಹೋಗುತ್ತಾರೆ.",
			"20": "<sup>20</sup> ಎಚ್ಚರವಾದವನು ಕನಸನ್ನು ಹೇಗೋ ಹಾಗೆ ಯೆಹೋವನೇ, ನೀನು ಏಳುವಾಗ ಅವರನ್ನು ಮಾಯಾರೂಪರೆಂದು ಭಾವಿಸುತ್ತೀ.",
			"21": "<sup>21</sup> ನನ್ನ ಮನಸ್ಸು ನೊಂದುಹೋಗಿತ್ತು; ಆಂತರ್ಯದಲ್ಲಿ ಅಲಗು ನೆಟ್ಟಂತಿತ್ತು.",
			"22": "<sup>22</sup> ನಾನು ವಿವೇಕಹೀನಪಾಮರನಾಗಿ ನಿನ್ನ ದೃಷ್ಟಿಯಲ್ಲಿ ಕೇವಲ ಪಶುವೇ ಆಗಿದ್ದೆನು.",
			"23": "<sup>23</sup> ಆದರೂ ಸದಾ ನಿನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿಯೇ ಇದ್ದೇನೆ. ನೀನು ನನ್ನ ಬಲಗೈಯನ್ನು ಹಿಡಿದು",
			"24": "<sup>24</sup> ನಿನ್ನ ಚಿತ್ತವನ್ನು ತಿಳಿಯಪಡಿಸಿ ನನ್ನನ್ನು ನಡಿಸಿ ತರುವಾಯ ಮಹಿಮೆಗೆ ಸೇರಿಸಿಕೊಳ್ಳುವಿ.",
			"25": "<sup>25</sup> ಪರಲೋಕದಲ್ಲಿ ನನಗೆ ನೀನಲ್ಲದೆ ಮತ್ತಾರು ಅವಶ್ಯ? ಇಹಲೋಕದಲ್ಲಿ ನಿನ್ನನ್ನಲ್ಲದೆ ಇನ್ನಾರನ್ನೂ ಬಯಸುವದಿಲ್ಲ.",
			"26": "<sup>26</sup> ತನುಮನಗಳು ಕ್ಷಯಿಸಿದರೂ ನನ್ನ ಆತ್ಮಕ್ಕೆ ಶರಣನೂ ನನ್ನ ಶಾಶ್ವತವಾದ ಪಾಲೂ ದೇವರೇ.",
			"27": "<sup>27</sup> ಇಗೋ, ನಿನ್ನನ್ನು ಬಿಟ್ಟವರು ನಾಶವಾಗುವರು; ನಿನಗೆ ದ್ರೋಹಮಾಡಿದವರೆಲ್ಲರನ್ನು ನಿರ್ಮೂಲ ಮಾಡುತ್ತೀ.",
			"28": "<sup>28</sup> ನನಗಾದರೋ ದೇವರ ಸಾನ್ನಿಧ್ಯವೇ ಭಾಗ್ಯವು. ಕರ್ತನೇ, ಯೆಹೋವನೇ, ನಾನು ನಿನ್ನನ್ನು ಆಶ್ರಯಿಸಿಕೊಂಡವನಾಗಿ ನಿನ್ನ ಮಹತ್ಕಾರ್ಯಗಳನ್ನು ಪ್ರಕಟಿಸುವೆನು."
		},
		"74": {
			"1": "<sup>1</sup> ದೇವರೇ, ನೀನು ನಮ್ಮನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ತಳ್ಳಿಬಿಟ್ಟದ್ದೇಕೆ? ನೀನು ಪಾಲಿಸುವ ಹಿಂಡಿನ ಮೇಲೆ ನಿನ್ನ ಕೋಪಾಗ್ನಿಯು ಹೊಗೆ ಆಡುವದೇನು?",
			"2": "<sup>2</sup> ನೀನು ಹಿಂದಿನ ಕಾಲದಲ್ಲಿ ಸ್ವಕುಲವಾಗಿರಬೇಕೆಂದು ಬಿಡಿಸಿ ಸಂಪಾದಿಸಿಕೊಂಡ ನಿನ್ನ ಮಂಡಲಿಯನ್ನು ಜ್ಞಾಪಿಸಿಕೋ; ನಿನ್ನ ವಾಸಸ್ಥಾನವಾಗಿದ್ದ ಚೀಯೋನ್‍ಗಿರಿಯನ್ನು ಮರೆಯಬೇಡ.",
			"3": "<sup>3</sup> ಬಹುಕಾಲದಿಂದ ಹಾಳುಬಿದ್ದಿರುವ ಈ ಸ್ಥಾನದ ಕಡೆಗೆ ಬಿಜಯಮಾಡು. ನೋಡು, ವೈರಿಯು ಪವಿತ್ರಾಲಯದಲ್ಲಿ ಎಲ್ಲವನ್ನು ಕೆಡಿಸಿಬಿಟ್ಟಿದ್ದಾನೆ.",
			"4": "<sup>4</sup> ನಿನ್ನ ದರ್ಶನಾಲಯಮಧ್ಯದಲ್ಲಿ ನಿನ್ನ ವಿರೋಧಿಗಳು ಆರ್ಭಟಿಸುತ್ತಾರೆ; ನಮ್ಮ ಆರಾಧನಾಚಿಹ್ನೆಗಳನ್ನು ತೆಗೆದು ತಮ್ಮ ಚಿಹ್ನೆಗಳನ್ನು ಇಟ್ಟಿದ್ದಾರೆ.",
			"5": "<sup>5</sup> ಮರಗಳ ಗುಂಪಿನಲ್ಲಿ ಕೊಡಲಿಗಳನ್ನು ಮೇಲೆತ್ತುವ ಜನರೋ ಎಂಬಂತಿದ್ದಾರೆ.",
			"6": "<sup>6</sup> ನೋಡು, ಅವರು ಈಗ ಕೈಕೊಡಲಿ ಸುತ್ತಿಗೆಗಳಿಂದ ದೇವಾಲಯದ ಕೆತ್ತನೆಯ ಕೆಲಸವನ್ನೆಲ್ಲಾ ಹೊಡೆದು ಹಾಕುತ್ತಿದ್ದಾರೆ.",
			"7": "<sup>7</sup> ಅವರು ನಿನ್ನ ಪವಿತ್ರಾಲಯಕ್ಕೆ ಬೆಂಕಿಹಾಕಿ ನಿನ್ನ ನಾಮಕ್ಕೆ ಪ್ರತಿಷ್ಠಿತವಾದ ಮಂದಿರವನ್ನು ಹೊಲೆಮಾಡಿ ನೆಲಸಮಗೊಳಿಸಿದ್ದಾರೆ.",
			"8": "<sup>8</sup> ನಾವು ಈ ಜನವನ್ನೆಲ್ಲಾ ಸಂಹರಿಸಿಬಿಡೋಣ ಅಂದುಕೊಂಡಿದ್ದಾರೆ; ದೇಶದಲ್ಲಿರುವ ಎಲ್ಲಾ ದೇವಸಭಾಮಂದಿರಗಳನ್ನು ಸುಟ್ಟುಬಿಟ್ಟಿದ್ದಾರೆ.",
			"9": "<sup>9</sup> ನಮ್ಮ ಆರಾಧನಾಚಿಹ್ನೆಗಳು ಈಗ ಕಾಣುವದಿಲ್ಲ. ಮುಂಚೆ ಇದ್ದಂತೆ ನಮಗೆ ಪ್ರವಾದಿಗಳು ಯಾರೂ ಇಲ್ಲ; ಇದು ಎಷ್ಟರವರೆಗೆ ಇರುವದೆಂದು ಬಲ್ಲವರು ನಮ್ಮಲ್ಲಿ ಯಾರೂ ಇಲ್ಲ.",
			"10": "<sup>10</sup> ದೇವರೇ, ವಿರೋಧಿಗಳು ಇನ್ನೆಲ್ಲಿಯತನಕ ನಿಂದಿಸುತ್ತಿರಬೇಕು? ವೈರಿಗಳು ನಿನ್ನ ನಾಮವನ್ನು ಸದಾಕಾಲವೂ ತಿರಸ್ಕರಿಸಬಹುದೋ?",
			"11": "<sup>11</sup> ಚಾಚಿದ ಬಲಗೈಯನ್ನು ಯಾಕೆ ಹಿಂದೆಗೆದಿದ್ದೀ? ಅದನ್ನು ಎದೆಯ ಮೇಲಿಂದ ತೆಗೆದು ಅವರನ್ನು ಸಂಹರಿಸಿಬಿಡು.",
			"12": "<sup>12</sup> ದೇವರೇ, ನೀನು ಮೊದಲಿಂದಲೂ ನನ್ನ ಅರಸನೂ ಲೋಕಮಧ್ಯದಲ್ಲಿ ರಕ್ಷಣೆಗಳನ್ನು ನಡಿಸಿದಾತನೂ ಆಗಿದ್ದೀಯಲ್ಲವೇ?",
			"13": "<sup>13</sup> ಸ್ವಶಕ್ತಿಯಿಂದ ಸಮುದ್ರವನ್ನು ಭೇದಿಸಿದವನು ನೀನು; ಜಲರಾಶಿಯ ಮೇಲೆ ತಿಮಿಂಗಿಲಗಳ ತಲೆಗಳನ್ನು ಜಜ್ಜಿಬಿಟ್ಟವನು ನೀನು.",
			"14": "<sup>14</sup> ಲಿವ್ಯಾತಾನನ ಶಿರಃಖಂಡನಮಾಡಿ ಅಡವಿಯ ಮೃಗಸಮುದಾಯಕ್ಕೆ ತಿನ್ನಕೊಟ್ಟವನು ನೀನು.",
			"15": "<sup>15</sup> ಬುಗ್ಗೆಹಳ್ಳಗಳನ್ನು ಉಕ್ಕಿಸಿದವನು ನೀನು; ಮಹಾನದಿಗಳನ್ನು ಒಣಗಿಸಿಬಿಟ್ಟವನು ನೀನು.",
			"16": "<sup>16</sup> ಹಗಲಿರುಳುಗಳಿಗೆ ನಿಯಾಮಕನು ನೀನು; ಸೂರ್ಯಾದಿ ಜ್ಯೋತಿರ್ಮಂಡಲಗಳ ನಿರ್ಮಾಣಕನು ನೀನು.",
			"17": "<sup>17</sup> ಭೂಮಿಯ ಎಲ್ಲಾ ಮೇರೆಗಳನ್ನು ಸ್ಥಾಪಿಸಿದವನು ನೀನು; ಶೀತೋಷ್ಣಕಾಲಗಳನ್ನು ನೇಮಿಸಿದವನು ನೀನು.",
			"18": "<sup>18</sup> ಯೆಹೋವನೇ, ವೈರಿಗಳು ನಿನ್ನನ್ನು ನಿಂದಿಸಿ ದುರ್ಮತಿಗಳು ನಿನ್ನ ನಾಮವನ್ನು ತಿರಸ್ಕರಿಸಿದ್ದನ್ನು ಜ್ಞಾಪಿಸಿಕೋ.",
			"19": "<sup>19</sup> ನಿನ್ನ ಬೆಳವದ ಜೀವವನ್ನು ಕಾಡುಮೃಗಕ್ಕೆ ಕೊಡಬೇಡ; ನಿನ್ನ ದೀನಮಂಡಲಿಯನ್ನು ಸದಾ ಮರೆತೇ ಬಿಡಬೇಡ.",
			"20": "<sup>20</sup> ನಿನ್ನ ಒಡಂಬಡಿಕೆಯನ್ನು ಲಕ್ಷ್ಯಕ್ಕೆ ತಂದುಕೋ. ದೇಶದ ಅಂಧಕಾರಸ್ಥಾನಗಳಲ್ಲಿ ಬಲಾತ್ಕಾರವು ತುಂಬಿ ವಾಸಿಸುತ್ತದಲ್ಲಾ.",
			"21": "<sup>21</sup> ಕುಗ್ಗಿದವರು ಆಶಾಭಂಗದಿಂದ ಹಿಂದಿರುಗದಿರಲಿ; ದುಃಖಿತರೂ ಬಡವರೂ ನಿನ್ನ ನಾಮವನ್ನು ಕೀರ್ತಿಸಲಿ.",
			"22": "<sup>22</sup> ದೇವರೇ, ಎದ್ದು ನಿನ್ನ ನ್ಯಾಯವನ್ನು ನಡಿಸುವವನಾಗು; ದುರ್ಮತಿಯು ಯಾವಾಗಲೂ ನಿನ್ನನ್ನು ನಿಂದಿಸುತ್ತಿರುವದನ್ನು ಜ್ಞಾಪಿಸಿಕೋ.",
			"23": "<sup>23</sup> ಮೇಲಣ ಲೋಕವನ್ನು ಎಡೆಬಿಡದೆ ಮುಟ್ಟುತ್ತಿರುವ ನಿನ್ನ ವೈರಿಗಳ ಗದ್ದಲವನ್ನೂ ನಿನ್ನ ವಿರೋಧಿಗಳ ದೊಂಬಿಯನ್ನೂ ಮರೆಯದಿರು."
		},
		"75": {
			"1": "<sup>1</sup> ದೇವರೇ, ಕೊಂಡಾಡುತ್ತೇವೆ; ನಿನ್ನನ್ನು ಕೊಂಡಾಡುತ್ತೇವೆ. ನಿನ್ನ ನಾಮಮಹತ್ವವು ಸಮೀಪವಾಗಿದೆ; ನಿನ್ನ ಮಹತ್ಕಾರ್ಯಗಳನ್ನು ವರ್ಣಿಸುತ್ತಾರೆ.",
			"2": "<sup>2</sup> [ದೇವರು ನುಡಿದದ್ದು -] ಕ್ಲುಪ್ತಕಾಲವು ಬಂದಾಗ ನಾನೇ ನೀತಿಯಿಂದ ತೀರ್ಪುಕೊಡುವೆನು.",
			"3": "<sup>3</sup> ಎಲ್ಲಾ ನಿವಾಸಿಗಳೊಡನೆ ಭೂಮಿಯು ಕರಗಿಹೋದರೂ ಅದರ ಸ್ತಂಭಗಳನ್ನು ಸ್ಥಾಪಿಸುವವನು ನಾನೇ [ಎಂಬದು]. ಸೆಲಾ.",
			"4": "<sup>4</sup> ಹೆಮ್ಮೆಗಾರರೇ, ಕೊಚ್ಚಿಕೊಳ್ಳಬೇಡಿರಿ; ದುಷ್ಟರೇ, ನಿಮ್ಮ ಕೊಂಬುಗಳನ್ನು ಮೇಲೆತ್ತಬೇಡಿರಿ;",
			"5": "<sup>5</sup> ಕೊಂಬುಗಳನ್ನು ಮೇಲೆತ್ತಬೇಡಿರಿ; ಸೊಕ್ಕಿನ ಕುತ್ತಿಗೆಯಿಂದ ಮಾತಾಡಬೇಡಿರಿ ಎಂದು ಹೇಳುವೆನು.",
			"6": "<sup>6</sup> ಯಾಕಂದರೆ ಉದ್ಧಾರವು ಮೂಡಲಿಂದಾಗಲಿ ಪಡುವಲಿಂದಾಗಲಿ ಅರಣ್ಯದಿಂದಾಗಲಿ ಬರುವದಿಲ್ಲ.",
			"7": "<sup>7</sup> ದೇವರೇ ನ್ಯಾಯಾಧೀಶನಾಗಿ ಒಬ್ಬನನ್ನು ತೆಗೆದು ಇನ್ನೊಬ್ಬನನ್ನು ಸ್ಥಾಪಿಸುತ್ತಾನೆ.",
			"8": "<sup>8</sup> ಯೆಹೋವನ ಕೈಯಲ್ಲಿ ಪಾತ್ರೆಯದೆ; ಅದು ಔಷಧಿಮಿಶ್ರವಾಗಿ ಉಕ್ಕುವ ದ್ರಾಕ್ಷಾರಸದಿಂದ ತುಂಬಿದೆ. ಅದರಿಂದ ಹೊಯ್ದು ಹಂಚುತ್ತಾನೆ. ಲೋಕದ ದುಷ್ಟರೆಲ್ಲರೂ ಅದರೊಳಗಿನ ಮಡ್ಡಿಯನ್ನು ಸಹ ಕುಡಿದು ಮುಗಿಸಬೇಕು.",
			"9": "<sup>9</sup> ನಾನಾದರೋ ಸದಾ ವರ್ಣಿಸುವವನಾಗಿ ಯಾಕೋಬವಂಶದವರ ದೇವರನ್ನು ಸಂಕೀರ್ತಿಸುವೆನು.",
			"10": "<sup>10</sup> ದುಷ್ಟರ ಕೊಂಬುಗಳನ್ನೆಲ್ಲಾ ಮುರಿದುಬಿಡುವೆನು; ಆದರೆ ಭಕ್ತರ ಕೊಂಬುಗಳು ಎತ್ತಲ್ಪಡುವವು."
		},
		"76": {
			"1": "<sup>1</sup> ದೇವರು ಯೆಹೂದದೇಶದಲ್ಲಿ ಖ್ಯಾತಿಗೊಂಡವನು; ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಆತನ ನಾಮವು ದೊಡ್ಡದು.",
			"2": "<sup>2</sup> ಸಾಲೇಮಿನಲ್ಲಿ ಆತನ ಬೀಡಾರವಿದೆ; ಚೀಯೋನಿನಲ್ಲಿ ಆತನು ನಿವಾಸಿಸುತ್ತಾನೆ.",
			"3": "<sup>3</sup> ಅಲ್ಲಿ ಆತನು ಮಿಂಚಿನಂತೆ ಹಾರಿ ಬರುವ ಬಾಣಗಳನ್ನೂ ಗುರಾಣಿ ಖಡ್ಗ ಮುಂತಾದ ಯುದ್ಧಾಯುಧಗಳನ್ನೂ ಮುರಿದುಬಿಟ್ಟಿದ್ದಾನೆ. ಸೆಲಾ.",
			"4": "<sup>4</sup> ತೇಜೋಮಯನು ನೀನು; ಬೆಟ್ಟಗಳಲ್ಲಿ ಕೊಳ್ಳೆಹೊಡೆದದರಿಂದ ಹೆಸರುಗೊಂಡವನು.",
			"5": "<sup>5</sup> ಧೀರಹೃದಯರು ಸುಲಿಗೆಯಾಗಿ ದೀರ್ಘನಿದ್ರೆಗೈದಿದ್ದಾರೆ; ಎಲ್ಲಾ ಶೂರರ ಕೈ ನಿಂತುಹೋದವು.",
			"6": "<sup>6</sup> ಯಾಕೋಬವಂಶದವರ ದೇವರೇ, ನಿನ್ನ ಗದರಿಕೆಯಿಂದ ರಥಬಲವೂ ಅಶ್ವಬಲವೂ ಮೈಮರೆತು ಬಿದ್ದವು.",
			"7": "<sup>7</sup> ನೀನು ಮಹಾಭಯಂಕರನು; ನೀನು ಸಿಟ್ಟುಗೊಂಡಾಗ ನಿನ್ನ ಮುಂದೆ ಯಾರು ನಿಂತಾರು?",
			"8": "<sup>8</sup> ಪರಲೋಕದಲ್ಲಿರುವ ನೀನು ನಿನ್ನ ನ್ಯಾಯವಿಧಿಯನ್ನು ಆಜ್ಞಾಪಿಸುವಾಗ",
			"9": "<sup>9</sup> ದೇವರು ಲೋಕದ ದೀನರನ್ನು ರಕ್ಷಿಸಿ ನ್ಯಾಯವನ್ನು ಸ್ಥಾಪಿಸುವದಕ್ಕೋಸ್ಕರ ಎದ್ದಿದ್ದಾನೆಂದು ಭೂನಿವಾಸಿಗಳು ಭಯದಿಂದ ಸ್ತಬ್ಧರಾದರು. ಸೆಲಾ.",
			"10": "<sup>10</sup> ಮನುಷ್ಯರ ಕೋಪವೂ ನಿನ್ನ ಘನಕ್ಕೇ ಸಾಧಕವಾಗುವದು; ಕೋಪಶೇಷವನ್ನು ನಡುಕಟ್ಟಿನಂತೆ ಬಿಗಿದುಕೊಳ್ಳುವಿ.",
			"11": "<sup>11</sup> ನಿಮ್ಮ ದೇವನಾದ ಯೆಹೋವನಿಗೆ ಹರಕೆಮಾಡಿ ಸಲ್ಲಿಸಿರಿ; ಸುತ್ತಣವರೆಲ್ಲರು ಭಯಂಕರನಿಗೆ ಕಾಣಿಕೆಗಳನ್ನು ಸಮರ್ಪಿಸಲಿ.",
			"12": "<sup>12</sup> ಆತನು ಭೂಪತಿಗಳಿಗೆ ಭಯಪ್ರದನಾಗಿ ಪ್ರಭುಗಳ ಅಹಂಭಾವವನ್ನು ತೊಡೆದುಬಿಡುವನು."
		},
		"77": {
			"1": "<sup>1</sup> ದೇವರಿಗೆ ಮೊರೆಯಿಡುವೆನು, ಕೂಗಿ ಮೊರೆಯಿಡುವೆನು; ನನಗೆ ಕಿವಿಗೊಡುವನು.",
			"2": "<sup>2</sup> ಇಕ್ಕಟ್ಟಿನಲ್ಲಿ ಸ್ವಾಮಿಯನ್ನು ಕರೆದೆನು; ಬೇಸರವಿಲ್ಲದೆ ಇರುಳೆಲ್ಲಾ ಕೈಯೊಡ್ಡಿಕೊಂಡೇ ಇದ್ದೆನು. ನನ್ನ ಮನಸ್ಸು ಶಾಂತಿಹೊಂದಲೊಲ್ಲದೆ ಇತ್ತು.",
			"3": "<sup>3</sup> ನಾನು ವ್ಯಥೆಪಡುತ್ತಾ ದೇವರನ್ನು ಸ್ಮರಿಸುವೆನು; ಮನಗುಂದಿದವನಾಗಿಯೇ ಹಂಬಲಿಸುವೆನು.",
			"4": "<sup>4</sup> ನಾನು ರೆಪ್ಪೆಗಳನ್ನು ಹಚ್ಚದಂತೆ ಮಾಡಿದಿ. ತಳಮಳಗೊಂಡು ಮಾತಾಡಲಾರದೆ ಇದ್ದೆನು.",
			"5": "<sup>5</sup> ಹಳೆಯ ದಿನಗಳನ್ನೂ ಪುರಾತನ ವರುಷಗಳನ್ನೂ ಯೋಚಿಸಿದೆನು.",
			"6": "<sup>6</sup> ನಾನು ರಾತ್ರಿಯಲ್ಲಿ ಮಾಡುತ್ತಿದ್ದ ಗಾನವನ್ನು ನೆನಸಿಕೊಳ್ಳುವೆನು, ನನ್ನ ಆಂತರ್ಯದಲ್ಲಿ ಮಾತಾಡಿಕೊಳ್ಳುವೆನು ಅಂದುಕೊಂಡು ಮನಸ್ಸಿನಲ್ಲಿ ವಿಚಾರಿಸಿಕೊಂಡದ್ದೇನಂದರೆ -",
			"7": "<sup>7</sup> ಕರ್ತನು ಸದಾಕಾಲಕ್ಕೂ ಬಿಟ್ಟೇಬಿಡುವನೋ? ಆತನು ಪುನಃ ಪ್ರಸನ್ನನಾಗುವದಿಲ್ಲವೋ?",
			"8": "<sup>8</sup> ಆತನ ಕೃಪಾವಾತ್ಸಲ್ಯವು ನಿಂತೇ ಹೋಯಿತೋ? ಆತನ ವಾಗ್ದಾನವು ಎಂದೆಂದಿಗೂ ಬಿದ್ದೇ ಹೋಯಿತೋ?",
			"9": "<sup>9</sup> ದೇವರು ದಯವಿಡಲಿಕ್ಕೆ ಮರೆತುಬಿಟ್ಟನೋ? ಕೋಪದಿಂದ ತನ್ನ ಕರುಳುಗಳನ್ನು ಬಿಗಿಹಿಡಿದಿದ್ದಾನೋ? ಸೆಲಾ.",
			"10": "<sup>10</sup> ಮತ್ತೂ ನಾನು ಅಂದುಕೊಂಡದ್ದೇನಂದರೆ - ಹೀಗೆ ನೆನಸುವದು ನನ್ನ ಬಲಹೀನತೆಯೇ. ಪರಾತ್ಪರನ ಭುಜಬಲವು ಪ್ರಕಟವಾದ ವರುಷಗಳನ್ನು ಜ್ಞಾಪಿಸಿಕೊಳ್ಳುವೆನು.",
			"11": "<sup>11</sup> ಯೆಹೋವನ ಕೃತ್ಯಗಳನ್ನು ವರ್ಣಿಸುವೆನು; ಪೂರ್ವದಿಂದ ನೀನು ನಡಿಸಿದ ಅದ್ಭುತಗಳನ್ನು ನೆನಪುಮಾಡಿಕೊಳ್ಳುವೆನು.",
			"12": "<sup>12</sup> ನಿನ್ನ ಕಾರ್ಯಗಳನ್ನೆಲ್ಲಾ ಧ್ಯಾನಿಸುವೆನು, ನಿನ್ನ ಪ್ರವರ್ತನೆಗಳನ್ನು ಸ್ಮರಿಸುವೆನು.",
			"13": "<sup>13</sup> ದೇವರೇ, ನಿನ್ನ ಮಾರ್ಗವು ಪರಿಶುದ್ಧವಾದದ್ದು. ನಮ್ಮ ದೇವರಂತೆ ಮಹತ್ವವುಳ್ಳ ದೇವರು ಯಾರು?",
			"14": "<sup>14</sup> ಅದ್ಭುತಗಳನ್ನು ನಡಿಸುವ ದೇವರು ನೀನೇ; ಜನಾಂಗಗಳಲ್ಲಿ ಪರಾಕ್ರಮವನ್ನು ತೋರ್ಪಡಿಸಿದಿ.",
			"15": "<sup>15</sup> ಯಾಕೋಬ ಯೋಸೇಫರ ವಂಶೀಯವಾದ ನಿನ್ನ ಪ್ರಜೆಯನ್ನು ಭುಜಬಲದಿಂದ ಬಿಡುಗಡೆ ಮಾಡಿದಿ. ಸೆಲಾ.",
			"16": "<sup>16</sup> ದೇವರೇ, ಜಲರಾಶಿಗಳು ನಿನ್ನನ್ನು ಕಂಡವು; ಕಾಣುತ್ತಲೇ ತಳಮಳಗೊಂಡು ತಳದವರೆಗೂ ಅಲ್ಲಕಲ್ಲೋಲವಾದವು.",
			"17": "<sup>17</sup> ಮೇಘಮಂಡಲವು ಮಳೆಗರೆಯಿತು; ಅಂತರಿಕ್ಷವು ಗರ್ಜಿಸಿತು; ನಿನ್ನ ಬಾಣಗಳು ಎಲ್ಲಾ ಕಡೆಯೂ ಹಾರಿದವು.",
			"18": "<sup>18</sup> ಬಿರುಗಾಳಿಯಲ್ಲಿ ನಿನ್ನ ಗುಡುಗು ಕೇಳಿಸಿತು; ಮಿಂಚುಗಳು ಭೂಮಂಡಲವನ್ನು ಬೆಳಗಿಸಿದವು; ಭೂಮಿಯು ಅಲ್ಲಾಡಿ ಕಂಪಿಸಿತು.",
			"19": "<sup>19</sup> ನೀನು ಸಮುದ್ರದಲ್ಲಿ ಮಾರ್ಗಮಾಡಿದಿ; ಮಹಾಜಲರಾಶಿಗಳನ್ನು ದಾಟಿದಿ; ನಿನ್ನ ಹೆಜ್ಜೆಯ ಗುರುತು ಕಾಣಲಿಲ್ಲ.",
			"20": "<sup>20</sup> ಕುರುಬನು ಕುರಿಹಿಂಡನ್ನು ಹೇಗೋ ಹಾಗೆ ನೀನು ಮೋಶೆ ಆರೋನರ ಮುಖಾಂತರ ನಿನ್ನ ಪ್ರಜೆಯನ್ನು ಕರೆದೊಯ್ದಿ."
		},
		"78": {
			"1": "<sup>1</sup> ನನ್ನ ಜನರೇ, ನನ್ನ ಉಪದೇಶಕ್ಕೆ ಕಿವಿಗೊಡಿರಿ; ನನ್ನ ನುಡಿಗಳನ್ನು ಲಾಲಿಸಿರಿ.",
			"2": "<sup>2</sup> ನಾನು ಬಾಯ್ದೆರೆದು ಸಾಮ್ಯರೂಪವಾಗಿ ಉಪದೇಶಿಸುವೆನು. ಪೂರ್ವಕಾಲದ ಗೂಢಾರ್ಥಗಳನ್ನು ಹೊರಪಡಿಸುವೆನು.",
			"3": "<sup>3</sup> ನಾವು ನಮ್ಮ ಹಿರಿಯರ ಬಾಯಿಂದಲೇ ತಿಳಿದ ಸಂಗತಿಗಳನ್ನು ಅವರ ಸಂತಾನದವರಿಗೆ ಮರೆಮಾಡದೆ",
			"4": "<sup>4</sup> ಯೆಹೋವನ ಘನತೆಯನ್ನೂ ಆತನ ಪರಾಕ್ರಮವನ್ನೂ ಅದ್ಭುತಕೃತ್ಯಗಳನ್ನೂ ಮುಂದಣ ಸಂತತಿಯವರಿಗೆ ವಿವರಿಸುವೆವು.",
			"5": "<sup>5</sup> ಆತನು ಯಾಕೋಬವಂಶದಲ್ಲಿ ಕಟ್ಟಳೆಯನ್ನಿಟ್ಟು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ನಿಯಮಮಾಡಿ ನಮ್ಮ ಹಿರಿಯರಿಗೆ ಆಜ್ಞಾಪಿಸಿದ್ದೇನಂದರೆ - ಅವುಗಳನ್ನು ನಿಮ್ಮ ಮಕ್ಕಳಿಗೆ ಹೇಳಿಕೊಡಿರಿ;",
			"6": "<sup>6</sup> ಇದರಿಂದ ಅವು ಮುಂದಿನ ತಲೆಯವರಿಗೆ ಗೊತ್ತಾಗಿ ಅವರ ಮಕ್ಕಳು ಅವುಗಳನ್ನು ತಮ್ಮ ಮಕ್ಕಳು ಮೊಮ್ಮಕ್ಕಳಿಗೆ ತಿಳಿಸುತ್ತಾ ಹೋಗುವರು.",
			"7": "<sup>7</sup> ಆಗ ಅವರು ಮೊಂಡರೂ ಅವಿಧೇಯರೂ ಚಪಲಚಿತ್ತರೂ ದೇವದ್ರೋಹಿಗಳೂ ಆದ ತಮ್ಮ ಪೂರ್ವಿಕರಂತಾಗದೆ",
			"8": "<sup>8</sup> ನನ್ನ ಮಹತ್ಕೃತ್ಯಗಳನ್ನು ಮರೆಯದೆ ನನ್ನಲ್ಲಿಯೇ ಭರವಸವಿಟ್ಟು ನನ್ನ ಆಜ್ಞೆಗಳನ್ನು ಕೈಕೊಂಡಾರು ಎಂಬದೇ.",
			"9": "<sup>9</sup> ಎಫ್ರಾಯೀಮ್ ಕುಲದವರು ಧನುರ್ಧಾರಿಗಳಾಗಿ ಹೋಗಿ ಯುದ್ಧಸಮಯದಲ್ಲಿ ಬೆಂಗೊಟ್ಟು ಓಡಿಹೋದರು.",
			"10": "<sup>10</sup> ಅವರು ದೇವರ ನಿಬಂಧನೆಯನ್ನು ಪಾಲಿಸದೆ ಆತನ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಅನುಸರಿಸಲೊಲ್ಲದೆ",
			"11": "<sup>11</sup> ಆತನ ಕಾರ್ಯಗಳನ್ನೂ ಆತನು ತಮ್ಮೆದುರಿನಲ್ಲಿ ನಡಿಸಿದ ಅದ್ಭುತಗಳನ್ನೂ ಮರೆತುಬಿಟ್ಟರು.",
			"12": "<sup>12</sup> ಐಗುಪ್ತ ದೇಶದ ಸೋನ್ ಸೀಮೆಯಲ್ಲಿ ಅವರ ಪೂರ್ವಿಕರ ಸಮಕ್ಷದಲ್ಲಿಯೇ ಆಶ್ಚರ್ಯ ಕ್ರಿಯೆಗಳನ್ನು ನಡಿಸಿದನು.",
			"13": "<sup>13</sup> ಆತನು ಸಮುದ್ರವನ್ನು ಭೇದಿಸಿ ಅದರ ನೀರನ್ನು ರಾಶಿಯಂತೆ ನಿಲ್ಲಿಸಿ ಅವರನ್ನು ದಾಟಿಸಿದನು.",
			"14": "<sup>14</sup> ಹಗಲು ಮೋಡದಿಂದಲೂ ಇರುಳು ಬೆಂಕಿಯ ಬೆಳಕಿನಿಂದಲೂ ಅವರನ್ನು ಕರೆದೊಯ್ದನು.",
			"15": "<sup>15</sup> ಆತನು ಅರಣ್ಯದಲ್ಲಿ ಬಂಡೆಗಳನ್ನು ಸೀಳಿ ಅವರಿಗೆ ಸಾಗರದಿಂದಲೋ ಎಂಬಂತೆ ಧಾರಾಳವಾಗಿ ನೀರು ಕುಡಿಸಿದನು.",
			"16": "<sup>16</sup> ಗಿರಿಶಿಲೆಯಿಂದ ಜಲಪ್ರವಾಹಗಳನ್ನು ಹೊರಡಿಸಿ ನದಿಗಳಾಗಿ ಹರಿಯಮಾಡಿದನು.",
			"17": "<sup>17</sup> ಆದರೂ ಅವರು ನಿರ್ಜಲಪ್ರದೇಶದಲ್ಲಿ ಪರಾತ್ಪರನಿಗೆ ಪದೇಪದೇ ಅವಿಧೇಯರಾಗಿ ಇನ್ನೂ ಹೆಚ್ಚು ಪಾಪಮಾಡುತ್ತಾ ಬಂದರು.",
			"18": "<sup>18</sup> ಅವರು ಇಷ್ಟಭೋಜನವನ್ನು ಕೇಳಿಕೊಳ್ಳುವಲ್ಲಿ ಸಂಶಯಾತ್ಮರಾಗಿ ದೇವರನ್ನು ಪರೀಕ್ಷಿಸಿದರು.",
			"19": "<sup>19</sup> ಅವರು ಆತನಿಗೆ ವಿರೋಧವಾಗಿ ಅಂದದ್ದೇನಂದರೆ - ದೇವರು ಅರಣ್ಯದಲ್ಲಿ ಊಟಕ್ಕೆ ಬಡಿಸಬಲ್ಲನೋ?",
			"20": "<sup>20</sup> ಬಂಡೆಯನ್ನು ಹೊಡೆದು ನೀರು ಚಿಮ್ಮಿ ಹೊರಗೆ ಬರುವಂತೆಯೂ ಪ್ರವಾಹವು ದಡಮೀರಿ ಹರಿಯುವಂತೆಯೂ ಮಾಡಿದನಲ್ಲವೋ? ಆತನು ರೊಟ್ಟಿಯನ್ನೂ ಕೊಡಶಕ್ತನೋ? ತನ್ನ ಜನರಿಗೆ ಮಾಂಸವನ್ನೂ ಒದಗಿಸುವನೋ ಎಂಬದೇ.",
			"21": "<sup>21</sup> ಯೆಹೋವನು ಕೇಳಿ ರೋಷಗೊಂಡನು. ಅವರು ದೇವರಲ್ಲಿ ಭರವಸವಿಡದೆಯೂ ಆತನ ರಕ್ಷಣೆಯನ್ನು ನಂಬದೆಯೂ ಹೋದದರಿಂದ",
			"22": "<sup>22</sup> ಆತನಿಗೆ ಇಸ್ರಾಯೇಲ್ಯರ ಮೇಲೆ ಕೋಪವುಂಟಾಯಿತು; ಯಾಕೋಬ್‍ವಂಶದವರಲ್ಲಿ ಬೆಂಕಿಹತ್ತಿತು.",
			"23": "<sup>23</sup> ಆತನು ಮೇಘಗಳಿಗೆ ಅಪ್ಪಣೆಕೊಟ್ಟು ಆಕಾಶದ್ವಾರಗಳನ್ನು ತೆರೆದು",
			"24": "<sup>24</sup> ಸ್ವರ್ಗಧಾನ್ಯವಾದ ಮನ್ನವನ್ನು ಅವರಿಗೋಸ್ಕರ ಸುರಿಸಿ ಉಣ್ಣಲಿಕ್ಕೆ ಕೊಟ್ಟನು.",
			"25": "<sup>25</sup> ಅವರಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬನು ದೇವದೂತರ ಆಹಾರವನ್ನು ಭುಜಿಸಿದನು. ಆತನು ಅವರಿಗೆ ತೃಪ್ತಿಭೋಜನವನ್ನು ಕೊಟ್ಟನು.",
			"26": "<sup>26</sup> ಆತನು ಗಗನಮಂಡಲದಲ್ಲಿ ಮೂಡಣ ಗಾಳಿಯನ್ನುಂಟುಮಾಡಿ ಪ್ರತಾಪದಿಂದ ತೆಂಕಣ ಗಾಳಿಯನ್ನೂ ಹೊಡೆದುಕೊಂಡು ಬಂದು",
			"27": "<sup>27</sup> ಧೂಳಿನಷ್ಟು ಮಾಂಸವೃಷ್ಟಿಯನ್ನು ಸುರಿಸಿದನು; ಸಮುದ್ರದ ಮರಳಿನಷ್ಟು ಪಕ್ಷಿಗಳು ಅವರಿದ್ದಲ್ಲಿ ಬೀಳುವಂತೆ ಮಾಡಿದನು.",
			"28": "<sup>28</sup> ಅವು ಪಾಳೆಯದ ಮಧ್ಯದಲ್ಲಿಯೇ ಅವರ ಬೀಡಾರಗಳ ಸುತ್ತಲೂ ಬಿದ್ದವು.",
			"29": "<sup>29</sup> ಅವರು ತಿಂದು ಸಂತೃಪ್ತರಾದರು; ಆತನು ಅವರಿಗೆ ಬಯಸಿದ್ದನ್ನು ಕೊಟ್ಟನು.",
			"30": "<sup>30</sup> ಅವರ ಇಷ್ಟಭೋಜನವು ಅವರಿಗೆ ಅಸಹ್ಯವಾಗುವದಕ್ಕಿಂತ ಮೊದಲು, ಅವರು ಅದನ್ನು ಇನ್ನೂ ಭುಜಿಸುತ್ತಿರುವಾಗಲೇ",
			"31": "<sup>31</sup> ದೇವರ ಕೋಪವು ಅವರಿಗೆ ವಿರೋಧವಾಗಿ ಎದ್ದು ಅವರಲ್ಲಿ ಕೊಬ್ಬಿದವರನ್ನು ವಧಿಸಿ ಇಸ್ರಾಯೇಲ್ಯಪ್ರಾಯಸ್ಥರನ್ನು ನೆಲಕ್ಕುರುಳಿಸಿತು.",
			"32": "<sup>32</sup> ಇಷ್ಟಾದರೂ ಪುನಃ ಪುನಃ ಪಾಪಮಾಡುತ್ತಾ ಬಂದರು; ಅವರು ಆತನ ಅದ್ಭುತಕೃತ್ಯಗಳನ್ನು ನಂಬದೆಹೋದರು.",
			"33": "<sup>33</sup> ಆದದರಿಂದ ಆತನು ಅವರ ಜೀವಿತ ದಿನಗಳನ್ನು ಉಸಿರಿನಂತೆಯೂ ಅವರ ವರುಷಗಳನ್ನು ಭೀಕರವಾಗಿಯೂ ಮುಗಿಸಿದನು.",
			"34": "<sup>34</sup> ಸಂಹಾರವಾಗುವಾಗೆಲ್ಲಾ ಅವರು ದೇವರನ್ನು ನೆನಸಿ ತಿರುಗಿ ಆತನ ಪ್ರಸನ್ನತೆಯನ್ನು ಆಶಿಸಿ -",
			"35": "<sup>35</sup> ದೇವರು ತಮ್ಮ ಶರಣನೂ ಪರಾತ್ಪರನು ತಮ್ಮ ವಿಮೋಚಕನೂ ಆಗಿದ್ದಾನೆ ಎಂಬದನ್ನು ನೆನಪಿಗೆ ತಂದುಕೊಳ್ಳುವರು.",
			"36": "<sup>36</sup> ಆದರೆ ಆತನ ಮುಂದೆ ಅವರ ಬಾಯಿ ವಂಚಿಸಿ ನಾಲಿಗೆ ಸುಳ್ಳಾಡಿತು.",
			"37": "<sup>37</sup> ಅವರ ಹೃದಯವು ಆತನಲ್ಲಿ ನೆಲೆಗೊಳ್ಳಲಿಲ್ಲ; ಆತನ ನಿಬಂಧನೆಯನ್ನು ನಿಷ್ಠೆಯಿಂದ ಕೈಕೊಳ್ಳಲಿಲ್ಲ.",
			"38": "<sup>38</sup> ಆದರೂ ಆತನು ಕರುಣಾಳುವೂ ಅಪರಾಧಿಗಳನ್ನು ಸಂಹರಿಸದೆ ಕ್ಷಮಿಸುವವನೂ ಆಗಿ ತನ್ನ ಸಿಟ್ಟನ್ನೆಲ್ಲಾ ಏರಗೊಡದೆ ಅದನ್ನು ಹಲವು ಸಾರಿ ತಡೆಯುತ್ತಾ ಬಂದನು.",
			"39": "<sup>39</sup> ಅವರು ಮಾಂಸಮಾತ್ರರೂ ತಿರಿಗಿ ಬಾರದ ಶ್ವಾಸಮಾತ್ರರೂ ಆಗಿದ್ದಾರೆಂಬದನ್ನು ನೆನಪುಮಾಡಿಕೊಳ್ಳುತ್ತಿದ್ದನು.",
			"40": "<sup>40</sup> ಅರಣ್ಯದಲ್ಲಿ ಅವರು ಎಷ್ಟೋ ಸಾರಿ ಅವಿಧೇಯರಾಗಿ ಅಲ್ಲಿ ಆತನನ್ನು ನೋಯಿಸಿದರು.",
			"41": "<sup>41</sup> ಆತನನ್ನು ಪದೇಪದೇ ಪರೀಕ್ಷಿಸಿ ಇಸ್ರಾಯೇಲ್ಯರ ಸದಮಲಸ್ವಾಮಿಯನ್ನು ಕರಕರೆಗೊಳಿಸಿದರು.",
			"42": "<sup>42</sup> ಅವರು ಆತನ ಭುಜಬಲವನ್ನೂ ಶತ್ರುಗಳಿಂದ ಬಿಡಿಸಿದ ಸಮಯವನ್ನೂ ಮರೆತುಬಿಟ್ಟರು.",
			"43": "<sup>43</sup> ಬಿಡಿಸುವ ಸಮಯದಲ್ಲಿ ಆತನು ಐಗುಪ್ತದಲ್ಲಿ ಮಹತ್ಕಾರ್ಯಗಳನ್ನೂ ಸೋನ್ ಸೀಮೆಯಲ್ಲಿ ಅದ್ಭುತಗಳನ್ನೂ ನಡಿಸಿದ್ದೂ ಹೇಗಂದರೆ -",
			"44": "<sup>44</sup> ಆತನು ಅಲ್ಲಿನ ನದಿಗಳನ್ನು ರಕ್ತವಾಗುವಂತೆಯೂ ಪ್ರವಾಹಗಳನ್ನು ಕುಡಿಯಕೂಡದಂತೆಯೂ ಮಾಡಿದನು.",
			"45": "<sup>45</sup> ಅವರನ್ನು ನಾಶಮಾಡುವ ವಿಷದ ಹುಳಗಳನ್ನೂ ಹಾಳುಮಾಡುವ ಕಪ್ಪೆಗಳನ್ನೂ ಕಳುಹಿಸಿಕೊಟ್ಟನು.",
			"46": "<sup>46</sup> ಅವರ ಬೆಳೆಗಳನ್ನು ಜಿಟ್ಟೆಹುಳಗಳಿಗೂ ಕಷ್ಟಫಲವನ್ನು ಮಿಡಿತೆಗಳಿಗೂ ಕೊಟ್ಟುಬಿಟ್ಟನು.",
			"47": "<sup>47</sup> ಅವರ ದ್ರಾಕ್ಷಾಲತೆಗಳನ್ನು ಆನೆಕಲ್ಲಿನಿಂದಲೂ ಅತ್ತಿಮರಗಳನ್ನು ಕಲ್ಮಳೆಯಿಂದಲೂ ಹಾಳುಮಾಡಿದನು.",
			"48": "<sup>48</sup> ಅವರ ದನಗಳನ್ನು ಕಲ್ಮಳೆಗೂ ಕುರಿಹಿಂಡುಗಳನ್ನು ಸಿಡಿಲಿಗೂ ಒಪ್ಪಿಸಿದನು.",
			"49": "<sup>49</sup> ಆತನು ಅವರ ಮೇಲೆ ತನ್ನ ಕೋಪ ರೌದ್ರಗಳನ್ನೂ ಉಗ್ರಹಿಂಸೆಗಳನ್ನೂ ಸಂಹಾರದೂತಗಣವನ್ನೋ ಎಂಬಂತೆ ಕಳುಹಿಸಿದನು.",
			"50": "<sup>50</sup> ತನ್ನ ರೌದ್ರಕ್ಕೆ ಇಂಬುಗೊಟ್ಟು ಅವರನ್ನು ಬದುಕಗೊಡದೆ ಅವರ ಜೀವವನ್ನು ಮರಣವ್ಯಾಧಿಗೆ ಆಹುತಿಕೊಟ್ಟನು.",
			"51": "<sup>51</sup> ಹಾಮನ ಮನೆತನದವರ ವೀರ್ಯಕ್ಕೆ ಪ್ರಥಮ ಫಲವಾಗಿರುವ ಐಗುಪ್ತ್ಯರ ಚೊಚ್ಚಲಮಕ್ಕಳನ್ನು ಸಂಹರಿಸಿದನು.",
			"52": "<sup>52</sup> ಆತನು ಕುರಿಗಳನ್ನೋ ಎಂಬಂತೆ ತನ್ನ ಜನವನ್ನು ಹೊರತಂದು ಅಡವಿಯಲ್ಲಿ ಅದನ್ನು ಪೋಷಿಸಿ ನಡಿಸಿದನು.",
			"53": "<sup>53</sup> ಆತನು ಸುರಕ್ಷಿತವಾಗಿ ನಡಿಸಿದ್ದರಿಂದ ಅವರು ಅಂಜಲಿಲ್ಲ; ಅವರ ಶತ್ರುಗಳನ್ನು ಸಮುದ್ರವು ಮುಚ್ಚಿಬಿಟ್ಟಿತು.",
			"54": "<sup>54</sup> ಅವರನ್ನು ತನ್ನ ಪವಿತ್ರದೇಶಕ್ಕೆ ಅಂದರೆ ತನ್ನ ಭುಜಬಲದಿಂದ ಸಂಪಾದಿಸಿದ ಈ ಪರ್ವತಸೀಮೆಗೆ ಕರತಂದನು.",
			"55": "<sup>55</sup> ಜನಾಂಗಗಳನ್ನು ಅವರ ಮುಂಗಡೆಯಿಂದ ಓಡಿಸಿಬಿಟ್ಟು ಅವರ ದೇಶವನ್ನು ಇಸ್ರಾಯೇಲ್ ಗೋತ್ರಗಳಿಗೆ ಸ್ವಾಸ್ತ್ಯವಾಗಿರುವಂತೆ ಹಂಚಿಕೊಟ್ಟು ಆ ಜನಾಂಗಗಳ ಬೀಡಾರಗಳಲ್ಲಿ ಅವರನ್ನು ನೆಲೆಗೊಳಿಸಿದನು.",
			"56": "<sup>56</sup> ಆದರೂ ಅವರು ಪರಾತ್ಪರನಾದ ದೇವರನ್ನು ಪರೀಕ್ಷಿಸಿ ಅವಿಧೇಯರಾದರು. ಆತನ ವಿಧಿಗಳನ್ನು ಕೈಕೊಳ್ಳದೆ",
			"57": "<sup>57</sup> ಆತನಿಗೆ ವಿಮುಖರಾಗಿ ತಮ್ಮ ಹಿರಿಯರಂತೆ ದ್ರೋಹಿಗಳಾದರು. ಮೋಸದ ಬಿಲ್ಲಿನಂತೆ ತಿರುಗಿಕೊಂಡರು.",
			"58": "<sup>58</sup> ಅವರು ತಮ್ಮ ಪೂಜಾಸ್ಥಳಗಳಿಂದ ಆತನನ್ನು ಬೇಸರಗೊಳಿಸಿ ವಿಗ್ರಹಗಳಿಂದ ರೇಗಿಸಿದರು.",
			"59": "<sup>59</sup> ದೇವರು ಇದನ್ನು ತಿಳಿದು ರೌದ್ರನಾಗಿ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಅಸಹ್ಯದಿಂದ ನಿರಾಕರಿಸಿಬಿಟ್ಟನು.",
			"60": "<sup>60</sup> ತಾನು ಜನಮಧ್ಯದಲ್ಲಿ ವಾಸಿಸುವದಕ್ಕೋಸ್ಕರ ಸಿಲೋವಿನಲ್ಲಿ ಹಾಕಿಸಿದ ಗುಡಾರವನ್ನು ತ್ಯಜಿಸಿಬಿಟ್ಟು",
			"61": "<sup>61</sup> ತನ್ನ ಪ್ರತಾಪವನ್ನು ಸೆರೆಗೂ ಮಹಿಮೆಯನ್ನು ವಿರೋಧಿಗಳಿಗೂ ಒಪ್ಪಿಸಿಬಿಟ್ಟನು.",
			"62": "<sup>62</sup> ಆತನು ತನ್ನ ಪ್ರಜೆಯನ್ನು ಖಡ್ಗಕ್ಕೆ ಗುರಿಮಾಡಿ ತನ್ನ ಬಾಧ್ಯತೆಯ ಮೇಲೆ ಉಗ್ರನಾದನು.",
			"63": "<sup>63</sup> ಅವರ ಯೌವನಸ್ಥರು ಅಗ್ನಿಗೆ ಆಹುತಿಯಾದರು; ಅವರ ಕನ್ಯೆಯರಿಗೆ ಶೋಭನವಾಗಲಿಲ್ಲ.",
			"64": "<sup>64</sup> ಯಾಜಕರು ಕತ್ತಿಯಿಂದ ಸಂಹೃತರಾದರು; ಇವರ ವಿಧವೆಯರು ದುಃಖಕ್ರಿಯೆಗಳನ್ನು ನೆರವೇರಿಸಲಿಲ್ಲ.",
			"65": "<sup>65</sup> ಆವರೆಗೆ ನಿದ್ರಿಸುವವನಂತೆಯೂ ದ್ರಾಕ್ಷಾರಸದಿಂದ ಮೈಮರೆತ ವೀರನಂತೆಯೂ ಇದ್ದ ಕರ್ತನು ಫಕ್ಕನೆ ಎಚ್ಚತ್ತು",
			"66": "<sup>66</sup> ತನ್ನ ಶತ್ರುಗಳನ್ನು ಮುರಿಬಡಿದು ನಿತ್ಯನಿಂದೆಗೆ ಅವರನ್ನು ಗುರಿಮಾಡಿದನು.",
			"67": "<sup>67</sup> ಯೋಸೇಫಕುಲದ ಗುಡಾರವನ್ನು ವರ್ಜಿಸಿ ಎಫ್ರಾಯೀಮ್ ಕುಲವನ್ನು ತ್ಯಜಿಸಿ",
			"68": "<sup>68</sup> ಯೆಹೂದಕುಲವನ್ನೂ ತನ್ನ ಪ್ರಿಯ ಚೀಯೋನ್ ಗಿರಿಯನ್ನೂ ಆರಿಸಿಕೊಂಡನು.",
			"69": "<sup>69</sup> ಆತನು ತನ್ನ ಆಲಯವನ್ನು ಪರ್ವತದಂತೆಯೂ ತಾನು ಸ್ಥಾಪಿಸಿದ ಭೂಮಿಯಂತೆಯೂ ಶಾಶ್ವತವಾಗಿ ಕಟ್ಟಿದನು.",
			"70": "<sup>70</sup> ಇದಲ್ಲದೆ ಆತನು ತನ್ನ ಸೇವಕನಾದ ದಾವೀದನನ್ನು ಆರಿಸಿ ಕುರಿಹಟ್ಟಿಯಿಂದ ತೆಗೆದುಕೊಂಡು",
			"71": "<sup>71</sup> ಮರೀಕುರಿಗಳನ್ನು ರಕ್ಷಿಸುವ ಕೆಲಸದಿಂದ ಬಿಡಿಸಿ ಅವನನ್ನು ತನ್ನ ಪ್ರಜೆಗಳಾದ ಯಾಕೋಬ್ಯರನ್ನು ಅಂದರೆ ತನ್ನ ಸ್ವಾಸ್ತ್ಯವಾಗಿರುವ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಸಾಕುವದಕ್ಕೆ ನೇಮಿಸಿದನು.",
			"72": "<sup>72</sup> ಇವನು ಅವರನ್ನು ಯಥಾರ್ಥಹೃದಯದಿಂದ ಸಾಕಿ ತನ್ನ ಹಸ್ತಕೌಶಲ್ಯದಿಂದ ನಡಿಸಿದನು."
		},
		"79": {
			"1": "<sup>1</sup> ದೇವರೇ, ಮ್ಲೇಚ್ಫರು ನಿನ್ನ ಸ್ವಾಸ್ತ್ಯವನ್ನು ಹೊಕ್ಕು ನಿನ್ನ ಪರಿಶುದ್ಧಾಲಯವನ್ನು ಹೊಲೆಮಾಡಿದರು; ಯೆರೂಸಲೇಮ್ ಪಟ್ಟಣವನ್ನು ಹಾಳು ದಿಬ್ಬಗಳಾಗಿ ಮಾಡಿಬಿಟ್ಟರು.",
			"2": "<sup>2</sup> ಅವರು ನಿನ್ನ ಸೇವಕರ ಹೆಣಗಳನ್ನು ಆಕಾಶದ ಪಕ್ಷಿಗಳಿಗೆ ಆಹಾರಮಾಡಿದರು; ನಿನ್ನ ಭಕ್ತರ ದೇಹಮಾಂಸವನ್ನು ಕಾಡುಮೃಗಗಳಿಗೆ ಹಾಕಿದರು.",
			"3": "<sup>3</sup> ಯೆರೂಸಲೇಮಿನ ಸುತ್ತಲೂ ಅವರ ರಕ್ತವನ್ನು ನೀರಿನ ಹಾಗೆ ಚೆಲ್ಲಿದರು; ಅವರನ್ನು ಹೂಣಿಡುವವರು ಯಾರೂ ಇರಲಿಲ್ಲ.",
			"4": "<sup>4</sup> ನಾವು ನಮ್ಮ ನೆರೆಹೊರೆಯ ಜನಾಂಗಗಳಿಗೆ ನಿಂದಾಸ್ಪದರಾದೆವು; ಸುತ್ತಣವರ ಪರಿಹಾಸ್ಯ ಕುಚೋದ್ಯಗಳಿಗೆ ಗುರಿಯಾದೆವು.",
			"5": "<sup>5</sup> ಯೆಹೋವನೇ, ಇನ್ನೆಷ್ಟರವರೆಗೆ ಕೋಪವುಳ್ಳವನಾಗಿರುವಿ? ನಿನ್ನ ರೋಷಾಗ್ನಿಯು ಸದಾಕಾಲವೂ ಉರಿಯುತ್ತಲೇ ಇರಬೇಕೋ?",
			"6": "<sup>6</sup> ನಿನ್ನನ್ನು ಅರಿಯದ ಮ್ಲೇಚ್ಫರ ಮೇಲೆಯೂ ನಿನ್ನ ನಾಮವನ್ನು ಉಚ್ಚರಿಸದ ರಾಜ್ಯಗಳ ಮೇಲೆಯೂ ನಿನ್ನ ರೌದ್ರವನ್ನು ಸುರಿದುಬಿಡು.",
			"7": "<sup>7</sup> ಅವರು ಯಾಕೋಬ್‍ವಂಶದವರನ್ನು ನುಂಗಿಬಿಟ್ಟು ಅವರ ವಾಸಸ್ಥಳಗಳನ್ನು ಹಾಳುಮಾಡಿದರಲ್ಲಾ.",
			"8": "<sup>8</sup> ಪೂರ್ವಿಕರ ಅಪರಾಧಗಳನ್ನು ನಮ್ಮ ಹಾನಿಗಾಗಿ ನೆನಸಿಕೊಳ್ಳಬೇಡ. ಬೇಗನೆ ನಿನ್ನ ಕನಿಕರವು ನಮ್ಮನ್ನು ಎದುರುಗೊಳ್ಳಲಿ; ಬಹುವಾಗಿ ಕುಗ್ಗಿಹೋಗಿದ್ದೇವೆ.",
			"9": "<sup>9</sup> ನಮ್ಮನ್ನು ರಕ್ಷಿಸುವ ದೇವರೇ, ನಿನ್ನ ನಾಮದ ಘನತೆಗೋಸ್ಕರ ಸಹಾಯಮಾಡು; ನಿನ್ನ ಹೆಸರಿಗೆ ತಕ್ಕಂತೆ ರಕ್ಷಿಸಿ ನಮ್ಮ ಪಾಪಗಳನ್ನು ಅಳಿಸಿಬಿಡು.",
			"10": "<sup>10</sup> ಮ್ಲೇಚ್ಫರು - ಅವರ ದೇವರು ಎಲ್ಲಿ ಎಂದು ಅನ್ನುವದೇಕೆ? ನಿನ್ನ ಸೇವಕರ ರಕ್ತವನ್ನು ಸುರಿಸಿದವರಿಗೆ ನಮ್ಮ ಮುಂದೆಯೇ ದಂಡನೆಯಾದದ್ದು ಜನಾಂಗಗಳಿಗೆ ಗೊತ್ತಾಗಲಿ.",
			"11": "<sup>11</sup> ಸೆರೆಹೋದವರ ನರಳುವಿಕೆಯು ನಿನ್ನ ಲಕ್ಷ್ಯಕ್ಕೆ ಬರಲಿ; ಸಾಯಲಿರುವವರನ್ನು ನಿನ್ನ ಭುಜಮಹತ್ತಿನಿಂದ ಉಳಿಸು.",
			"12": "<sup>12</sup> ಸ್ವಾಮಿಯೇ, ನಿನ್ನನ್ನು ನಿಂದಿಸಿದ ಸುತ್ತಣ ಜನಾಂಗಗಳ ಉಡಿಲಲ್ಲಿ ಏಳರಷ್ಟು ಪ್ರತಿಫಲವು ತುಂಬಿರುವಂತೆಮಾಡು.",
			"13": "<sup>13</sup> ನಿನ್ನ ಪ್ರಜೆಯೂ ನೀನು ಪಾಲಿಸುವ ಮಂದೆಯೂ ಆಗಿರುವ ನಾವಾದರೋ ಸದಾ ನಿನ್ನನ್ನು ಸ್ತುತಿಸುವವರಾಗಿ ಮುಂದಣ ಸಂತಾನದವರೆಲ್ಲರಿಗೂ ನಿನ್ನ ಮಹತ್ತನ್ನು ವರ್ಣಿಸುತ್ತಾ ಹೋಗುವೆವು."
		},
		"80": {
			"1": "<sup>1</sup> ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಕಾಯುವ ಕುರುಬನೇ, ಕಿವಿಗೊಡು; ಯೋಸೇಫನ ವಂಶದವರನ್ನು ಕುರಿಹಿಂಡಿನಂತೆ ಕರತಂದವನೇ, ಕೆರೂಬಿಯರ ಮಧ್ಯದಲ್ಲಿ ಆಸೀನನಾಗಿರುವಾತನೇ, ಪ್ರಕಾಶಿಸು.",
			"2": "<sup>2</sup> ಎಫ್ರಾಯೀಮ್ ಬೆನ್ಯಾಮೀನ್ ಮನಸ್ಸೆ ಕುಲಗಳ ಮುಂದೆ ಹೋಗುವವನಾಗಿ ನಿನ್ನ ಶೌರ್ಯವನ್ನು ತೋರ್ಪಡಿಸು; ಬಂದು ನಮಗೆ ಜಯಪ್ರದನಾಗು.",
			"3": "<sup>3</sup> ದೇವರೇ, ನಮ್ಮನ್ನು ತಿರಿಗಿ ಉನ್ನತಸ್ಥಿತಿಗೇರಿಸು. ಪ್ರಸನ್ನಮುಖದಿಂದ ನೋಡು; ಆಗ ಉದ್ಧಾರವಾಗುವೆವು.",
			"4": "<sup>4</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವದೇವರೇ, ನಿನಗೆ ಮೊರೆಯಿಡುವ ನಿನ್ನ ಪ್ರಜೆಗೆ ಇನ್ನೆಷ್ಟರವರೆಗೆ ಮುನಿದಿರುವಿ?",
			"5": "<sup>5</sup> ರೋದನವೇ ಅವರ ಅನ್ನವಾಗುವಂತೆಯೂ ಅಶ್ರುಧಾರೆಯೇ ಪಾನವಾಗುವಂತೆಯೂ ಮಾಡಿದಿ.",
			"6": "<sup>6</sup> ಸುತ್ತಣ ಜನಾಂಗಗಳಿಗೆ ನಮ್ಮನ್ನು ಕಚ್ಚಾಟದ ಹೇತುವನ್ನಾಗಿಯೂ ಶತ್ರುನಿಂದೆಗೆ ಗುರಿಯನ್ನಾಗಿಯೂ ಮಾಡಿದಿ.",
			"7": "<sup>7</sup> ಸೇನಾಧೀಶ್ವರನಾದ ದೇವರೇ, ನಮ್ಮನ್ನು ತಿರಿಗಿ ಉನ್ನತಸ್ಥಿತಿಗೇರಿಸು. ಪ್ರಸನ್ನಮುಖದಿಂದ ನೋಡು; ಆಗ ಉದ್ಧಾರವಾಗುವೆವು.",
			"8": "<sup>8</sup> ನೀನು ಐಗುಪ್ತದೇಶದಿಂದ ಒಂದು ದ್ರಾಕ್ಷಾಲತೆಯನ್ನು ತಂದು ಜನಾಂಗಗಳನ್ನು ಹೊರಗೆ ಹಾಕಿ ಅದನ್ನು ನೆಟ್ಟಿ.",
			"9": "<sup>9</sup> ಅದಕ್ಕೋಸ್ಕರ ನೆಲವನ್ನು ಹಸನುಮಾಡಿದ ಮೇಲೆ ಅದು ಬೇರು ಬಿಟ್ಟು ದೇಶದಲ್ಲೆಲ್ಲಾ ಹಬ್ಬಿಕೊಂಡಿತು.",
			"10": "<sup>10</sup> ಅದರ ನೆರಳಿನಿಂದ ಗುಡ್ಡಗಳೆಲ್ಲಾ ಕವಿಯಲ್ಪಟ್ಟವು. ಅದರ ಕುಡಿಗಳು ದೇವದಾರುವೃಕ್ಷಗಳ ಮೇಲೆ ಹಬ್ಬಿ ಅವುಗಳನ್ನು ಮುಚ್ಚಿಬಿಟ್ಟವು.",
			"11": "<sup>11</sup> ಅದರ ಕೊಂಬೆಗಳು ಸಮುದ್ರದವರೆಗೂ ಅದರ ರೆಂಬೆಗಳು ಮಹಾನದಿಯವರೆಗೂ ಹರಡಿಕೊಂಡವು.",
			"12": "<sup>12</sup> ನೀನು ಅದರ ಬೇಲಿಯನ್ನೇಕೆ ಕಿತ್ತು ಹಾಕಿದಿ? ದಾರಿಗರೆಲ್ಲರು ಅದನ್ನು ತರೆದುಬಿಡುತ್ತಾರೆ.",
			"13": "<sup>13</sup> ಕಾಡುಹಂದಿಯು ಅದನ್ನು ನಿರ್ಮೂಲಮಾಡುತ್ತದೆ; ಅರಣ್ಯಮೃಗಗಳು ಅದನ್ನು ತಿಂದುಹಾಕುತ್ತವೆ.",
			"14": "<sup>14</sup> ಸೇನಾಧೀಶ್ವರನಾದ ದೇವರೇ, ಅಭಿಮುಖನಾಗಬೇಕು; ನೀನು ಪರಲೋಕದಿಂದ ಕಟಾಕ್ಷಿಸಿ ಈ ದ್ರಾಕ್ಷಾಲತೆಯನ್ನು ಪರಾಂಬರಿಸು.",
			"15": "<sup>15</sup> ನಿನ್ನ ಬಲಗೈ ನೆಟ್ಟು ಸಾಕಿ ಬೆಳಸಿದ ಸಸಿಯನ್ನು ಕಾಪಾಡು.",
			"16": "<sup>16</sup> ಅದು ಕಡಿದು ಬೆಂಕಿಯಿಂದ ಸುಡಲ್ಪಟ್ಟಿದೆ. ಅವರು ನಿನ್ನ ಬಾಯಿಗದರಿಕೆಯಿಂದ ನಾಶವಾಗುತ್ತಾರೆ.",
			"17": "<sup>17</sup> ನಿನ್ನ ಬಲಗೈ ಉದ್ಧರಿಸಿದ ಪುರುಷನೂ ನೀನು ನಿನಗೋಸ್ಕರ ಸಾಕಿ ಬೆಳಸಿದ ನರಪುತ್ರನೂ ಆಗಿರುವವನನ್ನು ಹಸ್ತದಿಂದ ಹಿಡಿದಿರು.",
			"18": "<sup>18</sup> ಆಗ ನಾವು ನಿನ್ನಿಂದ ಅಗಲುವದಿಲ್ಲ. ನಿನ್ನ ಹೆಸರನ್ನು ಹೇಳಿಕೊಂಡು ಆರಾಧಿಸುವಂತೆ ನಮ್ಮನ್ನು ಚೈತನ್ಯಗೊಳಿಸು.",
			"19": "<sup>19</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವದೇವರೇ, ನಮ್ಮನ್ನು ತಿರಿಗಿ ಉನ್ನತಸ್ಥಿತಿಗೇರಿಸು. ಪ್ರಸನ್ನಮುಖದಿಂದ ನೋಡು; ಆಗ ಉದ್ಧಾರವಾಗುವೆವು."
		},
		"81": {
			"1": "<sup>1</sup> ನಮಗೆ ಬಲಪ್ರದನಾಗಿರುವ ದೇವರಿಗೆ ಉತ್ಸಾಹ ಧ್ವನಿಮಾಡಿರಿ; ಯಾಕೋಬ್ಯರ ದೇವರಿಗೆ ಜಯಘೋಷಮಾಡಿರಿ.",
			"2": "<sup>2</sup> ರಾಗವನ್ನು ಎತ್ತಿರಿ; ದಮ್ಮಡಿಯನ್ನು ಬಡಿಯಿರಿ; ಇಂಪಾದ ಕಿನ್ನರಿಯನ್ನೂ ಸ್ವರಮಂಡಲವನ್ನೂ ಬಾರಿಸಿರಿ.",
			"3": "<sup>3</sup> ಅಮಾವಾಸ್ಯೆಯಲ್ಲಿಯೂ ನಮ್ಮ ಉತ್ಸವದಿನವಾಗಿರುವ ಪೂರ್ಣಿಮೆಯಲ್ಲಿಯೂ ಕೊಂಬನ್ನು ಊದಿರಿ.",
			"4": "<sup>4</sup> ಇದು ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಒಂದು ಕಟ್ಟಳೆ; ಇದು ಯಾಕೋಬ್ಯರ ದೇವರು ವಿಧಿಸಿದ್ದು.",
			"5": "<sup>5</sup> ಆತನು ಐಗುಪ್ತದೇಶವನ್ನು ಬಾಧಿಸಲಿಕ್ಕೆ ಹೊರಟಾಗ ನೆನಪಿಗಾಗಿ ಯೋಸೇಫ್ಯರಲ್ಲಿ ಇದನ್ನು ನೇಮಿಸಿದನು. ಗುರುತಿಲ್ಲದವನ ಮಾತು ನನಗೆ ಕೇಳಿಸುತ್ತದೆ; ಏನಂದರೆ -",
			"6": "<sup>6</sup> ಅವನ ಹೆಗಲನ್ನು ಹೊರೆಗೆ ತಪ್ಪಿಸಿದೆನು; ಅವನ ಕೈಗಳನ್ನು ಪುಟ್ಟಿಯಿಂದ ಬಿಡಿಸಿದೆನು.",
			"7": "<sup>7</sup> ಕಷ್ಟದಲ್ಲಿ ಮೊರೆಯಿಟ್ಟ ನಿನ್ನನ್ನು ವಿಮೋಚಿಸಿದೆನು; ಗುಡುಗುವ ಮೋಡದಲ್ಲಿದ್ದು ನಿನಗೆ ಉತ್ತರವನ್ನು ಕೊಟ್ಟೆನು; ಮೆರೀಬಾ ಪ್ರವಾಹಗಳ ಬಳಿಯಲ್ಲಿ ನಿನ್ನನ್ನು ಪರೀಕ್ಷಿಸಿದೆನು. ಸೆಲಾ.",
			"8": "<sup>8</sup> ನನ್ನ ಜನರೇ, ಕೇಳಿರಿ; ಖಂಡಿತವಾಗಿ ಹೇಳುತ್ತೇನೆ. ಇಸ್ರಾಯೇಲ್ಯರೇ, ಕಿವಿಗೊಟ್ಟರೆ ಎಷ್ಟೋ ಒಳ್ಳೇದು -",
			"9": "<sup>9</sup> ನಿಮ್ಮಲ್ಲಿ ಅನ್ಯದೇವತೆಗಳು ಇರಬಾರದು; ಪರರ ದೇವತೆಗಳನ್ನು ಪೂಜಿಸಬಾರದು;",
			"10": "<sup>10</sup> ನಿಮ್ಮನ್ನು ಐಗುಪ್ತದಿಂದ ಕರತಂದ ಯೆಹೋವನೆಂಬ ನಾನೇ ನಿಮ್ಮ ದೇವರು. ಅಗಲವಾಗಿ ಬಾಯಿದೆರೆಯಿರಿ; ಅದನ್ನು ತುಂಬಿಸುವೆನು ಅಂದೆನು.",
			"11": "<sup>11</sup> ಆದರೆ ನನ್ನ ಜನರು ನನ್ನ ಮಾತು ಕೇಳಲಿಲ್ಲ; ಇಸ್ರಾಯೇಲ್ಯರು ನನಗೆ ಸಮ್ಮತಿಸಲಿಲ್ಲ.",
			"12": "<sup>12</sup> ಆದದರಿಂದ - ಇವರು ಹಟಮಾರಿಗಳು; ತಮ್ಮ ಮನಸ್ಸಿನಂತೆ ನಡೆಯಲಿ ಎಂದು ಸಡಿಲ ಬಿಟ್ಟೆನು.",
			"13": "<sup>13</sup> ನನ್ನ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಾಯೇಲ್ಯರು ನನ್ನ ಮಾತನ್ನು ಕೇಳಿ ನನ್ನ ಮಾರ್ಗದಲ್ಲಿ ನಡೆದರೆ ಎಷ್ಟೋ ಒಳ್ಳೇದು!",
			"14": "<sup>14</sup> ನಾನು ಅವರ ಎದುರಾಳಿಗಳ ಮೇಲೆ ಕೈಯೆತ್ತಿ ಅವರ ಶತ್ರುಗಳನ್ನು ಸುಲಭವಾಗಿ ಅಣಗಿಸುವೆನು.",
			"15": "<sup>15</sup> ಯೆಹೋವನ ದ್ವೇಷಿಗಳು ಅವರ ಮುಂದೆ ಮುದುರಿಕೊಳ್ಳುವರು; ಅವರಾದರೋ ಸದಾಕಾಲವೂ ಇರುವರು.",
			"16": "<sup>16</sup> ನಾನು ಅವರಿಗೆ ಶ್ರೇಷ್ಠವಾದ ಗೋದಿಯನ್ನು ಊಟಕ್ಕೆ ಕೊಟ್ಟು ಬಂಡೆಯೊಳಗಿನ ಜೇನಿನಿಂದ ತೃಪ್ತಿಪಡಿಸುವೆನು."
		},
		"82": {
			"1": "<sup>1</sup> ದೇವರು ತನ್ನ ಸಭೆಯಲ್ಲಿ ನಿಂತುಕೊಂಡವನಾಗಿ ದೇವರುಗಳೊಳಗೆ ನ್ಯಾಯವಿಧಿಸುತ್ತಾನೆ -",
			"2": "<sup>2</sup> ನೀವು ಅನ್ಯಾಯವಾಗಿ ತೀರ್ಪುಕೊಡುವದೂ ದುಷ್ಟರಿಗೆ ಮುಖದಾಕ್ಷಿಣ್ಯ ತೋರಿಸುವದೂ ಇನ್ನೆಷ್ಟರವರೆಗೆ? ಸೆಲಾ.",
			"3": "<sup>3</sup> ಕುಗ್ಗಿದವರ ಮತ್ತು ಅನಾಥರ ನ್ಯಾಯವನ್ನು ಸ್ಥಾಪಿಸಿರಿ. ದುಃಖಿತರ ಮತ್ತು ದರಿದ್ರರ ನೀತಿಯನ್ನು ಉಳಿಸಿರಿ.",
			"4": "<sup>4</sup> ಕುಗ್ಗಿದವರನ್ನೂ ಬಡವರನ್ನೂ ದುಷ್ಟರ ಕೈಯಿಂದ ಬಿಡಿಸಿ ರಕ್ಷಿಸಿರಿ.",
			"5": "<sup>5</sup> ಇವರು ಬುದ್ಧಿಹೀನರೂ ವಿವೇಕ ಶೂನ್ಯರೂ ಆಗಿ ಅಂಧಕಾರದಲ್ಲಿ ಅಲೆಯುತ್ತಾರೆ. ಭೂಮಿಯ ಅಸ್ತಿವಾರಗಳೆಲ್ಲಾ ಕದಲುತ್ತವೆ.",
			"6": "<sup>6</sup> ನೀವು ದೇವರುಗಳು, ಎಲ್ಲರೂ ಪರಾತ್ಪರನ ಮಕ್ಕಳು ಎಂದು ನಾನು ಹೇಳಿದೆನು.",
			"7": "<sup>7</sup> ಆದರೂ ನರರಂತೆ ಸತ್ತೇ ಹೋಗುವಿರಿ; ಪ್ರತಿಯೊಬ್ಬ ಪ್ರಭುವಿನಂತೆ ನೀವೆಲ್ಲರೂ ಬಿದ್ದು ಹೋಗುವಿರಿ.",
			"8": "<sup>8</sup> ದೇವರೇ, ಏಳು; ಭೂಲೋಕದಲ್ಲಿ ನ್ಯಾಯವನ್ನು ಸ್ಥಾಪಿಸು. ನೀನು ಎಲ್ಲಾ ಜನಾಂಗಗಳ ಒಡೆಯನು ಅಲ್ಲವೋ?"
		},
		"83": {
			"1": "<sup>1</sup> ದೇವರೇ, ಸುಮ್ಮನಿರಬೇಡ; ನಿಶ್ಚಿಂತನಾಗಿ ಮೌನದಿಂದಿರಬೇಡ ಸ್ವಾಮೀ.",
			"2": "<sup>2</sup> ನೋಡು, ನಿನ್ನ ಶತ್ರುಗಳು ಘೋಷಿಸುತ್ತಾರೆ; ನಿನ್ನ ದ್ವೇಷಿಗಳು ತಲೆಯೆತ್ತಿದ್ದಾರೆ.",
			"3": "<sup>3</sup> ಅವರು ನಿನ್ನ ಪ್ರಜೆಗಳಿಗೆ ವಿರೋಧವಾಗಿ ಒಳಸಂಚು ಮಾಡಿ ನಿನ್ನ ಮರೆಹೊಕ್ಕವರನ್ನು ಕೆಡಿಸಬೇಕೆಂದು ಆಲೋಚಿಸಿ -",
			"4": "<sup>4</sup> ಬನ್ನಿರಿ; ಅವರು ಜನಾಂಗವಾಗಿ ಉಳಿಯದಂತೆಯೂ ಇಸ್ರಾಯೇಲೆಂಬ ಹೆಸರು ಅಳಿದುಹೋಗುವಂತೆಯೂ ಅವರನ್ನು ಸಂಹರಿಸೋಣ ಅಂದುಕೊಳ್ಳುತ್ತಾರೆ.",
			"5": "<sup>5</sup> ಎದೋಮ್ಯರ ಮತ್ತು ಇಷ್ಮಾಯೇಲ್ಯರ ಪಾಳೆಯಗಳವರು, ಮೋವಾಬ್ಯರು, ಹಗ್ರೀಯರು,",
			"6": "<sup>6</sup> ಗೆಬಾಲ್ಯರು, ಅಮ್ಮೋನಿಯರು, ಅಮಾಲೇಕ್ಯರು, ಫಿಲಿಷ್ಟಿಯರು, ತೂರ್ ಸಂಸ್ಥಾನದವರು,",
			"7": "<sup>7</sup> ಇವರೆಲ್ಲಾ ಏಕಮನಸ್ಸಿನಿಂದ ಕೂಡಿ ನಿನಗೇ ವಿರೋಧವಾಗಿ ಒಳಸಂಚು ಮಾಡುತ್ತಾರಲ್ಲಾ;",
			"8": "<sup>8</sup> ಅಶ್ಯೂರ್ಯರೂ ಇವರೊಡನೆ ಕೂಡಿಕೊಂಡು ಲೋಟನ ವಂಶದವರಿಗೆ ಭುಜವಾಗಿದ್ದಾರೆ. ಸೆಲಾ.",
			"9": "<sup>9</sup> ನೀನು ಮಿದ್ಯಾನ್ಯರನ್ನು ಸಂಹರಿಸಿದಂತೆ ಇವರನ್ನೂ ಸಂಹರಿಸು. ಕೀಷೋನ್‍ಹಳ್ಳದ ಬಳಿಯಲ್ಲಿ ಸೀಸೆರ ಯಾಬೀನ್ ಎಂಬವರಿಗೆ ಮಾಡಿದಂತೆ ಇವರಿಗೂ ಮಾಡು.",
			"10": "<sup>10</sup> ಅವರು ಎಂದೋರಿನಲ್ಲಿ ವಧಿಸಲ್ಪಟ್ಟು ಹೊಲದ ಗೊಬ್ಬರವಾಗಿಹೋದರಲ್ಲಾ.",
			"11": "<sup>11</sup> ಓರೇಬ್ ಜೇಬ್ ಎಂಬವರ ಗತಿಯು ಇವರ ಶ್ರೀಮಂತರಿಗೂ ಸಂಭವಿಸಲಿ; ಜೇಬಹ ಚಲ್ಮುನ್ನ ಎಂಬವರ ಹಾಗೆ ಇವರ ಪ್ರಭುಗಳಿಗೂ ಆಗಲಿ.",
			"12": "<sup>12</sup> ದೇವರು ಅವರಿಗೆ ಕೊಟ್ಟ ದೇಶವನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವಾ ಅಂದುಕೊಳ್ಳುತ್ತಾರಲ್ಲಾ.",
			"13": "<sup>13</sup> ನನ್ನ ದೇವರೇ, ಅವರನ್ನು ಗಾಳಿಯಲ್ಲಿ ಸುತ್ತಿಯಾಡುವ ಧೂಳಿನಂತೆಯೂ ಹಾರಿಹೋಗುವ ಹೊಟ್ಟಿನಂತೆಯೂ ಮಾಡು.",
			"14": "<sup>14</sup> ನೀನು ಕಾಡನ್ನು ಸುಟ್ಟುಬಿಡುವ ಬೆಂಕಿಯಂತೆಯೂ ಪರ್ವತಗಳನ್ನು ದಹಿಸಿಬಿಡುವ ಜ್ವಾಲೆಯಂತೆಯೂ ಇದ್ದು",
			"15": "<sup>15</sup> ನಿನ್ನ ಸುಂಟರಗಾಳಿಯಿಂದ ಅವರನ್ನು ಬೆನ್ನಟ್ಟು; ತುಫಾನಿನಿಂದ ಅವರನ್ನು ಕಳವಳಗೊಳಿಸು.",
			"16": "<sup>16</sup> ನಾಚಿಕೆಯು ಅವರ ಮುಖವನ್ನು ಕವಿಯಲಿ. ಯೆಹೋವನೇ, ಆಗ ಅವರು ನಿನ್ನ ಹೆಸರನ್ನು ಕೇಳಿಕೊಂಡು ಬಂದಾರು.",
			"17": "<sup>17</sup> ಅವರು ನಿರಂತರವೂ ಆಶಾಭಂಗದಿಂದ ಕಳವಳಗೊಳ್ಳಲಿ; ಅಪಮಾನದಿಂದ ನಾಶವಾಗಲಿ.",
			"18": "<sup>18</sup> ಆಗ ಯೆಹೋವನಾಮದಿಂದ ಪ್ರಸಿದ್ಧನಾದ ನೀನೊಬ್ಬನೇ ಭೂಲೋಕದಲ್ಲೆಲ್ಲಾ ಸರ್ವೋನ್ನತನೆಂದು ಗ್ರಹಿಸುವರು."
		},
		"84": {
			"1": "<sup>1</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೇ, ನಿನ್ನ ನಿವಾಸಗಳು ಎಷ್ಟೋ ರಮ್ಯವಾಗಿವೆ.",
			"2": "<sup>2</sup> ಯೆಹೋವನ ಆಲಯದ ಅಂಗಳಗಳಲ್ಲಿ ಸೇರಬೇಕೆಂದು ನನ್ನ ಆತ್ಮವು ಹಂಬಲಿಸುತ್ತಾ ಕುಂದಿಹೋಗಿತ್ತು. [ಆದರೆ ಈಗ] ಚೈತನ್ಯಸ್ವರೂಪನಾದ ದೇವರಿಗೆ ನನ್ನ ತನುಮನಗಳಿಂದ ಹರ್ಷಧ್ವನಿ ಮಾಡುತ್ತೇನೆ.",
			"3": "<sup>3</sup> ಆಹಾ, ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವ ದೇವರೇ, ನನ್ನ ಅರಸನೇ, ನಿನ್ನ ವೇದಿಗಳ ಸಮೀಪದಲ್ಲಿಯೇ ಗುಬ್ಬಿಗೆ ಮನೆಯೂ ಪಾರಿವಾಳಕ್ಕೆ ಮರಿಮಾಡುವ ಗೂಡೂ ದೊರಕಿತಲ್ಲಾ!",
			"4": "<sup>4</sup> ನಿನ್ನ ಮಂದಿರದಲ್ಲಿ ವಾಸಿಸುವವರು ಧನ್ಯರು. ಅವರು ನಿತ್ಯವೂ ನಿನ್ನನ್ನು ಕೀರ್ತಿಸುತ್ತಾ ಇರುತ್ತಾರೆ. ಸೆಲಾ.",
			"5": "<sup>5</sup> ನಿನ್ನಲ್ಲಿ ಬಲವನ್ನು ಹೊಂದುವ ಮನುಷ್ಯರು ಧನ್ಯರು. ಅವರು ಯಾತ್ರಾಪರರಾಗಿ",
			"6": "<sup>6</sup> ಕಣ್ಣೀರಿನ ತಗ್ಗನ್ನು ದಾಟುವಾಗ ಅಲ್ಲಿ ಒರತೆಗಳನ್ನು ತೋಡಿ ನೀರಿನ ಸ್ಥಳವಾಗ ಮಾಡುತ್ತಾರೆ. ಮುಂಗಾರು ಮಳೆಯೂ ಅದನ್ನು ಸಮೃದ್ಧಿಗೊಳಿಸುತ್ತದೆ.",
			"7": "<sup>7</sup> ಅವರು ಹೆಚ್ಚುಹೆಚ್ಚಾಗಿ ಬಲಹೊಂದಿ ಚೀಯೋನ್‍ಗಿರಿಯಲ್ಲಿ ದೇವರ ಸನ್ನಿಧಿಯನ್ನು ಸೇರಿ -",
			"8": "<sup>8</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವ ದೇವರೇ, ನನ್ನ ಮೊರೆಯನ್ನು ಕೇಳು; ಯಾಕೋಬ್ಯರ ದೇವರೇ, ಕಿವಿಗೊಡು ಅನ್ನುತ್ತಾರೆ. ಸೆಲಾ.",
			"9": "<sup>9</sup> ದೇವರೇ, ನಮಗೆ ಗುರಾಣಿಯಾಗಿರುವವನನ್ನು ನೋಡು. ನಿನ್ನ ಅಭಿಷಿಕ್ತನ ಮುಖವನ್ನು ಕಟಾಕ್ಷಿಸು.",
			"10": "<sup>10</sup> ನಿನ್ನ ಆಲಯದ ಅಂಗಳಗಳಲ್ಲಿ ಕಳೆದ ಒಂದು ದಿನವು ಬೇರೆ ಸಹಸ್ರದಿನಗಳಿಗಿಂತ ಉತ್ತಮವಾಗಿದೆ. ದುಷ್ಟರ ಗುಡಾರಗಳಲ್ಲಿ ವಾಸಿಸುವದಕ್ಕಿಂತ ನನ್ನ ದೇವರ ಆಲಯದ ಬಾಗಿಲನ್ನು ಕಾಯ್ದುಕೊಂಡಿರುವದೇ ಲೇಸು.",
			"11": "<sup>11</sup> ಯೆಹೋವದೇವರು ಸೂರ್ಯನೂ ಗುರಾಣಿಯೂ ಆಗಿದ್ದಾನಲ್ಲಾ. ಯೆಹೋವನು ಕೃಪೆಯನ್ನೂ ಘನವನ್ನೂ ಅನುಗ್ರಹಿಸುವನು; ಆತನು ಸದ್ಭಕ್ತರಿಗೆ ಯಾವ ಶುಭವನ್ನು ದಯಪಾಲಿಸದೆ ಇದ್ದಾನು?",
			"12": "<sup>12</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೇ, ನಿನ್ನಲ್ಲಿ ಭರವಸವಿಡುವ ಮನುಷ್ಯನು ಧನ್ಯನು."
		},
		"85": {
			"1": "<sup>1</sup> ಯೆಹೋವನೇ, ನಿನ್ನ ದೇಶವನ್ನು ಕಟಾಕ್ಷಿಸಿದ್ದೀ; ಯಾಕೋಬ್ಯರನ್ನು ಸೆರೆಯಿಂದ ಬಿಡಿಸಿ ಬರಮಾಡಿದ್ದೀ.",
			"2": "<sup>2</sup> ನಿನ್ನ ಪ್ರಜೆಯ ದ್ರೋಹವನ್ನು ಕ್ಷಮಿಸಿದ್ದೀ; ಅವರ ಎಲ್ಲಾ ಪಾಪಗಳನ್ನು ಅಳಿಸಿಬಿಟ್ಟಿದ್ದೀ. ಸೆಲಾ.",
			"3": "<sup>3</sup> ನಿನ್ನ ರೌದ್ರವನ್ನೆಲ್ಲಾ ತೊರೆದಿದ್ದೀ; ನಿನ್ನ ಉಗ್ರಕೋಪವನ್ನು ಬಿಟ್ಟಿದ್ದೀ.",
			"4": "<sup>4</sup> ನಮ್ಮನ್ನು ರಕ್ಷಿಸುವ ದೇವರೇ, ನಮಗೆ ಅಭಿಮುಖನಾಗು; ನಮ್ಮ ವಿಷಯದಲ್ಲಿ ಬೇಸರವನ್ನು ಬಿಡು.",
			"5": "<sup>5</sup> ಸದಾಕಾಲವೂ ನಮ್ಮ ಮೇಲೆ ಸಿಟ್ಟುಮಾಡುವಿಯಾ? ತಲತಲಾಂತರಗಳಿಗೂ ಕೋಪವನ್ನು ಬೆಳೆಸುವಿಯೋ?",
			"6": "<sup>6</sup> ನಿನ್ನ ಪ್ರಜೆಯಾದ ನಾವು ನಿನ್ನಲ್ಲಿ ಆನಂದಿಸುವಂತೆ ನೀನು ನಮ್ಮನ್ನು ಚೈತನ್ಯ ಪಡಿಸುವದಿಲ್ಲವೋ?",
			"7": "<sup>7</sup> ಯೆಹೋವನೇ, ನಿನ್ನ ಕೃಪೆಯನ್ನು ನಮಗೆ ತೋರಿಸು; ನಿನ್ನ ರಕ್ಷಣೆಯನ್ನು ಅನುಗ್ರಹಿಸು.",
			"8": "<sup>8</sup> ಯೆಹೋವದೇವರು ಏನು ಹೇಳುತ್ತಾನೋ ಕೇಳುತ್ತೇನೆ. ಆತನು ತನ್ನ ಭಕ್ತಜನರಿಗೆ ಸಮಾಧಾನವಾಕ್ಯವನ್ನು ಹೇಳುತ್ತಾನಲ್ಲಾ. ಅವರಾದರೋ ತಿರಿಗಿ ಮೂರ್ಖತನದಲ್ಲಿ ಬೀಳದಿರಲಿ.",
			"9": "<sup>9</sup> ಭಯಭಕ್ತಿಯುಳ್ಳ ಜನರಿಗೆ ಆತನ ರಕ್ಷಣೆಯು ಹತ್ತಿರವಿರುವದು ಸತ್ಯ. ಇದರಿಂದ ಆತನ ಮಹಿಮೆ ನಮ್ಮ ದೇಶದಲ್ಲಿ ನೆಲೆಗೊಳ್ಳುವದು.",
			"10": "<sup>10</sup> ಕೃಪೆಯೂ ಸತ್ಯವೂ ಒಂದನ್ನೊಂದು ಕೂಡಿರುವವು; ನೀತಿಯೂ ಸಮಾಧಾನವೂ ಮುದ್ದಿಟ್ಟುಕೊಳ್ಳುವವು.",
			"11": "<sup>11</sup> ಸತ್ಯತೆಯು ಭೂಮಿಯಿಂದ ಹುಟ್ಟುವದು; ನೀತಿಯು ಆಕಾಶದಿಂದ ಕೆಳಗೆ ದೃಷ್ಟಿಸುವದು.",
			"12": "<sup>12</sup> ನಿಜವಾಗಿ ಯೆಹೋವನು ಒಳ್ಳೆಯದನ್ನು ಅನುಗ್ರಹಿಸುವನು; ನಮ್ಮ ದೇಶವು ತನ್ನ ಬೆಳೆ ಕೊಡುವದು.",
			"13": "<sup>13</sup> ನೀತಿಯು ಆತನ ಮುಂದೆ ಹೋಗುತ್ತಾ ನಾವು ಆತನ ಹೆಜ್ಜೆಹಿಡಿದು ನಡೆಯುವಂತೆ ದಾರಿಮಾಡುವದು."
		},
		"86": {
			"1": "<sup>1</sup> ಯೆಹೋವನೇ, ಕಿವಿಗೊಡು; ನನಗೆ ಸದುತ್ತರವನ್ನು ದಯಪಾಲಿಸು; ಕುಗ್ಗಿದವನೂ ದಿಕ್ಕಿಲ್ಲದವನೂ ಆಗಿದ್ದೇನೆ.",
			"2": "<sup>2</sup> ನಾನು ನಿನ್ನ ಭಕ್ತನು; ನನ್ನ ಪ್ರಾಣವನ್ನು ಉಳಿಸು; ನೀನೇ ನನ್ನ ದೇವರು; ನಿನ್ನಲ್ಲಿ ಭರವಸವಿಟ್ಟಿರುವ ನಿನ್ನ ಸೇವಕನನ್ನು ರಕ್ಷಿಸು.",
			"3": "<sup>3</sup> ಕರ್ತನೇ, ಕರುಣಿಸು; ದಿನವೆಲ್ಲಾ ನಿನಗೆ ಮೊರೆಯಿಡುತ್ತೇನೆ.",
			"4": "<sup>4</sup> ಕರ್ತನೇ, ನಿನ್ನ ಸೇವಕನ ಹೃದಯವನ್ನು ಆನಂದಗೊಳಿಸು; ನಿನ್ನನ್ನೇ ನಿರೀಕ್ಷಿಸುತ್ತಿರುವೆನಲ್ಲಾ.",
			"5": "<sup>5</sup> ಕರ್ತನೇ, ನೀನು ಒಳ್ಳೆಯವನೂ ಕ್ಷಮಿಸುವವನೂ ನಿನಗೆ ಮೊರೆಯಿಡುವವರೆಲ್ಲರಲ್ಲಿ ಕೃಪಾಪೂರ್ಣನೂ ಆಗಿದ್ದೀಯಲ್ಲಾ.",
			"6": "<sup>6</sup> ಯೆಹೋವನೇ, ನನ್ನ ಪ್ರಾರ್ಥನೆಗೆ ಕಿವಿಗೊಡು; ನನ್ನ ವಿಜ್ಞಾಪನೆಗಳ ಶಬ್ದವನ್ನು ಲಾಲಿಸು.",
			"7": "<sup>7</sup> ನೀನು ಸದುತ್ತರವನ್ನು ದಯಪಾಲಿಸುವಿಯೆಂದು ನಂಬಿ ನನ್ನ ಇಕ್ಕಟ್ಟಿನ ದಿನದಲ್ಲಿ ನಿನ್ನನ್ನೇ ಕರೆಯುವೆನು.",
			"8": "<sup>8</sup> ಕರ್ತನೇ, ದೇವರುಗಳಲ್ಲಿ ನಿನಗೆ ಸಮಾನನೇ ಇಲ್ಲ; ನಿನ್ನ ಕೃತ್ಯಗಳಿಗೆ ಸರಿಯಾದದ್ದು ಇನ್ನೊಂದಿಲ್ಲ.",
			"9": "<sup>9</sup> ಕರ್ತನೇ, ನಿನ್ನಿಂದುಂಟಾದ ಎಲ್ಲಾ ಜನಾಂಗಗಳು ಬಂದು ನಿನಗೆ ಅಡ್ಡಬಿದ್ದು ನಿನ್ನ ನಾಮವನ್ನು ಘನಪಡಿಸುವವು.",
			"10": "<sup>10</sup> ಮಹೋನ್ನತನೂ ಮಹತ್ಕಾರ್ಯಗಳನ್ನು ನಡಿಸುವವನೂ ನೀನು; ದೇವರು ನೀನೊಬ್ಬನೇ.",
			"11": "<sup>11</sup> ಯೆಹೋವನೇ, ನಿನ್ನ ಮಾರ್ಗವನ್ನು ನನಗೆ ಬೋಧಿಸು; ನಿನ್ನ ಸತ್ಯತೆಯನ್ನು ನನ್ನ ದೃಷ್ಟಿಯಲ್ಲೇ ಇಟ್ಟುಕೊಂಡು ನಡೆಯುವೆನು. ನಾನು ನಿನ್ನ ನಾಮದಲ್ಲಿ ಭಯಭಕ್ತಿಯಿಂದಿರುವಂತೆ ಏಕಮನಸ್ಸನ್ನು ಅನುಗ್ರಹಿಸು.",
			"12": "<sup>12</sup> ಕರ್ತನೇ, ನನ್ನ ದೇವರೇ, ಮನಃಪೂರ್ವಕವಾಗಿ ನಿನ್ನನ್ನು ಕೊಂಡಾಡುವೆನು; ಎಂದೆಂದಿಗೂ ನಿನ್ನ ನಾಮವನ್ನು ಘನಪಡಿಸುವೆನು.",
			"13": "<sup>13</sup> ನೀನು ಬಹಳವಾಗಿ ಕನಿಕರಿಸಿ ನನ್ನ ಪ್ರಾಣವನ್ನು ಪಾತಾಳತಲದಿಂದ ತಪ್ಪಿಸಿದ್ದೀಯಲ್ಲಾ.",
			"14": "<sup>14</sup> ದೇವರೇ, ಗರ್ವಿಗಳು ನನಗೆ ವಿರೋಧವಾಗಿ ಎದ್ದಿದ್ದಾರೆ; ಬಲಾತ್ಕಾರಿಗಳ ಗುಂಪು ನನ್ನ ಪ್ರಾಣಕ್ಕೆ ಕಾದಿದೆ. ಅವರು ನಿನ್ನನ್ನು ಲಕ್ಷಿಸುವದಿಲ್ಲ.",
			"15": "<sup>15</sup> ಕರ್ತನೇ, ನೀನು ಕನಿಕರವೂ ದಯೆಯೂ ಉಳ್ಳ ದೇವರು; ದೀರ್ಘಶಾಂತನು, ಬಹಳ ಪ್ರೀತಿಯೂ ನಂಬಿಕೆಯೂ ಉಳ್ಳವನು.",
			"16": "<sup>16</sup> ಕಟಾಕ್ಷವಿಟ್ಟು ನನ್ನನ್ನು ಕರುಣಿಸು; ನಿನ್ನ ಸೇವಕನಿಗೆ ಬಲವನ್ನು ಅನುಗ್ರಹಿಸು; ನಿನ್ನ ಸೇವಕಳ ಮಗನನ್ನು ರಕ್ಷಿಸು.",
			"17": "<sup>17</sup> ನನಗಾಗಿ ಒಂದು ಶುಭಸೂಚನೆಯನ್ನು ತೋರಿಸು; ನನ್ನ ಹಗೆಗಾರರು ನೋಡಿ - ಯೆಹೋವನು ನೆರವಾಗಿ ಇವನನ್ನು ಸಂತೈಸಿದ್ದಾನೆಂದು ನಾಚಿಕೆಪಡಲಿ."
		},
		"87": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಸ್ಥಾಪಿಸಿದ್ದು ಪರಿಶುದ್ಧ ಪರ್ವತಗಳಲ್ಲಿದೆ.",
			"2": "<sup>2</sup> ಆತನು ಯಾಕೋಬ್‍ವಂಶದವರ ಎಲ್ಲಾ ನಿವಾಸಗಳಿಗಿಂತ ಚೀಯೋನಿನ ದ್ವಾರಗಳನ್ನು ಹೆಚ್ಚಾಗಿ ಪ್ರೀತಿಸುತ್ತಾನೆ.",
			"3": "<sup>3</sup> ದೇವನಗರವೇ, ನಿನ್ನ ವಿಷಯವಾದ ಗೌರವೋಕ್ತಿಯೇನಂದರೆ - ಸೆಲಾ.",
			"4": "<sup>4</sup> ಐಗುಪ್ತ ಬಾಬೆಲ್ ದೇಶಗಳವರನ್ನು ನನ್ನನ್ನು ಬಲ್ಲವರಲ್ಲಿ ಎಣಿಸುವೆನು. ಇಗೋ ಫಿಲಿಷ್ಟಿಯ, ತೂರ್, ಕೂಷ್ ಜನಾಂಗಗಳು ಅಲ್ಲೇ ಹುಟ್ಟಿದವು ಎಂಬದೇ.",
			"5": "<sup>5</sup> ಇದರಿಂದ ಚೀಯೋನೇ ಪ್ರತಿಯೊಂದು ಜನಾಂಗದ ಜನ್ಮನಗರವೆಂದು ಹೇಳಲ್ಪಡುವದು; ಅದನ್ನು ಪರಾತ್ಪರನು ತಾನೇ ಸ್ಥಿರಪಡಿಸುವನು.",
			"6": "<sup>6</sup> ಯೆಹೋವನು ಜನಾಂಗಗಳ ಪಟ್ಟಿಯನ್ನು ಮಾಡುವಾಗ [ಪ್ರತಿಯೊಂದರ ವಿಷಯ] - ಇದು ಅಲ್ಲಿಯೇ ಹುಟ್ಟಿದ್ದು ಎಂದು ಬರೆಯುವನು. ಸೆಲಾ.",
			"7": "<sup>7</sup> ಇವರು ಹಾಡುತ್ತಾ ಕುಣಿಯುತ್ತಾ - ನನ್ನ [ಜೀವಜಲದ] ಒರತೆಗಳೆಲ್ಲಾ ನಿನ್ನಲ್ಲಿಯೇ ಅವೆ ಅನ್ನುವರು."
		},
		"88": {
			"1": "<sup>1</sup> ಯೆಹೋವನೇ, ನನ್ನನ್ನು ರಕ್ಷಿಸುವ ದೇವರೇ, ಹಗಲಿರುಳು ನಿನಗೆ ಮೊರೆಯಿಡುತ್ತೇನೆ.",
			"2": "<sup>2</sup> ನನ್ನ ಪ್ರಾರ್ಥನೆಯು ನಿನ್ನ ಸನ್ನಿಧಿಯನ್ನು ಮುಟ್ಟಲಿ; ಕಿವಿಗೊಟ್ಟು ನನ್ನ ಕೂಗನ್ನು ಕೇಳು.",
			"3": "<sup>3</sup> ನನ್ನ ಜೀವವು ಕಷ್ಟಗಳಿಂದ ತುಂಬಿಹೋಯಿತು; ನನ್ನ ಪ್ರಾಣವು ಪಾತಾಳಕ್ಕೆ ಹತ್ತಿರವಾಯಿತು.",
			"4": "<sup>4</sup> ಸಮಾಧಿಯಲ್ಲಿ ಸೇರುವವರೊಳಗೆ ಎಣಿಸಲ್ಪಟ್ಟಿದ್ದೇನೆ; ಗತಿಗೆಟ್ಟ ಮನುಷ್ಯನಂತಿದ್ದೇನೆ.",
			"5": "<sup>5</sup> ಸತ್ತವನ ಹಾಗೆ ನನ್ನನ್ನು ಹೊರಗೆ ಹಾಕಿದ್ದಾರೆ; ಸಮಾಧಿಯಲ್ಲಿ ಬಿದ್ದಿರುವ ಹತರಂತಿದ್ದೇನೆ. ಅವರು ನಿನ್ನ ಪರಿಪಾಲನೆಗೆ ಹೊರಗಾದವರು; ಅಂಥವರನ್ನು ನೀನು ಜ್ಞಾಪಿಸುವದೇ ಇಲ್ಲ.",
			"6": "<sup>6</sup> ಅಧೋಲೋಕದಲ್ಲಿಯೂ ಗಾಡಾಂಧಕಾರದಲ್ಲಿಯೂ ಅಗಾಧಸ್ಥಳದಲ್ಲಿಯೂ ನನ್ನನ್ನು ದೊಬ್ಬಿಬಿಟ್ಟಿದ್ದೀ.",
			"7": "<sup>7</sup> ನಿನ್ನ ಕೋಪಭಾರವು ನನ್ನನ್ನು ಕುಗ್ಗಿಸಿಬಿಟ್ಟಿದೆ; ನಿನ್ನ ಎಲ್ಲಾ ತೆರೆಗಳಿಂದ ನನ್ನನ್ನು ಬಾಧಿಸಿದ್ದೀ. ಸೆಲಾ.",
			"8": "<sup>8</sup> ನನ್ನ ಆಪ್ತರು ನನ್ನನ್ನು ಹೇಸಿ ಬಿಟ್ಟುಹೋಗುವಂತೆ ಮಾಡಿದ್ದೀ; ಕಟ್ಟಲ್ಪಟ್ಟಿದ್ದೇನೆ, ಬಿಡಿಸಿಕೊಳ್ಳಲಾರೆ.",
			"9": "<sup>9</sup> ಬಾಧೆಯಿಂದ ನನ್ನ ಕಣ್ಣುಗಳು ಮೊಬ್ಬಾಗಿ ಹೋಗಿವೆ. ಯೆಹೋವನೇ, ಹಗಲೆಲ್ಲಾ ಕೈಯೊಡ್ಡಿ ನಿನಗೆ ಮೊರೆಯಿಡುತ್ತೇನೆ.",
			"10": "<sup>10</sup> ನೀನು ಸತ್ತವರಿಗೆ ಅದ್ಭುತಕಾರ್ಯಗಳನ್ನು ತೋರಿಸುವಿಯೋ? ಪ್ರೇತಗಳು ಎದ್ದು ನಿನ್ನನ್ನು ಕೊಂಡಾಡುವವೋ? ಸೆಲಾ.",
			"11": "<sup>11</sup> ಸಮಾಧಿಯಲ್ಲಿ ನಿನ್ನ ಕೃಪೆಯನ್ನೂ ನಾಶಲೋಕದಲ್ಲಿ ನಿನ್ನ ಸತ್ಯತೆಯನ್ನೂ ಸಾರುವದುಂಟೋ?",
			"12": "<sup>12</sup> ಕತ್ತಲೆಯ ಲೋಕದಲ್ಲಿ ನಿನ್ನ ಮಹತ್ಕಾರ್ಯಗಳೂ ಮರವೆಯ ದೇಶದಲ್ಲಿ ನಿನ್ನ ನೀತಿಯೂ ತಿಳಿಯಲ್ಪಡುವವೋ?",
			"13": "<sup>13</sup> ನಾನಾದರೋ, ಯೆಹೋವನೇ, ನಿನಗೆ ಮೊರೆಯಿಡುತ್ತೇನೆ; ಉದಯದಲ್ಲಿ ನನ್ನ ವಿಜ್ಞಾಪನೆಯು ನಿನ್ನ ಮುಂದೆ ಬರುವದು.",
			"14": "<sup>14</sup> ಯೆಹೋವನೇ, ನನ್ನ ಪ್ರಾಣವನ್ನೇಕೆ ತಳ್ಳಿಬಿಟ್ಟಿ? ನಿನ್ನ ಮುಖವನ್ನು ಮರೆಮಾಡಿದ್ದೇಕೆ?",
			"15": "<sup>15</sup> ಯೌವನಾರಭ್ಯ ಕುಗ್ಗಿದವನೂ ಮೃತಪ್ರಾಯನೂ ನಿನ್ನ ಗದರಿಕೆಯಿಂದ ದೆಸೆಗೆಟ್ಟವನೂ ಆಗಿದ್ದೇನಲ್ಲಾ.",
			"16": "<sup>16</sup> ನಿನ್ನ ಕೋಪಜ್ವಾಲೆಯು ನನ್ನನ್ನು ಕವಿದದೆ. ನಿನ್ನಿಂದುಂಟಾದ ದಿಗಿಲಿನಿಂದ ಹಾಳಾದೆನು.",
			"17": "<sup>17</sup> ಅವು ದಿನವೆಲ್ಲಾ ನೀರಿನಂತೆ ನನ್ನನ್ನು ಆವರಿಸಿಕೊಂಡವೆ; ಒಟ್ಟುಗೂಡಿ ನನ್ನನ್ನು ಸುತ್ತಿಬಿಟ್ಟಿವೆ.",
			"18": "<sup>18</sup> ನನ್ನ ಆಪ್ತಮಿತ್ರರನ್ನು ದೂರಮಾಡಿದಿ; ಅಂಧಕಾರವೇ ನನ್ನ ಪರಿಚಯವು."
		},
		"89": {
			"1": "<sup>1</sup> ಯೆಹೋವನೇ, ನಿತ್ಯವೂ ನಿನ್ನ ಕೃಪಾತಿಶಯವನ್ನು ಹಾಡಿಹರಸುವೆನು; ನಿನ್ನ ಸತ್ಯತೆಯನ್ನು ಮುಂದಣ ಸಂತಾನದವರೆಲ್ಲರಿಗೂ ನನ್ನ ಬಾಯಿ ತಿಳಿಯಮಾಡುವದು.",
			"2": "<sup>2</sup> ನಿನ್ನ ಪ್ರೀತಿಸಂಕಲ್ಪವು ನಿತ್ಯವೂ ಸಿದ್ಧಿಗೆ ಬರುತ್ತಲೇ ಇರುವದು; ಆಕಾಶದಲ್ಲಿ ನಿನ್ನ ಸತ್ಯತೆಯನ್ನು ಸ್ಥಾಪಿಸುವಿ ಎಂದು ತಿಳಿದುಕೊಂಡಿದ್ದೇನೆ.",
			"3": "<sup>3</sup> [ನೀನು ನುಡಿದದ್ದೇನಂದರೆ - ] ನಾನು ಆರಿಸಿಕೊಂಡ ಸೇವಕನಾದ ದಾವೀದನ ಸಂಗಡ",
			"4": "<sup>4</sup> ಒಡಂಬಡಿಕೆಮಾಡಿಕೊಂಡು ಆಣೆಯಿಟ್ಟು - ಶಾಶ್ವತವಾಗಿ ನಿನ್ನ ಸಂತತಿಯನ್ನು ಸ್ಥಾಪಿಸುವೆನು, ತಲತಲಾಂತರಗಳಿಗೂ ನಿನ್ನ ಸಿಂಹಾಸನವನ್ನು ಸ್ಥಿರಪಡಿಸುವೆನು ಎಂದು ಹೇಳಿದ್ದೇನೆ ಎಂಬದೇ. ಸೆಲಾ.",
			"5": "<sup>5</sup> ಯೆಹೋವನೇ, ಗಗನವು ನಿನ್ನ ಮಹತ್ತನ್ನು ಪ್ರಸಿದ್ಧಪಡಿಸುವದು; ನಿನ್ನ ಸತ್ಯತೆಯನ್ನು ಪರಿಶುದ್ಧರ ಸಭೆಯಲ್ಲಿ ಕೀರ್ತಿಸುವದು.",
			"6": "<sup>6</sup> ಮೇಘಮಂಡಲದಲ್ಲಿ ಯೆಹೋವನಿಗೆ ಸಮಾನರು ಯಾರು? ದೇವದೂತರಲ್ಲಿ ಯೆಹೋವನಿಗೆ ಸರಿಯಾದವರು ಯಾರು?",
			"7": "<sup>7</sup> ಆತನು ಪರಿಶುದ್ಧರ ಸಭೆಯಲ್ಲಿ ಭಯಂಕರನಾದ ದೇವರು; ತನ್ನ ಎಲ್ಲಾ ಪರಿವಾರದವರಿಗಿಂತ ಭೀಕರನು.",
			"8": "<sup>8</sup> ಯೆಹೋವನೇ, ಸೇನಾಧೀಶ್ವರನಾದ ದೇವರೇ, ನಿನಗೆ ಸಮಾನರು ಯಾರು? ಯಾಹುವೇ, ನೀನು ಶಕ್ತನು, ಸತ್ಯತೆಯಿಂದ ಆವರಿಸಲ್ಪಟ್ಟವನು.",
			"9": "<sup>9</sup> ಸಮುದ್ರದ ಅಲ್ಲಕಲ್ಲೋಲಗಳನ್ನು ಅಂಕೆಯಲ್ಲಿಟ್ಟುಕೊಂಡಿರುವವನು ನೀನು; ತೆರೆಗಳು ಏಳುವಾಗ ಅವುಗಳನ್ನು ತಡೆಯುವವನು ನೀನು.",
			"10": "<sup>10</sup> ರಹಬನ್ನು ಛೇದಿಸಿ ಸಾಯಿಸಿದವನು ನೀನು; ನಿನ್ನ ಭುಜಬಲದಿಂದ ಶತ್ರುಗಳನ್ನು ಚದರಿಸಿಬಿಟ್ಟಿ.",
			"11": "<sup>11</sup> ಆಕಾಶವು ನಿನ್ನದು, ಭೂಮಿಯೂ ನಿನ್ನದೇ; ಲೋಕವನ್ನೂ ಅದರಲ್ಲಿರುವದೆಲ್ಲವನ್ನೂ ನಿರ್ಮಿಸಿದವನು ನೀನು.",
			"12": "<sup>12</sup> ದಕ್ಷಿಣೋತ್ತರದಿಕ್ಕುಗಳನ್ನು ಉಂಟುಮಾಡಿದವನು ನೀನು. ತಾಬೋರ್ ಹೆರ್ಮೋನ್ ಪರ್ವತಗಳು ನಿನ್ನ ನಾಮದಲ್ಲಿ ಆನಂದಧ್ವನಿಮಾಡುತ್ತವೆ.",
			"13": "<sup>13</sup> ನಿನ್ನ ಭುಜವು ಮಹಾಬಲವುಳ್ಳದ್ದು. ನಿನ್ನ ಹಸ್ತವು ಶಕ್ತಿಯುಳ್ಳದ್ದು; ನಿನ್ನ ಬಲಗೈ ಮಹತ್ತುಗಳನ್ನು ನಡಿಸುತ್ತದೆ.",
			"14": "<sup>14</sup> ನೀತಿನ್ಯಾಯಗಳು ನಿನ್ನ ಸಿಂಹಾಸನದ ಅಸ್ತಿವಾರವು; ನಿನ್ನ ಸಾನ್ನಿಧ್ಯದೂತರು ಪ್ರೀತಿಸತ್ಯತೆಗಳೇ.",
			"15": "<sup>15</sup> ಉತ್ಸಾಹಧ್ವನಿಯನ್ನು ಕೇಳಿದ ಜನರು ಧನ್ಯರು; ಯೆಹೋವನೇ, ಅವರು ನಿನ್ನ ಮುಖಪ್ರಕಾಶದಲ್ಲಿ ಸಂಚರಿಸುತ್ತಾರೆ.",
			"16": "<sup>16</sup> ಯಾವಾಗಲೂ ನಿನ್ನ ನಾಮದಲ್ಲಿ ಆನಂದಿಸುತ್ತಾರೆ; ನಿನ್ನ ನೀತಿಯಿಂದ ಏಳಿಗೆಹೊಂದುತ್ತಾರೆ.",
			"17": "<sup>17</sup> ಅವರು ಹಿಗ್ಗುವ ಬಲವು ನೀನೇ. ನಿನ್ನ ಕರುಣಾಕಟಾಕ್ಷದಿಂದ ನಮ್ಮ ಕೊಂಬು ಎತ್ತಲ್ಪಟ್ಟಿರುವದು.",
			"18": "<sup>18</sup> ನಮಗೆ ಗುರಾಣಿಯಂತಿರುವ ನಮ್ಮ ಅರಸನು ಯೆಹೋವನವನೇ; ಅವನು ಇಸ್ರಾಯೇಲ್ಯರ ಸದಮಲಸ್ವಾಮಿಯ ಸ್ವಕೀಯನು.",
			"19": "<sup>19</sup> ಆ ಕಾಲದಲ್ಲಿ ನೀನು ದರ್ಶನದಲ್ಲಿ ನಿನ್ನ ಭಕ್ತರಿಗೆ ಹೇಳಿದ್ದೇನಂದರೆ - ಒಬ್ಬ ಶೂರನಿಗೆ ರಕ್ಷಾಬಲವನ್ನು ಅನುಗ್ರಹಿಸಿದ್ದೇನೆ; ಪ್ರಜೆಗಳಲ್ಲಿ ಒಬ್ಬ ಯೌವನಸ್ಥನನ್ನು ಆರಿಸಿ ಉನ್ನತಸ್ಥಾನದಲ್ಲಿಟ್ಟಿದ್ದೇನೆ.",
			"20": "<sup>20</sup> ನನ್ನ ಸೇವಕನಾದ ದಾವೀದನನ್ನು ಕಂಡು ಪರಿಶುದ್ಧತೈಲದಿಂದ ಅವನನ್ನು ಅಭಿಷೇಕಿಸಿದ್ದೇನೆ.",
			"21": "<sup>21</sup> ನನ್ನ ಹಸ್ತವು ಅವನ ಮೇಲೆ ಸ್ಥಿರವಾಗಿರುವದು; ನನ್ನ ಭುಜವು ಅವನನ್ನು ಬಲಪಡಿಸುವದು.",
			"22": "<sup>22</sup> ವೈರಿಯ ಕುತಂತ್ರವು ಅವನಲ್ಲಿ ಸಾಗದು; ಯಾವ ಕೆಡುಕನೂ ಅವನನ್ನು ಕುಗ್ಗಿಸಲಾರನು.",
			"23": "<sup>23</sup> ಅವನ ವಿರೋಧಿಗಳನ್ನು ಅವನ ಮುಂದೆಯೇ ಜಜ್ಜಿಹಾಕುವೆನು; ಅವನ ದ್ವೇಷಿಗಳನ್ನು ಹತಮಾಡಿಬಿಡುವೆನು.",
			"24": "<sup>24</sup> ಆದರೆ ನನ್ನ ಪ್ರೀತಿಸತ್ಯತೆಗಳು ಅವನೊಡನೆ ಇರುವವು; ನನ್ನ ಹೆಸರಿನಿಂದ ಅವನ ಕೊಂಬು ಎತ್ತಲ್ಪಡುವದು.",
			"25": "<sup>25</sup> ಸಮುದ್ರದಿಂದ ನದಿಗಳವರೆಗೂ ಅವನ ಹಸ್ತಕ್ಕೆ ಅಧಿಕಾರವನ್ನು ಕೊಡುವೆನು.",
			"26": "<sup>26</sup> ಅವನು ನನ್ನೊಡನೆ - ನನ್ನ ತಂದೆಯೂ ದೇವರೂ ಆಶ್ರಯದುರ್ಗವೂ ನೀನೇ ಎಂದು ಹೇಳುವನು.",
			"27": "<sup>27</sup> ನಾನಾದರೋ ಅವನನ್ನು ಜ್ಯೇಷ್ಠಪುತ್ರನನ್ನೂ ಭೂರಾಜರಲ್ಲಿ ಉನ್ನತನನ್ನೂ ಮಾಡಿಕೊಳ್ಳುವೆನು.",
			"28": "<sup>28</sup> ನನ್ನ ಕೃಪೆಯು ಅವನಲ್ಲಿ ಶಾಶ್ವತವಾಗಿ ಇರುವದು; ನನ್ನ ಒಡಂಬಡಿಕೆಯು ಅವನಲ್ಲಿ ಸ್ಥಿರವಾಗಿರುವದು.",
			"29": "<sup>29</sup> ಅವನ ಸಂತಾನವನ್ನು ಯಾವಾಗಲೂ ಉಳಿಸುವೆನು. ಅವನ ಸಿಂಹಾಸನವು ಆಕಾಶವಿರುವವರೆಗೂ ಇರುವದು.",
			"30": "<sup>30</sup> ಅವನ ಸಂತಾನದವರು ನನ್ನ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಬಿಟ್ಟು ನನ್ನ ಆಜ್ಞೆಗಳಲ್ಲಿ ನಡೆಯದೆ",
			"31": "<sup>31</sup> ನನ್ನ ವಿಧಿಗಳನ್ನು ಮೀರಿ ನನ್ನ ನೇಮಗಳನ್ನು ಪಾಲಿಸದೆಹೋದರೆ",
			"32": "<sup>32</sup> ನಾನು ಅವರ ದ್ರೋಹಕ್ಕಾಗಿ ಅವರನ್ನು ಬೆತ್ತದಿಂದ ಹೊಡೆಯುವೆನು; ಅವರ ಅಪರಾಧಕ್ಕಾಗಿ ಪೆಟ್ಟುಕೊಡುವೆನು.",
			"33": "<sup>33</sup> ಆದರೂ ನಾನು ಅವನಲ್ಲಿಟ್ಟಿರುವ ಕೃಪೆಯನ್ನು ತಪ್ಪಿಸುವದಿಲ್ಲ; ನನ್ನ ನಂಬಿಗಸ್ತಿಕೆಯಿಂದ ಜಾರುವದಿಲ್ಲ.",
			"34": "<sup>34</sup> ನನ್ನ ಒಡಂಬಡಿಕೆಯನ್ನು ಭಂಗಪಡಿಸುವದಿಲ್ಲ; ನನ್ನ ಮಾತನ್ನು ಬದಲಿಸುವದಿಲ್ಲ.",
			"35": "<sup>35</sup> ನನ್ನ ಪವಿತ್ರತ್ವದಲ್ಲಿ ಆಣೆಯಿಟ್ಟು ಒಂದು ಸಂಗತಿ ಹೇಳಿದ್ದೇನೆ; ಅದರ ವಿಷಯದಲ್ಲಿ ದಾವೀದನಿಗೆ ಸುಳ್ಳುಗಾರನಾಗುವದಿಲ್ಲ. ಅದೇನಂದರೆ -",
			"36": "<sup>36</sup> ಅವನ ಸಂತತಿಯು ಶಾಶ್ವತವಾಗಿ ಇರುವದು; ಅವನ ಸಿಂಹಾಸನವು ಸೂರ್ಯನಂತೆ ನನ್ನ ಎದುರಿನಲ್ಲಿದ್ದು",
			"37": "<sup>37</sup> ಚಂದ್ರನಂತೆ ನಿತ್ಯಕ್ಕೂ ಸ್ಥಿರವಾಗಿರುವದು. ಪರಲೋಕದ ಸಾಕ್ಷಿ ಸತ್ಯವೇ ಸರಿ. ಸೆಲಾ.",
			"38": "<sup>38</sup> ಹೀಗೆ ಹೇಳಿದ ನೀನು ನಿನ್ನ ಅಭಿಷಿಕ್ತನ ಮೇಲೆ ರೇಗಿದವನಾಗಿ ಅವನನ್ನು ಅಸಹ್ಯಿಸಿ ತಳ್ಳಿಬಿಟ್ಟಿದ್ದೀ.",
			"39": "<sup>39</sup> ನೀನು ನಿನ್ನ ಸೇವಕನ ಸಂಗಡ ಮಾಡಿಕೊಂಡ ಒಡಂಬಡಿಕೆಯನ್ನು ಭಂಗಪಡಿಸಿ ಅವನ ಕಿರೀಟವನ್ನು ನೆಲಕ್ಕೆ ಹಾಕಿ ಅಪವಿತ್ರ ಮಾಡಿದ್ದೀ.",
			"40": "<sup>40</sup> ಅವನ ದೇಶದ ಮೇರೆಗಳನ್ನು ಕೆಡವಿ ದುರ್ಗಗಳನ್ನು ಹಾಳುಮಾಡಿದ್ದೀ.",
			"41": "<sup>41</sup> ದಾರಿಗರೆಲ್ಲರೂ ಅವನನ್ನು ಸುಲಿಗೆಮಾಡುತ್ತಾರೆ; ಸುತ್ತಣವರ ಪರಿಹಾಸ್ಯಕ್ಕೆ ಗುರಿಯಾಗಿದ್ದಾನೆ.",
			"42": "<sup>42</sup> ಅವನ ವಿರೋಧಿಗಳ ಹಸ್ತವನ್ನು ಉನ್ನತಪಡಿಸಿದ್ದೀ; ಅವನ ವೈರಿಗಳಿಗೆಲ್ಲಾ ಆನಂದವಾಗುವಂತೆ ಮಾಡಿದ್ದೀ.",
			"43": "<sup>43</sup> ಇದಲ್ಲದೆ ಅವನ ಕತ್ತಿಯನ್ನು ತಿರುಗಿಸಿಬಿಟ್ಟಿದ್ದೀ; ಅವನನ್ನು ಕಾಳಗದಲ್ಲಿ ನಿಲ್ಲದಂತೆ ಮಾಡಿದ್ದೀ.",
			"44": "<sup>44</sup> ಅವನ ತೇಜಸ್ಸನ್ನು ತಡೆದುಬಿಟ್ಟಿದ್ದಿ; ಅವನ ಸಿಂಹಾಸನವನ್ನು ನೆಲಕ್ಕುರುಳಿಸಿದ್ದೀ.",
			"45": "<sup>45</sup> ಅವನ ಯೌವನವನ್ನು ಬೇಗ ಮುಗಿಸಿ ನಾಚಿಕೆಯು ಅವನನ್ನು ಕವಿಯುವಂತೆ ಮಾಡಿದ್ದೀ. ಸೆಲಾ.",
			"46": "<sup>46</sup> ಯೆಹೋವನೇ, ಇನ್ನೆಷ್ಟರವರೆಗೆ ಮರೆಯಾಗಿರುವಿ? ನಿನ್ನ ಕೋಪಾಗ್ನಿಯು ನಮ್ಮ ಮೇಲೆ ಸದಾ ಉರಿಯುತ್ತಿರಬೇಕೋ?",
			"47": "<sup>47</sup> ನನ್ನ ಆಯುಷ್ಯವು ಎಷ್ಟು ಅಲ್ಪವೆಂದೂ ಎಂಥಾ ವ್ಯರ್ಥಕ್ಕಾಗಿಯೇ ಮನುಷ್ಯರನ್ನು ನಿರ್ಮಿಸಿದ್ದೀ ಎಂದೂ ಜ್ಞಾಪಿಸಿಕೋ.",
			"48": "<sup>48</sup> ತನ್ನನ್ನು ಪಾತಾಳಕ್ಕೆ ತಪ್ಪಿಸಿಕೊಂಡು ಮರಣಹೊಂದದೆ ಚಿರಂಜೀವಿಯಾಗಿರುವ ಮನುಷ್ಯನು ಯಾವನಿದ್ದಾನೆ? ಸೆಲಾ.",
			"49": "<sup>49</sup> ಕರ್ತನೇ, ನೀನು ನಿನ್ನ ಸತ್ಯತೆಯಲ್ಲಿ ಆಣೆಯಿಟ್ಟು ದಾವೀದನಿಗೆ ವಾಗ್ದಾನಮಾಡಿ ನಡಿಸುತ್ತಿದ್ದ ಮುಂಚಿನ ಕೃಪಾಕಾರ್ಯಗಳು ಎಲ್ಲಿ?",
			"50": "<sup>50</sup> ಯೆಹೋವನೇ, ನಿನ್ನ ವೈರಿಗಳು ನಿಂದಿಸುತ್ತಾರೆ; ನಿನ್ನ ಅಭಿಷಿಕ್ತನನ್ನು ಹೆಜ್ಜೆಹೆಜ್ಜೆಗೂ ಅಪಮಾನ ಮಾಡುತ್ತಾರೆ.",
			"51": "<sup>51</sup> ಕರ್ತನೇ, ನಿನ್ನ ಸೇವಕರಿಗಾಗುವ ಅಪವಾದವನ್ನೂ ನಾನು ಎಲ್ಲಾ ಜನಾಂಗಗಳ ನಿಂದಾಭಾರವನ್ನು ಉಡಿಲಲ್ಲಿ ಕಟ್ಟಿರುವದನ್ನೂ ಜ್ಞಾಪಿಸಿಕೋ.",
			"52": "<sup>52</sup> ಯೆಹೋವನಿಗೆ ಸದಾಕಾಲವೂ ಕೊಂಡಾಟವಾಗಲಿ. ಆಮೆನ್, ಆಮೆನ್."
		},
		"90": {
			"1": "<sup>1</sup> ಕರ್ತನೇ, ತಲತಲಾಂತರಗಳಿಂದಲೂ ನಮ್ಮ ವಾಸಸ್ಥಾನವು ನೀನೇ.",
			"2": "<sup>2</sup> ಬೆಟ್ಟಗಳು ಉಂಟಾಗುವದಕ್ಕಿಂತ, ಭೂಮಿಯೂ ಅದರ ದೇಶಗಳೂ ನಿರ್ಮಾಣವಾಗುವದಕ್ಕಿಂತ ಮುಂಚಿನಿಂದ ಯುಗಯುಗಾಂತರಗಳಲ್ಲಿಯೂ ನೀನೇ ದೇವರು.",
			"3": "<sup>3</sup> ಮನುಷ್ಯರೇ, ಸಾಯಿರಿ ಎಂದು ಆಜ್ಞಾಪಿಸಿ ಅವರನ್ನು ಪುನಃ ಮಣ್ಣಿಗೆ ಸೇರಿಸುತ್ತೀ.",
			"4": "<sup>4</sup> ಸಾವಿರ ವರುಷಗಳು ನಿನ್ನ ದೃಷ್ಟಿಯಲ್ಲಿ ಗತಿಸಿಹೋದ ನಿನ್ನಿನ ದಿನದಂತೆಯೂ ರಾತ್ರಿಯ ಜಾವದಂತೆಯೂ ಅವೆ.",
			"5": "<sup>5</sup> ನೀನು ಮನುಷ್ಯರನ್ನು ಪ್ರವಾಹದಿಂದ ಬಡುಕೊಂಡು ಹೋಗುತ್ತೀ; ಅವರು ನಿದ್ರೆಗೆ ಸಮಾನರೇ. ಅವರು ಹೊತ್ತಾರೆಯಲ್ಲಿ ಚಿಗುರುವ ಹುಲ್ಲಿನಂತಿದ್ದಾರೆ.",
			"6": "<sup>6</sup> ಅದು ಉದಯದಲ್ಲಿ ಬೆಳೆದು ಹೂಬಿಡುತ್ತದೆ; ಸಂಜೆಯಲ್ಲಿ ಕೊಯ್ಯಲ್ಪಟ್ಟು ಒಣಗಿಹೋಗುತ್ತದೆ.",
			"7": "<sup>7</sup> ನಿನ್ನ ಕೋಪದಿಂದ ಇಲ್ಲವಾದೆವು; ನಿನ್ನ ರೌದ್ರದಿಂದ ತಲ್ಲಣಗೊಂಡೆವು.",
			"8": "<sup>8</sup> ನಮ್ಮ ದ್ರೋಹಗಳನ್ನು ನಿನ್ನ ಮುಂದೆಯೂ ನಮ್ಮ ಗುಪ್ತಪಾಪಗಳನ್ನು ನಿನ್ನ ತೇಜೋದೃಷ್ಟಿಯಲ್ಲಿಯೂ ಇಟ್ಟುಕೊಂಡಿದ್ದೀ.",
			"9": "<sup>9</sup> ನಿನ್ನ ರೋಷದಿಂದ ನಮ್ಮ ಕಾಲವೆಲ್ಲಾ ಸಂದುಹೋಯಿತು; ನಮ್ಮ ವರುಷಗಳು ನಿಟ್ಟುಸಿರಿನಂತೆ ತೀರಿಹೋದವು.",
			"10": "<sup>10</sup> ನಮ್ಮ ಆಯುಷ್ಕಾಲವು ಎಪ್ಪತ್ತು ವರುಷ; ಬಲ ಹೆಚ್ಚಿದರೆ ಎಂಭತ್ತು. ಕಷ್ಟಸಂಕಟಗಳೇ ಅದರ ಆಡಂಬರ ಅದು ಬೇಗನೆ ಗತಿಸಿಹೋಗುತ್ತದೆ; ನಾವು ಹಾರಿ ಹೋಗುತ್ತೇವೆ.",
			"11": "<sup>11</sup> ನಿನ್ನ ಕೋಪದ ಬಲವನ್ನೂ ಭಯಭಕ್ತಿಗೆ ಕಾರಣವಾಗಿರತಕ್ಕ ನಿನ್ನ ರೌದ್ರವನ್ನೂ ಗ್ರಹಿಸುವವರಾರು?",
			"12": "<sup>12</sup> ನಮ್ಮ ದಿನಗಳು ಕೊಂಚವೇ ಎಂದು ಎಣಿಸಿಕೊಳ್ಳುವ ಹಾಗೆ ನಮಗೆ ಕಲಿಸು; ಆಗ ಜ್ಞಾನದ ಹೃದಯವನ್ನು ಪಡಕೊಳ್ಳುವೆವು.",
			"13": "<sup>13</sup> ಯೆಹೋವನೇ, ಮನಸ್ಸನ್ನು ಬೇರೆ ಮಾಡಿಕೋ; ಎಷ್ಟರವರೆಗೂ [ಕೋಪ ಮಾಡುವಿ]? ನಿನ್ನ ಸೇವಕರ ಮೇಲೆ ಅಂತಃಕರಣವಿರಲಿ.",
			"14": "<sup>14</sup> ಉದಯದಲ್ಲಿ ನಿನ್ನ ಕೃಪೆಯಿಂದ ನಮ್ಮನ್ನು ಸಂತೃಪ್ತಿಪಡಿಸು; ಆಗ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ಉಲ್ಲಾಸಿಸಿ ಹರ್ಷಿಸುವೆವು.",
			"15": "<sup>15</sup> ನೀನು ನಮ್ಮನ್ನು ಕುಗ್ಗಿಸಿದ ದಿವಸಗಳಿಗೂ ನಾವು ಕೇಡನ್ನು ಅನುಭವಿಸಿದ ವರುಷಗಳಿಗೂ ತಕ್ಕಂತೆ ನಮ್ಮನ್ನು ಸಂತೋಷಪಡಿಸು.",
			"16": "<sup>16</sup> ನಿನ್ನ ಸೇವಕರಿಗೆ ನಿನ್ನ ರಕ್ಷಾಕಾರ್ಯವೂ ಅವರ ಮಕ್ಕಳಿಗೋಸ್ಕರ ನಿನ್ನ ಮಹತ್ತೂ ಪ್ರಕಟವಾಗಲಿ.",
			"17": "<sup>17</sup> ನಮ್ಮ ಯೆಹೋವದೇವರ ಪ್ರಸನ್ನತೆಯು ನಮ್ಮ ಮೇಲೆ ಇರಲಿ. ನಾವು ಕೈಹಾಕಿದ ಕೆಲಸವನ್ನು ನಮಗೆ ಸಫಲಪಡಿಸು; ನಾವು ಕೈಹಾಕಿದ ಕೆಲಸವನ್ನು ಸಫಲಪಡಿಸು."
		},
		"91": {
			"1": "<sup>1</sup> ಪರಾತ್ಪರನ ಮರೆಹೊಕ್ಕಿರುವವನು ಸರ್ವಶಕ್ತನ ಆಶ್ರಯದಲ್ಲಿ ಸುರಕ್ಷಿತನಾಗಿರುವನು.",
			"2": "<sup>2</sup> ನಾನು ಯೆಹೋವನಿಗೆ - ನೀನೇ ನನ್ನ ಶರಣನು ನನ್ನ ದುರ್ಗವು ನಾನು ಭರವಸವಿಟ್ಟಿರುವ ನನ್ನ ದೇವರು ಎಂದು ಹೇಳುವೆನು.",
			"3": "<sup>3</sup> ನಿನ್ನನ್ನು ಬೇಟೆಗಾರನ ಬಲೆಯಿಂದಲೂ ಮರಣಕರವ್ಯಾಧಿಯಿಂದಲೂ ತಪ್ಪಿಸುವವನು ಆತನೇ.",
			"4": "<sup>4</sup> ಆತನು ನಿನ್ನನ್ನು ತನ್ನ ರೆಕ್ಕೆಗಳಿಂದ ಹೊದಗಿಸುವನು; ಆತನ ಪಕ್ಕಗಳ ಮರೆಯನ್ನು ಆಶ್ರಯಿಸಿಕೊಳ್ಳುವಿ. ಆತನ ಸತ್ಯತೆಯೇ ನಿನಗೆ ಖೇಡ್ಯವೂ ಗುರಾಣಿಯೂ ಆಗಿದೆ.",
			"5": "<sup>5</sup> ಇರುಳಲ್ಲಿ ಭಯಹುಟ್ಟಿಸುವ ಯಾವದಕ್ಕೂ ಹಗಲಲ್ಲಿ ಹಾರಿಬರುವ ಬಾಣಕ್ಕೂ",
			"6": "<sup>6</sup> ಕತ್ತಲೆಯಲ್ಲಿ ಸಂಚರಿಸುವ ವಿಪತ್ತಿಗೂ ಹಾನಿಕರವಾದ ಮಧ್ಯಾಹ್ನದ ಕೇಡಿಗೂ ಅಂಜ ಕಾರಣವಿಲ್ಲ.",
			"7": "<sup>7</sup> ನಿನ್ನ ಮಗ್ಗುಲಲ್ಲಿ ಸಾವಿರ ಜನರು, ನಿನ್ನ ಬಲಗಡೆಯಲ್ಲಿ ಹತ್ತುಸಾವಿರ ಜನರು ಸತ್ತುಬಿದ್ದರೂ ನಿನಗೇನೂ ತಟ್ಟದು.",
			"8": "<sup>8</sup> ನೀನು ಅದನ್ನು ಕಣ್ಣಾರೆ ಕಂಡು ದುಷ್ಟರಿಗೆ ಪ್ರತಿದಂಡನೆಯುಂಟೆಂಬದಕ್ಕೆ ಸಾಕ್ಷಿಯಾಗಿರುವಿಯಷ್ಟೆ.",
			"9": "<sup>9</sup> ಯೆಹೋವನೇ ನನ್ನ ಶರಣನು! ಪರಾತ್ಪರನನ್ನು ನಿವಾಸಸ್ಥಾನ ಮಾಡಿಕೊಂಡಿದ್ದೀಯಲ್ಲಾ.",
			"10": "<sup>10</sup> ಯಾವ ಕೇಡೂ ನಿನಗೆ ಸಂಭವಿಸದು; ಉಪದ್ರವವು ನಿನ್ನ ಗುಡಾರದ ಸಮೀಪಕ್ಕೂ ಬಾರದು.",
			"11": "<sup>11</sup> ನೀನು ಹೋಗುವಲ್ಲೆಲ್ಲಾ ನಿನ್ನನ್ನು ಕಾಯುವದಕ್ಕೆ ಆತನು ನಿನ್ನ ವಿಷಯವಾಗಿ ತನ್ನ ದೂತರಿಗೆ ಅಪ್ಪಣೆಕೊಡುವನು.",
			"12": "<sup>12</sup> ನಿನ್ನ ಕಾಲು ಕಲ್ಲಿಗೆ ತಗಲದಂತೆ ಅವರು ನಿನ್ನನ್ನು ಕೈಗಳಲ್ಲಿ ಎತ್ತಿಕೊಳ್ಳುವರು.",
			"13": "<sup>13</sup> ಸಿಂಹಸರ್ಪಗಳ ಮೇಲೆ ನಡೆಯುವಿ; ಪ್ರಾಯದ ಸಿಂಹವನ್ನೂ ಘಟಸರ್ಪವನ್ನೂ ತುಳಿದು ಬಿಡುವಿ.",
			"14": "<sup>14</sup> ಅವನು ನನ್ನಲ್ಲಿ ಆಸಕ್ತನಾಗಿರುವದರಿಂದ ಅವನನ್ನು ರಕ್ಷಿಸುವೆನು; ನನ್ನ ನಾಮವನ್ನು ಅರಿತವನಾಗಿರುವದರಿಂದ ಅವನನ್ನು ಉದ್ಧರಿಸುವೆನು.",
			"15": "<sup>15</sup> ಅವನು ನನಗೆ ಮೊರೆಯಿಡುವಾಗ ಸದುತ್ತರವನ್ನು ದಯಪಾಲಿಸುವೆನು; ಇಕ್ಕಟ್ಟಿನಲ್ಲಿ ಹತ್ತಿರವಿದ್ದು ಅವನನ್ನು ತಪ್ಪಿಸಿ ಘನಪಡಿಸುವೆನು;",
			"16": "<sup>16</sup> ದೀರ್ಘಾಯುಷ್ಯವನ್ನು ಅನುಗ್ರಹಿಸಿ ಅವನನ್ನು ತೃಪ್ತಿಪಡಿಸುವೆನು; ನನ್ನ ವಿಶೇಷವಾದ ರಕ್ಷಣೆಯನ್ನು ತೋರಿಸುವೆನು."
		},
		"92": {
			"1": "<sup>1</sup> ಯೆಹೋವನೇ, ನಿನ್ನನ್ನು ಕೊಂಡಾಡುವದೂ ಪರಾತ್ಪರನೇ, ನಿನ್ನ ನಾಮವನ್ನು ಸಂಕೀರ್ತಿಸುವದೂ ಯುಕ್ತವಾಗಿದೆ.",
			"2": "<sup>2</sup> ವೀಣಾಸ್ವರಮಂಡಲಗಳಿಂದಲೂ ಕಿನ್ನರಿಯ ಘನಸ್ವರದಿಂದಲೂ",
			"3": "<sup>3</sup> ಹೊತ್ತಾರೆಯಲ್ಲಿ ನಿನ್ನ ಪ್ರೇಮವನ್ನೂ ರಾತ್ರಿಯಲ್ಲಿ ನಿನ್ನ ಸತ್ಯತೆಯನ್ನೂ ವರ್ಣಿಸುವದು ಉಚಿತವಾಗಿದೆ.",
			"4": "<sup>4</sup> ಯೆಹೋವನೇ, ನಿನ್ನ ಕ್ರಿಯೆಗಳಿಂದ ನನ್ನನ್ನು ಸಂತೋಷಪಡಿಸಿದ್ದೀ; ನಿನ್ನ ಕೆಲಸಗಳ ದೆಸೆಯಿಂದ ಉತ್ಸಾಹಧ್ವನಿ ಮಾಡುತ್ತೇನೆ.",
			"5": "<sup>5</sup> ಯೆಹೋವನೇ, ನಿನ್ನ ಕೃತ್ಯಗಳು ಎಷ್ಟೋ ಶ್ರೇಷ್ಠವಾಗಿವೆ; ನಿನ್ನ ಆಲೋಚನೆಗಳು ಅಶೋಧ್ಯವಾಗಿವೆ.",
			"6": "<sup>6</sup> ಪಶುಪ್ರಾಯನು ಅರಿಯನು; ಹುಚ್ಚನು ತಿಳಿಯನು.",
			"7": "<sup>7</sup> ದುಷ್ಟರು ಹುಲ್ಲಿನಂತೆ ಬೆಳೆಯುವದೂ ಕೆಡುಕರು ಹೂವಿನಂತೆ ಮೆರೆಯುವದೂ ತೀರಾ ಹಾಳಾಗುವದಕ್ಕಾಗಿಯೇ.",
			"8": "<sup>8</sup> ಯೆಹೋವನೇ, ನೀನಾದರೋ ಸದಾ ಉನ್ನತಸ್ಥಾನದಲ್ಲಿರುತ್ತೀ.",
			"9": "<sup>9</sup> ಯೆಹೋವನೇ, ಇಗೋ ನಿನ್ನ ಶತ್ರುಗಳು! ಅವರೆಲ್ಲಾ ನಾಶವಾಗುತ್ತಿದ್ದಾರೆ. ಅಧರ್ಮಿಗಳೆಲ್ಲಾ ಚದರಿಹೋಗುವರು.",
			"10": "<sup>10</sup> ಆದರೆ ನನ್ನ ಕೊಂಬನ್ನು ಕಾಡುಕೋಣದ ಕೊಂಬಿನಂತೆ ಎತ್ತಿದ್ದೀ; ಚೈತನ್ಯತೈಲದಿಂದ ನನ್ನನ್ನು ಅಭಿಷೇಕಿಸಿದ್ದೀ.",
			"11": "<sup>11</sup> ನನ್ನ ಶತ್ರುಗಳಿಗುಂಟಾದ ದುರ್ಗತಿಯನ್ನು ಕಣ್ಣಾರೆ ಕಂಡಿದ್ದೇನೆ; ನನಗೆ ವಿರೋಧವಾಗಿ ಎದ್ದವರ ವಿಪತ್ತನ್ನು ಕಿವಿಯಾರೆ ಕೇಳಿದ್ದೇನೆ.",
			"12": "<sup>12</sup> ನೀತಿವಂತರು ಖರ್ಜೂರದ ಮರದಂತೆ ಬೆಳೆಯುವರು; ಲೆಬನೋನಿನ ದೇವದಾರುವೃಕ್ಷದ ಹಾಗೆ ವೃದ್ಧಿಯಾಗುವರು.",
			"13": "<sup>13</sup> ಯೆಹೋವನ ಆಲಯದಲ್ಲಿ [ಸಸಿಗಳಂತೆ] ನೆಡಲ್ಪಟ್ಟವರು ನಮ್ಮ ದೇವರ ಅಂಗಳದಲ್ಲಿ ಚೆನ್ನಾಗಿ ಬೆಳೆಯುವರು.",
			"14": "<sup>14</sup> ಮುಪ್ಪಿನಲ್ಲಿಯೂ ಫಲಿಸುವರು; ಪುಷ್ಟಿಯಾಗಿದ್ದು ಶೋಭಿಸುವರು.",
			"15": "<sup>15</sup> ಹೀಗೆ ಅವರು ಯೆಹೋವನ ಸತ್ಯಸಂಧತೆಗೆ ದೃಷ್ಟಾಂತರಾಗಿರುವರು. ಆತನೇ ನನ್ನ ಬಂಡೆಯು; ಆತನು ನಿರ್ವಂಚಕನು."
		},
		"93": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ರಾಜ್ಯಾಧಿಕಾರವನ್ನು ವಹಿಸಿದ್ದಾನೆ; ಮಹಿಮಾವಸ್ತ್ರವನ್ನು ಧರಿಸಿದ್ದಾನೆ, ಧರಿಸಿದ್ದಾನೆ. ಶೌರ್ಯವನ್ನು ನಡುಕಟ್ಟಾಗಿ ಬಿಗಿದಿದ್ದಾನೆ; ಭೂಲೋಕವು ಸ್ಥಿರವಾಗಿರುವದು, ಕದಲುವದಿಲ್ಲ.",
			"2": "<sup>2</sup> ಪೂರ್ವದಿಂದಲೂ ನಿನ್ನ ಸಿಂಹಾಸನವು ಸ್ಥಿರವಾಗಿದೆ; ಅನಾದಿಯಿಂದ ನೀನು ಇದ್ದೀ.",
			"3": "<sup>3</sup> ಯೆಹೋವನೇ, ನದಿಗಳು ಮೊರೆದವು, ನದಿಗಳು ಭೋರಿಟ್ಟವು; ನದಿಗಳು ಘೋಷಿಸುತ್ತವೆ.",
			"4": "<sup>4</sup> ಜಲರಾಶಿಗಳ ಘೋಷಕ್ಕಿಂತಲೂ ಮಹಾತರಂಗಗಳ ಗರ್ಜನೆಗಿಂತಲೂ ಉನ್ನತದಲ್ಲಿರುವ ಯೆಹೋವನ ಮಹಿಮೆಯು ಗಾಂಭೀರ್ಯವುಳ್ಳದ್ದು.",
			"5": "<sup>5</sup> ಯೆಹೋವನೇ, ನಿನ್ನ ಆಜ್ಞೆಗಳು ಬಹುಖಂಡಿತವಾಗಿವೆ; ಸದಾ ನಿನ್ನ ಮನೆಗೆ ಯೋಗ್ಯವಾದದ್ದು ಪರಿಶುದ್ಧತ್ವವೇ."
		},
		"94": {
			"1": "<sup>1</sup> ಯೆಹೋವನೇ, ಮುಯ್ಯಿತೀರಿಸುವ ದೇವರೇ, ಮುಯ್ಯಿತೀರಿಸುವ ದೇವರೇ, ಪ್ರಕಾಶಿಸು.",
			"2": "<sup>2</sup> ಲೋಕದ ನ್ಯಾಯಾಧಿಪತಿಯೇ, ಏಳು; ಗರ್ವಿಷ್ಠರಿಗೆ ಪ್ರತಿಫಲವನ್ನು ಕೊಡು.",
			"3": "<sup>3</sup> ಯೆಹೋವನೇ, ದುಷ್ಟರು ಎಲ್ಲಿಯವರೆಗೆ, ದುಷ್ಟರು ಎಲ್ಲಿಯವರೆಗೆ ಹಿಗ್ಗುತ್ತಿರಬೇಕು?",
			"4": "<sup>4</sup> ಕೆಡುಕರೆಲ್ಲರು ಉಬ್ಬಿಕೊಂಡು ಅಹಂಕಾರವನ್ನು ಕಕ್ಕುತ್ತಾರೆ.",
			"5": "<sup>5</sup> ಯೆಹೋವನೇ, ನಿನ್ನ ಪ್ರಜೆಯನ್ನು ಜಜ್ಜಿಹಾಕುತ್ತಾರೆ; ನಿನ್ನ ಸ್ವಾಸ್ತ್ಯವನ್ನು ಕುಗ್ಗಿಸಿಬಿಡುತ್ತಾರೆ;",
			"6": "<sup>6</sup> ವಿಧವೆಯನ್ನೂ ಪರದೇಶಿಯನ್ನೂ ಕೊಲ್ಲುತ್ತಾರೆ; ಅನಾಥರನ್ನು ಹತಮಾಡುತ್ತಾರೆ.",
			"7": "<sup>7</sup> ಯಾಹು ನೋಡುವದೇ ಇಲ್ಲ; ಯಾಕೋಬ್ಯರ ದೇವರು ಲಕ್ಷಿಸುವದೇ ಇಲ್ಲ ಅನ್ನುತ್ತಾರೆ.",
			"8": "<sup>8</sup> ಪಶುಪ್ರಾಯರಾದ ಪ್ರಜೆಗಳಿರಾ, ಲಕ್ಷಿಸಿರಿ; ಹುಚ್ಚರೇ, ನಿಮಗೆ ಬುದ್ಧಿಬರುವದು ಯಾವಾಗ?",
			"9": "<sup>9</sup> ಕಿವಿಮಾಡಿದವನು ಕೇಳನೋ? ಕಣ್ಣುಕೊಟ್ಟವನು ನೋಡನೋ?",
			"10": "<sup>10</sup> ಜನಾಂಗಗಳನ್ನು ಶಿಕ್ಷಿಸುವವನೂ ಮನುಷ್ಯರಿಗೆ ಬುದ್ಧಿಕಲಿಸುವವನೂ ಗದರಿಸನೋ?",
			"11": "<sup>11</sup> ಮನುಷ್ಯನ ಯೋಚನೆಗಳು ಉಸಿರೇ ಎಂದು ಯೆಹೋವನು ತಿಳುಕೊಳ್ಳುತ್ತಾನೆ.",
			"12": "<sup>12</sup> ಯಾಹುವೇ, ನೀನು ಯಾವನನ್ನು ಶಿಕ್ಷಿಸಿ ಧರ್ಮೋಪದೇಶಮಾಡುತ್ತೀಯೋ ಅವನೇ ಧನ್ಯನು.",
			"13": "<sup>13</sup> ಅಂಥವನನ್ನು, ದುಷ್ಟನಿಗೋಸ್ಕರ ಗುಂಡಿಯು ಅಗೆಯಲ್ಪಡುವ ತನಕ, ಆಪತ್ತಿನಲ್ಲಿಯೂ ಸಮಾಧಾನಪಡಿಸುತ್ತೀ.",
			"14": "<sup>14</sup> ಯೆಹೋವನು ತನ್ನ ಜನರನ್ನು ಬೇಡವೆಂದು ತಳ್ಳಿಬಿಡುವದಿಲ್ಲ; ತನ್ನ ಸ್ವಾಸ್ತ್ಯವನ್ನು ಕೈಬಿಡುವದಿಲ್ಲ.",
			"15": "<sup>15</sup> ನ್ಯಾಯತೀರ್ಪು ನೀತಿಗೆ ತಿರುಗಿಕೊಳ್ಳುವದು; ಯಥಾರ್ಥಚಿತ್ತರೆಲ್ಲರೂ ಅದನ್ನೇ ಅನುಸರಿಸುವರು.",
			"16": "<sup>16</sup> ದುಷ್ಟರಿಗೆ ವಿರೋಧವಾಗಿ ನನಗೋಸ್ಕರ ಏಳುವವರು ಯಾರು? ಕೆಡುಕರಿಗೆ ವಿರೋಧವಾಗಿ ನನಗೋಸ್ಕರ ಎದ್ದು ನಿಲ್ಲುವವರು ಯಾರು?",
			"17": "<sup>17</sup> ಯೆಹೋವನ ಸಹಾಯವಿಲ್ಲದಿದ್ದರೆ ಇಷ್ಟರಲ್ಲೇ ಸಮಾಧಿ ಸೇರುತ್ತಿದ್ದೆನು.",
			"18": "<sup>18</sup> ಯೆಹೋವನೇ, ನನ್ನ ಕಾಲು ಜಾರಿತೆಂದು ಅಂದಾಗಲೇ ನಿನ್ನ ಕೃಪೆಯು ನನಗೆ ಆಧಾರವಾಯಿತು.",
			"19": "<sup>19</sup> ನನ್ನಲ್ಲಿ ಅನೇಕ ಚಿಂತೆಗಳಿರುವಾಗ ನಿನ್ನ ಸಂತೈಸುವಿಕೆಯಿಂದಲೇ ನನ್ನ ಪ್ರಾಣಕ್ಕೆ ಸಂತೋಷವುಂಟಾಗುತ್ತದೆ.",
			"20": "<sup>20</sup> ಧರ್ಮಶಾಸ್ತ್ರದ ನೆವನದಿಂದ ಕೇಡುಕಲ್ಪಿಸುವ ನಾಶಕರನ್ಯಾಯಾಸನಕ್ಕೂ ನಿನಗೂ ಸಂಬಂಧವೇನು?",
			"21": "<sup>21</sup> ಜೀವತೆಗೆಯಬೇಕೆಂದು ನೀತಿವಂತರ ಮೇಲೆ ಬೀಳುತ್ತಾರೆ; ನಿರಪರಾಧಿಗಳಿಗೆ ಮರಣಶಿಕ್ಷೆ ವಿಧಿಸುತ್ತಾರೆ.",
			"22": "<sup>22</sup> ಆದರೆ ಯೆಹೋವನು ನನ್ನ ದುರ್ಗವೂ ನನ್ನ ದೇವರು ಆಶ್ರಯಗಿರಿಯೂ ಆಗಿದ್ದಾನೆ.",
			"23": "<sup>23</sup> ಅವರ ಕೆಟ್ಟತನವನ್ನು ಅವರಿಗೇ ತಿರುಗಿಸುವನು; ಅವರ ದುಷ್ಟತನದಿಂದಲೇ ಅವರನ್ನು ನಿರ್ಮೂಲ ಮಾಡುವನು. ನಮ್ಮ ಯೆಹೋವದೇವರು ಅವರನ್ನು ಸಂಹರಿಸಿಬಿಡುವನು."
		},
		"95": {
			"1": "<sup>1</sup> ಬನ್ನಿರಿ, ಯೆಹೋವನಿಗೆ ಉತ್ಸಾಹ ಧ್ವನಿಗೈಯುವಾ; ನಮ್ಮ ರಕ್ಷಕನಾದ ಶರಣನಿಗೆ ಜಯಘೋಷ ಮಾಡೋಣ.",
			"2": "<sup>2</sup> ಕೃತಜ್ಞತಾಸ್ತುತಿಯೊಡನೆ ಆತನ ಸನ್ನಿಧಿಗೆ ಸೇರೋಣ; ಕೀರ್ತನೆಗಳಿಂದ ಆತನಿಗೆ ಜಯಘೋಷ ಮಾಡೋಣ.",
			"3": "<sup>3</sup> ಯೆಹೋವನು ಮಹಾದೇವರೂ ಎಲ್ಲಾ ದೇವರುಗಳಲ್ಲಿ ಮಹಾರಾಜನೂ ಆಗಿದ್ದಾನೆ.",
			"4": "<sup>4</sup> ಭೂಮಿಯ ಅಗಾಧವು ಆತನ ಕೈಯಲ್ಲಿರುತ್ತದೆ; ಪರ್ವತಶಿಖರಗಳು ಆತನವೇ.",
			"5": "<sup>5</sup> ಆತನೇ ಸಮುದ್ರವನ್ನು ನಿರ್ಮಿಸಿದವನು; ಅದು ಆತನದೇ. ಒಣನೆಲವು ಆತನ ಕೈಕೆಲಸವೇ.",
			"6": "<sup>6</sup> ಬನ್ನಿರಿ; ನಮ್ಮ ನಿರ್ಮಾಣಿಕನಾದ ಯೆಹೋವನಿಗೆ ಸಾಷ್ಟಾಂಗವೆರಗಿ ಆರಾಧಿಸೋಣ.",
			"7": "<sup>7</sup> ಆತನು ನಮ್ಮ ದೇವರು; ನಾವೋ ಆತನು ಪಾಲಿಸುವ ಪ್ರಜೆಯೂ ಆತನ ಕೈಕೆಳಗಿರುವ ಹಿಂಡೂ ಆಗಿದ್ದೇವೆ. ನೀವು ಈ ಹೊತ್ತು ಆತನ ಶಬ್ದಕ್ಕೆ ಕಿವಿಗೊಟ್ಟರೆ ಎಷ್ಟೋ ಒಳ್ಳೇದು -",
			"8": "<sup>8</sup> ನಿಮ್ಮ ಹಿರಿಯರು ಅರಣ್ಯದಲ್ಲಿರುವ ಮೆರೀಬದಲ್ಲಿ ಮಸ್ಸಾದಿನದಲ್ಲಿ ಮಾಡಿದಂತೆ ನೀವು ನಿಮ್ಮ ಹೃದಯವನ್ನು ಕಠಿಣಮಾಡಿಕೊಳ್ಳಬೇಡಿರಿ.",
			"9": "<sup>9</sup> ಅಲ್ಲಿ ಅವರು ನನ್ನನ್ನು ಪರೀಕ್ಷಿಸಿದರು; ಅವರು ನನ್ನ ಮಹತ್ಕಾರ್ಯಗಳನ್ನು ನೋಡಿದರೂ ನನ್ನನ್ನು ಶೋಧಿಸಿದರು.",
			"10": "<sup>10</sup> ನಾನು ನಾಲ್ವತ್ತು ವರುಷ ಆ ಸಂತತಿಯವರ ವಿಷಯದಲ್ಲಿ ಬೇಸರಗೊಂಡೆನು; ಈ ಜನರು ಹೃದಯದಲ್ಲಿ ತಪ್ಪಿಹೋಗುವವರು, ನನ್ನ ಮಾರ್ಗವನ್ನು ತಿಳಿಯದವರು, ಎಂದು ಹೇಳಿದೆನು.",
			"11": "<sup>11</sup> ಆದದರಿಂದ ಇವರು ನನ್ನ ವಿಶ್ರಾಂತಿಯಲ್ಲಿ ಸೇರುವದೇ ಇಲ್ಲವೆಂದು ಕೋಪಗೊಂಡು ಪ್ರಮಾಣಮಾಡಿದೆನು."
		},
		"96": {
			"1": "<sup>1</sup> ಯೆಹೋವನಿಗೆ ಹೊಸ ಕೀರ್ತನೆಯನ್ನು ಹಾಡಿರಿ; ಎಲ್ಲಾ ಭೂನಿವಾಸಿಗಳೇ, ಯೆಹೋವನಿಗೆ ಹಾಡಿರಿ.",
			"2": "<sup>2</sup> ಯೆಹೋವನಿಗೆ ಹಾಡಿರಿ; ಆತನ ನಾಮವನ್ನು ಕೊಂಡಾಡಿರಿ. ಆತನ ರಕ್ಷಣೆಯನ್ನು ಪ್ರತಿನಿತ್ಯವೂ ಸಾರಿಹೇಳಿರಿ.",
			"3": "<sup>3</sup> ಜನಾಂಗಗಳಲ್ಲಿ ಆತನ ಘನತೆಯನ್ನೂ ಎಲ್ಲಾ ಜನರಲ್ಲಿ ಆತನ ಅದ್ಭುತಕೃತ್ಯಗಳನ್ನೂ ಪ್ರಸಿದ್ಧಪಡಿಸಿರಿ.",
			"4": "<sup>4</sup> ಯೆಹೋವನು ದೊಡ್ಡವನೂ ಬಹಳವಾಗಿ ಸ್ತುತ್ಯನೂ ಆಗಿದ್ದಾನೆ; ಎಲ್ಲಾ ದೇವರುಗಳಲ್ಲಿ ಆತನೇ ಭಯಂಕರನು.",
			"5": "<sup>5</sup> ಜನಾಂಗಗಳ ದೇವತೆಗಳೆಲ್ಲಾ ಬೊಂಬೆಗಳೇ; ಯೆಹೋವನಾದರೋ ಗಗನಮಂಡಲವನ್ನು ನಿರ್ಮಿಸಿದವನು.",
			"6": "<sup>6</sup> ಆತನ ಸಾನ್ನಿಧ್ಯದಲ್ಲಿ ಮಾನಮಹಿಮೆಗಳೂ ಆತನ ಪವಿತ್ರಾಲಯದಲ್ಲಿ ಬಲಸೌಂದರ್ಯಗಳೂ ಇರುತ್ತವೆ.",
			"7": "<sup>7</sup> ಭೂಜನಾಂಗಗಳೇ, ಬಲಪ್ರಭಾವಗಳು ಯೆಹೋವನವೇ ಯೆಹೋವನವೇ ಎಂದು ಹೇಳಿ ಆತನನ್ನು ಘನಪಡಿಸಿರಿ.",
			"8": "<sup>8</sup> ಯೆಹೋವನ ನಾಮಕ್ಕೆ ಯೋಗ್ಯವಾದ ಘನವನ್ನು ಸಲ್ಲಿಸಿರಿ; ಕಾಣಿಕೆಯೊಡನೆ ಆತನ ಅಂಗಳಗಳಿಗೆ ಬನ್ನಿರಿ.",
			"9": "<sup>9</sup> ಪರಿಶುದ್ಧತ್ವವೆಂಬ ಭೂಷಣದೊಡನೆ ಯೆಹೋವನಿಗೆ ನಮಸ್ಕರಿಸಿರಿ; ಎಲ್ಲಾ ಭೂನಿವಾಸಿಗಳೇ, ಆತನ ಮುಂದೆ ನಡುಗಿರಿ.",
			"10": "<sup>10</sup> ಯೆಹೋವನು ರಾಜ್ಯಾಧಿಕಾರವನ್ನು ವಹಿಸಿದ್ದಾನೆ; ಭೂಮಿಯು ಸ್ಥಿರವಾಗಿರುವದು, ಕದಲುವದಿಲ್ಲ; ಸರ್ವರಿಗೂ ನ್ಯಾಯಾನುಸಾರವಾಗಿ ತೀರ್ಪುಕೊಡುವನೆಂದು ಜನಾಂಗಗಳಲ್ಲಿ ಪ್ರಸಿದ್ಧಪಡಿಸಿರಿ.",
			"11": "<sup>11</sup> ಯೆಹೋವನ ಮುಂದೆ ಗಗನಮಂಡಲವು ಹರ್ಷಿಸಲಿ; ಭೂಲೋಕವು ಸಂತೋಷಿಸಲಿ; ಸಮುದ್ರವೂ ಅದರಲ್ಲಿರುವದೆಲ್ಲವೂ ಘೋಷಿಸಲಿ.",
			"12": "<sup>12</sup> ಹೊಲಗಳೂ ಅವುಗಳ ಪೈರುಗಳೂ ಉಲ್ಲಾಸಿಸಲಿ; ಮತ್ತು ವನದ ಎಲ್ಲಾ ಮರಗಳು ಉತ್ಸಾಹಧ್ವನಿ ಮಾಡಲಿ.",
			"13": "<sup>13</sup> ಆತನು ಬರುತ್ತಾನೆ; ಭೂನಿವಾಸಿಗಳಿಗೆ ನ್ಯಾಯತೀರಿಸಲಿಕ್ಕೆ ಬರುತ್ತಾನೆ. ಆತನು ಲೋಕಕ್ಕೆ ನೀತಿಗನುಸಾರವಾಗಿಯೂ ಜನಾಂಗಗಳಿಗೆ ಸತ್ಯತೆಯಿಂದಲೂ ನ್ಯಾಯತೀರಿಸುವನು."
		},
		"97": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ರಾಜ್ಯಾಧಿಕಾರವನ್ನು ವಹಿಸಿದ್ದಾನೆ; ಭೂಲೋಕವು ಸಂತೋಷಿಸಲಿ; ಸಮುದ್ರದ ತೀರಪ್ರದೇಶಗಳೆಲ್ಲಾ ಹರ್ಷಿಸಲಿ.",
			"2": "<sup>2</sup> ಮುಗಿಲೂ ಕಾರ್ಗತ್ತಲೆಯೂ ಆತನ ಸುತ್ತಲೂ ಇರುತ್ತವೆ; ನೀತಿನ್ಯಾಯಗಳು ಆತನ ಸಿಂಹಾಸನದ ಅಸ್ತಿವಾರ.",
			"3": "<sup>3</sup> ಬೆಂಕಿಯು ಆತನ ಮುಂಗಡೆಯಿಂದ ಹೋಗಿ ಸುತ್ತಲೂ ಆತನ ವೈರಿಗಳನ್ನು ದಹಿಸಿ ಬಿಡುತ್ತದೆ.",
			"4": "<sup>4</sup> ಆತನ ಮಿಂಚುಗಳು ಲೋಕವನ್ನು ಬೆಳಗಿಸಿದವು; ಭೂಮಿಯು ಅದನ್ನು ಕಂಡು ನಡುಗಿತು.",
			"5": "<sup>5</sup> ಸಾರ್ವಭೌಮನಾದ ಯೆಹೋವನ ಎದುರಿನಲ್ಲಿ ಪರ್ವತಗಳು ಮೇಣದಂತೆ ಕರಗಿಹೋದವು.",
			"6": "<sup>6</sup> ಗಗನಮಂಡಲವು ಆತನ ನೀತಿಯನ್ನು ಪ್ರಸಿದ್ಧಪಡಿಸಿತು; ಎಲ್ಲಾ ಜನರು ಆತನ ಮಹಿಮೆಯನ್ನು ಕಂಡರು.",
			"7": "<sup>7</sup> ವಿಗ್ರಹಗಳಲ್ಲಿ ಹಿಗ್ಗುವ ಮೂರ್ತಿಪೂಜಕರೆಲ್ಲಾ ತಲೆಬೊಗ್ಗಿಸಲಿ; ದೇವರುಗಳಿರಾ, ನೀವೆಲ್ಲರು ಆತನಿಗೆ ಅಡ್ಡಬೀಳಿರಿ.",
			"8": "<sup>8</sup> ಯೆಹೋವನೇ, ನೀನು ನ್ಯಾಯಸ್ಥಾಪಿಸಿದ ವಾರ್ತೆಯನ್ನು ಕೇಳಿ ಚೀಯೋನ್ ಪಟ್ಟಣವು ಹರ್ಷಿಸಿತು; ಯೆಹೂದ ಪ್ರಾಂತದ ಊರುಗಳು ಸಂತೋಷಿಸಿದವು.",
			"9": "<sup>9</sup> ಯೆಹೋವನೇ, ನೀನು ಭೂಲೋಕದ ಸರ್ವಾಧಿಕಾರಿ; ಎಲ್ಲಾ ದೇವರುಗಳಲ್ಲಿ ಮಹೋನ್ನತನು ನೀನೇ.",
			"10": "<sup>10</sup> ಯೆಹೋವನನ್ನು ಪ್ರೀತಿಸುವವರೇ, ಕೆಟ್ಟತನವನ್ನು ಹಗೆಮಾಡಿರಿ. ಆತನು ತನ್ನ ಭಕ್ತರ ಪ್ರಾಣಗಳನ್ನು ಕಾಯುವವನಾಗಿ ದುಷ್ಟರ ಕೈಯೊಳಗಿಂದ ಅವರನ್ನು ಬಿಡಿಸುವನು.",
			"11": "<sup>11</sup> ನೀತಿವಂತರಿಗೋಸ್ಕರ ಪ್ರಕಾಶವೂ ಯಥಾರ್ಥಹೃದಯವುಳ್ಳವರಿಗೆ ಸಂತೋಷವೂ ಬಿತ್ತಲ್ಪಟ್ಟಿವೆ.",
			"12": "<sup>12</sup> ನೀತಿವಂತರೇ, ಯೆಹೋವನಲ್ಲಿ ಆನಂದಿಸಿರಿ; ಆತನ ಪರಿಶುದ್ಧನಾಮವನ್ನು ಕೊಂಡಾಡಿರಿ."
		},
		"98": {
			"1": "<sup>1</sup> ಯೆಹೋವನಿಗೆ ಹೊಸ ಕೀರ್ತನೆಯನ್ನು ಹಾಡಿರಿ; ಆತನು ಅದ್ಭುತಕೃತ್ಯಗಳನ್ನು ನಡಿಸಿದ್ದಾನೆ. ಆತನ ಬಲಗೈಯೂ ಪರಿಶುದ್ಧಬಾಹುವೂ ಜಯವನ್ನು ಉಂಟುಮಾಡಿವೆ.",
			"2": "<sup>2</sup> ಯೆಹೋವನು ತನ್ನ ರಕ್ಷಣೆಯನ್ನು ಪ್ರಕಟಿಸಿದ್ದಾನೆ; ಜನಾಂಗಗಳೆದುರಿಗೆ ತನ್ನ ನೀತಿಯನ್ನು ತೋರ್ಪಡಿಸಿದ್ದಾನೆ.",
			"3": "<sup>3</sup> ಆತನು ಇಸ್ರಾಯೇಲನ ಮನೆತನದವರ ಕಡೆಗಿದ್ದ ತನ್ನ ಪ್ರೀತಿಸತ್ಯತೆಗಳನ್ನು ನೆನಪುಮಾಡಿಕೊಂಡಿದ್ದಾನೆ. ಭೂಲೋಕದ ಎಲ್ಲಾ ಕಡೆಯವರೂ ನಮ್ಮ ದೇವರ ರಕ್ಷಾಕಾರ್ಯವನ್ನು ನೋಡಿದ್ದಾರೆ.",
			"4": "<sup>4</sup> ಸಮಸ್ತಭೂನಿವಾಸಿಗಳೇ, ಯೆಹೋವನಿಗೆ ಜಯಘೋಷಮಾಡಿರಿ; ಹರ್ಷಿಸಿರಿ, ಉತ್ಸಾಹಧ್ವನಿಮಾಡಿ ಹಾಡಿರಿ.",
			"5": "<sup>5</sup> ಕಿನ್ನರಿಯೊಡನೆ ಯೆಹೋವನನ್ನು ಸ್ತುತಿಸಿರಿ; ಕಿನ್ನರಿಯನ್ನು ನುಡಿಸುತ್ತಾ ಭಜಿಸಿರಿ.",
			"6": "<sup>6</sup> ತುತೂರಿಗಳನ್ನೂ ಕೊಂಬನ್ನೂ ಊದುತ್ತಾ ಯೆಹೋವರಾಜನಿಗೆ ಜಯಘೋಷಮಾಡಿರಿ.",
			"7": "<sup>7</sup> ಯೆಹೋವನ ಮುಂದೆ ಸಮುದ್ರವೂ ಅದರಲ್ಲಿರುವದೆಲ್ಲವೂ ಭೂಮಿಯೂ ಅದರ ನಿವಾಸಿಗಳೂ ಘೋಷಿಸಲಿ.",
			"8": "<sup>8</sup> ನದಿಗಳು ಚಪ್ಪಾಳೆಹೊಡೆಯಲಿ; ಪರ್ವತಗಳೆಲ್ಲಾ ಉತ್ಸಾಹಧ್ವನಿಮಾಡಲಿ.",
			"9": "<sup>9</sup> ಆತನು ಭೂನಿವಾಸಿಗಳಿಗೆ ನ್ಯಾಯತೀರಿಸಲಿಕ್ಕೆ ಬರುತ್ತಾನೆ; ಆತನು ಲೋಕಕ್ಕೆ ನೀತಿಗನುಸಾರವಾಗಿಯೂ ಜನಾಂಗಗಳಿಗೆ ಯಥಾರ್ಥವಾಗಿಯೂ ತೀರ್ಪುಕೊಡುವನು."
		},
		"99": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ರಾಜ್ಯಾಧಿಕಾರವನ್ನು ವಹಿಸಿದ್ದಾನೆ; ಜನಾಂಗಗಳು ನಡುಗಲಿ. ಕೆರೂಬಿಯರ ಮಧ್ಯದಲ್ಲಿ ಆಸೀನನಾಗಿರುವಾತನು ಆಳುತ್ತಾನೆ; ಭೂಮಿಯು ಕಂಪಿಸಲಿ.",
			"2": "<sup>2</sup> ಚೀಯೋನಿನಲ್ಲಿರುವ ಯೆಹೋವನು ದೊಡ್ಡವನು; ಆತನು ಎಲ್ಲಾ ಜನಾಂಗಗಳಲ್ಲಿ ಮಹೋನ್ನತನು.",
			"3": "<sup>3</sup> ಅವರು ಯೆಹೋವನ ಪೂಜ್ಯವಾದ ಮಹಾನಾಮವನ್ನು ಕೊಂಡಾಡಲಿ; ಆತನು ಪರಿಶುದ್ಧನು.",
			"4": "<sup>4</sup> ಅರಸನ ಬಲವು ನೀತಿಯಲ್ಲಿ ಆಸಕ್ತವಾಗಿದೆ; ಯಥಾರ್ಥವಾದದ್ದನ್ನು ಸ್ಥಾಪಿಸಿದವನೂ ಯಾಕೋಬ್ಯರಲ್ಲಿ ನ್ಯಾಯನೀತಿಗಳನ್ನು ಸಿದ್ಧಿಗೆ ತಂದವನೂ ನೀನೇ.",
			"5": "<sup>5</sup> ನಮ್ಮ ಯೆಹೋವದೇವರನ್ನು ಘನಪಡಿಸಿರಿ; ಆತನ ಪಾದಪೀಠದ ಮುಂದೆ ಅಡ್ಡಬೀಳಿರಿ. ಆತನು ಪರಿಶುದ್ಧನು.",
			"6": "<sup>6</sup> ಆತನ ಯಾಜಕರಲ್ಲಿ ಮೋಶೆ ಆರೋನರೂ ಆತನ ಹೆಸರಿನಲ್ಲಿ ಪ್ರಾರ್ಥಿಸುವವರೊಳಗೆ ಸಮುವೇಲನೂ ಪ್ರಾರ್ಥಿಸಿಕೊಂಡಾಗೆಲ್ಲಾ ಯೆಹೋವನು ಅವರಿಗೆ ಸದುತ್ತರವನ್ನು ದಯಪಾಲಿಸುತ್ತಿದ್ದನು.",
			"7": "<sup>7</sup> ಆತನು ಮೇಘಸ್ತಂಭದಲ್ಲಿದ್ದು ಅವರೊಡನೆ ಮಾತಾಡುತ್ತಿದ್ದನು; ಅವರು ಆತನ ವಿಧಿನಿಯಮಗಳನ್ನು ಕೈಕೊಂಡವರು.",
			"8": "<sup>8</sup> ನಮ್ಮ ಯೆಹೋವದೇವರೇ, ಅವರಿಗೆ ಉತ್ತರಕೊಟ್ಟವನು ನೀನೇ. ನೀನು ದುಷ್ಕೃತ್ಯಗಳಿಗಾಗಿ ಅವರನ್ನು ದಂಡಿಸುತ್ತಿದ್ದರೂ ಕ್ಷಮಿಸುವ ದೇವರಾಗಿದ್ದಿ.",
			"9": "<sup>9</sup> ನಮ್ಮ ಯೆಹೋವದೇವರನ್ನು ಘನಪಡಿಸಿರಿ; ಆತನ ಪರಿಶುದ್ಧಪರ್ವತದ ಕಡೆಗೆ ಅಡ್ಡಬೀಳಿರಿ. ನಮ್ಮ ಯೆಹೋವದೇವರು ಪರಿಶುದ್ಧನು."
		},
		"100": {
			"1": "<sup>1</sup> ಸಮಸ್ತ ಭೂನಿವಾಸಿಗಳೇ, ಯೆಹೋವನಿಗೆ ಜಯಘೋಷ ಮಾಡಿರಿ.",
			"2": "<sup>2</sup> ಯೆಹೋವನನ್ನು ಸಂತೋಷದಿಂದ ಸೇವಿಸಿರಿ; ಉತ್ಸಾಹಧ್ವನಿಮಾಡುತ್ತಾ ಆತನ ಸನ್ನಿಧಿಗೆ ಬನ್ನಿರಿ.",
			"3": "<sup>3</sup> ಯೆಹೋವನೇ ದೇವರೆಂದು ತಿಳಿದುಕೊಳ್ಳಿರಿ. ನಮ್ಮನ್ನು ಉಂಟುಮಾಡಿದವನು ಆತನೇ; ನಾವು ಆತನವರು. ಆತನ ಪ್ರಜೆಯೂ ಆತನು ಪಾಲಿಸುವ ಹಿಂಡೂ ಆಗಿದ್ದೇವೆ.",
			"4": "<sup>4</sup> ಕೃತಜ್ಞತಾಸ್ತುತಿಯೊಡನೆ ಆತನ ಮಂದಿರದ್ವಾರಗಳಿಗೂ ಕೀರ್ತನೆಯೊಡನೆ ಆತನ ಅಂಗಳಗಳಿಗೂ ಬನ್ನಿರಿ; ಆತನ ಉಪಕಾರ ಸ್ಮರಿಸಿರಿ; ಆತನ ನಾಮವನ್ನು ಕೊಂಡಾಡಿರಿ.",
			"5": "<sup>5</sup> ಯೆಹೋವನು ಒಳ್ಳೆಯವನು; ಆತನ ಕೃಪೆಯು ಯುಗಯುಗಕ್ಕೂ ಆತನ ಸತ್ಯತೆಯು ತಲತಲಾಂತರಕ್ಕೂ ಇರುವವು."
		},
		"101": {
			"1": "<sup>1</sup> ಪ್ರೀತಿನೀತಿಗಳನ್ನು ಕುರಿತು ಹಾಡುವೆನು; ಯೆಹೋವನೇ, ನಿನ್ನನ್ನು ಕೊಂಡಾಡುವೆನು.",
			"2": "<sup>2</sup> ಸನ್ಮಾರ್ಗವನ್ನು ಲಕ್ಷಿಸಿ ನಡೆಯುವೆನು; ನನಗೆ ಯಾವಾಗ ದರ್ಶನಕೊಡುವಿ? ಮನೆಯೊಳಗೂ ಯಥಾರ್ಥಹೃದಯದಿಂದಲೇ ಪ್ರವರ್ತಿಸುವೆನು.",
			"3": "<sup>3</sup> ಯಾವ ನೀಚವಾದ ಕಾರ್ಯವನ್ನೂ ದೃಷ್ಟಿಸುವದಿಲ್ಲ; ದುರಾಚಾರವನ್ನು ಹಗೆಮಾಡುತ್ತೇನೆ, ಅದರ ಗೊಡವೆಯೇ ನನಗೆ ಬೇಡ.",
			"4": "<sup>4</sup> ಮೂರ್ಖತನವು ನನ್ನನ್ನು ಬಿಟ್ಟುಹೋಗಲಿ; ಕೆಟ್ಟತನವನ್ನು ಅರಿಯದಿರುವೆನು.",
			"5": "<sup>5</sup> ಗುಪ್ತದಲ್ಲಿ ನೆರೆಯವನ ಮೇಲೆ ಚಾಡಿಹೇಳುವವನನ್ನು ಸಂಹರಿಸುವೆನು; ಸೊಕ್ಕಿನ ಕಣ್ಣೂ ಉಬ್ಬಿದ ಮನಸ್ಸೂ ಉಳ್ಳವನನ್ನು ಸಹಿಸುವದಿಲ್ಲ.",
			"6": "<sup>6</sup> ದೇಶದಲ್ಲಿರುವ ನಂಬಿಗಸ್ತರನ್ನು ಆರಿಸಿಕೊಳ್ಳುವೆನು; ಅವರೇ ನನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ವಾಸಿಸಬೇಕು. ಸನ್ಮಾರ್ಗಿಯೇ ನನ್ನ ಸೇವೆಯಲ್ಲಿರಬೇಕು.",
			"7": "<sup>7</sup> ಮೋಸಗಾರನು ನನ್ನ ಮನೆಯಲ್ಲಿರಲೇಬಾರದು; ಸುಳ್ಳುಗಾರನು ನನ್ನ ಮುಂದೆ ನಿಲ್ಲಕೂಡದು.",
			"8": "<sup>8</sup> ಯೆಹೋವನ ಪಟ್ಟಣದಲ್ಲಿ ಕೆಡುಕರೇ ಉಳಿಯದಂತೆ ಪ್ರತಿದಿನವೂ ಬೆಳಗಿನಲ್ಲಿ ದೇಶದ ದುಷ್ಟರನ್ನು ಸಂಹರಿಸುತ್ತಾ ಬರುವೆನು."
		},
		"102": {
			"1": "<sup>1</sup> ಯೆಹೋವನೇ, ನನ್ನ ಪ್ರಾರ್ಥನೆಯನ್ನು ಕೇಳು; ನನ್ನ ಕೂಗು ನಿನಗೆ ಮುಟ್ಟಲಿ.",
			"2": "<sup>2</sup> ನನ್ನ ಕಷ್ಟದಲ್ಲಿ ವಿಮುಖನಾಗಬೇಡ; ನನ್ನ ಮೊರೆಗೆ ಕಿವಿಗೊಡು; ನಾನು ಕೂಗಿಕೊಳ್ಳುವ ದಿನದಲ್ಲೇ ಬೇಗನೆ ಸದುತ್ತರವನ್ನು ದಯಪಾಲಿಸು.",
			"3": "<sup>3</sup> ನನ್ನ ಜೀವಮಾನವು ಹೊಗೆಯಂತೆ ಮಾಯವಾಗುತ್ತದೆ. ನನ್ನ ಎಲುಬುಗಳು ಕೊಳ್ಳಿಯಂತೆ ಸುಟ್ಟು ಹೋಗುತ್ತವೆ.",
			"4": "<sup>4</sup> ನನ್ನ ಹೃದಯವು ಬಿಸಿಲಿನಿಂದ ಬಾಡಿಹೋದ ಹುಲ್ಲಿನಂತಿದೆ; ಊಟವನ್ನು ಮರೆತೆನು.",
			"5": "<sup>5</sup> ನಿಟ್ಟುಸುರಿನಿಂದ ನನ್ನಲ್ಲಿ ಎಲುಬು ತೊಗಲು ಮಾತ್ರ ಉಳಿದಿರುತ್ತವೆ.",
			"6": "<sup>6</sup> ಅಡವಿಯ ಬಕದಂತಿದ್ದೇನೆ; ಹಾಳೂರಿನ ಗೂಗೆಯಂತಿದ್ದೇನೆ.",
			"7": "<sup>7</sup> ನಿದ್ರೆಯಿಲ್ಲದವನಾಗಿ ಮನೆಮಾಳಿಗೆ ಮೇಲಿರುವ ಒಂಟಿಯಾದ ಪಕ್ಷಿಯಂತಿರುತ್ತೇನೆ.",
			"8": "<sup>8</sup> ವೈರಿಗಳು ಹಗಲೆಲ್ಲಾ ನನ್ನನ್ನು ನಿಂದಿಸುತ್ತಾರೆ; ನನ್ನ ಮೇಲೆ ಕೋಪಾವೇಶವುಳ್ಳವರು ಯಾರನ್ನಾದರೂ ಶಪಿಸುವಾಗ ನನ್ನನ್ನು ದೃಷ್ಟಾಂತಮಾಡಿ ಶಪಿಸುತ್ತಾರೆ.",
			"9": "<sup>9</sup> ಬೂದಿಯೇ ನನಗೆ ಆಹಾರವಾಯಿತು; ನನ್ನ ಪಾನದಲ್ಲಿ ಕಣ್ಣೀರು ಮಿಶ್ರವಾಯಿತು.",
			"10": "<sup>10</sup> ಇದಕ್ಕೆಲ್ಲಾ ನಿನ್ನ ಕೋಪರೌದ್ರಗಳೇ ಕಾರಣ. ನೀನು ನನ್ನನ್ನು ಎತ್ತಿ ಒಗೆದುಬಿಟ್ಟಿದ್ದೀಯಲ್ಲಾ!",
			"11": "<sup>11</sup> ನನ್ನ ಜೀವಮಾನವು ಸಂಜೆಯ ನೆರಳಿನಂತಿದೆ; ನಾನು ಬಾಡಿದ ಹುಲ್ಲಿನಂತಿದ್ದೇನೆ.",
			"12": "<sup>12</sup> ಯೆಹೋವನೇ, ನೀನಾದರೋ ಸದಾ ಸಿಂಹಾಸನಾರೂಢನಾಗಿರುವಿ; ನಿನ್ನ ನಾಮವು ತಲತಲಾಂತರಕ್ಕೂ ಸ್ಮರಿಸಲ್ಪಡುವದು.",
			"13": "<sup>13</sup> ನೀನು ಎದ್ದು ಚೀಯೋನನ್ನು ಕರುಣಿಸುವಿ. ಅದಕ್ಕೆ ಕೃಪೆತೋರಿಸುವ ಸಮಯ ಇದೇ; ನಿಯಮಿತಕಾಲವು ಬಂದದೆ.",
			"14": "<sup>14</sup> ಅದು ಕಲ್ಲುಕುಪ್ಪೆಯಾಗಿ ಹೋಗಿದ್ದರೂ ನಿನ್ನ ಸೇವಕರಿಗೆ ಅತಿಪ್ರಿಯವಾಗಿದೆ; ಅದರ ಧೂಳಿಗೆ ಅವರು ಮರಗುತ್ತಾರೆ.",
			"15": "<sup>15</sup> ಯೆಹೋವನು ಗತಿಹೀನರ ಮೊರೆಯನ್ನು ತಿರಸ್ಕರಿಸದೆ ನೆರವೇರಿಸಿದನೆಂದೂ",
			"16": "<sup>16</sup> ಆತನು ಮಹಿಮೆಯಲ್ಲಿ ಬಂದು ಚೀಯೋನನ್ನು ತಿರಿಗಿ ಕಟ್ಟಿಸಿದನೆಂದೂ",
			"17": "<sup>17</sup> ಜನಾಂಗಗಳು ಯೆಹೋವ ಎಂಬ ನಿನ್ನ ನಾಮಕ್ಕೂ ಭೂರಾಜರು ನಿನ್ನ ಪ್ರತಾಪಕ್ಕೂ ಭಯಪಡುವರು.",
			"18": "<sup>18</sup> ಇದು ಮುಂದಣ ಸಂತಾನದವರಿಗೋಸ್ಕರ ಶಾಸನವಾಗಿರಲಿ. ಮುಂದೆ ಹುಟ್ಟುವ ಪ್ರಜೆಯು ಯಾಹುವನ್ನು ಕೊಂಡಾಡುತ್ತಾ -",
			"19": "<sup>19</sup> ಯೆಹೋವನು ತನ್ನ ಮಹೋನ್ನತವಾದ ಪವಿತ್ರಸ್ಥಾನದಿಂದ ಕೆಳಕ್ಕೆ ನೋಡಿದ್ದಾನೆ; ಆತನು ಪರಲೋಕದಿಂದ ಭೂಮಿಯನ್ನು ದೃಷ್ಟಿಸಿದ್ದಾನೆ.",
			"20": "<sup>20</sup> ಸೆರೆಯವರ ಗೋಳಾಟವನ್ನು ಲಕ್ಷಿಸಿ ಮರಣಪಾತ್ರರನ್ನು ಬಿಡಿಸಿ",
			"21": "<sup>21</sup> ಅವರು ಚೀಯೋನಿನಲ್ಲಿ ಯೆಹೋವನಾಮವನ್ನು ವರ್ಣಿಸುವಂತೆಯೂ ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ತನ್ನ ಕೀರ್ತನೆಯನ್ನು ಹಾಡುವಂತೆಯೂ ಮಾಡಿದ್ದಾನೆ.",
			"22": "<sup>22</sup> ಜನಾಂಗಗಳೂ ರಾಜ್ಯಗಳೂ ಕೂಡಿಬಂದು ಇವರೊಡನೆ ಯೆಹೋವನನ್ನು ಸೇವಿಸುತ್ತವೆ ಎಂದು ಹೇಳುವದು.",
			"23": "<sup>23</sup> ಆತನು ದಾರಿಯಲ್ಲಿಯೇ ನನ್ನ ಬಲವನ್ನು ಕುಂದಿಸಿದ್ದಾನೆ;",
			"24": "<sup>24</sup> ನನ್ನ ಆಯುಷ್ಯವನ್ನು ಕಡಿಮೆಮಾಡಿ ಬಿಟ್ಟಿದ್ದಾನೆ. ಆದದರಿಂದ - ನನ್ನ ದೇವರೇ, ಅರ್ಧಾಯುಷ್ಯದಲ್ಲಿಯೇ ನನ್ನನ್ನು ಒಯ್ಯಬೇಡ; ನಿನ್ನ ವರುಷಗಳು ತಲತಲಾಂತರಕ್ಕೂ ಇರುತ್ತವೆ.",
			"25": "<sup>25</sup> ಆದಿಯಲ್ಲಿ ನೀನು ಭೂಮಿಗೆ ಅಸ್ತಿವಾರವನ್ನು ಹಾಕಿದಿ; ಗಗನಮಂಡಲವು ನಿನ್ನ ಕೈಕೆಲಸವಾಗಿದೆ.",
			"26": "<sup>26</sup> ಅವು ನಾಶವಾಗುವವು; ಆದರೆ ನೀನು ಶಾಶ್ವತವಾಗಿರುತ್ತೀ. ಅವೆಲ್ಲವೂ ವಸ್ತ್ರದಂತೆ ಹಳೆಯವಾಗುವವು. ಉಡುಪಿನಂತೆ ಅವುಗಳನ್ನು ಬದಲಿಸುತ್ತೀ; ಅವು ಮಾರ್ಪಡುವವು.",
			"27": "<sup>27</sup> ನೀನಾದರೋ ಏಕರೀತಿಯಾಗಿದ್ದೀ; ನಿನ್ನ ವರುಷಗಳು ಮುಗಿದುಹೋಗುವದಿಲ್ಲ.",
			"28": "<sup>28</sup> ನಿನ್ನ ಸೇವಕರ ಮಕ್ಕಳು ಬಾಳುವರು. ಅವರ ಸಂತತಿಯವರು ನಿನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸ್ಥಿರವಾಗಿರುವರು ಎಂದು ಮೊರೆಯಿಡುತ್ತೇನೆ."
		},
		"103": {
			"1": "<sup>1</sup> ನನ್ನ ಮನವೇ, ಯೆಹೋವನನ್ನು ಕೊಂಡಾಡು; ನನ್ನ ಸರ್ವೇಂದ್ರಿಯಗಳೇ, ಆತನ ಪವಿತ್ರನಾಮವನ್ನು ಕೀರ್ತಿಸಿರಿ.",
			"2": "<sup>2</sup> ನನ್ನ ಮನವೇ, ಯೆಹೋವನನ್ನು ಕೊಂಡಾಡು; ಆತನ ಉಪಕಾರಗಳಲ್ಲಿ ಒಂದನ್ನೂ ಮರೆಯಬೇಡ.",
			"3": "<sup>3</sup> ಆತನು ನಿನ್ನ ಎಲ್ಲಾ ಅಪರಾಧಗಳನ್ನು ಕ್ಷಮಿಸುವವನೂ ಸಮಸ್ತರೋಗಗಳನ್ನು ವಾಸಿಮಾಡುವವನೂ",
			"4": "<sup>4</sup> ನಿನ್ನ ಜೀವವನ್ನು ನಾಶದಿಂದ ತಪ್ಪಿಸುವವನೂ ಪ್ರೀತಿಕೃಪೆಗಳೆಂಬ ಕಿರೀಟದಿಂದ ನಿನ್ನನ್ನು ಶೃಂಗರಿಸುವವನೂ ಆಗಿದ್ದಾನೆ.",
			"5": "<sup>5</sup> ಶ್ರೇಷ್ಠವರಗಳಿಂದ ನಿನ್ನ ಆಶೆಯನ್ನು ಪೂರ್ತಿಗೊಳಿಸುತ್ತಾನೆ; ಹದ್ದಿಗೆ ಬರುವಂತೆಯೇ ನಿನಗೆ ಯೌವನವನ್ನು ತಿರಿಗಿ ಬರಮಾಡುತ್ತಾನೆ.",
			"6": "<sup>6</sup> ಯೆಹೋವನು ನೀತಿಯನ್ನು ಸಾಧಿಸುವವನಾಗಿ ಕುಗ್ಗಿಹೋದವರೆಲ್ಲರ ನ್ಯಾಯವನ್ನು ಸ್ಥಾಪಿಸುತ್ತಾನೆ.",
			"7": "<sup>7</sup> ಆತನು ಮೋಶೆಗೆ ತನ್ನ ಮಾರ್ಗವನ್ನೂ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ತನ್ನ ಕೃತ್ಯಗಳನ್ನೂ ಪ್ರಕಟಿಸಿದನು.",
			"8": "<sup>8</sup> ಯೆಹೋವನು ಕನಿಕರವೂ ದಯೆಯೂ ದೀರ್ಘಶಾಂತಿಯೂ ಪೂರ್ಣಪ್ರೀತಿಯೂ ಉಳ್ಳವನು.",
			"9": "<sup>9</sup> ಆತನು ಯಾವಾಗಲೂ ತಪ್ಪುಹುಡುಕುವವನಲ್ಲ; ನಿತ್ಯವೂ ಕೋಪಿಸುವವನಲ್ಲ.",
			"10": "<sup>10</sup> ಆತನು ನಮ್ಮ ಪಾಪಗಳಿಗೆ ಸರಿಯಾಗಿ ನಮ್ಮನ್ನು ಶಿಕ್ಷಿಸಲಿಲ್ಲ; ನಮ್ಮ ಅಪರಾಧಗಳಿಗೆ ತಕ್ಕಂತೆ ದಂಡಿಸಲಿಲ್ಲ.",
			"11": "<sup>11</sup> ಭೂಮಿಯ ಮೇಲೆ ಆಕಾಶವು ಎಷ್ಟು ಉನ್ನತವೋ ಆತನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರ ಮೇಲೆ ಆತನ ಕೃಪೆಯು ಅಷ್ಟು ಅಪಾರವಾಗಿದೆ.",
			"12": "<sup>12</sup> ಪೂರ್ವಕ್ಕೂ ಪಶ್ಚಿಮಕ್ಕೂ ಎಷ್ಟು ದೂರವೋ ನಮ್ಮ ದ್ರೋಹಗಳನ್ನು ನಮ್ಮಿಂದ ತೆಗೆದು ಅಷ್ಟು ದೂರ ಮಾಡಿದ್ದಾನೆ.",
			"13": "<sup>13</sup> ತಂದೆಯು ಮಕ್ಕಳನ್ನು ಕನಿಕರಿಸುವಂತೆ ಯೆಹೋವನು ತನ್ನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರನ್ನು ಕನಿಕರಿಸುತ್ತಾನೆ.",
			"14": "<sup>14</sup> ಆತನು ನಮ್ಮ ಪ್ರಕೃತಿಯನ್ನು ಬಲ್ಲನು; ನಾವು ಧೂಳಿಯಾಗಿದ್ದೇವೆಂಬದನ್ನು ನೆನಪುಮಾಡಿಕೊಳ್ಳುತ್ತಾನೆ.",
			"15": "<sup>15</sup> ಮನುಷ್ಯನ ಆಯುಷ್ಕಾಲವು ಹುಲ್ಲಿನಂತಿದೆ; ಅವನು ಅಡವಿಯ ಹೂವಿನ ಹಾಗೆ ಶೋಭಿಸುತ್ತಾನೆ.",
			"16": "<sup>16</sup> ಗಾಳಿಬಡಿಯುತ್ತಲೇ ಅದು ಹೋಗುವದು; ಅದರ ಸ್ಥಳವು ಅದನ್ನು ತಿರುಗಿ ಕಾಣುವದಿಲ್ಲ.",
			"17": "<sup>17</sup> ಆದರೆ ಯೆಹೋವನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರ ಮೇಲೆ ಆತನ ದಯವು ಯುಗಯುಗಾಂತರಗಳವರೆಗೂ ಇರುತ್ತದೆ.",
			"18": "<sup>18</sup> ಆತನ ನಿಬಂಧನೆಗಳನ್ನು ಕೈಕೊಂಡು ಆತನ ವಿಧಿಗಳನ್ನು ನೆನಪಿನಲ್ಲಿಟ್ಟುಕೊಂಡು ನಡೆಯುವವರ ಮಕ್ಕಳು ಮೊಮ್ಮಕ್ಕಳವರೆಗೂ ಆತನು ತನ್ನ ನೀತಿಯನ್ನು ಸಾಧಿಸುವನು.",
			"19": "<sup>19</sup> ಯೆಹೋವನು ಮೇಲಣ ಲೋಕದಲ್ಲಿ ತನ್ನ ಸಿಂಹಾಸನವನ್ನು ಸ್ಥಾಪಿಸಿದ್ದಾನೆ; ಆತನು ಸಮಸ್ತವನ್ನೂ ಆಳುತ್ತಾನೆ.",
			"20": "<sup>20</sup> ದೇವದೂತರೇ, ಆತನ ಶಬ್ದಕ್ಕೆ ಕಿವಿಗೊಡುವವರೇ, ಆತನ ಆಜ್ಞೆಯನ್ನು ನೆರವೇರಿಸುವ ಪರಾಕ್ರಮಶಾಲಿಗಳೇ, ಯೆಹೋವನನ್ನು ಕೊಂಡಾಡಿರಿ.",
			"21": "<sup>21</sup> ಆತನ ಸೈನ್ಯಗಳೇ, ಆತನ ಮೆಚ್ಚಿಕೆಯನ್ನು ನೆರವೇರಿಸುವ ಸೇವಕರೇ, ಯೆಹೋವನನ್ನು ಕೊಂಡಾಡಿರಿ.",
			"22": "<sup>22</sup> ಆತನ ರಾಜ್ಯದ ಸರ್ವಸ್ಥಳಗಳಲ್ಲಿರುವ ಎಲ್ಲಾ ಸೃಷ್ಟಿಗಳೇ, ಯೆಹೋವನನ್ನು ಕೊಂಡಾಡಿರಿ. ನನ್ನ ಮನವೇ, ಯೆಹೋವನನ್ನು ಕೊಂಡಾಡು."
		},
		"104": {
			"1": "<sup>1</sup> ನನ್ನ ಮನವೇ, ಯೆಹೋವನನ್ನು ಕೊಂಡಾಡು. ಯೆಹೋವನೇ, ನನ್ನ ದೇವರೇ, ನೀನು ಸರ್ವೋತ್ಕೃಷ್ಟನು; ಪ್ರಭಾವಮಹತ್ವಗಳಿಂದ ಭೂಷಿತನಾಗಿದ್ದೀ.",
			"2": "<sup>2</sup> ಬೆಳಕನ್ನು ವಸ್ತ್ರದಂತೆ ಹೊದ್ದುಕೊಂಡಿದ್ದೀ; ಆಕಾಶವನ್ನು ಗುಡಾರದಂತೆ ಎತ್ತಿ ಹರಡಿದ್ದೀ.",
			"3": "<sup>3</sup> ಜಲರಾಶಿಗಳ ಮೇಲೆ ಉಪ್ಪರಿಗೆಗಳನ್ನು ಕಟ್ಟಿಕೊಂಡಿದ್ದೀ; ಮೋಡಗಳನ್ನು ವಾಹನವಾಗಿ ಮಾಡಿಕೊಂಡಿದ್ದೀ; ವಾಯುವಿನ ರೆಕ್ಕೆಗಳ ಮೇಲೆ ಸಂಚರಿಸುತ್ತೀ.",
			"4": "<sup>4</sup> ಗಾಳಿಗಳನ್ನು ನಿನ್ನ ದೂತರನ್ನಾಗಿಯೂ ಅಗ್ನಿಜ್ವಾಲೆಯನ್ನು ಸೇವಕರನ್ನಾಗಿಯೂ ಮಾಡಿಕೊಳ್ಳುತ್ತೀ.",
			"5": "<sup>5</sup> ಭೂಮಿಯು ಯುಗಯುಗಾಂತರಕ್ಕೂ ಕದಲದ ಹಾಗೆ ಅದನ್ನು ದೃಢವಾದ ಅಸ್ತಿವಾರದ ಮೇಲೆ ಸ್ಥಾಪಿಸಿದ್ದೀ.",
			"6": "<sup>6</sup> ನೀನು ಅದನ್ನು ಆದಿಸಾಗರವೆಂಬ ವಸ್ತ್ರದಿಂದ ಹೊದಿಸಿದ್ದೀ; ನೀರು ಪರ್ವತಗಳನ್ನೂ ಮುಚ್ಚಿಬಿಟ್ಟಿತ್ತು.",
			"7": "<sup>7</sup> ನೀನು ಗದರಿಸಲು ಅದು ಓಡಿಹೋಯಿತು; ನಿನ್ನ ಗರ್ಜನೆಗೆ ಹೆದರಿ ನೀನು ನೇಮಿಸಿದ ಸ್ಥಳಕ್ಕೆ ಹೋಗಲು",
			"8": "<sup>8</sup> ಪರ್ವತಗಳು ಎದ್ದವು; ತಗ್ಗುಗಳು ಇಳಿದವು.",
			"9": "<sup>9</sup> ಜಲರಾಶಿಗಳು ಬಂದು ಪುನಃ ಭೂಮಿಯನ್ನು ಮುಚ್ಚಿಕೊಳ್ಳದ ಹಾಗೆ ನೀನು ಮೇರೆಯನ್ನು ಗೊತ್ತುಮಾಡಿದಿ.",
			"10": "<sup>10</sup> ಬುಗ್ಗೆಗಳನ್ನು ತಗ್ಗುಗಳಿಗೆ ಬರಮಾಡುತ್ತೀ; ಅವು ಪರ್ವತಗಳ ನಡುವೆ ಹರಿದು ಹೋಗುತ್ತವೆ.",
			"11": "<sup>11</sup> ಅವು ಅಡವಿಯ ಮೃಗಗಳಿಗೆ ನೀರು ಕೊಡುತ್ತವೆ; ಕಾಡುಕತ್ತೆಗಳು ದಾಹತೀರಿಸಿಕೊಳ್ಳುತ್ತವೆ.",
			"12": "<sup>12</sup> ಪಕ್ಷಿಗಳು ಅವುಗಳ ಬಳಿಯ ಮರಗಳ ಕೊಂಬೆಗಳಲ್ಲಿ ವಾಸವಾಗಿದ್ದು ಗಾನಮಾಡುತ್ತವೆ.",
			"13": "<sup>13</sup> ನೀನು ನಿನ್ನ ಮೇಲಂತಸ್ತುಗಳಿಂದ ಪರ್ವತಗಳಿಗೆ ನೀರು ಕೊಡುತ್ತೀ; ನಿನ್ನ ಕಾರ್ಯಫಲದಿಂದ ಭೂಮಿಯು ತೃಪ್ತಿಹೊಂದುತ್ತದೆ.",
			"14": "<sup>14</sup> ಪಶುಗಳಿಗೋಸ್ಕರ ಹುಲ್ಲನ್ನು ಮೊಳಿಸುತ್ತೀ; ಮನುಷ್ಯರಿಗೋಸ್ಕರ ಪೈರುಗಳನ್ನು ಹುಟ್ಟಿಸುತ್ತೀ. ಅವರು ಭೂವ್ಯವಸಾಯಮಾಡಿ ಆಹಾರವನ್ನೂ",
			"15": "<sup>15</sup> ಹೃದಯಾನಂದಕರವಾದ ದ್ರಾಕ್ಷಾರಸವನ್ನೂ ಮುಖಕ್ಕೆ ಕಾಂತಿಯನ್ನುಂಟುಮಾಡುವ ಎಣ್ಣೆಯನ್ನೂ ಪ್ರಾಣಾಧಾರವಾದ ರೊಟ್ಟಿಯನ್ನೂ ಸಂಪಾದಿಸಿಕೊಳ್ಳುತ್ತಾರೆ.",
			"16": "<sup>16</sup> ಯೆಹೋವನು ನೆಟ್ಟ ಮರಗಳಾದ ಲೆಬನೋನಿನ ದೇವದಾರುವೃಕ್ಷಗಳಿಗೆ ಬೇಕಾದಷ್ಟು ಜಲವಿರುತ್ತದೆ.",
			"17": "<sup>17</sup> ಪಕ್ಷಿಗಳು ಅವುಗಳಲ್ಲಿ ಗೂಡು ಕಟ್ಟಿಕೊಳ್ಳುತ್ತವೆ; ತುರಾಯಿಮರಗಳಲ್ಲಿ ಬಕಪಕ್ಷಿಗಳು ವಾಸಿಸುತ್ತವೆ.",
			"18": "<sup>18</sup> ಕಾಡುಕುರಿಗಳಿಗೆ ಉನ್ನತವಾದ ಪರ್ವತಗಳೂ ಬೆಟ್ಟದ ಮೊಲಗಳಿಗೆ ಬಂಡೆಗಳೂ ಆಶ್ರಯಸ್ಥಾನಗಳಾಗಿವೆ.",
			"19": "<sup>19</sup> ವಿಶೇಷಕಾಲಗಳ ಸೂಚನೆಗಾಗಿ ಚಂದ್ರನನ್ನು ನಿರ್ಮಿಸಿದ್ದೀ; ಸೂರ್ಯನು ತನ್ನ ಅಸ್ತಮಾನಸಮಯವನ್ನು ಬಲ್ಲನು.",
			"20": "<sup>20</sup> ನೀನು ಕತ್ತಲೆಯನ್ನು ಬರಮಾಡಲು ರಾತ್ರಿಯಾಗುತ್ತದೆ. ಆಗ ಕಾಡಿನ ಮೃಗಗಳೆಲ್ಲಾ ಹೊರಬರುತ್ತವೆ.",
			"21": "<sup>21</sup> ಪ್ರಾಯದ ಸಿಂಹಗಳು ಬೇಟೆಗಾಗಿ ಗರ್ಜಿಸುತ್ತವೆ; ದೇವರಿಂದ ಆಹಾರವನ್ನು ಕೇಳಿಕೊಳ್ಳುತ್ತವೆ.",
			"22": "<sup>22</sup> ಸೂರ್ಯೋದಯವಾಗಲು ಅವು ಬಂದು ತಮ್ಮ ಗವಿಗಳಲ್ಲಿ ಮಲಗಿಕೊಳ್ಳುತ್ತವೆ.",
			"23": "<sup>23</sup> ಮನುಷ್ಯನು ತನ್ನ ಕೆಲಸಕ್ಕೆ ಹೊರಟುಹೋಗುತ್ತಾನೆ; ಸಾಯಂಕಾಲದವರೆಗೆ ದುಡಿಯುತ್ತಾನೆ.",
			"24": "<sup>24</sup> ಯೆಹೋವನೇ, ನಿನ್ನ ಕೈಕೆಲಸಗಳು ಎಷ್ಟೋ ವಿಧವಾಗಿವೆ. ಅವುಗಳನ್ನೆಲ್ಲಾ ಜ್ಞಾನದಿಂದಲೇ ಮಾಡಿದ್ದೀ; ಭೂಲೋಕವು ನಿನ್ನ ಆಸ್ತಿಯಿಂದ ತುಂಬಿರುತ್ತದೆ.",
			"25": "<sup>25</sup> ಇಗೋ, ಬಹುವಿಶಾಲವಾದ ಸಮುದ್ರವು! ಅದರೊಳಗೆ ಲೆಕ್ಕವಿಲ್ಲದಷ್ಟು ಸಣ್ಣ ದೊಡ್ಡ ಜೀವಜಂತುಗಳು ಚಲಿಸುತ್ತಿರುತ್ತವೆ.",
			"26": "<sup>26</sup> ಅದರಲ್ಲಿ ಹಡಗುಗಳು ಸಂಚರಿಸುತ್ತವೆ; ನೀನು ಉಂಟುಮಾಡಿದ ಲಿವ್ಯಾತಾನವು ಅದರಲ್ಲಿ ಆಡುತ್ತದೆ.",
			"27": "<sup>27</sup> ಇವೆಲ್ಲಾ ಹೊತ್ತುಹೊತ್ತಿಗೆ ಆಹಾರಕ್ಕೋಸ್ಕರ ನಿನ್ನನ್ನೇ ನಿರೀಕ್ಷಿಸುತ್ತವೆ.",
			"28": "<sup>28</sup> ನೀನು ಕೊಡಲು ಅವು ತೆಗೆದುಕೊಳ್ಳುತ್ತವೆ; ನೀನು ಕೈತೆರೆಯಲು ಅವು ಒಳ್ಳೇದನ್ನು ಹೊಂದಿ ತೃಪ್ತಿಗೊಳ್ಳುತ್ತವೆ.",
			"29": "<sup>29</sup> ನೀನು ವಿಮುಖನಾಗಲು ತಲ್ಲಣಿಸುತ್ತವೆ; ಅವುಗಳ ಶ್ವಾಸವನ್ನು ತೆಗೆದುಕೊಳ್ಳಲು ಅವು ಸತ್ತು ತಿರಿಗಿ ಮಣ್ಣಿಗೆ ಸೇರುತ್ತವೆ.",
			"30": "<sup>30</sup> ನೀನು ಜೀವಶ್ವಾಸವನ್ನು ಊದಲು ಅವು ಹೊಸದಾಗಿ ಹುಟ್ಟುತ್ತವೆ. ಭೂಮಿಯನ್ನು ನೂತನಪಡಿಸುತ್ತಿರುತ್ತೀ.",
			"31": "<sup>31</sup> ಯೆಹೋವನ ಮಹಿಮೆಯು ಸದಾಕಾಲವೂ ಇರಲಿ; ಯೆಹೋವನಿಗೆ ಆತನ ಸೃಷ್ಟಿಗಳಿಂದ ಸಂತೋಷವಾಗಲಿ.",
			"32": "<sup>32</sup> ಆತನು ದೃಷ್ಟಿಸಿದ ಮಾತ್ರದಿಂದಲೇ ಭೂಮಿಯು ಕಂಪಿಸುತ್ತದೆ; ಮುಟ್ಟುತ್ತಲೇ ಪರ್ವತಗಳು ಹೊಗೆಹಾಯುತ್ತವೆ.",
			"33": "<sup>33</sup> ನಾನು ಬದುಕಿರುವವರೆಗೂ ಯೆಹೋವನನ್ನು ಕೀರ್ತಿಸುತ್ತಿರುವೆನು; ಜೀವಮಾನವೆಲ್ಲಾ ನನ್ನ ದೇವರನ್ನು ಭಜಿಸುತ್ತಿರುವೆನು.",
			"34": "<sup>34</sup> ನನ್ನ ಧ್ಯಾನದಿಂದ ಆತನಿಗೆ ಮೆಚ್ಚಿಕೆಯಾಗಲಿ; ನಾನಾದರೋ ಯೆಹೋವನಲ್ಲಿ ಆನಂದಿಸುವೆನು.",
			"35": "<sup>35</sup> ಪಾಪಾತ್ಮರು ಭೂಲೋಕದಲ್ಲಿ ಮುಗಿದು ಹೋಗಲಿ; ದುಷ್ಟರು ನಿರ್ಮೂಲವಾಗಲಿ. ನನ್ನ ಮನವೇ, ಯೆಹೋವನನ್ನು ಕೊಂಡಾಡು; ಯಾಹುವಿಗೆ ಸ್ತೋತ್ರ."
		},
		"105": {
			"1": "<sup>1</sup> ಯೆಹೋವನಿಗೆ ಕೃತಜ್ಞತಾಸ್ತುತಿ ಮಾಡಿರಿ; ಆತನ ನಾಮದ ಮಹತ್ವವನ್ನು ವರ್ಣಿಸಿರಿ; ಜನಾಂಗಗಳಲ್ಲಿ ಆತನ ಕೃತ್ಯಗಳನ್ನು ಪ್ರಸಿದ್ಧಪಡಿಸಿರಿ.",
			"2": "<sup>2</sup> ಆತನನ್ನು ಕೀರ್ತಿಸಿರಿ, ಭಜಿಸಿರಿ; ಆತನ ಅದ್ಭುತಕೃತ್ಯಗಳನ್ನೆಲ್ಲಾ ಧ್ಯಾನಿಸಿರಿ.",
			"3": "<sup>3</sup> ಆತನ ಪರಿಶುದ್ಧನಾಮದಲ್ಲಿ ಹಿಗ್ಗಿರಿ; ಯೆಹೋವನ ದರ್ಶನವನ್ನು ಕೋರುವವರ ಹೃದಯವು ಹರ್ಷಿಸಲಿ.",
			"4": "<sup>4</sup> ಯೆಹೋವನನ್ನೂ ಆತನ ಬಲವನ್ನೂ ಆಶ್ರಯಿಸಿರಿ; ನಿತ್ಯವೂ ಆತನ ದರ್ಶನವನ್ನು ಅಪೇಕ್ಷಿಸಿರಿ.",
			"5": "<sup>5</sup> ಆತನ ಸೇವಕನಾದ ಅಬ್ರಹಾಮನ ಸಂತತಿಯವರೇ, ಆತನು ಆರಿಸಿಕೊಂಡ ಯಾಕೋಬನ ವಂಶದವರೇ,",
			"6": "<sup>6</sup> ಆತನು ಮಾಡಿದ ಅದ್ಭುತಕೃತ್ಯ, ಆತನ ಮಹತ್ಕಾರ್ಯ, ಆತನ ಬಾಯಿಂದ ಹೊರಟ ನ್ಯಾಯನಿರ್ಣಯ ಇವುಗಳನ್ನು ನೆನಪುಮಾಡಿಕೊಳ್ಳಿರಿ.",
			"7": "<sup>7</sup> ಯೆಹೋವನೆಂಬಾತನೇ ನಮ್ಮ ದೇವರು; ಆತನ ನ್ಯಾಯವಿಧಿಗಳು ಭೂಲೋಕದಲ್ಲೆಲ್ಲಾ ಇವೆ.",
			"8": "<sup>8</sup> ಆತನು ತನ್ನ ವಾಗ್ದಾನವನ್ನು ಸಾವಿರ ತಲೆಗಳವರೆಗೂ ತನ್ನ ಒಡಂಬಡಿಕೆಯನ್ನು ನಿತ್ಯವೂ ನೆನಪಿನಲ್ಲಿಟ್ಟುಕೊಳ್ಳುತ್ತಾನೆ.",
			"9": "<sup>9</sup> ಆತನು ಆ ಒಡಂಬಡಿಕೆಯನ್ನು ಅಬ್ರಹಾಮನ ಸಂಗಡ ಮಾಡಿಕೊಂಡನು; ಇಸಾಕನಿಗೆ ಆಣೆಯಿಟ್ಟನು.",
			"10": "<sup>10</sup> ಅದು ರಾಜಶಾಸನದಂತಿರುವದೆಂದು ಯಾಕೋಬನಿಗೂ ಶಾಶ್ವತವಾದ ಒಡಂಬಡಿಕೆಯಾಗಿರುವದೆಂದು ಇಸ್ರಾಯೇಲನಿಗೂ ಮಾತುಕೊಟ್ಟನು.",
			"11": "<sup>11</sup> ಅವರು ಇನ್ನೂ ಬಹು ಸ್ವಲ್ಪ ಜನರೂ ಕಾನಾನ್‍ದೇಶದಲ್ಲಿ ಪ್ರವಾಸಿಗಳೂ ಆಗಿರುವಾಗಲೇ",
			"12": "<sup>12</sup> ಆತನು - ನಿಮಗೆ ಈ ದೇಶವನ್ನು ಕೊಡುವೆನು; ಅದು ನಿಮ್ಮ ಸಂತತಿಯವರಿಗೆ ಸ್ವಾಸ್ತ್ಯವಾಗಿರುವದು ಎಂದು ಹೇಳಿದನು.",
			"13": "<sup>13</sup> ಅವರು ದೇಶದಿಂದ ದೇಶಕ್ಕೂ ರಾಜ್ಯದಿಂದ ರಾಜ್ಯಕ್ಕೂ ಹೋಗುತ್ತಿರುವಲ್ಲಿ",
			"14": "<sup>14</sup> ಅವರಿಗೆ ಯಾರಿಂದಲೂ ಅನ್ಯಾಯವಾಗಗೊಡಿಸಲಿಲ್ಲ. ಆತನು ಅವರ ವಿಷಯದಲ್ಲಿ ಅರಸರನ್ನೂ ಗದರಿಸಿ -",
			"15": "<sup>15</sup> ನಾನು ಅಭಿಷೇಕಿಸಿದವರನ್ನು ಮುಟ್ಟಬಾರದು, ನನ್ನ ಪ್ರವಾದಿಗಳಿಗೆ ಯಾವ ಕೇಡನ್ನೂ ಮಾಡಬಾರದು ಎಂದು ಹೇಳಿದನು.",
			"16": "<sup>16</sup> ಅನಂತರ ಆತನು ದೇಶದಲ್ಲಿ ಕ್ಷಾಮವನ್ನು ಬರಮಾಡಿ ಆಹಾರವೆಂಬ ಊರುಗೋಲನ್ನು ಮುರಿದುಬಿಟ್ಟನು.",
			"17": "<sup>17</sup> ಆತನು ಅವರ ಮುಂದಾಗಿ ಒಬ್ಬನನ್ನು ಕಳುಹಿಸಿದನು; ದಾಸತ್ವಕ್ಕೆ ಮಾರಲ್ಪಟ್ಟ ಯೋಸೇಫನೇ ಅವನು.",
			"18": "<sup>18</sup> ಅವನ ಕಾಲುಗಳು ಕೋಳದಲ್ಲಿ ನೊಂದವು; ಕಬ್ಬಿಣದ ಬೇಡಿಗಳಿಂದ ಅವನು ಬಂಧಿತನಾದನು.",
			"19": "<sup>19</sup> ಅವನು ತನ್ನ ಮಾತು ನೆರವೇರುವ ತನಕ ಯೆಹೋವನ ವಾಕ್ಯದಿಂದ ಶೋಧಿತನಾದನು.",
			"20": "<sup>20</sup> ಅರಸನು ಅಪ್ಪಣೆಮಾಡಿ ಅವನನ್ನು ತಪ್ಪಿಸಿದನು; ಜನಪತಿಯು ಅವನನ್ನು ಬಿಡಿಸಿದನು.",
			"21": "<sup>21</sup> ಅವನನ್ನು ತನ್ನ ಮನೆಗೆ ಯಜಮಾನನನ್ನಾಗಿಯೂ ತನ್ನ ಆಸ್ತಿಗೆಲ್ಲಾ ಅಧಿಕಾರಿಯನ್ನಾಗಿಯೂ ಮಾಡಿದನು.",
			"22": "<sup>22</sup> ತನ್ನ ಪ್ರಧಾನರನ್ನು ಇಷ್ಟಾನುಸಾರವಾಗಿ ಬಂಧಿಸುವದಕ್ಕೂ ತನ್ನ ಮಂತ್ರಿಗಳಿಗೆ ಬುದ್ಧಿಕಲಿಸುವದಕ್ಕೂ ಅವನಿಗೆ ಅಧಿಕಾರಕೊಟ್ಟನು.",
			"23": "<sup>23</sup> ಆಗ ಇಸ್ರಾಯೇಲನು ಐಗುಪ್ತಕ್ಕೆ ಬಂದನು; ಯಾಕೋಬನು ಹಾಮನ ದೇಶದಲ್ಲಿ ಪ್ರವಾಸಿಯಾದನು.",
			"24": "<sup>24</sup> ದೇವರು ತನ್ನ ಜನರನ್ನು ಬಹಳವಾಗಿ ವೃದ್ಧಿಮಾಡಿ ಅವರು ಶತ್ರುಗಳಿಗಿಂತ ಬಲಿಷ್ಠರಾಗುವಂತೆ ಮಾಡಿದನು.",
			"25": "<sup>25</sup> ಆತನು ಆ ದೇಶದವರ ಹೃದಯವನ್ನು ಮಾರ್ಪಡಿಸಿದ್ದರಿಂದ ಅವರು ಆತನ ಜನರನ್ನು ದ್ವೇಷಿಸಿ ಆತನ ಸೇವಕರನ್ನು ಕುಯುಕ್ತಿಯಿಂದ ನಡಿಸಿದರು.",
			"26": "<sup>26</sup> ಆಗ ಆತನು ತನ್ನ ಸೇವಕನಾದ ಮೋಶೆಯನ್ನೂ ತಾನು ಆರಿಸಿಕೊಂಡ ಆರೋನನನ್ನೂ ಕಳುಹಿಸಿದನು.",
			"27": "<sup>27</sup> ಅವರು ಹಾಮನ ದೇಶದವರ ಮಧ್ಯದಲ್ಲಿ ಆತನು ಆಜ್ಞಾಪಿಸಿದ ವಿವಿಧ ಮಹತ್ಕಾರ್ಯಗಳನ್ನೂ ಅದ್ಭುತಗಳನ್ನೂ ನಡಿಸಿದರು.",
			"28": "<sup>28</sup> ಆತನು ಕತ್ತಲೆಯನ್ನು ಕಳುಹಿಸಲು ಕತ್ತಲೆಯಾಯಿತು. ಅವರು ಆತನ ಆಜ್ಞೆಗಳನ್ನು ತಿರಸ್ಕರಿಸಲಿಲ್ಲ.",
			"29": "<sup>29</sup> ಆತನು ಐಗುಪ್ತ್ಯರ ನೀರನ್ನು ರಕ್ತಮಾಡಿ ಮೀನುಗಳನ್ನು ಸಾಯಿಸಿದನು.",
			"30": "<sup>30</sup> ಅವರ ದೇಶದಲ್ಲೆಲ್ಲಾ ಕಪ್ಪೆಗಳು ತುಂಬಿಕೊಂಡವು; ಅರಮನೆಯಲ್ಲಿಯೂ ವ್ಯಾಪಿಸಿಕೊಂಡವು.",
			"31": "<sup>31</sup> ಆತನು ಆಜ್ಞಾಪಿಸಲು ಅವರ ಎಲ್ಲಾ ಪ್ರಾಂತಗಳಲ್ಲಿ ವಿಷದ ಹುಳಗಳೂ ಹೇನುಗಳೂ ಉಂಟಾದವು.",
			"32": "<sup>32</sup> ಆತನು ಅವರ ದೇಶದಲ್ಲಿ ಕಲ್ಮಳೆಯನ್ನೂ ಬೆಂಕಿಯುರಿಯನ್ನೂ ಬರಮಾಡಿ",
			"33": "<sup>33</sup> ಅವರ ದ್ರಾಕ್ಷಾಲತೆಗಳನ್ನೂ ಅಂಜೂರದ ಗಿಡಗಳನ್ನೂ ಹೊಡೆದು ಅವರ ಪ್ರಾಂತಗಳಲ್ಲಿದ್ದ ಮರಗಳನ್ನು ಮುರಿದುಬಿಟ್ಟನು.",
			"34": "<sup>34</sup> ಆತನು ಆಜ್ಞಾಪಿಸಲು ಮಿಡಿತೆಗಳೂ ಲೆಕ್ಕವಿಲ್ಲದಷ್ಟು ಜಿಟ್ಟೆಹುಳಗಳೂ ಬಂದು",
			"35": "<sup>35</sup> ಅವರ ದೇಶದಲ್ಲಿದ್ದ ಎಲ್ಲಾ ಪೈರುಗಳನ್ನೂ ಭೂಮಿಯ ಬೆಳೆಗಳನ್ನೂ ತಿಂದುಬಿಟ್ಟವು.",
			"36": "<sup>36</sup> ಆ ದೇಶದವರ ವೀರ್ಯಕ್ಕೆ ಪ್ರಥಮಫಲವಾಗಿದ್ದ ಚೊಚ್ಚಲ ಮಕ್ಕಳನ್ನು ಸಂಹರಿಸಿದನು.",
			"37": "<sup>37</sup> ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಬೆಳ್ಳಿಬಂಗಾರಗಳ ಸಹಿತವಾಗಿ ಹೊರಗೆ ಬರಮಾಡಿದನು; ಅವರ ಕುಲಗಳಲ್ಲಿ ಒಬ್ಬನಾದರೂ ಎಡವುವವನಿರಲಿಲ್ಲ.",
			"38": "<sup>38</sup> ಐಗುಪ್ತ್ಯರು ಅವರ ವಿಷಯದಲ್ಲಿ ಹೆದರಿಕೆಯುಳ್ಳವರಾದ್ದರಿಂದ ಅವರು ಹೊರಟು ಹೋದದಕ್ಕೆ ಸಂತೋಷಿಸಿದರು.",
			"39": "<sup>39</sup> ಅವರಿಗೆ [ಹಗಲಲ್ಲಿ] ನೆರಳಿಗೋಸ್ಕರ ಮೋಡವನ್ನೂ ಇರುಳಲ್ಲಿ ಬೆಳಕಿಗೋಸ್ಕರ ಬೆಂಕಿಯನ್ನೂ ಮೇಲೆ ಹರವಿದನು.",
			"40": "<sup>40</sup> ಅವರು ಬೇಡಿಕೊಳ್ಳಲು ಲಾವಕ್ಕಿಗಳನ್ನು ಬರಮಾಡಿದನು; ದಿವ್ಯಾಹಾರದಿಂದ ಅವರನ್ನು ತೃಪ್ತಿಗೊಳಿಸಿದನು.",
			"41": "<sup>41</sup> ಆತನು ಬಂಡೆಯನ್ನು ಸೀಳಲು ನೀರು ಚಿಮ್ಮಿ ಬಂದು ಅರಣ್ಯದಲ್ಲಿ ನದಿಯಾಗಿ ಹರಿಯಿತು.",
			"42": "<sup>42</sup> ಹೀಗೆ ಆತನು ತನ್ನ ಪರಿಶುದ್ಧವಚನವನ್ನೂ ತನ್ನ ಸೇವಕನಾದ ಅಬ್ರಹಾಮನನ್ನೂ ನೆನಪುಮಾಡಿಕೊಂಡು",
			"43": "<sup>43</sup> ತನ್ನ ಪ್ರಜೆಯು ಉಲ್ಲಾಸದಿಂದಲೂ ತಾನು ಆರಿಸಿಕೊಂಡವರು ಉತ್ಸಾಹಧ್ವನಿಯಿಂದಲೂ ಹೊರಗೆ ಬರುವಂತೆ ಮಾಡಿದನು.",
			"44": "<sup>44</sup> ಮತ್ತು ಆತನು ಅವರಿಗೆ ಪರಜನರ ದೇಶಗಳನ್ನು ಕೊಟ್ಟನು; ಅನ್ಯಜನಾಂಗಗಳ ಕಷ್ಟಾರ್ಜಿತವು ಅವರಿಗೆ ಕೈ ಸೇರಿತು.",
			"45": "<sup>45</sup> ಅವರು ತನ್ನ ವಿಧಿಗಳನ್ನು ಕೈಕೊಂಡು ತನ್ನ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಅನುಸರಿಸಬೇಕೆಂಬದೇ ಆತನ ಉದ್ದೇಶ. ಯಾಹುವಿಗೆ ಸ್ತೋತ್ರ!"
		},
		"106": {
			"1": "<sup>1</sup> ಯಾಹುವಿಗೆ ಸ್ತೋತ್ರ! ಯೆಹೋವನಿಗೆ ಕೃತಜ್ಞತಾಸ್ತುತಿ ಮಾಡಿರಿ. ಆತನು ಒಳ್ಳೆಯವನು. ಆತನ ಕೃಪೆಯು ಶಾಶ್ವತವಾಗಿರುವದು.",
			"2": "<sup>2</sup> ಯೆಹೋವನ ಮಹತ್ಕಾರ್ಯಗಳನ್ನು ಯಾರು ವರ್ಣಿಸಬಲ್ಲರು? ಆತನನ್ನು ತಕ್ಕಂತೆ ಕೀರ್ತಿಸುವದು ಯಾರಿಂದಾದೀತು?",
			"3": "<sup>3</sup> ಯಾವಾಗಲೂ ನ್ಯಾಯವನ್ನು ಕಾಪಾಡುವವರೂ ನೀತಿಯನ್ನು ಪಾಲಿಸುವವರೂ ಧನ್ಯರು.",
			"4": "<sup>4</sup> ಯೆಹೋವನೇ, ನನ್ನನ್ನು ನೆನಪುಮಾಡಿಕೊಂಡು ನಿನ್ನ ಪ್ರಜೆಗೆ ತೋರಿಸುವ ದಯೆಯನ್ನು ನನಗೂ ತೋರಿಸು. ನನ್ನನ್ನು ರಕ್ಷಿಸಲಿಕ್ಕೆ ಬಾ,",
			"5": "<sup>5</sup> ಆಗ ನೀನು ಆರಿಸಿಕೊಂಡ ಪ್ರಜೆಯ ಏಳಿಗೆಯನ್ನು ನೋಡಿ ನಾನೂ ಅವರೊಂದಿಗೆ ಸಂತೋಷಿಸುವೆನು; ನಿನ್ನ ಸ್ವಕೀಯರೊಂದಿಗೆ ನಾನೂ ಹಿಗ್ಗುವೆನು.",
			"6": "<sup>6</sup> ನಮ್ಮ ಪಿತೃಗಳಂತೆ ನಾವೂ ಪಾಪಿಗಳು; ಅಪರಾಧಮಾಡಿದೆವು; ದುಷ್ಟತನವನ್ನು ನಡಿಸಿದೆವು.",
			"7": "<sup>7</sup> ನಮ್ಮ ಪೂರ್ವಿಕರು ಐಗುಪ್ತದಲ್ಲಿ ನಿನ್ನ ಅದ್ಭುತಕ್ರಿಯೆಗಳನ್ನು ಲಕ್ಷಿಸಲಿಲ್ಲ; ನಿನ್ನ ಕೃಪಾತಿಶಯವನ್ನು ಸ್ಮರಿಸಲಿಲ್ಲ. ಕೆಂಪು ಸಮುದ್ರದ ಬಳಿಯಲ್ಲಿ ನಿನಗೆ ತಿರುಗಿಬಿದ್ದರು.",
			"8": "<sup>8</sup> ಆದರೂ ಆತನು ತನ್ನ ಹೆಸರಿನ ನಿಮಿತ್ತವಾಗಿಯೂ ತನ್ನ ಶೌರ್ಯವನ್ನು ಪ್ರಕಟಿಸುವದಕ್ಕಾಗಿಯೂ ಅವರನ್ನು ರಕ್ಷಿಸಿದನು.",
			"9": "<sup>9</sup> ಆತನು ಗದರಿಸಲು ಕೆಂಪು ಸಮುದ್ರವು ಒಣಗಿ ಹೋಯಿತು; ಅಡವಿಯನ್ನೋ ಎಂಬಂತೆ ಸಾಗರವನ್ನು ದಾಟಿಸಿದನು.",
			"10": "<sup>10</sup> ಹಗೆಗಳ ಕೈಯಿಂದ ಅವರನ್ನು ತಪ್ಪಿಸಿದನು; ವೈರಿಹಸ್ತದಿಂದ ಬಿಡಿಸಿದನು.",
			"11": "<sup>11</sup> ಜಲರಾಶಿಯು ಅವರ ವಿರೋಧಿಗಳನ್ನು ಮುಚ್ಚಿಬಿಟ್ಟಿತು; ಒಬ್ಬನೂ ಉಳಿಯಲಿಲ್ಲ.",
			"12": "<sup>12</sup> ಆಗ ಅವರು ಆತನ ಮಾತು ನಂಬಿ ಆತನನ್ನು ಕೀರ್ತಿಸಿದರು.",
			"13": "<sup>13</sup> ಆದರೆ ಅವರು ಬೇಗನೆ ಆತನ ಕೆಲಸಗಳನ್ನು ಮರೆತು ಆತನ ಸಂಕಲ್ಪವನ್ನು ಕಾದಿರದೆ",
			"14": "<sup>14</sup> ಅರಣ್ಯದಲ್ಲಿ ಆಶಾತುರರಾಗಿ ಅಡವಿಯಲ್ಲಿ ದೇವರನ್ನು ಪರೀಕ್ಷಿಸಿದರು.",
			"15": "<sup>15</sup> ಆತನು ಅವರ ಆಶೆಯನ್ನು ಪೂರೈಸಿದರೂ ಅವರ ಪ್ರಾಣಕ್ಕೆ ಕ್ಷಯವನ್ನು ಬರಮಾಡಿದನು.",
			"16": "<sup>16</sup> ಅವರು ಪಾಳೆಯದಲ್ಲಿ ಮೋಶೆಯ ಮೇಲೆಯೂ ಯೆಹೋವನು ಪ್ರತಿಷ್ಠಿಸಿದ ಆರೋನನ ಮೇಲೆಯೂ ಹೊಟ್ಟೆಕಿಚ್ಚುಪಡಲು",
			"17": "<sup>17</sup> ಭೂಮಿಯು ಬಾಯ್ದೆರೆದು ದಾತಾನನನ್ನು ನುಂಗಿಬಿಟ್ಟಿತು; ಅಬಿರಾಮನ ಕಡೆಯವರನ್ನು ಮುಚ್ಚಿಬಿಟ್ಟಿತು.",
			"18": "<sup>18</sup> ಮತ್ತು ಅವರ ಮಧ್ಯದಲ್ಲಿ ಬೆಂಕಿಯುಂಟಾಯಿತು; ಅಗ್ನಿಜ್ವಾಲೆಯು ದುಷ್ಟರನ್ನು ದಹಿಸಿಬಿಟ್ಟಿತು.",
			"19": "<sup>19</sup> ಹೋರೇಬಿನಲ್ಲಿ ಎರಕದ ಬಸವನನ್ನು ಮಾಡಿ ಅದಕ್ಕೆ ಅಡ್ಡಬಿದ್ದರು.",
			"20": "<sup>20</sup> ಹೀಗೆ ಅವರು ತಮ್ಮ ದಿವ್ಯಮಹಿಮೆಯನ್ನು ಬಿಟ್ಟು ಹುಲ್ಲುತಿನ್ನುವ ಎತ್ತಿನ ಜಡಮೂರ್ತಿಯನ್ನು ಹಿಡಿದುಕೊಂಡರು.",
			"21": "<sup>21</sup> ಐಗುಪ್ತದಲ್ಲಿ ಮಹತ್ತುಗಳನ್ನೂ ಹಾಮನ ದೇಶದಲ್ಲಿ ಅದ್ಭುತಗಳನ್ನೂ",
			"22": "<sup>22</sup> ಕೆಂಪುಸಮುದ್ರದ ಬಳಿಯಲ್ಲಿ ಘೋರ ಕೃತ್ಯಗಳನ್ನೂ ನಡಿಸಿದ ತಮ್ಮ ರಕ್ಷಕನಾದ ದೇವರನ್ನು ಮರೆತೇಬಿಟ್ಟರು.",
			"23": "<sup>23</sup> ಆದದರಿಂದ ಆತನು ಅವರನ್ನು ಸಂಹರಿಸುವೆನೆನ್ನಲು ಆತನು ಆರಿಸಿಕೊಂಡ ಮೋಶೆಯು ಮಧ್ಯಸ್ಥನಾಗಿ ಅವರನ್ನು ಸಂಹರಿಸದಂತೆ ಆತನ ಕೋಪವನ್ನು ಶಾಂತಪಡಿಸಿದನು.",
			"24": "<sup>24</sup> ಮತ್ತು ಅವರು ಆ ರಮಣೀಯದೇಶವನ್ನು ತಿರಸ್ಕರಿಸಿ ಆತನ ಮಾತನ್ನು ನಂಬಲಿಲ್ಲ.",
			"25": "<sup>25</sup> ತಮ್ಮ ತಮ್ಮ ಗುಡಾರಗಳಲ್ಲಿ ಗುಣುಗುಟ್ಟಿ ಯೆಹೋವನ ಮಾತಿಗೆ ಕಿವಿಗೊಡಲಿಲ್ಲ.",
			"26": "<sup>26</sup> ಇದರಿಂದ ಆತನು ಅವರನ್ನು ಅರಣ್ಯದಲ್ಲಿಯೇ ಬೀಳಿಸುವೆನೆಂದೂ",
			"27": "<sup>27</sup> ಅವರ ಸಂತಾನವನ್ನು ದೇಶಾಂತರಗಳಲ್ಲಿ ಚದರಿಸಿ ಅನ್ಯಜನಾಂಗಗಳ ನಡುವೆ ನಾಶಮಾಡುವೆನೆಂದೂ ಕೈಯೆತ್ತಿದನು.",
			"28": "<sup>28</sup> ಅವರು ತಮ್ಮನ್ನು ಬಾಳ್‍ಪೆಗೋರನ ಸೇವೆಗೆ ಒಪ್ಪಿಸಿ ಜಡಮೂರ್ತಿಗಳಿಗೆ ಅರ್ಪಿಸಿದ್ದನ್ನು ಊಟಮಾಡಿದರು.",
			"29": "<sup>29</sup> ಈ ತಮ್ಮ ಕ್ರಿಯೆಗಳಿಂದ ಆತನನ್ನು ಕೆಣಕಿದರು; ಅವರಲ್ಲಿ ವ್ಯಾಧಿಯುಂಟಾಯಿತು.",
			"30": "<sup>30</sup> ಫಿನೆಹಾಸನು ಎದ್ದು ಅವರನ್ನು ದಂಡಿಸಲು ಆ ವ್ಯಾಧಿಯು ನಿಂತುಹೋಯಿತು.",
			"31": "<sup>31</sup> ಅವನು ಮಾಡಿದ್ದು ತಲತಲಾಂತರಕ್ಕೂ ನೀತಿಯೆಂದು ಎಣಿಸಲ್ಪಟ್ಟಿತು.",
			"32": "<sup>32</sup> ಮತ್ತು ಅವರು ಮೆರೀಬದ ಪ್ರವಾಹ ಸಮೀಪದಲ್ಲಿ ಆತನನ್ನು ರೇಗಿಸಿದರು. ಅವರ ದೆಸೆಯಿಂದ ಮೋಶೆಗೆ ಕೇಡುಂಟಾಯಿತು.",
			"33": "<sup>33</sup> ಅವರು ದೇವರಾತ್ಮನಿಗೆ ವಿರೋಧವಾಗಿ ನಿಂತದ್ದರಿಂದ ಮೋಶೆ ದುಡುಕಿ ಮಾತಾಡಿದನು.",
			"34": "<sup>34</sup> ಅನ್ಯಜನಾಂಗದವರನ್ನು ಸಂಹರಿಸಬೇಕೆಂಬ ಯೆಹೋವನ ಆಜ್ಞೆಗೆ ಅವಿಧೇಯರಾಗಿ",
			"35": "<sup>35</sup> ಅವರೊಡನೆ ಕೂಡಿಕೊಂಡು ಅವರ ದುರಾಚಾರಗಳನ್ನು ಕಲಿತುಕೊಂಡರು.",
			"36": "<sup>36</sup> ಅವರ ವಿಗ್ರಹಗಳನ್ನು ಸೇವಿಸಿದರು; ಅವು ಅವರಿಗೆ ಉರುಲಿನಂತಾದವು.",
			"37": "<sup>37</sup> ಅವರು ತಮ್ಮ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ಭೂತಗಳಿಗೆ ಬಲಿಕೊಟ್ಟರು.",
			"38": "<sup>38</sup> ಕಾನಾನ್ಯರ ಮೂರ್ತಿಗಳ ಅರ್ಪಣೆಗೋಸ್ಕರ ತಮ್ಮ ಮಕ್ಕಳ ನಿರಪರಾಧರಕ್ತವನ್ನು ಸುರಿಸಿದರು; ಇಂಥ ಕೊಲೆಗಳಿಂದ ದೇಶವನ್ನು ಹೊಲೆಮಾಡಿದರು.",
			"39": "<sup>39</sup> ಅವರು ತಮ್ಮ ದುಷ್ಕೃತ್ಯಗಳಿಂದ ಅಶುದ್ಧರಾದರು. ದುರಾಚಾರಗಳಿಂದ ದೇವದ್ರೋಹಿಗಳಾದರು.",
			"40": "<sup>40</sup> ಆಗ ಯೆಹೋವನ ಕೋಪವು ಅವರ ಮೇಲೆ ಉರಿಗೊಂಡಿತು; ಆತನು ತನ್ನ ಸ್ವಾಸ್ತ್ಯವಾದ ಪ್ರಜೆಗಳನ್ನು ಅಸಹಿಸಿ",
			"41": "<sup>41</sup> ಅವರನ್ನು ಅನ್ಯಜನಾಂಗಗಳ ಕೈಗೆ ಒಪ್ಪಿಸಿದನು. ವೈರಿಗಳು ಅವರ ಮೇಲೆ ಅಧಿಕಾರ ನಡಿಸಿದರು;",
			"42": "<sup>42</sup> ಶತ್ರುಗಳು ಅವರನ್ನು ಕುಗ್ಗಿಸಿದರು; ಅವರ ಕೈಕೆಳಗೆ ತಗ್ಗಿಹೋದರು.",
			"43": "<sup>43</sup> ಆತನು ಅವರನ್ನು ಅನೇಕಾವರ್ತಿ ರಕ್ಷಿಸಿದರೂ ಅವರು ತಮ್ಮ ಆಲೋಚನೆಯನ್ನು ಅನುಸರಿಸಿ ಅವಿಧೇಯರಾದರು; ತಮ್ಮ ಅಕ್ರಮದಿಂದಲೇ ಹೀನಸ್ಥಿತಿಗೆ ಬಂದರು.",
			"44": "<sup>44</sup> ಆದರೆ ಆತನು ಕಷ್ಟದಲ್ಲಿದ್ದ ಅವರ ಮೊರೆಯನ್ನು ಕೇಳಿ ಪರಾಂಬರಿಸಿದನು.",
			"45": "<sup>45</sup> ತನ್ನ ಒಡಂಬಡಿಕೆಯನ್ನು ನೆನಪುಮಾಡಿಕೊಂಡು ತನ್ನ ಕೃಪಾತಿಶಯದಿಂದ ಅವರನ್ನು ಕನಿಕರಿಸಿದನು.",
			"46": "<sup>46</sup> ಅವರನ್ನು ಸೆರೆಯೊಯ್ದವರಲ್ಲಿ ಅವರ ಮೇಲೆ ದಯೆಹುಟ್ಟಿಸಿದನು.",
			"47": "<sup>47</sup> ಯೆಹೋವನೇ, ನಮ್ಮ ದೇವರೇ, ರಕ್ಷಿಸು; ಅನ್ಯಜನಗಳಲ್ಲಿ ಚದರಿರುವ ನಮ್ಮನ್ನು ತಿರುಗಿ ಕೂಡಿಸು. ಆಗ ನಿನ್ನ ಪರಿಶುದ್ಧ ನಾಮವನ್ನು ಕೊಂಡಾಡುವೆವು; ನಿನ್ನ ಸ್ತೋತ್ರದಲ್ಲಿ ಹೆಚ್ಚಳಪಡುವೆವು.",
			"48": "<sup>48</sup> ಇಸ್ರಾಯೇಲ್‍ದೇವನಾದ ಯೆಹೋವನಿಗೆ ಯುಗಯುಗಾಂತರಗಳವರೆಗೂ ಕೊಂಡಾಟವಾಗಲಿ; ಸರ್ವಜನರೂ ಆಮೆನ್ ಎನ್ನಲಿ. ಯಾಹುವಿಗೆ ಸ್ತೋತ್ರ!"
		},
		"107": {
			"1": "<sup>1</sup> ಯೆಹೋವನಿಗೆ ಕೃತಜ್ಞತಾಸ್ತುತಿ ಮಾಡಿರಿ; ಆತನು ಒಳ್ಳೆಯವನು. ಆತನ ಕೃಪೆಯು ಶಾಶ್ವತವಾಗಿರುವದು.",
			"2": "<sup>2</sup> ಯೆಹೋವನ ವಿಮುಕ್ತರು ಅಂದರೆ ಆತನು ಶತ್ರುಗಳಿಂದ ಬಿಡಿಸಿ",
			"3": "<sup>3</sup> ಪೂರ್ವಪಶ್ಚಿಮೋತ್ತರಗಳಲ್ಲಿಯೂ ಸಮುದ್ರದ ಕಡೆಯಲ್ಲಿಯೂ ಇರುವ ದೇಶಗಳಿಂದ ಕೂಡಿಸಿದವರೆಲ್ಲರೂ ಸ್ತುತಿಮಾಡಲಿ.",
			"4": "<sup>4</sup> ಅವರು ಅರಣ್ಯದಲ್ಲಿಯೂ ಮರಳುಕಾಡಿನಲ್ಲಿಯೂ ದಾರಿತಪ್ಪಿ ಅಲೆಯುವವರಾಗಿ ಜನವಿರುವ ಊರನ್ನು ಕಾಣದೆ",
			"5": "<sup>5</sup> ಹಸಿವೆನೀರಡಿಕೆಗಳಿಂದ ಬಲಗುಂದಿದವರಾಗಿದ್ದರು.",
			"6": "<sup>6</sup> ಅವರು ತಮ್ಮ ಇಕ್ಕಟ್ಟಿನಲ್ಲಿ ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಡಲು ಆತನು ಅವರನ್ನು ಕಷ್ಟದಿಂದ ಬಿಡಿಸಿದನು.",
			"7": "<sup>7</sup> ಜನವಿರುವ ಊರನ್ನು ಸೇರುವಂತೆ ಅವರನ್ನು ಸರಿಯಾದ ದಾರಿಯಲ್ಲಿ ನಡಿಸಿದನು.",
			"8": "<sup>8</sup> ಅವರು ಯೆಹೋವನ ಕೃಪೆಗೋಸ್ಕರವೂ ಆತನು ಮಾನವರಿಗಾಗಿ ನಡಿಸಿದ ಅದ್ಭುತಗಳಿಗೋಸ್ಕರವೂ ಆತನನ್ನು ಕೊಂಡಾಡಲಿ.",
			"9": "<sup>9</sup> ಆತನು ಬಾಯಾರಿದವರ ಆಶೆಯನ್ನು ಪೂರೈಸಿ ಹಸಿದವರನ್ನು ಮೃಷ್ಟಾನ್ನದಿಂದ ತೃಪ್ತಿಗೊಳಿಸುತ್ತಾನೆ.",
			"10": "<sup>10</sup> ಕತ್ತಲಲ್ಲಿಯೂ ಘೋರಾಂಧಕಾರದಲ್ಲಿಯೂ ಬೇಡಿಗಳಿಂದ ಬಂಧಿಸಲ್ಪಟ್ಟು ನೋವಿನಿಂದ ಬಿದ್ದುಕೊಂಡಿದ್ದರು.",
			"11": "<sup>11</sup> ಅವರು ದೇವರ ಕಟ್ಟಳೆಗಳಿಗೆ ವಿರೋಧವಾಗಿ ನಿಂತು ಪರಾತ್ಪರನ ಸಂಕಲ್ಪವನ್ನು ನಿರಾಕರಿಸಿದ್ದರಿಂದ",
			"12": "<sup>12</sup> ಆತನು ಅವರನ್ನು ಕಷ್ಟಗಳಿಂದ ಕುಗ್ಗಿಸಿದನು; ನಿರಾಶ್ರಯರಾಗಿ ಬಿದ್ದುಹೋದರು.",
			"13": "<sup>13</sup> ಅವರು ತಮ್ಮ ಇಕ್ಕಟ್ಟಿನಲ್ಲಿ ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಡಲು ಆತನು ಅವರನ್ನು ಕಷ್ಟದಿಂದ ತಪ್ಪಿಸಿದನು.",
			"14": "<sup>14</sup> ಆತನು ಅವರ ಬಂಧನಗಳನ್ನು ತೆಗೆದುಹಾಕಿ ಕತ್ತಲೆಯಿಂದಲೂ ಘೋರಾಂಧಕಾರದಿಂದಲೂ ಅವರನ್ನು ಹೊರತಂದನು.",
			"15": "<sup>15</sup> ಅವರು ಯೆಹೋವನ ಕೃಪೆಗೋಸ್ಕರವೂ ಆತನು ಮಾನವರಿಗಾಗಿ ನಡಿಸಿದ ಅದ್ಭುತಗಳಿಗೋಸ್ಕರವೂ ಆತನನ್ನು ಕೊಂಡಾಡಲಿ.",
			"16": "<sup>16</sup> ಆತನು ತಾಮ್ರದ ಕದಗಳನ್ನು ಒಡೆದು ಕಬ್ಬಿಣದ ಅಗುಳಿಗಳನ್ನು ಮುರಿದುಬಿಟ್ಟಿದ್ದಾನೆ.",
			"17": "<sup>17</sup> ಮೂರ್ಖರು ಅಪರಾಧದುರಾಚಾರಗಳ ದೆಸೆಯಿಂದ ಬಾಧೆಗೊಳಗಾದರು.",
			"18": "<sup>18</sup> ಎಲ್ಲಾ ಆಹಾರಕ್ಕೂ ಅಸಹ್ಯಪಟ್ಟು ಮರಣದ್ವಾರಕ್ಕೆ ಸಮೀಪವಾದರು.",
			"19": "<sup>19</sup> ಅವರು ತಮ್ಮ ಇಕ್ಕಟ್ಟಿನಲ್ಲಿ ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಡಲು ಆತನು ಅವರನ್ನು ಕಷ್ಟದಿಂದ ತಪ್ಪಿಸಿದನು.",
			"20": "<sup>20</sup> ಆತನು [ದೂತನನ್ನೋ ಎಂಬಂತೆ] ತನ್ನ ವಾಕ್ಯವನ್ನು ಕಳುಹಿಸಿ ಅವರನ್ನು ಗುಣಪಡಿಸಿದನು; ಸಮಾಧಿಗೆ ಸೇರದಂತೆ ಮಾಡಿದನು.",
			"21": "<sup>21</sup> ಅವರು ಯೆಹೋವನ ಕೃಪೆಗೋಸ್ಕರವೂ ಆತನು ಮಾನವರಿಗಾಗಿ ನಡಿಸಿದ ಅದ್ಭುತಗಳಿಗೋಸ್ಕರವೂ ಆತನನ್ನು ಕೊಂಡಾಡಲಿ.",
			"22": "<sup>22</sup> ಕೃತಜ್ಞತಾಯಜ್ಞಗಳನ್ನು ಸಮರ್ಪಿಸಿ ಉತ್ಸಾಹಧ್ವನಿಮಾಡುತ್ತಾ ಆತನ ಮಹತ್ಕಾರ್ಯಗಳನ್ನು ವರ್ಣಿಸಲಿ.",
			"23": "<sup>23</sup> ಹಡಗುಹತ್ತಿ ಸಮುದ್ರಪ್ರಯಾಣಮಾಡುತ್ತಾ ಮಹಾಜಲರಾಶಿಯಲ್ಲಿ ತಮ್ಮ ಉದ್ಯೋಗವನ್ನು ನಡಿಸುವವರು",
			"24": "<sup>24</sup> ಯೆಹೋವನ ಮಹತ್ಕಾರ್ಯಗಳನ್ನೂ ಅಗಾಧಜಲದಲ್ಲಿ ಆತನ ಅದ್ಭುತಗಳನ್ನೂ ನೋಡುತ್ತಾರೆ.",
			"25": "<sup>25</sup> ಆತನು ಅಪ್ಪಣೆಕೊಡಲು ಬಿರುಗಾಳಿಯುಂಟಾಗಿ ಅದರಲ್ಲಿ ತೆರೆಗಳನ್ನು ಎಬ್ಬಿಸಿತು.",
			"26": "<sup>26</sup> ಅವರು ಆಕಾಶಕ್ಕೆ ಏರುತ್ತಲೂ ಅಗಾಧಕ್ಕೆ ಇಳಿಯುತ್ತಲೂ ಕಂಗೆಟ್ಟು ಕರಗಿಹೋದರು.",
			"27": "<sup>27</sup> ಅವರು ದಿಕ್ಕುತೋರದವರಾಗಿ ಸುತ್ತುತ್ತಾ ಕುಡಿಕರಂತೆ ಹೊಯ್ದಾಡುತ್ತಿದ್ದರು.",
			"28": "<sup>28</sup> ಅವರು ತಮ್ಮ ಇಕ್ಕಟ್ಟಿನಲ್ಲಿ ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಡಲು ಆತನು ಅವರನ್ನು ಕಷ್ಟದಿಂದ ಹೊರತಂದನು.",
			"29": "<sup>29</sup> ಆತನು ಬಿರುಗಾಳಿಯನ್ನು ಶಾಂತಪಡಿಸಿದನು; ತೆರೆಗಳು ನಿಂತವು.",
			"30": "<sup>30</sup> ಆಗ ಹಡಗಿನವರು ಸಮುದ್ರವು ಶಾಂತವಾದದ್ದರಿಂದ ಸಂತೋಷಪಟ್ಟರು. ಅವರು ಮುಟ್ಟಬೇಕಾದ ರೇವಿಗೆ ಆತನು ಅವರನ್ನು ಸೇರಿಸಿದನು.",
			"31": "<sup>31</sup> ಅವರು ಯೆಹೋವನ ಕೃಪೆಗೋಸ್ಕರವೂ ಆತನು ಮಾನವರಿಗಾಗಿ ನಡಿಸಿದ ಅದ್ಭುತಗಳಿಗೋಸ್ಕರವೂ ಆತನನ್ನು ಕೊಂಡಾಡಲಿ.",
			"32": "<sup>32</sup> ನೆರೆದ ಸಭೆಯಲ್ಲಿ ಆತನನ್ನು ಕೀರ್ತಿಸಲಿ; ಹಿರಿಯರ ಕೂಟದಲ್ಲಿ ಕೊಂಡಾಡಲಿ.",
			"33": "<sup>33</sup> ಆತನು ನಿವಾಸಿಗಳ ದುಷ್ಟತನಕ್ಕಾಗಿ ನದಿಗಳನ್ನು ಅರಣ್ಯವಾಗುವಂತೆಯೂ",
			"34": "<sup>34</sup> ಬುಗ್ಗೆಗಳನ್ನು ಒಣನೆಲವಾಗುವಂತೆಯೂ ಫಲಭೂಮಿಯನ್ನು ಉಪ್ಪುನೆಲವಾಗುವಂತೆಯೂ ಮಾಡಿದನು.",
			"35": "<sup>35</sup> ಅರಣ್ಯವನ್ನು ಕೆರೆಯಾಗಿಯೂ ಒಣನೆಲವನ್ನು ಬುಗ್ಗೆಗಳಾಗಿಯೂ ಮಾಡಿ",
			"36": "<sup>36</sup> ಅಲ್ಲಿ ಹಸಿದವರನ್ನು ನೆಲೆಗೊಳಿಸಿದನು; ಅವರು ನಿವಾಸಪಟ್ಟಣವನ್ನು ಕಟ್ಟಿಕೊಂಡು",
			"37": "<sup>37</sup> ಹೊಲಗಳನ್ನು ಬಿತ್ತಿ ದ್ರಾಕ್ಷಾಲತೆಗಳನ್ನು ನೆಟ್ಟು ಆದಾಯವನ್ನು ಕೂಡಿಸಿಕೊಂಡರು.",
			"38": "<sup>38</sup> ಆತನು ಆಶೀರ್ವದಿಸಿದ್ದರಿಂದ ಅವರು ಬಹಳವಾಗಿ ಹೆಚ್ಚಿದರು. ಅವರಿಗೆ ದನಕರುಗಳೇನೂ ಕಡಿಮೆ ಇರಲಿಲ್ಲ.",
			"39": "<sup>39</sup> ಅವರು ಕೇಡುತೊಂದರೆಗಳಿಂದಲೂ ಸಂಕಟದಿಂದಲೂ ಕುಗ್ಗಿಹೋಗಿ ಸ್ವಲ್ಪ ಜನರಾದರು.",
			"40": "<sup>40</sup> ಪ್ರಭುಗಳಿಗೆ ಅಪಮಾನವನ್ನುಂಟುಮಾಡಿ ಅವರನ್ನು ದಾರಿಯಿಲ್ಲದ ಅರಣ್ಯದಲ್ಲಿ ಅಲೆದಾಡಿಸುವಾತನು",
			"41": "<sup>41</sup> ಕಷ್ಟದಲ್ಲಿದ್ದ ದೀನರನ್ನು ಉನ್ನತಸ್ಥಿತಿಗೇರಿಸಿ ಕುರಿಹಿಂಡಿನಂತೆ ಹೆಚ್ಚಿಸಿದನು.",
			"42": "<sup>42</sup> ಯಥಾರ್ಥರು ಇದನ್ನು ನೋಡಿ ಹಿಗ್ಗುವರು; ಕೆಡುಕುಬಾಯಿ ಮುಚ್ಚಿಹೋಗುವದು.",
			"43": "<sup>43</sup> ಜ್ಞಾನಿಗಳು ಈ ಸಂಗತಿಗಳನ್ನು ಗಮನಿಸಿ ಯೆಹೋವನ ಕೃಪಾಕಾರ್ಯಗಳನ್ನು ಗ್ರಹಿಸಿಕೊಳ್ಳಲಿ."
		},
		"108": {
			"1": "<sup>1</sup> ದೇವರೇ, ನನ್ನ ಹೃದಯವು ಸ್ಥಿರವಾಗಿದೆ; ನಾನು ಬಾರಿಸುತ್ತಾ ಹಾಡುವೆನು; ನನ್ನ ಅಂತರಂಗವೂ ಸಹ.",
			"2": "<sup>2</sup> ಸ್ವರಮಂಡಲವೇ, ಕಿನ್ನರಿಯೇ, ಎಚ್ಚರವಾಗಿರಿ. ಸಂಕೀರ್ತನೆಯಿಂದ ಉದಯವನ್ನು ಮುಂಗೊಳ್ಳುವೆನು.",
			"3": "<sup>3</sup> ಯೆಹೋವನೇ, ಜನಾಂಗಗಳಲ್ಲಿ ನಿನ್ನನ್ನು ಸ್ತುತಿಸುವೆನು; ಸರ್ವದೇಶದವರೊಳಗೆ ನಿನ್ನನ್ನು ಕೊಂಡಾಡುವೆನು.",
			"4": "<sup>4</sup> ಯಾಕಂದರೆ ನಿನ್ನ ಕೃಪೆಯು ಆಕಾಶಕ್ಕಿಂತ ದೊಡ್ಡದಾಗಿದೆ. ನಿನ್ನ ಸತ್ಯತೆಯು ಮುಗಿಲನ್ನು ನಿಲುಕುತ್ತದೆ.",
			"5": "<sup>5</sup> ದೇವರೇ, ಮೇಲಣ ಲೋಕಗಳಲ್ಲಿ ನಿನ್ನ ಪ್ರಭಾವವು ಮೆರೆಯಲಿ; ಭೂಮಂಡಲದ ಮೇಲೆಲ್ಲಾ ನಿನ್ನ ಮಹತ್ವವು ಹಬ್ಬಲಿ.",
			"6": "<sup>6</sup> ನಿನ್ನ ಪ್ರಿಯರಾದ ನಮ್ಮ ಮೊರೆಯನ್ನು ಲಾಲಿಸಿ ರಕ್ಷಿಸು; ನಿನ್ನ ಭುಜಬಲದಿಂದ ನಮ್ಮನ್ನು ಜಯಗೊಳಿಸು.",
			"7": "<sup>7</sup> ದೇವರು ತನ್ನ ಪವಿತ್ರತ್ವವನ್ನು ಸಾಕ್ಷಿಮಾಡಿ ನುಡಿದಿದ್ದಾನೆ. ಜಯಘೋಷಮಾಡುವೆನು; ಶೆಖೆಮ್ ಪ್ರದೇಶವನ್ನು ಹಂಚುವೆನು. ಸುಖೋತ್ ಬೈಲನ್ನು ಅಳೆದುಕೊಡುವೆನು.",
			"8": "<sup>8</sup> ಗಿಲ್ಯಾದ್ ಸೀಮೆಯೂ ಮನಸ್ಸೆಯ ದೇಶವೂ ನನ್ನವು; ಎಫ್ರಾಯೀಮ್ ಗೋತ್ರವು ನನಗೆ ಶಿರಸ್ತ್ರಾಣವು. ನನ್ನ ರಾಜದಂಡವು ಯೆಹೂದಕುಲವೇ.",
			"9": "<sup>9</sup> ಮೋವಾಬ್ ಪ್ರದೇಶವು ನನ್ನ ಸ್ನಾನಪಾತ್ರೆಯು; ಎದೋಮ್ ಸೀಮೆಯು ನನ್ನ ಕೆರಗಳನ್ನು ಬಿಡುವ ಸ್ಥಳ. ಫಿಲಿಷ್ಟಿಯದ ವಿಷಯ ಜಯಘೋಷ ಮಾಡುವೆನು.",
			"10": "<sup>10</sup> ಕೋಟೆಕೊತ್ತಲುಗಳುಳ್ಳ ನಗರಕ್ಕೆ ನನ್ನನ್ನು ಕರೆದುಕೊಂಡು ಹೋಗುವವರು ಯಾರು? ಎದೋಮ್ ಪ್ರಾಂತದೊಳಗೆ ನನ್ನನ್ನು ಸೇರಿಸುವವರು ಯಾರು?",
			"11": "<sup>11</sup> ದೇವರೇ, ನೀನು ನಮ್ಮ ಸೈನ್ಯಗಳ ಸಂಗಡ ಬರಲಿಲ್ಲವಲ್ಲಾ! ದೇವರೇ, ನಮ್ಮನ್ನು ಕೈಬಿಟ್ಟಿಯಾ?",
			"12": "<sup>12</sup> ನಮಗೆ ಕೈಕೊಟ್ಟು ಶತ್ರುಬಾಧೆಯಿಂದ ಪಾರುಮಾಡು. ಮನುಷ್ಯರ ಸಹಾಯವು ವ್ಯರ್ಥ.",
			"13": "<sup>13</sup> ದೇವರಿಂದ ಶೂರಕೃತ್ಯಗಳನ್ನು ನಡಿಸುವೆವು; ನಮ್ಮ ವೈರಿಗಳನ್ನು ತುಳಿದುಬಿಡುವವನು ಆತನೇ."
		},
		"109": {
			"1": "<sup>1</sup> ನಾನು ಸ್ತುತಿಸುವ ದೇವರೇ, ಸುಮ್ಮನಿರಬೇಡ.",
			"2": "<sup>2</sup> ದುಷ್ಟನೂ ವಂಚಕನೂ ನನಗೆ ವಿರೋಧವಾಗಿ ಬಾಯ್ದೆರೆಯ ಮಾಡಿದ್ದಾರೆ. ಸುಳ್ಳುನಾಲಿಗೆಯು ನನ್ನೊಡನೆ ಮಾತಾಡಿತು.",
			"3": "<sup>3</sup> ಅವರು ಹಗೆನುಡಿಗಳಿಂದ ನನ್ನನ್ನು ಮುತ್ತಿಕೊಂಡು ನಿಷ್ಕಾರಣವಾಗಿ ನನ್ನ ಸಂಗಡ ಯುದ್ಧಮಾಡಿದ್ದಾರೆ.",
			"4": "<sup>4</sup> ನಾನು ಅವರನ್ನು ಪ್ರೀತಿಸಿದರೂ ಅವರು ನನ್ನನ್ನು ವಿರೋಧಿಸುತ್ತಾರೆ; ನಾನಾದರೋ ನಿನಗೆ ಮೊರೆಯಿಡುತ್ತೇನೆ.",
			"5": "<sup>5</sup> ಉಪಕಾರಕ್ಕೆ ಅಪಕಾರವನ್ನು ಮಾಡಿದ್ದಾರೆ; ನಾನು ಪ್ರೀತಿಸಿದ್ದಕ್ಕೆ ಪ್ರತಿಯಾಗಿ ನನ್ನನ್ನು ದ್ವೇಷಿಸಿದ್ದಾರೆ.",
			"6": "<sup>6</sup> ದುಷ್ಟಾಧಿಕಾರಿಯನ್ನು ಅವನ ಮೇಲೆ ನೇಮಿಸು; ತಪ್ಪುಹೊರಿಸುವವನನ್ನು ಅವನ ಬಲಗಡೆಯಲ್ಲಿ ನಿಲ್ಲಿಸು.",
			"7": "<sup>7</sup> ನ್ಯಾಯವಿಚಾರಣೆಯಲ್ಲಿ ಅವನು ಅಪರಾಧಿಯೆಂದು ತೀರ್ಪುಹೊಂದಲಿ; ಅವನ ಪ್ರಾರ್ಥನೆಯೂ ಪಾಪವೆಂದು ಎಣಿಸಲ್ಪಡಲಿ.",
			"8": "<sup>8</sup> ಅವನ ಜೀವಿತದಿವಸಗಳು ಸ್ವಲ್ಪವಾಗಿರಲಿ; ಅವನ ಉದ್ಯೋಗವು ಮತ್ತೊಬ್ಬನಿಗಾಗಲಿ.",
			"9": "<sup>9</sup> ಅವನ ಮಕ್ಕಳು ಅನಾಥರೂ, ಹೆಂಡತಿ ವಿಧವೆಯೂ ಆಗಲಿ.",
			"10": "<sup>10</sup> ಅವನ ಮಕ್ಕಳು ತಮ್ಮ ಹಾಳಾದ ಸ್ಥಳಗಳನ್ನು ಬಿಟ್ಟು ತಿರುಕರಂತೆ ಅಲೆಯುತ್ತಾ ಭಿಕ್ಷೆಬೇಡಲಿ.",
			"11": "<sup>11</sup> ಸಾಲಕೊಟ್ಟವನು ಅವನ ಆಸ್ತಿಯನ್ನೆಲ್ಲಾ ಕಸುಕೊಳ್ಳಲಿ; ಪರರು ಅವನ ಕಷ್ಟಾರ್ಜಿತವನ್ನು ಸುಲುಕೊಳ್ಳಲಿ.",
			"12": "<sup>12</sup> ಅವನಿಗೆ ಕೃಪೆಮಾಡುತ್ತಿರುವವನೂ ಅವನ ದಿಕ್ಕಿಲ್ಲದ ಮಕ್ಕಳಿಗೆ ದಯೆತೋರಿಸುವವನೂ ಯಾವನೂ ಇಲ್ಲವಾಗಲಿ.",
			"13": "<sup>13</sup> ಅವನ ಸಂತತಿಯು ನಿರ್ಮೂಲವಾಗಲಿ. ಎರಡನೆಯ ತಲೆಯಲ್ಲಿಯೇ ಅದು ನಿರ್ನಾಮವಾಗಿ ಹೋಗಲಿ.",
			"14": "<sup>14</sup> ಯೆಹೋವನು ಅವನ ಹಿರಿಯರ ಪಾಪವನ್ನು ಮರೆಯದಿರಲಿ; ಅವನ ತಾಯಿಯ ಪಾಪವು ಅಳಿದುಹೋಗದಿರಲಿ.",
			"15": "<sup>15</sup> ಅವು ಯೆಹೋವನ ಜ್ಞಾಪಕದಲ್ಲಿ ಇದ್ದೇ ಇರಲಿ. ಆತನು ಅವರ ಹೆಸರನ್ನು ಭೂಮಿಯಿಂದ ತೆಗೆದುಹಾಕಲಿ.",
			"16": "<sup>16</sup> ಯಾಕಂದರೆ ಅವನು ಪ್ರೀತಿಸುವದನ್ನು ಮರೆತುಬಿಟ್ಟನು. ಬಡವನನ್ನೂ ದೀನನನ್ನೂ ಮನಗುಂದಿದವನನ್ನೂ ಹಿಂಸಿಸಿ ಕೊಲ್ಲಬೇಕೆಂದು ಯತ್ನಿಸಿದನು.",
			"17": "<sup>17</sup> ಶಪಿಸುವದು ಇಷ್ಟವಾಗಿದ್ದದರಿಂದ ಅದು ಅವನಿಗೆ ಬಂತು; ಆಶೀರ್ವದಿಸುವದು ಇಷ್ಟವಲ್ಲದ್ದರಿಂದ ಅದು ಅವನಿಗೆ ದೂರವಾಯಿತು.",
			"18": "<sup>18</sup> ಶಪಿಸುವದೇ ಅವನಿಗೆ ವಸ್ತ್ರವಾಗಿತ್ತು. ಅದು ನೀರಿನಂತೆ ಅವನ ಅಂತರಂಗದಲ್ಲಿಯೂ ಎಣ್ಣೆಯಂತೆ ಎಲುಬುಗಳಲ್ಲಿಯೂ ಸೇರಿತು.",
			"19": "<sup>19</sup> ಶಾಪವೇ ಅವನ ಹೊದಿಕೆಯೂ ನಿತ್ಯವಾದ ನಡುಕಟ್ಟೂ ಆಗಲಿ.",
			"20": "<sup>20</sup> ನನ್ನನ್ನು ಎದುರಿಸಿ ಕೀಳುಮಾತಾಡುವವರಿಗೆ ಯೆಹೋವನಿಂದ ಬರುವ ಪ್ರತಿಫಲ ಇದೇ.",
			"21": "<sup>21</sup> ಕರ್ತನೇ, ಯೆಹೋವನೇ, ನಿನ್ನ ಕೃಪೆಯು ಹಿತಕರವಾಗಿದೆ; ನಿನ್ನ ಹೆಸರಿನ ನಿಮಿತ್ತ ನನ್ನ ಪಕ್ಷವನ್ನು ಹಿಡಿದು ರಕ್ಷಿಸು.",
			"22": "<sup>22</sup> ನಾನು ಬಡವನೂ ದೀನನೂ ಆಗಿದ್ದೇನಲ್ಲಾ! ನನ್ನ ಹೃದಯದಲ್ಲಿ ಅಲಗು ನೆಟ್ಟಂತಾಗಿದೆ.",
			"23": "<sup>23</sup> ಸಂಜೆಯ ನೆರಳಿನಂತೆ ಗತಿಸಿಹೋಗುತ್ತಿದ್ದೇನೆ; ಗಾಳಿಯು ಬಡಕೊಂಡುಹೋಗುವ ಮಿಡಿತೆಯಂತಿದ್ದೇನೆ.",
			"24": "<sup>24</sup> ಉಪವಾಸದಿಂದ ನನ್ನ ಮೊಣಕಾಲುಗಳು ಬಲಹೀನವಾದವು; ನನ್ನ ದೇಹವು ಎಣ್ಣೆಯಿಲ್ಲದೆ ಕ್ಷೀಣವಾಯಿತು.",
			"25": "<sup>25</sup> ನಾನು ಜನಹಾಸ್ಯಕ್ಕೆ ಗುರಿಯಾಗಿದ್ದೇನೆ; ನನ್ನನ್ನು ನೋಡುವವರು ತಲೆಯಾಡಿಸುತ್ತಾರೆ.",
			"26": "<sup>26</sup> ಯೆಹೋವನೇ, ನನ್ನ ದೇವರೇ, ಸಹಾಯಮಾಡು; ನಿನ್ನ ಕೃಪೆಗೆ ತಕ್ಕಂತೆ ರಕ್ಷಿಸು.",
			"27": "<sup>27</sup> ಆಗ ಅವರು ಇದು ನಿನ್ನ ಕೈಕೆಲಸವೆಂದೂ ಯೆಹೋವನಾದ ನೀನೇ ಮಾಡಿದ್ದೆಂದೂ ತಿಳಿದುಕೊಳ್ಳುವರು.",
			"28": "<sup>28</sup> ಅವರು ಶಪಿಸಲಿ, ಚಿಂತೆಯಿಲ್ಲ; ನೀನು ಆಶೀರ್ವದಿಸುವಿ. ಅವರು ಎದ್ದು ಅಪಮಾನಹೊಂದುವರು. ಆದರೆ ನಿನ್ನ ಸೇವಕನಾದ ನಾನು ಉಲ್ಲಾಸಿಸುವೆನು.",
			"29": "<sup>29</sup> ಮಾನಭಂಗವೇ ನನ್ನ ವಿರೋಧಿಗಳಿಗೆ ವಸ್ತ್ರವಾಗಲಿ; ನಾಚಿಕೆಯೇ ಅವರ ಹೊದಿಕೆಯಾಗಲಿ.",
			"30": "<sup>30</sup> ಯೆಹೋವನನ್ನು ಬಹಳವಾಗಿ ಕೊಂಡಾಡುವೆನು; ಜನಸಮೂಹದ ಮಧ್ಯದಲ್ಲಿ ಆತನನ್ನು ಕೀರ್ತಿಸುವೆನು.",
			"31": "<sup>31</sup> ಆತನು ದೀನನ ಬಲಗಡೆಯಲ್ಲಿ ನಿಂತುಕೊಂಡು ಪ್ರಾಣಶಿಕ್ಷೆ ವಿಧಿಸುವವರ ಕೈಯಿಂದ ತಪ್ಪಿಸಿ ರಕ್ಷಿಸುವನು."
		},
		"110": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ನನ್ನ ಒಡೆಯನಿಗೆ - ನಾನು ನಿನ್ನ ವಿರೋಧಿಗಳನ್ನು ನಿನಗೆ ಪಾದಪೀಠವಾಗ ಮಾಡುವ ತನಕ ನನ್ನ ಬಲಗಡೆಯಲ್ಲಿ ಕೂತುಕೊಂಡಿರು ಎಂದು ನುಡಿದನು.",
			"2": "<sup>2</sup> ಯೆಹೋವನು ನಿನ್ನ ರಾಜದಂಡದ ಆಳಿಕೆಯನ್ನು ಚೀಯೋನಿನ ಹೊರಗೂ ಹಬ್ಬಿಸುವನು; ನಿನ್ನ ವೈರಿಗಳ ಮಧ್ಯದಲ್ಲಿ ದೊರೆತನಮಾಡು.",
			"3": "<sup>3</sup> ನೀನು ಸೈನ್ಯವನ್ನು ಕೂಡಿಸುವ ದಿನದಲ್ಲಿ ನಿನ್ನ ಪ್ರಜೆಗಳು ಸಂತೋಷದಿಂದ ತಾವಾಗಿಯೇ ಸೇರಿಕೊಳ್ಳುವರು. ಪರಿಶುದ್ಧ ವಸ್ತ್ರಭೂಷಿತರಾದ ನಿನ್ನ ಯುವಕ ಸೈನಿಕರು ಉದಯಕಾಲದ ಇಬ್ಬನಿಯಂತಿರುವರು.",
			"4": "<sup>4</sup> ನೀನು ಸದಾಕಾಲವೂ ಮೆಲ್ಕಿಜೆದೇಕನ ತರಹದ ಯಾಜಕನು ಎಂದು ಯೆಹೋವನು ಆಣೆಯಿಟ್ಟು ನುಡಿದಿದ್ದಾನೆ; ಪಶ್ಚಾತ್ತಾಪಪಡುವದಿಲ್ಲ.",
			"5": "<sup>5</sup> ನಿನ್ನ ಬಲಗಡೆಯಲ್ಲಿರುವ ಕರ್ತನು ತನ್ನ ಕೋಪದ ದಿನದಲ್ಲಿ ರಾಜರನ್ನು ಖಂಡಿಸುವನು.",
			"6": "<sup>6</sup> ಆತನು ಜನಾಂಗಗಳಲ್ಲಿ ನ್ಯಾಯಸ್ಥಾಪಿಸುವಾಗ ವಿಸ್ತಾರವಾದ ರಣರಂಗದಲ್ಲಿ ಶತ್ರುಗಳ ಶಿರಸ್ಸನ್ನು ಛೇದಿಸಿ ಹೆಣಗಳಿಂದ ಅದನ್ನು ತುಂಬಿಸುವನು.",
			"7": "<sup>7</sup> [ಒಡೆಯನು] ದಾರಿಯಲ್ಲಿ ಹಳ್ಳದ ನೀರು ಕುಡಿದು ತಲೆಯೆತ್ತುವನು."
		},
		"111": {
			"1": "<sup>1</sup> ಯಾಹುವಿಗೆ ಸ್ತೋತ್ರ! ನಾನು ಯೆಹೋವನನ್ನು ಕೊಂಡಾಡುವೆನು; ಯಥಾರ್ಥರ ಕೂಟದಲ್ಲಿಯೂ ನೆರೆದ ಸಭೆಯಲ್ಲಿಯೂ ಮನಃಪೂರ್ವಕವಾಗಿ ಕೀರ್ತಿಸುವೆನು.",
			"2": "<sup>2</sup> ಯೆಹೋವನ ಕೃತ್ಯಗಳು ಮಹತ್ತಾದವುಗಳು; ಅವುಗಳಲ್ಲಿ ಸಂತೋಷಿಸುವವರು ಅವುಗಳನ್ನೇ ಧ್ಯಾನಿಸುವರು.",
			"3": "<sup>3</sup> ಆತನ ಕಾರ್ಯವು ಘನಮಾನಗಳುಳ್ಳದ್ದು; ಆತನ ನೀತಿಯು ಸದಾಕಾಲವೂ ಇರುವದು.",
			"4": "<sup>4</sup> ಆತನು ತನ್ನ ಅದ್ಭುತಕೃತ್ಯಗಳ ಜ್ಞಾಪಕವನ್ನು ಉಳಿಯಮಾಡಿದ್ದಾನೆ. ಯೆಹೋವನು ದಯೆಯೂ ಕನಿಕರವೂ ಉಳ್ಳವನು.",
			"5": "<sup>5</sup> ತನ್ನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಿಗೆ ಆಹಾರವನ್ನು ಕೊಟ್ಟಿದ್ದಾನೆ; ತನ್ನ ಒಡಂಬಡಿಕೆಯನ್ನು ಯಾವಾಗಲೂ ನೆನಪಿನಲ್ಲಿಡುವನು.",
			"6": "<sup>6</sup> ಆತನು ಅನ್ಯಜನಗಳ ಸ್ವಾಸ್ತ್ಯವನ್ನು ತನ್ನ ಪ್ರಜೆಗೆ ಕೊಡುವದರ ಮೂಲಕ ಸ್ವಪ್ರತಾಪವನ್ನು ತೋರ್ಪಡಿಸಿದ್ದಾನೆ.",
			"7": "<sup>7</sup> ಆತನ ಕೈಕೆಲಸಗಳು ನೀತಿಸತ್ಯತೆಗಳನ್ನು ಪ್ರಕಟಿಸುತ್ತವೆ; ಆತನ ನಿಯಮಗಳೆಲ್ಲಾ ಸ್ಥಿರವಾಗಿವೆ.",
			"8": "<sup>8</sup> ಅವು ದೃಢವಾದ ಆಧಾರವುಳ್ಳವು; ಯುಗಯುಗಾಂತರಕ್ಕೂ ಇರುವವು. ಸತ್ಯನೀತಿಗಳಿಗನುಸಾರವಾಗಿ ವಿಧಿಸಲ್ಪಟ್ಟಿವೆ.",
			"9": "<sup>9</sup> ಯೆಹೋವನು ತನ್ನ ಜನರಿಗೆ ವಿಮೋಚನೆಯನ್ನುಂಟುಮಾಡಿದ್ದಾನೆ; ತನ್ನ ಒಡಂಬಡಿಕೆಯನ್ನು ನಿತ್ಯಕ್ಕೂ ಸ್ಥಾಪಿಸಿದ್ದಾನೆ; ಆತನ ನಾಮವು ಪರಿಶುದ್ಧವೂ ಭಯಂಕರವೂ ಆಗಿದೆ.",
			"10": "<sup>10</sup> ಯೆಹೋವನ ಭಯವೇ ಜ್ಞಾನಕ್ಕೆ ಮೂಲವು; ಆತನ ಕಟ್ಟಳೆಗಳನ್ನು ನಡಿಸುವವರು ಪೂರ್ಣ ವಿವೇಕಿಗಳು. ಆತನ ಸ್ತುತಿಯು ನಿರಂತರವಾದದ್ದು."
		},
		"112": {
			"1": "<sup>1</sup> ಯಾಹುವಿಗೆ ಸ್ತೋತ್ರ! ಯಾವನು ಯೆಹೋವನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವನಾಗಿ ಆತನ ಆಜ್ಞೆಗಳಲ್ಲಿ ಅತ್ಯಾನಂದಪಡುವನೋ ಅವನೇ ಧನ್ಯನು.",
			"2": "<sup>2</sup> ಅವನ ಸಂತಾನವು ಲೋಕದಲ್ಲಿ ಬಲಿಷ್ಠವಾಗುವದು; ನೀತಿವಂತನ ವಂಶವು ಶುಭಹೊಂದುವದು.",
			"3": "<sup>3</sup> ಅವನ ಮನೆಯಲ್ಲಿ ಧನೈಶ್ವರ್ಯಗಳಿರುವವು; ಅವನ ನೀತಿಯು ಸದಾಕಾಲವೂ ಫಲಿಸುತ್ತಿರುವದು.",
			"4": "<sup>4</sup> ಯಥಾರ್ಥರಿಗೆ ಕತ್ತಲೆಯಲ್ಲಿಯೂ ಜ್ಯೋತಿ ಮೂಡುವದು; ದಯೆಯೂ ಕನಿಕರವೂ ನೀತಿಯೂ ಉಳ್ಳ ದೇವರೇ ಆ ಜ್ಯೋತಿ.",
			"5": "<sup>5</sup> ದಯಾಳುವಾಗಿ ಧನಸಹಾಯಮಾಡುವವನೂ ತನ್ನ ಕಾರ್ಯಗಳನ್ನು ನೀತಿಯಿಂದ ನಡಿಸುವವನೂ ಭಾಗ್ಯವಂತನು.",
			"6": "<sup>6</sup> ಅವನು ಎಂದೂ ಕದಲುವದಿಲ್ಲ; ನೀತಿವಂತನನ್ನು ಯಾವಾಗಲೂ ನೆನಸುವರು.",
			"7": "<sup>7</sup> ಅವನಿಗೆ ಕೆಟ್ಟ ಸುದ್ದಿಯ ಭಯವಿರುವದಿಲ್ಲ; ಯೆಹೋವನಲ್ಲಿ ಭರವಸವಿಟ್ಟಿರುವದರಿಂದ ಅವನ ಮನಸ್ಸು ಸ್ಥಿರವಾಗಿರುವದು.",
			"8": "<sup>8</sup> ದುಷ್ಟರಿಗಾಗುವ ಶಿಕ್ಷೆಯನ್ನು ಕಣ್ಣಾರೆ ಕಾಣುವೆನೆಂಬ ಭರವಸವಿರುವದರಿಂದ ಅವನ ಮನಸ್ಸು ದೃಢವಾಗಿದೆ, ಹೆದರುವದಿಲ್ಲ.",
			"9": "<sup>9</sup> ಬಡವರಿಗೆ ಉದಾರವಾಗಿ ಕೊಡುತ್ತಾನೆ; ಅವನ ನೀತಿಯ ಫಲವು ಸದಾಕಾಲವೂ ಇರುವದು. ಮಹಿಮೆಯೊಡನೆ ಅವನ ಕೊಂಬು ಎತ್ತಲ್ಪಡುವದು.",
			"10": "<sup>10</sup> ದುಷ್ಟನು ನೋಡಿ ವ್ಯಥೆಪಡುವನು; ಅವನು ಹಲ್ಲುಕಡಿಯುತ್ತಾ ಲಯಹೊಂದುವನು. ದುಷ್ಟರ ನಿರೀಕ್ಷೆಯು ಭಂಗವಾಗುವದು."
		},
		"113": {
			"1": "<sup>1</sup> ಯಾಹುವಿಗೆ ಸ್ತೋತ್ರ! ಯೆಹೋವನ ಸೇವಕರೇ, ಸ್ತೋತ್ರಮಾಡಿರಿ; ಯೆಹೋವನಾಮಸ್ತುತಿಮಾಡಿರಿ.",
			"2": "<sup>2</sup> ಇಂದಿನಿಂದ ಯುಗಯುಗಕ್ಕೂ ಯೆಹೋವನಾಮವು ಕೀರ್ತಿಸಲ್ಪಡಲಿ.",
			"3": "<sup>3</sup> ಯೆಹೋವನ ನಾಮವು ಮೂಡಣಿಂದ ಪಡುವಣವರೆಗೂ ಸ್ತುತಿಹೊಂದಲಿ.",
			"4": "<sup>4</sup> ಯೆಹೋವನು ಎಲ್ಲಾ ಜನಾಂಗಗಳಲ್ಲಿ ಮಹೋನ್ನತನು; ಆತನ ಪ್ರಭಾವವು ಮೇಲಣ ಲೋಕಗಳಲ್ಲಿ ಮೆರೆಯುತ್ತದೆ.",
			"5": "<sup>5</sup> ನಮ್ಮ ಯೆಹೋವದೇವರಿಗೆ ಸಮಾನರು ಯಾರು? ಆತನು ಉನ್ನತಲೋಕದಲ್ಲಿ ಆಸನಾರೂಢನಾಗಿ",
			"6": "<sup>6</sup> ಆಕಾಶವನ್ನೂ ಭೂಮಿಯನ್ನೂ ನೋಡಲಿಕ್ಕೆ ಬಾಗುತ್ತಾನೆ.",
			"7": "<sup>7</sup> ಆತನು ದೀನರನ್ನು ಧೂಳಿಯಿಂದ ಎಬ್ಬಿಸಿ ಬಡವರನ್ನು ತಿಪ್ಪೆಯಿಂದ ಎತ್ತುತ್ತಾನೆ.",
			"8": "<sup>8</sup> ಪ್ರಭುಗಳ ಜೊತೆಯಲ್ಲಿ ಅಂದರೆ ಅವರ ಜನಾಧಿಪತಿಗಳೊಡನೆ ಅವರನ್ನು ಕುಳ್ಳಿರಿಸುತ್ತಾನೆ.",
			"9": "<sup>9</sup> ಬಂಜೆಯು ಮಕ್ಕಳೊಂದಿಗಳಾಗಿ ಸಂತೋಷದಿಂದ ಮನೆಯಲ್ಲಿ ವಾಸಿಸುವ ಹಾಗೆ ಮಾಡುತ್ತಾನೆ. ಯಾಹುವಿಗೆ ಸ್ತೋತ್ರ!"
		},
		"114": {
			"1": "<sup>1</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಐಗುಪ್ತ ದೇಶವನ್ನೂ ಯಾಕೋಬನ ಮನೆತನದವರು ಅನ್ಯಭಾಷೆಯ ಜನಾಂಗವನ್ನೂ ಬಿಟ್ಟ ಮೇಲೆ",
			"2": "<sup>2</sup> ಯೆಹೂದವು ದೇವರ ಪರಿಶುದ್ಧವಾಸಸ್ಥಾನವೂ ಇಸ್ರಾಯೇಲು ಆತನ ರಾಜ್ಯವೂ ಆದವು.",
			"3": "<sup>3</sup> ಸಮುದ್ರವು ಕಂಡು ಓಡಿಹೋಯಿತು; ಯೊರ್ದನ್ ಹೊಳೆಯು ಹಿಂದಿರುಗಿತು.",
			"4": "<sup>4</sup> ಪರ್ವತಗಳು ಟಗರುಗಳಂತೆಯೂ ಗುಡ್ಡಗಳು ಕುರಿಮರಿಗಳಂತೆಯೂ ಹಾರಾಡಿದವು.",
			"5": "<sup>5</sup> ಸಮುದ್ರವೇ, ನಿನಗೇನಾಯಿತು? ಯಾಕೆ ಓಡಿಹೋಗುತ್ತೀ? ಯೊರ್ದನೇ, ಯಾಕೆ ಹಿಂದಿರುಗುತ್ತೀ?",
			"6": "<sup>6</sup> ಪರ್ವತಗಳೇ, ನೀವು ಟಗರುಗಳಂತೆಯೂ ಗುಡ್ಡಗಳೇ, ನೀವು ಕುರಿಮರಿಗಳಂತೆಯೂ ಯಾಕೆ ಹಾರಾಡುತ್ತೀರಿ?",
			"7": "<sup>7</sup> ಭೂಲೋಕವೇ, ಕರ್ತನು ಪ್ರತ್ಯಕ್ಷನಾಗಿದ್ದಾನೆ, ಯಾಕೋಬನ ದೇವರು ನಿನ್ನ ಮುಂದೆ ಇದ್ದಾನೆ, ಕಂಪಿಸು.",
			"8": "<sup>8</sup> ಆತನು ಬಂಡೆಯನ್ನು ಕೆರೆಯನ್ನಾಗಿಯೂ ಶಿಲೆಯನ್ನು ಬುಗ್ಗೆಯನ್ನಾಗಿಯೂ ಮಾರ್ಪಡಿಸುತ್ತಾನೆ."
		},
		"115": {
			"1": "<sup>1</sup> ನಮ್ಮನ್ನಲ್ಲ, ಯೆಹೋವನೇ, ನಮ್ಮನ್ನಲ್ಲ, ನಿನ್ನ ಪ್ರೀತಿಸತ್ಯತೆಗಳ ನಿಮಿತ್ತವಾಗಿ ನಿನ್ನ ಹೆಸರನ್ನೇ ಘನಪಡಿಸು.",
			"2": "<sup>2</sup> ಅವರ ದೇವರು ಎಲ್ಲಿದ್ದಾನೆಂದು ಅನ್ಯರು ಯಾಕೆ ಹೇಳಬೇಕು?",
			"3": "<sup>3</sup> ನಮ್ಮ ದೇವರು ಪರಲೋಕದಲ್ಲಿದ್ದಾನೆ; ಆತನು ತನಗೆ ಬೇಕಾದದ್ದನ್ನೆಲ್ಲಾ ಮಾಡುತ್ತಾನೆ.",
			"4": "<sup>4</sup> ಅವರ ವಿಗ್ರಹಗಳೋ ಬೆಳ್ಳಿಬಂಗಾರದವುಗಳೇ; ಅವು ಮನುಷ್ಯರ ಕೈಕೆಲಸವಷ್ಟೇ.",
			"5": "<sup>5</sup> ಅವು ಬಾಯಿದ್ದರೂ ಮಾತಾಡುವದಿಲ್ಲ; ಕಣ್ಣಿದ್ದರೂ ನೋಡುವದಿಲ್ಲ.",
			"6": "<sup>6</sup> ಕಿವಿಯಿದ್ದರೂ ಕೇಳುವದಿಲ್ಲ; ಮೂಗಿದ್ದರೂ ಮೂಸುವದಿಲ್ಲ.",
			"7": "<sup>7</sup> ಕೈಯುಂಟು, ಮುಟ್ಟುವದಿಲ್ಲ; ಕಾಲುಂಟು ನಡೆಯುವದಿಲ್ಲ; ಅವುಗಳ ಗಂಟಲಲ್ಲಿ ಶಬ್ದವೇ ಇಲ್ಲ.",
			"8": "<sup>8</sup> ಅವುಗಳನ್ನು ಮಾಡುವವರೂ ಅವುಗಳಲ್ಲಿ ಭರವಸವಿಡುವವರೂ ಅವುಗಳಂತೆಯೇ.",
			"9": "<sup>9</sup> ಇಸ್ರಾಯೇಲ್ಯರೇ, ಯೆಹೋವನಲ್ಲಿ ಭರವಸವಿಡಿರಿ. ಅವರ ಸಹಾಯಕನೂ ಗುರಾಣಿಯೂ ಆತನೇ.",
			"10": "<sup>10</sup> ಆರೋನನ ಮನೆತನದವರೇ, ಯೆಹೋವನಲ್ಲಿ ಭರವಸವಿಡಿರಿ. ಅವರ ಸಹಾಯಕನೂ ಗುರಾಣಿಯೂ ಆತನೇ.",
			"11": "<sup>11</sup> ಯೆಹೋವನ ಭಕ್ತರೇ, ಯೆಹೋವನಲ್ಲಿ ಭರವಸವಿಡಿರಿ. ಅವರ ಸಹಾಯಕನೂ ಗುರಾಣಿಯೂ ಆತನೇ.",
			"12": "<sup>12</sup> ಯೆಹೋವನು ನಮ್ಮನ್ನು ನೆನಪುಮಾಡಿಕೊಂಡಿದ್ದಾನೆ; ಸಣ್ಣವರು ಮೊದಲುಗೊಂಡು ದೊಡ್ಡವರ ಪರ್ಯಂತರ ಎಲ್ಲರನ್ನೂ ಆಶೀರ್ವದಿಸುವನು.",
			"13": "<sup>13</sup> ಇಸ್ರಾಯೇಲನ ಮನೆತನದವರನ್ನು ಆಶೀರ್ವದಿಸುವನು; ಆರೋನನ ಮನೆತನದವರನ್ನು ಆಶೀರ್ವದಿಸುವನು. ತನ್ನ ಭಕ್ತರನ್ನು ಆಶೀರ್ವದಿಸುವನು.",
			"14": "<sup>14</sup> ಯೆಹೋವನು ನಿಮ್ಮನ್ನೂ ನಿಮ್ಮ ಮಕ್ಕಳನ್ನೂ ಅಭಿವೃದ್ಧಿಪಡಿಸಲಿ;",
			"15": "<sup>15</sup> ಯೆಹೋವನಿಂದ ನಿಮಗೆ ಆಶೀರ್ವಾದವಾಗಲಿ. ಆತನು ಭೂಪರಲೋಕಗಳನ್ನು ಉಂಟುಮಾಡಿದ್ದಾನೆ.",
			"16": "<sup>16</sup> ಪರಲೋಕವು ಯೆಹೋವನದು; ಭೂಲೋಕವನ್ನು ನರಸಂತಾನಕ್ಕೆ ಕೊಟ್ಟಿದ್ದಾನೆ.",
			"17": "<sup>17</sup> ಸತ್ತವರು ಯಾಹುವನ್ನು ಸ್ತುತಿಸುವದಿಲ್ಲ; ಮೌನಲೋಕವನ್ನು ಸೇರಿದವರಲ್ಲಿ ಯಾರೂ ಆತನನ್ನು ಕೀರ್ತಿಸುವದಿಲ್ಲ.",
			"18": "<sup>18</sup> ನಾವೋ ಇಂದಿನಿಂದ ಸದಾಕಾಲವೂ ಯಾಹುವನ್ನು ಕೊಂಡಾಡುವೆವು. ಯಾಹುವಿಗೆ ಸ್ತೋತ್ರ!"
		},
		"116": {
			"1": "<sup>1</sup> ಯೆಹೋವನನ್ನು ಪ್ರೀತಿಸುತ್ತೇನೆ; ಆತನು ನನ್ನ ಮೊರೆಯನ್ನು ಕೇಳುವವನು.",
			"2": "<sup>2</sup> ಆತನು ನನ್ನ ವಿಜ್ಞಾಪನೆಗೆ ಕಿವಿಗೊಟ್ಟಿದ್ದಾನೆ; ಜೀವದಿಂದಿರುವವರೆಗೂ ಆತನನ್ನೇ ಪ್ರಾರ್ಥಿಸುವೆನು.",
			"3": "<sup>3</sup> ಮರಣಪಾಶಗಳು ನನ್ನನ್ನು ಸುತ್ತಿಕೊಂಡಿದ್ದವು; ಪಾತಾಳವೇದನೆಗಳು ನನ್ನನ್ನು ಹಿಡಿದಿದ್ದವು. ಚಿಂತೆಯಲ್ಲಿಯೂ ಇಕ್ಕಟ್ಟಿನಲ್ಲಿಯೂ ಬಿದ್ದುಹೋಗಿದ್ದೆನು.",
			"4": "<sup>4</sup> ಆಗ ಯೆಹೋವನ ಹೆಸರು ಹೇಳಿ - ಯೆಹೋವನೇ, ಕೃಪೆಮಾಡಿ ನನ್ನ ಪ್ರಾಣವನ್ನು ರಕ್ಷಿಸು ಎಂದು ಪ್ರಾರ್ಥಿಸಿದೆನು.",
			"5": "<sup>5</sup> ಯೆಹೋವನು ಕೃಪಾಳುವೂ ನೀತಿವಂತನೂ ಆಗಿದ್ದಾನೆ; ನಮ್ಮ ದೇವರು ಕನಿಕರವುಳ್ಳವನು.",
			"6": "<sup>6</sup> ಯೆಹೋವನು ಸಾಧುಜನರನ್ನು ಕಾಪಾಡುವವನು; ಕುಗ್ಗಿದವನಾದ ನನ್ನನ್ನು ರಕ್ಷಿಸಿದನು.",
			"7": "<sup>7</sup> ನನ್ನ ಮನವೇ, ನಿನ್ನ ವಿಶ್ರಾಂತಿಯ ನೆಲೆಗೆ ತಿರುಗು. ಯೆಹೋವನು ನಿನಗೆ ಮಹೋಪಕಾರಗಳನ್ನು ಮಾಡಿದ್ದಾನಲ್ಲಾ.",
			"8": "<sup>8</sup> ಯೆಹೋವನೇ, ನಾನು ಜೀವಲೋಕದಲ್ಲಿದ್ದು ನಿನಗೆ ನಡೆದುಕೊಳ್ಳಬೇಕೆಂದು",
			"9": "<sup>9</sup> ನೀನು ನನ್ನ ಪ್ರಾಣವನ್ನು ಮರಣಕ್ಕೆ ತಪ್ಪಿಸಿ ಕಣ್ಣೀರನ್ನು ನಿಲ್ಲಿಸಿ ನನ್ನ ಪಾದಗಳನ್ನು ಎಡವದಂತೆ ಕಾದಿದ್ದೀ.",
			"10": "<sup>10</sup> ನಾನು ಬಹಳವಾಗಿ ಕುಗ್ಗಿಹೋದೆನೆಂದು ಹೇಳಿದಾಗಲೂ ಮನುಷ್ಯರೆಲ್ಲಾ ಟೊಳ್ಳೇ ಎಂದು ಭ್ರಾಂತಿಯಿಂದ ಉಸಿರಿದಾಗಲೂ",
			"11": "<sup>11</sup> ಭರವಸವುಳ್ಳವನಾಗಿಯೇ ಇದ್ದೆನು.",
			"12": "<sup>12</sup> ಯೆಹೋವನ ಮಹೋಪಕಾರಗಳಿಗೆ ಬದಲೇನು ಮಾಡಲಿ?",
			"13": "<sup>13</sup> ರಕ್ಷಣಾಪಾತ್ರೆಯನ್ನು ತೆಗೆದುಕೊಂಡು ಯೆಹೋವನಾಮವನ್ನು ಪ್ರಖ್ಯಾತಿಪಡಿಸುವೆನು.",
			"14": "<sup>14</sup> ಯೆಹೋವನಿಗೆ ಹೊತ್ತ ಹರಕೆಗಳನ್ನು ಆತನ ಎಲ್ಲಾ ಜನರ ಮುಂದೆಯೇ ಸಲ್ಲಿಸುವೆನು.",
			"15": "<sup>15</sup> ಯೆಹೋವನು ತನ್ನ ಭಕ್ತರ ಮರಣವನ್ನು ಅಲ್ಪವೆಂದು ಎಣಿಸುವದಿಲ್ಲ.",
			"16": "<sup>16</sup> ಯೆಹೋವನೇ, ಕರುಣಿಸು, ನಾನು ನಿನ್ನ ಕಿಂಕರನು; ನಿನ್ನ ದಾಸಿಯ ಮಗನೂ ನಿನ್ನ ಸೇವಕನೂ ಆಗಿದ್ದೇನೆ. ನನ್ನ ಬಂಧನಗಳನ್ನು ಬಿಚ್ಚಿಬಿಟ್ಟಿದ್ದೀ.",
			"17": "<sup>17</sup> ನಾನು ನಿನಗೆ ಕೃತಜ್ಞತಾಯಜ್ಞಗಳನ್ನು ಸಮರ್ಪಿಸುವೆನು; ಯೆಹೋವನಾಮವನ್ನು ಪ್ರಖ್ಯಾತಿಪಡಿಸುವೆನು.",
			"18": "<sup>18</sup> ಯೆರೂಸಲೇಮೇ, ನಿನ್ನ ಮಧ್ಯದಲ್ಲಿ, ಯೆಹೋವನ ಮಂದಿರದ ಅಂಗಳಗಳಲ್ಲಿ",
			"19": "<sup>19</sup> ಆತನಿಗೆ ಹೊತ್ತ ಹರಕೆಗಳನ್ನು ಆತನ ಎಲ್ಲಾ ಜನರ ಮುಂದೆಯೇ ಸಲ್ಲಿಸುವೆನು. ಯಾಹುವಿಗೆ ಸ್ತೋತ್ರ!"
		},
		"117": {
			"1": "<sup>1</sup> ಸರ್ವಜನಾಂಗಗಳೇ, ಯೆಹೋವನನ್ನು ಕೀರ್ತಿಸಿರಿ; ಸಮಸ್ತ ಪ್ರಜೆಗಳೇ, ಆತನನ್ನು ಕೊಂಡಾಡಿರಿ.",
			"2": "<sup>2</sup> ಆತನ ಕೃಪೆಯು ನಮ್ಮ ಮೇಲೆ ಅಪಾರವಾಗಿದೆ; ಯೆಹೋವನ ಸತ್ಯತೆಯು ಸದಾ ಇರುವದು. ಯಾಹುವಿಗೆ ಸ್ತೋತ್ರ!"
		},
		"118": {
			"1": "<sup>1</sup> ಯೆಹೋವನಿಗೆ ಕೃತಜ್ಞತಾಸ್ತುತಿಮಾಡಿರಿ; ಆತನು ಒಳ್ಳೆಯವನು. ಆತನ ಕೃಪೆಯು ಶಾಶ್ವತವು.",
			"2": "<sup>2</sup> ಇಸ್ರಾಯೇಲ್ಯರು - ಆತನ ಕೃಪೆಯು ಶಾಶ್ವತವೆಂದು ಹೇಳಲಿ.",
			"3": "<sup>3</sup> ಆರೋನನ ಮನೆತನದವರು - ಆತನ ಕೃಪೆಯು ಶಾಶ್ವತವೆಂದು ಹೇಳಲಿ.",
			"4": "<sup>4</sup> ಯೆಹೋವನ ಭಕ್ತರು - ಆತನ ಕೃಪೆಯು ಶಾಶ್ವತವೆಂದು ಹೇಳಲಿ.",
			"5": "<sup>5</sup> ಇಕ್ಕಟ್ಟಿನಲ್ಲಿ ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಟ್ಟೆನು; ಆತನು ಸದುತ್ತರವನ್ನು ದಯಪಾಲಿಸಿ ನನ್ನನ್ನು ವಿಶಾಲಸ್ಥಳದಲ್ಲಿ ಇಟ್ಟನು.",
			"6": "<sup>6</sup> ಯೆಹೋವನು ನನಗಿದ್ದಾನೆ; ಭಯಪಡೆನು; ಮನುಷ್ಯನು ನನಗೆ ಏನು ಮಾಡಾನು?",
			"7": "<sup>7</sup> ಯೆಹೋವನು ನನಗೆ ಇದ್ದಾನೆ; ಆತನೇ ನನಗೆ ಸಹಾಯಕನು; ನನ್ನ ವೈರಿಗಳಿಗಾಗುವ ಶಿಕ್ಷೆಯನ್ನು ನೋಡುವೆನು.",
			"8": "<sup>8</sup> ಮನುಷ್ಯರಲ್ಲಿ ಭರವಸವಿಡುವದಕ್ಕಿಂತ ಯೆಹೋವನನ್ನು ಆಶ್ರಯಿಸುವದು ಒಳ್ಳೇದು.",
			"9": "<sup>9</sup> ಪ್ರಭುಗಳಲ್ಲಿ ಭರವಸವಿಡುವದಕ್ಕಿಂತ ಯೆಹೋವನನ್ನು ಆಶ್ರಯಿಸುವದು ಒಳ್ಳೇದು.",
			"10": "<sup>10</sup> ಜನಾಂಗದವರೆಲ್ಲಾ ನನ್ನನ್ನು ಸುತ್ತಿಕೊಂಡರು; ಯೆಹೋವನ ಹೆಸರಿನಿಂದ ಅವರನ್ನು ಸಂಹರಿಸುವೆನು.",
			"11": "<sup>11</sup> ಅವರು ಸುತ್ತಲೂ ನನ್ನನ್ನು ಮುತ್ತಿದರು; ಯೆಹೋವನ ಹೆಸರಿನಿಂದ ಅವರನ್ನು ಸಂಹರಿಸುವೆನು.",
			"12": "<sup>12</sup> ಅವರು ಜೇನ್ನೊಣಗಳಂತೆ ನನ್ನನ್ನು ಕವಿದರು; ಮುಳ್ಳಿನ ಬೆಂಕಿಯಂತೆ ಕ್ಷಣದಲ್ಲಿ ಅಳಿದುಹೋಗುವರು. ಯೆಹೋವನ ಹೆಸರಿನಿಂದ ಅವರನ್ನು ಸಂಹಸರಿಸುವೆನು.",
			"13": "<sup>13</sup> [ವೈರಿಯೇ,] ನನ್ನನ್ನು ಬೀಳ ನೂಕಿದಿ; ಆದರೆ ಯೆಹೋವನು ನನಗೆ ಸಹಾಯ ಮಾಡಿದನು.",
			"14": "<sup>14</sup> ನನ್ನ ಬಲವೂ ಕೀರ್ತನೆಯೂ ಯಾಹುವೇ; ಆತನಿಂದ ನನಗೆ ರಕ್ಷಣೆಯುಂಟಾಯಿತು.",
			"15": "<sup>15</sup> ಉತ್ಸಾಹಧ್ವನಿಯೂ ಜಯಘೋಷವೂ ನೀತಿವಂತರ ಗುಡಾರಗಳಲ್ಲಿವೆ; ಯೆಹೋವನ ಬಲಗೈ ಪರಾಕ್ರಮವನ್ನು ನಡಿಸುತ್ತದೆ.",
			"16": "<sup>16</sup> ಯೆಹೋವನ ಬಲಗೈ ಉನ್ನತವಾಗಿದೆ; ಯೆಹೋವನ ಬಲಗೈ ಪರಾಕ್ರಮವನ್ನು ನಡಿಸುತ್ತದೆ.",
			"17": "<sup>17</sup> ನಾನು ಸಾಯುವದಿಲ್ಲ; ಜೀವದಿಂದಿದ್ದು ಯಾಹುವಿನ ಕ್ರಿಯೆಗಳನ್ನು ಸಾರುವೆನು.",
			"18": "<sup>18</sup> ಯಾಹು ನನ್ನನ್ನು ಕಠಿಣವಾಗಿ ಶಿಕ್ಷಿಸಿದನು; ಮರಣಕ್ಕೆ ಒಪ್ಪಿಸಲಿಲ್ಲ.",
			"19": "<sup>19</sup> ನೀತಿದ್ವಾರಗಳನ್ನು ತೆರೆಯಿರಿ; ಒಳಗೆ ಪ್ರವೇಶಿಸಿ ಯಾಹುವನ್ನು ಕೊಂಡಾಡುವೆನು.",
			"20": "<sup>20</sup> ಇದು ಯೆಹೋವನ ಮಂದಿರದ್ವಾರವು; ಇದರೊಳಗೆ ಪ್ರವೇಶಿಸತಕ್ಕವರು ನೀತಿವಂತರೇ.",
			"21": "<sup>21</sup> ನನಗೆ ಸದುತ್ತರವನ್ನು ದಯಪಾಲಿಸಿ ರಕ್ಷಿಸಿದಾತನೇ, ನಿನ್ನನ್ನು ಕೊಂಡಾಡುತ್ತೇನೆ.",
			"22": "<sup>22</sup> ಮನೆಕಟ್ಟುವವರು ಬೇಡವೆಂದು ಬಿಟ್ಟ ಕಲ್ಲೇ ಮುಖ್ಯವಾದ ಮೂಲೆಗಲ್ಲಾಯಿತು;",
			"23": "<sup>23</sup> ಇದು ಯೆಹೋವನಿಂದಲೇ ಆಯಿತು; ನಮಗೆ ಆಶ್ಚರ್ಯವಾಗಿ ತೋರುತ್ತದೆ.",
			"24": "<sup>24</sup> ಈ ದಿನವು ಯೆಹೋವನಿಂದಲೇ ನೇಮಕವಾದದ್ದು; ಇದರಲ್ಲಿ ನಾವು ಉಲ್ಲಾಸದಿಂದ ಆನಂದಿಸೋಣ.",
			"25": "<sup>25</sup> ಯೆಹೋವನೇ, ದಯವಿಟ್ಟು ರಕ್ಷಿಸು; ಯೆಹೋವನೇ, ದಯವಿಟ್ಟು ಸಾಫಲ್ಯಕೊಡು.",
			"26": "<sup>26</sup> ಯೆಹೋವನ ಹೆಸರಿನಲ್ಲಿ ಒಳಗೆ ಬರುವವನಿಗೆ ಆಶೀರ್ವಾದ; ಯೆಹೋವನ ಮಂದಿರದಲ್ಲಿರುವ ನಾವು ನಿಮ್ಮನ್ನು ಆಶೀರ್ವದಿಸುತ್ತೇವೆ.",
			"27": "<sup>27</sup> ಯೆಹೋವನೇ ದೇವರು; ಆತನು ನಮಗೆ ಪ್ರಕಾಶವನ್ನು ಅನುಗ್ರಹಿಸಿದ್ದಾನೆ. ರೆಂಬೆಗಳನ್ನು ಹಿಡಿದುಕೊಂಡು ಮೆರವಣಿಗೆಯಾಗಿ ಯಜ್ಞವೇದಿಯ ಕೊಂಬುಗಳ ಸಮೀಪಕ್ಕೆ ಬನ್ನಿರಿ.",
			"28": "<sup>28</sup> ನೀನು ನನ್ನ ದೇವರು; ನಿನ್ನನ್ನು ಕೊಂಡಾಡುತ್ತೇನೆ; ನನ್ನ ದೇವರೇ, ನಿನ್ನನ್ನು ಘನಪಡಿಸುತ್ತೇನೆ.",
			"29": "<sup>29</sup> ಯೆಹೋವನಿಗೆ ಕೃತಜ್ಞತಾಸ್ತುತಿಮಾಡಿರಿ; ಆತನು ಒಳ್ಳೆಯವನು. ಆತನ ಕೃಪೆಯು ಶಾಶ್ವತವಾದದ್ದು."
		},
		"119": {
			"1": "<sup>1</sup> ಯೆಹೋವನ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಅನುಸರಿಸಿ ಸದಾಚಾರಿಗಳಾಗಿ ನಡೆಯುವವರು ಧನ್ಯರು.",
			"2": "<sup>2</sup> ಆತನ ಕಟ್ಟಳೆಗಳನ್ನು ಕೈಕೊಂಡು ಸಂಪೂರ್ಣಮನಸ್ಸಿನಿಂದ ಆತನನ್ನು ಹುಡುಕುವವರು ಭಾಗ್ಯವಂತರು.",
			"3": "<sup>3</sup> ಅವರು ಆತನ ಮಾರ್ಗದಲ್ಲಿ ನಡೆಯುತ್ತಾರೆ; ಅನ್ಯಾಯಮಾಡುವದೇ ಇಲ್ಲ.",
			"4": "<sup>4</sup> ನಿನ್ನ ನೇಮಗಳನ್ನು ಜಾಗರೂಕತೆಯಿಂದ ಕೈಕೊಂಡು ನಡಿಸಬೇಕೆಂದು ನೀನೇ ಆಜ್ಞಾಪಿಸಿರುತ್ತೀ.",
			"5": "<sup>5</sup> ನಿನ್ನ ನಿಬಂಧನೆಗಳನ್ನು ಕೈಕೊಳ್ಳುವದರಲ್ಲಿ ನಾನು ಸ್ಥಿರಮನಸ್ಸುಳ್ಳವನಾಗಿದ್ದರೆ ಒಳ್ಳೇದು!",
			"6": "<sup>6</sup> ಹೀಗೆ ನಿನ್ನ ಆಜ್ಞೆಗಳನ್ನೆಲ್ಲಾ ಲಕ್ಷಿಸುವವನಾದರೆ ಆಶಾಭಂಗಪಡೆನು.",
			"7": "<sup>7</sup> ನಾನು ನಿನ್ನ ನೀತಿವಿಧಿಗಳನ್ನು ಕಲಿತಹಾಗೆಲ್ಲಾ ನಿನ್ನನ್ನು ಯಥಾರ್ಥಹೃದಯದಿಂದ ಕೊಂಡಾಡುತ್ತಾ ಹೋಗುವೆನು.",
			"8": "<sup>8</sup> ನಿನ್ನ ನಿಬಂಧನೆಗಳನ್ನು ಅನುಸರಿಸುವ ನನ್ನನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ಕೈಬಿಡಬೇಡ.",
			"9": "<sup>9</sup> ಯೌವನಸ್ಥನು ತನ್ನ ನಡತೆಯನ್ನು ಶುದ್ಧಪಡಿಸಿಕೊಳ್ಳುವದು ಯಾವದರಿಂದ? ನಿನ್ನ ವಾಕ್ಯವನ್ನು ಗಮನಿಸಿ ನಡೆಯುವದರಿಂದಲೇ.",
			"10": "<sup>10</sup> ನಾನು ಪೂರ್ಣಮನಸ್ಸಿನಿಂದ ನಿನ್ನನ್ನು ಹುಡುಕುತ್ತೇನೆ; ನಿನ್ನ ಆಜ್ಞೆಗಳಿಗೆ ತಪ್ಪಿಹೋಗದಂತೆ ನನ್ನನ್ನು ಕಾಯಿ.",
			"11": "<sup>11</sup> ನಿನಗೆ ವಿರೋಧವಾಗಿ ಪಾಪಮಾಡದಂತೆ ನಿನ್ನ ನುಡಿಗಳನ್ನು ನನ್ನ ಹೃದಯದಲ್ಲಿಟ್ಟುಕೊಂಡಿದ್ದೇನೆ.",
			"12": "<sup>12</sup> ಸ್ತುತಿಪಾತ್ರನಾದ ಯೆಹೋವನೇ, ನಿನ್ನ ನಿಬಂಧನೆಗಳನ್ನು ನನಗೆ ಕಲಿಸು.",
			"13": "<sup>13</sup> ನನ್ನ ತುಟಿಗಳು ನಿನ್ನ ವಿಧಿವಚನಗಳನ್ನೆಲ್ಲಾ ವರ್ಣಿಸುತ್ತವೆ.",
			"14": "<sup>14</sup> ಸರ್ವಸಂಪತ್ತಿನಲ್ಲಿ ಹೇಗೋ ಹಾಗೆಯೇ ನಿನ್ನ ಕಟ್ಟಳೆಯ ಮಾರ್ಗದಲ್ಲಿ ಆನಂದಿಸುತ್ತೇನೆ.",
			"15": "<sup>15</sup> ನಿನ್ನ ನೇಮಗಳನ್ನು ಧ್ಯಾನಿಸುತ್ತಾ ನಿನ್ನ ದಾರಿಯನ್ನು ಲಕ್ಷೀಕರಿಸಿಕೊಳ್ಳುವೆನು.",
			"16": "<sup>16</sup> ನಿನ್ನ ನಿಬಂಧನೆಗಳಲ್ಲಿ ಉಲ್ಲಾಸಪಡುವೆನು; ನಿನ್ನ ವಾಕ್ಯವನ್ನು ಮರೆಯುವದಿಲ್ಲ.",
			"17": "<sup>17</sup> ನಿನ್ನ ಸೇವಕನಾದ ನನ್ನ ಮೇಲೆ ದಯವಿಡು; ಆಗ ಜೀವದಿಂದಿದ್ದು ನಿನ್ನ ವಾಕ್ಯವನ್ನು ಕೈಕೊಳ್ಳುವೆನು.",
			"18": "<sup>18</sup> ನನ್ನ ಕಣ್ಣು ತೆರೆ; ಆಗ ನಿನ್ನ ಧರ್ಮಶಾಸ್ತ್ರದ ಅದ್ಭುತಗಳನ್ನು ಕಂಡುಕೊಳ್ಳುವೆನು.",
			"19": "<sup>19</sup> ನಾನು ಭೂಲೋಕದಲ್ಲಿ ಪ್ರವಾಸಿಯಾಗಿದ್ದೇನೆ; ನಿನ್ನ ಆಜ್ಞೆಗಳನ್ನು ನನಗೆ ಮರೆಮಾಡಬೇಡ.",
			"20": "<sup>20</sup> ಯಾವಾಗಲೂ ನಿನ್ನ ವಿಧಿಗಳನ್ನೇ ಹಂಬಲಿಸುತ್ತಿರುವ ನನ್ನ ಪ್ರಾಣವು ಜಜ್ಜಿಹೋಗಿದೆ.",
			"21": "<sup>21</sup> ನೀನು ಗರ್ವಿಷ್ಠರನ್ನು ಗದರಿಸುತ್ತೀ; ನಿನ್ನ ಆಜ್ಞೆಗಳನ್ನು ಮೀರಿದವರು ಶಾಪಗ್ರಸ್ತರು.",
			"22": "<sup>22</sup> ನಿನ್ನ ಕಟ್ಟಳೆಗಳನ್ನು ಕೈಕೊಂಡವನಾದ ನನಗಿರುವ ನಿಂದಾಪಮಾನಗಳನ್ನು ತೊಲಗಿಸು.",
			"23": "<sup>23</sup> ಪ್ರಭುಗಳು ಕೂತುಕೊಂಡು ನನಗೆ ವಿರೋಧವಾಗಿ ಒಳಸಂಚುಮಾಡಿದರೂ ನಿನ್ನ ಸೇವಕನು ನಿನ್ನ ನಿಬಂಧನೆಗಳನ್ನೇ ಧ್ಯಾನಿಸುತ್ತಿರುವನು.",
			"24": "<sup>24</sup> ನಿನ್ನ ಕಟ್ಟಳೆಗಳು ನನ್ನ ಆನಂದವು, ಅವೇ ನನ್ನ ಮಂತ್ರಾಲೋಚಕರು.",
			"25": "<sup>25</sup> ನನ್ನ ಪ್ರಾಣವು ಧೂಳಿನಲ್ಲಿ ಸೇರಿಹೋಗುತ್ತದೆ; ನಿನ್ನ ವಾಗ್ದಾನಕ್ಕನುಸಾರವಾಗಿ ನನ್ನನ್ನು ಉಜ್ಜೀವಿಸಮಾಡು.",
			"26": "<sup>26</sup> ನನ್ನ ಯಾತ್ರಾನುಭವವನ್ನು ನಿನಗೆ ಹೇಳಿಕೊಂಡಾಗ ನನಗೆ ಸದುತ್ತರವನ್ನು ದಯಪಾಲಿಸಿದಿ; ನಿನ್ನ ನಿಬಂಧನೆಗಳನ್ನು ನನಗೆ ಉಪದೇಶಿಸು.",
			"27": "<sup>27</sup> ನಿನ್ನ ನೇಮಗಳ ದಾರಿಯನ್ನು ತಿಳಿಯಪಡಿಸು; ಆಗ ನಿನ್ನ ಅದ್ಭುತಕೃತ್ಯಗಳನ್ನು ಧ್ಯಾನಿಸುವೆನು.",
			"28": "<sup>28</sup> ಮನೋವ್ಯಥೆಯಿಂದ ಕಣ್ಣೀರುಸುರಿಸುತ್ತೇನೆ; ನಿನ್ನ ವಾಗ್ದಾನಕ್ಕನುಸಾರವಾಗಿ ನನ್ನನ್ನು ಬಲಪಡಿಸು.",
			"29": "<sup>29</sup> ಮಿಥ್ಯಾಮಾರ್ಗವನ್ನು ನನಗೆ ದೂರಮಾಡು; ನಿನ್ನ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ನನಗೆ ಅನುಗ್ರಹಿಸು.",
			"30": "<sup>30</sup> ಭಕ್ತಿಮಾರ್ಗವನ್ನು ಆರಿಸಿಕೊಂಡಿದ್ದೇನೆ; ನಿನ್ನ ವಿಧಿಗಳನ್ನು ಮುಂದೆಯೇ ಇಟ್ಟುಕೊಂಡಿದ್ದೇನೆ.",
			"31": "<sup>31</sup> ನಾನು ನಿನ್ನ ಕಟ್ಟಳೆಗಳನ್ನು ಅಪ್ಪಿಕೊಂಡಿದ್ದೇನೆ; ಯೆಹೋವನೇ, ನನಗೆ ಆಶಾಭಂಗಪಡಿಸಬೇಡ.",
			"32": "<sup>32</sup> ನೀನು ನನ್ನ ಅಂತರಾತ್ಮವನ್ನು ಬಿಡುಗಡೆಮಾಡು; ಆಗ ಆಸಕ್ತಿಯಿಂದ ನಿನ್ನ ಆಜ್ಞಾಮಾರ್ಗವನ್ನು ಅನುಸರಿಸುವೆನು.",
			"33": "<sup>33</sup> ಯೆಹೋವನೇ, ನಿನ್ನ ನಿಬಂಧನೆಗಳ ಮಾರ್ಗವನ್ನು ಉಪದೇಶಿಸು; ಕಡೇತನಕ ಅದರಲ್ಲಿಯೇ ನಡೆಯುವೆನು.",
			"34": "<sup>34</sup> ನನಗೆ ಜ್ಞಾವನ್ನು ದಯಪಾಲಿಸು; ಆಗ ನಿನ್ನ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಪೂರ್ಣಮನಸ್ಸಿನಿಂದ ಕೈಕೊಂಡು ನಡೆಯುವೆನು.",
			"35": "<sup>35</sup> ನಿನ್ನ ಆಜ್ಞಾಮಾರ್ಗದಲ್ಲಿ ನನ್ನನ್ನು ನಡಿಸು; ಅದೇ ನನ್ನ ಇಷ್ಟ.",
			"36": "<sup>36</sup> ನನ್ನ ಮನಸ್ಸನ್ನು ದ್ರವ್ಯಾಶೆಗಲ್ಲ, ನಿನ್ನ ಕಟ್ಟಳೆಗಳಿಗೆ ತಿರುಗಿಸು.",
			"37": "<sup>37</sup> ವ್ಯರ್ಥಕಾರ್ಯಗಳಲ್ಲಿ ದೃಷ್ಟಿಯಿಡದಂತೆ ನನ್ನನ್ನು ಕಾಪಾಡು; ನಿನ್ನ ಮಾರ್ಗದಲ್ಲಿ ನಡೆಯುವಂತೆ ನನ್ನನ್ನು ಚೈತನ್ಯಗೊಳಿಸು.",
			"38": "<sup>38</sup> ನೀನು ನಿನ್ನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಿಗೋಸ್ಕರ ಹೇಳಿದ ನುಡಿಯನ್ನು ನಿನ್ನ ಸೇವಕನ ವಿಷಯದಲ್ಲಿ ನೆರವೇರಿಸು.",
			"39": "<sup>39</sup> ನನ್ನ ಅವಮಾನವನ್ನು ತೊಲಗಿಸು; ಅದಕ್ಕೋಸ್ಕರ ಅಂಜುತ್ತಿದ್ದೇನೆ. ನಿನ್ನ ವಿಧಿಗಳು ಹಿತಕರವಾಗಿವೆ.",
			"40": "<sup>40</sup> ಇಗೋ, ನಿನ್ನ ನೇಮಗಳನ್ನು ಪ್ರೀತಿಸುತ್ತೇನೆ; ನಿನ್ನ ನೀತಿಗನುಸಾರವಾಗಿ ನನ್ನನ್ನು ಚೈತನ್ಯಗೊಳಿಸು.",
			"41": "<sup>41</sup> ಯೆಹೋವನೇ, ನಿನ್ನ ಕೃಪೆಯು ನನಗೆ ದೊರಕಲಿ; ನಿನ್ನ ನುಡಿಗನುಸಾರವಾಗಿ ರಕ್ಷಣೆಯುಂಟಾಗಲಿ.",
			"42": "<sup>42</sup> ಆಗ ನನ್ನನ್ನು ನಿಂದಿಸುವವನಿಗೆ ಉತ್ತರಕೊಡುವೆನು; ನಾನು ನಿನ್ನ ವಾಕ್ಯದಲ್ಲಿ ಭರವಸವಿಟ್ಟಿದ್ದೇನಲ್ಲಾ.",
			"43": "<sup>43</sup> ಸತ್ಯವಾಕ್ಯವನ್ನು ನನ್ನ ಬಾಯಿಂದ ತೆಗೆಯಬೇಡ. ನಿನ್ನ ನ್ಯಾಯವಿಧಿಗಳನ್ನು ನಿರೀಕ್ಷಿಸಿಕೊಂಡಿದ್ದೇನೆ.",
			"44": "<sup>44</sup> ನಿನ್ನ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಸದಾ ತಪ್ಪದೆ ಕೈಕೊಳ್ಳುವೆನು.",
			"45": "<sup>45</sup> ನಾನು ನಿನ್ನ ನಿಯಮಗಳನ್ನು ಅಭ್ಯಾಸಿಸುವವನಾದ್ದರಿಂದ ಆಟಂಕವಿಲ್ಲದೆ ನಡೆಯುವೆನು.",
			"46": "<sup>46</sup> ಅರಸುಗಳ ಮುಂದೆಯೂ ನಿನ್ನ ಕಟ್ಟಳೆಗಳ ವಿಷಯದಲ್ಲಿ ಮಾತಾಡುವೆನು; ನಾಚಿಕೆಪಡುವದಿಲ್ಲ.",
			"47": "<sup>47</sup> ನಿನ್ನ ಆಜ್ಞೆಗಳಲ್ಲಿ ಆನಂದಪಡುತ್ತೇನೆ; ಅವು ನನಗೆ ಇಷ್ಟವಾಗಿವೆ.",
			"48": "<sup>48</sup> ನಿನ್ನ ಆಜ್ಞೆಗಳಿಗಾಗಿಯೇ ಕೈಯೊಡ್ಡುತ್ತೇನೆ, ಅವು ನನಗೆ ಇಷ್ಟವಾಗಿವೆ; ನಿನ್ನ ನಿಬಂಧನೆಗಳನ್ನು ಧ್ಯಾನಿಸುವೆನು.",
			"49": "<sup>49</sup> ನಿನ್ನ ವಾಗ್ದಾನವನ್ನು ನಿನ್ನ ಸೇವಕನಿಗೋಸ್ಕರ ನೆನಪುಮಾಡಿಕೋ; ನನ್ನಲ್ಲಿ ನಿರೀಕ್ಷೆಯನ್ನು ಹುಟ್ಟಿಸಿದ್ದೀಯಲ್ಲಾ!",
			"50": "<sup>50</sup> ನಿನ್ನ ನುಡಿಯು ನನ್ನನ್ನು ಚೈತನ್ಯಗೊಳಿಸುತ್ತದೆ; ಆಪತ್ಕಾಲದಲ್ಲಿ ಇದೇ ನನಗೆ ಆದರಣೆ.",
			"51": "<sup>51</sup> ಗರ್ವಿಷ್ಠರು ನನ್ನನ್ನು ಬಹಳವಾಗಿ ಹಾಸ್ಯಮಾಡಿದರು; ಆದರೂ ನಾನು ನಿನ್ನ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಬಿಡಲಿಲ್ಲ.",
			"52": "<sup>52</sup> ಯೆಹೋವನೇ, ನಿನ್ನ ಪುರಾತನ ವಿಧಿಗಳನ್ನು ನೆನಪುಮಾಡಿಕೊಂಡು ನನ್ನನ್ನು ಸಂತೈಸಿಕೊಂಡಿದ್ದೇನೆ.",
			"53": "<sup>53</sup> ನಿನ್ನ ಧರ್ಮಶಾಸ್ತ್ರಭ್ರಷ್ಟರಾದ ದುಷ್ಟರಿಗಾಗಿ ಉರಿಗೊಂಡಿದ್ದೇನೆ.",
			"54": "<sup>54</sup> ನನ್ನ ಪ್ರವಾಸದ ಮನೆಯಲ್ಲಿ ನಿನ್ನ ನಿಬಂಧನೆಗಳು ನನಗೆ ಗಾಯನವಾದವು.",
			"55": "<sup>55</sup> ಯೆಹೋವನೇ, ರಾತ್ರಿಯಲ್ಲಿ ನಿನ್ನ ನಾಮವನ್ನು ಸ್ಮರಿಸಿಕೊಳ್ಳುತ್ತೇನೆ; ನಿನ್ನ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಕೈಕೊಳ್ಳುತ್ತೇನೆ.",
			"56": "<sup>56</sup> ನಿನ್ನ ನೇಮಗಳನ್ನು ಅನುಸರಿಸಿದ್ದರಿಂದ ಇದೆಲ್ಲಾ ನನಗೆ ದೊರಕಿತು.",
			"57": "<sup>57</sup> ಯೆಹೋವನೇ, ನನ್ನ ಪಾಲು ನೀನೇ; ನಿನ್ನ ವಾಕ್ಯಗಳನ್ನು ಕೈಕೊಳ್ಳುವೆನೆಂದು ನಿರ್ಣಯಿಸಿಕೊಂಡಿದ್ದೇನೆ.",
			"58": "<sup>58</sup> ಪೂರ್ಣಮನಸ್ಸಿನಿಂದ ನಿನ್ನ ದಯೆಯನ್ನು ಅಪೇಕ್ಷಿಸಿದ್ದೇನೆ; ನಿನ್ನ ನುಡಿಗನುಸಾರವಾಗಿ ನನಗೆ ಪ್ರಸನ್ನನಾಗು.",
			"59": "<sup>59</sup> ನನ್ನ ನಡತೆಯನ್ನು ಶೋಧಿಸಿದೆನು. ನಿನ್ನ ಕಟ್ಟಳೆಗಳ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡಿದ್ದೇನೆ.",
			"60": "<sup>60</sup> ನಿನ್ನ ಆಜ್ಞೆಗಳನ್ನು ಅನುಸರಿಸುವದರಲ್ಲಿ ಆಸಕ್ತನಾದೆನು; ಆಲಸ್ಯಮಾಡಲಿಲ್ಲ.",
			"61": "<sup>61</sup> ದುಷ್ಟರ ಪಾಶಗಳು ನನ್ನನ್ನು ಸುತ್ತಿಕೊಂಡವು; ಆದರೂ ನಾನು ನಿನ್ನ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಮರೆಯಲಿಲ್ಲ.",
			"62": "<sup>62</sup> ನಿನ್ನ ನೀತಿವಿಧಿಗಳಿಗೋಸ್ಕರ ನಿನ್ನನ್ನು ಕೊಂಡಾಡಲು ಮಧ್ಯರಾತ್ರಿಯಲ್ಲಿ ಏಳುವೆನು.",
			"63": "<sup>63</sup> ನಿನ್ನ ನೇಮಗಳನ್ನು ಕೈಕೊಂಡು ನಿನ್ನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಿಗೆ ನಾನು ಸಂಗಡಿಗನು.",
			"64": "<sup>64</sup> ಯೆಹೋವನೇ, ಭೂಲೋಕವು ನಿನ್ನ ಕೃಪೆಯಿಂದ ಪೂರ್ಣವಾಗಿದೆ. ನಿನ್ನ ನಿಬಂಧನೆಗಳನ್ನು ನನಗೆ ಬೋಧಿಸು.",
			"65": "<sup>65</sup> ಯೆಹೋವನೇ, ನೀನು ನಿನ್ನ ವಾಗ್ದಾನಕ್ಕೆ ತಕ್ಕಂತೆ ನಿನ್ನ ಸೇವಕನಿಗೆ ಮಹೋಪಕಾರ ಮಾಡಿದ್ದೀ.",
			"66": "<sup>66</sup> ಉತ್ಕೃಷ್ಟವಾದ ಜ್ಞಾನವಿವೇಕಗಳನ್ನು ನನಗೆ ಹೇಳಿಕೊಡು; ನಿನ್ನ ಆಜ್ಞೆಗಳನ್ನು ನಂಬಿಕೊಂಡಿದ್ದೇನಲ್ಲಾ.",
			"67": "<sup>67</sup> ಕಷ್ಟಾನುಭವಕ್ಕಿಂತ ಮುಂಚೆ ತಪ್ಪಿಹೋಗುತ್ತಿದ್ದೆನು; ಈಗಲಾದರೋ ನಿನ್ನ ನುಡಿಗಳನ್ನು ಕೈಕೊಳ್ಳುತ್ತೇನೆ.",
			"68": "<sup>68</sup> ನೀನು ಒಳ್ಳೆಯವನೂ ಒಳ್ಳೆಯದನ್ನು ಮಾಡುವವನೂ ಆಗಿದ್ದೀ; ನಿನ್ನ ನಿಬಂಧನೆಗಳನ್ನು ನನಗೆ ಬೋಧಿಸು.",
			"69": "<sup>69</sup> ಗರ್ವಿಷ್ಠರು ನನಗೆ ವಿರೋಧವಾಗಿ ಸುಳ್ಳುಕಲ್ಪಿಸಿದ್ದಾರೆ; ನಾನಾದರೋ ಪೂರ್ಣಮನಸ್ಸಿನಿಂದ ನಿನ್ನ ನೇಮಗಳನ್ನು ಕೈಕೊಳ್ಳುವೆನು.",
			"70": "<sup>70</sup> ಕೊಬ್ಬಿನಂತೆ ಅವರ ಬುದ್ಧಿ ಮಂದವಾಯಿತು; ನಾನಾದರೋ ನಿನ್ನ ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿ ಉಲ್ಲಾಸಪಡುತ್ತೇನೆ.",
			"71": "<sup>71</sup> ಕಷ್ಟಾನುಭವವು ಹಿತಕರವಾಯಿತು; ಅದರಿಂದಲೇ ನಿನ್ನ ನಿಬಂಧನೆಗಳನ್ನು ಕಲಿತೆನು.",
			"72": "<sup>72</sup> ನೀನು ಉಸಿರಿದ ಧರ್ಮಶಾಸ್ತ್ರವು ಸಾವಿರಾರು ಚಿನ್ನಬೆಳ್ಳಿಯ ನಾಣ್ಯಗಳಿಗಿಂತಲೂ ನನಗೆ ಅತಿಪ್ರಿಯವಾಗಿದೆ.",
			"73": "<sup>73</sup> ನಿನ್ನ ಕೈಗಳು ನನ್ನನ್ನು ರೂಪಿಸಿ ನಿಲ್ಲಿಸಿದವು; ನಿನ್ನ ಆಜ್ಞೆಗಳನ್ನು ಕಲಿಯುವದಕ್ಕೆ ನನಗೆ ಬುದ್ಧಿಯನ್ನು ಕೊಡು.",
			"74": "<sup>74</sup> ನಿನ್ನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರು ನನ್ನನ್ನು ನೋಡಿ ಸಂತೋಷಿಸಲಿ; ನಾನು ನಿನ್ನ ವಾಕ್ಯವನ್ನೇ ನಿರೀಕ್ಷಿಸಿಕೊಂಡಿದ್ದೇನಲ್ಲಾ.",
			"75": "<sup>75</sup> ಯೆಹೋವನೇ, ನಿನ್ನ ವಿಧಿಗಳು ನೀತಿಯುಳ್ಳವುಗಳಾಗಿವೆ ಎಂದೂ ನೀನು ನಂಬಿಕೆಯಿಂದಲೇ ನನ್ನನ್ನು ಕುಗ್ಗಿಸಿದ್ದೀ ಎಂದೂ ತಿಳಿದುಕೊಂಡಿದ್ದೇನೆ.",
			"76": "<sup>76</sup> ನಿನ್ನ ಸೇವಕನಿಗೆ ನುಡಿದ ಪ್ರಕಾರ ನನ್ನ ಸಮಾಧಾನಕ್ಕೋಸ್ಕರ ಕೃಪೆಯನ್ನು ದಯಪಾಲಿಸು.",
			"77": "<sup>77</sup> ನಾನು ಬದುಕುವಂತೆ ನಿನ್ನ ಕರುಣೆಯು ನನಗೆ ದೊರೆಯಲಿ; ನಿನ್ನ ಧರ್ಮಶಾಸ್ತ್ರವೇ ನನ್ನ ಆನಂದವಾಗಿದೆ.",
			"78": "<sup>78</sup> ಗರ್ವಿಷ್ಠರು ಮಾನಭಂಗಹೊಂದಲಿ; ಅವರು ಮೋಸದಿಂದ ನನಗೆ ಕೇಡುಮಾಡಿದ್ದಾರೆ. ನಾನಾದರೋ ನಿನ್ನ ನೇಮಗಳನ್ನು ಧ್ಯಾನಿಸುತ್ತಿರುವೆನು.",
			"79": "<sup>79</sup> ನಿನ್ನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರು ನನ್ನ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡು ನಿನ್ನ ಕಟ್ಟಳೆಗಳನ್ನು ಗ್ರಹಿಸಿಕೊಳ್ಳಲಿ.",
			"80": "<sup>80</sup> ನನ್ನ ಮನಸ್ಸು ನಿನ್ನ ನಿಬಂಧನೆಗಳಲ್ಲಿ ಆಸಕ್ತವಾಗಲಿ; ಆಗ ನನ್ನ ಆಶಾಭಂಗಕ್ಕೆ ಕಾರಣವಿರುವದಿಲ್ಲ.",
			"81": "<sup>81</sup> ನಿನ್ನ ರಕ್ಷಣೆಯ ಬಯಕೆಯಿಂದಲೇ ನನ್ನ ಮನವು ಬಲಗುಂದಿತು; ನಾನು ನಿನ್ನ ವಾಕ್ಯದಲ್ಲೇ ನಿರೀಕ್ಷೆಯುಳ್ಳವನಾಗಿದ್ದೇನೆ.",
			"82": "<sup>82</sup> ನಿನ್ನ ನುಡಿಯನ್ನು ನೆರವೇರಿಸಿ ಯಾವಾಗ ನನ್ನನ್ನು ಸಂತೈಸುವಿ ಎಂಬದಕ್ಕೋಸ್ಕರವೇ ನನ್ನ ದೃಷ್ಟಿ ಮಂದವಾಯಿತು.",
			"83": "<sup>83</sup> ಹೊಗೆಯಲ್ಲಿ ನೇತುಹಾಕಿರುವ ಬುದ್ದಲಿಯಂತಿದ್ದೇನೆ; ಆದರೂ ನಿನ್ನ ನಿಬಂಧನೆಗಳನ್ನು ಮರೆಯಲಿಲ್ಲ.",
			"84": "<sup>84</sup> ನಿನ್ನ ಸೇವಕನ ದಿವಸಗಳು ಬಹುಸ್ವಲ್ಪವಲ್ಲಾ! ನನ್ನನ್ನು ಬಾಧಿಸುವವರಿಗೆ ಶಿಕ್ಷೆವಿಧಿಸುವದು ಯಾವಾಗ?",
			"85": "<sup>85</sup> ನಿನ್ನ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಅನುಸರಿಸದ ಗರ್ವಿಷ್ಠರು ನನಗಾಗಿ ಗುಂಡಿಗಳನ್ನು ಅಗೆದಿದ್ದಾರೆ.",
			"86": "<sup>86</sup> ನಿನ್ನ ಆಜ್ಞೆಗಳೆಲ್ಲಾ ಸ್ಥಿರವಾಗಿವೆ; ಅವರು ಮೋಸದಿಂದ ಹಿಂಸಿಸುತ್ತಾರೆ; ನನಗೆ ಸಹಾಯ ಮಾಡು.",
			"87": "<sup>87</sup> ನನ್ನನ್ನು ಭೂಮಿಯಿಂದ ತೆಗೆದೇಬಿಟ್ಟಿದ್ದರು; ಆದರೆ ನಾನು ನಿನ್ನ ನೇಮಗಳನ್ನು ಬಿಡಲೇ ಇಲ್ಲ.",
			"88": "<sup>88</sup> ನಿನ್ನ ಕೃಪೆಗನುಸಾರವಾಗಿ ನನ್ನನ್ನು ಚೈತನ್ಯಗೊಳಿಸು; ಆಗ ನೀನು ಉಸಿರಿದ ಕಟ್ಟಳೆಯನ್ನು ಕೈಕೊಳ್ಳುವೆನು.",
			"89": "<sup>89</sup> ಯೆಹೋವನೇ, ನಿನ್ನ ವಾಕ್ಯವು ಪರಲೋಕದಲ್ಲಿ ಶಾಶ್ವತವಾಗಿ ಸ್ಥಾಪಿಸಲ್ಪಟ್ಟದೆ.",
			"90": "<sup>90</sup> ನಿನ್ನ ಸತ್ಯತೆಯು ತಲತಲಾಂತರಕ್ಕೂ ಇರುವದು. ನೀನು ಭೂಮಿಯನ್ನು ಸ್ಥಾಪಿಸಿರುತ್ತೀ; ಅದು ಕದಲುವದಿಲ್ಲ.",
			"91": "<sup>91</sup> ನಿನ್ನ ವಿಧಿಗಳಿಗನುಸಾರವಾಗಿ ಅವು ಇಂದಿನವರೆಗೂ ಸ್ಥಿರವಾಗಿ ನಿಂತಿರುತ್ತವೆ. ಯಾಕಂದರೆ ಸರ್ವವಸ್ತುಗಳೂ ನಿನ್ನನ್ನು ಸೇವಿಸುತ್ತವೆ.",
			"92": "<sup>92</sup> ನಿನ್ನ ಧರ್ಮಶಾಸ್ತ್ರವು ನನಗೆ ಆನಂದಕರವಾಗದಿದ್ದರೆ ನನಗೊದಗಿದ ಆಪತ್ತಿನಲ್ಲಿ ಹಾಳಾಗಿ ಹೋಗುತ್ತಿದ್ದೆನು.",
			"93": "<sup>93</sup> ನಾನು ನಿನ್ನ ನೇಮಗಳನ್ನು ಎಂದಿಗೂ ಮರೆಯುವದಿಲ್ಲ; ಅವುಗಳಿಂದಲೇ ನನ್ನನ್ನು ಬದುಕಿಸಿದ್ದೀ.",
			"94": "<sup>94</sup> ನಾನು ನಿನ್ನವನು, ರಕ್ಷಿಸು. ನಿನ್ನ ನೇಮಗಳಲ್ಲಿ ಆಸಕ್ತನಾಗಿದ್ದೇನಲ್ಲಾ.",
			"95": "<sup>95</sup> ದುಷ್ಟರು ನನ್ನನ್ನು ಸಂಹರಿಸಬೇಕೆಂದು ಹೊಂಚಿ ನೋಡುತ್ತಾರೆ; ನಾನು ನಿನ್ನ ಕಟ್ಟಳೆಗಳನ್ನೇ ಲಕ್ಷಿಸಿಕೊಂಡಿರುವೆನು.",
			"96": "<sup>96</sup> ಎಲ್ಲಾ ಸಂಪೂರ್ಣತೆಗೂ ಮೇರೆಯುಂಟೆಂದು ಬಲ್ಲೆನು; ಆದರೆ ನಿನ್ನ ಆಜ್ಞಾಶಾಸನವು ಅಪಾರವಾದದ್ದು.",
			"97": "<sup>97</sup> ನಿನ್ನ ಧರ್ಮಶಾಸ್ತ್ರವು ನನಗೆ ಎಷ್ಟೋ ಪ್ರಿಯವಾಗಿದೆ; ದಿನವೆಲ್ಲಾ ಅದೇ ನನ್ನ ಧ್ಯಾನ.",
			"98": "<sup>98</sup> ನಿನ್ನ ಆಜ್ಞೆಗಳ ಮೂಲಕ ನನ್ನ ವೈರಿಗಳಿಗಿಂತ ಬುದ್ಧಿವಂತನಾಗಿದ್ದೇನೆ; ಸದಾಕಾಲವೂ ಅವೇ ನನಗಿವೆ.",
			"99": "<sup>99</sup> ನಿನ್ನ ಕಟ್ಟಳೆಗಳು ನನ್ನ ಧ್ಯಾನವಾಗಿರುವದರಿಂದ ನನ್ನ ಉಪಾಧ್ಯಾಯರಿಗಿಂತ ಜ್ಞಾನಿಯಾಗಿದ್ದೇನೆ.",
			"100": "<sup>100</sup> ನಿನ್ನ ನೇಮಗಳನ್ನು ಕೈಕೊಂಡಿರುವದರಿಂದ ಹಿರಿಯರಿಗಿಂತ ವಿವೇಕಿಯಾಗಿದ್ದೇನೆ.",
			"101": "<sup>101</sup> ನಿನ್ನ ವಾಕ್ಯವನ್ನೇ ಅನುಸರಿಸಬೇಕೆಂದು ನನ್ನ ಕಾಲುಗಳನ್ನು ಯಾವ ಕೆಟ್ಟ ದಾರಿಗೂ ಹೋಗದಂತೆ ಕಾದಿದ್ದೇನೆ.",
			"102": "<sup>102</sup> ನಾನು ನಿನ್ನ ವಿಧಿಗಳಿಂದ ಸ್ವಲ್ಪವೂ ತಪ್ಪಿಹೋಗಲಿಲ್ಲ; ಯಾಕಂದರೆ ನೀನು ನನ್ನನ್ನು ಬೋಧಿಸಿದ್ದೀ.",
			"103": "<sup>103</sup> ನಿನ್ನ ನುಡಿಗಳು ನನ್ನ ನಾಲಿಗೆಗೆ ಎಷ್ಟೋ ರುಚಿಯಾಗಿವೆ; ಅವು ನನ್ನ ಬಾಯಿಗೆ ಜೇನುತುಪ್ಪಕ್ಕಿಂತಲೂ ಸಿಹಿಯಾಗಿವೆ.",
			"104": "<sup>104</sup> ನಿನ್ನ ನೇಮಗಳ ಮೂಲಕ ವಿವೇಕಿಯಾದೆನು; ಮಿಥ್ಯಾಮಾರ್ಗವನ್ನೆಲ್ಲಾ ಹಗೆಮಾಡುತ್ತೇನೆ.",
			"105": "<sup>105</sup> ನಿನ್ನ ವಾಕ್ಯವು ನನ್ನ ಕಾಲಿಗೆ ದೀಪವೂ ನನ್ನ ದಾರಿಗೆ ಬೆಳಕೂ ಆಗಿದೆ.",
			"106": "<sup>106</sup> ನಿನ್ನ ನೀತಿವಿಧಿಗಳನ್ನು ಅನುಸರಿಸುವೆನೆಂದು ಪ್ರಮಾಣಮಾಡಿದ್ದೇನೆ; ಅದನ್ನು ನೆರವೇರಿಸುವೆನು.",
			"107": "<sup>107</sup> ನಾನು ಬಹಳವಾಗಿ ಕುಗ್ಗಿಹೋಗಿದ್ದೇನೆ; ಯೆಹೋವನೇ, ನಿನ್ನ ವಾಕ್ಯಾನುಸಾರವಾಗಿ ನನ್ನನ್ನು ಚೈತನ್ಯಗೊಳಿಸು.",
			"108": "<sup>108</sup> ಯೆಹೋವನೇ, ನನ್ನ ಮನಃಪೂರ್ವಕವಾದ ಸ್ತುತಿ ಸಮರ್ಪಣೆಗಳನ್ನು ದಯವಿಟ್ಟು ಅಂಗೀಕರಿಸು; ನಿನ್ನ ವಿಧಿಗಳನ್ನು ನನಗೆ ಕಲಿಸು.",
			"109": "<sup>109</sup> ನಾನು ಯಾವಾಗಲೂ ಕೈಯಲ್ಲಿ ಜೀವ ಹಿಡಿದಿದ್ದೇನೆ; ಆದರೂ ನಿನ್ನ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಮರೆಯುವದಿಲ್ಲ.",
			"110": "<sup>110</sup> ದುಷ್ಟರು ಬಲೆಯೊಡ್ಡಿದ್ದಾರೆ; ನಾನು ನಿನ್ನ ನೇಮಗಳನ್ನು ತಪ್ಪಿಹೋಗುವದಿಲ್ಲ.",
			"111": "<sup>111</sup> ನಿನ್ನ ಕಟ್ಟಳೆಗಳನ್ನು ನನ್ನ ನಿತ್ಯಸ್ವಾಸ್ತ್ಯವನ್ನಾಗಿ ಆರಿಸಿಕೊಂಡಿದ್ದೇನೆ; ಅವು ನನ್ನ ಹೃದಯಕ್ಕೆ ಉಲ್ಲಾಸಕರವಾಗಿವೆ.",
			"112": "<sup>112</sup> ಕಡೇವರೆಗೂ ಯಾವಾಗಲೂ ನಿನ್ನ ನಿಬಂಧನೆಗಳನ್ನು ಕೈಕೊಳ್ಳುವದಕ್ಕೆ ಮನಸ್ಸುಮಾಡಿದ್ದೇನೆ.",
			"113": "<sup>113</sup> ಚಪಲಚಿತ್ತರನ್ನು ದ್ವೇಷಿಸುತ್ತೇನೆ; ನಿನ್ನ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಪ್ರೀತಿಸುತ್ತೇನೆ.",
			"114": "<sup>114</sup> ನನ್ನ ಆಶ್ರಯವೂ ಗುರಾಣಿಯೂ ನೀನೇ; ನಿನ್ನ ವಾಕ್ಯವನ್ನೇ ನಿರೀಕ್ಷಿಸಿಕೊಂಡಿದ್ದೇನೆ.",
			"115": "<sup>115</sup> ದುಷ್ಟರೇ, ತೊಲಗಿರಿ; ನನ್ನ ದೇವರ ಆಜ್ಞೆಗಳನ್ನು ಕೈಕೊಳ್ಳುವೆನು.",
			"116": "<sup>116</sup> ನಿನ್ನ ನುಡಿಯ ಪ್ರಕಾರ ನನ್ನನ್ನು ಉದ್ಧಾರಮಾಡು; ಆಗ ಬದುಕುವೆನು. ನಾನು ನಿರೀಕ್ಷೆಯುಳ್ಳವನಾಗಿದ್ದೇನೆ, ನನ್ನನ್ನು ನಿರಾಶೆಪಡಿಸಬೇಡ.",
			"117": "<sup>117</sup> ನೀನು ನನಗೆ ಆಧಾರವಾಗಿರು; ಆಗ ನಾನು ಸುರಕ್ಷಿತನಾಗಿ ಸದಾ ನಿನ್ನ ನಿಬಂಧನೆಗಳನ್ನು ಲಕ್ಷಿಸುವೆನು.",
			"118": "<sup>118</sup> ನಿನ್ನ ನಿಬಂಧನೆಗಳಿಗೆ ತಪ್ಪಿದವರೆಲ್ಲರನ್ನು ನೀನು ತಳ್ಳಿಬಿಡುತ್ತೀ; ಅವರ ಕುಯುಕ್ತಿಯು ವ್ಯರ್ಥವಾದದ್ದೇ.",
			"119": "<sup>119</sup> ಭೂಲೋಕದ ದುಷ್ಟರೆಲ್ಲರನ್ನು ಕಿಟ್ಟದಂತೆ ತೆಗೆದುಬಿಡುತ್ತೀ; ಆದದರಿಂದ ನಾನು ನಿನ್ನ ಕಟ್ಟಳೆಗಳನ್ನು ಪ್ರೀತಿಸುತ್ತೇನೆ.",
			"120": "<sup>120</sup> ನಿನ್ನ ಭಯದಿಂದ ನನ್ನ ಮಾಂಸವು ಕಂಪಿಸುತ್ತದೆ; ನಿನ್ನ ವಿಧಿಗಳಿಗೆ ಅಂಜಿಕೊಂಡಿದ್ದೇನೆ.",
			"121": "<sup>121</sup> ನಾನು ನಿನ್ನ ನೀತಿವಿಧಿಗಳನ್ನು ಅನುಸರಿಸಿದ್ದೇನೆ; ಬಲಾತ್ಕಾರಿಗಳಿಗೆ ನನ್ನನ್ನು ಒಪ್ಪಿಸಬೇಡ.",
			"122": "<sup>122</sup> ನಿನ್ನ ಸೇವಕನ ಮೇಲಿಗಾಗಿ ಹೊಣೆಗಾರನಾಗು; ಗರ್ವಿಷ್ಠರು ನನ್ನನ್ನು ಬಾಧಿಸದಿರಲಿ.",
			"123": "<sup>123</sup> ನಿನ್ನ ರಕ್ಷಣೆಯನ್ನೂ ನಿನ್ನ ನೀತಿಯುಳ್ಳ ನುಡಿಯು ನೆರವೇರುವದನ್ನೂ ನಿರೀಕ್ಷಿಸುತ್ತಾ ನನ್ನ ದೃಷ್ಟಿ ಮೊಬ್ಬಾಯಿತು.",
			"124": "<sup>124</sup> ನಿನ್ನ ಸೇವಕನನ್ನು ಕೃಪೆಯಿಂದ ನಡಿಸು; ನಿನ್ನ ನಿಬಂಧನೆಗಳನ್ನು ನನಗೆ ಕಲಿಸು.",
			"125": "<sup>125</sup> ನಾನು ನಿನ್ನ ಸೇವಕನು; ನಿನ್ನ ಕಟ್ಟಳೆಗಳನ್ನು ತಿಳಿದುಕೊಳ್ಳುವಂತೆ ವಿವೇಕವನ್ನು ದಯಪಾಲಿಸು.",
			"126": "<sup>126</sup> ಯೆಹೋವನೇ, ನೀನು ಕಾರ್ಯನಡಿಸುವದಕ್ಕೆ ಸಮಯಬಂದದೆ; ನಿನ್ನ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಉಲ್ಲಂಘಿಸಿದ್ದಾರೆ.",
			"127": "<sup>127</sup> ನಿಜವಾಗಿ ನಿನ್ನ ಆಜ್ಞೆಗಳು ಬಂಗಾರಕ್ಕಿಂತಲೂ ಅಪರಂಜಿಗಿಂತಲೂ ನನಗೆ ಬಹುಪ್ರಿಯವಾಗಿವೆ.",
			"128": "<sup>128</sup> ನಿಜವಾಗಿ ನಿನ್ನ ಎಲ್ಲಾ ನೇಮಗಳು ನ್ಯಾಯವಾಗಿವೆ ಎಂದು ಒಪ್ಪಿಕೊಂಡಿದ್ದೇನೆ; ಎಲ್ಲಾ ಮಿಥ್ಯಾಮಾರ್ಗಗಳನ್ನು ಹಗೆಮಾಡುತ್ತೇನೆ.",
			"129": "<sup>129</sup> ನಿನ್ನ ಕಟ್ಟಳೆಗಳು ಮಹತ್ವವುಳ್ಳವುಗಳೇ; ಪೂರ್ಣಹೃದಯದಿಂದ ಅವುಗಳನ್ನು ಕೈಕೊಳ್ಳುತ್ತೇನೆ.",
			"130": "<sup>130</sup> ನಿನ್ನ ವಾಕ್ಯವಿವರಣೆಯಿಂದ ಯುಕ್ತಿಹೀನರಿಗೆ ಜ್ಞಾನೋದಯವಾಗುವದು.",
			"131": "<sup>131</sup> ನಾನು ಬಾಯ್ದೆರೆದು ಏದುತ್ತಿದ್ದೇನೆ; ನಿನ್ನ ಆಜ್ಞೆಗಳನ್ನು ಲವಲವಿಕೆಯಿಂದ ಆಶಿಸುತ್ತೇನೆ.",
			"132": "<sup>132</sup> ಅಭಿಮುಖನಾಗಿ ಕರುಣಿಸು; ನಿನ್ನ ಹೆಸರನ್ನು ಪ್ರೀತಿಸುವವರಿಗೆ ಹೀಗೆ ಮಾಡುವದು ನಿನಗೆ ವಿಧಿಯಷ್ಟೆ.",
			"133": "<sup>133</sup> ನಿನ್ನ ನುಡಿಗನುಸಾರವಾಗಿ ನನ್ನ ಹೆಜ್ಜೆಯನ್ನು ದೃಢಪಡಿಸು; ಯಾವ ಅನ್ಯಾಯವಾದರೂ ನನ್ನನ್ನು ಆಳದಂತೆ ಮಾಡು.",
			"134": "<sup>134</sup> ನರರ ಬಲಾತ್ಕಾರದಿಂದ ನನ್ನನ್ನು ಬಿಡಿಸು; ಆಗ ನಿನ್ನ ನೇಮಗಳನ್ನು ಕೈಕೊಳ್ಳುವೆನು.",
			"135": "<sup>135</sup> ನಿನ್ನ ದಾಸನ ಮೇಲೆ ನಿನ್ನ ಮುಖಪ್ರಸನ್ನತೆಯಿರಲಿ; ನಿನ್ನ ನಿಬಂಧನೆಗಳನ್ನು ನನಗೆ ಕಲಿಸು.",
			"136": "<sup>136</sup> ನಿನ್ನ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಅನುಸರಿಸದವರ ನಿಮಿತ್ತ ನನಗೆ ಕಣ್ಣೀರು ಪ್ರವಾಹವಾಗಿ ಹರಿಯುತ್ತದೆ.",
			"137": "<sup>137</sup> ಯೆಹೋವನೇ, ನೀನು ನೀತಿಸ್ವರೂಪನು; ನಿನ್ನ ವಿಧಿಗಳು ನ್ಯಾಯವಾಗಿವೆ.",
			"138": "<sup>138</sup> ನೀತಿಸತ್ಯತೆಗಳಿಂದಲೇ ನಿನ್ನ ಕಟ್ಟಳೆಗಳನ್ನು ಸ್ಥಾಪಿಸಿದ್ದೀ;",
			"139": "<sup>139</sup> ಮತಾಸಕ್ತಿಯು ನನ್ನನ್ನು ದಹಿಸಿಬಿಟ್ಟಿತು; ವೈರಿಗಳು ನಿನ್ನ ವಾಕ್ಯಗಳನ್ನು ಮರೆತುಬಿಟ್ಟಿದ್ದಾರಲ್ಲಾ.",
			"140": "<sup>140</sup> ನಿನ್ನ ನುಡಿಯು ಪರಿಶುದ್ಧವಾದದ್ದು; ನಿನ್ನ ಸೇವಕನು ಅದನ್ನೇ ಪ್ರೀತಿಸುತ್ತಾನೆ.",
			"141": "<sup>141</sup> ನಾನು ಅಲ್ಪನೂ ತಿರಸ್ಕಾರಹೊಂದಿದವನೂ ಆಗಿದ್ದೇನೆ; ಆದರೂ ನಿನ್ನ ನೇಮಗಳನ್ನು ಮರೆಯುವದಿಲ್ಲ.",
			"142": "<sup>142</sup> ನಿನ್ನ ನೀತಿಯು ನಿತ್ಯವಾದದ್ದೇ; ನಿನ್ನ ಧರ್ಮಶಾಸ್ತ್ರವು ಸತ್ಯವಾಗಿದೆ.",
			"143": "<sup>143</sup> ಕಷ್ಟಸಂಕಟಗಳು ನನ್ನನ್ನು ಹಿಡಿದವೆ; ನಿನ್ನ ಆಜ್ಞೆಗಳು ನನಗೆ ಸಂತೋಷಕರವಾಗಿವೆ.",
			"144": "<sup>144</sup> ನಿನ್ನ ಕಟ್ಟಳೆಗಳು ಸದಾಕಾಲವೂ ನೀತಿಯುಳ್ಳವುಗಳು; ನನಗೆ ವಿವೇಕವನ್ನು ದಯಪಾಲಿಸು, ಆಗ ಬದುಕುವೆನು.",
			"145": "<sup>145</sup> ಯೆಹೋವನೇ, ಸಂಪೂರ್ಣಮನಸ್ಸಿನಿಂದ ಮೊರೆಯಿಟ್ಟಿದ್ದೇನೆ, ಸದುತ್ತರವನ್ನು ದಯಪಾಲಿಸು; ನಿನ್ನ ನಿಬಂಧನೆಗಳನ್ನು ಅನುಸರಿಸುವೆನು.",
			"146": "<sup>146</sup> ನಿನಗೇ ಮೊರೆಯಿಟ್ಟಿದ್ದೇನೆ; ರಕ್ಷಿಸು. ನಿನ್ನ ಕಟ್ಟಳೆಗಳನ್ನು ಕೈಕೊಳ್ಳುವೆನು.",
			"147": "<sup>147</sup> ಅರುಣೋದಯದಲ್ಲಿ ಎದ್ದು ಮೊರೆಯಿಟ್ಟೆನು; ನಿನ್ನ ವಾಕ್ಯವನ್ನು ನಿರೀಕ್ಷಿಸಿಕೊಂಡಿದ್ದೇನೆ.",
			"148": "<sup>148</sup> ಇರುಳಿನ ಒಂದೊಂದು ಜಾವವನ್ನು ಮುಂಗೊಂಡು ನಿನ್ನ ನುಡಿಯನ್ನು ಧ್ಯಾನಿಸುತ್ತೇನೆ.",
			"149": "<sup>149</sup> ಯೆಹೋವನೇ, ನಿನ್ನ ಕೃಪೆಗೆ ತಕ್ಕಂತೆ ನನ್ನ ಮೊರೆಯನ್ನು ಕೇಳು; ನಿನ್ನ ವಿಧಿಗನುಸಾರವಾಗಿ ನನ್ನನ್ನು ಚೈತನ್ಯಗೊಳಿಸು.",
			"150": "<sup>150</sup> ನಿನ್ನ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಬಿಟ್ಟು ಕೆಟ್ಟದ್ದನ್ನು ಅನುಸರಿಸುವವರು ನನ್ನ ಸಮೀಪಕ್ಕೆ ಬಂದಿದ್ದಾರೆ.",
			"151": "<sup>151</sup> ಯೆಹೋವನೇ, ನೀನು ನನ್ನ ಹತ್ತಿರವೇ ಇದ್ದೀ; ನಿನ್ನ ಆಜ್ಞೆಗಳೆಲ್ಲಾ ಯಥಾರ್ಥವಾಗಿವೆ.",
			"152": "<sup>152</sup> ನೀನು ನಿನ್ನ ಕಟ್ಟಳೆಗಳನ್ನು ಯುಗಯುಗಾಂತರಕ್ಕೂ ಸ್ಥಾಪಿಸಿದ್ದೀ ಎಂದು ನಾನು ಮೊದಲಿನಿಂದಲೇ ಅವುಗಳ ಮೂಲಕ ತಿಳಿದುಕೊಂಡಿದ್ದೇನೆ.",
			"153": "<sup>153</sup> ನನ್ನ ಕಷ್ಟವನ್ನು ನೋಡಿ ನನ್ನನ್ನು ರಕ್ಷಿಸು; ನಾನು ನಿನ್ನ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಮರೆತವನಲ್ಲ.",
			"154": "<sup>154</sup> ನನ್ನ ವ್ಯಾಜ್ಯವನ್ನು ನಡಿಸಿ ನನ್ನನ್ನು ಬಿಡಿಸು; ನಿನ್ನ ನುಡಿಗನುಸಾರವಾಗಿ ನನ್ನನ್ನು ಚೈತನ್ಯಗೊಳಿಸು.",
			"155": "<sup>155</sup> ನಿನ್ನ ನಿಬಂಧನೆಗಳನ್ನು ಅಲಕ್ಷ್ಯಮಾಡುವ ದುಷ್ಟರಿಗೆ ರಕ್ಷಣೆಯೇ ಇಲ್ಲ.",
			"156": "<sup>156</sup> ಯೆಹೋವನೇ, ನಿನ್ನ ಕೃಪಾಕಾರ್ಯಗಳು ಬಹಳವಾಗಿವೆ; ನಿನ್ನ ವಿಧಿಗಳಿಗೆ ತಕ್ಕಂತೆ ನನ್ನನ್ನು ಚೈತನ್ಯಗೊಳಿಸು.",
			"157": "<sup>157</sup> ನನ್ನನ್ನು ಹಿಂಸಿಸುವ ವೈರಿಗಳು ಅನೇಕರಿದ್ದರೂ ನಾನು ನಿನ್ನ ಕಟ್ಟಳೆಗಳನ್ನು ಬಿಟ್ಟುನಡೆಯಲಿಲ್ಲ.",
			"158": "<sup>158</sup> ನಿನ್ನ ನುಡಿಯನ್ನು ಕೈಕೊಳ್ಳದ ಧರ್ಮಭ್ರಷ್ಟರನ್ನು ನೋಡಿ ಅಸಹ್ಯಪಟ್ಟಿದ್ದೇನೆ.",
			"159": "<sup>159</sup> ಯೆಹೋವನೇ, ನೋಡು, ನಿನ್ನ ನೇಮಗಳು ನನಗೆ ಎಷ್ಟೋ ಪ್ರಿಯವಾಗಿವೆ; ನಿನ್ನ ಕೃಪಾನುಸಾರವಾಗಿ ನನ್ನನ್ನು ಚೈತನ್ಯಗೊಳಿಸು.",
			"160": "<sup>160</sup> ನಿನ್ನ ವಾಕ್ಯದ ಸಾರಾಂಶವು ಸತ್ಯವೇ; ನಿನ್ನ ನೀತಿವಿಧಿಗಳೆಲ್ಲಾ ಯುಗಯುಗಕ್ಕೂ ಇರುವವು.",
			"161": "<sup>161</sup> ಪ್ರಭುಗಳು ನಿಷ್ಕಾರಣವಾಗಿ ನನ್ನನ್ನು ಹಿಂಸಿಸುತ್ತಾರೆ; ನನ್ನ ಹೃದಯವು ನಿನ್ನ ವಾಕ್ಯಕ್ಕೆ ಮಾತ್ರ ಭಯಪಡುತ್ತದೆ.",
			"162": "<sup>162</sup> ಒಬ್ಬನು ತಾನು ಸಂಪಾದಿಸಿದ ದೊಡ್ಡ ಕೊಳ್ಳೆಯಲ್ಲಿ ಹೇಗೋ ಹಾಗೆಯೇ ನಾನು ನಿನ್ನ ನುಡಿಯಲ್ಲಿ ಆನಂದಿಸುತ್ತೇನೆ.",
			"163": "<sup>163</sup> ಮಿಥ್ಯವಾದದ್ದನ್ನು ಹಗೆಮಾಡುತ್ತೇನೆ, ಅದು ನನಗೆ ಅಸಹ್ಯವಾಗಿದೆ; ನಿನ್ನ ಧರ್ಮಶಾಸ್ತ್ರವು ನನಗೆ ಪ್ರಿಯವಾಗಿದೆ.",
			"164": "<sup>164</sup> ನಿನ್ನ ನೀತಿವಿಧಿಗಳಿಗೋಸ್ಕರ ನಿನ್ನನ್ನು ದಿನಕ್ಕೆ ಏಳು ಸಾರಿ ಕೊಂಡಾಡುತ್ತೇನೆ.",
			"165": "<sup>165</sup> ನಿನ್ನ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಪ್ರೀತಿಸುವವರಿಗೆ ಸಂಪೂರ್ಣ ಸಮಾಧಾನವಿರುತ್ತದೆ; ಅಂಥವರಿಗೆ ವಿಘ್ನಕರವಾದದ್ದೇನೂ ಇರುವದಿಲ್ಲ.",
			"166": "<sup>166</sup> ಯೆಹೋವನೇ, ಯಾವಾಗ ರಕ್ಷಿಸುವಿಯೋ ಎಂದು ನಿರೀಕ್ಷಿಸುತ್ತಾ ಇದ್ದೇನೆ; ನಿನ್ನ ಆಜ್ಞೆಗಳನ್ನು ಕೈಕೊಂಡಿದ್ದೇನೆ.",
			"167": "<sup>167</sup> ನಿನ್ನ ಕಟ್ಟಳೆಗಳನ್ನು ಮನಃಪೂರ್ವಕವಾಗಿ ಅನುಸರಿಸಿದ್ದೇನೆ; ಅವು ನನಗೆ ಬಹುಪ್ರಿಯವಾಗಿವೆ.",
			"168": "<sup>168</sup> ನಿನ್ನ ನೇಮಗಳನ್ನೂ ಕಟ್ಟಳೆಗಳನ್ನೂ ಅನುಸರಿಸಿದ್ದೇನೆ; ನನ್ನ ನಡತೆಯೆಲ್ಲಾ ನಿನಗೆ ಗೊತ್ತದೆ.",
			"169": "<sup>169</sup> ಯೆಹೋವನೇ, ನನ್ನ ಕೂಗು ನಿನ್ನ ಸನ್ನಿಧಿಯನ್ನು ಮುಟ್ಟಲಿ; ನಿನ್ನ ವಾಕ್ಯಾನುಸಾರವಾಗಿ ನನಗೆ ಜ್ಞಾನವನ್ನು ದಯಪಾಲಿಸು.",
			"170": "<sup>170</sup> ನನ್ನ ವಿಜ್ಞಾಪನೆಯು ನಿನ್ನ ಸನ್ನಿಧಿಗೆ ಸೇರಲಿ; ನಿನ್ನ ನುಡಿಗೆ ತಕ್ಕಂತೆ ನನ್ನನ್ನು ರಕ್ಷಿಸು.",
			"171": "<sup>171</sup> ನನ್ನ ತುಟಿಗಳಿಂದ ಸ್ತುತಿ ಹೊರಡಲಿ; ನಿನ್ನ ನಿಬಂಧನೆಗಳನ್ನು ನನಗೆ ಕಲಿಸಿದ್ದೀಯಲ್ಲಾ.",
			"172": "<sup>172</sup> ನನ್ನ ನಾಲಿಗೆಯು ನಿನ್ನ ನುಡಿಗಳನ್ನು ವರ್ಣಿಸಲಿ; ನಿನ್ನ ಆಜ್ಞೆಗಳೆಲ್ಲಾ ನೀತಿಯೇ.",
			"173": "<sup>173</sup> ನಿನ್ನ ನೇಮಗಳನ್ನು ಆರಿಸಿಕೊಂಡಿದ್ದೇನೆ; ನಿನ್ನ ಕೈ ನನಗೆ ನೆರವಾಗಲಿ.",
			"174": "<sup>174</sup> ಯೆಹೋವನೇ, ನಿನ್ನಿಂದುಂಟಾಗುವ ರಕ್ಷಣೆಯನ್ನು ಕೋರುತ್ತೇನೆ; ನಿನ್ನ ಧರ್ಮಶಾಸ್ತ್ರವೇ ನನ್ನ ಆನಂದವು.",
			"175": "<sup>175</sup> ನನ್ನ ಪ್ರಾಣವನ್ನು ಉಳಿಸು; ಅದು ನಿನ್ನನ್ನು ಕೊಂಡಾಡಲಿ. ನಿನ್ನ ವಿಧಿಗಳಿಂದ ನನಗೆ ಸಹಾಯವಾಗಲಿ.",
			"176": "<sup>176</sup> ನಾನು ತಪ್ಪಿಹೋದ ಕುರಿಯಂತೆ ಅಲೆಯುತ್ತಿದ್ದೇನೆ; ನಿನ್ನ ಸೇವಕನನ್ನು ಪರಾಂಬರಿಸು. ನಾನು ನಿನ್ನ ಆಜ್ಞೆಗಳನ್ನು ಮರೆಯುವದಿಲ್ಲ."
		},
		"120": {
			"1": "<sup>1</sup> ಇಕ್ಕಟ್ಟಿನಲ್ಲಿ ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಟ್ಟೆನು; ಆತನು ಸದುತ್ತರವನ್ನು ದಯಪಾಲಿಸಿದನು.",
			"2": "<sup>2</sup> ಯೆಹೋವನೇ, ಸುಳ್ಳುಬಾಯಿಯೂ ವಂಚಿಸುವ ನಾಲಿಗೆಯೂ ಉಳ್ಳವರಿಂದ ನನ್ನನ್ನು ಬಿಡಿಸು.",
			"3": "<sup>3</sup> ವಂಚಿಸುವ ನಾಲಿಗೆಯೇ, ದೇವರು ನಿನಗೇನು ಕೊಡಬೇಕು? ಯಾವ ಹೆಚ್ಚಿನ ಶಿಕ್ಷೆಯನ್ನು ಒದಗಿಸಬೇಕು?",
			"4": "<sup>4</sup> ಶೂರನ ಹದವಾದ ಬಾಣಗಳನ್ನೂ ಜಾಲಿಯ ಕೆಂಡಗಳನ್ನೂ ನಿನಗೆ ಕೊಡಲಿ.",
			"5": "<sup>5</sup> ಅಯ್ಯೋ, ನಾನು ಮೇಷೆಕಿನವರಲ್ಲಿ ತಂಗಬೇಕಲ್ಲಾ! ಕೇದಾರಿನವರ ಪಾಳೆಯಗಳಲ್ಲಿ ವಾಸಿಸಬೇಕಾಯಿತಲ್ಲಾ!",
			"6": "<sup>6</sup> ಸಮಾಧಾನವನ್ನು ಹಗೆಮಾಡುವವರೊಳಗೆ ಇದ್ದು ಇದ್ದು ಸಾಕಾಯಿತು.",
			"7": "<sup>7</sup> ನಾನು ಸಮಾಧಾನಪ್ರಿಯನು; ಅವರೋ, ಮಾತಾಡಿದರೆ ಯುದ್ಧಕ್ಕೆ ಬರುತ್ತಾರೆ."
		},
		"121": {
			"1": "<sup>1</sup> ನಾನು ಕಣ್ಣೆತ್ತಿ ಪರ್ವತಗಳ ಕಡೆಗೆ ನೋಡುತ್ತೇನೆ; ನನ್ನ ಸಹಾಯವು ಎಲ್ಲಿಂದ ಬರುವದು?",
			"2": "<sup>2</sup> ಭೂಮ್ಯಾಕಾಶಗಳನ್ನು ನಿರ್ಮಿಸಿದ ಯೆಹೋವನಿಂದಲೇ ನನ್ನ ಸಹಾಯವು ಬರುತ್ತದೆ.",
			"3": "<sup>3</sup> ಆತನು ನಿನ್ನ ಪಾದಗಳನ್ನು ಕದಲಗೊಡಿಸದಿರಲಿ; ನಿನ್ನನ್ನು ಕಾಯುವವನು ತೂಕಡಿಸದಿರಲಿ.",
			"4": "<sup>4</sup> ಇಗೋ, ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಕಾಯುವಾತನು ತೂಕಡಿಸುವದಿಲ್ಲ, ನಿದ್ರಿಸುವದಿಲ್ಲ.",
			"5": "<sup>5</sup> ನಿನ್ನನ್ನು ಕಾಯುವವನು ಯೆಹೋವನೇ; ನಿನ್ನ ಬಲಗಡೆಯಲ್ಲಿ ನೆರಳಿನಂತೆ ನಿಂತಿರುವಾತನು ಯೆಹೋವನೇ.",
			"6": "<sup>6</sup> ಹಗಲಲ್ಲಿ ಸೂರ್ಯನೂ ಇರುಳಲ್ಲಿ ಚಂದ್ರನೂ ನಿನ್ನನ್ನು ಬಾಧಿಸುವದಿಲ್ಲ.",
			"7": "<sup>7</sup> ಯೆಹೋವನು ನಿನ್ನನ್ನು ಎಲ್ಲಾ ಕೇಡಿನಿಂದ ತಪ್ಪಿಸುವನು; ನಿನ್ನ ಪ್ರಾಣವನ್ನು ಕಾಯುವನು.",
			"8": "<sup>8</sup> ನೀನು ಹೋಗುವಾಗಲೂ ಬರುವಾಗಲೂ ಇಂದಿನಿಂದ ಸದಾಕಾಲವೂ ಯೆಹೋವನು ನಿನ್ನನ್ನು ಕಾಪಾಡುವನು."
		},
		"122": {
			"1": "<sup>1</sup> ಯೆಹೋವನ ಮಂದಿರಕ್ಕೆ ಹೋಗೋಣ, ಬಾ ಎಂದು ಜನರು ನನ್ನನ್ನು ಕರೆದಾಗ ನನಗೆ ಸಂತೋಷವಾಯಿತು.",
			"2": "<sup>2</sup> ಯೆರೂಸಲೇಮೇ, ನಮ್ಮ ಕಾಲುಗಳು ನಿನ್ನ ಬಾಗಲುಗಳಲ್ಲಿ ಸೇರಿರುತ್ತವಲ್ಲಾ!",
			"3": "<sup>3</sup> ಯೆರೂಸಲೇಮೇ, ನೀನು ಸರಿಯಾದ ಹೊಂದಿಕೆಯಿಂದ ಕಟ್ಟಲ್ಪಟ್ಟ ನಗರವಾಗಿದ್ದೀ.",
			"4": "<sup>4</sup> ಕುಲಗಳು ಅಂದರೆ ಯಾಹುವಿನ ಕುಲದವರು ಯೆಹೋವನ ನಾಮಕೀರ್ತನೆಗೋಸ್ಕರ ಇಲ್ಲಿಗೇ ಯಾತ್ರೆಮಾಡುತ್ತಿದ್ದರು. ಇದು ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿದ್ದ ಕಟ್ಟಳೆಯಷ್ಟೆ.",
			"5": "<sup>5</sup> ನ್ಯಾಯಪೀಠಗಳಾಗಿರುವ ದಾವೀದನ ಮನೆತನದವರ ಸಿಂಹಾಸನಗಳು ಇಲ್ಲಿಯೇ ಇದ್ದವಲ್ಲಾ.",
			"6": "<sup>6</sup> ಯೆರೂಸಲೇಮಿನ ಶುಭಕ್ಕೋಸ್ಕರ ಪ್ರಾರ್ಥಿಸಿರಿ. [ಯೆರೂಸಲೇಮೇ,] ನಿನ್ನನ್ನು ಪ್ರೀತಿಸುವವರಿಗೆ ಸೌಭಾಗ್ಯವುಂಟಾಗಲಿ.",
			"7": "<sup>7</sup> ನಿನ್ನ ಪೌಳಿಗೋಡೆಗಳೊಳಗೆ ಶುಭವುಂಟಾಗಲಿ; ನಿನ್ನ ಅರಮನೆಗಳಲ್ಲಿ ಸೌಭಾಗ್ಯವಿರಲಿ.",
			"8": "<sup>8</sup> ನನ್ನ ಬಂಧುಮಿತ್ರರ ನಿಮಿತ್ತವಾಗಿ ನಿನಗೆ ಶುಭವಾಗಲೆಂದು ಹೇಳುತ್ತೇನೆ.",
			"9": "<sup>9</sup> ನಮ್ಮ ಯೆಹೋವದೇವರ ಮಂದಿರ ನಿಮಿತ್ತವಾಗಿ ನಿನಗೆ ಸುಕ್ಷೇಮವನ್ನು ಹಾರೈಸುತ್ತೇನೆ."
		},
		"123": {
			"1": "<sup>1</sup> ಪರಲೋಕದಲ್ಲಿ ಆಸೀನನಾಗಿರುವಾತನೇ, ನನ್ನ ಕಣ್ಣುಗಳನ್ನು ನಿನ್ನ ಕಡೆಗೆ ಎತ್ತಿದ್ದೇನೆ.",
			"2": "<sup>2</sup> ಯಜಮಾನನ ಕೈಯನ್ನು ದಾಸನ ಕಣ್ಣುಗಳೂ ಯಜಮಾನಿಯ ಕೈಯನ್ನು ದಾಸಿಯ ಕಣ್ಣುಗಳೂ ನೋಡುವ ಪ್ರಕಾರವೇ ನಮ್ಮ ಕಣ್ಣುಗಳು ನಮ್ಮ ಯೆಹೋವ ದೇವರನ್ನು ನೋಡುತ್ತಾ ಆತನ ಕಟಾಕ್ಷವನ್ನು ನಿರೀಕ್ಷಿಸಿಕೊಂಡಿವೆ.",
			"3": "<sup>3</sup> ಯೆಹೋವನೇ, ನಮ್ಮನ್ನು ಕಟಾಕ್ಷಿಸು, ಕಟಾಕ್ಷಿಸು. ಅಪಮಾನ ಹೊಂದಿ ಹೊಂದಿ ನಮಗೆ ಸಾಕಾಯಿತು;",
			"4": "<sup>4</sup> ಲೋಕಭೋಗಿಗಳ ಹಾಸ್ಯದಿಂದಲೂ ಗರ್ವಿಷ್ಠರ ನಿಂದೆಯಿಂದಲೂ ನಮ್ಮ ಮನಸ್ಸು ಬೇಸತ್ತುಹೋಯಿತು."
		},
		"124": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ನಮಗಿಲ್ಲದಿದ್ದರೆ ನಮ್ಮ ಗತಿ ಏನಾಗುತ್ತಿತ್ತೆಂದು ಇಸ್ರಾಯೇಲ್ಯರೇ ಹೇಳಲಿ.",
			"2": "<sup>2</sup> ನರರು ನಮಗೆ ವಿರೋಧವಾಗಿ ಎದ್ದಾಗ ಯೆಹೋವನು ನಮಗಿಲ್ಲದಿದ್ದರೆ",
			"3": "<sup>3</sup> ನಿಶ್ಚಯವಾಗಿ ಅವರು ಕೋಪದಿಂದ ಉರಿಗೊಂಡು ನಮ್ಮನ್ನು ಜೀವಸಹಿತ ನುಂಗಿಬಿಡುತ್ತಿದ್ದರು;",
			"4": "<sup>4</sup> ನಿಶ್ಚಯವಾಗಿ ಪ್ರವಾಹವು ನಮ್ಮನ್ನು ಬಡುಕೊಂಡು ಹೋಗುತ್ತಿತ್ತು; ನದಿಯು ನಮ್ಮನ್ನು ಮುಣುಗಿಸುತ್ತಿತ್ತು;",
			"5": "<sup>5</sup> ಮೀರಿದ ಪ್ರವಾಹವು ನಮ್ಮನ್ನು ಮುಣುಗಿಸಿಬಿಡುತ್ತಿತ್ತು.",
			"6": "<sup>6</sup> ಯೆಹೋವನಿಗೆ ಕೊಂಡಾಟವಾಗಲಿ; ಆತನು ನಮ್ಮನ್ನು ಅವರ ಹಲ್ಲುಗಳಿಗೆ ಕೊಳ್ಳೆಕೊಡಲಿಲ್ಲ.",
			"7": "<sup>7</sup> ನಮ್ಮ ಜೀವವು ಬೇಟೆಗಾರನ ಬಲೆಯಿಂದ ತಪ್ಪಿಸಿಕೊಂಡ ಪಕ್ಷಿಯಂತಿದೆ; ಬಲೆಯು ಹರಿದುಹೋಯಿತು, ನಾವು ತಪ್ಪಿಸಿಕೊಂಡೆವು.",
			"8": "<sup>8</sup> ಭೂಮ್ಯಾಕಾಶಗಳನ್ನು ಉಂಟುಮಾಡಿದ ಯೆಹೋವನ ನಾಮದಲ್ಲಿ ನಮಗೆ ರಕ್ಷಣೆಯಾಗುವದು."
		},
		"125": {
			"1": "<sup>1</sup> ಯೆಹೋವನಲ್ಲಿ ಭರವಸವಿಡುವವರು ಚೀಯೋನ್ ಪರ್ವತದ ಹಾಗೆ ಇದ್ದಾರೆ; ಅದು ಕದಲುವದಿಲ್ಲ, ಸದಾ ಸ್ಥಿರವಾಗಿರುತ್ತದೆ.",
			"2": "<sup>2</sup> ಪರ್ವತಗಳು ಯೆರೂಸಲೇಮಿನ ಸುತ್ತಲೂ ಹೇಗೋ ಹಾಗೆಯೇ ಯೆಹೋವನು ಇಂದಿನಿಂದ ಸದಾಕಾಲವೂ ತನ್ನ ಜನರ ಸುತ್ತಲೂ ಇರುವನು.",
			"3": "<sup>3</sup> ದುಷ್ಟರ ದಂಡಾಧಿಕಾರವು ನೀತಿವಂತರ ಸ್ವಾಸ್ತ್ಯದಲ್ಲಿ ಉಳಿಯುವದೇ ಇಲ್ಲ; ಉಳಿದರೆ ನೀತಿವಂತರೂ ಅಕ್ರಮಕ್ಕೆ ಕೈ ಹಚ್ಚಾರು.",
			"4": "<sup>4</sup> ಯೆಹೋವನೇ, ಒಳ್ಳೆಯವರೂ ಯಥಾರ್ಥಚಿತ್ತರೂ ಆಗಿರುವವರಿಗೆ ಉಪಕಾರಮಾಡು.",
			"5": "<sup>5</sup> ಯೆಹೋವನು ಡೊಂಕುದಾರಿಹಿಡಿದವರನ್ನು ಪಾತಕಿಗಳ ಗತಿಗೆ ಹೋಗಮಾಡಲಿ. ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಶುಭವುಂಟಾಗಲಿ."
		},
		"126": {
			"1": "<sup>1</sup> ಸೆರೆಯಲ್ಲಿದ್ದ ನಮ್ಮನ್ನು ಯೆಹೋವನು ತಿರಿಗಿ ಚೀಯೋನಿಗೆ ಬರಮಾಡಿದಾಗ ನಾವು ಕನಸುಕಂಡವರಂತಿದ್ದೆವು.",
			"2": "<sup>2</sup> ಆಗ ನಮ್ಮ ಬಾಯಿ ಬಲು ನಗೆಯಿಂದಲೂ ನಮ್ಮ ನಾಲಿಗೆ ಹರ್ಷಗೀತದಿಂದಲೂ ತುಂಬಿದವು. ಅನ್ಯಜನರು - ಯೆಹೋವನು ಇವರಿಗೋಸ್ಕರ ಮಹತ್ಕಾರ್ಯಗಳನ್ನು ಮಾಡಿದ್ದಾನೆಂದು ತಮ್ಮೊಳಗೆ ಮಾತಾಡಿಕೊಳ್ಳುತ್ತಿದ್ದರು.",
			"3": "<sup>3</sup> ಯೆಹೋವನು ನಮಗೋಸ್ಕರ ಮಹತ್ಕಾರ್ಯಗಳನ್ನು ಮಾಡಿದ್ದು ನಿಜ; ಆದದರಿಂದ ನಾವು ಸಂತೋಷವುಳ್ಳವರಾಗಿದ್ದೇವೆ.",
			"4": "<sup>4</sup> ಯೆಹೋವನೇ, ನೀನು ದಕ್ಷಿಣದೇಶದ ಹಳ್ಳಗಳನ್ನೋ ಎಂಬಂತೆ ಸೆರೆಯಲ್ಲಿ ಉಳಿದಿರುವ ನಮ್ಮವರನ್ನೂ ತಿರಿಗಿ ಬರಮಾಡು",
			"5": "<sup>5</sup> ಅಳುತ್ತಾ ಬಿತ್ತುವವರು ಹಾಡುತ್ತಾ ಕೊಯ್ಯುವರು.",
			"6": "<sup>6</sup> ದುಃಖಿಸುತ್ತಾ ಬೀಜವನ್ನು ತೆಗೆದುಕೊಂಡು ಹೋಗುವವನು ಹರ್ಷಿಸುತ್ತಾ ಸಿವುಡುಗಳನ್ನು ಹೊತ್ತುಕೊಂಡು ಬರುವನು."
		},
		"127": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಮನೇ ಕಟ್ಟದಿದ್ದರೆ ಅದನ್ನು ಕಟ್ಟುವವರು ಕಷ್ಟಪಡುವದು ವ್ಯರ್ಥ; ಯೆಹೋವನು ಪಟ್ಟಣವನ್ನು ಕಾಯದಿದ್ದರೆ ಕಾವಲುಗಾರರು ಅದನ್ನು ಕಾಯುವದು ವ್ಯರ್ಥ.",
			"2": "<sup>2</sup> ಬೆಳಗಾಗುವದಕ್ಕಿಂತ ಮುಂಚೆ ಏಳುವವರೇ, ಹೊತ್ತು ಮೀರಿ ಮಲಗುವವರೇ, ನೀವು ಆಹಾರಕ್ಕಾಗಿ ಇಷ್ಟು ಕಷ್ಟಪಡುವದು ವ್ಯರ್ಥ; ಆತನು ಅದನ್ನು ತನ್ನ ಪ್ರಿಯರಿಗೆ ನಿದ್ರೆಯಲ್ಲಿಯೂ ಕೊಡುತ್ತಾನೆ.",
			"3": "<sup>3</sup> ಪುತ್ರಸಂತಾನವು ಯೆಹೋವನಿಂದ ಬಂದ ಸ್ವಾಸ್ತ್ಯವು; ಗರ್ಭಫಲವು ಆತನ ಬಹುಮಾನವೇ.",
			"4": "<sup>4</sup> ಯೌವನದಲ್ಲಿ ಹುಟ್ಟಿದ ಮಕ್ಕಳು ಯುದ್ಧವೀರನ ಕೈಯಲ್ಲಿರುವ ಅಂಬುಗಳಂತಿದ್ದಾರೆ;",
			"5": "<sup>5</sup> ಇವುಗಳಿಂದ ತನ್ನ ಬತ್ತಳಿಕೆಯನ್ನು ತುಂಬಿದವನು ಧನ್ಯನು. ಊರುಬಾಗಲಲ್ಲಿ ವೈರಿಗಳ ಸಂಗಡ ವ್ಯಾಜ್ಯವಾಡುವಾಗ ಅಂಥವರು ಅಪಮಾನಹೊಂದುವದಿಲ್ಲ."
		},
		"128": {
			"1": "<sup>1</sup> ಯೆಹೋವನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವನಾಗಿ ಆತನ ಮಾರ್ಗಗಳಲ್ಲಿ ನಡೆಯುವವನು ಧನ್ಯನು.",
			"2": "<sup>2</sup> ನಿನ್ನ ಕಷ್ಟಾರ್ಜಿತವನ್ನು ನೀನೇ ಅನುಭವಿಸುವಿ; ನೀನು ಧನ್ಯನು, ನಿನಗೆ ಶುಭವಿರುವದು.",
			"3": "<sup>3</sup> ಅಂತಃಪುರದಲ್ಲಿರುವ ನಿನ್ನ ಹೆಂಡತಿಯು ಫಲಭರಿತವಾದ ದ್ರಾಕ್ಷಾಲತೆಯಂತಿರುವಳು; ನಿನ್ನ ಸಂಗಡ ಊಟದ ಮಣೆಯ ಸುತ್ತಲೂ ಕೂತುಕೊಳ್ಳುವ ನಿನ್ನ ಮಕ್ಕಳು ಎಣ್ಣೇಮರದ ಸಸಿಗಳಂತಿರುವರು.",
			"4": "<sup>4</sup> ಇಗೋ, ಯೆಹೋವನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳ ಪುರುಷನು ಈ ರೀತಿಯಾಗಿ ಆಶೀರ್ವಾದಹೊಂದುವನು.",
			"5": "<sup>5</sup> ಚೀಯೋನಿನಲ್ಲಿರುವ ಯೆಹೋವನು ನಿನ್ನನ್ನು ಆಶೀರ್ವದಿಸಲಿ; ಜೀವಮಾನವೆಲ್ಲಾ ಯೆರೂಸಲೇಮಿನ ಸೌಭಾಗ್ಯವನ್ನು ನೋಡುವವನಾಗು.",
			"6": "<sup>6</sup> ಮಕ್ಕಳ ಮಕ್ಕಳನ್ನು ಕಾಣುವವನಾಗು. ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಶುಭವಾಗಲಿ."
		},
		"129": {
			"1": "<sup>1</sup> ನಮ್ಮ ಯೌವನಾರಭ್ಯ ಶತ್ರುಗಳು ನಮ್ಮನ್ನು ಹಲವು ಸಾರಿ ಬಾಧಿಸಿದ್ದಾರೆಂದು ಇಸ್ರಾಯೇಲು ಹೇಳಲಿ.",
			"2": "<sup>2</sup> ನಾವು ಯೌವನಾರಭ್ಯ ಎಷ್ಟೋ ಸಾರಿ ಬಾಧೆಹೊಂದಿದರೂ ಅವರು ನಮ್ಮನ್ನು ಜಯಿಸಲಿಲ್ಲ.",
			"3": "<sup>3</sup> ಉಳುವವರು ನಮ್ಮ ಬೆನ್ನಿನ ಮೇಲೆ ಉತ್ತುತ್ತು ಉದ್ದವಾದ ಗೆರೆಗಳನ್ನು ಮಾಡಿದರು;",
			"4": "<sup>4</sup> ಆದರೆ ನೀತಿಸ್ವರೂಪನಾದ ಯೆಹೋವನು ದುಷ್ಟರು ಹಾಕಿದ ಬಂಧನಗಳನ್ನು ಛೇಧಿಸಿಬಿಟ್ಟನು.",
			"5": "<sup>5</sup> ಚೀಯೋನನ್ನು ದ್ವೇಷಿಸುವವರೆಲ್ಲರು ಅವಮಾನದಿಂದ ಹಿಂದಿರುಗಲಿ.",
			"6": "<sup>6</sup> ಅವರು ಮಾಳಿಗೆಯ ಮೇಲಣ ಹುಲ್ಲಿನಂತಾಗಲಿ; ಅದು ಹೂವುಬಿಡುವದಕ್ಕೆ ಮೊದಲೇ ಒಣಗಿಹೋಗುತ್ತದೆ;",
			"7": "<sup>7</sup> ಅದನ್ನು ಕೊಯ್ಯುವವನ ಹಿಡಿಯೂ ಸಿವುಡುಕಟ್ಟುವವನ ಉಡಿಲೂ ತುಂಬುವದಿಲ್ಲ.",
			"8": "<sup>8</sup> ಹಾದುಹೋಗುವವರು - ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಆಶೀರ್ವದಿಸಲಿ ಎಂದೂ ಯೆಹೋವನಾಮದಲ್ಲಿ ನಿಮ್ಮನ್ನು ಆಶೀರ್ವದಿಸುತ್ತೇವೆ ಎಂದೂ ಹೇಳುವದಿಲ್ಲ."
		},
		"130": {
			"1": "<sup>1</sup> ಯೆಹೋವನೇ, ಜಲಾಗಾಧದಲ್ಲಿದ್ದು ನಿನ್ನನ್ನು ಕೂಗಿಕೊಳ್ಳುತ್ತೇನೆ.",
			"2": "<sup>2</sup> ಕರ್ತನೇ, ನನ್ನ ಮೊರೆಯನ್ನು ಕೇಳು; ನನ್ನ ವಿಜ್ಞಾಪನ ಶಬ್ದಕ್ಕೆ ಕಿವಿದೆರೆ.",
			"3": "<sup>3</sup> ಕರ್ತನೇ, ಯಾಹುವೇ, ನೀನು ಪಾಪಗಳನ್ನು ಎಣಿಸುವದಾದರೆ ನಿನ್ನ ಮುಂದೆ ಯಾರು ನಿಂತಾರು?",
			"4": "<sup>4</sup> ನೀನು ಪಾಪವನ್ನು ಕ್ಷಮಿಸುವವನಾದ್ದರಿಂದ ಮನುಷ್ಯರ ಭಯಭಕ್ತಿಗೆ ನೀನೇ ಪಾತ್ರನು.",
			"5": "<sup>5</sup> ನಾನು ಯೆಹೋವನನ್ನು ಎದುರುನೋಡುತ್ತೇನೆ; ನನ್ನ ಪ್ರಾಣವು ಆತನನ್ನು ಕಾದುಕೊಂಡಿದೆ; ಆತನ ನುಡಿಯನ್ನು ನಿರೀಕ್ಷಿಸಿಕೊಂಡಿದ್ದೇನೆ.",
			"6": "<sup>6</sup> ಕಾವಲುಗಾರರು ಬೆಳಗಾಗುವದನ್ನು ಮುನ್ನೋಡುತ್ತಾರಲ್ಲಾ; ಕಾವಲುಗಾರರು ಬೆಳಗಾಗುವದನ್ನು ಮುನ್ನೋಡುವದಕ್ಕಿಂತ ವಿಶೇಷವಾಗಿ ನನ್ನ ಅಂತರಾತ್ಮವು ಕರ್ತನನ್ನು ಮುನ್ನೋಡುತ್ತದೆ.",
			"7": "<sup>7</sup> ಇಸ್ರಾಯೇಲೇ, ಯೆಹೋವನನ್ನು ಮುಂಗಾದಿರು; ಯೆಹೋವನು ಕೃಪಾಸಂಪನ್ನನು; ಪೂರ್ಣವಿಮೋಚನೆಯು ಆತನಿಂದಲೇ ದೊರಕುವದು.",
			"8": "<sup>8</sup> ಆತನೇ ಇಸ್ರಾಯೇಲನ್ನು ಅದರ ಸಕಲಪಾಪಗಳಿಂದ ವಿಮೋಚಿಸುವನು."
		},
		"131": {
			"1": "<sup>1</sup> ಯೆಹೋವನೇ, ನನ್ನ ಎದೆಯಲ್ಲಿ ಹಮ್ಮಿಲ್ಲ; ನನಗೆ ಸೊಕ್ಕಿನ ಕಣ್ಣಿಲ್ಲ; ಅಸಾಧ್ಯಕಾರ್ಯಗಳಲ್ಲಿ ಕೈಹಾಕುವದಿಲ್ಲ.",
			"2": "<sup>2</sup> ನಿಜವಾಗಿ ನನ್ನ ಮನಸ್ಸನ್ನು ಸಮಾಧಾನಪಡಿಸಿದ್ದೇನೆ; ತಾಯಿಯ ಬಳಿಯಲ್ಲಿರುವ ಮೊಲೆಬಿಡಿಸಿದ ಕೂಸಿನಂತೆ ನೆಮ್ಮದಿಯಿಂದಿದೆ. ನನ್ನಲ್ಲಿ ನನ್ನ ಮನಸ್ಸು ಮೊಲೆಬಿಡಿಸಿದ ಕೂಸಿನಂತಿದೆ.",
			"3": "<sup>3</sup> ಇಸ್ರಾಯೇಲೇ, ಇಂದಿನಿಂದ ಯುಗಯುಗಕ್ಕೂ ಯೆಹೋವನನ್ನೇ ನಿರೀಕ್ಷಿಸಿಕೊಂಡಿರು."
		},
		"132": {
			"1": "<sup>1</sup> ಯೆಹೋವನೇ, ದಾವೀದನ ಹಿತಕ್ಕೋಸ್ಕರ ಅವನ ಶ್ರಮಗಳನ್ನು ನೆನಪುಮಾಡಿಕೋ.",
			"2": "<sup>2</sup> ಯಾಕೋಬ್ಯರ ಶೂರನಾದ ಯೆಹೋವನಿಗಾಗಿ ಒಂದು ವಾಸಸ್ಥಾನವನ್ನು ಏರ್ಪಡಿಸುವ ತನಕ",
			"3": "<sup>3</sup> ನಾನು ನನ್ನ ಮನೆಯೊಳಗೆ ಪ್ರವೇಶಿಸುವದಿಲ್ಲ, ಮಂಚವನ್ನು ಹತ್ತುವದಿಲ್ಲ,",
			"4": "<sup>4</sup> ಕಣ್ಣುಗಳನ್ನು ಮುಚ್ಚುವದಿಲ್ಲ, ರೆಪ್ಪೆಗಳನ್ನು ಕೂಡಿಸುವದಿಲ್ಲ ಎಂದು",
			"5": "<sup>5</sup> ಅವನು ಯಾಕೋಬ್ಯರ ಶೂರನಾದ ಯೆಹೋವನಿಗೆ ಪ್ರಮಾಣದಿಂದ ಹರಕೆಮಾಡಿಕೊಂಡನಲ್ಲಾ.",
			"6": "<sup>6</sup> ಇಗೋ, ಅದು ಎಫ್ರಾತದಲ್ಲಿರುತ್ತದೆಂದು ಕೇಳಿದೆವು; ಯಹಾರ್ ಮೈದಾನದಲ್ಲಿ ನಮಗೆ ಸಿಕ್ಕಿತು.",
			"7": "<sup>7</sup> ಬನ್ನಿರಿ, ಆತನ ಮಂದಿರಕ್ಕೆ ಹೋಗಿ ಆತನ ಪಾದಪೀಠದ ಮುಂದೆ ಅಡ್ಡಬೀಳೋಣ.",
			"8": "<sup>8</sup> ಯೆಹೋವನೇ, ಎದ್ದು ನಿನ್ನ ಪ್ರತಾಪಯುಕ್ತವಾದ ಮಂಜೂಷದೊಡನೆ ನಿನ್ನ ವಾಸಸ್ಥಾನದಲ್ಲಿ ಸೇರೋಣವಾಗಲಿ.",
			"9": "<sup>9</sup> ನಿನ್ನ ಯಾಜಕರು ನೀತಿಯೆಂಬ ವಸ್ತ್ರವನ್ನು ಹೊದ್ದುಕೊಳ್ಳಲಿ; ನಿನ್ನ ಭಕ್ತರು ಉತ್ಸಾಹಧ್ವನಿಮಾಡಲಿ.",
			"10": "<sup>10</sup> ನಿನ್ನ ಸೇವಕನಾದ ದಾವೀದನನ್ನು ನೆನಪುಮಾಡಿಕೋ; ನಿನ್ನ ಅಭಿಷಿಕ್ತನನ್ನು ತಳ್ಳಿಬಿಡಬೇಡ.",
			"11": "<sup>11</sup> ಯೆಹೋವನು ದಾವೀದನಿಗೆ ಮಾಡಿದ ಪ್ರಮಾಣವು ಸ್ಥಿರವಾಗಿದೆ; ಆತನು ಅದನ್ನು ಬದಲಿಸುವದಿಲ್ಲ - ನಿನ್ನ ಸಂತಾನದವರನ್ನು ನಿನ್ನ ಸಿಂಹಾಸನದ ಮೇಲೆ ಕುಳ್ಳಿರಿಸುವೆನು;",
			"12": "<sup>12</sup> ನಿನ್ನ ಮಕ್ಕಳು ನನ್ನ ನಿಬಂಧನೆಯನ್ನೂ ನಾನು ಆಜ್ಞಾಪಿಸಿದ ಕಟ್ಟಳೆಗಳನ್ನೂ ಕೈಕೊಂಡು ನಡೆಯುವದಾದರೆ ಅವರ ಮಕ್ಕಳು ನಿನ್ನ ಸಿಂಹಾಸನದಲ್ಲಿ ಸದಾಕಾಲ ಕೂತುಕೊಳ್ಳುವರು.",
			"13": "<sup>13</sup> ನಿಶ್ಚಯವಾಗಿ ಯೆಹೋವನು ಚೀಯೋನನ್ನು ಅಪೇಕ್ಷಿಸಿ ತನ್ನ ನಿವಾಸಕ್ಕಾಗಿ ಆರಿಸಿಕೊಂಡಿದ್ದಾನೆ.",
			"14": "<sup>14</sup> ಆತನು - ಇದು ನನ್ನ ಶಾಶ್ವತವಾಸಸ್ಥಾನ; ಇಲ್ಲೇ ಇರುವೆನು; ಇದು ನನಗೆ ಇಷ್ಟವು.",
			"15": "<sup>15</sup> ಇದರ ಆದಾಯವನ್ನು ಆಶೀರ್ವದಿಸುವೆನು; ಇಲ್ಲಿಯ ಬಡವರನ್ನು ಆಹಾರದಿಂದ ತೃಪ್ತಿಗೊಳಿಸುವೆನು;",
			"16": "<sup>16</sup> ಯಾಜಕರಿಗೆ ರಕ್ಷಣೆಯೆಂಬ ವಸ್ತ್ರವನ್ನು ಹೊದಿಸುವೆನು; ಭಕ್ತರು ಉತ್ಸಾಹಧ್ವನಿಮಾಡುವರು.",
			"17": "<sup>17</sup> ಇಲ್ಲಿಯೇ ದಾವೀದನ ಕೊಂಬು ಚಿಗುರುವಂತೆ ಮಾಡುವೆನು; ನನ್ನ ಅಭಿಷಿಕ್ತನ ದೀಪವು ಉರಿಯುತ್ತಲೇ ಇರಬೇಕೆಂದು ನೇಮಿಸಿದ್ದೇನೆ.",
			"18": "<sup>18</sup> ಅವನ ವೈರಿಗಳನ್ನು ನಾಚಿಕೆಯೆಂಬ ವಸ್ತ್ರದಿಂದ ಹೊದಿಸುವೆನು; ಆದರೆ ಅವನ ಮೇಲೆ ಕಿರೀಟವು ಶೋಭಿಸುತ್ತಿರುವದು."
		},
		"133": {
			"1": "<sup>1</sup> ಆಹಾ, ಸಹೋದರರು ಒಂದಾಗಿರುವದು ಎಷ್ಟೋ ಒಳ್ಳೇದು, ಎಷ್ಟೋ ರಮ್ಯವಾದದ್ದು!",
			"2": "<sup>2</sup> ಅದು ಆರೋನನ ತಲೆಯ ಮೇಲೆ ಹಾಕಲ್ಪಟ್ಟು ಅವನ ಗಡ್ಡದ ಮೇಲೆಯೂ ಅಲ್ಲಿಂದ ಅವನ ಅಂಗಿಗಳ ಕೊರಳಪಟ್ಟಿಯವರೆಗೂ ಹರಿದುಬರುವ ಶ್ರೇಷ್ಠತೈಲದಂತೆಯೂ",
			"3": "<sup>3</sup> ಹೆರ್ಮೋನ್‍ಪರ್ವತದಲ್ಲಿ ಹುಟ್ಟಿ ಚೀಯೋನ್‍ಪರ್ವತದ ಮೇಲೆ ಬೀಳುವ ಮಂಜಿನಂತೆಯೂ ಇದೆ. ಅಲ್ಲಿ ಆಶೀರ್ವಾದವೂ ಜೀವವೂ ಸದಾಕಾಲ ಇರಬೇಕೆಂದು ಯೆಹೋವನು ಆಜ್ಞಾಪಿಸಿದ್ದಾನೆ."
		},
		"134": {
			"1": "<sup>1</sup> ಎಲೈ ರಾತ್ರಿಯಲ್ಲಿ ಯೆಹೋವನ ಮಂದಿರದಲ್ಲಿರುವ ಆತನ ಸರ್ವಸೇವಕರೇ, ಯೆಹೋವನನ್ನು ಕೊಂಡಾಡಿರಿ.",
			"2": "<sup>2</sup> ಮಹಾಪರಿಶುದ್ಧ ಸ್ಥಳದ ಕಡೆಗೆ ಕೈಯೆತ್ತಿ ಯೆಹೋವನನ್ನು ಕೊಂಡಾಡಿರಿ.",
			"3": "<sup>3</sup> ಭೂಮ್ಯಾಕಾಶಗಳನ್ನು ಉಂಟುಮಾಡಿದ ಯೆಹೋವನು ಚೀಯೋನಿನೊಳಗಿಂದ ನಿನ್ನನ್ನು ಆಶೀರ್ವದಿಸಲಿ."
		},
		"135": {
			"1": "<sup>1</sup> ಯಾಹುವಿಗೆ ಸ್ತೋತ್ರ! ಯೆಹೋವನಾಮವನ್ನು ಸ್ತುತಿಸಿರಿ. ಯೆಹೋವನ ಸೇವಕರೇ,",
			"2": "<sup>2</sup> ಯೆಹೋವನ ಮಂದಿರದಲ್ಲಿಯೂ ನಮ್ಮ ದೇವರ ಆಲಯದ ಅಂಗಳಗಳಲ್ಲಿಯೂ ಸೇವೆಮಾಡುವವರೇ, ಆತನನ್ನು ಕೀರ್ತಿಸಿರಿ.",
			"3": "<sup>3</sup> ಯಾಹುವಿಗೆ ಸ್ತೋತ್ರ! ಯೆಹೋವನು ಒಳ್ಳೆಯವನು. ಆತನ ನಾಮವನ್ನು ಕೊಂಡಾಡಿರಿ; ಅದು ಮನೋಹರವಾಗಿದೆ.",
			"4": "<sup>4</sup> ಯಾಹುವು ಯಾಕೋಬನ ವಂಶದವರಾದ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಸ್ವಕೀಯಜನವನ್ನಾಗಿ ಆದುಕೊಂಡನಲ್ಲಾ.",
			"5": "<sup>5</sup> ಯೆಹೋವನು ದೊಡ್ಡವನೆಂದೂ ನಮ್ಮ ಕರ್ತನು ಎಲ್ಲಾ ದೇವರುಗಳಿಗಿಂತ ಹೆಚ್ಚಿನವನೆಂದೂ ತಿಳಿದಿದ್ದೇನೆ.",
			"6": "<sup>6</sup> ಯೆಹೋವನು ಭೂಮಿಯಲ್ಲಿಯೂ ಆಕಾಶದಲ್ಲಿಯೂ ಸಮುದ್ರಗಳಲ್ಲಿಯೂ ಬೇರೆ ಎಲ್ಲಾ ಜಲರಾಶಿಗಳಲ್ಲಿಯೂ ತನಗೆ ಬೇಕಾದದ್ದನ್ನು ಮಾಡುತ್ತಾನೆ.",
			"7": "<sup>7</sup> ಆತನು ಭೂಮಿಯ ಕಟ್ಟಕಡೆಯಿಂದ ಮೋಡಗಳನ್ನು ಏರಮಾಡುತ್ತಾನೆ; ಮಳೆಗೋಸ್ಕರ ಮಿಂಚನ್ನು ಹೊಳೆಯಮಾಡುತ್ತಾನೆ; ತನ್ನ ಉಗ್ರಾಣದಿಂದ ಗಾಳಿಯನ್ನು ಬೀಸಮಾಡುತ್ತಾನೆ.",
			"8": "<sup>8</sup> ಐಗುಪ್ತದ ಮನುಷ್ಯರು ಮೊದಲುಗೊಂಡು ಪಶುಗಳವರೆಗೂ ಚೊಚ್ಚಲುಗಳನ್ನೆಲ್ಲಾ ಸಂಹರಿಸಿದನು.",
			"9": "<sup>9</sup> ಐಗುಪ್ತವೇ, ಆತನು ನಿನ್ನ ಮಧ್ಯದಲ್ಲಿ ಫರೋಹನಿಗೂ ಅವನ ಸೇವಕರಿಗೂ ವಿರೋಧವಾಗಿ ಅದ್ಭುತಗಳನ್ನೂ ಮಹತ್ಕಾರ್ಯಗಳನ್ನೂ ನಡಿಸಿದನು.",
			"10": "<sup>10</sup> ಆತನು ದೊಡ್ಡ ಜನಾಂಗಗಳನ್ನು ಹೊಡೆದುಬಿಟ್ಟನು; ಬಲಿಷ್ಠರಾಜರನ್ನು ಕೊಂದುಹಾಕಿದನು.",
			"11": "<sup>11</sup> ಅವರಲ್ಲಿ ಅಮೋರಿಯರ ಅರಸನಾದ ಸೀಹೋನನೂ ಬಾಷಾನಿನ ಅರಸನಾದ ಓಗನೂ ಇದ್ದರು. ಕಾನಾನ್‍ದೇಶದ ಎಲ್ಲಾ ರಾಜ್ಯಗಳನ್ನು ನಿರ್ಮೂಲ ಮಾಡಿದನು.",
			"12": "<sup>12</sup> ಅವರ ದೇಶವನ್ನು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಕೊಟ್ಟನು; ಆತನ ಪ್ರಜೆಗೆ ಅದು ಸ್ವಾಸ್ತ್ಯವಾಯಿತು.",
			"13": "<sup>13</sup> ಯೆಹೋವನೇ, ನಿನ್ನ ನಾಮವು ಶಾಶ್ವತವಾದದ್ದು; ಯೆಹೋವನೇ, ನೀನು ತಲತಲಾಂತರಕ್ಕೂ ಸ್ಮರಿಸಲ್ಪಡುತ್ತೀ.",
			"14": "<sup>14</sup> ನಿಜವಾಗಿ ಯೆಹೋವನು ತನ್ನ ಪ್ರಜೆಯ ನ್ಯಾಯವನ್ನು ಸ್ಥಾಪಿಸುವನು; ತನ್ನ ಸೇವಕರನ್ನು ಕನಿಕರಿಸುವನು.",
			"15": "<sup>15</sup> ಅನ್ಯಜನಗಳ ವಿಗ್ರಹಗಳು ಬೆಳ್ಳಿಬಂಗಾರದವುಗಳೇ; ಅವು ಮನುಷ್ಯರ ಕೈಕೆಲಸವಷ್ಟೇ.",
			"16": "<sup>16</sup> ಅವುಗಳಿಗೆ ಬಾಯಿದ್ದರೂ ಮಾತಾಡುವದಿಲ್ಲ; ಕಣ್ಣಿದ್ದರೂ ನೋಡುವದಿಲ್ಲ;",
			"17": "<sup>17</sup> ಕಿವಿಯಿದ್ದರೂ ಕೇಳುವದಿಲ್ಲ; ಇದಲ್ಲದೆ ಅವುಗಳ ಬಾಯಲ್ಲಿ ಶ್ವಾಸವೇ ಇಲ್ಲ.",
			"18": "<sup>18</sup> ಅವುಗಳನ್ನು ಮಾಡುವವರೂ ಅವುಗಳಲ್ಲಿ ಭರವಸವಿಡುವವರೂ ಅವುಗಳಂತೆಯೇ.",
			"19": "<sup>19</sup> ಇಸ್ರಾಯೇಲನ ಮನೆತನದವರೇ, ಯೆಹೋವನನ್ನು ಕೊಂಡಾಡಿರಿ; ಆರೋನನ ಮನೆತನದವರೇ, ಯೆಹೋವನನ್ನು ಕೊಂಡಾಡಿರಿ;",
			"20": "<sup>20</sup> ಲೇವಿಯ ಮನೆತನದವರೇ, ಯೆಹೋವನನ್ನು ಕೊಂಡಾಡಿರಿ; ಯೆಹೋವನ ಭಕ್ತರೇ, ಯೆಹೋವನನ್ನು ಕೊಂಡಾಡಿರಿ.",
			"21": "<sup>21</sup> ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ವಾಸಿಸುವ ಯೆಹೋವನ ಕೀರ್ತಿಯು ಚೀಯೋನಿನಿಂದ ಹೊರಗೂ ಹಬ್ಬಲಿ. ಯಾಹುವಿಗೆ ಸ್ತೋತ್ರ!"
		},
		"136": {
			"1": "<sup>1</sup> ಯೆಹೋವನಿಗೆ ಕೃತಜ್ಞತಾಸ್ತುತಿಮಾಡಿರಿ; ಆತನು ಒಳ್ಳೆಯವನು. ಆತನ ಕೃಪೆಯು ಶಾಶ್ವತವಾದದ್ದು.",
			"2": "<sup>2</sup> ದೇವಾಧಿದೇವನಿಗೆ ಕೃತಜ್ಞತಾಸ್ತುತಿಮಾಡಿರಿ; ಆತನ ಕೃಪೆಯು ಶಾಶ್ವತವಾದದ್ದು.",
			"3": "<sup>3</sup> ಕರ್ತರ ಕರ್ತನಿಗೆ ಕೃತಜ್ಞತಾಸ್ತುತಿಮಾಡಿರಿ; ಆತನ ಕೃಪೆಯು ಶಾಶ್ವತವಾದದ್ದು.",
			"4": "<sup>4</sup> ಮಹತ್ಕಾರ್ಯಗಳನ್ನು ನಡಿಸುವದಕ್ಕೆ ಆತನೊಬ್ಬನೇ ಶಕ್ತನು; ಆತನ ಕೃಪೆಯು ಶಾಶ್ವತವಾದದ್ದು.",
			"5": "<sup>5</sup> ಆತನು ಆಕಾಶವನ್ನು ಜ್ಞಾನದಿಂದ ನಿರ್ಮಿಸಿದ್ದಾನೆ; ಆತನ ಕೃಪೆಯು ಶಾಶ್ವತವಾದದ್ದು.",
			"6": "<sup>6</sup> ಆತನು ಭೂಮಿಯನ್ನು ಜಲರಾಶಿಗಳ ಮೇಲೆ ಹಾಸಿದ್ದಾನೆ; ಆತನ ಕೃಪೆಯು ಶಾಶ್ವತವಾದದ್ದು.",
			"7": "<sup>7</sup> ಆತನು ಮಹಾಜ್ಯೋತಿರ್ಮಂಡಲಗಳನ್ನು ಸೃಷ್ಟಿಸಿದ್ದಾನೆ; ಆತನ ಕೃಪೆಯು ಶಾಶ್ವತವಾದದ್ದು.",
			"8": "<sup>8</sup> ಸೂರ್ಯನು ಹಗಲನ್ನಾಳುತ್ತಾನೆ; ಆತನ ಕೃಪೆಯು ಶಾಶ್ವತವಾದದ್ದು.",
			"9": "<sup>9</sup> ಚಂದ್ರನಕ್ಷತ್ರಗಳು ರಾತ್ರಿಯನ್ನಾಳುತ್ತವೆ; ಆತನ ಕೃಪೆಯು ಶಾಶ್ವತವಾದದ್ದು.",
			"10": "<sup>10</sup> ಆತನು ಐಗುಪ್ತ್ಯರ ಚೊಚ್ಚಲಮಕ್ಕಳನ್ನು ಸಂಹರಿಸಿದನು; ಆತನ ಕೃಪೆಯು ಶಾಶ್ವತವಾದದ್ದು.",
			"11": "<sup>11</sup> ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಅವರ ಮಧ್ಯದಿಂದ ಹೊರತಂದನು; ಆತನ ಕೃಪೆಯು ಶಾಶ್ವತವಾದದ್ದು.",
			"12": "<sup>12</sup> ತನ್ನ ಭುಜಬಲ, ಶಿಕ್ಷಾಹಸ್ತ ಇವುಗಳಿಂದ ಅವರನ್ನು ಬಿಡಿಸಿದನು; ಆತನ ಕೃಪೆಯು ಶಾಶ್ವತವಾದದ್ದು.",
			"13": "<sup>13</sup> ಆತನು ಕೆಂಪುಸಮುದ್ರವನ್ನು ಭೇದಿಸಿದನು; ಆತನ ಕೃಪೆಯು ಶಾಶ್ವತವಾದದ್ದು.",
			"14": "<sup>14</sup> ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಅದರ ಮಧ್ಯದಲ್ಲಿಯೇ ನಡಿಸಿದನು; ಆತನ ಕೃಪೆಯು ಶಾಶ್ವತವಾದದ್ದು.",
			"15": "<sup>15</sup> ಫರೋಹನನ್ನೂ ಅವನ ಸೈನ್ಯವನ್ನೂ ಕೆಂಪುಸಮುದ್ರದಲ್ಲಿ ಕೆಡವಿಬಿಟ್ಟನು; ಆತನ ಕೃಪೆಯು ಶಾಶ್ವತವಾದದ್ದು.",
			"16": "<sup>16</sup> ಆತನು ತನ್ನ ಪ್ರಜೆಯನ್ನು ಅರಣ್ಯದೊಳಗೆ ಕರತಂದನು; ಆತನ ಕೃಪೆಯು ಶಾಶ್ವತವಾದದ್ದು.",
			"17": "<sup>17</sup> ದೊಡ್ಡ ಅರಸರನ್ನು ಹೊಡೆದನು; ಆತನ ಕೃಪೆಯು ಶಾಶ್ವತವಾದದ್ದು.",
			"18": "<sup>18</sup> ಶ್ರೇಷ್ಠರಾಜರನ್ನು ಸಂಹರಿಸಿದನು; ಆತನ ಕೃಪೆಯು ಶಾಶ್ವತವಾದದ್ದು.",
			"19": "<sup>19</sup> ಅಮೋರಿಯರ ಅರಸನಾದ ಸೀಹೋನನು ಅವರಲ್ಲೊಬ್ಬನು; ಆತನ ಕೃಪೆಯು ಶಾಶ್ವತವಾದದ್ದು.",
			"20": "<sup>20</sup> ಬಾಷಾನಿನ ಅರಸನಾದ ಓಗನು ಇನ್ನೊಬ್ಬನು; ಆತನ ಕೃಪೆಯು ಶಾಶ್ವತವಾದದ್ದು.",
			"21": "<sup>21</sup> ಅವರ ದೇಶವನ್ನು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಕೊಟ್ಟನು; ಆತನ ಕೃಪೆಯು ಶಾಶ್ವತವಾದದ್ದು.",
			"22": "<sup>22</sup> ಆತನ ಸೇವಕರಿಗೆ ಅದು ಸ್ವಾಸ್ತ್ಯವಾಯಿತು; ಆತನ ಕೃಪೆಯು ಶಾಶ್ವತವಾದದ್ದು.",
			"23": "<sup>23</sup> ನಾವು ದೀನಾವಸ್ಥೆಯಲ್ಲಿದ್ದಾಗ ನಮ್ಮನ್ನು ನೆನಪುಮಾಡಿಕೊಂಡನು; ಆತನ ಕೃಪೆಯು ಶಾಶ್ವತವಾದದ್ದು.",
			"24": "<sup>24</sup> ನಮ್ಮ ಶತ್ರುಗಳಿಂದ ನಮ್ಮನ್ನು ಬಿಡಿಸಿದನು; ಆತನ ಕೃಪೆಯು ಶಾಶ್ವತವಾದದ್ದು.",
			"25": "<sup>25</sup> ಎಲ್ಲಾ ಜೀವಿಗಳಿಗೂ ಆಹಾರಕೊಡುವವನು ಆತನೇ; ಆತನ ಕೃಪೆಯು ಶಾಶ್ವತವಾದದ್ದು.",
			"26": "<sup>26</sup> ಪರಲೋಕದಲ್ಲಿರುವ ದೇವರಿಗೆ ಕೃತಜ್ಞತಾಸ್ತುತಿ ಮಾಡಿರಿ; ಆತನ ಕೃಪೆಯು ಶಾಶ್ವತವಾದದ್ದು."
		},
		"137": {
			"1": "<sup>1</sup> ನಾವು ಬಾಬೆಲ್ ದೇಶದ ನದಿಗಳ ಬಳಿಯಲ್ಲಿ ಕೂತುಕೊಂಡು ಚೀಯೋನನ್ನು ನೆನಸಿ ಅತ್ತೆವು.",
			"2": "<sup>2</sup> ಆ ದೇಶದ ನೀರವಂಜಿ ಮರಗಳಿಗೆ ನಮ್ಮ ಕಿನ್ನರಿಗಳನ್ನು ತೂಗಹಾಕಿದೆವು.",
			"3": "<sup>3</sup> ನಮ್ಮನ್ನು ಸೆರೆಹಿಡಿದು ಪೀಡಿಸುತ್ತಿದ್ದವರು ನಮಗೆ - ನೀವು ಚೀಯೋನಿನ ಗೀತಗಳಲ್ಲಿ ಒಂದನ್ನು ನಮ್ಮ ವಿನೋದಕ್ಕಾಗಿ ಹಾಡಿರಿ ಎಂದು ಹೇಳುತ್ತಿದ್ದರು.",
			"4": "<sup>4</sup> ನಾವು ಪರದೇಶದಲ್ಲಿ ಯೆಹೋವಗೀತಗಳನ್ನು ಹಾಡುವದು ಹೇಗೆ?",
			"5": "<sup>5</sup> ಯೆರೂಸಲೇಮೇ, ನಾನು ನಿನ್ನನ್ನು ಮರೆತುಬಿಟ್ಟರೆ ನನ್ನ ಬಲಗೈ ಮರೆತುಹೋಗಲಿ.",
			"6": "<sup>6</sup> ಯೆರೂಸಲೇಮೇ, ನಾನು ನಿನ್ನನ್ನು ನೆನಸದಿದ್ದರೆ, ಎಲ್ಲಾದಕ್ಕಿಂತ ಹೆಚ್ಚಾಗಿ ನಾನು ನಿನ್ನಲ್ಲಿ ಆನಂದಿಸದಿದ್ದರೆ, ನನ್ನ ನಾಲಿಗೆಯು ಸೇದಿಹೋಗಲಿ.",
			"7": "<sup>7</sup> ಯೆಹೋವನೇ, ಎದೋಮ್ಯರ ಹಾನಿಗಾಗಿ ಯೆರೂಸಲೇಮಿನ ನಾಶನದಿನವನ್ನು ನೆನಪುಮಾಡಿಕೋ. ಅವರು - ಅದನ್ನು ಹಾಳುಮಾಡಿರಿ, ಅಸ್ತಿವಾರಸಹಿತ ಹಾಳುಮಾಡಿರಿ ಎಂದು ಹೇಳಿದರಲ್ಲಾ.",
			"8": "<sup>8</sup> ಹಾಳಾಗುವದಕ್ಕಿರುವ ಬಾಬೆಲ್ ಪಟ್ಟಣವೇ, ನೀನು ನಮಗೆ ಮಾಡಿದ್ದಕ್ಕೆ ಮುಯ್ಯಿಸಲ್ಲಿಸುವವನು ಧನ್ಯನು.",
			"9": "<sup>9</sup> ನಿನ್ನ ಮಕ್ಕಳನ್ನು ಹಿಡಿದು ಬಂಡೆಗೆ ಅಪ್ಪಳಿಸುವವನು ಭಾಗ್ಯಹೊಂದಲಿ."
		},
		"138": {
			"1": "<sup>1</sup> [ಯೆಹೋವನೇ,] ಪೂರ್ಣಮನಸ್ಸಿನಿಂದ ನಿನ್ನನ್ನು ಕೊಂಡಾಡುವೆನು; ದೇವರುಗಳೆದುರಿನಲ್ಲಿ ನಿನ್ನನ್ನು ಕೀರ್ತಿಸುವೆನು.",
			"2": "<sup>2</sup> ನಿನ್ನ ಪರಿಶುದ್ಧಾಲಯದ ಕಡೆಗೆ ಅಡ್ಡ ಬೀಳುತ್ತೇನೆ; ನಿನ್ನ ಕೃಪೆ, ಸತ್ಯತೆ ಇವುಗಳಿಗೋಸ್ಕರ ನಿನ್ನ ನಾಮವನ್ನು ಕೊಂಡಾಡುತ್ತೇನೆ. ವಾಗ್ದಾನವನ್ನು ವಿಶೇಷರೀತಿಯಿಂದ ನೆರವೇರಿಸಿ ನಿನ್ನ ನಾಮಮಹತ್ತನ್ನು ಹೆಚ್ಚಿಸಿದ್ದೀ.",
			"3": "<sup>3</sup> ನಾನು ಮೊರೆಯಿಟ್ಟಾಗ ಸದುತ್ತರವನ್ನು ದಯಪಾಲಿಸಿದಿ; ನನ್ನ ಆತ್ಮಕ್ಕೆ ಬಲಕೊಟ್ಟು ನನ್ನನ್ನು ಧೈರ್ಯಪಡಿಸಿದ್ದೀ.",
			"4": "<sup>4</sup> ಯೆಹೋವನೇ, ಭೂರಾಜರೆಲ್ಲರು ನೀನು ಉಸಿರಿದ ನುಡಿಗಳನ್ನು ಕೇಳಿ ನಿನ್ನನ್ನು ಸ್ತುತಿಸುವರು;",
			"5": "<sup>5</sup> ಅವರು ಯೆಹೋವನ ಮಾರ್ಗಗಳನ್ನು ಹಾಡಿಹರಸುವರು; ಯೆಹೋವನ ಪ್ರಭಾವವು ದೊಡ್ಡದಾಗಿದೆಯಲ್ಲಾ.",
			"6": "<sup>6</sup> ಯೆಹೋವನು ಮಹೋನ್ನತನು; ಆದರೂ ದೀನರನ್ನು ಲಕ್ಷಿಸುತ್ತಾನೆ; ಗರ್ವಿಷ್ಠರನ್ನು ದೂರದಿಂದಲೇ ಗುರುತು ಹಿಡಿಯುತ್ತಾನೆ.",
			"7": "<sup>7</sup> ನಾನು ಇಕ್ಕಟ್ಟಿನ ದಾರಿಯಲ್ಲಿ ನಡೆಯುವಾಗ ನನ್ನನ್ನು ಚೈತನ್ಯಗೊಳಿಸುವಿ; ನನ್ನ ಶತ್ರುಗಳ ಕೋಪಕ್ಕೆ ವಿರೋಧವಾಗಿ ನಿನ್ನ ಕೈ ಚಾಚುವಿ; ನಿನ್ನ ಬಲಗೈ ನನ್ನನ್ನು ರಕ್ಷಿಸುವದು.",
			"8": "<sup>8</sup> ಯೆಹೋವನು ನನ್ನ ಕಾರ್ಯವನ್ನು ಸಿದ್ಧಿಗೆ ತರುವನು. ಯೆಹೋವನೇ, ನಿನ್ನ ಕೃಪೆಯು ಶಾಶ್ವತವಾದದ್ದು; ನೀನು ಕೈಹಚ್ಚಿದ ಕೆಲಸವನ್ನು ನೆರವೇರಿಸದೆ ಬಿಡಬೇಡ."
		},
		"139": {
			"1": "<sup>1</sup> ಯೆಹೋವನೇ, ನೀನು ನನ್ನನ್ನು ಪರೀಕ್ಷಿಸಿ ತಿಳುಕೊಂಡಿದ್ದೀ;",
			"2": "<sup>2</sup> ನಾನು ಕೂತುಕೊಳ್ಳುವದೂ ಏಳುವದೂ ನಿನಗೆ ಗೊತ್ತದೆ; ದೂರದಿಂದಲೇ ನನ್ನ ಆಲೋಚನೆಗಳನ್ನು ಬಲ್ಲವನಾಗಿರುತ್ತೀ;",
			"3": "<sup>3</sup> ನಾನು ನಡೆಯುವದನ್ನೂ ಮಲಗುವದನ್ನೂ ಶೋಧಿಸಿ ಗ್ರಹಿಸಿಕೊಳ್ಳುತ್ತೀ; ನನ್ನ ನಡತೆಯೆಲ್ಲಾ ನಿನಗೆ ಗೋಚರವಾಗಿದೆ.",
			"4": "<sup>4</sup> ಯೆಹೋವನೇ, ನನ್ನ ನಾಲಿಗೆಯ ಮಾತುಗಳಲ್ಲಿ ನೀನು ಅರಿಯದೆ ಇರುವಂಥದು ಒಂದೂ ಇಲ್ಲ.",
			"5": "<sup>5</sup> ಸುತ್ತಲೂ ನನ್ನನ್ನು ಆವರಿಸಿ ನಿನ್ನ ಕೈಯನ್ನು ನನ್ನ ಮೇಲೆ ಇಟ್ಟಿದ್ದೀ.",
			"6": "<sup>6</sup> ಇಂಥ ಜ್ಞಾನವು ನನಗೆ ಬಹು ಆಶ್ಚರ್ಯವಾಗಿದೆ; ಅದು ಉನ್ನತವಾದದ್ದು, ನನಗೆ ನಿಲುಕುವದಿಲ್ಲ.",
			"7": "<sup>7</sup> ನಾನು ನಿನ್ನ ಆತ್ಮಕ್ಕೆ ತಪ್ಪಿಸಿಕೊಳ್ಳುವಂತೆ ಎಲ್ಲಿಗೆ ಹೋಗಲಿ? ನಿನ್ನ ಕಣ್ಣಿಗೆ ಮರೆಯಾಗುವಂತೆ ಎಲ್ಲಿಗೆ ಓಡಲಿ?",
			"8": "<sup>8</sup> ಮೇಲಣ ಲೋಕಕ್ಕೆ ಏರಿಹೋದರೆ ಅಲ್ಲಿ ನೀನಿರುತ್ತೀ; ಪಾತಾಳಕ್ಕೆ ಹೋಗಿ ಮಲಗಿಕೊಂಡೇನಂದರೆ ಅಲ್ಲಿಯೂ ನೀನಿರುವಿ.",
			"9": "<sup>9</sup> ಅರುಣನ ರೆಕ್ಕೆಗಳನ್ನು ಕಟ್ಟಿಕೊಂಡು ಹಾರಿಹೋಗಿ ಸಮುದ್ರದ ಕಟ್ಟಕಡೆಯಲ್ಲಿ ವಾಸಮಾಡೇನಂದರೆ",
			"10": "<sup>10</sup> ಅಲ್ಲಿಯೂ ನಿನ್ನ ಕೈ ನನ್ನನ್ನು ನಡಿಸುವದು; ನಿನ್ನ ಬಲಗೈ ನನ್ನನ್ನು ಹಿಡಿದಿರುವದು.",
			"11": "<sup>11</sup> ಕಾರ್ಗತ್ತಲೆಯು ನನ್ನನ್ನು ಕವಿಯುವದು, ಹಗಲು ಹೋಗಿ ನನ್ನ ಸುತ್ತಲು ಇರುಳಾಗುವದು ಎಂದು ಹೇಳಿಕೊಂಡರೇನು?",
			"12": "<sup>12</sup> ನಿನಗೆ ಕತ್ತಲೆಯು ಕತ್ತಲೆಯಲ್ಲ; ಇರುಳು ಹಗಲಾಗಿಯೇ ಇರುತ್ತದೆ; ಕತ್ತಲು ಬೆಳಕುಗಳೆರಡೂ ನಿನಗೆ ಒಂದೇ.",
			"13": "<sup>13</sup> ನನ್ನ ಅಂತರಿಂದ್ರಿಯಗಳನ್ನು ಉಂಟುಮಾಡಿದವನೂ ತಾಯಿಯ ಗರ್ಭದಲ್ಲಿ ನನ್ನನ್ನು ರೂಪಿಸಿದವನೂ ನೀನಲ್ಲವೋ?",
			"14": "<sup>14</sup> ನೀನು ನನ್ನನ್ನು ಅದ್ಭುತವಾಗಿಯೂ ವಿಚಿತ್ರವಾಗಿಯೂ ರಚಿಸಿದ್ದರಿಂದ ನಿನ್ನನ್ನು ಕೊಂಡಾಡುತ್ತೇನೆ. ನಿನ್ನ ಕೃತ್ಯಗಳು ಆಶ್ಚರ್ಯವಾಗಿವೆಯೆಂದು ನನ್ನ ಹೃದಯವು ಚೆನ್ನಾಗಿ ಗ್ರಹಿಸಿಕೊಂಡಿದೆ.",
			"15": "<sup>15</sup> ನಾನು ಗುಪ್ತಸ್ಥಳದಲ್ಲಿ ಏರ್ಪಡುತ್ತಾ ಭೂಗರ್ಭದಲ್ಲಿ ರಚಿಸಲ್ಪಡುತ್ತಾ ಇದ್ದಾಗ ನನ್ನ ಅಸ್ಥಿಪಂಜರವು ನಿನಗೆ ಮರೆಯಾಗಿದ್ದಿಲ್ಲ.",
			"16": "<sup>16</sup> ನಾನು ಇನ್ನೂ ಕೇವಲ ಪಿಂಡವಾಗಿರುವಾಗ ನಿನ್ನ ಕಣ್ಣುಗಳು ನನ್ನನ್ನು ನೋಡಿದವು; ನನ್ನ ಆಯುಷ್ಕಾಲದ ಪ್ರಥಮದಿನವು ಪ್ರಾರಂಭವಾಗುವ ಮೊದಲೇ ಅದರ ಎಲ್ಲಾ ದಿನಗಳು ನಿನ್ನ ಪುಸ್ತಕದಲ್ಲಿ ಬರೆಯಲ್ಪಟ್ಟವು.",
			"17": "<sup>17</sup> ದೇವರೇ, ನಿನ್ನ ಸಂಕಲ್ಪಗಳು ನನ್ನ ಎಣಿಕೆಯಲ್ಲಿ ಎಷ್ಟೋ ಗೌರವವಾಗಿವೆ; ಅವುಗಳ ಒಟ್ಟು ಅಸಂಖ್ಯವಾಗಿದೆ.",
			"18": "<sup>18</sup> ಅವುಗಳನ್ನು ಲೆಕ್ಕಿಸುವದಾದರೆ [ಸಮುದ್ರದ] ಮರಳಿಗಿಂತ ಹೆಚ್ಚಾಗಿವೆ. ನಾನು ಎಚ್ಚರವಾಗಲು ಮುಂಚಿನಂತೆಯೇ ನಿನ್ನ ಬಳಿಯಲ್ಲಿದ್ದೇನೆ.",
			"19": "<sup>19</sup> ಯೆಹೋವನೇ, ನೀನು ದುಷ್ಟರನ್ನು ಸಂಹರಿಸಿಬಿಟ್ಟರೆ ಎಷ್ಟೋ ಒಳ್ಳೇದು. ಕೊಲೆಪಾತಕರೇ, ನನ್ನಿಂದ ತೊಲಗಿಹೋಗಿರಿ.",
			"20": "<sup>20</sup> [ದೇವರೇ,] ಅವರು ನಿನ್ನ ಶತ್ರುಗಳು; ಅಯೋಗ್ಯಕಾರ್ಯಕ್ಕಾಗಿ ನಿನ್ನ ಹೆಸರೆತ್ತುತ್ತಾರೆ, ಸುಳ್ಳಾಣೆಯಿಡುತ್ತಾರೆ.",
			"21": "<sup>21</sup> ಯೆಹೋವನೇ, ನಿನ್ನನ್ನು ದ್ವೇಷಿಸುವವರನ್ನು ನಾನು ದ್ವೇಷಿಸುತ್ತೇನಲ್ಲವೋ? ನಿನ್ನ ವಿರೋಧಿಗಳಿಗೆ ನಾನು ಬೇಸರಗೊಳ್ಳುವದಿಲ್ಲವೋ?",
			"22": "<sup>22</sup> ನಾನು ಅವರನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ಹಗೆಮಾಡುತ್ತೇನೆ; ಅವರು ನನಗೂ ವೈರಿಗಳೇ ಆಗಿದ್ದಾರೆ.",
			"23": "<sup>23</sup> ದೇವಾ, ನನ್ನನ್ನು ಪರೀಕ್ಷಿಸಿ ನನ್ನ ಹೃದಯವನ್ನು ತಿಳಿದುಕೋ; ನನ್ನನ್ನು ಶೋಧಿಸಿ ನನ್ನ ಆಲೋಚನೆಗಳನ್ನು ಗೊತ್ತುಮಾಡು.",
			"24": "<sup>24</sup> ನಾನು ಕೇಡಿನ ಮಾರ್ಗದಲ್ಲಿರುತ್ತೇನೋ ಏನೋ ನೋಡಿ ಸನಾತನಮಾರ್ಗದಲ್ಲಿ ನನ್ನನ್ನು ನಡಿಸು."
		},
		"140": {
			"1": "<sup>1</sup> ಯೆಹೋವನೇ, ಕೆಡುಕರಿಂದ ನನ್ನನ್ನು ಬಿಡಿಸು; ಬಲಾತ್ಕಾರಿಗಳಿಗೆ ತಪ್ಪಿಸಿ ಕಾಪಾಡು.",
			"2": "<sup>2</sup> ಅವರು ಕೇಡು ಕಲ್ಪಿಸುತ್ತಾರೆ; ಯಾವಾಗಲೂ ಜಗಳವೆಬ್ಬಿಸುತ್ತಾರೆ.",
			"3": "<sup>3</sup> ತಮ್ಮ ನಾಲಿಗೆಯನ್ನು ಸರ್ಪದಂತೆ ಹದಮಾಡಿದ್ದಾರೆ; ಅವರ ತುಟಿಗಳ ಹಿಂದೆ ಹಾವಿನ ವಿಷವದೆ. ಸೆಲಾ.",
			"4": "<sup>4</sup> ಯೆಹೋವನೇ, ದುಷ್ಟರ ಕೈಗೆ ಸಿಕ್ಕದಂತೆ ನನ್ನನ್ನು ಕಾಪಾಡು; ಬಲಾತ್ಕಾರಿಗಳಿಗೆ ತಪ್ಪಿಸಿ ರಕ್ಷಿಸು. ಅವರು ನನ್ನ ಕಾಲುಗಳನ್ನು ಎಡವಿಸಿಬಿಡಬೇಕೆಂದು ಯೋಚಿಸಿದ್ದಾರೆ.",
			"5": "<sup>5</sup> ಗರ್ವಿಷ್ಠರು ನನಗೋಸ್ಕರ ಗುಪ್ತಸ್ಥಳದಲ್ಲಿ ಉರುಲನ್ನೂ ಪಾಶಗಳನ್ನೂ ಒಡ್ಡಿದ್ದಾರೆ; ದಾರಿಯ ಮಗ್ಗುಲಲ್ಲಿ ಬಲೆಹಾಸಿದ್ದಾರೆ; ನನಗಾಗಿ ಬೋನಿಟ್ಟಿದ್ದಾರೆ. ಸೆಲಾ.",
			"6": "<sup>6</sup> ನಾನು ಯೆಹೋವನಿಗೆ - ನೀನೇ ನನ್ನ ದೇವರು; ಯೆಹೋವನೇ, ನನ್ನ ವಿಜ್ಞಾಪನೆಗೆ ಕಿವಿಗೊಡು;",
			"7": "<sup>7</sup> ಕರ್ತನೇ, ಯೆಹೋವನೇ, ನನ್ನ ಆಶ್ರಯದುರ್ಗವೇ, ಯುದ್ಧಸಮಯದಲ್ಲಿ ನೀನೇ ನನ್ನ ಶಿರಸ್ತ್ರಾಣ;",
			"8": "<sup>8</sup> ಯೆಹೋವನೇ, ದುಷ್ಟರು ತಮ್ಮನ್ನು ಹೆಚ್ಚಿಸಿಕೊಳ್ಳದ ಹಾಗೆ ಅವರ ಆಶೆಗಳನ್ನು ನೆರವೇರಿಸಬೇಡ; ಅವರ ಕುಯುಕ್ತಿಯನ್ನು ಸಾಗಗೊಡಬೇಡ ಎಂದು ಹೇಳುತ್ತೇನೆ. ಸೆಲಾ.",
			"9": "<sup>9</sup> ನನ್ನನ್ನು ಸುತ್ತಿಕೊಂಡಿರುವವರ ತುಟಿಗಳ ಕೇಡು ಅವರ ತಲೆಯ ಮೇಲೇ ಬರಲಿ;",
			"10": "<sup>10</sup> ಅವರ ಮೇಲೆ ಕೆಂಡಗಳು ಸುರಿಯಲಿ; ಅವರು ಅಗ್ನಿಕುಂಡದೊಳಕ್ಕೂ ದೊಡ್ಡ ಮಡುವಿನೊಳಕ್ಕೂ ದೊಬ್ಬಲ್ಪಟ್ಟು ತಿರಿಗಿ ಏಳದಿರಲಿ.",
			"11": "<sup>11</sup> ಚಾಡಿಗಾರನು ದೇಶದಲ್ಲಿ ಉಳಿಯನು; ಕೇಡು ಬಲಾತ್ಕಾರಿಯನ್ನು ಹಿಂದಟ್ಟಿ ಕೆಡವಿಬಿಡುವದು.",
			"12": "<sup>12</sup> ಯೆಹೋವನು ದೀನರ ವ್ಯಾಜ್ಯವನ್ನು ನಡಿಸುವನೆಂತಲೂ ಬಡವರ ನ್ಯಾಯವನ್ನು ಸ್ಥಾಪಿಸುವನೆಂತಲೂ ಬಲ್ಲೆನು.",
			"13": "<sup>13</sup> ನಿಶ್ಚಯವಾಗಿ ನೀತಿವಂತರು ನಿನ್ನ ಹೆಸರನ್ನು ಕೊಂಡಾಡುವರು; ಯಥಾರ್ಥರು ನಿನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಬದುಕುವರು."
		},
		"141": {
			"1": "<sup>1</sup> ಯೆಹೋವನೇ, ಮೊರೆಯಿಡುತ್ತೇನೆ; ಬೇಗನೆ ಬಾ. ನಾನು ಮೊರೆಯಿಡುವಾಗ ನನ್ನ ಕೂಗನ್ನು ಲಾಲಿಸು.",
			"2": "<sup>2</sup> ನನ್ನ ಪ್ರಾರ್ಥನೆಯು ಧೂಪದಂತೆಯೂ ನಾನು ಕೈಯೆತ್ತುವದು ಸಂಧ್ಯಾನೈವೇದ್ಯದಂತೆಯೂ ನಿನಗೆ ಸಮರ್ಪಕವಾಗಲಿ.",
			"3": "<sup>3</sup> ಯೆಹೋವನೇ, ನನ್ನ ಬಾಯಿಗೆ ಕಾವಲಿರಿಸು; ನನ್ನ ತುಟಿಗಳೆಂಬ ಕದವನ್ನು ಕಾಯಿ.",
			"4": "<sup>4</sup> ನನ್ನ ಹೃದಯವು ದುರಾಚಾರವನ್ನು ಮೆಚ್ಚದಂತೆಯೂ ನಾನು ದುಷ್ಟರೊಡನೆ ಕೂಡಿ ಕೆಟ್ಟ ಕೆಲಸಗಳನ್ನು ನಡಿಸದಂತೆಯೂ ನನ್ನನ್ನು ಕಾಪಾಡು. ಅವರ ಮೃಷ್ಟಾನ್ನವು ನನಗೆ ಬೇಡವೇ ಬೇಡ.",
			"5": "<sup>5</sup> ನೀತಿವಂತರು ನನ್ನನ್ನು ಹೊಡೆಯಲಿ, ಅದು ನನಗುಪಕಾರ; ಅವರು ನನ್ನನ್ನು ಶಿಕ್ಷಿಸಲಿ, ಅದು ನನ್ನ ತಲೆಗೆ ಎಣ್ಣೆಯಂತಿದೆ; ನನ್ನ ತಲೆಯು ಅದನ್ನು ಬೇಡವೆನ್ನದಿರಲಿ. ಆದರೆ ದುಷ್ಟರ ಕೆಟ್ಟತನಕ್ಕೆ ವಿರೋಧವಾಗಿ ದೇವರನ್ನು ಪ್ರಾರ್ಥಿಸುತ್ತಿರುವೆನು.",
			"6": "<sup>6</sup> ಅವರ ಪ್ರಮುಖರು ಕಡುಬಂಡೆಯಿಂದ ಕೆಳಕ್ಕೆ ದೊಬ್ಬಲ್ಪಟ್ಟ ಮೇಲೆ ಜನರು ನನ್ನ ಮಾತುಗಳಿಗೆ ಕಿವಿಗೊಡುವರು; ಅವು ಅವರಿಗೆ ಚೆನ್ನಾಗಿರುವವು.",
			"7": "<sup>7</sup> ಒಬ್ಬನು ಹೊಲವನ್ನು ಉತ್ತು ಹೆಂಟೆಗಳನ್ನು ಒಡೆದು ಚದರಿಸುವ ಪ್ರಕಾರವೇ ನಮ್ಮ ಎಲುಬುಗಳು ಪಾತಾಳದ್ವಾರದಲ್ಲಿ ಚದರಿಸಲ್ಪಟ್ಟಿರುತ್ತವೆ.",
			"8": "<sup>8</sup> ಕರ್ತನೇ, ಯೆಹೋವನೇ, ನನ್ನ ದೃಷ್ಟಿ ನಿನ್ನಲ್ಲಿದೆ; ನಿನ್ನನ್ನು ಆಶ್ರಯಿಸಿಕೊಂಡಿದ್ದೇನೆ, ನನ್ನ ಪ್ರಾಣವನ್ನು ಮರಣಕ್ಕೆ ಒಪ್ಪಿಸಬೇಡ.",
			"9": "<sup>9</sup> ಕೆಡುಕರು ಒಡ್ಡಿದ ಉರುಲಿನಲ್ಲಿ ಸಿಕ್ಕಿಬೀಳದಂತೆ ನನ್ನನ್ನು ಕಾಪಾಡು; ಅವರಿಟ್ಟ ಬೋನಿಗೆ ನನ್ನನ್ನು ತಪ್ಪಿಸು.",
			"10": "<sup>10</sup> ದುಷ್ಟರು ತಮ್ಮ ಬಲೆಯಲ್ಲಿ ತಾವೇ ಬಿದ್ದು ಹೋಗಲಿ; ಆಗಲೇ ನಾನು ತಪ್ಪಿಸಿಕೊಳ್ಳುವೆನು."
		},
		"142": {
			"1": "<sup>1</sup> ನಾನು ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಡುವೆನು; ಯೆಹೋವನಿಗೆ ಕೂಗಿ ಬಿನ್ನೈಸುವೆನು.",
			"2": "<sup>2</sup> ನನ್ನ ಚಿಂತೆಗಳನ್ನು ಆತನ ಮುಂದೆ ಬಿಚ್ಚುವೆನು; ನನ್ನ ಕಷ್ಟವನ್ನು ಆತನಿಗೆ ಅರಿಕೆಮಾಡುವೆನು;",
			"3": "<sup>3</sup> ನನ್ನ ಆತ್ಮವು ಕುಂದಿಹೋಗಿದೆ; ನೀನೇ ನನ್ನ ಮಾರ್ಗವನ್ನು ಬಲ್ಲವನು; ನಾನು ಹೋಗಬೇಕಾದ ದಾರಿಯಲ್ಲಿ ಉರುಲನ್ನೊಡ್ಡಿದ್ದಾರೆ.",
			"4": "<sup>4</sup> ನನ್ನ ಬಲಗಡೆಗೆ ನೋಡಿ ಪರಾಂಬರಿಸು; ಅಲ್ಲಿ ಸಹಾಯಕರು ಯಾರೂ ಇಲ್ಲ. ಆಶ್ರಯವು ನಾಶವಾಯಿತು; ನನ್ನ ಪ್ರಾಣಕ್ಕೆ ಹಿತಚಿಂತಕರು ಒಬ್ಬರೂ ಇಲ್ಲ.",
			"5": "<sup>5</sup> ಯೆಹೋವನೇ, ನಾನು ನಿನಗೆ - ನೀನೇ ನನ್ನ ಶರಣನೂ ಜೀವಲೋಕದಲ್ಲಿ ನನ್ನ ಪಾಲೂ ಆಗಿದ್ದೀ ಎಂದು ಕೂಗಿ ಮೊರೆಯಿಟ್ಟಿದ್ದೇನೆ.",
			"6": "<sup>6</sup> ನನ್ನ ಕೂಗನ್ನು ಲಾಲಿಸು, ಬಲು ಕುಗ್ಗಿಹೋಗಿದ್ದೇನೆ. ಹಿಂಸಕರ ಕೈಯಿಂದ ನನ್ನನ್ನು ತಪ್ಪಿಸು; ಅವರು ನನಗಿಂತ ಬಲಿಷ್ಠರಾಗಿದ್ದಾರಲ್ಲಾ.",
			"7": "<sup>7</sup> ಸೆರೆಯಿಂದ ನನ್ನನ್ನು ಬಿಡಿಸು; ನಿನ್ನ ನಾಮವನ್ನು ಕೀರ್ತಿಸುವೆನು. ನೀತಿವಂತರು ನಿನ್ನ ಮಹೋಪಕಾರಕ್ಕಾಗಿ ನನ್ನನ್ನು ಸುತ್ತಿಕೊಂಡು ಉತ್ಸಾಹಗೊಳ್ಳುವರು."
		},
		"143": {
			"1": "<sup>1</sup> ಯೆಹೋವನೇ, ನನ್ನ ಪ್ರಾರ್ಥನೆಯನ್ನು ಆಲಿಸು; ನನ್ನ ಬಿನ್ನಹಕ್ಕೆ ಕಿವಿಗೊಡು; ನಿನ್ನ ನೀತಿಸತ್ಯತೆಗಳಿಗನುಸಾರವಾಗಿ ಸದುತ್ತರವನ್ನು ದಯಪಾಲಿಸು.",
			"2": "<sup>2</sup> ನಿನ್ನ ಸೇವಕನನ್ನು ವಿಚಾರಣೆಗೆ ಗುರಿಮಾಡಬೇಡ; ನಿನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಯಾವ ಜೀವಿಯೂ ನೀತಿವಂತನಲ್ಲ.",
			"3": "<sup>3</sup> ವೈರಿಯು ನನ್ನ ಪ್ರಾಣವನ್ನು ಹಿಂಸಿಸಿದ್ದಾನೆ; ಜೀವವನ್ನು ನೆಲಕ್ಕೆ ಹಾಕಿ ಜಜ್ಜಿದ್ದಾನೆ. ಕಾರ್ಗತ್ತಲೆಯಲ್ಲಿ ನನ್ನನ್ನು ಹಾಕಿದ್ದಾನೆ; ಬಹು ಕಾಲದಿಂದ ಸತ್ತವರಂತಿದ್ದೇನೆ.",
			"4": "<sup>4</sup> ನನ್ನ ಆತ್ಮವು ಕುಂದಿಹೋಗಿದೆ; ನನ್ನ ಮನಸ್ಸು ಬೆರಗಾಗಿದೆ.",
			"5": "<sup>5</sup> ಹಳೇ ದಿನಗಳನ್ನು ನೆನಸಿಕೊಳ್ಳುತ್ತೇನೆ; ನಿನ್ನ ಎಲ್ಲಾ ಕಾರ್ಯಗಳನ್ನು ಧ್ಯಾನಿಸುತ್ತೇನೆ; ನಿನ್ನ ಕೈಕೆಲಸಗಳನ್ನು ಸ್ಮರಿಸುತ್ತೇನೆ.",
			"6": "<sup>6</sup> ಕೈಯೊಡ್ಡಿ ಬೇಡುತ್ತೇನೆ; ಒಣನೆಲವು ನೀರಿಗಾಗಿ ಹೇಗೋ ಹಾಗೆಯೇ ನನ್ನ ಆತ್ಮವು ನಿನಗಾಗಿ ತವಕಪಡುತ್ತದೆ. ಸೆಲಾ.",
			"7": "<sup>7</sup> ಯೆಹೋವನೇ, ಬೇಗನೆ ಸದುತ್ತರವನ್ನು ದಯಪಾಲಿಸು; ನನ್ನ ಆತ್ಮವು ಕುಂದಿಹೋಗಿದೆ. ನೀನು ವಿಮುಖನಾದರೆ ಸಮಾಧಿಯಲ್ಲಿ ಸೇರಿದವರಿಗೆ ನಾನು ಸಮಾನನಾಗಿರುವೆನು.",
			"8": "<sup>8</sup> ಹೊತ್ತಾರೆ ನಿನ್ನ ಕೃಪೆಯು ನನಗೆ ಪ್ರಕಟವಾಗಲಿ; ನಿನ್ನಲ್ಲಿ ಭರವಸವಿಟ್ಟಿದ್ದೇನಲ್ಲಾ. ನಾನು ನಡೆಯತಕ್ಕ ಮಾರ್ಗವನ್ನು ತಿಳಿಸು; ನನ್ನ ಮನಸ್ಸನ್ನು ನಿನ್ನಲ್ಲಿಯೇ ಇಟ್ಟಿದ್ದೇನೆ.",
			"9": "<sup>9</sup> ಯೆಹೋವನೇ, ನಿನ್ನನ್ನೇ ಮರೆಹೊಕ್ಕಿದ್ದೇನೆ; ಶತ್ರುಗಳಿಂದ ನನ್ನನ್ನು ಬಿಡಿಸು.",
			"10": "<sup>10</sup> ನಿನ್ನ ಚಿತ್ತದಂತೆ ನಡಕೊಳ್ಳುವದನ್ನು ನನಗೆ ಕಲಿಸು; ನನ್ನ ದೇವರು ನೀನಲ್ಲವೋ? ಶುಭಕಾರಿಯಾಗಿರುವ ನಿನ್ನ ಆತ್ಮನು ನನ್ನನ್ನು ಮಟ್ಟನೆಲದಲ್ಲಿ ನಡಿಸಲಿ.",
			"11": "<sup>11</sup> ಯೆಹೋವನೇ, ನಿನ್ನ ಹೆಸರಿನ ನಿಮಿತ್ತ ನನ್ನನ್ನು ಉಜ್ಜೀವಿಸಮಾಡು; ನಿನ್ನ ನೀತಿಗನುಸಾರವಾಗಿ ನನ್ನ ಪ್ರಾಣವನ್ನು ವಿಪತ್ತಿನಿಂದ ಬಿಡಿಸು.",
			"12": "<sup>12</sup> ನನಗೆ ಕೃಪೆತೋರಿಸಿ ನನ್ನ ವೈರಿಗಳನ್ನು ಖಂಡಿಸಿಬಿಡು. ನನ್ನ ಪ್ರಾಣಕಂಟಕರನ್ನೆಲ್ಲಾ ಕಡಿದುಹಾಕು; ನಾನು ನಿನ್ನ ಸೇವಕನಲ್ಲವೇ."
		},
		"144": {
			"1": "<sup>1</sup> ನನ್ನ ಶರಣನಾದ ಯೆಹೋವನಿಗೆ ಕೊಂಡಾಟವಾಗಲಿ. ಆತನು ನನ್ನ ಕೈಗಳಿಗೆ ಯುದ್ಧವಿದ್ಯೆಯನ್ನೂ ನನ್ನ ಬೆರಳುಗಳಿಗೆ ಕಾಳಗವನ್ನೂ ಕಲಿಸಿದ್ದಾನೆ.",
			"2": "<sup>2</sup> ನನ್ನ ಪ್ರೀತಿಯೂ ನನ್ನ ಕೋಟೆಯೂ ನನ್ನ ದುರ್ಗವೂ ನನ್ನ ರಕ್ಷಕನೂ ನನ್ನ ಗುರಾಣಿಯೂ ಆಶ್ರಯವೂ ನನ್ನ ಜನವನ್ನು ನನಗೆ ಅಧೀನಮಾಡಿದವನೂ ಆತನೇ.",
			"3": "<sup>3</sup> ಯೆಹೋವನೇ, ಮನುಷ್ಯನು ಎಷ್ಟು ಮಾತ್ರದವನು? ಅವನನ್ನು ನೀನು ಯಾಕೆ ನೆನಸಬೇಕು? ಮಾನವನು ಎಷ್ಟರವನು? ಅವನಲ್ಲಿ ಯಾಕೆ ಲಕ್ಷ್ಯವಿಡಬೇಕು?",
			"4": "<sup>4</sup> ಮನುಷ್ಯನು ಬರೀ ಉಸಿರೇ; ಅವನ ಜೀವಕಾಲವು ಬೇಗನೆ ಗತಿಸಿಹೋಗುವ ನೆರಳಿನಂತಿದೆ.",
			"5": "<sup>5</sup> ಯೆಹೋವನೇ, ಆಕಾಶವನ್ನು ತಗ್ಗಿಸಿ ಇಳಿದು ಬಾ; ಪರ್ವತಗಳನ್ನು ಮುಟ್ಟು, ಆಗ ಅವು ಹೊಗೆ ಹಾಯುವವು.",
			"6": "<sup>6</sup> ಸಿಡಿಲಿನಿಂದ ಶತ್ರುಗಳನ್ನು ಚದರಿಸಿಬಿಡು; ನಿನ್ನ ಬಾಣಗಳಿಂದ ಅವರನ್ನು ಭ್ರಾಂತಿಗೊಳಿಸು.",
			"7": "<sup>7</sup> ಮೇಲಣ ಲೋಕದಿಂದ ಕೈಚಾಚಿ ಮಹಾಜಲರಾಶಿಯೊಳಗಿಂದ ನನ್ನನ್ನು ಎಳೆದುಕೋ.",
			"8": "<sup>8</sup> ಕಪಟವಚನದವರೂ ಬಲಗೈಯೆತ್ತಿ ಸುಳ್ಳಾಣೆಯಿಡುವವರೂ ಆಗಿರುವ ಅನ್ಯಜನಗಳ ಕೈಯಿಂದ ನನ್ನನ್ನು ಬಿಡಿಸು.",
			"9": "<sup>9</sup> ದೇವರೇ, ನಿನಗೆ ನೂತನ ಕೀರ್ತನೆಯನ್ನು ಹಾಡುವೆನು; ಹತ್ತು ತಂತಿಗಳ ಸ್ವರಮಂಡಲವನ್ನು ಬಾರಿಸುತ್ತಾ ನಿನ್ನನ್ನು ಕೊಂಡಾಡುವೆನು.",
			"10": "<sup>10</sup> ಅರಸುಗಳಿಗೆ ಜಯಪ್ರದನೂ ಸೇವಕನಾದ ದಾವೀದನನ್ನು ಹಾನಿಕರವಾದ ಕತ್ತಿಗೆ ತಪ್ಪಿಸಿದವನೂ ನೀನೇ.",
			"11": "<sup>11</sup> ಕಪಟವಚನದವರೂ ಬಲಗೈಯೆತ್ತಿ ಸುಳ್ಳಾಣೆಯಿಡುವವರೂ ಆಗಿರುವ ಅನ್ಯಜನಗಳ ಕೈಯಿಂದ ನನ್ನನ್ನು ಬಿಡಿಸಿ ಕಾಪಾಡು.",
			"12": "<sup>12</sup> ಯೌವನಸ್ಥರಾದ ನಮ್ಮ ಗಂಡುಮಕ್ಕಳು ಹೊರವಾಗಿ ಬೆಳೆದಿರುವ ಸಸಿಗಳಂತಿರುವರು; ಹೆಣ್ಣುಮಕ್ಕಳು ಅರಮನೆಯಲ್ಲಿರುವ ವಿಚಿತ್ರವಾಗಿ ಕೆತ್ತಿದ ಮೂಲೆಗಂಬದಂತಿರುವರು.",
			"13": "<sup>13</sup> ನಮ್ಮ ಕಣಜಗಳು ಸಕಲವಿಧವಾದ ಧಾನ್ಯಗಳಿಂದ ತುಂಬಿರುವವು; ನಮ್ಮ ಹೊಲಗಳಲ್ಲಿರುವ ಕುರಿಗಳು ಸಾವಿರಾರು ಮರಿಗಳನ್ನು ಈಯುವವು.",
			"14": "<sup>14</sup> ನಮ್ಮ ಎತ್ತುಗಳು ದೊಡ್ಡ ಹೇರುಗಳನ್ನು ಹೊತ್ತು ಬರುವವು. ವೈರಿಗಳು ಒಳಗೆ ನುಗ್ಗುವದಾಗಲಿ ನಮ್ಮವರನ್ನು ಒಯ್ಯುವದಾಗಲಿ ಇರುವದಿಲ್ಲ; ಬೀದಿಗಳಲ್ಲಿ ಗೋಳಾಟವು ಕೇಳಿಸುವದಿಲ್ಲ.",
			"15": "<sup>15</sup> ಇಂಥ ಸುಸ್ಥಿತಿಯಲ್ಲಿರುವ ಜನರು ಧನ್ಯರು; ಯಾರಿಗೆ ಯೆಹೋವನು ದೇವರಾಗಿರುತ್ತಾನೋ ಅವರು ಭಾಗ್ಯವಂತರು."
		},
		"145": {
			"1": "<sup>1</sup> ನನ್ನ ದೇವರೇ, ಒಡೆಯನೇ, ನಿನ್ನನ್ನು ಘನಪಡಿಸುವೆನು; ನಿನ್ನ ನಾಮವನ್ನು ಯುಗಯುಗಾಂತರಗಳಲ್ಲಿಯೂ ಕೊಂಡಾಡುವೆನು.",
			"2": "<sup>2</sup> ದಿನಂಪ್ರತಿ ನಿನ್ನನ್ನು ಕೀರ್ತಿಸುವೆನು; ನಿನ್ನ ನಾಮವನ್ನು ಯುಗಯುಗಾಂತರಗಳಲ್ಲಿಯೂ ಸ್ತುತಿಸುವೆನು.",
			"3": "<sup>3</sup> ಯೆಹೋವನು ಮಹೋನ್ನತನೂ ಮಹಾಸ್ತುತಿಪಾತ್ರನೂ ಆಗಿದ್ದಾನೆ; ಆತನ ಮಹತ್ತು ಅಪಾರವಾದದ್ದು.",
			"4": "<sup>4</sup> ಜನರು ಪಾರಂಪರ್ಯವಾಗಿ ನಿನ್ನ ಕೃತ್ಯಗಳನ್ನು ಹೊಗಳುವರು; ನಿನ್ನ ಮಹತ್ಕಾರ್ಯಗಳನ್ನು ವರ್ಣಿಸುವರು.",
			"5": "<sup>5</sup> ನಾನು ನಿನ್ನ ಮಹಾಪ್ರಭಾವಯುಕ್ತವಾದ ಮಹಿಮೆಯನ್ನೂ ಅದ್ಭುತಕಾರ್ಯಗಳನ್ನೂ ಧ್ಯಾನಿಸುವೆನು.",
			"6": "<sup>6</sup> ಮನುಷ್ಯರು ನಿನ್ನ ಭಯಂಕರಕೃತ್ಯಗಳಲ್ಲಿ ಕಂಡುಬಂದ ಪ್ರತಾಪವನ್ನು ಕೊಂಡಾಡುವರು. ನಾನು ನಿನ್ನ ಮಹತ್ತನ್ನು ಪ್ರಸಿದ್ಧಪಡಿಸುವೆನು.",
			"7": "<sup>7</sup> ಜನರು ನಿನ್ನ ಮಹೋಪಕಾರವನ್ನು ನೆನಪಿನಲ್ಲಿಟ್ಟುಕೊಂಡು ಪ್ರಕಟಿಸುವರು; ನಿನ್ನ ನೀತಿಯನ್ನು ಹೊಗಳುವರು.",
			"8": "<sup>8</sup> ಯೆಹೋವನು ದಯೆಯೂ ಕನಿಕರವೂ ಉಳ್ಳವನು; ದೀರ್ಘಶಾಂತನೂ ಪ್ರೀತಿಪೂರ್ಣನೂ ಆಗಿದ್ದಾನೆ.",
			"9": "<sup>9</sup> ಯೆಹೋವನು ಸರ್ವೋಪಕಾರಿಯೂ ತಾನು ನಿರ್ಮಿಸಿದವುಗಳನ್ನೆಲ್ಲಾ ಕರುಣಿಸುವಾತನೂ ಆಗಿದ್ದಾನೆ.",
			"10": "<sup>10</sup> ಯೆಹೋವನೇ, ನಿನ್ನ ಸೃಷ್ಟಿಯೆಲ್ಲವು ನಿನ್ನನ್ನು ಸ್ತುತಿಸುವದು; ನಿನ್ನ ಭಕ್ತರು ನಿನ್ನನ್ನು ಕೊಂಡಾಡುವರು.",
			"11": "<sup>11</sup> ಅವರು ನಿನ್ನ ರಾಜ್ಯಮಹತ್ತನ್ನು ಪ್ರಸಿದ್ಧಪಡಿಸುವರು; ನಿನ್ನ ಪ್ರತಾಪವನ್ನು ವರ್ಣಿಸುವರು.",
			"12": "<sup>12</sup> ಹೀಗೆ ಮಾನವರು ನಿನ್ನ ಶೂರಕೃತ್ಯಗಳನ್ನೂ ನಿನ್ನ ರಾಜ್ಯದ ಮಹಾಪ್ರಭಾವವನ್ನೂ ಗ್ರಹಿಸಿಕೊಳ್ಳುವರು.",
			"13": "<sup>13</sup> ನಿನ್ನ ರಾಜ್ಯವು ಶಾಶ್ವತವಾಗಿದೆ; ನಿನ್ನ ಆಳ್ವಿಕೆಯು ತಲತಲಾಂತರಕ್ಕೂ ಇರುವದು.",
			"14": "<sup>14</sup> ಯೆಹೋವನು ಬಿದ್ದವರನ್ನೆಲ್ಲಾ ಎತ್ತುವವನೂ ಕುಗ್ಗಿದವರನ್ನೆಲ್ಲಾ ಉದ್ಧರಿಸುವವನೂ ಆಗಿದ್ದಾನೆ.",
			"15": "<sup>15</sup> [ಯೆಹೋವನೇ,] ಎಲ್ಲಾ ಜೀವಿಗಳ ಕಣ್ಣುಗಳು ನಿನ್ನನ್ನೇ ನೋಡುತ್ತವೆ; ನೀನು ಅವುಗಳಿಗೆ ಹೊತ್ತುಹೊತ್ತಿಗೆ ಆಹಾರಕೊಡುತ್ತೀ.",
			"16": "<sup>16</sup> ನೀನು ಕೈದೆರೆದು ಎಲ್ಲಾ ಜೀವಿಗಳ ಇಷ್ಟವನ್ನು ನೆರವೇರಿಸುತ್ತೀ.",
			"17": "<sup>17</sup> ಯೆಹೋವನ ಮಾರ್ಗಗಳೆಲ್ಲಾ ನೀತಿಯುಳ್ಳವುಗಳು; ಆತನು ಎಲ್ಲಾ ಕಾರ್ಯಗಳಲ್ಲಿ ಕೃಪೆತೋರಿಸುವವನು.",
			"18": "<sup>18</sup> ಯೆಹೋವನಿಗೆ ಮೊರೆಯಿಡುವವರು ಯಥಾರ್ಥವಾಗಿ ಮೊರೆಯಿಡುವದಾದರೆ ಆತನು ಹತ್ತಿರವಾಗಿಯೇ ಇದ್ದಾನೆ.",
			"19": "<sup>19</sup> ತನ್ನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರ ಇಷ್ಟವನ್ನು ನೆರವೇರಿಸುತ್ತಾನೆ; ಅವರ ಕೂಗನ್ನು ಕೇಳಿ ರಕ್ಷಿಸುತ್ತಾನೆ.",
			"20": "<sup>20</sup> ಯೆಹೋವನು ತನ್ನನ್ನು ಪ್ರೀತಿಸುವವರೆಲ್ಲರನ್ನು ಕಾಪಾಡುತ್ತಾನೆ; ಆದರೆ ಎಲ್ಲಾ ದುಷ್ಟರನ್ನು ಸಂಹರಿಸುತ್ತಾನೆ.",
			"21": "<sup>21</sup> ನನ್ನ ಬಾಯಿ ಯೆಹೋವನನ್ನು ಕೀರ್ತಿಸುವದು; ಎಲ್ಲಾ ಜೀವಿಗಳು ಆತನ ಪರಿಶುದ್ಧ ನಾಮವನ್ನು ಯುಗಯುಗಾಂತರಗಳಲ್ಲಿಯೂ ಕೊಂಡಾಡಲಿ."
		},
		"146": {
			"1": "<sup>1</sup> ಯಾಹುವಿಗೆ ಸ್ತೋತ್ರ! ನನ್ನ ಮನವೇ, ಯೆಹೋವನನ್ನು ಸ್ತುತಿಸು.",
			"2": "<sup>2</sup> ಪ್ರಾಣವಿರುವವರೆಗೂ ಯೆಹೋವನನ್ನು ಸ್ತುತಿಸುವೆನು; ಜೀವಮಾನವೆಲ್ಲಾ ನನ್ನ ದೇವರನ್ನು ಕೊಂಡಾಡುವೆನು.",
			"3": "<sup>3</sup> ಪ್ರಭುಗಳಲ್ಲಿ ಭರವಸವಿಡಬೇಡಿರಿ; ಮಾನವನನ್ನು ನೆಚ್ಚಬೇಡಿರಿ, ಅವನು ಸಹಾಯಮಾಡ ಶಕ್ತನಲ್ಲ;",
			"4": "<sup>4</sup> ಅವನ ಉಸಿರು ಹೋಗಲು ಮಣ್ಣಿಗೆ ಸೇರುತ್ತಾನೆ; ಅವನ ಸಂಕಲ್ಪಗಳೆಲ್ಲಾ ಅದೇ ದಿನದಲ್ಲಿ ಹಾಳಾಗುವವು.",
			"5": "<sup>5</sup> ಯಾವನಿಗೆ ಯಾಕೋಬನ ದೇವರು ಸಹಾಯಕನೋ ಯಾವನು ತನ್ನ ದೇವರಾದ ಯೆಹೋವನನ್ನು ನಂಬಿರುತ್ತಾನೋ ಅವನೇ ಧನ್ಯನು.",
			"6": "<sup>6</sup> ಭೂಮಿ, ಆಕಾಶ, ಸಾಗರ, ಚರಾಚರ ಇವುಗಳನ್ನು ನಿರ್ಮಿಸಿದವನೂ ವಾಗ್ದಾನವನ್ನು ಯಾವಾಗಲೂ ನೆರವೇರಿಸುವವನೂ",
			"7": "<sup>7</sup> ಹಿಂಸೆಗೆ ಗುರಿಯಾದವರ ನ್ಯಾಯವನ್ನು ಸ್ಥಾಪಿಸುವವನೂ ಹಸಿದವರಿಗೆ ಆಹಾರಕೊಡುವವನೂ ಆತನೇ. ಯೆಹೋವನು ಸೆರೆಯಲ್ಲಿರುವವರನ್ನು ಬಿಡಿಸುತ್ತಾನೆ.",
			"8": "<sup>8</sup> ಯೆಹೋವನು ಕುರುಡರಿಗೆ ಕಣ್ಣು ಕೊಡುತ್ತಾನೆ. ಯೆಹೋವನು ಕುಗ್ಗಿದವರನ್ನು ಉದ್ಧರಿಸುತ್ತಾನೆ. ಯೆಹೋವನು ನೀತಿವಂತರನ್ನು ಪ್ರೀತಿಸುತ್ತಾನೆ.",
			"9": "<sup>9</sup> ಯೆಹೋವನು ಪರದೇಶದವರನ್ನು ಕಾಪಾಡುತ್ತಾನೆ. ಆತನು ಅನಾಥರಿಗೂ ವಿಧವೆಯರಿಗೂ ಆಧಾರವಾಗಿದ್ದಾನೆ. ಆದರೆ ದುಷ್ಟರ ಮಾರ್ಗವನ್ನು ಡೊಂಕು ಮಾಡಿಬಿಡುತ್ತಾನೆ.",
			"10": "<sup>10</sup> ಯೆಹೋವನು ಸದಾಕಾಲವೂ ಅರಸನಾಗಿರುವನು; ಚೀಯೋನೇ, ನಿನ್ನ ದೇವರು ತಲತಲಾಂತರಕ್ಕೂ ಆಳುವನು. ಯಾಹುವಿಗೆ ಸ್ತೋತ್ರ!"
		},
		"147": {
			"1": "<sup>1</sup> ಯಾಹುವಿಗೆ ಸ್ತೋತ್ರ! ನಮ್ಮ ದೇವರನ್ನು ಸ್ತುತಿಸುವದು ಒಳ್ಳೇದೂ ಸಂತೋಷಕರವೂ ಆಗಿದೆ; ಆತನನ್ನು ಕೀರ್ತಿಸುವದು ಯುಕ್ತವಾಗಿದೆ.",
			"2": "<sup>2</sup> ಯೆಹೋವನು ಯೆರೂಸಲೇಮನ್ನು ಕಟ್ಟಿಸುತ್ತಿದ್ದಾನೆ; ಚದರಿಹೋಗಿದ್ದ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಕೂಡಿಸುತ್ತಿದ್ದಾನೆ.",
			"3": "<sup>3</sup> ಮುರಿದ ಮನಸ್ಸುಳ್ಳವರನ್ನು ವಾಸಿಮಾಡುತ್ತಾನೆ; ಅವರ ಗಾಯಗಳನ್ನು ಕಟ್ಟುತ್ತಾನೆ.",
			"4": "<sup>4</sup> ಆತನು ನಕ್ಷತ್ರಗಳ ಸಂಖ್ಯೆಯನ್ನು ಗೊತ್ತುಮಾಡಿ ಪ್ರತಿಯೊಂದಕ್ಕೆ ಹೆಸರಿಟ್ಟಿದ್ದಾನೆ.",
			"5": "<sup>5</sup> ನಮ್ಮ ಕರ್ತನು ದೊಡ್ಡವನೂ ಪರಾಕ್ರಮಿಯೂ ಆಗಿದ್ದಾನೆ; ಆತನ ಜ್ಞಾನವು ಅಪರಿಮಿತವಾಗಿದೆ.",
			"6": "<sup>6</sup> ಯೆಹೋವನು ದೀನರಿಗೆ ಆಧಾರವಾಗಿದ್ದಾನೆ; ದುಷ್ಟರನ್ನು ನೆಲಕ್ಕೆ ಹತ್ತಿಸಿಬಿಡುತ್ತಾನೆ.",
			"7": "<sup>7</sup> ಯೆಹೋವನಿಗೆ ಕೃಜ್ಞತಾಸ್ತುತಿಮಾಡಿರಿ; ಕಿನ್ನರಿಯೊಡನೆ ನಮ್ಮ ದೇವರನ್ನು ಕೊಂಡಾಡಿರಿ.",
			"8": "<sup>8</sup> ಆತನು ಆಕಾಶದಲ್ಲಿ ಮೋಡಗಳನ್ನು ಕವಿಸುತ್ತಾನೆ; ಭೂಮಿಗೋಸ್ಕರ ಮಳೆಯನ್ನು ಸಿದ್ಧಮಾಡುತ್ತಾನೆ; ಬೆಟ್ಟಗಳಲ್ಲಿ ಹುಲ್ಲನ್ನು ಬೆಳಸುತ್ತಾನೆ.",
			"9": "<sup>9</sup> ಆತನು ಪಶುಗಳಿಗೂ ಕೂಗುತ್ತಿರುವ ಕಾಗೇಮರಿಗಳಿಗೂ ಬೇಕಾದ ಆಹಾರಕೊಡುತ್ತಾನೆ.",
			"10": "<sup>10</sup> ಕುದುರೆಯ ಶಕ್ತಿಯಲ್ಲಿ ಆತನಿಗೆ ಇಷ್ಟವಿಲ್ಲ; ಆಳಿನ ತೊಡೆಯ ಬಲವನ್ನು ಮೆಚ್ಚುವದಿಲ್ಲ.",
			"11": "<sup>11</sup> ಯೆಹೋವನು ಆನಂದಿಸುವದು ತನ್ನ ಕೃಪೆಯನ್ನು ನಿರೀಕ್ಷಿಸುವ ಭಕ್ತರಲ್ಲೇ.",
			"12": "<sup>12</sup> ಯೆರೂಸಲೇಮೇ, ಯೆಹೋವನನ್ನು ಕೀರ್ತಿಸು; ಚೀಯೋನೇ, ನಿನ್ನ ದೇವರನ್ನು ಸ್ತುತಿಸು.",
			"13": "<sup>13</sup> ಆತನು ನಿನ್ನ ಹೆಬ್ಬಾಗಲುಗಳ ಅಗುಳಿಗಳನ್ನು ಬಲಪಡಿಸಿದ್ದಾನೆ; ನಿನ್ನ ಮಕ್ಕಳನ್ನು ಆಶೀರ್ವದಿಸಿದ್ದಾನೆ.",
			"14": "<sup>14</sup> ಆತನು ನಿನ್ನ ಪ್ರಾಂತದೊಳಗೆ ಸೌಭಾಗ್ಯವನ್ನುಂಟುಮಾಡುತ್ತಾನೆ; ಶ್ರೇಷ್ಠವಾದ ಗೋದಿಯಿಂದ ನಿನ್ನನ್ನು ತೃಪ್ತಿಗೊಳಿಸುತ್ತಾನೆ.",
			"15": "<sup>15</sup> ತನ್ನ ನುಡಿಯನ್ನು ಭೂಲೋಕಕ್ಕೆ ಕಳುಹಿಸುತ್ತಾನೆ; ಆತನ ವಾಕ್ಯವು ಬಹುವೇಗಶಾಲಿಯಾಗಿದೆ.",
			"16": "<sup>16</sup> ಉಣ್ಣೆಯಂತಿರುವ ಹಿಮವನ್ನು ಬೀಳಿಸುತ್ತಾನೆ; ಇಬ್ಬನಿಯನ್ನು ಬೂದಿಯಂತೆ ಹರವುತ್ತಾನೆ.",
			"17": "<sup>17</sup> ರೊಟ್ಟೀತುಂಡುಗಳಂತಿರುವ ಆನೆಕಲ್ಲುಗಳನ್ನು ಸುರಿಸುತ್ತಾನೆ; ಆತನು ಚಳಿಯನ್ನು ಬರಮಾಡಲು ಅದನ್ನು ಸಹಿಸುವವರು ಯಾರು?",
			"18": "<sup>18</sup> ಆತನು ಅಪ್ಪಣೆಕೊಡಲು ಅವು ಕರಗುತ್ತವೆ; ತನ್ನ ಗಾಳಿಯನ್ನು ಬೀಸಮಾಡಲು ನೀರು ಹರಿಯುತ್ತದೆ.",
			"19": "<sup>19</sup> ಆತನು ತನ್ನ ವಾಕ್ಯವನ್ನು ಯಾಕೋಬ್ಯರಿಗೆ ತಿಳಿಸುತ್ತಾನೆ; ತನ್ನ ನಿಯಮವಿಧಿಗಳನ್ನು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಪ್ರಕಟಿಸುತ್ತಾನೆ.",
			"20": "<sup>20</sup> ಬೇರೆ ಯಾವ ಜನಾಂಗದವರಿಗೂ ಆತನು ಹೀಗೆ ಮಾಡಲಿಲ್ಲ; ಆತನ ನ್ಯಾಯವಿಧಿಗಳನ್ನು ಅವರು ಅರಿಯರು. ಯಾಹುವಿಗೆ ಸ್ತೋತ್ರ!"
		},
		"148": {
			"1": "<sup>1</sup> ಯಾಹುವಿಗೆ ಸ್ತೋತ್ರ! ಆಕಾಶಮಂಡಲದಿಂದ ಯೆಹೋವನಿಗೆ ಸ್ತುತಿಯುಂಟಾಗಲಿ; ಮಹೋನ್ನತದಲ್ಲಿ ಆತನ ಸ್ತೋತ್ರವು ಕೇಳಿಸಲಿ.",
			"2": "<sup>2</sup> ಆತನ ಎಲ್ಲಾ ದೂತರೇ, ಆತನನ್ನು ಸ್ತುತಿಸಿರಿ; ಆತನ ಎಲ್ಲಾ ಸೈನ್ಯಗಳೇ, ಆತನನ್ನು ಸ್ತುತಿಸಿರಿ.",
			"3": "<sup>3</sup> ಸೂರ್ಯಚಂದ್ರರೇ, ಆತನನ್ನು ಸ್ತುತಿಸಿರಿ; ಹೊಳೆಯುವ ಎಲ್ಲಾ ನಕ್ಷತ್ರಗಳೇ, ಆತನನ್ನು ಸ್ತುತಿಸಿರಿ.",
			"4": "<sup>4</sup> ಉನ್ನತೋನ್ನತವಾದ ಆಕಾಶವೇ, ಅದರ ಮೇಲಿರುವ ಜಲರಾಶಿಗಳೇ, ಆತನನ್ನು ಸ್ತುತಿಸಿರಿ.",
			"5": "<sup>5</sup> ಅವು ಯೆಹೋವನ ನಾಮವನ್ನು ಸ್ತುತಿಸಲಿ; ಆತನು ಅಪ್ಪಣೆಕೊಡಲು ಅವು ಉಂಟಾದವು.",
			"6": "<sup>6</sup> ಅವನ್ನು ಯುಗಯುಗಾಂತರಕ್ಕೂ ಸ್ಥಾಪಿಸಿದ್ದಾನೆ; ಆತನು ಎಂದಿಗೂ ಮೀರಲಾಗದಂಥ ಕಟ್ಟಳೆಯನ್ನು ವಿಧಿಸಿದ್ದಾನೆ.",
			"7": "<sup>7</sup> ಭೂಮಂಡಲದಿಂದ ಯೆಹೋವನಿಗೆ ಸ್ತುತಿಯುಂಟಾಗಲಿ. ತಿಮಿಂಗಿಲಗಳೂ ಆದಿಸಾಗರಗಳೂ",
			"8": "<sup>8</sup> ಬೆಂಕಿ, ಕಲ್ಮಳೆ, ಹಿಮ, ಹಬೆ ಇವುಗಳೂ ಆತನ ಅಪ್ಪಣೆಯನ್ನು ನೆರವೇರಿಸುವ ಬಿರುಗಾಳಿಯೂ",
			"9": "<sup>9</sup> ಬೆಟ್ಟಗಳೂ ಎಲ್ಲಾ ಗುಡ್ಡಗಳೂ ಹಣ್ಣಿನ ಮರಗಳೂ ಎಲ್ಲಾ ತುರಾಯಿ ಗಿಡಗಳೂ",
			"10": "<sup>10</sup> ಎಲ್ಲಾ ಮೃಗಪಶುಪಕ್ಷಿಕ್ರಿಮಿಕೀಟಗಳೂ",
			"11": "<sup>11</sup> ಭೂರಾಜರೂ ಎಲ್ಲಾ ಜನಾಂಗಗಳೂ ಪ್ರಭುಗಳೂ ಸರ್ವದೇಶಾಧಿಪತಿಗಳೂ",
			"12": "<sup>12</sup> ಪ್ರಾಯಸ್ಥರಾದ ಸ್ತ್ರೀಪುರುಷರೂ ಮುದುಕರೂ ಹುಡುಗರೂ",
			"13": "<sup>13</sup> ಯೆಹೋವನನ್ನು ಕೊಂಡಾಡಲಿ. ಆತನ ನಾಮವೊಂದೇ ಮಹತ್ವವುಳ್ಳದ್ದು; ಆತನ ಪ್ರಭಾವವು ಭೂಮ್ಯಾಕಾಶಗಳಲ್ಲಿ ಮೆರೆಯುತ್ತದೆ.",
			"14": "<sup>14</sup> ಆತನು ತನ್ನ ಪ್ರಜೆಗೋಸ್ಕರ [ಘನದ] ಕೊಂಬನ್ನು ಎಬ್ಬಿಸಿದ್ದಾನೆ. ಆದದರಿಂದ ಆತನ ಎಲ್ಲಾ ಭಕ್ತರೂ ಆತನ ಸಮೀಪ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಾಯೇಲ್ಯರೂ ಹಿಗ್ಗುತ್ತಾರೆ. ಯಾಹುವಿಗೆ ಸ್ತೋತ್ರ!"
		},
		"149": {
			"1": "<sup>1</sup> ಯಾಹುವಿಗೆ ಸ್ತೋತ್ರ! ಯೆಹೋವನಿಗೆ ನೂತನಕೀರ್ತನೆಯನ್ನು ಹಾಡಿರಿ; ಭಕ್ತಸಭೆಯಲ್ಲಿ ಆತನನ್ನು ಸ್ತುತಿಸಿರಿ.",
			"2": "<sup>2</sup> ಇಸ್ರಾಯೇಲ್ಯರು ತಮ್ಮ ಸೃಷ್ಟಿಕರ್ತನಲ್ಲಿ ಹೆಚ್ಚಳಪಡಲಿ; ಚೀಯೋನಿನವರು ತಮ್ಮ ರಾಜನಲ್ಲಿ ಹರ್ಷಿಸಲಿ.",
			"3": "<sup>3</sup> ಕುಣಿಯುತ್ತಾ ಆತನ ನಾಮವನ್ನು ಕೀರ್ತಿಸಲಿ; ದಮ್ಮಡಿಕಿನ್ನರಿಗಳಿಂದ ಆತನನ್ನು ಭಜಿಸಲಿ.",
			"4": "<sup>4</sup> ಯೆಹೋವನು ತನ್ನ ಪ್ರಜೆಗೆ ಪ್ರಸನ್ನನಾದನು; ದೀನರನ್ನು ರಕ್ಷಣೆಯಿಂದ ಭೂಷಿಸುತ್ತಾನೆ.",
			"5": "<sup>5</sup> ಭಕ್ತರು ತಮಗುಂಟಾದ ಮಹಿಮೆಯಲ್ಲಿ ಹಿಗ್ಗಲಿ; ಹಾಸಿಗೆಯ ಮೇಲಿರುವಾಗಲೂ ಉತ್ಸಾಹಧ್ವನಿ ಮಾಡಲಿ.",
			"6": "<sup>6</sup> ಅವರ ಬಾಯಲ್ಲಿ ಯೆಹೋವನ ಸ್ತೋತ್ರವೂ ಕೈಯಲ್ಲಿ ಇಬ್ಬಾಯಿಕತ್ತಿಯೂ ಇರಲಿ.",
			"7": "<sup>7</sup> ಅವರು ಜನಾಂಗಗಳಿಗೆ ಮುಯ್ಯಿತೀರಿಸುವರು; ಅನ್ಯಜನಗಳನ್ನು ದಂಡಿಸುವರು;",
			"8": "<sup>8</sup> ಅವರ ರಾಜರನ್ನು ಸಂಕೋಲೆಯಿಂದ ಬಂಧಿಸುವರು; ಪ್ರಭುಗಳಿಗೆ ಕಬ್ಬಿಣದ ಬೇಡಿಗಳನ್ನು ಹಾಕುವರು.",
			"9": "<sup>9</sup> ಲಿಖಿತದಂತೆ ಅವರಿಗೆ ಶಿಕ್ಷೆಯನ್ನು ವಿಧಿಸುವರು; ಇದು ಆತನ ಭಕ್ತರೆಲ್ಲರಿಗೆ ಗೌರವವಾಗಿರುವದು. ಯಾಹುವಿಗೆ ಸ್ತೋತ್ರ!"
		},
		"150": {
			"1": "<sup>1</sup> ಯಾಹುವಿಗೆ ಸ್ತೋತ್ರ! ದೇವರನ್ನು ಆತನ ಪರಿಶುದ್ಧಾಲಯದಲ್ಲಿ ಸ್ತುತಿಸಿರಿ; ಆತನ ಶಕ್ತಿಪ್ರದರ್ಶಕವಾದ ಆಕಾಶಮಂಡಲದಲ್ಲಿ ಆತನನ್ನು ಸ್ತುತಿಸಿರಿ.",
			"2": "<sup>2</sup> ಆತನ ಮಹತ್ಕಾರ್ಯಗಳಿಗಾಗಿ ಆತನನ್ನು ಸ್ತುತಿಸಿರಿ; ಆತನ ಮಹಾಪ್ರಭಾವಕ್ಕೆ ಸರಿಯಾಗಿ ಆತನನ್ನು ಸ್ತುತಿಸಿರಿ.",
			"3": "<sup>3</sup> ಕೊಂಬೂದುತ್ತಾ ಆತನನ್ನು ಸ್ತುತಿಸಿರಿ; ಸ್ವರಮಂಡಲಕಿನ್ನರಿಗಳಿಂದ ಆತನನ್ನು ಸ್ತುತಿಸಿರಿ.",
			"4": "<sup>4</sup> ದಮ್ಮಡಿಬಡಿಯುತ್ತಾ ಕುಣಿಯುತ್ತಾ ಆತನನ್ನು ಸ್ತುತಿಸಿರಿ; ತಂತೀವಾದ್ಯಗಳಿಂದಲೂ ಕೊಳಲುಗಳಿಂದಲೂ ಆತನನ್ನು ಸ್ತುತಿಸಿರಿ.",
			"5": "<sup>5</sup> ತಾಳದಿಂದ ಆತನನ್ನು ಸ್ತುತಿಸಿರಿ; ಝಲ್ಲರಿಯಿಂದ ಆತನನ್ನು ಸ್ತುತಿಸಿರಿ.",
			"6": "<sup>6</sup> ಶ್ವಾಸವಿರುವದೆಲ್ಲವೂ ಯೆಹೋವನನ್ನು ಸ್ತುತಿಸಲಿ; ಯಾಹುವಿಗೆ ಸ್ತೋತ್ರ!"
		}
	}
};
module.exports = book;