var book = {
	"name": "ಯೆಶಾಯ",
	"numChapters": 66,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ಯೆಹೂದ ದೇಶದ ಅರಸರಾದ ಉಜ್ಜೀಯ, ಯೋಥಾಮ, ಆಹಾಜ, ಹಿಜ್ಕೀಯ, ಇವರ ಕಾಲದಲ್ಲಿ ಯೆಹೂದದ ಮತ್ತು ಯೆರೂಸಲೇಮಿನ ವಿಷಯವಾಗಿ ಆಮೋಚನ ಮಗನಾದ ಯೆಶಾಯನಿಗೆ ಆದ ದೈವದರ್ಶನ.",
			"2": "<sup>2</sup> ಆಕಾಶಮಂಡಲವೇ, ಆಲಿಸು! ಭೂಮಂಡಲವೇ, ಕೇಳು! ಯೆಹೋವನು ಮಾತಾಡುತ್ತಿದ್ದಾನೆ; ನಾನು ಸಾಕಿ ಸಲಹಿದ ಮಕ್ಕಳೇ ನನಗೆ ದ್ರೋಹಮಾಡಿದ್ದಾರೆ.",
			"3": "<sup>3</sup> ಎತ್ತು ಯಜಮಾನನನ್ನು, ಕತ್ತೆ ಒಡೆಯನ [ಕೊಟ್ಟಿಗೆಯ] ಗೋದಲಿಯನ್ನು ತಿಳಿದಿರುವವು; ಇಸ್ರಾಯೇಲ್ ಜನರಿಗೋ ತಿಳುವಳಿಕೆಯಿಲ್ಲ, ನನ್ನ ಪ್ರಜೆಯು ಆಲೋಚಿಸುವದಿಲ್ಲ.",
			"4": "<sup>4</sup> ಪಾಪಿಷ್ಠಜನವೇ, ಅಧರ್ಮಭಾರ ಹೊತ್ತಿರುವ ಪ್ರಜೆಯೇ, ದುಷ್ಟಜಾತಿಯೇ, ದ್ರೋಹಿಗಳಾದ ಮಕ್ಕಳೇ, ನಿಮ್ಮ ಗತಿಯನ್ನು ಏನು ಹೇಳಲಿ! ಯೆಹೋವನನ್ನು ತೊರೆದಿದ್ದಾರೆ, ಇಸ್ರಾಯೇಲ್ಯರ ಸದಮಲಸ್ವಾಮಿಯನ್ನು ಧಿಕ್ಕರಿಸಿದ್ದಾರೆ, ಆತನಿಗೆ ಬೆನ್ನುಮಾಡಿ ಬೇರೆಯಾಗಿದ್ದಾರೆ.",
			"5": "<sup>5</sup> ಏಕೆ ದ್ರೋಹವನ್ನು ಹೆಚ್ಚಿಸಿ ಹೆಚ್ಚಿಸಿ ಪೆಟ್ಟಿಗೆ ಗುರಿಯಾಗುತ್ತೀರಿ? ತಲೆಯೆಲ್ಲಾ ರೋಗ, ಹೃದಯವೆಲ್ಲಾ ದುರ್ಬಲ.",
			"6": "<sup>6</sup> ಅಂಗಾಲಿನಿಂದ ನಡುನೆತ್ತಿಯ ತನಕ ಪೆಟ್ಟು ಬಾಸುಂಡೆ ಮಾಗದ ಗಾಯಗಳೇ ಹೊರತು ಏನೂ ಸೌಖ್ಯವಿಲ್ಲ; ಅವನ್ನು ಹಿಸಕಿ ಮುಚ್ಚಿಲ್ಲ, ಕಟ್ಟಿಲ್ಲ, ಎಣ್ಣೆಸವರಿ ಮೃದು ಮಾಡಿಲ್ಲ.",
			"7": "<sup>7</sup> ನಿಮ್ಮ ದೇಶವು ಹಾಳಾಗಿದೆ; ನಿಮ್ಮ ಪಟ್ಟಣಗಳು ಸುಟ್ಟು ಹೋಗಿವೆ; ನಿಮ್ಮ ಭೂಮಿಯನ್ನು ಅನ್ಯರು ನಿಮ್ಮೆದುರಿಗೆ ನುಂಗಿಬಿಡುತ್ತಿದ್ದಾರೆ; ಅನ್ಯದೇಶಗಳು ನಾಶನವಾದಂತೆಯೇ ನಿಮ್ಮ ಭೂಮಿಯೂ ಹಾಳಾಯಿತು.",
			"8": "<sup>8</sup> ಚೀಯೋನ್ ನಗರಿಯೊಂದೇ ಉಳಿದು ದ್ರಾಕ್ಷೇತೋಟದ ಮಂಚಿಕೆಯಂತೆಯೂ ಸವುತೆಯ ಹೊಲದ ಗುಡಸಲಿನ ಹಾಗೂ ಮುತ್ತಿದ ಪಟ್ಟಣದೋಪಾದಿಯಲ್ಲಿಯೂ ಇದೆ.",
			"9": "<sup>9</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ನಮಗೆ ಕೊಂಚ ಜನವನ್ನೂ ಉಳಿಸದೆ ಹೋಗಿದ್ದರೆ ಸೊದೋಮಿನ ಗತಿಯೇ ನಮಗಾಗುತ್ತಿತ್ತು. ಗೊಮೋರದ ದುರ್ದಶೆಯೇ ಸಂಭವಿಸುತ್ತಿತ್ತು.",
			"10": "<sup>10</sup> ಸೊದೋಮಿನ ಅಧಿಪತಿಗಳೇ, ಯೆಹೋವನ ಮಾತನ್ನು ಆಲಿಸಿರಿ; ಗೊಮೋರದ ಪ್ರಜೆಗಳೇ, ನಮ್ಮ ದೇವರ ಧರ್ಮೋಪದೇಶವನ್ನು ಕೇಳಿರಿ!",
			"11": "<sup>11</sup> ಯೆಹೋವನು ಹೀಗೆನ್ನುತ್ತಾನೆ - ಲೆಕ್ಕವಿಲ್ಲದ ನಿಮ್ಮ ಯಜ್ಞಗಳು ನನಗೇಕೆ? ಟಗರುಗಳ ಸರ್ವಾಂಗಹೋಮ, ಪುಷ್ಟಪಶುಗಳ ಕೊಬ್ಬು, ಇದೆಲ್ಲಾ ನನಗೆ ಸಾಕಾಯಿತು; ಹೋರಿಕುರಿಹೋತಗಳ ರಕ್ತಕ್ಕೆ ನಾನು ಒಲಿಯೆನು.",
			"12": "<sup>12</sup> ನನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಕಾಣಿಸಿಕೊಳ್ಳಬೇಕೆಂದು ನೀವು ಬರುತ್ತೀರಲ್ಲಾ; ನನ್ನ ಪ್ರಾಕಾರಗಳನ್ನು ತುಳಿಯಲು ಯಾರು ನಿಮ್ಮನ್ನು ಕೇಳಿಕೊಂಡರು?",
			"13": "<sup>13</sup> ವ್ಯರ್ಥನೈವೇದ್ಯವನ್ನು ಇನ್ನು ತಾರದಿರಿ, ಧೂಪವು ನನಗೆ ಅಸಹ್ಯ; ಅಮಾವಾಸ್ಯೆ, ಸಬ್ಬತ್‍ದಿನ, ಕೂಟಪ್ರಕಟನೆ, ಇವು ಬೇಡ; ಅಧರ್ಮದಿಂದ ಕೂಡಿದ ಸಂಘವನ್ನು ನಾನು ಸಹಿಸಲಾರೆನು.",
			"14": "<sup>14</sup> ನಿಮ್ಮ ಅಮಾವಾಸ್ಯೆಗಳನ್ನೂ ಉತ್ಸವದಿನಗಳನ್ನೂ ಹಗೆ ಮಾಡುತ್ತೇನೆ; ಇವು ನನಗೆ ಭಾರ; ಸಹಿಸಲು ಬೇಸರ.",
			"15": "<sup>15</sup> ನೀವು [ನನ್ನ ಕಡೆಗೆ] ಕೈಯೆತ್ತುವಾಗ ನಿಮ್ಮನ್ನು ನೋಡದಂತೆ ನನ್ನ ಕಣ್ಣುಗಳನ್ನು ಮರೆಮಾಡಿಕೊಳ್ಳುವೆನು; ಹೌದು, ನೀವು ಬಹು ಪ್ರಾರ್ಥನೆಗಳನ್ನು ಮಾಡಿದರೂ ನಾನು ಕೇಳೆನು, ನಿಮ್ಮ ಕೈಗಳಲ್ಲಿ ರಕ್ತವು ತುಂಬಿದೆ.",
			"16": "<sup>16</sup> ನಿಮ್ಮನ್ನು ತೊಳೆದುಕೊಳ್ಳಿರಿ, ಶುದ್ಧಿಮಾಡಿಕೊಳ್ಳಿರಿ, ನನ್ನ ಕಣ್ಣೆದುರಿನ ನಿಮ್ಮ ದುಷ್ಕೃತ್ಯಗಳನ್ನು ತೊಲಗಿಸಿರಿ, ದುರಾಚಾರವನ್ನು ಬಿಡಿರಿ;",
			"17": "<sup>17</sup> ಸದಾಚಾರವನ್ನು ಅಭ್ಯಾಸಮಾಡಿರಿ; ನ್ಯಾಯನಿರತರಾಗಿರಿ, ಹಿಂಸಕನನ್ನು ತಿದ್ದಿ ಸರಿಪಡಿಸಿರಿ, ಅನಾಥನಿಗೆ ನ್ಯಾಯತೀರಿಸಿರಿ, ವಿಧವೆಯ ಪಕ್ಷವಾಗಿ ವಾದಿಸಿರಿ.",
			"18": "<sup>18</sup> ಬನ್ನಿರಿ ವಾದಿಸುವ ಎಂದು ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ; ನಿಮ್ಮ ಪಾಪಗಳು ಕಡು ಕೆಂಪಾಗಿದ್ದರೂ ಹಿಮದ ಹಾಗೆ ಬಿಳುಪಾಗುವವು; ಕಿರಮಂಜಿಬಣ್ಣವಾಗಿದ್ದರೂ ಉಣ್ಣೆಯಂತೆ ಬೆಳ್ಳಗಾಗುವವು.",
			"19": "<sup>19</sup> ನೀವು ಒಪ್ಪಿ ವಿಧೇಯರಾದರೆ ದೇಶದ ಮೇಲನ್ನು ಅನುಭವಿಸುವಿರಿ;",
			"20": "<sup>20</sup> ಒಪ್ಪದೆ ತಿರುಗಿ ಬಿದ್ದರೆ ಕತ್ತಿಯ ಬಾಯಿಗೆ ತುತ್ತಾಗುವಿರಿ; ಈ ಮಾತು ಯೆಹೋವನೇ ನುಡಿದದ್ದು.",
			"21": "<sup>21</sup> ಅಯ್ಯೋ, ಸುವ್ರತೆಯಾಗಿದ್ದ ನಗರಿಯು ಸೂಳೆಯಾದಳಲ್ಲಾ! ನ್ಯಾಯದಿಂದ ತುಂಬಿ ಧರ್ಮಕ್ಕೆ ನೆಲೆಯಾಗಿದ್ದವಳು ಈಗ ಕೊಲೆಪಾತಕರಿಗೆ ನೆಲೆಯಾಗಿದ್ದಾಳೆ.",
			"22": "<sup>22</sup> ನಿನ್ನ ಬೆಳ್ಳಿಯು ಕಂದಾಯಿತು, ನಿನ್ನ ದ್ರಾಕ್ಷಾರಸವು ನೀರಾಯಿತು.",
			"23": "<sup>23</sup> ನಿನ್ನ ಅಧಿಕಾರಿಗಳು ದ್ರೋಹಿಗಳೂ ಕಳ್ಳರ ಗೆಳೆಯರೂ ಆಗಿದ್ದಾರೆ; ಪ್ರತಿಯೊಬ್ಬನೂ ಕಾಣಿಕೆಗಳನ್ನು ಆಶಿಸಿ ಲಂಚಗಳನ್ನು ಹುಡುಕುವನು; ಅನಾಥರಿಗೆ ನ್ಯಾಯತೀರಿಸರು; ವಿಧವೆಯರ ವ್ಯಾಜ್ಯವು ಅವರ ಮನಸ್ಸಿಗೆ ಬಾರದು.",
			"24": "<sup>24</sup> ಇಂತಿರಲು ಕರ್ತನೂ ಸೇನಾಧೀಶ್ವರನೂ ಇಸ್ರಾಯೇಲ್ಯರ ಶೂರನೂ ಆಗಿರುವ ಯೆಹೋವನು ಹೀಗೆ ನುಡಿಯುತ್ತಾನೆ - ಆಹಾ, ನನ್ನ ವಿರೋಧಿಗಳನ್ನು ಅಡಗಿಸಿ ಶಾಂತನಾಗುವೆನು, ನನ್ನ ಶತ್ರುಗಳಿಗೆ ಮುಯ್ಯಿತೀರಿಸುವೆನು;",
			"25": "<sup>25</sup> ನಿನ್ನ ಮೇಲೆ ಕೈಮಾಡಿ ಪುಟಹಾಕಿ ನಿನ್ನ ಕಲ್ಮಷವನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ಹೋಗಲಾಡಿಸಿ ನಿನ್ನ ಕಂದುಕಸರನ್ನೆಲ್ಲಾ ತೆಗೆದು ಬಿಡುವೆನು.",
			"26": "<sup>26</sup> ಪೂರ್ವದಲ್ಲಿ ನಿನಗಿದ್ದಂಥ ನ್ಯಾಯಾಧಿಪತಿಗಳನ್ನೂ ಮಂತ್ರಾಲೋಚಕರನ್ನೂ ಪುನಃ ಒದಗಿಸಿಕೊಡುವೆನು; ಆಮೇಲೆ ನೀನು ಧರ್ಮಪುರಿ ಎಂತಲೂ ಸುವ್ರತನಗರಿ ಎಂತಲೂ ಅನ್ನಿಸಿಕೊಳ್ಳುವಿ.",
			"27": "<sup>27</sup> ಚೀಯೋನ್ ಪಟ್ಟಣಕ್ಕೆ [ನನ್ನ] ನ್ಯಾಯದ ಮೂಲಕವೂ ದೇಶಾಂತರದಿಂದ ಬಿಡುಗಡೆಯಾಗಿ ಬರುವ ಅದರ ಪ್ರಜೆಗಳಿಗೆ [ನನ್ನ] ಧರ್ಮದ ಮೂಲಕವೂ ವಿಮೋಚನೆಯಾಗುವದು.",
			"28": "<sup>28</sup> ಆದರೆ ದ್ರೋಹಿಗಳೂ ಪಾಪಿಗಳೂ ಒಟ್ಟಾಗಿ ನಾಶನವಾಗುವರು; ಹೌದು, ಯೆಹೋವನನ್ನು ತೊರೆದವರು ನಿರ್ಮೂಲರಾಗುವರು.",
			"29": "<sup>29</sup> ನೀವು ಇಷ್ಟಪಟ್ಟ ಏಲಾಮರಗಳ ನಿಮಿತ್ತ ನಾಚಿಕೆಗೊಳ್ಳುವಿರಿ, ಗೊತ್ತುಮಾಡಿಕೊಂಡ ವನಗಳ ವಿಷಯವಾಗಿ ಲಜ್ಜೆಪಡುವಿರಿ.",
			"30": "<sup>30</sup> ಎಲೆ ಒಣಗಿದ ಏಲಾಮರದಂತೆಯೂ ಜಲವಿಲ್ಲದ ವನದ ಹಾಗೂ ಇರುವಿರಿ.",
			"31": "<sup>31</sup> ನಿಮ್ಮಲ್ಲಿನ ಬಲಿಷ್ಠನೇ ಸಣಬಿನ ಗುಂಜು, ಅವನ ಕಾರ್ಯವೇ ಕಿಡಿ, ಎರಡು ಸೇರಿ ಯಾರೂ ನಂದಿಸಲಾಗದಂತೆ ಸುಟ್ಟುಹೋಗುವವು."
		},
		"2": {
			"1": "<sup>1</sup> ಯೆಹೂದದ ಮತ್ತು ಯೆರೂಸಲೇಮಿನ ವಿಷಯವಾಗಿ ಆಮೋಚನ ಮಗನಾದ ಯೆಶಾಯನಿಗೆ ಕಂಡು ಬಂದ ದೈವೋಕ್ತಿ -",
			"2": "<sup>2</sup> ಅಂತ್ಯಕಾಲದಲ್ಲಿ ಯೆಹೋವನ ಮಂದಿರದ ಬೆಟ್ಟವು ಗುಡ್ಡಬೆಟ್ಟಗಳಿಗಿಂತ ಉನ್ನತೋನ್ನತವಾಗಿ ಬೆಳೆದು ನೆಲೆಗೊಳ್ಳುವದು; ಆಗ ಸಕಲದೇಶಗಳವರು ಅದರ ಕಡೆಗೆ ಪ್ರವಾಹಗಳಂತೆ ಬರುವರು.",
			"3": "<sup>3</sup> ಹೊರಟುಬಂದ ಬಹು ಜನಾಂಗದವರು - ಬನ್ನಿರಿ, ಯೆಹೋವನ ಪರ್ವತಕ್ಕೆ, ಯಾಕೋಬ್ಯರ ದೇವರ ಮಂದಿರಕ್ಕೆ, ಹೋಗೋಣ! ಆತನು ತನ್ನ ಮಾರ್ಗಗಳ ವಿಷಯವಾಗಿ ನಮಗೆ ಬೋಧನೆ ಮಾಡುವನು, ನಾವು ಆತನ ದಾರಿಗಳಲ್ಲಿ ನಡೆಯುವೆವು ಎಂದು ಹೇಳುವರು. ಏಕಂದರೆ ಚೀಯೋನಿನಿಂದ ಧರ್ಮೋಪದೇಶವೂ ಯೆರೂಸಲೇಮಿನಿಂದ ಯೆಹೋವನ ವಾಕ್ಯವೂ ಹೊರಡುವವು.",
			"4": "<sup>4</sup> ಆತನು ದೇಶದೇಶಗಳ ವ್ಯಾಜ್ಯಗಳನ್ನು ವಿಚಾರಿಸುವನು, ಬಹು ರಾಷ್ಟ್ರದವರಿಗೆ ನ್ಯಾಯತೀರಿಸುವನು; ಅವರೋ ತಮ್ಮ [ಆಯುಧಗಳನ್ನು] ಕುಲುಮೆಗೆ ಹಾಕಿ ಕತ್ತಿಗಳನ್ನು ಗುಳಗಳನ್ನಾಗಿಯೂ ಬರ್ಜಿಗಳನ್ನು ಕುಡುಗೋಲುಗಳನ್ನಾಗಿಯೂ ಮಾಡುವರು; ಜನಾಂಗವು ಜನಾಂಗಕ್ಕೆ ವಿರುದ್ಧವಾಗಿ ಕತ್ತಿಯನ್ನೆತ್ತದು, ಇನ್ನು ಯುದ್ಧಾಭ್ಯಾಸವು ನಡೆಯುವದೇ ಇಲ್ಲ.",
			"5": "<sup>5</sup> ಯಾಕೋಬನ ಮನೆತನದವರೇ, ಬನ್ನಿರಿ, ಯೆಹೋವನ ಜ್ಞಾನಪ್ರಕಾಶದಲ್ಲಿ ನಡೆಯುವ!",
			"6": "<sup>6</sup> ಯಾಕೋಬನ ಮನೆತನದವರು ಮೂಡಣ ದೇಶಗಳ [ಮಂತ್ರತಂತ್ರಗಳಲ್ಲಿ] ಮಗ್ನರೂ ಫಿಲಿಷ್ಟಿಯರ ಹಾಗೆ ಕಣಿಹೇಳುವವರೂ ಅನ್ಯದೇಶಗಳವರ ಸಂಗಡ ಒಪ್ಪಂದಮಾಡುವವರೂ ಆಗಿರುವದರಿಂದ ಈ ನಿನ್ನ ಜನರನ್ನು ಕೈಬಿಟ್ಟಿದ್ದೀಯಷ್ಟೆ.",
			"7": "<sup>7</sup> ಇವರ ದೇಶವು ಬೆಳ್ಳಿ ಬಂಗಾರಗಳಿಂದ ತುಂಬಿದೆ, ಇವರ ನಿಧಿ ನಿಕ್ಷೇಪಗಳಿಗೆ ಮಿತಿಯಿಲ್ಲ; ಇವರ ದೇಶವು ಅಶ್ವಬಲಭರಿತವಾಗಿದೆ, ಇವರ ರಥಗಳಿಗೆ ಪಾರವಿಲ್ಲ;",
			"8": "<sup>8</sup> ಇವರ ದೇಶವು ವಿಗ್ರಹಗಳಿಂದಲೂ ತುಂಬಿದೆ, ತಮ್ಮ ಕೈಯಿಂದಲೇ, ತಮ್ಮ ಬೆರಳುಗಳಿಂದಲೇ ಮಾಡಿದ ಕೆಲಸಕ್ಕೆ ನಮಸ್ಕರಿಸುತ್ತಾರೆ.",
			"9": "<sup>9</sup> ಹೀಗಿರಲು ಅಧಮರಾಗಲಿ ಉತ್ತಮರಾಗಲಿ ತಗ್ಗಿಸಲ್ಪಡುವರು; (ಅವರನ್ನು ಕ್ಷಮಿಸಬೇಡ).",
			"10": "<sup>10</sup> ಗವಿಯೊಳಗೆ ನುಗ್ಗು, ಯೆಹೋವನ ಭಯಂಕರತನಕ್ಕೂ ಅತ್ಯುನ್ನತ ಮಹಿಮೆಗೂ ಹೆದರಿ ದೂಳಿನಲ್ಲಿ ಅವುತುಕೋ!",
			"11": "<sup>11</sup> ಸಾಮಾನ್ಯರ ಗರ್ವದೃಷ್ಟಿಯು ತಗ್ಗಿಹೋಗುವದು, ಮುಖಂಡರ ಅಹಂಕಾರವು ಕುಗ್ಗಿಹೋಗುವದು, ಯೆಹೋವನೊಬ್ಬನೇ ಆ ದಿನದಲ್ಲಿ ಉನ್ನತೋನ್ನತನಾಗಿರುವನು.",
			"12": "<sup>12</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನ [ನ್ಯಾಯನಿರ್ಣಯ] ದಿವಸವು ಗರ್ವದಿಂದ ಹೆಚ್ಚಿಕೊಂಡಿರುವ ಸಮಸ್ತಕ್ಕೂ ಸಂಭವಿಸುವದು; ಆಗ ಅದೆಲ್ಲಾ ತಗ್ಗುವದು.",
			"13": "<sup>13</sup> ಎತ್ತರವಾಗಿ ಬೆಳೆದಿರುವ ಲೆಬನೋನಿನ ಸರ್ವದೇವದಾರು ವೃಕ್ಷಗಳು, ಬಾಷಾನಿನ ಎಲ್ಲಾ ಅಲ್ಲೋನ್ ಮರಗಳು,",
			"14": "<sup>14</sup> ಶಿಖರಗಳನ್ನು ಉನ್ನತವಾಗಿ ಎತ್ತಿಕೊಂಡಿರುವ ಸಮಸ್ತ ಬೆಟ್ಟಗುಡ್ಡಗಳು,",
			"15": "<sup>15</sup> ಉದ್ದುದ್ದವಾದ ಸಕಲ ಗೋಪುರಗಳು, ದುರ್ಗಮವಾದ ಸಮಸ್ತ ಕೋಟೆಗಳು,",
			"16": "<sup>16</sup> ಎಲ್ಲಾ ದೊಡ್ಡ ದೊಡ್ಡ ಹಡಗುಗಳು, ಅಂತೂ ನೋಡತಕ್ಕ ಮನೋಹರವಾದ ಪ್ರತಿಯೊಂದು ವಸ್ತುವಿಗೂ ಆ ದಿನವು ತಪ್ಪದೆ ಬರುವದು.",
			"17": "<sup>17</sup> ಸಾಮಾನ್ಯರ ಗರ್ವವು ಕುಗ್ಗುವದು, ಮುಖಂಡರ ಅಹಂಕಾರವೂ ತಗ್ಗುವದು, ಯೆಹೋವನೊಬ್ಬನೇ ಆ ದಿನದಲ್ಲಿ ಉನ್ನತೋನ್ನತನಾಗಿರುವನು.",
			"18": "<sup>18</sup> ವಿಗ್ರಹಗಳು ಸಂಪೂರ್ಣವಾಗಿ ಹೋಗಿಬಿಡುವವು.",
			"19": "<sup>19</sup> ಯೆಹೋವನು ಭೂಮಂಡಲವನ್ನು ಕಂಪನ ಮಾಡುವದಕ್ಕೆ ಏಳುವಾಗ ಮನುಷ್ಯರು ಆತನ ಭಯಂಕರತನಕ್ಕೂ ಅತ್ಯುನ್ನತ ಮಹಿಮೆಗೂ ಹೆದರಿ ಬಂಡೆಗಳ ಗವಿಗಳಲ್ಲಿಯೂ ನೆಲದ ಹಳ್ಳಕೊಳ್ಳಗಳಲ್ಲಿಯೂ ಸೇರಿಕೊಳ್ಳುವರು.",
			"20": "<sup>20</sup> ಆ ದಿನದಲ್ಲಿ ಮನುಷ್ಯರು ತಮ್ಮ ಪೂಜೆಗೋಸ್ಕರ ಮಾಡಿಸಿಕೊಂಡ ಬೆಳ್ಳಿಯ ಬೊಂಬೆಗಳನ್ನೂ ಬಂಗಾರದ ವಿಗ್ರಹಗಳನ್ನೂ ಇಲಿಕಣ್ಣು ಕಪಟಗಳಿಗಾಗಿ ಬಿಸಾಟುಬಿಟ್ಟು",
			"21": "<sup>21</sup> ಯೆಹೋವನು ಭೂಮಂಡಲವನ್ನು ಕಂಪನ ಮಾಡುವದಕ್ಕೆ ಏಳುವಾಗ ಆತನ ಭಯಂಕರತನಕ್ಕೂ ಅತ್ಯುನ್ನತಮಹಿಮೆಗೂ ಹೆದರಿ ಬಂಡೆಗಳ ಸಂದುಗೊಂದುಗಳಲ್ಲಿಯೂ ಚೂಪಾದ ಶಿಲೆಗಳ ಸೀಳುಗಳಲ್ಲಿಯೂ ನುಗ್ಗುವರು.",
			"22": "<sup>22</sup> ಉಸಿರು ಮೂಗಿನಲ್ಲಿರುವ ತನಕ ಬದುಕುವ ನರಮನುಷ್ಯನನ್ನು ಬಿಟ್ಟುಬಿಡಿರಿ; ಅವನು ಯಾವ ಗಣನೆಗೆ ಬಂದಾನು?"
		},
		"3": {
			"1": "<sup>1</sup> ಇಗೋ, ಕರ್ತನೂ ಸೇನಾಧೀಶ್ವರನೂ ಆಗಿರುವ ಯೆಹೋವನೆಂಬ ನಾನು ಜೀವನಕ್ಕೆ ಆಧಾರಕೋದ್ಧಾರಕವಾದ ಅನ್ನಪಾನಗಳನ್ನೆಲ್ಲಾ ಯೆರೂಸಲೇಮಿನಿಂದಲೂ ಯೆಹೂದದಿಂದಲೂ ತೆಗೆದುಬಿಡುವೆನು.",
			"2": "<sup>2</sup> ಇದಲ್ಲದೆ ಶೂರ, ಭಟ, ನ್ಯಾಯಾಧಿಪತಿ, ಪ್ರವಾದಿ, ಶಕುನದವ, ಹಿರಿಯ, ಪಂಚದಶಾಧಿಪತಿ,",
			"3": "<sup>3</sup> ಘನವಂತ, ಮಂತ್ರಾಲೋಚಕ, ತಾಂತ್ರಿಕ, ಮಾಂತ್ರಿಕ, ಇವರೆಲ್ಲರನ್ನೂ ತೊಲಗಿಸಿಬಿಡುವೆನು.",
			"4": "<sup>4</sup> ಬಾಲಕರನ್ನು ಪ್ರಭುಗಳನ್ನಾಗಿ ಆ ದೇಶಕ್ಕೆ ನೇಮಿಸುವೆನು, ಬಾಲಚೇಷ್ಟೆಯು ಅದನ್ನಾಳುವದು.",
			"5": "<sup>5</sup> ಪ್ರಜೆಗಳು ಪರಸ್ಪರ ವಿರೋಧಿಗಳಾಗಿ ಒಬ್ಬರನ್ನೊಬ್ಬರು ಹಿಂಸಿಸುವರು; ಹುಡುಗನು ಮುದುಕನ ಮೇಲೆಯೂ ನೀಚನು ಘನವಂತನ ಮೇಲೆಯೂ ಸೊಕ್ಕೇರಿ ನಡೆಯುವರು.",
			"6": "<sup>6</sup> ಆ ದಿನದಲ್ಲಿ ಒಬ್ಬನು - ಎಲಾ, ಅಣ್ಣ, ನಿನಗೆ ನಿಲುವಂಗಿ ಇದೆ. ನೀನು ನಮಗೆ ಒಡೆಯನಾಗಬೇಕು, ಹಾಳಾದ ಈ ಪಟ್ಟಣವು ನಿನ್ನ ಕೈಕೆಳಗಿರಲಿ ಎಂದು ತಂದೆಯ ಮನೆಯೊಳಗೆ ಅಣ್ಣನನ್ನು ಬಲವಂತ ಮಾಡಲು",
			"7": "<sup>7</sup> ಅಣ್ಣನು ಹೂಂಕರಿಸಿ ದೇಶದ ವ್ರಣವೈದ್ಯನಾಗಿರುವದಕ್ಕೆ ನನಗಿಷ್ಟವಿಲ್ಲ, ನನ್ನ ಮನೆಯಲ್ಲಿ ಅನ್ನವೆಲ್ಲಿ, ಅಂಗಿಯೆಲ್ಲಿ? ನನ್ನನ್ನು ಜನದೊಡೆಯನನ್ನಾಗಿ ಮಾಡಬೇಡಿರಿ ಎಂದು ಹೇಳುವನು.",
			"8": "<sup>8</sup> ಯೆರೂಸಲೇಮು ಹಾಳಾಯಿತು, ಯೆಹೂದದವರು ಬಿದ್ದುಹೋದರು! ಅವರ ನಡೆನುಡಿಗಳು ಯೆಹೋವನಿಗೆ ವಿರುದ್ಧವಾಗಿ ಆತನ ಪ್ರಭಾವದ ದೃಷ್ಟಿಯನ್ನು ಪ್ರತಿಭಟಿಸುತ್ತವಲ್ಲವೆ.",
			"9": "<sup>9</sup> ಅವರ ಮುಖಭಾವವೇ ಅವರಿಗೆ ವಿರುದ್ಧ ಸಾಕ್ಷಿಯಾಗಿದೆ; ತಮ್ಮ ಪಾಪವನ್ನು ಮರೆಮಾಜದೆ ಸೊದೋಮಿನವರಂತೆ ಮೆರೆಯಿಸುತ್ತಾರೆ. ಅಯ್ಯೋ, ಅವರ ಆತ್ಮದ ಗತಿಯೇ! ತಮಗೆ ತಾವೇ ಕೇಡು ಮಾಡಿಕೊಂಡಿದ್ದಾರೆ.",
			"10": "<sup>10</sup> ಶಿಷ್ಟರಿಗೆ ಶುಭವೇ ಎಂದು ಹೇಳಿರಿ, ಅವರು ತಮ್ಮ ಸುಕೃತಫಲವನ್ನು ಅನುಭವಿಸುವರಷ್ಟೆ.",
			"11": "<sup>11</sup> ದುಷ್ಟರ ಗತಿಯನ್ನು ಏನು ಹೇಳಲಿ! ಅವರ ಕಾರ್ಯಗಳಿಗೆ ತಕ್ಕ ಪ್ರತಿಫಲವು ಅವರಿಗೆ ಲಭಿಸುವದಲ್ಲವೆ.",
			"12": "<sup>12</sup> ನನ್ನ ಜನರನ್ನೋ ಬಾಧಿಸುವವರು ಹುಡುಗರು, ಆಳುವವರು ಹೆಂಗಸರು. ನನ್ನ ಜನರೇ! ನಿಮ್ಮನ್ನು ನಡಿಸುವವರು ದಾರಿತಪ್ಪಿಸುವವರಾಗಿದ್ದಾರೆ, ನೀವು ನಡೆಯತಕ್ಕ ದಾರಿಯನ್ನು ಅಳಿಸಿಬಿಟ್ಟಿದ್ದಾರೆ.",
			"13": "<sup>13</sup> ಯೆಹೋವನು ವಾದಿಸುವದಕ್ಕೂ ಜನಗಳ ನ್ಯಾಯಾನ್ಯಾಯಗಳನ್ನು ನಿರ್ಣಯಿಸುವದಕ್ಕೂ ಎದ್ದು ನಿಂತಿದ್ದಾನೆ.",
			"14": "<sup>14</sup> ಯೆಹೋವನು ತನ್ನ ಜನರ ಹಿರಿಯರನ್ನೂ ಅಧಿಕಾರಿಗಳನ್ನೂ ನ್ಯಾಯವಿಚಾರಣೆಗೆ ತರುವನು; ನೀವು ದ್ರಾಕ್ಷೆಯ ತೋಟವನ್ನು ನುಂಗಿಬಿಟ್ಟಿದ್ದೀರಿ; ಬಡವರಿಂದ ಕೊಳ್ಳೆಹೊಡೆದದ್ದು ನಿಮ್ಮ ಮನೆಗಳಲ್ಲಿಯೇ ಇದೆ;",
			"15": "<sup>15</sup> ನೀವು ನನ್ನ ಜನರನ್ನು ಜಜ್ಜಿ ಬಡವರ ಮುಖವನ್ನು ಹಿಂಡುವದೇಕೆ ಎಂದು ಕರ್ತನಾದ ಸೇನಾಧೀಶ್ವರ ಯೆಹೋವನು ವಾದಿಸುವನು.",
			"16": "<sup>16</sup> ಇದಲ್ಲದೆ ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ಚೀಯೋನಿನ ಹೆಂಗಸರು ವಯ್ಯಾರವುಳ್ಳವರಾಗಿ ಕತ್ತು ತೂಗುತ್ತಾ ಕಡೆಗಣ್ಣುಹಾಕುತ್ತಾ ಕುಲುಕಿ ಹೆಜ್ಜೆಯಿಡುತ್ತಾ ಕಾಲುಗೆಜ್ಜೆ ಜಣಜಣಿಸುತ್ತಾ ನಡೆಯುವವರಾಗಿರುವದರಿಂದ",
			"17": "<sup>17</sup> ಕರ್ತನಾದ ಯೆಹೋವನು ಅವರ ನಡುನೆತ್ತಿಯನ್ನು ಹುಣ್ಣಿನಿಂದ ಬಾಧಿಸಿ ಅವರ ಮಾನವನ್ನು ಬೈಲುಮಾಡುವನು.",
			"18": "<sup>18</sup> ಆ ದಿನದಲ್ಲಿ ಕರ್ತನು ಅವರ ಅಂದುಗೆ, ತುರುಬು ಬಲೆ, ಅರ್ಧಚಂದ್ರ,",
			"19": "<sup>19</sup> ಜುಮಕಿ, ಬಳೆ, ಮುಸುಕು, ಮುಂಡಾಸ, ಕಾಲಸರಪಣಿ, ನಡುಕಟ್ಟು, ಗಂಧದ ಡಬ್ಬಿ, ತಾಯಿತಿ,",
			"21": "<sup>21</sup> ಮುದ್ರಿಕೆ, ಮೂಗುತಿ, ಹಬ್ಬದ ಬಟ್ಟೆ, ಮೇಲಂಗಿ,",
			"23": "<sup>23</sup> ಶಾಲು, ಚೀಲ, ಕೈಗನ್ನಡಿ, ನಾರುಮಡಿ, ಶಿರೋವೇಷ್ಟನ, ಮೇಲ್ಹೊದಿಕೆ, ಈ ಭೂಷಣಗಳನ್ನೆಲ್ಲಾ ತೆಗೆದುಹಾಕುವನು.",
			"24": "<sup>24</sup> ಮತ್ತು ಸುಗಂಧಕ್ಕೆ ಬದಲಾಗಿ ಕೊಳೆ, ನಡುಪಟ್ಟಿಯಿದ್ದಲ್ಲಿ ಹಗ್ಗ, ಅಂದವಾಗಿ ಸೆಕ್ಕಿದ ಜಡೆಯ ಸ್ಥಾನದಲ್ಲಿ ಬೋಡು, ನಡುವಿನ ಶಲ್ಯಕ್ಕೆ ಪ್ರತಿಯಾಗಿ ಗೋಣೀಪಟ್ಟಿ, ಬೆಡಗಿಗೆ ಬದಲಾಗಿ ಬರೆ, ಇವು ಆಗುವವು.",
			"25": "<sup>25</sup> [ಚೀಯೋನ್ ನಗರಿಯೇ,] ನಿನ್ನ ವೀರರು ಖಡ್ಗದಿಂದ ಬಿದ್ದುಹೋಗುವರು, ನಿನ್ನ ಶೌರ್ಯವು ಯುದ್ಧದಲ್ಲಿ ಅಡಗುವದು.",
			"26": "<sup>26</sup> ಅವಳ ಪುರದ್ವಾರಗಳಲ್ಲಿ ಪ್ರಲಾಪವೂ ದುಃಖವೂ ತುಂಬಿರುವವು; ಅವಳು ಹಾಳಾಗಿ ನೆಲದ ಮೇಲೆ ಕೂತುಬಿಡುವಳು."
		},
		"4": {
			"1": "<sup>1</sup> ಆ ದಿನದಲ್ಲಿ ಏಳು ಮಂದಿ ಹೆಂಗಸರು ಒಬ್ಬ ಗಂಡಸನ್ನು ಹಿಡಿದು ಸ್ವಂತವಾಗಿ ದುಡಿದ ಅನ್ನವನ್ನು ಉಣ್ಣುವೆವು, ನಾವೇ ಸಂಪಾದಿಸಿದ ಬಟ್ಟೆಯನ್ನು ಉಟ್ಟುಕೊಳ್ಳುವೆವು; ನಿನ್ನ ಹೆಸರು ಮಾತ್ರ ನಮಗಿದ್ದರೆ ಸಾಕು; ನಮ್ಮ ಅವಮಾನವನ್ನು ನೀಗಿಸಿಬಿಡು ಎಂದು ಕೇಳಿಕೊಳ್ಳುವರು.",
			"2": "<sup>2</sup> ಆ ದಿನದಲ್ಲಿ ಯೆಹೋವನು ದಯಪಾಲಿಸುವ ಬೆಳೆಯಿಂದ ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಉಳಿದವರಿಗೆ ಸೌಂದರ್ಯವೂ ಮಹಿಮೆಯೂ ಉಂಟಾಗುವವು, ದೇಶದ ಫಲದಿಂದ ಉನ್ನತಿಯೂ ಭೂಷಣವೂ ಲಭಿಸುವವು.",
			"3": "<sup>3</sup> ಮತ್ತು ಚೀಯೋನಿನಲ್ಲಿ ಉಳಿದವರು, ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ನಿಂತವರು, ಅಂತು ಜೀವಬಾಧ್ಯರ ಪಟ್ಟಿಯಲ್ಲಿ ಹೆಸರುಬಿದ್ದ ಯೆರೂಸಲೇಮಿನವರೆಲ್ಲರೂ ದೇವರ ಜನರೆನಿಸಿಕೊಳ್ಳುವರು.",
			"4": "<sup>4</sup> ಆಗ ಕರ್ತನಾದ ಯೆಹೋವನು ನ್ಯಾಯತೀರ್ಪಿನ ಆತ್ಮದಿಂದಲೂ ದಹಿಸುವ ಆತ್ಮದಿಂದಲೂ ಚೀಯೋನಿನ ಸ್ತ್ರೀಯರ ಕಲ್ಮಷವನ್ನೂ ಯೆರೂಸಲೇಮಿನ ಮಧ್ಯದಲ್ಲಿನ ರಕ್ತವನ್ನೂ ತೊಳೆದುಬಿಟ್ಟ ಮೇಲೆ",
			"5": "<sup>5</sup> ಚೀಯೋನ್ ಪರ್ವತದ ಸಂಪೂರ್ಣ ಮಂದಿರದ ಮೇಲೆಯೂ ಅಲ್ಲಿನ ಕೂಟಗಳ ಮೇಲೆಯೂ ಹಗಲಲ್ಲಿ ಧೂಮಮೇಘವನ್ನು, ಇರುಳಲ್ಲಿ ಪ್ರಜ್ವಲಿಸುವ ಅಗ್ನಿಯ ಪ್ರಕಾಶವನ್ನು, ಉಂಟು ಮಾಡುವನು. [ಈ] ಪ್ರಭಾವದ ಮೇಲೆಲ್ಲಾ ಆವರಣವಿರುವದು.",
			"6": "<sup>6</sup> ಹಗಲಿನ ಬಿಸಿಲಲ್ಲಿ ನೆರಳನ್ನೂ ಸಣ್ಣ ದೊಡ್ಡ ಮಳೆಗಳಲ್ಲಿ ಆಶ್ರಯವನ್ನೂ ಕೊಡುವ ಮಂಟಪವಿರುವದು."
		},
		"5": {
			"1": "<sup>1</sup> ನನ್ನ ಪ್ರಿಯನನ್ನೂ ಅವನ ತೋಟವನ್ನೂ ಕುರಿತ ನನ್ನ ಪ್ರಿಯನ ಒಂದು ಗೀತವನ್ನು ನಾನು ಹಾಡುವೆ, ಕೇಳಿರಿ. ಸಾರವತ್ತಾದ ಗುಡ್ಡದ ಮೇಲೆ ನನ್ನ ಪ್ರಿಯನಿಗೆ ದ್ರಾಕ್ಷೆಯ ತೋಟವಿತ್ತು.",
			"2": "<sup>2</sup> ಅವನು ಅದನ್ನು ಅಗತೆಮಾಡಿ ಕಲ್ಲುಗಳನ್ನು ತೆಗೆದುಹಾಕಿ ಒಳ್ಳೊಳ್ಳೇ ದ್ರಾಕ್ಷೆಯ ಸಸಿಗಳನ್ನು ನೆಟ್ಟು ಮಧ್ಯದಲ್ಲಿ ಬುರುಜನ್ನು ಕಟ್ಟಿ ದ್ರಾಕ್ಷೆಯ ತೊಟ್ಟಿಯನ್ನು ಕೊರೆಯಿಸಿ ಮಾಡಿಕೊಂಡು ತೋಟವು [ಒಳ್ಳೇ] ದ್ರಾಕ್ಷೆಯ ಹಣ್ಣನ್ನು ಕೊಡುವದೆಂದು ಎದುರುನೋಡುತ್ತಿರಲು ಅದು ಹೊಲಸುಹಣ್ಣನ್ನು ಬಿಟ್ಟಿತು.",
			"3": "<sup>3</sup> ಯೆರೂಸಲೇಮಿನ ನಿವಾಸಿಗಳೇ, ಯೆಹೂದದ ಜನರೇ, ಈಗ ನನಗೂ ನನ್ನ ತೋಟಕ್ಕೂ ನ್ಯಾಯತೀರಿಸಿರಿ,",
			"4": "<sup>4</sup> ನನ್ನ ತೋಟದಲ್ಲಿ ಹಿಂದೆ ನಾನು ಮಾಡಿದ್ದಕ್ಕಿಂತಲೂ ಇನ್ನೇನು ಮಾಡಬೇಕಾಗಿತ್ತು? ಅದು [ಒಳ್ಳೇ] ದ್ರಾಕ್ಷೆಯನ್ನು ಕೊಡುವದೆಂದು ನಾನು ನಿರೀಕ್ಷಿಸುತ್ತಿರಲು ಏಕೆ ಹೊಲಸುಹಣ್ಣನ್ನು ಬಿಟ್ಟಿತು?",
			"5": "<sup>5</sup> ನನ್ನ ತೋಟವನ್ನು ಏನು ಮಾಡುವೆನೋ ಈಗ ನಿಮಗೆ ತಿಳಿಸುವೆನು, ಕೇಳಿರಿ; ಅದರ ಬೇಲಿಯನ್ನು ಕೀಳುವೆನು, ದನವು ಅದನ್ನು ಮೇಯ್ದುಬಿಡುವದು; ಅದರ ಗೋಡೆಯನ್ನು ಕೆಡವಿಹಾಕುವೆನು, ಅದು ತುಳಿದಾಟಕ್ಕೆ ಈಡಾಗುವದು.",
			"6": "<sup>6</sup> ಅದನ್ನು ಹಾಳುಮಾಡುವೆನು, ಯಾರೂ ಕುಡಿಕತ್ತರಿಸಿ ಅಗತೆಮಾಡುವದಿಲ್ಲ; ಅದರಲ್ಲಿ ಮುಳ್ಳುಗಿಳ್ಳು ಬೆಳೆದುಹೋಗುವದು; ಮತ್ತು ಅದರ ಮೇಲೆ ಮಳೆಸುರಿಸಬಾರದೆಂದು ಮೇಘಗಳಿಗೆ ಅಪ್ಪಣೆಮಾಡುವೆನು.",
			"7": "<sup>7</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನ ದ್ರಾಕ್ಷೆಯ ತೋಟವು ಇಸ್ರಾಯೇಲ್ಯರ ಮನೆತನ, ಯೆಹೂದದ ಜನವೋ ಆತನ ಇಷ್ಟದ ಗಿಡವೇ; ಆತನು ನ್ಯಾಯವನ್ನು ನಿರೀಕ್ಷಿಸಿದಾಗ ಆಹಾ, ಸಿಕ್ಕಿದ್ದು ನರಹತ್ಯವೇ; ಧರ್ಮವನ್ನು ಎದುರುನೋಡಿದಾಗ ಆಹಾ, ದೊರಕಿದ್ದು ಅರಚಾಟವೇ.",
			"8": "<sup>8</sup> ಅಯ್ಯೋ, ಮನೆಗೆ ಮನೆಯನ್ನೂ ಹೊಲಕ್ಕೆ ಹೊಲವನ್ನೂ ಸೇರಿಸಿಕೊಳ್ಳುತ್ತಾ ಬಂದು ಇತರರಿಗೆ ಸ್ವಲ್ಪ ಸ್ಥಳವನ್ನೂ ಬಿಡದೆ ದೇಶದ ಮಧ್ಯದಲ್ಲಿ ತಾವೇ ತಾವಾಗಿ ವಾಸಿಸುವವರ ಗತಿಯನ್ನು ಏನು ಹೇಳಲಿ!",
			"9": "<sup>9</sup> ನನ್ನ ಕಿವಿಯಲ್ಲಿ ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನ ಒಂದು ಮಾತು ಬಿದ್ದಿತು, ನೋಡಿರಿ - ಸೊಗಸಾದ ಅನೇಕ ದೊಡ್ಡ ಮನೆಗಳು ವಾಸಿಸುವವರೇ ಇಲ್ಲದೆ ಖಂಡಿತವಾಗಿ ಹಾಳಾಗುವವು.",
			"10": "<sup>10</sup> ಏಕಂದರೆ ಹತ್ತು ಎಕರೆ ಬಾಗಾಯಿತು ಭೂಮಿಯು ನಾಲ್ಕು ಕೊಳಗದಷ್ಟೇ ದ್ರಾಕ್ಷಾರಸವನ್ನೀಯುವದು, ಬಿತ್ತಿದ ಒಂದು ಕಂಡುಗ ಬೀಜದಿಂದ ಇಕ್ಕಳ ದವಸವು ಬರುವದು.",
			"11": "<sup>11</sup> ಅಯ್ಯೋ, ಮದ್ಯದ ಗೀಳಿನಿಂದಲೇ ಮುಂಜಾನೆ ಎದ್ದು ಸಂಜೆಯಾದ ಮೇಲೆಯೂ ದ್ರಾಕ್ಷಾರಸದಿಂದ ಅಮಲೇರಿದವರಾಗಿ ಕಾಲ ಕಳೆಯುವವರ ಪಾಡು ಏನು ಹೇಳಲಿ!",
			"12": "<sup>12</sup> ಕಿನ್ನರಿ, ವೀಣೆ, ದಮ್ಮಡಿ, ಕೊಳಲು, ದ್ರಾಕ್ಷಾರಸ ಇವುಗಳೇ ಅವರ ಔತಣಗಳ ಸೊಬಗು; ಯೆಹೋವನ ಕೆಲಸವನ್ನೋ ಅವರು ಲಕ್ಷಿಸರು, ಆತನ ಹಸ್ತಕಾರ್ಯವನ್ನು ಆಲೋಚಿಸರು.",
			"13": "<sup>13</sup> ಆದಕಾರಣ ನನ್ನ ಜನರು ಜ್ಞಾನಹೀನರಾಗಿ ಸೆರೆಗೆ ಹೋಗುವದು ಖಂಡಿತ; ಘನವಂತರು ಹಸಿಯುವರು, ಸಾಮಾನ್ಯರು ಬಾಯಾರುವರು.",
			"14": "<sup>14</sup> ಮತ್ತು ಪಾತಾಳವು ಹೆಚ್ಚು ಆತುರದಿಂದ ಅಪಾರವಾಗಿ ಬಾಯಿತೆರೆಯಲು ಅವರ ಮಹಿಮೆ, ಕೋಲಾಹಲ, ಗದ್ದಲ, ಉಲ್ಲಾಸ ಇವೆಲ್ಲಾ [ಅದರೊಳಗೆ] ಬಿದ್ದುಹೋಗುವವು.",
			"15": "<sup>15</sup> ಕುಲಹೀನರು ಬೊಗ್ಗುವರು, ಕುಲೀನರು ಕುಗ್ಗುವರು, ಉನ್ನತರ ದೃಷ್ಟಿಯು ತಗ್ಗುವದು.",
			"16": "<sup>16</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೋ ನ್ಯಾಯತೀರಿಸುವದರಲ್ಲಿ ಉನ್ನತೋನ್ನತನಾಗಿ ಕಾಣಿಸಿಕೊಳ್ಳುವನು; ಪರಿಶುದ್ಧನಾದ ದೇವರು ಧರ್ಮವನ್ನು ನಡಿಸುವದರಲ್ಲಿ ಪರಿಶುದ್ಧನೆನಿಸಿಕೊಳ್ಳುವನು.",
			"17": "<sup>17</sup> ಪುಷ್ಟರ ಪ್ರದೇಶಗಳು ಹಾಳಾಗಿ ಪಶುಪಾಲಕರಿಗೆ ಜೀವನಕ್ಕಾಗುವವು, ಹುಲ್ಲುಗಾವಲಂತೆ ಕುರಿಗಳು ಅಲ್ಲಿ ಮೇಯುವವು.",
			"18": "<sup>18</sup> ಅಯ್ಯೋ, ಅಕಾರ್ಯಗಳೆಂಬ ಹಗ್ಗಗಳಿಂದ ಅಪರಾಧ ದಂಡನೆಯನ್ನು, ತೇರನ್ನು ಹೊರಜಿಯಿಂದಲೋ ಎಂಬಂತೆ ಪಾಪದ ಫಲವನ್ನು ತಮ್ಮ ಕಡೆಗೆ ಎಳೆದುಕೊಳ್ಳುತ್ತಾ ಆತನು ತ್ವರೆಪಡಲಿ,",
			"19": "<sup>19</sup> ನಾವು ಆತನ ಕೆಲಸವನ್ನು ನೋಡಬೇಕು, ಅದನ್ನು ಬೇಗನೆ ನಡಿಸಲಿ, ಇಸ್ರಾಯೇಲ್ಯರ ಸದಮಲಸ್ವಾಮಿಯ ಉದ್ದೇಶವನ್ನು ನಾವು ತಿಳಿಯಬೇಕು. ಅದು ಸಮೀಪಿಸಿ ಬರಲಿ ಎಂದು ನುಡಿಯುವವರ ದುರವಸ್ಥೆಯನ್ನು ಏನು ಹೇಳಲಿ!",
			"20": "<sup>20</sup> ಅಯ್ಯೋ, ಕೆಟ್ಟದ್ದನ್ನು ಒಳ್ಳೇದೆಂದೂ ಒಳ್ಳೇದನ್ನು ಕೆಟ್ಟದ್ದೆಂದೂ ಬೋಧಿಸಿ ಕತ್ತಲನ್ನು ಬೆಳಕೆಂದೂ ಬೆಳಕನ್ನು ಕತ್ತಲೆಂದೂ ಸಾಧಿಸಿ ಕಹಿಯು ಸಿಹಿ, ಸಿಹಿಯು ಕಹಿ ಎಂದು ಸ್ಥಾಪಿಸುವವರ ಗತಿಯನ್ನು ಏನೆಂದು ಹೇಳಲಿ!",
			"21": "<sup>21</sup> ಅಯ್ಯೋ, ತಮ್ಮಲ್ಲಿ ತಾವೇ ಜ್ಞಾನಿಗಳೆಂದೂ ಸ್ವಂತ ಗಣನೆಯಲ್ಲಿ ವಿವೇಕಿಗಳೆಂದೂ ಭಾವಿಸಿಕೊಳ್ಳುವವರ ಪಾಡು ಏನು ಹೇಳಲಿ!",
			"22": "<sup>22</sup> ಅಯ್ಯೋ, ಸುರಾಪಾನದಲ್ಲಿ ಶೂರರೂ ಮದ್ಯಮಿಶ್ರಣದಲ್ಲಿ ಸಾಹಸಿಗಳೂ ಆಗಿ",
			"23": "<sup>23</sup> ಲಂಚಕ್ಕೋಸ್ಕರ ದೋಷಿಗಳನ್ನು ನಿರ್ದೋಷಿಗಳೆಂದು ತೀರ್ಪುಮಾಡಿ ನ್ಯಾಯವಂತರ ನ್ಯಾಯವನ್ನು ಹಾಳುಮಾಡುವವರ ಗತಿಯನ್ನು ಏನೆಂದು ಹೇಳಲಿ!",
			"24": "<sup>24</sup> ಅವರು ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನ ಉಪದೇಶವನ್ನು ನಿರಾಕರಿಸಿ ಇಸ್ರಾಯೇಲ್ಯರ ಸದಮಲಸ್ವಾಮಿಯ ವಾಕ್ಯವನ್ನು ಅಸಡ್ಡೆ ಮಾಡಿದ್ದರಿಂದ ಅಗ್ನಿಯ ನಾಲಿಗೆಗಳು ಒಣಕೂಳೆಯನ್ನು ನುಂಗಿಬಿಡುವ ಹಾಗೂ ಒಣಹುಲ್ಲು ಬೆಂಕಿಯಲ್ಲಿ ಕುಗ್ಗುವಂತೆಯೂ ಅವರ ಬೇರು ಕೊಳೆತು ಅವರ ಹೂವು ದೂಳಾಗಿ ತೂರಿಹೋಗುವದು.",
			"25": "<sup>25</sup> ಹೀಗಿರಲು ಯೆಹೋವನು ತನ್ನ ಜನರಲ್ಲಿ ಉರಿಗೊಂಡು ಅವರ ಮೇಲೆ ಕೈಯೆತ್ತಿ ಅವರನ್ನು ಹೊಡೆದು ಬಿಡುವನು; ಆಗ ಬೆಟ್ಟಗಳು ನಡಗುವವು, ಹೆಣಗಳು ಬೀದಿಗಳಲ್ಲಿ ಕಸವಾಗಿ ಬಿದ್ದಿರುವವು. ಇಷ್ಟೆಲ್ಲಾ ನಡೆದರೂ ಆತನ ಕೋಪವು ತೀರದೆ ಕೈಯೆತ್ತಿಯೇ ಇರುವದು.",
			"26": "<sup>26</sup> ಆತನು ದೂರದ ಜನಾಂಗದವರಿಗೆ ಗುರುತಾಗಿ ಧ್ವಜವೆತ್ತಿ ಅವರನ್ನು ಭೂಲೋಕದ ಅಂಚಿನಲ್ಲಿಂದ ಸಿಳ್ಳುಹಾಕಿ ಕರೆಯುವನು; ಇಗೋ, ತ್ವರೆಪಟ್ಟು ವೇಗವಾಗಿ ಬರುತ್ತಾರೆ!",
			"27": "<sup>27</sup> ಅವರಲ್ಲಿ ಯಾರೂ ಬಳಲಿ ಮುಗ್ಗರಿಸರು, ಯಾರೂ ತೂಕಡಿಸಿ ನಿದ್ರಿಸರು; ಅವರ ನಡುಕಟ್ಟು ಬಿಚ್ಚಿಲ್ಲ, ಅವರ ಕೆರದ ಬಾರು ಕಿತ್ತಿಲ್ಲ;",
			"28": "<sup>28</sup> ಅವರ ಅಂಬುಗಳು ಮೊನೆಯಾಗಿವೆ, ಬಿಲ್ಲುಗಳು ಬಿಗಿದಿವೆ; ಕುದುರೆಗಳ ಗೊರಸೆಗಳು ಕಲ್ಲಿನಂತಿವೆ; ಚಕ್ರಗಳು ಬಿರುಗಾಳಿಯಂತೆ ರಭಸವಾಗಿವೆ;",
			"29": "<sup>29</sup> ಸಿಂಹದಂತೆ ಗರ್ಜಿಸುತ್ತಾರೆ, ಪ್ರಾಯದ ಸಿಂಹಗಳ ಹಾಗೆ ಆರ್ಭಟಿಸುತ್ತಾರೆ, ಗುರುಗುಟ್ಟುತ್ತಾ ಬೇಟೆಹಿಡಿದು ಹೊತ್ತುಕೊಂಡು ಹೋಗುತ್ತಾರೆ, ರಕ್ಷಿಸುವವರು ಯಾರೂ ಇಲ್ಲ.",
			"30": "<sup>30</sup> ಸಮುದ್ರವು ಭೋರ್ಗರೆಯುವಂತೆ ಆ ದಿನದಲ್ಲಿ ಇವರು ಯೆಹೂದ್ಯರ ಮೇಲೆ ಗುರುಗುಟ್ಟುವರು; ದೇಶದಲ್ಲಿಯೋ, ಆಹಾ, ಅಂಧಕಾರವೂ ವ್ಯಾಕುಲವೂ ತುಂಬಿರುವವು, ಮೋಡ ಕವಿದು ಬೆಳಕು ಕತ್ತಲಾಗುವದು."
		},
		"6": {
			"1": "<sup>1</sup> ಅರಸನಾದ ಉಜ್ಜೀಯನು ಕಾಲವಾದ ವರುಷದಲ್ಲಿ ಕರ್ತನು ಉನ್ನತೋನ್ನತ ಸಿಂಹಾಸನಾರೂಢನಾಗಿರುವದನ್ನು ಕಂಡೆನು. ಆತನ ವಸ್ತ್ರದ ನೆರಿಗೆಯು ಮಂದಿರದಲ್ಲೆಲ್ಲಾ ಹರಡಿತ್ತು.",
			"2": "<sup>2</sup> ಆತನ ಸುತ್ತ ಸೆರಾಫಿಯರು ಇದ್ದರು; ಪ್ರತಿಯೊಬ್ಬನು ಆರಾರು ರೆಕ್ಕೆಯುಳ್ಳವನಾಗಿ ಎರಡರಿಂದ ತನ್ನ ಮುಖವನ್ನು, ಎರಡರಿಂದ ತನ್ನ ಕಾಲುಗಳನ್ನು ಮುಚ್ಚಿಕೊಂಡು ಎರಡನ್ನು ಬಡಿಯುತ್ತಾ ನೆಲಸೋಕದೆ ನಿಂತಿದ್ದನು.",
			"3": "<sup>3</sup> ಆಗ ಒಬ್ಬನು ಮತ್ತೊಬ್ಬನಿಗೆ - ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಪರಿಶುದ್ಧನು, ಪರಿಶುದ್ಧನು, ಪರಿಶುದ್ಧನು; ಭೂಮಂಡಲವೆಲ್ಲಾ ಆತನ ಪ್ರಭಾವದಿಂದ ತುಂಬಿದೆ ಎಂದು ಕೂಗಿ ಹೇಳಿದನು.",
			"4": "<sup>4</sup> ಕೂಗುವವನ ಶಬ್ದಕ್ಕೆ ದ್ವಾರದ ಅಸ್ತಿವಾರವು ಕದಲಿತು; ಧೂಮವು ಮಂದಿರದಲ್ಲೆಲ್ಲಾ ತುಂಬಿತು.",
			"5": "<sup>5</sup> ಆಗ ನಾನು - ಅಯ್ಯೋ, ನನ್ನ ಗತಿಯನ್ನು ಏನು ಹೇಳಲಿ! ನಾಶವಾದೆನಲ್ಲಾ; ನಾನು ಹೊಲಸು ತುಟಿಯವನು, ಹೊಲಸು ತುಟಿಯವರ ಮಧ್ಯದಲ್ಲಿ ವಾಸಿಸುವವನು; ಇಂಥ ನನ್ನ ಕಣ್ಣುಗಳು ರಾಜಾಧಿರಾಜನನ್ನು, ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನನ್ನು ಕಂಡವು ಎಂದು ಕೂಗಿಕೊಳ್ಳಲು",
			"6": "<sup>6</sup> ಸೆರಾಫಿಯರಲ್ಲಿ ಒಬ್ಬನು ಯಜ್ಞವೇದಿಯಿಂದ ತಾನು ತಂಡಸದಲ್ಲಿ ತೆಗೆದ ಕೆಂಡವನ್ನು ಕೈಯಲ್ಲಿ ಹಿಡಿದುಕೊಂಡು ನನ್ನ ಬಳಿಗೆ ಹಾರಿ ಬಂದು",
			"7": "<sup>7</sup> ನನ್ನ ಬಾಯಿಗೆ ಮುಟ್ಟಿಸಿ - ಇಗೋ, ಇದು ನಿನ್ನ ತುಟಿಗಳಿಗೆ ತಗಲಿತು; ನಿನ್ನ ದೋಷವು ನೀಗಿತು, ನಿನ್ನ ಪಾಪಕ್ಕೆ ಪ್ರಾಯಶ್ಚಿತ್ತವಾಯಿತು ಅಂದನು.",
			"8": "<sup>8</sup> ಆಗ, ಯಾವನನ್ನು ಕಳುಹಿಸಲಿ, ಯಾವನು ನಮಗೋಸ್ಕರ ಹೋಗುವನು ಎಂಬ ಕರ್ತನ ನುಡಿಯನ್ನು ಕೇಳಿ, ಇಗೋ, ನಾನಿದ್ದೇನೆ, ನನ್ನನ್ನು ಕಳುಹಿಸು ಅಂದೆನು.",
			"9": "<sup>9</sup> ಅದಕ್ಕೆ ಆತನು - ನೀನು ಈ ಜನರ ಬಳಿಗೆ ಹೋಗಿ - ನೀವು ಕಿವಿಯಾರೆ ಕೇಳಿದರೂ ತಿಳಿಯಬಾರದು, ಕಣ್ಣಾರೆ ಕಂಡರೂ ಗ್ರಹಿಸಬಾರದು ಎಂದು ತಿಳಿಸಿ,",
			"10": "<sup>10</sup> ಕಣ್ಣಿನಿಂದ ಕಂಡು ಕಿವಿಯಿಂದ ಕೇಳಿ ಹೃದಯದಿಂದ ಗ್ರಹಿಸಿ ನನ್ನ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡು ನನ್ನಿಂದ ಸ್ವಸ್ಥತೆಯನ್ನು ಹೇಗೂ ಹೊಂದದ ಹಾಗೆ ಈ ಜನರ ಹೃದಯಕ್ಕೆ ಕೊಬ್ಬೇರಿಸಿ ಕಿವಿಯನ್ನು ಮಂದಮಾಡಿ ಕಣ್ಣಿಗೆ ಅಂಟುಬಳಿ ಎಂದು ನನಗೆ ಹೇಳಿದನು.",
			"11": "<sup>11</sup> ಅದಕ್ಕೆ ನಾನು - ಕರ್ತನೇ, ಇದು ಎಂದಿನ ತನಕ? ಎಂದು ಕೇಳಲು ಆತನು - ಯೆಹೋವನು ಜನರನ್ನು ದೂರ ತೊಲಗಿಸಿ",
			"12": "<sup>12</sup> ದೇಶದಲ್ಲಿ ಹಾಳು ಹೆಚ್ಚಿ ಪಟ್ಟಣಗಳು ಜನರಿಲ್ಲದೆ ಮನೆಗಳು ನಿವಾಸಿಗಳಿಲ್ಲದೆ ಧ್ವಂಸವಾಗಿ ಭೂಮಿಯು ತೀರಾ ಹಾಳಾಗುವ ತನಕ ಹೀಗಿರುವದು.",
			"13": "<sup>13</sup> ಆಗ ದೇಶದಲ್ಲಿ ಹತ್ತನೆಯ ಒಂದು ಭಾಗ ಉಳಿದಿದ್ದರೂ ಅದೂ ಕೂಡಾ ನಾಶವಾಗುವದು; ಏಲಾ ಅಲ್ಲೋನ್ ಮರಗಳನ್ನೂ ಕಡಿದ ಮೇಲೆ ಅವುಗಳ ಬುಡವು ನಿಲ್ಲುವ ಹಾಗೆ ದೇವಕುಲವು ಮೋಟುಬುಡವಾಗಿ ನಿಲ್ಲುವದು ಅಂದನು."
		},
		"7": {
			"1": "<sup>1</sup> ಯೋಥಾಮನ ಮಗನೂ ಉಜ್ಜೀಯನ ಮೊಮ್ಮಗನೂ ಯೆಹೂದದ ಅರಸನೂ ಆದ ಆಹಾಜನ ಕಾಲದಲ್ಲಿ ಅರಾಮ್ಯರ ಅರಸನಾದ ರೆಚೀನ, ರೆಮಲ್ಯನ ಮಗನೂ ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನೂ ಆದ ಪೆಕಹ ಎಂಬವರು ಯೆರೂಸಲೇಮಿನ ಮೇಲೆ ದಂಡೆತ್ತಿ ಬಂದರು. (ಅದನ್ನು ಜಯಿಸುವದಕ್ಕೆ ಆಗಲಿಲ್ಲ.)",
			"2": "<sup>2</sup> ಅರಾಮ್ಯರು ಎಫ್ರಾಯೀಮ್ಯರನ್ನು ಹೊಂದಿಕೊಂಡಿದ್ದಾರೆಂಬ ಸುದ್ದಿಯು ಅರಮನೆಗೆ ಮುಟ್ಟಿದಾಗ ಅರಸನ ಮನವೂ ಪ್ರಜೆಯ ಮನವೂ ಗಾಳಿಯಿಂದ ವನವೃಕ್ಷಗಳು ಅಲ್ಲಾಡುವಂತೆ ನಡುಗಿದವು.",
			"3": "<sup>3</sup> ಆಗ ಯೆಹೋವನು ಯೆಶಾಯನಿಗೆ ಹೀಗೆ ಹೇಳಿದನು - ಶೆಯಾರ್ ಯಾಶೂಬನೆಂಬ ನಿನ್ನ ಮಗನನ್ನು ಕರೆದುಕೊಂಡು ಹೋಗಿ ಮಡಿವಾಳರ ಹೊಲದ ಮೇಲೆ ಹೋಗುವ ರಾಜಮಾರ್ಗದಲ್ಲಿ ಮೇಲಿನ ಕೆರೆಯ ಕಾಲಿವೆಯ ಕೊನೆಯ ಹತ್ತಿರ ಆಹಾಜನನ್ನು ಎದುರುಗೊಂಡು",
			"4": "<sup>4</sup> ಅವನಿಗೆ ಈ ಪ್ರಕಾರ ಹೇಳಬೇಕು - ಜಾಗರೂಕನಾಗಿ ಸುಮ್ಮನಿರು, ಭಯಪಡಬೇಡ! ರೆಚೀನ ಅರಾಮ್ಯರು ರೆಮಲ್ಯನ ಮಗ ಇವರ ಕೋಪವು ಎಷ್ಟು ಹೆಚ್ಚಿದರೂ ಹೊಗೆಯಾಡುವ ಈ ಎರಡು ಮೋಟುಗೊಳ್ಳಿಗಳಿಗೆ ನಿನ್ನ ಹೃದಯವು ಕುಂದದಿರಲಿ.",
			"5": "<sup>5</sup> ಅರಾಮ್ಯರೂ ಎಫ್ರಾಯೀಮ್ಯರೂ ರೆಮಲ್ಯನ ಮಗನೂ ನಿನ್ನ ವಿಷಯವಾಗಿ ದುರಾಲೋಚನೆಮಾಡಿ",
			"6": "<sup>6</sup> ಯೆಹೂದದ ಮೇಲೆ ದಂಡೆತ್ತಿ ಹೆದರಿಸಿ ಒಳಗೆ ನುಗ್ಗಿಕೊಂಡು ಹೋಗಿ ಅದರಲ್ಲಿ ಟಾಬೇಲನ ಮಗನಿಗೆ ಪಟ್ಟಕಟ್ಟೋಣವೆಂದುಕೊಂಡದ್ದರಿಂದ",
			"7": "<sup>7</sup> ಕರ್ತನಾದ ಯೆಹೋವನು ಹೇಳುವದೇನಂದರೆ - ಇದು ನೆರವೇರದು, ಆಗುವದೇ ಇಲ್ಲ.",
			"8": "<sup>8</sup> ಅರಾಮಿಗೆ ಶಿರಸ್ಸು ದಮಸ್ಕ, ದಮಸ್ಕಕ್ಕೆ ಶಿರಸ್ಸು ರೆಚೀನನಷ್ಟೆ; ಎಫ್ರಾಯೀಮಿಗೆ ಶಿರಸ್ಸು ಸಮಾರ್ಯ, ಸಮಾರ್ಯಕ್ಕೆ ಶಿರಸ್ಸು ರೆಮಲ್ಯನ ಮಗನೇ ತಾನೆ.",
			"9": "<sup>9</sup> ಅರುವತ್ತೈದು ವರುಷಗಳೊಳಗೆ ಎಫ್ರಾಯೀಮ್ಯರು ಭಂಗಪಟ್ಟು ಜನಾಂಗವೆನಿಸಿಕೊಳ್ಳರು. ನಿಮಗೆ ನಂಬಿಕೆಯು ಸ್ಥಿರವಿಲ್ಲದಿದ್ದರೆ ನಿಮಗೆ ಸ್ಥಿರತೆಯೇ ಇಲ್ಲ ಎಂಬದೇ.",
			"10": "<sup>10</sup> ಮತ್ತೆ ಯೆಹೋವನು ಆಹಾಜನಿಗೆ - ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನಿಂದ ಒಂದು ಗುರುತನ್ನು ಕೇಳಿಕೋ; ಅದು ಪಾತಾಳದಷ್ಟು ಆಳದಲ್ಲಿದ್ದರೂ ಮೇಲಣ ಲೋಕದಷ್ಟು ಎತ್ತರದಲ್ಲಿದ್ದರೂ ಕೇಳಿಕೋ ಎಂದು ಹೇಳಿದನು.",
			"12": "<sup>12</sup> ಆಗ ಆಹಾಜನು, ನಾನು ಕೇಳಿಕೊಳ್ಳುವದೇ ಇಲ್ಲ. ಯೆಹೋವನನ್ನು ಪರೀಕ್ಷಿಸುವದಿಲ್ಲ ಅಂದನು.",
			"13": "<sup>13</sup> ಅದಕ್ಕೆ [ಯೆಶಾಯನು] ಹೀಗೆ ಹೇಳಿದನು - ದಾವೀದನ ಮನೆತನದವರೇ, ಕೇಳಿರಿ! ಮನುಷ್ಯರನ್ನು ಬೇಸರಗೊಳಿಸುವದು ಅಷ್ಟು ದೊಡ್ಡದಲ್ಲವೆಂದೆಣಿಸಿ ನನ್ನ ದೇವರನ್ನೂ ಬೇಸರಗೊಳಿಸುವಿರಾ?",
			"14": "<sup>14</sup> ಇದರಿಂದ ಕರ್ತನು ತಾನೇ ನಿಮಗೆ ಒಂದು ಗುರುತನ್ನು ಕೊಡುವನು. ಇಗೋ, ಒಬ್ಬ ಕನ್ನಿಕೆಯು ಗರ್ಭಿಣಿಯಾಗಿ ಮಗನನ್ನು ಹಡೆದು ಅವನಿಗೆ ಇಮ್ಮಾನುವೇಲ್ ಎಂದು ಹೆಸರಿಡುವಳು.",
			"15": "<sup>15</sup> ಅವನು ಕೆಟ್ಟದ್ದು ಬೇಡ, ಒಳ್ಳೇದು ಬೇಕು ಅನ್ನುವಷ್ಟು ಬಲ್ಲವನಾಗುವಾಗ ಮೊಸರನ್ನೂ ಜೇನತುಪ್ಪವನ್ನೂ ತಿನ್ನುವನು.",
			"16": "<sup>16</sup> ಆ ಮಗುವು ಕೆಟ್ಟದ್ದು ಬೇಡ, ಒಳ್ಳೇದು ಬೇಕು ಅನ್ನುವಷ್ಟು ಬಲ್ಲವನಾಗುವದರೊಳಗೆ, ಯಾವ ಇಬ್ಬರು ರಾಜರಿಗೆ ನೀನು ಹೆದರಿ ನಡುಗುತ್ತೀಯೋ ಅವರ ದೇಶವು ನಿರ್ಜನವಾಗುವದು.",
			"17": "<sup>17</sup> ಮತ್ತು ಯೆಹೂದದಿಂದ ಎಫ್ರಾಯೀಮು ಅಗಲಿದಂದಿನಿಂದ ಈಚೆಗೆ ಸಂಭವಿಸಿದ ದುಷ್ಕಾಲವನ್ನು ಯೆಹೋವನು ನಿನ್ನ ಮೇಲೂ ನಿನ್ನ ಪ್ರಜೆಯ ಮೇಲೂ ನಿನ್ನ ತಂದೆಯ ಮನೆತನದ ಮೇಲೂ ಬರಮಾಡುವನು; ಆ ದುಷ್ಕಾಲವು ಅಶ್ಶೂರದ ಅರಸೇ.",
			"18": "<sup>18</sup> ಆ ಕಾಲದಲ್ಲಿ ಐಗುಪ್ತದ ನದಿಗಳ ಕಟ್ಟಕಡೆಯಲ್ಲಿನ ನೊಣಗಳೂ ಅಶ್ಶೂರ್‍ದೇಶದ ತುಂಬಿಗಳೂ ಬರಲೆಂದು ಯೆಹೋವನು ಸಿಳ್ಳುಹಾಕುವನು.",
			"19": "<sup>19</sup> ಅವೆಲ್ಲಾ ಬಂದು ಕಡಿದಾದ ಡೊಂಗರಗಳಲ್ಲಿಯೂ ಬಂಡೆಗಳ ಸಂದುಗೊಂದುಗಳಲ್ಲಿಯೂ ಎಲ್ಲಾ ಮುಳ್ಳುಪೊದೆಗಳಲ್ಲಿಯೂ ಗೋಮಾಳಗಳಲ್ಲಿಯೂ ಮುತ್ತಿಕೊಳ್ಳುವವು.",
			"20": "<sup>20</sup> ಆಗ ಕರ್ತನು ಯೂಫ್ರೇಟೀಸ್ ನದಿಯ ಆಚೆಗಿರುವ ಅಶ್ಶೂರದ ರಾಜನೆಂಬ ಬಾಡಿಗೆಯ ಕ್ಷೌರದ ಕತ್ತಿಯಿಂದ [ಯೆಹೂದದ] ತಲೆಯನ್ನೂ ಕಾಲಕೂದಲನ್ನೂ ಬೋಳಿಸುವನು; ಅದು ಗಡ್ಡವನ್ನು ಸಹ ತೆಗೆದುಬಿಡುವದು.",
			"21": "<sup>21</sup> ಆ ಕಾಲದಲ್ಲಿ ಯಾವನೇ ಆಗಲಿ ಹಸುವಿನ ಕಡಸನ್ನೂ ಎರಡು ಕುರಿಗಳನ್ನೂ ಸಾಕಿ",
			"22": "<sup>22</sup> ಅವು ಕರೆಯುವ ಹೆಚ್ಚು ಹಾಲಿನಿಂದ ಮೊಸರನ್ನು ತಿನ್ನುವನು; ದೇಶದಲ್ಲಿ ಉಳಿದ ಪ್ರತಿಯೊಬ್ಬನೂ ಮೊಸರುಜೇನುಗಳನ್ನೇ ಉಂಡು ಜೀವಿಸುವನು.",
			"23": "<sup>23</sup> ಆಗ ಸಾವಿರ ರೂಪಾಯಿ ಬೆಲೆಯ ಸಹಸ್ರ ದ್ರಾಕ್ಷೆಯ ಬಳ್ಳಿಗಳು ಬೆಳೆಯುವ ಪ್ರತಿಯೊಂದು ಪ್ರದೇಶದಲ್ಲಿಯೂ ಮುಳ್ಳುಗಿಳ್ಳು ಮುಚ್ಚಿಕೊಂಡು ಹೋಗುವದು.",
			"24": "<sup>24</sup> ದೇಶವೆಲ್ಲಾ ಮುಳ್ಳುಪೊದೆಯಾಗಿರುವದರಿಂದ ಜನರು ಬಿಲ್ಲುಬಾಣಗಳೊಡನೆ ಸಂಚರಿಸುವರು.",
			"25": "<sup>25</sup> ಗುದ್ದಲಿಯಿಂದ ಅಗತೆಮಾಡುತ್ತಿದ್ದ ಯಾವ ಗುಡ್ಡಕ್ಕೂ ನೀನು ಮುಳ್ಳುಕಂಪೆಗಳಿಗೆ ಅಂಜಿ ಬರಲಾರದೆ ಇರುವಿ; ಅದು ದನಗಳನ್ನು ಬಿಡುವದಕ್ಕೂ ಕುರಿಗಳು ತುಳಿದಾಡುವದಕ್ಕೂ ಎಡೆಯಾಗುವದು."
		},
		"8": {
			"1": "<sup>1</sup> ಆಮೇಲೆ ಯೆಹೋವನು ನನಗೆ - ದೊಡ್ಡ ಹಲಿಗೆಯನ್ನು ತೆಗೆದುಕೊಂಡು ಸಾಧಾರಣ ಲಿಪಿಯಿಂದಲೇ ವಿಷಯಸೂಚಕವಾದ ಈ ಪದವನ್ನು ಬರೆ - ಮಹೇರ್ ಶಾಲಾಲ್ ಹಾಷ್ ಬಜ್ [ಅಂದರೆ ಸೂರೆಗೆ ಆತುರ, ಕೊಳ್ಳೆಗೆ ಅವಸರ];",
			"2": "<sup>2</sup> [ನೀನು ಹೀಗೆ ಬರೆದದ್ದಕ್ಕೆ] ಯಾಜಕನಾದ ಊರೀಯ, ಯೆಬೆರೆಕ್ಯನ ಮಗನಾದ ಜೆಕರ್ಯ ಈ ನಂಬಿಗಸ್ತರಾದ ಸಾಕ್ಷಿಗಳನ್ನು ಕರೆಯುವೆನು ಎಂದು ಹೇಳಿದನು.",
			"3": "<sup>3</sup> ಅನಂತರ ನಾನು ನನ್ನ ಹೆಂಡತಿಯನ್ನು ಕೂಡಲು ಆಕೆಯು ಬಸುರಾಗಿ ಗಂಡು ಮಗುವನ್ನು ಹೆತ್ತಳು. ಆಗ ಯೆಹೋವನು ನನಗೆ - ಅದಕ್ಕೆ ಮಹೇರ್ ಶಾಲಾಲ್ ಹಾಷ್ ಬಜ್ ಎಂದು ಹೆಸರಿಡು.",
			"4": "<sup>4</sup> ಆ ಮಗುವು ಅಪ್ಪಾ, ಅಮ್ಮಾ, ಎಂದು ಕೂಗಬಲ್ಲದಾಗುವದರೊಳಗೆ ಅಶ್ಶೂರದ ಅರಸನು ದಮಸ್ಕದ ಆಸ್ತಿಯನ್ನೂ ಸಮಾರ್ಯದ ಸೂರೆಯನ್ನೂ ಹೊರಿಸಿಕೊಂಡು ಹೋಗುವನು ಎಂದು ಹೇಳಿದನು.",
			"5": "<sup>5</sup> ಯೆಹೋವನು ಮತ್ತೊಂದಾವರ್ತಿ ನನಗೆ ಹೀಗೆ ನುಡಿದನು -",
			"6": "<sup>6</sup> ಈ ಜನರು ಮೆಲ್ಲಗೆ ಹರಿಯುವ ಸಿಲೋವದ ನೀರನ್ನು ಉಪೇಕ್ಷಿಸಿ ರೆಚೀನನನ್ನೂ ರೆಮಲ್ಯನ ಮಗನನ್ನೂ ನಂಬಿ ಮೆರೆದದ್ದರಿಂದ",
			"7": "<sup>7</sup> ಇಗೋ, ಕರ್ತನು ಪೂರ್ಣಪ್ರತಾಪದಿಂದ ಕೂಡಿದ ಅಶ್ಶೂರದ ಅರಸನೆಂಬ ಮಹಾನದಿಯ ರಭಸವಾದ ದೊಡ್ಡ ಪ್ರವಾಹವನ್ನು ಇವರ ಮೇಲೆ ಬರಮಾಡುವನು;",
			"8": "<sup>8</sup> ಅದು ತನ್ನ ಪಾತ್ರವನ್ನೆಲ್ಲಾ ಒಳಗೊಂಡು ದಡಗಳನ್ನೆಲ್ಲಾ ಮೀರಿ ಯೆಹೂದದಲ್ಲಿಯೂ ನುಗ್ಗಿ ತುಂಬಿತುಳುಕಿ ಹಬ್ಬಿಕೊಂಡು ಕತ್ತಿನವರೆಗೂ ಏರುವದು; ಇಮ್ಮಾನುವೇಲನೇ! ಆ ಅರಸನ ರೆಕ್ಕೆಗಳು ಹರಡಿ ನಿನ್ನ ದೇಶದ ಅಗಲದಲ್ಲೆಲ್ಲಾ ಮುತ್ತಿಬಿಡುವವು.",
			"9": "<sup>9</sup> ಜನಾಂಗಗಳೇ, ಭಂಗಪಡಿಸಿರಿ, ನೀವೂ ಭಂಗಪಡುವಿರಿ; ದೂರದೇಶೀಯರೇ, ಕಿವಿಗೊಡಿರಿ; ನಡುಕಟ್ಟಿರಿ, ಭಂಗಪಡುವಿರಿ; ಹೌದು ನಡುಕಟ್ಟಿರಿ, ಭಂಗಪಡುವಿರಿ.",
			"10": "<sup>10</sup> ಆಲೋಚನೆಮಾಡಿಕೊಳ್ಳಿರಿ, ಅದು ಮುರಿದುಹೋಗುವದು; ಅಪ್ಪಣೆಮಾಡಿರಿ, ಅದು ನಿಲ್ಲದು; ದೇವರು ನಮ್ಮ ಕೂಡ ಇದ್ದಾನಷ್ಟೆ.",
			"11": "<sup>11</sup> ಯೆಹೋವನು ನನ್ನ ಮೇಲೆ ಬಲವಾಗಿ ಕೈಯನ್ನಿಟ್ಟು ಈ ಜನರ ಮಾರ್ಗದಲ್ಲಿ ನಡೆಯಬಾರದೆಂದು ನನಗೆ ಉಪದೇಶಿಸುತ್ತಾ ಹೀಗಂದನು -",
			"12": "<sup>12</sup> ಇವರು ಯಾವದನ್ನು ಒಪ್ಪಂದವೆನ್ನುತ್ತಾರೋ ನೀವು ಅದನ್ನು ಒಪ್ಪಂದವೆನ್ನಬೇಡಿರಿ; ಇವರು ಯಾವದಕ್ಕೆ ಹೆದರುತ್ತಾರೋ ನೀವು ಅದಕ್ಕೆ ಹೆದರಬೇಡಿರಿ, ನಡುಗಬೇಡಿರಿ.",
			"13": "<sup>13</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನನ್ನೇ ಪ್ರತಿಷ್ಠೆಪಡಿಸಿರಿ; ಆತನಿಗೇ ಹೆದರಿಕೊಳ್ಳಿರಿ, ನಡುಗಿರಿ.",
			"14": "<sup>14</sup> ಆತನು ಆಶ್ರಯವಾಗುವನು; ಆದರೆ ಇಸ್ರಾಯೇಲಿನ ಎರಡು ಮನೆತನಕ್ಕೆ ಎಡವುವ ಕಲ್ಲೂ ಮುಗ್ಗರಿಸುವ ಬಂಡೆಯೂ ಯೆರೂಸಲೇಮಿನ ನಿವಾಸಿಗಳಿಗೆ ಬಲೆಯೂ ಬೋನೂ ಆಗುವನು.",
			"15": "<sup>15</sup> ಅವರಲ್ಲಿ ಅನೇಕರು ಎಡವಿ ಬಿದ್ದು ಭಂಗವಾಗುವರು, ಬೋನಿಗೆ ಸಿಕ್ಕಿ ವಶವಾಗುವರು.",
			"16": "<sup>16</sup> ಬೋಧನೆಯನ್ನು ಕಟ್ಟಿಡು, ನನ್ನ ಶಿಷ್ಯರೊಳಗೆ ಉಪದೇಶವನ್ನು ಮುಚ್ಚಿ ಮುದ್ರಿಸು.",
			"17": "<sup>17</sup> ಯಾಕೋಬನ ಮನೆತನದವರಿಗೆ ಮುಖವನ್ನು ಮರೆಮಾಡಿಕೊಂಡಿರುವ ಯೆಹೋವನಿಗಾಗಿ ನಾನು ಕಾದುಕೊಂಡು ಎದುರುನೋಡುತ್ತಿರುವೆನು.",
			"18": "<sup>18</sup> ಆಹಾ, ನಾನೂ ನನಗೆ ಯೆಹೋವನು ದಯಪಾಲಿಸಿರುವ ಮಕ್ಕಳೂ ಚೀಯೋನ್ ಪರ್ವತದಲ್ಲಿ ವಾಸವಾಗಿರುವ ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನಿಂದುಂಟಾದ ಗುರುತುಗಳಾಗಿಯೂ ಅದ್ಭುತಗಳಾಗಿಯೂ ಇಸ್ರಾಯೇಲ್ಯರ ಮಧ್ಯದಲ್ಲಿದ್ದೇವೆ.",
			"19": "<sup>19</sup> ಲೊಚಗುಟ್ಟುವ ಪಿಟಿಪಿಟಿಗುಟ್ಟುವ ಪ್ರೇತವಿಚಾರಕರನ್ನೂ ಬೇತಾಳಿಕರನ್ನೂ ಆಶ್ರಯಿಸಿರಿ ಎಂದು ಒಂದು ವೇಳೆ ನಿಮಗೆ ಹೇಳಾರು. ಜನರು ತಮ್ಮ ದೇವರನ್ನೇ ಆಶ್ರಯಿಸಬಾರದೋ? ಜೀವಿತರಿಗಾಗಿ ಸತ್ತವರಲ್ಲಿ ವಿಚಾರಿಸುವದು ಯುಕ್ತವೋ?",
			"20": "<sup>20</sup> ದೇವರ ಉಪದೇಶವನ್ನೂ ದೇವರ ಬೋಧನೆಯನ್ನೂ ವಿಚಾರಿಸುವ ಎಂದು ಅವರು ಹೇಳದಿದ್ದರೆ ಅವರಿಗೆ ಎಂದಿಗೂ ಬೆಳಗಾಗುವದಿಲ್ಲ.",
			"21": "<sup>21</sup> ಅವರು ಘೋರಕಷ್ಟಕ್ಕೊಳಗಾಗಿ ಹಸಿದುಕೊಂಡು ದೇಶದಲ್ಲಿ ಅಲೆಯುವರು; ಹಸಿದಾಗ ರೇಗಿಕೊಂಡು ತಮ್ಮ ರಾಜನನ್ನೂ ತಮ್ಮ ದೇವರನ್ನೂ ಶಪಿಸುವರು;",
			"22": "<sup>22</sup> ಮೇಲಕ್ಕೆ ಕಣ್ಣೆತ್ತಿದರೂ ಭೂಮಿಯನ್ನು ದೃಷ್ಟಿಸಿದರೂ ಇಗೋ, ಇಕ್ಕಟ್ಟೆಂಬ ಕತ್ತಲೂ ಸಂಕಟವೆಂಬ ಅಂಧಕಾರವೂ ಕವಿದುಕೊಂಡಿರುವವು; ಕಾರ್ಗತ್ತಲೆಗೆ ತಳ್ಳಲ್ಪಡುವರು."
		},
		"9": {
			"1": "<sup>1</sup> ಆದರೆ ಸಂಕಟಪಟ್ಟ ದೇಶಕ್ಕೆ ಅಂಧಕಾರವಿನ್ನಿಲ್ಲ. ಪೂರ್ವಕಾಲದಲ್ಲಿ ಜೆಬುಲೋನ್ ಮತ್ತು ನಫ್ತಾಲಿ ಸೀಮೆಗಳನ್ನು ಆತನು ಅವಮಾನಕ್ಕೆ ಗುರಿಮಾಡಿದನು; ಅನಂತರದಲ್ಲಿ ಯೊರ್ದನಿನ ಆಚೆಯ ಸೀಮೆ, ಸಮುದ್ರದ ಕಡೆಗಿರುವ ಸೀಮೆ, ಅನ್ಯಜನಗಳಿಗಿರುವ ಗಲಿಲಾಯ ಸೀಮೆ ಈ ಪ್ರಾಂತವನ್ನೆಲ್ಲಾ ಘನಪಡಿಸಿದ್ದಾನೆ.",
			"2": "<sup>2</sup> ಕತ್ತಲಲ್ಲಿ ಸಂಚರಿಸಿದ ಜನರಿಗೆ ದೊಡ್ಡ ಬೆಳಕು ಕಾಣಿಸಿತು, ಕಾರ್ಗತ್ತಲಾದ ದೇಶದಲ್ಲಿದ್ದವರಿಗೆ ಪ್ರಕಾಶವು ಹೊಳೆಯಿತು.",
			"3": "<sup>3</sup> ನೀನು ಪ್ರಜೆಗಳನ್ನು ವೃದ್ಧಿಗೊಳಿಸಿದ್ದೀ, ಅವರಿಗೆ ಸಂತೋಷವನ್ನು ಹೆಚ್ಚಿಸಿದ್ದೀ; ಸುಗ್ಗಿಕಾಲದಲ್ಲಿ ಜನರು ಹರ್ಷಿಸುವ ಹಾಗೂ ಕೊಳ್ಳೆಯನ್ನು ಹಂಚಿಕೊಳ್ಳುವವರು ಹೆಚ್ಚಳಪಡುವ ಹಾಗೂ ನಿನ್ನ ಮುಂದೆ ಆನಂದಿಸುತ್ತಾರೆ.",
			"4": "<sup>4</sup> ಅವರಿಗೆ ಭಾರವಾದ ನೊಗವನ್ನೂ ಬೆನ್ನನ್ನು ಹೊಡೆದ ಕೋಲನ್ನೂ ಬಿಟ್ಟಿಹಿಡಿದವನ ದೊಣ್ಣೆಯನ್ನೂ ಮಿದ್ಯಾನಿನ ನಾಶದಿನದಲ್ಲಿ ಮುರಿದುಬಿಟ್ಟಂತೆ ಮುರಿದುಬಿಟ್ಟಿದ್ದೀ.",
			"5": "<sup>5</sup> [ಯುದ್ಧದ] ಗದ್ದಲದಲ್ಲಿ ತುಳಿದಾಡಿದವರೆಲ್ಲರ ಮೆಟ್ಟುಗಳೂ ರಕ್ತದಲ್ಲಿ ಹೊರಳಾಡಿಸಿದ ಉಡುಪುಗಳೂ ಅಗ್ನಿಗೆ ಆಹಾರವಾಗಿ ಸುಟ್ಟುಹೋಗುವವು.",
			"6": "<sup>6</sup> ಒಂದು ಮಗು ನಮಗಾಗಿ ಹುಟ್ಟಿದೆಯಷ್ಟೆ, ವರದ ಮಗನು ನಮಗೆ ದೊರೆತನು; ಆಡಳಿತವು ಅವನ ಬಾಹುವಿನ ಮೇಲಿರುವದು; ಅದ್ಭುತಸ್ವರೂಪನು, ಆಲೋಚನಾಕರ್ತನು, ಪರಾಕ್ರಮಿಯಾದ ದೇವರು, ನಿತ್ಯನಾದ ತಂದೆ, ಸಮಾಧಾನದ ಪ್ರಭು ಎಂಬವು ಅವನ ಹೆಸರು.",
			"7": "<sup>7</sup> ಅವನ ಮುಖಾಂತರ ದಾವೀದನ ಸಿಂಹಾಸನದ ಆಡಳಿತವು ಅಭಿವೃದ್ಧಿಯಾಗುವದು, ದಾವೀದನ ರಾಜ್ಯದಲ್ಲಿ ನಿತ್ಯ ಸಮಾಧಾನವಿರುವದು, ಆ ರಾಜ್ಯವು ಇಂದಿನಿಂದ ಯಾವಾಗಲೂ ನೀತಿನ್ಯಾಯಗಳ ಮೂಲಕ ಸ್ಥಾಪಿತವಾಗಿ ಸ್ಥಿರಗೊಳ್ಳುವದು; ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನ ಆಗ್ರಹವು ಇದನ್ನು ನೆರವೇರಿಸುವದು.",
			"8": "<sup>8</sup> ಕರ್ತನು ಯಾಕೋಬ್ಯರಿಗೆ ವಿರುದ್ಧವಾಗಿ ಒಂದು ಮಾತನ್ನು ಹೇಳಿ ಕಳುಹಿಸಿದನು; ಅದು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ತಗಲಿತು.",
			"9": "<sup>9</sup> ಇಟ್ಟಿಗೆಗಳು ಬಿದ್ದುಹೋದವು, ಹೋಗಲಿ, ಕೆತ್ತಿದ ಕಲ್ಲಿನಿಂದ ಕಟ್ಟುವೆವು; ಅತ್ತಿಮರಗಳು ಕಡಿಯಲ್ಪಟ್ಟವು, ಇರಲಿ,",
			"10": "<sup>10</sup> ದೇವದಾರುಗಳನ್ನು ಹಾಕುವೆವು ಎಂದು ಗರ್ವದಿಂದಲೂ ಉಬ್ಬಟೆಯಿಂದಲೂ ಹೇಳಿಕೊಳ್ಳುವ ಎಫ್ರಾಯೀಮ್ಯರು ಸಮಾರ್ಯದ ನಿವಾಸಿಗಳು ಇವರೆಲ್ಲರಿಗೂ ಆ ಮಾತು ಗೊತ್ತಾಗುವದು.",
			"11": "<sup>11</sup> ಯೆಹೋವನು ರೆಚೀನನ ವೈರಿಗಳನ್ನು ಅವರಿಗೆ ವಿರುದ್ಧವಾಗಿ ಹೆಚ್ಚಿಸಿದ್ದಾನೆ; ಅವರ ಮುಂದೆ ಅರಾಮ್ಯರನ್ನು ಅವರ ಹಿಂದೆ ಫಿಲಿಷ್ಟಿಯರನ್ನು ಅಂತು ಅವನ ಶತ್ರುಗಳನ್ನೆಲ್ಲಾ ಎಬ್ಬಿಸಿದ್ದಾನೆ.",
			"12": "<sup>12</sup> ಇವರು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಬಾಯಿತೆರೆದು ನುಂಗಿದ್ದಾರೆ. ಇಷ್ಟೆಲ್ಲಾ ನಡೆದರೂ ಆತನ ಕೋಪವು ತೀರದೆ ಕೈ ಎತ್ತಿಯೇ ಇದೆ.",
			"13": "<sup>13</sup> ಹೀಗಿದ್ದರೂ ಆ ಜನರು ತಮ್ಮನ್ನು ಹೊಡೆದಾತನ ಕಡೆಗೆ ತಿರುಗಿಕೊಳ್ಳಲೂ ಇಲ್ಲ, ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನನ್ನು ಸೇವಿಸಲೂ ಇಲ್ಲ.",
			"14": "<sup>14</sup> ಆದಕಾರಣ ಯೆಹೋವನು ಇಸ್ರಾಯೇಲಿನಿಂದ ತಲೆಬಾಲಗಳನ್ನೂ ತಾಲತೃಣಗಳನ್ನೂ ಒಂದೇ ದಿನದಲ್ಲಿ ಕಡಿದುಬಿಟ್ಟಿದ್ದಾನೆ.",
			"15": "<sup>15</sup> (ಘನಹೊಂದಿದ ಹಿರಿಯನೇ ತಲೆ; ಸುಳ್ಳುಬೋಧನೆ ಮಾಡುವ ಪ್ರವಾದಿಯೇ ಬಾಲ).",
			"16": "<sup>16</sup> ಈ ಜನರನ್ನು ನಡಿಸುವವರು ದಾರಿತಪ್ಪಿಸುವವರಾಗಿದ್ದಾರಷ್ಟೆ; ನಡಿಸಲ್ಪಟ್ಟವರೂ ನಾಶವಾಗುತ್ತಾರೆ.",
			"17": "<sup>17</sup> ಹೀಗಿರಲು ಕರ್ತನು ಅವರ ಯೌವನಸ್ಥರಲ್ಲಿ ಉಲ್ಲಾಸಿಸನು, ಅವರಲ್ಲಿನ ಅನಾಥರನ್ನೂ ವಿಧವೆಯರನ್ನೂ ಕರುಣಿಸನು; ಪ್ರತಿಯೊಬ್ಬನೂ ಭ್ರಷ್ಟನೂ ದುಷ್ಟನೂ ಆಗಿದ್ದಾನೆ; ಎಲ್ಲರ ಬಾಯೂ ನೀಚವಾಗಿ ಮಾತಾಡುತ್ತದೆ. ಎಷ್ಟು ದಂಡಿಸಿದರೂ ಆತನ ಕೋಪವು ತೀರದೆ ಕೈ ಎತ್ತಿಯೇ ಇದೆ.",
			"18": "<sup>18</sup> ದುಷ್ಟತನವು ಬೆಂಕಿಯಂತೆ ಉರಿದು ಮುಳ್ಳುಗಿಳ್ಳನ್ನು ನುಂಗಿ ಅರಣ್ಯದ ಪೊದೆಗಳನ್ನು ಹತ್ತಿಕೊಳ್ಳಲು ಅವು ಹೊಗೆಹೊಗೆಯಾಗಿ ಸುತ್ತಿಸುತ್ತಿಕೊಂಡು ಮೇಲಕ್ಕೆ ಏರುತ್ತವೆ.",
			"19": "<sup>19</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನ ಕೋಪೋದ್ರೇಕದಿಂದ ದೇಶವು ಸುಟ್ಟುಹೋಗಿದೆ; ಪ್ರಜೆಯು ಅಗ್ನಿಗೆ ಆಹುತಿಯಾಗಿದೆ; ಅಣ್ಣನು ತಮ್ಮನನ್ನು ಕರುಣಿಸನು.",
			"20": "<sup>20</sup> ಬಲಗಡೆಯಲ್ಲಿರುವದನ್ನು ಕಿತ್ತುಕೊಂಡು [ತಿಂದರೂ] ಹಸಿದೇ ಇರುತ್ತಾರೆ; ಎಡಗಡೆಯಲ್ಲಿರುವದನ್ನು ಉಂಡರೂ ತೃಪ್ತಿಗೊಳ್ಳರು; ಒಬ್ಬೊಬ್ಬನೂ ತನ್ನ ತನ್ನ ತೋಳಿನ ಮಾಂಸವನ್ನು ತಿನ್ನುತ್ತಾನೆ.",
			"21": "<sup>21</sup> ಮನಸ್ಸೆಯು ಎಫ್ರಾಯೀಮನ್ನು, ಎಫ್ರಾಯೀಮು ಮನಸ್ಸೆಯನ್ನು ತಿಂದು ಬಿಡುತ್ತದೆ, ಈ ಎರಡೂ ಯೆಹೂದಕ್ಕೆ ವಿರುದ್ಧವಾಗಿವೆ. ಇಷ್ಟೆಲ್ಲಾ ನಡೆದರೂ ಆತನ ಕೋಪವು ತೀರದೆ ಕೈ ಎತ್ತಿಯೇ ಇದೆ."
		},
		"10": {
			"1": "<sup>1</sup> ಅಯ್ಯೋ, ಬಡವರನ್ನು ನ್ಯಾಯಸ್ಥಾನದಿಂದ ತಳ್ಳಿ ನನ್ನ ಜನರಲ್ಲಿನ ದರಿದ್ರರಿಗೆ ನ್ಯಾಯವನ್ನು ತಪ್ಪಿಸಬೇಕೆಂತಲೂ ವಿಧವೆಯರನ್ನು ಸೂರೆಮಾಡಿ ಅನಾಥರನ್ನು ಕೊಳ್ಳೆಹೊಡೆಯಬೇಕೆಂತಲೂ",
			"2": "<sup>2</sup> ಅನ್ಯಾಯವಾದ ತೀರ್ಪುಗಳನ್ನು ತೀರಿಸಿ ಕೇಡಿನ ಪತ್ರಗಳನ್ನು ಬರೆಯಿಸುವವರ ಗತಿಯನ್ನು ಏನೆಂದು ಹೇಳಲಿ!",
			"3": "<sup>3</sup> ದಂಡನೆಯ ದಿನದಲ್ಲಿಯೂ ದೂರದಿಂದ ಬರುವ ನಾಶನದಲ್ಲಿಯೂ ಏನು ಮಾಡುವಿರಿ? ಸಹಾಯಕ್ಕಾಗಿ ಯಾರ ಬಳಿಗೆ ಓಡುವಿರಿ? ನಿಮ್ಮ ಐಶ್ವರ್ಯವನ್ನು ಯಾರ ವಶಮಾಡುವಿರಿ?",
			"4": "<sup>4</sup> ಕೈದಿಗಳ [ಕಾಲ] ಕೆಳಗೆ ಮುದುರಿಕೊಂಡು ಹತರಾದವರ ಕೆಳಗೆ ಬಿದ್ದಿರುವದೇ ಇವರ ಗತಿ! ಇಷ್ಟೆಲ್ಲಾ ನಡೆದರೂ ಆತನ ಕೋಪವು ತೀರದೆ ಕೈ ಎತ್ತಿಯೇ ಇದೆ.",
			"5": "<sup>5</sup> ಆಹಾ, ಅಶ್ಶೂರವೇ, ನೀನು ನನ್ನ ಕೋಪವೆಂಬ ಕೋಲು, ನಿನ್ನ ಕೈಯಲ್ಲಿರುವ ದೊಣ್ಣೆಯು ನನ್ನ ರೋಷವೇ!",
			"6": "<sup>6</sup> ನಾನು ಅದನ್ನು ಭ್ರಷ್ಟ ಜನರಿಗೆ ವಿರುದ್ಧವಾಗಿ ಕಳುಹಿಸಿ ನನ್ನ ಕೋಪಕ್ಕೆ ಗುರಿಯಾದ [ನನ್ನ] ಪ್ರಜೆಯನ್ನು ಸೂರೆಮಾಡಿ ಕೊಳ್ಳೆಹೊಡೆದು ಬೀದಿಯ ಕೆಸರನ್ನೋ ಎಂಬಂತೆ ತುಳಿದುಹಾಕಬೇಕೆಂದು ಅದಕ್ಕೆ ಅಪ್ಪಣೆಕೊಡುವೆನು.",
			"7": "<sup>7</sup> ಅದರ ಅಭಿಪ್ರಾಯವೋ ಹಾಗಲ್ಲ; ಅನೇಕಾನೇಕ ಜನಾಂಗಗಳನ್ನು ತಾನೇ ಸಂಹರಿಸಿ ನಿರ್ಮೂಲಮಾಡುವೆನೆಂಬದೇ ಹೊರತು ಈ ಯೋಚನೆಯು ಅದರ ಮನಸ್ಸಿನಲ್ಲಿ ಇಲ್ಲವೇ ಇಲ್ಲ.",
			"8": "<sup>8</sup> ಅದು ಅಂದುಕೊಳ್ಳುವದೇನಂದರೆ - ನನ್ನ ಅಧಿಪತಿಗಳೆಲ್ಲಾ ರಾಜರಲ್ಲವೆ.",
			"9": "<sup>9</sup> ಕರ್ಕೆಮೀಷಿನ ಗತಿಯು ಕಲ್ನೋವಿಗೂ ಆಯಿತಲ್ಲವೆ, ಅರ್ಪದಿಗೆ ಆದ ಪಾಡು ಹಮಾತಿಗೂ ಬಂತಷ್ಟೆ; ದಮಸ್ಕದ ಅವಸ್ಥೆಯು ಸಮಾರ್ಯಕ್ಕೂ ಸಂಭವಿಸಿತಲ್ಲಾ.",
			"10": "<sup>10</sup> ಯೆರೂಸಲೇಮಿನ ಮತ್ತು ಸಮಾರ್ಯದ ವಿಗ್ರಹಗಳಿಗಿಂತ ವಿಶೇಷ ವಿಗ್ರಹಗಳುಳ್ಳ ದೇವತೆಗಳ ವಶವಾಗಿರುವ ರಾಜ್ಯಗಳ ಮೇಲೆ ನಾನು ಕೈಮಾಡಿದಂತೆಯೂ",
			"11": "<sup>11</sup> ಸಮಾರ್ಯವನ್ನೂ ಅದರ ವಿಗ್ರಹಗಳನ್ನೂ ನಾನು ಕೆಡವಿಬಿಟ್ಟಹಾಗೂ ಯೆರೂಸಲೇಮನ್ನೂ ಅದರ ಬೊಂಬೆಗಳನ್ನೂ ಕೆಡವಲಾರೆನೋ? ಎಂಬದೇ.",
			"12": "<sup>12</sup> ಆದಕಾರಣ ಕರ್ತನಾದ ನಾನು ಚೀಯೋನ್ ಪರ್ವತದಲ್ಲಿಯೂ ಯೆರೂಸಲೇಮಿನಲ್ಲಿಯೂ ನನ್ನ ಉದ್ದೇಶವನ್ನೆಲ್ಲಾ ನೆರವೇರಿಸಿದ ಮೇಲೆ ಅಶ್ಶೂರದ ರಾಜನ ಉಬ್ಬಟೆಯ ಕೊಚ್ಚಾಟಕ್ಕೂ ಗರ್ವದೃಷ್ಟಿಯ ಮೆರೆದಾಟಕ್ಕೂ ತಕ್ಕ ದಂಡನೆಯನ್ನು ಮಾಡುವೆನು.",
			"13": "<sup>13</sup> ಏಕಂದರೆ ಅವನು ತನ್ನೊಳಗೆ - ನನ್ನ ಜ್ಞಾನಭುಜ ಬಲಗಳಿಂದಲೇ ಇದನ್ನು ಮಾಡಿದ್ದೇನೆ, ನಾನೇ ವಿವೇಕಿ; ಜನಾಂಗಗಳ ಎಲ್ಲೆಗಳನ್ನು ಕಿತ್ತು ಅವರ ನಿಧಿನಿಕ್ಷೇಪಗಳನ್ನು ಸೂರೆಮಾಡಿ ಸಿಂಹಾಸನಾರೂಢರನ್ನು ಮಹಾವೀರನಂತೆ ಕೆಡವಿಬಿಟ್ಟಿದ್ದೇನೆ;",
			"14": "<sup>14</sup> ಜನಗಳ ಆಸ್ತಿಪಾಸ್ತಿಯು ಗೂಡಿನಂತೆ ನನ್ನ ಕೈಗೆ ಸಿಕ್ಕಿದೆ; ಪಕ್ಷಿಯು ಇಟ್ಟುಹೋದ ಮೊಟ್ಟೆಗಳನ್ನು ಸಂಗ್ರಹಿಸಿಕೊಳ್ಳುವವನಂತೆ ಭೂಮಿಯನ್ನೆಲ್ಲಾ ಸಂಗ್ರಹಿಸಿಕೊಂಡಿದ್ದೇನೆ; ರೆಕ್ಕೆಯಾಡಿಸಿ ಬಾಯಿದೆರೆದು ಕೀಚುಗುಟ್ಟುವ ಯಾರೂ ಇರಲಿಲ್ಲ ಎಂದು ಅಂದುಕೊಂಡನು.",
			"15": "<sup>15</sup> ಕೊಡಲಿಯು ಕಡಿಯುವವನಿಗೆ ವಿರುದ್ಧವಾಗಿ ಕೊಚ್ಚಿಕೊಂಡೀತೇ? ಗರಗಸವು ಆಡಿಸುವವನಿಗೆ ಪ್ರತಿಯಾಗಿ ತನ್ನನ್ನು ಹೆಚ್ಚಿಸಿಕೊಂಡೀತೇ? ಎತ್ತಿಕೊಂಡವನನ್ನು ಕೋಲು ಬೀಸಿದಂತಾಯಿತು, ಮರಕ್ಕಿಂತ ಶ್ರೇಷ್ಠನಾಗಿರುವ ಮನುಷ್ಯನನ್ನು ದೊಣ್ಣೆಯು ಎತ್ತಿಕೊಂಡ ಹಾಗಾಯಿತು.",
			"16": "<sup>16</sup> ಆದಕಾರಣ ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೆಂಬ ಕರ್ತನು ಅವನ ಕೊಬ್ಬಿದ ಪ್ರಜೆಗಳಿಗೆ ಕ್ಷಯವನ್ನುಂಟುಮಾಡುವನು; ದಹಿಸುವ ಅಗ್ನಿಯಂತಿರುವ ಉರಿಯೊಂದು ಅವನ ವೈಭವದ ಕೆಳಗೆ ಹತ್ತಿಕೊಳ್ಳುವದು.",
			"17": "<sup>17</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಪರಂಜ್ಯೋತಿಯು ದಹಿಸುವ ಅಗ್ನಿಯಾಗುವದು, ಅವರ ಸದಮಲಸ್ವಾಮಿಯು ಜ್ವಾಲೆಯಂತಿರುವನು; ಅದು ಒಂದೇ ದಿವಸದಲ್ಲಿ ಅಶ್ಶೂರದ ಮುಳ್ಳುಗಿಳ್ಳನ್ನು ಸುಟ್ಟು ನುಂಗಿಬಿಡುವದು.",
			"18": "<sup>18</sup> ಅದು ದೇಹಾತ್ಮಗಳನ್ನುಳಿಸದೆ ಅಲ್ಲಿನ ವನದ ಮತ್ತು ತೋಟದ ವೈಭವವನ್ನು ನಿರ್ಮೂಲಮಾಡುವದು; ದೇಶವೋ ಉಡುಗುತ್ತಿರುವ ರೋಗಿಯಂತಿರುವದು.",
			"19": "<sup>19</sup> ಅದರ ವನವೃಕ್ಷಗಳ ಸಂಖ್ಯೆಯು ಮಗು ಬರೆದು ಎಣಿಸುವಷ್ಟು ಕಡಿಮೆಯಾಗುವದು.",
			"20": "<sup>20</sup> ಆ ದಿನದಲ್ಲಿ ಇಸ್ರಾಯೇಲ್ಯರೊಳಗೆ ಉಳಿದವರು, ಯಾಕೋಬನ ಮನೆತನದವರಲ್ಲಿ ತಪ್ಪಿಸಿಕೊಂಡವರು ತಮ್ಮನ್ನು ಹೊಡೆದವನ ಆಧಾರವನ್ನು ಬಿಟ್ಟು ಯೆಹೋವನೆಂಬ ಇಸ್ರಾಯೇಲ್ಯರ ಸದಮಲಸ್ವಾಮಿಯನ್ನು ಯಥಾರ್ಥವಾಗಿ ಆಧಾರಮಾಡಿಕೊಳ್ಳುವರು.",
			"21": "<sup>21</sup> ಜನಶೇಷವು, ಅಂದರೆ ಯಾಕೋಬ್ಯರಲ್ಲಿ ಉಳಿದದ್ದು, ಪರಾಕ್ರಮಿಯಾದ ದೇವರ ಕಡೆಗೆ ತಿರುಗಿಕೊಳ್ಳುವದು.",
			"22": "<sup>22</sup> ಇಸ್ರಾಯೇಲೇ, ನಿನ್ನ ಜನರು ಸಮುದ್ರದ ಉಸುಬಿನಂತೆ ಅಸಂಖ್ಯವಾಗಿದ್ದರೂ ಅವರಲ್ಲಿ ಉಳಿದ ಒಂದು ಶೇಷ ಮಾತ್ರ ತಿರುಗಿಕೊಳ್ಳುವದು; ತುಂಬಿತುಳುಕುವ ನ್ಯಾಯಪ್ರವಾಹದ ಪ್ರಲಯ ನಿಶ್ಚಿತವಾಗಿದೆ.",
			"23": "<sup>23</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೆಂಬ ಕರ್ತನು ಭೂಮಂಡಲದಲ್ಲೆಲ್ಲಾ ನಿಶ್ಚಿತಪ್ರಲಯವನ್ನು ಉಂಟುಮಾಡುವನಷ್ಟೆ.",
			"24": "<sup>24</sup> ಇಂತಿರಲು ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೆಂಬ ಕರ್ತನು ಹೀಗೆ ಹೇಳುತ್ತಾನೆ - ಚೀಯೋನಿನಲ್ಲಿ ವಾಸಿಸುವ ನನ್ನ ಜನರೇ, ಐಗುಪ್ತ್ಯರು ನಿಮಗೆ ಮಾಡಿದಂತೆ ನಿಮ್ಮನ್ನು ಕೋಲಿನಿಂದ ಹೊಡೆದು ನಿಮಗೆ ವಿರುದ್ಧವಾಗಿ ದೊಣ್ಣೆಯನ್ನೆತ್ತುವ ಅಶ್ಶೂರ್ಯರಿಗೆ ಭಯಪಡಬೇಡಿರಿ.",
			"25": "<sup>25</sup> ಬಹು ಸ್ವಲ್ಪ ಕಾಲದೊಳಗೆ [ನಿಮ್ಮ ಮೇಲಿನ] ಉಗ್ರವು ತೀರಿ ನನ್ನ ಕೋಪವು ಅವರ ನಾಶನಕ್ಕಾಗುವದು.",
			"26": "<sup>26</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಓರೇಬನ ಬಂಡೆಯ ಹತ್ತಿರ ಮಿದ್ಯಾನ್ಯರನ್ನು ಹತಮಾಡಿದಂತೆ ಅವರ ಮೇಲೆಯೂ ಕೊರಡೆಯನ್ನು ಬೀಸುವನು; ಆತನು ದಂಡವನ್ನು ಸಮುದ್ರದ ಮೇಲೆ ಚಾಚಿ ಐಗುಪ್ತ್ಯರ ಮೇಲೆ ಎತ್ತಿದಂತೆ ಎತ್ತುವನು.",
			"27": "<sup>27</sup> ಆ ದಿನದಲ್ಲಿ ಅವರು ಹೊರಿಸಿದ ಹೊರೆಯು ನಿಮ್ಮ ಬೆನ್ನಿನಿಂದಲೂ ಹೂಡಿದ ನೊಗವು ಕತ್ತಿನಿಂದಲೂ ತೊಲಗುವವು; ನೀವು ಪುಷ್ಟರಾದ ಕಾರಣ ನೊಗವು ಮುರಿದು ಹೋಗುವದು.",
			"28": "<sup>28</sup> ಇಗೋ, ಬಂದರು, ಅಯ್ಯಾಥಿನ ಮೇಲೆ ಬಂದಿದ್ದಾರೆ, ಮಿಗ್ರೋನನ್ನು ಹಾದುಹೋಗಿದ್ದಾರೆ, ಮಿಕ್ಮಾಷಿನಲ್ಲಿ ತಮ್ಮ ಸಾಮಗ್ರಿಯನ್ನು ಇಟ್ಟುಬಿಟ್ಟಿದ್ದಾರೆ,",
			"29": "<sup>29</sup> ಕಣಿವೆಯನ್ನು ದಾಟಿದ್ದಾರೆ, ಗೆಬದಲ್ಲಿ ದಂಡಿಳಿಸುವ [ಅನ್ನುತ್ತಾರೆ], ರಾಮಾ ನಡುಗುತ್ತದೆ,",
			"30": "<sup>30</sup> ಸೌಲನ ಗಿಬೆಯ ಓಡಿಹೋಯಿತು; ಗಲ್ಲೀಮ್ ಗ್ರಾಮವೇ, ಕೂಗಿ ಕಿರಚಿಕೋ! ಲಯೆಷವೇ, ಕಿವಿಗೊಡು!",
			"31": "<sup>31</sup> ಅನಾತೋತೇ, ಪ್ರತಿಧ್ವನಿ ಕೊಡು! ಮದ್ಮೇನ ದಿಕ್ಕಾಪಾಲಾಯಿತು; ಗೇಬೀಮಿನ ನಿವಾಸಿಗಳು ವಲಸೆಹೋದರು;",
			"32": "<sup>32</sup> ಇದೇ ದಿವಸ [ಶತ್ರುಗಳು] ನೋಬಿನಲ್ಲಿ ಬೀಡುಗೊಳ್ಳುವರು; ಚೀಯೋನ್ ನಗರಿಯ ಪರ್ವತದ ಕಡೆಗೆ, ಯೆರೂಸಲೇಮಿನ ಬೆಟ್ಟದ ಕಡೆಗೆ, ಕೈ ಬೀಸುತ್ತಾರೆ.",
			"33": "<sup>33</sup> ಆಹಾ, ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೆಂಬ ಕರ್ತನು ಕೊಂಬೆಗಳನ್ನು ಕತ್ತರಿಸಿ ಢಮ್ಮನೆ ಬೀಳಿಸುವನು; ಉನ್ನತ ವೃಕ್ಷಗಳು ಕಡಿದುಹೋಗುವವು,",
			"34": "<sup>34</sup> ಎತ್ತರ ಮರಗಳು ನೆಲದ ಪಾಲಾಗುವವು; ಆತನು ಅಡವಿಯ ಪೊದೆಗಳನ್ನು ಕಬ್ಬಿಣದ ಮುಟ್ಟಿನಿಂದ ಕಡಿದುಬಿಡುವನು; ಲೆಬನೋನಿನ ವನವು ಆ ಬಲಿಷ್ಠನಿಂದ ಬಿದ್ದುಹೋಗುವದು."
		},
		"11": {
			"1": "<sup>1</sup> ಇಷಯನ ಬುಡದಿಂದ ಒಂದು ಚಿಗುರು ಒಡೆಯುವದು, ಅದರ ಬೇರಿನಿಂದ ಹೊರಟ ತಳಿರು ಫಲಿಸುವದು;",
			"2": "<sup>2</sup> ಆ ಅಂಕುರದ ಮೇಲೆ ಜ್ಞಾನವಿವೇಕದಾಯಕ ಆತ್ಮ, ಆಲೋಚನಪರಾಕ್ರಮಗಳನ್ನು ಹುಟ್ಟಿಸುವ ಆತ್ಮ, ತಿಳುವಳಿಕೆಯನ್ನೂ ಯೆಹೋವನ ಭಯವನ್ನೂ ಉಂಟುಮಾಡುವ ಆತ್ಮ, ಅಂತು ಯೆಹೋವನ ಆತ್ಮವೇ ನೆಲೆಗೊಂಡಿರುವದು;",
			"3": "<sup>3</sup> ಯೆಹೋವನ ಭಯವು ಅವನಿಗೆ ಪರಿಮಳಿಸುವದು; ಅವನು ಕಣ್ಣಿಗೆ ಕಂಡಂತೆ ತೀರ್ಪುಮಾಡುವದಿಲ್ಲ, ಕಿವಿಗೆ ಬಿದ್ದಂತೆ ನಿರ್ಣಯಿಸುವದಿಲ್ಲ;",
			"4": "<sup>4</sup> ಬಡವರಿಗೋಸ್ಕರ ನ್ಯಾಯವಾಗಿ ತೀರ್ಪುಮಾಡುವನು, ಲೋಕದ ದೀನರಿಗೋಸ್ಕರ ಧರ್ಮವಾಗಿ ನಿರ್ಣಯಿಸುವನು; ಲೋಕವನ್ನು ತನ್ನ ಬಾಯ ದಂಡದಿಂದ ದಂಡಿಸುವನು, ದುಷ್ಟರನ್ನು ತನ್ನ ಬಾಯುಸುರಿನಿಂದ ಕೊಲ್ಲುವನು.",
			"5": "<sup>5</sup> ಧರ್ಮವೇ ಅವನಿಗೆ ನಡುಕಟ್ಟು, ಪ್ರಾಮಾಣಿಕತೆಯೇ ಸೊಂಟಪಟ್ಟಿ.",
			"6": "<sup>6</sup> ತೋಳವು ಕುರಿಯ ಸಂಗಡ ವಾಸಿಸುವದು, ಚಿರತೆಯು ಮೇಕೆಮರಿಯೊಂದಿಗೆ ಮಲಗುವದು; ಕರುವೂ ಪ್ರಾಯದ ಸಿಂಹವೂ ಪುಷ್ಟಪಶುವೂ ಒಟ್ಟಿಗಿರುವವು; ಇವುಗಳನ್ನು ಚಿಕ್ಕ ಮಗುವು ನಡಿಸುವದು.",
			"7": "<sup>7</sup> ಹಸುವು ಕರಡಿಯು ಸಂಗಡ ಮೇಯುವವು; ಅವುಗಳ ಮರಿಗಳು ಜೊತೆಯಾಗಿ ಮಲಗುವವು; ಸಿಂಹವು ಎತ್ತಿನಂತೆ ಹುಲ್ಲು ತಿನ್ನುವದು.",
			"8": "<sup>8</sup> ಮೊಲೆಕೂಸು ನಾಗರಹುತ್ತದ ಮೇಲೆ ಆಡುವದು; ಮೊಲೆಬಿಟ್ಟ ಮಗು ಹಾವಿನ ಬಿಲದ ಮೇಲೆ ಕೈಹಾಕುವದು.",
			"9": "<sup>9</sup> ನನ್ನ ಪರಿಶುದ್ಧಪರ್ವತದಲ್ಲೆಲ್ಲಾ ಯಾರೂ ಕೇಡು ಮಾಡುವದಿಲ್ಲ, ಯಾರೂ ಹಾಳು ಮಾಡುವದಿಲ್ಲ; ಸಮುದ್ರದಲ್ಲಿ ನೀರು ಹೇಗೋ ಹಾಗೆಯೇ ಭೂಮಿಯಲ್ಲಿ ಯೆಹೋವನ ಜ್ಞಾನವು ತುಂಬಿಕೊಂಡಿರುವದು.",
			"10": "<sup>10</sup> ಆ ದಿನದಲ್ಲಿ ಜನಾಂಗಗಳು ತಮಗೆ ಧ್ವಜಪ್ರಾಯನಾಗಿ ನಿಂತಿರುವ ಇಷಯನ ಅಂಕುರದವನನ್ನು ಆಶ್ರಯಿಸುವರು; ಅವನ ವಿಶ್ರಮ ಸ್ಥಾನವು ವೈಭವವುಳ್ಳದ್ದಾಗಿರುವದು.",
			"11": "<sup>11</sup> ಆ ದಿನದಲ್ಲಿ ಕರ್ತನು ತನ್ನ ಜನಶೇಷವನ್ನು ಬಿಡಿಸಿಕೊಳ್ಳುವದಕ್ಕೆ ಎರಡನೆಯ ಸಾರಿ ಕೈಹಾಕಿ ಅಶ್ಶೂರ, ಐಗುಪ್ತ, ಪತ್ರೋಸ್, ಕೂಷ್, ಏಲಾಮ್, ಶಿನಾರ್, ಹಮಾಥ್, ಸಮುದ್ರದ ಕರಾವಳಿ, ಈ ಸ್ಥಳಗಳಲ್ಲಿ ಉಳಿದವರನ್ನು ಬರಮಾಡಿಕೊಳ್ಳುವನು.",
			"12": "<sup>12</sup> ಅವನು ಜನಾಂಗಗಳಿಗೆ ಧ್ವಜವನ್ನೆತ್ತಿ ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಸೆರೆಗೆ ಒಯ್ಯಲ್ಪಟ್ಟವರನ್ನೂ ಯೆಹೂದದಿಂದ ಚದರಿದವರನ್ನೂ ಭೂಮಿಯ ಚತುರ್ದಿಕ್ಕುಗಳಿಂದಲೂ ಕೂಡಿಸಿಕೊಳ್ಳುವನು.",
			"13": "<sup>13</sup> ಎಫ್ರಾಯೀಮಿನ ಹೊಟ್ಟೇಕಿಚ್ಚು ತೊಲಗುವದು, ಯೆಹೂದವನ್ನು ವಿರೋಧಿಸುವವರು ನಿರ್ಮೂಲವಾಗುವರು; ಎಫ್ರಾಯೀಮು ಯೆಹೂದವನ್ನು ಮತ್ಸರಿಸದು, ಯೆಹೂದವು ಎಫ್ರಾಯೀಮನ್ನು ವಿರೋಧಿಸದು.",
			"14": "<sup>14</sup> ಇವು ಪಡುವಲಲ್ಲಿ ಫಿಲಿಷ್ಟಿಯರ ಭುಜದ ಮೇಲೆ ಎರಗುವವು, ಜೊತೆಯಾಗಿ ಮೂಡಣದವರನ್ನು ಸೂರೆಗೊಳ್ಳುವವು; ಎದೋಮಿನ ಮತ್ತು ಮೋವಾಬಿನ ಮೇಲೆ ಕೈಮಾಡುವವು; ಅಮ್ಮೋನ್ಯರು ಇವುಗಳಿಗೆ ಅಧೀನರಾಗುವರು.",
			"15": "<sup>15</sup> ಆಗ ಯೆಹೋವನು ಐಗುಪ್ತ ಸಮುದ್ರದ ಕೊಲ್ಲಿಯನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ನಾಶನಮಾಡುವನು; ಯೂಫ್ರೇಟೀಸ್ ನದಿಯ ಮೇಲೆ ಕೈ ಝಾಡಿಸಿ ತನ್ನ ಬಿಸಿಗಾಳಿಯಿಂದ ಅದನ್ನು ಏಳು ತೊರೆಗಳನ್ನಾಗಿ ಒಡೆದು ಜನರು ಕೆರ ಮೆಟ್ಟಿ ದಾಟುವಂತೆ ಮಾಡುವನು.",
			"16": "<sup>16</sup> ಇದಲ್ಲದೆ ಇಸ್ರಾಯೇಲ್ಯರು ಐಗುಪ್ತ ದೇಶದಿಂದ ಹೊರಟುಬಂದ ಕಾಲದಲ್ಲಿ ಅವರಿಗೆ ಮಾರ್ಗವು ಹೇಗೆ ಸಿದ್ಧವಾಯಿತೋ ಹಾಗೆಯೇ ಅಶ್ಶೂರದಿಂದ ತಪ್ಪಿಸಿಕೊಂಡು ಬರುವ ಆತನ ಜನಶೇಷಕ್ಕೆ ರಾಜಮಾರ್ಗವು ಸಿದ್ಧವಾಗುವದು."
		},
		"12": {
			"1": "<sup>1</sup> ಆ ದಿನದಲ್ಲಿ ನೀನು ಹೇಳುವದೇನಂದರೆ - ಯೆಹೋವನೇ, ನಾನು ನಿನಗೆ ಕೃತಜ್ಞತೆಯನ್ನು ಸಲ್ಲಿಸುವೆನು; ನೀನು ನನ್ನ ಮೇಲೆ ಕೋಪಗೊಂಡಿದ್ದರೂ ಆ ನಿನ್ನ ಕೋಪವು ಪರಿಹಾರವಾಗಿ ನನ್ನನ್ನು ಸಂತೈಸುತ್ತೀಯಲ್ಲವೆ.",
			"2": "<sup>2</sup> ಇಗೋ ದೇವರೇ ನನಗೆ ರಕ್ಷಣೆ, ನಾನು ಹೆದರದೆ ಭರವಸಪಡುವೆನು; ನನ್ನ ಬಲವೂ ಕೀರ್ತನೆಯೂ ಯಾಹುಯೆಹೋವನಷ್ಟೆ, ಆತನೇ ನನಗೆ ರಕ್ಷಕನಾದನು ಎಂಬದೇ.",
			"3": "<sup>3</sup> ಮತ್ತು ರಕ್ಷಣೆಯೆಂಬ ಬಾವಿಗಳಿಂದ ನೀರನ್ನು ಉಲ್ಲಾಸದೊಡನೆ ಸೇದುವಿರಿ.",
			"4": "<sup>4</sup> ಆ ದಿನದಲ್ಲಿ ನೀವು ಹೇಳುವದೇನಂದರೆ - ಯೆಹೋವನಿಗೆ ಕೃತಜ್ಞತಾಸ್ತುತಿ ಮಾಡಿರಿ, ಆತನ ನಾಮದ ಮಹತ್ವವನ್ನು ವರ್ಣಿಸಿರಿ, ಜನಾಂಗಗಳಲ್ಲಿ ಆತನ ಕೃತ್ಯಗಳನ್ನು ಪ್ರಸಿದ್ಧಪಡಿಸಿರಿ, ಆತನ ನಾಮವು ಉನ್ನತೋನ್ನತವೆಂದು ಜ್ಞಾಪಕಪಡಿಸಿರಿ.",
			"5": "<sup>5</sup> ಯೆಹೋವನನ್ನು ಗಾನದಿಂದ ಸ್ತುತಿಸಿರಿ; ಆತನು ಮಹಿಮೆಯ ಕಾರ್ಯಗಳನ್ನು ಮಾಡಿದ್ದಾನೆ; ಇದು ಭೂಮಂಡಲದಲ್ಲೆಲ್ಲಾ ತಿಳಿದಿರಲಿ.",
			"6": "<sup>6</sup> ಚೀಯೋನಿನ ನಿವಾಸಿಗಳೇ, ಕೂಗಿರಿ, ಉತ್ಸಾಹ ಧ್ವನಿಮಾಡಿರಿ; ಇಸ್ರಾಯೇಲ್ಯರ ಸದಮಲಸ್ವಾಮಿಯು ನಿಮ್ಮ ಮಧ್ಯದಲ್ಲಿ ಮಹತ್ತಾಗಿದ್ದಾನಷ್ಟೆ ಎಂಬದೇ."
		},
		"13": {
			"1": "<sup>1</sup> ಆಮೋಚನ ಮಗನಾದ ಯೆಶಾಯನಿಗೆ ಬಾಬೆಲಿನ ವಿಷಯವಾಗಿ ಕಂಡುಬಂದ ದೈವೋಕ್ತಿ.",
			"2": "<sup>2</sup> [ಶತ್ರುಗಳು] ಬಂದು ಪ್ರಭುಗಳ ಪುರದ್ವಾರಗಳಲ್ಲಿ ನುಗ್ಗುವಂತೆ ಬೋಳುಬೆಟ್ಟದ ಮೇಲೆ ಧ್ವಜವೆತ್ತಿರಿ, ಕೂಗಿ ಅವರನ್ನು ಕರೆಯಿರಿ, ಕೈಸನ್ನೆಮಾಡಿರಿ.",
			"3": "<sup>3</sup> ನಾನು ಆರಿಸಿಕೊಂಡವರಿಗೆ ಅಪ್ಪಣೆಮಾಡಿದ್ದೇನೆ. ಹೌದು, ಉತ್ಸಾಹದಿಂದ ಮೆರೆಯುವ ಆ ನನ್ನ ಶೂರರು ನನ್ನ ಕೋಪವನ್ನು ತೀರಿಸಲಿ ಎಂದು ಅವರನ್ನು ಕರೆದಿದ್ದೇನೆ.",
			"4": "<sup>4</sup> ಆಹಾ, ಬಹು ಜನಸಮೂಹವಿದ್ದಂತೆ ಗದ್ದಲವು ಬೆಟ್ಟಗಳಲ್ಲಿ ಕೇಳಿಬರುತ್ತದೆ! ಇಗೋ, ಒಟ್ಟಿಗೆ ಕೂಡಿಕೊಂಡ ರಾಜ್ಯ ಜನಾಂಗಗಳ ಆರ್ಭಟ! ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಸೈನ್ಯವನ್ನು ಯುದ್ಧಕ್ಕಾಗಿ ಅಣಿಮಾಡುತ್ತಿದ್ದಾನೆ.",
			"5": "<sup>5</sup> ಯೆಹೋವನೂ ಆತನ ರೋಷಕ್ಕೆ ಆಯುಧಗಳಾದವರೂ ದೂರ ದೇಶದಿಂದ, ಅಂದರೆ ಆಕಾಶಮಂಡಲದ ಕಟ್ಟಕಡೆಯಿಂದ, ಭೂಮಿಯನ್ನೆಲ್ಲಾ ಹಾಳುಮಾಡುವದಕ್ಕಾಗಿ ಬಂದಿದ್ದಾರೆ.",
			"6": "<sup>6</sup> ಅರಚಿಕೊಳ್ಳಿರಿ, ಯೆಹೋವನ ದಿನವು ಸಮೀಪವಾಯಿತು; ಅದು ಸರ್ವಶಕ್ತನಿಂದ ನಾಶರೂಪವಾಗಿ ಬರುವದು.",
			"7": "<sup>7</sup> ಅದರಿಂದ ಎಲ್ಲರ ಕೈ ಜೋಲುಬೀಳುವದು, ಎಲ್ಲರ ಹೃದಯ ಕರಗಿ ನೀರಾಗುವದು;",
			"8": "<sup>8</sup> ಅವರು ಬೆಚ್ಚಿಬೀಳುವರು, ಯಾತನೆವೇದನೆಗಳು ಅವರನ್ನಾಕ್ರಮಿಸುವವು. ಹೆರುವ ಹೆಂಗಸಿನಂತೆ ಸಂಕಟಪಡುವರು; ಒಬ್ಬರನ್ನೊಬ್ಬರು ನೋಡಿ ವಿಸ್ಮಯಪಡುವರು, ಅವರ ಮುಖಗಳು [ತಲ್ಲಣದಿಂದ] ಬೆಂಕಿಬೆಂಕಿಯಾಗುವವು.",
			"9": "<sup>9</sup> ಇಗೋ, ಯೆಹೋವನ ದಿನವು ಬರುತ್ತಿದೆ; ಅದು ಭೂಮಿಯನ್ನು ಹಾಳುಮಾಡಿ ಪಾಪಿಗಳನ್ನು ನಿರ್ಮೂಲಪಡಿಸುವದಕ್ಕೆ [ಆತನ] ಕೋಪೋದ್ರೇಕದಿಂದಲೂ ತೀಕ್ಷ್ಣರೋಷದಿಂದಲೂ ಕ್ರೂರವಾಗಿರುವದು.",
			"10": "<sup>10</sup> ಆಕಾಶದ ತಾರೆಗಳೂ ನಕ್ಷತ್ರರಾಶಿಗಳೂ ಬೆಳಗವು, ಸೂರ್ಯನು ಮೂಡುತ್ತಲೇ ಕತ್ತಲಾಗುವನು, ಚಂದ್ರನು ಪ್ರಕಾಶಿಸನು.",
			"11": "<sup>11</sup> ನಾನು ಲೋಕದವರಿಗೆ ಅವರ ಪಾಪದ ಫಲವನ್ನೂ ದುಷ್ಟರಿಗೆ ಅವರ ದುಷ್ಕೃತ್ಯಗಳ ಫಲವನ್ನೂ ತಿನ್ನಿಸಿ ಸೊಕ್ಕಿದವರ ಸೊಕ್ಕನ್ನು ಅಡಗಿಸಿ ಭಯಂಕರರ ಹೆಮ್ಮೆಯನ್ನು ತಗ್ಗಿಸಿ",
			"12": "<sup>12</sup> ಮನುಷ್ಯರನ್ನು ಅಪರಂಜಿಗಿಂತಲೂ ಮಾನವರನ್ನು ಓಫೀರಿನ ಬಂಗಾರಕ್ಕಿಂತಲೂ ಅಪರೂಪವಾಗುವಂತೆ ಮಾಡುವೆನು.",
			"13": "<sup>13</sup> ಇದಕ್ಕಾಗಿ ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನ ಕೋಪೋದ್ರೇಕವೂ ತೀಕ್ಷ್ಣರೋಷವೂ ಮಸಗುತ್ತಿರುವ ದಿನದಲ್ಲಿ [ಯೆಹೋವನೆಂಬ] ನಾನು ಆಕಾಶಮಂಡಲವನ್ನು ನಡುಗಿಸಿ ಭೂಲೋಕವನ್ನು ಸ್ಥಳದಿಂದ ಕದಲಿಸುವೆನು.",
			"14": "<sup>14</sup> ಅಟ್ಟಿದ ಜಿಂಕೆಯಂತೆಯೂ ಯಾರೂ ಕೂಡದ ಕುರಿಗಳ ಹಾಗೂ ಪ್ರತಿಯೊಬ್ಬನು ಸ್ವಜನದ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡು ಸ್ವದೇಶಕ್ಕೆ ಓಡಿಹೋಗುವನು.",
			"15": "<sup>15</sup> ಸಿಕ್ಕಿಸಿಕ್ಕಿದವರೆಲ್ಲಾ ಇರಿಯಲ್ಪಡುವರು, ಅಟ್ಟಿ ಹಿಡಿಯಲ್ಪಟ್ಟ ಸಕಲರೂ ಕತ್ತಿಗೆ ತುತ್ತಾಗುವರು.",
			"16": "<sup>16</sup> ಅವರ ಮಕ್ಕಳನ್ನು ಬಂಡೆಗೆ ಅಪ್ಪಳಿಸಿಬಿಡುವರು, ಅವರ ಮನೆಗಳನ್ನು ಸೂರೆಮಾಡುವರು, ಅವರ ಹೆಂಡರನ್ನು ಕೆಡಿಸುವರು.",
			"17": "<sup>17</sup> ಇಗೋ, ಅವರಿಗೆ ವಿರುದ್ಧವಾಗಿ ಮೇದ್ಯರನ್ನು ಎಬ್ಬಿಸುವೆನು; ಇವರು ಬೆಳ್ಳಿಯನ್ನು ಲಕ್ಷಿಸರು, ಬಂಗಾರವನ್ನು ಪ್ರೀತಿಸರು.",
			"18": "<sup>18</sup> ಇವರ ಬಿಲ್ಲುಗಳು ಯುವಕರನ್ನು ಚೂರುಚೂರು ಮಾಡುವವು; ಇವರು ಗರ್ಭಫಲವನ್ನು ಕನಿಕರಿಸರು, ಇವರ ಕಣ್ಣು ಮಕ್ಕಳನ್ನೂ ಕರುಣಿಸದು.",
			"19": "<sup>19</sup> ರಾಜ್ಯಗಳಿಗೆ ಶಿರೋರತ್ನವೂ ಕಸ್ದೀಯರ ಮಹಿಮೆಗೆ ಭೂಷಣವೂ ಆದ ಬಾಬೆಲಿಗೆ ದೇವರು ಕೆಡವಿದ ಸೊದೋಮ್ ಗೊಮೋರಗಳ ಗತಿಯು ಸಂಭವಿಸುವದು.",
			"20": "<sup>20</sup> ಅದು ಎಂದಿಗೂ ನಿವಾಸಸ್ಥಳವಾಗದು, ತಲತಲಾಂತರಕ್ಕೂ ಅಲ್ಲಿ ಜನರು ಒಕ್ಕಲಿರರು; ಯಾವ ಅರಬಿಯನೂ ಗುಡಾರಹಾಕನು; ಕುರುಬರು ಮಂದೆಗಳನ್ನು ತಂಗಿಸರು.",
			"21": "<sup>21</sup> ಅದು ಕಾಡುಮೃಗಗಳಿಗೆ ಹಕ್ಕೆಯಾಗುವದು, ಅಲ್ಲಿನ ಮನೆಗಳಲ್ಲಿ ಗೂಬೆಗಳು ತುಂಬಿಕೊಳ್ಳುವವು, ಅಲ್ಲಿ ಉಷ್ಟ್ರಪಕ್ಷಿಗಳು ವಾಸಿಸುವವು, ದೆವ್ವಗಳು ಕುಣಿದಾಡುವವು.",
			"22": "<sup>22</sup> ಅರಮನೆಗಳಲ್ಲಿ ತೋಳಗಳೂ, ವಿಲಾಸಮಂದಿರಗಳಲ್ಲಿ ನರಿಗಳೂ ಎದುರುಬದುರಾಗಿ ಕೂಗುವವು; ಅದಕ್ಕೆ ಹೊತ್ತು ಸಮೀಪಿಸಿದೆ, ಇನ್ನು ದಿನ ಹೆಚ್ಚದು."
		},
		"14": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಯಾಕೋಬ್ಯರನ್ನು ಕನಿಕರಿಸಿ ಮತ್ತೆ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಆರಿಸಿಕೊಂಡು ಸ್ವದೇಶದಲ್ಲಿ ತಂಗಿಸಬೇಕೆಂದಿದ್ದಾನಷ್ಟೆ; ಪರದೇಶಿಗಳು ಅವರೊಂದಿಗೆ ಕೂಡಿ ಬಂದು ಯಾಕೋಬನ ಮನೆತನಕ್ಕೆ ಸೇರಿಕೊಳ್ಳುವರು.",
			"2": "<sup>2</sup> ಜನಾಂಗದವರು ಅವರನ್ನು ಕರತಂದು ಸ್ವಸ್ಥಳಕ್ಕೆ ಸೇರಿಸುವರು; ಆಗ ಇಸ್ರಾಯೇಲಿನ ಮನೆತನದವರು ಯೆಹೋವನ ದೇಶದಲ್ಲಿ ಆ ಜನಾಂಗದವರನ್ನು ಗಂಡುಹೆಣ್ಣಾಳುಗಳನ್ನಾಗಿ ಇಟ್ಟುಕೊಳ್ಳುವರು; ಯಾರಿಗೆ ಸೆರೆಯಾಗಿದ್ದರೋ ಅವರನ್ನು ಸೆರೆಹಿಡಿಯುವರು; ತಮ್ಮನ್ನು ಹಿಂಸಿಸಿದವರ ಮೇಲೆ ಅಧಿಕಾರ ನಡಿಸುವರು.",
			"3": "<sup>3</sup> ಮತ್ತು ಯೆಹೋವನು ನಿಮ್ಮ ಸಂಕಟದಿಂದಲೂ ಕಳವಳದಿಂದಲೂ ಕಠಿನವಾದ ಬಿಟ್ಟಿಯ ಸೇವೆಯಿಂದಲೂ",
			"4": "<sup>4</sup> ನಿಮ್ಮನ್ನು ವಿಶ್ರಾಂತಿಗೊಳಿಸುವ ದಿನದಲ್ಲಿ ಬಾಬೆಲಿನ ರಾಜನಿಗೆ ವಿರುದ್ಧವಾದ ಈ ಪದ್ಯವನ್ನು ನೀವು ಹೀಗೆ ಎತ್ತಿ ಹೇಳಬೇಕು - ಆಹಾ, ಹಿಂಸಕನು ಕೊನೆಗೊಂಡನು, ರೇಗಾಟವು ನಿಂತಿತು!",
			"5": "<sup>5</sup> ಜನಗಳನ್ನು ಕೋಪೋದ್ರೇಕದಿಂದ ಎಡೆಬಿಡದೆ ಹೊಡೆದು ಜನಾಂಗಗಳನ್ನು ಸಿಟ್ಟಿನಿಂದ ತಡೆಯಿಲ್ಲದೆ ಹಿಂಸಿಸಿ",
			"6": "<sup>6</sup> ಆಳುತ್ತಿದ್ದ ದುಷ್ಟರ ಕೋಲನ್ನೂ ರಾಜರ ದಂಡವನ್ನೂ ಯೆಹೋವನು ಮುರಿದುಬಿಟ್ಟನು.",
			"7": "<sup>7</sup> ಭೂಲೋಕವೆಲ್ಲಾ ಶಾಂತವಾಗಿ ವಿಶ್ರಾಂತಿಗೊಂಡಿದೆ, ಹರ್ಷಧ್ವನಿಗೈಯುತ್ತಾರೆ.",
			"8": "<sup>8</sup> ಹೌದು, ತುರಾಯಿಮರಗಳೂ ಲೆಬನೋನಿನ ದೇವದಾರು ವೃಕ್ಷಗಳೂ ನೀನು ಬಿದ್ದುಕೊಂಡ ಮೇಲೆ ಕಡಿಯುವವನು ಯಾವನೂ ನಮ್ಮ ತಂಟೆಗೆ ಬಂದಿಲ್ಲವೆಂದು ನಿನ್ನ ವಿಷಯವಾಗಿ ಉಲ್ಲಾಸಗೊಳ್ಳುತ್ತವೆ.",
			"9": "<sup>9</sup> ಬರುತ್ತಿರುವ ನಿನ್ನನ್ನು ಎದುರುಗೊಳ್ಳುವದಕ್ಕೆ ಕೆಳಗಡೆ ಪಾತಾಳವು ತಳಮಳಪಡುತ್ತದೆ; ನಿನಗೋಸ್ಕರ ಪ್ರೇತಗಳನ್ನು, ಅಂದರೆ ಭೂಲೋಕದಲ್ಲಿ ಮುಖಂಡರಾಗಿದ್ದವರೆಲ್ಲರನ್ನು ಎಚ್ಚರಗೊಳಿಸಿ ಜನಾಂಗಗಳ ಸಕಲ ರಾಜರನ್ನು ಅವರವರ ಆಸನಗಳಿಂದ ಎಬ್ಬಿಸುತ್ತದೆ.",
			"10": "<sup>10</sup> ಇವರೆಲ್ಲಾ ನಿನ್ನನ್ನು ಕುರಿತು - ನೀನು ಸಹ ನಮ್ಮ ಹಾಗೆ ಬಲಹೀನನಾದೆಯಾ? ನಮಗೆ ಸರಿಸಮಾನನಾದೆಯೋ? ಎಂದು ಹೇಳುತ್ತಾರೆ.",
			"11": "<sup>11</sup> ನಿನ್ನ ವೈಭವವೂ ವೀಣಾನಾದವೂ ಪಾತಾಳಕ್ಕೆ ತಳ್ಳಲ್ಪಟ್ಟಿವೆ; ನಿನಗೆ ಹುಳಗಳೇ ಹಾಸಿಗೆ, ಕ್ರಿಮಿಗಳೇ ಹೊದಿಕೆ.",
			"12": "<sup>12</sup> ಆಹಾ, ಉದಯನಕ್ಷತ್ರವೇ, ಬೆಳ್ಳಿಯೇ, ಆಕಾಶದಿಂದ ಹೇಗೆ ಬಿದ್ದೆ! ಜನಾಂಗಗಳನ್ನು ಕೆಡವಿದ ನೀನು ಕಡಿಯಲ್ಪಟ್ಟು ನೆಲಕ್ಕೆ ಉರುಳಿದೆಯಲ್ಲಾ!",
			"13": "<sup>13</sup> ನೀನು ನಿನ್ನ ಮನಸ್ಸಿನಲ್ಲಿ - ನಾನು ಆಕಾಶಕ್ಕೆ ಹತ್ತಿ ಉತ್ತರದಿಕ್ಕಿನ ಕಟ್ಟಕಡೆಯಿರುವ ಸುರಗಣ ಪರ್ವತದಲ್ಲಿ ನನ್ನ ಸಿಂಹಾಸನವನ್ನು ದೇವರ ನಕ್ಷತ್ರಗಳಿಗಿಂತ ಮೇಲೆ ಏರಿಸಿ ಆಸೀನನಾಗುವೆನು;",
			"14": "<sup>14</sup> ಉನ್ನತ ಮೇಘಮಂಡಲದ ಮೇಲೆ ಏರಿ ಉನ್ನತೋನ್ನತನಿಗೆ ಸರಿಸಮಾನನಾಗುವೆನು ಅಂದುಕೊಂಡಿದ್ದೆಯಲ್ಲಾ!",
			"15": "<sup>15</sup> ಆದರೆ ನೀನು ಪಾತಾಳಕ್ಕೆ, ಅಧೋಲೋಕದ ಅಗಾಧಸ್ಥಳಗಳಿಗೆ, ತಳ್ಳಲ್ಪಡುತ್ತಿದ್ದೀ.",
			"16": "<sup>16</sup> ನಿನ್ನನ್ನು ಕಂಡವರು ದಿಟ್ಟಿಸಿ ನೋಡಿ ಯೋಚಿಸುತ್ತಾ - ಭೂಮಿಯನ್ನು ನಡುಗಿಸಿ ರಾಜ್ಯಗಳನ್ನು ಕದಲಿಸಿ",
			"17": "<sup>17</sup> ಪಟ್ಟಣಗಳನ್ನು ಕೆಡವಿ ಲೋಕವನ್ನು ಕಾಡನ್ನಾಗಿ ಮಾಡಿ ಸೆರೆಹಿಡಿದವರನ್ನು ಮನೆಗೆ ಬಿಡದೆ ಇದ್ದವನು ಇವನೋ? ಅಂದುಕೊಳ್ಳುವರು.",
			"18": "<sup>18</sup> ಜನಾಂಗಗಳ ಎಲ್ಲಾ ಅರಸರೂ, ಹೌದು, ಸಕಲ ರಾಜರೂ ತಮ್ಮ ತಮ್ಮ ಮನೆಗಳಲ್ಲಿ ವೈಭವದೊಡನೆ ದೀರ್ಘನಿದ್ರೆಮಾಡುತ್ತಾರೆ.",
			"19": "<sup>19</sup> ನೀನಾದರೋ ನಿನ್ನ ಗೋರಿಗೆ ದೂರವಾಗಿ ಕೆಟ್ಟ ಮೊಳಿಕೆಯಂತೆ ಬಿಸಾಡಲ್ಪಟ್ಟಿದ್ದೀ; ನಿನ್ನ ಶವವು ತುಳಿತಕ್ಕೆ ಈಡಾಗಿದೆ, ಕತ್ತಿ ತಿವಿದು ಗುಂಡಿಯ ಕಲ್ಲುಗಳ ಪಾಲಾದ ಹತರೇ ಅದಕ್ಕೆ ಹೊದಿಕೆ.",
			"20": "<sup>20</sup> ನೀನು ಆ ರಾಜರಂತೆ ಗೋರಿಗೆ ಸೇರುವದಿಲ್ಲ; ನಿನ್ನ ಪ್ರಜೆಯನ್ನು ಕೊಂದು ನಿನ್ನ ದೇಶವನ್ನು ಹಾಳುಮಾಡಿದ್ದೀ, ದುಷ್ಟರ ಸಂತಾನವು ನಿರಂತರವೂ ನಿರ್ನಾಮವಾಗಿರುವದಲ್ಲವೆ.",
			"21": "<sup>21</sup> ಪಿತೃಗಳ ಅಧರ್ಮದ ನಿಮಿತ್ತ ಇವನ ಮಕ್ಕಳಿಗೂ ವಧ್ಯಸ್ಥಾನವನ್ನು ಸಿದ್ಧಮಾಡಿರಿ! ಅವರು ತಲೆಯೆತ್ತಿ ಲೋಕವನ್ನು ವಶಪಡಿಸಿಕೊಂಡು ಭೂಮಂಡಲದ ಮೇಲೆಲ್ಲಾ ಪಟ್ಟಣಗಳನ್ನು ಕಟ್ಟಿಕೊಳ್ಳದಿರಲಿ!",
			"22": "<sup>22</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನಿಂದ ಒಂದು ಮಾತು ಹೊರಟಿತು - ನಾನು ಅವರಿಗೆ ವಿರುದ್ಧವಾಗಿ ಎದ್ದು ಬಾಬೆಲಿನಿಂದ ಹೆಸರನ್ನೂ ಜನಶೇಷವನ್ನೂ ಪುತ್ರ ಪೌತ್ರರನ್ನೂ ಕತ್ತರಿಸಿಬಿಡುವೆನು;",
			"23": "<sup>23</sup> ಮತ್ತು ಆ ಪ್ರದೇಶವನ್ನು ಜವುಗುನೆಲವನ್ನಾಗಿಯೂ ಮುಳ್ಳುಹಂದಿಗೆ ಸೊತ್ತನ್ನಾಗಿಯೂ ಮಾಡಿ ನಾಶನವೆಂಬ ಬರಲಿನಿಂದ ಗುಡಿಸಿಬಿಡುವೆನು ಎಂದು ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಹೇಳಿದ್ದಾನೆ.",
			"24": "<sup>24</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಆಣೆಯಿಟ್ಟು ಹೇಳುವದೇನಂದರೆ - ನಾನು ಸಂಕಲ್ಪಿಸಿದ್ದೇ ನೆರವೇರುವದು, ಉದ್ದೇಶಿಸಿದ್ದೇ ನಿಲ್ಲುವದು, ಖಂಡಿತ.",
			"25": "<sup>25</sup> ಅಶ್ಶೂರ್ಯರನ್ನು ನನ್ನ ದೇಶದಲ್ಲಿ ಮುರಿದು ನನ್ನ ಬೆಟ್ಟಗಳ ಮೇಲೆ ತುಳಿದುಬಿಡುವೆನು; ಆಗ ಅವರು ಹೂಡಿದ ನೊಗವು ನನ್ನ ಜನರಿಂದ ತೊಲಗಿ ಹೊರಿಸಿದ ಹೊರೆಯು ನನ್ನ ಜನರ ಹೆಗಲಿಗೆ ದೂರವಾಗುವದು.",
			"26": "<sup>26</sup> ಇದೇ ಲೋಕದಲ್ಲೆಲ್ಲಾ ನೆರವೇರಬೇಕಾದ ನನ್ನ ಉದ್ದೇಶ, ಇದೇ ಜನಾಂಗಗಳ ಮೇಲೆಲ್ಲಾ ಚಾಚಿದ ನನ್ನ ಕೈ.",
			"27": "<sup>27</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಉದ್ದೇಶಮಾಡಿದ್ದಾನೆ, ಅದನ್ನು ಯಾರು ವ್ಯರ್ಥಪಡಿಸುವರು? ಆತನ ಕೈ ಚಾಚಿದೆ, ಹಿಂದಕ್ಕೆ ತಳ್ಳುವವರು ಯಾರು? ಎಂಬದೇ.",
			"28": "<sup>28</sup> ಅರಸನಾದ ಆಹಾಜನು ಕಾಲವಾದ ವರುಷದಲ್ಲಿ ಈ ದೈವೋಕ್ತಿಯು ಕೇಳ ಬಂತು -",
			"29": "<sup>29</sup> ಎಲೈ, ಎಲ್ಲಾ ಫಿಲಿಷ್ಟಿಯರೇ, ನಿಮ್ಮನ್ನು ಹೊಡೆದ ಕೋಲು ಮುರಿದುಹೋಯಿತೆಂದು ಉಲ್ಲಾಸಗೊಳ್ಳದಿರಿ; ಹಾವಿನ ಬೀಜದಿಂದ ಮಹಾ ನಾಗವುಂಟಾಗುವದು, ಆ ಬೀಜದ ಸಂತತಿಯು ಹಾರುವ ಅಗ್ನಿಮಯಸರ್ಪವೇ.",
			"30": "<sup>30</sup> [ನನ್ನ ಜನರಲ್ಲಿ] ಬಡತನದ ಹಿರಿಯ ಮಕ್ಕಳಿಗೂ ಪೋಷಣೆಯಾಗುವದು, ದಿಕ್ಕಿಲ್ಲದವರೂ ನಿರ್ಭಯವಾಗಿ ಮಲಗಿಕೊಳ್ಳುವರು; ನಿಮ್ಮ ಸಂತಾನವನ್ನೋ ಕ್ಷಾಮದಿಂದ ಸಾಯಿಸುವೆನು, ನಿಮ್ಮಲ್ಲಿ ಉಳಿದವರನ್ನು [ಆ ಸರ್ಪವೇ] ಕೊಲ್ಲುವದು.",
			"31": "<sup>31</sup> ಪುರದ್ವಾರವೇ, ಗೋಳಾಡು! ಪಟ್ಟಣವೇ, ಕೂಗು! ಎಲ್ಲಾ ಫಿಲಿಷ್ಟಿಯರೇ, ಕುಂದಿಹೋಗಿರಿ! ಬಡಗಲಿಂದ ಹೊಗೆಯು ಬರುತ್ತಿದೆ. ಆ ವ್ಯೂಹದಲ್ಲಿ ಹಿಂದು ಬೀಳುವವರಿಲ್ಲ.",
			"32": "<sup>32</sup> ಹೀಗಿರಲು ಪರಜನಾಂಗದ ರಾಯಭಾರಿಗಳಿಗೆ ಏನುತ್ತರ ಕೊಡಬೇಕಂದರೆ ಯೆಹೋವನು ಚೀಯೋನನ್ನು ಸ್ಥಾಪಿಸಿದ್ದಾನೆ, ಆತನ ಜನರಲ್ಲಿನ ದೀನದರಿದ್ರರು ಅದನ್ನೇ ಆಶ್ರಯಿಸಿಕೊಳ್ಳುವರು ಎಂಬದೇ."
		},
		"15": {
			"1": "<sup>1</sup> ಮೋವಾಬಿನ ವಿಷಯವಾದ ದೈವೋಕ್ತಿ. ಒಂದು ರಾತ್ರಿಯಲ್ಲಿ ಮೋವಾಬಿನ ಆರ್ ಪಟ್ಟಣವು ಹಾಳಾಗಿ ನಿರ್ಮೂಲವಾಯಿತಲ್ಲಾ! ಹೌದು, ಒಂದು ರಾತ್ರಿಯಲ್ಲಿ ಮೋವಾಬಿನ ಕೀರ್ ಪಟ್ಟಣವೂ ಹಾಳಾಗಿ ನಿರ್ಮೂಲವಾಯಿತು.",
			"2": "<sup>2</sup> ಬಯಿತ್ ಮತ್ತು ದೀಬೋನ್ ಊರುಗಳವರು ಅಳುವದಕ್ಕಾಗಿ ಪೂಜಾಸ್ಥಾನಗಳಿಗೆ ಹತ್ತಿದ್ದಾರೆ; ನೆಬೋವಿನಲ್ಲಿಯೂ ಮೇದೆಬದಲ್ಲಿಯೂ ಮೋವಾಬ್ಯರು ಗೋಳಾಡುತ್ತಾರೆ; ಎಲ್ಲರ ತಲೆಯು ಬೋಳು, ಎಲ್ಲರ ಗಡ್ಡವು ವಿಕಾರವಾಗಿ ಕತ್ತರಿಸಿದೆ.",
			"3": "<sup>3</sup> ಬೀದಿಗಳಲ್ಲಿ ತಿರುಗುವವರು ನಡುವಿಗೆ ಗೋಣಿತಟ್ಟು ಸುತ್ತಿಕೊಂಡಿದ್ದಾರೆ; ತಮ್ಮ ತಮ್ಮ ಮಾಳಿಗೆಗಳ ಮೇಲೆಯೂ ಚೌಕಗಳಲ್ಲಿಯೂ ಎಲ್ಲರು ಅರಚುತ್ತಾ ಕಣ್ಣೀರನ್ನು ಸುರಿಸಿ ಸುರಿಸಿ ಇಳಿದು ಹೋಗಿದ್ದಾರೆ.",
			"4": "<sup>4</sup> ಹೆಷ್ಬೋನ್ ಮತ್ತು ಎಲೆಯಾಲೆ ಕೂಗುತ್ತವೆ; ಇವುಗಳ ರೋದನವು ಯಹಜಿನವರೆಗೂ ಕೇಳಬರುತ್ತದೆ; ಇದರಿಂದ ಮೋವಾಬಿನ ಭಟರು ಕಿರಚಿಕೊಳ್ಳುತ್ತಾರೆ; ಅದರ ಆತ್ಮವು ತನ್ನೊಳಗೆ ತತ್ತರಿಸುತ್ತದೆ.",
			"5": "<sup>5</sup> ನನ್ನ ಹೃದಯವು ಮೋವಾಬಿನ ನಿಮಿತ್ತವಾಗಿ ಮೊರೆಯಿಡುತ್ತದೆ; ಅಲ್ಲಿಂದ ಪಲಾಯನವಾದವರು ಚೋಯರಿಗೂ ಎಗ್ಲತ್ ಶೆಲಿಶೀಯಕ್ಕೂ ಓಡಿಹೋಗುತ್ತಾರೆ, ಅಳುತ್ತಾ ಲೂಹೀಥ್ ದಿಣ್ಣೆಯನ್ನು ಹತ್ತುತ್ತಾರೆ, ಹೊರೊನಯಿಮಿನ ದಾರಿಯಲ್ಲಿ ನಡೆಯುತ್ತಾ ನಾಶನವಾದೆವಲ್ಲಾ ಎಂದು ದನಿಗೈಯುತ್ತಾರೆ.",
			"6": "<sup>6</sup> ನಿಮ್ರೀಮ್ ಹೊಳೆಯು ಹಾಳಾಯಿತು, ಹಸಿಹುಲ್ಲು ಬಾಡಿತು, ಮೇವು ತೀರಿತು, ಯಾವ ಹಸುರೂ ಇಲ್ಲ.",
			"7": "<sup>7</sup> ಆದಕಾರಣ ತಾವು ಸಂಪಾದಿಸಿದ ಆಸ್ತಿಯನ್ನೂ ಕೂಡಿಸಿಟ್ಟ ಸೊತ್ತನ್ನೂ ನೀರವಂಜಿಯ ಹೊಳೆಯ ಆಚೆಗೆ ಹೊತ್ತುಕೊಂಡು ಹೋಗುತ್ತಾರೆ.",
			"8": "<sup>8</sup> ಪ್ರಲಾಪವು ಮೋವಾಬಿನ ಎಲ್ಲೆಗಳ ತನಕ ಹಬ್ಬಿದೆ, ಅದರ ಅರಚಾಟವು ಎಗ್ಲಯಿಮಿನವರೆಗೂ ಬೆಯೇರ್ ಏಲೀಮಿನ ಪರ್ಯಂತವೂ ವ್ಯಾಪಿಸಿದೆ.",
			"9": "<sup>9</sup> ದೀಮೋನಿನ ನೀರೆಲ್ಲಾ ರಕ್ತಮಯವಾಯಿತು; ಮತ್ತು ದೀಮೋನಿನ ಮೇಲೆ ಹೆಚ್ಚು ಬಾಧೆಯನ್ನು, ಅಂದರೆ ಮೋವಾಬ್ಯರಲ್ಲಿ ತಪ್ಪಿಸಿಕೊಂಡವರ ಮೇಲೆಯೂ ದೇಶದಲ್ಲಿ ಉಳಿದವರ ಮೇಲೆಯೂ ಸಿಂಹವನ್ನು, ಬರಮಾಡುವೆನು."
		},
		"16": {
			"1": "<sup>1</sup> ದೇಶವನ್ನಾಳುವವನಿಗೆ ಕಪ್ಪವಾಗಿ ಕೊಡತಕ್ಕ ಕುರಿಗಳನ್ನು ಅರಣ್ಯದ ಕಡೆಯಿರುವ ಸೆಲದಿಂದ ಚೀಯೋನ್ ನಗರಿಯ ಪರ್ವತಕ್ಕೆ ಕಳುಹಿಸಿರಿ.",
			"2": "<sup>2</sup> ಅರ್ನೋನ್ ನದಿಯ ಹಾಯ್ಗಡಗಳಲ್ಲಿ ಮೋವಾಬಿನ ಊರುಗಳವರು ಅಲೆಯುವ ಪಕ್ಷಿಗಳಂತೆಯೂ ಗೂಡಿನಿಂದ ಚದರಿದ ಮರಿಗಳ ಹಾಗೂ ಆಗುವರು.",
			"3": "<sup>3</sup> ಆಲೋಚನೆಹೇಳಿರಿ, ತೀರ್ಮಾನಿಸಿರಿ; ಮಟ್ಟ ಮಧ್ಯಾಹ್ನದಲ್ಲಿ ನಿಮ್ಮ ನೆರಳು ರಾತ್ರಿಯ ಕತ್ತಲಿನಂತೆ ದಟ್ಟವಾಗಿರಲಿ; ದೇಶ ಭ್ರಷ್ಟರನ್ನು ಮರೆಮಾಜಿರಿ, ಅಲೆಯುವವರನ್ನು ಬೈಲಿಗೆ ತರಬೇಡಿರಿ.",
			"4": "<sup>4</sup> ನಮ್ಮ ದೇಶದಿಂದ ವಲಸೆಹೋದವರು ನಿಮ್ಮಲ್ಲಿ ವಾಸಿಸಲಿ; ಮೋವಾಬ್ಯರು ಬಲಾತ್ಕಾರಿಗಳಿಗೆ ವಶವಾಗದ ಹಾಗೆ ಆಶ್ರಯವಾಗಿರಿ. ಹಿಂಡಿ ಹಿಂಸಿಸುವವರು [ನಿಮ್ಮ] ದೇಶದಿಂದ ನಿರ್ಮೂಲರಾದರು, ಬಲಾತ್ಕಾರವು ತೀರಿತು, ತುಳಿದುಬಿಡುವವರು ಮುಗಿದುಹೋದರಷ್ಟೆ.",
			"5": "<sup>5</sup> ಇದಲ್ಲದೆ ಸಿಂಹಾಸನವು ಕೃಪಾಧಾರದ ಮೇಲೆ ಸ್ಥಾಪಿತವಾಗಿದೆ; ರಾಜ್ಯಭಾರಪ್ರವೀಣನೂ ಧರ್ಮಾಸಕ್ತನೂ ನ್ಯಾಯನಿಪುಣನೂ ಆದವನು ದಾವೀದನ ಗುಡಾರದಲ್ಲಿನ ಆ ಸಿಂಹಾಸನದ ಮೇಲೆ ಸತ್ಯಪರನಾಗಿ ಕುಳಿತಿದ್ದಾನೆ.",
			"6": "<sup>6</sup> ಮೋವಾಬ್ಯರಿಗೆ ಬಹು ಸೊಕ್ಕೇರಿದೆ ಎಂಬ ಅವರ ಹೆಮ್ಮೆಯ ಸುದ್ದಿಯೂ ಅವರ ಅಹಂಕಾರದಂಭ ಗರ್ವೋದ್ರೇಕ ಇವುಗಳ ಸಮಾಚಾರವೂ ನಮ್ಮ ಕಿವಿಗೆ ಬಿದ್ದಿವೆ. ಅವರು ಕೊಚ್ಚಿಕೊಳ್ಳುವದೆಲ್ಲಾ ಬರೀ ಬುರುಡೆಯೇ.",
			"7": "<sup>7</sup> ಆದಕಾರಣ ಮೋವಾಬಿನ ನಿಮಿತ್ತ ಮೋವಾಬೇ ಗೋಳಾಡುವದು; ಪ್ರತಿಯೊಬ್ಬನೂ ಪ್ರಲಾಪಿಸುವನು; ಮುರಿದು ಹೋದವರಾಗಿ ನೀವು ಕೀರ್ ಹರೆಷೆಥಿನ ದೀಪದ್ರಾಕ್ಷೆಯು ಹಾಳಾಯಿತಲ್ಲಾ ಎಂದು ನರಳುವಿರಿ.",
			"8": "<sup>8</sup> ಹೆಷ್ಬೋನಿನ ಭೂಮಿಯೂ ಸಿಬ್ಮದ ದ್ರಾಕ್ಷಾಲತೆಯೂ ನಿಸ್ಸಾರವಾಗಿವೆ; ಯಜ್ಜೇರಿನವರೆಗೆ ವ್ಯಾಪಿಸಿ ಅರಣ್ಯದಲ್ಲಿ ಹಬ್ಬಿ ಸಮುದ್ರದಾಚೆಗೆ ಸೇರುವಷ್ಟು ವಿಶಾಲವಾಗಿ ತನ್ನ ಶಾಖೆಗಳನ್ನು ಹರಡಿಕೊಂಡಿದ್ದ ಮೋವಾಬಿನ ರಾಜ ದ್ರಾಕ್ಷೆಗಳನ್ನು ಜನಾಂಗಗಳ ಒಡೆಯರು ಮುರಿದು ಬಿಟ್ಟಿದ್ದಾರೆ.",
			"9": "<sup>9</sup> ಹೀಗಿರಲು ಸಿಬ್ಮದ ದ್ರಾಕ್ಷಾಲತೆಯ ನಿಮಿತ್ತ ಯಜ್ಜೇರಿನವರೊಂದಿಗೆ ನಾನು ಅಳುವೆನು. ಹೆಷ್ಬೋನೇ, ಎಲ್ಲಾಲೇ, ನನ್ನ ಕಣ್ಣೀರಿನಿಂದ ನಿಮ್ಮನ್ನು ತೋಯಿಸುವೆನು; ನಿಮ್ಮ ಹಣ್ಣಿನ ಮೇಲೆಯೂ ಬೆಳೆಯ ಮೇಲೆಯೂ ಬಿದ್ದು ಆರ್ಭಟಿಸುತ್ತಾರೆ;",
			"10": "<sup>10</sup> ಹರ್ಷಾನಂದಗಳು ತೋಟಗಳಿಂದ ತೊಲಗಿವೆ, ದ್ರಾಕ್ಷೆಯ ತೋಟಗಳಲ್ಲಿ ಉತ್ಸಾಹಧ್ವನಿಯೂ ಉಲ್ಲಾಸದ ಆರ್ಭಟವೂ ಇರುವದಿಲ್ಲ, ತುಳಿಯುವವರು ತೊಟ್ಟಿಗಳಲ್ಲಿ ದ್ರಾಕ್ಷಾರಸವನ್ನು ತುಳಿದು ತೆಗೆಯುವದಿಲ್ಲ, ಅವರ ಕೂಗನ್ನು ನಿಲ್ಲಿಸಿಬಿಟ್ಟಿದ್ದೇನೆ.",
			"11": "<sup>11</sup> ಆದದರಿಂದ ಮೋವಾಬಿನ ನಿಮಿತ್ತ ನನ್ನ ಹೃದಯವು, ಕೀರ್‍ಹೆರೆಸಿನ ನಿಮಿತ್ತ ನನ್ನ ಅಂತರಂಗವು, ಕಿನ್ನರಿಯಂತೆ ಅಲುಗಿ ನುಡಿಯುತ್ತದೆ.",
			"12": "<sup>12</sup> ಮೋವಾಬ್ಯರು ಸನ್ನಿಧಿಗೆ ಸೇರಿ ದಿಣ್ಣೆಯಲ್ಲಿನ ಪೂಜೆಯಿಂದ ಬಹು ಆಯಾಸಗೊಂಡು ಪ್ರಾರ್ಥನೆಗಾಗಿ ತಮ್ಮ ದೇವಾಲಯಕ್ಕೆ ಬಂದರೂ ಪ್ರಾರ್ಥನೆಯು ಸಫಲವಾಗದು.",
			"13": "<sup>13</sup> ಯೆಹೋವನು ಮೋವಾಬಿನ ವಿಷಯವಾಗಿ ಹಿಂದೆ ನುಡಿದ ಉಕ್ತಿಯು ಇದೇ.",
			"14": "<sup>14</sup> ಈಗ ಯೆಹೋವನು ಹೇಳುವದೇನಂದರೆ - ಆಳಿನ ಒಂದೊಂದು ವರುಷ ವಾಯಿದೆಗೆ ಸರಿಯಾದ ಮೂರು ವರುಷಕ್ಕೇ ಮೋವಾಬಿನ ಮಹಿಮೆಯೂ ಅಲ್ಲಿನವರ ದೊಡ್ಡ ಗುಂಪೂ ಹೀನಾಯಕ್ಕೆ ಈಡಾಗುವವು; ಉಳಿದ ಜನವು ಹೆಚ್ಚದೆ ಕೇವಲ ಸ್ವಲ್ಪವಾಗಿಯೇ ಇರುವದು ಎಂಬದೇ."
		},
		"17": {
			"1": "<sup>1</sup> ದಮಸ್ಕದ ವಿಷಯವಾದ ದೈವೋಕ್ತಿ. ಆಹಾ ದಮಸ್ಕವು ಇನ್ನು ಊರಾಗಿರದೆ ಹಾಳು ದಿಬ್ಬವಾಗುವದು.",
			"2": "<sup>2</sup> ಆರೋಯೇರಿನ ಪಟ್ಟಣಗಳು ನಿರ್ಜನವಾಗಿ ಮಂದೆಗಳಿಗೆ ಆಸರೆಯಾಗುವವು; ಅವು ಯಾರ ಹೆದರಿಕೆಯೂ ಇಲ್ಲದೆ ಅಲ್ಲಿ ತಂಗುವವು.",
			"3": "<sup>3</sup> ಎಫ್ರಾಯೀಮಿಗೆ ರಕ್ಷಣೆಯಾಗಿರುವ ಕೋಟೆಯೂ ದಮಸ್ಕವು ನಡಿಸುವ ರಾಜ್ಯಾಧಿಕಾರವೂ ಇಲ್ಲವಾಗುವವು; ಅರಾಮ್ಯರಲ್ಲಿ ಉಳಿದವರಿಗೆ ಇಸ್ರಾಯೇಲ್ಯರ ಮಹಿಮೆಯ ಗತಿಯಾಗುವದು ಎಂಬದು ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನ ನುಡಿ.",
			"4": "<sup>4</sup> ಆ ದಿನದಲ್ಲಿ ಯಾಕೋಬಿನ ಮಾಂಸದ ಕೊಬ್ಬು ಕರಗಿ ಅದರ ಮಹಿಮೆಯು ಕ್ಷೀಣವಾಗುವದು.",
			"5": "<sup>5</sup> ಹೊಲಕೊಯ್ಯುವವನು ದಂಟುಗಳನ್ನು ಒಟ್ಟುಗೂಡಿಸಿ ಕೈಯಿಂದ ತೆನೆಗಳನ್ನು ಕತ್ತರಿಸುವ ಹಾಗಿರುವದು; ಹೌದು, ರೆಫಾಯೀಮ್ ತಗ್ಗಿನಲ್ಲಿ ಹಕ್ಕಲನ್ನು ಆರಿಸುವಂತಿರುವದು.",
			"6": "<sup>6</sup> ಆದರೂ ಎಣ್ಣೆಯ ಮರವನ್ನು ಬಡಿದ ಬಳಿಕ ಮೇಲಿನ ಕೊಂಬೆಯ ಕೊಟ್ಟಕೊನೆಯಲ್ಲಿ ಎರಡು ಮೂರು ಕಾಯಿಗಳು, ಫಲವತ್ತಾದ ಆ ಮರದ ರೆಂಬೆಗಳಲ್ಲೆಲ್ಲಾ ನಾಲ್ಕೈದು ಕಾಯಿಗಳು, ನಿಂತಿರುವಂತೆ ಹಕ್ಕಲು ಅದರಲ್ಲಿ ಉಳಿದಿರುವದು ಎಂದು ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರಾದ ಯೆಹೋವನು ನುಡಿಯುತ್ತಾನೆ.",
			"7": "<sup>7</sup> ಆ ದಿನದಲ್ಲಿ ಜನರು ತಮ್ಮ ಕೈಯಿಂದ ಮಾಡಿದ ಬಲಿಪೀಠಗಳನ್ನು ಲಕ್ಷಿಸದೆಯೂ ತಮ್ಮ ಬೆರಳುಗಳಿಂದ ನಿರ್ಮಿಸಿದ ಅಶೇರವೆಂಬ ವಿಗ್ರಹಸ್ತಂಭಗಳನ್ನಾಗಲಿ ಸೂರ್ಯಸ್ತಂಭಗಳನ್ನಾಗಲಿ ದೃಷ್ಟಿಸದೆಯೂ",
			"8": "<sup>8</sup> ತಮ್ಮ ಸೃಷ್ಟಿಕರ್ತನನ್ನೇ ಲಕ್ಷಿಸಿ ಇಸ್ರಾಯೇಲ್ಯರ ಸದಮಲಸ್ವಾಮಿಯ ಕಡೆಗೇ ಕಣ್ಣಿಡುವರು.",
			"9": "<sup>9</sup> ಆ ದಿನದಲ್ಲಿ ದೇಶದ ಬಲವಾದ ಕೋಟೆಗಳು [ಪೂರ್ವದಲ್ಲಿ] ಇಸ್ರಾಯೇಲ್ಯರ ಭಯದಿಂದ ನಿರ್ಜನವಾಗಿದ್ದ ಈಗ ಅಡವಿಯಲ್ಲಿಯೂ ಬೆಟ್ಟಗಳ ತುದಿಯ ಮೇಲೂ ಕಾಣುವ ಪಾಳು ನಿವೇಶನಗಳಂತಿರುವವು; ಅಂತು ದೇಶವೆಲ್ಲಾ ಹಾಳಾಗಿರುವದು;",
			"10": "<sup>10</sup> ಏಕಂದರೆ ನೀನು ನಿನ್ನ ರಕ್ಷಕನಾದ ದೇವರನ್ನು ಮರೆತುಬಿಟ್ಟು ನಿನ್ನ ಆಶ್ರಯಗಿರಿಯನ್ನು ಸ್ಮರಿಸಲಿಲ್ಲ; ಹೀಗಿರಲು ನೀನು ಇಷ್ಟವಾದ ಗಿಡಗಳನ್ನು ನೆಟ್ಟು ನಿನ್ನ ದೇಶದಲ್ಲಿ ದೇಶಾಂತರದ ಸಸಿಗಳನ್ನು ಹಾಕಿದ್ದಿ;",
			"11": "<sup>11</sup> ಬೀಜವನ್ನು ನೆಟ್ಟ ದಿವಸದಲ್ಲಿಯೇ ಬೇಲಿಕಟ್ಟಿ, ಮಾರಣೆಯ ಬೆಳಿಗ್ಗೆ ಮೊಳೆಯುವಂತೆ ಮಾಡಿದ್ದೀ; ಆದರೆ ಬೆಳೆಯನ್ನು ಕುಪ್ಪೆಯಾಗಿ ಕೂಡಿಸುವ ದಿನವು ವ್ಯಾಧಿಯ ಮತ್ತು ವಿಪರೀತವ್ಯಥೆಯ ದಿವಸವೇ.",
			"12": "<sup>12</sup> ಆಹಾ, ಸಮುದ್ರವು ಭೋರ್ಗರೆಯುವಂತೆ ಭೋರ್ಗರೆಯುವ ಬಹು ಜನಾಂಗಗಳ ಗದ್ದಲ! ಮಹಾ ಜಲಪ್ರವಾಹಗಳು ಘೋಷಿಸುವಂತೆ ಘೋಷಿಸುವ ಜನಗಳ ಘೋಷ!",
			"13": "<sup>13</sup> ಹೌದು, ಮಹಾ ಜಲಪ್ರವಾಹಗಳು ಘೋಷಿಸುವಂತೆ ಜನಾಂಗಗಳು ಘೋಷಿಸುತ್ತಿವೆ! ಆದರೆ ಆತನು ಅವರನ್ನು ಗದರಿಸುವಾಗ ಅವರು ದೂರ ಓಡಿಹೋಗಿ ಬೆಟ್ಟಗಳಲ್ಲಿ ಗಾಳಿಗೆ ಸಿಕ್ಕಿದ ಹೊಟ್ಟಿನಂತೆಯೂ ಸುಂಟರಗಾಳಿಯಿಂದ ಸುತ್ತಿಯಾಡುವ ದೂಳಿನಂತೆಯೂ ಅಟ್ಟಲ್ಪಡುವರು. ಇಗೋ, ಸಂಜೆಯಲ್ಲಿ ದಿಗಿಲುಬಿದ್ದು ಉದಯದೊಳಗಾಗಿ ಇಲ್ಲವಾಗುವರು!",
			"14": "<sup>14</sup> ನಮ್ಮನ್ನು ಸೂರೆಮಾಡುವವರಿಗೆ ಇದೇ ಗತಿ, ನಮ್ಮನ್ನು ಕೊಳ್ಳೆಹೊಡೆಯುವವರ ಪಾಡು ಇದೇ."
		},
		"18": {
			"1": "<sup>1</sup> ಓಹೋ, ಕೂಷಿನ ನದಿಗಳ ಆಚೆಯಲ್ಲಿರುವ ಸೀಮೆ, ರೆಕ್ಕೆಗಳು ಪಟಪಟನೆ ಆಡುವ ನಾಡು, ರಾಯಭಾರಿಗಳನ್ನು ನೀರಿನ ಮೇಲೆ ಬೆಂಡಿನ ದೋಣಿಗಳಲ್ಲಿ ನದೀಮಾರ್ಗವಾಗಿ ಕಳುಹಿಸುವ ದೇಶ, ಹೌದು.",
			"2": "<sup>2</sup> ವೇಗವುಳ್ಳ ದೂತರೇ, ಉನ್ನತವಾದ ನುಣುಪಿನ ಮೈಯವರೂ ಸರ್ವದಾ ಭಯಂಕರರೂ ಮಹಾಬಲದಿಂದ [ಶತ್ರುಗಳನ್ನು] ತುಳಿಯುವವರೂ ನದಿಗಳಿಂದ ವಿಂಗಡವಾಗಿರುವ ದೇಶದ ನಿವಾಸಿಗಳೂ ಆದ ಜನಾಂಗದವರ ಬಳಿಗೆ ಹೋಗಿರಿ.",
			"3": "<sup>3</sup> ಸಮಸ್ತ ಭೂನಿವಾಸಿಗಳೇ, ಲೋಕದ ಸಕಲ ಜನರೇ, ಬೆಟ್ಟಗಳಲ್ಲಿ ಧ್ವಜವನ್ನೆತ್ತುವಾಗ ನೋಡಿರಿ, ತುತೂರಿಯನ್ನೂದುವಾಗ ಕೇಳಿರಿ!",
			"4": "<sup>4</sup> ಯೆಹೋವನು ನನಗೆ ಹೇಳಿರುವದೇನಂದರೆ, ಬಿಸಿಲಿನಲ್ಲಿ ಜಳಜಳಿಸುವ ದಗೆಯಂತೆಯೂ ಸುಗ್ಗಿಯಲ್ಲಿನ ಮಂಜಿನ ಮೋಡದ ಹಾಗೂ ನಾನು ನನ್ನ ವಾಸಸ್ಥಾನದಲ್ಲಿ ಸುಮ್ಮನಿದ್ದು ದೃಷ್ಟಿಸುತ್ತಿರುವೆನು ಎಂಬದೇ.",
			"5": "<sup>5</sup> ಕೊಯ್ಲಿನ ಕಾಲಕ್ಕೆ ಮುಂಚೆ ಮೊಗ್ಗು ಬಿಟ್ಟಾದ ಮೇಲೆ ಹೂವಿನಿಂದಾದ ಹೀಚು ದೋರೆಗಾಯಿಯಾಗುತ್ತಿರುವಾಗ ಆತನು ಕುಡುಗೋಲುಗಳಿಂದ ಕುಡಿಗಳನ್ನು ಕತ್ತರಿಸಿ ಚಿಗುರುಗಳನ್ನು ಕಡಿದುಹಾಕುವನು.",
			"6": "<sup>6</sup> ಕಡಿಯಲ್ಪಟ್ಟವರ ಶವಗಳೆಲ್ಲಾ ಬೆಟ್ಟದ ಹದ್ದುಗಳಿಗೂ ಕಾಡುಮೃಗಗಳಿಗೂ ಪಾಲಾಗಿ ಬಿದ್ದಿರುವವು, ಬೇಸಿಗೆಯಲ್ಲಿ ಹದ್ದುಗಳಿಗೂ ಹಿಮಗಾಲದಲ್ಲಿ ಕಾಡುಮೃಗಗಳಿಗೂ ಜೀವನವಾಗುವವು.",
			"7": "<sup>7</sup> ಆ ಕಾಲದಲ್ಲಿ ಉನ್ನತರಾದ ನುಣುಪಿನ ಮೈಯವರೂ ಸರ್ವದಾ ಭಯಂಕರರೂ ಮಹಾಬಲದಿಂದ [ಶತ್ರುಗಳನ್ನು] ತುಳಿಯುವವರೂ ನದಿಗಳಿಂದ ವಿಂಗಡವಾಗಿರುವ ದೇಶದ ನಿವಾಸಿಗಳೂ ಆದ ಜನಾಂಗದವರು ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನ ನಾಮಮಹತ್ತಿರುವ ಚೀಯೋನ್ ಪರ್ವತಕ್ಕೆ ಆತನಿಗೋಸ್ಕರ ಕಾಣಿಕೆಯನ್ನು ತರುವರು."
		},
		"19": {
			"1": "<sup>1</sup> ಐಗುಪ್ತದ ವಿಷಯವಾದ ದೈವೋಕ್ತಿ. ಇಗೋ, ಯೆಹೋವನು ವೇಗಮೇಘವಾಹನನಾಗಿ ಐಗುಪ್ತಕ್ಕೆ ಬರುವನು; ಆತನು ಸಮ್ಮುಖನಾದಾಗ ಐಗುಪ್ತದ ವಿಗ್ರಹಗಳು ನಡುಗುವವು. ಐಗುಪ್ತ್ಯರ ಹೃದಯವು ತಮ್ಮೊಳಗೆ ಕರಗಿ ನೀರಾಗುವದು.",
			"2": "<sup>2</sup> ಐಗುಪ್ತ್ಯರನ್ನು ಐಗುಪ್ತ್ಯರ ಮೇಲೆಯೇ ಎಬ್ಬಿಸುವೆನು; ಅಣ್ಣತಮ್ಮಂದಿರು, ನೆರೆಹೊರೆಯವರು, ಪಟ್ಟಣ ಪಟ್ಟಣಗಳು, ರಾಷ್ಟ್ರ ರಾಷ್ಟ್ರಗಳು ಪರಸ್ಪರವಾಗಿ ಹೋರಾಡುವವು.",
			"3": "<sup>3</sup> ಐಗುಪ್ತದ ಅಂತರಾತ್ಮವು ಬರಿದಾಗುವದು; ಅದರ ಆಲೋಚನೆಯನ್ನು ಭಂಗಪಡಿಸುವೆನು; ಅಲ್ಲಿಯವರು ವಿಗ್ರಹಗಳನ್ನೂ ಮಂತ್ರಗಾರರನ್ನೂ ಪ್ರೇತವಿಚಾರಕರನ್ನೂ ಬೇತಾಳಿಕರನ್ನೂ ಆಶ್ರಯಿಸುವರು.",
			"4": "<sup>4</sup> ಇದಲ್ಲದೆ ಐಗುಪ್ತ್ಯರನ್ನು ಕ್ರೂರನಾದ ಒಡೆಯನ ಕೈಗೆ ಒಪ್ಪಿಸುವೆನು; ಭಯಂಕರನಾದ ರಾಜನು ಅವರನ್ನಾಳುವನು ಎಂದು ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ನುಡಿಯುತ್ತಾನೆ.",
			"5": "<sup>5</sup> ಮಹಾನದಿಯ ನೀರು ಬತ್ತಿಹೋಗುವದು.",
			"6": "<sup>6</sup> ಪ್ರವಾಹವು ಇಂಗಿ ಒಣಗುವದು, ಕಾಲುವೆಗಳು ನಾರುವವು, ಐಗುಪ್ತದ ಹೊಳೆಗಳು ಇಳಿದು ನೀರಿಲ್ಲದೆ ಹೋಗುವವು, ಆಪೂ ಜಂಬುಹುಲ್ಲೂ ಬಾಡುವವು.",
			"7": "<sup>7</sup> ನದಿಯ ಪಕ್ಕದ ತೀರಭೂಮಿಯು ಬೀಡು ಬೀಳುವದು; ನದಿಯಲ್ಲಿ ಬಿತ್ತಿದ ಎಲ್ಲಾ ಬೀಜವು ಒಣಗಿ ಗಾಳಿಗೆ ಸಿಕ್ಕಿ ಮಾಯವಾಗುವದು.",
			"8": "<sup>8</sup> ಬೆಸ್ತರು ಪ್ರಲಾಪಿಸುವರು, ನದಿಯಲ್ಲಿ ಗಾಳಹಾಕುವವರು ಶೋಕಪಡುವರು, ನೀರಿನ ಮೇಲೆ ಬಲೆಬೀಸುವವರು ಕುಗ್ಗುವರು, ನಯವಾದ ನಾರಿನ ಕೆಲಸದವರೂ",
			"9": "<sup>9</sup> ಬಿಳೀ ಬಟ್ಟೆಯನ್ನು ನೇಯುವವರೂ ನಾಚಿಕೊಳ್ಳುವರು.",
			"10": "<sup>10</sup> ದೇಶದ ಆಧಾರಭೂತರು ಮುರಿದುಹೋಗುವರು, ಕೂಲಿಯವರು ಮನಮರುಗುವರು.",
			"11": "<sup>11</sup> ಚೋಯನಿನ ಪ್ರಭುಗಳು ಕೇವಲ ಬುದ್ಧಿಹೀನರು; ಫರೋಹನ ಮಂತ್ರಿಗಳಲ್ಲಿ ಜ್ಞಾನವೃದ್ಧರ ಆಲೋಚನೆಯೂ ಹುಚ್ಚಾಟ; ನೀವು ಫರೋಹನಿಗೆ - ನಾನು ಜ್ಞಾನಿಗಳ ಸಂತಾನದವನು, ಪುರಾತನ ರಾಜವಂಶೀಯನು ಎಂದು ಹೇಗೆ ತಾನೆ ಹೇಳಿಕೊಳ್ಳುವಿರಿ? ನಿನ್ನ ಜ್ಞಾನಿಗಳು ಎಲ್ಲಿ?",
			"12": "<sup>12</sup> ಅವರು ನಿನಗೆ ತಿಳಿಯ ಹೇಳಲಿ, ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಐಗುಪ್ತದ ವಿಷಯವಾಗಿ ಉದ್ದೇಶಿಸಿದ್ದನ್ನು ಅರಿತುಕೊಳ್ಳಲಿ.",
			"13": "<sup>13</sup> ಚೋಯನಿನ ಪ್ರಭುಗಳು ಬುದ್ಧಿಗೆಟ್ಟಿದ್ದಾರೆ, ನೋಫಿನ ಪ್ರಧಾನರು ಮೋಸಹೋಗಿದ್ದಾರೆ, ಐಗುಪ್ತದ ಕುಲಗಳ ಪ್ರಮುಖರು ಅದನ್ನು ಭ್ರಮಗೊಳಿಸಿದ್ದಾರೆ.",
			"14": "<sup>14</sup> ಯೆಹೋವನು ಅದರ ಅಂತರಾತ್ಮದಲ್ಲಿ ವಕ್ರಬುದ್ಧಿಯನ್ನು ಕಲ್ಪಿಸಿದ್ದಾನೆ; ಅಮಲೇರಿದವನು ಕಕ್ಕುತ್ತಾ ಓಲಾಡುವ ಪ್ರಕಾರ ಐಗುಪ್ತವು ತನ್ನ ಒಂದೊಂದು ಕೆಲಸದಲ್ಲಿಯೂ ಓಲಾಡುವಂತೆ ಮಾಡಿದ್ದಾರೆ.",
			"15": "<sup>15</sup> ಮತ್ತು ಐಗುಪ್ತದಲ್ಲಿ ತಲೆಯಾಗಾಲಿ ಬಾಲವಾಗಲಿ, ತಾಳೆಯಾಗಲಿ ತೃಣವಾಗಲಿ, ಸಾಧಿಸಲು ಸಾಧ್ಯವಾದ ಯಾವ ಕೆಲಸವೂ ಇರುವದಿಲ್ಲ.",
			"16": "<sup>16</sup> ಆ ದಿನದಲ್ಲಿ ಐಗುಪ್ತವು ಹೆಣ್ಣಾಗಿ ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ತನ್ನ ಮೇಲೆ ಕೈಬೀಸುವದರಿಂದ ಭಯಪಟ್ಟು ನಡುಗುವದು.",
			"17": "<sup>17</sup> ಮತ್ತು ಐಗುಪ್ತವು ಬೆಚ್ಚಿ ಬೀಳುವದಕ್ಕೆ ಯೆಹೂದ ದೇಶವು ಕಾರಣವಾಗುವದು; ಈ ದೇಶದ ಹೆಸರನ್ನು ಕೇಳುವ ಪ್ರತಿಯೊಬ್ಬನೂ ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಐಗುಪ್ತಕ್ಕೆ ಪ್ರತಿಕೂಲವಾಗಿ ಮಾಡಿಕೊಂಡಿರುವ ಸಂಕಲ್ಪವನ್ನು ತಿಳಿದು ಬೆರಗಾಗುವನು.",
			"18": "<sup>18</sup> ಆ ದಿನದಲ್ಲಿ ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನ ಭಕ್ತರೆಂದು ಪ್ರಮಾಣಮಾಡಿ ಕಾನಾನಿನ ಭಾಷೆಯನ್ನಾಡುವವರಿಂದ ತುಂಬಿದ ಐದು ಪಟ್ಟಣಗಳು ಐಗುಪ್ತದೇಶದಲ್ಲಿರುವವು; ಇವುಗಳಲ್ಲಿ ಒಂದರ ಹೆಸರು ನಾಶಪುರ.",
			"19": "<sup>19</sup> ಆ ದಿನದಲ್ಲಿ ಐಗುಪ್ತದೇಶದ ಮಧ್ಯೆ ಯೆಹೋವನಿಗೆ ಒಂದು ಯಜ್ಞಪೀಠವೂ ದೇಶದ ಎಲ್ಲೆಯಲ್ಲಿ ಯೆಹೋವನಿಗೆ ಒಂದು ಸ್ತಂಭವೂ ಇರುವವು.",
			"20": "<sup>20</sup> ಅವು ಐಗುಪ್ತದೇಶದಲ್ಲಿ ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನ ಗುರುತಾಗಿಯೂ ಸಾಕ್ಷಿಯಾಗಿಯೂ ಇರುವವು; ಹಿಂಸಕರ ದೆಸೆಯಿಂದ ಯೆಹೋವನನ್ನು ಕೂಗಿಕೊಳ್ಳಲು ಆತನು ಅವರಿಗಾಗಿ ಹೋರಾಡುವ ಶೂರನಾದ ರಕ್ಷಕನನ್ನು ಕಳುಹಿಸಿ ಅವರನ್ನುದ್ಧರಿಸುವನು.",
			"21": "<sup>21</sup> ಮತ್ತು ಯೆಹೋವನು ತನ್ನನ್ನು ಐಗುಪ್ತ್ಯರಿಗೆ ತಿಳಿಯಪಡಿಸಲು ಅವರು ಆ ದಿನದಲ್ಲಿ ಯೆಹೋವನನ್ನು ತಿಳಿದುಕೊಳ್ಳುವರು; ಹೌದು, ಯಜ್ಞನೈವೇದ್ಯಗಳ ಸೇವೆಯನ್ನಾಚರಿಸಿ ಯೆಹೋವನಿಗೆ ಹರಕೆಮಾಡಿಕೊಂಡು ಅದನ್ನು ನೆರವೇರಿಸುವರು.",
			"22": "<sup>22</sup> ಇದಲ್ಲದೆ ಯೆಹೋವನು ಐಗುಪ್ತ್ಯರನ್ನು ಹೊಡೆಯುವನು, ಗಾಯಮಾಡಿ ವಾಸಿಮಾಡುವವನಾಗಿಯೇ ಹೊಡೆಯುವನು; ಅವರು ಯೆಹೋವನ ಕಡೆಗೆ ತಿರುಗಿಕೊಳ್ಳುವರು; ಆತನು ಅವರ ವಿಜ್ಞಾಪನೆಯನ್ನು ಲಾಲಿಸಿ ಅವರನ್ನು ಗುಣಪಡಿಸುವನು.",
			"23": "<sup>23</sup> ಆ ದಿನದಲ್ಲಿ ಐಗುಪ್ತದಿಂದ ಅಶ್ಶೂರಕ್ಕೆ ಹೋಗುವ ರಾಜಮಾರ್ಗವಿರುವದು; ಅಶ್ಶೂರ್ಯರು ಐಗುಪ್ತಕ್ಕೂ ಐಗುಪ್ತ್ಯರು ಅಶ್ಶೂರಕ್ಕೂ ಹೋಗಿಬರುತ್ತಿರುವರು; ಐಗುಪ್ತ್ಯರು ಅಶ್ಶೂರ್ಯರೊಂದಿಗೆ [ಯೆಹೋವನನ್ನು] ಸೇವಿಸುವರು.",
			"24": "<sup>24</sup> ಆ ದಿನದಲ್ಲಿ ಇಸ್ರಾಯೇಲು ಐಗುಪ್ತ ಅಶ್ಶೂರಗಳೊಂದಿಗೆ ಕಲೆತು ಈ ಮೂರೂ ಲೋಕದ ಮಧ್ಯದಲ್ಲಿ ಆಶೀರ್ವಾದದ ನಿಧಿಯಾಗುವವು.",
			"25": "<sup>25</sup> ನನ್ನ ಪ್ರಜೆಯಾದ ಐಗುಪ್ತಕ್ಕೂ ನನ್ನ ಸೃಷ್ಟಿಯಾದ ಅಶ್ಶೂರಕ್ಕೂ ನನ್ನ ಸ್ವಾಸ್ತ್ಯವಾದ ಇಸ್ರಾಯೇಲಿಗೂ ಶುಭವಾಗಲಿ ಎಂದು ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಅವುಗಳನ್ನು ಆಶೀರ್ವದಿಸಿದ್ದಾನಷ್ಟೆ."
		},
		"20": {
			"1": "<sup>1</sup> ಅಶ್ಶೂರದ ರಾಜನಾದ ಸರ್ಗೋನನು ಕಳುಹಿಸಿದ ದಳವಾಯಿಯು ಅಷ್ಡೋದಿಗೆ ಬಂದು ಅದನ್ನು ಮುತ್ತಿಹಿಡಿದ ವರುಷದಲ್ಲಿ",
			"2": "<sup>2</sup> ಯೆಹೋವನು ಆಮೋಚನ ಮಗನಾದ ಯೆಶಾಯನಿಗೆ - ಏಳು, ಸೊಂಟದಿಂದ ಗೋಣಿತಟ್ಟನ್ನು ಬಿಚ್ಚು, ಕಾಲಿನಿಂದ ಕೆರವನ್ನು ಕಳಚು ಎಂದು ಇತರರಿಗೆ ಸೂಚನೆಯಾಗಿ ಅಪ್ಪಣೆಕೊಟ್ಟನು. ಅವನು ಹಾಗೆಯೇ ಮಾಡಿ ಬೆತ್ತಲೆಯಾಗಿ ಕೆರವಿಲ್ಲದೆ ತಿರುಗುತ್ತಿದ್ದನು.",
			"3": "<sup>3</sup> ಆಗ ಯೆಹೋವನು ಹೀಗೆ ಹೇಳಿದನು - ನನ್ನ ಸೇವಕನಾದ ಯೆಶಾಯನು ಐಗುಪ್ತದ ಮತ್ತು ಕೂಷಿನ ನಾಶನಕ್ಕೆ ಸೋಜಿಗದ ಗುರುತಾಗಿ ಮೂರು ವರುಷ ಬಟ್ಟೆ ಕೆರಗಳಿಲ್ಲದೆ ನಡೆದ ಪ್ರಕಾರ",
			"4": "<sup>4</sup> ಅಶ್ಶೂರದ ಅರಸನು ಐಗುಪ್ತದ ಸೆರೆಯವರನ್ನೂ ಕೂಷಿನ ಕೈದಿಗಳನ್ನೂ ಅವರು ದೊಡ್ಡವರಾಗಲಿ ಚಿಕ್ಕವರಾಗಲಿ ಬಟ್ಟೆ ಕೆರಗಳಿಲ್ಲದೆ ಬರಿಕುಂಡಿಯವರಾಗಿ ಐಗುಪ್ತದ ಮಾನಭಂಗಕ್ಕೋಸ್ಕರ ಸೆರೆಗೆ ನಡೆಯುವಂತೆ ಮಾಡುವನು.",
			"5": "<sup>5</sup> ಆಗ [ನನ್ನ ಜನರು] ತಾವು ನಿರೀಕ್ಷಿಸಿಕೊಂಡಿದ್ದ ಕೂಷಿನ ನಿಮಿತ್ತವಾಗಿಯೂ ಕೊಚ್ಚಿಕೊಂಡಿದ್ದ ಐಗುಪ್ತದ ವಿಷಯವಾಗಿಯೂ ಬೆಚ್ಚಿಬೆರಗಾಗಿ ನಾಚಿಕೆಪಡುವರು.",
			"6": "<sup>6</sup> ಆ ದಿನದಲ್ಲಿ ಈ ಕರಾವಳಿಯಲ್ಲಿ ವಾಸಿಸುವವರು - ಹಾ, ಅಶ್ಶೂರದ ಅರಸನಿಂದ ಬಿಡುಗಡೆಯಾಗಬೇಕೆಂದು ನಾವು ಯಾರನ್ನು ಶರಣಾಗತರಾಗಿ ಆಶ್ರಯಿಸಿ ನಿರೀಕ್ಷಿಸಿದ್ದೆವೋ ಅವರಿಗೇ ಈ ಗತಿ ಬಂತಲ್ಲಾ; ನಮ್ಮಂಥವರಿಗೆ ಹೇಗೆ ರಕ್ಷಣೆಯಾದೀತು ಎಂದು ಅಂದುಕೊಳ್ಳುವರು."
		},
		"21": {
			"1": "<sup>1</sup> ಕಡಲಡವಿಯ ವಿಷಯವಾದ ದೈವೋಕ್ತಿ. ದಕ್ಷಿಣಸೀಮೆಯಲ್ಲಿ ಬೀಸುವ ಬಿರುಗಾಳಿಯಂತೆ ಅರಣ್ಯದ ಕಡೆಯ ಭಯಂಕರ ದೇಶದಿಂದ [ದೊಡ್ಡ ಅವಾಂತರ] ಬರುತ್ತದೆ.",
			"2": "<sup>2</sup> ಘೋರದರ್ಶನವು ನನಗೆ ತಿಳಿಯ ಬಂದಿದೆ; ಬಾಧಕನು ಬಾಧಿಸುತ್ತಿದ್ದಾನೆ; ಸೂರೆಗಾರನು ಸೂರೆಮಾಡುತ್ತಿದ್ದಾನೆ. ಏಲಾಮೇ, ಏಳು! ಮೇದ್ಯವೇ, ಮುತ್ತು! ನಿಮ್ಮ ನಿಟ್ಟುಸುರನ್ನು ನಿಲ್ಲಿಸಿಬಿಟ್ಟಿದ್ದೇನೆ.",
			"3": "<sup>3</sup> ಈ ದರ್ಶನದಿಂದ ನನ್ನ ಸೊಂಟದಲ್ಲಿ ನೋವು ತುಂಬಿದೆ, ಪ್ರಸವವೇದನೆಯಂತಿರುವ ವೇದನೆಯು ನನ್ನನ್ನು ಹಿಡಿದಿದೆ, ಕಿವಿ ಕಿವುಡಾಗುವಷ್ಟು ಯಾತನೆಪಡುತ್ತೇನೆ, ಕಣ್ಣು ಕುರುಡಾಗುವಷ್ಟು ತತ್ತರಗೊಂಡಿದ್ದೇನೆ.",
			"4": "<sup>4</sup> ನನ್ನ ಹೃದಯವು ಭ್ರಮೆಗೊಂಡಿದೆ, ನಡುಕವು ನನ್ನನ್ನು ಆಕ್ರಮಿಸಿದೆ, ನಾನು ಅಪೇಕ್ಷಿಸುತ್ತಿದ್ದ ಸಂಜೆಹೊತ್ತೇ ನನಗೆ ಭಯಂಕರವಾಗಿ ಪರಿಣಮಿಸಿದೆ.",
			"5": "<sup>5</sup> ಔತಣಕ್ಕೆ ಸಿದ್ಧಪಡಿಸಿಕೊಂಡು ಚಾಪೆಗಳನ್ನು ಹಾಸಿಕೊಂಡು ಉಂಡು ಕುಡಿಯುತ್ತಿದ್ದಾರಲ್ಲಾ; ಪ್ರಭುಗಳೇ, ಏಳಿರಿ, ಗುರಾಣಿಗೆ ಎಣ್ಣೆಯನ್ನು ಬಳಿಯಿರಿ!",
			"6": "<sup>6</sup> ಕರ್ತನು ನನಗೆ ಹೇಳಿರುವದೇನಂದರೆ - ಕಾವಲುಗಾರನನ್ನು ಇಡು, ನಡೆ; ಕಂಡದ್ದನ್ನು ತಿಳಿಸಲಿ;",
			"7": "<sup>7</sup> ಅವನು ಜೋಡಿ ಜೋಡಿಯಾಗಿ ಬರುವ ಸವಾರರ ಸಾಲನ್ನೂ ಕತ್ತೆಗಳ ಮತ್ತು ಒಂಟೆಗಳ ಸಾಲುಗಳನ್ನೂ ನೋಡಿದರೆ ಬಹು ಗಮನದಿಂದ ಕಿವಿಗೊಟ್ಟು ಗಮನಿಸಲಿ ಎಂಬದೇ.",
			"8": "<sup>8</sup> ಬಳಿಕ ಅವನು ಸಿಂಹಧ್ವನಿಯಿಂದ - ಕರ್ತನೇ, ಹಗಲೆಲ್ಲಾ ಕೋವರದಲ್ಲಿ ನಿಂತಿದ್ದೇನೆ, ರಾತ್ರಿಯೆಲ್ಲಾ ಕಾವಲಿನ ಕೆಲಸ ನನಗೆ ಬಿದ್ದಿದೆ;",
			"9": "<sup>9</sup> ಇಗೋ, ಸವಾರರು, ಜೋಡಿ ಜೋಡಿಯಾಗಿ ಬರುತ್ತಾರೆ ಎಂದು ಕೂಗಿ ಇನ್ನೂ ಹೇಳಿದ್ದೇನಂದರೆ - ಬಾಬೆಲ್ ಬಿತ್ತು, ಬಿತ್ತು! ಅದರ ದೇವತಾವಿಗ್ರಹಗಳನ್ನು ಒಡೆದು ನೆಲಸಮಮಾಡಿಬಿಟ್ಟರು ಎಂಬದೇ.",
			"10": "<sup>10</sup> ನನ್ನ ಕಣದ ದವಸವೇ, ನನ್ನ ಬಡಿತಕ್ಕೆ ಈಡಾದವರೇ, ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರಾಗಿರುವ ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನಿಂದ ನಾನು ಕೇಳಿದ್ದನ್ನೇ ನಿಮಗೆ ತಿಳಿಸಿದ್ದೇನೆ.",
			"11": "<sup>11</sup> ದೂಮದ ವಿಷಯವಾದ ದೈವೋಕ್ತಿ. ಕಾವಲುಗಾರನೇ, ಈಗ ರಾತ್ರಿ ಎಷ್ಟು ಕಳೆಯಿತು, ಕಾವಲುಗಾರನೇ, ರಾತ್ರಿ ಎಷ್ಟು ಕಳೆಯಿತು ಎಂಬ ಕೂಗು ಸೇಯೀರಿನಿಂದ ನನಗೆ ಕೇಳಿಸುತ್ತಿತ್ತು.",
			"12": "<sup>12</sup> ಉದಯವು ಬರುತ್ತದೆ, ರಾತ್ರಿಯೂ ಬರುತ್ತದೆ; ವಿಚಾರಿಸಬೇಕಾದರೆ ವಿಚಾರಿಸಿರಿ, ತಿರಿಗಿ ಬನ್ನಿರಿ ಎಂದು ಕಾವಲುಗಾರನು ಹೇಳಿದನು.",
			"13": "<sup>13</sup> ಅರಬಿಯ ವಿಷಯವಾದ ದೈವೋಕ್ತಿ. ಸಾರ್ಥವಾಹರಾದ ದೇದಾನ್ಯರೇ, ಮರುಭೂಮಿಯ ಪೊದೆಗಳಲ್ಲಿ ಇಳಿದುಕೊಳ್ಳಿರಿ.",
			"14": "<sup>14</sup> ಬಾಯಾರಿದವರಿಗೆ ನೀರನ್ನು ತಂದುಕೊಡಿರಿ, ತೇಮಾದೇಶದವರೇ, ಓಡಿಹೋದವರನ್ನು ಅನ್ನದೊಡನೆ ಎದುರುಗೊಳ್ಳಿರಿ.",
			"15": "<sup>15</sup> ಕತ್ತಿ, ಹಿರಿದ ಕತ್ತಿ, ಬಾಗಿದ ಬಿಲ್ಲು, ಯುದ್ಧದ ಬಾಧೆ, ಇವುಗಳ ಕಡೆಯಿಂದ ಓಡಿಹೋಗುತ್ತಿದ್ದಾರಷ್ಟೆ.",
			"16": "<sup>16</sup> ಕರ್ತನು ನನಗೆ ಹೇಳಿರುವದೇನಂದರೆ - ಆಳಿನ ವರುಷ ವಾಯಿದೆಗೆ ಸರಿಯಾದ ಒಂದು ವರುಷಕ್ಕೇ ಕೇದಾರಿನ ಸಕಲ ವೈಭವವು ಇಲ್ಲವಾಗುವದು;",
			"17": "<sup>17</sup> ಬಿಲ್ಲುಗಾರರಲ್ಲಿ ಉಳಿದವರೂ ಕೇದಾರಿನವರ ಶೂರರೂ ವಿರಳರಾಗುವರು; ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರಾದ ಯೆಹೋವನು ಇದನ್ನು ನುಡಿದಿದ್ದಾನೆ ಎಂಬದೇ."
		},
		"22": {
			"1": "<sup>1</sup> ದಿವ್ಯದರ್ಶನದ ತಗ್ಗಿನ ವಿಷಯವಾದ ದೈವೋಕ್ತಿ. ನಿನಗೆ ಏನಾಯಿತು? ನಿನ್ನ ಜನರೆಲ್ಲರೂ ಮಾಳಿಗೆಗಳನ್ನು ಹತ್ತಿದ್ದೇಕೆ?",
			"2": "<sup>2</sup> ಕೋಲಾಹಲದಿಂದ ತುಂಬಿ ಆರ್ಭಟಿಸುವ ಪಟ್ಟಣವೇ, ಸಂಭ್ರಮದ ಊರೇ! ನಿನ್ನಲ್ಲಿ ಹತರಾದವರು ಖಡ್ಗಹತರಲ್ಲ, ಯುದ್ಧದಲ್ಲಿ ಮೃತರಲ್ಲ.",
			"3": "<sup>3</sup> ನಿನ್ನ ಅಧಿಕಾರಿಗಳು ಒಟ್ಟಿಗೆ ಓಡಿ ಬಿಲ್ಲಿಲ್ಲದೆ ಸೆರೆಯಾಗಿದ್ದಾರೆ; ದೂರ ಓಡುತ್ತಾ [ಶತ್ರುವಿಗೆ] ಸಿಕ್ಕಿದ ನಿನ್ನ ಜನರೆಲ್ಲರೂ ಒಂದಿಗೆ ಕಟ್ಟುಬಿದ್ದಿದ್ದಾರೆ.",
			"4": "<sup>4</sup> ಹೀಗಿರಲು ನನ್ನ ಕಡೆಯಿಂದ ದೃಷ್ಟಿ ತಿರುಗಿಸಿರಿ. ಬಹುಸಂಕಟದಿಂದ ಅಳುವೆನು, ನನ್ನ ಜನವೆಂಬ ಯುವತಿಯು ಹಾಳಾದ ವಿಷಯದಲ್ಲಿ ನನ್ನನ್ನು ಸಂತೈಸುವದಕ್ಕೆ ತವಕಗೊಳ್ಳದಿರಿ ಅಂದೆನು.",
			"5": "<sup>5</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೆಂಬ ಕರ್ತನಿಂದ ದಿವ್ಯದರ್ಶನದ ತಗ್ಗಿಗೆ ಗಲಿಬಿಲಿಯ ದಿನ, ತುಳಿದಾಟದ ದಿನ, ಭ್ರಾಂತಿಯ ದಿನ, ಕೋಟೆಯು ಒಡೆದುಹೋಗಲು ಕೂಗು ಬೆಟ್ಟವನ್ನು ಮುಟ್ಟುವ ದಿನ ಬಂದಿದೆ.",
			"6": "<sup>6</sup> ಏಲಾಮು ರಥಾಶ್ವಪದಾತಿಗಳಿಂದ ಬತ್ತಳಿಕೆಯನ್ನು ಧರಿಸಿತು; ಕೀರಿನವರು ಗುರಾಣಿಯ ಗೌಸಣಿಗೆಯನ್ನು ತೆರೆದರು.",
			"7": "<sup>7</sup> ನಿಮ್ಮ ಪ್ರಿಯವಾದ ತಗ್ಗುಗಳಲ್ಲಿ ರಥಗಳು ತುಂಬಿದವು, ರಾಹುತರು ಬಾಗಿಲೆದುರಿಗೆ ವ್ಯೂಹಕಟ್ಟಿದರು.",
			"8": "<sup>8</sup> ಯೆಹೂದದ ತೆರೆಯು ತೆರೆದುಹೋಯಿತು; ಆ ದಿನದಲ್ಲಿ [ಲೆಬನೋನಿನ] ತೋಪು ಎನಿಸಿಕೊಳ್ಳುವ ಮಂದಿರದಲ್ಲಿನ ಆಯುಧಗಳ ಕಡೆಗೆ ದೃಷ್ಟಿಹಾಕಿದಿರಿ.",
			"9": "<sup>9</sup> ದಾವೀದನ ಕೋಟೆಯ ಒಡಕುಗಳು ಬಹಳವೆಂದು ನೋಡಿದಿರಿ; ಕೆಳಗಿನ ಕೊಳಕ್ಕೆ ನೀರನ್ನು ಹಾಯಿಸಿದಿರಿ.",
			"10": "<sup>10</sup> ಯೆರೂಸಲೇಮಿನ ಮನೆಗಳನ್ನು ಲೆಕ್ಕಿಸಿ ಪೌಳಿಗೋಡೆಯನ್ನು ಭದ್ರಪಡಿಸುವದಕ್ಕೆ ಮನೆಗಳನ್ನು ಕೆಡವಿಬಿಟ್ಟಿರಿ.",
			"11": "<sup>11</sup> ಎರಡು ಗೋಡೆಗಳ ನಡುವೆ ಹಳೆಯ ಕೊಳದ ನೀರಿಗಾಗಿ ತೊಟ್ಟಿಯನ್ನು ಮಾಡಿದಿರಿ; ಆದರೆ ಇದನ್ನೆಲ್ಲಾ ನಡಿಸಿದಾತನ ಕಡೆಗೆ ನೀವು ದೃಷ್ಟಿಯಿಡಲಿಲ್ಲ, ಪುರಾತನ ಕಾಲದಲ್ಲಿ ಸಂಕಲ್ಪಿಸಿದಾತನನ್ನು ನೀವು ಲಕ್ಷಿಸಲಿಲ್ಲ.",
			"12": "<sup>12</sup> ಕಣ್ಣೀರುಸುರಿಸಿ ಅಂಗಲಾಚಿ ತಲೆಬೋಳಿಸಿಕೊಂಡು ಗೋಣಿತಟ್ಟನ್ನು ಸುತ್ತಿಕೊಳ್ಳಿರಿ ಎಂದು ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೆಂಬ ಕರ್ತನು ಆ ದಿನದಲ್ಲಿ ನಿಮಗೆ ಆಜ್ಞಾಪಿಸಿದನು;",
			"13": "<sup>13</sup> ಆದರೆ ಇಗೋ, ಉಲ್ಲಾಸ, ಹರ್ಷ, ದನಕೊಯ್ಯುವದು, ಕುರಿಕಡಿಯುವದು, ಮಾಂಸತಿನ್ನುವದು, ದ್ರಾಕ್ಷಾರಸ ಕುಡಿಯುವದು, ನಾಳೆ ಸಾಯುತ್ತೇವೆಂದು ತಿಂದು ಕುಡಿಯುವದು [ಇವೇ ನಿಮ್ಮ ಕಾರ್ಯ].",
			"14": "<sup>14</sup> ಹೀಗಿರಲು ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ನನ್ನ ಕಿವಿಗೆ ಗೋಚರಪಡಿಸಿದ್ದೇನಂದರೆ - ನೀವು ಸಾಯುವ ತನಕ ಈ ಅಧರ್ಮಕ್ಕೆ ಪ್ರಾಯಶ್ಚಿತ್ತವು ಇಲ್ಲವೇ ಇಲ್ಲ ಎಂದು ಸೇನಾಧೀಶ್ವರನಾಗಿರುವ ಯೆಹೋವನೆಂಬ ಕರ್ತನಾದ ನಾನು ನುಡಿದಿದ್ದೇನೆ ಎಂಬದೇ.",
			"15": "<sup>15</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೆಂಬ ಕರ್ತನು ಹೀಗೆ ನುಡಿದಿದ್ದಾನೆ - ನಡೆ, ಅರಮನೆಯ ಮೇಲ್ವಿಚಾರಕನಾದ ಶೆಬ್ನನೆಂಬ ಈ ಅಧ್ಯಕ್ಷನ ಬಳಿಗೆ ಹೋಗಿ ಅವನಿಗೆ ಹೇಳತಕ್ಕದ್ದೇನಂದರೆ -",
			"16": "<sup>16</sup> ಇಲ್ಲಿ ನಿನಗೇನು? ಇಲ್ಲಿ ನಿನಗೆ ಯಾರಿದ್ದಾರೆ? ಇಲ್ಲಿ ನಿನಗಾಗಿ ಗೋರಿಯನ್ನು ತೋಡಿಸಿಕೊಂಡಿದ್ದೀಯಾ? ಎತ್ತರದಲ್ಲಿ ನಿನಗೆ ಸಮಾಧಿಯನ್ನು ಕೊರೆಸುತ್ತಾ ಬಂಡೆಯನ್ನು ಕಡೆಯಿಸಿ ನಿನಗೆ ನಿವಾಸವನ್ನು ಮಾಡಿಸುತ್ತಾ ಇದ್ದೀಯೋ?",
			"17": "<sup>17</sup> ಇಗೋ, ಬಲಿತವನೇ, ಯೆಹೋವನು ನಿನ್ನನ್ನು ಎಸೆದೇ ಎಸೆಯುವನು, ಹಿಡಿದೇ ಹಿಡಿಯುವನು;",
			"18": "<sup>18</sup> ನಿನ್ನನ್ನು ಚಂಡಿನಂತೆ ಸುತ್ತಿ ಸುತ್ತಿ ವಿಸ್ತಾರವಾದ ದೇಶಕ್ಕೆ ಬಿಸಾಡುವನು; ದಣಿಯ ಮನೆಗೆ ಅವಮಾನ ತಂದವನೇ, ಅಲ್ಲೇ ಸಾಯುವಿ, ನಿನ್ನ ವೈಭವದ ರಥಗಳು ಅಲ್ಲೇ ಇರುವವು.",
			"19": "<sup>19</sup> [ಯೆಹೋವನಾದ] ನಾನು ನಿನ್ನ ಉದ್ಯೋಗದಿಂದ ನಿನ್ನನ್ನು ತಳ್ಳಿಬಿಡುವೆನು, ನಿನ್ನ ಪದವಿಯಿಂದ ನಿನ್ನನ್ನು ಎಳೆದು ಹಾಕುವೆನು.",
			"20": "<sup>20</sup> ಆ ದಿನದಲ್ಲಿ ನನ್ನ ಸೇವಕನೂ ಹಿಲ್ಕೀಯನ ಮಗನೂ ಆದ ಎಲ್ಯಾಕೀಮನನ್ನು ಕರೆದು",
			"21": "<sup>21</sup> ನಿನ್ನ ನಿಲುವಂಗಿಯನ್ನು ಅವನಿಗೆ ತೊಡಿಸಿ ನಿನ್ನ ನಡುಕಟ್ಟನ್ನು ಅವನಿಗೆ ಕಟ್ಟಿ ನಿನ್ನ ಅಧಿಕಾರವನ್ನು ಅವನ ಕೈಗೆ ಕೊಡುವೆನು; ಅವನು ಯೆರೂಸಲೇಮಿನ ನಿವಾಸಿಗಳಿಗೂ ಯೆಹೂದದ ಮನೆತನದವರಿಗೂ ತಂದೆಯಾಗುವನು.",
			"22": "<sup>22</sup> ಅವನು ದಾವೀದನ ಮನೆಯ ಬೀಗದ ಕೈಯನ್ನು ತನ್ನ ಹೆಗಲ ಮೇಲೆ ವಹಿಸಿಕೊಳ್ಳುವಂತೆ ಮಾಡುವೆನು; ಅವನು ತೆರೆದರೆ ಯಾರೂ ಮುಚ್ಚರು, ಮುಚ್ಚಿದರೆ ಯಾರೂ ತೆರೆಯರು.",
			"23": "<sup>23</sup> ಗಟ್ಟಿಯಾದ ಸ್ಥಳದಲ್ಲಿ ಮೊಳೆಯನ್ನು ಹೊಡೆದ ಹಾಗೆ ಅವನನ್ನು ಭದ್ರಪಡಿಸುವೆನು. ಅವನು ತನ್ನ ವಂಶದವರಿಗೆ ಗೌರವಪೀಠವಾಗಿರುವನು.",
			"24": "<sup>24</sup> ಬಟ್ಟಲು ಮೊದಲುಗೊಂಡು ಕೊಡದ ತನಕ ಸಕಲ ಸಾಧಾರಣಪಾತ್ರೆಗಳನ್ನು ಅಂದರೆ ತಂದೆಯ ಸಂತಾನ ಸಂತತಿಯಾದ ಅವನ ವಂಶದ ಭಾರವನ್ನೆಲ್ಲಾ ಅವನಿಗೆ ತಗಲುಹಾಕುವರು.",
			"25": "<sup>25</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಹೇಳುವದೇನಂದರೆ - ಗಟ್ಟಿಯಾದ ಸ್ಥಳದಲ್ಲಿ ಹೊಡೆದ ಮೊಳೆಯು ಆ ದಿನದಲ್ಲಿ ಕುಸಿದು ಹೋಗುವದು; ಅದು ಬಡಿಯಲ್ಪಟ್ಟು ಬೀಳಲಾಗಿ ಅದಕ್ಕೆ ತಗಲುಹಾಕಿದ ಭಾರವೂ ನಾಶವಾಗುವದು. ಇದೇ ಯೆಹೋವನಾದ ನನ್ನ ನುಡಿ ಎಂಬದೇ."
		},
		"23": {
			"1": "<sup>1</sup> ತೂರಿನ ವಿಷಯವಾದ ದೈವೋಕ್ತಿ. ತಾರ್ಷೀಷಿನ ಹಡಗುಗಳೇ, ಅಂಗಲಾಚಿರಿ! [ನಿಮ್ಮ ಆಶ್ರಯವು] ಹಾಳಾಯಿತು, ನಿಮಗೆ ನೆಲೆಯಿಲ್ಲ, ರೇವಿಲ್ಲ. (ಕಿತ್ತೀಮ್ ದೇಶೀಯರಿಂದ ಅವುಗಳಿಗೆ ತಿಳಿಯಿತು).",
			"2": "<sup>2</sup> ಕರಾವಳಿಯ ನಿವಾಸಿಗಳೇ, ಸಮುದ್ರವನ್ನು ಹಾದುಹೋಗುವ ಚೀದೋನಿನ ವರ್ತಕರಿಂದ ಸಮೃದ್ಧಿಹೊಂದಿದವರೇ, ಮೌನವಾಗಿರಿ!",
			"3": "<sup>3</sup> ವಿಶಾಲವಾದ ಸಾಗರದ ಮೇಲೆ ತಂದ ಶೀಹೋರಿನ ಧಾನ್ಯದಿಂದಲೂ ನೈಲಿನ ಬೆಳೆಯಿಂದಲೂ ಆದಾಯ ಹೊಂದಿದ [ನಿಮ್ಮ ನಗರಿಯು] ಅನೇಕ ಜನಾಂಗಗಳಿಗೆ ಲಾಭಕರವಾಗಿತ್ತು.",
			"4": "<sup>4</sup> ಚೀದೋನೇ, ನಾಚಿಕೆಪಡು; ಸಮುದ್ರವು, ಸಮುದ್ರದುರ್ಗವು - ನಾವು ವೇದನೆಪಡಲಿಲ್ಲ, ಪ್ರಸವಿಸಲಿಲ್ಲ, ಯುವತಿಯುವಕರನ್ನು ಸಾಕಿ ಸಲಹಲಿಲ್ಲ ಎಂದು ನುಡಿದಿದೆಯಷ್ಟೆ.",
			"5": "<sup>5</sup> ತೂರಿನ ಸಮಾಚಾರವು ಐಗುಪ್ತ್ಯರಿಗೆ ಮುಟ್ಟಲು ಅದಕ್ಕಾಗಿ ಸಂಕಟಪಡುವರು.",
			"6": "<sup>6</sup> ತಾರ್ಷೀಷಿಗೆ ಸಮುದ್ರದ ಮೇಲೆ ಹಾದುಹೋಗಿರಿ, ಕರಾವಳಿಯ ನಿವಾಸಿಗಳೇ, ಅಂಗಲಾಚಿರಿ!",
			"7": "<sup>7</sup> ನಿಮ್ಮ ಉಲ್ಲಾಸದ ಪಟ್ಟಣವು ಇದೇನೋ? ಅದರ ಉತ್ಪತ್ತಿಯು ಪುರಾತನವಾದದ್ದೇ ಸರಿ; ಅದರ ಜನರು ಮುಂದೆ ನಡೆದು ಅತಿ ದೂರದಲ್ಲಿಯೂ ನಿವಾಸಮಾಡಿಕೊಂಡಿದ್ದರಲ್ಲಾ.",
			"8": "<sup>8</sup> ಅದು ಕಿರೀಟದಾಯಕವು; ಅದರ ವರ್ತಕರು ಪ್ರಭುಗಳು, ಅದರ ವ್ಯಾಪಾರಿಗಳು ಲೋಕಮಾನ್ಯರು; ಇಂಥ ತೂರಿಗೆ ವಿರುದ್ಧವಾಗಿ ಈ ಸಂಕಲ್ಪವನ್ನು ಮಾಡಿದವನು ಯಾರು?",
			"9": "<sup>9</sup> ಗರ್ವದ ಸಕಲ ವೈಭವವನ್ನು ಹೊಲಸುಮಾಡಬೇಕೆಂತಲೂ ಲೋಕಮಾನ್ಯರೆಲ್ಲರನ್ನು ಅವಮಾನ ಪಡಿಸಬೇಕೆಂತಲೂ ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೇ ಹೀಗೆ ಸಂಕಲ್ಪಿಸಿದ್ದಾನೆ.",
			"10": "<sup>10</sup> ತಾರ್ಷೀಷ್ ನಗರಿಯೇ, ನೈಲ್ ನದಿಯಂತೆ ನಿನ್ನ ದೇಶವನ್ನು ಆವರಿಸು; ನಡುಕಟ್ಟು [ಸಡಲಿತು], ಇನ್ನಿಲ್ಲ.",
			"11": "<sup>11</sup> ಯೆಹೋವನು ಸಮುದ್ರದ ಮೇಲೆ ಕೈಚಾಚಿ ರಾಜ್ಯಗಳನ್ನು ನಡುಗಿಸಿದ್ದಾನೆ; ಕಾನಾನಿನ ದುರ್ಗಗಳನ್ನು ನಾಶಮಾಡಲು ಅದರ ವಿಷಯವಾಗಿ ಅಪ್ಪಣೆಕೊಟ್ಟಿದ್ದಾನೆ;",
			"12": "<sup>12</sup> ಹಿಂಸೆಗೆ ಈಡಾದ ಕನ್ಯೆಯಂತಿರುವ ಚೀದೋನ್ ನಗರಿಯೇ, ಇನ್ನು ಮೇಲೆ ನಿನಗೆ ಹರ್ಷವೇ ಇಲ್ಲ; ಎದ್ದು ಕಿತ್ತೀಮಿಗೆ ಸಮುದ್ರದ ಮೇಲೆ ಹಾದುಹೋಗು, ಅಲ್ಲಿಯೂ ನಿನಗೆ ವಿಶ್ರಾಂತಿಯಿರದು ಎಂದು ಹೇಳಿದ್ದಾನೆ.",
			"13": "<sup>13</sup> ಇಗೋ, ಕಸ್ದೀಯರ ದೇಶವು! ಈ ಜನಾಂಗವು ನಿರ್ನಾಮವಾಯಿತು; ಅಶ್ಶೂರ್ಯರು ಈ ದೇಶವನ್ನು ಕಾಡುಮೃಗಗಳಿಗೆ ಈಡುಮಾಡಿದರು; ಬುರುಜುಗಳನ್ನು ಕಟ್ಟಿಕೊಂಡು ಇದರ ಕೋಟೆಗಳನ್ನು ಕೆಡವಿ ಇದನ್ನು ನಾಶಪಡಿಸಿದರು.",
			"14": "<sup>14</sup> ತಾರ್ಷೀಷಿನ ಹಡಗುಗಳೇ, ಅಂಗಲಾಚಿರಿ ನಿಮ್ಮ ಆಶ್ರಯವು ಹಾಳಾಯಿತು.",
			"15": "<sup>15</sup> ಆ ಕಾಲದಲ್ಲಿ ತೂರ್‍ಪಟ್ಟಣವು ಒಬ್ಬ ರಾಜನ ಆಡಳಿತದ ಎಪ್ಪತ್ತು ವರುಷಗಳ ತನಕ ಜ್ಞಾಪಕಕ್ಕೆ ಬಾರದೇ ಇರುವದು; ಎಪ್ಪತ್ತು ವರುಷದ ಮೇಲೆ ಸೂಳೆಯ ವಿಷಯವಾದ ಗೀತದಂತಾಗುವದು.",
			"16": "<sup>16</sup> ಅದೇನಂದರೆ ಎಲ್ಲರೂ ಮರೆತ ಸೂಳೆಯೇ, ಕಿನ್ನರಿಯನ್ನು ತೆಗೆದುಕೊಂಡು ಊರಲ್ಲಿ ಅಲೆಯುತ್ತಾ ಜನರು ನಿನ್ನನ್ನು ಜ್ಞಾಪಿಸಿಕೊಳ್ಳುವಂತೆ ಚೆನ್ನಾಗಿ ನುಡಿಸಿ ಬಹು ಗೀತಗಳನ್ನು ಹಾಡು ಎಂಬದೇ.",
			"17": "<sup>17</sup> ಎಪ್ಪತ್ತು ವರುಷಗಳ ಮೇಲೆ ಯೆಹೋವನು ತೂರ್ ಎಂಬವಳಿಗೆ ನೇಮಿಸಲು ಅವಳು ತನ್ನ ಆದಾಯಕ್ಕಾಗಿ ಹಿಂದಿರುಗಿ ಭೂಮಂಡಲದಲ್ಲಿ ಲೋಕದ ಸಕಲ ರಾಜ್ಯಗಳೊಂದಿಗೆ ಕಲೆಯುವಳು.",
			"18": "<sup>18</sup> ಅವಳ ವ್ಯಾಪಾರವೂ ಆದಾಯವೂ ಅವಳಿಗೆ ನಿಧಿನಿಕ್ಷೇಪವಾಗದೆ ಯೆಹೋವನಿಗೇ ಮೀಸಲಾಗುವದು; ಆ ವ್ಯಾಪಾರವು ಯೆಹೋವನ ಸನ್ನಿಧಾನದಲ್ಲಿ ವಾಸಿಸುವವರಿಗೆ ಬೇಕಾದಷ್ಟು ಅನ್ನವನ್ನೂ ಶ್ರೇಷ್ಠವಾದ ಉಡುಪನ್ನೂ ಒದಗಿಸಲು ಅನುಕೂಲಿಸುವದು."
		},
		"24": {
			"1": "<sup>1</sup> ಇಗೋ, ಯೆಹೋವನು ಲೋಕವನ್ನು ಬರಿದುಮಾಡಿ ಹಾಳಿಗೆ ತಂದು ವಿರೂಪಪಡಿಸಿ ಅದರ ನಿವಾಸಿಗಳನ್ನು ಚದರಿಸಿಬಿಡುವವನಾಗಿದ್ದಾನೆ.",
			"2": "<sup>2</sup> ಪ್ರಜೆ ಯಾಜಕ, ದಾಸ ದಣಿ, ತೊತ್ತು ಯಜಮಾನಿ, ಕೊಳ್ಳುವವನು ಮಾರುವವನು, ಸಾಲಕೊಡುವವನು ತರುವವನು, ಬಡ್ಡಿ ತೆಗೆಯುವವನು ತೆರುವವನು, ಇವರೆಲ್ಲರಿಗೂ ಒಂದೇ ಗತಿಯಾಗುವದು.",
			"3": "<sup>3</sup> ಭೂಮಿಯು ಬಟ್ಟಬರಿದಾಗುವದು, ಲೋಕಕ್ಕೆ ಸಂಪೂರ್ಣ ಸುಲಿಗೆಯಾಗುವದು; ಯೆಹೋವನೇ ಇದನ್ನು ನುಡಿದಿದ್ದಾನೆ.",
			"4": "<sup>4</sup> ಭೂಮಿಯು ಪ್ರಲಾಪಿಸುತ್ತಾ ಬಳಲಿದೆ, ಲೋಕವು ಕುಗ್ಗಿ ಹೋಗಿದೆ, ಲೋಕೋನ್ನತರು ಕಂಗೆಟ್ಟಿದ್ದಾರೆ.",
			"5": "<sup>5</sup> ಭೂನಿವಾಸಿಗಳು ದೈವಾಜ್ಞೆಗಳನ್ನು ಮೀರಿ ನಿಯಮವನ್ನು ಅತಿಕ್ರಮಿಸಿ ಶಾಶ್ವತವಾದ ಒಡಂಬಡಿಕೆಯನ್ನು ಭಂಗಪಡಿಸಿದ್ದರಿಂದ ಭೂಮಿಯು ಅವರ ಹೆಜ್ಜೆಯಿಂದ ಅಪವಿತ್ರವಾಯಿತು.",
			"6": "<sup>6</sup> ಆದಕಾರಣ ಶಾಪವು ಲೋಕವನ್ನು ನುಂಗಿಬಿಟ್ಟಿದೆ, ಅಲ್ಲಿನವರು ದಂಡನೆಗೆ ಒಳಗಾಗಿದ್ದಾರೆ; ಭೂನಿವಾಸಿಗಳು ಸುಟ್ಟುಹೋಗಿ ಕೆಲವರು ಮಾತ್ರ ಉಳಿದಿದ್ದಾರೆ.",
			"7": "<sup>7</sup> ದ್ರಾಕ್ಷಾರಸವು ವ್ಯಸನಭರಿತವಾಗಿದೆ, ದ್ರಾಕ್ಷೆಯ ಬಳ್ಳಿಯು ಬಾಡಿದೆ, ಹರ್ಷಹೃದಯರೆಲ್ಲಾ ನರಳುತ್ತಾರೆ.",
			"8": "<sup>8</sup> ದಮ್ಮಡಿಗಳ ಉತ್ಸಾಹವು ಮುಗಿದಿದೆ, ಉಲ್ಲಾಸಿಗಳ ಕೋಲಾಹಲವು ಕೊನೆಗೊಂಡಿದೆ, ಕಿನ್ನರಿಯ ಆನಂದವು ಅಡಗಿದೆ.",
			"9": "<sup>9</sup> ಇನ್ನು ಗಾನದೊಡನೆ ದ್ರಾಕ್ಷಾರಸವನ್ನು ಕುಡಿಯರು, ಮದ್ಯವು ಕುಡಿಯುವವರಿಗೆ ಕಹಿಯಾಗುವದು.",
			"10": "<sup>10</sup> ಹಾಳುಪಟ್ಟಣವು ಬಿದ್ದುಹೋಗಿದೆ, ಯಾರೂ ಹೊಗದಂತೆ ಪ್ರತಿಯೊಂದು ಮನೆಯೂ ಮುಚ್ಚಿದೆ.",
			"11": "<sup>11</sup> ದ್ರಾಕ್ಷಾರಸದ ಕೊರತೆಯಿಂದ ಬೀದಿಗಳಲ್ಲಿ ಅರಚುತ್ತಾರೆ; ಉಲ್ಲಾಸವೆಲ್ಲಾ ಅಸ್ತಮಿಸಿದೆ; ಲೋಕದ ಸಡಗರವು ಸೆರೆಯಾಗಿ ತೊಲಗಿದೆ.",
			"12": "<sup>12</sup> ಪಟ್ಟಣದಲ್ಲಿ ಹಾಳೇ ಉಳಿದಿದೆ; ನಾಶನವು ಹೆಬ್ಬಾಗಿಲಿಗೆ ತಟ್ಟಿದೆ.",
			"13": "<sup>13</sup> ಎಣ್ಣೆಯ ಮರವನ್ನು ಬಡಿದ ಮೇಲೆಯೂ ದ್ರಾಕ್ಷೆಯ ಸುಗ್ಗಿಯು ತೀರಿದನಂತರವೂ ನಿಲ್ಲುವ ಉಳಿಗಾಯ ಹಾಗೆ ಭೂಮಂಡಲದಲ್ಲಿ ಜನಾಂಗಗಳೊಳಗೆ ಶೇಷವಿರುವದು.",
			"14": "<sup>14</sup> ಇವರು ಉಚ್ಚಧ್ವನಿಗೈಯುವರು; ಯೆಹೋವನ ಮಹಿಮೆಯನ್ನು ತಿಳಿದುಕೊಂಡವರಾಗಿ ಸಮುದ್ರದ ಕಡೆಯಿಂದ ಆರ್ಭಟಿಸುವರು -",
			"15": "<sup>15</sup> ಮೂಡಣವರೇ, ಯೆಹೋವನನ್ನು ಸನ್ಮಾನಿಸಿರಿ; ಕರಾವಳಿಯವರೇ, ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರಾದ ಯೆಹೋವನ ನಾಮವನ್ನು ಘನಪಡಿಸಿರಿ ಎಂದು ಕೂಗುವರು.",
			"16": "<sup>16</sup> ಸದ್ಧರ್ಮಿಗಳಿಗೆ ಮಾನವಾಗಲೆಂಬ ಗೀತಗಳು ಭೂಮಂಡಲದ ಕಟ್ಟಕಡೆಯಿಂದ ನಮಗೆ ಕೇಳಬಂದಿವೆ. ನಾನಾದರೋ - ಕ್ಷಯಿಸೇ ಕ್ಷಯಿಸುತ್ತೇನೆ, ನನ್ನ ಗತಿಯನ್ನು ಏನು ಹೇಳಲಿ! ಬಾಧಕರು ಬಾಧಿಸುತ್ತಾರೆ, ಹೌದು, ಬಾಧಕರು ಬಾಧಿಸೇ ಬಾಧಿಸುತ್ತಾರೆ ಎಂದುಕೊಂಡೆನು.",
			"17": "<sup>17</sup> ಭೂನಿವಾಸಿಯೇ, ಭಯವೂ ಗುಂಡಿಯೂ ಬಲೆಯೂ ನಿನಗೆ ಕಾದಿವೆ.",
			"18": "<sup>18</sup> ಭಯದ ಸಪ್ಪಳದಿಂದ ಓಡಿಹೋಗುವವನು ಗುಂಡಿಯಲ್ಲಿ ಬೀಳುವನು, ಗುಂಡಿಯನ್ನು ಹತ್ತಿಬರುವವನು ಬಲೆಗೆ ಸಿಕ್ಕುವನು. ನೋಡು, ಆಕಾಶದ ದ್ವಾರಗಳು ತೆರೆದಿವೆ, ಭೂಮಿಯ ಅಸ್ತಿವಾರಗಳು ಕಂಪಿಸುತ್ತಿವೆ.",
			"19": "<sup>19</sup> ಭೂಮಿಯು ಒಡೆದೇ ಇದೆ, ಬಿರಿದೇ ಬಿರಿದಿದೆ, ಕದಲಿಯೇ ಹೋಗಿದೆ.",
			"20": "<sup>20</sup> ಭೂಮಿಯು ಅಮಲೇರಿದವನ ಹಾಗೆ ಓಲಾಡುತ್ತದೆ, ಮಂಚಿಕೆಯಂತೆ ತೂಗಾಡುತ್ತದೆ; ಅದರ ದ್ರೋಹವು ಅದಕ್ಕೆ ಭಾರವಾಗಿದೆ, ಅದು ಬಿದ್ದು ಹೋಗುತ್ತಿದೆ, ತಿರಿಗಿ ಏಳುವದೇ ಇಲ್ಲ.",
			"21": "<sup>21</sup> ಆ ದಿನದಲ್ಲಿ ಯೆಹೋವನು ಆಕಾಶಮಂಡಲದ ದೂತಸೈನ್ಯವನ್ನೂ ಭೂಮಂಡಲದ ನೆಲದೊಡೆಯರನ್ನೂ ದಂಡಿಸುವನು.",
			"22": "<sup>22</sup> ಇವರು ಬಂದಿಗಳ ಗುಂಪಿನಂತೆ ನೆಲಮಾಳಿಗೆಯೊಳಕ್ಕೆ ಒಟ್ಟಿಗೆ ತಳ್ಳಿಸಿಕೊಂಡು ಅದರಲ್ಲಿ ಮುಚ್ಚಲ್ಪಟ್ಟಿದ್ದು ಬಹು ದಿನಗಳ ಮೇಲೆ ದಂಡನೆಗೆ ಗುರಿಯಾಗುವರು.",
			"23": "<sup>23</sup> ಮತ್ತು ಚಂದ್ರನು ನಾಚಿಕೆಪಡುವನು, ಸೂರ್ಯನು ಲಜ್ಜೆಗೊಳ್ಳುವನು; ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಚೀಯೋನ್ ಪರ್ವತದಲ್ಲಿ ಯೆರೂಸಲೇಮಿನೊಳಗೆ ಆಳುವನಷ್ಟೆ. ಆತನ [ಪರಿವಾರದ] ಹಿರಿಯರ ಮುಂದೆ ಪ್ರಭಾವವು ಪ್ರತ್ಯಕ್ಷವಾಗುವದು."
		},
		"25": {
			"1": "<sup>1</sup> ಯೆಹೋವನೇ, ನೀನೇ ನನ್ನ ದೇವರು; ನೀನು ಸತ್ಯಪ್ರಾಮಾಣಿಕತೆಗಳನ್ನು ಅನುಸರಿಸಿ ಆದಿಸಂಕಲ್ಪಗಳನ್ನು ನೆರವೇರಿಸುತ್ತಾ ಅದ್ಭುತಗಳನ್ನು ನಡಿಸಿದ ಕಾರಣ ನಿನ್ನನ್ನು ಕೊಂಡಾಡುವೆನು, ನಿನ್ನ ನಾಮವನ್ನು ಮಹಿಮೆಗೊಳಿಸುವೆನು.",
			"2": "<sup>2</sup> ನೀನು ದುರ್ಗವನ್ನು ನಾಶಪಡಿಸಿ ಪಟ್ಟಣವನ್ನು ಹಾಳು ದಿಬ್ಬವನ್ನಾಗಿಯೂ ಅನ್ಯರ ಕೋಟೆಯನ್ನು ಯಾರೂ ಎಂದಿಗೂ ಕಟ್ಟಬಾರದ ಹಾಳೂರನ್ನಾಗಿಯೂ ಮಾಡಿದ್ದೀ.",
			"3": "<sup>3</sup> ಆದಕಾರಣ ಬಲಿಷ್ಠವಾದ ಜನಾಂಗವು ನಿನ್ನನ್ನು ಘನಪಡಿಸುವದು, ಭಯಂಕರ ಜನರ ಪಟ್ಟಣವು ನಿನಗೆ ಅಂಜುವದು.",
			"4": "<sup>4</sup> ನೀನು ದೀನರಿಗೆ ಕೋಟೆ, ಇಕ್ಕಟ್ಟಿನಲ್ಲಿ ದರಿದ್ರರಿಗೆ ರಕ್ಷಣದುರ್ಗ, ಬಿಸಿಲಿಗೆ ನೆರಳು, ಭೀಕರರ ಶ್ವಾಸವು ಗೋಡೆಗೆ ಬಡಿದುಬಿಡುವ ಬಿರುಗಾಳಿಯಂತಿರುವಾಗ ಆಶ್ರಯವೂ ಆಗಿದ್ದೀ.",
			"5": "<sup>5</sup> [ಮೇಘವು] ಒಣನೆಲದ ಕಾವನ್ನು ಆರಿಸುವಂತೆ ನೀನು ಅನ್ಯರ ಗದ್ದಲವನ್ನು ಅಣಗಿಸುವಿ; ಮೋಡದ ನೆರಳಿನಿಂದ ಬಿಸಿಲು ಹೇಗೋ ಹಾಗೆಯೇ ನಿನ್ನಿಂದ ಭೀಕರರ ಉತ್ಸಾಹಗಾನವು ನಿಂತುಹೋಗುವದು.",
			"6": "<sup>6</sup> ಇದಲ್ಲದೆ ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಈ ಪರ್ವತದಲ್ಲಿ ಸಕಲಜನಾಂಗಗಳಿಗೂ ಸಾರವತ್ತಾದ ಮೃಷ್ಟಾನ್ನದಿಂದಲೂ ಮಡ್ಡಿಗಟ್ಟಿದ ಮೇಲೆ ಶೋಧಿಸಿದ ದ್ರಾಕ್ಷಾರಸದಿಂದಲೂ ಕೂಡಿದ ಔತಣವನ್ನು ಅಣಿಮಾಡುವನು.",
			"7": "<sup>7</sup> ಮತ್ತು ಸಮಸ್ತ ಜನಾಂಗಗಳನ್ನು ಮುಚ್ಚಿರುವ ಮುಸುಕನ್ನೂ ಸಕಲ ದೇಶೀಯರ ಮೇಲೆ ಹಾಕಿರುವ ತೆರೆಯನ್ನೂ ಇದೇ ಪರ್ವತದಲ್ಲಿ ನಾಶಮಾಡುವನು.",
			"8": "<sup>8</sup> ಮರಣವನ್ನು ಶಾಶ್ವತವಾಗಿ ನಿರ್ನಾಮ ಮಾಡುವನು; ಕರ್ತನಾದ ಯೆಹೋವನು ಎಲ್ಲರ ಮುಖದಲ್ಲಿನ ಕಣ್ಣೀರನ್ನು ಒರಸಿಬಿಡುವನು; ತನ್ನ ಪ್ರಜೆಯ ಅವಮಾನವನ್ನು ಭೂಮಂಡಲದಿಂದಲೇ ತೊಲಗಿಸುವನು; ಯೆಹೋವನೇ ಇದನ್ನು ನುಡಿದಿದ್ದಾನೆ.",
			"9": "<sup>9</sup> ಆ ದಿನದಲ್ಲಿ ಜನರು - ಆಹಾ, ಈತನೇ ನಮ್ಮ ದೇವರು, ನಮ್ಮನ್ನು ರಕ್ಷಿಸಲಿ ಎಂದು ಈತನನ್ನು ನಿರೀಕ್ಷಿಸಿಕೊಂಡಿದ್ದೇವೆ; ಈತನೇ ಯೆಹೋವನು, ಈತನನ್ನು ನಿರೀಕ್ಷಿಸಿಕೊಂಡಿದ್ದೇವೆ, ಈತನ ರಕ್ಷಣೆಯಲ್ಲಿ ಆನಂದಿಸಿ ಉಲ್ಲಾಸಪಡುವೆವು ಎಂದು ಹೇಳಿಕೊಳ್ಳುವರು.",
			"10": "<sup>10</sup> ಏಕೆಂದರೆ ಯೆಹೋವನ ಹಸ್ತವು ಈ ಪರ್ವತದಲ್ಲಿ ನೆಲೆಯಾಗಿರುವದು; ಮೋವಾಬಾದರೋ ತಿಪ್ಪೆಗುಂಡಿಯ ರೊಚ್ಚಿನಲ್ಲಿ ಒಣಹುಲ್ಲು ತುಳಿಯಲ್ಪಡುವ ಹಾಗೆ ತಾನಿದ್ದಲ್ಲೇ ತುಳಿಯಲ್ಪಡುವದು.",
			"11": "<sup>11</sup> ಈಜುವವನು ಕೈಯಾಡಿಸುವಂತೆ ಅದರಲ್ಲಿಯೇ ಕೈಯಾಡಿಸುವದು; [ಯೆಹೋವನು] ಅದರ ಗರ್ವವನ್ನೂ ಕೈಯ ಚಮತ್ಕಾರವನ್ನೂ ತಗ್ಗಿಸಿಬಿಡುವನು.",
			"12": "<sup>12</sup> ದುರ್ಗಮವಾಗಿಯೂ ಎತ್ತರವಾಗಿಯೂ ಇರುವ ನಿನ್ನ ಕೋಟೆಗಳನ್ನು ಆತನು ಕೆಡವಿ ತಗ್ಗಿಸಿ ನೆಲಸಮಮಾಡಿ ದೂಳಿಗೇ ತರುವನು."
		},
		"26": {
			"1": "<sup>1</sup> ಆ ದಿವಸದಲ್ಲಿ ಯೆಹೂದ ದೇಶದೊಳಗೆ ಈ ಗೀತವನ್ನು ಹಾಡುವರು - ನಮಗೆ ಬಲವಾದ ಪಟ್ಟಣವಿದೆ. [ಯೆಹೋವನು ತನ್ನ] ರಕ್ಷಣೆಯನ್ನು ಕೋಟೆಯನ್ನಾಗಿಯೂ ಹೊರಪೌಳಿಯನ್ನಾಗಿಯೂ ಮಾಡಿದ್ದಾನೆ.",
			"2": "<sup>2</sup> ಬಾಗಿಲುಗಳನ್ನು ತೆರೆಯಿರಿ! ಧರ್ಮಸತ್ಯಗಳನ್ನು ಕೈಕೊಳ್ಳುವ ಜನಾಂಗವು ಪ್ರವೇಶಿಸಲಿ!",
			"3": "<sup>3</sup> ಸ್ಥಿರಚಿತ್ತನನ್ನು ಶಾಂತಿಯಲ್ಲಿ ನೆಲೆಗೊಳಿಸಿ ಕಾಯುವಿ; ಅವನಿಗೆ ನಿನ್ನಲ್ಲಿ ಭರವಸವಿದೆ.",
			"4": "<sup>4</sup> ಯೆಹೋವನಲ್ಲಿ ಸದಾ ಭರವಸವಿಡಿರಿ; ಯಾಹುಯೆಹೋವನು ಶಾಶ್ವತವಾಗಿ ಆಶ್ರಯಗಿರಿಯಾಗಿದ್ದಾನೆ.",
			"5": "<sup>5</sup> ಆತನು ಎತ್ತರದಲ್ಲಿ ವಾಸಿಸುವವರನ್ನೂ [ಅವರ] ಉನ್ನತಪಟ್ಟಣವನ್ನೂ ತಗ್ಗಿಸಿದ್ದಾನೆ. ಅದನ್ನು ಕೆಡವಿ ನೆಲಸಮಮಾಡಿ ದೂಳಿಗೆ ತಂದಿದ್ದಾನೆ.",
			"6": "<sup>6</sup> ಅದು ಕಾಲತುಳಿತಕ್ಕೆ ಈಡಾಗಿದೆ. ದಿಕ್ಕಿಲ್ಲದ ಬಡವರೂ ಅದನ್ನು ಕಾಲಿನಿಂದ ತುಳಿದುಬಿಡುತ್ತಾರೆ.",
			"7": "<sup>7</sup> ಶಿಷ್ಟನ ಮಾರ್ಗವು ನೇರವಾಗಿದೆ; ನೀನು ಅವನ ದಾರಿಯನ್ನು ಸರಿಪಡಿಸಿ ಸಮಮಾಡುತ್ತಿ.",
			"8": "<sup>8</sup> ಹೌದು, ಯೆಹೋವನೇ, ನಿನ್ನ ನ್ಯಾಯಕಾರ್ಯಗಳು ತೋರತಕ್ಕ ಮಾರ್ಗದಲ್ಲಿ ನಿನ್ನನ್ನು ಕಾದುಕೊಂಡಿದ್ದೇವೆ; ನಿನ್ನ ನಾಮಸ್ಮರಣೆಯು ನಮ್ಮ ಆತ್ಮಕ್ಕೆ ಕೇವಲ ಇಷ್ಟವಾಗಿದೆ.",
			"9": "<sup>9</sup> ರಾತ್ರಿಯಲ್ಲಿ ನಿನ್ನನ್ನು ಮನಃಪೂರ್ವಕವಾಗಿ ಹಾರೈಸಿದ್ದೇವೆ, ಹೌದು, ನಮ್ಮ ಆತ್ಮದಲ್ಲಿ ನಿನ್ನನ್ನು ತವಕದಿಂದ ಹುಡುಕಿದ್ದೇವೆ. ನೀನು ಲೋಕದಲ್ಲಿ ನ್ಯಾಯಕಾರ್ಯಗಳನ್ನು ನಡಿಸುವಾಗ ಭೂನಿವಾಸಿಗಳು ಧರ್ಮಜ್ಞಾನವನ್ನು ಪಡೆದುಕೊಳ್ಳುವರು.",
			"10": "<sup>10</sup> ದುಷ್ಟರನ್ನು ಕರುಣಿಸಿದರೂ ಅವರು ಧರ್ಮಜ್ಞಾನವನ್ನು ಪಡೆಯರು; ಯೆಹೋವನ ಮಹಿಮೆಯನ್ನು ಲಕ್ಷಿಸದೆ ಯಥಾರ್ಥವಂತರ ದೇಶದಲ್ಲಿಯೂ ಅನ್ಯಾಯವನ್ನಾಚರಿಸುವರು.",
			"11": "<sup>11</sup> ಯೆಹೋವನೇ, ನೀನು ಕೈಯೆತ್ತಿದ್ದರೂ ಅವರು ಲಕ್ಷಿಸರು. ಆದರೆ ನಿನ್ನ ಸ್ವಜನಾಭಿಮಾನವನ್ನು ನೋಡಿ ನಾಚಿಕೆಪಡುವರು; ಹೌದು, ಆ ನಿನ್ನ ವಿರೋಧಿಗಳನ್ನು ಅಗ್ನಿಯು ದಹಿಸಿಬಿಡುವದು.",
			"12": "<sup>12</sup> ಯೆಹೋವನೇ, ನೀನು ನಮಗೆ ಸಮಾಧಾನವನ್ನು ವಿಧಿಸುವಿ, ನಾವು ನಡಿಸಿದ್ದೆಲ್ಲವೂ ನೀನು ನಮಗೋಸ್ಕರ ನಡಿಸಿದ್ದೇ ಸರಿ.",
			"13": "<sup>13</sup> ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನೇ, ನಿನ್ನ ಸ್ಥಾನದಲ್ಲಿ ಬೇರೆ ಒಡೆಯರು ನಮ್ಮ ಮೇಲೆ ದೊರೆತನಮಾಡುತ್ತಿದ್ದರು; ಈಗ ನಿನ್ನ ನಾಮವನ್ನು ಹೊಗಳುವದಕ್ಕೆ ನಿನ್ನಿಂದಲೇ ನಮಗೆ ಅವಕಾಶವಾಯಿತು.",
			"14": "<sup>14</sup> ನೀನು ಆ ಒಡೆಯರ ಮೇಲೆ ಕೈಮಾಡಿ ನಿರ್ಮೂಲಪಡಿಸಿ ಅವರ ಜ್ಞಾಪಕವನ್ನು ಅಳಿಸಿಬಿಟ್ಟಿದ್ದೀ; ಸತ್ತವರು ಪುನಃ ಬದುಕುವದಿಲ್ಲ, ಪ್ರೇತಗಳು ಎದ್ದು ಬರುವದಿಲ್ಲವಲ್ಲಾ.",
			"15": "<sup>15</sup> ಯೆಹೋವನೇ, ನಿನ್ನ ಜನವನ್ನು ಹೆಚ್ಚಿಸಿದ್ದಿ, ಹೌದು ನಿನ್ನ ಪ್ರಜೆಯನ್ನು ವೃದ್ಧಿಗೊಳಿಸಿದ್ದಿ; ನೀನು ದೇಶದ ಮೇರೆಗಳನ್ನೆಲ್ಲಾ ವಿಸ್ತರಿಸಿ ಮಹಿಮೆಗೊಂಡಿದ್ದಿ.",
			"16": "<sup>16</sup> ಯೆಹೋವನೇ, [ನಿನ್ನ ಜನರು] ಇಕ್ಕಟ್ಟಿಗೆ ಸಿಕ್ಕಿ ನಿನ್ನನ್ನು ಆಶ್ರಯಿಸಿದರು, ನಿನ್ನ ಶಿಕ್ಷೆಗೆ ಗುರಿಯಾಗಿ ಜಪಮಾಡಿದರು.",
			"17": "<sup>17</sup> ಹೆರಿಗೆ ಹತ್ತಿರವಾದ ಗರ್ಭಿಣಿಯು ಯಾತನೆಪಡುತ್ತಾ ಬೇನೆಯಿಂದ ಅರಚುವಂತೆ ಯೆಹೋವನೇ, ನಾವು ನಿನ್ನ ಮುಂದೆ ಅರಚಿದ್ದೇವೆ;",
			"18": "<sup>18</sup> ನಾವು ಗರ್ಭಧರಿಸಿ ವೇದನೆಪಟ್ಟು ಗಾಳಿಯನ್ನು ಹಡೆದಂತಾಯಿತು, ದೇಶಕ್ಕೆ ನಮ್ಮಿಂದ ಯಾವ ಉದ್ಧಾರವೂ ಆಗಲಿಲ್ಲ, ನಮ್ಮೊಳಗೆ ಯಾವ ಭೂನಿವಾಸಿಗಳೂ ಹುಟ್ಟಲಿಲ್ಲ.",
			"19": "<sup>19</sup> ಮೃತರಾದ ನಿನ್ನ ಜನರು ಬದುಕುವರು, ನನ್ನವರ ಹೆಣಗಳು ಜೀವದಿಂದೇಳುವವು, ಮಣ್ಣಿನಲ್ಲಿ ಪವಳಿಸಿರುವವರೇ, ಎಚ್ಚತ್ತು ಹರ್ಷಧ್ವನಿಗೈಯಿರಿ! [ಯೆಹೋವನೇ,] ನೀನು ಸುರಿಯುವ ಇಬ್ಬನಿಯು ಜ್ಯೋತಿರ್ಮಯವಾದದ್ದು, ಭೂಮಿಯು ಸತ್ತವರನ್ನು ಹೊರಪಡಿಸುವದು.",
			"20": "<sup>20</sup> ನನ್ನ ಜನರೇ, ಬನ್ನಿರಿ, ನಿಮ್ಮ ನಿಮ್ಮ ಕೋಣೆಗಳಲ್ಲಿ ಸೇರಿ ಬಾಗಿಲು ಮುಚ್ಚಿಕೊಳ್ಳಿರಿ; ದೈವರೋಷವು ತೀರುವ ತನಕ ಒಂದು ಕ್ಷಣ ಅವಿತುಕೊಳ್ಳಿರಿ.",
			"21": "<sup>21</sup> ಇಗೋ, ಯೆಹೋವನು ಭೂನಿವಾಸಿಗಳಿಗೆ ಅವರ ಪಾಪಫಲವನ್ನು ತಿನ್ನಿಸಬೇಕೆಂದು ತನ್ನ ಸ್ಥಳದಿಂದ ಹೊರಡುತ್ತಾನೆ; ಭೂಮಿಯು ತನ್ನಲ್ಲಿ ಇಂಗಿದ್ದ ರಕ್ತವನ್ನು ವ್ಯಕ್ತಗೊಳಿಸುವದು, ತನ್ನ ನಿವಾಸಿಗಳಲ್ಲಿ ಹತರಾಗಿದ್ದವರನ್ನು ಇನ್ನು ಮರೆಮಾಜದು."
		},
		"27": {
			"1": "<sup>1</sup> ಆ ದಿನದಲ್ಲಿ ಯೆಹೋವನು ಕಠಿನವೂ ಮಹತ್ತೂ ಬಲವೂ ಆಗಿರುವ ತನ್ನ ಖಡ್ಗದಿಂದ ವೇಗವಾಗಿ ಓಡುವ ಸರ್ಪ, ಡೊಂಕಾಗಿ ಹರಿಯುವ ಸರ್ಪ, ಈ ಎರಡು ಹೆಬ್ಬಾವುಗಳನ್ನು ಹೊಡೆದು ಮಹಾ ನದಿಯಲ್ಲಿನ ಘಟಸರ್ಪವನ್ನೂ ಕೊಂದುಹಾಕುವನು.",
			"2": "<sup>2</sup> ಆ ದಿನದಲ್ಲಿ [ಹೀಗೆ ಗಾನ ಮಾಡುವರು] - ಇಗೋ, ವಿಹಾರದ ತೋಟ, ಇದನ್ನು ಕುರಿತು ಹಾಡಿರಿ;",
			"3": "<sup>3</sup> ಯೆಹೋವನಾದ ನಾನು ತೋಟಗಾರನು, ಕ್ಷಣಕ್ಷಣವೂ ನೀರು ಹೊಯ್ಯುತ್ತಿದ್ದೇನೆ; ಯಾರೂ ಮುಟ್ಟದ ಹಾಗೆ ಹಗಲಿರುಳೂ ಕಾಯುತ್ತೇನೆ.",
			"4": "<sup>4</sup> ರೌದ್ರವು ನನ್ನಲಿಲ್ಲ; ಮುಳ್ಳುಗಿಳ್ಳು ನನಗೆ ಎದುರುಬಿದ್ದರೆ ಎಷ್ಟೋ ಒಳ್ಳೇದು! ಅವುಗಳಿಗೆ ವಿರುದ್ಧವಾಗಿ ಯುದ್ಧಕ್ಕೆ ನಡೆದು ಹೋಗಿ ಒಟ್ಟಿಗೆ ಸುಟ್ಟುಬಿಡುವೆನು.",
			"5": "<sup>5</sup> ಬೇಡವಾದರೆ ಆ ಶತ್ರುಗಳು ನನ್ನ ಬಲವನ್ನು ಶರಣುಹೊಂದಲಿ, ನನ್ನ ಸಂಗಡ ಸಮಾಧಾನಕ್ಕೆ ಬರಲಿ, ನನ್ನೊಡನೆ ಸಂಧಿಮಾಡಿಕೊಳ್ಳಲಿ.",
			"6": "<sup>6</sup> ಮುಂದಿನ ಕಾಲದಲ್ಲಿ ಯಾಕೋಬು ಬೇರೂರುವದು, ಇಸ್ರಾಯೇಲು ಹೂಬಿಟ್ಟು ಚಿಗುರುವದು, ಆ ವೃಕ್ಷವು ಭೂಮಂಡಲವನ್ನೆಲ್ಲಾ ಫಲದಿಂದ ತುಂಬಿಸುವದು.",
			"7": "<sup>7</sup> ಯೆಹೋವನು ತನ್ನ ಪ್ರಜೆಯ ಘಾತಕರನ್ನು ಹೊಡೆದಂತೆ ಆ ತನ್ನ ಪ್ರಜೆಯನ್ನು ಹೊಡೆದನೋ? ಅವರಿಗಾದಂಥ ಸಂಹಾರವು ಆ ಪ್ರಜೆಗೆ ಆಯಿತೋ?",
			"8": "<sup>8</sup> ನಿನ್ನ ಪ್ರಜೆಯನ್ನು ಕಳುಹಿಸಿಬಿಡುವದರ ಮೂಲಕ ಅದರೊಡನೆ ಮಿತಿಮೀರದೆ ಹೋರಾಡುತ್ತಿದ್ದೀ; ಮೂಡಣ ಗಾಳಿಯು ಬೀಸಿದ ದಿನದಲ್ಲಿ ಅದರ ಕ್ರೂರವಾದ ಬಡಿತದಿಂದ ನಿನ್ನ ಜನವನ್ನು ತೊಲಗಿಸಿದಿ.",
			"9": "<sup>9</sup> ಹೀಗಿರಲು ಯಾಕೋಬ್ಯರು ಯಜ್ಞವೇದಿಯ ಕಲ್ಲುಗಳನ್ನೆಲ್ಲಾ ಸುಣ್ಣದಂತೆ ಪುಡಿ ಪುಡಿಮಾಡಿ ಅಶೇರವೆಂಬ ವಿಗ್ರಹಸ್ತಂಭಗಳನ್ನೂ ಸೂರ್ಯಸ್ತಂಭಗಳನ್ನೂ ಇನ್ನು ಪ್ರತಿಷ್ಠಿಸದೆ ಹೋದರೆ ಅದೇ ಅವರ ಅಧರ್ಮಕ್ಕೆ ಪ್ರಾಯಶ್ಚಿತ್ತವಾಗುವದು, ಮತ್ತು ಅವರ ಪಾಪಪರಿಹಾರಕ್ಕೆ ಗುರುತಾದ ಪೂರ್ಣಫಲವೂ ಇದೇ.",
			"10": "<sup>10</sup> ಕೋಟೆಕೊತ್ತಲದ ಪಟ್ಟಣವು ಹಾಳಾಗಿ ಕಾಡಿನಂತೆ ಜನರಿಲ್ಲದೆ ಶೂನ್ಯನಿವಾಸಸ್ಥಾನವಾಗಿದೆ; ಅಲ್ಲಿ ಕರು ಮೇದು ಮಲಗುವದು, ಅಲ್ಲಿನ ಚಿಗುರುಗಳನ್ನು ತಿಂದುಬಿಡುವದು.",
			"11": "<sup>11</sup> ಅಲ್ಲಿನ ರೆಂಬೆಗಳು ಒಣಗಿ ಮುರಿದುಹೋಗುವವು, ಹೆಂಗಸರು ಬಂದು ಬೆಂಕಿ ಹಚ್ಚಿ ಉರಿಸುವರು; ಆ ಪ್ರಜೆಯು ಬುದ್ಧಿಹೀನವಾದದ್ದೇ ಸರಿ; ಆದಕಾರಣ ಸೃಷ್ಟಿಸಿದಾತನು ಅದನ್ನು ಕರುಣಿಸನು, ನಿರ್ಮಿಸಿದಾತನು ಅದಕ್ಕೆ ದಯೆತೋರಿಸನು.",
			"12": "<sup>12</sup> ಇಸ್ರಾಯೇಲ್ಯರೇ, ತುಂಬಿತುಳುಕುವ [ಯೂಫ್ರೇಟೀಸ್] ನದಿಯಿಂದ ಐಗುಪ್ತ ದೇಶದ ನದಿಯವರೆಗೆ ಯೆಹೋವನು ತೆನೆಗಳನ್ನು ಒಕ್ಕುವ ದಿನವು ಬರುತ್ತದೆ; ಆಗ ನಿನ್ನನ್ನು ಒಬ್ಬೊಬ್ಬರನ್ನಾಗಿ ಆರಿಸುವನು.",
			"13": "<sup>13</sup> ಆ ದಿನದಲ್ಲಿ ದೊಡ್ಡ ಕೊಂಬನ್ನೂದಲು ಅಶ್ಶೂರ ದೇಶದಲ್ಲಿ ಹಾಳಾದವರೂ ಐಗುಪ್ತ ಸೀಮೆಯಲ್ಲಿನ ದೇಶಭ್ರಷ್ಟರಾದವರೂ ಬಂದು ಪರಿಶುದ್ಧಪರ್ವತದ ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಯೆಹೋವನ ಮುಂದೆ ಅಡ್ಡಬೀಳುವರು."
		},
		"28": {
			"1": "<sup>1</sup> ಅಯ್ಯೋ, ಕುಡುಕರಾದ ಎಫ್ರಾಯೀಮ್ಯರ ಮಹಿಮೆಯ ಕಿರೀಟದ ಗತಿಯೇ! ದ್ರಾಕ್ಷಾರಸಕ್ಕೆ ಸೋತುಹೋದವರ ಫಲವತ್ತಾದ ತಗ್ಗಿಗೆ ಶ್ರೇಷ್ಠ ಶಿರೋಭೂಷಣವಾಗಿದ್ದು ಬಾಡುತ್ತಿರುವ ಹೂವಿನ ಪಾಡೇ!",
			"2": "<sup>2</sup> ಆಹಾ, ಕರ್ತನು ಒಬ್ಬ ಮಹಾ ಬಲಿಷ್ಠನನ್ನು ನೇಮಿಸಿದ್ದಾನೆ; ಅವನು ರಭಸವಾಗಿ ಸುರಿಯುವ ಕಲ್ಮಳೆಯಂತೆಯೂ ನಾಶಕವಾದ ಬಿರುಗಾಳಿಯೋಪಾದಿಯಲ್ಲಿಯೂ ಮುಣುಗಿಸುವ ಅತಿಧಾರಾವೃಷ್ಟಿಯ ಹಾಗೂ ಬಲಾತ್ಕಾರದಿಂದ [ಆ ಪಟ್ಟಣವನ್ನು] ನೆಲಕ್ಕೆ ಬೀಳಿಸುವನು.",
			"3": "<sup>3</sup> ಕುಡುಕರಾದ ಎಫ್ರಾಯೀಮ್ಯರ ಮಹಿಮೆಯ ಕಿರೀಟವು ಕಾಲಿನಿಂದ ತುಳಿಯಲ್ಪಡುವದು.",
			"4": "<sup>4</sup> ಫಲವತ್ತಾದ ತಗ್ಗಿಗೆ ಶ್ರೇಷ್ಠ ಶಿರೋಭೂಷಣವಾಗಿದ್ದು ಬಾಡುತ್ತಿರುವ ಹೂವು ಫಲಕಾಲಕ್ಕೆ ಮುಂಚೆ ಮಾಗಿದ ಅಂಜೂರಕ್ಕೆ ಸಮಾನವಾಗುವದು; ಆ ಹಣ್ಣನ್ನು ಕಂಡವನು ಕಿತ್ತ ಕೂಡಲೆ ನುಂಗಿಬಿಡುವನಲ್ಲವೆ.",
			"5": "<sup>5</sup> ಆ ದಿನದಲ್ಲಿ ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೇ ತನ್ನ ಜನರಲ್ಲಿ ಉಳಿದವರಿಗೆ ಅಂದದ ಕಿರೀಟವೂ ಸುಂದರ ಮುಕುಟವೂ ಆಗಿರುವನು;",
			"6": "<sup>6</sup> ಆತನೇ ನ್ಯಾಯಾಸನದಲ್ಲಿ ಕುಳಿತವನಿಗೆ ನ್ಯಾಯವಿವೇಚನೆಯಾಗಿಯೂ ಊರಬಾಗಿಲಲ್ಲಿ ಶತ್ರುಗಳನ್ನು ತಳ್ಳಿಬಿಡುವವರಿಗೆ ಶೌರ್ಯವಾಗಿಯೂ ಪರಿಣಮಿಸುವನು.",
			"7": "<sup>7</sup> ಈ [ನನ್ನ] ಜನರು ಸಹ ದ್ರಾಕ್ಷಾರಸದಿಂದ ಓಲಾಡುತ್ತಾರೆ, ಮದ್ಯದಿಂದ ತೂಗಾಡುತ್ತಾರೆ; ಯಾಜಕ ಪ್ರವಾದಿಗಳೂ ಮದ್ಯದಿಂದ ಓಲಾಡುತ್ತಾರೆ, ದ್ರಾಕ್ಷಾರಸವೇ ಅವರನ್ನು ನುಂಗಿಬಿಟ್ಟಿದೆ, ಮದ್ಯದಿಂದ ತೂಗಾಡುತ್ತಾರೆ; ದೈವದರ್ಶನವಾಗುತ್ತಿರುವಾಗಲೂ ಓಲಾಡುತ್ತಾರೆ, ನ್ಯಾಯತೀರಿಸುತ್ತಿರುವಾಗಲೂ ಅತ್ತಿತ್ತ ತೂಗಾಡುತ್ತಾರೆ.",
			"8": "<sup>8</sup> [ಅವರ] ಮೇಜುಗಳ ಮೇಲೆಲ್ಲಾ ವಾಂತಿಯೂ ಎಂಜಲೂ ತುಂಬಿವೆ, ಶುದ್ಧಸ್ಥಳವೇ ಇಲ್ಲ.",
			"9": "<sup>9</sup> [ಇವರು ಹರಟಿಕೊಳ್ಳುವದೇನಂದರೆ] - ಇವನು ಯಾರಿಗೆ ಜ್ಞಾನವನ್ನು ಬೋಧಿಸುತ್ತಾನೆ? ಇವನು ಯಾರಿಗೆ ಯೆಹೋವನ ಮಾತನ್ನು ತಿಳಿಯಪಡಿಸುತ್ತಾನೆ? ಮೊಲೆ ಬಿಟ್ಟ ಕೂಸುಗಳಿಗೋ? ಎದೆಬಿಟ್ಟ ಮಕ್ಕಳಿಗೋ?",
			"10": "<sup>10</sup> ಆಜ್ಞೆಯ ಮೇಲೆ ಆಜ್ಞೆ, ಆಜ್ಞೆಯ ಮೇಲೆ ಆಜ್ಞೆ, ಸೂತ್ರದ ಮೇಲೆ ಸೂತ್ರ, ಸೂತ್ರದ ಮೇಲೆ ಸೂತ್ರ, ಇಲ್ಲಿ ಸ್ವಲ್ಪ, ಅಲ್ಲಿ ಸ್ವಲ್ಪ [ಪ್ರಕಟಿಸುತ್ತಾನಲ್ಲಾ ಇತ್ಯಾದಿ].",
			"11": "<sup>11</sup> ಹೌದು, ತೊದಲು ಮಾತಿನವರು, ಅನ್ಯಭಾಷಿಗಳು, ಇವರ ಮೂಲಕವಾಗಿಯೇ ಯೆಹೋವನು ಈ ಜನರ ಮಧ್ಯದಲ್ಲಿ ಮಾತಾಡುವನು.",
			"12": "<sup>12</sup> ಆತನು ಮೊದಲು - ಇದೇ ನಿಮಗೆ ಆವಶ್ಯಕವಾದ ವಿಶ್ರಾಂತಿ, ಬಳಲಿದವರನ್ನು ವಿಶ್ರಮಗೊಳಿಸಿರಿ, ನಿಮಗೆ ಅನುಕೂಲವಾದ ಉಪಶಮನವು ಇದೇ ಎಂದು ಹೇಳಿದಾಗ ಇವರು ಕೇಳಲೊಲ್ಲದೆ ಹೋದರು.",
			"13": "<sup>13</sup> ಹೀಗಿರಲು ಯೆಹೋವನ ಮಾತು ಆಜ್ಞೆಯ ಮೇಲೆ ಆಜ್ಞೆ, ಆಜ್ಞೆಯ ಮೇಲೆ ಆಜ್ಞೆ, ಸೂತ್ರದ ಮೇಲೆ ಸೂತ್ರ, ಸೂತ್ರದ ಮೇಲೆ ಸೂತ್ರ, ಇಲ್ಲಿ ಸ್ವಲ್ಪ, ಅಲ್ಲಿ ಸ್ವಲ್ಪ ಎಂಬದಾಗಿ ಇವರಿಗೆ ಪರಿಣಮಿಸುವದು; ಇವರು ನಡೆದು ಹಿಂದೆಬಿದ್ದು ಭಂಗಪಡುವರು, ಬೋನಿಗೆ ಸಿಕ್ಕಿ ವಶವಾಗುವರು.",
			"14": "<sup>14</sup> ಆದಕಾರಣ ಯೆರೂಸಲೇಮಿನ ಈ ಜನರನ್ನಾಳುವ ಧರ್ಮನಿಂದಕರೇ, ಯೆಹೋವನ ಮಾತನ್ನು ಕೇಳಿರಿ,",
			"15": "<sup>15</sup> ನೀವು ನಿಮ್ಮೊಳಗೆ - ಮೃತ್ಯುವಿನಿಂದ ಒಡಂಬಡಿಕೆಯನ್ನು ಪಡೆದುಕೊಂಡು ಪಾತಾಳದೊಡನೆ ಒಪ್ಪಂದ ಮಾಡಿಕೊಂಡಿದ್ದೇವೆ; ವಿಪರೀತ ಬಾಧೆಯು ದೇಶದೊಳಗೆ ಹಾದುಹೋಗುವಾಗ ಅದು ನಮ್ಮನ್ನು ಮುಟ್ಟದು; ನಾವು ಅಸತ್ಯವನ್ನು ಆಶ್ರಯಿಸಿಕೊಂಡು ಮೋಸವನ್ನು ಮರೆ ಹೊಕ್ಕಿದ್ದೇವಲ್ಲಾ ಅಂದುಕೊಂಡದರಿಂದ",
			"16": "<sup>16</sup> ಕರ್ತನಾದ ಯೆಹೋವನು ಹೀಗೆ ಹೇಳುತ್ತಾನೆ - ಇಗೋ, ಪರೀಕ್ಷಿತವಾಗಿಯೂ ಮಾನ್ಯವಾಗಿಯೂ ಇರುವ ಮೂಲೆಗಲ್ಲನ್ನು ಚೀಯೋನಿನಲ್ಲಿ ಸ್ಥಿರವಾದ ಅಸ್ತಿವಾರವನ್ನಾಗಿ ಇಡುತ್ತೇನೆ; ಭರವಸವಿಡುವವನು ಆತುರಪಡನು.",
			"17": "<sup>17</sup> ನಾನು ನ್ಯಾಯವನ್ನು ನೂಲನ್ನಾಗಿಯೂ ಧರ್ಮವನ್ನು ಮಟ್ಟಗೋಲನ್ನಾಗಿಯೂ ಮಾಡುವೆನು, ಕಲ್ಮಳೆಯು ಅಸತ್ಯದ ಆಶ್ರಯವನ್ನು ಬಡಿದುಕೊಂಡು ಹೋಗುವದು, ಜಲಪ್ರವಾಹವು [ಮೋಸದ] ಮರೆಯನ್ನು ಮುಣುಗಿಸುವದು.",
			"18": "<sup>18</sup> ಮೃತ್ಯುವಿನಿಂದ ನೀವು ಪಡೆದುಕೊಂಡ ಒಡಂಬಡಿಕೆಯು ಸಾಗದು, ಪಾತಾಳದೊಡನೆ ನೀವು ಮಾಡಿಕೊಂಡ ಒಪ್ಪಂದವು ನಿಲ್ಲದು; ವಿಪರೀತ ಬಾಧೆಯು ದೇಶದೊಳಗೆ ಹಾದುಹೋಗುವಾಗ ನಿಮ್ಮನ್ನು ತುಳಿದುಬಿಡುವದು.",
			"19": "<sup>19</sup> ಅದು ಹಾದುಹೋಗುವಾಗೆಲ್ಲಾ ನಿಮ್ಮನ್ನು ಹಿಡಿಯುವದು; ಬೆಳಬೆಳಗೂ ಹಗಲುರಾತ್ರಿಯೂ ಅದು ಹಾದುಹೋಗುವದು; ಆಗ ಯೆಹೋವನ ಮಾತನ್ನು ಗ್ರಹಿಸಿಕೊಳ್ಳುವದು ಬರೀ ಭಯವೇ.",
			"20": "<sup>20</sup> ಒಬ್ಬನು ಚಾಚಿಕೊಂಡು ಮಲಗೇನಂದರೆ ಹಾಸಿಗೆಯ ಉದ್ದ ಸಾಲದು; ಮುದುರಿಕೊಂಡು ಮಲಗೇನಂದರೆ ಹೊದಿಕೆಯ ಅಗಲ ಸಾಲದು.",
			"21": "<sup>21</sup> ಯೆಹೋವನು ಈಗ ಅಪರೂಪವಾದ ತನ್ನ ಕೆಲಸವನ್ನು ನಡಿಸಬೇಕೆಂತಲೂ ಅಪೂರ್ವವಾದ ತನ್ನ ಕಾರ್ಯವನ್ನು ನೆರವೇರಿಸಬೇಕೆಂತಲೂ ಪೆರಾಚೀಮ್ ಬೆಟ್ಟದಲ್ಲಿ ಎದ್ದಂತೆ ಏಳುವನು, ಗಿಬ್ಯೋನ್ ತಗ್ಗಿನಲ್ಲಿ ರೋಷಗೊಂಡಂತೆ ರೋಷಗೊಳ್ಳುವನು.",
			"22": "<sup>22</sup> ಆದಕಾರಣ ನಿಮ್ಮ ಬಂಧನಗಳು ಇನ್ನೂ ಬಿಗಿಯಾಗದಂತೆ ಧರ್ಮನಿಂದೆಯನ್ನು ಬಿಡಿರಿ; ಭೂಮಂಡಲದಲ್ಲೆಲ್ಲಾ ನಿಶ್ಚಿತಪ್ರಲಯವಾಗಲಿ ಎಂದು ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೆಂಬ ಕರ್ತನು ನುಡಿಯುವದನ್ನು ಕೇಳಿದ್ದೇನೆ.",
			"23": "<sup>23</sup> ನನ್ನ ಧ್ವನಿಗೆ ಕಿವಿಗೊಟ್ಟು ಕೇಳಿರಿ, ನನ್ನ ಮಾತಿಗೆ ಗಮನವಿಟ್ಟು ಆಲಿಸಿರಿ;",
			"24": "<sup>24</sup> ಬಿತ್ತನೆಗಾಗಿ ಉಳುವವನು ಸದಾ ಉಳುತ್ತಿರುವನೋ? [ಪ್ರತಿನಿತ್ಯವೂ] ಮಣ್ಣನ್ನು ಕೆಳಮೇಲು ಮಾಡುತ್ತಾ ಕುಂಟೆಹೊಡೆಯುವನೋ?",
			"25": "<sup>25</sup> ಅಂತು ಭೂಮಿಯನ್ನು ಹಸನುಮಾಡಿದ ಮೇಲೆ ಕರಿಯ ಜೀರಿಗೆಯನ್ನು ಚೆಲ್ಲಿ ಜೀರಿಗೆಯನ್ನು ಬಿತ್ತಿ ಗೋದಿಯನ್ನು ಸಾಲು ಸಾಲಾಗಿಯೂ ಜವೆಗೋದಿಯನ್ನು ತಕ್ಕ ಸ್ಥಳದಲ್ಲಿಯೂ ಕಡಲೆಯನ್ನು ಅಂಚಿನಲ್ಲಿಯೂ ಹಾಕುವನಲ್ಲವೆ.",
			"26": "<sup>26</sup> ಅವನ ದೇವರು ಇದನ್ನೆಲ್ಲಾ ಅವನಿಗೆ ಸರಿಯಾಗಿ ಕಲಿಸಿ ತಿದ್ದುತ್ತಾನೆ.",
			"27": "<sup>27</sup> ಆದಕಾರಣ ಅವನು ಕರಿಯ ಜೀರಿಗೆಯನ್ನು ಒಕ್ಕುವದು ಹಲಿವೆಯಿಂದಲ್ಲ, ಕೋಲಿನಿಂದಲೇ; ಜೀರಿಗೆಯನ್ನು ಒಕ್ಕುವದು ಕಣದ ಗುಂಡಿನಿಂದಲ್ಲ, ದೊಣ್ಣೆಯಿಂದಲೇ.",
			"28": "<sup>28</sup> ಗೋದಿಯ ಕಾಳನ್ನು ನುಚ್ಚು ನುಚ್ಚು ಮಾಡುವನೋ? ತನ್ನ ಕುದುರೆಗಳನ್ನು ಹೊಡೆಯುತ್ತಾ ಗುಂಡನ್ನು ಗಿರ್ರನೆ ಉರುಳಿಸುತ್ತಾ ಯಾವಾಗಲೂ ಒಕ್ಕುತ್ತಿರುವದಿಲ್ಲ; ನುಚ್ಚು ನುಚ್ಚು ಮಾಡನು.",
			"29": "<sup>29</sup> ಈ ವಿವೇಕವು ಸಹ ಅತಿಶಯಾಲೋಚನಾಪರನೂ ಸುಜ್ಞಾನಶ್ರೇಷ್ಠನೂ ಆಗಿರುವ ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನಿಂದಲೇ ಉಂಟಾಗುತ್ತದೆ."
		},
		"29": {
			"1": "<sup>1</sup> ಅಯ್ಯೋ, ಅರೀಯೇಲೇ, ಅರೀಯೇಲೇ, ದಾವೀದನು ಸೈನ್ಯಸಮೇತನಾಗಿ ವಾಸಿಸಿದ ಪಟ್ಟಣವೇ, ನಿನ್ನ ಗತಿಯನ್ನು ಏನು ಹೇಳಲಿ! ಈ ವರುಷಕ್ಕೆ ಮುಂದಿನ ವರುಷವನ್ನು ಸೇರಿಸಿರಿ; ಹಬ್ಬಗಳು ಸುತ್ತಿಬರಲಿ; ಆಮೇಲೆ ನಾನು ಅರೀಯೇಲನ್ನು ಬಾಧಿಸುವೆನು; ಅರಿಚಾಟ ಕಿರಿಚಾಟವಾಗುವದು;",
			"2": "<sup>2</sup> ಆ ಪಟ್ಟಣವು ನನಗೆ ಅರೀಯೇಲಾಗಿಯೇ ಪರಿಣಮಿಸುವದು.",
			"3": "<sup>3</sup> ನಾನು ನಿನ್ನ ಸುತ್ತಲು ದಂಡಿಳಿಸಿ ನಿನಗೆ ವಿರುದ್ಧವಾಗಿ ಕೊತ್ತಲಕಟ್ಟಿ ದಿಬ್ಬಹಾಕಿ ನಿನ್ನನ್ನು ಮುತ್ತುವೆನು.",
			"4": "<sup>4</sup> ನೀನು ಕುಗ್ಗಿ ಭೂಮಿಯೊಳಗಿಂದ ಮಾತಾಡುವಿ, ನಿನ್ನ ನುಡಿಯು ಮಣ್ಣಿನೊಳಗಿಂದ ಸಣ್ಣಸ್ವರವಾಗಿ ಹೊರಡುವದು. ನಿನ್ನ ಧ್ವನಿಯು ಪ್ರೇತದ ಧ್ವನಿಯಂತೆ ನೆಲದೊಳಗಿಂದ ಬರುವದು, ನಿನ್ನ ನುಡಿಯು ದೂಳಿನೊಳಗಿಂದ ಲೊಚಗುಟ್ಟುವದು.",
			"5": "<sup>5</sup> ಗುಂಪುಗುಂಪಾಗಿ ಕೂಡಿರುವ ನಿನ್ನ ಶತ್ರುಗಳು ಸೂಕ್ಷ್ಮ ದೂಳಿಯಂತೆಯೂ ತಂಡೋಪತಂಡವಾಗಿ ಬಂದಿರುವ ಭಯಂಕರರು ಹಾರಿಹೋಗುವ ಹೊಟ್ಟಿನ ಹಾಗೂ ಆಗುವರು; ತಟ್ಟನೆ ಕ್ಷಣದಲ್ಲಿ ಲಯಹೊಂದುವರು.",
			"6": "<sup>6</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಗುಡುಗು, ಭೂಕಂಪ, ಮಹಾಶಬ್ದ, ಬಿರುಗಾಳಿ, ಚಂಡಮಾರುತ, ದಹಿಸುವ ಅಗ್ನಿಯ ಜ್ವಾಲೆ ಇವುಗಳ ಮೂಲಕ ಆ ನಗರಿಯನ್ನು ರಕ್ಷಿಸುವನು.",
			"7": "<sup>7</sup> ಅರೀಯೇಲಿನ ಮೇಲೆ ಹೋರಾಡಿ ಅದಕ್ಕೂ ಅದರ ಕೋಟೆಗೂ ವಿರುದ್ಧವಾಗಿ ಯುದ್ಧಮಾಡಿ ಬಾಧಿಸುವ ಸಕಲ ಜನಾಂಗಗಳ ಗುಂಪು ಕನಸಿನಂತೆ, ರಾತ್ರಿಯ ಸ್ವಪ್ನದ ಹಾಗೆ, ಮಾಯವಾಗುವದು.",
			"8": "<sup>8</sup> ಹಸಿದವನು ಕನಸು ಕಂಡು ಆಹಾ, ಉಣ್ಣುತ್ತೇನೆ ಎಂದು ಕೊಂಡಂತಾಗುವದು; ಎಚ್ಚತ್ತಾಗ ಅವನ ಹೊಟ್ಟೆ ಬರಿದೇ. ಬಾಯಾರಿದವನು ಸ್ವಪ್ನದಲ್ಲಿ ಇಗೋ ಕುಡಿಯುತ್ತೇನೆ ಎಂದುಕೊಂಡ ಹಾಗೂ ಆಗುವದು; ನಿದ್ರೆ ತಿಳಿದಾಗ ಬಳಲಿ ನೀರನ್ನು ಬಯಸುವನು. ಚೀಯೋನ್ ಪರ್ವತಕ್ಕೆ ವಿರುದ್ಧವಾಗಿ ಹೋರಾಡುವ ಸಕಲಜನಾಂಗಗಳ ಗುಂಪಿಗೆ ಈ ಗತಿಯಾಗುವದು.",
			"9": "<sup>9</sup> ನಿಮ್ಮನ್ನು ನೀವೇ ಬೆರಗುಮಾಡಿಕೊಂಡು ಬೆರಗಾಗಿರಿ, ಕುರುಡುಮಾಡಿಕೊಂಡು ಕುರುಡರಾಗಿರಿ; ಇವರು ಅಮಲೇರಿದ್ದಾರೆ, ದ್ರಾಕ್ಷಾರಸದಿಂದಲ್ಲ; ಓಲಾಡುತ್ತಾರೆ, ಮದ್ಯದಿಂದಲ್ಲ.",
			"10": "<sup>10</sup> ಯೆಹೋವನು ನಿಮ್ಮ ಮೇಲೆ ಗಾಢನಿದ್ರೆಯನ್ನು ಬರಮಾಡಿ ಪ್ರವಾದಿಗಳೆಂಬ ಕಣ್ಣುಗಳನ್ನು ಮುಚ್ಚಿ ದಿವ್ಯದರ್ಶಿಗಳೆಂಬ ತಲೆಗಳಿಗೆ ಮುಸುಕು ಹಾಕಿದ್ದಾನಷ್ಟೆ.",
			"11": "<sup>11</sup> ದೈವದರ್ಶನವೆಲ್ಲಾ ಮುದ್ರೆಹಾಕಿದ ಶಾಸ್ತ್ರದ ಮಾತಿನ ಹಾಗಿದೆ; ಅದನ್ನು ಅಕ್ಷರಬಲ್ಲವನಿಗೆ - ದಯಮಾಡಿ ಓದೆಂದು ಒಪ್ಪಿಸಿದರೆ ಅವನು - ಮುದ್ರೆಹಾಕಿದೆಯಲ್ಲಾ, ಆಗುವದಿಲ್ಲ ಅನ್ನುವನು;",
			"12": "<sup>12</sup> ಅಕ್ಷರವಿಲ್ಲದವನಿಗೆ - ದಯವಿಟ್ಟು ಓದೆಂದು ಒಪ್ಪಿಸಿದರೆ ಅವನು - ನನಗೆ ವಿದ್ಯೆಯಿಲ್ಲ ಅನ್ನುವನು.",
			"13": "<sup>13</sup> ಮತ್ತು ಯೆಹೋವನು - ಈ ಜನರು ಮಾತಿನಿಂದ ನನ್ನನ್ನು ಸಮೀಪಿಸಿ ತುಟಿಯಿಂದ ನನ್ನನ್ನು ಸನ್ಮಾನಿಸುತ್ತಾರೆ, ಮನಸ್ಸನ್ನೋ ದೂರಮಾಡಿಕೊಂಡು ಬಾಯಿಪಾಠವಾಗಿ ಕಲಿತಿರುವ ಮನುಷ್ಯರ ಆಜ್ಞೆಗೆ ಸರಿಯಾದ ಭಯಭಕ್ತಿಯನ್ನು ಮಾತ್ರ ನನ್ನಲ್ಲಿಟ್ಟಿದ್ದಾರೆ;",
			"14": "<sup>14</sup> ಹೀಗಿರುವದರಿಂದ ನಾನು ಈ ಜನರ ಮಧ್ಯದಲ್ಲಿ ಅಧಿಕಾಶ್ಚರ್ಯವೂ ಅದ್ಭುತವೂ ಆದ ಕಾರ್ಯವನ್ನು ಇನ್ನು ಮಾಡುವೆನು; ಇವರ ಜ್ಞಾನಿಗಳ ಜ್ಞಾನವು ಅಳಿಯುವದು, ವಿವೇಕಿಗಳ ವಿವೇಕವು ಅಡಗುವದು ಎಂದು ಹೇಳಿದನು.",
			"15": "<sup>15</sup> ತಮ್ಮ ಆಲೋಚನೆಯನ್ನು ಯೆಹೋವನಿಗೆ ಮರೆಮಾಜುವದಕ್ಕೆ ಅಗಾಧೋಪಾಯ ಮಾಡಿ ನಮ್ಮನ್ನು ಯಾರು ನೋಡಿಯಾರು, ಯಾರು ತಿಳಿದಾರು ಅಂದುಕೊಂಡು ಕತ್ತಲಲ್ಲೇ ತಮ್ಮ ಕೆಲಸಗಳನ್ನು ನಡಿಸುವವರ ಗತಿಯನ್ನು ಏನು ಹೇಳಲಿ!",
			"16": "<sup>16</sup> ಅಯ್ಯೋ, ನೀವು ಎಂಥಾ ಮೂರ್ಖರು! ಕುಂಬಾರನು ಮಣ್ಣೆನಿಸಿಕೊಂಡಾನೇ? ಕಾರ್ಯವು ಕರ್ತೃವನ್ನು ಕುರಿತು ಅವನು ನನ್ನನ್ನು ಮಾಡಲಿಲ್ಲ ಎಂದುಕೊಂಡೀತೇ? ನಿರ್ಮಿತವಾದದ್ದು ನಿರ್ಮಿಸಿದವನ ವಿಷಯವಾಗಿ ಅವನಿಗೆ ವಿವೇಕವಿಲ್ಲ ಎಂದೀತೇ?",
			"17": "<sup>17</sup> ಅತ್ಯಲ್ಪಕಾಲದೊಳಗೆ ಲೆಬನೋನ್ ಅರಣ್ಯವು ತೋಟವಾಗುವದು, [ಈಗಿನ] ತೋಟವು [ಆಗಿನವರಿಗೆ] ಅರಣ್ಯವಾಗಿ ಕಾಣಿಸುವದು.",
			"18": "<sup>18</sup> ಆ ದಿನದಲ್ಲಿ ಕಿವುಡರು ಶಾಸ್ತ್ರದ ಮಾತುಗಳನ್ನು ಕೇಳುವರು, ಮೊಬ್ಬುಕತ್ತಲುಗಳಲ್ಲಿಯೂ ಕುರುಡರ ಕಣ್ಣು ಕಾಣುವದು.",
			"19": "<sup>19</sup> ದೀನರು ಯೆಹೋವನಲ್ಲಿ ಹೆಚ್ಚುಹೆಚ್ಚಾಗಿ ಆನಂದಿಸುವರು, ಬಡವರು ಇಸ್ರಾಯೇಲ್ಯರ ಸದಮಲಸ್ವಾಮಿಯಲ್ಲಿ ಉಲ್ಲಾಸಿಸುವರು.",
			"20": "<sup>20</sup> ಏಕಂದರೆ ಭಯಂಕರರು ನಿಶ್ಶೇಷವಾಗುವರು, ಧರ್ಮನಿಂದಕರು ನಿರ್ನಾಮವಾಗುವರು,",
			"21": "<sup>21</sup> ಅಪಸಾಕ್ಷಿಯಿಂದ ತಪ್ಪು ಹೊರಿಸುವವರೂ ಚಾವಡಿಯಲ್ಲಿ ದೋಷವನ್ನು ಖಂಡಿಸುವವನಿಗೆ ಉರುಲೊಡ್ಡುವವರೂ ನ್ಯಾಯವಂತನ ನ್ಯಾಯವನ್ನು ಸುಮ್ಮಸುಮ್ಮನೆ ತಪ್ಪಿಸುವವರೂ ಆಗಿರುವ ಅಧರ್ಮನಿರತರೆಲ್ಲಾ ನಿರ್ಮೂಲರಾಗುವರು.",
			"22": "<sup>22</sup> ಹೀಗಿರಲು ಅಬ್ರಹಾಮನನ್ನು ಉದ್ಧರಿಸಿದ ಯೆಹೋವನು ಯಾಕೋಬನ ಮನೆತನದ ವಿಷಯವಾಗಿ - ಯಾಕೋಬ್ಯರು ಇನ್ನು ನಾಚಿಕೆಗೆ ಈಡಾಗರು, ಅವರ ಮುಖವು ಇನ್ನು ಬಾಡದು,",
			"23": "<sup>23</sup> ಯಾಕೋಬ ವಂಶದವರು ತಮ್ಮ ಮಧ್ಯದಲ್ಲಿ ನಾನು ನಡಿಸುವ ಕೆಲಸವನ್ನು ನೋಡಿ ನನ್ನ ನಾಮವೇ ಆರಾಧನೆಗೆ ಅರ್ಹವೆಂದು ಪ್ರತಿಷ್ಠಿಸುವರು; ಹೌದು, ಯಾಕೋಬ್ಯರ ಸದಮಲಸ್ವಾಮಿಯನ್ನು ಪ್ರತಿಷ್ಠೆಪಡಿಸಿ ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರಿಗೆ ನಡುಗುವರು.",
			"24": "<sup>24</sup> ತಪ್ಪಿದ ಹೃದಯವುಳ್ಳವರೆಲ್ಲರೂ ವಿವೇಕಿಗಳಾಗುವರು, ಗುಣುಗುಟ್ಟುವವರು ಉಪದೇಶವನ್ನು ಗಮನಿಸುವರು ಎಂದು ಹೇಳುತ್ತಾನೆ."
		},
		"30": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಹೀಗೆ ನುಡಿಯುತ್ತಾನೆ :- ದ್ರೋಹಿಗಳಾದ [ನನ್ನ] ಮಕ್ಕಳ ಗತಿಯನ್ನು ಏನು ಹೇಳಲಿ! ಇವರು ನನ್ನನ್ನು ಕೇಳದೆ ಒಂದು ಆಲೋಚನೆಯನ್ನು ಸಾಗಿಸಿ ನನ್ನ ಆತ್ಮಪ್ರೇರಿತರಾಗದೆ ಉಪಾಯವನ್ನು ನೆಯ್ದು ಪಾಪದ ಮೇಲೆ ಪಾಪವನ್ನು ಸೇರಿಸಿಕೊಂಡಿದ್ದಾರೆ;",
			"2": "<sup>2</sup> ಫರೋಹನ ಆಶ್ರಯವನ್ನು ಪಡೆದು ಐಗುಪ್ತವನ್ನು ಮರೆಹೊಗಬೇಕೆಂದು ನನ್ನ ಮಾತನ್ನು ಕೇಳದೆ ಐಗುಪ್ತಕ್ಕೆ ಪ್ರಯಾಣವಾಗಿ ಹೊರಟಿದ್ದಾರೆ.",
			"3": "<sup>3</sup> ಆದಕಾರಣ ಫರೋಹನ ಆಶ್ರಯದಿಂದ ನಿಮಗೆ ನಾಚಿಕೆ, ಐಗುಪ್ತವನ್ನು ಮರೆಹೊಗುವದರಿಂದ ಅವಮಾನ.",
			"4": "<sup>4</sup> ಆ ಫರೋಹನ ಸಾಮಂತ ರಾಜರು ಚೋವನಿನಲ್ಲಿದ್ದರೂ ಅವನ ದೂತರು ಹಾನೇಸಿನ ತನಕ ಸೇರಿದರೂ",
			"5": "<sup>5</sup> ಸಹಾಯವನ್ನೂ ಪ್ರಯೋಜನವನ್ನೂ ಮಾಡಲಾರದೆ ನಾಚಿಕೆಯನ್ನೂ ಅವಮಾನವನ್ನೂ ಉಂಟುಮಾಡುವ ವ್ಯರ್ಥವಾದ ಈ ಜನಾಂಗದ ವಿಷಯವಾಗಿ ಎಲ್ಲರೂ ಲಜ್ಜೆಪಡುವರು.",
			"6": "<sup>6</sup> ದಕ್ಷಿಣ ಸೀಮೆಯ ನೀರಾನೆಯ ವಿಷಯವಾದ ದೈವೋಕ್ತಿ. [ರಾಯಭಾರಿಗಳು] ಕತ್ತೆಗಳ ಬೆನ್ನುಗಳ ಮೇಲೆ ತಮ್ಮ ಧನವನ್ನೂ ಒಂಟೆಗಳ ಡುಬ್ಬಗಳ ಮೇಲೆ ತಮ್ಮ ದ್ರವ್ಯವನ್ನೂ ಹೊರಿಸಿಕೊಂಡು ಮೃಗೇಂದ್ರ, ಸಿಂಹ, ವಿಷಸರ್ಪ, ಹಾರುವ ಉರಿಮಂಡಲ ಇವುಗಳಿಂದ ಭಯಂಕರವಾಗಿಯೂ ಶ್ರಮಸಂಕಟಗಳನ್ನು ಉಂಟುಮಾಡುವದಾಗಿಯೂ ಇರುವ ದೇಶದ ಮಾರ್ಗವಾಗಿ ನಿಷ್ಪ್ರಯೋಜನವಾದ ಜನಾಂಗದ ಬಳಿಗೆ ಹೋಗುತ್ತಾರೆ.",
			"7": "<sup>7</sup> ಐಗುಪ್ತದ ಸಹಾಯವು ವ್ಯರ್ಥವೇ ವ್ಯರ್ಥ; ಆದದರಿಂದ ನಾನು ಅದಕ್ಕೆ ಸುಮ್ಮನೆ ಬಿದ್ದಿರುವ ಜಂಬದ ಮೃಗವೆಂದು ಹೆಸರಿಟ್ಟಿದ್ದೇನೆ.",
			"8": "<sup>8</sup> [ಯೆಹೋವನು ನನಗೆ ಹೀಗೆ ಹೇಳಿದನು] - ನೀನೀಗ ಮನೆಗೆ ಹೋಗಿ ಈ ಮಾತು ಮುಂದಿನ ಕಾಲದಲ್ಲಿ ಶಾಶ್ವತ ಸಾಕ್ಷಿಯಾಗಿರುವಂತೆ ಇವರೆದುರಿಗೆ ಹಲಿಗೆಯ ಮೇಲೆ ಕೆತ್ತು, ಪುಸ್ತಕದಲ್ಲಿ ಬರೆ.",
			"9": "<sup>9</sup> ಇವರು ದ್ರೋಹದ ಜನಾಂಗದವರು, ಮೋಸದ ಸಂತಾನದವರು, ಯೆಹೋವನ ಉಪದೇಶವನ್ನು ಕೇಳಲೊಲ್ಲದ ಸಂತತಿಯವರಷ್ಟೆ;",
			"10": "<sup>10</sup> ಇವರು ದಿವ್ಯದರ್ಶಿಗಳನ್ನು ಕುರಿತು - ನಿಮಗೆ ದರ್ಶನವಾಗದಿರಲಿ ಅನ್ನುತ್ತಾರೆ; ಮತ್ತು ಸಾಕ್ಷಾತ್ಕಾರಿಗಳಿಗೆ - ನಮಗಾಗಿ ನ್ಯಾಯವಾದವುಗಳನ್ನು ಸಾಕ್ಷಾತ್ಕರಿಸಬೇಡಿರಿ, ನಯವಾದವುಗಳನ್ನು ನಮಗೆ ನುಡಿಯಿರಿ, ಮಾಯವಾದವುಗಳನ್ನೇ ಸಾಕ್ಷಾತ್ಕರಿಸಿರಿ;",
			"11": "<sup>11</sup> [ನೀವು ಹಿಡಿದಿರುವ] ಮಾರ್ಗದಿಂದ ತೊಲಗಿರಿ; ನಿಮ್ಮ ದಾರಿಗೆ ಓರೆಯಾಗಿರಿ; ಇಸ್ರಾಯೇಲ್ಯರ ಸದಮಲಸ್ವಾಮಿಯನ್ನು ನಮ್ಮ ಮುಂದೆ ನಿಲ್ಲದ ಹಾಗೆ ಮಾಡಿರಿ ಎಂದು ಹೇಳುತ್ತಾರೆ.",
			"12": "<sup>12</sup> ಆದಕಾರಣ ಇಸ್ರಾಯೇಲ್ಯರ ಸದಮಲಸ್ವಾಮಿಯು ಹೀಗನ್ನುತ್ತಾನೆ - ನೀವು ಈ ಮಾತನ್ನು ಅಸಡ್ಡೆಮಾಡಿ ಬಲಾತ್ಕಾರಕುಯುಕ್ತಿಗಳನ್ನು ನಂಬಿ ಅವುಗಳನ್ನೇ ಆಧಾರಮಾಡಿಕೊಂಡದರಿಂದ",
			"13": "<sup>13</sup> ಎತ್ತರವಾದ ಗೋಡೆಯ ಒಂದು ಭಾಗವು ಬೊಗ್ಗುತ್ತಾ ತಟ್ಟನೆ ಕ್ಷಣ ಮಾತ್ರದಲ್ಲಿ ಕವಚಿಕೊಳ್ಳುವ ಹಾಗೆ ನಿಮ್ಮ ಅಪರಾಧವೂ ನಿಮಗೆ ಅಪಾಯಕರವಾಗುವದು.",
			"14": "<sup>14</sup> ಉರಿಯಿಂದ ಕೆಂಡವನ್ನು ತೆಗೆಯುವದಕ್ಕಾಗಲಿ, ಬಾವಿಯಿಂದ ನೀರನ್ನು ಗೋಚುವದಕ್ಕಾಗಲಿ ಬೋಕಿಯ ತುಂಡುಗಳಲ್ಲಿ ಒಂದೂ ಉಳಿಯದಂತೆ ಒಬ್ಬನು ಕುಂಬಾರನ ಗಡಿಗೆಯನ್ನು ಚೂರುಚೂರಾಗಿ ಒಡೆದುಬಿಡುವ ರೀತಿಯಲ್ಲಿ ಆತನು ಆ ಗೋಡೆಯನ್ನು ಒಡೆದು ಬಿಡುವನು.",
			"15": "<sup>15</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಸದಮಲಸ್ವಾಮಿಯಾಗಿರುವ ಕರ್ತನಾದ ಯೆಹೋವನು - ನೀವು ತಿರುಗಿಕೊಂಡು ಸುಮ್ಮನಿದ್ದರೆ ನಿಮಗೆ ರಕ್ಷಣೆಯಾಗುವದು; ಶಾಂತರಾಗಿ ಭರವಸದಿಂದಿರುವದೇ ನಿಮಗೆ ಬಲ ಎಂದು ಹೇಳಿದ್ದರೂ ನೀವು ಒಪ್ಪಿಕೊಂಡಿಲ್ಲ.",
			"16": "<sup>16</sup> ನೀವು - ಬೇಡವೇ ಬೇಡ, ಕುದುರೆಗಳ ಮೇಲೆ ಓಡುವೆವು ಎಂದುಕೊಂಡದರಿಂದ ಓಡೇ ಹೋಗುವಿರಿ; ನೀವು - ವೇಗವಾಗಿ ಸವಾರಿಮಾಡುವೆವು ಎಂದುಕೊಂಡದರಿಂದ ವೇಗಿಗಳೇ ನಿಮ್ಮನ್ನು ಅಟ್ಟಿ ಬಿಡುವರು;",
			"17": "<sup>17</sup> ಒಬ್ಬನ ಬೆದರಿಕೆಗೆ ಸಾವಿರ ಜನರು ಓಡುವರು; ಐವರು ಬೆದರಿಸುವದರಿಂದ ನೀವು ಓಡಿ ಹೋಗುವಿರಿ; ಕಟ್ಟಕಡೆಗೆ ಬೆಟ್ಟದ ಶಿಖರದಲ್ಲಿನ ಸ್ತಂಭದ ಹಾಗೂ ಗುಡ್ಡದ ಮೇಗಣ ಕಂಬದಂತೆಯೂ [ಒಂಟಿಯಾಗಿ] ಉಳಿಯುವಿರಿ.",
			"18": "<sup>18</sup> ಹೀಗಿರಲು ಯೆಹೋವನು ನಿಮಗೆ ಕೃಪೆತೋರಿಸಬೇಕೆಂದು ಕಾದಿರುವನು; ನಿಮ್ಮನ್ನು ಕರುಣಿಸಬೇಕೆಂದು ಉನ್ನತೋನ್ನತನಾಗಿ ಕಾಣಿಸಿಕೊಳ್ಳುವನು; ಯೆಹೋವನು ನ್ಯಾಯಸ್ವರೂಪನಾದ ದೇವರು; ಆತನಿಗಾಗಿ ಕಾದಿರುವವರೆಲ್ಲರೂ ಧನ್ಯರು.",
			"19": "<sup>19</sup> ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ವಾಸಿಸುವ ಚೀಯೋನಿನ ಜನರೇ, ನೀವು ಇನ್ನು ಅಳುವದೇ ಇಲ್ಲ; ನೀವು ಕೂಗಿಕೊಂಡ ಶಬ್ದವನ್ನು ಆತನು ಕೇಳಿ ನಿಮಗೆ ಕೃಪೆತೋರಿಸೇ ತೋರಿಸುವನು; ಕೇಳಿದ ಕೂಡಲೆ ನಿಮಗೆ ಸದುತ್ತರವನ್ನು ದಯಪಾಲಿಸುವನು.",
			"20": "<sup>20</sup> ಕರ್ತನು ಕಷ್ಟವನ್ನೂ ಶ್ರಮವನ್ನೂ ನಿಮಗೆ ಅನ್ನಪಾನಗಳನ್ನಾಗಿ ಕೊಟ್ಟರೂ ನಿಮ್ಮ ಬೋಧಕನು ಇನ್ನು ಮರೆಯಾಗಿರನು, ನಿಮ್ಮ ಬೋಧಕನನ್ನು ಕಣ್ಣಾರೆ ಕಾಣುವಿರಿ;",
			"21": "<sup>21</sup> ನೀವು ಬಲಕ್ಕಾಗಲಿ ಎಡಕ್ಕಾಗಲಿ ತಿರುಗಿಕೊಳ್ಳುವಾಗ ಇದೇ ಮಾರ್ಗ, ಇದರಲ್ಲೇ ನಡೆಯಿರಿ ಎಂದು ನಿಮ್ಮ ಹಿಂದೆ ಆಡುವ ಮಾತು ನಿಮ್ಮ ಕಿವಿಗೆ ಬೀಳುವದು.",
			"22": "<sup>22</sup> ಕೆತ್ತಿದ ನಿಮ್ಮ ವಿಗ್ರಹಗಳ ಬೆಳ್ಳಿಯ ಕವಚಗಳನ್ನೂ ಎರಕದ ನಿಮ್ಮ ಬೊಂಬೆಗಳ ಬಂಗಾರದ ಮುಲಾಮನ್ನೂ ನೀವು ಹೊಲಸು ಮಾಡಿ ಆ ವಿಗ್ರಹಗಳನ್ನು - ತೊಲಗಿ ಎಂದು ಹೊಲೆಯಂತೆ ಬಿಸಾಟುಬಿಡುವಿರಿ.",
			"23": "<sup>23</sup> ಆಗ ನೀವು ಹೊಲದಲ್ಲಿ ಬೀಜಬಿತ್ತುವದಕ್ಕೆ ಆತನು ಬಿತ್ತನೆಯ ಮಳೆಯನ್ನು ದಯಪಾಲಿಸುವನು; ನೆಲದ ಬೆಳೆಯಿಂದ ಸಾರವಾದ ಆಹಾರವನ್ನು ಸಮೃದ್ಧಿಯಾಗಿ ಒದಗಿಸುವನು; ಆ ದಿನದಲ್ಲಿ ನಿಮ್ಮ ಮಂದೆಗಳು ದೊಡ್ಡದೊಡ್ಡ ಕಾವಲುಗಳಲ್ಲಿ ಮೇಯುವವು.",
			"24": "<sup>24</sup> ಹೊಲಗೇಯುವ ಎತ್ತುಕತ್ತೆಗಳು ಮೊರದಿಂದಲೂ ಕವೇಕೋಲಿನಿಂದಲೂ ತೂರಿದ ಉಪ್ಪುಪ್ಪಾದ ಮೇವನ್ನು ತಿನ್ನುವವು.",
			"25": "<sup>25</sup> ಬುರುಜುಗಳು ಬಿದ್ದುಹೋಗುವ ಮಹಾಸಂಹಾರದ ದಿನದಲ್ಲಿ ಪ್ರತಿಯೊಂದು ಉನ್ನತ ಪರ್ವತದಲ್ಲಿಯೂ ಎತ್ತರವಾದ ಗುಡ್ಡದಲ್ಲಿಯೂ ತೊರೆಗಳೂ ನೀರಿನ ಕಾಲುವೆಗಳೂ ಹರಿಯುತ್ತಿರುವವು.",
			"26": "<sup>26</sup> ಇದಲ್ಲದೆ ಯೆಹೋವನು ತನ್ನ ಜನರ ವ್ರಣವನ್ನು ಕಟ್ಟಿ ಅವರ ಪೆಟ್ಟಿನ ಗಾಯವನ್ನು ಗುಣಮಾಡುವ ದಿವಸದಲ್ಲಿ ಚಂದ್ರನ ಬೆಳಕು ಸೂರ್ಯನ ಬೆಳಕಿನಂತಿರುವದು, ಮತ್ತು ಸೂರ್ಯನ ಬೆಳಕು ಏಳರಷ್ಟು ಹೆಚ್ಚಿ ಏಳು ದಿನಗಳ ಬೆಳಕಿನಂತಾಗುವದು.",
			"27": "<sup>27</sup> ಇಗೋ, ಯೆಹೋವನಾಮಧೇಯನು ದೂರದಿಂದ ಬರುತ್ತಾನೆ; ಆತನ ಕೋಪವು ಉರಿಯುತ್ತಿದೆ, ಅದರಿಂದೇಳುವ ಹೊಗೆಯು ದಟ್ಟವಾಗಿದೆ; ಆತನ ತುಟಿಗಳು ರೋಷದಿಂದ ತುಂಬಿವೆ, ಆತನ ನಾಲಿಗೆಯು ನುಂಗುವ ಅಗ್ನಿ;",
			"28": "<sup>28</sup> ಆತನ ಶ್ವಾಸವು ತುಂಬಿತುಳುಕಿ ಕಂಠದ ಮಟ್ಟಿಗೂ ಏರುವ ತೊರೆ; ಜನಾಂಗಗಳನ್ನು ಏನೂ ಉಳಿಸದ ಜರಡಿಯಿಂದ ಜಾಲಿಸುವದಕ್ಕೆ ಬರುತ್ತಾನೆ; ದಾರಿತಪ್ಪಿಸುವ ಕಡಿವಾಣವು ಜನಗಳ ಕಟಬಾಯಲ್ಲಿರುವದು.",
			"29": "<sup>29</sup> ನೀವೋ ಹಬ್ಬದ ಸೌರಣೆಯ ರಾತ್ರಿಯಲ್ಲೋ ಎಂಬಂತೆ ಹಾಡುವಿರಿ; ಇಸ್ರಾಯೇಲ್ಯರ ಶರಣನ ಸಾನ್ನಿಧ್ಯವನ್ನು ಬಯಸಿ ಯೆಹೋವನ ಪರ್ವತಕ್ಕೆ ಪಿಳ್ಳಂಗೋವಿಯ ನಾದದೊಡನೆ ಹೋಗುವವನಂತೆ ಹೃದಯಾನಂದಪಡುವಿರಿ.",
			"30": "<sup>30</sup> ಆಗ ಯೆಹೋವನು ತನ್ನ ಗಂಭೀರವಾದ ಧ್ವನಿಯನ್ನು ಕೇಳಮಾಡಿ ತೀವ್ರಕೋಪ, ಕಬಳಿಸುವ ಅಗ್ನಿಜ್ವಾಲೆ, ಬಿರಿದ ಮೋಡ, ಅತಿವೃಷ್ಟಿ, ಕಲ್ಮಳೆ ಇವುಗಳಿಂದ ತನ್ನ ಶಿಕ್ಷಾಹಸ್ತವನ್ನು ತೋರ್ಪಡಿಸುವನು.",
			"31": "<sup>31</sup> ಹೀಗೆ ಯೆಹೋವನು ದಂಡದಿಂದ ದಂಡಿಸುವಾಗ ಅಶ್ಶೂರ್ಯರು ಆತನ ಧ್ವನಿಯಿಂದಲೇ ಭಂಗಪಡುವರು.",
			"32": "<sup>32</sup> ಮತ್ತು ಯೆಹೋವನು ಸಂಕಲ್ಪಿಸಿದ ದಂಡದ ಪ್ರತಿಯೊಂದು ಪೆಟ್ಟೂ ದಮ್ಮಡಿ ಕಿನ್ನರಿಗಳ ನಾದದೊಡನೆ ಅವರ ಮೇಲೆ ಬೀಳುವದು. ಆತನು ಅವರೊಂದಿಗೆ ಹೋರಾಡುತ್ತಾ ಕೈಬೀಸಿ ಯುದ್ಧಮಾಡುವನು.",
			"33": "<sup>33</sup> ಪುರಾತನ ಕಾಲದಿಂದಲೂ ಅಗ್ನಿಕುಂಡವು ಸಿದ್ಧವಾಗಿದೆ; ಹೌದು, ರಾಜನಿಗೆ ಅಣಿಯಾಗಿದೆ; ಅದನ್ನು ಆಳವಾಗಿಯೂ ಅಗಲವಾಗಿಯೂ ಮಾಡಿದ್ದಾನೆ; ಅದರಲ್ಲಿನ ಚಿತೆಯೊಳಗೆ ಬೆಂಕಿಯೂ ಬೇಕಾದಷ್ಟು ಕೊರಡುಗಳೂ ಇವೆ; ಯೆಹೋವನ ಶ್ವಾಸವು ಗಂಧಕದ ಪ್ರವಾಹದೋಪಾದಿಯಲ್ಲಿ ಅದನ್ನು ಉರಿಸುವದು."
		},
		"31": {
			"1": "<sup>1</sup> ಅಯ್ಯೋ, ಸಹಾಯಕ್ಕಾಗಿ ಐಗುಪ್ತವನ್ನು ಸೇರಿ ಅಶ್ವಬಲವನ್ನು ಆಶ್ರಯಿಸುವವರ ಗತಿಯನ್ನು ಏನು ಹೇಳಲಿ! ರಥಗಳು ಬಹಳವೆಂದು ಅವುಗಳಲ್ಲಿ ನಂಬಿಕೆಯಿಟ್ಟು ಸವಾರರು ಬಹು ಬಲಿಷ್ಠರೆಂದು ಅವರ ಮೇಲೆ ಭರವಸಪಡುತ್ತಾರೆ; ಆದರೆ ಇಸ್ರಾಯೇಲ್ಯರ ಸದಮಲಸ್ವಾಮಿಯ ಕಡೆಗೆ ದೃಷ್ಟಿಯಿಡುವದಿಲ್ಲ, ಯೆಹೋವನನ್ನು ಆಶ್ರಯಿಸುವದಿಲ್ಲ;",
			"2": "<sup>2</sup> ಇಗೋ, ಆತನೂ ವಿವೇಕಿ, ತನ್ನ ಮಾತುಗಳನ್ನು ಹಿಂದಕ್ಕೆ ತೆಗೆಯದೆ ಕೇಡನ್ನು ಬರಮಾಡುವನು; ಕೆಡುಕರ ಮನೆತನಕ್ಕೂ ಅನ್ಯಾಯಗಾರರ ಸಹಾಯಕರಿಗೂ ವಿರುದ್ಧವಾಗಿ ಏಳುವನು.",
			"3": "<sup>3</sup> ಐಗುಪ್ತ್ಯರು ಮನುಷ್ಯ ಮಾತ್ರದವರೇ, ದೇವರಲ್ಲ; ಅವರ ಅಶ್ವಗಳು ಮಾಂಸಮಯವೇ, ಆತ್ಮವಲ್ಲ; ಯೆಹೋವನು ಕೈಚಾಚುವಾಗ ಸಹಾಯ ಮಾಡಿದವನು ಮುಗ್ಗರಿಸುವನು, ಸಹಾಯಪಡೆದವನು ಬಿದ್ದು ಹೋಗುವನು, ಅಂತು ಎಲ್ಲರೂ ಒಟ್ಟಿಗೆ ಲಯವಾಗುವರು.",
			"4": "<sup>4</sup> ಯೆಹೋವನು ನನಗೆ ಹೀಗೆ ಹೇಳಿದ್ದಾನೆ - ಸಿಂಹವು, ಪ್ರಾಯದ ಸಿಂಹವು, ಬೇಟೆಯ ಮೇಲೆ ಬಿದ್ದು ಗುರುಗುಟ್ಟುತ್ತಿರುವಾಗ ಕಾವಲುಗಾರನ ಕೂಗನ್ನು ಕೇಳಿ ಬಹುಮಂದಿ ಕುರುಬರು ಅದಕ್ಕೆ ವಿರುದ್ಧವಾಗಿ ಕೂಡಿ ಬಂದರೂ ಅದು ಅವರ ಕೂಗಾಟಕ್ಕೆ ಹೆದರದೆ ಅವರ ಗದ್ದಲದಿಂದ ಹೇಗೆ ಧೈರ್ಯಗುಂದುವದಿಲ್ಲವೋ ಹಾಗೆಯೇ ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಯುದ್ಧಮಾಡಲು ಚೀಯೋನ್ ಪರ್ವತದ ಶಿಖರದ ಮೇಲೆ ಇಳಿಯುವನು.",
			"5": "<sup>5</sup> ಹೆಣ್ಣುಹಕ್ಕಿಗಳು ಹಾರಿಯಾಡುತ್ತಾ [ಮರಿಗಳನ್ನು] ಕಾಪಾಡುವಂತೆ ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಯೆರೂಸಲೇಮನ್ನು ಕಾಪಾಡುವನು; ಅದನ್ನು ರಕ್ಷಿಸಿ ಕಾಯುವನು, ಹಾದು ಹೋಗುತ್ತಾ ಅಪಾಯದಿಂದ ತಪ್ಪಿಸುವನು.",
			"6": "<sup>6</sup> ಇಸ್ರಾಯೇಲ್ಯರೇ, ನೀವು ಯಾರಿಗೆ ಅಗಾಧ ದ್ರೋಹವನ್ನು ಮಾಡಿದಿರೋ ಆತನ ಕಡೆಗೆ ತಿರಿಗಿಕೊಳ್ಳಿರಿ.",
			"7": "<sup>7</sup> ನೀವು ನಿಮ್ಮ ಕೈಯಿಂದ ರೂಪಿಸಿಕೊಂಡಿರುವ ಬೆಳ್ಳಿಯ ಮತ್ತು ಬಂಗಾರದ ವಿಗ್ರಹಗಳನ್ನು ಆ ದಿನದಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬನೂ ಬಿಸಾಟುಬಿಡುವನು; ಅವುಗಳನ್ನು ರೂಪಿಸಿದ್ದು ಪಾಪವಷ್ಟೆ.",
			"8": "<sup>8</sup> ಆಗ ಅಶ್ಶೂರ್ಯರು ಕತ್ತಿಯಿಂದ ಬೀಳುವರು, ಅದು ಮನುಷ್ಯರ ಕತ್ತಿಯಲ್ಲ; ಖಡ್ಗವು ಅವರನ್ನು ನುಂಗುವದು, ಅದು ಮಾನವಖಡ್ಗವಲ್ಲ; ಅವರು ಕತ್ತಿಯ ಕಡೆಯಿಂದ ಓಡುವರು, ಅವರ ಯೌವನಸ್ಥರು ಬಿಟ್ಟಿಹಿಡಿಯಲ್ಪಡುವರು.",
			"9": "<sup>9</sup> ಅವರ ಶರಣನು ಭಯದಿಂದ ಹೋಗಿಬಿಡುವನು; ಅವರ ಪ್ರಧಾನರು ಹೆದರಿ ಧ್ವಜಸ್ಥಾನದಿಂದ ದಿಕ್ಕಾಪಾಲಾಗುವರು. ಚೀಯೋನಿನಲ್ಲಿ ಅಗ್ನಿಯನ್ನೂ ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಒಲೆಯನ್ನೂ ಮಾಡಿಕೊಂಡಿರುವ ಯೆಹೋವನ ನುಡಿ ಇದೇ."
		},
		"32": {
			"1": "<sup>1</sup> ಇಗೋ, ಒಬ್ಬ ರಾಜನು ನೀತಿಗನುಸಾರವಾಗಿ ಆಳುವನು, ಅಧಿಪತಿಗಳು ನ್ಯಾಯದಿಂದ ದೊರೆತನ ಮಾಡುವರು.",
			"2": "<sup>2</sup> ಮತ್ತು ಒಬ್ಬ ಪುರುಷನು ಗಾಳಿಯಲ್ಲಿ ಮರೆಯಂತೆಯೂ ಅತಿವೃಷ್ಟಿಯಲ್ಲಿ ಆವರಣದ ಹಾಗೂ ಮರುಭೂಮಿಯಲ್ಲಿ ನೀರಿನ ಕಾಲುವೆಗಳ ಪ್ರಕಾರವೂ ಬೆಂಗಾಡಿನಲ್ಲಿ ದೊಡ್ಡ ಬಂಡೆಯ ನೆರಳಿನೋಪಾದಿಯಲ್ಲಿಯೂ ಇರುವನು.",
			"3": "<sup>3</sup> ಆಗ ನೋಡುವವರ ಕಣ್ಣು ಮೊಬ್ಬಾಗದು, ಕೇಳುವವರ ಕಿವಿ ಮಂದವಾಗದು.",
			"4": "<sup>4</sup> ಆತುರಗಾರರ ಮನವು ಅರುಹನ್ನರಿಯುವದು, ತೊದಲುಮಾತಿನವರ ನಾಲಿಗೆಯು ಸ್ವಚ್ಫವಾಗಿಯೂ ಶೀಘ್ರವಾಗಿಯೂ ನುಡಿಯುವದು.",
			"5": "<sup>5</sup> ಇನ್ನು ಮೇಲೆ ನೀಚನು ಘನವಂತನೆನಿಸಿಕೊಳ್ಳನು. ಕಳ್ಳನು ಮಹನೀಯನೆನಿಸನು.",
			"6": "<sup>6</sup> ನೀಚನು ನೀಚವಾಗಿ ಮಾತಾಡುವನು; ಅವನ ಹೃದಯವು ಕೇಡನ್ನು ಕಲ್ಪಿಸಿ ಅಲ್ಲದ್ದನ್ನು ನಡೆಯಿಸಿ ಯೆಹೋವನಿಗೆ ವಿರುದ್ಧವಾಗಿ ಅಸತ್ಯವನ್ನಾಡಿ ಹಸಿವೆಗೊಂಡವನ ಆಶೆಯನ್ನು ಬರಿದು ಮಾಡಿ ಬಾಯಾರಿದವನ ಪಾನವನ್ನು ತಪ್ಪಿಸುವದಷ್ಟೆ.",
			"7": "<sup>7</sup> ಕಳ್ಳನ ಸಲಕರಣೆಗಳು ಕೆಟ್ಟವುಗಳೇ; ದೀನದರಿದ್ರರು ನ್ಯಾಯವಾದಿಗಳಾಗಿದ್ದರೂ ಅವರನ್ನು ಸುಳ್ಳುಮಾತುಗಳಿಂದ ಕೆಡಿಸುವದಕ್ಕೆ ಕುಯುಕ್ತಿಗಳನ್ನು ಕಲ್ಪಿಸುವನು.",
			"8": "<sup>8</sup> ಘನವಂತನಾದರೋ ಘನಕಾರ್ಯಗಳನ್ನು ಕಲ್ಪಿಸುವನು; ಘನವಾದವುಗಳಲ್ಲಿಯೇ ನಿರತನಾಗಿರುವನು.",
			"9": "<sup>9</sup> ನಿಶ್ಚಿಂತೆಯ ಹೆಂಗಸರೇ, ಏಳಿರಿ, ನನ್ನ ಧ್ವನಿಯನ್ನು ಕೇಳಿರಿ; ಭಯವಿಲ್ಲದ ಹೆಣ್ಣುಗಳಿರಾ, ನನ್ನ ಮಾತಿಗೆ ಕಿವಿಗೊಡಿರಿ!",
			"10": "<sup>10</sup> ನಿರ್ಭೀತರೇ, ಒಂದು ವರುಷದ ಮೇಲೆ ಕೆಲವು ದಿನಗಳಲ್ಲಿ ಕಳವಳಗೊಳ್ಳುವಿರಿ; ದ್ರಾಕ್ಷೆಯ ಕೊಯ್ಲು ಇಲ್ಲದೆ ಹೋಗುವದು, ಯಾವ ಬೆಳೆಯೂ ದೊರೆಯದು.",
			"11": "<sup>11</sup> ನಿಶ್ಚಿಂತೆಯವರೇ, ನಡುಗಿರಿ! ನಿರ್ಭೀತರೇ, ಕಳವಳಗೊಳ್ಳಿರಿ! ನಿಮ್ಮ ಬಟ್ಟೆಯನ್ನು ಕಿತ್ತುಹಾಕಿ ಬೆತ್ತಲೆಯಾಗಿ ಸೊಂಟಕ್ಕೆ [ಗೋಣಿತಟ್ಟನ್ನು] ಸುತ್ತಿಕೊಳ್ಳಿರಿ.",
			"12": "<sup>12</sup> ಇಷ್ಟವಾದ ಹೊಲಗಳ ಮತ್ತು ಫಲವತ್ತಾದ ದ್ರಾಕ್ಷಾಲತೆಗಳ ವಿಷಯದಲ್ಲಿ ಎದೆಬಡಿದುಕೊಳ್ಳುವರು.",
			"13": "<sup>13</sup> ನನ್ನ ಜನರ ಭೂಮಿಯ ಮೇಲೂ ಉತ್ಸಾಹಪಟ್ಟಣದಲ್ಲಿ ಉಲ್ಲಾಸಗೊಳ್ಳುವ ಎಲ್ಲಾ ಮನೆಗಳ ಮೇಲೂ ಮುಳ್ಳುಗಿಳ್ಳು ಹತ್ತಿಕೊಳ್ಳುವದು;",
			"14": "<sup>14</sup> ಅರಮನೆಯು ಬಿಕೋ ಎನ್ನುವದು, ಗಿಜಿಗುಟ್ಟುವ ಪಟ್ಟಣವು ನಿರ್ಜನವಾಗುವದು, ಓಫೆಲ್ ಗುಡ್ಡವೂ ಕೋವರವೂ ಯಾವಾಗಲೂ ಗುಹೆಗಳಾಗಿಯೂ ಕಾಡುಕತ್ತೆಗಳಿಗೆ ಉಲ್ಲಾಸಕರವಾಗಿಯೂ ದನಕುರಿಗಳಿಗೆ ಕಾವಲಾಗಿಯೂ ಇರುವವು.",
			"15": "<sup>15</sup> ಬಳಿಕ ಉನ್ನತಲೋಕದಿಂದ ದಿವ್ಯಾತ್ಮಧಾರೆಯು ನಮ್ಮ ಮೇಲೆ ಸುರಿಸಲ್ಪಡುವದು; ಆಗ ಅರಣ್ಯವು ತೋಟವಾಗುವದು, [ಈಗಿನ] ತೋಟವು [ಆಗಿನವರಿಗೆ] ಅರಣ್ಯವಾಗಿ ಕಾಣಿಸುವದು.",
			"16": "<sup>16</sup> ನ್ಯಾಯವು ಅಡವಿಯಲ್ಲಿಯೂ ನೆಲೆಗೊಳ್ಳುವದು, ಧರ್ಮವು ತೋಟದಲ್ಲಿ ಇದ್ದೇ ಇರುವದು.",
			"17": "<sup>17</sup> ಧರ್ಮದಿಂದ ಸಮಾಧಾನವು ಫಲಿಸುವದು, ಶಾಂತಿ ನಿರ್ಭಯಗಳು ಧರ್ಮದ ನಿತ್ಯಪರಿಣಾಮವಾಗಿರುವವು.",
			"18": "<sup>18</sup> ನನ್ನ ಜನರು ಸಮಾಧಾನನಿವಾಸದಲ್ಲಿಯೂ ನಿರ್ಭಯನಿಲಯಗಳಲ್ಲಿಯೂ ನೆಮ್ಮದಿಯ ಆಶ್ರಯಗಳಲ್ಲಿಯೂ ನೆಲೆಗೊಳ್ಳುವರು.",
			"19": "<sup>19</sup> ಆದರೆ ಕಲ್ಮಳೆಸುರಿಯಲು ವನವು ಹಾಳಾಗುವದು; ಪಟ್ಟಣವು ನೆಲಸಮವಾಗುವದು,",
			"20": "<sup>20</sup> ನೀರಾವರಿಗಳಲ್ಲೆಲ್ಲಾ ಬೀಜಬಿತ್ತುತ್ತಲೂ ದನ ಕತ್ತೆಗಳನ್ನು [ಕಾವಲಿಗೆ] ಮೇಯಬಿಡುತ್ತಲೂ ಇರುವ ನೀವು ಧನ್ಯರೇ ಸರಿ!"
		},
		"33": {
			"1": "<sup>1</sup> ಸೂರೆಯಾಗದಿದ್ದರೂ ಸೂರೆಮಾಡಿದಿ, ಬಾಧೆಪಡದಿದ್ದರೂ ಬಾಧಿಸಿದಿ; ನಿನ್ನ ಗತಿಯನ್ನು ಏನು ಹೇಳಲಿ; ನೀನು ಸೂರೆಮಾಡಿ ಬಿಟ್ಟ ಮೇಲೆ ನೀನೂ ಸೂರೆಯಾಗುವಿ! ಬಾಧಿಸಿ ಬಿಟ್ಟ ಮೇಲೆ ನಿನ್ನನ್ನೂ ಬಾಧಿಸುವರು.",
			"2": "<sup>2</sup> ಯೆಹೋವನೇ, ನಮ್ಮನ್ನು ಕರುಣಿಸು! ನಿನ್ನನ್ನು ಕಾದುಕೊಂಡಿದ್ದೇವೆ; ಪ್ರತಿ ಮುಂಜಾನೆಯೂ ನಮಗೆ ಭುಜಬಲವಾಗಿಯೂ ಇಕ್ಕಟ್ಟಿನಲ್ಲಿ ನಮಗೆ ರಕ್ಷಣೆಯಾಗಿಯೂ ಇರು.",
			"3": "<sup>3</sup> ಭೋರ್ಗರೆಯುವ ಶಬ್ದಕ್ಕೆ ಜನಾಂಗಗಳು ಓಡುವವು; ನೀನು ಏಳುವಾಗ ರಾಜ್ಯಗಳು ದಿಕ್ಕಾಪಾಲಾಗುವವು.",
			"4": "<sup>4</sup> ನೀವು ಕೊಳ್ಳೆಹೊಡೆದದ್ದನ್ನು ಇತರರು ಮಿಡತೆಗಳಂತೆ ಕೊಳ್ಳೆಮಾಡುವರು; ಶಲಭಗಳಂತೆ ಅದರ ಮೇಲೆ ಹಾರಿಬೀಳುವರು;",
			"5": "<sup>5</sup> ಯೆಹೋವನು ಉನ್ನತೋನ್ನತನಾಗಿದ್ದಾನೆ; ಮೇಲಿನ ಲೋಕದಲ್ಲಿ ವಾಸಿಸುತ್ತಾನಷ್ಟೆ; ಚೀಯೋನನ್ನು ನೀತಿನ್ಯಾಯಗಳಿಂದ ತುಂಬಿಸಿದ್ದಾನೆ.",
			"6": "<sup>6</sup> ಜ್ಞಾನವೂ ತಿಳುವಳಿಕೆಯೂ ರಕ್ಷಣ ಕಾರ್ಯಸಮೃದ್ಧಿಯೂ ಇರುವದರಿಂದ ನಿನ್ನ ಕಾಲದಲ್ಲಿ ಸ್ಥೈರ್ಯವಿರುವದು; ಯೆಹೋವನ ಮೇಲಣ ಭಯಭಕ್ತಿಯು ನಿನಗೆ ನಿಧಿಯಾಗುವದು.",
			"7": "<sup>7</sup> ಇಗೋ, ಸಿಂಹವೀರರು ಹೊರಗೆ ಹಾಹಾಕಾರ ಮಾಡುತ್ತಿದ್ದಾರೆ; ಸಮಾಧಾಯಕ ರಾಯಭಾರಿಗಳು ಘೋರವಾಗಿ ಅಳುತ್ತಿದ್ದಾರೆ.",
			"8": "<sup>8</sup> ರಾಜಮಾರ್ಗಗಳು ಹಾಳಾಗಿವೆ, ದಾರಿಗರು ಇಲ್ಲವೇ ಇಲ್ಲ; [ಶತ್ರುವು] ಒಪ್ಪಂದವನ್ನು ಮೀರಿದ್ದಾನೆ, ಊರಿನವರನ್ನು ತಿರಸ್ಕರಿಸಿದ್ದಾನೆ, ಮನುಷ್ಯರನ್ನು ಗಣನೆಗೆ ತಾರನು.",
			"9": "<sup>9</sup> ದೇಶವು ಪ್ರಲಾಪಿಸಿ ಕುಗ್ಗುತ್ತದೆ, ಲೆಬನೋನ್ ನಾಚಿಕೊಂಡು ಒಣಗುತ್ತದೆ. ಶಾರೋನ್ ಬೆಂಗಾಡಾಗಿದೆ, ಬಾಷಾನ್ ಮತ್ತು ಕರ್ಮೆಲ್ [ತಳಿರುಗಳನ್ನು] ಉದುರಿಸಿಬಿಟ್ಟಿವೆ.",
			"10": "<sup>10</sup> ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ಈಗ ಏಳುವೆನು, ಈಗಲೇ ನನ್ನನ್ನು ಉನ್ನತಪಡಿಸಿಕೊಳ್ಳುವೆನು, ಕೂಡಲೆ ಉನ್ನತೋನ್ನತನಾಗುವೆನು.",
			"11": "<sup>11</sup> ನೀವು ಹೊಟ್ಟನ್ನು ಗರ್ಭಧರಿಸಿ ಕೂಳೆಯನ್ನು ಹೆರುವಿರಿ; ನಿಮ್ಮ ಬುಸುಗುಟ್ಟುವಿಕೆಯು ನಿಮ್ಮನ್ನೇ ನುಂಗುವ ಜ್ವಾಲೆಯಾಗುವದು.",
			"12": "<sup>12</sup> ಜನಾಂಗಗಳು ಸುಟ್ಟ ಸುಣ್ಣದ ಹಾಗಿರುವವು; ಕತ್ತರಿಸಿದ ಮುಳ್ಳು ಕಂಪೆಗೆ ಬೆಂಕಿ ಹಚ್ಚಿದಂತಾಗುವವು.",
			"13": "<sup>13</sup> ದೂರಸ್ಥರೇ, ನಾನು ನಡಿಸಿದ್ದನ್ನು ಕೇಳಿರಿ; ಸಮೀಪಸ್ಥರೇ, ನನ್ನ ಪರಾಕ್ರಮವನ್ನು ತಿಳಿದುಕೊಳ್ಳಿರಿ.",
			"14": "<sup>14</sup> ಚೀಯೋನಿನ ಪಾಪಿಗಳು ಹೆದರುತ್ತಾರೆ, ಆ ಭ್ರಷ್ಟರು ನಡುಕಕ್ಕೆ ಒಳಗಾಗಿ - ನಮ್ಮಲ್ಲಿ ಯಾರು ನುಂಗುವ ಕಿಚ್ಚಿನ ಸಂಗಡ ವಾಸಿಸಬಲ್ಲರು? ನಮ್ಮಲ್ಲಿ ಯಾರು ಸದಾ ಉರಿಯುವ ಜ್ವಾಲೆಗಳೊಡನೆ ತಂಗಿಯಾರು ಅಂದುಕೊಳ್ಳುತ್ತಾರೆ.",
			"15": "<sup>15</sup> [ಕೇಳಿರಿ], ಸನ್ಮಾರ್ಗದಲ್ಲಿ ನಡೆದು ಯಥಾರ್ಥವಾಗಿ ನುಡಿದು ದೋಚಿಕೊಂಡ ಲಾಭ ಬೇಡವೆಂದು ಲಂಚ ಮುಟ್ಟದಂತೆ ಕೈ ಒದರಿ ಕೊಲೆಯ ಮಾತಿಗೆ ಕಿವಿಗೊಡದೆ ಕೆಟ್ಟದ್ದನ್ನು ನೋಡದಂತೆ ಕಣ್ಣು ಮುಚ್ಚಿಕೊಳ್ಳುವವನೇ ಉನ್ನತಸನ್ನಿಧಾನದಲ್ಲಿ ವಾಸಿಸುವನು;",
			"16": "<sup>16</sup> ಇವನಿಗೆ ಆಶ್ರಯವು ಗಿರಿದುರ್ಗ, ಅನ್ನವು ಉಚಿತವಾಗಿ ಒದಗುವದು, ನೀರೂ ನಿಸ್ಸಂದೇಹ.",
			"17": "<sup>17</sup> ನೀವು ಭೂಷಿತರಾಜನನ್ನು ಕಣ್ಣಾರೆ ದರ್ಶನ ಮಾಡುವಿರಿ. ಅತಿವಿಸ್ತಾರವಾದ ಸ್ವದೇಶವನ್ನು ಕಣ್ಣು ತುಂಬಾ ನೋಡುವಿರಿ.",
			"18": "<sup>18</sup> ಆಗ ನೀವು [ಹಿಂದಿನ] ಭಯವನ್ನು ಮನಸ್ಸಿಗೆ ತಂದುಕೊಳ್ಳುತ್ತಾ - [ಕಪ್ಪವನ್ನು] ಲೆಕ್ಕಿಸಿದವನು ಎಲ್ಲಿ, ತೂಕಮಾಡಿದವನು ಎಲ್ಲಿ, ಬುರುಜುಗಳನ್ನು ಗಣಿಸಿದವನು ಎಲ್ಲಿ ಅಂದುಕೊಳ್ಳುವಿರಿ.",
			"19": "<sup>19</sup> ನಿಮಗೆ ತಿಳಿಯಲಾಗದ ಅನ್ಯಭಾಷೆಯನ್ನೂ ನೀವು ಗ್ರಹಿಸಲಾರದ ತೊದಲುಮಾತುಗಳನ್ನೂ ಆಡುವ ಆ ಕ್ರೂರಜನರನ್ನು ಕಾಣದೆ ಇರುವಿರಿ.",
			"20": "<sup>20</sup> ನಮ್ಮ ಉತ್ಸವಗಳು ನಡೆಯುವ ಚೀಯೋನ್ ಪಟ್ಟಣವನ್ನು ದೃಷ್ಟಿಸಿರಿ; ಯೆರೂಸಲೇಮು ನೆಮ್ಮದಿಯ ನಿವಾಸವಾಗಿಯೂ ಗೂಟಕೀಳದ, ಹಗ್ಗಹರಿಯದ, ಒಂದೇ ಕಡೆ ಇರುವ ಗುಡಾರವಾಗಿಯೂ ಇರುವದನ್ನು ನೀವು ಕಣ್ಣಾರೆ ಕಾಣುವಿರಿ.",
			"21": "<sup>21</sup> ಅಲ್ಲಿ ಯೆಹೋವನು ಘನಹೊಂದಿದವನಾಗಿ ಹುಟ್ಟುಗೋಲಿನ ದೋಣಿ ಹೋಗದ, ಘನನಾವೆಯೂ ಸಂಚರಿಸದ ವಿಸ್ತೀರ್ಣನದೀನದಗಳಂತೆ ನಮ್ಮೊಂದಿಗಿರುವನು.",
			"22": "<sup>22</sup> ಯೆಹೋವನು ನಮ್ಮ ನ್ಯಾಯಾಧಿಪತಿ, ಯೆಹೋವನು ನಮಗೆ ಧರ್ಮವಿಧಾಯಕ, ಯೆಹೋವನು ನಮ್ಮ ರಾಜ; ಆತನೇ ನಮ್ಮನ್ನು ರಕ್ಷಿಸುವನು.",
			"23": "<sup>23</sup> ನಿನ್ನ ಹಗ್ಗಗಳು ಸಡಲಿ ಸ್ತಂಭದ ಪಾದವನ್ನು ಸ್ಥಿರಪಡಿಸಿಕೊಳ್ಳಲಾರದೆ ಹೋದವು, ಪಠವನ್ನು ಮುದುರದಂತೆ ಹಿಡಿದಿರಲಿಕ್ಕೂ ಆಗಲಿಲ್ಲ; ಆಗ ದೊಡ್ಡ ಸೂರೆಯು ಕೊಳ್ಳೆಯಾಗಿ ಹಂಚುವದಕ್ಕೆ ಆಸ್ಪದವಾಯಿತು. ಕುಂಟರೂ ಸುಲಿಗೆಮಾಡಿದರು.",
			"24": "<sup>24</sup> ಚೀಯೋನಿನ ಯಾವ ನಿವಾಸಿಯೂ ತಾನು ಅಸ್ವಸ್ಥನು ಎಂದು ಹೇಳನು; ಅಲ್ಲಿಯ ಜನರ ಪಾಪವು ಪರಿಹಾರವಾಗುವದು."
		},
		"34": {
			"1": "<sup>1</sup> ಜನಾಂಗಗಳೇ, ಸಮೀಪಿಸಿ ಕೇಳಿರಿ; ಜನಗಳೇ, ಕಿವಿಗೊಡಿರಿ! ಭೂಮಿಯೂ ಅದರಲ್ಲಿನ ಸಮಸ್ತವೂ, ಲೋಕವೂ ಅದರಿಂದ ಉದ್ಭವಿಸುವದೆಲ್ಲವೂ ಆಲಿಸಲಿ.",
			"2": "<sup>2</sup> ಯೆಹೋವನು ಸಕಲ ಜನಾಂಗಗಳಲ್ಲಿ ಕೋಪಮಾಡಿ ಅವುಗಳ ಸೈನ್ಯದ ಮೇಲೆ ರೋಷಗೊಂಡು ಅವರನ್ನು ಕೊಲೆಗೆ ಈಡುಮಾಡಿ ನಿಶ್ಶೇಷವಾಗಿ ಸಂಹರಿಸಿದ್ದಾನಷ್ಟೆ.",
			"3": "<sup>3</sup> ಅವರಲ್ಲಿ ಹತರಾದವರು ಬಿಸಾಡಲ್ಪಡುವರು, ಅವರ ಶವಗಳ ದುರ್ವಾಸನೆಯು ಮೇಲಕ್ಕೆ ಬಡಿಯುವದು; ಅವರ ರಕ್ತಪ್ರವಾಹದಿಂದ ಗುಡ್ಡಗಳು ಕರಗುವವು.",
			"4": "<sup>4</sup> ನಕ್ಷತ್ರಸೈನ್ಯವೆಲ್ಲಾ ಕ್ಷಯಿಸುವದು, ಆಕಾಶ ಮಂಡಲವು ಸುರಳಿಯಂತೆ ಸುತ್ತಿಕೊಳ್ಳುವದು; ದ್ರಾಕ್ಷೆಯ ಎಲೆ ಒಣಗಿ ಗಿಡದಿಂದ ಬೀಳುವಂತೆಯೂ ಅಂಜೂರ ಮರದಿಂದ ತರಗು ಉದುರುವ ಹಾಗೂ ತಾರಾಮಂಡಲವೆಲ್ಲಾ ಬಾಡಿ ಬೀಳುವದು.",
			"5": "<sup>5</sup> ನನ್ನ ಖಡ್ಗವು ಮೇಲಿನ ಲೋಕದಲ್ಲಿ ರೋಷಪಾನಮಾಡಿ ನಾನು ಶಪಿಸಿದ ಎದೋಮೆಂಬ ಜನಾಂಗದ ಮೇಲೆ ನ್ಯಾಯತೀರಿಸುವದಕ್ಕೆ ದುಮುಕುವದು.",
			"6": "<sup>6</sup> ಯೆಹೋವನ ಖಡ್ಗವು ರಕ್ತಮಯವಾಗಿದೆ; ಅದು ವಪೆಯಿಂದಲೂ ಕುರಿ ಹೋತಗಳ ರಕ್ತದಿಂದಲೂ ಟಗರುಗಳ ಹುರುಳಿಕಾಯಿಗಳ ಕೊಬ್ಬಿನಿಂದಲೂ ಭರಿತವಾಗಿದೆ; ಯೆಹೋವನು ಬೊಚ್ರದಲ್ಲಿ ಬಲಿಯನ್ನೂ ಎದೋಮ್ ಸೀಮೆಯಲ್ಲಿ ದೊಡ್ಡ ಕೊಲೆಯನ್ನೂ ಮಾಡಬೇಕೆಂದಿದ್ದಾನಷ್ಟೆ.",
			"7": "<sup>7</sup> ಈ ಯಜ್ಞಪಶುಗಳೊಂದಿಗೆ ಕಾಡುಕೋಣಗಳೂ ಹೋರಿಗೂಳಿಗಳೂ ಬರುವವು; ಆ ದೇಶವು ರಕ್ತದಿಂದ ನೆನೆಯುವದು, ಅಲ್ಲಿನ ದೂಳು ಕೊಬ್ಬಿನಿಂದ ಜಿಡ್ಡಾಗುವದು.",
			"8": "<sup>8</sup> ಏಕಂದರೆ ಯೆಹೋವನು ಮುಯ್ಯಿತೀರಿಸುವ ದಿನವು ಬಂದಿದೆ; ಚೀಯೋನಿನ ವ್ಯಾಜ್ಯದಲ್ಲಿ [ಎದೋಮಿಗೆ] ದಂಡನೆವಿಧಿಸತಕ್ಕ ವರುಷವು ಒದಗಿದೆ.",
			"9": "<sup>9</sup> ಅಲ್ಲಿ ತೊರೆಗಳು ಶಿಲಾಜತುವಾಗಿ ಹರಿಯುವವು, ದೂಳು ಗಂಧಕವಾಗುವದು, ದೇಶವೆಲ್ಲಾ ಉರಿಯುವ ಶಿಲಾಜತುವಾಗುವದು,",
			"10": "<sup>10</sup> ಅದು ಹಗಲಿರುಳೂ ಆರದು, ಅದರ ಹೊಗೆ ನಿರಂತರ ಏರುತ್ತಿರುವದು. ದೇಶವು ತಲತಲಾಂತರಕ್ಕೂ ಹಾಳುಬಿದ್ದಿರುವದು, ಯುಗಯುಗಾಂತರಕ್ಕೂ ಅಲ್ಲಿ ಯಾರೂ ಹಾದು ಹೋಗರು.",
			"11": "<sup>11</sup> ಅದು ಕೊಕ್ಕರೆ ಮುಳ್ಳುಹಂದಿಗಳ ಹಕ್ಕು ಆಗುವದು; ಕಾಗೆಗೂಗೆಗಳು ಅಲ್ಲಿ ವಾಸಿಸುವವು; ಯೆಹೋವನು ಅದರ ಮೇಲೆ ಹಾಳೆಂಬ ನೂಲನ್ನೂ ಪಾಳೆಂಬ ಮಟ್ಟಗೋಲನ್ನೂ ಎಳೆಯುವನು.",
			"12": "<sup>12</sup> ಅಲ್ಲಿ ಪಟ್ಟಕ್ಕೆ ಕರೆಯಲು ಪ್ರಮುಖರಲ್ಲಿ ಯಾರೂ ಸಿಕ್ಕರು; ದೇಶದಲ್ಲಿ ಪ್ರಧಾನರೇ ಇಲ್ಲ.",
			"13": "<sup>13</sup> ಅಲ್ಲಿನ ಅರಮನೆಗಳಲ್ಲಿ ದಬ್ಬೆಗಳ್ಳಿಗಳೂ ಕೋಟೆಗಳಲ್ಲಿ ಮುಳ್ಳುಗಿಳ್ಳುಗಳೂ ಹಬ್ಬಿಕೊಳ್ಳುವವು; ಅದು ನರಿಗಳಿಗೆ ಹಕ್ಕೆಯಾಗಿಯೂ ಉಷ್ಟ್ರಪಕ್ಷಿಗಳಿಗೆ ಎಡೆಯಾಗಿಯೂ ಇರುವದು.",
			"14": "<sup>14</sup> ಕಾಡು ನಾಯಿ ತೋಳಗಳು ಅಲ್ಲಿ ಸಂಧಿಸುವವು, ದೆವ್ವವು ತನ್ನ ಜೊತೆಯನ್ನು ಕೂಗುವದು, ಬೇತಾಳವು ಅಲ್ಲಿ ಹಾಯಾಗಿ ಆಸರೆಗೊಳ್ಳುವದು.",
			"15": "<sup>15</sup> ಹಾರುವ ಹಾವು ಅಲ್ಲಿ ಗೂಡುಮಾಡಿಕೊಂಡು ಮೊಟ್ಟೆಯಿಕ್ಕಿ ಮರಿಮಾಡಿ ತನ್ನ ಮರೆಯಲ್ಲಿ ಕೂಡಿಸಿಕೊಳ್ಳುವದು; ಹೌದು, ಹದ್ದುಗಳು ಅಲ್ಲಿ ಜೋಡಿಜೋಡಿಯಾಗಿ ಸೇರಿಕೊಳ್ಳುವವು.",
			"16": "<sup>16</sup> ಯೆಹೋವನ ಶಾಸ್ತ್ರದಲ್ಲಿ ಹುಡುಕಿ ಓದಿರಿ, ಇವುಗಳಲ್ಲಿ ಯಾವದೂ ಇಲ್ಲದಿರದು, ಎಲ್ಲವೂ ಜೊತೆಜೊತೆಯಾಗಿರುವವು; ಆತನ ಬಾಯಿಂದ ಅಪ್ಪಣೆಯಾಯಿತಲ್ಲವೆ, ಆತನ ಆತ್ಮವು ಇವುಗಳನ್ನು ಒಟ್ಟಿಗೆ ಬರಮಾಡಿತಷ್ಟೆ.",
			"17": "<sup>17</sup> ಆತನೇ ಇವುಗಳಿಗೆ ಪಾಲುಮಾಡಿಕೊಟ್ಟಿದ್ದಾನೆ, ಆತನ ಕೈಯೇ ನೂಲುಹಾಕಿ ದೇಶವನ್ನು ಹಂಚಿದೆ; ಅದು ಇವುಗಳಿಗೆ ನಿತ್ಯಸ್ವಾಸ್ತ್ಯವಾಗುವದು, ಅವು ತಲತಲಾಂತರಕ್ಕೂ ಅಲ್ಲಿ ವಾಸಿಸುವವು."
		},
		"35": {
			"1": "<sup>1</sup> ಅರಣ್ಯವೂ ಮರುಭೂಮಿಯೂ ಆನಂದಿಸುವವು; ಒಣನೆಲವು ಹರ್ಷಿಸಿ ತಾವರೆಯಂತೆ ಕಳಕಳಿಸುವದು.",
			"2": "<sup>2</sup> ಅದು ಸಮೃದ್ಧಿಯಾಗಿ ಹೂಬಿಟ್ಟು ಉತ್ಸಾಹ ಧ್ವನಿಮಾಡುವಷ್ಟು ಉಲ್ಲಾಸಿಸುವದು; ಲೆಬನೋನಿನ ಮಹಿಮೆಯೂ ಕರ್ಮೆಲಿನ ಮತ್ತು ಶಾರೋನಿನ ವೈಭವವೂ ಅದಕ್ಕೆ ದೊರೆಯುವವು; ಇವೆಲ್ಲಾ ಯೆಹೋವನ ಮಹಿಮೆಯನ್ನೂ ನಮ್ಮ ದೇವರ ವೈಭವವನ್ನೂ ಕಾಣುವವು.",
			"3": "<sup>3</sup> ಜೋಲುಬಿದ್ದ ಕೈಗಳನ್ನೂ ನಡುಗುವ ಮೊಣಕಾಲುಗಳನ್ನೂ ಬಲಗೊಳಿಸಿರಿ.",
			"4": "<sup>4</sup> ಭಯಭ್ರಾಂತಹೃದಯರಿಗೆ - ಬಲಗೊಳ್ಳಿರಿ, ಹೆದರಬೇಡಿರಿ; ಇಗೋ, ನಿಮ್ಮ ದೇವರು ಮುಯ್ಯಿ ತೀರಿಸುವದಕ್ಕೂ ದೈವಿಕ ಪ್ರತಿಫಲವನ್ನು ಕೊಡುವದಕ್ಕೂ ಬರುವನು; ತಾನೇ ಬಂದು ನಿಮ್ಮನ್ನು ರಕ್ಷಿಸುವನು ಎಂದು ಹೇಳಿರಿ.",
			"5": "<sup>5</sup> ಆಗ ಕುರುಡರ ಕಣ್ಣು ಕಾಣುವದು, ಕಿವುಡರ ಕಿವಿ ಕೇಳುವದು, ಕುಂಟನು ಜಿಂಕೆಯಂತೆ ಹಾರುವನು, ಮೂಕನ ನಾಲಿಗೆಯು ಹರ್ಷಧ್ವನಿಗೈಯುವದು;",
			"6": "<sup>6</sup> ಅರಣ್ಯದಲ್ಲಿ ಒರತೆಗಳು ಒಡೆಯುವವು, ಒಣ ನೆಲದಲ್ಲಿ ನದಿಗಳು ಹುಟ್ಟಿ ಹರಿಯುವವು.",
			"7": "<sup>7</sup> ಬೆಂಗಾಡು ಸರೋವರವಾಗುವದು; ಒಣನೆಲದಲ್ಲಿ ಬುಗ್ಗೆಗಳುಕ್ಕುವವು; ನರಿಗಳು ಮಲಗುತ್ತಿದ್ದ ಹಕ್ಕೆಯು ಆಪುಜಂಬುಗಳ ಪ್ರದೇಶವಾಗುವದು.",
			"8": "<sup>8</sup> ಅಲ್ಲಿ ರಾಜಮಾರ್ಗವಿರುವದು, ಹೌದು [ಹೋಗಿಬರುವ] ದಾರಿ; ಅದು ಪರಿಶುದ್ಧಮಾರ್ಗ ಎನಿಸಿಕೊಳ್ಳುವದು; ಯಾವ ಅಶುದ್ಧನೂ ಅಲ್ಲಿ ನಡೆಯನು, ಅದು ದೇವಜನರಿಗಾಗಿಯೇ ಇರುವದು, ಅಲ್ಲಿ ಹೋಗುವ ಮೂಢನೂ ದಾರಿತಪ್ಪನು.",
			"9": "<sup>9</sup> ಸಿಂಹವು ಅಲ್ಲಿರದು, ಕ್ರೂರ ಜಂತು ಅಲ್ಲಿ ಸೇರದು, ಕಾಣುವದೇ ಇಲ್ಲ; ಬಿಡುಗಡೆಯಾದವರೇ ಅಲ್ಲಿ ನಡೆಯುವರು;",
			"10": "<sup>10</sup> ಯೆಹೋವನು ವಿಮೋಚಿಸಿದವರು ಹಿಂದಿರುಗಿ ಶಾಶ್ವತಸಂತೋಷವೆಂಬ ಕಿರೀಟವನ್ನು ಧರಿಸಿಕೊಂಡು ಉತ್ಸಾಹಧ್ವನಿಯೊಡನೆ ಚೀಯೋನಿಗೆ ಸೇರುವರು; ಹರ್ಷಾನಂದಗಳನ್ನು ಅನುಭವಿಸುವರು, ಮೊರೆಯೂ ಕರಕರೆಯೂ ತೊಲಗಿಹೋಗುವವು."
		},
		"36": {
			"1": "<sup>1</sup> ಅರಸನಾದ ಹಿಜ್ಕೀಯನ ಆಳಿಕೆಯ ಹದಿನಾಲ್ಕನೆಯ ವರುಷದಲ್ಲಿ ಅಶ್ಶೂರದ ಅರಸನಾದ ಸನ್ಹೇರೀಬನು ಬಂದು ಯೆಹೂದ ಪ್ರಾಂತದೊಳಗೆ ಕೋಟೆಕೊತ್ತಲಗಳುಳ್ಳ ಎಲ್ಲಾ ಪಟ್ಟಣಗಳನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡನು.",
			"2": "<sup>2</sup> ಆಗ ಅಶ್ಶೂರದ ಅರಸನು ಲಾಕೀಷಿನಿಂದ ಮಹಾಸೈನ್ಯ ಸಹಿತನಾದ ರಬ್ಷಾಕೆ ಎಂಬವನನ್ನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿದ್ದ ಹಿಜ್ಕೀಯನ ಬಳಿಗೆ ಕಳುಹಿಸಿದನು. ಇವನು ಮಡಿವಾಳರ ಹೊಲದ ಮೇಲೆ ಹೋಗುವ ರಾಜಮಾರ್ಗದ ಹತ್ತಿರ ಅಲ್ಲಿನ ಕೆರೆಯ ಕಾಲುವೆಯ ಬಳಿಯಲ್ಲಿ ಪಾಳೆಯಮಾಡಿಕೊಂಡನು.",
			"3": "<sup>3</sup> ಆಮೇಲೆ ಹಿಲ್ಕೀಯನ ಮಗನೂ ರಾಜಗೃಹಾಧಿಪತಿಯೂ ಆದ ಎಲ್ಯಾಕೀಮ್, ಲೇಖಕನಾದ ಶೆಬ್ನ, ಆಸಾಫನ ಮಗನೂ ಮಂತ್ರಿಯೂ ಆಗಿದ್ದ ಯೋವ ಎಂಬವರು ಬಂದರು.",
			"4": "<sup>4</sup> ರಬ್ಷಾಕೆಯು ಅವರಿಗೆ - ನೀವು ಹೋಗಿ ಮಹಾರಾಜನಾದ ಅಶ್ಶೂರದ ಅರಸನ ಈ ಮಾತುಗಳನ್ನು ಹಿಜ್ಕೀಯನಿಗೆ ತಿಳಿಸಿರಿ - ಈ ನಿನ್ನ ಭರವಸಕ್ಕೆ ಯಾವ ಆಧಾರವುಂಟು?",
			"5": "<sup>5</sup> ಯುದ್ಧಕ್ಕೆ ಬೇಕಾದ ವಿವೇಕವೂ ಬಲವೂ ಉಂಟು ಎಂಬ ನಿನ್ನ ಮಾತು ಬರೀ ಬಾಯಿಮಾತೇ ಎಂದು ನಾನು ಹೇಳಬಲ್ಲೆ. ನೀನು ಯಾರನ್ನು ನಂಬಿಕೊಂಡು ನನಗೆ ವಿರೋಧವಾಗಿ ತಿರುಗಿಬಿದ್ದಿದ್ದೀ?",
			"6": "<sup>6</sup> ಜಜ್ಜಿದ ದಂಟಿಗೆ ಸಮಾನವಾದ ಐಗುಪ್ತವೆಂಬ ಕೋಲಿನ ಮೇಲೆ ಭರವಸವಿಟ್ಟಿರುತ್ತೀಯಷ್ಟೆ. ಒಬ್ಬನು ಅಂಥ ಕೋಲನ್ನು ಊರಿಕೊಳ್ಳುವದಾದರೆ ಅದು ಅವನ ಕೈಯನ್ನು ತಿವಿದು ಒಳಕ್ಕೆ ಹೋಗುತ್ತದೆ! ಐಗುಪ್ತದ ಅರಸನಾದ ಫರೋಹನಲ್ಲಿ ಭರವಸವಿಟ್ಟವರಿಗೆ ಅದೇ ಗತಿಯಾಗುವದು.",
			"7": "<sup>7</sup> ಒಂದು ವೇಳೆ ನೀನು - ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ನಂಬಿಕೊಂಡಿದ್ದೇವೆಂದು ಹೇಳಬಹುದು. ಹಿಜ್ಕೀಯನು ಇದೇ ಯಜ್ಞವೇದಿಯ ಮುಂದೆ ಆರಾಧನೆ ಮಾಡಬೇಕೆಂಬದಾಗಿ ಯೆಹೂದ್ಯರಿಗೂ ಯೆರೂಸಲೇಮಿನವರಿಗೂ ಆಜ್ಞಾಪಿಸಿ ಆ ಯೆಹೋವನ ಪೂಜಾ ಸ್ಥಳಗಳನ್ನೂ ಬೇರೆ ಎಲ್ಲಾ ಯಜ್ಞವೇದಿಗಳನ್ನೂ ಹಾಳು ಮಾಡಿದನಲ್ಲಾ!",
			"8": "<sup>8</sup> ನನ್ನ ಒಡೆಯನಾದ ಅಶ್ಶೂರದ ಅರಸನೊಂದಿಗೆ ಪಂಥಹಾಕುವದಕ್ಕೆ ನಿನಗೆ ಮನಸ್ಸುಂಟೋ? ಹಾಗಾದರೆ ಅವನು ನಿನಗೆ ಎರಡು ಸಾವಿರ ಕುದುರೆಗಳನ್ನು ಕೊಡುತ್ತಾನೆ; ನೀನು ಅಷ್ಟು ಮಂದಿ ಸವಾರರನ್ನು ಅವುಗಳ ಮೇಲೆ ಕುಳ್ಳಿರಿಸುವಿಯೋ?",
			"9": "<sup>9</sup> ಇದೂ ನಿನಗೆ ಅಸಾಧ್ಯವಾಗುವದಾದರೆ ನನ್ನ ಒಡೆಯನ ಸೇನಾಪತಿಗಳಲ್ಲಿ ಅತ್ಯಲ್ಪನನ್ನಾದರೂ ಸೋಲಿಸುವದು ಹೇಗೆ? ರಥಾಶ್ವಬಲಗಳಿಗಾಗಿ ಐಗುಪ್ತ್ಯರನ್ನು ನಂಬಿರುತ್ತೀಯೆಂದು ಕಾಣುತ್ತದೆ.",
			"10": "<sup>10</sup> ಈ ದೇಶವನ್ನು ಹಾಳುಮಾಡುವದಕ್ಕೆ ಯೆಹೋವನ ಚಿತ್ತವಿಲ್ಲದೆ ಬಂದೆನೆಂದು ನೆನಸುತ್ತೀಯೋ? ಇಲ್ಲಿಗೆ ಬಂದು ಇದನ್ನು ಹಾಳುಮಾಡಿಬಿಡು ಎಂದು ಯೆಹೋವನೇ ನನಗೆ ಆಜ್ಞಾಪಿಸಿದನು ಎನ್ನುವ ಅರಸನ ಮಾತುಗಳನ್ನು ತಿಳಿಸಿರಿ ಎಂದು ಹೇಳಿದನು.",
			"11": "<sup>11</sup> ಆಗ ಎಲ್ಯಾಕೀಮ್, ಶೆಬ್ನ, ಯೋವ ಎಂಬವರು ರಬ್ಷಾಕೆಗೆ - ನೀನು ಮಾತಾಡುವದು ಪೌಳಿಗೋಡೆಯ ಮೇಲಿರುವವರಿಗೆ ಕೇಳಿಸುತ್ತದೆ. ಆದದರಿಂದ ದಯವಿಟ್ಟು ನಿನ್ನ ಸೇವಕರಾದ ನಮ್ಮೊಡನೆ ಅರಾಮ್ಯ ಭಾಷೆಯಲ್ಲಿ ಮಾತಾಡು; ಅದು ನಮಗೆ ತಿಳಿಯುತ್ತದೆ. ಆದರೆ ಯೂದಾಯ ಭಾಷೆಯಲ್ಲಿ ಮಾತಾಡಬೇಡ ಎಂದು ಹೇಳಿದರು.",
			"12": "<sup>12</sup> ಅದಕ್ಕೆ ರಬ್ಷಾಕೆಯು - ನನ್ನ ಯಜಮಾನನು ನಿಮ್ಮ ಸಂಗಡವಾಗಲಿ ನಿಮ್ಮ ಒಡೆಯನ ಸಂಗಡವಾಗಲಿ ಮಾತಾಡಬೇಕೆಂದು ನನ್ನನ್ನು ಕಳುಹಿಸಲಿಲ್ಲ. ಈ ಗೋಡೆಯ ಮೇಲೆ ಕೂತಿರುವ ಜನರ ಸಂಗಡ ಮಾತಾಡುವದಕ್ಕೋಸ್ಕರ ಕಳುಹಿಸಿದ್ದಾನೆ. ಅವರು ನನಗೆ ವಿರುದ್ಧವಾಗಿ ನಿಂತರೆ ನಿಮ್ಮೊಡನೆ ಸ್ವಂತ ಮಲವನ್ನು ತಿಂದು ಸ್ವಂತ ಮೂತ್ರವನ್ನು ಕುಡಿಯಬೇಕಾಗುವದು ಎಂದು ಉತ್ತರಕೊಟ್ಟು",
			"13": "<sup>13</sup> ಎದ್ದು ಗೋಡೆಯ ಮೇಲೆ ನಿಂತವರಿಗೆ ಯೂದಾಯ ಭಾಷೆಯಲ್ಲಿ - ಅಶ್ಶೂರದ ಮಹಾರಾಜನ ಮಾತನ್ನು ಕೇಳಿರಿ;",
			"14": "<sup>14</sup> ಇವನು ನಿಮಗೆ - ಹಿಜ್ಕೀಯನಿಂದ ಮೋಸಹೋಗಬೇಡಿರಿ. ಅವನು ನಿಮ್ಮನ್ನು ಬಿಡಿಸಲಾರನು.",
			"15": "<sup>15</sup> ಹಿಜ್ಕೀಯನು ನಿಮಗೆ - ಯೆಹೋವನನ್ನು ನಂಬಿರಿ; ಆತನು ನಮ್ಮನ್ನು ಹೇಗೂ ರಕ್ಷಿಸುವನು; ಈ ಪಟ್ಟಣವು ಅಶ್ಶೂರದ ಅರಸನ ವಶವಾಗುವದಿಲ್ಲ ಎಂಬದಾಗಿ ಹೇಳಿದರೆ ಅವನಿಗೆ ಕಿವಿಗೊಡಬೇಡಿರಿ, ಒಪ್ಪಬೇಡಿರಿ.",
			"16": "<sup>16</sup> ಅಶ್ಶೂರದ ಅರಸನಾದ ನನ್ನ ಮಾತನ್ನು ಕೇಳಿರಿ; ನನ್ನೊಡನೆ ಒಡಂಬಡಿಕೆಮಾಡಿಕೊಂಡು ನನ್ನ ಆಶ್ರಯದಲ್ಲಿ ಸೇರಿರಿ. ನಿಮ್ಮಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬನೂ ತನ್ನ ತನ್ನ ಅಂಜೂರದ ಮರ, ದ್ರಾಕ್ಷಾಲತೆ ಇವುಗಳ ಹಣ್ಣುಗಳನ್ನು ತಿಂದು ತನ್ನ ತನ್ನ ಬಾವಿಯ ನೀರನ್ನು ಕುಡಿಯುವನು.",
			"17": "<sup>17</sup> ಸ್ವಲ್ಪ ಕಾಲವಾದನಂತರ ನಾನು ಬಂದು ನಿಮ್ಮನ್ನು ಧಾನ್ಯ ದ್ರಾಕ್ಷಾರಸ, ಆಹಾರ, ದ್ರಾಕ್ಷೇತೋಟ ಇವು ಸಮೃದ್ಧಿಯಾಗಿರುವ ನಿಮ್ಮ ದೇಶಕ್ಕೆ ಸಮಾನವಾದ ಇನ್ನೊಂದು ದೇಶಕ್ಕೆ ಕರಕೊಂಡು ಹೋಗುವೆನು;",
			"18": "<sup>18</sup> ಯೆಹೋವನು ನಮ್ಮನ್ನು ರಕ್ಷಿಸುವನು ಎಂಬ ನಂಬಿಕೆಯನ್ನು ಹಿಜ್ಕೀಯನು ನಿಮ್ಮಲ್ಲಿ ಹುಟ್ಟಿಸಾನು ನೋಡಿಕೊಳ್ಳಿರಿ. ಯಾವ ಜನಾಂಗದ ದೇವತೆಯು ತನ್ನ ದೇಶವನ್ನು ಅಶ್ಶೂರದ ಅರಸನ ಕೈಗೆ ಸಿಕ್ಕದಂತೆ ಕಾಪಾಡಿತು?",
			"19": "<sup>19</sup> ಹಮಾತ್, ಅರ್ಪಾದ್, ಸೆಫರ್ವಯಿಮ್ ಎಂಬ ಪಟ್ಟಣಗಳ ದೇವತೆಗಳೇನಾದವು? ಅವು ಸಮಾರ್ಯವನ್ನು ನನ್ನ ಕೈ ತಪ್ಪಿಸಿದವೋ?",
			"20": "<sup>20</sup> ಯಾವ ಜನಾಂಗದ ದೇವತೆಯಾದರೂ ತನ್ನ ದೇಶವನ್ನು ನನ್ನ ಕೈಯಿಂದ ಬಿಡಿಸಲಾರದೆ ಹೋದ ಮೇಲೆ ಯೆಹೋವನು ಯೆರೂಸಲೇಮನ್ನು ನನ್ನ ಕೈಗೆ ತಪ್ಪಿಸಿ ಕಾಪಾಡುವನೋ ಅನ್ನುತ್ತಾನೆ ಎಂದು ಹೇಳಿದನು.",
			"21": "<sup>21</sup> ಆ ಸೇನಾಧಿಪತಿಗಳಿಗೆ ಯಾವ ಉತ್ತರವನ್ನೂ ಕೊಡಬಾರದೆಂದು ಅರಸನು ತನ್ನ ಪ್ರಜೆಗಳಿಗೆ ಆಜ್ಞಾಪಿಸಿದ್ದರಿಂದ ಅವರು ಸುಮ್ಮನಿದ್ದರು; ಏನೂ ಅನ್ನಲಿಲ್ಲ.",
			"22": "<sup>22</sup> ಹಿಲ್ಕೀಯನ ಮಗನೂ ರಾಜಗೃಹಾಧಿಪತಿಯೂ ಆಗಿದ್ದ ಎಲ್ಯಾಕೀಮ್, ಲೇಖಕನಾದ ಶೆಬ್ನ, ಆಸಾಫನ ಮಗನೂ ಮಂತ್ರಿಯೂ ಆಗಿದ್ದ ಯೋವ ಎಂಬವರು ತಮ್ಮ ಬಟ್ಟೆಗಳನ್ನು ಹರಿದುಕೊಂಡು ಅರಸನಾದ ಹಿಜ್ಕೀಯನ ಬಳಿಗೆ ಬಂದು ಅವನಿಗೆ ರಬ್ಷಾಕೆಯ ಮಾತುಗಳನ್ನು ತಿಳಿಸಿದರು."
		},
		"37": {
			"1": "<sup>1</sup> ಆಗ ಹಿಜ್ಕೀಯನು ಬಟ್ಟೆಗಳನ್ನು ಹರಿದುಕೊಂಡು ಗೋಣೀತಟ್ಟನ್ನು ಕಟ್ಟಿಕೊಂಡು ಯೆಹೋವನ ಆಲಯಕ್ಕೆ ಹೋದನು.",
			"2": "<sup>2</sup> ಇದಲ್ಲದೆ ಅವನು ರಾಜಗೃಹಾಧಿಪತಿಯಾದ ಎಲ್ಯಾಕೀಮ್, ಲೇಖಕನಾದ ಶೆಬ್ನ, ವೃದ್ಧಯಾಜಕರು ಇವರನ್ನು ಕರಿಸಿ - ನೀವು ಗೋಣೀತಟ್ಟನ್ನು ಕಟ್ಟಿಕೊಂಡು ಆಮೋಚನ ಮಗನೂ ಪ್ರವಾದಿಯೂ ಆಗಿರುವ ಯೆಶಾಯನ ಬಳಿಗೆ ಹೋಗಿ ಹೀಗೆ ಹೇಳಿರಿ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"3": "<sup>3</sup> ಅವರು ಅದರಂತೆ ಯೆಶಾಯನ ಬಳಿಯಲ್ಲಿ - ಈ ದಿವಸದಲ್ಲಿ ನಮಗೆ ಮಹಾಕಷ್ಟವೂ ಶಿಕ್ಷೆಯೂ ನಿಂದೆಯೂ ಸಂಭವಿಸಿರುತ್ತದೆ. ಹೆರಿಗೆಯ ಕಾಲ ಬಂದದೆ; ಆದರೆ ಹೆರುವದಕ್ಕೆ ಬಲ ಸಾಲದು.",
			"4": "<sup>4</sup> ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನು ಅಶ್ಶೂರದ ಅರಸನಿಂದ ಕಳುಹಿಸಲ್ಪಟ್ಟ ರಬ್ಷಾಕೆಯ ನಿಂದಾವಾಕ್ಯಗಳನ್ನು ಕೇಳಿರುವನು; ಅವನು ತನ್ನ ಯಜಮಾನನ ಹೆಸರಿನಲ್ಲಿ ಜೀವಸ್ವರೂಪನಾದ ದೇವರನ್ನು ದೂಷಿಸಿದ್ದರಿಂದ ಆತನು ಅವನಿಗೆ ಮುಯ್ಯಿತೀರಿಸಾನು. ಆದದರಿಂದ ಉಳಿದಿರುವ ಸ್ವಲ್ಪ ಜನರಿಗೋಸ್ಕರ ಆತನನ್ನು ಪ್ರಾರ್ಥಿಸು ಎಂಬದಾಗಿ ಹಿಜ್ಕೀಯನು ಅನ್ನುತ್ತಾನೆ ಎಂದು ಹೇಳಿದರು.",
			"5": "<sup>5</sup> ಯೆಶಾಯನು ಅರಸನಾದ ಹಿಜ್ಕೀಯನ ಕಡೆಯಿಂದ ತನ್ನ ಬಳಿಗೆ ಬಂದ ಸೇವಕರಿಗೆ - ಅಶ್ಶೂರದ ಅರಸನ ಸೇವಕರು ನನ್ನನ್ನು ದೂಷಿಸಿದ ಮಾತುಗಳನ್ನು ಕೇಳಿದ್ದೀ; ಅವುಗಳಿಗೆ ನೀನು ಹೆದರಬೇಡ.",
			"7": "<sup>7</sup> ಇಗೋ, ನಾನು ಅವನ ಮೇಲೆ [ಭಯದ] ಆತ್ಮವನ್ನು ಬರಮಾಡುವೆನು. ಅವನು ಒಂದು ಸುದ್ದಿಯನ್ನು ಕೇಳಿ ತಿರುಗಿಕೊಂಡು ಸ್ವದೇಶಕ್ಕೆ ಹೋಗಿ ಅಲ್ಲಿ ಕತ್ತಿಯಿಂದ ಬೀಳುವಂತೆ ಮಾಡುವೆನು ಎಂಬೀ ಯೆಹೋವನ ಮಾತನ್ನು ನಿಮ್ಮ ಯಜಮಾನನಿಗೆ ಹೇಳಿರಿ ಎಂಬದಾಗಿ ಉತ್ತರಕೊಟ್ಟನು.",
			"8": "<sup>8</sup> ರಬ್ಷಾಕೆಯು ಹಿಂದಿರುಗಿ ಹೋಗುವಾಗ ದಾರಿಯಲ್ಲಿ ಅಶ್ಶೂರದ ಅರಸನು ಲಾಕೀಷನ್ನು ಬಿಟ್ಟು ಹೋದನೆಂಬ ವರ್ತಮಾನ ಕೇಳಿ ಲಿಬ್ನಕ್ಕೆ ಹೋಗಿ ಅಲ್ಲಿ ಅವನನ್ನು ಕಂಡನು. ಆಗ ಅವನು ಆ ಪಟ್ಟಣಕ್ಕೆ ವಿರೋಧವಾಗಿ ಯುದ್ಧಮಾಡುತ್ತಾ ಇದ್ದನು.",
			"9": "<sup>9</sup> ಅಷ್ಟರಲ್ಲಿ ಕೂಷಿನ ಅರಸನಾದ ತಿರ್ಹಾಕನು ತನಗೆ ವಿರೋಧವಾಗಿ ಹೊರಟಿದ್ದಾನೆಂಬ ಸುದ್ದಿಯನ್ನು ಅಶ್ಶೂರದ ಅರಸನು ಕೇಳಿ -",
			"10": "<sup>10</sup> [ಹಿಜ್ಕೀಯನೇ,] ನೀನು ನಂಬುವ ದೇವರು - ಯೆರೂಸಲೇಮು ಅಶ್ಶೂರದ ಅರಸನಿಗೆ ವಶವಾಗುವದಿಲ್ಲವೆಂದು ಹೇಳಿ ನಿನ್ನನ್ನು ಮೋಸಗೊಳಿಸಾನು.",
			"11": "<sup>11</sup> ಅಶ್ಶೂರದ ಅರಸರು ಎಲ್ಲಾ ರಾಜ್ಯಗಳನ್ನು ಪೂರ್ಣವಾಗಿ ನಾಶ ಮಾಡಿದರೆಂದು ಕೇಳಿದಿಯಲ್ಲಾ; ಹೀಗಿದ್ದ ಮೇಲೆ ನೀನು ಉಳಿಯುವಿಯೋ?",
			"12": "<sup>12</sup> ನನ್ನ ತಂದೆತಾತಂದಿರು ಗೋಜಾನ್, ಖಾರಾನ್, ರೆಚೆಫ್, ಎಂಬ ಪಟ್ಟಣಗಳ ಜನರನ್ನೂ ತೆಲಸ್ಸಾರ್ ಪ್ರಾಂತದಲ್ಲಿರುವ ಎದೆನಿನ ಜನರನ್ನೂ ನಾಶಮಾಡುವದಕ್ಕೆ ಹೋದಾಗ ಅವರ ದೇವತೆಗಳು ಅವರನ್ನು ಕಾಪಾಡಿದವೋ?",
			"13": "<sup>13</sup> ಹಮಾತ್, ಅರ್ಪಾದ್, ಸೆಫರ್ವಯಿಮ್, ಹೇನ, ಇವ್ವಾ ಎಂಬ ಪಟ್ಟಣಗಳ ಅರಸರು ಏನಾದರು ಎಂಬೀ ನನ್ನ ಮಾತನ್ನು ಯೆಹೂದದ ಅರಸನಾದ ಹಿಜ್ಕೀಯನಿಗೆ ಹೇಳಿರಿ ಎಂಬದಾಗಿ ದೂತರನ್ನು ಹಿಜ್ಕೀಯನ ಬಳಿಗೆ ಕಳುಹಿಸಿದನು.",
			"14": "<sup>14</sup> ಹಿಜ್ಕೀಯನು ಆ ದೂತರು ತಂದ ಪತ್ರವನ್ನು ತೆಗೆದುಕೊಂಡು ಓದಿದ ನಂತರ ಯೆಹೋವನ ಆಲಯಕ್ಕೆ ಹೋಗಿ ಅದನ್ನು ಯೆಹೋವನ ಮುಂದೆ ತೆರೆದಿಟ್ಟು",
			"15": "<sup>15</sup> ಆತನಿಗೆ - ಕೆರೂಬಿಗಳ ಮೇಲೆ ಆಸೀನನಾಗಿರುವಾತನೇ, ಇಸ್ರಾಯೇಲ್ ದೇವರೇ, ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೇ, ಎಲ್ಲಾ ಭೂರಾಜ್ಯಗಳನ್ನು ಆಳುವ ದೇವರು ನೀನೊಬ್ಬನೇ; ಪರಲೋಕ, ಭೂಲೋಕಗಳನ್ನುಂಟು ಮಾಡಿದವನು ನೀನೇ.",
			"17": "<sup>17</sup> ಯೆಹೋವನೇ, ಕಿವಿಗೊಟ್ಟು ಕೇಳು; ಯೆಹೋವನೇ, ಕಣ್ಣಿಟ್ಟು ನೋಡು. ಸನ್ಹೇರೀಬನು ಜೀವಸ್ವರೂಪದೇವರಾದ ನಿನ್ನನ್ನು ನಿಂದಿಸುವದಕ್ಕೋಸ್ಕರ ಹೇಳಿ ಕಳುಹಿಸಿದ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ಮನಸ್ಸಿಗೆ ತಂದುಕೋ.",
			"18": "<sup>18</sup> ಯೆಹೋವನೇ, ಅಶ್ಶೂರದ ಅರಸರು ಸಕಲಜನಾಂಗಗಳನ್ನೂ ಅವರ ದೇಶಗಳನ್ನೂ ಹಾಳುಮಾಡಿ",
			"19": "<sup>19</sup> ಅವರ ದೇವತೆಗಳನ್ನು ಬೆಂಕಿಯಲ್ಲಿ ಹಾಕಿದ್ದು ನಿಜ, ಅವು ದೇವತೆಗಳಲ್ಲ, ಮನುಷ್ಯರು ಕೆತ್ತಿದ ಕಲ್ಲು ಮರಗಳ ಬೊಂಬೆಗಳಷ್ಟೆ; ಆದದರಿಂದಲೇ ಅವು ಅವರಿಂದ ಹಾಳಾದವು.",
			"20": "<sup>20</sup> ಯೆಹೋವನೇ, ನಮ್ಮ ದೇವರೇ, ನೀನೇ ಅದ್ವಿತೀಯನಾದ ಯೆಹೋವನೆಂಬದನ್ನು ಭೂರಾಜ್ಯಗಳೆಲ್ಲವೂ ತಿಳಿದುಕೊಳ್ಳುವಂತೆ ನಮ್ಮನ್ನು ಇವನ ಕೈಯಿಂದ ಬಿಡಿಸು ಎಂದು ಪ್ರಾರ್ಥಿಸಿದನು.",
			"21": "<sup>21</sup> ಆಗ ಆಮೋಚನ ಮಗನಾದ ಯೆಶಾಯನು ಹಿಜ್ಕೀಯನಿಗೆ ಹೇಳಿ ಕಳುಹಿಸಿದ್ದೇನಂದರೆ - ಇಸ್ರಾಯೇಲ್ ದೇವರಾದ ಯೆಹೋವನ ಈ ಮಾತುಗಳನ್ನು ಕೇಳು; ನೀನು ಅಶ್ಶೂರದ ಅರಸನಾದ ಸನ್ಹೇರೀಬನ ವಿಷಯವಾಗಿ ಮಾಡಿದ ಬಿನ್ನಹಗಳನ್ನು ಯೆಹೋವನು ಕೇಳಿ ಅವನನ್ನು ಕುರಿತು -",
			"22": "<sup>22</sup> ಕನ್ನಿಕೆಯಾಗಿರುವ ಚೀಯೋನ್ ಕುಮಾರ್ತೆಯು ನಿನ್ನನ್ನು ತಿರಸ್ಕರಿಸಿ ಪರಿಹಾಸ್ಯಮಾಡುತ್ತಾಳೆ; ಯೆರೂಸಲೇಮ್ ಕುಮಾರ್ತೆಯು ನಿನ್ನ ಹಿಂದೆ ತಲೆಯಾಡಿಸುತ್ತಾಳೆ.",
			"23": "<sup>23</sup> ನೀನು ಯಾರನ್ನು ನಿಂದಿಸಿ ಯಾರನ್ನು ದೂಷಿಸಿದಿ? ಯಾರಿಗೆ ವಿರೋಧವಾಗಿ ಬಾಯ್ದೆರೆದು ಒದರಿದಿ? ನೀನು ಸೊಕ್ಕಿನಿಂದ ನೋಡಿದ್ದು ಯಾರನ್ನು? ಇಸ್ರಾಯೇಲ್ಯರ ಸದಮಲಸ್ವಾಮಿಯನ್ನಲ್ಲವೇ!",
			"24": "<sup>24</sup> ನೀನು ನಿನ್ನ ಸೇವಕರ ಮುಖಾಂತರವಾಗಿ ಕರ್ತನನ್ನು ನಿಂದಿಸಿ - ನನ್ನ ರಥಸಮೂಹದೊಡನೆ ಪರ್ವತಶಿಖರಗಳನ್ನು ಹತ್ತಿದ್ದೇನೆ; ಲೆಬನೋನಿನ ದುರ್ಗಮಸ್ಥಳಗಳಿಗೆ ಹೋಗಿದ್ದೇನೆ; ಅದರ ಎತ್ತರವಾದ ದೇವದಾರು ವೃಕ್ಷಗಳನ್ನೂ ಶ್ರೇಷ್ಠವಾದ ತುರಾಯಿ ಮರಗಳನ್ನೂ ಕಡಿದು ಬಿಟ್ಟಿದ್ದೇನೆ; ಅಲ್ಲಿನ ಬಹುದೂರದ ಶಿಖರವನ್ನೂ ಉದ್ಯಾನವನಗಳನ್ನೂ ಪ್ರವೇಶಿಸಿದ್ದೇನೆ;",
			"25": "<sup>25</sup> [ಪರದೇಶಗಳಲ್ಲಿ] ಅಗೆದು ನೀರು ತೆಗೆದು ಕುಡಿದಿದ್ದೇನೆ; ನನ್ನ ಪಾದಗಳಿಂದ ಐಗುಪ್ತದ ಎಲ್ಲಾ ಹೊಳೆಗಳನ್ನು ಬತ್ತಿಸಿದ್ದೇನೆ ಎಂಬದಾಗಿ ನೀನು ಕೊಚ್ಚಿಕೊಂಡಿದ್ದೀ.",
			"26": "<sup>26</sup> ಇದನ್ನು ನೀನು ಕೇಳಿಲ್ಲವೋ? ಹೀಗಾಗಬೇಕೆಂದು ಬಹುಕಾಲದ ಹಿಂದೆಯೇ ಗೊತ್ತುಮಾಡಿದೆನು; ಪೂರ್ವಕಾಲದಲ್ಲಿ ನಿರ್ಣಯಿಸಿದ್ದನ್ನು ಈಗ ನೆರವೇರಿಸಿದ್ದೇನೆ. ಆದದರಿಂದ ಕೋಟೆಕೊತ್ತಲುಗಳ ಪಟ್ಟಣಗಳನ್ನು ಹಾಳುದಿಬ್ಬಗಳನ್ನಾಗಿ ಮಾಡುವದು ನಿನಗೆ ಸಾಧ್ಯವಾಯಿತು;",
			"27": "<sup>27</sup> ಅವುಗಳ ನಿವಾಸಿಗಳು ಬಲವಿಲ್ಲದವರಾಗಿ ಆಶಾಭಂಗಪಟ್ಟು ಕಳವಳಗೊಂಡರು; ಅವರು ಹೊಲದ ಕಾಯಿಪಲ್ಯಕ್ಕೂ ಎಳೆಗರಿಕೆಗೂ ಮಾಳಿಗೆಯ ಮೇಲಣ ಹುಲ್ಲಿಗೂ ಹೊಡೆಹಾಯುವದಕ್ಕಿಂತ ಮೊದಲೇ [ಒಣಗಿ ಹೋದ] ಪೈರಿಗೂ ಸಮಾನರಾದರು.",
			"28": "<sup>28</sup> ನೀನು ಕೂತುಕೊಳ್ಳುವದೂ ಹೋಗುವದೂ ಬರುವದೂ ನನಗೆ ಗೊತ್ತುಂಟು; ನೀನು ನನ್ನ ಮೇಲೆ ರೌದ್ರಾವೇಶನಾಗಿರುವದನ್ನೂ ಬಲ್ಲೆನು.",
			"29": "<sup>29</sup> ನೀನು ನನಗೆ ವಿರೋಧವಾಗಿ ಕುಪಿತನಾಗಿರುವದೂ ಉಬ್ಬಿಕೊಂಡಿರುವದೂ ನನಗೆ ತಿಳಿದುಬಂತು. ಆದದರಿಂದ ನಿನಗೆ ಮೂಗುದಾರವನ್ನೂ ಕಡಿವಾಣವನ್ನೂ ಹಾಕಿ ಬಂದ ದಾರಿಯಿಂದಲೇ ನಿನ್ನನ್ನು ಹಿಂದಕ್ಕೆ ಎಳೆದುಕೊಂಡು ಹೋಗುವೆನು ಎಂಬದಾಗಿ ಹೇಳಿದ್ದಾನೆ.",
			"30": "<sup>30</sup> ಈ ಮಾತುಗಳು ನೆರವೇರುವವೆಂಬದಕ್ಕೆ ನೀವು ಈ ವರುಷದಲ್ಲಿ ಕೂಳೆಬೆಳೆಯನ್ನೂ ಮುಂದಿನ ವರುಷದಲ್ಲಿ ತನ್ನಷ್ಟಕ್ಕೆ ತಾನೇ ಬೆಳೆದದ್ದನ್ನೂ ಮೂರನೆಯ ವರುಷ ಹೊಲಗಳಲ್ಲಿ ಬಿತ್ತಿ ಕೊಯ್ದದ್ದನ್ನೂ ದ್ರಾಕ್ಷೇತೋಟಗಳಲ್ಲಿ ವ್ಯವಸಾಯಮಾಡಿ ಕೂಡಿಸಿದ್ದನ್ನೂ ಅನುಭವಿಸುವದೇ ಗುರುತಾಗಿರುವದು.",
			"31": "<sup>31</sup> ತಪ್ಪಿಸಿಕೊಂಡುಳಿದ ಯೆಹೂದ್ಯರು ದೇಶದಲ್ಲಿ ನೆಲೆಗೊಂಡು ಅಭಿವೃದ್ಧಿಯಾಗುವರು.",
			"32": "<sup>32</sup> ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಉಳಿದವರು ಹರಡಿಕೊಳ್ಳುವರು; ಚೀಯೋನ್ ಪರ್ವತದಲ್ಲಿ ತಪ್ಪಿಸಿಕೊಂಡವರು ಅಭಿವೃದ್ಧಿಹೊಂದುವರು; ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನ ಆಗ್ರಹವು ಇದನ್ನು ನೆರವೇರಿಸುವದು.",
			"33": "<sup>33</sup> ಆತನು ಅಶ್ಶೂರದ ಅರಸನನ್ನು ಕುರಿತು - ಅವನು ಪಟ್ಟಣವನ್ನು ಸಮೀಪಿಸುವದಿಲ್ಲ, ಅದಕ್ಕೆ ಬಾಣವನ್ನೆಸೆಯುವದಿಲ್ಲ, ಗುರಾಣಿ ಹಿಡಿದಿರುವವರನ್ನು ಕಳುಹಿಸುವದಿಲ್ಲ, ಅದನ್ನು ಕೆಡವಿಬಿಡುವದಕ್ಕೋಸ್ಕರ ಅದರ ಎದುರಾಗಿ ಮಣ್ಣಿನ ದಿಬ್ಬವನ್ನು ಮಾಡುವದಿಲ್ಲ.",
			"34": "<sup>34</sup> ಅವನು ಬಂದ ದಾರಿಯಿಂದಲೇ ಹಿಂದಿರುಗಿ ಹೋಗುವನು; ಈ ಪಟ್ಟಣಕ್ಕೆ ಬರುವದೇ ಇಲ್ಲ.",
			"35": "<sup>35</sup> ನನಗೋಸ್ಕರವಾಗಿಯೂ ನನ್ನ ಸೇವಕನಾದ ದಾವೀದನಿಗೋಸ್ಕರವಾಗಿಯೂ ಇದನ್ನು ಉಳಿಸಿ ಕಾಪಾಡುವೆನು ಎಂದು ಹೇಳುತ್ತಾನೆ ಎಂಬದೇ.",
			"36": "<sup>36</sup> ಆಗ ಯೆಹೋವನ ದೂತನು ಹೊರಟುಬಂದು ಅಶ್ಶೂರ್ಯರ ಪಾಳೆಯದಲ್ಲಿ ಲಕ್ಷದ ಎಂಭತ್ತೈದು ಸಾವಿರ ಮಂದಿ ಸೈನಿಕರನ್ನು ಸಂಹರಿಸಿದನು. ಬೆಳಿಗ್ಗೆ ಎದ್ದು ನೋಡುವಲ್ಲಿ ಅವರೆಲ್ಲಾ ಹೆಣಗಳಾಗಿದ್ದರು.",
			"37": "<sup>37</sup> ಆಗ ಅಶ್ಶೂರ್ಯರ ಅರಸನಾದ ಸನ್ಹೇರೀಬನು ಹಿಂದಿರುಗಿ ನಿನೆವೆ ಪಟ್ಟಣಕ್ಕೆ ಹೋಗಿ ಅಲ್ಲಿ ವಾಸಿಸಿದನು.",
			"38": "<sup>38</sup> ಅವನು ಒಂದು ದಿವಸ ಗುಡಿಗೆ ಹೋಗಿ ತನ್ನ ದೇವರಾದ ನಿಸ್ರೋಕನನ್ನು ಆರಾಧಿಸುತ್ತಿರುವಾಗ ಅದ್ರಮ್ಮೆಲೆಕ್, ಸರೆಚೆರ್ ಎಂಬ ಅವನ ಮಕ್ಕಳು ಅವನನ್ನು ಕತ್ತಿಯಿಂದ ಕೊಂದು ಅರರಾಟ್ ದೇಶಕ್ಕೆ ಓಡಿಹೋದರು. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ಏಸರ್‍ಹದ್ದೋನನು ಅರಸನಾದನು."
		},
		"38": {
			"1": "<sup>1</sup> ಆ ಕಾಲದಲ್ಲಿ ಹಿಜ್ಕೀಯನು ಮರಣಕರ ರೋಗದಲ್ಲಿ ಬಿದ್ದನು. ಆಗ ಆಮೋಚನ ಮಗನೂ ಪ್ರವಾದಿಯೂ ಆದ ಯೆಶಾಯನು ಅವನ ಬಳಿಗೆ ಬಂದು ಅವನಿಗೆ - ನಿನ್ನ ಮನೆಯ ವಿಷಯವಾಗಿ ವ್ಯವಸ್ಥೆಮಾಡು; ಏಕಂದರೆ ನೀನು ಉಳಿಯುವ ಹಾಗಿಲ್ಲ, ಸಾಯಬೇಕಾಗಿದೆ ಎಂಬದಾಗಿ ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ ಎಂದು ಹೇಳಿದನು.",
			"2": "<sup>2</sup> ಇದನ್ನು ಕೇಳಿದೊಡನೆ ಹಿಜ್ಕೀಯನು ಮೋರೆಯನ್ನು ಗೋಡೆಯ ಕಡೆಗೆ ತಿರುಗಿಸಿ -",
			"3": "<sup>3</sup> ಯೆಹೋವನೇ, ನಾನು ನಂಬಿಗಸ್ತನಾಗಿಯೂ ಯಥಾರ್ಥಚಿತ್ತನಾಗಿಯೂ ನಿನಗೆ ನಡೆದುಕೊಂಡು ನಿನ್ನ ದೃಷ್ಟಿಯಲ್ಲಿ ಒಳ್ಳೆಯವನಾಗಿದ್ದದ್ದನ್ನು ನೆನಪುಮಾಡಿಕೋ ಎಂದು ಪ್ರಾರ್ಥಿಸಿ ಬಹಳವಾಗಿ ಅತ್ತನು.",
			"4": "<sup>4</sup> ಆಗ ಯೆಹೋವನು ಯೆಶಾಯನಿಗೆ -",
			"5": "<sup>5</sup> ನೀನು ಹೋಗಿ ನನ್ನ ಪ್ರಜೆಗಳ ಪ್ರಭುವಾಗಿರುವ ಹಿಜ್ಕೀಯನಿಗೆ - ನಾನು ನಿನ್ನ ಪ್ರಾರ್ಥನೆಯನ್ನು ಕೇಳಿದ್ದೇನೆ, ನಿನ್ನ ಕಣ್ಣೀರನ್ನು ನೋಡಿದ್ದೇನೆ; ನಿನ್ನ ಆಯುಷ್ಯಕ್ಕೆ ಹದಿನೈದು ವರುಷಗಳನ್ನು ಕೂಡಿಸುತ್ತೇನೆ.",
			"6": "<sup>6</sup> ನಿನ್ನನ್ನೂ ಈ ಪಟ್ಟಣವನ್ನೂ ಅಶ್ಶೂರದ ಅರಸನ ಕೈಗೆ ಸಿಕ್ಕದಂತೆ ತಪ್ಪಿಸುವೆನು; ಈ ಪಟ್ಟಣವನ್ನು ಉಳಿಸಿ ಕಾಪಾಡುವೆನು,",
			"7": "<sup>7</sup> ಯೆಹೋವನು ನುಡಿದದ್ದನ್ನು ನೆರವೇರಿಸುವನು ಎಂಬದಕ್ಕೆ ಒಂದು ಗುರುತನ್ನು ಕಾಣುವಿ.",
			"8": "<sup>8</sup> ಇಗೋ, ಸೂರ್ಯನ ಇಳಿತರದಿಂದ ಆಹಾಜನ ಸೋಪಾನಪಂಕ್ತಿಯಲ್ಲಿ ಮುಂದೆ ಹೋಗಿದ್ದ ನೆರಳನ್ನು ಹತ್ತು ಮೆಟ್ಲು ಹಿಂದಕ್ಕೆ ಬರಮಾಡುವೆನು ಎಂದು ನಿನ್ನ ಪೂರ್ವಿಕನಾದ ದಾವೀದನ ದೇವರಾಗಿರುವ ಯೆಹೋವನು ಹೇಳುತ್ತಾನೆಂಬದಾಗಿ ತಿಳಿಸು ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು. ಅದರಂತೆ ಸೋಪಾನಪಂಕ್ತಿಯಲ್ಲಿ ಮುಂದೆ ಹೋಗಿದ್ದ ನೆರಳು ಹತ್ತು ಮೆಟ್ಲು ಹಿಂದಕ್ಕೆ ಬಂತು.",
			"9": "<sup>9</sup> ಯೆಹೂದದ ಅರಸನಾದ ಹಿಜ್ಕೀಯನು ರೋಗದಿಂದ ಆರೋಗ್ಯವನ್ನು ಹೊಂದಿ ಬರೆದದ್ದು -",
			"10": "<sup>10</sup> [ನನ್ನ ರೋಗದಲ್ಲಿ] ಹೀಗೆಲ್ಲಾ ಅಂದುಕೊಂಡೆನು - ನನ್ನ ಮಧ್ಯಪ್ರಾಯದಲ್ಲಿ ಪಾತಾಳದ್ವಾರಗಳೊಳಗೆ ಸೇರಿದ್ದೇನೆ, ನನ್ನ ಆಯುಷ್ಯದಲ್ಲಿ ಕಳೆದು ಮಿಕ್ಕ ವರುಷಗಳು ನನಗೆ ನಷ್ಟವಾದವು.",
			"11": "<sup>11</sup> ನಾನು ಇನ್ನು ಜೀವಲೋಕದವನಾಗಿ ಯಾಹು ಯೆಹೋವನ ದರ್ಶನವನ್ನು ಹೊಂದೆನಲ್ಲಾ, ಇನ್ನು ಭೂಲೋಕ ನಿವಾಸಿಗಳಲ್ಲೊಬ್ಬನಾಗಿ ಮನುಷ್ಯರನ್ನು ದೃಷ್ಟಿಸಲಾರೆನಲ್ಲಾ.",
			"12": "<sup>12</sup> ನನ್ನ ಆಶ್ರಯವು ಕುರುಬನ ಗುಡಾರದಂತೆ ಕಿತ್ತು ನನ್ನ ಕಡೆಯಿಂದ ಒಯ್ಯಲ್ಪಟ್ಟಿದೆ; ಆತನು ನನ್ನ ಆಯುಷ್ಯದ ಹಾಸನ್ನು ಕತ್ತರಿಸಿದ್ದರಿಂದ ನೆಯಿಗೆಯವನಂತೆ ನನ್ನ ಜೀವಮಾನವನ್ನು ಸುತ್ತಿಬಿಟ್ಟಿದ್ದೇನೆ; ಉದಯದಿಂದ ಅಸ್ತಮಾನದೊಳಗೇ ನನ್ನನ್ನು ತೀರಿಸುತ್ತೀ.",
			"13": "<sup>13</sup> ಆತನು ಸಿಂಹದಂತೆ ನನ್ನ ಎಲುಬುಗಳನ್ನೆಲ್ಲಾ ಮುರಿಯುತ್ತಿದ್ದರೂ ಬೆಳಗಿನ ತನಕ ತಡಕೊಂಡೇ ಇದ್ದೆನು. ಉದಯದಿಂದ ಅಸ್ತಮಾನದೊಳಗೇ ನನ್ನನ್ನು ತೀರಿಸುತ್ತೀಯಲ್ಲಾ ಎಂದು ಪ್ರಲಾಪಿಸಿದೆನು.",
			"14": "<sup>14</sup> ನಾನು ಬಾನಕ್ಕಿಯಂತೆಯೂ ಬಕದ ಹಾಗೂ ಕೀಚುಗುಟ್ಟಿದೆನು, ಪಾರಿವಾಳದಂತೆ ಮೊರೆಯಿಟ್ಟೆನು, ಯೆಹೋವನೇ, ನಾನು ಬಾಧೆಪಡುತ್ತಿದ್ದೇನೆ, ನನಗೆ ಹೊಣೆಯಾಗು ಎಂದು ಮೇಲೆ ಮೇಲೆ ದೃಷ್ಟಿಸುತ್ತಾ ಕಂಗೆಟ್ಟೆನು.",
			"15": "<sup>15</sup> ನಾನು ಏನು ಹೇಳಲಿ! ಆತನು ನನಗೆ ಮಾತು ಕೊಟ್ಟು ತಾನೇ ನೆರವೇರಿಸಿದ್ದಾನೆ! ನನ್ನ ಆತ್ಮಕ್ಕೆ ಸಂಭವಿಸಿದ ದುಃಖವನ್ನು ಸ್ಮರಿಸುತ್ತಾ ನನ್ನ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ಮೆಲ್ಲಗೆ ನಡೆಯುವೆನು.",
			"16": "<sup>16</sup> ಯೆಹೋವನೇ, ಇಂಥಾ ಸಂಭವಗಳಿಂದ ಮನುಷ್ಯರು ಬದುಕುತ್ತಾರೆ, ಅವುಗಳಿಂದಲೇ ನನ್ನ ಆತ್ಮವು ಜೀವಿಸುತ್ತದೆ. ಈಗ ನನ್ನನ್ನು ಸ್ವಸ್ಥಮಾಡಿ ಬದುಕಿಸು.",
			"17": "<sup>17</sup> ಆಹಾ, ನಾನು ಪಟ್ಟ ದುಃಖವು ನನ್ನ ಸುಖಕ್ಕಾಗಿಯೇ ಆಯಿತು, ನನ್ನ ಆತ್ಮವನ್ನು ನಾಶಕೂಪದಿಂದ ರಕ್ಷಿಸಿದ್ದು ನಿನ್ನ ಪ್ರೀತಿಯೇ. ನನ್ನ ಪಾಪಗಳನ್ನೆಲ್ಲಾ ನಿನ್ನ ಬೆನ್ನ ಹಿಂದೆ ಹಾಕಿಬಿಟ್ಟಿದ್ದೀ.",
			"18": "<sup>18</sup> ಪಾತಾಳದವರು ನಿನ್ನನ್ನು ಸ್ತುತಿಸರು, ಸತ್ತವರು ನಿನ್ನನ್ನು ಕೀರ್ತಿಸರು, ಅಧೋಲೋಕಕ್ಕೆ ಇಳಿದುಹೋದವರು ನಿನ್ನ ಸತ್ಯಸಂಧತೆಯನ್ನು ಆಶ್ರಯಿಸಲಾರರು.",
			"19": "<sup>19</sup> ಜೀವಂತನು ಜೀವಂತನೇ ನಿನ್ನನ್ನು ಸ್ತುತಿಸುವನು, ಹೌದು, ಜೀವಂತನಾದ ನಾನೇ ಈ ಹೊತ್ತು ನಿನ್ನನ್ನು ಹೊಗಳುವೆನು. ತಂದೆಯು ಮಕ್ಕಳಿಗೆ ನಿನ್ನ ಸತ್ಯಸಂಧತೆಯನ್ನು ಬೋಧಿಸುವನು.",
			"20": "<sup>20</sup> ಯೆಹೋವನು ನನಗೆ ರಕ್ಷಣಪರನಾಗಿದ್ದಾನಷ್ಟೆ; ನಮ್ಮ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ಯೆಹೋವನ ಆಲಯದಲ್ಲಿ ನಮ್ಮ ಕಿನ್ನರಿ ವೀಣೆಗಳನ್ನು ನುಡಿಸುವೆವು.",
			"21": "<sup>21</sup> (ಯೆಶಾಯನು - ಅಂಜೂರದ ಹಣ್ಣುಗಳ ಅಡೆಯನ್ನು ತರಿಸಿ ಕುರುವಿನ ಮೇಲೆ ಇಟ್ಟರೆ ಅರಸನು ಗುಣಹೊಂದುವನು ಎಂದು ಅಪ್ಪಣೆಕೊಡಲಾಗಿ",
			"22": "<sup>22</sup> ಹಿಜ್ಕೀಯನು - ನಾನು [ಗುಣಹೊಂದಿ] ಯೆಹೋವನ ಆಲಯವನ್ನು ಸೇರುವೆನೆಂಬದಕ್ಕೆ ಗುರುತೇನು ಎಂದು ಕೇಳಿದ್ದನು)."
		},
		"39": {
			"1": "<sup>1</sup> ಅದೇ ಕಾಲದಲ್ಲಿ ಬಲದಾನನ ಮಗನೂ ಬಾಬೆಲಿನ ಅರಸನೂ ಆದ ಮೆರೋದಕಬಲದಾನನೆಂಬವನು ಹಿಜ್ಕೀಯನು ಅಸ್ವಸ್ಥನಾಗಿದ್ದು ಗುಣಹೊಂದಿದನು ಎಂದು ಕೇಳಿ ಅವನಿಗೆ ಪತ್ರವನ್ನೂ ಬಹುಮಾನವನ್ನೂ ಕಳುಹಿಸಿದನು.",
			"2": "<sup>2</sup> ಹಿಜ್ಕೀಯನು ಬಂದ ದೂತರನ್ನು ಸಂತೋಷದಿಂದ ನೋಡಿ ಅವರಿಗೆ ಬೆಳ್ಳಿ, ಬಂಗಾರ, ಸುಗಂಧ ದ್ರವ್ಯ, ಪರಿಮಳತೈಲ ಮೊದಲಾದ ಪದಾರ್ಥಗಳಿರುವ ಮನೆಯನ್ನೂ ಆಯುಧ ಶಾಲೆಯನ್ನೂ ತನ್ನ ಭಂಡಾರದಲ್ಲಿದ್ದದ್ದೆಲ್ಲವನ್ನೂ ತೋರಿಸಿದನು. ಅವನ ಅರಮನೆಯಲ್ಲಿಯೂ ರಾಜ್ಯದಲ್ಲಿಯೂ ಅವರಿಗೆ ತೋರಿಸದಿದ್ದ ವಸ್ತುವು ಒಂದೂ ಇರಲಿಲ್ಲ.",
			"3": "<sup>3</sup> ಆಗ ಪ್ರವಾದಿಯಾದ ಯೆಶಾಯನು ಹಿಜ್ಕೀಯನ ಬಳಿಗೆ ಬಂದು ಅವನನ್ನು - ಆ ಮನುಷ್ಯರು ಎಲ್ಲಿಯವರು, ಏನು ಹೇಳಿದರು ಎಂದು ಕೇಳಲು ಅವನು - ಅವರು ಬಹುದೂರದೇಶದಿಂದ ಬಾಬೆಲಿನಿಂದ ನನ್ನ ಬಳಿಗೆ ಬಂದವರು ಎಂದು ಉತ್ತರ ಕೊಟ್ಟನು.",
			"4": "<sup>4</sup> ಯೆಶಾಯನು ತಿರಿಗಿ ಅವನನ್ನು - ಅವರು ನಿನ್ನ ಅರಮನೆಯಲ್ಲಿ ಏನು ನೋಡಿದರು ಎಂದು ಕೇಳಲು ಅವನು - ಅರಮನೆಯಲ್ಲಿರುವದೆಲ್ಲವನ್ನೂ ನೋಡಿದರು. ನನ್ನ ಭಂಡಾರದಲ್ಲಿ ನಾನು ಅವರಿಗೆ ತೋರಿಸದಿದ್ದ ವಸ್ತುವು ಒಂದೂ ಇರುವದಿಲ್ಲ ಅಂದನು.",
			"5": "<sup>5</sup> ಆಗ ಅವನು ಹಿಜ್ಕೀಯನಿಗೆ - ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನ ಮಾತನ್ನು ಕೇಳು;",
			"6": "<sup>6</sup> ಆತನು - ನಿನ್ನ ಪೂರ್ವಿಕರ ಕಾಲದಿಂದ ಇಂದಿನವರೆಗೆ ಅರಮನೆಯಲ್ಲಿ ಸಂಗ್ರಹವಾದದ್ದೆಲ್ಲವೂ ಬಾಬೆಲಿಗೆ ಒಯ್ಯಲ್ಪಡುವ ದಿನ ಬರುವದು; ಇಲ್ಲೇನೂ ಉಳಿಯುವದಿಲ್ಲ.",
			"7": "<sup>7</sup> ಬಾಬೆಲಿನ ಅರಸರು ಬಂದು ನೀನು ಪಡೆದ ಮಕ್ಕಳನ್ನು ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ಅವರನ್ನು ತಮ್ಮ ಅರಮನೆಯಲ್ಲಿ ಕಂಚುಕಿಗಳನ್ನಾಗಿ ನೇಮಿಸಿಕೊಳ್ಳುವರು ಅನ್ನುತ್ತಾನೆ ಎಂದು ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ಹೇಗೂ ನನ್ನ ಜೀವಮಾನದಲ್ಲಿ ತಪ್ಪದೆ ಸೌಭಾಗ್ಯವಿರುವದು ಅಂದುಕೊಂಡು ಹಿಜ್ಕೀಯನು ಯೆಶಾಯನಿಗೆ - ನೀನು ತಿಳಿಸಿದ ಯೆಹೋವನ ಮಾತು ಒಳ್ಳೇದಾಗಿದೆ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು."
		},
		"40": {
			"1": "<sup>1</sup> ನನ್ನ ಜನರನ್ನು ಸಂತೈಸಿರಿ, ಸಂತೈಸಿರಿ;",
			"2": "<sup>2</sup> ಯೆರೂಸಲೇಮಿನ ಸಂಗಡ ಹೃದಯಂಗಮವಾಗಿ ಮಾತಾಡಿರಿ; ಅದಕ್ಕೆ ಗಡು ತೀರಿತು, ವಿಧಿಸಿದ ದೋಷಫಲವೆಲ್ಲಾ ನೆರವೇರಿತು, ಅದರ ಎಲ್ಲಾ ಪಾಪಗಳಿಗೂ ಯೆಹೋವನ ಕೈಯಿಂದ ಎರಡರಷ್ಟು ಶಿಕ್ಷೆಯಾಯಿತು ಎಂದು ಆ ನಗರಿಗೆ ಕೂಗಿ ಹೇಳಿರಿ; ಇದೇ ನಿಮ್ಮ ದೇವರ ಆಜ್ಞೆ.",
			"3": "<sup>3</sup> ಇಗೋ ಒಂದು ವಾಣಿ! - ಅರಣ್ಯದಲ್ಲಿ ಯೆಹೋವನ ದಾರಿಯನ್ನು ಸರಿಪಡಿಸಿರಿ, ಅಡವಿಯಲ್ಲಿ ನಮ್ಮ ದೇವರಿಗೆ ರಾಜ ಮಾರ್ಗವನ್ನು ನೆಟ್ಟಗೆ ಮಾಡಿರಿ;",
			"4": "<sup>4</sup> ಎಲ್ಲಾ ಹಳ್ಳಕೊಳ್ಳಗಳು ಮುಚ್ಚಲ್ಪಡುವವು, ಎಲ್ಲಾ ಬೆಟ್ಟಗುಡ್ಡಗಳು ತಗ್ಗಿಸಲ್ಪಡುವವು, ಮಲೆನಾಡು ಬೈಲುಸೀಮೆಯಾಗುವದು, ಕೊರಕಲ ನೆಲವು ಸಮವಾಗುವದು.",
			"5": "<sup>5</sup> ಯೆಹೋವನ ಮಹಿಮೆಯು ಗೋಚರವಾಗುವದು, ಎಲ್ಲಾ ಮನುಷ್ಯರೂ ಒಟ್ಟಿಗೆ ಅದನ್ನು ಕಾಣುವರು, ಯೆಹೋವನ ಬಾಯೇ ಇದನ್ನು ನುಡಿದಿದೆ ಎಂದು ಒಬ್ಬನು ಕೂಗುತ್ತಾನೆ.",
			"6": "<sup>6</sup> ಆಹಾ, ವಾಣಿಯು ಮತ್ತೆ ಕೇಳಿಸಿ - ಕೂಗು ಎನ್ನುತ್ತದೆ. ಅದಕ್ಕೆ ನಾನು - ಏನು ಕೂಗಲಿ ಎಂದು ಕೇಳಲು ನರಜಾತಿಯೆಲ್ಲಾ ಹುಲ್ಲಿನ ಹಾಗಿದೆ, ಅದರ ಲಾವಣ್ಯವೆಲ್ಲಾ ಅಡವಿಯ ಹೂವಿನಂತಿದೆ.",
			"7": "<sup>7</sup> ಯೆಹೋವನ ಶ್ವಾಸವು ಅದರ ಮೇಲೆ ಬೀಸುವದರಿಂದ ಹುಲ್ಲು ಒಣಗಿಹೋಗುವದು, ಹೂವು ಬಾಡಿ ಹೋಗುವದು. ಜನವು ಹುಲ್ಲೇ ಹುಲ್ಲು!",
			"8": "<sup>8</sup> ಹುಲ್ಲು ಒಣಗಿಹೋಗುವದು, ಹೂವು ಬಾಡಿಹೋಗುವದು, ನಮ್ಮ ದೇವರ ಮಾತೋ ಸದಾಕಾಲವೂ ಇರುವದು ಎಂದು ಉತ್ತರವಾಯಿತು.",
			"9": "<sup>9</sup> ಶುಭಸಮಾಚಾರವನ್ನು ತಿಳಿಸಬಲ್ಲ ಚೀಯೋನೇ, ಉನ್ನತಪರ್ವತವನ್ನು ಹತ್ತಿಕೋ; ಸುವರ್ತಮಾನವನ್ನು ಪ್ರಕಟಿಸಬಲ್ಲ ಯೆರೂಸಲೇಮೇ, ನಿನ್ನ ಧ್ವನಿಯನ್ನು ಗಟ್ಟಿಯಾಗಿ ಎತ್ತು, ನಿರ್ಭಯವಾಗಿ ಎತ್ತಿ, ಯೆಹೂದದ ಪಟ್ಟಣಗಳಿಗೆ - ಇಗೋ, ನಿಮ್ಮ ದೇವರು!",
			"10": "<sup>10</sup> ಆಹಾ, ಕರ್ತನಾದ ಯೆಹೋವನು ಶೂರನಾಗಿ ಬರುವನು, ತನ್ನ ಭುಜಬಲದಿಂದಲೇ ಆಳುವನು; ಇಗೋ, ಆತನ ಕ್ರಿಯಾಲಾಭವು ಆತನೊಂದಿಗಿದೆ, ಆತನ ಶ್ರಮದ ಫಲವು ಆತನ ಮುಂದಿದೆ.",
			"11": "<sup>11</sup> ಆತನು ಕುರುಬನಂತೆ ತನ್ನ ಮಂದೆಯನ್ನು ಮೇಯಿಸುವನು, ಮರಿಗಳನ್ನು ಕೈಯಿಂದ ಕೂಡಿಸಿ ಎದೆಗೆತ್ತಿಕೊಳ್ಳುವನು. ಹಾಲುಕುಡಿಸುವ ಕುರಿಗಳನ್ನು ಮೆಲ್ಲಗೆ ನಡಿಸುವನು ಎಂದು ಸಾರು.",
			"12": "<sup>12</sup> ಯಾವನು ಸಾಗರಸಮುದ್ರಗಳನ್ನು ಸೇರೆಯಿಂದ ಅಳತೆಮಾಡಿದನು? ಯಾವನು ಆಕಾಶಮಂಡಲದ ಪರಿಮಾಣವನ್ನು ಗೇಣಿನಿಂದ ನಿರ್ಣಯಿಸಿದನು? ಭೂಲೋಕದ ಮಣ್ಣನ್ನೆಲ್ಲಾ ಕೊಳಗಕ್ಕೆ ತುಂಬಿದವನು ಯಾರು? ಬೆಟ್ಟಗಳನ್ನು ತ್ರಾಸಿನಿಂದಲೂ ಗುಡ್ಡಗಳನ್ನು ತಕ್ಕಡಿಯಿಂದಲೂ ತೂಗಿದವನು ಯಾರು?",
			"13": "<sup>13</sup> ಯೆಹೋವನ ಆತ್ಮಕ್ಕೆ ಯಾವನು ವಿಧಿಯನ್ನು ನೇಮಿಸಿದನು? ಯಾವನು ಆಲೋಚನಾಕರ್ತನಾಗಿ ಆತನಿಗೆ ಉಪದೇಶಿಸಿದನು?",
			"14": "<sup>14</sup> ಆತನು ಯಾವನ ಆಲೋಚನೆಯನ್ನು ಕೇಳಿದನು? ಯಾವನು ಆತನಿಗೆ ಬುದ್ಧಿಕಲಿಸಿ ಆತನನ್ನು ನ್ಯಾಯಮಾರ್ಗದಲ್ಲಿ ನಡೆಯಿಸಿದನು? ಯಾವನು ಆತನಿಗೆ ಜ್ಞಾನವನ್ನು ಬೋಧಿಸಿ ವಿವೇಕಮಾರ್ಗವನ್ನು ತೋರಿಸಿದನು?",
			"15": "<sup>15</sup> ಆಹಾ, ಆತನ ಗಣನೆಯಲ್ಲಿ ಜನಾಂಗಗಳು ಕಪಿಲೆಯಿಂದುದುರುವ ತುಂತುರಿನಂತೆಯೂ ತ್ರಾಸಿನ ತಟ್ಟೆಯ ದೂಳಿನ ಹಾಗೂ ಇರುತ್ತವೆ; ಇಗೋ, ದ್ವೀಪಗಳನ್ನು ಅಣುರೇಣುವಿನಂತೆ ಎತ್ತುತ್ತಾನೆ.",
			"16": "<sup>16</sup> [ಆತನಿಗರ್ಪಿಸತಕ್ಕ] ಹೋಮಕ್ಕೆ ಲೆಬನೋನಿನ ಪಶುಗಳು ಸಾಲವು, ಅಲ್ಲಿನ ವನವು ಸಮಿತ್ತಿಗೆ ಸಾಲದು.",
			"17": "<sup>17</sup> ಸಕಲ ಜನಾಂಗಗಳು ಆತನ ದೃಷ್ಟಿಯಲ್ಲಿ ಏನೂ ಇಲ್ಲದಂತಿವೆ, ಅವು ಆತನ ಎಣಿಕೆಯಲ್ಲಿ ಶುದ್ಧಶೂನ್ಯವೇ.",
			"18": "<sup>18</sup> ಹೀಗಿರಲು ದೇವರನ್ನು ಯಾರಿಗೆ ಹೋಲಿಸೀರಿ? ಯಾವ ರೂಪವನ್ನು ಆತನಿಗೆ ಸಮಮಾಡೀರಿ?",
			"19": "<sup>19</sup> ವಿಗ್ರಹವಾದರೋ ಶಿಲ್ಪಿಯು ಅದನ್ನು ಎರಕಹೊಯ್ಯುವನು, ಅಕ್ಕಸಾಲಿಗನು ಅದಕ್ಕೆ ಚಿನ್ನದ ಕವಚವನ್ನು ಬಡಿದು ಬೆಳ್ಳಿಯ ಸರಪಣಿಗಳನ್ನು ಮಾಡುವನು.",
			"20": "<sup>20</sup> [ಇಂಥದನ್ನು ದೇವರಿಗೆ] ಕಾಣಿಕೆಯನ್ನಾಗಿ ಪ್ರತಿಷ್ಠಿಸಿಕೊಳ್ಳಲಾರದ ಬಡವನು ಹುಳಿತುಹೋಗದ ಮರವನ್ನು ಹುಡುಕಿ ಅಲುಗದ ವಿಗ್ರಹವನ್ನು ಮಾಡಿಸಲು ಕುಶಲನನ್ನು ವಿಚಾರಿಸಿಕೊಳ್ಳುವನು.",
			"21": "<sup>21</sup> ನಿಮಗೆ ತಿಳಿದಿಲ್ಲವೋ? ನೀವು ಕೇಳಲಿಲ್ಲವೋ? ಆದಿಯಿಂದ ನಿಮಗೆ ಉಪದೇಶವಾಗಿ ಬಂದಿಲ್ಲವೋ? ಭೂಮಿಯು ಸ್ಥಾಪಿತವಾದಂದಿನಿಂದ ನೀವು ಗ್ರಹಿಸುತ್ತಿಲ್ಲವೋ?",
			"22": "<sup>22</sup> ಭೂಮಂಡಲನಿವಾಸಿಗಳು ಮಿಡತೆಗಳಂತೆ ಸಣ್ಣಗೆ ಕಾಣಿಸುವಷ್ಟು ಉನ್ನತವಾದ ಆಕಾಶದಲ್ಲಿ ಆತನು ಆಸೀನನಾಗಿದ್ದಾನೆ; ಆಕಾಶಮಂಡಲವನ್ನು ನವರುಬಟ್ಟೆಯಂತೆ ಹರಡಿ ನಿವಾಸದ ಗುಡಾರದಂತೆ ಎತ್ತಿಕಟ್ಟಿದ್ದಾನೆ.",
			"23": "<sup>23</sup> ಪ್ರಭುಗಳನ್ನು ನಿರ್ನಾಮ ಮಾಡುತ್ತಾನೆ, ಭೂಪತಿಗಳನ್ನು ಶೂನ್ಯಗೊಳಿಸುತ್ತಾನೆ.",
			"24": "<sup>24</sup> ಇವರು ನೆಡಲ್ಪಟ್ಟ ಕೂಡಲೆ, ಬಿತ್ತಲ್ಪಟ್ಟ ಕ್ಷಣವೇ, ಇವರ ಸಂತಾನವು ಭೂಮಿಯಲ್ಲಿ ಬೇರೂರಿದಾಗಲೇ, ಆತನ ಶ್ವಾಸದಿಂದ ಬಾಡುವರು, ಬಿರುಗಾಳಿಯು ಇವರನ್ನು ಒಣಹುಲ್ಲಿನಂತೆ ಬಡಿದುಕೊಂಡು ಹೋಗುವದು.",
			"25": "<sup>25</sup> ಹೀಗಿರಲು ನನ್ನನ್ನು ಯಾರಿಗೆ ಹೋಲಿಸಿ ಸರಿಸಮಾನಮಾಡುತ್ತೀರಿ ಎಂದು ಸದಮಲ ಸ್ವಾಮಿಯು ಕೇಳುತ್ತಾನೆ.",
			"26": "<sup>26</sup> ನಿಮ್ಮ ಕಣ್ಣುಗಳನ್ನು ಮೇಲಕ್ಕೆತ್ತಿಕೊಂಡು ನೋಡಿರಿ! ಈ ನಕ್ಷತ್ರಗಳನ್ನು ಸೃಷ್ಟಿಸಿದಾತನು ಯಾರು? ಈ ಸೈನ್ಯವನ್ನು ಲೆಕ್ಕಕ್ಕೆ ಸರಿಯಾಗಿ ಮುಂದರಿಸುತ್ತಾನಲ್ಲಾ; ಎಲ್ಲವನ್ನೂ ಹೆಸರೆತ್ತಿ ಕರೆಯುತ್ತಾನೆ; ಆತನು ಅತಿ ಬಲಾಢ್ಯನೂ ಮಹಾಶಕ್ತನೂ ಆಗಿರುವದರಿಂದ ಅವುಗಳೊಳಗೆ ಒಂದೂ ಕಡಿಮೆಯಾಗದು.",
			"27": "<sup>27</sup> ಯಾಕೋಬೇ, ಇಸ್ರಾಯೇಲೇ, ನನ್ನ ಮಾರ್ಗವು ಯೆಹೋವನಿಗೆ ಮರೆಯಾಗಿದೆ, ನನಗೆ ಸಿಕ್ಕಬೇಕಾದ ನ್ಯಾಯವು ನನ್ನ ದೇವರ ಲಕ್ಷ್ಯಕ್ಕೆ ಬಿದ್ದಿಲ್ಲವಲ್ಲಾ ಎಂದು ಏಕೆ ಅಂದುಕೊಳ್ಳುತ್ತೀ?",
			"28": "<sup>28</sup> ನೀನು ಗ್ರಹಿಸಲಿಲ್ಲವೋ? ಕೇಳಲಿಲ್ಲವೋ? ಯೆಹೋವನು ನಿರಂತರ ದೇವರೂ ಭೂಮಿಯ ಕಟ್ಟಕಡೆಗಳನ್ನು ನಿರ್ಮಿಸಿದವನೂ ಆಗಿದ್ದಾನೆ; ಆತನು ದಣಿದು ಬಳಲುವದಿಲ್ಲ; ಆತನ ವಿವೇಕವು ಪರಿಶೋಧನೆಗೆ ಅಗಮ್ಯ.",
			"29": "<sup>29</sup> ಸೋತವನಿಗೆ ತ್ರಾಣವನ್ನು ಅನುಗ್ರಹಿಸಿ ನಿರ್ಬಲನಿಗೆ ಬಹು ಬಲವನ್ನು ದಯಪಾಲಿಸುತ್ತಾನೆ.",
			"30": "<sup>30</sup> ಯುವಕರೂ ದಣಿದು ಬಳಲುವರು, ತರುಣರೂ ಸೊರಗಿ ಮುಗ್ಗರಿಸುವರು.",
			"31": "<sup>31</sup> ಯೆಹೋವನನ್ನು ನಿರೀಕ್ಷಿಸುವವರೋ ಹೊಸ ಬಲವನ್ನು ಹೊಂದುವರು; ಹದ್ದುಗಳಂತೆ ರೆಕ್ಕೆಗಳನ್ನು ಚಾಚಿಕೊಂಡು [ಏರುವರು]; ಓಡಿ ದಣಿಯರು, ನಡೆದು ಬಳಲರು."
		},
		"41": {
			"1": "<sup>1</sup> ದ್ವೀಪನಿವಾಸಿಗಳೇ, ನನ್ನ ಕಡೆಗೆ ತಿರುಗಿ ಮೌನದಿಂದಿರಿ; ಜನಾಂಗಗಳು [ಎಷ್ಟಾದರೂ] ತಮ್ಮನ್ನು ಬಲಪಡಿಸಿಕೊಳ್ಳಲಿ; ಸಮೀಪಕ್ಕೆ ಬಂದು ಮಾತಾಡಲಿ; ನ್ಯಾಯಸ್ಥಾನಕ್ಕೆ ಒಟ್ಟಿಗೆ ಹೋಗುವ.",
			"2": "<sup>2</sup> ಮೂಡಲಲ್ಲಿ ಒಬ್ಬನನ್ನು ಎಬ್ಬಿಸಿ ನ್ಯಾಯದ ಸಂಕಲ್ಪಾನುಸಾರವಾಗಿ ತನ್ನ ಪಾದಸನ್ನಿಧಿಗೆ ಕರೆದು ಜನಾಂಗಗಳನ್ನು ಅವನ ವಶಕ್ಕೆ ಕೊಟ್ಟು ಅವನನ್ನು ರಾಜರ ಮೇಲೆ ಆಳಗೊಡಿಸಿ ಅವರ ಕತ್ತಿಯನ್ನು ದೂಳನ್ನಾಗಿಯೂ ಬಿಲ್ಲನ್ನು ಗಾಳಿ ಬಡಿದುಕೊಂಡು ಹೋಗುವ ಒಣಹುಲ್ಲನ್ನಾಗಿಯೂ ಮಾಡಿ",
			"3": "<sup>3</sup> ಅವನು ಅವರನ್ನು ಹಿಂದಟ್ಟುತ್ತಾ ತಾನು ಎಂದೂ ಹೆಜ್ಜೆಯಿಡದ ಮಾರ್ಗದಲ್ಲಿ ಸುರಕ್ಷಿತವಾಗಿ ಮುಂದೆ ಹಾದುಹೋಗುವಂತೆ ಗೈದವನು ಯಾರು?",
			"4": "<sup>4</sup> ಇದನ್ನೆಲ್ಲಾ ನಡೆಯಿಸಿ ನೆರವೇರಿಸಿದವನು ಯಾರು? ಆದಿಯಿಂದ ಈಗಿನವರೆಗೂ ತಲತಲಾಂತರಗಳನ್ನು ಬರಮಾಡುವವನಾದ ಯೆಹೋವನೆಂಬ ನಾನೇ; ಹೌದು, ಆದಿಪುರುಷನೂ ಅಂತ್ಯಕಾಲದವರ ಸಂಗಡಿಗನೂ ಆಗಿರುವ ನಾನೊಬ್ಬನೇ.",
			"5": "<sup>5</sup> ದ್ವೀಪ ನಿವಾಸಿಗಳು ಕಂಡು ಬೆರಗಾದರು, ಭೂಮಿಯ ಕಟ್ಟಕಡೆಯವರು ನಡುಗಿದರು, ಎಲ್ಲರೂ ನೆರೆದುಬಂದರು.",
			"6": "<sup>6</sup> ಒಬ್ಬರಿಗೊಬ್ಬರು ಸಹಾಯಮಾಡಿದರು, ಒಬ್ಬನಿಗೊಬ್ಬನು ಧೈರ್ಯವಾಗಿರು ಎಂದು ಹೇಳಿದನು.",
			"7": "<sup>7</sup> ಬೆಸಿಗೆ ಚೆನ್ನಾಗಿದೆ ಎಂದು ಹೇಳಿ ಶಿಲ್ಪಿಯು ಎರಕದವನನ್ನೂ ಸುತ್ತಿಗೆಯಿಂದ ಸಮತಟ್ಟುವವನು ಅಡಿಗಲ್ಲಿನ ಮೇಲೆ ಕುಟ್ಟುವವನನ್ನೂ ಪ್ರೋತ್ಸಾಹಗೊಳಿಸಿದರು; ವಿಗ್ರಹವನ್ನು ಅಲುಗದಂತೆ ಮೊಳೆಗಳಿಂದ ಬಿಗಿದರು.",
			"8": "<sup>8</sup> ನನ್ನ ಸೇವಕನಾದ ಇಸ್ರಾಯೇಲೇ, ನಾನು ಆದುಕೊಂಡ ಯಾಕೋಬೇ, ನನ್ನ ಸ್ನೇಹಿತನಾದ ಅಬ್ರಹಾಮನ ಸಂತತಿಯೇ,",
			"9": "<sup>9</sup> ನಾನು ಭೂಮಿಯ ಕಟ್ಟಕಡೆಯಲ್ಲಿ ಹಿಡಿದು ದಿಗಂತಗಳಿಂದ ಕರೆದ ಜನವೇ, ನೀನು ನನ್ನ ಸೇವಕನು, ನಾನು ನಿನ್ನನ್ನು ಆರಿಸಿಕೊಂಡೆನು, ತಳ್ಳಲಿಲ್ಲ ಎಂದು ನಾನು ಹೇಳಿ ಸಂಬೋಧಿಸಿದ ಪ್ರಜೆಯೇ, ನೀನಂತು ಹೆದರಬೇಡ,",
			"10": "<sup>10</sup> ನಾನೇ ನಿನ್ನೊಂದಿಗಿದ್ದೇನೆ; ದಿಗ್ಭ್ರಮೆಗೊಳ್ಳದಿರು, ನಾನೇ ನಿನ್ನ ದೇವರು; ನಾನು ನಿನ್ನನ್ನು ಬಲಪಡಿಸುತ್ತೇನೆ; ಹೌದು, ನಿನಗೆ ಸಹಾಯಕೊಡುತ್ತೇನೆ; ನನ್ನ ಧರ್ಮದ ಬಲಗೈಯನ್ನು ನಿನಗೆ ಆಧಾರಮಾಡುತ್ತೇನೆ.",
			"11": "<sup>11</sup> ಆಹಾ, ನಿನ್ನ ಮೇಲೆ ಕಿಡಿಕಿಡಿಯಾದವರು ಆಶಾಭಂಗಪಟ್ಟು ಅವಮಾನ ಹೊಂದುವರು; ನಿನ್ನ ಸಂಗಡ ವ್ಯಾಜ್ಯವಾಡಿದವರು ನಾಶವಾಗಿ ಇಲ್ಲದೆ ಹೋಗುವರು;",
			"12": "<sup>12</sup> ನಿನ್ನೊಡನೆ ಹೋರಾಡಿದವರನ್ನು ಹುಡುಕಿದರೂ ಅವರು ನಿನಗೆ ಕಾಣಿಸರು; ನಿನಗೆ ವಿರುದ್ಧವಾಗಿ ಯುದ್ಧಮಾಡಿದವರು ಇಲ್ಲದೆ ಹೋಗಿ ನಿರ್ನಾಮವಾಗುವರು.",
			"13": "<sup>13</sup> ಭಯಪಡಬೇಡ, ನಾನೇ ನಿನಗೆ ಸಹಾಯಮಾಡುತ್ತೇನೆ ಎಂದು ನಿನಗೆ ಹೇಳುವ ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನೆಂಬ ನಾನೇ ನಿನ್ನ ಕೈಹಿಡಿಯುತ್ತೇನಲ್ಲಾ.",
			"14": "<sup>14</sup> ಕ್ರಿಮಿಪ್ರಾಯವಾದ ಯಾಕೋಬೇ, ಇಸ್ರಾಯೇಲ್ ಜನವೇ, ಭಯಪಡಬೇಡ; ನಾನೇ ನಿನಗೆ ಸಹಾಯಕನು, ಇಸ್ರಾಯೇಲಿನ ಸದಮಲಸ್ವಾಮಿಯು ನಿನಗೆ ವಿಮೋಚಕನು ಎಂದು ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ.",
			"15": "<sup>15</sup> ಇಗೋ, ನಾನು ನಿನ್ನನ್ನು ಮಸೆದ ಮೊನೆಹಲ್ಲಿನ ಹೊಸ ಹಂತಿಕುಂಟೆಯನ್ನಾಗಿ ಮಾಡಿದ್ದೇನೆ, ನೀನು ಬೆಟ್ಟಗಳನ್ನು ಒಕ್ಕುತ್ತಾ ಪುಡಿಪುಡಿಗೈದು ಗುಡ್ಡಗಳನ್ನು ಹೊಟ್ಟುಮಾಡುವಿ.",
			"16": "<sup>16</sup> ನೀನು ತೂರಲು ಅವುಗಳನ್ನು ಗಾಳಿಯು ಬಡಿದುಕೊಂಡು ಹೋಗುವದು; ಬಿರುಗಾಳಿಯು ಚೆಲ್ಲಾಪಿಲ್ಲಿ ಮಾಡುವದು; ನೀನಂತು ಯೆಹೋವನಲ್ಲಿ ಆನಂದಿಸುವಿ, ಇಸ್ರಾಯೇಲಿನ ಸದಮಲಸ್ವಾಮಿಯಲ್ಲಿ ಹೆಚ್ಚಳಪಡುವಿ.",
			"17": "<sup>17</sup> ಬಾಯಾರಿ ನಾಲಿಗೆ ಒಣಗಿ ನೀರನ್ನು ಹುಡುಕಿ ಹೊಂದದ ದೀನದರಿದ್ರರಿಗೆ ಯೆಹೋವನೆಂಬ ನಾನು ಪ್ರಸನ್ನನಾಗುವೆನು, ಇಸ್ರಾಯೇಲಿನ ದೇವರಾದ ನಾನು ಅವರನ್ನು ಕೈಬಿಡೆನು.",
			"18": "<sup>18</sup> ಬೋಳುಗುಡ್ಡಗಳಲ್ಲಿ ನದಿಗಳನ್ನು, ತಗ್ಗುಗಳಲ್ಲಿ ಒರತೆಗಳನ್ನು ಹೊರಡಿಸಿ ಅರಣ್ಯವನ್ನು ಕೆರೆಯಾಗಿಯೂ ಮರುಭೂಮಿಯನ್ನು ಬುಗ್ಗೆಗಳಾಗಿಯೂ ಮಾಡುವೆನು.",
			"19": "<sup>19</sup> ದೇವದಾರು ಕಸ್ತೂರಿಜಾಲಿ ಸುಗಂಧ ಒಲೀವ ಮರಗಳನ್ನು ಅಡವಿಯಲ್ಲಿ ನೆಡುವೆನು, ತುರಾಯಿ ತಪಸಿ ತಿಲಕ ವೃಕ್ಷಗಳನ್ನು ಅರಣ್ಯದಲ್ಲಿ ತೋಪಾಗಿ ಬೆಳೆಯಿಸುವೆನು;",
			"20": "<sup>20</sup> ಆಗ ಯೆಹೋವನ ಹಸ್ತವು ಇದನ್ನು ಮಾಡಿದೆ, ಹೌದು, ಇಸ್ರಾಯೇಲಿನ ಸದಮಲ ಸ್ವಾಮಿಯೇ ಸೃಷ್ಟಿಸಿದ್ದಾನೆ ಎಂದು ಎಲ್ಲರೂ ಕಂಡು ತಿಳಿದು ಮನಮುಟ್ಟಿ ಗ್ರಹಿಸಿಕೊಳ್ಳುವರು.",
			"21": "<sup>21</sup> ಯಾಕೋಬ್ಯರ ಅರಸನಾದ ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ನಿಮ್ಮ ವ್ಯಾಜ್ಯವು ಈಚೆಗೆ ಬರಲಿ, ನಿಮ್ಮ ಬಲವಾದ ನ್ಯಾಯಗಳನ್ನು ತೋರ್ಪಡಿಸಿರಿ.",
			"22": "<sup>22</sup> ತಮ್ಮ ನ್ಯಾಯಗಳನ್ನು ಮುಂದಕ್ಕೆ ತರಲಿ, ಭವಿಷ್ಯತ್ತನ್ನು ನಮಗೆ ತಿಳಿಸಲಿ; ನಡೆದ ಸಂಗತಿಗಳ ವಿಶೇಷವನ್ನು ಸೂಚಿಸಿರಿ, ನಾವು ಅವುಗಳನ್ನು ಮನಸ್ಸಿಗೆ ತಂದು ಅವುಗಳ ಪರಿಣಾಮವನ್ನು ಗ್ರಹಿಸುವೆವು; ಅಥವಾ ಭವಿಷ್ಯತ್ತುಗಳನ್ನು ಅರುಹಿರಿ.",
			"23": "<sup>23</sup> ನೀವು ದೇವರುಗಳೆಂದು ನಮಗೆ ಅರಿವು ಹುಟ್ಟುವಂತೆ ಮುಂದಾಗತಕ್ಕವುಗಳನ್ನು ತಿಳಿಸಿರಿ; ನಾವು ಒಟ್ಟಿಗೆ ಕಕ್ಕಾಬಿಕ್ಕಿಯಾಗಿ ನೋಡುವಂತೆ ಮೇಲಾಗಲಿ ಕೇಡಾಗಲಿ ಏನಾದರೂ ಮಾಡಿರಿ.",
			"24": "<sup>24</sup> ಆಹಾ, ನೀವು ಶೂನ್ಯವೇ! ನಿಮ್ಮ ಕಾರ್ಯವು ಮಟ್ಟಮಾಯವೇ! ನಿಮ್ಮನ್ನು ಮರೆಹೊಗುವವರು ತುಚ್ಫರೇ ಸರಿ!",
			"25": "<sup>25</sup> ನಾನು ಬಡಗಲಿಂದ ಒಬ್ಬನನ್ನು ಎಬ್ಬಿಸಿ ಕರತಂದಿದ್ದೇನೆ, ನನ್ನ ನಾಮವನ್ನು ಪ್ರಚುರಪಡಿಸತಕ್ಕವನು ಮೂಡಲಿಂದ ಬಂದಿದ್ದಾನೆ; ಅವನು ಉಪರಾಜರನ್ನು ಮಣ್ಣೇ ಎಂದು ಭಾವಿಸಿ ಮೇಲೆ ಬಿದ್ದು ಕುಂಬಾರನು ಜೇಡಿಯನ್ನು ತುಳಿಯುವ ಹಾಗೆ ತುಳಿಯುವನು.",
			"26": "<sup>26</sup> ಕಾರ್ಯವು ನಡೆಯುವದಕ್ಕೆ ಮುಂಚೆ [ಇವರಲ್ಲಿ] ಯಾವನು ಅದನ್ನು ಅರುಹಿದ್ದಾನೆ? ಅರುಹಿದ್ದರೆ [ಅವನು ನಿಜ ದೇವರೆಂದು] ನಮಗೆ ಗೊತ್ತಾಗುವದು; ಯಾವನು ಮುಂತಿಳಿಸಿದ್ದಾನೆ? ತಿಳಿಸಿದ್ದರೆ ಅವನನ್ನು ಸತ್ಯವಂತನೆನ್ನುವೆವು. ಯಾರೂ ಏನನ್ನೂ ತಿಳಿಸಬಲ್ಲವರಲ್ಲ, ಏನನ್ನೂ ಹೇಳತಕ್ಕವರಲ್ಲ, ನಿಮ್ಮ ಮಾತುಗಳು ಯಾರ ಕಿವಿಗೂ ಬೀಳುವದಿಲ್ಲ.",
			"27": "<sup>27</sup> ಇಗೋ ನೋಡು ಎನ್ನುವ ಮುಂದೂತನನ್ನು ಚೀಯೋನಿಗೆ, ಶುಭಸಮಾಚಾರ ತರತಕ್ಕವನನ್ನು ಯೆರೂಸಲೇಮಿಗೆ, ಅನುಗ್ರಹಿಸುವೆನು.",
			"28": "<sup>28</sup> ನಾನು ನೋಡಲು ಇವರಲ್ಲಿ ಸಮರ್ಥರು ಯಾರೂ ಇಲ್ಲ; ನಾನು ಪ್ರಶ್ನೆಮಾಡಿದರೆ ಒಂದು ಮಾತನ್ನಾದರೂ ಹೇಳಬಲ್ಲ ಆಲೋಚಕನು ಇಲ್ಲವೇ ಇಲ್ಲ.",
			"29": "<sup>29</sup> ಆಹಾ, ಇವರೆಲ್ಲಾ ಮಾಯೆ, ಇವರ ಕಾರ್ಯಗಳು ಶೂನ್ಯ, ಇವರ ಎರಕದ ಬೊಂಬೆಗಳು ಗಾಳಿಯೇ, ಹಾಳೇ!"
		},
		"42": {
			"1": "<sup>1</sup> ಇಗೋ, ನನ್ನ ಸೇವಕನು! ಇವನಿಗೆ ನಾನೇ ಆಧಾರ; ಇವನು ನನಗೆ ಇಷ್ಟನು, ನನ್ನ ಪ್ರಾಣಪ್ರಿಯನು. ಇವನಲ್ಲಿ ನನ್ನ ಆತ್ಮವನ್ನು ಇರಿಸಿದ್ದೇನೆ; ಇವನು ಅನ್ಯಜನಗಳಲ್ಲಿಯೂ ಸದ್ಧರ್ಮವನ್ನು ಪ್ರಚುರ ಪಡಿಸುವನು.",
			"2": "<sup>2</sup> ಇವನು ಕೂಗಾಡುವದಿಲ್ಲ, ಆರ್ಭಟಿಸುವದಿಲ್ಲ, ಬೀದಿಗಳಲ್ಲಿ ಇವನ ಧ್ವನಿಯೇ ಕೇಳಿಸುವದಿಲ್ಲ.",
			"3": "<sup>3</sup> ಜಜ್ಜಿದ ದಂಟನ್ನು ಮುರಿದು ಹಾಕದೆ ಕಳೆಗುಂದಿದ ದೀಪವನ್ನು ನಂದಿಸದೆ ಸದ್ಧರ್ಮವನ್ನು ಪ್ರಚುರಪಡಿಸಿ ಸಿದ್ಧಿಗೆ ತರುವನು.",
			"4": "<sup>4</sup> ಲೋಕದಲ್ಲಿ ಸದ್ಧರ್ಮವನ್ನು ಸ್ಥಾಪಿಸುವ ತನಕ ಇವನು ಕಳೆಗುಂದದೆಯೂ ಜಜ್ಜಿಹೋಗದೆಯೂ [ತನ್ನ ಕಾರ್ಯದಲ್ಲಿ ನಿರತನಾಗಿರುವನು]; ದ್ವೀಪದ್ವೀಪಾಂತರಗಳು ಇವನ ಧರ್ಮಪ್ರಮಾಣಕ್ಕೆ ಕಾದಿರುವವು.",
			"5": "<sup>5</sup> ಆಕಾಶಮಂಡಲವನ್ನುಂಟುಮಾಡಿ ಹರವಿ ಭೂಮಂಡಲವನ್ನೂ ಅದರಲ್ಲಿನ ಉತ್ಪತ್ತಿಯನ್ನೂ ವಿಸ್ತರಿಸಿ ಲೋಕದ ಜನರಿಗೆ ಪ್ರಾಣವನ್ನು, ಹೌದು, ಭೂಚರರಿಗೆ ಜೀವಾತ್ಮವನ್ನೂ ದಯಪಾಲಿಸುವ ಯೆಹೋವನೆಂಬ ದೇವರು ಹೀಗನ್ನುತ್ತಾನೆ -",
			"6": "<sup>6</sup> ನೀನು ಕುರುಡರಿಗೆ ಕಣ್ಣುಕೊಟ್ಟು ಬಂದಿಗಳನ್ನು ಸೆರೆಯಿಂದಲೂ ಕತ್ತಲಲ್ಲಿ ಬಿದ್ದವರನ್ನು ಕಾರಾಗೃಹದಿಂದಲೂ ಹೊರಗೆ ಕರತರಬೇಕು ಎಂದು",
			"7": "<sup>7</sup> ಯೆಹೋವನೆಂಬ ನಾನು ನನ್ನ ಧರ್ಮದ ಸಂಕಲ್ಪನುಸಾರವಾಗಿ ನಿನ್ನನ್ನು ಕರೆದು ಕೈಹಿಡಿದು ಕಾಪಾಡಿ ನನ್ನ ಜನರಿಗೆ ಒಡಂಬಡಿಕೆಯ ಆಧಾರವನ್ನಾಗಿಯೂ ಅನ್ಯಜನಗಳಿಗೆ ಬೆಳಕನ್ನಾಗಿಯೂ ನೇಮಿಸಿದ್ದೇನೆ.",
			"8": "<sup>8</sup> ನಾನೇ ಯೆಹೋವನು; ಇದೇ ನನ್ನ ನಾಮವು; ನನ್ನ ಮಹಿಮೆಯನ್ನು ಮತ್ತೊಬ್ಬನಿಗೆ ಸಲ್ಲಗೊಡಿಸೆನು, ನನ್ನ ಸ್ತೋತ್ರವನ್ನು ವಿಗ್ರಹಗಳ ಪಾಲು ಮಾಡೆನು.",
			"9": "<sup>9</sup> ಇಗೋ, ಮೊದಲನೆಯ ಸಂಗತಿಗಳು ನೆರವೇರಿವೆ, ಹೊಸ ಸಂಗತಿಗಳನ್ನು ಪ್ರಕಟಿಸುತ್ತೇವೆ; ಅವು ತಲೆದೋರುವದಕ್ಕೆ ಮುಂಚೆ ಅವುಗಳನ್ನು ನಿಮಗೆ ತಿಳಿಸುತ್ತೇನೆ.",
			"10": "<sup>10</sup> ಸಮುದ್ರಪ್ರಯಾಣಿಕರೇ, ಸಕಲ ಜಲಚರಗಳೇ, ದ್ವೀಪಾಂತರಗಳೇ, ದ್ವೀಪಾಂತರವಾಸಿಗಳೇ, ಯೆಹೋವನ ಘನತೆಗಾಗಿ ನೂತನಗೀತವನ್ನು ಹಾಡಿ ದಿಗಂತಗಳಲ್ಲಿಯೂ ಆತನನ್ನು ಕೀರ್ತಿಸಿರಿ.",
			"11": "<sup>11</sup> ಅರಣ್ಯವೂ ಅಲ್ಲಿನ ಊರುಗಳೂ ಕೇದಾರಿನವರ ಗ್ರಾಮಗಳೂ ಆರ್ಭಟಿಸಲಿ, ಸೆಲಪಟ್ಟಣದವರು ಹರ್ಷಧ್ವನಿಗೈದು ಪರ್ವತಾಗ್ರಗಳಲ್ಲಿ ಕೇಕೆ ಹಾಕಲಿ.",
			"12": "<sup>12</sup> ಯೆಹೋವನನ್ನು ಘನಪಡಿಸಿ ದ್ವೀಪಾಂತರಗಳಲ್ಲಿ ಆತನ ಸ್ತೋತ್ರವನ್ನು ಹಬ್ಬಿಸಲಿ.",
			"13": "<sup>13</sup> ಯೆಹೋವನು ಶೂರನಂತೆ ಹೊರಟು ಯುದ್ಧವೀರನ ಹಾಗೆ ತನ್ನ ರೌದ್ರವನ್ನು ಎಬ್ಬಿಸುವನು; ಆರ್ಭಟಿಸಿ ಗರ್ಜಿಸಿ ಶತ್ರುಗಳ ಮೇಲೆ ಬಿದ್ದು ತನ್ನ ಪರಾಕ್ರಮವನ್ನು ತೋರ್ಪಡಿಸುವನು.",
			"14": "<sup>14</sup> ನಾನು ಬಹುಕಾಲದಿಂದ ಸುಮ್ಮನೆ ಮೌನವಾಗಿ ನನ್ನನ್ನು ಬಿಗಿಹಿಡಿದಿದ್ದೆನು. [ಈಗ] ಹೆರುವವಳಂತೆ ಮೂಲುಗುತ್ತಾ ಅಸುರುಸಿರಿನೊಡನೆ ಏದುವೆನು.",
			"15": "<sup>15</sup> ಬೆಟ್ಟಗುಡ್ಡಗಳನ್ನು ನಾಶಪಡಿಸಿ ಅಲ್ಲಿಯ ಮೇವನ್ನೆಲ್ಲಾ ತಾರಿಸಿ ನದಿಗಳನ್ನು ಒಣದಿಣ್ಣೆ ಮಾಡಿ ಕೆರೆಗಳನ್ನು ಬತ್ತಿಸುವೆನು.",
			"16": "<sup>16</sup> ಮತ್ತು ಕುರುಡರನ್ನು ನೆಪ್ಪಿಲ್ಲದ ಮಾರ್ಗದಲ್ಲಿ ಬರಮಾಡುವೆನು, ಅವರಿಗೆ ಗೊತ್ತಿಲ್ಲದ ದಾರಿಗಳಲ್ಲಿ ಅವರನ್ನು ನಡೆಯಿಸುವೆನು; ಅವರೆದುರಿನ ಕತ್ತಲನ್ನು ಬೆಳಕುಮಾಡಿ ಡೊಂಕನ್ನು ಸರಿಪಡಿಸುವೆನು. ಈ ಕಾರ್ಯಗಳನ್ನು ಬಿಡದೆ ಮಾಡುವೆನು.",
			"17": "<sup>17</sup> ಕೆತ್ತಿದ ವಿಗ್ರಹಗಳಲ್ಲಿ ಭರವಸವಿಟ್ಟು ನೀವೇ ನಮ್ಮ ದೇವರುಗಳು ಎಂದು ಎರಕದ ಬೊಂಬೆಗಳಿಗೆ ಅರಿಕೆಮಾಡುವವರು ಹಿಂದೆ ಬಿದ್ದು ಕೇವಲ ಅವಮಾನಕ್ಕೆ ಈಡಾಗುವರು.",
			"18": "<sup>18</sup> ಕಿವುಡರೇ, ಕೇಳಿರಿ! ಕುರುಡರೇ, ನಿಮ್ಮ ಕಣ್ಣಿಟ್ಟು ನೋಡಿರಿ!",
			"19": "<sup>19</sup> ನನ್ನ ಸೇವಕನ ಹೊರತು ಯಾರು ಕುರುಡರು? ನಾನು ಕಳುಹಿಸುವ ದೂತನಂತೆ ಯಾರು ಕಿವುಡರು? ನನ್ನ ಭಕ್ತನ ಹಾಗೆ ಯಾರು ಕುರುಡರು? ಯೆಹೋವನ ಸೇವಕನ ಪ್ರಕಾರ ಅಂಧಕರು ಯಾರು?",
			"20": "<sup>20</sup> ನೀನು ಬಹು ವಿಷಯಗಳನ್ನು ಕಂಡಿದ್ದರೂ ನಿನಗೆ ಗಮನವಿಲ್ಲ; ಇವನ ಕಿವಿ ತೆರೆದಿದ್ದರೂ ಕೇಳನು.",
			"21": "<sup>21</sup> ಯೆಹೋವನು ಧರ್ಮಸ್ವರೂಪನಾಗಿರುವದರಿಂದ ಧರ್ಮೋಪದೇಶವನ್ನು ಘನಪಡಿಸಿ ಮಹತ್ತಿಗೆ ತರಬೇಕೆಂದು ಇಷ್ಟಪಟ್ಟನು.",
			"22": "<sup>22</sup> ಈ ಜನರೇ ಕೊಳ್ಳೆಗೆ ಈಡಾಗಿ ಸೂರೆಹೋಗಿದ್ದಾರೆ; ಎಲ್ಲರೂ ಹಳ್ಳಕೊಳ್ಳಗಳಿಗೆ ಸಿಕ್ಕಿಬಿದ್ದಿದ್ದಾರೆ, ಸೆರೆಮನೆಗಳಲ್ಲಿ ಮುಚ್ಚಲ್ಪಟ್ಟಿದ್ದಾರೆ; ಅವರು ಸುಲಿಗೆಯಾಗಿದ್ದರೂ ಯಾರೂ ಬಿಡಿಸರು, ಸೂರೆಯಾಗಿದ್ದರೂ ಯಾರೂ ಹಿಂದಕ್ಕೆ ಬಿಡು ಅನ್ನರು.",
			"23": "<sup>23</sup> ನಿಮ್ಮಲ್ಲಿ ಯಾರು ಈ ಮಾತಿಗೆ ಕಿವಿಗೊಡುವರು? ಯಾರು ಇನ್ನು ಮುಂದೆ ಆಲಿಸಿ ಕೇಳುವರು?",
			"24": "<sup>24</sup> ಯಾಕೋಬನ್ನು ಸುಲಿಗೆಗೂ ಇಸ್ರಾಯೇಲನ್ನು ಕೊಳ್ಳೆಗೂ ಕೊಟ್ಟವನು ಯಾರು? ಯಾವನಿಗೆ ವಿರುದ್ಧವಾಗಿ ನಾವು ದ್ರೋಹ ಮಾಡಿದೆವೋ ಆ ಯೆಹೋವನಲ್ಲವೇ. ಆತನ ಜನರು ಆತನ ಮಾರ್ಗಗಳಲ್ಲಿ ನಡೆಯಲೊಲ್ಲದೆ ಆತನ ಉಪದೇಶವನ್ನು ಕೇಳದೆ ಹೋದರಷ್ಟೆ.",
			"25": "<sup>25</sup> ಆಗ ಆತನು ಅವರ ಮೇಲೆ ತನ್ನ ರೋಷಾಗ್ನಿಯನ್ನೂ ಯುದ್ಧದ ರೌದ್ರವನ್ನೂ ಸುರಿಸಿದನು. ಎಲ್ಲಾ ಕಡೆಯಿಂದಲೂ ಅವರಿಗೆ ಉರಿಹತ್ತಿತು, ಆದರೆ ಅವರು ತಿಳಿಯಲಿಲ್ಲ; ದಹಿಸಿತು, ಲಕ್ಷ್ಯಕ್ಕೆ ತರಲಿಲ್ಲ."
		},
		"43": {
			"1": "<sup>1</sup> ಈಗಲಾದರೋ, ಯಾಕೋಬವಂಶವೇ, ಇಸ್ರಾಯೇಲ್ ಸಂತಾನವೇ, ನಿನ್ನನ್ನು ಸೃಷ್ಟಿಸಿ ರೂಪಿಸಿದವನಾದ ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ಭಯಪಡಬೇಡ, ನಾನು ನಿನ್ನನ್ನು ವಿಮೋಚಿಸಿದೆನಲ್ಲಾ, ನಿನ್ನ ಹೆಸರುಹಿಡಿದು ಕರೆದೆನಲ್ಲಾ; ನೀನು ನನ್ನವನೇ.",
			"2": "<sup>2</sup> ನೀನು ಜಲರಾಶಿಯನ್ನು ಹಾದು ಹೋಗುವಾಗ ನಾನು ನಿನ್ನ ಸಂಗಡ ಇರುವೆನು; ನದಿಗಳನ್ನು ದಾಟುವಾಗ ಅವು ನಿನ್ನನ್ನು ಮುಣುಗಿಸವು; ಉರಿಯಲ್ಲಿ ನಡೆಯುವಾಗ ನೀನು ಕಂದದಿರುವಿ, ಜ್ವಾಲೆಯು ನಿನ್ನನ್ನು ದಹಿಸದು.",
			"3": "<sup>3</sup> ಯೆಹೋವನೆಂಬ ನಾನು ನಿನ್ನ ದೇವರಾಗಿದ್ದೇನಷ್ಟೆ, ಇಸ್ರಾಯೇಲ್ಯರ ಸದಮಲಸ್ವಾಮಿಯಾದ ನಾನು ನಿನ್ನ ರಕ್ಷಕನು. ಐಗುಪ್ತವನ್ನು ನಿನ್ನ ವಿಮೋಚನೆಗೆ ಈಡುಮಾಡಿದ್ದೇನೆ; ನಿನಗೆ ಬದಲಾಗಿ ಕೂಷ್ ಮತ್ತು ಸೆಬಾ ಸೀಮೆಗಳನ್ನು ಕೊಟ್ಟಿದ್ದೇನೆ.",
			"4": "<sup>4</sup> ನೀನು ನನ್ನ ದೃಷ್ಟಿಯಲ್ಲಿ ಅಮೂಲ್ಯನೂ ಮಾನ್ಯನೂ ಪ್ರಿಯನೂ ಆಗಿರುವದರಿಂದ ನಾನು ನಿನಗೆ ಬದಲಾಗಿ ಮನುಷ್ಯರನ್ನೂ ನಿನ್ನ ಪ್ರಾಣಕ್ಕೆ ಪ್ರತಿಯಾಗಿ ಜನಾಂಗಗಳನ್ನೂ ಕೊಡುವೆನು.",
			"5": "<sup>5</sup> ಭಯಪಡಬೇಡ, ನಾನು ನಿನ್ನ ಸಂಗಡ ಇದ್ದೇನೆ; ನಿನ್ನ ಸಂತತಿಯವರನ್ನು ಮೂಡಲಿಂದ ತರುವೆನು; ನಿನ್ನವರನ್ನು ಪಡುವಲಿಂದ ಕೂಡಿಸುವೆನು;",
			"6": "<sup>6</sup> ನಾನು ಒಪ್ಪಿಸಿಬಿಡು ಎಂದು ಬಡಗಲಿಗೂ ತಡೆಯಬೇಡ ಎಂದು ತೆಂಕಲಿಗೂ ಹೇಳಿ ದೂರದಲ್ಲಿರುವ ನನ್ನ ಕುಮಾರರನ್ನೂ, ದಿಗಂತಗಳಲ್ಲಿರುವ ನನ್ನ ಕುಮಾರಿಯರನ್ನೂ",
			"7": "<sup>7</sup> ನನ್ನ ಮಹಿಮೆಗಾಗಿ ನಾನು ಸೃಷ್ಟಿಸಿ ರೂಪಿಸಿ ಉಂಟುಮಾಡಿದ ನನ್ನ ಹೆಸರಿನವರೆಲ್ಲರನ್ನೂ ಬರಮಾಡಬೇಕೆಂದು ಅಪ್ಪಣೆಕೊಡುವೆನು.",
			"8": "<sup>8</sup> ಕಣ್ಣಿದ್ದರೂ ಕುರುಡಾದ ಕಿವಿಯಿದ್ದರೂ ಕಿವುಡಾದ ಜನವನ್ನು ಕರೆ.",
			"9": "<sup>9</sup> ಎಲ್ಲಾ ಜನಾಂಗಗಳು ಒಟ್ಟಿಗೆ ಬರಲಿ, ಸಕಲದೇಶೀಯರು ನೆರೆಯಲಿ, ಇವರ ದೇವರುಗಳಲ್ಲಿ ಯಾರು ಈ ಸಂಗತಿಯನ್ನು ಮುಂತಿಳಿಸಬಲ್ಲರು? ನಡೆದ ಸಂಗತಿಗಳನ್ನು ಯಾರು ವಿವರಿಸಾರು? ತಾವು ಸತ್ಯವಂತರೆಂದು ಸ್ಥಾಪಿಸಿಕೊಳ್ಳುವದಕ್ಕೆ ಸಾಕ್ಷಿಗಳನ್ನು ಕರತರಲಿ; ಆ ಸಾಕ್ಷಿಗಳು [ಇವರ ಮಾತನ್ನು] ಕೇಳಿ ನಿಜವೆಂದು ಹೇಳಲಿ.",
			"10": "<sup>10</sup> ಯೆಹೋವನ ಮಾತೇನಂದರೆ - ನೀವು ನನ್ನ ಸಾಕ್ಷಿ, ನಾನು ಆರಿಸಿಕೊಂಡಿರುವ ಸೇವಕನು; ನೀವು ನನ್ನನ್ನು ತಿಳಿದು ನಂಬಿ ನನ್ನನ್ನೇ ಪರಮಾತ್ಮನು ಎಂದು ಗ್ರಹಿಸುವ ಹಾಗೆ [ಇದನ್ನು ನಡಿಸಿದೆನು]; ನನಗಿಂತ ಮುಂಚೆ ಯಾವ ದೇವರೂ ಇರಲಿಲ್ಲ, ನನ್ನ ಅನಂತರದಲ್ಲಿಯೂ ಇರುವದಿಲ್ಲ.",
			"11": "<sup>11</sup> ನಾನೇ, ನಾನೇ ಯೆಹೋವನು; ನಾನು ಹೊರತು ಯಾವ ರಕ್ಷಕನೂ ಇಲ್ಲ.",
			"12": "<sup>12</sup> ನಾನೇ ರಕ್ಷಣೆಯನ್ನು ಮುಂತಿಳಿಸಿ ನೆರವೇರಿಸಿ ಪ್ರಕಟಿಸಿದ್ದೇನೆ, ನಿಮ್ಮಲ್ಲಿ ಅನ್ಯದೇವರು ಯಾರೂ ಇರಲಿಲ್ಲವಲ್ಲ; ನೀವೇ ನನ್ನ ಸಾಕ್ಷಿಗಳು, ನಾನೊಬ್ಬನೇ ದೇವರು;",
			"13": "<sup>13</sup> ಹೌದು, ಇಂದಿನಿಂದ ನಾನೇ ಪರಮಾತ್ಮನು; ನನ್ನ ಕೈಯಿಂದ ಬಿಡಿಸಬಲ್ಲವರು ಯಾರೂ ಇಲ್ಲ; ನನ್ನ ಕೆಲಸಕ್ಕೆ ಯಾರು ಅಡ್ಡಬಂದಾರು? ಎಂಬದೇ ಯೆಹೋವನ ಮಾತು.",
			"14": "<sup>14</sup> ನಿಮ್ಮ ವಿಮೋಚಕನೂ ಇಸ್ರಾಯೇಲ್ಯರ ಸದಮಲಸ್ವಾಮಿಯೂ ಆದ ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ನಾನು ನಿಮಗೋಸ್ಕರ [ದೂತನನ್ನು] ಬಾಬೆಲಿಗೆ ಕಳುಹಿಸಿ ಕಸ್ದೀಯರನ್ನೆಲ್ಲಾ ತಮ್ಮ ವಿನೋದದ ಹಡಗುಗಳ ಮೇಲೆ ಪಲಾಯಿತರನ್ನಾಗಿ ಅಟ್ಟಿಬಿಡುವೆನು.",
			"15": "<sup>15</sup> ನಾನು ಯೆಹೋವನು, ನಿಮ್ಮ ಸದಮಲಸ್ವಾಮಿಯು, ಇಸ್ರಾಯೇಲನ್ನು ಸೃಷ್ಟಿಸಿದವನು, ನಿಮ್ಮ ಅರಸನೂ ಆಗಿದ್ದೇನೆ.",
			"16": "<sup>16</sup> ಯಾವನು ಸಮುದ್ರದಲ್ಲಿ ದಾರಿಮಾಡಿ ಜಲಪ್ರವಾಹಗಳಲ್ಲಿ ಮಾರ್ಗವನ್ನು ಏರ್ಪಡಿಸಿದನೋ,",
			"17": "<sup>17</sup> ಯಾವನು ಅಶ್ವರಥಭಟಸೈನ್ಯಗಳನ್ನು ಹೊರಡಿಸಿ ಅವು ಬಿದ್ದು ಏಳಲಾರದಂತೆಯೂ ದೀಪದೋಪಾದಿಯಲ್ಲಿ ನಂದಿ ಆರಿಹೋಗುವಂತೆಯೂ ಮಾಡಿದನೋ,",
			"18": "<sup>18</sup> ಆ ಯೆಹೋವನು ಹೀಗೆನ್ನುತ್ತಾನೆ - ಹಳೇ ಸಂಗತಿಗಳನ್ನು ಜ್ಞಾಪಕಕ್ಕೆ ತಂದುಕೊಳ್ಳಬೇಡಿರಿ, ಪುರಾತನಕಾರ್ಯಗಳನ್ನು ಮರೆತುಬಿಡಿ.",
			"19": "<sup>19</sup> ಇಗೋ, ಹೊಸ ಕಾರ್ಯವನ್ನು ಮಾಡುವೆನು, ಈಗ ತಲೆದೋರುತ್ತಲಿದೆ, ನಿಮಗೆ ಕಾಣುವದಿಲ್ಲವೋ? ನಾನು ಅರಣ್ಯದಲ್ಲಿ ಮಾರ್ಗವನ್ನು ಏರ್ಪಡಿಸಿ ಅಡವಿಯಲ್ಲಿ ನದಿಗಳನ್ನು ಹರಿಸುವೆನು.",
			"20": "<sup>20</sup> ನನ್ನ ಸ್ತೋತ್ರವನ್ನು ಪ್ರಚಾರಪಡಿಸಲಿ ಎಂದು ನಾನು ಸೃಷ್ಟಿಸಿಕೊಂಡ ಆಪ್ತಜನರ ಜಲಪಾನಕ್ಕಾಗಿ",
			"21": "<sup>21</sup> ನಾನು ಅರಣ್ಯದಲ್ಲಿ ನೀರನ್ನು ಒದಗಿಸಿ ಅಡವಿಯಲ್ಲಿ ನದಿಗಳನ್ನು ಹರಿಸುವವನಾದ ಕಾರಣ ನರಿ ಉಷ್ಟ್ರಪಕ್ಷಿ ಮೊದಲಾದ ಕಾಡು ಮೃಗಗಳು ನನ್ನನ್ನು ಘನಪಡಿಸುವವು.",
			"22": "<sup>22</sup> ಯಾಕೋಬೇ, ನೀನಾದರೋ ನನ್ನನ್ನು ಪ್ರಾರ್ಥಿಸಲಿಲ್ಲ, ಇಸ್ರಾಯೇಲೇ, ನೀನು ನನ್ನ ವಿಷಯದಲ್ಲಿ ಬೇಸರಗೊಂಡಿದ್ದೀ.",
			"23": "<sup>23</sup> ನೀನು ನನಗೆ ಹೋಮಕ್ಕಾಗಿ ಕುರಿಮೇಕೆಗಳನ್ನು ತಂದು ಯಜ್ಞಗಳಿಂದ ನನ್ನನ್ನು ಘನಪಡಿಸಲಿಲ್ಲ, ನೈವೇದ್ಯಕ್ಕಾಗಿ ನಿನ್ನನ್ನು ನಾನು ತೊಂದರೆಪಡಿಸಲಿಲ್ಲ, ಧೂಪಕ್ಕಾಗಿ ನಿನ್ನನ್ನು ಬೇಸರಗೊಳಿಸಲಿಲ್ಲ.",
			"24": "<sup>24</sup> ನೀನು ನನಗೋಸ್ಕರ ಹಣಕೊಟ್ಟು ಬಜೆಯನ್ನು ತರಲಿಲ್ಲ, ಯಜ್ಞಪಶುಗಳ ವಪೆಯಿಂದ ನನ್ನನ್ನು ತೃಪ್ತಿಪಡಿಸಲಿಲ್ಲ; ಆದರೆ ನಿನ್ನ ಪಾಪಗಳಿಂದ ನನ್ನನ್ನು ನೀನು ತೊಂದರೆಪಡಿಸಿದ್ದೀ, ನಿನ್ನ ದೋಷಗಳಿಂದ ನನ್ನನ್ನು ಬೇಸರಗೊಳಿಸಿದ್ದೀ.",
			"25": "<sup>25</sup> ನಾನಾಗಿ ನಾನೇ ನನಗೋಸ್ಕರ ನಿನ್ನ ದ್ರೋಹಗಳನ್ನು ಅಳಿಸಿ ಬಿಡುತ್ತೇನೆ, ನಿನ್ನ ಪಾಪಗಳನ್ನು ನನ್ನ ನೆನಪಿನಲ್ಲಿಡೆನು.",
			"26": "<sup>26</sup> ನನಗೆ ಜ್ಞಾಪಕಪಡಿಸು, ನಾವಿಬ್ಬರೂ ವಾದಿಸುವ; ನಿನ್ನ ಸದ್ಧರ್ಮವು ಗೊತ್ತಾಗುವಂತೆ ನಿನ್ನ ನ್ಯಾಯವನ್ನು ತೋರ್ಪಡಿಸು.",
			"27": "<sup>27</sup> ನಿನ್ನ ಮೂಲಪಿತೃವು ಪಾಪಿಷ್ಠನು, ನಿನ್ನ ಮಧ್ಯಸ್ಥರು ನನಗೆ ದ್ರೋಹಮಾಡಿದವರು.",
			"28": "<sup>28</sup> ಆದಕಾರಣ ನಾನು ಪವಿತ್ರಾಲಯದ ಅಧಿಪತಿಗಳನ್ನು ಹೊಲಸಿಗೆ ತಂದು ಯಾಕೋಬನ್ನು ಶಾಪಕ್ಕೂ ಇಸ್ರಾಯೇಲನ್ನು ದೂಷಣೆಗೂ ಗುರಿಮಾಡಿದೆನು."
		},
		"44": {
			"1": "<sup>1</sup> ನನ್ನ ಸೇವಕನಾದ ಯಾಕೋಬೇ, ನಾನು ಆದುಕೊಂಡ ಇಸ್ರಾಯೇಲೇ, ಈಗ ಕೇಳು.",
			"2": "<sup>2</sup> ನಿನ್ನನ್ನು ನಿರ್ಮಾಣಮಾಡಿ ಗರ್ಭದಿಂದಲೂ ರೂಪಿಸುತ್ತಾ ಬಂದು ನಿನಗೆ ಸಹಾಯಮಾಡುವವನಾದ ಯೆಹೋವನು ಹೀಗೆನ್ನುತ್ತಾನೆ - ನನ್ನ ಸೇವಕನಾದ ಯಾಕೋಬೇ, ನಾನು ಆದುಕೊಂಡ ಯೆಶುರೂನೇ, ಭಯಪಡಬೇಡ!",
			"3": "<sup>3</sup> ಬತ್ತಿದ ಭೂಮಿಯಲ್ಲಿ ಮಳೆಗರೆದು ಒಣನೆಲದಲ್ಲಿ ಕಾಲುವೆಗಳನ್ನು ಹರಿಸುವೆನು; ನಿನ್ನ ಸಂತಾನದವರಲ್ಲಿ ನನ್ನ ಆತ್ಮವನ್ನು, ನಿನ್ನ ಸಂತತಿಯವರ ಮೇಲೆ ನನ್ನ ಆಶೀರ್ವಾದವನ್ನು, ಸುರಿಸುವೆನು.",
			"4": "<sup>4</sup> ನೀರಿನ ಕಾಲುವೆಗಳ ಬಳಿಯಲ್ಲಿ ಹಸುರು ಹುಲ್ಲಿನ ನಡುವೆ ಬೆಳೆಯುವ ನೀರವಂಜಿಗಳಂತೆ ವೃದ್ಧಿಯಾಗುವರು.",
			"5": "<sup>5</sup> [ಅನ್ಯಜನರಲ್ಲಿ] ಒಬ್ಬನು - ನಾನು ಯೆಹೋವನ ಭಕ್ತನು ಎಂದು ಹೇಳಿಕೊಳ್ಳುವನು; ಇನ್ನೊಬ್ಬನು ಯಾಕೋಬ್ಯನು ಎನಿಸಿಕೊಳ್ಳುವನು; ಮತ್ತೊಬ್ಬನು ತನ್ನ ಕೈಯ ಮೇಲೆ ಯೆಹೋವದಾಸನು ಎಂದು ಬರೆದುಕೊಂಡು ಇಸ್ರಾಯೇಲ್ಯನೆಂಬ ಬಿರುದನ್ನು ಧರಿಸಿಕೊಳ್ಳುವನು.",
			"6": "<sup>6</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನೂ ವಿಮೋಚಕನೂ ಆಗಿರುವ ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಹೀಗೆನ್ನುತ್ತಾನೆ - ನಾನೇ ಆದಿ, ಅಂತವೂ ನಾನೇ; ನಾನು ಹೊರತು ಯಾವ ದೇವರೂ ಇಲ್ಲ.",
			"7": "<sup>7</sup> ನಾನು ಪುರಾತನದವರನ್ನು ಸೃಷ್ಟಿಸಿದಂದಿನಿಂದ ನನ್ನ ಹಾಗೆ ಯಾರು ಪ್ರಕಟಿಸಿಕೊಂಡಿದ್ದಾರೆ? ಅಂಥವರಿದ್ದರೆ ನನ್ನೆದುರಿಗೆ ಹೇಳಿ ಸ್ಥಾಪಿಸಲಿ; ಇಲ್ಲವೆ ಮುಂದಿನವುಗಳನ್ನು ಈಗ ತಿಳಿಸಲಿ, ಭವಿಷ್ಯತ್ತುಗಳನ್ನು ಹೇಳಲಿ.",
			"8": "<sup>8</sup> ಹೆದರಬೇಡಿರಿ, ಅಂಜದಿರಿ! ನಾನು ಪೂರ್ವದಿಂದಲೂ ನಿಮಗೆ ಹೇಳಿ ಪ್ರಕಟಿಸಿಕೊಂಡೆನಷ್ಟೆ. ನೀವೇ ನನ್ನ ಸಾಕ್ಷಿಗಳು. ನಾನಲ್ಲದೆ ಇನ್ನೊಬ್ಬ ದೇವರಿದ್ದಾನೋ? ಇನ್ನು ಯಾವ ಶರಣನೂ ಇಲ್ಲ, ಯಾರೂ ನನಗೆ ಗೊತ್ತಿಲ್ಲ.",
			"9": "<sup>9</sup> ವಿಗ್ರಹ ಕೆತ್ತುವವರೆಲ್ಲಾ ಮಟ್ಟಮಾಯವೇ; ಅವರ ಇಷ್ಟದ ಬೊಂಬೆಗಳು ಯಾತಕ್ಕೂ ಬಾರವು; ಬೊಂಬೆಗಳ ಪಕ್ಷದ ಸಾಕ್ಷಿಗಾರರು ನೋಡುವವರಲ್ಲ, ಗ್ರಹಿಸುವವರಲ್ಲ; ನಾಚಿಕೆಗೆ ಗುರಿಯಾಗುವರು.",
			"10": "<sup>10</sup> ದೇವತೆಯನ್ನು ರೂಪಿಸುವವರೂ ವ್ಯರ್ಥವಿಗ್ರಹವನ್ನು ಎರಕ ಹೊಯ್ಯುವವರೂ ಎಂಥವರು?",
			"11": "<sup>11</sup> ವಿಗ್ರಹಶರಣರೆಲ್ಲಾ ಆಶಾಭಂಗಪಡುವರು; ಅದನ್ನು ಕೆತ್ತಿದವರು ಮನುಷ್ಯಮಾತ್ರದವರೇ; ಅವರೆಲ್ಲರು ಕೂಡಿ ನಿಲ್ಲಲಿ, ಒಟ್ಟಿಗೆ ಹೆದರಿಕೊಂಡು ಲಜ್ಜೆಪಡುವರು.",
			"12": "<sup>12</sup> ಕಮ್ಮಾರನು ಮುಟ್ಟನ್ನು ತೆಗೆದುಕೊಂಡು ಕೆಂಡದಲ್ಲಿ ಕೆಲಸಮಾಡುತ್ತಾ ಚಮಟಿಕೆಗಳಿಂದ ಬಡಿದು ತೋಳಿನ ಬಲದಿಂದ ವಿಗ್ರಹವನ್ನು ರೂಪಿಸುವನು; ಅವನೂ ಹಸಿದು ಬಳಲುವನು, ನೀರಿಲ್ಲದೆ ಕಂಗೆಡುವನು.",
			"13": "<sup>13</sup> ಬಡಗಿಯು [ಮರಕ್ಕೆ] ನೂಲು ಹಾಕಿ ಮೊಳೆಯಿಂದ ಗೆರೆಯೆಳೆದು ಉಳಿಬಾಚಿಗಳಿಂದ ಕೆತ್ತಿ ಕೈವಾರದಿಂದ ಗುರುತಿಸಿ ಅದು ಮನೆಯಲ್ಲಿ ವಾಸಿಸತಕ್ಕದ್ದಾಗಲೆಂದು ಮನುಷ್ಯನ ಆಕಾರಕ್ಕೆ ತಂದು ನರನ ಅಂದದಂತೆ ರೂಪಿಸುವನು.",
			"14": "<sup>14</sup> ಅವನು ತನ್ನ ಕೆಲಸಕ್ಕಾಗಿ ದೇವದಾರುಗಳನ್ನು ಕಡಿಯುವನು, ತುರಾಯಿ ಅಲ್ಲೋನ್ ಮರಗಳನ್ನು ತೆಗೆದುಕೊಳ್ಳುವನು, ವನವೃಕ್ಷಗಳಲ್ಲಿ ಒಂದನ್ನು ತನಗೋಸ್ಕರ ಸಲಹುವನು; ಅವನು ಪೀತದಾರವನ್ನು ನೆಡಲು ಮಳೆಯು ಅದನ್ನು ಬೆಳೆಯಿಸುವದು.",
			"15": "<sup>15</sup> ಅದು ಸೌದೆಗಾಗುವದು; ಅವನು ಸ್ವಲ್ಪ ತೆಗೆದು ಕಾಯಿಸಿಕೊಳ್ಳುವನು, ಉರಿಸಿ ರೊಟ್ಟಿಸುಡುವನು; ಅದರಲ್ಲೇ ಒಂದು ದೇವರನ್ನು ಮಾಡಿಕೊಂಡು ಪೂಜಿಸುವನು, ಬೊಂಬೆ ಕೆತ್ತಿ ಅಡ್ಡಬೀಳುವನು.",
			"16": "<sup>16</sup> ಒಂದು ಭಾಗವನ್ನು ಬೆಂಕಿಯಲ್ಲಿ ಉರಿಸುವನು; ಅದು ಮಾಂಸಭೋಜನಕ್ಕೆ ಅನುಕೂಲಿಸುವದು; ಬಾಡು ಸುಟ್ಟು ಹಸಿವೆ ತೀರಿಸಿಕೊಳ್ಳುವನು; ಮತ್ತು ಕಾಯಿಸಿಕೊಳ್ಳುತ್ತಾ - ಆಹಾ, ಬೆಂಕಿ ಕಂಡೆ; ಬೆಚ್ಚಗಾಯಿತು ಎಂದುಕೊಳ್ಳುವನು.",
			"17": "<sup>17</sup> ಉಳಿದ ಭಾಗವನ್ನು ತನ್ನ ದೇವರನ್ನಾಗಿ ಬೊಂಬೆ ಕೆತ್ತಿ ಅದಕ್ಕೆ ಎರಗಿ ಅಡ್ಡಬಿದ್ದು - ನೀನೇ ನನ್ನ ದೇವರು, ನನ್ನನ್ನು ರಕ್ಷಿಸು ಎಂದು ಪ್ರಾರ್ಥಿಸುವನು.",
			"18": "<sup>18</sup> ಇಂಥವರು ಏನೂ ತಿಳಿಯದವರು, ಏನೂ ಗ್ರಹಿಸಲಾರದವರು; ಅವರ ಕಣ್ಣು ಕಾಣದಂತೆಯೂ ಹೃದಯ ಗ್ರಹಿಸದಂತೆಯೂ ಯೆಹೋವನು ಅಂಟು ಬಳಿದಿದ್ದಾನಲ್ಲಾ.",
			"19": "<sup>19</sup> ನಾನು ಒಂದು ಭಾಗವನ್ನು ಬೆಂಕಿಯಲ್ಲಿ ಉರಿಸಿದೆನು, ಹೌದು, ಅದರ ಕೆಂಡದಲ್ಲಿ ರೊಟ್ಟಿಮಾಡಿ ಮಾಂಸ ಸುಟ್ಟು ತಿಂದೆನಲ್ಲಾ; ಮಿಕ್ಕದ್ದನ್ನು ನಾನು ಬೊಂಬೆ ಮಾಡಲೋ, ಮರದ ತುಂಡಿಗೆ ಅಡ್ಡಬೀಳಬಹುದೋ, ಅಂದುಕೊಳ್ಳುವಷ್ಟು ಜ್ಞಾನ ವಿವೇಕಗಳು ಯಾರಿಗೂ ಇಲ್ಲ, ಯಾರೂ ಇದನ್ನು ಮನಸ್ಸಿಗೆ ತಾರರು.",
			"20": "<sup>20</sup> ಅವನು ತಿನ್ನುವದು ಬೂದಿಯೇ; ಮೋಸಕ್ಕೊಳಗಾದ ಹೃದಯವು ಅವನಿಗೆ ದಾರಿತಪ್ಪಿಸಿದ ಕಾರಣ ನನ್ನ ಕೈ ಸುಳ್ಳನ್ನು ಹಿಡಿದಿದೆಯಲ್ಲಾ ಎಂದುಕೊಳ್ಳಲೂ ಆಗದು, ತನ್ನನ್ನು ರಕ್ಷಿಸಿಕೊಳ್ಳಲೂ ಆಗದು.",
			"21": "<sup>21</sup> ಯಾಕೋಬೇ, ಇಸ್ರಾಯೇಲೇ, ಈ ವಿಷಯಗಳನ್ನು ಜ್ಞಾಪಕದಲ್ಲಿಟ್ಟುಕೋ, ನನ್ನ ಸೇವಕನಾಗಿದ್ದೀಯಲ್ಲವೇ; ಇಸ್ರಾಯೇಲೇ, ನಾನು ನಿನ್ನನ್ನು ನಿರ್ಮಿಸಿದೆನು, ನೀನು ನನ್ನ ಸೇವಕನು, ನಿನ್ನನ್ನು ಮರೆತುಬಿಡೆನು.",
			"22": "<sup>22</sup> ನಿನ್ನ ದ್ರೋಹಗಳನ್ನು ಮಂಜಿನಂತೆ ಪರಿಹರಿಸಿದ್ದೇನೆ, ನಿನ್ನ ಪಾಪಗಳನ್ನು ಮೋಡದಂತೆ ಹಾರಿಸಿದ್ದೇನೆ; ನಿನ್ನನ್ನು ವಿಮೋಚಿಸಿದ್ದೇನೆ, ನನ್ನ ಕಡೆಗೆ ತಿರುಗಿಕೋ.",
			"23": "<sup>23</sup> ಆಕಾಶವೇ, ಹರ್ಷಧ್ವನಿಗೈ, ಯೆಹೋವನು ತನ್ನ ಕಾರ್ಯವನ್ನು ನೆರವೇರಿಸಿದ್ದಾನೆ; ಭೂಮಿಯ ಅಧೋಭಾಗವೇ, ಆರ್ಭಟಿಸು; ಪರ್ವತಗಳೇ, ವನವೇ, ಸಕಲವನವೃಕ್ಷಗಳೇ, ಕೋಲಾಹಲಮಾಡಿರಿ; ಯೆಹೋವನು ಯಾಕೋಬನ್ನು ವಿಮೋಚಿಸಿದ್ದಾನೆ. ಇಸ್ರಾಯೇಲಿನ ರಕ್ಷಣೆಯಿಂದ ತನ್ನ ಮಹಿಮೆಯನ್ನು ಪ್ರಚುರಗೊಳಿಸುವನು.",
			"24": "<sup>24</sup> ನಿನ್ನನ್ನು ಗರ್ಭದಿಂದಲೂ ರೂಪಿಸುತ್ತಾ ಬಂದಿರುವ ನಿನ್ನ ವಿಮೋಚಕನಾದ ಯೆಹೋವನು ಹೀಗೆನ್ನುತ್ತಾನೆ - ನಾನೇ ಸರ್ವಕಾರ್ಯಕರ್ತನಾದ ಯೆಹೋವನು, ನಾನೊಬ್ಬನೇ ಗಗನಮಂಡಲವನ್ನು ಹರವಿ ಭೂಮಂಡಲವನ್ನು ವಿಸ್ತರಿಸುವವನಾಗಿದ್ದೇನೆ; ನನಗೆ ಸಹಾಯಕನುಂಟೋ?",
			"25": "<sup>25</sup> ನಾನು ಕೊಚ್ಚಿಕೊಳ್ಳುವವರ ಶಕುನಗಳನ್ನು ನಿರರ್ಥಕಪಡಿಸಿ ಕಣಿಹೇಳುವವರನ್ನು ಮರುಳುಗೊಳಿಸಿ ಜ್ಞಾನಿಗಳನ್ನು ಹಿಂದಕ್ಕೆ ತಳ್ಳಿ ಅವರ ತಿಳುವಳಿಕೆಯನ್ನು ಹುಚ್ಚುತನವಾಗಮಾಡಿ",
			"26": "<sup>26</sup> ನನ್ನ ಸೇವಕನ ಮಾತನ್ನು ಸ್ಥಾಪಿಸಿ ನನ್ನ ದೂತರ ಮಂತ್ರಾಲೋಚನೆಯನ್ನು ನೆರವೇರಿಸುವವನಾಗಿದ್ದೇನೆ; ಯೆರೂಸಲೇಮು ಜನನಿವಾಸವಾಗುವದು, ಯೆಹೂದದ ಪಟ್ಟಣಗಳು ತಿರಿಗಿ ಕಟ್ಟಲ್ಪಡುವವು, ಅಲ್ಲಿನ ಹಾಳನ್ನು ಹಸನುಮಾಡುವೆನು ಎಂದು ನಾನು ಮುಂತಿಳಿಸಿ",
			"27": "<sup>27</sup> ಜಲರಾಶಿಗೆ - ಬತ್ತಿಹೋಗು, ನಿನ್ನಲ್ಲಿ ಸೇರುವ ನದಿಗಳನ್ನು ಒಣಗಿಸುವೆನು ಎಂದು ನಾನು ಅಪ್ಪಣೆಕೊಡುವವನಾಗಿದ್ದೇನೆ;",
			"28": "<sup>28</sup> ಮತ್ತು ಕೋರೆಷನ ವಿಷಯವಾಗಿ - ಅವನು ನನ್ನ ಮಂದೆ ಕಾಯುವವನು, ಯೆರೂಸಲೇಮು ಕಟ್ಟಲ್ಪಡಲಿ, ದೇವಸ್ಥಾನದ ಅಸ್ತಿವಾರವು ಹಾಕಲ್ಪಡಲಿ ಎಂದು ಹೇಳಿ ನನ್ನ ಇಷ್ಟಾರ್ಥವನ್ನೆಲ್ಲಾ ನೆರವೇರಿಸತಕ್ಕವನು ಎಂಬದಾಗಿ ಮಾತಾಡುವವನಾಗಿದ್ದೇನೆ."
		},
		"45": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಯಾವನ ಕೈಹಿಡಿದು ಯಾವನೆದುರಿಗೆ ಜನಾಂಗಗಳನ್ನು ತುಳಿದು ರಾಜರ ನಡುಕಟ್ಟನ್ನು ಬಿಚ್ಚಿ ಯಾವನ ಮುಂದೆ ಬಾಗಿಲುಹೆಬ್ಬಾಗಿಲುಗಳನ್ನು ತೆರೆದು ಮುಚ್ಚಲೀಸನೋ ತಾನು ಅಭಿಷೇಕಿಸಿದ ಆ ಕೋರೆಷನಿಗೆ ಹೀಗೆನ್ನುತ್ತಾನೆ -",
			"2": "<sup>2</sup> ನಾನು ನಿನ್ನ ಮುಂದೆ ಹೋಗಿ ದಿಣ್ಣೆಗಳನ್ನು ಸಮಮಾಡುವೆನು, ತಾಮ್ರದ ಕದಗಳನ್ನು ಒಡೆದು ಕಬ್ಬಿಣದ ಅಗುಳಿಗಳನ್ನು ಮುರಿದು ಬಿಡುವೆನು.",
			"3": "<sup>3</sup> ನಿನ್ನ ಹೆಸರುಹಿಡಿದು ಕರೆಯುವ ನಾನು ಯೆಹೋವನು, ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರು ಎಂದು ನೀನು ತಿಳಿದುಕೊಳ್ಳುವ ಹಾಗೆ ಕತ್ತಲಲ್ಲಿ ಬಚ್ಚಿಟ್ಟಿರುವ ಆಸ್ತಿಪಾಸ್ತಿಯನ್ನೂ ಗುಪ್ತಸ್ಥಳಗಳಲ್ಲಿ ಮರೆಮಾಡಿದ ನಿಧಿನಿಕ್ಷೇಪವನ್ನೂ ನಿನಗೆ ಕೊಡುವೆನು.",
			"4": "<sup>4</sup> ನನ್ನ ಸೇವಕನಾದ ಯಾಕೋಬಿಗಾಗಿ, ನಾನು ಆದುಕೊಂಡ ಇಸ್ರಾಯೇಲಿಗಾಗಿ ಹೆಸರುಹಿಡಿದು ನಿನ್ನನ್ನು ಕರೆದಿದ್ದೇನೆ; ನೀನು ನನ್ನನ್ನು ಅರಿಯದವನಾಗಿದ್ದರೂ ನಿನಗೆ ಬಿರುದನ್ನು ದಯಪಾಲಿಸಿದ್ದೇನೆ.",
			"5": "<sup>5</sup> ನಾನೇ ಯೆಹೋವ, ಇನ್ನು ಯಾವನೂ ಅಲ್ಲ, ನಾನು ಹೊರತು ಯಾವ ದೇವರೂ ಇಲ್ಲ; ನೀನು ನನ್ನನ್ನು ಅರಿಯದವನಾಗಿದ್ದರೂ ನಿನಗೆ ನಡುಕಟ್ಟುವೆನು.",
			"6": "<sup>6</sup> ಮೂಡಲಿಂದ ಪಡುವಲ ತನಕ ಇರುವವರೆಲ್ಲರೂ ಇದನ್ನು ನೋಡಿ ನನ್ನ ವಿನಹ ಯಾರೂ ಇಲ್ಲ, ನಾನೇ ಯೆಹೋವನು,",
			"7": "<sup>7</sup> ಇನ್ನು ಯಾವನೂ ಅಲ್ಲ, ನಾನು ಬೆಳಕಿಗೂ ಕತ್ತಲಿಗೂ ಸೃಷ್ಟಿಕರ್ತನು, ಮೇಲನ್ನೂ ಕೇಡನ್ನೂ ಬರಮಾಡುವವನು, ಈ ಸಮಸ್ತ ಕಾರ್ಯಗಳನ್ನು ನಡೆಯಿಸುವ ಯೆಹೋವನು ನಾನೇ ಎಂದು ತಿಳಿದುಕೊಳ್ಳುವರು.",
			"8": "<sup>8</sup> ಆಕಾಶಮಂಡಲವೇ, ಮೇಲಿನಿಂದ ಧರ್ಮರಸವನ್ನು ವರ್ಷಿಸು, ಗಗನವು ಸುರಿಸಲಿ; ಭೂಮಿಯು ಒಡೆದು ರಕ್ಷಣೆಯನ್ನು ಮೊಳೆಯಿಸಿ ಅದರೊಡನೆ ನೀತಿಫಲವನ್ನು ಬೆಳೆಯಿಸಲಿ, ಯೆಹೋವನೆಂಬ ನಾನೇ ಇದಕ್ಕೆಲ್ಲಾ ಕರ್ತನು.",
			"9": "<sup>9</sup> ಅಯ್ಯೋ, ತನ್ನನ್ನು ರೂಪಿಸಿದಾತನ ಸಂಗಡ ವ್ಯಾಜ್ಯವಾಡುವವನ ಗತಿ ಏನು! ಮಣ್ಣು ಮಡಕೆಗಳಲ್ಲಿ ಅವನೂ ಒಂದು ಮಡಕೆಯಲ್ಲವೆ. ಮಣ್ಣು - ಏನು ಮಾಡುತ್ತೀ ಎಂದು ರೂಪಿಸುವವನನ್ನು ಕೇಳುವದುಂಟೇ? ನಿನ್ನ ಕಾರ್ಯವು [ನಿನ್ನ ವಿಷಯವಾಗಿ] ಅವನಿಗೆ ಕೈಯಿಲ್ಲ ಅಂದೀತೇ?",
			"10": "<sup>10</sup> ನೀನು ಹುಟ್ಟಿಸುವದೇನು ಎಂದು ತಂದೆಯನ್ನು ಕೇಳುವವನ ಪಾಡು ಪಾಡೇ! ನೀನು ಹೆರುವದೇನು ಎಂದು ಹೆಂಗಸನ್ನು ಕೇಳುವವನ ಗತಿ ಗತಿಯೇ!",
			"11": "<sup>11</sup> [ಆದರೂ] ಇಸ್ರಾಯೇಲ್ಯರ ಸದಮಲಸ್ವಾಮಿಯೂ ಸೃಷ್ಟಿಕರ್ತನೂ ಆದ ಯೆಹೋವನ ಮಾತನ್ನು ಕೇಳಿರಿ - ಭವಿಷ್ಯತ್ತುಗಳ ವಿಷಯವಾಗಿ ನನ್ನನ್ನು ವಿಚಾರಿಸಿರಿ, ನಾನೇ ನಿರ್ಮಿಸಿದ ನನ್ನ ಮಕ್ಕಳ ವಿಷಯದಲ್ಲಿ ನನಗೆ ಏನನ್ನು ವಿಧಿಸಿದರೂ ವಿಧಿಸಿರಿ.",
			"12": "<sup>12</sup> ಭೂಲೋಕವನ್ನುಂಟುಮಾಡಿ ಅದರಲ್ಲಿನ ಮನುಷ್ಯರನ್ನು ಸೃಷ್ಟಿಸಿದವನು ನಾನೇ; ನನ್ನ ಕೈಗಳೇ ಆಕಾಶ ಮಂಡಲವನ್ನು ಹರಡಿದವು; ನಕ್ಷತ್ರಸೈನ್ಯಕ್ಕೆ ಅಪ್ಪಣೆಕೊಟ್ಟವನೂ ನಾನೇ.",
			"13": "<sup>13</sup> ನಾನು ಕೋರೆಷನನ್ನು ಧರ್ಮೋದ್ದೇಶದಿಂದ ಉನ್ನತಿಗೆ ತಂದು ನೇಮಿಸಿದ್ದೇನೆ, ಅವನ ಮಾರ್ಗಗಳನ್ನೆಲ್ಲಾ ಸರಾಗಮಾಡುವೆನು; ಅವನು ನನ್ನ ಪಟ್ಟಣವನ್ನು ತಿರಿಗಿ ಕಟ್ಟಿ ಕ್ರಯವನ್ನಾಗಲಿ ಬಹುಮಾನವನ್ನಾಗಲಿ ಕೇಳಿಕೊಳ್ಳದೆ ಸೆರೆಯಾದ ನನ್ನ ಜನರನ್ನು ಬಿಡುಗಡೆ ಮಾಡುವನು ಎಂದು ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ.",
			"14": "<sup>14</sup> ಯೆಹೋವನ ಈ ಮಾತನ್ನು ಕೇಳಿರಿ - ಐಗುಪ್ತದ ಆದಾಯವೂ ಕೂಷಿನ ವ್ಯಾಪಾರವೂ ಎತ್ತರದ ಮನುಷ್ಯರಾದ ಸೆಬಾಯರೂ ನಿಮ್ಮಲ್ಲಿಗೆ ಸೇರಿ ನಿಮ್ಮವರಾಗಿ ನಿಮ್ಮನ್ನು ಅನುಸರಿಸುವರು; ಸಂಕೋಲೆಗಳನ್ನು ಕಟ್ಟಿಕೊಂಡು ಬಂದು ನಿಮ್ಮ ಮುಂದೆ ಅಡ್ಡಬಿದ್ದು ಹೀಗೆ ಅರಿಕೆಮಾಡುವರು - ದೇವರು ನಿಜವಾಗಿ ನಿಮ್ಮಲ್ಲಿದ್ದಾನೆ. ಇನ್ನು ಯಾವ ದೇವರೂ ಇಲ್ಲವೇ ಇಲ್ಲ.",
			"15": "<sup>15</sup> ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರೇ, ರಕ್ಷಕನೇ, ನಿಶ್ಚಯವಾಗಿಯೂ ನೀನು ಮರೆಮಾಡಿಕೊಳ್ಳುವ ದೇವರಾಗಿದ್ದೀ.",
			"16": "<sup>16</sup> ಮೂರ್ತಿಗಳನ್ನುಂಟುಮಾಡುವವರೆಲ್ಲರೂ ನಾಚಿಕೆಗೀಡಾಗಿ ಮಾನಭಂಗಪಡುವರು, ಹೌದು, ಒಟ್ಟಿಗೆ ಅವಮಾನದಲ್ಲಿ ಮುಣುಗುವರು.",
			"17": "<sup>17</sup> ಇಸ್ರಾಯೇಲಿಗೋ ಯೆಹೋವನಿಂದ ಶಾಶ್ವತರಕ್ಷಣೆ ದೊರೆಯುವದು; ಯುಗಯುಗಾಂತರಕ್ಕೂ ನೀವು ನಾಚಿಕೆಗೀಡಾಗುವದಿಲ್ಲ, ಮಾನಭಂಗಪಡುವದಿಲ್ಲ.",
			"18": "<sup>18</sup> ಆಕಾಶಮಂಡಲವನ್ನು ಸೃಷ್ಟಿಸಿದ ಯೆಹೋವನ ಮಾತನ್ನು ಕೇಳಿರಿ; ಆತನೇ ದೇವರು, ಭೂಲೋಕವನ್ನು ನಿರ್ಮಿಸಿ ರೂಪಿಸಿ ಸ್ಥಾಪಿಸಿದನು; ಅದನ್ನು ಶೂನ್ಯಸ್ಥಾನವಾಗಿರಲೆಂದು ಸೃಷ್ಟಿಸದೆ ಜನನಿವಾಸಕ್ಕಾಗಿಯೇ ರೂಪಿಸಿದನು. ಈತನು ಹೀಗನ್ನುತ್ತಾನೆ - ನಾನೇ ಯೆಹೋವನು, ಇನ್ನು ಯಾವನೂ ಅಲ್ಲ.",
			"19": "<sup>19</sup> ನಾನು ರಹಸ್ಯದಲ್ಲಿ ನುಡಿದವನಲ್ಲ, ತಮೋಲೋಕದೊಳಗಿಂದ ಮಾತಾಡಿದವನಲ್ಲ, ಶೂನ್ಯವನ್ನೋ ಎಂಬಂತೆ ನನ್ನನ್ನು ಹುಡುಕಿರಿ ಎಂದು ಯಾಕೋಬ ವಂಶದವರಿಗೆ ನಾನು ಅಪ್ಪಣೆಕೊಡಲಿಲ್ಲ. ಯೆಹೋವನೆಂಬ ನಾನು ಸತ್ಯಾನುಸಾರ ನುಡಿಯುವವನು, ನೆಟ್ಟನೆಯ ಮಾತುಗಳನ್ನೇ ಆಡುತ್ತೇನೆ.",
			"20": "<sup>20</sup> ತಪ್ಪಿಸಿಕೊಂಡ ಅನ್ಯಜನರೇ, ನೆರೆದು ಬನ್ನಿರಿ, ಒಟ್ಟಿಗೆ ಸಮೀಪಿಸಿರಿ! ತಮ್ಮ ಮರದ ಬೊಂಬೆಯನ್ನು ಹೊತ್ತುಕೊಂಡು ರಕ್ಷಿಸಲಾರದ ಆ ದೇವರಿಗೆ ಬಿನ್ನಯಿಸುವವರು ಏನೂ ತಿಳಿಯದವರಾಗಿದ್ದಾರೆ.",
			"21": "<sup>21</sup> ಹೇಳಿರಿ, ನಿಮ್ಮ ನ್ಯಾಯಗಳನ್ನು ಮುಂದಕ್ಕೆ ತನ್ನಿರಿ, ನಿಮ್ಮ ದೇವರುಗಳು ತಮ್ಮತಮ್ಮೊಳಗೆ ಆಲೋಚಿಸಿಕೊಳ್ಳಲಿ. ಪೂರ್ವದಿಂದಲೂ ಈ ಸಂಗತಿಯನ್ನು ಪ್ರಕಟಿಸಿದವರು ಯಾರು? ಅದು ನಡೆಯುವದಕ್ಕೆ ಮುಂಚೆ ಯಾರು ತಿಳಿಸಿದರು? ಯೆಹೋವನೆಂಬ ನಾನಲ್ಲವೆ. ನಾನು ಹೊರತು ಇನ್ನು ಯಾವ ದೇವರೂ ಇಲ್ಲ, ನನ್ನ ವಿನಹ ಸತ್ಯಸ್ವರೂಪನೂ ರಕ್ಷಕನೂ ಆದ ದೇವರು ಇಲ್ಲವೇ ಇಲ್ಲ.",
			"22": "<sup>22</sup> ಎಲ್ಲಾ ದಿಗಂತಗಳವರೇ, ನನ್ನ ಕಡೆಗೆ ತಿರುಗಿಕೊಳ್ಳಿರಿ, ರಕ್ಷಣೆಯನ್ನು ಹೊಂದಿಕೊಳ್ಳಿರಿ; ನಾನೇ ದೇವರು, ಇನ್ನು ಯಾರೂ ಇಲ್ಲ.",
			"23": "<sup>23</sup> ಎಲ್ಲರೂ ನನಗೆ ಅಡ್ಡಬೀಳುವರು, ಎಲ್ಲರೂ ನನ್ನನ್ನು ದೇವರೆಂದು ಪ್ರತಿಜ್ಞೆಮಾಡುವರು ಎಂಬ ನುಡಿ ನನ್ನ ಸತ್ಯದ ಬಾಯಿಂದ ಹೊರಟಿದೆ, ಅದನ್ನು ಹಿಂತೆಗೆಯೆನು, ನನ್ನ ಮೇಲೆ ಆಣೆಯಿಟ್ಟಿದ್ದೇನೆ.",
			"24": "<sup>24</sup> ಯೆಹೋವನಲ್ಲಿ ಮಾತ್ರ ಸತ್ಯಕಾರ್ಯಗಳೂ ಶಕ್ತಿಯೂ ಉಂಟು; ಆತನ ಮೇಲೆ ಉರಿಗೊಂಡವರೆಲ್ಲರೂ ನಾಚಿಕೆಪಟ್ಟು ಆತನನ್ನು ಮರೆಹೊಗುವರು;",
			"25": "<sup>25</sup> ಇಸ್ರಾಯೇಲಿನ ಸಮಸ್ತ ಸಂತತಿಯವರೂ ಯೆಹೋವನ ಮೂಲಕ ಸತ್ಯವಂತರಾಗಿ ಆತನಲ್ಲಿ ಆನಂದಿಸುವರು ಎಂದು ನನ್ನ ವಿಷಯವಾಗಿ ಜನರು ಅಂದುಕೊಳ್ಳುತ್ತಾರೆ."
		},
		"46": {
			"1": "<sup>1</sup> ಬೇಲ್ ದೇವತೆಯು ಬೊಗ್ಗಿದೆ, ನೆಬೋದೇವತೆಯು ಕುಗ್ಗಿದೆ, ಅವುಗಳ ಬೊಂಬೆಗಳು ದನ ಮೊದಲಾದವುಗಳ ಮೇಲೆ ಹೊರಿಸಲ್ಪಟ್ಟಿವೆ, ನೀವು ಮೆರವಣಿಗೆಯಾಗಿ ಹೊರುತ್ತಿದ್ದವುಗಳು ಪಶುಗಳಿಗೆ ಭಾರವಾದ ಹೊರೆಯಾಗಿ ಅವುಗಳನ್ನು ಬಳಲಿಸಿವೆ.",
			"2": "<sup>2</sup> ಒಟ್ಟಿಗೆ ಕುಗ್ಗಿ ಬೊಗ್ಗಿವೆ, ತಮ್ಮ ಮೇಲೆ ಬಿದ್ದ ಭಾರವನ್ನು ಹೊತ್ತು ನಿರ್ವಹಿಸಲಾರದೆ ತಾವೇ ಸೆರೆಗೆ ಹೋಗಿವೆ.",
			"3": "<sup>3</sup> ಯಾಕೋಬವಂಶವೇ, ಇಸ್ರಾಯೇಲ್ ಸಂತಾನಶೇಷವೇ, ನನ್ನ ಮಾತಿಗೆ ಕಿವಿಗೊಡಿರಿ; ನಿಮ್ಮನ್ನು ಗರ್ಭದಿಂದ ಹೊರುತ್ತಿದ್ದೇನೆ, ಹುಟ್ಟಿದಂದಿನಿಂದ ವಹಿಸುತ್ತಿದ್ದೇನೆ;",
			"4": "<sup>4</sup> ನಿಮ್ಮ ಮುಪ್ಪಿನ ತನಕ ನಾನೇ ಆಧಾರ, ನರೆಬಂದಾಗಲೂ ನಿಮ್ಮನ್ನು ಹೊತ್ತು ಸಹಿಸುವೆನು; ನಾನೇ ಉಂಟುಮಾಡಿದೆನು, ನಾನೇ ಹೊರುವೆನು, ಹೌದು, ನಿಮ್ಮನ್ನು ಹೊತ್ತು ಸಹಿಸಿ ನಿರ್ವಹಿಸುವೆನು.",
			"5": "<sup>5</sup> ನನ್ನನ್ನು ಯಾರಿಗೆ ಸರಿಕಟ್ಟಿ ಹೋಲಿಸೀರಿ, ಇಬ್ಬರೂ ಸಮಾನರೆಂದು ನನ್ನನ್ನು ಯಾರೊಡನೆ ಸಮಮಾಡೀರಿ?",
			"6": "<sup>6</sup> ಚೀಲದಿಂದ ಚಿನ್ನವನ್ನು ಸುರಿದು ತ್ರಾಸಿನಲ್ಲಿ ಬೆಳ್ಳಿಯನ್ನು ತೂಗಿ ಅಕ್ಕಸಾಲಿಗನಿಗೆ ಕೊಟ್ಟು ಕೂಲಿಗೊತ್ತುಮಾಡಿ ದೇವರನ್ನು ಮಾಡಿಸಿ ಅದಕ್ಕೆ ಎರಗಿ ಪೂಜೆಮಾಡುವರು.",
			"7": "<sup>7</sup> ಅದನ್ನು ಹೆಗಲ ಮೇಲೆ ಹೊತ್ತುಕೊಂಡು ಮೆರವಣಿಗೆ ಮಾಡಿ ಪೀಠದ ಮೇಲೆ ಇಳಿಸಿ ನಿಲ್ಲಿಸುವರು. ಅದು ಅಲ್ಲಿಂದ ಜರಗದು, ಒಬ್ಬನು ಕೂಗಿಕೊಂಡರೂ ಉತ್ತರಕೊಟ್ಟು ಕಷ್ಟದಿಂದ ಅವನನ್ನು ಬಿಡಿಸಲಾರದು.",
			"8": "<sup>8</sup> ದ್ರೋಹಿಗಳೇ, ಇದನ್ನು ಜ್ಞಾಪಕದಲ್ಲಿಟ್ಟುಕೊಂಡು ಗಮನಿಸಿ ಮನುಷ್ಯರಿಗೆ ತಕ್ಕಂತೆ ನಡೆಯಿರಿ!",
			"9": "<sup>9</sup> ಪುರಾತನದ ಹಳೇ ಸಂಗತಿಗಳನ್ನು ಜ್ಞಾಪಿಸಿಕೊಳ್ಳಿರಿ; ನಾನೇ ದೇವರು, ಇನ್ನು ಯಾರೂ ಅಲ್ಲ; ನಾನೇ ಪರಮದೇವರು, ನನಗೆ ಸರಿಸಮಾನರಿಲ್ಲ.",
			"10": "<sup>10</sup> ಆರಂಭದಲ್ಲಿಯೇ ಅಂತ್ಯವನ್ನು ತಿಳಿಸಿದ್ದೇನೆ; ನನ್ನ ಸಂಕಲ್ಪವು ನಿಲ್ಲುವದು, ನನ್ನ ಇಷ್ಟಾರ್ಥವನ್ನೆಲ್ಲಾ ನೆರವೇರಿಸುವೆನು ಎಂದು ಹೇಳಿ ಇನ್ನೂ ನಡೆಯದಿರುವ ಕಾರ್ಯಗಳನ್ನು ಪುರಾತನಕಾಲದಲ್ಲಿಯೇ ಅರುಹಿದ್ದೇನೆ;",
			"11": "<sup>11</sup> ಮೂಡಲಿಂದ ಹದ್ದು ಎರಗಲಿ ಎಂದು, ಅಂದರೆ ನನ್ನ ಸಂಕಲ್ಪವನ್ನು ನೆರವೇರಿಸತಕ್ಕವನು ದೂರದೇಶದಿಂದ ಬರಲಿ ಎಂದು ಕೂಗಿದ್ದೇನೆ; ನಾನು ನುಡಿದಿದ್ದೇನೆ, ಈಡೇರಿಸುವೆನು; ಆಲೋಚಿಸಿದ್ದೇನೆ, ಸಾಧಿಸುವೆನು.",
			"12": "<sup>12</sup> ಧರ್ಮಕ್ಕೆ ದೂರರಾದ ಹಟಗಾರರೇ, ನನ್ನ ಮಾತಿಗೆ ಕಿವಿಗೊಡಿರಿ;",
			"13": "<sup>13</sup> ನನ್ನ ಧರ್ಮವನ್ನು ಹತ್ತಿರಕ್ಕೆ ಬರಮಾಡುವೆನು, ಅದು ಇನ್ನು ದೂರವಾಗಿರದು, ನನ್ನ ರಕ್ಷಣೆಯು ತಡವಾಗದು; ಚೀಯೋನಿನಲ್ಲಿ ರಕ್ಷಣಾಕಾರ್ಯವನ್ನು ನಡಿಸಿ ಇಸ್ರಾಯೇಲಿಗೆ ನನ್ನ ಮಹಿಮೆಯನ್ನು ದಯಪಾಲಿಸುವೆನು."
		},
		"47": {
			"1": "<sup>1</sup> ಯುವತಿಯೇ, ಬಾಬೆಲ್‍ಪುರಿಯೇ, ಕೆಳಕ್ಕಿಳಿದು ಧೂಳಿನಲ್ಲಿ ಕೂತುಕೋ, ಕಸ್ದೀಯರ ನಗರಿಯೇ, ಸಿಂಹಾಸನರಹಿತಳಾಗಿ ನೆಲದ ಮೇಲೆ ಕುಕ್ಕರಿಸು! ನೀನು ಇನ್ನು ಕೋಮಲೆ, ಸುಕುಮಾರಿ, ಎಂದು ಎನಿಸಿಕೊಳ್ಳೆ.",
			"2": "<sup>2</sup> ಬೀಸುವ ಕಲ್ಲನ್ನು ಹಿಡಿದು ಹಿಟ್ಟು ಬೀಸು, ಮುಸುಕನ್ನು ತೆಗೆದುಹಾಕಿ ಈಜಾಡುವ ನೆರಿಗೆಯನ್ನು ಹರಿದುಬಿಟ್ಟು ಬೆತ್ತಲೆಕಾಲಾಗಿ ಹೊಳೆಗಳನ್ನು ಹಾದುಹೋಗು.",
			"3": "<sup>3</sup> ನಿನ್ನ ಮಾನವು ಬೈಲಿಗೆ ಬಿದ್ದು ನೀನು ನಾಚಿಕೆಗೀಡಾಗುವಿ, ನಾನು ಯಾರನ್ನೂ ಕರುಣಿಸದೆ ಮುಯ್ಯಿ ತೀರಿಸುವೆನು.",
			"4": "<sup>4</sup> ನಮ್ಮ ವಿಮೋಚಕನಿಗೆ ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೆಂಬ ನಾಮಧೇಯ, ಆತನೇ ಇಸ್ರಾಯೇಲ್ಯರ ಸದಮಲಸ್ವಾಮಿ!",
			"5": "<sup>5</sup> ಕಸ್ದೀಯರ ನಗರಿಯೇ, ಮೌನವಾಗಿ ಕೂತುಕೋ, ಕತ್ತಲೊಳಗೆ ಹೊಗು, ನೀನು ಇನ್ನು ರಾಜ್ಯಗಳ ರಾಣಿ ಎನಿಸಿಕೊಳ್ಳೆ.",
			"6": "<sup>6</sup> ನಾನು ನನ್ನ ಜನರ ಮೇಲೆ ರೋಷಗೊಂಡು ಅವರನ್ನು ನಿನ್ನ ಕೈವಶ ಮಾಡಿ ನನ್ನ ಸ್ವಾಸ್ತ್ಯವನ್ನು ಹೊಲೆಗೆಡಿಸಿದೆನು; ನೀನು ಅವರನ್ನು ಹೇಗೂ ಕರುಣಿಸದೆ ಮುದುಕರ ಮೇಲೆಯೂ ಬಹುಭಾರವಾದ ನೊಗವನ್ನು ಹೊರಿಸಿದಿ.",
			"7": "<sup>7</sup> ನಾನು ಶಾಶ್ವತವಾದ ರಾಣಿ ಎಂದು ನೀನು ಅಂದುಕೊಂಡು ಅವರ ಹಿಂಸೆಗಳನ್ನು ಮನಸ್ಸಿಗೆ ತಾರದೆ, ಅವುಗಳಿಂದ ನಿನಗಾಗುವ ಪರಿಣಾಮವನ್ನು ನೆನಸಿಕೊಳ್ಳಲಿಲ್ಲ.",
			"8": "<sup>8</sup> ನಾನೇ ಇರುವವಳು, ನನ್ನ ಹೊರತು ಇನ್ನು ಯಾರೂ ಇಲ್ಲ, ನಾನು ವಿಧವೆಯಾಗಿ ಕೂತುಕೊಳ್ಳುವದಿಲ್ಲ, ಪುತ್ರಶೋಕವನ್ನು ಅನುಭವಿಸುವದಿಲ್ಲ ಅಂದುಕೊಳ್ಳುವವಳೇ, ಭೋಗಾಸಕ್ತಳೇ, ನೆಮ್ಮದಿಯಾಗಿ ನೆಲೆಗೊಂಡಿರುವವಳೇ, ಈಗ ಇದನ್ನು ಕೇಳು -",
			"9": "<sup>9</sup> ಒಂದೇ ದಿನದೊಳಗೆ, ಒಂದು ಕ್ಷಣದಲ್ಲಿ, ಪುತ್ರಶೋಕ, ವೈಧವ್ಯ, ಇವೆರಡೂ ನಿನಗುಂಟಾಗುವವು; ನೀನು ಎಷ್ಟು ಮಾಟಮಾಡಿದರೂ ಎಷ್ಟೇ ಮಂತ್ರತಂತ್ರಗಳನ್ನು ನಡಿಸಿದರೂ ಇವುಗಳನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ಅನುಭವಿಸುವಿ.",
			"10": "<sup>10</sup> ನೀನು ಮಾಡಿದ ಕೇಡಿಗೆ ಭಯಪಡದೆ ನನ್ನನ್ನು ಯಾರೂ ನೋಡರು ಎಂದುಕೊಂಡಿದ್ದೀ; ನಿನ್ನ ಜ್ಞಾನವಿವೇಕಗಳು ನಿನ್ನನ್ನು ಸೊಟ್ಟಗೆ ತಿರುಗಿಸಿದ್ದರಿಂದ ನಾನೇ ಇರುವವಳು, ನನ್ನ ಹೊರತು ಇನ್ನು ಯಾರೂ ಇಲ್ಲವೆಂದು ಯೋಚಿಸಿಕೊಂಡಿದ್ದೀ.",
			"11": "<sup>11</sup> ಹೀಗಿರಲು ನೀನು ಮಂತ್ರಿಸಿ ನಿವಾರಿಸಲಾರದ ಕೇಡು ನಿನಗೆ ಸಂಭವಿಸುವದು, ನೀನು ಪರಿಹರಿಸಲಾಗದ ವಿಪತ್ತು ನಿನ್ನ ಮೇಲೆ ಬೀಳುವದು; ನೀನು ತಪ್ಪಿಸಲರಿಯದ ನಾಶನವು ನಿನಗೇ ತಟ್ಟನೆ ಉಂಟಾಗುವದು.",
			"12": "<sup>12</sup> ಎದ್ದು ನಿಲ್ಲು, ನೀನು ಬಾಲ್ಯಾರಭ್ಯ ಪ್ರಯಾಸಪಟ್ಟು ಅಭ್ಯಾಸಿಸಿರುವ ನಿನ್ನ ಮಂತ್ರತಂತ್ರಗಳನ್ನೂ ಲೆಕ್ಕವಿಲ್ಲದ ನಿನ್ನ ಮಾಟಗಳನ್ನೂ ಪ್ರಯೋಗಿಸು; ಇದರಿಂದ ಒಂದು ವೇಳೆ ನಿನಗೆ ಪ್ರಯೋಜನವಾದೀತು, ಒಂದು ವೇಳೆ [ನಿನ್ನ ಶತ್ರುವಿನಲ್ಲಿ] ಭಯಹುಟ್ಟೀತು.",
			"13": "<sup>13</sup> ಮಂತ್ರಾಲೋಚನೆಗಳನ್ನು ಕೇಳಿ ಕೇಳಿ ನಿನಗೆ ಸಾಕಾಯಿತಲ್ಲವೇ; ಖಗೋಲಜ್ಞರು, ಜೋಯಿಸರು, ಪಂಚಾಂಗದವರು, ಇವರೆಲ್ಲರೂ ನಿಂತು ನಿನಗೆ ಬರುವ ವಿಪತ್ತುಗಳಿಂದ ನಿನ್ನನ್ನು ಉದ್ಧರಿಸಲಿ.",
			"14": "<sup>14</sup> ಆಹಾ, ಇವರೆಲ್ಲಾ ಕೂಳೆಯಂತಿರುವರು, ಬೆಂಕಿಯು ಅವರನ್ನು ಸುಟ್ಟುಬಿಡುವದು, ಜ್ವಾಲೆಯ ರಭಸದಿಂದ ತಮ್ಮನ್ನು ರಕ್ಷಿಸಿಕೊಳ್ಳಲಾರರು; ಅದು ಕಾಯಿಸಿಕೊಳ್ಳುವ ಕೆಂಡವಲ್ಲ, ಮುಂದೆ ಕುಳಿತರೆ ಹಾಯಾಗಿರುವ ಉರಿಯಲ್ಲ.",
			"15": "<sup>15</sup> ನೀನು ಪ್ರಯಾಸಪಟ್ಟವುಗಳೆಲ್ಲಾ ನಿನಗೆ ಹೀಗಾಗುವವು; ನಿನ್ನ ಬಾಲ್ಯದಿಂದ ನಿನ್ನಲ್ಲಿ ವ್ಯಾಪಾರಮಾಡಿದವರೆಲ್ಲರೂ ಚದರಿ ತಮ್ಮ ತಮ್ಮ ಪ್ರಾಂತಕ್ಕೆ ಹೋಗಿಬಿಡುವರು; ನಿನ್ನನ್ನು ರಕ್ಷಿಸಲು ಒಬ್ಬನೂ ಇರನು."
		},
		"48": {
			"1": "<sup>1</sup> ಇಸ್ರಾಯೇಲೆಂಬ ಹೆಸರಿನವರೂ ಯೆಹೂದವಂಶೋತ್ಪನ್ನರೂ ಆದ ಯಾಕೋಬನ ಮನೆತನದವರೇ, ನೀವು ಯೆಹೋವನ ನಾಮದ ಮೇಲೆ ಆಣೆಯಿಟ್ಟು ಇಸ್ರಾಯೇಲಿನ ದೇವರನ್ನು ಸ್ಮರಿಸುತ್ತೀರಿ, ನಿಜ, ಆದರೆ ಈ ನಿಮ್ಮ ಕಾರ್ಯಗಳು ಸತ್ಯಕ್ಕೂ ಧರ್ಮಕ್ಕೂ ಅನುಸಾರವಾಗಿಲ್ಲ.",
			"2": "<sup>2</sup> ಇವರು ತಮ್ಮನ್ನು ಪರಿಶುದ್ಧ ಪಟ್ಟಣದವರು ಎಂದುಕೊಂಡು ದೇವರು ಇಸ್ರಾಯೇಲಿನವನೇ ಎಂದು ಆತನ ಮೇಲೆ ಭರವಸವಿಟ್ಟುಕೊಂಡಿರುತ್ತಾರೆ. ಆತನ ಹೆಸರೋ ಸಕಲ ಸೈನ್ಯಗಳ ಅಧೀಶ್ವರನಾದ ಯೆಹೋವನೆಂಬದು.",
			"3": "<sup>3</sup> ಯಾಕೋಬನ ಮನೆತನವೇ, ಕೇಳು, ಈವರೆಗೂ ನಡೆದ ಸಂಗತಿಗಳನ್ನು ಪುರಾತನಕಾಲದಲ್ಲಿಯೇ ತಿಳಿಸಿದೆನು, ಹೌದು, ನನ್ನ ಬಾಯಿಂದ ಹೊರಟವು, ಪ್ರಕಟಿಸಿದೆನು; ನಾನು ತಟ್ಟನೆ ನಡಿಸಲು ನೆರವೇರಿದವು.",
			"4": "<sup>4</sup> ನೀನು ಹಟಗಾರ, ನಿನ್ನ ಕತ್ತಿನ ನರವು ಕಬ್ಬಿಣ, ನಿನ್ನ ಹಣೆ ತಾಮ್ರ ಎಂದು ನಾನು ತಿಳಿದುಕೊಂಡೆನು;",
			"5": "<sup>5</sup> ಅದರಿಂದಲೇ ಈ ಕಾರ್ಯಗಳನ್ನು ನನ್ನ ವಿಗ್ರಹವು ನಡಿಸಿದೆ, ನನ್ನ ಕೆತ್ತಿದ ಬೊಂಬೆ, ಎರಕದ ಪುತ್ತಳಿ ಇವುಗಳನ್ನು ವಿಧಿಸಿದೆ ಎಂದು ನೀನು ಹೇಳಿಕೊಳ್ಳದಂತೆ ನಾನು ಪುರಾತನಕಾಲದಲ್ಲಿಯೇ ತಿಳಿಸಿದೆನು, ನಡೆಯುವದಕ್ಕೆ ಮುಂಚೆಯೇ ಪ್ರಕಟಿಸಿದೆನು.",
			"6": "<sup>6</sup> [ಆಗಲೇ] ಕೇಳಿದಿಯಲ್ಲಾ, [ಈಗ] ನಡೆದದ್ದನ್ನೆಲ್ಲಾ ನೋಡು, ನಿನ್ನವರೇ ಪ್ರಸಿದ್ಧಿಪಡಿಸಲಿ, ಇಂದಿನಿಂದ ಹೊಸ ಸಂಗತಿಗಳನ್ನು, ನೀನು ತಿಳಿಯದಿದ್ದ ಗುಪ್ತ ವಿಷಯಗಳನ್ನು, ನಿನಗೆ ಅರುಹುತ್ತೇನೆ.",
			"7": "<sup>7</sup> ಈಗಲೇ ಉಂಟಾಗುತ್ತಿವೆ, ಪುರಾತನಕಾಲದಲ್ಲಿ ಆಗಿಲ್ಲ; ಆಹಾ, ಮೊದಲೇ ನಾನು ತಿಳಿದುಕೊಂಡಿದ್ದೆನು ಎಂದು ನೀನು ಅಂದುಕೊಳ್ಳದ ಹಾಗೆ ಈ ದಿನಕ್ಕೆ ಮುಂಚೆ ನಾನು ನಿನಗೆ ಕೇಳಗೊಡಿಸಲಿಲ್ಲ.",
			"8": "<sup>8</sup> ನೀನು ಕೇಳಿಲ್ಲ, ತಿಳಿದೂ ಇಲ್ಲ, ಪುರಾತನಕಾಲದಿಂದ ನಿನ್ನ ಕಿವಿಯು ತೆರೆದೇ ಇಲ್ಲ; ನೀನು ದೊಡ್ಡ ವಂಚಕನು, ಆಗರ್ಭದ್ರೋಹಿ ಎನಿಸಿಕೊಂಡವನು ಎಂದು ನಾನು ತಿಳಿದುಕೊಂಡಿದ್ದೆನಷ್ಟೆ.",
			"9": "<sup>9</sup> ನನ್ನ ಹೆಸರು ಕೆಡದಂತೆ ನಿನ್ನ ಮೇಲಣ ಕೋಪವನ್ನು ತಡೆದು ನನ್ನ ಕೀರ್ತಿಗೆ ಕಳಂಕಬಾರದಂತೆ ನಿನ್ನನ್ನು ನಿರ್ಮೂಲಮಾಡದೆ ತಾಳಿಕೊಳ್ಳುವೆನು.",
			"10": "<sup>10</sup> ನೋಡು, ನಿನ್ನನ್ನು ಶೋಧಿಸಿದ್ದೇನೆ, ಆದರೆ ಬೆಳ್ಳಿಯಷ್ಟು ಶೋಧಿಸಲಿಲ್ಲ; ಸಂಕಟವೆಂಬ ಪುಟಕ್ಕೆ ಹಾಕಿ ನಿನ್ನನ್ನು ಪರೀಕ್ಷಿಸಿದ್ದೇನೆ.",
			"11": "<sup>11</sup> ನನಗಾಗಿ, ನನಗೋಸ್ಕರವೇ, ಇದನ್ನು ಮಾಡುವೆನು; ನನ್ನ ಹೆಸರು ಕೆಡಬಾರದಷ್ಟೆ; ನನ್ನ ಮಹಿಮೆಯನ್ನು ಮತ್ತೊಬ್ಬನಿಗೆ ಸಲ್ಲಗೊಡಿಸೆನು.",
			"12": "<sup>12</sup> ಯಾಕೋಬೇ, ನಾನು ಕರೆದ ಇಸ್ರಾಯೇಲೇ, ನನ್ನ ಕಡೆಗೆ ಕಿವಿಗೊಡು; ನಾನೇ ಪರಮಾತ್ಮನು; ನಾನೇ ಆದಿ, ಅಂತವೂ ನಾನೇ.",
			"13": "<sup>13</sup> ನನ್ನ ಕೈ ಭೂಮಿಗೆ ಅಸ್ತಿವಾರವನ್ನು ಹಾಕಿತು, ನನ್ನ ಬಲಗೈ ಆಕಾಶವನ್ನು ಹರಡಿತು, ನಾನು ಕರೆದ ಕೂಡಲೆ ಅವೆರಡೂ ಸಿದ್ಧವಾಗಿ ನಿಲ್ಲುವವು.",
			"14": "<sup>14</sup> ನೀವೆಲ್ಲರು ಕೂಡಿಕೊಂಡು ಕೇಳಿರಿ! ಇವರಲ್ಲಿನ ಯಾವ ದೇವರು ಈ ಸಂಗತಿಗಳನ್ನು ಅರುಹಿದ್ದಾನೆ? ಯೆಹೋವನ ಪ್ರೀತಿಪಾತ್ರನು ಬಾಬೆಲಿನಲ್ಲಿ ಆತನ ಇಷ್ಟವನ್ನು ನೆರವೇರಿಸುವನು, ಕಸ್ದೀಯರ ಮೇಲೆ ಕೈಮಾಡುವನು.",
			"15": "<sup>15</sup> ನಾನೇ, ನಾನೇ ಬಾಯಿಬಿಟ್ಟು ಅವನನ್ನು ಕರೆದು ಬರಮಾಡಿದ್ದೇನೆ; ಅವನ ಮಾರ್ಗವು ಸಾರ್ಥಕವಾಗುವದು.",
			"16": "<sup>16</sup> ನನ್ನ ಬಳಿಗೆ ಬಂದು ಇದನ್ನು ಕೇಳಿರಿ; ಆದಿಯಿಂದಲೂ ನಾನು ಗುಟ್ಟಾಗಿ ಮಾತಾಡಲಿಲ್ಲ, [ಭೂಮಿಯು] ಉಂಟಾದಂದಿನಿಂದ ಅಲ್ಲಿ ಇದ್ದೇನೆ. (ಈಗ ಯೆಹೋವನೆಂಬ ದೇವರು ತನ್ನ ಆತ್ಮದೊಡನೆ ನನ್ನನ್ನು ಕಳುಹಿಸಿದ್ದಾನೆ.)",
			"17": "<sup>17</sup> ನಿನ್ನ ವಿಮೋಚಕನೂ ಇಸ್ರಾಯೇಲ್ಯರ ಸದಮಲಸ್ವಾಮಿಯೂ ಆದ ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ನಾನೇ ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನು, ನಿನಗೆ ವೃದ್ಧಿಮಾರ್ಗವನ್ನು ಬೋಧಿಸಿ ನೀನು ನಡೆಯಬೇಕಾದ ದಾರಿಯಲ್ಲಿ ನಿನ್ನನ್ನು ನಡೆಯಿಸುವವನಾಗಿದ್ದೇನೆ.",
			"18": "<sup>18</sup> ನೀನು ನನ್ನ ಆಜ್ಞೆಗಳನ್ನು ಕೇಳಿದ್ದರೆ ಎಷ್ಟೋ ಚೆನ್ನಾಗಿತ್ತು! ನಿನ್ನ ಸುಖವು ದೊಡ್ಡ ನದಿಯಂತೆಯೂ ನಿನ್ನ ಕ್ಷೇಮವು ಸಮುದ್ರದ ಅಲೆಗಳ ಹಾಗೂ ಇರುತ್ತಿದ್ದವು;",
			"19": "<sup>19</sup> ನಿನ್ನ ಸಂತತಿಯವರು ಮರಳಿನೋಪಾದಿಯಲ್ಲಿಯೂ ನಿನ್ನ ಹೊಟ್ಟೆಯಲ್ಲಿ ಹುಟ್ಟಿದವರು ಉಸುಬಿನ ಅಣುರೇಣುಗಳ ಪ್ರಕಾರವೂ ಅಸಂಖ್ಯಾತರಾಗುತ್ತಿದ್ದರು; ಅವರ ಹೆಸರು ನನ್ನ ಮುಂದೆ ನಿರ್ಮೂಲವಾಗಿ ಹಾಳಾಗುತ್ತಿರಲಿಲ್ಲ.",
			"20": "<sup>20</sup> ಬಾಬೆಲಿನಿಂದ ಹೊರಡಿರಿ, ಕಸ್ದೀಯರ ಕಡೆಯಿಂದ ಹಾರಿಹೋಗಿರಿ; ಹರ್ಷಧ್ವನಿಯಿಂದ ಇದನ್ನು ತಿಳಿಸಿರಿ, ಪ್ರಕಟಿಸಿರಿ, ಭೂಮಿಯ ಕಟ್ಟಕಡೆಯವರೆಗೂ ಪ್ರಚಾರಪಡಿಸಿರಿ; ಯೆಹೋವನು ತನ್ನ ಸೇವಕನಾದ ಯಾಕೋಬನನ್ನು ವಿಮೋಚಿಸಿದ್ದಾನೆಂದು ಹೇಳಿರಿ.",
			"21": "<sup>21</sup> ಆತನು ಅವರನ್ನು ಮರುಭೂಮಿಯಲ್ಲಿ ನಡಿಸಿದಾಗ ಅವರು ದಾಹಗೊಳ್ಳಲಿಲ್ಲ; ಅವರಿಗಾಗಿ ನೀರನ್ನು ಕಲ್ಲಿನೊಳಗಿಂದ ಹರಿಸಿದನು; ಆತನು ಬಂಡೆಯನ್ನು ಸೀಳಲು ಜಲಪಾತವಾಯಿತು.",
			"22": "<sup>22</sup> ದುಷ್ಟರಿಗೆ ಸಮಾಧಾನವೇ ಇಲ್ಲ ಎಂದು ಯೆಹೋವನು ನುಡಿಯುತ್ತಾನೆ."
		},
		"49": {
			"1": "<sup>1</sup> ದ್ವೀಪನಿವಾಸಿಗಳೇ, ನನ್ನ ಕಡೆಗೆ ಕಿವಿಗೊಡಿರಿ, ದೂರದ ಜನಾಂಗಗಳೇ, ಆಲಿಸಿರಿ! ನಾನು ಗರ್ಭದಲ್ಲಿದ್ದಾಗಲೇ ಯೆಹೋವನು ನನ್ನನ್ನು ಕರೆದನು, ತಾಯಿಯ ಉದರದಲ್ಲಿದ್ದಂದಿನಿಂದಲೂ ನನ್ನ ಹೆಸರನ್ನು ಹೇಳುತ್ತಿದ್ದಾನೆ.",
			"2": "<sup>2</sup> ನನ್ನ ಬಾಯನ್ನು ಹದವಾದ ಖಡ್ಗವನ್ನಾಗಿ ಮಾಡಿ ತನ್ನ ಕೈಯ ನೆರಳಿನಲ್ಲಿ ನನ್ನನ್ನು ಹುದುಗಿಸಿದ್ದಾನೆ; ನನ್ನನ್ನು ಚೂಪಾದ ಬಾಣವನ್ನಾಗಿ ರೂಪಿಸಿ ತನ್ನ ಬತ್ತಳಿಕೆಯಲ್ಲಿ ಮುಚ್ಚಿಟ್ಟಿದ್ದಾನೆ.",
			"3": "<sup>3</sup> ಆತನು ನನಗೆ - ನೀನು ನನ್ನ ಸೇವಕನೂ ನಾನು ಪ್ರಭಾವಹೊಂದಬೇಕಾದ ಇಸ್ರಾಯೇಲೂ ಆಗಿದ್ದೀ ಎಂದು ಹೇಳಿದನು.",
			"4": "<sup>4</sup> ಅದಕ್ಕೆ ನಾನು - ನನ್ನ ಪ್ರಯಾಸವು ವ್ಯರ್ಥ, ನನ್ನ ಶಕ್ತಿಯನ್ನೆಲ್ಲಾ ವ್ರಯಮಾಡಿದ್ದು ಹಾಳೇ, ಬರಿ ಗಾಳಿಯೇ; ಆದರೂ ನನ್ನ ನ್ಯಾಯವು ಯೆಹೋವನಲ್ಲಿದೆ, ನನಗೆ ಲಾಭವು ನನ್ನ ದೇವರಿಂದಲೇ ಆಗುವದು ಅಂದುಕೊಂಡೆನು.",
			"5": "<sup>5</sup> ಆಹಾ, ನಾನು ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ಮಾನ್ಯನು, ನನ್ನ ದೇವರೇ ನನಗೆ ಬಲವು. ಯಾಕೋಬನ್ನು ತನಗೆ ಸೇರಿಸಿಕೊಳ್ಳಬೇಕೆಂತಲೂ ಇಸ್ರಾಯೇಲು ತನ್ನ ಕಡೆಗೆ ಕೂಡಿಬರಬೇಕೆಂತಲೂ ಆತನು ಗರ್ಭದಲ್ಲಿಯೇ ನನ್ನನ್ನು ತನ್ನ ಸೇವಕನನ್ನಾಗಿ ರೂಪಿಸಿದನಲ್ಲವೆ.",
			"6": "<sup>6</sup> ಆತನೇ ಈಗ ಹೀಗನ್ನುತ್ತಾನೆ - ನೀನು ನನ್ನ ಸೇವಕನಾಗಿ ಮಾಡಬೇಕಾದವುಗಳಲ್ಲಿ ಯಾಕೋಬಿನ ಕುಲಗಳನ್ನು ಉನ್ನತಪಡಿಸುವದೂ ಇಸ್ರಾಯೇಲಿನಲ್ಲಿ ರಕ್ಷಿತರಾದವರನ್ನು ತಿರಿಗಿ ಬರಮಾಡುವದೂ ಅಲ್ಪಕಾರ್ಯವೇ ಸರಿ; ನನ್ನ ರಕ್ಷಣೆಯು ಲೋಕದ ಕಟ್ಟಕಡೆಯವರೆಗೆ ವ್ಯಾಪಿಸುವಂತೆ ನಿನ್ನನ್ನು ಅನ್ಯಜನಾಂಗಗಳಿಗೂ ಬೆಳಕನ್ನಾಗಿ ದಯಪಾಲಿಸುವೆನು.",
			"7": "<sup>7</sup> ಮನಃಪೂರ್ವಕವಾಗಿ ತಿರಸ್ಕರಿಸಲ್ಪಟ್ಟವನೂ ಅನ್ಯಜನಾಂಗಕ್ಕೆ ಅಸಹ್ಯನೂ ಜನದೊಡೆಯರ ಸೇವಕನೂ ಆದವನಿಗೆ ಇಸ್ರಾಯೇಲಿನ ವಿಮೋಚಕನೂ ಸದಮಲಸ್ವಾಮಿಯೂ ಆದ ಯೆಹೋವನು ಹೀಗೆ ಹೇಳುತ್ತಾನೆ - ಯೆಹೋವನ ಪ್ರಾಮಾಣಿಕತೆಯನ್ನೂ ಇಸ್ರಾಯೇಲಿನ ಸದಮಲಸ್ವಾಮಿಯು ನಿನ್ನನ್ನು ಪರಿಗ್ರಹಿಸಿರುವದನ್ನೂ ಅರಸರು ನೋಡಿ ಎದ್ದು ನಿಲ್ಲುವರು, ಅಧಿಪತಿಗಳು ಅಡ್ಡಬೀಳುವರು.",
			"8": "<sup>8</sup> ಇದೇ ಯೆಹೋವನ ನುಡಿ - ಈ ಪ್ರಸನ್ನತೆಯ ಕಾಲದಲ್ಲಿ ನಿನಗೆ ಸದುತ್ತರವನ್ನು ದಯಪಾಲಿಸಿದ್ದೇನೆ, ಈ ರಕ್ಷಣೆಯ ದಿನದಲ್ಲಿ ನಿನಗೆ ಸಹಾಯಮಾಡಿದ್ದೇನೆ; ನಾನು ನಿನ್ನನ್ನು ಕಾಪಾಡುತ್ತಾ [ನನ್ನ] ಜನರಿಗೆ ಒಡಂಬಡಿಕೆಯ ಆಧಾರವನ್ನಾಗಿ ನೇಮಿಸಿ ಬಂದಿಗಳಿಗೆ -",
			"9": "<sup>9</sup> ಹೊರಟುಹೋಗಿರಿ, ಕತ್ತಲಲ್ಲಿರುವವರಿಗೆ - ಬೆಳಕಿಗೆ ಹೊರಡಿರಿ ಎಂದು ಅಪ್ಪಣೆಕೊಟ್ಟು ಹಾಳಾಗಿದ್ದ ಸ್ವಾಸ್ತ್ಯಗಳನ್ನು ಅವರಿಗೆ ಹಂಚಿ ದೇಶವನ್ನು ಜೀರ್ಣೋದ್ಧಾರಮಾಡುವೆನು. ನನ್ನ ಜನವೆಂಬ ಹಿಂಡು ದಾರಿಗಳಲ್ಲಿ ಮೇಯುವದು. ಎಲ್ಲಾ ಬೋಳುಬೆಟ್ಟಗಳೂ ಕೂಡ ಅದಕ್ಕೆ ಹುಲ್ಲುಗಾವಲಾಗುವವು.",
			"10": "<sup>10</sup> ಅವರಿಗೆ ಹಸಿವೆ ಬಾಯಾರಿಕೆಗಳು ಆಗವು. ಝಳವೂ ಬಿಸಿಲೂ ಬಡಿಯವು; ಅವರನ್ನು ಕರುಣಿಸುವಾತನು ದಾರಿತೋರಿಸುತ್ತಾ ನೀರುಕ್ಕುವ ಒರತೆಗಳ ಬಳಿಯಲ್ಲಿ ನಡಿಸುವನು.",
			"11": "<sup>11</sup> ನನ್ನ ಸೃಷ್ಟಿಯ ಬೆಟ್ಟಗಳನ್ನೆಲ್ಲಾ ಸಮದಾರಿಯನ್ನಾಗಿ ಮಾಡಿ ನನ್ನ ರಾಜಮಾರ್ಗಗಳನ್ನು ಎತ್ತರಿಸುವೆನು.",
			"12": "<sup>12</sup> ಇಗೋ, ಇವರು ದೂರದಿಂದ ಬರುತ್ತಾರೆ, ಇಗೋ, ಇವರು ಬಡಗಲಿಂದ ಮತ್ತು ಪಡುವಲಿಂದ, ಇವರು ಸೀನೀಮ್ ದೇಶದಿಂದ ಬರುತ್ತಿದ್ದಾರೆ.",
			"13": "<sup>13</sup> ಆಕಾಶವೇ, ಹರ್ಷಧ್ವನಿಗೈ! ಭೂಮಿಯೇ, ಉಲ್ಲಾಸಗೊಳ್ಳು! ಪರ್ವತಗಳೇ, ಕೋಲಾಹಲಮಾಡಿರಿ! ಯೆಹೋವನು ತನ್ನ ಪ್ರಜೆಯನ್ನು ಸಂತೈಸಿ ದಿಕ್ಕಿಲ್ಲದ ತನ್ನ ಜನರನ್ನು ಕರುಣಿಸುತ್ತಿದ್ದಾನೆ.",
			"14": "<sup>14</sup> ಚೀಯೋನ್ ನಗರಿಯಾದರೋ - ಯೆಹೋವನು ನನ್ನನ್ನು ಕೈಬಿಟ್ಟಿದ್ದಾನೆ, ಕರ್ತನು ನನ್ನನ್ನು ಮರೆತಿದ್ದಾನಲ್ಲಾ ಅಂದುಕೊಂಡಳು.",
			"15": "<sup>15</sup> ಒಬ್ಬ ಹೆಂಗಸು ತಾನು ಹೆತ್ತ ಮಗುವಿನ ಮೇಲೆ ಕರುಣೆಯಿಡದೆ ತನ್ನ ಮೊಲೆಕೂಸನ್ನು ಮರೆತಾಳೇ? ಒಂದು ವೇಳೆ ಮರೆತಾಳು, ನಾನಾದರೆ ನಿನ್ನನ್ನು ಮರೆಯೆ.",
			"16": "<sup>16</sup> ಇಗೋ, ನನ್ನ ಅಂಗೈಗಳಲ್ಲಿ ನಿನ್ನನ್ನು ಚಿತ್ರಿಸಿದ್ದೇನೆ; ನಿನ್ನ ಪೌಳಿಗೋಡೆಗಳು ಸದಾ ನನ್ನ ಕಣ್ಣೆದುರಿನಲ್ಲಿವೆ.",
			"17": "<sup>17</sup> ನಿನ್ನ ಮಕ್ಕಳು ತ್ವರೆಪಟ್ಟು ಬರುತ್ತಿದ್ದಾರೆ; ನಿನ್ನನ್ನು ಕೆಡವಿ ಹಾಳುಮಾಡಿದವರು ನಿನ್ನೊಳಗಿಂದ ಹೊರಟು ಹೋಗುತ್ತಾರೆ.",
			"18": "<sup>18</sup> ಕಣ್ಣೆತ್ತಿ ಸುತ್ತಲು ನೋಡು! ಇವರೆಲ್ಲರೂ ಕೂಡಿಕೊಂಡು ನಿನ್ನ ಬಳಿಗೆ ಬರುತ್ತಿದ್ದಾರೆ. ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ನನ್ನ ಜೀವದಾಣೆ, ನೀನು ಇವರನ್ನೆಲ್ಲಾ ಆಭರಣವನ್ನಾಗಿ ಧರಿಸಿಕೊಳ್ಳುವಿ, ವಧುವು ಒಡ್ಯಾಣವನ್ನು ಕಟ್ಟಿಕೊಳ್ಳುವಂತೆ ನೀನು ಇವರನ್ನು ಕಟ್ಟಿಕೊಳ್ಳುವಿ.",
			"19": "<sup>19</sup> ನಿನ್ನ ಹಾಳು ಪ್ರದೇಶಗಳು, ನಿನ್ನ ಬೀಳು ಭೂಮಿಯು, ಕೆಟ್ಟುಹೋದ ನಿನ್ನ ಸೀಮೆಯು ಇವುಗಳಿಗೆ ಏನಾಗುವದೆಂದರೆ - ನೋಡು, ನಿನ್ನನ್ನು ನುಂಗಿದವರು ದೂರವಾಗುವರು, ನಿನ್ನ ನಿವಾಸಿಗಳಿಗೆ ನೀನು ಸಂಕೋಚಸ್ಥಳವಾಗುವಿ,",
			"20": "<sup>20</sup> ಸಂತಾನವನ್ನು ಕಳೆದುಕೊಂಡವಳಾದ ನಿನ್ನ ಮಕ್ಕಳು - ಸ್ಥಳವು ನನಗೆ ಸಂಕೋಚ, ನಾನು ವಾಸಿಸುವದಕ್ಕೆ ಸ್ಥಳವಾಗುವಂತೆ ಸರಿದುಕೋ ಎಂದು ಆಡಿಕೊಳ್ಳುವ ಮಾತು ಇನ್ನು ಮೇಲೆ ನಿನ್ನ ಕಿವಿಗೆ ಬೀಳುವದು.",
			"21": "<sup>21</sup> ಆಗ ನೀನು ನಿನ್ನ ಮನದೊಳಗೆ - ನನಗೋಸ್ಕರ ಇವರನ್ನು ಯಾರು ಹೆತ್ತರು? ನಾನೋ ಮಕ್ಕಳನ್ನು ಕಳೆದುಕೊಂಡವಳು, ಪುತ್ರಹೀನಳು, ದೇಶಭ್ರಷ್ಟಳು, ತಿರುಕಳು; ಇವರನ್ನು ಸಾಕಿದವರು ಯಾರು? ಆಹಾ, ನಾನು ಒಂಟಿಯಾಗಿ ಉಳಿದಿದ್ದೆನಲ್ಲಾ; ಇವರೆಲ್ಲಿದ್ದರು? ಅಂದುಕೊಳ್ಳುವಿ.",
			"22": "<sup>22</sup> ಕರ್ತನಾದ ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ಇಗೋ, ನಾನು ಜನಾಂಗಗಳಿಗೆ ಕೈಸನ್ನೆಮಾಡಿ ದೇಶಾಂತರಗಳವರಿಗೆ ನನ್ನ ಧ್ವಜವನ್ನು ಎತ್ತುವೆನು; ಅವರು ನಿನ್ನ ಕುಮಾರರನ್ನು ಎದೆಗಪ್ಪಿಕೊಂಡು ಬರುವರು, ನಿನ್ನ ಕುಮಾರ್ತೆಯರನ್ನು ಹೆಗಲ ಮೇಲೆ ಕರತರುವರು.",
			"23": "<sup>23</sup> ರಾಜರು ನಿನಗೆ ಸಾಕುತಂದೆಗಳು, ಅವರ ರಾಣಿಗಳು ನಿನಗೆ ಸಾಕುತಾಯಿಯರು ಆಗುವರು; ನಿನಗೆ ಸಾಷ್ಟಾಂಗವಾಗಿ ಅಡ್ಡಬಿದ್ದು ನಿನ್ನ ಪಾದಧೂಳಿಯನ್ನು ನೆಕ್ಕುವರು; ನಾನೇ ಯೆಹೋವನು. ನನ್ನನ್ನು ನಿರೀಕ್ಷಿಸಿಕೊಂಡವರು ಆಶಾಭಂಗಪಡರು ಎಂದು ನಿನಗೆ ಗೊತ್ತಾಗುವದು.",
			"24": "<sup>24</sup> ಶೂರನಿಂದ ಕೊಳ್ಳೆಯನ್ನು ಕಸುಕೊಳ್ಳಬಹುದೇ? ಭೀಕರನಿಗೆ ಸೆರೆಯಾದವರನ್ನು ಬಿಡಿಸಲಾದೀತೇ [ಎಂದುಕೊಳ್ಳುತ್ತೀಯಾ]?",
			"25": "<sup>25</sup> ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ಶೂರನ ಸೆರೆಯವರೂ ಅಪಹರಿಸಲ್ಪಡುವರು, ಭೀಕರನ ಕೊಳ್ಳೆಯೂ ತೆಗೆಯಲ್ಪಡುವದು; ನಿನ್ನೊಡನೆ ಹೋರಾಡುವವನ ಸಂಗಡ ನಾನೇ ಹೋರಾಡಿ ನಿನ್ನ ಮಕ್ಕಳನ್ನು ಉದ್ಧರಿಸುವೆನು.",
			"26": "<sup>26</sup> ನಿನ್ನ ಹಿಂಸಕರು ಸ್ವಮಾಂಸವನ್ನು ತಿನ್ನುವಂತೆ ಮಾಡುವೆನು; ದ್ರಾಕ್ಷಾರಸವನ್ನು ಕುಡಿಯುವ ಹಾಗೆ ಅವರು ಸ್ವಂತ ರಕ್ತವನ್ನು ಕುಡಿದು ಅಮಲೇರುವರು; ಆಗ ಯೆಹೋವನಾದ ನಾನೇ ನಿನ್ನ ರಕ್ಷಕನು, ನಿನ್ನ ವಿಮೋಚಕನು, ಯಾಕೋಬ್ಯರ ಶೂರನು ಎಂದು ನರಜನ್ಮದವರೆಲ್ಲರಿಗೂ ಗೊತ್ತಾಗುವದು."
		},
		"50": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ನಾನು ನಿಮ್ಮ ತಾಯಿಯನ್ನು ತ್ಯಜಿಸಿದ ತ್ಯಾಗಪತ್ರವು ಎಲ್ಲಿ? ನನ್ನ ಸಾಲಗಾರರಲ್ಲಿ ಯಾರಿಗೆ ನಿಮ್ಮನ್ನು ಮಾರಿಬಿಟ್ಟಿದ್ದೇನೆ ನೋಡಿರಿ, ನಿಮ್ಮ ದೋಷಗಳ ನಿಮಿತ್ತ ನಿಮ್ಮನ್ನು ಮಾರಿದೆನು, ನಿಮ್ಮ ದ್ರೋಹಗಳಿಗಾಗಿ ನಿಮ್ಮ ತಾಯಿಯನ್ನು ಬಿಟ್ಟೆನು.",
			"2": "<sup>2</sup> ನಾನು ಬಂದಾಗ ಏಕೆ ಯಾರೂ ಕಾದಿರಲಿಲ್ಲ, ನಾನು ಕರೆದಾಗ ಏಕೆ ಯಾರೂ ಉತ್ತರಕೊಡಲಿಲ್ಲ? ನನ್ನ ಹಸ್ತವು ರಕ್ಷಿಸಲಾರದ ಮೋಟುಗೈಯೋ? ಉದ್ಧರಿಸುವ ಶಕ್ತಿಯು ನನ್ನಲ್ಲಿಲ್ಲವೋ? ಇಗೋ, ನನ್ನ ತರ್ಜನದಿಂದಲೇ ಸಮುದ್ರವನ್ನು ಬತ್ತಿಸಿ ನದಿಗಳನ್ನು ಒಣಕಾಡನ್ನಾಗಿ ಮಾಡುತ್ತೇನೆ; ನೀರಿಲ್ಲದೆ ಅಲ್ಲಿನ ಮೀನುಗಳು ಬಾಯಾರಿ ಸತ್ತು ನಾರುವವು.",
			"3": "<sup>3</sup> ಆಕಾಶಕ್ಕೆ ಅಂಧಕಾರವನ್ನು ಹೊದಿಸಿ ಗೋಣಿತಟ್ಟಿನ ಮುಸುಕನ್ನು ಹಾಕುತ್ತೇನೆ.",
			"4": "<sup>4</sup> ಬಳಲಿಹೋದವರನ್ನು ಮಾತುಗಳಿಂದ ಸುಧಾರಿಸುವದಕ್ಕೆ ನಾನು ಬಲ್ಲವನಾಗುವಂತೆ ಕರ್ತನಾದ ಯೆಹೋವನು ಶಿಕ್ಷಿತರ ನಾಲಿಗೆಯನ್ನು ನನಗೆ ದಯಪಾಲಿಸಿದ್ದಾನೆ; ಬೆಳಬೆಳಗೂ ನನ್ನನ್ನು ಎಚ್ಚರಿಸಿ ಶಿಕ್ಷಿತರಂತೆ ನಾನು ಕೇಳುವ ಹಾಗೆ ನನ್ನ ಕಿವಿಯನ್ನು ಜಾಗರಗೊಳಿಸುತ್ತಾನೆ.",
			"5": "<sup>5</sup> ಕರ್ತನಾದ ಯೆಹೋವನು ನನ್ನ ಕಿವಿಯನ್ನು ತೆರೆದಿದ್ದಾನೆ; ನಾನು ಎದುರು ಬೀಳಲಿಲ್ಲ, ವಿಮುಖನಾಗಲೂ ಇಲ್ಲ.",
			"6": "<sup>6</sup> ಹೊಡೆಯುವವರಿಗೆ ಬೆನ್ನುಕೊಟ್ಟು ಕೂದಲುಕೀಳುವವರಿಗೆ ಗಡ್ಡವನ್ನು ಒಡ್ಡಿದೆನು; ಉಗುಳಿಸಿಕೊಳ್ಳುವ ಅವಮಾನಕ್ಕೆ ನನ್ನ ಮುಖವನ್ನು ಮರೆಮಾಡಲಿಲ್ಲ.",
			"7": "<sup>7</sup> ಕರ್ತನಾದ ಯೆಹೋವನು ನನಗೆ ಸಹಾಯಮಾಡುವನು; ಆದಕಾರಣ ನಾನು ನಾಚಿಕೆಯಿಂದ ಕೊರಗಲಿಲ್ಲ; ನನ್ನ ಮುಖವನ್ನು ಕಗ್ಗಲ್ಲಿನಂತೆ ಗಟ್ಟಿಮಾಡಿಕೊಂಡಿದ್ದೇನೆ; ಆಶಾಭಂಗಪಡಲಾರೆನೆಂದು ನನಗೆ ಗೊತ್ತು.",
			"8": "<sup>8</sup> ನನ್ನ ನ್ಯಾಯಸ್ಥಾಪಕನು ಸಮೀಪದಲ್ಲಿದ್ದಾನೆ; ನನ್ನೊಡನೆ ಯಾರು ವ್ಯಾಜ್ಯವಾಡುವರು? ನಾವಿಬ್ಬರು [ನ್ಯಾಯಾಸನದ ಮುಂದೆ] ನಿಂತುಕೊಳ್ಳುವ; ನನಗೆ ಪ್ರತಿಕಕ್ಷಿಯು ಯಾರು? ನನ್ನ ಬಳಿಗೆ ಬರಲಿ.",
			"9": "<sup>9</sup> ಆಹಾ, ಕರ್ತನಾದ ಯೆಹೋವನು ನನಗೆ ಸಹಾಯಮಾಡುವನು; ನನ್ನನ್ನು ಅಪರಾಧಿ ಎಂದು ನಿರ್ಣಯಿಸುವವನು ಯಾರು? ಇಗೋ, ಅವರೆಲ್ಲರೂ ವಸ್ತ್ರದಂತೆ ಜೀರ್ಣವಾಗುವರು; ಅವರನ್ನು ನುಸಿಯು ತಿಂದುಬಿಡುವದು.",
			"10": "<sup>10</sup> ನಿಮ್ಮೊಳಗೆ ಯಾವನು ಯೆಹೋವನಲ್ಲಿ ಭಯಭಕ್ತಿಯಿಟ್ಟು ಆತನ ಸೇವಕನ ಮಾತನ್ನು ಕೇಳುವನು? ಕತ್ತಲಲ್ಲಿ ನಡೆಯುತ್ತಾ ಬೆಳಕಿಲ್ಲದವನು ಯೆಹೋವನ ನಾಮದಲ್ಲಿ ಭರವಸವಿಟ್ಟು ತನ್ನ ದೇವರನ್ನು ಆಧಾರಮಾಡಿಕೊಳ್ಳಲಿ.",
			"11": "<sup>11</sup> ಓಹೋ, ಬೆಂಕಿಯಿಕ್ಕಿ ಸುತ್ತುಮುತ್ತಲು ಕೊಳ್ಳಿಗಳನ್ನು ಹತ್ತಿಸಿಕೊಂಡಿರುವವರೇ, ನಿಮ್ಮ ಬೆಂಕಿಯ ಬೆಳಕಿನಲ್ಲಿಯೂ ನೀವು ಹತ್ತಿಸಿದ ಕೊಳ್ಳಿಗಳ ನಡುವೆಯೂ ನಡೆದರೂ ನಡೆಯಿರಿ. ಇಗೋ, ನನ್ನ ಹಸ್ತದಿಂದ ನಿಮಗಾಗುವ ಗತಿಯು ಇದೇ, ದುಃಖಕ್ಕೆ ಒಳಗಾಗಿ ಸಾಯುವಿರಿ."
		},
		"51": {
			"1": "<sup>1</sup> ಸದ್ಧರ್ಮನಿರತರಾದ ಯೆಹೋವನ ಭಕ್ತರೇ, ನನ್ನ ಮಾತನ್ನು ಕೇಳಿರಿ, ನೀವು ಯಾವ ಬಂಡೆಯೊಳಗಿಂದ ಒಡೆದು ತೆಗೆಯಲ್ಪಟ್ಟಿರಿ, ಯಾವ ಗುಂಡಿಯಿಂದ ತೋಡಲ್ಪಟ್ಟಿರಿ ಎಂಬದನ್ನು ನೋಡಿರಿ.",
			"2": "<sup>2</sup> ನಿಮ್ಮ ಪಿತೃವಾದ ಅಬ್ರಹಾಮನನ್ನೂ ನಿಮ್ಮನ್ನು ಹೆತ್ತ ಸಾರಳನ್ನೂ ದೃಷ್ಟಿಸಿರಿ; ಅಬ್ರಹಾಮನು ಒಬ್ಬನೇ ಇದ್ದಾಗ ನಾನು ಅವನನ್ನು ಕರೆದು ಆಶೀರ್ವದಿಸಿ ಸಂತಾನದಿಂದ ಹೆಚ್ಚಿಸಿದೆನಲ್ಲವೆ.",
			"3": "<sup>3</sup> ಯೆಹೋವನು ಚೀಯೋನನ್ನು ಸಂತೈಸೇ ಸಂತೈಸುವನು; ಅಲ್ಲಿನ ಹಾಳು ಪ್ರದೇಶಗಳನ್ನೆಲ್ಲಾ ಸುಧಾರಿಸಿ ಕಾಡುನೆಲವನ್ನು ಏದೆನ್ ಉದ್ಯಾನದಂತೆಯೂ ಬೀಳುಭೂಮಿಯನ್ನು ಯೆಹೋವನ ವನದ ಹಾಗೂ ಕಳಕಳಿಸುವಂತೆ ಮಾಡುವನು; ಹರ್ಷ, ಉಲ್ಲಾಸ, ಸ್ತೋತ್ರ, ಗಾನಧ್ವನಿ, ಇವುಗಳು ಅಲ್ಲಿ ನೆಲೆಯಾಗಿರುವವು.",
			"4": "<sup>4</sup> ನನ್ನ ಜನರೇ, ಆಲಿಸಿರಿ! ನನ್ನ ಪ್ರಜೆಗಳೇ, ಕಿವಿಗೊಡಿರಿ! ಧರ್ಮೋಪದೇಶವು ನನ್ನಿಂದ ಹೊರಡುವದು, ನನ್ನ ನ್ಯಾಯಬೋಧನೆಯನ್ನು ಜನಾಂಗಗಳಿಗೆ ಬೆಳಕನ್ನಾಗಿ ಸ್ಥಾಪಿಸುವೆನು.",
			"5": "<sup>5</sup> ನನ್ನ ರಕ್ಷಣಧರ್ಮದ ಕಾರ್ಯವು ಸಮೀಪಿಸಿದೆ, ನನ್ನ ವಿಮೋಚನಕ್ರಿಯೆಯು ತಲೆದೋರಿದೆ, ನನ್ನ ಹಸ್ತವು ಜನಾಂಗಗಳಿಗೆ ನ್ಯಾಯತೀರಿಸುವದು; ದ್ವೀಪನಿವಾಸಿಗಳು ನನ್ನನ್ನು ನಿರೀಕ್ಷಿಸಿಕೊಂಡು ನನ್ನ ಹಸ್ತಕಾರ್ಯಕ್ಕೆ ಕಾದಿರುವರು.",
			"6": "<sup>6</sup> ನಿಮ್ಮ ಕಣ್ಣುಗಳನ್ನು ಮೇಲಕ್ಕೆತ್ತಿ ಆಕಾಶಮಂಡಲವನ್ನು ನೋಡಿರಿ, ಕೆಳಗೆ ಭೂಮಂಡಲವನ್ನು ದೃಷ್ಟಿಸಿರಿ; ಆಕಾಶವು ಹೊಗೆಯಂತೆ ಚದರಿ ಹೋಗುವದು, ಭೂಮಿಯು ವಸ್ತ್ರದ ಹಾಗೆ ಜೀರ್ಣವಾಗುವದು, ಭೂನಿವಾಸಿಗಳು ಸೊಳ್ಳೆಗಳೋಪಾದಿಯಲ್ಲಿ ಸಾಯುವರು; ನನ್ನ ವಿಮೋಚನಕ್ರಿಯೆಯೋ ಶಾಶ್ವತವಾಗಿ ನಿಲ್ಲುವದು, ನನ್ನ ರಕ್ಷಣಧರ್ಮದ ಕಾರ್ಯಕ್ಕೆ ಭಂಗವೇ ಇಲ್ಲ.",
			"7": "<sup>7</sup> ಧರ್ಮವನ್ನರಿತು ನನ್ನ ಉಪದೇಶವನ್ನು ಹೃದಯದಲ್ಲಿಟ್ಟುಕೊಂಡಿರುವ ಜನರೇ ಕಿವಿಗೊಡಿರಿ, ಮನುಷ್ಯರ ದೂರಿಗೆ ಹೆದರಬೇಡಿರಿ, ಅವರ ದೂಷಣೆಗೆ ಅಂಜದಿರಿ. ನುಸಿಯು ಅವರನ್ನು ಬಟ್ಟೆಯಂತೆ ತಿಂದುಹಾಕುವದು, ಹುಳವು ಅವರನ್ನು ಉಣ್ಣೆಯಂತೆ ಮೆದ್ದುಬಿಡುವದು;",
			"8": "<sup>8</sup> ನನ್ನ ರಕ್ಷಣ ಧರ್ಮದ ಕಾರ್ಯವೋ ಶಾಶ್ವತವಾಗಿ ನಿಲ್ಲುವದು, ನನ್ನ ವಿಮೋಚನ ಕ್ರಿಯೆಯು ತಲತಲಾಂತರಕ್ಕೂ ನೆಲೆಯಾಗಿರುವದು.",
			"9": "<sup>9</sup> ಯೆಹೋವನ ಭುಜವೇ, ಎಚ್ಚರಗೊಳ್ಳು, ಎಚ್ಚರಗೊಳ್ಳು, ಬಲವನ್ನು ತಂದುಕೋ! ಪೂರ್ವಕಾಲದಲ್ಲಿ, ಪುರಾತನದ ತಲಾಂತರಗಳಲ್ಲಿ, ಎಚ್ಚರಪಟ್ಟಂತೆ ಈಗಲೂ ಎಚ್ಚತ್ತುಕೋ, ರಹಬನ್ನು ಛೇದಿಸಿ ಘಟಸರ್ಪವನ್ನು ತಿವಿದುಬಿಟ್ಟ ತೋಳು ನೀನಲ್ಲವೆ;",
			"10": "<sup>10</sup> ಸಮುದ್ರವನ್ನೂ ಮಹಾಸಾಗರದ ಜಲರಾಶಿಯನ್ನೂ ಬತ್ತಿಸಿ ವಿಮುಕ್ತಜನರು ಹಾದುಹೋಗುವದಕ್ಕೆ ಸಮುದ್ರದ ತಳವನ್ನು ಮಾರ್ಗವನ್ನಾಗಿ ಮಾಡಿದ ಬಾಹು ನೀನೇ ಹೌದು.",
			"11": "<sup>11</sup> ಯೆಹೋವನು ವಿಮೋಚಿಸಿದವರು ಹಿಂದಿರುಗಿ ಶಾಶ್ವತ ಸಂತೋಷವೆಂಬ ಕಿರೀಟವನ್ನು ಧರಿಸಿಕೊಂಡು ಉತ್ಸಾಹಧ್ವನಿಯೊಡನೆ ಚೀಯೋನಿಗೆ ಸೇರುವರು; ಹರ್ಷಾನಂದಗಳನ್ನು ಅನುಭವಿಸುವರು, ಮೊರೆಯೂ ಕರಕರೆಯೂ ತೊಲಗಿಹೋಗುವವು.",
			"12": "<sup>12</sup> ನಾನೇ, ನಾನೇ ನಿನ್ನನ್ನು ಸಂತೈಸುವವನಾಗಿರುವಲ್ಲಿ ಮರ್ತ್ಯಮನುಷ್ಯನಿಗೆ, ಹುಲ್ಲಿನ ಗತಿಗೆ ಬರುವ ನರಜನ್ಮದವನಿಗೆ ಭಯಪಡುವ ನೀನು ಎಂಥವನು?",
			"13": "<sup>13</sup> ಆಕಾಶಮಂಡಲವನ್ನು ಹರಡಿ ಭೂಲೋಕವನ್ನು ಸ್ಥಾಪಿಸಿದ ನಿನ್ನ ಸೃಷ್ಟಿಕರ್ತನಾದ ಯೆಹೋವನನ್ನು ಮರೆತುಬಿಟ್ಟಿಯಾ? ನಾಶಮಾಡಬೇಕೆಂದು [ಬಾಣವನ್ನು] ಹೂಡುವ ಹಿಂಸಕನ ಕ್ರೋಧಕ್ಕೆ ದಿನವೆಲ್ಲಾ ಎಡೆಬಿಡದೆ ಅಂಜುತ್ತೀಯಾ? ಆ ಹಿಂಸಕನ ಕ್ರೋಧವು ಎಲ್ಲಿ ತಾನೇ ಇದೆ?",
			"14": "<sup>14</sup> [ಸೆರೆಯಲ್ಲಿ] ಕುಗ್ಗಿರುವವನು ಸಾಯನು, ಪಾತಾಳಕ್ಕೆ ಇಳಿಯನು, ಬೇಗನೆ ಬಿಡುಗಡೆಯಾಗುವನು; ಅವನಿಗೆ ಅನ್ನದ ಕೊರತೆಯೇ ಇರದು.",
			"15": "<sup>15</sup> ಅಲೆಗಳು ಭೋರ್ಗರೆಯುವಷ್ಟು ಸಮುದ್ರವನ್ನು ರೇಗಿಸುವ ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನು ನಾನೇ; ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೆಂಬದೇ ನನ್ನ ನಾಮಧೇಯ.",
			"16": "<sup>16</sup> ಆಕಾಶವನ್ನು ನಿಲ್ಲಿಸಬೇಕೆಂತಲೂ ಭೂಲೋಕವನ್ನು ಸ್ಥಾಪಿಸಬೇಕೆಂತಲೂ ಚೀಯೋನಿಗೆ ನೀನು ನನ್ನ ಜನವೆಂದು ಹೇಳಬೇಕೆಂತಲೂ ನಿನ್ನ ಬಾಯಲ್ಲಿ ನನ್ನ ಮಾತುಗಳನ್ನು ಇಟ್ಟು ನನ್ನ ಕೈಯ ನೆರಳಿನಲ್ಲಿ ನಿನ್ನನ್ನು ಹುದುಗಿಸಿದ್ದೇನೆ.",
			"17": "<sup>17</sup> ಯೆಹೋವನು ತನ್ನ ರೋಷವನ್ನು ತುಂಬಿಕೊಟ್ಟ ಪಾತ್ರೆಯಿಂದ ಕುಡಿದ ಯೆರೂಸಲೇಮೇ, ಎಚ್ಚತ್ತುಕೋ, ಎಚ್ಚತ್ತುಕೋ, ಎದ್ದು ನಿಲ್ಲು! ಭ್ರಮಣಗೊಳಿಸುವ ಆ ಪಾನದ ಪಾತ್ರೆಯಲ್ಲಿ ಒಂದು ತೊಟ್ಟನ್ನೂ ಉಳಿಸದೆ ಕುಡಿದು ಬಿಟ್ಟಿದ್ದೀ.",
			"18": "<sup>18</sup> ಹೆತ್ತ ಮಕ್ಕಳಲ್ಲೆಲ್ಲಾ ಅವಳನ್ನು ಕರೆದುಕೊಂಡು ಹೋಗುವವನು ಯಾರೂ ಇಲ್ಲ; ಸಾಕಿದ ಆ ಸಕಲ ಕುಮಾರರಲ್ಲಿ ಅವಳ ಕೈ ಹಿಡಿಯುವವನು ಒಬ್ಬನೂ ಇಲ್ಲ.",
			"19": "<sup>19</sup> ಸಹತಾಪದಿಂದ ನಿನಗಾಗಿ ಬಡುಕೊಳ್ಳುವವರು ಯಾರಿದ್ದಾರೆ? ನಿನಗೆ ಈ ಎರಡು ಬಾಧೆಗಳು ಬಂದಿವೆ, [ನಿನ್ನ ದೇಶವು] ಹಾಳುಪಾಳಾಗಿದೆ, [ನಿನ್ನ ಪ್ರಜೆಗೆ] ಕ್ಷಾಮವೂ ಖಡ್ಗವೂ ಪ್ರಾಪ್ತವಾಗಿವೆ; ನಾನು ನಿನ್ನನ್ನು ಹೇಗೆ ಸಂತೈಸಲಿ?",
			"20": "<sup>20</sup> ನಿನ್ನ ಮಕ್ಕಳು ಪ್ರಜ್ಞೆತಪ್ಪಿದವರಾಗಿ ಬಲೆಗೆ ಸಿಕ್ಕಿದ ಜಿಂಕೆಯಂತೆ ಬೀದಿಗಳೆಲ್ಲಾ ಕೂಡುವ ಚೌಕದಲ್ಲಿ ಬಿದ್ದಿದ್ದಾರೆ; ಯೆಹೋವನ ರೋಷವನ್ನೂ ನಿನ್ನ ದೇವರ ತರ್ಜನವನ್ನೂ ಹೊಟ್ಟೆ ತುಂಬಾ ಕುಡಿದಿದ್ದಾರೆ.",
			"21": "<sup>21</sup> ಹೀಗಿರಲು, ಗತಿಹೀನಳೇ, ದ್ರಾಕ್ಷಾರಸವಿಲ್ಲದೆ ಅಮಲೇರಿದವಳೇ, ಇದನ್ನು ಕೇಳು;",
			"22": "<sup>22</sup> ನಿನ್ನ ಕರ್ತನೂ ತನ್ನ ಜನರ ಪಕ್ಷವಾಗಿ ವ್ಯಾಜ್ಯವಾಡುವ ನಿನ್ನ ದೇವರೂ ಆದ ಯೇಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ಇಗೋ, ಭ್ರಮಣಗೊಳಿಸುವ ಪಾನದ ಪಾತ್ರೆಯನ್ನು ಅಂದರೆ ನನ್ನ ರೋಷವು ತುಂಬಿರುವ ಬಟ್ಟಲನ್ನು ನಿನ್ನ ಕೈಯೊಳಗಿಂದ ತೆಗೆದುಹಾಕುವೆನು; ಅದನ್ನು ನೀನು ಇನ್ನು ಕುಡಿಯೆ;",
			"23": "<sup>23</sup> ನಿನ್ನನ್ನು ಬಾಧಿಸುವವರ ಕೈಗೆ ಅದನ್ನು ಕೊಡುವೆನು; ಅವರು ನಿನಗೆ - ನೀನು ಬಿದ್ದುಕೋ, ನಿನ್ನನ್ನು ತುಳಿಯುತ್ತಾ ಹೋಗುವೆವು ಎಂದು ಹೇಳಲು ನೀನು ಹಾದುಹೋಗುವ ಅವರಿಗೆ ನಿನ್ನ ಬೆನ್ನನ್ನು ನೆಲವನ್ನಾಗಿಯೂ ಬೀದಿಯ ಮಣ್ಣನ್ನಾಗಿಯೂ ಮಾಡಿಕೊಂಡಿಯಲ್ಲಾ."
		},
		"52": {
			"1": "<sup>1</sup> ಚೀಯೋನೇ, ಎಚ್ಚರಗೊಳ್ಳು, ಎಚ್ಚರಗೊಳ್ಳು, ನಿನ್ನ ಪ್ರತಾಪವನ್ನು ಧರಿಸಿಕೋ! ಯೆರೂಸಲೇಮೇ, ಪರಿಶುದ್ಧ ಪಟ್ಟಣವೇ, ನಿನ್ನ ಚಂದದ ಉಡುಪನ್ನು ಹಾಕಿಕೋ! ಇಂದಿನಿಂದ ಅಶುದ್ಧರೂ ಸುನ್ನತಿಯಿಲ್ಲದವರೂ ನಿನ್ನೊಳಗೆ ಪ್ರವೇಶಮಾಡರು.",
			"2": "<sup>2</sup> ಯೆರೂಸಲೇಮೇ, ದೂಳನ್ನು ಝಾಡಿಸಿಕೋ! ಎದ್ದು ಆಸನದ ಮೇಲೆ ಕೂಡ್ರು! ಸೆರೆಬಿದ್ದ ಚೀಯೋನ್ ಕನ್ಯೆಯೇ, ನಿನ್ನ ಕತ್ತಿನ ಪಾಶವನ್ನು ಬಿಚ್ಚಿಬಿಡು!",
			"3": "<sup>3</sup> ಯೆಹೋವನ ಈ ಮಾತನ್ನು ಕೇಳಿರಿ, ಲಾಭಪಡೆಯದೆ ನಿಮ್ಮನ್ನು ಮಾರಿದೆನು, ಹಣತಕ್ಕೊಳ್ಳದೆ ನಿಮ್ಮನ್ನು ಬಿಡುಗಡೆ ಮಾಡುವೆನು.",
			"4": "<sup>4</sup> ಕರ್ತನಾದ ಯೆಹೋವನು ಹೀಗಂದುಕೊಳ್ಳುತ್ತಾನೆ - ನನ್ನ ಜನರು ಮೊಟ್ಟಮೊದಲು ಐಗುಪ್ತದಲ್ಲಿ ಇಳಿದುಕೊಳ್ಳಬೇಕೆಂದು ಹೋಗಿ [ಅಲ್ಲಿ ಸೆರೆಯಾದರು]; ಅಶ್ಶೂರ್ಯರು ಹಕ್ಕಿಲ್ಲದೆ ಅವರನ್ನು ಬಾಧೆಪಡಿಸಿದರು.",
			"5": "<sup>5</sup> ಈಗಲೂ ಅನ್ಯರು ನನ್ನ ಜನರನ್ನು ಹಕ್ಕಿಲ್ಲದೆ ಒಯ್ದಿರಲು ನಾನು ಇಲ್ಲಿ ಸುಮ್ಮನಿರುವದು ಹೇಗೆ? ನನ್ನ ಪ್ರಜೆಯನ್ನು ಆಳುವವರು [ರೌದ್ರದಿಂದ] ಕಿರಚುತ್ತಾರೆ; ನನ್ನ ನಾಮವು ದಿನವೆಲ್ಲಾ ಎಡೆಬಿಡದೆ ದೂಷಣೆಗೆ ಗುರಿಯಾಗಿದೆ.",
			"6": "<sup>6</sup> ಹೀಗಿರಲು ನನ್ನ ಜನರು ನನ್ನ ನಾಮದ ಮಹತ್ತನ್ನು ತಿಳಿದು [ತಮ್ಮ ಸಂಗಡ] ಮಾತಾಡುವವನು ನಾನೇ, ಹೌದು ನಾನೇ, ಎಂದು [ರಕ್ಷಣೆಯ] ಆ ದಿನದಲ್ಲಿ ಗ್ರಹಿಸಿಕೊಳ್ಳುವ ಹಾಗೆ ಮಾಡುವೆನು. ಇದೇ ಯೆಹೋವನ ನುಡಿ.",
			"7": "<sup>7</sup> ಪರ್ವತಗಳ ಮೇಲೆ ತ್ವರೆಪಡುತ್ತಾ ಶುಭಸಮಾಚಾರವನ್ನು ತಂದು ಸಮಾಧಾನವನ್ನು ಸಾರುವ ದೂತನ ಪಾದಗಳು ಎಷ್ಟೋ ಅಂದವಾಗಿವೆ! ಒಳ್ಳೆಯ ಶುಭವರ್ತಮಾನವನ್ನು, ಶುಭದ ಸುವಾರ್ತೆಯನ್ನು ತಂದು ರಕ್ಷಣೆಯನ್ನು ಪ್ರಕಟಿಸುತ್ತಾ - ನಿನ್ನ ದೇವರು ರಾಜ್ಯಭಾರವನ್ನು ವಹಿಸಿದ್ದಾನೆ ಎಂದು ಚೀಯೋನಿಗೆ ತಿಳಿಸುವವನಾಗಿದ್ದಾನೆ.",
			"8": "<sup>8</sup> ಇಗೋ, ನಿನ್ನ ಕಾವಲುಗಾರರ ಕೂಗು! ಸ್ವರವೆತ್ತಿ ಒಟ್ಟಿಗೆ ಹರ್ಷಧ್ವನಿಗೈಯುತ್ತಾರೆ; ಯೆಹೋವನು ಚೀಯೋನಿಗೆ ತಿರುಗಿ ಬರುವದನ್ನು ಪ್ರತ್ಯಕ್ಷವಾಗಿ ನೋಡುತ್ತಿದ್ದಾರೆ.",
			"9": "<sup>9</sup> ಯೆರೂಸಲೇಮಿನ ಹಾಳುಪ್ರದೇಶಗಳೇ, ತಟ್ಟನೆ ಜಯಘೋಷಮಾಡಿ ಒಟ್ಟಿಗೆ ಹರ್ಷಧ್ವನಿಗೈಯಿರಿ! ಯೆಹೋವನು ಯೆರೂಸಲೇಮನ್ನು ವಿಮೋಚಿಸಿ ತನ್ನ ಜನರನ್ನು ಸಂತೈಸಿದ್ದಾನೆ.",
			"10": "<sup>10</sup> ಯೆಹೋವನು ಸಕಲ ಜನಾಂಗಗಳ ಕಣ್ಣೆದುರಿನಲ್ಲಿ ತನ್ನ ದಿವ್ಯಬಾಹುವನ್ನು ತೆರೆದು ತೋರಿಸಿದ್ದಾನೆ; ಭೂಮಿಯ ಎಲ್ಲಾ ದಿಕ್ಕಿನವರೂ ನಮ್ಮ ದೇವರ ರಕ್ಷಣಕಾರ್ಯವನ್ನು ನೋಡುವರು.",
			"11": "<sup>11</sup> ತೊಲಗಿರಿ, ತೊಲಗಿರಿ, ಬಾಬೆಲಿನಿಂದ ಹೊರಡಿರಿ, ಅಶುದ್ಧವಾದ ಯಾವದನ್ನೂ ಮುಟ್ಟದಿರಿ, ಅದರ ಮಧ್ಯದೊಳಗಿಂದ ತೆರಳಿರಿ, ಯೆಹೋವನ ಆರಾಧನೆಯ ಉಪಕರಣಗಳನ್ನು ಹೊರುವವರೇ, ಶುದ್ಧರಾಗಿರಿ!",
			"12": "<sup>12</sup> ನೀವು ಅವಸರದಿಂದ ಹೊರಡಬೇಕಾಗಿಲ್ಲ, ಓಡಿಹೋಗಬೇಕಾಗಿಲ್ಲ; ಯೆಹೋವನು ನಿಮಗೆ ಮುಂಬಲವಾಗಿ ಮುಂದರಿಯುವನು, ಇಸ್ರಾಯೇಲಿನ ದೇವರು ನಿಮಗೆ ಹಿಂಬಲವಾಗಿಯೂ ಇರುವನು.",
			"13": "<sup>13</sup> ಇಗೋ, ನನ್ನ ಸೇವಕನು ಕೃತಾರ್ಥನಾಗುವನು; ಅವನು ಉನ್ನತನಾಗಿ ಮೇಲಕ್ಕೇರಿ ಮಹೋನ್ನತ ಪದವಿಗೆ ಬರುವನು.",
			"14": "<sup>14</sup> ಅವನ ಮುಖವು ಸಕಲ ಮನುಷ್ಯರ ಮುಖಕ್ಕಿಂತಲೂ ಅವನ ರೂಪವು ಎಲ್ಲಾ ನರಜನ್ಮದವರ ರೂಪಕ್ಕಿಂತಲೂ ವಿಕಾರವಾಗಿರುವದನ್ನು ನೋಡಿ ಅನೇಕರು ಹೇಗೆ ಅವನಿಂದ ಬೆದರಿ ಬಿದ್ದರೋ",
			"15": "<sup>15</sup> ಹಾಗೆಯೇ ಅನೇಕ ಜನಾಂಗಗಳವರು ಅವನನ್ನು ಕಂಡು ವಿಸ್ಮಯದಿಂದ ಚಮಕಿತರಾಗುವರು; ಅರಸರೂ ಅವನ ಮುಂದೆ ಬಾಯಿಮುಚ್ಚಿಕೊಳ್ಳುವರು; ಏಕಂದರೆ ಸುದ್ದಿಯೇ ಇಲ್ಲದ ಸಂಗತಿಯನ್ನು ನೋಡುವರು, ಎಂದೂ ಕೇಳದ ವಿಷಯವನ್ನು ಗ್ರಹಿಸಿಕೊಳ್ಳುವರು."
		},
		"53": {
			"1": "<sup>1</sup> ನಾವು ಕೇಳಿದ ಸಂಗತಿಯನ್ನು [ನಮ್ಮಲ್ಲಿ] ಯಾರು ನಂಬಿದ್ದರು? ಯೆಹೋವನ ಬಾಹುವು ಯಾರಿಗೆ ಗೋಚರವಾಗಿತ್ತು?",
			"2": "<sup>2</sup> ಒಣನೆಲದೊಳಗೆ ಬೇರಿನಿಂದ ಹೊರಡುವ ಅಂಕುರದಂತೆಯೂ ಬುಡದಿಂದ ಒಡೆಯುವ ಚಿಗುರಿನ ಹಾಗೂ ಅವನು ಯೆಹೋವನ ದೃಷ್ಟಿಯಲ್ಲಿ ಬೆಳೆದನು. ಅವನಲ್ಲಿ ಯಾವ ಅಂದಚಂದಗಳೂ ಇರಲಿಲ್ಲ; ನಾವು ಅವನನ್ನು ನೋಡಿದಾಗ ನೋಡತಕ್ಕ ಯಾವ ಲಕ್ಷಣವೂ ಕಾಣಲಿಲ್ಲ.",
			"3": "<sup>3</sup> ಅವನು ಧಿಕ್ಕರಿಸಲ್ಪಟ್ಟವನು, ಮನುಷ್ಯರು ಸೇರಿಸಿಕೊಳ್ಳದವನು, ಸಂಕಷ್ಟಕ್ಕೊಳಗಾದವನು, ವ್ಯಾಧಿಪೀಡಿತನು, ಜನರು ಮುಖವನ್ನು ಓರೆಮಾಡಿಕೊಳ್ಳುವ ಧಿಕ್ಕಾರಕ್ಕೆ ಒಳಗಾದವನೂ ಆಗಿದ್ದನು. ನಾವು ಅವನನ್ನು ಲಕ್ಷ್ಯಕ್ಕೇ ತರಲಿಲ್ಲ.",
			"4": "<sup>4</sup> ನಿಜವಾಗಿಯೂ ನಮ್ಮ ಸಂಕಷ್ಟಗಳನ್ನು ಅನುಭವಿಸಿದನು; ಅವನು ಹೊತ್ತ ಹೊರೆಯು ನಮ್ಮ ಸಂಕಟವೇ, ಹೌದು; ನಾವಾದರೋ ಅವನು ದೇವರಿಂದ ಬಾಧಿತನು, ಪೆಟ್ಟು ತಿಂದವನು, ಕುಗ್ಗಿಸಲ್ಪಟ್ಟವನು ಎಂದು ಭಾವಿಸಿಕೊಂಡೆವು.",
			"5": "<sup>5</sup> ನಮ್ಮ ದ್ರೋಹಗಳ ದೆಸೆಯಿಂದ ಅವನಿಗೆ ಗಾಯವಾಯಿತು, ನಮ್ಮ ಅಪರಾಧಗಳ ನಿಮಿತ್ತ ಅವನು ಜಜ್ಜಲ್ಪಟ್ಟನು; ನಮಗೆ ಸುಕ್ಷೇಮವನ್ನುಂಟುಮಾಡುವ ದಂಡನೆಯನ್ನು ಅವನು ಅನುಭವಿಸಿದನು; ಅವನ ಬಾಸುಂಡೆಗಳಿಂದ ನಮಗೆ ಗುಣವಾಯಿತು.",
			"6": "<sup>6</sup> ನಾವೆಲ್ಲರೂ ಕುರಿಗಳಂತೆ ದಾರಿತಪ್ಪಿ ತೊಳಲುತ್ತಿದ್ದೆವು, ಪ್ರತಿಯೊಬ್ಬನೂ ತನ್ನ ತನ್ನ ದಾರಿಯನ್ನು ಹಿಡಿಯುತ್ತಿದ್ದನು; ನಮ್ಮೆಲ್ಲರ ದೋಷಫಲವನ್ನೂ ಯೆಹೋವನು ಅವನ ಮೇಲೆ ಹಾಕಿದನು.",
			"7": "<sup>7</sup> ಅವನು ಬಾಧೆಗೆ ಒಳಗಾಗಿ ತನ್ನನ್ನು ತಗ್ಗಿಸಿಕೊಂಡನು, ಬಾಯಿ ತೆರೆಯಲಿಲ್ಲ; ವಧ್ಯಸ್ಥಾನಕ್ಕೆ ಒಯ್ಯಲ್ಪಡುವ ಕುರಿಯಂತೆಯೂ ಉಣ್ಣೆ ಕತ್ತರಿಸುವವರ ಮುಂದೆ ಮೌನವಾಗಿರುವ ಕುರಿಯ ಹಾಗೂ ಇದ್ದನು, ಬಾಯಿ ತೆರೆಯಲೇ ಇಲ್ಲ.",
			"8": "<sup>8</sup> ಹಿಂಸೆಯ ನ್ಯಾಯದಿಂದ ಕೊಲ್ಲಲ್ಪಟ್ಟನು, ಆಹಾ, ಇವನು ಜೀವಲೋಕದಿಂದ ಕೀಳಲ್ಪಟ್ಟಿದ್ದಾನೆ, ನಮ್ಮ ಜನಗಳ ದ್ರೋಹಗಳ ದೆಸೆಯಿಂದ ಈ ಪೆಟ್ಟು ಅವನ ಮೇಲೆ ಬಿತ್ತಲ್ಲಾ ಎಂದು ಅವನ ಕಾಲದವರಲ್ಲಿ ಯಾರು ಮನಮುಟ್ಟಿ ಮರುಗಿದರು?",
			"9": "<sup>9</sup> ಅವನು ಯಾವ ಅನ್ಯಾಯವನ್ನು ಮಾಡದಿದ್ದರೂ ಅವನ ಬಾಯಲ್ಲಿ ಯಾವ ವಂಚನೆಯು ಇಲ್ಲದಿದ್ದರೂ ಅವನು ಸತ್ತಾಗ ದುಷ್ಟರ ಮಧ್ಯೆ ಪುಷ್ಟರ ನಡುವೆ ಅವನನ್ನು ಹೂಣಿಟ್ಟರು.",
			"10": "<sup>10</sup> ಅವನನ್ನು ಜಜ್ಜುವದು ಯೆಹೋವನ ಸಂಕಲ್ಪವಾಗಿತ್ತು. ಆತನು ಅವನನ್ನು ವ್ಯಾಧಿಯಿಂದ ಬಾಧಿಸಿ ಹೀಗೆಂದುಕೊಂಡನು; ಇವನು ತನ್ನ ಆತ್ಮವನ್ನು ಪ್ರಾಯಶ್ಚಿತ್ತಯಜ್ಞಕ್ಕಾಗಿ ಒಪ್ಪಿಸಿದ ಮೇಲೆ ತನ್ನ ಸಂತಾನವನ್ನು ನೋಡುವನು, ಚಿರಂಜೀವಿಯಾಗುವನು, ನನ್ನ ಸಂಕಲ್ಪವು ಇವನ ಕೈಯಿಂದ ನೆರವೇರುವದು;",
			"11": "<sup>11</sup> ತನ್ನ ಆತ್ಮವು ಅನುಭವಿಸಿದ ಶ್ರಮೆಯ ಫಲವನ್ನು ಕಂಡು ತೃಪ್ತನಾಗುವನು; ಧರ್ಮಾತ್ಮನಾದ ನನ್ನ ಸೇವಕನು ತನ್ನ ಜ್ಞಾನದ ಮೂಲಕ ಬಹು ಜನರನ್ನು ಧರ್ಮಮಾರ್ಗಕ್ಕೆ ತರುವನು; ಅವರ ಅಪರಾಧಗಳನ್ನು ಹೊತ್ತುಕೊಂಡು ಹೋಗುವನು.",
			"12": "<sup>12</sup> ಇವನು ಇಂಥವನಾಗಿರುವದರಿಂದ ನಾನು ಇವನಿಗೆ ದೊಡ್ಡವರ ಸಂಗಡ ಪಾಲು ಕೊಡುವೆನು, ಇವನು ಬಲಿಷ್ಠರೊಡನೆ ಸೂರೆಯನ್ನು ಹಂಚಿಕೊಳ್ಳುವನು; ತನ್ನ ಪ್ರಾಣವನ್ನು ಧಾರೆಯೆರೆದು ಮರಣಹೊಂದಿ ದ್ರೋಹಿಗಳೊಂದಿಗೆ ತನ್ನನ್ನೂ ಎಣಿಸಿಕೊಂಡು ಬಹುಜನ ದ್ರೋಹಿಗಳ ಪಾಪವನ್ನು ಹೊರುತ್ತಾ ಅವರಿಗಾಗಿ ವಿಜ್ಞಾಪನೆಮಾಡಿದನಲ್ಲಾ."
		},
		"54": {
			"1": "<sup>1</sup> ಹೆರದವಳೇ, ಬಂಜೆಯೇ, ಹರ್ಷಧ್ವನಿಗೈ! ಪ್ರಸವವೇದನೆಯನ್ನನುಭವಿಸದವಳೇ, ಆನಂದ ಸ್ವರವೆತ್ತಿ ಕೂಗು! ಗಂಡನುಳ್ಳವಳಿಗಿಂತ ಗಂಡಬಿಟ್ಟವಳಿಗೆ ಮಕ್ಕಳು ಹೆಚ್ಚು ಎಂದು ಯೆಹೋವನು ಹೇಳುತ್ತಾನೆ.",
			"2": "<sup>2</sup> ನಿನ್ನ ಗುಡಾರದ ಸ್ಥಳವನ್ನು ವಿಸ್ತರಿಸು, ನಿನ್ನ ನಿವಾಸದ ಪರದೆಗಳು ಹರಡಲಿ, ಸಂಕೋಚಪಡಬೇಡ; ನಿನ್ನ ಹಗ್ಗಗಳನ್ನು ಉದ್ದಮಾಡಿ ಗೂಟಗಳನ್ನು ಬಲಪಡಿಸು.",
			"3": "<sup>3</sup> ನೀನು ಎಲ್ಲಾ ಕಡೆಗಳಲ್ಲಿಯೂ ಹಬ್ಬಿಕೊಳ್ಳುವಿ; ನಿನ್ನ ಸಂತಾನದವರು ಜನಾಂಗಗಳನ್ನು ವಶಮಾಡಿಕೊಂಡು ಹಾಳು ಪಟ್ಟಣಗಳನ್ನು ಜನಭರಿತವಾಗುವಂತೆ ಮಾಡುವರು.",
			"4": "<sup>4</sup> ಹೆದರಬೇಡ, ನಿನಗೆ ಅವಮಾನವಾಗುವದಿಲ್ಲ, ನಾಚಿಕೆಪಡದಿರು, ನಿನಗೆ ಆಶಾಭಂಗವಾಗದು; ಯೌವನದಲ್ಲಿ ನಿನಗಾದ ಅವಮಾನವನ್ನು ಮರೆತುಬಿಡುವಿ, ವೈಧವ್ಯದಲ್ಲಿ ನಿನಗೆ ಸಂಭವಿಸಿದ ನಿಂದೆಯು ಇನ್ನು ನಿನ್ನ ನೆನಪಿಗೆ ಬಾರದು.",
			"5": "<sup>5</sup> ನಿನ್ನ ಸೃಷ್ಟಿಕರ್ತನೇ ನಿನ್ನ ಪತಿ, ಆತನ ಹೆಸರು ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವ; ಇಸ್ರಾಯೇಲಿನ ಸದಮಲಸ್ವಾಮಿಯು ನಿನ್ನ ನ್ಯಾಯಸ್ಥಾಪಕನಾಗಿದ್ದಾನೆ; ಸರ್ವಲೋಕದ ದೇವರೆಂಬ ನಾಮಧೇಯವನ್ನು ಹೊಂದಿರುವನು.",
			"6": "<sup>6</sup> ನೀನು ಬಿಡಲ್ಪಟ್ಟು ಮನನೊಂದ ಹೆಂಡತಿ, ಹೌದು, ತ್ಯಜಿಸಲ್ಪಟ್ಟವಳಾದ ನನ್ನ ಯೌವನಕಾಲದ ಪತ್ನಿ ಎಂದು ಯೆಹೋವನು ನಿನ್ನನ್ನು ಕನಿಕರಿಸಿ ಕರೆದಿದ್ದಾನೆ ಎಂಬದು ನಿನ್ನ ದೇವರ ನುಡಿ.",
			"7": "<sup>7</sup> ಕ್ಷಣಮಾತ್ರ ನಿನ್ನನ್ನು ಬಿಟ್ಟಿದ್ದೆನು, ಮಹಾಕೃಪೆಯಿಂದ ಸೇರಿಸಿಕೊಳ್ಳುವೆನು.",
			"8": "<sup>8</sup> ತಟ್ಟನೆ ಉಬ್ಬಿ ಹರಿಯುವ ಕೋಪದಿಂದ ಒಂದು ಕ್ಷಣ ಮಾತ್ರ ನನ್ನ ಮುಖವನ್ನು ನಿನಗೆ ಮರೆಮಾಡಿಕೊಂಡಿದ್ದೆನು, ಶಾಶ್ವತ ಕೃಪೆಯಿಂದ ನಿನ್ನನ್ನು ಕರುಣಿಸುವೆನು ಎಂದು ನಿನ್ನ ವಿಮೋಚಕನಾದ ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ.",
			"9": "<sup>9</sup> ನಿನ್ನನ್ನು ತ್ಯಜಿಸಿದ್ದು ನನಗೆ ನೋಹನ ಕಾಲದ ಜಲಪ್ರಲಯದಂತಿದೆ; ಇಂಥಾ ಜಲಪ್ರಲಯವು ಭೂಮಿಯನ್ನು ತಿರಿಗಿ ಆವರಿಸುವದಿಲ್ಲವೆಂದು ನಾನು ಹೇಗೆ ಪ್ರಮಾಣಮಾಡಿದೆನೋ ಹಾಗೆಯೇ ನಾನು ನಿನ್ನ ಮೇಲೆ ಇನ್ನು ಕೋಪಮಾಡುವದಿಲ್ಲ, ಗದರಿಸುವದಿಲ್ಲ ಎಂದು ಈಗ ಪ್ರಮಾಣಮಾಡಿದ್ದೇನೆ.",
			"10": "<sup>10</sup> ಬೆಟ್ಟಗಳು ಸ್ಥಳವನ್ನು ಬಿಟ್ಟುಹೋದಾವು, ಗುಡ್ಡಗಳು ಕದಲಿಯಾವು; ಆದರೆ ನನ್ನ ಕೃಪೆಯು ನಿನ್ನನ್ನು ಬಿಟ್ಟುಹೋಗದು, ಸಮಾಧಾನದ ನನ್ನ ಒಪ್ಪಂದವು ಕದಲದು ಎಂದು ನಿನ್ನನ್ನು ಕರುಣಿಸುವ ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ.",
			"11": "<sup>11</sup> ಕುಗ್ಗಿದವಳೇ, ಗಾಳಿಯ ಬಡಿತಕ್ಕೆ ಗುರಿಯಾದವಳೇ, ಯಾರೂ ಸಂತೈಸದವಳೇ, ಇಗೋ, ಇಂದ್ರನೀಲಮಣಿಗಳಿಂದ ನಿನಗೆ ಅಸ್ತಿವಾರವನ್ನು ಹಾಕಿ ನಿನ್ನ ಕಲ್ಲುಗಳನ್ನು ನೀಲಾಂಜನದ ಗಾರೆಯಿಂದ ಕಟ್ಟುವೆನು.",
			"12": "<sup>12</sup> ನಿನ್ನ ಶಿಖರಗಳನ್ನು ಮಾಣಿಕ್ಯಗಳಿಂದ, ನಿನ್ನ ಬಾಗಿಲುಗಳನ್ನು ಪದ್ಮರಾಗಗಳಿಂದ, ನಿನ್ನ ಪೌಳಿಗೋಡೆಯನ್ನು ಮನೋಹರರತ್ನಗಳಿಂದ ನಿರ್ಮಿಸುವೆನು.",
			"13": "<sup>13</sup> ನಿನ್ನ ಮಕ್ಕಳೆಲ್ಲರೂ ಯೆಹೋವನಿಂದ ಶಿಕ್ಷಿತರಾಗಿರುವರು, ಅವರಿಗೆ ಅಧಿಕ ಸುಕ್ಷೇಮವಾಗುವದು.",
			"14": "<sup>14</sup> ಧರ್ಮವೇ ನಿನಗೆ ಆಧಾರ; ನೀನು ಹಿಂಸೆಗೆ ದೂರವಾಗಿರುವಿ, ನಿನಗೆ ಹೆದರಿಕೆ ಇರುವದಿಲ್ಲ, ನಾಶನವು ದೂರವಾಗಿರುವದು, ನಿನ್ನ ಹತ್ತಿರಕ್ಕೆಬಾರದು.",
			"15": "<sup>15</sup> ನೋಡು, ಯಾರಾದರೂ ನಿನ್ನ ಸಂಗಡ ವ್ಯಾಜ್ಯವಾಡಿದರೆ ಅದು ನನ್ನ ಅಪ್ಪಣೆಯಿಂದ ಅಲ್ಲ; ಯಾರು ನಿನ್ನ ಮೇಲೆ ವ್ಯಾಜ್ಯವಾಡುತ್ತಾರೋ ಅವರು ನಿನ್ನ ನಿಮಿತ್ತ ಕೆಡವಲ್ಪಡುವರು.",
			"16": "<sup>16</sup> ಕೇಳು, ಕೆಂಡವನ್ನು ಊದುತ್ತಾ ಉಪಯುಕ್ತವಾದ ಆಯುಧವನ್ನುಂಟುಮಾಡುವ ಕಮ್ಮಾರನನ್ನು ಸೃಷ್ಟಿಸಿದವನು ನಾನೇ, ಹಾಳು ಮಾಡುವ ಕೆಡುಕನನ್ನು ನಿರ್ಮಿಸಿದವನೂ ನಾನೇ.",
			"17": "<sup>17</sup> ನಿನ್ನನ್ನು ಎದುರಿಸಲು ಕಲ್ಪಿಸಿದ ಯಾವ ಆಯುಧವೂ ಜಯಿಸದು; ನ್ಯಾಯವಿಚಾರಣೆಯಲ್ಲಿ ನಿನಗೆ ವಿರುದ್ಧವಾಗಿ ಏಳುವ ಪ್ರತಿಯೊಂದು ನಾಲಿಗೆಯನ್ನು ದೋಷಿಯೆಂದು ನೀನು ಖಂಡಿಸುವಿ. ಈ ಸ್ಥಿತಿಯೇ ಯೆಹೋವನ ಸೇವಕರ ಸ್ವಾಸ್ತ್ಯವೂ ನಾನು ದಯಪಾಲಿಸುವ ಸದ್ಧರ್ಮಫಲವೂ ಆಗಿದೆ ಎಂದು ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ."
		},
		"55": {
			"1": "<sup>1</sup> ಎಲೈ, ಬಾಯಾರಿದ ಸಕಲ ಜನರೇ, ನೀರಿನ ಬಳಿಗೆ ಬನ್ನಿರಿ, ಹಣವಿಲ್ಲದವನು ಸಹ ಬರಲಿ! ಬನ್ನಿರಿ, ಕೊಂಡುಕೊಳ್ಳಿರಿ, ಉಣ್ಣಿರಿ! ಬಂದು ದ್ರಾಕ್ಷಾರಸವನ್ನೂ ಹಾಲನ್ನೂ ಹಣಕೊಡದೆ ಕ್ರಯವಿಲ್ಲದೆ ತೆಗೆದುಕೊಳ್ಳಿರಿ.",
			"2": "<sup>2</sup> ಆಹಾರವಲ್ಲದ್ದಕ್ಕೆ ಹಣವನ್ನು ಏಕೆ ವ್ರಯಮಾಡುತ್ತೀರಿ? ತೃಪ್ತಿಗೊಳಿಸದ ಪದಾರ್ಥಕ್ಕೆ ನಿಮ್ಮ ದುಡಿತವನ್ನು ವೆಚ್ಚಮಾಡುವದೇಕೆ? ಕಿವಿಗೊಟ್ಟು ನನ್ನ ಮಾತನ್ನು ಕೇಳಿರಿ, ಒಳ್ಳೇದನ್ನೇ ಉಂಡು ಮೃಷ್ಟಾನ್ನದಲ್ಲಿ ಆನಂದಪಡಿರಿ.",
			"3": "<sup>3</sup> ಕಿವಿಯನ್ನು ಇತ್ತ ತಿರುಗಿಸಿರಿ, ನನ್ನ ಬಳಿಗೆ ಬನ್ನಿರಿ; ಆಲಿಸಿದರೆ ಬದುಕಿ ಬಾಳುವಿರಿ. ದಾವೀದನಿಗೆ ಖಂಡಿತವಾಗಿ ವಾಗ್ದಾನಮಾಡಿದ ಕೃಪಾವರಗಳನ್ನು ನಿಮಗೆ ಕೊಡುತ್ತೇನೆಂಬ ಶಾಶ್ವತವಾದ ಒಡಂಬಡಿಕೆಯನ್ನು ನಿಮ್ಮೊಂದಿಗೆ ಮಾಡಿಕೊಳ್ಳುವೆನು.",
			"4": "<sup>4</sup> ಇಗೋ, ನಾನು ಅವನನ್ನು ಜನಾಂಗಗಳಿಗೆ ಸಾಕ್ಷಿಯನ್ನಾಗಿಯೂ ನಾಯಕನನ್ನಾಗಿಯೂ ಅಧಿಪತಿಯನ್ನಾಗಿಯೂ ನೇಮಿಸಿದೆನು.",
			"5": "<sup>5</sup> ಎಲೈ, [ಇಸ್ರಾಯೇಲೇ], [ಅವನಂತೆ ನೀನೂ] ನಿನಗೆ ಗೊತ್ತಿಲ್ಲದ ಜನಾಂಗವನ್ನು ನಿನ್ನ ಅಧೀನಕ್ಕೆ ಕರೆಯುವಿ, ಇಸ್ರಾಯೇಲಿನ ಸದಮಲಸ್ವಾಮಿ ಎನಿಸಿಕೊಳ್ಳುವ ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನು ನಿನಗೆ ವೈಭವ ಕೊಟ್ಟಿರುವದನ್ನು ನೋಡಿ ನಿನ್ನನ್ನು ತಿಳಿಯದ ಜನಾಂಗವು ನಿನ್ನ ಆಶ್ರಯಕ್ಕೆ ಓಡಿಬರುವದು.",
			"6": "<sup>6</sup> ಯೆಹೋವನು ಸಿಕ್ಕುವ ಕಾಲದಲ್ಲಿ ಆತನನ್ನು ಆಶ್ರಯಿಸಿರಿ, ಆತನು ಸಮೀಪದಲ್ಲಿರುವಾಗ ಆತನಿಗೆ ಬಿನ್ನಹಮಾಡಿರಿ.",
			"7": "<sup>7</sup> ದುಷ್ಟನು ತನ್ನ ದುರ್ಮಾರ್ಗವನ್ನು ಬಿಡಲಿ, ಕೆಡುಕನು ತನ್ನ ದುರಾಲೋಚನೆಗಳನ್ನು ತ್ಯಜಿಸಲಿ; ಯೆಹೋವನ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡು ಬರಲಿ, ಆತನು ಅವನನ್ನು ಕರುಣಿಸುವನು; ನಮ್ಮ ದೇವರನ್ನು ಆಶ್ರಯಿಸಲಿ, ಆತನು ಮಹಾ ಕೃಪೆಯಿಂದ ಕ್ಷಮಿಸುವನು.",
			"8": "<sup>8</sup> ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ನನ್ನ ಆಲೋಚನೆಗಳು ನಿಮ್ಮ ಆಲೋಚನೆಗಳಲ್ಲ, ನಿಮ್ಮ ಮಾರ್ಗಗಳು ನನ್ನ ಮಾರ್ಗಗಳಲ್ಲ.",
			"9": "<sup>9</sup> ಭೂಮಿಯ ಮೇಲೆ ಆಕಾಶವು ಎಷ್ಟು ಉನ್ನತವೋ ನಿಮ್ಮ ಮಾರ್ಗಗಳಿಗಿಂತ ನನ್ನ ಮಾರ್ಗಗಳೂ ನಿಮ್ಮ ಆಲೋಚನೆಗಳಿಗಿಂತ ನನ್ನ ಆಲೋಚನೆಗಳೂ ಅಷ್ಟು ಉನ್ನತವಾಗಿವೆ.",
			"10": "<sup>10</sup> ಮಳೆಯೂ ಹಿಮವೂ ಆಕಾಶದಿಂದ ಬಿದ್ದು ಭೂಮಿಯನ್ನು ತೋಯಿಸಿ ಹಸಿರುಗೊಳಿಸಿ ಫಲಿಸುವಂತೆ ಮಾಡಿ ಬಿತ್ತುವವನಿಗೆ ಬೀಜವನ್ನು, ಉಣ್ಣುವವನಿಗೆ ಆಹಾರವನ್ನು ಒದಗಿಸಿದ ಹೊರತು ಹೇಗೆ ಆಕಾಶಕ್ಕೆ ಸುಮ್ಮನೆ ಹಿಂದಿರುಗುವದಿಲ್ಲವೋ",
			"11": "<sup>11</sup> ಹಾಗೆಯೇ ನನ್ನ ಬಾಯಿಂದ ಹೊರಟ ಮಾತು ನನ್ನ ಇಷ್ಟಾರ್ಥವನ್ನು ನೆರವೇರಿಸಿ ನಾನು ಉದ್ದೇಶಿಸಿದ್ದನ್ನು ಕೈಗೂಡಿಸಿದ ಹೊರತು ನನ್ನ ಕಡೆಗೆ ವ್ಯರ್ಥವಾಗಿ ಹಿಂದಿರುಗುವದಿಲ್ಲ.",
			"12": "<sup>12</sup> ನೀವು ಆನಂದಭರಿತರಾಗಿ ಹೊರಡುವಿರಿ, ಸಮಾಧಾನದಿಂದ ಮೆರವಣಿಗೆಯಾಗಿ ಬರುವಿರಿ; ಬೆಟ್ಟಗುಡ್ಡಗಳು ನಿಮ್ಮ ಮುಂದೆ ಜಯ ಘೋಷಮಾಡುವವು, ಅಡವಿಯ ಮರಗಳೆಲ್ಲಾ ಚಪ್ಪಾಳೆಹೊಡೆಯುವವು.",
			"13": "<sup>13</sup> ಮುಳ್ಳಿಗೆ ಬದಲಾಗಿ ತುರಾಯಿಯೂ ದತ್ತೂರಿಗೆ ಪ್ರತಿಯಾಗಿ ಸುಗಂಧವೂ ಬೆಳೆಯುವವು; ಆ ವನವು ಯೆಹೋವನಾಮಸ್ಮರಣೆಗೆ ಶಾಶ್ವತವಾದ ಗುರುತಾಗಿ ಎಂದಿಗೂ ಅಳಿಯದಿರುವದು."
		},
		"56": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ನ್ಯಾಯವನ್ನು ಅನುಸರಿಸಿರಿ, ಧರ್ಮವನ್ನು ಆಚರಿಸಿರಿ; ಏಕಂದರೆ ನನ್ನ ವಿಮೋಚನ ಕ್ರಿಯೆಯು ಬೇಗನೆ ಬರುವದು, ನನ್ನ ರಕ್ಷಣಧರ್ಮದ ಕಾರ್ಯವು ಶೀಘ್ರವಾಗಿ ವ್ಯಕ್ತವಾಗುವದು.",
			"2": "<sup>2</sup> ಈ ವಿಧಿಯನ್ನು ಕೈಕೊಳ್ಳುವ ಮನುಷ್ಯನು ಧನ್ಯನು; ಇದನ್ನೇ ಭದ್ರವಾಗಿ ಹಿಡಿದು ಸಬ್ಬತ್‍ದಿನವನ್ನು ಹೊಲೆಮಾಡದೆ ದೇವರ ದಿನವೆಂದು ಆಚರಿಸುತ್ತಾ ಯಾವ ಕೇಡಿಗೂ ಕೈಹಾಕದ ಮಾನವನು ಭಾಗ್ಯವಂತನು.",
			"3": "<sup>3</sup> ಯೆಹೋವನನ್ನು ಅವಲಂಬಿಸಿದ ವಿದೇಶೀಯನು - ಯೆಹೋವನು ತನ್ನ ಜನರಿಂದ ನನ್ನನ್ನು ಖಂಡಿತವಾಗಿ ಅಗಲಿಸುವನು ಎಂದು ಮಾತಾಡದಿರಲಿ; ಮತ್ತು ನಪುಂಸಕನು - ಅಯ್ಯೋ, ನಾನು ಗೊಡ್ಡುಮರ ಎಂದು ಅಂದುಕೊಳ್ಳದಿರಲಿ.",
			"4": "<sup>4</sup> ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ಸಬ್ಬತ್ ದಿನಗಳನ್ನು ನನ್ನ ದಿನಗಳೆಂದು ಆಚರಿಸಿ ನನಗೆ ಮೆಚ್ಚಿಕೆಯಾದದ್ದನ್ನು ಕೈಕೊಂಡು ನನ್ನ ಒಡಂಬಡಿಕೆಯನ್ನು ಭದ್ರವಾಗಿ ಹಿಡಿದುಕೊಂಡಿರುವ ನಪುಂಸಕರಿಗೆ",
			"5": "<sup>5</sup> ನನ್ನ ಪ್ರಾಕಾರಗಳೊಳಗೆ ನನ್ನ ಆಲಯದಲ್ಲಿ ಅವರ ಜ್ಞಾಪಕಾರ್ಥವಾಗಿ ಶಿಲೆಯನ್ನಿಟ್ಟು ಹೆಣ್ಣುಗಂಡು ಮಕ್ಕಳಿಗಿಂತ ಮೇಲಾದ ಹೆಸರುವಾಸಿಯನ್ನು ದಯಪಾಲಿಸುವೆನು; ಹೌದು, ಎಂದಿಗೂ ಅಳಿಯದ ಶಾಶ್ವತ ನಾಮವನ್ನು ಅನುಗ್ರಹಿಸುವೆನು.",
			"6": "<sup>6</sup> ಅನ್ಯದೇಶೀಯರಲ್ಲಿ ಯಾರು ಯೆಹೋವನೆಂಬ ನನ್ನನ್ನು ಅವಲಂಬಿಸಿ ಸೇವಿಸಿ ನನ್ನ ನಾಮವನ್ನು ಪ್ರೀತಿಸಿ ನನಗೆ ದಾಸರಾಗಿ ಸಬ್ಬತ್ ದಿನವನ್ನು ಹೊಲೆಮಾಡದೆ ನನ್ನ ದಿನವೆಂದು ಆಚರಿಸಿ ನನ್ನ ಒಡಂಬಡಿಕೆಯನ್ನು ಭದ್ರವಾಗಿ ಹಿಡಿಯುತ್ತಾರೋ",
			"7": "<sup>7</sup> ಅವರೆಲ್ಲರನ್ನೂ ನಾನು ನನ್ನ ಪವಿತ್ರಪರ್ವತಕ್ಕೆ ಬರಮಾಡಿ ನನ್ನ ಪ್ರಾರ್ಥನಾಲಯದಲ್ಲಿ ಉಲ್ಲಾಸಗೊಳಿಸುವೆನು; ನನ್ನ ಯಜ್ಞವೇದಿಯಲ್ಲಿ ಅವರು ಅರ್ಪಿಸುವ ಸರ್ವಾಂಗಹೋಮಗಳೂ ಯಜ್ಞಗಳೂ ನನಗೆ ಮೆಚ್ಚಿಕೆಯಾಗುವವು; ನನ್ನ ಆಲಯವು ಎಲ್ಲಾ ಜನಾಂಗಗಳಿಗೂ ಪ್ರಾರ್ಥನಾಲಯವೆನಿಸಿಕೊಳ್ಳುವದು.",
			"8": "<sup>8</sup> ಇಸ್ರಾಯೇಲಿನ ದೇಶಭ್ರಷ್ಟರನ್ನು ಕೂಡಿಸಿಕೊಳ್ಳುವ ಕರ್ತನಾದ ಯೆಹೋವನ ನುಡಿಯೇನಂದರೆ - ನಾನು ಕೂಡಿಸಿದ ಇಸ್ರಾಯೇಲ್ಯರೊಂದಿಗೆ ಇನ್ನೂ ಹಲವರನ್ನು ಕೂಡಿಸುವೆನು ಎಂಬದೇ.",
			"9": "<sup>9</sup> ಅರಣ್ಯದ ಸಕಲ ಮೃಗಗಳೇ, ಕಾಡಿನ ಎಲ್ಲಾ ಜಂತುಗಳೇ, ನುಂಗಿಬಿಡುವದಕ್ಕೆ ಬನ್ನಿರಿ!",
			"10": "<sup>10</sup> ಇವರ ಕಾವಲುಗಾರರು ಕುರುಡರು, ಇವರಲ್ಲಿ ಯಾರಿಗೂ ತಿಳುವಳಿಕೆಯಿಲ್ಲ; ಇವರೆಲ್ಲರೂ ಬೊಗಳಲಾರದ ಮೂಗನಾಯಿಗಳು, ನಿದ್ರೆಯನ್ನಾಶಿಸಿ ಮಲಗಿಕೊಂಡು ಕನವರಿಸುವ ನಾಯಿಗಳು.",
			"11": "<sup>11</sup> ಮತ್ತು ಇವು ಹೊಟ್ಟೆಬಾಕ ನಾಯಿಗಳು, ಇವುಗಳಿಗೆ ಎಂದಿಗೂ ಸಾಕು ಎನಿಸದು. ಇಂಥವರು ಕುರಿಗಳನ್ನು ಕಾಯತಕ್ಕವರೋ! ಬುದ್ಧಿಹೀನರಾಗಿದ್ದಾರೆ, ಇವರಲ್ಲಿ ಯಾವನೂ ತಪ್ಪದೆ ಪ್ರತಿಯೊಬ್ಬನೂ ಕೊಳ್ಳೆಹೊಡೆಯಬೇಕೆಂದು ತನ್ನ ತನ್ನ ಮಾರ್ಗಕ್ಕೆ ತಿರುಗಿಕೊಂಡಿದ್ದಾನೆ.",
			"12": "<sup>12</sup> ಬನ್ನಿರಿ, ನಾನು ದ್ರಾಕ್ಷಾರಸವನ್ನು ತರಿಸುವೆನು, ಬೇಕಾದಷ್ಟು ಮದ್ಯವನ್ನು ಕುಡಿಯುವ; ನಾಳೆಯೂ ಈ ದಿನದಂತೆ ಕೇವಲ ಅತ್ಯಂತ ವಿಶೇಷದಿನವಾಗಿರುವದು [ಎಂದು ಹರಟಿಕೊಳ್ಳುವರು]."
		},
		"57": {
			"1": "<sup>1</sup> ಸಜ್ಜನರು ನಾಶವಾಗುತ್ತಾರೆ, ಯಾರೂ ಮನಸ್ಸಿಗೆ ತಾರರು; ಸದ್ಭಕ್ತರು ಗತಿಸುತ್ತಾರೆ, ಆಹಾ, ಶಿಷ್ಟರು ಕೇಡಿನಿಂದ ಪಾರಾಗಿದ್ದಾರೆ ಎಂದು ಯಾರೂ ಯೋಚಿಸರು.",
			"2": "<sup>2</sup> ಇವರು ಸಮಾಧಾನದಲ್ಲಿ ಸೇರಿದ್ದಾರಲ್ಲಾ; ನೆಟ್ಟಗೆ ನಡೆಯುವ ಪ್ರತಿಯೊಬ್ಬನು ತನ್ನ ತನ್ನ ದೀರ್ಘನಿದ್ರಾಸ್ಥಾನದಲ್ಲಿ ವಿಶ್ರಮಿಸಿಕೊಳ್ಳುತ್ತಿದ್ದಾನೆ.",
			"3": "<sup>3</sup> ಮಾಟಗಾತಿಯ ಮಕ್ಕಳೇ, ಸೂಳೆಗಾರನ ಮತ್ತು ಸೂಳೆಯ ಸಂತಾನದವರೇ, ನೀವು ಇಲ್ಲಿಗೆ ಬನ್ನಿರಿ! ಯಾರನ್ನು ಅಣಕಿಸಿ ವಿನೋದ ಪಡುತ್ತೀರಿ?",
			"4": "<sup>4</sup> ಯಾರನ್ನು ನೋಡಿ ಬಾಯಿ ಕಿಸಿದು ನಾಲಿಗೆಯನ್ನು ಚಾಚುತ್ತೀರಿ?",
			"5": "<sup>5</sup> ಏಲಾ ಮರಗಳ ತೋಪುಗಳಲ್ಲಿಯೂ ಸೊಂಪಾಗಿ ಬೆಳೆದಿರುವ ಪ್ರತಿಯೊಂದು ಮರದ ನೆರಳಿನಲ್ಲಿಯೂ ಮದವೇರಿಸಿಕೊಂಡು ಹೊಳೆ ಕೊರೆದ ಡೊಂಗರಗಳಲ್ಲಿಯೂ ಬೆಟ್ಟದ ಗವಿಗಳಲ್ಲಿಯೂ ಮಕ್ಕಳನ್ನು ಕೊಂದುಹಾಕುವ ನೀವು ದ್ರೋಹದ ಸಂತಾನವೂ ಸುಳ್ಳಿನ ಸಂತತಿಯೂ ಆಗಿದ್ದೀರಿ.",
			"6": "<sup>6</sup> [ಕೆಟ್ಟ ಸಂತಾನವೇ] ಹೊಳೆಯ ಗುಂಡುಗಳು ನಿನಗೆ ಗತಿ, ಅವುಗಳೇ ನಿನ್ನ ಪಾಲು; ಅವುಗಳಿಗೇ ನೀನು ಪಾನದ್ರವ್ಯವನ್ನು ನೈವೇದ್ಯವಾಗಿ ಸುರಿದು ಧಾನ್ಯದ್ರವ್ಯವನ್ನು ಅರ್ಪಿಸಿದ್ದೀ. ನಾನು ಇದನ್ನೆಲ್ಲಾ ನೋಡಿ ಕೋಪವನ್ನು ಅಣಗಿಸಿಕೊಳ್ಳಲಾದೀತೇ?",
			"7": "<sup>7</sup> ಮಹೋನ್ನತಪರ್ವತದಲ್ಲಿ ನೀನು ಮಂಚವನ್ನು ಹಾಕಿಕೊಂಡಿದ್ದೀ, ಯಜ್ಞಮಾಡಲು ಆ ಬೆಟ್ಟವನ್ನೇರಿದ್ದೀ.",
			"8": "<sup>8</sup> ಕದದ ಮತ್ತು ನಿಲುವಿನ ಹಿಂಭಾಗದಲ್ಲಿ ನೀನು ವಿಗ್ರಹವನ್ನು ಇಟ್ಟುಕೊಂಡಿದ್ದೀ; ನನ್ನನ್ನು ಬಿಟ್ಟುಬಿಟ್ಟು ನಿನ್ನ ಮಂಚದ ಗವಸಣಿಗೆಯನ್ನು ತೆಗೆದು ಅದನ್ನು ಹತ್ತಿ ಅದರಲ್ಲಿ ಅನ್ಯರಿಗೆ ಸ್ಥಳಕೊಟ್ಟು ಅವರೊಂದಿಗೆ ಒಪ್ಪಂದಮಾಡಿಕೊಂಡಿದ್ದೀ. ಅವರ ಸಂಗಮವನ್ನು ಆಶಿಸಿ ಸ್ಥಳವನ್ನು ಸಂಕೇತಮಾಡಿಕೊಂಡಿದ್ದೀ.",
			"9": "<sup>9</sup> ಬಹು ಸುಗಂಧದ್ರವ್ಯಗಳನ್ನು ಕೂಡಿಸಿ ತೈಲವನ್ನೂ ತೆಗೆದುಕೊಂಡು ಪರರಾಜನ ಬಳಿಗೆ ಪ್ರಯಾಣಮಾಡಿದ್ದೀ; ನಿನ್ನ ರಾಯಭಾರಿಗಳನ್ನು ದೂರ ದೂರ ಕಳುಹಿಸಿದ್ದೀ. ನಿನ್ನನ್ನು ಪಾತಾಳದವರೆಗೂ ತಗ್ಗಿಸಿಕೊಂಡಿದ್ದೀ.",
			"10": "<sup>10</sup> ನೀನು ನಡೆದು ನಡೆದು ಆಯಾಸಗೊಂಡರೂ ದಿಕ್ಕಿಲ್ಲವಲ್ಲಾ ಅಂದುಕೊಳ್ಳಲಿಲ್ಲ; ಹೊಸ ಬಲವನ್ನು ತಂದುಕೊಂಡಿ; ಆದದರಿಂದ ನೀನು ಸೋತುಹೋಗಲಿಲ್ಲ.",
			"11": "<sup>11</sup> ನೀನು ಯಾರಿಗೆ ಬೆದರಿ ಹೆದರಿದ್ದರಿಂದ ನನ್ನನ್ನು ಮರೆತು ನನಗೆ ಮೋಸಮಾಡಿದಿ? ಈ ದ್ರೋಹಕ್ಕೂ ಹಿಂದೆಗೆಯಲಿಲ್ಲವಲ್ಲಾ! ನೀನು ನನಗೆ ಅಂಜದೆ ಇರುವದಕ್ಕೆ ನಾನು ಬಹುಕಾಲದಿಂದ ಸುಮ್ಮನೆ ಇದ್ದದ್ದೇ ಕಾರಣವಲ್ಲವೆ.",
			"12": "<sup>12</sup> ನಾನು ನಿನ್ನ ಧರ್ಮವನ್ನು ಬೈಲಿಗೆ ತರುವೆನು, ನಿನ್ನ ಕಾರ್ಯಗಳೇನೋ ನಿನಗೆ ನಿಷ್ಪ್ರಯೋಜನ.",
			"13": "<sup>13</sup> ನೀನು ಕೂಗಿಕೊಳ್ಳುವಾಗ ನೀನು ಕೂಡಿಹಾಕಿಕೊಂಡಿರುವ ಇವುಗಳೇ ನಿನ್ನನ್ನುದ್ಧರಿಸಲಿ. ಆದರೆ ಇವುಗಳನ್ನೆಲ್ಲಾ ಗಾಳಿಯು ಬಡಿದುಕೊಂಡು ಹೋಗುವದು, ಬರೀ ಉಸಿರಾದರೂ ಒಯ್ಯುವದು. ನನ್ನನ್ನು ಆಶ್ರಯಿಸುವವನೋ ದೇಶವನ್ನನುಭವಿಸಿ ನನ್ನ ಪರಿಶುದ್ಧಪರ್ವತವನ್ನು ಬಾಧ್ಯವಾಗಿ ಹೊಂದುವನು.",
			"14": "<sup>14</sup> ಮಣ್ಣು ಹಾಕಿರಿ, ಹಾಕಿರಿ, ಮಾರ್ಗವನ್ನು ಸರಿಮಾಡಿರಿ; ನನ್ನ ಜನರ ದಾರಿಯೊಳಗಿಂದ ಆಟಂಕವನ್ನು ಎತ್ತಿಹಾಕಿರಿ ಎಂದು ಒಂದು ವಾಣಿಯು ನುಡಿಯುತ್ತದೆ.",
			"15": "<sup>15</sup> ಸದಮಲನೆನಿಸಿಕೊಂಡು ಶಾಶ್ವತಲೋಕದಲ್ಲಿ ನಿತ್ಯನಿವಾಸಿಯಾದ ಮಹೋನ್ನತನು ಹೀಗನ್ನುತ್ತಾನೆ - ಉನ್ನತಲೋಕವೆಂಬ ಪವಿತ್ರಾಲಯದಲ್ಲಿ ವಾಸಿಸುವ ನಾನು ಜಜ್ಜಿಹೋದ ದೀನಮನದೊಂದಿಗೆ ಇದ್ದುಕೊಂಡು ದೀನನ ಆತ್ಮವನ್ನೂ ಜಜ್ಜಿಹೋದ ಮನ ಮನಸ್ಸನ್ನೂ ಉಜ್ಜೀವಿಸುವವನಾಗಿದ್ದೇನೆ.",
			"16": "<sup>16</sup> ನಾನು ಸರ್ವದಾ ವ್ಯಾಜ್ಯವಾಡೆನು, ಕಡೆಯ ತನಕ ಕೋಪಿಸಿಕೊಳ್ಳೆನು, ಹೀಗೆ ಮಾಡಿದರೆ ಮನುಷ್ಯಾತ್ಮವೂ ನಾನು ಸೃಷ್ಟಿಸಿದ ಜೀವವೂ ನನ್ನಿಂದ ಕುಂದಿಹೋದಾವು.",
			"17": "<sup>17</sup> ನನ್ನ ಜನರು ಲಾಭವನ್ನು ದೋಚಿಕೊಂಡ ಅನ್ಯಾಯವನ್ನು ನಾನು ನೋಡಿ ಕೋಪಗೊಂಡು ಅವರನ್ನು ಹೊಡೆದೆನು, [ನನ್ನ ಮುಖವನ್ನು] ಮುಚ್ಚಿಕೊಂಡು ರೋಷಭರಿತನಾಗಿದ್ದೆನು; ಅವರೋ ತಿರುಗಿಕೊಂಡು ಮನಸ್ಸಿಗೆ ತೋರಿದ ಹಾಗೆಯೇ ನಡೆಯುತ್ತಾ ಬಂದಿದ್ದಾರೆ.",
			"18": "<sup>18</sup> ಅವರ ಮಾರ್ಗಗಳನ್ನು ನೋಡಿದ್ದೇನೆ, ಅವರನ್ನು ಸ್ವಸ್ಥಮಾಡಿ ನಡಿಸುತ್ತಾ ಅವರಿಗೆ, ಅವರಲ್ಲಿಯೂ ದುಃಖಿತರಿಗೆ, ಆದರಣೆಯನ್ನು ಪ್ರತಿಫಲವಾಗಿ ಕೊಡುವೆನು.",
			"19": "<sup>19</sup> ಯೆಹೋವನು ಮನುಷ್ಯರ ಬಾಯ ಯೋಗ್ಯಫಲವಾಗಿರುವ ಸ್ತೋತ್ರವನ್ನು ಉಂಟುಮಾಡುವವನಾಗಿ - ದೂರದವನಿಗೂ ಸಮೀಪದವನಿಗೂ ಕ್ಷೇಮವಿರಲಿ, ಸುಕ್ಷೇಮವಿರಲಿ, ನಾನು ಅವರನ್ನು ಸ್ವಸ್ಥಮಾಡುವೆನು ಎಂದು ಹೇಳುತ್ತಾನೆ.",
			"20": "<sup>20</sup> ದುಷ್ಟರಾದರೋ ಅಲ್ಲೋಲಕಲ್ಲೋಲವಾದ ಸಮುದ್ರದಂತಿದ್ದಾರೆ; ಅದು ಸುಮ್ಮನಿರದು, ಅದರ ತೆರೆಗಳು ಕೆಸರನ್ನೂ ಬುರುದೆಯನ್ನೂ ಕಾರುತ್ತಲಿರುತ್ತವೆ.",
			"21": "<sup>21</sup> ದುಷ್ಟರಿಗೆ ಸಮಾಧಾನವೇ ಇಲ್ಲವೆಂದು ನನ್ನ ದೇವರು ನುಡಿಯುತ್ತಾನೆ."
		},
		"58": {
			"1": "<sup>1</sup> ಗಂಟಲೆತ್ತಿ ಕೂಗು, ಸಂಕೋಚಪಡಬೇಡ, ಕೊಂಬಿನಂತೆ ದನಿಗೈದು ನನ್ನ ಜನರಿಗೆ ಅವರ ದ್ರೋಹವನ್ನು ತಿಳಿಸು, ಯಾಕೋಬವಂಶದವರಿಗೆ ಅವರ ಪಾಪಗಳನ್ನು ಅರುಹು.",
			"2": "<sup>2</sup> ಅವರಂತು ದಿನ ದಿನವೂ ನನ್ನ ದರ್ಶನಕ್ಕಾಗಿ ಬಂದು ನನ್ನ ಮಾರ್ಗವನ್ನು ತಿಳಿದುಕೊಳ್ಳುವದರಲ್ಲಿ ಹೆಚ್ಚಳಪಡುತ್ತಾರೆ; ತನ್ನ ದೇವರ ನಿಯಮಗಳನ್ನು ಬಿಡದೆ ಧರ್ಮವನ್ನಾಚರಿಸುವ ಜನಾಂಗವೋ ಎಂಬಂತೆ ಧೈರ್ಯವಾಗಿ ನನ್ನಿಂದ ನ್ಯಾಯವಿಧಿಗಳನ್ನು ಕೇಳಿಕೊಂಡು ದೇವದರ್ಶನದಲ್ಲಿ ಆನಂದಿಸುತ್ತಾರೆ.",
			"3": "<sup>3</sup> ನಾವು ಉಪವಾಸಮಾಡಿದ್ದೇವೆ, ನೀನು ಏಕೆ ಕಟಾಕ್ಷಿಸುವದಿಲ್ಲ; ನಮ್ಮ ಆತ್ಮವನ್ನು ಕುಂದಿಸಿಕೊಂಡಿದ್ದೇವೆ, ನೀನು ಗಮನಿಸದಿರುವದೇಕೆ ಅಂದುಕೊಳ್ಳುತ್ತಾರೆ. ಇಗೋ, ನಿಮ್ಮ ಉಪವಾಸದ ದಿನದಲ್ಲಿಯೂ ನಿಮ್ಮ ನಿತ್ಯದ ಕೆಲಸವನ್ನು ನಡಿಸಿ ನಿಮ್ಮ ಆಳುಗಳನ್ನು ದುಡಿತಕ್ಕೆಳೆಯುತ್ತೀರಿ.",
			"4": "<sup>4</sup> ನೋಡಿರಿ, ನಿಮ್ಮ ಉಪವಾಸದ ಫಲವೇನಂದರೆ - ವ್ಯಾಜ್ಯ, ಕಲಹ, ಕೇಡಿನ ಗುದ್ದು, ಇವುಗಳೇ. ನೀವು ಈಗ ಮಾಡುವ ಉಪವಾಸವು ನಿಮ್ಮ ಪ್ರಾರ್ಥನೆಯನ್ನು ಉನ್ನತಲೋಕಕ್ಕೆ ಮುಟ್ಟಿಸತಕ್ಕದ್ದಲ್ಲ.",
			"5": "<sup>5</sup> ಮನುಷ್ಯನು ತನ್ನ ಆತ್ಮವನ್ನು ಕುಂದಿಸಿಕೊಳ್ಳುವದಕ್ಕಾಗಿ ನಾನು ನೇಮಿಸಿದ ಉಪವಾಸದಿನವು ಇಂಥದೋ? ಒಬ್ಬನು ಜೊಂಡಿನಂತೆ ತಲೆಯನ್ನು ಬೊಗ್ಗಿಸಿಕೊಂಡು ಗೋಣೀತಟ್ಟನ್ನೂ ಬೂದಿಯನ್ನೂ ಆಸನಮಾಡಿಕೊಳ್ಳುವದು ಯೆಹೋವನಿಗೆ ಸಮರ್ಪಕವಾದ ಉಪವಾಸ ಎನ್ನುತ್ತೀರೋ?",
			"6": "<sup>6</sup> ನೋಡಿರಿ, ಕೇಡಿನ ಬಂಧಗಳನ್ನು ಬಿಚ್ಚುವದು, ನೊಗದ ಕಣ್ಣಿಗಳನ್ನು ಕಳಚುವದು, ಜಜ್ಜಿಹೋದವರನ್ನು ಬಿಡುಗಡೆಮಾಡುವದು, ನೊಗಗಳನ್ನೆಲ್ಲಾ ಮುರಿಯುವದು,",
			"7": "<sup>7</sup> ಹಸಿದವರಿಗೆ ಅನ್ನವನ್ನು ಹಂಚುವದು, ಅಲೆಯುತ್ತಿರುವ ಬಡವರನ್ನು ಮನೆಗೆ ಬರಮಾಡಿಕೊಳ್ಳುವದು, ಬೆತ್ತಲೆಯವರನ್ನು ಕಂಡಾಗೆಲ್ಲಾ ಅವರಿಗೆ ಹೊದಿಸುವದು, ನಿನ್ನಂತೆ ನರನಾಗಿರುವ ಯಾವನಿಗೇ ಆಗಲಿ ಮುಖ ತಪ್ಪಿಸಿಕೊಳ್ಳದಿರುವದು, ಇವುಗಳೇ ನನಗೆ ಇಷ್ಟವಾದ ಉಪವಾಸವ್ರತವಲ್ಲವೇ.",
			"8": "<sup>8</sup> ಇದನ್ನು ಆಚರಿಸುವಾಗ ನಿಮಗೆ ಬೆಳಕು ಉದಯದಂತೆ ಭೇದಿಸಿಕೊಂಡು ಬರುವದು, ನಿಮ್ಮ ಕ್ಷೇಮವು ಬೇಗನೆ ವಿಕಸಿಸುವದು; ನಿಮ್ಮ ಧರ್ಮವು ನಿಮಗೆ ಮುಂಬಲವಾಗಿ ಮುಂದರಿಯುವದು, ಯೆಹೋವನ ಮಹಿಮೆಯು ನಿಮಗೆ ಹಿಂಬಲವಾಗಿರುವದು.",
			"9": "<sup>9</sup> ಆಗ ನೀವು ಕೂಗಿದರೆ ಯೆಹೋವನು ಉತ್ತರಕೊಡುವನು, ಮೊರೆಯಿಟ್ಟು ಕರೆದರೆ ಇಗೋ, ಇದ್ದೇನೆ, ಅನ್ನುವನು. ನೀವು ನಿಮ್ಮ ಮಧ್ಯದೊಳಗಿಂದ ನೊಗವನ್ನೂ ಬೆರಳಸನ್ನೆಯನ್ನೂ ಕೆಡುಕಿನ ನುಡಿಯನ್ನೂ ಹೋಗಲಾಡಿಸಿ",
			"10": "<sup>10</sup> ನಿಮಗೆ ಪ್ರಿಯವಾದದ್ದನ್ನು ಹಸಿದವರಿಗೆ ಒದಗಿಸಿ ಕುಗ್ಗಿದವರ ಆಶೆಯನ್ನು ನೆರವೇರಿಸಿದರೆ ಆಗ ನಿಮಗೆ ಕತ್ತಲಲ್ಲಿ ಬೆಳಕು ಉದಯಿಸುವದು, ನಿಮ್ಮ ಅಂಧಕಾರವು ಪರಿಹಾರವಾಗಿ ಮಧ್ಯಾಹ್ನವಾಗುವದು.",
			"11": "<sup>11</sup> ಮತ್ತು ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ನಿತ್ಯವೂ ನಡಿಸುತ್ತಾ ಮರುಭೂಮಿಯಲ್ಲಿಯೂ ನಿಮ್ಮ ಆತ್ಮವನ್ನು ತೃಪ್ತಿಗೊಳಿಸಿ ನಿಮ್ಮ ಎಲುಬುಗಳನ್ನು ಸಸಾರಮಾಡುವನು; ನೀವು ತಂಪಾದ ತೋಟಕ್ಕೂ ನೀರಿಗೆ ಮೋಸವಿಲ್ಲದ ಬುಗ್ಗೆಗೂ ಸಮಾನವಾಗುವಿರಿ.",
			"12": "<sup>12</sup> ನಿಮ್ಮ ಸಂತಾನದವರು ಪುರಾತನಕಾಲದಲ್ಲಿ ಹಾಳಾದ ನಿವೇಶನಗಳನ್ನು ತಿರಿಗಿ ಕಟ್ಟುವರು. ನೀವು ತಲತಲಾಂತರಗಳಿಂದ ಪಾಳುಬಿದ್ದಿರುವ ಅಸ್ತಿವಾರಗಳ ಮೇಲೆ ಕಟ್ಟಡಗಳನ್ನು ಎಬ್ಬಿಸುವಿರಿ; ಬಿದ್ದ ಗೋಡೆಯನ್ನು ಹಾಕುವ ಜನಾಂಗ, ಹಾದಿಗಳನ್ನು ತಿರುಗಾಡುವ ಸ್ಥಿತಿಗೆ ತರುವ ಜನಾಂಗ, ಎಂಬೀ ಬಿರುದುಗಳು ನಿಮಗುಂಟಾಗುವವು.",
			"13": "<sup>13</sup> ನೀವು ಸಬ್ಬತ್ ದಿನವನ್ನು ಅಗಮ್ಯವಾದದ್ದೆಂದು ತಿಳಿದು ಆ ನನ್ನ ಪರಿಶುದ್ಧ ದಿವಸದಲ್ಲಿ ನಿಮ್ಮ ನಿತ್ಯದ ಕೆಲಸವನ್ನು ಮಾಡದೆ ಸ್ವೇಚ್ಫೆಯಾಗಿ ನಡೆಯದೆ ಸ್ವಕಾರ್ಯದಲ್ಲಿ ನಿರತರಾಗದೆ ಹರಟೆಹರಟದೆ ಯೆಹೋವನ ಸಬ್ಬತ್ತೆಂಬ ಪರಿಶುದ್ಧ ದಿನವು ಉಲ್ಲಾಸಕರವೂ ಮಾನ್ಯವೂ ಆದದ್ದೆಂದು ಘನಪಡಿಸಿದರೆ",
			"14": "<sup>14</sup> ಆಗ ನೀವು ಯೆಹೋವನಲ್ಲಿ ಉಲ್ಲಾಸಪಡುವಿರಿ, ಮತ್ತು ನಾನು ನಿಮ್ಮನ್ನು ಭೂಮಿಯ ಎತ್ತರವಾದ ಪ್ರದೇಶಗಳ ಮೇಲೆ ಹತ್ತಿಸಿ ನಿಮ್ಮ ಪಿತೃವಾದ ಯಾಕೋಬನ ಸ್ವಾಸ್ತ್ಯವನ್ನು ನೀವು ಅನುಭವಿಸುವಂತೆ ಮಾಡುವೆನು. ಯೆಹೋವನೇ ಇದನ್ನು ನುಡಿದಿದ್ದಾನೆ."
		},
		"59": {
			"1": "<sup>1</sup> ಇಗೋ, ಯೆಹೋವನ ಹಸ್ತವು ರಕ್ಷಿಸಲಾರದಂತೆ ಮೋಟುಗೈಯಲ್ಲ; ಆತನ ಕಿವಿಯು ಕೇಳಲಾರದ ಹಾಗೆ ಕಿವುಡಲ್ಲ.",
			"2": "<sup>2</sup> ಆದರೆ ನಿಮ್ಮ ಅಪರಾಧಗಳೇ ನಿಮ್ಮ ದೇವರಿಂದ ನಿಮ್ಮನ್ನು ಅಗಲಿಸುತ್ತಾ ಬಂದಿವೆ; ನಿಮ್ಮ ಪಾಪಗಳೇ ಆತನು ಕೇಳಲಾರದಂತೆ ಆತನ ಮುಖಕ್ಕೆ ಅಡ್ಡವಾಗಿವೆ.",
			"3": "<sup>3</sup> ನಿಮ್ಮ ಕೈಗಳು ರಕ್ತದಿಂದ ಹೊಲಸಾಗಿವೆ, ನಿಮ್ಮ ಬೆರಳುಗಳು ಅಪರಾಧದಿಂದ ಅಶುದ್ಧವಾಗಿವೆ; ನಿಮ್ಮ ತುಟಿಗಳು ಸುಳ್ಳಾಡುತ್ತವೆ, ನಿಮ್ಮ ನಾಲಿಗೆಯು ಕೆಡುಕನ್ನು ನುಡಿಯುತ್ತದೆ.",
			"4": "<sup>4</sup> [ಇವರಲ್ಲಿ] ಯಾರೂ ನ್ಯಾಯವಾಗಿ ನ್ಯಾಯಾಸ್ಥಾನಕ್ಕೆ ಹೋಗುವದಿಲ್ಲ, ಯಾರೂ ಸತ್ಯವಾಗಿ ವಾದಿಸುವದಿಲ್ಲ; ಇವರು ಶೂನ್ಯವಾಗಿರುವದನ್ನು ನಂಬಿಕೊಂಡು ಅಬದ್ಧವನ್ನಾಡುತ್ತಾರೆ; ಹಿಂಸೆಯನ್ನು ಗರ್ಭಧರಿಸಿ ಶ್ರಮೆಯನ್ನು ಹೆರುತ್ತಾರೆ.",
			"5": "<sup>5</sup> ಹಾವಿನಂತೆ [ಕೇಡಿನ] ಮೊಟ್ಟೆಗಳನ್ನು ಮರಿಮಾಡುತ್ತಾರೆ, ಜಾಡರ ಹುಳದಂತೆ ಬಲೆಯನ್ನು ನೇಯುತ್ತಾರೆ; ಆ ಮೊಟ್ಟೆಗಳನ್ನು ತಿನ್ನುವವನಿಗೆ ಮರಣವಾಗುವದು, ಒಡೆದುಬಿಡುವವನಿಗೆ ವಿಷದ ಮರಿಹೊರಡುವದು.",
			"6": "<sup>6</sup> ಇವರ ಬಲೆಯ ದಾರವು ಬಟ್ಟೆಯಾಗದು, ಇವರು ನೇಯ್ದದ್ದು ಹೊದಿಕೆಯಾಗದು; ಇವರ ಕಾರ್ಯಗಳು ಅಧರ್ಮಕಾರ್ಯಗಳು, ಇವರ ಕೈಯಲ್ಲಿ ಬಲಾತ್ಕಾರವೇ.",
			"7": "<sup>7</sup> ಇವರ ಕಾಲುಗಳು ಕೇಡನ್ನು ಹಿಂಬಾಲಿಸಿ ಓಡುತ್ತವೆ, ಇವರು ನಿರಪರಾಧಿಯ ರಕ್ತವನ್ನು ಸುರಿಸಲು ಆತುರಪಡುತ್ತಾರೆ, ಇವರ ಯೋಚನೆಗಳು ಅಧರ್ಮದ ಯೋಚನೆಗಳೇ, ಇವರು ಹೋದ ದಾರಿಗಳಲ್ಲಿ ಭಂಗವೂ ನಾಶನವೂ ಉಂಟಾಗುತ್ತವೆ;",
			"8": "<sup>8</sup> ಇವರು ಸಮಾಧಾನದ ಮಾರ್ಗವನ್ನೇ ಅರಿಯರು, ಇವರ ಹಾದಿಗಳಲ್ಲಿ ಯಾವ ನ್ಯಾಯವೂ ಇಲ್ಲ, ತಮ್ಮ ಮಾರ್ಗಗಳನ್ನು ವಕ್ರಮಾಡಿಕೊಂಡಿದ್ದಾರೆ, ಅವುಗಳಲ್ಲಿ ನಡೆಯುವ ಯಾವನೂ ಸಮಾಧಾನವನ್ನರಿಯನು.",
			"9": "<sup>9</sup> ಆದಕಾರಣ [ಯೆಹೋವನ] ನ್ಯಾಯನಿರ್ಣಯವು ನಮಗೆ ದೂರವಾಗಿದೆ. [ಆತನ] ರಕ್ಷಣಧರ್ಮದ ಕಾರ್ಯವು ನಮಗೆ ಸಂಧಿಸುವದಿಲ್ಲ; ಬೆಳಕನ್ನು ಎದುರುನೋಡುತ್ತಿರುವ ನಮ್ಮನ್ನು ಕತ್ತಲೆಯೇ ಆವರಿಸಿದೆ, ಜ್ಯೋತಿಯನ್ನು ನಿರೀಕ್ಷಿಸುತ್ತಿರುವ ನಾವು ಅಂಧಕಾರದಲ್ಲೇ ನಡೆಯುತ್ತೇವೆ.",
			"10": "<sup>10</sup> ಕುರುಡರಂತೆ ಗೋಡೆಯನ್ನು ತಡವರಿಸುತ್ತೇವೆ, ಹೌದು, ಕಣ್ಣಿಲ್ಲದವರ ಹಾಗೆ ತಡವಾಡುತ್ತೇವೆ, ಮಧ್ಯಾಹ್ನದಲ್ಲಿ ಸಂಜೆಯಂತೆ ಎಡವುತ್ತೇವೆ, ಪುಷ್ಟರ ನಡುವೆ ಸತ್ತವರ ಹಾಗಿದ್ದೇವೆ.",
			"11": "<sup>11</sup> ನಾವೆಲ್ಲರೂ ಕರಡಿಗಳಂತೆ ಗುರುಗುಟ್ಟುತ್ತೇವೆ, ಪಾರಿವಾಳಗಳಂತೆ ಮೂಲುಗುತ್ತಲೇ ಇದ್ದೇವೆ; ನಾವು [ಯೆಹೋವನ] ನ್ಯಾಯನಿರ್ಣಯವನ್ನು ಎದುರುನೋಡಿದರೂ ಅದು ಸಿಕ್ಕದು, [ಆತನ] ರಕ್ಷಣಕಾರ್ಯವನ್ನು ನಿರೀಕ್ಷಿಸಿದರೂ ಅದು ನಮಗೆ ದೂರ.",
			"12": "<sup>12</sup> ಏಕೆಂದರೆ ನಮ್ಮ ತಪ್ಪುಗಳು ನಿನ್ನ ದೃಷ್ಟಿಗೆ ಹೆಚ್ಚಾಗಿ ಬಿದ್ದಿವೆ, ನಮ್ಮ ಪಾಪಗಳು ನಮಗೆ ವಿರುದ್ಧವಾಗಿ ಸಾಕ್ಷಿಕೊಡುತ್ತವೆ; ನಮ್ಮ ತಪ್ಪುಗಳು ನಮ್ಮ ಸಂಗಡಲೇ ಇವೆ, ನಮ್ಮ ಅಪರಾಧಗಳನ್ನು ನಾವೇ ಬಲ್ಲೆವು.",
			"13": "<sup>13</sup> ಹೌದು, ಯೆಹೋವನಿಗೆ ತಪ್ಪಿ ನಡೆದು ಆತನನ್ನು ಅಲ್ಲಗಳೆದಿದ್ದೇವೆ, ನಮ್ಮ ದೇವರ ಅನುಸರಣೆಯನ್ನು ಬಿಟ್ಟು ತಿರುಗಿಕೊಂಡಿದ್ದೇವೆ, ಅನ್ಯಾಯವನ್ನೂ ದ್ರೋಹವನ್ನೂ ನುಡಿದಿದ್ದೇವೆ, ಹೃದಯಪೂರ್ವಕವಾಗಿ ಸುಳ್ಳುಮಾತುಗಳನ್ನು ಕಲ್ಪಿಸಿ ಆಡಿದ್ದೇವೆ.",
			"14": "<sup>14</sup> ನ್ಯಾಯವು ಹಿಂದಕ್ಕೆ ತಳ್ಳಲ್ಪಟ್ಟಿದೆ, ಧರ್ಮವು ದೂರದಲ್ಲಿ ನಿಂತಿದೆ, ಸತ್ಯವು ಚಾವಡಿಯಲ್ಲಿ ಬಿದ್ದುಹೋಗಿದೆ, ಯಥಾರ್ಥವು ಪ್ರವೇಶಿಸಲಾರದು.",
			"15": "<sup>15</sup> ಸತ್ಯವು ಇಲ್ಲವೇ ಇಲ್ಲ, ಕೇಡನ್ನು ಬಿಟ್ಟವನು ಸೂರೆಯಾಗುತ್ತಾನೆ.",
			"16": "<sup>16</sup> ಯೆಹೋವನು ಇದನ್ನು ನೋಡಿ ನ್ಯಾಯವಿಲ್ಲವಲ್ಲಾ ಎಂದು ವ್ಯಸನಗೊಂಡನು; ಉದ್ಧಾರಕನಾದ ಯಾವ ಪುರುಷನೂ ಇಲ್ಲವೆಂದು ಕಂಡು ಸ್ತಬ್ಧನಾದನು; ಆಗ ಆತನ ಸ್ವಹಸ್ತವೇ ಆತನಿಗೆ ರಕ್ಷಣಸಾಧನವಾಯಿತು, ಆತನ ಧರ್ಮವೇ ಆತನಿಗೆ ಆಧಾರವಾಯಿತು.",
			"17": "<sup>17</sup> ಧರ್ಮವನ್ನು ವಜ್ರ ಕವಚವನ್ನಾಗಿ ತೊಟ್ಟು ರಕ್ಷಣೆಯೆಂಬ ಶಿರಸ್ತ್ರಾಣವನ್ನು ಧರಿಸಿಕೊಂಡನು; ರೋಷವೆಂಬ ವಸ್ತ್ರವನ್ನು ಉಡುಪುಮಾಡಿಕೊಂಡು ಆಗ್ರಹವನ್ನು ನಿಲುವಂಗಿಯನ್ನಾಗಿ ಹಾಕಿಕೊಂಡನು.",
			"18": "<sup>18</sup> ತನ್ನ ವಿರೋಧಿಗಳಲ್ಲಿಟ್ಟ ಕ್ರೋಧವನ್ನು ಈಡೇರಿಸಿ ತನ್ನ ಶತ್ರುಗಳಿಗೆ ದಂಡನೆಯನ್ನು ವಿಧಿಸಿ ಅವರವರ ಕೃತ್ಯಗಳಿಗೆ ತಕ್ಕಂತೆ ಮುಯ್ಯಿತೀರಿಸುವನು; ಕರಾವಳಿಯವರಿಗೂ ಪ್ರತಿಕ್ರಿಯೆಮಾಡುವನು.",
			"19": "<sup>19</sup> ಹೀಗಿರಲು ಪಡುವಣವರು ಯೆಹೋವನ ನಾಮಕ್ಕೆ ಹೆದರುವರು, ಮೂಡಣವರು ಆತನ ಮಹಿಮೆಗೆ ಅಂಜುವರು; ಬಿರುಗಾಳಿಯಿಂದ ಹೊಡೆಯಲ್ಪಟ್ಟು ಇಕ್ಕಟ್ಟಿನಲ್ಲಿ ಹರಿಯುವ ತೊರೆಯ ಹಾಗೆ ಆತನು ರಭಸದಿಂದ ಬರುವನಷ್ಟೆ;",
			"20": "<sup>20</sup> ಆದರೆ ಚೀಯೋನಿಗೂ ದ್ರೋಹವನ್ನು ಬಿಟ್ಟುಬಿಟ್ಟ ಯಾಕೋಬ್ಯರ ಬಳಿಗೂ ವಿಮೋಚಕನಾಗಿ ಬರುವನು. ಯೆಹೋವನೇ ಇದನ್ನು ನುಡಿದಿದ್ದಾನೆ.",
			"21": "<sup>21</sup> ನಾನಂತೂ ಅವರೊಂದಿಗೆ ಈ ಒಡಂಬಡಿಕೆಯನ್ನು ಮಾಡಿಕೊಳ್ಳುವೆನು, ನೋಡಿರಿ; ನಿಮ್ಮಲ್ಲಿ ಆವೇಶಿಸಿರುವ ನನ್ನ ಆತ್ಮವೂ ನಿಮ್ಮ ಬಾಯಿಗೆ ನಾನು ಕೊಟ್ಟಿರುವ ಮಾತುಗಳೂ ನಿಮ್ಮ ಬಾಯಿಂದಾಗಲಿ ನಿಮ್ಮ ಸಂತತಿಯ ಬಾಯಿಂದಾಗಲಿ ನಿಮ್ಮ ಸಂತತಿಯ ಸಂತಾನದ ಬಾಯಿಂದಾಗಲಿ ಇಂದಿನಿಂದ ಎಂದಿಗೂ ತೊಲಗುವದಿಲ್ಲ ಎಂದು ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ."
		},
		"60": {
			"1": "<sup>1</sup> ಏಳು, ಪ್ರಕಾಶಿಸು, ನಿನಗೆ ಬೆಳಕು ಬಂತು, ಯೆಹೋವನ ತೇಜಸ್ಸು ನಿನ್ನ ಮೇಲೆ ಉದಯಿಸಿದೆ.",
			"2": "<sup>2</sup> ಇಗೋ, ಕತ್ತಲು ಭೂಮಿಯನ್ನು ಆವರಿಸಿದೆ, ಕಾರ್ಗತ್ತಲು ಜನಾಂಗಗಳನ್ನು ಮುಚ್ಚಿದೆ; ನಿನ್ನ ಮೇಲಾದರೋ ಯೆಹೋವನು ಉದಯಿಸುವನು, ಆತನ ತೇಜಸ್ಸು ನಿನ್ನಲ್ಲಿ ಕಾಣಿಸುವದು.",
			"3": "<sup>3</sup> ಜನಾಂಗಗಳು ನಿನ್ನ ಬೆಳಕಿಗೆ ನೆರೆಯುವವು, ಅರಸರು ನಿನ್ನಲ್ಲಿನ ಉದಯಪ್ರಕಾಶಕ್ಕೆ ಬರುವರು.",
			"4": "<sup>4</sup> ಕಣ್ಣೆತ್ತಿ ಸುತ್ತಲು ನೋಡು, ನಿನ್ನ ಮಕ್ಕಳೆಲ್ಲರೂ ಗುಂಪುಕೂಡಿ ನಿನ್ನ ಬಳಿಗೆ ಸೇರುತ್ತಿದ್ದಾರೆ; ಗಂಡು ಮಕ್ಕಳು ದೂರದಿಂದ ಸಮೀಪಿಸುತ್ತಾರೆ, ಹೆಣ್ಣುಮಕ್ಕಳು ಕಂಕುಳಿನಲ್ಲಿ ಕುಳಿತು ಬರುತ್ತಾರೆ.",
			"5": "<sup>5</sup> ನೀನು ನೋಡಿ ಕಳೆಗೊಳ್ಳುವಿ, ನಿನ್ನ ಹೃದಯವು ಅದರುತ್ತಾ ಉಬ್ಬುವದು; ಏಕಂದರೆ ಸಮುದ್ರವ್ಯಾಪಾರ ಸಮೃದ್ಧಿಯು ನಿನ್ನ ಕಡೆಗೆ ತಿರುಗುವದು, ಜನಾಂಗಗಳ ಐಶ್ವರ್ಯವು ನಿನಗೆ ದೊರೆಯುವದು.",
			"6": "<sup>6</sup> ಉಷ್ಟ್ರಸಮೂಹವೂ ಮಿದ್ಯಾನಿನ ಮತ್ತು ಏಫದ ಪ್ರಾಯದ ಒಂಟೆಗಳೂ ನಿನ್ನಲ್ಲಿ ತುಂಬಿರುವವು; ಆ ಸ್ವಾರ್ಥವಾಹರೆಲ್ಲಾ ಕನಕವನ್ನೂ ಧೂಪವನ್ನೂ ತೆಗೆದುಕೊಂಡು ಶೆಬದಿಂದ ಬಂದು ಯೆಹೋವನ ಸ್ತುತ್ಯಕೃತ್ಯಗಳನ್ನು ಸಾರುವರು.",
			"7": "<sup>7</sup> ಕೇದಾರಿನ ಹಿಂಡುಗಳೆಲ್ಲಾ ನಿನ್ನಲ್ಲಿ ಕೂಡುವವು; ನೆಬಾಯೋತಿನ ಟಗರುಗಳು ನೀನು ಮಾಡುವ ಯಜ್ಞಕ್ಕೆ ಅನುಕೂಲಿಸಿ ನನ್ನ ಯಜ್ಞವೇದಿಯ ಮೇಲೆ ಸಮರ್ಪಕವಾಗಿ ಒಯ್ಯಲ್ಪಡುವವು; ನನ್ನ ಸುಂದರಾಲಯವನ್ನು ಚಂದಗೊಳಿಸುವೆನು.",
			"8": "<sup>8</sup> ಆಹಾ, ದ್ವೀಪನಿವಾಸಿಗಳೂ ನನ್ನನ್ನು ನಿರೀಕ್ಷಿಸುವವರಾಗಿದ್ದಾರೆ! ಮೇಘದೋಪಾದಿಯಲ್ಲಿಯೂ ಗೂಡುಗಳಿಗೆ ತ್ವರೆಪಡುವ ಪಾರಿವಾಳಗಳಂತೆಯೂ ಹಾರಿಬರುತ್ತಿರುವ ಇವರು ಯಾರು?",
			"9": "<sup>9</sup> ಯೆಹೋವನು ನಿನ್ನನ್ನು ವೈಭವಪಡಿಸಿರುವದನ್ನು ಕೇಳಿ ತಾರ್ಷೀಷಿನ ಹಡಗುಗಳು ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನ ನಾಮಮಹತ್ತಿನ ವಿಧಿಯೂ ಇಸ್ರಾಯೇಲಿನ ಸದಮಲಸ್ವಾಮಿಯ ಸನ್ನಿಧಿಯೂ ಆದ ಸ್ಥಾನಕ್ಕೆ ನಿನ್ನ ಮಕ್ಕಳನ್ನು ಅವರ ಬೆಳ್ಳಿ ಬಂಗಾರಗಳ ಸಮೇತ ದೂರದಿಂದ ತರುವದರಲ್ಲಿ ಮುಂದಾಗುತ್ತಿವೆ.",
			"10": "<sup>10</sup> ವಿದೇಶೀಯರು ನಿನ್ನ ಪೌಳಿಗೋಡೆಗಳನ್ನು ಕಟ್ಟುವರು, ಅವರ ಅರಸರು ನಿನ್ನನ್ನು ಸೇವಿಸುವರು; ನನ್ನ ಕೋಪದಿಂದ ನಿನ್ನನ್ನು ಹೊಡೆದುಬಿಟ್ಟು ನನ್ನ ಕೃಪೆಯಿಂದ ನಿನ್ನನ್ನು ಕರುಣಿಸಿದ್ದೇನಷ್ಟೆ.",
			"11": "<sup>11</sup> ಜನಾಂಗಗಳ ಐಶ್ವರ್ಯವನ್ನು ನಿನ್ನಲ್ಲಿಗೆ ತರುತ್ತಿರುವರು, ಅವುಗಳ ಅರಸರು ಮೆರವಣಿಗೆಯಾಗಿ ಬರುತ್ತಿರುವರು; ಇದರಿಂದ ನಿನ್ನ ಬಾಗಿಲುಗಳು ಹಗಲಿರುಳೂ ಮುಚ್ಚದೆ ಸದಾ ತೆರೆದಿರುವವು.",
			"12": "<sup>12</sup> ನಿನ್ನನ್ನು ಸೇವಿಸಲೊಲ್ಲದ ಜನಾಂಗ ರಾಜ್ಯಗಳು ನಾಶವಾಗುವವು; ಹೌದು, ಆ ಜನಾಂಗಗಳು ಹಾಳೇ ಹಾಳಾಗಿ ಹೋಗುವವು.",
			"13": "<sup>13</sup> ನನ್ನ ಪವಿತ್ರಾಲಯವನ್ನು ಭೂಷಿಸುವದಕ್ಕಾಗಿ ತುರಾಯಿ, ತಪಸಿ, ತಿಲಕ ಈ ವೃಕ್ಷರೂಪವಾದ ಲೆಬನೋನಿನ ಮಹಿಮೆಯು ನಿನಗಾಗುವದು; ನನ್ನ ಪಾದಸನ್ನಿಧಿಯ ಮಂದಿರವನ್ನು ವೈಭವಪಡಿಸುವೆನು.",
			"14": "<sup>14</sup> ನಿನ್ನನ್ನು ಕುಗ್ಗಿಸಿದವರ ಸಂತಾನದವರು ನಿನ್ನ ಬಳಿಗೆ ತಗ್ಗಿಬಗ್ಗಿ ಬರುವರು; ನಿನ್ನನ್ನು ಅಸಡ್ಡೆಮಾಡಿದವರೆಲ್ಲರೂ ನಿನ್ನ ಕಾಲಕೆಳಗೆ ಅಡ್ಡಬಿದ್ದು ನೀನು ಯೆಹೋವನ ಪಟ್ಟಣ, ಇಸ್ರಾಯೇಲಿನ ಸದಮಲಸ್ವಾಮಿಯ ಚೀಯೋನ್ ಎಂದು ಕೊಂಡಾಡುವರು.",
			"15": "<sup>15</sup> ನಿವಾಸಿಗಳು ಬಿಟ್ಟು ಬಿಟ್ಟ, ದ್ವೇಷಕ್ಕೀಡಾದ, ಯಾರೂ ಹಾದು ಹೋಗದ ನಿನಗೆ ನಾನು ನಿತ್ಯಶ್ರೇಷ್ಠತೆಯನ್ನು ದಯಪಾಲಿಸಿ ನಿನ್ನನ್ನು ತಲತಲಾಂತರಗಳವರಿಗೆ ಉಲ್ಲಾಸಕರವಾಗುವಂತೆ ಮಾಡುವೆನು.",
			"16": "<sup>16</sup> ನೀನು ಜನಾಂಗಗಳ ಮೊಲೆಕೂಸಾಗುವಿ, ರಾಜರು ನಿನಗೆ ಮೊಲೆಯೂಡಿಸುವರು; ಆಗ ಯೆಹೋವನಾದ ನಾನೇ ನಿನ್ನ ರಕ್ಷಕನು, ನಿನ್ನ ವಿಮೋಚಕನು, ಯಾಕೋಬ್ಯರ ಶೂರನು ಎಂದು ನೀನು ತಿಳಿದುಕೊಳ್ಳುವಿ.",
			"17": "<sup>17</sup> ತಾಮ್ರಕ್ಕೆ ಬದಲಾಗಿ ಚಿನ್ನವನ್ನು, ಕಬ್ಬಿಣಕ್ಕೆ ಪ್ರತಿಯಾಗಿ ಬೆಳ್ಳಿಯನ್ನು, ಮರವಿದ್ದಲ್ಲಿ ತಾಮ್ರವನ್ನು, ಕಲ್ಲುಗಳ ಸ್ಥಾನದಲ್ಲಿ ಕಬ್ಬಿಣವನ್ನು ಒದಗಿಸುವೆನು; ಸಮಾಧಾನವನ್ನು ನಿನಗೆ ಅಧಿಪತಿಯನ್ನಾಗಿ ಧರ್ಮವನ್ನು ನಿನಗೆ ಅಧಿಕಾರಿಯನ್ನಾಗಿ ನೇಮಿಸುವೆನು.",
			"18": "<sup>18</sup> ನಿನ್ನ ದೇಶದಲ್ಲಿ ಬಲಾತ್ಕಾರದ ಸುದ್ದಿಯೇ ಕಿವಿಗೆ ಬೀಳದು, ನಿನ್ನ ಪ್ರಾಂತಗಳೊಳಗೆ ಭಂಗನಾಶನಗಳ ವಾರ್ತೆಯೇ ಕೇಳಬರುವದಿಲ್ಲ; ಆಗ ದೇವರಕ್ಷಣೆಯು ನನ್ನ ಪೌಳಿಗೋಡೆಗಳು, ದೇವಸ್ತೋತ್ರವು ನನ್ನ ಬಾಗಿಲುಗಳು ಎಂದು ಅಂದುಕೊಳ್ಳುವಿ.",
			"19": "<sup>19</sup> ಇನ್ನು ಮೇಲೆ ಹಗಲಿನಲ್ಲಿ ನಿನಗಿರುವ ಪ್ರಕಾಶವು ಸೂರ್ಯನದಲ್ಲ; ಚಂದ್ರನು ನಿನ್ನ ಬೆಳಕಿಗಾಗಿ ಎಂದಿಗೂ ಕಳೆಗೊಳ್ಳನು; ಯೆಹೋವನೇ ನಿನಗೆ ನಿತ್ಯಪ್ರಕಾಶವು, ನಿನ್ನ ದೇವರೇ ನಿನಗೆ ತೇಜಸ್ಸು.",
			"20": "<sup>20</sup> ನಿನ್ನ ಸೂರ್ಯನು ಇನ್ನು ಮುಣುಗನು, ನಿನ್ನ ಚಂದ್ರನು ತೊಲಗನು; ಯೆಹೋವನೇ ನಿನಗೆ ನಿತ್ಯಪ್ರಕಾಶವಾಗಿರುವನು, ನೀನು ದುಃಖಿಸುವ ದಿನಗಳು ಕೊನೆಗಾಣುವವು.",
			"21": "<sup>21</sup> ನಿನ್ನ ಜನರೆಲ್ಲಾ ಸದ್ಧರ್ಮಿಗಳಾಗಿರುವರು; ನನ್ನ ಪ್ರಭಾವಕ್ಕೋಸ್ಕರ ನಾನು ನೆಟ್ಟ ಸಸಿಯಾಗಿಯೂ ನನ್ನ ಕೈ ಸೃಷ್ಟಿಸಿದ ಪ್ರಜೆಯಾಗಿಯೂ ದೇಶವನ್ನು ಸದಾ ಅನುಭವಿಸುವರು.",
			"22": "<sup>22</sup> ಚಿಕ್ಕವನಿಂದ ಒಂದು ಕುಲವಾಗುವದು, ಅಲ್ಪನಿಂದ ಬಲವಾದ ಜನಾಂಗವಾಗುವದು; ಯೆಹೋವನೆಂಬ ನಾನು ಕ್ಲುಪ್ತಕಾಲದಲ್ಲಿ ಇದನ್ನು ಬಲುಬೇಗನೆ ಉಂಟುಮಾಡುವೆನು."
		},
		"61": {
			"1": "<sup>1</sup> ಕರ್ತನಾದ ಯೆಹೋವನ ಆತ್ಮವು ನನ್ನ ಮೇಲೆ ಅದೆ, ಆತನು ನನ್ನನ್ನು ಬಡವರಿಗೆ ಶುಭವರ್ತಮಾನವನ್ನು ಸಾರುವದಕ್ಕೆ ಅಭಿಷೇಕಿಸಿದನು; ಮನಮುರಿದವರನ್ನು ಕಟ್ಟಿ ವಾಸಿಮಾಡುವದಕ್ಕೂ ಸೆರೆಯವರಿಗೆ ಬಿಡುಗಡೆಯಾಗುವದನ್ನು, ಬಂದಿಗಳಿಗೆ [ಕದ] ತೆರೆಯುವದನ್ನು ಪ್ರಸಿದ್ಧಿಪಡಿಸುವದಕ್ಕೂ",
			"2": "<sup>2</sup> ಯೆಹೋವನು ನೇಮಿಸಿರುವ ಶುಭವರುಷ, ನಮ್ಮ ದೇವರು ಮುಯ್ಯಿ ತೀರಿಸುವ ದಿನ, ಇವುಗಳನ್ನು ಪ್ರಚುರಗೊಳಿಸುವದಕ್ಕೂ",
			"3": "<sup>3</sup> ದುಃಖಿತರೆಲ್ಲರನ್ನು ಸಂತೈಸುವದಕ್ಕೂ ಚೀಯೋನಿನಲ್ಲಿ ಶೋಕಿಸುವವರಿಗೆ ಬೂದಿಗೆ ಬದಲಾಗಿ ಶಿರೋಭೂಷಣ, ದುಃಖವಿದ್ದಲ್ಲಿ ಆನಂದತೈಲ, ಕುಂದಿದ ಮನಕ್ಕೆ ಪ್ರತಿಯಾಗಿ ಉತ್ಸಾಹಸ್ತೋತ್ರದ ವಸ್ತ್ರ ಇವುಗಳನ್ನು ಒದಗಿಸಿಕೊಡುವದಕ್ಕೂ ಆತನು ನನ್ನನ್ನು ಕಳುಹಿಸಿದ್ದಾನೆ. ಯೆಹೋವನು ತನ್ನ ಪ್ರಭಾವಕ್ಕೋಸ್ಕರ ಹಾಕಿದ ನೀತಿವೃಕ್ಷಗಳು ಎಂಬ ಬಿರುದು ಇವರಿಗಾಗುವದು.",
			"4": "<sup>4</sup> ಪುರಾತನ ಕಾಲದಲ್ಲಿ ಹಾಳಾದ ನಿವೇಶನಗಳನ್ನು ತಿರಿಗಿ ಕಟ್ಟುವರು, ಪೂರ್ವದಲ್ಲಿ ಪಾಳುಬಿದ್ದದ್ದನ್ನು ಪುನಃ ಎಬ್ಬಿಸುವರು, ತಲತಲಾಂತರಗಳಿಂದ ಹಾಳುಬೀಳಾದ ಪಟ್ಟಣಗಳನ್ನು ಜೀರ್ಣೋದ್ಧಾರಮಾಡುವರು.",
			"5": "<sup>5</sup> ಆಗ ವಿದೇಶೀಯರು ನಿಂತುಕೊಂಡು ನಿಮ್ಮ ಮಂದೆಗಳನ್ನು ಮೇಯಿಸುವರು, ಅನ್ಯರು ನಿಮಗೆ ಉಳುವವರೂ ತೋಟಗಾರರೂ ಆಗುವರು.",
			"6": "<sup>6</sup> ನೀವೋ ಯೆಹೋವನ ಯಾಜಕರೆಂಬ ಬಿರುದನ್ನು ಹೊಂದುವಿರಿ, ಜನರು ನಿಮ್ಮನ್ನು ನಮ್ಮ ದೇವರ ಸೇವಕರು ಎಂದು ಕರೆಯುವರು; ಜನಾಂಗಗಳ ಆಸ್ತಿಯನ್ನನುಭವಿಸುವಿರಿ, ಅವುಗಳ ವೈಭವವನ್ನು ಪಡೆದು ಹೊಗಳಿಕೊಳ್ಳುವಿರಿ.",
			"7": "<sup>7</sup> ನಿಮಗಾದ ಅವಮಾನಕ್ಕೆ ಬದಲಾಗಿ ಎರಡರಷ್ಟು ಮಾನವನ್ನು ಹೊಂದುವಿರಿ; ನಾಚಿಕೆಪಟ್ಟದ್ದಕ್ಕೆ ಪ್ರತಿಯಾಗಿ ಇವರು ತಮ್ಮ ಸ್ವಾಸ್ತ್ಯದಲ್ಲಿ ಹಿಗ್ಗುವರು; ಹೀಗೆ ತಮ್ಮ ದೇಶದಲ್ಲಿ ಎರಡರಷ್ಟನ್ನು ಅನುಭವಿಸುವರು; ಇವರಿಗೆ ಶಾಶ್ವತ ಸಂತೋಷವಾಗುವದು.",
			"8": "<sup>8</sup> ಯೆಹೋವನೆಂಬ ನಾನು ನ್ಯಾಯವನ್ನು ಪ್ರೀತಿಸುತ್ತೇನೆ, ಕೊಳ್ಳೆಯನ್ನೂ ಅನ್ಯಾಯವನ್ನೂ ದ್ವೇಷಿಸುತ್ತೇನೆ; ಸತ್ಯಸಂಧತೆಯನ್ನು ಅನುಸರಿಸಿ ಇವರ ನಷ್ಟಕ್ಕೆ ಪ್ರತಿಫಲವನ್ನು ಕೊಟ್ಟು ಇವರೊಂದಿಗೆ ನಿತ್ಯವಾದ ಒಡಂಬಡಿಕೆಯನ್ನು ಮಾಡಿಕೊಳ್ಳುವೆನು.",
			"9": "<sup>9</sup> ಇವರ ಸಂತಾನವು ಜನಾಂಗಗಳಲ್ಲಿ ಪ್ರಖ್ಯಾತವಾಗುವದು, ಇವರ ಸಂತತಿಯು ಅನ್ಯದೇಶೀಯರಲ್ಲಿ ಹೆಸರುಗೊಳ್ಳುವದು; ಇವರನ್ನು ನೋಡುವವರೆಲ್ಲರು ಯೆಹೋವನು ಆಶೀರ್ವದಿಸಿದ ವಂಶವು ಇದೇ ಎಂದು ಒಪ್ಪಿಕೊಳ್ಳುವರು.",
			"10": "<sup>10</sup> ನಾನು ಯೆಹೋವನಲ್ಲಿ ಪರಮಾನಂದಪಡುವೆನು, ನನ್ನ ಆತ್ಮವು ನನ್ನ ದೇವರಲ್ಲಿ ಹಿಗ್ಗುವದು; ವರನು ಬಾಸಿಂಗವನ್ನು ಧರಿಸಿಕೊಳ್ಳುವಂತೆಯೂ ವಧುವು ತನ್ನನ್ನು ಆಭರಣಗಳಿಂದ ಅಲಂಕರಿಸಿಕೊಳ್ಳುವ ಹಾಗೂ ಆತನು ನನಗೆ ರಕ್ಷಣೆಯೆಂಬ ವಸ್ತ್ರವನ್ನು ಹೊದಿಸಿ ಧರ್ಮವೆಂಬ ನಿಲುವಂಗಿಯನ್ನು ತೊಡಿಸಿದ್ದಾನಲ್ಲಾ.",
			"11": "<sup>11</sup> ಭೂಮಿಯು ತನ್ನೊಳಗಿಂದ ಮೊಳಿಕೆಯನ್ನು ಹೊರಡಿಸುವಂತೆಯೂ ತೋಟವು ತನ್ನಲ್ಲಿ ಬಿತ್ತಿದ್ದನ್ನು ಮೊಳೆಯಿಸುವ ಹಾಗೂ ಕರ್ತನಾದ ಯೆಹೋವನು ಎಲ್ಲಾ ಜನಾಂಗಗಳ ಎದುರಿನಲ್ಲಿ ಧರ್ಮವನ್ನೂ ಸ್ತೋತ್ರವನ್ನೂ ಮೊಳೆಯಿಸುವನು."
		},
		"62": {
			"1": "<sup>1</sup> ಚೀಯೋನಿನ ಧರ್ಮವು ಪ್ರಕಾಶಗೊಳ್ಳುವ ತನಕ, ಯೆರೂಸಲೇಮಿನ ರಕ್ಷಣೆಯು ದೀವಿಟಿಗೆಯಂತೆ ಪ್ರಜ್ವಲಿಸುವವರೆಗೆ ನಾನು ಮೌನವಾಗಿರದೆ ಚೀಯೋನಿನ ಹಿತವನ್ನು ಲಕ್ಷಿಸುತ್ತಿರುವೆನು; ಸುಮ್ಮನಿರದೆ ಯೆರೂಸಲೇಮಿನ ಕ್ಷೇಮವನ್ನು ಚಿಂತಿಸುತ್ತಿರುವೆನು.",
			"2": "<sup>2</sup> ಜನಾಂಗಗಳು ನಿನ್ನ ಧರ್ಮವನ್ನು, ಸಕಲ ರಾಜರು ನಿನ್ನ ವೈಭವವನ್ನು ನೋಡುವರು; ಯೆಹೋವನ ಬಾಯಿ ನೇಮಿಸಿದ ಹೊಸ ಹೆಸರು ನಿನಗೆ ದೊರೆಯುವದು.",
			"3": "<sup>3</sup> ನೀನು ಯೆಹೋವನ ಕೈಯಲ್ಲಿ ಸುಂದರ ಕಿರೀಟವಾಗಿಯೂ ನಿನ್ನ ದೇವರ ಹಸ್ತದಲ್ಲಿ ರಾಜಶಿರೋವೇಷ್ಟನವಾಗಿಯೂ ಇರುವಿ.",
			"4": "<sup>4</sup> ನೀನು ಇನ್ನು ಮೇಲೆ ಗಂಡಬಿಟ್ಟವಳು ಎನಿಸಿಕೊಳ್ಳೆ, ನಿನ್ನ ಸೀಮೆಗೆ ಬಂಜೆ ಎಂಬ ಹೆಸರು ಇನ್ನಿರದು; ನೀನು ಎನ್ನುಲ್ಲಾಸಿನಿ ಎನಿಸಿಕೊಳ್ಳುವಿ, ನಿನ್ನ ಸೀಮೆಗೆ ವಿವಾಹಿತೆ ಎಂಬ ಹೆಸರಾಗುವದು; ಏಕಂದರೆ ಯೆಹೋವನು ನಿನ್ನಲ್ಲಿ ಉಲ್ಲಾಸಗೊಳ್ಳುತ್ತಾನೆ, ನಿನ್ನ ಸೀಮೆಗೆ ವಿವಾಹವಾಗುವದು,",
			"5": "<sup>5</sup> ಯುವಕನು ಯುವತಿಯನ್ನು ವರಿಸುವಂತೆ ನಿನ್ನ ಮಕ್ಕಳು ನಿನ್ನನ್ನು ವರಿಸುವರು; ವರನು ವಧುವಿನಲ್ಲಿ ಆನಂದಿಸುವ ಹಾಗೆ ನಿನ್ನ ದೇವರು ನಿನ್ನಲ್ಲಿ ಆನಂದಿಸುವನು.",
			"6": "<sup>6</sup> ಯೆರೂಸಲೇಮೇ, ನಾನು ನಿನ್ನ ಪೌಳಿಗೋಡೆಗಳಲ್ಲಿ ಕಾವಲುಗಾರರನ್ನು ನೇಮಿಸಿದ್ದೇನೆ; ಅವರು ಹಗಲೂ ಇರುಳೂ ಮೌನವಾಗಿರರು.",
			"7": "<sup>7</sup> ಯೆಹೋವನಿಗೆ ಜ್ಞಾಪಿಸುವವರೇ, ಆತನು ಯೆರೂಸಲೇಮನ್ನು ಭದ್ರಪಡಿಸಿ ಲೋಕಪ್ರಸಿದ್ಧಿಗೆ ತರುವ ತನಕ ನಿಮಗೆ ವಿರಾಮವಿಲ್ಲದಿರಲಿ, ಆತನಿಗೂ ವಿರಾಮಕೊಡದಿರಿ.",
			"8": "<sup>8</sup> ಯೆಹೋವನು ತನ್ನ ಬಲಗೈ ಮೇಲೆ, ತನ್ನ ಭುಜಬಲದ ಮೇಲೆ, ಆಣೆಯಿಟ್ಟು - ನಿನ್ನ ದವಸವನ್ನು ನಿನ್ನ ಶತ್ರುಗಳ ಆಹಾರಕ್ಕೆ ಇನ್ನು ಕೊಡಲೇ ಕೊಡೆನು. ನೀನು ದುಡಿದ ದ್ರಾಕ್ಷಾರಸವನ್ನು ಅನ್ಯರು ಕುಡಿಯುವದೇ ಇಲ್ಲ;",
			"9": "<sup>9</sup> ನಿನ್ನ ಬೆಳೆಯನ್ನು ಕೊಯ್ದು ಕುಪ್ಪೆ ಹಾಕಿದವರೇ ಅದನ್ನು ಉಂಡು ಯೆಹೋವನನ್ನು ಸ್ತುತಿಸುವರು. ನಿನ್ನ ದ್ರಾಕ್ಷೆಯನ್ನು ಕಿತ್ತು ತಂದವರೇ ಅದರ ರಸವನ್ನು ನನ್ನ ಪವಿತ್ರಾಲಯದ ಪ್ರಾಕಾರಗಳಲ್ಲಿ ಕುಡಿಯುವರು ಎಂದು ಹೇಳಿದ್ದಾನೆ.",
			"10": "<sup>10</sup> ಊರ ಬಾಗಿಲುಗಳಲ್ಲಿ ಹಾದು ಬನ್ನಿರಿ, ಹಾದು ಬನ್ನಿರಿ, ಜನರಿಗೆ ದಾರಿಯನ್ನು ಸರಿಮಾಡಿರಿ, ರಾಜಮಾರ್ಗವನ್ನು ಎತ್ತರಿಸಿರಿ, ಎತ್ತರಿಸಿರಿ, ಕಲ್ಲುಗಳನ್ನು ತೆಗೆದುಹಾಕಿರಿ, ಜನಾಂಗಗಳಿಗೆ ಧ್ವಜವನ್ನೆತ್ತಿರಿ!",
			"11": "<sup>11</sup> ಇಗೋ, ನಿನ್ನ ರಕ್ಷಣೆಯು ಸಮೀಪವಾಯಿತು, ಯೆಹೋವನು ದಯಪಾಲಿಸುವ ಬಹುಮಾನವು ಆತನೊಂದಿಗಿದೆ, ಆತನು ಅನುಗ್ರಹಿಸುವ ಪ್ರತಿಫಲವು ಆತನ ಮುಂದಿದೆ ಎಂದು ಚೀಯೋನೆಂಬಾಕೆಗೆ ಹೇಳಿರಿ ಎಂಬದಾಗಿ ಯೆಹೋವನು ಭೂಮಿಯ ಕಟ್ಟಕಡೆಯವರೆಗೂ ಅಪ್ಪಣೆಮಾಡಿದ್ದಾನೆ.",
			"12": "<sup>12</sup> ನಿನ್ನ ಜನರು ಪರಿಶುದ್ಧಜನರು, ಯೆಹೋವನು ವಿಮೋಚಿಸಿದವರು ಎಂದು ಅನ್ನಿಸಿಕೊಳ್ಳುವರು, ನಿನಗೋ ಪತಿಯು ವರಿಸಿ ತ್ಯಜಿಸದ ಪುರಿ ಎಂದು ಹೆಸರು ಬರುವದು."
		},
		"63": {
			"1": "<sup>1</sup> ಆಹಾ, ರಕ್ತವರ್ಣದ ಉಡುಪನ್ನುಟ್ಟು ಘನವಸ್ತ್ರಗಳನ್ನು ಧರಿಸಿಕೊಂಡು ಮಹಾಶೌರ್ಯಯುಕ್ತನಾಗಿ ಮೆರೆಯುತ್ತಾ ಎದೋಮಿನ ಬೊಚ್ರದಿಂದ ಬರುವ ಈತನು ಯಾರು? ಸತ್ಯಾನುಸಾರವಾಗಿ ಮಾತಾಡುವ, ರಕ್ಷಿಸಲು ಸಮರ್ಥನಾದ ನಾನೇ.",
			"2": "<sup>2</sup> ನಿನ್ನ ಉಡುಪು ಏಕೆ ಕೆಂಪಾಗಿದೆ, ನಿನ್ನ ವಸ್ತ್ರಗಳು ದ್ರಾಕ್ಷೆಯನ್ನು ತುಳಿಯುವವನ ಬಟ್ಟೆಯ ಹಾಗಿರುವದು ಏಕೆ?",
			"3": "<sup>3</sup> ನಾನೊಬ್ಬನಾಗಿಯೇ ದ್ರಾಕ್ಷೆಯನ್ನು ತೊಟ್ಟಿಯಲ್ಲಿ ತುಳಿದಿದ್ದೇನೆ, ಜನಾಂಗದವರಲ್ಲಿ ಯಾರೂ ನನ್ನೊಂದಿಗಿರಲಿಲ್ಲ. ನನ್ನ ಕೋಪದಿಂದ ಶತ್ರುಗಳನ್ನು ತುಳಿದೆನು, ರೋಷವೇರಿದವನಾಗಿ ಅವರನ್ನು ಕಾಲಿನಿಂದ ಹೊಸಗಿದೆನು; ಅವರ ಸಾರವು ನನ್ನ ವಸ್ತ್ರಗಳ ಮೇಲೆ ಸಿಡಿದಿದೆ, ನನ್ನ ಉಡುಪನ್ನೆಲ್ಲಾ ಮಾಸಿಕೊಂಡೆನು.",
			"4": "<sup>4</sup> ಏಕಂದರೆ ಮುಯ್ಯಿ ತೀರಿಸುವ ದಿನವು ನನ್ನ ಹೃದಯದಲ್ಲಿ ಸಿದ್ಧವಾಗಿತ್ತು, ನನ್ನ ಜನರನ್ನು ವಿಮೋಚಿಸುವ ವರುಷವು ಒದಗಿತ್ತು.",
			"5": "<sup>5</sup> ನಾನು ನೋಡಲು ಸಹಾಯಕರು ಯಾರೂ ಇರಲಿಲ್ಲ, ಯಾವ ಬೆಂಬಲವೂ ಇಲ್ಲದ್ದನ್ನು ಕಂಡು ಸ್ತಬ್ಧನಾದೆನು; ಆಗ ನನ್ನ ಸ್ವಹಸ್ತವೇ ನನಗೆ ರಕ್ಷಣ ಸಾಧನವಾಯಿತು, ನನ್ನ ರೌದ್ರವೇ ನನಗೆ ಆಧಾರವಾಯಿತು.",
			"6": "<sup>6</sup> ನನ್ನ ಕೋಪದಿಂದ ಜನಾಂಗಗಳನ್ನು ತುಳಿದು ರೋಷವೇರಿದವನಾಗಿ ಅವುಗಳನ್ನು ಚೂರುಚೂರು ಮಾಡಿ ನೆಲದ ಮೇಲೆ ಅವುಗಳ ಸಾರವನ್ನು ಸುರಿಸಿದೆನು.",
			"7": "<sup>7</sup> ಯೆಹೋವನು ನಮಗೆ ಅನುಗ್ರಹಿಸಿದ್ದನ್ನೆಲ್ಲಾ ಸ್ಮರಿಸಿ ಆತನ ಕೃಪಾಕಾರ್ಯಗಳನ್ನೂ ಸ್ತುತ್ಯಕೃತ್ಯಗಳನ್ನೂ ಆತನು ಕನಿಕರದಿಂದಲೂ ಕೃಪಾತಿಶಯದಿಂದಲೂ ಇಸ್ರಾಯೇಲ್ ವಂಶದವರಿಗೆ ದಯಪಾಲಿಸಿರುವ ಮಹೋಪಕಾರವನ್ನೂ ಪ್ರಸಿದ್ಧಿಪಡಿಸುವೆನು.",
			"8": "<sup>8</sup> ನಿಜವಾಗಿ ಇವರು ನನ್ನ ಜನರು, ನನ್ನ ಮಕ್ಕಳು, ಮೋಸ ಮಾಡಲಾರರು ಎಂದು ಆತನು ಅಂದುಕೊಂಡು ಅವರಿಗೆ ರಕ್ಷಕನಾದನು.",
			"9": "<sup>9</sup> ಅವರು ಶ್ರಮೆಪಡುತ್ತಿರುವಾಗೆಲ್ಲಾ ಆತನೂ ಶ್ರಮೆಪಟ್ಟನು; ಆತನ ಶ್ರೀಮುಖಸ್ವರೂಪದೂತನು ಅವರನ್ನು ರಕ್ಷಿಸಿದನು; ತನ್ನಲ್ಲಿನ ಮಮತೆಯಿಂದಲೂ ತಾಳ್ಮೆಯಿಂದಲೂ ಅವರನ್ನು ವಿಮೋಚಿಸಿ ಪುರಾತನಕಾಲದಲ್ಲೆಲ್ಲಾ ಎತ್ತಿಕೊಂಡು ಹೊರುತ್ತಾ ಬಂದನು.",
			"10": "<sup>10</sup> ಅವರಾದರೋ ಎದುರುಬಿದ್ದು ಆತನ ಪವಿತ್ರಾತ್ಮನನ್ನು ದುಃಖಪಡಿಸಿದರು; ಆದದರಿಂದ ಆತನು ಮಾರ್ಪಟ್ಟು ಅವರಿಗೆ ಶತ್ರುವಾಗಿ ತಾನೇ ಅವರೊಡನೆ ಹೋರಾಡಿದನು.",
			"11": "<sup>11</sup> ಆಗ ಆತನ ಜನರು ಪುರಾತನವಾದ ಮೋಶೆಯ ಕಾಲವನ್ನು ಜ್ಞಾಪಕಮಾಡಿಕೊಂಡು ಹೀಗಂದರು - ತನ್ನ ಜನವೆಂಬ ಮಂದೆಯನ್ನು ಕುರುಬರ ಸಹಿತ ಸಮುದ್ರದೊಳಗಿಂದ ಮೇಲಕ್ಕೆ ಬರಮಾಡಿದಾತನು ಎಲ್ಲಿ?",
			"12": "<sup>12</sup> ಅವರ ಮಧ್ಯದಲ್ಲಿ ತನ್ನಪವಿತ್ರಾತ್ಮವನ್ನಿರಿಸಿ ಮೋಶೆಯ ಬಲಗೈಯೊಂದಿಗೆ ತನ್ನ ಘನಹಸ್ತವನ್ನೂ ಮುಂದರಿಸುತ್ತಾ ತನ್ನ ಹೆಸರು ಶಾಶ್ವತವಾಗಿರಬೇಕೆಂದು ಅವರೆದುರಿಗೆ ಜಲರಾಶಿಯನ್ನು ಭೇದಿಸಿ",
			"13": "<sup>13</sup> ಸಾಗರದ ಅಡಿಯಲ್ಲಿ ಅವರನ್ನು ಕುದುರೆಯು ಮೈದಾನದಲ್ಲಿ ನಡೆಯುವ ಪ್ರಕಾರ ಮುಗ್ಗರಿಸದಂತೆ ನಡೆಯಿಸಿದಾತನು ಎಲ್ಲಿ?",
			"14": "<sup>14</sup> ಯೆಹೋವನ ಆತ್ಮವು ತಗ್ಗಿಗೆ ಇಳಿಯುವ ದನಗಳೋಪಾದಿಯಲ್ಲಿ ಅವರನ್ನು ವಿಶ್ರಮಸ್ಥಾನಕ್ಕೆ ಕರತಂದಿತು; ನಿನ್ನ ನಾಮವನ್ನು ಘನಪಡಿಸಿಕೊಳ್ಳುವದಕ್ಕಾಗಿ ನಿನ್ನ ಜನರನ್ನು ಹೀಗೆ ನಡಿಸಿದಿ.",
			"15": "<sup>15</sup> ಆಕಾಶದಿಂದ ನೋಡು, ಪರಿಶುದ್ಧವೂ ಘನವೂ ಆದ ನಿನ್ನ ಉನ್ನತಸ್ಥಾನದಿಂದ ಲಕ್ಷಿಸು; ನಿನ್ನ ಆಗ್ರಹವೆಲ್ಲಿ, ನಿನ್ನ ಸಾಹಸಕಾರ್ಯಗಳೆಲ್ಲಿ? ನಿನ್ನ ಕರುಳ ಮರುಗಾಟವನ್ನೂ ನಿನ್ನ ಕನಿಕರವನ್ನೂ ನಮ್ಮ ಕಡೆಗೆ ಬಿಗಿಹಿಡಿದಿದ್ದೀ.",
			"16": "<sup>16</sup> ನೀನೇ ನಮ್ಮ ಪಿತೃವಾಗಿದ್ದೀಯಲ್ಲಾ; ಅಬ್ರಹಾಮನು ನಮ್ಮನ್ನರಿಯನು, ಇಸ್ರಾಯೇಲನು ನಮ್ಮನ್ನು ಗುರುತಿಸನು; ಯೆಹೋವಾ, ನೀನೇ ನಮ್ಮ ಪಿತೃ; ಆದಿಯಿಂದಲೂ ನಮ್ಮ ವಿಮೋಚಕನು ಅನ್ನಿಸಿಕೊಂಡಿದ್ದೀ.",
			"17": "<sup>17</sup> ಯೆಹೋವನೇ, ನಾವು ನಿನ್ನ ಮಾರ್ಗದಿಂದ ತಪ್ಪಿ ಅಲೆಯುವಂತೆ ಏಕೆ ಮಾಡುತ್ತೀ? ನಾವು ನಿನಗೆ ಭಯಪಡದ ಹಾಗೆ ನಮ್ಮ ಹೃದಯವನ್ನು ಕಠಿನ ಪಡಿಸುವದೇಕೆ? ನಿನ್ನ ಸೇವಕರ ನಿಮಿತ್ತ ನಿನ್ನ ಬಾಧ್ಯವಾದ ಕುಲಗಳಿಗಾಗಿ ತಿರುಗಿ ಪ್ರಸನ್ನನಾಗು,",
			"18": "<sup>18</sup> ನಿನ್ನ ಸ್ವಕೀಯಜನರು ಸ್ವಲ್ಪಕಾಲ ಮಾತ್ರ [ನಿನ್ನ ಸ್ವಾಸ್ತ್ಯವನ್ನು] ಅನುಭವಿಸುತ್ತಿದ್ದರು; ಈಗ ನಮ್ಮ ವೈರಿಗಳು ನಿನ್ನ ಪವಿತ್ರಾಲಯವನ್ನು ತುಳಿದುಬಿಟ್ಟಿದ್ದಾರೆ.",
			"19": "<sup>19</sup> ನಿನ್ನ ದೊರೆತನಕ್ಕೆ ಎಂದಿಗೂ ಒಳಪಡದೆ ನಿನ್ನ ನಾಮವನ್ನು ಧರಿಸದೆ ಇರುವ ಜನರ ಹಾಗಿದ್ದೇವೆ."
		},
		"64": {
			"1": "<sup>1</sup> ಆಹಾ, ನೀನು ಆಕಾಶವನ್ನು ಸೀಳಿ ಇಳಿದು ಬರಬಾರದೇ! ನಿನ್ನ ದರ್ಶನವು ಉಂಟಾಗಿ ಪರ್ವತಗಳು ಅಲುಗಿದರೆ ಎಷ್ಟೋ ಒಳ್ಳೇದು!",
			"2": "<sup>2</sup> ಒಣಗಿಡಕ್ಕೆ ಹತ್ತುವ ಕಿಚ್ಚಿನ ಪ್ರಕಾರವೂ ನೀರನ್ನು ಕುದಿಸುವ ಉರಿಯೋಪಾದಿಯಲ್ಲಿಯೂ [ನೀನು ಪ್ರತ್ಯಕ್ಷನಾಗಿ]",
			"3": "<sup>3</sup> ನಮ್ಮ ನಿರೀಕ್ಷೆಗೆ ಮೀರಿದ ಭಯಂಕರಕೃತ್ಯಗಳನ್ನು ನಡಿಸಿ ನಿನ್ನ ನಾಮಮಹತ್ತನ್ನು ನಿನ್ನ ಶತ್ರುಗಳಿಗೆ ತಿಳಿಯಪಡಿಸಿ ಜನಾಂಗಗಳನ್ನು ನಿನ್ನ ದರ್ಶನದಿಂದ ನಡುಗಿಸಬಾರದೇ! ಹೌದು, ನೀನು ಇಳಿದು ಬಂದು ಪರ್ವತಗಳು ನಿನ್ನ ದರ್ಶನದಿಂದ ಅಲುಗಿದರೆ ಎಷ್ಟೋ ಲೇಸು!",
			"4": "<sup>4</sup> ನಿನ್ನನ್ನು ಬಿಟ್ಟರೆ ನಿರೀಕ್ಷಿಸುವವನಿಗೆ ಕಾರ್ಯಕರ್ತನಾದ ಯಾವ ದೇವರನ್ನೂ ಆದಿಯಿಂದ ಯಾರೂ ಕೇಳಲಿಲ್ಲ, ಯಾರ ಕಿವಿಗೂ ಬೀಳಲಿಲ್ಲ, ಯಾರ ಕಣ್ಣೂ ಕಾಣಲಿಲ್ಲ.",
			"5": "<sup>5</sup> ಧರ್ಮವನ್ನು ಅನುಸರಿಸಲು ಸಂತೋಷಪಟ್ಟು ನಿನ್ನ ಮಾರ್ಗದಲ್ಲಿ ನಡೆಯುತ್ತಾ ನಿನ್ನನ್ನು ಸ್ಮರಿಸುವವರಿಗೆ ಪ್ರಸನ್ನನಾಗಿದ್ದೀ; ನಮ್ಮ ಮೇಲಾದರೋ ರೋಷಗೊಂಡಿದ್ದೀ; ಆದರೂ ನಾವು ಪಾಪವನ್ನು ನಡಿಸುತ್ತಾ ಬಂದೆವು; ಬಹುಕಾಲದಿಂದ ಪಾಪದಲ್ಲಿ ಮುಣುಗಿರುವ ನಮಗೆ ರಕ್ಷಣೆಯಾದೀತೇ?",
			"6": "<sup>6</sup> ನಾವೆಲ್ಲರು ಅಶುದ್ಧನ ಹಾಗಿದ್ದೇವೆ, ನಮ್ಮ ಧರ್ಮಕಾರ್ಯಗಳೆಲ್ಲಾ ಹೊಲೆಯ ಬಟ್ಟೆಯಂತಿವೆ; ನಾವೆಲ್ಲರೂ ತರಗೆಲೆಯೋಪಾದಿಯಲ್ಲಿ ಒಣಗಿ ಹೋಗಿದ್ದೇವೆ; ನಮ್ಮ ಅಪರಾಧಗಳು ಬಿರುಗಾಳಿಯ ಪ್ರಕಾರ ನಮ್ಮನ್ನು ಬಡಿದುಕೊಂಡು ಹೋಗಿವೆ.",
			"7": "<sup>7</sup> ಈಗ ನಿನ್ನ ಹೆಸರೆತ್ತಿ ಪ್ರಾರ್ಥಿಸಿ ನಿನ್ನನ್ನು ಆಶ್ರಯಿಸಲು ತ್ರಾಣ ತಂದುಕೊಳ್ಳುವವನು ಎಲ್ಲಿಯೂ ಇಲ್ಲ; ನೀನು ನಮಗೆ ವಿಮುಖನಾಗಿ ನಮ್ಮನ್ನು ನಮ್ಮ ಅಪರಾಧಗಳಿಗೆ ವಶಮಾಡಿದ್ದೀಯಲ್ಲಾ.",
			"8": "<sup>8</sup> ಈಗಲಾದರೋ ಯೆಹೋವನೇ, ನೀನು ನಮ್ಮ ತಂದೆಯಾಗಿದ್ದೀ; ನಾವು ಜೇಡಿಮಣ್ಣು, ನೀನು ಕುಂಬಾರ, ನಾವೆಲ್ಲರೂ ನಿನ್ನ ಕೈಕೆಲಸವೇ.",
			"9": "<sup>9</sup> ಯೆಹೋವನೇ, ಅತಿರೋಷಗೊಳ್ಳದಿರು, ನಮ್ಮ ಅಧರ್ಮವನ್ನು ಕಡೆಯವರೆಗೂ ಜ್ಞಾಪಕದಲ್ಲಿಡಬೇಡ; ಎಲೈ, ಸ್ವಾಮೀ, ಕಟಾಕ್ಷಿಸು, ನಾವೆಲ್ಲರೂ ನಿನ್ನ ಜನರಾಗಿದ್ದೇವೆ.",
			"10": "<sup>10</sup> ನಿನ್ನ ಪರಿಶುದ್ಧ ಪಟ್ಟಣಗಳು ಕಾಡಾಗಿವೆ, ಚೀಯೋನು ಬೀಡಾಗಿದೆ, ಯೆರೂಸಲೇಮು ಹಾಳಾಗಿಹೋಗಿದೆ.",
			"11": "<sup>11</sup> ನಮ್ಮ ಪಿತೃಗಳು ನಿನ್ನನ್ನು ಕೀರ್ತಿಸುತ್ತಿದ್ದ ನಮ್ಮ ಸುಂದರ ಪವಿತ್ರಾಲಯವನ್ನು ಬೆಂಕಿಯು ಸುಟ್ಟುಬಿಟ್ಟಿದೆ, ನಮ್ಮ ಅಮೂಲ್ಯವಸ್ತುಗಳೆಲ್ಲಾ ನಾಶವಾಗಿವೆ.",
			"12": "<sup>12</sup> ಯೆಹೋವನೇ, ಇವುಗಳನ್ನು ನೋಡಿಯೂ ನಿನ್ನನ್ನು ಬಿಗಿಹಿಡಿಯುವಿಯೋ? ಸುಮ್ಮನಿರುವಿಯೋ? ನಮ್ಮನ್ನು ಹೆಚ್ಚಾಗಿ ಕುಗ್ಗಿಸುವಿಯೋ?"
		},
		"65": {
			"1": "<sup>1</sup> ವಿಜ್ಞಾಪಿಸಿಕೊಳ್ಳದವರಿಗೂ ಪ್ರಸನ್ನನಾಗಿದ್ದೆನು, ನನ್ನನ್ನು ಹುಡುಕದವರಿಗೂ ಸಿಕ್ಕಿದೆನು, ನನ್ನ ನಾಮವನ್ನೆತ್ತಿ ಪ್ರಾರ್ಥಿಸದ ಜನಾಂಗಕ್ಕೂ - ಇಗೋ, ನಾನಿದ್ದೇನೆ ನಾನಿದ್ದೇನೆ ಎನ್ನುತ್ತಿದ್ದೆನು.",
			"2": "<sup>2</sup> ನನ್ನನ್ನು ತೊರೆದು ಮನಸ್ಸುಬಂದ ಹಾಗೆ ದುರ್ಮಾರ್ಗದಲ್ಲಿ ನಡೆಯುವ ಜನರನ್ನು ನಾನು ದಿನವೆಲ್ಲಾ ಕೈ ಚಾಚಿ ಕರೆದೆನು.",
			"3": "<sup>3</sup> ಈ ಜನರು ವನಗಳಲ್ಲಿ ಯಜ್ಞಮಾಡುತ್ತಾ ಇಟ್ಟಿಗೆಯ ವೇದಿಯ ಮೇಲೆ ಧೂಪಹಾಕುತ್ತಾ ಗೋರಿಗಳಲ್ಲಿ ಕೂತುಕೊಳ್ಳುತ್ತಾ",
			"4": "<sup>4</sup> ಗುಪ್ತಸ್ಥಳಗಳಲ್ಲಿ ರಾತ್ರಿಕಳೆಯುತ್ತಾ ಹಂದಿಯ ಬಾಡು ತಿನ್ನುತ್ತಾ ಅಸಹ್ಯಪದಾರ್ಥಗಳ ಸಾರನ್ನು ತಮ್ಮ ಗಂಗಳಗಳಲ್ಲಿ ಬಡಿಸಿಕೊಳ್ಳುತ್ತಾ ಅಲ್ಲಿ ನಿಂತಿರು,",
			"5": "<sup>5</sup> ಹತ್ತಿರ ಬರಬೇಡ, ನಾನು ಮಡಿವಂತನು, ನೀನು ಸೇರತಕ್ಕವನಲ್ಲ ಎನ್ನುತ್ತಾ ಅಂತು ಪ್ರತಿನಿತ್ಯವೂ ಮುಖದೆದುರಿಗೆ ನನ್ನನ್ನು ಕೆಣಕುತ್ತಾ ನನ್ನ ಮೂಗಿಗೆ ದಿನವೆಲ್ಲಾ ಉರಿಯುವ ಬೆಂಕಿಯ ಹೊಗೆಯಾಗಿದ್ದಾರೆ.",
			"6": "<sup>6</sup> ಇಗೋ, ಇದೆಲ್ಲಾ ಶಾಸನವಾಗಿ ನನ್ನ ಕಣ್ಣೆದುರಿಗಿದೆ, ನಾನು ಮುಯ್ಯಿತೀರಿಸುವ ತನಕ ಸುಮ್ಮನಿರಲಾರೆನು;",
			"7": "<sup>7</sup> ಇವರೂ ಇವರ ಪಿತೃಗಳೂ ಬೆಟ್ಟಗುಡ್ಡಗಳಲ್ಲಿ ಧೂಪಹಾಕಿ ನನ್ನನ್ನು ಹೀನೈಸಿ ನಡಿಸಿದ ಅಪರಾಧಗಳ ಪ್ರತಿಫಲವನ್ನು ಇವರ ಮಡಲಿಗೆ ಹಾಕುವೆನು; ಹೌದು, ಇವರ ಕಾರ್ಯದ ಫಲವನ್ನು ಅಳತೆಗೆ ಸರಿಯಾಗಿ ಮೊಟ್ಟಮೊದಲೇ ಇವರ ಮಡಲಿಗೆ ಸುರಿಯುವೆನು ಎಂದು ಯೆಹೋವನು ಹೇಳುತ್ತಾನೆ.",
			"8": "<sup>8</sup> ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ರಸ ದೊರೆಯಬಹುದಾದ ದ್ರಾಕ್ಷೆಯ ಗೊಂಚಲನ್ನು ಒಬ್ಬನು ನೋಡಿ - ಹಾಳುಮಾಡಬೇಡ, ಅದರಲ್ಲಿ ಪ್ರಯೋಜನವಿದೆ ಎನ್ನುವಂತೆ ನಾನು ನನ್ನ ಸೇವಕರನ್ನು ಲಕ್ಷ್ಯಕ್ಕೆ ತಂದು ಇವರನ್ನೆಲ್ಲಾ ಹಾಳುಮಾಡಬಾರದು ಎಂದು ಅಂದುಕೊಳ್ಳುವೆನು,",
			"9": "<sup>9</sup> ಯಾಕೋಬಿನಿಂದ ಸಂತಾನವನ್ನು ಹೊರಪಡಿಸುವೆನು, ಯೆಹೂದವಂಶದಿಂದ ನನ್ನ ಪರ್ವತಗಳ ಸ್ವಾಸ್ತ್ಯದ ಬಾಧ್ಯಸಂತತಿಯನ್ನು ಬರಮಾಡುವೆನು, ನನ್ನ ಆಪ್ತರು ಆ ಸ್ವಾಸ್ತ್ಯವನ್ನು ಅನುಭವಿಸುವರು, ನನ್ನ ಸೇವಕರು ಅಲ್ಲಿ ವಾಸಿಸುವರು.",
			"10": "<sup>10</sup> ಆಗ ನನ್ನ ಭಕ್ತಜನರ ಹಿತಕ್ಕಾಗಿ ಶಾರೋನು ಹಿಂಡುಗಳಿಗೆ ಹುಲ್ಲುಗಾವಲಾಗಿಯೂ ಆಕೋರಿನ ತಗ್ಗು ದನದ ಹಕ್ಕೆಯಾಗಿಯೂ ಇರುವವು.",
			"11": "<sup>11</sup> ಆದರೆ ಯೆಹೋವನಾದ ನನ್ನನ್ನು ತೊರೆದು ನನ್ನ ಪವಿತ್ರಪರ್ವತವನ್ನು ಮರೆತು ಶುಭದಾಯಕದೇವತೆಗೆ ಔತಣವನ್ನು ಅಣಿಮಾಡಿ ಗತಿದಾಯಕದೇವತೆಗೆ ಮದ್ಯವನ್ನು ತುಂಬಾ ಬೆರಸಿದ ನಿಮಗೆ ಕತ್ತಿಯನ್ನೇ ಗತಿಯಾಗಮಾಡುವೆನು.",
			"12": "<sup>12</sup> ನೀವೆಲ್ಲರೂ ಕೊಲೆಗೊಳಗಾಗಿ ಬೀಳುವಿರಿ; ಏಕಂದರೆ ನಾನು ಕೂಗಲು ನೀವು ಉತ್ತರಕೊಡಲಿಲ್ಲ. ನಾನು ಹೇಳಲು ನೀವು ಕೇಳಲಿಲ್ಲ; ನನ್ನ ಚಿತ್ತಕ್ಕೆ ವಿರುದ್ಧವಾದದ್ದನ್ನು ನಡಿಸಿ ನನಗೆ ಇಷ್ಟವಲ್ಲದ್ದನ್ನು ಆರಿಸಿಕೊಂಡಿರಿ.",
			"13": "<sup>13</sup> ಹೀಗಿರಲು ಕರ್ತನಾದ ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ಇಗೋ, ನನ್ನ ಸೇವಕರು ಊಟಮಾಡುವರು, ನೀವು ಹಸಿದಿರುವಿರಿ; ಇಗೋ, ನನ್ನ ಸೇವಕರು ಕುಡಿಯುವರು, ನೀವು ದಾಹಗೊಳ್ಳುವಿರಿ; ಇಗೋ, ನನ್ನ ಸೇವಕರು ಉಲ್ಲಾಸಗೊಳ್ಳುವರು, ನೀವು ಆಶಾಭಂಗಪಡುವಿರಿ.",
			"14": "<sup>14</sup> ಇಗೋ, ನನ್ನ ಸೇವಕರು ಹೃದಯಾನಂದದಿಂದ ಹರ್ಷಧ್ವನಿಗೈಯುವರು, ನೀವೋ ಮನೋವ್ಯಥೆಯಿಂದ ಮೊರೆಯಿಟ್ಟು, ಆತ್ಮಕ್ಲೇಶದಿಂದ ಗೋಳಾಡುವಿರಿ.",
			"15": "<sup>15</sup> ಕರ್ತನಾದ ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಕೊಂದುಹಾಕುವನು, ನಿಮ್ಮ ಹೆಸರು ನನ್ನ ಆಪ್ತಜನರು ಶಪಿಸುವ ಶಾಪದ ಮಾತಾಗಿಯೇ ಉಳಿಯುವದು; ನಾನು ನನ್ನ ಸೇವಕರಿಗೆ ಹೊಸ ಹೆಸರನ್ನು ಕೊಡುವೆನು.",
			"16": "<sup>16</sup> ನಿಮ್ಮ ಪೂರ್ವದ ಕಷ್ಟಗಳು ಇನ್ನು ನನ್ನ ಕಣ್ಣಿಗೆ ಬೀಳದೆ ಮರೆತುಹೋಗಿರುವವಾದದರಿಂದ ಲೋಕದಲ್ಲಿ ತನ್ನನ್ನು ಆಶೀರ್ವದಿಸಿಕೊಳ್ಳುವ ಪ್ರತಿಯೊಬ್ಬನೂ ಸತ್ಯಸಂಧನಾದ ದೇವರ ಹೆಸರಿನಿಂದ ಆಶೀರ್ವದಿಸಿಕೊಳ್ಳುವನು; ಲೋಕದಲ್ಲಿ ಆಣೆಯಿಡುವ ಪ್ರತಿಯೊಬ್ಬನೂ ಸತ್ಯಸಂಧನಾದ ದೇವರ ಮೇಲೆ ಆಣೆಯಿಡುವನು.",
			"17": "<sup>17</sup> ಇಗೋ, ನೂತನಾಕಾಶಮಂಡಲವನ್ನೂ ನೂತನ ಭೂಮಂಡಲವನ್ನೂ ಸೃಷ್ಟಿಸುವೆನು; ಮೊದಲಿದ್ದದ್ದನ್ನು ಯಾರೂ ಜ್ಞಾಪಿಸಿಕೊಳ್ಳರು, ಅದು ನೆನಪಿಗೆಬಾರದು.",
			"18": "<sup>18</sup> ನಾನು ಮಾಡುವ ಸೃಷ್ಟಿಕಾರ್ಯದಲ್ಲಿಯೇ ಹರ್ಷಗೊಂಡು ಸದಾ ಉಲ್ಲಾಸಿಸಿರಿ; ಆಹಾ, ನಾನು ಯೆರೂಸಲೇಮನ್ನು ಉಲ್ಲಾಸದ ನಿವಾಸವನ್ನಾಗಿಯೂ ಅದರ ಜನರನ್ನು ಹರ್ಷಭರಿತರನ್ನಾಗಿಯೂ ಮಾಡುವೆನು.",
			"19": "<sup>19</sup> ನಾನೂ ಯೆರೂಸಲೇಮನ್ನು ದೃಷ್ಟಿಸುತ್ತಾ ಉಲ್ಲಾಸಿಸುವೆನು, ಅದರ ಜನರನ್ನು ಈಕ್ಷಿಸುತ್ತಾ ಹರ್ಷಗೊಳ್ಳುವೆನು. ರೋದನಶಬ್ದವೂ ಪ್ರಲಾಪಧ್ವನಿಯೂ ಅಲ್ಲಿ ಇನ್ನು ಕೇಳಿಸವು.",
			"20": "<sup>20</sup> ಕೆಲವು ದಿವಸ ಮಾತ್ರ ಬದುಕತಕ್ಕ ಕೂಸಾಗಲಿ ವಯಸ್ಸಾಗದೆ ಮುದುಕನಾದವನಾಗಲಿ ಅಲ್ಲಿ ಇರುವದಿಲ್ಲ; ಯುವಕನು ನೂರು ವರುಷದೊಳಗೆ ಸಾಯನು, ಪಾಪಿಷ್ಠನಿಗೂ ನೂರು ವರುಷದೊಳಗೆ ಶಾಪತಗಲದು.",
			"21": "<sup>21</sup> ಅಲ್ಲಿನ ಜನರು ತಾವು ಕಟ್ಟಿದ ಮನೆಗಳಲ್ಲಿ ತಾವೇ ವಾಸಿಸುವರು, ತಾವು ಮಾಡಿದ ತೋಟಗಳ ಫಲವನ್ನು ತಾವೇ ಅನುಭವಿಸುವರು.",
			"22": "<sup>22</sup> ಒಬ್ಬನು ಕಟ್ಟಿದ ಮನೆಯಲ್ಲಿ ಬೇರೊಬ್ಬನು ವಾಸಿಸನು; ಒಬ್ಬನು ಮಾಡಿದ ತೋಟದ ಫಲವು ಇನ್ನೊಬ್ಬನಿಗೆ ವಶವಾಗದು; ನನ್ನ ಜನರ ಆಯುಸ್ಸು ವೃಕ್ಷದ ಆಯುಸ್ಸಿನಂತಿರುವದು; ನನ್ನ ಆಪ್ತರು ತಮ್ಮ ಕೈಕೆಲಸದ ಆದಾಯವನ್ನು ಪೂರಾ ಅನುಭವಿಸುವರು.",
			"23": "<sup>23</sup> ಅವರು ವ್ಯರ್ಥವಾಗಿ ದುಡಿಯರು, ಅವರಿಗೆ ಹುಟ್ಟುವ ಮಕ್ಕಳು ಘೋರ ವ್ಯಾಧಿಗೆ ಗುರಿಯಾಗರು. ಅವರು ಯೆಹೋವನ ಆಶೀರ್ವಾದವನ್ನು ಹೊಂದಿದವರ ಸಂತಾನವಷ್ಟೆ; ಅವರ ಸಂತತಿಯವರು ಅವರೊಂದಿಗೆ ಬಹುದಿನವಿರುವರು.",
			"24": "<sup>24</sup> ಆಗ ಅವರು ಬೇಡುವದರೊಳಗೆ ಸದುತ್ತರವನ್ನು ದಯಪಾಲಿಸುವೆನು; ಅವರು ಹೇಳುತ್ತಿರುವಾಗಲೇ ಕೇಳುವೆನು.",
			"25": "<sup>25</sup> ತೋಳವು ಕುರಿಯ ಸಂಗಡ ಮೇಯುವದು, ಸಿಂಹವು ಎತ್ತಿನಂತೆ ಹುಲ್ಲು ತಿನ್ನುವದು. ಹಾವಿಗೆ ದೂಳೇ ಆಹಾರವಾಗುವದು. ನನ್ನ ಪರಿಶುದ್ಧ ಪರ್ವತದಲ್ಲೆಲ್ಲಾ ಯಾರೂ ಕೇಡುಮಾಡುವದಿಲ್ಲ, ಯಾರೂ ಹಾಳುಮಾಡುವದಿಲ್ಲ ಎಂದು ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ."
		},
		"66": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ಆಕಾಶವು ನನಗೆ ಸಿಂಹಾಸನ, ಭೂಮಿಯು ನನಗೆ ಪಾದಪೀಠ. ನೀವು ನನಗೆ ಇನ್ನೆಂಥಾ ಮನೆಯನ್ನು ಕಟ್ಟಿಕೊಡುವಿರಿ? ನನ್ನ ವಿಶ್ರಾಂತಿಗೆ ತಕ್ಕ ಸ್ಥಳವು ಎಂಥದು?",
			"2": "<sup>2</sup> ಇವುಗಳನ್ನೆಲ್ಲಾ ನನ್ನ ಕೈಯೇ ನಿರ್ಮಿಸಿತು, ಹೌದು, [ನನ್ನ ಕೈಯಿಂದಲೇ] ಇವುಗಳೆಲ್ಲಾ ಉಂಟಾದವು. ನಾನು ಕಟಾಕ್ಷಿಸುವವನು ಎಂಥವನೆಂದರೆ ದೀನನೂ ಮನಮುರಿದವನೂ ನನ್ನ ಮಾತಿಗೆ ಭಯಪಡುವವನೂ ಆಗಿರುವವನೇ.",
			"3": "<sup>3</sup> ಹೋರಿಯನ್ನು ವಧಿಸುವವನು ನರಬಲಿಯನ್ನು ಕೊಡುತ್ತಾನೆ; ಕುರಿಯನ್ನು ಕಡಿಯುವವನು ನಾಯಿಯ ಕತ್ತನ್ನು ಮುರಿಯುತ್ತಾನೆ; ನೈವೇದ್ಯಮಾಡುವವನು ಹಂದಿಯ ರಕ್ತವನ್ನು ಅರ್ಪಿಸುತ್ತಾನೆ; ಧೂಪಹಾಕುವವನು ವಿಗ್ರಹಾರಾಧನೆ ಮಾಡುತ್ತಾನೆ; ಇವರು ಮನಸ್ಸಿಗೆ ಬಂದ ಮಾರ್ಗಗಳನ್ನು ಆರಿಸಿಕೊಂಡು ತಮ್ಮ ಅಸಹ್ಯಕಾರ್ಯಗಳಲ್ಲಿ ಮನಃಪೂರ್ವಕವಾಗಿ ಆನಂದಿಸುತ್ತಾರೆ;",
			"4": "<sup>4</sup> ಅದಕ್ಕೆ ತಕ್ಕಂತೆ ನಾನು ಇವರಿಗೋಸ್ಕರ ಕುತಂತ್ರಗಳನ್ನು ಆರಿಸಿಕೊಂಡು ಇವರು ಅಂಜುತ್ತಿದ್ದ ವಿಪತ್ತುಗಳನ್ನು ಬರಮಾಡುವೆನು; ಏಕೆಂದರೆ ನಾನು ಕೂಗಿದಾಗ ಯಾರೂ ಉತ್ತರಕೊಡಲಿಲ್ಲ, ನಾನು ಹೇಳಿದಾಗ ಇವರು ಕೇಳಲಿಲ್ಲ; ನನ್ನ ಚಿತ್ತಕ್ಕೆ ವಿರುದ್ಧವಾದದ್ದನ್ನು ನಡಿಸಿ ನನಗೆ ಇಷ್ಟವಲ್ಲದ್ದನ್ನು ಆರಿಸಿಕೊಂಡರು.",
			"5": "<sup>5</sup> ಯೆಹೋವನ ಮಾತಿಗೆ ಭಯಪಡುವವರೇ, ಆತನ ಮಾತನ್ನು ಕೇಳಿರಿ! ನನ್ನ ಹೆಸರಿನ ನಿಮಿತ್ತ ನಿಮ್ಮನ್ನು ದ್ವೇಷಿಸಿ ಬಹಿಷ್ಕರಿಸಿದ ನಿಮ್ಮ ಸಂಬಂಧಿಗಳು - ಯೆಹೋವನು ಮಹಿಮೆಪಡಲಿ, ನಿಮಗಾಗುವ ಉತ್ಸಾಹವನ್ನು ನೋಡೋಣ ಎಂದು ಹೇಳಿದ್ದಾರಲ್ಲಾ; ಅವರಿಗಂತು ಅವಮಾನವಾಗುವದು.",
			"6": "<sup>6</sup> ಇಗೋ, ಪಟ್ಟಣದ ಕಡೆಯಿಂದ ಗದ್ದಲದ ಶಬ್ದ! ದೇವಾಲಯದಲ್ಲಿ ಶಬ್ದ! ಯೆಹೋವನು ತನ್ನ ಶತ್ರುಗಳಿಗೆ ಮುಯ್ಯಿತೀರಿಸುವ ಶಬ್ದ!",
			"7": "<sup>7</sup> ಆಹಾ, ಬೇನೆ ತಿನ್ನುವದರೊಳಗೆ ಹೆರಿಗೆಯಾಯಿತು, ಪ್ರಸವವೇದನೆ ಇನ್ನು ಕಾಣದಿರುವಲ್ಲಿ ಗಂಡನ್ನು ಹಡೆದಳು.",
			"8": "<sup>8</sup> ಇಂಥಾ ಸುದ್ದಿಯನ್ನು ಯಾರು ಕೇಳಿದ್ದಾರೆ? ಇಂಥಾ ಸಂಗತಿಯನ್ನು ಕಂಡವರಾರು? ಒಂದು ದಿನದಲ್ಲಿ ರಾಷ್ಟ್ರವು ಹುಟ್ಟೀತೇ? ಕ್ಷಣಮಾತ್ರದಲ್ಲಿ ಜನಾಂಗವನ್ನು ಹೆರಲಿಕ್ಕಾದೀತೇ? ಹೌದು, ಚೀಯೋನೆಂಬಾಕೆಯು ಬೇನೆ ತಿಂದು ತನಗಾಗಿ ಮಕ್ಕಳನ್ನು ಹಡೆದಿದ್ದಾಳೆ.",
			"9": "<sup>9</sup> ನಾನು ಗರ್ಭದ್ವಾರವನ್ನು ತೆರೆದ ಮೇಲೆ ಪ್ರಸವಿಸಗೊಡದೆ ಹೋದೇನೋ ಎಂದು ಯೆಹೋವನು ಹೇಳುತ್ತಾನೆ. ಪ್ರಸವಮಾಡಿಸುವವನಾದ ನಾನು ಗರ್ಭವನ್ನು ಮುಚ್ಚೇನೋ ಎಂದು ನಿನ್ನ ದೇವರು ನುಡಿಯುತ್ತಾನೆ.",
			"10": "<sup>10</sup> ಯೆರೂಸಲೇಮ್ ಪುರಿಯನ್ನು ಪ್ರೀತಿಸುವವರೇ, ನೀವೆಲ್ಲರೂ ಆಕೆಯೊಂದಿಗೆ ಆನಂದಿಸಿರಿ, ಆಕೆಯ ವಿಷಯದಲ್ಲಿ ಹರ್ಷಗೊಳ್ಳಿರಿ!",
			"11": "<sup>11</sup> ಆಕೆಯ ನಿಮಿತ್ತ ದುಃಖಿಸುವವರೇ, ನೀವೆಲ್ಲರೂ - ಆಕೆಯ ಸಮಾಧಾಯಕ ಸ್ತನ್ಯವನ್ನು ಕುಡಿದು ತೃಪ್ತಿಗೊಳ್ಳುವೆವು. ಹೌದು, ಆಕೆಯ ವೈಭವದ ಸಮೃದ್ಧಿಯನ್ನು ಹೀರುತ್ತಾ ಹಿಗ್ಗುವೆವು ಎಂದು ಆಕೆಯೊಂದಿಗೆ ಉತ್ಸಾಹದಿಂದ ಉಲ್ಲಾಸಿಸಿರಿ.",
			"12": "<sup>12</sup> ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ಇಗೋ, ನಾನು ಆಕೆಗೆ ಸುಖವನ್ನು ನದಿಯಂತೆ ದಯಪಾಲಿಸಿ ತುಂಬಿ ತುಳುಕುವ ತೊರೆಯನ್ನೋ ಎಂಬಂತೆ ಜನಾಂಗಗಳ ವೈಭವವನ್ನು ನೀಡುವೆನು; ನೀವು ಪಾನಮಾಡುವಿರಿ; ನಿಮ್ಮನ್ನು ಕಂಕುಳಲ್ಲಿ ಎತ್ತಿಕೊಂಡು ಹೋಗುವರು, ತೊಡೆಯ ಮೇಲೆ ನಲಿದಾಡಿಸುವರು.",
			"13": "<sup>13</sup> ತಾಯಿ ಮಗನನ್ನು ಸಂತೈಸುವ ಪ್ರಕಾರ ನಾನು ನಿಮ್ಮನ್ನು ಸಂತೈಸುವೆನು; ಯೆರೂಸಲೇಮಿನಲ್ಲೇ ನಿಮಗೆ ದುಃಖಶಮನವಾಗುವದು.",
			"14": "<sup>14</sup> ನೀವು ಇದನ್ನು ಕಣ್ಣಾರೆ ಕಾಣುವಿರಿ, ನಿಮ್ಮ ಹೃದಯವು ಉಲ್ಲಾಸಿಸುವದು, ನಿಮ್ಮ ಎಲುಬುಗಳು ಹಸಿಹುಲ್ಲಿನಂತೆ ರಸವತ್ತಾಗುವವು; ಆಗ ಯೆಹೋವನು ತನ್ನ ಸೇವಕರ ಮೇಲೆ ಕೃಪಾಹಸ್ತವನ್ನು ವ್ಯಕ್ತಪಡಿಸಿ ತನ್ನ ಶತ್ರುಗಳ ಮೇಲೆ ರೋಷಗೊಳ್ಳುವನು.",
			"15": "<sup>15</sup> ಆಹಾ, ಯೆಹೋವನು ಬೆಂಕಿಯನ್ನು ಕಟ್ಟಿಕೊಂಡು ಬರುವನು; ಆತನ ರಥಗಳು ಬಿರುಗಾಳಿಯಂತಿರುವವು; ರೌದ್ರಾವೇಶದಿಂದ ತನ್ನ ಸಿಟ್ಟನ್ನು ತೀರಿಸುವನು, ಅಗ್ನಿಜ್ವಾಲೆಯಿಂದ ಖಂಡಿಸುವನು.",
			"16": "<sup>16</sup> ಯೆಹೋವನು ಅಗ್ನಿಯಿಂದಲೂ ತನ್ನ ಖಡ್ಗದಿಂದಲೂ ಎಲ್ಲಾ ನರಜನ್ಮದವರಿಗೂ ನ್ಯಾಯತೀರಿಸುವನು; ಆಗ ಆತನಿಂದ ಹತರಾಗುವವರು ಬಹುಜನ.",
			"17": "<sup>17</sup> ತೋಟಗಳೊಳಗೆ ಪ್ರವೇಶಿಸುವದಕ್ಕೆ ತಮ್ಮ ಮಧ್ಯದಲ್ಲಿನ ಒಬ್ಬನ ಅಂಗಾಭಿನಯವನ್ನು ಅನುಸರಿಸಿ ತಮ್ಮನ್ನು ಶುದ್ಧೀಕರಿಸಿ ಪವಿತ್ರಮಾಡಿಕೊಂಡು ಹಂದಿಯ ಮಾಂಸವನ್ನೂ ಅಶುದ್ಧಪದಾರ್ಥವನ್ನೂ ಇಲಿಯನ್ನೂ ತಿನ್ನುವವರು ಒಟ್ಟಿಗೆ ಕೊನೆಗಾಣುವರು ಎಂದು ಯೆಹೋವನು ನುಡಿಯುತ್ತಾನೆ.",
			"18": "<sup>18</sup> ಅವರ ಕೃತ್ಯಗಳಿಗೂ ಆಲೋಚನೆಗಳಿಗೂ [ತಕ್ಕದ್ದನ್ನು ಮಾಡುವೆನು]; ನಾನು ಇನ್ನು ಮುಂದೆ ಸಮಸ್ತಜನಾಂಗಗಳನ್ನೂ ಸಕಲಭಾಷೆಯವರನ್ನೂ ಒಟ್ಟಿಗೆ ಬರಮಾಡುವೆನು; ಅವರು ಬಂದು ನನ್ನ ಮಹಿಮೆಯನ್ನು ನೋಡುವರು.",
			"19": "<sup>19</sup> ಅವರ ಮಧ್ಯದಲ್ಲಿ ಒಂದು ಸೂಚಕಕಾರ್ಯವನ್ನು ಮಾಡುವೆನು; ನನ್ನ ಸುದ್ದಿಯನ್ನು ಕೇಳದೆಯೂ ನನ್ನ ಮಹಿಮೆಯನ್ನು ನೋಡದೆಯೂ ಇರುವ ತಾರ್ಷೀಷ್, ಪೂಲ್, ಬಿಲ್ಲುಗಾರರಿಗೆ ಪ್ರಸಿದ್ಧಸ್ಥಳವಾದ ಲೂದ್, ತೂಬಲ್, ಯಾವಾನ್ ಎಂಬ ಜನಾಂಗಗಳು ಮತ್ತು ದೂರವಾದ ದ್ವೀಪ ನಿವಾಸಿಗಳು ಇವರೆಲ್ಲರ ಬಳಿಗೆ ಹತಶೇಷರನ್ನು ಕಳುಹಿಸುವೆನು; ಇವರು ಜನಾಂಗಗಳಲ್ಲಿ ನನ್ನ ಮಹಿಮೆಯನ್ನು ಪ್ರಕಟಿಸುವರು.",
			"20": "<sup>20</sup> ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ಇಸ್ರಾಯೇಲ್ಯರು ಯೆಹೋವನ ಆಲಯಕ್ಕೆ ಶುದ್ಧ ಪಾತ್ರೆಯಲ್ಲಿ ನೈವೇದ್ಯವನ್ನು ತೆಗೆದುಕೊಂಡು ಬರುವ ಪ್ರಕಾರ ಸಮಸ್ತಜನಾಂಗಗಳಲ್ಲಿ ಚದರಿಹೋಗಿರುವ ನಮ್ಮ ಸಹೋದರರನ್ನು ಕುದುರೆ, ತೇರು, ಪಾಲಕಿ, ಹೇಸರಗತ್ತೆ, ಒಂಟೆ ಇವುಗಳ ಮೇಲೆ ಯೆಹೋವನ ನೈವೇದ್ಯಕ್ಕಾಗಿ ಯೆರೂಸಲೇಮೆಂಬ ನನ್ನ ಪರಿಶುದ್ಧ ಪರ್ವತಕ್ಕೆ ಕರತರುವರು.",
			"21": "<sup>21</sup> ಇದಲ್ಲದೆ ಇವರಲ್ಲಿ ಯಾಜಕರೂ ಲೇವಿಯರೂ ಆಗತಕ್ಕವರನ್ನು ಆರಿಸಿಕೊಳ್ಳುವೆನು ಎಂದು ಯೆಹೋವನು ಹೇಳುತ್ತಾನೆ.",
			"22": "<sup>22</sup> ನಾನು ಸೃಷ್ಟಿಸುವ ನೂತನಾಕಾಶಮಂಡಲವೂ ನೂತನಭೂಮಂಡಲವೂ ನನ್ನ ಮುಂದೆ ಸ್ಥಿರವಾಗಿ ನಿಲ್ಲುವ ಪ್ರಕಾರ ನಿಮ್ಮ ಸಂತತಿಯೂ ನಿಮ್ಮ ಹೆಸರೂ ಸ್ಥಿರವಾಗಿ ನಿಲ್ಲುವವು.",
			"23": "<sup>23</sup> ಪ್ರತಿಯೊಂದು ಅಮಾವಾಸ್ಯೆಯಲ್ಲಿಯೂ ಒಂದೊಂದು ಸಬ್ಬತ್ ದಿನದಲ್ಲಿಯೂ ಸಕಲ ನರಜನ್ಮದವರೂ ನನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಎರಗುವದಕ್ಕೆ ಬರುವರು; ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"24": "<sup>24</sup> ಅವರು ಆಚೆ ಹೋಗಿ ನನಗೆ ದ್ರೋಹಮಾಡಿದವರ ಹೆಣಗಳನ್ನು ನೋಡುವರು; ಅವುಗಳನ್ನು ಕಡಿಯುವ ಹುಳವು ಸಾಯುವದಿಲ್ಲ, ಸುಡುವ ಬೆಂಕಿಯು ಆರುವದಿಲ್ಲ; ಅವು ಲೋಕದವರಿಗೆಲ್ಲಾ ಅಸಹ್ಯವಾಗಿರುವವು."
		}
	}
};
module.exports = book;