var book = {
	"name": "ಯೆರೆಮೀಯ",
	"numChapters": 52,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ಬೆನ್ಯಾಮೀನ್ ಸೀಮೆಗೆ ಸೇರಿದ ಅನಾತೋತ್ ಊರಿನ ಯಾಜಕವರ್ಗದವನೂ ಹಿಲ್ಕೀಯನ ಮಗನೂ ಆದ ಯೆರೆಮೀಯನ ಪ್ರವಾದನೆಗಳು.",
			"2": "<sup>2</sup> ಯೆಹೂದದ ಅರಸನೂ ಆಮೋನನ ಮಗನೂ ಆದ ಯೋಷೀಯನ ಆಳಿಕೆಯ ಹದಿಮೂರನೆಯ ವರುಷದಲ್ಲಿ ಯೆಹೋವನ ವಾಕ್ಯವು ಇವನಿಗೆ ಒದಗಿತು.",
			"3": "<sup>3</sup> ಅಲ್ಲದೆ ಯೆಹೂದದ ಅರಸನೂ ಯೋಷೀಯನ ಮಗನೂ ಆದ ಯೆಹೋಯಾಕೀಮನ ಕಾಲದಿಂದ ಯೆಹೂದದ ಅರಸನೂ ಯೋಷೀಯನ ಮಗನೂ ಆದ ಚಿದ್ಕೀಯನ ಆಳಿಕೆಯ ಹನ್ನೊಂದನೆಯ ವರುಷದ ಐದನೆಯ ತಿಂಗಳಿನಲ್ಲಿ ಯೆರೂಸಲೇಮಿನವರು ಸೆರೆಹೋಗುವ ತನಕ ಯೆಹೋವನ ವಾಕ್ಯವು ಒದಗುತ್ತಿತ್ತು.",
			"4": "<sup>4</sup> ಯೆಹೋವನು ಈ ವಾಕ್ಯವನ್ನು ನನಗೆ ದಯಪಾಲಿಸಿದನು -",
			"5": "<sup>5</sup> ನಾನು ನಿನ್ನನ್ನು ಗರ್ಭದಲ್ಲಿ ರೂಪಿಸುವದಕ್ಕೆ ಮುಂಚೆ ನಿನ್ನನ್ನು ತಿಳಿದಿದ್ದೆನು; ನೀನು ಉದರದಿಂದ ಬರುವದಕ್ಕೆ ಮೊದಲೇ ನಿನ್ನನ್ನು ಪ್ರತಿಷ್ಠಿಸಿದ್ದೆನು; ಜನಾಂಗಗಳಿಗೆ ಪ್ರವಾದಿಯನ್ನಾಗಿ ನಿನ್ನನ್ನು ನೇಮಿಸಿದ್ದೇನೆ.",
			"6": "<sup>6</sup> ಅದಕ್ಕೆ ನಾನು - ಅಯ್ಯೋ, ಕರ್ತನಾದ ಯೆಹೋವನೇ, ನಾನು ಮಾತು ಬಲ್ಲವನಲ್ಲ, ಬಾಲಕನು ಎಂದು ಬಿನ್ನವಿಸಿದೆನು.",
			"7": "<sup>7</sup> ಆಗ ಯೆಹೋವನು ನನಗೆ - ಬಾಲಕನೆನ್ನಬೇಡ; ಯಾರ ಬಳಿಗೆ ನಿನ್ನನ್ನು ಕಳುಹಿಸುತ್ತೇನೋ ಅವರೆಲ್ಲರ ಬಳಿಗೆ ನೀನು ಹೋಗೇ ಹೋಗುವಿ; ನಾನು ಆಜ್ಞಾಪಿಸುವದನ್ನೆಲ್ಲಾ ನುಡಿಯಲೇ ನುಡಿಯುವಿ.",
			"8": "<sup>8</sup> ಅವರಿಗೆ ಅಂಜಬೇಡ; ನಿನ್ನನ್ನುದ್ಧರಿಸಲು ನಾನೇ ನಿನ್ನೊಂದಿಗಿರುವೆನು, ಇದು ಯೆಹೋವನಾದ ನನ್ನ ಮಾತು ಎಂದು ಹೇಳಿದನು.",
			"9": "<sup>9</sup> ಆಗ ಯೆಹೋವನು ಕೈಚಾಚಿ ನನ್ನ ಬಾಯನ್ನು ಮುಟ್ಟಿ - ಇಗೋ, ನಿನ್ನ ಬಾಯಲ್ಲಿ ನನ್ನ ಮಾತುಗಳನ್ನು ಇಟ್ಟಿದ್ದೇನೆ;",
			"10": "<sup>10</sup> ಕಿತ್ತುಹಾಕುವದು, ಕೆಡವುವದು, ನಾಶಪಡಿಸುವದು, ಹಾಳುಮಾಡುವದು, ಕಟ್ಟುವದು, ನೆಡುವದು, ಈ ಕಾರ್ಯಗಳನ್ನು ಮಾಡುವದಕ್ಕೋಸ್ಕರ ಜನಾಂಗಗಳ ಮೇಲೂ ರಾಜ್ಯಗಳ ಮೇಲೂ ನಿನ್ನನ್ನು ಈ ದಿನ ನೇಮಿಸಿದ್ದೇನೆ ಅಂದನು.",
			"11": "<sup>11</sup> ಇದಲ್ಲದೆ ಯೆಹೋವನು - ಯೆರೆಮೀಯನೇ, ಏನು ನೋಡುತ್ತೀ ಎಂದು ನನ್ನನ್ನು ಕೇಳಿದನು, ಅದಕ್ಕೆ ನಾನು - ಚಚ್ಚರ ಮರದ ರೆಂಬೆಯನ್ನು ನೋಡುತ್ತೇನೆ ಅಂದೆನು.",
			"12": "<sup>12</sup> ಆಗ ಯೆಹೋವನು ನನಗೆ - ಸರಿಯಾಗಿ ನೋಡಿದ್ದೀ, ನನ್ನ ಮಾತನ್ನು ನೆರವೇರಿಸುವದಕ್ಕೆ ಎಚ್ಚರಗೊಂಡಿದ್ದೇನೆ ಎಂದು ತಿಳಿದುಕೋ ಎಂಬದಾಗಿ ಹೇಳಿದನು.",
			"13": "<sup>13</sup> ಇನ್ನೊಂದು ಸಲ ಯೆಹೋವನು ಏನು ನೋಡುತ್ತೀ ಎಂದು ನನ್ನನ್ನು ಕೇಳಿದನು. ಅದಕ್ಕೆ ನಾನು - ಉರಿಸುತ್ತಿರುವ ಬೆಂಕಿಯಿಂದುಕ್ಕುವ ಹಂಡೆಯನ್ನು ನೋಡುತ್ತೇನೆ. ಅದರ ಬಾಯಿ ಬಡಗಲಿಂದ ಇತ್ತ ಬಾಗಿಕೊಂಡಿದೆ ಅಂದೆನು.",
			"14": "<sup>14</sup> ಆಗ ಯೆಹೋವನು ನನಗೆ ಹೀಗೆ ಹೇಳಿದನು - ಈ ದೇಶನಿವಾಸಿಗಳೆಲ್ಲರ ಮೇಲೆ ಬಡಗಲಿಂದ ಕೇಡು ಉಕ್ಕಿ ಬರುವದು.",
			"15": "<sup>15</sup> ಇಗೋ, ನಾನು ಬಡಗಣ ರಾಜ್ಯಗಳ ಜನಾಂಗಗಳನ್ನೆಲ್ಲಾ ಕರೆಯುವೆನು; ಅವರು ಬಂದು ಯೆರೂಸಲೇಮಿನ ಊರು ಬಾಗಲುಗಳ ಎದುರಿನಲ್ಲಿಯೂ ಅದರ ಎಲ್ಲಾ ಪೌಳಿಗೋಡೆಗಳ ಸುತ್ತಲೂ ಯೆಹೂದದ ಸಕಲ ಪಟ್ಟಣಗಳ ಮುಂದೆಯೂ ತಮ್ಮ ತಮ್ಮ ನ್ಯಾಯಾಸನಗಳನ್ನು ಹಾಕಿಕೊಳ್ಳುವರು.",
			"16": "<sup>16</sup> ನನ್ನ ಜನರು ನನ್ನನ್ನು ಬಿಟ್ಟದ್ದು, ಅನ್ಯದೇವತೆಗಳಿಗೆ ಹೋಮಮಾಡಿದ್ದು, ತಮ್ಮ ಕೈಯಿಂದ ನಿರ್ಮಿಸಿದ್ದಕ್ಕೆ ಅಡ್ಡಬಿದ್ದದ್ದು, ಈ ಅಧರ್ಮಕ್ಕೆಲ್ಲಾ ನಾನು ಅವರಿಗೆ ವಿಧಿಸಿರುವ ನ್ಯಾಯದಂಡನೆಗಳನ್ನು ತಿಳಿಸುವೆನು.",
			"17": "<sup>17</sup> ಹೀಗಿರಲು ನೀನು ಎದ್ದು ನಡುಕಟ್ಟಿಕೊಂಡು ನಾನು ಆಜ್ಞಾಪಿಸುವದನ್ನೆಲ್ಲಾ ಅವರಿಗೆ ಹೇಳು. ನೀನು ಅವರಿಗೆ ಕಂಗೆಡಬೇಡ; ಕಂಗೆಟ್ಟರೆ ನಾನೂ ನಿನ್ನನ್ನು ಅವರ ಮುಂದೆ ಕಂಗೆಡಿಸುವೆನು.",
			"18": "<sup>18</sup> ಇಗೋ, ನಾನು ಈ ಹೊತ್ತು ನಿನ್ನನ್ನು ಯೆಹೂದದ ಅರಸರು, ಅಧಿಪತಿಗಳು, ಯಾಜಕರು, ಸಾಧಾರಣ ಜನರು, ಅಂತು ದೇಶದವರೆಲ್ಲರನ್ನೂ ಎದುರಿಸತಕ್ಕ ಕೋಟೆಕೊತ್ತಲದ ಪಟ್ಟಣವನ್ನಾಗಿಯೂ ಕಬ್ಬಿಣದ ಕಂಬವನ್ನಾಗಿಯೂ ತಾಮ್ರದ ಪೌಳಿಗೋಡೆಯನ್ನಾಗಿಯೂ ಸ್ಥಾಪಿಸಿದ್ದೇನೆ.",
			"19": "<sup>19</sup> ಅವರು ನಿನಗೆ ವಿರುದ್ಧವಾಗಿ ಯುದ್ಧಮಾಡುವರು, ಆದರೆ ನಿನ್ನನ್ನು ಸೋಲಿಸಲಾಗುವದಿಲ್ಲ; ನಿನ್ನನ್ನುದ್ಧರಿಸಲು ನಾನೇ ನಿನ್ನೊಂದಿಗಿರುವೆನು, ಇದು ಯೆಹೋವನಾದ ನನ್ನ ಮಾತು."
		},
		"2": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಈ ವಾಕ್ಯವನ್ನು ನನಗೆ ದಯಪಾಲಿಸಿದನು -",
			"2": "<sup>2</sup> ನೀನು ಹೋಗಿ ಯೆರೂಸಲೇಮಿನ ಕಿವಿಗೆ ಮುಟ್ಟುವಂತೆ ಈ ಮಾತುಗಳನ್ನು ಸಾರು - ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ, ನೀನು ಯೌವನದಲ್ಲಿ [ನನ್ನ ಮೇಲೆ] ಇಟ್ಟಿದ್ದ ಪ್ರೀತಿಯನ್ನೂ ವಿವಾಹ ಕಾಲದ ನಿನ್ನ ಪ್ರೇಮವನ್ನೂ ಬೀಜಬಿತ್ತಿಯೇ ಇಲ್ಲದ ಅರಣ್ಯದಲ್ಲಿ ನೀನು ನನ್ನನ್ನು ಹಿಂಬಾಲಿಸಿದ ನಿನ್ನ ಪಾತಿವ್ರತ್ಯವನ್ನೂ ನಿನ್ನ ಹಿತಕ್ಕಾಗಿ ಜ್ಞಾಪಕದಲ್ಲಿಟ್ಟಿದ್ದೇನೆ.",
			"3": "<sup>3</sup> ಇಸ್ರಾಯೇಲು ಯೆಹೋವನ ಬೆಳೆಯ ಪ್ರಥಮಫಲವಾಗಿ ಆತನಿಗೆ ಮೀಸಲಾಗಿತ್ತು; ಅದನ್ನು ತಿಂದುಬಿಟ್ಟವರೆಲ್ಲರೂ ದೋಷಿಗಳಾಗಿ ಕೇಡಿಗೆ ಗುರಿಯಾದರು ಎಂದು ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ.",
			"4": "<sup>4</sup> ಯಾಕೋಬನ ಮನೆತನವೇ, ಇಸ್ರಾಯೇಲ್ ವಂಶದ ಸಕಲ ಗೋತ್ರಗಳೇ, ಯೆಹೋವನ ವಾಕ್ಯವನ್ನು ಕೇಳಿರಿ;",
			"5": "<sup>5</sup> ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ, ನಿಮ್ಮ ಪಿತೃಗಳು ನನ್ನಲ್ಲಿ ಯಾವ ಅನ್ಯಾಯವನ್ನು ಕಂಡದರಿಂದ ವ್ಯರ್ಥಾಚಾರವನ್ನು ಅನುಸರಿಸಿ ತಾವೇ ವ್ಯರ್ಥರಾಗಿ ನನ್ನನ್ನು ಬಿಟ್ಟು ದೂರವಾದರು?",
			"6": "<sup>6</sup> ನಮ್ಮನ್ನು ಐಗುಪ್ತದೇಶದೊಳಗಿಂದ ಬರಮಾಡಿ ಕಾಡಾಗಿಯೂ ಹಳ್ಳಕೊಳ್ಳವಾಗಿಯೂ ನಿರ್ಜಲವಾಗಿಯೂ ಘೋರಾಂಧಕಾರವಾಗಿಯೂ ಯಾರೂ ಹಾದುಹೋಗದೆಯೂ ಯಾರೂ ವಾಸಿಸದೆಯೂ ಇರುವ ಅರಣ್ಯದಲ್ಲಿ ನಡಿಸಿದ ಯೆಹೋವನು ಎಲ್ಲಿ ಎಂದು ಅವರು ಅಂದುಕೊಳ್ಳಲಿಲ್ಲವಲ್ಲ.",
			"7": "<sup>7</sup> ನಾನು ನಿಮ್ಮನ್ನು ಫಲವತ್ತಾದ ಸೀಮೆಗೆ ಕರತಂದು ಅದರ ಫಲವನ್ನೂ ಸಾರವನ್ನೂ ಅನುಭವಿಸುವ ಹಾಗೆ ಮಾಡಿದೆನು; ಆದರೆ ನೀವು ಪ್ರವೇಶಿಸಿ ಆ ನನ್ನ ದೇಶವನ್ನು ಹೊಲೆಮಾಡಿ ನನ್ನ ಸ್ವಾಸ್ತ್ಯವನ್ನು ಅಸಹ್ಯಪಡಿಸಿದಿರಿ.",
			"8": "<sup>8</sup> ಯಾಜಕರಿಗೆ ಯೆಹೋವನು ಎಲ್ಲಿ ಎಂಬ ವಿಚಾರವೇ ಇರಲಿಲ್ಲ, ಧರ್ಮೋಪದೇಶಕರು ನನ್ನನ್ನು ತಿಳಿಯಲಿಲ್ಲ, ಪಾಲಕರು ನನಗೆ ದ್ರೋಹಮಾಡಿದರು, ಪ್ರವಾದಿಗಳು ಬಾಳ್‍ದೇವತೆಯ ಆವೇಶದಿಂದ ಪ್ರವಾದಿಸಿ ಕೆಲಸಕ್ಕೆ ಬಾರದವುಗಳನ್ನು ಸೇವಿಸಿದರು.",
			"9": "<sup>9</sup> ಆದಕಾರಣ ನಾನು ನಿಮ್ಮೊಂದಿಗೆ ಇನ್ನೂ ವ್ಯಾಜ್ಯವಾಡುವೆನು; ನಿಮ್ಮ ಸಂತಾನದವರೊಂದಿಗೂ ವ್ಯಾಜ್ಯವಾಡುವೆನು ಎಂದು ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ.",
			"10": "<sup>10</sup> ಕಿತ್ತೀಮ್ ದ್ವೀಪಗಳಿಗೆ ಹೋಗಿ ನೋಡಿರಿ, ಕೇದಾರಿಗೆ ಕಳುಹಿಸಿ ವಿಚಾರಮಾಡಿರಿ; ಇಂಥಾ ಕಾರ್ಯವು ಎಲ್ಲಿಯಾದರೂ ನಡೆಯಿತೇ ಎಂದು ಚೆನ್ನಾಗಿ ಆಲೋಚಿಸಿರಿ.",
			"11": "<sup>11</sup> ತನ್ನ ದೇವತೆಗಳು ದೇವರಲ್ಲದೆ ಇದ್ದರೂ ಅವುಗಳನ್ನು ಯಾವ ಜನಾಂಗವಾದರೂ ಬದಲಾಯಿಸಿಕೊಂಡಿತೋ? ಆದರೆ ನನ್ನ ಜನರು ತಮ್ಮ ಮಹಿಮೆಯಾದ ನನಗೆ ಬದಲಾಗಿ ಕೆಲಸಕ್ಕೆ ಬಾರದ್ದನ್ನು ಆರಿಸಿಕೊಂಡಿದ್ದಾರೆ.",
			"12": "<sup>12</sup> ಆಕಾಶ ಮಂಡಲವೇ, ಇದಕ್ಕೆ ಬೆಚ್ಚಿಬೆರಗಾಗು, ತತ್ತರಿಸು, ಹಾಳಾಗು ಎಂದು ಯೆಹೋವನು ನುಡಿಯುತ್ತಾನೆ.",
			"13": "<sup>13</sup> ನನ್ನ ಜನರು ಎರಡು ಅಪರಾಧಗಳನ್ನು ಮಾಡಿದ್ದಾರೆ; ಜೀವಜಲದ ಬುಗ್ಗೆಯಾದ ನನ್ನನ್ನು ಬಿಟ್ಟಿದ್ದಾರೆ, ತಮಗೋಸ್ಕರ ತೊಟ್ಟಿಗಳನ್ನು, ನೀರು ನಿಲ್ಲದ ಬಿರಿದ ತೊಟ್ಟಿಗಳನ್ನು ಕೊರೆದುಕೊಂಡಿದ್ದಾರೆ.",
			"14": "<sup>14</sup> ಇಸ್ರಾಯೇಲು ಒಬ್ಬ ದಾಸನೋ? ಮನೆಯ ಗುಲಾಮನಾಗಿ ಹುಟ್ಟಿದವನೋ? ಏಕೆ ಸೂರೆಯಾದನು?",
			"15": "<sup>15</sup> ಪ್ರಾಯದ ಸಿಂಹಗಳು ಅವನ ಮೇಲೆ ಗರ್ಜಿಸಿ ಆರ್ಭಟಿಸುತ್ತಿವೆ, ಅವನ ದೇಶವನ್ನು ಹಾಳು ಮಾಡಿವೆ, ಅವನ ಊರುಗಳು ಸುಟ್ಟು ನಿರ್ನಿವಾಸಿಗಳಾಗಿವೆ.",
			"16": "<sup>16</sup> ನೋಫ್ ಮತ್ತು ತಹಪನೇಸ್ ಪಟ್ಟಣಗಳವರು ನಿನ್ನ ನೆತ್ತಿಯನ್ನೂ ನುಣ್ಣಗೆ ಮೇದುಬಿಟ್ಟಿದ್ದಾರೆ.",
			"17": "<sup>17</sup> ನಿನ್ನನ್ನು ದಾರಿಯಲ್ಲಿ ನಡಿಸುತ್ತಿದ್ದ ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನನ್ನು ತೊರೆದುಬಿಟ್ಟು ಇದನ್ನೆಲ್ಲಾ ನೀನೇ ನಿನ್ನ ಮೇಲೆ ಬರಮಾಡಿಕೊಂಡಿಯಲ್ಲಾ.",
			"18": "<sup>18</sup> ನೀನು ಐಗುಪ್ತದ ದಾರಿಯಲ್ಲಿ ನಡೆದದ್ದೇಕೆ? ನೈಲ್‍ನದಿಯ ನೀರನ್ನು ಕುಡಿಯಬೇಕಾದದ್ದೇನು? ಅಶ್ಶೂರದ ಮಾರ್ಗದಲ್ಲಿ ಹೆಜ್ಜೆಯಿಟ್ಟದ್ದೇಕೆ? [ಯೂಫ್ರೇಟೀಸ್] ನದಿಯ ಜಲವನ್ನು ಪಾನಮಾಡಬೇಕಾದದ್ದೇನು?",
			"19": "<sup>19</sup> ನಿನ್ನ ಕೆಟ್ಟತನವೇ ನಿನ್ನನ್ನು ಶಿಕ್ಷಿಸುವದು, ನಿನ್ನ ದ್ರೋಹಗಳೇ ನಿನ್ನನ್ನು ಖಂಡಿಸುವವು; ನೀನು ನನ್ನ ಭಯವಿಲ್ಲದೆ ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನೆಂಬ ನನ್ನನ್ನು ತೊರೆದುಬಿಟ್ಟದ್ದು ಕೆಟ್ಟದ್ದಾಗಿಯೂ ವಿಷವಾಗಿಯೂ ಇರುತ್ತದೆಂದು ಗ್ರಹಿಸಿಕೋ, ಕಣ್ಣಾರೆ ನೋಡು ಎಂಬದು ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೆಂಬ ಕರ್ತನ ನುಡಿ.",
			"20": "<sup>20</sup> ಬಹುಕಾಲದಿಂದ ನೀನು ನಿನ್ನ ನೊಗವನ್ನು ಮುರಿದು ಕಣ್ಣಿಗಳನ್ನು ಕಿತ್ತು ನಾನು ಸೇವೆ ಮಾಡುವದಿಲ್ಲವೆಂದು ಅಂದುಕೊಳ್ಳುತ್ತಿದ್ದೀ; ಎತ್ತರವಾದ ಎಲ್ಲಾ ಗುಡ್ಡಗಳ ಮೇಲೂ ಸೊಂಪಾಗಿ ಬೆಳೆದಿರುವ ಎಲ್ಲಾ ಮರಗಳ ಕೆಳಗೂ ನೀನು ಅಡ್ಡಬಿದ್ದು ಸೂಳೆಯಾಗಿ ನಡೆದಿದ್ದೀ.",
			"21": "<sup>21</sup> ನಾನು ನಿನ್ನನ್ನು ಅತ್ಯುತ್ತಮ ಬೀಜದಿಂದ ಬೆಳೆದ ಒಳ್ಳೇಯ ದ್ರಾಕ್ಷಾಲತೆಯನ್ನಾಗಿ ನೆಟ್ಟಿರಲು ನೀನು ನನಗೆ ಕಾಡುದ್ರಾಕ್ಷೇಬಳ್ಳಿಯ ಕೆಟ್ಟ ರೆಂಬೆಗಳಾದದ್ದೇನು?",
			"22": "<sup>22</sup> ನೀನು ಬಹಳ ಚೌಳನ್ನು ಹಾಕಿಕೊಂಡು ಸಬ್ಬಿನಿಂದ ತೊಳಕೊಂಡರೂ ನಿನ್ನ ಅಧರ್ಮವು ನನ್ನ ಕಣ್ಣೆದುರಿಗೆ ಕರೆಯಾಗಿ ನಿಂತಿದೆ ಎಂದು ಕರ್ತನಾದ ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ.",
			"23": "<sup>23</sup> ನನಗೆ ಮುಡಚಟ್ಟಾಗಲಿಲ್ಲ, ಅನ್ಯದೇವತೆಗಳನ್ನು ನಾನು ಹಿಂಬಾಲಿಸಲಿಲ್ಲವೆಂದು ಹೇಗೆ ಹೇಳುತ್ತೀ? ಆ ತಗ್ಗಿನಲ್ಲಿನ ನಿನ್ನ ನಡತೆಯನ್ನು ನೋಡು, ನೀನು ಮಾಡಿದ ಕೆಲಸವನ್ನು ಮನಸ್ಸಿಗೆ ತಂದುಕೋ, ತನ್ನ ಜಾಡೆಗಳನ್ನು ತಾರುಮಾರು ಮಾಡುವ ವೇಗವಾದ ಹೆಣ್ಣು ಒಂಟೆಯಂತಿದ್ದೀ.",
			"24": "<sup>24</sup> ಅಡವಿಯಲ್ಲಿ ಪಳಗಿದ್ದು ಮದದಿಂದ ಗಾಳಿಯನ್ನು ಬುಸುಬುಸನೆ ಮೂಸಿನೋಡುವ ಹೆಣ್ಣು ಕಾಡುಕತ್ತೆಯ ಹಾಗಿದ್ದೀ; ಅದಕ್ಕೆ ಬೆದೆ ಏರಿದಾಗ ಯಾವ ಗಂಡುಕತ್ತೆಯು ನೂಕಿಬಿಟ್ಟೀತು? ಆಯಾ ತಿಂಗಳಿನಲ್ಲಿ ಅದನ್ನು ಹುಡುಕುವವುಗಳೆಲ್ಲಾ ಆಯಾಸಗೊಳ್ಳದೆ ಅದನ್ನು ದೊರಕಿಸಿಕೊಳ್ಳುವವು.",
			"25": "<sup>25</sup> ನಿನ್ನ ಕಾಲು ಸವೆಯದಂತೆಯೂ ನಿನ್ನ ಗಂಟಲು ಆರದಂತೆಯೂ ತಡೆದುಕೋ; ಆದರೆ ನೀನು - ನಿರೀಕ್ಷೆಯಿಲ್ಲ, ಆಗಲಾರದು, ಅನ್ಯರ ಮೇಲೆ ಮೋಹಗೊಂಡಿದ್ದೇನೆ. ಅವರ ಹಿಂದೆಯೇ ಹೋಗುವೆನು ಅಂದುಕೊಂಡಿದ್ದೀ.",
			"26": "<sup>26</sup> ಕಳ್ಳನು ಸಿಕ್ಕಿಬಿದ್ದು ಹೇಗೆ ನಾಚಿಕೆಪಡುವನೋ ಹಾಗೆಯೇ ಇಸ್ರಾಯೇಲ್ ವಂಶವು ನಾಚಿಕೆಪಡುವದು; ಮರಕ್ಕೆ, ನೀನು ನನ್ನ ತಂದೆ ಎಂತಲೂ ಕಲ್ಲಿಗೆ, ಹೆತ್ತ ತಾಯಿ ನೀನು ಎಂತಲೂ ಹೇಳುವ ಪ್ರಜೆಗಳು, ಅವರ ಅರಸರು, ಅಧಿಪತಿಗಳು, ಯಾಜಕರು, ಪ್ರವಾದಿಗಳು, ಇವರೆಲ್ಲರೂ ನಾಚಿಕೆಗೆ ಈಡಾಗುವರು;",
			"27": "<sup>27</sup> ನನ್ನ ಕಡೆಗೆ ಮುಖ ತಿರುಗಿಸದೆ ಬೆನ್ನುಮಾಡಿದ್ದಾರೆ, ಆದರೆ ಅವರಿಗೆ ಕೇಡು ಸಂಭವಿಸಿದಾಗ - ಎದ್ದು ನಮ್ಮನ್ನುದ್ಧರಿಸು ಎಂದು ಮೊರೆಯಿಡುವರು.",
			"28": "<sup>28</sup> ಯೆಹೂದವೇ, ನೀನು ನಿರ್ಮಿಸಿಕೊಂಡ ದೇವರುಗಳು ಎಲ್ಲಿ? ನಿನಗೆ ಕೇಡು ಸಂಭವಿಸಿದಾಗ ನಿನ್ನನ್ನುದ್ಧರಿಸಲು ಶಕ್ತರಾದರೆ ಏಳಲಿ! ನಿನ್ನ ಪಟ್ಟಣಗಳೆಷ್ಟೋ, ನಿನ್ನ ದೇವರುಗಳೂ ಅಷ್ಟು.",
			"29": "<sup>29</sup> ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ಏಕೆ ನನ್ನೊಡನೆ ವ್ಯಾಜ್ಯವಾಡುತ್ತೀರಿ? ನೀವೆಲ್ಲರೂ ನನಗೆ ದ್ರೋಹಿಗಳು.",
			"30": "<sup>30</sup> ನಾನು ನಿಮ್ಮ ವಂಶದವರನ್ನು ಹೊಡೆದದ್ದು ವ್ಯರ್ಥ, ತಿದ್ದುಪಾಟಾಗಲಿಲ್ಲ; ಸಂಹರಿಸುವ ಸಿಂಹದಂತೆ ನಿಮ್ಮ ಕೈಯ ಕತ್ತಿಯೇ ನಿಮ್ಮ ಪ್ರವಾದಿಗಳನ್ನು ನುಂಗಿಬಿಟ್ಟಿದೆ.",
			"31": "<sup>31</sup> ನೀವು ಎಂಥಾ ದುರ್ವಂಶ! ಯೆಹೋವನ ಮಾತನ್ನು ನೋಡಿರಿ, ನಾನು ಇಸ್ರಾಯೇಲಿಗೆ ಅರಣ್ಯವಾಗಿಯೂ ಗಾಢಾಂಧಕಾರದ ಪ್ರದೇಶವಾಗಿಯೂ ಪರಿಣಮಿಸಿದ್ದೆನೋ? ನಾವು ಮನಬಂದಂತೆ ತಿರುಗುತ್ತಿದ್ದೇವೆ, ನಿನ್ನ ಹತೋಟಿಗೆ ಇನ್ನು ಬಾರೆವು ಎಂದು ನನ್ನ ಜನರು ಹೇಳುವದು ಹೇಗೆ.",
			"32": "<sup>32</sup> ಯುವತಿಯು ತನ್ನ ಆಭರಣಗಳನ್ನು, ವಧುವು ತನ್ನ ಒಡ್ಯಾಣವನ್ನು ಮರೆಯುವದುಂಟೇ? ನನ್ನ ಜನರೋ ಲೆಕ್ಕವಿಲ್ಲದಷ್ಟು ದಿನ ನನ್ನನ್ನು ಮರೆತಿದ್ದಾರೆ.",
			"33": "<sup>33</sup> ಕಾಮವನ್ನು ತೀರಿಸಿಕೊಳ್ಳಬೇಕೆಂದು ಎಷ್ಟೋ ಮುಂದರಿದಿದ್ದೀ! ಆದಕಾರಣ ನಿನ್ನ ದುರಭ್ಯಾಸಗಳಿಗೆ ನಿನ್ನ ನಡತೆಯನ್ನು ಒಗ್ಗಿಸಿಕೊಂಡಿದ್ದೀ.",
			"34": "<sup>34</sup> ಇದಲ್ಲದೆ ನಿರ್ದೋಷಿಗಳಾದ ದರಿದ್ರರ ಪ್ರಾಣರಕ್ತವು ನಿನ್ನ ನೆರಿಗೆಯಲ್ಲಿ ಅಂಟಿಕೊಂಡಿದೆ; ಇವರು ಕನ್ನ ಕೊರೆಯುವದನ್ನು ಕಂಡೆನೆಂಬ ನೆವ ನಿನಗಿಲ್ಲ, ನಿನ್ನ ಈ ಎಲ್ಲಾ ದುರಭ್ಯಾಸಗಳ ದೆಸೆಯಿಂದ [ನಿನ್ನನ್ನು ದಂಡಿಸುವೆನು].",
			"35": "<sup>35</sup> ನೀನಾದರೆ - ನಾನು ನಿರ್ದೋಷಿ, ಆತನ ಕೋಪವು ನನ್ನ ಮೇಲಿಂದ ತೊಲಗಿ ಹೋಗಿದೆ ನಿಶ್ಚಯ ಎಂದುಕೊಂಡಿದ್ದೀ; ನಾನು ಪಾಪಮಾಡಲಿಲ್ಲವೆಂದು ನೀನು ಹೇಳಿದ ಕಾರಣ, ಇಗೋ ನಾನು ನಿನಗೆ ನ್ಯಾಯತೀರಿಸುವೆನು.",
			"36": "<sup>36</sup> ಹೊಸ ಹೊಸ ಮಾರ್ಗಗಳನ್ನು ಅನುಸರಿಸಲು ಏಕೆ ಅಷ್ಟು ಅತ್ತಿತ್ತ ಹೋಗುತ್ತೀ? ಅಶ್ಶೂರದ ವಿಷಯವಾಗಿ ಹೇಗೆ ಆಶಾಭಂಗಪಟ್ಟಿಯೋ ಹಾಗೆಯೇ ಐಗುಪ್ತದ ವಿಷಯವಾಗಿಯೂ ಆಶಾಭಂಗಪಡುವಿ.",
			"37": "<sup>37</sup> ಅಲ್ಲಿಂದಲೂ ತಲೆಯ ಮೇಲೆ ಕೈಯಿಟ್ಟುಕೊಂಡು ಹೊರಡುವಿ; ಏಕಂದರೆ ನಿನ್ನ ಭರವಸಗಳನ್ನು ಯೆಹೋವನು ನಿರಾಕರಿಸಿದ್ದಾನೆ; ಅವುಗಳ ಮುಖಾಂತರ ನಿನ್ನ ಕಾರ್ಯವು ಸಾಗದು."
		},
		"3": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ಗಂಡನು ತ್ಯಜಿಸಿದವಳು ಅವನಿಂದ ಹೊರಟು ಮತ್ತೊಬ್ಬನವಳಾದ ಮೇಲೆ ಅವಳನ್ನು ಆ ಗಂಡನು ತಿರಿಗಿ ಸೇರಿಸಿಕೊಂಡಾನೇ? ಸೇರಿಸಿಕೊಂಡರೆ ಆ ದೇಶವು ಕೇವಲ ಅಪವಿತ್ರವಾಗಿ ಹೋಗುವದಲ್ಲವೆ! ಹೀಗಿರಲು ಬಹುಮಂದಿ ಮಿಂಡರೊಡನೆ ಸೂಳೆತನ ಮಾಡಿದ ನೀನು ನನ್ನ ಬಳಿಗೆ ತಿರಿಗಿ ಬರುತ್ತೀಯಾ?",
			"2": "<sup>2</sup> ಕಣ್ಣೆತ್ತಿ ಬೋಳುಗುಡ್ಡಗಳನ್ನು ನೋಡು, ಯಾವದರಲ್ಲಿ ನಿನ್ನನ್ನು ಕೆಡಿಸಲಿಲ್ಲ? ಅರಬಿಯನು ಅಡವಿಯಲ್ಲಿ ಹೊಂಚುಹಾಕುವ ಹಾಗೆ ನೀನು ದಾರಿಯ ಮಗ್ಗುಲಲ್ಲಿ ಅವರಿಗಾಗಿ ಹೊಂಚುತ್ತಾ ಕೂತಿದ್ದೀ; ನಿನ್ನ ಸೂಳೆತನದಿಂದಲೂ ನಿನ್ನ ಕೆಟ್ಟತನದಿಂದಲೂ ದೇಶವನ್ನು ಅಪವಿತ್ರಮಾಡಿದ್ದೀ.",
			"3": "<sup>3</sup> ಆದಕಾರಣ ಹದಮಳೆಗೆ ಅಡ್ಡಿಯಾಯಿತು, ಹಿಂಗಾರೂ ಆಗಲಿಲ್ಲ; ಆದರೂ ನೀನು ಸೂಳೆಯ ಮುಖವುಳ್ಳವಳಾಗಿ ನಾಚಿಕೆಗೆಟ್ಟಿದ್ದೀ.",
			"4": "<sup>4</sup> ಈಗತಾನೆ ನನ್ನನ್ನು - ನನ್ನ ತಂದೆ, ನನ್ನ ಯೌವನದ ಆಪ್ತನು ಎಂದು ಕರೆಯುತ್ತೀಯಲ್ಲಾ.",
			"5": "<sup>5</sup> ಆತನು ನಿತ್ಯವಾಗಿ ಕೋಪಮಾಡುವನೋ? ಕೊನೆಯ ತನಕ ಇಟ್ಟುಕೊಳ್ಳುವನೋ? ಅಂದುಕೊಳ್ಳುತ್ತಿದ್ದೀ. ಇಗೋ, ನೀನು ಹೀಗೆ ಮಾತಾಡಿದರೇನು? ದುಷ್ಕೃತ್ಯಗಳನ್ನು ನಡಿಸಿ ಕೃತಾರ್ಥಳಾಗಿದ್ದೀ.",
			"6": "<sup>6</sup> ಮತ್ತೆ ಅರಸನಾದ ಯೋಷೀಯನ ಕಾಲದಲ್ಲಿ ಯೆಹೋವನು ನನಗೆ ಹೀಗೆ ಹೇಳಿದನು - ಭ್ರಷ್ಟಳಾದ ಇಸ್ರಾಯೇಲು ಮಾಡಿದ್ದನ್ನು ನೋಡಿದಿಯಾ? ಅವಳು ಎತ್ತರವಾದ ಎಲ್ಲಾ ಗುಡ್ಡಗಳನ್ನು ಹತ್ತಿ ಸೊಂಪಾಗಿ ಬೆಳೆದಿರುವ ಎಲ್ಲಾ ಮರಗಳ ಕೆಳಗೆ ಹೋಗಿ ಸೂಳೆಯಾಗಿ ನಡೆದಳಷ್ಟೆ.",
			"7": "<sup>7</sup> ಅವಳು ಇವುಗಳನ್ನೆಲ್ಲಾ ನಡಿಸಿದ ಮೇಲೆ ನನ್ನ ಬಳಿಗೆ ಪುನಃ ಬಂದಾಳು ಅಂದುಕೊಂಡೆನು. ಆದರೆ ಬರಲಿಲ್ಲ. ಆಗ ಯೆಹೂದವೆಂಬ ದ್ರೋಹಿಯಾದ ಅವಳ ತಂಗಿಯು ಇದನ್ನು ನೋಡಿದಳು.",
			"8": "<sup>8</sup> ಭ್ರಷ್ಟಳಾದ ಇಸ್ರಾಯೇಲು ಹಾದರಮಾಡಿದ ಕಾರಣದಿಂದಲೇ ನಾನು ಅವಳನ್ನು ನಿರಾಕರಿಸಿ ತ್ಯಾಗಪತ್ರ ಕೊಟ್ಟದ್ದನ್ನು ಯೆಹೂದವೆಂಬ ದ್ರೋಹಿಯಾದ ಅವಳ ತಂಗಿಯು ನೋಡಿಯೂ ಅಂಜದೆ ತಾನೂ ಹೋಗಿ ಸೂಳೆತನವನ್ನು ನಡಿಸಿದಳು.",
			"9": "<sup>9</sup> ಕಲ್ಲು ಮರಗಳೊಡನೆ ಹಾದರಮಾಡಿ ಲಘುವೆಂದು ಭಾವಿಸಿಕೊಂಡ ತನ್ನ ಸೂಳೆತನದಿಂದ ದೇಶವನ್ನು ಅಪವಿತ್ರಪಡಿಸಿದಳು.",
			"10": "<sup>10</sup> ಇಸ್ರಾಯೇಲಿಗೆ ಇಷ್ಟು ದಂಡನೆ ಆದರೂ ಯೆಹೂದವೆಂಬ ದ್ರೋಹಿಯಾದ ಅವಳ ತಂಗಿಯು ನನ್ನ ಕಡೆಗೆ ಪೂರ್ಣಮನದಿಂದಲ್ಲ, ಕಪಟದಿಂದಲೇ ತಿರುಗಿಕೊಂಡಿದ್ದಾಳೆ ಎಂಬದು ಯೆಹೋವನಾದ ನನ್ನ ನುಡಿ.",
			"11": "<sup>11</sup> ಮತ್ತು ಯೆಹೋವನು ನನಗೆ ಹೀಗೆ ಹೇಳಿದನು - ಭ್ರಷ್ಟಳಾದ ಇಸ್ರಾಯೇಲು ದ್ರೋಹಿಯಾದ ಯೆಹೂದಕ್ಕಿಂತಲೂ ಶಿಷ್ಟಳಾಗಿ ಕಂಡುಬಂದಿದ್ದಾಳೆ.",
			"12": "<sup>12</sup> ನೀನು ಹೋಗಿ ಉತ್ತರದಿಕ್ಕಿಗೆ ಈ ಮಾತುಗಳನ್ನು ಸಾರು - ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ, ಭ್ರಷ್ಟಳಾದ ಇಸ್ರಾಯೇಲೇ, ಹಿಂದಿರುಗು; ನಾನು ಕೋಪ ಮುಖದಿಂದ ನಿನ್ನನ್ನು ನೋಡೆನು, ನಾನು ಕರುಣಾಶಾಲಿ, ನಿತ್ಯಕೋಪಿಯಲ್ಲ.",
			"13": "<sup>13</sup> ಇದೊಂದನ್ನು ಮಾಡು, ನೀನು ಕಂಡಕಡೆಯೆಲ್ಲ ತಿರುಗುತ್ತಾ ಸೊಂಪಾಗಿ ಬೆಳೆದಿರುವ ಪ್ರತಿಯೊಂದು ಮರದ ಕೆಳಗೆ ಅನ್ಯರನ್ನು ಸೇರಿ ನನ್ನ ಮಾತನ್ನು ಕೇಳದೆ ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನೆಂಬ ನನಗೆ ದ್ರೋಹಮಾಡಿದ್ದೀ ಎಂಬದಕ್ಕೆ ಒಪ್ಪಿಕೋ.",
			"14": "<sup>14</sup> ಇದೇ ಯೆಹೋವನ ನುಡಿ - ಭ್ರಷ್ಟರಾದ ಮಕ್ಕಳೇ, ತಿರುಗಿಕೊಳ್ಳಿರಿ, ನಾನು ನಿಮಗೆ ಪತಿ; ಒಂದು ಪಟ್ಟಣಕ್ಕೆ ಒಬ್ಬನಂತೆಯೂ ಗೋತ್ರಕ್ಕೆ ಇಬ್ಬರಂತೆಯೂ ಆರಿಸಿ ಚೀಯೋನಿಗೆ ಕರತರುವೆನು.",
			"15": "<sup>15</sup> ಇದಲ್ಲದೆ ನನ್ನ ಮನಸ್ಸು ಒಪ್ಪುವ ಪಾಲಕರನ್ನು ನಿಮಗೆ ದಯಪಾಲಿಸುವೆನು; ಅವರು ನಿಮ್ಮನ್ನು ಜ್ಞಾನವಿವೇಕಗಳಿಂದ ಪೋಷಿಸುವರು.",
			"16": "<sup>16</sup> ನೀವು ದೇಶದಲ್ಲಿ ಹೆಚ್ಚಿನ ವೃದ್ಧಿಗೆ ಬಂದಿರುವಾಗ ಇನ್ನು ಯೆಹೋವನ ನಿಬಂಧನಮಂಜೂಷದ ಪ್ರಸ್ತಾಪವಿರದು, ಅದು ಜ್ಞಾಪಕಕ್ಕೆಬಾರದು, ಯಾರೂ ಸ್ಮರಿಸರು, ಅದು ಇಲ್ಲವಲ್ಲಾ ಎಂದು ದುಃಖಿಸರು, ಹೊಸದಾಗಿ ಕಲ್ಪಿಸಿಕೊಳ್ಳರು, ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"17": "<sup>17</sup> ಆ ಕಾಲದಲ್ಲಿ ಯೆರೂಸಲೇಮನ್ನು ಯೆಹೋವನ ಸಿಂಹಾಸನವೆಂದು ಕರೆಯುವರು; ಯೆಹೋವನ ನಾಮಮಹತ್ವದ ಸ್ಥಾನವಾದ ಯೆರೂಸಲೇಮಿಗೆ ಸಕಲ ಜನಾಂಗಗಳವರು ನೆರೆದುಬರುವರು; ಇನ್ನು ಮೇಲೆ ತಮ್ಮ ದುಷ್ಟಹೃದಯದ ಹಟದಂತೆ ನಡೆಯರು.",
			"18": "<sup>18</sup> ಆ ಕಾಲದಲ್ಲಿ ಯೆಹೂದವಂಶವು ಇಸ್ರಾಯೇಲ್ ವಂಶದೊಡನೆ ಜೊತೆಯಾಗಿ ನಡೆಯುವದು, ಎರಡೂ ಕಲೆತು ನಾನು ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ಬಾಧ್ಯವಾಗಿ ದಯಪಾಲಿಸಿದ ದೇಶಕ್ಕೆ ಬಡಗಣ ಸೀಮೆಯೊಳಗಿಂದ ಬರುವವು.",
			"19": "<sup>19</sup> ನಾನು ಎಷ್ಟೋ ಸಂತೋಷದಿಂದ ಗಂಡು ಮಕ್ಕಳೊಳಗೆ ನಿನ್ನನ್ನೂ ಎಣಿಸಿ ನಿನಗೆ ಮನೋಹರವಾದ ದೇಶವನ್ನು ಅಂದರೆ ಸಮಸ್ತಜನಾಂಗಗಳ ಬಾಧ್ಯಗಳಲ್ಲಿ ರಮಣೀಯವಾದದ್ದನ್ನು ಕೊಡುವೆನು; ನೀನು ನನ್ನ ಅನುಸರಣೆಯನ್ನು ಬಿಟ್ಟು ಓರೆಯಾಗದೆ ನನ್ನನ್ನು ತಂದೆಯೆನ್ನುವಿ ಅಂದುಕೊಂಡೆನು.",
			"20": "<sup>20</sup> ಇಸ್ರಾಯೇಲ್ ವಂಶದವರೇ, ಒಬ್ಬ ಹೆಂಗಸು ಪತಿದ್ರೋಹ ಮಾಡಿದಂತೆ ನೀವು ನನಗೆ ದ್ರೋಹವನ್ನು ಮಾಡಿಯೇ ಇದ್ದೀರಿ ಎಂದು ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ.",
			"21": "<sup>21</sup> ಆಹಾ, ಬೋಳುಗುಡ್ಡಗಳಲ್ಲಿ ಒಂದು ಶಬ್ದ! ತಾವು ಡೊಂಕುದಾರಿಯನ್ನು ಹಿಡಿದು ತಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಮರೆತು ಬಿಟ್ಟಿದ್ದೇವೆಂದು ಇಸ್ರಾಯೇಲ್ಯರು ಕಣ್ಣೀರು ಸುರಿಸಿ ದೇವರ ಕೃಪೆಯನ್ನು ಬೇಡುತ್ತಿದ್ದಾರಲ್ಲಾ.",
			"22": "<sup>22</sup> ಭ್ರಷ್ಟರಾದ ಮಕ್ಕಳೇ, ಹಿಂದಿರುಗಿರಿ, ನಿಮ್ಮ ಭ್ರಷ್ಟತ್ವವನ್ನು ಪರಿಹರಿಸುವೆನು [ಎಂದು ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ. ಅದಕ್ಕೆ ಜನರು] - ಇಗೋ, ನಿನ್ನ ಬಳಿಗೆ ಬಂದೆವು. ನೀನು ಯೆಹೋವನು, ನಮ್ಮ ದೇವರು.",
			"23": "<sup>23</sup> ನಿಶ್ಚಯವಾಗಿ ಗುಡ್ಡಗಳಿಂದಲೂ ಬೆಟ್ಟಗಳ ಜಾತ್ರೆಯ ಗದ್ದಲದಿಂದಲೂ ಮೋಸವಾಯಿತು; ನಿಜನಿಜವಾಗಿ ಇಸ್ರಾಯೇಲಿನ ರಕ್ಷಣೆಯು ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಲ್ಲಿ ನೆಲೆಗೊಂಡಿದೆ.",
			"24": "<sup>24</sup> ನಮ್ಮ ಪಿತೃಗಳು ದುಡಿದದ್ದನ್ನೂ ಅವರ ದನಕುರಿಗಳನ್ನೂ ಅವರ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನೂ ಬಾಳ್ ದೇವತೆಯು ನಮ್ಮ ಬಾಲ್ಯಾರಭ್ಯ ನುಂಗುತ್ತಾ ಬಂದಿದೆ.",
			"25": "<sup>25</sup> ನಾವು ತಂದುಕೊಂಡ ಅವಮಾನದಲ್ಲಿ ಬಿದ್ದಿರೋಣ, ನಮ್ಮ ನಾಚಿಕೆಯು ನಮ್ಮನ್ನು ಮುಚ್ಚಿಬಿಡಲಿ; ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ನಾವೂ ನಮ್ಮ ಪಿತೃಗಳೂ ನಮ್ಮ ಚಿಕ್ಕತನದಿಂದ ಈಗಿನವರೆಗೆ ಪಾಪಮಾಡುತ್ತಾ ಬಂದಿದ್ದೇವಷ್ಟೆ; ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಮಾತನ್ನು ನಾವು ಕೇಳಲೇ ಇಲ್ಲ ಎಂದು ಮೊರೆಯಿಡುತ್ತಾರೆ."
		},
		"4": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ಇಸ್ರಾಯೇಲೇ, ನೀನು ಹಿಂದಿರುಗುವಿಯಾದರೆ ನನ್ನ ಬಳಿಗೇ ಬರುವಿ; ನನ್ನ ಕಣ್ಣೆದುರಿನಿಂದ ನಿನ್ನ ಅಸಹ್ಯವಸ್ತುಗಳನ್ನು ತೆಗೆದುಬಿಟ್ಟರೆ ನೀನು ಇನ್ನು ದಿಕ್ಕಾಪಾಲಾಗಿರುವದಿಲ್ಲ;",
			"2": "<sup>2</sup> ಯೆಹೋವನ ಜೀವದಾಣೆ ಎಂದು ಸತ್ಯನ್ಯಾಯ ಧರ್ಮಾನುಸಾರವಾಗಿ ನೀನು ಪ್ರಮಾಣಮಾಡಿದರೆ ಜನಾಂಗಗಳು ಆತನಿಂದ ತಮ್ಮನ್ನು ಆಶೀರ್ವದಿಸಿಕೊಂಡು ಆತನಲ್ಲಿ ಹೆಚ್ಚಳಪಡುವರು.",
			"3": "<sup>3</sup> ಯೆಹೋವನು ಯೆಹೂದದವರಿಗೂ ಯೆರೂಸಲೇಮಿನವರಿಗೂ ಹೀಗೆ ಹೇಳುತ್ತಾನೆ - ಗೆಯ್ಯದ ನಿಮ್ಮ ಭೂಮಿಯನ್ನು ಗೆಯ್ಯಿರಿ, ಮುಳ್ಳುಗಳಲ್ಲಿ ಬಿತ್ತಬೇಡಿರಿ.",
			"4": "<sup>4</sup> ಯೆಹೂದದವರೇ, ಯೆರೂಸಲೇಮಿನ ನಿವಾಸಿಗಳೇ, ನಿಮ್ಮ ಹೃದಯದ ಮುಂದೊಗಲನ್ನು ತೆಗೆದುಹಾಕಿ ಯೆಹೋವನಿಗಾಗಿ ಸುನ್ನತಿಯಾಗಿರಿ; ಇಲ್ಲವಾದರೆ ನಿಮ್ಮ ದುಷ್ಕೃತ್ಯಗಳ ನಿಮಿತ್ತ ನನ್ನ ರೋಷವು ಜ್ವಾಲೆಯಂತೆ ಹೊರಟು ಯಾರೂ ಆರಿಸಲಾಗದಷ್ಟು ರಭಸವಾಗಿ ಧಗಧಗಿಸುವದು.",
			"5": "<sup>5</sup> ಯೆಹೂದದಲ್ಲಿ ಸಾರಿರಿ, ಯೆರೂಸಲೇಮಿನೊಳಗೆ ಪ್ರಕಟಿಸಿರಿ; ದೇಶದಲ್ಲೆಲ್ಲಾ ಕೊಂಬೂದುವಂತೆ ಅಪ್ಪಣೆಕೊಡಿರಿ; ಕೂಡಿಕೊಂಡು ಕೋಟೆಕೊತ್ತಲಗಳ ಊರುಗಳನ್ನು ಸೇರೋಣ ಎಂದು ಕೂಗಿರಿ;",
			"6": "<sup>6</sup> ಚೀಯೋನಿನ ಕಡೆಗೆ ಧ್ವಜವನ್ನೆತ್ತಿರಿ, ತಡಮಾಡದೆ ವಲಸೆಹೋಗಿರಿ; ನಾನು ಬಡಗಲಿಂದ ವಿಪತ್ತನ್ನೂ ಘೋರನಾಶನವನ್ನೂ ಬರಮಾಡುವೆನು.",
			"7": "<sup>7</sup> ಜನಾಂಗಘಾತುಕವಾದ ಒಂದು ಸಿಂಹವು ತನ್ನ ಪೊದೆಯೊಳಗಿಂದ ಎದ್ದಿದೆ; ಹೊರಟಿದೆ, ಸ್ಥಳದಿಂದ ತೆರಳಿದೆ; ನಿನ್ನ ದೇಶವನ್ನು ಹಾಳುಮಾಡುವದು, ನಿನ್ನ ಪಟ್ಟಣಗಳು ಪಾಳುಬಿದ್ದು ನಿರ್ನಿವಾಸಿಗಳಾಗುವವು.",
			"8": "<sup>8</sup> ಇದರ ನಿಮಿತ್ತ ಗೋಣಿತಟ್ಟನ್ನು ಸುತ್ತಿಕೊಳ್ಳಿರಿ, ಹಲುಬಿ ಗೋಳಾಡಿರಿ; ಯೆಹೋವನ ರೋಷಾಗ್ನಿಯು ನಮ್ಮನ್ನು ಬಿಟ್ಟಿಲ್ಲವಲ್ಲಾ.",
			"9": "<sup>9</sup> ಆ ಕಾಲದಲ್ಲಿ ಅರಸನ ಎದೆಯು ಕುಂದುವದು, ಅಧಿಪತಿಗಳ ಹೃದಯವು ಕುಗ್ಗುವದು, ಯಾಜಕರು ಸ್ತಬ್ಧರಾಗುವರು, ಪ್ರವಾದಿಗಳು ಬೆರಗಾಗುವರು ಎಂದು ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ.",
			"10": "<sup>10</sup> ಆಗ ನಾನು - ಅಯ್ಯೋ, ಕರ್ತನಾದ ಯೆಹೋವನೇ, ನೀನು ಈ ಜನರಿಗೂ ಯೆರೂಸಲೇಮಿಗೂ ನಿಮಗೆ ಸಮಾಧಾನವಾಗುವದೆಂದು ಹೇಳಿ ನಿಶ್ಚಯವಾಗಿ ಇವರನ್ನು ಬಹಳ ಮೋಸಗೊಳಿಸಿದ್ದೀ; ಖಡ್ಗವು ಪ್ರಾಣದ ಮಟ್ಟಿಗೂ ಇಳಿಯುತ್ತದಲ್ಲಾ ಅಂದೆನು.",
			"11": "<sup>11</sup> ಆ ಕಾಲದಲ್ಲಿ ಈ ಜನರಿಗೂ ಯೆರೂಸಲೇಮಿಗೂ ಈ ಮಾತಾಗುವದು - ಬಿಸಿಗಾಳಿಯು ಅರಣ್ಯದ ಬೋಳು ಗುಡ್ಡಗಳಿಂದ ನನ್ನ ಪ್ರಜೆಯೆಂಬ ಯುವತಿಯ ಮೇಲೆ ಬೀಸುತ್ತದೆ; ಅದು ತೂರುವದಕ್ಕೂ ಶೋಧಿಸುವದಕ್ಕೂ ಆಗದು.",
			"12": "<sup>12</sup> ಇಂಥಾ ಕೆಲಸಗಳಿಗೆ ಅನುಕೂಲಿಸದಂತೆ ನನ್ನ ಅಪ್ಪಣೆಯ ಮೇರೆಗೆ ಬಿರುಸಾಗಿ ಹೊಡೆಯುತ್ತದೆ; ಈಗ ನನ್ನ ಜನರಿಗೆ ನ್ಯಾಯದಂಡನೆಗಳನ್ನು ವಿಧಿಸುವೆನು;",
			"13": "<sup>13</sup> ಇಗೋ, [ಆ ಶತ್ರುವು] ಮೇಘಗಳೋಪಾದಿಯಲ್ಲಿ ಬರುತ್ತಾನೆ, ಅವನ ರಥಗಳು ಬಿರುಗಾಳಿಯಂತಿವೆ, ಅವನ ಅಶ್ವಗಳು ಹದ್ದುಗಳಿಗಿಂತ ವೇಗವಾಗಿವೆ! ನಮ್ಮ ಗತಿಯನ್ನು ಏನು ಹೇಳೋಣ, ಹಾಳಾದೆವಲ್ಲಾ!",
			"14": "<sup>14</sup> ಯೆರೂಸಲೇಮೇ, ನಿನಗೆ ರಕ್ಷಣೆಯಾಗುವಂತೆ ನಿನ್ನ ಹೃದಯದ ಕೆಟ್ಟತನವನ್ನು ತೊಳೆದುಕೋ. ದುರಾಲೋಚನೆಗಳು ನಿನ್ನಲ್ಲಿ ಇನ್ನೆಷ್ಟರವರೆಗೆ ತಂಗಿರುವವು?",
			"15": "<sup>15</sup> ಆಹಾ, ದಾನಿನಿಂದ ಪ್ರಕಟನೆಯ ಧ್ವನಿಯು ಕೇಳಬರುತ್ತದೆ, ಎಫ್ರಾಯೀಮಿನ ಬೆಟ್ಟದಲ್ಲಿಯೂ ಕೇಡನ್ನು ಸಾರುತ್ತದೆ.",
			"16": "<sup>16</sup> ಇಗೋ, ಜನಾಂಗಗಳಿಗೆ ಅರುಹಿರಿ, ಮುತ್ತುವವರು ದೂರದೇಶದಿಂದ ಯೆರೂಸಲೇಮಿಗೆ ಬರುತ್ತಾರೆ, ಯೆಹೂದದ ಪಟ್ಟಣಗಳಿಗೆ ವಿರುದ್ಧವಾಗಿ ದನಿಗೈಯುತ್ತಾರೆ, ಎಂದು ಪ್ರಚುರಪಡಿಸಿರಿ.",
			"17": "<sup>17</sup> ಆ ನಗರಿಯು ನನಗೆ ತಿರುಗಿಬಿದ್ದ ಕಾರಣ ಅದನ್ನು ಹೊಲಕಾಯುವವರಂತೆ ಸುತ್ತಿಕೊಂಡಿದ್ದಾರೆ ಎಂದು ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ.",
			"18": "<sup>18</sup> ನಿನ್ನ ನಡತೆಯೂ ನಿನ್ನ ಕೃತ್ಯಗಳೂ ಈ ಕಷ್ಟವನ್ನು ನಿನಗೆ ಒದಗಿಸಿವೆ; ಇದು ನಿನ್ನ ದುರ್ನೀತಿಯ ಫಲ, ಇದು ವಿಷವೇ; ಹೃದಯದವರೆಗೂ ಏರುತ್ತದೆ.",
			"19": "<sup>19</sup> ಹಾ, ನನ್ನ ಕರುಳು! ಕರುಳು! ಯಾತನೆಪಡುತ್ತೇನೆ; ಆಹಾ, ನನ್ನ ಗುಂಡಿಗೆಯ ಪಕ್ಕಗಳು! ನನ್ನ ಹೃದಯವು ನನ್ನೊಳಗೆ ತಳಮಳಗೊಂಡಿದೆ! ನಾನು ಬಾಯಿಮುಚ್ಚಿಕೊಂಡಿರಲಾರೆ; ನನ್ನ ಆತ್ಮವೇ, ನೀನು ತುತೂರಿಯ ಶಬ್ದವನ್ನೂ ಯುದ್ಧಘೋಷವನ್ನೂ ಕೇಳುತ್ತೀಯಲ್ಲಾ.",
			"20": "<sup>20</sup> ನಾಶನದ ಮೇಲೆ ನಾಶನವು, ಒಂದರ ಮೇಲೊಂದು ನಾಶನದ ಸುದ್ದಿಬರುತ್ತಿದೆ; ದೇಶವೆಲ್ಲಾ ಹಾಳಾಯಿತು; ತಟ್ಟನೆ ನಮ್ಮ ಗುಡಾರಗಳು, ಕ್ಷಣಮಾತ್ರದಲ್ಲಿ ನಮ್ಮ ಡೇರೆಗಳು ಭಗ್ನವಾದವು;",
			"21": "<sup>21</sup> ನಾನು ಎಷ್ಟರವರೆಗೆ ಧ್ವಜವನ್ನು ನೋಡಲಿ, ಎಂದಿನ ತನಕ ತುತೂರಿಯ ಶಬ್ದವನ್ನು ಕೇಳಲಿ!",
			"22": "<sup>22</sup> ನನ್ನ ಜನರು ಮೂರ್ಖರು, ನನ್ನನ್ನು ತಿಳಿಯರು; ಮೂಢಸಂತತಿಯವರು, ಅವಿವೇಕಿಗಳು; ದುಷ್ಕಾರ್ಯದಲ್ಲಿ ನಿಪುಣರು, ಸತ್ಕಾರ್ಯವನ್ನು ಮಾಡಲರಿಯರು.",
			"23": "<sup>23</sup> ನಾನು [ದಿವ್ಯಜ್ಞಾನದಿಂದ] ನೋಡಿದೆನು, ಹಾ, ಭೂಲೋಕವು ಹಾಳುಪಾಳಾಗಿತ್ತು; ಆಕಾಶವನ್ನು ನೋಡಿದೆನು, ಅದರಲ್ಲಿ ಬೆಳಕೇ ಇರಲಿಲ್ಲ.",
			"24": "<sup>24</sup> ಪರ್ವತಗಳನ್ನು ನೋಡಿದೆನು, ಆಹಾ, ನಡುಗುತ್ತಿದ್ದವು, ಎಲ್ಲಾ ಗುಡ್ಡಗಳೂ ಅಲ್ಲಕಲ್ಲೋಲವಾಗಿದ್ದವು.",
			"25": "<sup>25</sup> ನಾನು ನೋಡಲಾಗಿ ಅಯ್ಯೋ, ಜನವೇ ಇರಲಿಲ್ಲ, ಆಕಾಶದ ಸಕಲ ಪಕ್ಷಿಗಳು ಹಾರಿಹೋಗಿದ್ದವು.",
			"26": "<sup>26</sup> ನಾನು ನೋಡಲಾಗಿ ಫಲವತ್ತಾದ ಭೂಮಿಯು ಕಾಡಾಗಿತ್ತು. ಅಕಟಾ! ಅಲ್ಲಿನ ಊರುಗಳೆಲ್ಲಾ ರೋಷಾಗ್ನಿಯುಕ್ತನಾದ ಯೆಹೋವನ ಪ್ರತ್ಯಕ್ಷತೆಯಿಂದ ಬಿದ್ದು ಹೋಗಿದ್ದವು.",
			"27": "<sup>27</sup> ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ದೇಶವೆಲ್ಲಾ ಬಟ್ಟಬರಿದಾಗುವದು, ಆದರೆ ನಾನು ಸಂಪೂರ್ಣವಾಗಿ ಲಯಮಾಡೆನು.",
			"28": "<sup>28</sup> ಇದಕ್ಕಾಗಿ ಭೂಲೋಕವು ಪ್ರಲಾಪಿಸುವದು, ಮೇಲೆ ಆಕಾಶವು ಕಪ್ಪಾಗುವದು; ನಾನು ನುಡಿದಿದ್ದೇನೆ, ಪಶ್ಚಾತ್ತಾಪಪಡೆನು; ಉದ್ದೇಶಿಸಿದ್ದೇನೆ, ಹಿಂದೆಗೆಯೆನು.",
			"29": "<sup>29</sup> ಪಟ್ಟಣದವರೆಲ್ಲರೂ ಸವಾರರ ಮತ್ತು ಬಿಲ್ಲುಗಾರರ ಆರ್ಭಟಕ್ಕೆ ಹೆದರಿ ಓಡುತ್ತಾರೆ; ಇತ್ತ ಪೊದೆಗಳಲ್ಲಿ ಅವಿತುಕೊಳ್ಳುತ್ತಾರೆ, ಅತ್ತ ಬಂಡೆಗಳನ್ನು ಹತ್ತುತ್ತಾರೆ; ಪ್ರತಿಯೊಂದು ಪಟ್ಟಣವು ಯಾವ ನಿವಾಸಿಯೂ ಇಲ್ಲದೆ ನಿರ್ಜನವಾಗುತ್ತದೆ.",
			"30": "<sup>30</sup> ಎಲೈ, [ನಗರಿಯೇ,] ನೀನು ಸೂರೆಯಾಗುವಾಗ ಏನು ಮಾಡುವಿ? ನೀನು ರಕ್ತಾಂಬರವನ್ನು ಹೊದ್ದು ಸುವರ್ಣಾಭರಣಗಳಿಂದ ನಿನ್ನನ್ನು ಅಲಂಕರಿಸಿಕೊಂಡು ನೀಲಾಂಜನದಿಂದ ವಿಶಾಲಾಕ್ಷಿಯಾದರೇನು, ನಿನ್ನನ್ನು ಸೊಗಸು ಮಾಡಿಕೊಳ್ಳುವದು ವ್ಯರ್ಥ; ನಿನ್ನ ಮಿಂಡರು ನಿನ್ನನ್ನು ಧಿಕ್ಕರಿಸಿ ನಿನ್ನ ಪ್ರಾಣವನ್ನೇ ಹುಡುಕುತ್ತಾರೆ.",
			"31": "<sup>31</sup> ಚೀಯೋನ್ ನಗರಿಯು ಬೇನೆ ತಿನ್ನುವವಳಂತೆ, ಚೊಚ್ಚಲಹೆರಿಗೆಯ ವೇದನೆಯನ್ನು ಅನುಭವಿಸುವವಳ ಹಾಗೆ ಕೂಗಿಕೊಳ್ಳುವದನ್ನು ನಾನು ಕೇಳಿದ್ದೇನೆ; ಏದುತ್ತಾ ಕೈಚಾಚಿ - ಅಯ್ಯೋ, ನನ್ನ ಗತಿಯನ್ನು ಏನು ಹೇಳಲಿ! ಕೊಲೆಗಾರರಿಂದ ನನ್ನ ಆತ್ಮವು ಉಡುಗುತ್ತದೆ ಎಂದು ಬಡುಕೊಳ್ಳುತ್ತಾಳೆ."
		},
		"5": {
			"1": "<sup>1</sup> ಯೆರೂಸಲೇಮಿನ ಬೀದಿಗಳಲ್ಲಿ ಅತ್ತಿತ್ತ ಓಡಾಡುತ್ತಾ ಅಲ್ಲಿನ ಚೌಕಗಳಲ್ಲಿ ಹುಡುಕಿರಿ; ನ್ಯಾಯವನ್ನು ಕೈಕೊಂಡು ಸತ್ಯವನ್ನನುಸರಿಸುವ ಒಬ್ಬನಾದರೂ ಇದ್ದಾನೋ, ಇಂಥ ಸತ್ಪುರುಷನನ್ನು ಕಂಡುಕೊಳ್ಳಬಹುದೇ ಎಂಬದನ್ನು ನೋಡಿ ನಿಶ್ಚಯಿಸಿರಿ; ಸಿಕ್ಕಿದರೆ ನಾನು ಪಟ್ಟಣವನ್ನು ಕ್ಷಮಿಸುವೆನು.",
			"2": "<sup>2</sup> ಜನರು ಯೆಹೋವನ ಜೀವದಾಣೆ ಎಂದರೂ ಆ ಆಣೆಯು ಸುಳ್ಳೇ ಸುಳ್ಳು [ಎಂದು ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ].",
			"3": "<sup>3</sup> ಯೆಹೋವನೇ, ನಿನ್ನ ಕಣ್ಣುಗಳು ಸತ್ಯದ ಮೇಲೆ ಇರುತ್ತವಲ್ಲಾ; ನೀನು ಹೊಡೆದರೂ ಅವರು ಪಶ್ಚಾತ್ತಾಪಪಡಲಿಲ್ಲ, ಸಂಹರಿಸಿದರೂ ಶಿಕ್ಷಣಕ್ಕೆ ಒಪ್ಪಲಿಲ್ಲ; ತಮ್ಮ ಮುಖವನ್ನು ಕಲ್ಲಿಗಿಂತ ಕಠಿಣಮಾಡಿಕೊಂಡು ತಿರುಗಿಕೊಳ್ಳಲೊಲ್ಲದೆ ಹೋಗಿದ್ದಾರೆ.",
			"4": "<sup>4</sup> ಹೀಗಿರುವಲ್ಲಿ ನಾನು - ಇವರು ಬಡವರು, ಮೂಢರು, ಯೆಹೋವನ ಮಾರ್ಗವನ್ನೂ ತಮ್ಮ ದೇವರ ನ್ಯಾಯವಿಧಿಗಳನ್ನೂ ತಿಳಿಯದವರು;",
			"5": "<sup>5</sup> ನಾನು ದೊಡ್ಡ ಮನುಷ್ಯರ ಬಳಿಗೆ ಹೋಗಿ ಅವರ ಸಂಗಡ ಮಾತಾಡುವೆನು; ಯೆಹೋವನ ಮಾರ್ಗವೂ ಅವರ ದೇವರ ನ್ಯಾಯವಿಧಿಗಳೂ ಅವರಿಗೆ ಗೊತ್ತು ಅಂದುಕೊಂಡೆನು. ಆದರೆ ಈ ದೊಡ್ಡ ಮನುಷ್ಯರು ಒಮ್ಮನವಾಗಿ [ತಮ್ಮ ಹೆಗಲ] ನೊಗವನ್ನು ಮುರಿದು ಕಣ್ಣಿಗಳನ್ನು ಕಿತ್ತುಬಿಟ್ಟಿದ್ದಾರೆ.",
			"6": "<sup>6</sup> ಆದಕಾರಣ ಅಡವಿಯ ಸಿಂಹವು ಅವರನ್ನು ಕೊಲ್ಲುವದು, ಕಾಡಿನ ತೋಳವು ಕೊಳ್ಳೆಮಾಡುವದು, ಚಿರತೆಯು ಅವರ ಪಟ್ಟಣಗಳಿಗೆ ಹೊಂಚುಹಾಕಿ ಅಲ್ಲಿಂದ ಹೊರಡುವ ಪ್ರತಿಯೊಬ್ಬನನ್ನೂ ಸೀಳುವದು; ಅವರ ಅಪರಾಧಗಳು ಬಹಳ, ಅವರ ದ್ರೋಹಗಳು ಅಪಾರ.",
			"7": "<sup>7</sup> ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ನಾನು ನಿನ್ನನ್ನು ಹೇಗೆ ಕ್ಷಮಿಸಲಿ? ನಿನ್ನ ಜನರು ನನ್ನನ್ನು ವಿಸರ್ಜಿಸಿ ದೇವರಲ್ಲದವುಗಳ ಮೇಲೆ ಆಣೆಯಿಟ್ಟಿದ್ದಾರೆ; ನಾನು ಅವರಿಗೆ ಹೊಟ್ಟೆತುಂಬಾ ಆಹಾರಕೊಟ್ಟ ಮೇಲೆ ಅವರು ಹಾದರ ಮಾಡಿದರು, ಸೂಳೆಯರ ಮನೆಗಳಲ್ಲಿ ಗುಂಪುಕೂಡಿದರು.",
			"8": "<sup>8</sup> ಕೊಬ್ಬಿ ಅತ್ತಿತ್ತ ಓಡಾಡುವ ಕುದುರೆಗಳಂತೆ ಅವರಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬನು ತನ್ನ ನೆರೆಯವನ ಹೆಂಡತಿಯನ್ನು ನೋಡಿ ಹೇಕರಿಸುತ್ತಿದ್ದನು.",
			"9": "<sup>9</sup> ನಾನು ಇವುಗಳಿಗಾಗಿ ದಂಡಿಸಬಾರದೋ? ಇಂಥಾ ಜನಾಂಗದ ಮೇಲೆ ನನ್ನ ರೋಷವನ್ನು ತೀರಿಸದಿರುವೆನೋ?",
			"10": "<sup>10</sup> ಚೀಯೋನೆಂಬ ತೋಟದ ಸಾಲುಗಿಡಗಳ ಮೇಲೆ ಬಿದ್ದು ಹಾಳುಮಾಡಿರಿ, ಆದರೆ ನಿರ್ಮೂಲಮಾಡಬೇಡಿರಿ; ಅದರ ರೆಂಬೆಗಳನ್ನು ತೆಗೆದುಹಾಕಿರಿ, ಅವು ಯೆಹೋವನವುಗಳಲ್ಲ.",
			"11": "<sup>11</sup> ಇಸ್ರಾಯೇಲ್ ವಂಶದವರೂ ಯೆಹೂದ ವಂಶದವರೂ ನನಗೆ ಕೇವಲ ದ್ರೋಹವನ್ನು ಮಾಡಿರುತ್ತಾರೆ ಎಂದು ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ.",
			"12": "<sup>12</sup> ಅವರು ಯೆಹೋವನನ್ನು ಅಲ್ಲಗಳೆದು - ಆತನು ಏನು ಮಾಡಿಯಾನು? ನಮಗೆ ಕೇಡುಬಾರದು, ಖಡ್ಗವೂ ಕ್ಷಾಮವೂ ನಮ್ಮ ಕಣ್ಣಿಗೆ ಬೀಳವು;",
			"13": "<sup>13</sup> ಪ್ರವಾದಿಗಳು ಬರೀ ಗಾಳಿಯೇ, ದೈವೋಕ್ತಿಯು ಅವರಲ್ಲಿಲ್ಲ; ಅವರಿಗೇ ಹೀಗಾಗಲಿ ಅಂದುಕೊಂಡಿದ್ದಾರೆ.",
			"14": "<sup>14</sup> ಹೀಗಿರಲು ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೆಂಬ ದೇವರು ಇಂತೆನ್ನುತ್ತಾನೆ - ನೀವು ಹೀಗೆ ಅಂದುಕೊಂಡದ್ದರಿಂದ ಆಹಾ, ನಾನು ಪ್ರವಾದಿಯ ಬಾಯಲ್ಲಿನ ನನ್ನ ಮಾತುಗಳನ್ನು ಬೆಂಕಿಯನ್ನಾಗಿಯೂ ಈ ಜನರನ್ನು ಸೌದೆಯನ್ನಾಗಿಯೂ ಮಾಡುವೆನು, ಅದು ಅವರನ್ನು ತಿಂದುಬಿಡುವದು.",
			"15": "<sup>15</sup> ಇಗೋ, ಇಸ್ರಾಯೇಲ್ ವಂಶವೇ, ಯೆಹೋವನ ಮಾತನ್ನು ಕೇಳು, ನಾನು ಒಂದು ಜನಾಂಗವನ್ನು ದೂರದಿಂದ ನಿಮ್ಮ ಮೇಲೆ ಬರಮಾಡುವೆನು; ಅದು ತಲತಲಾಂತರಗಳಿಂದ ಸ್ಥಿರವಾದ ಜನಾಂಗ, ಅದರ ಭಾಷೆಯು ನಿನಗೆ ತಿಳಿಯದು, ಅದು ಆಡುವ ಮಾತುಗಳನ್ನು ನೀನು ಗ್ರಹಿಸಲಾರೆ.",
			"16": "<sup>16</sup> ಅದರ ಬತ್ತಳಿಕೆಯು ಬಾಯ್ದೆರೆದ ಗೋರಿಯೇ, ಅದರ ಭಟರೆಲ್ಲರೂ ಶೂರರೇ.",
			"17": "<sup>17</sup> ಇವರು ನಿನ್ನ ಬೆಳೆಯನ್ನೂ ನಿನ್ನ ಆಹಾರವನ್ನೂ ನಿನ್ನ ಗಂಡು ಹೆಣ್ಣುಮಕ್ಕಳನ್ನೂ ನಿನ್ನ ದನಕುರಿಗಳನ್ನೂ ನಿನ್ನ ದ್ರಾಕ್ಷಾಲತೆಗಳನ್ನೂ ಅಂಜೂರದ ಗಿಡಗಳನ್ನೂ ತಿಂದುಬಿಡುವರು; ನಿನ್ನ ಭರವಸದ ಕೋಟೆಕೊತ್ತಲುಗಳ ಪಟ್ಟಣಗಳನ್ನು ಖಡ್ಗದಿಂದ ಹಾಳುಮಾಡುವರು.",
			"18": "<sup>18</sup> ಆದರೆ ಆ ದಿನಗಳಲ್ಲಿಯೂ ನಾನು ನಿಮ್ಮನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ನಿರ್ಮೂಲಮಾಡೆನು; ಇದು ಯೆಹೋವನೆಂಬ ನನ್ನ ನುಡಿ.",
			"19": "<sup>19</sup> ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಇವುಗಳನ್ನೆಲ್ಲಾ ನಮಗೆ ಏಕೆ ಮಾಡಿದ್ದಾನೆ ಎಂದು ನನ್ನ ಜನರು ಅನ್ನುವಾಗ ನೀನು ಅವರಿಗೆ - ನೀವು ಯೆಹೋವನನ್ನು ತೊರೆದು ಸ್ವದೇಶದಲ್ಲಿ ಅನ್ಯದೇವತೆಗಳನ್ನು ಸೇವಿಸಿದ ಹಾಗೆ ಪರದೇಶದಲ್ಲಿ ಅನ್ಯರನ್ನು ಸೇವಿಸುವಿರಿ ಎಂದು ಹೇಳು.",
			"20": "<sup>20</sup> ಯಾಕೋಬ ಮನೆತನದೊಳಗೆ ಇದನ್ನು ಸಾರಿರಿ, ಯೆಹೂದದಲ್ಲಿ ಹೀಗೆ ಪ್ರಕಟಿಸಿರಿ -",
			"21": "<sup>21</sup> ಕಣ್ಣಿದ್ದರೂ ಕಾಣದ, ಕಿವಿ ಇದ್ದರೂ ಕೇಳದ, ಬುದ್ಧಿಯಿಲ್ಲದ ಮೂಢಜನರೇ, ಯೆಹೋವನ ಈ ಮಾತನ್ನು ಕೇಳಿರಿ -",
			"22": "<sup>22</sup> ನೀವು ನನಗೆ ಅಂಜುವದಿಲ್ಲವೋ, ನನ್ನೆದುರಿಗೆ ನಡುಗುವದಿಲ್ಲವೋ? ಸಮುದ್ರವು ದಾಟದ ಹಾಗೆ ಅದಕ್ಕೆ ಮರಳನ್ನು ನಿತ್ಯನಿಬಂಧನೆಯಿಂದ ಮೇರೆಯನ್ನಾಗಿ ನೇಮಿಸಿದ್ದೇನಷ್ಟೆ. ತೆರೆಗಳು ಅಲ್ಲಕಲ್ಲೋಲವಾದರೂ ಮೀರಲಾರವು, ಭೋರ್ಗರೆದರೂ ಹಾಯಲಾರವು.",
			"23": "<sup>23</sup> ಈ ಜನರ ಹೃದಯವೋ [ನನ್ನ] ಅಧೀನ ತಪ್ಪಿ ತಿರುಗಿಬಿದ್ದಿದೆ; ಇವರು ನನ್ನ ಅಧೀನವನ್ನು ಮೀರಿ ಬಿಟ್ಟುಹೋಗಿದ್ದಾರೆ.",
			"24": "<sup>24</sup> ಮುಂಗಾರು, ಹಿಂಗಾರು ಅಂತು ಸಕಾಲದ ಮಳೆಯನ್ನು ದಯಪಾಲಿಸಿ ಸುಗ್ಗಿಯ ಕ್ಲುಪ್ತವಾರಗಳನ್ನು ನಮಗಾಗಿ ಪ್ರತ್ಯೇಕಪಡಿಸುವ ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಲ್ಲಿ ಭಯಭಕ್ತಿಯಿಡೋಣ ಎಂದು ಮನದಲ್ಲಿ ಅಂದುಕೊಳ್ಳುವದಿಲ್ಲ.",
			"25": "<sup>25</sup> ನಿಮ್ಮ ಅಪರಾಧಗಳು ಇವುಗಳನ್ನು ತಪ್ಪಿಸಿಬಿಟ್ಟಿವೆ, ನಿಮ್ಮ ಪಾಪಗಳು ನಿಮಗೆ ಮೇಲನ್ನು ತಡೆದಿವೆ.",
			"26": "<sup>26</sup> ನನ್ನ ಜನರಲ್ಲಿ ದುಷ್ಟರು ಕಂಡು ಬಂದಿದ್ದಾರೆ; ಬೇಡರು ಹೊಂಚುವ ಹಾಗೆ ಹೊಂಚುಹಾಕುತ್ತಾರೆ; ಬೋನೊಡ್ಡಿ ಮನುಷ್ಯರನ್ನು ಹಿಡಿಯುತ್ತಾರೆ.",
			"27": "<sup>27</sup> ಪಂಜರದಲ್ಲಿ ಪಕ್ಷಿಗಳು ತುಂಬಿರುವಂತೆ ಅವರ ಮನೆಗಳಲ್ಲಿ ಮೋಸದ ಫಲವು ತುಂಬಿದೆ; ಇದರಿಂದ ಹೆಚ್ಚಿ ಧನವಂತರಾಗಿದ್ದಾರೆ.",
			"28": "<sup>28</sup> ಕೊಬ್ಬಿ ಮೆರಮೆರುಗಾಗಿದ್ದಾರೆ; ಇದಲ್ಲದೆ ದುಷ್ಕೃತ್ಯಗಳಲ್ಲಿ ನಿಸ್ಸೀಮರಾಗಿದ್ದಾರೆ. ಅನಾಥರ ವೃದ್ಧಿಗಾಗಿ ಅವರ ಪಕ್ಷಹಿಡಿದು ವ್ಯಾಜ್ಯ ನಡಿಸರು, ದಿಕ್ಕಿಲ್ಲದವರಿಗೆ ನ್ಯಾಯ ದೊರಕಿಸರು.",
			"29": "<sup>29</sup> ನಾನು ಇವುಗಳಿಗಾಗಿ ದಂಡಿಸಬಾರದೋ? ಇಂಥಾ ಜನಾಂಗದ ಮೇಲೆ ನನ್ನ ರೋಷವನ್ನು ತೀರಿಸದಿರುವೆನೋ? ಇದು ಯೆಹೋವನಾದ ನನ್ನ ಮಾತು.",
			"30": "<sup>30</sup> ಭೀಕರವೂ ಅಸಹ್ಯವೂ ಆದ ಸಂಗತಿಯು ದೇಶದಲ್ಲಿ ನಡೆದುಬಂದಿದೆ;",
			"31": "<sup>31</sup> ಪ್ರವಾದಿಗಳು ಸುಳ್ಳಾಗಿ ಪ್ರವಾದಿಸುತ್ತಾರೆ, ಯಾಜಕರು ಅವರಿಂದ ಅಧಿಕಾರ ಹೊಂದಿ ದೊರೆತನ ಮಾಡುತ್ತಾರೆ, ನನ್ನ ಜನರು ಇದನ್ನೇ ಪ್ರೀತಿಸುತ್ತಾರೆ; ಕಟ್ಟಕಡೆಗೆ ನೀವು ಏನು ಮಾಡುವಿರಿ?"
		},
		"6": {
			"1": "<sup>1</sup> ಬೆನ್ಯಾಮೀನ್ ಕುಲದವರೇ, ಯೆರೂಸಲೇಮಿನೊಳಗಿಂದ ವಲಸೆ ಹೋಗಿರಿ, ತೆಕೋವದಲ್ಲಿ ಕೊಂಬನ್ನೂದಿರಿ, ಬೇತ್‍ಹಕ್ಕೆರೆಮಿನಲ್ಲಿ ಧ್ವಜವನ್ನೆತ್ತಿರಿ; ಏಕಂದರೆ ಅತಿನಾಶಕವಾದ ವಿಪತ್ತು ಬಡಗಲಿಂದ ತಲೆದೋರುತ್ತಿದೆ.",
			"2": "<sup>2</sup> ಚೀಯೋನ್ ನಗರಿಯು ಸೊಂಪಾದ ಹಸುರುಗಾವಲಿಗೆ ಸಮಾನವಾಗಿ ನನಗೆ ಕಾಣುತ್ತದೆ.",
			"3": "<sup>3</sup> ಕುರುಬರು ತಮ್ಮ ಹಿಂಡುಗಳೊಡನೆ ಅಲ್ಲಿಗೆ ಬಂದು ಅವರೆದುರಿಗೆ ಸುತ್ತಲೂ ತಮ್ಮ ಗುಡಾರಗಳನ್ನು ಹಾಕುವರು; ಪ್ರತಿಯೊಬ್ಬನು ತನ್ನ ತನ್ನ ಸ್ಥಳದಲ್ಲಿ ಮೇಯಿಸಿಬಿಡುವನು.",
			"4": "<sup>4</sup> [ಇವರು] - ಚೀಯೋನಿಗೆ ವಿರುದ್ಧವಾಗಿ ಸನ್ನದ್ಧರಾಗಿರಿ; ಏಳಿರಿ, ಮಧ್ಯಾಹ್ನಕ್ಕೆ ಅದರ ಮೇಲೆ ನುಗ್ಗುವ! ಅಯ್ಯೋ, ಇದೇನು! ನಮಗೆ ಹೊತ್ತು ತಿರುಗಿತಲ್ಲಾ, ಸಂಜೆಯ ನೆರಳುಗಳು ಉದ್ದವಾದವು.",
			"5": "<sup>5</sup> ಎದ್ದು ರಾತ್ರಿಯಲ್ಲಿ ಅದರ ಮೇಲೆ ಬಿದ್ದು ಅಲ್ಲಿನ ಅರಮನೆಗಳನ್ನು ಹಾಳುಮಾಡೋಣ [ಅಂದುಕೊಳ್ಳುವರು].",
			"6": "<sup>6</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಹೀಗೆ ಹೇಳಿದ್ದಾನೆ - ಸುತ್ತಣ ಮರಗಳನ್ನು ಕಡಿದುಬಿಡಿರಿ, ಯೆರೂಸಲೇಮಿನೆದುರಿಗೆ ದಿಬ್ಬ ಹಾಕಿರಿ; ಇದೇ ನೀವು ದಂಡಿಸತಕ್ಕ ಪಟ್ಟಣ; ಅದರೊಳಗೆ ಬರೀ ದೋಚಾಟವೇ ತುಂಬಿದೆ.",
			"7": "<sup>7</sup> ತೊಟ್ಟಿಯು ತನ್ನಲ್ಲಿನ ನೀರನ್ನು ತಿಳಿಯಾಗಿ ಇಟ್ಟುಕೊಳ್ಳುವಂತೆ ಈ ಪಟ್ಟಣವು ತನ್ನಲ್ಲಿನ ದುಷ್ಟತನವನ್ನು ನಿಚ್ಚಳವಾಗಿ ಇಟ್ಟುಕೊಂಡಿದೆ; ಅಲ್ಲಿ ಬಲಾತ್ಕಾರದ ಮತ್ತು ಕೊಳ್ಳೆಯ ಸುದ್ದಿಯೇ ಕೇಳಬರುತ್ತದೆ; ರೋಗಗಳೂ ವ್ರಣಗಳೂ ಸದಾ ನನ್ನ ಕಣ್ಣಿಗೆ ಬೀಳುತ್ತವೆ.",
			"8": "<sup>8</sup> ಯೆರೂಸಲೇಮೇ, ನಾನು ನಿನ್ನನ್ನು ಅಗಲಿ ನಾಶಪಡಿಸಿ ನಿವಾಸಿಗಳಿಲ್ಲದ ದೇಶವನ್ನಾಗಿ ಮಾಡದಂತೆ ಶಿಕ್ಷಣವನ್ನು ಹೊಂದು.",
			"9": "<sup>9</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ಇಸ್ರಾಯೇಲಿನ ಶೇಷವನ್ನು ದ್ರಾಕ್ಷೆಯ ಹಕ್ಕಲನ್ನೋ ಎಂಬಂತೆ ಆಯಬೇಕು; ದ್ರಾಕ್ಷೆಯ ಹಣ್ಣನ್ನು ಕೀಳುವವನಂತೆ ನೀನು ನಿನ್ನ ಕೈಯನ್ನು ತಿರಿಗಿ ರೆಂಬೆಗಳಲ್ಲಿ ಹಾಕು.",
			"10": "<sup>10</sup> [ಅದಕ್ಕೆ ನಾನು] - ಯಾರ ಸಂಗಡ ಮಾತಾಡಿ ಅವರು ಗಮನಿಸುವಂತೆ ಸಾಕ್ಷಿಕೊಡಲಿ? ಇಗೋ, ಅವರು ಕರ್ಣಶುದ್ಧಿಯಿಲ್ಲದವರು, ಕೇಳಲಾರರು; ಅಯ್ಯೋ, ಯೆಹೋವನ ಮಾತು ಅವರಿಗೆ ತಿರಸ್ಕಾರ, ಅದರಲ್ಲಿ ಅವರಿಗೆ ಆನಂದವೇ ಇಲ್ಲ.",
			"11": "<sup>11</sup> ಆದಕಾರಣ ಯೆಹೋವನ ರೋಷವು ನನ್ನಲ್ಲಿ ತುಂಬಿತುಳುಕುತ್ತದೆ; ತಡೆದು ತಡೆದು ನನಗೆ ಸಾಕಾಯಿತು; ಬೀದಿಯಲ್ಲಿನ ಮಕ್ಕಳ ಮೇಲೂ ಯುವಕರ ಸಂಘದ ಮೇಲೂ ಅದನ್ನು ಹೊಯ್ದುಬಿಡಬೇಕು; ಗಂಡನೂ ಹೆಂಡತಿಯೂ ಮುದುಕನೂ ಮುಪ್ಪಿನ ಮುದುಕನೂ (ಅಂತು ಎಲ್ಲರೂ) ಅಪಹರಿಸಲ್ಪಡುವರು.",
			"12": "<sup>12</sup> ಅವರ ಹೆಂಡತಿ, ಮನೆ, ಹೊಲಗದ್ದೆ ಇವೆಲ್ಲಾ ಅನ್ಯರ ಪಾಲಾಗುವವು; ದೇಶದ ನಿವಾಸಿಗಳ ಮೇಲೆ ಕೈಮಾಡುವೆನಷ್ಟೆ ಎಂದು ಯೆಹೋವನು ಹೇಳಿದ್ದಾನೆ.",
			"13": "<sup>13</sup> ಚಿಕ್ಕವರು ಮೊದಲುಗೊಂಡು ದೊಡ್ಡವರ ತನಕ ಎಲ್ಲರು ಬಾಚಿಕೊಳ್ಳುತ್ತಲೇ ಇದ್ದಾರೆ; ಪ್ರವಾದಿಗಳು ಮೊದಲಾಗಿ ಯಾಜಕರವರೆಗೆ ಸಕಲರು ಮೋಸಮಾಡುತ್ತಾರೆ.",
			"14": "<sup>14</sup> ಮೇಲಾಗದಿದ್ದರೂ ಮೇಲು ಮೇಲು ಎಂದು ಹೇಳಿ ನನ್ನ ಜನರ ಗಾಯವನ್ನು ಮೇಲೆ ಮೇಲೆ ವಾಸಿಮಾಡಿದ್ದಾರೆ.",
			"15": "<sup>15</sup> ಅಸಹ್ಯಕಾರ್ಯಗಳನ್ನು ಮಾಡಿ ಅವಮಾನಕ್ಕೆ ಗುರಿಯಾದರೂ ಎಳ್ಳಷ್ಟೂ ನಾಚಿಕೆಪಡಲಿಲ್ಲ, ಲಜ್ಜೆಯ ಗಂಧವನ್ನೂ ತಿಳಿಯಲಿಲ್ಲ; ಆದಕಾರಣ ನಾನು ದಂಡಿಸುವ ಸಮಯದಲ್ಲಿ ಅವರು ಮುಗ್ಗರಿಸುವರು, ಬೀಳುವವರ ಸಂಗಡ ಬಿದ್ದೇ ಹೋಗುವರು ಎಂದು ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ.",
			"16": "<sup>16</sup> ಯೆಹೋವನು ಹೀಗೆ ನುಡಿಯುತ್ತಾನೆ - ದಾರಿಗಳು ಕೂಡುವ ಸ್ಥಳದಲ್ಲಿ ನಿಂತುಕೊಂಡು ನೋಡಿ ಪುರಾತನ ಮಾರ್ಗಗಳು ಯಾವವು, ಆ ಸನ್ಮಾರ್ಗವು ಎಲ್ಲಿ ಎಂದು ವಿಚಾರಿಸಿ ಅದರಲ್ಲೇ ನಡೆಯಿರಿ; ಇದರಿಂದ ನಿಮ್ಮ ಆತ್ಮಗಳಿಗೆ ವಿಶ್ರಾಂತಿ ಸಿಕ್ಕುವದು (ಎಂದು ನಾನು ಅಪ್ಪಣೆಕೊಟ್ಟಾಗ) ಅದರಲ್ಲಿ ನಾವು ನಡೆಯುವದೇ ಇಲ್ಲವೆಂದರು.",
			"17": "<sup>17</sup> ತುತೂರಿಯ ಶಬ್ದವನ್ನು ಕೇಳಿರಿ ಎಂದು ನಾನು ಅವರ ಮೇಲೆ ಕಾವಲುಗಾರರನ್ನು ನೇಮಿಸಲು ನಾವು ಕೇಳಲೊಲ್ಲೆವು ಎಂದು ಹೇಳಿದರು.",
			"18": "<sup>18</sup> ಆದಕಾರಣ, ಜನಾಂಗಗಳೇ, ಕೇಳಿರಿ! ಜನಸಮೂಹವೇ, ಅವರಲ್ಲಿ ನಡೆದದ್ದನ್ನು ಮನಸ್ಸಿಗೆ ತಂದುಕೋ!",
			"19": "<sup>19</sup> ಭೂಲೋಕವೇ, ಕೇಳು! ಆಹಾ, ಈ ಜನರು ನನ್ನ ಮಾತುಗಳನ್ನು ಕೇಳದೆ ನನ್ನ ಧರ್ಮಬೋಧನೆಯನ್ನು ಅಸಡ್ಡೆ ಮಾಡಿದ್ದರಿಂದ ಇವರ ಆಲೋಚನೆಗಳ ಫಲವಾದ ಕೇಡನ್ನು ಇವರ ಮೇಲೆ ಬರಮಾಡುವೆನು.",
			"20": "<sup>20</sup> ನೀವು ಶೆಬದ ಧೂಪವನ್ನೂ ದೂರದೇಶದ ಒಳ್ಳೆ ಬಜೆಯನ್ನೂ ನನಗೆ ಅರ್ಪಿಸುವದರಿಂದ ಏನು ಪ್ರಯೋಜನ? ನಿಮ್ಮ ಹೋಮಗಳು ನನಗೆ ಮೆಚ್ಚಿಕೆಯಲ್ಲ, ನಿಮ್ಮ ಯಜ್ಞಗಳು ನನಗೆ ಇಷ್ಟವಲ್ಲ.",
			"21": "<sup>21</sup> ಆದಕಾರಣ ಇಗೋ, ನಾನು ಈ ಜನರ ಮುಂದೆ ಆಟಂಕಗಳನ್ನು ಒಡ್ಡುವೆನು; ತಂದೆಗಳೂ ಮಕ್ಕಳೂ ಅವುಗಳನ್ನು ಎಡವುವರು, ನೆರೆಹೊರೆಯವರೂ ನಾಶವಾಗುವರು. ಇದೇ ಯೆಹೋವನಾದ ನನ್ನ ನುಡಿ.",
			"22": "<sup>22</sup> ಆಹಾ, ಬಡಗಲಿಂದ ಒಂದು ಜನಾಂಗವು ಬರುತ್ತದೆ, ಮಹಾ ಜನವು ಎಬ್ಬಿಸಲ್ಪಟ್ಟು ಲೋಕದ ಕಟ್ಟಕಡೆಯಿಂದ ಹೊರಡುತ್ತದೆ.",
			"23": "<sup>23</sup> ಬಿಲ್ಲನ್ನೂ ಈಟಿಯನ್ನೂ ಹಿಡಿದುಕೊಂಡಿದ್ದಾರೆ, ಅವರು ಕ್ರೂರರು, ನಿಷ್ಕರುಣಿಗಳು; ಅವರ ಧ್ವನಿಯು ಸಮುದ್ರದಂತೆ ಮೊರೆಯುತ್ತದೆ, ಕುದುರೆ ಹತ್ತಿದ್ದಾರೆ; ಚೀಯೋನ್ ನಗರಿಯೇ, ಆ ಸೈನ್ಯವು ಶೂರನಂತೆ ನಿನ್ನ ಮೇಲೆ ಯುದ್ಧಸನ್ನದ್ಧವಾಗಿದೆ ಎಂದು ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ.",
			"24": "<sup>24</sup> ನಾವು ಅದರ ಸುದ್ದಿಯನ್ನು ಕೇಳಿದೆವು, ನಮ್ಮ ಕೈಗಳು ಜೋಲುಬಿದ್ದವು. ಪ್ರಸವವೇದನೆಯಂತಿರುವ ಯಾತನೆಯು ನಮ್ಮನ್ನು ಹಿಡಿದಿದೆ.",
			"25": "<sup>25</sup> ಊರ ಹೊರಗೆ ಹೋಗಬೇಡಿರಿ, ದಾರಿಯಲ್ಲಿ ನಡೆಯಬೇಡಿರಿ; ಶತ್ರುವಿನ ಕತ್ತಿಯಿದೆ, ಸುತ್ತುಮುತ್ತಲು ದಿಗಿಲು.",
			"26": "<sup>26</sup> ನನ್ನ ಪ್ರಜೆಯೆಂಬ ಯುವತಿಯೇ, ಗೋಣಿತಟ್ಟನ್ನು ಸುತ್ತಿಕೊಂಡು ಬೂದಿಯಲ್ಲಿ ಬಿದ್ದು ಹೊರಳಾಡು; ಇದ್ದೊಬ್ಬ ಮಗನನ್ನು ಕಳೆದುಕೊಂಡಂತೆ ದುಃಖಪಟ್ಟು ಘೋರಪ್ರಲಾಪಮಾಡು; ಕೊಳ್ಳೆಗಾರನು ತಟ್ಟನೆ ನಮ್ಮ ಮೇಲೆ ಬೀಳುವನಷ್ಟೆ.",
			"27": "<sup>27</sup> ಯೆಹೋವನು ನನಗೆ - ನಾನು ನಿನ್ನನ್ನು ನನ್ನ ಜನವೆಂಬ ಅದುರಿಗೆ ಶೋಧಕನನ್ನಾಗಿ ನೇಮಿಸಿದ್ದೇನೆ; ನೀನು ಅವರ ನಡತೆಯನ್ನು ಪರೀಕ್ಷಿಸಿ ತಿಳಿದುಕೊಳ್ಳಬೇಕು.",
			"28": "<sup>28</sup> ಅವರೆಲ್ಲರೂ ಕೇವಲ ದ್ರೋಹಿಗಳು, ಚಾಡಿಹೇಳುತ್ತಾ ತಿರುಗಾಡುವವರು; ಅವರು [ಬೆಳ್ಳಿಯಲ್ಲಾ,] ತಾಮ್ರ, ಕಬ್ಬಿಣ; ಎಲ್ಲರೂ ಕೇಡಿಗರು.",
			"29": "<sup>29</sup> ತಿದಿಯು ಬುಸುಬುಸುಗುಟ್ಟುತ್ತದೆ, ಸೀಸವು ಉರಿಯಿಂದ ಸುಟ್ಟುಹೋಗುತ್ತದೆ, ಅಕ್ಕಸಾಲಿಗನು ಎಷ್ಟು ಶೋಧಿಸಿದರೂ ನಿಷ್ಪ್ರಯೋಜನ, ಕಲ್ಮಷವು ತೆಗೆಯಲ್ಪಡುವದಿಲ್ಲ.",
			"30": "<sup>30</sup> ಯೆಹೋವನಾದ ನಾನು ಇವರನ್ನು ನೂಕಿಬಿಟ್ಟದ್ದರಿಂದ ಕಂದುಬೆಳ್ಳಿಯೆನಿಸಿಕೊಳ್ಳುವರು ಎಂದು ಹೇಳಿದ್ದಾನೆ."
		},
		"7": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಯೆರೆಮೀಯನಿಗೆ ಈ ಮಾತನ್ನು ದಯಪಾಲಿಸಿದನು -",
			"2": "<sup>2</sup> ನೀನು ಯೆಹೋವನ ಆಲಯದ ಬಾಗಿಲಿನಲ್ಲಿ ನಿಂತು ಈ ವಾಕ್ಯವನ್ನು ಅಲ್ಲಿ ಸಾರು - ಯೆಹೋವನಿಗೆ ಅಡ್ಡಬೀಳುವದಕ್ಕೆ ಈ ದ್ವಾರಗಳನ್ನು ಪ್ರವೇಶಿಸುವ ಸಕಲ ಯೆಹೋದ್ಯರೇ, ಯೆಹೋವನ ನುಡಿಯನ್ನು ಕೇಳಿರಿ.",
			"3": "<sup>3</sup> ಇಸ್ರಾಯೇಲಿನ ದೇವರೂ ಸೇನಾಧೀಶ್ವರನೂ ಆದ ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ನೀವು ನಿಮ್ಮ ನಡತೆಯನ್ನೂ ಕೃತ್ಯಗಳನ್ನೂ ತಿದ್ದಿಕೊಳ್ಳಿರಿ; ಹೀಗೆ ಮಾಡಿದರೆ ನಾನು ನಿಮ್ಮನ್ನು ಈ ಸ್ಥಳದಲ್ಲಿ ನೆಲೆಗೊಳಿಸುವೆನು.",
			"4": "<sup>4</sup> ಈ ಕಟ್ಟಡಗಳೇ ಯೆಹೋವನ ಆಲಯ, ಯೆಹೋವನ ಆಲಯ, ಯೆಹೋವನ ಆಲಯ ಎಂಬುವ ಸುಳ್ಳುಮಾತುಗಳ ಮೇಲೆ ಭರವಸವಿಡಬೇಡಿರಿ.",
			"5": "<sup>5</sup> ನೀವು ನಿಮ್ಮ ನಡತೆಯನ್ನೂ ಕೃತ್ಯಗಳನ್ನೂ ಸಂಪೂರ್ಣವಾಗಿ ತಿದ್ದಿಕೊಂಡು ಕಕ್ಷಿಪ್ರತಿಕಕ್ಷಿಗಳಿಗೆ ಕೇವಲ ನ್ಯಾಯವನ್ನೇ ತೀರಿಸಿ",
			"6": "<sup>6</sup> ಪರದೇಶಿಯನ್ನಾಗಲಿ ಅನಾಥನನ್ನಾಗಲಿ ವಿಧವೆಯನ್ನಾಗಲಿ ಹಿಂಸಿಸದೆ ಈ ಸ್ಥಳದಲ್ಲಿ ನಿರ್ದೋಷಿಗಳ ರಕ್ತವನ್ನು ಸುರಿಸದೆ ನಿಮಗೆ ಹಾನಿಕರವಾದ ಅನ್ಯದೇವತಾಭಕ್ತಿಯನ್ನು ಮಾಡದೆ ಇದ್ದರೆ",
			"7": "<sup>7</sup> ನಾನು ನಿಮ್ಮನ್ನು ಈ ಸ್ಥಳದಲ್ಲಿ, ನಾನೇ ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ಶಾಶ್ವತವಾಗಿ ದಯಪಾಲಿಸಿದ ಈ ದೇಶದಲ್ಲಿ ನೆಲೆಗೊಳಿಸುವೆನು.",
			"8": "<sup>8</sup> ಆಹಾ, ನೀವು ನಿಷ್ಪ್ರಯೋಜನವಾದ ಸುಳ್ಳುಮಾತುಗಳಲ್ಲಿ ಭರವಸವಿಟ್ಟಿದ್ದೀರಿ.",
			"9": "<sup>9</sup> ನೀವು ಕಳವು ಕೊಲೆ ಹಾದರಗಳನ್ನು ಮಾಡಿ ಸುಳ್ಳುಸಾಕ್ಷಿ ಹೇಳಿ ಬಾಳನಿಗೆ ಹೋಮವನ್ನರ್ಪಿಸಿ ಕಂಡುಕೇಳದ ಅನ್ಯದೇವತೆಗಳನ್ನು ಹಿಂಬಾಲಿಸಿದ ಮೇಲೆ",
			"10": "<sup>10</sup> ನನ್ನ ಹೆಸರುಗೊಂಡಿರುವ ಈ ಆಲಯಕ್ಕೆ ಬಂದು ನನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನಿಂತು - ನಮಗೆ ಪರಿಹಾರವಾಯಿತು ಎಂದು ಹೇಳಿ ಈ ಎಲ್ಲಾ ಅಸಹ್ಯಕಾರ್ಯಗಳನ್ನು ಇನ್ನೂ ನಡಿಸುವದಕ್ಕೆ ಅವಕಾಶ ಮಾಡಿಕೊಳ್ಳುವಿರೋ?",
			"11": "<sup>11</sup> ನನ್ನ ಹೆಸರುಗೊಂಡಿರುವ ಈ ಆಲಯವು ನಿಮ್ಮ ದೃಷ್ಟಿಯಲ್ಲಿ ಕಳ್ಳರ ಗವಿಯಾಯಿತೋ? ಆಹಾ, ನಾನೇ, ನಾನೇ ಇದನ್ನೆಲ್ಲಾ ನೋಡಿದ್ದೇನೆ ಎಂಬದು ಯೆಹೋವನಾದ ನನ್ನ ನುಡಿ.",
			"12": "<sup>12</sup> ನಾನು ಮೊದಲು ನನ್ನ ನಾಮವನ್ನು ಸ್ಥಾಪಿಸಿದ ಶೀಲೋವಿನಲ್ಲಿನ ನನ್ನ ಸ್ಥಾನಕ್ಕೆ ನೀವು ಹೋಗಿ ನನ್ನ ಜನರಾದ ಇಸ್ರಾಯೇಲ್ಯರ ಅಧರ್ಮದ ನಿಮಿತ್ತ ನಾನು ಅದಕ್ಕೆ ತಂದ ಗತಿಯನ್ನು ನೋಡಿರಿ.",
			"13": "<sup>13</sup> ನಾನು ಸಾವಕಾಶ ಮಾಡದೆ ನಿಮಗೆ ನುಡಿದ ಮಾತುಗಳನ್ನು ನೀವು ಕೇಳದೆ ನಿಮ್ಮನ್ನು ಕರೆದ ನನಗೆ ಉತ್ತರಕೊಡದೆ",
			"14": "<sup>14</sup> ಈ ಕೃತ್ಯಗಳನ್ನೆಲ್ಲಾ ನಡಿಸಿದ ಕಾರಣ ನನ್ನ ನಾಮಕ್ಕೆ ನೆಲೆಯಾಗಿ ನಿಮ್ಮ ಭರವಸಕ್ಕೆ ಆಧಾರವಾದ ಆಲಯಕ್ಕೂ ನಾನು ನಿಮಗೆ ಮತ್ತು ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ದಯಪಾಲಿಸಿದ ಸ್ಥಳಕ್ಕೂ ಶೀಲೋವಿಗೆ ತಂದ ಗತಿಯನ್ನೇ ಈಗ ತರುವೆನು.",
			"15": "<sup>15</sup> ಮತ್ತು ನಾನು ನಿಮ್ಮ ಎಲ್ಲಾ ಸಹೋದರರನ್ನು ಅಂದರೆ ಸಮಸ್ತ ಎಫ್ರಾಯೀಮ್ ವಂಶದವರನ್ನು ಎಸೆದುಬಿಟ್ಟ ಹಾಗೆ ನಿಮ್ಮನ್ನೂ ನನ್ನ ಕಣ್ಣೆದುರಿನಿಂದ ಎಸೆದುಬಿಡುವೆನು.",
			"16": "<sup>16</sup> ನೀನಂತು ಈ ಜನರಿಗೋಸ್ಕರ ಬೇಡಿಕೊಳ್ಳಬೇಡ, ಇವರಿಗಾಗಿ ಮೊರೆಯಿಡಬೇಡ, ಪ್ರಾರ್ಥಿಸಬೇಡ, ನನಗೆ ವಿಜ್ಞಾಪಿಸಲೂಬೇಡ, ನಾನು ಕೇಳಲೊಲ್ಲೆ.",
			"17": "<sup>17</sup> ಇವರು ಯೆಹೂದದ ಪಟ್ಟಣಗಳಲ್ಲಿಯೂ ಯೆರೂಸಲೇಮಿನ ಬೀದಿಗಳಲ್ಲಿಯೂ ಮಾಡುವದನ್ನು ನೀನು ನೋಡಲಿಲ್ಲವೋ?",
			"18": "<sup>18</sup> ಗಗನದ ಒಡತಿಗೆ ಹೋಳಿಗೆಗಳನ್ನು ಮಾಡುವದಕ್ಕಾಗಿ ಮಕ್ಕಳು ಸೌದೆಯನ್ನು ಆರಿಸುತ್ತಾರೆ, ತಂದೆಗಳು ಬೆಂಕಿಯನ್ನು ಹೊತ್ತಿಸುತ್ತಾರೆ, ಹೆಂಗಸರು ಕಣಕವನ್ನು ನಾದುತ್ತಾರೆ; ಅನ್ಯದೇವತೆಗಳಿಗೆ ಪಾನನೈವೇದ್ಯವನ್ನು ಅರ್ಪಿಸುತ್ತಾರೆ; ನನ್ನನ್ನು ಕೆಣಕಬೇಕೆಂದೇ ಇದನ್ನೆಲ್ಲಾ ಮಾಡುತ್ತಾರೆ.",
			"19": "<sup>19</sup> ಅವರು ನನಗೆ ಕೇಡುಮಾಡುತ್ತಾರೋ, ಅವಮಾನವು ತಮ್ಮ ಮುಖವನ್ನು ಮುಚ್ಚುವಷ್ಟರ ಮಟ್ಟಿಗೆ ತಮಗೇ ಕೇಡುಮಾಡಿಕೊಳ್ಳುತ್ತಾರಲ್ಲಾ ಎಂದು ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ.",
			"20": "<sup>20</sup> ಹೀಗಿರಲು ಕರ್ತನಾದ ಯೆಹೋವನ ಈ ಮಾತನ್ನು ಕೇಳಿರಿ - ಆಹಾ, ನನ್ನ ಕೋಪವೆಂಬ ರೋಷಾಗ್ನಿಯು ಈ ಸ್ಥಳದಲ್ಲಿ ಸುರಿಯಲ್ಪಡುವದು; ಮನುಷ್ಯರ ಮೇಲೆಯೂ ಪಶುಗಳ ಮೇಲೆಯೂ ಕಾಡುಮರಗಳ ಮೇಲೆಯೂ ಭೂಮಿಯ ಬೆಳೆಯ ಮೇಲೆಯೂ ಅದನ್ನು ಸುರಿಯುವೆನು; ಆರದೆ ದಹಿಸುವದು.",
			"21": "<sup>21</sup> ಇಸ್ರಾಯೇಲಿನ ದೇವರೂ ಸೇನಾಧೀಶ್ವರನೂ ಆದ ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ನೀವು ಅರ್ಪಿಸುವ ಸರ್ವಾಂಗಹೋಮಗಳನ್ನು [ಸಾಧಾರಣ] ಯಜ್ಞಗಳೆಂದು ಭಾವಿಸಿ ಅವುಗಳ ಮಾಂಸವನ್ನೂ ತಿನ್ನಿರಿ.",
			"22": "<sup>22</sup> ನಿಮ್ಮ ಪಿತೃಗಳನ್ನು ಐಗುಪ್ತದೇಶದೊಳಗಿಂದ ಬರಮಾಡಿದಾಗ ಸರ್ವಾಂಗಹೋಮದ ಮತ್ತು ಯಜ್ಞದ ವಿಷಯವಾಗಿ ನಾನು ಅವರಿಗೆ ಏನೂ ಹೇಳಲಿಲ್ಲ, ಯಾವ ಅಪ್ಪಣೆಯನ್ನೂ ಕೊಡಲಿಲ್ಲ;",
			"23": "<sup>23</sup> ಆದರೆ ಈ ಒಂದಪ್ಪಣೆಯನ್ನು ಮಾತ್ರ ಕೊಟ್ಟೆನು - ನನ್ನ ಧ್ವನಿಗೆ ಕಿವಿಗೊಡಿರಿ, ನಾನು ನಿಮ್ಮ ದೇವರಾಗಿರುವೆನು, ನೀವು ನನ್ನ ಪ್ರಜೆಯಾಗಿರುವಿರಿ; ನಿಮಗೆ ಹಿತವಾಗುವಂತೆ ನಾನು ವಿಧಿಸುವ ಮಾರ್ಗದಲ್ಲೇ ನಡೆಯಿರಿ.",
			"24": "<sup>24</sup> ಅವರಾದರೋ ಕೇಳಲಿಲ್ಲ, ಕಿವಿಗೊಡಲಿಲ್ಲ, ಸ್ವಂತ ಆಲೋಚನೆಗಳನ್ನು ಅನುಸರಿಸಿ ತಮ್ಮ ದುಷ್ಟಹೃದಯದ ಹಟದಂತೆ ನಡೆದು ಹಿಂದಿರುಗಿಯೇ ಹೋದರು, ಮುಂದರಿಯಲಿಲ್ಲ.",
			"25": "<sup>25</sup> ನಿಮ್ಮ ಪಿತೃಗಳು ಐಗುಪ್ತದೇಶದೊಳಗಿಂದ ಹೊರಟ ದಿನ ಮೊದಲುಗೊಂಡು ಈ ದಿನದವರೆಗೂ ನನ್ನ ಸೇವಕರಾದ ಪ್ರವಾದಿಗಳನ್ನೆಲ್ಲಾ ನಿಮ್ಮವರ ಬಳಿಗೆ ಕಳುಹಿಸುತ್ತಾ ಬಂದಿದ್ದೇನೆ, ದಿನ ದಿನವೂ ಸಾವಕಾಶ ಮಾಡದೆ ಕಳುಹಿಸಿದ್ದೇನೆ.",
			"26": "<sup>26</sup> ನಿಮ್ಮವರಾದರೋ ನನ್ನ ಮಾತನ್ನು ಕೇಳಲಿಲ್ಲ, ಕಿವಿಗೊಡಲಿಲ್ಲ, ನನ್ನ ಆಜ್ಞೆಗೆ ಮಣಿಯಲಿಲ್ಲ; ತಮ್ಮ ಪಿತೃಗಳಿಗಿಂತಲೂ ಕೆಟ್ಟವರಾಗಿ ನಡೆದರು.",
			"27": "<sup>27</sup> ನೀನು ಈ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ಹೇಳುವಾಗ ಅವರು ಕಿವಿಗೊಡುವದಿಲ್ಲ, ಕರೆಯುವಾಗ ಉತ್ತರಕೊಡುವದಿಲ್ಲ.",
			"28": "<sup>28</sup> ಆದಕಾರಣ ನೀನು ಅವರಿಗೆ - ತಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಧ್ವನಿಗೆ ಕಿವಿಗೊಡದೆ ಶಿಕ್ಷಿತರಾಗದೆ ಇರುವ ಜನವು ಇದೇ, ಸತ್ಯವು ಅವರ ಬಾಯಿಂದ ನಿರ್ಮೂಲವಾಗಿ ಅಳಿದುಹೋಗಿದೆ ಎಂದು ಹೇಳು.",
			"29": "<sup>29</sup> [ಯೆರೂಸಲೇಮೇ,] ತಲೆಯನ್ನು ಬೋಳಿಸಿಕೊಂಡು ಕೂದಲನ್ನು ಬಿಸಾಟುಬಿಟ್ಟು ಬೋಳುಗುಡ್ಡಗಳಲ್ಲಿ ಶೋಕಗೀತವನ್ನು ಹಾಡು; ಯೆಹೋವನು ತನ್ನ ಕೋಪಕ್ಕೆ ಗುರಿಯಾದ ಈ ವಂಶವನ್ನು ನಿರಾಕರಿಸಿ ತ್ಯಜಿಸಿದ್ದಾನೆ.",
			"30": "<sup>30</sup> ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ಯೆಹೂದ್ಯರು ನನ್ನ ಚಿತ್ತಕ್ಕೆ ವಿರುದ್ಧವಾದದ್ದನ್ನೇ ನಡಿಸಿದ್ದಾರೆ, ನನ್ನ ಹೆಸರಿನಿಂದ ಖ್ಯಾತಿಗೊಂಡ ಆಲಯದಲ್ಲಿ ತಮ್ಮ ಅಸಹ್ಯ ವಸ್ತುಗಳನ್ನು ಇಟ್ಟು ಅದನ್ನು ಹೊಲೆಮಾಡಿದ್ದಾರೆ.",
			"31": "<sup>31</sup> ತಮ್ಮ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ಆಹುತಿಕೊಡುವದಕ್ಕೆ ಬೆನ್‍ಹಿನ್ನೋಮ್ ತಗ್ಗಿನಲ್ಲಿನ ತೋಫೆತೆಂಬ ಪೂಜಾಸ್ಥಳವನ್ನು ಕಟ್ಟಿದ್ದಾರೆ; ನಾನು ಆ ಸಂಸ್ಕಾರವನ್ನು ವಿಧಿಸಲಿಲ್ಲ, ನನ್ನ ಮನಸ್ಸಿನಲ್ಲಿ ಹುಟ್ಟಲೂ ಇಲ್ಲ.",
			"32": "<sup>32</sup> ಹಾ, ಈ ಕಾರಣದಿಂದ ಆ ಸ್ಥಳಕ್ಕೆ ತೋಫೆತ್ ಮತ್ತು ಬೆನ್‍ಹಿನ್ನೋಮಿನ ತಗ್ಗು ಎಂಬ ಹೆಸರುಗಳು ಹೋಗಿ ಸಂಹಾರದ ತಗ್ಗು ಎಂಬ ಹೆಸರಾಗುವ ದಿನಗಳು ಬರುವವು; ಇನ್ನೆಲ್ಲಿಯೂ ಸ್ಥಳವಿಲ್ಲವಾಗಿ ಶವಗಳನ್ನು ತೋಫೆತಿನಲ್ಲಿ ಹೂಣುವರು.",
			"33": "<sup>33</sup> ಈ ಜನರ ಹೆಣಗಳು ಆಕಾಶಪಕ್ಷಿಗಳಿಗೂ ಭೂಜಂತುಗಳಿಗೂ ಆಹಾರವಾಗುವವು; ಅವುಗಳನ್ನು ಬೆದರಿಸುವವರು ಯಾರೂ ಇರುವದಿಲ್ಲ.",
			"34": "<sup>34</sup> ಯೆಹೂದದ ಪಟ್ಟಣಗಳಲ್ಲಿಯೂ ಯೆರೂಸಲೇಮಿನ ಬೀದಿಗಳಲ್ಲಿಯೂ ಹರ್ಷಸಂಭ್ರಮಗಳ ಧ್ವನಿಯನ್ನೂ ವಧೂವರರ ಸ್ವರವನ್ನೂ ನಿಲ್ಲಿಸಿಬಿಡುವೆನು, ದೇಶವು ಹಾಳೇ ಹಾಳಾಗುವದು. ಇದು ಯೆಹೋವನ ನುಡಿ."
		},
		"8": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ಆ ಕಾಲದಲ್ಲಿ ಯೆಹೂದದ ಅರಸರು, ಅಧಿಪತಿಗಳು, ಯಾಜಕರು, ಪ್ರವಾದಿಗಳು, ಯೆರೂಸಲೇಮಿನ ನಿವಾಸಿಗಳು, ಇವರೆಲ್ಲರ ಎಲುಬುಗಳನ್ನು ಗೋರಿಗಳೊಳಗಿಂದ ತೆಗೆದು",
			"2": "<sup>2</sup> ಇವರು ಪ್ರೀತಿಸಿ ಸೇವಿಸಿ ಹಿಂಬಾಲಿಸಿ ಆಶ್ರಯಿಸಿ ಪೂಜಿಸಿದ ಸೂರ್ಯಚಂದ್ರತಾರಾಗಣಗಳ ಎದುರಿಗೆ ಹರಡಿಬಿಡುವರು; ಅವುಗಳನ್ನು ಯಾರೂ ಕೂಡಿಸಿ ಹೂಣಿಡುವದಿಲ್ಲ; ಅವು ಭೂಮಿಯ ಮೇಲೆ ಗೊಬ್ಬರವಾಗುವವು.",
			"3": "<sup>3</sup> ನಾನು ಯಾವಾವ ಸ್ಥಳಗಳಿಗೆ ಈ ದುರ್ವಂಶದವರನ್ನು ಅಟ್ಟಿಬಿಟ್ಟಿದ್ದೇನೋ ಅಲ್ಲೆಲ್ಲಾ ಉಳಿದವರೆಲ್ಲರೂ ಜೀವಿಸುವದಕ್ಕಿಂತ ಮರಣವು ಲೇಸೆಂದು ಅದನ್ನು ಬಯಸುವರು. ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"4": "<sup>4</sup> ಮತ್ತು ನೀನು ಅವರಿಗೆ ಹೀಗೆ ಹೇಳಬೇಕು - ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಬಿದ್ದವರು ಏಳದಿರುವರೋ? ಬಿಟ್ಟು ಹೋದವನು ತಿರಿಗಿ ಬರುವದಿಲ್ಲವೋ?",
			"5": "<sup>5</sup> ಈ ಯೆರೂಸಲೇಮಿನವರು ಎಂದಿಗೂ ಹಿಂದಿರುಗದಂತೆ ಬಿಟ್ಟುಹೋದದ್ದೇಕೆ? ಮೋಸವನ್ನೇ ಪಟ್ಟಾಗಿ ಹಿಡಿದಿದ್ದಾರೆ, ತಿರಿಗಿ ಬರಲೊಲ್ಲರು.",
			"6": "<sup>6</sup> ನಾನು ಕಿವಿಗೊಟ್ಟು ಕೇಳುವಾಗ ಅವರು ಯಥಾರ್ಥವಾಗಿ ಮಾತಾಡಿಕೊಳ್ಳುತ್ತಿರಲಿಲ್ಲ; ಯಾವನೂ ತನ್ನ ಅಧರ್ಮದ ವಿಷಯವಾಗಿ ಪಶ್ಚಾತ್ತಾಪಪಟ್ಟು - ಆಹಾ, ನಾನು ಎಂಥಾ ಕೆಲಸ ಮಾಡಿದೆ ಎಂದು ಕೊಳ್ಳುತ್ತಿರಲಿಲ್ಲ; ಯುದ್ಧದಲ್ಲಿ ರಭಸವಾಗಿ ಓಡುವ ಕುದುರೆಯಂತೆ ಪ್ರತಿಯೊಬ್ಬನೂ ತನ್ನ ತನ್ನ ಮಾರ್ಗಕ್ಕೇ ತ್ವರೆಪಡುತ್ತಾನೆ.",
			"7": "<sup>7</sup> ಆಕಾಶದಲ್ಲಿ ಹಾರುವ ಬಕವೂ ತನ್ನ ನಿಯಮಿತ ಕಾಲಗಳನ್ನು ತಿಳಿದುಕೊಂಡಿದೆ, ಬೆಳವಕ್ಕಿಯೂ ಬಾನಕ್ಕಿಯೂ ಕೊಕ್ಕರೆಯೂ ತಮ್ಮ ಗಮನಾಗಮನ ಸಮಯಗಳನ್ನು ಗಮನಿಸುತ್ತವೆ; ನನ್ನ ಜನರಾದರೋ ಯೆಹೋವನ ನಿಯಮವನ್ನು ತಿಳಿಯರು.",
			"8": "<sup>8</sup> ಹೀಗಿರಲು ನಾವು ಜ್ಞಾನಿಗಳು, ಯೆಹೋವನ ಧರ್ಮಶಾಸ್ತ್ರವು ನಮ್ಮಲ್ಲಿಯೇ ಇದೆ ಎಂದು ನೀವು ಅಂದುಕೊಳ್ಳುವದು ಹೇಗೆ? ಆಹಾ, ಶಾಸ್ತ್ರಿಗಳ ಸುಳ್ಳು ಲೇಖನಿಯು ಅದನ್ನು ಸುಳ್ಳನ್ನಾಗಿ ಮಾಡಿದೆ.",
			"9": "<sup>9</sup> ಜ್ಞಾನಿಗಳು ಆಶಾಭಂಗಪಟ್ಟು ಬೆಬ್ಬರಬಿದ್ದು ಸಿಕ್ಕಿಕೊಂಡಿದ್ದಾರೆ; ಇಗೋ, ಯೆಹೋವನ ಮಾತನ್ನು ನಿರಾಕರಿಸಿದರು;",
			"10": "<sup>10</sup> ಅವರ ಜ್ಞಾನವು ಎಷ್ಟರದು? ಆದದರಿಂದ ನಾನು ಇವರ ಹೆಂಡರನ್ನು ಅನ್ಯರಿಗೂ ಇವರ ಹೊಲಗದ್ದೆಗಳನ್ನು ಆಕ್ರಮಿಸುವವರಿಗೂ ಕೊಟ್ಟುಬಿಡುವೆನು; ಚಿಕ್ಕವರು ಮೊದಲುಗೊಂಡು ದೊಡ್ಡವರ ತನಕ ಎಲ್ಲರೂ ಬಾಚಿಕೊಳ್ಳುತ್ತಲೇ ಇದ್ದಾರೆ; ಪ್ರವಾದಿಗಳು ಮೊದಲಾಗಿ ಯಾಜಕರವರೆಗೆ ಸಕಲರೂ ಮೋಸಮಾಡುತ್ತಾರೆ.",
			"11": "<sup>11</sup> ಮೇಲಾಗದಿದ್ದರೂ ಮೇಲು ಮೇಲು ಎಂದು ಹೇಳಿ ನನ್ನ ಪ್ರಜೆಯೆಂಬಾಕೆಯ ಗಾಯವನ್ನು ಮೇಲೆ ಮೇಲೆ ವಾಸಿಮಾಡಿದ್ದಾರೆ.",
			"12": "<sup>12</sup> ಅಸಹ್ಯಕಾರ್ಯಗಳನ್ನು ಮಾಡಿ ಅವಮಾನಕ್ಕೆ ಗುರಿಯಾದರೂ ಎಳ್ಳಷ್ಟೂ ನಾಚಿಕೆಪಡಲಿಲ್ಲ, ಲಜ್ಜೆಯ ಗಂಧವನ್ನೂ ತಿಳಿಯಲಿಲ್ಲ; ಆದಕಾರಣ ನಾನು ದಂಡಿಸುವ ಸಮಯದಲ್ಲಿ ಅವರು ಮುಗ್ಗರಿಸುವರು, ಬೀಳುವವರ ಸಂಗಡ ಬಿದ್ದೇಹೋಗುವರು ಎಂದು ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ.",
			"13": "<sup>13</sup> ನಾನು ಇವರನ್ನು ನಿರ್ಮೂಲಮಾಡಿ ಕೊಚ್ಚಿಬಿಡುವೆನು; ದ್ರಾಕ್ಷೆಯ ಬಳ್ಳಿಯಲ್ಲಿಯೂ ಅಂಜೂರದ ಮರದಲ್ಲಿಯೂ ಹಣ್ಣಿರುವದಿಲ್ಲ, ಎಲೆಯು ಬಾಡುವದು; ನಾನು ಹಾಳು ಮಾಡುವವರನ್ನು ಅವರಿಗೆ ನೇಮಿಸುವೆನು. ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"14": "<sup>14</sup> [ಆ ಕಾಲದಲ್ಲಿ ಜನರು ಹೀಗನ್ನುವರು - ] ಸುಮ್ಮನೆ ಕೂತಿರುವದೇಕೆ? ಕೂಡಿಕೊಳ್ಳಿರಿ, ಕೋಟೆಕೊತ್ತಲಗಳ ಪಟ್ಟಣಗಳಲ್ಲಿ ಸೇರಿ ಅಲ್ಲೇ ನಾಶವಾಗೋಣ; ನಾವು ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ಪಾಪಮಾಡಿದ್ದರಿಂದ ಆತನು ನಮ್ಮನ್ನು ನಾಶನಕ್ಕೆ ಗುರಿಮಾಡಿ ಕಹಿಯಾದ ನೀರನ್ನು ಕುಡಿಸಿದ್ದಾನಷ್ಟೆ.",
			"15": "<sup>15</sup> ನಾವು ಸುಖವನ್ನು ನಿರೀಕ್ಷಿಸಿದೆವು, ಯಾವ ಮೇಲೂ ಆಗಲಿಲ್ಲ; ಕ್ಷೇಮಕಾಲವನ್ನು ಎದುರುನೋಡಿದೆವು, ಹಾ, ಅಂಜಿಕೆಯೇ!",
			"16": "<sup>16</sup> ವೈರಿಯ ಕುದುರೆಗಳ ಬುಸುಗಾಟವು ದಾನಿನಿಂದ ಕೇಳಬರುತ್ತದೆ; ತುರಂಗಗಳ ಕೆನೆತದ ಶಬ್ದಕ್ಕೆ ದೇಶವೆಲ್ಲಾ ಕಂಪಿಸುತ್ತದೆ; ಶತ್ರುಗಳು ಬಂದರು, ಸೀಮೆಯನ್ನೂ ಅದರಲ್ಲಿರುವ ಸಮಸ್ತವನ್ನೂ ಪಟ್ಟಣವನ್ನೂ ಪಟ್ಟಣದ ನಿವಾಸಿಗಳನ್ನೂ ನುಂಗಿಬಿಟ್ಟರು.",
			"17": "<sup>17</sup> ಯೆಹೋವನು - ಇಗೋ, ನಾನು ನಿಮ್ಮ ಮೇಲೆ ಹಾವುಗಳನ್ನು, ಮಂತ್ರಕ್ಕೆ ಅಧೀನವಾಗದ ನಾಗಗಳನ್ನು ಬಿಡುವೆನು; ಅವು ನಿಮ್ಮನ್ನು ಕಚ್ಚುವವು ಎಂದು ನುಡಿದಿದ್ದಾನೆ.",
			"18": "<sup>18</sup> ಅಯ್ಯೋ, ನನ್ನ ದುಃಖಕ್ಕೆ ಬೆಳಗೆಂದಿಗಾದೀತು? ನನ್ನ ಹೃದಯವು ನನ್ನೊಳಗೆ ಕುಂದಿಹೋಗಿದೆ.",
			"19": "<sup>19</sup> ಆಹಾ, ನನ್ನ ಪ್ರಜೆಯೆಂಬಾಕೆಯು - ಯೆಹೋವನು ಚೀಯೋನಿನಲ್ಲಿಲ್ಲವೋ, ಅದರ ರಾಜನು ಅಲ್ಲಿ ವಾಸವಾಗಿಲ್ಲವೋ ಎಂದು ಮೊರೆಯಿಡುವ ಶಬ್ದವು ದೂರದೇಶದಿಂದ ಕೇಳಿಸುತ್ತದೆ. [ಅದಕ್ಕೆ ಯೆಹೋವನು - ] ಇವರು ತಮ್ಮ ವಿಗ್ರಹಗಳಿಂದಲೂ ಅನ್ಯದೇವತೆಗಳ ಶೂನ್ಯರೂಪಗಳಿಂದಲೂ ಏಕೆ ನನ್ನನ್ನು ಕೆಣಕಿದ್ದಾರೆ [ಅನ್ನುತ್ತಾನೆ].",
			"20": "<sup>20</sup> ಸುಗ್ಗಿ ತೀರಿತು, ಹಣ್ಣಿನ ಕಾಲ ಮುಗಿಯಿತು, ನಮಗೆ ಉದ್ಧಾರವೇ ಆಗಲಿಲ್ಲ.",
			"21": "<sup>21</sup> ನನ್ನ ಪ್ರಜೆಯೆಂಬಾಕೆಯ ಭಂಗದಿಂದ ನನಗೆ ಭಂಗವಾಯಿತು, ನಾನು ಕರ್ರಗಾದೆನು, ಭಯವು ನನ್ನನ್ನು ಹಿಡಿದಿದೆ.",
			"22": "<sup>22</sup> ಗಿಲ್ಯಾದಿನಲ್ಲಿ ಔಷಧವಿಲ್ಲವೋ? ಅಲ್ಲಿ ವೈದ್ಯನು ಸಿಕ್ಕನೋ? ನನ್ನ ಪ್ರಜೆಯೆಂಬಾಕೆಗೆ ಏಕೆ ಗುಣವಾಗಲಿಲ್ಲ?"
		},
		"9": {
			"1": "<sup>1</sup> ಅಯ್ಯೋ, ನನ್ನ ಶಿರಸ್ಸು ಜಲಮಯವಾಗಿಯೂ ನನ್ನ ನೇತ್ರಗಳು ಕಣ್ಣೀರಿನ ಬುಗ್ಗೆಯಾಗಿಯೂ ಇದ್ದರೆ ಎಷ್ಟೋ ಲೇಸು! ನನ್ನ ಜನರಲ್ಲಿ ಹತರಾದವರ ನಿಮಿತ್ತ ಹಗಲಿರುಳೂ ಅಳಬೇಕಲ್ಲಾ!",
			"2": "<sup>2</sup> ಅಕಟಾ, ದಾರಿಗರು ತಂಗುವ ಗುಡಿಸಲು ಕಾಡಿನಲ್ಲಿ ನನಗೆ ಸಿಕ್ಕಿದರೆ ಎಷ್ಟೋ ಒಳ್ಳೇದು! ಆಗ ನನ್ನ ಜನರನ್ನು ತ್ಯಜಿಸಿ ಹೋಗುತ್ತಿದ್ದೆನು. ಅವರೆಲ್ಲರೂ ಸೂಳೆಗಾರರು, ದ್ರೋಹಿಗಳ ಗುಂಪೇ.",
			"3": "<sup>3</sup> ಸುಳ್ಳಾಡುವದಕ್ಕೆ ತಮ್ಮ ನಾಲಿಗೆಯನ್ನು ಬಿಲ್ಲಿನಂತೆ ಬೊಗ್ಗಿಸುತ್ತಾರೆ; ಸತ್ಯಕ್ಕೆ ಪ್ರತಿಕೂಲವಾಗಿ ದೇಶದಲ್ಲಿ ಪ್ರಬಲಿಸಿದ್ದಾರೆ; ಕೇಡಿನಿಂದ ಕೇಡಿಗೆ ಹಾರುತ್ತಾರೆ; ನನ್ನನ್ನು ಅರಿಯರು ಎಂದು ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ.",
			"4": "<sup>4</sup> ನೀವೆಲ್ಲರೂ ನಿಮ್ಮ ನಿಮ್ಮ ನೆರೆಯವರ ವಿಷಯದಲ್ಲಿ ಎಚ್ಚರಿಕೆಯಾಗಿರಿ, ಯಾರೇ ಆಗಲಿ ತನ್ನ ತಮ್ಮನನ್ನೂ ನಂಬದಿರಲಿ; ಪ್ರತಿಯೊಬ್ಬ ತಮ್ಮನೂ ವಂಚಿಸೇ ವಂಚಿಸುವನು, ಒಬ್ಬೊಬ್ಬ ನೆರೆಯವನೂ ಚಾಡಿಹೇಳುತ್ತಾ ತಿರುಗಾಡುವನು.",
			"5": "<sup>5</sup> ಎಲ್ಲರೂ ತಮ್ಮ ತಮ್ಮ ನೆರೆಯವರಿಗೆ ಮೋಸಮಾಡುತ್ತಾರೆ, ಸತ್ಯವನ್ನು ಆಡುವದೇ ಇಲ್ಲ; ತಮ್ಮ ನಾಲಿಗೆಗೆ ಸುಳ್ಳನ್ನು ಕಲಿಸಿದ್ದಾರೆ; ತಮಗೆ ಆಯಾಸವಾಗುವ ಮಟ್ಟಿಗೂ ಕೆಡುಕನ್ನು ಮಾಡುತ್ತಿದ್ದಾರೆ.",
			"6": "<sup>6</sup> ನಿಮ್ಮ ನಿವಾಸವು ಮೋಸದೊಳಗೇ ಇದೆ; ಅಯ್ಯೋ, ಮೋಸಗಾರರಾಗಿರುವದರಿಂದ ನನ್ನನ್ನು ಅರಿಯಲೊಲ್ಲರು ಎಂದು ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ.",
			"7": "<sup>7</sup> ಹೀಗಿರಲು ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಆಹಾ, ನಾನು ನನ್ನ ಜನರ ದ್ರೋಹಕ್ಕೆ ಏನು ಮಾಡಲಿ? ಅವರನ್ನು ಪುಟಕ್ಕೆ ಹಾಕಿ ಶೋಧಿಸುವೆನು.",
			"8": "<sup>8</sup> ಅವರ ನಾಲಿಗೆಯು ಕೊಲ್ಲುವ ಬಾಣವಾಗಿದೆ, ಅದು ಸುಳ್ಳನ್ನೇ ಆಡುತ್ತದೆ; ಒಬ್ಬನು ತನ್ನ ನೆರೆಯವನಿಗೆ ಬಾಯಿಂದ ಶುಭವಾಗಲಿ ಎಂದು ಹೇಳಿದರೂ ತನ್ನ ಹೃದಯದಲ್ಲಿ ಅವನಿಗಾಗಿ ಹೊಂಚುಹಾಕುತ್ತಾನೆ.",
			"9": "<sup>9</sup> ನಾನು ಅವರನ್ನು ಇವುಗಳಿಗಾಗಿ ದಂಡಿಸಬಾರದೋ? ಇಂಥಾ ಜನಾಂಗದ ಮೇಲೆ ನನ್ನ ರೋಷವನ್ನು ತೀರಿಸದಿರುವೆನೋ?",
			"10": "<sup>10</sup> ನಾನು ಪರ್ವತಗಳಿಗಾಗಿ ಅತ್ತು ಗೋಳಾಡುವೆನು, ಅಡವಿಯ ಕಾವಲುಗಳಿಗಾಗಿ ಶೋಕಗೀತವನ್ನು ಹಾಡುವೆನು; ಅವು ಸುಟ್ಟುಹೋಗಿವೆ, ಯಾರೂ ಹಾದುಹೋಗರು, ದನದ ಸದ್ದು ಕಿವಿಗೆ ಬೀಳದು, ಮೃಗಪಕ್ಷಿಗಳು ತೊಲಗಿಹೋಗಿವೆ.",
			"11": "<sup>11</sup> ನಾನು ಯೆರೂಸಲೇಮನ್ನು ಹಾಳುದಿಬ್ಬಗಳನ್ನಾಗಿಯೂ ನರಿಗಳ ಹಕ್ಕೆಯನ್ನಾಗಿಯೂ ಗೈದು ಯೆಹೂದದ ಪಟ್ಟಣಗಳನ್ನು ಜನವಿಲ್ಲದ ಬೀಳುಭೂಮಿಯನ್ನಾಗಿ ಮಾಡುವೆನು [ಎಂದು ಯೆಹೋವನು ಹೇಳಿದ್ದಾನಲ್ಲಾ].",
			"12": "<sup>12</sup> ಯಾರೂ ದಾಟದ ಹಾಗೆ ದೇಶವು ಹಾಳಾಗಿ ಕಾಡಿನಂತೆ ಸುಟ್ಟುಹೋದದ್ದು ಏಕೆ? ಈ ವಿಚಾರವನ್ನು ಗ್ರಹಿಸತಕ್ಕ ಜ್ಞಾನಿಯು ಯಾರು? ಅದನ್ನು ಯೆಹೋವನ ಬಾಯಿಂದಲೇ ಕೇಳಿ ತಿಳಿಸಬಲ್ಲವನು ಯಾರು?",
			"13": "<sup>13</sup> ಇಸ್ರಾಯೇಲಿನ ದೇವರೂ ಸೇನಾಧೀಶ್ವರನೂ ಆದ ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ನಾನು ಇವರ ಕಣ್ಣೆದುರಿಗೇ ಇಟ್ಟಿರುವ ಧರ್ಮೋಪದೇಶವನ್ನು ಇವರು ಲಕ್ಷ್ಯಕ್ಕೆ ತಾರದೆ ನನ್ನ ಮಾತನ್ನು ಕೇಳದೆ ಆ ಧರ್ಮಮಾರ್ಗದಲ್ಲಿ ನಡೆಯದೆ",
			"14": "<sup>14</sup> ಸ್ವಂತ ಹೃದಯದ ಹಟದಂತೆ ನಡೆದು ತಮ್ಮ ಪಿತೃಗಳು ಕಲಿಸಿಕೊಟ್ಟ ಹಾಗೆ ಬಾಳ್ ದೇವತೆಗಳನ್ನು ಹಿಂಬಾಲಿಸಿದ್ದರಿಂದ",
			"15": "<sup>15</sup> ಆಹಾ, ನಾನು ಈ ಜನರಿಗೆ ಕಹಿಯಾದ ಆಹಾರಪಾನಗಳನ್ನು ಕೊಡುವೆನು.",
			"16": "<sup>16</sup> ಇವರಿಗಾಗಲಿ ಇವರ ಪಿತೃಗಳಿಗಾಗಲಿ ಗೊತ್ತಿಲ್ಲದ ಜನಾಂಗಗಳೊಳಗೆ ಇವರನ್ನು ಚದರಿಸಿಬಿಟ್ಟು ಇವರು ನಿರ್ಮೂಲವಾಗುವ ತನಕ ಖಡ್ಗವನ್ನು ಇವರ ಹಿಂದೆ ಕಳುಹಿಸುವೆನು.",
			"17": "<sup>17</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನ ಮಾತನ್ನು ಕೇಳಿರಿ - [ನಿಮ್ಮ ಸಂಗತಿಯನ್ನು] ಯೋಚಿಸಿರಿ; ರೋದನಗಾಯಕಿಯರನ್ನು ಕರೆಯಿಸಿರಿ, ಅವರು ಬರಲಿ; ಜಾಣೆಯರನ್ನು ಕರೆಯ ಕಳುಹಿಸಿರಿ, ಅವರು ಸೇರಲಿ;",
			"18": "<sup>18</sup> ಅವರು ತ್ವರೆಪಟ್ಟು ನಮ್ಮ ನೇತ್ರಗಳಿಂದ ಧಾರೆ ಹೊರಡುವಂತೆಯೂ ನಮ್ಮ ರೆಪ್ಪೆಗಳೊಳಗಿಂದ ನೀರುಕ್ಕುವಂತೆಯೂ ನಮಗೋಸ್ಕರ ಗೋಳೆತ್ತಲಿ.",
			"19": "<sup>19</sup> ಅಯ್ಯೋ, ಹಾಳಾದೆವು, ಭಂಗಪಟ್ಟೆವು; ನಾವು ನಮ್ಮ ದೇಶವನ್ನು ಬಿಟ್ಟೆವಲ್ಲಾ, [ಶತ್ರುಗಳು] ನಮ್ಮ ಮನೆಗಳನ್ನು ಕೆಡವಿದ್ದಾರಲ್ಲಾ ಎಂಬ ಪ್ರಲಾಪಧ್ವನಿಯು ಚೀಯೋನಿನೊಳಗಿಂದ ಕೇಳಿಸುತ್ತದೆ.",
			"20": "<sup>20</sup> ಸ್ತ್ರೀಯರೇ, ಯೆಹೋವನ ನುಡಿಯನ್ನು ಕೇಳಿ ಆತನ ಬಾಯಿಂದ ಹೊರಟ ಮಾತಿಗೆ ಕಿವಿದೆರೆಯಿರಿ; ನಿಮ್ಮ ಹೆಣ್ಣುಮಕ್ಕಳಿಗೂ ಗೋಳಾಟವನ್ನು ಕಲಿಸಿರಿ, ಪ್ರತಿಯೊಬ್ಬಳು ತನ್ನ ನೆರೆಯವಳಿಗೂ ಶೋಕಗೀತವನ್ನು ಅಭ್ಯಾಸಮಾಡಿಸಲಿ;",
			"21": "<sup>21</sup> ಏಕಂದರೆ ಬೀದಿಗಳಲ್ಲಿ ಮಕ್ಕಳೂ ಚೌಕಗಳಲ್ಲಿ ಯುವಕರೂ ಇಲ್ಲದ ಹಾಗೆ ಮೃತ್ಯುವು ನಮ್ಮ ಕಿಟಕಿಗಳ ಮೇಲೆ ಹತ್ತಿ ನಮ್ಮ ಉಪ್ಪರಿಗೆಗಳೊಳಗೆ ನುಗ್ಗಿದೆ.",
			"22": "<sup>22</sup> ಯೆಹೋವನ ಈ ಮಾತನ್ನು ಸಾರು ಎಂದು ನನಗೆ ಅಪ್ಪಣೆಯಾಯಿತು - ಜನರ ಹೆಣಗಳು ನೆಲದ ಮೇಲೆ ಬಿದ್ದು ಗೊಬ್ಬರವಾಗುವವು; ಅವು ಹೊಲಕೊಯ್ಯುವವನ ಹಿಂದೆ ಯಾರೂ ಕೂಡಿಸದೆ ಬಿದ್ದಿರುವ ಅರಿಗಳಂತಿರುವವು.",
			"23": "<sup>23</sup> ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ಜ್ಞಾನಿಯು ತನ್ನ ಜ್ಞಾನಕ್ಕೆ, ಪರಾಕ್ರಮಿಯು ತನ್ನ ಪರಾಕ್ರಮಕ್ಕೆ, ಐಶ್ವರ್ಯವಂತನು ತನ್ನ ಐಶ್ವರ್ಯಕ್ಕೆ ಹೆಚ್ಚಳಪಡದಿರಲಿ;",
			"24": "<sup>24</sup> ಹೆಚ್ಚಳಪಡುವವನು ತಾನು ನನ್ನನ್ನು ತಿಳಿದು ನಾನು ಲೋಕದಲ್ಲಿ ಪ್ರೀತಿನೀತಿನ್ಯಾಯಗಳನ್ನು ತೋರ್ಪಡಿಸುವ ಯೆಹೋವನಾಗಿರುವೆನು ಎಂದು ಗ್ರಹಿಸಿಕೊಂಡಿದ್ದೇನೆ ಎಂಬದಕ್ಕೇ ಹೆಚ್ಚಳಪಡಲಿ; ಪ್ರೀತಿನೀತಿನ್ಯಾಯಗಳೇ ನನಗೆ ಆನಂದವೆಂದು ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ.",
			"25": "<sup>25</sup> ಆಹಾ, ಐಗುಪ್ತ್ಯರು ಯೆಹೂದ್ಯರು ಎದೋಮ್ಯರು ಅಮ್ಮೋನ್ಯರು ಮೋವಾಬ್ಯರು ಚಂಡಿಕೆಬಿಟ್ಟುಕೊಂಡಿರುವ ಅರಣ್ಯನಿವಾಸಿಗಳು, ಅಂತು ಸುನ್ನತಿಯುಳ್ಳವರಾದರೂ ಸುನ್ನತಿಯಿಲ್ಲದ ಇವರೆಲ್ಲರನ್ನೂ ನಾನು ದಂಡಿಸುವ ದಿನಗಳು ಬರುತ್ತವೆ;",
			"26": "<sup>26</sup> ಏಕಂದರೆ ಸಕಲಜನಾಂಗಗಳವರೂ ಸುನ್ನತಿಹೀನರು, ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ ವಂಶದವರೂ ಹೃದಯ ಸುನ್ನತಿಯಿಲ್ಲದವರು ಎಂದು ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ."
		},
		"10": {
			"1": "<sup>1</sup> ಇಸ್ರಾಯೇಲ್ ವಂಶದವರೇ, ಯೆಹೋವನು ನಿಮಗೆ ನುಡಿಯುವ ಮಾತನ್ನು ಕೇಳಿರಿ;",
			"2": "<sup>2</sup> ಯೆಹೋವನು ಹೀಗೆ ಹೇಳುತ್ತಾನೆ - ಜನಾಂಗಗಳ ಆಚರಣೆಯನ್ನು ಅಭ್ಯಾಸಿಸದಿರಿ, ಜನಾಂಗಗಳು ಹೆದರುವ ಆಕಾಶದ ಉತ್ಪಾತಗಳಿಗೆ ನೀವು ಹೆದರಬೇಡಿರಿ.",
			"3": "<sup>3</sup> ಜನಾಂಗಗಳ ಕಟ್ಟಳೆಗಳು ಬರಿದೇ; ಅರಣ್ಯದ ಮರವನ್ನು ಕತ್ತರಿಸುವರು, ಬಡಗಿಯು ಅದನ್ನು ಕೈಯಿಂದ, ಬಾಚಿಯಿಂದ ರೂಪಿಸುವನು.",
			"4": "<sup>4</sup> ಅದನ್ನು ಬೆಳ್ಳಿಬಂಗಾರಗಳಿಂದ ಭೂಷಿಸುತ್ತಾರೆ, ಅಲುಗದಹಾಗೆ ಮೊಳೆಗಳಿಂದ ಸುತ್ತಿಗೆಯಿಂದ ಭದ್ರಪಡಿಸುತ್ತಾರೆ.",
			"5": "<sup>5</sup> ಇಂಥಾ ಬೊಂಬೆಗಳು ಸೌತೆಯ ತೋಟದ ಬೆದರುಗಂಬದಂತಿವೆ, ಮಾತಾಡಲಾರವು; ಹೊತ್ತುಕೊಂಡುಹೋಗಬೇಕು, ನಡೆಯಲಾರವು. ಅವುಗಳಿಗೆ ಅಂಜಬೇಡಿರಿ, ಕೇಡುಮಾಡಲಾರವು, ಮೇಲುಮಾಡಲಿಕ್ಕೂ ಅವುಗಳಿಗೆ ಸಾಮರ್ಥ್ಯವಿಲ್ಲ.",
			"6": "<sup>6</sup> ಯೆಹೋವನೇ, ನಿನ್ನ ಸಮಾನನು ಯಾವನೂ ಇಲ್ಲ; ನೀನು ಮಹತ್ತಮನು, ನಿನ್ನ ನಾಮವೂ ಸಾಮರ್ಥ್ಯದಿಂದ ಕೂಡಿ ಮಹತ್ತಮವಾಗಿದೆ.",
			"7": "<sup>7</sup> ಜನಾಂಗಗಳ ಅರಸನೇ, ಯಾರು ನಿನಗೆ ಅಂಜದೆ ಇದ್ದಾರು? ಇದು ನಿನಗೆ ತಕ್ಕದ್ದು; ಜನಾಂಗಗಳ ಜ್ಞಾನಿಗಳಲ್ಲಿಯೂ ರಾಜಪರಂಪರೆಯಲ್ಲಿಯೂ ನಿನ್ನ ಸಮಾನನು ಯಾವನೂ ಇಲ್ಲವಷ್ಟೆ.",
			"8": "<sup>8</sup> ಅವರೆಲ್ಲರೂ ಪಶುಪ್ರಾಯರು, ಮಂದರು; ಬೊಂಬೆಗಳಿಂದಾಗುವ ಶಿಕ್ಷಣವು ಮರದಂತೆ ಮೊದ್ದು.",
			"9": "<sup>9</sup> ಬೆಳ್ಳಿಯ ತಗಡುಗಳು ತಾರ್ಷೀಷಿನಿಂದಲೂ ಕೆತ್ತನೆಗಾರನ ಮತ್ತು ಎರಕದವನ ಕೈಕೆಲಸವಾದ ಚಿನ್ನವು ಊಫಜಿನಿಂದಲೂ ಸಾಗಿಬರುತ್ತವೆ; ನೀಲಧೂಮ್ರವಸ್ತ್ರಗಳು ಅವುಗಳ ಉಡುಪಾಗಿವೆ; ಇವೆಲ್ಲಾ ಕುಶಲರ ಕೌಶಲ್ಯ.",
			"10": "<sup>10</sup> ಯೆಹೋವನಾದರೋ ಸತ್ಯದೇವರು; ಆತನು ಚೈತನ್ಯಸ್ವರೂಪನಾದ ದೇವನೂ ಶಾಶ್ವತರಾಜನೂ ಆಗಿದ್ದಾನೆ; ಆತನ ಕೋಪಕ್ಕೆ ಭೂಲೋಕವು ನಡುಗುತ್ತದೆ, ಜನಾಂಗಗಳು ಆತನ ರೋಷವನ್ನು ತಾಳಲಾರವು.",
			"11": "<sup>11</sup> ನೀವು ಅವುಗಳಿಗೆ - ಭೂಮ್ಯಾಕಾಶಗಳನ್ನು ಸೃಷ್ಟಿಸದಿರುವ ದೇವರುಗಳು ಭೂಮಿಯ ಮೇಲಿನಿಂದ, ಆಕಾಶದ ಕೆಳಗಿನಿಂದ, ಅಳಿದುಹೋಗುವವು ಎಂದು ಹೇಳಿರಿ.",
			"12": "<sup>12</sup> ಆತನು ತನ್ನ ಶಕ್ತಿಯಿಂದ ಭೂಮಿಯನ್ನು ನಿರ್ಮಿಸಿ ತನ್ನ ಜ್ಞಾನದಿಂದ ಲೋಕವನ್ನು ಸ್ಥಾಪಿಸಿ ತನ್ನ ವಿವೇಕದಿಂದ ಆಕಾಶಮಂಡಲವನ್ನು ಹರಡಿದ್ದಾನೆ;",
			"13": "<sup>13</sup> ಆತನ ಗರ್ಜನೆಗೆ ಆಕಾಶದಲ್ಲಿ ನೀರು ಮೊರೋ ಎನ್ನುತ್ತದೆ; ಆತನು ಭೂಮಿಯ ಕಟ್ಟಕಡೆಯಿಂದ ಮೋಡಗಳನ್ನು ಏರಮಾಡುತ್ತಾನೆ; ಮಳೆಗೋಸ್ಕರ ಮಿಂಚನ್ನು ಹೊಳೆಯಮಾಡುತ್ತಾನೆ; ತನ್ನ ಭಂಡಾರದಿಂದ ಗಾಳಿಯನ್ನು ಬೀಸಮಾಡುತ್ತಾನೆ.",
			"14": "<sup>14</sup> ಎಲ್ಲರೂ ತಿಳುವಳಿಕೆಯಿಲ್ಲದೆ ಪಶುಪ್ರಾಯರಾಗಿದ್ದಾರೆ; ತಾನು ಕೆತ್ತಿದ ವಿಗ್ರಹದ ನಿಮಿತ್ತ ಪ್ರತಿಯೊಬ್ಬ ಅಕ್ಕಸಾಲಿಗನೂ ಅವಮಾನಕ್ಕೆ ಗುರಿಯಾಗುವನು; ಅವನು ಎರಕ ಹೊಯ್ದ ಪುತ್ತಳಿಯು ಸುಳ್ಳು, ಅವುಗಳಲ್ಲಿ ಶ್ವಾಸವೇ ಇಲ್ಲ.",
			"15": "<sup>15</sup> ಅವು ವ್ಯರ್ಥ, ಹಾಸ್ಯಾಸ್ಪದವಾದ ಕೆಲಸ; ದಂಡನೆಯಾಗುವಾಗ ಅಳಿದು ಹೋಗುವವು;",
			"16": "<sup>16</sup> ಯಾಕೋಬ್ಯರ ಸ್ವಾಸ್ತ್ಯವಾದಾತನು ಅವುಗಳ ಹಾಗಲ್ಲ; ಆತನು ಸಮಸ್ತವನ್ನೂ ನಿರ್ಮಿಸಿದವನು; ಇಸ್ರಾಯೇಲು ಆತನ ಸ್ವಾಸ್ತ್ಯವಾದ ವಂಶ; ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೆಂಬದು ಆತನ ನಾಮಧೇಯ.",
			"17": "<sup>17</sup> ಮುತ್ತಿಗೆಗೆ ಈಡಾದ ಜನವೇ, ಗಂಟು ಕಟ್ಟಿಕೊಂಡು ದೇಶದೊಳಗಿಂದ ನಡೆ.",
			"18": "<sup>18</sup> ಇಗೋ, ನಾನು ಈ ಸಾರಿ ದೇಶನಿವಾಸಿಗಳನ್ನು ಎಸೆದೇ ಬಿಡುವೆನು, ಅವರಿಗೆ ಬುದ್ಧಿಬರಲೆಂದು ಅವರನ್ನು ಬಾಧಿಸುವೆನು ಎಂದು ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ.",
			"19": "<sup>19</sup> ಅಯ್ಯೋ, ನನ್ನ ಗಾಯ! ನನಗೆ ಬಿದ್ದ ಪೆಟ್ಟು ಗಡುಸು! ಆದರೂ ನಾನು - ಇದು ನನಗೆ ವ್ಯಾಧಿಯೇ ಸರಿ, ಸಹಿಸಬೇಕು ಅಂದುಕೊಂಡಿದ್ದೇನೆ.",
			"20": "<sup>20</sup> ನನ್ನ ಗುಡಾರವು ಹಾಳಾಗಿದೆ, ಹಗ್ಗಗಳು ಹರಿದುಹೋಗಿವೆ, ಮಕ್ಕಳು ನನ್ನೊಳಗಿಂದ ತೊಲಗಿ ಇಲ್ಲವಾಗಿದ್ದಾರೆ; ನನ್ನ ಗುಡಾರವನ್ನು ಹಾಕುವದಕ್ಕೂ ನನ್ನ ಪರದೆಗಳನ್ನು ಬಿಗಿಯುವದಕ್ಕೂ ಇನ್ನು ಯಾರೂ ಇರುವದಿಲ್ಲ.",
			"21": "<sup>21</sup> ಏಕಂದರೆ ಪಾಲಕರು ಪಶುಪ್ರಾಯರಾಗಿ ಯೆಹೋವನ ಕಡೆಗೆ ನೋಡದೆ ಇದ್ದಾರೆ; ಆದಕಾರಣ ಅವರ ಕಾರ್ಯವು ಸಾರ್ಥಕವಾಗಲಿಲ್ಲ, ಅವರ ಹಿಂಡುಗಳು ಚದರಿಹೋದವು.",
			"22": "<sup>22</sup> ಇಗೋ, ಒಂದು ಸದ್ದು! ಆಹಾ, ಬರುತ್ತಿದೆ! ಯೆಹೂದದ ಪಟ್ಟಣಗಳನ್ನು ನಾಶಪಡಿಸಿ ನರಿಗಳ ಹಕ್ಕೆಯನ್ನಾಗಿ ಮಾಡುವದಕ್ಕೆ ಉತ್ತರದೇಶದಿಂದ ಮಹಾಕಂಪನವು ಸಂಭವಿಸುತ್ತದೆ.",
			"23": "<sup>23</sup> ಯೆಹೋವನೇ, ಮಾನವನ ಮಾರ್ಗವು ಅವನ ಸ್ವಾಧೀನದಲ್ಲಿಲ್ಲವೆಂದು ನನಗೆ ಗೊತ್ತು; ಮನುಷ್ಯನು ನಡೆದಾಡುತ್ತಾ ಸರಿಯಾದ ಕಡೆಗೆ ತನ್ನ ಹೆಜ್ಜೆಯನ್ನಿಡಲಾರನು.",
			"24": "<sup>24</sup> ಯೆಹೋವನೇ, ನನ್ನನ್ನು ಶಿಕ್ಷಿಸು, ಆದರೆ ಮಿತಿಮೀರಿ ಬೇಡ; ರೋಷದಿಂದ ದಂಡಿಸದಿರು, ನಾನು ಕೇವಲ ಕ್ಷೀಣನಾದೇನು.",
			"25": "<sup>25</sup> ನಿನ್ನನ್ನು ಅರಿಯದ ಅನ್ಯಜನಾಂಗಗಳ ಮೇಲೆಯೂ ನಿನ್ನ ನಾಮವನ್ನು ಉಚ್ಚರಿಸದ ವಂಶಗಳ ಮೇಲೆಯೂ ನಿನ್ನ ರೌದ್ರವನ್ನು ಸುರಿದುಬಿಡು. ಅವರು ಯಾಕೋಬ್ಯರನ್ನು ನುಂಗಿ ಹೌದು, ಪೂರಾ ನುಂಗಿಬಿಟ್ಟು ಅವರ ವಾಸಸ್ಥಳವನ್ನು ಹಾಳುಮಾಡಿದ್ದಾರಲ್ಲಾ."
		},
		"11": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಯೆರೆಮೀಯನಿಗೆ ದಯಪಾಲಿಸಿದ ವಾಕ್ಯ -",
			"2": "<sup>2</sup> ಈ ನಿಬಂಧನವಚನಗಳನ್ನು ಕೇಳಿ ಯೆಹೂದ್ಯರಿಗೂ ಯೆರೂಸಲೇಮಿನ ನಿವಾಸಿಗಳಿಗೂ ಹೀಗೆ ಸಾರಿರಿ -",
			"3": "<sup>3</sup> ಕಬ್ಬಿಣ ಕರಗಿಸುವ ಕುಲಿಮೆಯೋಪಾದಿಯಲ್ಲಿದ್ದ ಐಗುಪ್ತದೇಶದಿಂದ ನಿಮ್ಮ ಪಿತೃಗಳನ್ನು ಬರಮಾಡಿದಾಗ - ನೀವು ನನ್ನ ಮಾತನ್ನು ಕೇಳಿ ನಾನು ನಿಮಗೆ ಆಜ್ಞಾಪಿಸಿರುವ ಈ ವಿಧಿಗಳನ್ನೆಲ್ಲಾ ಕೈಕೊಂಡರೆ ನೀವು ನನ್ನ ಪ್ರಜೆಯಾಗಿರುವಿರಿ,",
			"5": "<sup>5</sup> ನಾನು ನಿಮ್ಮ ದೇವರಾಗಿದ್ದು ಹಾಲೂ ಜೇನೂ ಹರಿಯುವ ದೇಶವನ್ನು ಕೊಡುವದಾಗಿ ನಿಮ್ಮ ಮೂಲಪಿತೃಗಳಿಗೆ ಮಾಡಿದ ವಾಗ್ದಾನವನ್ನು ನೆರವೇರಿಸುವೆನು ಎಂದು ಹೇಳಿದೆನಷ್ಟೆ; ಆ ನನ್ನ ಮಾತು ಈಗ ಕೈಗೂಡಿದೆ. ನಾನು ಅವರಿಗೆ ವಿಧಿಸಿದ ನಿಬಂಧನವಾಕ್ಯಗಳಿಗೆ ಕಿವಿಗೊಡದವನು ಶಾಪಗ್ರಸ್ತನಾಗಲಿ ಎಂಬದಾಗಿ ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರಾದ ಯೆಹೋವನು ನುಡಿಯುತ್ತಾನೆಂದೇ ಸಾರಬೇಕು. ಆಗ ನಾನು - ಅಪ್ಪಣೆಯಂತಾಗಲಿ, ಯೆಹೋವನೇ, ಎಂದು ಉತ್ತರಕೊಟ್ಟೆನು.",
			"6": "<sup>6</sup> ಯೆಹೋವನು ನನಗೆ ಹೀಗೆ ಹೇಳಿದನು - ಯೆಹೂದದ ಪಟ್ಟಣಗಳಲ್ಲಿಯೂ ಯೆರೂಸಲೇಮಿನ ಬೀದಿಗಳಲ್ಲಿಯೂ ಈ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ಸಾರು - ಈ ನಿಬಂಧನವಾಕ್ಯಗಳನ್ನೆಲ್ಲಾ ಕೇಳಿ ಕೈಕೊಳ್ಳಿರಿ.",
			"7": "<sup>7</sup> ನಿಮ್ಮ ಪಿತೃಗಳನ್ನು ಐಗುಪ್ತ ದೇಶದೊಳಗಿಂದ ಬರಮಾಡಿದಾರಭ್ಯ ಇಂದಿನವರೆಗೂ ನಾನು ಅವರಿಗೆ - ನನ್ನ ಮಾತನ್ನು ಕೇಳಿರಿ ಎಂದು ಸಾವಕಾಶಮಾಡದೆ ಖಂಡಿತವಾಗಿ ಆಜ್ಞಾಪಿಸುತ್ತಾ ಬಂದೆನಷ್ಟೆ.",
			"8": "<sup>8</sup> ಅವರಾದರೋ ಕೇಳಲಿಲ್ಲ, ಕಿವಿಗೊಡಲಿಲ್ಲ, ಪ್ರತಿಯೊಬ್ಬನೂ ತನ್ನ ತನ್ನ ದುಷ್ಟಹೃದಯದ ಹಟಕ್ಕೆ ತಕ್ಕ ಹಾಗೆ ನಡೆದನು; ಆದಕಾರಣ ಕೈಕೊಳ್ಳಬೇಕೆಂದು ನಾನು ಆಜ್ಞಾಪಿಸಿದರೂ ಅವರು ಕೈಕೊಳ್ಳದ ಈ ನಿಬಂಧನೆಯ ಶಾಪಗಳನ್ನೆಲ್ಲಾ ಅವರ ಮೇಲೆ ಬರಮಾಡಿದೆನು.",
			"9": "<sup>9</sup> ಯೆಹೋವನು ನನಗೆ ಹೀಗೆ ಹೇಳಿದನು - ಯೆಹೂದ್ಯರಲ್ಲಿಯೂ ಯೆರೂಸಲೇಮಿನ ನಿವಾಸಿಗಳಲ್ಲಿಯೂ ಒಳಸಂಚು ಕಂಡುಬಂದಿದೆ.",
			"10": "<sup>10</sup> ನನ್ನ ಮಾತುಗಳನ್ನು ಕೇಳಲೊಲ್ಲದಿದ್ದ ತಮ್ಮ ಮೂಲ ಪಿತೃಗಳ ದುಷ್ಕೃತ್ಯಗಳ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡು ಅನ್ಯದೇವತೆಗಳನ್ನು ಹಿಂಬಾಲಿಸಿ ಸೇವಿಸಿದ್ದಾರೆ; ಇಸ್ರಾಯೇಲ್ ವಂಶದವರೂ ಯೆಹೂದ ವಂಶದವರೂ ನಾನು ಅವರ ಪಿತೃಗಳ ಸಂಗಡ ಮಾಡಿಕೊಂಡ ಒಡಂಬಡಿಕೆಯನ್ನು ಮೀರಿದ್ದಾರೆ.",
			"11": "<sup>11</sup> ಆದಕಾರಣ ಯೆಹೋವನೆಂಬ ನಾನು ಹೀಗನ್ನುತ್ತೇನೆ - ಇಗೋ, ಅವರು ತಪ್ಪಿಸಿಕೊಳ್ಳಲಾರದ ಕೇಡನ್ನು ಅವರ ಮೇಲೆ ಬರಮಾಡುವೆನು; ನನಗೆ ಮೊರೆಯಿಟ್ಟರೂ ಕೇಳೆನು.",
			"12": "<sup>12</sup> ಆಗ ಯೆಹೂದದ ಪಟ್ಟಣಗಳವರೂ ಯೆರೂಸಲೇಮಿನ ನಿವಾಸಿಗಳೂ ಯಾವ ದೇವತೆಗಳಿಗೆ ಹೋಮಮಾಡಿದರೋ ಅವುಗಳನ್ನು ಮರೆಹೊಕ್ಕು ಕೂಗಿಕೊಳ್ಳುವರು, ಆದರೆ ಅಂಥ ಕೇಡಿನ ಕಾಲದಲ್ಲಿ ಅವುಗಳಿಂದ ಅವರಿಗೆ ಎಷ್ಟು ಮಾತ್ರವೂ ರಕ್ಷಣೆಯಾಗುವದಿಲ್ಲ.",
			"13": "<sup>13</sup> ಯೆಹೂದದವರೇ, ನಿಮ್ಮ ಪಟ್ಟಣಗಳೆಷ್ಟೋ ನಿಮ್ಮ ದೇವರುಗಳೂ ಅಷ್ಟು; ಯೆರೂಸಲೇಮಿನ ಬೀದಿಗಳೆಷ್ಟೋ ತುಚ್ಫದೇವತೆಯಾದ ಬಾಳನಿಗೆ ಹೋಮಮಾಡುವದಕ್ಕಾಗಿ ನೀವು ಮಾಡಿಕೊಂಡಿರುವ ಬಲಿಪೀಠಗಳೂ ಅಷ್ಟು.",
			"14": "<sup>14</sup> ಆದಕಾರಣ ಈ ಜನರಿಗೋಸ್ಕರ ಬೇಡಿಕೊಳ್ಳಬೇಡ, ಇವರಿಗಾಗಿ ಮೊರೆಯಿಡಬೇಡ, ಪ್ರಾರ್ಥಿಸಲೂ ಬೇಡ; ಅವರು ತಮ್ಮ ಕೇಡಿನ ನಿಮಿತ್ತ ನನಗೆ ಮೊರೆಯಿಡುವಾಗ ನಾನು ಕೇಳೆನು.",
			"15": "<sup>15</sup> ನನ್ನ ಆಪ್ತಜನವು ನನ್ನ ಮನೆಯಲ್ಲಿ ಅಸಹ್ಯಕಾರ್ಯವನ್ನು ಮಾಡಿದ್ದೇಕೆ? ವ್ರತಗಳೂ ಮೀಸಲಿನ ಮಾಂಸವೂ ನಿನ್ನ ದುಷ್ಟತನವನ್ನು ಪರಿಹರಿಸಿಯಾವೇ? ಇಂಥವುಗಳ ಮೂಲಕ ತಪ್ಪಿಸಿಕೊಂಡೀಯಾ?",
			"16": "<sup>16</sup> ಯೆಹೋವನು ನಿನಗೆ ಸುಂದರಫಲದಿಂದ ಅಂದವಾದ ಹಚ್ಚನೆಯ ಒಲೀವಮರವೆಂದು ಹೆಸರಿಟ್ಟಿದ್ದನು; ಆ ಮರಕ್ಕೆ ಬೆಂಕಿಯಿಕ್ಕಿ ಬಲು ಧಗಧಗಿಸುವಂತೆ ಮಾಡಿದ್ದಾನೆ; ಅದರ ರೆಂಬೆಗಳು ಮುರಿದುಹೋಗಿವೆ.",
			"17": "<sup>17</sup> ಇಸ್ರಾಯೇಲ್ ವಂಶವೂ ಯೆಹೂದ ವಂಶವೂ ಬಾಳನಿಗೆ ಹೋಮವನ್ನರ್ಪಿಸಿ ನನ್ನನ್ನು ಕೆಣಕಿ ತಮಗೆ ಕೆಡುಕನ್ನು ಮಾಡಿಕೊಂಡದ್ದರಿಂದ ನಿನ್ನನ್ನು ನಾಟಿದ ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ನಿನಗೆ ಕೆಡುಕಾಗಲಿ ಎಂದು ಶಪಿಸಿದ್ದಾನೆ.",
			"18": "<sup>18</sup> ಯೆಹೋವನು ತಿಳಿಸಿದ್ದರಿಂದ [ಅವರ ಕುತಂತ್ರವು] ನನಗೆ ಗೊತ್ತಾಯಿತು; ಆಗಲೇ ಅವರ ಕೃತ್ಯಗಳನ್ನು ನನಗೆ ತೋರಿಸಿದಿ.",
			"19": "<sup>19</sup> ನಾನಾದರೋ ವಧೆಗೆ ಒಯ್ಯುವ ಸಾಧು ಕುರಿಗೆ ಸಮಾನನಾಗಿದ್ದೆನು; ಮರವನ್ನು ಫಲಸಹಿತವಾಗಿ ನಾಶಪಡಿಸೋಣ, ಇವನು ನಿರ್ನಾಮವಾಗುವಂತೆ ಜೀವಲೋಕದಿಂದ ಇವನನ್ನು ನಿರ್ಮೂಲಮಾಡೋಣ ಎಂದು ಅವರು ನನಗೆ ವಿರುದ್ಧವಾಗಿ ಕುಯುಕ್ತಿಗಳನ್ನು ಕಲ್ಪಿಸಿದ್ದು ನನಗೆ ತಿಳಿದಿರಲಿಲ್ಲ.",
			"20": "<sup>20</sup> ಹೃದಯವನ್ನೂ ಅಂತರಿಂದ್ರಿಯವನ್ನೂ ಪರೀಕ್ಷಿಸಿ ನ್ಯಾಯವಾಗಿ ತೀರ್ಪುಮಾಡುವ ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೇ, ನೀನು ಅವರಿಗೆ ಕೊಡುವ ಪ್ರತಿಫಲವನ್ನು ನಾನು ಕಾಣುವೆನು; ನನ್ನ ವ್ಯಾಜ್ಯವನ್ನು ನಿನಗೇ ಅರಿಕೆ ಮಾಡಿದ್ದೇನಷ್ಟೆ.",
			"21": "<sup>21</sup> ಹೀಗಿರಲು ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ನನಗೆ - ನಮ್ಮ ಕೈಯಿಂದ ನೀನು ಸಾಯಬಾರದಾಗಿದ್ದರೆ ಯೆಹೋವನ ಹೆಸರೆತ್ತಿ ಪ್ರವಾದಿಸಬೇಡ ಎಂದು ನಿನ್ನ ಪ್ರಾಣಕ್ಕೆ ಹೊಂಚುಹಾಕುತ್ತಿರುವ ಅನಾತೋತಿನವರ ವಿಷಯದಲ್ಲಿ ನನ್ನ ತೀರ್ಮಾನವು ಇದೇ -",
			"22": "<sup>22</sup> ಇಗೋ, ಅವರನ್ನು ದಂಡಿಸುವೆನು, ಯೌವನಸ್ಥರು ಖಡ್ಗದಿಂದ ನಾಶವಾಗುವರು, ಅವರ ಗಂಡು ಹೆಣ್ಣುಮಕ್ಕಳು ಕ್ಷಾಮದಿಂದ ಸಾಯುವರು;",
			"23": "<sup>23</sup> ದಂಡನೆಯ ವರುಷದಲ್ಲಿ ಅನಾತೋತಿನವರಿಗೆ ಕೇಡನ್ನು ಬರಮಾಡುವೆನು, ಅವರಲ್ಲಿ ಯಾರೂ ಉಳಿಯರು ಎಂದು ಹೇಳಿದ್ದಾನೆ."
		},
		"12": {
			"1": "<sup>1</sup> ಯೆಹೋವನೇ, ನೀನು ಧರ್ಮಸ್ವರೂಪನಾಗಿರುವದರಿಂದ ನಾನು ನಿನ್ನೊಡನೆ ವ್ಯಾಜ್ಯವಾಡಲಾರೆನು, ಆದರೂ ನಿನ್ನ ಸಂಗಡ ನ್ಯಾಯವನ್ನು ಚರ್ಚಿಸುವೆನು. ದುಷ್ಟರ ನಡತೆಗೆ ಏಕೆ ಸುಫಲವಾಗುತ್ತದೆ? ದ್ರೋಹಿಗಳೆಲ್ಲರೂ ನೆಮ್ಮದಿಯಾಗಿರುವದೇಕೆ?",
			"2": "<sup>2</sup> ನೀನು ಅವರನ್ನು ನಾಟಿದ್ದೀ, ಅವರು ಬೇರೂರಿ ಬೆಳೆದು ಹಣ್ಣುಬಿಟ್ಟಿದ್ದಾರೆ; ನೀನು ಅವರ ಬಾಯಿಗೆ ಸಮೀಪ, ಅಂತರಿಂದ್ರಿಯಕ್ಕೆ ದೂರ.",
			"3": "<sup>3</sup> ಯೆಹೋವನೇ, ನೀನು ನನ್ನನ್ನು ತಿಳಿದಿದ್ದೀ, ನನ್ನನ್ನು ನೋಡುತ್ತಾ ನಿನ್ನೊಂದಿಗೆ ಅನ್ಯೋನ್ಯವಾಗಿರುವ ನನ್ನ ಹೃದಯವನ್ನು ಪರೀಕ್ಷಿಸುತ್ತೀ, ಅವರನ್ನು ಕುರಿಗಳನ್ನೋ ಎಂಬಂತೆ ಕೊಲೆಗೆ ಎಳೆದು ವಧೆಯ ದಿನಕ್ಕೆ ಗೊತ್ತುಮಾಡು.",
			"4": "<sup>4</sup> ಇನ್ನೆಷ್ಟರವರೆಗೆ ಎಲ್ಲಾ ಸೀಮೆಯ ಹುಲ್ಲು ಸೊಪ್ಪು ಒಣಗಿ ದೇಶವು ದುಃಖಿಸುತ್ತಿರುವದು? ಅಲ್ಲಿನ ನಿವಾಸಿಗಳು ದುಷ್ಟರಾಗಿರುವದರಿಂದ ಮೃಗಪಕ್ಷಿಗಳು ಬಡಿದುಕೊಂಡು ಹೋಗಿವೆ; ಆಹಾ, ಅವನು ನಮ್ಮ ಅಂತ್ಯಗತಿಯನ್ನು ನೋಡುವದೇ ಇಲ್ಲ ಎಂದು ಹೇಳಿದ್ದಾರಷ್ಟೆ.",
			"5": "<sup>5</sup> [ಇಂತೆನ್ನುವ ನನಗೆ ಯೆಹೋವನು ಹೀಗೆ ಹೇಳಿದ್ದಾನೆ - ] ನೀನು ಕಾಲಾಳುಗಳ ಸಂಗಡ ಓಡಿ ಆಯಾಸಗೊಂಡಿದ್ದರೆ ಕುದುರೆಗಳೊಂದಿಗೆ ಓಡಿ ಹೇಗೆ ಮುಂದಾಗುವಿ? ನಿರಪಾಯದೇಶದಲ್ಲಿ ನೀನು ನಿರ್ಭಯವಾಗಿದ್ದರೂ ಯೊರ್ದನಿನ ದಟ್ಟಡವಿಯಲ್ಲಿ ಏನು ಮಾಡುವಿ?",
			"6": "<sup>6</sup> ನಿನ್ನ ಸಹೋದರರೂ ನಿನ್ನ ತಂದೆಯ ಮನೆಯವರೂ ನಿನಗೆ ದ್ರೋಹ ಮಾಡಿರುತ್ತಾರೆ. ಇವರೇ ನಿನ್ನ ಬೆನ್ನಟ್ಟುತ್ತಾ ಕೂಗಿರುತ್ತಾರೆ; ಒಳ್ಳೇ ಮಾತಾಡಿದರೂ ಇವರನ್ನು ನಂಬಬೇಡ.",
			"7": "<sup>7</sup> ನಾನು ನನ್ನ ಮನೆಯನ್ನು ತೊರೆದು ನನ್ನ ಸ್ವಾಸ್ತ್ಯವನ್ನು ನಿರಾಕರಿಸಿದ್ದೇನೆ; ನನ್ನ ಪ್ರಾಣಪ್ರಿಯಳನ್ನು ಅವಳ ಶತ್ರುಗಳ ಕೈಗೆ ಒಪ್ಪಿಸಿದ್ದೇನೆ.",
			"8": "<sup>8</sup> ನನ್ನ ಸ್ವಾಸ್ತ್ಯವಾದ ಜನವು ನನ್ನ ಪಾಲಿಗೆ ಅರಣ್ಯದಲ್ಲಿರುವ ಸಿಂಹದಂತಿದೆ; ಅದು ನನ್ನ ಮೇಲೆ ಗರ್ಜಿಸಿದೆ; ಆದಕಾರಣ ಅದನ್ನು ಹಗೆಮಾಡಿದ್ದೇನೆ.",
			"9": "<sup>9</sup> ನನ್ನ ಸ್ವಾಸ್ತ್ಯವಾದ ಜನವು ನನ್ನ ದೃಷ್ಟಿಗೆ ಚಿತ್ರವರ್ಣದ ಹದ್ದಿನಂತಿದೆಯೋ? ಹದ್ದುಗಳು ಅದರ ಸುತ್ತಲು ವಿರುದ್ಧವಾಗಿ ಕೂತಿವೆಯೋ? ಹೋಗಿರಿ, ಅದನ್ನು ತಿಂದುಬಿಡುವದಕ್ಕೆ ಎಲ್ಲಾ ಕಾಡುಮೃಗಗಳನ್ನು ಕರತನ್ನಿರಿ.",
			"10": "<sup>10</sup> ಬಹುಮಂದಿ ಮಂದೆಗಾರರು ನನ್ನ ತೋಟವನ್ನು ಕೆಡಿಸಿದ್ದಾರೆ; ನನ್ನ ಸ್ವಾಸ್ತ್ಯವನ್ನು ತುಳಿದು ನನಗೆ ಇಷ್ಟವಾದ ಆ ಸೊತ್ತನ್ನು ಹಾಳು ಕಾಡನ್ನಾಗಿ ಮಾಡಿದ್ದಾರೆ.",
			"11": "<sup>11</sup> ಹೌದು, ಹಾಳು ಮಾಡಿದ್ದಾರೆ, ಅದು ಹಾಳಾಗಿ ನನಗೆ ಗೋಳಿಡುತ್ತದೆ; ಯಾವನೂ ಮನಸ್ಸಿಗೆ ತಾರದ್ದರಿಂದ ದೇಶವೆಲ್ಲಾ ಹಾಳುಬಿದ್ದಿದೆ.",
			"12": "<sup>12</sup> ಕೊಳ್ಳೆಗಾರರು ಅರಣ್ಯದ ಎಲ್ಲಾ ಬೋಳುಗುಡ್ಡಗಳಲ್ಲಿಯೂ ಕಂಡುಬಂದಿದ್ದಾರೆ; ಯೆಹೋವನ ಖಡ್ಗವು ದೇಶದ ಒಂದು ಕಡೆಯಿಂದ ಇನ್ನೊಂದು ಕಡೆಯವರೆಗೆ ನುಂಗಿಬಿಡುತ್ತಿದೆ; ಯಾರಿಗೂ ನೆಮ್ಮದಿಯಿಲ್ಲ.",
			"13": "<sup>13</sup> ಗೋದಿಯನ್ನು ಬಿತ್ತಿ ಮುಳ್ಳನ್ನು ಕೊಯ್ದಿದ್ದಾರೆ; ಕ್ಷೇಮ ಕೆಡುವಷ್ಟು ಪ್ರಯಾಸಪಟ್ಟರೂ ಯಾವ ಲಾಭವನ್ನೂ ಹೊಂದಲಿಲ್ಲ; ಯೆಹೋವನ ರೋಷಾಗ್ನಿಯ ನಿಮಿತ್ತ ನಿಮ್ಮ ಬೆಳೆಯ ವಿಷಯವಾಗಿ ನಿಮಗೆ ನಾಚಿಕೆಯಾಗುವದು.",
			"14": "<sup>14</sup> ಯೆಹೋವನು ತನ್ನ ಜನರಾದ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ದಯಪಾಲಿಸಿದ ಸ್ವಾಸ್ತ್ಯಕ್ಕೆ ಕೈ ಹಾಕುವ ತನ್ನ ಕೆಟ್ಟ ನೆರೆಯವರ ವಿಷಯದಲ್ಲಿ ಹೀಗೆ ಹೇಳುತ್ತಾನೆ - ಆಹಾ, ಅವರನ್ನು ಅವರವರ ಸೀಮೆಗಳೊಳಗಿಂದ ಕಿತ್ತುಹಾಕಿ ಅವರ ಮಧ್ಯದಲ್ಲಿನ ಯೆಹೂದ ವಂಶವನ್ನೂ ಕಿತ್ತುಹಾಕುವೆನು.",
			"15": "<sup>15</sup> ನೋಡಿರಿ, ನಾನು ಅವರನ್ನು ಕಿತ್ತುಹಾಕಿದ ಮೇಲೆ ತಿರಿಗಿ ಅವರ ಮೇಲೆ ಕನಿಕರಪಟ್ಟು ಪ್ರತಿಯೊಬ್ಬನನ್ನೂ ಅವನವನ ಸ್ವಾಸ್ತ್ಯಕ್ಕೂ ಅವನವನ ದೇಶಕ್ಕೂ ಪುನಃ ಬರಮಾಡುವೆನು.",
			"16": "<sup>16</sup> ಇವರು ಮೊದಲು ಬಾಳನ ಹೆಸರೆತ್ತಿ ಪ್ರಮಾಣಮಾಡುವ ಅಭ್ಯಾಸವನ್ನು ನನ್ನ ಜನರಿಗೆ ಕಲಿಸಿಕೊಟ್ಟ ಪ್ರಕಾರ ಈಗ ನನ್ನ ಹೆಸರನ್ನೆತ್ತಿ ಯೆಹೋವನ ಜೀವದಾಣೆ ಎಂದು ಪ್ರಮಾಣ ಮಾಡುವ ನನ್ನ ಜನರ ಅಭ್ಯಾಸವನ್ನು ಚೆನ್ನಾಗಿ ಮಾಡಿಕೊಂಡರೆ ನನ್ನ ಜನರ ಮಧ್ಯದಲ್ಲಿ ನೆಲೆಗೊಂಡು ವೃದ್ಧಿಯಾಗುವರು.",
			"17": "<sup>17</sup> ಆದರೆ ಯಾವ ಜನಾಂಗವು ಕೇಳುವದಿಲ್ಲವೋ ಅದನ್ನು ನಾನು ಕಿತ್ತುಹಾಕುವೆನು, ಹೌದು, ಕಿತ್ತುಹಾಕಿ ನಾಶಮಾಡುವೆನು ಎಂಬದು ಯೆಹೋವನ ನುಡಿ."
		},
		"13": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ನನಗೆ - ನೀನು ಹೋಗಿ ನಾರಿನ ನಡುಕಟ್ಟನ್ನು ಕೊಂಡುಕೊಂಡು ಸೊಂಟಕ್ಕೆ ಬಿಗಿದುಕೋ, ನೀರಿನಲ್ಲಿ ನೆನಸಬೇಡ ಎಂದು ಅಪ್ಪಣೆಕೊಟ್ಟನು.",
			"2": "<sup>2</sup> ಆಗ ನಾನು ಯೆಹೋವನ ಮಾತಿನಂತೆ ನಡುಕಟ್ಟನ್ನು ಕೊಂಡುಕೊಂಡು ಸೊಂಟಕ್ಕೆ ಬಿಗಿದುಕೊಂಡೆನು.",
			"3": "<sup>3</sup> ಆಮೇಲೆ - ನೀನು ಕೊಂಡು ಸೊಂಟಕ್ಕೆ ಬಿಗಿದ ನಡುಕಟ್ಟನ್ನು ತೆಗೆದುಕೊಂಡು ಹೊರಟು ಯೂಫ್ರೇಟೀಸ್ ನದಿಗೆ ಹೋಗಿ ಅದನ್ನು ಅಲ್ಲಿ ಬಂಡೆಯ ಸಂದಿನೊಳಗೆ ಬಚ್ಚಿಡು ಎಂಬದಾಗಿ ಯೆಹೋವನ ಎರಡನೆಯ ಮಾತು ನನಗೆ ಬಂತು.",
			"5": "<sup>5</sup> ಆಗ ಯೆಹೋವನ ಅಪ್ಪಣೆಯ ಮೇರೆಗೆ ನಾನು ಹೋಗಿ ಯೂಫ್ರೇಟೀಸ್ ನದಿಯ ಬಳಿಯಲ್ಲಿ ಅದನ್ನು ಬಚ್ಚಿಟ್ಟೆನು.",
			"6": "<sup>6</sup> ಬಹುದಿನಗಳ ಮೇಲೆ ಯೆಹೋವನು ನನಗೆ - ಹೊರಡು, ಯೂಫ್ರೇಟೀಸ್ ನದಿಗೆ ಹೋಗಿ ಅಲ್ಲಿ ಬಚ್ಚಿಡಬೇಕೆಂದು ನಾನು ಅಪ್ಪಣೆಕೊಟ್ಟ ನಡುಕಟ್ಟನ್ನು ಅಲ್ಲಿಂದ ತೆಗೆ ಎಂದು ಹೇಳಿದನು.",
			"7": "<sup>7</sup> ಆಗ ನಾನು ಯೂಫ್ರೇಟೀಸ್ ನದಿಗೆ ಹೋಗಿ ಅಗೆದು ನಾನು ಬಚ್ಚಿಟ್ಟ ಸ್ಥಳದೊಳಗಿಂದ ನಡುಕಟ್ಟನ್ನು ತೆಗೆದೆನು; ಆಹಾ, ಅದು ಕೆಟ್ಟು ಯಾವ ಕೆಲಸಕ್ಕೂ ಬಾರದ್ದಾಗಿತ್ತು.",
			"8": "<sup>8</sup> ಆಗ ಯೆಹೋವನು ಈ ವಾಕ್ಯವನ್ನು ನನಗೆ ದಯಪಾಲಿಸಿದನು -",
			"9": "<sup>9</sup> ಇದು ಯೆಹೋವನಾದ ನನ್ನ ನುಡಿ - ಇದೇ ಪ್ರಕಾರವಾಗಿ ನಾನು ಯೆಹೂದದ ದೊಡ್ಡಸ್ತಿಕೆಯನ್ನೂ ಯೆರೂಸಲೇಮಿನ ಬಲು ದೊಡ್ಡಸ್ತಿಕೆಯನ್ನೂ ಕೆಡಿಸಿಬಿಡುವೆನು.",
			"10": "<sup>10</sup> ನನ್ನ ಮಾತುಗಳನ್ನು ಕೇಳಲೊಲ್ಲದೆ ತಮ್ಮ ಹೃದಯದ ಹಟದಂತೆ ನಡೆದು ಅನ್ಯದೇವತೆಗಳನ್ನು ಹಿಂಬಾಲಿಸಿ ಸೇವಿಸಿ ಪೂಜಿಸುತ್ತಿರುವ ಈ ದುಷ್ಟ ಜನರು ಯಾವ ಕೆಲಸಕ್ಕೂ ಬಾರದ ಈ ನಡುಕಟ್ಟಿಗೆ ಸಾಟಿಯಾಗುವರು.",
			"11": "<sup>11</sup> ಸಮಸ್ತ ಇಸ್ರಾಯೇಲ್ ವಂಶವೂ ಸಕಲ ಯೆಹೂದ ವಂಶವೂ ನನ್ನ ಪ್ರಜೆಯಾಗಿದ್ದು ನನಗೆ ಹೆಸರುವಾಸಿಯೂ ಸ್ತೋತ್ರವೂ ಮಹಿಮೆಯೂ ಆಗಿರಲೆಂದು ನಡುಕಟ್ಟು ಸೊಂಟಕ್ಕೆ ಬಿಗಿದಿರುವಂತೆ ಇವರನ್ನು ಬಿಗಿದುಕೊಂಡಿದ್ದೇನೆ; ಆದರೂ ಇವರು ಕೇಳಲೊಲ್ಲದೆ ಹೋದರು ಎಂದು ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ.",
			"12": "<sup>12</sup> ಹೀಗಿರಲು ಪ್ರತಿಯೊಂದು ಗಡಿಗೆಯು ದ್ರಾಕ್ಷಾರಸದಿಂದ ತುಂಬಿರುವದು ಎಂಬದಾಗಿ ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರಾದ ಯೆಹೋವನು ಹೇಳುವ ಮಾತನ್ನು ಕೇಳಿರಿ ಎಂದು ನೀನು ಅವರಿಗೆ ಸಾರು; ಆಗ ಅವರು ನಿನಗೆ - ಪ್ರತಿಯೊಂದು ಗಡಿಗೆಯು ದ್ರಾಕ್ಷಾರಸದಿಂದ ತುಂಬಿರುವದು ನಮಗೆ ಗೊತ್ತಿಲ್ಲವೋ ಎಂದು ಹೇಳುವರು.",
			"13": "<sup>13</sup> ಆಗ ನೀನು ಅವರಿಗೆ - ಯೆಹೋವನ ಈ ಮಾತನ್ನು ಕೇಳಿರಿ, ದಾವೀದನ ಸಿಂಹಾಸನವನ್ನೇರಿದ ಅರಸರನ್ನೂ ಯಾಜಕರನ್ನೂ ಪ್ರವಾದಿಗಳನ್ನೂ ಯೆರೂಸಲೇಮಿನ ಸಕಲ ನಿವಾಸಿಗಳನ್ನೂ ಅಂತು ದೇಶದ ಜನರೆಲ್ಲರನ್ನೂ ನಾನು ಅಮಲಿನಿಂದ ತುಂಬಿಸಿ",
			"14": "<sup>14</sup> ಹಿರಿಕಿರಿಯರಾದ ಸಮಸ್ತರನ್ನು ಒಬ್ಬರಿಗೊಬ್ಬರು ಬಡಿದಾಡುವಂತೆ ಮಾಡುವೆನು; ನಾನು ಅವರನ್ನು ಉಳಿಸೆನು, ಕನಿಕರಿಸೆನು, ಕರುಣಿಸೆನು, ನಾಶಮಾಡದೆ ಬಿಡೆನು; ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"15": "<sup>15</sup> ಕಿವಿಗೊಟ್ಟು ಕೇಳಿರಿ, ಹೆಮ್ಮೆ ಪಡಬೇಡಿರಿ; ಯೆಹೋವನೇ ಮಾತಾಡಿದ್ದಾನೆ.",
			"16": "<sup>16</sup> ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಘನಪಡಿಸಿರಿ; ಇಲ್ಲದಿದ್ದರೆ ಸ್ವಲ್ಪಕಾಲದೊಳಗೆ ಆತನು ಕತ್ತಲನ್ನು ಉಂಟುಮಾಡುವನು, ನಿಮ್ಮ ಕಾಲುಗಳು ಮೊಬ್ಬಿನ ಬೆಟ್ಟಗಳಲ್ಲಿ ಮುಗ್ಗರಿಸುವವು; ನೀವು ಬೆಳಕನ್ನು ನಿರೀಕ್ಷಿಸುತ್ತಿರುವಾಗ ಆತನು ಅದನ್ನು ಮರಣಾಂಧಕಾರದ ಕಾರ್ಗತ್ತಲನ್ನಾಗಿ ಮಾಡುವನು.",
			"17": "<sup>17</sup> ನೀವು ಕೇಳದಿದ್ದರೆ ನನ್ನ ಆತ್ಮವು ಗುಟ್ಟಾದ ಸ್ಥಳದಲ್ಲಿ ನಿಮ್ಮ ಗರ್ವದ ನಿಮಿತ್ತ ಗೋಳಾಡುವದು; ಯೆಹೋವನ ಮಂದೆಯು ಸೆರೆಯಾಗಿ ಹೋದದರಿಂದ ಬಹಳವಾಗಿ ಅಳುವೆನು, ನನ್ನ ನೇತ್ರವು ಅಶ್ರುಧಾರೆಯನ್ನು ಸುರಿಸುವದು.",
			"18": "<sup>18</sup> ರಾಜನಿಗೂ ರಾಜಮಾತೆಗೂ - ನೆಲದಲ್ಲಿ ಕೂತುಕೊಳ್ಳಿರಿ, ನಿಮ್ಮ ಅಂದದ ಕಿರೀಟವು ನಿಮ್ಮ ತಲೆಯಿಂದ ಕೆಳಗೆ ಬಿದ್ದಿದೆ ಎಂದು ಹೇಳಿರಿ.",
			"19": "<sup>19</sup> ದಕ್ಷಿಣದೇಶದ ಪಟ್ಟಣಗಳು ಮುತ್ತಿಗೆಯಾಗಿವೆ, ಅವುಗಳನ್ನು ಬಿಡಿಸಲು ಯಾರೂ ಇಲ್ಲ; ಯೆಹೂದವೆಲ್ಲಾ ಸೆರೆಹೋಗಿದೆ, ಸಂಪೂರ್ಣವಾಗಿ ಸೆರೆಯಾಗಿದೆ.",
			"20": "<sup>20</sup> [ಚೀಯೋನೇ] ಕಣ್ಣೆತ್ತು, ಬಡಗಲಿಂದ ಬರುವವರನ್ನು ನೋಡು; ನಿನಗೆ ಒಪ್ಪಿಸಿದ ಹಿಂಡು, ನಿನ್ನ ಅಂದವಾದ ಹಿಂಡು ಎಲ್ಲಿ?",
			"21": "<sup>21</sup> ನಿನ್ನಲ್ಲಿ ಬಳಿಕೆಗೊಳ್ಳುವಂತೆ ನೀನು ಅಭ್ಯಾಸಮಾಡಿಸಿದವರನ್ನು [ಯೆಹೋವನು] ನಿನಗೆ ತಲೆಯಾಗಿ ನೇಮಿಸುವಾಗ ಏನು ಹೇಳುವಿ? ವೇದನೆಯು ನಿನ್ನನ್ನು ಹೆರುವವಳನ್ನೋ ಎಂಬಂತೆ ಹಿಡಿಯುವದಲ್ಲವೆ.",
			"22": "<sup>22</sup> ಇವು ನನಗೆ ಏಕೆ ಸಂಭವಿಸಿದವು ಎಂದು ನೀನು ಮನದೊಳಗೆ ಅಂದುಕೊಂಡರೆ ನಿನ್ನ ನೆರಿಗೆಯು ಕೀಳಲ್ಪಟ್ಟು ನಿನ್ನ ಹಿಮ್ಮಡಿಯು ನಾಚಿಕೆಗೆ ಈಡಾದದ್ದಕ್ಕೆ ನಿನ್ನ ಮಹಾಪಾಪವೇ ಕಾರಣ ಎಂದು ನಿನಗೆ ಗೊತ್ತಾಗುವದು.",
			"23": "<sup>23</sup> ಕೂಷ್ಯನು ತನ್ನ ಚರ್ಮವನ್ನು ಮಾರ್ಪಡಿಸಬಲ್ಲನೇ? ಚಿರತೆಯು ತನ್ನ ಚುಕ್ಕೆಗಳನ್ನು ಬದಲಾಯಿಸೀತೇ? ಹೀಗಾದರೆ ದುಷ್ಟತನದಲ್ಲಿ ಶಿಕ್ಷಿತರಾದ ನೀವು ಶಿಷ್ಟತನದಲ್ಲಿ ನಡೆಯಬಹುದು.",
			"24": "<sup>24</sup> ಆದಕಾರಣ ಬೈಲುಗಾಳಿಯು ಬಡಿದುಕೊಂಡು ಹೋಗುವ ಒಣಹುಲ್ಲನ್ನೋ ಎಂಬಂತೆ ನಾನು ಅವರನ್ನು ಚದರಿಸಿಬಿಡುವೆನು.",
			"25": "<sup>25</sup> ನೀನು ನನ್ನನ್ನು ಮರೆತು ಸುಳ್ಳನ್ನು ನಂಬಿದ್ದರಿಂದ ಇದೇ ನಿನ್ನ ಪಾಲು, ಇದೇ ನಾನು ನಿನಗೆ ಅಳೆದುಕೊಟ್ಟ ಭಾಗ ಎಂದು ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ.",
			"26": "<sup>26</sup> ನಿನ್ನ ನೆರಿಗೆಯನ್ನು ನಿನ್ನ ಕಣ್ಣ ಮುಂದೆಯೇ ಕೀಳಿಸುವೆನು, ನಿನಗೆ ಅವಮಾನವಾಗುವದು.",
			"27": "<sup>27</sup> ನಾನು ನಿನ್ನ ವ್ಯಭಿಚಾರಗಳನ್ನು, ನಿನ್ನ ಹೇಕಾರಗಳನ್ನು, ನಿನ್ನ ಕೆಟ್ಟ ಸೂಳೆಗಾರಿಕೆಯನ್ನು, ಅಂತು ಗುಡ್ಡಗಳಲ್ಲಿಯೂ ಬೈಲಿನಲ್ಲಿಯೂ ನೀನು ನಡಿಸಿದ ಅಸಹ್ಯಕಾರ್ಯಗಳನ್ನು ನೋಡಿದ್ದೇನೆ. ಯೆರೂಸಲೇಮೇ, ನಿನ್ನ ಗತಿಯನ್ನು ಏನೆಂದು ಹೇಳಲಿ! ನೀನು ಶುದ್ಧಳಾಗಲು ಇನ್ನೆಷ್ಟು ಕಾಲಬೇಕು?"
		},
		"14": {
			"1": "<sup>1</sup> ಬರದ ವಿಷಯವಾಗಿ ಯೆಹೋವನು ಯೆರೆಮೀಯನಿಗೆ ದಯಪಾಲಿಸಿದ ನುಡಿ.",
			"2": "<sup>2</sup> ಯೆಹೂದವು ದುಃಖಿಸುತ್ತದೆ, ಅದರ ಊರುಗಳವರು ಕಂಗೆಟ್ಟು ಕರ್ರಗಾಗಿ ನೆಲದ ಮೇಲೆ ಬಿದ್ದಿದ್ದಾರೆ, ಯೆರೂಸಲೇಮಿನವರ ಗೋಳಾಟವು ಕೇಳಿಸುತ್ತದೆ.",
			"3": "<sup>3</sup> ಅವರಲ್ಲಿನ ಶ್ರೀಮಂತರು ತಮ್ಮ ಕೈಕೆಳಗಿನವರನ್ನು ನೀರಿಗೆ ಕಳುಹಿಸುತ್ತಾರೆ; ಅವರು ಕೊಳಗಳಿಗೆ ಹೋದಾಗ ನೀರು ಕಾಣುವದಿಲ್ಲ, ಬರೀ ಪಾತ್ರೆಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಹಿಂದಕ್ಕೆ ಬರುತ್ತಾರೆ; ಆಶೆಗೆಟ್ಟು ನಾಚಿಕೆಪಟ್ಟು ಮೋರೆ ಮುಚ್ಚಿಕೊಳ್ಳುತ್ತಾರೆ.",
			"4": "<sup>4</sup> ದೇಶದಲ್ಲಿ ಮಳೆಯಿಲ್ಲದೆ ಭೂಮಿಯು ಬಿರುಕುಬಿಟ್ಟಿರುವದರಿಂದ ರೈತರು ನಾಚಿಕೆಪಟ್ಟು ಮೋರೆಮುಚ್ಚಿಕೊಳ್ಳುತ್ತಾರೆ.",
			"5": "<sup>5</sup> ಇದಲ್ಲದೆ ಹುಲ್ಲೆಯು ಕಾಡಿನಲ್ಲಿ ಈದು ಹುಲ್ಲಿಲ್ಲದ ಕಾರಣ ತನ್ನ ಮರಿಯನ್ನು ಬಿಟ್ಟುಬಿಡುತ್ತದೆ.",
			"6": "<sup>6</sup> ಕಾಡುಕತ್ತೆಗಳು ಬೋಳುಗುಡ್ಡಗಳಲ್ಲಿ ನಿಂತುಕೊಂಡು ನರಿಗಳಂತೆ ಅಸುರುಸುರಾಗುತ್ತಾ ಗಾಳಿಯನ್ನು ಹಾರೈಸುತ್ತವೆ, ಸೊಪ್ಪುಸದೆಯಿಲ್ಲದೆ ಕಂಗೆಡುತ್ತವೆ.",
			"7": "<sup>7</sup> ಯೆಹೋವನೇ, ನಮ್ಮ ಅಪರಾಧಗಳು ನಮಗೆ ವಿರುದ್ಧವಾಗಿ ಸಾಕ್ಷಿಕೊಟ್ಟರೂ ನಿನ್ನ ಹೆಸರಿನ ನಿಮಿತ್ತ ಕಾರ್ಯವನ್ನು ಸಾಧಿಸು; ನಮ್ಮ ದ್ರೋಹಗಳು ಬಹಳ, ನಿನಗೆ ಪಾಪವನ್ನು ಮಾಡಿದ್ದೇವೆ.",
			"8": "<sup>8</sup> ಇಸ್ರಾಯೇಲ್ಯರ ನಿರೀಕ್ಷೆಯೇ, ಇಕ್ಕಟ್ಟಿನಲ್ಲಿ ಅವರಿಗೆ ರಕ್ಷಕನೇ, ಏಕೆ ದೇಶದಲ್ಲಿ ಪರದೇಶಿಯಂತೆಯೂ ಇಳಿದುಕೊಳ್ಳುವದಕ್ಕೆ ಗುಡಾರಹಾಕಿಕೊಳ್ಳುವ ಪ್ರಯಾಣಿಕನ ಹಾಗೆಯೂ ಇದ್ದೀ?",
			"9": "<sup>9</sup> ಏಕೆ ಸ್ತಬ್ಧನಂತೆಯೂ ರಕ್ಷಿಸಲಾರದ ಶೂರನ ಹಾಗೆಯೂ ಇದ್ದೀ? ಆದರೂ ಯೆಹೋವನೇ, ನೀನು ನಮ್ಮ ಮಧ್ಯದಲ್ಲಿರುತ್ತೀ, ನಾವು ನಿನ್ನ ಹೆಸರಿನವರು, ನಮ್ಮನ್ನು ಕೈಬಿಡಬೇಡ.",
			"10": "<sup>10</sup> ಯೆಹೋವನು ಈ ಜನರನ್ನು ಕುರಿತು - ಇವರು [ನನ್ನನ್ನು ಪ್ರಯಾಣಿಕನು ಎಂದ] ಹಾಗೆಯೇ ಅಲೆಯುವದಕ್ಕೆ ಇಷ್ಟಪಟ್ಟಿದ್ದಾರೆ, ಕಾಲನ್ನು ಹಿಂದೆಗೆಯಲಿಲ್ಲ; ಆದಕಾರಣ ಯೆಹೋವನಾದ ನಾನು ಇವರನ್ನು ಕಟಾಕ್ಷಿಸೆನು; ಈಗಲೇ ಇವರ ಅಪರಾಧವನ್ನು ಮನಸ್ಸಿಗೆ ತಂದುಕೊಂಡು ಇವರ ಪಾಪಗಳಿಗೆ ದಂಡನೆಯನ್ನು ವಿಧಿಸುವೆನು ಎಂದು ಹೇಳಿದ್ದಾನೆ.",
			"11": "<sup>11</sup> ಮತ್ತು ಯೆಹೋವನು ನನಗೆ - ಈ ಜನರ ಹಿತವನ್ನು ಬಯಸಿ ಇವರಿಗೋಸ್ಕರ ಪ್ರಾರ್ಥನೆಮಾಡಬೇಡ.",
			"12": "<sup>12</sup> ಇವರು ಉಪವಾಸ ಮಾಡುವಾಗ ಇವರ ಮೊರೆಯನ್ನು ಕೇಳೆನು; ಹೋಮನೈವೇದ್ಯಗಳನ್ನು ಅರ್ಪಿಸಲು ಸ್ವೀಕರಿಸೆನು; ಖಡ್ಗ ಕ್ಷಾಮ ವ್ಯಾಧಿಗಳಿಂದ ಇವರನ್ನು ನಿರ್ಮೂಲಮಾಡುವೆನು ಎಂದು ಹೇಳಿದನು.",
			"13": "<sup>13</sup> ಅದಕ್ಕೆ ನಾನು - ಅಯ್ಯೋ, ಕರ್ತನಾದ ಯೆಹೋವನೇ, ಇಗೋ ಪ್ರವಾದಿಗಳು ಈ ಜನರಿಗೆ - ಖಡ್ಗವು ನಿಮ್ಮ ಕಣ್ಣಿಗೆ ಕಾಣದು, ಕ್ಷಾಮವು ನಿಮಗೆ ಸಂಭವಿಸದು; ಯೆಹೋವನು ಈ ಸ್ಥಳದಲ್ಲಿ ಸ್ಥಿರಸಮಾಧಾನವನ್ನು ನಿಮಗೆ ಕೊಡುವನು ಎಂದು ನುಡಿಯುತ್ತಾರೆ ಎಂಬದಾಗಿ ಹೇಳಿದೆನು.",
			"14": "<sup>14</sup> ಆಗ ಯೆಹೋವನು ನನಗೆ ಹೀಗೆ ನುಡಿದನು - ಪ್ರವಾದಿಗಳು ನನ್ನ ಹೆಸರಿನಿಂದ ಸುಳ್ಳಾಗಿ ಪ್ರವಾದಿಸುತ್ತಾರೆ; ನಾನು ಅವರನ್ನು ಕಳುಹಿಸಲಿಲ್ಲ; ಅವರಿಗೆ ಅಪ್ಪಣೆಕೊಡಲಿಲ್ಲ, ಏನೂ ಹೇಳಲಿಲ್ಲ; ಅವರು ಸುಳ್ಳಾದ ದಿವ್ಯದರ್ಶನವನ್ನೂ ಕಣಿಯನ್ನೂ ಮಾಯಾತಂತ್ರವನ್ನೂ ಸ್ವಕಲ್ಪಿತಮೋಸವನ್ನೂ ನಿಮಗೆ ಪ್ರಕಟಿಸುತ್ತಾರೆ.",
			"15": "<sup>15</sup> ಆದಕಾರಣ ನನ್ನ ಅಪ್ಪಣೆಯಿಲ್ಲದೆ ನನ್ನ ಹೆಸರಿನಿಂದ ಪ್ರವಾದಿಸುತ್ತಾ ಖಡ್ಗವೂ ಕ್ಷಾಮವೂ ಈ ದೇಶಕ್ಕೆ ಬಾರವು ಎಂದು ಬೋಧಿಸುವ ಪ್ರವಾದಿಗಳ ವಿಷಯದಲ್ಲಿ ಯೆಹೋವನಾದ ನಾನು - ಖಡ್ಗದಿಂದಲೂ ಕ್ಷಾಮದಿಂದಲೂ ಈ ಪ್ರವಾದಿಗಳು ನಿರ್ಮೂಲರಾಗುವರು ಎಂದು ಹೇಳುತ್ತೇನೆ.",
			"16": "<sup>16</sup> ಮತ್ತು ಇವರ ಪ್ರವಾದನೆಯನ್ನು ಕೇಳುವ ಜನರು ಕ್ಷಾಮಖಡ್ಗಗಳ ದೆಸೆಯಿಂದ ಯೆರೂಸಲೇಮಿನ ಬೀದಿಗಳಲ್ಲಿ ಬಿಸಾಡಲ್ಪಡುವರು; ಅವರನ್ನೂ ಅವರ ಹೆಂಡರುಮಕ್ಕಳನ್ನೂ ಹೂಣಿಡುವದಕ್ಕೆ ಯಾರೂ ಇರುವದಿಲ್ಲ; ನಾನು ಅವರ ದುಷ್ಟತನವನ್ನೇ ಅವರ ಮೇಲೆ ಹೊಯ್ದುಬಿಡುವೆನು.",
			"17": "<sup>17</sup> ನೀನು ಈ ಮಾತನ್ನು ಅವರಿಗೆ ಹೇಳು - ನನ್ನ ಕಣ್ಣೀರು ರಾತ್ರೀಹಗಲು ನಿರಂತರ ಸುರಿಯಲಿ; ಏಕಂದರೆ ನನ್ನ ಜನವೆಂಬ ಯುವತಿಗೆ ದೊಡ್ಡ ಗಾಯವಾಯಿತು, ಹೌದು ಗಡುಸಾದ ಪೆಟ್ಟುಬಿತ್ತು.",
			"18": "<sup>18</sup> ನಾನು ಊರಹೊರಗೆ ಹೋದರೆ ಇಗೋ, ಖಡ್ಗದಿಂದ ಹತರಾದವರು; ಊರೊಳಗೆ ಬಂದರೆ ಇಗೋ, ಕ್ಷಾಮದಿಂದ ಕೊರಗುವವರು! ಪ್ರವಾದಿಗಳೂ ಯಾಜಕರೂ ತಮಗೆ ಗೊತ್ತಿಲ್ಲದ ದೇಶಕ್ಕೆ ಗಡೀಪಾರಾಗಿದ್ದಾರೆ.",
			"19": "<sup>19</sup> ನೀನು ಯೆಹೂದವನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ತ್ಯಜಿಸಿಬಿಟ್ಟಿದ್ದೀಯೋ? ಚೀಯೋನಿನ ಮೇಲೆ ಮನಃಪೂರ್ವಕವಾಗಿ ಅಸಹ್ಯಪಟ್ಟಿದ್ದೀಯೋ? ನಮಗೆ ಗುಣವಾಗದಂತೆ ಏಕೆ ಪೆಟ್ಟುಹೊಡೆದಿ? ಸುಖವನ್ನು ನಿರೀಕ್ಷಿಸಿದೆವು, ಯಾವ ಮೇಲೂ ಆಗಲಿಲ್ಲ; ಕ್ಷೇಮಕಾಲವನ್ನು ಎದುರುನೋಡಿದೆವು, ಹಾ, ಅಂಜಿಕೆಯೇ!",
			"20": "<sup>20</sup> ಯೆಹೋವನೇ, ನಮ್ಮ ದುಷ್ಟತನವನ್ನೂ ನಮ್ಮ ಪಿತೃಗಳ ದುರಾಚಾರವನ್ನೂ ತಿಳಿದುಕೊಂಡಿದ್ದೇವೆ; ನಿನಗೇ ಪಾಪಮಾಡಿದ್ದೇವಲ್ಲ.",
			"21": "<sup>21</sup> ನಿನ್ನ ನಾಮದ ಮೇಲೆ ದೃಷ್ಟಿಯಿಡು, ನಿನ್ನ ಮಹಿಮೆಯ ಸಿಂಹಾಸನವನ್ನು ಅಸಡ್ಡೆಮಾಡಬೇಡ, ಅವಮಾನಪಡಿಸಬೇಡ; ನೀನು ನಮ್ಮೊಂದಿಗೆ ಮಾಡಿಕೊಂಡ ಒಡಂಬಡಿಕೆಯನ್ನು ಜ್ಞಾಪಕಕ್ಕೆ ತಂದುಕೋ, ಅದನ್ನು ಭಂಗಪಡಿಸದಿರು.",
			"22": "<sup>22</sup> ಜನಾಂಗಗಳ ವ್ಯರ್ಥವಿಗ್ರಹಗಳೊಳಗೆ ಮಳೆಯನ್ನು ಸುರಿಸಬಲ್ಲವುಗಳುಂಟೇ? ಆಕಾಶವು ತಾನಾಗಿ ಹದಮಳೆಗಳನ್ನು ಕೊಟ್ಟೀತೇ? ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವಾ, ನೀನೇ ವೃಷ್ಟಿಪ್ರದನು; ನಾವು ನಿನ್ನನ್ನೇ ನಿರೀಕ್ಷಿಸುವೆವು; ನೀನು ಇವುಗಳನ್ನೆಲ್ಲಾ ನಡಿಸುವವನಾಗಿದ್ದೀಯಷ್ಟೆ."
		},
		"15": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ನನಗೆ ಹೀಗೆ ಹೇಳಿದನು - ಮೋಶೆಯೂ ಸಮುವೇಲನೂ ನನಗೆ ವಿಜ್ಞಾಪಿಸಿದರೂ ನನ್ನ ಮನಸ್ಸು ಈ ಜನರ ಕಡೆಗೆ ತಿರುಗದು; ನನ್ನ ಕಣ್ಣೆದುರಿನಿಂದ ಇವರನ್ನು ನೂಕಿಬಿಡು, ತೊಲಗಿಹೋಗಲಿ! ಅವರು - ನಾವು ಎಲ್ಲಿಗೆ ಹೋಗೋಣ ಎಂದು ಕೇಳಲು",
			"2": "<sup>2</sup> ನೀನು ಅವರಿಗೆ - ಮರಣವ್ಯಾಧಿಗೆ ಗೊತ್ತಾದವರು ಮರಣವ್ಯಾಧಿಗೆ, ಖಡ್ಗಕ್ಕೆ ಗೊತ್ತಾದವರು ಖಡ್ಗಕ್ಕೆ, ಕ್ಷಾಮಕ್ಕೆ ಗೊತ್ತಾದವರು ಕ್ಷಾಮಕ್ಕೆ, ಸೆರೆಗೆ ಗೊತ್ತಾದವರು ಸೆರೆಗೆ ಗುರಿಯಾಗಿ ಹೋಗಲಿ; ಇದೇ ಯೆಹೋವನ ನುಡಿ ಎಂದು ಹೇಳು.",
			"3": "<sup>3</sup> ನಾನು ಅವರಿಗೆ ನಾಲ್ಕು ವಿಧವಾದ ಬಾಧೆಗಳನ್ನು, ಅಂದರೆ ಕಡಿಯುವದಕ್ಕೆ ಖಡ್ಗವನ್ನು, ಸೀಳುವದಕ್ಕೆ ನಾಯಿಗಳನ್ನು, ನುಂಗಿ ಹಾಳುಮಾಡುವದಕ್ಕೆ ಆಕಾಶಪಕ್ಷಿಗಳನ್ನೂ ಭೂಜಂತುಗಳನ್ನೂ ನೇಮಿಸುವೆನು ಎಂಬದು ಯೆಹೋವನಾದ ನನ್ನ ಮಾತು.",
			"4": "<sup>4</sup> ಹಿಜ್ಕೀಯನ ಮಗನೂ ಯೆಹೂದದ ಅರಸನೂ ಆದ ಮನಸ್ಸೆಯು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ನಡಿಸಿದ್ದಕ್ಕೆ ಪ್ರತಿಫಲವಾಗಿ ನಾನು ಅವರನ್ನು ಲೋಕದ ಸಮಸ್ತರಾಜ್ಯಗಳಿಗೆ ಭಯಾಸ್ಪದವಾಗುವಂತೆ ಮಾಡುವೆನು.",
			"5": "<sup>5</sup> ಯೆರೂಸಲೇಮೇ, ಯಾರು ನಿನ್ನನ್ನು ಕರುಣಿಸುವರು? ನಿನಗಾಗಿ ಯಾರು ಬಡುಕೊಳ್ಳುವರು? ನಿನ್ನ ಕ್ಷೇಮವನ್ನು ವಿಚಾರಿಸಲು ಯಾರು ನಿನ್ನ ಕಡೆಗೆ ತಿರುಗುವರು?",
			"6": "<sup>6</sup> ಯೆಹೋವನ ಈ ಮಾತನ್ನು ಕೇಳು - ನೀನು ನನ್ನನ್ನು ಅಲ್ಲಗಳೆದು ಹಿಂದಿರುಗಿದ್ದೀ; ಆದಕಾರಣ ನಾನು ನಿನ್ನ ಮೇಲೆ ಕೈಯೆತ್ತಿ ನಿನ್ನನ್ನು ನಾಶಮಾಡುವೆನು; ಕ್ಷಮಿಸಿ ಕ್ಷಮಿಸಿ ಸಾಕಾಯಿತು.",
			"7": "<sup>7</sup> ನನ್ನ ಜನರನ್ನು ದೇಶದ ಉಕ್ಕಡಗಳಲ್ಲಿ ಕವೆಗೋಲಿನಿಂದ ಹಾರಿಸಿ ತೂರುವೆನು; ಅವರನ್ನು ಪುತ್ರಶೋಕಕ್ಕೆ ಈಡುಮಾಡಿ ನಾಶಪಡಿಸುವೆನು; ಅವರು ಹೋದ ದಾರಿಯಿಂದ ಹಿಂದಿರುಗರು.",
			"8": "<sup>8</sup> ಅವರ ವಿಧವೆಯರು ನನ್ನ ಭಾಗಕ್ಕೆ ಸಮುದ್ರದ ಉಸುಬಿಗಿಂತ ಹೆಚ್ಚಾಗಿರುವರು; ಯುವಕರ ತಾಯಿಗಳ ಮೇಲೆ ಕೊಳ್ಳೆಹೊಡೆಯುವವನನ್ನು ಮಧ್ಯಾಹ್ನದಲ್ಲೇ ಬರಮಾಡುವೆನು; ಅವರ ಮೇಲೆ ಕಳವಳವನ್ನೂ ದಿಗಿಲನ್ನೂ ತಟ್ಟನೆ ಬೀಳಿಸುವೆನು.",
			"9": "<sup>9</sup> ಏಳು ಮಂದಿ ಮಕ್ಕಳನ್ನು ಹೆತ್ತ ಭಾಗ್ಯವಂತಳೂ ಬಳಲಿ ಪ್ರಾಣಬಿಟ್ಟಂತಿದ್ದಾಳೆ, ಹಗಲಿನಲ್ಲಿಯೇ ಆಕೆಯ ಪಾಲಿಗೆ ಸೂರ್ಯನು ಮುಣುಗಿದ್ದಾನೆ, ಆಶಾಭಂಗಪಟ್ಟು ಅವಮಾನಕ್ಕೆ ಈಡಾಗಿದ್ದಾಳೆ; ಆಕೆಯ ಉಳಿದ ಸಂತಾನವನ್ನು ಶತ್ರುಗಳ ಕಣ್ಣೆದುರಿನಲ್ಲಿ ನಾನು ಖಡ್ಗಕ್ಕೆ ಗುರಿಮಾಡುವೆನು, ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"10": "<sup>10</sup> ನನ್ನ ತಾಯೀ, ನನ್ನ ಗತಿಯನ್ನು ಏನು ಹೇಳಲಿ! ನಿನ್ನ ಗರ್ಭದಿಂದ ಬಂದ ನಾನು ಲೋಕದವರೆಲ್ಲರಿಗೆ ಜಗಳಗಂಟಿಗನೂ ವ್ಯಾಜ್ಯಗಾರನೂ ಆಗಿದ್ದೇನಲ್ಲಾ. ನಾನು ಹಣವನ್ನು ಬಡ್ಡಿಗೆ ಕೊಟ್ಟವನಲ್ಲ, ತೆಗೆದುಕೊಂಡವನಲ್ಲ, ಆದರೂ ನನ್ನನ್ನು ಎಲ್ಲರೂ ಬಯ್ಯುತ್ತಾರೆ.",
			"11": "<sup>11</sup> ಇದಕ್ಕೆ ಯೆಹೋವನು - ನಿನಗೆ ಮೇಲಾಗುವಂತೆ ನಿಶ್ಚಯವಾಗಿ ನಿನ್ನನ್ನು ಬಿಡುಗಡೆಮಾಡುವೆನು, ನಿಜನಿಜವಾಗಿ ನಿನ್ನ ಶತ್ರುವನ್ನು ಕೇಡಿನಲ್ಲಿಯೂ ಇಕ್ಕಟ್ಟಿನಲ್ಲಿಯೂ ನಿನಗೆ ಶರಣಾಗತನನ್ನಾಗಿ ಮಾಡುವೆನು.",
			"12": "<sup>12</sup> ಕಬ್ಬಿಣವನ್ನು, ಬಡಗಣ ಕಬ್ಬಿಣವನ್ನು ಯಾರು ಮುರಿದಾರು? ತಾಮ್ರವನ್ನು ಮುರಿಯುವದು ಯಾರಿಂದಾದೀತು ಎಂದು ಹೇಳಿದನು.",
			"13": "<sup>13</sup> ನಿನ್ನ ಸಕಲ ಪ್ರಾಂತ್ಯಗಳಲ್ಲಿ ನೀನು ಮಾಡಿದ ಎಲ್ಲಾ ಪಾಪಗಳ ನಿಮಿತ್ತ ನಾನು ನಿನ್ನ ಸೊತ್ತು ಸಂಪತ್ತುಗಳನ್ನು ಲಾಭವಿಲ್ಲದೆ ಸೂರೆಗೆ ಈಡುಮಾಡಿ",
			"14": "<sup>14</sup> ನಿನ್ನ ಶತ್ರುಗಳ ಕೈಗೆ ಕೊಟ್ಟು ನೀನು ನೋಡದ ದೇಶಕ್ಕೆ ಸಾಗಿಸಿಬಿಡುವೆನು. ನನ್ನ ರೋಷದಿಂದ ಉರಿಯು ಹತ್ತಿಕೊಂಡಿದೆ, ಅದು ನಿಮ್ಮನ್ನು ಸುಟ್ಟುಬಿಡುವದು.",
			"15": "<sup>15</sup> [ಅದಕ್ಕೆ ನಾನು] - ಯೆಹೋವನೇ, ನೀನೇ ಬಲ್ಲೆ, ನನ್ನನ್ನು ಜ್ಞಾಪಕಕ್ಕೆ ತಂದು ಕಟಾಕ್ಷಿಸು; ನನಗಾಗಿ ನನ್ನ ಹಿಂಸಕರಿಗೆ ಮುಯ್ಯಿತೀರಿಸು; ಅವರಿಗೆ ಹೆಚ್ಚು ತಾಳ್ಮೆಯನ್ನು ತೋರಿಸಬೇಡ, ನನ್ನನ್ನು ನಿರ್ಮೂಲಮಾಡಬೇಡ; ನಿನ್ನ ನಿಮಿತ್ತವೇ ನಾನು ನಿಂದೆಗೆ ಗುರಿಯಾದೆನೆಂದು ಚಿತ್ತಕ್ಕೆ ತಂದುಕೋ.",
			"16": "<sup>16</sup> ನನಗೆ ದೊರೆತ ನಿನ್ನ ಮಾತುಗಳನ್ನು ಆಹಾರಮಾಡಿಕೊಂಡೆನು, ನಿನ್ನ ನುಡಿಗಳು ನನಗೆ ಹರ್ಷವೂ ಹೃದಯಾನಂದವೂ ಆದವು; ಸೇನಾಧೀಶ್ವರನಾದ ದೇವರೇ, ಯೆಹೋವನೇ, ನಾನು ನಿನ್ನ ಹೆಸರಿನವನಲ್ಲವೇ!",
			"17": "<sup>17</sup> ನಾನು ವಿನೋದಗಾರರ ಕೂಟದಲ್ಲಿ ಕೂತುಕೊಳ್ಳಲಿಲ್ಲ, ಉಲ್ಲಾಸಪಡಲೂ ಇಲ್ಲ; ನೀನು ನನ್ನ ಮೇಲೆ ಕೈಯಿಟ್ಟಿದ್ದರಿಂದ ಒಂಟಿಗನಾಗಿ ಕೂತೆನು; ನನ್ನನ್ನು ರೋಷದಿಂದ ತುಂಬಿಸಿದ್ದಿಯಷ್ಟೆ.",
			"18": "<sup>18</sup> ಏಕೆ ನನ್ನ ವ್ಯಥೆಯು ನಿರಂತರವಾಗಿದೆ? ನನ್ನ ಗಾಯವು ಗಡುಸಾಗಿ ಗುಣಹೊಂದಲೊಲ್ಲದೇಕೆ? ನೀನು ನನಗೆ ನೀರು ಬತ್ತುವ ಕಳ್ಳತೊರೆಯಂತಿರಬೇಕೋ [ಎಂದು ಅರಿಕೆಮಾಡಿಕೊಂಡೆನು.]",
			"19": "<sup>19</sup> ಇದರಿಂದ ಯೆಹೋವನು ಹೀಗಂದಿದ್ದಾನೆ - ನೀನು [ನನ್ನ ಕಡೆಗೆ] ಹಿಂದಿರುಗಿದರೆ ನನ್ನ ಸಮ್ಮುಖದಲ್ಲಿ ನಿಲ್ಲುವಂತೆ ನಾನು ನಿನ್ನನ್ನು ತಿರುಗಿ ಸೇರಿಸಿಕೊಳ್ಳುವೆನು; ನೀನು ತುಚ್ಫವಾದದ್ದನ್ನು ನಿರಾಕರಿಸಿ ಅಮೂಲ್ಯವಾದದ್ದನ್ನು ಪ್ರಕಾಶಪಡಿಸಿದರೆ ನೀನು ನನ್ನ ಬಾಯಂತಿರುವಿ. ಜನರು ನಿನ್ನ ಕಡೆಗೆ ತಿರುಗಿಕೊಳ್ಳುವರು, ನೀನು ಅವರ ಕಡೆಗೆ ತಿರುಗದಿರುವಿ.",
			"20": "<sup>20</sup> ನಾನು ನಿನ್ನನ್ನು ಈ ಜನರಿಗೆ ದುರ್ಗಮವಾದ ತಾಮ್ರದ ಪೌಳಿಗೋಡೆಯನ್ನಾಗಿ ಮಾಡುವೆನು; ಅವರು ನಿನಗೆ ವಿರುದ್ಧವಾಗಿ ಯುದ್ಧಮಾಡುವರು. ಆದರೆ ನಿನ್ನನ್ನು ಸೋಲಿಸಲಾಗುವದಿಲ್ಲ. ನಿನ್ನನ್ನು ಉದ್ಧರಿಸಿ ರಕ್ಷಿಸಲು ನಾನೇ ನಿನ್ನೊಂದಿಗಿರುವೆನು. ಇದು ಯೆಹೋವನಾದ ನನ್ನ ಮಾತು.",
			"21": "<sup>21</sup> ನಾನು ನಿನ್ನನ್ನು ದುಷ್ಟರ ಕೈಯಿಂದ ಉದ್ಧರಿಸುವೆನು; ಹೌದು, ಕ್ರೂರರ ಕೈಯಿಂದ ರಕ್ಷಿಸುವೆನು."
		},
		"16": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಈ ಮಾತನ್ನು ನನಗೆ ದಯಪಾಲಿಸಿದನು -",
			"2": "<sup>2</sup> ನೀನು ಮದುವೆಯಾಗಬೇಡ, ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳು ಈ ಸ್ಥಳದಲ್ಲಿ ನಿನಗೆ ಹುಟ್ಟದಿರಲಿ.",
			"3": "<sup>3</sup> ಏಕಂದರೆ ಈ ಸ್ಥಳದಲ್ಲಿ ಹುಟ್ಟುವ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳು, ಅವರನ್ನು ಹೆತ್ತ ತಾಯಿಯರು, ಅವರನ್ನು ಈ ದೇಶದಲ್ಲಿ ಪಡೆದ ತಂದೆಗಳು, ಇವರೆಲ್ಲರ ವಿಷಯವಾಗಿ ಯೆಹೋವನು ಹೀಗೆ ನುಡಿದಿದ್ದಾನೆ -",
			"4": "<sup>4</sup> ಅವರು ಘೋರಮರಣಕ್ಕೆ ಗುರಿಯಾಗುವರು, ಅವರಿಗಾಗಿ ಯಾರೂ ಗೋಳಾಡರು, ಅವರನ್ನು ಯಾರೂ ಹೂಣಿಡರು; ಅವರು ಭೂಮಿಯ ಮೇಲೆ ಗೊಬ್ಬರವಾಗುವರು; ಖಡ್ಗವೂ ಕ್ಷಾಮವೂ ಅವರನ್ನು ನಿರ್ಮೂಲ ಮಾಡುವವು; ಅವರ ಹೆಣಗಳು ಆಕಾಶದ ಪಕ್ಷಿಗಳಿಗೂ ಭೂಮಿಯ ಜಂತುಗಳಿಗೂ ಆಹಾರವಾಗುವವು.",
			"5": "<sup>5</sup> ಯೆಹೋವನು [ನನಗೆ] ಹೀಗೆ ಹೇಳಿದ್ದಾನೆ - ಗೋಳಾಟದ ಮನೆಯಲ್ಲಿ ಸೇರಬೇಡ, ಪ್ರಲಾಪಿಸಲಿಕ್ಕೆ ಹೋಗಬೇಡ, ಅವರಿಗಾಗಿ ಎದೆ ಬಡುಕೊಳ್ಳಲೂಬೇಡ; ನಾನು ದಯಪಾಲಿಸಿದ ಸಮಾಧಾನವನ್ನು, ಅಂದರೆ ನನ್ನ ಪ್ರೀತಿಯನ್ನೂ ಕೃಪೆಯನ್ನೂ ಈ ಜನರಿಂದ ತೆಗೆದುಬಿಟ್ಟಿದ್ದೇನಷ್ಟೆ.",
			"6": "<sup>6</sup> ಈ ದೇಶದಲ್ಲಿನ ದೊಡ್ಡವರೂ ಅಲ್ಪರೂ ಸಾಯುವರು; ಅವರನ್ನು ಯಾರೂ ಹೂಣಿಡರು, ಅವರಿಗಾಗಿ ಯಾರೂ ಗೋಳಾಡರು, ಗಾಯ ಮಾಡಿಕೊಳ್ಳರು, ತಲೆಬೋಳಿಸಿಕೊಳ್ಳರು;",
			"7": "<sup>7</sup> ಸತ್ತವನಿಗೋಸ್ಕರ ದುಃಖಪಡುವವರನ್ನು ಸಂತೈಸುವದಕ್ಕಾಗಿ ಯಾರೂ ಕಜ್ಜಾಯವನ್ನು ಮುರಿದುಕೊಡರು; ತಂದೆಯನ್ನಾಗಲಿ ತಾಯಿಯನ್ನಾಗಲಿ ಕಳಕೊಂಡವರಿಗೆ ಯಾರೂ ಸಮಾಧಾಯಕ ಪಾನಪಾತ್ರೆಯನ್ನು ನೀಡರು.",
			"8": "<sup>8</sup> ಔತಣದ ಮನೆಯಲ್ಲಿಯೂ ಸೇರಬೇಡ, ಅಲ್ಲಿನವರೊಂದಿಗೆ ಕೂತು ತಿಂದು ಕುಡಿಯಬೇಡ.",
			"9": "<sup>9</sup> ಇಸ್ರಾಯೇಲಿನ ದೇವರೂ ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೂ ಆಗಿರುವಾತನು ಹೀಗಂದಿದ್ದಾನೆ - ಇಗೋ, ನಿಮ್ಮ ಕಾಲದಲ್ಲಿ ನಿಮ್ಮ ಕಣ್ಣೆದುರಿಗೆ ಹರ್ಷಸಂಭ್ರಮಗಳ ಧ್ವನಿಯನ್ನೂ ವಧೂವರರ ಸ್ವರವನ್ನೂ ಈ ಸ್ಥಳದೊಳಗೆ ನಿಲ್ಲಿಸಿಬಿಡುವೆನು.",
			"10": "<sup>10</sup> ಈ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ಈ ಜನರಿಗೆ ತಿಳಿಸುತ್ತಿರುವ ನಿನಗೆ ಪ್ರತ್ಯುತ್ತರವಾಗಿ ಅವರು - ಈ ಮಹಾ ವಿಪತ್ತು ನಮಗೆ ಸಂಭವಿಸಬೇಕೆಂದು ಯೆಹೋವನು ಏಕೆ ವಿಧಿಸಿದ್ದಾನೆ? ನಮ್ಮ ಅಪರಾಧವೇನು? ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನಿಗೆ ವಿರುದ್ಧವಾಗಿ ನಾವು ಮಾಡಿದ ಪಾಪವೇನು ಎಂದು ಕೇಳುವಾಗ",
			"11": "<sup>11</sup> ನೀನು ಅವರಿಗೆ ಹೀಗೆ ಹೇಳಬೇಕು - ಯೆಹೋವನ ಮಾತನ್ನು ಕೇಳಿರಿ, ನಿಮ್ಮ ಪಿತೃಗಳು ನನ್ನನ್ನು ತೊರೆದು ಅನ್ಯದೇವತೆಗಳನ್ನು ಹಿಂಬಾಲಿಸಿ ಸೇವಿಸಿ ಪೂಜಿಸಿ ನನ್ನನ್ನು ಬಿಟ್ಟು ನನ್ನ ಧರ್ಮವಿಧಿಗಳನ್ನು ಮೀರಿದ್ದರಿಂದಲೂ",
			"12": "<sup>12</sup> ಇಗೋ, ನೀವೆಲ್ಲರೂ ನಿಮ್ಮ ದುಷ್ಟಹೃದಯದ ಹಟದಂತೆ ನಡೆಯುತ್ತಾ ನನ್ನನ್ನು ಕೇಳದೆ ನಿಮ್ಮ ಪಿತೃಗಳಿಗಿಂತ ಹೆಚ್ಚು ಕೇಡನ್ನು ಮಾಡಿದ್ದರಿಂದಲೂ",
			"13": "<sup>13</sup> ನೀವಾಗಲಿ ನಿಮ್ಮ ಪಿತೃಗಳಾಗಲಿ ನೋಡದ ದೇಶಕ್ಕೆ ನಿಮ್ಮನ್ನು ಈ ದೇಶದೊಳಗಿಂದ ಎಸೆದುಬಿಡುವೆನು; ಅಲ್ಲಿ ಹಗಲಿರುಳೂ ಅನ್ಯದೇವತೆಗಳನ್ನು ಸೇವಿಸುವಿರಿ; ನಾನು ನಿಮಗೆ ದಯೆತೋರಿಸೆನು.",
			"14": "<sup>14</sup> ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ಇಗೋ, ಮುಂದಿನ ಕಾಲದಲ್ಲಿ ಜನರು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಐಗುಪ್ತದೇಶದೊಳಗಿಂದ ಉದ್ಧರಿಸಿದ ಯೆಹೋವನ ಜೀವದಾಣೆ ಎಂಬದಾಗಿ ಪ್ರಮಾಣಮಾಡುವದನ್ನು ಬಿಟ್ಟು",
			"15": "<sup>15</sup> ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಬಡಗಣ ದೇಶದಿಂದಲೂ ತಾನು ಅವರನ್ನು ತಳ್ಳಿಬಿಟ್ಟಿದ್ದ ಸಕಲ ಸೀಮೆಗಳಿಂದಲೂ ಉದ್ಧರಿಸಿದ ಯೆಹೋವನ ಜೀವದಾಣೆ ಎಂದು ಪ್ರಮಾಣಮಾಡುವರು; ನಾನು ಅವರ ಪಿತೃಗಳಿಗೆ ದಯಪಾಲಿಸಿದ ದೇಶಕ್ಕೆ ಅವರನ್ನು ಹಿಂದಿರುಗಿಸುವೆನಷ್ಟೆ.",
			"16": "<sup>16</sup> ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ಇಗೋ, ನನ್ನ ಜನರನ್ನು ಹಿಡಿಯುವದಕ್ಕೆ ಬಹುಮಂದಿ ಬೆಸ್ತರನ್ನು ಕರೆಯಿಸುವೆನು; ಆಮೇಲೆ ಎಲ್ಲಾ ಬೆಟ್ಟಗುಡ್ಡಗಳಿಂದಲೂ ಬಂಡೆಗಳ ಸಂದುಗೊಂದುಗಳಿಂದಲೂ ಅವರನ್ನು ಹೊರಡಿಸಿ ಬೇಟೆಯಾಡುವದಕ್ಕೆ ಬಹುಜನ ಬೇಡರನ್ನು ಕರೆಯಿಸುವೆನು.",
			"17": "<sup>17</sup> ಅವರ ನಡತೆಯನ್ನೆಲ್ಲಾ ದೃಷ್ಟಿಸುತ್ತೇನೆ, ಅದು ನನ್ನ ಮುಖಕ್ಕೆ ಮರೆಯಾಗಿಲ್ಲ; ಅವರ ಅಧರ್ಮವು ನನಗೆ ಗುಟ್ಟಲ್ಲ, ಪ್ರತ್ಯಕ್ಷದಲ್ಲೇ ಇದೆ.",
			"18": "<sup>18</sup> ಅವರ ಅಧರ್ಮಕ್ಕೂ ಪಾಪಕ್ಕೂ ಎರಡರಷ್ಟು ಶಿಕ್ಷೆಯನ್ನು ಮೊದಲು ಕೊಡುವೆನು; ಹೆಣಗಳಂತಿರುವ ತಮ್ಮ ಹೇಯ ವಿಗ್ರಹಗಳಿಂದ ನನ್ನ ದೇಶವನ್ನು ಹೊಲಸು ಮಾಡಿ ನನ್ನ ಸ್ವಾಸ್ತ್ಯವನ್ನು ತಮ್ಮ ಅಸಹ್ಯ ವಸ್ತುಗಳಿಂದ ತುಂಬಿಸಿದ್ದಾರಷ್ಟೆ.",
			"19": "<sup>19</sup> (ಯೆಹೋವನೇ, ನನ್ನ ಬಲವೇ, ಬಲವಾದ ನನ್ನ ದುರ್ಗವೇ, ಇಕ್ಕಟ್ಟಿನ ದಿನದಲ್ಲಿ ನನ್ನ ಆಶ್ರಯವೇ, ಲೋಕದ ಕಟ್ಟಕಡೆಯಿಂದ ಜನಾಂಗಗಳು ನಿನ್ನ ಬಳಿಗೆ ಸೇರಿ - ನಮ್ಮ ಪಿತೃಗಳು ಶುದ್ಧ ಸುಳ್ಳನ್ನು, ಮಾಯವನ್ನು, ಪ್ರಯೋಜನವಿಲ್ಲದವುಗಳನ್ನು ಪಾರಂಪರ್ಯವಾಗಿ ಹೊಂದಿದ್ದಾರೆ ಎಂದು ಅರಿಕೆ ಮಾಡುವರು.",
			"20": "<sup>20</sup> ಮನುಷ್ಯಮಾತ್ರದವನು ದೇವರುಗಳನ್ನು ಕಲ್ಪಿಸಿಕೊಳ್ಳಬಲ್ಲನೇ? ಇವು ದೇವರುಗಳೇ ಅಲ್ಲ.)",
			"21": "<sup>21</sup> ಈ ನಿಮಿತ್ತವಾಗಿ, ಇಗೋ, ಇದೊಂದೇ ಸಲ ನನ್ನ ಭುಜಬಲವನ್ನೂ ಪರಾಕ್ರಮವನ್ನೂ ಅವರಿಗೆ ತಿಳಿಯಪಡಿಸುವೆನು, ಹೌದು, ಗ್ರಹಿಸಮಾಡುವೆನು; ನನ್ನ ನಾಮಧೇಯವು ಯೆಹೋವನೆಂದೇ ಅವರಿಗೆ ಗೊತ್ತಾಗುವದು."
		},
		"17": {
			"1": "<sup>1</sup> ಯೆಹೂದದ ಪಾಪವು ಉಕ್ಕಿನ ಕಂಠದಿಂದಲೂ ವಜ್ರದ ಮೊನೆಯಿಂದಲೂ ಲಿಖಿತವಾಗಿದೆ; ಅವರ ಹೃದಯದ ಹಲಗೆಯಲ್ಲಿಯೂ ಅವರ ಯಜ್ಞವೇದಿಗಳ ಕೊಂಬುಗಳಲ್ಲಿಯೂ ಕೆತ್ತಿದೆ.",
			"2": "<sup>2</sup> ಅವರ ಮಕ್ಕಳು ಸೊಂಪಾಗಿ ಬೆಳೆದಿರುವ ಮರಗಳನ್ನೂ ಎತ್ತರವಾದ ಗುಡ್ಡಗಳನ್ನೂ ಕಂಡಾಗೆಲ್ಲಾ ಅವರ ಯಜ್ಞವೇದಿಗಳನ್ನೂ ಅಶೇರ ವಿಗ್ರಹಸ್ತಂಭಗಳನ್ನೂ ಜ್ಞಾಪಿಸಿಕೊಳ್ಳುವರು.",
			"3": "<sup>3</sup> ಬೈಲಿನ ನನ್ನ ಪರ್ವತದ ಜನರೇ, ನಿಮ್ಮ ಸಕಲ ಪ್ರಾಂತಗಳಲ್ಲಿ ನೀವು ಮಾಡಿದ ಪಾಪದ ನಿಮಿತ್ತ ನಾನು ನಿಮ್ಮ ಎಲ್ಲಾ ಸೊತ್ತು ಸಂಪತ್ತುಗಳನ್ನೂ ಪೂಜಾಸ್ಥಳಗಳನ್ನೂ ಸೂರೆಗೆ ಈಡುಮಾಡುವೆನು.",
			"4": "<sup>4</sup> ನಾನು ನಿಮಗೆ ದಯಪಾಲಿಸಿದ ಸ್ವಾಸ್ತ್ಯವನ್ನು ಸ್ವದೋಷದಿಂದಲೇ ಕಳೆದುಕೊಳ್ಳುವಿರಿ; ನೀವು ನೋಡದ ದೇಶದಲ್ಲಿ ನಿಮ್ಮನ್ನು ನಿಮ್ಮ ಶತ್ರುಗಳಿಗೆ ದಾಸರನ್ನಾಗಿ ಮಾಡುವೆನು; ನೀವು ನನ್ನ ರೋಷಾಗ್ನಿಯನ್ನು ಹತ್ತಿಸಿದ್ದೀರಿ, ಅದು ನಿತ್ಯವೂ ಜ್ವಲಿಸುತ್ತಿರುವದು.",
			"5": "<sup>5</sup> ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ಮಾನವಮಾತ್ರದವರಲ್ಲಿ ಭರವಸವಿಟ್ಟು ನರಜನ್ಮದವರನ್ನು ತನ್ನ ಭುಜಬಲವೆಂದು ತಿಳಿದು ಯೆಹೋವನನ್ನು ತೊರೆಯುವ ಮನಸ್ಸುಳ್ಳವನು ಶಾಪಗ್ರಸ್ತನು.",
			"6": "<sup>6</sup> ಇವನು ಅಡವಿಯಲ್ಲಿನ ಜಾಲಿಗೆ ಸಮಾನನು; ಶುಭಸಂಭವಿಸಿದರೂ ಅದನ್ನು ಕಾಣನು; ಯಾರೂ ವಾಸಿಸದ ಚೌಳು ನೆಲವಾಗಿರುವ ಅರಣ್ಯದ ಬೆಗ್ಗಾಡಿನಲ್ಲಿ ವಾಸಿಸುವ ಸ್ಥಿತಿಯೇ ಅವನದು.",
			"7": "<sup>7</sup> ಯಾವನು ಯೆಹೋವನಲ್ಲಿ ನಂಬಿಕೆಯಿಟ್ಟಿದ್ದಾನೋ ಯಾವನಿಗೆ ಯೆಹೋವನು ಭರವಸವಾಗಿದ್ದಾನೋ ಅವನು ಧನ್ಯನು.",
			"8": "<sup>8</sup> ನೀರಾವರಿಯಲ್ಲಿ ನೆಡಲ್ಪಟ್ಟು ಹೊಳೆಯ ದಡದಲ್ಲಿ ತನ್ನ ಬೇರುಗಳನ್ನು ಹರಡಿ ದಗೆಗೆ ಭಯಪಡದೆ ಹಸುರೆಲೆಯನ್ನು ಬಿಡುತ್ತಾ ಕ್ಷಾಮ ವರ್ಷದಲ್ಲಿಯೂ ನಿಶ್ಚಿಂತೆಯಾಗಿ ಸದಾ ಫಲಕೊಡುತ್ತಾ ಇರುವ ಮರಕ್ಕೆ ಸಮಾನನಾಗಿರುವನು.",
			"9": "<sup>9</sup> ಹೃದಯವು ಎಲ್ಲಕ್ಕಿಂತಲೂ ವಂಚಕ; ಗುಣವಾಗದ ರೋಗಕ್ಕೆ ಒಳಗಾಗಿದೆ; ಅದನ್ನು ಯಾರು ತಿಳಿದಾರು?",
			"10": "<sup>10</sup> ಯೆಹೋವನಾದ ನಾನು ಪ್ರತಿಯೊಬ್ಬನಿಗೂ ಕರ್ಮಫಲವನ್ನು ಅವನವನ ನಡತೆಗೆ ತಕ್ಕ ಹಾಗೆ ಕೊಡಬೇಕೆಂದು ಹೃದಯವನ್ನು ಪರೀಕ್ಷಿಸುವವನೂ ಅಂತರಿಂದ್ರಿಯವನ್ನು ಶೋಧಿಸುವವನೂ ಆಗಿದ್ದೇನೆ.",
			"11": "<sup>11</sup> ಆಸ್ತಿಪಾಸ್ತಿಗಳನ್ನು ಅನ್ಯಾಯವಾಗಿ ಸಂಪಾದಿಸಿಕೊಂಡವನು ತನ್ನದಲ್ಲದ ಮರಿಗಳನ್ನು ಕೂಡಿಸಿಕೊಳ್ಳುವ ಕೌಜುಗ ಹಕ್ಕಿಗೆ ಸಮಾನನು; ಅವನ ಮಧ್ಯಪ್ರಾಯದಲ್ಲಿ ಅವು ಅವನಿಂದ ತೊಲಗಿಹೋಗುವವು; ಅವನು ತನ್ನ ಅಂತ್ಯಕಾಲದಲ್ಲಿ ಹುಚ್ಚನಾಗಿ ಕಂಡುಬರುವನು.",
			"12": "<sup>12</sup> ನಮ್ಮ ಪವಿತ್ರಾಲಯಸ್ಥಾನವು ಆದಿಯಿಂದಲೂ ಉನ್ನತವಾದ ಮಹಿಮೆಯ ಸಿಂಹಾಸನವಾಗಿದೆ.",
			"13": "<sup>13</sup> ಯೆಹೋವನೇ, ಇಸ್ರಾಯೇಲ್ಯರ ನಿರೀಕ್ಷೆಯೇ, ನಿನ್ನನ್ನು ಬಿಟ್ಟುಹೋಗುವವರು ಆಶಾಭಂಗಪಡುವರು; ಯೆಹೋವನಾದ ನಿನ್ನನ್ನು ತೊರೆದವರು ಜೀವಜಲದ ಬುಗ್ಗೆಯನ್ನು ತೊರೆದವರಾಗಿದ್ದಾರೆ; ಅವರ ಹೆಸರು ದೂಳಿನಲ್ಲಿ ಬರೆಯಲ್ಪಡುವದು.",
			"14": "<sup>14</sup> ಯೆಹೋವನೇ, ನನ್ನನ್ನು ಸ್ವಸ್ಥಪಡಿಸು, ಆಗ ಸ್ವಸ್ಥನೇ ಆಗುವೆನು; ನನ್ನನ್ನು ರಕ್ಷಿಸು, ಆಗ ರಕ್ಷಿತನೇ ಆಗುವೆನು; ನೀನೇ ನನಗೆ ಸ್ತುತ್ಯನು.",
			"15": "<sup>15</sup> ಆಹಾ, ಜನರು ನನಗೆ - ಯೆಹೋವನ ಮಾತೆಲ್ಲಿ? ಅದು ಈಗಲೇ ನೆರವೇರಲಿ ಎಂದು ಹೇಳುತ್ತಾರೆ.",
			"16": "<sup>16</sup> ನಾನಾದರೋ ಸಭಾ ಪಾಲನೆಯಲ್ಲಿ ನಿನ್ನನ್ನು ಹಿಂಬಾಲಿಸುವದನ್ನು ಬಿಡುವದಕ್ಕೆ ಆತುರಪಡಲಿಲ್ಲ; ಅನಿವಾರ್ಯ ವಿಪತ್ತಿನ ದಿನವನ್ನು ಅಪೇಕ್ಷಿಸಲಿಲ್ಲ; ನೀನೇ ಬಲ್ಲೆ; ನಿನ್ನ ಸಮಕ್ಷಮದಲ್ಲೇ ನನ್ನ ಬಾಯ ಮಾತು ಹೊರಟಿತು.",
			"17": "<sup>17</sup> ನನಗೆ ಭಯಾಸ್ಪದವಾಗಬೇಡ; ಕೇಡಿನ ಕಾಲದಲ್ಲಿ ನನಗೆ ಆಶ್ರಯವಾಗಿದ್ದೀ.",
			"18": "<sup>18</sup> ನನ್ನ ಹಿಂಸಕರಿಗೆ ಅವಮಾನವಾಗಲಿ, ನನಗಾಗದಿರಲಿ; ಭಯಭ್ರಾಂತಿಯು ಅವರನ್ನು ಹಿಡಿಯಲಿ, ನನ್ನನ್ನು ಬೇಡ; ಅವರಿಗೆ ಕೇಡುಗಾಲವನ್ನು ಬರಮಾಡಿ ವಿಪರೀತಭಂಗದಿಂದ ಅವರನ್ನು ನಾಶಪಡಿಸು.",
			"19": "<sup>19</sup> ಯೆಹೋವನು ನನಗೆ ಇಂತೆಂದನು - ನೀನು ಹೊರಟು ಯೆಹೂದದ ಅರಸರು ಹೋಗಿ ಬರುವ ಸಾಮಾನ್ಯಜನರ ಬಾಗಲಿನಲ್ಲಿಯೂ ಯೆರೂಸಲೇಮಿನ ಎಲ್ಲಾ ಬಾಗಿಲುಗಳಲ್ಲಿಯೂ ನಿಂತುಕೊಂಡು ಅವರಿಗೆ ಹೀಗೆ ಸಾರು -",
			"20": "<sup>20</sup> ಈ ಬಾಗಿಲುಗಳಲ್ಲಿ ಸೇರುವ ಯೆಹೂದದ ಅರಸರೇ, ಎಲ್ಲಾ ಯೆಹೂದ್ಯರೇ, ಯೆರೂಸಲೇಮಿನ ಸಕಲ ನಿವಾಸಿಗಳೇ, ಯೆಹೋವನ ಈ ಮಾತನ್ನು ಕೇಳಿರಿ -",
			"21": "<sup>21</sup> ಮನಮುಟ್ಟಿ ಗಮನಿಸಿರಿ, ಸಬ್ಬತ್ ದಿನದಲ್ಲಿ ಯಾವ ಹೊರೆಯನ್ನೂ ಹೊರಬೇಡಿರಿ, ಯೆರೂಸಲೇಮಿನ ಬಾಗಿಲುಗಳಲ್ಲಿ ತರಲೇಬೇಡಿರಿ;",
			"22": "<sup>22</sup> ಆ ದಿನದಲ್ಲಿ ನಿಮ್ಮ ಮನೆಗಳಿಂದ ಯಾವ ಹೊರೆಯನ್ನೂ ಈಚೆಗೆ ತೆಗೆದುಕೊಂಡು ಬರಬೇಡಿರಿ, ಯಾವ ಕೆಲಸವನ್ನೂ ಮಾಡದಿರಿ; ಸಬ್ಬತ್ ದಿನವನ್ನು ನನ್ನ ದಿನವೆಂದು ಆಚರಿಸಿರಿ; ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ನಾನು ಹೀಗೆ ಆಜ್ಞೆಮಾಡಿದೆನಲ್ಲಾ;",
			"23": "<sup>23</sup> ಅವರಾದರೋ ಕಿವಿಗೊಟ್ಟು ಕೇಳಲಿಲ್ಲ, ಕೇಳಲಿಕ್ಕೂ ಉಪದೇಶ ಹೊಂದಲಿಕ್ಕೂ ಒಪ್ಪಲಿಲ್ಲ.",
			"24": "<sup>24</sup> ಯೆಹೋವನಾದ ನನ್ನ ನುಡಿ ಇದೇ - ನೀವು ನನ್ನ ಕಡೆಗೆ ಚೆನ್ನಾಗಿ ಕಿವಿಗೊಟ್ಟು ಸಬ್ಬತ್ ದಿನದಲ್ಲಿ ಈ ಊರಿನ ಬಾಗಿಲುಗಳೊಳಗೆ ಯಾವ ಹೊರೆಯನ್ನೂ ತಾರದೆ ಯಾವ ಕೆಲಸವನ್ನೂ ಮಾಡದೆ ಆ ದಿನವನ್ನು ನನ್ನ ದಿನವೆಂದು ಆಚರಿಸಿದರೆ",
			"25": "<sup>25</sup> ಆಗ ದಾವೀದನ ಸಿಂಹಾಸನಾರೂಢರಾದ ಅರಸರೂ ಪ್ರಭುಗಳೂ ರಥಾಶ್ವಗಳನ್ನೇರಿದವರಾಗಿ ಈ ಪಟ್ಟಣದ ಬಾಗಿಲುಗಳನ್ನು ಪ್ರವೇಶಿಸುವರು; ಇವರು, ಇವರ ಪ್ರಧಾನರು, ಯೆಹೂದದ ಜನರು, ಯೆರೂಸಲೇಮಿನ ನಿವಾಸಿಗಳು, ಎಲ್ಲರೂ ಇಲ್ಲಿ ಸೇರುವರು; ಈ ಪಟ್ಟಣವು ನಿತ್ಯವೂ ನೆಲೆಯಾಗಿರುವದು.",
			"26": "<sup>26</sup> ಮತ್ತು ಯೆಹೂದದ ಪಟ್ಟಣಗಳು, ಯೆರೂಸಲೇಮಿನ ಸುತ್ತಣ ಪ್ರದೇಶಗಳು, ಬೆನ್ಯಾಮೀನ್ ಸೀಮೆ, ಇಳಕಲಿನ ಪ್ರದೇಶ, ಬೆಟ್ಟದ ಮೇಲಣ ಪ್ರದೇಶ, ದಕ್ಷಿಣಪ್ರಾಂತ, ಈ ಎಲ್ಲಾ ಸ್ಥಳಗಳಿಂದಲೂ ಸರ್ವಾಂಗಹೋಮಪಶು, ಯಜ್ಞಪಶು, ನೈವೇದ್ಯ, ಧೂಪ ಎಂಬೀ ಕೃತಜ್ಞತಾರ್ಪಣಗಳನ್ನು ಯೆಹೋವನ ಆಲಯಕ್ಕೆ ಜನರು ತೆಗೆದುಕೊಂಡು ಬರುವರು.",
			"27": "<sup>27</sup> ಆದರೆ ನೀವು ನನ್ನ ಕಡೆಗೆ ಕಿವಿಗೊಡದೆ ಸಬ್ಬತ್ ದಿನದಲ್ಲಿ ಹೊರೆಯನ್ನು ಹೊತ್ತು ಯೆರೂಸಲೇಮಿನ ಬಾಗಿಲುಗಳೊಳಗೆ ಪ್ರವೇಶಿಸಬಾರದೆಂಬ ನಿಯಮವನ್ನು ಕೈಕೊಳ್ಳದೆ ಆ ದಿನವನ್ನು ನನ್ನ ದಿನವೆಂದು ಆಚರಿಸದೆ ಇದ್ದರೆ ಆಗ ನಾನು ಊರಬಾಗಿಲುಗಳಲ್ಲಿ ಬೆಂಕಿಯನ್ನು ಹೊತ್ತಿಸುವೆನು, ಅದು ಯೆರೂಸಲೇಮಿನ ಉಪ್ಪರಿಗೆಗಳನ್ನು ದಹಿಸಿಬಿಡುವದು, ಆರುವದೇ ಇಲ್ಲ."
		},
		"18": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಯೆರೆಮೀಯನಿಗೆ -",
			"2": "<sup>2</sup> ನೀನೆದ್ದು ಕುಂಬಾರನ ಮನೆಗೆ ಇಳಿದುಹೋಗು; ಅಲ್ಲೇ ನನ್ನ ನುಡಿಯನ್ನು ನಿನಗೆ ಕೇಳಮಾಡುವೆನು ಎಂಬ ಮಾತನ್ನು ದಯಪಾಲಿಸಿದನು.",
			"3": "<sup>3</sup> ಆಗ ನಾನು ಕುಂಬಾರನ ಮನೆಗೆ ಇಳಿದುಹೋದೆನು; ಇಗೋ, ಅವನು ಚಕ್ರದ ಮೇಲೆ ತನ್ನ ಕೆಲಸವನ್ನು ನಡಿಸುತ್ತಿದ್ದನು.",
			"4": "<sup>4</sup> ಆ ಕುಂಬಾರನು ಜೇಡಿಮಣ್ಣಿನಿಂದ ಮಾಡುತ್ತಿದ್ದ ಪಾತ್ರೆಯು ಅವನ ಕೈಯಲ್ಲಿ ಕೆಟ್ಟು ಹೋಗಲು ಪುನಃ ತನಗೆ ಸರಿತೋಚಿದ ಹಾಗೆ ಅದನ್ನು ಹೊಸಪಾತ್ರೆಯನ್ನಾಗಿ ಮಾಡಿದನು.",
			"5": "<sup>5</sup> ಆಗ ಯೆಹೋವನು ನನಗೆ ಈ ಮಾತನ್ನು ಅನುಗ್ರಹಿಸಿದನು -",
			"6": "<sup>6</sup> ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ಇಸ್ರಾಯೇಲ್ ವಂಶದವರೇ, ಈ ಕುಂಬಾರನು ಮಾಡಿದಂತೆ ನಾನು ನಿಮ್ಮನ್ನು ಮಾಡಕೂಡದೋ? ಇಸ್ರಾಯೇಲ್ ಮನೆತನದವರೇ, ಜೇಡಿಮಣ್ಣು ಕುಂಬಾರನ ಕೈಯಲ್ಲಿ ಹೇಗೋ, ಹಾಗೆಯೇ ನೀವು ನನ್ನ ಕೈಯಲ್ಲಿದ್ದೀರಿ.",
			"7": "<sup>7</sup> ನಾನು ಒಂದು ವೇಳೆ ಒಂದು ಜನಾಂಗವನ್ನಾಗಲಿ ರಾಜ್ಯವನ್ನಾಗಲಿ ಕಿತ್ತು ಕೆಡವಿ ನಾಶಪಡಿಸಬೇಕೆಂದು ಅಪ್ಪಣೆಕೊಟ್ಟಾಗ",
			"8": "<sup>8</sup> ನಾನು ದಂಡನೆ ನಿರ್ಣಯಿಸಿದ ಆ ಜನಾಂಗದವರು ತಮ್ಮ ಕೆಟ್ಟತನದಿಂದ ತಿರುಗಿಕೊಂಡರೆ ನಾನು ಮಾಡಬೇಕೆಂದಿದ್ದ ಕೇಡನ್ನು ಮನಮರುಗಿ ಮಾಡದಿರುವೆನು.",
			"9": "<sup>9</sup> ನಾನು ಒಂದು ವೇಳೆ ಒಂದು ಜನಾಂಗವನ್ನಾಗಲಿ ರಾಜ್ಯವನ್ನಾಗಲಿ ನೆಟ್ಟು ಕಟ್ಟಬೇಕೆಂದು ಅಪ್ಪಣೆಕೊಟ್ಟಾಗ",
			"10": "<sup>10</sup> ಅದು ನನ್ನ ಮಾತನ್ನು ಕೇಳದೆ ನನ್ನ ದೃಷ್ಟಿಯಲ್ಲಿ ಕೆಟ್ಟತನವನ್ನು ಮಾಡಿದರೆ ನಾನು ಮನಸ್ಸನ್ನು ಬದಲಾಯಿಸಿಕೊಂಡು ಅದಕ್ಕೆ ಮಾಡಬೇಕೆಂದಿದ್ದ ಮೇಲನ್ನು ಮಾಡದೆ ಇರುವೆನು.",
			"11": "<sup>11</sup> ಈಗ ನೀನು ಯೆಹೂದ್ಯರಿಗೂ ಯೆರೂಸಲೇಮಿನವರಿಗೂ ಈ ಮಾತನ್ನು ಹೇಳು - ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ಆಹಾ, ನಾನು ನಿಮಗೆ ವಿರುದ್ಧವಾಗಿ ಯೋಚಿಸಿ ಕೇಡನ್ನು ಕಲ್ಪಿಸುತ್ತಿದ್ದೇನೆ; ನಿಮ್ಮಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬನೂ ತನ್ನ ತನ್ನ ದುರ್ಮಾರ್ಗದಿಂದ ಹಿಂದಿರುಗಲಿ, ನಡತೆಯನ್ನೂ ಕೃತ್ಯಗಳನ್ನೂ ಸರಿಪಡಿಸಿಕೊಳ್ಳಲಿ.",
			"12": "<sup>12</sup> ಅವರೋ - ಏನೂ ನಿರೀಕ್ಷೆಯಿಲ್ಲ; ನಮ್ಮ ಆಲೋಚನೆಗಳಿಗೆ ತಕ್ಕಂತೆ ನಡೆಯುವೆವು, ನಮ್ಮಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬರೂ ನಮ್ಮ ನಮ್ಮ ದುಷ್ಟ ಹೃದಯದ ಹಾಗೆ ಮಾಡುವೆವು ಎಂದು ಹೇಳುತ್ತಾರೆ.",
			"13": "<sup>13</sup> ಹೀಗಿರಲು ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ಜನಾಂಗಗಳಲ್ಲಿ ವಿಚಾರಿಸಿರಿ, ಇಂಥಾ ಸುದ್ದಿಯನ್ನು ಯಾರು ಕೇಳಿದ್ದಾರೆ? ಇಸ್ರಾಯೇಲೆಂಬ ಯುವತಿಯು ಕೇವಲ ಅಸಹ್ಯ ಕಾರ್ಯವನ್ನು ಮಾಡಿದ್ದಾಳೆ.",
			"14": "<sup>14</sup> ಲೆಬನೋನಿನ ಹಿಮವು ಅರಣ್ಯದ ಶಿಖರದಿಂದ ತಪ್ಪುವದೇ? ದೂರದಿಂದ ಇಳಿದು ಹರಿಯುವ ತೊರೆತದ ನೀರು ಬತ್ತುವದೇ?",
			"15": "<sup>15</sup> ನನ್ನ ಜನರೋ ನನ್ನನ್ನು ಮರೆತು ವ್ಯರ್ಥವಿಗ್ರಹಗಳಿಗೆ ಧೂಪಹಾಕಿದ್ದಾರಲ್ಲಾ; ಅವು ಪುರಾತನ ಪದ್ಧತಿಯಾದ ಅವರ ಮಾರ್ಗಗಳಲ್ಲಿ ಅವರು ಮುಗ್ಗರಿಸುವಂತೆಯೂ ಸರಿಯಲ್ಲದ ಸೀಳುದಾರಿಗಳಲ್ಲಿ ಅಲೆಯುವಂತೆಯೂ ಮಾಡಿವೆ.",
			"16": "<sup>16</sup> ಆದಕಾರಣ ಬೆರಗಿನ ಸಿಳ್ಳಿಗೆ ಅವರ ದೇಶವು ನಿತ್ಯವಾಗಿ ಗುರಿಯಾಗುವದು; ಹಾದುಹೋಗುವವರೆಲ್ಲರೂ ಬೆರಗಾಗಿ ತಲೆದೂಗುವರು.",
			"17": "<sup>17</sup> ಶತ್ರುಗಳು ಮೇಲೆ ಬಿದ್ದಾಗ ನನ್ನ ಜನರನ್ನು ಮೂಡಣ ಗಾಳಿಯಿಂದಲೋ ಎಂಬಂತೆ ದಿಕ್ಕಾಪಾಲುಮಾಡುವೆನು. ಅವರ ವಿಪತ್ತಿನ ದಿನದಲ್ಲಿ ಅವರ ಕಡೆಗೆ ಮುಖತಿರುಗಿಸದೆ ಬೆನ್ನು ಮಾಡುವೆನು.",
			"18": "<sup>18</sup> ಆಗ ಅವರು - ಯೆರೆಮೀಯನಿಗೆ ವಿರುದ್ಧವಾಗಿ ಒಳಸಂಚು ಮಾಡೋಣ ಬನ್ನಿರಿ; ಧರ್ಮೋಪದೇಶವು ಯಾಜಕನಿಂದ, ಮಂತ್ರಾಲೋಚನೆಯು ಜ್ಞಾನಿಯಿಂದ, ದೈವೋಕ್ತಿಯು ಪ್ರವಾದಿಯಿಂದ ಎಂದಿಗೂ ತಪ್ಪದಷ್ಟೆ. ಬನ್ನಿರಿ, ಅವನನ್ನು ಬಾಯಿಂದ ಬಡಿಯೋಣ, ಅವನ ಯಾವ ಮಾತಿಗೂ ಕಿವಿಗೊಡದಿರುವ ಎಂದುಕೊಂಡರು.",
			"19": "<sup>19</sup> ಯೆಹೋವನೇ, ನನ್ನ ಕಡೆಗೆ ಕಿವಿಗೊಡು, ನನ್ನೊಡನೆ ವ್ಯಾಜ್ಯವಾಡುವವರ ಮಾತನ್ನು ಕೇಳು.",
			"20": "<sup>20</sup> ಮೇಲಿಗೆ ಮುಯ್ಯಾಗಿ ಕೇಡು ಆಗ ತಕ್ಕದ್ದೋ? ನನ್ನ ಪ್ರಾಣವನ್ನು ಹಿಡಿಯಬೇಕೆಂದು ಗುಂಡಿಯನ್ನು ತೋಡಿದ್ದಾರಲ್ಲಾ. ಅವರ ಮೇಲಣ ನಿನ್ನ ರೋಷವನ್ನು ತಪ್ಪಿಸಲು ನಾನು ನಿನ್ನ ಮುಂದೆ ನಿಂತುಕೊಂಡು ಅವರ ಹಿತಕ್ಕಾಗಿ ವಿಜ್ಞಾಪಿಸಿಕೊಂಡದ್ದನ್ನು ಚಿತ್ತವಿಸು.",
			"21": "<sup>21</sup> ಆದಕಾರಣ ಅವರ ಮಕ್ಕಳನ್ನು ಕ್ಷಾಮಕ್ಕೆ ಗುರಿಮಾಡಿ ಕತ್ತಿಯ ಬಾಯಿಗೆ ಒಪ್ಪಿಸು; ಅವರ ಹೆಂಡಿರು ಮಕ್ಕಳನ್ನು ನೀಗಿಕೊಂಡು ವಿಧವೆಯರಾಗಲಿ; ಮೃತ್ಯುವು ಅವರ ಗಂಡಂದಿರನ್ನು ಸಂಹರಿಸಲಿ;",
			"22": "<sup>22</sup> ಯುದ್ಧದಲ್ಲಿ ಖಡ್ಗವು ಅವರ ಯುವಕರನ್ನು ವಧಿಸಲಿ. ಸುಲಿಗೆಯವರ ಗುಂಪನ್ನು ಫಕ್ಕನೆ ಅವರ ಮೇಲೆ ಬೀಳಮಾಡು, ಅವರ ಮನೆಗಳೊಳಗಿಂದ ಅರಚಾಟವು ಕೇಳಿಸಲಿ; ಅವರು ನನ್ನನ್ನು ಹಿಡಿಯಲಿಕ್ಕೆ ಗುಂಡಿಯನ್ನು ತೋಡಿ ನನ್ನ ಕಾಲುಗಳಿಗೆ ಪಾಶಗಳನ್ನು ಗುಪ್ತವಾಗಿ ಒಡ್ಡಿದ್ದಾರೆ.",
			"23": "<sup>23</sup> ಯೆಹೋವನೇ, ನನ್ನನ್ನು ಕೊಲ್ಲಬೇಕೆಂದು ಅವರು ಮಾಡಿಕೊಂಡಿರುವ ಆಲೋಚನೆಯನ್ನೆಲ್ಲಾ ನೀನೇ ಬಲ್ಲೆ; ಅವರ ಅಪರಾಧವನ್ನು ಕ್ಷಮಿಸಬೇಡ, ಅವರ ಪಾಪವನ್ನು ಅಳಿಸದಿರು, ಅದು ನಿನ್ನ ಕಣ್ಣೆದುರಿಗಿರಲಿ; ಅವರು ನಿನ್ನ ಮುಂದೆ ಕೆಡವಲ್ಪಡಲಿ; ನಿನ್ನ ಕೋಪವನ್ನು ವ್ಯಕ್ತಪಡಿಸುವಾಗ ಅವರಿಗೆ ತಕ್ಕದ್ದನ್ನು ಮಾಡು."
		},
		"19": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ನನಗೆ ಹೀಗೆ ಹೇಳಿದನು - ಹೊರಡು, ಕುಂಬಾರನು ಮಾಡಿದ ಮಣ್ಣಿನ ಕೂಜವನ್ನು ಕೊಂಡುಕೊಂಡು ಜನರ ಹಿರಿಯರಲ್ಲಿಯೂ ಯಾಜಕರ ಹಿರಿಯರಲ್ಲಿಯೂ ಕೆಲವರನ್ನು ಕರೆದುಕೊಂಡು",
			"2": "<sup>2</sup> ಬೋಕಿಯ ಬಾಗಿಲಿನ ಸಮೀಪದಲ್ಲಿರುವ ಬೆನ್‍ಹಿನ್ನೋಮ್ ತಗ್ಗಿಗೆ ಹೋಗಿ ನಾನು ನಿನಗೆ ತಿಳಿಸುವ ಮಾತುಗಳನ್ನು ಸಾರು -",
			"3": "<sup>3</sup> ಯೆಹೂದದ ಅರಸರೇ, ಯೆರೂಸಲೇಮಿನ ನಿವಾಸಿಗಳೇ, ಯೆಹೋವನ ಮಾತನ್ನು ಕೇಳಿರಿ! ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರೂ ಸೇನಾಧೀಶ್ವರನೂ ಆದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಆಹಾ, ನಾನು ಈ ಸ್ಥಳದ ಮೇಲೆ ಕೇಡನ್ನು ಬರಮಾಡುವೆನು; ಅದರ ಸುದ್ದಿಯನ್ನು ಕೇಳುವವರ ಎರಡು ಕಿವಿಗಳೂ ಮೊರ್ರೆನ್ನುವವು.",
			"4": "<sup>4</sup> ಈ ಜನರು ನನ್ನನ್ನು ತೊರೆದು ತಮಗಾಗಲಿ ತಮ್ಮ ಪಿತೃಗಳಿಗಾಗಲಿ ಯೆಹೂದದ ಅರಸರಿಗಾಗಲಿ ತಿಳಿಯದ ಅನ್ಯದೇವತೆಗಳಿಗೆ ಧೂಪಹಾಕುತ್ತಾ ಈ ಸ್ಥಳವನ್ನು ಅನ್ಯಸ್ಥಾನವಾಗಿ ಮಾಡಿ",
			"5": "<sup>5</sup> ಅದನ್ನು ನಿರ್ದೋಷಿಗಳ ರಕ್ತದಿಂದ ತುಂಬಿಸಿ ತಮ್ಮ ಮಕ್ಕಳನ್ನು ಬಾಳ್ ದೇವತೆಗೆ ಆಹುತಿಕೊಡುವದಕ್ಕೆ ಬಾಳನ ಪೂಜಾಸ್ಥಳಗಳನ್ನು ಏರ್ಪಡಿಸಿಕೊಂಡಿದ್ದಾರಲ್ಲಾ; ನಾನು ಇಂಥಾ ಆಚಾರವನ್ನು ವಿಧಿಸಲಿಲ್ಲ. ಅದರ ಮಾತನ್ನೇ ಆಡಲಿಲ್ಲ, ಅದು ನನ್ನ ಮನಸ್ಸಿನಲ್ಲಿ ಹುಟ್ಟಲೂ ಇಲ್ಲ;",
			"6": "<sup>6</sup> ಆಹಾ, ಈ ಕಾರಣದಿಂದ ಈ ಸ್ಥಳಕ್ಕೆ ತೋಫೆತ್ ಮತ್ತು ಬೆನ್‍ಹಿನ್ನೋಮಿನ ತಗ್ಗು ಎಂಬ ಹೆಸರುಗಳು ಹೋಗಿ ಕೊಲೆಯ ತಗ್ಗು ಎಂಬ ಹೆಸರಾಗುವ ಕಾಲ ಬರುವದು;",
			"7": "<sup>7</sup> ನಾನು ಈ ಸ್ಥಳದಲ್ಲಿ ಯೆಹೂದ್ಯರ ಮತ್ತು ಯೆರೂಸಲೇಮಿನವರ ಆಲೋಚನೆಯನ್ನು ಚೆಲ್ಲಿ ಮಣ್ಣುಪಾಲುಮಾಡಿ ಅವರ ಪ್ರಾಣವನ್ನು ಹುಡುಕುವ ಶತ್ರುಗಳ ಖಡ್ಗದಿಂದ ಅವರನ್ನು ಬೀಳಿಸಿ ಆ ಹೆಣಗಳನ್ನು ಆಕಾಶಪಕ್ಷಿಗಳಿಗೂ ಭೂಜಂತುಗಳಿಗೂ ಆಹಾರವನ್ನಾಗಿ ಮಾಡುವೆನು.",
			"8": "<sup>8</sup> ಈ ಪಟ್ಟಣವನ್ನು ಬೆರಗಿನ ಸಿಳ್ಳಿಗೆ ಗುರಿಮಾಡುವೆನು; ಹಾದುಹೋಗುವವರೆಲ್ಲರೂ ಅದಕ್ಕೆ ಸಂಬಂಧಿಸಿದ ವಿಪತ್ತುಗಳನ್ನು ಕಂಡು ಬೆರಗಾಗಿ ಸಿಳ್ಳುಹಾಕುವರು.",
			"9": "<sup>9</sup> ಅವರ ಪ್ರಾಣವನ್ನು ಹುಡುಕುವ ಶತ್ರುಗಳು ಅವರನ್ನು ಮುತ್ತಿ ಬಹಳವಾಗಿ ಇರಕಿಸುವಾಗ ಅವರು ತಮ್ಮ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳ ಮಾಂಸವನ್ನೂ ಇಷ್ಟ ಮಿತ್ರರ ಮಾಂಸವನ್ನೂ ತಿನ್ನುವಂತೆ ಮಾಡುವೆನು. ಇದು ಯೆಹೋವನಾದ ನನ್ನ ನುಡಿ.",
			"10": "<sup>10</sup> ಆಗ ನೀನು ನಿನ್ನ ಸಂಗಡ ಬಂದವರ ಕಣ್ಣೆದುರಿಗೆ ಆ ಕೂಜವನ್ನು ಒಡೆದುಬಿಟ್ಟು ಅವರಿಗೆ ಹೀಗೆ ಹೇಳು -",
			"11": "<sup>11</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಒಡೆದು ಸರಿಪಡಿಸಲಾಗದ ಮಣ್ಣಿನ ಪಾತ್ರೆಯಂತೆ ನಾನು ಈ ಜನವನ್ನೂ ಪಟ್ಟಣವನ್ನೂ ಒಡೆದುಬಿಡುವೆನು; ಇನ್ನೆಲ್ಲಿಯೂ ಸ್ಥಳವಿಲ್ಲವಾಗಿ ಶವಗಳನ್ನು ತೋಫೆತಿನಲ್ಲಿ ಹೂಣುವರು.",
			"12": "<sup>12</sup> ನಾನು ಈ ಸ್ಥಳವನ್ನೂ ಇದರ ನಿವಾಸಿಗಳನ್ನೂ ಹೀಗೆ ನಾಶಪಡಿಸಿ ಈ ಪಟ್ಟಣವನ್ನು ತೋಫೆತಿನ ಸ್ಥಿತಿಗೆ ತರುವೆನು;",
			"13": "<sup>13</sup> ಯೆರೂಸಲೇಮಿನ ಮನೆಗಳೂ ಯೆಹೂದದ ಅರಸರ ಮನೆಗಳೂ ಅಂದರೆ ಯಾವ ಯಾವ ಮನೆಗಳ ಮಾಳಿಗೆಗಳ ಮೇಲೆ ಆಕಾಶದ ನಕ್ಷತ್ರಗಣಕ್ಕೆಲ್ಲ ಧೂಪಹಾಕಿ ಅನ್ಯದೇವತೆಗಳಿಗೆ ಪಾನನೈವೇದ್ಯಗಳನ್ನು ಸುರಿದಿದ್ದಾರೋ ಆ ಮನೆಗಳೆಲ್ಲಾ ಹೊಲಸಾಗಿ ತೋಫೆತೆಂಬ ಸ್ಥಳಕ್ಕೆ ಸಮಾನವಾಗುವವು.",
			"14": "<sup>14</sup> ಯೆರೆಮೀಯನು ಯೆಹೋವನ ಅಪ್ಪಣೆಯಂತೆ ತೋಫೆತಿನಲ್ಲಿ ಪ್ರವಾದಿಸಿದ ಮೇಲೆ ಅಲ್ಲಿಂದ ಬಂದು ಯೆಹೋವನ ಆಲಯದ ಪ್ರಾಕಾರದಲ್ಲಿ ನಿಂತುಕೊಂಡು -",
			"15": "<sup>15</sup> ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರೂ ಸೇನಾಧೀಶ್ವರನೂ ಆದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಆಹಾ, ಈ ಪಟ್ಟಣದವರೂ ಇದರ ಸುತ್ತಣ ಎಲ್ಲಾ ಊರುಗಳವರೂ ನನ್ನ ಮಾತುಗಳನ್ನು ಕೇಳಲೊಲ್ಲದೆ ತಮ್ಮ ಮನಸ್ಸನ್ನು ಕಠಿಣಮಾಡಿಕೊಂಡ ಕಾರಣ ನಾನು ಈ ಪಟ್ಟಣಕ್ಕೆ ಕೊಟ್ಟ ಶಾಪದ ಕೇಡನ್ನೆಲ್ಲಾ ಇವರಿಗೆ ಬರಮಾಡುವೆನು ಎಂದು ಸಮಸ್ತ ಜನರಿಗೆ ಸಾರಿದನು."
		},
		"20": {
			"1": "<sup>1</sup> ಯೆರೆಮೀಯನು ಈ ಪ್ರವಾದನೆ ಮಾಡುವದನ್ನು ಯಾಜಕ ಇಮ್ಮೇರನ ಮಗನೂ ಯೆಹೋವನ ಆಲಯದ ಮುಖ್ಯಾಧಿಕಾರಿಯೂ ಆದ ಪಷ್ಹೂರನು ಕೇಳಿ",
			"2": "<sup>2</sup> ಪ್ರವಾದಿಯಾದ ಯೆರೆಮೀಯನನ್ನು ಹೊಡೆಯಿಸಿ ಯೆಹೋವನ ಆಲಯಕ್ಕೆ ಸೇರಿದ ಮೇಲಣ ಬೆನ್ಯಾಮೀನ್ ಬಾಗಿಲಲ್ಲಿದ್ದ ಕೋಳಕ್ಕೆ ಹಾಕಿಸಿದನು.",
			"3": "<sup>3</sup> ಮರುದಿನ ಪಷ್ಹೂರನು ಯೆರೆಮೀಯನನ್ನು ಕೋಳದಿಂದ ಬಿಡಿಸಿದನು. ಆಗ ಯೆರೆಮೀಯನು ಅವನಿಗೆ ಹೀಗೆ ಹೇಳಿದನು - ಯೆಹೋವನು ನಿನ್ನ ಹೆಸರನ್ನು ಪಷ್ಹೂರ್ ಎಂದು ಹೇಳದೆ ಮಾಗೋರ್ ಮಿಸ್ಸಾಬೀಬ್ ಎಂದು ಹೇಳಿದ್ದಾನೆ.",
			"4": "<sup>4</sup> ಏಕಂದರೆ ಯೆಹೋವನು - ಇಗೋ, ನಾನು ನಿನ್ನನ್ನು ನಿನಗೂ ನಿನ್ನ ಎಲ್ಲಾ ಸ್ನೇಹಿತರಿಗೂ ದಿಗಿಲಿಗೆ ಆಸ್ಪದನನ್ನಾಗಿ ಮಾಡುವೆನು; ಅವರು ತಮ್ಮ ಶತ್ರುಗಳ ಖಡ್ಗದಿಂದ ಬೀಳುವರು, ಅದನ್ನು ಕಣ್ಣಾರೆ ಕಾಣುವಿ; ನಾನು ಯೆಹೂದ್ಯರನ್ನೆಲ್ಲಾ ಬಾಬೆಲಿನ ಅರಸನ ಕೈಗೆ ಒಪ್ಪಿಸುವೆನು; ಅವನು ಅವರನ್ನು ಬಾಬೆಲಿಗೆ ಸೆರೆಯಾಗಿ ಒಯ್ದು ಕತ್ತಿಯಿಂದ ಕಡಿಯುವನು.",
			"5": "<sup>5</sup> ಮತ್ತು ಈ ಪಟ್ಟಣದ ಎಲ್ಲಾ ಆಸ್ತಿಯನ್ನೂ ಆದಾಯವನ್ನೂ ಸಂಪತ್ತನ್ನೂ ಯೆಹೂದದ ಅರಸರ ಸಕಲ ನಿಧಿನಿಕ್ಷೇಪಗಳನ್ನೂ ಅವರ ಶತ್ರುಗಳ ಕೈವಶಮಾಡುವೆನು; ಅವರು ಅವುಗಳನ್ನು ಕೊಳ್ಳೆಹೊಡೆದು ಬಾಬೆಲಿಗೆ ತೆಗೆದುಕೊಂಡು ಹೋಗುವರು.",
			"6": "<sup>6</sup> ಪಷ್ಹೂರನೇ, ನೀನೋ ನಿನ್ನ ಮನೆಯವರೆಲ್ಲರೊಡನೆ ಸೆರೆಗೆ ಹೋಗುವಿ; ನೀನೂ ನಿನ್ನ ಮಿಥ್ಯಾ ಪ್ರವಾದನೆಯನ್ನು ಕೇಳಿದ ನಿನ್ನ ಸಕಲ ಸ್ನೇಹಿತರೂ ಬಾಬೆಲಿಗೆ ಸೇರಿ ಅಲ್ಲೇ ಸತ್ತು ಮಣ್ಣಾಗುವಿರಿ ಎಂದು ನುಡಿದಿದ್ದಾನೆ.",
			"7": "<sup>7</sup> ಯೆಹೋವನೇ, ನೀನು ನನ್ನನ್ನು ಮರುಳುಗೊಳಿಸಿದಿ. ನಾನು ಮರುಳಾದೆನು; ನೀನು ನನಗಿಂತ ಬಲಿಷ್ಟನಾಗಿ ಗೆದ್ದುಕೊಂಡಿ; ನಾನು ಹಗಲೆಲ್ಲಾ ಗೇಲಿಗೆ ಗುರಿಯಾಗಿದ್ದೇನೆ, ಎಲ್ಲರೂ ನನ್ನನ್ನು ಅಣಕಿಸುತ್ತಾರೆ.",
			"8": "<sup>8</sup> ನಾನು ಮಾತಾಡುವದೆಲ್ಲಾ ಅರಚಾಟವೇ; ಬಲಾತ್ಕಾರ, ಕೊಳ್ಳೆ ಎಂದೇ ಕೂಗಿಕೊಳ್ಳುತ್ತೇನೆ; [ನಾನು ಸಾರುವ] ಯೆಹೋವನ ವಾಕ್ಯವು ನನ್ನನ್ನು ಜನರ ದೂಷಣೆಗೂ ಪರಿಹಾಸಕ್ಕೂ ಗುರಿಮಾಡಿದೆ.",
			"9": "<sup>9</sup> ನಾನು ಯೆಹೋವನ ವಿಷಯವನ್ನು ಪ್ರಕಟಿಸೆನು, ಆತನ ಹೆಸರಿನಲ್ಲಿ ಇನ್ನು ಮಾತಾಡೆನು ಎಂದುಕೊಂಡರೆ ಉರಿಯುವ ಬೆಂಕಿಯು ನನ್ನ ಎಲುಬುಗಳಲ್ಲಿ ಅಡಕವಾಗಿದೆಯೋ ಎಂಬಂತೆ ನನ್ನ ಹೃದಯದಲ್ಲಿ ಸಂಕಟವಾಗುತ್ತದೆ; ತಡೆದು ತಡೆದು ಆಯಾಸಗೊಂಡಿದ್ದೇನೆ, ಸಹಿಸಲಾರೆ.",
			"10": "<sup>10</sup> ಇವನ ಮೇಲೆ ದೂರು ಹೇಳಿರಿ, ನಾವೂ ಹೇಳುವೆವು ಎಂದು ಬಹುಜನರು ಗುಸುಗುಟ್ಟುವದನ್ನು ಕೇಳಿದ್ದೇನೆ, ಸುತ್ತುಮುತ್ತಲೂ ದಿಗಿಲು; ನನ್ನ ಆಪ್ತಸ್ನೇಹಿತರೆಲ್ಲರೂ ಇವನು ಎಡವಿಬೀಳಲಿ ಎಂದು ನನ್ನನ್ನು ಹೊಂಚಿನೋಡುತ್ತಾ - ಒಂದು ವೇಳೆ ಸಿಕ್ಕಿಬಿದ್ದಾನು, ಇವನನ್ನು ಗೆದ್ದು ಮುಯ್ಯಿತೀರಿಸಿಕೊಳ್ಳುವೆವು ಎಂದು ತಮ್ಮತಮ್ಮೊಳಗೆ ಅಂದುಕೊಳ್ಳುತ್ತಿದ್ದಾರೆ.",
			"11": "<sup>11</sup> ಯೆಹೋವನಾದರೋ ಭಯಂಕರಶೂರನಾಗಿ ನನ್ನ ಸಂಗಡ ಇದ್ದಾನೆ. ಆದದರಿಂದ ನನ್ನ ಹಿಂಸಕರು ಗೆಲ್ಲದೆ ಮುಗ್ಗರಿಸುವರು; ತಮ್ಮ ಇಷ್ಟಾರ್ಥವು ನೆರವೇರದ ಕಾರಣ ದೊಡ್ಡ ನಾಚಿಕೆಗೆ ಈಡಾಗುವರು, ಎಂದಿಗೂ ಮರೆಯದ ಶಾಶ್ವತಾವಮಾನಕ್ಕೆ ಒಳಗಾಗುವರು.",
			"12": "<sup>12</sup> ಹೃದಯವನ್ನೂ ಅಂತರಿಂದ್ರಿಯವನ್ನೂ ಪರೀಕ್ಷಿಸಿ ಶಿಷ್ಟರನ್ನು ಶೋಧಿಸುವ ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೇ, ನೀನು ನನ್ನ ಹಿಂಸಕರಿಗೆ ಕೊಡುವ ಪ್ರತಿಫಲವನ್ನು ನನ್ನ ಕಣ್ಣು ನೋಡಲಿ; ನಿನಗೇ ನನ್ನ ವ್ಯಾಜ್ಯವನ್ನು ಅರಿಕೆ ಮಾಡಿದ್ದೇನಷ್ಟೆ.",
			"13": "<sup>13</sup> ಯೆಹೋವನನ್ನು ಕೀರ್ತಿಸಿರಿ, ಯೆಹೋವನನ್ನು ಸ್ತುತಿಸಿರಿ; ಆತನು ಕೆಡುಕರ ಕೈಯಿಂದ ದೀನನ ಪ್ರಾಣವನ್ನು ರಕ್ಷಿಸಿದ್ದಾನೆ.",
			"14": "<sup>14</sup> ನಾನು ಹುಟ್ಟಿದ ದಿವಸವು ಶಾಪಗ್ರಸ್ತವಾಗಲಿ, ತಾಯಿಯು ನನ್ನನ್ನು ಹೆತ್ತ ದಿನವು ಶುಭವೆನಿಸಿಕೊಳ್ಳದಿರಲಿ!",
			"15": "<sup>15</sup> ನಿನಗೆ ಗಂಡುಮಗು ಹುಟ್ಟಿದೆ ಎಂಬ ಸಮಾಚಾರವನ್ನು ನನ್ನ ತಂದೆಗೆ ತಿಳಿಸಿ ಅವನಿಗೆ ಕೇವಲ ಆನಂದವನ್ನುಂಟುಮಾಡಿದವನು ಶಪಿಸಲ್ಪಡಲಿ!",
			"16": "<sup>16</sup> ಯೆಹೋವನು ಕನಿಕರಪಡದೆ ಕೆಡವಿಬಿಟ್ಟ ಪಟ್ಟಣಗಳ ಗತಿಯು ಅವನಿಗೆ ಬರಲಿ; ಪ್ರಾತಃಕಾಲದಲ್ಲಿ ಅರಚಾಟವು, ಮಧ್ಯಾಹ್ನದಲ್ಲಿ ಕೂಗಾಟವು ಅವನ ಕಿವಿಗೆ ಬೀಳಲಿ!",
			"17": "<sup>17</sup> ಅವನು ನನ್ನನ್ನು ಗರ್ಭದಲ್ಲೇ ಕೊಲ್ಲಲಿಲ್ಲವಲ್ಲಾ; ಹೀಗೆ ಮಾಡಿದ್ದರೆ ನನ್ನ ತಾಯಿಯೇ ನನಗೆ ಗೋರಿಯಾಗುತ್ತಿದ್ದಳು, ಆಕೆಯ ಗರ್ಭವು ಯಾವಾಗಲೂ ಬಸುರಾಗಿಯೇ ಇರುತ್ತಿತ್ತು.",
			"18": "<sup>18</sup> ನಾನು ಶ್ರಮದುಃಖಗಳನ್ನು ನೋಡುವದಕ್ಕೂ ನನ್ನ ಆಯುಸ್ಸು ಅವಮಾನದಿಂದ ಕ್ಷಯಿಸುವದಕ್ಕೂ ಗರ್ಭದಿಂದ ಏಕೆ ಹೊರಟುಬಂದೆನು?"
		},
		"21": {
			"1": "<sup>1</sup> ಅರಸನಾದ ಚಿದ್ಕೀಯನು ಮಲ್ಕೀಯನ ಮಗನಾದ ಪಷ್ಹೂರನನ್ನೂ ಯಾಜಕ ಮಾಸೇಯನ ಮಗನಾದ ಚೆಫನ್ಯನನ್ನೂ ಯೆರೆಮೀಯನ ಬಳಿಗೆ ಕಳುಹಿಸಿ -",
			"2": "<sup>2</sup> ದಯಮಾಡಿ ಯೆಹೋವನ ಚಿತ್ತವೇನೆಂದು ಆತನನ್ನು ನಮಗೋಸ್ಕರ ವಿಚಾರಿಸು; ಬಾಬೆಲಿನ ಅರಸನಾದ ನೆಬೂಕದ್ನೆಚ್ಚರನು ನಮಗೆ ವಿರುದ್ಧವಾಗಿ ಯುದ್ಧಮಾಡುತ್ತಿದ್ದಾನಲ್ಲಾ; ಒಂದುವೇಳೆ ಯೆಹೋವನು ತನ್ನ ಸಮಸ್ತ ಅದ್ಭುತಕಾರ್ಯಗಳಿಗೆ ಅನುಗುಣವಾಗಿ ನಮ್ಮ ಪಕ್ಷವನ್ನು ವಹಿಸಿ ಈ ಶತ್ರುವು ನಮ್ಮ ಕಡೆಯಿಂದ ತೊಲಗುವಂತೆ ಮಾಡಿಯಾನು ಎಂದು ಅವರ ಮೂಲಕ ವಿಜ್ಞಾಪಿಸಿದಾಗ ಯೆಹೋವನಿಂದ ಯೆರೆಮೀಯನಿಗೆ ದೈವೋಕ್ತಿಯು ದೊರೆಯಿತು.",
			"3": "<sup>3</sup> ಆಗ ಯೆರೆಮೀಯನು ಈ ಉತ್ತರವನ್ನು ಚಿದ್ಕೀಯನಿಗೆ ತಿಳಿಸಿರಿ ಎಂದು ಅವರಿಗೆ ಹೇಳಿದನು -",
			"4": "<sup>4</sup> ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರಾದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಇಗೋ, ನಿಮ್ಮನ್ನು ಮುತ್ತುವ ಕಸ್ದೀಯರಿಗೂ ಬಾಬೆಲಿನ ಅರಸನಿಗೂ ವಿರುದ್ಧವಾಗಿ ಪೌಳಿಗೋಡೆಯ ಹೊರಗೆ ಯುದ್ಧಮಾಡುವದಕ್ಕೆ ನೀವು ಹಿಡಿದಿರುವ ಆಯುಧಗಳನ್ನು ನಾನು ಹಿಂದಕ್ಕೆ ತಳ್ಳಿ ಈ ಪಟ್ಟಣದೊಳಗೆ ಕೂಡಿಹಾಕುವೆನು.",
			"5": "<sup>5</sup> ನಾನೇ ಕೋಪರೋಷ ಮಹಾಕ್ರೋಧಭರಿತನಾಗಿ ಶಿಕ್ಷಾಹಸ್ತದಿಂದಲೂ ಭುಜಪರಾಕ್ರಮದಿಂದಲೂ ನಿಮಗೆ ವಿರುದ್ಧವಾಗಿ ಯುದ್ಧಮಾಡುವೆನು.",
			"6": "<sup>6</sup> ಈ ಪಟ್ಟಣದಲ್ಲಿ ವಾಸಿಸುವ ಮನುಷ್ಯರನ್ನೂ ಪಶುಗಳನ್ನೂ ಬಾಧಿಸುವೆನು; ಘೋರವ್ಯಾಧಿಯು ಸಾಯಿಸುವದು.",
			"7": "<sup>7</sup> ತರುವಾಯ ನಾನು ಯೆಹೂದದ ಅರಸನಾದ ಚಿದ್ಕೀಯನನ್ನೂ ಅವನ ಸೇವಕರನ್ನೂ ವ್ಯಾಧಿಖಡ್ಗಕ್ಷಾಮಗಳಿಂದ ಹತಶೇಷರಾದ ಪ್ರಜೆಗಳನ್ನೂ ಬಾಬೆಲಿನ ಅರಸನಾದ ನೆಬೂಕದ್ನೆಚ್ಚರನ ಕೈಗೂ ಅವರ ಪ್ರಾಣವನ್ನು ಹುಡುಕುತ್ತಿರುವ ಅವರ ಶತ್ರುಗಳ ಕೈಗೂ ಕೊಟ್ಟುಬಿಡುವೆನು; ಅವನು ಅವರನ್ನು ಕರುಣಿಸದೆ ಕನಿಕರಪಡದೆ ಉಳಿಸದೆ ಕತ್ತಿಯಿಂದ ಸಂಹರಿಸುವನು.",
			"8": "<sup>8</sup> ಮತ್ತು ಈ ಜನರಿಗೆ ಹೀಗೆ ಹೇಳು ಎಂದು ಯೆಹೋವನು ನನಗೆ ಅಪ್ಪಣೆಕೊಟ್ಟಿದ್ದಾನೆ. ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಇಗೋ, ನಾನು ಜೀವಿಸುವ ಮಾರ್ಗವನ್ನೂ ಸಾಯುವ ಮಾರ್ಗವನ್ನೂ ನಿಮ್ಮ ಮುಂದೆ ಇಟ್ಟಿದ್ದೇನೆ.",
			"9": "<sup>9</sup> ಪಟ್ಟಣದಲ್ಲಿ ನಿಲ್ಲುವವನು ಖಡ್ಗಕ್ಷಾಮವ್ಯಾಧಿಗಳಿಂದ ಸಾಯುವನು; ನಿಮ್ಮನ್ನು ಮುತ್ತುವ ಕಸ್ದೀಯರನ್ನು ಮರೆಹೊಗಲು ಪಟ್ಟಣವನ್ನು ಬಿಡುವವನು ಬದುಕುವನು, ತನ್ನ ಪ್ರಾಣವೊಂದನ್ನೇ ಬಾಚಿಕೊಂಡು ಹೋಗುವನು.",
			"10": "<sup>10</sup> ಮೇಲಿಗಲ್ಲ ಕೇಡಿಗಾಗಿಯೇ ಈ ಪಟ್ಟಣದ ಮೇಲೆ ದೃಷ್ಟಿಯಿಟ್ಟಿದ್ದೇನೆ; ಇದು ಬಾಬೆಲಿನ ಅರಸನ ಕೈವಶವಾಗುವದು, ಅವನು ಸುಟ್ಟು ಬಿಡುವನು; ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"11": "<sup>11</sup> ಯೆಹೂದದ ಅರಸನ ಮನೆತನದ ವಿಷಯವಾಗಿ ಯೆಹೋವನು ಹೇಳಿರುವ ಈ ಮಾತನ್ನು ಕೇಳಿರಿ -",
			"12": "<sup>12</sup> ದಾವೀದನ ಮನೆತನದವರೇ, ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ನಿಮ್ಮ ದುಷ್ಕೃತ್ಯಗಳ ನಿಮಿತ್ತ ನನ್ನ ರೋಷವು ಜ್ವಾಲೆಯಂತೆ ಹೊರಟು ಯಾರೂ ಆರಿಸಲಾಗದಷ್ಟು ರಭಸವಾಗಿ ದಹಿಸಬಾರದಾಗಿದ್ದರೆ ಮುಂಜಾನೆ ನ್ಯಾಯತೀರಿಸಿರಿ, ಸುಲಿಗೆಯಾದವನನ್ನು ದೋಚಿಕೊಂಡವನ ಕೈಯಿಂದ ಬಿಡಿಸಿರಿ.",
			"13": "<sup>13</sup> ಆಹಾ, ತಗ್ಗಿನ ನಗರಿಯೇ, ಪ್ರಸ್ಥಭೂಮಿಯ ಶಿಖರದ ಮೇಲಣ ಪುರಿಯೇ, ನಾನು ನಿನಗೆ ವಿರುದ್ಧವಾಗಿದ್ದೇನೆ; ಯಾರು ಇಳಿದು ನಮ್ಮ ಮೇಲೆ ಬಂದಾರು, ನಮ್ಮ ನಿವಾಸಗಳಲ್ಲಿ ಯಾರು ನುಗ್ಗಿಯಾರು ಎನ್ನುವವರೇ,",
			"14": "<sup>14</sup> ನಿಮ್ಮ ದುಷ್ಕೃತ್ಯಗಳಿಗೆ ಪ್ರತಿಫಲವಾಗಿ ನಿಮ್ಮನ್ನು ದಂಡಿಸುವೆನು; ನಿಮ್ಮ ಪುರಿಯೆಂಬ ವನಕ್ಕೆ ಬೆಂಕಿಹಚ್ಚುವೆನು, ಅದು ಸುತ್ತುಮುತ್ತಣದನ್ನೆಲ್ಲಾ ನುಂಗಿಬಿಡುವದು."
		},
		"22": {
			"1": "<sup>1</sup> ಯೆಹೋವನು [ನನಗೆ] ಹೀಗೆ ಅಪ್ಪಣೆಕೊಟ್ಟನು - ನೀನು ಯೆಹೂದದ ಅರಸನ ಮನೆಗೆ ಇಳಿದು ಹೋಗಿ ಈ ಮಾತನ್ನು ಅಲ್ಲಿ ಹೇಳು -",
			"2": "<sup>2</sup> ದಾವೀದನ ಸಿಂಹಾಸನಾರೂಢನಾದ ಯೆಹೂದದ ಅರಸನೇ, ಯೆಹೋವನ ಮಾತನ್ನು ಕೇಳು; ನಿನ್ನ ಸೇವಕರೂ ಈ ಬಾಗಿಲುಗಳಲ್ಲಿ ಸೇರುವ ನಿನ್ನ ಪ್ರಜೆಗಳೂ ಕೇಳಲಿ.",
			"3": "<sup>3</sup> ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ನೀತಿನ್ಯಾಯಗಳನ್ನು ಆಚರಿಸಿರಿ, ಸುಲಿಗೆಯಾದವನನ್ನು ದೋಚಿಕೊಂಡವನ ಕೈಯಿಂದ ಬಿಡಿಸಿರಿ; ವಿದೇಶಿ, ಅನಾಥ, ವಿಧವೆ, ಇವರಿಗೆ ಯಾವ ಅನ್ಯಾಯವನ್ನೂ ಹಿಂಸೆಯನ್ನೂ ಮಾಡಬೇಡಿರಿ; ನಿರ್ದೋಷಿಯ ರಕ್ತವನ್ನು ಈ ಸ್ಥಳದಲ್ಲಿ ಸುರಿಸದಿರಿ.",
			"4": "<sup>4</sup> ನೀವು ಈ ಆಜ್ಞೆಗಳನ್ನು ಮನಃಪೂರ್ವಕವಾಗಿ ಕೈಕೊಂಡರೆ ದಾವೀದನ ಸಿಂಹಾಸನಾರೂಢರಾದ ಅರಸರು ರಥಾಶ್ವಗಳನ್ನೇರಿದವರಾಗಿ ತಮ್ಮ ಸೇವಕರೊಡನೆಯೂ ಪ್ರಜೆಗಳೊಡನೆಯೂ ಈ ಅರಮನೆಯ ಬಾಗಿಲುಗಳಲ್ಲಿ ಪ್ರವೇಶಿಸುವರು.",
			"5": "<sup>5</sup> ನೀವು ಈ ಮಾತುಗಳನ್ನು ಕೇಳದಿದ್ದರೆ ಈ ಅರಮನೆಯು ಹಾಳುಬೀಳುವದು ಎಂದು ನನ್ನ ಮೇಲೆ ಆಣೆಯಿಡುತ್ತೇನೆ. ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"6": "<sup>6</sup> ಯೆಹೋವನು ಯೆಹೂದದ ಅರಸನ ಮನೆತನವನ್ನು ಕುರಿತು ಹೀಗನ್ನುತ್ತಾನೆ - ನೀನು ನನ್ನ ದೃಷ್ಟಿಯಲ್ಲಿ ಗಿಲ್ಯಾದಿನಂತೆಯೂ ಲೆಬನೋನಿನ ಶಿಖರದ ಹಾಗೂ ಇದ್ದೀ, ಆದರೂ ನಾನು ನಿನ್ನನ್ನು ಮರುಭೂಮಿಯನ್ನಾಗಿಯೂ ನಿರ್ಜನ ಪಟ್ಟಣವನ್ನಾಗಿಯೂ ಮಾಡುವದು ಖಂಡಿತ.",
			"7": "<sup>7</sup> ಆಯುಧ ಸನ್ನದ್ಧರಾದ ಸಂಹಾರಕರನ್ನು ನಿನಗೆ ವಿರುದ್ಧವಾಗಿ ಏರ್ಪಡಿಸುವೆನು;",
			"8": "<sup>8</sup> ಅವರು ನಿನ್ನ ಶ್ರೇಷ್ಠ ದೇವದಾರುಗಳನ್ನು ಕಡಿದು ಬೆಂಕಿಯಲ್ಲಿ ಹಾಕುವರು. ಅನೇಕ ಜನಾಂಗಗಳವರು ಈ ಪಟ್ಟಣದ ಮಾರ್ಗವಾಗಿ ಹೋಗುತ್ತಾ - ಯೆಹೋವನು ಈ ಮಹಾ ಪಟ್ಟಣಕ್ಕೆ ಹೀಗೆ ಏಕೆ ಮಾಡಿದ ಎಂದು ಒಬ್ಬರಿಗೊಬ್ಬರು ಮಾತಾಡಿಕೊಳ್ಳುತ್ತಾ -",
			"9": "<sup>9</sup> ಈ ಪಟ್ಟಣದವರು ತಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಒಡಂಬಡಿಕೆಯನ್ನು ನಿರಾಕರಿಸಿ ಅನ್ಯದೇವತೆಗಳನ್ನು ಪೂಜಿಸಿ ಸೇವಿಸಿದ್ದರಿಂದಲೇ ಇದಕ್ಕೆ ಈ ಗತಿಯಾಯಿತು ಅಂದುಕೊಳ್ಳುವರು.",
			"10": "<sup>10</sup> ಸತ್ತವನಿಗಾಗಿ ಅಳಬೇಡಿರಿ, ಬಡುಕೊಳ್ಳಬೇಡಿರಿ; ಸೆರೆಯಾದವನಿಗೇ ಬಿಕ್ಕಿಬಿಕ್ಕಿ ಅಳಿರಿ; ಅವನು ಇನ್ನು ಹಿಂದಿರುಗನು, ಜನ್ಮಭೂಮಿಯನ್ನು ನೋಡನು.",
			"11": "<sup>11</sup> ಯೆಹೂದದ ಅರಸನಾದ ಯೋಷೀಯನ ಮಗನೂ ತನ್ನ ತಂದೆಯಾದ ಯೋಷೀಯನ ತರುವಾಯ ಪಟ್ಟಕ್ಕೆ ಬಂದವನೂ ಇಲ್ಲಿಂದ ಸೆರೆಯಾಗಿ ಹೋದವನೂ ಆದ ಶಲ್ಲೂಮನ ವಿಷಯವಾಗಿ ಯೆಹೋವನು ಹೇಳುವದೇನಂದರೆ - ಅವನು ಇಲ್ಲಿಗೆ ಇನ್ನು ಹಿಂದಿರುಗಿ ಬಾರನು;",
			"12": "<sup>12</sup> ಅವನನ್ನು ಎಲ್ಲಿಗೆ ಸೆರೆಯೊಯ್ದರೋ ಅಲ್ಲೇ ಸಾಯುವನು, ಈ ದೇಶವನ್ನು ಇನ್ನು ಕಾಣನು ಎಂಬದೇ.",
			"13": "<sup>13</sup> ತನ್ನ ಅರಮನೆಯನ್ನು ದುರ್ನೀತಿಯಿಂದಲೂ ಮಹಡಿಗಳನ್ನು ಅನ್ಯಾಯದಿಂದಲೂ ಕಟ್ಟಿಸಿಕೊಳ್ಳುವವನ ಗತಿಯನ್ನು ಏನು ಹೇಳಲಿ! ಕೂಲಿಕೊಡದೆ ನೆರೆಯವನಿಂದ ಬಿಟ್ಟಿಕೆಲಸವನ್ನು ಮಾಡಿಸಿ -",
			"14": "<sup>14</sup> ಆಹಾ, ನಾನು ವಿಸ್ತಾರವಾದ ಅರಮನೆಯನ್ನೂ ವಿಶಾಲವಾದ ಮಹಡಿಗಳನ್ನೂ ಕಟ್ಟಿಸಿಕೊಳ್ಳುವೆನು ಎಂದು ಹೇಳಿ ಅಗಲಗಲವಾದ ಕಿಟಕಿಗಳನ್ನಿಡಿಸಿ [ಒಳಗೆ ಗೋಡೆಗೆಲ್ಲಾ] ದೇವದಾರಿನ ಹಲಿಗೆಗಳನ್ನು ಹೊದಿಸಿ ಕಿರಿಮಂಜಿಯ ಬಣ್ಣವನ್ನು ಬಳಿಸಿಕೊಳ್ಳುವವನ ಪಾಡನ್ನು ಏನು ಹೇಳಲಿ!",
			"15": "<sup>15</sup> ನೀನು ದೇವದಾರುವಿನ ಕೆಲಸದಲ್ಲಿ ಸ್ಪರ್ಧೆಯುಳ್ಳವನಾಗಿರುವದರಿಂದಲೇ ಆಳತಕ್ಕವನಾಗಿದ್ದೀಯೋ? ನಿನ್ನ ತಂದೆಯು ಎಷ್ಟೇ ಉಂಡು ಕುಡಿದರೂ ನೀತಿನ್ಯಾಯಗಳನ್ನಂತು ಕೈಕೊಳ್ಳುತ್ತಿದ್ದನು;",
			"16": "<sup>16</sup> ಆಗ ಅವನಿಗೆ ನೆಮ್ಮದಿಯಾಗಿತ್ತು. ದೀನದರಿದ್ರರ ವ್ಯಾಜ್ಯವನ್ನು ತೀರಿಸುತ್ತಿದ್ದನು; ಆಗ ಸುಖವಾಗಿತ್ತು. ನನ್ನನ್ನು ಅರಿಯುವದೆಂದರೆ ಇದೇ ಎಂಬದು ಯೆಹೋವನ ನುಡಿ.",
			"17": "<sup>17</sup> ಆದರೆ ನಿರ್ದೋಷಿಗಳ ರಕ್ತವನ್ನು ಸುರಿಸುವದು, ದೋಚಿಕೊಳ್ಳುವದು, ಹಿಂಸಿಸುವದು, ಜಜ್ಜುವದು, ಇವುಗಳನ್ನು ಮಾಡುವದರಲ್ಲಿಯೇ ನಿನ್ನ ದೃಷ್ಟಿಯೂ ನಿನ್ನ ಮನಸ್ಸೂ ನೆಲೆಗೊಂಡಿವೆ.",
			"18": "<sup>18</sup> ಆದಕಾರಣ ಯೋಷೀಯನ ಮಗನೂ ಯೆಹೂದದ ಅರಸನೂ ಆದ ಯೆಹೋಯಾಕೀಮನ ವಿಷಯವಾಗಿ ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ಅಯ್ಯೋ, ಅಣ್ಣಾ, ಅಯ್ಯೋ, ಅಕ್ಕಾ ಎಂದು ಗೋಳಾಡುವಂತೆ ಇವನಿಗಾಗಿ ಯಾರೂ ಗೋಳಾಡುವದಿಲ್ಲ; ಅಯ್ಯೋ, ನಮ್ಮೊಡೆಯಾ, ಅಕಟಾ, ದೊರೆಯ ಮಹಿಮೆಯೇ ಎಂದು ಇವನಿಗಾಗಿ ಯಾರೂ ಪ್ರಲಾಪಿಸರು.",
			"19": "<sup>19</sup> ಇವನನ್ನು ಯೆರೂಸಲೇಮಿನ ಬಾಗಿಲುಗಳ ಹೊರಗೆ ಕತ್ತೆಯಂತೆ ಎಳೆದು ಬಿಸಾಟು ಮಣ್ಣುಪಾಲುಮಾಡುವರು.",
			"20": "<sup>20</sup> [ಚೀಯೋನ್ ಯುವತಿಯೇ,] ಲೆಬನೋನ್ ಪರ್ವತವನ್ನು ಹತ್ತಿ ಕೂಗಿಕೋ! ಬಾಷಾನಿನಲ್ಲಿ ಮೊರೆಯಿಡು, ಅಬಾರೀಮಿನಲ್ಲಿ ಅರಚಿಕೋ! ನಿನ್ನ ಮಿಂಡರೆಲ್ಲಾ ಹಾಳಾಗಿ ಹೋದರಲ್ಲಾ.",
			"21": "<sup>21</sup> ನಾನು ನಿನ್ನ ನೆಮ್ಮದಿಯಲ್ಲಿ ನಿನ್ನೊಡನೆ ಮಾತಾಡಿದಾಗ ಕೇಳಲೊಲ್ಲೆನು ಎಂದು ನೀನು ಹೇಳಿದಿ. ನನ್ನ ಮಾತನ್ನು ಕೇಳದಿರುವದು ನಿನಗೆ ಬಾಲ್ಯದಿಂದಲೇ ಅಭ್ಯಾಸ.",
			"22": "<sup>22</sup> ನಿನ್ನನ್ನು ಅಟ್ಟಿಕೊಂಡು ಹೋಗುವ ಕುರುಬರನ್ನು ಗಾಳಿಯು ಅಟ್ಟಿಬಿಡುವದು; ನಿನ್ನ ಮಿಂಡರು ಸೆರೆಹೋಗುವರು; ಆಗ ನಿನ್ನ ಸಕಲ ದುಷ್ಕೃತ್ಯಗಳ ನಿಮಿತ್ತ ನೀನು ಆಶಾಭಂಗಪಟ್ಟು ಅವಮಾನಕ್ಕೀಡಾಗುವದು ಖಂಡಿತ.",
			"23": "<sup>23</sup> ಲೆಬನೋನಿನಲ್ಲಿ ವಾಸಿಸುವವಳೇ, ದೇವದಾರುಗಳಲ್ಲಿ ಗೂಡುಮಾಡಿಕೊಂಡಿರುವವಳೇ, ಪ್ರಸವವೇದನೆಯಂತಿರುವ ಸಂಕಟವು ನಿನಗೆ ಸಂಭವಿಸುವಾಗ ನಿನ್ನ ಸ್ಥಿತಿಯು ಎಷ್ಟೋ ದುಃಖಕರವಾಗಿರುವದು!",
			"24": "<sup>24</sup> ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ನನ್ನ ಜೀವದಾಣೆ, ಯೆಹೋಯಾಕೀಮನ ಮಗನೂ ಯೆಹೂದದ ಅರಸನೂ ಆದ ಕೊನ್ಯನೆಂಬ ನೀನು ನನ್ನ ಬಲಗೈಯ ಮುದ್ರೆಯುಂಗುರವಾಗಿದ್ದರೂ ನಾನು ನಿನ್ನನ್ನು ಅಲ್ಲಿಂದ ಕಿತ್ತು ಹಾಕುತ್ತಿದ್ದೆನು!",
			"25": "<sup>25</sup> ನಿನ್ನ ಪ್ರಾಣವನ್ನು ಹುಡುಕಿ ನಿನ್ನಲ್ಲಿ ಭಯವನ್ನು ಹುಟ್ಟಿಸುವವರ ಕೈಗೆ ಅಂದರೆ ಬಾಬೆಲಿನ ಅರಸನಾದ ನೆಬೂಕದ್ನೆಚ್ಚರನ ಮತ್ತು ಕಸ್ದೀಯರ ಕೈಗೆ ಕೊಟ್ಟು ಬಿಡುವೆನು.",
			"26": "<sup>26</sup> ನಿನ್ನನ್ನೂ ನಿನ್ನ ಹೆತ್ತ ತಾಯಿಯನ್ನೂ ಜನ್ಮಭೂಮಿಯಲ್ಲದ ಅನ್ಯದೇಶಕ್ಕೆ ಎಸೆಡುಬಿಡುವೆನು;",
			"27": "<sup>27</sup> ಅಲ್ಲೇ ಸಾಯುವಿರಿ, ಹಿಂದಿರುಗಬೇಕೆಂದು ಹಂಬಲಿಸುವ ದೇಶಕ್ಕೆ ಅವರು ಹಿಂದಿರುಗುವದೇ ಇಲ್ಲ.",
			"28": "<sup>28</sup> ಕೊನ್ಯನೆಂಬ ಈ ಮನುಷ್ಯನು ಅಸಡ್ಡೆಗೀಡಾದ ಒಡಕು ಗಡಿಗೆಯೋ? ಯಾರಿಗೂ ಇಷ್ಟವಲ್ಲದ ಮಡಿಕೆಯೋ? ಅವನೂ ಅವನ ಸಂತತಿಯವರೂ ಏಕೆ ಬಿಸಾಡಲ್ಪಟ್ಟಿದ್ದಾರೆ? ನೋಡದ ದೇಶಕ್ಕೆ ಏಕೆ ಎಸೆಯಲ್ಪಟ್ಟಿದ್ದಾರೆ?",
			"29": "<sup>29</sup> ದೇಶವೇ, ದೇಶವೇ, ದೇಶವೇ, ಯೆಹೋವನ ಮಾತನ್ನು ಕೇಳು!",
			"30": "<sup>30</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಇವನನ್ನು ಮಕ್ಕಳಿಲ್ಲದವನು, ವ್ಯರ್ಥಜನ್ಮದವನು ಎಂದು ಪಟ್ಟಿಯಲ್ಲಿ ಬರೆಯಿರಿ; ಇವನ ಸಂತಾನದಲ್ಲಿ ಇನ್ನು ಮೇಲೆ ಯಾವನೂ ದಾವೀದನ ಸಿಂಹಾಸನದಲ್ಲಿ ಕೂತು ಯೆಹೂದವನ್ನು ಆಳಿ ಬಾಳನು."
		},
		"23": {
			"1": "<sup>1</sup> ನನ್ನ ಕಾವಲಿನ ಕುರಿಗಳನ್ನು ಚದರಿಸಿ ಹಾಳುಮಾಡುವ ಕುರುಬರ ಗತಿಯನ್ನು ಏನೆಂದು ಹೇಳಲಿ ಎಂಬದು ಯೆಹೋವನ ನುಡಿ.",
			"2": "<sup>2</sup> ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರಾದ ಯೆಹೋವನು ತನ್ನ ಜನರೆಂಬ ಕುರಿಗಳನ್ನು ಮೇಯಿಸುವ ಕುರುಬರ ದ್ರೋಹವನ್ನು ಕಂಡು ಅವರನ್ನು ಕುರಿತು ಇಂತೆನ್ನುತ್ತಾನೆ - ನೀವು ನನ್ನ ಮಂದೆಯನ್ನು ಚದರಿಸಿ ಅಟ್ಟಿಬಿಟ್ಟಿದ್ದೀರಿ, ವಿಚಾರಿಸಲೇ ಇಲ್ಲ; ಆಹಾ, ನಿಮ್ಮ ದುಷ್ಕೃತ್ಯಗಳ ನಿಮಿತ್ತ ನಾನು ನಿಮ್ಮನ್ನು ವಿಚಾರಿಸುವೆನು.",
			"3": "<sup>3</sup> ನಾನು ನನ್ನ ಮಂದೆಯನ್ನು ಯಾವ ದೇಶಗಳಿಗೆ ಅಟ್ಟಿಬಿಟ್ಟೆನೋ ಆ ಸಕಲ ದೇಶಗಳಿಂದ ಉಳಿದ ಕುರಿಗಳನ್ನು ಕೂಡಿಸಿ ತಮ್ಮ ತಮ್ಮ ಹಟ್ಟಿಗಳಿಗೆ ತಿರಿಗಿ ಬರಮಾಡುವೆನು; ಅವು ದೊಡ್ಡ ಪೀಳಿಗೆಯಾಗಿ ಹೆಚ್ಚುವವು.",
			"4": "<sup>4</sup> ನಾನು ಅವುಗಳ ಮೇಲೆ ಕುರುಬರನ್ನು ನೇಮಿಸುವೆನು, ಅವರು ಅವುಗಳನ್ನು ಮೇಯಿಸುವರು; ಅವು ಇನ್ನು ಭಯಪಡವು, ಬೆದರವು, ಅವುಗಳಲ್ಲಿ ಒಂದೂ ಕಡಿಮೆಯಾಗದು; ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"5": "<sup>5</sup> ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ಇಗೋ, ನಾನು ಮುಂದಿನ ಕಾಲದಲ್ಲಿ ದಾವೀದನೆಂಬ ಮೂಲದಿಂದ ಸದ್ಧರ್ಮಿಯಾದ ಮೊಳಿಕೆಯನ್ನು ಚಿಗುರಿಸುವೆನು; ಅವನು ರಾಜನಾಗಿ ಆಳುತ್ತಾ ವಿವೇಕದಿಂದ ಕಾರ್ಯವನ್ನು ಸಾಧಿಸುತ್ತಾ ದೇಶದಲ್ಲಿ ನೀತಿನ್ಯಾಯಗಳನ್ನು ನಿರ್ವಹಿಸುವನು.",
			"6": "<sup>6</sup> ಅವನ ಕಾಲದಲ್ಲಿ ಯೆಹೂದ್ಯರು ಸುರಕ್ಷಿತರಾಗಿರುವರು, ಇಸ್ರಾಯೇಲ್ಯರು ನೆಮ್ಮದಿಯಾಗಿ ವಾಸಿಸುವರು; ಯೆಹೋವ ಚಿದ್ಕೇನು [ಅಂದರೆ ಯೆಹೋವನೇ ನಮ್ಮ ಸದ್ಧರ್ಮ] ಎಂಬ ಹೆಸರು ಅವನಿಗಾಗುವದು.",
			"7": "<sup>7</sup> ಆಹಾ, ಮುಂದಿನ ಕಾಲದಲ್ಲಿ ಜನರು - ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಐಗುಪ್ತ ದೇಶದೊಳಗಿಂದ ಉದ್ಧರಿಸಿದ ಯೆಹೋವನ ಜೀವದಾಣೆ ಎಂಬದಾಗಿ ಪ್ರಮಾಣಮಾಡುವದನ್ನು ಬಿಟ್ಟು",
			"8": "<sup>8</sup> ಇಸ್ರಾಯೇಲ್ ಮನೆತನವೆಂಬ ಸಂತಾನವನ್ನು ಬಡಗಣ ದೇಶದಿಂದಲೂ ತಾನು ಅವರನ್ನು ತಳ್ಳಿಬಿಟ್ಟಿದ್ದ ಸಕಲ ಸೀಮೆಗಳಿಂದಲೂ ಬರಮಾಡಿ ಉದ್ಧರಿಸಿದ ಯೆಹೋವನ ಜೀವದಾಣೆ ಎಂದು ಪ್ರಮಾಣಮಾಡುವರು; ಅವರು ಸ್ವದೇಶದಲ್ಲಿ ಸುಖವಾಸಿಗಳಾಗುವರು. ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"9": "<sup>9</sup> ಪ್ರವಾದಿಗಳ ವಿಷಯ. ನನ್ನ ಹೃದಯವು ನನ್ನೊಳಗೆ ಒಡೆದುಹೋಗಿದೆ; ನನ್ನ ಎಲುಬುಗಳೆಲ್ಲಾ ಕರಗಿಹೋಗಿವೆ. ನಾನು ಯೆಹೋವನಿಗೂ ಆತನ ಪರಿಶುದ್ಧವಾಕ್ಯಗಳಿಗೂ ವಶನಾಗಿ ಅಮಲೇರಿದವನಂತಿದ್ದೇನೆ. ಹೌದು, ದ್ರಾಕ್ಷಾರಸಕ್ಕೆ ಸೋತವನ ಹಾಗಿದ್ದೇನೆ.",
			"10": "<sup>10</sup> ಸೀಮೆಯು ಸೂಳೆಗಾರರಿಂದ ತುಂಬಿದೆ, ದೈವಶಾಪದ ನಿಮಿತ್ತ ದೇಶವು ದುಃಖಿಸುತ್ತದೆ; ಅಡವಿಯ ಹುಲ್ಗಾವಲು ಬಾಡಿದೆ; ದೇಶದವರು ತ್ವರೆಪಡುವ ಮಾರ್ಗವು ದುರ್ಮಾರ್ಗ, ಅವರ ಶೌರ್ಯವು ಅನ್ಯಾಯಸಾಧಕ.",
			"11": "<sup>11</sup> ಪ್ರವಾದಿಗಳು ಭ್ರಷ್ಟರು, ಯಾಜಕರೂ ಭ್ರಷ್ಟರು! ಹೌದು ನನ್ನ ಆಲಯದಲ್ಲಿಯೇ ಅವರ ಕೆಟ್ಟತನವನ್ನು ಕಂಡಿದ್ದೇನೆ ಎಂದು ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ.",
			"12": "<sup>12</sup> ಆದಕಾರಣ ಅವರ ಮಾರ್ಗವು ಕತ್ತಲಲ್ಲಿ ಜಾರಿಬೀಳುವ ಇಳುಮೇಡುಗಳಿಗೆ ಸಮಾನವಾಗುವದು; ಅವರು ಅಟ್ಟಲ್ಪಟ್ಟು ಅಂಧಕಾರದಲ್ಲಿ ಬಿದ್ದುಹೋಗುವರು; ನಾನು ಅವರ ಮೇಲೆ ಕೇಡನ್ನು, ದಂಡನೆಯ ವರುಷದ ಕೇಡನ್ನು ಬರಮಾಡುವೆನು. ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"13": "<sup>13</sup> ನಾನು ಸಮಾರ್ಯದ ಪ್ರವಾದಿಗಳಲ್ಲಿ ಅಯುಕ್ತವನ್ನು ನೋಡಿದ್ದೇನೆ; ಅವರು ಬಾಳ್ ದೇವತೆಯ ಆವೇಶದಿಂದ ಪ್ರವಾದಿಸಿ ಇಸ್ರಾಯೇಲೆಂಬ ನನ್ನ ಜನರನ್ನು ಮಾರ್ಗತಪ್ಪಿಸಿದ್ದಾರೆ.",
			"14": "<sup>14</sup> ಯೆರೂಸಲೇಮಿನ ಪ್ರವಾದಿಗಳಲ್ಲಿಯೂ ನಾನು ಅಸಹ್ಯವನ್ನು ನೋಡಿದ್ದೇನೆ; ಅವರು ವ್ಯಭಿಚಾರಮಾಡಿ ಮೋಸದಲ್ಲಿ ನಡೆದು ದುಷ್ಟರಲ್ಲಿ ಯಾವನೂ ತನ್ನ ದುಷ್ಟತನವನ್ನು ಬಿಡದಂತೆ ಅವರ ಕೈಗಳನ್ನು ಬಲಪಡಿಸುತ್ತಿದ್ದಾರೆ; ಅವರೆಲ್ಲರು ಸೊದೋಮಿನಂತೆ, ಆ ಪುರನಿವಾಸಿಗಳು ಗೊಮೋರದ ಹಾಗೆ ನನಗೆ ಕಾಣುತ್ತಾರೆ.",
			"15": "<sup>15</sup> ಆದಕಾರಣ ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಪ್ರವಾದಿಗಳ ವಿಷಯದಲ್ಲಿ ಹೀಗನ್ನುತ್ತಾನೆ - ಆಹಾ, ನಾನು ಇವರಿಗೆ ಕಹಿಯಾದ ಆಹಾರಪಾನಗಳನ್ನು ಕೊಡುವೆನು; ಯೆರೂಸಲೇಮಿನ ಪ್ರವಾದಿಗಳಿಂದ ಭ್ರಷ್ಟತನವು ದೇಶದಲ್ಲೆಲ್ಲಾ ಹರಡಿದೆಯಷ್ಟೆ.",
			"16": "<sup>16</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ನಿಮಗೆ ಪ್ರವಾದಿಸುವ ಪ್ರವಾದಿಗಳ ಮಾತುಗಳನ್ನು ಕೇಳಬೇಡಿರಿ; ನಿಮ್ಮಲ್ಲಿ ವ್ಯರ್ಥನಿರೀಕ್ಷೆಯನ್ನು ಹುಟ್ಟಿಸುತ್ತಾರೆ; ಯೆಹೋವನ ಬಾಯಿಂದ ಹೊರಟದ್ದನ್ನು ನುಡಿಯದೆ ಸ್ವಂತ ಹೃದಯಕ್ಕೆ ಹೊಳೆದದ್ದನ್ನೇ ಹೇಳುತ್ತಾರೆ.",
			"17": "<sup>17</sup> ನನ್ನನ್ನು ಅಸಡ್ಡೆಮಾಡುವವರಿಗೆ - ನಿಮಗೆ ಶುಭವಾಗುವದು ಎಂಬದಾಗಿ ಯೆಹೋವನು ಅಂದಿದ್ದಾನೆ ಎಂದು ಹೇಳುತ್ತಲೇ ಇದ್ದಾರೆ; ಸ್ವಂತ ಹೃದಯದ ಹಟದಂತೆ ನಡೆಯುವವರೆಲ್ಲರಿಗೂ - ನಿಮಗೆ ಯಾವ ಕೇಡೂ ಸಂಭವಿಸದು ಎಂದು ನುಡಿಯುತ್ತಾರೆ.",
			"18": "<sup>18</sup> [ಅವರಲ್ಲಿ] ಯಾರು ಯೆಹೋವನ ಆಲೋಚನಾಸಭೆಯಲ್ಲಿ ನಿಂತು ಆತನ ಮಾತನ್ನು ಕೇಳಿ ಕಂಡಿದ್ದಾರೆ? ಯಾರು ಆತನ ನುಡಿಗೆ ಕಿವಿಗೊಟ್ಟು ಗಮನಿಸಿದ್ದಾರೆ?",
			"19": "<sup>19</sup> ಆಹಾ, ಯೆಹೋವನ ರೋಷವೆಂಬ ಬಿರುಗಾಳಿಯು, ಸುಂಟರಗಾಳಿಯು, ಹೊರಟಿದೆ; ಅದು ದುಷ್ಟರ ತಲೆಯ ಮೇಲೆ ಹೊಡೆಯುವದು.",
			"20": "<sup>20</sup> ಯೆಹೋವನು ತನ್ನ ಹೃದಯಾಲೋಚನೆಗಳನ್ನು ನಡಿಸಿ ನೆರವೇರಿಸುವ ತನಕ ಆತನ ರೋಷವು ಹಿಂದಿರುಗದು; ಕಟ್ಟಕಡೆಯ ದಿನಗಳಲ್ಲಿ ನೀವು ಇದನ್ನು ಸ್ಪಷ್ಟವಾಗಿ ಗ್ರಹಿಸುವಿರಿ.",
			"21": "<sup>21</sup> ನಾನು ಈ ಪ್ರವಾದಿಗಳನ್ನು ಕಳುಹಿಸಲಿಲ್ಲ, ತಾವೇ ಆತುರಗೊಂಡರು; ನಾನು ಇವರಿಗೆ ಏನೂ ಹೇಳಲಿಲ್ಲ, ತಾವೇ ಪ್ರವಾದಿಸಿದರು.",
			"22": "<sup>22</sup> ಇವರು ನನ್ನ ಆಲೋಚನಾಸಭೆಯಲ್ಲಿ ನಿಂತಿದ್ದರೆ ನನ್ನ ಮಾತುಗಳನ್ನು ನನ್ನ ಜನರ ಕಿವಿಗೆ ಮುಟ್ಟಿಸಿ ಅವರನ್ನು ಅವರ ದುರ್ಮಾರ್ಗದಿಂದಲೂ ದುಷ್ಕೃತ್ಯಗಳಿಂದಲೂ ತಪ್ಪಿಸುತ್ತಿದ್ದರು.",
			"23": "<sup>23</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ನಾನು ಸಮೀಪದಲ್ಲಿ ಮಾತ್ರ ಇರುವ ದೇವರೋ? ದೂರದಲ್ಲಿಯೂ ಇರುವವನಲ್ಲವೋ?",
			"24": "<sup>24</sup> ನನ್ನ ಕಣ್ಣಿಗೆ ಬೀಳದಂತೆ ಯಾವನಾದರೂ ಗುಪ್ತಸ್ಥಳಗಳಲ್ಲಿ ತನ್ನನ್ನು ಮರೆಮಾಡಿಕೊಂಡಾನೇ? ನಾನು ಭೂಮ್ಯಾಕಾಶಗಳಲ್ಲಿಯೂ ವ್ಯಾಪಿಸಿರುವವನಲ್ಲವೆ.",
			"25": "<sup>25</sup> ನನಗೆ ಕನಸು ಬಿತ್ತು, ಕನಸು ಬಿತ್ತು ಎಂದು ನನ್ನ ಹೆಸರಿನಿಂದ ಸುಳ್ಳಾಗಿ ಪ್ರವಾದಿಸುವ ಪ್ರವಾದಿಗಳ ನುಡಿಯನ್ನು ಕೇಳಿದ್ದೇನೆ.",
			"26": "<sup>26</sup> ಇವರ ಪಿತೃಗಳು ಬಾಳನನ್ನು ಸೇರಿ ನನ್ನ ಹೆಸರನ್ನು ಮರೆತ ಪ್ರಕಾರ ಈ ಪ್ರವಾದಿಗಳು ಸ್ವಹೃದಯಕಲ್ಪಿತ ಮೋಸವನ್ನು ಪ್ರಕಟಿಸುತ್ತಾ ತಾವು ತಮ್ಮ ನೆರೆಹೊರೆಯವರಿಗೆ ತಿಳಿಸುವ ತಮ್ಮ ಕನಸುಗಳ ಮೂಲಕ ನನ್ನ ಜನರು ನನ್ನ ಹೆಸರನ್ನು ಮರೆಯುವಂತೆ ಮಾಡಬೇಕೆಂದು ಆಲೋಚಿಸಿಕೊಂಡಿದ್ದಾರಲ್ಲಾ.",
			"27": "<sup>27</sup> ಇಂಥಾ ಯೋಚನೆಯು ಸುಳ್ಳಾಗಿ ಪ್ರವಾದಿಸುವ ಈ ಪ್ರವಾದಿಗಳ ಹೃದಯದಲ್ಲಿ ಎಂದಿನವರೆಗೆ ಇರುವದು? ಕನಸುಕಂಡ ಪ್ರವಾದಿಯು ಕನಸನ್ನು ತಿಳಿಸಿದರೆ ತಿಳಿಸಲಿ;",
			"28": "<sup>28</sup> ನನ್ನ ಮಾತನ್ನು ಕೇಳಿದವನೋ ನನ್ನ ಮಾತನ್ನು ಯಥಾರ್ಥವಾಗಿ ನುಡಿಯಲಿ.",
			"29": "<sup>29</sup> ಹೊಟ್ಟೆಲ್ಲಿ? ಕಾಳೆಲ್ಲಿ? ನನ್ನ ವಾಕ್ಯವು ಬೆಂಕಿಗೂ ಬಂಡೆಯನ್ನು ಒಡೆದುಬಿಡುವ ಚಮಟಿಗೆಗೂ ಸಮಾನವಾಗಿದೆಯಲ್ಲಾ. ಯೆಹೋವನೇ ಇದನ್ನು ನುಡಿದಿದ್ದಾನೆ.",
			"30": "<sup>30</sup> ಆಹಾ, ನನ್ನ ಮಾತುಗಳನ್ನು ತಮ್ಮ ತಮ್ಮ ನೆರೆಯವರಿಂದ ಕದ್ದುಕೊಳ್ಳುವ ಪ್ರವಾದಿಗಳಿಗೆ ನಾನು ವಿರುದ್ಧನಾಗಿದ್ದೇನೆ. ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"31": "<sup>31</sup> ಆಹಾ, ತಮ್ಮ ನಾಲಿಗೆಗಳನ್ನು ಚಾಚಿ ಯೆಹೋವನು ನುಡಿಯುತ್ತಾನೆ ಎಂದು ಹೇಳುವ ಪ್ರವಾದಿಗಳಿಗೆ ನಾನು ಪ್ರತಿಭಟಿಸುವವನಾಗಿದ್ದೇನೆ. ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"32": "<sup>32</sup> ಆಹಾ, ಸುಳ್ಳು ಕನಸುಗಳನ್ನು ಪ್ರಕಟಿಸಿ ವಿವರಿಸಿ ತಮ್ಮ ಸುಳ್ಳು ಮಾತುಗಳಿಂದಲೂ ಕೊಚ್ಚಾಟದಿಂದಲೂ ನನ್ನ ಜನರಿಗೆ ದಾರಿತಪ್ಪಿಸುವ ಪ್ರವಾದಿಗಳನ್ನು ಎದುರಿಸುವವನಾಗಿದ್ದೇನೆ; ನಾನು ಅವರನ್ನು ಕಳುಹಿಸಲಿಲ್ಲ, ಆಜ್ಞಾಪಿಸಲಿಲ್ಲ; ಅವರಿಂದ ಈ ಜನರಿಗೆ ಯಾವ ಪ್ರಯೋಜನವೂ ಆಗುವದಿಲ್ಲ. ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"33": "<sup>33</sup> ಪ್ರವಾದಿಯಾಗಲಿ ಯಾಜಕನಾಗಲಿ ಈ ಜನರಲ್ಲಿ ಯಾರಾಗಲಿ ಯೆಹೋವನು ದಯಪಾಲಿಸಿರುವ ವಾಕ್ಯಭಾರವೇನು ಎಂದು ನಿನ್ನನ್ನು ಕೇಳಿದರೆ ನೀನು ಅವರಿಗೆ ಹೀಗೆ ಹೇಳು - ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ನೀವೇ ನನ್ನ ಭಾರ, ನಾನು ನಿಮ್ಮನ್ನು ಎಸೆದುಬಿಡುವೆನು.",
			"34": "<sup>34</sup> ಪ್ರವಾದಿ, ಯಾಜಕ, ಸಾಧಾರಣ ಜನ, ಇವರಲ್ಲಿ ಯಾವನು - ಯೆಹೋವನ ಭಾರ ಎಂದು ಉಚ್ಚರಿಸುವನೋ ಅವನನ್ನೂ ಅವನ ಮನೆಯವರನ್ನೂ ದಂಡಿಸುವೆನು.",
			"35": "<sup>35</sup> ನೀವು ನಿಮ್ಮ ನಿಮ್ಮ ನೆರೆಹೊರೆಯವರನ್ನೂ ಅಣ್ಣತಮ್ಮಂದಿರನ್ನೂ ಯೆಹೋವನ ಉತ್ತರವೇನು, ಯೆಹೋವನು ಏನು ನುಡಿದಿದ್ದಾನೆ ಎಂದು ಕೇಳಬೇಕೇ ಹೊರತು",
			"36": "<sup>36</sup> ಯೆಹೋವನ ಭಾರ ಎಂಬ ಮಾತನ್ನು ಇನ್ನು ಎತ್ತಲೇ ಕೂಡದು. ಪ್ರತಿಯೊಬ್ಬನ ನುಡಿಯೇ ಅವನವನಿಗೆ ಭಾರವಾಗಿರುವದು; ಜೀವಸ್ವರೂಪನಾದ ದೇವರ ನುಡಿಗಳನ್ನು, ಹೌದು, ನಮ್ಮ ದೇವರೂ ಸೇನಾಧೀಶ್ವರನೂ ಆದ ಯೆಹೋವನ ನುಡಿಗಳನ್ನು ತಲೆಕೆಳಗು ಮಾಡಿದ್ದೀರಷ್ಟೆ.",
			"37": "<sup>37</sup> ಯೆಹೋವನಿಂದ ನಿನಗೆ ಯಾವ ಉತ್ತರವಾಯಿತು, ಯೆಹೋವನು ಏನು ನುಡಿದಿದ್ದಾನೆ ಎಂದು ಪ್ರವಾದಿಯನ್ನು ಕೇಳಿರಿ.",
			"38": "<sup>38</sup> ನೀವು ಯೆಹೋವನ ಭಾರ ಎಂದು ಉಚ್ಚರಿಸಿದರೆ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ನಾನು ನಿಮಗೆ - ಯೆಹೋವನ ಭಾರ ಎಂಬ ಮಾತನ್ನು ಎತ್ತಲೇ ಕೂಡದು ಎಂದು ಹೇಳಿಕಳುಹಿಸಿರುವಲ್ಲಿ ನೀವು ಯೆಹೋವನ ಭಾರ ಎಂದು ಅಂದಕಾರಣ",
			"39": "<sup>39</sup> ಇಗೋ, ಖಂಡಿತವಾಗಿ ನಿಮ್ಮನ್ನೂ ನಾನು ನಿಮಗೆ ಮತ್ತು ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ದಯಪಾಲಿಸಿದ ಪಟ್ಟಣವನ್ನೂ ಭಾರವೆಂದು ಎತ್ತಿ ನನ್ನೆದುರಿನಿಂದ ಎಸೆದುಬಿಟ್ಟು",
			"40": "<sup>40</sup> ನಿತ್ಯನಿಂದೆಯನ್ನೂ ಎಂದಿಗೂ ಮರೆಯದ ಶಾಶ್ವತಾಪಮಾನವನ್ನೂ ನಿಮಗೆ ಬರಮಾಡುವೆನು."
		},
		"24": {
			"1": "<sup>1</sup> ಬಾಬೆಲಿನ ಅರಸನಾದ ನೆಬೂಕದ್ನೆಚ್ಚರನು ಯೆಹೋಯಾಕೀಮನ ಮಗನೂ ಯೆಹೂದದ ಅರಸನೂ ಆದ ಯೆಕೊನ್ಯನನ್ನೂ ಯೆಹೂದದ ಪ್ರಧಾನರನ್ನೂ ಶಿಲ್ಪಿಗಳನ್ನೂ ಕಮ್ಮಾರರನ್ನೂ ಯೆರೂಸಲೇಮಿನಿಂದ ಬಾಬೆಲಿಗೆ ಸೆರೆ ಒಯ್ದ ಮೇಲೆ ಯೆಹೋವನ ಆಲಯದ ಮುಂದೆ ಇಟ್ಟಿರುವ ಅಂಜೂರದ ಹಣ್ಣು ತುಂಬಿದ ಎರಡು ಪುಟ್ಟಿಗಳನ್ನು ಯೆಹೋವನು ನನಗೆ ಕಾಣಮಾಡಿದನು.",
			"2": "<sup>2</sup> ಒಂದು ಪುಟ್ಟಿಯಲ್ಲಿ ಫಲಕಾಲಕ್ಕೆ ಮುಂಚೆ ಮಾಗಿದ ಹಣ್ಣುಗಳ ಹಾಗೆ ಕಾಣುತ್ತಿದ್ದ ಅತ್ಯುತ್ತಮವಾದ ಅಂಜೂರದ ಫಲಗಳು ತುಂಬಿದ್ದವು; ಇನ್ನೊಂದು ಪುಟ್ಟಿಯಲ್ಲಿ ಯಾರೂ ತಿನ್ನದ ಹಾಗೆ ಬಹಳ ಕೆಟ್ಟು ಕೇವಲ ಅಸಹ್ಯವಾದ ಅಂಜೂರದ ಹಣ್ಣುಗಳು ತುಂಬಿದ್ದವು.",
			"3": "<sup>3</sup> ಆಗ ಯೆಹೋವನು ನನ್ನನ್ನು - ಯೆರೆಮೀಯನೇ, ಏನು ನೋಡುತ್ತೀ ಎಂದು ಕೇಳಲು ನಾನು - ಅಂಜೂರದ ಫಲಗಳನ್ನು ನೋಡುತ್ತೇನೆ; ಉತ್ತಮ ಫಲಗಳು ಅತ್ಯುತ್ತಮವಾಗಿವೆ; ಕೆಟ್ಟ ಫಲಗಳು ಬಹಳ ಕೆಟ್ಟಿವೆ, ಯಾರೂ ತಿನ್ನದ ಹಾಗೆ ಕೇವಲ ಅಸಹ್ಯವಾಗಿವೆ ಎಂದು ಉತ್ತರಕೊಟ್ಟೆನು.",
			"4": "<sup>4</sup> ಆಗ ಯೆಹೋವನು ಈ ನುಡಿಯನ್ನು ನನಗೆ ದಯಪಾಲಿಸಿದನು -",
			"5": "<sup>5</sup> ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರಾದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ನಾನು ಈ ಸ್ಥಳದೊಳಗಿಂದ ಕಸ್ದೀಯರ ದೇಶಕ್ಕೆ ಕಳುಹಿಸಿಬಿಟ್ಟ ಸೆರೆಯವರಾದ ಯೆಹೂದ್ಯರನ್ನು ಈ ಉತ್ತಮವಾದ ಅಂಜೂರದ ಫಲಗಳಂತೆ ಲಕ್ಷಿಸಿ ಅವರಿಗೆ ಮೇಲನ್ನುಂಟುಮಾಡುವೆನು.",
			"6": "<sup>6</sup> ಅವರನ್ನು ಕಟಾಕ್ಷಿಸಿ ಈ ದೇಶಕ್ಕೆ ತಿರಿಗಿ ಬರಮಾಡುವೆನು; ಅವರನ್ನು ಕಟ್ಟುವೆನು, ಕೆಡವುವದಿಲ್ಲ; ನೆಡುವೆನು, ಕೀಳುವದಿಲ್ಲ.",
			"7": "<sup>7</sup> ಅವರು ನನ್ನನ್ನು ಯೆಹೋವನೆಂದು ಗ್ರಹಿಸತಕ್ಕ ಹೃದಯವನ್ನು ಅವರಿಗೆ ಅನುಗ್ರಹಿಸುವೆನು; ಅವರು ನನ್ನ ಜನರಾಗಿರುವರು, ನಾನು ಅವರ ದೇವರಾಗಿರುವೆನು; ಅವರು ನನ್ನ ಕಡೆಗೆ ಮನಃಪೂರ್ವಕವಾಗಿ ಹಿಂದಿರುಗಿ ಬರುವರು.",
			"8": "<sup>8</sup> ಇದಲ್ಲದೆ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಯೆಹೂದದ ಅರಸನಾದ ಚಿದ್ಕೀಯ, ಅವನ ಪ್ರಧಾನರು, ಈ ದೇಶದಲ್ಲಿ ನಿಂತಿರುವ ಯೆರೂಸಲೇಮಿನವರ ಶೇಷ, ಐಗುಪ್ತದೇಶಕ್ಕೆ ವಲಸೆಯಾದವರು, ಇವರೆಲ್ಲರನ್ನು ಕೆಟ್ಟು ಯಾರೂ ತಿನ್ನದ ಹಾಗೆ ಕೇವಲ ಅಸಹ್ಯವಾದ ಅಂಜೂರದ ಹಣ್ಣುಗಳ ಗತಿಗೆ ತರುವೆನು.",
			"9": "<sup>9</sup> ಅವರ ಕೇಡಿಗಾಗಿ ನಾನು ಅವರನ್ನು ಲೋಕದ ಸಮಸ್ತ ರಾಜ್ಯಗಳಿಗೆ ಭಯಾಸ್ಪದವಾಗುವಂತೆ ಮಾಡುವೆನು; ನಾನು ಅವರನ್ನು ಅಟ್ಟಿಬಿಡುವ ಸ್ಥಳಗಳಲ್ಲೆಲ್ಲಾ ಅವರು ನಿಂದೆ, ಕಟ್ಟುಗಾದೆ, ಪರಿಹಾಸ್ಯ, ಶಾಪ, ಇವುಗಳಿಗೆ ಗುರಿಯಾಗುವರು.",
			"10": "<sup>10</sup> ನಾನು ಅವರಿಗೂ ಅವರ ಪಿತೃಗಳಿಗೂ ಅನುಗ್ರಹಿಸಿದ ದೇಶದೊಳಗಿಂದ ಅವರು ನಿರ್ಮೂಲರಾಗುವ ತನಕ ಖಡ್ಗಕ್ಷಾಮವ್ಯಾಧಿಗಳನ್ನು ಅವರ ಮೇಲೆ ಕಳುಹಿಸುವೆನು."
		},
		"25": {
			"1": "<sup>1</sup> ಯೋಷೀಯನ ಮಗನೂ ಯೆಹೂದದ ಅರಸನೂ ಆದ ಯೆಹೋಯಾಕೀಮನ ಆಳಿಕೆಯ ನಾಲ್ಕನೆಯ ವರುಷದಲ್ಲಿ, ಅಂದರೆ ಬಾಬೆಲಿನ ಅರಸನಾದ ನೆಬೂಕದ್ನೆಚ್ಚರನ ಆಳಿಕೆಯ ಮೊದಲನೆಯ ವರುಷದಲ್ಲಿ ಪ್ರವಾದಿಯಾದ ಯೆರೆಮೀಯನು ಯೆಹೂದ್ಯರೆಲ್ಲರ ವಿಷಯವಾಗಿ ತನಗೆ ಉಂಟಾದ ಈ ದೈವೋಕ್ತಿಯನ್ನು",
			"2": "<sup>2</sup> ಸಕಲ ಯೆಹೂದ್ಯರಿಗೂ ಸಮಸ್ತ ಯೆರೂಸಲೇಮಿನವರಿಗೂ ಸಾರಿದನು -",
			"3": "<sup>3</sup> ಆಮೋನನ ಮಗನೂ ಯೆಹೂದದ ಅರಸನೂ ಆದ ಯೋಷೀಯನ ಆಳಿಕೆಯ ಹದಿಮೂರನೆಯ ವರುಷದಿಂದ ಈ ದಿನದವರೆಗೆ, ಇಪ್ಪತ್ತುಮೂರು ವರುಷಗಳಿಂದಲೂ ಯೆಹೋವನು ತನ್ನ ವಾಕ್ಯವನ್ನು ನನಗೆ ದಯಪಾಲಿಸುತ್ತಿದ್ದಾನೆ; ಅದನ್ನು ನಿಮಗೆ ಸಾವಕಾಶಮಾಡದೆ ಪ್ರಕಟಿಸುತ್ತಾ ಬಂದಿದ್ದೇನೆ; ಆದರೆ ನೀವು ಅದಕ್ಕೆ ಕಿವಿಗೊಡಲಿಲ್ಲ.",
			"4": "<sup>4</sup> ಯೆಹೋವನು ತನ್ನ ಸೇವಕರಾದ ಪ್ರವಾದಿಗಳನ್ನೆಲ್ಲಾ ನಿಮ್ಮ ಬಳಿಗೆ ಸಾವಕಾಶಮಾಡದೆ ಕಳುಹಿಸುತ್ತಿದ್ದರೂ ನೀವು ಕೇಳಲಿಲ್ಲ, ಕಿವಿಗೊಡಲೂ ಇಲ್ಲ.",
			"5": "<sup>5</sup> ಆತನು ನಿಮಗೆ ಹೇಳಿಕಳುಹಿಸಿದ ಮಾತೇನಂದರೆ - ನೀವೆಲ್ಲರು ನಿಮ್ಮ ನಿಮ್ಮ ದುರ್ಮಾರ್ಗದಿಂದಲೂ ದುಷ್ಕೃತ್ಯಗಳಿಂದಲೂ ಹಿಂದಿರುಗಿರಿ, ನಿಮಗೆ ಶಾಶ್ವತ ಸ್ವಾಸ್ತ್ಯವಾಗಿರಲಿ ಎಂದು ಯೆಹೋವನು ಪುರಾತನ ಕಾಲದಲ್ಲಿ ನಿಮಗೂ ನಿಮ್ಮ ಪಿತೃಗಳಿಗೂ ಅನುಗ್ರಹಿಸಿದ ದೇಶದೊಳಗೆ ನೆಲೆಗೊಳ್ಳಿರಿ;",
			"6": "<sup>6</sup> ಅನ್ಯದೇವತೆಗಳನ್ನು ಹಿಂಬಾಲಿಸಿ ಸೇವಿಸಿ ಪೂಜಿಸಬೇಡಿರಿ; ನಿಮ್ಮ ಕೈಕೆಲಸದ ಬೊಂಬೆಗಳಿಂದ ನನ್ನನ್ನು ರೇಗಿಸದಿರಿ; ಹೀಗಾದರೆ ನಾನು ನಿಮಗೆ ಯಾವ ಕೇಡನ್ನೂ ಮಾಡೆನು ಎಂಬದೇ.",
			"7": "<sup>7</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ನೀವು ನನ್ನ ಕಡೆಗೆ ಕಿವಿಗೊಡದೆ ನಿಮ್ಮ ಕೈ ಕೆಲಸದ ಬೊಂಬೆಗಳಿಂದ ನನ್ನನ್ನು ಕೆಣಕಿ ನಿಮಗೆ ಕೇಡನ್ನು ತಂದುಕೊಂಡಿದ್ದೀರಿ.",
			"8": "<sup>8</sup> ಹೀಗಿರಲು ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನ ಈ ನುಡಿಯನ್ನು ಕೇಳಿರಿ - ನೀವು ನನ್ನ ಮಾತುಗಳನ್ನು ಕೇಳದ ಕಾರಣ",
			"9": "<sup>9</sup> ಇಗೋ, ನಾನು ಬಡಗಣ ಜನಾಂಗಗಳನ್ನೆಲ್ಲಾ ಕರೆಯಿಸಿ ಬಾಬೆಲಿನ ಅರಸನೂ ನನ್ನ ಸೇವಕನೂ ಆದ ನೆಬೂಕದ್ನೆಚ್ಚರನನ್ನು ಬರಮಾಡಿ ಇವರೆಲ್ಲರನ್ನು ಈ ದೇಶದ ಮೇಲೂ ಇದರ ನಿವಾಸಿಗಳ ಮೇಲೂ ಸುತ್ತಲಿನ ಸಕಲ ಜನಾಂಗಗಳ ಮೇಲೂ ಬೀಳಿಸಿ ಅವುಗಳನ್ನು ತುಂಬಾ ಹಾಳುಗೈದು ಬೆರಗಿನ ಸಿಳ್ಳಿಗೆ ಗುರಿಪಡಿಸಿ ನಿತ್ಯನಾಶನಕ್ಕೆ ಈಡುಮಾಡುವೆನು.",
			"10": "<sup>10</sup> ಇದಲ್ಲದೆ ನಾನು ಅವುಗಳಲ್ಲಿ ಹರ್ಷಸಂಭ್ರಮಗಳ ಧ್ವನಿಯನ್ನೂ ವಧೂವರರ ಸ್ವರವನ್ನೂ ಬೀಸುವ ಕಲ್ಲಿನ ಸದ್ದನ್ನೂ ದೀಪದ ಬೆಳಕನ್ನೂ ನಿಲ್ಲಿಸಿಬಿಡುವೆನು.",
			"11": "<sup>11</sup> ಈ ದೇಶವೆಲ್ಲಾ ಹಾಳಾಗಿ ಬೆರಗಿಗೆ ಈಡಾಗುವದು; ಮತ್ತು ಈ ಜನಾಂಗಗಳು ಎಪ್ಪತ್ತು ವರುಷ ಬಾಬೆಲಿನ ಅರಸನ ಅಡಿಯಾಳಾಗಿ ಬಿದ್ದಿರುವವು.",
			"12": "<sup>12</sup> ಎಪ್ಪತ್ತು ವರುಷಗಳ ತರುವಾಯ ನಾನು ಬಾಬೆಲಿನ ಅರಸನನ್ನೂ ಕಸ್ದೀಯರ ದೇಶವನ್ನೂ ಆ ಜನಾಂಗದವರನ್ನೂ ಅವರ ದ್ರೋಹಕ್ಕಾಗಿ ದಂಡಿಸಿ ಆ ದೇಶವನ್ನು ನಿತ್ಯನಾಶನಕ್ಕೆ ಗುರಿಮಾಡುವೆನು. ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"13": "<sup>13</sup> ನಾನು ಆ ದೇಶದ ವಿಷಯದಲ್ಲಿ ನುಡಿದದ್ದನ್ನೆಲ್ಲಾ (ಅಂದರೆ ಯೆರೆಮೀಯನು ಸಕಲ ಜನಾಂಗಗಳ ವಿಷಯವಾಗಿ ಮಾಡಿರುವ ಈ ಪ್ರವಾದನೆಗಳ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದದ್ದನ್ನೆಲ್ಲಾ) ಅದರ ಮೇಲೆ ಬರಮಾಡುವೆನು.",
			"14": "<sup>14</sup> ಅನೇಕ ಜನಾಂಗಗಳೂ ಮಹಾರಾಜರೂ ಅವರನ್ನೇ ಅಡಿಯಾಳಾಗಿ ಮಾಡಿಕೊಳ್ಳುವರು; ಅವರ ಕೃತ್ಯಗಳಿಗೂ ಕೈಕೆಲಸಗಳಿಗೂ ತಕ್ಕಂತೆ ಅವರಿಗೆ ಮುಯ್ಯಿತೀರಿಸುವೆನು.",
			"15": "<sup>15</sup> ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರಾದ ಯೆಹೋವನು ನನಗೆ - ರೋಷರೂಪಮದ್ಯದ ಈ ಪಾತ್ರೆಯನ್ನು ನೀನು ನನ್ನ ಕೈಯಿಂದ ತೆಗೆದುಕೊಂಡು ಯಾವ ಜನಾಂಗಗಳ ಬಳಿಗೆ ನಿನ್ನನ್ನು ಕಳುಹಿಸುತ್ತೇನೋ ಆ ಸಕಲ ಜನಾಂಗಗಳು ಇದರಲ್ಲಿ ಕುಡಿಯುವಂತೆ ಮಾಡು.",
			"16": "<sup>16</sup> ನಾನು ಅವರಲ್ಲಿಗೆ ಖಡ್ಗವನ್ನು ಕಳುಹಿಸುವೆನು; [ಆ ನನ್ನ ರೋಷವನ್ನು] ಅವರು ಕುಡಿದು ಓಲಾಡುವರು, ಹುಚ್ಚುಚ್ಚಾಗುವರು, ಎಂದು ಅಪ್ಪಣೆಕೊಟ್ಟನು.",
			"17": "<sup>17</sup> ಆಗ ನಾನು ಯೆಹೋವನ ಕೈಯಿಂದ ಆ ಪಾತ್ರೆಯನ್ನು ತೆಗೆದುಕೊಂಡು ಯೆಹೋವನು ಯಾವ ಜನಾಂಗಗಳ ಬಳಿಗೆ ನನ್ನನ್ನು ಕಳುಹಿಸಿದನೋ ಆ ಸಕಲ ಜನಾಂಗಗಳು ಅದರಲ್ಲಿ ಕುಡಿಯುವಂತೆ ಮಾಡಿದೆನು.",
			"18": "<sup>18</sup> ಯೆರೂಸಲೇಮಿಗೂ ಯೆಹೂದದ ಪಟ್ಟಣಗಳಿಗೂ ಅರಸರಿಗೂ ಪ್ರಧಾನರಿಗೂ ಕುಡಿಸಿದೆನು; ಇದರಿಂದ ಅವರು ಹಾಳಾಗಿ ಬೆರಗಿನ ಸಿಳ್ಳಿಗೂ ಶಾಪಕ್ಕೂ ಗುರಿಯಾಗುವದಕ್ಕೆ ಆಸ್ಪದವಾಯಿತು.",
			"19": "<sup>19</sup> ಅದು ಈ ದಿನ ನೆರವೇರಿದೆ. ಮತ್ತು ಐಗುಪ್ತದ ಅರಸನಾದ ಫರೋಹನು, ಅವನ ಸೇವಕರು, ಪ್ರಧಾನರು,",
			"20": "<sup>20</sup> ಅವನ ಎಲ್ಲಾ ಜನರು, ಬಗೆಬಗೆಯ ಎಲ್ಲಾ ವಿದೇಶೀಯರು, ಊಚ್ ದೇಶದ ಎಲ್ಲಾ ಅರಸರು, ಫಿಲಿಷ್ಟಿಯ ದೇಶದ ಎಲ್ಲಾ ಅರಸರು, ಅಷ್ಕೆಲೋನ್,",
			"21": "<sup>21</sup> ಗಾಜಾ, ಎಕ್ರೋನ್, ಅಷ್ಟೋದಿನ ಶೇಷ, ಎದೋಮ್ಯರು, ಮೋವಾಬ್ಯರು,",
			"22": "<sup>22</sup> ಅಮ್ಮೋನ್ಯರು, ತೂರಿನ ಎಲ್ಲಾ ಅರಸರು, ಚೀದೋನಿನ ಎಲ್ಲಾ ಅರಸರು,",
			"23": "<sup>23</sup> ಸಮುದ್ರದ ಆಚಿನ ಕರಾವಳಿಯ ಅರಸರು, ದೆದಾನ್ಯರು, ತೇಮಾದೇಶದವರು, ಬೂಜ್‍ದೇಶದವರು, ಚಂಡಿಕೆಬಿಟ್ಟುಕೊಂಡಿರುವವರೆಲ್ಲರು,",
			"24": "<sup>24</sup> ಅರಬಿಯ ಎಲ್ಲಾ ಅರಸರು, ಅರಣ್ಯವಾಸಿಗಳಾದ ಮಿಶ್ರಜಾತಿಯವರ ಎಲ್ಲಾ ಅರಸರು,",
			"25": "<sup>25</sup> ಜಿಮ್ರಿಯ ಎಲ್ಲಾ ಅರಸರು, ಏಲಾಮಿನ ಎಲ್ಲಾ ಅರಸರು,",
			"26": "<sup>26</sup> ಮೇದ್ಯರ ಎಲ್ಲಾ ಅರಸರು, ದೂರವೇನು ಸಮೀಪವೇನು ಅಂತು ಉತ್ತರದೇಶದ ಎಲ್ಲಾ ಅರಸರು, ಭೂಲೋಕದಲ್ಲಿರುವ ಸಕಲರಾಜ್ಯಗಳವರು, ಇವರೆಲ್ಲರಿಗೂ ಕುಡಿಸು; ಇವರು ಕುಡಿದ ಮೇಲೆ ಶೇಷಕಿನ ಅರಸನೂ ಕುಡಿಯಲಿ [ಎಂಬದೇ ನನಗಾದ ಅಪ್ಪಣೆ].",
			"27": "<sup>27</sup> [ಇದಲ್ಲದೆ ಈ ಅಪ್ಪಣೆಯಾಯಿತು] - ನೀನು ಅವರಿಗೆ ನುಡಿಯತಕ್ಕದ್ದೇನಂದರೆ - ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರೂ ಸೇನಾಧೀಶ್ವರನೂ ಆದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ನಾನು ನಿಮ್ಮಲ್ಲಿಗೆ ಖಡ್ಗವನ್ನು ಕಳುಹಿಸುವೆನು; [ಆ ನನ್ನ ರೋಷವನ್ನು] ಕುಡಿದು ಅಮಲೇರಿದವರಾಗಿ ಕಕ್ಕಿ ಬಿದ್ದು ಏಳದಿರಿ.",
			"28": "<sup>28</sup> ಅವರು ಒಂದು ವೇಳೆ ಪಾತ್ರೆಯನ್ನು ನಿನ್ನ ಕೈಯಿಂದ ತೆಗೆದುಕೊಳ್ಳಲಿಕ್ಕೂ ಕುಡಿಯಲಿಕ್ಕೂ ಒಲ್ಲದಿದ್ದರೆ ನೀನು ಅವರಿಗೆ ಹೀಗೆ ಹೇಳು - ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ನೀವು ಕುಡಿಯಲೇಬೇಕು.",
			"29": "<sup>29</sup> ಇಗೋ, ನನ್ನ ಹೆಸರುಗೊಂಡಿರುವ ಪಟ್ಟಣದಲ್ಲಿ ಬಾಧಿಸಲು ಪ್ರಾರಂಭಿಸುತ್ತೇನೆ. ನೀವು ಆ ದಂಡನೆಗೆ ತಪ್ಪಿಸಿಕೊಂಡೀರೋ? ಆಗುವದೇ ಇಲ್ಲ. ಖಡ್ಗವೇ, ಭೂನಿವಾಸಿಗಳನ್ನೆಲ್ಲ ಸಂಹರಿಸಲಿಕ್ಕೆ ಬಾ ಎಂದು ಕೂಗುವೆನು. ಇದು ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೆಂಬ ನನ್ನ ನುಡಿ.",
			"30": "<sup>30</sup> ಇಂತಿರಲು ನೀನು ಪ್ರವಾದಿಸುತ್ತಾ ಈ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ಅವರಿಗೆ ಹೇಳು - ಯೆಹೋವನು ಉನ್ನತಲೋಕದಿಂದ ಗರ್ಜಿಸುವನು, ತನ್ನ ಘನನಿವಾಸದಿಂದ ದನಿಗೈಯುವನು; ಆತನು ಗಟ್ಟಿಯಾಗಿ ಗರ್ಜಿಸಿ ತನ್ನ ಹುಲ್ಗಾವಲಿನ ಮಂದೆಯನ್ನು ಬೆದರಿಸುವನು; ದ್ರಾಕ್ಷೆಯ ಹಣ್ಣನ್ನು ತುಳಿಯುವವರು ಕೂಗಾಡುವಂತೆ ಭೂನಿವಾಸಿಗಳೆಲ್ಲರಿಗೂ ಭಯಂಕರನಾಗಿ ಕೂಗುವನು.",
			"31": "<sup>31</sup> ಶಬ್ದವು ಭೂಮಿಯ ಕಟ್ಟ ಕಡೆಯವರೆಗೆ ವ್ಯಾಪಿಸುವದು; ಯೆಹೋವನಿಗೂ ಜನಾಂಗಗಳಿಗೂ ವ್ಯಾಜ್ಯವುಂಟಷ್ಟೆ; ನರಜನ್ಮದವರೆಲ್ಲರ ಸಂಗಡ ನ್ಯಾಯಕ್ಕೆ ನಿಲ್ಲುವನು; ದುಷ್ಟರನ್ನು ಖಡ್ಗಕ್ಕೆ ಗುರಿಮಾಡುವನು. ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"32": "<sup>32</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಆಹಾ, ಕೇಡು ಜನಾಂಗದಿಂದ ಜನಾಂಗಕ್ಕೆ ಹರಡುವದು; ದೊಡ್ಡ ಬಿರುಗಾಳಿಯು ಲೋಕದ ಕಟ್ಟಕಡೆಯಿಂದ ಎದ್ದುಬರುವದು.",
			"33": "<sup>33</sup> ಆ ದಿನದಲ್ಲಿ ಯೆಹೋವನಿಂದ ಹತರಾದವರು ಲೋಕದ ಒಂದು ಕಡೆಯಿಂದ ಇನ್ನೊಂದು ಕಡೆಯವರೆಗೂ ಬಿದ್ದಿರುವರು; ಅವರಿಗಾಗಿ ಯಾರೂ ಗೋಳಾಡರು, ಅವರನ್ನು ಯಾರೂ ಒಟ್ಟುಗೂಡಿಸರು, ಯಾರೂ ಹೂಣಿಡರು, ಭೂಮಿಯ ಮೇಲೆ ಗೊಬ್ಬರವಾಗುವರು.",
			"34": "<sup>34</sup> ಕುರುಬರೇ, ಅರಚಿ ಗೋಳಾಡಿರಿ! ಮಂದೆಯಲ್ಲಿನ ಹಿರಿಯ ಮಣಿಗಳೇ, [ಬೂದಿಯಲ್ಲಿ] ಬಿದ್ದು ಹೊರಳಾಡಿರಿ! ನಿಮ್ಮನ್ನು ವಧಿಸುವ ಕಾಲವು ತುಂಬಿದೆ; ನಾನು ನಿಮ್ಮನ್ನು ಭಂಗಪಡಿಸುವೆನು; ನೀವು ಬಿದ್ದುಹೋದ ಅಂದವಾದ ಪಾತ್ರೆಯಂತೆ ಚೂರುಚೂರಾಗುವಿರಿ.",
			"35": "<sup>35</sup> ಕುರುಬರು ಓಡಿಹೋಗುವದಕ್ಕೆ ಮಾರ್ಗವು ಸಿಕ್ಕದು, ಮಂದೆಯ ಹಿರಿಯ ಮಣಿಗಳು ತಪ್ಪಿಸಿಕೊಳ್ಳುವದಕ್ಕೆ ಆಸ್ಪದ ದೊರೆಯದು.",
			"36": "<sup>36</sup> ಆಹಾ, ಕುರುಬರ ಕೂಗಾಟ! ಮಂದೆಯ ಹಿರಿಯ ಮಣಿಗಳ ಅರಚಾಟ! ಯೆಹೋವನು ಆ ಹುಲ್ಗಾವಲನ್ನು ಹಾಳುಮಾಡುತ್ತಿದ್ದಾನಲ್ಲಾ.",
			"37": "<sup>37</sup> ನೆಮ್ಮದಿಯ ಗೋಮಾಳಗಳು ಯೆಹೋವನ ರೋಷಾಗ್ನಿಯಿಂದ ನಿಶ್ಶಬ್ದವಾಗಿವೆ.",
			"38": "<sup>38</sup> ಆತನು ಸಿಂಹದಂತೆ ತನ್ನ ಹಕ್ಕೆಯನ್ನು ಬಿಟ್ಟುಬಂದಿದ್ದಾನೆ; ಹಿಂಸಿಸುವ ಖಡ್ಗದಿಂದಲೂ ಆತನ ರೋಷಾಗ್ನಿಯಿಂದಲೂ ಅವರ ದೇಶವು ಬೆರಗಿಗೆ ಈಡಾಗಿದೆ."
		},
		"26": {
			"1": "<sup>1</sup> ಯೋಷೀಯನ ಮಗನೂ ಯೆಹೂದದ ಅರಸನೂ ಆದ ಯೆಹೋಯಾಕೀಮನ ಆಳಿಕೆಯ ಆರಂಭದಲ್ಲಿ ಯೆಹೋವನಿಂದ [ಯೆರೆಮೀಯನಿಗೆ] ಈ ಅಪ್ಪಣೆಯಾಯಿತು -",
			"2": "<sup>2</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ನೀನು ಯೆಹೋವನ ಆಲಯದ ಪ್ರಾಕಾರದಲ್ಲಿ ನಿಂತು ಆ ಮಂದಿರದಲ್ಲಿ ಆರಾಧಿಸುವದಕ್ಕೆ ಬರುವ ಯೆಹೂದದ ಎಲ್ಲಾ ಊರಿನವರಿಗೆ ನಾನು ನಿನಗೆ ಆಜ್ಞಾಪಿಸುವ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ಹೇಳು; ಒಂದು ಮಾತನ್ನೂ ಬಿಡಬೇಡ.",
			"3": "<sup>3</sup> ಅವರು ಒಂದು ವೇಳೆ ಕಿವಿಗೊಟ್ಟು ತಮ್ಮ ತಮ್ಮ ದುರ್ಮಾರ್ಗಗಳಿಂದ ಹಿಂದಿರುಗಿಯಾರು; ಹಾಗಾದರೆ ಅವರ ದುಷ್ಕೃತ್ಯಗಳ ನಿಮಿತ್ತ ನಾನು ಅವರಿಗೆ ಮಾಡಬೇಕೆಂದಿದ್ದ ಕೇಡನ್ನು ಮನಮರುಗಿ ಮಾಡದಿರುವೆನು.",
			"4": "<sup>4</sup> ನೀನು ಅವರಿಗೆ ಹೀಗೆ ಹೇಳು - ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ನೀವು ನನ್ನ ಕಡೆಗೆ ಕಿವಿಗೊಡದೆ ನಾನು ನಿಮ್ಮ ಮುಂದೆ ಇಟ್ಟಿರುವ ಧರ್ಮಶಾಸ್ತ್ರಕ್ಕೆ ಅನುಸಾರವಾಗಿ ನಡೆಯದೆ",
			"5": "<sup>5</sup> ಕೇಳಲೊಲ್ಲದ ನಿಮ್ಮ ಬಳಿಗೆ ನಾನು ಸಾವಕಾಶವಿಲ್ಲದೆ ಕಳುಹಿಸುತ್ತಾ ಬಂದಿರುವ ನನ್ನ ಸೇವಕರಾದ ಪ್ರವಾದಿಗಳ ಮಾತುಗಳನ್ನು ಕೇಳದೆಯೂ ಇದ್ದರೆ -",
			"6": "<sup>6</sup> ಆಗ ನಾನು ಈ ಆಲಯವನ್ನು ಶಿಲೋವಿನ ಗತಿಗೆ ತಂದು ಈ ಪಟ್ಟಣವು ಲೋಕದ ಸಮಸ್ತಜನಾಂಗಗಳಲ್ಲಿ ಶಾಪದ ಮಾತಾಗುವಂತೆ ಮಾಡುವೆನು.",
			"7": "<sup>7</sup> ಯೆರೆಮೀಯನು ಯೆಹೋವನ ಆಲಯದಲ್ಲಿ ಹೀಗೆ ಮಾತಾಡುವದನ್ನು ಯಾಜಕರೂ ಪ್ರವಾದಿಗಳೂ ಸಕಲಜನರೂ ಕೇಳಿದರು.",
			"8": "<sup>8</sup> ಸಮಸ್ತಜನರಿಗೆ ಹೇಳಬೇಕೆಂದು ಯೆಹೋವನು ಆಜ್ಞಾಪಿಸಿದ್ದನ್ನೆಲ್ಲಾ ಯೆರೆಮೀಯನು ನುಡಿದು ಮುಗಿಸಿದ ಮೇಲೆ ಯಾಜಕರೂ ಪ್ರವಾದಿಗಳೂ ಎಲ್ಲಾ ಜನರೂ ಅವನನ್ನು ಹಿಡಿದು ನಿನಗೆ ಮರಣವೇ ಆಗಬೇಕು;",
			"9": "<sup>9</sup> ನೀನು ಯೆಹೋವನ ಹೆಸರೆತ್ತಿ ಪ್ರವಾದಿಸುತ್ತಾ - ಈ ಆಲಯವು ಶಿಲೋವಿನ ಗತಿಗೆ ಬರುವದು, ಈ ಪಟ್ಟಣವು ಒಕ್ಕಲಿಲ್ಲದೆ ಹಾಳುಬೀಳುವದು ಎಂದು ಏಕೆ ನುಡಿದೆ ಅಂದರು. ಆಗ ಯೆಹೋವನ ಆಲಯದಲ್ಲಿ ಎಲ್ಲಾ ಜನರು ಯೆರೆಮೀಯನ ಸುತ್ತಲು ಸುತ್ತಿಕೊಂಡಿದ್ದರು.",
			"10": "<sup>10</sup> ಯೆಹೂದದ ಸರದಾರರು ಈ ವರ್ತಮಾನವನ್ನು ಕೇಳಿ ಅರಮನೆಯಿಂದ ಯೆಹೋವನ ಆಲಯಕ್ಕೆ ಹತ್ತಿಬಂದು ಆ ಮಂದಿರದ ಹೊಸ ಬಾಗಿಲಿನಲ್ಲಿ ಕೂತುಕೊಂಡಿರಲಾಗಿ",
			"11": "<sup>11</sup> ಯಾಜಕರೂ ಪ್ರವಾದಿಗಳೂ ಇವನು ಮರಣ ದಂಡನೆಗೆ ತಕ್ಕವನು; ಇವನು ಈ ಪಟ್ಟಣಕ್ಕೆ ಅಹಿತನುಡಿದದ್ದನ್ನು ನೀವು ಕಿವಿಯಾರ ಕೇಳಿದಿರಷ್ಟೆ ಎಂದು ಸರದಾರರಿಗೂ ಎಲ್ಲಾ ಜನರಿಗೂ ಹೇಳಿದರು.",
			"12": "<sup>12</sup> ಆಗ ಯೆರೆಮೀಯನು ಸಕಲ ಸರದಾರರಿಗೂ ಸಮಸ್ತ ಜನರಿಗೂ ಹೀಗೆ ಹೇಳಿದನು - ನೀವು ಕೇಳಿದ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ಈ ಆಲಯಕ್ಕೂ ಈ ಪಟ್ಟಣಕ್ಕೂ ಪ್ರತಿಕೂಲವಾಗಿ ನುಡಿಯಬೇಕೆಂದು ಯೆಹೋವನು ನನ್ನನ್ನು ಕಳುಹಿಸಿದನು.",
			"13": "<sup>13</sup> ಈಗಲೇ ನಿಮ್ಮ ಮಾರ್ಗಗಳನ್ನೂ ಕೃತ್ಯಗಳನ್ನೂ ತಿದ್ದಿಕೊಂಡು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ನುಡಿಗೆ ಕಿವಿಗೊಡಿರಿ; ಹಾಗಾದರೆ ಯೆಹೋವನು ನಿಮ್ಮ ವಿಷಯವಾಗಿ ನುಡಿದಿರುವ ಕೇಡನ್ನು ಮನಮರುಗಿ ಮಾಡದಿರುವನು.",
			"14": "<sup>14</sup> ನಾನೋ ನಿಮ್ಮ ಕೈಯಲ್ಲಿದ್ದೇನಲ್ಲಾ; ನಿಮಗೆ ಒಳ್ಳೇದಾಗಿಯೂ ನ್ಯಾಯವಾಗಿಯೂ ತೋರುವದನ್ನು ನನಗೆ ಮಾಡಿರಿ.",
			"15": "<sup>15</sup> ಆದರೆ ನೀವು ನನ್ನನ್ನು ಕೊಂದುಹಾಕಿದ ಪಕ್ಷಕ್ಕೆ ನೀವೂ ಈ ಪಟ್ಟಣವೂ ಇದರ ನಿವಾಸಿಗಳೂ ನಿರ್ದೋಷಿಯ ರಕ್ತವನ್ನು ಸುರಿಸಿದ ಅಪರಾಧಕ್ಕೆ ಪಾತ್ರರಾಗುವಿರಿ ಎಂದು ನಿಶ್ಚಯವಾಗಿ ತಿಳಿದುಕೊಳ್ಳಿರಿ. ಈ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ನಿಮ್ಮ ಕಿವಿಗೆ ಮುಟ್ಟಿಸುವಂತೆ ಯೆಹೋವನು ನನ್ನನ್ನು ಕಳುಹಿಸಿರುವದು ಖಂಡಿತ.",
			"16": "<sup>16</sup> ಇದನ್ನು ಕೇಳಿ ಸರದಾರರೂ ಸಕಲ ಜನರೂ ಯಾಜಕಪ್ರವಾದಿಗಳಿಗೆ - ಇವನು ಮರಣ ದಂಡನೆಗೆ ತಕ್ಕವನಲ್ಲ; ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಹೆಸರಿನಲ್ಲಿ ಇದನ್ನು ನಮಗೆ ನುಡಿದಿದ್ದಾನಷ್ಟೆ ಎಂದು ಹೇಳಿದ ತರುವಾಯ ದೇಶದ ಹಿರಿಯರಲ್ಲಿ ಕೆಲವರು ಎದ್ದು -",
			"17": "<sup>17</sup> ಯೆಹೂದದ ಅರಸನಾದ ಹಿಜ್ಕೀಯನ ಕಾಲದಲ್ಲಿ ಮೋರೆಷೆತ್ ಊರಿನವನಾದ",
			"18": "<sup>18</sup> ಮೀಕಾಯನು ಯೆಹೂದ್ಯರೆಲ್ಲರಿಗೆ - ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನ ಮಾತನ್ನು ಕೇಳಿರಿ, ಚೀಯೋನ್ ಪಟ್ಟಣವು ಹೊಲದಂತೆ ಗೇಯಲ್ಪಡುವದು, ಯೆರೂಸಲೇಮು ಹಾಳುದಿಬ್ಬಗಳಾಗಿ ಬೀಳುವದು, ಯೆಹೋವನ ಆಲಯದ ಪರ್ವತವು ಕಾಡುಗುಡ್ಡಗಳಂತಾಗುವದು ಎಂದು ಹೇಳಲಾಗಿ",
			"19": "<sup>19</sup> ಯೆಹೂದದ ಅರಸನಾದ ಹಿಜ್ಕೀಯನೂ ಎಲ್ಲಾ ಯೆಹೂದ್ಯರೂ ಅವನನ್ನು ಕೊಂದುಹಾಕಿದರೋ? ಇಲ್ಲವೇ ಇಲ್ಲ. ಆ ಅರಸನು ಯೆಹೋವನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವನಾಗಿ ಆತನ ದಯೆಯನ್ನು ಬೇಡಿಕೊಳ್ಳಲು ಯೆಹೋವನು ಅವರ ವಿಷಯವಾಗಿ ನುಡಿದಿದ್ದ ಕೇಡನ್ನು ಮನಮರುಗಿ ಮಾಡದೆ ಬಿಟ್ಟನಲ್ಲಾ. ನಾವಾದರೋ [ಇವನನ್ನು ಕೊಂದುಹಾಕಿ] ನಮಗೆ ದೊಡ್ಡ ಕೇಡನ್ನು ಉಂಟುಮಾಡಿಕೊಳ್ಳುವವರಾಗಿದ್ದೇವೆ ಎಂಬದಾಗಿ ಸಕಲಜನ ಸಮೂಹಕ್ಕೆ ಹೇಳಿದರು.",
			"20": "<sup>20</sup> ಮತ್ತು ಶಮಾಯನ ಮಗನೂ ಕಿರ್ಯತ್‍ಯಾರೀಮ್ ಊರಿನವನೂ ಆದ ಊರೀಯನೆಂಬವನೊಬ್ಬನು ಯೆಹೋವನ ಹೆಸರಿನಲ್ಲಿ ಪ್ರವಾದಿಸುವವನಾಗಿದ್ದು ಯೆರೆಮೀಯನು ನುಡಿದಂತೆಯೇ ಈ ಪಟ್ಟಣಕ್ಕೂ ಈ ದೇಶಕ್ಕೂ ಅಹಿತವನ್ನು ನುಡಿಯುತ್ತಿದ್ದನು.",
			"21": "<sup>21</sup> ಅರಸನಾದ ಯೆಹೋಯಾಕೀಮನೂ ಅವನ ಎಲ್ಲಾ ಪ್ರಸಿದ್ಧ ಶೂರರೂ ಸರದಾರರೂ ಇವನ ಮಾತುಗಳನ್ನು ಕೇಳಿದಾಗ ಅರಸನು ಇವನನ್ನು ಕೊಂದುಹಾಕಲು ಮನಮಾಡಿದನು; ಆದರೆ ಊರೀಯನು ಆ ಸುದ್ದಿಯನ್ನು ಕೇಳಿ ಭಯಪಟ್ಟು ಓಡಿಹೋಗಿ ಐಗುಪ್ತವನ್ನು ಸೇರಿಕೊಂಡನು.",
			"22": "<sup>22</sup> ಆಗ ಅರಸನಾದ ಯೆಹೋಯಾಕೀಮನು ಅಕ್ಬೋರನ ಮಗನಾದ ಎಲ್ನಾಥಾನನನ್ನೂ ಅವನ ಸಂಗಡ ಇನ್ನು ಕೆಲವರನ್ನೂ ಐಗುಪ್ತಕ್ಕೆ ಕಳುಹಿಸಿದನು;",
			"23": "<sup>23</sup> ಅವರು ಊರೀಯನನ್ನು ಐಗುಪ್ತದಿಂದ ಎಳೆದು ಅರಸನಾದ ಯೆಹೋಯಾಕೀಮನ ಬಳಿಗೆ ತರಲು ಅವನು ಇವನನ್ನು ಕತ್ತಿಯಿಂದ ಕಡಿಸಿ ಶವವನ್ನು ಸಾಧಾರಣ ಜನರ ಗೋರಿಗಳ ನಡುವೆ ಹಾಕಿಸಿದನು.",
			"24": "<sup>24</sup> ಇತ್ತ ಯಾಜಕರೂ ಪ್ರವಾದಿಗಳೂ ಯೆರೆಮೀಯನನ್ನು ಜನರ ಕೈಗೆ ಸಿಕ್ಕಿಸಿ ಕೊಲ್ಲಿಸದ ಹಾಗೆ ಶಾಫಾನನ ಮಗನಾದ ಅಹೀಕಾಮನ ಸಹಾಯವು ಅವನಿಗೆ ಒದಗಿತು."
		},
		"27": {
			"1": "<sup>1</sup> ಯೋಷೀಯನ ಮಗನೂ ಯೆಹೂದದ ಅರಸನೂ ಆದ ಯೆಹೋಯಾಕೀಮನ ಆಳಿಕೆಯ ಆರಂಭದಲ್ಲಿ ಯೆಹೋವನಿಂದ ಯೆರೆಮೀಯನಿಗೆ ಈ ಅಪ್ಪಣೆಯಾಯಿತು -",
			"2": "<sup>2</sup> ಇದೇ ಯೆಹೋವನ ನುಡಿ - ನೀನು ಕಣ್ಣಿನೊಗಗಳನ್ನು ಮಾಡಿ ನಿನ್ನ ಹೆಗಲಿಗೆ ಹಾಕಿಕೊಂಡು",
			"3": "<sup>3</sup> ಯೆಹೂದದ ಅರಸನಾದ ಚಿದ್ಕೀಯನ ಬಳಿಗೆ ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಬಂದಿರುವ ರಾಯಭಾರಿಗಳ ಮೂಲಕ ಅವುಗಳನ್ನು ಎದೋಮಿನ ರಾಜ, ಮೋವಾಬಿನ ರಾಜ, ಅಮ್ಮೋನ್ಯರ ರಾಜ, ತೂರಿನ ರಾಜ, ಚೀದೋನಿನ ರಾಜ, ಇವರಿಗೆ ಕಳುಹಿಸು;",
			"4": "<sup>4</sup> ಆಗ ಆ ರಾಯಭಾರಿಗಳಿಗೆ - ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರೂ ಸೇನಾಧೀಶ್ವರನೂ ಆದ ಯೆಹೋವನು ನಿಮ್ಮ ಒಡೆಯರಿಗೆ ನೀವು ಹೀಗೆ ಅರಿಕೆಮಾಡುವಂತೆ ಅಪ್ಪಣೆಕೊಟ್ಟಿದ್ದಾನೆ ಎಂಬದಾಗಿ ಹೇಳಿ ಅವರು ತಮ್ಮ ತಮ್ಮ ಒಡೆಯರಿಗೆ ತಿಳಿಸಬೇಕಾದ ಈ ಮಾತುಗಳನ್ನು ಅವರಿಗೆ ಆಜ್ಞಾಪಿಸು -",
			"5": "<sup>5</sup> ನಾನೇ ಕೈ ನೀಡಿ ನನ್ನ ಮಹಾ ಶಕ್ತಿಯಿಂದ ಲೋಕವನ್ನೂ ಭೂಮಿಯನ್ನೂ ಅದರ ಮಾನವರನ್ನೂ ಪಶುಗಳನ್ನೂ ಸೃಷ್ಟಿಸಿದ್ದೇನೆ;",
			"6": "<sup>6</sup> ಈ ನನ್ನ ಸೃಷ್ಟಿಯನ್ನು ನನಗೆ ಸರಿತೋಚಿದವನಿಗೆ ಕೊಡಬಲ್ಲೆನು; ಈಗ ಈ ದೇಶಗಳನ್ನೆಲ್ಲಾ ಬಾಬೆಲಿನ ಅರಸನೂ ನನ್ನ ಸೇವಕನೂ ಆದ ನೆಬೂಕದ್ನೆಚ್ಚರನ ಕೈವಶ ಮಾಡಿದ್ದೇನೆ; ಅವನ ಸೇವೆಗಾಗಿ ಭೂಜಂತುಗಳನ್ನೂ ಕೊಟ್ಟಿದ್ದೇನೆ.",
			"7": "<sup>7</sup> ಅವನ ದೇಶಕ್ಕೆ ಕಾಲವು ಹತ್ತರಿಸುವ ತನಕ ಸಕಲ ಜನಾಂಗಗಳು ಅವನಿಗೂ ಅವನ ಮಗನಿಗೂ ಮೊಮ್ಮಗನಿಗೂ ಅಡಿಯಾಳಾಗಿ ಬಿದ್ದಿರುವವು; ಆಮೇಲೆ ಅನೇಕ ಜನಾಂಗಗಳೂ ಮಹಾರಾಜರೂ ಅವನನ್ನೇ ಅಡಿಯಾಳನ್ನಾಗಿ ಮಾಡಿಕೊಳ್ಳುವರು.",
			"8": "<sup>8</sup> ಯಾವ ಜನಾಂಗ, ಯಾವ ರಾಜ್ಯ, ಬಾಬೆಲಿನ ಅರಸನಾದ ನೆಬೂಕದ್ನೆಚ್ಚರನ ಅಡಿಯಾಳಾಗಲಿಕ್ಕೂ ಬಾಬೆಲಿನ ಅರಸನ ನೊಗಕ್ಕೆ ಹೆಗಲು ಕೊಡಲಿಕ್ಕೂ ಒಪ್ಪದೋ ಆ ಜನಾಂಗವನ್ನು ನಾನು ಖಡ್ಗ ಕ್ಷಾಮವ್ಯಾಧಿಗಳಿಂದ ದಂಡಿಸುತ್ತಾ ಬಂದು ಕಡೆಯಲ್ಲಿ ಅವನ ಕೈಯಿಂದಲೇ ನಿರ್ಮೂಲ ಮಾಡಿಸುವೆನು. ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"9": "<sup>9</sup> ನೀವು ಬಾಬೆಲಿನ ಅರಸನ ಅಡಿಯಾಳಾಗುವದಿಲ್ಲ ಎಂದು ನಿಮಗೆ ನುಡಿಯುವ ನಿಮ್ಮ ಪ್ರವಾದಿಗಳು, ಶಕುನದವರು, ಕನಸಿನವರು, ಕಣಿಯವರು, ಮಾಟದವರು, ಇವರಿಗೆ ಕಿವಿಗೊಡಲೇಬಾರದು;",
			"10": "<sup>10</sup> ಅವರು ನಿಮಗೆ ಸುಳ್ಳನ್ನು ಸಾರುತ್ತಾರೆ; ನೀವು ನನ್ನಿಂದ ಅಟ್ಟಲ್ಪಟ್ಟು ದೇಶಭ್ರಷ್ಟರಾಗಿ ಅಳಿದುಹೋಗುವದಕ್ಕೆ ಅವರ ದುರ್ಬೋಧನೆಯು ಆಸ್ಪದವಾಗುವದು.",
			"11": "<sup>11</sup> ಯಾವ ಜನಾಂಗವು ಬಾಬೆಲಿನ ಅರಸನ ನೊಗಕ್ಕೆ ಹೆಗಲು ಕೊಟ್ಟು ಅವನ ಅಡಿಯಾಳಾಗುವದೋ ಆ ಜನಾಂಗದವರನ್ನು ನಾನು ಅವರ ದೇಶದಲ್ಲಿ ನೆಲೆಗೊಳಿಸುವೆನು; ಅವರು ಅಲ್ಲಿ ಗೆಯ್ದು ವಾಸಿಸುವರು. ಯೆಹೋವನೇ ಇದನ್ನು ನುಡಿದಿದ್ದಾನೆ.",
			"12": "<sup>12</sup> ಇದಲ್ಲದೆ ನಾನು ಆ ಎಲ್ಲಾ ಮಾತುಗಳನ್ನು ಅನುಸರಿಸಿ ಯೆಹೂದದ ಅರಸನಾದ ಚಿದ್ಕೀಯನಿಗೂ ಹೀಗೆ ನುಡಿದೆನು - ಬಾಬೆಲಿನ ಅರಸನ ನೊಗಕ್ಕೆ ಹೆಗಲುಕೊಟ್ಟು ಅವನಿಗೂ ಅವನ ಜನರಿಗೂ ಅಡಿಯಾಳಾಗಿರಿ, ಆಗ ಬದುಕುವಿರಿ.",
			"13": "<sup>13</sup> ನೀನೂ ನಿನ್ನ ಜನರೂ ಖಡ್ಗ ಕ್ಷಾಮವ್ಯಾಧಿಗಳಿಂದ ಏಕೆ ಸಾಯಬೇಕು? ಬಾಬೆಲಿನ ಅರಸನ ಅಡಿಯಾಳಾಗಲು ಒಪ್ಪದ ಜನಾಂಗಕ್ಕೆ ಈ ಗತಿಯಾಗುವದೆಂದು ಯೆಹೋವನು ನುಡಿದಿದ್ದಾನಷ್ಟೆ.",
			"14": "<sup>14</sup> ನೀವು ಬಾಬೆಲಿನ ಅರಸನ ಅಡಿಯಾಳಾಗುವದಿಲ್ಲ ಎಂದು ನಿಮಗೆ ನುಡಿಯುವ ಪ್ರವಾದಿಗಳ ಮಾತುಗಳಿಗೆ ಕಿವಿಗೊಡಬೇಡಿರಿ; ಅವರು ನಿಮಗೆ ಸುಳ್ಳನ್ನು ಸಾರುತ್ತಾರೆ.",
			"15": "<sup>15</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ನಾನು ಅವರನ್ನು ಕಳುಹಿಸಲಿಲ್ಲ; ನನ್ನ ಹೆಸರೆತ್ತಿ ಸುಳ್ಳನ್ನು ಸಾರುತ್ತಾರೆ; ನೀವೂ ನಿಮಗೆ ಪ್ರವಾದಿಸುವ ಪ್ರವಾದಿಗಳೂ ನನ್ನಿಂದ ಅಟ್ಟಲ್ಪಟ್ಟು ಅಳಿದುಹೋಗುವದಕ್ಕೆ ಅವರ ದುರ್ಬೋಧನೆಯು ಆಸ್ಪದವಾಗುವದು.",
			"16": "<sup>16</sup> ಇದಲ್ಲದೆ ನಾನು ಯಾಜಕರಿಗೂ ಈ ಸಕಲ ಜನರಿಗೂ ಹೀಗೆ ನುಡಿದೆನು - ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಇಗೋ, ಯೆಹೋವನ ಆಲಯದ ಉಪಕರಣಗಳು ಬೇಗನೆ ಬಾಬೆಲಿನಿಂದ ಹಿಂದಕ್ಕೆ ತರಲ್ಪಡುವವು ಎಂಬದಾಗಿ ನಿಮಗೆ ಪ್ರವಾದಿಸುವ ಪ್ರವಾದಿಗಳ ಮಾತುಗಳನ್ನು ಕೇಳಬೇಡಿರಿ; ನಿಮಗೆ ಸುಳ್ಳನ್ನು ಸಾರುತ್ತಾರೆ.",
			"17": "<sup>17</sup> ಅವರ ಕಡೆಗೆ ಕಿವಿಗೊಡಲೇ ಬಾರದು; ಬಾಬೆಲಿನ ಅರಸನ ಅಡಿಯಾಳಾಗಿರಿ, ಬದುಕುವಿರಿ;",
			"18": "<sup>18</sup> ಈ ಪಟ್ಟಣವು ಏಕೆ ಹಾಳಾಗಬೇಕು? ಅವರು ಪ್ರವಾದಿಗಳಾಗಿದ್ದು ಯೆಹೋವನ ವಾಕ್ಯಕ್ಕೆ ನೆಲೆಯಾಗಿದ್ದರೆ ಈಗ ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನಿಗೆ ವಿಜ್ಞಾಪನೆ ಮಾಡಿ ಯೆಹೋವನ ಆಲಯದಲ್ಲಿಯೂ ಯೆಹೂದದ ಅರಸನ ಮನೆಯಲ್ಲಿಯೂ ಯೆರೂಸಲೇಮಿನಲ್ಲಿಯೂ ಉಳಿದಿರುವ ಉಪಕರಣಗಳು ಬಾಬೆಲಿಗೆ ಹೋಗದಂತೆ ಬೇಡಿಕೊಳ್ಳಲಿ.",
			"19": "<sup>19</sup> ಬಾಬೆಲಿನ ಅರಸನಾದ ನೆಬೂಕದ್ನೆಚ್ಚರನು ಯೆಹೋಯಾಕೀಮನ ಮಗನೂ ಯೆಹೂದದ ಅರಸನೂ ಆದ ಯೆಕೊನ್ಯನನ್ನೂ ಯೆಹೂದದ ಮತ್ತು ಯೆರೂಸಲೇಮಿನ ಸಮಸ್ತ ಪ್ರಧಾನರನ್ನೂ ಯೆರೂಸಲೇಮಿನಿಂದ ಬಾಬೆಲಿಗೆ ಸೆರೆ ಒಯ್ದಾಗ ಕೆಲವು ಸಾಮಾನುಗಳನ್ನು ತೆಗೆದುಕೊಳ್ಳಲಿಲ್ಲವಲ್ಲಾ;",
			"20": "<sup>20</sup> ಆ ಕಂಬಗಳು, ಸಮುದ್ರವೆನಿಸಿಕೊಂಡ ಪಾತ್ರೆಯು, ಪೀಠಗಳು, ಈ ಪಟ್ಟಣದಲ್ಲಿ ನಿಂತಿರುವ ಉಪಕರಣಗಳು,",
			"21": "<sup>21</sup> ಇವುಗಳ ವಿಷಯವಾಗಿ, ಅಂದರೆ ಯೆಹೋವನ ಆಲಯದಲ್ಲಿಯೂ ಯೆಹೂದದ ಅರಸನ ಮನೆಯಲ್ಲಿಯೂ ಯೆರೂಸಲೇಮಿನಲ್ಲಿಯೂ ಉಳಿದಿರುವ ಉಪಕರಣಗಳ ವಿಷಯವಾಗಿ ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರೂ ಸೇನಾಧೀಶ್ವರನೂ ಆದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ -",
			"22": "<sup>22</sup> ಅವು ಬಾಬೆಲಿಗೆ ಒಯ್ಯಲ್ಪಟ್ಟು ನಾನು ಅವುಗಳನ್ನು ಉದ್ಧರಿಸುವ ದಿನದ ತನಕ ಅಲ್ಲೇ ಇರುವವು; ಆಗ ನಾನು ಅವುಗಳನ್ನು ಪುನಃ ತಂದು ಈ ಸ್ಥಳಕ್ಕೆ ಸೇರಿಸುವೆನು. ಇದು ಯೆಹೋವನ ನುಡಿ."
		},
		"28": {
			"1": "<sup>1</sup> ಅದೇ ವರುಷದಲ್ಲಿ, ಯೆಹೂದದ ಅರಸನಾದ ಚಿದ್ಕೀಯನ ಆಳಿಕೆಯ ಆರಂಭದಲ್ಲಿ, ಅಂದರೆ ನಾಲ್ಕನೆಯ ವರುಷದ ಐದನೆಯ ತಿಂಗಳಲ್ಲಿ ಪ್ರವಾದಿಯಾದ ಅಜ್ಜೂರನ ಮಗನೂ ಗಿಬ್ಯೋನ್ ಊರಿನವನೂ ಆದ ಹನನ್ಯನು ಯೆಹೋವನ ಆಲಯದೊಳಗೆ ಯಾಜಕರ ಮತ್ತು ಸಕಲಜನರ ಎದುರಿನಲ್ಲಿ ನನಗೆ ಹೀಗೆ ಹೇಳಿದನು -",
			"2": "<sup>2</sup> ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರೂ ಸೇನಾಧೀಶ್ವರನೂ ಆದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಬಾಬೆಲಿನ ಅರಸನು [ನಿಮಗೆ] ಹೇರಿರುವ ನೊಗವನ್ನು ಮುರಿದುಬಿಟ್ಟಿದ್ದೇನೆ.",
			"3": "<sup>3</sup> ಬಾಬೆಲಿನ ಅರಸನಾದ ನೆಬೂಕದ್ನೆಚ್ಚರನು ಈ ಸ್ಥಳದಿಂದ ತೆಗೆದು ಬಾಬೆಲಿಗೆ ಒಯ್ದ ಯೆಹೋವನ ಆಲಯದ ಸಕಲ ಉಪಕರಣಗಳನ್ನು ಎರಡು ವರುಷಗಳೊಳಗಾಗಿ ನಾನು ಪುನಃ ಈ ಸ್ಥಳಕ್ಕೆ ಸೇರಿಸುವೆನು.",
			"4": "<sup>4</sup> ಮತ್ತು ಯೆಹೋಯಾಕೀಮನ ಮಗನೂ ಯೆಹೂದದ ಅರಸನೂ ಆದ ಯೆಕೊನ್ಯನನ್ನೂ ಬಾಬೆಲಿಗೆ ಸೆರೆಹೋದ ಎಲ್ಲಾ ಯೆಹೂದ್ಯರನ್ನೂ ಈ ಸ್ಥಳಕ್ಕೆ ತಿರಿಗಿ ಬರಮಾಡುವೆನು. ಬಾಬೆಲಿನ ಅರಸನು [ನಿಮಗೆ] ಹೇರಿರುವ ನೊಗವನ್ನು ನಾನು ಮುರಿದುಬಿಡುವೆನು. ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"5": "<sup>5</sup> ಆಗ ಪ್ರವಾದಿಯಾದ ಯೆರೆಮೀಯನು ಯೆಹೋವನ ಆಲಯದೊಳಗೆ ನಿಂತಿದ್ದ ಯಾಜಕರ ಮತ್ತು ಸಕಲಜನರ ಎದುರಿನಲ್ಲಿ ಪ್ರವಾದಿಯಾದ ಹನನ್ಯನಿಗೆ -",
			"6": "<sup>6</sup> ಹಾಗಾಗಲಿ, ಯೆಹೋವನು ಹಾಗೇ ಮಾಡಲಿ! ಯೆಹೋವನು ತನ್ನ ಆಲಯದ ಉಪಕರಣಗಳನ್ನೂ ಸೆರೆಹೋದವರೆಲ್ಲರನ್ನೂ ಬಾಬೆಲಿನಿಂದ ಈ ಸ್ಥಳಕ್ಕೆ ಪುನಃ ಬರಮಾಡಿ ನೀನು ನುಡಿದ ಮಾತುಗಳನ್ನು ನೆರವೇರಿಸಲಿ!",
			"7": "<sup>7</sup> ಆದರೆ ನಾನು ನಿನ್ನ ಮತ್ತು ಸಕಲಜನರ ಕಿವಿಗೆ ಬೀಳುವಂತೆ ನುಡಿಯುವ ಈ ಮಾತನ್ನು ಕೇಳು -",
			"8": "<sup>8</sup> ನನಗೂ ನಿನಗೂ ಮುಂಚೆ ಪುರಾತನ ಕಾಲದಿಂದಿದ್ದ ಪ್ರವಾದಿಗಳು ಅನೇಕದೇಶಗಳಿಗೂ ದೊಡ್ಡ ದೊಡ್ಡ ರಾಜ್ಯಗಳಿಗೂ ಯುದ್ಧ, ವಿಪತ್ತು, ವ್ಯಾಧಿ, ಇವುಗಳ ವಿಷಯವಾಗಿ ಸಾರುತ್ತಿದ್ದರಷ್ಟೆ.",
			"9": "<sup>9</sup> ಹಿತ ಸಮಾಚಾರವನ್ನು ಸಾರುವ ಪ್ರವಾದಿಯೋ ಯೆಹೋವನಿಂದ ನಿಜವಾಗಿ ಕಳುಹಿಸಲ್ಪಟ್ಟವನೆಂದು ಅವನ ಮಾತು ಕೈಗೂಡಿದ ಮೇಲೇ ತಿಳಿಯತಕ್ಕದ್ದು ಎಂದು ಹೇಳಿದನು.",
			"10": "<sup>10</sup> ಆಗ ಪ್ರವಾದಿಯಾದ ಹನನ್ಯನು ಪ್ರವಾದಿಯಾದ ಯೆರೆಮೀಯನ ಹೆಗಲಿನಿಂದ ನೊಗವನ್ನು ತೆಗೆದು ಮುರಿದುಹಾಕಿ ಎಲ್ಲಾ ಜನರ ಎದುರಿಗೆ - ಯೆಹೋವನು ಹೇಳಿರುವದೇನಂದರೆ -",
			"11": "<sup>11</sup> ನಾನು ಹೀಗೆಯೇ ಬಾಬೆಲಿನ ಅರಸನಾದ ನೆಬೂಕದ್ನೆಚ್ಚರನ ನೊಗವನ್ನು ಎರಡು ವರುಷದೊಳಗಾಗಿ ಸಮಸ್ತ ಜನಾಂಗಗಳ ಹೆಗಲಿನಿಂದ ತೆಗೆದು ಮುರಿದುಹಾಕುವೆನು ಎಂದು ಹೇಳಿದನು. ಇದನ್ನು ಕೇಳಿ ಪ್ರವಾದಿಯಾದ ಯೆರೆಮೀಯನು ಅಲ್ಲಿಂದ ಹೊರಟು ಹೋದನು.",
			"12": "<sup>12</sup> ಪ್ರವಾದಿಯಾದ ಹನನ್ಯನು ಪ್ರವಾದಿಯಾದ ಯೆರೆಮೀಯನ ಹೆಗಲಿನಿಂದ ನೊಗವನ್ನು ತೆಗೆದು ಮುರಿದ ಮೇಲೆ ಯೆಹೋವನು ಯೆರೆಮೀಯನಿಗೆ ಈ ಮಾತನ್ನು ದಯಪಾಲಿಸಿದನು -",
			"13": "<sup>13</sup> ನೀನು ಹೋಗಿ ಹನನ್ಯನಿಗೆ - ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ನೀನು ಮರದ ನೊಗಗಳನ್ನು ಮುರಿದುಬಿಟ್ಟಿ, ಅವುಗಳಿಗೆ ಬದಲಾಗಿ ಕಬ್ಬಿಣದ ನೊಗಗಳನ್ನು ಮಾಡು,",
			"14": "<sup>14</sup> ಏಕಂದರೆ ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರೂ ಸೇನಾಧೀಶ್ವರನೂ ಆದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಈ ಎಲ್ಲಾ ಜನಾಂಗಗಳು ಬಾಬೆಲಿನ ಅರಸನಾದ ನೆಬೂಕದ್ನೆಚ್ಚರನನ್ನು ಸೇವಿಸಲೆಂದು ನಾವು ಅವುಗಳ ಹೆಗಲಿಗೆ ಕಬ್ಬಿಣದ ನೊಗವನ್ನು ಹೇರಿದ್ದೇನೆ, ಅವು ಅವನನ್ನು ಸೇವಿಸೇ ಸೇವಿಸುವವು; ಇದಲ್ಲದೆ ಭೂಜಂತುಗಳನ್ನೂ ಅವನಿಗೆ ಕೊಟ್ಟಿದ್ದೇನೆ ಎಂದು ಹೇಳು.",
			"15": "<sup>15</sup> ಆಗ ಪ್ರವಾದಿಯಾದ ಯೆರೆಮೀಯನು ಪ್ರವಾದಿಯಾದ ಹನನ್ಯನಿಗೆ - ಹನನ್ಯನೇ, ಕೇಳು, ಯೆಹೋವನು ನಿನ್ನನ್ನು ಕಳುಹಿಸಲಿಲ್ಲ; ಈ ಜನರು ಸುಳ್ಳನ್ನು ನಂಬುವಂತೆ ಮಾಡುತ್ತೀ.",
			"16": "<sup>16</sup> ಆದಕಾರಣ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಇಗೋ, ನಾನು ನಿನ್ನನ್ನು ಭೂಮಿಯ ಮೇಲಿಂದ ತೊಲಗಿಸುವೆನು; ನೀನು ಯೆಹೋವನಾದ ನನಗೆ ವಿರುದ್ಧವಾಗಿ ದ್ರೋಹದ ಮಾತನ್ನು ಆಡಿದ್ದರಿಂದ ಇದೇ ವರುಷ ಸಾಯುವಿ ಎಂದು ಹೇಳಿದನು.",
			"17": "<sup>17</sup> ಅದರಂತೆ ಪ್ರವಾದಿಯಾದ ಹನನ್ಯನು ಅದೇ ವರುಷ ಏಳನೆಯ ತಿಂಗಳಲ್ಲಿ ಸತ್ತನು."
		},
		"29": {
			"1": "<sup>1</sup> ಅರಸನಾದ ಯೆಕೊನ್ಯನು, ರಾಜಮಾತೆಯು, ಕಂಚುಕಿಗಳು, ಯೆಹೂದದ ಮತ್ತು ಯೆರೂಸಲೇಮಿನ ಸರದಾರರು, ಶಿಲ್ಪಿಗಳು, ಕಮ್ಮಾರರು, ಇವರು ಯೆರೂಸಲೇಮಿನಿಂದ ತೊಲಗಿದ ಮೇಲೆ",
			"2": "<sup>2</sup> ಪ್ರವಾದಿಯಾದ ಯೆರೆಮೀಯನು ಸೆರೆಹೋಗಿದ್ದ ಹಿರಿಯರಲ್ಲಿ ಉಳಿದವರಿಗೂ ಯಾಜಕರಿಗೂ ಪ್ರವಾದಿಗಳಿಗೂ ಅಂತು ನೆಬೂಕದ್ನೆಚ್ಚರನು ಯೆರೂಸಲೇಮಿನಿಂದ ಬಾಬೆಲಿಗೆ ಸೆರೆ ಒಯ್ದಿದ್ದ ಸಕಲಜನರಿಗೂ",
			"3": "<sup>3</sup> ಯೆಹೂದದ ಅರಸನಾದ ಚಿದ್ಕೀಯನಿಂದ ಬಾಬೆಲಿನ ಅರಸನಾದ ನೆಬೂಕದ್ನೆಚ್ಚರನ ಬಳಿಗೆ ರಾಯಭಾರಿಗಳಾಗಿ ಬಾಬೆಲಿಗೆ ಕಳುಹಿಸಲ್ಪಟ್ಟ ಶಾಫಾನನ ಮಗನಾದ ಎಲ್ಲಾಸ, ಹಿಲ್ಕೀಯನ ಮಗನಾದ ಗೆಮರ್ಯ, ಇವರ ಕೈಯಲ್ಲಿ ಯೆರೂಸಲೇಮಿನಿಂದ ಬರೆದು ರವಾನಿಸಿದ ಕಾಗದದ ಮಾತೇನಂದರೆ -",
			"4": "<sup>4</sup> ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರೂ ಸೇನಾಧೀಶ್ವರನೂ ಆದ ಯೆಹೋವನು ತಾನು ಯೆರೂಸಲೇಮಿನಿಂದ ಬಾಬೆಲಿಗೆ ಸಾಗಿಸಿದ ಸೆರೆಯವರೆಲ್ಲರಿಗೂ ಹೀಗೆ ಹೇಳುತ್ತಾನೆ -",
			"5": "<sup>5</sup> ನೀವು ಮನೆಗಳನ್ನು ಕಟ್ಟಿಕೊಂಡು ಅವುಗಳಲ್ಲಿ ವಾಸಿಸಿರಿ, ತೋಟಗಳನ್ನು ಮಾಡಿಕೊಂಡು ಅವುಗಳ ಫಲವನ್ನು ಅನುಭವಿಸಿರಿ;",
			"6": "<sup>6</sup> ಸಂಸಾರಿಗಳಾಗಿ ಗಂಡುಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ಪಡೆಯಿರಿ; ನಿಮ್ಮ ಗಂಡುಗಳಿಗೆ ಹೆಣ್ಣುಗಳನ್ನು ತೆಗೆದುಕೊಳ್ಳಿರಿ, ನಿಮ್ಮ ಹೆಣ್ಣುಗಳನ್ನು ಗಂಡುಗಳಿಗೆ ಕೊಡಿರಿ; ಅವರೂ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ಪಡೆಯಲಿ; ನೀವು ಇರುವಲ್ಲಿಯೇ ವೃದ್ಧಿಯಾಗಿರಿ, ಕಡಿಮೆಯಾಗದಿರಿ.",
			"7": "<sup>7</sup> ನಾನು ಯಾವ ಪಟ್ಟಣಕ್ಕೆ ನಿಮ್ಮನ್ನು ಸಾಗಿಸಿದೆನೋ ಅದರ ಕ್ಷೇಮವನ್ನು ಹಾರೈಸಿ ಅದಕ್ಕಾಗಿ ಯೆಹೋವನನ್ನು ಪ್ರಾರ್ಥಿಸಿರಿ; ಅದರ ಕ್ಷೇಮವೇ ನಿಮ್ಮ ಕ್ಷೇಮ.",
			"8": "<sup>8</sup> ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರೂ ಸೇನಾಧೀಶ್ವರನೂ ಆದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ನಿಮ್ಮ ಮಧ್ಯದಲ್ಲಿರುವ ನಿಮ್ಮ ಪ್ರವಾದಿಗಳಿಂದಲೂ ಶಕುನದವರಿಂದಲೂ ಮೋಸಹೋಗಬೇಡಿರಿ; ನಿಮಗಾಗಿ ಕನಸುಕಂಡು ಹೇಳುವವರಿಗೆ ಕಿವಿಗೊಡದಿರಿ;",
			"9": "<sup>9</sup> ಅವರು ನನ್ನ ಹೆಸರಿನಲ್ಲಿ ನಿಮಗೆ ಸುಳ್ಳನ್ನು ಸಾರುತ್ತಾರೆ; ನಾನು ಅವರನ್ನು ಕಳುಹಿಸಲಿಲ್ಲ. ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"10": "<sup>10</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಬಾಬೆಲ್ ರಾಜ್ಯವು ಎಪ್ಪತ್ತು ವರುಷ ಪ್ರಬಲಿಸಿದ ಮೇಲೆ ನಾನು ನಿಮ್ಮನ್ನು ಕಟಾಕ್ಷಿಸಿ ಈ ಸ್ಥಳಕ್ಕೆ ತಿರಿಗಿ ಬರಮಾಡುವೆನೆಂಬ ನನ್ನ ಶುಭವಾಕ್ಯವನ್ನು ನಿಮಗಾಗಿ ನೆರವೇರಿಸುವೆನು.",
			"11": "<sup>11</sup> ಇವರಿಗೆ ಗತಿಯಾಗಲಿ, ನಿರೀಕ್ಷೆಯಿರಲಿ ಎಂದು ನಾನು ನಿಮ್ಮ ವಿಷಯದಲ್ಲಿ ಮಾಡಿಕೊಳ್ಳುತ್ತಿರುವ ಆಲೋಚನೆಗಳನ್ನು ನಾನೇ ಬಲ್ಲೆನು; ಅವು ಅಹಿತದ ಯೋಚನೆಗಳಲ್ಲ, ಹಿತದ ಯೋಚನೆಗಳೇ.",
			"12": "<sup>12</sup> ನೀವು ನನಗೆ ಮೊರೆಯಿಡುವಿರಿ, ನನ್ನನ್ನು ಪ್ರಾರ್ಥಿಸಲು ಹೋಗುವಿರಿ; ನಾನು ಕಿವಿಗೊಡುವೆನು.",
			"13": "<sup>13</sup> ನೀವು ನನ್ನನ್ನು ಹುಡುಕುವಿರಿ, ಮನಃಪೂರ್ವಕವಾಗಿ ಹುಡುಕಿದಾಗ ನನ್ನನ್ನು ಕಂಡುಕೊಳ್ಳುವಿರಿ.",
			"14": "<sup>14</sup> ನಾನು ನಿಮಗೆ ದೊರೆಯುವೆನು, ನಿಮ್ಮನ್ನು ನಿಮ್ಮ ದುರವಸ್ಥೆಯಿಂದ ತಪ್ಪಿಸಿ ನಾನು ನಿಮ್ಮನ್ನು ಅಟ್ಟಿಬಿಟ್ಟಿದ್ದ ಸಮಸ್ತದೇಶಗಳಿಂದಲೂ ಸಕಲಜನಾಂಗಗಳ ಮಧ್ಯದಿಂದಲೂ ಒಟ್ಟುಗೂಡಿಸಿ ನಿಮ್ಮನ್ನು ಯಾವ ಸ್ಥಳದಿಂದ ಸೆರೆಗೆ ಸಾಗಿಸಿದನೋ ಅಲ್ಲಿಗೆ ತಿರಿಗಿ ಬರಮಾಡುವೆನು. ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"15": "<sup>15</sup> (ನೀವೋ - ಯೆಹೋವನು ನಮಗಾಗಿ ಬಾಬೆಲಿನಲ್ಲೇ ಪ್ರವಾದಿಗಳನ್ನು ಏರ್ಪಡಿಸಿದ್ದಾನಲ್ಲಾ ಅಂದುಕೊಂಡಿದ್ದೀರಿ.)",
			"16": "<sup>16</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ದಾವೀದನ ಸಿಂಹಾಸನಾರೂಢನಾದ ಅರಸನ ವಿಷಯವಾಗಿಯೂ ನಿಮ್ಮೊಂದಿಗೆ ಸೆರೆಹೋಗದ ನಿಮ್ಮ ಸಹೋದರರಾದ ಈ ಪಟ್ಟಣದ ಸಕಲನಿವಾಸಿಗಳ ವಿಷಯವಾಗಿಯೂ ಇಂತೆನ್ನುತ್ತಾನೆ -",
			"17": "<sup>17</sup> ಆಹಾ, ನಾನು ಅವರ ಮೇಲೆ ಖಡ್ಗಕ್ಷಾಮವ್ಯಾಧಿಗಳನ್ನು ಬರಮಾಡಿ ಯಾರೂ ತಿನ್ನದ ಹಾಗೆ ಕೆಟ್ಟು ಕೇವಲ ಅಸಹ್ಯವಾದ ಅಂಜೂರದ ಹಣ್ಣುಗಳ ಗತಿಗೆ ಅವರನ್ನು ತರುವೆನು,",
			"18": "<sup>18</sup> ನಾನು ಖಡ್ಗಕ್ಷಾಮವ್ಯಾಧಿಗಳಿಂದ ಅವರನ್ನು ಹಿಂದಟ್ಟುತ್ತಾ ಲೋಕದ ಸಮಸ್ತರಾಜ್ಯಗಳಿಗೆ ಭಯಾಸ್ಪದವಾಗುವಂತೆಯೂ ನಾನು ಅವರನ್ನು ಅಟ್ಟಿಬಿಟ್ಟಿರುವ ಸಕಲ ಜನಾಂಗಗಳಲ್ಲಿ ಶಾಪ, ಬೆರಗಿನ ಸಿಳ್ಳು, ದೂಷಣೆ, ಇವುಗಳಿಗೆ ಗುರಿಯಾಗುವಂತೆಯೂ ಮಾಡುವೆನು.",
			"19": "<sup>19</sup> ಅವರು ನನ್ನ ಮಾತುಗಳನ್ನು ಕೇಳಲಿಲ್ಲವಲ್ಲಾ; ನಾನು ನನ್ನ ಸೇವಕರಾದ ಪ್ರವಾದಿಗಳನ್ನು ಅವರ ಬಳಿಗೆ ಸಾವಕಾಶಮಾಡದೆ ಕಳುಹಿಸಿ ಹೇಳಿಸುತ್ತಾ ಬಂದರೂ ಅವರು ಕೇಳಲೊಲ್ಲದೆ ಹೋದರು. ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"20": "<sup>20</sup> ನಾನು ಯೆರೂಸಲೇಮಿನಿಂದ ಬಾಬೆಲಿಗೆ ಸಾಗಿಸಿದ ಸೆರೆಯವರೇ, ನೀವೆಲ್ಲರೂ ಯೆಹೋವನ ವಾಕ್ಯವನ್ನು ಕೇಳಿರಿ-",
			"21": "<sup>21</sup> ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರೂ ಸೇನಾಧೀಶ್ವರನೂ ಆದ ಯೆಹೋವನು ತನ್ನ ಹೆಸರೆತ್ತಿ ಸುಳ್ಳನ್ನು ಸಾರುವ ಕೊಲಾಯನ ಮಗನಾದ ಅಹಾಬ, ಮಾಸೇಯನ ಮಗನಾದ ಚಿದ್ಕೀಯ ಇವರ ವಿಷಯವಾಗಿ ಇಂತೆನ್ನುತ್ತಾನೆ - ಇಗೋ, ನಾನು ಇವರನ್ನು ಬಾಬೆಲಿನ ಅರಸನಾದ ನೆಬೂಕದ್ನೆಚ್ಚರನ ಕೈಗೆ ಕೊಡುವೆನು; ಅವನು ನಿಮ್ಮ ಕಣ್ಣೆದುರಿಗೆ ಇವರನ್ನು ಕೊಲ್ಲಿಸುವನು;",
			"22": "<sup>22</sup> ಇವರ ಚರಿತ್ರೆಯನ್ನು ಬಾಬೆಲಿನಲ್ಲಿ ಸೆರೆಯಾಗಿರುವ ಯೆಹೂದ್ಯರೆಲ್ಲರು ಶಪಿಸುವ ಮಾತಾಗಿ ತೆಗೆದುಕೊಂಡು ಬಾಬೆಲಿನ ಅರಸನು ಬೆಂಕಿಯಲ್ಲಿ ಸುಟ್ಟುಬಿಟ್ಟ ಚಿದ್ಕೀಯನ ಮತ್ತು ಅಹಾಬನ ಗತಿಯನ್ನು ಯೆಹೋವನು ನಿನಗೆ ತರಲಿ ಎಂದು ಶಪಿಸುವರು;",
			"23": "<sup>23</sup> ಇವರು ಇಸ್ರಾಯೇಲಿನಲ್ಲಿ ದುರಾಚಾರವನ್ನು ನಡಿಸಿ ನೆರೆಯವರ ಹೆಂಡಿರಲ್ಲಿ ವ್ಯಭಿಚಾರಮಾಡಿ ನಾನು ಆಜ್ಞಾಪಿಸಿದ ಮಾತುಗಳನ್ನು ನನ್ನ ಹೆಸರಿನ ಮೇಲೆ ಸುಳ್ಳಾಗಿ ಸಾರಿದರಲ್ಲಾ; ನಾನೇ ಬಲ್ಲೆ, ನಾನೇ ಸಾಕ್ಷಿ ಎಂದು ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ ಎಂಬದೇ.",
			"24": "<sup>24</sup> [ಆಮೇಲೆ ಯೆಹೋವನು ಯೆರೆಮೀಯನಿಗೆ ಈ ಅಪ್ಪಣೆಯನ್ನು ಕೊಟ್ಟನು] - ನೀನು ನೆಹೆಲಾಮ್ಯನಾದ ಶೆಮಾಯನಿಗೆ ಹೀಗೆ ತಿಳಿಸಬೇಕು -",
			"25": "<sup>25</sup> ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರೂ ಸೇನಾಧೀಶ್ವರನೂ ಆದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ನೀನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿರುವ ಎಲ್ಲಾ ಜನರಿಗೂ ಯಾಜಕನಾದ ಮಾಸೇಯನ ಮಗನಾಗಿರುವ ಚೆಫನ್ಯನಿಗೂ ಸಮಸ್ತ ಯಾಜಕರಿಗೂ ನಿನ್ನ ಹೆಸರಿನ ಮೇಲೆ ಕಳುಹಿಸಿದ ಕಾಗದದಲ್ಲಿ -",
			"26": "<sup>26</sup> ಯೆಹೋವನು ನಿನ್ನನ್ನು ಯೆಹೋಯಾದನ ಸ್ಥಾನದಲ್ಲಿ ಯಾಜಕನನ್ನಾಗಿ ನೇಮಿಸಿ ಯಾವಯಾವನು ಮೈದುಂಬಿ ಪ್ರವಾದಿಯಾಗಿ ನಟಿಸುತ್ತಾನೋ ಅವನನ್ನು ಕೋಳಕ್ಕೆ ಹಾಕಿ ಕೊರಳಿಗೆ ಕವೆಯೊಡ್ಡುವ ಅಧಿಕಾರವನ್ನು ತನ್ನ ಆಲಯದಲ್ಲಿ ನಿನಗೆ ವಹಿಸಿದ್ದಾನಲ್ಲಾ.",
			"27": "<sup>27</sup> ಹೀಗಿರಲು ನಿಮ್ಮ ಮಧ್ಯದಲ್ಲಿ ಪ್ರವಾದಿಯಾಗಿ ನಟಿಸುವ ಅನಾತೋತಿನವನಾದ ಯೆರೆಮೀಯನನ್ನು ನೀನು ಏಕೆ ಖಂಡಿಸಲಿಲ್ಲ?",
			"28": "<sup>28</sup> ಇವನು - ಪರದೇಶ ವಾಸವು ನಿಮಗೆ ದೀರ್ಘವಾಗುವದು, ನೀವು ಮನೆಗಳನ್ನು ಕಟ್ಟಿಕೊಂಡು ಅವುಗಳಲ್ಲಿ ವಾಸಿಸಿರಿ, ತೋಟಗಳನ್ನು ಮಾಡಿಕೊಂಡು ಫಲವನ್ನು ಅನುಭವಿಸಿರಿ ಎಂದು ಬಾಬೆಲಿನಲ್ಲಿರುವ ನಮಗೆ ಹೇಳಿ ಕಳುಹಿಸಿದ್ದಾನೆ ನೋಡು ಎಂಬದಾಗಿ ಬರೆದ ಕಾರಣ [ನಾನು ನಿನ್ನನ್ನು ದಂಡಿಸುವದು ಖಂಡಿತ].",
			"29": "<sup>29</sup> ಯಾಜಕನಾದ ಚೆಫನ್ಯನು ಈ ಕಾಗದವನ್ನು ಪ್ರವಾದಿಯಾದ ಯೆರೆಮೀಯನ ಮುಂದೆ ಓದಿದನಂತರ",
			"30": "<sup>30</sup> ಯೆಹೋವನು ಯೆರೆಮೀಯನಿಗೆ ಈ ಅಪ್ಪಣೆ ಮಾಡಿದನು -",
			"31": "<sup>31</sup> ಸೆರೆಯವರೆಲ್ಲರಿಗೆ ಹೀಗೆ ಹೇಳಿ ಕಳುಹಿಸು - ನೆಹೆಲಾಮ್ಯನಾದ ಶೆಮಾಯನ ವಿಷಯದಲ್ಲಿ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ನಾನು ಕಳುಹಿಸದೆ ಹೋದರೂ ಶೆಮಾಯನು ನಿಮಗೆ ಪ್ರವಾದನೆಮಾಡಿ ಸುಳ್ಳಿನಲ್ಲಿ ನಂಬಿಕೆಯನ್ನು ಹುಟ್ಟಿಸಿದ ಕಾರಣ",
			"32": "<sup>32</sup> ಇಗೋ, ನಾನು ನೆಹೆಲಾಮ್ಯನಾದ ಶೆಮಾಯನನ್ನೂ ಅವನ ಸಂತತಿಯವರನ್ನೂ ದಂಡಿಸುವೆನು; ಅವನು ಯೆಹೋವನಾದ ನನಗೆ ವಿರುದ್ಧವಾಗಿ ದ್ರೋಹದ ಮಾತನ್ನು ಆಡಿದ್ದರಿಂದ ಈ ಜನರ ಮಧ್ಯದಲ್ಲಿ ವಾಸಿಸಲು ಅವನಿಗೆ ಯಾವ ಸಂತಾನವೂ ಇರದು; ನಾನು ನನ್ನ ಜನರಿಗೆ ಉಂಟುಮಾಡಬೇಕೆಂದಿರುವ ಮೇಲನ್ನು ಅವನು ನೋಡದೆ ಹೋಗುವನು; ಇದು ಯೆಹೋವನ ನುಡಿ."
		},
		"30": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಯೆರೆಮೀಯನಿಗೆ ಈ ಮಾತನ್ನು ದಯಪಾಲಿಸಿದನು -",
			"2": "<sup>2</sup> ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರಾದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ನಾನು ನಿನಗೆ ಹೇಳಿರುವ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ಗ್ರಂಥವಾಗಿ ಬರೆ.",
			"3": "<sup>3</sup> ಇಗೋ ನಾನು ನನ್ನ ಜನರಾದ ಇಸ್ರಾಯೇಲ್ಯರನ್ನೂ ಯೆಹೂದ್ಯರನ್ನೂ ಅವರ ದುರವಸ್ಥೆಯಿಂದ ತಪ್ಪಿಸುವ ದಿನಗಳು ಬರುವವು. ಆಗ ನಾನು ಅವರನ್ನು ಅವರ ಪಿತೃಗಳಿಗೆ ಅನುಗ್ರಹಿಸಿದ ದೇಶಕ್ಕೆ ಪುನಃ ಬರಮಾಡುವೆನು. ಅವರು ಅದನ್ನು ಅನುಭವಿಸುವರು. ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"4": "<sup>4</sup> ಯೆಹೋವನು ಇಸ್ರಾಯೇಲಿನ ಮತ್ತು ಯೆಹೂದದ ವಿಷಯವಾಗಿ ಈ ಮಾತುಗಳನ್ನು ಹೇಳಿದನು -",
			"5": "<sup>5</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ, ಭೀತಿಯಿಂದಾಗುವ ಶಬ್ದವು ಕೇಳಿಸಿದೆ; ಭಯವೇ ಹೊರತು ಸಮಾಧಾನವಿಲ್ಲ;",
			"6": "<sup>6</sup> ವಿಚಾರಿಸಿ ನೋಡಿರಿ, ಗಂಡಸು ಪ್ರಸವವೇದನೆ ಪಡುವದುಂಟೇ? ಪ್ರತಿಯೊಬ್ಬನು ಹೆರುವವಳಂತೆ ಸೊಂಟವನ್ನು ಹಿಸಕಿಕೊಳ್ಳುವದು, ಎಲ್ಲಾ ಮುಖಗಳು ಬಿಳಿಚಿಕೊಂಡಿರುವದು, ಇವು ಏಕೆ ನನ್ನ ಕಣ್ಣಿಗೆ ಬೀಳುತ್ತವೆ?",
			"7": "<sup>7</sup> ಅಯ್ಯೋ, ಆ ದಿನವು ಘೋರವಾದದ್ದು, ಅದಕ್ಕೆ ಎಣೆಯಿಲ್ಲ; ಅದು ಯಾಕೋಬ್ಯರಿಗೆ ಇಕ್ಕಟ್ಟಿನ ದಿನ; ಆದರೂ ಅದರಿಂದ ಪಾರಾಗುವರು.",
			"8": "<sup>8</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ನಾನು ಆ ದಿನದಲ್ಲಿ ಅವನು ಹೇರಿದ ನೊಗವನ್ನು ಅವರ ಹೆಗಲಿನಿಂದ ಮುರಿದುಬಿಟ್ಟು ಕಣ್ಣಿಗಳನ್ನು ಕಿತ್ತುಹಾಕುವೆನು; ಇನ್ನು ಅನ್ಯರು ಅವರನ್ನು ಅಡಿಯಾಳಾಗಿ ಮಾಡಿಕೊಳ್ಳರು;",
			"9": "<sup>9</sup> ಅವರು ತಮ್ಮ ದೇವರಾದ ಯೆಹೋವನೆಂಬ ನನ್ನನ್ನೂ ನಾನು ಅವರಿಗಾಗಿ ಏರ್ಪಡಿಸುವ ರಾಜನಾದ ದಾವೀದನನ್ನೂ ಸೇವಿಸುವರು.",
			"10": "<sup>10</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ನನ್ನ ಸೇವಕ ಯಾಕೋಬೇ, ಭಯಪಡಬೇಡ! ಇಸ್ರಾಯೇಲೇ, ಅಂಜಬೇಡ! ಇಗೋ ನಾನು ನಿನ್ನನ್ನು ದೂರದೇಶದಿಂದ ಉದ್ಧರಿಸುವೆನು, ನಿನ್ನ ಸಂತಾನವನ್ನು ಸೆರೆಹೋದ ಸೀಮೆಯಿಂದ ರಕ್ಷಿಸುವೆನು; ಯಾಕೋಬು ಹಿಂದಿರುಗಿ ನೆಮ್ಮದಿಯಾಗಿಯೂ ಹಾಯಾಗಿಯೂ ಇರುವದು;",
			"11": "<sup>11</sup> ಯಾರೂ ಅದನ್ನು ಹೆದರಿಸರು. ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ನಾನು ನಿನ್ನನ್ನು ರಕ್ಷಿಸಲು ನಿನ್ನೊಂದಿಗಿದ್ದೇನೆ; ನಾನು ಯಾವ ಜನಾಂಗಗಳಲ್ಲಿಗೆ ನಿನ್ನನ್ನು ಅಟ್ಟಿ ಚದರಿಸಿದೆನೋ ಆ ಜನಾಂಗಗಳನ್ನೆಲ್ಲಾ ನಿರ್ಮೂಲಮಾಡುವೆನು, ನಿನ್ನನ್ನೋ ನಿರ್ಮೂಲಮಾಡೆನು; ನಿನ್ನನ್ನು ಮಿತಿಮೀರಿ ಶಿಕ್ಷಿಸೆನು. ಆದರೆ ಶಿಕ್ಷಿಸದೆ ಬಿಡುವದಿಲ್ಲ.",
			"12": "<sup>12</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ನಿನ್ನ ಗಾಯವು ಗುಣಹೊಂದದು, ನಿನ್ನ ಬಾಸುಂಡೆಯು ಘೋರವಾಗಿದೆ.",
			"13": "<sup>13</sup> ನಿನ್ನ ಭಾಗಕ್ಕೆ ಯಾರೂ ಇಲ್ಲ, ನಿನ್ನ ವ್ರಣಕ್ಕೆ ಔಷಧವಿಲ್ಲ, ನಿನಗೆ ವಾಸಿಯಾಗದು.",
			"14": "<sup>14</sup> ನಿನ್ನ ಮಿಂಡರಾರೂ ನಿನ್ನನ್ನು ಹುಡುಕುವದಿಲ್ಲ, ಮರೆತುಬಿಟ್ಟರು; ನಿನ್ನ ಅಪರಾಧವು ಹೆಚ್ಚಿ ಪಾಪಗಳು ಬಹಳವಾದದರಿಂದ ನಾನು ಶತ್ರುವಾಗಿ ನಿನ್ನನ್ನು ಹೊಡೆದೆನು, ಕ್ರೂರನಾಗಿ ದಂಡಿಸಿದೆನು.",
			"15": "<sup>15</sup> ಅಯ್ಯೋ, ನೋವು ನಿಲ್ಲದು ಎಂದು ನಿನ್ನ ವ್ರಣಕ್ಕಾಗಿ ಏಕೆ ಅರಚಿಕೊಳ್ಳುತ್ತೀ? ನಿನ್ನ ಅಪರಾಧವು ಹೆಚ್ಚಿ ಪಾಪಗಳು ಬಹಳವಾದದರಿಂದ ನಿನಗೆ ಇದನ್ನೆಲ್ಲಾ ಮಾಡಿದೆನು.",
			"16": "<sup>16</sup> ಹೀಗಿರಲು ನಿನ್ನನ್ನು ನುಂಗುವವರೆಲ್ಲರು ನುಂಗಲ್ಪಡುವರು; ನಿನ್ನ ಶತ್ರುಗಳೆಲ್ಲಾ ಒಬ್ಬನೂ ತಪ್ಪದೆ ಸೆರೆಗೆ ಹೋಗುವರು, ನಿನ್ನನ್ನು ಸೂರೆಮಾಡುವವರು ಸೂರೆಯಾಗುವರು, ನಿನ್ನನ್ನು ಕೊಳ್ಳೆ ಹೊಡೆಯುವವರನ್ನು ಕೊಳ್ಳೆಗೆ ಈಡುಮಾಡುವೆನು.",
			"17": "<sup>17</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಇಗೋ, ಇದು ಚೀಯೋನ್, ಯಾರಿಗೂ ಬೇಡವಾಗಿರುವ ನಗರಿ ಎಂದು ಜನರು ಹೇಳಿ ನಿನ್ನನ್ನು ಭ್ರಷ್ಟಳೆಂದದ್ದನ್ನು ನಾನು ಸಹಿಸಲಾರದೆ ನಿನಗೆ ಗುಣಪಡಿಸಿ ನಿನ್ನ ಬಾಸುಂಡೆಗಳನ್ನು ವಾಸಿಮಾಡುವೆನು.",
			"18": "<sup>18</sup> ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ಆಹಾ, ನಾನು ಯಾಕೋಬಿನ ಮನೆಗಳ ದುರವಸ್ಥೆಯನ್ನು ತಪ್ಪಿಸಿ ಅದರ ನಿವಾಸಗಳನ್ನು ಕರುಣಿಸುವೆನು; ಪಟ್ಟಣವು ತನ್ನ ಹಾಳುದಿಬ್ಬದ ಮೇಲೆ ಪುನಃ ಕಟ್ಟಲ್ಪಡುವದು, ಅರಮನೆಯು ತಾನಿದ್ದ ಸ್ಥಳದಲ್ಲೇ ನೆಲೆಯಾಗಿರುವದು.",
			"19": "<sup>19</sup> ಅವುಗಳೊಳಗಿಂದ ಸ್ತೋತ್ರವೂ ವಿನೋದಪಡುವವರ ಧ್ವನಿಯೂ ಕೇಳಿಸುವವು; ನಾನು ಆ ಜನರನ್ನು ಹೆಚ್ಚಿಸುವೆನು, ಇನ್ನು ಕೊಂಚವಾಗಿರರು, ಅವರನ್ನು ಘನಪಡಿಸುವೆನು, ಇನ್ನು ಹೀನರಾಗಿರರು.",
			"20": "<sup>20</sup> ಅವರ ಸಂತಾನವು ಪೂರ್ವದ ಸ್ಥಿತಿಯನ್ನು ಹೊಂದುವದು, ಅವರ ಸಮಾಜವು ನನ್ನ ಮುಂದೆ ನೆಲೆಯಾಗಿರುವದು, ಅವರನ್ನು ಬಾಧಿಸುವವರನ್ನೆಲ್ಲಾ ದಂಡಿಸುವೆನು.",
			"21": "<sup>21</sup> ಅವರಲ್ಲೇ ಒಬ್ಬನು ಅವರಿಗೆ ಪ್ರಭುವಾಗಿರುವನು, ಅವರ ವಂಶದವನೇ ಅವರನ್ನಾಳುವನು; ನಾನು ಅವನನ್ನು ನನ್ನ ಹತ್ತಿರಕ್ಕೆ ಬರಗೊಡಿಸಲಾಗಿ ಅವನು ನನ್ನ ಸನ್ನಿಧಾನಕ್ಕೆ ಸೇರುವನು; ನನ್ನನ್ನು ಸಮೀಪಿಸುವದಕ್ಕೆ ಯಾರು ಧೈರ್ಯಗೊಂಡಾರು? ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"22": "<sup>22</sup> ನೀವು ನನಗೆ ಜನರಾಗಿರುವಿರಿ, ನಾನು ನಿಮಗೆ ದೇವರಾಗಿರುವೆನು.",
			"23": "<sup>23</sup> ಆಹಾ, ಯೆಹೋವನ ರೋಷವೆಂಬ ಬಿರುಗಾಳಿಯು, ಬಡಿದುಕೊಂಡು ಹೋಗುವ ಗಾಳಿಯು, ಹೊರಟಿದೆ; ಅದು ದುಷ್ಟರ ತಲೆಯ ಮೇಲೆ ಹೊಡೆಯುವದು.",
			"24": "<sup>24</sup> ಯೆಹೋವನು ತನ್ನ ಹೃದಯಾಲೋಚನೆಯನ್ನು ನಡಿಸಿ ನೆರವೇರಿಸುವ ತನಕ ಆತನ ರೋಷವು ಹಿಂದಿರುಗದು; ಕಟ್ಟಕಡೆಯ ದಿನಗಳಲ್ಲಿ ನೀವು ಇದನ್ನು ಗ್ರಹಿಸುವಿರಿ."
		},
		"31": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಆ ಕಾಲದಲ್ಲಿ ನಾನು ಇಸ್ರಾಯೇಲಿನ ಸಕಲ ಗೋತ್ರಗಳವರಿಗೆ ದೇವರಾಗಿರುವೆನು, ಅವರು ನನಗೆ ಪ್ರಜೆಯಾಗಿರುವರು.",
			"2": "<sup>2</sup> ಇದು ಯೆಹೋವನ ನುಡಿ - ಹತಶೇಷವಾದ ಜನಕ್ಕೆ ಅರಣ್ಯದಲ್ಲಿ ದಯೆದೊರೆಯುವದು; ಇಸ್ರಾಯೇಲು ವಿಶ್ರಾಂತಿಯನ್ನು ಹುಡುಕುವದಕ್ಕೆ ಹೋಗುವಾಗ ಯೆಹೋವನು ದೂರದಿಂದ ಬಂದು ಅದಕ್ಕೆ ದರ್ಶನಕೊಟ್ಟು ಹೀಗನ್ನುವನು -",
			"3": "<sup>3</sup> ನಾನು ನಿನ್ನನ್ನು ಪ್ರೀತಿಸಿರುವದು ಶಾಶ್ವತ ಪ್ರೇಮದಿಂದಲೇ; ಆದಕಾರಣ ನಿನ್ನನ್ನು ಮಮತೆಯಿಂದ ಸೆಳೆದುಕೊಂಡಿದ್ದೇನೆ,",
			"4": "<sup>4</sup> ಇಸ್ರಾಯೇಲೆಂಬ ಯುವತಿಯೇ, ನಾನು ನಿನ್ನ ಹಾಳುಪ್ರದೇಶಗಳನ್ನು ಪುನಃ ಕಟ್ಟುವೆನು; ಅವು ಕಟ್ಟಡಗಳಾಗುವವು. ನೀನು ಮತ್ತೆ ದಮ್ಮಡಿಗಳಿಂದ ವೈಭವಗೊಂಡು ವಿನೋದಪಡುವವರ ನಾಟ್ಯಗಳಲ್ಲಿ ಸೇರಿಕೊಳ್ಳುವಿ.",
			"5": "<sup>5</sup> ನೀನು ತಿರಿಗಿ ಸಮಾರ್ಯದ ಗುಡ್ಡಗಳಲ್ಲಿ ತೋಟಗಳನ್ನು ಮಾಡಿಕೊಳ್ಳುವಿ; ನೆಡುವವರು ನೆಟ್ಟು ಫಲವನ್ನನುಭವಿಸುವರು.",
			"6": "<sup>6</sup> ಒಂದು ದಿನವು ಬರುವದು, ಆಗ ಎಫ್ರಾಯೀಮಿನ ಗುಡ್ಡಗಳ ಮೇಲಿರುವ ಕಾವಲುಗಾರರು - ಏಳಿರಿ, ಚೀಯೋನಿಗೆ, ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಸನ್ನಿಧಾನಕ್ಕೆ ಹೋಗೋಣ ಬನ್ನಿ ಎಂದು ಕೂಗುವರು.",
			"7": "<sup>7</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಯಾಕೋಬಿನ ನಿಮಿತ್ತ ಹರ್ಷಧ್ವನಿಗೈಯಿರಿ, ಜನಾಂಗಗಳ ಶಿರೋಮಣಿಯ ವಿಷಯದಲ್ಲಿ ಕೇಕೆಹಾಕಿರಿ; ಯೆಹೋವನೇ, ಇಸ್ರಾಯೇಲಿನ ಶೇಷವಾದ ನಿನ್ನ ಜನವನ್ನು ರಕ್ಷಿಸು ಎಂದು ಘೋಷಿಸಿ ಸ್ತುತಿಸಿರಿ.",
			"8": "<sup>8</sup> ಇಗೋ, ನಾನು ಅವರನ್ನು ಉತ್ತರ ದೇಶದಿಂದ ಬರಮಾಡುವೆನು, ದಿಗಂತಗಳಿಂದ ಅವರನ್ನು ಕೂಡಿಸುವೆನು, ಅವರೊಂದಿಗೆ ಕುರುಡರನ್ನೂ ಕುಂಟರನ್ನೂ ಗರ್ಭಿಣಿಯರನ್ನೂ ದಿನತುಂಬಿದ ಬಸುರಿಯರನ್ನೂ ಒಟ್ಟಿಗೆ ಕರತರುವೆನು; ದೊಡ್ಡಗುಂಪಾಗಿ ಇಲ್ಲಿಗೆ ಹಿಂದಿರುಗುವರು.",
			"9": "<sup>9</sup> ಅವರು ಅಳುತ್ತಾ ನಡೆದು ಬರುವರು, ಅವರ ವಿಜ್ಞಾಪನೆಗಳಿಂದಲೇ ಅವರನ್ನು ಮುಂದರಿಸುವೆನು; ಎಡವದ ಸಮಮಾರ್ಗದಲ್ಲಿ ನಡಿಸುತ್ತಾ ತುಂಬಿದ ತೊರೆಗಳ ಬಳಿಗೆ ಬರಮಾಡುವೆನು; ನಾನು ಇಸ್ರಾಯೇಲಿಗೆ ತಂದೆ, ಎಫ್ರಾಯೀಮು ನನ್ನ ಹಿರಿಯ ಮಗನಲ್ಲವೆ.",
			"10": "<sup>10</sup> ಜನಾಂಗಗಳೇ, ಯೆಹೋವನ ವಾಕ್ಯವನ್ನು ಕೇಳಿರಿ, ದೂರದ್ವೀಪಗಳಲ್ಲಿ ಸಾರಿರಿ! ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಚದರಿಸಿದಾತನು ಕುರುಬನು ಮಂದೆಯನ್ನು ಕಾಯುವ ಕುರುಬನಂತೆ ಅವರನ್ನು ಕೂಡಿಸಿ ಕಾಪಾಡುವನು ಎಂದು ಪ್ರಕಟಿಸಿರಿ.",
			"11": "<sup>11</sup> ಯೆಹೋವನು ಯಾಕೋಬ್ಯರನ್ನು ವಿಮೋಚಿಸಿದ್ದಾನಲ್ಲಾ, ಅವರಿಗಿಂತ ಬಲಿಷ್ಟರ ಕೈಯಿಂದ ಅವರನ್ನು ಬಿಡಿಸಿದ್ದಾನಷ್ಟೆ.",
			"12": "<sup>12</sup> ಅವರು ಬಂದು ಚೀಯೋನ್ ಶಿಖರದಲ್ಲಿ ಹಾಡುವರು; ಧಾನ್ಯ, ದ್ರಾಕ್ಷಾರಸ, ಎಣ್ಣೆ, ಕರು, ಕುರಿಮರಿ, ಅಂತು ಯೆಹೋವನು ಅನುಗ್ರಹಿಸುವ ಎಲ್ಲಾ ಮೇಲುಗಳನ್ನು ಅನುಭವಿಸಲು ಪ್ರವಾಹ ಪ್ರವಾಹವಾಗಿ ಬರುವರು; ಅವರ ಆತ್ಮವು ಹದವಾಗಿ ನೀರು ಹಾಯಿಸಿದ ತೋಟದಂತಿರುವದು; ಅವರು ಇನ್ನು ಕಳೆಗುಂದರು.",
			"13": "<sup>13</sup> ಆಗ ಯುವತಿಯು ನಾಟ್ಯವಾಡುತ್ತಾ ಉಲ್ಲಾಸಿಸುವಳು; ಯುವಕರೂ ವೃದ್ಧರೂ ಜೊತೆಯಾಗಿ ಹರ್ಷಿಸುವರು; ನಾನು ಅವರ ದುಃಖವನ್ನು ನೀಗಿಸಿ ಸಂತೋಷವನ್ನುಂಟುಮಾಡಿ ಅವರು ತಮ್ಮ ವ್ಯಸನವನ್ನು ಬಿಟ್ಟು ಆನಂದಿಸುವಂತೆ ಅವರನ್ನು ಸಂತೈಸುವೆನು.",
			"14": "<sup>14</sup> ಯಾಜಕರನ್ನು ಮೃಷ್ಟಭೋಜನದಿಂದ ತಂಪುಗೊಳಿಸುವೆನು; ನಾನು ಅನುಗ್ರಹಿಸುವ ಮೇಲುಗಳನ್ನು ನನ್ನ ಜನರು ಸಮೃದ್ಧಿಯಾಗಿ ಅನುಭವಿಸುವರು. ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"15": "<sup>15</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ರಾಮದಲ್ಲಿ ಬೊಬ್ಬೆಯು ಕೇಳಿಸುತ್ತದೆ, ಗೋಳಾಟವೂ ಘೋರರೋದನವೂ ಉಂಟಾಗಿವೆ; ರಾಹೇಲಳು ತನ್ನ ಮಕ್ಕಳಿಗೋಸ್ಕರ ಅತ್ತತ್ತು ಅವರು ಇಲ್ಲದೆಹೋದದ್ದಕ್ಕಾಗಿ ಸಮಾಧಾನಹೊಂದಲೊಲ್ಲದೆ ಇದ್ದಾಳೆ.",
			"16": "<sup>16</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ನಿನ್ನ ಧ್ವನಿಯು ಗೋಳಾಗದಿರಲಿ, ನಿನ್ನ ಕಣ್ಣು ನೀರು ಸುರಿಸದಿರಲಿ; ನಿನ್ನ ಪ್ರಯಾಸವು ಸಾರ್ಥಕವಾಗುವದು; ಇದು ಯೆಹೋವನ ನುಡಿ; ನಿನ್ನ ಮಕ್ಕಳು ಶತ್ರುವಿನ ದೇಶದಿಂದ ಹಿಂದಿರುಗುವರು;",
			"17": "<sup>17</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ನಿನಗೆ ಮುಂದೆ ಒಳ್ಳೆಯ ಗತಿಯಾಗುವದೆಂದು ನಿರೀಕ್ಷೆಯಿದೆ. ನಿನ್ನ ಮಕ್ಕಳು ಹಿಂದಿರುಗಿ ಸ್ವಂತ ಸೀಮೆಯನ್ನು ಸೇರುವರು.",
			"18": "<sup>18</sup> ಎಫ್ರಾಯೀಮು ಹೀಗೆ ಪ್ರಲಾಪಿಸಿಕೊಳ್ಳುವದನ್ನು ಕೇಳೇ ಕೇಳಿದ್ದೇನೆ - ನೀನು ನನ್ನನ್ನು ಶಿಕ್ಷಿಸಿದ್ದೀ, ಪಳಗದ ಹೋರಿಯಂತೆ ಶಿಕ್ಷೆಯನ್ನು ಅನುಭವಿಸಿದೆನು; ನನ್ನನ್ನು ತಿರುಗಿಸು, ನೀನು ತಿರುಗಿಸಿದ ಹಾಗೆ ತಿರುಗುವೆನು; ನೀನು ನನ್ನ ದೇವರಾದ ಯೆಹೋವನಲ್ಲವೆ.",
			"19": "<sup>19</sup> ತಿರುಗಿಸಲ್ಪಟ್ಟ ಮೇಲೆಯೇ ಪಶ್ಚಾತ್ತಾಪಪಟ್ಟೆನು; ತಿಳುವಳಿಕೆಯನ್ನು ಹೊಂದಿದ ಮೇಲೆಯೇ ತೊಡೆಯನ್ನು ಬಡಿದುಕೊಂಡೆನು; ನನ್ನ ಯೌವನದ ಅವಮಾನವು ನನ್ನ ಮೇಲೆ ಹೊತ್ತ ಕಾರಣ ಲಜ್ಜೆಗೊಂಡೆನು, ಹೌದು, ತುಂಬಾ ನಾಚಿಕೆಪಟ್ಟೆನು.",
			"20": "<sup>20</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಎಫ್ರಾಯೀಮು ನನಗೆ ಪ್ರಿಯಪುತ್ರನೋ, ಮುದ್ದುಮಗುವೋ, ಏನೋ? ಅವನ ಹೆಸರೆತ್ತಿದಾಗೆಲ್ಲಾ ಅವನನ್ನು ಜ್ಞಾಪಿಸಿಕೊಳ್ಳುತ್ತಲೇ ಬರುತ್ತೇನೆ; ಇದರಿಂದ ನನ್ನ ಕರುಳು ಅವನಿಗಾಗಿ ಮರುಗುತ್ತದಲ್ಲಾ; ಅವನನ್ನು ಕರುಣಿಸೇ ಕರುಣಿಸುವೆನು.",
			"21": "<sup>21</sup> ದಾರಿದೋರುವ ಕಂಬಗಳನ್ನೂ ಕೈಮರಗಳನ್ನೂ ನಿಲ್ಲಿಸಿಕೋ; ನೀನು ಯಾವ ಮಾರ್ಗದಲ್ಲಿ ಹೋಗಿಬಿಟ್ಟಿಯೋ ಅದಕ್ಕೆ ನಿನ್ನ ಮನಸ್ಸನ್ನು ತಿರುಗಿಸಿಕೋ; ಇಸ್ರಾಯೇಲೆಂಬ ಯುವತಿಯೇ, ತಿರುಗಿಕೋ, ಈ ನಿನ್ನ ಪಟ್ಟಣಗಳಿಗೆ ಹಿಂದಿರುಗು.",
			"22": "<sup>22</sup> ದ್ರೋಹಿಯಾದ ಮಗಳೇ, ಎಂದಿನವರೆಗೆ ಅತ್ತಿತ್ತ ಅಲೆದಾಡುವಿ? ಯೆಹೋವನು ಲೋಕದಲ್ಲಿ ಅಪೂರ್ವವಾದದ್ದನ್ನು ಉಂಟುಮಾಡಿದ್ದಾನೆ ನೋಡು, ಹೆಂಗಸು ಗಂಡಸನ್ನು ಕಾಪಾಡುವಳು.",
			"23": "<sup>23</sup> ಇಸ್ರಾಯೇಲಿನ ದೇವರೂ ಸೇನಾಧೀಶ್ವರನೂ ಆದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ನಾನು ನನ್ನ ಜನರ ದುರವಸ್ಥೆಯನ್ನು ತಪ್ಪಿಸುವಾಗ ಯೆಹೂದ ದೇಶದಲ್ಲಿಯೂ ಅವರ ಪಟ್ಟಣಗಳಲ್ಲಿಯೂ - ನ್ಯಾಯದ ನಿವಾಸವೇ, ಪರಿಶುದ್ಧ ಪರ್ವತವೇ, ಯೆಹೋವನು ನಿನ್ನನ್ನು ಆಶೀರ್ವದಿಸಲಿ ಎಂದು ಮತ್ತೆ ಮಾತಾಡುವರು.",
			"24": "<sup>24</sup> ಆ ಸಕಲ ಪಟ್ಟಣಗಳವರು, ವ್ಯವಸಾಯಗಾರರು, ಮಂದೆ ಮೇಯಿಸುತ್ತಾ ದೇಶದಲ್ಲಿ ಸಂಚರಿಸುವವರು, ಅಂತು ಯೆಹೂದ್ಯರೆಲ್ಲರೂ ಅಲ್ಲಿ ಒಟ್ಟಿಗೆ ವಾಸಿಸುವರು.",
			"25": "<sup>25</sup> ನಾನು ಬಳಲಿದವರನ್ನು ತಂಪುಗೊಳಿಸಿ ಕುಂದಿದವರೆಲ್ಲರನ್ನೂ ತೃಪ್ತಿಪಡಿಸುವೆನಷ್ಟೆ.",
			"26": "<sup>26</sup> (ಇದಾದ ಮೇಲೆ ನಾನು ಎಚ್ಚತ್ತು ನಿಜಸ್ಥಿತಿಯನ್ನು ನೋಡಿದೆನು; ಆಗ ನನ್ನ ಕನಸು ನನಗೆ ಮನೋಹರವಾಗಿ ಕಾಣಿಸಿತು.)",
			"27": "<sup>27</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಇಗೋ, ನಾನು ಇಸ್ರಾಯೇಲ್ ಯೆಹೂದ ಕ್ಷೇತ್ರಗಳಲ್ಲಿ ಮನುಷ್ಯರನ್ನೂ ಪಶುಗಳನ್ನೂ ಬಿತ್ತುವ ದಿನಗಳು ಬರುವವು.",
			"28": "<sup>28</sup> ನಾನು ಮೊದಲು ಇವರನ್ನು ಕಿತ್ತುಹಾಕಲು, ಕೆಡವಲು, ಹಾಳುಮಾಡಲು, ನಾಶಪಡಿಸಲು, ಬಾಧಿಸಲು ಹೇಗೆ ಎಚ್ಚರಗೊಂಡಿದ್ದೆನೋ ಹಾಗೆಯೇ ಇನ್ನು ಮೇಲೆ ಇವರನ್ನು ಕಟ್ಟಲು, ನೆಡಲು ಎಚ್ಚರಗೊಳ್ಳುವೆನು. ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"29": "<sup>29</sup> ತಂದೆಗಳು ಹುಳಿದ್ರಾಕ್ಷೆಯನ್ನು ತಿಂದರು, ಮಕ್ಕಳ ಹಲ್ಲುಗಳು ಚಳಿತುಹೋಗಿವೆ ಎಂದು ಆ ಕಾಲದಲ್ಲಿ ಜನರು ಮತ್ತೆ ಅನ್ನರು.",
			"30": "<sup>30</sup> ಸಾಯತಕ್ಕವನು ತನ್ನ ತನ್ನ ದೋಷದಿಂದ ಸಾಯುವನು. ಹುಳಿದ್ರಾಕ್ಷೆಯನ್ನು ತಿನ್ನುವವನ ಹಲ್ಲುಗಳೇ ಚಳಿತುಹೋಗುವವು.",
			"31": "<sup>31</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಇಗೋ, ನಾನು ಇಸ್ರಾಯೇಲ್ ವಂಶದವರೊಂದಿಗೂ ಯೆಹೂದ ವಂಶದವರೊಂದಿಗೂ ಹೊಸದಾಗಿರುವ ಒಂದು ಒಡಂಬಡಿಕೆಯನ್ನು ಮಾಡಿಕೊಳ್ಳುವ ದಿನಗಳು ಬರುವವು;",
			"32": "<sup>32</sup> ಈ ಒಡಂಬಡಿಕೆಯು ನಾನು ಇವರ ಪಿತೃಗಳನ್ನು ಕೈಹಿಡಿದು ಐಗುಪ್ತದೇಶದೊಳಗಿಂದ ಕರತಂದಾಗ ಅವರ ಸಂಗಡ ಮಾಡಿಕೊಂಡ ಒಡಂಬಡಿಕೆಯಂಥದಲ್ಲ; ನಾನು ಅವರಿಗೆ ಪತಿಯಾಗಿದ್ದರೂ ಆ ನನ್ನ ಒಡಂಬಡಿಕೆಯನ್ನು ಅವರು ಮೀರಿದರಷ್ಟೆ.",
			"33": "<sup>33</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಆ ದಿನಗಳು ಬಂದಮೇಲೆ ನಾನು ಇಸ್ರಾಯೇಲ್ ವಂಶದವರೊಂದಿಗೆ ಮಾಡಿಕೊಳ್ಳುವ ಒಡಂಬಡಿಕೆಯು ಹೀಗಿರುವದು - ನನ್ನ ಧರ್ಮೋಪದೇಶವನ್ನು ಅವರ ಅಂತರಂಗದಲ್ಲಿ ಇಡುವೆನು, ಅವರ ಹೃದಯದೊಳಗೆ ಅದನ್ನು ಬರೆಯುವೆನು; ನಾನು ಅವರಿಗೆ ದೇವರಾಗಿರುವೆನು, ಅವರು ನನಗೆ ಪ್ರಜೆಯಾಗಿರುವರು;",
			"34": "<sup>34</sup> ನೆರೆಹೊರೆಯವರೂ ಅಣ್ಣತಮ್ಮಂದಿರೂ ಒಬ್ಬರಿಗೊಬ್ಬರು - ಯೆಹೋವನ ಜ್ಞಾನವನ್ನು ಪಡೆಯಿರಿ ಎಂದು ಇನ್ನು ಮೇಲೆ ಬೋಧಿಸಬೇಕಾಗಿರುವದಿಲ್ಲ; ಚಿಕ್ಕವರು ಮೊದಲುಗೊಂಡು ದೊಡ್ಡವರ ತನಕ ಎಲ್ಲರೂ ನನ್ನ ಜ್ಞಾನವನ್ನು ಪಡೆದಿರುವರು; ನಾನು ಅವರ ಅಪರಾಧವನ್ನು ಕ್ಷಮಿಸಿ ಅವರ ಪಾಪವನ್ನು ನನ್ನ ನೆನಪಿಗೆ ಎಂದಿಗೂ ತರುವದಿಲ್ಲ. ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"35": "<sup>35</sup> ಹಗಲಿನಲ್ಲಿ ಸೂರ್ಯನನ್ನು, ರಾತ್ರಿಯಲ್ಲಿ ಚಂದ್ರ ನಕ್ಷತ್ರಗಳ ಕಟ್ಟಳೆಗಳನ್ನು ಪ್ರಕಾಶಕ್ಕಾಗಿ ನೇಮಿಸುವಾತನೂ ಅಲೆಗಳು ಭೋರ್ಗರೆಯುವಷ್ಟು ಸಮುದ್ರವನ್ನು ರೇಗಿಸುವಾತನೂ ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೆಂಬ ನಾಮಧೇಯದಿಂದ ಪ್ರಸಿದ್ಧನೂ ಆಗಿರುವ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ -",
			"36": "<sup>36</sup> ಈ ಕಟ್ಟಳೆಗಳು ನನ್ನ ಪರಾಂಬರಿಕೆಗೆ ಬಾರದೆ ತೊಲಗಿಹೋದರೆ ಆಗ ಇಸ್ರಾಯೇಲ್ ಸಂತಾನವು ನನ್ನ ಪರಾಂಬರಿಕೆಗೆ ಬಾರದೆ ಎಂದಿಗೂ ಜನಾಂಗವಾಗದೆ ಹೋಗುವದು.",
			"37": "<sup>37</sup> ಯೆಹೋವನು ಹೀಗೆನ್ನುತ್ತಾನೆ - ಮೇಲೆ ಆಕಾಶಮಂಡಲವನ್ನು ಅಳೆಯಬಹುದಾದರೆ ಕೆಳಗೆ ಭೂಮಂಡಲದ ಅಸ್ತಿವಾರವನ್ನು ಪರೀಕ್ಷಿಸಬಹುದಾದರೆ ಆಗ ಇಸ್ರಾಯೇಲ್ ವಂಶವು ಮಾಡಿರುವ ಸಕಲ ದುಷ್ಕೃತ್ಯಗಳ ನಿಮಿತ್ತ ನಾನು ಆ ವಂಶವನ್ನು ನಿರಾಕರಿಸಿಬಿಟ್ಟೇನು.",
			"38": "<sup>38</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಇಗೋ, ಮುಂದಿನ ಕಾಲದಲ್ಲಿ ಈ ಪಟ್ಟಣವು ಹನನೇಲನ ಬುರುಜಿನಿಂದ ಮೂಲೆಯ ಬಾಗಿಲಿನವರೆಗೆ ಯೆಹೋವನ ಘನತೆಗಾಗಿ ವಿಸ್ತರಿಸುವದು.",
			"39": "<sup>39</sup> ಅಳತೆಯ ನೂಲು ಇನ್ನು ಮೇಲೆ ಗಾರೇಬ್ ಗುಡ್ಡದ ತನಕ ನೆಟ್ಟಗೆ ಎಳೆಯಲ್ಪಟ್ಟು ಗೋಯದ ಕಡೆಗೆ ತಿರುಗುವದು.",
			"40": "<sup>40</sup> ಆಗ ಬೂದಿ ಹೆಣ ಇವುಗಳಿಂದ ಹೊಲಸಾದ ತಗ್ಗೂ ಅಲ್ಲಿಂದ ಕಿದ್ರೋನ್ ಹಳ್ಳ, ಪೂರ್ವದಿಕ್ಕಿನ ಕುದುರೆ ಬಾಗಿಲಮೂಲೆ ಇವುಗಳವರೆಗಿರುವ ಪ್ರದೇಶವೆಲ್ಲವೂ ಯೆಹೋವನಿಗೆ ಪರಿಶುದ್ಧ ಸ್ಥಾನವಾಗುವವು; ಪಟ್ಟಣವು ಇನ್ನು ಮೇಲೆ ಎಂದಿಗೂ ಕೀಳಲ್ಪಡದು, ಕೆಡವಲ್ಪಡದು."
		},
		"32": {
			"1": "<sup>1</sup> ಯೆಹೂದದ ಅರಸನಾದ ಚಿದ್ಕೀಯನ ಆಳಿಕೆಯ ಹತ್ತನೆಯ ವರುಷದಲ್ಲಿ, ಅಂದರೆ ನೆಬೂಕದ್ನೆಚ್ಚರನ ಆಳಿಕೆಯ ಹದಿನೆಂಟನೆಯ ವರುಷದಲ್ಲಿ, ಯೆಹೋವನು ಯೆರೆಮೀಯನಿಗೆ ತನ್ನ ವಾಕ್ಯವನ್ನು ದಯಪಾಲಿಸಿದನು.",
			"2": "<sup>2</sup> ಆ ಕಾಲದಲ್ಲಿ ಬಾಬೆಲಿನ ಅರಸನ ಸೈನ್ಯವು ಯೆರೂಸಲೇಮನ್ನು ಮುತ್ತಿತ್ತು, ಮತ್ತು ಪ್ರವಾದಿಯಾದ ಯೆರೆಮೀಯನು ಯೆಹೂದದ ರಾಜನ ಮನೆಗೆ ಸೇರಿದ ಕಾರಾಗೃಹದ ಅಂಗಳದಲ್ಲಿ ಸೆರೆಯಾಗಿದ್ದನು.",
			"3": "<sup>3</sup> ಯೆಹೂದದ ಅರಸನಾದ ಚಿದ್ಕೀಯನು ಯೆರೆಮೀಯನಿಗೆ - ನೀನು ಪ್ರವಾದನೆ ಮಾಡುತ್ತಾ - ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ನಾನು ಈ ಪಟ್ಟಣವನ್ನು ಬಾಬೆಲಿನ ಅರಸನ ಕೈಗೆ ಸಿಕ್ಕಿಸುವೆನು, ಅವನು ಇದನ್ನು ಆಕ್ರಮಿಸುವನು;",
			"4": "<sup>4</sup> ಮತ್ತು ಯೆಹೂದದ ಅರಸನಾದ ಚಿದ್ಕೀಯನು ಕಸ್ದೀಯರ ಕೈಯಿಂದ ತಪ್ಪಿಸಿಕೊಳ್ಳಲಾರದೆ ಬಾಬೆಲಿನ ಅರಸನ ವಶವಾಗುವದು ಖಂಡಿತ, ಅವನಿಗೆ ಸಾಕ್ಷಾತ್ತಾಗಿ ನಿಂತು ಎದುರೆದುರಾಗಿ ಮಾತಾಡುವನು;",
			"5": "<sup>5</sup> ಆ ಅರಸನು ಚಿದ್ಕೀಯನನ್ನು ಬಾಬೆಲಿಗೆ ಒಯ್ದುಕೊಂಡುಹೋಗುವನು, ನಾನು ಅವನಿಗೆ ದಯೆತೋರಿಸುವ ತನಕ ಅಲ್ಲೇ ಇರುವನು; ನೀವು ಕಸ್ದೀಯರೊಡನೆ ಯುದ್ಧಮಾಡಿದರೂ ನಿಮಗೆ ಜಯವಾಗದು; ಇದು ಯೆಹೋವನ ನುಡಿ ಎಂಬದಾಗಿ ಏಕೆ ನುಡಿಯುತ್ತೀ ಎಂದು ಹೇಳಿ ಅವನನ್ನು ಸೆರೆಯಲ್ಲಿ ಹಾಕಿಸಿದ್ದನು.",
			"6": "<sup>6</sup> ಯೆರೆಮೀಯನು ತಿಳಿಸಿದ್ದು, ಯೆಹೋವನು ಈ ವಾಕ್ಯವನ್ನು ನನಗೆ ದಯಪಾಲಿಸಿದನು -",
			"7": "<sup>7</sup> ಇಗೋ, ನಿನ್ನ ಚಿಕ್ಕಪ್ಪನಾದ ಶಲ್ಲೂಮನ ಮಗನಾಗಿರುವ ಹನಮೇಲನು ನಿನ್ನ ಬಳಿಗೆ ಬಂದು - ಅನಾತೋತಿನಲ್ಲಿರುವ ನನ್ನ ಹೊಲವನ್ನು ಕೊಂಡುಕೋ; ಪರಾಧೀನವಾಗದಂತೆ ಅದನ್ನು ಕೊಂಡುಕೊಳ್ಳುವ ಹಕ್ಕು ನಿನ್ನದೇ ಎಂದು ಹೇಳುವನು.",
			"8": "<sup>8</sup> ಬಳಿಕ ಯೆಹೋವನ ವಾಕ್ಯಾನುಸಾರ ನನ್ನ ಚಿಕ್ಕಪ್ಪನ ಮಗನಾದ ಹನಮೇಲನು ಕಾರಾಗೃಹದ ಅಂಗಳದಲ್ಲಿ ನನ್ನ ಬಳಿಗೆ ಬಂದು - ಬೆನ್ಯಾಮೀನ್ ಸೀಮೆಯ ಅನಾತೋತಿನಲ್ಲಿರುವ ನನ್ನ ಹೊಲವನ್ನು ದಯಮಾಡಿ ಕೊಂಡುಕೋ, ಅದಕ್ಕೆ ನೀನೇ ಬಾಧ್ಯನು, ಪರಾಧೀನವಾಗದಂತೆ ಕೊಂಡುಕೊಳ್ಳುವ ಹಕ್ಕೂ ನಿನ್ನದೇ; ನಿನಗಾಗಿಯೇ ಕೊಂಡುಕೋ ಎಂದು ಹೇಳಿದನು. ಕೂಡಲೆ ಇದು ಯೆಹೋವನ ನುಡಿ ಎಂದು ನನಗೆ ಗೊತ್ತಾಯಿತು.",
			"9": "<sup>9</sup> ಆಗ ನನ್ನ ಚಿಕ್ಕಪ್ಪನ ಮಗನಾದ ಹನಮೇಲನಿಂದ ಅನಾತೋತಿನಲ್ಲಿರುವ ಹೊಲವನ್ನು ನಾನು ಕೊಂಡುಕೊಂಡು ಅದರ ಕ್ರಯವಾಗಿ ಹದಿನೇಳು ತೊಲ ಬೆಳ್ಳಿಯನ್ನು ತೂಕ ಮಾಡಿ ಅವನಿಗೆ ಕೊಟ್ಟೆನು.",
			"10": "<sup>10</sup> ಪತ್ರಕ್ಕೆ ರುಜು ಹಾಕಿ ಮುಚ್ಚಿ [ಮುಚ್ಚಳಕ್ಕೆ] ಸಾಕ್ಷಿಗಳನ್ನು ಹಾಕಿಸಿ ತ್ರಾಸಿನಲ್ಲಿ ಆ ಬೆಳ್ಳಿಯನ್ನು ತೂಗಿ ಅವನಿಗೆ ಕೊಟ್ಟೆನು.",
			"11": "<sup>11</sup> ಆಮೇಲೆ ನಾನು ಕ್ರಯಪತ್ರವನ್ನು, ಅಂದರೆ ಚಕ್ಕುಬಂದಿಷರತ್ತುಗಳಿಂದೊಡಗೂಡಿ ಮುಚ್ಚಿದ್ದ ಪತ್ರವನ್ನೂ ಮುಚ್ಚಳದ ಪತ್ರವನ್ನೂ ತೆಗೆದುಕೊಂಡು",
			"12": "<sup>12</sup> ನನ್ನ ಚಿಕ್ಕಪ್ಪನ ಮಗನಾದ ಹನಮೇಲನ ಮುಂದೆ ಕ್ರಯಪತ್ರಕ್ಕೆ ರುಜುಮಾಡಿದ ಸಾಕ್ಷಿಗಳ ಸಮಕ್ಷಮ ಕಾರಾಗೃಹದ ಅಂಗಳದಲ್ಲಿ ಕೂತಿದ್ದ ಎಲ್ಲಾ ಯೆಹೂದ್ಯರ ಎದುರಿನಲ್ಲಿ ಆ ಕ್ರಯಪತ್ರವನ್ನು ಮಹ್ಸೇಮನ ಮೊಮ್ಮಗನೂ ನೇರೀಯನ ಮಗನೂ ಆದ ಬಾರೂಕನ ಕೈಗೆ ಕೊಟ್ಟೆನು.",
			"13": "<sup>13</sup> ಅವರೆಲ್ಲರ ಮುಂದೆ ಅವನಿಗೆ -",
			"14": "<sup>14</sup> ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರೂ ಸೇನಾಧೀಶ್ವರನೂ ಆದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ನೀನು ಈ ಕ್ರಯಪತ್ರಗಳನ್ನು, ಅಂದರೆ ಮುಚ್ಚಿದ ಪತ್ರವನ್ನೂ ಮುಚ್ಚಳದ ಪತ್ರವನ್ನೂ ತೆಗೆದುಕೊಂಡು ಬಹು ದಿವಸ ಇರುವ ಹಾಗೆ ಮಡಿಕೆಯಲ್ಲಿ ಬಚ್ಚಿಡು.",
			"15": "<sup>15</sup> ಈ ದೇಶದಲ್ಲಿ ಜನರು ಮನೆಹೊಲತೋಟಗಳನ್ನು ಮತ್ತೆ ಕೊಳ್ಳುವರು, ಕೊಡುವರು. ಇದು ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರೂ ಸೇನಾಧೀಶ್ವರನೂ ಆದ ಯೆಹೋವನ ನುಡಿ ಎಂದು ಖಂಡಿತವಾಗಿ ಹೇಳಿದೆನು.",
			"16": "<sup>16</sup> ಕ್ರಯಪತ್ರವನ್ನು ನೇರೀಯನ ಮಗನಾದ ಬಾರೂಕನ ಕೈಗೆ ಕೊಟ್ಟ ಮೇಲೆ ನಾನು ಯೆಹೋವನಿಗೆ ಹೀಗೆ ವಿಜ್ಞಾಪನೆ ಮಾಡಿದೆನು -",
			"17": "<sup>17</sup> ಎಲೈ, ಕರ್ತನಾದ ಯೆಹೋವನೇ! ಆಹಾ, ನೀನು ಭುಜವನ್ನೆತ್ತಿ ನಿನ್ನ ಮಹಾ ಶಕ್ತಿಯಿಂದ ಭೂಮ್ಯಾಕಾಶಗಳನ್ನು ಸೃಷ್ಟಿಸಿದ್ದೀ; ಯಾವ ಕಾರ್ಯವೂ ನಿನಗೆ ಅಸಾಧ್ಯವಲ್ಲ.",
			"18": "<sup>18</sup> ನೀನು ಸಾವಿರಾರು ತಲೆಗಳವರೆಗೆ ದಯೆತೋರಿಸುವವನೂ ತಂದೆಗಳ ದೋಷಫಲವನ್ನು ಅವರ ತರುವಾಯ ಮಕ್ಕಳ ಮಡಲಿಗೆ ಹಾಕುವವನೂ ಆಗಿದ್ದೀ; ನೀನು ಮಹಾ ಪರಾಕ್ರಮಿಯಾದ ದೇವರು; ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೆಂಬದು ನಿನ್ನ ನಾಮಧೇಯ;",
			"19": "<sup>19</sup> ನೀನು ಆಲೋಚನೆಯಲ್ಲಿ ಶ್ರೇಷ್ಠನು, ಕಾರ್ಯದಲ್ಲಿ ಸಮರ್ಥನು; ನೀನು ಪ್ರತಿಯೊಬ್ಬನಿಗೂ ಅವನವನ ಕರ್ಮಕ್ಕೂ ನಡತೆಗೂ ತಕ್ಕ ಫಲವನ್ನು ಕೊಡಬೇಕೆಂದು ನರಜನ್ಮದವರ ಮಾರ್ಗಗಳನ್ನೆಲ್ಲಾ ಕಣ್ಣಾರೆ ನೋಡುತ್ತೀ;",
			"20": "<sup>20</sup> ನೀನು ಐಗುಪ್ತದೇಶದಲ್ಲಿ ಮಹತ್ಕಾರ್ಯಗಳನ್ನೂ ಉತ್ಪಾತಗಳನ್ನೂ ನಡಿಸಿ ಈ ದಿನದವರೆಗೆ ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿಯೂ ಇತರ ಜನರಲ್ಲಿಯೂ ಅವುಗಳನ್ನು ಮಾಡುತ್ತಾ ಬಂದು ಹೆಸರನ್ನು ಪಡೆದುಕೊಂಡಿ; ಅದು ಈಗಲೂ ಸ್ಥಿರವಾಗಿದೆ;",
			"21": "<sup>21</sup> ನೀನು ಮಹತ್ಕಾರ್ಯಗಳನ್ನೂ ಉತ್ಪಾತಗಳನ್ನೂ ನಡಿಸಿ ಭುಜಪರಾಕ್ರಮವನ್ನು ತೋರಿಸಿ ಶಿಕ್ಷಾಹಸ್ತವನ್ನು ಎತ್ತಿ ಮಹಾ ಭೀತಿಯನ್ನುಂಟುಮಾಡಿ ನಿನ್ನ ಜನರಾದ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಐಗುಪ್ತದೇಶದೊಳಗಿಂದ ಬರಮಾಡಿ",
			"22": "<sup>22</sup> ಅವರ ಪಿತೃಗಳಿಗೆ ವಾಗ್ದಾನಮಾಡಿದ ಪ್ರಕಾರ ಹಾಲೂ ಜೇನೂ ಹರಿಯುವ ಈ ದೇಶವನ್ನು ಅವರಿಗೆ ಅನುಗ್ರಹಿಸಿದಿ;",
			"23": "<sup>23</sup> ಅವರು ಅದನ್ನು ಸೇರಿ ಅನುಭವಿಸಿದರೂ ನಿನ್ನ ಮಾತಿಗೆ ಕಿವಿಗೊಡಲಿಲ್ಲ, ನಿನ್ನ ಧರ್ಮಾನುಸಾರ ನಡೆಯಲಿಲ್ಲ; ನೀನು ಆಜ್ಞಾಪಿಸಿದವುಗಳಲ್ಲೆಲ್ಲಾ ಒಂದನ್ನೂ ಮಾಡಲಿಲ್ಲ. ಆದಕಾರಣ ನೀನು ಈ ಕೇಡನ್ನೆಲ್ಲಾ ಅವರ ಮೇಲೆ ಬರಮಾಡಿದ್ದೀ.",
			"24": "<sup>24</sup> ಇಗೋ, ಮುತ್ತಿಗೆಯ ದಿಬ್ಬಗಳು, ಪಟ್ಟಣವನ್ನು ಆಕ್ರಮಿಸುವದಕ್ಕೆ ಬಂದಿದ್ದಾರೆ; ಪಟ್ಟಣವು ಖಡ್ಗಕ್ಷಾಮವ್ಯಾಧಿಗಳಿಂದ ಕ್ಷೀಣವಾಗಿ ವಿರೋಧಿಗಳಾದ ಕಸ್ದೀಯರ ಕೈಗೆ ಸಿಕ್ಕಿದೆ; ನೀನು ನುಡಿದದ್ದು ನೆರವೇರಿದೆ, ಇಗೋ, ನೋಡುತ್ತಿದ್ದೀ.",
			"25": "<sup>25</sup> ಕರ್ತನಾದ ಯೆಹೋವನೇ, ನೀನು ನನಗೆ - ಸಾಕ್ಷಿಗಳನ್ನು ಕರೆಯಿಸಿ ಕ್ರಯಕೊಟ್ಟು ಹೊಲವನ್ನು ಕೊಂಡುಕೋ ಎಂದು ಅಪ್ಪಣೆಕೊಟ್ಟಿ; ನೋಡು, ಪಟ್ಟಣವು ಕಸ್ದೀಯರ ಕೈಸೇರಿದೆಯಲ್ಲಾ.",
			"26": "<sup>26</sup> ಆಗ ಯೆಹೋವನು ಯೆರೆಮೀಯನಿಗೆ ಈ ಮಾತನ್ನು ದಯಪಾಲಿಸಿದನು -",
			"27": "<sup>27</sup> ಇಗೋ, ನಾನು ಯೆಹೋವನು, ನರಪ್ರಾಣಿಗೆಲ್ಲಾ ದೇವರು; ನನಗೆ ಅಸಾಧ್ಯವಾದದ್ದುಂಟೋ?",
			"28": "<sup>28</sup> ಆದಕಾರಣ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಇಗೋ, ನಾನು ಈ ಪಟ್ಟಣವನ್ನು ಕಸ್ದೀಯರ ವಶಕ್ಕೆ, ಅಂದರೆ ಬಾಬೆಲಿನ ಅರಸನಾದ ನೆಬೂಕದ್ನೆಚ್ಚರನ ಕೈಗೆ ಸಿಕ್ಕಿಸುವೆನು, ಅವನು ಇದನ್ನು ಆಕ್ರಮಿಸುವನು;",
			"29": "<sup>29</sup> ಮತ್ತು ಈ ಪಟ್ಟಣಕ್ಕೆ ಪ್ರತಿಭಟಿಸುವ ಕಸ್ದೀಯರು ಇದರೊಳಗೆ ನುಗ್ಗಿ ಬೆಂಕಿಯಿಕ್ಕಿ ಯಾವ ಮನೆಗಳ ಮಾಳಿಗೆಗಳಲ್ಲಿ ನನ್ನ ಜನರು ಬಾಳನಿಗೆ ಧೂಪಹಾಕಿ ಅನ್ಯದೇವತೆಗಳಿಗೆ ಪಾನವನ್ನು ನೈವೇದ್ಯವಾಗಿ ಅರ್ಪಿಸಿ ನನ್ನನ್ನು ಕೆಣಕಿದ್ದಾರೋ ಆ ಮನೆಗಳಿರುವ ಈ ಪಟ್ಟಣವನ್ನು ಸುಟ್ಟುಬಿಡುವರು.",
			"30": "<sup>30</sup> ಇಸ್ರಾಯೇಲ್ ವಂಶವೂ ಯೆಹೂದ ವಂಶವೂ ತಮ್ಮ ಚಿಕ್ಕಂದಿನಿಂದ ನನ್ನ ಚಿತ್ತಕ್ಕೆ ವಿರುದ್ಧವಾಗಿಯೇ ನಡೆಯುತ್ತಾ ಬಂದಿವೆ; ಹೌದು, ಇಸ್ರಾಯೇಲ್ ವಂಶದವರು ತಮ್ಮ ಕೈಕೆಲಸದ ಬೊಂಬೆಗಳಿಂದ ನನ್ನನ್ನು ರೇಗಿಸುತ್ತಲೇ ಬಂದಿದ್ದಾರೆ. ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"31": "<sup>31</sup> ಈ ಪಟ್ಟಣವು ಕಟ್ಟಿದಂದಿನಿಂದ ಇಂದಿನವರೆಗೂ ನನ್ನ ಕೋಪ ರೋಷಗಳಿಗೆ ಆಸ್ಪದವಾಗಿದೆ.",
			"32": "<sup>32</sup> ಅರಸರು, ಪ್ರಧಾನರು, ಯಾಜಕರು, ಪ್ರವಾದಿಗಳು, ಯೆಹೂದದ ಪ್ರಜೆಗಳು, ಯೆರೂಸಲೇಮಿನ ನಿವಾಸಿಗಳು, ಅಂತು ಎಲ್ಲಾ ಇಸ್ರಾಯೇಲ್ಯರೂ ಯೆಹೂದ್ಯರೂ ನನ್ನನ್ನು ರೇಗಿಸತಕ್ಕ ಅಧರ್ಮವನ್ನು ತುಂಬಾ ಮಾಡಿದ್ದರಿಂದ ನಾನು ಈ ಪಟ್ಟಣವನ್ನು ನನ್ನ ಸನ್ನಿಧಿಯಿಂದ ತೊಲಗಿಸಬೇಕಾಗಿ ಬಂತು.",
			"33": "<sup>33</sup> ಅವರು ನನಗೆ ಮುಖಕೊಡದೆ ಬೆನ್ನುಮಾಡಿದ್ದಾರೆ; ನಾನು ಎಡೆಬಿಡದೆ ಅವರಿಗೆ ಬೋಧಿಸುತ್ತಾ ಬಂದರೂ ಅವರು ಉಪದೇಶವನ್ನು ಹೊಂದಲೊಲ್ಲದೆ ನನಗೆ ಕಿವಿಗೊಡಲೇ ಇಲ್ಲ.",
			"34": "<sup>34</sup> ನನ್ನ ಹೆಸರಿನಿಂದ ಖ್ಯಾತಿಗೊಂಡ ಆಲಯದಲ್ಲಿ ತಮ್ಮ ಅಸಹ್ಯವಸ್ತುಗಳನ್ನು ಇಟ್ಟು ಹೊಲೆಮಾಡಿದ್ದಾರೆ.",
			"35": "<sup>35</sup> ತಮ್ಮ ಗಂಡು ಹೆಣ್ಣುಮಕ್ಕಳನ್ನು ಮೋಲೆಕ್ ದೇವತೆಗಾಗಿ ಆಹುತಿಕೊಡುವದಕ್ಕೆ ಬೆನ್‍ಹಿನ್ನೋಮ್ ತಗ್ಗಿನಲ್ಲಿನ ಬಾಳನ ಪೂಜಾಸ್ಥಳಗಳನ್ನು ಕಟ್ಟಿ ಯೆಹೂದವನ್ನು ಪಾಪಕ್ಕೆ ಸಿಕ್ಕಿಸಿದ್ದಾರೆ; ನಾನು ಇಂಥ ಅಸಹ್ಯ ಕಾರ್ಯವನ್ನು ವಿಧಿಸಲಿಲ್ಲ, ಅದರ ಸಂಕಲ್ಪವೂ ನನ್ನ ಮನಸ್ಸಿನಲ್ಲಿ ಹುಟ್ಟಲಿಲ್ಲ.",
			"36": "<sup>36</sup> ಹೀಗಿರಲು ಖಡ್ಗಕ್ಷಾಮವ್ಯಾಧಿಗಳಿಂದ ಕ್ಷೀಣವಾಗಿ ಬಾಬೆಲಿನ ಅರಸನ ಕೈವಶವಾಗಿದೆ ಎಂದು ನೀವು ಹೇಳುವ ಈ ಪಟ್ಟಣದ ವಿಷಯವಾಗಿ ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರಾದ ಯೆಹೋವನು ಈಗ ಇಂತೆನ್ನುತ್ತಾನೆ -",
			"37": "<sup>37</sup> ಇಗೋ, ನಾನು ಕೋಪರೋಷ ಮಹಾಕ್ರೋಧಭರಿತನಾಗಿ ಈ ಜನರನ್ನು ಯಾವ ದೇಶಗಳಿಗೆ ಅಟ್ಟಿದೆನೋ ಆ ಸಕಲ ದೇಶಗಳಿಂದ ಈ ಸ್ಥಳಕ್ಕೆ ಪುನಃ ಕರತಂದು ನಿರ್ಭಯವಾಗಿ ವಾಸಿಸುವಂತೆ ಮಾಡುವೆನು.",
			"38": "<sup>38</sup> ಅವರು ನನಗೆ ಜನರಾಗಿರುವರು, ನಾನು ಅವರಿಗೆ ದೇವರಾಗಿರುವೆನು.",
			"39": "<sup>39</sup> ನಾನು ಅವರ ಹಿತವನ್ನೂ ಅವರ ತರುವಾಯ ಅವರ ಮಕ್ಕಳ ಹಿತವನ್ನೂ ಬಯಸುತ್ತಾ ಅವರು ಸದಾ ನನ್ನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿರುವಂತೆ ಎಲ್ಲರಿಗೂ ಒಂದೇ ಮನಸ್ಸನ್ನೂ ಒಂದೇ ಮಾರ್ಗವನ್ನೂ ಅನುಗ್ರಹಿಸುವೆನು.",
			"40": "<sup>40</sup> ನಾನು ನಿಮಗೆ ಹಿತಮಾಡುವದನ್ನು ಬಿಟ್ಟು ವಿಮುಖನಾಗೆನು ಎಂಬುವ ಶಾಶ್ವತವಾದ ಒಡಂಬಡಿಕೆಯನ್ನು ಅವರೊಂದಿಗೆ ಮಾಡಿಕೊಳ್ಳುವೆನು; ಅವರು ನನ್ನಿಂದಗಲದಂತೆ ಅವರ ಹೃದಯದೊಳಗೆ ನನ್ನ ಮೇಲಣ ಭಯಭಕ್ತಿಯನ್ನು ನೆಲೆಗೊಳಿಸುವೆನು.",
			"41": "<sup>41</sup> ನಾನು ಅವರ ಹಿತವನ್ನು ಬಯಸುತ್ತಾ ಅವರಲ್ಲಿ ಆನಂದಿಸುವೆನು, ನಿಶ್ಚಯವಾಗಿ ಅವರನ್ನು ಈ ದೇಶದಲ್ಲಿ ನನ್ನ ಪೂರ್ಣವಾದ ಹೃದಯದಿಂದಲೂ ಮನಸ್ಸಿನಿಂದಲೂ ಸ್ಥಾಪಿಸುವೆನು.",
			"42": "<sup>42</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ನಾನು ಈ ಜನರಿಗೆ ಇಷ್ಟು ದೊಡ್ಡ ಕೇಡನ್ನು ಬರಮಾಡಿದಂತೆ ವಾಗ್ದಾನಮಾಡಿದ ಎಲ್ಲಾ ಮೇಲನ್ನೂ ಬರಮಾಡುವೆನು.",
			"43": "<sup>43</sup> ಜನ ಪಶುಗಳಿಲ್ಲದೆ ಹಾಳಾಗಿ ಕಸ್ದೀಯರ ಕೈವಶವಾಗಿದೆ ಎಂದು ನೀವು ಹೇಳುವ ಈ ದೇಶದಲ್ಲಿ ಹೊಲಗದ್ದೆಗಳನ್ನು ಕೊಳ್ಳುವರು, ಕೊಡುವರು.",
			"44": "<sup>44</sup> ಬೆನ್ಯಮೀನ್ ಸೀಮೆ, ಯೆರೂಸಲೇಮಿನ ಸುತ್ತಣ ಪ್ರದೇಶಗಳು, ಯೆಹೂದದ ಊರುಗಳು, ಬೆಟ್ಟದ ಊರುಗಳು, ಇಳಕಲಿನ ಊರುಗಳು, ದಕ್ಷಿಣಪ್ರಾಂತದ ಊರುಗಳು, ಈ ಎಲ್ಲಾ ಸ್ಥಳಗಳಲ್ಲಿ ಜನರು ಹೊಲಗದ್ದೆಗಳಿಗೆ ಕ್ರಯಕೊಟ್ಟು ಪತ್ರಕ್ಕೆ ರುಜುಹಾಕಿ ಮುಚ್ಚಿ ಸಾಕ್ಷಿಗಳನ್ನು ಹಾಕಿಸಿ ಕೊಂಡುಕೊಳ್ಳುವರು; ನಾನು ನನ್ನ ಜನರ ದುರವಸ್ಥೆಯನ್ನು ತಪ್ಪಿಸುವೆನಷ್ಟೆ. ಇದು ಯೆಹೋವನ ನುಡಿ."
		},
		"33": {
			"1": "<sup>1</sup> ಯೆರೆಮೀಯನು ಕಾರಾಗೃಹದ ಅಂಗಳದಲ್ಲಿ ಸೆರೆಯಾಗಿದ್ದಾಗ ಅವನಿಗೆ ಯೆಹೋವನು ಈ ಎರಡನೆಯ ವಾಕ್ಯವನ್ನು ಅನುಗ್ರಹಿಸಿದನು -",
			"2": "<sup>2</sup> ಕಾರ್ಯವನ್ನು ಸಾಧಿಸಿಕೊಳ್ಳುವ, ಉದ್ದೇಶಿಸಿ ನೆರವೇರಿಸುವ ಯೆಹೋವನಾಮಾಂಕಿತನು ಇಂತೆನ್ನುತ್ತಾನೆ -",
			"3": "<sup>3</sup> ನನ್ನನ್ನು ಕೇಳಿಕೋ, ನಾನು ನಿನಗೆ ಸದುತ್ತರವನ್ನು ದಯಪಾಲಿಸಿ ನಿನಗೆ ತಿಳಿಯದ ಮಹತ್ತಾದ ಗೂಢಾರ್ಥಗಳನ್ನು ಗೋಚರಪಡಿಸುವೆನು.",
			"4": "<sup>4</sup> ದಿಬ್ಬಗಳಿಗೂ ಕತ್ತಿಗಳಿಗೂ [ಅಡ್ಡವಾಗಿ ಗೋಡೆಯನ್ನು ಕಟ್ಟಲು] ಕೆಡವಿರುವ ಈ ಊರಿನವರ ಮನೆಗಳ ವಿಷಯವಾಗಿಯೂ ಯೆಹೂದದ ಅರಸರ ಉಪ್ಪರಿಗೆಗಳ ವಿಷಯವಾಗಿಯೂ ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರಾದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ -",
			"5": "<sup>5</sup> ಈ ಊರಿನವರು ಕಸ್ದೀಯರನ್ನು ಪ್ರತಿಭಟಿಸುವದಕ್ಕೆ ಹೊರಟರೆ ಏನು? ತಮ್ಮ ಹೆಣಗಳಿಂದ ಅವರನ್ನು ತೃಪ್ತಿಪಡಿಸುವರು; ಇವರ ಅಧರ್ಮವನ್ನು ಕಂಡು ಈ ಪಟ್ಟಣಕ್ಕೆ ವಿಮುಖನಾಗಿ ಕೋಪರೋಷಭರಿತನಾದ ನಾನೇ ಇವರನ್ನು ಹತಿಸುವೆನು.",
			"6": "<sup>6</sup> ಇಗೋ, ನಾನು ಈ ಪಟ್ಟಣವನ್ನು ಜೀರ್ಣೋದ್ಧಾರಮಾಡಿ ಅದಕ್ಕೆ ಸೌಖ್ಯವನ್ನು ಕೊಟ್ಟು ನಿವಾಸಿಗಳನ್ನು ಗುಣಪಡಿಸುವೆನು; ಸ್ಥೈರ್ಯಸಮಾಧಾನಗಳನ್ನು ಸಮೃದ್ಧಿಯಾಗಿ ಅವರ ಅನುಭವಕ್ಕೆ ತರುವೆನು;",
			"7": "<sup>7</sup> ಯೆಹೂದದ ಮತ್ತು ಇಸ್ರಾಯೇಲಿನ ದುರವಸ್ಥೆಯನ್ನು ತಪ್ಪಿಸಿ ಮೊದಲಿನಂತೆಯೇ ಅವುಗಳನ್ನು ಊರ್ಜಿತಪಡಿಸುವೆನು.",
			"8": "<sup>8</sup> ಅವರು ನನಗೆ ವಿರುದ್ಧವಾಗಿ ಮಾಡಿರುವ ಅಧರ್ಮವನ್ನೆಲ್ಲಾ ತೊಲಗಿಸಿ ಅವರನ್ನು ಶುದ್ಧೀಕರಿಸುವೆನು; ಅವರು ನನಗೆ ಪಾಪದ್ರೋಹಗಳನ್ನು ಮಾಡಿ ನಡಿಸಿರುವ ಅಪರಾಧಗಳನ್ನೆಲ್ಲಾ ಕ್ಷಮಿಸುವೆನು.",
			"9": "<sup>9</sup> ನಾನು ಈ ಜನರಿಗೆ ಅನುಗ್ರಹಿಸುವ ಎಲ್ಲಾ ಮೇಲುಗಳ ಸುದ್ದಿಯನ್ನು ಕೇಳಿ ನಾನು ಈ ಪಟ್ಟಣಕ್ಕೆ ಉಂಟುಮಾಡುವ ಸುಖಸಮಾಧಾನಗಳನ್ನು ನೋಡಿ ಹೆದರಿ ನಡುಗುವ ಸಕಲ ಭೂರಾಜ್ಯಗಳ ಮುಂದೆ ಈ ಪಟ್ಟಣವು ನನ್ನ ಕೀರ್ತಿಯೂ ಮಹಿಮೆಯೂ ಆನಂದದ ಬಿರುದೂ ಆಗುವದು.",
			"10": "<sup>10</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಜನಪಶುಗಳಿಲ್ಲದೆ ಹಾಳಾಗಿದೆ ಎಂದು ನೀವೆನ್ನುವ ಈ ಸ್ಥಳದಲ್ಲಿ, ಅಂದರೆ ಜನಪಶುರಹಿತವಾಗಿ ನಿವಾಸಿಗಳಿಲ್ಲದೆ ಹಾಳುಬಿದ್ದಿರುವ ಯೆಹೂದದ ಊರುಗಳಲ್ಲಿ, ಯೆರೂಸಲೇಮಿನ ಬೀದಿಗಳಲ್ಲಿ ಹರ್ಷಧ್ವನಿ,",
			"11": "<sup>11</sup> ಉಲ್ಲಾಸ ಕೋಲಾಹಲ, ವಧೂವರರ ಸ್ವರ, ಇವುಗಳು ಕೇಳ ಬರುವವು. ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನಿಗೆ ಕೃತಜ್ಞತಾಸ್ತುತಿಮಾಡಿರಿ, ಯೆಹೋವನು, ಒಳ್ಳೆಯವನು, ಆತನ ಕೃಪೆಯು ಶಾಶ್ವತ ಎಂದು ಕೀರ್ತಿಸುತ್ತಾ ಕೃತಜ್ಞತಾಬಲಿಯನ್ನು ಯೆಹೋವನ ಆಲಯಕ್ಕೆ ತರುವವರ ಗಾನವೂ ಕಿವಿಗೆ ಬೀಳುವದು. ನಾನು ದೇಶವನ್ನು ದುರವಸ್ಥೆಯಿಂದ ತಪ್ಪಿಸಿ ಪೂರ್ವಸ್ಥಿತಿಗೆ ತರುವೆನು.",
			"12": "<sup>12</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಜನಪಶುಗಳಿಲ್ಲದೆ ಹಾಳಾಗಿರುವ ಈ ಪ್ರಾಂತವೂ ಇಲ್ಲಿನ ಎಲ್ಲಾ ಊರುಗಳೂ ಕುರುಬರು ತಮ್ಮ ಹಿಂಡುಗಳನ್ನು ತಂಗಿಸುವದಕ್ಕೆ ಮತ್ತೆ ಆಸರೆಯಾಗುವವು.",
			"13": "<sup>13</sup> ಬೆಟ್ಟದ ಊರುಗಳು, ಇಳಕಲಿನ ಊರುಗಳು, ದಕ್ಷಿಣ ಪ್ರಾಂತದ ಊರುಗಳು, ಬೆನ್ಯಾಮೀನ್ ಸೀಮೆ, ಯೆರೂಸಲೇಮಿನ ಸುತ್ತಣ ಪ್ರದೇಶಗಳು, ಯೆಹೂದದ ಊರುಗಳು, ಈ ಎಲ್ಲಾ ಸ್ಥಳಗಳಲ್ಲಿ ಹಿಂಡುಗಳು ಎಣಿಸುವವನ ಕೈಗೆ ಲೆಕ್ಕವಾಗುವಂತೆ ಮತ್ತೆ ಹಾದುಹೋಗುವವು. ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"14": "<sup>14</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಆಹಾ, ನಾನು ಇಸ್ರಾಯೇಲ್ ಯೆಹೂದ ವಂಶಗಳ ವಿಷಯವಾಗಿ ನುಡಿದಿರುವ ಶುಭವಾಕ್ಯವನ್ನು ನೆರವೇರಿಸುವ ದಿನಗಳು ಬರುವವು.",
			"15": "<sup>15</sup> ಆ ದಿನಗಳಲ್ಲಿ, ಆ ಕಾಲದಲ್ಲಿ, ದಾವೀದನೆಂಬ ಮೂಲದಿಂದ ಸದ್ಧರ್ಮಿಯಾದ ಮೊಳಿಕೆಯನ್ನು ಚಿಗುರಿಸುವೆನು; ಅವನು ದೇಶದಲ್ಲಿ ನೀತಿನ್ಯಾಯಗಳನ್ನು ನಿರ್ವಹಿಸುವನು.",
			"16": "<sup>16</sup> ಆಗ ಯೆಹೂದ್ಯರು ಸುರಕ್ಷಿತವಾಗಿರುವರು, ಯೆರೂಸಲೇಮಿನವರು ನೆಮ್ಮದಿಯಾಗಿ ವಾಸಿಸುವರು; ಯೆಹೋವ ಚಿದ್ಕೇನು [ಅಂದರೆ ಯೆಹೋವನೇ ನಮ್ಮ ಸದ್ಧರ್ಮ] ಎಂಬ ಹೆಸರು ಈ ಪಟ್ಟಣಕ್ಕೆ ಸಲ್ಲುವದು.",
			"17": "<sup>17</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ದಾವೀದನ ವಂಶವು ನಿಂತುಹೋಗದೆ ಆ ಸಂತಾನದವರು ಇಸ್ರಾಯೇಲ್ಯರ ಸಿಂಹಾಸನದಲ್ಲಿ ತಪ್ಪದೆ ಆಸೀನರಾಗುತ್ತಾ ಬರುವರು;",
			"18": "<sup>18</sup> ನನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನಿರಂತರವಾಗಿ ಸರ್ವಾಂಗಹೋಮ, ನೈವೇದ್ಯ, ಯಜ್ಞ ಇವುಗಳನ್ನು ಮಾಡಲು ಲೇವಿಯರಾದ ಯಾಜಕರು ಇದ್ದೇ ಇರುವರು.",
			"19": "<sup>19</sup> ಮತ್ತು ಯೆಹೋವನು ಯೆರೆಮೀಯನಿಗೆ ಈ ಮಾತನ್ನು ದಯಪಾಲಿಸಿದನು -",
			"20": "<sup>20</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ನೀವು ಹಗಲಿರುಳೆಂಬ ನನ್ನ ನಿಬಂಧನೆಗಳನ್ನು ನಿಲ್ಲಿಸಿ ಹಗಲನ್ನು, ಇರುಳನ್ನು ಅದರದರ ಸಮಯದಲ್ಲಿ ಉಂಟಾಗದಂತೆ ಮಾಡಬಹುದಾದರೆ",
			"21": "<sup>21</sup> ಆಗ ನನ್ನ ದಾಸನಾದ ದಾವೀದನಿಗೂ ನನ್ನ ಸೇವಕರಾಗಿರುವ ಲೇವಿಯರಾದ ಯಾಜಕರಿಗೂ ನಾನು ಮಾಡಿದ ನಿಬಂಧನೆಯು ನಿಂತುಹೋಗಿ ದಾವೀದನ ಸಿಂಹಾಸನಾಸೀನನಾಗಿ ಆಳತಕ್ಕ ಅವನ ಸಂತಾನದವನೊಬ್ಬನೂ ಉಳಿಯದೆ ಹೋದಾನು.",
			"22": "<sup>22</sup> ನಾನು ನನ್ನ ದಾಸನಾದ ದಾವೀದನ ಸಂತಾನವನ್ನು ಅಸಂಖ್ಯಾತವಾದ ನಕ್ಷತ್ರಗಣದಷ್ಟು ಹೆಚ್ಚಿಸುವೆನು; ನನ್ನ ಸೇವೆ ನಡಿಸುವ ಲೇವಿಯರ ಸಂಖ್ಯೆಯನ್ನು ಅಳೆಯಲಾಗದ ಸಮುದ್ರತೀರದ ಉಸುಬಿನಷ್ಟು ವೃದ್ಧಿಪಡಿಸುವೆನು.",
			"23": "<sup>23</sup> ಇದಲ್ಲದೆ ಯೆಹೋವನು ಯೆರೆಮೀಯನಿಗೆ ಈ ವಾಕ್ಯವನ್ನು ದಯಪಾಲಿಸಿದನು -",
			"24": "<sup>24</sup> ಯೆಹೋವನು ತಾನು ಆರಿಸಿಕೊಂಡ ಎರಡು ವಂಶಗಳನ್ನು ನಿರಾಕರಿಸಿಬಿಟ್ಟಿದ್ದಾನೆ ಎಂದು ಈ ಜನರು ಆಡಿಕೊಳ್ಳುವ ಮಾತು ನಿನ್ನ ಲಕ್ಷ್ಯಕ್ಕೆ ಬರಲಿಲ್ಲವೋ? ಇವರು ಜನಾಂಗವೇ ಅಲ್ಲ ಎನ್ನುವಷ್ಟು ನನ್ನ ಜನರನ್ನು ಅಸಡ್ಡೆಮಾಡುತ್ತಾರಲ್ಲಾ.",
			"25": "<sup>25</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಹಗಲಿರುಳೆಂಬ ನನ್ನ ನಿಬಂಧನೆಯು ಸ್ಥಿರವಾಗಿ ನಿಲ್ಲದಿದ್ದರೆ, ಭೂಮ್ಯಾಕಾಶಗಳ ಕಟ್ಟಳೆಗಳನ್ನು ನಾನು ವಿಧಿಸಿದವನಲ್ಲದಿದ್ದರೆ",
			"26": "<sup>26</sup> ಆಗ ನಾನು ಯಾಕೋಬನ ಸಂತಾನದವರನ್ನು ತ್ಯಜಿಸಿ ಅಬ್ರಹಾಮ, ಇಸಾಕ, ಯಾಕೋಬ, ಇವರ ಸಂತತಿಯನ್ನು ಆಳತಕ್ಕ ಒಡೆಯರನ್ನು ನನ್ನ ದಾಸನಾದ ದಾವೀದನ ವಂಶದಿಂದ ಆರಿಸದೆ ಆ ವಂಶದವರನ್ನು ನಿರಾಕರಿಸಿಬಿಟ್ಟೇನು. ಅವರನ್ನು ದುರವಸ್ಥೆಯಿಂದ ತಪ್ಪಿಸಿ ಕರುಣಿಸೇ ಕರುಣಿಸುವೆನು."
		},
		"34": {
			"1": "<sup>1</sup> ಬಾಬೆಲಿನ ಅರಸನಾದ ನೆಬೂಕದ್ನೆಚ್ಚರನು, ಅವನ ಪೂರ್ಣಸೈನ್ಯವು, ಅವನ ಕೈಕೆಳಗಣ ಸಮಸ್ತ ಭೂರಾಜ್ಯಗಳು, ಸಕಲ ಜನಗಳು, ಇವೆಲ್ಲಾ ಯೆರೂಸಲೇಮಿಗೂ ಅದಕ್ಕೆ ಸೇರಿದ ಎಲ್ಲಾ ಊರುಗಳಿಗೂ ವಿರುದ್ಧವಾಗಿ ಯುದ್ಧ ಮಾಡುತ್ತಿದ್ದಾಗ ಯೆಹೋವನು ಯೆರೆಮೀಯನಿಗೆ ಈ ಮಾತನ್ನು ದಯಪಾಲಿಸಿದನು -",
			"2": "<sup>2</sup> ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರಾದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ನೀನು ಯೆಹೂದದ ಅರಸನಾದ ಚಿದ್ಕೀಯನ ಬಳಿಗೆ ಹೋಗಿ ಅವನಿಗೆ ಹೀಗೆ ಹೇಳು - ಯೆಹೋವನ ಮಾತನ್ನು ಆಲಿಸು - ಇಗೋ, ನಾನು ಈ ಪಟ್ಟಣವನ್ನು ಬಾಬೆಲಿನ ಅರಸನ ಕೈವಶಮಾಡುವೆನು, ಅವನು ಅದನ್ನು ಬೆಂಕಿಯಿಂದ ಸುಟ್ಟುಬಿಡುವನು;",
			"3": "<sup>3</sup> ನೀನು ಅವನ ಕೈಯಿಂದ ತಪ್ಪಿಸಿಕೊಳ್ಳಲಾರದೆ ಹಿಡಿಯಲ್ಪಟ್ಟು ಅವನ ವಶವಾಗುವಿ; ಬಾಬೆಲಿನ ಅರಸನ ಸಮಕ್ಷಮದಲ್ಲಿ ನಿಲ್ಲುವಿ; ಅವನು ನಿನ್ನ ಸಂಗಡ ಎದುರೆದುರಾಗಿ ಮಾತಾಡುವನು; ನೀನು ಬಾಬೆಲಿಗೆ ಗಡೀಪಾರಾಗುವಿ.",
			"4": "<sup>4</sup> ಆದರೂ ಯೆಹೂದದ ಅರಸನಾದ ಚಿದ್ಕೀಯನೇ, ಯೆಹೋವನ ಮಾತನ್ನು ಕೈಕೊಳ್ಳು; ಯೆಹೋವನು ನಿನ್ನ ವಿಷಯವಾಗಿ ಇಂತೆನ್ನುತ್ತಾನೆ - [ನೀನು ಕೇಳಿ ಕೈಕೊಂಡರೆ] ಖಡ್ಗದಿಂದ ಹತನಾಗುವದಿಲ್ಲ; ಸುಖವಾಗಿ ಸಾಯುವಿ;",
			"5": "<sup>5</sup> ನಿನಗಿಂತ ಮುಂಚಿನ ನಿನ್ನ ಪಿತೃಗಳಾದ ಅರಸರಿಗೆ ಧೂಪಹಾಕಿದ ಪ್ರಕಾರ ನಿನಗೂ ಹಾಕುವರು; ಅಯ್ಯೋ, ಪ್ರಭುವೇ ಎಂದು ನಿನಗಾಗಿ ಗೋಳಾಡುವರು; ನಾನೇ ನುಡಿದಿದ್ದೇನೆ ಎಂದು ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ.",
			"6": "<sup>6</sup> ಆಗ ಪ್ರವಾದಿಯಾದ ಯೆರೆಮೀಯನು ಈ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಯೆಹೂದದ ಅರಸನಾದ ಚಿದ್ಕೀಯನಿಗೆ ಹೇಳಿದನು.",
			"7": "<sup>7</sup> ಬಾಬೆಲಿನ ಅರಸನ ಸೈನ್ಯವು ಯೆರೂಸಲೇಮಿಗೂ ಯೆಹೂದದೊಳಗೆ ಕೋಟೆಕೊತ್ತಲದ ಪಟ್ಟಣಗಳಿಗಾಗಿ ಉಳಿದಿದ್ದ ಲಾಕೀಷಿಗೂ ಅಜೇಕಕ್ಕೂ ವಿರುದ್ಧವಾಗಿ ಯುದ್ಧಮಾಡುತ್ತಿದ್ದಾಗ ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ಆಮೇಲೆ ಯೆಹೋವನು ಯೆರೆಮೀಯನಿಗೆ ಒಂದು ಮಾತನ್ನು ದಯಪಾಲಿಸಿದನು. ಇಷ್ಟರೊಳಗೆ ಅರಸನಾದ ಚಿದ್ಕೀಯನು ಯೆರೂಸಲೇಮಿನವರೆಲ್ಲರೊಂದಿಗೆ ಸೇರಿ -",
			"9": "<sup>9</sup> ನಮ್ಮಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬನು ತನ್ನ ದಾಸದಾಸಿಯರೊಳಗೆ ಇಬ್ರಿಯರನ್ನು ಬಿಡುಗಡೆಮಾಡುವನು; ಯಾರೂ ತನ್ನ ಸಹೋದರನಾದ ಯೆಹೂದ್ಯನಿಂದ ಇನ್ನು ಗುಲಾಮತನ ಮಾಡಿಸಿಕೊಳ್ಳುವದಿಲ್ಲ ಎಂದು ಅವರಿಗೆ ವಿಮೋಚನೆಯನ್ನು ಪ್ರಕಟಿಸುವ ಒಡಂಬಡಿಕೆಯನ್ನು ಮಾಡಿಕೊಂಡಿದ್ದನು.",
			"10": "<sup>10</sup> ಆಗ ತಾವು ತಮ್ಮ ತಮ್ಮ ದಾಸದಾಸಿಯರನ್ನು ಬಿಡುಗಡೆಮಾಡಿ ಅವರಿಂದ ಇನ್ನು ಗುಲಾಮತನ ಮಾಡಿಸಿಕೊಳ್ಳುವದಿಲ್ಲ ಎಂದು ಒಡಂಬಡಿಕೆಗೆ ಒಪ್ಪಿಕೊಂಡ ಸಕಲ ಪ್ರಧಾನರೂ ಸಮಸ್ತ ಪ್ರಜೆಗಳೂ ಅದರಂತೆ ನಡೆದು ಅವರನ್ನು ವಿಮೋಚಿಸಿದರು.",
			"11": "<sup>11</sup> ಆದರೆ ಕೆಲವು ಕಾಲದ ಮೇಲೆ ಮನಸ್ಸನ್ನು ಬೇರೆಮಾಡಿಕೊಂಡು ತಾವು ಬಿಟ್ಟಿದ್ದವರನ್ನು ಪುನಃ ದಾಸದಾಸಿಯರನ್ನಾಗಿ ಸೇರಿಸಿಕೊಂಡು ಅಧೀನಮಾಡಿಕೊಂಡರು.",
			"12": "<sup>12</sup> ಹೀಗಿರಲು ಯೆಹೋವನು ಯೆರೆಮೀಯನಿಗೆ ಈ ವಾಕ್ಯವನ್ನು ದಯಪಾಲಿಸಿದನು -",
			"13": "<sup>13</sup> ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರಾದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಐಗುಪ್ತದೇಶದಲ್ಲಿ ದಾಸರಾಗಿದ್ದ ನಿಮ್ಮ ಪಿತೃಗಳನ್ನು ನಾನು ಅಲ್ಲಿಂದ ಕರತಂದಾಗ -",
			"14": "<sup>14</sup> ತನ್ನನ್ನು ನಿಮಗೆ ಮಾರಿಕೊಂಡು ಆರು ವರುಷ ನಿಮ್ಮ ದಾಸನಾಗಿದ್ದ ಇಬ್ರಿಯನಾದ ಪ್ರತಿಯೊಬ್ಬ ಸಹೋದರನನ್ನು ಏಳನೆಯ ವರುಷದಲ್ಲಿ ಸ್ವತಂತ್ರನನ್ನಾಗಿ ಬಿಟ್ಟುಬಿಡಿರಿ ಎಂದು ವಿಧಿಸಿ ಅವರೊಂದಿಗೆ ಒಡಂಬಡಿಕೆ ಮಾಡಿಕೊಂಡೆನಷ್ಟೆ; ಆದರೆ ನಿಮ್ಮ ಪಿತೃಗಳು ನನ್ನ ಮಾತಿಗೆ ಕಿವಿಗೊಡಲಿಲ್ಲ, ಕೇಳಲಿಲ್ಲ.",
			"15": "<sup>15</sup> ನೀವೋ ಈಗಲೇ ಮನಮರುಗಿ ನಿಮ್ಮ ನಿಮ್ಮ ನೆರೆಹೊರೆಯವರಿಗೆ ವಿಮೋಚನೆಯನ್ನು ಪ್ರಕಟಿಸಿ ನನ್ನ ಚಿತ್ತಾನುಸಾರವಾಗಿ ನಡೆದಿರಿ; ನನ್ನ ಹೆಸರಿನಿಂದ ಖ್ಯಾತಿಗೊಂಡಿರುವ ಆಲಯದಲ್ಲಿ ನನ್ನ ಮುಂದೆ ಒಡಂಬಡಿಕೆಮಾಡಿಕೊಂಡಿರಿ;",
			"16": "<sup>16</sup> ಆಮೇಲೆ ನೀವು ಮನಸ್ಸನ್ನು ಬೇರೆ ಮಾಡಿಕೊಂಡಿರಿ; ಸ್ವತಂತ್ರರನ್ನಾಗಿ ಬಿಟ್ಟಿದ್ದವರನ್ನು ಪುನಃ ದಾಸದಾಸಿಯರನ್ನಾಗಿ ಸೇರಿಸಿಕೊಂಡು ಅಧೀನಮಾಡಿಕೊಂಡು ನನ್ನ ನಾಮವನ್ನು ಅಪಕೀರ್ತಿಗೆ ಗುರಿಮಾಡಿದಿರಿ.",
			"17": "<sup>17</sup> ಆದಕಾರಣ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ನೀವು ನನ್ನನ್ನು ಕೇಳಲಿಲ್ಲ, ನಿಮ್ಮ ನಿಮ್ಮ ಸಹೋದರರಿಗೂ ನೆರೆಹೊರೆಯವರಿಗೂ ಬಿಡುಗಡೆಯನ್ನು ಪ್ರಕಟಿಸಲಿಲ್ಲ; ಇದೇ ಯೆಹೋವನ ನುಡಿ - ಇಗೋ, ನಾನು ನಿಮಗೆ ಬಿಡುಗಡೆಯನ್ನು ಪ್ರಕಟಿಸುತ್ತೇನೆ; ನಿಮ್ಮನ್ನು ಖಡ್ಗವ್ಯಾಧಿಕ್ಷಾಮಗಳಿಗೆ ಗುರಿಯಾಗುವಂತೆ ಬಿಡುತ್ತೇನೆ; ನಿಮ್ಮನ್ನು ಲೋಕದ ಸಮಸ್ತ ರಾಜ್ಯಗಳಿಗೆ ಭಯಾಸ್ಪದವಾಗುವಂತೆ ಮಾಡುವೆನು.",
			"18": "<sup>18</sup> ಕರುವನ್ನು ಸೀಳಿ ಅದರ ಹೋಳುಗಳ ನಡುವೆ ಹಾದುಹೋಗಿ ನನ್ನ ಮುಂದೆ ಮಾಡಿದ ಒಪ್ಪಂದದ ನಿಬಂಧನೆಗಳನ್ನು ಕೈಕೊಳ್ಳದೆ",
			"19": "<sup>19</sup> ನನ್ನ ಒಡಂಬಡಿಕೆಯನ್ನು ಮೀರಿದ ಯೆಹೂದದ ಪ್ರಧಾನರು, ಯೆರೂಸಲೇಮಿನ ಪ್ರಧಾನರು, ಕಂಚುಕಿಗಳು, ಯಾಜಕರು, ಕರುವಿನ ಹೋಳುಗಳ ನಡುವೆ ಹಾದುಹೋದ ದೇಶದ ಸಕಲ ಜನರು,",
			"20": "<sup>20</sup> ಇವರೆಲ್ಲರನ್ನೂ ಇವರ ಪ್ರಾಣವನ್ನು ಹುಡುಕುವ ಶತ್ರುಗಳ ಕೈಗೆ ಕೊಡುವೆನು; ಇವರ ಹೆಣಗಳು ಆಕಾಶಪಕ್ಷಿಗಳಿಗೂ ಭೂಜಂತುಗಳಿಗೂ ಆಹಾರವಾಗುವವು.",
			"21": "<sup>21</sup> ಯೆಹೂದದ ಅರಸನಾದ ಚಿದ್ಕೀಯನನ್ನೂ ಅವನ ಪ್ರಧಾನರನ್ನೂ ಅವರ ಪ್ರಾಣ ಹುಡುಕುವ ಶತ್ರುಗಳ ಕೈಗೆ, ಅಂದರೆ ನಿಮ್ಮನ್ನು ಬಿಟ್ಟುಹೋಗಿರುವ ಬಾಬೆಲಿನ ಅರಸನ ಕೈಗೆ ಕೊಡುವೆನು.",
			"22": "<sup>22</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಇಗೋ, ನಾನು ಅಪ್ಪಣೆಕೊಟ್ಟು ಆ ಶತ್ರುಗಳು ಈ ಪಟ್ಟಣಕ್ಕೆ ಹಿಂದಿರುಗುವಂತೆ ಮಾಡುವೆನು; ಅವರು ಅದಕ್ಕೆ ವಿರುದ್ಧವಾಗಿ ಯುದ್ಧಮಾಡಿ ಅದನ್ನು ಆಕ್ರಮಿಸಿ ಬೆಂಕಿಯಿಂದ ಸುಟ್ಟುಬಿಡುವರು; ಮತ್ತು ನಾನು ಯೆಹೂದದ ಪಟ್ಟಣಗಳನ್ನು ಜನವಿಲ್ಲದ ಬೀಳುಭೂಮಿಯನ್ನಾಗಿ ಮಾಡುವೆನು."
		},
		"35": {
			"1": "<sup>1</sup> ಯೋಷೀಯನ ಮಗನೂ ಯೆಹೂದದ ಅರಸನೂ ಆದ ಯೆಹೋಯಾಕೀಮನ ಕಾಲದಲ್ಲಿ ಯೆಹೋವನು ಯೆರೆಮೀಯನಿಗೆ ದಯಪಾಲಿಸಿದ ವಾಕ್ಯ -",
			"2": "<sup>2</sup> ನೀನು ರೇಕಾಬನ ಮನೆತನದವರ ಬಳಿಗೆ ಹೋಗಿ ಅವರ ಸಂಗಡ ಮಾತಾಡಿ ಅವರನ್ನು ಯೆಹೋವನ ಆಲಯದ ಒಂದು ಕೋಣೆಯೊಳಕ್ಕೆ ಕರೆದು ದ್ರಾಕ್ಷಾರಸವನ್ನು ಕುಡಿಯುವದಕ್ಕೆ ಕೊಡು.",
			"3": "<sup>3</sup> ಆಗ ಪ್ರವಾದಿಯು ಯೆರೆಮೀಯನ ಮಗನೂ ಹಬಚ್ಚಿನ್ಯನ ಮೊಮ್ಮಗನೂ ಆದ ಯಾಜನ್ಯನನ್ನು, ಅವನ ಅಣ್ಣತಮ್ಮಂದಿರನ್ನು, ಅವನ ಎಲ್ಲಾ ಮಕ್ಕಳನ್ನು, ಅಂತು ರೇಕಾಬನ ಮನೆತನದವರೆಲ್ಲರನ್ನೂ",
			"4": "<sup>4</sup> ಯೆಹೋವನ ಆಲಯಕ್ಕೆ ಕರೆದು ಇಗ್ದಲ್ಯನ ಮಗನೂ ದೇವರ ಮನುಷ್ಯನೂ ಆದ ಹಾನಾನನ ಮಕ್ಕಳ ಕೋಣೆಯೊಳಕ್ಕೆ, ಅಂದರೆ ಸರದಾರರ ಕೋಣೆಯ ಪಕ್ಕದಲ್ಲಿ, ಶಲ್ಲೂಮನ ಮಗನೂ ದ್ವಾರಪಾಲಕನೂ ಆದ ಮಾಸೇಯನ ಕೋಣೆಯ ಮೇಲ್ಗಡೆ",
			"5": "<sup>5</sup> ಇರುವ ಕೋಣೆಯೊಳಕ್ಕೆ ಬರಮಾಡಿ ಆ ರೇಕಾಬನ ಮನೆತನದವರ ಮುಂದೆ ದ್ರಾಕ್ಷಾರಸ ತುಂಬಿದ ಬಟ್ಟಲುಗಳನ್ನೂ ಪಂಚಪಾತ್ರೆಗಳನ್ನೂ ಇಟ್ಟು ದ್ರಾಕ್ಷಾರಸವನ್ನು ಕುಡಿಯಿರಿ ಅಂದನು.",
			"6": "<sup>6</sup> ಅದಕ್ಕವರು - ನಾವು ದ್ರಾಕ್ಷಾರಸವನ್ನು ಕುಡಿಯುವದೇ ಇಲ್ಲ; ನಮ್ಮ ಪಿತೃವಾದ ರೇಕಾಬನ ಮಗನಾದ ಯೋನಾದಾಬನು ನಮಗೆ - ನೀವಾಗಲಿ ನಿಮ್ಮ ಸಂತಾನದವರಾಗಲಿ ಯುಗಯುಗಾಂತರಕ್ಕೂ ದ್ರಾಕ್ಷಾರಸ ಕುಡಿಯಬಾರದು;",
			"7": "<sup>7</sup> ಮನೆಕಟ್ಟಬಾರದು, ಬೀಜ ಬಿತ್ತಬಾರದು, ತೋಟ ಮಾಡಬಾರದು, ಅನುಭವಿಸಲೂ ಬಾರದು; ನಿಮ್ಮ ಜೀವಮಾನವೆಲ್ಲಾ ಗುಡಾರಗಳಲ್ಲೇ ವಾಸಿಸಬೇಕು; ಇದರಿಂದ ನೀವು ತಂಗುವ ದೇಶದಲ್ಲಿ ನಿಮಗೆ ದೀರ್ಘಾಯುಷ್ಯವಾಗುವದು ಎಂದು ಅಪ್ಪಣೆಕೊಟ್ಟಿದ್ದಾನೆ.",
			"8": "<sup>8</sup> ನಮ್ಮ ಪಿತೃವಾದ ರೇಕಾಬನ ಮಗನಾಗಿರುವ ಯೋನಾದಾಬನು ನಮಗೆ ಕೊಟ್ಟ ಈ ಅಪ್ಪಣೆಯನ್ನು ಎಲ್ಲಾ ವಿಷಯದಲ್ಲಿಯೂ ಕೈಕೊಳ್ಳುತ್ತಿದ್ದೇವೆ; ನಾವಾಗಲಿ ನಮ್ಮ ಹೆಂಡರಾಗಲಿ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳಾಗಲಿ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ದ್ರಾಕ್ಷಾರಸವನ್ನು ಕುಡಿಯುವದಿಲ್ಲ;",
			"9": "<sup>9</sup> ನಮ್ಮ ವಾಸಕ್ಕೆ ಮನೆಯನ್ನು ಕಟ್ಟುವದಿಲ್ಲ; ನಮಗೆ ತೋಟ ಹೊಲಬೀಜಗಳೂ ಇಲ್ಲ;",
			"10": "<sup>10</sup> ನಾವು ಗುಡಾರಗಳಲ್ಲಿ ವಾಸಿಸುತ್ತಾ ನಮ್ಮ ಪಿತೃವಾದ ಯೋನಾದಾಬನು ನಮಗೆ ವಿಧಿಸಿದ್ದನ್ನೆಲ್ಲಾ ಶಿರಸಾವಹಿಸಿ ನಡಿಸುತ್ತಿದ್ದೇವೆ.",
			"11": "<sup>11</sup> ಆದರೆ ಬಾಬೆಲಿನ ಅರಸನಾದ ನೆಬೂಕದ್ನೆಚ್ಚರನು ದೇಶದೊಳಗೆ ನುಗ್ಗಿದಾಗ - ಕಸ್ದೀಯರ ಮತ್ತು ಅರಾಮ್ಯರ ಸೈನ್ಯಗಳ ಮುಂದೆ ನಿಲ್ಲದೆ ಯೆರೂಸಲೇಮಿಗೆ ಹೋಗೋಣ ಬನ್ನಿ ಅಂದುಕೊಂಡು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ವಾಸಿಸುತ್ತಿದ್ದೇವೆ ಎಂದು ಹೇಳಿದರು.",
			"12": "<sup>12</sup> ಆಗ ಯೆಹೋವನು ಯೆರೆಮೀಯನಿಗೆ ಈ ಮಾತನ್ನು ದಯಪಾಲಿಸಿದನು -",
			"13": "<sup>13</sup> ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರೂ ಸೇನಾಧೀಶ್ವರನೂ ಆದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ನೀನು ಹೋಗಿ ಯೆಹೂದ್ಯರಿಗೂ ಯೆರೂಸಲೇಮಿನ ನಿವಾಸಿಗಳಿಗೂ ಹೀಗೆ ಹೇಳು - [ಇದರಿಂದ] ನೀವು ಬುದ್ಧಿ ತಂದುಕೊಂಡು ನನ್ನ ಮಾತುಗಳನ್ನು ಕೇಳುವದಿಲ್ಲವೋ ಎಂದು ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ.",
			"14": "<sup>14</sup> ರೇಕಾಬನ ಮಗನಾದ ಯೋನಾದಾಬನು ತನ್ನ ಸಂತಾನದವರಿಗೆ ದ್ರಾಕ್ಷಾರಸವನ್ನು ಕುಡಿಯಬಾರದೆಂದು ಕೊಟ್ಟ ಅಪ್ಪಣೆ ನೆರವೇರಿದೆ; ಅವರು ಇಂದಿನವರೆಗೂ ಕುಡಿಯಲಿಲ್ಲ, ತಮ್ಮ ಪಿತೃವಿನ ಆಜ್ಞೆಯನ್ನು ಕೈಕೊಂಡಿದ್ದಾರೆ; ನೀವೋ ನಾನು ನಿಮಗೆ ಎಡೆಬಿಡದೆ ಹೇಳುತ್ತಾ ಬಂದರೂ ನನ್ನ ಮಾತಿಗೆ ಕಿವಿಗೊಡಲಿಲ್ಲ.",
			"15": "<sup>15</sup> ಇದಲ್ಲದೆ ನಾನು ನನ್ನ ಸೇವಕರಾದ ಪ್ರವಾದಿಗಳನ್ನೆಲ್ಲಾ ನಿಮ್ಮ ಬಳಿಗೆ ನಿತ್ಯವೂ ಕಳುಹಿಸುತ್ತಾ - ನೀವೆಲ್ಲರೂ ನಿಮ್ಮ ನಿಮ್ಮ ದುರ್ಮಾರ್ಗಗಳಿಂದ ಹಿಂದಿರುಗಿ ನಿಮ್ಮ ನಡತೆಗಳನ್ನು ಸರಿಪಡಿಸಿಕೊಳ್ಳಿರಿ, ಅನ್ಯದೇವತೆಗಳನ್ನು ಹಿಂಬಾಲಿಸಿ ಸೇವಿಸದಿರಿ; ಹೀಗೆ ಮಾಡಿದರೆ ನಾನು ನಿಮಗೂ ನಿಮ್ಮ ಪಿತೃಗಳಿಗೂ ದಯಪಾಲಿಸಿದ ದೇಶದಲ್ಲಿ ನೀವು ಸುಖವಾಸಿಗಳಾಗಿರುವಿರಿ ಎಂದು ಹೇಳಿಸಿದರೂ ನೀವು ನನ್ನ ಮಾತನ್ನು ಕೇಳಲಿಲ್ಲ, ಕಿವಿಗೊಡಲೂ ಇಲ್ಲ.",
			"16": "<sup>16</sup> ರೇಕಾಬನ ಮಗನಾದ ಯೋನಾದಾಬನ ಸಂತಾನದವರು ತಮ್ಮ ಪಿತೃವು ತಮಗೆ ವಿಧಿಸಿದ್ದನ್ನು ಕೇಳಿ ಅನುಸರಿಸುತ್ತಿರುವಲ್ಲಿ ಈ ಜನರು ನನ್ನ ಮಾತನ್ನು ಕೇಳದೆ ಹೋದದರಿಂದ",
			"17": "<sup>17</sup> ಇಗೋ, ನಾನು ಯೆಹೂದ್ಯರಿಗೂ ಯೆರೂಸಲೇಮಿನವರೆಲ್ಲರಿಗೂ ಕೊಟ್ಟ ಶಾಪದ ಕೇಡನ್ನೆಲ್ಲಾ ಅವರ ಮೇಲೆ ಬರಮಾಡುವೆನು; ನಾನು ಹೇಳಿದರೂ ಅವರು ಕೇಳಲಿಲ್ಲ, ಕೂಗಿದರೂ ಉತ್ತರಕೊಡಲಿಲ್ಲ ಎಂದು ಸೇನಾಧೀಶ್ವರ ಸ್ವಾಮಿಯೂ ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರೂ ಆದ ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ.",
			"18": "<sup>18</sup> ಮತ್ತು ಯೆರೆಮೀಯನು ರೇಕಾಬ್ಯರಿಗೆ - ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರೂ ಸೇನಾಧೀಶ್ವರನೂ ಆದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ನೀವು ನಿಮ್ಮ ಪಿತೃವಾದ ಯೋನಾದಾಬನ ಅಪ್ಪಣೆಯನ್ನು ಕೇಳಿ ಅವನ ಸಕಲ ವಿಧಿಗಳನ್ನು ಅನುಸರಿಸಿ ಅವನು ಆಜ್ಞಾಪಿಸಿದ್ದನ್ನೆಲ್ಲಾ ನೆರವೇರಿಸಿದ ಕಾರಣ",
			"19": "<sup>19</sup> ರೇಕಾಬನ ಮಗನಾದ ಯೋನಾದಾಬನ ಸಂತಾನದವರೊಳಗೆ ನನ್ನ ಸಮ್ಮುಖಸೇವೆಮಾಡತಕ್ಕವರು ತಲತಲಾಂತರಕ್ಕೂ ಇದ್ದೇ ಇರುವರು. ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರೂ ಸೇನಾಧೀಶ್ವರನೂ ಆದ ಯೆಹೋವನು ಇದನ್ನು ನುಡಿದಿದ್ದಾನೆ."
		},
		"36": {
			"1": "<sup>1</sup> ಯೋಷೀಯನ ಮಗನೂ ಯೆಹೂದದ ಅರಸನೂ ಆದ ಯೆಹೋಯಾಕೀಮನ ಆಳಿಕೆಯ ನಾಲ್ಕನೆಯ ವರುಷದಲ್ಲಿ ಯೆಹೋವನು ಯೆರೆಮೀಯನಿಗೆ ಈ ಮಾತನ್ನು ದಯಪಾಲಿಸಿದನು -",
			"2": "<sup>2</sup> ನೀನು ಗ್ರಂಥ ಬರೆಯತಕ್ಕ ಸುರಳಿಯನ್ನು ತೆಗೆದುಕೊಂಡು ನಾನು ಯೋಷೀಯನ ಕಾಲದಲ್ಲಿ ನಿನ್ನ ಸಂಗಡ ಮಾತಾಡಿದಂದಿನಿಂದ ಇಂದಿನವರೆಗೆ ಇಸ್ರಾಯೇಲು, ಯೆಹೂದ, ಸಕಲ ಜನಾಂಗಗಳು, ಇವುಗಳ ವಿಷಯವಾಗಿ ನಿನಗೆ ನುಡಿಯುತ್ತಾ ಬಂದಿರುವ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ಬರೆ.",
			"3": "<sup>3</sup> ಒಂದು ವೇಳೆ ಯೆಹೂದ ವಂಶದವರು ನಾನು ಅವರಿಗೆ ಮಾಡಬೇಕೆಂದಿರುವ ಕೇಡನ್ನು ಕೇಳಿ ತಮ್ಮ ತಮ್ಮ ದುರ್ಮಾರ್ಗಗಳಿಂದ ಹಿಂದಿರುಗಿಯಾರು; ಹಿಂದಿರುಗಿದರೆ ಅವರ ಪಾಪಾಪರಾಧಗಳನ್ನು ಕ್ಷಮಿಸಿಬಿಡುವೆನು.",
			"4": "<sup>4</sup> ಆಗ ಯೆರೆಮೀಯನು ನೇರೀಯನ ಮಗನಾದ ಬಾರೂಕನನ್ನು ಕರೆಯಿಸಿ ಯೆಹೋವನ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ನುಡಿಯಲು ಅವನು ಯೆರೆಮೀಯನ ಬಾಯಿಂದ ಬಂದ ಹಾಗೆಯೇ ಅವುಗಳನ್ನು ಗ್ರಂಥದ ಸುರಳಿಯಲ್ಲಿ ಬರೆದನು.",
			"5": "<sup>5</sup> ಮತ್ತು ಯೆರೆಮೀಯನು ಬಾರೂಕನಿಗೆ - ಯೆಹೋವನ ಆಲಯದೊಳಗೆ ಪ್ರವೇಶಿಸಲು ನನಗೆ ತಡೆಯಾಗಿದೆ;",
			"6": "<sup>6</sup> ನೀನೇ ಹೋಗಿ ನನ್ನ ಬಾಯಿಂದ ಬಂದ ಹಾಗೆ ನೀನು ಬರೆದ ಈ ಸುರಳಿಯಲ್ಲಿನ ಯೆಹೋವನ ಮಾತುಗಳನ್ನು ಉಪವಾಸದ ದಿನದಲ್ಲಿ ಯೆಹೋವನ ಆಲಯದೊಳಗೆ [ಯೆರೂಸಲೇಮಿನ] ಜನರಿಗೂ ತಮ್ಮ ತಮ್ಮ ಊರುಗಳಿಂದ ಬಂದಿರುವ ಎಲ್ಲಾ ಯೆಹೂದ್ಯರಿಗೂ ಕೇಳಿಸುವಂತೆ ಓದು.",
			"7": "<sup>7</sup> ಅವರು ಒಂದು ವೇಳೆ ಯೆಹೋವನಿಗೆ ಶರಣಾಗತರಾಗಿ ತಮ್ಮ ದುರ್ಮಾರ್ಗಗಳಿಂದ ಹಿಂದಿರುಗಿಯಾರು; ಯೆಹೋವನು ಈ ಜನರ ಮೇಲೆ ತೀರಿಸಬೇಕೆಂದು ಪ್ರಕಟಿಸಿರುವ ಕೋಪರೋಷಗಳು ಅಪಾರವಾಗಿವೆ ಎಂದು ಅಪ್ಪಣೆಕೊಟ್ಟನು.",
			"8": "<sup>8</sup> ನೇರೀಯನ ಮಗನಾದ ಬಾರೂಕನು ಸುರಳಿಯಲ್ಲಿನ ಯೆಹೋವನ ಮಾತುಗಳನ್ನು ಯೆಹೋವನ ಆಲಯದೊಳಗೆ ಓದಿ ಪ್ರವಾದಿಯಾದ ಯೆರೆಮೀಯನು ವಿಧಿಸಿದಂತೆಯೇ ಮಾಡಿದನು.",
			"9": "<sup>9</sup> ಯೋಷೀಯನ ಮಗನೂ ಯೆಹೂದದ ಅರಸನೂ ಆದ ಯೆಹೋಯಾಕೀಮನ ಆಳಿಕೆಯ ಐದನೆಯ ವರುಷದ ಒಂಭತ್ತನೆಯ ತಿಂಗಳಿನಲ್ಲಿ ಯೆರೂಸಲೇಮಿನವರೆಲ್ಲರೂ ಯೆಹೂದದ ಊರುಗಳಿಂದ ಯೆರೂಸಲೇಮಿಗೆ ಬಂದವರೆಲ್ಲರೂ ಯೆಹೋವನ ಆರಾಧನೆಯ ಉಪವಾಸವನ್ನು ಗೊತ್ತುಮಾಡಿ ಪ್ರಕಟಿಸಿದರು.",
			"10": "<sup>10</sup> ಆಗ ಬಾರೂಕನು ಯೆಹೋವನ ಆಲಯದೊಳಗೆ ಮೇಲಣ ಪ್ರಾಕಾರದಲ್ಲಿ ಹೊಸ ಬಾಗಿಲ ಹತ್ತಿರ ಲೇಖಕನಾದ ಶಾಫಾನನ ಮಗನಾಗಿರುವ ಗೆಮರ್ಯನ ಕೋಣೆಯಲ್ಲಿ ಸುರಳಿಯಲ್ಲಿನ ಯೆರೆಮೀಯನ ಮಾತುಗಳನ್ನು ಎಲ್ಲಾ ಜನರಿಗೂ ಕೇಳಿಸುವಂತೆ ಓದಿದನು.",
			"11": "<sup>11</sup> ಶಾಫಾನನ ಮೊಮ್ಮಗನೂ ಗೆಮರ್ಯನ ಮಗನೂ ಆದ ಮೀಕಾಯನು ಸುರಳಿಯಲ್ಲಿನ ಯೆಹೋವನ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ಕೇಳಿ",
			"12": "<sup>12</sup> ಅರಮನೆಯಲ್ಲಿ ಲೇಖಕನ ಕೋಣೆಗೆ ಇಳಿದುಹೋದನು. ಅಲ್ಲಿ ಲೇಖಕನಾದ ಎಲೀಷಾಮನು, ಶೆಮಾಯನ ಮಗನಾದ ದೆಲಾಯನು, ಅಕ್ಬೋರನ ಮಗನಾದ ಎಲ್ನಾಥಾನನು, ಶಾಫಾನನ ಮಗನಾದ ಗೆಮರ್ಯನು, ಹನನೀಯನ ಮಗನಾದ ಚಿದ್ಕೀಯನು, ಅಂತು ಪ್ರಧಾನರೆಲ್ಲರೂ ಕೂತುಕೊಂಡಿದ್ದರು.",
			"13": "<sup>13</sup> ಮೀಕಾಯನು ಜನರ ಮುಂದೆ ಗ್ರಂಥವನ್ನು ಓದುತ್ತಿರುವ ಬಾರೂಕನ ಬಾಯಿಂದ ತಾನು ಕೇಳಿದ್ದ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ಅವರಿಗೆ ತಿಳಿಸಲು",
			"14": "<sup>14</sup> ಸಕಲ ಪ್ರಧಾನರು ಬಾರೂಕನಿಗೆ - ನೀನು ಜನರ ಮುಂದೆ ಓದಿದ ಸುರಳಿಯನ್ನು ಕೈಯಲ್ಲಿ ತೆಗೆದುಕೊಂಡು ಬಾ ಎಂದು ನೆಥನ್ಯನ ಮಗನೂ ಶೆಲೆಮ್ಯನ ಮೊಮ್ಮಗನೂ ಕೂಷಿಯ ಮರಿಮಗನೂ ಆದ ಯೆಹೂದಿಯ ಮೂಲಕ ಹೇಳಿಕಳುಹಿಸಿದರು. ಕೂಡಲೆ ನೇರೀಯನ ಮಗನಾದ ಬಾರೂಕನು ಸುರಳಿಯನ್ನು ಕೈಯಲ್ಲಿ ತೆಗೆದುಕೊಂಡು ಅವರ ಬಳಿಗೆ ಬಂದನು.",
			"15": "<sup>15</sup> ಅವರು ಅವನಿಗೆ - ದಯಮಾಡಿ ಕೂತುಕೊಂಡು ನಮ್ಮ ಮುಂದೆ ಓದು ಎಂದು ಹೇಳಲು ಬಾರೂಕನು ಅದನ್ನು ಅವರ ಮುಂದೆ ಓದಿದನು.",
			"16": "<sup>16</sup> ಅವರು ಆ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ಕೇಳಿ ಬೆಬ್ಬರಬಿದ್ದು ಒಬ್ಬರನ್ನೊಬ್ಬರು ನೋಡುತ್ತಾ, ನಾವು ಈ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ಅರಸನಿಗೆ ತಿಳಿಸಲೇ ಬೇಕು ಎಂದು ಬಾರೂಕನಿಗೆ ಹೇಳಿದರು.",
			"17": "<sup>17</sup> ಮತ್ತು, ನೀನು ಈ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ಹೇಗೆ ಬರೆದಿ? ಅವನ ಬಾಯಿಂದ ಬಂದ ಹಾಗೆಯೋ? ದಯಮಾಡಿ ನಮಗೆ ತಿಳಿಸು ಎಂದು ಅವನನ್ನು ಕೇಳಲು ಬಾರೂಕನು ಅವರಿಗೆ -",
			"18": "<sup>18</sup> ಅವನು ಈ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ತನ್ನ ಬಾಯಿಂದಲೇ ನನಗೆ ನುಡಿದನು, ನಾನು ಅವುಗಳನ್ನು ಮಶಿಯಿಂದ ಈ ಪುಸ್ತಕದಲ್ಲಿ ಬರೆದೆನು ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"19": "<sup>19</sup> ಆಗ ಪ್ರಧಾನರು ಬಾರೂಕನಿಗೆ - ಹೊರಡು, ನೀನೂ ಯೆರೆಮೀಯನೂ ಅಡಗಿಕೊಳ್ಳಿರಿ, ನೀವೆಲ್ಲಿದ್ದೀರೆಂಬದು ಯಾರಿಗೂ ಗೊತ್ತಾಗಬಾರದು ಎಂದು ಹೇಳಿ",
			"20": "<sup>20</sup> ಸುರಳಿಯನ್ನು ಲೇಖಕನಾದ ಎಲೀಷಾಮನ ಕೋಣೆಯಲ್ಲಿ ಇಟ್ಟುಬಿಟ್ಟು ಪ್ರಾಕಾರದಲ್ಲಿನ ಅರಸನ ಬಳಿಗೆ ಹೋಗಿ ಈ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ಅವನಿಗೆ ಅರಿಕೆಮಾಡಿದರು.",
			"21": "<sup>21</sup> ಕೂಡಲೇ ಅರಸನು ಸುರಳಿಯನ್ನು ತರುವದಕ್ಕೆ ಯೆಹೂದಿಯನ್ನು ಕಳುಹಿಸಿದನು; ಅವನು ಲೇಖಕನಾದ ಎಲೀಷಾಮನ ಕೋಣೆಯೊಳಗಿಂದ ಅದನ್ನು ತೆಗೆದುಕೊಂಡು ಬಂದು ಅರಸನ ಮತ್ತು ಅವನ ಪಕ್ಕದಲ್ಲಿ ನಿಂತಿದ್ದ ಸಕಲ ಪ್ರಧಾನರ ಮುಂದೆ ಓದಿದನು.",
			"22": "<sup>22</sup> ಆಗ ಪುಷ್ಯಮಾಸ; ಅರಸನು ಚಳಿಗಾಲದ ಮನೆಯಲ್ಲಿ ಕೂತಿದ್ದನು; ಅವನ ಮುಂದೆ ಅಗ್ಗಿಷ್ಟಿಕೆಯು ಉರಿಯುತ್ತಿತ್ತು.",
			"23": "<sup>23</sup> ಯೆಹೂದಿಯು ಬರವಣಿಗೆಯ ಮೂರು ನಾಲ್ಕು ಪುಟಗಳನ್ನು ಓದಿದ ಹಾಗೆಲ್ಲಾ ಅರಸನು ಆಯಾ ಭಾಗಗಳನ್ನು ಚೂರಿಯಿಂದ ಕತ್ತರಿಸಿ ಸುರಳಿಯು ಸಂಪೂರ್ಣವಾಗಿ ಸುಟ್ಟುಹೋಗುವ ತನಕ ಅಗ್ಗಿಷ್ಟಿಕೆಯ ಬೆಂಕಿಯಲ್ಲಿ ಹಾಕುತ್ತಾ ಬಂದನು.",
			"24": "<sup>24</sup> ಅರಸನಾಗಲಿ ಸುರಳಿಯ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ಕೇಳಿದ ಅವನ ಸೇವಕರಲ್ಲಿ ಯಾರೇ ಆಗಲಿ ಭಯಪಡಲಿಲ್ಲ, ಬಟ್ಟೆಗಳನ್ನು ಹರಿದುಕೊಳ್ಳಲಿಲ್ಲ.",
			"25": "<sup>25</sup> ಎಲ್ನಾಥಾನನೂ ದೆಲಾಯನೂ ಗೆಮರ್ಯನೂ - ದಯಮಾಡಿ ಸುರಳಿಯನ್ನು ಸುಡಬಾರದೆಂದು ಅರಸನಿಗೆ ವಿಜ್ಞಾಪನೆಮಾಡಿದರೂ ಅವನು ಅವರ ಮಾತನ್ನು ಕೇಳಲಿಲ್ಲ.",
			"26": "<sup>26</sup> ಮತ್ತು ಅರಸನು ರಾಜವಂಶೀಯನಾದ ಎರಖ್ಮೆಯೇಲ, ಅಜ್ರಿಯೇಲನ ಮಗನಾದ ಸೆರಾಯ, ಅಬ್ದೆಯೇಲನ ಮಗನಾದ ಶಲೆಮ್ಯ, ಇವರಿಗೆ ಲೇಖಕನಾದ ಬಾರೂಕನನ್ನೂ ಪ್ರವಾದಿಯಾದ ಯೆರೆಮೀಯನನ್ನೂ ಹಿಡಿಯಬೇಕೆಂದು ಅಪ್ಪಣೆಕೊಟ್ಟನು; ಆದರೆ ಯೆಹೋವನು ಅವರನ್ನು ಮರೆಯಲ್ಲಿಟ್ಟನು.",
			"27": "<sup>27</sup> ಯೆರೆಮೀಯನ ಬಾಯಿಂದ ಬಂದ ಹಾಗೆ ಬಾರೂಕನು ಬರೆದಿದ್ದ ಮಾತುಗಳ ಸುರಳಿಯನ್ನು ಅರಸನು ಸುಟ್ಟುಬಿಟ್ಟ ಮೇಲೆ ಯೆಹೋವನು ಯೆರೆಮೀಯನಿಗೆ ಈ ವಾಕ್ಯವನ್ನು ದಯಪಾಲಿಸಿದನು -",
			"28": "<sup>28</sup> ನೀನು ಇನ್ನೊಂದು ಸುರಳಿಯನ್ನು ತೆಗೆದುಕೊಂಡು ಯೆಹೂದದ ಅರಸನಾದ ಯೆಹೋಯಾಕೀಮನು ಸುಟ್ಟ ಮೊದಲನೆಯ ಸುರಳಿಯಲ್ಲಿದ್ದ ಪೂರ್ವದ ಆ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ಪುನಃ ಬರೆಯಿಸು.",
			"29": "<sup>29</sup> ಅಲ್ಲದೆ ಯೆಹೂದದ ಅರಸನಾದ ಯೆಹೋಯಾಕೀಮನ ವಿಷಯದಲ್ಲಿ ಹೀಗೆ ಬರೆಯಿಸು - ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಬಾಬೆಲಿನ ಅರಸನು ಬಂದು ಈ ದೇಶವನ್ನು ಜನಪಶುಗಳಿಲ್ಲದಂತೆ ಹಾಳುಮಾಡುವದು ಖಂಡಿತ ಎಂದು ಈ ಸುರಳಿಯನ್ನು ಬರೆಯಿಸಿದ್ದೇಕೆ ಎಂಬದಾಗಿ ನೀನು ಆಕ್ಷೇಪಿಸಿ ಅದನ್ನು ಸುಟ್ಟುಬಿಟ್ಟಿಯಲ್ಲ;",
			"30": "<sup>30</sup> ಆದಕಾರಣ ಯೆಹೂದದ ಅರಸನಾದ ಯೆಹೋಯಾಕೀಮನ ವಿಷಯದಲ್ಲಿ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಇವನ ಸಂತಾನದಲ್ಲಿ ಯಾವನೂ ದಾವೀದನ ಸಿಂಹಾಸನಾರೂಢನಾಗನು; ಇವನ ಹೆಣವು ಬಿಸಾಡಲ್ಪಟ್ಟು ಹಗಲಿನ ತಾಪಕ್ಕೂ ರಾತ್ರಿಯ ಚಳಿಗೂ ಈಡಾಗುವದು.",
			"31": "<sup>31</sup> ಇವನೂ ಇವನ ಸಂತತಿಯವರೂ ಸೇವಕರೂ ನಡಿಸಿದ ಅಧರ್ಮಕ್ಕಾಗಿ ಇವರನ್ನು ದಂಡಿಸುವೆನು. ನಾನು ಇವರನ್ನೂ ಯೆರೂಸಲೇಮಿನವರನ್ನೂ ಯೆಹೂದ್ಯರನ್ನೂ ನಿಮಗೆ ಬಹು ಕೇಡಾಗುವದೆಂದು ಎಚ್ಚರಿಸಿದರೂ ಇವರುಗಳು ಕೇಳಲಿಲ್ಲವಾದ್ದರಿಂದ ಆ ಕೇಡನ್ನೆಲ್ಲಾ ಇವರೆಲ್ಲರ ಮೇಲೆ ಬರಮಾಡುವೆನು.",
			"32": "<sup>32</sup> ಆಗ ಯೆರೆಮೀಯನು ಇನ್ನೊಂದು ಸುರಳಿಯನ್ನು ತೆಗೆದುಕೊಂಡು ನೇರೀಯನ ಮಗನಾಗಿರುವ ಲೇಖಕನಾದ ಬಾರೂಕನಿಗೆ ಕೊಡಲು ಅವನು ಅದರಲ್ಲಿ ಯೆಹೂದದ ಅರಸನಾದ ಯೆಹೋಯಾಕೀಮನು ಬೆಂಕಿಯಲ್ಲಿ ಸುಟ್ಟ ಗ್ರಂಥದ ಎಲ್ಲಾ ಮಾತುಗಳನ್ನಲ್ಲದೆ ಇನ್ನು ಇಂಥಾ ಅನೇಕ ಮಾತುಗಳನ್ನು ಯೆರೆಮೀಯನ ಬಾಯಿಂದ ಬಂದ ಹಾಗೆ ಬರೆದನು."
		},
		"37": {
			"1": "<sup>1</sup> ಯೋಷೀಯನ ಮಗನಾದ ಚಿದ್ಕೀಯನು ಬಾಬೆಲಿನ ಅರಸನಾದ ನೆಬೂಕದ್ನೆಚ್ಚರನಿಂದ ಯೆಹೂದದೇಶದ ಪಟ್ಟಕ್ಕೆ ತರಲ್ಪಟ್ಟು ಯೆಹೋಯಾಕೀಮನ ಮಗನಾದ ಕೊನ್ಯನಿಗೆ ಬದಲಾಗಿ ರಾಜ್ಯವಾಳುತ್ತಿದ್ದಾಗ",
			"2": "<sup>2</sup> ಅವನಾಗಲಿ ಅವನ ಸೇವಕರಾಗಲಿ ದೇಶದ ಜನರಾಗಲಿ ಯೆಹೋವನು ಪ್ರವಾದಿಯಾದ ಯೆರೆಮೀಯನಿಂದ ನುಡಿಸಿದ ಮಾತುಗಳನ್ನು ಕೇಳದೆ ಹೋದರು.",
			"3": "<sup>3</sup> ಹೀಗಿರುವಲ್ಲಿ ಅರಸನಾದ ಚಿದ್ಕೀಯನು ಶೆಲೆಮ್ಯನ ಮಗನಾದ ಯೆಹೂಕಲ, ಯಾಜಕ ಮಾಸೇಯನ ಮಗನಾದ ಚೆಫನ್ಯ, ಇವರ ಮೂಲಕ - ದಯಮಾಡಿ ನಮಗಾಗಿ ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಪ್ರಾರ್ಥಿಸು ಎಂದು ಪ್ರವಾದಿಯಾದ ಯೆರೆಮೀಯನಿಗೆ ಹೇಳಿಕಳುಹಿಸಿದನು.",
			"4": "<sup>4</sup> ಆ ಕಾಲದಲ್ಲಿ ಯೆರೆಮೀಯನು ಇನ್ನೂ ಸೆರೆಯಾಗಿರಲಿಲ್ಲ; ಜನರಲ್ಲಿ ಹೋಗುತ್ತಾ ಬರುತ್ತಾ ಇದ್ದನು.",
			"5": "<sup>5</sup> ಆಗ ಫರೋಹನ ಸೈನ್ಯವು ಐಗುಪ್ತದಿಂದ ಹೊರಟಿತ್ತು; ಇದನ್ನು ಕೇಳಿ ಯೆರೂಸಲೇಮನ್ನು ಮುತ್ತಿದ್ದ ಕಸ್ದೀಯರು ಬಿಟ್ಟು ಹೋಗಿದ್ದರು.",
			"6": "<sup>6</sup> ಹೀಗಿರಲು ಯೆಹೋವನು ಪ್ರವಾದಿಯಾದ ಯೆರೆಮೀಯನಿಗೆ ಈ ವಾಕ್ಯವನ್ನು ದಯಪಾಲಿಸಿದನು -",
			"7": "<sup>7</sup> ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರಾದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ನನ್ನನ್ನು ವಿಚಾರಿಸುವದಕ್ಕೆ ನಿಮ್ಮನ್ನು ನನ್ನ ಬಳಿಗೆ ಕಳುಹಿಸಿದ ಯೆಹೂದದ ಅರಸನಿಗೆ ಹೀಗೆ ಹೇಳಿರಿ - ಇಗೋ, ನಿಮ್ಮ ಸಹಾಯಕ್ಕೆ ಹೊರಟಿರುವ ಫರೋಹನ ಸೈನ್ಯವು ಸ್ವದೇಶವಾದ ಐಗುಪ್ತಕ್ಕೆ ಹಿಂದಿರುಗುವದು.",
			"8": "<sup>8</sup> ಕಸ್ದೀಯರು ಪುನಃ ಮತ್ತೆ ಬಂದು ಈ ಪಟ್ಟಣವನ್ನು ಪ್ರತಿಭಟಿಸಿ ಆಕ್ರಮಿಸಿ ಬೆಂಕಿಯಿಂದ ಸುಟ್ಟುಬಿಡುವರು.",
			"9": "<sup>9</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಕಸ್ದೀಯರು ನಮ್ಮಿಂದ ತೊಲಗಿ ಹೋಗುವದು ಖಂಡಿತ ಎಂದು ನಿಮ್ಮನ್ನು ನೀವೇ ಮೋಸಪಡಿಸಿಕೊಳ್ಳಬೇಡಿರಿ; ಅವರು ತೊಲಗುವದೇ ಇಲ್ಲ.",
			"10": "<sup>10</sup> ನಿಮ್ಮನ್ನು ಪ್ರತಿಭಟಿಸುವ ಕಸ್ದೀಯರಲ್ಲಿ ಗಾಯಪಟ್ಟವರು ಮಾತ್ರ ಉಳಿಯುವಂತೆ ನೀವು ಆ ಸೈನ್ಯವನ್ನೆಲ್ಲಾ ಒಂದು ವೇಳೆ ಹೊಡೆದಿದ್ದರೂ ಆ ಗಾಯಪಟ್ಟವರೇ ತಮ್ಮ ತಮ್ಮ ಗುಡಾರಗಳಿಂದ ಎದ್ದುಬಂದು ಈ ಪಟ್ಟಣವನ್ನು ಬೆಂಕಿಯಿಂದ ಸುಟ್ಟುಬಿಡುತ್ತಿದ್ದರು.",
			"11": "<sup>11</sup> ಕಸ್ದೀಯರ ಸೈನ್ಯವು ಫರೋಹನ ಸೈನ್ಯಕ್ಕೆ ಭಯಪಟ್ಟು ಯೆರೂಸಲೇಮನ್ನು ಬಿಟ್ಟುಹೋದ ಮೇಲೆ ಯೆರೆಮೀಯನು ಬೆನ್ಯಾಮೀನ್ ಸೀಮೆಯಲ್ಲಿ",
			"12": "<sup>12</sup> ತನಗೆ ಬರಬೇಕಾದ ಸ್ವಾಸ್ತ್ಯವನ್ನು ಜನರ ಸಮಕ್ಷಮ ತೆಗೆದುಕೊಳ್ಳಲು ಯೆರೂಸಲೇಮಿನಿಂದ ಅಲ್ಲಿಗೆ ಹೊರಟು",
			"13": "<sup>13</sup> ಬೆನ್ಯಾಮೀನಿನ ಬಾಗಿಲನ್ನು ಹಾದುಹೋಗುತ್ತಿರುವಾಗ ಶೆಲೆಮ್ಯನ ಮಗನೂ ಹನನ್ಯನ ಮೊಮ್ಮಗನೂ ಆದ ಇರೀಯನು ಅಲ್ಲಿ ಪಹರೆಕೊಡುತ್ತಿದ್ದು ಪ್ರವಾದಿಯಾದ ಆ ಯೆರೆಮೀಯನನ್ನು ಹಿಡಿದು ನೀನು ಕಸ್ದೀಯರನ್ನು ಮರೆಹೊಗಲು ಹೋಗುತ್ತಿದ್ದೀ ಎಂದು ಹೇಳಿದನು.",
			"14": "<sup>14</sup> ಅದಕ್ಕೆ ಯೆರೆಮೀಯನು - ಅದು ಸುಳ್ಳು, ನಾನು ಕಸ್ದೀಯರನ್ನು ಮರೆಹೊಗುವವನಲ್ಲ ಎನ್ನಲು ಇರೀಯನು ಅವನ ಮಾತನ್ನು ಕೇಳದೆ ಅವನನ್ನು ಹಿಡಿದು ಪ್ರಧಾನರ ಬಳಿಗೆ ಕರತಂದನು.",
			"15": "<sup>15</sup> ಆ ಪ್ರಧಾನರು ಯೆರೆಮೀಯನ ಮೇಲೆ ಕೋಪಗೊಂಡು ಅವನನ್ನು ಹೊಡೆಯಿಸಿ ತಾವು ಬಂದಿಖಾನೆಯನ್ನಾಗಿ ಮಾಡಿದ್ದ ಲೇಖಕನಾದ ಯೆಹೋನಾಥಾನನ ಮನೆಯಲ್ಲಿ ಸೆರೆಹಾಕಿಸಿದರು.",
			"16": "<sup>16</sup> ಯೆರೆಮೀಯನು ಆ ಬಂದಿಖಾನೆಯ ನೆಲಮನೆಗಳಲ್ಲಿ ಕೆಲವು ದಿವಸ ಇದ್ದ ಮೇಲೆ",
			"17": "<sup>17</sup> ಅರಸನಾದ ಚಿದ್ಕೀಯನು ಅವನನ್ನು ಕರತರಿಸಿ ಯೆಹೋವನಿಂದ ದೈವೋಕ್ತಿಯು ದೊರೆಯಿತೋ ಎಂದು ತನ್ನ ಮನೆಯಲ್ಲಿ ರಹಸ್ಯವಾಗಿ ವಿಚಾರಿಸಲು ಯೆರೆಮೀಯನು - ಹೌದು, ದೊರೆಯಿತು, ನೀನು ಬಾಬೆಲಿನ ಅರಸನ ಕೈಯಲ್ಲಿ ಸಿಕ್ಕಿ ಬೀಳುವಿ ಅಂದನು.",
			"18": "<sup>18</sup> ಅಲ್ಲದೆ ಯೆರೆಮೀಯನು ಅರಸನಾದ ಚಿದ್ಕೀಯನಿಗೆ - ನೀವು ನನ್ನನ್ನು ಸೆರೆಯಲ್ಲಿ ಹಾಕಿಸಿದ್ದಕ್ಕೆ ನಾನು ನಿನಗಾಗಲಿ ನಿನ್ನ ಸೇವಕರಿಗಾಗಲಿ ಈ ಜನರಿಗಾಗಲಿ ಯಾವ ಅಪರಾಧವನ್ನು ಮಾಡಿದೆನು?",
			"19": "<sup>19</sup> ಬಾಬೆಲಿನ ಅರಸನು ನಿಮಗೂ ಈ ದೇಶಕ್ಕೂ ವಿರುದ್ಧವಾಗಿ ಬಾರನು ಎಂದು ನಿಮಗೆ ಸಾರಿದ ನಿಮ್ಮ ಪ್ರವಾದಿಗಳು ಈಗ ಎಲ್ಲಿ?",
			"20": "<sup>20</sup> ಎನ್ನೊಡೆಯನೇ, ಅರಸನೇ, ಈಗ ದಯಮಾಡಿ ಲಾಲಿಸು; ಲೇಖಕನಾದ ಯೆಹೋನಾಥಾನನ ಮನೆಗೆ ನನ್ನನ್ನು ತಿರಿಗಿ ಸೇರಿಸಬೇಡ, ಅಲ್ಲೇ ಸತ್ತೇನು, ನನ್ನ ಬಿನ್ನಹವು ನಿನಗೆ ಮುಟ್ಟಲಿ ಎಂದು ಅರಿಕೆಮಾಡಿದನು.",
			"21": "<sup>21</sup> ಅರಸನಾದ ಚಿದ್ಕೀಯನು ಇದನ್ನು ಕೇಳಿ ಅಪ್ಪಣೆಕೊಡಲು ಯೆರೆಮೀಯನನ್ನು ಕಾರಾಗೃಹದ ಅಂಗಳದಲ್ಲಿಟ್ಟು ಪಟ್ಟಣದ ರೊಟ್ಟಿಯೆಲ್ಲಾ ತೀರುವ ತನಕ ರೊಟ್ಟಿಯ ಪೇಟೆಯಿಂದ ದಿನವಹಿ ಒಂದೊಂದು ರೊಟ್ಟಿಯನ್ನು ಅವನಿಗೆ ತಂದು ಕೊಡುತ್ತಿದ್ದರು. ಹೀಗೆ ಯೆರೆಮೀಯನು ಕಾರಾಗೃಹದ ಅಂಗಳದಲ್ಲೇ ವಾಸಿಸುತ್ತಿದ್ದನು."
		},
		"38": {
			"1": "<sup>1</sup> ಯೆರೆಮೀಯನು ಸಮಸ್ತ ಜನರಿಗೆ - ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಪಟ್ಟಣದಲ್ಲಿ ನಿಲ್ಲುವವನು ಖಡ್ಗಕ್ಷಾಮವ್ಯಾಧಿಗಳಿಂದ ಸಾಯುವನು; ಪಟ್ಟಣವನ್ನು ಬಿಟ್ಟುಹೋಗಿ ಕಸ್ದೀಯರನ್ನು ಮರೆಹೊಗುವವನು ಬದುಕುವನು, ತನ್ನ ಪ್ರಾಣವೊಂದನ್ನೇ ಬಾಚಿಕೊಂಡು ಹೋಗಿ ಬದುಕುವನು;",
			"2": "<sup>2</sup> ಬಾಬೆಲಿನ ಅರಸನು ಈ ಪಟ್ಟಣವನ್ನು ಆಕ್ರಮಿಸುವನು; ಅದು ಅವನ ಸೈನ್ಯದ ವಶವಾಗುವದು ಖಂಡಿತ ಎಂದು ಸಾರುತ್ತಿರುವಾಗ",
			"3": "<sup>3</sup> ಮತ್ತಾನನ ಮಗನಾದ ಶೆಫತ್ಯ, ಪಷ್ಹೂರನ ಮಗನಾದ ಗೆದಲ್ಯ, ಸೆಲೆಮ್ಯನ ಮಗನಾದ ಯೂಕಲ, ಮಲ್ಕೀಯನ ಮಗನಾದ ಪಷ್ಹೂರ, ಇವರು ಅವನ ಮಾತುಗಳನ್ನು ಕೇಳಿದರು.",
			"4": "<sup>4</sup> ಆಮೇಲೆ ಆ ಪ್ರಧಾನರು ಅರಸನಿಗೆ - ಒಡೆಯಾ, ಇವನಿಗೆ ಮರಣದಂಡನೆಯಾಗಬೇಕು. ಇವನು ಪಟ್ಟಣದಲ್ಲಿ ಉಳಿದಿರುವ ಯೋಧರಿಗೂ ಸಕಲ ಜನರಿಗೂ ಇಂಥಾ ಮಾತುಗಳನ್ನು ನುಡಿಯುತ್ತಾ ಅವರನ್ನು ಜೋಲುಗೈಯವರನ್ನಾಗಿ ಮಾಡುತ್ತಾನೆ; ಈ ಜನರ ಕ್ಷೇಮವನ್ನಲ್ಲ, ಹಾನಿಯನ್ನೇ ಹಾರೈಸುತ್ತಾನೆ ಎಂದು ಹೇಳಿದರು.",
			"5": "<sup>5</sup> ಅದಕ್ಕೆ ಅರಸನಾದ ಚಿದ್ಕೀಯನು - ಇಗೋ, ಯೆರೆಮೀಯನು ನಿಮ್ಮ ಕೈಯಲ್ಲಿದ್ದಾನೆ; ಅರಸನು ನಿಮಗೆ ಅಡ್ಡ ಬಂದು ಏನೂ ಮಾಡಬಲ್ಲವನಲ್ಲ ಎಂದು ಹೇಳಲು",
			"6": "<sup>6</sup> ಅವರು ಯೆರೆಮೀಯನನ್ನು ಹಿಡಿದು ಕಾರಾಗೃಹದ ಅಂಗಳದಲ್ಲಿ ರಾಜವಂಶೀಯನಾದ ಮಲ್ಕೀಯನ ಬಾವಿಯೊಳಗೆ ಹಗ್ಗಗಳಿಂದಿಳಿಸಿ ಹಾಕಿಬಿಟ್ಟರು. ಆ ಬಾವಿಯಲ್ಲಿ ನೀರಿರಲಿಲ್ಲ, ಕೆಸರಿತ್ತು; ಅದರೊಳಗೆ ಯೆರೆಮೀಯನು ದೊಸಕೊಂಡನು.",
			"7": "<sup>7</sup> ಅರಮನೆಯ ಕಂಚುಕಿಯಾದ ಎಬೆದ್ಮೆಲೆಕನೆಂಬ ಕೂಷ್ಯನು ಯೆರೆಮೀಯನನ್ನು ಬಾವಿಯಲ್ಲಿ ಹಾಕಿದ್ದಾರೆಂದು ಕೇಳಿದಾಗ",
			"8": "<sup>8</sup> ಅವನು ಅರಮನೆಯಿಂದ ಹೊರಟು ಬೆನ್ಯಾಮೀನಿನ ಬಾಗಿಲಿನಲ್ಲಿ ಕೂತುಕೊಂಡಿದ್ದ ಅರಸನ ಬಳಿಗೆ ಹೋಗಿ -",
			"9": "<sup>9</sup> ಎನ್ನೊಡೆಯನೇ, ಅರಸನೇ, ಇವರು ಪ್ರವಾದಿಯಾದ ಯೆರೆಮೀಯನಿಗೆ ಮಾಡಿದ್ದೆಲ್ಲಾ ದುಷ್ಟಕಾರ್ಯವೇ ಸರಿ, ಅವನನ್ನು ಬಾವಿಯಲ್ಲಿ ಹಾಕಿದ್ದಾರೆ; ಅವನು ಆಹಾರವಿಲ್ಲದೆ ಬಿದ್ದಲ್ಲೇ ಸಾಯುವದು ಖಂಡಿತ, ಪಟ್ಟಣದಲ್ಲಿ ಇನ್ನು ರೊಟ್ಟಿಯಿಲ್ಲವಲ್ಲಾ ಎಂದು ಅರಿಕೆಮಾಡಿದನು.",
			"10": "<sup>10</sup> ಅರಸನು ಇದನ್ನು ಕೇಳಿ ಕೂಷ್ಯನಾದ ಎಬೆದ್ಮೆಲೆಕನಿಗೆ - ನೀನು ಇಲ್ಲಿಂದ ಮೂವತ್ತು ಮಂದಿಯನ್ನು ಕರೆದುಕೊಂಡುಹೋಗಿ ಪ್ರವಾದಿಯಾದ ಯೆರೆಮೀಯನು ಸಾಯವದರೊಳಗಾಗಿ ಅವನನ್ನು ಬಾವಿಯಿಂದ ಎತ್ತಿಸು ಎಂದು ಅಪ್ಪಣೆಕೊಟ್ಟನು.",
			"11": "<sup>11</sup> ಕೂಡಲೇ ಕೂಷ್ಯನಾದ ಎಬೆದ್ಮೆಲೆಕನು ಅವರನ್ನು ಕರೆದುಕೊಂಡುಹೋಗಿ ಅರಮನೆಯನ್ನು ಹೊಕ್ಕು ಖಜಾನೆಯ ಕೆಳಗಣ ಕೋಣೆಯಿಂದ ಜೀರ್ಣವಾದ ಹರಕು ಚಿಂದಿಪಂದಿಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಬಾವಿಯೊಳಗೆ ಹಗ್ಗಗಳಿಂದ ಯೆರೆಮೀಯನಿಗೆ ಮುಟ್ಟಿಸಿ",
			"12": "<sup>12</sup> ಜೀರ್ಣವಾದ ಈ ಹರಕು ಚಿಂದಿಗಳನ್ನು ಹಗ್ಗಕ್ಕೆ ಸುತ್ತಿ ಕಂಕಳಲ್ಲಿ ಇಟ್ಟುಕೋ ಎನ್ನಲು ಯೆರೆಮೀಯನು ಅದರಂತೆಯೇ ಮಾಡಿದನು.",
			"13": "<sup>13</sup> ಈ ಪ್ರಕಾರ ಅವರು ಯೆರೆಮೀಯನನ್ನು ಹಗ್ಗಗಳ ಮೂಲಕ ಸೇದಿಕೊಂಡು ಬಾವಿಯಿಂದ ಮೇಲಕ್ಕೆ ಎತ್ತಿದರು; ಆಮೇಲೆ ಯೆರೆಮೀಯನು [ಹಿಂದಿನಂತೆ] ಕಾರಾಗೃಹದ ಅಂಗಳದಲ್ಲಿ ವಾಸಿಸುತ್ತಿದ್ದನು.",
			"14": "<sup>14</sup> ಹೀಗಿರುವಲ್ಲಿ ಅರಸನಾದ ಚಿದ್ಕೀಯನು ಪ್ರವಾದಿಯಾದ ಯೆರೆಮೀಯನನ್ನು ಯೆಹೋವನ ಆಲಯದ ಮೂರನೆಯ ಬಾಗಿಲಿಗೆ ಕರತರಿಸಿ - ನಾನು ನಿನ್ನನ್ನು ಒಂದು ಕೇಳುವೆನು, ನನಗೆ ಸ್ವಲ್ಪವೂ ಮರೆಮಾಡಬೇಡ ಎನ್ನಲು",
			"15": "<sup>15</sup> ಯೆರೆಮೀಯನು ಚಿದ್ಕೀಯನಿಗೆ - ನಾನು ನಿನಗೆ ತಿಳಿಸಿದರೆ ನನ್ನನ್ನು ಸಾಯಿಸಿಯೇ ಸಾಯಿಸುವಿ ಅಲ್ಲವೇ? ಆಲೋಚನೆಹೇಳಿದರೂ ನೀನು ಕೇಳುವದಿಲ್ಲ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"16": "<sup>16</sup> ಆಗ ಅರಸನಾದ ಚಿದ್ಕೀಯನು ಯೆರೆಮೀಯನಿಗೆ - ಈ ಪ್ರಾಣವನ್ನು ನಮಗೆ ದಯಪಾಲಿಸಿದ ಯೆಹೋವನ ಜೀವದಾಣೆ, ನಾನು ನಿನ್ನನ್ನು ಸಾಯಿಸೆನು, ನಿನ್ನ ಪ್ರಾಣವನ್ನು ಹುಡುಕುವ ಇವರ ಕೈಗೂ ನಿನ್ನನ್ನು ಸಿಕ್ಕಿಸೆನು ಎಂದು ರಹಸ್ಯವಾಗಿ ಪ್ರಮಾಣಮಾಡಲು",
			"17": "<sup>17</sup> ಯೆರೆಮೀಯನು ಚಿದ್ಕೀಯನಿಗೆ - ಸೇನಾಧೀಶ್ವರ ಸ್ವಾಮಿಯೂ ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರೂ ಆದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ನೀನು ಬಾಬೆಲಿನ ಅರಸನ ಸರದಾರರನ್ನು ಮರೆಹೊಕ್ಕರೆ ನಿನ್ನ ಪ್ರಾಣವುಳಿಯುವದು, ಈ ಪಟ್ಟಣವೂ ಬೆಂಕಿಯಿಂದ ಸುಡದು, ನೀನೂ ನಿನ್ನ ಮನೆತನದವರೂ ಬದುಕುವಿರಿ;",
			"18": "<sup>18</sup> ನೀನು ಬಾಬೆಲಿನ ಅರಸನ ಸರದಾರರನ್ನು ಮರೆಹೊಗದಿದ್ದರೆ ಈ ಪಟ್ಟಣವು ಕಸ್ದೀಯರ ಕೈವಶವಾಗುವದು. ಅವರು ಅದನ್ನು ಬೆಂಕಿಯಿಂದ ಸುಟ್ಟುಬಿಡುವರು, ನೀನು ಅವರ ಕೈಯಿಂದ ತಪ್ಪಿಸಿಕೊಳ್ಳಲಾರೆ ಎಂದು ಹೇಳಿದನು.",
			"19": "<sup>19</sup> ಇದನ್ನು ಕೇಳಿ ಅರಸನಾದ ಚಿದ್ಕೀಯನು ಯೆರೆಮೀಯನಿಗೆ - ಕಸ್ದೀಯರು ತಮ್ಮನ್ನು ಮರೆಹೊಕ್ಕಿರುವ ಯೆಹೂದ್ಯರಿಗೆ ನನ್ನನ್ನು ಒಪ್ಪಿಸುವರೋ ಏನೋ; ಅವರು ನನ್ನನ್ನು ಹಿಂಸಿಸಿಯಾರು ಎಂದು ಶಂಕೆಪಡುತ್ತೇನೆ ಎಂದುತ್ತರಕೊಡಲು",
			"20": "<sup>20</sup> ಯೆರೆಮೀಯನು - ನಿನ್ನನ್ನು ಒಪ್ಪಿಸರು; ಜೀಯಾ, ನಾನು ನಿನಗೆ ಹೇಳಿರುವ ಯೆಹೋವನ ಮಾತನ್ನು ಕೈಕೊಳ್ಳು; ಕೈಕೊಂಡರೆ ಪ್ರಾಣವುಳಿಯುವದು, ಸುಖವೂ ಆಗುವದು.",
			"21": "<sup>21</sup> ಆದರೆ ನೀನು - ಶತ್ರುವನ್ನು ಮರೆಹೊಗೆನು ಎನ್ನುವ ಪಕ್ಷದಲ್ಲಿ ಯೆಹೋವನು ನಿನ್ನ ಮುಂದಿನ ಗತಿಯನ್ನು ನನಗೆ ಹೀಗೆ ತೋರ್ಪಡಿಸಿದ್ದಾನೆ -",
			"22": "<sup>22</sup> ಯೆಹೂದದ ಅರಸನ ಮನೆಯಲ್ಲಿ ಉಳಿದಿರುವ ಸಕಲ ಸ್ತ್ರೀಯರು ಬಾಬೆಲಿನ ಅರಸನ ಸರದಾರರ ಬಳಿಗೆ ತರಲ್ಪಟ್ಟವರಾಗಿ ನಿನ್ನನ್ನು ಪ್ರೇರಿಸಿ ಒಳಪಡಿಸಿಕೊಂಡಿದ್ದ ನಿನ್ನ ಆಪ್ತಮಿತ್ರರು ನಿನ್ನ ಕಾಲುಗಳು ಬದಿಯಲ್ಲಿ ಹೂತದ್ದನ್ನು ನೋಡಿ ಹಿಂದಿರುಗಿದ್ದಾರೆ ಎಂದು ನಿಂದಿಸುವರು.",
			"23": "<sup>23</sup> ನಿನ್ನ ಸಮಸ್ತ ಪತ್ನಿಯರೂ ಕುಮಾರಕುಮಾರ್ತೆಯರೂ ಕಸ್ದೀಯರ ಬಳಿಗೆ ತರಲ್ಪಡುವರು; ನೀನೂ ಅವರಿಂದ ತಪ್ಪಿಸಿಕೊಳ್ಳಲಾರದೆ ಬಾಬೆಲಿನ ಅರಸನ ಕೈಗೆ ಸಿಕ್ಕಿಕೊಳ್ಳುವಿ; ಈ ಪಟ್ಟಣವು ನಿನ್ನ ನಿಮಿತ್ತ ಬೆಂಕಿಯಿಂದ ಸುಟ್ಟುಹೋಗುವದು ಎಂದು ಹೇಳಿದನು.",
			"24": "<sup>24</sup> ಆಗ ಚಿದ್ಕೀಯನು ಯೆರೆಮೀಯನಿಗೆ - ಈ ಮಾತುಗಳು ಯಾರಿಗೂ ತಿಳಿಯದಿರಲಿ, ಜೋಕೆ; ತಿಳಿಯದಿದ್ದರೆ ನೀನು ಸಾಯೆ.",
			"25": "<sup>25</sup> ನಾನು ನಿನ್ನ ಸಂಗಡ ಮಾತಾಡಿದ ಸುದ್ದಿಯನ್ನು ಪ್ರಧಾನರು ಕೇಳಿ ನಿನ್ನ ಬಳಿಗೆ ಬಂದು - ನೀನು ಅರಸನಿಗೆ ಹೇಳಿದ್ದನ್ನು ನಮಗೆ ತಿಳಿಸು; ನಮಗೆ ಮರೆಮಾಡದಿದ್ದರೆ ನಿನ್ನನ್ನು ಕೊಲ್ಲೆವು; ಅರಸನು ನಿನಗೆ ಹೇಳಿದ್ದನ್ನೂ ತಿಳಿಸು ಎಂದು ಪ್ರಶ್ನೆಮಾಡಿದ ಪಕ್ಷದಲ್ಲಿ",
			"26": "<sup>26</sup> ನೀನು ಅವರಿಗೆ - ಯೆಹೋನಾಥಾನನ ಮನೆಗೆ ನನ್ನನ್ನು ತಿರಿಗಿ ಸೇರಿಸಬೇಡ, ಅಲ್ಲೇ ಸತ್ತೇನು ಎಂದು ಅರಸನಿಗೆ ಬಿನ್ನಹ ಮಾಡಿಕೊಂಡೆನು ಎಂಬದಾಗಿ ಹೇಳು ಎಂದು ಅಪ್ಪಣೆಕೊಟ್ಟನು.",
			"27": "<sup>27</sup> ಕೆಲವು ಕಾಲದ ಮೇಲೆ ಸಕಲ ಪ್ರಧಾನರು ಯೆರೆಮೀಯನ ಬಳಿಗೆ ಬಂದು ಹಾಗೆಯೇ ಪ್ರಶ್ನೆಮಾಡಿದರು. ಅವನು ಅರಸನ ಅಪ್ಪಣೆಯಂತೆಯೇ ಉತ್ತರಕೊಡಲು ಅವರು ಮಾತನ್ನು ಮುಗಿಸಿದರು; ಏನೂ ತಿಳಿದುಕೊಳ್ಳಲಿಲ್ಲ.",
			"28": "<sup>28</sup> ಯೆರೂಸಲೇಮು ಶತ್ರುವಶವಾಗುವ ತನಕ ಯೆರೆಮೀಯನು ಕಾರಾಗೃಹದ ಅಂಗಳದಲ್ಲೇ ವಾಸಿಸುತ್ತಿದ್ದನು."
		},
		"39": {
			"1": "<sup>1</sup> ಯೆಹೂದದ ಅರಸನಾದ ಚಿದ್ಕೀಯನ ಆಳಿಕೆಯ ಒಂಭತ್ತನೆಯ ವರುಷದ ಹತ್ತನೆಯ ತಿಂಗಳಿನಲ್ಲಿ ಬಾಬೆಲಿನ ಅರಸನಾದ ನೆಬೂಕದ್ನೆಚ್ಚರನು ಸಕಲ ಸೈನ್ಯಸಹಿತ ಬಂದು ಯೆರೂಸಲೇಮನ್ನು ಮುತ್ತಿದನು.",
			"2": "<sup>2</sup> ಚಿದ್ಕೀಯನ ಹನ್ನೊಂದನೆಯ ವರುಷದ ನಾಲ್ಕನೆಯ ತಿಂಗಳಿನ ಒಂಭತ್ತನೆಯ ದಿನದಲ್ಲಿ ಪೌಳಿಗೋಡೆ ಒಡಕುಬಿದ್ದು ಯೆರೂಸಲೇಮು ಶತ್ರುವಶವಾಗಲು",
			"3": "<sup>3</sup> ನೇರ್ಗಲ್‍ಸರೆಚರ್, ಸಮ್ಗರ್‍ನೆಬೋ, ಕಂಚುಕಿಯರ ಮುಖ್ಯಸ್ಥನಾದ ಸರ್ಸೆಕೀಮ್, ಜೋಯಿಸರ ಮುಖ್ಯಸ್ಥನಾದ ನೇರ್ಗಲ್‍ಸರೆಚರ್, ಅಂತು ಬಾಬೆಲಿನ ಅರಸನ ಸಕಲ ಸರದಾರರೂ ಪುರಪ್ರವೇಶಮಾಡಿ ಮಧ್ಯಮದ್ವಾರದೊಳಗೆ ಆಸೀನರಾದರು.",
			"4": "<sup>4</sup> ಯೆಹೂದದ ಅರಸನಾದ ಚಿದ್ಕೀಯನೂ ಸಮಸ್ತ ಸೈನಿಕರೂ ಇವರನ್ನು ನೋಡಿ ಅದೇ ರಾತ್ರಿಯಲ್ಲಿ ಹೊರಟು ಅರಸನ ಉದ್ಯಾನಮಾರ್ಗವಾಗಿ ಎರಡು ಗೋಡೆಗಳ ನಡುವಣ ಬಾಗಿಲಿಂದ ಪಟ್ಟಣವನ್ನು ಬಿಟ್ಟು ಅರಾಬಾ ಎಂಬ ತಗ್ಗಾದ ಪ್ರದೇಶದ ಕಡೆಗೆ ಪಲಾಯನಮಾಡಿದರು.",
			"5": "<sup>5</sup> ಕಸ್ದೀಯರ ಸೈನಿಕರು ಅವರನ್ನು ಹಿಂದಟ್ಟುತ್ತಾ ಯೆರಿಕೋವಿನ ಬೈಲಿನಲ್ಲಿ ಚಿದ್ಕೀಯನನ್ನು ತಗಲಿ ಹಿಡಿದು ಹಮಾತ್ ಸೀಮೆಯ ರಿಬ್ಲದಲ್ಲಿದ್ದ ನೆಬೂಕದ್ನೆಚ್ಚರನೆಂಬ ಬಾಬೆಲಿನ ಅರಸನ ಬಳಿಗೆ ತೆಗೆದುಕೊಂಡು ಬಂದರು; ಆ ಬಾಬೆಲಿನ ಅರಸನು ಅಲ್ಲಿ ಚಿದ್ಕೀಯನಿಗೆ ಶಿಕ್ಷೆಯನ್ನು ವಿಧಿಸಿ",
			"6": "<sup>6</sup> ಅವನ ಮಕ್ಕಳನ್ನು ಅಲ್ಲೇ ಅವನ ಕಣ್ಣೆದುರಿಗೆ ವಧಿಸಿ ಯೆಹೂದದ ಸಮಸ್ತ ಶ್ರೀಮಂತರನ್ನೂ ಕೊಲ್ಲಿಸಿದನು;",
			"7": "<sup>7</sup> ಇದಲ್ಲದೆ ಚಿದ್ಕೀಯನ ಕಣ್ಣುಗಳನ್ನು ಕೀಳಿಸಿ ಅವನನ್ನು ಬಾಬೆಲಿಗೆ ಸಾಗಿಸಬೇಕೆಂದು ಬೇಡಿಹಾಕಿಸಿದನು.",
			"8": "<sup>8</sup> ಮತ್ತು ಕಸ್ದೀಯರು ಅರಮನೆಯನ್ನೂ ಪ್ರಜೆಗಳ ಮನೆಗಳನ್ನೂ ಬೆಂಕಿಯಿಂದ ಸುಟ್ಟು ಯೆರೂಸಲೇಮಿನ ಸುತ್ತಣ ಗೋಡೆಗಳನ್ನು ಕೆಡವಿದರು.",
			"9": "<sup>9</sup> ಆಗ ಕಾವಲು ದಂಡಿನ ಅಧಿಪತಿಯಾದ ನೆಬೂಜರದಾನನು ಪಟ್ಟಣದಲ್ಲಿ ಉಳಿದವರನ್ನೂ ಮೊದಲೇ ತನ್ನನ್ನು ಮರೆಹೊಕ್ಕವರನ್ನೂ ಇತರ ಸಮಸ್ತ ಜನರನ್ನೂ ಬಾಬೆಲಿಗೆ ಸೆರೆಯೊಯ್ದನು.",
			"10": "<sup>10</sup> ಆದರೆ ಏನೂ ಇಲ್ಲದ ಕೆಲವು ಬಡ ಜನರನ್ನು ಯೆಹೂದ ದೇಶದಲ್ಲಿ ಬಿಟ್ಟು ಆಗಲೇ ಅವರಿಗೆ ಹೊಲತೋಟಗಳನ್ನು ಕೊಟ್ಟನು.",
			"11": "<sup>11</sup> ಬಾಬೆಲಿನ ಅರಸನಾದ ನೆಬೂಕದ್ನೆಚ್ಚರನು ಯೆರೆಮೀಯನ ವಿಷಯವಾಗಿ ಕಾವಲು ದಂಡಿನ ಅಧಿಪತಿಯಾದ ನೆಬೂಜರದಾನನಿಗೆ -",
			"12": "<sup>12</sup> ಅವನನ್ನು ಕರೆಸಿಕೊಂಡು ಏನೂ ಕೇಡುಮಾಡದೆ ಅವನು ಬೇಡುವದನ್ನೆಲ್ಲಾ ನೆರವೇರಿಸಿ ಕಟಾಕ್ಷಿಸು ಎಂದು ಅಪ್ಪಣೆಕೊಡಲು",
			"13": "<sup>13</sup> ಕಾವಲುದಂಡಿನ ಅಧಿಪತಿಯಾದ ನೆಬೂಜರದಾನ್, ಕಂಚುಕಿಯರ ಮುಖ್ಯಸ್ಥನಾದ ನೆಬೂಷಜ್ಬಾನ್, ಜೋಯಿಸರ ಮುಖ್ಯಸ್ಥನಾದ ನೇರ್ಗಲ್‍ಸರೆಚರ್ ಅಂತು ಬಾಬೆಲಿನ ಅರಸನ ಸಮಸ್ತ ಮುಖ್ಯಾಧಿಕಾರಿಗಳು",
			"14": "<sup>14</sup> ಯೆರೆಮೀಯನನ್ನು ಕಾರಾಗೃಹದ ಅಂಗಳದಿಂದ ಕರತರಿಸಿ ಅಹೀಕಾಮನ ಮಗನೂ ಶಾಫಾನನ ಮೊಮ್ಮಗನೂ ಆದ ಗೆದಲ್ಯನ ವಶಕ್ಕೆ ಒಪ್ಪಿಸಿ ಮನೆಗೆ ಕರೆದುಕೊಂಡು ಹೋಗುವಂತೆ ಹೇಳಿದರು. ಅಂದಿನಿಂದ ಯೆರೆಮೀಯನು [ಎಂದಿನಂತೆ] ಜನರ ಮಧ್ಯೆ ವಾಸಿಸಿದನು.",
			"15": "<sup>15</sup> ಯೆರೆಮೀಯನು ಕಾರಾಗೃಹದ ಅಂಗಳದಲ್ಲಿ ಸೆರೆಯಾಗಿದ್ದಾಗ ಯೆಹೋವನು ಅವನಿಗೆ ಈ ಮಾತನ್ನು ದಯಪಾಲಿಸಿದನು -",
			"16": "<sup>16</sup> ನೀನು ಹೋಗಿ ಕೂಷ್ಯನಾದ ಎಬೆದ್ಮೆಲೆಕನಿಗೆ ಹೀಗೆ ಹೇಳು - ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರೂ ಸೇನಾಧೀಶ್ವರನೂ ಆದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಆಹಾ, ನಾನು ನುಡಿದ ಮೇಲನ್ನಲ್ಲ, ನುಡಿದ ಕೇಡನ್ನೇ ಈ ಪಟ್ಟಣದ ಮೇಲೆ ಬರಮಾಡುವೆನು; ಅದು ಆ ದಿನದಲ್ಲಿ ನಿನ್ನ ಕಣ್ಣೆದುರಿಗೆ ಆಗುವದು.",
			"17": "<sup>17</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಆ ದಿನದಲ್ಲಿ ನಾನು ನಿನ್ನನ್ನು ಉದ್ಧರಿಸುವೆನು; ನೀನು ಹೆದರುವ ಮನುಷ್ಯರ ಕೈಗೆ ಸಿಕ್ಕುವದಿಲ್ಲ.",
			"18": "<sup>18</sup> ನೀನು ನನ್ನಲ್ಲಿ ಭರವಸವಿಟ್ಟ ಕಾರಣ ನಿನ್ನನ್ನು ರಕ್ಷಿಸೇ ರಕ್ಷಿಸುವೆನು; ನೀನು ಖಡ್ಗಕ್ಕೆ ತುತ್ತಾಗದೆ ಅದರ ಬಾಯೊಳಗಿಂದ ನಿನ್ನ ಪ್ರಾಣವನ್ನು ಸೆಳಕೊಂಡು ಹೋಗುವಿ; ಇದು ಯೆಹೋವನ ನುಡಿ."
		},
		"40": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಮತ್ತೊಮ್ಮೆ ತನ್ನ ವಾಕ್ಯವನ್ನು ಯೆರೆಮೀಯನಿಗೆ ದಯಪಾಲಿಸಿದನು. ಇಷ್ಟರೊಳಗೆ ಕಾವಲುದಂಡಿನ ಅಧಿಪತಿಯಾದ ನೆಬೂಜರದಾನನು ಯೆರೂಸಲೇಮಿನವರ ಮತ್ತು ಯೆಹೂದ್ಯರ ಗುಂಪನ್ನು ಬಾಬೆಲಿಗೆ ಸೆರೆಯೊಯ್ಯುತ್ತಿದ್ದಾಗ ಅವರಲ್ಲಿ ಸಂಕೋಲೆಗಳಿಂದ ಕಟ್ಟಲ್ಪಟ್ಟಿದ್ದ ಯೆರೆಮೀಯನನ್ನು ರಾಮದ ಬಳಿಯಲ್ಲಿ ಬಿಡಿಸಿಬಿಟ್ಟಿದ್ದನು.",
			"2": "<sup>2</sup> ಆಗ ಕಾವಲು ದಂಡಿನ ಅಧಿಪತಿಯು ಯೆರೆಮೀಯನನ್ನು ಕರೆಯಿಸಿ ಅವನಿಗೆ - ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನು ಈ ಸ್ಥಳಕ್ಕೆ ಇಂಥ ಕೆಡುಕಾಗಲಿ ಎಂದು ಶಾಪಕೊಟ್ಟನು;",
			"3": "<sup>3</sup> ಆತನು ಅದನ್ನೇ ಬರಮಾಡಿ ತನ್ನ ಮಾತನ್ನು ನೆರವೇರಿಸಿದ್ದಾನೆ; ನೀವು ಯೆಹೋವನ ಧ್ವನಿಗೆ ಕಿವಿಗೊಡದೆ ಆತನಿಗೆ ಪಾಪಮಾಡಿದ್ದರಿಂದಲೇ ಇದು ನಿಮಗೆ ಸಂಭವಿಸಿದೆ.",
			"4": "<sup>4</sup> ಇಗೋ, ನಿನ್ನ ಕೈಗೆ ಹಾಕಿದ ಬೇಡಿಗಳನ್ನು ಈ ದಿವಸ ತೆಗೆಸಿದ್ದೇನೆ. ನನ್ನ ಸಂಗಡ ಬಾಬೆಲಿಗೆ ಬರಬೇಕೆಂದು ನಿನಗೆ ಮನಸ್ಸಿದ್ದರೆ ಬಾ; ನಿನ್ನನ್ನು ಕಟಾಕ್ಷಿಸುವೆನು; ನನ್ನ ಸಂಗಡ ಬಾಬೆಲಿಗೆ ಬರುವದಕ್ಕೆ ಮನಸ್ಸಿಲ್ಲದೆ ಹೋದರೆ ನಿಂತುಬಿಡು; ಇಗೋ, ದೇಶವೆಲ್ಲಾ ನಿನ್ನ ಕಣ್ಣೆದುರಿಗಿದೆ; ನಿನಗೆ ಯಾವ ಕಡೆ ಹೋಗುವದು ಸರಿಯೋ ಒಳ್ಳೆದೋ ಆ ಕಡೆಗೆ ಹೋಗು ಎಂದು ಹೇಳಿದನು.",
			"5": "<sup>5</sup> ಯೆರೆಮೀಯನು ಇನ್ನೂ ಹಿಂದಿರುಗದಿರುವಲ್ಲಿ ನೆಬೂಜರದಾನನು ಅವನಿಗೆ - ಬಾಬೆಲಿನ ಅರಸನು ಅಹೀಕಾಮನ ಮಗನೂ ಶಾಫಾನನ ಮೊಮ್ಮಗನೂ ಆದ ಗೆದಲ್ಯನನ್ನು ಯೆಹೂದದ ಪಟ್ಟಣಗಳಿಗೆ ಅಧಿಪತಿಯನ್ನಾಗಿ ನೇಮಿಸಿದ್ದಾನಲ್ಲಾ; ಅವನ ಕಡೆಗೆ ನೀನು ಹಿಂದಿರುಗಿ ಅವನ ಹತ್ತಿರ ಜನರ ಮಧ್ಯೆ ವಾಸಿಸು; ಇಲ್ಲವೆ ಎಲ್ಲಿ ಬೇಕಾದರೂ ಹೋಗು ಎಂದು ಹೇಳಿ ಬುತ್ತಿಯನ್ನೂ ಬಹುಮಾನವನ್ನೂ ಕೊಡಿಸಿ ಅವನನ್ನು ಕಳುಹಿಸಿಬಿಟ್ಟನು.",
			"6": "<sup>6</sup> ಆಗ ಯೆರೆಮೀಯನು ಅಹೀಕಾಮನ ಮಗನಾದ ಗೆದಲ್ಯನು ಇದ್ದ ಮಿಚ್ಪಕ್ಕೆ ಹೋಗಿ ದೇಶದಲ್ಲಿ ಉಳಿದ ಜನರ ಮಧ್ಯೆ ಅವನ ಸಂಗಡ ವಾಸಿಸಿದನು.",
			"7": "<sup>7</sup> ಬಾಬೆಲಿನ ಅರಸನು ಅಹೀಕಾಮನ ಮಗನಾದ ಗೆದಲ್ಯನನ್ನು ದೇಶಾಧಿಪತಿಯನ್ನಾಗಿ ನೇಮಿಸಿ ಬಾಬೆಲಿಗೆ ಸೆರೆಯೊಯ್ಯಲ್ಪಡದ ದೇಶೀಯರಲ್ಲಿ ದರಿದ್ರರಾದ ಗಂಡಸರನ್ನೂ ಹೆಂಗಸರನ್ನೂ ಮಕ್ಕಳನ್ನೂ ಅವನ ಅಧಿಕಾರಕ್ಕೆ ಒಪ್ಪಿಸಿದನೆಂಬ ಸುದ್ದಿಯನ್ನು ಕಾಡುಮೇಡುಗಳಲ್ಲಿದ್ದ ಸಕಲ ಯೆಹೂದ ಸೈನಿಕರೂ ಸೇನಾಪತಿಗಳೂ ಕೇಳಿ ಮಿಚ್ಪದಲ್ಲಿ ವಾಸವಾಗಿದ್ದ ಅವನ ಬಳಿಗೆ ಹೋದರು.",
			"8": "<sup>8</sup> ಆ ಸೇನಾಪತಿಗಳು ಯಾರಂದರೆ - ನೆತನ್ಯನ ಮಗನಾದ ಇಷ್ಮಾಯೇಲ, ಕಾರೇಹನ ಮಕ್ಕಳಾದ ಯೋಹಾನಾನ ಯೋನಾಥಾನ, ತನ್ಹುಮೆತನ ಮಗನಾದ ಸೆರಾಯ, ಮಾಕಾ ಊರಿನವನ ಮಗನಾದ ಯೆಜನ್ಯ, ನೆಟೋಫದವನಾದ ಏಫಯನ ಮಕ್ಕಳು, ಇವರೇ.",
			"9": "<sup>9</sup> ಅಹೀಕಾಮನ ಮಗನೂ ಶಾಫಾನನ ಮೊಮ್ಮಗನೂ ಆದ ಗೆದಲ್ಯನು ಇವರಿಗೂ ಇವರ ಸೈನಿಕರಿಗೂ - ಕಸ್ದೀಯರಿಗೆ ಅಧೀನರಾಗಿರಲಿಕ್ಕೆ ಹೆದರಬೇಡಿರಿ; ಬಾಬೆಲಿನ ಅರಸನಿಗೆ ಅಧೀನರಾಗಿ ಇಲ್ಲಿಯೇ ವಾಸಿಸಿರಿ; ಆಗ ನಿಮಗೆ ಸುಖವಾಗುವದು.",
			"10": "<sup>10</sup> ನಾನಂತು ಮಿಚ್ಪದಲ್ಲಿ ವಾಸವಾಗಿದ್ದು ನಮ್ಮ ಬಳಿಗೆ ಬರುವ ಕಸ್ದೀಯರ ಮುಂದೆ ನಿಮ್ಮ ಪಕ್ಷವನ್ನು ವಹಿಸುವೆನು; ನೀವು ದ್ರಾಕ್ಷಾರಸ, ಹಣ್ಣು, ಎಣ್ಣೆ, ಇವುಗಳನ್ನು ಸಂಗ್ರಹಿಸಿ ನಿಮ್ಮ ಪಾತ್ರೆಗಳಲ್ಲಿ ತುಂಬಿಸಿಟ್ಟು ನೀವು ಹಿಡಿದ ಪಟ್ಟಣಗಳಲ್ಲಿ ವಾಸಿಸಿರಿ ಎಂದು ಖಂಡಿತವಾಗಿ ಹೇಳಿದನು.",
			"11": "<sup>11</sup> ಇದಲ್ಲದೆ ಅಮ್ಮೋನ್ಯರ ಮಧ್ಯದೊಳಗೂ ಮೋವಾಬ್ ಎದೋಮ್ ಮೊದಲಾದ ದೇಶಗಳಲ್ಲಿಯೂ ಚದರಿಹೋಗಿದ್ದ ಯೆಹೂದ್ಯರೆಲ್ಲರೂ ಬಾಬೆಲಿನ ಅರಸನು ಯೆಹೂದದಲ್ಲಿ ಕೆಲವರನ್ನು ಉಳಿಸಿ ಅಹೀಕಾಮನ ಮಗನೂ ಶಾಫಾನನ ಮೊಮ್ಮಗನೂ ಆದ ಗೆದಲ್ಯನನ್ನು ಅವರಿಗೆ ಅಧಿಪತಿಯನ್ನಾಗಿ ನೇಮಿಸಿದ್ದಾನೆಂಬ ವರ್ತಮಾನವನ್ನು ಕೇಳಿ",
			"12": "<sup>12</sup> ತಾವು ಅಟ್ಟಲ್ಪಟ್ಟು ಸೇರಿದ್ದ ಎಲ್ಲಾ ಸ್ಥಳಗಳಿಂದಲೂ ಯೆಹೂದಕ್ಕೆ ಹಿಂದಿರುಗಿ ಮಿಚ್ಪದಲ್ಲಿ ವಾಸವಾಗಿದ್ದ ಗೆದಲ್ಯನ ಬಳಿಗೆ ಬಂದು ದ್ರಾಕ್ಷಾರಸವನ್ನೂ ಹಣ್ಣುಗಳನ್ನೂ ಸಮೃದ್ಧಿಯಾಗಿ ಸಂಗ್ರಹಿಸಿದರು.",
			"13": "<sup>13</sup> ಆಮೇಲೆ ಕಾರೇಹನ ಮಗನಾದ ಯೋಹಾನಾನನೂ ಕಾಡುಮೇಡುಗಳಲ್ಲಿದ್ದ ಸಕಲ ಸೇನಾಪತಿಗಳೂ ಮಿಚ್ಪದಲ್ಲಿ ವಾಸವಾಗಿದ್ದ ಗೆದಲ್ಯನ ಬಳಿಗೆ ಹೋಗಿ -",
			"14": "<sup>14</sup> ಅಮ್ಮೋನ್ಯರ ಅರಸನಾದ ಬಾಲೀಸನು ನಿನ್ನನ್ನು ಕೊಲ್ಲುವದಕ್ಕಾಗಿ ನೆತನ್ಯನ ಮಗನಾದ ಇಷ್ಮಾಯೇಲನನ್ನು ಕಳುಹಿಸಿದ್ದಾನೆಂಬದು ನಿನಗೆ ಗೊತ್ತಿದೆಯೋ ಎಂದು ಕೇಳಿದರು. ಆದರೆ ಅಹೀಕಾಮನ ಮಗನಾದ ಗೆದಲ್ಯನು ಅವರನ್ನು ನಂಬಲಿಲ್ಲ.",
			"15": "<sup>15</sup> ಆಗ ಕಾರೇಹನ ಮಗನಾದ ಯೋಹಾನಾನನು ಮಿಚ್ಪದಲ್ಲಿ ಗೆದಲ್ಯನಿಗೆ - ನಾನು ಹೋಗಿ ನೆತನ್ಯನ ಮಗನಾದ ಇಷ್ಮಾಯೇಲನನ್ನು ಕೊಲ್ಲಲಿಕ್ಕೆ ಅಪ್ಪಣೆಯಾಗಲಿ; ಇದು ನಿನ್ನ ಅಪ್ಪಣೆ ಎಂದು ಯಾರಿಗೂ ತಿಳಿದು ಬರುವದಿಲ್ಲ; ಅವನು ನಿನ್ನನ್ನು ಕೊಂದರೆ ನಿನ್ನ ಆಶ್ರಯದ ಯೆಹೂದ್ಯರೆಲ್ಲರೂ ದಿಕ್ಕಾಪಾಲಾಗಿ ಯೆಹೂದದ ಶೇಷವು ನಿಶ್ಶೇಷವಾಗುವದಲ್ಲಾ; ಏಕೆ ಹೀಗಾಗಬೇಕು ಎಂದು ರಹಸ್ಯವಾಗಿ ವಿಜ್ಞಾಪಿಸಲು",
			"16": "<sup>16</sup> ಅಹೀಕಾಮನ ಮಗನಾದ ಗೆದಲ್ಯನು ಅವನಿಗೆ - ನೀನು ಹಾಗೆ ಮಾಡಲೇ ಕೂಡದು; ನೀನು ಇಷ್ಮಾಯೇಲನ ವಿಷಯವಾಗಿ ಹೇಳಿದ್ದು ಸುಳ್ಳು ಎಂದು ಉತ್ತರಕೊಟ್ಟನು."
		},
		"41": {
			"1": "<sup>1</sup> ಏಳನೆಯ ತಿಂಗಳಿನಲ್ಲಿ ರಾಜವಂಶದವನೂ ಎಲೀಷಾಮನ ಮೊಮ್ಮಗನೂ ನೆತನ್ಯನ ಮಗನೂ ಅರಸನ ಪ್ರಧಾನರಲ್ಲಿ ಒಬ್ಬನೂ ಆದ ಇಷ್ಮಾಯೇಲನು ಹತ್ತು ಮಂದಿಯೊಂದಿಗೆ ಅಹೀಕಾಮನ ಮಗನಾದ ಗೆದಲ್ಯನು ವಾಸವಾಗಿದ್ದ ಮಿಚ್ಪಕ್ಕೆ ಬಂದನು; ಅಲ್ಲಿ ಅವನೊಂದಿಗೆ ಊಟಮಾಡಿದರು.",
			"2": "<sup>2</sup> ಆಗ ನೆತನ್ಯನ ಮಗನಾದ ಇಷ್ಮಾಯೇಲನೂ ಅವನೊಂದಿಗಿದ್ದ ಹತ್ತು ಜನರೂ ಎದ್ದು ಅಹೀಕಾಮನ ಮಗ ಶಾಫಾನನ ಮೊಮ್ಮಗ ಬಾಬೆಲಿನ ಅರಸನಿಂದ ದೇಶಾಧಿಪತಿಯಾಗಿ ನೇಮಿಸಲ್ಪಟ್ಟವನೂ ಆದ ಗೆದಲ್ಯನನ್ನು ಕತ್ತಿಯಿಂದ ಹೊಡೆದು ಕೊಂದರು.",
			"3": "<sup>3</sup> ಇದಲ್ಲದೆ ಇಷ್ಮಾಯೇಲನು ಗೆದಲ್ಯನ ಸಂಗಡ ಮಿಚ್ಪದಲ್ಲಿದ್ದ ಯೆಹೂದ್ಯರೆಲ್ಲರನ್ನೂ ಅಲ್ಲಿ ಸಿಕ್ಕಿದ ಕಸ್ದೀಯರಾದ ಸೈನಿಕರನ್ನೂ ಹತಿಸಿದನು.",
			"4": "<sup>4</sup> ಅವನು ಗೆದಲ್ಯನನ್ನು ಕೊಂದ ಎರಡನೆಯ ದಿನದಲ್ಲಿ ಆ ಸಂಗತಿಯು ಇನ್ನೂ ಯಾರಿಗೂ ತಿಳಿಯದಿರಲು",
			"5": "<sup>5</sup> ಗಡ್ಡ ಬೋಳಿಸಿ ಬಟ್ಟೆಹರಿದು ಗಾಯಮಾಡಿಕೊಂಡಿದ್ದ ಎಂಭತ್ತು ಜನರು ಶೆಕೆಮ್, ಶಿಲೋ, ಸಮಾರ್ಯ ಎಂಬ ಊರುಗಳಿಂದ ಒಂದು ಕೈಯಲ್ಲಿ ಧೂಪನೈವೇದ್ಯದ್ರವ್ಯಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಯೆಹೋವನ ಆಲಯಕ್ಕೆ ಹೋಗುತ್ತಿದ್ದರು.",
			"6": "<sup>6</sup> ಆಗ ನೆತನ್ಯನ ಮಗನಾದ ಇಷ್ಮಾಯೇಲನು ಮಿಚ್ಪದಿಂದ ಅವರ ಕಡೆಗೆ ಹೊರಟು ದಾರಿಯುದ್ದಕ್ಕೂ ಅಳುತ್ತಾ ನಡೆದು ಅವರನ್ನು ಎದುರುಗೊಂಡು ಅಹೀಕಾಮನ ಮಗನಾದ ಗೆದಲ್ಯನ ಬಳಿಗೆ ಬನ್ನಿರಿ ಎಂದು ಹೇಳಿದನು.",
			"7": "<sup>7</sup> ಅವರು ಊರೊಳಕ್ಕೆ ಬಂದ ಮೇಲೆ ನೆತನ್ಯನ ಮಗನಾದ ಇಷ್ಮಾಯೇಲನೂ ಅವನ ಸಂಗಡಿಗರೂ ಅವರನ್ನು ಹತಿಸಿ ಬಾವಿಯಲ್ಲಿ ಹಾಕಿದರು.",
			"8": "<sup>8</sup> ಆದರೆ ಆ ಎಂಭತ್ತು ಮಂದಿಯಲ್ಲಿ ಹತ್ತು ಜನರು ಇಷ್ಮಾಯೇಲನಿಗೆ - ನಮ್ಮನ್ನು ಕೊಲ್ಲಬೇಡ; ಗೋದಿ, ಜವೆಗೋದಿ, ಎಣ್ಣೆ, ಜೇನುತುಪ್ಪ, ಇವುಗಳನ್ನು ಕಾಡಿನಲ್ಲಿ ಬಹಳವಾಗಿ ಬಚ್ಚಿಟ್ಟಿದ್ದೇವೆ ಎಂದು ಬಿನ್ನೈಸಲು ಅವನು ಅವರನ್ನು ಅವರ ಜೊತೆಯವರಂತೆ ಕೊಲ್ಲದೆ ಬಿಟ್ಟನು.",
			"9": "<sup>9</sup> ಇಷ್ಮಾಯೇಲನು ತಾನು ಕೊಂದವರ ಶವಗಳನ್ನು ಗೆದಲ್ಯನ ಶವದ ಮೇಲೆ ಹಾಕಿದ ಬಾವಿಯು, ಇಸ್ರಾಯೇಲಿನ ಅರಸನಾದ ಬಾಷನ ಭಯದಿಂದ ಅರಸನಾದ ಆಸನು ತೋಡಿಸಿದ್ದ ಬಾವಿಯೇ. ಆ ಬಾವಿಯನ್ನು ನೆತನ್ಯನ ಮಗನಾದ ಇಷ್ಮಾಯೇಲನು ಹತರಾದವರ ಶವಗಳಿಂದ ತುಂಬಿಸಿಬಿಟ್ಟನು.",
			"10": "<sup>10</sup> ಆಮೇಲೆ ನೆತನ್ಯನ ಮಗನಾದ ಇಷ್ಮಾಯೇಲನು ರಾಜಕುಮಾರ್ತೆಯರನ್ನು, ಕಾವಲು ದಂಡಿನ ಅಧಿಪತಿಯಾದ ನೆಬೂಜರದಾನನು ಅಹೀಕಾಮನ ಮಗನಾದ ಗೆದಲ್ಯನಿಗೆ ಒಪ್ಪಿಸಿದ ಮಿಚ್ಪದ ಜನಶೇಷವನ್ನು, ಅಂತು ಮಿಚ್ಪದಲ್ಲಿ ಉಳಿದವರೆಲ್ಲರನ್ನೂ ಸೆರೆಹಿಡಿದು ಅಮ್ಮೋನ್ಯರ ಸೀಮೆಗೆ ಹೊರಟನು.",
			"11": "<sup>11</sup> ಕಾರೇಹನ ಮಗನಾದ ಯೋಹಾನಾನನೂ ಅವನೊಂದಿಗಿದ್ದ ಸಕಲ ಸೇನಾಧಿಪತಿಗಳೂ ನೆತನ್ಯನ ಮಗನಾದ ಇಷ್ಮಾಯೇಲನು ಮಾಡಿದ ಎಲ್ಲಾ ಕೆಡುಕನ್ನು ಕೇಳಿ",
			"12": "<sup>12</sup> ತಮ್ಮ ಸೈನಿಕರನ್ನೆಲ್ಲಾ ಕರಕೊಂಡು ನೆತನ್ಯನ ಮಗನಾದ ಇಷ್ಮಾಯೇಲನ ಮೇಲೆ ಯುದ್ಧಕ್ಕೆ ಹೊರಟು ಗಿಬ್ಯೋನಿನ ದೊಡ್ಡ ಕೆರೆಯ ಹತ್ತಿರ ಅವನನ್ನು ಕಂಡರು.",
			"13": "<sup>13</sup> ಆಗ ಇಷ್ಮಾಯೇಲನ ಸಂಗಡ ಇದ್ದ ಎಲ್ಲಾ ಜನರು ಕಾರೇಹನ ಮಗನಾದ ಯೋಹಾನಾನನನ್ನೂ ಅವನೊಂದಿಗಿದ್ದ ಸಮಸ್ತ ಸೇನಾಪತಿಗಳನ್ನೂ ನೋಡಿ ಸಂತೋಷಪಟ್ಟರು.",
			"14": "<sup>14</sup> ಇಷ್ಮಾಯೇಲನು ಮಿಚ್ಪದಿಂದ ಸೆರೆಯಾಗಿ ಒಯ್ದಿದ್ದ ಸಕಲಜನರು ಹಿಂದಿರುಗಿ ಹೋಗಿ ಕಾರೇಹನ ಮಗನಾದ ಯೋಹಾನಾನನ ಬಳಿಗೆ ಸೇರಿದರು.",
			"15": "<sup>15</sup> ನೆತನ್ಯನ ಮಗನಾದ ಇಷ್ಮಾಯೇಲನೋ ಎಂಟು ಮಂದಿಯ ಸಂಗಡ ಯೋಹಾನಾನನಿಂದ ತಪ್ಪಿಸಿಕೊಂಡು ಅಮ್ಮೋನ್ಯರನ್ನು ಮರೆಹೊಕ್ಕನು.",
			"16": "<sup>16</sup> ಆಗ ಕಾರೇಹನ ಮಗನಾದ ಯೋಹಾನಾನನೂ ಅವನೊಂದಿಗಿದ್ದ ಸಮಸ್ತ ಸೇನಾಪತಿಗಳೂ ತಾವು ನೆತನ್ಯನ ಮಗನಾದ ಇಷ್ಮಾಯೇಲನ ಕೈಯಿಂದ ತಪ್ಪಿಸಿ ಗಿಬ್ಯೋನಿನಿಂದ ತಂದಿದ್ದ ಭಟರು, ಹೆಂಗಸರು, ಮಕ್ಕಳು, ಕಂಚುಕಿಗಳು, ಅಂತು ಇಷ್ಮಾಯೇಲನು ಅಹೀಕಾಮನ ಮಗನಾದ ಗೆದಲ್ಯನನ್ನು ಕೊಂದ ಮೇಲೆ ಮಿಚ್ಪದಿಂದ ಒಯ್ದ ಜನಶೇಷವನ್ನೆಲ್ಲಾ ಕರೆದುಕೊಂಡು ಹೋಗಿ",
			"17": "<sup>17</sup> ಕಸ್ದೀಯರ ಭಯದಿಂದ ಐಗುಪ್ತಕ್ಕೆ ವಲಸೆಹೋಗಬೇಕೆಂದು ಬೇತ್ಲೆಹೇಮಿನ ಸಮೀಪವಾಗಿರುವ ಗೇರುಥ್ ಕಿಮ್ಹಾಮಿನಲ್ಲಿ ಇಳಿದುಕೊಂಡರು.",
			"18": "<sup>18</sup> ಬಾಬೆಲಿನ ಅರಸನಿಂದ ದೇಶಕ್ಕೆ ಅಧಿಪತಿಯಾಗಿ ನೇಮಿಸಲ್ಪಟ್ಟವನೂ ಅಹೀಕಾಮನ ಮಗನೂ ಆದ ಗೆದಲ್ಯನನ್ನು ನೆತನ್ಯನ ಮಗನಾದ ಇಷ್ಮಾಯೇಲನು ಕೊಂದುಹಾಕಿದ್ದರಿಂದ ಕಸ್ದೀಯರಿಗೆ ಅಂಜಿದ್ದರು."
		},
		"42": {
			"1": "<sup>1</sup> ಆಗ ಸಕಲ ಸೇನಾಪತಿಗಳೂ ಕಾರೇಹನ ಮಗನಾದ ಯೋಹಾನಾನನೂ ಹೋಷಾಯನ ಮಗನಾದ ಯೆಜನ್ಯನೂ ಚಿಕ್ಕವರು ಮೊದಲುಗೊಂಡು ದೊಡ್ಡವರ ತನಕ",
			"2": "<sup>2</sup> ಎಲ್ಲಾ ಜನರೂ ಪ್ರವಾದಿಯಾದ ಯೆರೆಮೀಯನ ಬಳಿಗೆ ಬಂದು ಅವನಿಗೆ - ದಯಮಾಡು, ನಮ್ಮ ಬಿನ್ನಹವನ್ನು ಲಾಲಿಸು;",
			"3": "<sup>3</sup> ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನು ನಾವು ನಡೆಯಬೇಕಾದ ಮಾರ್ಗವನ್ನೂ ಮಾಡತಕ್ಕ ಕಾರ್ಯವನ್ನೂ ತೋರಿಸುವ ಹಾಗೆ ನಮಗಾಗಿ, ಅಂದರೆ ಉಳಿದಿರುವ ಈ ಜನರೆಲ್ಲರಿಗಾಗಿ ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಪ್ರಾರ್ಥಿಸು; ನಿನ್ನ ಕಣ್ಣಿಗೆ ಕಾಣುವ ಪ್ರಕಾರ ಅಪಾರ ಜನರಲ್ಲಿ ನಾವು ಕೆಲವರು ಮಾತ್ರ ಉಳಿದಿದ್ದೇವೆ ಎಂದು ವಿಜ್ಞಾಪಿಸಿದರು.",
			"4": "<sup>4</sup> ಇದನ್ನು ಕೇಳಿ ಪ್ರವಾದಿಯಾದ ಯೆರೆಮೀಯನು ಅವರಿಗೆ - ನಿಮ್ಮ ಬಿನ್ನಹವನ್ನು ಕೇಳಿದೆನು; ಇಗೋ, ನಿಮ್ಮ ಮಾತಿನಂತೆ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಪ್ರಾರ್ಥಿಸುವೆನು; ಯೆಹೋವನು ಯಾವ ಉತ್ತರವನ್ನು ದಯಪಾಲಿಸುವನೋ ಒಂದನ್ನೂ ಮುಚ್ಚದೆ ನಿಮಗೆ ತಿಳಿಸುವೆನು ಎಂದು ಹೇಳಲು",
			"5": "<sup>5</sup> ಅವರು ಯೆರೆಮೀಯನಿಗೆ - ನಿನ್ನ ದೇವರಾದ ಯೆಹೋವನು ನಿನ್ನ ಮೂಲಕ ನಮಗೆ ಕಳುಹಿಸುವ ಮಾತಿನಂತೆ ನಾವು ನಡೆಯದಿದ್ದರೆ ಯೆಹೋವನು ಸ್ಥಿರವಾದ ಸತ್ಯಸಾಕ್ಷಿಯಾಗಿ ನಿಂತು ನಮ್ಮನ್ನು ಖಂಡಿಸಲಿ!",
			"6": "<sup>6</sup> ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಮಾತು ಹಿತವಾಗಲಿ ಅಹಿತವಾಗಲಿ ಆತನ ಸನ್ನಿಧಿಯಲ್ಲಿ ವಿಚಾರಿಸುವದಕ್ಕೆ ನಿನ್ನನ್ನು ಕಳುಹಿಸುವ ನಾವು ಅದನ್ನು ಕೈಕೊಳ್ಳುವೆವು; ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಮಾತನ್ನು ನಾವು ಕೇಳಿದರೆ ನಮಗೆ ಶುಭವಾಗುವದಷ್ಟೆ ಎಂಬದಾಗಿ ಹೇಳಿದರು.",
			"7": "<sup>7</sup> ಹತ್ತು ದಿನಗಳಾದ ಮೇಲೆ ಯೆಹೋವನು ತನ್ನ ವಾಕ್ಯವನ್ನು ಯೆರೆಮೀಯನಿಗೆ ದಯಪಾಲಿಸಿದನು.",
			"8": "<sup>8</sup> ಆಗ ಅವನು ಕಾರೇಹನ ಮಗನಾದ ಯೋಹಾನಾನನನ್ನೂ ಅವನೊಂದಿಗಿದ್ದ ಸಕಲ ಸೇನಾಪತಿಗಳನ್ನೂ ಚಿಕ್ಕವರು ಮೊದಲುಗೊಂಡು ದೊಡ್ಡವರ ತನಕ ಎಲ್ಲಾ ಜನರನ್ನೂ ಕರೆದು -",
			"9": "<sup>9</sup> ನಿಮ್ಮ ವಿಜ್ಞಾಪನೆಯನ್ನು ಯೆಹೋವನಿಗೆ ಅರಿಕೆ ಮಾಡುವದಕ್ಕಾಗಿ ನನ್ನನ್ನು ಕಳುಹಿಸಿದ್ದಿರಲ್ಲಾ; ಅದಕ್ಕೆ ಇಸ್ರಾಯೇಲಿನ ದೇವರಾದ ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ -",
			"10": "<sup>10</sup> ನೀವು ಈ ದೇಶದಲ್ಲಿ ನೆಲೆಗೊಂಡಿದ್ದರೆ ನಾನು ನಿಮ್ಮನ್ನು ಕಟ್ಟುವೆನು, ಕೆಡವುವದಿಲ್ಲ; ನೆಡುವೆನು, ಕೀಳುವದಿಲ್ಲ; ನಾನು ನಿಮಗೆ ಮಾಡಿದ ಕೇಡಿಗೆ ಪಶ್ಚಾತ್ತಾಪಪಡುತ್ತೇನೆ.",
			"11": "<sup>11</sup> ನೀವು ಹೆದರಿರುವ ಬಾಬೆಲಿನ ಅರಸನಿಗೆ ಹೆದರಬೇಡಿರಿ; ಅವನಿಗೆ ಅಂಜದಿರಿ; ನಾನು ನಿಮ್ಮನ್ನು ಅವನ ಕೈಯಿಂದ ತಪ್ಪಿಸಿ ರಕ್ಷಿಸಲಿಕ್ಕೆ ನಿಮ್ಮೊಂದಿಗಿದ್ದೇನಲ್ಲಾ. ಇದು ಯೆಹೋವನಾದ ನನ್ನ ನುಡಿ.",
			"12": "<sup>12</sup> ಅವನು ನಿಮ್ಮನ್ನು ಕರುಣಿಸಿ ಸ್ವದೇಶಕ್ಕೆ ಹೋಗಗೊಡಿಸುವಂತೆ ನಾನು ನಿಮಗೆ ಕರುಣೆಯನ್ನು ಅನುಗ್ರಹಿಸುವೆನು.",
			"13": "<sup>13</sup> ಆದರೆ ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ನುಡಿಗೆ ಕಿವಿಗೊಡದೆ - ಈ ದೇಶದಲ್ಲಿ ನಾವು ನೆಲೆಸುವದೇ ಇಲ್ಲ; ಬೇಡವೇ ಬೇಡ;",
			"14": "<sup>14</sup> ನಾವು ಯುದ್ಧಕಾಣದೆ ತುತೂರಿಯ ಶಬ್ದಕೇಳದೆ ಅನ್ನದ ಕೊರತೆಯಿಂದ ಹಸಿವೆಗೊಳ್ಳದೆ ಇರುವ ಐಗುಪ್ತದೇಶಕ್ಕೆ ಹೋಗಿ ಅಲ್ಲೇ ವಾಸಿಸುವೆವು ಎಂದುಕೊಂಡರೆ",
			"15": "<sup>15</sup> ಯೆಹೂದ್ಯರಲ್ಲಿ ಉಳಿದವರೇ, ಯೆಹೋವನ ವಾಕ್ಯವನ್ನು ಈಗ ಕೇಳಿರಿ; ಇಸ್ರಾಯೇಲಿನ ದೇವರೂ ಸೇನಾಧೀಶ್ವರನೂ ಆದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ನೀವು ಐಗುಪ್ತಕ್ಕೆ ಹೋಗಬೇಕೆಂದು ಹಟಹಿಡಿದು ಅಲ್ಲಿಗೆ ಸೇರಿ ವಾಸಮಾಡುವದಾದರೆ",
			"16": "<sup>16</sup> ಆಗ ನೀವು ಹೆದರುತ್ತಿರುವ ಖಡ್ಗವು ಐಗುಪ್ತದಲ್ಲಿಯೂ ನಿಮ್ಮನ್ನು ಹಿಂದಟ್ಟಿ ಹಿಡಿಯುವದು, ನೀವು ಅಂಜುತ್ತಿರುವ ಕ್ಷಾಮವು ಅಲ್ಲಿಯೂ ನಿಮ್ಮ ಬೆನ್ನು ಹತ್ತುವದು; ಅಲ್ಲೇ ಸಾಯುವಿರಿ.",
			"17": "<sup>17</sup> ಐಗುಪ್ತಕ್ಕೆ ಹೋಗಿ ಅಲ್ಲಿ ವಾಸಿಸಲು ಹಟಹಿಡಿಯುವ ಎಲ್ಲಾ ಜನರಿಗೂ ಇದೇ ಗತಿಯಾಗುವದು; ಖಡ್ಗಕ್ಷಾಮವ್ಯಾಧಿಗಳಿಂದ ಸಾಯುವರು; ನಾನು ಅವರಿಗೆ ಬರಮಾಡುವ ವಿಪತ್ತಿನಿಂದ ಯಾರೂ ತಪ್ಪಿಸಿಕೊಳ್ಳರು, ಯಾರೂ ಉಳಿಯರು.",
			"18": "<sup>18</sup> ಇಸ್ರಾಯೇಲಿನ ದೇವರೂ ಸೇನಾಧೀಶ್ವರನೂ ಆದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ನನ್ನ ಉಗ್ರರೋಷಾಗ್ನಿಯು ಯೆರೂಸಲೇಮಿನವರ ಮೇಲೆ ಹೇಗೆ ಸುರಿಯಿತೋ ಹಾಗೆಯೇ ನೀವು ಐಗುಪ್ತದಲ್ಲಿ ಕಾಲಿಟ್ಟ ಕೂಡಲೆ ನನ್ನ ರೋಷಾಗ್ನಿಯು ನಿಮ್ಮ ಮೇಲೆಯೂ ಸುರಿಯುವದು; ನೀವು ಅಪವಾದ ವಿಸ್ಮಯ ಶಾಪ ದೂಷಣೆಗಳಿಗೆ ಗುರಿಯಾಗುವಿರಿ; ಈ ಸ್ಥಳವು ಮತ್ತೆ ನಿಮ್ಮ ಕಣ್ಣಿಗೆ ಬೀಳದು.",
			"19": "<sup>19</sup> ಯೆಹೂದ್ಯರಲ್ಲಿ ಉಳಿದವರೇ, ಯೆಹೋವನು ನಿಮ್ಮನ್ನು ಕುರಿತು - ಐಗುಪ್ತಕ್ಕೆ ಹೋಗಬೇಡಿರಿ ಎಂದು ಅಪ್ಪಣೆಮಾಡಿದ್ದಾನೆ; ನಾನು ಈ ದಿವಸ [ಆ ಮಾತನ್ನು] ನಿಮಗೆ ಪ್ರಕಟಿಸಿದ್ದೇನೆ, ಚೆನ್ನಾಗಿ ತಿಳಿದಿರಲಿ.",
			"20": "<sup>20</sup> ನಿಮ್ಮನ್ನು ನೀವೇ ಮೋಸಪಡಿಸಿಕೊಂಡಿದ್ದೀರಿ; ನೀವು ನನ್ನ ಬಳಿಗೆ ಬಂದು - ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ನಮಗಾಗಿ ಪ್ರಾರ್ಥಿಸು; ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ಯಾವ ಅಪ್ಪಣೆಕೊಡುತ್ತಾನೋ ಅದನ್ನು ನಮಗೆ ತಿಳಿಸು, ಅದರಂತೆಯೇ ನಡೆಯುವೆವು ಎಂಬದಾಗಿ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನ ಸನ್ನಿಧಿಯಲ್ಲಿ ವಿಚಾರಿಸುವದಕ್ಕೆ ನನ್ನನ್ನು ಕಳುಹಿಸಿದಿರಲ್ಲಾ.",
			"21": "<sup>21</sup> ನಾನು ಯೆಹೋವನ ಅಪ್ಪಣೆಯನ್ನು ಈ ದಿವಸ ನಿಮಗೆ ತಿಳಿಸಿದ್ದೇನೆ; ನೀವಾದರೆ ನಿಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನನ್ನ ಮೂಲಕ ನಿಮಗೆ ಹೇಳಿಕಳುಹಿಸಿದ ಯಾವ ಮಾತನ್ನೂ ಕೇಳಲಿಲ್ಲ.",
			"22": "<sup>22</sup> ನೀವು ಯಾವ ಸ್ಥಳದಲ್ಲಿ ವಾಸಮಾಡಲಪೇಕ್ಷಿಸಿ ಹೋಗುತ್ತೀರೋ ಆ ಸ್ಥಳದಲ್ಲೇ ಖಡ್ಗಕ್ಷಾಮವ್ಯಾಧಿಗಳಿಂದ ಸಾಯುವಿರಿ ಎಂದು ಖಂಡಿತ ತಿಳಿಯಿರಿ."
		},
		"43": {
			"1": "<sup>1</sup> ಯೆರೆಮೀಯನು ಸಮಸ್ತ ಜನರನ್ನು ಸಂಬೋಧಿಸಿ ಅವರ ದೇವರಾದ ಯೆಹೋವನು ತನ್ನ ಮೂಲಕ ಅವರಿಗೆ ಹೇಳಿ ಕಳುಹಿಸಿದ ಈ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ತಿಳಿಸಿಬಿಟ್ಟ ಮೇಲೆ",
			"2": "<sup>2</sup> ಹೋಷಾಯನ ಮಗನಾದ ಅಜರ್ಯನೂ ಕಾರೇಹನ ಮಗನಾದ ಯೋಹಾನಾನನೂ ಸೊಕ್ಕೇರಿದವರೆಲ್ಲರೂ ಅವನಿಗೆ - ನಿನ್ನ ಮಾತು ಸುಳ್ಳು; ಐಗುಪ್ತಕ್ಕೆ ಹೋಗಿ ವಾಸಮಾಡಬಾರದು ಎಂದು ತಿಳಿಸುವಂತೆ ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಿನ್ನನ್ನು ಕಳುಹಿಸಲಿಲ್ಲ;",
			"3": "<sup>3</sup> ಕಸ್ದೀಯರು ನಮ್ಮನ್ನು ಕೊಲ್ಲುವದಕ್ಕೋ ಬಾಬೆಲಿಗೆ ಸೆರೆ ಒಯ್ಯುವದಕ್ಕೋ ನಮ್ಮನ್ನು ಅವರ ಕೈಗೆ ಸಿಕ್ಕಿಸಬೇಕೆಂದು ನೇರೀಯನ ಮಗನಾದ ಬಾರೂಕನೇ ನಿನ್ನನ್ನು ನಮ್ಮ ಮೇಲೆ ನೂಕಿದ್ದಾನೆ ಎಂದು ಹೇಳಿದರು.",
			"4": "<sup>4</sup> ಹೀಗೆ ಯೆಹೂದ ದೇಶದಲ್ಲಿಯೇ ವಾಸಮಾಡಿರಿ ಎನ್ನುವ ಯೆಹೋವನ ಮಾತನ್ನು ಕಾರೇಹನ ಮಗನಾದ ಯೋಹಾನಾನನೂ ಸಮಸ್ತ ಸೇನಾಪತಿಗಳೂ ಸಕಲಜನರೂ ಕೇಳದೆ ಹೋದರು.",
			"5": "<sup>5</sup> ಆಗ ಕಾವಲು ದಂಡಿನ ಅಧಿಪತಿಯಾದ ನೆಬೂಜರದಾನನು ಅಹೀಕಾಮನ ಮಗನೂ ಶಾಫಾನನ ಮೊಮ್ಮಗನೂ ಆದ ಗೆದಲ್ಯನ ವಶಕ್ಕೆ ಒಪ್ಪಿಸಿದ ಜನರು, ಅನ್ಯದೇಶಗಳಿಗೆ ಅಟ್ಟಲ್ಪಟ್ಟು ಯೆಹೂದದಲ್ಲಿ ವಾಸಿಸುವದಕ್ಕೆ ಹಿಂದಿರುಗಿ ಬಂದಿದ್ದ ಯೆಹೂದ ಜನಶೇಷದವರು, ಪ್ರವಾದಿಯಾದ ಯೆರೆಮೀಯನು,",
			"6": "<sup>6</sup> ನೇರೀಯನ ಮಗನಾದ ಬಾರೂಕನು, ರಾಜಕುಮಾರ್ತೆಯರು, ಅಂತು ಗಂಡಸರು, ಹೆಂಗಸರು, ಮಕ್ಕಳು,",
			"7": "<sup>7</sup> ಇವರೆಲ್ಲರನ್ನೂ ಕಾರೇಹನ ಮಗನಾದ ಯೋಹಾನಾನನೂ ಸಕಲ ಸೇನಾಪತಿಗಳೂ ಕರೆದುಕೊಂಡು ಐಗುಪ್ತಕ್ಕೆ ಹೋಗಿ ತಹಪನೇಸ್ ಊರಿಗೆ ಸೇರಿದರು. ಯೆಹೋವನ ಮಾತನ್ನು ಕೇಳಲೇ ಇಲ್ಲ.",
			"8": "<sup>8</sup> ಯೆರೆಮೀಯನು ತಹಪನೇಸಿನಲ್ಲಿರುವಾಗ ಯೆಹೋವನು ಈ ವಾಕ್ಯವನ್ನು ಅವನಿಗೆ ದಯಪಾಲಿಸಿದನು -",
			"9": "<sup>9</sup> ನೀನು ನಿನ್ನ ಕೈಯಲ್ಲಿ ದೊಡ್ಡ ಕಲ್ಲುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ತಹಪನೇಸಿನಲ್ಲಿರುವ ಫರೋಹನ ಮನೆಯ ಬಾಗಿಲ ಮುಂದೆ ಯೆಹೂದ್ಯರ ಕಣ್ಣೆದುರಿಗೆ ನೆಲಗಟ್ಟಿನ ಕೆಳಗೆ ಇಟ್ಟು ಗಾರೆಯಿಂದ ಮುಚ್ಚಿಬಿಟ್ಟು ಅವರಿಗೆ ಹೀಗೆ ಹೇಳು -",
			"10": "<sup>10</sup> ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರೂ ಸೇನಾಧೀಶ್ವರನೂ ಆದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಇಗೋ, ನಾನು ಬಾಬೆಲಿನ ಅರಸನೂ ನನ್ನ ಸೇವಕನೂ ಆದ ನೆಬೂಕದ್ನೆಚ್ಚರನನ್ನು ಕರೆಯಿಸಿ ನಾನು ಮರೆಮಾಡಿಸಿರುವ ಈ ಕಲ್ಲುಗಳ ಮೇಲೆ ಅವನ ಸಿಂಹಾಸನವನ್ನು ಹಾಕಿಸುವೆನು; ಅವನು ಈ ಕಲ್ಲುಗಳ ಮೇಲೆಯೇ ತನ್ನ ರತ್ನಗಂಬಳಿಯನ್ನು ಹಾಸಿಸುವನು.",
			"11": "<sup>11</sup> ಅವನು ಬಂದು ಐಗುಪ್ತದೇಶವನ್ನು ಹೊಡೆಯುವನು; ಮರಣಕ್ಕೆ ಗೊತ್ತಾದವರು ಮರಣಕ್ಕೆ, ಸೆರೆಗೆ ಗೊತ್ತಾದವರು ಸೆರೆಗೆ, ಖಡ್ಗಕ್ಕೆ ಗೊತ್ತಾದವರು ಖಡ್ಗಕ್ಕೆ ಗುರಿಯಾಗುವರು.",
			"12": "<sup>12</sup> ನಾನು ಐಗುಪ್ತದ ದೇವಾಲಯಗಳಲ್ಲಿ ಬೆಂಕಿಹೊತ್ತಿಸುವೆನು, ಅವನು ಅವುಗಳನ್ನು ಸುಟ್ಟು ದೇವತೆಗಳನ್ನು ಸೆರೆ ಒಯ್ಯುವನು; ಕುರುಬನು ತನ್ನ ಕಂಬಳಿಯನ್ನು ಸುತ್ತಿಕೊಳ್ಳುವಂತೆ ಅವನು ಐಗುಪ್ತದೇಶವನ್ನು ಸುತ್ತಿಕೊಳ್ಳುವನು; ಸಮಾಧಾನವಾಗಿ ಅಲ್ಲಿಂದ ಹೊರಟು ಹೋಗುವನು.",
			"13": "<sup>13</sup> ಐಗುಪ್ತದೇಶದಲ್ಲಿರುವ ಸೂರ್ಯಪುರಿಯ ಸ್ತಂಭಗಳನ್ನು ಒಡೆದುಹಾಕಿ ಐಗುಪ್ತದ ದೇವಾಲಯಗಳನ್ನು ಬೆಂಕಿಯಿಂದ ಸುಟ್ಟುಬಿಡುವನು."
		},
		"44": {
			"1": "<sup>1</sup> ಐಗುಪ್ತದೇಶದ ಮಿಗ್ದೋಲ್, ತಹಪನೇಸ್, ನೋಫ್ ಎಂಬ ಪಟ್ಟಣಗಳಲ್ಲಿಯೂ ಪತ್ರೋಸ್ ಪ್ರಾಂತದಲ್ಲಿಯೂ ವಾಸಮಾಡುತ್ತಿದ್ದ ಯೆಹೂದ್ಯರೆಲ್ಲರ ವಿಷಯವಾಗಿ ಯೆಹೋವನು ಯೆರೆಮೀಯನಿಗೆ ದಯಪಾಲಿಸಿದ ವಾಕ್ಯ -",
			"2": "<sup>2</sup> ಇಸ್ರಾಯೇಲಿನ ದೇವರೂ ಸೇನಾಧೀಶ್ವರನೂ ಆದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ನಾನು ಯೆರೂಸಲೇಮಿಗೂ ಯೆಹೂದದ ಎಲ್ಲಾ ಊರುಗಳಿಗೂ ಬರಮಾಡಿದ ಕೇಡನ್ನು ನೀವು ನೋಡಿದ್ದೀರಷ್ಟೆ;",
			"3": "<sup>3</sup> ಆಹಾ, ಅಲ್ಲಿನವರು ತಮಗೂ ನಿಮಗೂ ನಿಮ್ಮ ಪಿತೃಗಳಿಗೂ ತಿಳಿಯದ ಅನ್ಯದೇವತೆಗಳಿಗೆ ಧೂಪಹಾಕಿ ಸೇವೆಮಾಡುವದಕ್ಕೆ ಆತುರಪಟ್ಟು ನನ್ನನ್ನು ಕೆಣಕಬೇಕೆಂದೇ ಈ ದುರಾಚಾರವನ್ನು ನಡಿಸಿದ್ದರಿಂದ ಆ ಸ್ಥಳಗಳು ಹಾಳಾದವು. ಅಲ್ಲಿ ಯಾರೂ ವಾಸಿಸರು.",
			"4": "<sup>4</sup> ನಾನು ನನ್ನ ಸೇವಕರಾದ ಪ್ರವಾದಿಗಳನ್ನೆಲ್ಲಾ ಅವರ ಬಳಿಗೆ ಸಾವಕಾಶಮಾಡದೆ ಕಳುಹಿಸುತ್ತಾ - ಅಹಹ, ನಾನು ಹೇಸುವ ಈ ಅಸಹ್ಯಕಾರ್ಯವನ್ನು ಮಾಡಬೇಡಿರಿ ಎಂದು ಪ್ರಕಟಿಸುತ್ತಾ ಬಂದರೂ ಅವರು ಕಿವಿಗೊಟ್ಟು ಕೇಳಲಿಲ್ಲ,",
			"5": "<sup>5</sup> ಅನ್ಯದೇವತೆಗಳಿಗೆ ಧೂಪಹಾಕುವದನ್ನು ನಿಲ್ಲಿಸಲಿಲ್ಲ, ತಮ್ಮ ದುರಾಚಾರವನ್ನು ಬಿಡಲಿಲ್ಲ.",
			"6": "<sup>6</sup> ಆದಕಾರಣ ನನ್ನ ಉಗ್ರರೋಷಾಗ್ನಿಯು ಸುರಿದು ಯೆಹೂದದ ಊರುಗಳಲ್ಲಿಯೂ ಯೆರೂಸಲೇಮಿನ ಬೀದಿಗಳಲ್ಲಿಯೂ ಧಗಧಗಿಸಿತು; ಅವು ಈಗಲೂ ಹಾಳುಹಾಳಾಗಿವೆ.",
			"7": "<sup>7</sup> ಹೀಗಿರಲು ಸೇನಾಧೀಶ್ವರಸ್ವಾಮಿಯೂ ಇಸ್ರಾಯೇಲಿನ ದೇವರೂ ಆದ ಯೆಹೋವನು ಈಗ ಇಂತೆನ್ನುತ್ತಾನೆ - ಈ ದೊಡ್ಡ ಕೇಡನ್ನು ನಿಮಗೆ ನೀವೇ ಉಂಟುಮಾಡಿಕೊಳ್ಳುವದೇಕೆ? ಈ ದುರಾಚಾರದ ನಿಮಿತ್ತ ಯೆಹೂದದಲ್ಲಿರುವ ಗಂಡಸರು, ಹೆಂಗಸರು, ಮಕ್ಕಳು, ಮೊಲೆಕೂಸುಗಳು ನಿರ್ಮೂಲವಾಗುವರು, ನಿಮ್ಮಲ್ಲಿ ಯಾರೂ ಉಳಿಯರು.",
			"8": "<sup>8</sup> ಪ್ರವಾಸಿಗಳಾಗಿ ನೀವು ಬಂದಿರುವ ಐಗುಪ್ತದಲ್ಲಿ ಅನ್ಯದೇವತೆಗಳಿಗೆ ಧೂಪಹಾಕುವ ದುಷ್ಕೃತ್ಯವನ್ನು ನಡಿಸುತ್ತಾ ನನ್ನನ್ನು ಕೆಣಕುವದರಿಂದ ನೀವು ನಿರ್ಮೂಲರಾಗಿ ಸಕಲ ಭೂರಾಜ್ಯಗಳ ಶಾಪದೂಷಣೆಗಳಿಗೆ ಗುರಿಯಾಗುವಿರಿ.",
			"9": "<sup>9</sup> ನಿಮ್ಮ ಪಿತೃಗಳು, ಯೆಹೂದದ ಅರಸರು, ಅವರ ಹೆಂಡಿರು, ನೀವು, ನಿಮ್ಮ ಹೆಂಡಿರು, ಇವರೆಲ್ಲರೂ ಯೆಹೂದದೇಶದಲ್ಲಿಯೂ ಯೆರೂಸಲೇಮಿನ ಬೀದಿಗಳಲ್ಲಿಯೂ ನಡಿಸಿದ ದುರಾಚಾರಗಳನ್ನು ಮರೆತುಬಿಟ್ಟಿರೋ?",
			"10": "<sup>10</sup> ಅವರು ಇಂದಿನವರೆಗೂ ತಗ್ಗಲಿಲ್ಲ, ನನಗೆ ಭಯಪಡಲಿಲ್ಲ; ನಾನು ನಿಮಗೂ ನಿಮ್ಮ ಪಿತೃಗಳಿಗೂ ನೇಮಿಸಿದ ನಿಯಮನಿಷ್ಠೆಗಳನ್ನು ಅನುಸರಿಸಿ ನಡೆಯಲೂ ಇಲ್ಲ.",
			"11": "<sup>11</sup> ಆದಕಾರಣ ಇಸ್ರಾಯೇಲಿನ ದೇವರೂ ಸೇನಾಧೀಶ್ವರನೂ ಆದ ಯೆಹೋವನು ಹೀಗನ್ನುತ್ತಾನೆ - ಆಹಾ, ಯೆಹೂದವನ್ನೆಲ್ಲಾ ನಿರ್ಮೂಲಮಾಡುವೆನು, ಕೇಡಿಗಾಗಿಯೇ ನಿಮ್ಮ ಮೇಲೆ ದೃಷ್ಟಿಯಿಡುವೆನು.",
			"12": "<sup>12</sup> ಐಗುಪ್ತಕ್ಕೆ ಹೋಗಿ ಅಲ್ಲಿ ಪ್ರವಾಸಿಸಲು ಹಟಹಿಡಿದ ಯೆಹೂದಜನಶೇಷವನ್ನು ನಾನು ಹಿಡಿದು ನಾಶಮಾಡುವೆನು; ಐಗುಪ್ತದಲ್ಲೇ ಒರಗಿ ಹೋಗುವರು; ಖಡ್ಗಕ್ಷಾಮಗಳಿಂದ ನಾಶವಾಗುವರು; ಚಿಕ್ಕವರು ಮೊದಲುಗೊಂಡು ದೊಡ್ಡವರ ತನಕ ಖಡ್ಗಕ್ಷಾಮಗಳಿಂದ ಸತ್ತು ಅಪವಾದ ವಿಸ್ಮಯ ಶಾಪ ದೂಷಣೆಗಳಿಗೆ ಗುರಿಯಾಗುವರು.",
			"13": "<sup>13</sup> ನಾನು ಯೆರೂಸಲೇಮಿನವರನ್ನು ಖಡ್ಗಕ್ಷಾಮವ್ಯಾಧಿಗಳಿಂದ ದಂಡಿಸಿದಂತೆ ಐಗುಪ್ತದಲ್ಲಿ ವಾಸಿಸುವವರನ್ನೂ ದಂಡಿಸುವೆನು;",
			"14": "<sup>14</sup> ಐಗುಪ್ತಕ್ಕೆ ಹೋಗಿ ಪ್ರವಾಸಿಸುತ್ತಿರುವ ಯೆಹೂದಜನಶೇಷದವರು ಸ್ವದೇಶಕ್ಕೆ ಹಿಂದಿರುಗಬೇಕೆಂದು ಎಷ್ಟು ಆಶಿಸಿದರೂ ಅವರಲ್ಲಿ ಯಾರೂ ಉಳಿದು ಅಲ್ಲಿಂದ ತಪ್ಪಿಸಿಕೊಂಡು ಯೆಹೂದಕ್ಕೆ ಹಿಂದಿರುಗುವದಿಲ್ಲ; ಓಡಿಬರುವ ಕೊಂಚ ಜನವೇ ಹೊರತು ಯಾವನೂ ಹಿಂದಿರುಗನು.",
			"15": "<sup>15</sup> ಆಗ ತಮ್ಮ ಹೆಂಡತಿಯರು ಅನ್ಯದೇವತೆಗಳಿಗೆ ಧೂಪಹಾಕುತ್ತಿದ್ದರೆಂದು ತಿಳಿದುಕೊಂಡ ಗಂಡಸರು, ಅಲ್ಲಿ ದೊಡ್ಡ ಗುಂಪಾಗಿ ನಿಂತುಕೊಂಡಿದ್ದ ಹೆಂಗಸರು, ಅಂತು ಐಗುಪ್ತದಲ್ಲಿಯೂ ಪತ್ರೋಸಿನಲ್ಲಿಯೂ ವಾಸವಾಗಿದ್ದವರೆಲ್ಲರೂ ಯೆರೆಮೀಯನಿಗೆ ಹೀಗೆ ಹೇಳಿದರು -",
			"16": "<sup>16</sup> ಯೆಹೋವನ ಹೆಸರಿನಿಂದ ನೀನು ನಮಗೆ ನುಡಿದ ಮಾತನ್ನು ಕೇಳಲೊಲ್ಲೆವು.",
			"17": "<sup>17</sup> ಗಗನದ ಒಡತಿಗೆ ಧೂಪಹಾಕಿ ಪಾನನೈವೇದ್ಯವನ್ನು, ಸುರಿಯುವೆವು ಎಂದು ನಾವು ಬಾಯಿಬಿಟ್ಟು ಹೇಳಿದ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ಖಂಡಿತವಾಗಿ ನೆರವೇರಿಸುವೆವು. ಮೊದಲು ಯೆಹೂದದ ಊರುಗಳಲ್ಲಿ ಮತ್ತು ಯೆರೂಸಲೇಮಿನ ಬೀದಿಗಳಲ್ಲಿ ನಾವೂ ನಮ್ಮ ಪಿತೃಗಳೂ ನಮ್ಮ ಅರಸರೂ ನಮ್ಮ ಪ್ರಧಾನರೂ ಹೀಗೆ ಮಾಡುತ್ತಿದ್ದಾಗ ನಾವು ಯಾವ ಕೇಡನ್ನೂ ಕಾಣದೆ ಹೊಟ್ಟೆತುಂಬಾ ಉಂಡು ಸುಖಪಡುತ್ತಿದ್ದೆವು.",
			"18": "<sup>18</sup> ಆದರೆ ನಾವು ಗಗನದ ಒಡತಿಗೆ ಧೂಪಹಾಕುವದನ್ನೂ ಪಾನವನ್ನು ನೈವೇದ್ಯವಾಗಿ ಸುರಿಯುವದನ್ನೂ ನಿಲ್ಲಿಸಿಬಿಟ್ಟಂದಿನಿಂದ ಎಲ್ಲಾ ಕೊರತೆಗೂ ಗುರಿಯಾಗಿ ಖಡ್ಗಕ್ಷಾಮಗಳಿಂದ ನಾಶವಾಗುತ್ತಿದ್ದೇವೆ.",
			"19": "<sup>19</sup> ಗಗನದ ಒಡತಿಗೆ ಧೂಪಹಾಕಿ ಪಾನನೈವೇದ್ಯವನ್ನು ಸುರಿಯುತ್ತಿದ್ದಾಗ ನಾವು ಆಕೆಯ ಪೂಜೆಗಾಗಿ ಆಕೆಯ ಆಕಾರವಾದ ಹೋಳಿಗೆಗಳನ್ನು ಮಾಡಿ ಪಾನದ್ರವ್ಯವನ್ನು ಅರ್ಪಿಸಿದ್ದು ನಮ್ಮ ಗಂಡಂದಿರಿಗೆ ಒಪ್ಪಿಗೆಯಾಗಿರಲಿಲ್ಲವೋ?",
			"20": "<sup>20</sup> ಆಗ ಯೆರೆಮೀಯನು ಗಂಡಸರಿಗೂ ಹೆಂಗಸರಿಗೂ ಅಂತು ತನಗೆ ಆ ಉತ್ತರಕೊಟ್ಟ ಎಲ್ಲಾ ಜನರಿಗೂ ಹೀಗೆ ಹೇಳಿದನು -",
			"21": "<sup>21</sup> ನೀವು, ನಿಮ್ಮ ಪಿತೃಗಳು, ನಿಮ್ಮ ಅರಸರು, ನಿಮ್ಮ ಪ್ರಧಾನರು, ಸಾಮಾನ್ಯ ಜನರು, ನೀವೆಲ್ಲರೂ ಯೆಹೂದದ ಊರುಗಳಲ್ಲಿ ಮತ್ತು ಯೆರೂಸಲೇಮಿನ ಬೀದಿಗಳಲ್ಲಿ ಹಾಕಿದ ಧೂಪವು ಯೆಹೋವನ ನೆನಪಿನಲ್ಲಿ ಇಲ್ಲವೋ, ಅದು ಆತನ ಜ್ಞಾಪಕಕ್ಕೆ ಬರಲಿಲ್ಲವೋ?",
			"22": "<sup>22</sup> ನೀವು ನಡಿಸಿದ ದುರಾಚಾರಗಳನ್ನೂ ಅಸಹ್ಯಕಾರ್ಯಗಳನ್ನೂ ಯೆಹೋವನು ಇನ್ನು ಸಹಿಸಲಾರದೆ ಹೋದದರಿಂದ ನಿಮ್ಮ ದೇಶವು ಹಾಳುಬಿದ್ದು ಜನವಿಲ್ಲದೆ ಬೆರಗಿಗೂ ಶಾಪಕ್ಕೂ ಆಸ್ಪದವಾಯಿತು; ಈಗಲೂ ಹಾಗೆಯೇ ಇದೆ.",
			"23": "<sup>23</sup> ನೀವು ಯೆಹೋವನ ಮಾತನ್ನು ಕೇಳದೆ ಆತನ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನೂ ನಿಬಂಧನೆಗಳನ್ನೂ ಕಟ್ಟಳೆಗಳನ್ನೂ ಅನುಸರಿಸದೆ ಆತನಿಗೆ ಪಾಪಮಾಡಿ [ಅನ್ಯದೇವತೆಗಳಿಗೆ] ಧೂಪ ಹಾಕಿದ್ದರಿಂದಲೇ ಇಂಥಾ ಕೇಡು ನಿಮಗೆ ಸಂಭವಿಸಿದೆ.",
			"24": "<sup>24</sup> ಇದಲ್ಲದೆ ಯೆರೆಮೀಯನು ಹೆಂಗಸರಿಗೂ ಎಲ್ಲಾ ಜನರಿಗೂ ಹೀಗೆ ಹೇಳಿದನು - ಐಗುಪ್ತದಲ್ಲಿರುವ ಯೆಹೂದ್ಯರೇ, ನೀವೆಲ್ಲರೂ ಯೆಹೋವನ ಮಾತನ್ನು ಕೇಳಿರಿ -",
			"25": "<sup>25</sup> ಇಸ್ರಾಯೇಲಿನ ದೇವರೂ ಸೇನಾಧೀಶ್ವರನೂ ಆದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಗಗನದ ಒಡತಿಗೆ ಧೂಪಹಾಕಿ ಪಾನನೈವೇದ್ಯವನ್ನು ಸುರಿಯುವೆವು ಎಂದು ನಾವು ಮಾಡಿಕೊಂಡ ಹರಕೆಗಳನ್ನು ಖಂಡಿತವಾಗಿ ತೀರಿಸುವೆವು ಎಂಬದಾಗಿ ನೀವೂ ನಿಮ್ಮ ಹೆಂಡಿರೂ ಬಾಯಿಂದ ಪ್ರತಿಜ್ಞೆಮಾಡಿ ಕೈಯಿಂದ ನೆರವೇರಿಸಿದ್ದೀರಲ್ಲಾ; ನಿಮ್ಮ ಹರಕೆಗಳನ್ನು ತೀರಿಸೇ ತೀರಿಸಿರಿ, ನೆರವೇರಿಸೇ ನೆರವೇರಿಸಿರಿ!",
			"26": "<sup>26</sup> ಐಗುಪ್ತದಲ್ಲಿ ವಾಸವಾಗಿರುವ ಯೆಹೂದ್ಯರೇ, ನೀವೆಲ್ಲರೂ ಯೆಹೋವನ ಮಾತನ್ನು ಕೇಳಿರಿ; ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಆಹಾ, ಕರ್ತನಾದ ಯೆಹೋವನ ಜೀವದಾಣೆ ಎಂದು ನನ್ನ ಹೆಸರನ್ನು ಬಾಯಿಂದ ಎತ್ತಲಿಕ್ಕೆ ಐಗುಪ್ತದಲ್ಲಿ ಯಾವ ಯೆಹೂದ್ಯನೂ ಇನ್ನಿರನು ಎಂಬದಾಗಿ ನನ್ನ ಮಹಾನಾಮದ ಮೇಲೆ ಆಣೆಯಿಟ್ಟಿದ್ದೇನೆ.",
			"27": "<sup>27</sup> ಇಗೋ, ಇವರಿಗೆ ಮೇಲನ್ನಲ್ಲ, ಕೇಡನ್ನೇ ಮಾಡಬೇಕೆಂದು ಎಚ್ಚರಗೊಂಡಿದ್ದೇನೆ; ಐಗುಪ್ತದಲ್ಲಿನ ಎಲ್ಲಾ ಯೆಹೂದ್ಯರು ಖಡ್ಗಕ್ಷಾಮಗಳಿಂದ ನಾಶಹೊಂದುತ್ತಾ ನಿರ್ನಾಮವಾಗುವರು.",
			"28": "<sup>28</sup> ಖಡ್ಗಕ್ಕೆ ತಪ್ಪಿಸಿಕೊಂಡವರು ಕೊಂಚ ಜನ ಮಾತ್ರವೇ ಆಗಿ ಐಗುಪ್ತದಿಂದ ಯೆಹೂದಕ್ಕೆ ಹಿಂದಿರುಗುವರು; ಆಗ ಐಗುಪ್ತಕ್ಕೆ ಹೋಗಿ ಪ್ರವಾಸಿಸುತ್ತಿರುವ ಯೆಹೂದ ಜನಶೇಷದವರೆಲ್ಲರೂ ನನ್ನ ಮಾತು ಸಾಗತಕ್ಕದ್ದೋ ತಮ್ಮ ಮಾತು ಸಾಗತಕ್ಕದ್ದೋ ಎಂದು ತಿಳಿದುಕೊಳ್ಳುವರು.",
			"29": "<sup>29</sup> ನಾನು ನಿಮ್ಮನ್ನು ಈ ಸ್ಥಳದಲ್ಲಿ ದಂಡಿಸುವೆನೆಂಬದಕ್ಕೆ ಒಂದು ಗುರುತು ಕಾಣುವದು; ನನ್ನ ಮಾತುಗಳು ಈಡೇರಿ ನಿಮಗೆ ಕೇಡನ್ನುಂಟುಮಾಡುವವು ಎಂಬದಾಗಿ ಅದರಿಂದಲೇ ನಿಮಗೆ ತಿಳಿದುಬರುವದು;",
			"30": "<sup>30</sup> ಅದೇನಂದರೆ - ಇಗೋ, ನಾನು ಯೆಹೂದದ ಅರಸನಾದ ಚಿದ್ಕೀಯನನ್ನು ಅವನ ಪ್ರಾಣ ಹುಡುಕುತ್ತಿದ್ದ ಶತ್ರುವಾದ ನೆಬೂಕದ್ನೆಚ್ಚರನೆಂಬ ಬಾಬೆಲಿನ ಅರಸನ ಕೈಗೆ ಕೊಟ್ಟಂತೆ ಐಗುಪ್ತದ ಅರಸನಾದ ಫರೋಹ ಹೊಫ್ರನನ್ನು ಅವನ ಪ್ರಾಣ ಹುಡುಕುವ ಶತ್ರುಗಳ ಕೈಗೆ ಕೊಡುವೆನು ಎಂಬದೇ; ಇದು ಯೆಹೋವನಾದ ನನ್ನ ನುಡಿ."
		},
		"45": {
			"1": "<sup>1</sup> ಯೋಷೀಯನ ಮಗನೂ ಯೆಹೂದದ ಅರಸನೂ ಆದ ಯೆಹೋಯಾಕೀಮನ ಆಳಿಕೆಯ ನಾಲ್ಕನೆಯ ವರುಷದಲ್ಲಿ ನೇರೀಯನ ಮಗನಾದ ಬಾರೂಕನು ಯೆರೆಮೀಯನ ಮಾತುಗಳನ್ನು ಅವನ ಬಾಯಿಂದ ಬಂದ ಹಾಗೆಯೇ ಪುಸ್ತಕದಲ್ಲಿ ಬರೆದಾಗ ಪ್ರವಾದಿಯಾದ ಯೆರೆಮೀಯನು ಯೆಹೋವನ ಅಪ್ಪಣೆಯ ಪ್ರಕಾರ",
			"2": "<sup>2</sup> ಈ ವಾಕ್ಯವನ್ನು ಅವನಿಗೆ ನುಡಿದನು - ಬಾರೂಕನೇ, ಇಸ್ರಾಯೇಲಿನ ದೇವರಾದ ಯೆಹೋವನು ನಿನಗೆ ಹೀಗೆ ಹೇಳುತ್ತಾನೆ - ನೀನು, ನನ್ನ ಗತಿಯನ್ನು ಏನು ಹೇಳಲಿ!",
			"3": "<sup>3</sup> ವ್ಯಥೆಪಡುತ್ತಿದ್ದ ನನ್ನನ್ನು ಯೆಹೋವನು ಹೆಚ್ಚೆಚ್ಚಾಗಿ ದುಃಖಪಡಿಸಿದ್ದಾನೆ; ನಾನು ನರಳಿ ನರಳಿ ದಣಿದಿದ್ದೇನೆ, ನನಗೆ ಯಾವ ವಿಶ್ರಾಂತಿಯೂ ದೊರಕದು ಎಂದು ಹೇಳಿದಿಯಲ್ಲಾ.",
			"4": "<sup>4</sup> ಯೆಹೋವನ ಈ ವಾಕ್ಯವನ್ನು ಕೇಳು - ಇಗೋ, ನಾನು ಕಟ್ಟಿದ್ದನ್ನು ನಾನೇ ಕೆಡವುವೆನು, ನಾನು ನೆಟ್ಟದ್ದನ್ನು ನಾನೇ ಕಿತ್ತುಹಾಕುವೆನು; ಹೌದು, ಭೂಮಂಡಲದಲ್ಲೆಲ್ಲಾ ಹಾಗೆ ಮಾಡುವೆನು.",
			"5": "<sup>5</sup> ಮಹಾಪದವಿಯನ್ನು ನಿರೀಕ್ಷಿಸಿಕೊಳ್ಳುತ್ತೀಯೋ? ನಿರೀಕ್ಷಿಸಬೇಡ; ಆಹಾ, ನಾನು ನರಜನ್ಮದವರೆಲ್ಲರಿಗೂ ಕೇಡನ್ನುಂಟುಮಾಡುವೆನು; ಆದರೆ ನೀನು ಎಲ್ಲಿಗೆ ಹೋದರೂ ಪ್ರಾಣವೊಂದನ್ನೇ ಬಾಚಿಕೊಂಡುಹೋಗುವದಕ್ಕೆ ನಿನಗೆ ಅವಕಾಶಕೊಡುವೆನು. ಇದು ಯೆಹೋವನಾದ ನನ್ನ ನುಡಿ."
		},
		"46": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಜನಾಂಗಗಳ ವಿಷಯವಾಗಿ ಪ್ರವಾದಿಯಾದ ಯೆರೆಮೀಯನಿಗೆ ದಯಪಾಲಿಸಿದ ವಾಕ್ಯವು.",
			"2": "<sup>2</sup> ಐಗುಪ್ತವನ್ನು ಕುರಿತದ್ದು. ಐಗುಪ್ತದ ಅರಸನಾದ ಫರೋಹನೆಕೋವಿನ ಸೈನ್ಯವು ಯೂಫ್ರೇಟೀಸ್ ನದಿಯ ಬಳಿ ಕರ್ಕೆಮೀಷಿನಲ್ಲಿರುವಾಗ ಯೋಷೀಯನ ಮಗನೂ ಯೆಹೂದದ ಅರಸನೂ ಆದ ಯೆಹೋಯಾಕೀಮನ ಆಳಿಕೆಯ ನಾಲ್ಕನೆಯ ವರುಷದಲ್ಲಿ ಬಾಬೆಲಿನ ಅರಸನಾದ ನೆಬೂಕದ್ನೆಚ್ಚರನು ಆ ಸೈನ್ಯವನ್ನು ಹೊಡೆದ ವಿಷಯ.",
			"3": "<sup>3</sup> ಗುರಾಣಿಖೇಡ್ಯಗಳನ್ನು ಸನ್ನಾಹಮಾಡಿಕೊಂಡು ಯುದ್ಧಕ್ಕೆ ಹೊರಡಿರಿ!",
			"4": "<sup>4</sup> ರಥಾಶ್ವಗಳನ್ನು ಕಟ್ಟಿರಿ, ವಾಹನಾಶ್ವಗಳನ್ನು ಹತ್ತಿರಿ, ಶಿರಸ್ತ್ರಾಣಗಳನ್ನು ಹಾಕಿಕೊಂಡು ಯುದ್ಧಕ್ಕೆ ನಿಲ್ಲಿರಿ, ಬರ್ಜಿಗಳನ್ನು ಬೆಳಗಿರಿ, ಕವಚ ತೊಟ್ಟುಕೊಳ್ಳಿರಿ.",
			"5": "<sup>5</sup> ಆಹಾ, ನನ್ನ ಕಣ್ಣಿಗೆ ಬಿದ್ದದ್ದೇನು? ಅವರು ಧೈರ್ಯಗೆಟ್ಟು ಬೆನ್ನು ಕೊಟ್ಟಿದ್ದಾರೆ. ಅವರ ಶೂರರು ಪೆಟ್ಟುತಿಂದು ಹಿಂದಿರುಗದೆ ಓಡಿಹೋಗುತ್ತಾರೆ; ಸುತ್ತುಮುತ್ತಲು ದಿಗಿಲು ಎಂದು ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ.",
			"6": "<sup>6</sup> ವೇಗಶಾಲಿಗಳೂ ಓಡಿಹೋಗಲಾರರು, ಬಲಿಷ್ಠರೂ ತಪ್ಪಿಸಿಕೊಳ್ಳಲಾರರು; ಬಡಗಲಲ್ಲಿ ಯೂಫ್ರೇಟೀಸ್ ನದಿಯ ಹತ್ತಿರ ಎಡವಿಬಿದ್ದಿದ್ದಾರೆ.",
			"7": "<sup>7</sup> ನೈಲ್ ನದಿಯಂತೆ ಉಬ್ಬಿರುವದು ಇದೇನು? ನೈಲಿನ ಶಾಖೆಗಳ ಹಾಗೆ ತುಂಬಿತುಳುಕುವ ಈ ಪ್ರವಾಹವು ಯಾವದು?",
			"8": "<sup>8</sup> ಐಗುಪ್ತವೇ ನೈಲಿನಂತೆ ಉಬ್ಬಿದೆ; ಐಗುಪ್ತವೆಂಬ ಪ್ರವಾಹವೇ ನೈಲಿನ ಶಾಖೆಗಳ ಹಾಗೆ ತುಂಬಿ ತುಳುಕುತ್ತದೆ; ನಾನು ಉಬ್ಬಿಕೊಂಡು ಲೋಕದಲ್ಲೆಲ್ಲಾ ಹಬ್ಬುವೆನು, ಪಟ್ಟಣವನ್ನು ಪಟ್ಟಣನಿವಾಸಿಗಳನ್ನು ನಾಶಮಾಡುವೆನು ಅಂದುಕೊಳ್ಳುತ್ತದೆ.",
			"9": "<sup>9</sup> ಅಶ್ವಗಳೇ, ಕುಣಿದಾಡಿರಿ! ರಥಗಳೇ, ರಭಸವಾಗಿರಿ! ಶೂರರು ಹೊರಡಲಿ! ಖೇಡ್ಯಸನ್ನದ್ಧರಾದ ಕೂಷ್ಯರು, ಪೂಟ್ಯರು, ಧನುರ್ಧಾರಿಗಳಾದ ಲೂದ್ಯರು ಮುಂದರಿಯಲಿ!",
			"10": "<sup>10</sup> ಆದರೆ ಆ ಯುದ್ಧದಿನವು ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೆಂಬ ಕರ್ತನು ತನ್ನ ಶತ್ರುಗಳಿಗೆ ಮುಯ್ಯಿತೀರಿಸುವ ದಿನವಾಗಿದೆ; ಅದು ದಂಡನೆಯ ದಿವಸ; ಆಗ ಖಡ್ಗವು ನುಂಗಿ ನುಂಗಿ ತಣಿಯುವದು, ಅವರ ರಕ್ತವನ್ನು ತುಂಬಾ ಹೀರುವದು; ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೆಂಬ ಕರ್ತನು ಬಡಗಲಲ್ಲಿ ಯೂಫ್ರೇಟೀಸ್ ನದಿಯ ಹತ್ತಿರ ಬಲಿಮಾಡಬೇಕೆಂದಿದ್ದಾನಲ್ಲಾ.",
			"11": "<sup>11</sup> ಐಗುಪ್ತಕುಮಾರಿಯೇ, ಯುವತಿಯೇ, ಗಿಲ್ಯಾದಿಗೆ ಹೋಗಿ ಔಷಧವನ್ನು ಸಂಪಾದಿಸು; ನೀನು ಬಗೆಬಗೆಯ ಮದ್ದನ್ನು ಉಪಯೋಗಿಸುವದು ಪ್ರಯೋಜನವಿಲ್ಲ; ನಿನಗೆ ಗುಣವಾಗದು.",
			"12": "<sup>12</sup> ನಿನ್ನ ಅವಮಾನದ ಸುದ್ದಿಯು ಜನಾಂಗಗಳಿಗೆ ಕೇಳಬಂದಿದೆ, ನಿನ್ನ ಕೂಗಾಟವು ಲೋಕದಲ್ಲೆಲ್ಲಾ ತುಂಬಿದೆ; ವೀರನು ವೀರನನ್ನು ಎಡವಿ ಇಬ್ಬರೂ ಬಿದ್ದಿದ್ದಾರೆ.",
			"13": "<sup>13</sup> ಬಾಬೆಲಿನ ಅರಸನಾದ ನೆಬೂಕದ್ನೆಚ್ಚರನು ಬಂದು ಐಗುಪ್ತದೇಶವನ್ನು ಹೊಡೆಯುವನು ಎಂಬದಾಗಿ ಯೆಹೋವನು ಪ್ರವಾದಿಯಾದ ಯೆರೆಮೀಯನಿಗೆ ನುಡಿದ ವಾಕ್ಯವು.",
			"14": "<sup>14</sup> ಐಗುಪ್ತದಲ್ಲಿ ಪ್ರಕಟಿಸಿರಿ, ಮಿಗ್ದೋಲ್, ತಹಪನೇಸ್, ನೋಫ್ ಎಂಬೀ ಪಟ್ಟಣಗಳಲ್ಲಿ ಪ್ರಚುರಪಡಿಸಿರಿ - [ಐಗುಪ್ತವೇ,] ಸ್ಥಿರವಾಗಿ ನಿಂತುಕೊಂಡು ಸನ್ನದ್ಧವಾಗು! ಖಡ್ಗವು ನಿನ್ನ ಸುತ್ತಲು ನುಂಗಿಬಿಟ್ಟಿದೆ ಎಂದು ಸಾರಿರಿ.",
			"15": "<sup>15</sup> ನಿನ್ನ ಬಸವ ಬಿದ್ದು ಹೋದದ್ದೇನು? ಯೆಹೋವನು ಅದನ್ನು ಕೆಡವಿದನು. ನಿಲ್ಲಲಾರದೆ ಹೋಯಿತು.",
			"16": "<sup>16</sup> ಬಹು ಜನರು ಮುಗ್ಗರಿಸುವಂತೆ ಮಾಡಿದನು; ಹೌದು, ಒಬ್ಬರಿಗೊಬ್ಬರು ತಗಲಿ ಬಿದ್ದರು; ನಾವು ಎದ್ದು ಜನ್ಮಭೂಮಿಗೆ ಹೊರಟು ಸ್ವಜನರನ್ನು ಸೇರಿ ಹಿಂಸೆಯ ಖಡ್ಗದಿಂದ ತಪ್ಪಿಸಿಕೊಳ್ಳೋಣ ಎಂದುಕೊಂಡರು.",
			"17": "<sup>17</sup> ಐಗುಪ್ತದ ಅರಸನಾದ ಫರೋಹನು ಧಡಮ್ಮನೆ ಬಿದ್ದವನು, ಯುಕ್ತಸಮಯವನ್ನು ಉಪೇಕ್ಷಿಸಿದವನು ಎಂದು ಅಲ್ಲಿ ಕೂಗಿದರು.",
			"18": "<sup>18</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೆಂಬ ನಾಮವುಳ್ಳ ರಾಜಾಧಿರಾಜನು ಇಂತೆನ್ನುತ್ತಾನೆ - ನನ್ನ ಜೀವದಾಣೆ, ಇಗೋ, ಪರ್ವತಗಳನ್ನು ಮೀರುವ ತಾಬೋರ್ ಬೆಟ್ಟದಂತೆಯೂ ಸಮುದ್ರದಿಂದೆದ್ದಿರುವ ಕರ್ಮೆಲ್ ಗುಡ್ಡದ ಹಾಗೂ ಉನ್ನತನಾದವನೊಬ್ಬನು ಬರುವನು.",
			"19": "<sup>19</sup> ಐಗುಪ್ತದಲ್ಲಿ ವಾಸಿಸುವ ಯುವತಿಯೇ, ಸೆರೆಗೆ ಬೇಕಾದ ಸಾಮಗ್ರಿಗಳನ್ನು ಒದಗಿಸಿಕೋ; ನೋಫ್ ಪಟ್ಟಣವು ಹಾಳಾಗಿ ಸುಟ್ಟು ನಿರ್ಜನವಾಗುವದು.",
			"20": "<sup>20</sup> ಐಗುಪ್ತವು ಅಂದವಾದ ಕಡಸಾಗಿದೆ; ತೊಣಚೆಯು ಬಡಗಲಿಂದ ಅದರ ಮೇಲೆ ಬಂದಿದೆ.",
			"21": "<sup>21</sup> ಸಂಬಳಕ್ಕಾಗಿ ಐಗುಪ್ತವನ್ನು ಸೇರಿರುವ ದಂಡಾಳುಗಳು ಕೊಬ್ಬಿದ ಕರುಗಳಂತಿದ್ದಾರೆ; ಅವರೂ ಬೆನ್ನುಕೊಟ್ಟು ಓಡಿಹೋಗಿದ್ದಾರೆ; ನಿಲ್ಲಲೇ ಇಲ್ಲ; ಅವರಿಗೆ ವಿಪತ್ಕಾಲವು ಒದಗಿದೆ, ದಂಡನೆಯ ದಿನವು ಸಂಭವಿಸಿದೆ.",
			"22": "<sup>22</sup> ಐಗುಪ್ತವು ಓಡಿಹೋಗುವ ಶಬ್ದವು ಸರಿಯುವ ಸರ್ಪದ ಸಪ್ಪಳಕ್ಕೆ ಸಮನಾಗಿರುವದು; [ಶತ್ರುಗಳು] ದಂಡೆತ್ತಿಬಂದು ಕಟ್ಟಿಗೆ ಕಡಿಯುವವರಂತೆ ಕೊಡಲಿಗಳಿಂದ ಅದರ ಮೇಲೆ ಬೀಳುವರು.",
			"23": "<sup>23</sup> ಅವರು ಮಿಡಿತೆಗಳಿಗಿಂತಲೂ ಅಸಂಖ್ಯಾತರಾಗಿ ದಾಟಲಾಗದ ಐಗುಪ್ತದ ದಟ್ಟವಾದ ಕಾಡನ್ನು ಕಡಿದು ಹಾಕುವರು. ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"24": "<sup>24</sup> ಐಗುಪ್ತವೆಂಬ ಯುವತಿಯು ಅವಮಾನಕ್ಕೆ ಗುರಿಯಾಗುವಳು; ಬಡಗಲವರ ಕೈವಶವಾಗುವಳು.",
			"25": "<sup>25</sup> ಇಸ್ರಾಯೇಲಿನ ದೇವರೂ ಸೇನಾಧೀಶ್ವರನೂ ಆದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಇಗೋ, ನಾನು ನೋ ಪಟ್ಟಣದಲ್ಲಿನ ಆಮೋನ್ ದೇವತೆ, ಫರೋಹ, ಐಗುಪ್ತ, ಐಗುಪ್ತದ ದೇವತೆಗಳು, ಅಲ್ಲಿನ ಅರಸರು, ಅಂತು ಫರೋಹನನ್ನೂ, ಅವನಲ್ಲಿ ನಂಬಿಕೆಯಿಟ್ಟವರನ್ನೂ ದಂಡಿಸಿ",
			"26": "<sup>26</sup> ಅವರ ಪ್ರಾಣವನ್ನು ಹುಡುಕುವ ಬಾಬೆಲಿನ ಅರಸನಾದ ನೆಬೂಕದ್ನೆಚ್ಚರನ ಮತ್ತು ಅವನ ಸೇವಕರ ಕೈಗೆ ಕೊಟ್ಟುಬಿಡುವೆನು; ಅನಂತರ ಅಲ್ಲಿ ಪೂರ್ವಕಾಲದ ಹಾಗೆ ಜನರು ವಾಸಿಸುವರು. ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"27": "<sup>27</sup> ನನ್ನ ಸೇವಕ ಯಾಕೋಬೇ, ಭಯಪಡಬೇಡ! ಇಸ್ರಾಯೇಲೇ, ಅಂಜಬೇಡ! ಇಗೋ, ನಾನು ನಿನ್ನನ್ನು ದೂರದೇಶದಿಂದ ಉದ್ಧರಿಸುವೆನು, ನಿನ್ನ ಸಂತಾನವನ್ನು ಸೆರೆಹೋದ ಸೀಮೆಯಿಂದ ರಕ್ಷಿಸುವೆನು; ಯಾಕೋಬು ಹಿಂದಿರುಗಿ ನೆಮ್ಮದಿಯಾಗಿಯೂ ಹಾಯಾಗಿಯೂ ಇರುವದು; ಯಾರೂ ಅದನ್ನು ಹೆದರಿಸರು.",
			"28": "<sup>28</sup> ನನ್ನ ಸೇವಕ ಯಾಕೋಬೇ, ಭಯಪಡಬೇಡ, ನಾನು ನಿನ್ನೊಂದಿಗಿದ್ದೇನೆ; ನಾನು ಯಾವ ಜನಾಂಗಗಳಲ್ಲಿಗೆ ನಿನ್ನನ್ನು ಅಟ್ಟಿದೆನೋ ಆ ಜನಾಂಗಗಳನ್ನೆಲ್ಲಾ ನಿರ್ಮೂಲಮಾಡುವೆನು; ನಿನ್ನನ್ನೋ ನಿರ್ಮೂಲಮಾಡೆನು; ಮಿತಿಮೀರಿ ಶಿಕ್ಷಿಸೆನು, ಆದರೆ ಶಿಕ್ಷಿಸದೆ ಬಿಡುವದಿಲ್ಲ. ಇದು ಯೆಹೋವನ ನುಡಿ."
		},
		"47": {
			"1": "<sup>1</sup> ಫರೋಹನು ಗಾಜಾ ಊರನ್ನು ಹೊಡೆದದ್ದಕ್ಕೆ ಮುಂಚಿತವಾಗಿ ಯೆಹೋವನು ಫಿಲಿಷ್ಟಿಯರ ವಿಷಯವಾಗಿ ಪ್ರವಾದಿಯಾದ ಯೆರೆಮೀಯನಿಗೆ ದಯಪಾಲಿಸಿದ ವಾಕ್ಯ.",
			"2": "<sup>2</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಇಗೋ, ಪ್ರವಾಹವು ಬಡಗಲಿಂದ ಹೊರಟು ತುಂಬಿತುಳುಕುವ ತೊರೆಯಾಗಿ ದೇಶವನ್ನೂ ಅದರಲ್ಲಿರುವ ಸಮಸ್ತವನ್ನೂ ಪಟ್ಟಣವನ್ನೂ ಪಟ್ಟಣದ ನಿವಾಸಿಗಳನ್ನೂ ಆಕ್ರಮಿಸುವದು; ಆಗ ಅಲ್ಲಿನವರು ಮೊರೆಯಿಡುವರು, ದೇಶದವರೆಲ್ಲಾ ಗೋಳಾಡುವರು.",
			"3": "<sup>3</sup> ಶತ್ರುವಿನ ತುರಗಗಳ ಗೊರಸುಗಳು ನೆಲವನ್ನು ಅಪ್ಪಳಿಸುವ ಶಬ್ದಕ್ಕೂ, ರಥಗಳ ರಭಸಕ್ಕೂ, ಚಕ್ರಗಳ ಬಿರುಗುಟ್ಟುವಿಕೆಗೂ ತಂದೆಗಳು ಹೆದರುತ್ತಾ ಜೋಲುಗೈಯವರಾಗಿ ತಮ್ಮ ಮಕ್ಕಳನ್ನು ಹಿಂದಿರುಗಿ ನೋಡರು;",
			"4": "<sup>4</sup> ಏಕಂದರೆ ಆ ದಿನದಲ್ಲಿ ಫಿಲಿಷ್ಟಿಯರೆಲ್ಲರೂ ಸೂರೆಯಾಗುವರು, ತೂರಿನ ಮತ್ತು ಚೀದೋನಿನ ಸಹಾಯಕರಲ್ಲಿ ಕಳೆದುಳಿದವರೆಲ್ಲಾ ನಿರ್ಮೂಲವಾಗುವರು; ಕಫ್ತೋರ್ ದ್ವೀಪದವರಿಂದ ಉತ್ಪನ್ನರಾದ ಫಿಲಿಷ್ಟಿಯರನ್ನು ಯೆಹೋವನೇ ಸೂರೆಮಾಡುವನು.",
			"5": "<sup>5</sup> ಗಾಜಾ ಊರು ಬೋಳಿಸಿಕೊಂಡಿದೆ, ಅಷ್ಕೆಲೋನ್ ನಾಶನಹೊಂದಿದೆ ಫಿಲಿಷ್ಟಿಯದ ಬೈಲುಸೀಮೆಯಲ್ಲಿ ಉಳಿದ ಪಟ್ಟಣವೇ, ನಿನ್ನನ್ನು ಎಂದಿನವರೆಗೆ ಕತ್ತರಿಸಿಕೊಳ್ಳುತ್ತಿರುವಿ?",
			"6": "<sup>6</sup> ಅಯ್ಯೋ, ಯೆಹೋವನ ಖಡ್ಗವೇ, ಇನ್ನೆಷ್ಟರವರೆಗೆ ವಿಶ್ರಾಂತಿಗೊಳ್ಳದೆ ಇರುವಿ? ನಿನ್ನನ್ನು ಒರೆಯಲ್ಲಿ ಅಣಗಿಕೋ! ಶಾಂತಿಗೊಳ್ಳು, ಸುಮ್ಮನಿರು!",
			"7": "<sup>7</sup> ಖಡ್ಗವು ವಿಶ್ರಾಂತಿಗೊಳ್ಳುವದು ಹೇಗೆ? ಅಷ್ಕೆಲೋನಿನ ಮತ್ತು ಕರಾವಳಿಯ ವಿಷಯವಾಗಿ ಯೆಹೋವನು ಅದಕ್ಕೆ ಅಪ್ಪಣೆಕೊಟ್ಟನಲ್ಲಾ; ಅಲ್ಲಿಗೇ ಅದನ್ನು ನೇಮಿಸಿದ್ದಾನೆ."
		},
		"48": {
			"1": "<sup>1</sup> ಮೋವಾಬನ್ನು ಕುರಿತದ್ದು. ಇಸ್ರಾಯೇಲಿನ ದೇವರೂ ಸೇನಾಧೀಶ್ವರನೂ ಆದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಅಯ್ಯೋ, ನೆಬೋಊರಿನ ಗತಿಯನ್ನು ಏನು ಹೇಳಲಿ! ಅದು ಹಾಳಾಯಿತು; ಕಿರ್ಯಾತಯಿಮು ಶತ್ರುವಿನ ಕೈಗೆ ಸಿಕ್ಕಿ ಮಾನಭಂಗಹೊಂದಿದೆ, ಮಿಸ್ಗಾಬ್ ಕೆಡವಲ್ಪಟ್ಟು ಅವಮಾನಕ್ಕೆ ಗುರಿಯಾಗಿದೆ.",
			"2": "<sup>2</sup> ಮೋವಾಬಿನ ಕೀರ್ತಿಯು ಮಾಯವಾಯಿತು; ಶತ್ರುಗಳು ಹೆಷ್ಬೋನಿನಲ್ಲಿ ದೇಶಕ್ಕೆ ಕೇಡನ್ನು ಕಲ್ಪಿಸಿ - ಮೋವಾಬು ಇನ್ನು ಜನಾಂಗವೆನಿಸಿಕೊಳ್ಳದಂತೆ ಅದನ್ನು ನಿರ್ಮೂಲಮಾಡೋಣ ಬನ್ನಿರಿ ಎಂದು ಆಲೋಚಿಸಿಕೊಂಡಿದ್ದಾರೆ. ಮದ್ಮೆನೇ, ನೀನು ಸಹ ಸುಮ್ಮನಾಗುವಿ; ಖಡ್ಗವು ನಿನ್ನನ್ನು ಹಿಂದಟ್ಟುವದು.",
			"3": "<sup>3</sup> ಅಯ್ಯೋ, ಸೂರೆಹೋದೆವು, ಬಹು ನಾಶವಾದೆವು ಎಂಬ ಕೂಗಾಟವು ಹೊರೊನಯಿಮಿನಿಂದ ಕೇಳಬರುತ್ತದೆ!",
			"4": "<sup>4</sup> ಮೋವಾಬಿಗೆ ಭಂಗವಾಯಿತು; ಅಲ್ಲಿ ದಿಕ್ಕೆಟ್ಟವರು ಮೊರೆಯಿಟ್ಟಿದ್ದಾರೆ.",
			"5": "<sup>5</sup> ಅಳುತ್ತಳುತ್ತಾ ಲೂಹೀತ್ ದಿಣ್ಣೆಯನ್ನು ಹತ್ತುತ್ತಾರೆ; ನಾಶವಾದೆವಲ್ಲಾ ಎಂಬ ಪ್ರಾಣಸಂಕಟ ಪ್ರಲಾಪವು ಹೊರೊನಯಿಮ್ ಇಳಿಜಾರಿನಲ್ಲಿ ಕೇಳಿಸುತ್ತದೆ.",
			"6": "<sup>6</sup> ಓಡಿಹೋಗಿರಿ, ನಿಮ್ಮ ಪ್ರಾಣಗಳನ್ನು ಉಳಿಸಿಕೊಳ್ಳಿರಿ, ಅಡವಿಯಲ್ಲಿನ ಜಾಲಿಯಂತಿರಿ.",
			"7": "<sup>7</sup> ನೀನು ನಿನ್ನ ಕೆಲಸಗಳಲ್ಲಿಯೂ ಧನರಾಶಿಗಳಲ್ಲಿಯೂ ಭರವಸವಿಟ್ಟದ್ದರಿಂದ ನೀನೂ ಶತ್ರುವಿನ ಕೈವಶವಾಗುವಿ; ಕೆಮೋಷ್ ದೇವತೆಯೂ ಅದರ ಭಕ್ತರಾದ ಯಾಜಕರೂ ಪ್ರಧಾನರೂ ಒಟ್ಟಾಗಿ ಸೆರೆಗೆ ಹೋಗುವರು.",
			"8": "<sup>8</sup> ಸೂರೆಗಾರನು ಪ್ರತಿಯೊಂದು ಪಟ್ಟಣದ ಮೇಲೆ ಬೀಳುವನು, ಯಾವ ಪಟ್ಟಣವೂ ಉಳಿಯದು; ಯೆಹೋವನು ನುಡಿದಂತೆಯೇ ತಗ್ಗಿನ ಭೂಮಿಯು ಹಾಳಾಗುವದು, ಮೇಲ್ನಾಡೂ ನಾಶವಾಗುವದು.",
			"9": "<sup>9</sup> ಮೋವಾಬಿಗೆ ರೆಕ್ಕೆ ಕಟ್ಟಿರಿ, ಅದು ಹಾರಿಹೋಗಬೇಕಲ್ಲಾ; ಅದರ ಪಟ್ಟಣಗಳು ಹಾಳುಬಿದ್ದು ನಿರ್ಜನವಾಗುವವು.",
			"10": "<sup>10</sup> ಯೆಹೋವನು ನೇಮಿಸಿದ ಕೆಲಸದಲ್ಲಿ ಆಲಸ್ಯಗಾರನು ಶಾಪಗ್ರಸ್ತನಾಗಲಿ; ತನ್ನ ಕತ್ತಿಯನ್ನು ರಕ್ತಸುರಿಸದಂತೆ ತಡೆಯುವವನಿಗೆ ಶಾಪತಗಲಲಿ.",
			"11": "<sup>11</sup> ಮೋವಾಬು ಚಿಕ್ಕತನದಿಂದಲೂ ನೆಮ್ಮದಿಯಾಗಿದೆ; ಅದು ಮಡ್ಡಿಯ ಮೇಲೆ ನಿಂತಿರುವ ದ್ರಾಕ್ಷಾರಸದಂತಿದೆ; ಅದನ್ನು ಪಾತ್ರೆಯಿಂದ ಪಾತ್ರೆಗೆ ಯಾರೂ ಹೊಯ್ಯಲಿಲ್ಲ, ಅದು ಸೆರೆಹೋಗಲಿಲ್ಲ; ಆದಕಾರಣ ಅದರ ರುಚಿಯು ಅದರಲ್ಲಿದೆ, ಅದರ ವಾಸನೆಯು ಬೇರ್ಪಡಲಿಲ್ಲ.",
			"12": "<sup>12</sup> ಹೀಗಿರಲು ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಇಗೋ, ಮುಂದಿನ ಕಾಲದಲ್ಲಿ ನಾನು ಅವರ ಬಳಿಗೆ ಹೊಯ್ಯತಕ್ಕವರನ್ನು ಕಳುಹಿಸುವೆನು, ಅವರು ಅದನ್ನು ಹೊಯ್ದುಬಿಡುವರು, ಪಾತ್ರೆಗಳನ್ನು ಬರಿದು ಮಾಡುವರು, ಗಡಿಗೆಗಳನ್ನು ಒಡೆದುಬಿಡುವರು.",
			"13": "<sup>13</sup> ಆಗ ಇಸ್ರಾಯೇಲ್ ವಂಶದವರು ತಮಗೆ ಭರವಸವಾದ ಬೇತೇಲಿನಿಂದ ಆಶಾಭಂಗಪಟ್ಟಂತೆ ಮೋವಾಬ್ಯರು ಕೆಮೋಷಿನಿಂದ ಆಶಾಭಂಗಪಡುವರು.",
			"14": "<sup>14</sup> ನಾವು ಶೂರರು, ಯುದ್ಧಪ್ರವೀಣರಾದ ಪರಾಕ್ರಮಶಾಲಿಗಳು ಎಂದು ನೀವು ಅಂದುಕೊಳ್ಳುವದು ಹೇಗೆ? ಮೋವಾಬು ಹಾಳಾಗಿದೆ;",
			"15": "<sup>15</sup> ಸುಟ್ಟುಹೋದ ಅದರ ಪಟ್ಟಣಗಳಿಂದ ಹೊಗೆ ಎದ್ದಿದೆ; ಅದರ ಯುವಕ ಶ್ರೇಷ್ಠರು ಹತರಾಗುವದಕ್ಕೆ ಇಳಿದುಹೋಗಿದ್ದಾರೆ. ಇದು ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೆಂಬ ನಾಮವುಳ್ಳ ರಾಜಾಧಿರಾಜನ ನುಡಿ.",
			"16": "<sup>16</sup> ಮೋವಾಬಿಗೆ ದುರ್ಗತಿ ಸಮೀಪಿಸಿದೆ, ವಿಪತ್ತು ಬೇಗ ಬರುವದು.",
			"17": "<sup>17</sup> ಮೋವಾಬಿನ ನೆರೆಹೊರೆಯವರೇ, ಅದರ ಹೆಸರುವಾಸಿಯನ್ನು ಬಲ್ಲವರೇ, ನೀವೆಲ್ಲರೂ ಅದಕ್ಕಾಗಿ ಎದೆಬಡುಕೊಳ್ಳಿರಿ; ಅಯ್ಯೋ, ಬಲವಾದ ಕೋಲು, ಮಹಿಮೆಯ ದಂಡವು ಮುರಿದುಹೋಯಿತಲ್ಲಾ ಎಂದು ಪ್ರಲಾಪಿಸಿರಿ.",
			"18": "<sup>18</sup> ಯುವತಿಯೇ, ದೀಬೋನ್‍ಪುರಿಯೇ, ನಿನ್ನ ಮಹಿಮೆಯ ಪದವಿಯಿಂದ ಕೆಳಕ್ಕಿಳಿದು ಬಾಯಾರಿದವಳಾಗಿ ಕೂತುಕೋ! ಮೋವಾಬನ್ನು ಹಾಳುಮಾಡುವವನು ನಿನಗೆ ವಿರುದ್ಧವಾಗಿ ಬಂದು ನಿನ್ನ ಕೋಟೆಕೊತ್ತಲಗಳನ್ನು ಕೆಡವಿದ್ದಾನಲ್ಲಾ.",
			"19": "<sup>19</sup> ಅರೋಯೇರಿನವರೇ, ದಾರಿಯ ಮಗ್ಗುಲಲ್ಲಿ ನಿಂತುಕೊಂಡು ನೋಡಿರಿ; ಓಡಿಹೋಗುವವನನ್ನೂ ತಪ್ಪಿಸಿಕೊಳ್ಳುವವಳನ್ನೂ ಏನಾಯಿತು ಎಂದು ವಿಚಾರಿಸಿರಿ.",
			"20": "<sup>20</sup> ಮೋವಾಬು ಭಂಗಪಟ್ಟು ಅವಮಾನಕ್ಕೆ ಈಡಾಗಿದೆ; ಗೋಳಾಡಿರಿ, ಅರಿಚಿರಿ; ಮೋವಾಬು ಹಾಳಾಯಿತೆಂದು ಅರ್ನೋನಿನ ತೀರದಲ್ಲಿ ತಿಳಿಸಿರಿ.",
			"21": "<sup>21</sup> ಮೇಲ್ನಾಡಿಗೆ ದಂಡನೆಯಾಗಿದೆ; ಹೋಲೋನ್, ಯಾಚಾ, ಮೆಫಾತ್, ದೀಬೋನ್,",
			"22": "<sup>22</sup> ನೆಬೋ, ಬೇತ್‍ದಿಬ್ಲಾತಯಿಮ್,",
			"23": "<sup>23</sup> ಕಿರ್ಯಾತಯೀಮ್, ಬೇತ್‍ಗಾಮೂಲ್, ಬೇತ್‍ಮೆಯೋನ್,",
			"24": "<sup>24</sup> ಕೆರೀಯೋತ್, ಬೊಚ್ರ, ಅಂತು ದೂರದ ಹತ್ತಿರದ ಮೋವಾಬಿನ ಎಲ್ಲಾ ಪಟ್ಟಣಗಳಿಗೂ ದಂಡನೆಯಾಗದೆ ಇಲ್ಲ.",
			"25": "<sup>25</sup> ಮೋವಾಬಿನ ಕೊಂಬು ಕಡಿದು ಬಿದ್ದಿದೆ, ಅದರ ತೋಳು ಮುರಿದುಹೋಗಿದೆ. ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"26": "<sup>26</sup> ಮೋವಾಬಿಗೆ ತಲೆಗೇರಕುಡಿಸಿರಿ, ಅದು ಯೆಹೋವನನ್ನು ತಿರಸ್ಕರಿಸಿ ಉಬ್ಬಿಕೊಂಡಿತಲ್ಲಾ; ಅದು ತನ್ನ ವಾಂತಿಯಲ್ಲಿ ದೊಪ್ಪನೆ ಬಿದ್ದು ಗೇಲಿಗೆ ಗುರಿಯಾಗುವದು.",
			"27": "<sup>27</sup> ಇಸ್ರಾಯೇಲು ನಿನಗೆ ಗೇಲಿಯಾಯಿತಷ್ಟೆ. ಅದು ಕಳ್ಳರ ಗುಂಪಿನಲ್ಲಿ ಸೇರಿದ್ದಾಗಿ ಹಿಡಿಯಲ್ಪಟ್ಟಿತೇನು? ನೀನು ಇಸ್ರಾಯೇಲಿನ ಪ್ರಸ್ತಾಪ ಎತ್ತುವಾಗೆಲ್ಲಾ ತಲೆಯಾಡಿಸುತ್ತೀಯಲ್ಲವೆ.",
			"28": "<sup>28</sup> ಮೋವಾಬ್ಯರೇ, ಪಟ್ಟಣಗಳನ್ನು ಬಿಟ್ಟು ಬಂಡೆಯ ಗುಹೆಗಳಲ್ಲಿ ವಾಸಿಸಿರಿ; ಹಳ್ಳಕೊಳ್ಳದ ಆಚೆಯ ಪಕ್ಕೆಯಲ್ಲಿ ಗೂಡನ್ನು ಕಟ್ಟಿಕೊಳ್ಳುವ ಪಾರಿವಾಳದಂತಿರಿ;",
			"29": "<sup>29</sup> ಮೋವಾಬ್ಯರಿಗೆ ಬಹು ಸೊಕ್ಕೇರಿದೆ ಎಂಬ ಅವರ ಹೆಮ್ಮೆಯ ಸುದ್ದಿಯೂ ಅವರ ಡಂಭ, ಅಹಂಕಾರ, ಸ್ವಾಭಿಮಾನ ಸ್ವಪ್ರತಿಷ್ಠೆ ಇವುಗಳ ಸಮಾಚಾರವೂ ನಮ್ಮ ಕಿವಿಗೆ ಬಿದ್ದಿವೆ.",
			"30": "<sup>30</sup> ಯೆಹೋವನು ಹೇಳುವದೇನಂದರೆ - ಅವರ ಗರ್ವೋದ್ರೇಕವು ನನಗೆ ಗೊತ್ತು. ಅದು ಬರೀ ಬುರುಡೆಯೇ; ಅವರು ಕೊಚ್ಚಿಕೊಳ್ಳುವದೆಲ್ಲಾ ನಿರರ್ಥಕ ಎಂಬದೇ.",
			"31": "<sup>31</sup> ಆದಕಾರಣ ನಾನು ಮೋವಾಬಿನ ನಿಮಿತ್ತ ಗೋಳಾಡುವೆನು; ಹೌದು, ಇಡೀ ದೇಶದ ದುರ್ಗತಿಯನ್ನು ನೋಡಿ ಪ್ರಲಾಪಿಸುವೆನು; ಕೀರ್ ಹೆರೆಸಿನವರ ವಿಷಯವಾಗಿ ನರಳಾಟವಾಗುವದು.",
			"32": "<sup>32</sup> ಸಿಬ್ಮದ ದ್ರಾಕ್ಷಾಲತೆಯೇ, ಯಜ್ಜೇರಿನ ನಿಮಿತ್ತ ಅಳುವದಕ್ಕಿಂತಲೂ ನಿನ್ನನ್ನು ಕಂಡು ಹೆಚ್ಚಾಗಿ ಅಳುವೆನು; ನಿನ್ನ ಶಾಖೆಗಳು ಸಮುದ್ರದಾಚೆಗೂ ಯಜ್ಜೇರಿನ ಸರೋವರದವರೆಗೂ ವ್ಯಾಪಿಸಿದ್ದವಲ್ಲಾ; ಈಗ ನಿನ್ನ ಹಣ್ಣುಹಂಪಲ ಮೇಲೆಯೂ ದ್ರಾಕ್ಷೆಯ ಸುಗ್ಗಿಯ ಮೇಲೆಯೂ ಸೂರೆಗಾರನು ಬಿದ್ದಿದ್ದಾನೆ.",
			"33": "<sup>33</sup> ಹರ್ಷಾನಂದಗಳು ತೋಟಗಳಿಂದಲೂ ಮೋವಾಬಿನ ಇಡೀ ದೇಶದಿಂದಲೂ ತೊಲಗಿವೆ; ತೊಟ್ಟಿಗಳಲ್ಲಿ ದ್ರಾಕ್ಷಾರಸವು ಇಲ್ಲದಂತೆ ಮಾಡಿದ್ದೇನೆ; ಯಾರೂ ತೊಟ್ಟಿಯಲ್ಲಿ ತುಳಿಯುತ್ತಾ ಹರ್ಷಧ್ವನಿಗೈಯರು; ಕೇಳಿಸುವ ಧ್ವನಿ ಹರ್ಷಧ್ವನಿಯಲ್ಲ.",
			"34": "<sup>34</sup> ಹೆಷ್ಬೋನಿನಿಂದ ಎಲೆಯಾಲೆಯ ಮತ್ತು ಯಹಚಿನವರೆಗೆ ಅರಿಚಿಕೊಳ್ಳುತ್ತಾರೆ. ಚೋಯರಿನಿಂದ ಹೊರೊನಯಿಮಿನ ಮತ್ತು ಎಗ್ಲತ್ ಶೆಲಿಶೀಯದವರೆಗೆ ಕಿರಿಚಿಕೊಳ್ಳುತ್ತಾರೆ; ನಿಮ್ರೀಮ್ ಹಳ್ಳವೂ ಹಾಳಾಗಿದೆ.",
			"35": "<sup>35</sup> ಮೋವಾಬಿನೊಳಗೆ ಪೂಜಾಸ್ಥಾನದಲ್ಲಿ ಬಲಿಯರ್ಪಿಸುವವರನ್ನೂ ತಮ್ಮ ದೇವರುಗಳಿಗೆ ಧೂಪಹಾಕುವವರನ್ನೂ ನಿರ್ಮೂಲ ಮಾಡುವೆನು. ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"36": "<sup>36</sup> ಆದದರಿಂದ ಮೋವಾಬಿನ ಮತ್ತು ಕೀರ್ ಹೆರೆಸಿನವರ ನಿಮಿತ್ತ ನನ್ನ ಹೃದಯವು ಕೊಳಲುಗಳಂತೆ ಮೊರೆಯಿಡುತ್ತದೆ; ಮೋವಾಬ್ಯರು ಕೂಡಿಸಿಟ್ಟ ಹೇರಳವಾದ ಆಸ್ತಿಯು ಮಾಯವಾಯಿತಲ್ಲಾ.",
			"37": "<sup>37</sup> ಎಲ್ಲರ ತಲೆಯು ಬೋಳು, ಎಲ್ಲರ ಗಡ್ಡವು ವಿಕಾರ, ಎಲ್ಲರ ಕೈಯಲ್ಲಿ ಗಾಯ, ಎಲ್ಲರ ನಡುವಿನ ಮೇಲೆ ಗೋಣಿತಟ್ಟು.",
			"38": "<sup>38</sup> ಮೋವಾಬಿನ ಎಲ್ಲಾ ಮಾಳಿಗೆಗಳ ಮೇಲೆಯೂ ಚೌಕಗಳಲ್ಲಿಯೂ ರೋದನವು ತುಂಬಿದೆ; ಮೋವಾಬನ್ನು ಯಾರಿಗೂ ಇಷ್ಟವಲ್ಲದ ಮಡಿಕೆಯಂತೆ ಒಡೆದು ಬಿಟ್ಟಿದ್ದೇನೆ ಎಂದು ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ.",
			"39": "<sup>39</sup> ಆಹಾ, ಭಂಗವಾಯಿತು! ಕಿರಿಚಿಕೊಳ್ಳುತ್ತಾರಲ್ಲಾ! ಓಹೋ, ಮೋವಾಬು ನಾಚಿಕೆಯಿಂದ ಬೆನ್ನು ತೋರಿಸಿದೆ! ಅದರ ನೆರೆಹೊರೆಯವರು ಅಣಕಿಸುವದಕ್ಕೂ ಬೆಚ್ಚಿಬೀಳುವದಕ್ಕೂ ಆಸ್ಪದವಾಗಿದೆ.",
			"40": "<sup>40</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಇಗೋ, ಮೋವಾಬಿನ ಮೇಲೆ ಎರಗಬೇಕೆಂದು ಶತ್ರುವು ರಣಹದ್ದಿನಂತೆ ರೆಕ್ಕೆಗಳನ್ನು ಹರಡಿ ಹಾರುವನು.",
			"41": "<sup>41</sup> ಪಟ್ಟಣಗಳನ್ನು ಹಿಡಿದಿದ್ದಾನೆ, ಕೋಟೆಗಳನ್ನು ಆಕ್ರಮಿಸಿದ್ದಾನೆ; ಆ ದಿನದಲ್ಲಿ ಮೋವಾಬಿನ ಶೂರರ ಎದೆಯು ಹೆರುವ ಹೆಂಗಸಿನ ಎದೆಯಂತೆ ಅದರುವದು.",
			"42": "<sup>42</sup> ಮೋವಾಬು ಯೆಹೋವನನ್ನು ತಿರಸ್ಕರಿಸಿ ಉಬ್ಬಿಕೊಂಡದರಿಂದ ಅದು ಹಾಳಾಗಿ ಇನ್ನು ಜನಾಂಗವೆನಿಸಿಕೊಳ್ಳದು.",
			"43": "<sup>43</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಮೋವಾಬಿನ ನಿವಾಸಿಯೇ, ಭಯವೂ, ಗುಂಡಿಯೂ, ಬಲೆಯೂ ನಿನಗೆ ಕಾದಿವೆ;",
			"44": "<sup>44</sup> ಭಯಕ್ಕೆ ಓಡುವವನು ಗುಂಡಿಯಲ್ಲಿ ಬೀಳುವನು, ಗುಂಡಿಯಿಂದ ಹತ್ತಿ ಬರುವವನು ಬಲೆಗೆ ಸಿಕ್ಕುವನು; ನಾನು ದಂಡನೆಯ ವರುಷವನ್ನು ಮೋವಾಬಿಗೆ ಬರಮಾಡುವೆನಷ್ಟೆ. ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"45": "<sup>45</sup> ಪಲಾಯನವಾದವರು ಬಲಗುಂದಿದವರಾಗಿ ಹೆಷ್ಬೋನಿನ ನೆರಳಿನಲ್ಲಿ ನಿಂತಿದ್ದಾರೆ; ಆದರೆ ಸೀಹೋನನ ರಾಜ್ಯಕ್ಕೆ ಸೇರಿದ ಹೆಷ್ಬೋನಿನಿಂದಲೂ ಅಗ್ನಿಜ್ವಾಲೆ ಹೊರಟು ಮೋವಾಬಿನ ಕೆನ್ನೆಯನ್ನೂ ಯುದ್ಧವೀರರ ನೆತ್ತಿಯನ್ನೂ ನುಂಗಿಬಿಟ್ಟಿದೆ.",
			"46": "<sup>46</sup> ಮೋವಾಬ್ಯರೇ, ನಿಮ್ಮ ಗತಿಯೇನೆಂದು ಹೇಳಲಿ! ಕೆಮೋಷಿನ ಭಕ್ತರು ನಾಶವಾದರು; ನಿನ್ನ ಗಂಡುಹೆಣ್ಣುಮಕ್ಕಳು ಗಡೀಪಾರಾಗಿ ಸೆರೆಯಾದರು.",
			"47": "<sup>47</sup> ಆದರೂ ಅಂತ್ಯದಲ್ಲಿ ನಾನು ಮೋವಾಬಿನ ದುರವಸ್ಥೆಯನ್ನು ತಪ್ಪಿಸುವೆನು ಎಂದು ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ. ಮೋವಾಬಿನ ವಿಷಯವಾದ ನ್ಯಾಯತೀರ್ಪು ಇದೇ."
		},
		"49": {
			"1": "<sup>1</sup> ಅಮ್ಮೋನ್ಯರನ್ನು ಕುರಿತದ್ದು. ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಇಸ್ರಾಯೇಲಿಗೆ ಮಕ್ಕಳಿಲ್ಲವೋ? ಬಾಧ್ಯನು ಇಲ್ಲವೋ? ಮಲ್ಕಾಮ್ ದೇವತೆಯು ಗಾದಿನ ಸೀಮೆಯನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡಿರುವದೇಕೆ, ಮಲ್ಕಾಮನ ಪ್ರಜೆಗಳು ಗಾದಿನ ಪಟ್ಟಣಗಳಲ್ಲಿ ಏಕೆ ವಾಸಿಸುತ್ತಾರೆ?",
			"2": "<sup>2</sup> ಆದದರಿಂದ ಇಗೋ, ಶತ್ರುಗಳು ನನ್ನ ಅಪ್ಪಣೆಯ ಮೇರೆಗೆ ರಬ್ಬಾ ಎಂಬ ಅಮ್ಮೋನ್ಯರ ಪಟ್ಟಣದ ಮೇಲೆ ಬಿದ್ದು ಯುದ್ಧಘೋಷಮಾಡುವ ದಿನಗಳು ಬರುವವು; ಆ ಪಟ್ಟಣವು ಹಾಳುದಿಬ್ಬವಾಗುವದು; ಅದಕ್ಕೆ ಸೇರಿದ ಗ್ರಾಮಗಳು ಬೆಂಕಿಯಿಂದ ಸುಟ್ಟುಹೋಗುವವು; ಆಗ ಇಸ್ರಾಯೇಲು ತನ್ನನ್ನು ವಶಮಾಡಿಕೊಂಡವರನ್ನು ತಾನು ವಶಮಾಡಿಕೊಳ್ಳುವದು. ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"3": "<sup>3</sup> ಹೆಷ್ಬೋನಿನವರೇ, ಗೋಳಾಡಿರಿ; ಆಯಿ ಎಂಬ ಊರು ಹಾಳಾಯಿತು! ರಬ್ಬಾ ಪಟ್ಟಣಕ್ಕೆ ಸೇರಿದ ಗ್ರಾಮಗಳವರೇ, ಕಿರಿಚಿರಿ, ಗೋಣಿತಟ್ಟನ್ನು ಸುತ್ತಿಕೊಳ್ಳಿರಿ, ಪ್ರಲಾಪಿಸಿರಿ, ಹಟ್ಟಿಗಳಲ್ಲಿ ಅತ್ತಿತ್ತ ಓಡಾಡಿರಿ; ಏಕಂದರೆ ಮಲ್ಕಾಮ್ ದೇವತೆಯೂ ಅದರ ಯಾಜಕರೂ ಪ್ರಧಾನರೂ ಒಟ್ಟಾಗಿ ಸೆರೆಗೆ ಹೋಗುವರು.",
			"4": "<sup>4</sup> ನನ್ನ ಮೇಲೆ ಯಾರು ತಾನೆ ಬಿದ್ದಾರು ಎಂದು ಸ್ವಂತ ಆಸ್ತಿಪಾಸ್ತಿಯಲ್ಲಿ ಭರವಸವಿಟ್ಟಿರುವ ಭ್ರಷ್ಟದೇಶವೇ, ನನ್ನ ತಗ್ಗುಗಳಲ್ಲಿ ತುಂಬಾ ನೀರು ಹರಿಯುತ್ತದೆಂದು ಏಕೆ ಕೊಚ್ಚಿಕೊಳ್ಳುತ್ತೀ?",
			"5": "<sup>5</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೆಂಬ ಕರ್ತನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಇಗೋ, ನಿನ್ನ ನೆರೆಹೊರೆಯವರೆಲ್ಲರೂ ನಿನಗೆ ಭಯಾಸ್ಪದರಾಗುವಂತೆ ಮಾಡುವೆನು, ನಿನ್ನವರಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬನು ನಿಂತಮುಖವಾಗಿಯೇ ಅಟ್ಟಲ್ಪಡುವನು; ಚದರಿದವರನ್ನು ಕೂಡಿಸುವದಕ್ಕೆ ಯಾರೂ ಇಲ್ಲವಾಗುವದು.",
			"6": "<sup>6</sup> ಆದರೂ ಕಾಲಾಂತರದಲ್ಲಿ ಅಮ್ಮೋನ್ಯರ ದುರವಸ್ಥೆಯನ್ನು ತಪ್ಪಿಸುವೆನು. ಇದು ಯೆಹೋವನ ನುಡಿ. ಎದೋಮ್ಯರನ್ನು ಕುರಿತದ್ದು.",
			"7": "<sup>7</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ತೇಮಾನ್ ಸೀಮೆಗಿದ್ದ ಬುದ್ಧಿಯು ಇನ್ನಿಲ್ಲವೋ? ವಿವೇಕಿಗಳ ಆಲೋಚನಾಶಕ್ತಿಯು ಅಳಿಯಿತೋ? ಅವರ ಜ್ಞಾನವು ಮಾಯವಾಯಿತೋ?",
			"8": "<sup>8</sup> ದೆದಾನಿನವರೇ, ಹಿಂದಿರುಗಿರಿ, ಓಡಿಹೋಗಿರಿ, ಒಳಪ್ರಾಂತ್ಯದಲ್ಲಿ ವಾಸಿಸಿರಿ; ಏಕಂದರೆ ನಾನು ಎದೋಮನ್ನು ದಂಡಿಸುವ ಕಾಲದಲ್ಲಿ ಏಸಾವನಿಗೆ ವಿಧಿಸಿದ ಆಪತ್ತನ್ನು ಅದರ ಮೇಲೆ ಬರಮಾಡುವೆನು.",
			"9": "<sup>9</sup> ದ್ರಾಕ್ಷೆಯ ಹಣ್ಣನ್ನು ಕೀಳುವವರು ನಿನ್ನ ಕಡೆಗೆ ಬಂದರೆ ಹಕ್ಕಲನ್ನೂ ಉಳಿಸುವದಿಲ್ಲ; ಕಳ್ಳರು ರಾತ್ರಿವೇಳೆಯಲ್ಲಿ ನುಗ್ಗಿದರೆ ತಮಗೆ ಸಾಕಾಗುವವರೆಗೂ ಹಾಳುಮಾಡುವರು.",
			"10": "<sup>10</sup> ನಾನೇ ಏಸಾವನ ಆಸರೆಯನ್ನು ಕಿತ್ತಿದ್ದೇನೆ, ಅವನ ಗುಪ್ತಸ್ಥಳಗಳನ್ನು ಬಟ್ಟಬೈಲುಮಾಡಿದ್ದೇನೆ, ಅವಿತುಕೊಳ್ಳಲಾರನು; ಅವನ ಸಂತಾನದವರೂ ಸಹೋದರರೂ ನೆರೆಹೊರೆಯವರೂ ಹಾಳಾದರು, ಅವನೂ ಇಲ್ಲವಾದನು.",
			"11": "<sup>11</sup> ನೀನು ನಿನ್ನ ಅನಾಥರನ್ನು ಬಿಡು, ನಾನೇ ಅವರನ್ನು ಉಳಿಸುವೆನು; ನಿನ್ನ ವಿಧವೆಯರು ನನ್ನಲ್ಲಿ ಭರವಸವಿಡಲಿ.",
			"12": "<sup>12</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಪಾತ್ರೆಯಲ್ಲಿ ಕುಡಿಯುವದು ಯಾರ ಪಾಲಿಗೆ ಬರಲಿಲ್ಲವೋ ಅವರೇ ಖಂಡಿತ ಕುಡಿಯಬೇಕಾಗಿರುವಲ್ಲಿ ನೀನು ಅದಕ್ಕೆ ತಪ್ಪಿಸಿಕೊಂಡೀಯಾ? ಆಗುವುದೇ ಇಲ್ಲ, ನೀನು ಕುಡಿಯಲೇ ಕುಡಿಯುವಿ.",
			"13": "<sup>13</sup> ಬೊಚ್ರವು ಹಾಳುಬಿದ್ದು ಬೆರಗಿಗೂ ನಿಂದೆಗೂ ಶಾಪಕ್ಕೂ ಗುರಿಯಾಗಿ ಅದಕ್ಕೆ ಸೇರಿದ ಊರುಗಳೆಲ್ಲಾ ನಿತ್ಯನಾಶನವನ್ನು ಹೊಂದುವವು ಎಂದು ನನ್ನ ಮೇಲೆ ಆಣೆಯಿಟ್ಟಿದ್ದೇನೆ. ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"14": "<sup>14</sup> ಯೆಹೋವನಿಂದ ಬಂದ ಸಮಾಚಾರವನ್ನು ಕೇಳಿದ್ದೇನೆ; ದೂತನ ಮೂಲಕ ಜನಾಂಗಗಳಿಗೆ ಹೀಗೆ ಹೇಳಿ ಕಳುಹಿಸಿದ್ದಾನೆ - ನೀವು ಕೂಡಿಕೊಂಡು ಯುದ್ಧಕ್ಕೆ ಹೊರಟು ಎದೋಮಿನ ಮೇಲೆ ಬೀಳಿರಿ.",
			"15": "<sup>15</sup> ಇಗೋ [ಎದೋಮೇ,] ನೀನು ಜನಾಂಗಗಳಲ್ಲಿ ಹೀನವಾಗಿ ಜನರ ತಾತ್ಸಾರಕ್ಕೆ ಈಡಾಗುವಂತೆ ಮಾಡಿದ್ದೇನೆ.",
			"16": "<sup>16</sup> ಆಹಾ, ನಿನ್ನ ಭೀಕರತ್ವವೆಲ್ಲಿ! ಪರ್ವತಾಗ್ರದಲ್ಲಿ ನೆಲೆಗೊಂಡು ಬಂಡೆಯ ಬಿರುಕುಗಳಲ್ಲಿ ವಾಸಿಸುವ ಜನವೇ, ನಿನ್ನೆದೆಯ ಹೆಮ್ಮೆಯು ನಿನ್ನನ್ನು ಮೋಸಗೊಳಿಸಿದೆ; ನೀನು ಹದ್ದಿನಂತೆ ನಿನ್ನ ಗೂಡನ್ನು ಉನ್ನತಸ್ಥಾನದಲ್ಲಿ ಕಟ್ಟಿಕೊಂಡರೂ ನಿನ್ನನ್ನು ಅಲ್ಲಿಂದ ಇಳಿಸಿಬಿಡುವೆನು. ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"17": "<sup>17</sup> ಎದೋಮು ಬೆರಗಿಗೆ ಆಸ್ಪದವಾಗುವದು; ಹಾದು ಹೋಗುವವರೆಲ್ಲರೂ ಅದಕ್ಕೆ ಸಂಭವಿಸಿದ ವಿಪತ್ತುಗಳಿಗೆ ಬೆರಗಾಗಿ ಸಿಳ್ಳುಹಾಕುವರು.",
			"18": "<sup>18</sup> ನಾನು ಕೆಡವಿದ ಸೊದೋಮ್ ಗೊಮೋರ ಪಟ್ಟಣಗಳಲ್ಲಿಯೂ ಸುತ್ತಣ ಊರುಗಳಲ್ಲಿಯೂ ಹೇಗೋ ಹಾಗೆಯೇ ಎದೋಮಿನಲ್ಲಿಯೂ ಯಾರೂ ವಾಸಿಸರು, ಯಾವ ನರಪ್ರಾಣಿಯೂ ಇಳುಕೊಳ್ಳನು.",
			"19": "<sup>19</sup> ಆಹಾ, ಒಬ್ಬನು ಸಿಂಹದೋಪಾದಿಯಲ್ಲಿ ಯೊರ್ದನಿನ ದಟ್ಟಡವಿಯಿಂದ [ಎದೋಮ್ಯರಿಗೆ] ನಿತ್ಯನೆಲೆಯಾದ ಗೋಮಾಳಕ್ಕೆ ಏರಿಬರುವನು; ಕ್ಷಣಮಾತ್ರದಲ್ಲಿ ನಾನು ಅವರನ್ನು ಅಲ್ಲಿಂದ ಓಡಿಸಿಬಿಡುವೆನು; ನಾನು ಆರಿಸಿಕೊಂಡವನನ್ನೇ ಅದನ್ನು ಕಾಯುವದಕ್ಕೆ ನೇಮಿಸುವೆನು; ನನ್ನ ಸಮಾನನು ಯಾವನು? ನನ್ನನ್ನು ನ್ಯಾಯವಿಚಾರಣೆಗೆ ಯಾವನು ಕರೆದಾನು? ಮಂದೆಯನ್ನು ಕಾಯುವ ಯಾವನು ನನ್ನೆದುರಿಗೆ ನಿಲ್ಲಬಲ್ಲನು?",
			"20": "<sup>20</sup> ಹೀಗಿರಲು ಯೆಹೋವನು ಎದೋಮಿನ ವಿಷಯವಾಗಿ ಮಾಡಿಕೊಂಡಿರುವ ಆಲೋಚನೆಯನ್ನೂ ಆತನು ತೇಮಾನ್ಯರನ್ನು ಕುರಿತು ಸಂಕಲ್ಪಿಸಿರುವ ಉದ್ದೇಶವನ್ನೂ ಆಲಿಸಿರಿ; [ಮೃಗಗಳು] ಖಂಡಿತವಾಗಿ ಹಿಂಡಿನ ಮರಿಗಳನ್ನು ಎಳೆದುಕೊಂಡು ಹೋಗುವವು; ನಿಶ್ಚಯವಾಗಿ ಅವುಗಳ ಹುಲ್ಗಾವಲು ಅವುಗಳ ನಾಶನಕ್ಕಾಗಿ ಬೆದರುವದು.",
			"21": "<sup>21</sup> ಎದೋಮ್ಯರು ಧಡಮ್ಮನೆ ಬೀಳಲು ಭೂಮಿಯು ಕಂಪಿಸುವದು; ಗೋಳು ಕೆಂಪುಸಮುದ್ರದವರೆಗೂ ಕೇಳಿಸುವದು!",
			"22": "<sup>22</sup> ಇಗೋ, ಬೊಚ್ರದ ಮೇಲೆ ಎರಗಬೇಕೆಂದು ಶತ್ರುವು ರಣಹದ್ದಿನಂತೆ ತನ್ನ ರೆಕ್ಕೆಗಳನ್ನು ಹರಡಿ ಹಾರಿ ಏರುವನು; ಆ ದಿನದಲ್ಲಿ ಎದೋಮಿನ ಶೂರರ ಎದೆಯು ಹೆರುವ ಹೆಂಗಸಿನ ಎದೆಯಂತೆ ಅದರುವದು.",
			"23": "<sup>23</sup> ದಮಸ್ಕವನ್ನು ಕುರಿತದ್ದು. ಹಮಾತಿಗೂ ಅರ್ಪಾದಿಗೂ ಆಶಾಭಂಗವಾಯಿತು; ಅವು ಅಶುಭಸಮಾಚಾರವನ್ನು ಕೇಳಿ ಕರಗಿಹೋಗಿವೆ; ಸಮುದ್ರಕ್ಕೆ ತಲ್ಲಣಹತ್ತಿದೆ, ಸುಮ್ಮನಿರಲಾರದು.",
			"24": "<sup>24</sup> ದಮಸ್ಕವು ಕುಂದಿದೆ, ಓಡಿಹೋಗಲು ಹಿಂದಿರುಗಿದೆ; ಅದಕ್ಕೆ ನಡುಕ ಹಿಡಿದಿದೆ, ಪ್ರಸವವೇದನೆಯಂತಿರುವ ಯಾತನಾವ್ಯಥೆಗಳಿಗೆ ಒಳಗಾಗಿದೆ.",
			"25": "<sup>25</sup> ಅಯ್ಯೋ, ಹೆಸರುವಾಸಿಯ ಪಟ್ಟಣವು, ನನ್ನ ಇಷ್ಟದ ಪುರವು, ಏಕೆ ವಲಸೆಹೋಗಲಿಲ್ಲ!",
			"26": "<sup>26</sup> ಅದರ ದುರ್ಗತಿಯಿಂದ ಆ ದಿನದಲ್ಲಿ ಅಲ್ಲಿನ ಯೌವನಸ್ಥರು ಚೌಕಗಳಲ್ಲಿ ಬಿದ್ದುಬಿಡುವರು, ಯುದ್ಧವೀರರೆಲ್ಲಾ ಸುಮ್ಮನಾಗುವರು.",
			"27": "<sup>27</sup> ಆಗ ನಾನು ದಮಸ್ಕದ ಕೋಟೆಯಲ್ಲಿ ಬೆಂಕಿಯನ್ನು ಹೊತ್ತಿಸುವೆನು, ಅದು ಬೆನ್‍ಹದದನ ಅರಮನೆಗಳನ್ನು ದಹಿಸಿಬಿಡುವದು ಎಂದು ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ.",
			"28": "<sup>28</sup> ಬಾಬೆಲಿನ ಅರಸನಾದ ನೆಬೂಕದ್ನೆಚ್ಚರನು ಹೊಡೆದ ಕೇದಾರನ್ನೂ ಹಾಚೋರಿನ ರಾಷ್ಟ್ರಗಳನ್ನೂ ಕುರಿತದ್ದು. ಏಳಿರಿ, ಕೇದಾರಿಗೆ ಹೋಗಿ ಮೂಡಣ ದೇಶದವರನ್ನು ಹಾಳುಮಾಡಿರಿ ಎಂದು ಯೆಹೋವನು ಶತ್ರುಗಳಿಗೆ ಅಪ್ಪಣೆಮಾಡಿದ್ದಾನೆ.",
			"29": "<sup>29</sup> ಶತ್ರುಗಳು ಅವರ ಗುಡಾರಗಳನ್ನೂ ಹಿಂಡುಗಳನ್ನೂ ಅಪಹರಿಸಿಕೊಳ್ಳುವರು, ಅವರ ಪರದೆಗಳನ್ನೂ, ಸಮಸ್ತ ಸಾಮಗ್ರಿಗಳನ್ನೂ, ಒಂಟೆಗಳನ್ನೂ ತೆಗೆದುಕೊಂಡು ಹೋಗುವರು, ಸುತ್ತುಮುತ್ತಲು ದಿಗಿಲು ಎಂಬದಾಗಿ ಅವರ ಕಡೆಗೆ ಕೂಗುವರು.",
			"30": "<sup>30</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಹಾಚೋರಿನವರೇ, ಓಡಿಹೋಗಿರಿ, ದೂರ ಅಲೆದಾಡಿರಿ, ಒಳಪ್ರಾಂತ್ಯದಲ್ಲಿ ವಾಸಿಸಿರಿ, ಏಕಂದರೆ ಬಾಬೆಲಿನ ಅರಸನಾದ ನೆಬೂಕದ್ನೆಚ್ಚರನು ನಿಮಗೆ ಕೇಡನ್ನು ಬಗೆದು ನಿಮ್ಮ ನಾಶನಕ್ಕೆ ಉಪಾಯವನ್ನು ಕಲ್ಪಿಸಿಕೊಂಡಿದ್ದಾನೆ.",
			"31": "<sup>31</sup> [ಬಾಬೆಲಿನವರೇ,] ಏಳಿರಿ. ಅಗುಳಿಯೂ ಬಾಗಿಲೂ ಬೇಕೆನ್ನದೆ ನೆಮ್ಮದಿಯಿಂದ ನಿರ್ಭಯವಾಗಿ ಪ್ರತ್ಯೇಕ ವಾಸಿಸುವ ಜನಾಂಗದ ಮೇಲೆ ಬೀಳಿರಿ!",
			"32": "<sup>32</sup> ಅವರ ಒಂಟೆಗಳು ಕೊಳ್ಳೆಯಾಗುವವು, ಅವರ ಲೆಕ್ಕವಿಲ್ಲದ ದನಕುರಿಗಳು ಸೂರೆಹೋಗುವವು; ಚಂಡಿಕೆಬಿಟ್ಟುಕೊಂಡಿರುವ ಜನರನ್ನು ಎಲ್ಲಾ ಕಡೆಯ ಗಾಳಿಗೂ ತೂರುವೆನು; ಎಲ್ಲಾ ಕಡೆಯಿಂದಲೂ ವಿಪತ್ತನ್ನು ಅವರ ಮೇಲೆ ಬರಮಾಡುವೆನು.",
			"33": "<sup>33</sup> ಆಗ ಹಾಚೋರು ಸದಾ ಹಾಳುಬಿದ್ದು ನರಿಗಳಿಗೆ ಹಕ್ಕೆಯಾಗುವದು; ಅಲ್ಲಿ ಯಾರೂ ವಾಸಿಸರು, ಯಾವ ನರಮನುಷ್ಯನೂ ಇಳುಕೊಳ್ಳನು. ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"34": "<sup>34</sup> ಯೆಹೂದದ ಅರಸನಾದ ಚಿದ್ಕೀಯನ ಆಳಿಕೆಯ ಪ್ರಾರಂಭದಲ್ಲಿ ಯೆಹೋವನು ಏಲಾಮಿನ ವಿಷಯವಾದ ಈ ವಾಕ್ಯವನ್ನು ಪ್ರವಾದಿಯಾದ ಯೆರೆಮೀಯನಿಗೆ ದಯಪಾಲಿಸಿದನು -",
			"35": "<sup>35</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಇಗೋ, ನಾನು ಏಲಾಮಿನ ಮುಖ್ಯ ಬಲವಾದ ಬಿಲ್ಲನ್ನು ಮುರಿದುಬಿಡುವೆನು.",
			"36": "<sup>36</sup> ನಾನು ನಾಲ್ಕು ದಿಕ್ಕುಗಳಿಂದಲೂ ನಾಲ್ಕು ಗಾಳಿಗಳನ್ನು ಏಲಾಮ್ಯರ ಮೇಲೆ ಬರಮಾಡಿ ಅವರನ್ನು ಆಯಾ ಗಾಳಿಗಳಿಗೆ ತೂರಿಬಿಡುವೆನು; ಏಲಾಮು ದೇಶಭ್ರಷ್ಟರು ಸೇರದೆ ಇರುವ ರಾಜ್ಯವೇ ಇರದು.",
			"37": "<sup>37</sup> ಏಲಾಮ್ಯರು ತಮ್ಮ ಪ್ರಾಣ ಹುಡುಕುವ ಶತ್ರುಗಳಿಂದ ಕಂಗೆಡುವಂತೆ ಮಾಡುವೆನು; ನನ್ನ ರೋಷಾಗ್ನಿಯ ವಿಪತ್ತನ್ನು ಅವರ ಮೇಲೆ ಬರಮಾಡುವೆನು; ಇದು ಯೆಹೋವನ ನುಡಿ; ಅವರು ನಿರ್ಮೂಲವಾಗುವ ತನಕ ಅವರ ಹಿಂದೆ ಖಡ್ಗವನ್ನು ಅಟ್ಟುವೆನು;",
			"38": "<sup>38</sup> ನಿನ್ನ ಸಿಂಹಾಸನವನ್ನು ಏಲಾಮಿನಲ್ಲಿ ಸ್ಥಾಪಿಸಿ ಆ ದೇಶದ ಅರಸನನ್ನೂ ಪ್ರಧಾನರನ್ನೂ ಅಳಿಸಿಬಿಡುವೆನು. ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"39": "<sup>39</sup> ಆದರೂ ಕಟ್ಟಕಡೆಯಲ್ಲಿ ಏಲಾಮಿನ ದುರವಸ್ಥೆಯನ್ನು ತಪ್ಪಿಸುವೆನು. ಇದು ಯೆಹೋವನ ನುಡಿ."
		},
		"50": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಪ್ರವಾದಿಯಾದ ಯೆರೆಮೀಯನಿಂದ ಕಸ್ದೀಯರ ದೇಶವಾದ ಬಾಬೆಲಿನ ವಿಷಯವಾಗಿ ನುಡಿಸಿದ ವಾಕ್ಯ.",
			"2": "<sup>2</sup> ಜನಾಂಗಗಳಲ್ಲಿ ಪ್ರಚುರಪಡಿಸಿರಿ, ಧ್ವಜವೆತ್ತಿ ಪ್ರಕಟಿಸಿರಿ, ಮರೆಮಾಜದೆ ಹೀಗೆ ಸಾರಿರಿ - ಬಾಬೆಲ್ ಶತ್ರುವಾಯಿತು, ಬೇಲ್ ದೇವತೆಯು ನಾಚಿಕೆಗೊಂಡಿದೆ, ಮೆರೋದಾಕ್ ದೇವತೆಯು ಬೆಚ್ಚಿಬಿದ್ದಿದೆ, ಅದರ ಮೂರ್ತಿಗಳು ಅವಮಾನಕ್ಕೆ ಗುರಿಯಾಗಿವೆ, ಅದರ ಬೊಂಬೆಗಳು ಚೂರುಚೂರಾಗಿವೆ.",
			"3": "<sup>3</sup> ಒಂದು ಜನಾಂಗವು ಬಡಗಲಿಂದ ಬಾಬೆಲಿನ ಮೇಲೆ ಬರುತ್ತಿದೆ, ಅದು ಬಾಬೆಲ್ ದೇಶವನ್ನು ಹಾಳುಮಾಡುವದು, ಅಲ್ಲಿ ಯಾರೂ ವಾಸಿಸರು, ಪಶುಗಳೂ ಜನರೂ ಓಡಿಹೋಗಿದ್ದಾರೆ, ತೊಲಗಿಬಿಟ್ಟಿದ್ದಾರೆ.",
			"4": "<sup>4</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಆ ದಿನಗಳಲ್ಲಿ, ಆ ಕಾಲದಲ್ಲಿ ಇಸ್ರಾಯೇಲ್ಯರೂ ಯೆಹೂದ್ಯರೂ ಒಟ್ಟಿಗೆ ಹಿಂದಿರುಗಿ ದಾರಿಯುದ್ದಕ್ಕೂ ಅಳುತ್ತಾ ಬಂದು ತಮ್ಮ ದೇವರಾದ ಯೆಹೋವನನ್ನು ಮರೆಹೊಗುವರು.",
			"5": "<sup>5</sup> ಚೀಯೋನಿಗೆ ಅಭಿಮುಖರಾಗಿ ಮಾರ್ಗವನ್ನು ವಿಚಾರಿಸಿ - ಬನ್ನಿರಿ, ಯೆಹೋವನನ್ನು ಆಶ್ರಯಿಸಿ ಎಂದಿಗೂ ಮರೆಯದ ಶಾಶ್ವತವಾದ ಒಡಂಬಡಿಕೆಯನ್ನು ಆತನೊಂದಿಗೆ ಮಾಡಿಕೊಳ್ಳೋಣ ಎಂದು ಹೇಳುವರು.",
			"6": "<sup>6</sup> ನನ್ನ ಜನರು ತಪ್ಪಿಸಿಕೊಂಡ ಕುರಿಗಳು; ಪಾಲಕರು ಅವರನ್ನು ದಾರಿತಪ್ಪಿಸಿ ಪರ್ವತಗಳಲ್ಲಿ ಅಲೆದಾಡಿಸಿದ್ದಾರೆ; ನನ್ನ ಜನರು ತಮ್ಮ ಹಕ್ಕೆಯನ್ನು ಮರೆತು ಬೆಟ್ಟಗುಡ್ಡಗಳಲ್ಲಿ ತಿರುಗಾಡುತ್ತಲಿದ್ದಾರೆ.",
			"7": "<sup>7</sup> ಕಂಡಕಂಡವರೆಲ್ಲರೂ ಅವರನ್ನು ನುಂಗಿಬಿಟ್ಟಿದ್ದಾರೆ; ಅವರ ವಿರೋಧಿಗಳು - ನಾವು ಅವರನ್ನು ನುಂಗಿದ್ದು ದೋಷವಲ್ಲ, ಅವರು ನ್ಯಾಯನಿವಾಸವೆನಿಸಿಕೊಂಡ ಯೆಹೋವನಿಗೆ, ಹೌದು, ತಮ್ಮ ಪಿತೃಗಳ ನಿರೀಕ್ಷೆಯಾದ ಯೆಹೋವನಿಗೆ ಪಾಪಮಾಡಿದರಲ್ಲಾ ಅಂದುಕೊಂಡರು.",
			"8": "<sup>8</sup> ಬಾಬೆಲ್ ದೇಶದೊಳಗಿಂದ ಓಡಿಹೋಗಿರಿ. ಕಸ್ದೀಯರ ಸೀಮೆಯಿಂದ ಹೊರಡಿರಿ; ಹಿಂಡುಗಳ ಮುಂದಣ ಹಿರಿಮಣಿ ಹೋತಗಳಂತಿರಿ.",
			"9": "<sup>9</sup> ಇಗೋ, ನಾನು ಮಹಾ ಜನಾಂಗಗಳ ಸಮೂಹವನ್ನು ಎಬ್ಬಿಸಿ ಬಡಗಲಿಂದ ಬಾಬೆಲಿನ ಮೇಲೆ ಬೀಳಮಾಡುವೆನು; ಅವು ಬಾಬೆಲಿಗೆ ವಿರುದ್ಧವಾಗಿ ವ್ಯೂಹ ಕಟ್ಟಿ ಅದನ್ನು ಸ್ಥಳದೊಳಗಿಂದ ಕಿತ್ತುಬಿಡುವವು; ಅವುಗಳ ಬಾಣಗಳು ಸುಮ್ಮನೆ ಹಿಂದಿರುಗದ ಯುದ್ಧಪ್ರವೀಣ ಶೂರನಂತಿರುವವು.",
			"10": "<sup>10</sup> ಕಸ್ದೀಯ ರಾಜ್ಯವು ಸೂರೆಯಾಗುವದು; ಸೂರೆಗಾರರೆಲ್ಲರೂ ತೃಪ್ತಿಗೊಳ್ಳುವರು. ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"11": "<sup>11</sup> ನನ್ನ ಸ್ವಾಸ್ತ್ಯವನ್ನು ಕೊಳ್ಳೆಹೊಡೆಯುವವರೇ, ನೀವು ಹರ್ಷಿಸಿ ಉಲ್ಲಾಸಿಸುವವರೂ ಕಣತುಳಿಯುವ ಕಡಸಿನ ಹಾಗೆ ಕುಣಿದಾಡುವವರೂ ಕೊಬ್ಬಿದ ಕುದುರೆಗಳಂತೆ ಹೇಕರಿಸುವವರೂ ಆಗಿರುವದರಿಂದ",
			"12": "<sup>12</sup> ನಿಮ್ಮ ಮಾತೃಭೂಮಿಯು ಮಾನಭಂಗಕ್ಕೆ ಈಡಾಗುವದು, ನಿಮ್ಮನ್ನು ಹೆತ್ತ ರಾಜ್ಯವು ನಾಚಿಕೊಳ್ಳುವದು; ಆಹಾ, ಅದು ಕಾಡು, ಕಗ್ಗಾಡು, ಬೆಗ್ಗಾಡು, ಜನಾಂಗಗಳಲ್ಲಿ ಕನಿಷ್ಠವೂ ಆಗುವದು.",
			"13": "<sup>13</sup> ಯೆಹೋವನ ರೋಷದ ದೆಸೆಯಿಂದ ಅದು ನಿರ್ಜನವಾಗಿ ತೀರಾ ಹಾಳುಬೀಳುವದು; ಬಾಬೆಲನ್ನು ಹಾದುಹೋಗುವವರೆಲ್ಲರೂ ಅದಕ್ಕೆ ಸಂಭವಿಸಿದ ವಿಪತ್ತುಗಳನ್ನು ಕಂಡು ಬೆರಗಾಗಿ ಸಿಳ್ಳುಹಾಕುವರು.",
			"14": "<sup>14</sup> ಬಿಲ್ಲುಗಾರರೇ, ನೀವೆಲ್ಲರೂ ವ್ಯೂಹಕಟ್ಟಿ ಬಾಬೆಲಿಗೆ ಮುತ್ತಿಗೆ ಹಾಕಿರಿ, ಬಾಣಗಳನ್ನು ಮಿಗಿಸದೆ ಎಸೆಯಿರಿ; ಅದು ಯೆಹೋವನಿಗೆ ಪಾಪಮಾಡಿತಲ್ಲಾ.",
			"15": "<sup>15</sup> ಅದರ ಸುತ್ತಲು ಜಯಘೋಷಮಾಡಿರಿ, ಅಧೀನವಾಯಿತು, ಅದರ ಕೊತ್ತಲಗಳು ಬಿದ್ದವು, ಅದರ ಪೌಳಿಗೋಡೆಯು ಕೆಡವಲ್ಪಟ್ಟಿತು; ಯೆಹೋವನು ಅದಕ್ಕೆ ಮುಯ್ಯಿತೀರಿಸಿದ್ದಾನೆ; ಮುಯ್ಯಿತೀರಿಸಿರಿ; ಅದು ಮಾಡಿದಂತೆಯೇ ಅದಕ್ಕೆ ಮಾಡಿರಿ.",
			"16": "<sup>16</sup> ಬೀಜಬಿತ್ತುವವನನ್ನೂ ಸುಗ್ಗಿಯಲ್ಲಿ ಕುಡುಗೋಲು ಹಿಡಿಯುವವನನ್ನೂ ಬಾಬೆಲಿನಿಂದ ನಿರ್ಮೂಲಮಾಡಿರಿ; ಪ್ರತಿಯೊಬ್ಬ ವಿದೇಶೀಯನು ಹಿಂಸೆಯ ಖಡ್ಗದ ದೆಸೆಯಿಂದ ಸ್ವಜನದ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡು ಸ್ವದೇಶಕ್ಕೆ ಓಡಿಹೋಗುವನು.",
			"17": "<sup>17</sup> ಇಸ್ರಾಯೇಲು ಚದರಿಹೋದ ಹಿಂಡು; ಸಿಂಹಗಳು ಅದನ್ನು ಓಡಿಸಿಬಿಟ್ಟಿವೆ; ಮೊಟ್ಟಮೊದಲು ಅಶ್ಶೂರದ ಅರಸನು ಅದನ್ನು ತಿಂದನು, ಕಟ್ಟಕಡೆಗೆ ಈಗ ಬಾಬೆಲಿನ ಅರಸನಾದ ನೆಬೂಕದ್ನೆಚ್ಚರನು ಅದರ ಎಲುಬುಗಳನ್ನು ಕಡಿದುಬಿಟ್ಟನು.",
			"18": "<sup>18</sup> ಹೀಗಿರಲು ಇಸ್ರಾಯೇಲಿನ ದೇವರೂ ಸೇನಾಧೀಶ್ವರನೂ ಆದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಆಹಾ, ನಾನು ಅಶ್ಶೂರದ ಅರಸನನ್ನು ದಂಡಿಸಿದಂತೆ ಬಾಬೆಲಿನ ಅರಸನನ್ನೂ ಅವನ ದೇಶವನ್ನೂ ದಂಡಿಸುವೆನು.",
			"19": "<sup>19</sup> ಇಸ್ರಾಯೇಲನ್ನು ಪುನಃ ಅದರ ಹುಲ್ಗಾವಲಿಗೆ ಸೇರಿಸುವೆನು; ಅದು ಕರ್ಮೆಲಿನಲ್ಲಿಯೂ ಬಾಷಾನಿನಲ್ಲಿಯೂ ಮೇಯುವದು; ಎಫ್ರಾಯೀಮಿನ ಮತ್ತು ಗಿಲ್ಯಾದಿನ ಬೆಟ್ಟಗಳಲ್ಲಿ ತೃಪ್ತಿಗೊಳ್ಳುವದು.",
			"20": "<sup>20</sup> ಆ ದಿನಗಳಲ್ಲಿ, ಆ ಕಾಲದಲ್ಲಿ ಇಸ್ರಾಯೇಲಿನ ಅಧರ್ಮವನ್ನು ಎಲ್ಲಿ ಹುಡುಕಿದರೂ ಇರುವದೇ ಇಲ್ಲ; ಯೆಹೂದದ ಪಾಪವನ್ನು ಎಲ್ಲಿ ತಡಕಿದರೂ ಸಿಕ್ಕುವದೇ ಇಲ್ಲ; ನಾನು ಉಳಿಸುವ ಜನಶೇಷವನ್ನು ಕ್ಷಮಿಸುವೆನಲ್ಲವೆ. ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"21": "<sup>21</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಮೆರಾಥಯಿಮ್ ದೇಶಕ್ಕೂ ಪೆಕೋದಿನ ನಿವಾಸಿಗಳಿಗೂ ವಿರುದ್ಧವಾಗಿ ದಂಡೆತ್ತಿ ಹೋಗಿ ಅವರನ್ನು ಸಂಹರಿಸಿ ಅನಂತರ ದೇಶವನ್ನು ತೀರಾ ಹಾಳುಮಾಡಿ ನಾನು ನಿನಗೆ ಆಜ್ಞಾಪಿಸಿದ್ದನ್ನೆಲ್ಲಾ ನೆರವೇರಿಸು.",
			"22": "<sup>22</sup> ಯುದ್ಧದ ಆರ್ಭಟವೂ ಮಹಾನಾಶನದ ಶಬ್ದವೂ ದೇಶದೊಳಗೆ ಕೇಳಿಸುತ್ತವೆ.",
			"23": "<sup>23</sup> ಆಹಾ, ಲೋಕವನ್ನೆಲ್ಲಾ ಹೊಡೆದ ಚಮಟಿಗೆಯು ಮುರಿದು ತುಂಡುತುಂಡಾಯಿತು! ಬಾಬೆಲ್‍ರಾಜ್ಯವು ಜನಾಂಗಗಳ ನಡುವೆ ಹಾಳಾಯಿತಲ್ಲಾ!",
			"24": "<sup>24</sup> ಬಾಬೆಲೇ, ನಾನು ನಿನಗೆ ಉರುಲೊಡ್ಡಿದೆನು; ನೀನು ತಿಳಿಯದೆ ಸಿಕ್ಕಿಕೊಂಡಿ; ನೀನು ಯೆಹೋವನೊಂದಿಗೆ ಹೋರಾಡಿದ್ದರಿಂದ ಬೋನಿಗೆ ಬಿದ್ದು ವಶವಾದಿ.",
			"25": "<sup>25</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೆಂಬ ಕರ್ತನು ಕಸ್ದೀಯರ ದೇಶದಲ್ಲಿ ನಿರ್ವಹಿಸಬೇಕಾದ ಕಾರ್ಯವೊಂದುಂಟು; ಅದಕ್ಕಾಗಿ ಯೆಹೋವನು ತನ್ನ ಆಯುಧ ಶಾಲೆಯನ್ನು ತೆರೆದು ತನ್ನ ರೋಷದ ಅಸ್ತ್ರಶಸ್ತ್ರಗಳನ್ನು ಹೊರಗೆ ತೆಗೆದಿದ್ದಾನೆ.",
			"26": "<sup>26</sup> ಲೋಕದ ಕಟ್ಟಕಡೆಯಿಂದ ಬಾಬೆಲಿಗೆ ಬನ್ನಿರಿ, ಅದರ ಕಣಜಗಳನ್ನು ತೆರೆಯಿರಿ; ಪಟ್ಟಣವನ್ನು ಹಾಳುದಿಬ್ಬಗಳಾಗಿ ಮಾಡಿ ಪೂರ್ಣವಾಗಿ ನಾಶಪಡಿಸಿರಿ; ಏನೂ ಉಳಿಯದಿರಲಿ.",
			"27": "<sup>27</sup> ಅದರ ಹೋರಿಗಳನ್ನೆಲ್ಲಾ ಕೊಲ್ಲಿರಿ, ಅವು ವಧ್ಯಸ್ಥಾನಕ್ಕೆ ಹೋಗಲಿ; ಅವುಗಳ ಗತಿಯನ್ನು ಏನೆಂದು ಹೇಳಲಿ! ಅವುಗಳಿಗೆ ವಿಪತ್ಕಾಲವು ಒದಗಿದೆ, ದಂಡನೆಯ ದಿನವು ಸಂಭವಿಸಿದೆ.",
			"28": "<sup>28</sup> ಓಹೋ, ಬಾಬೆಲಿನಿಂದ ತಪ್ಪಿಸಿಕೊಂಡು ಓಡಿಹೋಗುವವರ ಶಬ್ದ! ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ತನ್ನ ಆಲಯವನ್ನು ನಾಶಮಾಡಿದವರಿಗೆ ಮುಯ್ಯಿತೀರಿಸಿದ್ದಾನೆ ಎಂಬ ಸಮಾಚಾರವನ್ನು ಚೀಯೋನಿನಲ್ಲಿ ಪ್ರಕಟಿಸುವದಕ್ಕೆ ಹೋಗುತ್ತಾರಲ್ಲಾ.",
			"29": "<sup>29</sup> ಬಿಲ್ಲನ್ನು ಬೊಗ್ಗಿಸಿ ಬಾಣಬಿಡುವವರನ್ನೆಲ್ಲಾ ಬಾಬೆಲಿಗೆ ಕರೆಯಿರಿ; ಅದರ ಸುತ್ತಲು ದಂಡಿಳಿಸಿರಿ; ಅದರ ನಿವಾಸಿಗಳಲ್ಲಿ ಯಾರೂ ತಪ್ಪಿಸಿಕೊಳ್ಳದ ಹಾಗಿರಲಿ; ಅದರ ಕೃತ್ಯಕ್ಕೆ ತಕ್ಕಂತೆ ಮುಯ್ಯಿತೀರಿಸಿರಿ; ಅದು ಮಾಡಿದಂತೆಯೇ ಅದಕ್ಕೆ ಮಾಡಿರಿ; ಅದು ಸೊಕ್ಕೇರಿ ಇಸ್ರಾಯೇಲ್ಯರ ಸದಮಲಸ್ವಾಮಿಯಾದ ಯೆಹೋವನನ್ನು ಅಸಡ್ಡೆಮಾಡಿತಲ್ಲಾ.",
			"30": "<sup>30</sup> ಆದಕಾರಣ ಆ ದಿನದಲ್ಲಿ ಅಲ್ಲಿನ ಯೌವನಸ್ಥರು ಚೌಕಗಳಲ್ಲಿ ಬಿದ್ದುಬಿಡುವರು, ಯುದ್ಧವೀರರೆಲ್ಲಾ ಸುಮ್ಮನಾಗುವರು; ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"31": "<sup>31</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೆಂಬ ಕರ್ತನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಆಹಾ, ಸೊಕ್ಕಿನ ರಾಜ್ಯವೇ! ನಾನು ನಿನಗೆ ವಿರುದ್ಧನು; ನಿನಗೆ ಹೊತ್ತು ಬಂದಿದೆ, ನಿನ್ನನ್ನು ದಂಡಿಸತಕ್ಕ ಕಾಲ ಸಂಭವಿಸಿದೆ.",
			"32": "<sup>32</sup> ಸೊಕ್ಕಿನ ರಾಜ್ಯವು ಎಡವಿಬೀಳುವದು, ಅದನ್ನು ಯಾರೂ ಎತ್ತರು; ಅದರ ಪಟ್ಟಣಗಳಿಗೆ ಬೆಂಕಿಹೊತ್ತಿಸುವೆನು, ಅದು ಸುತ್ತುಮುತ್ತಣದನ್ನೆಲ್ಲಾ ನುಂಗಿಬಿಡುವದು.",
			"33": "<sup>33</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಇಸ್ರಾಯೇಲ್ಯರು ಯೆಹೂದ್ಯರು ಉಭಯರು ಹಿಂಸೆಗೆ ಗುರಿಯಾಗಿದ್ದಾರೆ; ಅವರನ್ನು ಸೆರೆ ಒಯ್ದವರೆಲ್ಲರೂ ಅವರನ್ನು ಬಿಡಿಸಲೊಲ್ಲದೆ ಬಿಗಿಹಿಡಿದಿದ್ದಾರೆ.",
			"34": "<sup>34</sup> ಅವರ ರಕ್ಷಕನು ಬಲಿಷ್ಠ. ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೆಂಬದೇ ಆತನ ನಾಮಧೇಯ; ಆತನು ಅವರ ವ್ಯಾಜ್ಯವನ್ನು ಜಯಿಸಿ ಲೋಕವನ್ನು ವಿಶ್ರಾಂತಿಗೊಳಿಸಿ ಬಾಬೆಲಿನವರನ್ನು ಕಳವಳಪಡಿಸುವನು.",
			"35": "<sup>35</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಖಡ್ಗವು ಕಸ್ದೀಯರ ಮೇಲೆ ಬೀಳಲಿ! ಬಾಬೆಲಿನ ಸಾಮಾನ್ಯಜನರು, ಪ್ರಧಾನರು, ಪಂಡಿತರು, ಇವರನ್ನೆಲ್ಲಾ ಹತಿಸಲಿ!",
			"36": "<sup>36</sup> ಖಡ್ಗವು ಕೊಚ್ಚಿಕೊಳ್ಳುವವರನ್ನು ಇರಿಯಲಿ! ಅವರ ಬುದ್ಧಿಹೀನತೆಯು ಬೈಲಿಗೆ ಬರುವದು. ಖಡ್ಗವು ಬಾಬೆಲಿನ ಶೂರರನ್ನು ಸಂಹರಿಸಲಿ!",
			"37": "<sup>37</sup> ಅವರು ಬೆಬ್ಬರಬೀಳುವರು. ಖಡ್ಗವು ಅವರ ಅಶ್ವಗಳನ್ನೂ ರಥಗಳನ್ನೂ ಬಾಬೆಲಿನಲ್ಲಿರುವ ಬಗೆಬಗೆಯ ವಿದೇಶೀಯರನ್ನೆಲ್ಲ ಹೊಡೆಯಲಿ! ಅವರು ಹೆಂಗಸರಂತೆ ಬೆದರುವರು, ಖಡ್ಗವು ಅವರ ಸಂಪತ್ತನ್ನು ನುಂಗಲಿ! ಅವರು ಸೂರೆಗೆ ಈಡಾಗುವರು.",
			"38": "<sup>38</sup> ಬೇಗೆಯು ಅವರ ನೀರನ್ನೆಲ್ಲಾ ಹೀರಲಿ! ಅದು ಬತ್ತಿಹೋಗುವದು. ಅದು ಬೊಂಬೆಗಳಿಂದ ತುಂಬಿದ ದೇಶ, ಅದರ ಜನರು ಅಕಟವಿಕಟ ವಿಗ್ರಹಗಳ ಪೂಜೆಯಿಂದ ಮದವೇರಿಸಿಕೊಳ್ಳುತ್ತಾರಷ್ಟೆ;",
			"39": "<sup>39</sup> ಆದಕಾರಣ ತೋಳ ಮುಂತಾದ ಕಾಡುಮೃಗಗಳು ಅಲ್ಲಿ ಹಕ್ಕೆಮಾಡಿಕೊಳ್ಳುವವು, ಉಷ್ಟ್ರಪಕ್ಷಿಗಳು ತಂಗುವವು; ಅದು ಎಂದಿಗೂ ನಿವಾಸಸ್ಥಳವಾಗದು, ತಲತಕಾಂತರಕ್ಕೂ ಅಲ್ಲಿ ಜನರು ಒಕ್ಕಲಿರರು.",
			"40": "<sup>40</sup> ದೇವರು ಕೆಡವಿದ ಸೊದೋಮ್ ಗೊಮೋರ ಪಟ್ಟಣಗಳಲ್ಲಿಯೂ ಸುತ್ತಣ ಊರುಗಳಲ್ಲಿಯೂ ಹೇಗೋ ಹಾಗೆಯೇ ಬಾಬೆಲಿನಲ್ಲಿಯೂ ಯಾರೂ ವಾಸಿಸರು, ಯಾವ ನರಪ್ರಾಣಿಯೂ ಇಳಕೊಳ್ಳನು.",
			"41": "<sup>41</sup> ಆಹಾ, ಬಡಗಲಿಂದ ಒಂದು ಜನಾಂಗವು ಬರುತ್ತದೆ, ಮಹಾ ಜನವೂ ಬಹುಮಂದಿ ಅರಸರೂ ಎಬ್ಬಿಸಲ್ಪಟ್ಟು ಲೋಕದ ಕಟ್ಟಕಡೆಯಿಂದ ಬರುತ್ತಾರೆ.",
			"42": "<sup>42</sup> ಬಿಲ್ಲನ್ನೂ ಈಟಿಯನ್ನೂ ಹಿಡಿದುಕೊಂಡಿದ್ದಾರೆ, ಅವರು ಕ್ರೂರರು, ನಿಷ್ಕರುಣಿಗಳು; ಅವರ ಧ್ವನಿಯು ಸಮುದ್ರದಂತೆ ಮೊರೆಯುತ್ತದೆ, ಕುದುರೆ ಹತ್ತಿದ್ದಾರೆ; ಬಾಬೆಲ್ ನಗರಿಯೇ, ಆ ಸೈನ್ಯವು ಶೂರನಂತೆ ನಿನ್ನ ಮೇಲೆ ಯುದ್ಧಸನ್ನದ್ಧವಾಗಿದೆ ಎಂದು ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ.",
			"43": "<sup>43</sup> ಬಾಬೆಲಿನ ಅರಸನು ಅದರ ಸುದ್ದಿಯನ್ನು ಕೇಳಿದನು, ಅವನ ಕೈಗಳು ಜೋಲುಬಿದ್ದವು, ಪ್ರಸವವೇದನೆಯಂತಿರುವ ಯಾತನೆಯು ಅವನನ್ನು ಹಿಡಿದಿದೆ.",
			"44": "<sup>44</sup> ಆಹಾ, ಒಬ್ಬನು ಸಿಂಹದೋಪಾದಿಯಲ್ಲಿ ಯೋರ್ದನಿನ ದಟ್ಟಡವಿಯಿಂದ [ಕಸ್ದೀಯರಿಗೆ] ನಿತ್ಯನೆಲೆಯಾದ ಗೋಮಾಳಕ್ಕೆ ಏರಿ ಬರುವನು; ಕ್ಷಣಮಾತ್ರದಲ್ಲಿ ನಾನು ಅವರನ್ನು ಅಲ್ಲಿಂದ ಓಡಿಸಿಬಿಡುವೆನು; ನಾನು ಆರಿಸಿಕೊಂಡವನನ್ನೇ ಅದನ್ನು ಕಾಯುವದಕ್ಕೆ ನೇಮಿಸುವೆನು; ನನ್ನ ಸಮಾನನು ಯಾವನು? ನನ್ನನ್ನು ನ್ಯಾಯವಿಚಾರಣೆಗೆ ಯಾವನು ಕರೆದಾನು? ಮಂದೆಯನ್ನು ಕಾಯುವ ಯಾವನು ನನ್ನೆದುರಿಗೆ ನಿಲ್ಲಬಲ್ಲನು?",
			"45": "<sup>45</sup> ಹೀಗಿರಲು ಯೆಹೋವನು ಬಾಬೆಲಿನ ವಿಷಯವಾಗಿ ಮಾಡಿಕೊಂಡಿರುವ ಆಲೋಚನೆಯನ್ನೂ ಆತನು ಕಸ್ದೀಯರ ದೇಶವನ್ನು ಕುರಿತು ಸಂಕಲ್ಪಿಸಿರುವ ಉದ್ದೇಶವನ್ನೂ ಆಲಿಸಿರಿ; [ಮೃಗಗಳು] ಹಿಂಡಿನ ಮರಿಗಳನ್ನು ಖಂಡಿತವಾಗಿ ಎಳೆದುಕೊಂಡು ಹೋಗುವವು; ನಿಶ್ಚಯವಾಗಿ ಅವುಗಳ ಹುಲ್ಗಾವಲು ಅವುಗಳ ನಾಶನಕ್ಕೆ ಬೆದರುವದು.",
			"46": "<sup>46</sup> ಬಾಬೆಲು ಶತ್ರುವಶವಾಯಿತೆಂಬ ಕೋಲಾಹಲಕ್ಕೆ ಭೂಮಿಯು ಕಂಪಿಸುತ್ತದೆ, ಅದರ ಮೊರೆಯು ಜನಾಂಗಗಳಲ್ಲಿ ಕೇಳಿಸುತ್ತದೆ."
		},
		"51": {
			"1": "<sup>1</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಇಗೋ, ನಾನು ಬಾಬೆಲಿನ ಮೇಲೂ ಲೇಬ್‍ಕಾಮೈ ದೇಶದವರ ಮೇಲೂ ನಾಶನದ ಗಾಳಿಯನ್ನು ಬೀಸಮಾಡುವೆನು.",
			"2": "<sup>2</sup> ಅನ್ಯಜನರನ್ನು ಬಾಬೆಲಿಗೆ ಕಳುಹಿಸುವೆನು, ಅವರು ಅದನ್ನು ತೂರುವರು, ಆ ದೇಶವನ್ನು ಬಟ್ಟಬರಿದು ಮಾಡುವರು; ನಿಯಮಿತ ವಿಪತ್ಕಾಲದಲ್ಲಿ ಅದನ್ನು ಮುತ್ತುವರಷ್ಟೆ.",
			"3": "<sup>3</sup> ಧನುರ್ಧಾರಿಗಳಿಗೂ ಕವಚ ಸನ್ನದ್ಧರಿಗೂ ಬಿಲ್ಲುಗಾರರು ಬಾಣವನ್ನೆಸೆಯಲಿ. ಬಾಬೆಲಿನ ಯುವಕರನ್ನು ಉಳಿಸದಿರಿ; ಅವರ ಸೈನ್ಯವನ್ನೆಲ್ಲಾ ನಿಶ್ಶೇಷಮಾಡಿರಿ.",
			"4": "<sup>4</sup> ಕಸ್ದೀಯರು ತಮ್ಮ ದೇಶದಲ್ಲಿ ಹತರಾಗಿ ಬೀಳುವರು, ಬಾಬೆಲಿನ ಬೀದಿಗಳಲ್ಲಿ ಇರಿಯಲ್ಪಟ್ಟು ಮಣ್ಣುಪಾಲಾಗುವರು.",
			"5": "<sup>5</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಸದಮಲಸ್ವಾಮಿಗೆ ಆತನ ಜನರು ಮಾಡಿದ ಅಪರಾಧವು ತಮ್ಮ ದೇಶದಲ್ಲಿ ತುಂಬಿದ್ದರೂ ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೆಂಬ ದೇವರು ಇಸ್ರಾಯೇಲನ್ನಾಗಲಿ ಯೆಹೂದವನ್ನಾಗಲಿ ತ್ಯಜಿಸಲಿಲ್ಲ.",
			"6": "<sup>6</sup> ನೀವು ಬಾಬೆಲಿನೊಳಗಿಂದ ಓಡಿಹೋಗಿ ನಿಮ್ಮ ನಿಮ್ಮ ಪ್ರಾಣಗಳನ್ನು ಉಳಿಸಿಕೊಳ್ಳಿರಿ; ಅದಕ್ಕೆ ಉಂಟಾಗುವ ದಂಡನೆಗೆ ಒಳಗಾಗಬೇಡಿರಿ; ಯೆಹೋವನು ಮುಯ್ಯಿತೀರಿಸುವ ಕಾಲ ಬಂದಿದೆ; ಆತನು ಬಾಬೆಲಿಗೆ ಪ್ರತೀಕಾರಮಾಡುವನು.",
			"7": "<sup>7</sup> ಬಾಬೆಲು ಯೆಹೋವನ ಕೈಯಲ್ಲಿನ ಹೊನ್ನಿನ ಪಾತ್ರೆ; ಲೋಕದವರೆಲ್ಲರೂ ಅದರಲ್ಲಿ ಕುಡಿದು ಮತ್ತರಾದರು; ಜನಾಂಗಗಳು ಅದರಲ್ಲಿನ ದ್ರಾಕ್ಷಾರಸವನ್ನು ಕುಡಿದು ಹುಚ್ಚಾದವು.",
			"8": "<sup>8</sup> ಬಾಬೆಲ್ ತಟ್ಟನೆ ಬಿದ್ದು ಹಾಳಾಯಿತು! ಅದಕ್ಕಾಗಿ ಅರಿಚಿರಿ; ಅದರ ನೋವನ್ನು ನೀಗಿಸುವದಕ್ಕೆ ಔಷಧವನ್ನು ಸಂಪಾದಿಸಿರಿ, ಗುಣವಾದೀತು.",
			"9": "<sup>9</sup> ಬಾಬೆಲನ್ನು ಸ್ವಸ್ಥಮಾಡುವದಕ್ಕೆ ನಾವು ಪ್ರಯತ್ನಪಟ್ಟರೂ ಅದು ಸ್ವಸ್ಥವಾಗಲಿಲ್ಲ; ಬಾಬೆಲನ್ನು ಬಿಟ್ಟು ನಾವೆಲ್ಲರೂ ನಮ್ಮ ನಮ್ಮ ದೇಶಗಳಿಗೆ ಹೋಗೋಣ; ಅದು ಹೊಂದಬೇಕಾದ ದಂಡನೆಯು ಆಕಾಶವನ್ನು ಮುಟ್ಟುವಷ್ಟು ದೊಡ್ಡದಾಗಿದೆ, ಹೌದು, ಗಗನದವರೆಗೂ ಬೆಳೆದಿದೆ.",
			"10": "<sup>10</sup> ಯೆಹೋವನು ನಮ್ಮ ನ್ಯಾಯವನ್ನು ವ್ಯಕ್ತಪಡಿಸಿದ್ದಾನೆ; ನಮ್ಮ ದೇವರಾದ ಯೆಹೋವನು ನಡಿಸಿದ ಕಾರ್ಯವನ್ನು ಚೀಯೋನಿನಲ್ಲಿ ಪ್ರಕಟಿಸೋಣ ಬನ್ನಿರಿ!",
			"11": "<sup>11</sup> ಯೆಹೋವನು ಬಾಬೆಲನ್ನು ಹಾಳುಮಾಡಬೇಕೆಂದು ಉದ್ದೇಶಿಸಿ ಮೇದ್ಯರ ಅರಸರು ಅದನ್ನು ನಾಶಮಾಡುವಂತೆ ಅವರ ಮನಸ್ಸನ್ನು ಪ್ರೇರಿಸಿದ್ದಾನೆ; ಆ ನಾಶನವು ಯೆಹೋವನು ತನ್ನ ಆಲಯವನ್ನು ಕೆಡವಿದವರಿಗೆ ಮಾಡಬೇಕೆಂದಿದ್ದ ಪ್ರತೀಕಾರವೇ; [ಮೇದ್ಯರೇ,] ಬಾಣಗಳನ್ನು ಮಸೆಯಿರಿ, ಸನ್ನದ್ಧರಾಗಿರಿ!",
			"12": "<sup>12</sup> ಬಾಬೆಲ್ ಕೋಟೆಗೆ ಎದುರಾಗಿ ಧ್ವಜವೆತ್ತಿರಿ, ಠಾಣ್ಯವನ್ನು ಬಲಪಡಿಸಿರಿ, ಕಾವಲುಗಾರರನ್ನು ನಿಲ್ಲಿಸಿರಿ, ಹೊಂಚುಗಾರರನ್ನು ಗೊತ್ತುಮಾಡಿರಿ; ಯೆಹೋವನು ಬಾಬೆಲಿನವರ ವಿಷಯದಲ್ಲಿ ನುಡಿದದ್ದನ್ನು ಚಿತ್ತಕ್ಕೆ ತಂದು ನೆರವೇರಿಸಿದ್ದಾನೆ.",
			"13": "<sup>13</sup> ಆಹಾ, ಬಹುಜಲಮಧ್ಯನಿವಾಸಿನಿಯಾದ ನಗರಿಯೇ, ಧನಭರಿತ ಪುರಿಯೇ, ನಿನ್ನ ಅಂತ್ಯವು ಬಂದಿದೆ, ನೀನು ಸೂರೆಮಾಡಿದ್ದು ಸಾಕು.",
			"14": "<sup>14</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ತನ್ನ ಮೇಲೆ ಆಣೆಯಿಟ್ಟು - ಖಂಡಿತವಾಗಿ ನಾನು ನಿನ್ನನ್ನು ಮಿಡಿತೆಗಳಷ್ಟು ಅಸಂಖ್ಯಜನರಿಂದ ತುಂಬಿಸುವೆನು, ಅವರು ನಿನ್ನ ಮೇಲೆ ಆರ್ಭಟಿಸುವರು.",
			"15": "<sup>15</sup> ಆತನು ತನ್ನ ಶಕ್ತಿಯಿಂದ ಭೂಮಿಯನ್ನು ನಿರ್ಮಿಸಿ ತನ್ನ ಜ್ಞಾನದಿಂದ ಲೋಕವನ್ನು ಸ್ಥಾಪಿಸಿ ತನ್ನ ವಿವೇಕದಿಂದ ಆಕಾಶಮಂಡಲವನ್ನು ಹರಡಿದ್ದಾನೆ;",
			"16": "<sup>16</sup> ಆತನ ಗರ್ಜನೆಗೆ ಆಕಾಶದಲ್ಲಿ ನೀರು ಮೊರೋ ಎನ್ನುತ್ತದೆ; ಆತನು ಭೂಮಿಯ ಕಟ್ಟಕಡೆಯಿಂದ ಮೋಡಗಳನ್ನು ಏರಮಾಡುತ್ತಾನೆ; ಮಳೆಗೋಸ್ಕರ ಮಿಂಚನ್ನು ಹೊಳೆಯಮಾಡುತ್ತಾನೆ; ತನ್ನ ಭಂಡಾರದಿಂದ ಗಾಳಿಯನ್ನು ಬೀಸಮಾಡುತ್ತಾನೆ.",
			"17": "<sup>17</sup> ಎಲ್ಲರೂ ತಿಳುವಳಿಕೆಯಿಲ್ಲದೆ ಪಶುಪ್ರಾಯರಾಗಿದ್ದಾರೆ; ತಾನು ಕೆತ್ತಿದ ವಿಗ್ರಹದ ನಿಮಿತ್ತ ಪ್ರತಿಯೊಬ್ಬ ಅಕ್ಕಸಾಲಿಗನೂ ಅವಮಾನಕ್ಕೆ ಗುರಿಯಾಗುವನು; ಅವನು ಎರಕಹೊಯ್ದ ಪುತ್ತಳಿಯು ಸುಳ್ಳು, ಅವುಗಳಲ್ಲಿ ಶ್ವಾಸವೇ ಇಲ್ಲ.",
			"18": "<sup>18</sup> ಅವು ವ್ಯರ್ಥ, ಹಾಸ್ಯಾಸ್ಪದವಾದ ಕೆಲಸ; ದಂಡನೆಯಾಗುವಾಗ ಅಳಿದುಹೋಗುವವು.",
			"19": "<sup>19</sup> ಯಾಕೋಬ್ಯರ ಸ್ವಾಸ್ತ್ಯವಾದಾತನು ಅವುಗಳ ಹಾಗಲ್ಲ; ಆತನು ಸಮಸ್ತವನ್ನೂ ನಿರ್ಮಿಸಿದವನು; ಇಸ್ರಾಯೇಲು ಆತನ ಸ್ವಾಸ್ತ್ಯವಾದ ವಂಶ; ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನೆಂಬದು ಆತನ ನಾಮಧೇಯ.",
			"20": "<sup>20</sup> [ಬಾಬೆಲೇ,] ನೀನು ನನಗೆ ಗದೆಯು, ಶಸ್ತ್ರವು, ನಾನು ನಿನ್ನಿಂದ ಜನಾಂಗಗಳನ್ನು ಒಡೆದುಬಿಡುತ್ತೇನೆ;",
			"21": "<sup>21</sup> ನಿನ್ನಿಂದ ರಾಜ್ಯಗಳನ್ನು ಅಳಿಸುತ್ತೇನೆ; ನಿನ್ನಿಂದ ಕುದುರೆಯನ್ನೂ ಸವಾರನನ್ನೂ ಒಡೆದುಬಿಡುತ್ತೇನೆ; ನಿನ್ನಿಂದ ರಥವನ್ನೂ ಸಾರಥಿಯನ್ನೂ ಒಡೆದುಬಿಡುತ್ತೇನೆ; ನಿನ್ನಿಂದ ಸ್ತ್ರೀಪುರುಷರನ್ನು ಒಡೆದುಬಿಡುತ್ತೇನೆ;",
			"22": "<sup>22</sup> ನಿನ್ನಿಂದ ಬಾಲವೃದ್ಧರನ್ನು ಒಡೆದುಬಿಡುತ್ತೇನೆ; ನಿನ್ನಿಂದ ಯುವತೀ ಯುವಕರನ್ನು ಒಡೆದುಬಿಡುತ್ತೇನೆ;",
			"23": "<sup>23</sup> ನಿನ್ನಿಂದ ಕುರುಬರನ್ನೂ ಹಿಂಡನ್ನೂ ಒಡೆದುಬಿಡುತ್ತೇನೆ; ನಿನ್ನಿಂದ ರೈತನನ್ನೂ ನೊಗದ ಎತ್ತುಗಳನ್ನೂ ಒಡೆದುಬಿಡುತ್ತೇನೆ; ನಿನ್ನಿಂದ ದೇಶಾಧಿಪತಿಗಳನ್ನೂ ನಾಡೊಡೆಯರನ್ನೂ ಒಡೆದುಬಿಡುತ್ತೇನೆ;",
			"24": "<sup>24</sup> ಆದರೆ ಬಾಬೆಲಿನವರೂ ಕಸ್ದೀಯರೂ ಚೀಯೋನಿನಲ್ಲಿ ಮಾಡಿದ ಎಲ್ಲಾ ಹಾನಿಗೆ ಪ್ರತಿಯಾಗಿ ನಾನು ಅವರೆಲ್ಲರಿಗೂ ಚೀಯೋನಿನವರ ಕಣ್ಣೆದುರಿಗೆ ಮುಯ್ಯಿತೀರಿಸುವೆನು ಎಂದು ಯೆಹೋವನು ಅನ್ನುತ್ತಾನೆ.",
			"25": "<sup>25</sup> ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಆಹಾ, ಲೋಕವನ್ನೆಲ್ಲಾ ಹಾಳುಮಾಡುವ ನಾಶಕ ಪರ್ವತವೇ, ನಾನು ನಿನಗೆ ವಿರುದ್ಧನಾಗಿದ್ದೇನೆ; ನಾನು ನಿನ್ನ ಮೇಲೆ ಕೈಮಾಡಿ ನಿನ್ನನ್ನು ಜರಿಯ ಮೇಲಿಂದ ಕೆಳಕ್ಕೆ ಉರುಳಿಸುವೆನು, ಸುಟ್ಟ ಬೆಟ್ಟವನ್ನಾಗಿ ಮಾಡುವೆನು.",
			"26": "<sup>26</sup> ಮೂಲೆಗಲ್ಲಿಗಾಗಲಿ ಅಸ್ತಿವಾರಕ್ಕಾಗಲಿ ಯಾರೂ ನಿನ್ನಿಂದ ಕಲ್ಲನ್ನು ತೆಗೆಯರು, ನೀನು ನಿತ್ಯನಾಶನಕ್ಕೆ ಗುರಿಯಾಗುವಿ. ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"27": "<sup>27</sup> ದೇಶದಲ್ಲಿ ಧ್ವಜವೆತ್ತಿರಿ, ರಾಜ್ಯಗಳಲ್ಲೆಲ್ಲಾ ಕೊಂಬೂದಿರಿ, ಜನಾಂಗಗಳನ್ನು ಸನ್ನಾಹಮಾಡಿರಿ, ಬಾಬೆಲಿನ ಮೇಲೆ ಬೀಳಲಿಕ್ಕೆ ಅರರಾಟ್, ಮಿನ್ನಿ, ಅಷ್ಕೆನಜ್ ಎಂಬ ರಾಷ್ಟ್ರಗಳನ್ನು ಕರೆದುಕೊಳ್ಳಿರಿ, ಸೋಲಿಸಲು ಸೇನಾಪತಿಯನ್ನು ನೇಮಿಸಿರಿ, ಅಶ್ವಬಲವನ್ನು ಬಿರುಸಾದ ಮಿಡಿತೆದಂಡಿನೋಪಾದಿಯಲ್ಲಿ ಬರಮಾಡಿರಿ.",
			"28": "<sup>28</sup> ಜನಾಂಗಗಳು, ಮೇದ್ಯರ ಅರಸರು, ಅಧಿಪತಿಗಳು, ಅಧಿಕಾರಿಗಳು, ಅವರ ಅಧೀನದಲ್ಲಿರುವ ಸಂಪೂರ್ಣ ದೇಶದವರು, ಇವರೆಲ್ಲರನ್ನೂ ಅದಕ್ಕೆ ವಿರುದ್ಧವಾಗಿ ಸನ್ನಾಹಮಾಡಿರಿ.",
			"29": "<sup>29</sup> ದೇಶವೆಲ್ಲಾ ನೊಂದು ನಡುಗುತ್ತದೆ; ಏಕಂದರೆ ಬಾಬೆಲ್ ದೇಶವು ಹಾಳುಬಿದ್ದು ನಿರ್ಜನವಾಗಲಿ ಎಂದು ಯೆಹೋವನು ಅದರ ವಿಷಯವಾಗಿ ಮಾಡಿಕೊಂಡಿರುವ ಸಂಕಲ್ಪಗಳು ಸ್ಥಿರವಾಗಿವೆ.",
			"30": "<sup>30</sup> ಬಾಬೆಲಿನ ಶೂರರು ಯುದ್ಧಕ್ಕೆ ಹಿಂದೆಗೆದು ಹೆಂಗಸರಂತೆ ಅಬಲರಾಗಿ ತಮ್ಮ ಕೋಟೆಗಳಲ್ಲಿ ನಿಂತಿದ್ದಾರೆ; ಅದರ ಮನೆಗಳಿಗೆ ಬೆಂಕಿಯಿಕ್ಕಿದೆ, ಅದರ ಅಗುಳಿಗಳು ಮುರಿದುಹೋಗಿವೆ;",
			"31": "<sup>31</sup> ಓಡಾಳುಗಳೂ ದೂತರೂ ಓಡಿ ಓಡಿ ಒಬ್ಬರಿಗೊಬ್ಬರು ಎದುರುಬದುರಾಗಿ ಅರಸನನ್ನು ಸೇರಿ ಅವನಿಗೆ - ರಾಜಧಾನಿಯನ್ನು ಎಲ್ಲಾ ಕಡೆಯಲ್ಲಿಯೂ ಆಕ್ರಮಿಸಿದ್ದಾರೆ,",
			"32": "<sup>32</sup> ಹಾಯ್ಗಡಗಳನ್ನು ಹಿಡಿದಿದ್ದಾರೆ, ಜೊಂಡುಹುಲ್ಲನ್ನು ಸುಟ್ಟುಬಿಟ್ಟಿದ್ದಾರೆ, ರಣವೀರರು ಬೆಚ್ಚಿಬಿದ್ದಿದ್ದಾರೆ ಎಂದು ತಿಳಿಸುವರು.",
			"33": "<sup>33</sup> ಇಸ್ರಾಯೇಲ್ಯರ ದೇವರೂ ಸೇನಾಧೀಶ್ವರನೂ ಆದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಬಾಬೆಲ್ ಪುರಿಯು ತುಳಿದು ತುಳಿದು ಸರಿಮಾಡುತ್ತಿರುವ ಕಣದಂತಿದೆ; ಸ್ವಲ್ಪ ಕಾಲವಾದ ಮೇಲೆ ಒಕ್ಕುವ ಸಮಯವು ಅದಕ್ಕೆ ಸಂಭವಿಸುವದು.",
			"34": "<sup>34</sup> [ಯೆಹೂದವು ಹೀಗೆ ಪ್ರಲಾಪಿಸುತ್ತದೆ] - ಬಾಬೆಲಿನ ಅರಸನಾದ ನೆಬೂಕದ್ನೆಚ್ಚರನು ನನ್ನನ್ನು ತಿಂದು ಹಾಕಿದ್ದಾನೆ, ಒಡೆದುಬಿಟ್ಟಿದ್ದಾನೆ, ಬರೀಪಾತ್ರೆಯನ್ನಾಗಿ ಕುಕ್ಕಿದ್ದಾನೆ, ಘಟಸರ್ಪದ ಹಾಗೆ ನನ್ನನ್ನು ನುಂಗಿದ್ದಾನೆ, ನನ್ನ ರುಚಿಪದಾರ್ಥಗಳಿಂದ ಹೊಟ್ಟೆತುಂಬಿಸಿಕೊಂಡಿದ್ದಾನೆ, ನನ್ನನ್ನು [ಗಂಗಾಳದಂತೆ] ತೊಳೆದು ಬಿಟ್ಟಿದ್ದಾನೆ.",
			"35": "<sup>35</sup> ನಮ್ಮ ಪ್ರಾಣವನ್ನು ಹಿಂಸಿಸಿದ ದೋಷ ಬಾಬೆಲಿಗೆ ತಟ್ಟಲಿ ಎಂದು ಚೀಯೋನಿನವರು ಅನ್ನುತ್ತಾರೆ; ನಮ್ಮ ರಕ್ತವನ್ನು ಸುರಿಸಿದ ಅಪರಾಧವು ಕಸ್ದೀಯರಿಗೆ ಬಡಿಯಲಿ ಎಂದು ಯೆರೂಸಲೇಮಿನವರು ಹೇಳುತ್ತಾರೆ.",
			"36": "<sup>36</sup> ಆದದರಿಂದ ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಆಹಾ, ನಾನು ನಿನ್ನ ಪಕ್ಷವಾಗಿ ವ್ಯಾಜ್ಯವಾಡಿ ನಿನ್ನನ್ನು ಹಿಂಸಿಸಿದ ರಾಜ್ಯಕ್ಕೆ ಮುಯ್ಯಿತೀರಿಸುವೆನು; ಅದರ ಸರೋವರವು ಬತ್ತುವಂತೆಯೂ ಅದರ ಪ್ರವಾಹವು ಒಣಗುವಂತೆಯೂ ಮಾಡುವೆನು.",
			"37": "<sup>37</sup> ಆಗ ಬಾಬೆಲು ಹಾಳುದಿಬ್ಬಗಳೂ ನರಿಗಳ ಹಕ್ಕೆಯೂ ಆಗಿರುವದು; ಅದು ನಿರ್ಜನವಾಗಿ ಬೆರಗಿನ ಸಿಳ್ಳಿಗೆ ಗುರಿಯಾಗುವದು.",
			"38": "<sup>38</sup> ಆ ದೇಶದವರು ಪ್ರಾಯದ ಸಿಂಹಗಳಂತೆ ಒಟ್ಟಿಗೆ ಗರ್ಜಿಸುತ್ತಿದ್ದಾರೆ, ಸಿಂಹದ ಮರಿಗಳ ಹಾಗೆ ಗುರುಗುಟ್ಟುತ್ತಿದ್ದಾರೆ.",
			"39": "<sup>39</sup> ಅವರು ಉರಿಗೊಂಡಿರುವಾಗ ನಾನು ಅವರಿಗೆ ಔತಣವನ್ನು ಸಿದ್ಧಪಡಿಸಿ ಅವರು ಅದರಿಂದ ಸಂಭ್ರಮಪಟ್ಟು ಎಂದಿಗೂ ಎಚ್ಚರಗೊಳ್ಳದೆ ದೀರ್ಘನಿದ್ರೆಮಾಡುವಂತೆ ತಲೆಗೇರ ಕುಡಿಸುವೆನು. ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"40": "<sup>40</sup> ನಾನು ಅವರನ್ನು ಕುರಿ ಟಗರು ಹೋತಗಳ ಹಾಗೆ ವಧ್ಯಸ್ಥಾನಕ್ಕೆ ಬರಮಾಡುವೆನು.",
			"41": "<sup>41</sup> ಆಹಾ, ಶೇಷಕ್ ಶತ್ರುವಶವಾಯಿತು, ಲೋಕಪ್ರಸಿದ್ಧವಾದ ಪಟ್ಟಣವು ಹಿಡಿಯಲ್ಪಟ್ಟಿದೆ,",
			"42": "<sup>42</sup> ಬಾಬೆಲು ಜನಾಂಗಗಳ ಬೆರಗಿಗೆ ಈಡಾಗಿದೆ! ಸಮುದ್ರವು ಬಾಬೆಲಿನ ಮೇಲೆ ನುಗ್ಗಿದೆ, ಲೆಕ್ಕವಿಲ್ಲದ ತೆರೆಗಳು ಆ ರಾಜ್ಯವನ್ನು ಮುಚ್ಚಿಬಿಟ್ಟಿವೆ.",
			"43": "<sup>43</sup> ಅದರ ಪಟ್ಟಣಗಳು ಕಾಡು, ಕಗ್ಗಾಡು, ಬೆಗ್ಗಾಡೂ ಆಗಿವೆ, ಆ ದೇಶದಲ್ಲಿ ಯಾರೂ ವಾಸಿಸರು, ಯಾವ ಮನುಷ್ಯನೂ ಹಾದುಹೋಗನು.",
			"44": "<sup>44</sup> ನಾನು ಬಾಬೆಲಿನಲ್ಲಿ ಬೇಲ್ ದೇವತೆಯನ್ನು ದಂಡಿಸಿ ಅದು ನುಂಗಿದ್ದನ್ನು ಅದರ ಬಾಯೊಳಗಿಂದ ಕಕ್ಕಿಸುವೆನು; ಪ್ರವಾಹ ಪ್ರವಾಹವಾಗಿ ಬಾಬೆಲಿಗೆ ಬರುತ್ತಿದ್ದ ಸಕಲದೇಶೀಯರು ಇನ್ನು ಮೇಲೆ ಬಾರರು; ಇದಲ್ಲದೆ ಬಾಬೆಲಿನ ಪೌಳಿಗೋಡೆಯು ಬಿದ್ದುಹೋಗುವದು.",
			"45": "<sup>45</sup> ನನ್ನ ಜನರೇ, ನೀವೆಲ್ಲರೂ ಬಾಬೆಲಿನೊಳಗಿಂದ ಹೊರಟು ಯೆಹೋವನ ರೋಷಾಗ್ನಿಯಿಂದ ತಪ್ಪಿಸಿಕೊಳ್ಳಿರಿ.",
			"46": "<sup>46</sup> ನಿಮ್ಮ ಎದೆಯು ಕುಂದದಿರಲಿ, ದೇಶದಲ್ಲಿ ಕಿವಿಗೆ ಬೀಳುವ ಸುದ್ದಿಯು ನಿಮ್ಮನ್ನು ಹೆದರಿಸದಿರಲಿ; ಒಂದು ವರುಷ ಒಂದು ಸುದ್ದಿಯು, ಮತ್ತೊಂದು ವರುಷ ಮತ್ತೊಂದು ಸುದ್ದಿಯು ಹರಡುತ್ತಿರುವವು; ಬಲಾತ್ಕಾರವು ದೇಶದಲ್ಲಿ ಪ್ರಬಲವಾಗುವದು, ಅಧಿಕಾರಿಯು ಅಧಿಕಾರಿಗೆ ವಿರೋಧಿಯಾಗುವನು.",
			"47": "<sup>47</sup> ಇದರಿಂದ ನಾನು ಬಾಬೆಲಿನ ಬೊಂಬೆಗಳನ್ನು ದಂಡಿಸುವ ದಿನಗಳು ಬರುತ್ತವೆ ಎಂದು ತಿಳಿದುಕೊಳ್ಳಿರಿ. ಆ ದೇಶವೆಲ್ಲಾ ನಾಚಿಕೆಪಡುವದು; ಅದರ ಪ್ರಜೆಗಳು ಅದರೊಳಗೆ ಹತರಾಗಿ ಬೀಳುವರು.",
			"48": "<sup>48</sup> ಆಗ ಭೂಮ್ಯಾಕಾಶಗಳೂ ಅಲ್ಲಿನ ಸಮಸ್ತವೂ ಬಾಬೆಲಿನ ನಾಶನವನ್ನು ನೋಡಿ ಹರ್ಷಧ್ವನಿಗೈಯುವವು; ಏಕಂದರೆ ಹಾಳುಮಾಡುವವರು ಬಡಗಲಿಂದ ಬಂದು ಅದರ ಮೇಲೆ ಬೀಳುವರು; ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"49": "<sup>49</sup> ಹತರಾದ ಇಸ್ರಾಯೇಲ್ಯರೇ, ಬಾಬೆಲಿನ ಹಿತಕ್ಕಾಗಿ ಲೋಕದಲ್ಲೆಲ್ಲಾ ಬಹುಜನರು ಹೇಗೆ ಹತರಾದರೋ ಹಾಗೆಯೇ ಬಾಬೆಲೂ ಹತವಾಗುವದು.",
			"50": "<sup>50</sup> ಖಡ್ಗಕ್ಕೆ ತಪ್ಪಿಸಿಕೊಂಡವರೇ, ಸುಮ್ಮನೆ ನಿಂತುಕೊಳ್ಳದೆ ನಡೆಯಿರಿ; ದೂರದಲ್ಲಿಯೂ ಯೆಹೋವನನ್ನು ಸ್ಮರಿಸಿರಿ, ಯೆರೂಸಲೇಮಿನ ಹಂಬಲು ನಿಮ್ಮ ಮನಸ್ಸಿನಲ್ಲಿ ಹುಟ್ಟಲಿ.",
			"51": "<sup>51</sup> ನಾವು ನಿಂದೆಯನ್ನು ಕೇಳಿ ನಾಚಿಕೆಗೊಂಡೆವು, ಯೆಹೋವನ ಆಲಯದ ಪವಿತ್ರಸ್ಥಾನಗಳನ್ನು ಮ್ಲೇಚ್ಫರು ಪ್ರವೇಶಿಸಿದ್ದರಿಂದ ಅವಮಾನವು ನಮ್ಮ ಮುಖವನ್ನು ಮುಚ್ಚಿಕೊಂಡಿದೆ [ಎಂಬದಾಗಿ ಅಂದುಕೊಳ್ಳುತ್ತೀರೋ?]",
			"52": "<sup>52</sup> ಹೀಗಿರಲು ಯೆಹೋವನು ಇಂತೆನ್ನುತ್ತಾನೆ - ಇಗೋ, ನಾನು ಬಾಬೆಲಿನ ಬೊಂಬೆಗಳನ್ನು ದಂಡಿಸುವ ದಿನಗಳು ಬರುತ್ತವೆ; ಆಗ ಆ ದೇಶದಲ್ಲೆಲ್ಲಾ ಗಾಯಪಟ್ಟವರು ನರಳಾಡುವರು.",
			"53": "<sup>53</sup> ಬಾಬೆಲು ಆಕಾಶದ ತನಕ ಬೆಳೆದು ಎತ್ತರವಾಗಿಯೂ ಬಲವಾಗಿಯೂ ಇರುವ ತನ್ನ ಕೋಟೆಕೊತ್ತಲಗಳನ್ನು ಭದ್ರಪಡಿಸಿದರೂ ಹಾಳುಮಾಡುವವರು ನನ್ನ ಅಪ್ಪಣೆಯಿಂದ ಅದರ ಮೇಲೆ ಬೀಳುವರು. ಇದು ಯೆಹೋವನ ನುಡಿ.",
			"54": "<sup>54</sup> ಆಹಾ, ಬಾಬೆಲಿನಿಂದ ಮೊರೆಯೂ ಕಸ್ದೀಯರ ದೇಶದಿಂದ ಮಹಾನಾಶನದ ಶಬ್ದವೂ ಕೇಳ ಬರುತ್ತವೆ!",
			"55": "<sup>55</sup> ಯೆಹೋವನು ಬಾಬೆಲನ್ನು ಹಾಳುಮಾಡುತ್ತಿದ್ದಾನಲ್ಲಾ, ಅದರ ಬಲು ಗದ್ದಲವನ್ನು ಅಣಗಿಸಿಬಿಡುತ್ತಾನೆ; ಮಹಾಜಲಪ್ರವಾಹಗಳು ಭೋರ್ಗರೆಯುವಂತೆ ತರಂಗತರಂಗವಾಗಿ ಬಂದ ಶತ್ರುಗಳು ಘೋಷಿಸುತ್ತಾರೆ, ಕೂಗಿ ಆರ್ಭಟಿಸುತ್ತಾರೆ;",
			"56": "<sup>56</sup> ಹಾಳುಮಾಡುವವನು ಬಾಬೆಲಿನ ಮೇಲೆ ಬಿದ್ದಿದ್ದಾನೆ, ಅದರ ಶೂರರು ಸೆರೆಯಾಗಿದ್ದಾರೆ, ಅದರ ಬಿಲ್ಲುಗಳು ಮುರಿದುಹೋಗಿವೆ; ಏಕಂದರೆ ಯೆಹೋವನು ಮುಯ್ಯಿತೀರಿಸುವ ದೇವರು, ಆತನು ಪ್ರತಿಕ್ರಿಯೆಮಾಡದೆ ಬಿಡನು.",
			"57": "<sup>57</sup> ನಾನು ಅದರ ಪ್ರಧಾನರು, ಮಂತ್ರಿಗಳು, ಅಧಿಪತಿಗಳು, ಅಧಿಕಾರಿಗಳು, ಬಲಿಷ್ಠರು, ಇವರೆಲ್ಲರಿಗೂ ತಲೆಗೇರ ಕುಡಿಸುವೆನು; ಅವರು ಎಂದಿಗೂ ಎಚ್ಚರಗೊಳ್ಳದೆ ದೀರ್ಘನಿದ್ರೆಮಾಡುವರು ಎಂದು ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನಾಮಧೇಯದ ರಾಜಾಧಿರಾಜನು ಅನ್ನುತ್ತಾನೆ.",
			"58": "<sup>58</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಯೆಹೋವನು ಹೀಗೆ ನುಡಿಯುತ್ತಾನೆ - ಬಾಬೆಲಿನ ಗಾತ್ರವಾದ ಪೌಳಿ ಗೋಡೆಯು ಸಂಪೂರ್ಣವಾಗಿ ನೆಲಸಮವಾಗುವದು, ಅದರ ಉನ್ನತದ್ವಾರಗಳು ಬೆಂಕಿಯಿಂದ ಸುಟ್ಟುಹೋಗುವವು; ಜನಾಂಗಗಳು ಪಟ್ಟ ಪರಿಶ್ರಮ ವ್ಯರ್ಥ, ಜನಗಳು ಆಯಾಸಗೊಂಡು ದುಡಿದದ್ದು ಬೆಂಕಿಗೆ ತುತ್ತಾಗುವದು.",
			"59": "<sup>59</sup> ಮಹ್ಸೇಯನ ಮೊಮ್ಮಗನೂ ನೇರೀಯನ ಮಗನೂ ಆದ ಸೆರಾಯನು ಯೆಹೂದದ ಅರಸನಾದ ಚಿದ್ಕೀಯನ ಆಳಿಕೆಯ ನಾಲ್ಕನೆಯ ವರುಷ ಚಿದ್ಕೀಯನೊಡನೆ ಬಾಬೆಲಿಗೆ ಪ್ರಯಾಣಮಾಡಿದಾಗ ಪ್ರವಾದಿಯಾದ ಯೆರೆಮೀಯನು ಅವನಿಗೆ ಕೊಟ್ಟ ಅಪ್ಪಣೆ. (ಸೆರಾಯನು ಯಾರಂದರೆ ಸರಬರಾಯಿ ಕಾರುಬಾರಿ.)",
			"60": "<sup>60</sup> ಯೆರೆಮೀಯನು ಬಾಬೆಲಿಗೆ ಸಂಭವಿಸತಕ್ಕ ಎಲ್ಲಾ ಕೇಡಿನ ವಿಷಯವನ್ನು (ಅಂದರೆ ಬಾಬೆಲಿನ ಸಂಬಂಧವಾಗಿ ಹಿಂದೆ ಲಿಖಿತವಾದ ಎಲ್ಲಾ ಸಂಗತಿಗಳನ್ನು) ಒಂದು ಗ್ರಂಥವನ್ನಾಗಿ ಬರೆದು",
			"61": "<sup>61</sup> ಸೆರಾಯನಿಗೆ ಹೀಗೆ ಹೇಳಿದನು - ನೋಡು, ನೀನು ಬಾಬೆಲಿಗೆ ಸೇರಿದ ಮೇಲೆ ಈ ಮಾತುಗಳನ್ನೆಲ್ಲಾ [ತಕ್ಕವರಿಗೆ] ಓದಿ ಹೇಳಿ -",
			"62": "<sup>62</sup> ಯೆಹೋವನೇ, ನೀನು ಈ ಸ್ಥಳವನ್ನು ನಿರ್ಮೂಲಮಾಡಬೇಕೆಂದು ಉದ್ದೇಶಿಸಿ ಇದು ಜನರಿಗೂ ಪಶುಗಳಿಗೂ ನೆಲೆಯಾಗದೆ ಸದಾ ಹಾಳಾಗಿಯೇ ಇರಲಿ ಎಂದು ನುಡಿದಿದ್ದೀಯಲ್ಲಾ ಎಂಬದಾಗಿ ಅರಿಕೆಮಾಡಬೇಕು.",
			"63": "<sup>63</sup> ನೀನು ಈ ಪುಸ್ತಕವನ್ನು ಓದಿ ಮುಗಿಸಿದ ಮೇಲೆ ಇದಕ್ಕೆ ಕಲ್ಲುಕಟ್ಟಿ ಯೂಫ್ರೇಟೀಸ್ ನದಿಯ ಮಧ್ಯದೊಳಗೆ ಹಾಕಿ",
			"64": "<sup>64</sup> ಯೆಹೋವನು ಬಾಬೆಲಿಗೆ ಬರಮಾಡುವ ವಿಪತ್ತಿನಿಂದ ಆ ಪಟ್ಟಣವೂ ಹೀಗೆಯೇ ಮುಣುಗುವದು, ಮತ್ತೆ ಏಳದು ಎಂದು ಹೇಳು. ಜನಗಳು ಆಯಾಸಗೊಂಡು ದುಡಿದದ್ದು [ಬೆಂಕಿಗೆ ತುತ್ತಾಗುವದು] ಎಂಬ ಮಾತಿನವರೆಗೆ ಯೆರೆಮೀಯನ ಪ್ರವಾದನೆ."
		},
		"52": {
			"1": "<sup>1</sup> ಚಿದ್ಕೀಯನು ಅರಸನಾದಾಗ ಇಪ್ಪತ್ತೊಂದು ವರುಷದವನಾಗಿದ್ದನು. ಇವನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಹನ್ನೊಂದು ವರುಷ ಆಳಿದನು. ಲಿಬ್ನದ ಯೆರೆಮೀಯನ ಮಗಳು ಆದ ಹಮೂಟಲ್ ಎಂಬಾಕೆಯು ಅವನ ತಾಯಿ.",
			"2": "<sup>2</sup> ಇವನು ಯೆಹೋಯಾಕೀಮನಂತೆ ಯೆಹೋವನ ಚಿತ್ತಕ್ಕೆ ವಿರುದ್ಧವಾಗಿ ನಡೆದನು.",
			"3": "<sup>3</sup> ಯೆಹೋವನು ಯೆರೂಸಲೇಮಿನವರ ಮೇಲೆಯೂ ಬೇರೆ ಎಲ್ಲಾ ಯೆಹೂದ್ಯರ ಮೇಲೆಯೂ ಇದನ್ನೆಲ್ಲಾ ಬರಮಾಡಿ ಕಡೆಯಲ್ಲಿ ಅವರನ್ನು ತನ್ನ ಸನ್ನಿಧಿಯಿಂದ ತಳ್ಳಿಬಿಡುವಷ್ಟು ರೋಷವುಳ್ಳವನಾದನು. ಚಿದ್ಕೀಯನು ಬಾಬೆಲಿನ ಅರಸನಾದ ನೆಬೂಕದ್ನೆಚ್ಚರನಿಗೆ ವಿರುದ್ಧವಾಗಿ ತಿರಿಗಿಬಿದ್ದದರಿಂದ",
			"4": "<sup>4</sup> ಅವನು ಚಿದ್ಕೀಯನ ಆಳಿಕೆಯ ಒಂಭತ್ತನೆಯ ವರುಷ ಹತ್ತನೆಯ ತಿಂಗಳಿನ ಹತ್ತನೆಯ ದಿನದಲ್ಲಿ ಸರ್ವಸೈನ್ಯಸಹಿತನಾಗಿ ಯೆರೂಸಲೇಮಿಗೆ ಬಂದು ಅಲ್ಲಿ ಪಾಳೆಯ ಮಾಡಿಕೊಂಡು ಅದರ ಸುತ್ತಲು ಮಣ್ಣಿನ ದಿಬ್ಬವನ್ನು ಮಾಡಿ",
			"5": "<sup>5</sup> ಚಿದ್ಕೀಯನ ಆಳಿಕೆಯ ಹನ್ನೊಂದನೆಯ ವರುಷದವರೆಗೆ ಅದಕ್ಕೆ ಮುತ್ತಿಗೆಹಾಕಿದನು.",
			"6": "<sup>6</sup> ಘೋರಕ್ಷಾಮದ ದೆಸೆಯಿಂದ ಪಟ್ಟಣದಲ್ಲಿರುವವರಿಗೆ ಆಹಾರ ಸಿಕ್ಕದೆಹೋಯಿತು. ಪಟ್ಟಣಕ್ಕೆ ಮುತ್ತಿಗೆ ಹಾಕಿದ್ದ ಕಸ್ದೀಯರು ಹನ್ನೊಂದನೆಯ ವರುಷದ ನಾಲ್ಕನೆಯ ತಿಂಗಳಿನ ಒಂಭತ್ತನೆಯ ದಿವಸ",
			"7": "<sup>7</sup> ಪೌಳಿಗೋಡೆಯಲ್ಲಿ ಒಂದು ದ್ವಾರವನ್ನು ಮಾಡಿದ್ದರಿಂದ ಒಳಗಿದ್ದ ಎಲ್ಲಾ ಸೈನಿಕರು ಅದೇ ರಾತ್ರಿ ಅರಸನ ತೋಟದ ಬಳಿಯಲ್ಲಿರುವ ಬಾಗಿಲಿನ ಮೂಲಕ ಪಟ್ಟಣದೊಳಗಿಂದ ಹೊರಟು ಓಡಿಹೋದರು; (ಆ ಬಾಗಿಲು ಎರಡು ಗೋಡೆಗಳ ಮಧ್ಯದಲ್ಲಿತ್ತು); ಅವರು ಅರಾಬಾ ಎಂಬ ತಗ್ಗಾದ ಪ್ರದೇಶದ ಮಾರ್ಗವಾಗಿ ಪಲಾಯನಮಾಡಿದರು.",
			"8": "<sup>8</sup> ಕಸ್ದೀಯರ ಸೈನ್ಯದವರು ಅರಸನಾದ ಚಿದ್ಕೀಯನನ್ನು ಹಿಂದಟ್ಟಿ ಯೆರಿಕೋವಿನ ಬೈಲಿನಲ್ಲಿ ಹಿಡಿದರು. ಅಷ್ಟರಲ್ಲಿ ಅವನ ಎಲ್ಲಾ ಸೈನಿಕರು ಅವನನ್ನು ಬಿಟ್ಟು ಚದರಿಹೋಗಿದ್ದರು.",
			"9": "<sup>9</sup> ಅನಂತರ ಕಸ್ದೀಯರು ಅವನನ್ನು ಹಮಾತ್ ಪ್ರದೇಶದ ರಿಬ್ಲದಲ್ಲಿದ್ದ ತಮ್ಮ ಅರಸನ ಬಳಿಗೆ ತೆಗೆದುಕೊಂಡು ಬರಲು ಆ ಅರಸನು ಅವನಿಗೆ ಶಿಕ್ಷೆಯನ್ನು ವಿಧಿಸಿ",
			"10": "<sup>10</sup> ಅವನ ಮಕ್ಕಳನ್ನು ಅವನ ಎದುರಿನಲ್ಲಿಯೇ ವಧಿಸಿ ಯೆಹೂದದ ಸಕಲ ಸರದಾರರನ್ನು ರಿಬ್ಲದಲ್ಲಿ ಕೊಲ್ಲಿಸಿದನು.",
			"11": "<sup>11</sup> ಇದಲ್ಲದೆ ಬಾಬೆಲಿನ ಅರಸನು ಚಿದ್ಕೀಯನ ಎರಡು ಕಣ್ಣುಗಳನ್ನೂ ಕಿತ್ತು ಅವನಿಗೆ ಬೇಡಿ ಹಾಕಿ ಬಾಬೆಲಿಗೆ ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ಅವನು ಜೀವದಿಂದಿರುವವರೆಗೆ ಸೆರೆಯಲ್ಲಿ ಇಟ್ಟನು.",
			"12": "<sup>12</sup> ಐದನೆಯ ತಿಂಗಳಿನ ಹತ್ತನೆಯ ದಿನದಲ್ಲಿ ಅಂದರೆ ಬಾಬೆಲಿನ ಅರಸನಾದ ನೆಬೂಕದ್ನೆಚ್ಚರನ ಆಳಿಕೆಯ ಹತ್ತೊಂಭತ್ತನೆಯ ವರುಷದಲ್ಲಿ ಬಾಬೆಲಿನ ಅರಸನ ಸೇವಕನೂ ಕಾವಲುದಂಡಿನ ಅಧಿಪತಿಯೂ ಆಗಿದ್ದ ನೆಬೂಜರದಾನನೆಂಬವನು ಯೆರೂಸಲೇಮಿಗೆ ಬಂದು",
			"13": "<sup>13</sup> ಯೆಹೋವನ ಆಲಯವನ್ನೂ ಅರಮನೆಯನ್ನೂ ಯೆರೂಸಲೇಮಿನ ಎಲ್ಲಾ ದೊಡ್ಡ ಮನೆಗಳನ್ನೂ ಸುಟ್ಟುಬಿಟ್ಟನು.",
			"14": "<sup>14</sup> ಅವನ ಜೊತೆಯಲ್ಲಿ ಬಂದಿದ್ದ ಕಸ್ದೀಯ ಸೈನ್ಯದವರು ಯೆರೂಸಲೇಮಿನ ಸುತ್ತು ಗೋಡೆಗಳನ್ನೆಲ್ಲಾ ಕೆಡವಿದರು.",
			"15": "<sup>15</sup> ಕಾವಲುದಂಡಿನ ಅಧಿಪತಿಯಾದ ನೆಬೂಜರದಾನನು ಕೇವಲ ದರಿದ್ರರಲ್ಲಿ ಅನೇಕರನ್ನೂ ಪಟ್ಟಣದಲ್ಲಿ ಉಳಿದವರನ್ನೂ ಮೊದಲೇ ಬಾಬೆಲಿನ ಅರಸನ ಮರೆಹೊಕ್ಕವರನ್ನೂ ಬೇರೆ ಎಲ್ಲಾ ಜನರನ್ನೂ ಸೆರೆಯೊಯ್ದನು.",
			"16": "<sup>16</sup> ಹೊಲಗಳನ್ನೂ ದ್ರಾಕ್ಷೆಯ ತೋಟಗಳನ್ನೂ ವ್ಯವಸಾಯ ಮಾಡುವದಕ್ಕಾಗಿ ದೇಶದ ಜನರಲ್ಲಿ ಕೇವಲ ದರಿದ್ರರನ್ನು ಮಾತ್ರ ಬಿಟ್ಟುಹೋದನು.",
			"17": "<sup>17</sup> ಕಸ್ದೀಯರು ಯೆಹೋವನ ಆಲಯದ ಬಳಿಯಲ್ಲಿದ್ದ ತಾಮ್ರದ ಕಂಬಗಳನ್ನೂ ಪೀಠಗಳನ್ನೂ ಸಮುದ್ರವೆನಿಸಿಕೊಂಡ ಪಾತ್ರೆಯನ್ನೂ ಒಡೆದು ಅವುಗಳ ಎಲ್ಲಾ ತಾಮ್ರವನ್ನೂ",
			"18": "<sup>18</sup> ಯೆಹೋವನ ಆಲಯದ ಸೇವೆಗಾಗಿ ಉಪಯೋಗಿಸುತ್ತಿದ್ದ ಬೋಗುಣಿ, ಸಲಿಕೆ, ಕತ್ತರಿ, ಬಟ್ಟಲು, ಧೂಪಾರತಿ ಮೊದಲಾದ ತಾಮ್ರದ ಸಾಮಾನುಗಳನ್ನೂ ಬಾಬೆಲಿಗೆ ತೆಗೆದುಕೊಂಡು ಹೋದರು.",
			"19": "<sup>19</sup> ಕಾವಲುದಂಡಿನ ಅಧಿಪತಿಯು ಬೆಳ್ಳಿಬಂಗಾರದ ಪಂಚಪಾತ್ರೆ, ಅಗ್ಗಿಷ್ಟಿಗೆ, ಬಟ್ಟಲು, ಬೋಗುಣಿ, ದೀಪಸ್ತಂಭ, ಧೂಪಾರತಿ, ತಾಂಬಾಣ ಮೊದಲಾದವುಗಳನ್ನು ತೆಗೆದುಕೊಂಡನು.",
			"20": "<sup>20</sup> ಅರಸನಾದ ಸೊಲೊಮೋನನು ಯೆಹೋವನ ಆಲಯಕ್ಕೋಸ್ಕರ ಮಾಡಿಸಿದ ಎರಡು ಕಂಬಗಳು, ಸಮುದ್ರವೆನಿಸಿಕೊಂಡ ಪಾತ್ರೆಯು, ಪೀಠಗಳನ್ನು ಹೊರುವ ಹನ್ನೆರಡು ತಾಮ್ರದ ಹೋರಿಗಳು, ಇವುಗಳಿಗೆ ಲೆಕ್ಕವಿಲ್ಲದಷ್ಟು ತಾಮ್ರ ಹಿಡಿದಿತ್ತು.",
			"21": "<sup>21</sup> ಕಂಬಗಳು ಎಂಥವುಗಳಂದರೆ ಮೊದಲನೆಯ ಕಂಬವು ಹದಿನೆಂಟು ಮೊಳ ಎತ್ತರವಿತ್ತು; ಅದರ ಸುತ್ತಳತೆಯು ಹನ್ನೆರಡು ಮೊಳ; ಅದು ಹೊಳ್ಳು, ನಾಲ್ಕು ಬೆರಳು ದಪ್ಪವಾಗಿತ್ತು.",
			"22": "<sup>22</sup> ಕಂಬದ ಮೇಲೆ ಐದು ಮೊಳ ಎತ್ತರವಾದ ಒಂದು ತಾಮ್ರದ ಕುಂಭವಿತ್ತು; ಕುಂಭದ ಮೇಲೆ ಸುತ್ತಲೂ ತಾಮ್ರದ ಜಾಲರಿಯೂ ದಾಳಿಂಬಹಣ್ಣುಗಳೂ ಇದ್ದವು. ಎರಡನೆಯ ಕಂಬವು ದಾಳಿಂಬಹಣ್ಣುಗಳಿಂದ ಅಲಂಕಾರವಾಗಿ ಹಾಗೆಯೇ ಇತ್ತು.",
			"23": "<sup>23</sup> ನಾಲ್ಕು ಪಕ್ಕಗಳಲ್ಲಿ ತೊಂಭತ್ತಾರು ದಾಳಿಂಬಹಣ್ಣುಗಳಿದ್ದವು; ಜಾಲರಿಯ ಮೇಲೆ ಸುತ್ತಲೂ ಒಟ್ಟಾಗಿ ನೂರು ದಾಳಿಂಬ ಹಣ್ಣುಗಳಿದ್ದವು.",
			"24": "<sup>24</sup> ಕಾವಲು ದಂಡಿನ ಅಧಿಪತಿಯಾದ ನೆಬೂಜರದಾನನು ಹಿಡಿದುಕೊಂಡುಹೋದ ಜನರಲ್ಲಿ ಮಹಾಯಾಜಕನಾದ ಸೆರಾಯನು, ಎರಡನೆಯ ತರಗತಿಯ ಯಾಜಕನಾದ ಚೆಫನ್ಯನು, ಮೂರು ಮಂದಿ ದ್ವಾರಪಾಲಕರು,",
			"25": "<sup>25</sup> ಸೈನ್ಯಸಂಬಂಧವಾದ ಎಲ್ಲಾ ವ್ಯವಸ್ಥೆಯನ್ನು ಮಾಡುವ ಕಂಚುಕಿ, ಪಟ್ಟಣದಲ್ಲಿ ಉಳಿದು ಅರಸನಿಗೆ ಆಪ್ತರಾಗಿದ್ದ ಏಳು ಮಂದಿ ಮಂತ್ರಿಗಳು, ಯುದ್ಧಕ್ಕೆ ಹೋಗತಕ್ಕವರ ಪಟ್ಟಿಯನ್ನು ಮಾಡುವ ಸೇನಾಪತಿಯ ಲೇಖಕನು, ಪಟ್ಟಣದಲ್ಲಿದ್ದ ಅರುವತ್ತು ಮಂದಿ ರೈತರು ಇದ್ದರು.",
			"26": "<sup>26</sup> ಅವನು ಇವರನ್ನೆಲ್ಲಾ ತೆಗೆದುಕೊಂಡುಹೋಗಿ ಹಮಾತ್ ಪ್ರದೇಶದ ರಿಬ್ಲದಲ್ಲಿದ್ದ ಬಾಬೆಲಿನ ಅರಸನಿಗೆ ಒಪ್ಪಿಸಲು",
			"27": "<sup>27</sup> ಅವನು ಇವರನ್ನು ಅಲ್ಲಿಯೇ ಕೊಲ್ಲಿಸಿದನು. ಹೀಗೆ ಯೆಹೂದ್ಯರು ಸೆರೆಯವರಾಗಿ ತಮ್ಮ ದೇಶವನ್ನು ಬಿಟ್ಟುಹೋಗಬೇಕಾಯಿತು.",
			"28": "<sup>28</sup> ನೆಬೂಕದ್ನೆಚ್ಚರನು ಸೆರೆಯೊಯ್ದವರ ಲೆಕ್ಕವು ಹೀಗಿದೆ - ಅವನು ತನ್ನ ಆಳಿಕೆಯ ಏಳನೆಯ ವರುಷದಲ್ಲಿ ಮೂರು ಸಾವಿರದ ಇಪ್ಪತ್ತು ಮೂರು ಮಂದಿ ಯೆಹೂದ್ಯರನ್ನು ಸೆರೆಯೊಯ್ದನು;",
			"29": "<sup>29</sup> ಹದಿನೆಂಟನೆಯ ವರುಷದಲ್ಲಿ ಎಂಟುನೂರು ಮೂವತ್ತೆರಡು ಜನರನ್ನು ಯೆರೂಸಲೇಮಿನಿಂದ ಸೆರೆಯೊಯ್ದನು;",
			"30": "<sup>30</sup> ನೆಬೂಕದ್ನೆಚ್ಚರನ ಆಳಿಕೆಯ ಇಪ್ಪತ್ತು ಮೂರನೆಯ ವರುಷದಲ್ಲಿ ಕಾವಲುದಂಡಿನ ಅಧಿಪತಿಯಾದ ನೆಬೂಜರದಾನನು ಏಳುನೂರು ನಾಲ್ವತ್ತೈದು ಮಂದಿ ಯೆಹೂದ್ಯರನ್ನು ಸೆರೆಯೊಯ್ದನು; ಸೆರೆಯಾದವರು ಒಟ್ಟಾಗಿ ನಾಲ್ಕು ಸಾವಿರದ ಆರುನೂರು ಜನ.",
			"31": "<sup>31</sup> ಎವೀಲ್ಮೆರೋದಕನು ತಾನು ಪಟ್ಟಕ್ಕೆ ಬಂದ ಮೊದಲನೆಯ ವರುಷದಲ್ಲಿ ಅಂದರೆ ಯೆಹೂದದ ಅರಸನಾದ ಯೆಹೋಯಾಖೀನನ ಸೆರೆಯ ಮೂವತ್ತೇಳನೆಯ ವರುಷದ ಹನ್ನೆರಡನೆಯ ತಿಂಗಳಿನ ಇಪ್ಪತ್ತೈದನೆಯ ದಿನದಲ್ಲಿ ಯೆಹೋಯಾಖೀನನನ್ನು ಸೆರೆಮನೆಯಿಂದ ಬಿಡಿಸಿ ಅವನನ್ನು ಮೇಲಕ್ಕೆ ಎತ್ತಿದನು.",
			"32": "<sup>32</sup> ಅವನೊಡನೆ ಪ್ರೀತಿಯಿಂದ ಮಾತಾಡಿ ತನ್ನ ಸಂಗಡ ಬಾಬೆಲಿನಲ್ಲಿದ್ದ ಎಲ್ಲಾ ಅರಸುಗಳಲ್ಲಿ ಅವನಿಗೆ ಉನ್ನತಸ್ಥಾನವನ್ನು ಕೊಟ್ಟನು.",
			"33": "<sup>33</sup> ಯೆಹೋಯಾಖೀನನು ಸೆರೆಯ ಬಟ್ಟೆಗಳನ್ನು ತೆಗೆದುಹಾಕಿ ಜೀವದಿಂದಿರುವವರೆಗೂ ಅರಸನ ಪಂಙ್ತಿಯಲ್ಲಿ ಊಟಮಾಡುತ್ತಿದ್ದನು.",
			"34": "<sup>34</sup> ಅವನ ಜೀವಿತಕಾಲವೆಲ್ಲ, ಸಾಯುವ ತನಕ ಅವನಿಗೆ ಬೇಕಾಗುವ ಎಲ್ಲಾ ಪದಾರ್ಥಗಳು ಪ್ರತಿನಿತ್ಯವೂ ಬಾಬೆಲಿನ ಅರಸನಿಂದಲೇ ದೊರಕುತ್ತಿದ್ದವು."
		}
	}
};
module.exports = book;