var book = {
	"name": "ಮಾರ್ಕ",
	"numChapters": 16,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ದೇವಕುಮಾರನಾದ ಯೇಸು ಕ್ರಿಸ್ತನ ವಿಷಯವಾದ ಸುವಾರ್ತೆಯ ಪ್ರಾರಂಭವು.",
			"2": "<sup>2</sup> ಇಗೋ, ನಾನು ನನ್ನ ದೂತನನ್ನು ನಿನ್ನ ಮುಂದೆ ಕಳುಹಿಸುತ್ತೇನೆ; ನೀನು ಹೋಗುವ ದಾರಿಯನ್ನು ಅವನು ಸರಿಮಾಡುವನೆಂತಲೂ",
			"3": "<sup>3</sup> ಕರ್ತನ ದಾರಿಯನ್ನು ಸಿದ್ಧಮಾಡಿರಿ; ಆತನ ಹಾದಿಗಳನ್ನು ನೆಟ್ಟಗೆಮಾಡಿರಿ ಎಂದು ಅಡವಿಯಲ್ಲಿ ಕೂಗುವವನ ಶಬ್ದವದೆ ಎಂತಲೂ",
			"4": "<sup>4</sup> ಪ್ರವಾದಿಯಾದ ಯೆಶಾಯನ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುವ ಪ್ರಕಾರ ಯೋಹಾನನು ಬಂದು ಜನರಿಗೆ - ನೀವು ಪಾಪಪರಿಹಾರಕ್ಕಾಗಿ ದೇವರ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡು ದೀಕ್ಷಾಸ್ನಾನ ಮಾಡಿಸಿಕೊಳ್ಳಬೇಕೆಂದು ಸಾರಿಹೇಳುತ್ತಾ ಅಡವಿಯಲ್ಲಿ ದೀಕ್ಷಾಸ್ನಾನಮಾಡಿಸುತ್ತಾ ಇದ್ದನು.",
			"5": "<sup>5</sup> ಆಗ ಯೂದಾಯ ಸೀಮೆಯೆಲ್ಲವೂ ಯೆರೂಸಲೇಮಿನವರೆಲ್ಲರೂ ಅವನ ಬಳಿಗೆ ಹೊರಟುಹೋಗಿ ತಮ್ಮತಮ್ಮ ಪಾಪಗಳನ್ನು ಹೇಳಿಕೊಂಡು ಯೊರ್ದನ್‍ಹೊಳೆಯಲ್ಲಿ ಅವನಿಂದ ದೀಕ್ಷಾಸ್ನಾನ ಮಾಡಿಸಿಕೊಳ್ಳುತ್ತಿದ್ದರು.",
			"6": "<sup>6</sup> ಈ ಯೋಹಾನನು ಒಂಟೆಯ ಕೂದಲಿನ ಹೊದಿಕೆಯನ್ನು ಹೊದ್ದುಕೊಂಡು ಸೊಂಟಕ್ಕೆ ತೊಗಲಿನ ನಡುಕಟ್ಟನ್ನು ಕಟ್ಟಿಕೊಂಡಿದ್ದನು; ಅವನು ಮಿಡಿತೆಯನ್ನೂ ಕಾಡುಜೇನನ್ನೂ ಆಹಾರ ಮಾಡುತ್ತಿದ್ದನು.",
			"7": "<sup>7</sup> ಅವನು - ನನಗಿಂತ ಶಕ್ತನು ನನ್ನ ಹಿಂದೆ ಬರುತ್ತಾನೆ; ಆತನ ಕೆರಗಳ ಬಾರನ್ನು ಬೊಗ್ಗಿಕೊಂಡು ಬಿಚ್ಚುವದಕ್ಕೂ ನಾನು ಯೋಗ್ಯನಲ್ಲ.",
			"8": "<sup>8</sup> ನಾನು ನಿಮಗೆ ನೀರಿನ ಸ್ನಾನ ಮಾಡಿಸಿದೆನು; ಆತನಾದರೋ ನಿಮಗೆ ಪವಿತ್ರಾತ್ಮದ ಸ್ನಾನಮಾಡಿಸುವನು ಎಂದು ಸಾರಿಹೇಳಿದನು.",
			"9": "<sup>9</sup> ಆ ದಿನಗಳಲ್ಲಿ ಯೇಸು ಗಲಿಲಾಯ ಸೀಮೆಗೆ ಸೇರಿದ ನಜರೇತೆಂಬ ಊರಿನಿಂದ ಬಂದು ಯೊರ್ದನ್ ಹೊಳೆಯಲ್ಲಿ ಯೋಹಾನನಿಂದ ಸ್ನಾನಮಾಡಿಸಿಕೊಂಡನು.",
			"10": "<sup>10</sup> ಮಾಡಿಸಿಕೊಂಡ ಕೂಡಲೆ ಆತನು ನೀರಿನೊಳಗಿಂದ ಮೇಲಕ್ಕೆ ಬರಲು ಆಕಾಶವೊಡೆದು ದೇವರಾತ್ಮವು ಪಾರಿವಾಳದ ಹಾಗೆ ತನ್ನ ಮೇಲೆ ಇಳಿಯುವದನ್ನು ಕಂಡನು.",
			"11": "<sup>11</sup> ಆಗ - ನೀನು ಪ್ರಿಯನಾಗಿರುವ ನನ್ನ ಮಗನು, ನಿನ್ನನ್ನು ನಾನು ಮೆಚ್ಚಿದ್ದೇನೆ ಎಂದು ಆಕಾಶವಾಣಿ ಆಯಿತು.",
			"12": "<sup>12</sup> ಕೂಡಲೆ ಯೇಸು ದೇವರಾತ್ಮಪ್ರೇರಿತನಾಗಿ ಅಡವಿಗೆ ಹೋದನು.",
			"13": "<sup>13</sup> ಆತನು ನಾಲ್ವತ್ತು ದಿವಸ ಅಡವಿಯಲ್ಲಿ ಮೃಗಗಳೊಂದಿಗೆ ಇದ್ದು ಸೈತಾನನಿಂದ ಶೋಧಿಸಲ್ಪಡುವವನಾದನು; ಮತ್ತು ದೇವದೂತರು ಆತನಿಗೆ ಉಪಚಾರಮಾಡಿದರು.",
			"14": "<sup>14</sup> ಯೋಹಾನನು ಸೆರೆಗೆ ಬಿದ್ದ ಮೇಲೆ ಯೇಸು ಗಲಿಲಾಯ ಸೀಮೆಗೆ ಬಂದು -",
			"15": "<sup>15</sup> ಕಾಲ ಪರಿಪೂರ್ಣವಾಯಿತು, ದೇವರ ರಾಜ್ಯವು ಸಮೀಪಿಸಿತು, ದೇವರ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡು ಸುವಾರ್ತೆಯನ್ನು ನಂಬಿರಿ ಎಂದು ದೇವರ ಸುವಾರ್ತೆಯನ್ನು ಸಾರಿಹೇಳಿದನು.",
			"16": "<sup>16</sup> ಹೀಗಿರುವಲ್ಲಿ ಆತನು ಗಲಿಲಾಯ ಸಮುದ್ರದ ಬಳಿಯಲ್ಲಿ ಹೋಗುತ್ತಿರುವಾಗ ಸೀಮೋನನೂ ಸೀಮೋನನ ತಮ್ಮನಾದ ಅಂದ್ರೆಯನೂ ಸಮುದ್ರದಲ್ಲಿ ಬಲೆಬೀಸುವದನ್ನು ಕಂಡನು. ಅವರು ಬೆಸ್ತರು.",
			"17": "<sup>17</sup> ಯೇಸು ಅವರಿಗೆ - ನನ್ನ ಹಿಂದೆ ಬನ್ನಿರಿ; ಮನುಷ್ಯರನ್ನು ಹಿಡಿಯುವ ಬೆಸ್ತರನ್ನಾಗಿ ನಿಮ್ಮನ್ನು ಮಾಡುವೆನು ಎಂದು ಹೇಳುತ್ತಲೇ",
			"18": "<sup>18</sup> ಅವರು ತಮ್ಮ ಬಲೆಗಳನ್ನು ಬಿಟ್ಟು ಆತನ ಹಿಂದೆ ಹೋದರು.",
			"19": "<sup>19</sup> ಇನ್ನೂ ಸ್ವಲ್ಪ ಮುಂದೆ ಹೋಗಿ ಜೆಬೆದಾಯನ ಮಗನಾದ ಯಾಕೋಬನನ್ನೂ ಅವನ ತಮ್ಮನಾದ ಯೋಹಾನನನ್ನೂ ಕಂಡನು.",
			"20": "<sup>20</sup> ಇವರು ದೋಣಿಯಲ್ಲಿ ತಮ್ಮ ಬಲೆಗಳನ್ನು ಸರಿಮಾಡುತ್ತಿದ್ದರು. ಕೂಡಲೆ ಇವರನ್ನೂ ಕರೆದನು. ಅವರು ತಮ್ಮ ತಂದೆಯಾದ ಜೆಬೆದಾಯನನ್ನು ಕೂಲಿಯಾಳುಗಳ ಸಂಗಡ ದೋಣಿಯಲ್ಲಿ ಬಿಟ್ಟು ಆತನ ಹಿಂದೆ ಹೋದರು.",
			"21": "<sup>21</sup> ಬಳಿಕ ಅವರು ಕಪೆರ್ನೌಮೆಂಬ ಊರಿಗೆ ಸೇರಿದರು. ಕೂಡಲೆ ಸಬ್ಬತ್‍ದಿನದಲ್ಲಿ ಯೇಸು ಸಭಾಮಂದಿರಕ್ಕೆ ಹೋಗಿ ಉಪದೇಶ ಮಾಡಿದನು.",
			"22": "<sup>22</sup> ಜನರು ಆತನ ಉಪದೇಶಕ್ಕೆ ಅತ್ಯಾಶ್ಚರ್ಯಪಟ್ಟರು; ಯಾಕಂದರೆ ಆತನು ಶಾಸ್ತ್ರಿಗಳಂತೆ ಉಪದೇಶಮಾಡದೆ ಅಧಿಕಾರವಿದ್ದವನಂತೆ ಅವರಿಗೆ ಉಪದೇಶ ಮಾಡುತ್ತಿದ್ದನು.",
			"23": "<sup>23</sup> ಆಗ ಅವರ ಸಭಾಮಂದಿರದಲ್ಲಿ ದೆವ್ವಹಿಡಿದ ಒಬ್ಬ ಮನುಷ್ಯನಿದ್ದನು.",
			"24": "<sup>24</sup> ಆ ದೆವ್ವವು - ನಜರೇತಿನ ಯೇಸುವೇ, ನಮ್ಮ ಗೊಡವೆ ನಿನಗೇಕೆ? ನಮ್ಮನ್ನು ನಾಶಮಾಡುವದಕ್ಕೆ ಬಂದೆಯಾ? ನಿನ್ನನ್ನು ಬಲ್ಲೆನು; ನೀನು ದೇವರು ಪ್ರತಿಷ್ಠಿಸಿದವನೇ ಎಂದು ಕೂಗಿ ಹೇಳಿತು.",
			"25": "<sup>25</sup> ಯೇಸು ಅದನ್ನು ಗದರಿಸಿ - ಸುಮ್ಮನಿರು, ಇವನನ್ನು ಬಿಟ್ಟು ಹೋಗು ಎನ್ನಲಾಗಿ",
			"26": "<sup>26</sup> ಆ ದೆವ್ವವು ಅವನನ್ನು ಒದ್ದಾಡಿಸಿ ಅಬ್ಬರಿಸಿಕೂಗಿ ಬಿಟ್ಟು ಹೋಯಿತು.",
			"27": "<sup>27</sup> ಅದಕ್ಕೆ ಎಲ್ಲರು ಬೆರಗಾಗಿ - ಇದೇನಿರಬಹುದು? ಇದು ಹೊಸ ಉಪದೇಶ. ಈತನು ಅಧಿಕಾರದಿಂದ ದೆವ್ವಗಳಿಗೂ ಅಪ್ಪಣೆಕೊಡುತ್ತಾನೆ; ಅವು ಆತನ ಮಾತನ್ನು ಕೇಳುತ್ತವೆ ಎಂದು ತಮ್ಮ ತಮ್ಮೊಳಗೆ ವಿಚಾರಮಾಡಿಕೊಂಡರು.",
			"28": "<sup>28</sup> ಕೂಡಲೆ ಆತನ ಸುದ್ದಿಯು ಗಲಿಲಾಯ ಸೀಮೆಯಲ್ಲಿ ಎಲ್ಲೆಲ್ಲಿಯೂ ಹಬ್ಬಿತು.",
			"29": "<sup>29</sup> ಆಮೇಲೆ ಅವರು ಸಭಾಮಂದಿರದಿಂದ ಹೊರಟು ಯಾಕೋಬ ಯೋಹಾನರ ಸಂಗಡ ಸೀಮೋನ ಅಂದ್ರೆಯರ ಮನೆಗೆ ಬಂದರು.",
			"30": "<sup>30</sup> ಅಲ್ಲಿ ಸೀಮೋನನ ಅತ್ತೆ ಜ್ವರ ಬಂದು ಮಲಗಿರಲಾಗಿ ಅವರು ಆಕೆಯ ಸಂಗತಿಯನ್ನು ಆತನಿಗೆ ತಿಳಿಸಿದರು.",
			"31": "<sup>31</sup> ಆತನು ಹತ್ತಿರಕ್ಕೆ ಬಂದು ಆಕೆಯ ಕೈಯನ್ನು ಹಿಡಿದು ಎಬ್ಬಿಸಲು ಜ್ವರವು ಆಕೆಯನ್ನು ಬಿಟ್ಟು ಹೋಯಿತು; ಆಕೆಯು ಅವರಿಗೆ ಉಪಚಾರಮಾಡಿದಳು.",
			"32": "<sup>32</sup> ಸಂಜೆಯಾಗಿ ಹೊತ್ತು ಮುಣುಗಿದ ಮೇಲೆ ಜನರು ಮೈಯಲ್ಲಿ ನೆಟ್ಟಗಿಲ್ಲದವರೆಲ್ಲರನ್ನೂ ದೆವ್ವಹಿಡಿದವರನ್ನೂ ಆತನ ಬಳಿಗೆ ಕರತಂದರು.",
			"33": "<sup>33</sup> ಊರೆಲ್ಲಾ ಬಾಗಲಿನ ಮುಂದೆ ಒಟ್ಟುಗೂಡಿತ್ತು.",
			"34": "<sup>34</sup> ಆಗ ಆತನು ತರತರವಾದ ರೋಗಗಳಿಂದ ಮೈಯಲ್ಲಿ ನೆಟ್ಟಗಿಲ್ಲದ ಬಹು ಜನರನ್ನು ವಾಸಿಮಾಡಿ ಅನೇಕ ದೆವ್ವಗಳನ್ನು ಬಿಡಿಸಿದನು. ಮತ್ತು ತಾನು ಇಂಥವನೆಂದು ಅವುಗಳಿಗೆ ತಿಳಿದದ್ದರಿಂದ ಆತನು ಅವುಗಳನ್ನು ಮಾತಾಡಗೊಡಿಸಲಿಲ್ಲ.",
			"35": "<sup>35</sup> ಮುಂಜಾನೆ ಇನ್ನೂ ಮೊಬ್ಬಿರುವಾಗ ಆತನು ಎದ್ದು ಹೊರಟು ಅಡವಿಯ ಸ್ಥಳಕ್ಕೆ ಹೋಗಿ ದೇವರನ್ನು ಪ್ರಾರ್ಥಿಸುತ್ತಿದ್ದನು.",
			"36": "<sup>36</sup> ಸೀಮೋನನೂ ಅವನ ಸಂಗಡ ಇದ್ದವರೂ ಆತನನ್ನು ಹಿಂದಟ್ಟಿ ಕಂಡು -",
			"37": "<sup>37</sup> ಎಲ್ಲರು ನಿನ್ನನ್ನು ಹುಡುಕುತ್ತಾರೆ ಅನ್ನಲಾಗಿ ಆತನು ಅವರಿಗೆ -",
			"38": "<sup>38</sup> ನಾವು ಸಮೀಪದಲ್ಲಿರುವ ಬೇರೆ ಊರುಗಳಿಗೆ ಹೋಗೋಣ; ಅಲ್ಲಿಯೂ ನಾನು ಸುವಾರ್ತೆಯನ್ನು ಸಾರಬೇಕು; ಇದಕ್ಕಾಗಿಯೇ ನಾನು ಹೊರಟು ಬಂದಿದ್ದೇನೆ ಎಂದು ಹೇಳಿದನು.",
			"39": "<sup>39</sup> ಬಳಿಕ ಆತನು ಗಲಿಲಾಯದಲ್ಲೆಲ್ಲಾ ಹೋಗಿ ಅವರ ಸಭಾಮಂದಿರಗಳಲ್ಲಿ ಸುವಾರ್ತೆಯನ್ನು ಸಾರುತ್ತಾ ದೆವ್ವಗಳನ್ನು ಬಿಡಿಸುತ್ತಾ ಇದ್ದನು.",
			"40": "<sup>40</sup> ಒಬ್ಬ ಕುಷ್ಠರೋಗಿಯು ಯೇಸುವಿನ ಬಳಿಗೆ ಬಂದು ಆತನ ಮುಂದೆ ಮೊಣಕಾಲೂರಿಕೊಂಡು - ನಿನಗೆ ಮನಸ್ಸಿದ್ದರೆ ನನ್ನನ್ನು ಶುದ್ಧಮಾಡಬಲ್ಲೆ ಎಂದು ಬೇಡಿಕೊಳ್ಳಲು ಆತನು ಕನಿಕರಪಟ್ಟು ಕೈನೀಡಿ ಅವನನ್ನು ಮುಟ್ಟಿ -",
			"41": "<sup>41</sup> ನನಗೆ ಮನಸ್ಸುಂಟು; ಶುದ್ಧವಾಗು ಅಂದನು.",
			"42": "<sup>42</sup> ಕೂಡಲೆ ಅವನ ಕುಷ್ಠವು ಹೋಗಿ, ಅವನು ಶುದ್ಧವಾದನು. ಆಗ ಯೇಸು ಅವನಿಗೆ - ಯಾರಿಗೂ ಏನೂ ಹೇಳಬೇಡ ನೋಡು; ಆದರೆ ಹೋಗಿ ಯಾಜಕನಿಗೆ ನಿನ್ನ ಮೈ ತೋರಿಸಿ ಮೋಶೆಯು ನೇಮಕಮಾಡಿರುವದನ್ನು ಅರ್ಪಿಸಿ ನಿನ್ನ ಶುದ್ಧಾಚಾರವನ್ನು ನೆರವೇರಿಸು, ಅದು ಜನರಿಗೆ ಸಾಕ್ಷಿಯಾಗಲಿ ಎಂದು ಖಂಡಿತವಾಗಿ ಹೇಳಿ ಅವನನ್ನು ಕಳುಹಿಸಿಬಿಟ್ಟನು.",
			"45": "<sup>45</sup> ಆದರೆ ಅವನು ಹೊರಟುಹೋಗಿ ಆ ಸಂಗತಿಯನ್ನು ಬಹಳವಾಗಿ ಸಾರಿ ಹಬ್ಬಿಸಿದ್ದರಿಂದ ಯೇಸು ಬಹಿರಂಗವಾಗಿ ಇನ್ನು ಯಾವ ಊರೊಳಕ್ಕೂ ಹೋಗಲಾರದೆ ಹೊರಗೆ ಅಡವಿಯ ಸ್ಥಳಗಳಲ್ಲಿ ಇದ್ದನು. ಮತ್ತು ಜನರು ನಾಲ್ಕು ಕಡೆಯಿಂದಲೂ ಆತನ ಬಳಿಗೆ ಬರುತ್ತಿದ್ದರು."
		},
		"2": {
			"1": "<sup>1</sup> ಕೆಲವು ದಿನಗಳಾದ ಮೇಲೆ ಆತನು ಕಪೆರ್ನೌಮಿಗೆ ತಿರಿಗಿ ಬಂದನು. ಮನೆಯಲ್ಲಿದ್ದಾನೆಂಬ ವರ್ತಮಾನವು ಹಬ್ಬಿದಾಗ ಬಹುಜನರು ಕೂಡಿ ಬಂದಿದ್ದರಿಂದ ಬಾಗಿಲಿನ ಬಳಿಯಲ್ಲಾದರೂ ಸ್ಥಳವಿರಲಿಲ್ಲ.",
			"2": "<sup>2</sup> ಆತನು ಅವರಿಗೆ ದೇವರ ವಾಕ್ಯವನ್ನು ಹೇಳುತ್ತಿದ್ದನು.",
			"3": "<sup>3</sup> ಅಷ್ಟರಲ್ಲಿ ಕೆಲವರು ಒಬ್ಬ ಪಾರ್ಶ್ವವಾಯು ರೋಗಿಯನ್ನು ನಾಲ್ಕು ಮಂದಿಯಿಂದ ಹೊರಿಸಿಕೊಂಡು ಆತನ ಬಳಿಗೆ ತಂದರು.",
			"4": "<sup>4</sup> ಜನರು ಗುಂಪಾಗಿದ್ದದರಿಂದ ಅವರು ಆತನ ಹತ್ತಿರಕ್ಕೆ ಸೇರಲಾರದೆ ಆತನಿದ್ದ ಮನೆಯ ಮೇಲ್ಚಾವಣಿಯನ್ನು ಬಿಚ್ಚಿ ದ್ವಾರಮಾಡಿ ಪಾರ್ಶ್ವವಾಯುರೋಗಿಯು ಮಲಗಿದ್ದ ಹಾಸಿಗೆಯನ್ನು ಇಳಿಸಿದರು.",
			"5": "<sup>5</sup> ಯೇಸು ಅವರ ನಂಬಿಕೆಯನ್ನು ನೋಡಿ ಆ ಪಾರ್ಶ್ವವಾಯು ರೋಗಿಗೆ - ಮಗನೇ, ನಿನ್ನ ಪಾಪಗಳು ಕ್ಷಮಿಸಲ್ಪಟ್ಟಿವೆ ಎಂದು ಹೇಳಿದನು.",
			"6": "<sup>6</sup> ಆದರೆ ಶಾಸ್ತ್ರಿಗಳಲ್ಲಿ ಕೆಲವರು ಅಲ್ಲಿ ಕೂತುಕೊಂಡಿದ್ದು -",
			"7": "<sup>7</sup> ಇವನು ಯಾಕೆ ಹೀಗೆ ಮಾತನಾಡುತ್ತಾನೆ? ಇದು ದೇವದೂಷಣೆ. ದೇವರೊಬ್ಬನೇ ಹೊರತು ಮತ್ತಾರು ಪಾಪಗಳನ್ನು ಕ್ಷಮಿಸಬಲ್ಲರು? ಎಂದು ತಮ್ಮ ತಮ್ಮ ಮನಸ್ಸಿನಲ್ಲಿ ಅಂದುಕೊಂಡರು.",
			"8": "<sup>8</sup> ಹೀಗೆ ಅಂದುಕೊಳ್ಳುವದನ್ನು ಕೂಡಲೆ ಯೇಸು ತನ್ನ ಆತ್ಮದಲ್ಲಿ ತಿಳಿದುಕೊಂಡು ಅವರಿಗೆ - ನೀವು ನಿಮ್ಮ ಮನಸ್ಸಿನಲ್ಲಿ ಯಾಕೆ ಹೀಗೆ ಅಂದುಕೊಳ್ಳುತ್ತೀರಿ? ಯಾವದು ಸುಲಭ?",
			"9": "<sup>9</sup> ನಿನ್ನ ಪಾಪಗಳು ಕ್ಷಮಿಸಲ್ಪಟ್ಟಿವೆ ಅನ್ನುವದೋ? ಎದ್ದು ನಿನ್ನ ಹಾಸಿಗೆಯನ್ನು ತೆಗೆದುಕೊಂಡು ನಡೆ ಅನ್ನುವದೋ?",
			"10": "<sup>10</sup> ಆದರೆ ಪಾಪಗಳನ್ನು ಕ್ಷಮಿಸಿಬಿಡುವದಕ್ಕೆ ಮನುಷ್ಯಕುಮಾರನಿಗೆ ಭೂಲೋಕದಲ್ಲಿ ಅಧಿಕಾರ ಉಂಟೆಂಬದು ನಿಮಗೆ ತಿಳಿಯಬೇಕು ಎಂದು ಹೇಳಿ ಪಾರ್ಶ್ವವಾಯುರೋಗಿಯನ್ನು ನೋಡಿ -",
			"11": "<sup>11</sup> ಎದ್ದು ನಿನ್ನ ಹಾಸಿಗೆಯನ್ನು ಎತ್ತಿಕೊಂಡು ಮನೆಗೆ ಹೋಗೆಂದು ನಿನಗೆ ಹೇಳುತ್ತೇನೆ ಅಂದನು.",
			"12": "<sup>12</sup> ಅವನು ಎದ್ದು ಕೂಡಲೆ ತನ್ನ ಹಾಸಿಗೆಯನ್ನು ತೆಗೆದುಕೊಂಡು ಎಲ್ಲರ ಮುಂದೆ ಹೊರಟು ಹೋದನು. ಅದಕ್ಕೆ ಎಲ್ಲರು ಬೆರಗಾಗಿ - ಇದುವರೆಗೆ ನಾವು ಇಂಥದನ್ನು ಎಂದಿಗೂ ನೋಡಿದ್ದೇ ಇಲ್ಲವೆಂದು ಹೇಳುತ್ತಾ ದೇವರನ್ನು ಕೊಂಡಾಡಿದರು.",
			"13": "<sup>13</sup> ಆತನು ಹೊರಟು ತಿರಿಗಿ ಸಮುದ್ರದ ಬಳಿಗೆ ಹೋದನು. ಜನರ ಗುಂಪೆಲ್ಲಾ ಆತನ ಬಳಿಗೆ ಬಂದಾಗ ಆತನು ಅವರಿಗೆ ಉಪದೇಶಮಾಡಿದನು.",
			"14": "<sup>14</sup> ಆತನು [ಅಲ್ಲಿಂದ] ಹೋಗುತ್ತಿರುವಾಗ ಸುಂಕಕ್ಕೆ ಕೂತಿದ್ದ ಅಲ್ಫಾಯನ ಮಗನಾದ ಲೇವಿಯನ್ನು ನೋಡಿ - ನನ್ನನ್ನು ಹಿಂಬಾಲಿಸು ಎಂದು ಅವನನ್ನು ಕರೆಯಲು ಅವನು ಎದ್ದು ಆತನ ಹಿಂದೆ ಹೋದನು.",
			"15": "<sup>15</sup> ಅನಂತರ ಆತನು ಅವನ ಮನೆಯೊಳಗೆ ಊಟಕ್ಕೆ ಕೂತಿರುವಲ್ಲಿ, ಬಹು ಮಂದಿ ಸುಂಕದವರೂ ಪಾಪಿಗಳೂ ಯೇಸುವಿನ ಮತ್ತು ಆತನ ಶಿಷ್ಯರ ಪಂಙ್ತಿಯಲ್ಲೇ ಕೂತುಕೊಂಡರು.",
			"16": "<sup>16</sup> ಅಂಥವರು ಬಹು ಮಂದಿ ಇದ್ದರು. ಫರಿಸಾಯರಾದ ಶಾಸ್ತ್ರಿಗಳು ಆತನ ಹಿಂದೆ ಹೋಗಿ ಆತನು ಪಾಪಿಗಳ ಮತ್ತು ಸುಂಕದವರ ಸಂಗಡ ಊಟಮಾಡುವದನ್ನು ಕಂಡು ಆತನ ಶಿಷ್ಯರಿಗೆ - ಈತನು ಸುಂಕದವರ ಮತ್ತು ಪಾಪಿಗಳ ಸಂಗಡ ಊಟಮಾಡುತ್ತಾನೆ ನೋಡಿರಿ ಎಂದು ಹೇಳಿದರು.",
			"17": "<sup>17</sup> ಯೇಸು ಅದನ್ನು ಕೇಳಿ - ಕ್ಷೇಮದಿಂದಿರುವವರಿಗೆ ವೈದ್ಯನು ಬೇಕಾಗಿಲ್ಲ; ಕ್ಷೇಮವಿಲ್ಲದವರಿಗೆ ಬೇಕು. ನಾನು ನೀತಿವಂತರನ್ನು ಕರೆಯುವದಕ್ಕೆ ಬಂದವನಲ್ಲ, ಪಾಪಿಗಳನ್ನು ಕರೆಯುವದಕ್ಕೆ ಬಂದವನು ಅಂದನು.",
			"18": "<sup>18</sup> ಇದಲ್ಲದೆ ಯೋಹಾನನ ಶಿಷ್ಯರೂ ಫರಿಸಾಯರೂ ಉಪವಾಸವಾಗಿದ್ದರು. ಅವರು ಆತನ ಬಳಿಗೆ ಬಂದು - ಯೋಹಾನನ ಶಿಷ್ಯರೂ ಫರಿಸಾಯರ ಶಿಷ್ಯರೂ ಉಪವಾಸಮಾಡುತ್ತಾರಲ್ಲ? ನಿನ್ನ ಶಿಷ್ಯರು ಯಾಕೆ ಉಪವಾಸಮಾಡುವದಿಲ್ಲ? ಎಂದು ಕೇಳಲು",
			"19": "<sup>19</sup> ಯೇಸು ಅವರಿಗೆ - ಮದುವೆಯ ಜನರು ತಮ್ಮ ಸಂಗಡ ಮದಲಿಂಗನು ಇರುವಲ್ಲಿ ಉಪವಾಸಮಾಡುವದುಂಟೇ? ಮದಲಿಂಗನು ತಮ್ಮ ಸಂಗಡ ಇರುವ ತನಕ ಅವರು ಉಪವಾಸಮಾಡಲಾಗದು.",
			"20": "<sup>20</sup> ಆದರೆ ಮದಲಿಂಗನನ್ನು ಅವರ ಬಳಿಯಿಂದ ತೆಗೆದುಕೊಂಡು ಹೋಗುವ ಕಾಲ ಬರುತ್ತದೆ; ಆ ಕಾಲದಲ್ಲೇ ಉಪವಾಸಮಾಡುವರು.",
			"21": "<sup>21</sup> ಯಾರೂ ಹೊಸ ಬಟ್ಟೆಯ ತುಂಡನ್ನು ಹಳೆಯ ವಸ್ತ್ರಕ್ಕೆ ತ್ಯಾಪೆಹಚ್ಚುವದಿಲ್ಲ; ಹಚ್ಚಿದರೆ ಆ ಹೊಸ ತ್ಯಾಪೆಯು ಹಳೆಯ ವಸ್ತ್ರವನ್ನು ಹಿಂಜುವದರಿಂದ ಹರಕು ಹೆಚ್ಚಾಗುವದು.",
			"22": "<sup>22</sup> ಮತ್ತು ಹಳೆಯ ಬುದ್ದಲಿಗಳಲ್ಲಿ ಹೊಸ ದ್ರಾಕ್ಷಾರಸವನ್ನು ಯಾರೂ ಹಾಕಿಡುವದಿಲ್ಲ; ಇಟ್ಟರೆ ಆ ದ್ರಾಕ್ಷಾರಸವು ಬುದ್ದಲಿಗಳನ್ನು ಒಡೆದು ದ್ರಾಕ್ಷಾರಸವು ಬುದ್ದಲಿಗಳು ಎರಡೂ ಕೆಟ್ಟುಹೋಗುವವು. ಆದರೆ ಹೊಸ ದ್ರಾಕ್ಷಾರಸವನ್ನು ಹೊಸ ಬುದ್ದಲಿಗಳಲ್ಲಿ [ಹಾಕಿಡತಕ್ಕದ್ದು] ಎಂದು ಹೇಳಿದನು.",
			"23": "<sup>23</sup> ಯೇಸು ಸಬ್ಬತ್‍ದಿನದಲ್ಲಿ ಪೈರಿನ ಹೊಲಗಳನ್ನು ಹಾದುಹೋಗುತ್ತಿರುವಾಗ, ಆತನ ಶಿಷ್ಯರು ನಡೆಯುತ್ತಾ ತೆನೆಗಳನ್ನು ಮುರುಕೊಂಡರು.",
			"24": "<sup>24</sup> ಫರಿಸಾಯರು ಆತನನ್ನು - ನೋಡು, ಇವರು ಸಬ್ಬತ್‍ದಿನದಲ್ಲಿ ಮಾಡಬಾರದ ಕೆಲಸವನ್ನು ಯಾಕೆ ಮಾಡುತ್ತಾರೆ ಎಂದು ಕೇಳಿದರು.",
			"25": "<sup>25</sup> ಆತನು ಅವರಿಗೆ - ದಾವೀದನು ತಾನೂ ತನ್ನ ಸಂಗಡ ಇದ್ದವರೂ ಕೊರತೆಯುಳ್ಳವರಾಗಿ ಹಸಿದಾಗ ಏನು ಮಾಡಿದನೆಂಬದನ್ನು ನೀವು ಎಂದಾದರೂ ಓದಲಿಲ್ಲವೋ?",
			"26": "<sup>26</sup> ಅವನು ಮಹಾಯಾಜಕನಾದ ಅಬಿಯಾತರನ ಕಾಲದಲ್ಲಿ ದೇವರಮಂದಿರದೊಳಕ್ಕೆ ಹೋಗಿ ಯಾಜಕರು ಹೊರತು ಮತ್ತಾರೂ ತಿನ್ನಬಾರದ ನೈವೇದ್ಯದ ರೊಟ್ಟಿಗಳನ್ನು ತಾನು ತಿಂದು ತನ್ನ ಸಂಗಡ ಇದ್ದವರಿಗೂ ಕೊಟ್ಟನಲ್ಲಾ? ಎಂದು ಹೇಳಿದನು.",
			"27": "<sup>27</sup> ಮತ್ತು ಆತನು - ಸಬ್ಬತ್‍ದಿನವು ಮನುಷ್ಯರಿಗೋಸ್ಕರ ಉಂಟಾಯಿತೇ ಹೊರತು ಮನುಷ್ಯರು ಸಬ್ಬತ್ ದಿನಕ್ಕೋಸ್ಕರ ಉಂಟಾಗಲಿಲ್ಲ;",
			"28": "<sup>28</sup> ಹೀಗಿರಲಾಗಿ ಮನುಷ್ಯಕುಮಾರನು ಸಬ್ಬತ್‍ದಿನಕ್ಕೂ ಒಡೆಯನಾಗಿದ್ದಾನೆ ಅಂದನು."
		},
		"3": {
			"1": "<sup>1</sup> ಇನ್ನೊಂದು ಸಮಯದಲ್ಲಿ ಯೇಸು ಸಭಾಮಂದಿರಕ್ಕೆ ಹೋದಾಗ ಅಲ್ಲಿ ಕೈ ಬತ್ತಿಹೋದವನೊಬ್ಬನಿದ್ದನು.",
			"2": "<sup>2</sup> ಅವರು ಆತನ ಮೇಲೆ ತಪ್ಪುಹೊರಿಸಬೇಕೆಂದು ಆಲೋಚಿಸಿ ಸಬ್ಬತ್‍ದಿನದಲ್ಲಿ ಅವನನ್ನು ಸ್ವಸ್ಥಮಾಡುವನೋ ಏನೋ ಎಂದು ಆತನನ್ನು ಹೊಂಚಿನೋಡುತ್ತಾ ಇದ್ದರು.",
			"3": "<sup>3</sup> ಆತನು ಕೈಬತ್ತಿದ್ದವನಿಗೆ - ಎದ್ದು ನಡುವೆ ಬಾ ಎಂದು ಹೇಳಿ ಅವರಿಗೆ -",
			"4": "<sup>4</sup> ಸಬ್ಬತ್‍ದಿನದಲ್ಲಿ ಯಾವದನ್ನು ಮಾಡುವದು ನ್ಯಾಯ? ಮೇಲನ್ನು ಮಾಡುವದೋ, ಕೇಡನ್ನು ಮಾಡುವದೋ? ಪ್ರಾಣವನ್ನು ಉಳಿಸುವದೋ, ತೆಗೆಯುವದೋ? ಎಂದು ಹೇಳಲು ಅವರು ಸುಮ್ಮನಿದ್ದರು.",
			"5": "<sup>5</sup> ಆಗ ಆತನು ಅವರ ಮನಸ್ಸು ಕಲ್ಲಾಗಿರುವದನ್ನು ಕಂಡು ದುಃಖಪಟ್ಟು ಕೋಪದಿಂದ ಸುತ್ತಲೂ ಅವರನ್ನು ನೋಡಿ ಆ ಮನುಷ್ಯನಿಗೆ - ನಿನ್ನ ಕೈಚಾಚು ಎಂದು ಹೇಳಿದನು. ಅವನು ಚಾಚಿದನು; ಕೈ ವಾಸಿಯಾಯಿತು.",
			"6": "<sup>6</sup> ಫರಿಸಾಯರು ಹೊರಕ್ಕೆ ಹೋಗಿ ಕೂಡಲೆ ಹೆರೋದ್ಯರನ್ನು ಕೂಡಿಕೊಂಡು ಇವನನ್ನು ಯಾವ ಉಪಾಯದಿಂದ ಕೊಲ್ಲೋಣ ಎಂದು ಆತನಿಗೆ ವಿರೋಧವಾಗಿ ಆಲೋಚನೆ ಮಾಡಿಕೊಂಡರು.",
			"7": "<sup>7</sup> ಯೇಸು ತನ್ನ ಶಿಷ್ಯರ ಕೂಡ ಆ ಸ್ಥಳವನ್ನು ಬಿಟ್ಟು ಸಮುದ್ರದ ಬಳಿಗೆ ಹೋದನು. ಗಲಿಲಾಯದಿಂದ ಬಹುಜನರು ಆತನ ಹಿಂದೆ ಹೋದರು.",
			"8": "<sup>8</sup> ಇದಲ್ಲದೆ ಆತನು ಇಂಥಿಂಥ ಕೆಲಸಗಳನ್ನು ಮಾಡುತ್ತಾನೆಂದು ಕೇಳಿ ಬಹುಜನರು ಯೂದಾಯದಿಂದಲೂ ಯೆರೂಸಲೇಮಿನಿಂದಲೂ ಇದೂಮಾಯದಿಂದಲೂ ಯೊರ್ದನ್ ಹೊಳೆಯ ಆಚೆಯಿಂದಲೂ ತೂರ್ ಸೀದೋನ್ ಪಟ್ಟಣಗಳ ಸುತ್ತಲಿನಿಂದಲೂ ಆತನ ಬಳಿಗೆ ಬಂದರು.",
			"9": "<sup>9</sup> ಜನರು ಗುಂಪಾಗಿದ್ದದರಿಂದ ಮೈಮೇಲೆ ಬಿದ್ದು ನೂಕಾರೆಂದು ಆತನು ತನಗೆ ಒಂದು ದೋಣಿಯು ಸಿದ್ಧವಾಗಿರಲಿ ಎಂಬದಾಗಿ ಶಿಷ್ಯರಿಗೆ ಹೇಳಿದನು.",
			"10": "<sup>10</sup> ಯಾಕಂದರೆ ಆತನು ಅನೇಕರನ್ನು ಸ್ವಸ್ಥಮಾಡಿದ್ದದರಿಂದ ರೋಗಪೀಡಿತರಾಗಿದ್ದವರೆಲ್ಲರೂ ಆತನನ್ನು ಮುಟ್ಟಬೇಕೆಂದು ಮೇಲೆ ಬೀಳುತ್ತಿದ್ದರು.",
			"11": "<sup>11</sup> ಮತ್ತು ದೆವ್ವಗಳು ಆತನನ್ನು ಕಂಡಾಗೆಲ್ಲಾ ಆತನ ಪಾದಕ್ಕೆ ಬಿದ್ದು - ನೀನು ದೇವಕುಮಾರನೆಂದು ಕೂಗುತ್ತಿದ್ದವು.",
			"12": "<sup>12</sup> ಆದರೆ ಆತನು ತಾನು ಇಂಥವನೆಂಬದಾಗಿ ಯಾರಿಗೂ ಪ್ರಕಟಿಸಬಾರದೆಂದು ಅವರಿಗೆ ಬಹು ಖಂಡಿತವಾಗಿ ಹೇಳಿದನು.",
			"13": "<sup>13</sup> ಆಮೇಲೆ ಯೇಸು ಬೆಟ್ಟವನ್ನು ಹತ್ತಿ ತನಗೆ ಬೇಕಾದವರನ್ನು ಹತ್ತಿರಕ್ಕೆ ಕರೆಯಲು ಅವರು ಆತನ ಬಳಿಗೆ ಬಂದರು.",
			"14": "<sup>14</sup> ಆತನು ಹನ್ನೆರಡು ಮಂದಿಯನ್ನು ಆರಿಸಿ ಅವರು ತನ್ನ ಸಂಗಡ ಇರಬೇಕೆಂತಲೂ",
			"15": "<sup>15</sup> ದೆವ್ವಗಳನ್ನು ಬಿಡಿಸುವ ಅಧಿಕಾರವುಳ್ಳವರಾಗಿ ಸುವಾರ್ತೆಯನ್ನು ಸಾರುವದಕ್ಕೆ ತಾನು ಅವರನ್ನು ಕಳುಹಿಸುವೆನೆಂತಲೂ ಆ ಹನ್ನೆರಡು ಮಂದಿಯನ್ನು ಆರಿಸಿ ನೇಮಿಸಿದನು.",
			"16": "<sup>16</sup> ಸೀಮೋನನೆಂಬವನಿಗೆ ಪೇತ್ರನೆಂದು ಹೆಸರನ್ನು ಇಟ್ಟನು.",
			"17": "<sup>17</sup> ಜೆಬೆದಾಯನ ಮಗನಾದ ಯಾಕೋಬನಿಗೂ ಯಾಕೋಬನ ತಮ್ಮನಾದ ಯೋಹಾನನಿಗೂ ಬೊವನೆರ್ಗೆಸ್ ಅಂದರೆ ಗುಡುಗಿನ ಮರಿಗಳು ಎಂದು ಹೆಸರಿಟ್ಟನು.",
			"18": "<sup>18</sup> ಉಳಿದವರು ಯಾರಂದರೆ, ಅಂದ್ರೆಯ, ಫಿಲಿಪ್ಪ, ಬಾರ್ತೊಲೊಮಾಯ, ಮತ್ತಾಯ, ತೋಮ, ಅಲ್ಫಾಯನ ಮಗನಾದ ಯಾಕೋಬ, ತದ್ದಾಯ, ಮತಾಭಿಮಾನಿ ಎಂದು ಹೆಸರುಗೊಂಡ ಸೀಮೋನ,",
			"19": "<sup>19</sup> ಯೇಸುವನ್ನು ಹಿಡುಕೊಟ್ಟ ಇಸ್ಕರಿಯೋತ ಯೂದ, ಇವರೇ.",
			"20": "<sup>20</sup> ಆತನು ಮನೆಯೊಳಗೆ ಬಂದಾಗ ಜನರು ತಿರಿಗಿ ನೆರೆದದ್ದರಿಂದ ಊಟಕ್ಕೂ ಅವಕಾಶವಾಗಲಿಲ್ಲ.",
			"21": "<sup>21</sup> ಇದನ್ನು ಕೇಳಿ ಆತನ ಬಂಧುಗಳು ಅವನಿಗೆ ಹುಚ್ಚುಹಿಡಿದದೆ ಎಂದು ಹೇಳಿ ಆತನನ್ನು ಹಿಡಿಯುವದಕ್ಕೆ ಹೊರಟರು.",
			"22": "<sup>22</sup> ಇದಲ್ಲದೆ ಯೆರೂಸಲೇಮಿನಿಂದ ಬಂದಿದ್ದ ಶಾಸ್ತ್ರಿಗಳು - ಇವನನ್ನು ಬೆಲ್ಜೆಬೂಲನು ಹಿಡಿದಿದ್ದಾನೆಂತಲೂ ಇವನು ದೆವ್ವಗಳ ಒಡೆಯನ ಸಹಾಯದಿಂದ ದೆವ್ವಗಳನ್ನು ಬಿಡಿಸುತ್ತಾನೆಂತಲೂ ಹೇಳುತ್ತಿದ್ದರು.",
			"23": "<sup>23</sup> ಆತನು ಅವರನ್ನು ಹತ್ತಿರಕ್ಕೆ ಕರೆದು ಅವರಿಗೆ ಸಾಮ್ಯರೂಪವಾಗಿ ಹೇಳಿದ್ದೇನಂದರೆ - ಸೈತಾನನು ಸೈತಾನನನ್ನು ಬಿಡಿಸುವದುಂಟೇ?",
			"24": "<sup>24</sup> ಒಂದು ರಾಜ್ಯದಲ್ಲಿ ಭೇದಹುಟ್ಟಿದರೆ ಆ ರಾಜ್ಯವು ಉಳಿಯಲಾರದು;",
			"25": "<sup>25</sup> ಒಂದು ಮನೆಯಲ್ಲಿ ಭೇದಹುಟ್ಟಿದರೆ ಆ ಮನೆಯು ನಿಲ್ಲಲಾರದು.",
			"26": "<sup>26</sup> ಅದರಂತೆ ಸೈತಾನನು ತನಗೆ ವಿರೋಧವಾಗಿ ಎದ್ದು ತನ್ನಲ್ಲಿ ಭೇದ ಹುಟ್ಟಿಸಿಕೊಂಡರೆ ಅವನು ನಿಲ್ಲಲಾರದೆ ನಾಶವಾಗಿ ಹೋಗುವನು.",
			"27": "<sup>27</sup> ಅದಲ್ಲದೆ ಒಬ್ಬನು ಮೊದಲು ಬಲಿಷ್ಠನನ್ನು ಕಟ್ಟಿಹಾಕದೆ ಆ ಬಲಿಷ್ಠನ ಮನೆಯನ್ನು ಹೊಕ್ಕು ಅವನ ಸೊತ್ತನ್ನು ಸುಲುಕೊಳ್ಳುವದಕ್ಕಾಗುವದಿಲ್ಲ; ಕಟ್ಟಿಹಾಕಿದ ಮೇಲೆ ಅವನ ಮನೆಯನ್ನು ಸುಲುಕೊಂಡಾನು.",
			"28": "<sup>28</sup> ಇದಲ್ಲದೆ ಅವರು - ಇವನಿಗೆ ದೆವ್ವ ಹಿಡಿದದೆ ಎಂದು ಹೇಳಿದ್ದರಿಂದ ಆತನು ಅವರಿಗೆ - ನಿಮಗೆ ಸತ್ಯವಾಗಿ ಹೇಳುತ್ತೇನೆ, ಮನುಷ್ಯರು ಎಷ್ಟು ದೂಷಿಸಿದಾಗ್ಯೂ ಅವರು ಮಾಡುವ ಎಲ್ಲಾ ಪಾಪಗಳಿಗೂ ದೂಷಣೆಗಳಿಗೂ ಕ್ಷಮಾಪಣೆ ಉಂಟಾಗುವದು, ಆದರೆ ಪವಿತ್ರಾತ್ಮನನ್ನು ದೂಷಿಸಿದವನು ಎಂದಿಗೂ ಕ್ಷಮಾಪಣೆ ಹೊಂದನು; ಅವನು ಶಾಶ್ವತ ಪಾಪದೊಳಗೆ ಸೇರಿದವನಾದನು ಎಂದು ಹೇಳಿದನು.",
			"31": "<sup>31</sup> ಜನರು ಆತನ ಸುತ್ತಲು ಕೂತುಕೊಂಡಿದ್ದಾಗ ಆತನ ತಾಯಿಯೂ ತಮ್ಮಂದಿರೂ ಬಂದು ಹೊರಗೆ ನಿಂತುಕೊಂಡು ಆತನನ್ನು ಕರೇ ಕಳುಹಿಸಿದರು.",
			"32": "<sup>32</sup> ಆ ಜನರು ಆತನಿಗೆ - ನಿನ್ನ ತಾಯಿಯೂ ನಿನ್ನ ತಮ್ಮಂದಿರೂ ನಿನ್ನನ್ನು ಹುಡುಕಿಕೊಂಡು ಬಂದು ಹೊರಗೆ ನಿಂತಿರುತ್ತಾರೆ ನೋಡು ಎಂದು ಹೇಳಿದ್ದಕ್ಕೆ",
			"33": "<sup>33</sup> ಆತನು ಅವರಿಗೆ - ನನಗೆ ತಾಯಿಯೂ ತಮ್ಮಂದಿರೂ ಯಾರು? ಎಂದು ಹೇಳಿ",
			"34": "<sup>34</sup> ತನ್ನ ಸುತ್ತಲು ಕೂತಿದ್ದವರನ್ನು ನೋಡಿ - ಇಗೋ, ನನ್ನ ತಾಯಿ, ನನ್ನ ತಮ್ಮಂದಿರು.",
			"35": "<sup>35</sup> ದೇವರ ಚಿತ್ತದಂತೆ ನಡೆಯುವವನೇ ನನಗೆ ತಮ್ಮನೂ ತಂಗಿಯೂ ತಾಯಿಯೂ ಆಗಬೇಕು ಅಂದನು."
		},
		"4": {
			"1": "<sup>1</sup> ಆತನು ತಿರಿಗಿ ಸಮುದ್ರದ ದಡದಲ್ಲಿ ಉಪದೇಶ ಮಾಡುತ್ತಿದ್ದನು. ಬಹುಜನರು ಆತನ ಬಳಿಗೆ ಕೂಡಿ ಬಂದದ್ದರಿಂದ ಆತನು ಸಮುದ್ರದಲ್ಲಿದ್ದ ದೋಣಿಹತ್ತಿ ಕೂತುಕೊಂಡನು;",
			"2": "<sup>2</sup> ಆ ಜನರೆಲ್ಲರು ಸಮುದ್ರದ ಹತ್ತರ ನೆಲದ ಮೇಲೆ ಇದ್ದರು. ಆಗ ಆತನು ಅವರಿಗೆ ಸಾಮ್ಯರೂಪವಾಗಿ ಅನೇಕ ಸಂಗತಿಗಳನ್ನು ಉಪದೇಶ ಮಾಡಿದನು. ಆ ಉಪದೇಶದಲ್ಲಿ ಅವರಿಗೆ ಹೇಳಿದ್ದೇನಂದರೆ -",
			"3": "<sup>3</sup> ಕೇಳಿರಿ! ಬಿತ್ತುವವನು ಬಿತ್ತುವದಕ್ಕೆ ಹೊರಟನು.",
			"4": "<sup>4</sup> ಬಿತ್ತುವಾಗ ಕೆಲವು ಬೀಜ ದಾರಿಯ ಮಗ್ಗುಲಲ್ಲಿ ಬಿದ್ದವು; ಹಕ್ಕಿಗಳು ಬಂದು ಅವುಗಳನ್ನು ತಿಂದುಬಿಟ್ಟವು.",
			"5": "<sup>5</sup> ಬೇರೆ ಕೆಲವು ಬೀಜ ಬಹಳ ಮಣ್ಣಿಲ್ಲದ ಬಂಡೆಯ ನೆಲದಲ್ಲಿ ಬಿದ್ದವು; ಅಲ್ಲಿ ಮಣ್ಣು ತೆಳ್ಳಗಿದ್ದದರಿಂದ ಅವು ಬೇಗ ಮೊಳೆತವು;",
			"6": "<sup>6</sup> ಆದರೆ ಬಿಸಿಲೇರಿದಾಗ ಬಾಡಿ ಬೇರಿಲ್ಲದಕಾರಣ ಒಣಗಿಹೋದವು.",
			"7": "<sup>7</sup> ಮತ್ತೆ ಕೆಲವು ಬೀಜ ಮುಳ್ಳುಗಿಡಗಳಲ್ಲಿ ಬಿದ್ದವು; ಮುಳ್ಳುಗಿಡಗಳು ಬೆಳೆದು ಅವುಗಳನ್ನು ಅಡಗಿಸಿಬಿಟ್ಟದ್ದರಿಂದ ಅವು ಫಲಕೊಡಲಿಲ್ಲ.",
			"8": "<sup>8</sup> ಇನ್ನು ಕೆಲವು ಬೀಜ ಒಳ್ಳೆಯ ನೆಲದಲ್ಲಿ ಬಿದ್ದು ಹುಟ್ಟಿ ಬೆಳೆಯುತ್ತಾ ಬಂದು ಒಂದು ಮೂವತ್ತರಷ್ಟು, ಒಂದು ಅರುವತ್ತರಷ್ಟು,",
			"9": "<sup>9</sup> ಒಂದು ನೂರರಷ್ಟು ಫಲವನ್ನು ಕೊಟ್ಟವು ಎಂದು ಹೇಳಿ - ಕೇಳುವದಕ್ಕೆ ಕಿವಿಯುಳ್ಳವನು ಕೇಳಲಿ ಅಂದನು.",
			"10": "<sup>10</sup> ಆತನು ಒಬ್ಬನೇ ಇದ್ದಾಗ ಆತನ ಹತ್ತಿರ ಇದ್ದವರು ಹನ್ನೆರಡು ಮಂದಿ ಶಿಷ್ಯರ ಕೂಡ ಆ ಸಾಮ್ಯಗಳ ವಿಷಯದಲ್ಲಿ ಆತನನ್ನು ಕೇಳಿದರು.",
			"11": "<sup>11</sup> ಆತನು ಅವರಿಗೆ - ದೇವರ ರಾಜ್ಯದ ಗುಟ್ಟು ನಿಮಗೇ ಕೊಟ್ಟಿದೆ; ಹೊರಗಿನವರಿಗೆ ಎಲ್ಲವೂ ಸಾಮ್ಯದ ಮರೆಯಲ್ಲಿವೆ.",
			"12": "<sup>12</sup> ಅವರು ಕಣ್ಣಿದ್ದು ನೋಡಿದರೂ ಕಾಣಬಾರದು, ಕಿವಿಯಿದ್ದು ಕೇಳಿದರೂ ತಿಳುಕೊಳ್ಳಬಾರದು, ಹಾಗೆ ಕಂಡು ತಿಳುಕೊಂಡರೆ ಅವರು ದೇವರ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡು ಪಾಪಕ್ಷಮೆಯನ್ನು ಹೊಂದಾರು ಎಂದೇ ಮರೆಮಾಡಲಾಗಿದೆ.",
			"13": "<sup>13</sup> ಆಮೇಲೆ ಆತನು ಅವರಿಗೆ ಹೇಳಿದ್ದೇನಂದರೆ - ಈ ಸಾಮ್ಯದ ಅರ್ಥ ನಿಮಗೆ ಗೊತ್ತಾಗಲಿಲ್ಲವೋ? ಹಾಗಾದರೆ ನನ್ನ ಎಲ್ಲಾ ಸಾಮ್ಯಗಳನ್ನು ಹೇಗೆ ತಿಳುಕೊಂಡೀರಿ?",
			"14": "<sup>14</sup> ಆ ಬಿತ್ತುವವನು ವಾಕ್ಯವೆಂಬ ಬೀಜವನ್ನು ಬಿತ್ತುತ್ತಾನೆ.",
			"15": "<sup>15</sup> ಕೆಲವರು ವಾಕ್ಯವನ್ನು ಕೇಳಿದ ಕೂಡಲೇ ಸೈತಾನನು ಬಂದು ಅವರಲ್ಲಿ ಬಿತ್ತಿದ್ದ ವಾಕ್ಯವನ್ನು ತೆಗೆದುಬಿಡುತ್ತಾನೆ. ಇವರೇ ವಾಕ್ಯವು ಬಿತ್ತಲ್ಪಟ್ಟ ದಾರಿಯ ಮಗ್ಗುಲಾಗಿರುವವರು.",
			"16": "<sup>16</sup> ಅದೇ ಪ್ರಕಾರ ಬೇರೆ ಕೆಲವರು ವಾಕ್ಯವನ್ನು ಕೇಳಿದ ಕೂಡಲೆ ಸಂತೋಷದಿಂದ ಅದನ್ನು ಸ್ವೀಕರಿಸುತ್ತಾರೆ; ತಮಗೆ ಬೇರಿಲ್ಲದ ಕಾರಣ ಇವರು ಸ್ವಲ್ಪ ಕಾಲ ಮಾತ್ರವೇ ಇದ್ದು ಬಳಿಕ ಆ ವಾಕ್ಯದ ನಿಮಿತ್ತವಾಗಿ ಸಂಕಟವಾಗಲಿ ಹಿಂಸೆಯಾಗಲಿ ಬಂದರೆ ಬೇಗ ಎಡವಿಬೀಳುತ್ತಾರೆ; ಇವರೇ ಬೀಜಬಿದ್ದ ಬಂಡೆಯ ನೆಲವಾಗಿರುವವರು.",
			"18": "<sup>18</sup> ಇನ್ನು ಕೆಲವರು ವಾಕ್ಯವನ್ನು ಕೇಳಿದಾಗ್ಯೂ ಪ್ರಪಂಚದ ಚಿಂತೆಗಳೂ ಐಶ್ವರ್ಯದಿಂದುಂಟಾಗುವ ಮೋಸವೂ ಇತರ ವಿಷಯಗಳ ಮೇಲಣ ಆಶೆಗಳೂ ಒಳಗೆ ಸೇರಿ ಆ ವಾಕ್ಯವನ್ನು ಅಡಗಿಸಿಬಿಡುವದರಿಂದ ಫಲವನ್ನು ಕೊಡದೆ ಇರುತ್ತಾರೆ; ಇವರೇ ಮುಳ್ಳುಗಿಡಗಳಲ್ಲಿ ಬೀಜಬಿದ್ದ ನೆಲವಾಗಿರುವವರು.",
			"20": "<sup>20</sup> ಮತ್ತೆ ಕೆಲವರು ವಾಕ್ಯವನ್ನು ಕೇಳಿ ಕೈಕೊಂಡು ಮೂವತ್ತರಷ್ಟಾಗಲಿ ಅರುವತ್ತರಷ್ಟಾಗಲಿ ನೂರರಷ್ಟಾಗಲಿ ಫಲವನ್ನು ಕೊಡುತ್ತಾರೆ; ಇವರೇ ಬೀಜಬಿದ್ದ ಒಳ್ಳೆಯ ನೆಲವಾಗಿರುವವರು ಅಂದನು.",
			"21": "<sup>21</sup> ಇದಲ್ಲದೆ ಆತನು ಅವರಿಗೆ - ದೀಪವನ್ನು ತಂದು ಕೊಳಗದೊಳಗಾಗಲಿ ಮಂಚದ ಕೆಳಗಾಗಲಿ ಇಡುವದುಂಟೇ? ದೀಪಸ್ತಂಭದ ಮೇಲೆ ಇಡುತ್ತಾರಲ್ಲಾ?",
			"22": "<sup>22</sup> ಯಾವದಾಗಲಿ ಪ್ರಕಾಶಕ್ಕೆ ಬರಬೇಕೇ ಹೊರತು ರಹಸ್ಯವಾಗಿರುವದಿಲ್ಲ; ಯಾವದಾಗಲಿ ಬೈಲಿಗೆ ಬರಬೇಕೇ ಹೊರತು ಗುಟ್ಟಾಗಿರುವದಿಲ್ಲ.",
			"23": "<sup>23</sup> ಕೇಳುವದಕ್ಕೆ ಕಿವಿಯುಳ್ಳವನು ಕೇಳಲಿ ಎಂದು ಹೇಳಿದನು.",
			"24": "<sup>24</sup> ಮತ್ತು ಆತನು ಅವರಿಗೆ - ನೀವು ಕಿವಿಗೊಡುವ ವಿಷಯದಲ್ಲಿ ಎಚ್ಚರಿಕೆ. ನೀವು ಅಳೆಯುವ ಅಳತೆಯಿಂದಲೇ ನಿಮಗೂ ಅಳೆಯುವರು; ಇನ್ನೂ ಕೂಡಿಸಿ ಕೊಡುವರು.",
			"25": "<sup>25</sup> ಇದ್ದವನಿಗೆ ಕೊಡಲ್ಪಡುವದು; ಇಲ್ಲದವನ ಕಡೆಯಿಂದ ಇದ್ದದ್ದೂ ತೆಗೆಯಲ್ಪಡುವದು ಎಂದು ಹೇಳಿದನು.",
			"26": "<sup>26</sup> ಇನ್ನೂ ಆತನು ಅವರಿಗೆ ಹೇಳಿದ್ದು - ಒಬ್ಬನು ಭೂಮಿಯಲ್ಲಿ ಬೀಜವನ್ನು ಹಾಕಿ ರಾತ್ರಿ ಮಲಗುತ್ತಾ ಹಗಲು ಎದ್ದಿರುತ್ತಾ ಇರಲು",
			"27": "<sup>27</sup> ಅವನಿಗೆ ತಿಳಿಯದ ರೀತಿಯಲ್ಲಿ ಆ ಬೀಜವು ಹುಟ್ಟಿ ಬೆಳೆಯುವದು. ಇದರಂತೆ ದೇವರ ರಾಜ್ಯ.",
			"28": "<sup>28</sup> ಭೂಮಿಯು ಮೊದಲು ಗರಿಯನ್ನೂ, ಆಮೇಲೆ ಹೊಡೆಯನ್ನೂ, ತರುವಾಯ ತೆನೆಯಲ್ಲಿ ತುಂಬಾ ಕಾಳನ್ನೂ ತನ್ನಷ್ಟಕ್ಕೆ ತಾನೇ ಹುಟ್ಟಿಸುತ್ತದೆ.",
			"29": "<sup>29</sup> ಆದರೆ ಫಲಮಾಗಿದ ಕೂಡಲೆ ಅವನು ಸುಗ್ಗಿ ಕಾಲ ಬಂತೆಂದು ಕೊಯಿಸುತ್ತಾನೆ ಅಂದನು.",
			"30": "<sup>30</sup> ಇನ್ನೂ ಆತನು ಹೇಳಿದ್ದು - ದೇವರ ರಾಜ್ಯವನ್ನು ಯಾವದಕ್ಕೆ ಹೋಲಿಸೋಣ? ಯಾವ ಸಾಮ್ಯದಿಂದ ಅದನ್ನು ತೋರಿಸಿಕೊಡೋಣ?",
			"31": "<sup>31</sup> ಅದು ಸಾಸಿವೆ ಕಾಳಿನಂತಿರುತ್ತದೆ. ಭೂಮಿಯಲ್ಲಿ ಬಿತ್ತುವಾಗ ಅದು ಭೂಮಿಯಲ್ಲಿರುವ ಎಲ್ಲಾ ಬೀಜಗಳಿಗಿಂತಲೂ ಸಣ್ಣದಾಗಿದೆ.",
			"32": "<sup>32</sup> ಬಿತ್ತಿದ ಮೇಲೆ ಅದು ಬೆಳೆದು ಎಲ್ಲಾ ಕಾಯಿಪಲ್ಯದ ಗಿಡಗಳಿಗಿಂತ ದೊಡ್ಡದಾಗಿ ದೊಡ್ಡ ದೊಡ್ಡ ಕೊಂಬೆಗಳನ್ನು ಬಿಡುವದರಿಂದ ಆಕಾಶದಲ್ಲಿ ಹಾರಾಡುವ ಹಕ್ಕಿಗಳು ಅದರ ನೆರಳಿನಲ್ಲಿ ವಾಸಮಾಡುವದಕ್ಕಾಗುತ್ತದೆ ಅಂದನು.",
			"33": "<sup>33</sup> ಆತನು ಈ ತರದ ಅನೇಕ ಸಾಮ್ಯಗಳಿಂದ ಜನರ ಗ್ರಹಿಕೆಗೆ ತಕ್ಕ ಹಾಗೆ ದೇವರ ವಾಕ್ಯವನ್ನು ಹೇಳುತ್ತಿದ್ದನು. ಸಾಮ್ಯವಿಲ್ಲದೆ ಅವರಿಗೆ ಹೇಳಲಿಲ್ಲ.",
			"34": "<sup>34</sup> ಆದರೆ ಪ್ರತ್ಯೇಕವಾಗಿ ಆತನು ತನ್ನ ಆಪ್ತಶಿಷ್ಯರಿಗೆ ಎಲ್ಲವನ್ನು ಬಿಚ್ಚಿ ಹೇಳಿದನು.",
			"35": "<sup>35</sup> ಆ ಹೊತ್ತು ಸಾಯಂಕಾಲವಾದಾಗ ಆತನು ಅವರಿಗೆ - ಆಚೇದಡಕ್ಕೆ ಹೋಗೋಣ ಎಂದು ಹೇಳಲು",
			"36": "<sup>36</sup> ಅವರು ಆ ಜನರ ಗುಂಪನ್ನು ಬಿಟ್ಟು ಆತನನ್ನು ಅದೇ ದೋಣಿಯಲ್ಲಿ ಹಾಗೆಯೇ ಕರಕೊಂಡುಹೋದರು.",
			"37": "<sup>37</sup> ಬೇರೆ ದೋಣಿಗಳೂ ಆತನ ಸಂಗಡ ಇದ್ದವು. ತರುವಾಯ ದೊಡ್ಡ ಬಿರುಗಾಳಿ ಎದ್ದು ತೆರೆಗಳು ಆ ದೋಣಿಗೆ ಬಡಿದು ಒಳಗೆ ನುಗ್ಗಿದ್ದರಿಂದ ಆ ದೋಣಿ ಆಗಲೇ ತುಂಬುವದಕ್ಕೆ ಬಂದಿತ್ತು.",
			"38": "<sup>38</sup> ಆತನು ದೋಣಿಯ ಹಿಂಭಾಗದಲ್ಲಿ ತಲೆಗಿಂಬನ್ನು ಒರಗಿ ನಿದ್ದೆ ಮಾಡುತ್ತಿದ್ದನು. ಅವರು ಆತನನ್ನು ಎಬ್ಬಿಸಿ - ಗುರುವೇ, ನಾವು ಮುಳುಗಿಹೋಗುವದರಲ್ಲಿ ನಿನಗೆ ಚಿಂತೆಯಿಲ್ಲವೇ ಎಂದು ಕೇಳಲು",
			"39": "<sup>39</sup> ಆತನು ಎದ್ದು ಗಾಳಿಯನ್ನು ಗದರಿಸಿ ಸಮುದ್ರಕ್ಕೆ - ಸುಮ್ಮನಿರು, ಮೊರೆಯಬೇಡ ಎಂದು ಅಪ್ಪಣೆಕೊಟ್ಟನು. ಕೊಡುತ್ತಲೆ ಗಾಳಿ ನಿಂತು ಹೋಗಿ ಎಲ್ಲಾ ಶಾಂತವಾಯಿತು.",
			"40": "<sup>40</sup> ತರುವಾಯ ಆತನು ಅವರನ್ನು - ಯಾಕೆ ಧೈರ್ಯಗೆಡುತ್ತೀರಿ? ಇನ್ನೂ ನಿಮಗೆ ನಂಬಿಕೆಯಿಲ್ಲವೇ ಎಂದು ಕೇಳಲು ಅವರು ಬಹು ಭಯಪಟ್ಟು -",
			"41": "<sup>41</sup> ಈತನು ಯಾರಿರಬಹುದು? ಗಾಳಿಯೂ ಸಮುದ್ರವೂ ಸಹ ಈತನು ಹೇಳಿದ ಹಾಗೆ ಕೇಳುತ್ತವಲ್ಲಾ ಎಂದು ತಮ್ಮ ತಮ್ಮೊಳಗೆ ಮಾತಾಡಿಕೊಂಡರು."
		},
		"5": {
			"1": "<sup>1</sup> ಆಮೇಲೆ ಅವರು ಸಮುದ್ರದ ಆಚೇದಡಕ್ಕೆ ಗೆರಸೇನರ ಸೀಮೆಗೆ ಮುಟ್ಟಿದರು.",
			"2": "<sup>2</sup> ಆತನು ದೋಣಿಯಿಂದ ಇಳಿದುಬಂದ ಕೂಡಲೆ ದೆವ್ವಹಿಡಿದವನೊಬ್ಬನು ಸಮಾಧಿಯ ಗವಿಗಳೊಳಗಿಂದ ಆತನೆದುರಿಗೆ ಬಂದನು.",
			"3": "<sup>3</sup> ಅವನಿಗೆ ಸಮಾಧಿಯ ಗವಿಗಳೇ ವಾಸಸ್ಥಳವಾಗಿದ್ದವು. ಅವನನ್ನು ಅನೇಕಸಾರಿ ಬೇಡಿಗಳಿಂದಲೂ ಸರಪಣಿಗಳಿಂದಲೂ ಕಟ್ಟಿದ್ದರೂ ಅವನು ಸರಪಣಿಗಳನ್ನು ಕಿತ್ತು ಬೇಡಿಗಳನ್ನು ಮುರಿದುಹಾಕಿದ್ದನು; ಅವನನ್ನು ಇನ್ನು ಯಾರೂ ಸರಪಣಿಯಿಂದಾದರೂ ಕಟ್ಟಲಾರದೆ ಹೋದರು; ಅವನನ್ನು ಹತೋಟಿಗೆ ತರುವದಕ್ಕೆ ಯಾರಿಂದಲೂ ಆಗಲಿಲ್ಲ.",
			"5": "<sup>5</sup> ಅವನು ನಿತ್ಯ ರಾತ್ರಿಹಗಲು ಸಮಾಧಿಯ ಗವಿಗಳಲ್ಲಿಯೂ ಗುಡ್ಡಗಳಲ್ಲಿಯೂ ಆರ್ಭಟಿಸುತ್ತಾ ಕಲ್ಲುಗಳಿಂದ ತನ್ನನ್ನು ಕಡಿದುಕೊಳ್ಳುತ್ತಾ ಇದ್ದನು.",
			"6": "<sup>6</sup> ಅವನು ಯೇಸುವನ್ನು ದೂರದಿಂದ ಕಂಡು ಓಡಿಬಂದು ಆತನಿಗೆ ಅಡ್ಡಬಿದ್ದು ಮಹಾಶಬ್ದದಿಂದ ಆರ್ಭಟಿಸಿ -",
			"7": "<sup>7</sup> ಯೇಸುವೇ, ಪರಾತ್ಪರನಾದ ದೇವರ ಮಗನೇ, ನನ್ನ ಗೊಡವೆ ನಿನಗೇಕೆ? ದೇವರಾಣೆ, ನನ್ನನ್ನು ಕಾಡಬೇಡ ಎಂದು ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ಯಾಕಂದರೆ - ಎಲಾ ದೆವ್ವಾ, ಇವನನ್ನು ಬಿಟ್ಟುಹೋಗು ಎಂದು ಯೇಸು ಹೇಳಿದ್ದನು.",
			"9": "<sup>9</sup> ಮತ್ತು - ನಿನ್ನ ಹೆಸರೇನೆಂದು ಅವನನ್ನು ಕೇಳಿದ್ದಕ್ಕೆ ಅವನು - ನನ್ನ ಹೆಸರು ದಂಡು; ಯಾಕಂದರೆ ನಾವು ಬಹು ಮಂದಿ ಇದ್ದೇವೆ ಎಂದು ಹೇಳಿ -",
			"10": "<sup>10</sup> ನಮ್ಮನ್ನು ಈ ಸೀಮೆಯಿಂದ ಹೊರಡಿಸಬೇಡವೆಂದು ಆತನನ್ನು ಬಹಳವಾಗಿ ಬೇಡಿಕೊಂಡನು.",
			"11": "<sup>11</sup> ಅಲ್ಲಿಯ ಗುಡ್ಡದಲ್ಲಿ ಹಂದಿಗಳ ದೊಡ್ಡ ಹಿಂಡು ಮೇಯುತ್ತಿತ್ತು.",
			"12": "<sup>12</sup> ಆ ದೆವ್ವಗಳು - ಆ ಹಂದಿಗಳೊಳಗೆ ಸೇರಿಕೊಳ್ಳುವದಕ್ಕೆ ನಮ್ಮನ್ನು ಕಳುಹಿಸಿಕೊಡು ಎಂದು ಬೇಡಿಕೊಂಡವು.",
			"13": "<sup>13</sup> ಆತನು - ಆಗಲಿ ಎಂದು ಅವುಗಳಿಗೆ ಅಪ್ಪಣೆಕೊಡಲು ಆ ದೆವ್ವಗಳು ಹೊರಗೆ ಬಂದು ಹಂದಿಗಳೊಳಗೆ ಹೊಕ್ಕವು; ಆ ಗುಂಪು ಓಡಿ ಕಡಿದಾದ ಸ್ಥಳದಿಂದ ಸಮುದ್ರದೊಳಗೆ ಬಿದ್ದು ಅಲ್ಲಿ ಉಸುರುಕಟ್ಟಿ ಸತ್ತಿತು. ಹೆಚ್ಚು ಕಡಿಮೆ ಎರಡು ಸಾವಿರ ಹಂದಿಗಳಿದ್ದವು.",
			"14": "<sup>14</sup> ಅವುಗಳನ್ನು ಮೇಯಿಸುವವರು ಓಡಿಹೋಗಿ ಆ ಊರಲ್ಲಿಯೂ ಹಳ್ಳಿಗಳಲ್ಲಿಯೂ ತಿಳಿಸಲು ಜನರು ನಡೆದ ಸಂಗತಿ ಏನೆಂದು ನೋಡುವದಕ್ಕೆ ಹೊರಟು ಯೇಸುವಿದ್ದಲ್ಲಿಗೆ ಬಂದು ಆ ದೆವ್ವಹಿಡಿದಿದ್ದವನು,",
			"15": "<sup>15</sup> ಅಂದರೆ ದೆವ್ವಗಳ ದಂಡಿನಿಂದ ಹಿಡಿಯಲ್ಪಟ್ಟಿದ್ದವನು, ಬಟ್ಟೆಗಳನ್ನು ಹಾಕಿಕೊಂಡು ಸ್ವಸ್ಥಬುದ್ಧಿಯಿಂದ ಕೂತಿರುವದನ್ನು ನೋಡಿ ಹೆದರಿದರು.",
			"16": "<sup>16</sup> ನಡೆದ ಸಂಗತಿಯನ್ನು ನೋಡಿದ್ದವರು ಆ ದೆವ್ವಹಿಡಿದಿದ್ದವನಿಗೆ ಹೀಗೆ ಹೀಗಾಯಿತೆಂಬದನ್ನೂ ಹಂದಿಗಳ ವಿಷಯವನ್ನೂ ಅವರಿಗೆ ವಿವರಿಸಲು",
			"17": "<sup>17</sup> ಅವರು ತಮ್ಮ ಸೀಮೆಯನ್ನು ಬಿಟ್ಟು ಹೋಗಬೇಕೆಂದು ಯೇಸುವನ್ನು ಬೇಡಿಕೊಂಡರು.",
			"18": "<sup>18</sup> ಆತನು ದೋಣಿಯನ್ನು ಹತ್ತುವಾಗ ಆ ದೆವ್ವಹಿಡಿದಿದ್ದವನು - ನಾನು ನಿನ್ನ ಬಳಿಯಲ್ಲಿಯೇ ಇರುತ್ತೇನೆಂದು ಆತನನ್ನು ಬೇಡತೊಡಗಿದನು.",
			"19": "<sup>19</sup> ಆದರೆ ಆತನು ಅವನನ್ನು ಇರಗೊಡದೆ ಅವನಿಗೆ - ನೀನು ನಿನ್ನ ಮನೆಗೂ ನಿನ್ನ ಜನರ ಬಳಿಗೂ ಹೋಗಿ ಸ್ವಾಮಿಯು ನಿನ್ನಲ್ಲಿ ಕರುಣೆಯಿಟ್ಟು ನಿನಗೆ ಎಂಥೆಂಥ ಉಪಕಾರಗಳನ್ನು ಮಾಡಿದ್ದಾನೋ ಅದನ್ನು ಹೇಳು ಅಂದನು.",
			"20": "<sup>20</sup> ಅವನು ಹೊರಟುಹೋಗಿ ಯೇಸು ತನಗೆ ಮಾಡಿದ ಉಪಕಾರಗಳನ್ನು ದೆಕಪೊಲಿ ಎಂಬ ಸೀಮೆಯಲ್ಲಿ ಸಾರುವದಕ್ಕೆ ಪ್ರಾರಂಭಿಸಿದನು; ಎಲ್ಲರೂ ಆಶ್ಚರ್ಯಪಟ್ಟರು.",
			"21": "<sup>21</sup> ಯೇಸು ತಿರಿಗಿ ದೋಣಿಯಲ್ಲಿ ಈಚೇದಡಕ್ಕೆ ಬಂದಾಗ ಜನರು ಬಹು ದೊಡ್ಡ ಗುಂಪಾಗಿ ಆತನ ಬಳಿಗೆ ಬಂದರು; ಆತನು ಸಮುದ್ರದ ದಡದಲ್ಲಿದ್ದನು.",
			"22": "<sup>22</sup> ಆಗ ಸಭಾಮಂದಿರದ ಅಧಿಕಾರಿಗಳಲ್ಲಿ ಒಬ್ಬನಾದ ಯಾಯೀರನೆಂಬವನು ಬಂದು ಯೇಸುವನ್ನು ಕಂಡು ಆತನ ಪಾದಗಳಿಗೆ ಬಿದ್ದು -",
			"23": "<sup>23</sup> ನನ್ನ ಚಿಕ್ಕ ಮಗಳು ಈಗ ಸಾಯುತ್ತಾಳೆ; ಆಕೆಯು ವಾಸಿಯಾಗಿ ಬದುಕಿಕೊಳ್ಳುವಂತೆ ನೀನು ಬಂದು ಆಕೆಯ ಮೇಲೆ ಕೈಯಿಡಬೇಕು ಎಂದು ಆತನನ್ನು ಬಹಳವಾಗಿ ಬೇಡಿಕೊಂಡನು.",
			"24": "<sup>24</sup> ಆತನು ಅವನ ಸಂಗಡ ಹೋದನು; ಮತ್ತು ಬಹು ಜನರು ಆತನನ್ನು ನೂಕಿಕೊಂಡು ಹಿಂದೆ ಹೋದರು.",
			"25": "<sup>25</sup> ಆಗ ಹನ್ನೆರಡು ವರುಷದಿಂದ ರಕ್ತ ಕುಸುಮರೋಗವಿದ್ದ ಒಬ್ಬ ಹೆಂಗಸು ಬಂದಳು.",
			"26": "<sup>26</sup> ಆಕೆಯು ಅನೇಕ ವೈದ್ಯರಿಂದ ಬಹು ಕಷ್ಟವನ್ನು ಅನುಭವಿಸಿ ಕೈಯಲ್ಲಿದ್ದದ್ದನ್ನೆಲ್ಲಾ ಕಳಕೊಂಡರೂ ರೋಗವು ಹೆಚ್ಚುತ್ತಾ ಬಂದದ್ದೇ ಹೊರತು ಮತ್ತೇನು ಪ್ರಯೋಜನವನ್ನೂ ಹೊಂದಿರಲಿಲ್ಲ.",
			"27": "<sup>27</sup> ಆ ಹೆಂಗಸು ಯೇಸುವಿನ ಸಮಾಚಾರವನ್ನು ಕೇಳಿ - ನಾನು ಆತನ ಉಡುಪನ್ನು ಮುಟ್ಟಿದರೆ ಸಾಕು, ನೆಟ್ಟಗಾಗುವೆನು ಎಂದು ಆಲೋಚಿಸಿ",
			"29": "<sup>29</sup> ಗುಂಪಿನಲ್ಲಿ ಹಿಂದಿನಿಂದ ಬಂದು ಆತನ ಉಡುಪನ್ನು ಮುಟ್ಟಿದಳು. ಮುಟ್ಟಿದ ಕೂಡಲೆ ಆಕೆಗೆ ರಕ್ತಹರಿಯುವದು ನಿಂತುಹೋದದರಿಂದ ಆಕೆಯು - ನನ್ನನ್ನು ಕಾಡಿದ ರೋಗವು ಹೋಗಿ ನನಗೆ ಗುಣವಾಯಿತು ಎಂದು ತನ್ನೊಳಗೆ ತಿಳುಕೊಂಡಳು.",
			"30": "<sup>30</sup> ಆ ಕ್ಷಣವೇ ಯೇಸು ತನ್ನಿಂದ ಶಕ್ತಿಯು ಹೊರಟಿತೆಂದು ತನ್ನಲ್ಲಿ ತಿಳುಕೊಂಡು ಗುಂಪಿನಲ್ಲಿ ಹಿಂತಿರುಗಿ - ನನ್ನ ಉಡುಪನ್ನು ಯಾರು ಮುಟ್ಟಿದರು? ಎಂದು ಕೇಳಲು",
			"31": "<sup>31</sup> ಆತನ ಶಿಷ್ಯರು ಆತನಿಗೆ - ಜನರು ನಿನ್ನನ್ನು ನೂಕುವದನ್ನು ಕಂಡೂ ಕಂಡು ನನ್ನನ್ನು ಯಾರು ಮುಟ್ಟಿದರೆಂದು ಕೇಳುತ್ತೀಯಲ್ಲಾ ಎಂದು ಹೇಳಿದರು.",
			"32": "<sup>32</sup> ಆದರೆ ಆತನು ಈ ಕೆಲಸ ಮಾಡಿದವಳನ್ನು ಕಾಣಬೇಕೆಂದು ಸುತ್ತಲು ನೋಡುತ್ತಾ ಇರಲಾಗಿ",
			"33": "<sup>33</sup> ಆ ಹೆಂಗಸು ತನಗಾದದ್ದನ್ನು ತಿಳುಕೊಂಡು ಅಂಜಿ ನಡುಗುತ್ತಾ ಬಂದು ಆತನಿಗೆ ಅಡ್ಡ ಬಿದ್ದು ಇದ್ದ ಸಂಗತಿಯನ್ನೆಲ್ಲಾ ಹೇಳಿದಳು.",
			"34": "<sup>34</sup> ಆತನು ಆಕೆಗೆ - ಮಗಳೇ, ನಿನ್ನ ನಂಬಿಕೆಯೇ ನಿನ್ನನ್ನು ಸ್ವಸ್ಥಮಾಡಿತು; ಸಮಾಧಾನದಿಂದ ಹೋಗು; ನಿನ್ನನ್ನು ಕಾಡಿದ ರೋಗವು ಹೋಗಿ ನಿನಗೆ ಗುಣವಾಗಲಿ ಎಂದು ಹೇಳಿದನು.",
			"35": "<sup>35</sup> ಆತನು ಇನ್ನೂ ಮಾತಾಡುತ್ತಿರುವಲ್ಲಿ ಸಭಾಮಂದಿರದ ಅಧಿಕಾರಿಯ ಕಡೆಯವರು ಬಂದು - ನಿನ್ನ ಮಗಳು ತೀರಿಹೋದಳು; ಇನ್ನೇಕೆ ಗುರುವಿಗೆ ತೊಂದರೆ ಕೊಡುವದು? ಅಂದರು.",
			"36": "<sup>36</sup> ಅವರು ಆಡಿದ ಮಾತನ್ನು ಯೇಸು ಲಕ್ಷ್ಯಮಾಡದೆ ಸಭಾಮಂದಿರದ ಅಧಿಕಾರಿಗೆ - ಅಂಜಬೇಡ, ನಂಬಿಕೆ ಮಾತ್ರ ಇರಲಿ ಎಂದು ಹೇಳಿದನು.",
			"37": "<sup>37</sup> ಮತ್ತು ಆತನು ಪೇತ್ರ, ಯಾಕೋಬ, ಯಾಕೋಬನ ತಮ್ಮನಾದ ಯೋಹಾನ ಇವರನ್ನೇ ಹೊರತು ಬೇರೆ ಯಾರನ್ನೂ ತನ್ನೊಂದಿಗೆ ಬರಗೊಡಲಿಲ್ಲ.",
			"38": "<sup>38</sup> ಅವರು ಆ ಸಭಾಮಂದಿರದ ಅಧಿಕಾರಿಯ ಮನೆಗೆ ಬಂದಾಗ ಆತನು ಗದ್ದಲವನ್ನೂ ಕೆಲವರು ಬಹಳವಾಗಿ ಅಳುವದನ್ನೂ ಗೋಳಾಡುವದನ್ನೂ ಕಂಡನು.",
			"39": "<sup>39</sup> ಆತನು ಒಳಕ್ಕೆ ಹೋಗಿ - ನೀವು ಗದ್ದಲ ಮಾಡುವದೂ ಅಳುವದೂ ಯಾಕೆ? ಹುಡುಗಿ ಸತ್ತಿಲ್ಲ;",
			"40": "<sup>40</sup> ನಿದ್ದೆಮಾಡುತ್ತಾಳೆ ಅನ್ನಲು ಅವರು ಆತನನ್ನು ಹಾಸ್ಯಮಾಡಿದರು. ಆದರೆ ಆತನು ಎಲ್ಲರನ್ನು ಹೊರಕ್ಕೆ ಕಳುಹಿಸಿ ಆ ಹುಡುಗಿಯ ತಂದೆತಾಯಿಗಳನ್ನೂ ತನ್ನೊಂದಿಗಿದ್ದವರನ್ನೂ ಹುಡುಗಿಯಿದ್ದಲ್ಲಿಗೆ ಕರಕೊಂಡು ಹೋಗಿ ಅವಳ ಕೈಹಿಡಿದು -",
			"41": "<sup>41</sup> ತಲಿಥಾ ಕೂಮ್ ಅಂದನು. ಆ ಮಾತಿಗೆ - ಅಮ್ಮಣ್ಣೀ, ಏಳನ್ನುತ್ತೇನೆ ಎಂದರ್ಥ.",
			"42": "<sup>42</sup> ಕೂಡಲೆ ಆ ಹುಡುಗಿ ಎದ್ದು ನಡೆದಾಡಿದಳು; ಆಕೆಯು ಹನ್ನೆರಡು ವರುಷ ವಯಸ್ಸಿನವಳು.",
			"43": "<sup>43</sup> ಅವರು ಬಹಳ ಆಶ್ಚರ್ಯದಿಂದ ಬೆರಗಾದರು. ಆಮೇಲೆ ಆತನು ಅವರಿಗೆ - ಇದು ಯಾರಿಗೂ ತಿಳಿಯಬಾರದೆಂದು ಬಹು ಖಂಡಿತವಾಗಿ ಹೇಳಿ ಆ ಹುಡುಗಿಗೆ ಊಟಮಾಡಿಸಿರಿ ಅಂದನು."
		},
		"6": {
			"1": "<sup>1</sup> ಆತನು ಅಲ್ಲಿಂದ ಹೊರಟು ತನ್ನ ಊರಿಗೆ ಬಂದನು; ಶಿಷ್ಯರು ಆತನ ಹಿಂದೆಯೇ ಬಂದರು.",
			"2": "<sup>2</sup> ಸಬ್ಬತ್ ದಿನ ಬಂದಾಗ ಆತನು ಸಭಾಮಂದಿರಕ್ಕೆ ಹೋಗಿ ಉಪದೇಶ ಮಾಡುವದಕ್ಕೆ ಪ್ರಾರಂಭಿಸಿದನು. ಎಲ್ಲರು ಕೇಳಿ ಅತ್ಯಾಶ್ಚರ್ಯಪಟ್ಟು - ಇವನಿಗೆ ಈ ಮಾತುಗಳು ಎಲ್ಲಿಂದ ಬಂದಿದ್ದಾವು? ಇವನು ಪಡೆದಿರುವ ಈ ಜ್ಞಾನವೇನು? ಇವನ ಕೈಯಿಂದ ಇಂಥ ಮಹತ್ಕಾರ್ಯಗಳಾಗುವದೇನು?",
			"3": "<sup>3</sup> ಇವನು ಆ ಬಡಗಿಯಲ್ಲವೇ. ಇವನು ಮರಿಯಳ ಮಗನಲ್ಲವೇ. ಯಾಕೋಬ ಯೋಸೆ ಯೂದ ಸೀಮೋನ ಇವರ ಅಣ್ಣನಲ್ಲವೇ. ಇವನ ತಂಗಿಯರು ಇಲ್ಲಿ ನಮ್ಮಲ್ಲಿ ಇದ್ದಾರಲ್ಲವೇ ಎಂದು ಮಾತಾಡಿಕೊಂಡು ಆತನ ವಿಷಯವಾಗಿ ಬೇಸರಗೊಂಡರು.",
			"4": "<sup>4</sup> ಯೇಸು ಅವರಿಗೆ - ಪ್ರವಾದಿಯು ಬೇರೆ ಎಲ್ಲಿದ್ದರೂ ಅವನಿಗೆ ಮರ್ಯಾದೆ ಉಂಟು; ಆದರೆ ಸ್ವದೇಶದಲ್ಲಿಯೂ ಸ್ವಂತ ಜನರಲ್ಲಿಯೂ ಸ್ವಂತ ಮನೆಯಲ್ಲಿಯೂ ಮಾತ್ರ ಮರ್ಯಾದೆಯಿಲ್ಲ ಎಂದು ಹೇಳಿದನು.",
			"5": "<sup>5</sup> ಆತನು ಅಲ್ಲಿ ಕೆಲವು ಮಂದಿ ರೋಗಿಗಳ ಮೇಲೆ ಕೈಯಿಟ್ಟು ಸ್ವಸ್ಥಮಾಡಿದ್ದೇ ಹೊರತು ಬೇರೆ ಯಾವ ಮಹತ್ಕಾರ್ಯವನ್ನೂ ಮಾಡುವದಕ್ಕಾಗಲಿಲ್ಲ.",
			"6": "<sup>6</sup> ಅವರು ತನ್ನನ್ನು ನಂಬದೆ ಹೋದದ್ದಕ್ಕೆ ಆತನು ಆಶ್ಚರ್ಯಪಟ್ಟನು.",
			"7": "<sup>7</sup> ಆತನು ಹಳ್ಳಿಪಳ್ಳಿಗಳನ್ನು ಸುತ್ತಿಕೊಂಡು ಉಪದೇಶ ಮಾಡುತ್ತಾ ಬಂದನು. ಮತ್ತು ಹನ್ನೆರಡು ಮಂದಿ ಶಿಷ್ಯರನ್ನು ಹತ್ತಿರಕ್ಕೆ ಕರೆದು ಅವರನ್ನು ಇಬ್ಬರಿಬ್ಬರಾಗಿ ಕಳುಹಿಸಲಾರಂಭಿಸಿದನು. ಕಳುಹಿಸುವಾಗ ಅವರಿಗೆ ದೆವ್ವಗಳ ಮೇಲೆ ಅಧಿಕಾರಕೊಟ್ಟು -",
			"8": "<sup>8</sup> ಕೋಲು ಹೊರತು ದಾರಿಗೆ ಏನೂ ತಕ್ಕೊಂಡು ಹೋಗಬೇಡಿರಿ; ಬುತ್ತಿ, ಹಸಿಬೆ, ಹಮ್ಮೀಣಿಯಲ್ಲಿ ದುಡ್ಡು ಬೇಡ,",
			"9": "<sup>9</sup> ಕೆರಗಳನ್ನು ಮೆಟ್ಟಿಕೊಳ್ಳಬಹುದು. ಎರಡು ಅಂಗಿಗಳನ್ನು ತೊಟ್ಟುಕೊಳ್ಳಬೇಡಿರಿ ಎಂದು ಅಪ್ಪಣೆಕೊಟ್ಟನು.",
			"10": "<sup>10</sup> ಇದಲ್ಲದೆ ಆತನು ಅವರಿಗೆ - ನೀವು ಎಲ್ಲಿಯಾದರೂ ಒಂದು ಮನೆಯಲ್ಲಿ ಇಳುಕೊಂಡಾಗ ಆ ಸ್ಥಳದಿಂದ ಹೊರಡುವ ತನಕ ಅಲ್ಲೇ ಇರಿ.",
			"11": "<sup>11</sup> ಮತ್ತು ಯಾವ ಸ್ಥಳದವರಾದರೂ ನಿಮ್ಮನ್ನು ಸೇರಿಸಿಕೊಳ್ಳದೆಯೂ ನೀವು ಹೇಳುವದನ್ನು ಕೇಳದೆಯೂ ಹೋದರೆ ಆ ಸ್ಥಳದಿಂದ ಹೊರಟುಹೋಗುವಾಗ ನಿಮ್ಮ ಕಾಲಿಗೆ ಹತ್ತಿದ ದೂಳನ್ನು ಝಾಡಿಸಿಬಿಡಿರಿ; ಅದು ಅವರಿಗೆ ಸಾಕ್ಷಿಯಾಗಿರಲಿ ಎಂದು ಹೇಳಿದನು.",
			"12": "<sup>12</sup> ಅವರು ಹೊರಟುಹೋಗಿ ಜನರಿಗೆ - ದೇವರ ಕಡೆಗೆ ತಿರುಗಿಕೊಳ್ಳಬೇಕೆಂದು ಸಾರಿ",
			"13": "<sup>13</sup> ಅನೇಕ ದೆವ್ವಗಳನ್ನು ಬಿಡಿಸಿ ಅನೇಕ ರೋಗಿಗಳಿಗೆ ಎಣ್ಣೆಹಚ್ಚಿ ವಾಸಿಮಾಡಿದರು.",
			"14": "<sup>14</sup> ಮತ್ತು ಅರಸನಾದ ಹೆರೋದನು [ಯೇಸುವಿನ ಸುದ್ದಿಯನ್ನು] ಕೇಳಿದನು. ಯಾಕಂದರೆ ಆತನ ಹೆಸರು ಪ್ರಸಿದ್ಧವಾಯಿತು, ಮತ್ತು ಜನರು ಆತನ ವಿಷಯದಲ್ಲಿ - ಸ್ನಾನಿಕನಾದ ಯೋಹಾನನೇ ತಿರಿಗಿ ಬದುಕಿಬಂದಿದ್ದಾನೆ; ಆದದರಿಂದ ಮಹತ್ಕಾರ್ಯಗಳನ್ನು ನಡಿಸುವ ಶಕ್ತಿಗಳು ಅವನಲ್ಲಿ ಅವೆ ಎಂದು ಹೇಳುತ್ತಿದ್ದರು.",
			"15": "<sup>15</sup> ಕೆಲವರು - ಇವನು ಎಲೀಯನೆಂತಲೂ ಕೆಲವರು - ಇವನು ಹಿಂದಿನ ಪ್ರವಾದಿಗಳಂತಿರುವ ಒಬ್ಬ ಪ್ರವಾದಿಯೆಂತಲೂ ಹೇಳಿದರು.",
			"16": "<sup>16</sup> ಆದರೆ ಹೆರೋದನು ಆತನ ಸುದ್ದಿಯನ್ನು ಕೇಳಿ - ನಾನು ತಲೆಹೊಯಿಸಿದ ಯೋಹಾನನೇ ತಿರಿಗಿ ಬದುಕಿ ಬಂದಿದ್ದಾನೆ ಅಂದನು.",
			"17": "<sup>17</sup> ಹೆರೋದನು ತಾನು ಇಟ್ಟುಕೊಂಡಿದ್ದ ಹೆರೋದ್ಯಳ ನಿಮಿತ್ತ ಯೋಹಾನನನ್ನು ಹಿಡಿತರಿಸಿ ಸೆರೆಯಲ್ಲಿ ಕಟ್ಟಿ ಹಾಕಿಸಿದ್ದನು. ಆ ಹೆರೋದ್ಯಳು ಹೆರೋದನ ಅಣ್ಣನಾದ ಫಿಲಿಪ್ಪನ ಹೆಂಡತಿ.",
			"18": "<sup>18</sup> ಯೋಹಾನನು ಹೆರೋದನಿಗೆ - ನಿನ್ನ ಅಣ್ಣನ ಹೆಂಡತಿಯನ್ನು ಇಟ್ಟುಕೊಂಡಿರುವದು ಧರ್ಮವಲ್ಲವೆಂದು ಹೇಳುತ್ತಿದ್ದನು.",
			"19": "<sup>19</sup> ಇದಕ್ಕೆ ಹೆರೋದ್ಯಳು ಅವನ ಮೇಲೆ ದ್ವೇಷವಿಟ್ಟುಕೊಂಡು ಅವನನ್ನು ಕೊಲ್ಲಿಸಬೇಕೆಂದಿದ್ದರೂ ಆಗದೆ ಹೋಯಿತು.",
			"20": "<sup>20</sup> ಹೆರೋದನು ಯೋಹಾನನನ್ನು ನೀತಿವಂತನೆಂದೂ ದೇವಭಕ್ತನೆಂದೂ ತಿಳಿದು ಅಂಜಿಕೊಂಡು ಅವನಿಗೆ ಯಾವ ಅಪಾಯವೂ ಬಾರದಂತೆ ಇಟ್ಟಿದ್ದನು. ಇದಲ್ಲದೆ ಯೋಹಾನನು ಹೇಳಿದ್ದನ್ನು ಅವನು ಕೇಳಿದಾಗ ಮನಸ್ಸಿನಲ್ಲಿ ಬಹು ಗಲಿಬಿಲಿ ಹುಟ್ಟಿದರೂ ಅವನ ಮಾತನ್ನು ಸಂತೋಷದಿಂದ ಕೇಳುತ್ತಿದ್ದನು.",
			"21": "<sup>21</sup> ಹೀಗಿರಲಾಗಿ ಅವಳಿಗೆ ಅನುಕೂಲವಾದ ದಿವಸ ಬಂತು; ಹೇಗಂದರೆ ಹೆರೋದನು ತಾನು ಹುಟ್ಟಿದ ದಿವಸದಲ್ಲಿ ಪ್ರಭುಗಳಿಗೂ ಸಹಸ್ರಾಧಿಪತಿಗಳಿಗೂ ಗಲಿಲಾಯ ಸೀಮೆಯ ಮುಖ್ಯಸ್ಥರಿಗೂ ಔತಣವನ್ನು ಮಾಡಿಸಿದಾಗ",
			"22": "<sup>22</sup> ಹೆರೋದ್ಯಳ ಮಗಳು ಒಳಕ್ಕೆ ಬಂದು ನಾಟ್ಯವಾಡಿ ಹೆರೋದನನ್ನೂ ಅವನ ಸಂಗಡ ಪಂಙ್ತಿಯಲ್ಲಿ ಊಟಕ್ಕೆ ಕೂತಿದ್ದವರನ್ನೂ ಮೆಚ್ಚಿಸಿದಳು. ಅರಸನು ಆ ಹುಡುಗಿಗೆ - ಬೇಕಾದದ್ದನ್ನು ಕೇಳಿಕೋ, ಕೊಡುತ್ತೇನೆ.",
			"23": "<sup>23</sup> ನೀನು ನನ್ನನ್ನು ಏನು ಕೇಳಿಕೊಂಡರೂ ಸರಿ, ನನ್ನ ರಾಜ್ಯದಲ್ಲಿ ಅರ್ಧ ರಾಜ್ಯವನ್ನು ಕೇಳಿದರೂ, ನಿನಗೆ ಕೊಡುತ್ತೇನೆ ಎಂದು ಆಣೆಯಿಟ್ಟುಕೊಂಡು ಹೇಳಿದನು.",
			"24": "<sup>24</sup> ಆಗ ಅವಳು ಹೊರಕ್ಕೆ ಹೋಗಿ - ನಾನು ಏನು ಕೇಳಿಕೊಳ್ಳಲಿ? ಎಂದು ತನ್ನ ತಾಯಿಯನ್ನು ಕೇಳಲಾಗಿ ಅವಳು - ಸ್ನಾನಿಕನಾದ ಯೋಹಾನನ ತಲೆಯನ್ನು ಕೇಳಿಕೋ ಅಂದಳು.",
			"25": "<sup>25</sup> ಕೂಡಲೆ ಅವಳು ಅರಸನ ಬಳಿಗೆ ಅವಸರದಿಂದ ಬಂದು - ಈ ಕ್ಷಣದಲ್ಲೇ ಸ್ನಾನಿಕನಾದ ಯೋಹಾನನ ತಲೆಯನ್ನು ಪರಾತಿನಲ್ಲಿ ನನಗೆ ತರಿಸಿಕೊಡಬೇಕೆಂದೇ ನನ್ನ ಕೋರಿಕೆ ಎಂದು ಅರಿಕೆ ಮಾಡಿಕೊಂಡಳು.",
			"26": "<sup>26</sup> ಅದಕ್ಕೆ ಅರಸನು ಬಹು ದುಃಖಪಟ್ಟರೂ ತಾನು ಇಟ್ಟುಕೊಂಡ ಆಣೆಗಳ ನಿಮಿತ್ತವಾಗಿಯೂ ತನ್ನ ಪಂಙ್ತಿಯಲ್ಲಿ ಕೂತಿದ್ದವರ ನಿಮಿತ್ತವಾಗಿಯೂ ಅವಳನ್ನು ಅಸಡ್ಡೆ ಮಾಡುವದಕ್ಕೆ ಮನಸ್ಸಿಲ್ಲದೆ",
			"27": "<sup>27</sup> ಕೂಡಲೆ ತನ್ನ ಮೈಗಾವಲಿನ ಸಿಪಾಯಿಗಳಲ್ಲಿ ಒಬ್ಬನಿಗೆ - ನೀನು ಹೋಗಿ ಯೋಹಾನನ ತಲೆಯನ್ನು ತಂದುಕೊಡು ಎಂದು ಅಪ್ಪಣೆಕೊಟ್ಟು ಕಳುಹಿಸಿದನು;",
			"28": "<sup>28</sup> ಅವನು ಹೋಗಿ ಸೆರೆಮನೆಯಲ್ಲಿ ಅವನ ತಲೆಯನ್ನು ಹೊಯಿದು ಪರಾತಿನಲ್ಲಿ ತಂದು ಆ ಹುಡುಗಿಗೆ ಕೊಟ್ಟನು. ಆ ಹುಡುಗಿ ಅದನ್ನು ತನ್ನ ತಾಯಿಗೆ ಕೊಟ್ಟಳು.",
			"29": "<sup>29</sup> ಯೋಹಾನನ ಶಿಷ್ಯರು ಈ ಸಂಗತಿಗಳನ್ನು ಕೇಳಿದಾಗ ಅವರು ಬಂದು ಅವನ ಶವವನ್ನು ಹೊತ್ತುಕೊಂಡು ಹೋಗಿ ಸಮಾಧಿಯಲ್ಲಿ ಇಟ್ಟರು.",
			"30": "<sup>30</sup> ಇತ್ತಲಾಗಿ ಅಪೊಸ್ತಲರು ಆತನ ಬಳಿಗೆ ಕೂಡಿ ಬಂದು ತಾವು ಮಾಡಿದ್ದನ್ನೂ ಉಪದೇಶಿಸಿದ್ದನ್ನೂ ಎಲ್ಲಾ ಆತನಿಗೆ ತಿಳಿಸಲು ಆತನು ಅವರಿಗೆ -",
			"31": "<sup>31</sup> ನೀವು ಮಾತ್ರ ವಿಂಗಡವಾಗಿ ಅಡವಿಗೆ ಬಂದು ಸ್ವಲ್ಪ ದಣುವಾರಿಸಿಕೊಳ್ಳಿರಿ ಎಂದು ಹೇಳಿದನು. ಯಾಕಂದರೆ ಬಹಳ ಮಂದಿ ಬರುತ್ತಾ ಹೋಗುತ್ತಾ ಇದ್ದದರಿಂದ ಅವರಿಗೆ ಊಟಮಾಡುವದಕ್ಕೂ ಅವಕಾಶ ಸಿಕ್ಕಲಿಲ್ಲ.",
			"32": "<sup>32</sup> ಆಗ ಅವರು ದೋಣಿಯಲ್ಲಿ ಹೊರಟು ವಿಂಗಡವಾಗಿ ಅಡವಿಗೆ ಹೋದರು.",
			"33": "<sup>33</sup> ಅವರು ಹೋಗುವದನ್ನು ಬಹು ಜನರು ಕಂಡು ಅವರ ಗುರುತನ್ನು ಹಿಡಿದು ಎಲ್ಲಾ ಊರುಗಳಿಂದ ಕಾಲುನಡಿಗೆಯಾಗಿ ಓಡಿ ಅವರಿಗಿಂತ ಮುಂಚೆ ಅಲ್ಲಿ ಸೇರಿದರು.",
			"34": "<sup>34</sup> ಆತನು ಹೊರಗೆ ಬಂದು ಬಹುಜನರ ಗುಂಪನ್ನು ಕಂಡು ಇವರು ಕುರುಬನಿಲ್ಲದ ಕುರಿಗಳ ಹಾಗಿದ್ದಾರಲ್ಲಾ ಎಂದು ಕನಿಕರಪಟ್ಟು ಅವರಿಗೆ ಬಹಳ ಉಪದೇಶ ಮಾಡುತ್ತಿದ್ದನು.",
			"35": "<sup>35</sup> ಅಷ್ಟರಲ್ಲಿ ಬಹಳ ಹೊತ್ತಾದ್ದರಿಂದ ಆತನ ಶಿಷ್ಯರು ಆತನ ಬಳಿಗೆ ಬಂದು -",
			"36": "<sup>36</sup> ಇದು ಅಡವಿ, ಈಗ ಹೊತ್ತು ಬಹಳವಾಯಿತು; ಈ ಜನರಿಗೆ ಅಪ್ಪಣೆಕೊಡು; ಇವರು ಸುತ್ತಲಿರುವ ಹಳ್ಳಿಪಳ್ಳಿಗಳಿಗೆ ಹೋಗಿ ತಮ್ಮ ಊಟಕ್ಕೆ ಏನಾದರೂ ಕೊಂಡುಕೊಳ್ಳಲಿ ಅಂದದ್ದಕ್ಕೆ ಯೇಸು -",
			"37": "<sup>37</sup> ನೀವೇ ಅವರಿಗೆ ಊಟಕ್ಕೆ ಕೊಡಿರಿ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು. ಅದಕ್ಕವರು - ನಾವು ಹೋಗಿ ಇನ್ನೂರು ಹಣಕ್ಕೆ ರೊಟ್ಟಿಯನ್ನು ಕೊಂಡುಕೊಂಡು ಅವರಿಗೆ ಊಟಕ್ಕೆ ಕೊಡಬೇಕೋ? ಎಂದು ಕೇಳಲು ಆತನು -",
			"38": "<sup>38</sup> ನಿಮ್ಮಲ್ಲಿ ಎಷ್ಟು ರೊಟ್ಟಿಗಳಿವೆ? ಹೋಗಿ ನೋಡಿಕೊಂಡು ಬನ್ನಿ ಅಂದನು. ಅವರು ತಿಳುಕೊಂಡು ಬಂದು - ಐದು ರೊಟ್ಟಿ ಎರಡು ಮೀನು ಅಂದರು.",
			"39": "<sup>39</sup> ಆಗ ಆತನು ಅವರಿಗೆ - ಎಲ್ಲರೂ ಹಸುರುಹುಲ್ಲಿನ ಮೇಲೆ ಪಂಙ್ತಿಪಂಙ್ತಿಯಾಗಿ ಕೂತುಕೊಳ್ಳಲಿ ಎಂದು ಅಪ್ಪಣೆಕೊಟ್ಟಾಗ",
			"40": "<sup>40</sup> ಜನರು ಪಂಙ್ತಿಗೆ ನೂರರಂತೆ ಐವತ್ತರಂತೆ ಸಾಲುಸಾಲಾಗಿ ಕೂತುಕೊಂಡರು.",
			"41": "<sup>41</sup> ಆಮೇಲೆ ಆತನು ಆ ಐದು ರೊಟ್ಟಿ ಎರಡು ಮೀನುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಆಕಾಶದ ಕಡೆಗೆ ನೋಡಿ ದೇವರ ಸ್ತೋತ್ರಮಾಡಿ ಆ ರೊಟ್ಟಿಗಳನ್ನು ಮುರಿದು - ನೀವು ಜನರಿಗೆ ಹಂಚಿರಿ ಎಂದು ಶಿಷ್ಯರ ಕೈಗೆ ಕೊಟ್ಟು ಆ ಎರಡು ಮೀನುಗಳನ್ನೂ ಎಲ್ಲರಿಗೆ ಹಂಚಿಸಿದನು.",
			"42": "<sup>42</sup> ಅವರೆಲ್ಲರೂ ಊಟಮಾಡಿ ತೃಪ್ತರಾದರು.",
			"43": "<sup>43</sup> ರೊಟ್ಟಿತುಂಡುಗಳನ್ನೂ ಮೀನಿನ ತುಂಡುಗಳನ್ನೂ ಕೂಡಿಸಲಾಗಿ ಹನ್ನೆರಡು ಪುಟ್ಟಿ ತುಂಬಿತು.",
			"44": "<sup>44</sup> ಊಟಮಾಡಿದವರು ಐದುಸಾವಿರ ಮಂದಿ ಗಂಡಸರು.",
			"45": "<sup>45</sup> ಇದಾದ ಕೂಡಲೆ ಆತನು ತನ್ನ ಶಿಷ್ಯರಿಗೆ - ನಾನು ಈ ಜನರ ಗುಂಪನ್ನು ಕಳುಹಿಸಿ ಬಿಡುವಷ್ಟರೊಳಗೆ ನೀವು ದೋಣಿಯನ್ನು ಹತ್ತಿ ಮುಂದಾಗಿ ಆಚೇದಡಕ್ಕೆ ಬೇತ್ಸಾಯಿದಕ್ಕೆ ಹೋಗಿರಿ ಎಂದು ಬಲವಂತ ಮಾಡಿದನು.",
			"46": "<sup>46</sup> ಆತನು ಜನರಿಗೆ ಅಪ್ಪಣೆಕೊಟ್ಟನಂತರ ಪ್ರಾರ್ಥನೆ ಮಾಡುವದಕ್ಕಾಗಿ ಬೆಟ್ಟಕ್ಕೆ ಹೋದನು.",
			"47": "<sup>47</sup> ಹೊತ್ತು ಮುಳುಗಿದ ಮೇಲೆ ದೋಣಿಯು ಸಮುದ್ರದ ನಡುವೆ ಇತ್ತು; ಯೇಸು ಒಬ್ಬನೇ ದಡದಲ್ಲಿದ್ದನು.",
			"48": "<sup>48</sup> ಎದುರು ಗಾಳಿ ಬೀಸುತ್ತಿದ್ದದರಿಂದ ಶಿಷ್ಯರು ಹುಟ್ಟು ಹಾಕಿ ಹಾಕಿ ಒದ್ದಾಡುವದನ್ನು ಆತನು ಕಂಡು ಹೆಚ್ಚುಕಡಿಮೆ ರಾತ್ರಿಯ ನಾಲ್ಕನೆಯ ಜಾವದಲ್ಲಿ ಸಮುದ್ರದ ಮೇಲೆ ನಡೆದು ಅವರ ಕಡೆಗೆ ಬಂದು ಅವರನ್ನು ದಾಟಿ ಮುಂದಕ್ಕೆ ಹೋಗಬೇಕೆಂದಿದ್ದನು.",
			"49": "<sup>49</sup> ಸಮುದ್ರದ ಮೇಲೆ ನಡೆಯುವ ಆತನನ್ನು ಅವರು ಕಂಡು ಭೂತವೆಂದು ಭಾವಿಸಿ ಅಬ್ಬರಿಸಿ ಕೂಗಿದರು; ಅವರೆಲ್ಲರೂ ಆತನನ್ನು ನೋಡಿ ತತ್ತರಿಸಿದರು.",
			"50": "<sup>50</sup> ಆತನು ಕೂಡಲೆ ಅವರನ್ನು ಮಾತಾಡಿಸಿ ಅವರಿಗೆ - ಧೈರ್ಯವಾಗಿರಿ; ನಾನು; ಅಂಜಬೇಡಿರಿ ಎಂದು ಹೇಳಿದನು.",
			"51": "<sup>51</sup> ತರುವಾಯ ಆತನು ದೋಣಿಯನ್ನು ಹತ್ತಿ ಅವರ ಬಳಿಗೆ ಬಂದ ಮೇಲೆ ಗಾಳಿ ನಿಂತುಹೋಯಿತು. ಆಗ ಅವರು ತಮ್ಮೊಳಗೆ ಬಹಳವಾಗಿ ಬೆರಗಾದರು.",
			"52": "<sup>52</sup> ಯಾಕಂದರೆ ಅವರು ರೊಟ್ಟಿಯ ವಿಷಯವಾದ ಮಹತ್ಕಾರ್ಯವನ್ನು ಗ್ರಹಿಸಲಿಲ್ಲ; ಅವರ ಮನಸ್ಸು ಕಲ್ಲಾಗಿತ್ತು.",
			"53": "<sup>53</sup> ಅವರು ಸಮುದ್ರವನ್ನು ದಾಟಿ ಗೆನೆಜರೇತ್ ದೇಶದ ದಡಕ್ಕೆ ಮುಟ್ಟಿ ದೋಣಿಯನ್ನು ಕಟ್ಟಿದರು.",
			"54": "<sup>54</sup> ಅವರು ದೋಣಿಯಿಂದ ಇಳಿದ ಕೂಡಲೆ ಜನರು ಆತನ ಗುರುತನ್ನು ಹಿಡಿದು",
			"55": "<sup>55</sup> ಆ ಸೀಮೆಯಲ್ಲೆಲ್ಲಾ ಓಡಾಡುತ್ತಾ ಮೈಯಲ್ಲಿ ನೆಟ್ಟಗಿಲ್ಲದವರನ್ನು ದೋಲಿಯ ಮೇಲೆ ಹಾಕಿಕೊಂಡು ಆತನು ಎಲ್ಲೆಲ್ಲಿ ಇದ್ದಾನೆಂದು ಕೇಳಿದರೋ ಅಲ್ಲಲ್ಲಿಗೆ ತೆಗೆದುಕೊಂಡು ಹೋದರು.",
			"56": "<sup>56</sup> ಆತನು ಯಾವ ಗ್ರಾಮಗಳಿಗೂ ಯಾವ ಊರು ಹಳ್ಳಿಗಳಿಗೂ ಹೋದನೋ ಅಲ್ಲಿಯವರು ರೋಗಿಗಳನ್ನು ಪೇಟೆಗಳಲ್ಲಿ ಇಟ್ಟು - ನಿನ್ನ ಉಡುಪಿನ ಗೊಂಡೆಯನ್ನಾದರೂ ಮುಟ್ಟಗೊಡಿಸಬೇಕೆಂದು ಆತನನ್ನು ಬೇಡಿಕೊಂಡರು; ಆತನನ್ನು ಮುಟ್ಟಿದವರೆಲ್ಲರೂ ಸ್ವಸ್ಥರಾದರು."
		},
		"7": {
			"1": "<sup>1</sup> ತರುವಾಯ ಯೆರೂಸಲೇಮಿನಿಂದ ಬಂದಿದ್ದ ಫರಿಸಾಯರೂ ಶಾಸ್ತ್ರಿಗಳಲ್ಲಿ ಕೆಲವರೂ ಆತನ ಬಳಿಗೆ ಕೂಡಿ ಬಂದರು.",
			"2": "<sup>2</sup> ಫರಿಸಾಯರೂ ಯೆಹೂದ್ಯರೆಲ್ಲರೂ ಹಿರಿಯರಿಂದ ಬಂದ ಸಂಪ್ರದಾಯವನ್ನು ಹಿಡಿದು ತಮ್ಮ ಕೈಗಳನ್ನು ಚೆನ್ನಾಗಿ ತೊಳಕೊಳ್ಳದೆ ಆಹಾರ ತೆಗೆದುಕೊಳ್ಳುವದಿಲ್ಲ; ಮತ್ತು ಪೇಟೆಗೆ ಹೋಗಿ ಬಂದರೆ ನೀರನ್ನು ಪ್ರೋಕ್ಷಿಸಿಕೊಳ್ಳದೆ ಆಹಾರ ತೆಗೆದುಕೊಳ್ಳುವದಿಲ್ಲ; ಇದಲ್ಲದೆ ಅವರಲ್ಲಿ ತಂಬಿಗೆ ಬಟ್ಟಲು ತಪ್ಪಲೆಗಳನ್ನು ಬೆಳಗಿ ತೊಳೆಯುವದೇ ಮೊದಲಾದ ಅನೇಕಾಚಾರಗಳನ್ನು ನಡಿಸುವ ನೇಮಕವುಂಟು. ಆತನ ಶಿಷ್ಯರಲ್ಲಿ ಕೆಲವರು ಮೈಲಿಗೆಯ ಕೈಯಿಂದ, ಅಂದರೆ ಕೈತೊಳಕೊಳ್ಳದೆ ಆಹಾರ ತೆಗೆದುಕೊಳ್ಳುವದನ್ನು ಆ ಫರಿಸಾಯರೂ ಶಾಸ್ತ್ರಿಗಳೂ ಕಂಡು",
			"5": "<sup>5</sup> ಆತನನ್ನು - ನಿನ್ನ ಶಿಷ್ಯರು ಹಿರಿಯರಿಂದ ಬಂದ ಸಂಪ್ರದಾಯದಂತೆ ಯಾಕೆ ನಡೆದುಕೊಳ್ಳುವದಿಲ್ಲ? ಮೈಲಿಗೆಯ ಕೈಯಿಂದ ಯಾಕೆ ಆಹಾರ ತೆಗೆದುಕೊಳ್ಳುತ್ತಾರೆ ಎಂದು ಕೇಳಿದರು.",
			"6": "<sup>6</sup> ಅದಕ್ಕೆ ಆತನು - ಕಪಟಿಗಳಾದ ನಿಮ್ಮ ವಿಷಯದಲ್ಲಿ ಯೆಶಾಯನು ವಿಹಿತವಾಗಿ ಪ್ರವಾದಿಸಿದ್ದಾನೆ; ಅವನು ಬರೆದದ್ದೇನಂದರೆ - ಈ ಜನರು ಮಾತಿನಿಂದ ನನ್ನನ್ನು ಸನ್ಮಾನಿಸುತ್ತಾರೆ, ಆದರೆ ಅವರ ಮನಸ್ಸು ನನಗೆ ದೂರವಾಗಿದೆ.",
			"7": "<sup>7</sup> ಮನುಷ್ಯರು ಕಲ್ಪಿಸಿದ ಕಟ್ಟಳೆಗಳನ್ನೇ ಅವರು ಬೋಧಿಸುವದರಿಂದ ನನಗೆ ಭಕ್ತಿತೋರಿಸುವದು ವ್ಯರ್ಥ ಎಂಬದೇ.",
			"8": "<sup>8</sup> ನೀವು ದೇವರ ಆಜ್ಞೆಯನ್ನು ಬಿಟ್ಟು ಮನುಷ್ಯರು ಕಲ್ಪಿಸಿದ ಸಂಪ್ರದಾಯವನ್ನು ಹಿಡಿದಿದ್ದೀರಿ ಎಂದು ಹೇಳಿದನು.",
			"9": "<sup>9</sup> ಆತನು ಇನ್ನೂ ಅವರಿಗೆ - ನೀವು ನಿಮ್ಮ ಸಂಪ್ರದಾಯವನ್ನು ಹಿಡಿದು ನಡಿಸುವದಕ್ಕಾಗಿ ದೇವರ ಆಜ್ಞೆಯನ್ನು ವ್ಯರ್ಥಮಾಡುತ್ತೀರಿ; ಬಹು ಚೆನ್ನಾಗಿದೆಯಲ್ಲವೇ.",
			"10": "<sup>10</sup> ತಂದೆತಾಯಿಗಳನ್ನು ಸನ್ಮಾನಿಸಬೇಕೆಂತಲೂ ತಂದೆಯನ್ನಾಗಲಿ ತಾಯಿಯನ್ನಾಗಲಿ ದೂಷಿಸುವವನಿಗೆ ಮರಣದಂಡನೆ ಆಗಬೇಕೆಂತಲೂ ಮೋಶೆಯು ಹೇಳಿದ್ದಾನೆ;",
			"11": "<sup>11</sup> ನೀವೋ ಒಬ್ಬನು ತನ್ನ ತಂದೆಯನ್ನಾಗಲಿ ತಾಯಿಯನ್ನಾಗಲಿ ನೋಡಿ - ನಾನು ನಿನ್ನ ಸಂರಕ್ಷಣೆಗಾಗಿ ಕೊಡತಕ್ಕದ್ದನ್ನು ಕೊರ್ಬಾನ್ ಮಾಡಿದ್ದೇನೆ (ಅಂದರೆ ದೇವರಿಗಾಗಿ ಇಟ್ಟಿದ್ದೇನೆ) ಎಂದು ಹೇಳುವದಾದರೆ",
			"12": "<sup>12</sup> ಅವನು ತನ್ನ ತಂದೆಗಾಗಲಿ ತಾಯಿಗಾಗಲಿ ಇನ್ನೇನೂ ಮಾಡಬಾರದೆಂದು ಹೇಳುತ್ತೀರಿ.",
			"13": "<sup>13</sup> ಹೀಗೆ ನೀವು ಕಲಿಸುತ್ತಾ ಬಂದಿರುವ ಸಂಪ್ರದಾಯದಿಂದ ದೇವರ ವಾಕ್ಯವನ್ನು ನಿರರ್ಥಕಮಾಡುತ್ತೀರಿ, ಮತ್ತು ಇಂಥ ಕಾರ್ಯಗಳನ್ನು ಇನ್ನೂ ಎಷ್ಟೋ ಮಾಡುತ್ತೀರಿ ಎಂದು ಹೇಳಿದನು.",
			"14": "<sup>14</sup> ಆಮೇಲೆ ಯೇಸು ಜನರ ಗುಂಪನ್ನು ತಿರಿಗಿ ಹತ್ತಿರಕ್ಕೆ ಕರೆದು ಅವರಿಗೆ - ಎಲ್ಲರೂ ಕಿವಿಗೊಟ್ಟು ನನ್ನ ಮಾತನ್ನು ಕೇಳಿರಿ, ತಿಳುಕೊಳ್ಳಿರಿ.",
			"15": "<sup>15</sup> ಹೊರಗಿನಿಂದ ಮನುಷ್ಯನೊಳಕ್ಕೆ ಹೋಗಿ ಅವನನ್ನು ಹೊಲೆಮಾಡತಕ್ಕಂಥದು ಒಂದೂ ಇಲ್ಲ; ಆದರೆ ಮನುಷ್ಯನೊಳಗಿಂದ ಹೊರಡುವವುಗಳೇ ಮನುಷ್ಯನನ್ನು ಹೊಲೆಮಾಡುವಂಥವುಗಳಾಗಿವೆ ಎಂದು ಹೇಳಿದನು.",
			"17": "<sup>17</sup> ತರುವಾಯ ಆತನು ಜನರನ್ನು ಬಿಟ್ಟು ಮನೆಯೊಳಕ್ಕೆ ಹೋದಾಗ ಆತನ ಶಿಷ್ಯರು ಆ ಸಾಮ್ಯದ ಅರ್ಥವೇನೆಂದು ಆತನನ್ನು ಕೇಳಲು",
			"18": "<sup>18</sup> ಆತನು ಅವರಿಗೆ - ನಿಮಗೂ ಅಷ್ಟು ಬುದ್ಧಿ ಇಲ್ಲವೇ? ಹೊರಗಿನಿಂದ ಮನುಷ್ಯನೊಳಕ್ಕೆ ಹೋಗುವಂಥದು",
			"19": "<sup>19</sup> ಅವನ ಹೃದಯದೊಳಕ್ಕೆ ಸೇರದೆ ಹೊಟ್ಟೆಯಲ್ಲಿ ಸೇರಿ ಬಹಿರ್ದೇಶಕ್ಕೆ ಹೋಗುವದರಿಂದ ಅವನನ್ನು ಹೊಲೆಮಾಡಲಾರದೆಂದು ನಿಮಗೆ ತಿಳಿಯಲಿಲ್ಲವೋ ಎಂದು ಹೇಳಿದನು. ಹೀಗೆ ಹೇಳಿದ್ದರಿಂದ ತಿನ್ನುವ ಪದಾರ್ಥಗಳೆಲ್ಲಾ ಶುದ್ಧವೆಂದು ಸೂಚಿಸಿದನು.",
			"20": "<sup>20</sup> ಆತನು ಇನ್ನೂ - ಮನುಷ್ಯನೊಳಗಿಂದ ಹೊರಡುವಂಥದೇ ಮನುಷ್ಯನನ್ನು ಹೊಲೆಮಾಡುವದು;",
			"21": "<sup>21</sup> ಒಳಗಿನಿಂದ ಅಂದರೆ ಮನುಷ್ಯರ ಮನಸ್ಸಿನೊಳಗಿಂದ",
			"22": "<sup>22</sup> ಸೂಳೆಗಾರಿಕೆ ಕಳ್ಳತನ ಕೊಲೆ ಹಾದರ ದ್ರವ್ಯಾಶೆ ಕೆಡುಕುತನ ಮೋಸ ಬಂಡತನ ಹೊಟ್ಟೇಕಿಚ್ಚು ಬೈಗಳು ಸೊಕ್ಕು ಬುದ್ಧಿಗೇಡಿತನ ಇವೇ ಮೊದಲಾದವುಗಳ ಕೆಟ್ಟ ಆಲೋಚನೆಗಳು ಹೊರಡುತ್ತವೆ.",
			"23": "<sup>23</sup> ಈ ಕೆಟ್ಟ ವಿಷಯಗಳೆಲ್ಲಾ ಒಳಗಿನಿಂದ ಹೊರಟು ಮನುಷ್ಯನನ್ನು ಹೊಲೆಮಾಡುತ್ತವೆ ಅಂದನು.",
			"24": "<sup>24</sup> ಅಲ್ಲಿಂದ ಯೇಸು ಎದ್ದು ಹೊರಟು ತೂರ್ ಪಟ್ಟಣದ ಪ್ರಾಂತ್ಯಗಳಿಗೆ ಹೋಗಿ ಒಂದು ಮನೆಯೊಳಗೆ ಇಳುಕೊಂಡು ಅದು ಯಾರಿಗೂ ಗೊತ್ತಾಗಬಾರದೆಂದಿದ್ದನು; ಆದರೆ ಆತನು ಗುಪ್ತವಾಗಿರುವದಕ್ಕೆ ಆಗದೆ ಹೋಯಿತು.",
			"25": "<sup>25</sup> ಕೂಡಲೆ ಒಬ್ಬ ಹೆಂಗಸು ಆತನ ಸುದ್ದಿಯನ್ನು ಕೇಳಿ ಬಂದು ಆತನ ಪಾದಕ್ಕೆ ಬಿದ್ದಳು.",
			"26": "<sup>26</sup> ಆಕೆಯ ಮಗಳಿಗೆ ದೆವ್ವ ಹಿಡಿದಿತ್ತು. ಆ ಹೆಂಗಸು ಅನ್ಯಮತದವಳೂ ಸುರೋಪೊಯಿನಿಕ್ಯರವಳೂ ಆಗಿದ್ದಳು.",
			"27": "<sup>27</sup> ಆಕೆಯು ತನ್ನ ಮಗಳ ದೆವ್ವವನ್ನು ಬಿಡಿಸಬೇಕೆಂದು ಆತನನ್ನು ಬೇಡಿಕೊಂಡಾಗ ಆತನು ಆಕೆಗೆ - ಮಕ್ಕಳಿಗೆ ಮೊದಲು ತೃಪ್ತಿಯಾಗಲಿ; ಮಕ್ಕಳು ತಿನ್ನುವ ರೊಟ್ಟಿಯನ್ನು ತಕ್ಕೊಂಡು ನಾಯಿಮರಿಗಳಿಗೆ ಹಾಕುವದು ಸರಿಯಲ್ಲ ಎಂದು ಹೇಳಿದನು.",
			"28": "<sup>28</sup> ಅದಕ್ಕೆ ಆಕೆಯು - ಸ್ವಾಮೀ, ಆ ಮಾತು ನಿಜವೇ; ಮೇಜಿನ ಕೆಳಗಿರುವ ನಾಯಿಮರಿಗಳಂತೂ ಮಕ್ಕಳ ಕೈಯಿಂದ ಬೀಳುವ ರೊಟ್ಟೀತುಂಡುಗಳನ್ನು ತಿನ್ನುತ್ತವಲ್ಲಾ ಎಂದು ಉತ್ತರಕೊಡಲು",
			"29": "<sup>29</sup> ಆತನು - ಈ ಮಾತನ್ನು ಹೇಳಿದ್ದರಿಂದ ದೆವ್ವವು ನಿನ್ನ ಮಗಳನ್ನು ಬಿಟ್ಟು ಬಿಟ್ಟಿದೆ, ಹೋಗು ಅಂದನು.",
			"30": "<sup>30</sup> ಆಕೆಯು ತನ್ನ ಮನೆಗೆ ಹೋಗಿ ನೋಡಲಾಗಿ ಆ ಹುಡುಗಿ ಹಾಸಿಗೆಯ ಮೇಲೆ ಮಲಗಿದ್ದಳು; ದೆವ್ವ ಅವಳನ್ನು ಬಿಟ್ಟು ಹೋಗಿತ್ತು.",
			"31": "<sup>31</sup> ಅನಂತರ ಆತನು ತೂರ್ ಪಟ್ಟಣದ ಪ್ರಾಂತ್ಯಗಳನ್ನು ಬಿಟ್ಟು ಸೀದೋನ್ ಪಟ್ಟಣದ ಮಾರ್ಗವಾಗಿ ದೆಕಪೊಲಿಯ ಪ್ರಾಂತ್ಯಗಳನ್ನು ಹಾದು ಗಲಿಲಾಯ ಸಮುದ್ರಕ್ಕೆ ಬಂದನು.",
			"32": "<sup>32</sup> ಆಗ ಕೆಲವರು ತೊದಲು ಮಾತಾಡುವ ಒಬ್ಬ ಕಿವುಡನನ್ನು ಆತನ ಬಳಿಗೆ ಕರತಂದು - ಇವನ ಮೇಲೆ ನಿನ್ನ ಕೈಯಿಡಬೇಕು ಎಂದು ಆತನನ್ನು ಬೇಡಿಕೊಂಡರು.",
			"33": "<sup>33</sup> ಆತನು ಅವನನ್ನು ಜನರ ಗುಂಪಿನಿಂದ ಒತ್ತಟ್ಟಿಗೆ ಕರಕೊಂಡು ಹೋಗಿ ತನ್ನ ಬೆರಳುಗಳನ್ನು ಅವನ ಕಿವಿಗಳಲ್ಲಿ ಇಟ್ಟು ಉಗುಳಿ",
			"34": "<sup>34</sup> ಅವನ ನಾಲಿಗೆಯನ್ನು ಮುಟ್ಟಿ ಆಕಾಶದ ಕಡೆಗೆ ನೋಡಿ ನಿಟ್ಟುಸುರು ಬಿಟ್ಟು ಅವನಿಗೆ - ಎಪ್ಫಥಾ ಎಂದು ಹೇಳಿದನು. ಆ ಶಬ್ದಕ್ಕೆ ತೆರೆಯಲಿ ಎಂದರ್ಥ.",
			"35": "<sup>35</sup> ಹೇಳುತ್ತಲೇ ಅವನ ಕಿವಿ ತೆರೆದವು; ಅವನ ನಾಲಿಗೆಯ ಕಟ್ಟು ಬಿಚ್ಚಿಹೋಯಿತು; ಅವನು ನೆಟ್ಟಗೆ ಮಾತಾಡಿದನು.",
			"36": "<sup>36</sup> ಆಗ ಆತನು - ಯಾರಿಗೂ ತಿಳಿಸಬಾರದೆಂದು ಅವರಿಗೆ ಖಂಡಿತವಾಗಿ ಹೇಳಿದನು. ಆದರೆ ಎಷ್ಟು ಖಂಡಿತವಾಗಿ ಹೇಳಿದರೂ ಅವರು ಮತ್ತಷ್ಟು ಹೆಚ್ಚಾಗಿ ಅದನ್ನು ಹಬ್ಬಿಸಿದರು.",
			"37": "<sup>37</sup> ಅವರು ಅತ್ಯಂತಾಶ್ಚರ್ಯಪಟ್ಟು - ಎಲ್ಲಾ ಚೆನ್ನಾಗಿ ಮಾಡಿದ್ದಾನೆ; ಕಿವುಡರಾದರೂ ಕೇಳುವಂತೆ ಮಾಡುತ್ತಾನೆ, ಮೂಕರಾದರೂ ಮಾತಾಡುವಂತೆ ಮಾಡುತ್ತಾನೆ ಅಂದುಕೊಂಡರು."
		},
		"8": {
			"1": "<sup>1</sup> ಆ ದಿವಸಗಳಲ್ಲಿ ಜನರು ತಿರಿಗಿ ಗುಂಪುಕೂಡಿ ಬಂದಾಗ ಅವರಿಗೆ ಊಟಕ್ಕೆ ಏನೂ ಇಲ್ಲದೆ ಇರಲು",
			"2": "<sup>2</sup> ಯೇಸು ತನ್ನ ಶಿಷ್ಯರನ್ನು ಹತ್ತಿರಕ್ಕೆ ಕರೆದು - ಈ ಜನರನ್ನು ನೋಡಿ ಕನಿಕರಪಡುತ್ತೇನೆ; ಇವರು ನನ್ನ ಬಳಿಗೆ ಬಂದು ಮೂರು ದಿನವಾಯಿತು; ಇವರಿಗೆ ಊಟಕ್ಕೆ ಏನೂ ಇಲ್ಲ;",
			"3": "<sup>3</sup> ಇವರನ್ನು ಉಪವಾಸವಾಗಿ ಮನೆಗೆ ಕಳುಹಿಸಿಬಿಟ್ಟರೆ ದಾರಿಯಲ್ಲಿ ಬಳಲಿಹೋಗುವರು; ಇವರಲ್ಲಿ ಕೆಲವರು ದೂರದಿಂದ ಬಂದಿದ್ದಾರಲ್ಲಾ ಎಂದು ಅವರಿಗೆ ಹೇಳಿದ್ದಕ್ಕೆ",
			"4": "<sup>4</sup> ಶಿಷ್ಯರು - ಈ ಅಡವಿಯಲ್ಲಿ ಎಲ್ಲಿಂದ ರೊಟ್ಟಿಯನ್ನು ತಂದು ಈ ಜನರನ್ನು ತೃಪ್ತಿಪಡಿಸುವದಕ್ಕಾದೀತು? ಅಂದರು.",
			"5": "<sup>5</sup> ಆತನು - ನಿಮ್ಮಲ್ಲಿ ಎಷ್ಟು ರೊಟ್ಟಿಗಳಿವೆ ಎಂದು ಅವರನ್ನು ಕೇಳಿದ್ದಕ್ಕೆ ಅವರು - ಏಳು ಅವೆ ಅಂದರು.",
			"6": "<sup>6</sup> ಆಗ ಆತನು ಜನರ ಗುಂಪಿಗೆ - ನೆಲದ ಮೇಲೆ ಕೂತುಕೊಳ್ಳಿರಿ ಎಂದು ಅಪ್ಪಣೆಕೊಟ್ಟು ಆ ಏಳು ರೊಟ್ಟಿಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ದೇವರ ಸ್ತೋತ್ರ ಮಾಡಿ ಅವುಗಳನ್ನು ಮುರಿದು ಹಂಚುವದಕ್ಕೆ ತನ್ನ ಶಿಷ್ಯರ ಕೈಗೆ ಕೊಟ್ಟನು; ಅವರು ಗುಂಪಿನವರಿಗೆ ಹಂಚಿಕೊಟ್ಟರು.",
			"7": "<sup>7</sup> ಇದಲ್ಲದೆ ಅವರಲ್ಲಿ ಕೆಲವು ಸಣ್ಣ ಮೀನುಗಳಿದ್ದವು; ಅವುಗಳನ್ನು ಆತನು ತೆಗೆದುಕೊಂಡು ದೇವರ ಸ್ತೋತ್ರ ಮಾಡಿ - ಇವುಗಳನ್ನೂ ಹಂಚಿಕೊಡಿರಿ ಅಂದನು.",
			"8": "<sup>8</sup> ಅವರು ಊಟಮಾಡಿ ತೃಪ್ತರಾದರು. ಮಿಕ್ಕ ತುಂಡುಗಳನ್ನು ಕೂಡಿಸಲಾಗಿ ಏಳು ಹೆಡಿಗೆ ಆದವು.",
			"9": "<sup>9</sup> ಊಟಮಾಡಿದವರು ಹೆಚ್ಚು ಕಡಿಮೆ ನಾಲ್ಕುಸಾವಿರ ಮಂದಿ.",
			"10": "<sup>10</sup> ಬಳಿಕ ಆತನು ಅವರನ್ನು ಕಳುಹಿಸಿಬಿಟ್ಟು ಕೂಡಲೆ ತನ್ನ ಶಿಷ್ಯರನ್ನು ಕರಕೊಂಡು ದೋಣಿಯನ್ನು ಹತ್ತಿ ದಲ್ಮನೂಥ ಸೀಮೆಗೆ ಬಂದನು.",
			"11": "<sup>11</sup> ತರುವಾಯ ಫರಿಸಾಯರು ಹೊರಟು ಬಂದು ಆತನ ಕೂಡ ತರ್ಕಮಾಡುತ್ತಾ ಪರೀಕ್ಷಿಸುವದಕ್ಕಾಗಿ - ನೀನು ಆಕಾಶದಲ್ಲಿ ಒಂದು ಸೂಚಕಕಾರ್ಯವನ್ನು ತೋರಿಸಿಕೊಡಬೇಕೆಂದು ಕೇಳುತ್ತಿರಲು ಆತನು ತನ್ನ ಆತ್ಮದಲ್ಲಿ ನೊಂದು ನಿಟ್ಟುಸುರುಬಿಟ್ಟು -",
			"12": "<sup>12</sup> ಈ ಸಂತತಿಯು ಸೂಚಕಕಾರ್ಯವನ್ನು ಅಪೇಕ್ಷಿಸುವದು ಯಾಕೆ? ನಿಮಗೆ ಸತ್ಯವಾಗಿ ಹೇಳುತ್ತೇನೆ, ಈ ಸಂತತಿಗೆ ಸೂಚಕಕಾರ್ಯವು ಸಿಕ್ಕುವದೇ ಇಲ್ಲ ಎಂದು ಹೇಳಿ",
			"13": "<sup>13</sup> ಅವರನ್ನು ಬಿಟ್ಟು ತಿರಿಗಿ ದೋಣಿಯನ್ನು ಹತ್ತಿ ಆಚೇದಡಕ್ಕೆ ಹೋದನು.",
			"14": "<sup>14</sup> ಆದರೆ ಶಿಷ್ಯರು ರೊಟ್ಟೀಬುತ್ತಿ ಕಟ್ಟಿಕೊಳ್ಳುವದನ್ನು ಮರೆತರು. ದೋಣಿಯಲ್ಲಿ ಅವರಿಗೆ ಒಂದು ರೊಟ್ಟಿ ಮಾತ್ರ ಇತ್ತು; ಬೇರೆ ಇರಲಿಲ್ಲ.",
			"15": "<sup>15</sup> ಯೇಸು ಅವರಿಗೆ - ಎಚ್ಚರಿಕೆ, ಫರಿಸಾಯರ ಮತ್ತು ಹೆರೋದನ ಹುಳಿಹಿಟ್ಟಿನ ವಿಷಯದಲ್ಲಿ ನೋಡಿಕೊಳ್ಳಿರಿ ಎಂದು ಖಂಡಿತವಾಗಿ ಹೇಳಲು",
			"16": "<sup>16</sup> ಅವರು - ನಮ್ಮಲ್ಲಿ ರೊಟ್ಟಿಯಿಲ್ಲವಲ್ಲಾ ಎಂದು ತಮ್ಮ ತಮ್ಮೊಳಗೆ ಮಾತಾಡಿಕೊಳ್ಳುತ್ತಿದ್ದರು.",
			"17": "<sup>17</sup> ಆತನು ಅದನ್ನು ತಿಳಿದು - ರೊಟ್ಟಿಯಿಲ್ಲವಲ್ಲಾ ಎಂದು ಮಾತಾಡಿಕೊಳ್ಳುವದೇಕೆ? ನೀವು ಇನ್ನೂ ಗ್ರಹಿಸಲಿಲ್ಲವೋ? ತಿಳುವಳಿಕೆ ಬರಲಿಲ್ಲವೋ?",
			"18": "<sup>18</sup> ನಿಮ್ಮ ಮನಸ್ಸು ಕಲ್ಲಾಗಿದೆಯೋ? ಕಣ್ಣಿದ್ದೂ ಕಾಣುವದಿಲ್ಲವೋ? ಕಿವಿಯಿದ್ದೂ ಕೇಳುವದಿಲ್ಲವೋ? ನಿಮಗೆ ನೆನಪಿಲ್ಲವೋ?",
			"19": "<sup>19</sup> ನಾನು ಆ ಐದು ರೊಟ್ಟಿಗಳನ್ನು ಮುರಿದು ಐದುಸಾವಿರ ಜನರಿಗೆ ಹಂಚಿಸಿದಾಗ ತುಂಡು ತುಂಬಿದ ಎಷ್ಟು ಪುಟ್ಟಿಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಹೋದಿರಿ ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ ಅವರು - ಹನ್ನೆರಡು ಪುಟ್ಟಿ ಅಂದರು.",
			"20": "<sup>20</sup> ಮತ್ತು ಆ ಏಳು ರೊಟ್ಟಿಗಳನ್ನು ನಾಲ್ಕು ಸಾವಿರ ಜನರಿಗೆ ಹಂಚಿಸಿದಾಗ ತುಂಡು ತುಂಬಿದ ಎಷ್ಟು ಹೆಡಿಗೆಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಹೋದಿರಿ ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ ಅವರು - ಏಳು ಹೆಡಿಗೆ ಅಂದರು.",
			"21": "<sup>21</sup> ಆಗ ಆತನು ಅವರಿಗೆ - ನಿಮಗೆ ಇನ್ನೂ ತಿಳುವಳಿಕೆ ಬರಲಿಲ್ಲವೇ? ಎಂದು ಹೇಳಿದನು.",
			"22": "<sup>22</sup> ಮತ್ತು ಅವರು ಬೇತ್ಸಾಯಿದ ಊರಿಗೆ ಬಂದರು. ಅಲ್ಲಿ ಜನರು ಒಬ್ಬ ಕುರುಡನನ್ನು ಯೇಸುವಿನ ಬಳಿಗೆ ಕರಕೊಂಡುಬಂದು - ಇವನನ್ನು ಮುಟ್ಟಬೇಕು ಎಂದು ಆತನನ್ನು ಬೇಡಿಕೊಂಡರು.",
			"23": "<sup>23</sup> ಆತನು ಕುರುಡನ ಕೈ ಹಿಡುಕೊಂಡು ಊರ ಹೊರಕ್ಕೆ ಕರಕೊಂಡು ಹೋಗಿ ಅವನ ಕಣ್ಣುಗಳಲ್ಲಿ ಉಗುಳಿ ಅವನ ಮೇಲೆ ಕೈಯಿಟ್ಟು - ನಿನಗೇನಾದರೂ ಕಾಣುತ್ತದೋ? ಎಂದು ಅವನನ್ನು ಕೇಳಿದನು.",
			"24": "<sup>24</sup> ಅವನು ತಲೆಯೆತ್ತಿ ನೋಡಿ - ನನಗೆ ಜನರು ಕಾಣುತ್ತಾರೆ; ಅವರು ಮರಗಳಂತೆ ಕಾಣಿಸಿದರೂ ತಿರುಗಾಡುತ್ತಾ ಇದ್ದಾರೆ ಅಂದನು.",
			"25": "<sup>25</sup> ಆಗ ಆತನು ಅವನ ಕಣ್ಣುಗಳ ಮೇಲೆ ತಿರಿಗಿ ಕೈಯಿಟ್ಟನು; ಅವನು ಕಣ್ಣು ಬಿಟ್ಟು ನೋಡಲು ಅವನಿಗೆ ಗುಣವಾಗಿತ್ತು; ಎಲ್ಲವೂ ಸ್ಪಷ್ಟವಾಗಿ ಕಾಣಿಸಿತು.",
			"26": "<sup>26</sup> ತರುವಾಯ ಆತನು - ನೀನು ಊರೊಳಕ್ಕೇನೂ ಹೋಗಬೇಡ ಎಂದು ಹೇಳಿ ಅವನನ್ನು ಅವನ ಮನೆಗೆ ಕಳುಹಿಸಿಬಿಟ್ಟನು.",
			"27": "<sup>27</sup> ಯೇಸುವೂ ಆತನ ಶಿಷ್ಯರೂ ಫಿಲಿಪ್ಪನ ಕೈಸರೈಯ ಎಂಬ ಪಟ್ಟಣಕ್ಕೆ ಸೇರಿದ ಗ್ರಾಮಗಳಿಗೆ ಹೊರಟರು. ದಾರಿಯಲ್ಲಿ ಆತನು - ಜನರು ನನ್ನನ್ನು ಯಾರು ಅನ್ನುತ್ತಾರೆ ಎಂದು ತನ್ನ ಶಿಷ್ಯರನ್ನು ಕೇಳಿದ್ದಕ್ಕೆ ಅವರು -",
			"28": "<sup>28</sup> ನಿನ್ನನ್ನು ಕೆಲವರು ಸ್ನಾನಿಕನಾದ ಯೋಹಾನನು ಅನ್ನುತ್ತಾರೆ; ಕೆಲವರು ಎಲೀಯನು ಅನ್ನುತ್ತಾರೆ; ಇನ್ನು ಕೆಲವರು ಪ್ರವಾದಿಗಳಲ್ಲಿ ಒಬ್ಬನು ಅನ್ನುತ್ತಾರೆ ಎಂದು ಹೇಳಿದರು.",
			"29": "<sup>29</sup> ಆತನು ಅವರನ್ನು - ಆದರೆ ನೀವು ನನ್ನನ್ನು ಯಾರನ್ನುತ್ತೀರಿ? ಎಂದು ಕೇಳಲಾಗಿ ಪೇತ್ರನು - ನೀನು ಬರಬೇಕಾಗಿರುವ ಕ್ರಿಸ್ತನು ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"30": "<sup>30</sup> ಅದಕ್ಕೆ ಆತನು - ನನ್ನ ವಿಷಯವಾಗಿ ಯಾರಿಗೂ ಹೇಳಬೇಡಿರಿ ಎಂದು ಅವರಿಗೆ ಖಂಡಿತವಾಗಿ ಹೇಳಿದನು.",
			"31": "<sup>31</sup> ಇದಲ್ಲದೆ ಆತನು - ಮನುಷ್ಯಕುಮಾರನು ಬಹು ಕಷ್ಟಗಳನ್ನನುಭವಿಸಿ ಹಿರಿಯರಿಂದಲೂ ಮಹಾಯಾಜಕರಿಂದಲೂ ಶಾಸ್ತ್ರಿಗಳಿಂದಲೂ ನಿರಾಕೃತವಾಗಿ ಕೊಲ್ಲಲ್ಪಟ್ಟು ಮೂರು ದಿನದ ಮೇಲೆ ಜೀವಿತನಾಗಿ ಎದ್ದು ಬರಬೇಕಾಗಿದೆ ಎಂದು ಉಪದೇಶಮಾಡುವದಕ್ಕೆ ಪ್ರಾರಂಭಮಾಡಿದನು.",
			"32": "<sup>32</sup> ಆತನು ಈ ಮಾತನ್ನು ಏನೂ ಗುಂಬವಿಲ್ಲದೆ ಹೇಳಿದನು. ಆಗ ಪೇತ್ರನು ಆತನ ಕೈ ಹಿಡಿದು ಆತನನ್ನು ಗದರಿಸುವದಕ್ಕೆ ಪ್ರಾರಂಭಿಸಲು",
			"33": "<sup>33</sup> ಆತನು ಹಿಂತಿರುಗಿಕೊಂಡು ತನ್ನ ಶಿಷ್ಯರನ್ನು ನೋಡಿ ಪೇತ್ರನಿಗೆ - ಸೈತಾನನೇ, ನನ್ನ ಮುಂದೆ ನಿಲ್ಲಬೇಡ, ನಡೆ, ನಿನ್ನ ಯೋಚನೆ ಮನುಷ್ಯರ ಯೋಚನೆಯೇ ಹೊರತು ದೇವರದಲ್ಲ ಎಂದು ಗದರಿಸಿ ಹೇಳಿದನು.",
			"34": "<sup>34</sup> ಆಮೇಲೆ ಆತನು ತನ್ನ ಶಿಷ್ಯರ ಜೊತೆಗೆ ಜನರ ಗುಂಪನ್ನೂ ಹತ್ತಿರ ಕರೆದು ಅವರಿಗೆ ಹೇಳಿದ್ದೇನಂದರೆ - ಯಾವನಿಗಾದರೂ ನನ್ನ ಹಿಂದೆ ಬರುವದಕ್ಕೆ ಮನಸ್ಸಿದ್ದರೆ ಅವನು ತನ್ನನ್ನು ನಿರಾಕರಿಸಿ ತನ್ನ ಶಿಲುಬೆಯನ್ನು ಹೊತ್ತುಕೊಂಡು ನನ್ನ ಹಿಂದೆ ಬರಲಿ.",
			"35": "<sup>35</sup> ತನ್ನ ಪ್ರಾಣವನ್ನು ಉಳಿಸಿಕೊಳ್ಳಬೇಕೆಂದಿರುವವನು ಅದನ್ನು ಕಳಕೊಳ್ಳುವನು; ಆದರೆ ನನ್ನ ನಿಮಿತ್ತವಾಗಿಯೂ ಸುವಾರ್ತೆಯ ನಿಮಿತ್ತವಾಗಿಯೂ ತನ್ನ ಪ್ರಾಣವನ್ನು ಕಳಕೊಂಡವನು ಅದನ್ನು ಉಳಿಸಿಕೊಳ್ಳುವನು.",
			"36": "<sup>36</sup> ಒಬ್ಬ ಮನುಷ್ಯನು ಲೋಕವನ್ನೆಲ್ಲಾ ಸಂಪಾದಿಸಿಕೊಂಡರೂ ಪ್ರಾಣನಷ್ಟಪಟ್ಟರೆ ಅವನಿಗೆ ಪ್ರಯೋಜನವೇನು?",
			"37": "<sup>37</sup> ಒಬ್ಬನು ತನ್ನ ಪ್ರಾಣಕ್ಕೆ ಏನು ಈಡು ಕೊಡಬಹುದು?",
			"38": "<sup>38</sup> ವ್ಯಭಿಚಾರಿಣಿಯಂತಿರುವ ಈ ಪಾಪಿಷ್ಠ ಸಂತತಿಯಲ್ಲಿ ಯಾವನು ನನಗೂ ನನ್ನ ಮಾತುಗಳಿಗೂ ನಾಚಿಕೊಳ್ಳುತ್ತಾನೋ ಅವನಿಗೆ ಮನುಷ್ಯಕುಮಾರನು, ತಾನು ತನ್ನ ತಂದೆಯ ಪ್ರಭಾವದೊಡನೆ ಪರಿಶುದ್ಧ ದೇವದೂತರ ಸಮೇತವಾಗಿ ಬರುವಾಗ ನಾಚಿಕೊಳ್ಳುವನು ಅಂದನು."
		},
		"9": {
			"1": "<sup>1</sup> ಮತ್ತು ಅವರಿಗೆ - ನಿಮಗೆ ನಿಜವಾಗಿ ಹೇಳುತ್ತೇನೆ, ಇಲ್ಲಿ ನಿಂತಿರುವವರೊಳಗೆ ಕೆಲವರು ದೇವರ ರಾಜ್ಯವು ಪ್ರಬಲವಾಗಿ ಬಂದಿರುವದನ್ನು ನೋಡುವ ತನಕ ಮರಣ ಹೊಂದುವದಿಲ್ಲವೆಂದು ಹೇಳಿದನು.",
			"2": "<sup>2</sup> ಆರು ದಿವಸಗಳಾದ ಮೇಲೆ ಯೇಸು ಪೇತ್ರ ಯಾಕೋಬ ಯೋಹಾನರನ್ನು ಮಾತ್ರ ವಿಂಗಡವಾಗಿ ಕರಕೊಂಡು ಎತ್ತರವಾದ ಬೆಟ್ಟಕ್ಕೆ ಹೋದನು. ಅಲ್ಲಿ ಅವರ ಕಣ್ಣ ಮುಂದೆ ಆತನ ರೂಪ ಬೇರೆಯಾಯಿತು.",
			"3": "<sup>3</sup> ಆತನ ವಸ್ತ್ರಗಳು ಬಹು ಬೆಳ್ಳಗೆ ಹೊಳೆಯುತ್ತಿದ್ದವು; ಭೂಲೋಕದಲ್ಲಿರುವ ಯಾವ ಅಗಸನಾದರೂ ಅಷ್ಟು ಬೆಳ್ಳಗೆ ಮಾಡಲಾರನು.",
			"4": "<sup>4</sup> ಇದಲ್ಲದೆ ಎಲೀಯನೂ ಮೋಶೆಯೂ ಅವರಿಗೆ ಕಾಣಿಸಿಕೊಂಡು ಯೇಸುವಿನ ಸಂಗಡ ಮಾತಾಡುತ್ತಿದ್ದರು.",
			"5": "<sup>5</sup> ಆಗ ಪೇತ್ರನು ಯೇಸುವಿಗೆ - ಗುರುವೇ, ನಾವು ಇಲ್ಲೇ ಇರುವದು ಒಳ್ಳೇದು; ಮೂರು ಪರ್ಣಶಾಲೆಗಳನ್ನು ಕಟ್ಟುವೆವು; ನಿನಗೊಂದು ಮೋಶೆಗೊಂದು ಎಲೀಯನಿಗೊಂದು ಎಂದು ಹೇಳಿದನು.",
			"6": "<sup>6</sup> ಏನು ಹೇಳಬೇಕೋ ತಿಳಿಯದೆ ಹೀಗೆ ಮಾತಾಡಿದನು. ಅವರು ಬಹಳವಾಗಿ ಹೆದರಿಕೊಂಡಿದ್ದರು.",
			"7": "<sup>7</sup> ಅಷ್ಟರಲ್ಲಿ ಮೋಡವು ಬಂದು ಅವರ ಮೇಲೆ ಕವಿಯಿತು. ಆ ಮೋಡದೊಳಗಿಂದ - ಈತನು ಪ್ರಿಯನಾಗಿರುವ ನನ್ನ ಮಗನು; ಈತನ ಮಾತನ್ನು ಕೇಳಿರಿ ಎಂದು ಆಕಾಶವಾಣಿ ಆಯಿತು.",
			"8": "<sup>8</sup> ಫಕ್ಕನೆ ಅವರು ಸುತ್ತಲೂ ನೋಡಿದಾಗ ತಮ್ಮ ಸಂಗಡ ಯೇಸುವನ್ನೇ ಹೊರತು ಆಗಿನಿಂದ ಮತ್ತಾರನ್ನೂ ಕಾಣಲಿಲ್ಲ.",
			"9": "<sup>9</sup> ಅವರು ಆ ಬೆಟ್ಟದಿಂದ ಇಳಿದು ಬರುತ್ತಿರುವಾಗ ಆತನು ಅವರಿಗೆ - ನೀವು ಕಂಡಿರುವದನ್ನು ಮನುಷ್ಯಕುಮಾರನು ಸತ್ತು ಜೀವಿತನಾಗಿ ಎದ್ದು ಬರುವತನಕ ಯಾರಿಗೂ ತಿಳಿಸಬಾರದೆಂದು ಖಂಡಿತವಾಗಿ ಹೇಳಿದನು.",
			"10": "<sup>10</sup> ಅವರು ಈ ಮಾತನ್ನು ಹಿಡಿದು ಸತ್ತುಜೀವಿತನಾಗಿ ಏಳುವದಂದರೇನು ಎಂದು ತಮ್ಮ ತಮ್ಮೊಳಗೆ ಮಾತಾಡಿಕೊಂಡರು.",
			"11": "<sup>11</sup> ಇದಲ್ಲದೆ ಅವರು - ಎಲೀಯನು ಮೊದಲು ಬರುವದು ಅಗತ್ಯವೆಂಬದಾಗಿ ಶಾಸ್ತ್ರಿಗಳು ಹೇಳುತ್ತಾರಲ್ಲಾ, ಇದು ಹೇಗೆ? ಎಂದು ಆತನನ್ನು ಕೇಳಿದ್ದಕ್ಕೆ",
			"12": "<sup>12</sup> ಆತನು - ಎಲೀಯನು ಮೊದಲು ಬಂದು ಎಲ್ಲವನ್ನು ತಿರಿಗಿ ಸರಿಮಾಡುವದು ನಿಜ. ಮತ್ತು ಮನುಷ್ಯಕುಮಾರನ ವಿಷಯವಾಗಿ - ಅವನು ಬಹು ಕಷ್ಟಗಳನ್ನನುಭವಿಸಿ ಹೀನೈಸಲ್ಪಡಬೇಕೆಂಬದಾಗಿ ಬರೆದದೆ, ಇದು ಹೇಗೆ?",
			"13": "<sup>13</sup> ಆದರೆ ನಾನು ನಿಮಗೆ ಹೇಳುವದೇನಂದರೆ - ಎಲೀಯನು ಬಂದನು; ಮತ್ತು ಅವನ ವಿಷಯವಾಗಿ ಬರೆದಿರುವ ಪ್ರಕಾರ ಜನರು ತಮ್ಮ ಮನಸ್ಸಿಗೆ ಬಂದಂತೆ ಅವನಿಗೆ ಮಾಡಿದರು ಅಂದನು.",
			"14": "<sup>14</sup> ತರುವಾಯ ಅವರು [ಉಳಿದ] ಶಿಷ್ಯರ ಬಳಿಗೆ ಬಂದು ಅವರ ಸುತ್ತಲು ಜನರ ದೊಡ್ಡ ಗುಂಪು ಇರುವದನ್ನೂ ಅವರ ಸಂಗಡ ಶಾಸ್ತ್ರಿಗಳು ತರ್ಕಮಾಡುವದನ್ನೂ ಕಂಡರು.",
			"15": "<sup>15</sup> ಕೂಡಲೆ ಆ ಗುಂಪಿನವರೆಲ್ಲರು ಯೇಸುವನ್ನು ಕಂಡು ಬಹು ಆಶ್ಚರ್ಯಪಟ್ಟು ಆತನ ಬಳಿಗೆ ಓಡಿ ಬಂದು ಆತನನ್ನು ವಂದಿಸಿದರು.",
			"16": "<sup>16</sup> ಆತನು ಶಾಸ್ತ್ರಿಗಳನ್ನು - ಇವರ ಸಂಗಡ ಏನು ತರ್ಕಮಾಡುತ್ತೀರಿ? ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ",
			"17": "<sup>17</sup> ಆ ಗುಂಪಿನಲ್ಲಿ ಒಬ್ಬನು - ಬೋಧಕನೇ, ನನ್ನ ಮಗನನ್ನು ನಿನ್ನ ಬಳಿಗೆ ಕರಕೊಂಡು ಬಂದೆನು;",
			"18": "<sup>18</sup> ಅವನಿಗೊಂದು ಮೂಗದೆವ್ವ ಹಿಡಿದದೆ; ಅದು ಎಲ್ಲಿ ಅವನ ಮೇಲೆ ಬಂದರೂ ಅವನನ್ನು ಕೆಡವುತ್ತದೆ; ಆಗ ಅವನು ನೊರೆ ಸುರಿಸುತ್ತಾ ಕರಕರನೆ ಹಲ್ಲು ಕಡಿಯುತ್ತಾ ಕುಂದಿ ಹೋಗುತ್ತಾನೆ. ಅದನ್ನು ಬಿಡಿಸಬೇಕೆಂದು ನಿನ್ನ ಶಿಷ್ಯರಿಗೆ ಹೇಳಿಕೊಂಡೆ; ಅವರ ಕೈಯಿಂದ ಆಗದೆಹೋಯಿತು ಎಂದು ಉತ್ತರ ಕೊಟ್ಟನು.",
			"19": "<sup>19</sup> ಅದಕ್ಕಾತನು - ಎಲಾ, ನಂಬಿಕೆಯಿಲ್ಲದಂಥ ಸಂತಾನವೇ, ನಾನು ಇನ್ನೆಷ್ಟು ದಿನ ನಿಮ್ಮ ಸಂಗಡ ಇರಲಿ? ಇನ್ನೆಷ್ಟು ದಿನ ನಿಮ್ಮನ್ನು ಸಹಿಸಿಕೊಳ್ಳಲಿ? ಅವನನ್ನು ನನ್ನ ಬಳಿಗೆ ತೆಗೆದುಕೊಂಡು ಬನ್ನಿರಿ, ಅನ್ನಲು",
			"20": "<sup>20</sup> ಅವನನ್ನು ಆತನ ಬಳಿಗೆ ತೆಗೆದುಕೊಂಡು ಬಂದರು. ಆತನನ್ನು ಕಂಡಕೂಡಲೆ ಆ ದೆವ್ವವು ಅವನನ್ನು ಬಹಳವಾಗಿ ಒದ್ದಾಡಿಸಲು ಅವನು ನೆಲಕ್ಕೆ ಬಿದ್ದು ನೊರೆ ಸುರಿಸುತ್ತಾ ಹೊರಳಾಡಿದನು.",
			"21": "<sup>21</sup> ಯೇಸು ಅವನ ತಂದೆಯನ್ನು - ಇದು ಇವನಿಗೆ ಬಂದು ಎಷ್ಟು ದಿನವಾಯಿತು ಎಂದು ಕೇಳಿದಾಗ ಅವನು - ಚಿಕ್ಕಂದಿನಲ್ಲಿಯೇ ಬಂದಿದೆ;",
			"22": "<sup>22</sup> ಮತ್ತು ಇವನನ್ನು ಕೊಲ್ಲಬೇಕೆಂದು ಆಗಾಗ್ಗೆ ಬೆಂಕಿಯಲ್ಲಿಯೂ ನೀರಿನಲ್ಲಿಯೂ ಕೆಡವಿತು; ನಿನ್ನ ಕೈಲಿ ಏನಾದರೂ ಆಗುವ ಹಾಗಿದ್ದರೆ ನಮ್ಮ ಮೇಲೆ ಕರುಣವಿಟ್ಟು ನಮಗೆ ಸಹಾಯಮಾಡು ಅಂದನು.",
			"23": "<sup>23</sup> ಯೇಸು ಅವನಿಗೆ - ನಿನ್ನ ಕೈಲಿ ಆಗುವ ಹಾಗಿದ್ದರೆ ಅನ್ನುತ್ತೀಯೋ? ನಂಬುವವನಿಗೆ ಎಲ್ಲಾ ಆಗುವದು ಎಂದು ಹೇಳಿದನು.",
			"24": "<sup>24</sup> ಕೂಡಲೆ ಆ ಹುಡುಗನ ತಂದೆಯು - ನಂಬುತ್ತೇನೆ, ನನಗೆ ನಂಬಿಕೆ ಕಡಿಮೆಯಾಗಿದ್ದರೂ ಸಹಾಯಮಾಡು ಎಂದು ಕೂಗಿ ಹೇಳಿದನು.",
			"25": "<sup>25</sup> ಆಗ ಯೇಸು ಜನರ ಗುಂಪು ಓಡಿಬರುವದನ್ನು ಕಂಡು ಆ ದೆವ್ವವನ್ನು ಗದರಿಸಿ - ಎಲೇ, ಕಿವುಡಾದ ಮೂಗದೆವ್ವವೇ, ಇವನನ್ನು ಬಿಟ್ಟುಹೋಗು; ಇನ್ನು ಮುಂದೆ ಹಿಡಿಯಬೇಡವೆಂದು ನಾನು ನಿನಗೆ ಅಪ್ಪಣೆಕೊಡುತ್ತೇನೆ ಅಂದನು.",
			"26": "<sup>26</sup> ಆಗ ಅದು ಅಬ್ಬರಿಸಿ ಅವನನ್ನು ಒದ್ದಾಡಿಸಿ ಬಿಟ್ಟು ಹೋಯಿತು. ಆ ಹುಡುಗನು ಸತ್ತಹಾಗೆ ಬಿದ್ದಿದ್ದರಿಂದ ಬಹುಜನರು - ಸತ್ತುಹೋದ ಅಂದರು.",
			"27": "<sup>27</sup> ಆದರೆ ಯೇಸು ಅವನನ್ನು ಕೈಹಿಡಿದು ಎಬ್ಬಿಸಲು ಅವನು ಎದ್ದು ನಿಂತನು.",
			"28": "<sup>28</sup> ಯೇಸು ಮನೆಗೆ ಬಂದಾಗ ಆತನ ಶಿಷ್ಯರು - ಅದನ್ನು ಬಿಡಿಸಲಿಕ್ಕೆ ನಮ್ಮಿಂದ ಯಾಕೆ ಆಗಲಿಲ್ಲವೆಂದು ಆತನನ್ನು ಏಕಾಂತದಲ್ಲಿ ಕೇಳಲು",
			"29": "<sup>29</sup> ಆತನು - ಈ ಜಾತಿಯು ದೇವರ ಪ್ರಾರ್ಥನೆ ಮತ್ತು ಉಪವಾಸದಿಂದಲೇ ಹೊರತು ಬೇರೆ ಯಾತರಿಂದಲೂ ಬಿಟ್ಟು ಹೋಗುವದಿಲ್ಲವೆಂದು ಅವರಿಗೆ ಹೇಳಿದನು.",
			"30": "<sup>30</sup> ಅಲ್ಲಿಂದ ಅವರು ಹೊರಟು ಗಲಿಲಾಯವನ್ನು ಹಾದು ಹೋಗುತ್ತಿದ್ದರು. ಇದು ಜನರಿಗೆ ಗೊತ್ತಾಗಬಾರದೆಂದು ಆತನಿಗೆ ಮನಸ್ಸಿತ್ತು.",
			"31": "<sup>31</sup> ಯಾಕಂದರೆ ಆತನು ತನ್ನ ಶಿಷ್ಯರಿಗೆ ಉಪದೇಶಮಾಡುತ್ತಾ - ಮನುಷ್ಯಕುಮಾರನು ಜನರ ಕೈಗೆ ಒಪ್ಪಿಸಲ್ಪಡುತ್ತಾನೆ; ಅವರು ಅವನನ್ನು ಕೊಲ್ಲುವರು; ಕೊಂದ ಮೂರು ದಿನದ ಮೇಲೆ ಆತನು ಜೀವಿತನಾಗಿ ಎದ್ದು ಬರುವನೆಂದು ಹೇಳಿದನು.",
			"32": "<sup>32</sup> ಅವರು ಆ ಮಾತನ್ನು ಗ್ರಹಿಸಲಿಲ್ಲ, ಆದರೆ ಆತನನ್ನು ಕೇಳುವದಕ್ಕೆ ಅಂಜಿದರು.",
			"33": "<sup>33</sup> ಮತ್ತು ಅವರು ಕಪೆರ್ನೌಮಿಗೆ ಬಂದರು. ಅಲ್ಲಿ ಆತನು ಮನೆಯಲ್ಲಿದ್ದಾಗ - ನೀವು ದಾರಿಯಲ್ಲಿ ಏನು ಮಾತಾಡಿಕೊಳ್ಳುತ್ತಿದ್ದಿರಿ ಎಂದು ಶಿಷ್ಯರನ್ನು ಕೇಳಲು ಅವರು ಸುಮ್ಮನಿದ್ದರು;",
			"34": "<sup>34</sup> ಯಾಕಂದರೆ ಅವರು ದಾರಿಯಲ್ಲಿ ಒಬ್ಬರಕೂಡೊಬ್ಬರು - [ತಮ್ಮಲ್ಲಿ] ಯಾವನು ಹೆಚ್ಚಿನವನೆಂದು ವಾಗ್ವಾದಮಾಡಿಕೊಂಡಿದ್ದರು.",
			"35": "<sup>35</sup> ಆತನು ಕೂತುಕೊಂಡು ತನ್ನ ಹನ್ನೆರಡು ಮಂದಿ ಶಿಷ್ಯರನ್ನು ಕರೆದು ಅವರಿಗೆ - ಯಾವನಾದರೂ ಮೊದಲಿನವನಾಗಬೇಕೆಂದಿದ್ದರೆ ಅವನು ಎಲ್ಲರಲ್ಲಿ ಕಡೆಯವನೂ ಎಲ್ಲರ ಆಳೂ ಆಗಿರಬೇಕು ಎಂದು ಹೇಳಿ",
			"36": "<sup>36</sup> ಒಂದು ಚಿಕ್ಕ ಮಗುವನ್ನು ತೆಗೆದುಕೊಂಡು ಅವರ ನಡುವೆ ನಿಲ್ಲಿಸಿ ಅಪ್ಪಿಕೊಂಡು ಅವರಿಗೆ -",
			"37": "<sup>37</sup> ಯಾವನಾದರೂ ನನ್ನ ಹೆಸರಿನಲ್ಲಿ ಇಂಥ ಚಿಕ್ಕ ಮಕ್ಕಳಲ್ಲಿ ಒಂದನ್ನು ಸೇರಿಸಿಕೊಂಡರೆ ನನ್ನನ್ನು ಸೇರಿಸಿಕೊಂಡ ಹಾಗಾಯಿತು; ಮತ್ತು ಯಾವನಾದರೂ ನನ್ನನ್ನು ಸೇರಿಸಿಕೊಂಡರೆ ನನ್ನನ್ನಲ್ಲ, ನನ್ನನ್ನು ಕಳುಹಿಸಿಕೊಟ್ಟಾತನನ್ನೇ ಸೇರಿಸಿಕೊಂಡ ಹಾಗಾಯಿತು ಎಂದು ಹೇಳಿದನು.",
			"38": "<sup>38</sup> ಯೋಹಾನನು ಆತನಿಗೆ - ಬೋಧಕನೇ, ಯಾವನೋ ಒಬ್ಬನು ನಿನ್ನ ಹೆಸರನ್ನು ಹೇಳಿ ದೆವ್ವ ಬಿಡಿಸುವದನ್ನು ನಾವು ಕಂಡು ಅವನು ನಮಗೆ ಸೇರಿದವನಲ್ಲವಾದ್ದರಿಂದ ಅವನಿಗೆ ಅಡ್ಡಿಮಾಡಿದೆವು ಎಂದು ಹೇಳಲಾಗಿ ಯೇಸು -",
			"39": "<sup>39</sup> ಅವನಿಗೆ ಅಡ್ಡಿಮಾಡಬೇಡಿರಿ; ನನ್ನ ಹೆಸರಿನ ಮೇಲೆ ಮಹತ್ತನ್ನು ಮಾಡಿ ಏಕಾಏಕಿ ನನ್ನನ್ನು ದೂಷಿಸುವವನು ಒಬ್ಬನೂ ಇಲ್ಲ.",
			"40": "<sup>40</sup> ನಮ್ಮನ್ನು ಎದುರಿಸದವನು ನಮ್ಮ ಪಕ್ಷದವನೇ.",
			"41": "<sup>41</sup> ನೀವು ಕ್ರಿಸ್ತನವರೆಂದು ನಿಮಗೆ ಯಾವನಾದರೂ ಒಂದು ತಂಬಿಗೆ ನೀರನ್ನು ಕುಡಿಯುವದಕ್ಕೆ ಕೊಟ್ಟರೆ ಬರತಕ್ಕ ಪ್ರತಿಫಲವು ಅವನಿಗೆ ತಪ್ಪುವದೇ ಇಲ್ಲವೆಂದು ನಿಮಗೆ ಸತ್ಯವಾಗಿ ಹೇಳುತ್ತೇನೆ.",
			"42": "<sup>42</sup> ಮತ್ತು ನಂಬುವ ಈ ಚಿಕ್ಕವರಲ್ಲಿ ಒಬ್ಬನಿಗೆ ಯಾವನಾದರೂ ಅಡ್ಡಿಯಾದರೆ ಅಂಥವನ ಕೊರಳಿಗೆ ಬೀಸುವ ಕಲ್ಲು ಕಟ್ಟಿ ಅವನನ್ನು ಸಮುದ್ರದಲ್ಲಿ ಹಾಕಿದರೂ ಅವನಿಗೆ ಲೇಸು.",
			"43": "<sup>43</sup> ಇದಲ್ಲದೆ ನಿನ್ನ ಕೈ ನಿನ್ನನ್ನು ಪಾಪದಲ್ಲಿ ಸಿಕ್ಕಿಸುವದಾದರೆ ಅದನ್ನು ಕತ್ತರಿಸಿಬಿಡು; ಎರಡು ಕೈಯುಳ್ಳವನಾಗಿದ್ದು ಆರದ ಬೆಂಕಿಯಾಗಿರುವ ನರಕದಲ್ಲಿ ಬೀಳುವದಕ್ಕಿಂತ ಕೈಕಳಕೊಂಡವನಾಗಿ ಜೀವದಲ್ಲಿ ಸೇರುವದು ನಿನಗೆ ಉತ್ತಮ.",
			"45": "<sup>45</sup> ಮತ್ತು ನಿನ್ನ ಕಾಲು ನಿನ್ನನ್ನು ಪಾಪದಲ್ಲಿ ಸಿಕ್ಕಿಸುವದಾದರೆ ಅದನ್ನು ಕಡಿದುಬಿಡು; ಎರಡು ಕಾಲುಳ್ಳವನಾಗಿದ್ದು ನರಕದಲ್ಲಿ ಹಾಕಿಸಿಕೊಳ್ಳುವದಕ್ಕಿಂತ ಕಾಲುಕಳಕೊಂಡವನಾಗಿ ಜೀವದಲ್ಲಿ ಸೇರುವದು ನಿನಗೆ ಉತ್ತಮ.",
			"47": "<sup>47</sup> ನಿನ್ನ ಕಣ್ಣು ನಿನ್ನನ್ನು ಪಾಪದಲ್ಲಿ ಸಿಕ್ಕಿಸುವದಾದರೆ ಅದನ್ನು ಕಿತ್ತು ಬಿಸಾಡು; ಎರಡು ಕಣ್ಣುಳ್ಳವನಾಗಿದ್ದು ನರಕದಲ್ಲಿ ಹಾಕಿಸಿಕೊಳ್ಳುವದಕ್ಕಿಂತ ಒಂದೇ ಕಣ್ಣುಳ್ಳವನಾಗಿದ್ದು ದೇವರ ರಾಜ್ಯದಲ್ಲಿ ಸೇರುವದು ನಿನಗೆ ಉತ್ತಮ.",
			"48": "<sup>48</sup> ನರಕದಲ್ಲಿ ಅವರನ್ನು ಕಡಿಯುವ ಹುಳ ಸಾಯುವದಿಲ್ಲ, ಬೆಂಕಿ ಆರುವದಿಲ್ಲ.",
			"49": "<sup>49</sup> ಪ್ರತಿಯೊಬ್ಬನಿಗೂ ಬೆಂಕಿಯೆಂಬ ಉಪ್ಪಿನ ಹದವು ಕೊಡಲ್ಪಡಬೇಕು.",
			"50": "<sup>50</sup> ಉಪ್ಪು ಒಳ್ಳೆಯ ಪದಾರ್ಥವೇ; ಉಪ್ಪು ಸಪ್ಪೆಯಾದರೆ ಅದನ್ನು ಇನ್ಯಾತರಿಂದ ರುಚಿಗೊಳಿಸುವಿರಿ? ನಿಮ್ಮೊಳಗೆ ಉಪ್ಪು ಇರಲಿ, ಒಬ್ಬರಿಗೊಬ್ಬರು ಸಮಾಧಾನದಿಂದಿರಿ."
		},
		"10": {
			"1": "<sup>1</sup> ಅಲ್ಲಿಂದ ಆತನು ಎದ್ದು ಯೂದಾಯ ಪ್ರಾಂತ್ಯಕ್ಕೂ ಯೊರ್ದನ್ ಹೊಳೆಯ ಆಚೆಯ ಸೀಮೆಗೂ ಬಂದನು. ಅಲ್ಲಿ ಜನರು ತಿರಿಗಿ ಗುಂಪು ಗುಂಪಾಗಿ ಆತನ ಬಳಿಗೆ ಕೂಡಿ ಬರಲು ಆತನು ತಿರಿಗಿ ಎಂದಿನಂತೆ ಅವರಿಗೆ ಉಪದೇಶಮಾಡುತ್ತಿದ್ದನು.",
			"2": "<sup>2</sup> ಆಗ ಫರಿಸಾಯರು ಹತ್ತಿರಕ್ಕೆ ಬಂದು ಆತನನ್ನು ಪರೀಕ್ಷಿಸಬೇಕೆಂಬ ಯೋಚನೆಯಿಂದ - ಒಬ್ಬನು ಹೆಂಡತಿಯನ್ನು ಬಿಟ್ಟುಬಿಡುವದು ಧರ್ಮವೋ ಹೇಗೆ ಎಂದು ಕೇಳಿದರು.",
			"3": "<sup>3</sup> ಆತನು ಅದಕ್ಕೆ - ಮೋಶೆ ನಿಮಗೆ ಏನು ಆಜ್ಞೆಕೊಟ್ಟನು ಅನ್ನಲಾಗಿ ಅವರು -",
			"4": "<sup>4</sup> ತ್ಯಾಗಪತ್ರವನ್ನು ಬರೆದುಕೊಟ್ಟು ಆಕೆಯನ್ನು ಬಿಟ್ಟುಬಿಡಬಹುದೆಂದು ಮೋಶೆಯು ಅಪ್ಪಣೆಕೊಟ್ಟನು ಅಂದರು.",
			"5": "<sup>5</sup> ಆದರೆ ಯೇಸು ಅವರಿಗೆ - ಅವನು ನಿಮ್ಮ ಮೊಂಡತನವನ್ನು ನೋಡಿ ನಿಮಗೆ ಇಂಥ ಆಜ್ಞೆಯನ್ನು ಬರೆದಿಟ್ಟನು.",
			"6": "<sup>6</sup> ದೇವರಾದರೋ ಸೃಷ್ಟಿಯ ಮೊದಲಿನಿಂದಲೇ ಮನುಷ್ಯರನ್ನು ಗಂಡುಹೆಣ್ಣಾಗಿ ನಿರ್ಮಿಸಿದನೆಂತಲೂ",
			"7": "<sup>7</sup> ಈ ಕಾರಣದಿಂದ ಪುರುಷನು ತನ್ನ ತಂದೆತಾಯಿಗಳನ್ನು ಬಿಟ್ಟು ತನ್ನ ಹೆಂಡತಿಯನ್ನು ಸೇರಿಕೊಂಡು ಅವರಿಬ್ಬರು ಒಂದೇ ಶರೀರವಾಗಿರುವರೆಂತಲೂ ಬರೆದದೆ.",
			"8": "<sup>8</sup> ಹೀಗಿರುವಲ್ಲಿ ಅವರು ಇನ್ನು ಇಬ್ಬರಲ್ಲ, ಒಂದೇ ಶರೀರವಾಗಿದ್ದಾರೆ.",
			"9": "<sup>9</sup> ಆದದರಿಂದ ದೇವರು ಕೂಡಿಸಿದ್ದನ್ನು ಮನುಷ್ಯರು ಅಗಲಿಸಬಾರದು ಎಂದು ಹೇಳಿದನು.",
			"10": "<sup>10</sup> ಮನೆಯಲ್ಲಿ ಆತನ ಶಿಷ್ಯರು ಈ ವಿಷಯದಲ್ಲಿ ಆತನನ್ನು ತಿರಿಗಿ ಕೇಳಿದಾಗ ಆತನು ಅವರಿಗೆ -",
			"11": "<sup>11</sup> ತನ್ನ ಹೆಂಡತಿಯನ್ನು ಬಿಟ್ಟು ಮತ್ತೊಬ್ಬಳನ್ನು ಮದುವೆಮಾಡಿಕೊಳ್ಳುವವನು ತನ್ನ ಹೆಂಡತಿಗೆ ದ್ರೋಹಿಯಾಗಿ ವ್ಯಭಿಚಾರ ಮಾಡಿದವನಾಗಿದ್ದಾನೆ;",
			"12": "<sup>12</sup> ಮತ್ತು ಹೆಂಡತಿಯು ತನ್ನ ಗಂಡನನ್ನು ಬಿಟ್ಟು ಬೇರೊಬ್ಬನನ್ನು ಮದುವೆಮಾಡಿಕೊಂಡರೆ ಅವಳು ವ್ಯಭಿಚಾರ ಮಾಡಿದವಳಾಗಿದ್ದಾಳೆ ಎಂದು ಹೇಳಿದನು.",
			"13": "<sup>13</sup> ಬಳಿಕ ಕೆಲವರು ತಮ್ಮ ಚಿಕ್ಕ ಮಕ್ಕಳನ್ನು ಯೇಸುವಿನಿಂದ ಮುಟ್ಟಿಸಬೇಕೆಂದು ಆತನ ಬಳಿಗೆ ತರಲು ಶಿಷ್ಯರು ಅವರನ್ನು ಗದರಿಸಿದರು.",
			"14": "<sup>14</sup> ಆದರೆ ಯೇಸು ಅದನ್ನು ಕಂಡು ಕೋಪಗೊಂಡು ಅವರಿಗೆ - ಮಕ್ಕಳನ್ನು ನನ್ನ ಹತ್ತಿರಕ್ಕೆ ಬರಗೊಡಿಸಿರಿ; ಅವುಗಳಿಗೆ ಅಡ್ಡಿಮಾಡಬೇಡಿರಿ; ದೇವರ ರಾಜ್ಯವು ಇಂಥವರದೇ.",
			"15": "<sup>15</sup> ನಿಮಗೆ ಸತ್ಯವಾಗಿ ಹೇಳುತ್ತೇನೆ, ಯಾವನು ಶಿಶುಭಾವದಿಂದ ದೇವರ ರಾಜ್ಯವನ್ನು ಅಂಗೀಕರಿಸುವದಿಲ್ಲವೋ ಅವನು ಅದರಲ್ಲಿ ಸೇರುವದೇ ಇಲ್ಲ ಎಂದು ಹೇಳಿ",
			"16": "<sup>16</sup> ಅವುಗಳನ್ನು ಅಪ್ಪಿಕೊಂಡು ಅವುಗಳ ಮೇಲೆ ಕೈಯಿಟ್ಟು ಆಶೀರ್ವದಿಸಿದನು.",
			"17": "<sup>17</sup> ಆತನು ಹೊರಟು ದಾರಿಹಿಡಿದು ಹೋಗುವಾಗ ಒಬ್ಬನು ಓಡುತ್ತಾ ಆತನ ಎದುರಿಗೆ ಬಂದು ಮೊಣಕಾಲೂರಿ - ಒಳ್ಳೇ ಬೋಧಕನೇ, ನಾನು ನಿತ್ಯಜೀವಕ್ಕೆ ಬಾಧ್ಯಸ್ಥನಾಗಬೇಕಾದರೆ ಏನು ಮಾಡಬೇಕೆಂದು ಆತನನ್ನು ಕೇಳಲಾಗಿ ಯೇಸು ಅವನಿಗೆ -",
			"18": "<sup>18</sup> ನನ್ನನ್ನು ಒಳ್ಳೆಯವನೆಂದು ಯಾಕೆ ಹೇಳುತ್ತೀ? ದೇವರೊಬ್ಬನೇ ಹೊರತು ಮತ್ತಾವನೂ ಒಳ್ಳೇಯವನಲ್ಲ.",
			"19": "<sup>19</sup> ನರಹತ್ಯ ಮಾಡಬಾರದು; ವ್ಯಭಿಚಾರಮಾಡಬಾರದು; ಕದಿಯಬಾರದು; ಸುಳ್ಳುಸಾಕ್ಷಿ ಹೇಳಬಾರದು; ಮೋಸಮಾಡಬಾರದು; ನಿನ್ನ ತಂದೆತಾಯಿಗಳನ್ನು ಸನ್ಮಾನಿಸಬೇಕು ಎಂಬ ದೇವರಾಜ್ಞೆಗಳು ನಿನಗೆ ಗೊತ್ತಿವೆಯಷ್ಟೆ ಎಂದು ಹೇಳಿದನು.",
			"20": "<sup>20</sup> ಅವನು ಆತನಿಗೆ - ಬೋಧಕನೇ, ನಾನು ಚಿಕ್ಕಂದಿನಿಂದಲೂ ಇವೆಲ್ಲಕ್ಕೂ ಸರಿಯಾಗಿ ನಡಕೊಂಡು ಬಂದಿದ್ದೇನೆ ಎಂದು ಹೇಳಲು",
			"21": "<sup>21</sup> ಯೇಸು ಅವನನ್ನು ದೃಷ್ಟಿಸಿನೋಡಿ ಪ್ರೀತಿಸಿ ಅವನಿಗೆ - ನಿನಗೆ ಒಂದು ಕಡಿಮೆಯಾಗಿದೆ; ಹೋಗು, ನಿನ್ನ ಬದುಕನ್ನೆಲ್ಲಾ ಮಾರಿ ಬಡವರಿಗೆ ಕೊಡು; ಪರಲೋಕದಲ್ಲಿ ನಿನಗೆ ಸಂಪತ್ತಿರುವದು; ನೀನು ಬಂದು ನನ್ನನ್ನು ಹಿಂಬಾಲಿಸು ಎಂದು ಹೇಳಿದನು.",
			"22": "<sup>22</sup> ಆದರೆ ಅವನು ಬಹಳ ಆಸ್ತಿಯುಳ್ಳವನಾಗಿದ್ದದರಿಂದ ಈ ಮಾತಿಗೆ ಮೋರೆ ಬಾಡಿದವನಾಗಿ ದುಃಖದಿಂದ ಹೊರಟುಹೋದನು.",
			"23": "<sup>23</sup> ಆಗ ಯೇಸು ಸುತ್ತಲೂ ನೋಡಿ ತನ್ನ ಶಿಷ್ಯರಿಗೆ - ಧನವಂತರು ದೇವರ ರಾಜ್ಯದಲ್ಲಿ ಸೇರುವದು ಎಷ್ಟೋ ಕಷ್ಟ ಎಂದು ಹೇಳಿದನು.",
			"24": "<sup>24</sup> ಶಿಷ್ಯರು ಆತನ ಮಾತುಗಳನ್ನು ಕೇಳಿ ಬೆರಗಾದರು. ಯೇಸು ತಿರಿಗಿ ಅವರಿಗೆ - ಮಕ್ಕಳಿರಾ, ದೇವರ ರಾಜ್ಯದಲ್ಲಿ ಸೇರುವದು ಎಷ್ಟೋ ಕಷ್ಟ.",
			"25": "<sup>25</sup> ಐಶ್ವರ್ಯವಂತನು ದೇವರ ರಾಜ್ಯದಲ್ಲಿ ಸೇರುವದಕ್ಕಿಂತ ಒಂಟೆಯು ಸೂಜೀಕಣ್ಣಿನಲ್ಲಿ ನುಗ್ಗಿ ಹೋಗುವದು ಸುಲಭ ಎಂದು ಹೇಳಿದನು.",
			"26": "<sup>26</sup> ಈ ಮಾತಿಗೆ ಅವರು ಇನ್ನಷ್ಟಾಶ್ಚರ್ಯಪಟ್ಟು - [ಹೀಗಿದ್ದರೆ] ಯಾರಿಗೆ ರಕ್ಷಣೆಯಾದೀತು ಎಂದು ತಮ್ಮ ತಮ್ಮೊಳಗೆ ಹೇಳಿಕೊಳ್ಳಲು",
			"27": "<sup>27</sup> ಯೇಸು ಅವರನ್ನು ದೃಷ್ಟಿಸಿ ನೋಡಿ - ಇದು ಮನುಷ್ಯರಿಗೆ ಅಸಾಧ್ಯ; ದೇವರಿಗೆ ಅಸಾಧ್ಯವಲ್ಲ; ದೇವರಿಗೆ ಎಲ್ಲವು ಸಾಧ್ಯವೇ ಅಂದನು.",
			"28": "<sup>28</sup> ಪೇತ್ರನು ಆತನಿಗೆ - ಇಗೋ, ನಾವು ಎಲ್ಲಾ ಬಿಟ್ಟುಬಿಟ್ಟು ನಿನ್ನನ್ನು ಹಿಂಬಾಲಿಸಿದ್ದೇವೆ ಎಂದು ಹೇಳುವದಕ್ಕೆ ಪ್ರಾರಂಭಿಸಲು ಯೇಸು -",
			"29": "<sup>29</sup> ನಿಮಗೆ ಸತ್ಯವಾಗಿ ಹೇಳುತ್ತೇನೆ, ಯಾವನು ನನ್ನ ನಿಮಿತ್ತವೂ ಸುವಾರ್ತೆಯ ನಿಮಿತ್ತವೂ ಮನೆಯನ್ನಾಗಲಿ ಅಣ್ಣತಮ್ಮಂದಿರನ್ನಾಗಲಿ ಅಕ್ಕತಂಗಿಯರನ್ನಾಗಲಿ ತಾಯಿಯನ್ನಾಗಲಿ ತಂದೆಯನ್ನಾಗಲಿ ಮಕ್ಕಳನ್ನಾಗಲಿ ಭೂಮಿಯನ್ನಾಗಲಿ ಬಿಟ್ಟುಬಿಟ್ಟಿರುವನೋ",
			"30": "<sup>30</sup> ಅವನಿಗೆ ಈಗಿನ ಕಾಲದಲ್ಲಿ ಮನೆ ಅಣ್ಣ ತಮ್ಮ ಅಕ್ಕ ತಂಗಿ ತಾಯಿ ಮಕ್ಕಳು ಭೂಮಿ ಇವೆಲ್ಲವೂ ಹಿಂಸೆಗಳು ಸಹಿತವಾಗಿ ನೂರರಷ್ಟು ಸಿಕ್ಕೇ ಸಿಕ್ಕುತ್ತವೆ; ಮತ್ತು ಮುಂದಣ ಲೋಕದಲ್ಲಿ ನಿತ್ಯಜೀವವು ದೊರೆಯುವದು.",
			"31": "<sup>31</sup> ಆದರೆ ಬಹುಮಂದಿ ಮೊದಲಿನವರು ಕಡೆಯವರಾಗುವರು, ಕಡೆಯವರು ಮೊದಲಿನವರಾಗುವರು ಎಂದು ಹೇಳಿದನು.",
			"32": "<sup>32</sup> ಅವರು ಯೆರೂಸಲೇಮಿನ ದಾರಿಯನ್ನು ಹಿಡಿದು ಹೋಗುತ್ತಿರುವಾಗ ಯೇಸು ಅವರ ಮುಂದೆ ಮುಂದೆ ಹೋಗುತ್ತಿದ್ದನು. ಅವರು [ನೋಡಿ] ಬೆರಗಾದರು; ಆತನ ಹಿಂದೆ ಬರುವವರು ಭಯಪಡುವವರಾದರು. ಆಗ ಆತನು ತನ್ನ ಹನ್ನೆರಡು ಮಂದಿ ಶಿಷ್ಯರನ್ನು ತಿರಿಗಿ ಕರೆದುಕೊಂಡು ಮುಂದೆ ತನಗಾಗುವದನ್ನು ಹೇಳುವದಕ್ಕೆ ತೊಡಗಿ ಅವರಿಗೆ -",
			"33": "<sup>33</sup> ನೋಡಿರಿ, ನಾವು ಯೆರೂಸಲೇಮಿಗೆ ಹೋಗುತ್ತಾ ಇದ್ದೇವೆ. ಮತ್ತು ಮನುಷ್ಯಕುಮಾರನನ್ನು ಮಹಾಯಾಜಕರ ಮತ್ತು ಶಾಸ್ತ್ರಿಗಳ ಕೈಗೆ ಹಿಡುಕೊಡುವರು. ಅವರು ಅವನಿಗೆ ಮರಣದಂಡನೆಯನ್ನು ವಿಧಿಸಿ ಅವನನ್ನು ಅನ್ಯರ ಕೈಗೆ ಒಪ್ಪಿಸುವರು.",
			"34": "<sup>34</sup> ಇವರು ಅವನನ್ನು ಅಪಹಾಸ್ಯಮಾಡುವರು, ಅವನ ಮೇಲೆ ಉಗುಳುವರು, ಅವನನ್ನು ಕೊರಡೆಗಳಿಂದ ಹೊಡೆಯುವರು, ಕೊಂದುಹಾಕುವರು. ಅವನು ಮೂರು ದಿನದ ಮೇಲೆ ಜೀವಿತನಾಗಿ ಎದ್ದು ಬರುವನು ಎಂದು ಹೇಳಿದನು.",
			"35": "<sup>35</sup> ಆಗ ಜೆಬೆದಾಯನ ಮಕ್ಕಳಾದ ಯಾಕೋಬ ಯೋಹಾನರು ಆತನ ಬಳಿಗೆ ಬಂದು ಆತನಿಗೆ - ಗುರುವೇ, ನಾವು ಒಂದನ್ನು ಕೇಳಿಕೊಳ್ಳುತ್ತೇವೆ; ಅದನ್ನು ನಮಗೋಸ್ಕರ ನಡಿಸಿಕೊಡಬೇಕೆಂದು ಹೇಳಿದರು.",
			"36": "<sup>36</sup> ಆತನು ಅವರನ್ನು - ನಿಮಗೇನು ನಡಿಸಿಕೊಡಬೇಕು ಎಂದು ಕೇಳಲು ಅವರು -",
			"37": "<sup>37</sup> ನಿನಗೆ ಮಹಾಪದವಿ ಬಂದಾಗ ನಮ್ಮಲ್ಲಿ ಒಬ್ಬನು ನಿನ್ನ ಬಲಗಡೆಯಲ್ಲೂ ಮತ್ತೊಬ್ಬನು ಎಡಗಡೆಯಲ್ಲೂ ಕೂತುಕೊಳ್ಳುವಂತೆ ನಮಗೆ ಅನುಗ್ರಹಮಾಡಬೇಕು ಅಂದರು.",
			"38": "<sup>38</sup> ಆದರೆ ಯೇಸು ಅವರನ್ನು - ನೀವು ಬೇಡಿಕೊಂಡದ್ದು ಏನೆಂದು ನಿಮಗೆ ತಿಳಿಯದು; ನಾನು ಕುಡಿಯುವ ಪಾತ್ರೆಯಲ್ಲಿ ಕುಡಿಯುವದು ನಿಮ್ಮಿಂದಾದೀತೇ? ನನಗಾಗುವ ದೀಕ್ಷಾಸ್ನಾನವನ್ನು ಹೊಂದುವದು ನಿಮ್ಮಿಂದಾದೀತೇ ಎಂದು ಕೇಳಲು ಅವರು - ಆಗುವದು ಅಂದರು.",
			"39": "<sup>39</sup> ಆಗ ಯೇಸು ಅವರಿಗೆ - ನಾನು ಕುಡಿಯುವ ಪಾತ್ರೆಯಲ್ಲಿ ಕುಡಿಯುವಿರಿ; ನನಗಾಗುವ ಸ್ನಾನವು ನಿಮಗಾಗುವದು;",
			"40": "<sup>40</sup> ಆದರೆ ನನ್ನ ಬಲಗಡೆಯಲ್ಲಾದರೂ ಎಡಗಡೆಯಲ್ಲಾದರೂ ಕೂತುಕೊಳ್ಳುವಂತೆ ಅನುಗ್ರಹಮಾಡುವದು ನನ್ನದಲ್ಲ; ಅದು ಯಾರಿಗೆಂದು ಸಿದ್ಧಪಡಿಸಿದೆಯೋ ಅವರಿಗೇ ಸಿಕ್ಕುವದು ಎಂದು ಹೇಳಿದನು.",
			"41": "<sup>41</sup> ಉಳಿದ ಹತ್ತು ಮಂದಿ ಶಿಷ್ಯರು ಇದನ್ನು ಕೇಳಿ ಯಾಕೋಬ ಯೋಹಾನರ ಮೇಲೆ ಸಿಟ್ಟಿಗೆದ್ದರು.",
			"42": "<sup>42</sup> ಯೇಸು ಅವರನ್ನು ಹತ್ತಿರಕ್ಕೆ ಕರೆದು ಅವರಿಗೆ - ಜನಗಳನ್ನಾಳುವವರೆನಿಸಿಕೊಳ್ಳುವವರು ಅವರ ಮೇಲೆ ಅಹಂಕಾರದಿಂದ ದೊರೆತನಮಾಡುತ್ತಾರೆ, ಮತ್ತು ಅವರಲ್ಲಿ ದೊಡ್ಡವರು ಬಲಾತ್ಕಾರದಿಂದ ಅಧಿಕಾರ ನಡಿಸುತ್ತಾರೆ ಎಂದು ನೀವು ಬಲ್ಲಿರಷ್ಟೆ;",
			"43": "<sup>43</sup> ಆದರೆ ನಿಮ್ಮಲ್ಲಿ ಹಾಗಿರಬಾರದು; ನಿಮ್ಮಲ್ಲಿ ದೊಡ್ಡವನಾಗಬೇಕೆಂದಿರುವವನು ನಿಮ್ಮ ಸೇವಕನಾಗಿರಬೇಕು;",
			"44": "<sup>44</sup> ನಿಮ್ಮಲ್ಲಿ ಮೊದಲನೆಯವನಾಗಬೇಕೆಂದಿರುವವನು ಎಲ್ಲರ ಆಳಾಗಿರಬೇಕು.",
			"45": "<sup>45</sup> ಮನುಷ್ಯಕುಮಾರನು ಸಹ ಸೇವೆಮಾಡಿಸಿಕೊಳ್ಳುವದಕ್ಕೆ ಬರಲಿಲ್ಲ, ಸೇವೆಮಾಡುವದಕ್ಕೂ ಅನೇಕರನ್ನು ಬಿಡಿಸಿಕೊಳ್ಳುವದಕ್ಕಾಗಿ ತನ್ನ ಪ್ರಾಣವನ್ನು ಈಡುಕೊಡುವದಕ್ಕೂ ಬಂದನು ಎಂದು ಹೇಳಿದನು.",
			"46": "<sup>46</sup> ಆಮೇಲೆ ಅವರು ಯೆರಿಕೋವಿಗೆ ಬಂದರು. ಆತನೂ ಆತನ ಶಿಷ್ಯರೂ ಬಹು ಜನರ ಗುಂಪೂ ಆ ಊರಿನಿಂದ ಹೊರಟು ಹೋಗುತ್ತಿರುವಾಗ ತಿಮಾಯನ ಮಗನಾದ ಬಾರ್ತಿಮಾಯನು ದಾರಿಯ ಮಗ್ಗುಲಲ್ಲಿ ಕೂತಿದ್ದನು.",
			"47": "<sup>47</sup> ಅವನು ಕುರುಡ ಭಿಕ್ಷಗಾರನು. ಈ ಮಾರ್ಗವಾಗಿ ಹೋಗುವವನು ನಜರೇತಿನ ಯೇಸುವೆಂದು ಅವನು ಕೇಳಿ - ದಾವೀದನ ಕುಮಾರನೇ, ಯೇಸುವೇ, ನನ್ನನ್ನು ಕರುಣಿಸು ಎಂದು ಕೂಗಿಕೊಳ್ಳುವದಕ್ಕೆ ಪ್ರಾರಂಭಿಸಿದನು.",
			"48": "<sup>48</sup> ಅನೇಕರು - ಸುಮ್ಮನಿರು ಎಂದು ಅವನನ್ನು ಗದರಿಸಲು ಅವನು - ದಾವೀದನ ಕುಮಾರನೇ, ನನ್ನನ್ನು ಕರುಣಿಸು ಎಂದು ಮತ್ತಷ್ಟು ಕೂಗಿಕೊಂಡನು.",
			"49": "<sup>49</sup> ಆಗ ಯೇಸು ನಿಂತು - ಅವನನ್ನು ಕರೆಯಿರಿ ಅನ್ನಲು ಅವರು ಆ ಕುರುಡನನ್ನು ಕರೆದು - ಧೈರ್ಯವಿರಲಿ, ಏಳು, ನಿನ್ನನ್ನು ಕರೆಯುತ್ತಾನೆ ಅಂದರು.",
			"50": "<sup>50</sup> ಅವನು ತನ್ನ ಹೊದಿಕೆಯನ್ನು ತೆಗೆದುಹಾಕಿ ತಟ್ಟನೆ ಎದ್ದು ಯೇಸುವಿನ ಬಳಿಗೆ ಬಂದನು.",
			"51": "<sup>51</sup> ಯೇಸು ಅವನನ್ನು - ನಾನು ನಿನಗೆ ಏನು ಮಾಡಬೇಕೆಂದು ಕೋರುತ್ತೀ ಎಂದು ಕೇಳಲು ಆ ಕುರುಡನು - ನನಗೆ ಕಣ್ಣು ಬರುವಂತೆ ಮಾಡಬೇಕು, ಗುರುವೇ, ಅಂದನು.",
			"52": "<sup>52</sup> ಯೇಸು ಅವನಿಗೆ - ಹೋಗು, ನಿನ್ನ ನಂಬಿಕೆಯೇ ನಿನ್ನನ್ನು ಸ್ವಸ್ಥಮಾಡಿ ಅದೆ ಎಂದು ಹೇಳಿದನು; ಕೂಡಲೇ ಅವನಿಗೆ ಕಣ್ಣು ಬಂದವು; ಅವನು ಆ ದಾರಿಯಲ್ಲಿ ಆತನ ಹಿಂದೆ ಹೋದನು."
		},
		"11": {
			"1": "<sup>1</sup> ಅವರು ಯೆರೂಸಲೇಮಿಗೆ ಸಮೀಪಿಸಿ ಎಣ್ಣೇಮರಗಳ ಗುಡ್ಡದ ಬಳಿಯಲ್ಲಿರುವ ಬೇತ್ಪಗೆಗೂ ಬೇಥಾನ್ಯಕ್ಕೂ ಬಂದಾಗ",
			"2": "<sup>2</sup> ಆತನು ತನ್ನ ಶಿಷ್ಯರಲ್ಲಿ ಇಬ್ಬರನ್ನು ಕರೆದು - ನಿಮ್ಮೆದುರಿಗಿರುವ ಹಳ್ಳಿಗೆ ಹೋಗಿರಿ; ಅದರೊಳಕ್ಕೆ ಸೇರುತ್ತಿರುವಾಗಲೇ ಅಲ್ಲಿ ಕಟ್ಟಿರುವ ಒಂದು ಕತ್ತೇಮರಿಯನ್ನು ಕಾಣುವಿರಿ; ಇದುವರೆಗೆ ಅದರ ಮೇಲೆ ಯಾರೂ ಹತ್ತಿಲ್ಲ; ಅದನ್ನು ಬಿಚ್ಚಿ ಹಿಡುಕೊಂಡು ಬನ್ನಿರಿ.",
			"3": "<sup>3</sup> ಯಾವನಾದರೂ ನಿಮ್ಮನ್ನು - ಏನು ಹೀಗೆ ಮಾಡುವದು ಎಂದು ಕೇಳಿದರೆ - ಇದು ಸ್ವಾಮಿಯವರಿಗೆ ಬೇಕಾಗಿದೆ, ಅವರು ತಡಮಾಡದೆ ತಿರಿಗಿ ಇಲ್ಲಿಗೆ ಕಳುಹಿಸಿಕೊಡುವರು ಅನ್ನಿರಿ ಎಂದು ಹೇಳಿ ಕಳುಹಿಸಿದನು.",
			"4": "<sup>4</sup> ಆ ಶಿಷ್ಯರು ಹೋಗಿ ಬೀದಿಯಲ್ಲಿ ಹೊರಗೆ ಬಾಗಿಲಿನ ಹತ್ತಿರ ಒಂದು ಕತ್ತೇಮರಿ ಕಟ್ಟಿರುವದನ್ನು ಕಂಡು ಅದನ್ನು ಬಿಚ್ಚಿದರು.",
			"5": "<sup>5</sup> ಅಲ್ಲಿ ನಿಂತಿದ್ದವರಲ್ಲಿ ಕೆಲವರು - ಈ ಮರಿಯನ್ನು ಬಿಚ್ಚಿ ಏನು ಮಾಡುತ್ತೀರಿ? ಎಂದು ಅವರನ್ನು ಕೇಳಿದ್ದಕ್ಕೆ",
			"6": "<sup>6</sup> ಯೇಸು ಹೇಳಿದಂತೆಯೇ ಅವರು ಹೇಳಿದಾಗ ಆ ಜನರು ತೆಗೆದುಕೊಂಡು ಹೋಗಗೊಡಿಸಿದರು.",
			"7": "<sup>7</sup> ಶಿಷ್ಯರು ಆ ಮರಿಯನ್ನು ಯೇಸುವಿನ ಬಳಿಗೆ ತಂದು ತಮ್ಮ ಬಟ್ಟೆಗಳನ್ನು ಅದರ ಮೇಲೆ ಹಾಕಲು ಆತನು ಅದನ್ನು ಹತ್ತಿ ಕೂತುಕೊಂಡನು.",
			"8": "<sup>8</sup> ಆಗ ಅನೇಕರು ತಮ್ಮ ಬಟ್ಟೆಗಳನ್ನು ದಾರಿಯಲ್ಲಿ ಹಾಸಿದರು. ಬೇರೆ ಕೆಲವರು ತೋಟಗಳಿಂದ ಚಿಗುರುಗಳನ್ನು ಕೊಯಿದು ತಂದು ಹಾಸಿದರು.",
			"9": "<sup>9</sup> ಆತನ ಹಿಂದೆಯೂ ಮುಂದೆಯೂ ಹೋಗುತ್ತಿದ್ದವರು - ಜಯ, ಕರ್ತನ ಹೆಸರಿನಲ್ಲಿ ಬರುವವನಿಗೆ ಆಶೀರ್ವಾದ;",
			"10": "<sup>10</sup> ನಮ್ಮ ಪಿತೃವಾದ ದಾವೀದನ ರಾಜ್ಯವು ಬರುತ್ತದೆ, ಅದಕ್ಕೆ ಆಶೀರ್ವಾದ; ಮೇಲಣ ಲೋಕಗಳಲ್ಲಿ ಜಯ ಎಂದು ಆರ್ಭಟಿಸಿದರು.",
			"11": "<sup>11</sup> ತರುವಾಯ ಯೇಸು ಯೆರೂಸಲೇಮನ್ನು ಸೇರಿ ದೇವಾಲಯಕ್ಕೆ ಹೋಗಿ ಸುತ್ತಲೂ ಎಲ್ಲಾ ನೋಡಿಕೊಂಡು ಅಷ್ಟರಲ್ಲಿ ಸಂಜೆಯಾದದ್ದರಿಂದ ಹನ್ನೆರಡು ಮಂದಿ ಶಿಷ್ಯರನ್ನು ಕರಕೊಂಡು ಬೇಥಾನ್ಯಕ್ಕೆ ಹೊರಟುಹೋದನು.",
			"12": "<sup>12</sup> ಮರುದಿನ ಅವರು ಬೇಥಾನ್ಯವನ್ನು ಬಿಟ್ಟು ಬರುತ್ತಿರುವಾಗ ಆತನು ಹಸಿದನು.",
			"13": "<sup>13</sup> ಮತ್ತು ಎಲೆಬಿಟ್ಟಿದ್ದ ಒಂದು ಅಂಜೂರದ ಮರವನ್ನು ದೂರದಲ್ಲಿ ಕಂಡು ಅದರಲ್ಲಿ ತನಗೇನಾದರೂ ಹಣ್ಣು ಸಿಕ್ಕೀತೆಂದು ಅಲ್ಲಿಗೆ ಹೋದನು. ಅದರ ಹತ್ತಿರಕ್ಕೆ ಬಂದಾಗ ಅದರಲ್ಲಿ ಬರೀ ಎಲೆಗಳನ್ನೇ ಹೊರತು ಮತ್ತೇನೂ ಕಾಣಲಿಲ್ಲ; ಅದು ಅಂಜೂರದ ಹಣ್ಣಿನ ಕಾಲವಲ್ಲ.",
			"14": "<sup>14</sup> ಆಗ ಯೇಸು ಆ ಮರಕ್ಕೆ - ಇನ್ನು ಮೇಲೆ ಒಬ್ಬರೂ ನಿನ್ನಲ್ಲಿ ಹಣ್ಣನ್ನು ಎಂದೆಂದಿಗೂ ತಿನ್ನದ ಹಾಗಾಗಲಿ ಎಂದು ಹೇಳಿದನು. ಆತನ ಶಿಷ್ಯರು ಅದನ್ನು ಕೇಳಿದರು.",
			"15": "<sup>15</sup> ಅವರು ಯೆರೂಸಲೇಮಿಗೆ ಬಂದಾಗ ಯೇಸು ದೇವಾಲಯಕ್ಕೆ ಹೋಗಿ ಅದರಲ್ಲಿ ಮಾರುವವರನ್ನೂ ಕೊಳ್ಳುವವರನ್ನೂ ಹೊರಡಿಸಿಬಿಡುವದಕ್ಕೆ ಪ್ರಾರಂಭಿಸಿ ಚಿನಿವಾರರ ಮೇಜುಗಳನ್ನೂ ಪಾರಿವಾಳಮಾರುವವರ ಕಾಲ್ಮಣೆಗಳನ್ನೂ ಕೆಡವಿದನು;",
			"16": "<sup>16</sup> ಒಬ್ಬನನ್ನಾದರೂ ಸಾಮಾನು ಹೊತ್ತುಕೊಂಡು ದೇವಾಲಯದೊಳಗೆ ಹಾದು ಹೋಗಗೊಡಿಸಲಿಲ್ಲ.",
			"17": "<sup>17</sup> ಮತ್ತು ಆತನು ಉಪದೇಶಮಾಡಿ - ನನ್ನ ಆಲಯವು ಎಲ್ಲಾ ಜನಾಂಗಗಳಿಗೂ ಪ್ರಾರ್ಥನಾಲಯವೆನಿಸಿಕೊಳ್ಳುವದು ಎಂದು ಬರೆದಿದೆಯಲ್ಲಾ? ಆದರೆ ನೀವು ಅದನ್ನು ಕಳ್ಳರ ಗವಿ ಮಾಡಿದ್ದೀರಿ ಎಂದು ಹೇಳಿದನು.",
			"18": "<sup>18</sup> ಅದನ್ನು ಮಹಾಯಾಜಕರೂ ಶಾಸ್ತ್ರಿಗಳೂ ಕೇಳಿ ಅವನನ್ನು ಯಾವ ಉಪಾಯದಿಂದ ಕೊಲ್ಲೋಣ ಎಂದು ಸಂದರ್ಭ ನೋಡುತ್ತಿದ್ದರು; ಯಾಕಂದರೆ ಜನರೆಲ್ಲಾ ಆತನ ಉಪದೇಶವನ್ನು ಕೇಳಿ ಅತ್ಯಾಶ್ಚರ್ಯಪಟ್ಟದ್ದರಿಂದ ಅವರು ಆತನಿಗೆ ಹೆದರುತ್ತಿದ್ದರು.",
			"19": "<sup>19</sup> ಪ್ರತಿಸಾಯಂಕಾಲ ಯೇಸುವೂ ಆತನ ಶಿಷ್ಯರೂ ಪಟ್ಟಣವನ್ನು ಬಿಟ್ಟು ಹೊರಗೆ ಹೋಗುತ್ತಿದ್ದರು.",
			"20": "<sup>20</sup> ಬೆಳಿಗ್ಗೆ ಅವರು ಬರುತ್ತಿರುವಾಗ ಆ ಅಂಜೂರದ ಮರವು ಬುಡದಿಂದಲೂ ಒಣಗಿ ಹೋಗಿರುವದನ್ನು ಕಂಡರು.",
			"21": "<sup>21</sup> ಆಗ ಪೇತ್ರನು ಹಿಂದಿನ ದಿನದ ಸಂಗತಿಯನ್ನು ನೆನಸಿಕೊಂಡು ಆತನಿಗೆ - ಗುರುವೇ, ಇಗೋ, ನೀನು ಶಾಪಕೊಟ್ಟ ಅಂಜೂರದ ಮರವು ಒಣಗಿಹೋಗಿದೆ ಎಂದು ಹೇಳಿದ್ದಕ್ಕೆ",
			"22": "<sup>22</sup> ಯೇಸು ಹೇಳಿದ್ದೇನಂದರೆ - ನಿಮಗೆ ದೇವರಲ್ಲಿ ನಂಬಿಕೆಯಿರಲಿ.",
			"23": "<sup>23</sup> ನಿಮಗೆ ಸತ್ಯವಾಗಿ ಹೇಳುತ್ತೇನೆ, ಯಾವನಾದರೂ ಈ ಬೆಟ್ಟಕ್ಕೆ - ನೀನು ಕಿತ್ತುಕೊಂಡು ಹೋಗಿ ಸಮುದ್ರದಲ್ಲಿ ಬೀಳು ಎಂದು ಹೇಳಿ ತನ್ನ ಮನಸ್ಸಿನಲ್ಲಿ ಸಂಶಯಪಡದೆ ತಾನು ಹೇಳಿದ್ದು ಆಗುವದೆಂದು ನಂಬಿದರೆ ಅವನು ಹೇಳಿದಂತೆಯೇ ಆಗುವದು.",
			"24": "<sup>24</sup> ಆದಕಾರಣ ನೀವು ಪ್ರಾರ್ಥನೆಮಾಡಿ ಏನೇನು ಬೇಡಿಕೊಳ್ಳುತ್ತೀರೋ ಅದನ್ನೆಲ್ಲಾ ಹೊಂದಿದ್ದೇವೆಂದು ನಂಬಿರಿ; ಅದು ನಿಮಗೆ ಸಿಕ್ಕುವದೆಂದು ನಿಮಗೆ ಹೇಳುತ್ತೇನೆ.",
			"25": "<sup>25</sup> ಇದಲ್ಲದೆ ನೀವು ನಿಂತುಕೊಂಡು ಪ್ರಾರ್ಥನೆಮಾಡುವಾಗೆಲ್ಲಾ ಯಾರ ಮೇಲೆ ಏನಾದರೂ ವಿರೋಧವಿದ್ದರೆ ಅದನ್ನು ಅವನಿಗೆ ಕ್ಷಮಿಸಿರಿ; ಕ್ಷಮಿಸಿದರೆ ಪರಲೋಕದಲ್ಲಿರುವ ನಿಮ್ಮ ತಂದೆಯು ಸಹ ನಿಮ್ಮ ತಪ್ಪುಗಳನ್ನು ನಿಮಗೆ ಕ್ಷಮಿಸಿಬಿಡುವನು ಅಂದನು.",
			"27": "<sup>27</sup> ಅವರು ತಿರಿಗಿ ಯೆರೂಸಲೇಮಿಗೆ ಬಂದಾಗ ಆತನು ದೇವಾಲಯದಲ್ಲಿ ತಿರುಗಾಡುತ್ತಿರಲಾಗಿ ಮಹಾಯಾಜಕರೂ ಶಾಸ್ತ್ರಿಗಳೂ ಹಿರಿಯರೂ ಆತನ ಬಳಿಗೆ ಬಂದು -",
			"28": "<sup>28</sup> ನೀನು ಯಾವ ಅಧಿಕಾರದಿಂದ ಇದನ್ನೆಲ್ಲಾ ಮಾಡುತ್ತೀ? ಇದನ್ನು ಮಾಡುವದಕ್ಕೆ ಈ ಅಧಿಕಾರವನ್ನು ನಿನಗೆ ಯಾರು ಕೊಟ್ಟರು ಎಂದು ಆತನನ್ನು ಕೇಳಿದರು.",
			"29": "<sup>29</sup> ಯೇಸು ಅವರಿಗೆ - ನಾನು ನಿಮ್ಮನ್ನೂ ಒಂದು ಮಾತು ಕೇಳುತ್ತೇನೆ, ನನಗೆ ಉತ್ತರ ಹೇಳಿದರೆ ನಾನು ಸಹ ಯಾವ ಅಧಿಕಾರದಿಂದ ಇದನ್ನು ಮಾಡುತ್ತೇನೆಂಬದನ್ನು ನಿಮಗೆ ಹೇಳುತ್ತೇನೆ.",
			"30": "<sup>30</sup> ದೀಕ್ಷಾಸ್ನಾನ ಮಾಡಿಸುವ ಅಧಿಕಾರವು ಯೋಹಾನನಿಗೆ ಪರಲೋಕದಿಂದ ಬಂತೋ? ಮನುಷ್ಯರಿಂದ ಬಂತೋ? ಉತ್ತರಕೊಡಿರಿ ಅಂದನು.",
			"31": "<sup>31</sup> ಆಗ ಅವರು - ಪರಲೋಕದಿಂದ ಬಂತೆಂದು ನಾವು ಹೇಳಿದರೆ ಹಾಗಾದರೆ ನೀವು ಅವನನ್ನು ಯಾಕೆ ನಂಬಲಿಲ್ಲ ಅಂದಾನು;",
			"32": "<sup>32</sup> ಮನುಷ್ಯರಿಂದ ಬಂತೆಂದು ಹೇಳಿದರೆ ಏನಾಗುವದೋ ಎಂಬದಾಗಿ ತಮ್ಮ ತಮ್ಮೊಳಗೆ ಮಾತಾಡಿಕೊಳ್ಳುತ್ತಿದ್ದರು. ಯೋಹಾನನು ಪ್ರವಾದಿಯೆಂದು ಜನರೆಲ್ಲರೂ ಅನುಮಾನವಿಲ್ಲದೆ ತಿಳುಕೊಂಡಿದ್ದದರಿಂದ ಇವರಿಗೆ ಜನರ ಭಯವಿತ್ತು.",
			"33": "<sup>33</sup> ಹೀಗಿರಲಾಗಿ ಅವರು - ನಾವು ಅರಿಯೆವು ಎಂದು ಯೇಸುವಿಗೆ ಉತ್ತರಕೊಟ್ಟರು. ಆಗ ಯೇಸು ಅವರಿಗೆ - ಯಾವ ಅಧಿಕಾರದಿಂದ ಇದನ್ನು ಮಾಡುತ್ತೇನೋ ಅದನ್ನು ನಾನೂ ನಿಮಗೆ ಹೇಳುವದಿಲ್ಲ ಅಂದನು."
		},
		"12": {
			"1": "<sup>1</sup> ಆಗ ಯೇಸು ಅವರ ಕೂಡ ಸಾಮ್ಯರೂಪವಾಗಿ ಮಾತಾಡುವದಕ್ಕೆ ತೊಡಗಿ - ಒಬ್ಬ ಮನುಷ್ಯನು ಒಂದು ದ್ರಾಕ್ಷೆಯ ತೋಟವನ್ನು ಮಾಡಿ ಸುತ್ತಲು ಬೇಲಿಹಾಕಿಸಿ ದ್ರಾಕ್ಷೆಯ ಆಲೆಗಾಗಿ ಒಂದು ಗುಂಡಿಯನ್ನು ತೆಗಿಸಿ ಹೂಡೆಯನ್ನು ಕಟ್ಟಿಸಿ ಒಕ್ಕಲಿಗರಿಗೆ ವಾರಕ್ಕೆ ಕೊಟ್ಟು ಬೇರೊಂದು ದೇಶಕ್ಕೆ ಹೋದನು.",
			"2": "<sup>2</sup> ಫಲಕಾಲ ಬಂದಾಗ ದ್ರಾಕ್ಷೆಯ ತೋಟದ ಹಣ್ಣಿನ ಪಾಲನ್ನು ಆ ಒಕ್ಕಲಿಗರಿಂದ ತೆಗೆದುಕೊಂಡು ಬರುವದಕ್ಕಾಗಿ ಒಬ್ಬ ಆಳನ್ನು ಅವರ ಬಳಿಗೆ ಕಳುಹಿಸಿದನು.",
			"3": "<sup>3</sup> ಅವರು ಅವನನ್ನು ಹಿಡುಕೊಂಡು ಹೊಡೆದು ಬರಿಗೈಲಿ ಕಳುಹಿಸಿಬಿಟ್ಟರು.",
			"4": "<sup>4</sup> ಆ ಧಣಿಯು ತಿರಿಗಿ ಮತ್ತೊಬ್ಬ ಆಳನ್ನು ಅವರ ಬಳಿಗೆ ಕಳುಹಿಸಲು ಅವನನ್ನು ಅವರು ತಲೆತಲೆಯ ಮೇಲೆ ಹೊಡೆದು ಅಪಮಾನ ಪಡಿಸಿದರು.",
			"5": "<sup>5</sup> ಆಮೇಲೆ ಇನ್ನೊಬ್ಬನನ್ನು ಕಳುಹಿಸಲು ಅವನನ್ನು ಕೊಂದುಹಾಕಿದರು. ಇನ್ನೂ ಅನೇಕರನ್ನು ಕಳುಹಿಸಿದನು. ಅವರಲ್ಲಿ ಕೆಲವರನ್ನು ಹೊಡೆದರು, ಕೆಲವರನ್ನು ಕಡಿದುಹಾಕಿದರು.",
			"6": "<sup>6</sup> ಅವನಿಗೆ ಇನ್ನೂ ಒಬ್ಬನಿದ್ದನು; ಯಾರಂದರೆ ಅವನ ಮುದ್ದು ಮಗನೇ. ನನ್ನ ಮಗನಿಗಾದರೂ ಮರ್ಯಾದೆ ತೋರಿಸಿಯಾರೆಂದು ಧಣಿಯು ಕಡೆಯಲ್ಲಿ ಅವನನ್ನು ಅವರ ಬಳಿಗೆ ಕಳುಹಿಸಿದನು.",
			"7": "<sup>7</sup> ಆದರೆ ಆ ಒಕ್ಕಲಿಗರು - ಇವನೇ ಬಾಧ್ಯಸ್ಥನು; ಬನ್ನಿ, ಇವನನ್ನು ಕೊಂದುಹಾಕೋಣ; ಆಗ ಸ್ವಾಸ್ತ್ಯ ನಮ್ಮದಾಗುವದು ಎಂದು ಒಬ್ಬರ ಸಂಗಡಲೊಬ್ಬರು ಮಾತಾಡಿಕೊಂಡು",
			"8": "<sup>8</sup> ಅವನನ್ನು ಹಿಡಿದು ಕೊಂದುಹಾಕಿ ದ್ರಾಕ್ಷೆಯ ತೋಟದಿಂದ ಹೊರಕ್ಕೆ ಬಿಸಾಟುಬಿಟ್ಟರು.",
			"9": "<sup>9</sup> ಹಾಗಾದರೆ ದ್ರಾಕ್ಷೆಯ ತೋಟದ ಧಣಿಯು ಏನು ಮಾಡುವನು? ಅವನು ಬಂದು ಆ ಒಕ್ಕಲಿಗರನ್ನು ಸಂಹರಿಸಿ ತನ್ನ ತೋಟವನ್ನು ಬೇರೆ ಜನರಿಗೆ ಮಾಡುವದಕ್ಕೆ ಕೊಡುವನು.",
			"10": "<sup>10</sup> ಮನೆಕಟ್ಟುವವರು ಬೇಡವೆಂದು ಬಿಟ್ಟ ಕಲ್ಲೇ ಮುಖ್ಯವಾದ ಮೂಲೆಗಲ್ಲಾಯಿತು. ಇದು ಕರ್ತನಿಂದಲೇ ಆಯಿತು; ನಮಗೆ ಆಶ್ಚರ್ಯವಾಗಿ ತೋರುತ್ತದೆ ಎಂಬುವ ಶಾಸ್ತ್ರವಚನವನ್ನು ನೀವು ಓದಿಲ್ಲವೇ ಅಂದನು.",
			"12": "<sup>12</sup> ಇವನು ತಮ್ಮನ್ನೇ ಕುರಿತು ಈ ಸಾಮ್ಯವನ್ನು ಹೇಳಿದನು ಎಂದು ಅವರು ತಿಳುಕೊಂಡು ಆತನನ್ನು ಹಿಡಿಯುವದಕ್ಕೆ ಸಂದರ್ಭನೋಡುತ್ತಿದ್ದರು. ಆದರೆ ಜನರಿಗೆ ಭಯಪಟ್ಟು ಆತನನ್ನು ಬಿಟ್ಟುಹೋದರು.",
			"13": "<sup>13</sup> ಆಮೇಲೆ ಅವರು ಆತನನ್ನು ಮಾತಿನಲ್ಲಿ ಹಿಡಿಯುವದಕ್ಕೆ ಫರಿಸಾಯರಲ್ಲಿಯೂ ಹೆರೋದ್ಯರಲ್ಲಿಯೂ ಕೆಲವರನ್ನು ಆತನ ಬಳಿಗೆ ಕಳುಹಿಸಿದರು.",
			"14": "<sup>14</sup> ಇವರು ಬಂದು - ಬೋಧಕನೇ, ನೀನು ಸತ್ಯವಂತನು, ಯಾರಿಗೂ ಹೆದರದವನು; ನೀನು ಜನರ ಮುಖದಿಚ್ಫೆಗೆ ಮಾತಾಡದೆ ದೇವರ ಮಾರ್ಗವನ್ನು ಸತ್ಯವಾಗಿ ಬೋಧಿಸುವವನಾಗಿದ್ದೀ ಎಂದು ಬಲ್ಲೆವು. ಕೈಸರನಿಗೆ ತೆರಿಗೆ ಕೊಡುವದು ಸರಿಯೋ ಸರಿಯಲ್ಲವೋ,",
			"15": "<sup>15</sup> ಕೊಡಬೇಕೋ ಬೇಡವೋ ಎಂದು ಆತನನ್ನು ಕೇಳಿದರು. ಆತನು ಅವರ ಕಪಟವನ್ನು ತಿಳಿದು - ನನ್ನನ್ನು ಯಾಕೆ ಪರೀಕ್ಷೆ ಮಾಡುತ್ತೀರಿ? ನನಗೆ ಒಂದು ಹಣವನ್ನು ತಂದು ತೋರಿಸಿರಿ ಅನ್ನಲು ಅವರು ತಂದರು.",
			"16": "<sup>16</sup> ಆಗ ಆತನು - ಈ ತಲೆಯೂ ಮುದ್ರೆಯೂ ಯಾರದು ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ ಅವರು - ಕೈಸರನದು ಅಂದರು.",
			"17": "<sup>17</sup> ಯೇಸು - ಕೈಸರನದನ್ನು ಕೈಸರನಿಗೆ ಕೊಡಿರಿ; ದೇವರದನ್ನು ದೇವರಿಗೆ ಕೊಡಿರಿ ಎಂದು ಹೇಳಿದನು. ಅವರು ಆತನ ವಿಷಯದಲ್ಲಿ ಬಹು ಆಶ್ಚರ್ಯಪಟ್ಟರು.",
			"18": "<sup>18</sup> ತರುವಾಯ ಸದ್ದುಕಾಯರು ಆತನ ಬಳಿಗೆ ಬಂದರು. ಇವರು ಪುನರುತ್ಥಾನವಿಲ್ಲ ಅನ್ನುವವರು.",
			"19": "<sup>19</sup> ಇವರು ಆತನಿಗೆ ಪ್ರಶ್ನೆಮಾಡಿದ್ದೇನಂದರೆ - ಬೋಧಕನೇ, ಅಣ್ಣನಾದವನು ಮಕ್ಕಳಿಲ್ಲದೆ ಹೆಂಡತಿಯನ್ನು ಬಿಟ್ಟು ಸತ್ತರೆ ಆಕೆಯನ್ನು ಅವನ ತಮ್ಮನು ಮದುವೆ ಮಾಡಿಕೊಂಡು ತನ್ನ ಅಣ್ಣನಿಗೆ ಸಂತಾನವನ್ನು ಪಡೆಯಬೇಕೆಂದು ಮೋಶೆಯು ನಮಗೆ ಬರೆದಿಟ್ಟನಷ್ಟೆ.",
			"20": "<sup>20</sup> ಏಳು ಮಂದಿ ಅಣ್ಣತಮ್ಮಂದಿರಿದ್ದರು. ಮೊದಲನೆಯವನು ಹೆಂಡತಿಯನ್ನು ಮಾಡಿಕೊಂಡು ಸಂತಾನವಿಲ್ಲದೆ ಸತ್ತನು.",
			"21": "<sup>21</sup> ಎರಡನೆಯವನೂ ಆಕೆಯನ್ನು ಮದುವೆಮಾಡಿಕೊಂಡು ಸಂತಾನವಿಲ್ಲದೆ ಸತ್ತನು. ಅದರಂತೆ ಮೂರನೆಯವನು.",
			"22": "<sup>22</sup> ಇದೇ ಪ್ರಕಾರ ಏಳು ಮಂದಿಯೂ ಸಂತಾನವಿಲ್ಲದೆ ಹೋದರು. ಕಡೆಯಲ್ಲಿ ಆ ಸ್ತ್ರೀಯೂ ಸತ್ತಳು.",
			"23": "<sup>23</sup> ಪುನರುತ್ಥಾನದಲ್ಲಿ ಆಕೆ ಯಾರ ಹೆಂಡತಿಯಾಗಿರುವಳು? ಆಕೆಯನ್ನು ಏಳುಮಂದಿಯೂ ಮದುವೆ ಮಾಡಿಕೊಂಡಿದ್ದರಲ್ಲಾ ಅಂದರು.",
			"24": "<sup>24</sup> ಯೇಸು ಅವರಿಗೆ - ನೀವು ಶಾಸ್ತ್ರವನ್ನಾದರೂ ದೇವರ ಶಕ್ತಿಯನ್ನಾದರೂ ತಿಳಿಯದಿರುವದರಿಂದಲೇ ತಪ್ಪುವವರಾಗಿದ್ದೀರಲ್ಲವೆ.",
			"25": "<sup>25</sup> ಸತ್ತವರು ಬದುಕಿ ಎದ್ದ ಮೇಲೆ ಮದುವೆ ಮಾಡಿಕೊಳ್ಳುವದೂ ಇಲ್ಲ, ಮಾಡಿಕೊಡುವದೂ ಇಲ್ಲ; ಪರಲೋಕದಲ್ಲಿರುವ ದೇವದೂತರಂತೆ ಇರುತ್ತಾರೆ.",
			"26": "<sup>26</sup> ಆದರೆ ಸತ್ತವರು ಬದುಕಿ ಏಳುತ್ತಾರೆಂಬ ವಿಷಯದಲ್ಲಿ ಹೇಳಬೇಕಾದರೆ - ನಾನು ಅಬ್ರಹಾಮನ ದೇವರು, ಇಸಾಕನ ದೇವರು, ಯಾಕೋಬನ ದೇವರು ಎಂದು ದೇವರು ಮೋಶೆಗೆ ನುಡಿದದ್ದನ್ನು ನೀವು ಮೋಶೆಯ ಗ್ರಂಥದಲ್ಲಿ ಪೊದೆಯ ಸಂಗತಿಯಿರುವ ಅಧ್ಯಾಯದಲ್ಲಿ ಓದಲಿಲ್ಲವೇ?",
			"27": "<sup>27</sup> ಆತನು ಜೀವಿತರಿಗೆ ದೇವರಾಗಿದ್ದಾನೆ ಹೊರತು ಜೀವವಿಲ್ಲದವರಿಗೆ ಅಲ್ಲ; ನೀವು ಹಿಡಿದ ಅಭಿಪ್ರಾಯವು ಬಹು ತಪ್ಪಾಗಿದೆ ಎಂದು ಹೇಳಿದನು.",
			"28": "<sup>28</sup> ಶಾಸ್ತ್ರಿಗಳಲ್ಲಿ ಒಬ್ಬನು ಅವರ ತರ್ಕವನ್ನು ಕೇಳಿ ಯೇಸು ಅವರಿಗೆ ಒಳ್ಳೆಯ ಉತ್ತರ ಕೊಟ್ಟನೆಂದು ತಿಳಿದು ಹತ್ತಿರಕ್ಕೆ ಬಂದು - ಎಲ್ಲಾ ಆಜ್ಞೆಗಳಲ್ಲಿ ಮೊದಲನೆಯದು ಯಾವದು ಎಂದು ಆತನನ್ನು ಕೇಳಿದ್ದಕ್ಕೆ ಯೇಸು -",
			"29": "<sup>29</sup> ಇಸ್ರಾಯೇಲ್ ಜನವೇ ಕೇಳು, ನಮ್ಮ ದೇವರಾದ ಕರ್ತನು ಒಬ್ಬನೇ ದೇವರು;",
			"30": "<sup>30</sup> ನಿನ್ನ ದೇವರಾದ ಕರ್ತನನ್ನು ಪೂರ್ಣಹೃದಯದಿಂದಲೂ ಪೂರ್ಣಪ್ರಾಣದಿಂದಲೂ ಪೂರ್ಣಬುದ್ಧಿಯಿಂದಲೂ ಪೂರ್ಣಶಕ್ತಿಯಿಂದಲೂ ಪ್ರೀತಿಸಬೇಕು ಎಂಬದೇ ಮೊದಲನೆಯ ಆಜ್ಞೆ;",
			"31": "<sup>31</sup> ನಿನ್ನ ನೆರೆಯವನನ್ನು ನಿನ್ನಂತೆಯೇ ಪ್ರೀತಿಸಬೇಕು ಎಂಬದೇ ಎರಡನೆಯ ಆಜ್ಞೆ; ಇವುಗಳಿಗಿಂತ ಹೆಚ್ಚಿನ ಆಜ್ಞೆ ಮತ್ತೊಂದಿಲ್ಲ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"32": "<sup>32</sup> ಆಗ ಆ ಶಾಸ್ತ್ರಿಯು - ಬೋಧಕನೇ, ಚೆನ್ನಾಗಿ ಹೇಳಿದೆ; ಒಬ್ಬನೇ ಇದ್ದಾನೆ, ಆತನ ಹೊರತು ಮತ್ತೊಬ್ಬ ದೇವರಿಲ್ಲ ಎಂದು ನೀನು ಹೇಳಿದ್ದು ಸತ್ಯ.",
			"33": "<sup>33</sup> ಆತನನ್ನು ಪೂರ್ಣಮನಸ್ಸಿನಿಂದಲೂ ಪೂರ್ಣಬುದ್ಧಿಯಿಂದಲೂ ಪೂರ್ಣಶಕ್ತಿಯಿಂದಲೂ ಪ್ರೀತಿಸುವದು ಮತ್ತು ತನ್ನನ್ನು ಪ್ರೀತಿಸಿಕೊಳ್ಳುವಂತೆ ತನ್ನ ನೆರೆಯವನನ್ನು ಪ್ರೀತಿಸುವದು ಇವೆರಡೂ ಎಲ್ಲಾ ಸರ್ವಾಂಗಹೋಮಗಳಿಗಿಂತಲೂ ಎಲ್ಲಾ ಯಜ್ಞಗಳಿಗಿಂತಲೂ ಹೆಚ್ಚಿನವು ಅಂದನು.",
			"34": "<sup>34</sup> ಅವನು ಬುದ್ಧಿಯಿಂದ ಉತ್ತರಕೊಟ್ಟದ್ದನ್ನು ಯೇಸು ಕಂಡು - ನೀನು ದೇವರ ರಾಜ್ಯಕ್ಕೆ ದೂರವಾದವನಲ್ಲ ಅಂದನು. ಅಂದಿನಿಂದ ಆತನನ್ನು ಪ್ರಶ್ನೆಮಾಡುವದಕ್ಕೆ ಯಾರಿಗೂ ಧೈರ್ಯವಿರಲಿಲ್ಲ.",
			"35": "<sup>35</sup> ಯೇಸು ದೇವಾಲಯದಲ್ಲಿ ಉಪದೇಶ ಮಾಡುತ್ತಿರಲಾಗಿ ಪ್ರಶ್ನೆಮಾಡಿ - ಶಾಸ್ತ್ರಿಗಳು ಬರಬೇಕಾದ ಕ್ರಿಸ್ತನನ್ನು ದಾವೀದನ ಮಗನೆಂದು ಹೇಳುತ್ತಾರಲ್ಲಾ, ಅದು ಹೇಗಾದೀತು?",
			"36": "<sup>36</sup> ನಾನು ನಿನ್ನ ವಿರೋಧಿಗಳನ್ನು ನಿನ್ನ ಪಾದಗಳ ಕೆಳಗೆ ಹಾಕುವ ತನಕ ನನ್ನ ಬಲಗಡೆಯಲ್ಲಿ ಕೂತುಕೊಂಡಿರು ಎಂದು ಕರ್ತನು ನನ್ನ ಒಡೆಯನಿಗೆ ನುಡಿದನು ಎಂಬದಾಗಿ ದಾವೀದನೇ ಪವಿತ್ರಾತ್ಮನ ಪ್ರೇರಣೆಯಿಂದ ಹೇಳಿದನು.",
			"37": "<sup>37</sup> ದಾವೀದನೇ ಆತನನ್ನು ಒಡೆಯನೆಂದು ಹೇಳಿರುವಲ್ಲಿ ಆತನು ಅವನಿಗೆ ಮಗನಾಗುವದು ಹೇಗೆ? ಎಂದು ಕೇಳಿದನು.",
			"38": "<sup>38</sup> ಸಾಧಾರಣ ಜನರು ಆತನ ಮಾತನ್ನು ಸಂತೋಷದಿಂದ ಕೇಳುತ್ತಿದ್ದರು. ಆತನು ಉಪದೇಶಮಾಡಿ ಹೇಳಿದ್ದೇನಂದರೆ - ಶಾಸ್ತ್ರಿಗಳ ವಿಷಯದಲ್ಲಿ ಎಚ್ಚರಿಕೆಯಾಗಿರಿ. ನಿಲುವಂಗಿಗಳನ್ನು ತೊಟ್ಟುಕೊಂಡು ತಿರುಗಾಡುವದು, ಅಂಗಡಿಬೀದಿಗಳಲ್ಲಿ ನಮಸ್ಕಾರಗಳು,",
			"39": "<sup>39</sup> ಸಭಾಮಂದಿರಗಳಲ್ಲಿ ಮುಖ್ಯಪೀಠಗಳು, ಔತಣಪ್ರಸ್ತಗಳಲ್ಲಿ ಪ್ರಥಮಸ್ಥಾನಗಳು, ಇವುಗಳಲ್ಲಿಯೇ ಅವರು ಮನಸ್ಸುಳ್ಳವರಾಗಿದ್ದಾರೆ.",
			"40": "<sup>40</sup> ವಿಧವೆಯರ ಮನೆಗಳನ್ನು ನುಂಗಿ ನಟನೆಗಾಗಿ ದೇವರಿಗೆ ಉದ್ದವಾದ ಪ್ರಾರ್ಥನೆಗಳನ್ನು ಮಾಡುವವರಾದ ಇವರು ಹೆಚ್ಚಾದ ದಂಡನೆಯನ್ನು ಹೊಂದುವರು ಅಂದನು.",
			"41": "<sup>41</sup> ಯೇಸು ಬೊಕ್ಕಸದ ಮನೆಗೆ ಎದುರಾಗಿ ಕೂತುಕೊಂಡು ಜನರು ಬೊಕ್ಕಸದಲ್ಲಿ ಹಣ ಹಾಕುವದನ್ನು ನೋಡುತ್ತಿದ್ದನು. ಅನೇಕ ಮಂದಿ ಐಶ್ವರ್ಯವಂತರು ಬಹಳ ಬಹಳ ಹಾಕುತ್ತಿರಲು",
			"42": "<sup>42</sup> ಒಬ್ಬ ಬಡ ವಿಧವೆಯು ಬಂದು ಎರಡು ಕಾಸು ಅಂದರೆ ಒಂದು ದುಗ್ಗಾಣಿ ಹಾಕಿದಳು.",
			"43": "<sup>43</sup> ಆಗ ಆತನು ತನ್ನ ಶಿಷ್ಯರನ್ನು ಹತ್ತಿರಕ್ಕೆ ಕರೆದು - ನಿಮಗೆ ನಿಜವಾಗಿ ಹೇಳುತ್ತೇನೆ, ಬೊಕ್ಕಸದಲ್ಲಿ ಹಾಕಿದವರೆಲ್ಲರಲ್ಲಿ ಈ ಬಡ ವಿಧವೆ ಹೆಚ್ಚು ಹಾಕಿದ್ದಾಳೆ.",
			"44": "<sup>44</sup> ಹೇಗೆಂದರೆ ಎಲ್ಲರು ತಮಗೆ ಸಾಕಾಗಿ ಮಿಕ್ಕದ್ದರಲ್ಲಿ ಹಾಕಿದರು; ಈಕೆಯೋ ತನ್ನ ಬಡತನದಲ್ಲಿಯೂ ತನಗಿದ್ದದ್ದನ್ನೆಲ್ಲಾ ಹಾಕಿದಳು, ತನ್ನ ಜೀವನವನ್ನೇ ಕೊಟ್ಟುಬಿಟ್ಟಳು ಅಂದನು."
		},
		"13": {
			"1": "<sup>1</sup> ಯೇಸು ದೇವಾಲಯವನ್ನು ಬಿಟ್ಟು ಹೋಗುತ್ತಿರಲು ಆತನ ಶಿಷ್ಯರಲ್ಲಿ ಒಬ್ಬನು ಆತನಿಗೆ - ಗುರುವೇ, ನೋಡು, ಎಂಥಾ ಕಲ್ಲುಗಳು! ಎಂಥಾ ಕಟ್ಟಣಗಳು! ಎಂದು ಹೇಳಿದನು.",
			"2": "<sup>2</sup> ಯೇಸು - ಈ ದೊಡ್ಡ ಕಟ್ಟಣಗಳನ್ನು ನೋಡುತ್ತೀಯಾ? ಇಲ್ಲಿ ಕಲ್ಲಿನ ಮೇಲೆ ಕಲ್ಲು ಉಳಿಯುವದಿಲ್ಲ; ಎಲ್ಲಾ ಕೆಡವಲ್ಪಡುವದು ಅಂದನು.",
			"3": "<sup>3</sup> ಬಳಿಕ ಆತನು ಎಣ್ಣೇಮರಗಳ ಗುಡ್ಡದ ಮೇಲೆ ದೇವಾಲಯಕ್ಕೆ ಎದುರಾಗಿ ಕೂತಿದ್ದಾಗ ಪೇತ್ರ ಯಾಕೋಬ ಯೋಹಾನ ಅಂದ್ರೆಯ ಇವರು -",
			"4": "<sup>4</sup> ಅದು ಯಾವಾಗ ಆಗುವದು? ಅದೆಲ್ಲಾ ನೆರವೇರುವದಕ್ಕಿರುವಾಗ ಯಾವ ಸೂಚನೆ ತೋರುವದು? ನಮಗೆ ಹೇಳು ಎಂದು ಆತನನ್ನು ಪ್ರತ್ಯೇಕವಾಗಿ ಕೇಳಲು",
			"5": "<sup>5</sup> ಯೇಸು ಅವರಿಗೆ ಹೇಳಿದ್ದೇನಂದರೆ - ಯಾರಾದರೂ ನಿಮ್ಮನ್ನು ಮೋಸಗೊಳಿಸಾರು ನೋಡಿಕೊಳ್ಳಿರಿ.",
			"6": "<sup>6</sup> ಅನೇಕರು ಬಂದು ನನ್ನ ಹೆಸರನ್ನು ಎತ್ತಿಕೊಂಡು - ನಾನು ಕ್ರಿಸ್ತನು, ನಾನು ಕ್ರಿಸ್ತನು ಎಂದು ಹೇಳಿ ಎಷ್ಟೋ ಜನರನ್ನು ಮೋಸಗೊಳಿಸುವರು.",
			"7": "<sup>7</sup> ಇದಲ್ಲದೆ ಯುದ್ಧಗಳಾಗುವದನ್ನೂ ಯುದ್ಧವಾಗುವ ಹಾಗಿದೆ ಎಂಬ ಸುದ್ದಿಗಳನ್ನೂ ನೀವು ಕೇಳುವಾಗ ಕಳವಳಪಡಬೇಡಿರಿ. ಹಾಗಾಗುವದು ಅಗತ್ಯ; ಆದರೂ ಇದು ಇನ್ನೂ ಅಂತ್ಯಕಾಲವಲ್ಲ.",
			"8": "<sup>8</sup> ಜನಕ್ಕೆ ವಿರೋಧವಾಗಿ ಜನವೂ ರಾಜ್ಯಕ್ಕೆ ವಿರೋಧವಾಗಿ ರಾಜ್ಯವೂ ಏಳುವವು; ಮತ್ತು ಭೂಕಂಪಗಳು ಅಲ್ಲಲ್ಲಿ ಆಗುವವು; ಬರಗಳು ಬರುವವು; ಇವೆಲ್ಲಾ [ನೂತನ ಕಾಲವು ಹುಟ್ಟುವ] ಪ್ರಸವವೇದನೆಯ ಪ್ರಾರಂಭ.",
			"9": "<sup>9</sup> ಆದರೆ ನಿಮ್ಮ ವಿಷಯದಲ್ಲಿ ನೋಡಿಕೊಳ್ಳಿರಿ. ನಿಮ್ಮನ್ನು ನ್ಯಾಯವಿಚಾರಣೆಯ ಸಭೆಗಳಿಗೆ ಎಳಕೊಂಡುಹೋಗುವರು; ಸಭಾಮಂದಿರಗಳಲ್ಲಿ ನಿಮ್ಮನ್ನು ಹೊಡೆಯುವರು; ನನ್ನ ನಿಮಿತ್ತವಾಗಿ ನಿಮ್ಮನ್ನು ಅಧಿಪತಿಗಳ ಮುಂದೆಯೂ ಅರಸುಗಳ ಮುಂದೆಯೂ ಸಾಕ್ಷಿಗಳಾಗಿ ನಿಲ್ಲಿಸುವರು. ಹೀಗೆ ಅವರಿಗೆ ಸಾಕ್ಷಿಯಾಗುವದು.",
			"10": "<sup>10</sup> ಮೊದಲು ಎಲ್ಲಾ ಜನಾಂಗಗಳಿಗೆ ಸುವಾರ್ತೆಯು ಸಾರಲ್ಪಡಬೇಕು.",
			"11": "<sup>11</sup> ನಿಮ್ಮನ್ನು ಹಿಡುಕೊಂಡುಹೋಗಿ ಒಪ್ಪಿಸುವಾಗ ಏನು ಹೇಳಬೇಕು ಎಂದು ಮುಂಚಿತವಾಗಿ ಚಿಂತೆಮಾಡಬೇಡಿರಿ; ಆ ಗಳಿಗೆಯಲ್ಲಿ ನಿಮಗೆ ಸೂಚನೆಯಾಗುವಂಥ ಮಾತನ್ನೇ ಆಡಿರಿ; ಮಾತಾಡುವವರು ನೀವಲ್ಲ, ಪವಿತ್ರಾತ್ಮನೇ.",
			"12": "<sup>12</sup> ಇದಲ್ಲದೆ ಅಣ್ಣನು ತಮ್ಮನನ್ನು ತಂದೆಯು ಮಗನನ್ನು ಮರಣಕ್ಕೆ ಒಪ್ಪಿಸುವರು. ಮಕ್ಕಳು ಹೆತ್ತವರ ಮೇಲೆ ತಿರುಗಿಬಿದ್ದು ಅವರನ್ನು ಕೊಲ್ಲಿಸುವರು.",
			"13": "<sup>13</sup> ಮತ್ತು ನನ್ನ ಹೆಸರಿನ ನಿಮಿತ್ತವಾಗಿ ನಿಮ್ಮನ್ನು ಎಲ್ಲರೂ ಹಗೆಮಾಡುವರು. ಆದರೆ ಕಡೇವರೆಗೂ ತಾಳುವವನು ರಕ್ಷಣೆ ಹೊಂದುವನು.",
			"14": "<sup>14</sup> ಇದಲ್ಲದೆ ಹಾಳುಮಾಡುವ ಅಸಹ್ಯ ವಸ್ತುವು ನಿಲ್ಲಬಾರದ ಸ್ಥಳದಲ್ಲಿ ನಿಂತಿರುವದನ್ನು ನೀವು ಕಾಣುವಾಗ (ಇದನ್ನು ಓದುವವನು ತಿಳುಕೊಳ್ಳಲಿ) ಯೂದಾಯದಲ್ಲಿರುವವರು ಬೆಟ್ಟಗಳಿಗೆ ಓಡಿಹೋಗಲಿ.",
			"15": "<sup>15</sup> ಮಾಳಿಗೆಯ ಮೇಲೆ ಇರುವವನು ತನ್ನ ಮನೆಯೊಳಗಿಂದ ಏನಾದರೂ ತೆಗೆದುಕೊಳ್ಳುವದಕ್ಕೆ ಇಳಿಯದೆ ಒಳಕ್ಕೆ ಹೋಗದೆ",
			"16": "<sup>16</sup> ಹೊಲದಲ್ಲಿರುವವನು ತನ್ನ ಹೊದಿಕೆಯನ್ನು ತೆಗೆದುಕೊಳ್ಳುವದಕ್ಕೆ ಹಿಂತಿರಿಗಿ ಬಾರದೆ ಓಡಿಹೋಗಲಿ.",
			"17": "<sup>17</sup> ಆದರೆ ಆ ದಿನಗಳಲ್ಲಿ ಬಸುರಿಯರಿಗೂ ಮೊಲೆಕೂಸಿರುವ ಹೆಂಗಸರಿಗೂ ಆಗುವ ಕಷ್ಟವನ್ನು ಏನು ಹೇಳಲಿ!",
			"18": "<sup>18</sup> ಆ ದಿವಸಗಳು ಸಂಕಟ ದಿವಸಗಳಾಗಿರುವದರಿಂದ ಇದೆಲ್ಲಾ ಚಳಿಗಾಲದಲ್ಲಿ ಆಗಬಾರದೆಂದು ಪ್ರಾರ್ಥನೆ ಮಾಡಿರಿ; ಅಂಥ ಸಂಕಟವು ದೇವರು ಮಾಡಿದ ಸೃಷ್ಟಿ ಮೊದಲುಗೊಂಡು ಇಂದಿನವರೆಗೂ ಆಗಲಿಲ್ಲ; ಇನ್ನು ಮೇಲೆಯೂ ಆಗುವದಿಲ್ಲ.",
			"20": "<sup>20</sup> ಕರ್ತನು ಆ ದಿನಗಳನ್ನು ಕಡಿಮೆಮಾಡದಿದ್ದರೆ ಒಂದು ನರಪ್ರಾಣಿಯಾದರೂ ಉಳಿಯದು; ಆದರೆ ತಾನು ಆದುಕೊಂಡವರಿಗೋಸ್ಕರ ಆ ದಿನಗಳನ್ನು ಕಡಿಮೆಮಾಡಿದ್ದಾನೆ.",
			"21": "<sup>21</sup> ಆಗ ಯಾರಾದರೂ ನಿಮಗೆ - ಇಗೋ ಕ್ರಿಸ್ತನು ಇಲ್ಲಿದ್ದಾನೆ, ಅಗೋ ಅಲ್ಲಿದ್ದಾನೆ ಎಂದು ಹೇಳಿದರೆ ನಂಬಬೇಡಿರಿ;",
			"22": "<sup>22</sup> ಸುಳ್ಳು ಕ್ರಿಸ್ತರೂ ಸುಳ್ಳುಪ್ರವಾದಿಗಳೂ ಎದ್ದು ಸಾಧ್ಯವಾದರೆ ದೇವರಾದುಕೊಂಡವರನ್ನು ಮೋಸಗೊಳಿಸುವದಕ್ಕೋಸ್ಕರ ಸೂಚಕಕಾರ್ಯಗಳನ್ನೂ ಅದ್ಭುತಕಾರ್ಯಗಳನ್ನೂ ಮಾಡಿ ತೋರಿಸುವರು.",
			"23": "<sup>23</sup> ನೀವಂತು ನೋಡಿಕೊಳ್ಳಿರಿ; ನಿಮಗೆ ಎಲ್ಲವನ್ನೂ ಮುಂದಾಗಿ ಹೇಳಿದ್ದೇನೆ.",
			"24": "<sup>24</sup> ಇದಲ್ಲದೆ ಆ ದಿನಗಳಲ್ಲಿ ಆ ಸಂಕಟ ತೀರಿದ ಮೇಲೆ ಸೂರ್ಯನು ಕತ್ತಲಾಗಿ ಹೋಗುವನು;",
			"25": "<sup>25</sup> ಚಂದ್ರನು ಬೆಳಕುಕೊಡದೆ ಇರುವನು; ನಕ್ಷತ್ರಗಳು ಆಕಾಶದಿಂದ ಉದುರುತ್ತಿರುವವು; ಆಕಾಶದಲ್ಲಿರುವ ಶಕ್ತಿಗಳು ಕದಲುವವು.",
			"26": "<sup>26</sup> ಆಗ ಮನುಷ್ಯಕುಮಾರನು ಬಹು ಬಲದಿಂದಲೂ ಮಹಿಮೆಯಿಂದಲೂ ಮೇಘಗಳಲ್ಲಿ ಬರುವದನ್ನು ಕಾಣುವರು.",
			"27": "<sup>27</sup> ಮತ್ತು ಆತನು ತನ್ನ ದೂತರನ್ನು ಕಳುಹಿಸಿ ತಾನು ಆದುಕೊಂಡವರನ್ನು ಭೂಮಿಯ ಕಟ್ಟ ಕಡೆಯಿಂದ ಆಕಾಶದ ಕಟ್ಟ ಕಡೆಯವರೆಗೆ ನಾಲ್ಕೂ ದಿಕ್ಕುಗಳಿಂದ ಒಟ್ಟುಗೂಡಿಸುವನು.",
			"28": "<sup>28</sup> ಅಂಜೂರದ ಮರದ ದೃಷ್ಟಾಂತದಿಂದ ಬುದ್ಧಿ ಕಲಿಯಿರಿ; ಅದರ ಕೊಂಬೆ ಇನ್ನೂ ಎಳೇದಾಗಿದ್ದು ಎಲೆ ಬಿಡುವಾಗ ಬೇಸಿಗೆಯು ಹತ್ತಿರವಾಯಿತೆಂದು ತಿಳುಕೊಳ್ಳುತ್ತೀರಲ್ಲಾ.",
			"29": "<sup>29</sup> ಹಾಗೆಯೇ ನೀವು ಸಹ ಇದೆಲ್ಲಾ ಆಗುವದನ್ನು ನೋಡುವಾಗ ಹತ್ತಿರವದೆ, ಬಾಗಿಲಲ್ಲೇ ಅದೆ ಎಂದು ತಿಳುಕೊಳ್ಳಿರಿ.",
			"30": "<sup>30</sup> ಇದೆಲ್ಲಾ ಆಗುವ ತನಕ ಈ ಸಂತತಿಯು ಅಳಿದುಹೋಗುವದೇ ಇಲ್ಲವೆಂದು ನಿಮಗೆ ಸತ್ಯವಾಗಿ ಹೇಳುತ್ತೇನೆ.",
			"31": "<sup>31</sup> ಭೂಮ್ಯಾಕಾಶಗಳು ಅಳಿದುಹೋಗುವವು, ಆದರೆ ನನ್ನ ಮಾತುಗಳು ಅಳಿದುಹೋಗುವದೇ ಇಲ್ಲ.",
			"32": "<sup>32</sup> ಇದಲ್ಲದೆ ಆ ದಿನದ ವಿಷಯವಾಗಲಿ ಆ ಗಳಿಗೆಯ ವಿಷಯವಾಗಲಿ ನನ್ನ ತಂದೆಗೆ ತಿಳಿಯುವದೇ ಹೊರತು ಮತ್ತಾರಿಗೂ ತಿಳಿಯದು; ಪರಲೋಕದಲ್ಲಿರುವ ದೂತರಿಗೂ ತಿಳಿಯದು; ಮಗನಿಗೂ ತಿಳಿಯದು.",
			"33": "<sup>33</sup> ಆ ಕಾಲವು ಯಾವಾಗ ಬರುವದೋ ನಿಮಗೆ ಗೊತ್ತಿಲ್ಲವಾದ್ದರಿಂದ ನೋಡಿಕೊಳ್ಳಿರಿ, ಜಾಗರೂಕರಾಗಿರಿ.",
			"34": "<sup>34</sup> ಒಬ್ಬ ಮನುಷ್ಯಸು ತನ್ನ ಮನೆಯನ್ನು ಬಿಟ್ಟು ಬೇರೊಂದು ದೇಶಕ್ಕೆ ಹೋಗುವಾಗ ತನ್ನ ಆಳುಗಳಿಗೆ ಮನೇ ಆಡಳಿತವನ್ನು ಒಪ್ಪಿಸಿಕೊಟ್ಟು ಒಬ್ಬೊಬ್ಬನಿಗೆ ಅವನವನ ಕೆಲಸವನ್ನು ನೇಮಿಸಿ ಬಾಗಿಲುಕಾಯುವವನನ್ನು ಕರೆದು - ನೀನು ಎಚ್ಚರವಾಗಿರಬೇಕೆಂದು ಅಪ್ಪಣೆ ಕೊಡುವ ಪ್ರಕಾರ [ನಾನು ನಿಮಗೆ ಅಪ್ಪಣೆ ಕೊಡುತ್ತೇನೆ].",
			"35": "<sup>35</sup> ಮನೇಯಜಮಾನನು ಸಂಜೆಯಲ್ಲೋ ಸರುಹೊತ್ತಿನಲ್ಲೋ ಕೋಳಿ ಕೂಗುವಾಗಲೋ ಮುಂಜಾನೆಯಲ್ಲೋ ಯಾವಾಗ ಬರುತ್ತಾನೋ ನಿಮಗೆ ಗೊತ್ತಿಲ್ಲದ ಕಾರಣ ಎಚ್ಚರವಾಗಿರಿ;",
			"36": "<sup>36</sup> ಅವನು ಏಕಾಏಕಿ ಬಂದು ನೀವು ನಿದ್ದೆ ಮಾಡುವದನ್ನು ಕಂಡಾನು.",
			"37": "<sup>37</sup> ನಾನು ನಿಮಗೆ ಹೇಳಿದ್ದನ್ನು ಎಲ್ಲರಿಗೂ ಹೇಳುತ್ತೇನೆ, ಎಚ್ಚರವಾಗಿರಿ ಅಂದನು."
		},
		"14": {
			"1": "<sup>1</sup> ಪಸ್ಕದ ಮತ್ತು ಹುಳಿಯಿಲ್ಲದ ರೊಟ್ಟಿಯ ಹಬ್ಬ ಬರುವದಕ್ಕೆ ಇನ್ನೂ ಎರಡು ದಿನಗಳಿದ್ದಾಗ ಮಹಾಯಾಜಕರೂ ಶಾಸ್ತ್ರಿಗಳೂ ಯೇಸುವನ್ನು ಉಪಾಯದಿಂದ ಹಿಡಿದು ಕೊಲ್ಲುವದಕ್ಕೆ ಮಾರ್ಗವನ್ನು ಹುಡುಕುತ್ತಿದ್ದರು.",
			"2": "<sup>2</sup> ಯಾಕಂದರೆ - ಹಬ್ಬದಲ್ಲಿ ಹಿಡಿಯಬಾರದು, ನಮ್ಮ ಜನರಲ್ಲಿ ಗದ್ದಲವಾದೀತು ಎಂದು ಮಾತಾಡಿಕೊಂಡರು.",
			"3": "<sup>3</sup> ಆತನು ಬೇಥಾನ್ಯದಲ್ಲಿ ಕುಷ್ಠರೋಗಿಯಾದ ಸೀಮೋನನ ಮನೆಯಲ್ಲಿ ಇದ್ದು ಊಟಕ್ಕೆ ಕೂತಿದ್ದಾಗ ಒಬ್ಬ ಸ್ತ್ರೀಯು ಬಹು ಬೆಲೆಯುಳ್ಳ ಅಚ್ಚ ಜಟಾಮಾಂಸಿ ತೈಲದ ಭರಣಿಯನ್ನು ತೆಗೆದುಕೊಂಡು ಬಂದು ಆ ಭರಣಿಯನ್ನು ಒಡೆದು ತೈಲವನ್ನು ಆತನ ತಲೆಯ ಮೇಲೆ ಹೊಯಿದಳು.",
			"4": "<sup>4</sup> ಆದರೆ ಕೆಲವರು ತಮ್ಮೊಳಗೆ ಕೋಪಗೊಂಡು - ಈ ತೈಲವನ್ನು ನಷ್ಟಮಾಡಿದ್ದೇಕೆ?",
			"5": "<sup>5</sup> ಇದನ್ನು ಮುನ್ನೂರು ಹಣಕ್ಕಿಂತ ಹೆಚ್ಚು ಬೆಲೆಗೆ ಮಾರಿ ಬಡವರಿಗೆ ಕೊಡಬಹುದಾಗಿತ್ತಲ್ಲಾ ಎಂದು ಹೇಳಿ ಆಕೆಗೆ ಛೀ ಹಾಕಿದರು.",
			"6": "<sup>6</sup> ಆದರೆ ಯೇಸು - ಈಕೆಯನ್ನು ಬಿಡಿರಿ. ಈಕೆಗೆ ಯಾಕೆ ತೊಂದರೆಕೊಡುತ್ತೀರಿ? ಈಕೆ ನನಗೆ ಒಳ್ಳೆಯ ಕಾರ್ಯವನ್ನು ಮಾಡಿದ್ದಾಳೆ.",
			"7": "<sup>7</sup> ಬಡವರು ಯಾವಾಗಲೂ ನಿಮ್ಮ ಬಳಿಯಲ್ಲಿ ಇರುತ್ತಾರೆ; ನಿಮಗೆ ಮನಸ್ಸು ಬಂದಾಗ ಅವರಿಗೆ ಉಪಕಾರಮಾಡಬಹುದು; ಆದರೆ ನಾನು ಯಾವಾಗಲೂ ನಿಮ್ಮ ಬಳಿಯಲ್ಲಿ ಇರುವದಿಲ್ಲ.",
			"8": "<sup>8</sup> ಈಕೆಯು ತನ್ನ ಕೈಲಾಗುವಷ್ಟು ಮಾಡಿದ್ದಾಳೆ; ಈಕೆಯು ಮುಂದಾಗಿ ನನ್ನ ಉತ್ತರಕ್ರಿಯೆಗೋಸ್ಕರ ನನ್ನ ದೇಹಕ್ಕೆ ಈ ತೈಲವನ್ನು ಹಚ್ಚಿದ್ದಾಳೆ.",
			"9": "<sup>9</sup> ಸುವಾರ್ತೆಯು ಸರ್ವಲೋಕದಲ್ಲಿ ಎಲ್ಲೆಲ್ಲಿ ಸಾರಲಾಗುವದೋ ಅಲ್ಲಲ್ಲಿ ಈಕೆ ಮಾಡಿದ್ದನ್ನು ಸಹ ಈಕೆಯ ನೆನಪಿಗಾಗಿ ಹೇಳುವರೆಂದು ನಿಮಗೆ ಸತ್ಯವಾಗಿ ಹೇಳುತ್ತೇನೆ ಅಂದನು.",
			"10": "<sup>10</sup> ಆಮೇಲೆ ಹನ್ನೆರಡು ಮಂದಿ ಶಿಷ್ಯರಲ್ಲಿ ಒಬ್ಬನಾದ ಇಸ್ಕರಿಯೋತ ಯೂದನು ಯೇಸುವನ್ನು ಮಹಾಯಾಜಕರಿಗೆ ಹಿಡುಕೊಡಬೇಕೆಂತ ಅವರ ಬಳಿಗೆ ಹೋದನು.",
			"11": "<sup>11</sup> ಅವರು ಕೇಳಿ ಸಂತೋಷಪಟ್ಟು - ನಿನಗೆ ಹಣಕೊಡುತ್ತೇವೆಂದು ಮಾತುಕೊಟ್ಟರು. ಅವನು ಆತನನ್ನು ಅನುಕೂಲವಾಗಿ ಹಿಡುಕೊಡುವದು ಹೇಗೆ ಎಂದು ನೋಡುತ್ತಿದ್ದನು.",
			"12": "<sup>12</sup> ಹುಳಿಯಿಲ್ಲದ ರೊಟ್ಟಿಯ ಹಬ್ಬದ ಮೊದಲನೆಯ ದಿವಸ, ಅಂದರೆ ಪಸ್ಕದ ಕುರಿಮರಿಯನ್ನು ಕೊಯ್ಯುವ ದಿವಸ, ಆತನ ಶಿಷ್ಯರು - ನೀನು ಪಸ್ಕದ ಊಟ ಮಾಡುವಂತೆ ನಾವು ಎಲ್ಲಿ ಹೋಗಿ ಸಿದ್ಧಮಾಡಬೇಕನ್ನುತ್ತೀ ಎಂದು ಆತನನ್ನು ಕೇಳಲು",
			"13": "<sup>13</sup> ಆತನು ತನ್ನ ಶಿಷ್ಯರಲ್ಲಿ ಇಬ್ಬರನ್ನು ಕರೆದು - ನೀವು ಪಟ್ಟಣದೊಳಕ್ಕೆ ಹೋಗಿರಿ; ಅಲ್ಲಿ ಒಬ್ಬ ಮನುಷ್ಯನು ತುಂಬಿದ ಕೊಡವನ್ನು ಹೊತ್ತುಕೊಂಡು ನಿಮ್ಮೆದುರಿಗೆ ಬರುವನು; ನೀವು ಅವನ ಹಿಂದೆ ಹೋಗಿರಿ;",
			"14": "<sup>14</sup> ಅವನು ಯಾವ ಮನೆಗೆ ಹೋಗುವನೋ ಆ ಮನೆಯ ಯಜಮಾನನಿಗೆ - ನಮ್ಮ ಬೋಧಕನು ತನ್ನ ಶಿಷ್ಯರ ಸಂಗಡ ಪಸ್ಕದ ಊಟವನ್ನು ಮಾಡುವದಕ್ಕೆ ತನಗೆ ಭೋಜನಶಾಲೆ ಎಲ್ಲಿ ಎಂದು ಕೇಳುತ್ತಾನೆ ಎಂಬದಾಗಿ ಹೇಳಿರಿ.",
			"15": "<sup>15</sup> ಅವನು ತಕ್ಕ ಸಾಮಾನೆಲ್ಲಾ ಸಿದ್ಧವಾಗಿ ಇಟ್ಟಿರುವ ಮೇಲಂತಸ್ತಿನ ದೊಡ್ಡ ಕೊಠಡಿಯನ್ನು ನಿಮಗೆ ತೋರಿಸುವನು; ಅಲ್ಲಿ ನಮಗೆ ಸಿದ್ಧಮಾಡಿರಿ ಎಂದು ಹೇಳಿ ಅವರನ್ನು ಕಳುಹಿಸಿಕೊಟ್ಟನು.",
			"16": "<sup>16</sup> ಶಿಷ್ಯರು ಹೊರಟು ಪಟ್ಟಣಕ್ಕೆ ಹೋಗಿ ಆತನು ತಮಗೆ ಹೇಳಿದಂತೆ ಕಂಡು ಪಸ್ಕಕ್ಕೆ ಸೌರಿಸಿದರು.",
			"17": "<sup>17</sup> ಸಂಜೆಯಾದಾಗ ಆತನು ಹನ್ನೆರಡು ಮಂದಿ ಶಿಷ್ಯರ ಸಂಗಡ ಬಂದನು.",
			"18": "<sup>18</sup> ಅವರು ಕೂತುಕೊಂಡು ಊಟಮಾಡುತ್ತಿರುವಾಗ ಯೇಸು - ನಿಮ್ಮಲ್ಲಿ ಒಬ್ಬನು ನನ್ನನ್ನು ಹಿಡುಕೊಡುವನು, ಅವನು ನನ್ನ ಸಂಗಡ ಊಟಮಾಡುವವನೇ ಎಂದು ನಿಮಗೆ ಸತ್ಯವಾಗಿ ಹೇಳುತ್ತೇನೆ ಅಂದನು.",
			"19": "<sup>19</sup> ಆಗ ಅವರು ದುಃಖಿತರಾಗಿ - ನಾನಲ್ಲವಲ್ಲಾ, ನಾನಲ್ಲವಲ್ಲಾ ಎಂದು ಒಬ್ಬೊಬ್ಬರೂ ಆತನನ್ನು ಕೇಳತೊಡಗಿದರು.",
			"20": "<sup>20</sup> ಆತನು ಅವರಿಗೆ - ಅವನು ನನ್ನ ಹನ್ನೆರಡು ಮಂದಿಯಲ್ಲಿ ಒಬ್ಬನು, ನನ್ನ ಸಂಗಡ ಬಟ್ಟಲಲ್ಲಿ ಕೈ ಅದ್ದುವವನೇ.",
			"21": "<sup>21</sup> ಮನುಷ್ಯಕುಮಾರನು ಹೊರಟುಹೋಗುತ್ತಾನೆ ಸರಿ; ಹಾಗೆ ಆತನ ವಿಷಯವಾಗಿ ಬರೆದದೆಯಲ್ಲಾ. ಆದರೆ ಯಾವನು ಮನುಷ್ಯಕುಮಾರನನ್ನು ಹಿಡುಕೊಡುವನೋ ಅವನ ಗತಿಯನ್ನು ಏನು ಹೇಳಲಿ. ಆ ಮನುಷ್ಯನು ಹುಟ್ಟದಿದ್ದರೆ ಅವನಿಗೆ ಒಳ್ಳೇದಾಗಿತ್ತು ಎಂದು ಹೇಳಿದನು.",
			"22": "<sup>22</sup> ಅವರು ಊಟಮಾಡುತ್ತಿರುವಾಗ ಆತನು ರೊಟ್ಟಿಯನ್ನು ತೆಗೆದುಕೊಂಡು ದೇವರ ಸ್ತೋತ್ರಮಾಡಿ ಮುರಿದು ಅವರಿಗೆ ಕೊಟ್ಟು - ತಕ್ಕೊಳ್ಳಿರಿ; ಇದು ನನ್ನ ದೇಹ ಅಂದನು.",
			"23": "<sup>23</sup> ಆಮೇಲೆ ಪಾತ್ರೆಯನ್ನು ತೆಗೆದುಕೊಂಡು ಸ್ತೋತ್ರಮಾಡಿ ಅವರಿಗೆ ಕೊಟ್ಟನು; ಅವರೆಲ್ಲರೂ ಅದರಲ್ಲಿ ಕುಡಿದರು.",
			"24": "<sup>24</sup> ಮತ್ತು ಆತನು ಅವರಿಗೆ - ಇದು ನನ್ನ ರಕ್ತ, ಇದು ಒಡಂಬಡಿಕೆಯ ರಕ್ತ; ಇದು ಬಹು ಜನರಿಗೋಸ್ಕರ ಸುರಿಸಲ್ಪಡುವ ರಕ್ತ.",
			"25": "<sup>25</sup> ನಿಮಗೆ ಸತ್ಯವಾಗಿ ಹೇಳುತ್ತೇನೆ, ನಾನು ದೇವರ ರಾಜ್ಯದಲ್ಲಿ ದ್ರಾಕ್ಷಾರಸವನ್ನು ಹೊಸದಾಗಿ ಕುಡಿಯುವ ದಿನದವರೆಗೂ ಅದನ್ನು ಇನ್ನು ಕುಡಿಯುವದೇ ಇಲ್ಲ ಅಂದನು.",
			"26": "<sup>26</sup> ಬಳಿಕ ಅವರು ಕೀರ್ತನೆಹಾಡಿ ಎಣ್ಣೇಮರಗಳ ಗುಡ್ಡಕ್ಕೆ ಹೊರಟುಹೋದರು.",
			"27": "<sup>27</sup> ಆಗ ಯೇಸು ಅವರಿಗೆ - ನೀವೆಲ್ಲರೂ ದಿಗಿಲು ಪಟ್ಟು ಹಿಂಜರಿಯುವಿರಿ. ಕುರುಬನನ್ನು ಹೊಡೆಯುವೆನು, ಕುರಿಗಳು ಚದರಿಹೋಗುವವು ಎಂದು ಬರೆದದೆಯಲ್ಲಾ.",
			"28": "<sup>28</sup> ಆದಾಗ್ಯೂ ನಾನು ಜೀವಿತನಾಗಿ ಎದ್ದ ಮೇಲೆ ನಿಮ್ಮ ಮುಂದೆ ಗಲಿಲಾಯಕ್ಕೆ ಹೋಗುವೆನು ಎಂದು ಹೇಳಿದನು.",
			"29": "<sup>29</sup> ಆದರೆ ಪೇತ್ರನು ಆತನಿಗೆ - ಎಲ್ಲರೂ ದಿಗಿಲುಪಟ್ಟು ಹಿಂಜರಿದರೂ ನಾನು ಹಿಂಜರಿಯುವದಿಲ್ಲ ಎಂದು ಹೇಳಲು",
			"30": "<sup>30</sup> ಯೇಸು - ನಿನಗೆ ಸತ್ಯವಾಗಿ ಹೇಳುತ್ತೇನೆ, ಈ ಹೊತ್ತು ಈ ರಾತ್ರಿಯಲ್ಲಿಯೇ ಕೋಳಿ ಎರಡು ಸಾರಿ ಕೂಗುವದಕ್ಕಿಂತ ಮುಂಚೆ ನೀನು ನನ್ನ ವಿಷಯದಲ್ಲಿ - ಅವನನ್ನು ಅರಿಯೆನೆಂಬದಾಗಿ ಮೂರು ಸಾರಿ ಹೇಳುವಿ ಅಂದನು.",
			"31": "<sup>31</sup> ಆದರೆ ಅವನು - ನಾನು ನಿನ್ನ ಸಂಗಡ ಸಾಯಬೇಕಾದರೂ ನಿನ್ನನ್ನು ಅರಿಯೆನೆಂಬದಾಗಿ ಹೇಳುವದೇ ಇಲ್ಲವೆಂದು ಬಹು ಖಂಡಿತವಾಗಿ ಮಾತಾಡಿದನು. ಅದರಂತೆ ಅವರೆಲ್ಲರೂ ಹೇಳಿದರು.",
			"32": "<sup>32</sup> ತರುವಾಯ ಅವರು ಗೆತ್ಸೇಮನೆ ಎಂಬ ತೋಟಕ್ಕೆ ಬಂದರು. ಆತನು ತನ್ನ ಶಿಷ್ಯರಿಗೆ - ಇಲ್ಲೇ ಕೂತುಕೊಳ್ಳಿರಿ, ನಾನು ಪ್ರಾರ್ಥನೆಮಾಡಿ ಬರುತ್ತೇನೆ ಎಂದು ಹೇಳಿ ಪೇತ್ರ ಯಾಕೋಬ ಯೋಹಾನರನ್ನು ತನ್ನ ಜೊತೆಯಲ್ಲಿ ಕರಕೊಂಡು ಹೋಗಿ",
			"33": "<sup>33</sup> ಬೆಚ್ಚಿಬೆರಗಾಗಿ ಮನಗುಂದಿದವನಾದನು.",
			"34": "<sup>34</sup> ಮತ್ತು ಅವರಿಗೆ - ನನ್ನ ಪ್ರಾಣವು ಸಾಯುವಷ್ಟು ದುಃಖಕ್ಕೆ ಒಳಗಾಗಿದೆ. ನೀವು ಇಲ್ಲೇ ಇದ್ದು ಎಚ್ಚರವಾಗಿರ್ರಿ ಎಂದು ಹೇಳಿ ಸ್ವಲ್ಪ ಮುಂದೆ ಹೋಗಿ",
			"35": "<sup>35</sup> ನೆಲದ ಮೇಲೆ ಅಡ್ಡಬಿದ್ದು ಸಾಧ್ಯವಾದರೆ ಆ ಗಳಿಗೆ ತನ್ನನ್ನು ಬಿಟ್ಟುಹೋಗುವಂತೆ ದೇವರನ್ನು ಪ್ರಾರ್ಥಿಸಿ-",
			"36": "<sup>36</sup> ಅಪ್ಪಾ, ತಂದೆಯೇ, ನಿನಗೆ ಎಲ್ಲವೂ ಸಾಧ್ಯ; ಈ ಪಾತ್ರೆಯನ್ನು ನನ್ನಿಂದ ತೊಲಗಿಸು; ಆದರೂ ನನ್ನ ಚಿತ್ತದಂತಾಗದೆ ನಿನ್ನ ಚಿತ್ತದಂತೆಯೇ ಆಗಲಿ ಅಂದನು.",
			"37": "<sup>37</sup> ಆಮೇಲೆ ಆತನು ಬಂದು ಅವರು ನಿದ್ದೆಮಾಡುವದನ್ನು ಕಂಡು ಪೇತ್ರನಿಗೆ - ಸೀಮೋನನೇ, ನಿದ್ದೆಮಾಡುತ್ತೀಯಾ? ಒಂದು ಗಳಿಗೆಯಾದರೂ ಎಚ್ಚರವಾಗಿರಲಾರೆಯಾ?",
			"38": "<sup>38</sup> ಶೋಧನೆಗೆ ಒಳಗಾಗದಂತೆ ಎಚ್ಚರವಾಗಿದ್ದು ಪ್ರಾರ್ಥಿಸಿರಿ; ಮನಸ್ಸು ಸಿದ್ಧವಾಗಿದೆ ಸರಿ, ಆದರೆ ದೇಹಕ್ಕೆ ಬಲ ಸಾಲದು ಎಂದು ಹೇಳಿದನು.",
			"39": "<sup>39</sup> ಆಮೇಲೆ ತಿರಿಗಿ ಹೋಗಿ ಅದೇ ಮಾತನ್ನು ಹೇಳುತ್ತಾ ಪ್ರಾರ್ಥಿಸಿದನು.",
			"40": "<sup>40</sup> ತಿರಿಗಿ ಬಂದಾಗ ಅವರು ನಿದ್ದೆ ಮಾಡುವದನ್ನು ಕಂಡನು; ಅವರ ಕಣ್ಣುಗಳು ಬಹು ಭಾರವಾಗಿದ್ದವು; ಮತ್ತು ಆತನು ಕೇಳಿದ್ದಕ್ಕೆ ಏನು ಉತ್ತರ ಕೊಡಬೇಕೋ ಅವರಿಗೆ ತಿಳಿಯಲಿಲ್ಲ.",
			"41": "<sup>41</sup> ಆತನು ಮೂರನೆಯ ಸಾರಿ ಹೋಗಿ ಬಂದು ಅವರಿಗೆ - ನೀವು ಇನ್ನೂ ನಿದ್ದೆಮಾಡಿ ದಣುವಾರಿಸಿಕೊಳ್ಳಿರಿ; ಸಾಕು;",
			"42": "<sup>42</sup> ಆ ಗಳಿಗೆ ಬಂತು; ಇಗೋ, ಮನುಷ್ಯಕುಮಾರನು ದುರ್ಜನರ ಕೈಗೆ ಒಪ್ಪಿಸಲ್ಪಡುತ್ತಾನೆ. ಏಳಿರಿ, ಹೋಗೋಣ; ನನ್ನನ್ನು ಹಿಡುಕೊಡುವವನು ಹತ್ತಿರಕ್ಕೆ ಬಂದಿದ್ದಾನೆ ನೋಡಿರಿ ಎಂದು ಹೇಳಿದನು.",
			"43": "<sup>43</sup> ಆತನು ಇನ್ನೂ ಮಾತಾಡುತ್ತಿರುವಾಗಲೇ ಹನ್ನೆರಡು ಮಂದಿ ಶಿಷ್ಯರಲ್ಲಿ ಒಬ್ಬನಾದ ಯೂದನು ಬಂದನು. ಮಹಾಯಾಜಕರು ಶಾಸ್ತ್ರಿಗಳು ಹಿರಿಯರು ಇವರ ಕಡೆಯಿಂದ ಒಂದು ಗುಂಪು ಕತ್ತಿದೊಣ್ಣೆಗಳನ್ನು ಹಿಡುಕೊಂಡು ಅವನ ಸಂಗಡ ಬಂತು;",
			"44": "<sup>44</sup> ಇದಲ್ಲದೆ ಆತನನ್ನು ಹಿಡುಕೊಡುವವನು ಅವರಿಗೆ - ನಾನು ಯಾರಿಗೆ ಮುದ್ದಿಡುತ್ತೇನೋ ಅವನೇ ಆತನು; ಅವನನ್ನು ಹಿಡಿದು ಭದ್ರವಾಗಿ ತೆಗೆದುಕೊಂಡುಹೋಗಿರಿ ಎಂದು ಗುರುತು ಹೇಳಿಕೊಟ್ಟಿದ್ದನು.",
			"45": "<sup>45</sup> ಅಲ್ಲಿಗೆ ಬಂದ ಕೂಡಲೆ ಅವನು ಯೇಸುವಿನ ಬಳಿಗೆ ಹೋಗಿ - ಗುರುವೇ ಎಂದು ಹೇಳಿ",
			"46": "<sup>46</sup> ಆತನಿಗೆ ಮುದ್ದಿಡಲು ಅವರು ಆತನ ಮೇಲೆ ಕೈಹಾಕಿ ಆತನನ್ನು ಹಿಡಿದರು.",
			"47": "<sup>47</sup> ಆದರೆ ಹತ್ತಿರ ನಿಂತಿದ್ದವರಲ್ಲಿ ಒಬ್ಬನು ತನ್ನ ಕತ್ತಿಯನ್ನು ಹಿರಿದು ಮಹಾಯಾಜಕನ ಆಳನ್ನು ಹೊಡೆದು ಅವನ ಕಿವಿಯನ್ನು ಕಡಿದುಹಾಕಿದನು.",
			"48": "<sup>48</sup> ಅವರು ಯೇಸುವನ್ನು ಹಿಡಿದಾಗ ಆತನು ಅವರಿಗೆ - ಕಳ್ಳನನ್ನು ಹಿಡಿಯುವದಕ್ಕೆ ಬಂದಂತೆ ಕತ್ತಿಗಳನ್ನೂ ದೊಣ್ಣೆಗಳನ್ನೂ ತೆಗೆದುಕೊಂಡು ನನ್ನನ್ನು ಹಿಡಿಯುವದಕ್ಕೆ ಬಂದಿರಾ?",
			"49": "<sup>49</sup> ನಾನು ದಿನಾಲು ನಿಮ್ಮ ಸಂಗಡ ಇದ್ದು ದೇವಾಲಯದಲ್ಲಿ ಬೋಧಿಸುತ್ತಿದ್ದಾಗ ನೀವು ನನ್ನನ್ನು ಹಿಡಿಯಲಿಲ್ಲ. ಆದರೆ ಶಾಸ್ತ್ರ ವಚನಗಳು ನೆರವೇರುವಂತೆ ಇದೆಲ್ಲಾ ಆಯಿತು ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"50": "<sup>50</sup> ಆಗ ಶಿಷ್ಯರೆಲ್ಲರೂ ಆತನನ್ನು ಬಿಟ್ಟು ಓಡಿಹೋದರು.",
			"51": "<sup>51</sup> ಒಬ್ಬಾನೊಬ್ಬ ಯೌವನಸ್ಥನು ಬರೀ ಮೈ ಮೇಲೆ ನಾರುಮಡಿಯನ್ನು ಹೊದ್ದವನಾಗಿ ಆತನ ಹಿಂದೆ ಜನರ ಕೂಡ ಬರುತ್ತಿದ್ದನು.",
			"52": "<sup>52</sup> ಅವರು ಅವನನ್ನು ಹಿಡಿಯಲಾಗಿ ಅವನು ಆ ನಾರುಮಡಿಯನ್ನು ಬಿಟ್ಟು ಬರೀ ಮೈಲಿ ಓಡಿಹೋದನು.",
			"53": "<sup>53</sup> ಅಷ್ಟರಲ್ಲಿ ಅವರು ಯೇಸುವನ್ನು ಮಹಾಯಾಜಕನ ಬಳಿಗೆ ತೆಗೆದುಕೊಂಡು ಹೋದರು. ಇವನ ಬಳಿಗೆ ಮಹಾಯಾಜಕರು ಹಿರಿಯರು ಶಾಸ್ತ್ರಿಗಳು ಇವರೆಲ್ಲರೂ ಕೂಡಿಬಂದರು.",
			"54": "<sup>54</sup> ಮತ್ತು ಪೇತ್ರನು ದೂರದಿಂದ ಆತನ ಹಿಂದೆ ಹೋಗುತ್ತಾ ಮಹಾಯಾಜಕನ ಮಠದ ಅಂಗಳದೊಳಗೂ ಬಂದು ಓಲೇಕಾರರ ಸಂಗಡ ಬೆಂಕಿಯ ಮುಂದೆ ಕೂತುಕೊಂಡು ಚಳಿಕಾಯಿಸಿಕೊಳ್ಳುತ್ತಿದ್ದನು.",
			"55": "<sup>55</sup> ಆಗ ಮಹಾಯಾಜಕರೂ ಹಿರೀಸಭೆಯವರೆಲ್ಲರೂ ಯೇಸುವನ್ನು ಕೊಲ್ಲಿಸಬೇಕೆಂದು ಆತನ ಮೇಲೆ ಸಾಕ್ಷಿಯನ್ನು ಹುಡುಕಿದರು; ಆದರೆ ಏನೂ ಸಿಕ್ಕಲಿಲ್ಲ.",
			"56": "<sup>56</sup> ಬಹುಮಂದಿ ಬಂದು ಆತನ ಮೇಲೆ ಸುಳ್ಳುಸಾಕ್ಷಿ ಹೇಳಿದರೂ ಅವರ ಸಾಕ್ಷಿ ಒಂದಕ್ಕೊಂದು ಸರಿಬೀಳಲಿಲ್ಲ.",
			"57": "<sup>57</sup> ತರುವಾಯ ಕೆಲವರು ಎದ್ದು ನಿಂತು - ಕೈಯಿಂದ ಕಟ್ಟಿರುವ ಈ ದೇವಾಲಯವನ್ನು ನಾನು ಕೆಡವಿಬಿಟ್ಟು ಕೈಯಿಂದ ಕಟ್ಟದಿರುವ ಮತ್ತೊಂದನ್ನು ಮೂರು ದಿನಗಳಲ್ಲಿ ಕಟ್ಟುವೆನು ಎಂದು ಇವನು ಹೇಳಿದ್ದನ್ನು ನಾವು ಕೇಳಿದ್ದೇವೆ ಎಂಬದಾಗಿ ಆತನ ಮೇಲೆ ಸುಳ್ಳುಸಾಕ್ಷಿ ಹೇಳಿದರು.",
			"59": "<sup>59</sup> ಹೀಗೆ ಹೇಳಿದರೂ ಅವರ ಸಾಕ್ಷಿ ಒಂದಕ್ಕೊಂದು ಸರಿಬೀಳಲಿಲ್ಲ.",
			"60": "<sup>60</sup> ಆಗ ಮಹಾಯಾಜಕನು ಎದ್ದು ನಡುವೆ ನಿಂತು - ನೀನೇನೂ ಉತ್ತರಹೇಳುವದಿಲ್ಲವೋ? ಇವರು ನಿನ್ನ ಮೇಲೆ ಹೇಳುವ ಈ ಸಾಕ್ಷಿ ಏನು? ಎಂದು ಯೇಸುವನ್ನು ಪ್ರಶ್ನಿಸಿದನು.",
			"61": "<sup>61</sup> ಆದರೆ ಆತನು ಸುಮ್ಮನಿದ್ದು ಏನೂ ಉತ್ತರ ಹೇಳಲಿಲ್ಲ. ತಿರಿಗಿ ಮಹಾಯಾಜಕನು - ಭಗವಂತನ ಕುಮಾರನಾದ ಕ್ರಿಸ್ತನೋ ನೀನು? ಎಂದು ಆತನನ್ನು ಕೇಳಲಾಗಿ",
			"62": "<sup>62</sup> ಯೇಸು - ನಾನೇ, ಇದಲ್ಲದೆ ಮನುಷ್ಯಕುಮಾರನು ಸರ್ವಶಕ್ತನ ಬಲಗಡೆಯಲ್ಲಿ ಆಸೀನನಾಗಿರುವದನ್ನೂ ಆಕಾಶದ ಮೇಘಗಳೊಂದಿಗೆ ಬರುವದನ್ನೂ ನೋಡುವಿರಿ ಅಂದನು.",
			"63": "<sup>63</sup> ಇದನ್ನು ಕೇಳಿ ಮಹಾಯಾಜಕನು ತನ್ನ ಅಂಗಿಗಳನ್ನು ಹರಕೊಂಡು - ನಮಗೆ ಸಾಕ್ಷಿಗಳು ಇನ್ನು ಯಾತಕ್ಕೆ ಬೇಕು?",
			"64": "<sup>64</sup> ಇವನು ಮಾಡಿದ ದೂಷಣೆಯನ್ನು ಕೇಳಿದಿರಲ್ಲಾ; ನಿಮಗೆ ಹೇಗೆ ಕಾಣುತ್ತದೆ ಅನ್ನಲು ಅವರೆಲ್ಲರು - ಇವನು ಮರಣದಂಡನೆ ಹೊಂದತಕ್ಕವನು ಎಂದು ತೀರ್ಪುಮಾಡಿದರು.",
			"65": "<sup>65</sup> ಆಮೇಲೆ ಕೆಲವರು ಆತನನ್ನು ಉಗುಳಿ ಆತನ ಮುಖಕ್ಕೆ ಮುಸುಕುಹಾಕಿ ಗುದ್ದಿ - ಪ್ರವಾದನೆ ಹೇಳು ಅನ್ನುತ್ತಿದ್ದರು. ಮತ್ತು ಓಲೇಕಾರರು ಆತನ ಕೆನ್ನೆಗೆ ಏಟು ಹಾಕುತ್ತಾ ತಮ್ಮ ವಶಕ್ಕೆ ತೆಗೆದುಕೊಂಡರು.",
			"66": "<sup>66</sup> ಇತ್ತಲಾಗಿ ಪೇತ್ರನು ಕೆಳಗೆ ಅಂಗಳದಲ್ಲಿ ಇದ್ದಾಗ ಮಹಾಯಾಜಕನ ದಾಸಿಯರಲ್ಲಿ ಒಬ್ಬಳು ಬಂದು",
			"67": "<sup>67</sup> ಅವನು ಚಳಿಕಾಯಿಸಿಕೊಳ್ಳುತ್ತಿರುವದನ್ನು ಕಂಡು ಅವನನ್ನು ನೋಡಿ - ನೀನು ಸಹ ಆ ನಜರೇತಿನವನಾದ ಯೇಸುವಿನ ಕೂಡ ಇದ್ದವನು ಅಂದಳು.",
			"68": "<sup>68</sup> ಅವನು - ಅಲ್ಲ; ನೀನು ಏನನ್ನುತ್ತೀಯೋ ನನಗೆ ಗೊತ್ತಿಲ್ಲ, ತಿಳಿಯುವದಿಲ್ಲ ಎಂದು ಹೇಳಿ ಹೊರಂಗಳಕ್ಕೆ ಹೋದನು.",
			"69": "<sup>69</sup> ಅಲ್ಲಿ ದಾಸಿಯು ಅವನನ್ನು ಕಂಡು ಹತ್ತಿರದಲ್ಲಿ ನಿಂತಿದ್ದವರಿಗೆ - ಇವನೂ ಅವರಲ್ಲಿ ಒಬ್ಬನು ಎಂದು ತಿರಿಗಿ ಹೇಳುವದಕ್ಕೆ ತೊಡಗಿದಳು.",
			"70": "<sup>70</sup> ಅವನು ತಿರಿಗಿ ಅಲ್ಲ ಅಂದನು. ಸ್ವಲ್ಪ ಹೊತ್ತಿನ ಮೇಲೆ ಅಲ್ಲಿ ನಿಂತವರು ಪೇತ್ರನಿಗೆ - ನಿಶ್ಚಯವಾಗಿ ನೀನು ಅವರಲ್ಲಿ ಒಬ್ಬನು; ನೀನು ಗಲಿಲಾಯದವನಷ್ಟೆ ಎಂದು ತಿರಿಗಿ ಹೇಳಿದರು.",
			"71": "<sup>71</sup> ಆದರೆ ಅವನು - ನೀವು ಹೇಳುವ ಆ ಮನುಷ್ಯನನ್ನು ನಾನರಿಯೆನು ಎಂದು ಹೇಳಿ ಶಪಿಸಿಕೊಳ್ಳುವದಕ್ಕೂ ಆಣೆಯಿಟ್ಟುಕೊಳ್ಳುವದಕ್ಕೂ ಪ್ರಾರಂಭಿಸಿದನು. ಕೂಡಲೆ ಕೋಳಿ ಎರಡನೆಯ ಸಾರಿ ಕೂಗಿತು.",
			"72": "<sup>72</sup> ಆಗ ಪೇತ್ರನು - ಕೋಳಿ ಎರಡು ಸಾರಿ ಕೂಗುವದಕ್ಕಿಂತ ಮುಂಚೆ ಮೂರು ಸಾರಿ ನನ್ನ ವಿಷಯವಾಗಿ ಅವನನ್ನು ಅರಿಯೆನೆಂಬದಾಗಿ ಹೇಳುವಿ ಎಂದು ಯೇಸು ತನಗೆ ಹೇಳಿದ ಮಾತನ್ನು ನೆನಪಿಗೆ ತಂದುಕೊಂಡು ಆ ವಿಷಯದಲ್ಲಿ ಯೋಚಿಸಿ ಅತ್ತನು."
		},
		"15": {
			"1": "<sup>1</sup> ಬೆಳಗಾದ ಕೂಡಲೆ ಮಹಾಯಾಜಕರೂ ಹಿರಿಯರೂ ಶಾಸ್ತ್ರಿಗಳೂ ಹಿರೀಸಭೆಯವರೆಲ್ಲರೂ ಒಟ್ಟುಗೂಡಿ ಆಲೋಚನೆ ಮಾಡಿಕೊಂಡು ಯೇಸುವನ್ನು ಕಟ್ಟಿಸಿ ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ಪಿಲಾತನಿಗೆ ಒಪ್ಪಿಸಿದರು.",
			"2": "<sup>2</sup> ಪಿಲಾತನು ಆತನನ್ನು - ನೀನು ಯೆಹೂದ್ಯರ ಅರಸನು ಹೌದೋ ಎಂದು ಕೇಳಲು ಆತನು - ನೀನೇ ಹೇಳಿದ್ದೀ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"3": "<sup>3</sup> ಮಹಾಯಾಜಕರು ಆತನ ಮೇಲೆ ತುಂಬಾ ದೂರು ಹೇಳುತ್ತಿದ್ದರು. ಆಗ ಪಿಲಾತನು ಇನ್ನೊಂದಾವರ್ತಿ ಆತನನ್ನು - ನೀನು ಏನೂ ಉತ್ತರಕೊಡುವದಿಲ್ಲವೇ? ಇವರು ನಿನ್ನ ಮೇಲೆ ಎಷ್ಟು ದೂರುಗಳನ್ನು ಹೇಳುತ್ತಾರೆ ನೋಡು ಎಂದು ಕೇಳಿದನು.",
			"5": "<sup>5</sup> ಆದರೆ ಯೇಸು ಇನ್ನೇನೂ ಉತ್ತರಕೊಡಲಿಲ್ಲ. ಅದನ್ನು ನೋಡಿ ಪಿಲಾತನು ಆಶ್ಚರ್ಯಪಟ್ಟನು.",
			"6": "<sup>6</sup> ಅವನು ಆ ಹಬ್ಬದಲ್ಲಿ ಜನರು ಬೇಡಿದ ಸೆರೆಯವನನ್ನು ಅವರಿಗೆ ಬಿಟ್ಟುಕೊಡುವ ಪದ್ಧತಿಯಿತ್ತು.",
			"7": "<sup>7</sup> ಆಗ ಬರಬ್ಬನೆಂಬವನೊಬ್ಬನಿದ್ದನು; ಇವನನ್ನು ದಂಗೆಯಲ್ಲಿ ಕೊಲೆಪಾತಕ ಮಾಡಿದ ದಂಗೆಗಾರರ ಕೂಡ ಕಟ್ಟಿಹಾಕಿದ್ದರು.",
			"8": "<sup>8</sup> ಜನರು ಪಿಲಾತನ ಬಳಿಗೆ ಹೋಗಿ - ನೀನು ನಮಗೆ ಮಾಡುತ್ತಾ ಬಂದ ಪ್ರಕಾರ ಮಾಡು ಎಂದು ಬೇಡಿಕೊಳ್ಳುತ್ತಿದ್ದರು.",
			"9": "<sup>9</sup> ಮಹಾಯಾಜಕರು ಹೊಟ್ಟೇಕಿಚ್ಚಿನಿಂದ ಯೇಸುವನ್ನು ಒಪ್ಪಿಸಿಕೊಟ್ಟಿದ್ದಾರೆಂದು ಪಿಲಾತನು ಗ್ರಹಿಸಿಕೊಂಡು - ನಾನು ಯೆಹೂದ್ಯರ ಅರಸನನ್ನು ನಿಮಗೆ ಬಿಟ್ಟು ಕೊಡಬೇಕನ್ನುತ್ತೀರೋ ಎಂದು ಅವರನ್ನು ಕೇಳಿದನು.",
			"11": "<sup>11</sup> ಅತ್ತಲಾಗಿ ಮಹಾಯಾಜಕರು - ಬರಬ್ಬನನ್ನೇ ಬಿಟ್ಟುಕೊಡಬೇಕೆಂದು ಬೇಡಿಕೊಳ್ಳಿರಿ ಎಂಬದಾಗಿ ಜನರನ್ನು ಪ್ರೇರೇಪಿಸಿದರು.",
			"12": "<sup>12</sup> ಅದಕ್ಕೆ ಪಿಲಾತನು ತಿರಿಗಿ ಅವರನ್ನು - ಹಾಗಾದರೆ ನೀವು ಹೇಳುವ ಯೆಹೂದ್ಯರ ಅರಸನನ್ನು ನಾನೇನು ಮಾಡಲಿ ಎಂದು ಕೇಳಲು ಅವರು -",
			"13": "<sup>13</sup> ಅವನನ್ನು ಶಿಲುಬೆಗೆ ಹಾಕಿಸು ಎಂದು ತಿರಿಗಿ ಬೊಬ್ಬೆಹಾಕಿದರು.",
			"14": "<sup>14</sup> ಪಿಲಾತನು - ಯಾಕೆ? ಕೆಟ್ಟದ್ದೇನು ಮಾಡಿದನು? ಅಂದನು. ಆದರೆ ಅವರು - ಅವನನ್ನು ಶಿಲುಬೆಗೆ ಹಾಕಿಸು ಎಂದು ಬಹಳವಾಗಿ ಆರ್ಭಟಿಸಿದರು.",
			"15": "<sup>15</sup> ಅದಕ್ಕೆ ಪಿಲಾತನು ಜನರ ಮನಸ್ಸನ್ನು ಸಮಾಧಾನಪಡಿಸಬೇಕೆಂದು ಬರಬ್ಬನನ್ನು ಅವರಿಗೆ ಬಿಟ್ಟುಕೊಟ್ಟು ಯೇಸುವನ್ನು ಕೊರಡೆಗಳಿಂದ ಹೊಡಿಸಿ ಶಿಲುಬೆಗೆ ಹಾಕುವದಕ್ಕೆ ಒಪ್ಪಿಸಿದನು.",
			"16": "<sup>16</sup> ಸಿಪಾಯಿಗಳು ಆತನನ್ನು ಅಧಿಪತಿಯ ಅರಮನೆಗೆ ಸೇರಿದ ಅಂಗಳದೊಳಕ್ಕೆ ಸಾಗಿಸಿಕೊಂಡುಹೋಗಿ ಪಟಾಲಮನ್ನೆಲ್ಲಾ ಒಟ್ಟಿಗೆ ಕರೆದು",
			"17": "<sup>17</sup> ಆತನಿಗೆ ಕೆಂಪು ಒಲ್ಲಿಯನ್ನು ಹೊದಿಸಿ ಮುಳ್ಳಿನ ಕಿರೀಟವನ್ನು ಹೆಣೆದು ಆತನಿಗಿಟ್ಟು -",
			"18": "<sup>18</sup> ಯೆಹೂದ್ಯರ ಅರಸನೇ, ನಿನಗೆ ನಮಸ್ಕಾರ ಎಂದು ಆತನಿಗೆ ವಂದನೆಮಾಡಿ",
			"19": "<sup>19</sup> ಬೆತ್ತದಿಂದ ಆತನ ತಲೆಯ ಮೇಲೆ ಹೊಡೆದು ಆತನ ಮೇಲೆ ಉಗುಳಿ ಆತನ ಮುಂದೆ ಮೊಣಕಾಲೂರಿ ಅಡ್ಡಬಿದ್ದರು.",
			"20": "<sup>20</sup> ಹೀಗೆ ಆತನನ್ನು ಪರಿಹಾಸ್ಯ ಮಾಡಿದ ಮೇಲೆ ಆ ಒಲ್ಲಿಯನ್ನು ತೆಗೆದು ಆತನ ಬಟ್ಟೆಗಳನ್ನು ಹೊದಿಸಿ ಆತನನ್ನು ಶಿಲುಬೆಗೆ ಹಾಕುವದಕ್ಕೆ ತೆಗೆದುಕೊಂಡು ಹೋದರು.",
			"21": "<sup>21</sup> ಆಗ ಹೊಲದಿಂದ ಹೊರಟು ಆ ಮಾರ್ಗವಾಗಿ ಬರುತ್ತಿದ್ದ ಒಬ್ಬನನ್ನು ಆತನ ಶಿಲುಬೆಯನ್ನು ಹೊರುವದಕ್ಕೆ ಬಿಟ್ಟೀಹಿಡಿದರು. ಇವನು ಯಾರಂದರೆ - ಅಲೆಕ್ಸಾಂದ್ರ ರೂಫ ಎಂಬವರ ತಂದೆಯಾದ ಕುರೇನೆ ಪಟ್ಟಣದ ಸೀಮೋನನು.",
			"22": "<sup>22</sup> ಬಳಿಕ ಆತನನ್ನು ಗೊಲ್ಗೊಥಾ ಎಂಬ ಸ್ಥಳಕ್ಕೆ ಕರಕೊಂಡು ಬಂದರು. ಗೊಲ್ಗೊಥಾ ಅಂದರೆ ಕಪಾಲಸ್ಥಳ.",
			"23": "<sup>23</sup> ಅಲ್ಲಿ ಆತನಿಗೆ ರಕ್ತಬೋಳ ಬೆರಸಿದ ದ್ರಾಕ್ಷಾರಸವನ್ನು ಕೊಟ್ಟರು; ಆದರೆ ಆತನು ತೆಗೆದುಕೊಳ್ಳಲಿಲ್ಲ.",
			"24": "<sup>24</sup> ಅವರು ಆತನನ್ನು ಶಿಲುಬೆಗೆ ಹಾಕಿ ಆತನ ಬಟ್ಟೆಗಳಲ್ಲಿ ಯಾವ ಯಾವದು ಯಾರಾರಿಗೆ ಆಗಬೇಕೆಂದು ಚೀಟುಹಾಕಿ ಪಾಲುಮಾಡಿಕೊಂಡರು.",
			"25": "<sup>25</sup> ಆತನನ್ನು ಶಿಲುಬೆಗೆ ಹಾಕುವಾಗ ಹೊತ್ತಾರೆ ಒಂಭತ್ತು ಗಂಟೆಯಾಗಿತ್ತು.",
			"26": "<sup>26</sup> ಇದಲ್ಲದೆ ಅವರು ಹೊರಿಸಿದ ಅಪರಾಧವನ್ನು ಸೂಚಿಸುವ ಒಂದು ವಿಳಾಸವು ಶಿಲುಬೆಯ ಮೇಲೆ ಬರೆದಿತ್ತು; ಅದೇನಂದರೆ - ಯೆಹೂದ್ಯರ ಅರಸನು ಎಂಬದೇ.",
			"27": "<sup>27</sup> ಅದಲ್ಲದೆ ಇಬ್ಬರು ಕಳ್ಳರನ್ನು ತಂದು ಒಬ್ಬನನ್ನು ಆತನ ಬಲಗಡೆಯಲ್ಲಿ ಒಬ್ಬನನ್ನು ಎಡಗಡೆಯಲ್ಲಿ ಆತನ ಸಂಗಡ ಶಿಲುಬೆಗೆ ಹಾಕಿದರು.",
			"29": "<sup>29</sup> ಅಲ್ಲಿ ಹಾದುಹೋಗುವವರು ತಲೇ ಆಡಿಸಿ ಆಹಾ, ದೇವಾಲಯವನ್ನು ಕೆಡವಿ ಮೂರು ದಿನದಲ್ಲಿ ಕಟ್ಟುವವನೇ,",
			"30": "<sup>30</sup> ಶಿಲುಬೆಯಿಂದ ಇಳಿದು ನಿನ್ನನ್ನು ರಕ್ಷಿಸಿಕೋ ಎಂದು ಆತನನ್ನು ಹಂಗಿಸುತ್ತಿದ್ದರು.",
			"31": "<sup>31</sup> ಅದೇ ಮೇರೆಗೆ ಮಹಾಯಾಜಕರೂ ಶಾಸ್ತ್ರಿಗಳೂ - ಅವನು ಮತ್ತೊಬ್ಬರನ್ನು ರಕ್ಷಿಸಿದನು; ತನ್ನನ್ನು ರಕ್ಷಿಸಿಕೊಳ್ಳಲಾರನು.",
			"32": "<sup>32</sup> ಇಸ್ರಾಯೇಲ್ಯರ ಅರಸನಾದ ಈ ಕ್ರಿಸ್ತನು ಈಗ ಶಿಲುಬೆಯಿಂದ ಇಳಿದು ಬರಲಿ; ಇಳಿದುಬಂದರೆ ನಾವು ನೋಡಿ ನಂಬೇವು ಎಂದು ತಮ್ಮತಮ್ಮೊಳಗೆ ಹಾಸ್ಯದಿಂದ ಮಾತಾಡುತ್ತಿದ್ದರು. ಆತನ ಸಂಗಡ ಶಿಲುಬೆಗೆ ಹಾಕಲ್ಪಟ್ಟವರು ಸಹ ಆತನನ್ನು ನಿಂದಿಸುತ್ತಿದ್ದರು.",
			"33": "<sup>33</sup> ಮಧ್ಯಾಹ್ನವಾದಾಗ ದೇಶದ ಮೇಲೆಲ್ಲಾ ಕತ್ತಲೆ ಕವಿದು ಸಾಯಂಕಾಲ ಮೂರು ಗಂಟೆಯ ತನಕ ಇತ್ತು.",
			"34": "<sup>34</sup> ಮೂರು ಗಂಟೆಗೆ ಯೇಸು - ಎಲೋಹಿ, ಎಲೋಹಿ, ಲಮಾ ಸಬಕ್ತಾನೀ ಅಂದರೆ ನನ್ನ ದೇವರೇ, ನನ್ನ ದೇವರೇ, ಯಾಕೆ ನನ್ನನ್ನು ಕೈಬಿಟ್ಟಿದ್ದೀ ಎಂದು ಮಹಾಧ್ವನಿಯಿಂದ ಕೂಗಿದನು.",
			"35": "<sup>35</sup> ಹತ್ತಿರ ನಿಂತಿದ್ದವರಲ್ಲಿ ಕೆಲವರು ಅದನ್ನು ಕೇಳಿ - ಎಲೀಯನನ್ನು ಕರೆಯುತ್ತಾನೆ ಕಾಣಿರೋ, ಅಂದರು.",
			"36": "<sup>36</sup> ಆಗ ಒಬ್ಬನು ಓಡಿಹೋಗಿ ಸ್ಪಂಜನ್ನು ಹುಳಿರಸದಿಂದ ತುಂಬಿಸಿ ಕೋಲಿಗೆ ಸಿಕ್ಕಿಸಿ ಆತನಿಗೆ ಕುಡಿಯುವದಕ್ಕೆ ಕೊಟ್ಟು - ಬಿಡಿರಿ, ಎಲೀಯನು ಇವನನ್ನು ಇಳಿಸುವದಕ್ಕೆ ಬರುವನೇನೋ ನೋಡೋಣ ಅಂದನು.",
			"37": "<sup>37</sup> ಆದರೆ ಯೇಸು ಮಹಾಧ್ವನಿಯನ್ನು ಗೈದು ಪ್ರಾಣಬಿಟ್ಟನು.",
			"38": "<sup>38</sup> ಆಗ ದೇವಾಲಯದ ತೆರೆಯು ಮೇಲಿಂದ ಕೆಳಗಿನವರೆಗೂ ಹರಿದು ಎರಡು ಭಾಗವಾಯಿತು.",
			"39": "<sup>39</sup> ಆತನು ಹೀಗೆ ಪ್ರಾಣಬಿಟ್ಟದ್ದನ್ನು ಆತನ ಹತ್ತಿರ ಎದುರಿಗೆ ನಿಂತಿದ್ದ ಶತಾಧಿಪತಿಯು ನೋಡಿ - ನಿಜವಾಗಿ ಈ ಮನುಷ್ಯನು ದೇವಕುಮಾರನಾಗಿದ್ದನು ಅಂದನು.",
			"40": "<sup>40</sup> ಇದಲ್ಲದೆ ಅಲ್ಲಿ ಸ್ತ್ರೀಯರು ಸಹ ದೂರದಿಂದ ನೋಡುತ್ತಿದ್ದರು; ಇವರಲ್ಲಿ, ಆತನು ಗಲಿಲಾಯದಲ್ಲಿದ್ದಾಗ ಆತನನ್ನು ಹಿಂಬಾಲಿಸಿ ಸೇವೆ ಮಾಡುತ್ತಿದ್ದವರಾದ ಮಗ್ದಲದ ಮರಿಯ, ಚಿಕ್ಕ ಯಾಕೋಬನ ಮತ್ತು ಯೋಸೆಯ ತಾಯಿಯಾದ ಮರಿಯ, ಸಲೋಮೆ ಎಂಬವರೂ ಆತನ ಸಂಗಡ ಯೆರೂಸಲೇಮಿಗೆ ಬಂದಿದ್ದ ಇನ್ನೂ ಅನೇಕ ಸ್ತ್ರೀಯರೂ ಇದ್ದರು.",
			"42": "<sup>42</sup> ಆಗಲೇ ಸಂಜೆಯಾಗಿತ್ತು; ಮತ್ತು ಆ ದಿನವು ಸೌರಣೆಯ ದಿನ, ಅಂದರೆ ಸಬ್ಬತ್ ದಿನದ ಹಿಂದಿನ ದಿನ ಆಗಿತ್ತು.",
			"43": "<sup>43</sup> ಹೀಗಿರಲಾಗಿ ಅರಿಮಥಾಯದ ಯೋಸೇಫನೆಂಬವನು ಬಂದು ಧೈರ್ಯದಿಂದ ಪಿಲಾತನ ಬಳಿಗೆ ಹೋಗಿ ಯೇಸುವಿನ ದೇಹವನ್ನು ತನಗೆ ಕೊಡಿಸಬೇಕೆಂದು ಬೇಡಿಕೊಂಡನು. ಇವನು ಹಿರೀಸಭೆಯವರಲ್ಲಿ ಘನವಂತನು, ದೇವರ ರಾಜ್ಯವನ್ನು ಎದುರು ನೋಡುತ್ತಿದ್ದವನು.",
			"44": "<sup>44</sup> ಪಿಲಾತನು - ಅವನು ಇಷ್ಟು ಬೇಗ ಸತ್ತನೋ ಎಂದು ಆಶ್ಚರ್ಯಪಟ್ಟು ಶತಾಧಿಪತಿಯನ್ನು ಕರೆಸಿ - ಅವನು ಆಗಲೇ ಸತ್ತನೋ ಎಂದು ಅವನನ್ನು ಕೇಳಿ",
			"45": "<sup>45</sup> ಆತನು ಸತ್ತದ್ದನ್ನು ಶತಾಧಿಪತಿಯಿಂದ ತಿಳಿದು ಶವವನ್ನು ಯೋಸೇಫನಿಗೆ ಕೊಡಿಸಿದನು.",
			"46": "<sup>46</sup> ಯೋಸೇಫನು ನಾರು ಮಡಿಯನ್ನು ಕೊಂಡುತಂದು ಆತನನ್ನು ಇಳಿಸಿ ನಾರುಮಡಿಯಲ್ಲಿ ಸುತ್ತಿ ಬಂಡೆಯಲ್ಲಿ ತೋಡಿದ್ದ ಸಮಾಧಿಯಲ್ಲಿಟ್ಟು ಸಮಾಧಿಯ ಬಾಗಿಲಿಗೆ ಕಲ್ಲನ್ನು ಉರುಳಿಸಿದನು.",
			"47": "<sup>47</sup> ಮಗ್ದಲದ ಮರಿಯಳೂ ಯೋಸೆಯ ತಾಯಿ ಮರಿಯಳೂ ಆತನನ್ನಿಡುವ ಸ್ಥಳವನ್ನು ನೋಡಿದರು."
		},
		"16": {
			"1": "<sup>1</sup> ಸಬ್ಬತ್‍ದಿನ ಕಳೆದ ಮೇಲೆ, ಮಗ್ದಲದ ಮರಿಯಳೂ ಯಾಕೋಬನ ತಾಯಿ ಮರಿಯಳೂ ಸಲೋಮೆಯೂ ಆತನ ದೇಹಕ್ಕೆ ಹಚ್ಚುವದಕ್ಕಾಗಿ ಸುಗಂಧದ್ರವ್ಯಗಳನ್ನು ಕೊಂಡರು.",
			"2": "<sup>2</sup> ಮತ್ತು ವಾರದ ಮೊದಲನೆಯ ದಿನದ ಬೆಳಿಗ್ಗೆ ಹೊತ್ತು ಮೂಡುವಾಗ ಅವರು ಸಮಾಧಿಯ ಬಳಿಗೆ ಬಂದರು.",
			"3": "<sup>3</sup> ಸಮಾಧಿಗೆ ಮುಚ್ಚಿದ್ದ ಕಲ್ಲು ಬಹಳ ದೊಡ್ಡದಾಗಿದ್ದದರಿಂದ ಅವರು - ಸಮಾಧಿಯ ಬಾಗಿಲಿಂದ ಆ ಕಲ್ಲನ್ನು ನಮಗೋಸ್ಕರ ಉರುಳಿಸುವವರು ಯಾರಿದ್ದಾರೆ ಎಂದು ತಮ್ಮತಮ್ಮೊಳಗೆ ಮಾತಾಡಿಕೊಳ್ಳುತ್ತಾ ಇರಲಾಗಿ",
			"4": "<sup>4</sup> ತಲೆಯೆತ್ತಿ ನೋಡಿ ಆ ಕಲ್ಲು ಉರುಳಿಸಲ್ಪಟ್ಟಿರುವದನ್ನು ಕಂಡರು.",
			"5": "<sup>5</sup> ಅವರು ಸಮಾಧಿಯೊಳಕ್ಕೆ ಹೋಗಿ ಒಬ್ಬ ಯೌವನಸ್ಥನು ಬಿಳೀ ಅಂಗಿಯನ್ನು ತೊಟ್ಟುಕೊಂಡು ಬಲಗಡೆ ಕೂತಿರುವದನ್ನು ಕಂಡು ಬೆಚ್ಚಿ ಬೆರಗಾದರು.",
			"6": "<sup>6</sup> ಅವನು ಅವರಿಗೆ - ಬೆರಗಾಗಬೇಡಿರಿ. ಶಿಲುಬೆಗೆ ಹಾಕಲ್ಪಟ್ಟಿದ್ದ ನಜರೇತಿನ ಯೇಸುವನ್ನು ಹುಡುಕುತ್ತೀರಲ್ಲವೇ. ಆತನು ಜೀವಿತನಾಗಿ ಎದ್ದಿದ್ದಾನೆ; ಇಲ್ಲಿ ಇಲ್ಲ. ಆತನನ್ನು ಇಟ್ಟಿದ್ದ ಸ್ಥಳವು ಇದೇ, ನೋಡಿರಿ.",
			"7": "<sup>7</sup> ಆದರೆ ನೀವು ಹೋಗಿ ಆತನ ಶಿಷ್ಯರಿಗೂ ಪೇತ್ರನಿಗೂ - ಆತನು ನಿಮಗೆ ಹೇಳಿದಂತೆ ನಿಮ್ಮ ಮುಂದೆ ಗಲಿಲಾಯಕ್ಕೆ ಹೋಗುತ್ತಾನೆ, ಅಲ್ಲಿ ಆತನನ್ನು ಕಾಣುವಿರಿ ಎಂದು ತಿಳಿಸಿರಿ ಎಂದು ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ಅವರು ಭ್ರಮೆಹಿಡಿದವರಾಗಿ ನಡುಗುತ್ತಾ ಹೊರಕ್ಕೆ ಬಂದು ಸಮಾಧಿಯ ಬಳಿಯಿಂದ ಓಡಿಹೋದರು; ಅವರು ಹೆದರಿಕೊಂಡದರಿಂದ ಯಾರಿಗೂ ಏನೂ ಹೇಳಲಿಲ್ಲ.",
			"9": "<sup>9</sup> [ವಾರದ ಮೊದಲನೆಯ ದಿನ ಬೆಳಿಗ್ಗೆ ಯೇಸು ಜೀವಿತನಾಗಿ ಎದ್ದ ಮೇಲೆ ಮಗ್ದಲದ ಮರಿಯಳಿಗೆ ಮೊದಲು ಕಾಣಿಸಿಕೊಂಡನು. ಆಕೆಗೆ ಏಳು ದೆವ್ವಗಳನ್ನು ಬಿಡಿಸಿದ್ದನು.",
			"10": "<sup>10</sup> ಆಕೆ ಹೋಗಿ ಆತನು ಕಾಣಿಸಿಕೊಂಡದ್ದನ್ನು ಮುಂಚೆ ಆತನ ಜೊತೆಯಲ್ಲಿದ್ದವರಿಗೆ ತಿಳಿಸಿದಳು; ಇವರು ಹಂಬಲಿಸುತ್ತಾ ಅಳುತ್ತಾ ಕೂತಿದ್ದರು.",
			"11": "<sup>11</sup> ಆತನು ಬದುಕಿದ್ದಾನೆ, ನನಗೆ ಕಾಣಿಸಿಕೊಂಡನು ಎಂಬ ಮಾತನ್ನು ಕೇಳಿದಾಗ ಅವರು ನಂಬಲಿಲ್ಲ.",
			"12": "<sup>12</sup> ಇದಾದ ಮೇಲೆ ಶಿಷ್ಯರಲ್ಲಿ ಇಬ್ಬರು ಒಂದು ಹಳ್ಳಿಗೆ ನಡಕೊಂಡು ಹೋಗುತ್ತಿದ್ದಾಗ ಆತನು ಅವರಿಗೆ ರೂಪಾಂತರದಿಂದ ಕಾಣಿಸಿಕೊಂಡನು.",
			"13": "<sup>13</sup> ಅವರು ಹೋಗಿ ಉಳಿದವರಿಗೆ ತಿಳಿಸಿದರು. ಆದರೆ ಅವರನ್ನೂ ನಂಬಲಿಲ್ಲ.",
			"14": "<sup>14</sup> ತರುವಾಯ ಹನ್ನೊಂದು ಮಂದಿ ಶಿಷ್ಯರು ಊಟಕ್ಕೆ ಕೂತಿದ್ದಾಗ ಆತನು ಅವರಿಗೂ ಕಾಣಿಸಿಕೊಂಡನು. ತಾನು ಜೀವಿತನಾಗಿ ಎದ್ದು ಬಂದ ಮೇಲೆ ತನ್ನನ್ನು ಕಂಡವರ ಮಾತನ್ನು ಅವರು ನಂಬಲಿಲ್ಲವಾದ ಕಾರಣ ಆತನು ಅವರ ಅಪನಂಬಿಕೆಗೂ ಅವರ ಮನಸ್ಸಿನ ಕಾಠಿಣ್ಯಕ್ಕೂ ಅವರನ್ನು ಗದರಿಸಿದನು.",
			"15": "<sup>15</sup> ಆಮೇಲೆ ಅವರಿಗೆ - ನೀವು ಲೋಕದ ಎಲ್ಲಾ ಕಡೆಗೆ ಹೋಗಿ ಜಗತ್ತಿಗೆಲ್ಲಾ ಸುವಾರ್ತೆಯನ್ನು ಸಾರಿರಿ.",
			"16": "<sup>16</sup> ನಂಬಿ ದೀಕ್ಷಾಸ್ನಾನ ಮಾಡಿಸಿಕೊಳ್ಳುವವನು ರಕ್ಷಣೆಹೊಂದುವನು; ನಂಬದೆ ಹೋಗುವವನು ದಂಡನೆಗೆ ಗುರಿಯಾಗುವನು.",
			"17": "<sup>17</sup> ಇದಲ್ಲದೆ ನಂಬುವವರಿಂದ ಈ ಸೂಚಕಕಾರ್ಯಗಳು ಉಂಟಾಗುವವು; ನನ್ನ ಹೆಸರನ್ನು ಹೇಳಿ ದೆವ್ವಗಳನ್ನು ಬಿಡಿಸುವರು; ಹೊಸಭಾಷೆಗಳಿಂದ ಮಾತಾಡುವರು; ಹಾವುಗಳನ್ನು ಎತ್ತುವರು;",
			"18": "<sup>18</sup> ವಿಷಪದಾರ್ಥವನ್ನೇನಾದರೂ ಕುಡಿದರೂ ಅವರಿಗೆ ಯಾವ ಕೇಡೂ ಆಗುವದಿಲ್ಲ; ಅವರು ರೋಗಿಗಳ ಮೇಲೆ ಕೈಯಿಟ್ಟರೆ ಅವರಿಗೆ ಗುಣವಾಗುವದು ಎಂದು ಹೇಳಿದನು.",
			"19": "<sup>19</sup> ಯೇಸುಸ್ವಾಮಿಯು ಅವರ ಕೂಡ ಮಾತಾಡಿದ ಮೇಲೆ ಆಕಾಶಕ್ಕೆ ಒಯ್ಯಲ್ಪಟ್ಟು ದೇವರ ಬಲಗಡೆಯಲ್ಲಿ ಆಸನಾರೂಢನಾದನು.",
			"20": "<sup>20</sup> ಇತ್ತಲಾಗಿ ಅವರು ಹೊರಟುಹೋಗಿ ಎಲ್ಲೆಲ್ಲಿಯೂ ಸುವಾರ್ತೆಯನ್ನು ಸಾರಿದರು; ಮತ್ತು ಕರ್ತನು ಅವರ ಕೂಡ ಕೆಲಸ ಮಾಡುತ್ತಾ ಸೂಚಕಕಾರ್ಯಗಳಿಂದ ಸುವಾರ್ತೆಯ ವಾಕ್ಯವನ್ನು ಬಲಪಡಿಸುತ್ತಾ ಇದ್ದನು.]"
		}
	}
};
module.exports = book;