var book = {
	"name": "ಯೋಹಾನ",
	"numChapters": 21,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ಆದಿಯಲ್ಲಿ ವಾಕ್ಯವಿತ್ತು; ಆ ವಾಕ್ಯವು ದೇವರ ಬಳಿಯಲ್ಲಿತ್ತು; ಆ ವಾಕ್ಯವು ದೇವರಾಗಿತ್ತು.",
			"2": "<sup>2</sup> ಆ ವಾಕ್ಯವೆಂಬವನು ಆದಿಯಲ್ಲಿ ದೇವರ ಬಳಿಯಲ್ಲಿದ್ದನು.",
			"3": "<sup>3</sup> ಆತನ ಮೂಲಕವಾಗಿ ಸಮಸ್ತವು ಉಂಟಾಯಿತು; ಉಂಟಾಗಿರುವ ವಸ್ತುಗಳಲ್ಲಿ ಆತನಿಲ್ಲದೆ ಒಂದಾದರೂ ಉಂಟಾಗಲಿಲ್ಲ.",
			"4": "<sup>4</sup> ಆತನಲ್ಲಿ ಜೀವವಿತ್ತು; ಆ ಜೀವವು ಮನುಷ್ಯರಿಗೆ ಬೆಳಕಾಗಿತ್ತು.",
			"5": "<sup>5</sup> ಆ ಬೆಳಕು ಕತ್ತಲಲ್ಲಿ ಪ್ರಕಾಶಿಸುತ್ತದೆ; ಕತ್ತಲು ಅದನ್ನು ಮುಸುಕಲಿಲ್ಲ.",
			"6": "<sup>6</sup> ದೇವರು ಕಳುಹಿಸಿದ ಒಬ್ಬ ಮನುಷ್ಯನು ಬಂದನು; ಅವನ ಹೆಸರು ಯೋಹಾನನು. ಈ ಯೋಹಾನನು ಸಾಕ್ಷಿಗಾಗಿ ಬಂದನು.",
			"7": "<sup>7</sup> ತನ್ನ ಮೂಲಕವಾಗಿ ಎಲ್ಲರು ನಂಬುವಂತೆ ಅವನು ಆ ಬೆಳಕಿನ ವಿಷಯದಲ್ಲಿ ಸಾಕ್ಷಿಕೊಡುವದಕ್ಕೆ ಬಂದನು.",
			"8": "<sup>8</sup> ಅವನೇ ಆ ಬೆಳಕಲ್ಲ, ಆ ಬೆಳಕಿನ ವಿಷಯದಲ್ಲಿ ಸಾಕ್ಷಿಕೊಡುವದಕ್ಕಾಗಿ ಬಂದವನು.",
			"9": "<sup>9</sup> ನಿಜವಾದ ಬೆಳಕು ಲೋಕಕ್ಕೆ ಬರುವದಾಗಿತ್ತು; ಆ ಬೆಳಕೇ ಪ್ರತಿ ಮನುಷ್ಯನಿಗೂ ಬೆಳಕನ್ನು ಕೊಡುವಂಥದು.",
			"10": "<sup>10</sup> ಆತನು ಲೋಕದಲ್ಲಿ ಇದ್ದನು; ಮತ್ತು ಲೋಕವು ಆತನ ಮೂಲಕವಾಗಿ ಉಂಟಾಯಿತು; ಆದರೂ ಲೋಕವು ಆತನನ್ನು ಅರಿಯಲಿಲ್ಲ.",
			"11": "<sup>11</sup> ಆತನು ತನ್ನ ಸ್ವಾಸ್ಥ್ಯಕ್ಕೆ ಬಂದನು; ಆದರೆ ಸ್ವಂತಜನರು ಆತನನ್ನು ಅಂಗೀಕರಿಸಲಿಲ್ಲ.",
			"12": "<sup>12</sup> ಯಾರಾರು ಆತನನ್ನು ಅಂಗೀಕರಿಸಿದರೋ, ಅಂದರೆ ಆತನ ಹೆಸರಿನಲ್ಲಿ ನಂಬಿಕೆಯಿಟ್ಟರೋ, ಅವರಿಗೆ ದೇವರ ಮಕ್ಕಳಾಗುವ ಅಧಿಕಾರ ಕೊಟ್ಟನು.",
			"13": "<sup>13</sup> ಇವರು ರಕ್ತಸಂಬಂಧದಿಂದಾಗಲಿ ಕಾಮದಿಂದಾಗಲಿ ಪುರುಷಸಂಕಲ್ಪದಿಂದಾಗಲಿ ಹುಟ್ಟಿದವರಲ್ಲ, ದೇವರಿಂದಲೇ ಹುಟ್ಟಿದವರು.",
			"14": "<sup>14</sup> ಆ ವಾಕ್ಯವೆಂಬವನು ನರಾವತಾರ ಎತ್ತಿ ನಮ್ಮ ಮಧ್ಯದಲ್ಲಿ ವಾಸಮಾಡಿದನು. ನಾವು ಆತನ ಮಹಿಮೆಯನ್ನು ನೋಡಿದೆವು; ಆ ಮಹಿಮೆಯು ತಂದೆಯ ಬಳಿಯಿಂದ ಬಂದ ಒಬ್ಬನೇ ಮಗನಿಗೆ ಇರತಕ್ಕ ಮಹಿಮೆ. ಆತನು ಕೃಪೆಯಿಂದಲೂ ಸತ್ಯದಿಂದಲೂ ತುಂಬಿದವನಾಗಿದ್ದನು.",
			"15": "<sup>15</sup> ಯೋಹಾನನು ಆತನ ವಿಷಯವಾಗಿ ಸಾಕ್ಷಿಕೊಡುತ್ತಾನೆ; ಹೇಗಂದರೆ - ನನ್ನ ಹಿಂದೆ ಬರುವವನು ನನಗಿಂತ ಮೊದಲೇ ಇದ್ದದರಿಂದ ನನಗೆ ಮುಂದಿನವನಾದನು ಎಂಬದಾಗಿ ನಾನು ಹೇಳಿದವನು ಈತನೇ ಎಂದು ಕೂಗಿ ಹೇಳಿದ್ದಾನೆ.",
			"16": "<sup>16</sup> ನಾವೆಲ್ಲರು ಆತನ ಪರಿಪೂರ್ಣತೆಯೊಳಗಿಂದ ಕೃಪೆಯ ಮೇಲೆ ಕೃಪೆಯನ್ನು ಹೊಂದಿದೆವು.",
			"17": "<sup>17</sup> ಧರ್ಮಶಾಸ್ತ್ರವು ಮೋಶೆಯ ಮುಖಾಂತರ ಕೊಡಲ್ಪಟ್ಟಿತು; ಕೃಪೆಯೂ ಸತ್ಯವೂ ಯೇಸು ಕ್ರಿಸ್ತನ ಮುಖಾಂತರ ಬಂದವು.",
			"18": "<sup>18</sup> ದೇವರನ್ನು ಯಾರೂ ಎಂದೂ ಕಂಡಿಲ್ಲ; ಯಾವನು ಏಕಪುತ್ರನೂ ಸ್ವತಃ ದೇವರೂ ಆಗಿದ್ದು ತಂದೆಯ ಎದೆಯಲ್ಲಿದ್ದಾನೋ ಆತನೇ ತಿಳಿಯಪಡಿಸಿದನು.",
			"19": "<sup>19</sup> ಯೋಹಾನನು ಕೊಟ್ಟ ಸಾಕ್ಷಿ ಏನಂದರೆ - ಯೆಹೂದ್ಯರು ಯೆರೂಸಲೇಮಿನಿಂದ ಯಾಜಕರನ್ನೂ ಲೇವಿಯರನ್ನೂ ಅವನ ಬಳಿಗೆ ಕಳುಹಿಸಿ - ನೀನು ಯಾರೆಂದು ಕೇಳಿದ್ದಕ್ಕೆ",
			"20": "<sup>20</sup> ಅವನು ಮರೆಮಾಜದೆ - ನಾನು ಕ್ರಿಸ್ತನಲ್ಲವೆಂದು ಎಲ್ಲರ ಮುಂದೆ ಹೇಳಿದನು.",
			"21": "<sup>21</sup> ಹಾಗಾದರೆ ನೀನು ಯಾರು? ಎಲೀಯನೋ? ಎಂದು ಅವರು ಕೇಳಲು ಅಲ್ಲವೆಂದನು. ನೀನು ಬರಬೇಕಾದ ಆ ಪ್ರವಾದಿಯೋ? ಅಂದದ್ದಕ್ಕೂ ಅಲ್ಲವೆಂದನು.",
			"22": "<sup>22</sup> ಆಗ ಅವರು - ನೀನು ಯಾರು? ನಮ್ಮನ್ನು ಕಳುಹಿಸಿದವರಿಗೆ ನಾವು ಉತ್ತರ ಹೇಳಬೇಕಲ್ಲಾ; ನಿನ್ನ ವಿಷಯವಾಗಿ ಏನು ಹೇಳುತ್ತೀ? ಎಂದು ಕೇಳಲು ಅವನು -",
			"23": "<sup>23</sup> ಕರ್ತನ ದಾರಿಯನ್ನು ನೆಟ್ಟಗೆ ಮಾಡಿರಿ ಎಂದು ಅಡವಿಯಲ್ಲಿ ಕೂಗುವವನ ಶಬ್ದವದೆ ಎಂಬದಾಗಿ ಯೆಶಾಯಪ್ರವಾದಿಯು ಹೇಳಿದನಷ್ಟೆ; ಆ ಶಬ್ದವೇ ನಾನು ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"24": "<sup>24</sup> ಬಂದಿದ್ದವರು ಫರಿಸಾಯರ ಕಡೆಯವರಾಗಿದ್ದು -",
			"25": "<sup>25</sup> ನೀನು ಕ್ರಿಸ್ತನೂ ಎಲೀಯನೂ ಆ ಪ್ರವಾದಿಯೂ ಅಲ್ಲವಾದರೆ ಸ್ನಾನಮಾಡಿಸುವದೇನು? ಎಂದು ಅವನನ್ನು ಕೇಳಿದರು.",
			"26": "<sup>26</sup> ಅದಕ್ಕೆ ಯೋಹಾನನು - ನಾನು ನೀರಿನಿಂದ ಸ್ನಾನಮಾಡಿಸುವವನು; ಆದರೆ ನೀವು ಅರಿಯದೆ ಇರುವ ಒಬ್ಬಾತನು ನಿಮ್ಮ ಮಧ್ಯದಲ್ಲಿ ನಿಂತಿದ್ದಾನೆ;",
			"27": "<sup>27</sup> ಆತನೇ ನನ್ನ ಹಿಂದೆ ಬರತಕ್ಕವನು; ಆತನ ಕೆರದ ಬಾರನ್ನು ಬಿಚ್ಚುವದಕ್ಕೂ ನಾನು ಯೋಗ್ಯನಲ್ಲ ಅಂದನು.",
			"28": "<sup>28</sup> ಇದೆಲ್ಲಾ ಯೊರ್ದನ್ ಹೊಳೆಯ ಆಚೇಕಡೆಯಲ್ಲಿರುವ ಬೇಥಾನ್ಯದಲ್ಲಿ ನಡೆಯಿತು. ಅಲ್ಲಿ ಯೋಹಾನನು ಸ್ನಾನ ಕೊಡುತ್ತಿದ್ದನು.",
			"29": "<sup>29</sup> ಮರುದಿನ ಯೋಹಾನನು ತನ್ನ ಕಡೆಗೆ ಬರುವ ಯೇಸುವನ್ನು ನೋಡಿ - ಅಗೋ [ಯಜ್ಞಕ್ಕೆ] ದೇವರು ನೇಮಿಸಿದ ಕುರಿ, ಲೋಕದ ಪಾಪವನ್ನು ನಿವಾರಣೆ ಮಾಡುವವನು.",
			"30": "<sup>30</sup> ನನ್ನ ಹಿಂದೆ ಬರುವವನು ನನಗಿಂತ ಮೊದಲೇ ಇದ್ದದರಿಂದ ನನಗೆ ಮುಂದಿನವನಾದನು ಎಂದು ನಾನು ಹೇಳಿದ ಮಾತು ಈತನ ವಿಷಯವಾಗಿಯೇ.",
			"31": "<sup>31</sup> ನನಗೂ ಆತನ ಗುರುತಿರಲಿಲ್ಲ; ಆದರೆ ಆತನನ್ನು ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ತಿಳಿಯಪಡಿಸುವದಕ್ಕೋಸ್ಕರವೇ ನಾನು ನೀರಿನ ಸ್ನಾನವನ್ನು ಮಾಡಿಸುವವನಾಗಿ ಬಂದೆನು ಅಂದನು.",
			"32": "<sup>32</sup> ಇದಲ್ಲದೆ ಯೋಹಾನನು ಸಾಕ್ಷಿಕೊಟ್ಟು ಹೇಳಿದ್ದೇನಂದರೆ - ದೇವರಾತ್ಮವು ಪಾರಿವಾಳದಂತೆ ಆಕಾಶದಿಂದ ಇಳಿಯುವದನ್ನು ನೋಡಿದೆನು. ಅದು ಆತನ ಮೇಲೆ ನೆಲೆಗೊಂಡಿತು.",
			"33": "<sup>33</sup> ನನಗೂ ಆತನ ಗುರುತಿರಲಿಲ್ಲ; ಆದರೆ ನೀರಿನ ಸ್ನಾನವನ್ನು ಮಾಡಿಸುವದಕ್ಕೆ ನನ್ನನ್ನು ಕಳುಹಿಸಿದಾತನು - ಯಾವನ ಮೇಲೆ ಆತ್ಮವು ಇಳಿದುಬಂದು ಇರುವದನ್ನು ನೀನು ನೋಡುವಿಯೋ ಆತನೇ ಪವಿತ್ರಾತ್ಮದ ಸ್ನಾನವನ್ನು ಕೊಡುವವನು ಎಂದು ತಾನೇ ನನಗೆ ಹೇಳಿದನು.",
			"34": "<sup>34</sup> ನಾನು ಅದನ್ನು ನೋಡಿ ಈತನೇ ದೇವಕುಮಾರನೆಂದು ಸಾಕ್ಷಿಕೊಟ್ಟಿದ್ದೇನೆ ಅಂದನು.",
			"35": "<sup>35</sup> ಮರುದಿನ ತಿರಿಗಿ ಯೋಹಾನನೂ ಅವನ ಶಿಷ್ಯರಲ್ಲಿ ಇಬ್ಬರೂ ನಿಂತುಕೊಂಡಿದ್ದಾಗ ಯೋಹಾನನು ಅಲ್ಲಿ ತಿರುಗಾಡುತ್ತಿರುವ ಯೇಸುವನ್ನು ನೋಡಿ -",
			"36": "<sup>36</sup> ಅಗೋ ಯಜ್ಞಕ್ಕೆ ದೇವರು ನೇಮಿಸಿದ ಕುರಿ ಅಂದನು.",
			"37": "<sup>37</sup> ಆ ಇಬ್ಬರು ಶಿಷ್ಯರು ಅದನ್ನು ಕೇಳಿ ಯೇಸುವಿನ ಹಿಂದೆ ಹೋದರು.",
			"38": "<sup>38</sup> ಯೇಸು ಹಿಂತಿರುಗಿ ಅವರು ತನ್ನ ಹಿಂದೆ ಬರುವದನ್ನು ಕಂಡು - ನಿಮಗೆ ಏನು ಬೇಕಾಗಿದೆ ಎಂದು ಕೇಳಲು ಅವರು - ರಬ್ಬಿಯೇ, ನೀನು ಇಳುಕೊಂಡಿರುವದು ಎಲ್ಲಿ? ಅಂದರು. (ರಬ್ಬಿ ಅಂದರೆ ಗುರು).",
			"39": "<sup>39</sup> ಆತನು - ಬಂದು ನೋಡಿರಿ ಅಂದಾಗ ಅವರು ಬಂದು ಆತನು ಇಳುಕೊಂಡಿದ್ದ ಸ್ಥಳವನ್ನು ನೋಡಿ ಆ ದಿವಸ ಆತನ ಸಂಗಡ ಇದ್ದರು. ಆಗ ಹೆಚ್ಚುಕಡಿಮೆ ಸಾಯಂಕಾಲ ನಾಲ್ಕು ಘಂಟೆಯಾಗಿತ್ತು.",
			"40": "<sup>40</sup> ಯೋಹಾನನ ಮಾತನ್ನು ಕೇಳಿ ಯೇಸುವನ್ನು ಹಿಂಬಾಲಿಸಿದ ಆ ಇಬ್ಬರಲ್ಲಿ ಸೀಮೋನ್ ಪೇತ್ರನ ತಮ್ಮನಾದ ಅಂದ್ರೆಯನು ಒಬ್ಬನು.",
			"41": "<sup>41</sup> ಇವನು ಮೊದಲು ತನ್ನ ಅಣ್ಣನಾದ ಸೀಮೋನನನ್ನು ಕಂಡುಕೊಂಡು ಅವನಿಗೆ - ಮೆಸ್ಸೀಯನು ನಮಗೆ ಸಿಕ್ಕಿದನು ಎಂದು ಹೇಳಿ",
			"42": "<sup>42</sup> ಅವನನ್ನು ಯೇಸುವಿನ ಬಳಿಗೆ ಕರಕೊಂಡು ಬಂದನು. (ಮೆಸ್ಸೀಯನು ಅಂದರೆ ಕ್ರಿಸ್ತನು. ) ಯೇಸು ಅವನನ್ನು ನೋಡಿ - ನೀನು ಯೋಹಾನನ ಮಗನಾದ ಸೀಮೋನನು; ಇನ್ನು ಮೇಲೆ ಕೇಫನೆನಿಸಿಕೊಳ್ಳುವಿ ಅಂದನು (ಕೇಫನಂದರೆ ಪೇತ್ರ. )",
			"43": "<sup>43</sup> ಮರುದಿನ ಯೇಸು ಗಲಿಲಾಯಕ್ಕೆ ಹೊರಟುಹೋಗಬೇಕೆಂದಿರುವಾಗ ಫಿಲಿಪ್ಪನನ್ನು ಕಂಡುಕೊಂಡು - ನನ್ನನ್ನು ಹಿಂಬಾಲಿಸು ಅಂದನು.",
			"44": "<sup>44</sup> ಈ ಫಿಲಿಪ್ಪನು ಬೇತ್ಸಾಯಿದದವನು, ಅಂದರೆ ಅಂದ್ರೆಯ ಪೇತ್ರರ ಊರಿನವನು.",
			"45": "<sup>45</sup> ಫಿಲಿಪ್ಪನು ನತಾನಯೇಲನನ್ನು ಕಂಡುಕೊಂಡು ಅವನಿಗೆ - ಯಾವನ ವಿಷಯವಾಗಿ ಮೋಶೆಯು ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿ ಬರೆದನೋ ಮತ್ತು ಪ್ರವಾದಿಗಳು ಬರೆದರೋ, ಆತನು ನಮಗೆ ಸಿಕ್ಕಿದನು; ಆತನು ಯಾರಂದರೆ ಯೋಸೇಫನ ಮಗನಾದ ನಜರೇತಿನ ಯೇಸು ಎಂದು ಹೇಳಿದನು.",
			"46": "<sup>46</sup> ನತಾನಯೇಲನು - ಒಳ್ಳೇದೇನಾದರೂ ನಜರೇತಿನಿಂದ ಬರುವದುಂಟೇ? ಅಂದಾಗ ಫಿಲಿಪ್ಪನು - ಬಂದು ನೋಡು ಅಂದನು.",
			"47": "<sup>47</sup> ಯೇಸು ತನ್ನ ಕಡೆಗೆ ಬರುವ ನತಾನಯೇಲನನ್ನು ಕಂಡು ಅವನ ವಿಷಯವಾಗಿ - ಇಗೋ ಇವನು ನಿಜವಾದ ಇಸ್ರಾಯೇಲನು; ಇವನಲ್ಲಿ ಕಪಟವಿಲ್ಲ ಅಂದನು.",
			"48": "<sup>48</sup> ನತಾನಯೇಲನು - ನನ್ನನ್ನು ನೀನು ಹೇಗೆ ಬಲ್ಲೆ? ಎಂದು ಯೇಸುವನ್ನು ಕೇಳಿದ್ದಕ್ಕೆ ಆತನು - ಫಿಲಿಪ್ಪನು ನಿನ್ನನ್ನು ಕರೆಯುವದಕ್ಕಿಂತ ಮುಂಚೆ ನೀನು ಆ ಅಂಜೂರದ ಮರದ ಕೆಳಗೆ ಇದ್ದಾಗ ನಿನ್ನನ್ನು ನೋಡಿದೆನು ಅಂದನು.",
			"49": "<sup>49</sup> ಅದಕ್ಕೆ ನತಾನಯೇಲನು - ಗುರುವೇ, ನೀನು ದೇವಕುಮಾರನು ಸರಿ; ನೀನೇ ಇಸ್ರಾಯೇಲಿನ ಅರಸನು ಅಂದನು.",
			"50": "<sup>50</sup> ಅದಕ್ಕೆ ಯೇಸು - ಆ ಅಂಜೂರದ ಮರದ ಕೆಳಗೆ ನಿನ್ನನ್ನು ನೋಡಿದೆನೆಂದು ನಾನು ನಿನಗೆ ಹೇಳಿದ ಮಾತ್ರದಿಂದ ನಂಬುತ್ತೀಯೋ? ಇದಕ್ಕಿಂತ ಹೆಚ್ಚಿನ ಕಾರ್ಯಗಳನ್ನು ನೋಡುವಿ ಅಂದನು.",
			"51": "<sup>51</sup> ಇದಲ್ಲದೆ ಅವನಿಗೆ - ನಾನು ನಿಮಗೆ ನಿಜನಿಜವಾಗಿ ಹೇಳುತ್ತೇನೆ, ಪರಲೋಕವು ತೆರೆದಿರುವದನ್ನೂ ಮನುಷ್ಯಕುಮಾರನ ಮೇಲೆ ದೇವದೂತರು ಏರಿಹೋಗುತ್ತಾ ಇಳಿದುಬರುತ್ತಾ ಇರುವದನ್ನೂ ನೋಡುವಿರಿ ಎಂದು ಹೇಳಿದನು."
		},
		"2": {
			"1": "<sup>1</sup> ಮೂರನೆಯ ದಿನದಲ್ಲಿ ಗಲಿಲಾಯದ ಕಾನಾ ಊರಿನಲ್ಲಿ ಒಂದು ಮದುವೆ ಆಯಿತು; ಯೇಸುವಿನ ತಾಯಿ ಅಲ್ಲಿ ಇದ್ದಳು;",
			"2": "<sup>2</sup> ಯೇಸುವನ್ನೂ ಆತನ ಶಿಷ್ಯರನ್ನೂ ಮದುವೆಗೆ ಕರೆದರು.",
			"3": "<sup>3</sup> ದ್ರಾಕ್ಷಾರಸವು ಸಾಲದೆ ಹೋಗಲಾಗಿ ಯೇಸುವಿನ ತಾಯಿ ಆತನಿಗೆ - ಅವರಲ್ಲಿ ದ್ರಾಕ್ಷಾರಸವಿಲ್ಲ ಎಂದು ಹೇಳಿದಳು.",
			"4": "<sup>4</sup> ಯೇಸು ಆಕೆಗೆ - ಅಮ್ಮಾ, ನನ್ನ ಗೊಡವೆ ನಿನಗೇಕೆ? ನನ್ನ ಸಮಯವು ಇನ್ನೂ ಬಂದಿಲ್ಲ ಎಂದು ಹೇಳಲು",
			"5": "<sup>5</sup> ಆತನ ತಾಯಿಯು ಕೆಲಸದವರಿಗೆ - ಆತನು ನಿಮಗೆ ಏನು ಹೇಳುವನೋ ಅದನ್ನು ಮಾಡಿರಿ ಅಂದಳು.",
			"6": "<sup>6</sup> ಯೆಹೂದ್ಯರ ಶುದ್ಧಾಚಾರದ ಪದ್ಧತಿಯ ಪ್ರಕಾರ ಎರಡು ಮೂರು ಕೊಳಗ ನೀರು ಹಿಡಿಯುವ ಆರು ಕಲ್ಲಿನ ಬಾನೆಗಳು ಅಲ್ಲಿ ಇಟ್ಟಿದ್ದವು.",
			"7": "<sup>7</sup> ಯೇಸು ಅವರಿಗೆ - ಆ ಬಾನೆಗಳಲ್ಲಿ ನೀರು ತುಂಬಿರಿ ಅನ್ನಲು ಅವುಗಳನ್ನು ಕಂಠದ ಮಟ್ಟಿಗೆ ತುಂಬಿದರು.",
			"8": "<sup>8</sup> ಆಗ ಅವರಿಗೆ - ಈಗ ಅದನ್ನು ತೋಡಿಕೊಂಡು ಹೋಗಿ ಔತಣದ ಪಾರುಪತ್ಯಗಾರನಿಗೆ ಕೊಡಿರಿ ಎಂದು ಹೇಳಿದಾಗ ತಕ್ಕೊಂಡು ಹೋಗಿ ಕೊಟ್ಟರು.",
			"9": "<sup>9</sup> ಅದು ಎಲ್ಲಿಂದ ಬಂತೋ ನೀರನ್ನು ತೋಡಿದ ಕೆಲಸದವರಿಗೆ ತಿಳಿದಿತ್ತೇ ಹೊರತು ಔತಣದ ಪಾರುಪತ್ಯಗಾರನಿಗೆ ತಿಳಿಯದಿರಲಾಗಿ ಅವನು ಅಷ್ಟರೊಳಗೆ ದ್ರಾಕ್ಷಾರಸವಾದ ಆ ನೀರನ್ನು ರುಚಿನೋಡಿದಾಗ",
			"10": "<sup>10</sup> ಮದಲಿಂಗನನ್ನು ಕರೆದು - ಎಲ್ಲರು ಹಿರಿದಿನ ದ್ರಾಕ್ಷಾರಸವನ್ನು ಮೊದಲುಕೊಟ್ಟು ಅಮಲೇರಿದ ಮೇಲೆ ಕಿರಿದಿನ ದ್ರಾಕ್ಷಾರಸವನ್ನು ಕೊಡುತ್ತಾರೆ; ನೀನು ಹಿರಿದಿನ ದ್ರಾಕ್ಷಾರಸವನ್ನು ಇದುವರೆಗೂ ಇಟ್ಟುಕೊಂಡಿದ್ದೀ ಅಂದನು.",
			"11": "<sup>11</sup> ಯೇಸು ಈ ಮೊದಲನೆಯ ಸೂಚಕಕಾರ್ಯವನ್ನು ಗಲಿಲಾಯದ ಕಾನಾ ಊರಿನಲ್ಲಿ ಮಾಡಿ ತನ್ನ ಮಹಿಮೆಯನ್ನು ತೋರ್ಪಡಿಸಿದನು; ಮತ್ತು ಆತನ ಶಿಷ್ಯರು ಆತನಲ್ಲಿ ನಂಬಿಕೆಯಿಟ್ಟರು.",
			"12": "<sup>12</sup> ಇದಾದ ಮೇಲೆ ಆತನೂ ಆತನ ತಾಯಿಯೂ ತಮ್ಮಂದಿರೂ ಆತನ ಶಿಷ್ಯರೂ ಘಟ್ಟಾ ಇಳಿದು ಕಪೆರ್ನೌಮಿಗೆ ಹೋಗಿ ಅಲ್ಲಿ ಕೆಲವು ದಿವಸ ಇದ್ದರು.",
			"13": "<sup>13</sup> ಆಗ ಯೆಹೂದ್ಯರ ಪಸ್ಕಹಬ್ಬವು ಹತ್ತರ ಬಂದದರಿಂದ ಯೇಸು ಯೆರೂಸಲೇಮಿಗೆ ಹೋದನು.",
			"14": "<sup>14</sup> ಅಲ್ಲಿ ದೇವಾಲಯದೊಳಗೆ ದನ ಕುರಿ ಪಾರಿವಾಳ ಮಾರುವವರನ್ನೂ ಕೂತಿರುವ ಚಿನಿವಾರರನ್ನೂ ಕಂಡು ಹಗ್ಗದಿಂದ ಕೊರಡೆ ಮಾಡಿ",
			"15": "<sup>15</sup> ಅದರಿಂದ ಕುರಿ ದನ ಸಹಿತ ಎಲ್ಲರನ್ನು ದೇವಾಲಯದ ಹೊರಕ್ಕೆ ಅಟ್ಟಿ ಚಿನಿವಾರರ ರೊಕ್ಕವನ್ನು ಚೆಲ್ಲಿ ಮೇಜುಗಳನ್ನು ಕೆಡವಿದನು.",
			"16": "<sup>16</sup> ಪಾರಿವಾಳ ಮಾರುವವರಿಗೆ - ಇವುಗಳನ್ನು ಇಲ್ಲಿಂದ ತಕ್ಕೊಂಡು ಹೋಗಿರಿ; ನನ್ನ ತಂದೆಯ ಮನೆಯನ್ನು ಸಂತೆ ಮಾಡಬೇಡಿರಿ ಎಂದು ಹೇಳಿದನು.",
			"17": "<sup>17</sup> ಆಗ - ನಿನ್ನ ಆಲಯಾಭಿಮಾನವು ಬೆಂಕಿಯಂತೆ ನನ್ನನ್ನು ದಹಿಸುತ್ತದೆ ಎಂದು ಬರೆದ ಮಾತು ಶಿಷ್ಯರ ನೆನಪಿಗೆ ಬಂತು.",
			"18": "<sup>18</sup> ಯೆಹೂದ್ಯರು ಆತನನ್ನು - ನಿನಗೆ ಇದನ್ನೆಲ್ಲಾ ಮಾಡುವ ಅಧಿಕಾರ ಉಂಟೆಂಬದಕ್ಕೆ ಏನು ಸೂಚಕ ಕಾರ್ಯವನ್ನು ತೋರಿಸುತ್ತೀ? ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ",
			"19": "<sup>19</sup> ಯೇಸು - ಈ ದೇವಾಲಯವನ್ನು ಕೆಡವಿರಿ; ನಾನು ಮೂರು ದಿನದಲ್ಲಿ ಅದನ್ನು ಎಬ್ಬಿಸುವೆನು ಅಂದನು.",
			"20": "<sup>20</sup> ಅದಕ್ಕೆ ಯೆಹೂದ್ಯರು - ಈ ದೇವಾಲಯವನ್ನು ಕಟ್ಟುವದಕ್ಕೆ ನಾಲ್ವತ್ತಾರು ವರುಷ ಹಿಡಿಯಿತು; ನೀನು ಮೂರು ದಿನದಲ್ಲಿ ಅದನ್ನು ಎಬ್ಬಿಸುವಿಯೋ? ಅಂದರು.",
			"21": "<sup>21</sup> ಆದರೆ ಆತನು ತನ್ನ ದೇಹವೆಂಬ ದೇವಾಲಯದ ವಿಷಯವಾಗಿ ಆ ಮಾತನ್ನು ಹೇಳಿದ್ದನು.",
			"22": "<sup>22</sup> ಆದದರಿಂದ ಆತನು ಸತ್ತು ಬದುಕಿ ಎದ್ದ ಮೇಲೆ ಈ ಮಾತು ಆತನ ಶಿಷ್ಯರ ನೆನಪಿಗೆ ಬಂದು ಶಾಸ್ತ್ರದಲ್ಲಿ ಬರೆದದ್ದನ್ನೂ ಆತನ ಮಾತನ್ನೂ ಅವರು ನಂಬುವವರಾದರು.",
			"23": "<sup>23</sup> ಆತನು ಪಸ್ಕಹಬ್ಬದ ಜಾತ್ರೆಯಲ್ಲಿ ಯೆರೂಸಲೇಮಿನೊಳಗೆ ಇದ್ದಾಗ ಬಹು ಜನರು ಆತನು ಮಾಡಿದ ಸೂಚಕಕಾರ್ಯಗಳನ್ನು ನೋಡಿ ಆತನ ಹೆಸರಿನಲ್ಲಿ ನಂಬಿಕೆಯಿಟ್ಟರು.",
			"24": "<sup>24</sup> ಆದರೆ ಯೇಸು ಎಲ್ಲರನ್ನೂ ಬಲ್ಲವನಾದ್ದರಿಂದ ಅವರಿಗೆ ವಶವಾಗಲಿಲ್ಲ.",
			"25": "<sup>25</sup> ಆತನು ಪ್ರತಿಮನುಷ್ಯನ ಆಂತರ್ಯವನ್ನು ತಿಳಿದವನಾದ ಕಾರಣ ಯಾವನೂ ಯಾವ ಮನುಷ್ಯನ ವಿಷಯದಲ್ಲಿಯೂ ಆತನಿಗೆ ಸಾಕ್ಷಿ ಕೊಡಬೇಕಾದ ಅವಶ್ಯವಿರಲಿಲ್ಲ."
		},
		"3": {
			"1": "<sup>1</sup> ಫರಿಸಾಯರಲ್ಲಿ ಯೆಹೂದ್ಯರ ಹಿರೀಸಭೆಯವನಾದ ನಿಕೊದೇಮನೆಂಬ ಒಬ್ಬ ಮನುಷ್ಯನಿದ್ದನು.",
			"2": "<sup>2</sup> ಅವನು ರಾತ್ರಿಯಲ್ಲಿ ಯೇಸುವಿನ ಬಳಿಗೆ ಬಂದು ಆತನಿಗೆ - ಗುರುವೇ, ನೀನು ದೇವರ ಕಡೆಯಿಂದ ಬಂದ ಬೋಧಕನೆಂದು ಬಲ್ಲೆವು. ನೀನು ಮಾಡುವಂಥ ಈ ಸೂಚಕಕಾರ್ಯಗಳನ್ನು ದೇವರ ಸಹಾಯವಿಲ್ಲದೆ ಮಾಡುವದು ಯಾರಿಂದಲೂ ಆಗದು ಎಂದು ಹೇಳಿದನು.",
			"3": "<sup>3</sup> ಅದಕ್ಕೆ ಯೇಸು - ನಾನು ನಿನಗೆ ನಿಜನಿಜವಾಗಿ ಹೇಳುತ್ತೇನೆ, ಒಬ್ಬನು ಹೊಸದಾಗಿ ಹುಟ್ಟದಿದ್ದರೆ ಅವನು ದೇವರ ರಾಜ್ಯವನ್ನು ಕಾಣಲಾರನು ಅಂದನು.",
			"4": "<sup>4</sup> ನಿಕೊದೇಮನು ಆತನನ್ನು - ಮನುಷ್ಯನು ಮುದುಕನಾದ ಮೇಲೆ ಹುಟ್ಟುವದು ಹೇಗೆ? ಅವನು ತನ್ನ ತಾಯಿಯ ಗರ್ಭದಲ್ಲಿ ತಿರಿಗಿ ಸೇರಿ ಹುಟ್ಟುವದಾದೀತೇ? ಎಂದು ಕೇಳಿದನು.",
			"5": "<sup>5</sup> ಅದಕ್ಕೆ ಯೇಸು - ನಾನು ನಿನಗೆ ನಿಜನಿಜವಾಗಿ ಹೇಳುತ್ತೇನೆ, ಒಬ್ಬನು ನೀರಿನಿಂದಲೂ ಆತ್ಮನಿಂದಲೂ ಹುಟ್ಟದಿದ್ದರೆ ದೇವರ ರಾಜ್ಯಕ್ಕೆ ಸೇರಲಾರನು.",
			"6": "<sup>6</sup> ದೇಹದಿಂದ ಹುಟ್ಟಿದ್ದು ದೇಹವೇ; ಆತ್ಮನಿಂದ ಹುಟ್ಟಿದ್ದು ಆತ್ಮವೇ.",
			"7": "<sup>7</sup> ನೀವು ಹೊಸದಾಗಿ ಹುಟ್ಟಬೇಕು ಎಂದು ನಾನು ನಿನಗೆ ಹೇಳಿದ್ದರಿಂದ ಆಶ್ಚರ್ಯಪಡಬೇಡ.",
			"8": "<sup>8</sup> ಗಾಳಿಯು ಮನಸ್ಸು ಬಂದ ಕಡೆ ಬೀಸುತ್ತದೆ; ಅದರ ಸಪ್ಪಳವನ್ನು ಕೇಳುತ್ತೀ; ಆದರೆ ಅದು ಎಲ್ಲಿಂದ ಬರುತ್ತದೋ ಎಲ್ಲಿಗೆ ಹೋಗುತ್ತದೋ ನಿನಗೆ ತಿಳಿಯದು; ಆತ್ಮನಿಂದ ಹುಟ್ಟಿದವರೆಲ್ಲರು ಅದರಂತೆಯೇ ಅಂದನು.",
			"9": "<sup>9</sup> ಅದಕ್ಕೆ ನಿಕೊದೇಮನು - ಇದು ಆಗುವದು ಹೇಗೆ? ಅಂದಾಗ",
			"10": "<sup>10</sup> ಯೇಸು ಅವನಿಗೆ ಹೇಳಿದ್ದೇನಂದರೆ - ಇಸ್ರಾಯೇಲ್ ಜನಕ್ಕೆ ಬೋಧಕನಾಗಿರುವ ನಿನಗೆ ಇದು ತಿಳಿಯುವದಿಲ್ಲವೋ?",
			"11": "<sup>11</sup> ನಿನಗೆ ನಿಜನಿಜವಾಗಿ ಹೇಳುತ್ತೇನೆ, ತಿಳಿದದ್ದನ್ನು ಹೇಳುತ್ತೇವೆ, ನೋಡಿದ್ದಕ್ಕೆ ಸಾಕ್ಷಿಕೊಡುತ್ತೇವೆ; ನೀವು ನಮ್ಮ ಸಾಕ್ಷಿಯನ್ನು ಒಪ್ಪುವದಿಲ್ಲ.",
			"12": "<sup>12</sup> ನಾನು ಭೂಲೋಕದಲ್ಲಿ ನಡೆಯುವ ಕಾರ್ಯಗಳನ್ನು ಕುರಿತು ನಿಮಗೆ ಹೇಳುವಾಗ ನೀವು ನಂಬದೆಹೋದರೆ ಪರಲೋಕದ ಕಾರ್ಯಗಳನ್ನು ಕುರಿತು ನಿಮಗೆ ಹೇಳಿದರೆ ಹೇಗೆ ನಂಬೀರಿ?",
			"13": "<sup>13</sup> ಪರಲೋಕದಿಂದ ಇಳಿದುಬಂದವನೇ ಅಂದರೆ ಮನುಷ್ಯಕುಮಾರನೇ ಹೊರತು ಮತ್ತಾರೂ ಪರಲೋಕಕ್ಕೆ ಏರಿಹೋದವನಲ್ಲ.",
			"14": "<sup>14</sup> ಇದಲ್ಲದೆ [ಜನರು ನೋಡಿ ಜೀವದಿಂದುಳಿಯಬೇಕೆಂದು] ಮೋಶೆಯು ಅಡವಿಯಲ್ಲಿ ಆ ಸರ್ಪವನ್ನು ಎತ್ತರದಲ್ಲಿಟ್ಟ ಹಾಗೆಯೇ",
			"15": "<sup>15</sup> ಮನುಷ್ಯಕುಮಾರನು ತನ್ನನ್ನು ನಂಬುವವರೆಲ್ಲರೂ ನಿತ್ಯಜೀವವನ್ನು ಪಡೆಯಬೇಕೆಂದು ಎತ್ತರದಲ್ಲಿಡಲ್ಪಡಬೇಕು.",
			"16": "<sup>16</sup> ದೇವರು ಲೋಕದ ಮೇಲೆ ಎಷ್ಟೋ ಪ್ರೀತಿಯನ್ನಿಟ್ಟು ತನ್ನ ಒಬ್ಬನೇ ಮಗನನ್ನು ಕೊಟ್ಟನು; ಆತನನ್ನು ನಂಬುವ ಒಬ್ಬನಾದರೂ ನಾಶವಾಗದೆ ಎಲ್ಲರೂ ನಿತ್ಯಜೀವವನ್ನು ಪಡೆಯಬೇಕೆಂದು ಆತನನ್ನು ಕೊಟ್ಟನು.",
			"17": "<sup>17</sup> ದೇವರು ತನ್ನ ಮಗನ ಮುಖಾಂತರ ಲೋಕಕ್ಕೆ ರಕ್ಷಣೆಯಾಗಬೇಕೆಂದು ಆತನನ್ನು ಕಳುಹಿಸಿಕೊಟ್ಟನೇ ಹೊರತು ತೀರ್ಪುಮಾಡುವದಕ್ಕಾಗಿ ಕಳುಹಿಸಲಿಲ್ಲ.",
			"18": "<sup>18</sup> ಆತನನ್ನು ನಂಬುವವನಿಗೆ ತೀರ್ಪು ಆಗುವದಿಲ್ಲ; ಆದರೆ ನಂಬದವನಿಗೆ ದೇವರ ಒಬ್ಬನೇ ಮಗನ ಹೆಸರನ್ನು ನಂಬದೆಹೋದದರಿಂದ ಆಗಲೇ ತೀರ್ಪು ಆಗಿಹೋಯಿತು.",
			"19": "<sup>19</sup> ಆ ತೀರ್ಪು ಏನಂದರೆ - ಬೆಳಕು ಲೋಕಕ್ಕೆ ಬಂದಿದ್ದರೂ ಮನುಷ್ಯರ ಕೃತ್ಯಗಳು ಕೆಟ್ಟವುಗಳಾಗಿರುವದರಿಂದ ಅವರು ಬೆಳಕಿಗಿಂತ ಕತ್ತಲೆಯನ್ನೇ ಹೆಚ್ಚಾಗಿ ಪ್ರೀತಿಸಿದರು.",
			"20": "<sup>20</sup> ಕೆಟ್ಟದ್ದನ್ನು ಮಾಡುವವರು ಬೆಳಕನ್ನು ಸಹಿಸುವದಿಲ್ಲ, ತಮ್ಮ ಕೃತ್ಯಗಳು ದುಷ್ಕೃತ್ಯಗಳಾಗಿ ತೋರಿಬಂದಾವೆಂದು ಬೆಳಕಿಗೆ ಬರುವದಿಲ್ಲ;",
			"21": "<sup>21</sup> ಆದರೆ ಸತ್ಯವನ್ನು ಅನುಸರಿಸಿ ನಡೆಯುವವನು ತಾನು ದೇವರಿಂದ ನಡಿಸಿಕೊಂಡು ತನ್ನ ಕೃತ್ಯಗಳನ್ನು ಮಾಡಿದ್ದೇನೆಂದು ತೋರಿಬರುವಂತೆ ಬೆಳಕಿಗೆ ಬರುತ್ತಾನೆ.",
			"22": "<sup>22</sup> ಆಮೇಲೆ ಯೇಸು ತನ್ನ ಶಿಷ್ಯರೊಡನೆ ಯೂದಾಯ ದೇಶಕ್ಕೆ ಬಂದು ಅಲ್ಲಿ ಅವರ ಕೂಡ ಇದ್ದು ದೀಕ್ಷಾಸ್ನಾನಮಾಡಿಸುತ್ತಿದ್ದನು.",
			"23": "<sup>23</sup> ಯೋಹಾನನೂ ಸಲೀಮೂರಿನ ಹತ್ತಿರವಿರುವ ಐನೋನೆಂಬ ಸ್ಥಳದಲ್ಲಿದ್ದು ದೀಕ್ಷಾಸ್ನಾನಮಾಡಿಸುತ್ತಿದ್ದನು; ಅಲ್ಲಿ ಬಹಳ ನೀರಿತ್ತು; ಜನರು ಬಂದು ಸ್ನಾನಮಾಡಿಸಿಕೊಳ್ಳುತ್ತಾ ಇದ್ದರು;",
			"24": "<sup>24</sup> ಆ ಕಾಲದಲ್ಲಿ ಯೋಹಾನನು ಇನ್ನೂ ಸೆರೆಯಲ್ಲಿ ಹಾಕಲ್ಪಟ್ಟಿರಲಿಲ್ಲ.",
			"25": "<sup>25</sup> ಹೀಗಿರಲಾಗಿ ಯೋಹಾನನ ಶಿಷ್ಯರಲ್ಲಿ ಕೆಲವರಿಗೂ ಒಬ್ಬ ಯೆಹೂದ್ಯನಿಗೂ ಶುದ್ಧಾಚಾರವನ್ನು ಕುರಿತು ವಿಚಾರ ನಡೆಯಿತು.",
			"26": "<sup>26</sup> ಆಗ ಅವರು ಯೋಹಾನನ ಬಳಿಗೆ ಬಂದು ಅವನಿಗೆ - ಗುರುವೇ, ಯೊರ್ದನ್‍ಹೊಳೆಯ ಆಚೆ ನಿನ್ನ ಬಳಿಯಲ್ಲಿದ್ದ ಒಬ್ಬನ ವಿಷಯದಲ್ಲಿ ನೀನು ಸಾಕ್ಷಿಕೊಟ್ಟಿಯಲ್ಲಾ; ಅವನೂ ಸ್ನಾನ ಮಾಡಿಸುತ್ತಾನೆ, ಅವನ ಬಳಿಗೆ ಎಲ್ಲರು ಹೋಗುತ್ತಾರೆ ಎಂದು ಹೇಳಲು",
			"27": "<sup>27</sup> ಯೋಹಾನನು - ಪರಲೋಕದಿಂದ ಅನುಗ್ರಹಿಸೋಣವಾದದ್ದನ್ನೇ ಹೊರತು ಮನುಷ್ಯನು ಹೆಚ್ಚೇನು ಹೊಂದಲಾರನು.",
			"28": "<sup>28</sup> ನಾನು ಕ್ರಿಸ್ತನಲ್ಲ. ಆತನ ಮುಂದೆ ಕಳುಹಿಸಲ್ಪಟ್ಟವನಾಗಿದ್ದೇನೆ ಎಂದು ನಾನು ಹೇಳಿದ್ದಕ್ಕೆ ನೀವೇ ಸಾಕ್ಷಿ.",
			"29": "<sup>29</sup> ಮದಲಗಿತ್ತಿಯುಳ್ಳವನೇ ಮದಲಿಂಗನು; ಆದರೂ ಮದಲಿಂಗನ ಗೆಳೆಯನು ಅವನ ಹತ್ತರ ನಿಂತುಕೊಂಡು ಅವನ ಮಾತುಗಳನ್ನು ಕೇಳಿ ಮದಲಿಂಗನ ಧ್ವನಿಗೆ ಬಹು ಸಂತೋಷಪಡುತ್ತಾನಲ್ಲವೇ. ಇದರಂತೆ ನನಗಿರುವ ಸಂತೋಷ; ಅದು ನೆರವೇರಿತು.",
			"30": "<sup>30</sup> ಆತನು ವೃದ್ಧಿಯಾಗಬೇಕು; ನಾನು ಕಡಿಮೆಯಾಗಬೇಕು ಅಂದನು.",
			"31": "<sup>31</sup> ಮೇಲಣಿಂದ ಬರುವವನು ಎಲ್ಲರ ಮೇಲೆ ಇದ್ದಾನೆ; ಭೂಲೋಕದಿಂದ ಹುಟ್ಟಿದವನು ಭೂಲೋಕದವನಾಗಿದ್ದು ಭೂಲೋಕದ ಮಾತನ್ನು ಆಡುತ್ತಾನೆ; ಪರಲೋಕದಿಂದ ಬರುವವನು ಎಲ್ಲರ ಮೇಲೆ ಇದ್ದಾನೆ.",
			"32": "<sup>32</sup> ತಾನು ನೋಡಿ ಕೇಳಿದ್ದಕ್ಕೆ ಸಾಕ್ಷಿಕೊಡುತ್ತಾನೆ; ಆದರೆ ಆತನ ಸಾಕ್ಷಿಯನ್ನು ಯಾರೂ ಒಪ್ಪುವದಿಲ್ಲ.",
			"33": "<sup>33</sup> ಆತನ ಸಾಕ್ಷಿಯನ್ನು ಒಪ್ಪಿದವನು ದೇವರು ಸತ್ಯವಂತನೆಂಬ ಮಾತಿಗೆ ಮುದ್ರೆ ಹಾಕಿದವನಾಗಿದ್ದಾನೆ.",
			"34": "<sup>34</sup> ಹೇಗಂದರೆ ದೇವರು ಯಾರನ್ನು ಕಳುಹಿಸಿದ್ದಾನೋ ಆತನಿಗೆ ಆತ್ಮವರವನ್ನು ಅಳತೆಮಾಡದೆ ಅನುಗ್ರಹಿಸುವದರಿಂದ ಆತನು ದೇವರ ಮಾತುಗಳನ್ನೇ ಆಡುತ್ತಾನೆ.",
			"35": "<sup>35</sup> ತಂದೆಯಾದ ದೇವರು ತನ್ನ ಮಗನನ್ನು ಪ್ರೀತಿಸಿ ಎಲ್ಲವನ್ನೂ ಆತನ ಕೈಯಲ್ಲಿ ಕೊಟ್ಟಿದ್ದಾನೆ.",
			"36": "<sup>36</sup> ಆತನ ಮಗನನ್ನು ನಂಬುವವನಿಗೆ ನಿತ್ಯಜೀವ ಉಂಟು; ಮಗನಿಗೆ ಒಳಗಾಗದವನು ಜೀವವನ್ನು ಕಾಣುವದೇ ಇಲ್ಲ; ದೇವರ ಕೋಪವು ಅವನ ಮೇಲೆ ನೆಲೆಗೊಂಡಿರುವದು."
		},
		"4": {
			"1": "<sup>1</sup> ಯೇಸು ಯೋಹಾನನಿಗಿಂತ ಹೆಚ್ಚು ಶಿಷ್ಯರನ್ನು ಮಾಡಿಕೊಂಡು ಅವರಿಗೆ ದೀಕ್ಷಾಸ್ನಾನಮಾಡಿಸುತ್ತಾನೆಂಬ ಸುದ್ದಿಯನ್ನು ಫರಿಸಾಯರು ಕೇಳಿದರೆಂದು ಕರ್ತನಿಗೆ ತಿಳಿದಾಗ",
			"2": "<sup>2</sup> ಆತನು ಯೂದಾಯವನ್ನು ಬಿಟ್ಟು ಗಲಿಲಾಯಕ್ಕೆ ತಿರಿಗಿ ಹೋದನು. (ಆದರೂ ಸ್ನಾನ ಮಾಡಿಸುತ್ತಿದ್ದವನು ಯೇಸುವಲ್ಲ; ಆತನ ಶಿಷ್ಯರೇ ಮಾಡಿಸುತ್ತಿದ್ದರು.)",
			"4": "<sup>4</sup> ಆತನು ಸಮಾರ್ಯಸೀಮೆಯನ್ನು ಹಾದು ಹೋಗಬೇಕಾಯಿತು.",
			"5": "<sup>5</sup> ಹೋಗುವಾಗ ಸಮಾರ್ಯಸೀಮೆಗೆ ಸೇರಿದ ಸುಖರೆಂಬ ಊರಿಗೆ ಬಂದನು. ಅದು ಯಾಕೋಬನು ತನ್ನ ಮಗನಾದ ಯೋಸೇಫನಿಗೆ ಕೊಟ್ಟ ಭೂಮಿಯ ಹತ್ತಿರದಲ್ಲಿತ್ತು;",
			"6": "<sup>6</sup> ಅಲ್ಲಿ ಯಾಕೋಬನು ತೆಗೆಸಿದ ಬಾವಿಯಿತ್ತು. ಯೇಸು ದಾರಿನಡೆದು ದಣಿದು ಆ ಬಾವಿಯ ಬಳಿಯಲ್ಲಿ ಹಾಗೆ ಕೂತುಕೊಂಡನು; ಹೆಚ್ಚು ಕಡಿಮೆ ಮಧ್ಯಾಹ್ನವಾಗಿತ್ತು.",
			"7": "<sup>7</sup> ಆತನ ಶಿಷ್ಯರು ಊಟಕ್ಕೆ ಬೇಕಾದದ್ದನ್ನು ಕೊಂಡುಕೊಳ್ಳುವದಕ್ಕಾಗಿ ಊರೊಳಗೆ ಹೋಗಿರಲು ಸಮಾರ್ಯದವಳಾದ ಒಬ್ಬ ಹೆಂಗಸು ನೀರು ಸೇದುವದಕ್ಕೆ ಬಂದಳು. ಯೇಸು ಆಕೆಯನ್ನು - ಅಮ್ಮಾ, ನೀರು ಕುಡಿಯುವದಕ್ಕೆ ಕೊಡು ಎಂದು ಕೇಳಿದನು.",
			"9": "<sup>9</sup> ಯೆಹೂದ್ಯರಿಗೂ ಸಮಾರ್ಯರಿಗೂ ಬಳಕೆಯಿಲ್ಲದಿರಲಾಗಿ ಆ ಸಮಾರ್ಯದವಳು ಆತನಿಗೆ - ಯೆಹೂದ್ಯನಾದ ನೀನು ಸಮಾರ್ಯದವಳಾದ ನನ್ನನ್ನು ನೀರು ಬೇಡುವದು ಹೇಗೆ ಎಂದು ಹೇಳಿದಳು.",
			"10": "<sup>10</sup> ಅದಕ್ಕೆ ಯೇಸು - ದೇವರು ಕೊಡುವ ವರವೇನೆಂಬದೂ ಕುಡಿಯುವದಕ್ಕೆ ನೀರುಕೊಡು ಎಂದು ನಿನಗೆ ಹೇಳಿದವನು ಯಾರೆಂಬದೂ ನಿನಗೆ ತಿಳಿದಿದ್ದರೆ ನೀನು ಅವನನ್ನು ಬೇಡಿಕೊಳ್ಳುತ್ತಿದ್ದಿ, ಅವನು ನಿನಗೆ ಜೀವಕರವಾದ ನೀರನ್ನು ಕೊಡುತ್ತಿದ್ದನು ಅಂದನು.",
			"11": "<sup>11</sup> ಆ ಹೆಂಗಸು ಆತನಿಗೆ - ಅಯ್ಯಾ, ಸೇದುವದಕ್ಕೆ ನಿನ್ನಲ್ಲಿ ಏನೂ ಇಲ್ಲ, ಬಾವಿ ಆಳವಾಗಿದೆ; ಹೀಗಿರುವಾಗ ಆ ಜೀವಕರವಾದ ನೀರು ನಿನಗೆ ಎಲ್ಲಿಂದ ಬಂತು?",
			"12": "<sup>12</sup> ನಮ್ಮ ಹಿರಿಯನಾದ ಯಾಕೋಬನಿಗಿಂತ ನೀನು ದೊಡ್ಡವನೋ? ಅವನೇ ಈ ಬಾವಿಯನ್ನು ನಮಗೆ ಕೊಟ್ಟವನು; ಅವನೂ ಅವನ ಮಕ್ಕಳೂ ಅವನು ಸಾಕಿದೆಲ್ಲವೂ ಸಹಿತವಾಗಿ ಇದೇ ಬಾವಿಯ ನೀರು ಕುಡಿದರು ಅನ್ನಲು",
			"13": "<sup>13</sup> ಯೇಸು ಆಕೆಗೆ - ಈ ನೀರನ್ನು ಕುಡಿಯುವವರೆಲ್ಲರಿಗೆ ತಿರಿಗಿ ನೀರಡಿಕೆಯಾಗುವದು;",
			"14": "<sup>14</sup> ನಾನು ಕೊಡುವ ನೀರನ್ನು ಕುಡಿದವನಿಗೆ ಎಂದಿಗೂ ನೀರಡಿಕೆಯಾಗುವದಿಲ್ಲ; ನಾನು ಅವನಿಗೆ ಕೊಡುವ ನೀರು ಅವನಲ್ಲಿ ಉಕ್ಕುವ ಒರತೆಯಾಗಿದ್ದು ನಿತ್ಯಜೀವವನ್ನು ಉಂಟುಮಾಡುವದು ಎಂದು ಹೇಳಿದನು.",
			"15": "<sup>15</sup> ಆ ಹೆಂಗಸು - ಅಯ್ಯಾ, ನನಗೆ ಆ ನೀರನ್ನು ಕೊಡು; ಕೊಟ್ಟರೆ ಇನ್ನು ಮೇಲೆ ನನಗೆ ನೀರಡಿಕೆಯಾಗಲಿಕ್ಕಿಲ್ಲ; ನೀರು ಸೇದುವದಕ್ಕೆ ಇಷ್ಟು ದೂರ ಬರಬೇಕಾದದ್ದೂ ಇರುವದಿಲ್ಲ ಅನ್ನಲು",
			"16": "<sup>16</sup> ಯೇಸು ಅವಳಿಗೆ - ಹೋಗಿ ನಿನ್ನ ಗಂಡನನ್ನು ಇಲ್ಲಿಗೆ ಕರಕೊಂಡು ಬಾ ಎಂದು ಹೇಳಿದನು.",
			"17": "<sup>17</sup> ಅದಕ್ಕೆ ಆ ಹೆಂಗಸು - ನನಗೆ ಗಂಡನಿಲ್ಲ ಅಂದಳು. ಯೇಸು ಆಕೆಗೆ - ನನಗೆ ಗಂಡನಿಲ್ಲವೆಂದು ನೀನು ಹೇಳಿದ್ದು ಸರಿಯಾದ ಮಾತು;",
			"18": "<sup>18</sup> ನಿನಗೆ ಐದುಮಂದಿ ಗಂಡಂದಿರಿದ್ದರು, ಈಗ ಇರುವವನು ನಿನಗೆ ಗಂಡನಲ್ಲ; ನೀನು ಹೇಳಿದ್ದು ನಿಜವಾದ ಸಂಗತಿ ಅಂದನು.",
			"19": "<sup>19</sup> ಆ ಹೆಂಗಸು ಆತನಿಗೆ - ಅಯ್ಯಾ, ನೀನು ಪ್ರವಾದಿಯೆಂದು ನನಗೆ ಕಾಣುತ್ತದೆ.",
			"20": "<sup>20</sup> ನಮ್ಮ ಹಿರಿಯರು ಈ ಬೆಟ್ಟದಲ್ಲಿ ದೇವಾರಾಧನೆ ಮಾಡುತ್ತಿದ್ದರು; ಆದರೆ ನೀವು ಆರಾಧನೆಮಾಡತಕ್ಕ ಸ್ಥಳವು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಅದೆ ಅನ್ನುತ್ತೀರಿ ಎಂದು ಹೇಳಿದಾಗ",
			"21": "<sup>21</sup> ಯೇಸು ಆಕೆಗೆ - ಅಮ್ಮಾ, ನಾನು ಹೇಳುವ ಮಾತನ್ನು ನಂಬು; ಒಂದು ಕಾಲ ಬರುತ್ತದೆ, ಆ ಕಾಲದಲ್ಲಿ ನೀವು ತಂದೆಯನ್ನು ಆರಾಧಿಸಬೇಕಾದರೆ ಈ ಬೆಟ್ಟಕ್ಕೂ ಹೋಗುವದಿಲ್ಲ, ಯೆರೂಸಲೇಮಿಗೂ ಹೋಗುವದಿಲ್ಲ.",
			"22": "<sup>22</sup> ಬರಬೇಕಾದ ರಕ್ಷಣೆಯು ಯೆಹೂದ್ಯರೊಳಗಿಂದ ಬರುತ್ತದಷ್ಟೆ. ಆದದರಿಂದ ನಾವು ಅರಿತಿರುವದನ್ನೇ ಆರಾಧಿಸುವವರಾಗಿದ್ದೇವೆ; ನೀವು ಅರಿಯದೆ ಇರುವಂಥದನ್ನು ಆರಾಧಿಸುವವರು.",
			"23": "<sup>23</sup> ಅದಿರಲಿ; ಸತ್ಯಭಾವದಿಂದ ದೇವಾರಾಧನೆ ಮಾಡುವವರು ಆತ್ಮೀಯ ರೀತಿಯಲ್ಲಿ ಸತ್ಯಕ್ಕೆ ತಕ್ಕ ಹಾಗೆ ತಂದೆಯನ್ನು ಆರಾಧಿಸುವ ಕಾಲ ಬರುತ್ತದೆ; ಅದು ಈಗಲೇ ಬಂದಿದೆ; ತಂದೆಯು ತನ್ನನ್ನು ಆರಾಧಿಸುವವರು ಇಂಥವರೇ ಆಗಿರಬೇಕೆಂದು ಅಪೇಕ್ಷಿಸುತ್ತಾನಲ್ಲವೇ.",
			"24": "<sup>24</sup> ದೇವರು ಆತ್ಮಸ್ವರೂಪನು; ಆತನನ್ನು ಆರಾಧಿಸುವವರು ಆತ್ಮೀಯ ರೀತಿಯಲ್ಲಿ ಸತ್ಯಕ್ಕೆ ತಕ್ಕ ಹಾಗೆ ಆರಾಧಿಸಬೇಕು ಅಂದನು.",
			"25": "<sup>25</sup> ಆ ಹೆಂಗಸು ಆತನಿಗೆ - ಮೆಸ್ಸೀಯನು ಅಂದರೆ ಕ್ರಿಸ್ತನು ಬರುತ್ತಾನೆಂದು ಬಲ್ಲೆನು; ಆತನು ಬಂದ ಮೇಲೆ ನಮಗೆ ಎಲ್ಲಾ ತಿಳಿಸುವನು ಎಂದು ಹೇಳಲು",
			"26": "<sup>26</sup> ಯೇಸು ಆಕೆಗೆ - ನಿನ್ನ ಸಂಗಡ ಮಾತಾಡುತ್ತಿರುವ ನಾನೇ ಆ ಮೆಸ್ಸೀಯನು ಎಂದು ಹೇಳಿದನು.",
			"27": "<sup>27</sup> ಅಷ್ಟರೊಳಗೆ ಆತನ ಶಿಷ್ಯರು ಬಂದು ಆತನು ಒಬ್ಬ ಹೆಂಗಸಿನ ಸಂಗಡ ಮಾತಾಡುತ್ತಿರುವದನ್ನು ಕಂಡು ಆಶ್ಚರ್ಯಪಟ್ಟರು. ಆದರೂ - ನಿನಗೆ ಏನು ಬೇಕು? ಆಕೆಯ ಸಂಗಡ ಯಾಕೆ ಮಾತಾಡುತ್ತೀ? ಎಂದು ಒಬ್ಬರೂ ಕೇಳಲಿಲ್ಲ.",
			"28": "<sup>28</sup> ಆಗ ಆ ಹೆಂಗಸು ತನ್ನ ಕೊಡವನ್ನು ಅಲ್ಲೇ ಬಿಟ್ಟು ಊರೊಳಕ್ಕೆ ಹೋಗಿ ಜನರಿಗೆ -",
			"29": "<sup>29</sup> ಅಲ್ಲಿ ಒಬ್ಬನಿದ್ದಾನೆ, ನಾನು ಇದುವರೆಗೆ ಮಾಡಿದ್ದನ್ನೆಲ್ಲಾ ನನಗೆ ಹೇಳಿದನು; ಬಂದು ಅವನನ್ನು ನೋಡಿರಿ; ಬರತಕ್ಕ ಕ್ರಿಸ್ತನು ಅವನೇ ಏನೋ? ಎಂದು ಹೇಳಲು",
			"30": "<sup>30</sup> ಆತನ ಬಳಿಗೆ ಬರುವದಕ್ಕೆ ಊರೊಳಗಿಂದ ಹೊರಟರು.",
			"31": "<sup>31</sup> ಅಷ್ಟರೊಳಗೆ ಶಿಷ್ಯರು - ಗುರುವೇ, ಊಟಮಾಡು ಎಂದು ಆತನನ್ನು ಬೇಡಿಕೊಂಡರು.",
			"32": "<sup>32</sup> ಆದರೆ ಆತನು ಅವರಿಗೆ - ನಿಮಗೆ ತಿಳಿಯದಂಥ ಆಹಾರವು ನನಗುಂಟು ಎಂದು ಹೇಳಲು",
			"33": "<sup>33</sup> ಶಿಷ್ಯರು - ಆತನಿಗೆ ಯಾರಾದರೂ ಊಟಕ್ಕೆ ತಂದುಕೊಟ್ಟರೋ ಏನೋ ಎಂದು ತಮ್ಮ ತಮ್ಮೊಳಗೆ ಮಾತಾಡಿಕೊಂಡರು.",
			"34": "<sup>34</sup> ಯೇಸು ಅವರಿಗೆ - ನನ್ನನ್ನು ಕಳುಹಿಸಿದಾತನ ಚಿತ್ತದಂತೆ ಮಾಡಿ ಆತನ ಕೆಲಸವನ್ನು ಪೂರೈಸುವದೇ ನನ್ನ ಆಹಾರವು.",
			"35": "<sup>35</sup> ಇನ್ನು ನಾಲ್ಕು ತಿಂಗಳಿಗೆ ಸುಗ್ಗಿಬರುವದೆಂದು ನೀವು ಹೇಳುವದುಂಟಷ್ಟೆ. ನಿಮ್ಮ ಕಣ್ಣೆತ್ತಿ ಹೊಲಗಳನ್ನು ನೋಡಿರಿ; ಅವು ಬೆಳ್ಳಗಾಗಿ ಕೊಯ್ಲಿಗೆ ಬಂದವೆಂದು ನಿಮಗೆ ಹೇಳುತ್ತೇನೆ.",
			"36": "<sup>36</sup> ಕೊಯ್ಯುವವನಿಗೆ ಈಗಲೇ ಕೂಲಿ ದೊರೆಯುತ್ತದೆ; ಅವನು ನಿತ್ಯಜೀವಕ್ಕೆ ಫಲವನ್ನು ಕೂಡಿಸಿಡುತ್ತಾನೆ; ಹೀಗೆ ಬಿತ್ತುವವನಿಗೂ ಕೊಯ್ಯುವವನಿಗೂ ಕೂಡ ಸಂತೋಷವಾಗುವದು.",
			"37": "<sup>37</sup> ಬಿತ್ತುವವನೊಬ್ಬ, ಕೊಯ್ಯುವವನೊಬ್ಬ ಎಂಬ ಗಾದೆಯಂತೆ ಆಯಿತು.",
			"38": "<sup>38</sup> ನೀವು ಕಷ್ಟಮಾಡದಂಥ ಬೆಳೆಯನ್ನು ಕೊಯ್ಯುವದಕ್ಕೆ ನಾನು ನಿಮ್ಮನ್ನು ಕಳುಹಿಸಿದೆನು; ಬೇರೊಬ್ಬರು ಕಷ್ಟಮಾಡಿದ್ದಾರೆ; ನೀವು ನಡುವೆ ಅವರ ಕಷ್ಟದಲ್ಲಿ ಸೇರಿದ್ದೀರಿ ಎಂದು ಹೇಳಿದನು.",
			"39": "<sup>39</sup> ನಾನು ಮಾಡಿದ್ದನ್ನೆಲ್ಲಾ ಆತನು ನನಗೆ ಹೇಳಿದನೆಂಬದಾಗಿ ಸಾಕ್ಷಿಕೊಡುತ್ತಿರುವ ಆ ಹೆಂಗಸಿನ ಮಾತಿನ ಮೇಲೆ ಆ ಊರಿನ ಸಮಾರ್ಯರಲ್ಲಿ ಅನೇಕರು ಆತನನ್ನು ನಂಬುವವರಾದರು.",
			"40": "<sup>40</sup> ಆದ್ದರಿಂದ ಆ ಸಮಾರ್ಯರು ಆತನ ಬಳಿಗೆ ಬಂದು - ನೀನು ನಮ್ಮಲ್ಲಿ ಇರಬೇಕೆಂದು ಆತನನ್ನು ಬೇಡಿಕೊಳ್ಳಲು ಆತನು ಎರಡು ದಿವಸ ಅವರಲ್ಲೇ ಇದ್ದನು.",
			"41": "<sup>41</sup> ಹೆಚ್ಚು ಜನರು ಆತನ ವಾಕ್ಯವನ್ನು ಕೇಳಿ ನಂಬಿ",
			"42": "<sup>42</sup> ಆ ಹೆಂಗಸಿಗೆ - ನಾವು ಆತನನ್ನು ನಂಬಿರುವದು ಇನ್ನು ನಿನ್ನ ಮಾತಿನ ಮೇಲೆ ಅಲ್ಲ; ನಾವು ಕಿವಿಯಾರೆ ಕೇಳಿ ಈತನು ಲೋಕರಕ್ಷಕನೇ ಹೌದೆಂದು ತಿಳುಕೊಂಡಿದ್ದೇವೆ ಎಂದು ಹೇಳಿದರು.",
			"43": "<sup>43</sup> ಎರಡು ದಿವಸಗಳಾದ ಮೇಲೆ ಆತನು ಅಲ್ಲಿಂದ ಹೊರಟು ಗಲಿಲಾಯಕ್ಕೆ ಹೋದನು.",
			"44": "<sup>44</sup> ಯಾಕಂದರೆ ಪ್ರವಾದಿಗೆ ಸ್ವದೇಶದಲ್ಲಿ ಮರ್ಯಾದೆಯಿಲ್ಲವೆಂದು ಯೇಸು ತಾನೇ ಹೇಳಿದನು.",
			"45": "<sup>45</sup> ಗಲಿಲಾಯದವರು ತಾವೂ ಯೆರೂಸಲೇಮಿನ ಜಾತ್ರೆಗೆ ಹೋಗಿ ಆತನು ಅಲ್ಲಿ ಮಾಡಿದ್ದನ್ನೆಲ್ಲಾ ನೋಡಿದ್ದರಿಂದ ಆತನು ಗಲಿಲಾಯಕ್ಕೆ ಬಂದಾಗ ಆತನನ್ನು ಆದರದಿಂದ ಅಂಗೀಕರಿಸಿದರು.",
			"46": "<sup>46</sup> ಹೀಗಿರಲಾಗಿ ಯೇಸು, ತಾನು ನೀರನ್ನು ದ್ರಾಕ್ಷಾರಸವಾಗಿ ಮಾಡಿದ ಗಲಿಲಾಯದ ಕಾನಾ ಊರಿಗೆ ಮತ್ತೊಮ್ಮೆ ಬಂದನು. ಅಲ್ಲಿದ್ದಾಗ ಅರಮನೆಯ ಒಬ್ಬ ಪ್ರಧಾನನ ಮಗನು ಕಪೆರ್ನೌಮಿನಲ್ಲಿ ಅಸ್ವಸ್ಥನಾಗಿದ್ದನು.",
			"47": "<sup>47</sup> ಯೇಸು ಯೂದಾಯದಿಂದ ಗಲಿಲಾಯಕ್ಕೆ ಬಂದ ಸುದ್ದಿಯನ್ನು ಆ ಪ್ರಧಾನನು ಕೇಳಿದಾಗ ತನ್ನ ಮಗನು ಸಾಯುವ ಹಾಗಿದ್ದದರಿಂದ ಯೇಸುವಿನ ಬಳಿಗೆ ಹೋಗಿ - ನೀನು ಇಳಿದು ಬಂದು ನನ್ನ ಮಗನನ್ನು ಸ್ವಸ್ಥಮಾಡಬೇಕೆಂದು ಬೇಡಿಕೊಂಡನು;",
			"48": "<sup>48</sup> ಯೇಸು ಅವನಿಗೆ - ನೀವು ಸೂಚಕಕಾರ್ಯಗಳನ್ನೂ ಅದ್ಭುತಕಾರ್ಯಗಳನ್ನೂ ನೋಡದಿದ್ದರೆ ನಂಬುವದೇ ಇಲ್ಲವೆಂದು ಹೇಳಿದನು.",
			"49": "<sup>49</sup> ಆ ಪ್ರಧಾನನು - ಸ್ವಾಮೀ, ನನ್ನ ಮಗನು ಸಾಯುವದಕ್ಕೆ ಮುಂಚೆ ನೀನು ಬರಬೇಕು ಅನ್ನಲು",
			"50": "<sup>50</sup> ಯೇಸು ಅವನಿಗೆ - ನಿನ್ನ ಮಗನು ಬದುಕುತ್ತಾನೆ ಹೋಗು ಎಂದು ಉತ್ತರ ಕೊಟ್ಟನು. ಆ ಗೃಹಸ್ಥನು ಯೇಸು ತನಗೆ ಹೇಳಿದ ಮಾತನ್ನು ನಂಬಿ ಹೊರಟುಹೋದನು.",
			"51": "<sup>51</sup> ಅವನು ಇನ್ನೂ ಘಟ್ಟಾಯಿಳಿದು ಹೋಗುತ್ತಿರುವಲ್ಲಿ ಅವನ ಆಳುಗಳು ಅವನೆದುರಿಗೆ ಬಂದು - ನಿನ್ನ ಮಗನು ಬದುಕಿಕೊಂಡನು ಅಂದರು.",
			"52": "<sup>52</sup> ಎಷ್ಟು ಘಂಟೆಗೆ ಅವನಿಗೆ ರೋಗವು ಇಳಿತರವಾಯಿತೆಂದು ಅವನು ಅವರನ್ನು ಕೇಳಿದಾಗ ಅವರು - ನಿನ್ನೆ ಮಧ್ಯಾಹ್ನದ ಒಂದು ಗಂಟೆಗೆ ಜ್ವರವು ಅವನನ್ನು ಬಿಟ್ಟಿತು ಅಂದರು.",
			"53": "<sup>53</sup> ಆಗ ನಿನ್ನ ಮಗನು ಬದುಕುತ್ತಾನೆಂದು ಯೇಸು ತನಗೆ ಹೇಳಿದ ತಾಸಿನಲ್ಲಿಯೇ ಅದಾಯಿತೆಂದು ತಂದೆಯು ತಿಳುಕೊಂಡದರಿಂದ ಅವನೂ ಅವನ ಮನೆಯವರೆಲ್ಲರೂ ಆತನನ್ನು ನಂಬಿದರು.",
			"54": "<sup>54</sup> ಯೇಸು ಯೂದಾಯದಿಂದ ಗಲಿಲಾಯಕ್ಕೆ ಬಂದ ಮೇಲೆ ಈ ಎರಡನೆಯ ಸೂಚಕಕಾರ್ಯವನ್ನು ಮಾಡಿದನು."
		},
		"5": {
			"1": "<sup>1</sup> ಇದಾದ ಮೇಲೆ ಯೆಹೂದ್ಯರದೊಂದು ಜಾತ್ರೆ ಬಂದದರಿಂದ ಯೇಸು ಯೆರೂಸಲೇಮಿಗೆ ಹೋದನು.",
			"2": "<sup>2</sup> ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಕುರೀ ಅಗಸೇಬಾಗಲಿನ ಹತ್ತಿರದಲ್ಲಿ ಒಂದು ಕೊಳವಿದೆ; ಇದಕ್ಕೆ ಇಬ್ರಿಯ ಮಾತಿನಲ್ಲಿ ಬೇತ್ಸಥಾ ಎಂದು ಹೆಸರು.",
			"3": "<sup>3</sup> ಅಲ್ಲಿ ಐದು ಮಂಟಪಗಳಿವೆ; ಇವುಗಳಲ್ಲಿ ಬಹುಮಂದಿ ರೋಗಿಗಳೂ ಕುರುಡರೂ ಕುಂಟರೂ ಮೈಒಣಗಿದವರೂ ಬಿದ್ದುಕೊಂಡಿರುವರು.",
			"5": "<sup>5</sup> ಅಲ್ಲಿ ಮೂವತ್ತೆಂಟು ವರುಷದಿಂದ ರೋಗಿಯಾಗಿದ್ದ ಒಬ್ಬ ಮನುಷ್ಯನಿದ್ದನು.",
			"6": "<sup>6</sup> ಅವನು ಬಿದ್ದಿರುವದನ್ನು ಯೇಸು ಕಂಡು ಇವನು ಹೀಗಾಗಿ ಬಹು ಕಾಲವಾಗಿದೆ ಎಂದು ತಿಳಿದು - ನಿನಗೆ ಸ್ವಸ್ಥವಾಗಲಿಕ್ಕೆ ಮನಸ್ಸುಂಟೋ? ಎಂದು ಅವನನ್ನು ಕೇಳಿದ್ದಕ್ಕೆ",
			"7": "<sup>7</sup> ಆ ರೋಗಿಯು - ಸ್ವಾಮೀ, ನೀರು ಉಕ್ಕುವಾಗ ನನ್ನನ್ನು ಕೊಳದೊಳಗೆ ಇಳಿಸುವವರು ಒಬ್ಬರೂ ಇಲ್ಲ; ನಾನು ಬರುವದರೊಳಗೆ ನನಗಿಂತ ಮುಂದಾಗಿ ಮತ್ತೊಬ್ಬನು ಇಳಿಯುತ್ತಾನೆ ಅಂದನು.",
			"8": "<sup>8</sup> ಯೇಸು ಅವನಿಗೆ - ಎದ್ದು ನಿನ್ನ ಹಾಸಿಗೆಯನ್ನು ಹೊತ್ತುಕೊಂಡು ನಡೆ ಎಂದು ಹೇಳಿದನು.",
			"9": "<sup>9</sup> ಆ ಕ್ಷಣವೇ ಆ ಮನುಷ್ಯನು ಸ್ವಸ್ಥನಾಗಿ ತನ್ನ ಹಾಸಿಗೆಯನ್ನು ಹೊತ್ತುಕೊಂಡು ನಡೆದನು.",
			"10": "<sup>10</sup> ಆದರೆ ಆ ದಿವಸ ಸಬ್ಬತ್‍ದಿನವಾಗಿತ್ತು; ಹೀಗಿರುವದರಿಂದ ಯೆಹೂದ್ಯರು ಸ್ವಸ್ಥವಾದವನಿಗೆ - ಈ ಹೊತ್ತು ಸಬ್ಬತ್‍ದಿನವಾಗಿದೆ; ನೀನು ಹಾಸಿಗೆಯನ್ನು ಹೊರುವದು ಸರಿಯಲ್ಲವೆಂದು ಹೇಳಿದ್ದಕ್ಕೆ",
			"11": "<sup>11</sup> ಅವನು - ನನ್ನನ್ನು ಸ್ವಸ್ಥಮಾಡಿದವನೇ ನಿನ್ನ ಹಾಸಿಗೆಯನ್ನು ಹೊತ್ತುಕೊಂಡು ನಡೆ ಎಂದು ನನಗೆ ಹೇಳಿದನು ಅಂದನು.",
			"12": "<sup>12</sup> ಹೊತ್ತುಕೊಂಡು ನಡೆ ಎಂದು ನಿನಗೆ ಹೇಳಿದ ಮನುಷ್ಯನು ಯಾರೆಂದು ಅವನನ್ನು ಕೇಳಿದರು.",
			"13": "<sup>13</sup> ಆದರೆ ಅವನಾರೆಂಬದು ಸ್ವಸ್ಥವಾದವನಿಗೆ ತಿಳಿದಿರಲಿಲ್ಲ; ಆ ಸ್ಥಳದಲ್ಲಿ ಜನರ ಗುಂಪು ಇದ್ದದರಿಂದ ಯೇಸು ಸರಕೊಂಡುಹೋಗಿದ್ದನು.",
			"14": "<sup>14</sup> ಇದಾದ ಮೇಲೆ ಯೇಸು ಅವನನ್ನು ದೇವಾಲಯದಲ್ಲಿ ಕಂಡುಕೊಂಡು - ನಿನಗೆ ಸ್ವಸ್ಥವಾಯಿತಲ್ಲಾ; ಇನ್ನು ಮೇಲೆ ಪಾಪಮಾಡಬೇಡ; ನಿನಗೆ ಹೆಚ್ಚಿನ ಕೇಡು ಬಂದೀತು ಅಂದನು.",
			"15": "<sup>15</sup> ಆ ಮನುಷ್ಯನು ಅಲ್ಲಿಂದ ಹೋಗಿ - ನನ್ನನ್ನು ಸ್ವಸ್ಥಮಾಡಿದವನು ಯೇಸುವೇ ಎಂದು ಯೆಹೂದ್ಯರಿಗೆ ತಿಳಿಸಿದನು.",
			"16": "<sup>16</sup> ಆದಕಾರಣ ಯೆಹೂದ್ಯರು - ಯೇಸು ಸಬ್ಬತ್‍ದಿನದಲ್ಲಿ ಇಂಥ ಕೆಲಸಗಳನ್ನು ಮಾಡುತ್ತಾನಲ್ಲಾ ಎಂದು ಆತನನ್ನು ಹಿಂಸಿಸುವವರಾದರು.",
			"17": "<sup>17</sup> ಅದಕ್ಕೆ ಯೇಸು - ನನ್ನ ತಂದೆಯು ಇಂದಿನವರೆಗೂ ಕೆಲಸಮಾಡುತ್ತಾನೆ, ನಾನೂ ಕೆಲಸಮಾಡುತ್ತೇನೆ ಅಂದನು.",
			"18": "<sup>18</sup> ಈ ಮಾತನ್ನು ಹೇಳಿದ್ದರಿಂದ ಆತನು ಸಬ್ಬತ್‍ದಿನವನ್ನು ಅಲಕ್ಷ್ಯಮಾಡಿದ್ದಲ್ಲದೆ ದೇವರನ್ನು ತನ್ನ ತಂದೆ ಎಂದು ಹೇಳಿ ತನ್ನನ್ನು ದೇವರಿಗೆ ಸರಿಗಟ್ಟಿಕೊಂಡನೆಂದು ಯೆಹೂದ್ಯರು ಆತನನ್ನು ಕೊಲ್ಲುವದಕ್ಕೆ ಮತ್ತಷ್ಟು ಪ್ರಯತ್ನಮಾಡಿದರು.",
			"19": "<sup>19</sup> ಅದಕ್ಕೆ ಯೇಸು ಅವರಿಗೆ - ನಿಮಗೆ ನಿಜನಿಜವಾಗಿ ಹೇಳುತ್ತೇನೆ, ತಂದೆಯು ಮಾಡುವದನ್ನು ಕಂಡು ಮಗನು ಮಾಡುತ್ತಾನೆ ಹೊರತು ತನ್ನಷ್ಟಕ್ಕೆ ತಾನೇ ಏನೂ ಮಾಡಲಾರನು; ಆತನು ಮಾಡುವದನ್ನೆಲ್ಲಾ ಹಾಗೆಯೇ ಮಗನೂ ಮಾಡುತ್ತಾನೆ.",
			"20": "<sup>20</sup> ತಂದೆಯು ಮಗನ ಮೇಲೆ ಮಮತೆಯಿಟ್ಟು ತಾನು ಮಾಡುವವುಗಳನ್ನೆಲ್ಲಾ ಅವನಿಗೆ ತೋರಿಸುತ್ತಾನೆ. ಇದಲ್ಲದೆ ಇವುಗಳಿಗಿಂತ ದೊಡ್ಡ ಕೆಲಸಗಳನ್ನು ಅವನಿಗೆ ತೋರಿಸುವನು; ಅವುಗಳನ್ನು ನೋಡಿ ನಿಮಗೆ ಆಶ್ಚರ್ಯವಾಗುವದು.",
			"21": "<sup>21</sup> ತಂದೆಯು ಹೇಗೆ ಸತ್ತವರನ್ನು ಎಬ್ಬಿಸಿ ಬದುಕಿಸುತ್ತಾನೋ ಹಾಗೆಯೇ ಮಗನು ಸಹ ತನಗೆ ಬೇಕಾದವರನ್ನು ಬದುಕಿಸುತ್ತಾನೆಂದು ತಿಳುಕೊಳ್ಳಿರಿ.",
			"22": "<sup>22</sup> ಇದಲ್ಲದೆ ತಂದೆಯು ಯಾರಿಗೂ ತೀರ್ಪುಮಾಡುವದೂ ಇಲ್ಲ; ತೀರ್ಪುಮಾಡುವ ಅಧಿಕಾರವನ್ನೆಲ್ಲಾ ಮಗನಿಗೆ ಕೊಟ್ಟಿದ್ದಾನೆ;",
			"23": "<sup>23</sup> ಎಲ್ಲರು ತನಗೆ ಮಾನ ಕೊಡುವ ಪ್ರಕಾರವೇ ಮಗನಿಗೂ ಮಾನ ಕೊಡಬೇಕೆಂದು ತಂದೆಯು ಇಚ್ಫೈಸುತ್ತಾನೆ. ಮಗನಿಗೆ ಮಾನಕೊಡದವನು ಅವನನ್ನು ಕಳುಹಿಸಿದ ತಂದೆಗೂ ಮಾನಕೊಡದವನಾಗಿದ್ದಾನೆ.",
			"24": "<sup>24</sup> ನಿಮಗೆ ನಿಜನಿಜವಾಗಿ ಹೇಳುತ್ತೇನೆ, ನನ್ನ ವಾಕ್ಯವನ್ನು ಕೇಳಿ ನನ್ನನ್ನು ಕಳುಹಿಸಿದಾತನನ್ನು ನಂಬುವವನು ನಿತ್ಯಜೀವವನ್ನು ಹೊಂದಿದ್ದಾನೆ. ಅವನು ತೀರ್ಪಿಗೆ ಗುರಿಯಾಗುವದಿಲ್ಲ; ಮರಣದಿಂದ ಪಾರಾಗಿ ಜೀವಕ್ಕೆ ಸೇರಿದ್ದಾನೆ.",
			"25": "<sup>25</sup> ನಿಮಗೆ ನಿಜನಿಜವಾಗಿ ಹೇಳುತ್ತೇನೆ, ಸತ್ತವರು ದೇವಕುಮಾರನ ಧ್ವನಿಯನ್ನು ಕೇಳುವ ಕಾಲ ಬರುತ್ತದೆ, ಈಗಲೇ ಬಂದಿದೆ; ಕೇಳಿದವರು ಬದುಕುವರು.",
			"26": "<sup>26</sup> ತಂದೆಯು ತಾನು ಹೇಗೆ ಸ್ವತಃಜೀವವುಳ್ಳವನಾಗಿದ್ದಾನೋ ಹಾಗೆಯೇ ಮಗನೂ ಸ್ವತಃಜೀವವುಳ್ಳವನಾಗಿರುವಂತೆ ಮಾಡಿದನು.",
			"27": "<sup>27</sup> ಮತ್ತು ಮಗನು ಮನುಷ್ಯಕುಮಾರನಾಗಿರುವದರಿಂದ ತೀರ್ಪುಮಾಡುವ ಅಧಿಕಾರವನ್ನೂ ಅವನಿಗೆ ಕೊಟ್ಟನು.",
			"28": "<sup>28</sup> ಅದಕ್ಕೆ ಆಶ್ಚರ್ಯಪಡಬೇಡಿರಿ; ಸಮಾಧಿಗಳಲ್ಲಿರುವವರೆಲ್ಲರು ಆತನ ಧ್ವನಿಯನ್ನು ಕೇಳಿ",
			"29": "<sup>29</sup> ಎದ್ದು ಹೊರಗೆ ಬರುವ ಕಾಲ ಬರುತ್ತದೆ. ಒಳ್ಳೇದನ್ನು ಮಾಡಿದವರಿಗೆ ಜೀವಕ್ಕಾಗಿ ಪುನರುತ್ಥಾನವಾಗುವದು; ಕೆಟ್ಟದ್ದನ್ನು ನಡಿಸಿದವರಿಗೆ ತೀರ್ಪಿಗಾಗಿ ಪುನರುತ್ಥಾನವಾಗುವದು.",
			"30": "<sup>30</sup> ನನ್ನಷ್ಟಕ್ಕೆ ನಾನೇ ಏನೂ ಮಾಡಲಾರೆನು; ತಂದೆ ಹೇಳಿದ್ದನ್ನು ಕೇಳಿ ನ್ಯಾಯತೀರಿಸುತ್ತೇನೆ; ಮತ್ತು ಸ್ವಂತ ಚಿತ್ತ ನೆರವೇರಬೇಕೆಂದು ಅಪೇಕ್ಷಿಸದೆ ನನ್ನನ್ನು ಕಳುಹಿಸಿದಾತನ ಚಿತ್ತ ನೆರವೇರಬೇಕೆಂದು ಅಪೇಕ್ಷಿಸುವದರಿಂದ ನಾನು ಮಾಡುವ ತೀರ್ಪು ನ್ಯಾಯವಾಗಿದೆ.",
			"31": "<sup>31</sup> ನನ್ನ ವಿಷಯವಾಗಿ ನಾನೇ ಸಾಕ್ಷಿ ಹೇಳಿಕೊಂಡರೆ ನನ್ನ ಸಾಕ್ಷಿ ನಿಜವಾಗದು.",
			"32": "<sup>32</sup> ನನ್ನ ವಿಷಯವಾಗಿ ಸಾಕ್ಷಿಹೇಳುವವನು ಮತ್ತೊಬ್ಬನಿದ್ದಾನೆ; ಆತನು ನನ್ನ ವಿಷಯವಾಗಿ ಹೇಳುವ ಸಾಕ್ಷಿ ನಿಜವೆಂದು ಬಲ್ಲೆನು.",
			"33": "<sup>33</sup> ನೀವು ಯೋಹಾನನ ಬಳಿಯಲ್ಲಿ ಕೇಳಿಕೊಂಡು ಬರುವದಕ್ಕೆ ಕಳುಹಿಸಿದಿರಿ; ಅವನು ಸತ್ಯಕ್ಕೆ ಸಾಕ್ಷಿ ಹೇಳಿದನು.",
			"34": "<sup>34</sup> ನಾನಂತೂ ನನಗೆ ಬೇಕಾದ ಸಾಕ್ಷಿಯನ್ನು ಮನುಷ್ಯರಿಂದ ತಕ್ಕೊಳ್ಳುವದಿಲ್ಲ; ಆದರೂ ನಿಮಗೆ ರಕ್ಷಣೆ ಆಗಬೇಕೆಂದು ಇದನ್ನು ಹೇಳಿದ್ದೇನೆ.",
			"35": "<sup>35</sup> ಯೋಹಾನನು ಉರಿಯುವ ದೀಪದೋಪಾದಿಯಲ್ಲಿ ಪ್ರಕಾಶಿಸಿದನು. ಅವನು ಕೊಡುವ ಬೆಳಕಿನಲ್ಲಿ ಸ್ವಲ್ಪಕಾಲ ವಿನೋದಗೊಳ್ಳುವದಕ್ಕೆ ಮನಸ್ಸುಮಾಡಿದಿರಿ.",
			"36": "<sup>36</sup> ನನಗಂತೂ ಯೋಹಾನನ ಸಾಕ್ಷಿಗಿಂತ ಹೆಚ್ಚಿನ ಸಾಕ್ಷಿ ಉಂಟು; ಹೇಗಂದರೆ ಪೂರೈಸುವದಕ್ಕೆ ತಂದೆ ನನಗೆ ಕೊಟ್ಟಿರುವ ಕೆಲಸಗಳು, ಅಂದರೆ ನಾನು ಮಾಡುವ ಕೆಲಸಗಳೇ, ತಂದೆಯು ನನ್ನನ್ನು ಕಳುಹಿಸಿಕೊಟ್ಟನೆಂಬದಾಗಿ ನನ್ನ ವಿಷಯದಲ್ಲಿ ಸಾಕ್ಷಿಕೊಡುತ್ತವೆ.",
			"37": "<sup>37</sup> ಇದಲ್ಲದೆ ನನ್ನನ್ನು ಕಳುಹಿಸಿಕೊಟ್ಟ ತಂದೆಯು ನನ್ನ ವಿಷಯವಾಗಿ ಸಾಕ್ಷಿ ಹೇಳಿದ್ದಾನೆ. ನೀವು ಎಂದಾದರೂ ಆತನ ಧ್ವನಿಯನ್ನು ಕೇಳಿದ್ದೂ ಇಲ್ಲ, ಆತನ ರೂಪವನ್ನು ನೋಡಿದ್ದೂ ಇಲ್ಲ,",
			"38": "<sup>38</sup> ಆತನ ವಾಕ್ಯವು ನಿಮ್ಮಲ್ಲಿ ನೆಲೆಗೊಂಡದ್ದೂ ಇಲ್ಲ; ಆತನು ಕಳುಹಿಸಿಕೊಟ್ಟವನ ಮಾತನ್ನು ನೀವು ನಂಬದೆ ಇದ್ದೀರಷ್ಟೆ.",
			"39": "<sup>39</sup> ಶಾಸ್ತ್ರಗಳಿಂದ ನಿತ್ಯಜೀವವು ದೊರೆಯುತ್ತದೆಂದು ನೀವು ನೆನಸಿ ಅವುಗಳನ್ನು ವಿಚಾರಿಸುತ್ತೀರಲ್ಲಾ; ಅವುಗಳೂ ನನ್ನ ವಿಷಯದಲ್ಲಿ ಸಾಕ್ಷಿಕೊಡುವವುಗಳಾಗಿವೆ.",
			"40": "<sup>40</sup> ಆದರೂ ಜೀವ ಹೊಂದುವದಕ್ಕಾಗಿ ನನ್ನ ಬಳಿಗೆ ಬರುವದಕ್ಕೆ ನಿಮಗೆ ಮನಸ್ಸಿಲ್ಲ.",
			"41": "<sup>41</sup> ನಾನು ಮನುಷ್ಯರಿಂದಾಗುವ ಮಾನವನ್ನು ಅಂಗೀಕರಿಸುವದಿಲ್ಲ.",
			"42": "<sup>42</sup> ಆದರೆ ನಿಮ್ಮನ್ನು ನಾನು ಬಲ್ಲೆನು, ದೇವರಲ್ಲಿ ನಿಮಗೆ ಪ್ರೀತಿಯಿಲ್ಲವೆಂದು ನನಗೆ ತಿಳಿದದೆ.",
			"43": "<sup>43</sup> ನಾನು ನನ್ನ ತಂದೆಯ ಹೆಸರಿನ ಮೇಲೆ ಬಂದಿದ್ದೇನೆ; ನನ್ನನ್ನು ನೀವು ಒಪ್ಪಿಕೊಳ್ಳುವದಿಲ್ಲ; ಮತ್ತೊಬ್ಬನು ಸ್ವಂತ ಹೆಸರಿನ ಮೇಲೆ ಬಂದರೆ ಅವನನ್ನು ಒಪ್ಪಿಕೊಳ್ಳುವಿರಿ.",
			"44": "<sup>44</sup> ಒಬ್ಬನೇ ದೇವರಿಂದ ಬರುವಂಥ ಮಾನವನ್ನು ಅಪೇಕ್ಷಿಸದೆ ಒಬ್ಬರಿಂದೊಬ್ಬರು ಮಾನವನ್ನು ಅಂಗೀಕರಿಸುವವರಾದ ನೀವು ಹೇಗೆ ನಂಬೀರಿ?",
			"45": "<sup>45</sup> ನಾನು ತಂದೆಯ ಮುಂದೆ ನಿಮ್ಮ ಮೇಲೆ ತಪ್ಪುಹೊರಿಸುವೆನೆಂದು ನೆನಸಬೇಡಿರಿ; ನೀವು ಆಶ್ರಯಿಸಿಕೊಂಡಿರುವ ಮೋಶೆಯೇ ನಿಮ್ಮ ಮೇಲೆ ತಪ್ಪುಹೊರಿಸುವವನು.",
			"46": "<sup>46</sup> ಅವನು ನನ್ನ ವಿಷಯವಾಗಿ ಬರೆದನು; ಆದದರಿಂದ ನೀವು ಮೋಶೆಯ ಮಾತನ್ನು ನಂಬುವವರಾಗಿದ್ದರೆ ನನ್ನ ಮಾತನ್ನೂ ನಂಬುತ್ತಿದ್ದಿರಿ.",
			"47": "<sup>47</sup> ಅವನು ಬರೆದ ಮಾತುಗಳನ್ನು ನೀವು ನಂಬದಿದ್ದರೆ ನಾನು ಹೇಳುವ ಮಾತುಗಳನ್ನು ಹೇಗೆ ನಂಬೀರಿ? ಅಂದನು."
		},
		"6": {
			"1": "<sup>1</sup> ತರುವಾಯ ಯೇಸು ಗಲಿಲಾಯದ ಸಮುದ್ರದ ಆಚೆಗೆ ಹೋದನು; ಅದಕ್ಕೆ ತಿಬೇರಿಯದ ಸಮುದ್ರವೆಂತಲೂ ಹೇಳುತ್ತಾರೆ.",
			"2": "<sup>2</sup> ಬಹು ಜನರ ಗುಂಪು ರೋಗಿಗಳಲ್ಲಿ ಆತನು ನಡಿಸಿದ ಸೂಚಕಕಾರ್ಯಗಳನ್ನು ನೋಡಿದ್ದರಿಂದ ಆತನ ಹಿಂದೆ ಹೋಗುತ್ತಿತ್ತು.",
			"3": "<sup>3</sup> ಆದರೆ ಯೇಸು ಬೆಟ್ಟವನ್ನೇರಿ ಅಲ್ಲಿ ತನ್ನ ಶಿಷ್ಯರ ಸಂಗಡ ಕೂತುಕೊಂಡನು.",
			"4": "<sup>4</sup> ಆ ಕಾಲದಲ್ಲಿ ಯೆಹೂದ್ಯರ ಪಸ್ಕ ಹಬ್ಬವು ಹತ್ತಿರವಾಗಿತ್ತು.",
			"5": "<sup>5</sup> ಹೀಗಿರಲಾಗಿ ಯೇಸು ಕಣ್ಣೆತ್ತಿ ನೋಡಿದಾಗ ಬಹು ಜನರ ಗುಂಪು ತನ್ನ ಬಳಿಗೆ ಬರುವದನ್ನು ಕಂಡು - ಇವರ ಊಟಕ್ಕೆ ನಾವು ಎಲ್ಲಿಂದ ರೊಟ್ಟಿ ಕೊಂಡುತರೋಣ? ಎಂದು ಫಿಲಿಪ್ಪನನ್ನು ಕೇಳಿದನು.",
			"6": "<sup>6</sup> ತಾನು ಮಾಡಬೇಕೆಂದಿದ್ದದ್ದು ತನಗೆ ತಿಳಿದಿದ್ದರೂ ಅವನನ್ನು ಪರೀಕ್ಷಿಸುವದಕ್ಕೆ ಈ ಮಾತನ್ನು ಹೇಳಿದನು.",
			"7": "<sup>7</sup> ಅದಕ್ಕೆ ಫಿಲಿಪ್ಪನು - ಒಬ್ಬೊಬ್ಬನಿಗೆ ಸ್ವಲ್ಪ ಸ್ವಲ್ಪ ಸಿಕ್ಕಬೇಕಾದರೆ ಇನ್ನೂರು ಹಣದ ರೊಟ್ಟಿಯಾದರೂ ಸಾಲದು ಅಂದನು.",
			"8": "<sup>8</sup> ಆಗ ಆತನ ಶಿಷ್ಯರಲ್ಲಿ ಒಬ್ಬನಾಗಿರುವ ಸೀಮೋನ್‍ಪೇತ್ರನ ತಮ್ಮನಾದ ಅಂದ್ರೆಯನು ಆತನಿಗೆ -",
			"9": "<sup>9</sup> ಇಲ್ಲಿರುವ ಒಬ್ಬ ಹುಡುಗನ ಬಳಿಯಲ್ಲಿ ಐದು ಜವೆಗೋದಿಯ ರೊಟ್ಟಿಗಳೂ ಎರಡು ಮೀನುಗಳೂ ಅವೆ; ಆದರೆ ಇಷ್ಟು ಜನರಿಗೆ ಅವು ಯಾತಕ್ಕಾದಾವು?",
			"10": "<sup>10</sup> ಎಂದು ಹೇಳಲಾಗಿ ಯೇಸು - ಆ ಜನರನ್ನು ಊಟಕ್ಕೆ ಕೂಡ್ರಿಸಿರಿ ಅಂದನು. ಆ ಸ್ಥಳದಲ್ಲಿ ಬಹಳ ಹುಲ್ಲು ಬೆಳೆದಿತ್ತು; ಜನರು ಕೂತುಕೊಂಡರು. ಗಂಡಸರ ಸಂಖ್ಯೆ ಹೆಚ್ಚುಕಡಿಮೆ ಐದು ಸಾವಿರವಿತ್ತು.",
			"11": "<sup>11</sup> ತರುವಾಯ ಯೇಸು ಆ ರೊಟ್ಟಿಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ದೇವರ ಸ್ತೋತ್ರಮಾಡಿ ಕೂತುಕೊಂಡವರಿಗೆ ಹಂಚಿ ಕೊಟ್ಟನು; ಅದರಂತೆ ಮೀನುಗಳಲ್ಲಿಯೂ ಅವರಿಗೆ ಬೇಕಾದಷ್ಟು ಹಂಚಿಕೊಟ್ಟನು.",
			"12": "<sup>12</sup> ಅವರಿಗೆ ತೃಪ್ತಿಯಾದ ಮೇಲೆ ಯೇಸು ತನ್ನ ಶಿಷ್ಯರಿಗೆ - ಮಿಕ್ಕತುಂಡುಗಳನ್ನು ಕೂಡಿಸಿರಿ; ಇದರಲ್ಲಿ ಯಾವದೂ ವ್ಯರ್ಥವಾಗಬಾರದು ಎಂದು ಹೇಳಿದನು.",
			"13": "<sup>13</sup> ಅವರು ಕೂಡಿಸಲಾಗಿ ಆ ಐದು ಜವೆಗೋದಿಯ ರೊಟ್ಟಿಗಳಲ್ಲಿ ಜನರು ತಿಂದು ಮಿಕ್ಕತುಂಡುಗಳಿಂದ ಹನ್ನೆರಡು ಪುಟ್ಟಿ ತುಂಬಿದವು.",
			"14": "<sup>14</sup> ಆತನು ಮಾಡಿದ ಈ ಸೂಚಕಕಾರ್ಯವನ್ನು ಆ ಜನರು ನೋಡಿ - ಲೋಕಕ್ಕೆ ಬರಬೇಕಾದ ಪ್ರವಾದಿ ಈತನೇ ನಿಜ ಎಂದು ಹೇಳಿಕೊಂಡರು.",
			"15": "<sup>15</sup> ಆಗ ಯೇಸು - ಅವರು ಬಂದು ತನ್ನನ್ನು ಹಿಡುಕೊಂಡುಹೋಗಿ ಅರಸನನ್ನಾಗಿ ಮಾಡಬೇಕೆಂಬ ಯೋಚನೆಯಲ್ಲಿದ್ದಾರೆಂದು ತಿಳಿದುಕೊಂಡು ತಿರಿಗಿ ತಾನೊಬ್ಬನೇ ಒಂಟಿಗನಾಗಿ ಬೆಟ್ಟಕ್ಕೆ ಹೋಗಿಬಿಟ್ಟನು.",
			"16": "<sup>16</sup> ಸಂಜೆಯಾದಾಗ ಆತನ ಶಿಷ್ಯರು ಸಮುದ್ರಕ್ಕೆ ಇಳಿದು ದೋಣಿಯನ್ನು ಹತ್ತಿ",
			"17": "<sup>17</sup> ಸಮುದ್ರಮಾರ್ಗವಾಗಿ ಕಪೆರ್ನೌಮಿನ ಕಡೆಗೆ ಹೊರಟರು. ಆಗಲೇ ಕತ್ತಲಾಗಿತ್ತು ಮತ್ತು ಯೇಸು ಅವರ ಬಳಿಗೆ ಇನ್ನೂ ಬಂದಿರಲಿಲ್ಲ.",
			"18": "<sup>18</sup> ಇದಲ್ಲದೆ ಬಿರುಗಾಳಿ ಬೀಸುವದರಿಂದ ಸಮುದ್ರವು ಅಲ್ಲಕಲ್ಲೋಲವಾಗಿತ್ತು.",
			"19": "<sup>19</sup> ಹೀಗಿರಲಾಗಿ ಅವರು ಹುಟ್ಟುಹಾಕುತ್ತಾ ಹೆಚ್ಚು ಕಡಿಮೆ ಒಂದು ಹರದಾರಿ ದೂರ ಹೋದ ಬಳಿಕ ಯೇಸು ಸಮುದ್ರದ ಮೇಲೆ ನಡೆಯುತ್ತಾ ದೋಣಿಯ ಸಮೀಪಕ್ಕೆ ಬರುವದನ್ನು ನೋಡಿ ಅಂಜಿದರು.",
			"20": "<sup>20</sup> ಆದರೆ ಆತನು - ಅಂಜಬೇಡಿರಿ, ನಾನು, ಅಂದನು.",
			"21": "<sup>21</sup> ಆಗ ಆತನನ್ನು ದೋಣಿಯೊಳಗೆ ಕರಕೊಳ್ಳುವದಕ್ಕೆ ಮನಸ್ಸಾಗಿದ್ದರು; ಮತ್ತು ಆ ಕ್ಷಣವೇ ಆ ದೋಣಿಯು ಅವರು ಹೋಗಬೇಕೆಂದಿದ್ದ ಭೂಮಿಗೆ ಮುಟ್ಟಿತು.",
			"22": "<sup>22</sup> ಮರುದಿವಸ ಸಮುದ್ರದ ಆಚೇಕಡೆಯಲ್ಲಿ ನಿಂತುಕೊಂಡಿದ್ದ ಜನರು ಯೇಸುವನ್ನು ಹುಡುಕುತ್ತಿದ್ದರು; ಇಲ್ಲಿ ಒಂದೇ ಹಡಗು ಇದ್ದ ಹೊರತು ಬೇರೆ ದೋಣಿಯು ಇರಲಿಲ್ಲವೆಂದೂ ಶಿಷ್ಯರು ಮಾತ್ರ ಹೊರಟು ಹೋದದ್ದಲ್ಲದೆ ಯೇಸು ತನ್ನ ಶಿಷ್ಯರ ಸಂಗಡ ಆ ಹಡಗನ್ನು ಹತ್ತಲಿಲ್ಲವೆಂದೂ ಅವರಿಗೆ ತಿಳಿದಿತ್ತು.",
			"23": "<sup>23</sup> ಆದರೆ ಅಷ್ಟರೊಳಗೆ ಬೇರೆ ದೋಣಿಗಳು ತಿಬೇರಿಯದಿಂದ ಹೊರಟು ಸ್ವಾಮಿಯು ಸ್ತೋತ್ರಮಾಡಿ ಜನರಿಗೆ ರೊಟ್ಟೀ ಊಟಮಾಡಿಸಿದ ಸ್ಥಳದ ಸಮೀಪಕ್ಕೆ ಬಂದಿದ್ದವು.",
			"24": "<sup>24</sup> ಹೀಗಿರಲಾಗಿ ಯೇಸು ಇಲ್ಲಿ ಇಲ್ಲ, ಆತನ ಶಿಷ್ಯರೂ ಇಲ್ಲ ಎಂದು ಆ ಜನರು ನೋಡಿ ತಾವೇ ಆ ಸಣ್ಣ ದೋಣಿಗಳನ್ನು ಹತ್ತಿ ಯೇಸುವನ್ನು ಹುಡುಕುತ್ತಾ ಕಪೆರ್ನೌಮಿಗೆ ಬಂದರು.",
			"25": "<sup>25</sup> ಮತ್ತು ಸಮುದ್ರದ ಆಚೇಕಡೆಯಲ್ಲಿ ಆತನನ್ನು ಕಂಡುಕೊಂಡು - ಗುರುವೇ, ಯಾವಾಗ ಇಲ್ಲಿಗೆ ಬರೋಣವಾಯಿತು? ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ,",
			"26": "<sup>26</sup> ಯೇಸು ಅವರಿಗೆ - ನಿಮಗೆ ನಿಜನಿಜವಾಗಿ ಹೇಳುತ್ತೇನೆ, ನೀವು ನನ್ನನ್ನು ಹುಡುಕುವದು ಸೂಚಕಕಾರ್ಯಗಳನ್ನು ನೋಡಿದ್ದರಿಂದಲ್ಲ, ಆ ರೊಟ್ಟಿ ತಿಂದು ಹೊಟ್ಟೆ ತುಂಬಿಸಿಕೊಂಡಿದ್ದರಿಂದಲೇ.",
			"27": "<sup>27</sup> ದುಡಿಯಿರಿ; ಕೆಟ್ಟುಹೋಗುವ ಆಹಾರಕ್ಕಾಗಿ ದುಡಿಯಬೇಡಿರಿ, ನಿತ್ಯಜೀವಕ್ಕೆ ಉಳಿಯುವ ಆಹಾರಕ್ಕಾಗಿ ದುಡಿಯಿರಿ; ಇಂಥ ಆಹಾರವನ್ನು ಮನುಷ್ಯಕುಮಾರನು ನಿಮಗೆ ಕೊಡುವನು; ಇದಕ್ಕಾಗಿ ತಂದೆಯಾದ ದೇವರು ಆತನನ್ನು ನೇಮಿಸಿ ಮುದ್ರೆಹಾಕಿದ್ದಾನೆ ಅಂದನು.",
			"28": "<sup>28</sup> ಅವರು - ದೇವರಿಗೆ ಮೆಚ್ಚಿಕೆಯಾದ ಕೆಲಸಗಳನ್ನು ನಾವು ನಡಿಸಬೇಕಾದರೆ ಏನು ಮಾಡಬೇಕೆಂದು ಆತನನ್ನು ಕೇಳಿದ್ದಕ್ಕೆ",
			"29": "<sup>29</sup> ಯೇಸು - ದೇವರು ಮೆಚ್ಚುವ ಕೆಲಸ ಯಾವದಂದರೆ ಆತನು ಕಳುಹಿಸಿಕೊಟ್ಟವನನ್ನು ನೀವು ನಂಬುವದೇ ಅಂದನು.",
			"30": "<sup>30</sup> ಅವರು ಆತನನ್ನು - ಹಾಗಾದರೆ ನಾವು ನೋಡಿ ನಿನ್ನ ಮಾತನ್ನು ನಂಬುವಂತೆ ಏನು ಸೂಚಕಕಾರ್ಯ ಮಾಡುತ್ತೀ?",
			"31": "<sup>31</sup> ನಮ್ಮ ಹಿರಿಯರು ಅಡವಿಯಲ್ಲಿ ಮನ್ನಾ ತಿಂದರು; ಪರಲೋಕದಿಂದ ರೊಟ್ಟಿಯನ್ನು ಅವರಿಗೆ ತಿನ್ನುವದಕ್ಕೆ ಕೊಟ್ಟನು ಎಂದು ಶಾಸ್ತ್ರದಲ್ಲಿ ಬರೆದದೆಯಲ್ಲಾ, ನೀನು ಏನು ಕೆಲಸ ನಡಿಸುತ್ತೀ? ಎಂದು ಕೇಳಿದಾಗ",
			"32": "<sup>32</sup> ಯೇಸು ಅವರಿಗೆ - ನಿಮಗೆ ನಿಜನಿಜವಾಗಿ ಹೇಳುತ್ತೇನೆ, ಮೋಶೆಯು ನಿಮಗೆ ಪರಲೋಕದಿಂದ ರೊಟ್ಟಿ ಕೊಟ್ಟವನಲ್ಲ; ನನ್ನ ತಂದೆಯು ಪರಲೋಕದಿಂದ ಬರುವ ನಿಜವಾದ ರೊಟ್ಟಿಯನ್ನು ನಿಮಗೆ ಕೊಡುತ್ತಾನೆ.",
			"33": "<sup>33</sup> ದೇವರು ಕೊಡುವ ಆ ರೊಟ್ಟಿ ಯಾವದಂದರೆ ಪರಲೋಕದಿಂದ ಇಳಿದು ಬಂದು ಲೋಕಕ್ಕೆ ಜೀವವನ್ನು ಉಂಟುಮಾಡುವಂಥದೇ ಎಂದು ಹೇಳಿದನು.",
			"34": "<sup>34</sup> ಆಗ ಅವರು - ಸ್ವಾಮೀ, ಆ ರೊಟ್ಟಿಯನ್ನು ನಮಗೆ ಯಾವಾಗಲೂ ಕೊಡು ಅನ್ನಲು ಯೇಸು",
			"35": "<sup>35</sup> ಅವರಿಗೆ - ಜೀವಕೊಡುವ ರೊಟ್ಟಿ ನಾನೇ; ನನ್ನ ಬಳಿಗೆ ಬರುವವನಿಗೆ ಎಂದಿಗೂ ಹಸಿವೆಯಾಗುವದಿಲ್ಲ, ನನ್ನನ್ನು ನಂಬುವವನಿಗೆ ಎಂದಿಗೂ ನೀರಡಿಕೆಯಾಗುವದಿಲ್ಲ.",
			"36": "<sup>36</sup> ಆದರೆ ನೀವು ನನ್ನನ್ನು ನೋಡಿದ್ದರೂ ನಂಬದೆ ಇದ್ದೀರಿ ಎಂದು ನಿಮಗೆ ಹೇಳಿದೆನಲ್ಲವೇ.",
			"37": "<sup>37</sup> ತಂದೆಯು ನನಗೆ ಕೊಡುವಂಥವರೆಲ್ಲರು ನನ್ನ ಬಳಿಗೆ ಬರುವರು; ಮತ್ತು ನನ್ನ ಬಳಿಗೆ ಬರುವವನನ್ನು ನಾನು ತಳ್ಳಿಬಿಡುವದೇ ಇಲ್ಲ.",
			"38": "<sup>38</sup> ನನ್ನ ಚಿತ್ತದಂತೆ ನಡೆಯುವದಕ್ಕಾಗಿ ನಾನು ಬಂದಿಲ್ಲ, ನನ್ನನ್ನು ಕಳುಹಿಸಿದಾತನ ಚಿತ್ತವನ್ನೇ ನೆರವೇರಿಸುವದಕ್ಕೆ ಪರಲೋಕದಿಂದ ಬಂದೆನು. ನನ್ನನ್ನು ಕಳುಹಿಸಿದಾತನ ಚಿತ್ತವು ಏನಂದರೆ ಆತನು",
			"39": "<sup>39</sup> ನನಗೆ ಕೊಟ್ಟವರಲ್ಲಿ ಒಬ್ಬನನ್ನೂ ನಾನು ಕೆಡಗೊಡಿಸದೆ ಅವನನ್ನು ಕಡೇದಿನದಲ್ಲಿ ಎಬ್ಬಿಸಬೇಕೆಂಬದೇ. ಮಗನನ್ನು ನೋಡಿ ಆತನನ್ನು ನಂಬುವ ಪ್ರತಿಯೊಬ್ಬನಿಗೆ",
			"40": "<sup>40</sup> ನಿತ್ಯ ಜೀವವು ಸಿಕ್ಕಬೇಕೆಂಬದೇ ನನ್ನ ತಂದೆಯ ಚಿತ್ತವಾಗಿದೆ; ಮತ್ತು ನಾನು ಕಡೇದಿನದಲ್ಲಿ ಅವನನ್ನು ಎಬ್ಬಿಸುವೆನು ಎಂದು ಹೇಳಿದನು.",
			"41": "<sup>41</sup> ಆತನು - ಪರಲೋಕದಿಂದ ಇಳಿದು ಬಂದ ರೊಟ್ಟಿ ನಾನೇ ಎಂದು ಹೇಳಿದ್ದಕ್ಕೆ ಯೆಹೂದ್ಯರು ಆತನ ವಿಷಯವಾಗಿ ಗುಣುಗುಟ್ಟಿ - ಇವನು ಯೋಸೇಫನ ಮಗನಾದ ಯೇಸು ಅಲ್ಲವೇ.",
			"42": "<sup>42</sup> ಇವನ ತಂದೆತಾಯಿಗಳನ್ನು ನಾವು ಬಲ್ಲೆವಲ್ಲವೇ. ಈಗ ಇವನು ಪರಲೋಕದಿಂದ ಇಳಿದುಬಂದಿದ್ದೇನೆಂದು ಹೇಳುವದು ಹೇಗೆ? ಅಂದರು.",
			"43": "<sup>43</sup> ಅದಕ್ಕೆ ಯೇಸು ಅವರಿಗೆ - ನಿಮ್ಮ ನಿಮ್ಮೊಳಗೆ ಗುಣುಗುಟ್ಟಬೇಡಿರಿ.",
			"44": "<sup>44</sup> ನನ್ನನ್ನು ಕಳುಹಿಸಿಕೊಟ್ಟಂಥ ತಂದೆಯು ಎಳೆದ ಹೊರತು ಯಾವನೂ ನನ್ನ ಬಳಿಗೆ ಬರಲಾರನು; ಮತ್ತು ನಾನು ಅವನನ್ನು ಕಡೇದಿನದಲ್ಲಿ ಎಬ್ಬಿಸುವೆನು.",
			"45": "<sup>45</sup> ಅವರೆಲ್ಲರು ದೇವರಿಂದ ಶಿಕ್ಷಿತರಾಗಿರುವರು ಎಂದು ಪ್ರವಾದಿಗಳ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದದೆ; ತಂದೆಯಿಂದ ಕೇಳಿ ಕಲಿತವರೆಲ್ಲರು ನನ್ನ ಬಳಿಗೆ ಬರುತ್ತಾರೆ.",
			"46": "<sup>46</sup> ಯಾವನಾದರೂ ತಂದೆಯನ್ನು ನೋಡಿದ್ದಾನೆಂದು ತಿಳಿಯಬೇಡಿರಿ; ದೇವರ ಬಳಿಯಿಂದ ಬಂದವನೊಬ್ಬನೇ ತಂದೆಯನ್ನು ನೋಡಿದ್ದಾನೆ.",
			"47": "<sup>47</sup> ನಂಬಿರುವವನು ನಿತ್ಯಜೀವವನ್ನು ಹೊಂದಿದ್ದಾನೆಂದು ನಿಮಗೆ ನಿಜನಿಜವಾಗಿ ಹೇಳುತ್ತೇನೆ.",
			"48": "<sup>48</sup> ಜೀವ ಕೊಡುವ ರೊಟ್ಟಿ ನಾನೇ.",
			"49": "<sup>49</sup> ನಿಮ್ಮ ಹಿರಿಯರು ಅಡವಿಯಲ್ಲಿ ಮನ್ನಾ ತಿಂದರೂ ಸತ್ತುಹೋದರು;",
			"50": "<sup>50</sup> ಪರಲೋಕದಿಂದ ಇಳಿದುಬರುವ ರೊಟ್ಟಿ ಎಂಥದಂದರೆ ಅದನ್ನು ತಿಂದವನು ಸಾಯುವದಿಲ್ಲ.",
			"51": "<sup>51</sup> ಪರಲೋಕದಿಂದ ಇಳಿದುಬರುವ ಜೀವವುಳ್ಳ ರೊಟ್ಟಿಯು ನಾನೇ; ಈ ರೊಟ್ಟಿಯನ್ನು ಯಾವನಾದರೂ ತಿಂದರೆ ಅವನು ಸದಾಕಾಲ ಬದುಕುವನು. ಮತ್ತು ನಾನು ಕೊಡುವ ರೊಟ್ಟಿ ನನ್ನ ಮಾಂಸವೇ; ಅದನ್ನು ಲೋಕದ ಜೀವಕ್ಕೋಸ್ಕರ ಕೊಡುವೆನು ಎಂದು ಹೇಳಿದನು.",
			"52": "<sup>52</sup> ಆಗ ಯೆಹೂದ್ಯರು - ಇವನು ತನ್ನ ಮಾಂಸವನ್ನು ನಮಗೆ ಹೇಗೆ ತಿನ್ನುವದಕ್ಕೆ ಕೊಟ್ಟಾನು ಎಂದು ತಮ್ಮತಮ್ಮೊಳಗೆ ವಾಗ್ವಾದಮಾಡುತ್ತಿರಲು",
			"53": "<sup>53</sup> ಯೇಸು ಅವರಿಗೆ - ನಿಮಗೆ ನಿಜನಿಜವಾಗಿ ಹೇಳುತ್ತೇನೆ, ನೀವು ಮನುಷ್ಯಕುಮಾರನ ಮಾಂಸವನ್ನು ತಿನ್ನದೆ ಅವನ ರಕ್ತವನ್ನು ಕುಡಿಯದೆ ಹೋದರೆ ನಿಮ್ಮೊಳಗೆ ಜೀವವಿಲ್ಲ;",
			"54": "<sup>54</sup> ನನ್ನ ಮಾಂಸವನ್ನು ತಿಂದು ನನ್ನ ರಕ್ತವನ್ನು ಕುಡಿಯುವವನು ನಿತ್ಯಜೀವವನ್ನು ಹೊಂದಿದ್ದಾನೆ; ಮತ್ತು ನಾನು ಅವನನ್ನು ಕಡೇದಿನದಲ್ಲಿ ಎಬ್ಬಿಸುವೆನು.",
			"55": "<sup>55</sup> ಯಾಕಂದರೆ ನನ್ನ ಮಾಂಸವೇ ನಿಜವಾದ ಆಹಾರ, ನನ್ನ ರಕ್ತವೇ ನಿಜವಾದ ಪಾನ;",
			"56": "<sup>56</sup> ನನ್ನ ಮಾಂಸವನ್ನು ತಿಂದು ನನ್ನ ರಕ್ತವನ್ನು ಕುಡಿಯುವವನು ನನ್ನಲ್ಲಿ ಇರುತ್ತಾನೆ; ನಾನು ಅವನಲ್ಲಿ ಇರುತ್ತೇನೆ.",
			"57": "<sup>57</sup> ಜೀವ ಸ್ವರೂಪನಾದ ತಂದೆಯು ನನ್ನನ್ನು ಕಳುಹಿಸಿಕೊಟ್ಟಿರಲಾಗಿ ನಾನು ತಂದೆಯಿಂದ ಜೀವಿಸುತ್ತೇನೆ; ಅದರಂತೆ ನನ್ನನ್ನು ತಿನ್ನುವವನೇ ನನ್ನಿಂದ ಜೀವಿಸುವನು.",
			"58": "<sup>58</sup> ಪರಲೋಕದಿಂದ ಇಳಿದುಬಂದ ರೊಟ್ಟಿ ಇದೇ. ನಿಮ್ಮ ಹಿರಿಯರು ಮನ್ನಾ ತಿಂದರೂ ಸತ್ತರು; ಇದು ಹಾಗಲ್ಲ; ಈ ರೊಟ್ಟಿಯನ್ನು ತಿನ್ನುವವನು ಸದಾಕಾಲ ಜೀವಿಸುವನು ಎಂದು ಹೇಳಿದನು.",
			"59": "<sup>59</sup> ಕಪೆರ್ನೌಮಿನ ಸಭಾಮಂದಿರದಲ್ಲಿ ಉಪದೇಶ ಮಾಡುವಾಗ ಇದನ್ನು ಹೇಳಿದನು.",
			"60": "<sup>60</sup> ಆತನ ಶಿಷ್ಯರಲ್ಲಿ ಅನೇಕರು ಇದನ್ನು ಕೇಳಿ - ಇದು ಕಠಿನವಾದ ಮಾತು, ಇದನ್ನು ಯಾರು ಕೇಳಾರು? ಅಂದುಕೊಂಡರು.",
			"61": "<sup>61</sup> ಯೇಸು ತನ್ನ ಶಿಷ್ಯರು ಇದಕ್ಕೆ ಗುಣುಗುಟ್ಟುತ್ತಾರೆಂದು ತನ್ನಲ್ಲಿ ತಿಳುಕೊಂಡು ಅವರಿಗೆ - ಈ ಮಾತಿನಿಂದ ನಿಮಗೆ ಬೇಸರವಾಯಿತೋ?",
			"62": "<sup>62</sup> ಹಾಗಾದರೆ ಮನುಷ್ಯಕುಮಾರನು ತಾನು ಮೊದಲು ಇದ್ದಲ್ಲಿಗೆ ಏರಿಹೋಗುವದನ್ನು ನೀವು ನೋಡಿದರೆ ಏನನ್ನುವಿರಿ?",
			"63": "<sup>63</sup> ಬದುಕಿಸುವಂಥದು ಆತ್ಮವೇ; ಮಾಂಸವು ಯಾವದಕ್ಕೂ ಬರುವದಿಲ್ಲ. ನಾನು ನಿಮಗೆ ಹೇಳಿರುವ ಮಾತುಗಳೇ ಆತ್ಮವಾಗಿಯೂ ಜೀವವಾಗಿಯೂ ಅವೆ.",
			"64": "<sup>64</sup> ಆದರೆ ನಿಮ್ಮಲ್ಲಿ ಕೆಲವರು ನಂಬದವರಾಗಿದ್ದಾರೆ ಅಂದನು. ನಂಬದವರು ಇಂಥವರು, ತನ್ನನ್ನು ಹಿಡುಕೊಡುವವನು ಇಂಥವನು ಎಂಬದು ಯೇಸುವಿಗೆ ಮೊದಲಿನಿಂದ ತಿಳಿದಿತ್ತು.",
			"65": "<sup>65</sup> ಮತ್ತು ಆತನು - ತಂದೆಯ ಅನುಗ್ರಹವಿಲ್ಲದೆ ಯಾರೂ ನನ್ನ ಬಳಿಗೆ ಬರಲಾರರು ಎಂಬದನ್ನು ನಾನು ನಿಮಗೆ ಅದರ ದೆಸೆಯಿಂದಲೇ ಹೇಳಿದೆನು ಅಂದನು.",
			"66": "<sup>66</sup> ಅಂದಿನಿಂದ ಆತನ ಶಿಷ್ಯರಲ್ಲಿ ಅನೇಕರು ಹಿಂಜರಿದು ಆತನ ಕೂಡ ಸಂಚಾರಮಾಡುವದನ್ನು ಬಿಟ್ಟರು.",
			"67": "<sup>67</sup> ಯೇಸು ಇದನ್ನು ನೋಡಿ ತನ್ನ ಹನ್ನೆರಡು ಮಂದಿಯನ್ನು - ನೀವು ಸಹ ಹೋಗಬೇಕೆಂದಿದ್ದೀರಾ? ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ ಸೀಮೋನ್‍ಪೇತ್ರನು -",
			"68": "<sup>68</sup> ಸ್ವಾಮೀ, ನಿನ್ನನ್ನು ಬಿಟ್ಟು ನಾವು ಇನ್ನಾರ ಬಳಿಗೆ ಹೋಗೋಣ? ನಿನ್ನಲ್ಲಿ ನಿತ್ಯಜೀವವನ್ನು ಉಂಟುಮಾಡುವ ವಾಕ್ಯಗಳುಂಟು;",
			"69": "<sup>69</sup> ನೀನು ದೇವರು ಪ್ರತಿಷ್ಠಿಸಿದವನೇ ಎಂದು ನಾವು ನಂಬಿದ್ದೇವೆ ಮತ್ತು ತಿಳಿದಿದ್ದೇವೆ ಅಂದನು.",
			"70": "<sup>70</sup> ಅದಕ್ಕೆ ಯೇಸು - ಹನ್ನೆರಡು ಮಂದಿಯಾದ ನಿಮ್ಮನ್ನು ನಾನು ಆರಿಸಿ ತೆಗೆದುಕೊಂಡೆನಲ್ಲವೋ? ನಿಮ್ಮಲ್ಲಿಯೂ ಒಬ್ಬನು ಸೈತಾನನ ಮಗನಾಗಿದ್ದಾನೆ ಅಂದನು.",
			"71": "<sup>71</sup> ಈ ಮಾತಿನಿಂದ ಆತನು ಸೀಮೋನನ ಮಗನಾದ ಇಸ್ಕರಿಯೋತ ಯೂದನನ್ನು ಸೂಚಿಸಿದನು. ಇವನು ಆ ಹನ್ನೆರಡು ಮಂದಿಯಲ್ಲಿ ಒಬ್ಬನಾಗಿದ್ದು ಮುಂದೆ ಆತನನ್ನು ಹಿಡುಕೊಡುವವನಾದನು."
		},
		"7": {
			"1": "<sup>1</sup> ಈ ಸಂಗತಿಗಳಾದ ಮೇಲೆ ಯೇಸು ಗಲಿಲಾಯದಲ್ಲಿ ಸಂಚಾರಮಾಡುತ್ತಿದ್ದನು; ಯೆಹೂದ್ಯರು ಆತನನ್ನು ಕೊಲ್ಲುವ ಪ್ರಯತ್ನದಲ್ಲಿದ್ದದರಿಂದ ಆತನಿಗೆ ಯೂದಾಯದಲ್ಲಿ ಸಂಚಾರಮಾಡುವ ಮನಸ್ಸಿರಲಿಲ್ಲ.",
			"2": "<sup>2</sup> ಪರ್ಣಶಾಲೆಗಳ ಹಬ್ಬವೆಂಬ ಯೆಹೂದ್ಯರದೊಂದು ಜಾತ್ರೆಯು ಸಮೀಪವಾದಾಗ",
			"3": "<sup>3</sup> ಆತನ ತಮ್ಮಂದಿರು ಆತನಿಗೆ - ನೀನು ಮಾಡುವ ಕಾರ್ಯಗಳನ್ನು ನಿನ್ನ ಶಿಷ್ಯರು ಸಹ ನೋಡುವಂತೆ ಇಲ್ಲಿಂದ ಹೊರಟು ಯೂದಾಯಕ್ಕೆ ಹೋಗು;",
			"4": "<sup>4</sup> ಪ್ರಸಿದ್ಧಿಗೆ ಬರಬೇಕೆಂದಿರುವ ಯಾವನಾದರೂ ತಾನು ಮಾಡುವದನ್ನು ಮರೆಯಲ್ಲಿ ಮಾಡುವದಿಲ್ಲವಲ್ಲಾ; ನೀನು ಇಂಥ ಕಾರ್ಯಗಳನ್ನು ಮಾಡುವವನಾದ್ದರಿಂದ ಲೋಕಕ್ಕೆ ತಿಳಿಯಪಡಿಸಿಕೋ ಎಂದು ಹೇಳಿದರು.",
			"5": "<sup>5</sup> ಯಾಕಂದರೆ ಆತನ ಸಹೋದರರು ಸಹ ಆತನನ್ನು ನಂಬದೆ ಇದ್ದರು.",
			"6": "<sup>6</sup> ಯೇಸು ಅವರಿಗೆ - ನನ್ನ ಸಮಯವು ಇನ್ನೂ ಬಂದಿಲ್ಲ; ನಿಮ್ಮ ಸಮಯವು ಯಾವಾಗಲೂ ಸಿದ್ಧವಾಗಿದೆ.",
			"7": "<sup>7</sup> ಲೋಕವು ನಿಮ್ಮನ್ನು ಹಗೆಮಾಡಲಾರದು; ಆದರೆ ನಾನು ಅದರ ಕ್ರಿಯೆಗಳು ಕೆಟ್ಟವುಗಳೆಂದು ಸಾಕ್ಷಿಹೇಳುವದರಿಂದ ನನ್ನನ್ನು ಹಗೆಮಾಡುತ್ತದೆ.",
			"8": "<sup>8</sup> ನೀವೇ ಜಾತ್ರೆಗೆ ಹೋಗಿರಿ; ನನ್ನ ಸಮಯವು ಇನ್ನೂ ಒದಗದೆ ಇರುವದರಿಂದ ನಾನು ಈ ಜಾತ್ರೆಗೆ ಈಗ ಹೋಗುವದಿಲ್ಲವೆಂದು ಹೇಳಿದನು.",
			"9": "<sup>9</sup> ಇದನ್ನು ಹೇಳಿ ಗಲಿಲಾಯದಲ್ಲಿಯೇ ನಿಂತನು.",
			"10": "<sup>10</sup> ಆದರೆ ಆತನ ಸಹೋದರರು ಜಾತ್ರೆಗೆ ಹೋದ ಮೇಲೆ ಆತನು ಸಹ ಹೋದನು; ಜನರಿಗೆ ಕಾಣುವಂತೆ ಹೋಗದೆ ಮರೆಯಾಗಿ ಹೋದನು.",
			"11": "<sup>11</sup> ಜಾತ್ರೆಯಲ್ಲಿ ಯೆಹೂದ್ಯರು - ಅವನು ಎಲ್ಲಿದ್ದಾನೆಂದು ಆತನನ್ನು ಹುಡುಕುತ್ತಿದ್ದರು.",
			"12": "<sup>12</sup> ಮತ್ತು ಜನರ ಗುಂಪುಗಳಲ್ಲಿ ಆತನ ವಿಷಯವಾಗಿ ಬಹಳ ಗುಜುಗುಜು ಮಾತು ನಡೆಯುತ್ತಿತ್ತು. ಕೆಲವರು - ಆತನು ಒಳ್ಳೆಯವನು ಅಂದರು; ಇನ್ನು ಕೆಲವರು - ಅಲ್ಲ, ಜನರನ್ನು ತಪ್ಪಾದ ಮಾರ್ಗಕ್ಕೆ ಎಳೆಯುತ್ತಾನೆ ಅಂದರು.",
			"13": "<sup>13</sup> ಆದರೂ ಯೆಹೂದ್ಯರ ಭಯದಿಂದ ಯಾರೂ ಆತನ ವಿಷಯದಲ್ಲಿ ಧಾರಾಳವಾಗಿ ಮಾತಾಡಲಿಲ್ಲ.",
			"14": "<sup>14</sup> ಜಾತ್ರೆಯ ಮಧ್ಯಕಾಲದಲ್ಲಿ ಯೇಸು ದೇವಾಲಯಕ್ಕೆ ಹೋಗಿ ಬೋಧಿಸುತ್ತಿದ್ದನು.",
			"15": "<sup>15</sup> ಯೆಹೂದ್ಯರು ಅದಕ್ಕೆ ಆಶ್ಚರ್ಯಪಟ್ಟು - ವಿದ್ಯಾಭ್ಯಾಸ ಮಾಡದಿರುವ ಈತನಿಗೆ ಶಾಸ್ತ್ರಗಳು ತಿಳಿದಿರುವದು ಹೇಗೆ ಎಂದು ಹೇಳುತ್ತಿದ್ದರು.",
			"16": "<sup>16</sup> ಅದಕ್ಕೆ ಯೇಸು - ನಾನು ಹೇಳುವ ಬೋಧನೆಯು ನನ್ನದಲ್ಲ, ನನ್ನನ್ನು ಕಳುಹಿಸಿದಾತನದು.",
			"17": "<sup>17</sup> ಆತನ ಚಿತ್ತದಂತೆ ನಡೆಯುವದಕ್ಕೆ ಯಾರಿಗೆ ಮನಸ್ಸದೆಯೋ ಅವರಿಗೆ ಈ ಬೋಧನೆಯು ದೇವರಿಂದ ಬಂದದ್ದೋ ನಾನೇ ಕಲ್ಪಿಸಿ ಹೇಳಿದ್ದೋ ಗೊತ್ತಾಗುವದು.",
			"18": "<sup>18</sup> ಕಲ್ಪಿಸಿ ಹೇಳುವವನು ತನಗೆ ಮಾನ ಬರಬೇಕೆಂದು ಅಪೇಕ್ಷಿಸುತ್ತಾನೆ; ತನ್ನನ್ನು ಕಳುಹಿಸಿದವನಿಗೆ ಮಾನಬರಬೇಕೆಂದು ಅಪೇಕ್ಷಿಸುವವನು ಸತ್ಯವಂತನು, ಅಧರ್ಮವು ಅವನಲ್ಲಿ ಇಲ್ಲ.",
			"19": "<sup>19</sup> ಮೋಶೆಯು ನಿಮಗೆ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಕೊಟ್ಟಿದ್ದರೂ ನಿಮ್ಮಲ್ಲಿ ಒಬ್ಬನಾದರೂ ಧರ್ಮಶಾಸ್ತ್ರದಂತೆ ನಡೆಯುವದಿಲ್ಲವಲ್ಲಾ; ಯಾಕೆ ನನ್ನನ್ನು ಕೊಲ್ಲುವದಕ್ಕೆ ನೋಡುತ್ತೀರಿ? ಎಂದು ಹೇಳಿದನು.",
			"20": "<sup>20</sup> ಅದಕ್ಕೆ ಆ ಜನರು - ನಿನಗೆ ದೆವ್ವ ಹಿಡಿದದೆ; ಯಾರು ನಿನ್ನನ್ನು ಕೊಲ್ಲುವದಕ್ಕೆ ನೋಡುತ್ತಾರೆ? ಅಂದರು.",
			"21": "<sup>21</sup> ಯೇಸು ಅವರಿಗೆ - ಒಂದೇ ಕ್ರಿಯೆಯನ್ನು ಮಾಡಿದೆನು; ಅದಕ್ಕೆ ನೀವೆಲ್ಲರು ಬೆರಗಾಗಿದ್ದೀರಿ.",
			"22": "<sup>22</sup> ಮೋಶೆಯು ನಿಮಗೆ ಸುನ್ನತಿಯೆಂಬ ಸಂಸ್ಕಾರವನ್ನು ನೇಮಿಸಿದನು. ಅದು ಮೋಶೆಯಿಂದ ಬಂದದ್ದೆಂದು ನಾನು ಹೇಳುವದಿಲ್ಲ, ಮೂಲಪುರುಷರ ಕಾಲದಿಂದ ಬಂದದ್ದು. ಮತ್ತು ಆ ಸುನ್ನತಿಯನ್ನು ಸಬ್ಬತ್‍ದಿನದಲ್ಲಿಯೂ ಮಾಡುತ್ತೀರಿ.",
			"23": "<sup>23</sup> ಮೋಶೆಯ ನೇಮವನ್ನು ಮೀರಬಾರದೆಂದು ಒಬ್ಬನು ಸಬ್ಬತ್‍ದಿನದಲ್ಲಿ ಸುನ್ನತಿಮಾಡಿಸಿಕೊಳ್ಳುವದಾದರೆ ನಾನು ಸಬ್ಬತ್‍ದಿನದಲ್ಲಿ ಒಬ್ಬ ಮನುಷ್ಯನನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ಸ್ವಸ್ಥಮಾಡಿದ್ದಕ್ಕೆ ನನ್ನ ಮೇಲೆ ಕೋಪಗೊಳ್ಳುತ್ತೀರೋ?",
			"24": "<sup>24</sup> ಕಣ್ಣಿಗೆ ತೋರಿದ್ದರ ಮೇಲೆ ತೀರ್ಪುಮಾಡಬೇಡಿರಿ; ನ್ಯಾಯವಾದ ತೀರ್ಪುಮಾಡಿರಿ ಎಂದು ಹೇಳಿದನು.",
			"25": "<sup>25</sup> ಯೆರೂಸಲೇಮಿನವರಲ್ಲಿ ಕೆಲವರು - ಇವನನ್ನಲ್ಲವೇ ಅವರು ಕೊಲ್ಲಬೇಕೆಂದಿರುವದು?",
			"26": "<sup>26</sup> ನೋಡಿರಿ, ಇವನು ಧಾರಾಳವಾಗಿ ಮಾತಾಡುತ್ತಾನೆ, ಅವರು ಅವನಿಗೆ ಏನೂ ಹೇಳುವದಿಲ್ಲ. ಹಿರೀಸಭೆಯವರು ಇವನೇ ಬರಬೇಕಾದ ಕ್ರಿಸ್ತನೆಂದು ನಿಶ್ಚಯಮಾಡಿಕೊಂಡಿದ್ದಾರೇನೋ?",
			"27": "<sup>27</sup> ಆದರೂ ಇವನು ಎಲ್ಲಿಯವನೆಂದು ಬಲ್ಲೆವು; ಕ್ರಿಸ್ತನು ಬರುವಾಗ ಅವನು ಎಲ್ಲಿಯವನೋ ಒಬ್ಬರಿಗೂ ತಿಳಿಯುವದಿಲ್ಲವೆಂದು ಮಾತಾಡಿಕೊಳ್ಳುತ್ತಿದ್ದರು.",
			"28": "<sup>28</sup> ಯೇಸು ಇದನ್ನು ತಿಳಿದು ದೇವಾಲಯದಲ್ಲಿ ಉಪದೇಶ ಮಾಡುವಾಗ - ನನ್ನನ್ನು ಬಲ್ಲಿರಿ, ನಾನು ಎಲ್ಲಿಯವನೆಂದು ಬಲ್ಲಿರಿ ನಿಜವೇ. ಆದರೂ ನಾನು ನನ್ನಷ್ಟಕ್ಕೆ ಬಂದವನಲ್ಲ, ನನ್ನನ್ನು ಕಳುಹಿಸಿದಾತನು ನಿಜವಾದವನು; ನೀವು ಆತನನ್ನು ಅರಿತವರಲ್ಲ.",
			"29": "<sup>29</sup> ನಾನು ಆತನನ್ನು ಬಲ್ಲೆನು; ಆತನ ಕಡೆಯಿಂದ ಬಂದವನಾದದರಿಂದಲೂ ನನ್ನನ್ನು ಆತನೇ ಕಳುಹಿಸಿದ್ದರಿಂದಲೂ ಆತನನ್ನು ಬಲ್ಲೆನೆಂದು ಕೂಗಿ ಹೇಳಿದನು.",
			"30": "<sup>30</sup> ಇದಕ್ಕಾಗಿ ಆತನನ್ನು ಹಿಡಿಯುವದಕ್ಕೆ ನೋಡುತ್ತಿದ್ದರು; ಆದರೆ ಆತನ ಕಾಲ ಇನ್ನೂ ಬಾರದೆ ಇದ್ದಕಾರಣ ಯಾರೂ ಆತನ ಮೇಲೆ ಕೈಹಾಕಲಿಲ್ಲ.",
			"31": "<sup>31</sup> ಜನರಲ್ಲಿ ಅನೇಕರು ಆತನನ್ನು ನಂಬಿ - ಕ್ರಿಸ್ತನು ಬಂದಾಗ ಈತನು ಮಾಡಿದ್ದಕ್ಕಿಂತ ಹೆಚ್ಚು ಸೂಚಕಕಾರ್ಯಗಳನ್ನು ಮಾಡಾನೋ ಎಂದು ಹೇಳಿದರು.",
			"32": "<sup>32</sup> ಜನರು ಆತನ ವಿಷಯವಾಗಿ ಗುಜುಗುಜು ಆಡಿದ ಈ ಮಾತುಗಳನ್ನು ಫರಿಸಾಯರು ಕೇಳಲು ಮಹಾಯಾಜಕರೂ ಫರಿಸಾಯರೂ ಆತನನ್ನು ಹಿಡಿಯುವದಕ್ಕೆ ಓಲೇಕಾರರನ್ನು ಕಳುಹಿಸಿದರು.",
			"33": "<sup>33</sup> ಆಗ ಯೇಸು - ಇನ್ನು ಸ್ವಲ್ಪಕಾಲ ನಾನು ನಿಮ್ಮಲ್ಲಿ ಇದ್ದು ಆಮೇಲೆ ನನ್ನನ್ನು ಕಳುಹಿಸಿದಾತನ ಬಳಿಗೆ ಹೊರಟು ಹೋಗುತ್ತೇನೆ.",
			"34": "<sup>34</sup> ನೀವು ನನ್ನನ್ನು ಹುಡುಕುವಿರಿ, ಆದರೆ ನನ್ನನ್ನು ಕಂಡುಕೊಳ್ಳದೆ ಹೋಗುವಿರಿ, ನಾನು ಇರುವಲ್ಲಿಗೆ ನೀವು ಬರಲಾರಿರಿ ಅಂದನು.",
			"35": "<sup>35</sup> ಅದಕ್ಕೆ ಯೆಹೂದ್ಯರು - ಇವನು ನಮಗೆ ಸಿಕ್ಕದ ಹಾಗೆ ಎಲ್ಲಿ ಹೋಗಬೇಕೆಂದಿರುತ್ತಾನೆ? ಗ್ರೀಕರೊಳಗೆ ಚದರಿಕೊಂಡಿರುವ ನಮ್ಮವರ ಬಳಿಗೆ ಹೋಗಿ ಆ ಗ್ರೀಕರಿಗೆ ಉಪದೇಶ ಮಾಡಬೇಕೆಂದಿರುತ್ತಾನೋ?",
			"36": "<sup>36</sup> ನನ್ನನ್ನು ಹುಡುಕುವಿರಿ, ಆದರೆ ನನ್ನನ್ನು ಕಂಡುಕೊಳ್ಳದೆ ಹೋಗುವಿರಿ, ನಾನು ಇರುವಲ್ಲಿಗೆ ನೀವು ಬರಲಾರಿರಿ ಎಂದು ಅವನು ಹೇಳಿದ ಮಾತು ಏನಿದ್ದೀತು? ಎಂಬದಾಗಿ ತಮ್ಮತಮ್ಮೊಳಗೆ ಮಾತಾಡಿಕೊಂಡರು.",
			"37": "<sup>37</sup> ಆ ಜಾತ್ರೆಯ ಮಹಾದಿವಸವಾದ ಕಡೇ ದಿನದಲ್ಲಿ ಯೇಸು ನಿಂತುಕೊಂಡು - ಯಾವನಿಗಾದರೂ ನೀರಡಿಕೆಯಾಗಿದ್ದರೆ ಅವನು ನನ್ನ ಬಳಿಗೆ ಬಂದು ಕುಡಿಯಲಿ.",
			"38": "<sup>38</sup> ನನ್ನನ್ನು ನಂಬಿದವನ ಹೊಟ್ಟೆಯೊಳಗಿಂದ ಶಾಸ್ತ್ರದಲ್ಲಿ ಹೇಳಿರುವ ಪ್ರಕಾರ ಜೀವಕರವಾದ ನೀರಿನ ಹೊಳೆಗಳು ಹರಿಯುವವು ಎಂದು ಕೂಗಿ ಹೇಳಿದನು.",
			"39": "<sup>39</sup> ಇದನ್ನು ಯೇಸು ತನ್ನನ್ನು ನಂಬಿದವರು ಹೊಂದಲಿರುವ ಪವಿತ್ರಾತ್ಮವರವನ್ನು ಕುರಿತು ಹೇಳಿದನು; ಆತನು ಇನ್ನೂ ತನ್ನ ಮಹಿಮೆಯ ಪದವಿಯನ್ನು ಹೊಂದದೆ ಇದ್ದಕಾರಣ ಪವಿತ್ರಾತ್ಮವರವು ಇನ್ನೂ ಬಂದಿದ್ದಿಲ್ಲ.",
			"40": "<sup>40</sup> ಜನರಲ್ಲಿ ಕೆಲವರು ಆ ಮಾತುಗಳನ್ನು ಕೇಳಿ - ನಿಜವಾಗಿ ಈತನು ಬರಬೇಕಾದ ಪ್ರವಾದಿ ಅಂದರು;",
			"41": "<sup>41</sup> ಮತ್ತು ಕೆಲವರು - ಈತನು ಬರಬೇಕಾದ ಕ್ರಿಸ್ತನು ಅಂದರು.",
			"42": "<sup>42</sup> ಇನ್ನೂ ಕೆಲವರು - ಕ್ರಿಸ್ತನು ಗಲಿಲಾಯದಿಂದ ಬರುವದು ಹೇಗೆ? ದಾವೀದನ ಸಂತಾನದಿಂದಲೂ ದಾವೀದನು ಇದ್ದ ಬೇತ್ಲೆಹೇಮೆಂಬ ಗ್ರಾಮದಿಂದಲೂ ಕ್ರಿಸ್ತನು ಬರುವನು ಎಂಬದಾಗಿ ಶಾಸ್ತ್ರದಲ್ಲಿ ಉಂಟಲ್ಲವೇ ಎಂದು ಹೇಳಿದರು.",
			"43": "<sup>43</sup> ಹೀಗೆ ಜನರಲ್ಲಿ ಆತನನ್ನು ಕುರಿತು ಭೇದ ಉಂಟಾಯಿತು.",
			"44": "<sup>44</sup> ಅವರಲ್ಲಿ ಕೆಲವರು ಆತನನ್ನು ಹಿಡಿಯಬೇಕೆಂದಿದ್ದರೂ ಒಬ್ಬರೂ ಆತನ ಮೇಲೆ ಕೈಹಾಕಲಿಲ್ಲ.",
			"45": "<sup>45</sup> ಬಳಿಕ ಆ ಓಲೇಕಾರರು ಮಹಾಯಾಜಕರ ಮತ್ತು ಫರಿಸಾಯರ ಬಳಿಗೆ ಬಂದಾಗ ಇವರು - ನೀವು ಯಾಕೆ ಅವನನ್ನು ಹಿಡತರಲಿಲ್ಲ? ಎಂದು ಅವರನ್ನು ಕೇಳಿದ್ದಕ್ಕೆ",
			"46": "<sup>46</sup> ಆ ಓಲೇಕಾರರು - ಈ ಮನುಷ್ಯನು ಮಾತಾಡುವ ರೀತಿಯಲ್ಲಿ ಯಾರೂ ಎಂದೂ ಮಾತಾಡಿದ್ದಿಲ್ಲ ಎಂದು ಉತ್ತರಕೊಟ್ಟರು.",
			"47": "<sup>47</sup> ಅದಕ್ಕೆ ಫರಿಸಾಯರು - ನೀವೂ ಮರುಳಾದಿರಾ?",
			"48": "<sup>48</sup> ಹಿರೀಸಭೆಯವರಲ್ಲಿಯಾಗಲಿ ಫರಿಸಾಯರಲ್ಲಿಯಾಗಲಿ ಯಾರಾದರೂ ಅವನನ್ನು ನಂಬಿದ್ದಾರೋ?",
			"49": "<sup>49</sup> ಧರ್ಮಶಾಸ್ತ್ರವನ್ನರಿಯದಂಥ ಈ ಹಿಂಡು ಶಾಪಗ್ರಸ್ತವಾದದ್ದೇ ಅಂದರು.",
			"50": "<sup>50</sup> ಮುಂಚೆ ಯೇಸುವಿನ ಬಳಿಗೆ ಬಂದಿದ್ದ ನಿಕೊದೇಮನು ಅವರಲ್ಲಿ ಒಬ್ಬನಾಗಿದ್ದನು;",
			"51": "<sup>51</sup> ಅವನು ಅವರಿಗೆ - ನಮ್ಮ ಧರ್ಮಶಾಸ್ತ್ರವು ಮೊದಲು ಒಬ್ಬನನ್ನು ವಿಚಾರಿಸಿ ಅವನು ಮಾಡುವದೇನೆಂದು ತಿಳುಕೊಳ್ಳದೆ ಅವನ ವಿಷಯವಾಗಿ ತೀರ್ಪುಮಾಡುವದುಂಟೇ? ಎಂದು ಹೇಳಿದ್ದಕ್ಕೆ",
			"52": "<sup>52</sup> ಅವರು - ನೀನೂ ಗಲಿಲಾಯದವನೋ? ಗಲಿಲಾಯದಲ್ಲಿ ಪ್ರವಾದಿ ಹುಟ್ಟುವದೇ ಇಲ್ಲ, ವಿಚಾರಿಸಿ ನೋಡು ಅಂದರು.",
			"53": "<sup>53</sup> [ಎಲ್ಲರು ತಮ್ಮ ತಮ್ಮ ಮನೆಗೆ ಹೋದರು."
		},
		"8": {
			"1": "<sup>1</sup> ಯೇಸು ಎಣ್ಣೇಮರಗಳ ಗುಡ್ಡಕ್ಕೆ ಹೋದನು.",
			"2": "<sup>2</sup> ಬೆಳಿಗ್ಗೆ ಆತನು ತಿರಿಗಿ ದೇವಾಲಯಕ್ಕೆ ಬಂದಾಗ ಜನರೆಲ್ಲರು ಆತನ ಬಳಿಗೆ ಬಂದರು;",
			"3": "<sup>3</sup> ಆತನು ಕೂತುಕೊಂಡು ಅವರಿಗೆ ಉಪದೇಶಮಾಡುತ್ತಿರುವಲ್ಲಿ ಶಾಸ್ತ್ರಿಗಳೂ ಫರಿಸಾಯರೂ ವ್ಯಭಿಚಾರದಲ್ಲಿ ಸಿಕ್ಕಿದ ಒಬ್ಬ ಹೆಂಗಸನ್ನು ಆತನ ಬಳಿಗೆ ತಂದು ಆಕೆಯನ್ನು ನಡುವೆ ನಿಲ್ಲಿಸಿ -",
			"4": "<sup>4</sup> ಬೋಧಕನೇ, ಈ ಹೆಂಗಸು ವ್ಯಭಿಚಾರ ಮಾಡುತ್ತಿರುವಾಗಲೇ ಕೈಗೆ ಸಿಕ್ಕಿದಳು.",
			"5": "<sup>5</sup> ಇಂಥವರನ್ನು ಕಲ್ಲೆಸೆದು ಕೊಲ್ಲಬೇಕೆಂದು ಮೋಶೆಯು ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿ ನಮಗೆ ಅಪ್ಪಣೆಕೊಟ್ಟಿದ್ದಾನೆ; ಈಕೆಯ ವಿಷಯದಲ್ಲಿ ನೀನು ಏನು ಹೇಳುತ್ತೀ ಎಂದು ಆತನನ್ನು ಕೇಳಿದರು.",
			"6": "<sup>6</sup> ಆತನ ಮೇಲೆ ತಪ್ಪುಹೊರಿಸುವದಕ್ಕೆ ಏನಾದರೂ ಒಂದು ನಿಮಿತ್ತ ಬೇಕಾಗಿದ್ದದರಿಂದ ಈತನು ಏನನ್ನುತ್ತಾನೋ ನೋಡೋಣ ಎಂದು ಹಾಗೆ ಕೇಳಿದರು. ಆದರೆ ಯೇಸು ಬೊಗ್ಗಿಕೊಂಡು ಬೆರಳಿನಿಂದ ನೆಲದ ಮೇಲೆ ಬರೆಯುತ್ತಾ ಇದ್ದನು.",
			"7": "<sup>7</sup> ಅವರು ಆತನನ್ನು ಬಿಡದೆ ಕೇಳುತ್ತಿರುವಾಗ ಆತನು ನೆಟ್ಟಗೆ ಕೂತುಕೊಂಡು ಅವರಿಗೆ - ನಿಮ್ಮಲ್ಲಿ ಪಾಪವಿಲ್ಲದವನು ಯಾರೋ ಅವನೇ ಮೊದಲು ಆಕೆಯ ಮೇಲೆ ಕಲ್ಲನ್ನು ಹಾಕಲಿ ಎಂದು ಹೇಳಿ",
			"8": "<sup>8</sup> ತಿರಿಗಿ ಬೊಗ್ಗಿಕೊಂಡು ನೆಲದ ಮೇಲೆ ಬರೆಯುತ್ತಾ ಇದ್ದನು.",
			"9": "<sup>9</sup> ಆ ಮಾತನ್ನು ಕೇಳಿ ತಾವೇ ಪಾಪಿಗಳೆಂದು ಮನಸ್ಸಿನಲ್ಲಿ ತಿಳುಕೊಂಡು ಹಿರಿಯರು ಮೊದಲುಗೊಂಡು ಕಿರಿಯರವರೆಗೂ ಒಬ್ಬರ ಹಿಂದೊಬ್ಬರು ಹೊರಗೆ ಹೋದರು; ಯೇಸು ಒಬ್ಬನೇ ಉಳಿದನು, ಮತ್ತು ನಡುವೆ ನಿಂತಿದ್ದ ಹೆಂಗಸು ಅಲ್ಲೇ ಇದ್ದಳು.",
			"10": "<sup>10</sup> ಯೇಸು ನೆಟ್ಟಗೆ ಕೂತುಕೊಂಡು ಆ ಸ್ತ್ರೀಯನ್ನೇ ಹೊರತು ಯಾರನ್ನೂ ನೋಡದೆ ಆಕೆಗೆ - ಅಮ್ಮಾ, ನಿನ್ನ ಮೇಲೆ ತಪ್ಪುಹೊರಿಸುವವರು ಎಲ್ಲಿದ್ದಾರೆ? ನಿನಗೆ ಯಾರೂ ಶಿಕ್ಷೆಯನ್ನು ವಿಧಿಸಲಿಲ್ಲವೋ? ಅಂದನು. ಯಾರೂ ವಿಧಿಸಲಿಲ್ಲ, ಸ್ವಾಮೀ, ಅಂದಳು.",
			"11": "<sup>11</sup> ಯೇಸು ಆಕೆಗೆ - ನಾನೂ ನಿನಗೆ ಶಿಕ್ಷೆ ವಿಧಿಸುವದಿಲ್ಲ; ಹೋಗು, ಇನ್ನು ಮೇಲೆ ಪಾಪಮಾಡಬೇಡ ಎಂದು ಹೇಳಿದನು.]",
			"12": "<sup>12</sup> ಯೇಸು ತಿರಿಗಿ ಅವರ ಸಂಗಡ ಮಾತಾಡಲಾರಂಭಿಸಿ - ನಾನೇ ಲೋಕಕ್ಕೆ ಬೆಳಕು; ನನ್ನನ್ನು ಅನುಸರಿಸುವವನು ಕತ್ತಲೆಯಲ್ಲಿ ನಡೆಯದೆ ಜೀವಕೊಡುವ ಬೆಳಕನ್ನು ಹೊಂದಿದವನಾಗಿರುವನು ಎಂದು ಹೇಳಿದನು.",
			"13": "<sup>13</sup> ಇದನ್ನು ಕೇಳಿ ಫರಿಸಾಯರು ಆತನಿಗೆ - ನಿನ್ನ ವಿಷಯವಾಗಿ ನೀನೇ ಸಾಕ್ಷಿಹೇಳಿಕೊಳ್ಳುತ್ತೀ; ನಿನ್ನ ಸಾಕ್ಷಿ ನಿಜವಾಗದು ಎಂದು ಹೇಳಲು",
			"14": "<sup>14</sup> ಯೇಸು ಅವರಿಗೆ - ನಾನು ಎಲ್ಲಿಂದ ಬಂದವನಾಗಿಯೂ ಎಲ್ಲಿಗೆ ಹೋಗುವವನಾಗಿಯೂ ಇದ್ದೇನೆಂಬದು ನನಗೆ ಗೊತ್ತಿರುವದರಿಂದ ನನ್ನ ವಿಷಯವಾಗಿ ನಾನೇ ಸಾಕ್ಷಿಹೇಳಿಕೊಂಡರೂ ನನ್ನ ಸಾಕ್ಷಿ ನಿಜವಾಗಿರುವದು. ನೀವಾದರೋ ನಾನು ಎಲ್ಲಿಂದ ಬಂದವನು, ಎಲ್ಲಿಗೆ ಹೋಗುವವನು ಎಂಬದನ್ನು ತಿಳಿಯದವರು.",
			"15": "<sup>15</sup> ನೀವು ಹೊರಗಿನ ತೋರಿಕೆಯ ಪ್ರಕಾರ ತೀರ್ಪುಮಾಡುವವರು; ನಾನು ಯಾವನಿಗೂ ತೀರ್ಪುಮಾಡುವದಿಲ್ಲ.",
			"16": "<sup>16</sup> ತೀರ್ಪುಮಾಡಿದರೂ ನನ್ನ ತೀರ್ಪು ಸರಿಯಾದದ್ದೇ; ಯಾಕಂದರೆ ನಾನು ಒಂಟಿಗನಲ್ಲ; ನಾನೂ ನನ್ನನ್ನು ಕಳುಹಿಸಿಕೊಟ್ಟ ತಂದೆಯೂ ಇಬ್ಬರು ಇದ್ದೇವೆ.",
			"17": "<sup>17</sup> ಇಬ್ಬರ ಸಾಕ್ಷಿ ನಿಜವಾಗುತ್ತದೆಂಬದಾಗಿ ನಿಮ್ಮ ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲೇ ಬರೆದದೆ.",
			"18": "<sup>18</sup> ನನ್ನನ್ನು ಕುರಿತು ನಾನು ಸಾಕ್ಷಿ ಹೇಳುವವನಾಗಿದ್ದೇನೆ, ಅದಲ್ಲದೆ ನನ್ನನ್ನು ಕಳುಹಿಸಿದ ತಂದೆಯೂ ನನ್ನ ವಿಷಯವಾಗಿ ಸಾಕ್ಷಿಹೇಳುತ್ತಾನೆ ಅಂದನು.",
			"19": "<sup>19</sup> ಅವರು - ನಿನ್ನ ತಂದೆ ಎಲ್ಲಿದ್ದಾನೆ? ಎಂದು ಕೇಳಲು ಯೇಸು - ನೀವು ನನ್ನನ್ನೂ ತಿಳಿದಿಲ್ಲ, ನನ್ನ ತಂದೆಯನ್ನೂ ತಿಳಿದಿಲ್ಲ; ನನ್ನನ್ನು ತಿಳಿದಿದ್ದರೆ ನನ್ನ ತಂದೆಯನ್ನೂ ತಿಳಿದಿರುವಿರಿ ಅಂದನು.",
			"20": "<sup>20</sup> ಯೇಸು ದೇವಾಲಯದೊಳಗೆ ಕಾಣಿಕೆಯ ಪೆಟ್ಟಿಗೆಗಳು ಇರುವ ಅಂಗಳದಲ್ಲಿ ಉಪದೇಶಮಾಡುತ್ತಿರುವಾಗ ಈ ಮಾತುಗಳನ್ನು ಹೇಳಿದನು. ಆತನ ಕಾಲ ಇನ್ನೂ ಬಾರದೆ ಇದ್ದ ಕಾರಣ ಯಾರೂ ಆತನನ್ನು ಹಿಡಿಯಲಿಲ್ಲ.",
			"21": "<sup>21</sup> ಯೇಸು ತಿರಿಗಿ ಅವರಿಗೆ - ನಾನು ಹೋಗುತ್ತೇನೆ; ನೀವು ನನ್ನನ್ನು ಹುಡುಕುವಿರಿ; ಮತ್ತು ನಿಮ್ಮ ಪಾಪದಲ್ಲೇ ಸಾಯುವಿರಿ. ನಾನು ಹೋಗುವಲ್ಲಿಗೆ ನೀವು ಬರಲಾರಿರಿ ಅಂದನು.",
			"22": "<sup>22</sup> ಆ ಮಾತಿಗೆ ಯೆಹೂದ್ಯರು - ನಾನು ಹೋಗುವಲ್ಲಿಗೆ ನೀವು ಬರಲಾರಿರೆಂದು ಹೇಳುತ್ತಾನಲ್ಲಾ? ಇವನು ತನ್ನನ್ನು ತಾನೇ ಕೊಂದುಕೊಳ್ಳಬೇಕೆಂದಿದ್ದಾನೋ ಎಂದು ಮಾತಾಡಿಕೊಂಡರು.",
			"23": "<sup>23</sup> ಆತನು ಅವರಿಗೆ - ನೀವು ಕೆಳಗಿನವರು, ನಾನು ಮೇಲಿನವನು; ನೀವು ಈ ಲೋಕದವರು, ನಾನು ಈ ಲೋಕದವನಲ್ಲ.",
			"24": "<sup>24</sup> ಆದದರಿಂದ ನೀವು ನಿಮ್ಮ ಪಾಪಗಳಲ್ಲಿಯೇ ಸಾಯುವಿರೆಂದು ನಿಮಗೆ ಹೇಳಿದೆನು; ನನ್ನನ್ನು ಆತನೆಂದು ನೀವು ನಂಬದೆ ಹೋದರೆ ನಿಮ್ಮ ಪಾಪಗಳಲ್ಲಿಯೇ ಸಾಯುವಿರೆಂದು ಹೇಳಿದನು.",
			"25": "<sup>25</sup> ಆಗ ಅವರು - ನೀನು ಯಾರು? ಅಂದಾಗ ಯೇಸು - ನಾನು ನಿಮಗೆ ಹೇಳಿದಂಥದೇ.",
			"26": "<sup>26</sup> ನಿಮ್ಮ ವಿಷಯವಾಗಿ ಮಾತಾಡುವದಕ್ಕೂ ನಿಮ್ಮ ಮೇಲೆ ತೀರ್ಪುಮಾಡುವದಕ್ಕೂ ಅನೇಕ ಸಂಗತಿಗಳು ನನಗುಂಟು; ಆದರೆ ನನ್ನನ್ನು ಕಳುಹಿಸಿಕೊಟ್ಟಾತನು ಸತ್ಯವಂತನು; ಆತನಿಂದ ನಾನು ಕೇಳಿದವುಗಳನ್ನೇ ಲೋಕಕ್ಕೆ ಹೇಳುತ್ತೇನೆ ಅಂದನು.",
			"27": "<sup>27</sup> ಆತನು ತಂದೆಯನ್ನು ಸೂಚಿಸಿ ಇದನ್ನು ಹೇಳಿದನೆಂದು ಅವರಿಗೆ ಗೊತ್ತಾಗಲಿಲ್ಲ.",
			"28": "<sup>28</sup> ಹೀಗಿರಲಾಗಿ ಯೇಸು - ನೀವು ಮನುಷ್ಯಕುಮಾರನನ್ನು ಎತ್ತರದಲ್ಲಿಟ್ಟಾಗ ಇವನೇ ಆತನೆಂದೂ ತನ್ನಷ್ಟಕ್ಕೆ ತಾನೇ ಏನೂ ಮಾಡದೆ ತಂದೆಯು ತನಗೆ ಬೋಧಿಸಿದ ಹಾಗೆ ಅದನ್ನೆಲ್ಲಾ ಮಾತಾಡಿದನೆಂದೂ ನನ್ನ ವಿಷಯವಾಗಿ ನಿಮಗೆ ತಿಳಿಯುವದು.",
			"29": "<sup>29</sup> ನನ್ನನ್ನು ಕಳುಹಿಸಿಕೊಟ್ಟಾತನು ನನ್ನ ಸಂಗಡ ಇದ್ದಾನೆ, ನಾನು ಆತನಿಗೆ ಮೆಚ್ಚಿಕೆಯಾದದ್ದನ್ನು ಯಾವಾಗಲೂ ಮಾಡುವದರಿಂದ ಆತನು ನನ್ನನ್ನು ಒಂಟಿಗನಾಗಿ ಬಿಡಲಿಲ್ಲ ಅಂದನು.",
			"30": "<sup>30</sup> ಆತನು ಈ ಮಾತುಗಳನ್ನಾಡುವಾಗ ಅನೇಕರು ಆತನಲ್ಲಿ ನಂಬಿಕೆಯಿಟ್ಟರು.",
			"31": "<sup>31</sup> ಆಗ ಯೇಸು ತನ್ನನ್ನು ನಂಬಿದ ಯೆಹೂದ್ಯರಿಗೆ - ನೀವು ನನ್ನ ವಾಕ್ಯದಲ್ಲಿ ನೆಲೆಗೊಂಡವರಾದರೆ ನಿಜವಾಗಿ ನನ್ನ ಶಿಷ್ಯರಾಗಿದ್ದು ಸತ್ಯವನ್ನು ತಿಳಿದುಕೊಳ್ಳುವಿರಿ;",
			"32": "<sup>32</sup> ಮತ್ತು ಸತ್ಯವು ನಿಮ್ಮನ್ನು ಬಿಡುಗಡೆಮಾಡುವದು ಎಂದು ಹೇಳಿದನು.",
			"33": "<sup>33</sup> ಅದಕ್ಕವರು - ನಾವು ಅಬ್ರಹಾಮನ ಸಂತಾನದವರು; ನಾವು ಯಾರಿಗೂ ಎಂದೂ ದಾಸರಾಗಿಲ್ಲ; ನಿಮಗೆ ಬಿಡುಗಡೆಯಾಗುವದೆಂದು ನೀನು ಹೇಳುವದು ಹೇಗೆ? ಅಂದರು.",
			"34": "<sup>34</sup> ಅದಕ್ಕೆ ಯೇಸು - ನಿಮಗೆ ನಿಜನಿಜವಾಗಿ ಹೇಳುತ್ತೇನೆ, ಪಾಪಮಾಡುವವರೆಲ್ಲರು ಪಾಪಕ್ಕೆ ದಾಸರಾಗಿದ್ದಾರೆ.",
			"35": "<sup>35</sup> ದಾಸನು ಮನೆಯಲ್ಲಿ ಶಾಶ್ವತವಾಗಿ ಇರುವದಿಲ್ಲ; ಮಗನು ಶಾಶ್ವತವಾಗಿ ಇರುವನು;",
			"36": "<sup>36</sup> ಆದದರಿಂದ ಮಗನು ನಿಮ್ಮನ್ನು ಬಿಡುಗಡೆಮಾಡಿದರೆ ನಿಜವಾಗಿ ನಿಮಗೆ ಬಿಡುಗಡೆಯಾಗುವದು.",
			"37": "<sup>37</sup> ನೀವು ಅಬ್ರಹಾಮನ ಸಂತಾನದವರು ನಿಜ; ಆದರೂ ನನ್ನ ವಾಕ್ಯವು ನಿಮ್ಮಲ್ಲಿ ಸಾಗದೆ ಇರುವ ಕಾರಣ ನನ್ನನ್ನು ಕೊಲ್ಲುವದಕ್ಕೆ ನೋಡುತ್ತೀರಿ.",
			"38": "<sup>38</sup> ನಾನು ತಂದೆಯ ಬಳಿಯಲ್ಲಿ ನೋಡಿದ್ದನ್ನು ಹೇಳುತ್ತೇನೆ; ನೀವೂ ತಂದೆಯಿಂದ ಕೇಳಿದ್ದನ್ನು ಮಾಡಿರಿ ಅಂದನು.",
			"39": "<sup>39</sup> ಅದಕ್ಕವರು - ನಮ್ಮ ತಂದೆಯು ಅಬ್ರಹಾಮನೇ ಅಂದಾಗ ಯೇಸು ಅವರಿಗೆ - ನೀವು ಅಬ್ರಹಾಮನ ಮಕ್ಕಳಾಗಿದ್ದರೆ ಅಬ್ರಹಾಮನು ಮಾಡಿದಂಥ ಕೃತ್ಯಗಳನ್ನು ಮಾಡುತ್ತಿದ್ದಿರಿ.",
			"40": "<sup>40</sup> ನೀವು ಹಾಗೆ ಮಾಡದೆ ದೇವರಿಂದ ಕೇಳಿದ ಸತ್ಯವನ್ನು ನಿಮಗೆ ಹೇಳಿದವನಾದ ನನ್ನನ್ನು ಕೊಲ್ಲುವದಕ್ಕೆ ನೋಡುತ್ತೀರಿ; ಅಬ್ರಹಾಮನು ಹೀಗೆ ಮಾಡಲಿಲ್ಲ.",
			"41": "<sup>41</sup> ನೀವು ನಿಮ್ಮ ತಂದೆಯ ಕೃತ್ಯಗಳನ್ನು ಮಾಡುತ್ತೀರಿ ಅಂದನು. ಅದಕ್ಕವರು - ನಾವು ಹಾದರಕ್ಕೆ ಹುಟ್ಟಿದವರಲ್ಲ; ನಮಗೆ ಒಬ್ಬನೇ ತಂದೆ, ಆತನು ದೇವರೇ ಅಂದರು.",
			"42": "<sup>42</sup> ಯೇಸು ಅವರಿಗೆ - ದೇವರು ನಿಮ್ಮ ತಂದೆಯಾಗಿದ್ದರೆ, ನನ್ನನ್ನು ಪ್ರೀತಿಸುತ್ತಿದ್ದಿರಿ; ಯಾಕಂದರೆ ನಾನು ದೇವರಿಂದಲೇ ಹೊರಟು ಲೋಕಕ್ಕೆ ಬಂದವನಾಗಿದ್ದೇನೆ. ನನ್ನಷ್ಟಕ್ಕೆ ನಾನೇ ಬರಲಿಲ್ಲ; ಆತನೇ ನನ್ನನ್ನು ಕಳುಹಿಸಿದ್ದಾನೆ.",
			"43": "<sup>43</sup> ನೀವು ನನ್ನ ಮಾತನ್ನು ಗ್ರಹಿಸದೆ ಇರುವದಕ್ಕೆ ಕಾರಣವೇನು? ನನ್ನ ಬೋಧನೆಗೆ ಕಿವಿಗೊಡಲಾರದೆ ಇರುವದೇ ಕಾರಣ.",
			"44": "<sup>44</sup> ಸೈತಾನನು ನಿಮ್ಮ ತಂದೆ; ನೀವು ಆ ತಂದೆಯಿಂದ ಹುಟ್ಟಿದವರಾಗಿದ್ದು ನಿಮ್ಮ ತಂದೆಯ ದುರಿಚ್ಫೆಗಳನ್ನೇ ನಡಿಸಬೇಕೆಂದಿದ್ದೀರಿ. ಅವನು ಆದಿಯಿಂದಲೂ ಕೊಲೆಗಾರನಾಗಿದ್ದು ಸತ್ಯದಲ್ಲಿ ನಿಲ್ಲಲಿಲ್ಲ; ಅವನಲ್ಲಿ ಸತ್ಯವು ಇಲ್ಲವೇ ಇಲ್ಲ. ಅವನು ಸುಳ್ಳಾಡುವಾಗ ಸ್ವಭಾವಾನುಸಾರವಾಗಿ ಆಡುತ್ತಾನೆ; ಅವನು ಸುಳ್ಳುಗಾರನೂ ಸುಳ್ಳಿಗೆ ಮೂಲಪುರುಷನೂ ಆಗಿದ್ದಾನೆ.",
			"45": "<sup>45</sup> ನಾನು ಸತ್ಯವನ್ನು ಹೇಳುವವನಾದದರಿಂದ ನೀವು ನನ್ನನ್ನು ನಂಬುವದಿಲ್ಲ.",
			"46": "<sup>46</sup> ನನ್ನಲ್ಲಿ ಪಾಪವನ್ನು ತೋರಿಸಿಕೊಡುವವರು ನಿಮ್ಮೊಳಗೆ ಯಾರಿದ್ದಾರೆ? ನಾನು ಸತ್ಯವನ್ನು ಹೇಳುವವನಾಗಿರುವಲ್ಲಿ ನೀವು ನನ್ನ ಮಾತನ್ನು ನಂಬದೆ ಇರುವದು ಯಾಕೆ?",
			"47": "<sup>47</sup> ದೇವರಿಂದ ಹುಟ್ಟಿದವನು ದೇವರ ಮಾತುಗಳನ್ನು ಕೇಳುತ್ತಾನೆ. ನೀವು ದೇವರಿಂದ ಹುಟ್ಟದ ಕಾರಣ ಅವುಗಳನ್ನು ಕೇಳುವದಿಲ್ಲ ಎಂದು ಹೇಳಿದನು.",
			"48": "<sup>48</sup> ಅದಕ್ಕೆ ಯೆಹೂದ್ಯರು - ನೀನು ಸಮಾರ್ಯನೂ ದೆವ್ವಹಿಡಿದವನೂ ಆಗಿದ್ದೀ ಎಂದು ನಾವು ಹೇಳುವದು ಸರಿಯಲ್ಲವೋ ಎಂದು ಹೇಳಲು",
			"49": "<sup>49</sup> ಯೇಸು - ನಾನು ದೆವ್ವಹಿಡಿದವನಲ್ಲ, ನನ್ನ ತಂದೆಯನ್ನು ಸನ್ಮಾನಿಸುವವನಾಗಿದ್ದೇನೆ; ಆದರೂ ನೀವು ನನ್ನನ್ನು ಅವಮಾನಪಡಿಸುತ್ತೀರಿ.",
			"50": "<sup>50</sup> ನನಗೆ ಮಾನಬರಬೇಕೆಂದು ನಾನು ಅಪೇಕ್ಷಿಸುವದಿಲ್ಲ; ನನಗೆ ಮಾನಬರಬೇಕೆಂದು ಅಪೇಕ್ಷಿಸುವವನು ಒಬ್ಬನಿದ್ದಾನೆ; ಆತನೇ ನ್ಯಾಯತೀರಿಸುವನು.",
			"51": "<sup>51</sup> ನಿಮಗೆ ನಿಜನಿಜವಾಗಿ ಹೇಳುತ್ತೇನೆ, ಯಾವನಾದರೂ ನನ್ನ ವಾಕ್ಯವನ್ನು ಕೈಕೊಂಡು ನಡೆದರೆ ಅವನು ಎಂದಿಗೂ ಸಾವನ್ನು ಕಾಣುವದಿಲ್ಲ ಅಂದನು.",
			"52": "<sup>52</sup> ಅದಕ್ಕೆ ಯೆಹೂದ್ಯರು - ನೀನು ದೆವ್ವಹಿಡಿದವನೆಂದು ಈಗ ನಮಗೆ ತಿಳಿಯಿತು; ಅಬ್ರಹಾಮನು ಸತ್ತನು. ಪ್ರವಾದಿಗಳೂ ಸತ್ತರು; ಆದರೆ ನೀನು - ನನ್ನ ವಾಕ್ಯವನ್ನು ಕೈಕೊಂಡು ನಡೆಯುವವನು ಎಂದಿಗೂ ಸಾವನ್ನು ಅನುಭವಿಸುವದಿಲ್ಲವೆಂದು ಹೇಳುತ್ತೀ.",
			"53": "<sup>53</sup> ನಮ್ಮ ಮೂಲಪುರುಷನಾದ ಅಬ್ರಹಾಮನಿಗಿಂತ ನೀನು ದೊಡ್ಡವನೋ? ಅವನೂ ಸತ್ತುಹೋದನು, ಪ್ರವಾದಿಗಳೂ ಸತ್ತುಹೋದರು; ನಿನ್ನನ್ನು ಯಾರೆಂತ ಮಾಡಿಕೊಳ್ಳುತ್ತೀ ಎಂದು ಹೇಳಲು",
			"54": "<sup>54</sup> ಯೇಸು - ನನ್ನನ್ನು ನಾನೇ ಮಹಿಮೆಪಡಿಸಿಕೊಂಡರೆ ನನ್ನ ಮಹಿಮೆ ಹುರುಳಿಲ್ಲದ್ದು; ನನ್ನನ್ನು ಮಹಿಮೆಪಡಿಸುವವನು ನನ್ನ ತಂದೆಯೇ. ನೀವು ಆತನನ್ನು ನಮ್ಮ ದೇವರು ಅನ್ನುತ್ತೀರಿ; ಆದರೂ ನೀವು ಆತನನ್ನು ತಿಳಿಯದೆ ಇದ್ದೀರಿ.",
			"55": "<sup>55</sup> ನಾನಂತು ಆತನನ್ನು ಬಲ್ಲೆನು; ಆತನನ್ನು ಅರಿಯೆನೆಂದು ಹೇಳಿದರೆ ನಿಮ್ಮ ಹಾಗೆ ಸುಳ್ಳುಗಾರನಾಗುವೆನು; ಆದರೆ ಆತನನ್ನು ತಿಳಿದಿದ್ದೇನೆ, ಆತನ ವಾಕ್ಯವನ್ನು ಕೈಕೊಂಡು ನಡೆಯುತ್ತೇನೆ.",
			"56": "<sup>56</sup> ನಿಮ್ಮ ಮೂಲಪುರುಷನಾದ ಅಬ್ರಹಾಮನು ನನ್ನ ದಿನವನ್ನು ತಾನು ನೋಡೇನೆಂದು ಉಲ್ಲಾಸಪಟ್ಟನು; ಅದನ್ನು ನೋಡಿ ಸಂತೋಷಗೊಂಡನು ಅಂದನು.",
			"57": "<sup>57</sup> ಅದಕ್ಕೆ ಯೆಹೂದ್ಯರು - ನಿನಗೆ ಇನ್ನೂ ಐವತ್ತು ವರುಷವಾಗಿಲ್ಲ; ಅಬ್ರಹಾಮನನ್ನು ನೋಡಿದ್ದೀಯಾ? ಅಂದಾಗ",
			"58": "<sup>58</sup> ಯೇಸು - ನಿಮಗೆ ನಿಜನಿಜವಾಗಿ ಹೇಳುತ್ತೇನೆ, ಅಬ್ರಹಾಮನು ಹುಟ್ಟುವದಕ್ಕಿಂತ ಮುಂಚಿನಿಂದಲೂ ನಾನು ಇದ್ದೇನೆ ಅಂದನು.",
			"59": "<sup>59</sup> ಅವರು ಈ ಮಾತನ್ನು ಕೇಳಿ ಆತನನ್ನು ಕೊಲ್ಲುವದಕ್ಕಾಗಿ ಕಲ್ಲು ತಕ್ಕೊಳ್ಳಲು ಯೇಸು ಅಡಗಿಕೊಂಡು ದೇವಾಲಯದೊಳಗಿಂದ ಹೊರಟುಹೋದನು."
		},
		"9": {
			"1": "<sup>1</sup> ಯೇಸು ಹಾದುಹೋಗುತ್ತಿರುವಾಗ ಒಬ್ಬ ಹುಟ್ಟುಕುರುಡನನ್ನು ಕಂಡನು.",
			"2": "<sup>2</sup> ಆತನ ಶಿಷ್ಯರು - ಗುರುವೇ, ಇವನು ಕುರುಡನಾಗಿ ಹುಟ್ಟಿರುವದಕ್ಕೆ ಯಾರು ಪಾಪಮಾಡಿದರು? ಇವನೋ? ಇವನ ತಂದೆತಾಯಿಗಳೋ? ಎಂದು ಆತನನ್ನು ಕೇಳಿದ್ದಕ್ಕೆ",
			"3": "<sup>3</sup> ಯೇಸು - ಇವನೂ ಪಾಪಮಾಡಲಿಲ್ಲ, ಇವನ ತಂದೆತಾಯಿಗಳೂ ಪಾಪಮಾಡಲಿಲ್ಲ; ದೇವರ ಕ್ರಿಯೆಗಳು ಇವನಲ್ಲಿ ತೋರಿಬರುವದಕ್ಕೆ ಇದಾಯಿತು.",
			"4": "<sup>4</sup> ನನ್ನನ್ನು ಕಳುಹಿಸಿದಾತನ ಕಾರ್ಯಗಳನ್ನು ನಾವು ಹಗಲಿರುವಾಗಲೇ ನಡಿಸಬೇಕು. ರಾತ್ರಿ ಬರುತ್ತದೆ, ಅದು ಬಂದ ಮೇಲೆ ಯಾರೂ ಕೆಲಸ ಮಾಡಲಾರರು.",
			"5": "<sup>5</sup> ನಾನು ಲೋಕದಲ್ಲಿರುವಾಗ ಲೋಕಕ್ಕೆ ಬೆಳಕಾಗಿದ್ದೇನೆ ಅಂದನು.",
			"6": "<sup>6</sup> ಇದನ್ನು ಹೇಳಿ ನೆಲದ ಮೇಲೆ ಉಗುಳಿ ಆ ಉಗುಳಿನಿಂದ ಕೆಸರುಮಾಡಿ ಆ ಕೆಸರನ್ನು ಅವನ ಕಣ್ಣುಗಳಿಗೆ ಹಚ್ಚಿ -",
			"7": "<sup>7</sup> ನೀನು ಸಿಲೋವ ಕೊಳಕ್ಕೆ ಹೋಗಿ ತೊಳಕೋ ಅಂದನು. (ಸಿಲೋವ ಎಂಬ ಮಾತಿಗೆ ಕಳುಹಿಸಲ್ಪಟ್ಟವನು ಎಂದರ್ಥ.) ಅವನು ಹೋಗಿ ತೊಳಕೊಂಡು ಕಣ್ಣು ಹೊಂದಿದವನಾಗಿ ಬಂದನು.",
			"8": "<sup>8</sup> ಆಗ ನೆರೆಹೊರೆಯವರೂ ಅವನು ಭಿಕ್ಷಗಾರನಾಗಿದ್ದದ್ದನ್ನು ಮೊದಲು ನೋಡಿದವರೂ - ಇವನು ಕೂತಿದ್ದ ಆ ಭಿಕ್ಷುಕನಲ್ಲವೇ ಅಂದರು.",
			"9": "<sup>9</sup> ಕೆಲವರು - ಹೌದು, ಅವನೇ ಅಂದರು; ಇನ್ನೂ ಕೆಲವರು - ಅವನಲ್ಲ, ಅವನ ಹಾಗಿದ್ದಾನೆ ಅಂದರು. ಅವನಾದರೆ - ನಾನೇ ಅವನು ಅಂದನು.",
			"10": "<sup>10</sup> ಆಗ ಅವರು - ನಿನಗೆ ಕಣ್ಣು ಹೇಗೆ ಬಂದವು? ಎಂದು ಅವನನ್ನು ಕೇಳಿದ್ದಕ್ಕೆ ಇವನು - ಯೇಸು ಎಂಬವನಿದ್ದಾನಲ್ಲಾ,",
			"11": "<sup>11</sup> ಅವನು ಕೆಸರುಮಾಡಿ ನನ್ನ ಕಣ್ಣಿಗೆ ಹಚ್ಚಿ ಸಿಲೋವ ಕೊಳಕ್ಕೆ ಹೋಗಿ ತೊಳಕೋ ಎಂದು ಹೇಳಿದನು; ನಾನು ಹೋಗಿ ತೊಳಕೊಂಡೆನು; ಕಣ್ಣು ಬಂದವು ಅಂದನು.",
			"12": "<sup>12</sup> ಅವರು - ಅವನೆಲ್ಲಿದ್ದಾನೆ ಎಂದು ಅವನನ್ನು ಕೇಳಿದ್ದಕ್ಕೆ ಅವನು - ನನಗೆ ಗೊತ್ತಿಲ್ಲ ಅಂದನು.",
			"13": "<sup>13</sup> ಮುಂಚೆ ಕುರುಡನಾಗಿದ್ದ ಇವನನ್ನು ಫರಿಸಾಯರ ಬಳಿಗೆ ಕರಕೊಂಡು ಹೋದರು.",
			"14": "<sup>14</sup> ಯೇಸು ಕೆಸರುಮಾಡಿ ಅವನಿಗೆ ಕಣ್ಣು ಕೊಟ್ಟ ದಿವಸವು ಸಬ್ಬತ್‍ದಿವಸವಾಗಿತ್ತು.",
			"15": "<sup>15</sup> ಆದಕಾರಣ ಈ ಫರಿಸಾಯರು ಸಹ ಅವನನ್ನು ವಿಚಾರಿಸಿ - ನಿನಗೆ ಹೇಗೆ ಕಣ್ಣು ಬಂದವು ಎಂದು ಕೇಳಿದರು. ಅವನು ಅವರಿಗೆ - ಕೆಸರನ್ನು ನನ್ನ ಕಣ್ಣಿನ ಮೇಲೆ ಇಟ್ಟನು, ನಾನು ತೊಳಕೊಂಡೆನು, ಕಣ್ಣು ಕಾಣುತ್ತವೆ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"16": "<sup>16</sup> ಆ ಫರಿಸಾಯರಲ್ಲಿ ಕೆಲವರು - ಈ ಮನುಷ್ಯನು ದೇವರಿಂದ ಬಂದವನಲ್ಲ; ಅವನು ಸಬ್ಬತ್‍ದಿವಸವನ್ನು ಲಕ್ಷ್ಯಮಾಡುವದಿಲ್ಲವಲ್ಲಾ ಅಂದರು. ಇನ್ನು ಕೆಲವರು - ಇಂಥ ಮಹತ್ತುಗಳನ್ನು ಮಾಡುವದು ಭಕ್ತಿಹೀನನ ಕೈಯಿಂದ ಹೇಗಾದೀತು? ಅಂದರು. ಹೀಗೆ ಅವರಲ್ಲಿ ಭೇದವಾಯಿತು.",
			"17": "<sup>17</sup> ಹೀಗಿರಲಾಗಿ ಅವರು ತಿರಿಗಿ ಆ ಕುರುಡನನ್ನು - ಅವನು ನಿನಗೆ ಕಣ್ಣುಕೊಟ್ಟದ್ದರಿಂದ ನೀನು ಅವನ ವಿಷಯವಾಗಿ ಏನು ಹೇಳುತ್ತೀ ಎಂದು ಕೇಳಿದಾಗ ಅವನು - ಆತನು ಪ್ರವಾದಿ ಅನ್ನುತ್ತೇನೆ ಎಂದು ಹೇಳಿದನು.",
			"18": "<sup>18</sup> ಆದರೆ ಕಣ್ಣುಬಂದವನ ವಿಷಯವಾಗಿ ಆ ಯೆಹೂದ್ಯರು - ಇವನು ಕುರುಡನಾಗಿದ್ದು ಈಗ ಕಣ್ಣುಳ್ಳವನಾದನೆಂದು ನಂಬದೆ ಅವನ ತಂದೆತಾಯಿಗಳನ್ನು ಕರೆಯಿಸಿ -",
			"19": "<sup>19</sup> ಕುರುಡನಾಗಿ ಹುಟ್ಟಿದನೆಂದು ನೀವು ಹೇಳುವಂಥ ನಿಮ್ಮ ಮಗನು ಇವನೋ? ಈಗ ಇವನಿಗೆ ಕಣ್ಣು ಹೇಗೆ ಬಂದವು ಎಂದು ಅವರನ್ನು ಕೇಳಿದರು.",
			"20": "<sup>20</sup> ಅವನ ತಂದೆತಾಯಿಗಳು - ಇವನು ನಮ್ಮ ಮಗನೆಂದೂ ಕುರುಡನಾಗಿ ಹುಟ್ಟಿದನೆಂದೂ ಬಲ್ಲೆವು;",
			"21": "<sup>21</sup> ಈಗ ಹೇಗೆ ಕಣ್ಣು ಬಂದವೋ ಅರಿಯೆವು; ಕಣ್ಣು ಕೊಟ್ಟವನು ಯಾರೋ ಅರಿಯೆವು; ಇವನನ್ನೇ ಕೇಳಿರಿ; ಪ್ರಾಯದವನಾಗಿದ್ದಾನಲ್ಲಾ; ಇವನೇ ತನ್ನ ವಿಷಯವಾಗಿ ಹೇಳುವನು ಎಂದು ಉತ್ತರಕೊಟ್ಟರು.",
			"22": "<sup>22</sup> ಅವನ ತಂದೆತಾಯಿಗಳು ಯೆಹೂದ್ಯರಿಗೆ ಅಂಜಿದ ಕಾರಣ ಈ ಪ್ರಕಾರ ಹೇಳಿದರು. ಯಾಕಂದರೆ - ಯಾರಾದರೂ ಯೇಸುವನ್ನು ಕ್ರಿಸ್ತನೆಂದು ಒಪ್ಪಿಕೊಂಡು ಹೇಳಿದರೆ ಅಂಥವನಿಗೆ ಬಹಿಷ್ಕಾರಹಾಕಬೇಕೆಂದು ಯೆಹೂದ್ಯರು ಅದಕ್ಕೆ ಮುಂಚೆ ಗೊತ್ತುಮಾಡಿಕೊಂಡಿದ್ದರು.",
			"23": "<sup>23</sup> ಇವನು ಪ್ರಾಯದವನು, ಇವನನ್ನೇ ಕೇಳಿರಿ ಎಂದು ಅವನ ತಂದೆತಾಯಿಗಳು ಹೇಳಿದ್ದಕ್ಕೆ ಇದೇ ಕಾರಣ.",
			"24": "<sup>24</sup> ಆದಕಾರಣ ಅವರು ಕುರುಡನಾಗಿದ್ದ ಆ ಮನುಷ್ಯನನ್ನು ಎರಡನೆಯ ಸಾರಿ ಕರೆದು - ನೀನು ದೇವರಿಗೆ ಮಾನಬರುವಂತೆ ಹೇಳು; ಆ ಮನುಷ್ಯನು ಭಕ್ತಿಹೀನನೆಂದು ನಾವು ಬಲ್ಲೆವು ಎಂದು ಹೇಳಲು",
			"25": "<sup>25</sup> ಅವನು - ಆತನು ಭಕ್ತಿಹೀನನೋ ಏನೋ ನಾನರಿಯೆ, ಒಂದು ಮಾತ್ರ ಬಲ್ಲೆ, ಕುರುಡನಾಗಿದ್ದೆನು, ಈಗ ಕಣ್ಣು ಕಾಣುತ್ತವೆ ಅಂದನು.",
			"26": "<sup>26</sup> ಅವರು ಅವನನ್ನು - ನಿನಗೆ ಏನು ಮಾಡಿದನು? ನಿನಗೆ ಹೇಗೆ ಕಣ್ಣುಕೊಟ್ಟನು ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ ಅವನು -",
			"27": "<sup>27</sup> ನಿಮಗೆ ಆಗಲೇ ಹೇಳಿದೆನಲ್ಲಾ, ನೀವು ಕೇಳಲಿಲ್ಲ; ಯಾಕೆ ತಿರಿಗಿ ಕೇಳಬೇಕೆಂದಿದ್ದೀರಿ? ಆತನ ಶಿಷ್ಯರಾಗುವದಕ್ಕೆ ನಿಮಗೂ ಮನಸ್ಸದೆಯೋ? ಅಂದನು.",
			"28": "<sup>28</sup> ಅದಕ್ಕೆ ಅವರು ಅವನನ್ನು ಬೈದು - ನೀನು ಆ ಮನುಷ್ಯನ ಶಿಷ್ಯ; ನಾವು ಮೋಶೆಯ ಶಿಷ್ಯರು;",
			"29": "<sup>29</sup> ಮೋಶೆಯ ಸಂಗಡ ದೇವರು ಮಾತಾಡಿದನೆಂದು ನಾವು ಬಲ್ಲೆವು; ಆದರೆ ಇವನು ಎಲ್ಲಿಯವನೋ ಅರಿಯೆವು ಅಂದರು.",
			"30": "<sup>30</sup> ಅದಕ್ಕೆ ಆ ಮನುಷ್ಯನು - ಆತನು ನನಗೆ ಕಣ್ಣುಕೊಟ್ಟರೂ ಆತನು ಎಲ್ಲಿಯವನೋ ನಿಮಗೆ ಗೊತ್ತಿಲ್ಲದೆ ಇರುವದು ಆಶ್ಚರ್ಯವಲ್ಲವೇ.",
			"31": "<sup>31</sup> ಭಕ್ತಿಹೀನರ ಪ್ರಾರ್ಥನೆಯನ್ನು ದೇವರು ಕೇಳುವದಿಲ್ಲ; ಯಾವನು ಭಕ್ತನಾಗಿದ್ದು ಆತನ ಚಿತ್ತದಂತೆ ನಡೆಯುತ್ತಾನೋ ಅವನ ಪ್ರಾರ್ಥನೆಯನ್ನು ಕೇಳುತ್ತಾನೆಂದು ಬಲ್ಲೆವು.",
			"32": "<sup>32</sup> ಹುಟ್ಟುಕುರುಡನಿಗೆ ಯಾರಾದರೂ ಕಣ್ಣು ಕೊಟ್ಟ ಸಂಗತಿಯನ್ನು ಲೋಕಾದಿಯಿಂದ ಒಬ್ಬರೂ ಕೇಳಿದ್ದಿಲ್ಲ;",
			"33": "<sup>33</sup> ಈತನು ದೇವರಿಂದ ಬಂದವನಲ್ಲದಿದ್ದರೆ ಏನೂ ಮಾಡಲಾರದೆ ಇರುತ್ತಿದ್ದನು ಅಂದನು.",
			"34": "<sup>34</sup> ಈ ಮಾತಿಗೆ ಅವರು - ನೀನು ಕೇವಲ ಪಾಪದಲ್ಲಿ ಹುಟ್ಟಿದವನು, ನಮಗೆ ಉಪದೇಶಮಾಡುತ್ತೀಯೋ ಎಂದು ಹೇಳಿ ಅವನನ್ನು ಹೊರಗೆ ಹಾಕಿದರು.",
			"35": "<sup>35</sup> ಅವನನ್ನು ಹೊರಗೆ ಹಾಕಿದರೆಂದು ಯೇಸು ಕೇಳಿ ಅವನನ್ನು ಕಂಡುಕೊಂಡು - ನೀನು ಮನುಷ್ಯಕುಮಾರನನ್ನು ನಂಬುತ್ತೀಯೋ ಎಂದು ಕೇಳಿದನು.",
			"36": "<sup>36</sup> ಅದಕ್ಕವನು - ಆತನು ಯಾರು, ಸ್ವಾಮೀ? ತಿಳಿಸಿದರೆ ಆತನನ್ನು ನಂಬುತ್ತೇನೆ ಅಂದಾಗ",
			"37": "<sup>37</sup> ಯೇಸು - ನೀನು ಅವನನ್ನು ಕಂಡಿದ್ದೀ; ನಿನ್ನ ಸಂಗಡ ಮಾತಾಡುತ್ತಿರುವ ನಾನೇ ಅವನು ಅಂದನು.",
			"38": "<sup>38</sup> ಅವನು - ನಂಬುತ್ತೇನೆ, ಸ್ವಾಮೀ, ಎಂದು ಹೇಳಿ ಆತನಿಗೆ ಅಡ್ಡಬಿದ್ದನು.",
			"39": "<sup>39</sup> ಆಗ ಯೇಸು - ನಾನು ನ್ಯಾಯತೀರ್ಪಿಗಾಗಿ ಈ ಲೋಕಕ್ಕೆ ಬಂದಿದ್ದೇನೆ; ಆ ತೀರ್ಪು ಏನಂದರೆ, ಕಣ್ಣಿಲ್ಲದವರಿಗೆ ಕಣ್ಣುಬರುವವು. ಕಣ್ಣಿದ್ದವರು ಕುರುಡರಾಗುವರು ಅಂದನು.",
			"40": "<sup>40</sup> ಇದನ್ನು ಆತನ ಸಂಗಡ ಇದ್ದ ಫರಿಸಾಯರು ಕೇಳಿ - ನಾವೂ ಕುರುಡರೇನು? ಎಂದು ಕೇಳಿದಾಗ",
			"41": "<sup>41</sup> ಯೇಸು ಅವರಿಗೆ - ನೀವು ಕುರುಡರಾಗಿದ್ದರೆ ನಿಮಗೆ ಪಾಪವು ಇರುತ್ತಿರಲಿಲ್ಲ; ನಮಗೆ ಕಣ್ಣು ಕಾಣುತ್ತವೆ ಅನ್ನುತ್ತೀರಾದ್ದರಿಂದ ನಿಮ್ಮ ಪಾಪವು ಉಳಿದದೆ."
		},
		"10": {
			"1": "<sup>1</sup> ನಿಮಗೆ ನಿಜನಿಜವಾಗಿ ಹೇಳುತ್ತೇನೆ, ಕುರೀಹಟ್ಟಿಯೊಳಗೆ ಬಾಗಲಿಂದ ಬಾರದೆ ಮತ್ತೆಲ್ಲಿಂದಾದರೂ ಹತ್ತಿಬರುವವನು ಕಳ್ಳನೂ ಸುಲುಕೊಳ್ಳುವವನೂ ಆಗಿದ್ದಾನೆ.",
			"2": "<sup>2</sup> ಬಾಗಲಿಂದ ಒಳಗೆ ಬರುವವನು ಆ ಕುರಿಗಳ ಕುರುಬನು.",
			"3": "<sup>3</sup> ಬಾಗಲು ಕಾಯುವವನು ಅವನಿಗೆ ಬಾಗಲನ್ನು ತೆರೆಯುತ್ತಾನೆ, ಕುರಿಗಳು ಅವನ ಸ್ವರಕ್ಕೆ ಕಿವಿಗೊಡುತ್ತವೆ.",
			"4": "<sup>4</sup> ಅವನು ಸ್ವಂತ ಕುರಿಗಳನ್ನು ಹೆಸರು ಹೇಳಿ ಕರೆದು ಹೊರಗೆ ಬಿಡುತ್ತಾನೆ. ಸ್ವಂತ ಕುರಿಗಳನ್ನೆಲ್ಲಾ ಹೊರಗೆ ಬಿಟ್ಟ ಮೇಲೆ ತಾನು ಅವುಗಳ ಮುಂದೆ ಹೋಗುತ್ತಾನೆ; ಕುರಿಗಳು ಅವನ ಸ್ವರವನ್ನು ತಿಳುಕೊಂಡು ಅವನ ಹಿಂದೆ ಹೋಗುತ್ತವೆ.",
			"5": "<sup>5</sup> ಅವು ಅನ್ಯರ ಸ್ವರವನ್ನು ತಿಳಿಯುವದಿಲ್ಲ; ಅನ್ಯನ ಹಿಂದೆ ಹೋಗದೆ ಅವನ ಬಳಿಯಿಂದ ಓಡಿಹೋಗುತ್ತವೆ.",
			"6": "<sup>6</sup> ಈ ಉಪಮಾನವನ್ನು ಯೇಸು ಅವರಿಗೆ ಹೇಳಿದನು; ಆದರೂ ಆತನ ಮಾತಿನ ಅರ್ಥವನ್ನು ಅವರು ಗ್ರಹಿಸಲಿಲ್ಲ.",
			"7": "<sup>7</sup> ಆದಕಾರಣ ಯೇಸು ಮತ್ತೂ ಅವರಿಗೆ - ನಿಮಗೆ ನಿಜನಿಜವಾಗಿ ಹೇಳುತ್ತೇನೆ, ಕುರಿಗಳಿಗೆ ನಾನೇ ಬಾಗಲಾಗಿದ್ದೇನೆ.",
			"8": "<sup>8</sup> ನನಗಿಂತ ಮುಂಚೆ ಬಂದವರೆಲ್ಲರು ಕಳ್ಳರೂ ಸುಲುಕೊಳ್ಳುವವರೂ ಆಗಿದ್ದಾರೆ; ಆದರೆ ಕುರಿಗಳು ಅವರಿಗೆ ಕಿವಿಗೊಡಲಿಲ್ಲ.",
			"9": "<sup>9</sup> ನಾನೇ ಆ ಬಾಗಲು; ನನ್ನ ಮುಖಾಂತರವಾಗಿ ಯಾವನಾದರೂ ಒಳಗೆ ಹೋದರೆ ಸುರಕ್ಷಿತವಾಗಿದ್ದು ಒಳಗೆ ಹೋಗುವನು, ಹೊರಗೆ ಬರುವನು, ಮೇವನ್ನು ಕಂಡುಕೊಳ್ಳುವನು.",
			"10": "<sup>10</sup> ಕಳ್ಳನು ಕದ್ದುಕೊಳ್ಳುವದಕ್ಕೂ ಕೊಯ್ಯುವದಕ್ಕೂ ಹಾಳುಮಾಡುವದಕ್ಕೂ ಬರುತ್ತಾನೆ ಹೊರತು ಮತ್ತಾವದಕ್ಕೂ ಬರುವದಿಲ್ಲ; ನಾನಾದರೋ ಅವುಗಳಿಗೆ ಜೀವವು ಇರಬೇಕೆಂತಲೂ ಅದು ಸಮೃದ್ಧಿಯಾಗಿ ಇರಬೇಕೆಂತಲೂ ಬಂದೆನು.",
			"11": "<sup>11</sup> ನಾನೇ ಒಳ್ಳೇ ಕುರುಬನು; ಒಳ್ಳೇ ಕುರುಬನು ತನ್ನ ಕುರಿಗಳಿಗೋಸ್ಕರ ತನ್ನ ಪ್ರಾಣವನ್ನು ಕೊಡುತ್ತಾನೆ.",
			"12": "<sup>12</sup> ಕೂಲಿಯಾಳು ಕುರುಬನೂ ಕುರಿಗಳ ಒಡೆಯನೂ ಆಗಿರದೆ ತೋಳ ಬರುವದನ್ನು ಕಾಣುತ್ತಲೇ ಕುರಿಗಳನ್ನು ಬಿಟ್ಟು ಓಡಿಹೋಗುತ್ತಾನೆ; ತೋಳವು ಅವುಗಳನ್ನು ಹಿಡುಕೊಂಡು ಹಿಂಡನ್ನು ಚದರಿಸುತ್ತದೆ.",
			"13": "<sup>13</sup> ಅವನು ಕೂಲಿಯಾಳು; ಕುರಿಗಳ ಚಿಂತೆ ಅವನಿಗಿಲ್ಲ.",
			"14": "<sup>14</sup> ನಾನೇ ಒಳ್ಳೇ ಕುರುಬನು; ತಂದೆಯು ನನ್ನನ್ನು ನಾನು ತಂದೆಯನ್ನು ತಿಳಿದಿರುವಂತೆಯೇ ನಾನು ನನ್ನ ಕುರಿಗಳನ್ನು ತಿಳಿದಿದ್ದೇನೆ; ನನ್ನ ಕುರಿಗಳು ನನ್ನನ್ನು ತಿಳಿದವೆ; ಮತ್ತು ಕುರಿಗಳಿಗೋಸ್ಕರ ನನ್ನ ಪ್ರಾಣವನ್ನು ಕೊಡುತ್ತೇನೆ.",
			"16": "<sup>16</sup> ಇದಲ್ಲದೆ ಈ ಹಟ್ಟಿಗೆ ಸೇರದಿರುವ ಇನ್ನು ಬೇರೆ ಕುರಿಗಳು ನನಗೆ ಅವೆ, ಅವುಗಳನ್ನೂ ನಾನು ತರಬೇಕು; ಅವು ನನ್ನ ಸ್ವರಕ್ಕೆ ಕಿವಿಗೊಡುವವು; ಆಗ ಒಂದೇ ಹಿಂಡು ಆಗುವದು, ಒಬ್ಬನೇ ಕುರುಬನಿರುವನು.",
			"17": "<sup>17</sup> ನಾನು ತಿರಿಗಿ ಪಡಕೊಳ್ಳುತ್ತೇನೆಂದು ನನ್ನ ಪ್ರಾಣವನ್ನು ಕೊಡುತ್ತೇನೆ; ಅದರ ನಿಮಿತ್ತವಾಗಿ ತಂದೆಯು ನನ್ನನ್ನು ಪ್ರೀತಿಸುತ್ತಾನೆ.",
			"18": "<sup>18</sup> ನನ್ನ ಪ್ರಾಣವನ್ನು ಯಾರೂ ನನ್ನಿಂದ ತೆಗೆಯರು, ನನ್ನಷ್ಟಕ್ಕೆ ನಾನೇ ಅದನ್ನು ಕೊಡುತ್ತೇನೆ; ಅದನ್ನು ಕೊಡುವದಕ್ಕೆ ನನಗೆ ಅಧಿಕಾರ ಉಂಟು, ಅದನ್ನು ತಿರಿಗಿ ಪಡಕೊಳ್ಳುವದಕ್ಕೆ ನನಗೆ ಅಧಿಕಾರ ಉಂಟು; ಈ ಅಪ್ಪಣೆಯನ್ನು ನನ್ನ ತಂದೆಯಿಂದ ಹೊಂದಿದ್ದೇನೆ ಅಂದನು.",
			"19": "<sup>19</sup> ಈ ಮಾತುಗಳ ದೆಸೆಯಿಂದ ಯೆಹೂದ್ಯರಲ್ಲಿ ತಿರಿಗಿ ಭೇದವಾಯಿತು.",
			"20": "<sup>20</sup> ಅವರಲ್ಲಿ ಅನೇಕರು - ಅವನಿಗೆ ದೆವ್ವಹಿಡಿದದೆ, ಹುಚ್ಚುಹಿಡಿದದೆ, ಯಾಕೆ ಅವನ ಮಾತು ಕೇಳುತ್ತೀರಿ? ಅಂದರು.",
			"21": "<sup>21</sup> ಮತ್ತು ಕೆಲವರು - ಇವು ದೆವ್ವಹಿಡಿದವನ ಮಾತುಗಳಲ್ಲ; ದೆವ್ವವು ಕುರುಡರಿಗೆ ಕಣ್ಣುಕೊಡಬಲ್ಲದೇ? ಅಂದರು.",
			"22": "<sup>22</sup> ಆ ಕಾಲದಲ್ಲಿ ದೇವಾಲಯಪ್ರತಿಷ್ಠೆಯ ಹಬ್ಬವು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ನಡೆಯಿತು; ಆಗ ಚಳಿಕಾಲವಾಗಿತ್ತು.",
			"23": "<sup>23</sup> ಯೇಸು ದೇವಾಲಯದೊಳಗೆ ಸೊಲೊಮೋನನ ಮಂಟಪದಲ್ಲಿ ತಿರುಗಾಡುತ್ತಾ ಇರುವಾಗ ಯೆಹೂದ್ಯರು ಆತನನ್ನು ಸುತ್ತಿಕೊಂಡು -",
			"24": "<sup>24</sup> ಇನ್ನು ಎಷ್ಟು ಕಾಲ ನಮ್ಮಲ್ಲಿ ಅನುಮಾನ ಹುಟ್ಟಿಸುತ್ತೀ? ನೀನು ಕ್ರಿಸ್ತನಾಗಿದ್ದರೆ ಅದನ್ನು ನಮಗೆ ಸ್ಪಷ್ಟವಾಗಿ ಹೇಳು ಅಂದರು.",
			"25": "<sup>25</sup> ಅದಕ್ಕೆ ಯೇಸು - ನಿಮಗೆ ಹೇಳಿದೆನು, ಆದರೆ ನೀವು ನಂಬದೆ ಇದ್ದೀರಿ; ನನ್ನ ತಂದೆಯ ಹೆಸರಿನಲ್ಲಿ ನಾನು ಮಾಡುವ ಕ್ರಿಯೆಗಳೇ ನನಗೆ ಸಾಕ್ಷಿಯಾಗಿವೆ.",
			"26": "<sup>26</sup> ಆದರೂ ನೀವು ನನ್ನ ಕುರಿಗಳಿಗೆ ಸೇರಿದವರಲ್ಲವಾದದರಿಂದ ನಂಬದೆ ಇದ್ದೀರಿ.",
			"27": "<sup>27</sup> ನನ್ನ ಕುರಿಗಳು ನನ್ನ ಸ್ವರಕ್ಕೆ ಕಿವಿಗೊಡುತ್ತವೆ; ನಾನು ಅವುಗಳನ್ನು ಬಲ್ಲೆನು; ಅವು ನನ್ನ ಹಿಂದೆ ಬರುತ್ತವೆ.",
			"28": "<sup>28</sup> ನಾನು ಅವುಗಳಿಗೆ ನಿತ್ಯಜೀವವನ್ನು ಕೊಡುತ್ತೇನೆ; ಅವು ಎಂದಿಗೂ ನಾಶವಾಗುವದೇ ಇಲ್ಲ; ಅವುಗಳನ್ನು ಯಾರೂ ನನ್ನ ಕೈಯೊಳಗಿಂದ ಕಸಕೊಳ್ಳರು.",
			"29": "<sup>29</sup> ನನ್ನ ತಂದೆಯು ನನಗೆ ಕೊಟ್ಟದ್ದು ಎಲ್ಲಾದ್ದಕ್ಕಿಂತ ದೊಡ್ಡದು; ಅದನ್ನು ತಂದೆಯ ಕೈಯೊಳಗಿಂದ ಯಾರೂ ಕಸಕೊಳ್ಳಲಾರರು.",
			"30": "<sup>30</sup> ನಾನೂ ತಂದೆಯೂ ಒಂದಾಗಿದ್ದೇವೆ ಅಂದನು.",
			"31": "<sup>31</sup> ಯೆಹೂದ್ಯರು ಆತನನ್ನು ಹೊಡೆದು ಕೊಲ್ಲಬೇಕೆಂದು ತಿರಿಗಿ ಕಲ್ಲುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಬರಲು",
			"32": "<sup>32</sup> ಯೇಸು ಅವರನ್ನು - ತಂದೆಯ ಕಡೆಯಿಂದ ಅನೇಕ ಒಳ್ಳೇ ಕ್ರಿಯೆಗಳನ್ನು ನಿಮಗೆ ತೋರಿಸಿದೆನು; ಅವುಗಳಲ್ಲಿ ಯಾವ ಕ್ರಿಯೆಯ ದೆಸೆಯಿಂದ ನನ್ನ ಮೇಲೆ ಕಲ್ಲೆಸೆಯುತ್ತೀರಿ ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ",
			"33": "<sup>33</sup> ಯೆಹೂದ್ಯರು - ನಾವು ನಿನ್ನ ಮೇಲೆ ಕಲ್ಲೆಸೆಯುವದು ಒಳ್ಳೇ ಕಾರ್ಯದ ದೆಸೆಯಿಂದಲ್ಲ, ದೇವದೂಷಣೆಯ ದೆಸೆಯಿಂದಲೂ ನೀನು ಮನುಷ್ಯನಾಗಿದ್ದು ನಿನ್ನನ್ನು ನೀನೇ ದೇವರನ್ನಾಗಿ ಮಾಡಿಕೊಳ್ಳುವದರ ದೆಸೆಯಿಂದಲೂ ನಿನ್ನ ಮೇಲೆ ಕಲ್ಲೆಸೆಯುತ್ತೇವೆ ಅಂದರು.",
			"34": "<sup>34</sup> ಅದಕ್ಕೆ ಯೇಸು - ನೀವು ದೇವರುಗಳೇ ಎಂದು ನಾನು ಹೇಳಿದೆನು ಎಂಬದಾಗಿ ನಿಮ್ಮ ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿ ಬರೆದದೆಯಲ್ಲಾ; ಶಾಸ್ತ್ರವು ಸುಳ್ಳಾಗಲಾರದಷ್ಟೆ;",
			"35": "<sup>35</sup> ಹಾಗಾದರೆ ದೇವರ ವಾಕ್ಯವನ್ನು ಹೊಂದಿದವರನ್ನು ದೇವರುಗಳೆಂದು ಆತನು ಹೇಳಿರುವಲ್ಲಿ",
			"36": "<sup>36</sup> ತಂದೆಯು ಪ್ರತಿಷ್ಠೆಮಾಡಿ ಲೋಕಕ್ಕೆ ಕಳುಹಿಸಿಕೊಟ್ಟವನಾದ ನಾನು ದೇವರ ಮಗನಾಗಿದ್ದೇನೆಂದು ಹೇಳಿದ್ದಕ್ಕೆ ನೀನು ದೇವದೂಷಣೆ ಮಾಡುತ್ತೀ ಅನ್ನುತ್ತೀರೋ?",
			"37": "<sup>37</sup> ನಾನು ನನ್ನ ತಂದೆಯ ಕ್ರಿಯೆಗಳನ್ನು ಮಾಡದಿದ್ದರೆ ನನ್ನ ಮಾತನ್ನು ನಂಬಬೇಡಿರಿ;",
			"38": "<sup>38</sup> ಮಾಡಿದರೆ ನನ್ನ ಮಾತನ್ನು ನಂಬದೆ ಇದ್ದರೂ ಆ ಕ್ರಿಯೆಗಳ ಸಾಕ್ಷಿಯನ್ನಾದರೂ ನಂಬಿರಿ; ಆಗ ನನ್ನಲ್ಲಿ ತಂದೆಯು ಇದ್ದಾನೆಂತಲೂ ತಂದೆಯಲ್ಲಿ ನಾನು ಇದ್ದೇನೆಂತಲೂ ನೀವು ತಿಳುಕೊಂಡು ಮನನಮಾಡಿಕೊಳ್ಳುವಿರಿ ಅಂದನು.",
			"39": "<sup>39</sup> ಅವರು ತಿರಿಗಿ ಆತನನ್ನು ಹಿಡಿಯುವದಕ್ಕೆ ನೋಡಿದರು; ಆದರೆ ಅವರ ಕೈಗೆ ತಪ್ಪಿಸಿಕೊಂಡು ಹೋದನು.",
			"40": "<sup>40</sup> ಆಮೇಲೆ ಆತನು ಯೊರ್ದನ್‍ಹೊಳೆಯ ಆಚೆ ಯೋಹಾನನು ಮೊದಲು ಸ್ನಾನಮಾಡಿಸುತ್ತಿದ್ದ ಸ್ಥಳಕ್ಕೆ ತಿರಿಗಿ ಹೋಗಿ ಅಲ್ಲಿ ಇದ್ದನು.",
			"41": "<sup>41</sup> ಅನೇಕರು ಆತನ ಬಳಿಗೆ ಬಂದು - ಯೋಹಾನನು ಒಂದು ಸೂಚಕಕಾರ್ಯವನ್ನೂ ಮಾಡಲಿಲ್ಲ; ಆದರೆ ಈತನ ವಿಷಯವಾಗಿ ಯೋಹಾನನು ಹೇಳಿದ್ದೆಲ್ಲಾ ನಿಜವಾಗಿತ್ತು ಎಂದು ಮಾತಾಡುತ್ತಿದ್ದರು.",
			"42": "<sup>42</sup> ಅಲ್ಲಿ ಅನೇಕರು ಆತನನ್ನು ನಂಬಿದರು."
		},
		"11": {
			"1": "<sup>1</sup> ಬೇಥಾನ್ಯದ ಲಾಜರನೆಂಬವನೊಬ್ಬನು ಅಸ್ವಸ್ಥನಾಗಿದ್ದನು. ಅದು ಮಾರ್ಥಳು ಮರಿಯಳು ಎಂಬ ಅಕ್ಕತಂಗಿಯರು ಇದ್ದ ಊರು.",
			"2": "<sup>2</sup> ಈ ಮರಿಯಳು ಯೇಸುಸ್ವಾಮಿಗೆ ತೈಲವನ್ನು ಹಚ್ಚಿ ಆತನ ಪಾದಗಳನ್ನು ತನ್ನ ತಲೇಕೂದಲಿನಿಂದ ಒರಸಿದವಳು. ಈಕೆಯ ತಮ್ಮನಾದ ಲಾಜರನೇ ಅಸ್ವಸ್ಥನಾಗಿದ್ದನು.",
			"3": "<sup>3</sup> ಹೀಗಿರಲಾಗಿ ಅವನ ಅಕ್ಕಂದಿರು - ಸ್ವಾಮೀ, ನಿನ್ನ ಪ್ರಿಯ ಮಿತ್ರನು ಅಸ್ವಸ್ಥನಾಗಿದ್ದಾನೆ ಎಂಬದಾಗಿ ಯೇಸುವಿನ ಬಳಿಗೆ ಹೇಳಿಕಳುಹಿಸಿದರು.",
			"4": "<sup>4</sup> ಯೇಸು ಅದನ್ನು ಕೇಳಿ - ಈ ರೋಗವು ಮರಣಕ್ಕಾಗಿ ಬಂದದ್ದಲ್ಲ; ಇದರಿಂದ ದೇವಕುಮಾರನಿಗೆ ಮಹಿಮೆ ಉಂಟಾಗುವಂತೆ ದೇವರ ಮಹಿಮೆಗೋಸ್ಕರ ಬಂದದ್ದು ಅಂದನು.",
			"5": "<sup>5</sup> ಮಾರ್ಥಳಲ್ಲಿಯೂ ಆಕೆಯ ತಂಗಿಯಲ್ಲಿಯೂ ಲಾಜರನಲ್ಲಿಯೂ ಯೇಸುವಿಗೆ ಪ್ರೀತಿ ಇತ್ತು.",
			"6": "<sup>6</sup> ಆದರೂ ಲಾಜರನು ಅಸ್ವಸ್ಥನಾಗಿದ್ದಾನೆಂದು ಆತನು ಕೇಳಿದ ಮೇಲೆ ಎರಡು ದಿನ ತಾನಿದ್ದ ಸ್ಥಳದಲ್ಲೇ ನಿಂತನು.",
			"7": "<sup>7</sup> ಬಳಿಕ ಶಿಷ್ಯರಿಗೆ - ತಿರಿಗಿ ಯೂದಾಯಕ್ಕೆ ಹೋಗೋಣ ಎಂದು ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ಶಿಷ್ಯರು - ಗುರುವೇ, ಯೆಹೂದ್ಯರು ಆಗಲೇ ನಿನ್ನನ್ನು ಕಲ್ಲೆಸೆದು ಕೊಲ್ಲಬೇಕೆಂದಿದ್ದರು; ತಿರಿಗಿ ಅಲ್ಲಿಗೇ ಹೋಗುತ್ತೀಯಾ? ಅನ್ನಲು",
			"9": "<sup>9</sup> ಯೇಸು - ಹಗಲಿಗೆ ಹನ್ನೆರಡು ತಾಸು ಉಂಟಲ್ಲವೇ. ಒಬ್ಬನು ಹಗಲಲ್ಲಿ ತಿರುಗಾಡಿದರೆ ಈ ಲೋಕದ ಬೆಳಕು ಕಾಣಿಸುವದರಿಂದ ಆತನು ಎಡವುವದಿಲ್ಲ;",
			"10": "<sup>10</sup> ರಾತ್ರಿಯಲ್ಲಿ ತಿರುಗಾಡಿದರೆ ಅವನಿಗೆ ಬೆಳಕಿಲ್ಲದ್ದರಿಂದ ಎಡವುವನು ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"11": "<sup>11</sup> ಈ ಮಾತುಗಳನ್ನು ಹೇಳಿದ ಮೇಲೆ ಅವರಿಗೆ - ನಮ್ಮ ಮಿತ್ರನಾದ ಲಾಜರನು ನಿದ್ರೆಮಾಡುತ್ತಾನೆ; ನಾನು ಅವನನ್ನು ನಿದ್ರೆಯಿಂದ ಎಬ್ಬಿಸುವದಕ್ಕಾಗಿ ಹೋಗುತ್ತೇನೆ ಎಂದು ಹೇಳಿದನು.",
			"12": "<sup>12</sup> ಅದಕ್ಕೆ ಶಿಷ್ಯರು - ಸ್ವಾಮೀ, ಅವನು ನಿದ್ರೆಮಾಡುತ್ತಿದ್ದರೆ ಸ್ವಸ್ಥನಾಗುವನು ಅಂದರು.",
			"13": "<sup>13</sup> ಯೇಸು ಅವನ ಮರಣವನ್ನು ಸೂಚಿಸಿ ಅದನ್ನು ಹೇಳಿದ್ದನು; ಆದರೆ ಅವರು ನಿದ್ರೆಯ ವಿಶ್ರಾಂತಿಯನ್ನು ಕುರಿತು ಹೇಳುತ್ತಾನೆಂದು ನೆನಸಿದರು.",
			"14": "<sup>14</sup> ಆಗ ಯೇಸು ಸ್ಪಷ್ಟವಾಗಿ ಅವರಿಗೆ - ಲಾಜರನು ಸತ್ತುಹೋದನು;",
			"15": "<sup>15</sup> ಮತ್ತು ನಾನು ಅಲ್ಲಿ ಇಲ್ಲದೆ ಇದ್ದದ್ದಕ್ಕೆ ನಿಮ್ಮ ನಿಮಿತ್ತವಾಗಿ ಸಂತೋಷಪಡುತ್ತೇನೆ, ಯಾಕಂದರೆ ಅದರಿಂದ ನೀವು ನಂಬುವದಕ್ಕೆ ಮಾರ್ಗವಾಯಿತು; ಆದರೆ ಅವನ ಬಳಿಗೆ ಹೋಗೋಣ ಎಂದು ಹೇಳಿದನು.",
			"16": "<sup>16</sup> ಆಗ ದಿದುಮನೆಂಬ ತೋಮನು ಜೊತೇ ಶಿಷ್ಯರಿಗೆ - ನಾವು ಸಹ ಹೋಗಿ ಆತನೊಡನೆ ಸಾಯೋಣ ಎಂದು ಹೇಳಿದನು.",
			"17": "<sup>17</sup> ಯೇಸು ಬಂದಾಗ ಲಾಜರನನ್ನು ಸಮಾಧಿಯಲ್ಲಿಟ್ಟು ನಾಲ್ಕು ದಿವಸವಾಯಿತೆಂದು ಆತನಿಗೆ ತಿಳಿದುಬಂತು.",
			"18": "<sup>18</sup> ಬೇಥಾನ್ಯವು ಯೆರೂಸಲೇಮಿಗೆ ಸಮೀಪವಾಗಿತ್ತು; ಇದಕ್ಕೂ ಅದಕ್ಕೂ ಹೆಚ್ಚುಕಡಿಮೆ ಒಂದು ಹರದಾರಿ ಅಂತರ.",
			"19": "<sup>19</sup> ಆದದರಿಂದ ಯೆಹೂದ್ಯರಲ್ಲಿ ಅನೇಕರು ಮಾರ್ಥಳ ಮತ್ತು ಮರಿಯಳ ಬಳಿಗೆ ಅವರನ್ನು ತಮ್ಮನ ವಿಷಯದಲ್ಲಿ ಸಂತೈಸಬೇಕೆಂದು ಬಂದಿದ್ದರು.",
			"20": "<sup>20</sup> ಹೀಗಿರುವಲ್ಲಿ ಮಾರ್ಥಳು ಯೇಸು ಬರುತ್ತಾನೆಂದು ಕೇಳಿ ಆತನನ್ನು ಎದುರುಗೊಳ್ಳುವದಕ್ಕೆ ಹೋದಳು; ಆದರೆ ಮರಿಯಳು ಮನೆಯಲ್ಲೇ ಕೂತುಕೊಂಡಿದ್ದಳು.",
			"21": "<sup>21</sup> ಮತ್ತು ಮಾರ್ಥಳು ಯೇಸುವಿಗೆ - ಸ್ವಾಮೀ, ನೀನು ಇಲ್ಲಿ ಇರುತ್ತಿದ್ದರೆ ನನ್ನ ತಮ್ಮನು ಸಾಯುತ್ತಿರಲಿಲ್ಲ;",
			"22": "<sup>22</sup> ಈಗಲಾದರೂ ದೇವರನ್ನು ಏನು ಬೇಡಿಕೊಳ್ಳುತ್ತೀಯೋ ಅದನ್ನು ದೇವರು ನಿನಗೆ ಅನುಗ್ರಹಿಸುವನೆಂದು ಬಲ್ಲೆನು ಎಂದು ಹೇಳಿದಳು.",
			"23": "<sup>23</sup> ಯೇಸು ಆಕೆಗೆ - ನಿನ್ನ ತಮ್ಮನು ಎದ್ದುಬರುವನೆಂದು ಹೇಳಿದನು.",
			"24": "<sup>24</sup> ಮಾರ್ಥಳು - ಸತ್ತವರಿಗೆ ಕಡೇ ದಿನದಲ್ಲಿ ಪುನರುತ್ಥಾನವಾಗುವಾಗ ಅವನೂ ಎದ್ದು ಬರುವನೆಂದು ನಾನು ಬಲ್ಲೆನು ಅಂದಳು.",
			"25": "<sup>25</sup> ಯೇಸು ಆಕೆಗೆ - ನಾನೇ ಪುನರುತ್ಥಾನವೂ ಜೀವವೂ ಆಗಿದ್ದೇನೆ; ನನ್ನನ್ನು ನಂಬುವವನು ಸತ್ತರೂ ಬದುಕುವನು;",
			"26": "<sup>26</sup> ಮತ್ತು ಬದುಕುತ್ತಾ ನನ್ನನ್ನು ನಂಬುವ ಪ್ರತಿಯೊಬ್ಬನು ಎಂದಿಗೂ ಸಾಯುವದಿಲ್ಲ; ಇದನ್ನು ನಂಬುತ್ತೀಯಾ? ಎಂದು ಹೇಳಿದನು.",
			"27": "<sup>27</sup> ಆಕೆ - ಹೌದು, ಸ್ವಾಮೀ, ಲೋಕಕ್ಕೆ ಬರಬೇಕಾದ ದೇವಕುಮಾರನಾದ ಕ್ರಿಸ್ತನು ನೀನೇ ಎಂದು ನಂಬಿದ್ದೇನೆ ಅಂದಳು.",
			"28": "<sup>28</sup> ಇದನ್ನು ಹೇಳಿ ಆಕೆ ಹೋಗಿ ತನ್ನ ತಂಗಿಯಾದ ಮರಿಯಳನ್ನು ಗುಪ್ತವಾಗಿ ಕರೆದು - ಗುರುವು ಬಂದಿದ್ದಾನೆ, ನಿನ್ನನ್ನು ಕರೆಯುತ್ತಾನೆ ಅಂದಳು.",
			"29": "<sup>29</sup> ಮರಿಯಳು ಇದನ್ನು ಕೇಳುತ್ತಲೇ ತಟ್ಟನೆ ಎದ್ದು ಆತನ ಬಳಿಗೆ ಹೋದಳು.",
			"30": "<sup>30</sup> ಯೇಸು ಇನ್ನೂ ಊರೊಳಗೆ ಬಾರದೆ ಮಾರ್ಥಳು ಆತನಿಗೆದುರಾಗಿ ಬಂದ ಸ್ಥಳದಲ್ಲೇ ಇದ್ದನು.",
			"31": "<sup>31</sup> ಮರಿಯಳ ಕೂಡ ಮನೆಯಲ್ಲಿ ಇದ್ದುಕೊಂಡು ಆಕೆಯನ್ನು ಸಂತೈಸುತ್ತಿದ್ದ ಯೆಹೂದ್ಯರು - ಆಕೆ ತಟ್ಟನೆ ಎದ್ದು ಹೊರಗೆ ಹೋಗುವದನ್ನು ಕಂಡು ಈಕೆ ಅಳುವದಕ್ಕಾಗಿ ಸಮಾಧಿಗೆ ಹೋಗುತ್ತಾಳೆಂದು ನೆನಸಿ ಆಕೆಯ ಹಿಂದೆ ಹೋದರು.",
			"32": "<sup>32</sup> ಮರಿಯಳು ಯೇಸು ಇದ್ದಲ್ಲಿಗೆ ಬಂದು ಆತನನ್ನು ಕಂಡು ಆತನ ಪಾದಕ್ಕೆ ಬಿದ್ದು - ಸ್ವಾಮೀ, ನೀನು ಇಲ್ಲಿ ಇರುತ್ತಿದ್ದರೆ ನನ್ನ ತಮ್ಮನು ಸಾಯುತ್ತಿರಲಿಲ್ಲ ಅಂದಳು.",
			"33": "<sup>33</sup> ಆಕೆ ಗೋಳಾಡುವದನ್ನೂ ಆಕೆಯ ಸಂಗಡ ಬಂದ ಯೆಹೂದ್ಯರು ಗೋಳಾಡುವದನ್ನೂ ಯೇಸು ಕಂಡಾಗ ಆತ್ಮದಲ್ಲಿ ನೊಂದುಕೊಂಡು ತತ್ತರಿಸಿ -",
			"34": "<sup>34</sup> ಅವನನ್ನು ಎಲ್ಲಿ ಇಟ್ಟಿದ್ದೀರಿ ಎಂದು ಕೇಳಿದನು. ಅವರು - ಸ್ವಾಮೀ, ಬಂದು ನೋಡು ಅಂದರು.",
			"35": "<sup>35</sup> ಯೇಸು ಕಣ್ಣೀರು ಬಿಟ್ಟನು.",
			"36": "<sup>36</sup> ಯೆಹೂದ್ಯರು ನೋಡಿ - ಆಹಾ, ಈತನು ಅವನ ಮೇಲೆ ಎಷ್ಟೋ ಮಮತೆ ಇಟ್ಟಿದ್ದನು ಅಂದುಕೊಂಡರು.",
			"37": "<sup>37</sup> ಅವರಲ್ಲಿ ಕೆಲವರು - ಈತನು ಆ ಕುರುಡನಿಗೆ ಕಣ್ಣು ಕೊಟ್ಟನಲ್ಲಾ; ಈ ಮನುಷ್ಯನನ್ನು ಸಾಯದ ಹಾಗೆ ಮಾಡಲಾರದೆ ಇದ್ದನೇ? ಅಂದರು.",
			"38": "<sup>38</sup> ಯೇಸು ತನ್ನಲ್ಲಿ ತಿರಿಗಿ ನೊಂದುಕೊಳ್ಳುತ್ತಾ ಸಮಾಧಿಯ ಬಳಿಗೆ ಬಂದನು. ಅದು ಗವಿಯಾಗಿತ್ತು, ಅದರ ಬಾಯಿಗೆ ಒಂದು ಕಲ್ಲು ಆನಿಸಿತ್ತು.",
			"39": "<sup>39</sup> ಯೇಸು - ಆ ಕಲ್ಲನ್ನು ತೆಗೆದುಹಾಕಿರಿ ಅನ್ನಲು ತೀರಿಹೋದವನ ಅಕ್ಕನಾದ ಮಾರ್ಥಳು - ಸ್ವಾಮೀ, ಅವನು ಸತ್ತು ನಾಲ್ಕು ದಿವಸವಾಯಿತು; ಈಗ ನಾತ ಹುಟ್ಟಿದೆ ಅಂದಳು.",
			"40": "<sup>40</sup> ಯೇಸು ಆಕೆಗೆ - ನೀನು ನಂಬಿದರೆ ದೇವರ ಮಹಿಮೆಯನ್ನು ಕಾಣುವಿ ಎಂದು ನಾನು ನಿನಗೆ ಹೇಳಲಿಲ್ಲವೇ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"41": "<sup>41</sup> ಆಗ ಕಲ್ಲನ್ನು ತೆಗೆದುಹಾಕಿದರು. ಮತ್ತು ಯೇಸು ಕಣ್ಣೆತ್ತಿ ಮೇಲಕ್ಕೆ ನೋಡಿ - ತಂದೆಯೇ, ನೀನು ನನ್ನ ಪ್ರಾರ್ಥನೆಯನ್ನು ಕೇಳಿದ್ದಕ್ಕೆ ನಿನ್ನನ್ನು ಕೊಂಡಾಡುತ್ತೇನೆ;",
			"42": "<sup>42</sup> ನೀನು ಯಾವಾಗಲೂ ನನ್ನ ಪ್ರಾರ್ಥನೆಯನ್ನು ಕೇಳುವವನಾಗಿದ್ದೀ ಎಂದು ನನಗೆ ಗೊತ್ತೇ ಇದೆ; ಆದರೂ ನೀನೇ ನನ್ನನ್ನು ಕಳುಹಿಸಿಕೊಟ್ಟಿ ಎಂದು ಸುತ್ತಲು ನಿಂತಿರುವ ಜನರು ನಂಬುವಂತೆ ಅವರಿಗೋಸ್ಕರ ಈ ಮಾತುಗಳನ್ನಾಡಿದೆನೆಂದು ಹೇಳಿದನು.",
			"43": "<sup>43</sup> ಅದನ್ನು ಹೇಳಿದ ಮೇಲೆ ದೊಡ್ಡ ಶಬ್ದದಿಂದ - ಲಾಜರನೇ, ಹೊರಗೆ ಬಾ ಎಂದು ಕೂಗಿದನು.",
			"44": "<sup>44</sup> ಸತ್ತಿದ್ದವನು ಹೊರಗೆ ಬಂದನು; ಅವನ ಕೈಕಾಲುಗಳು ಬಟ್ಟೆಗಳಿಂದ ಕಟ್ಟಿದ್ದವು, ಅವನ ಮುಖವು ಕೈಪಾವುಡದಿಂದ ಸುತ್ತಿತ್ತು. ಯೇಸು ಅವರಿಗೆ - ಅವನನ್ನು ಬಿಚ್ಚಿರಿ, ಹೋಗಲಿ ಎಂದು ಹೇಳಿದನು.",
			"45": "<sup>45</sup> ಮರಿಯಳ ಬಳಿಗೆ ಬಂದ ಯೆಹೂದ್ಯರು ಆತನು ಮಾಡಿದ ಕಾರ್ಯವನ್ನು ನೋಡಿದಾಗ ಅವರಲ್ಲಿ ಅನೇಕರು ಆತನನ್ನು ನಂಬಿದರು;",
			"46": "<sup>46</sup> ಆದರೆ ಕೆಲವರು ಫರಿಸಾಯರ ಬಳಿಗೆ ಹೋಗಿ ಯೇಸು ಮಾಡಿದ್ದನ್ನು ಅವರಿಗೆ ತಿಳಿಸಿದರು.",
			"47": "<sup>47</sup> ಆಗ ಮಹಾಯಾಜಕರೂ ಫರಿಸಾಯರೂ ಹಿರೀಸಭೆಯನ್ನು ಕೂಡಿಸಿ - ನಾವು ಮಾಡುವದು ಇದೇನು? ಈ ಮನುಷ್ಯನು ಬಹು ಸೂಚಕಕಾರ್ಯಗಳನ್ನು ಮಾಡುತ್ತಾನಲ್ಲಾ;",
			"48": "<sup>48</sup> ನಾವು ಅವನನ್ನು ಹೀಗೆಯೇ ಬಿಟ್ಟರೆ ಎಲ್ಲರೂ ಅವನನ್ನು ನಂಬಾರು; ಮತ್ತು ರೋಮ್ ರಾಜ್ಯದವರು ಬಂದು ನಮ್ಮ ಸ್ಥಾನವನ್ನೂ ಜನವನ್ನೂ ಸೆಳಕೊಂಡಾರು ಅಂದರು.",
			"49": "<sup>49</sup> ಆದರೆ ಅವರಲ್ಲಿ ಕಾಯಫನೆಂಬವನೊಬ್ಬನು ಆ ವರುಷಕ್ಕೆ ಮಹಾಯಾಜಕನಾಗಿದ್ದು ಅವರಿಗೆ - ನಿಮಗೇನೂ ತಿಳಿಯುವದಿಲ್ಲ;",
			"50": "<sup>50</sup> ಜನವೆಲ್ಲಾ ನಾಶವಾಗುವದಕ್ಕಿಂತ ಒಬ್ಬ ಮನುಷ್ಯನು ಜನಕ್ಕೋಸ್ಕರ ಸಾಯುವದು ನಿಮಗೆ ಹಿತವೆಂದು ನೀವು ಆಲೋಚಿಸುವದೂ ಇಲ್ಲ ಎಂಬದಾಗಿ ಹೇಳಿದನು.",
			"51": "<sup>51</sup> ಇದನ್ನು ಅವನು ತನ್ನಷ್ಟಕ್ಕೆ ತಾನೇ ಹೇಳಲಿಲ್ಲ; ಆದರೆ ತಾನು ಆ ವರುಷದ ಮಹಾಯಾಜಕನಾಗಿದ್ದದರಿಂದ ದೇವಪ್ರೇರಣೆಯಿಂದ ಮಾತಾಡಿ ಯೇಸು ಆ ಜನಕ್ಕೋಸ್ಕರ ಸಾಯುವದಕ್ಕಿದ್ದನೆಂದು ಹೇಳಿದನು.",
			"52": "<sup>52</sup> ಯೇಸು ಆ ಜನಕ್ಕೋಸ್ಕರ ಮಾತ್ರವಲ್ಲದೆ ಚದರಿರುವ ದೇವರ ಮಕ್ಕಳನ್ನು ಒಟ್ಟುಗೂಡಿಸುವದಕ್ಕೆ ಸಹ ಸಾಯುವದಕ್ಕಿದ್ದನು.",
			"53": "<sup>53</sup> ಆ ದಿನದಿಂದ ಆತನನ್ನು ಕೊಲ್ಲುವದಕ್ಕೆ ಆಲೋಚಿಸುತ್ತಿದ್ದರು.",
			"54": "<sup>54</sup> ಹೀಗಿರಲಾಗಿ ಯೇಸು ಇನ್ನೂ ಯೆಹೂದ್ಯರೊಳಗೆ ಬಹಿರಂಗವಾಗಿ ತಿರುಗಾಡದೆ ಅಲ್ಲಿಂದ ಅಡವಿಯ ಹತ್ತಿರದ ಪ್ರದೇಶಕ್ಕೆ ಹೋಗಿ ಎಫ್ರಾಯಿಮ್ ಎಂಬ ಊರಲ್ಲಿ ಶಿಷ್ಯರ ಸಂಗಡ ಇದ್ದನು.",
			"55": "<sup>55</sup> ಆಗ ಯೆಹೂದ್ಯರ ಪಸ್ಕಹಬ್ಬ ಹತ್ತಿರವಾಗಿರಲಾಗಿ ಬಹು ಜನರು ತಮ್ಮನ್ನು ಶುದ್ಧಿಮಾಡಿಕೊಳ್ಳುವದಕ್ಕಾಗಿ ಹಬ್ಬಕ್ಕಿಂತ ಮುಂಚೆ ಹಳ್ಳಿಗಳಿಂದ ಯೆರೂಸಲೇಮಿಗೆ ಬಂದರು.",
			"56": "<sup>56</sup> ಅವರು ಯೇಸುವನ್ನು ಹುಡುಕುತ್ತಾ ದೇವಾಲಯದಲ್ಲಿ ನಿಂತುಕೊಂಡು - ಅವನು ಹಬ್ಬಕ್ಕೆ ಬಾರದೆ ಇದ್ದಾನೋ? ನಿಮಗೆ ಹೇಗೆ ಕಾಣುತ್ತದೆ ಎಂದು ತಮ್ಮಲ್ಲಿ ಮಾತಾಡಿಕೊಳ್ಳುತ್ತಾ ಇದ್ದರು.",
			"57": "<sup>57</sup> ಆದರೆ ಮಹಾಯಾಜಕರೂ ಫರಿಸಾಯರೂ ಆತನನ್ನು ಹಿಡಿಯಬೇಕೆಂಬದಾಗಿ ಯೋಚಿಸಿ - ಅವನಿರುವ ಸ್ಥಳವು ಯಾರಿಗಾದರೂ ತಿಳಿದರೆ ಅದನ್ನು ತಮಗೆ ತಿಳಿಸಬೇಕೆಂದು ಅಪ್ಪಣೆ ಕೊಟ್ಟಿದ್ದರು."
		},
		"12": {
			"1": "<sup>1</sup> ಪಸ್ಕಹಬ್ಬವು ಇನ್ನು ಆರು ದಿವಸ ಮುಂದೆ ಇರಲಾಗಿ ಯೇಸು ಬೇಥಾನ್ಯಕ್ಕೆ ಬಂದನು. ಯೇಸುವು ಸತ್ತವರೊಳಗಿಂದ ಎಬ್ಬಿಸಿದ ಲಾಜರನು ಅಲ್ಲಿದ್ದನು.",
			"2": "<sup>2</sup> ಯೇಸುವಿಗೆ ಅಲ್ಲಿ ಔತಣಮಾಡಿಸಲು ಮಾರ್ಥಳು ನೆರವಾಗಿದ್ದಳು; ಲಾಜರನು ಆತನ ಸಂಗಡ ಊಟಕ್ಕೆ ಕೂತವರಲ್ಲಿ ಒಬ್ಬನು.",
			"3": "<sup>3</sup> ಆಗ ಮರಿಯಳು ಬಹು ಬೆಲೆಯುಳ್ಳ ಅಚ್ಚ ಜಟಾಮಾಂಸಿ ತೈಲವನ್ನು ಒಂದು ಸೇರಷ್ಟು ತಂದು ಯೇಸುವಿನ ಪಾದಕ್ಕೆ ಹಚ್ಚಿ ತನ್ನ ತಲೇಕೂದಲಿನಿಂದ ಆತನ ಪಾದಗಳನ್ನು ಒರಸಿದಳು. ಆ ತೈಲದ ಪರಿಮಳವು ಮನೆಯಲ್ಲೆಲ್ಲಾ ತುಂಬಿಕೊಂಡಿತು.",
			"4": "<sup>4</sup> ಆದರೆ ಆತನ ಶಿಷ್ಯರಲ್ಲಿ ಒಬ್ಬನು, ಅಂದರೆ ಆತನನ್ನು ಹಿಡುಕೊಡುವದಕ್ಕಿದ್ದ ಇಸ್ಕರಿಯೋತ ಯೂದನೆಂಬವನು -",
			"5": "<sup>5</sup> ಯಾಕೆ ಈ ತೈಲವನ್ನು ಮುನ್ನೂರು ಹಣಕ್ಕೆ ಮಾರಿ ಬಡವರಿಗೆ ಕೊಡಲಿಲ್ಲ ಅಂದನು.",
			"6": "<sup>6</sup> ಅವನು ಬಡವರಿಗೋಸ್ಕರ ಚಿಂತಿಸಿ ಇದನ್ನು ಹೇಳಲಿಲ್ಲ; ಅವನು ಕಳ್ಳನಾಗಿದ್ದು ತನ್ನ ವಶದಲ್ಲಿದ್ದ ಅವರ ಹಣದ ಚೀಲದಲ್ಲಿ ಹಾಕಿದ್ದನ್ನು ತಕ್ಕೊಳ್ಳುವವನಾಗಿದ್ದದರಿಂದಲೇ ಹೇಳಿದನು.",
			"7": "<sup>7</sup> ಆಗ ಯೇಸು - ಈಕೆಯನ್ನು ಬಿಡು; ನನ್ನನ್ನು ಹೂಣಿಡುವ ದಿವಸಕ್ಕಾಗಿ ಅದನ್ನು ಇಟ್ಟುಕೊಳ್ಳಲಿ.",
			"8": "<sup>8</sup> ಬಡವರು ಯಾವಾಗಲೂ ನಿಮ್ಮ ಬಳಿಯಲ್ಲಿ ಇರುತ್ತಾರೆ, ಆದರೆ ನಾನು ಯಾವಾಗಲೂ ನಿಮ್ಮ ಬಳಿಯಲ್ಲಿ ಇರುವದಿಲ್ಲ ಅಂದನು.",
			"9": "<sup>9</sup> ಯೆಹೂದ್ಯರ ಜನಸಮೂಹವು ಆತನು ಅಲ್ಲಿ ಇದ್ದಾನೆಂದು ತಿಳಿದು ಯೇಸುವನ್ನು ನೋಡುವದಕ್ಕಾಗಿ ಮಾತ್ರವಲ್ಲದೆ ಆತನು ಸತ್ತವರೊಳಗಿಂದ ಎಬ್ಬಿಸಿದ ಲಾಜರನನ್ನು ಸಹ ನೋಡಬೇಕೆಂಬದಾಗಿ ಬಂದರು.",
			"10": "<sup>10</sup> ಆದರೆ ಮಹಾಯಾಜಕರು ಲಾಜರನನ್ನೂ ಕೊಲ್ಲಬೇಕೆಂದು ಆಲೋಚಿಸಿದರು.",
			"11": "<sup>11</sup> ಯಾಕಂದರೆ ಅವನ ದೆಸೆಯಿಂದ ಯೆಹೂದ್ಯರಲ್ಲಿ ಅನೇಕರು ಹೋಗಿ ಯೇಸುವನ್ನು ನಂಬುವವರಾದರು.",
			"12": "<sup>12</sup> ಮರುದಿವಸ ಜಾತ್ರೆಗೆ ಬಂದಿದ್ದ ಜನಸಮೂಹವು ಯೇಸು ಯೆರೂಸಲೇಮಿಗೆ ಬರುತ್ತಾನೆಂದು ಕೇಳಿ ಖರ್ಜೂರದ ಗರಿಗಳನ್ನು ತಕ್ಕೊಂಡು",
			"13": "<sup>13</sup> ಆತನನ್ನು ಎದುರುಗೊಳ್ಳುವದಕ್ಕೆ ಹೊರಗೆ ಬಂದು - ಜಯ, ಕರ್ತನ ಹೆಸರಿನಲ್ಲಿ ಬರುವವನಿಗೆ ಆಶೀರ್ವಾದ; ಇಸ್ರಾಯೇಲಿನ ಅರಸನಿಗೆ ಆಶೀರ್ವಾದ ಎಂದು ಆರ್ಭಟಿಸಿದರು.",
			"14": "<sup>14</sup> ಮತ್ತು ಯೇಸು ಕತ್ತೆಮರಿಯನ್ನು ತರಿಸಿಕೊಂಡು ಅದರ ಮೇಲೆ ಕೂತುಕೊಂಡನು. ಇದರಿಂದ -",
			"15": "<sup>15</sup> ಚೀಯೋನ್ ನಗರಿಯೇ, ಹೆದರಬೇಡ; ಇಗೋ, ನಿನ್ನ ಅರಸನು ಕತ್ತೆಮರಿಯ ಮೇಲೆ ಕೂತುಕೊಂಡು ಬರುತ್ತಾನೆ ಎಂಬ ಶಾಸ್ತ್ರದ ಮಾತು ನೆರವೇರಿತು.",
			"16": "<sup>16</sup> ಇದು ಆತನ ಶಿಷ್ಯರಿಗೆ ಮೊದಲು ತಿಳಿದಿರಲಿಲ್ಲ. ಆದರೆ ಯೇಸು ತನ್ನ ಮಹಿಮೆಯ ಪದವಿಗೆ ಬಂದ ಮೇಲೆ ಇದು ಆತನ ವಿಷಯವಾಗಿಯೇ ಬರೆದದೆ ಎಂದೂ ಆತನಿಗೆ ನಾವು ಅದನ್ನೆಲ್ಲಾ ಮಾಡಿದೆವೆಂದೂ ಅವರ ನೆನಪಿಗೆ ಬಂತು.",
			"17": "<sup>17</sup> ಇದಲ್ಲದೆ ಆತನು ಲಾಜರನನ್ನು ಸಮಾಧಿಯೊಳಗಿಂದ ಕರೆದು ಬದುಕಿಸಿದ ವೇಳೆಯಲ್ಲಿ ಆತನ ಸಂಗಡ ಇದ್ದ ಜನರು ಅದನ್ನು ಸಾರುತ್ತಿದ್ದರು.",
			"18": "<sup>18</sup> ಆತನು ಈ ಸೂಚಕಕಾರ್ಯವನ್ನು ಮಾಡಿದನೆಂದು ಕೇಳಿದ ಕಾರಣದಿಂದಲೂ ಜನರು ಆತನನ್ನು ಎದುರುಗೊಳ್ಳುವದಕ್ಕೆ ಹೋಗಿದ್ದರು.",
			"19": "<sup>19</sup> ಹೀಗಿರಲಾಗಿ ಫರಿಸಾಯರು - ನಿಮ್ಮ ಯತ್ನವೇನೂ ಸಾಗಲಿಲ್ಲ, ನೋಡಿರಿ; ಲೋಕವೆಲ್ಲಾ ಆತನ ಹಿಂದೆ ಹೋಯಿತಲ್ಲಾ ಎಂದು ಒಬ್ಬರಿಗೊಬ್ಬರು ಮಾತಾಡಿಕೊಂಡರು.",
			"20": "<sup>20</sup> ಇದಲ್ಲದೆ ದೇವಾರಾಧನೆ ಮಾಡಬೇಕೆಂದು ಜಾತ್ರೆಗೆ ಬಂದವರಲ್ಲಿ ಕೆಲವು ಮಂದಿ ಗ್ರೀಕರಿದ್ದರು.",
			"21": "<sup>21</sup> ಇವರು ಗಲಿಲಾಯದಲ್ಲಿರುವ ಬೇತ್ಸಾಯಿದ ಊರಿನ ಫಿಲಿಪ್ಪನ ಬಳಿಗೆ ಬಂದು - ಅಯ್ಯಾ, ಯೇಸುವನ್ನು ನೋಡಬೇಕೆಂದಿದ್ದೇವೆಂದು ಅವನನ್ನು ಬೇಡಿಕೊಂಡರು.",
			"22": "<sup>22</sup> ಫಿಲಿಪ್ಪನು ಬಂದು ಅಂದ್ರೆಯನಿಗೆ ಹೇಳಿದನು; ಅಂದ್ರೆಯನೂ ಫಿಲಿಪ್ಪನೂ ಯೇಸುವಿಗೆ ಹೇಳಿದರು.",
			"23": "<sup>23</sup> ಆಗ ಯೇಸು ಅವರಿಗೆ ಉತ್ತರಕೊಟ್ಟು ಹೇಳಿದ್ದೇನಂದರೆ - ಮನುಷ್ಯಕುಮಾರನು ತನ್ನ ಮಹಿಮೆಯ ಪದವಿಯನ್ನು ಹೊಂದುವ ಸಮಯ ಬಂದದೆ.",
			"24": "<sup>24</sup> ನಿಮಗೆ ನಿಜನಿಜವಾಗಿ ಹೇಳುತ್ತೇನೆ, ಗೋದಿಯ ಕಾಳು ಭೂಮಿಯಲ್ಲಿ ಬಿದ್ದು ಸಾಯದಿದ್ದರೆ ಒಂದೇಯಾಗಿ ಉಳಿಯುವದು; ಸತ್ತರೆ ಬಹಳ ಫಲಕೊಡುವದು.",
			"25": "<sup>25</sup> ತನ್ನ ಪ್ರಾಣದ ಮೇಲೆ ಮಮತೆಯಿಡುವವನು ಅದನ್ನು ಕಳಕೊಳ್ಳುವನು, ಇಹಲೋಕದಲ್ಲಿ ತನ್ನ ಪ್ರಾಣವನ್ನು ಹಗೆಮಾಡುವವನು ನಿತ್ಯಜೀವಕ್ಕಾಗಿ ಅದನ್ನು ಕಾಪಾಡಿಕೊಳ್ಳುವನು.",
			"26": "<sup>26</sup> ಯಾವನಾದರೂ ನನ್ನ ಸೇವೆಯನ್ನು ಮಾಡುವವನಾದರೆ ನನ್ನ ಹಿಂದೆ ಬರಲಿ; ಮತ್ತು ಎಲ್ಲಿ ನಾನು ಇರುತ್ತೇನೋ ಅಲ್ಲಿ ನನ್ನ ಸೇವಕನು ಸಹ ಇರುವನು. ಯಾವನಾದರೂ ನನ್ನ ಸೇವೆಯನ್ನು ಮಾಡುವವನಾದರೆ ತಂದೆಯು ಅವನಿಗೆ ಬಹುಮಾನ ಮಾಡುವನು.",
			"27": "<sup>27</sup> ಈಗ ನನ್ನ ಪ್ರಾಣವು ತತ್ತರಿಸುತ್ತದೆ; ಮತ್ತು ನಾನೇನು ಹೇಳಲಿ? ತಂದೆಯೇ, ಈ ಕಾಲದೊಳಗಿಂದ ನನ್ನನ್ನು ತಪ್ಪಿಸು. ಆದರೆ ಇದಕ್ಕಾಗಿಯೇ ಈ ಕಾಲ ಸೇರಿದೆನು.",
			"28": "<sup>28</sup> ತಂದೆಯೇ, ನಿನ್ನ ಹೆಸರನ್ನು ಮಹಿಮೆಪಡಿಸಿಕೋ ಅಂದನು. ಅದಕ್ಕೆ - ಮಹಿಮೆಪಡಿಸಿದ್ದೇನೆ, ತಿರಿಗಿ ಮಹಿಮೆಪಡಿಸುವೆನು ಎಂದು ಆಕಾಶವಾಣಿಯಾಯಿತು.",
			"29": "<sup>29</sup> ಅಲ್ಲಿ ನಿಂತುಕೊಂಡಿದ್ದ ಜನರು ಅದನ್ನು ಕೇಳಿ - ಗುಡುಗಿತು ಅಂದರು. ಇನ್ನು ಕೆಲವರು - ದೇವದೂತನು ಈತನ ಸಂಗಡ ಮಾತಾಡಿದನು ಅಂದರು.",
			"30": "<sup>30</sup> ಆದರೆ ಯೇಸು ಅವರಿಗೆ - ಈ ಆಕಾಶವಾಣಿ ನನಗಾಗಿ ಆಗಲಿಲ್ಲ; ನಿಮಗಾಗಿ ಆಯಿತು.",
			"31": "<sup>31</sup> ಈಗ ಈ ಲೋಕಕ್ಕೆ ತೀರ್ಪು ಆಗುತ್ತದೆ; ಈಗ ಇಹಲೋಕಾಧಿಪತಿಯು ಹೊರಗೆ ನೂಕಲ್ಪಡುವನು.",
			"32": "<sup>32</sup> ಆದರೆ ನಾನು ಭೂಮಿಯಲ್ಲಿಂದ ಮೇಲಕ್ಕೆ ಎತ್ತಲ್ಪಟ್ಟಾಗ ಎಲ್ಲರನ್ನೂ ನನ್ನ ಬಳಿಗೆ ಎಳಕೊಳ್ಳುವೆನು ಎಂದು ಹೇಳಿದನು.",
			"33": "<sup>33</sup> ಈ ಮಾತಿನಿಂದ ತಾನು ಇಂಥ ಸಾವು ಸಾಯತಕ್ಕವನೆಂದು ಸೂಚಿಸಿದನು.",
			"34": "<sup>34</sup> ಅದಕ್ಕೆ ಆ ಜನರು - ಕ್ರಿಸ್ತನು ಸದಾಕಾಲವೂ ಇರುತ್ತಾನೆಂದು ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿ ಕೇಳಿದ್ದೇವೆ; ಹಾಗಾದರೆ ಮನುಷ್ಯಕುಮಾರನು ಮೇಲಕ್ಕೆ ಎತ್ತಲ್ಪಡಬೇಕೆಂದು ನೀನು ಹೇಳುವದು ಹೇಗೆ? ಆ ಮನುಷ್ಯಕುಮಾರನು ಯಾರು? ಅಂದರು.",
			"35": "<sup>35</sup> ಯೇಸು ಅವರಿಗೆ - ಇನ್ನು ಸ್ವಲ್ಪ ಕಾಲವೇ ಬೆಳಕು ನಿಮ್ಮಲ್ಲಿ ಇರುತ್ತದೆ; ಕತ್ತಲೆಯು ನಿಮ್ಮನ್ನು ಮುಸುಕಿಕೊಳ್ಳದಂತೆ ಬೆಳಕು ನಿಮಗೆ ಇರುವಾಗಲೇ ಸಂಚಾರಮಾಡಿರಿ. ಕತ್ತಲೆಯಲ್ಲಿ ಸಂಚಾರಮಾಡುವವನಿಗೆ ತಾನು ಎಲ್ಲಿ ಹೋಗುತ್ತೇನೆಂದು ತಿಳಿಯದು.",
			"36": "<sup>36</sup> ನಿಮಗೆ ಬೆಳಕು ಇರುವಾಗಲೇ ಬೆಳಕನ್ನು ನಂಬಿರಿ; ನಂಬಿದರೆ ನೀವು ಬೆಳಕಿನವರಾಗುವಿರಿ ಎಂದು ಹೇಳಿದನು. ಇದನ್ನು ಯೇಸು ಹೇಳಿದ ಮೇಲೆ ಅವರನ್ನು ಬಿಟ್ಟುಹೋಗಿ ಅಡಗಿಕೊಂಡನು.",
			"37": "<sup>37</sup> ಆತನು ಅವರ ಮುಂದೆ ಅಷ್ಟು ಸೂಚಕಕಾರ್ಯಗಳನ್ನು ಮಾಡಿದರೂ ಅವರು ಆತನನ್ನು ನಂಬಲಿಲ್ಲ.",
			"38": "<sup>38</sup> ಇದರಿಂದ ಪ್ರವಾದಿಯಾದ ಯೆಶಾಯನು ನುಡಿದ ಮಾತು ನೆರವೇರಿತು; ಅದೇನಂದರೆ - ಕರ್ತನೇ, ನಮ್ಮ ಉಪದೇಶವನ್ನು ಯಾರು ನಂಬಿದರು? ಕರ್ತನ ಬಾಹುವು ಯಾರಿಗೆ ಗೋಚರವಾಯಿತು? ಎಂಬದು.",
			"39": "<sup>39</sup> ಅವರು ನಂಬಲಾರದೆ ಹೋದದ್ದಕ್ಕೆ ಯೆಶಾಯನು ಮತ್ತೊಂದು ಮಾತಿನಲ್ಲಿ ಕಾರಣವನ್ನು ಸೂಚಿಸಿದ್ದಾನೆ; ಅದೇನಂದರೆ -",
			"40": "<sup>40</sup> ಅವರು ಕಣ್ಣಿನಿಂದ ಕಾಣದೆ ಹೃದಯದಿಂದ ಗ್ರಹಿಸದೆ ತಿರುಗಿಕೊಳ್ಳದೆ ತನ್ನಿಂದ ಸ್ವಸ್ಥತೆಯನ್ನು ಹೊಂದದೆ ಇರುವಂತೆ ಆತನು ಅವರ ಕಣ್ಣುಗಳನ್ನು ಕುರುಡುಮಾಡಿ ಅವರ ಹೃದಯವನ್ನು ಕಠಿನಮಾಡಿದನು ಎಂಬದೇ.",
			"41": "<sup>41</sup> ಯೆಶಾಯನು ಆತನ ಮಹಿಮೆಯನ್ನು ನೋಡಿದ್ದರಿಂದ ಆತನ ವಿಷಯದಲ್ಲಿ ಮಾತಾಡುತ್ತಿರುವಾಗ ಆ ಮಾತನ್ನು ನುಡಿದನು.",
			"42": "<sup>42</sup> ಆದರೂ ಹಿರೀಸಭೆಯವರಲ್ಲಿಯೂ ಅನೇಕರು ಆತನನ್ನು ನಂಬಿದರು. ಆದರೆ ತಮಗೆ ಸಭೆಯಿಂದ ಬಹಿಷ್ಕಾರವಾದೀತೆಂದು ಫರಿಸಾಯರಿಗೆ ಅಂಜಿಕೊಂಡು ಅದನ್ನು ಜನರ ಮುಂದೆ ಹೇಳಲಿಲ್ಲ.",
			"43": "<sup>43</sup> ಯಾಕಂದರೆ ದೇವರಿಂದ ಬರುವ ಮಾನಕ್ಕಿಂತ ಮನುಷ್ಯರಿಂದ ಬರುವ ಮಾನವೇ ಅವರಿಗೆ ಇಷ್ಟವಾಗಿತ್ತು.",
			"44": "<sup>44</sup> ಯೇಸು ಕೂಗಿ ಹೇಳಿದ ಮಾತು ಏನಂದರೆ - ನನ್ನನ್ನು ನಂಬಿದವನು ನನ್ನನ್ನೇ ನಂಬುವವನಲ್ಲ; ನನ್ನನ್ನು ಕಳುಹಿಸಿಕೊಟ್ಟಾತನನ್ನು ನಂಬುವವನಾಗಿದ್ದಾನೆ.",
			"45": "<sup>45</sup> ಮತ್ತು ನನ್ನನ್ನು ನೋಡುವವನು ನನ್ನನ್ನು ಕಳುಹಿಸಿಕೊಟ್ಟಾತನನ್ನು ನೋಡುವವನಾಗಿದ್ದಾನೆ.",
			"46": "<sup>46</sup> ನನ್ನನ್ನು ನಂಬುವ ಒಬ್ಬನಾದರೂ ಕತ್ತಲೆಯಲ್ಲಿ ಇರಬಾರದೆಂದು ನಾನು ಬೆಳಕಾಗಿ ಲೋಕಕ್ಕೆ ಬಂದಿದ್ದೇನೆ.",
			"47": "<sup>47</sup> ಯಾವನಾದರೂ ನನ್ನ ಮಾತುಗಳನ್ನು ಕೇಳಿದರೂ ಕೈಕೊಂಡು ನಡೆಯದೆ ಹೋದರೆ ನಾನೇ ಅವನಿಗೆ ತೀರ್ಪುಮಾಡುವದಿಲ್ಲ; ಲೋಕಕ್ಕೆ ತೀರ್ಪುಮಾಡುವದಕ್ಕಾಗಿ ನಾನು ಬಂದಿಲ್ಲ; ಲೋಕವನ್ನು ರಕ್ಷಿಸುವದಕ್ಕಾಗಿ ಬಂದಿದ್ದೇನೆ.",
			"48": "<sup>48</sup> ನನ್ನನ್ನು ಲಕ್ಷ್ಯಮಾಡದೆ ನನ್ನ ಮಾತುಗಳನ್ನು ಅಂಗೀಕರಿಸದೆ ಇರುವವನಿಗೆ ತೀರ್ಪುಮಾಡುವಂಥದು ಒಂದು ಇದೆ, ಅದು ಯಾವದಂದರೆ, ನಾನು ಆಡಿದ ಮಾತು; ಅದೇ ಅವನಿಗೆ ಕಡೇ ದಿನದಲ್ಲಿ ತೀರ್ಪುಮಾಡುವದು.",
			"49": "<sup>49</sup> ಯಾಕಂದರೆ ನನ್ನಷ್ಟಕ್ಕೆ ನಾನೇ ಮಾತಾಡಿದವನಲ್ಲ; ನನ್ನನ್ನು ಕಳುಹಿಸಿಕೊಟ್ಟ ತಂದೆಯೇ - ನೀನು ಇಂಥಿಂಥದನ್ನು ಹೇಳಬೇಕು, ಹೀಗೆ ಹೀಗೆ ಮಾತಾಡಬೇಕು ಎಂಬದಾಗಿ ನನಗೆ ಆಜ್ಞೆಕೊಟ್ಟಿದ್ದಾನೆ.",
			"50": "<sup>50</sup> ಆತನ ಆಜ್ಞೆ ನಿತ್ಯಜೀವವಾಗಿದೆ ಎಂದು ಬಲ್ಲೆನು. ಆದದರಿಂದ ನಾನು ಮಾತಾಡುವದನ್ನೆಲ್ಲಾ ತಂದೆ ನನಗೆ ಹೇಳಿದ ಮೇರೆಗೆ ಮಾತಾಡುತ್ತೇನೆ ಅಂದನು."
		},
		"13": {
			"1": "<sup>1</sup> ಪಸ್ಕಹಬ್ಬದ ಮುಂದೆ ಯೇಸು ತಾನು ಈ ಲೋಕವನ್ನು ಬಿಟ್ಟು ತಂದೆಯ ಬಳಿಗೆ ಹೋಗಬೇಕಾದ ಕಾಲ ಬಂತೆಂದು ತಿಳುಕೊಂಡು ಲೋಕದಲ್ಲಿರುವ ತನ್ನವರನ್ನು ಪ್ರೀತಿಸಿ ಪರಿಪೂರ್ಣವಾಗಿ ಅವರನ್ನು ಪ್ರೀತಿಸುತ್ತಾ ಬಂದನು.",
			"2": "<sup>2</sup> ಅವರು ಊಟಕ್ಕೆ ಕೂತುಕೊಂಡಿದ್ದರು; ಅಷ್ಟರೊಳಗೆ ಸೈತಾನನು ಸೀಮೋನನ ಮಗನಾದ ಇಸ್ಕರಿಯೋತ ಯೂದನ ಮನಸ್ಸಿನಲ್ಲಿ ಯೇಸುವನ್ನು ಹಿಡುಕೊಡಬೇಕೆಂಬ ಆಲೋಚನೆಯನ್ನು ಹುಟ್ಟಿಸಿದ್ದನು.",
			"3": "<sup>3</sup> ಯೇಸು ತನ್ನ ಕೈಯಲ್ಲಿ ತಂದೆ ಎಲ್ಲವನ್ನು ಕೊಟ್ಟಿದ್ದಾನೆಂತಲೂ ತಾನು ದೇವರ ಬಳಿಯಿಂದ ಹೊರಟುಬಂದಿದ್ದು ಮತ್ತೆ ದೇವರ ಬಳಿಗೆ ಹೋಗುತ್ತೇನೆಂತಲೂ ತಿಳುಕೊಂಡವನಾಗಿ ಊಟವನ್ನು ಬಿಟ್ಟು ಎದ್ದು",
			"4": "<sup>4</sup> ಹೊದ್ದಿದ್ದ ಮೇಲ್ಹೊದಿಕೆಯನ್ನು ತೆಗೆದಿಟ್ಟು ಕೈಪಾವುಡವನ್ನು ತಕ್ಕೊಂಡು ನಡುವಿಗೆ ಕಟ್ಟಿಕೊಂಡನು.",
			"5": "<sup>5</sup> ಆಗ ಬೋಗುಣಿಯಲ್ಲಿ ನೀರು ಹಾಕಿಕೊಂಡು ಶಿಷ್ಯರ ಕಾಲುಗಳನ್ನು ತೊಳೆಯುವದಕ್ಕೂ ನಡುವಿಗೆ ಕಟ್ಟಿಕೊಂಡಿದ್ದ ಕೈಪಾವುಡದಿಂದ ಒರಸುವದಕ್ಕೂ ಪ್ರಾರಂಭಿಸಿದನು.",
			"6": "<sup>6</sup> ಹೀಗೆ ಸೀಮೋನ ಪೇತ್ರನ ಬಳಿಗೆ ಬರಲು ಇವನು ಆತನಿಗೆ - ಸ್ವಾಮೀ, ನೀನು ನನ್ನ ಕಾಲುಗಳನ್ನು ತೊಳೆಯಬೇಕೇ? ಎಂದು ಹೇಳಿದನು.",
			"7": "<sup>7</sup> ಅದಕ್ಕೆ ಯೇಸು - ನಾನು ಮಾಡುವದು ಈಗ ನಿನಗೆ ತಿಳಿಯುವದಿಲ್ಲ, ಇನ್ನು ಮೇಲೆ ನಿನಗೆ ತಿಳಿಯುವದು ಅಂದನು.",
			"8": "<sup>8</sup> ಪೇತ್ರನು - ನೀನು ನನ್ನ ಕಾಲುಗಳನ್ನು ಎಂದಿಗೂ ತೊಳೆಯಬಾರದು ಎಂದು ಹೇಳಿದ್ದಕ್ಕೆ ಯೇಸು - ನಾನು ನಿನ್ನನ್ನು ತೊಳೆಯದಿದ್ದರೆ ನನ್ನ ಸಂಗಡ ನಿನಗೆ ಪಾಲಿಲ್ಲ ಅಂದನು.",
			"9": "<sup>9</sup> ಸೀಮೋನ ಪೇತ್ರನು - ಸ್ವಾಮೀ, ನನ್ನ ಕಾಲುಗಳನ್ನು ಮಾತ್ರವಲ್ಲದೆ ಕೈಗಳನ್ನೂ ತಲೆಯನ್ನೂ ಸಹ ತೊಳೆಯಬೇಕು ಅಂದನು.",
			"10": "<sup>10</sup> ಯೇಸು ಅವನಿಗೆ - ಸ್ನಾನಮಾಡಿಕೊಂಡವನ ಕಾಲುಗಳನ್ನು ತೊಳೆಯುವದಲ್ಲದೆ ಬೇರೇನೂ ತೊಳೆಯಬೇಕಾಗಿರುವದಿಲ್ಲ, ಅವನ ಮೈಯೆಲ್ಲಾ ಶುದ್ಧವಾಗಿದೆ. ನೀವೂ ಶುದ್ಧರಾಗಿದ್ದೀರಿ; ಆದರೆ ನಿಮ್ಮಲ್ಲಿ ಎಲ್ಲರೂ ಶುದ್ಧರಲ್ಲವೆಂದು ಹೇಳಿದನು.",
			"11": "<sup>11</sup> ಯೇಸು ತನ್ನನ್ನು ಹಿಡುಕೊಡುವವನು ಇಂಥವನೇ ಎಂದು ತಿಳಿದಿದ್ದದರಿಂದ ನಿಮ್ಮಲ್ಲಿ ಎಲ್ಲರೂ ಶುದ್ಧರಲ್ಲವೆಂದನು.",
			"12": "<sup>12</sup> ಆತನು ಅವರ ಕಾಲುಗಳನ್ನು ತೊಳೆದ ಮೇಲೆ ತನ್ನ ಮೇಲ್ಹೊದಿಕೆಯನ್ನು ಹಾಕಿಕೊಂಡು ತಿರಿಗಿ ಕೂತುಕೊಂಡು ಅವರಿಗೆ ಹೇಳಿದ್ದೇನಂದರೆ - ನಾನು ನಿಮಗೆ ಮಾಡಿದ್ದು ಏನೆಂದು ಗೊತ್ತಾಯಿತೋ?",
			"13": "<sup>13</sup> ನೀವು ನನ್ನನ್ನು ಗುರುವೆಂದೂ ಕರ್ತನೆಂದೂ ಕರೆಯುತ್ತೀರಿ; ನೀವು ಕರೆಯುವದು ಸರಿ; ನಾನು ಅಂಥವನೇ ಹೌದು.",
			"14": "<sup>14</sup> ಕರ್ತನೂ ಗುರುವೂ ಆಗಿರುವ ನಾನು ನಿಮ್ಮ ಕಾಲುಗಳನ್ನು ತೊಳೆದಿರಲಾಗಿ ನೀವು ಸಹ ಒಬ್ಬರ ಕಾಲನ್ನು ಒಬ್ಬರು ತೊಳೆಯುವ ಹಂಗಿನವರಾಗಿದ್ದೀರಿ.",
			"15": "<sup>15</sup> ನಾನು ನಿಮಗೆ ಮಾಡಿದ ಮೇರೆಗೆ ನೀವು ಸಹ ಮಾಡುವಂತೆ ನಿಮಗೆ ಮಾದರಿಯನ್ನು ತೋರಿಸಿದ್ದೇನೆ.",
			"16": "<sup>16</sup> ನಿಮಗೆ ನಿಜನಿಜವಾಗಿ ಹೇಳುತ್ತೇನೆ, ದಣಿಗಿಂತ ಆಳು ದೊಡ್ಡವನಲ್ಲ, ಕಳುಹಿಸಲ್ಪಟ್ಟವನು ಕಳುಹಿಸಿದವನಿಗಿಂತ ದೊಡ್ಡವನಲ್ಲ.",
			"17": "<sup>17</sup> ನೀವು ಇದನ್ನು ತಿಳುಕೊಂಡು ಇದರಂತೆ ಮಾಡಿದರೆ ನೀವು ಧನ್ಯರು.",
			"18": "<sup>18</sup> ನಾನು ನಿಮ್ಮೆಲ್ಲರನ್ನು ಕುರಿತು ಈ ಮಾತನ್ನು ಹೇಳಲಿಲ್ಲ; ನಾನು ಆದುಕೊಂಡವರನ್ನು ನಾನು ಬಲ್ಲೆನು; ಆದರೆ - ನನ್ನ ಕೂಡ ಊಟಮಾಡುವವನೇ ನನಗೆ ಕಾಲನ್ನು ಅಡ್ಡಗೊಟ್ಟಿದ್ದಾನೆ ಎಂಬ ಶಾಸ್ತ್ರದ ಮಾತು ನೆರವೇರಬೇಕಾಗಿದೆ.",
			"19": "<sup>19</sup> ಅದು ನಡೆಯುವಾಗ ನನ್ನನ್ನೇ ಆತನೆಂದು ನೀವು ನಂಬುವಂತೆ ಅದು ನಡೆಯುವದಕ್ಕಿಂತ ಮುಂಚೆ ಅದನ್ನು ಇಂದಿನಿಂದ ನಿಮಗೆ ಹೇಳುತ್ತೇನೆ.",
			"20": "<sup>20</sup> ನಿಮಗೆ ನಿಜನಿಜವಾಗಿ ಹೇಳುತ್ತೇನೆ, ನಾನು ಕಳುಹಿಸಿಕೊಟ್ಟವನನ್ನು ಅಂಗೀಕರಿಸುವವನು ನನ್ನನ್ನು ಅಂಗೀಕರಿಸುವವನಾಗಿದ್ದಾನೆ; ನನ್ನನ್ನು ಅಂಗೀಕರಿಸುವವನು ನನ್ನನ್ನು ಕಳುಹಿಸಿಕೊಟ್ಟಾತನನ್ನು ಅಂಗೀಕರಿಸುವವನಾಗಿದ್ದಾನೆ.",
			"21": "<sup>21</sup> ಇದನ್ನು ಹೇಳಿ ಯೇಸು ಆತ್ಮದಲ್ಲಿ ತತ್ತರಗೊಂಡು - ನಿಮಗೆ ನಿಜನಿಜವಾಗಿ ಹೇಳುತ್ತೇನೆ, ನಿಮ್ಮಲ್ಲಿ ಒಬ್ಬನು ನನ್ನನ್ನು ಹಿಡುಕೊಡುವನೆಂದು ಸ್ಪಷ್ಟವಾಗಿ ಹೇಳಿದನು.",
			"22": "<sup>22</sup> ಈತನು ಯಾರ ವಿಷಯವಾಗಿ ಈ ಮಾತನ್ನು ಹೇಳುತ್ತಾನೆಂದು ಶಿಷ್ಯರು ಅನುಮಾನಪಟ್ಟು ಒಬ್ಬರನ್ನೊಬ್ಬರು ನೋಡುವವರಾದರು.",
			"23": "<sup>23</sup> ಶಿಷ್ಯರೊಳಗೆ ಯೇಸುವಿಗೆ ಪ್ರಿಯನಾಗಿದ್ದ ಒಬ್ಬನು ಆತನ ಎದೆಯ ಹತ್ತರ ಒರಗಿಕೊಂಡಿದ್ದನು;",
			"24": "<sup>24</sup> ಇವನಿಗೆ ಸೀಮೋನ ಪೇತ್ರನು ಸನ್ನೆಮಾಡಿ - ಯಾರನ್ನು ಕುರಿತು ಮಾತಾಡಿದನು, ತಿಳಿಸು ಎಂದು ಹೇಳಿದನು.",
			"25": "<sup>25</sup> ಅವನು ಹಾಗೆಯೇ ಯೇಸುವಿನ ಎದೆಯ ಕಡೆಗೆ ಬಾಗಿ - ಸ್ವಾಮೀ, ಅವನು ಯಾರು? ಅಂತ ಕೇಳಿದನು.",
			"26": "<sup>26</sup> ಅದಕ್ಕೆ ಯೇಸು - ನಾನು ಈ ತುತ್ತನ್ನು ಅದ್ದಿ ಯಾರಿಗೆ ಕೊಡುತ್ತೇನೋ ಅವನೇ ಎಂದು ಹೇಳಿ ಆ ತುತ್ತನ್ನು ಅದ್ದಿ ತೆಗೆದು ಸೀಮೋನನ ಮಗನಾದ ಇಸ್ಕರಿಯೋತ ಯೂದನಿಗೆ ಕೊಟ್ಟನು.",
			"27": "<sup>27</sup> ಅವನು ಆ ತುತ್ತನ್ನು ತೆಗೆದುಕೊಂಡ ಕೂಡಲೆ ಸೈತಾನನು ಅವನೊಳಗೆ ಹೊಕ್ಕನು. ಆಗ ಯೇಸು ಅವನಿಗೆ - ನೀನು ಮಾಡುವದನ್ನು ಬೇಗನೆ ಮಾಡಿಬಿಡು ಎಂದು ಹೇಳಿದನು.",
			"28": "<sup>28</sup> ಆತನು ಅವನಿಗೆ ಇದನ್ನು ಯಾತಕ್ಕಾಗಿ ಹೇಳಿದನೆಂಬದು ಊಟಕ್ಕೆ ಕೂತವರಲ್ಲಿ ಯಾರಿಗೂ ಗೊತ್ತಾಗಲಿಲ್ಲ;",
			"29": "<sup>29</sup> ಅವರಲ್ಲಿ ಕೆಲವರು - ಯೂದನ ವಶದಲ್ಲಿ ಹಣದ ಚೀಲವಿದ್ದದರಿಂದ ಹಬ್ಬಕ್ಕಾಗಿ ನಮಗೆ ಬೇಕಾದದ್ದನ್ನು ಕೊಂಡುಕೋ ಎಂಬದಾಗಿಯೋ ಬಡವರಿಗೆ ಏನಾದರೂ ಕೊಡು ಎಂಬದಾಗಿಯೋ ಯೇಸು ಅವನಿಗೆ ಹೇಳುತ್ತಾನೆ ಎಂದು ನೆನಸಿದರು.",
			"30": "<sup>30</sup> ಅವನು ಆ ತುತ್ತನ್ನು ತೆಗೆದುಕೊಂಡ ಕೂಡಲೇ ಹೊರಗೆ ಹೋದನು. ಆಗ ರಾತ್ರಿಯಾಗಿತ್ತು.",
			"31": "<sup>31</sup> ಅವನು ಹೊರಗೆ ಹೋದ ಮೇಲೆ ಯೇಸು - ಈಗ ಮನುಷ್ಯಕುಮಾರನು ಮಹಿಮೆಪಟ್ಟಿದ್ದಾನೆ, ಮತ್ತು ದೇವರು ಆತನಲ್ಲಿ ಮಹಿಮೆಪಟ್ಟಿದ್ದಾನೆ.",
			"32": "<sup>32</sup> ದೇವರು ಆತನಲ್ಲಿ ಮಹಿಮೆಪಟ್ಟಿರಲಾಗಿ ತನ್ನಲ್ಲಿ ದೇವರು ಆತನನ್ನೂ ಮಹಿಮೆಪಡಿಸುವನು, ಕೂಡಲೇ ಮಹಿಮೆಪಡಿಸುವನು.",
			"33": "<sup>33</sup> ಪ್ರಿಯ ಮಕ್ಕಳೇ, ಇನ್ನು ಸ್ವಲ್ಪಕಾಲವೇ ನಿಮ್ಮ ಸಂಗಡ ಇರುತ್ತೇನೆ. ನನ್ನನ್ನು ಹುಡುಕುವಿರಿ, ಆದರೆ ನಾನು ಹೋಗುವಲ್ಲಿಗೆ ನೀವು ಬರಲಾರಿರಿ ಎಂದು ನಾನು ಯೆಹೂದ್ಯರಿಗೆ ಹೇಳಿದಂತೆ ಈಗ ನಿಮಗೂ ಹೇಳುತ್ತೇನೆ.",
			"34": "<sup>34</sup> ನಾನು ನಿಮಗೆ ಒಂದು ಹೊಸ ಆಜ್ಞೆಯನ್ನು ಕೊಡುತ್ತೇನೆ; ಏನಂದರೆ ನೀವು ಒಬ್ಬರನ್ನೊಬ್ಬರು ಪ್ರೀತಿಸಬೇಕು; ನಾನು ನಿಮ್ಮನ್ನು ಪ್ರೀತಿಸಿದ ಮೇರೆಗೆ ನೀವು ಸಹ ಒಬ್ಬರನ್ನೊಬ್ಬರು ಪ್ರೀತಿಸಬೇಕು ಎಂಬದೇ.",
			"35": "<sup>35</sup> ನಿಮ್ಮೊಳಗೆ ಒಬ್ಬರ ಮೇಲೊಬ್ಬರಿಗೆ ಪ್ರೀತಿಯಿದ್ದರೆ ಎಲ್ಲರೂ ನಿಮ್ಮನ್ನು ನನ್ನ ಶಿಷ್ಯರೆಂದು ತಿಳುಕೊಳ್ಳುವರು ಅಂದನು.",
			"36": "<sup>36</sup> ಸೀಮೋನ ಪೇತ್ರನು ಆತನನ್ನು - ಸ್ವಾಮೀ, ಎಲ್ಲಿಗೆ ಹೋಗುತ್ತೀ? ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ ಯೇಸು - ನಾನು ಹೋಗುವಲ್ಲಿಗೆ ನೀನು ಈಗ ನನ್ನ ಹಿಂದೆ ಬರಲಾರಿ, ತರುವಾಯ ನನ್ನ ಹಿಂದೆ ಬರುವಿ ಅಂದನು.",
			"37": "<sup>37</sup> ಪೇತ್ರನು ಆತನಿಗೆ - ಸ್ವಾಮೀ, ನಾನು ಯಾಕೆ ಈಗ ನಿನ್ನ ಹಿಂದೆ ಬರಲಾರೆನು? ನಿನಗಾಗಿ ನನ್ನ ಪ್ರಾಣವನ್ನಾದರೂ ಕೊಟ್ಟೇನು ಎಂದು ಹೇಳಲು",
			"38": "<sup>38</sup> ಯೇಸು - ನನಗಾಗಿ ಪ್ರಾಣ ಕೊಟ್ಟೀಯಾ? ನಿನಗೆ ನಿಜನಿಜವಾಗಿ ಹೇಳುತ್ತೇನೆ, ನೀನು ನನ್ನ ವಿಷಯದಲ್ಲಿ ಅವನನ್ನು ಅರಿಯೆನೆಂದು ಮೂರು ಸಾರಿ ಹೇಳುವ ತನಕ ಕೋಳಿ ಕೂಗುವದೇ ಇಲ್ಲ ಅಂದನು."
		},
		"14": {
			"1": "<sup>1</sup> ನಿಮ್ಮ ಹೃದಯವು ಕಳವಳಗೊಳ್ಳದೆ ಇರಲಿ; ದೇವರನ್ನು ನಂಬಿರಿ, ನನ್ನನ್ನೂ ನಂಬಿರಿ.",
			"2": "<sup>2</sup> ನನ್ನ ತಂದೆಯ ಮನೆಯಲ್ಲಿ ಬಹಳ ಬಿಡಾರಗಳು ಅವೆ; ಇಲ್ಲದಿದ್ದರೆ ನಿಮಗೆ ಹೇಳುತ್ತಿದ್ದೆನು; ನಿಮಗೆ ಸ್ಥಳವನ್ನು ಸಿದ್ಧಮಾಡುವದಕ್ಕೆ ಹೋಗುತ್ತೇನಲ್ಲಾ.",
			"3": "<sup>3</sup> ನಾನು ಹೋಗಿ ನಿಮಗೆ ಸ್ಥಳವನ್ನು ಸಿದ್ಧಮಾಡಿದ ಮೇಲೆ ತಿರಿಗಿ ಬಂದು ನಿಮ್ಮನ್ನು ಕರಕೊಂಡು ಹೋಗಿ ನನ್ನ ಬಳಿಗೆ ಸೇರಿಸಿಕೊಳ್ಳುವೆನು; ಯಾಕಂದರೆ ನಾನಿರುವ ಸ್ಥಳದಲ್ಲಿ ನೀವು ಸಹ ಇರಬೇಕು.",
			"4": "<sup>4</sup> ಮತ್ತು ನಾನು ಹೋಗುವ ಸ್ಥಳದ ಮಾರ್ಗವು ನಿಮಗೆ ತಿಳಿದದೆ ಅಂದನು.",
			"5": "<sup>5</sup> ತೋಮನು ಆತನನ್ನು - ಸ್ವಾಮೀ, ನೀನು ಎಲ್ಲಿಗೆ ಹೋಗುತ್ತೀಯೋ ನಮಗೆ ತಿಳಿಯದು; ಮಾರ್ಗವು ಹೇಗೆ ತಿಳಿದೀತು ಎಂದು ಕೇಳಲು",
			"6": "<sup>6</sup> ಯೇಸು ಅವನಿಗೆ - ನಾನೇ ಮಾರ್ಗವೂ ಸತ್ಯವೂ ಜೀವವೂ ಆಗಿದ್ದೇನೆ; ನನ್ನ ಮೂಲಕವಾಗಿ ಹೊರತು ಯಾರೂ ತಂದೆಯ ಬಳಿಗೆ ಬರುವದಿಲ್ಲ.",
			"7": "<sup>7</sup> ನೀವು ನನ್ನನ್ನು ಅರಿತಿದ್ದರೆ ನನ್ನ ತಂದೆಯನ್ನೂ ಅರಿಯುತ್ತಿದ್ದಿರಿ; ಈಗಿನಿಂದ ಆತನನ್ನು ಅರಿತುಕೊಂಡಿದ್ದೀರಿ; ಆತನನ್ನು ನೋಡಿದ್ದೀರಲ್ಲಾ ಎಂದು ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ಫಿಲಿಪ್ಪನು - ಸ್ವಾಮೀ, ನಮಗೆ ತಂದೆಯನ್ನು ತೋರಿಸು, ನಮಗೆ ಅಷ್ಟೇ ಸಾಕು ಅಂದನು.",
			"9": "<sup>9</sup> ಯೇಸು ಅವನಿಗೆ - ಫಿಲಿಪ್ಪನೇ, ನಾನು ಇಷ್ಟು ದಿವಸ ನಿಮ್ಮ ಸಂಗಡ ಇದ್ದರೂ ನೀನು ಇನ್ನೂ ನನ್ನನ್ನು ಅರಿತುಕೊಳ್ಳಲಿಲ್ಲವೇ. ನನ್ನನ್ನು ನೋಡಿದವನು ತಂದೆಯನ್ನು ನೋಡಿದ್ದಾನೆ. ನಮಗೆ ತಂದೆಯನ್ನು ತೋರಿಸೆಂದು ನೀನು ಹೇಳುವದು ಹೇಗೆ?",
			"10": "<sup>10</sup> ನಾನು ತಂದೆಯಲ್ಲಿದ್ದೇನೆ, ತಂದೆಯು ನನ್ನಲ್ಲಿ ಇದ್ದಾನೆ ಎಂದು ನೀನು ನಂಬುವದಿಲ್ಲವೋ? ನಾನು ನಿಮಗೆ ಹೇಳುವ ಮಾತುಗಳನ್ನು ನನ್ನಷ್ಟಕ್ಕೆ ನಾನೇ ಆಡುವದಿಲ್ಲ; ತಂದೆಯು ನನ್ನಲ್ಲಿ ಇದ್ದುಕೊಂಡು ತನ್ನ ಕ್ರಿಯೆಗಳನ್ನು ನಡಿಸುತ್ತಾನೆ.",
			"11": "<sup>11</sup> ನಾನು ತಂದೆಯಲ್ಲಿ ಇದ್ದೇನೆ, ತಂದೆಯು ನನ್ನಲ್ಲಿ ಇದ್ದಾನೆಂಬುವ ನನ್ನ ಮಾತನ್ನು ನಂಬಿರಿ; ಇಲ್ಲದಿದ್ದರೆ ಆ ಕ್ರಿಯೆಗಳನ್ನೇ ನೋಡಿ ನನ್ನ ಮಾತನ್ನು ನಂಬಿರಿ.",
			"12": "<sup>12</sup> ನಿಮಗೆ ನಿಜನಿಜವಾಗಿ ಹೇಳುತ್ತೇನೆ, ನನ್ನನ್ನು ನಂಬುವವನು ನಾನು ನಡಿಸುವ ಕ್ರಿಯೆಗಳನ್ನು ತಾನೂ ನಡಿಸುವನು; ಮತ್ತು ಅವುಗಳಿಗಿಂತ ಮಹತ್ತಾದ ಕ್ರಿಯೆಗಳನ್ನು ನಡಿಸುವನು. ಯಾಕಂದರೆ ನಾನು ತಂದೆಯ ಬಳಿಗೆ ಹೋಗುತ್ತೇನೆ.",
			"13": "<sup>13</sup> ಇದಲ್ಲದೆ ನೀವು ನನ್ನ ಹೆಸರಿನಲ್ಲಿ ಏನೇನು ಬೇಡಿಕೊಳ್ಳುವಿರೋ, ಅದನ್ನು ನೆರವೇರಿಸುವೆನು; ಹೀಗೆ ಮಗನ ಮೂಲಕವಾಗಿ ತಂದೆಗೆ ಮಹಿಮೆ ಉಂಟಾಗುವದು.",
			"14": "<sup>14</sup> ನೀವು ನನ್ನ ಹೆಸರಿನಲ್ಲಿ ನನ್ನನ್ನು ಏನಾದರೂ ಬೇಡಿಕೊಂಡರೆ ಅದನ್ನು ನೆರವೇರಿಸುವೆನು.",
			"15": "<sup>15</sup> ನೀವು ನನ್ನನ್ನು ಪ್ರೀತಿಸುವವರಾದರೆ ನನ್ನ ಆಜ್ಞೆಗಳನ್ನು ಕೈಕೊಂಡು ನಡೆಯುವಿರಿ.",
			"16": "<sup>16</sup> ಆಗ ನಾನು ತಂದೆಯನ್ನು ಕೇಳಿಕೊಳ್ಳುವೆನು; ಆತನು ನಿಮಗೆ ಬೇರೊಬ್ಬ ಸಹಾಯಕನನ್ನು ಸದಾಕಾಲ ನಿಮ್ಮ ಸಂಗಡ ಇರುವದಕ್ಕೆ ಕೊಡುವನು.",
			"17": "<sup>17</sup> ಆ ಸಹಾಯಕನು ಯಾರಂದರೆ ಸತ್ಯದ ಆತ್ಮನೇ. ಲೋಕವು ಆತನನ್ನು ನೋಡದೆಯೂ ತಿಳಿಯದೆಯೂ ಇರುವದರಿಂದ ಆತನನ್ನು ಹೊಂದಲಾರದು. ನೀವು ಆತನನ್ನು ಬಲ್ಲಿರಿ; ಹೇಗಂದರೆ ನಿಮ್ಮ ಬಳಿಯಲ್ಲಿ ವಾಸಮಾಡುತ್ತಾನೆ ಮತ್ತು ನಿಮ್ಮೊಳಗೆ ಇರುವನು.",
			"18": "<sup>18</sup> ನಾನು ನಿಮ್ಮನ್ನು ಅನಾಥರಾಗಿ ಬಿಡುವದಿಲ್ಲ; ನಿಮ್ಮ ಬಳಿಗೆ ಬರುತ್ತೇನೆ.",
			"19": "<sup>19</sup> ಇನ್ನು ಸ್ವಲ್ಪಕಾಲವಾದ ಮೇಲೆ ಲೋಕವು ನನ್ನನ್ನು ನೋಡುವದಿಲ್ಲ, ಆದರೆ ನೀವು ನನ್ನನ್ನು ನೋಡುವಿರಿ; ನಾನು ಬದುಕುವದರಿಂದ ನೀವೂ ಬದುಕುವಿರಿ.",
			"20": "<sup>20</sup> ನಾನು ನನ್ನ ತಂದೆಯಲ್ಲಿಯೂ ನೀವು ನನ್ನಲ್ಲಿಯೂ ನಾನು ನಿಮ್ಮಲ್ಲಿಯೂ ಇರುವದನ್ನು ನೀವು ಆ ದಿನದಲ್ಲಿ ತಿಳಿದುಕೊಳ್ಳುವಿರಿ.",
			"21": "<sup>21</sup> ನನ್ನ ಆಜ್ಞೆಗಳನ್ನು ಹೊಂದಿ ಅವುಗಳನ್ನು ಕೈಕೊಂಡು ನಡೆಯುವವನೇ ನನ್ನನ್ನು ಪ್ರೀತಿಸುವವನು. ನನ್ನನ್ನು ಪ್ರೀತಿಸುವವನು ನನ್ನ ತಂದೆಗೆ ಪ್ರಿಯನಾಗಿರುವನು; ನಾನೂ ಅವನನ್ನು ಪ್ರೀತಿಸಿ ಅವನಿಗೆ ಕಾಣಿಸಿಕೊಳ್ಳುವೆನು ಎಂದು ಹೇಳಿದನು.",
			"22": "<sup>22</sup> ಯೂದನು (ಇವನು ಇಸ್ಕರಿಯೋತನಲ್ಲ) - ಸ್ವಾಮೀ, ನೀನು ಲೋಕಕ್ಕೆ ಕಾಣಿಸಿಕೊಳ್ಳದೆ ನಮಗೆ ಮಾತ್ರ ಕಾಣಿಸಿಕೊಳ್ಳುವದಕ್ಕೆ ಏನು ಸಂಭವಿಸಿತು ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ",
			"23": "<sup>23</sup> ಯೇಸು ಅವನಿಗೆ - ಯಾರಾದರೂ ನನ್ನನ್ನು ಪ್ರೀತಿಸುವವನಾದರೆ ನನ್ನ ಮಾತನ್ನು ಕೈಕೊಂಡು ನಡೆಯುವನು; ಅವನನ್ನು ನನ್ನ ತಂದೆಯು ಪ್ರೀತಿಸುವನು, ಮತ್ತು ನಾವಿಬ್ಬರೂ ಅವನ ಬಳಿಗೆ ಬಂದು ಅವನ ಬಳಿಯಲ್ಲಿ ಬಿಡಾರವನ್ನು ಮಾಡಿಕೊಳ್ಳುವೆವು.",
			"24": "<sup>24</sup> ನನ್ನನ್ನು ಪ್ರೀತಿಸದೆ ಇರುವವನು ನನ್ನ ಮಾತುಗಳನ್ನು ಕೈಕೊಂಡು ನಡೆಯುವದಿಲ್ಲ; ನೀವು ಆಲಿಸುವ ಮಾತು ನನ್ನದಲ್ಲ, ನನ್ನನ್ನು ಕಳುಹಿಸಿದ ತಂದೆಯ ಮಾತೇ.",
			"25": "<sup>25</sup> ಈ ಮಾತುಗಳನ್ನು ನಾನು ಇನ್ನೂ ನಿಮ್ಮ ಬಳಿಯಲ್ಲಿರುವಾಗ ನಿಮಗೆ ಹೇಳಿದ್ದೇನೆ.",
			"26": "<sup>26</sup> ಆದರೆ ಆ ಸಹಾಯಕನು ಅಂದರೆ ನನ್ನ ಹೆಸರಿನಲ್ಲಿ ತಂದೆಯು ಕಳುಹಿಸಿಕೊಡುವ ಪವಿತ್ರಾತ್ಮನೇ ನಿಮಗೆ ಎಲ್ಲವನ್ನು ಉಪದೇಶಿಸಿ ನಾನು ನಿಮಗೆ ಹೇಳಿದ್ದನ್ನೆಲ್ಲಾ ನಿಮ್ಮ ನೆನಪಿಗೆ ತರುವನು.",
			"27": "<sup>27</sup> ಶಾಂತಿಯನ್ನು ನಿಮಗೆ ಬಿಟ್ಟುಹೋಗುತ್ತೇನೆ, ನನ್ನಲ್ಲಿರುವಂಥ ಶಾಂತಿಯನ್ನು ನಿಮಗೆ ಕೊಡುತ್ತೇನೆ; ಲೋಕವು ಕೊಡುವ ರೀತಿಯಿಂದ ನಾನು ನಿಮಗೆ ಕೊಡುವದಿಲ್ಲ. ನಿಮ್ಮ ಹೃದಯವು ಕಳವಳಗೊಳ್ಳದಿರಲಿ, ಹೆದರದಿರಲಿ.",
			"28": "<sup>28</sup> ನಾನು ಹೋಗಿ ತಿರಿಗಿ ನಿಮ್ಮ ಬಳಿಗೆ ಬರುತ್ತೇನೆಂದು ನಿಮಗೆ ಹೇಳಿದ್ದನ್ನು ಕೇಳಿದಿರಿ. ನೀವು ನನ್ನನ್ನು ಪ್ರೀತಿಸುವವರಾಗಿದ್ದರೆ ನಾನು ತಂದೆಯ ಬಳಿಗೆ ಹೋಗುವ ವಿಷಯದಲ್ಲಿ ಸಂತೋಷಪಡುತ್ತಿದ್ದಿರಿ; ಯಾಕಂದರೆ ತಂದೆಯು ನನಗಿಂತ ದೊಡ್ಡವನು.",
			"29": "<sup>29</sup> ಅದೆಲ್ಲಾ ನಡೆಯುವಾಗ ನೀವು ನನ್ನನ್ನು ನಂಬುವಂತೆ ಅದು ನಡೆಯುವದಕ್ಕಿಂತ ಮುಂಚೆಯೇ ಈಗ ನಿಮಗೆ ಹೇಳಿದ್ದೇನೆ.",
			"30": "<sup>30</sup> ಇನ್ನು ನಾನು ನಿಮ್ಮ ಸಂಗಡ ಬಹಳ ಮಾತುಗಳನ್ನಾಡುವದಿಲ್ಲ, ಯಾಕಂದರೆ ಇಹಲೋಕಾಧಿಪತಿಯು ಬರುತ್ತಾನೆ. ಅವನಿಗೆ ಸಂಬಂಧಪಟ್ಟದ್ದು ಯಾವದೊಂದೂ ನನ್ನಲ್ಲಿಲ್ಲ;",
			"31": "<sup>31</sup> ಆದರೂ ನಾನು ತಂದೆಯನ್ನು ಪ್ರೀತಿಸುತ್ತೇನೆಂಬದು ಲೋಕಕ್ಕೆ ತಿಳಿದಿರಬೇಕೆಂದು ತಂದೆಯು ನನಗೆ ಕೊಟ್ಟ ಆಜ್ಞೆಯ ಮೇರೆಗೆ ಇದನ್ನು ಮಾಡುತ್ತೇನೆ. ಏಳಿರಿ, ಇಲ್ಲಿಂದ ಹೋಗೋಣ."
		},
		"15": {
			"1": "<sup>1</sup> ನಾನೇ ನಿಜವಾದ ದ್ರಾಕ್ಷೇಬಳ್ಳಿ. ನನ್ನ ತಂದೆ ತೋಟಗಾರನು.",
			"2": "<sup>2</sup> ನನ್ನಲ್ಲಿದ್ದು ಫಲಕೊಡದಿರುವ ಪ್ರತಿಯೊಂದು ಕೊಂಬೆಯನ್ನು ಆತನು ತೆಗೆದುಹಾಕುತ್ತಾನೆ; ಫಲಕೊಡುವ ಪ್ರತಿಯೊಂದು ಕೊಂಬೆಯು ಹೆಚ್ಚು ಫಲಕೊಡುವ ಹಾಗೆ ಅದನ್ನು ಶುದ್ಧಿಮಾಡುತ್ತಾನೆ.",
			"3": "<sup>3</sup> ನಾನು ನಿಮಗೆ ಹೇಳಿದ ವಾಕ್ಯದ ದೆಸೆಯಿಂದ ಈಗ ಶುದ್ಧರಾಗಿದ್ದೀರಿ.",
			"4": "<sup>4</sup> ನೀವು ನನ್ನಲ್ಲಿ ನೆಲೆಗೊಂಡಿರ್ರಿ, ನಾನೂ ನಿಮ್ಮಲ್ಲಿ ನೆಲೆಗೊಂಡಿರುವೆನು. ಕೊಂಬೆಯು ಬಳ್ಳಿಯಲ್ಲಿ ನೆಲೆಗೊಂಡಿರದಿದ್ದರೆ ಹೇಗೆ ತನ್ನಷ್ಟಕ್ಕೆ ತಾನೇ ಫಲಕೊಡಲಾರದೋ ಹಾಗೆಯೇ ನೀವು ನನ್ನಲ್ಲಿ ನೆಲೆಗೊಂಡಿರದಿದ್ದರೆ ಫಲಕೊಡಲಾರಿರಿ.",
			"5": "<sup>5</sup> ನಾನು ದ್ರಾಕ್ಷೇ ಬಳ್ಳಿ, ನೀವು ಕೊಂಬೆಗಳು; ಒಬ್ಬನು ನನ್ನಲ್ಲಿಯೂ ನಾನು ಅವನಲ್ಲಿಯೂ ನೆಲೆಗೊಂಡಿದ್ದರೆ ಅವನೇ ಬಹಳ ಫಲಕೊಡುವನು; ನೀವು ನನ್ನನ್ನು ಬಿಟ್ಟು ಏನೂ ಮಾಡಲಾರಿರಿ.",
			"6": "<sup>6</sup> ಯಾವನು ನನ್ನಲ್ಲಿ ನೆಲೆಗೊಂಡಿರುವದಿಲ್ಲವೋ ಅವನು ಆ ಕೊಂಬೆಯಂತೆ ಹೊರಕ್ಕೆ ಬಿಸಾಡಲ್ಪಟ್ಟು ಒಣಗಿಹೋಗುವನು; ಅಂಥ ಕೊಂಬೆಗಳನ್ನು ಕೂಡಿಸಿ ಬೆಂಕಿಯಲ್ಲಿ ಹಾಕುತ್ತಾರೆ, ಅವು ಸುಟ್ಟುಹೋಗುತ್ತವೆ.",
			"7": "<sup>7</sup> ನೀವು ನನ್ನಲ್ಲಿಯೂ ನನ್ನ ವಾಕ್ಯಗಳು ನಿಮ್ಮಲ್ಲಿಯೂ ನೆಲೆಗೊಂಡಿದ್ದರೆ ಏನು ಬೇಕಾದರೂ ಬೇಡಿಕೊಳ್ಳಿರಿ, ಅದು ನಿಮಗೆ ದೊರೆಯುವದು.",
			"8": "<sup>8</sup> ನೀವು ಬಹಳ ಫಲಕೊಡುವದರಿಂದಲೇ ನನ್ನ ತಂದೆಗೆ ಮಹಿಮೆ ಉಂಟಾಗುವದು; ಮತ್ತು ನನ್ನ ಶಿಷ್ಯರಾಗುವಿರಿ.",
			"9": "<sup>9</sup> ತಂದೆ ನನ್ನನ್ನು ಪ್ರೀತಿಸಿದ ಹಾಗೆಯೇ ನಾನೂ ನಿಮ್ಮನ್ನು ಪ್ರೀತಿಸಿದ್ದೇನೆ; ನನ್ನ ಪ್ರೀತಿಯಲ್ಲಿ ನೆಲೆಗೊಂಡಿರ್ರಿ.",
			"10": "<sup>10</sup> ನಾನು ನನ್ನ ತಂದೆಯ ಆಜ್ಞೆಗಳನ್ನು ಕೈಕೊಂಡು ನಡೆದು ಆತನ ಪ್ರೀತಿಯಲ್ಲಿ ನೆಲೆಗೊಂಡಿರುವ ಮೇರೆಗೆ ನೀವು ನನ್ನ ಆಜ್ಞೆಗಳನ್ನು ಕೈಕೊಂಡು ನಡೆದರೆ ನನ್ನ ಪ್ರೀತಿಯಲ್ಲಿ ನೆಲೆಗೊಂಡಿರುವಿರಿ.",
			"11": "<sup>11</sup> ನನ್ನಲ್ಲಿರುವ ಆನಂದವು ನಿಮ್ಮಲ್ಲಿಯೂ ಇರಬೇಕೆಂತಲೂ ನಿಮ್ಮ ಆನಂದವು ಪರಿಪೂರ್ಣವಾಗಬೇಕೆಂತಲೂ ಇದನ್ನೆಲ್ಲಾ ನಿಮಗೆ ಹೇಳಿದ್ದೇನೆ.",
			"12": "<sup>12</sup> ನಾನು ನಿಮ್ಮನ್ನು ಪ್ರೀತಿಸಿದ ಮೇರೆಗೆ ನೀವು ಒಬ್ಬರನ್ನೊಬ್ಬರು ಪ್ರೀತಿಸಬೇಕೆಂಬದೇ ನಾನು ಕೊಡುವ ಆಜ್ಞೆಯಾಗಿದೆ.",
			"13": "<sup>13</sup> ಪ್ರಾಣವನ್ನೇ ಸ್ನೇಹಿತರಿಗೋಸ್ಕರ ಕೊಡುವ ಪ್ರೀತಿಗಿಂತ ಹೆಚ್ಚಿನ ಪ್ರೀತಿಯು ಯಾವದೂ ಇಲ್ಲ.",
			"14": "<sup>14</sup> ನಾನು ನಿಮಗೆ ಕೊಟ್ಟ ಆಜ್ಞೆಗಳಿಗೆ ಸರಿಯಾಗಿ ನೀವು ನಡೆದರೆ ನೀವು ನನ್ನ ಸ್ನೇಹಿತರು.",
			"15": "<sup>15</sup> ಇನ್ನು ಮೇಲೆ ನಾನು ನಿಮ್ಮನ್ನು ಆಳುಗಳನ್ನುವದಿಲ್ಲ; ಯಜಮಾನನು ಮಾಡುವಂಥದು ಆಳಿಗೆ ತಿಳಿಯುವದಿಲ್ಲ. ನಿಮ್ಮನ್ನು ಸ್ನೇಹಿತರೆಂದು ಹೇಳಿದ್ದೇನೆ; ತಂದೆಯ ಕಡೆಯಿಂದ ನಾನು ಕೇಳಿದ್ದನ್ನೆಲ್ಲಾ ನಿಮಗೆ ತಿಳಿಸಿದ್ದೇನೆ.",
			"16": "<sup>16</sup> ನೀವು ನನ್ನನ್ನು ಆರಿಸಿ ತೆಗೆದುಕೊಂಡಿಲ್ಲ; ನಾನು ನಿಮ್ಮನ್ನು ಆರಿಸಿ ತೆಗೆದುಕೊಂಡೆನು. ನೀವು ಹೊರಟುಹೋಗಿ ಫಲಕೊಡುವವರಾಗಬೇಕೆಂತಲೂ ನೀವು ಕೊಡುವ ಫಲವು ನಿಲ್ಲುವಂಥದಾಗಬೇಕೆಂತಲೂ ನಿಮ್ಮನ್ನು ನೇಮಿಸಿದ್ದೇನೆ. ಹೀಗಿರಲಾಗಿ ನನ್ನ ಹೆಸರಿನಲ್ಲಿ ತಂದೆಯನ್ನು ಏನೇನು ಬೇಡಿಕೊಳ್ಳುವಿರೋ ಅದನ್ನು ಆತನು ನಿಮಗೆ ಕೊಡುವನು.",
			"17": "<sup>17</sup> ನೀವು ಒಬ್ಬರನ್ನೊಬ್ಬರು ಪ್ರೀತಿಸಬೇಕೆಂತಲೇ ಇವುಗಳನ್ನು ನಿಮಗೆ ಆಜ್ಞಾಪಿಸುತ್ತೇನೆ.",
			"18": "<sup>18</sup> ಲೋಕವು ನಿಮ್ಮ ಮೇಲೆ ದ್ವೇಷಮಾಡುವದಾದರೆ ಅದು ಮೊದಲು ನನ್ನ ಮೇಲೆ ದ್ವೇಷಮಾಡಿತೆಂದು ತಿಳುಕೊಳ್ಳಿರಿ.",
			"19": "<sup>19</sup> ನೀವು ಲೋಕದ ಕಡೆಯವರಾಗಿರುತ್ತಿದ್ದರೆ ಲೋಕವು ನಿಮ್ಮ ಮೇಲೆ ತನ್ನವರೆಂದು ಮಮತೆ ಇಡುತ್ತಿತ್ತು; ಆದರೆ ನೀವು ಲೋಕದ ಕಡೆಯವರಲ್ಲದೆ ಇರುವದರಿಂದಲೂ ನಾನು ನಿಮ್ಮನ್ನು ಲೋಕದೊಳಗಿಂದ ಆರಿಸಿ ತೆಗೆದುಕೊಂಡಿರುವದರಿಂದಲೂ ಲೋಕವು ನಿಮ್ಮ ಮೇಲೆ ದ್ವೇಷ ಮಾಡುತ್ತದೆ.",
			"20": "<sup>20</sup> ದಣಿಗಿಂತ ಆಳು ದೊಡ್ಡವನಲ್ಲವೆಂಬದಾಗಿ ನಾನು ನಿಮಗೆ ಹೇಳಿದ ಮಾತನ್ನು ಜ್ಞಾಪಕಮಾಡಿಕೊಳ್ಳಿರಿ. ಅವರು ನನ್ನನ್ನು ಹಿಂಸೆಪಡಿಸಿದರೆ ನಿಮ್ಮನ್ನು ಸಹ ಹಿಂಸೆಪಡಿಸುವರು; ಅವರು ನನ್ನ ಮಾತನ್ನು ಕೈಕೊಂಡು ನಡೆದರೆ ನಿಮ್ಮ ಮಾತನ್ನು ಸಹ ಕೈಕೊಂಡು ನಡೆಯುವರು.",
			"21": "<sup>21</sup> ಆದರೆ ಅವರು ನನ್ನನ್ನು ಕಳುಹಿಸಿಕೊಟ್ಟಾತನನ್ನು ತಿಳಿಯದವರಾದದರಿಂದ ಇದನ್ನೆಲ್ಲಾ ನನ್ನ ಹೆಸರಿನ ನಿಮಿತ್ತ ನಿಮಗೆ ಮಾಡುವರು.",
			"22": "<sup>22</sup> ನಾನು ಬಂದು ಅವರ ಕೂಡ ಮಾತಾಡದಿದ್ದರೆ ಅವರಿಗೆ ಪಾಪವು ಇರುತ್ತಿದ್ದಿಲ್ಲ; ಆದರೆ ಅವರು ಮಾಡಿದ ಪಾಪಕ್ಕಾಗಿ ಅವರಿಗೆ ಈಗ ನೆವವಿಲ್ಲ.",
			"23": "<sup>23</sup> ನನ್ನ ಮೇಲೆ ದ್ವೇಷಮಾಡುವವನು ನನ್ನ ತಂದೆಯ ಮೇಲೆಯೂ ದ್ವೇಷಮಾಡುವವನಾಗಿದ್ದಾನೆ.",
			"24": "<sup>24</sup> ಮತ್ತಾರೂ ನಡಿಸದ ಕ್ರಿಯೆಗಳನ್ನು ನಾನು ಅವರಲ್ಲಿ ನಡಿಸದೆ ಇದ್ದರೆ ಅವರಿಗೆ ಪಾಪವು ಇರುತ್ತಿದ್ದಿಲ್ಲ; ಆದರೆ ಈಗ ಅವರು ನೋಡಿದ್ದಾರೆ, ಆದರೂ ನನ್ನ ಮೇಲೆಯೂ ನನ್ನ ತಂದೆಯ ಮೇಲೆಯೂ ದ್ವೇಷಮಾಡಿದ್ದಾರೆ.",
			"25": "<sup>25</sup> ಅವರು ನನ್ನನ್ನು ನಿಷ್ಕಾರಣವಾಗಿ ದ್ವೇಷಿಸಿದರು ಎಂದು ಅವರ ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿ ಬರೆದಿರುವ ಮಾತು ನೆರವೇರುವಂತೆ ಹೀಗಾಯಿತು.",
			"26": "<sup>26</sup> ಆದರೆ ತಂದೆಯ ಬಳಿಯಿಂದ ನಾನು ನಿಮಗೆ ಕಳುಹಿಸಿಕೊಡುವ ಸಹಾಯಕನು, ಅಂದರೆ ತಂದೆಯ ಬಳಿಯಿಂದ ಹೊರಡುವ ಸತ್ಯದ ಆತ್ಮನು, ಬಂದಾಗ ಆತನು ನನ್ನನ್ನು ಕುರಿತು ಸಾಕ್ಷಿಹೇಳುವನು.",
			"27": "<sup>27</sup> ನೀವು ಮೊದಲಿನಿಂದ ನನ್ನ ಸಂಗಡ ಇದ್ದ ಕಾರಣ ನೀವೂ ಸಾಕ್ಷಿಗಳಾಗಿದ್ದೀರಿ."
		},
		"16": {
			"1": "<sup>1</sup> ನೀವು ಧೈರ್ಯಗೆಟ್ಟು ಹಿಂಜರಿಯಬಾರದೆಂದು ಇದನ್ನೆಲ್ಲಾ ನಿಮಗೆ ಹೇಳಿದ್ದೇನೆ.",
			"2": "<sup>2</sup> ನಿಮಗೆ ಬಹಿಷ್ಕಾರ ಹಾಕುವರು; ಅದಲ್ಲದೆ ನಿಮ್ಮನ್ನು ಕೊಲ್ಲುವವನು ತಾನು ದೇವರಿಗೆ ಯಜ್ಞವನ್ನು ಮಾಡಿದ್ದೇನೆಂದು ನೆನಸುವ ಕಾಲ ಬರುತ್ತದೆ.",
			"3": "<sup>3</sup> ಅವರು ತಂದೆಯನ್ನಾದರೂ ನನ್ನನ್ನಾದರೂ ತಿಳಿಯದವರಾಗಿರುವದರಿಂದ ಇಂಥದನ್ನು ನಿಮಗೆ ಮಾಡುವರು.",
			"4": "<sup>4</sup> ಆ ಕಾಲ ಬಂದಾಗ ಆತನು ನಮಗೆ ಇಂಥಿಂಥದನ್ನು ಹೇಳಿದನಲ್ಲಾ ಎಂದು ನೀವು ನೆನಪಿಗೆ ತರುವಂತೆ ನಿಮ್ಮ ಸಂಗಡ ಈ ಮಾತುಗಳನ್ನು ಆಡಿದ್ದೇನೆ. ನಾನು ನಿಮ್ಮ ಸಂಗಡ ಇದ್ದದರಿಂದ ಮೊದಲು ಇವುಗಳನ್ನು ನಿಮಗೆ ಹೇಳಲಿಲ್ಲ.",
			"5": "<sup>5</sup> ಆದರೆ ಈಗ ನಾನು ನನ್ನನ್ನು ಕಳುಹಿಸಿಕೊಟ್ಟಾತನ ಬಳಿಗೆ ಹೋಗುತ್ತೇನೆ. ನೀನು ಎಲ್ಲಿಗೆ ಹೋಗುತ್ತೀ ಎಂದು ನಿಮ್ಮಲ್ಲಿ ಒಬ್ಬನಾದರೂ ನನ್ನನ್ನು ಕೇಳುವದಿಲ್ಲ;",
			"6": "<sup>6</sup> ಆದರೆ ನಾನು ಈ ಮಾತುಗಳನ್ನು ನಿಮಗೆ ಹೇಳಿದ್ದರಿಂದ ನಿಮ್ಮ ಹೃದಯವು ದುಃಖದಿಂದ ತುಂಬಿಯದೆ.",
			"7": "<sup>7</sup> ಆದರೂ ನಾನು ನಿಮಗೆ ಸತ್ಯವನ್ನು ಹೇಳುತ್ತೇನೆ, ಕೇಳಿರಿ; ನಾನು ಹೋಗುವದು ನಿಮಗೆ ಹಿತಕರವಾಗಿದೆ; ಹೇಗಂದರೆ ನಾನು ಹೋಗದಿದ್ದರೆ ಆ ಸಹಾಯಕನು ನಿಮ್ಮ ಬಳಿಗೆ ಬರುವದಿಲ್ಲ; ನಾನು ಹೋದರೆ ಆತನನ್ನು ನಿಮ್ಮ ಬಳಿಗೆ ಕಳುಹಿಸಿಕೊಡುತ್ತೇನೆ.",
			"8": "<sup>8</sup> ಆತನು ಬಂದು ಪಾಪ ನೀತಿ ನ್ಯಾಯತೀರ್ವಿಕೆ ಈ ವಿಷಯಗಳಲ್ಲಿ ಲೋಕಕ್ಕೆ ಅರುಹನ್ನು ಹುಟ್ಟಿಸುವನು.",
			"9": "<sup>9</sup> ಅವರು ನನ್ನನ್ನು ನಂಬದೆ ಇರುವದರಿಂದ ಪಾಪದ ವಿಷಯದಲ್ಲಿಯೂ",
			"10": "<sup>10</sup> ನಾನು ತಂದೆಯ ಬಳಿಗೆ ಹೋಗಿ ಇನ್ನು ಮೇಲೆ ನಿಮಗೆ ಕಾಣಿಸದೆ ಇರುವದರಿಂದ ನೀತಿಯ ವಿಷಯದಲ್ಲಿಯೂ",
			"11": "<sup>11</sup> ಇಹಲೋಕಾಧಿಪತಿಗೆ ನ್ಯಾಯತೀರ್ವಿಕೆಯಾದದರಿಂದ ನ್ಯಾಯತೀರ್ವಿಕೆಯ ವಿಷಯದಲ್ಲಿಯೂ ಲೋಕಕ್ಕೆ ಅರುಹನ್ನು ಹುಟ್ಟಿಸುವನು.",
			"12": "<sup>12</sup> ನಾನು ನಿಮಗೆ ಹೇಳಬೇಕಾದದ್ದು ಇನ್ನೂ ಬಹಳ ಉಂಟು; ಆದರೆ ಸದ್ಯಕ್ಕೆ ಅದನ್ನು ಹೊರಲಾರಿರಿ.",
			"13": "<sup>13</sup> ಸತ್ಯದ ಆತ್ಮನು ಬಂದಾಗ ಆತನು ನಿಮ್ಮನ್ನು ನಡಿಸಿಕೊಂಡು ಹೋಗಿ ಸಕಲ ವಿಷಯದಲ್ಲಿಯೂ ಸತ್ಯಕ್ಕೆ ಸೇರಿಸುವನು. ಆತನು ತನ್ನಷ್ಟಕ್ಕೆ ತಾನೇ ಮಾತಾಡದೆ ಕೇಳಿದ ಮಾತುಗಳನ್ನೇ ಆಡುವನು; ಮತ್ತು ಮುಂದಾಗುವ ಸಂಗತಿಗಳನ್ನು ನಿಮಗೆ ತಿಳಿಸುವನು.",
			"14": "<sup>14</sup> ಆತನು ನನ್ನದರೊಳಗಿಂದ ತೆಗೆದುಕೊಂಡು ನಿಮಗೆ ತಿಳಿಸುತ್ತಾ ನನ್ನನ್ನೇ ಮಹಿಮೆಪಡಿಸುವನು.",
			"15": "<sup>15</sup> ತಂದೆಗೆ ಇರುವದೆಲ್ಲಾ ನನ್ನದು; ಆದದರಿಂದಲೇ ನನ್ನದರೊಳಗಿಂದ ತೆಗೆದುಕೊಂಡು ನಿಮಗೆ ತಿಳಿಸುವನೆಂದು ಹೇಳಿದೆನು.",
			"16": "<sup>16</sup> ಸ್ವಲ್ಪಕಾಲವಾದ ಮೇಲೆ ನಾನು ನಿಮಗೆ ಕಾಣುವದಿಲ್ಲ; ಅನಂತರ ಸ್ವಲ್ಪಕಾಲವಾದ ಮೇಲೆ ನನ್ನನ್ನು ನೋಡುವಿರಿ ಅಂದನು.",
			"17": "<sup>17</sup> ಇದನ್ನು ಕೇಳಿ ಶಿಷ್ಯರಲ್ಲಿ ಕೆಲವರು - ಇದೇನು ಈತನು ಹೇಳುವ ಮಾತು? ಸ್ವಲ್ಪಕಾಲವಾದ ಮೇಲೆ ನಾನು ನಿಮಗೆ ಕಾಣುವದಿಲ್ಲ, ಅನಂತರ ಸ್ವಲ್ಪಕಾಲವಾದ ಮೇಲೆ ನನ್ನನ್ನು ನೋಡುವಿರಿ ಅನ್ನುತ್ತಾನೆ; ಮತ್ತು ನಾನು ತಂದೆಯ ಬಳಿಗೆ ಹೋಗುವದರಿಂದ ಅನ್ನುತ್ತಾನೆ ಎಂದು ತಮ್ಮತಮ್ಮೊಳಗೆ ಮಾತಾಡಿಕೊಂಡು -",
			"18": "<sup>18</sup> ಸ್ವಲ್ಪಕಾಲವೆಂದು ಈತನು ಹೇಳುವ ಈ ಮಾತೇನು? ಏನು ಮಾತಾಡುತ್ತಾನೋ ನಮಗೆ ತಿಳಿಯದು ಅಂದುಕೊಳ್ಳುತ್ತಿದ್ದರು.",
			"19": "<sup>19</sup> ಯೇಸು ತನ್ನನ್ನು ಕೇಳಬೇಕೆಂದಿದ್ದಾರೆಂದು ತಿಳಿದು ಅವರಿಗೆ - ಸ್ವಲ್ಪಕಾಲವಾದ ಮೇಲೆ ನಾನು ನಿಮಗೆ ಕಾಣುವದಿಲ್ಲ, ಇನ್ನು ಸ್ವಲ್ಪಕಾಲವಾದ ಮೇಲೆ ನನ್ನನ್ನು ನೋಡುವಿರಿ ಎಂದು ನಾನು ಹೇಳಿದ್ದಕ್ಕೆ ನೀವು ನಿಮ್ಮನಿಮ್ಮೊಳಗೆ ವಿಚಾರಿಸಿಕೊಳ್ಳುತ್ತೀರಾ?",
			"20": "<sup>20</sup> ನಿಮಗೆ ನಿಜನಿಜವಾಗಿ ಹೇಳುತ್ತೇನೆ, ನೀವು ಅಳುತ್ತಾ ಗೋಳಾಡುತ್ತಾ ಇರುವಿರಿ, ಆದರೆ ಲೋಕವು ಸಂತೋಷಿಸುವದು; ನಿಮಗೆ ದುಃಖವಾಗುವದು, ಆದರೆ ನಿಮ್ಮ ದುಃಖವು ಹೋಗಿ ಆನಂದ ಬರುವದು.",
			"21": "<sup>21</sup> ಒಬ್ಬ ಸ್ತ್ರೀ ಹೆರುವಾಗ ತನ್ನ ಬೇನೆಯ ಕಾಲ ಬಂತೆಂದು ಆಕೆಗೆ ದುಃಖವಾಗುತ್ತದೆ; ಆದರೆ ಕೂಸನ್ನು ಹೆತ್ತ ಮೇಲೆ ಲೋಕದಲ್ಲಿ ಒಬ್ಬ ಮನುಷ್ಯನು ಹುಟ್ಟಿದನೆಂಬ ಆನಂದದಿಂದ ಆ ಬೇನೆಯನ್ನು ನೆನಸುವದಿಲ್ಲ.",
			"22": "<sup>22</sup> ಹಾಗೆಯೇ ನಿಮಗೂ ಈಗ ದುಃಖವಿದ್ದರೂ ನಾನು ನಿಮ್ಮನ್ನು ತಿರಿಗಿ ನೋಡುವೆನು, ಆಗ ನಿಮ್ಮ ಹೃದಯಕ್ಕೆ ಆನಂದವಾಗುವದು, ಮತ್ತು ನಿಮ್ಮ ಆನಂದವನ್ನು ಯಾರೂ ನಿಮ್ಮಿಂದ ತೆಗೆಯುವದಿಲ್ಲ.",
			"23": "<sup>23</sup> ಆ ದಿನದಲ್ಲಿ ನೀವು ನನಗೆ ಯಾವ ಪ್ರಶ್ನೆಯನ್ನೂ ಮಾಡುವದಿಲ್ಲ. ನಿಮಗೆ ನಿಜನಿಜವಾಗಿ ಹೇಳುತ್ತೇನೆ, ನೀವು ತಂದೆಯನ್ನು ಏನಾದರೂ ಬೇಡಿಕೊಂಡರೆ ಅದನ್ನು ಆತನು ನನ್ನ ಹೆಸರಿನ ಮೇಲೆ ನಿಮಗೆ ಕೊಡುವನು.",
			"24": "<sup>24</sup> ನೀವು ಇದುವರೆಗೆ ನನ್ನ ಹೆಸರಿನ ಮೇಲೆ ಯಾವದೊಂದನ್ನೂ ಬೇಡಿಕೊಂಡಿಲ್ಲ; ಬೇಡಿಕೊಳ್ಳಿರಿ, ನಿಮಗೆ ಸಿಕ್ಕುವದು; ಆಗ ನಿಮ್ಮ ಆನಂದವು ಪರಿಪೂರ್ಣವಾಗುವದು.",
			"25": "<sup>25</sup> ಈ ಸಂಗತಿಗಳನ್ನು ಗೂಢಾರ್ಥವಾಗಿ ನಿಮಗೆ ಹೇಳಿದ್ದೇನೆ; ಆದರೆ ಇನ್ನು ಮೇಲೆ ಸಾಮ್ಯರೂಪವಾಗಿ ನಿಮ್ಮ ಸಂಗಡ ಮಾತಾಡದೆ ತಂದೆಯ ವಿಷಯದಲ್ಲಿ ಸ್ಪಷ್ಟವಾಗಿ ತಿಳಿಸುವ ಕಾಲ ಬರುತ್ತದೆ.",
			"26": "<sup>26</sup> ಆ ದಿನದಲ್ಲಿ ನನ್ನ ಹೆಸರಿನ ಮೇಲೆ ಬೇಡಿಕೊಳ್ಳುವಿರಿ. ನಾನು ತಂದೆಯನ್ನು ನಿಮಗೋಸ್ಕರ ಕೇಳಿಕೊಳ್ಳುವೆನೆಂತ ನಿಮಗೆ ಹೇಳುವದಿಲ್ಲ;",
			"27": "<sup>27</sup> ನೀವು ನನ್ನ ಮೇಲೆ ಮಮತೆಯಿಟ್ಟು ನನ್ನನ್ನು ತಂದೆಯ ಬಳಿಯಿಂದ ಹೊರಟುಬಂದವನೆಂದು ನಂಬಿದ್ದರಿಂದ ತಂದೆಯು ತಾನೇ ನಿಮ್ಮ ಮೇಲೆ ಮಮತೆ ಇಡುತ್ತಾನಲ್ಲವೇ.",
			"28": "<sup>28</sup> ತಂದೆಯ ಬಳಿಯಿಂದ ಹೊರಟು ಈ ಲೋಕಕ್ಕೆ ಬಂದಿದ್ದೇನೆ; ಇನ್ನು ಲೋಕವನ್ನು ಬಿಟ್ಟು ತಂದೆಯ ಬಳಿಗೆ ಹೋಗುತ್ತೇನೆ ಅಂದನು.",
			"29": "<sup>29</sup> ಅದಕ್ಕೆ ಶಿಷ್ಯರು - ನೀನು ಹೇಳುವ ಸಂಗತಿಯನ್ನು ಈಗ ಗೂಢಾರ್ಥವಾಗಿ ಹೇಳದೆ ಸ್ಪಷ್ಟವಾಗಿ ಮಾತಾಡುತ್ತೀಯಲ್ಲಾ;",
			"30": "<sup>30</sup> ನೀನು ಸಮಸ್ತವನ್ನು ತಿಳಿದವನೆಂದೂ ನಿನಗೆ ಪ್ರಶ್ನೆಮಾಡಬೇಕಾದ ಅವಶ್ಯವಿಲ್ಲವೆಂದೂ ಈಗ ತಿಳಿಯಿತು; ಇದರಿಂದ ನಿನ್ನನ್ನು ದೇವರ ಬಳಿಯಿಂದ ಬಂದವನೆಂದು ನಂಬುತ್ತೇವೆ ಅಂದದ್ದಕ್ಕೆ",
			"31": "<sup>31</sup> ಯೇಸು - ಈಗ ನಂಬುತ್ತೀರೋ?",
			"32": "<sup>32</sup> ನೋಡಿರಿ, ನಿಮ್ಮಲ್ಲಿ ಒಬ್ಬೊಬ್ಬನು ತನ್ನತನ್ನ ಸ್ಥಳಕ್ಕೆ ಚದರಿಹೋಗಿ ನನ್ನನ್ನು ಒಂಟಿಗನಾಗಿ ಬಿಡುವ ಕಾಲ ಬರುವದು, ಈಗ ಬಂದಿದೆ. ಆದರೆ ನಾನು ಒಂಟಿಗನಲ್ಲ, ತಂದೆಯು ನನ್ನ ಸಂಗಡ ಇದ್ದಾನೆ.",
			"33": "<sup>33</sup> ನೀವು ನನ್ನಲ್ಲಿದ್ದು ಮನಶ್ಶಾಂತಿಯನ್ನು ಹೊಂದಿದವರಾಗಿರಬೇಕೆಂದು ಇದನ್ನೆಲ್ಲಾ ನಿಮಗೆ ಹೇಳಿದ್ದೇನೆ. ಲೋಕದಲ್ಲಿ ನಿಮಗೆ ಸಂಕಟ ಉಂಟು; ಧೈರ್ಯವಾಗಿರಿ, ನಾನು ಲೋಕವನ್ನು ಜಯಿಸಿದ್ದೇನೆ ಎಂದು ಹೇಳಿದನು."
		},
		"17": {
			"1": "<sup>1</sup> ಯೇಸು ಈ ಮಾತುಗಳನ್ನಾಡಿದ ಮೇಲೆ ಆಕಾಶದ ಕಡೆಗೆ ಕಣ್ಣೆತ್ತಿ ಹೇಳಿದ್ದೇನಂದರೆ - ತಂದೆಯೇ, ಕಾಲ ಬಂದದೆ; ನಿನ್ನ ಮಗನನ್ನು ಮಹಿಮೆಪಡಿಸು, ಆಗ ಮಗನು ನಿನ್ನನ್ನು ಮಹಿಮೆಪಡಿಸುವದಕ್ಕಾಗುವದು.",
			"2": "<sup>2</sup> ನೀನು ಅವನಿಗೆ ಯಾರಾರನ್ನು ಕೊಟ್ಟಿದ್ದೀಯೋ ಅವರೆಲ್ಲರಿಗೆ ಅವನು ನಿತ್ಯಜೀವವನ್ನು ಕೊಡಬೇಕೆಂದು ಅವನಿಗೆ ಎಲ್ಲಾ ಮನುಷ್ಯರ ಮೇಲೆ ಅಧಿಕಾರವನ್ನು ಕೊಟ್ಟಿಯಲ್ಲಾ.",
			"3": "<sup>3</sup> ಒಬ್ಬನೇ ಸತ್ಯದೇವರಾಗಿರುವ ನಿನ್ನನ್ನೂ ನೀನು ಕಳುಹಿಸಿಕೊಟ್ಟ ಯೇಸು ಕ್ರಿಸ್ತನನ್ನೂ ತಿಳಿಯುವದೇ ನಿತ್ಯಜೀವವು.",
			"4": "<sup>4</sup> ಮಾಡಬೇಕೆಂದು ನೀನು ನನಗೆ ಕೊಟ್ಟ ಕೆಲಸವನ್ನು ನಾನು ನೆರವೇರಿಸಿ ನಿನ್ನನ್ನು ಭೂಲೋಕದಲ್ಲಿ ಮಹಿಮೆಪಡಿಸಿದೆನು.",
			"5": "<sup>5</sup> ಈಗ ತಂದೆಯೇ, ನೀನು ನಿನ್ನ ಬಳಿಯಲ್ಲಿ ನನ್ನನ್ನು ಮಹಿಮೆಪಡಿಸು; ಲೋಕ ಉಂಟಾಗುವದಕ್ಕಿಂತ ಮುಂಚೆ ನಿನ್ನ ಬಳಿಯಲ್ಲಿ ನನಗಿದ್ದ ಮಹಿಮೆಯಿಂದಲೇ ನನ್ನನ್ನು ಮಹಿಮೆಪಡಿಸು.",
			"6": "<sup>6</sup> ಲೋಕದೊಳಗಿಂದ ನೀನು ನನಗೆ ಕೊಟ್ಟ ಮನುಷ್ಯರಿಗೆ ನಿನ್ನ ಹೆಸರನ್ನು ತಿಳಿಯಪಡಿಸಿದೆನು. ಇವರು ನಿನ್ನವರಾಗಿದ್ದರು, ನೀನು ಇವರನ್ನು ನನಗೆ ಕೊಟ್ಟೆ; ಮತ್ತು ಇವರು ನಿನ್ನ ವಾಕ್ಯವನ್ನು ಕೈಕೊಂಡು ನಡೆದಿದ್ದಾರೆ.",
			"7": "<sup>7</sup> ನೀನು ನನಗೆ ಕೊಟ್ಟದ್ದೆಲ್ಲಾ ನಿನ್ನಿಂದಲೇ ಬಂದದೆ ಎಂದು ಈಗ ಇವರು ತಿಳುಕೊಂಡಿದ್ದಾರೆ.",
			"8": "<sup>8</sup> ಹೇಗಂದರೆ ನೀನು ನನಗೆ ಕೊಟ್ಟ ಮಾತುಗಳನ್ನು ನಾನು ಇವರಿಗೆ ಕೊಟ್ಟಿದ್ದೇನೆ; ಇವರು ಆ ಮಾತುಗಳನ್ನು ಕೈಕೊಂಡು ನನ್ನನ್ನು ನಿನ್ನ ಬಳಿಯಿಂದ ಹೊರಟುಬಂದವನೆಂದು ನಿಜವಾಗಿ ತಿಳಿದು ನೀನೇ ನನ್ನನ್ನು ಕಳುಹಿಸಿಕೊಟ್ಟಿರುವದಾಗಿ ನಂಬಿದ್ದಾರೆ.",
			"9": "<sup>9</sup> ನಾನು ಇವರಿಗೋಸ್ಕರ ಕೇಳಿಕೊಳ್ಳುತ್ತೇನೆ; ಲೋಕಕ್ಕೋಸ್ಕರ ಕೇಳಿಕೊಳ್ಳದೆ ನೀನು ನನಗೆ ಕೊಟ್ಟವರಿಗೋಸ್ಕರವೇ ಕೇಳಿಕೊಳ್ಳುತ್ತೇನೆ; ನಿನ್ನವರಾಗಿದ್ದಾರೆ.",
			"10": "<sup>10</sup> ನನ್ನದೆಲ್ಲಾ ನಿನ್ನದೇ, ನಿನ್ನದೆಲ್ಲಾ ನನ್ನದೇ; ಮತ್ತು ಇವರಿಂದ ನನಗೆ ಮಹಿಮೆ ಉಂಟಾಗಿದೆ.",
			"11": "<sup>11</sup> ಇನ್ನು ನಾನು ಲೋಕದಲ್ಲಿ ಇರುವದಿಲ್ಲ, ಇವರು ಲೋಕದಲ್ಲಿ ಇರುತ್ತಾರೆ, ನಾನು ನಿನ್ನ ಬಳಿಗೆ ಬರುತ್ತೇನೆ. ಪವಿತ್ರನಾದ ತಂದೆಯೇ, ನಾವು ಒಂದಾಗಿರುವ ಹಾಗೆ ಇವರೂ ಒಂದಾಗಿರಬೇಕೆಂದು ನೀನು ನನಗೆ ಕೊಟ್ಟ ನಿನ್ನ ಹೆಸರಿನಲ್ಲಿ ಇವರನ್ನು ಕಾಯಬೇಕು.",
			"12": "<sup>12</sup> ನಾನು ಇವರ ಸಂಗಡ ಇದ್ದಾಗ ನೀನು ನನಗೆ ಕೊಟ್ಟಿರುವ ನಿನ್ನ ಹೆಸರಿನಲ್ಲಿ ಇವರನ್ನು ಕಾಯುತ್ತಾ ಬಂದೆನು, ಇವರನ್ನು ಕಾಪಾಡಿದೆನು; ಶಾಸ್ತ್ರದ ಮಾತು ನೆರವೇರುವಂತೆ ನಾಶಕ್ಕೆ ಗುರಿಯಾದ ಆ ಮನುಷ್ಯನೇ ಹೊರತು ಇವರಲ್ಲಿ ಮತ್ತಾರೂ ನಾಶವಾಗಲಿಲ್ಲ.",
			"13": "<sup>13</sup> ಆದರೆ ಈಗ ನಿನ್ನ ಬಳಿಗೆ ಬರುವವನಾಗಿದ್ದೇನೆ; ಆದದರಿಂದ ಇವರಲ್ಲಿ ನನ್ನ ಆನಂದವು ಪರಿಪೂರ್ಣವಾಗಿರುವಂತೆ ಲೋಕದಲ್ಲಿದ್ದು ಈ ಮಾತುಗಳನ್ನು ಆಡುತ್ತೇನೆ.",
			"14": "<sup>14</sup> ನಾನು ನಿನ್ನ ವಾಕ್ಯವನ್ನು ಇವರಿಗೆ ಕೊಟ್ಟಿದ್ದೇನೆ; ನಾನು ಲೋಕದವನಲ್ಲದೆ ಇರುವ ಪ್ರಕಾರ ಇವರೂ ಲೋಕದವರಲ್ಲ; ಆದಕಾರಣ ಲೋಕವು ಇವರ ಮೇಲೆ ದ್ವೇಷ ಮಾಡಿ ಅದೆ.",
			"15": "<sup>15</sup> ಇವರನ್ನು ಲೋಕದೊಳಗಿಂದ ತೆಗೆದುಕೊಂಡು ಹೋಗಬೇಕೆಂದು ನಾನು ಕೇಳಿಕೊಳ್ಳುವದಿಲ್ಲ; ಕೆಡುಕನಿಂದ ತಪ್ಪಿಸಿ ಕಾಪಾಡಬೇಕೆಂದು ಕೇಳಿಕೊಳ್ಳುತ್ತೇನೆ.",
			"16": "<sup>16</sup> ನಾನು ಲೋಕದವನಲ್ಲದೆ ಇರುವ ಪ್ರಕಾರ ಇವರು ಲೋಕದವರಲ್ಲ.",
			"17": "<sup>17</sup> ಇವರನ್ನು ಸತ್ಯದಲ್ಲಿ ಸೇರಿಸಿ ಪ್ರತಿಷ್ಠೆ ಪಡಿಸು; ನಿನ್ನ ವಾಕ್ಯವೇ ಸತ್ಯವು.",
			"18": "<sup>18</sup> ನೀನು ನನ್ನನ್ನು ಲೋಕಕ್ಕೆ ಕಳುಹಿಸಿಕೊಟ್ಟಂತೆ ನಾನೂ ಇವರನ್ನು ಲೋಕಕ್ಕೆ ಕಳುಹಿಸಿಕೊಟ್ಟೆನು.",
			"19": "<sup>19</sup> ಇವರು ನಿಜವಾಗಿ ಪ್ರತಿಷ್ಠಿತರಾಗಬೇಕೆಂದು ಇವರಿಗೋಸ್ಕರ ನಾನು ನನ್ನನ್ನು ಪ್ರತಿಷ್ಠೆಪಡಿಸಿಕೊಳ್ಳುತ್ತೇನೆ.",
			"20": "<sup>20</sup> ಆದರೆ ಇವರಿಗೋಸ್ಕರ ಮಾತ್ರವಲ್ಲದೆ ಇವರ ವಾಕ್ಯದಿಂದ ನನ್ನನ್ನು ನಂಬುವವರಿಗೋಸ್ಕರ ಸಹ ಕೇಳಿಕೊಳ್ಳುತ್ತೇನೆ.",
			"21": "<sup>21</sup> ನೀನು ನನ್ನನ್ನು ಕಳುಹಿಸಿಕೊಟ್ಟಿದ್ದೀ ಎಂದು ಲೋಕವು ನಂಬುವದಕ್ಕಾಗಿ ಅವರೆಲ್ಲರೂ ಒಂದಾಗಿರಬೇಕೆಂತಲೂ ತಂದೆಯೇ, ನೀನು ನನ್ನಲ್ಲಿಯೂ ನಾನು ನಿನ್ನಲ್ಲಿಯೂ ಇರುವ ಪ್ರಕಾರ ಅವರೂ ನಮ್ಮಲ್ಲಿ ಇರಬೇಕೆಂತಲೂ ಕೇಳಿಕೊಳ್ಳುತ್ತೇನೆ.",
			"22": "<sup>22</sup> ನಾವು ಒಂದಾಗಿರುವ ಪ್ರಕಾರ ಅವರೂ ಒಂದಾಗಿರಬೇಕೆಂದು ನೀನು ನನಗೆ ಕೊಟ್ಟಿರುವ ಮಹಿಮೆಯನ್ನು ನಾನು ಅವರಿಗೆ ಕೊಟ್ಟಿದ್ದೇನೆ.",
			"23": "<sup>23</sup> ನಾನು ಅವರಲ್ಲಿಯೂ ನೀನು ನನ್ನಲ್ಲಿಯೂ ಇರಲಾಗಿ ಅವರ ಐಕ್ಯವು ಪೂರ್ಣಸಿದ್ಧಿಗೆ ಬರುವದರಿಂದ ನೀನು ನನ್ನನ್ನು ಕಳುಹಿಸಿಕೊಟ್ಟಿದ್ದೀ ಎಂದೂ ನೀನು ನನ್ನನ್ನು ಪ್ರೀತಿಸಿದಂತೆ ಅವರನ್ನೂ ಪ್ರೀತಿಸಿದ್ದೀ ಎಂದೂ ಲೋಕಕ್ಕೆ ತಿಳಿದುಬರುವದು.",
			"24": "<sup>24</sup> ತಂದೆಯೇ, ನೀನು ಯಾರನ್ನು ನನಗೆ ಕೊಟ್ಟಿಯೋ ಅವರು ನಾನಿರುವ ಸ್ಥಳದಲ್ಲಿ ನನ್ನ ಕೂಡ ಇದ್ದುಕೊಂಡು ಲೋಕವು ಹುಟ್ಟುವದಕ್ಕಿಂತ ಮುಂಚೆಯೇ ನೀನು ನನ್ನನ್ನು ಪ್ರೀತಿಸಿ ನನಗೆ ಕೊಟ್ಟಿರುವ ಮಹಿಮೆಯನ್ನು ನೋಡಬೇಕೆಂದು ಇಚ್ಫೈಸುತ್ತೇನೆ.",
			"25": "<sup>25</sup> ನೀತಿಸ್ವರೂಪನಾದ ತಂದೆಯೇ, ಲೋಕವಂತೂ ನಿನ್ನನ್ನು ತಿಳಿಯಲಿಲ್ಲ, ಆದರೆ ನಾನು ನಿನ್ನನ್ನು ತಿಳಿದಿದ್ದೇನೆ; ಮತ್ತು ಇವರು ನೀನೇ ನನ್ನನ್ನು ಕಳುಹಿಸಿಕೊಟ್ಟಿರುವದಾಗಿ ತಿಳುಕೊಂಡಿದ್ದಾರೆ.",
			"26": "<sup>26</sup> ನಾನು ಅವರಿಗೆ ನಿನ್ನ ಹೆಸರನ್ನು ತಿಳಿಸಿದ್ದೇನೆ, ಇನ್ನೂ ತಿಳಿಸುವೆನು. ನೀನು ನನ್ನ ಮೇಲೆ ಇಟ್ಟಂಥ ಪ್ರೀತಿಯು ಅವರಲ್ಲಿ ಇರಬೇಕೆಂದೂ ನಾನೂ ಅವರಲ್ಲಿ ಇರಬೇಕೆಂದೂ ಪ್ರಾರ್ಥಿಸುತ್ತೇನೆ ಅಂದನು."
		},
		"18": {
			"1": "<sup>1</sup> ಯೇಸು ಇದನ್ನು ಹೇಳಿದ ಮೇಲೆ ತನ್ನ ಶಿಷ್ಯರ ಸಂಗಡ ಕೆದ್ರೋನ್ ಹಳ್ಳದ ಆಚೆಗೆ ಹೊರಟುಹೋದನು. ಅಲ್ಲಿ ಒಂದು ತೋಟವಿತ್ತು; ಅದರೊಳಕ್ಕೆ ಆತನೂ ಆತನ ಶಿಷ್ಯರೂ ಹೋದರು.",
			"2": "<sup>2</sup> ಯೇಸುವೂ ಆತನ ಶಿಷ್ಯರೂ ಆಗಾಗ್ಗೆ ಅಲ್ಲಿ ಕೂಡುತ್ತಾ ಇದ್ದದರಿಂದ ಆತನನ್ನು ಹಿಡುಕೊಟ್ಟ ಯೂದನಿಗೂ ಆ ಸ್ಥಳವು ಗೊತ್ತಾಗಿತ್ತು.",
			"3": "<sup>3</sup> ಹೀಗಿರಲಾಗಿ ಯೂದನು ಸಿಪಾಯಿಗಳ ಪಟಾಲಮನ್ನೂ ಮಹಾಯಾಜಕರು ಮತ್ತು ಫರಿಸಾಯರು ಕೊಟ್ಟ ಓಲೇಕಾರರನ್ನೂ ಕರಕೊಂಡು ದೀವಿಟಿಗಳನ್ನೂ ಪಂಜುಗಳನ್ನೂ ಆಯುಧಗಳನ್ನೂ ಹಿಡಿಸಿಕೊಂಡು ಅಲ್ಲಿಗೆ ಬಂದನು.",
			"4": "<sup>4</sup> ಆಗ ಯೇಸು ತನ್ನ ಮೇಲೆ ಬರುವದನ್ನೆಲ್ಲಾ ತಿಳಿದು ಹೊರಗೆ ಹೋಗಿ - ನೀವು ಯಾರನ್ನು ಹುಡುಕುತ್ತೀರಿ ಎಂದು ಅವರನ್ನು ಕೇಳಿದನು.",
			"5": "<sup>5</sup> ಅದಕ್ಕೆ ಅವರು - ನಜರೇತಿನ ಯೇಸುವನ್ನು ಹುಡುಕುತ್ತೇವೆ ಅನ್ನಲು ಯೇಸು ಅವರಿಗೆ - ನಾನೇ ಅವನು ಎಂದು ಹೇಳಿದನು. ಆತನನ್ನು ಹಿಡುಕೊಡುವ ಯೂದನು ಸಹ ಅವರ ಸಂಗಡ ನಿಂತಿದ್ದನು.",
			"6": "<sup>6</sup> ಆತನು ಅವರಿಗೆ - ನಾನೇ ಅವನು ಎಂದು ಹೇಳಿದಾಗ ಅವರು ಹಿಂದಕ್ಕೆ ಸರಿದು ನೆಲದ ಮೇಲೆ ಬಿದ್ದರು.",
			"7": "<sup>7</sup> ಆತನು - ಯಾರನ್ನು ಹುಡುಕುತ್ತೀರೆಂದು ತಿರಿಗಿ ಅವರನ್ನು ಕೇಳಲು ಅವರು - ನಜರೇತಿನ ಯೇಸುವನ್ನು ಹುಡುಕುತ್ತೇವೆ ಅಂದರು.",
			"8": "<sup>8</sup> ಅದಕ್ಕೆ ಯೇಸು - ನಾನೇ ಅವನೆಂದು ನಿಮಗೆ ಹೇಳಿದೆನಲ್ಲಾ; ನೀವು ನನ್ನನ್ನೇ ಹುಡುಕುವವರಾದರೆ ಇವರು ಹೋಗಬಿಡಿರಿ ಅಂದನು.",
			"9": "<sup>9</sup> ನೀನು ನನಗೆ ಕೊಟ್ಟವರಲ್ಲಿ ಒಬ್ಬನನ್ನಾದರೂ ನಾನು ಕಳಕೊಳ್ಳಲಿಲ್ಲವೆಂದು ಆತನು ಹೇಳಿದ ಮಾತು ಹೀಗೆ ನೆರವೇರಿತು.",
			"10": "<sup>10</sup> ಸೀಮೋನ ಪೇತ್ರನಲ್ಲಿ ಒಂದು ಕತ್ತಿ ಇತ್ತು; ಅದನ್ನು ಅವನು ಹಿರಿದು ಮಹಾಯಾಜಕನ ಆಳನ್ನು ಹೊಡೆದು ಅವನ ಬಲಗಿವಿಯನ್ನು ಕತ್ತರಿಸಿದನು. ಆ ಆಳಿನ ಹೆಸರು ಮಲ್ಕನು.",
			"11": "<sup>11</sup> ಆಗ ಯೇಸು ಪೇತ್ರನಿಗೆ - ಕತ್ತಿಯನ್ನು ಒರೆಯಲ್ಲಿ ಹಾಕು; ತಂದೆ ನನಗೆ ಕೊಟ್ಟಿರುವ ಪಾತ್ರೆಯಲ್ಲಿ ನಾನು ಕುಡಿಯಬಾರದೋ ಎಂದು ಹೇಳಿದನು.",
			"12": "<sup>12</sup> ಆಮೇಲೆ ಆ ಸಿಪಾಯಿಗಳೂ ಸಹಸ್ರಾಧಿಪತಿಯೂ ಯೆಹೂದ್ಯರ ಓಲೇಕಾರರೂ ಯೇಸುವನ್ನು ಹಿಡುಕೊಂಡು ಕಟ್ಟಿ ಮೊದಲು ಅನ್ನನ ಬಳಿಗೆ ಕರಕೊಂಡು ಹೋದರು.",
			"13": "<sup>13</sup> ಇವನು ಆ ವರುಷದಲ್ಲಿ ಮಹಾಯಾಜಕನಾಗಿದ್ದ ಕಾಯಫನ ಮಾವನಾಗಿದ್ದನು.",
			"14": "<sup>14</sup> ಆ ಕಾಯಫನೇ - ಒಬ್ಬ ಮನುಷ್ಯನು ಜನಕ್ಕೋಸ್ಕರ ಸಾಯುವದು ಹಿತಕರವೆಂದು ಯೆಹೂದ್ಯರಿಗೆ ಆಲೋಚನೆ ಕೊಟ್ಟವನು.",
			"15": "<sup>15</sup> ಸೀಮೋನ ಪೇತ್ರನೂ ಮತ್ತೊಬ್ಬ ಶಿಷ್ಯನೂ ಯೇಸುವಿನ ಹಿಂದೆ ಹೋದರು. ಆ ಶಿಷ್ಯನಿಗೆ ಮಹಾಯಾಜಕನ ಪರಿಚಿತಿ ಇದ್ದದರಿಂದ ಯೇಸುವಿನ ಕೂಡ ಮಹಾಯಾಜಕನ ಅಂಗಳದೊಳಕ್ಕೆ ಹೋದನು;",
			"16": "<sup>16</sup> ಪೇತ್ರನು ಹೊರಗೆ ಬಾಗಲ ಹತ್ತರ ನಿಂತಿದ್ದರಿಂದ ಮಹಾಯಾಜಕನ ಪರಿಚಿತನಾದ ಆ ಶಿಷ್ಯನು ಹೊರಗೆ ಬಂದು ಬಾಗಲು ಕಾಯುವವಳ ಸಂಗಡ ಮಾತಾಡಿ ಪೇತ್ರನನ್ನು ಒಳಕ್ಕೆ ಕರಕೊಂಡು ಹೋದನು.",
			"17": "<sup>17</sup> ಆಗ ಬಾಗಲು ಕಾಯುವ ದಾಸಿಯು - ನೀನು ಸಹ ಈ ಮನುಷ್ಯನ ಶಿಷ್ಯರಲ್ಲಿ ಒಬ್ಬನೇನು? ಎಂದು ಪೇತ್ರನನ್ನು ಕೇಳಲು ಅವನು - ಅಲ್ಲ ಅಂದನು.",
			"18": "<sup>18</sup> ಆಗ ಚಳಿಯಾಗಿದ್ದದರಿಂದ ಆಳುಗಳೂ ಓಲೇಕಾರರೂ ಇದ್ದಲಿನ ಬೆಂಕಿಮಾಡಿ ಚಳಿಕಾಯಿಸಿಕೊಳ್ಳುತ್ತಾ ನಿಂತಿರಲಾಗಿ ಪೇತ್ರನೂ ಅವರ ಸಂಗಡ ನಿಂತುಕೊಂಡು ಚಳಿಕಾಯಿಸಿಕೊಳ್ಳುತ್ತಿದ್ದನು.",
			"19": "<sup>19</sup> ಅಷ್ಟರೊಳಗೆ ಮಹಾಯಾಜಕನು ಯೇಸುವನ್ನು ಆತನ ಶಿಷ್ಯರ ವಿಷಯವಾಗಿಯೂ ಉಪದೇಶದ ವಿಷಯವಾಗಿಯೂ ವಿಚಾರಿಸಿದನು.",
			"20": "<sup>20</sup> ಅದಕ್ಕೆ ಯೇಸು - ನಾನು ಧಾರಾಳವಾಗಿ ಲೋಕದ ಮುಂದೆ ಮಾತಾಡಿದ್ದೇನೆ; ಯೆಹೂದ್ಯರೆಲ್ಲಾ ಕೂಡುವಂಥ ಸಭಾಮಂದಿರಗಳಲ್ಲಿಯೂ ದೇವಾಲಯದಲ್ಲಿಯೂ ಯಾವಾಗಲೂ ಉಪದೇಶಮಾಡುತ್ತಾ ಬಂದೆನು; ಮರೆಯಾಗಿ ಯಾವದನ್ನೂ ಮಾತಾಡಲಿಲ್ಲ.",
			"21": "<sup>21</sup> ನನ್ನನ್ನು ಯಾಕೆ ವಿಚಾರಿಸುತ್ತೀ? ನಾನು ಏನೇನು ಮಾತಾಡಿದೆನೋ ಅದನ್ನು ಕೇಳಿದವರಲ್ಲಿ ವಿಚಾರಿಸು; ನಾನು ಹೇಳಿದ್ದು ಇವರಿಗೆ ತಿಳಿದಿದೆಯಲ್ಲಾ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"22": "<sup>22</sup> ಯೇಸು ಈ ಮಾತನ್ನು ಹೇಳಿದಾಗ ಹತ್ತರ ನಿಂತಿದ್ದ ಓಲೇಕಾರರಲ್ಲಿ ಒಬ್ಬನು - ಮಹಾಯಾಜಕನಿಗೆ ಹೀಗೆ ಉತ್ತರಕೊಡುತ್ತೀಯಾ ಎಂದು ಹೇಳಿ ಆತನ ಕೆನ್ನೆಗೆ ಒಂದು ಏಟು ಹಾಕಿದನು.",
			"23": "<sup>23</sup> ಯೇಸು ಅವನಿಗೆ - ನಾನು ಮಾತಾಡಿದ್ದರಲ್ಲಿ ಏನಾದರೂ ದೋಷವಿದ್ದರೆ ಆ ದೋಷ ಇಂಥದೆಂದು ಸಾಕ್ಷಿ ಹೇಳು; ನಾನು ಮಾತಾಡಿದ್ದು ಸರಿಯಾಗಿದ್ದರೆ ನನ್ನನ್ನು ಯಾಕೆ ಹೊಡೆಯುತ್ತೀ ಎಂದು ಹೇಳಿದನು.",
			"24": "<sup>24</sup> ಆಗ ಅನ್ನನು ಆತನನ್ನು ಕಟ್ಟಿಸಿ ಮಹಾಯಾಜಕನಾದ ಕಾಯಫನ ಬಳಿಗೆ ಕಳುಹಿಸಿಕೊಟ್ಟನು.",
			"25": "<sup>25</sup> ಇತ್ತಲಾಗಿ ಸೀಮೋನ ಪೇತ್ರನು ನಿಂತುಕೊಂಡು ಚಳಿಕಾಸಿಕೊಳ್ಳುತ್ತಾ ಇದ್ದನು. ಆಗ ಅಲ್ಲಿದ್ದವರು ಅವನನ್ನು - ನೀನು ಅವನ ಶಿಷ್ಯರಲ್ಲಿ ಒಬ್ಬನೇನು? ಎಂದು ಕೇಳಲು ಅವನು - ಅಲ್ಲ ಅಂದನು.",
			"26": "<sup>26</sup> ಮಹಾಯಾಜಕನ ಆಳುಗಳಲ್ಲಿ ಪೇತ್ರನು ಕಿವಿ ಕತ್ತರಿಸಿದವನ ಬಂಧುವಾಗಿದ್ದ ಒಬ್ಬನು - ನಾನು ನಿನ್ನನ್ನು ತೋಟದಲ್ಲಿ ಅವನ ಸಂಗಡ ಕಂಡೆನಲ್ಲವೇ ಅಂದನು.",
			"27": "<sup>27</sup> ಅದಕ್ಕೆ ಪೇತ್ರನು ತಿರಿಗಿ ಅಲ್ಲ ಅಂದನು. ಕೂಡಲೆ ಕೋಳಿ ಕೂಗಿತು.",
			"28": "<sup>28</sup> ಆಮೇಲೆ ಅವರು ಯೇಸುವನ್ನು ಕಾಯಫನ ಬಳಿಯಿಂದ ಅರಮನೆಗೆ ಕರಕೊಂಡು ಹೋದರು. ಆಗ ಮುಂಜಾನೆ ಆಗಿತ್ತು. ಅವರು ತಮಗೆ ಮೈಲಿಗೆ ತಟ್ಟಿ ಪಸ್ಕದ ಊಟವನ್ನು ಮಾಡುವದಕ್ಕೆ ಅಡ್ಡಿಯಾದೀತೆಂದು ಅರಮನೆಯೊಳಕ್ಕೆ ಹೋಗಲಿಲ್ಲ.",
			"29": "<sup>29</sup> ಹೀಗಿರಲಾಗಿ ಪಿಲಾತನು ಹೊರಕ್ಕೆ ಅವರ ಬಳಿಗೆ ಬಂದು - ಈ ಮನುಷ್ಯನ ಮೇಲೆ ಏನು ದೂರು ತರುತ್ತೀರಿ ಎಂದು ಕೇಳಿದನು.",
			"30": "<sup>30</sup> ಅವರು ಅವನಿಗೆ - ಇವನು ದುಷ್ಕರ್ಮಿಯಲ್ಲದಿದ್ದರೆ ನಾವು ಇವನನ್ನು ನಿನಗೆ ಒಪ್ಪಿಸಿಕೊಡುತ್ತಿದ್ದಿಲ್ಲವೆಂದು ಉತ್ತರ ಕೊಟ್ಟರು.",
			"31": "<sup>31</sup> ಆಗ ಪಿಲಾತನು ಅವರಿಗೆ - ನೀವೇ ಅವನನ್ನು ತಕ್ಕೊಂಡುಹೋಗಿ ನಿಮ್ಮ ಧರ್ಮಶಾಸ್ತ್ರದ ಪ್ರಕಾರ ಅವನಿಗೆ ತೀರ್ಪುಮಾಡಿರಿ ಎಂದು ಹೇಳಿದನು. ಅದಕ್ಕೆ ಯೆಹೂದ್ಯರು - ಮರಣ ದಂಡನೆಯನ್ನು ವಿಧಿಸುವ ಅಧಿಕಾರವು ನಮಗಿಲ್ಲ ಅಂದರು.",
			"32": "<sup>32</sup> ಇದರಿಂದ ಯೇಸು ತಾನು ಎಂಥಾ ಸಾವು ಸಾಯುವೆನೆಂದು ಸೂಚಿಸಿ ಹೇಳಿದ ಮಾತು ನೆರವೇರಿತು.",
			"33": "<sup>33</sup> ಆಗ ಪಿಲಾತನು ತಿರಿಗಿ ಅರಮನೆಯೊಳಕ್ಕೆ ಹೋಗಿ ಯೇಸುವನ್ನು ಕರೆದು ಆತನನ್ನು - ನೀನು ಯೆಹೂದ್ಯರ ಅರಸನು ಹೌದೋ ಎಂದು ಕೇಳಿದನು.",
			"34": "<sup>34</sup> ಯೇಸು - ನಿನ್ನಷ್ಟಕ್ಕೆ ನೀನೇ ಇದನ್ನು ಹೇಳುತ್ತೀಯೋ? ಮತ್ತೊಬ್ಬರು ನನ್ನ ವಿಷಯದಲ್ಲಿ ನಿನಗೆ ಹೇಳಿದ್ದಾರೋ ಎಂದು ಕೇಳಲು",
			"35": "<sup>35</sup> ಪಿಲಾತನು - ನಾನೇನು ಯೆಹೂದ್ಯನೇ? ನಿನ್ನ ಸ್ವಂತ ಜನವೂ ಮಹಾಯಾಜಕರೂ ನಿನ್ನನ್ನು ನನಗೆ ಒಪ್ಪಿಸಿದ್ದಾರೆ; ನೀನು ಏನು ಮಾಡಿದ್ದೀ ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ",
			"36": "<sup>36</sup> ಯೇಸು - ನನ್ನ ರಾಜ್ಯವು ಈ ಲೋಕದ್ದಲ್ಲ; ನನ್ನ ರಾಜ್ಯವು ಈ ಲೋಕದ್ದಾಗಿದ್ದರೆ ನಾನು ಯೆಹೂದ್ಯರ ಕೈಯಲ್ಲಿ ಬೀಳದಂತೆ ನನ್ನ ಪರಿವಾರದವರು ಕಾದಾಡುತ್ತಿದ್ದರು; ಆದರೆ ನನ್ನ ರಾಜ್ಯವು ಇಲ್ಲಿಯದಲ್ಲವೆಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"37": "<sup>37</sup> ಅದಕ್ಕೆ ಪಿಲಾತನು - ಹಾಗಾದರೆ ನೀನು ಅರಸನು ಹೌದಲ್ಲವೇ ಅಂದನು. ಯೇಸು ಅವನಿಗೆ - ನನ್ನನ್ನು ಅರಸನೆಂದು ನೀನೇ ಹೇಳಿದ್ದೀ. ನಾನು ಸತ್ಯದ ವಿಷಯದಲ್ಲಿ ಸಾಕ್ಷಿ ಹೇಳುವದಕ್ಕೋಸ್ಕರ ಹುಟ್ಟಿದವನು, ಅದಕ್ಕೋಸ್ಕರವೇ ಈ ಲೋಕಕ್ಕೆ ಬಂದಿದ್ದೇನೆ. ಸತ್ಯಪರರೆಲ್ಲರು ನನ್ನ ಮಾತಿಗೆ ಕಿವಿಕೊಡುತ್ತಾರೆ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"38": "<sup>38</sup> ಪಿಲಾತನು - ಸತ್ಯವಂದರೇನು? ಅಂದನು. ಇದನ್ನು ಹೇಳಿ ಪಿಲಾತನು ತಿರಿಗಿ ಯೆಹೂದ್ಯರ ಬಳಿಗೆ ಹೊರಕ್ಕೆ ಬಂದು - ನನಗೆ ಅವನಲ್ಲಿ ಯಾವ ಅಪರಾಧವೂ ಕಾಣಿಸುವದಿಲ್ಲ.",
			"39": "<sup>39</sup> ಆದರೆ ಪಸ್ಕಹಬ್ಬದಲ್ಲಿ ನಾನು ಒಬ್ಬನನ್ನು ನಿಮಗೆ ಬಿಟ್ಟುಕೊಡುವ ಪದ್ಧತಿ ಉಂಟಷ್ಟೆ; ನಾನು ಯೆಹೂದ್ಯರ ಅರಸನನ್ನು ಬಿಟ್ಟುಕೊಡುವದು ನಿಮಗೆ ಇಷ್ಟವೋ ಎಂದು ಅವರನ್ನು ಕೇಳಿದನು.",
			"40": "<sup>40</sup> ಅದಕ್ಕೆ ಅವರು - ಇವನನ್ನು ಬೇಡ, ಬರಬ್ಬನನ್ನು ಬಿಟ್ಟುಕೊಡಬೇಕು ಎಂದು ತಿರಿಗಿ ಕೂಗಿ ಹೇಳಿದರು. ಈ ಬರಬ್ಬನು ಕಳ್ಳನಾಗಿದ್ದನು."
		},
		"19": {
			"1": "<sup>1</sup> ಆಮೇಲೆ ಪಿಲಾತನು ಯೇಸುವನ್ನು ಕೊರಡೆಯಿಂದ ಹೊಡಿಸಿದನು.",
			"2": "<sup>2</sup> ಮತ್ತು ಸಿಪಾಯಿಗಳು ಮುಳ್ಳುಬಳ್ಳಿಯಿಂದ ಕಿರೀಟವನ್ನು ಹೆಣೆದು ಆತನ ತಲೆಯ ಮೇಲೆ ಇಟ್ಟು ಕೆಂಪು ಒಲ್ಲಿಯನ್ನು ಆತನಿಗೆ ಹೊದಿಸಿ",
			"3": "<sup>3</sup> ಆತನ ಬಳಿಗೆ ಬಂದು - ಯೆಹೂದ್ಯರ ಅರಸನೇ, ನಿನಗೆ ನಮಸ್ಕಾರ; ಎಂದು ಹೇಳಿ ಆತನ ಕೆನ್ನೆಗೆ ಏಟುಹಾಕಿದರು.",
			"4": "<sup>4</sup> ತರುವಾಯ ಪಿಲಾತನು ತಿರಿಗಿ ಹೊರಗೆ ಬಂದು ಯೆಹೂದ್ಯರಿಗೆ - ನನಗೆ ಅವನಲ್ಲಿ ಯಾವ ಅಪರಾಧವೂ ಕಾಣಿಸಲಿಲ್ಲವೆಂಬದು ನಿಮಗೆ ತಿಳಿಯುವಂತೆ ಅವನನ್ನು ನಿಮ್ಮ ಬಳಿಗೆ ಹೊರಗೆ ತರುತ್ತೇನೆ, ನೋಡಿರಿ ಎಂದು ಹೇಳಿದನು.",
			"5": "<sup>5</sup> ಆಗ ಯೇಸು ಮುಳ್ಳಿನ ಕಿರೀಟವನ್ನೂ ಕೆಂಪು ಒಲ್ಲಿಯನ್ನೂ ಧರಿಸಿದವನಾಗಿ ಹೊರಕ್ಕೆ ಬಂದನು. ಪಿಲಾತನು ಅವರಿಗೆ - ಇಗೋ, ಈ ಮನುಷ್ಯನು! ಅಂದನು.",
			"6": "<sup>6</sup> ಮಹಾಯಾಜಕರೂ ಓಲೇಕಾರರೂ ಆತನನ್ನು ಕಾಣುತ್ತಲೇ - ಶಿಲುಬೆಗೆ ಹಾಕಿಸು, ಶಿಲುಬೆಗೆ ಹಾಕಿಸು ಎಂದು ಕೂಗಿದರು. ಪಿಲಾತನು ಅವರಿಗೆ - ಬೇಕಾದರೆ ನೀವೇ ಅವನನ್ನು ತಕ್ಕೊಂಡುಹೋಗಿ ಶಿಲುಬೆಗೆ ಹಾಕಿಸಿರಿ; ನನಗೆ ಅವನಲ್ಲಿ ಅಪರಾಧವು ಕಾಣಿಸಲಿಲ್ಲವೆಂದು ಹೇಳಿದನು.",
			"7": "<sup>7</sup> ಯೆಹೂದ್ಯರು ಅವನಿಗೆ - ನಮಗೆ ಒಂದು ನೇಮ ಉಂಟು, ಆ ನೇಮದ ಪ್ರಕಾರ ಇವನು ಸಾಯತಕ್ಕವನು; ಯಾಕಂದರೆ ಅವನು ತನ್ನನ್ನು ದೇವರ ಮಗನಾಗಿ ಮಾಡಿಕೊಂಡಿದ್ದಾನೆ ಎಂದು ಉತ್ತರಕೊಟ್ಟರು.",
			"8": "<sup>8</sup> ಪಿಲಾತನು ಈ ಮಾತನ್ನು ಕೇಳಿ ಮತ್ತಷ್ಟು ಹೆದರಿಕೊಂಡು",
			"9": "<sup>9</sup> ತಿರಿಗಿ ಅರಮನೆಯೊಳಕ್ಕೆ ಹೋಗಿ - ನೀನು ಎಲ್ಲಿಂದ ಬಂದವನು ಎಂದು ಯೇಸುವನ್ನು ಕೇಳಿದನು. ಆದರೆ ಯೇಸು ಅವನಿಗೆ ಉತ್ತರಕೊಡಲಿಲ್ಲ.",
			"10": "<sup>10</sup> ಪಿಲಾತನು - ನನ್ನ ಸಂಗಡಲೂ ನೀನು ಮಾತಾಡುವದಿಲ್ಲವೋ? ನಿನ್ನನ್ನು ಬಿಡಿಸುವ ಅಧಿಕಾರವೂ ನಿನ್ನನ್ನು ಶಿಲುಬೆಗೆ ಹಾಕಿಸುವ ಅಧಿಕಾರವೂ ನನಗೆ ಉಂಟೆಂಬದು ನಿನಗೆ ಗೊತ್ತಿಲ್ಲವೋ ಎಂದು ಆತನನ್ನು ಕೇಳಿದನು.",
			"11": "<sup>11</sup> ಅದಕ್ಕೆ ಯೇಸು - ಮೇಲಣಿಂದ ನಿನಗೆ ಕೊಡಲ್ಪಡದಿದ್ದರೆ ನನ್ನ ಮೇಲೆ ನಿನಗೆ ಯಾವ ಅಧಿಕಾರವೂ ಇರುತ್ತಿರಲಿಲ್ಲ. ಆದಕಾರಣ ನನ್ನನ್ನು ನಿನಗೆ ಒಪ್ಪಿಸಿದವನಿಗೆ ಹೆಚ್ಚಿನ ಪಾಪ ಉಂಟು ಅಂದನು.",
			"12": "<sup>12</sup> ಈ ಮಾತಿನ ದೆಸೆಯಿಂದ ಪಿಲಾತನು ಆತನನ್ನು ಬಿಡಿಸುವದಕ್ಕೆ ಪೇಚಾಡಿದನು. ಆದರೆ ಯೆಹೂದ್ಯರು - ನೀನು ಇವನನ್ನು ಬಿಡಿಸಿದರೆ ನೀನು ಕೈಸರನಿಗೆ ಮಿತ್ರನಲ್ಲ; ತನ್ನನ್ನು ಅರಸನಾಗಿ ಮಾಡಿಕೊಳ್ಳುವವನು ಕೈಸರನಿಗೆ ವಿರೋಧಿ ಎಂದು ಕೂಗಿ ಹೇಳಿದರು.",
			"13": "<sup>13</sup> ಈ ಮಾತುಗಳನ್ನು ಪಿಲಾತನು ಕೇಳಿ ಯೇಸುವನ್ನು ಹೊರಕ್ಕೆ ಕರತರಿಸಿ ಇಬ್ರಿಯ ಮಾತಿನಲ್ಲಿ ಗಬ್ಬಥಾಯೆನಿಸಿಕೊಳ್ಳುವ ಕಲ್ಲುಹಾಸಿದ ಕಟ್ಟೆ ಎಂಬ ಹೆಸರುಳ್ಳ ಸ್ಥಳಕ್ಕೆ ಹೋಗಿ ನ್ಯಾಯಪೀಠದ ಮೇಲೆ ಕೂತುಕೊಂಡನು.",
			"14": "<sup>14</sup> ಪಸ್ಕಹಬ್ಬದ ಸೌರಣೆಯ ದಿವಸದಲ್ಲಿ ಬೆಳಿಗ್ಗೆ ಹೆಚ್ಚುಕಡಿಮೆ ಆರು ಘಂಟೆಯಾಗಿತ್ತು. ಅವನು ಯೆಹೂದ್ಯರಿಗೆ - ಇಗೋ, ನಿಮ್ಮ ಅರಸನು! ಎಂದು ಹೇಳಿದನು.",
			"15": "<sup>15</sup> ಅದಕ್ಕೆ ಅವರು - ಅವನನ್ನು ಕೊಲ್ಲಿಸು, ಕೊಲ್ಲಿಸು, ಶಿಲುಬೆಗೆ ಹಾಕಿಸು ಎಂದು ಕೂಗಿದರು. ಪಿಲಾತನು - ನಿಮ್ಮ ಅರಸನನ್ನು ಶಿಲುಬೆಗೆ ಹಾಕಿಸಲೋ? ಅಂದದ್ದಕ್ಕೆ ಮಹಾಯಾಜಕರು - ಕೈಸರನೇ ಹೊರತು ನಮಗೆ ಬೇರೆ ಅರಸನಿಲ್ಲ ಎಂದು ಉತ್ತರಕೊಟ್ಟರು.",
			"16": "<sup>16</sup> ಆಗ ಅವನು ಆತನನ್ನು ಶಿಲುಬೆಗೆ ಹಾಕಿಸುವದಕ್ಕೆ ಅವರ ವಶಕ್ಕೆ ಕೊಟ್ಟನು.",
			"17": "<sup>17</sup> ಅವರು ಯೇಸುವನ್ನು ತೆಗೆದುಕೊಂಡು ಹೋದರು; ಆತನು ತನ್ನ ಶಿಲುಬೆಯನ್ನು ತಾನೇ ಹೊತ್ತುಕೊಂಡು ಕಪಾಲಸ್ಥಳವೆಂಬ ಸ್ಥಾನಕ್ಕೆ ಹೋದನು. ಇದಕ್ಕೆ ಇಬ್ರಿಯ ಮಾತಿನಲ್ಲಿ ಗೊಲ್ಗೊಥಾ ಎಂದು ಹೆಸರು.",
			"18": "<sup>18</sup> ಅಲ್ಲಿ ಆತನನ್ನೂ ಆತನ ಸಂಗಡ ಇನ್ನಿಬ್ಬರನ್ನೂ ಶಿಲುಬೆಗೆ ಹಾಕಿದರು; ಅವರಲ್ಲಿ ಆ ಕಡೆ ಒಬ್ಬನನ್ನು ಈ ಕಡೆ ಒಬ್ಬನನ್ನು ನಡುವೆ ಯೇಸುವನ್ನು ಇಟ್ಟರು.",
			"19": "<sup>19</sup> ಇದಲ್ಲದೆ ಪಿಲಾತನು ಒಂದು ವಿಳಾಸವನ್ನು ಬರೆದು ಆತನ ಶಿಲುಬೆಯ ಮೇಲ್ಗಡೆ ಹಚ್ಚಿಸಿದನು. ಅದೇನಂದರೆ - ನಜರೇತಿನ ಯೇಸು, ಯೆಹೂದ್ಯರ ಅರಸನು ಎಂಬದೇ.",
			"20": "<sup>20</sup> ಅವರು ಯೇಸುವನ್ನು ಶಿಲುಬೆಗೆ ಹಾಕಿದ ಸ್ಥಳವು ಪಟ್ಟಣಕ್ಕೆ ಹತ್ತಿರವಾಗಿದ್ದದರಿಂದ ಯೆಹೂದ್ಯರಲ್ಲಿ ಬಹಳ ಮಂದಿ ಆ ವಿಳಾಸವನ್ನು ಓದಿದರು. ಅದು ಇಬ್ರಿಯ ಲಾತೀನ್ ಗ್ರೀಕ್ ಭಾಷೆಗಳಲ್ಲಿ ಬರೆದಿತ್ತು.",
			"21": "<sup>21</sup> ಹೀಗಿರಲಾಗಿ ಯೆಹೂದ್ಯರ ಮಹಾಯಾಜಕರು ಪಿಲಾತನಿಗೆ - ಯೆಹೂದ್ಯರ ಅರಸನು ಎಂದು ಬರೆಯದೆ ನಾನು ಯೆಹೂದ್ಯರ ಅರಸನೆಂದು ಹೇಳಿದವನು ಎಂಬದಾಗಿ ಬರೆಯಬೇಕೆಂತ ಹೇಳಿದರು.",
			"22": "<sup>22</sup> ಅದಕ್ಕೆ ಪಿಲಾತನು - ನಾನು ಬರೆದದ್ದು ಬರೆದಾಯಿತು ಅಂದನು.",
			"23": "<sup>23</sup> ಸಿಪಾಯಿಗಳು ಯೇಸುವನ್ನು ಶಿಲುಬೆಗೆ ಹಾಕಿದ ಮೇಲೆ ಅವರು ಆತನ ಮೇಲುಹೊದಿಕೆಗಳನ್ನು ತಕ್ಕೊಂಡು ನಾಲ್ಕು ಪಾಲು ಮಾಡಿ ಒಬ್ಬೊಬ್ಬ ಸಿಪಾಯಿಗೆ ಒಂದೊಂದು ಪಾಲಿನ ಪ್ರಕಾರ ಹಂಚಿಕೊಂಡರು. ಆತನ ಒಳಂಗಿಯನ್ನು ಸಹ ತಕ್ಕೊಂಡರು; ಆದರೆ ಆ ಅಂಗಿಗೆ ಹೊಲಿಗೆ ಇರಲಿಲ್ಲ. ಅದು ಮೇಲಿನಿಂದ ಅಖಂಡವಾಗಿ ಹೆಣೆದದ್ದಾಗಿತ್ತು.",
			"24": "<sup>24</sup> ಅದನ್ನು ನೋಡಿ ಅವರು - ನಾವು ಇದನ್ನು ಹರಿಯಬಾರದು; ಚೀಟುಹಾಕಿ ಇದು ಯಾರಿಗೆ ಬರುವದೋ ನೋಡೋಣ ಎಂದು ಮಾತಾಡಿಕೊಂಡರು. ಇದರಿಂದ - ನನ್ನ ಬಟ್ಟೆಗಳನ್ನು ತಮ್ಮಲ್ಲಿ ಪಾಲುಮಾಡಿಕೊಂಡರು, ನನ್ನ ಅಂಗಿಗೋಸ್ಕರ ಚೀಟು ಹಾಕಿದರು ಎಂಬ ಶಾಸ್ತ್ರದ ಮಾತು ನೆರವೇರಿತು. ಸಿಪಾಯಿಗಳು ಇದನ್ನೆಲ್ಲಾ ಮಾಡಿದರು.",
			"25": "<sup>25</sup> ಯೇಸುವಿನ ಶಿಲುಬೆಯ ಬಳಿಯಲ್ಲಿ ಆತನ ತಾಯಿಯೂ ಆತನ ತಾಯಿಯ ತಂಗಿಯೂ ಕ್ಲೋಪನ ಹೆಂಡತಿಯಾದ ಮರಿಯಳೂ ಮಗ್ದಲದ ಮರಿಯಳೂ ನಿಂತಿದ್ದರು.",
			"26": "<sup>26</sup> ಯೇಸು ತನ್ನ ತಾಯಿಯನ್ನೂ ಹತ್ತರದಲ್ಲಿ ನಿಂತಿದ್ದ ತನ್ನ ಪ್ರೀತಿಯ ಶಿಷ್ಯನನ್ನೂ ನೋಡಿ ತಾಯಿಗೆ - ಅಮ್ಮಾ, ಇಗೋ ನಿನ್ನ ಮಗನು ಎಂದು ಹೇಳಿದನು.",
			"27": "<sup>27</sup> ತರುವಾಯ ಆ ಶಿಷ್ಯನಿಗೆ - ಇಗೋ ನಿನ್ನ ತಾಯಿ ಎಂದು ಹೇಳಿದನು. ಆ ಹೊತ್ತಿನಿಂದ ಆ ಶಿಷ್ಯನು ಆಕೆಯನ್ನು ತನ್ನ ಮನೆಯಲ್ಲೇ ಇರಿಸಿಕೊಂಡನು.",
			"28": "<sup>28</sup> ಇದಾದ ಮೇಲೆ ಯೇಸು ಈಗ ಎಲ್ಲಾ ತೀರಿತೆಂದು ತಿಳಿದು ಶಾಸ್ತ್ರದ ಮಾತು ನೆರವೇರುವಂತೆ - ನನಗೆ ನೀರಡಿಕೆ ಆಗಿದೆ ಅಂದನು.",
			"29": "<sup>29</sup> ಅಲ್ಲಿ ಹುಳಿರಸ ತುಂಬಿದ ಗಡಿಗೆ ಇಟ್ಟಿರಲಾಗಿ ಅವರು ಸ್ಪಂಜನ್ನು ಹುಳಿರಸದಿಂದ ತುಂಬಿಸಿ ಹಿಸೋಪ್‍ಗಿಡದ ಕೋಲಿಗೆ ಸಿಕ್ಕಿಸಿ ಆತನ ಬಾಯಿಗೆ ಮುಟ್ಟಿಸಿದರು.",
			"30": "<sup>30</sup> ಯೇಸು ಆ ಹುಳಿರಸವನ್ನು ತಕ್ಕೊಂಡ ಮೇಲೆ - ತೀರಿತು ಎಂದು ಹೇಳಿ ತಲೇಬಾಗಿಸಿ ಆತ್ಮವನ್ನು ಒಪ್ಪಿಸಿಕೊಟ್ಟನು.",
			"31": "<sup>31</sup> ಆ ಹೊತ್ತು ಸೌರಣೆಯ ದಿವಸವಾಗಿದ್ದದರಿಂದ ಯೆಹೂದ್ಯರು - ಸಬ್ಬತ್‍ದಿನದಲ್ಲಿ ಶವಗಳು ಶಿಲುಬೆಯ ಮೇಲೆ ಇರಬಾರದೆಂದು ಪಿಲಾತನನ್ನು - ಅವರ ಕಾಲುಗಳನ್ನು ಮುರಿಸಿ ಅವರನ್ನು ತೆಗೆಸಿಹಾಕಬೇಕೆಂಬದಾಗಿ ಕೇಳಿಕೊಂಡರು; ಯಾಕಂದರೆ ಆ ಸಬ್ಬತ್‍ದಿನವು ಬಹು ವಿಶೇಷವಾದದ್ದು.",
			"32": "<sup>32</sup> ಆದದರಿಂದ ಸಿಪಾಯಿಗಳು ಬಂದು ಮೊದಲನೆಯವನ ಕಾಲುಗಳನ್ನೂ ಅವನ ಜೊತೆಯಲ್ಲಿ ಶಿಲುಬೆಗೆ ಹಾಕಿಸಿಕೊಂಡಿದ್ದ ಮತ್ತೊಬ್ಬನ ಕಾಲುಗಳನ್ನೂ ಮುರಿದರು.",
			"33": "<sup>33</sup> ಆದರೆ ಯೇಸುವಿನ ಬಳಿಗೆ ಬಂದಾಗ ಆತನು ಆಗಲೇ ಸತ್ತಿರುವದನ್ನು ಅವರು ಕಂಡು ಆತನ ಕಾಲುಗಳನ್ನು ಮುರಿಯಲಿಲ್ಲ.",
			"34": "<sup>34</sup> ಆದರೂ ಸಿಪಾಯಿಗಳಲ್ಲಿ ಒಬ್ಬನು ಈಟಿಯಿಂದ ಆತನ ಪಕ್ಕೆಯನ್ನು ತಿವಿದನು; ತಿವಿದ ಕೂಡಲೆ ರಕ್ತವೂ ನೀರೂ ಹೊರಟಿತು.",
			"35": "<sup>35</sup> ಅದನ್ನು ಕಂಡವನೇ ಸಾಕ್ಷಿ ಹೇಳಿದ್ದಾನೆ, ಅವನ ಸಾಕ್ಷಿ ಸತ್ಯವೇ; ತಾನು ಹೇಳುವದು ಸತ್ಯವೆಂದು ಅವನು ಬಲ್ಲನು; ನೀವು ಸಹ ನಂಬಬೇಕೆಂದು ಇದನ್ನು ಹೇಳಿದ್ದಾನೆ.",
			"36": "<sup>36</sup> ಯಾಕಂದರೆ - ಆತನ ಒಂದು ಎಲುಬನ್ನಾದರೂ ಮುರಿಯಕೂಡದು ಎಂದು ಶಾಸ್ತ್ರದಲ್ಲಿ ಬರೆದ ಮಾತು ನೆರವೇರುವಂತೆ ಇದಾಯಿತು;",
			"37": "<sup>37</sup> ಮತ್ತು - ಅವರು ತಾವು ಇರಿದವನನ್ನು ದಿಟ್ಟಿಸಿನೋಡುವರು ಎಂದು ಶಾಸ್ತ್ರದಲ್ಲಿ ಮತ್ತೊಂದು ಮಾತು ಹೇಳಿ ಅದೆ.",
			"38": "<sup>38</sup> ಇದಾದ ಮೇಲೆ ಯೆಹೂದ್ಯರ ಭಯದಿಂದ ಗುಪ್ತವಾಗಿ ಯೇಸುವಿನ ಶಿಷ್ಯನಾಗಿದ್ದ ಅರಿಮಥಾಯದ ಯೋಸೇಫನು - ಯೇಸುವಿನ ದೇಹವನ್ನು ತೆಗೆದುಕೊಂಡು ಹೋಗುವದಕ್ಕೆ ಅಪ್ಪಣೆಯಾಗಬೇಕೆಂದು ಪಿಲಾತನನ್ನು ಬೇಡಿಕೊಂಡನು. ಪಿಲಾತನು ಅಪ್ಪಣೆಕೊಡಲಾಗಿ ಅವನು ಬಂದು ಆತನ ದೇಹವನ್ನು ತೆಗೆದುಕೊಂಡು ಹೋದನು.",
			"39": "<sup>39</sup> ಇದಲ್ಲದೆ ಮೊದಲು ಒಂದು ಸಾರಿ ರಾತ್ರಿವೇಳೆಯಲ್ಲಿ ಯೇಸುವಿನ ಬಳಿಗೆ ಬಂದಿದ್ದ ನಿಕೊದೇಮನು ಸಹ ರಕ್ತಬೋಳ ಅಗರುಗಳನ್ನು ಕಲಸಿದ ಚೂರ್ಣವನ್ನು ನೂರು ಸೇರಿನಷ್ಟು ತಕ್ಕೊಂಡು ಅಲ್ಲಿಗೆ ಬಂದನು.",
			"40": "<sup>40</sup> ಅವರು ಯೇಸುವಿನ ದೇಹವನ್ನು ತಕ್ಕೊಂಡು ಯೆಹೂದ್ಯರಲ್ಲಿ ಹೂಣಿಡುವ ಪದ್ಧತಿಯ ಪ್ರಕಾರ ಅದನ್ನು ಆ ಸುಗಂಧದ್ರವ್ಯಗಳ ಸಹಿತವಾಗಿ ನಾರುಬಟ್ಟೆಯಲ್ಲಿ ಸುತ್ತಿದರು.",
			"41": "<sup>41</sup> ಆತನನ್ನು ಶಿಲುಬೆಗೆ ಹಾಕಿದ ಸ್ಥಳದಲ್ಲಿ ಒಂದು ತೋಟವಿತ್ತು; ಆ ತೋಟದಲ್ಲಿ ಒಂದು ಹೊಸ ಸಮಾಧಿ ಇತ್ತು; ಅದರಲ್ಲಿ ಅದುವರೆಗೆ ಯಾರನ್ನೂ ಇಟ್ಟಿದ್ದಿಲ್ಲ.",
			"42": "<sup>42</sup> ಆ ದಿನವು ಯೆಹೂದ್ಯರ ಸೌರಣೆಯ ದಿನವಾದದ್ದರಿಂದ ಆ ಸಮಾಧಿ ಹತ್ತರವಿರುತ್ತದೆಂದು ಯೇಸುವನ್ನು ಅಲ್ಲೇ ಇಟ್ಟರು."
		},
		"20": {
			"1": "<sup>1</sup> ವಾರದ ಮೊದಲನೆಯ ದಿನದಲ್ಲಿ ಮಗ್ದಲದ ಮರಿಯಳು ಮುಂಜಾನೆ ಇನ್ನೂ ಕತ್ತಲೆ ಇರುವಾಗಲೇ ಸಮಾಧಿಯ ಬಳಿಗೆ ಬಂದು ಸಮಾಧಿಯ ಕಲ್ಲು ಅಲ್ಲಿಂದ ತೆಗೆದಿರುವದನ್ನು ಕಂಡಳು.",
			"2": "<sup>2</sup> ಆಕೆ ಸೀಮೋನ ಪೇತ್ರನ ಬಳಿಗೂ ಯೇಸುವಿಗೆ ಪ್ರಿಯನಾಗಿದ್ದ ಆ ಮತ್ತೊಬ್ಬ ಶಿಷ್ಯನ ಬಳಿಗೂ ಓಡಿ ಬಂದು ಅವರಿಗೆ - ಸ್ವಾಮಿಯನ್ನು ಸಮಾಧಿಯಿಂದ ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ಆತನನ್ನು ಎಲ್ಲಿಟ್ಟಿದ್ದಾರೋ ನಮಗೆ ಗೊತ್ತಿಲ್ಲವೆಂದು ಹೇಳಿದಳು.",
			"3": "<sup>3</sup> ಆಗ ಪೇತ್ರನೂ ಆ ಮತ್ತೊಬ್ಬ ಶಿಷ್ಯನೂ ಹೊರಟು ಸಮಾಧಿಯ ಕಡೆಗೆ ಹೋದರು. ಅವರಿಬ್ಬರೂ ಜೊತೆಯಾಗಿ ಓಡಿದರು;",
			"4": "<sup>4</sup> ಆದರೆ ಆ ಮತ್ತೊಬ್ಬ ಶಿಷ್ಯನು ಪೇತ್ರನಿಗಿಂತ ಮುಂದೆ ಓಡಿ ಮೊದಲೇ ಸಮಾಧಿಗೆ ಮುಟ್ಟಿ",
			"5": "<sup>5</sup> ಬೊಗ್ಗಿ ನೋಡಿ ಆ ನಾರುಬಟ್ಟೆಗಳು ಬಿದ್ದಿರುವದನ್ನು ಕಂಡನು; ಆದರೆ ಒಳಕ್ಕೆ ಹೋಗಲಿಲ್ಲ.",
			"6": "<sup>6</sup> ಸೀಮೋನ ಪೇತ್ರನು ಅವನ ಹಿಂದೆ ಬಂದು ಸಮಾಧಿಯೊಳಕ್ಕೆ ಹೋಗಿ ಆ ನಾರುಬಟ್ಟೆಗಳು ಬಿದ್ದಿರುವದನ್ನೂ ಆತನ ತಲೆಯ ಮೇಲಿದ್ದ ಕೈಪಾವುಡವು",
			"7": "<sup>7</sup> ಆ ನಾರುಬಟ್ಟೆಗಳ ಕೂಡ ಇರದೆ ಸುತ್ತಿ ಒಂದು ಕಡೆಯಲ್ಲಿ ಬೇರೇ ಇರುವದನ್ನೂ ನೋಡಿದನು.",
			"8": "<sup>8</sup> ಆಗ ಸಮಾಧಿಯ ಬಳಿಗೆ ಮೊದಲು ಬಂದಿದ್ದ ಆ ಮತ್ತೊಬ್ಬ ಶಿಷ್ಯನು ಸಹ ಒಳಕ್ಕೆ ಹೋಗಿ ನೋಡಿ ನಂಬಿದನು.",
			"9": "<sup>9</sup> ಆತನು ಸತ್ತ ಮೇಲೆ ಜೀವದಿಂದೆದ್ದು ಬರಬೇಕೆಂಬದಾಗಿ ಶಾಸ್ತ್ರದಲ್ಲಿ ಬರೆದಿರುವ ಮಾತು ಅವರಿಗೆ ಇನ್ನೂ ತಿಳಿದಿರಲಿಲ್ಲ.",
			"10": "<sup>10</sup> ತರುವಾಯ ಆ ಶಿಷ್ಯರು ತಿರಿಗಿ ತಮ್ಮವರ ಬಳಿಗೆ ಹೋದರು.",
			"11": "<sup>11</sup> ಆದರೆ ಮರಿಯಳು ಹೊರಗೆ ಸಮಾಧಿಯ ಬಳಿಯಲ್ಲಿ ಅಳುತ್ತಾ ನಿಂತಿದ್ದಳು. ಹೀಗೆ ಆಕೆ ಅಳುತ್ತಿರುವಾಗ ಸಮಾಧಿಯೊಳಗೆ ಬೊಗ್ಗಿ ನೋಡಲು",
			"12": "<sup>12</sup> ಯೇಸುವಿನ ದೇಹವು ಇಟ್ಟಿದ್ದ ಸ್ಥಳದಲ್ಲಿ ಬೆಳ್ಳಗಿರುವ ವಸ್ತ್ರಗಳನ್ನು ಹೊದ್ದುಕೊಂಡ ಇಬ್ಬರು ದೇವದೂತರನ್ನು ಕಂಡಳು; ಅವರಲ್ಲಿ ಒಬ್ಬನು ತಲೆಯಿದ್ದ ಕಡೆಯಲ್ಲಿ ಒಬ್ಬನು ಕಾಲಿದ್ದ ಕಡೆಯಲ್ಲಿ ಕೂತಿದ್ದರು.",
			"13": "<sup>13</sup> ಅವರು ಆಕೆಯನ್ನು - ಅಮ್ಮಾ, ನೀನು ಯಾಕೆ ಅಳುತ್ತೀ ಎಂದು ಕೇಳಲು ಆಕೆ - ನನ್ನ ಸ್ವಾಮಿಯನ್ನು ತೆಗೆದುಕೊಂಡು ಹೋಗಿದ್ದಾರೆ; ಆತನನ್ನು ಎಲ್ಲಿ ಇಟ್ಟಿದ್ದಾರೋ ನನಗೆ ಗೊತ್ತಿಲ್ಲ ಅಂದಳು.",
			"14": "<sup>14</sup> ಇದನ್ನು ಹೇಳಿ ತಿರುಗಿಕೊಂಡು ಯೇಸು ನಿಂತಿರುವದನ್ನು ಕಂಡಳು, ಆದರೆ ಆತನು ಯೇಸು ಅಂತ ಆಕೆಗೆ ತಿಳಿಯಲಿಲ್ಲ.",
			"15": "<sup>15</sup> ಯೇಸು ಆಕೆಯನ್ನು - ಅಮ್ಮಾ, ಯಾಕೆ ಅಳುತ್ತೀ? ಯಾರನ್ನು ಹುಡುಕುತ್ತೀ? ಎಂದು ಕೇಳಲು ಆಕೆ ಆತನನ್ನು ತೋಟಗಾರನೆಂದು ನೆನಸಿ ಆತನಿಗೆ - ಅಯ್ಯಾ, ನೀನು ಆತನನ್ನು ಎತ್ತಿಕೊಂಡು ಹೋಗಿದ್ದರೆ ಎಲ್ಲಿ ಇಟ್ಟಿದ್ದೀ ನನಗೆ ಹೇಳು; ನಾನು ತೆಗೆದುಕೊಂಡು ಹೋಗುತ್ತೇನೆ ಎಂದು ಹೇಳಿದಳು.",
			"16": "<sup>16</sup> ಯೇಸು ಆಕೆಗೆ - ಮರಿಯಳೇ, ಎಂದು ಹೇಳಿದನು. ಆಕೆ ತಿರುಗಿಕೊಂಡು ಇಬ್ರಿಯ ಮಾತಿನಲ್ಲಿ - ರಬ್ಬೂನಿ, ಅಂದಳು. (ರಬ್ಬೂನಿ ಅಂದರೆ ಗುರುವೇ ಎಂದು ಅರ್ಥ).",
			"17": "<sup>17</sup> ಯೇಸು ಆಕೆಗೆ - ನನ್ನನ್ನು ಹಿಡಿಯಬೇಡ; ಯಾಕಂದರೆ ನಾನು ಇನ್ನೂ ತಂದೆಯ ಬಳಿಗೆ ಏರಿಹೋದವನಲ್ಲ. ನೀನು ನನ್ನ ಸಹೋದರರ ಬಳಿಗೆ ಹೋಗಿ - ನಾನು ನನ್ನ ತಂದೆಯೂ ನಿಮ್ಮ ತಂದೆಯೂ ನನ್ನ ದೇವರೂ ನಿಮ್ಮ ದೇವರೂ ಆಗಿರುವಾತನ ಬಳಿಗೆ ಏರಿಹೋಗುತ್ತೇನೆ ಎಂದು ಅವರಿಗೆ ಹೇಳು ಅಂದನು.",
			"18": "<sup>18</sup> ಮಗ್ದಲದ ಮರಿಯಳು ಹೋಗಿ - ನಾನು ಸ್ವಾಮಿಯವರನ್ನು ನೋಡಿದ್ದೇನೆ; ಆತನು ಇಂಥಿಂಥದನ್ನು ನನಗೆ ಹೇಳಿದನು ಎಂದು ಶಿಷ್ಯರಿಗೆ ತಿಳಿಸಿದಳು.",
			"19": "<sup>19</sup> ಅದೇ ದಿವಸದ ಅಂದರೆ ವಾರದ ಮೊದಲನೆಯ ದಿವಸದ ಸಂಜೆಯಲ್ಲಿ ಶಿಷ್ಯರು ಯೆಹೂದ್ಯರ ಭಯದಿಂದ ತಾವು ಇದ್ದ ಮನೆಯ ಬಾಗಲುಗಳನ್ನು ಮುಚ್ಚಿಕೊಂಡಿರಲು ಯೇಸು ಬಂದು ನಡುವೆ ನಿಂತು - ನಿಮಗೆ ಸಮಾಧಾನವಾಗಲಿ ಅಂದನು.",
			"20": "<sup>20</sup> ಇದನ್ನು ಹೇಳಿದ ಮೇಲೆ ಅವರಿಗೆ ತನ್ನ ಕೈಗಳನ್ನೂ ಪಕ್ಕೆಯನ್ನೂ ತೋರಿಸಿದನು. ಶಿಷ್ಯರು ಸ್ವಾಮಿಯನ್ನು ನೋಡಿದ್ದರಿಂದ ಸಂತೋಷಪಟ್ಟರು.",
			"21": "<sup>21</sup> ಯೇಸು ಅವರಿಗೆ - ನಿಮಗೆ ಸಮಾಧಾನವಾಗಲಿ ಎಂದು ತಿರಿಗಿ ಹೇಳಿ - ತಂದೆ ನನ್ನನ್ನು ಕಳುಹಿಸಿಕೊಟ್ಟ ಹಾಗೆಯೇ ನಾನೂ ನಿಮ್ಮನ್ನು ಕಳುಹಿಸಿಕೊಡುತ್ತೇನೆ ಅಂದನು.",
			"22": "<sup>22</sup> ಇದನ್ನು ಹೇಳಿ ಅವರ ಮೇಲೆ ಊದಿ ಅವರಿಗೆ - ಪವಿತ್ರಾತ್ಮವರವನ್ನು ತಕ್ಕೊಳ್ಳಿರಿ;",
			"23": "<sup>23</sup> ನೀವು ಯಾರ ಪಾಪಗಳನ್ನು ಕ್ಷಮಿಸಿಬಿಡುತ್ತೀರೋ ಅವರಿಗೆ ಅವುಗಳ ಕ್ಷಮಾಪಣೆಯಾಗುತ್ತದೆ; ಯಾರ ಪಾಪಗಳನ್ನು ಕ್ಷಮಿಸದೆ ಉಳಿಸುತ್ತೀರೋ ಅವರಿಗೆ ಅವುಗಳು ಉಳಿಯುತ್ತವೆ ಅಂದನು.",
			"24": "<sup>24</sup> ಯೇಸು ಬಂದಾಗ ಹನ್ನೆರಡು ಮಂದಿ ಶಿಷ್ಯರೊಳಗೆ ಒಬ್ಬನಾದ ದಿದುಮನೆನಿಸಿಕೊಳ್ಳುವ ತೋಮನು ಅವರ ಸಂಗಡ ಇರಲಿಲ್ಲ.",
			"25": "<sup>25</sup> ಆದದರಿಂದ ಉಳಿದ ಶಿಷ್ಯರು - ನಾವು ಸ್ವಾಮಿಯನ್ನು ನೋಡಿದ್ದೇವೆ ಎಂದು ಅವನಿಗೆ ಹೇಳಿದರು. ಅದಕ್ಕೆ ಅವನು - ನಾನು ಆತನ ಕೈಗಳಲ್ಲಿ ಮೊಳೆಗಳಿಂದಾದ ಗಾಯವನ್ನು ನೋಡಿ ಆ ಮೊಳೆಯ ಗಾಯದಲ್ಲಿ ನನ್ನ ಬೆರಳನ್ನು ಇಟ್ಟು ಆತನ ಪಕ್ಕೆಯಲ್ಲಿ ನನ್ನ ಕೈಯನ್ನು ಹಾಕಿದ ಹೊರತು ನಿಮ್ಮ ಮಾತನ್ನು ನಂಬುವದೇ ಇಲ್ಲ ಅಂದನು.",
			"26": "<sup>26</sup> ಎಂಟು ದಿವಸಗಳಾದ ಮೇಲೆ ಆತನ ಶಿಷ್ಯರು ತಿರಿಗಿ ಒಳಗಿದ್ದಾಗ ತೋಮನೂ ಅವರ ಸಂಗಡ ಇದ್ದನು. ಬಾಗಲುಗಳು ಮುಚ್ಚಿದ್ದವು. ಆಗ ಯೇಸು ಬಂದು ನಡುವೆ ನಿಂತು - ನಿಮಗೆ ಸಮಾಧಾನವಾಗಲಿ ಅಂದನು.",
			"27": "<sup>27</sup> ಆಮೇಲೆ ತೋಮನಿಗೆ - ನಿನ್ನ ಬೆರಳನ್ನು ಈ ಕಡೆ ಚಾಚಿ ನನ್ನ ಕೈಗಳನ್ನು ಮುಟ್ಟಿ ನೋಡು; ನಿನ್ನ ಕೈಚಾಚಿ ನನ್ನ ಪಕ್ಕೆಯಲ್ಲಿ ಹಾಕು; ನಂಬದವನಾಗಿರಬೇಡ, ನಂಬುವವನಾಗು ಎಂದು ಹೇಳಿದನು.",
			"28": "<sup>28</sup> ತೋಮನು ಆತನಿಗೆ - ನನ್ನ ಸ್ವಾಮೀ, ನನ್ನ ದೇವರು! ಎಂದು ಹೇಳಿದನು.",
			"29": "<sup>29</sup> ಯೇಸು ಅವನಿಗೆ - ನೀನು ನನ್ನನ್ನು ನೋಡಿದ್ದರಿಂದ ನಂಬಿದ್ದೀ; ನೋಡದೆ ನಂಬಿದವರು ಧನ್ಯರು ಎಂದು ಹೇಳಿದನು.",
			"30": "<sup>30</sup> ಯೇಸು ಇನ್ನು ಬೇರೆ ಎಷ್ಟೋ ಸೂಚಕಕಾರ್ಯಗಳನ್ನು ತನ್ನ ಶಿಷ್ಯರ ಮುಂದೆ ಮಾಡಿದನು. ಅವುಗಳು ಈ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುವದಿಲ್ಲ.",
			"31": "<sup>31</sup> ಆದರೆ ಯೇಸು ದೇವಕುಮಾರನಾದ ಕ್ರಿಸ್ತನೆಂದು ನೀವು ನಂಬುವಂತೆಯೂ, ನಂಬಿ ಆತನ ಹೆಸರಿನ ಮೂಲಕವಾಗಿ ಜೀವವನ್ನು ಪಡಕೊಳ್ಳುವಂತೆಯೂ ಇಷ್ಟೆಲ್ಲಾ ಬರೆದದೆ."
		},
		"21": {
			"1": "<sup>1</sup> ತರುವಾಯ ಯೇಸು ತಿಬೇರಿಯ ಸಮುದ್ರದ ಬಳಿಯಲ್ಲಿ ಶಿಷ್ಯರಿಗೆ ತಿರಿಗಿ ತನ್ನನ್ನು ತೋರಿಸಿಕೊಂಡನು.",
			"2": "<sup>2</sup> ತನ್ನನ್ನು ತೋರಿಸಿಕೊಂಡ ರೀತಿ ಯಾವದಂದರೆ - ಸೀಮೋನ ಪೇತ್ರನೂ ದಿದುಮನೆಂಬ ತೋಮನೂ ಗಲಿಲಾಯದ ಕಾನಾ ಊರಿನ ನತಾನಯೇಲನೂ ಜೆಬೆದಾಯನ ಕುಮಾರರೂ ಆತನ ಶಿಷ್ಯರಲ್ಲಿ ಇನ್ನಿಬ್ಬರೂ ಕೂಡಿದ್ದರು.",
			"3": "<sup>3</sup> ಅವರಿಗೆ ಸೀಮೋನ ಪೇತ್ರನು - ನಾನು ಮೀನು ಹಿಡಿಯುವದಕ್ಕೆ ಹೋಗುತ್ತೇನೆಂದು ಹೇಳಲು ಅವರು - ನಾವೂ ನಿನ್ನ ಸಂಗಡ ಬರುತ್ತೇವೆ ಅಂದರು. ಅವರು ಹೊರಟು ದೋಣಿಯನ್ನು ಹತ್ತಿದರು. ಆದರೆ ಆ ರಾತ್ರಿಯಲ್ಲಿ ಒಂದು ಮೀನೂ ಸಿಕ್ಕಲಿಲ್ಲ.",
			"4": "<sup>4</sup> ಬೆಳಗಾಗುವಾಗ ಯೇಸು ದಡದಲ್ಲಿ ನಿಂತಿದ್ದನು; ಆದಾಗ್ಯೂ ಶಿಷ್ಯರು ಆತನನ್ನು ಯೇಸುವೆಂದು ತಿಳಿಯಲಿಲ್ಲ.",
			"5": "<sup>5</sup> ಆಗ ಯೇಸು ಅವರನ್ನು - ಮಕ್ಕಳಿರಾ, ಊಟಕ್ಕೆ ನಿಮಗೆ ಏನೂ ಇಲ್ಲವೇ ಎಂದು ಕೇಳಲು ಅವರು ಇಲ್ಲವೆಂದು ಉತ್ತರಕೊಟ್ಟರು.",
			"6": "<sup>6</sup> ಆತನು ಅವರಿಗೆ - ನೀವು ದೋಣಿಯ ಬಲಗಡೆಯಲ್ಲಿ ಬಲೆ ಬೀಸಿದರೆ ಸಿಕ್ಕುವದು ಎಂದು ಹೇಳಿದನು. ಅವರು ಹಾಗೆ ಬೀಸಿದಾಗ ಮೀನುಗಳು ಬಹಳ ಹೆಚ್ಚಾಗಿ ಸಿಕ್ಕಿದ್ದರಿಂದ ಬಲೆಯನ್ನು ಎಳೆಯುವದಕ್ಕೆ ಆಗದೆ ಹೋಯಿತು.",
			"7": "<sup>7</sup> ಆಗ ಯೇಸುವಿಗೆ ಪ್ರಿಯನಾಗಿದ್ದ ಶಿಷ್ಯನು ಪೇತ್ರನಿಗೆ - ಅವರು ಸ್ವಾಮಿಯವರು ಎಂದು ಹೇಳಿದನು. ಸ್ವಾಮಿಯವರೆಂದು ಸೀಮೋನ ಪೇತ್ರನು ಕೇಳಿ ಮೈ ಮೇಲೆ ಬಟ್ಟೆಯಿಲ್ಲದವನಾಗಿದ್ದರಿಂದ ಒಲ್ಲಿಯನ್ನು ಸುತ್ತಿಕೊಂಡು ಸಮುದ್ರದಲ್ಲಿ ದುಮುಕಿದನು.",
			"8": "<sup>8</sup> ದಡವು ದೂರವಾಗಿರದೆ ಹೆಚ್ಚುಕಡಿಮೆ ಇನ್ನೂರು ಮೊಳದಲ್ಲಿದ್ದರಿಂದ ಉಳಿದ ಶಿಷ್ಯರು ಮೀನು ತುಂಬಿದ ಆ ಬಲೆಯನ್ನು ಎಳೆಯುತ್ತಾ ದೋಣಿಯಲ್ಲಿಯೇ ಬಂದರು.",
			"9": "<sup>9</sup> ಅವರು ಭೂಮಿಗೆ ಇಳಿದಾಗ ಕೆಂಡಗಳನ್ನೂ ಅವುಗಳ ಮೇಲೆ ಇಟ್ಟಿದ್ದ ಮೀನುಗಳನ್ನೂ ರೊಟ್ಟಿ ಸಹಿತವಾಗಿ ಕಂಡರು.",
			"10": "<sup>10</sup> ಯೇಸು ಅವರಿಗೆ - ನೀವು ಈಗ ಹಿಡಿದ ಮೀನುಗಳಲ್ಲಿ ಕೆಲವನ್ನು ತನ್ನಿರಿ ಎಂದು ಹೇಳಲು",
			"11": "<sup>11</sup> ಸೀಮೋನ ಪೇತ್ರನು ದೋಣಿಯನ್ನು ಹತ್ತಿ ಬಲೆಯನ್ನು ಭೂಮಿಗೆ ಎಳಕೊಂಡು ಬಂದನು. ಅದು ನೂರೈವತ್ತು ಮೂರು ದೊಡ್ಡ ಮೀನುಗಳಿಂದ ತುಂಬಿತ್ತು. ಅಷ್ಟು ಮೀನುಗಳು ಇದ್ದರೂ ಬಲೆ ಹರಿಯಲಿಲ್ಲ.",
			"12": "<sup>12</sup> ಯೇಸು ಅವರಿಗೆ - ಬಂದು ಊಟಮಾಡಿರಿ ಎಂದು ಹೇಳಿದನು. ಶಿಷ್ಯರು ಆತನನ್ನು ಸ್ವಾಮಿಯವರೆಂದು ತಿಳಿದಿದ್ದರಿಂದ ನೀನಾರೆಂದು ವಿಚಾರಿಸುವದಕ್ಕೆ ಅವರಲ್ಲಿ ಒಬ್ಬರೂ ಧೈರ್ಯಪಡಲಿಲ್ಲ.",
			"13": "<sup>13</sup> ಯೇಸು ಬಂದು ರೊಟ್ಟಿಯನ್ನು ತೆಗೆದುಕೊಂಡು ಅವರಿಗೆ ಕೊಟ್ಟನು; ಹಾಗೆಯೇ ಮೀನನ್ನೂ ಕೊಟ್ಟನು.",
			"14": "<sup>14</sup> ಯೇಸು ಸತ್ತು ಜೀವದಿಂದೆದ್ದ ಮೇಲೆ ಶಿಷ್ಯರಿಗೆ ಕಾಣಿಸಿಕೊಂಡದ್ದು ಇದು ಮೂರನೆಯ ಸಾರಿ.",
			"15": "<sup>15</sup> ಅವರು ಊಟಮಾಡಿದ ಮೇಲೆ ಯೇಸು ಸೀಮೋನ ಪೇತ್ರನನ್ನು - ಯೋಹಾನನ ಮಗನಾದ ಸೀಮೋನನೇ, ನೀನು ಇವರಿಗಿಂತ ಹೆಚ್ಚಾಗಿ ನನ್ನ ಮೇಲೆ ಪ್ರೀತಿ ಇಟ್ಟಿದ್ದೀಯೋ ಎಂದು ಕೇಳಲು ಅವನು - ಹೌದು, ಸ್ವಾಮೀ, ನಿನ್ನ ಮೇಲೆ ಮಮತೆ ಇಟ್ಟಿದ್ದೇನೆಂಬದನ್ನು ನೀನೇ ಬಲ್ಲೆ ಅಂದನು. ಆತನು ಅವನಿಗೆ - ನನ್ನ ಕುರಿಮರಿಗಳನ್ನು ಮೇಯಿಸು ಎಂದು ಹೇಳಿದನು.",
			"16": "<sup>16</sup> ಆತನು ತಿರಿಗಿ ಎರಡನೆಯ ಸಾರಿ ಅವನನ್ನು - ಯೋಹಾನನ ಮಗನಾದ ಸೀಮೋನನೇ, ನನ್ನ ಮೇಲೆ ಪ್ರೀತಿ ಇಟ್ಟಿದ್ದೀಯೋ ಎಂದು ಕೇಳಲು ಅವನು - ಹೌದು, ಸ್ವಾಮೀ, ನಿನ್ನ ಮೇಲೆ ಮಮತೆ ಇಟ್ಟಿದ್ದೇನೆಂಬದನ್ನು ನೀನೇ ಬಲ್ಲೆ ಅಂದನು. ಆತನು ಅವನಿಗೆ - ನನ್ನ ಕುರಿಗಳನ್ನು ಕಾಯಿ ಎಂದು ಹೇಳಿದನು.",
			"17": "<sup>17</sup> ಆತನು ಮೂರನೆಯ ಸಾರಿ - ಯೋಹಾನನ ಮಗನಾದ ಸೀಮೋನನೇ, ನನ್ನ ಮೇಲೆ ಮಮತೆ ಇಟ್ಟಿದ್ದೀಯೋ ಎಂದು ಕೇಳಿದನು. ಮೂರನೆಯ ಸಾರಿ ಆತನು - ನನ್ನ ಮೇಲೆ ಮಮತೆ ಇಟ್ಟಿದ್ದೀಯೋ ಎಂದು ತನ್ನನ್ನು ಕೇಳಿದ್ದಕ್ಕೆ ಪೇತ್ರನು ದುಃಖಪಟ್ಟು - ಸ್ವಾಮೀ, ನೀನು ಎಲ್ಲಾ ಬಲ್ಲೆ; ನಿನ್ನ ಮೇಲೆ ನಾನು ಮಮತೆ ಇಟ್ಟಿದ್ದೇನೆಂಬದು ನಿನಗೆ ತಿಳಿದದೆ ಅಂದನು. ಅವನಿಗೆ ಯೇಸು - ನನ್ನ ಕುರಿಗಳನ್ನು ಮೇಯಿಸು.",
			"18": "<sup>18</sup> ನಿನಗೆ ನಿಜನಿಜವಾಗಿ ಹೇಳುತ್ತೇನೆ, ನೀನು ಯೌವನಸ್ಥನಾಗಿದ್ದಾಗ ನೀನೇ ನಡುವನ್ನು ಕಟ್ಟಿಕೊಂಡು ಇಷ್ಟಬಂದ ಕಡೆಗೆ ತಿರುಗಾಡುತ್ತಿದ್ದಿ; ಆದರೆ ನೀನು ಮುದುಕನಾದಾಗ ನಿನ್ನ ಕೈಗಳನ್ನು ಚಾಚುವಿ, ಮತ್ತೊಬ್ಬನು ನಿನ್ನ ನಡುವನ್ನು ಕಟ್ಟಿಕೊಂಡು ನಿನಗೆ ಇಷ್ಟವಿಲ್ಲದ ಕಡೆಗೆ ನಿನ್ನನ್ನು ತೆಗೆದುಕೊಂಡು ಹೋಗುವನು ಎಂದು ಹೇಳಿದನು.",
			"19": "<sup>19</sup> ಅವನು ಎಂಥಾ ಸಾವಿನಿಂದ ದೇವರನ್ನು ಮಹಿಮೆಪಡಿಸುವನು ಎಂಬದನ್ನು ಆತನು ಈ ಮಾತಿನಿಂದ ಸೂಚಿಸಿದನು. ಇದನ್ನು ಹೇಳಿ ಆತನು ಅವನಿಗೆ - ನನ್ನನ್ನು ಹಿಂಬಾಲಿಸು ಎಂದು ಹೇಳಿದನು.",
			"20": "<sup>20</sup> ಪೇತ್ರನು ತಿರುಗಿಕೊಂಡು ಯೇಸುವಿಗೆ ಪ್ರಿಯನಾಗಿದ್ದ ಶಿಷ್ಯನು ಹಿಂದೆ ಬರುವದನ್ನು ಕಂಡನು. ಆ ಶಿಷ್ಯನು ಊಟದಲ್ಲಿ ಯೇಸುವಿನ ಎದೆಯ ಕಡೆಗೆ ಬಾಗಿ - ಸ್ವಾಮೀ, ನಿನ್ನನ್ನು ಹಿಡುಕೊಡುವವನು ಯಾರು ಎಂದು ಕೇಳಿದವನೇ.",
			"21": "<sup>21</sup> ಇವನನ್ನು ಪೇತ್ರನು ಕಂಡು ಯೇಸುವನ್ನು - ಸ್ವಾಮೀ, ಇವನ ವಿಷಯವೇನು ಎಂದು ಕೇಳಲು",
			"22": "<sup>22</sup> ಯೇಸು ಅವನಿಗೆ - ನಾನು ಬರುವ ತನಕ ಇವನು ಇರಬೇಕೆಂದು ನನಗೆ ಮನಸ್ಸಿದ್ದರೆ ಅದು ನಿನಗೇನು? ನೀನು ನನ್ನನ್ನು ಹಿಂಬಾಲಿಸು ಎಂದು ಹೇಳಿದನು.",
			"23": "<sup>23</sup> ಇದರ ದೆಸೆಯಿಂದ ಆ ಶಿಷ್ಯನು ಸಾಯುವದಿಲ್ಲವೆಂಬ ಮಾತು ಸಹೋದರರಲ್ಲಿ ಹಬ್ಬಿತು. ಆದರೆ ಅವನು ಸಾಯುವದಿಲ್ಲವೆಂದು ಯೇಸು ಅವನಿಗೆ ಹೇಳಲಿಲ್ಲ; ನಾನು ಬರುವ ತನಕ ಅವನು ಇರಬೇಕೆಂದು ನನಗೆ ಮನಸ್ಸಿದ್ದರೆ ಅದು ನಿನಗೇನು ಎಂತಲೇ ಆತನು ಹೇಳಿದನು.",
			"24": "<sup>24</sup> ಈ ಸಂಗತಿಗಳನ್ನು ಬರೆದು ಇವುಗಳ ವಿಷಯವಾಗಿ ಸಾಕ್ಷಿ ಹೇಳುವ ಶಿಷ್ಯನು ಇವನೇ. ಇವನ ಸಾಕ್ಷಿ ಸತ್ಯವೆಂದು ನಾವು ಬಲ್ಲೆವು.",
			"25": "<sup>25</sup> ಇದಲ್ಲದೆ ಯೇಸು ಮಾಡಿದ್ದು ಇನ್ನೂ ಬಹಳವಿದೆ; ಅದನ್ನೆಲ್ಲಾ ಒಂದೊಂದಾಗಿ ಬರೆಯುವದಾದರೆ ಬರೆಯಬೇಕಾದ ಪುಸ್ತಕಗಳನ್ನು ಲೋಕವೇ ಹಿಡಿಸದೆ ಹೋದೀತೆಂದು ನಾನು ನೆನಸುತ್ತೇನೆ."
		}
	}
};
