var book = {
	"name": "ಅಪೊಸ್ತಲರ ಕೃತ್ಯಗಳು",
	"numChapters": 28,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ಥೆಯೊಫಿಲನೇ, ನಾನು ಮೊದಲು ಬರೆದ ಚರಿತ್ರೆಯಲ್ಲಿ ಯೇಸು ಮಾಡುವದಕ್ಕೂ ಉಪದೇಶಿಸುವದಕ್ಕೂ ಪ್ರಾರಂಭಿಸಿದ್ದೆಲ್ಲವನ್ನು ಹೇಳಿ",
			"2": "<sup>2</sup> ತಾನು ಆರಿಸಿಕೊಂಡಿದ್ದ ಅಪೊಸ್ತಲರಿಗೆ ಪವಿತ್ರಾತ್ಮನ ಮೂಲಕ ಆಜ್ಞೆಕೊಟ್ಟು ಮೇಲಣ ಲೋಕವನ್ನೇರಿದ ದಿನದವರೆಗೆ ವಿವರಿಸಿದೆನಷ್ಟೆ.",
			"3": "<sup>3</sup> ಆತನು ಬಾಧೆಪಟ್ಟು ಸತ್ತ ಮೇಲೆ ತನ್ನನ್ನು ಜೀವಂತನೆಂದು ಅನೇಕ ಪ್ರಮಾಣಗಳಿಂದ ಅಪೊಸ್ತಲರಿಗೆ ತೋರಿಸಿಕೊಂಡನು. ನಾಲ್ವತ್ತು ದಿವಸಗಳ ತನಕ ಅವರಿಗೆ ಕಾಣಿಸಿಕೊಳ್ಳುತ್ತಾ ದೇವರ ರಾಜ್ಯದ ವಿಷಯವಾದ ಸಂಗತಿಗಳನ್ನು ಹೇಳುತ್ತಾ ಇದ್ದನು.",
			"4": "<sup>4</sup> ಹೀಗಿರಲಾಗಿ ಆತನು ಅವರೊಂದಿಗೆ ಕೂಡಿದ್ದಾಗ್ಗೆ ಅವರಿಗೆ - ಯೋಹಾನನಂತೂ ನೀರಿನಲ್ಲಿ ದೀಕ್ಷಾಸ್ನಾನ ಮಾಡಿಸಿದನು, ನಿಮಗಾದರೋ ಇನ್ನು ಸ್ವಲ್ಪ ದಿವಸಗಳೊಳಗಾಗಿ ಪವಿತ್ರಾತ್ಮದಲ್ಲಿ ಸ್ನಾನವಾಗುವದು. ಆದಕಾರಣ ನೀವು ಯೆರೂಸಲೇಮನ್ನು ಬಿಟ್ಟು ಹೋಗದೆ ತಂದೆ ಮಾಡಿದಂಥ ನೀವು ನನ್ನಿಂದ ಕೇಳಿದಂಥ ವಾಗ್ದಾನಕ್ಕೆ ಕಾದುಕೊಂಡಿರ್ರಿ ಎಂದು ಅಪ್ಪಣೆಕೊಟ್ಟನು.",
			"6": "<sup>6</sup> ಕೂಡಿ ಬಂದವರು ಆತನನ್ನು - ಸ್ವಾಮೀ, ನೀನು ಇದೇ ಕಾಲದಲ್ಲಿ ಇಸ್ರಾಯೇಲ್ ಜನರಿಗೆ ರಾಜ್ಯವನ್ನು ತಿರಿಗಿ ಸ್ಥಾಪಿಸಿಕೊಡುವಿಯೋ ಎಂದು ಕೇಳಲು",
			"7": "<sup>7</sup> ಆತನು ಅವರಿಗೆ - ತಂದೆಯು ಸ್ವಂತ ಅಧಿಕಾರದಲ್ಲಿಟ್ಟುಕೊಂಡಿರುವ ಕಾಲಗಳನ್ನೂ ಸಮಯಗಳನ್ನೂ ತಿಳುಕೊಳ್ಳುವದು ನಿಮ್ಮ ಕೆಲಸವಲ್ಲ.",
			"8": "<sup>8</sup> ಆದರೆ ಪವಿತ್ರಾತ್ಮ ನಿಮ್ಮ ಮೇಲೆ ಬರಲು ನೀವು ಬಲವನ್ನು ಹೊಂದಿ ಯೆರೂಸಲೇಮಿನಲ್ಲಿಯೂ ಎಲ್ಲಾ ಯೂದಾಯ ಸಮಾರ್ಯ ಸೀಮೆಗಳಲ್ಲಿಯೂ ಭೂಲೋಕದ ಕಟ್ಟಕಡೆಯವರೆಗೂ ನನಗೆ ಸಾಕ್ಷಿಗಳಾಗಿರಬೇಕು ಅಂದನು.",
			"9": "<sup>9</sup> ಈ ಮಾತುಗಳನ್ನು ಹೇಳಿದ ಬಳಿಕ ಅವರು ನೋಡುತ್ತಾ ಇದ್ದ ಹಾಗೆ ಆತನು ಏರಿಸಲ್ಪಟ್ಟನು; ಮೋಡವು ಆತನನ್ನು ಕವಿದುಕೊಂಡದ್ದರಿಂದ ಅವರ ಕಣ್ಣಿಗೆ ಮರೆಯಾದನು.",
			"10": "<sup>10</sup> ಆತನು ಹೋಗುತ್ತಿರುವಾಗ ಅವರು ಆಕಾಶದ ಕಡೆಗೆ ದೃಷ್ಟಿಸಿ ನೋಡುತ್ತಾ ಇರಲಾಗಿ ಶುಭ್ರವಸ್ತ್ರಧಾರಿಗಳಾದ ಇಬ್ಬರು ಪುರುಷರು ಫಕ್ಕನೆ ಅವರ ಹತ್ತರ ನಿಂತುಕೊಂಡು -",
			"11": "<sup>11</sup> ಗಲಿಲಾಯದವರೇ, ನೀವು ಯಾಕೆ ಆಕಾಶದ ಕಡೆಗೆ ನೋಡುತ್ತಾ ನಿಂತಿದ್ದೀರಿ? ನಿಮ್ಮ ಬಳಿಯಿಂದ ಆಕಾಶದೊಳಕ್ಕೆ ಸೇರಿಸಲ್ಪಟ್ಟಿರುವ ಈ ಯೇಸು ಯಾವ ರೀತಿಯಲ್ಲಿ ಆಕಾಶದೊಳಕ್ಕೆ ಹೋಗಿರುವದನ್ನು ನೀವು ಕಂಡಿರೋ ಅದೇ ರೀತಿಯಲ್ಲಿ ಬರುವನು ಎಂದು ಹೇಳಿದರು.",
			"12": "<sup>12</sup> ಆಗ ಅವರು ಎಣ್ಣೇಮರಗಳ ತೋಪು ಎನಿಸಿಕೊಳ್ಳುವ ಗುಡ್ಡದಿಂದ ಯೆರೂಸಲೇಮಿಗೆ ಹಿಂತಿರುಗಿ ಬಂದರು. ಆ ಗುಡ್ಡಕ್ಕೂ ಯೆರೂಸಲೇಮಿಗೂ ಸಬ್ಬತ್‍ದಿನದಲ್ಲಿ ಪ್ರಯಾಣಮಾಡುವಷ್ಟು ದೂರ.",
			"13": "<sup>13</sup> ಅವರು ಅಲ್ಲಿಗೆ ಬಂದು ತಾವು ಇರುತ್ತಿದ್ದ ಮೇಲಂತಸ್ತಿಗೆ ಹೋದರು; ಯಾರಾರಂದರೆ - ಪೇತ್ರ, ಯೋಹಾನ, ಯಾಕೋಬ, ಅಂದ್ರೆಯ, ಫಿಲಿಪ್ಪ, ತೋಮ, ಬಾರ್ತೊಲೊಮಾಯ, ಮತ್ತಾಯ, ಅಲ್ಫಾಯನ ಯಾಕೋಬ, ಮತಾಭಿಮಾನಿ ಅನ್ನಿಸಿಕೊಂಡ ಸೀಮೋನ, ಯಾಕೋಬನ ಯೂದ, ಇವರೇ.",
			"14": "<sup>14</sup> ಇವರೆಲ್ಲರು ಏಕಮನಸ್ಸಾಗಿ ದೇವರ ಪ್ರಾರ್ಥನೆಯಲ್ಲಿ ನಿರತರಾಗಿದ್ದರು. ಕೆಲವು ಮಂದಿ ಹೆಂಗಸರೂ ಯೇಸುವಿನ ತಾಯಿಯಾದ ಮರಿಯಳೂ ಆತನ ತಮ್ಮಂದಿರೂ ಅವರ ಸಂಗಡ ಇದ್ದರು.",
			"15": "<sup>15</sup> ಆ ದಿವಸಗಳಲ್ಲಿ ಸುಮಾರು ನೂರಿಪ್ಪತ್ತು ಮಂದಿ ಸಹೋದರರು ಕೂಡಿಬಂದಿರಲಾಗಿ ಪೇತ್ರನು ಅವರ ಮಧ್ಯದಲ್ಲಿ ಎದ್ದು ನಿಂತು ಹೀಗಂದನು -",
			"16": "<sup>16</sup> ಸಹೋದರರೇ, ಯೇಸುವನ್ನು ಹಿಡಿದುಕೊಂಡವರಿಗೆ ದಾರೀತೋರಿಸಿದ ಯೂದನ ವಿಷಯವಾಗಿ ಪವಿತ್ರಾತ್ಮನು ದಾವೀದನ ಬಾಯಿಂದ ಮೊದಲೇ ಹೇಳಿಸಿದ ಶಾಸ್ತ್ರವಚನವು ನೆರವೇರುವದು ಅವಶ್ಯವಾಗಿತ್ತು.",
			"17": "<sup>17</sup> ಯೂದನು ನಮ್ಮ ಲೆಕ್ಕದಲ್ಲಿ ಸೇರಿ ಈ ಸೇವೆಯಲ್ಲಿ ಪಾಲು ಹೊಂದಿದವನಾಗಿದ್ದನು.",
			"18": "<sup>18</sup> (ಈ ಮನುಷ್ಯನು ತನ್ನ ದ್ರೋಹದಿಂದ ಸಂಪಾದಿಸಿದ ಹಣಕ್ಕೆ ಒಂದು ಹೊಲವನ್ನು ಕೊಂಡುಕೊಂಡನು; ಮತ್ತು ತಲೆಕೆಳಗಾಗಿ ಬಿದ್ದು ಅವನ ಹೊಟ್ಟೆ ಒಡೆದು ಕರುಳುಗಳೆಲ್ಲಾ ಹೊರಗೆ ಸುರಿದವು.",
			"19": "<sup>19</sup> ಇದು ಯೆರೂಸಲೇಮ್ ಪಟ್ಟಣದ ನಿವಾಸಿಗಳಿಗೆಲ್ಲಾ ತಿಳಿದುಬಂದದರಿಂದ ಆ ಹೊಲಕ್ಕೆ ಅವರ ಮಾತಿನಲ್ಲಿ ಅಕೆಲ್ದಮಾ, ಅಂದರೆ ಜೀವಹತ್ಯದ ಹೊಲ ಎಂಬ ಹೆಸರು ಬಂತು.)",
			"20": "<sup>20</sup> ಅವನ ವಿಷಯವಾಗಿ - ಅವನ ಮನೆ ಹಾಳುಬೀಳಲಿ, ಅದು ಜನಶೂನ್ಯವಾಗಲಿ ಎಂತಲೂ ಅವನ ಉದ್ಯೋಗವು ಮತ್ತೊಬ್ಬನಿಗಾಗಲಿ ಎಂತಲೂ ಕೀರ್ತನೆಗಳ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದದೆ.",
			"21": "<sup>21</sup> ಆದದರಿಂದ ಕರ್ತನಾದ ಯೇಸು ನಮ್ಮಲ್ಲಿ ಬರುತ್ತಾ ಹೋಗುತ್ತಾ ಇದ್ದ ಕಾಲವೆಲ್ಲಾ, ಅಂದರೆ ಯೋಹಾನನು ದೀಕ್ಷಾಸ್ನಾನಮಾಡಿಸಿದ್ದು ಮೊದಲುಗೊಂಡು ಯೇಸು ನಮ್ಮ ಬಳಿಯಿಂದ ಮೇಲಣ ಲೋಕವನ್ನೇರಿದ ದಿನದವರೆಗೂ ನಮ್ಮ ಜೊತೆಯಲ್ಲಿದ್ದವರೊಳಗೆ ಒಬ್ಬನು ನಮ್ಮೊಂದಿಗೆ ಆತನ ಪುನರುತ್ಥಾನದ ವಿಷಯದಲ್ಲಿ ಸಾಕ್ಷಿ ಹೇಳುವವನಾಗಬೇಕು ಅಂದನು.",
			"23": "<sup>23</sup> ಈ ಮಾತುಗಳನ್ನು ಕೇಳಿ ಅವರು ಯೂಸ್ತನೆನಿಸಿಕೊಳ್ಳುವ ಬಾರ್ಸಬನೆಂಬ ಯೋಸೇಫನನ್ನೂ",
			"24": "<sup>24</sup> ಮತ್ತೀಯನನ್ನೂ ನಿಲ್ಲಿಸಿ ಪ್ರಾರ್ಥನೆ ಮಾಡುತ್ತಾ - ಕರ್ತನೇ, ಎಲ್ಲರ ಹೃದಯವನ್ನು ಬಲ್ಲಾತನೇ, ಯೂದನು ಅಪೊಸ್ತಲತನವೆಂಬ ಈ ಸೇವಾಸ್ಥಾನದಿಂದ ಭ್ರಷ್ಟನಾಗಿ ತನಗೆ ತಕ್ಕ ಸ್ಥಳಕ್ಕೆ ಹೋಗಿರುವದರಿಂದ ಆ ಸ್ಥಾನವನ್ನು ಹೊಂದುವದಕ್ಕೆ ಈ ಇಬ್ಬರಲ್ಲಿ ನೀನು ಆರಿಸಿಕೊಂಡವನನ್ನು ತೋರಿಸಿಕೊಡು ಎಂದು ಹೇಳಿದರು.",
			"26": "<sup>26</sup> ಆಮೇಲೆ ಅವರಿಗೋಸ್ಕರ ಚೀಟುಹಾಕಿದರು. ಚೀಟು ಮತ್ತೀಯನಿಗೆ ಬಂದದರಿಂದ ಅವನು ಹನ್ನೊಂದು ಮಂದಿ ಅಪೊಸ್ತಲರ ಸಂಗಡ ಸೇರಿ ಎಣಿಸಲ್ಪಟ್ಟನು."
		},
		"2": {
			"1": "<sup>1</sup> ಪಂಚಾಶತ್ತಮ ದಿನದ ಹಬ್ಬವು ಬಂದಿರಲಾಗಿ ಅವರೆಲ್ಲರು ಏಕಮನಸ್ಸಿನಿಂದ ಒಂದೇ ಸ್ಥಳದಲ್ಲಿ ಕೂಡಿದ್ದರು.",
			"2": "<sup>2</sup> ಆಗ ಬಿರುಗಾಳಿ ಬೀಸುತ್ತದೋ ಎಂಬಂತೆ ಫಕ್ಕನೆ ಆಕಾಶದೊಳಗಿಂದ ಒಂದು ಶಬ್ದವುಂಟಾಗಿ ಅವರು ಕೂತಿದ್ದ ಮನೆಯನ್ನೆಲ್ಲಾ ತುಂಬಿಕೊಂಡಿತು.",
			"3": "<sup>3</sup> ಅದಲ್ಲದೆ ಉರಿಯಂತಿದ್ದ ನಾಲಿಗೆಗಳು ವಿಂಗಡಿಸಿಕೊಳ್ಳುವ ಹಾಗೆ ಅವರಿಗೆ ಕಾಣಿಸಿ ಅವರಲ್ಲಿ ಒಬ್ಬೊಬ್ಬರ ಮೇಲೆ ಒಂದೊಂದಾಗಿ ಕೂತುಕೊಂಡವು.",
			"4": "<sup>4</sup> ಆಗ ಅವರೆಲ್ಲರು ಪವಿತ್ರಾತ್ಮಭರಿತರಾಗಿ ಆ ಆತ್ಮ ತಮತಮಗೆ ನುಡಿಯುವ ಶಕ್ತಿಯನ್ನು ಕೊಡುವ ಪ್ರಕಾರ ಬೇರೆಬೇರೆ ಭಾಷೆಗಳಿಂದ ಮಾತಾಡುವದಕ್ಕೆ ಪ್ರಾರಂಭಿಸಿದರು.",
			"5": "<sup>5</sup> ಆಕಾಶದ ಕೆಳಗಿರುವ ಎಲ್ಲಾ ದೇಶದವರೊಳಗಿಂದ ಬಂದ ಸದ್ಭಕ್ತರಾದ ಯೆಹೂದ್ಯರು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ವಾಸವಾಗಿದ್ದರು.",
			"6": "<sup>6</sup> ಆ ಸಪ್ಪಳವಾದಾಗ ಜನರು ಗುಂಪುಗುಂಪಾಗಿ ಕೂಡಿಬಂದು ಪ್ರತಿಯೊಬ್ಬರು ತಮ್ಮ ತಮ್ಮ ಭಾಷೆಯಲ್ಲೇ ಇವರು ಮಾತಾಡುವದನ್ನು ಕೇಳಿ ಭ್ರಮೆಗೊಂಡರು.",
			"7": "<sup>7</sup> ಎಲ್ಲರು ಆಶ್ಚರ್ಯದಿಂದ ಬೆರಗಾಗಿ - ಇಗೋ, ಮಾತಾಡುತ್ತಿರುವ ಇವರೆಲ್ಲರು ಗಲಿಲಾಯದವರಲ್ಲವೇ.",
			"8": "<sup>8</sup> ನಾವು ಪ್ರತಿಯೊಬ್ಬರು ನಮ್ಮ ನಮ್ಮ ಹುಟ್ಟುಭಾಷೆಯಲ್ಲಿ ಇವರು ಮಾತಾಡುವದನ್ನು ಕೇಳುತ್ತೇವಲ್ಲಾ, ಇದು ಹೇಗೆ?",
			"9": "<sup>9</sup> ಪಾರ್ಥ್ಯರೂ ಮೇದ್ಯರೂ ಏಲಾಮ್ಯರೂ ಮೆಸೊಪೊತಾಮ್ಯ, ಯೂದಾಯ, ಕಪ್ಪದೋಕ್ಯ, ಪೊಂತ, ಆಸ್ಯ, ಫ್ರುಗ್ಯ,",
			"10": "<sup>10</sup> ಪಂಫುಲ್ಯ, ಐಗುಪ್ತ, ಕುರೇನೆಯ ಮಗ್ಗುಲಲ್ಲಿರುವ ಲಿಬ್ಯ, ಈ ಸೀಮೆಗಳಲ್ಲಿ ವಾಸವಾಗಿರುವವರೂ ರೋಮಾಪುರದಿಂದ ಬಂದು ಇಳಿದಿರುವ ಯೆಹೂದ್ಯರೂ ಯೆಹೂದ್ಯ ಮತಾವಲಂಬಿಗಳೂ ಕ್ರೇತ್ಯರೂ",
			"11": "<sup>11</sup> ಅರಬೀದೇಶದವರೂ ಆಗಿರುವ ನಾವು ನಮ್ಮ ನಮ್ಮ ಭಾಷೆಗಳಲ್ಲಿ ಇವರು ದೇವರ ಮಹತ್ತುಗಳ ವಿಷಯವಾಗಿ ಹೇಳುವದನ್ನು ಕೇಳುತ್ತೇವೆ ಅಂದುಕೊಂಡರು.",
			"12": "<sup>12</sup> ಎಲ್ಲರೂ ಬೆರಗಾಗಿ ಕಳವಳಗೊಂಡು - ಇದೇನಾಗಿರಬಹುದು ಎಂದು ಒಬ್ಬರನ್ನೊಬ್ಬರು ಕೇಳುತ್ತಿದ್ದರು.",
			"13": "<sup>13</sup> ಕೆಲವರು - ಇವರು ಮದ್ಯಪಾನ ಮಾಡಿ ಮತ್ತರಾಗಿದ್ದಾರೆಂದು ಹಾಸ್ಯಮಾಡಿದರು.",
			"14": "<sup>14</sup> ಆಗ ಪೇತ್ರನು ಹನ್ನೊಂದು ಮಂದಿ ಅಪೊಸ್ತಲರ ಸಹಿತ ಎದ್ದು ನಿಂತು ಗಟ್ಟಿಯಾದ ಧ್ವನಿಯಿಂದ ಅವರಿಗೆ ಹೀಗೆ ಪ್ರಸಂಗ ಮಾಡಿದನು - ಯೆಹೂದ್ಯರೇ, ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ವಾಸವಾಗಿರುವ ಎಲ್ಲಾ ಜನರೇ, ಈ ಸಂಗತಿಯು ಏನೆಂದು ನಿಮಗೆ ಗೊತ್ತಾಗುವ ಹಾಗೆ ನನ್ನ ಮಾತಿಗೆ ಕಿವಿಗೊಡಿರಿ;",
			"15": "<sup>15</sup> ನೀವು ಭಾವಿಸಿದಂತೆ ಇವರು ಅಮಲೇರಿದವರಲ್ಲ; ಈಗ ಬೆಳಿಗ್ಗೆ ಒಂಭತ್ತು ಘಂಟೆಯಾಗಿದೆಯಷ್ಟೆ.",
			"16": "<sup>16</sup> ಆದರೆ ಇದು ಪ್ರವಾದಿಯಾದ ಯೋವೇಲನ ಮೂಲಕವಾಗಿ ಹೇಳಿಸಿದ ಸಂಗತಿ. ಅದೇನಂದರೆ -",
			"17": "<sup>17</sup> ಕಡೇ ದಿವಸಗಳಲ್ಲಿ ನಾನು ಎಲ್ಲಾ ಮನುಷ್ಯರ ಮೇಲೆ ನನ್ನ ಆತ್ಮವನ್ನು ಸುರಿಸುವೆನು; ನಿಮ್ಮಲ್ಲಿರುವ ಗಂಡಸರೂ ಹೆಂಗಸರೂ ಪ್ರವಾದಿಸುವರು; ನಿಮ್ಮ ಯೌವನಸ್ಥರಿಗೆ ದಿವ್ಯದರ್ಶನಗಳಾಗುವವು; ನಿಮ್ಮ ಹಿರಿಯರಿಗೆ ಕನಸುಗಳು ಬೀಳುವವು;",
			"18": "<sup>18</sup> ಇದಲ್ಲದೆ ಆ ದಿನಗಳಲ್ಲಿ ನನ್ನ ದಾಸದಾಸಿಯರ ಮೇಲೆಯೂ ನನ್ನ ಆತ್ಮವನ್ನು ಸುರಿಸುವೆನು; ಅವರೂ ಪ್ರವಾದಿಸುವರು.",
			"19": "<sup>19</sup> ಕರ್ತನ ಆಗಮನದ ಗಂಭೀರವಾದ ಮಹಾದಿನವು ಬರುವದಕ್ಕೆ ಮುಂಚೆ ಮೇಲೆ ಆಕಾಶದಲ್ಲಿ ಅದ್ಭುತಕಾರ್ಯಗಳನ್ನೂ ಕೆಳಗೆ ಭೂಮಿಯಲ್ಲಿ ಸೂಚಕಕಾರ್ಯಗಳನ್ನೂ ಉಂಟುಮಾಡುವೆನು. ರಕ್ತ, ಬೆಂಕಿ, ಹಬೆಯಂತೆ ಏರುವ ಹೊಗೆ, ಇವು ಉಂಟಾಗುವವು. ಸೂರ್ಯನು ಕತ್ತಲಾಗುವನು, ಚಂದ್ರನು ರಕ್ತವಾಗುವನು;",
			"21": "<sup>21</sup> ಆದರೂ ಕರ್ತನ ನಾಮವನ್ನು ಹೇಳಿಕೊಳ್ಳುವವರೆಲ್ಲರಿಗೆ ರಕ್ಷಣೆಯಾಗುವದೆಂದು ದೇವರು ಹೇಳುತ್ತಾನೆ ಎಂಬದೇ.",
			"22": "<sup>22</sup> ಇಸ್ರಾಯೇಲ್ ಜನರೇ, ನನ್ನ ಮಾತುಗಳನ್ನು ಲಾಲಿಸಿರಿ - ನಜರೇತಿನ ಯೇಸು ಇದ್ದನಲ್ಲಾ, ದೇವರು ನಿಮಗೂ ತಿಳಿದಿರುವಂತೆ ಆತನ ಕೈಯಿಂದ ಮಹತ್ತುಗಳನ್ನೂ ಅದ್ಭುತಗಳನ್ನೂ ಸೂಚಕಕಾರ್ಯಗಳನ್ನೂ ನಿಮ್ಮಲ್ಲಿ ನಡಿಸಿ ಆತನನ್ನು ತನಗೆ ಮೆಚ್ಚಿಕೆಯಾದವನೆಂದು ನಿಮಗೆ ತೋರಿಸಿಕೊಟ್ಟನು.",
			"23": "<sup>23</sup> ಆ ಯೇಸು ದೇವರ ಸ್ಥಿರಸಂಕಲ್ಪಕ್ಕೂ ಭವಿಷ್ಯದ್‍ಜ್ಞಾನಕ್ಕೂ ಅನುಸಾರವಾಗಿ ಒಪ್ಪಿಸಲ್ಪಟ್ಟಿರಲು ನೀವು ಅನ್ಯಜನರ ಕೈಯಿಂದ ಆತನನ್ನು ಶಿಲುಬೆಗೆ ಹಾಕಿಸಿ ಕೊಂದಿರಿ.",
			"24": "<sup>24</sup> ಆತನನ್ನು ದೇವರು ಮರಣವೇದನೆಗಳಿಂದ ಬಿಡಿಸಿ ಎಬ್ಬಿಸಿದನು; ಯಾಕಂದರೆ ಮರಣವು ಆತನನ್ನು ಹಿಡುಕೊಂಡಿರುವದು ಅಸಾಧ್ಯವಾಗಿತ್ತು.",
			"25": "<sup>25</sup> ಆತನ ವಿಷಯದಲ್ಲಿ ದಾವೀದನು - ಕರ್ತನು ಯಾವಾಗಲೂ ನನ್ನೆದುರಿನಲ್ಲಿರುವದನ್ನು ನೋಡುತ್ತಿದ್ದೆನು. ನಾನು ಕದಲದಂತೆ ಆತನು ನನ್ನ ಬಲಗಡೆಯಲ್ಲಿಯೇ ಇದ್ದಾನೆ.",
			"26": "<sup>26</sup> ಆದಕಾರಣ ನನ್ನ ಹೃದಯವು ಹರ್ಷಿಸಿತು; ನನ್ನ ನಾಲಿಗೆಯು ಉಲ್ಲಾಸಧ್ವನಿಯನ್ನು ಮಾಡಿತು. ನನ್ನ ಶರೀರವೂ ನಿರೀಕ್ಷೆಯಿಂದ ನೆಲೆಯಾಗಿರುವದು;",
			"27": "<sup>27</sup> ಯಾಕಂದರೆ ನೀನು ನನ್ನ ಜೀವಾತ್ಮವನ್ನು ಪಾತಾಳದಲ್ಲಿ ಬಿಡುವದಿಲ್ಲ, ನಿನ್ನ ಪ್ರಿಯನಿಗೆ ಕೊಳೆಯುವ ಅವಸ್ಥೆಯನ್ನು ನೋಡಗೊಡಿಸುವದಿಲ್ಲ.",
			"28": "<sup>28</sup> ಜೀವಮಾರ್ಗಗಳನ್ನು ನನಗೆ ತಿಳಿಯಪಡಿಸಿದ್ದೀ; ನಿನ್ನ ಸಮ್ಮುಖದಲ್ಲಿ ನನ್ನನ್ನು ಆನಂದಭರಿತನಾಗಮಾಡುವಿ ಎಂದು ಹೇಳಿದ್ದಾನೆ.",
			"29": "<sup>29</sup> ಸಹೋದರರೇ, ಮೂಲ ಪಿತೃವಾದ ದಾವೀದನ ವಿಷಯದಲ್ಲಿ ನಾನು ಧೈರ್ಯದಿಂದ ನಿಮ್ಮ ಸಂಗಡ ಮಾತಾಡಬಹುದು ಏನಂದರೆ - ಅವನು ತೀರಿಹೋಗಿ ಹೂಣಲ್ಪಟ್ಟನು; ಅವನ ಸಮಾಧಿ ಈ ದಿನದವರೆಗೂ ನಮ್ಮಲ್ಲಿ ಅದೆ.",
			"30": "<sup>30</sup> ಅವನು ಪ್ರವಾದಿಯಾಗಿದ್ದು - ನಿನ್ನ ಸಂತತಿಯವರೊಳಗೆ ಒಬ್ಬನನ್ನು ನಾನು ನಿನ್ನ ಸಿಂಹಾಸನದ ಮೇಲೆ ಕುಳ್ಳಿರಿಸುವೆನೆಂದು ತನಗೆ ದೇವರು ಆಣೆಯಿಟ್ಟು ಹೇಳಿದ್ದನ್ನು ಬಲ್ಲವನಾಗಿ",
			"31": "<sup>31</sup> ಮುಂದಾಗುವದನ್ನು ಕಂಡು ಕ್ರಿಸ್ತನ ಪುನರುತ್ಥಾನವನ್ನೇ ಕುರಿತು - ಆತನು ಪಾತಾಳದಲ್ಲಿ ಬಿಡಲ್ಪಡಲಿಲ್ಲವೆಂತಲೂ ಆತನ ಶರೀರವು ಕೊಳೆಯುವ ಅವಸ್ಥೆಯನ್ನನುಭವಿಸುವದಿಲ್ಲವೆಂತಲೂ ಹೇಳಿದನು.",
			"32": "<sup>32</sup> ಈ ಯೇಸುವನ್ನೇ ದೇವರು ಎಬ್ಬಿಸಿದನು; ಇದಕ್ಕೆ ನಾವೆಲ್ಲರು ಸಾಕ್ಷಿಗಳಾಗಿದ್ದೇವೆ.",
			"33": "<sup>33</sup> ಆತನು ದೇವರ ಬಲಗೈಯಿಂದ ಉನ್ನತಸ್ಥಾನಕ್ಕೆ ಏರಿಸಲ್ಪಟ್ಟು ವಾಗ್ದಾನವಾಗಿದ್ದ ಪವಿತ್ರಾತ್ಮವರವನ್ನು ತಂದೆಯಿಂದ ಹೊಂದಿ",
			"34": "<sup>34</sup> ನೀವು ನೋಡಿ ಕೇಳುವದನ್ನು ಸುರಿಸಿದ್ದಾನೆ. ದಾವೀದನು ಆಕಾಶಕ್ಕೆ ಏರಿಹೋಗಲಿಲ್ಲವಲ್ಲಾ. ತಾನೇ ಹೇಳುವ ಮಾತೇನಂದರೆ - ನಾನು ನಿನ್ನ ವಿರೋಧಿಗಳನ್ನು ನಿನ್ನ ಪಾದಗಳಿಗೆ ಪೀಠವಾಗ ಮಾಡುವ ತನಕ ನನ್ನ ಬಲಗಡೆಯಲ್ಲಿ ಕೂತುಕೊಂಡಿರು ಎಂದು ಕರ್ತನು ನನ್ನ ಒಡೆಯನಿಗೆ ನುಡಿದನು ಎಂಬದೇ.",
			"36": "<sup>36</sup> ಆದದರಿಂದ ನೀವು ಶಿಲುಬೆಗೆ ಹಾಕಿಸಿದ ಈ ಯೇಸುವನ್ನೇ ದೇವರು ಒಡೆಯನನ್ನಾಗಿಯೂ ಕ್ರಿಸ್ತನನ್ನಾಗಿಯೂ ಮಾಡಿದ್ದಾನೆಂಬದು ಇಸ್ರಾಯೇಲ್ ಕುಲದವರಿಗೆಲ್ಲಾ ನಿಸ್ಸಂದೇಹವಾಗಿ ತಿಳಿದಿರಲಿ ಎಂದು ಹೇಳಿದನು.",
			"37": "<sup>37</sup> ಇದನ್ನು ಅವರು ಕೇಳಿ ಹೃದಯದಲ್ಲಿ ಅಲಗುನೆಟ್ಟಂತಾಗಿ ಪೇತ್ರನನ್ನೂ ಉಳಿದ ಅಪೊಸ್ತಲರನ್ನೂ - ಸಹೋದರರೇ, ನಾವೇನು ಮಾಡಬೇಕು ಎಂದು ಕೇಳಲು",
			"38": "<sup>38</sup> ಪೇತ್ರನು ಅವರಿಗೆ - ನಿಮ್ಮ ಪಾಪಗಳು ಪರಿಹಾರವಾಗುವದಕ್ಕಾಗಿ ನಿಮ್ಮಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬರು ದೇವರ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡು ಯೇಸು ಕ್ರಿಸ್ತನ ಹೆಸರಿನ ಮೇಲೆ ದೀಕ್ಷಾಸ್ನಾನಮಾಡಿಸಿಕೊಳ್ಳಿರಿ, ಆಗ ನೀವು ಪವಿತ್ರಾತ್ಮದಾನವನ್ನು ಹೊಂದುವಿರಿ;",
			"39": "<sup>39</sup> ಆ ವಾಗ್ದಾನವು ನಿಮಗೂ ನಿಮ್ಮ ಮಕ್ಕಳಿಗೂ ದೂರವಾಗಿರುವವರೆಲ್ಲರಿಗೂ ಅಂತೂ ನಮ್ಮ ದೇವರಾಗಿರುವ ಕರ್ತನು ತನ್ನ ಕಡೆಗೆ ಕರೆಯುವವರೆಲ್ಲರಿಗೆ ಮಾಡೋಣವಾಗಿದೆ ಎಂದು ಹೇಳಿದನು.",
			"40": "<sup>40</sup> ಬೇರೆ ಅನೇಕವಾದ ಮಾತುಗಳಿಂದ ಅವನು ಖಂಡಿತವಾಗಿ ಸಾಕ್ಷಿನುಡಿದು - ವಕ್ರಬುದ್ಧಿಯುಳ್ಳ ಈ ಸಂತತಿಯವರಿಂದ ತಪ್ಪಿಸಿಕೊಳ್ಳಿರಿ ಎಂದು ಅವರನ್ನು ಎಚ್ಚರಿಸಿದನು.",
			"41": "<sup>41</sup> ಅವನ ಮಾತಿಗೆ ಒಪ್ಪಿಕೊಂಡವರು ದೀಕ್ಷಾಸ್ನಾನ ಮಾಡಿಸಿಕೊಂಡರು. ಆ ದಿವಸ ಸುಮಾರು ಮೂರುಸಾವಿರ ಜನರು ಅವರಲ್ಲಿ ಸೇರಿದರು.",
			"42": "<sup>42</sup> ಇವರು ಅಪೊಸ್ತಲರ ಬೋಧನೆಯನ್ನು ಕೇಳುವದರಲ್ಲಿಯೂ ಸಹೋದರರ ಅನ್ಯೋನ್ಯತೆಯಲ್ಲಿಯೂ ರೊಟ್ಟಿ ಮುರಿಯುವದರಲ್ಲಿಯೂ ಪ್ರಾರ್ಥನೆಗಳಲ್ಲಿಯೂ ನಿರತರಾಗಿದ್ದರು.",
			"43": "<sup>43</sup> ಎಲ್ಲರಿಗೂ ಭಯ ಉಂಟಾಯಿತು, ಇದಲ್ಲದೆ ಅನೇಕ ಅದ್ಭುತಕಾರ್ಯಗಳೂ ಸೂಚಕಕಾರ್ಯಗಳೂ ಅಪೊಸ್ತಲರ ಕೈಯಿಂದ ನಡೆದವು.",
			"44": "<sup>44</sup> ನಂಬಿದವರೆಲ್ಲರು ಒಗ್ಗಟ್ಟಾಗಿದ್ದು ತಮ್ಮದೆಲ್ಲವನ್ನೂ ಹುದುವಾಗಿ ಅನುಭೋಗಿಸುತ್ತಾ",
			"45": "<sup>45</sup> ತಮ್ಮ ತಮ್ಮ ಚರಸ್ಥಿರಸೊತ್ತುಗಳನ್ನು ಮಾರಿ ಬಂದ ಹಣವನ್ನು ಎಲ್ಲರಿಗೂ ಅವರವರ ಅಗತ್ಯವಿದ್ದ ಹಾಗೆ ಹಂಚಿಕೊಡುತ್ತಾ ಇದ್ದರು.",
			"46": "<sup>46</sup> ಅವರು ದಿನಾಲು ಏಕಮನಸ್ಸಿನಿಂದ ದೇವಾಲಯದಲ್ಲಿ ಕೂಡುತ್ತಾ ಮನೆಮನೆಗಳಲ್ಲಿ ರೊಟ್ಟಿಮುರಿದು ಉಲ್ಲಾಸದಿಂದಲೂ ಸರಲ ಹೃದಯದಿಂದಲೂ ಊಟಮಾಡುತ್ತಾ ಇದ್ದರು;",
			"47": "<sup>47</sup> ದೇವರನ್ನು ಕೊಂಡಾಡುವವರಾಗಿಯೂ ಜನರೆಲ್ಲರ ದಯವನ್ನು ಹೊಂದುವವರಾಗಿಯೂ ಇದ್ದರು. ಕರ್ತನು ರಕ್ಷಣೆಯ ಮಾರ್ಗದಲ್ಲಿರುವವರನ್ನು ದಿನಾಲು ಅವರ ಮಂಡಲಿಗೆ ಸೇರಿಸುತ್ತಿದ್ದನು."
		},
		"3": {
			"1": "<sup>1</sup> ಒಂದಾನೊಂದು ದಿವಸ ಪೇತ್ರ ಯೋಹಾನರು ಮಧ್ಯಾಹ್ನದ ಮೇಲೆ ಮೂರು ಘಂಟೆಗೆ ನಡೆಯ ತಕ್ಕ ಪ್ರಾರ್ಥನೆಗಾಗಿ ದೇವಾಲಯಕ್ಕೆ ಹೋಗುತ್ತಿರಲು ಹುಟ್ಟುಕುಂಟನಾಗಿದ್ದ ಒಬ್ಬ ಮನುಷ್ಯನನ್ನು ಯಾರೋ ಹೊತ್ತುಕೊಂಡು ಬಂದರು.",
			"2": "<sup>2</sup> ದೇವಾಲಯದೊಳಕ್ಕೆ ಹೋಗುವವರಿಂದ ಭಿಕ್ಷೆಬೇಡುವದಕ್ಕಾಗಿ ಅವನನ್ನು ದೇವಾಲಯದ ಸುಂದರದ್ವಾರವೆಂಬ ಬಾಗಿಲಿನಲ್ಲಿ ದಿನಾಲು ಕೂಡ್ರಿಸುತ್ತಿದ್ದರು.",
			"3": "<sup>3</sup> ಅವನು ದೇವಾಲಯದೊಳಕ್ಕೆ ಹೋಗುತ್ತಿರುವ ಪೇತ್ರ ಯೋಹಾನರನ್ನು ಕಂಡು ಭಿಕ್ಷಾಕೊಡಬೇಕು ಎಂದು ಕೇಳಲು",
			"4": "<sup>4</sup> ಪೇತ್ರ ಯೋಹಾನರಿಬ್ಬರೂ ಅವನನ್ನು ದೃಷ್ಟಿಸಿ ನೋಡಿದರು. ಪೇತ್ರನು - ನಮ್ಮನ್ನು ನೋಡು ಅಂದನು.",
			"5": "<sup>5</sup> ಅವನು ಅವರಿಂದ ಏನಾದರೂ ದೊರಕೀತೆಂದು ನಿರೀಕ್ಷಿಸಿ ಅವರನ್ನು ಲಕ್ಷ್ಯವಿಟ್ಟು ನೋಡಿದನು.",
			"6": "<sup>6</sup> ಆಗ ಪೇತ್ರನು - ಬೆಳ್ಳಿಬಂಗಾರವಂತೂ ನನ್ನಲ್ಲಿಲ್ಲ, ನನ್ನಲ್ಲಿರುವದನ್ನು ನಿನಗೆ ಕೊಡುತ್ತೇನೆ. ನಜರೇತಿನ ಯೇಸು ಕ್ರಿಸ್ತನ ಹೆಸರಿನಲ್ಲೇ ಎದ್ದು ನಡೆದಾಡು ಎಂದು ಹೇಳಿ",
			"7": "<sup>7</sup> ಅವನನ್ನು ಬಲಗೈಹಿಡಿದು ಎತ್ತಿದನು. ಆ ಕ್ಷಣವೇ ಅವನ ಕಾಲುಗಳಿಗೂ ಹರಡುಗಳಿಗೂ ಬಲಬಂತು;",
			"8": "<sup>8</sup> ಅವನು ಹಾರಿ ನಿಂತು ನಡೆದಾಡಿದನು; ನಡೆಯುತ್ತಾ ಹಾರುತ್ತಾ ದೇವರನ್ನು ಕೊಂಡಾಡುತ್ತಾ ಅವರ ಜೊತೆಯಲ್ಲಿ ದೇವಾಲಯದೊಳಗೆ ಹೋದನು.",
			"9": "<sup>9</sup> ಅವನು ನಡೆಯುತ್ತಾ ದೇವರನ್ನು ಕೊಂಡಾಡುತ್ತಾ ಇರುವದನ್ನು ಜನರೆಲ್ಲರು ನೋಡಿ",
			"10": "<sup>10</sup> ಇವನು ದೇವಾಲಯದ ಸುಂದರದ್ವಾರವೆಂಬ ಬಾಗಿಲಿನಲ್ಲಿ ಕೂತು ಭಿಕ್ಷಾಬೇಡುತ್ತಿದ್ದವನೆಂದು ಗುರುತುಹಿಡಿದು ಅವನಿಗೆ ಸಂಭವಿಸಿದ್ದಕ್ಕೆ ವಿಸ್ಮಯಹಿಡಿದವರಾಗಿ ಬಹಳ ಬೆರಗಾದರು.",
			"11": "<sup>11</sup> ಅವನು ಪೇತ್ರ ಯೋಹಾನರನ್ನು ಹಿಡುಕೊಂಡೇ ಇರುವಾಗ ಆಶ್ಚರ್ಯಪಡುತ್ತಿರುವ ಆ ಜನರೆಲ್ಲರು ಅವರ ಬಳಿಗೆ ಸೊಲೊಮೋನನದೆಂಬ ಹೆಸರುಳ್ಳ ಮಂಟಪಕ್ಕೆ ಗುಂಪು ಗುಂಪಾಗಿ ಓಡಿಬಂದರು.",
			"12": "<sup>12</sup> ಪೇತ್ರನು ಇದನ್ನು ನೋಡಿ ಜನರಿಗೆ - ಇಸ್ರಾಯೇಲ್ ಜನರೇ, ನೀವು ಯಾಕೆ ಇದಕ್ಕೆ ಆಶ್ಚರ್ಯಪಡುತ್ತೀರಿ? ನೀವು ನಮ್ಮ ಮೇಲೆ ದೃಷ್ಟಿಯಿಟ್ಟು ನೋಡುವದೇನು? ನಾವು ಸ್ವಂತ ಶಕ್ತಿಯಿಂದಾಗಲಿ ದೇವಭಕ್ತಿಯಿಂದಾಗಲಿ ಇವನನ್ನು ನಡೆಯುವಂತೆ ಮಾಡಿದೆವೆಂದು ಭಾವಿಸಬೇಡಿರಿ.",
			"13": "<sup>13</sup> ನಮ್ಮ ಪಿತೃಗಳಾದ ಅಬ್ರಹಾಮ ಇಸಾಕ ಯಾಕೋಬರ ದೇವರು ತನ್ನ ಸೇವಕನಾದ ಯೇಸುವನ್ನು ಮಹಿಮೆಪಡಿಸಿದ್ದಾನೆ. ನೀವಂತೂ ಆತನನ್ನು ಹಿಡಿದುಕೊಟ್ಟಿರಿ; ಮತ್ತು ಪಿಲಾತನು ಆತನನ್ನು ಬಿಡಿಸಬೇಕೆಂದು ನಿರ್ಣಯಿಸಿದಾಗ ಆತನನ್ನು ಬೇಡವೆಂದಿರಿ.",
			"14": "<sup>14</sup> ನೀವು ಪರಿಶುದ್ಧನೂ ನೀತಿವಂತನೂ ಆಗಿರುವ ಪುರುಷನನ್ನು ಬೇಡವೆಂದು ಹೇಳಿ ಕೊಲೆಗಾರನಾದ ಮನುಷ್ಯನನ್ನು ಬಿಡಿಸಿಕೊಡಬೇಕೆಂದು ಬೇಡಿಕೊಂಡು ಜೀವನಾಯಕನನ್ನು ಕೊಲ್ಲಿಸಿದಿರಿ.",
			"15": "<sup>15</sup> ಆದರೆ ದೇವರು ಆತನನ್ನು ಸತ್ತವರೊಳಗಿಂದ ಎಬ್ಬಿಸಿದನು; ಈ ವಿಷಯದಲ್ಲಿ ನಾವೇ ಸಾಕ್ಷಿಗಳು.",
			"16": "<sup>16</sup> ನೀವು ನೋಡುವಂಥ ನಿಮಗೆ ಗುರುತಿರುವಂಥ ಈ ಮನುಷ್ಯನು ನೆಟ್ಟಗಾದದ್ದಕ್ಕೆ ಆತನ ಹೆಸರಿನಲ್ಲಿ ಇಟ್ಟ ನಂಬಿಕೆಯೇ ಕಾರಣ; ಆ ಹೆಸರೇ ಇವನನ್ನು ಬಲಪಡಿಸಿತು. ಆತನ ಮೂಲಕ ಉಂಟಾಗಿರುವ ನಂಬಿಕೆಯೇ ಇವನಿಗೆ ನಿಮ್ಮೆಲ್ಲರ ಮುಂದೆ ಪೂರ್ಣಕ್ಷೇಮವನ್ನು ಕೊಟ್ಟಿತು.",
			"17": "<sup>17</sup> ಸಹೋದರರೇ, ಅದಿರಲಿ, ನೀವು ಆ ಕಾರ್ಯವನ್ನು ತಿಳಿಯದೆ ಮಾಡಿದಿರೆಂದು ಬಲ್ಲೆನು; ನಿಮ್ಮ ಅಧಿಕಾರಿಗಳೂ ತಿಳಿಯದೆ ಮಾಡಿದರು.",
			"18": "<sup>18</sup> ಆದರೆ ದೇವರು ತಾನು ನೇಮಿಸಿದ ಕ್ರಿಸ್ತನು ಬಾಧೆಯನ್ನನುಭವಿಸಿ ಸಾಯಬೇಕೆಂಬದಾಗಿ ಎಲ್ಲಾ ಪ್ರವಾದಿಗಳ ಬಾಯಿಂದ ಮುಂಚಿತವಾಗಿ ಹೇಳಿಸಿದ ಮಾತನ್ನು ಹೀಗೆ ನೆರವೇರಿಸಿದನು.",
			"19": "<sup>19</sup> ಆದದರಿಂದ ದೇವರು ನಿಮ್ಮ ಪಾಪಗಳನ್ನು ಅಳಿಸಿಬಿಡುವ ಹಾಗೆ ನೀವು ಪಶ್ಚಾತ್ತಾಪಪಟ್ಟು ಆತನ ಕಡೆಗೆ ತಿರುಗಿಕೊಳ್ಳಿರಿ. ತಿರುಗಿದರೆ ದೇವರ ಸನ್ನಿಧಾನದಿಂದ ವಿಶ್ರಾಂತಿಕಾಲಗಳು ಒದಗಿ",
			"20": "<sup>20</sup> ಆತನು ನಿಮಗೆ ನೇಮಕವಾಗಿರುವ ಕ್ರಿಸ್ತನಾದ ಯೇಸುವನ್ನು ಕಳುಹಿಸಿಕೊಡುವನು.",
			"21": "<sup>21</sup> ಆದರೆ ಸಮಸ್ತವನ್ನು ಸರಿಮಾಡುವ ಕಾಲವು ಬರುವ ತನಕ ಪರಲೋಕವೇ ಆ ಕ್ರಿಸ್ತನ ಸ್ಥಾನವಾಗಿರಬೇಕು. ಆ ಕಾಲದ ವಿಷಯವಾಗಿ ದೇವರು ಪೂರ್ವದಲ್ಲಿದ್ದ ತನ್ನ ಪರಿಶುದ್ಧ ಪ್ರವಾದಿಗಳ ಬಾಯಿಂದ ಹೇಳಿಸಿದ್ದಾನೆ.",
			"22": "<sup>22</sup> ಮೋಶೆಯು ಹೇಳಿದ್ದೇನಂದರೆ - ದೇವರಾಗಿರುವ ಕರ್ತನು ನನ್ನನ್ನು ಏರ್ಪಡಿಸಿದಂತೆ ನಿಮ್ಮ ಸಹೋದರರಲ್ಲಿ ಮತ್ತೊಬ್ಬ ಪ್ರವಾದಿಯನ್ನು ನಿಮಗೆ ಏರ್ಪಡಿಸಿಕೊಡುವನು, ಆತನು ನಿಮಗೆ ಹೇಳುವ ಎಲ್ಲಾ ಮಾತುಗಳಿಗೆ ಕಿವಿಗೊಡಬೇಕು;",
			"23": "<sup>23</sup> ಆ ಪ್ರವಾದಿಯ ಮಾತನ್ನು ಕೇಳದಿರುವ ಪ್ರತಿಯೊಬ್ಬನು ನಮ್ಮ ಜನರೊಳಗಿಂದ ಸಂಪೂರ್ಣವಾಗಿ ನಾಶವಾಗುವನು ಎಂಬದೇ.",
			"24": "<sup>24</sup> ಇದಲ್ಲದೆ ಸಮುವೇಲನೂ ಅವನ ತರುವಾಯ ಬಂದು ಪ್ರವಾದಿಸಿದ ಎಲ್ಲಾ ಪ್ರವಾದಿಗಳೂ ಸಹ ಈಗಿನ ದಿನಗಳ ವಿಷಯದಲ್ಲಿ ತಿಳಿಸಿದರು.",
			"25": "<sup>25</sup> ನೀವು ಪ್ರವಾದಿಗಳ ಶಿಷ್ಯರೂ ದೇವರು ನಮ್ಮ ಪಿತೃಗಳ ಸಂಗಡ ಮಾಡಿಕೊಂಡ ಒಡಂಬಡಿಕೆಗೆ ಬಾಧ್ಯರೂ ಆಗಿದ್ದೀರಿ. ಆತನು ಅಬ್ರಹಾಮನಿಗೆ - ನಿನ್ನ ಸಂತತಿಯ ಮೂಲಕ ಭೂಲೋಕದ ಎಲ್ಲಾ ಕುಲದವರಿಗೂ ಆಶೀರ್ವಾದವುಂಟಾಗುವದು ಎಂದು ಹೇಳಿ ಆ ಒಡಂಬಡಿಕೆಯನ್ನು ಮಾಡಿಕೊಂಡನಲ್ಲಾ.",
			"26": "<sup>26</sup> ಮೊದಲು ನಿಮಗಾಗಿಯೇ ದೇವರು ತನ್ನ ಸೇವಕನನ್ನು ಏರ್ಪಡಿಸಿ ಈತನು ನಿಮ್ಮಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬನನ್ನು ಅವನವನ ಕೆಟ್ಟ ನಡತೆಗಳಿಂದ ತಿರುಗಿಸಿ ನಿಮ್ಮನ್ನು ಆಶೀರ್ವದಿಸಬೇಕೆಂದು ಆತನನ್ನು ಕಳುಹಿಸಿಕೊಟ್ಟಿದ್ದಾನೆ ಎಂದು ಹೇಳಿದನು."
		},
		"4": {
			"1": "<sup>1</sup> ಅಪೊಸ್ತಲರು ಜನರಿಗೆ ಉಪದೇಶ ಮಾಡುತ್ತಾ",
			"2": "<sup>2</sup> ಸತ್ತವರು ಜೀವಿತರಾಗಿ ಏಳುವರು ಎಂಬದನ್ನು ಯೇಸುವಿನ ದೃಷ್ಟಾಂತದಿಂದ ಸಾರುತ್ತಾ ಇದ್ದದ್ದಕ್ಕೆ ಯಾಜಕರೂ ದೇವಾಲಯದ ಅಧಿಪತಿಯೂ ಸದ್ದುಕಾಯರೂ ಅಸಮಾಧಾನಪಟ್ಟು ಅಪೊಸ್ತಲರು ಜನರ ಸಂಗಡ ಮಾತಾಡುತ್ತಿರುವಾಗಲೇ ಬಂದು",
			"3": "<sup>3</sup> ಅವರನ್ನು ಹಿಡಿದು ಆಗಲೇ ಸಾಯಂಕಾಲವಾಗಿದ್ದದರಿಂದ ಮರುದಿನದವರೆಗೆ ಕಾವಲಲ್ಲಿಟ್ಟರು.",
			"4": "<sup>4</sup> ಆದರೆ ವಾಕ್ಯವನ್ನು ಕೇಳಿದವರಲ್ಲಿ ಅನೇಕರು ನಂಬಿದರು; ಗಂಡಸರ ಸಂಖ್ಯೆ ಸುಮಾರು ಐದುಸಾವಿರ ತನಕ ಬೆಳೆಯಿತು.",
			"5": "<sup>5</sup> ಮರುದಿನ ಯೆಹೂದ್ಯರ ಅಧಿಕಾರಿಗಳೂ ಹಿರಿಯರೂ ಶಾಸ್ತ್ರಿಗಳೂ ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಕೂಡಿಬಂದರು.",
			"6": "<sup>6</sup> ಅನ್ನನೆಂಬ ಮಹಾಯಾಜಕನೂ ಕಾಯಫನೂ ಯೋಹಾನನೂ ಅಲೆಕ್ಸಾಂದ್ರನೂ ಮಹಾಯಾಜಕನ ಸಂಬಂಧಿಗಳೆಲ್ಲರೂ ಇದ್ದರು.",
			"7": "<sup>7</sup> ಇವರು ಪೇತ್ರ ಯೋಹಾನರನ್ನು ನಡುವೆ ನಿಲ್ಲಿಸಿ - ನೀವು ಎಂಥಾ ಶಕ್ತಿಯಿಂದ ಇಲ್ಲವೆ ಎಂಥಾ ಹೆಸರಿನಿಂದ ಇದನ್ನು ಮಾಡಿದಿರಿ ಎಂದು ಕೇಳಲು",
			"8": "<sup>8</sup> ಪೇತ್ರನು ಪವಿತ್ರಾತ್ಮಭರಿತನಾಗಿ ಅವರಿಗೆ - ಜನರ ಅಧಿಕಾರಿಗಳೇ, ಹಿರಿಯರೇ,",
			"9": "<sup>9</sup> ಒಬ್ಬ ಅಂಗಹೀನನಿಗೆ ಆದ ಉಪಕಾರದ ವಿಷಯದಲ್ಲಿ ಅವನಿಗೆ ಯಾತರಿಂದ ನೆಟ್ಟಗಾಯಿತು ಎಂಬದಾಗಿ ನಮ್ಮನ್ನು ಈ ಹೊತ್ತು ವಿಚಾರಣೆ ಮಾಡುವದಾದರೆ",
			"10": "<sup>10</sup> ನಿಮ್ಮೆಲ್ಲರಿಗೂ ಇಸ್ರಾಯೇಲ್ ಜನರೆಲ್ಲರಿಗೂ ತಿಳಿಯಬೇಕಾದದ್ದೇನಂದರೆ - ನೀವು ಶಿಲುಬೆಗೆ ಹಾಕಿಸಿದಂಥ ಮತ್ತು ದೇವರು ಸತ್ತವರೊಳಗಿಂದ ಎಬ್ಬಿಸಿದಂಥ ನಜರೇತಿನ ಯೇಸು ಕ್ರಿಸ್ತನ ಹೆಸರಿನಿಂದಲೇ ಈ ಮನುಷ್ಯನು ನಿಮ್ಮೆದುರಿನಲ್ಲಿ ಸ್ವಸ್ಥನಾಗಿ ನಿಂತಿರುತ್ತಾನೆ.",
			"11": "<sup>11</sup> ಮನೆ ಕಟ್ಟುವವರಾದ ನೀವು ಹೀನೈಸಿದ ಕಲ್ಲು ಆತನು; ಆತನೇ ಮುಖ್ಯವಾದ ಮೂಲೆಗಲ್ಲಾದನು.",
			"12": "<sup>12</sup> ಬರಬೇಕಾದ ರಕ್ಷಣೆಯು ಇನ್ನಾರಲ್ಲಿಯೂ ಸಿಕ್ಕುವದಿಲ್ಲ; ಆ ಹೆಸರಿನಿಂದಲೇ ಹೊರತು ಆಕಾಶದ ಕೆಳಗೆ ಮನುಷ್ಯರೊಳಗೆ ಕೊಡಲ್ಪಟ್ಟಿರುವ ಬೇರೆ ಯಾವ ಹೆಸರಿನಿಂದಲೂ ನಮಗೆ ರಕ್ಷಣೆಯಾಗುವದಿಲ್ಲ ಎಂದು ಹೇಳಿದನು.",
			"13": "<sup>13</sup> ಪೇತ್ರ ಯೋಹಾನರು ಧೈರ್ಯದಿಂದ ಮಾತಾಡುವದನ್ನು ಆ ಸಭಿಕರು ನೋಡಿ ಅವರು ಶಾಸ್ತ್ರಾಭ್ಯಾಸ ಮಾಡದ ಸಾಧಾರಣರೆಂದು ತಿಳಿದು ಆಶ್ಚರ್ಯಪಟ್ಟರು.",
			"14": "<sup>14</sup> ಇವರು ಯೇಸುವಿನ ಸಂಗಡ ಇದ್ದವರೆಂದು ಗುರುತು ಹಿಡಿದರು; ಮತ್ತು ಕ್ಷೇಮಹೊಂದಿದ ಆ ಮನುಷ್ಯನು ಅವರ ಕೂಡ ನಿಂತಿರುವದನ್ನು ನೋಡಿ ಪ್ರತಿಯಾಗಿ ಏನೂ ಮಾತಾಡಲಾರದೆ ಇದ್ದರು.",
			"15": "<sup>15</sup> ಆಮೇಲೆ ಸಭೆಯಿಂದ ಸ್ವಲ್ಪ ಹೊರಗೆ ಹೋಗಿರೆಂದು ಅವರಿಗೆ ಅಪ್ಪಣೆಕೊಟ್ಟು -",
			"16": "<sup>16</sup> ಈ ಮನುಷ್ಯರಿಗೆ ನಾವೇನು ಮಾಡೋಣ? ಪ್ರಸಿದ್ಧವಾದ ಒಂದು ಸೂಚಕಕಾರ್ಯವು ಇವರ ಮೂಲಕವಾಗಿ ನಡೆಯಿತೆಂಬದು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ವಾಸಮಾಡುವವರೆಲ್ಲರಿಗೂ ಗೊತ್ತಾಗಿದೆಯಷ್ಟೆ; ಅದು ಆಗಲಿಲ್ಲವೆನ್ನುವದಕ್ಕಾಗದು.",
			"17": "<sup>17</sup> ಆದರೆ ಇದು ಜನರಲ್ಲಿ ಇನ್ನೂ ಹಬ್ಬದಂತೆ ಮುಂದೆ ಆ ಹೆಸರನ್ನು ಎತ್ತಿ ಯಾರ ಸಂಗಡಲೂ ಮಾತಾಡಬಾರದೆಂದು ಅವರನ್ನು ಬೆದರಿಸೋಣ ಎಂಬದಾಗಿ ಒಬ್ಬರ ಕೂಡೊಬ್ಬರು ಆಲೋಚನೆ ಮಾಡಿಕೊಂಡು",
			"18": "<sup>18</sup> ಅವರನ್ನು ಕರೆಯಿಸಿ - ಆ ಯೇಸುವಿನ ಹೆಸರನ್ನು ಎತ್ತಿ ಹೇಗೂ ಮಾತಾಡಬಾರದು, ಉಪದೇಶ ಮಾಡಲೂಬಾರದು ಎಂದು ಅವರಿಗೆ ಖಂಡಿತವಾಗಿ ಅಪ್ಪಣೆಕೊಟ್ಟರು.",
			"19": "<sup>19</sup> ಅದಕ್ಕೆ ಪೇತ್ರ ಯೋಹಾನರು ಅವರಿಗೆ - ದೇವರ ಮಾತನ್ನು ಕೇಳುವದಕ್ಕಿಂತಲೂ ನಿಮ್ಮ ಮಾತನ್ನು ಕೇಳುವದು ದೇವರ ಮುಂದೆ ನ್ಯಾಯವೋ ಏನು? ನೀವೇ ತೀರ್ಪುಮಾಡಿಕೊಳ್ಳಿರಿ;",
			"20": "<sup>20</sup> ನಾವಂತೂ ಕಂಡು ಕೇಳಿದ್ದನ್ನು ಹೇಳದೆ ಇರಲಾರೆವು ಎಂದು ಉತ್ತರ ಕೊಟ್ಟರು.",
			"21": "<sup>21</sup> ನಡೆದಿದ್ದ ಕಾರ್ಯಕ್ಕೋಸ್ಕರ ಜನರೆಲ್ಲರು ದೇವರನ್ನು ಕೊಂಡಾಡುತ್ತಾ ಇದ್ದ ಕಾರಣ ಅವರ ದೆಸೆಯಿಂದ ಸಭಿಕರು ಅಪೊಸ್ತಲರನ್ನು ದಂಡಿಸತಕ್ಕ ಉಪಾಯವನ್ನು ಕಾಣದೆ ಅವರನ್ನು ಇನ್ನಷ್ಟು ಬೆದರಿಸಿ ಬಿಟ್ಟುಬಿಟ್ಟರು.",
			"22": "<sup>22</sup> ಈ ಸೂಚಕಕಾರ್ಯದಿಂದ ಸ್ವಸ್ಥತೆಹೊಂದಿದ ಆ ಮನುಷ್ಯನಿಗೆ ನಾಲ್ವತ್ತು ವರುಷಕ್ಕಿಂತ ಹೆಚ್ಚು ವಯಸ್ಸಾಗಿತ್ತು.",
			"23": "<sup>23</sup> ಇವರು ಬಿಡುಗಡೆ ಹೊಂದಿ ಸ್ವಂತ ಜನರ ಬಳಿಗೆ ಹೋಗಿ ಮಹಾಯಾಜಕರೂ ಹಿರಿಯರೂ ತಮಗೆ ಹೇಳಿದ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ತಿಳಿಸಿದರು.",
			"24": "<sup>24</sup> ಅವರು ಕೇಳಿ ಏಕಮನಸ್ಸಾಗಿ ದೇವರನ್ನು ಗಟ್ಟಿಯಾದ ಧ್ವನಿಯಿಂದ ಹೀಗೆ ಪ್ರಾರ್ಥಿಸಿದರು - ಒಡೆಯನೇ, ಭೂಮ್ಯಾಕಾಶಗಳನ್ನೂ ಸಮುದ್ರವನ್ನೂ ಅವುಗಳಲ್ಲಿರುವ ಸಮಸ್ತವನ್ನೂ ಉಂಟುಮಾಡಿದಾತನೇ,",
			"25": "<sup>25</sup> ನೀನು ಪವಿತ್ರಾತ್ಮ ಮೂಲಕವಾಗಿ ನಿನ್ನ ಸೇವಕನಾಗಿದ್ದ ನಮ್ಮ ಪಿತೃವಾದ ದಾವೀದನ ಬಾಯಿಂದ - ಅನ್ಯಜನಗಳು ಯಾಕೆ ರೇಗಿದರು? ಜನಾಂಗಗಳವರು ವ್ಯರ್ಥಕಾರ್ಯಗಳನ್ನು ಯಾಕೆ ಯೋಚಿಸಿದರು?",
			"26": "<sup>26</sup> ಕರ್ತನಿಗೂ ಆತನು ಅಭಿಷೇಕಿಸಿದವನಿಗೂ ವಿರೋಧವಾಗಿ ಭೂಪತಿಗಳು ಸನ್ನದ್ಧರಾಗಿ ನಿಂತರು, ಅಧಿಕಾರಿಗಳು ಏಕವಾಗಿ ಕೂಡಿಕೊಂಡರು ಎಂದು ಹೇಳಿಸಿದಿಯಲ್ಲವೇ.",
			"27": "<sup>27</sup> ಆ ಮಾತಿಗೆ ಅನುಸಾರವಾಗಿಯೇ ಈ ಪಟ್ಟಣದಲ್ಲಿ ಹೆರೋದನೂ ಪೊಂತ್ಯ ಪಿಲಾತನೂ ನೀನು ಅಭಿಷೇಕಿಸಿದ ನಿನ್ನ ಪವಿತ್ರ ಸೇವಕನಾದ ಯೇಸುವಿಗೆ ವಿರೋಧವಾಗಿ ಅನ್ಯಜನರ ಮತ್ತು ಇಸ್ರಾಯೇಲ್ ಜನರ ಸಹಿತವಾಗಿ ಕೂಡಿಕೊಂಡು",
			"28": "<sup>28</sup> ನಿನ್ನ ಕೈಯೂ ನಿನ್ನ ಸಂಕಲ್ಪವೂ ಮೊದಲು ನೇಮಿಸಿದ್ದನ್ನೇ ನಡಿಸಿದರು.",
			"29": "<sup>29</sup> ಕರ್ತನೇ, ಈಗ ನೀನು ಅವರ ಬೆದರಿಸುವಿಕೆಗಳನ್ನು ನೋಡಿ ನಿನ್ನ ಪವಿತ್ರ ಸೇವಕನಾದ ಯೇಸುವಿನ ಹೆಸರಿನ ಮೂಲಕವಾಗಿ ರೋಗ ಪರಿಹಾರವೂ ಸೂಚಕಕಾರ್ಯಗಳೂ ಅದ್ಭುತಕಾರ್ಯಗಳೂ ಉಂಟಾಗುವಂತೆ ನಿನ್ನ ಕೈ ಚಾಚುತ್ತಿರುವಲ್ಲಿ ನಿನ್ನ ದಾಸರು ನಿನ್ನ ವಾಕ್ಯವನ್ನು ಧೈರ್ಯದಿಂದ ಹೇಳುವ ಹಾಗೆ ಅನುಗ್ರಹಿಸು ಅಂದರು.",
			"31": "<sup>31</sup> ಹೀಗೆ ಪ್ರಾರ್ಥನೆ ಮಾಡಿದ ಮೇಲೆ ಅವರು ಕೂಡಿದ್ದ ಸ್ಥಳವು ನಡುಗಿತು, ಅವರೆಲ್ಲರು ಪವಿತ್ರಾತ್ಮಭರಿತರಾಗಿ ದೇವರ ವಾಕ್ಯವನ್ನು ಧೈರ್ಯದಿಂದ ಹೇಳುವವರಾದರು.",
			"32": "<sup>32</sup> ನಂಬಿದ್ದ ಮಂಡಲಿಯವರ ಹೃದಯವೂ ಪ್ರಾಣವೂ ಒಂದೇ ಆಗಿತ್ತು. ಇದಲ್ಲದೆ ಒಬ್ಬನಾದರೂ ತನಗಿದ್ದ ಆಸ್ತಿಯಲ್ಲಿ ಯಾವದೊಂದನ್ನೂ ಸ್ವಂತವಾದದ್ದೆಂದು ಹೇಳಲಿಲ್ಲ. ಎಲ್ಲವೂ ಅವರಿಗೆ ಹುದುವಾಗಿ ಇತ್ತು.",
			"33": "<sup>33</sup> ಮತ್ತು ಕರ್ತನಾದ ಯೇಸು ಜೀವಂತನಾಗಿ ಎದ್ದುಬಂದನೆಂಬದಕ್ಕೆ ಅಪೊಸ್ತಲರು ಬಹು ಬಲವಾಗಿ ಸಾಕ್ಷಿ ಹೇಳುತ್ತಿದ್ದರು. ಬಹಳ ದಯವು ಅವರೆಲ್ಲರ ಮೇಲೆ ಇತ್ತು.",
			"34": "<sup>34</sup> ಅವರಲ್ಲಿ ಕೊರತೆಪಡುವವನು ಒಬ್ಬನೂ ಇರಲಿಲ್ಲ, ಯಾಕಂದರೆ ಹೊಲಮನೆಗಳಿದ್ದವರು ಅವುಗಳನ್ನು ಮಾರಿ",
			"35": "<sup>35</sup> ಬಂದ ಕ್ರಯವನ್ನು ತಂದು ಅಪೊಸ್ತಲರ ಪಾದಗಳ ಬಳಿಯಲ್ಲಿ ಇಡುತ್ತಿದ್ದರು; ಅದನ್ನು ಪ್ರತಿಯೊಬ್ಬನಿಗೆ ಅವನವನ ಅವಶ್ಯದಂತೆ ಹಂಚಿಕೊಡುತ್ತಿದ್ದರು.",
			"36": "<sup>36</sup> ಈ ಪ್ರಕಾರ ಮಾಡಿದವರೊಳಗೆ ಕುಪ್ರದ್ವೀಪದಲ್ಲಿ ಹುಟ್ಟಿದ ಲೇವಿಯನಾಗಿದ್ದ ಯೋಸೇಫನೆಂಬವನು ಒಬ್ಬನು. ಅವನಿಗೆ ಅಪೊಸ್ತಲರು ಬಾರ್ನಬ ಅಂದರೆ ಧೈರ್ಯದಾಯಕ ಎಂದು ಹೆಸರಿಟ್ಟಿದ್ದರು.",
			"37": "<sup>37</sup> ಅವನು ತನಗಿದ್ದ ಭೂಮಿಯನ್ನು ಮಾರಿ ಬಂದ ಹಣವನ್ನು ತಂದು ಅಪೊಸ್ತಲರ ಪಾದಗಳ ಬಳಿಯಲ್ಲಿ ಇಟ್ಟನು."
		},
		"5": {
			"1": "<sup>1</sup> ಆದರೆ ಅನನೀಯನೆಂಬ ಒಬ್ಬ ಮನುಷ್ಯನು ಸಪ್ಫೈರಳೆಂಬ ತನ್ನ ಹೆಂಡತಿಯ ಸಮೇತ ಒಂದು ಭೂಮಿಯನ್ನು ಮಾರಿ",
			"2": "<sup>2</sup> ಹೆಂಡತಿಯ ಸಮ್ಮತಿಯಿಂದ ಅದರ ಕ್ರಯದಲ್ಲಿ ಒಂದು ಭಾಗವನ್ನು ಬಚ್ಚಿಟ್ಟುಕೊಂಡು ಒಂದು ಭಾಗವನ್ನು ತಂದು ಅಪೊಸ್ತಲರ ಪಾದಗಳ ಬಳಿಯಲ್ಲಿ ಇಟ್ಟನು.",
			"3": "<sup>3</sup> ಆಗ ಪೇತ್ರನು - ಅನನೀಯಾ, ಸೈತಾನನು ನಿನ್ನ ಹೃದಯದಲ್ಲಿ ತುಂಬಿಕೊಂಡದ್ದೇನು? ನೀನು ಯಾಕೆ ಆ ಹೊಲದ ಕ್ರಯದಲ್ಲಿ ಕೆಲವನ್ನು ಬಚ್ಚಿಟ್ಟುಕೊಂಡು ಪವಿತ್ರಾತ್ಮನನ್ನು ವಂಚಿಸಬೇಕೆಂದಿದ್ದೀ?",
			"4": "<sup>4</sup> ಆ ಆಸ್ತಿ ಇದ್ದಾಗ ನಿನ್ನದಾಗಿಯೇ ಇತ್ತಲ್ಲವೇ. ಮಾರಿದ ಮೇಲೆಯೂ ಬಂದ ಹಣವು ನಿನ್ನ ಅಧೀನದಲ್ಲೇ ಇತ್ತಲ್ಲವೇ. ನೀನು ಈ ಕಾರ್ಯವನ್ನು ನಿನ್ನ ಹೃದಯದಲ್ಲಿ ಯೋಚಿಸಿಕೊಂಡದ್ದು ಯಾಕೆ? ನೀನು ಸುಳ್ಳಾಡಿದ್ದು ಮನುಷ್ಯರಿಗಲ್ಲ, ದೇವರಿಗೆ ಆಡಿದಿ ಅಂದನು.",
			"5": "<sup>5</sup> ಈ ಮಾತುಗಳನ್ನು ಅನನೀಯನು ಕೇಳಿದ ಕೂಡಲೆ ಬಿದ್ದು ಪ್ರಾಣಬಿಟ್ಟನು; ಕೇಳಿದವರೆಲ್ಲರಿಗೂ ಮಹಾ ಭಯ ಹಿಡಿಯಿತು.",
			"6": "<sup>6</sup> ಆಗ ಯೌವನಸ್ಥರು ಎದ್ದು ಅವನನ್ನು ವಸ್ತ್ರದಲ್ಲಿ ಸುತ್ತಿ ಹೊತ್ತುಕೊಂಡು ಹೋಗಿ ಹೂಣಿಟ್ಟರು.",
			"7": "<sup>7</sup> ಸುಮಾರು ಮೂರು ಘಂಟೆ ಹೊತ್ತಿನ ಮೇಲೆ ಅವನ ಹೆಂಡತಿಯು ನಡೆದ ಸಂಗತಿಯನ್ನು ತಿಳಿಯದೆ ಒಳಗೆ ಬಂದಳು.",
			"8": "<sup>8</sup> ಪೇತ್ರನು ಆಕೆಯನ್ನು ನೋಡಿ - ನೀವು ಆ ಹೊಲವನ್ನು ಮಾರಿದ್ದು ಇಷ್ಟೇ ಹಣಕ್ಕೋ? ನನಗೆ ಹೇಳು ಎಂದು ಕೇಳಲು ಆಕೆಯು - ಹೌದು, ಇಷ್ಟಕ್ಕೇ ಅಂದಳು.",
			"9": "<sup>9</sup> ಆಗ ಪೇತ್ರನು ಆಕೆಗೆ - ನೀವಿಬ್ಬರೂ ಕರ್ತನ ಆತ್ಮನನ್ನು ಪರೀಕ್ಷಿಸುವದಕ್ಕೆ ಯಾಕೆ ಒಡಂಬಟ್ಟಿರಿ? ಅಗೋ, ನಿನ್ನ ಗಂಡನನ್ನು ಹೂಣಿಟ್ಟವರು ಬಂದು ಬಾಗಿಲಿನಲ್ಲಿ ಕಾಲಿಟ್ಟಿದ್ದಾರೆ, ನಿನ್ನನ್ನೂ ಹೊತ್ತುಕೊಂಡು ಹೋಗುವರು ಎಂದು ಹೇಳಿದನು.",
			"10": "<sup>10</sup> ಕೂಡಲೆ ಆಕೆಯು ಅವನ ಪಾದಗಳ ಮುಂದೆ ಬಿದ್ದು ಪ್ರಾಣಬಿಟ್ಟಳು. ಆ ಯೌವನಸ್ಥರು ಒಳಗೆ ಬಂದು ಆಕೆಯು ಸತ್ತಿರುವದನ್ನು ಕಂಡು ಆಕೆಯನ್ನು ಹೊತ್ತುಕೊಂಡು ಹೋಗಿ ಗಂಡನ ಮಗ್ಗುಲಲ್ಲಿ ಹೂಣಿಟ್ಟರು.",
			"11": "<sup>11</sup> ಮತ್ತು ಸರ್ವಸಭೆಗೂ ಈ ಸಂಗತಿಗಳನ್ನು ಕೇಳಿದವರೆಲ್ಲರಿಗೂ ಮಹಾ ಭಯಹಿಡಿಯಿತು.",
			"12": "<sup>12</sup> ಇದಲ್ಲದೆ ಅಪೊಸ್ತಲರ ಕೈಯಿಂದ ಅನೇಕ ಸೂಚಕಕಾರ್ಯಗಳೂ ಅದ್ಭುತಕಾರ್ಯಗಳೂ ಜನರಲ್ಲಿ ನಡೆಯುತ್ತಾ ಇದ್ದವು. ಮತ್ತು ಎಲ್ಲರು ಒಗ್ಗಟ್ಟಾಗಿ ಸೊಲೊಮೋನನ ಮಂಟಪದಲ್ಲಿ ಇರುತ್ತಿದ್ದರು.",
			"13": "<sup>13</sup> ಅವರ ಜೊತೆಯಲ್ಲಿರುವದಕ್ಕೆ ಮಿಕ್ಕಾದವರೊಳಗೆ ಒಬ್ಬರಿಗೂ ಧೈರ್ಯವಿರಲಿಲ್ಲ. ಆದರೂ ಜನರು ಅವರನ್ನು ಬಹಳವಾಗಿ ಕೊಂಡಾಡುತ್ತಿದ್ದರು;",
			"14": "<sup>14</sup> ಮತ್ತು ಇನ್ನು ಎಷ್ಟೋ ಮಂದಿ ಗಂಡಸರೂ ಹೆಂಗಸರೂ ಕರ್ತನಲ್ಲಿ ನಂಬಿಕೆಯಿಡುವವರಾಗಿ ಅವರ ಮಂಡಲಿಯಲ್ಲಿ ಸೇರಿಕೊಳ್ಳುತ್ತಿದ್ದರು.",
			"15": "<sup>15</sup> ಹೀಗಿರುವದರಿಂದ ಜನರು ರೋಗಿಗಳನ್ನು ದೋಲಿಗಳ ಮೇಲೆಯೂ ಹಾಸಿಗೆಗಳ ಮೇಲೆಯೂ ಇಟ್ಟು ಪೇತ್ರನು ಬರುವಾಗ್ಗೆ ಅವನ ನೆರಳು ಇವರಲ್ಲಿ ಯಾವನ ಮೇಲಾದರೂ ಬೀಳಲಿ ಎಂದು ಅವರನ್ನು ಬೀದಿಗೆ ತೆಗೆದುಕೊಂಡು ಬರುತ್ತಿದ್ದರು.",
			"16": "<sup>16</sup> ಯೆರೂಸಲೇಮಿನ ಸುತ್ತಲಿರುವ ಊರುಗಳ ಜನರು ಸಹ ರೋಗಿಗಳನ್ನೂ ದೆವ್ವಗಳ ಕಾಟವಿದ್ದವರನ್ನೂ ಹೊತ್ತುಕೊಂಡು ಗುಂಪು ಗುಂಪಾಗಿ ಬರುತ್ತಿದ್ದರು; ಅವರೆಲ್ಲರಿಗೂ ವಾಸಿಯಾಗುವದು.",
			"17": "<sup>17</sup> ಹೀಗಿರುವಲ್ಲಿ ಮಹಾಯಾಜಕನೂ ಸದ್ದುಕಾಯರ ಮತಕ್ಕೆ ಸೇರಿದವರಾದ ಅವನ ಜೊತೆಯಲ್ಲಿದ್ದವರೆಲ್ಲರೂ ಎದ್ದು",
			"18": "<sup>18</sup> ಮತಾಭಿಮಾನದಿಂದ ತುಂಬಿದವರಾಗಿ ಅಪೊಸ್ತಲರನ್ನು ಹಿಡಿದು ಪಟ್ಟಣದ ಕಾವಲಿನಲ್ಲಿಟ್ಟರು.",
			"19": "<sup>19</sup> ಆದರೆ ಕರ್ತನ ದೂತನು ರಾತ್ರಿಯಲ್ಲಿ ಸೆರೆಮನೆಯ ಬಾಗಿಲುಗಳನ್ನು ತೆರೆದು ಅವರನ್ನು ಹೊರಕ್ಕೆ ಕರೆದುಕೊಂಡು ಬಂದು -",
			"20": "<sup>20</sup> ನೀವು ಹೋಗಿ ದೇವಾಲಯದಲ್ಲಿ ನಿಂತುಕೊಂಡು ಈ ಸಜ್ಜೀವವಿಷಯವಾದ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ಜನರಿಗೆ ಹೇಳಿರಿ ಅಂದನು.",
			"21": "<sup>21</sup> ಅವರು ಈ ಆಜ್ಞೆಯನ್ನು ಹೊಂದಿ ಬೆಳಕುಹರಿಯುವಾಗಲೇ ದೇವಾಲಯದೊಳಗೆ ಹೋಗಿ ಉಪದೇಶ ಮಾಡಿದರು. ಇತ್ತಲಾಗಿ ಮಹಾಯಾಜಕನೂ ಅವನ ಜೊತೆಯಲ್ಲಿದ್ದವರೂ ಬಂದು ಹಿರೀಸಭೆಯನ್ನೂ ಇಸ್ರಾಯೇಲ್ಯರ ಯಜಮಾನರ ಸಭೆಯೆಲ್ಲವನ್ನೂ ಕೂಡಿಸಿ ಅವರನ್ನು ಕರತರುವದಕ್ಕೆ ಸೆರೆಮನೆಗೆ ಕಳುಹಿಸಿದರು.",
			"22": "<sup>22</sup> ಓಲೇಕಾರರು ಹೋಗಿ ಅವರನ್ನು ಸೆರೆಮನೆಯಲ್ಲಿ ಕಾಣದೆ ಹಿಂತಿರಿಗಿ ಬಂದು -",
			"23": "<sup>23</sup> ಕಾವಲಿನ ಮನೆ ಪೂರ್ಣ ಭದ್ರವಾಗಿ ಮುಚ್ಚಿರುವದನ್ನೂ ಕಾವಲುಗಾರರು ಬಾಗಿಲುಗಳಲ್ಲಿ ನಿಂತಿರುವದನ್ನೂ ಕಂಡೆವು; ಆದರೆ ತೆರೆದಾಗ ಒಳಗೆ ಒಬ್ಬರನ್ನೂ ಕಾಣಲಿಲ್ಲ ಎಂದು ತಿಳಿಸಿದರು.",
			"24": "<sup>24</sup> ದೇವಾಲಯದ ಅಧಿಪತಿಯೂ ಮಹಾಯಾಜಕರೂ ಈ ಮಾತುಗಳನ್ನು ಕೇಳಿ ಇದರಿಂದ ಏನಾದೀತೋ ಎಂದು ಕಳವಳಪಟ್ಟರು.",
			"25": "<sup>25</sup> ಹೀಗಿರಲಾಗಿ ಯಾರೋ ಒಬ್ಬನು ಬಂದು - ಅಗೋ, ನೀವು ಸೆರೆಮನೆಯಲ್ಲಿಟ್ಟಿದ್ದ ಆ ಮನುಷ್ಯರು ದೇವಾಲಯದಲ್ಲಿ ನಿಂತುಕೊಂಡು ಜನರಿಗೆ ಉಪದೇಶಮಾಡುತ್ತಿದ್ದಾರೆಂದು ಅವರಿಗೆ ತಿಳಿಸಿದನು.",
			"26": "<sup>26</sup> ಆಗ ಅಧಿಪತಿಯು ಓಲೇಕಾರರ ಸಂಗಡ ಹೋಗಿ ಅವರನ್ನು ಕರೆದುಕೊಂಡು ಬಂದನು; ಆದರೆ ತಮಗೆ ಜನರು ಕಲ್ಲೆಸೆದಾರೆಂದು ಹೆದರಿ ಬಲಾತ್ಕಾರ ಮಾಡಲಿಲ್ಲ.",
			"27": "<sup>27</sup> ಅವರನ್ನು ಕರತಂದು ಹಿರೀಸಭೆಯ ಮುಂದೆ ನಿಲ್ಲಿಸಲು ಮಹಾಯಾಜಕನು ಅವರನ್ನು ವಿಚಾರಣೆ ಮಾಡುತ್ತಾ -",
			"28": "<sup>28</sup> ನೀವು ಈ ಹೆಸರನ್ನು ಎತ್ತಿ ಉಪದೇಶ ಮಾಡಲೇಬಾರದೆಂದು ನಾವು ನಿಮಗೆ ಖಂಡಿತವಾಗಿ ಅಪ್ಪಣೆಕೊಟ್ಟೆವಲ್ಲಾ; ಆದರೂ ನೀವು ಯೆರೂಸಲೇಮನ್ನು ನಿಮ್ಮ ಉಪದೇಶದಿಂದ ತುಂಬಿಸಿದಿರಿ, ಮತ್ತು ಆ ಮನುಷ್ಯನನ್ನು ಕೊಲ್ಲಿಸಿದ್ದಕ್ಕೆ ನಮ್ಮನ್ನು ಹೊಣೆಮಾಡಬೇಕೆಂದಿದ್ದೀರಿ ಎಂದು ಹೇಳಿದ್ದಕ್ಕೆ",
			"29": "<sup>29</sup> ಪೇತ್ರನೂ ಉಳಿದ ಅಪೊಸ್ತಲರೂ - ಮನುಷ್ಯರಿಗಿಂತಲೂ ದೇವರಿಗೆ ಹೆಚ್ಚಾಗಿ ವಿಧೇಯರಾಗಿರಬೇಕಲ್ಲಾ.",
			"30": "<sup>30</sup> ನೀವು ಮರದ ಕಂಬಕ್ಕೆ ತೂಗಹಾಕಿ ಕೊಂದ ಯೇಸುವನ್ನು ನಮ್ಮ ಪಿತೃಗಳ ದೇವರು ಎಬ್ಬಿಸಿದನು.",
			"31": "<sup>31</sup> ದೇವರು ಆತನನ್ನೇ ಇಸ್ರಾಯೇಲ್ ಜನರಿಗೆ ಮಾನಸಾಂತರವನ್ನೂ ಪಾಪಪರಿಹಾರವನ್ನೂ ದಯಪಾಲಿಸುವದಕ್ಕಾಗಿ ನಾಯಕನೆಂತಲೂ ರಕ್ಷಕನೆಂತಲೂ ತನ್ನ ಬಲಗೈಯಿಂದ ಉನ್ನತಸ್ಥಾನಕ್ಕೆ ಏರಿಸಿದನು.",
			"32": "<sup>32</sup> ಈ ಕಾರ್ಯಗಳಿಗೆ ನಾವು ಸಾಕ್ಷಿ; ದೇವರು ತನಗೆ ವಿಧೇಯರಾಗಿರುವವರಿಗೆ ದಯಪಾಲಿಸಿರುವ ಪವಿತ್ರಾತ್ಮನೂ ಸಾಕ್ಷಿ ಎಂದು ಹೇಳಿದರು.",
			"33": "<sup>33</sup> ಸಭಿಕರು ಈ ಮಾತನ್ನು ಕೇಳಿ ರೌದ್ರವಾಗಿ ಅವರನ್ನು ಕೊಲ್ಲಿಸಬೇಕೆಂದು ಯೋಚಿಸಿದರು;",
			"34": "<sup>34</sup> ಆದರೆ ಎಲ್ಲಾ ಜನರಿಂದ ಮಾನಹೊಂದಿದ ನ್ಯಾಯಶಾಸ್ತ್ರಿಯಾದ ಗಮಲಿಯೇಲನೆಂಬ ಒಬ್ಬ ಫರಿಸಾಯನು ಹಿರೀಸಭೆಯಲ್ಲಿ ಎದ್ದುನಿಂತು ಈ ಮನುಷ್ಯರನ್ನು ಸ್ವಲ್ಪ ಹೊತ್ತು ಹೊರಗೆ ಕಳುಹಿಸಬೇಕೆಂದು ಅಪ್ಪಣೆಕೊಟ್ಟು ಸಭೆಯವರಿಗೆ -",
			"35": "<sup>35</sup> ಇಸ್ರಾಯೇಲ್ ಜನರೇ, ನೀವು ಈ ಮನುಷ್ಯರ ವಿಷಯದಲ್ಲಿ ಮಾಡಬೇಕೆಂದಿರುವದನ್ನು ಕುರಿತು ಎಚ್ಚರಿಕೆಯುಳ್ಳವರಾಗಿರ್ರಿ.",
			"36": "<sup>36</sup> ಇದಕ್ಕಿಂತ ಮುಂಚೆ ಥೈದನು ಎದ್ದು ತಾನು ದೊಡ್ಡ ಮನುಷ್ಯನೆಂದು ಹೇಳಿಕೊಂಡನು. ಅವನ ಪಕ್ಷಕ್ಕೆ ಸುಮಾರು ನಾನೂರು ಜನರು ಸೇರಿಕೊಂಡರು. ಅವನು ಕೊಲ್ಲಲ್ಪಟ್ಟನು, ಮತ್ತು ಅವನನ್ನು ನಂಬಿದವರೆಲ್ಲರು ಚದರಿಸಲ್ಪಟ್ಟು ಇಲ್ಲವಾದರು.",
			"37": "<sup>37</sup> ಅವನ ತರುವಾಯ ಖಾನೇಷುಮಾರಿಯ ಕಾಲದಲ್ಲಿ ಗಲಿಲಾಯದ ಯೂದನು ಎದ್ದು ಜನರನ್ನು ತನ್ನ ಸಹಿತ ತಿರುಗಿಬೀಳುವದಕ್ಕೆ ಪ್ರೇರೇಪಿಸಿದನು. ಅವನೂ ಹಾಳಾದನು; ಮತ್ತು ಅವನನ್ನು ನಂಬಿದವರೆಲ್ಲರು ಚದರಿಹೋದರು.",
			"38": "<sup>38</sup> ಹೀಗಿರುವದರಿಂದ ನಾನು ನಿಮಗೆ ಹೇಳುವದೇನಂದರೆ ನೀವು ಆ ಮನುಷ್ಯರ ಗೊಡವೆಗೆ ಹೋಗಬೇಡಿರಿ, ಅವರನ್ನು ಬಿಡಿರಿ; ಯಾಕಂದರೆ ಈ ಯೋಚನೆಯು ಅಥವಾ ಈ ಕೆಲಸವು ಮನುಷ್ಯರಿಂದಾಗಿದ್ದರೆ ತಾನೇ ಕೆಡುವದು;",
			"39": "<sup>39</sup> ಅದು ದೇವರಿಂದಾಗಿದ್ದರೆ ಅದನ್ನು ಕೆಡಿಸುವದಕ್ಕೆ ನಿಮ್ಮಿಂದ ಆಗುವದಿಲ್ಲ. ನೀವು ಒಂದು ವೇಳೆ ದೇವರ ಮೇಲೆ ಯುದ್ಧಮಾಡುವವರಾಗಿ ಕಾಣಿಸಿಕೊಂಡೀರಿ ಎಂದು ಹೇಳಿದನು.",
			"40": "<sup>40</sup> ಅವರು ಅವನ ಮಾತಿಗೆ ಒಪ್ಪಿ ಅಪೊಸ್ತಲರನ್ನು ಕರೆಸಿ ಹೊಡಿಸಿ ಯೇಸುವಿನ ಹೆಸರನ್ನು ಹೇಳಿ ಮಾತಾಡಬಾರದೆಂದು ಅಪ್ಪಣೆಕೊಟ್ಟು ಅವರನ್ನು ಬಿಟ್ಟು ಬಿಟ್ಟರು.",
			"41": "<sup>41</sup> ಅಪೊಸ್ತಲರು ತಾವು ಆ ಹೆಸರಿನ ನಿಮಿತ್ತವಾಗಿ ಅವಮಾನ ಪಡುವದಕ್ಕೆ ಯೋಗ್ಯರೆನಿಸಿಕೊಂಡೆವೆಂದು ಸಂತೋಷಿಸುತ್ತಾ",
			"42": "<sup>42</sup> ಹಿರೀಸಭೆಯ ಎದುರಿನಿಂದ ಹೊರಟುಹೋಗಿ ಪ್ರತಿದಿನ ಎಡೆಬಿಡದೆ ದೇವಾಲಯದಲ್ಲಿಯೂ ಮನೆಮನೆಯಲ್ಲಿಯೂ ಉಪದೇಶಮಾಡುತ್ತಾ ಕ್ರಿಸ್ತನಾದ ಯೇಸುವಿನ ವಿಷಯವಾದ ಶುಭವರ್ತಮಾನವನ್ನು ಸಾರುತ್ತಾ ಇದ್ದರು."
		},
		"6": {
			"1": "<sup>1</sup> ಆ ದಿವಸಗಳಲ್ಲಿ ಶಿಷ್ಯರು ಹೆಚ್ಚುತ್ತಾ ಬರಲಾಗಿ ಅವರೊಳಗೆ ಗ್ರೀಕ್‍ಭಾಷೆಯವರು ಇಬ್ರಿಯ ಭಾಷೆಯವರ ಮೇಲೆ - ದಿನದಿನದ ಉಪಚಾರದಲ್ಲಿ ನಮ್ಮ ವಿಧವೆಯರನ್ನು ಸರಿಯಾಗಿ ಪರಾಂಬರಿಸುವದಿಲ್ಲವೆಂದು ಗುಣುಗುಟ್ಟಿದರು.",
			"2": "<sup>2</sup> ಆಗ ಹನ್ನೆರಡು ಮಂದಿ ಅಪೊಸ್ತಲರು ಶಿಷ್ಯಮಂಡಲಿಯನ್ನು ಕೂಡಿಸಿ - ನಾವು ದೇವರ ವಾಕ್ಯೋಪದೇಶವನ್ನು ಬಿಟ್ಟು ಉಪಚಾರಮಾಡುತ್ತಿರುವದು ತಕ್ಕದ್ದಲ್ಲವಲ್ಲಾ;",
			"3": "<sup>3</sup> ಆದದರಿಂದ ಸಹೋದರರೇ, ಸಂಭಾವಿತರೂ ಪವಿತ್ರಾತ್ಮಭರಿತರೂ ಜ್ಞಾನಸಂಪನ್ನರೂ ಆಗಿರುವ ಏಳುಮಂದಿಯನ್ನು ನಿಮ್ಮೊಳಗಿಂದ ನೋಡಿ ಆರಿಸಿಕೊಳ್ಳಿರಿ; ಅವರನ್ನು ಈ ಕೆಲಸದ ಮೇಲೆ ನೇಮಿಸುವೆವು.",
			"4": "<sup>4</sup> ನಾವಾದರೋ ಪ್ರಾರ್ಥನೆಯನ್ನೂ ವಾಕ್ಯೋಪದೇಶವನ್ನೂ ಮಾಡುವದರಲ್ಲಿ ನಿರತರಾಗಿರುವೆವು ಎಂದು ಹೇಳಿದರು.",
			"5": "<sup>5</sup> ಈ ಮಾತು ಸರ್ವಮಂಡಲಿಗೆ ಒಳ್ಳೇದೆಂದು ತೋಚಿತು. ಅವರು ಪವಿತ್ರಾತ್ಮಭರಿತನೂ ನಂಬಿಕೆಯಿಂದ ತುಂಬಿದವನೂ ಆದ ಸ್ತೆಫನನನ್ನೂ, ಫಿಲಿಪ್ಪ, ಪ್ರೊಖೋರ, ನಿಕನೋರ, ತಿಮೋನ, ಪರ್ಮೇನ, ಯೆಹೂದ್ಯ ಮತಾವಲಂಬಿಯಾದ ಅಂತಿಯೋಕ್ಯದ ನಿಕೊಲಾಯ ಎಂಬವರನ್ನೂ ಆರಿಸಿಕೊಂಡು ಅಪೊಸ್ತಲರ ಮುಂದೆ ನಿಲ್ಲಿಸಿದರು.",
			"6": "<sup>6</sup> ಅಪೊಸ್ತಲರು ಪ್ರಾರ್ಥನೆ ಮಾಡಿ ಅವರ ಮೇಲೆ ಕೈಗಳನ್ನಿಟ್ಟು ನೇಮಿಸಿದರು.",
			"7": "<sup>7</sup> ಮತ್ತು ದೇವರ ವಾಕ್ಯವು ಪ್ರಬಲವಾಯಿತು. ಶಿಷ್ಯರ ಸಂಖ್ಯೆಯು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಬಹಳವಾಗಿ ಹೆಚ್ಚುತ್ತಾ ಬಂತು. ಯಾಜಕರಲ್ಲಿಯೂ ಬಹುಜನರು ಕ್ರಿಸ್ತನಂಬಿಕೆಗೆ ಒಳಗಾಗುತ್ತಾ ಇದ್ದರು.",
			"8": "<sup>8</sup> ಸ್ತೆಫನನು ದೇವರ ಕೃಪೆಯಿಂದಲೂ ಬಲದಿಂದಲೂ ತುಂಬಿದವನಾಗಿ ಜನರಲ್ಲಿ ಮಹಾ ಅದ್ಭುತಕಾರ್ಯಗಳನ್ನೂ ಸೂಚಕಕಾರ್ಯಗಳನ್ನೂ ಮಾಡುತ್ತಾ ಇದ್ದನು.",
			"9": "<sup>9</sup> ಆದರೆ ಲಿಬೆರ್ತೀನರೆಂಬವರ ಸಮಾಜದವರಲ್ಲಿಯೂ ಕುರೇನ್ಯದವರ ಮತ್ತು ಅಲೆಕ್ಸಾಂದ್ರಿಯದವರ ಸಮಾಜದವರಲ್ಲಿಯೂ ಕಿಲಿಕ್ಯ ಆಸ್ಯ ಸೀಮೆಗಳಿಂದ ಬಂದವರ ಸಮಾಜದವರಲ್ಲಿಯೂ ಕೆಲವರು ಎದ್ದು ಸ್ತೆಫನನ ಸಂಗಡ ತರ್ಕಮಾಡುತ್ತಾ ಇರಲು",
			"10": "<sup>10</sup> ಅವನ ಮಾತಿನಲ್ಲಿ ಕಂಡುಬಂದ ಜ್ಞಾನವನ್ನೂ ಪವಿತ್ರಾತ್ಮ ಶಕ್ತಿಯನ್ನೂ ಎದುರಿಸಲಾರದೆ ಹೋದರು.",
			"11": "<sup>11</sup> ಆಗ ಅವರು ಕೆಲವು ಮನುಷ್ಯರಿಗೆ - ಇವನು ಮೋಶೆಗೆ ವಿರೋಧವಾಗಿಯೂ ದೇವರಿಗೆ ವಿರೋಧವಾಗಿಯೂ ದೂಷಣೆಯ ಮಾತುಗಳನ್ನಾಡುವದನ್ನು ಕೇಳಿದೆವೆಂದು ಹೇಳಿರಿ ಎಂಬದಾಗಿ ಬೋಧಿಸಿ",
			"12": "<sup>12</sup> ಜನರನ್ನೂ ಹಿರಿಯರನ್ನೂ ಶಾಸ್ತ್ರಿಗಳನ್ನೂ ರೇಗಿಸಿದರು; ಮತ್ತು ಬಂದು ಅವನನ್ನು ಹಿಡಿದು ಹಿರೀಸಭೆಗೆ ತೆಗೆದುಕೊಂಡು ಹೋಗಿ",
			"13": "<sup>13</sup> ಸುಳ್ಳುಸಾಕ್ಷಿಗಳನ್ನು ಕರತಂದು ನಿಲ್ಲಿಸಿದರು. ಆ ಸಾಕ್ಷಿಗಳು - ಈ ಮನುಷ್ಯನು ಈ ಪರಿಶುದ್ಧವಾದ ಸ್ಥಳಕ್ಕೆ ವಿರೋಧವಾಗಿಯೂ ಧರ್ಮಶಾಸ್ತ್ರಕ್ಕೆ ವಿರೋಧವಾಗಿಯೂ ಮಾತಾಡುವದನ್ನು ಬಿಡುವದೇ ಇಲ್ಲ.",
			"14": "<sup>14</sup> ಆ ನಜರೇತಿನ ಯೇಸು ಈ ದೇವಾಲಯವನ್ನು ಕೆಡವಿ ಮೋಶೆ ನಮಗೆ ನೇಮಿಸಿರುವ ಆಚಾರಗಳನ್ನು ಬೇರೆ ಮಾಡುವನೆಂಬದಾಗಿ ಇವನು ಹೇಳುವದನ್ನು ನಾವು ಕೇಳಿದ್ದೇವೆ ಅಂದರು.",
			"15": "<sup>15</sup> ಆಗ ಹಿರೀಸಭೆಯಲ್ಲಿ ಕೂತಿದ್ದವರೆಲ್ಲರು ಅವನನ್ನು ದೃಷ್ಟಿಸಿ ನೋಡಿ ಅವನ ಮುಖವು ದೇವದೂತನ ಮುಖದಂತೆ ಇರುವದನ್ನು ಕಂಡರು."
		},
		"7": {
			"1": "<sup>1</sup> ಆಗ ಮಹಾಯಾಜಕನು - ಈ ಮಾತುಗಳು ಹೌದೋ ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ ಅವನು - ಸಹೋದರರೇ, ತಂದೆಗಳೇ, ಕೇಳಿರಿ -",
			"2": "<sup>2</sup> ನಮ್ಮ ಮೂಲಪುರುಷನಾದ ಅಬ್ರಹಾಮನು ಖಾರಾನಿನಲ್ಲಿ ವಾಸಮಾಡಿದ್ದಕ್ಕಿಂತ ಮುಂಚೆ ಮೆಸೊಪೊತಾಮ್ಯದಲ್ಲಿದ್ದಾಗ ಪ್ರಭಾವ ಸ್ವರೂಪನಾದ ದೇವರು",
			"3": "<sup>3</sup> ಅವನಿಗೆ ಕಾಣಿಸಿಕೊಂಡು - ನಿನ್ನ ಸ್ವದೇಶವನ್ನೂ ನಿನ್ನ ಬಂಧುಬಳಗವನ್ನೂ ಬಿಟ್ಟು ನಾನು ತೋರಿಸುವ ದೇಶಕ್ಕೆ ಹೊರಟುಹೋಗು ಎಂದು ಹೇಳಿದನು.",
			"4": "<sup>4</sup> ಆಗ ಅವನು ಕಸ್ದೀಯರ ದೇಶದಿಂದ ಹೊರಟುಹೋಗಿ ಖಾರಾನಿನಲ್ಲಿ ವಾಸವಾಗಿದ್ದನು. ಅವನ ತಂದೆ ಸತ್ತಮೇಲೆ ದೇವರು ಅವನನ್ನು ಅಲ್ಲಿಂದ ನೀವು ಈಗ ವಾಸವಾಗಿರುವ ಈ ದೇಶಕ್ಕೆ ಬರಮಾಡಿದನು.",
			"5": "<sup>5</sup> ಇದರಲ್ಲಿ ಅವನಿಗೆ ಕಾಲಿಡುವಷ್ಟು ಭೂಮಿಯನ್ನು ಸ್ವಾಸ್ತ್ಯವಾಗಿ ಕೊಡದೆ ಅವನಿಗೆ ಮಕ್ಕಳು ಇನ್ನೂ ಇಲ್ಲದಿರುವಾಗ - ಈ ದೇಶವನ್ನು ನಿನಗೂ ನಿನ್ನ ಹಿಂದೆ ಬರುವ ನಿನ್ನ ಸಂತತಿಗೂ ಸ್ವಾಸ್ತ್ಯವಾಗಿ ಕೊಡುವೆನು ಎಂದು ಅವನಿಗೆ ವಾಗ್ದಾನ ಮಾಡಿದನು.",
			"6": "<sup>6</sup> ಇದಲ್ಲದೆ ದೇವರು ಹೇಳಿದ್ದೇನಂದರೆ - ನಿನ್ನ ಸಂತಾನದವರು ಅನ್ಯದೇಶದಲ್ಲಿ ಪ್ರವಾಸಿಗಳಾಗಿರುವರು. ಆ ಅನ್ಯದೇಶದವರು ಅವರನ್ನು ದಾಸರನ್ನಾಗಿ ಮಾಡಿಕೊಂಡು ನಾನೂರು ವರುಷಗಳ ತನಕ ಕ್ರೂರವಾಗಿ ನಡಿಸುವರು.",
			"7": "<sup>7</sup> ಮತ್ತು ನಿನ್ನ ಸಂತಾನದವರು ದಾಸರಾಗಿ ಸೇವಿಸುವ ಅನ್ಯಜನರಿಗೆ ನಾನೇ ನ್ಯಾಯತೀರಿಸುವೆನು. ಆಮೇಲೆ ಅವರು ಹೊರಟುಬಂದು ಈ ಸ್ಥಳದಲ್ಲಿ ನನ್ನನ್ನು ಆರಾಧಿಸುವರು ಎಂಬದೇ.",
			"8": "<sup>8</sup> ಇದಲ್ಲದೆ ದೇವರು ಅವನ ಸಂಗಡ ಒಡಂಬಡಿಕೆಯನ್ನು ಮಾಡಿಕೊಂಡು ಅದಕ್ಕೆ ಗುರುತಾಗಿ ಸುನ್ನತಿಯನ್ನು ನೇಮಿಸಿದನು. ಅದಕ್ಕನುಸಾರವಾಗಿ ಅಬ್ರಹಾಮನು ಇಸಾಕನನ್ನು ಪಡೆದು ಎಂಟನೆಯ ದಿವಸದಲ್ಲಿ ಅವನಿಗೆ ಸುನ್ನತಿಮಾಡಿದನು. ಮುಂದೆ ಇಸಾಕನು ಯಾಕೋಬನನ್ನು ಪಡೆದನು, ಯಾಕೋಬನು ಹನ್ನೆರಡು ಮಂದಿ ಮೂಲಪಿತೃಗಳನ್ನು ಪಡೆದನು.",
			"9": "<sup>9</sup> ಮೂಲಪಿತೃಗಳು ಹೊಟ್ಟೇಕಿಚ್ಚಿನಿಂದ ಯೋಸೇಫನನ್ನು ಐಗುಪ್ತದೇಶಕ್ಕೆ ಮಾರಿಬಿಟ್ಟರು.",
			"10": "<sup>10</sup> ಅಲ್ಲಿ ದೇವರು ಅವನ ಸಂಗಡ ಇದ್ದು ಅವನಿಗೆ ಬಂದ ಎಲ್ಲಾ ಸಂಕಟಗಳಿಂದ ಅವನನ್ನು ಬಿಡಿಸಿ ಐಗುಪ್ತದೇಶದ ಅರಸನಾದ ಫರೋಹನ ಸಮ್ಮುಖದಲ್ಲಿ ದಯಾಪಾತ್ರನೂ ಜ್ಞಾನವುಳ್ಳವನೂ ಆಗಿರುವಂತೆ ಅನುಗ್ರಹಿಸಿದನು. ಫರೋಹನು ಅವನನ್ನು ಐಗುಪ್ತದೇಶದ ಮೇಲೆಯೂ ತನ್ನ ಎಲ್ಲಾ ಮನೆಯ ಮೇಲೆಯೂ ಅಧಿಕಾರಿಯಾಗಿ ಇಟ್ಟನು.",
			"11": "<sup>11</sup> ಆಗ ಐಗುಪ್ತ ಕಾನಾನ್‍ದೇಶಗಳಲ್ಲಿ ಎಲ್ಲೆಲ್ಲಿಯೂ ಬರ ಬಂದು ದೊಡ್ಡ ಸಂಕಟವಾಯಿತು; ನಮ್ಮ ಪಿತೃಗಳಿಗೆ ಕಾಳುಕಡ್ಡಿ ಸಿಕ್ಕಲಿಲ್ಲ.",
			"12": "<sup>12</sup> ಆದರೆ ಐಗುಪ್ತದೇಶದಲ್ಲಿ ದವಸಧಾನ್ಯ ಉಂಟೆಂಬದನ್ನು ಯಾಕೋಬನು ಕೇಳಿ ನಮ್ಮ ಪಿತೃಗಳನ್ನು ಮೊದಲು ಒಂದು ಸಾರಿ ಅಲ್ಲಿಗೆ ಕಳುಹಿಸಿದನು.",
			"13": "<sup>13</sup> ಅವರು ಎರಡನೆಯ ಸಾರಿ ಬಂದಾಗ ಯೋಸೇಫನು ಅಣ್ಣತಮ್ಮಂದಿರಿಗೆ ತನ್ನ ಗುರುತು ಸಿಕ್ಕುವಂತೆ ಮಾಡಿದನು. ಮತ್ತು ಯೋಸೇಫನ ವಂಶವು ಫರೋಹನಿಗೆ ತಿಳಿಯ ಬಂತು.",
			"14": "<sup>14</sup> ಆಮೇಲೆ ಯೋಸೇಫನು ತನ್ನ ತಂದೆಯಾದ ಯಾಕೋಬನನ್ನೂ ತನ್ನ ಎಲ್ಲಾ ಬಂಧುಬಳಗವನ್ನೂ ಒಟ್ಟಿಗೆ ಎಪ್ಪತ್ತೈದು ಮಂದಿಯನ್ನು ಕರೆಯಿಸಿಕೊಳ್ಳಲಾಗಿ ಯಾಕೋಬನು ಐಗುಪ್ತದೇಶಕ್ಕೆ ಇಳಿದುಹೋದನು.",
			"15": "<sup>15</sup> ಅಲ್ಲಿ ಅವನೂ ನಮ್ಮ ಪಿತೃಗಳೂ ತೀರಿಹೋದರು.",
			"16": "<sup>16</sup> ಅವರನ್ನು ಶೇಕೆಮಿಗೆ ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ಅಲ್ಲಿ ಹಮೋರನ ಮಕ್ಕಳಿಂದ ಅಬ್ರಹಾಮನು ಬೆಳ್ಳಿಕೊಟ್ಟು ಕೊಂಡುಕೊಂಡಿದ್ದ ಸಮಾಧಿಯೊಳಗೆ ಇಟ್ಟರು.",
			"17": "<sup>17</sup> ಆದರೆ ದೇವರು ಅಬ್ರಹಾಮನಿಗೆ ವಾಗ್ದಾನ ಮಾಡಿದ್ದ ಕಾಲವು ಹತ್ತರಕ್ಕೆ ಬರುತ್ತಿರುವಾಗ ನಮ್ಮ ಜನರು ಐಗುಪ್ತದೇಶದಲ್ಲಿ ಹೆಚ್ಚಿ ಬಹಳವಾದರು.",
			"18": "<sup>18</sup> ಕಡೆಗೆ ಯೋಸೇಫನನ್ನು ಅರಿಯದ ಬೇರೊಬ್ಬ ಅರಸನು ಆಳಿಕೆಗೆ ಬಂದನು.",
			"19": "<sup>19</sup> ಈ ಅರಸನು ನಮ್ಮ ಜನರಲ್ಲಿ ಕುಯುಕ್ತಿ ಮಾಡಿ ಅವರ ಕೂಸುಗಳನ್ನು ಜೀವದಿಂದುಳಿಸಬಾರದೆಂದು ಹೊರಗೆ ಹಾಕಿಸಿ ನಮ್ಮ ಪಿತೃಗಳನ್ನು ಕ್ರೂರವಾಗಿ ನಡಿಸಿದನು.",
			"20": "<sup>20</sup> ಆ ಸಮಯದಲ್ಲೇ ಮೋಶೆಯು ಹುಟ್ಟಿದನು. ಅವನು ದಿವ್ಯಸುಂದರನಾಗಿದ್ದನು; ಮತ್ತು ತಂದೆಯ ಮನೆಯಲ್ಲಿ ಮೂರು ತಿಂಗಳು ಸಾಕಲ್ಪಟ್ಟನು.",
			"21": "<sup>21</sup> ಆಮೇಲೆ ಅವನನ್ನು ಹೊರಗೆ ಹಾಕಿದಾಗ ಫರೋಹನ ಮಗಳು ಅವನನ್ನು ಎತ್ತಿಕೊಂಡು ಹೋಗಿ ತನ್ನ ಮಗನಾಗಿ ಸಾಕಿದಳು.",
			"22": "<sup>22</sup> ಮೋಶೆಯು ಐಗುಪ್ತದೇಶದವರ ಸರ್ವವಿದ್ಯೆಗಳಲ್ಲಿಯೂ ಉಪದೇಶಹೊಂದಿ ಮಾತುಗಳಲ್ಲಿಯೂ ಕಾರ್ಯಗಳಲ್ಲಿಯೂ ಸಮರ್ಥನಾದನು.",
			"23": "<sup>23</sup> ಅವನಿಗೆ ನಾಲ್ವತ್ತು ವರುಷ ವಯಸ್ಸು ತುಂಬುತ್ತಾ ಇರಲು ಇಸ್ರಾಯೇಲ್ ವಂಶಸ್ಥರಾದ ತನ್ನ ಸಹೋದರರನ್ನು ಹೋಗಿನೋಡಬೇಕೆಂಬ ಆಶೆಯು ಅವನ ಹೃದಯದಲ್ಲಿ ಹುಟ್ಟಿತು.",
			"24": "<sup>24</sup> ಅವರಲ್ಲಿ ಒಬ್ಬನಿಗೆ ಅನ್ಯಾಯವಾಗುವದನ್ನು ಅವನು ನೋಡಿ ಅವನಿಗೆ ಆಶ್ರಯಕೊಟ್ಟು ಪೀಡಿಸುವ ಐಗುಪ್ತ್ಯನನ್ನು ಹೊಡೆದುಹಾಕಿ ಮುಯ್ಯಿಗೆ ಮುಯ್ಯಿ ತೀರಿಸಿದನು.",
			"25": "<sup>25</sup> ದೇವರು ತನ್ನ ಕೈಯಿಂದ ಬಿಡುಗಡೆಯನ್ನು ಉಂಟುಮಾಡುತ್ತಾನೆಂಬದು ತನ್ನ ಸಹೋದರರಿಗೆ ತಿಳಿದುಬರುವದೆಂದು ಭಾವಿಸಿದನು; ಆದರೆ ಅವರು ಹಾಗೆ ತಿಳುಕೊಳ್ಳಲಿಲ್ಲ.",
			"26": "<sup>26</sup> ಮರುದಿನ ತಾವು ತಾವೇ ಹೊಡೆದಾಡಿಕೊಳ್ಳುತ್ತಿರುವಾಗ ಅವನು ಎದುರಾಗಿ ಬಂದು - ಜನರೇ, ನೀವು ಸಹೋದರರಲ್ಲವೇ? ನೀವೇಕೆ ಒಬ್ಬರಿಗೊಬ್ಬರು ಅನ್ಯಾಯಮಾಡುತ್ತೀರಿ ಎಂದು ಹೇಳಿ ಅವರನ್ನು ಸಮಾಧಾನಪಡಿಸಬೇಕೆಂದಿದ್ದನು.",
			"27": "<sup>27</sup> ಆದರೆ ತನ್ನ ಸ್ವಕುಲದವನಿಗೆ ಅನ್ಯಾಯಮಾಡುತ್ತಿದ್ದವನು - ನಿನ್ನನ್ನು ನಮ್ಮ ಮೇಲೆ ಅಧಿಕಾರಿಯನ್ನಾಗಿಯೂ ನ್ಯಾಯಾಧಿಪತಿಯನ್ನಾಗಿಯೂ ಇಟ್ಟವರು ಯಾರು?",
			"28": "<sup>28</sup> ನಿನ್ನೆ ಆ ಐಗುಪ್ತ್ಯನನ್ನು ಕೊಂದಂತೆ ನನ್ನನ್ನೂ ಕೊಲ್ಲಬೇಕೆಂದಿದ್ದೀಯೋ ಎಂದು ಅವನನ್ನು ನೂಕಿಬಿಟ್ಟನು.",
			"29": "<sup>29</sup> ಈ ಮಾತನ್ನು ಕೇಳಿ ಮೋಶೆಯು ಓಡಿಹೋಗಿ ಮಿದ್ಯಾನ್ ದೇಶದಲ್ಲಿ ಪ್ರವಾಸಿಯಾದನು. ಅಲ್ಲಿ ಇಬ್ಬರು ಗಂಡುಮಕ್ಕಳನ್ನು ಪಡೆದನು.",
			"30": "<sup>30</sup> ನಾಲ್ವತ್ತು ವರುಷ ತುಂಬಿದ ಮೇಲೆ ದೇವದೂತನು ಸೀನಾಯಿಬೆಟ್ಟದ ಅಡವಿಯಲ್ಲಿ ಮುಳ್ಳಿನ ಪೊದೆಯೊಳಗೆ ಉರಿಯುವ ಬೆಂಕಿಯಲ್ಲಿ ಅವನಿಗೆ ಕಾಣಿಸಿಕೊಂಡನು.",
			"31": "<sup>31</sup> ಮೋಶೆಯು ಅದನ್ನು ಕಂಡು ಆ ನೋಟಕ್ಕೆ ಆಶ್ಚರ್ಯಪಟ್ಟು ಚೆನ್ನಾಗಿ ನೋಡಬೇಕೆಂದು ಹತ್ತಿರ ಬರಲು -",
			"32": "<sup>32</sup> ನಾನು ನಿನ್ನ ಪಿತೃಗಳ ದೇವರು, ಅಬ್ರಹಾಮ ಇಸಾಕ ಯಾಕೋಬರ ದೇವರು ಎಂದು ಕರ್ತನ ಶಬ್ದವುಂಟಾಯಿತು. ಆಗ ಮೋಶೆಯು ನಡುಗುತ್ತಾ ಸ್ಥಿರವಾಗಿ ನೋಡುವದಕ್ಕೆ ಧೈರ್ಯವಿಲ್ಲದವನಾದನು.",
			"33": "<sup>33</sup> ಮತ್ತು ಕರ್ತನು ಅವನಿಗೆ - ನಿನ್ನ ಕಾಲಿನ ಕೆರಗಳನ್ನು ತೆಗೆ; ನೀನು ನಿಂತಿರುವ ಸ್ಥಳವು ಪರಿಶುದ್ಧ ಭೂಮಿ.",
			"34": "<sup>34</sup> ಐಗುಪ್ತದೇಶದಲ್ಲಿರುವ ನನ್ನ ಜನರ ದುರವಸ್ಥೆಯನ್ನು ನೋಡೇ ನೋಡಿದ್ದೇನೆ; ಅವರ ಮೊರೆಯನ್ನು ಕೇಳಿ ಅವರನ್ನು ಬಿಡಿಸುವದಕ್ಕೆ ಇಳಿದುಬಂದಿದ್ದೇನೆ; ಈಗ ನಿನ್ನನ್ನು ಐಗುಪ್ತದೇಶಕ್ಕೆ ಕಳುಹಿಸುತ್ತೇನೆ, ಬಾ, ಎಂದು ಹೇಳಿದನು.",
			"35": "<sup>35</sup> ಅವರು ಯಾವ ಮೋಶೆಯನ್ನು - ನಿನ್ನನ್ನು ಅಧಿಕಾರಿಯನ್ನಾಗಿಯೂ ನ್ಯಾಯಾಧಿಪತಿಯನ್ನಾಗಿಯೂ ಇಟ್ಟವರು ಯಾರೆಂದು ಹೇಳಿ ಬೇಡವೆಂದರೋ ಅವನನ್ನೇ ದೇವರು ಪೊದೆಯಲ್ಲಿ ಕಾಣಿಸಿಕೊಂಡ ದೂತನ ಕೈಯಿಂದ ಅಧಿಕಾರಿಯನ್ನಾಗಿಯೂ ಬಿಡುಗಡೆಮಾಡುವವನನ್ನಾಗಿಯೂ ನೇಮಿಸಿದನು.",
			"36": "<sup>36</sup> ಅವನೇ ಐಗುಪ್ತದೇಶದಲ್ಲಿಯೂ ಕೆಂಪು ಸಮುದ್ರದಲ್ಲಿಯೂ ನಾಲ್ವತ್ತು ವರುಷ ಅಡವಿಯಲ್ಲಿಯೂ ಅದ್ಭುತಕಾರ್ಯಗಳನ್ನೂ ಸೂಚಕಕಾರ್ಯಗಳನ್ನೂ ಮಾಡುತ್ತಾ ಅವರನ್ನು ನಡಿಸಿಕೊಂಡು ಬಂದವನು.",
			"37": "<sup>37</sup> ಆ ಮೋಶೆಯೇ - ದೇವರು ನನ್ನನ್ನು ಏರ್ಪಡಿಸಿದಂತೆ ನಿಮ್ಮ ಸಹೋದರರಲ್ಲಿ ಮತ್ತೊಬ್ಬ ಪ್ರವಾದಿಯನ್ನು ನಿಮಗೆ ಏರ್ಪಡಿಸಿಕೊಡುವನು ಎಂದು ಇಸ್ರಾಯೇಲ್ ಜನರಿಗೆ ಹೇಳಿದವನು.",
			"38": "<sup>38</sup> ಅವನೇ ಸೀನಾಯಿಬೆಟ್ಟದಲ್ಲಿ ತನ್ನೊಡನೆ ಮಾತಾಡಿದ ದೂತನಿಗೂ ನಮ್ಮ ಪಿತೃಗಳಿಗೂ ಅಡವಿಯಲ್ಲಿದ್ದ ಸಭೆಯೊಳಗೆ ಮಧ್ಯಸ್ಥನಾಗಿದ್ದು ಜೀವಕರವಾದ ದೇವೋಕ್ತಿಗಳನ್ನು ಹೊಂದಿ ನಮಗೆ ಕೊಟ್ಟವನು.",
			"39": "<sup>39</sup> ಆದರೆ ನಮ್ಮ ಪಿತೃಗಳು ಅವನ ಮಾತುಗಳನ್ನು ಕೇಳುವದಕ್ಕೆ ಮನಸ್ಸಿಲ್ಲದವರಾಗಿ ಅವನನ್ನು ತಳ್ಳಿ ಬಿಟ್ಟು ತಿರಿಗಿ ಐಗುಪ್ತದೇಶದ ಕಡೆಗೆ ಮನಸ್ಸಿಟ್ಟರು.",
			"40": "<sup>40</sup> ಅವರು ಆರೋನನಿಗೆ - ಐಗುಪ್ತದೇಶದಿಂದ ನಮ್ಮನ್ನು ಕರಕೊಂಡು ಬಂದ ಆ ಮೋಶೆಯು ಏನಾದನೋ ಗೊತ್ತಿಲ್ಲ, ನಮ್ಮ ಮುಂದುಗಡೆಯಲ್ಲಿ ಹೋಗುವದಕ್ಕೆ ದೇವರುಗಳನ್ನು ನಮಗೆ ಮಾಡಿಕೊಡು ಎಂದು ಹೇಳಿದರು.",
			"41": "<sup>41</sup> ಆ ದಿನಗಳಲ್ಲಿ ಅವರು ಒಂದು ಬಸವನನ್ನು ಮಾಡಿ ಆ ವಿಗ್ರಹಕ್ಕೆ ಬಲಿಯನ್ನರ್ಪಿಸಿ ತಮ್ಮ ಕೈಗಳಿಂದ ಮಾಡಿದ ವಸ್ತುಗಳಲ್ಲಿ ಉಲ್ಲಾಸಪಟ್ಟರು.",
			"42": "<sup>42</sup> ಆದರೆ ದೇವರು ಅವರಿಗೆ ವಿಮುಖನಾಗಿ ಆಕಾಶದ ನಕ್ಷತ್ರ ಗಣವನ್ನು ಪೂಜಿಸುವದಕ್ಕೆ ಅವರನ್ನು ಒಪ್ಪಿಸಿಬಿಟ್ಟನು. ಪ್ರವಾದಿಗಳ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುವದೇನಂದರೆ - ಇಸ್ರಾಯೇಲ್ ವಂಶದವರೇ, ನೀವು ಅರಣ್ಯದಲ್ಲಿದ್ದ ನಾಲ್ವತ್ತು ವರುಷ ನನಗೆ ಯಜ್ಞಗಳನ್ನೂ ಬಲಿಗಳನ್ನೂ ಅರ್ಪಿಸುತ್ತಿದ್ದಿರೋ? ಇಲ್ಲವಲ್ಲಾ;",
			"43": "<sup>43</sup> ಅದಕ್ಕೆ ಬದಲಾಗಿ ನೀವು ಪೂಜಿಸಬೇಕೆಂದು ಮಾಡಿಕೊಂಡ ಮೂರ್ತಿಗಳನ್ನು ಅಂದರೆ ಮೊಲೋಖನ ಗುಡಾರವನ್ನೂ ರೊಂಫಾ ದೇವತೆಯ ನಕ್ಷತ್ರರೂಪವನ್ನೂ ಹೊತ್ತುಕೊಂಡು ಹೋದಿರಿ. ಆದದರಿಂದ ನಾನು ನಿಮ್ಮನ್ನು ಬಾಬೆಲಿನ ಆಚೆಗೆ ಗಡೀಪಾರು ಮಾಡುವೆನು ಎಂಬದು.",
			"44": "<sup>44</sup> ದೇವದರ್ಶನಗುಡಾರವು ಅಡವಿಯೊಳಗೆ ನಮ್ಮ ಪಿತೃಗಳ ಬಳಿಯಲ್ಲಿ ಇತ್ತು. ಮೋಶೆಯ ಸಂಗಡ ಮಾತಾಡಿದವನು - ನೀನು ನೋಡಿದ ಮಾದರಿಯ ಪ್ರಕಾರವೇ ಅದನ್ನು ಮಾಡಿಸಬೇಕೆಂದು ನೇಮಿಸಿದ್ದನು.",
			"45": "<sup>45</sup> ನಮ್ಮ ಹಿರಿಯರು ಅದನ್ನು ತಮ್ಮ ತಂದೆಗಳಿಂದ ಹೊಂದಿದರು; ಅವರು ಯೆಹೋಶುವನ ಹಿಂದೆ ಬಂದು ದೇವರು ತಮ್ಮ ಮುಂದೆ ಹೊರಡಿಸಿದ ಅನ್ಯಜನಗಳ ದೇಶವನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡಾಗ ಆ ಗುಡಾರವನ್ನು ಕೂಡ ತಂದರು. ಅದು ದಾವೀದನ ಕಾಲದವರೆಗೂ ಇಲ್ಲೇ ಇತ್ತು.",
			"46": "<sup>46</sup> ದಾವೀದನು ದೇವರ ಸನ್ನಿಧಾನದಲ್ಲಿ ಕೃಪೆಹೊಂದಿ ಯಾಕೋಬನ ವಂಶದವರಿಗೋಸ್ಕರ ದೇವಾಲಯವನ್ನು ಕಟ್ಟಿಸುವದಕ್ಕೆ ಅಪ್ಪಣೆಯಾಗಬೇಕೆಂದು ಕೇಳಿಕೊಂಡನು.",
			"47": "<sup>47</sup> ಆದರೆ ಆತನಿಗೋಸ್ಕರ ಮನೆಯನ್ನು ಕಟ್ಟಿಸಿದವನು ಸೊಲೊಮೋನನು.",
			"48": "<sup>48</sup> ಆದರೂ ಪರಾತ್ಪರನು ಕೈಯಿಂದ ಕಟ್ಟಿದ ಮನೆಗಳಲ್ಲಿ ವಾಸಮಾಡುವವನಲ್ಲ.",
			"49": "<sup>49</sup> ಆಕಾಶವು ನನಗೆ ಸಿಂಹಾಸನ, ಭೂಮಿಯು ನನ್ನ ಪಾದಗಳಿಗೆ ಪೀಠ. ನೀವು ನನಗೆ ಎಂಥ ಮನೆಯನ್ನು ಕಟ್ಟಿಕೊಡುವಿರಿ? ನನ್ನ ವಿಶ್ರಾಂತಿಗೆ ತಕ್ಕ ಸ್ಥಳವು ಯಾವದು?",
			"50": "<sup>50</sup> ಇವುಗಳನ್ನೆಲ್ಲಾ ನನ್ನ ಕೈಯೇ ನಿರ್ಮಿಸಿತಲ್ಲಾ ಎಂಬದಾಗಿ ಕರ್ತನು ಹೇಳುತ್ತಾನೆ ಎಂದು ಪ್ರವಾದಿಯು ನುಡಿದಿದ್ದಾನೆ.",
			"51": "<sup>51</sup> ಚಂಡಿಗಳೇ, ಮನಶ್ಶುದ್ಧಿಯೂ ಕರ್ಣಶುದ್ಧಿಯೂ ಇಲ್ಲದವರೇ, ನಿಮ್ಮ ಪಿತೃಗಳು ಹೇಗೋ ಹಾಗೆಯೇ ನೀವೂ ಯಾವಾಗಲೂ ಪವಿತ್ರಾತ್ಮನನ್ನು ಎದುರಿಸುವವರಾಗಿದ್ದೀರಿ.",
			"52": "<sup>52</sup> ಪ್ರವಾದಿಗಳಲ್ಲಿ ನಿಮ್ಮ ಪಿತೃಗಳು ಹಿಂಸೆಪಡಿಸದವರು ಯಾರಿದ್ದಾರೆ? ಅವರು ಆ ನೀತಿ ಸ್ವರೂಪನ ಆಗಮನದ ವಿಷಯದಲ್ಲಿ ಮುಂತಿಳಿಸಿದವರನ್ನು ಕೊಂದರು, ನೀವು ಈಗ ಆತನನ್ನು ಹಿಡುಕೊಟ್ಟು ಕೊಂದವರಾದಿರಿ.",
			"53": "<sup>53</sup> ದೇವದೂತರ ಮೂಲಕ ನೇಮಕವಾದ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ನೀವು ಹೊಂದಿದವರಾದರೂ ಅದನ್ನು ಅನುಸರಿಸುವದಿಲ್ಲ ಎಂದು ಹೇಳಿದನು.",
			"54": "<sup>54</sup> ಈ ಮಾತುಗಳನ್ನು ಕೇಳಿ ಅವರು ರೌದ್ರಮನಸ್ಸುಳ್ಳವರಾಗಿ ಅವನ ಮೇಲೆ ಹಲ್ಲು ಕಡಿದರು.",
			"55": "<sup>55</sup> ಆದರೆ ಅವನು ಪವಿತ್ರಾತ್ಮಭರಿತನಾಗಿ ಆಕಾಶದ ಕಡೆಗೆ ದೃಷ್ಟಿಸಿನೋಡಿ ದೇವರ ಪ್ರಭಾವವನ್ನೂ ದೇವರ ಬಲಗಡೆಯಲ್ಲಿ ನಿಂತಿದ್ದ ಯೇಸುವನ್ನೂ ಕಂಡು -",
			"56": "<sup>56</sup> ಅಗೋ, ಆಕಾಶವು ತೆರೆದಿರುವದನ್ನೂ ಮನುಷ್ಯಕುಮಾರನು ದೇವರ ಬಲಗಡೆಯಲ್ಲಿ ನಿಂತಿರುವದನ್ನೂ ನೋಡುತ್ತೇನೆ ಎಂದು ಹೇಳಿದನು.",
			"57": "<sup>57</sup> ಆದರೆ ಅವರು ಮಹಾ ಶಬ್ದದಿಂದ ಕೂಗಿ ಕಿವಿಗಳನ್ನು ಮುಚ್ಚಿಕೊಂಡು ಒಟ್ಟಾಗಿ ಅವನ ಮೇಲೆ ಬಿದ್ದು",
			"58": "<sup>58</sup> ಅವನನ್ನು ಊರ ಹೊರಕ್ಕೆ ನೂಕಿಕೊಂಡು ಹೋಗಿ ಕೊಲ್ಲುವದಕ್ಕೆ ಕಲ್ಲೆಸೆದರು. ಸಾಕ್ಷಿಯವರು ತಮ್ಮ ಬಟ್ಟೆಗಳನ್ನು ತೆಗೆದು ಸೌಲನೆಂಬ ಒಬ್ಬ ಯೌವನಸ್ಥನ ಕಾಲುಗಳ ಬಳಿಯಲ್ಲಿ ಇಟ್ಟರು.",
			"59": "<sup>59</sup> ಅವರು ಸ್ತೆಫನನ ಮೇಲೆ ಕಲ್ಲೆಸೆಯುತ್ತಾ ಇರಲು ಅವನು ಕರ್ತನ ಹೆಸರನ್ನು ಹೇಳಿ -",
			"60": "<sup>60</sup> ಯೇಸುಸ್ವಾಮಿಯೇ, ನನ್ನಾತ್ಮವನ್ನು ಸೇರಿಸಿಕೋ ಎಂದು ಪ್ರಾರ್ಥಿಸಿ ಮೊಣಕಾಲೂರಿ - ಕರ್ತನೇ, ಈ ಪಾಪವನ್ನು ಅವರ ಮೇಲೆ ಹೊರಿಸಬೇಡವೆಂದು ಮಹಾಶಬ್ದದಿಂದ ಕೂಗಿದನು."
		},
		"8": {
			"1": "<sup>1</sup> ಇದನ್ನು ಹೇಳಿ ನಿದ್ರೆಹೋದನು. ಸೌಲನು ಅವನ ಕೊಲೆಗೆ ಸಮ್ಮತಿಸುವವನಾಗಿದ್ದನು. ಆ ದಿವಸದಲ್ಲಿ ಯೆರೂಸಲೇಮಿನಲ್ಲಿದ್ದ ಸಭೆಗೆ ದೊಡ್ಡ ಹಿಂಸೆ ಉಂಟಾಯಿತು. ಅಪೊಸ್ತಲರು ಹೊರತಾಗಿ ಎಲ್ಲರು ಯೂದಾಯ ಸಮಾರ್ಯ ಸೀಮೆಗಳಲ್ಲಿ ಚದರಿಹೋದರು.",
			"2": "<sup>2</sup> ಭಕ್ತರಾದ ಜನರು ಸ್ತೆಫನನನ್ನು ಹೂಣಿಟ್ಟು ಅವನಿಗೋಸ್ಕರ ಬಹಳವಾಗಿ ಗೋಳಾಡಿದರು.",
			"3": "<sup>3</sup> ಆದರೆ ಸೌಲನು ಮನೆಮನೆಗಳಲ್ಲಿ ಹೊಕ್ಕು ಗಂಡಸರನ್ನೂ ಹೆಂಗಸರನ್ನೂ ಎಳಕೊಂಡು ಬಂದು ಸೆರೆಮನೆಗೆ ಹಾಕಿಸಿ ಸಭೆಯನ್ನು ಹಾಳು ಮಾಡುತ್ತಿದ್ದನು.",
			"4": "<sup>4</sup> ಚದರಿಹೋದವರು ಅಲ್ಲಲ್ಲಿ ಹೋಗಿ ಸುವಾರ್ತಾವಾಕ್ಯವನ್ನು ಸಾರುತ್ತಿದ್ದರು.",
			"5": "<sup>5</sup> ಫಿಲಿಪ್ಪನು ಸಮಾರ್ಯವೆಂಬ ಪಟ್ಟಣಕ್ಕೆ ಹೋಗಿ ಕ್ರಿಸ್ತನನ್ನು ಅಲ್ಲಿರುವವರಿಗೆ ಪ್ರಕಟಿಸಿದನು.",
			"6": "<sup>6</sup> ಗುಂಪಾಗಿ ಕೂಡಿದ ಜನಗಳು ಫಿಲಿಪ್ಪನ ಮಾತುಗಳನ್ನು ಕೇಳಿ ಅವನು ಮಾಡಿದ ಸೂಚಕಕಾರ್ಯಗಳನ್ನು ನೋಡಿ ಅವನು ಹೇಳಿದ ಸಂಗತಿಗಳಿಗೆ ಏಕಮನಸ್ಸಾಗಿ ಲಕ್ಷ್ಯಕೊಟ್ಟರು.",
			"7": "<sup>7</sup> ಯಾಕಂದರೆ ಅನೇಕರೊಳಗಿಂದ ದೆವ್ವಗಳು ಮಹಾಶಬ್ದದಿಂದ ಕೂಗಿ ಹೊರಗೆ ಬಂದವು; ಮತ್ತು ಅನೇಕ ಪಾರ್ಶ್ವವಾಯು ರೋಗಿಗಳೂ ಕುಂಟರೂ ಸ್ವಸ್ಥಮಾಡಲ್ಪಟ್ಟರು.",
			"8": "<sup>8</sup> ಆ ಪಟ್ಟಣದಲ್ಲಿ ಬಹು ಸಂತೋಷವಾಯಿತು.",
			"9": "<sup>9</sup> ಆದರೆ ಕೆಲವು ಕಾಲದಿಂದ ಆ ಪಟ್ಟಣದಲ್ಲಿದ್ದ ಸೀಮೋನನೆಂಬ ಒಬ್ಬ ಮನುಷ್ಯನು ತಾನು ಏನೋ ಒಬ್ಬ ಮಹಾತ್ಮನೆಂದು ಹೇಳಿಕೊಂಡು ಮಂತ್ರತಂತ್ರಗಳನ್ನು ನಡಿಸಿ ಸಮಾರ್ಯದ ಜನರಲ್ಲಿ ಬೆರಗನ್ನು ಹುಟ್ಟಿಸುತ್ತಿದ್ದನು.",
			"10": "<sup>10</sup> ಚಿಕ್ಕವರು ಮೊದಲುಗೊಂಡು ದೊಡ್ಡವರ ಪರ್ಯಂತರ ಎಲ್ಲರೂ - ಇವನು ಮಹಾ ಶಕ್ತಿ ಎನಿಸಿಕೊಂಡಿರುವ ದೇವರ ಶಕ್ತಿಯೇ ಎಂದು ಹೇಳುತ್ತಾ ಅವನಿಗೆ ಲಕ್ಷ್ಯಕೊಡುತ್ತಿದ್ದರು.",
			"11": "<sup>11</sup> ಬಹುಕಾಲದಿಂದಲೂ ಅವನು ಮಂತ್ರತಂತ್ರಗಳನ್ನು ನಡಿಸಿ ಜನರಲ್ಲಿ ಬೆರಗನ್ನು ಹುಟ್ಟಿಸಿದ್ದ ಕಾರಣದಿಂದ ಅವರು ಅವನಿಗೆ ಲಕ್ಷ್ಯಕೊಡುತ್ತಿದ್ದರು.",
			"12": "<sup>12</sup> ಆದರೆ ಫಿಲಿಪ್ಪನು ದೇವರ ರಾಜ್ಯದ ವಿಷಯದಲ್ಲಿಯೂ ಯೇಸು ಕ್ರಿಸ್ತನ ಹೆಸರಿನ ವಿಷಯದಲ್ಲಿಯೂ ಶುಭವರ್ತಮಾನವನ್ನು ಸಾರಲು ಗಂಡಸರೂ ಹೆಂಗಸರೂ ನಂಬಿ ದೀಕ್ಷಾಸ್ನಾನ ಮಾಡಿಸಿಕೊಂಡರು.",
			"13": "<sup>13</sup> ಆಗ ಸೀಮೋನನು ಕೂಡ ನಂಬಿ ದೀಕ್ಷಾಸ್ನಾನಮಾಡಿಸಿಕೊಂಡು ಫಿಲಿಪ್ಪನ ಸಂಗಡ ಯಾವಾಗಲೂ ಇದ್ದು ಸೂಚಕಕಾರ್ಯಗಳೂ ಮಹತ್ಕಾರ್ಯಗಳೂ ಉಂಟಾಗುವದನ್ನು ನೋಡಿ ಬೆರಗಾದನು.",
			"14": "<sup>14</sup> ಸಮಾರ್ಯದವರು ದೇವರ ವಾಕ್ಯವನ್ನು ಸ್ವೀಕರಿಸಿದ ವರ್ತಮಾನವನ್ನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿದ್ದ ಅಪೊಸ್ತಲರು ಕೇಳಿ ಪೇತ್ರ ಯೋಹಾನರನ್ನು ಅವರ ಬಳಿಗೆ ಕಳುಹಿಸಿದರು.",
			"15": "<sup>15</sup> ಇವರು ಅಲ್ಲಿಗೆ ಬಂದು ಆ ಜನರಿಗೆ ಪವಿತ್ರಾತ್ಮವರವು ದೊರೆಯಬೇಕೆಂದು ಅವರಿಗೋಸ್ಕರ ಪ್ರಾರ್ಥನೆಮಾಡಿದರು.",
			"16": "<sup>16</sup> ಯಾಕಂದರೆ ಆ ವರವು ಅವರಲ್ಲಿ ಒಬ್ಬನ ಮೇಲಾದರೂ ಇನ್ನೂ ಬಂದಿರಲಿಲ್ಲ. ಅವರು ಕರ್ತನಾದ ಯೇಸುವಿನ ಹೆಸರಿನಲ್ಲಿ ದೀಕ್ಷಾಸ್ನಾನವನ್ನು ಮಾತ್ರ ಮಾಡಿಸಿಕೊಂಡಿದ್ದರು.",
			"17": "<sup>17</sup> ಅಪೊಸ್ತಲರು ಅವರ ಮೇಲೆ ಕೈಗಳನ್ನಿಡಲು ಅವರು ಪವಿತ್ರಾತ್ಮವರವನ್ನು ಹೊಂದಿದರು.",
			"18": "<sup>18</sup> ಅಪೊಸ್ತಲರು ಕೈಗಳನ್ನಿಡುವದರ ಮೂಲಕವಾಗಿ ಪವಿತ್ರಾತ್ಮದಾನವಾಗುವದನ್ನು ಸೀಮೋನನು ನೋಡಿ ಹಣವನ್ನು ತಂದು ಅವರ ಮುಂದಿಟ್ಟು -",
			"19": "<sup>19</sup> ನಾನು ಯಾರ ಮೇಲೆ ಕೈಗಳನ್ನಿಡುತ್ತೇನೋ ಅವರು ಪವಿತ್ರಾತ್ಮ ವರವನ್ನು ಹೊಂದುವಂತೆ ಈ ಅಧಿಕಾರವನ್ನು ನನಗೂ ಕೊಡಿರಿ ಅಂದನು.",
			"20": "<sup>20</sup> ಆದರೆ ಪೇತ್ರನು ಅವನಿಗೆ - ನಿನ್ನ ಬೆಳ್ಳಿಯು ನಿನ್ನ ಕೂಡ ಹಾಳಾಗಿಹೋಗಲಿ. ದೇವರ ವರವನ್ನು ಹಣಕ್ಕೆ ಕೊಂಡುಕೊಳ್ಳಬಹುದೆಂದು ಭಾವಿಸುತ್ತೀಯೋ?",
			"21": "<sup>21</sup> ಈ ಕಾರ್ಯದಲ್ಲಿ ನಿನಗೆ ಭಾಗವೂ ಇಲ್ಲ, ಪಾಲೂ ಇಲ್ಲ; ನಿನ್ನ ಹೃದಯವು ದೇವರ ಮುಂದೆ ಸರಿಯಿಲ್ಲ.",
			"22": "<sup>22</sup> ಆದದರಿಂದ ಈ ನಿನ್ನ ಕೆಟ್ಟತನವನ್ನು ಬಿಟ್ಟು ದೇವರ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡು ಕರ್ತನು ನಿನ್ನ ಮನಸ್ಸಿನ ಆಲೋಚನೆಯನ್ನು ಕ್ಷಮಿಸುವನೋ ಏನೋ ಆತನನ್ನು ಬೇಡಿಕೋ.",
			"23": "<sup>23</sup> ಯಾಕಂದರೆ ನೀನು ಹಾನಿಕರವಾದ ವಿಷವೂ ದುಷ್ಟತ್ವದಲ್ಲಿ ಬಿಗಿಯುವ ಬಂಧನವೂ ಆಗಿರುವವನೆಂದು ಕಾಣುತ್ತದೆ ಅಂದನು.",
			"24": "<sup>24</sup> ಅದಕ್ಕೆ ಸೀಮೋನನು - ನೀವು ಹೇಳಿರುವ ಸಂಗತಿಗಳಲ್ಲಿ ಯಾವದೂ ನನ್ನ ಮೇಲೆ ಬಾರದಂತೆ ನನಗೋಸ್ಕರ ನೀವೇ ಕರ್ತನನ್ನು ಬೇಡಿಕೊಳ್ಳಿರಿ ಎಂದು ಹೇಳಿದನು.",
			"25": "<sup>25</sup> ಹೀಗಿರಲಾಗಿ ಅವರು ಕರ್ತನ ವಾಕ್ಯವನ್ನು ಪ್ರಮಾಣಪೂರ್ವಕವಾಗಿ ಹೇಳಿದ ಮೇಲೆ ಯೆರೂಸಲೇಮಿಗೆ ಹಿಂತಿರಿಗಿ ಹೊರಟು ಸಮಾರ್ಯದವರ ಅನೇಕ ಊರುಗಳಲ್ಲಿ ಸುವಾರ್ತೆಯನ್ನು ಸಾರಿದರು.",
			"26": "<sup>26</sup> ಅಷ್ಟರಲ್ಲಿ ಕರ್ತನ ದೂತನು ಫಿಲಿಪ್ಪನಿಗೆ - ನೀನು ಎದ್ದು ದಕ್ಷಿಣ ಕಡೆಗೆ ಯೆರೂಸಲೇಮಿನಿಂದ ಗಾಜಕ್ಕೆ ಹೋಗುವ ದಾರಿಯಲ್ಲಿ ಹೋಗು, ಅದು ಅಡವಿ ಎಂದು ಹೇಳಿದನು.",
			"27": "<sup>27</sup> ಅವನು ಎದ್ದು ಹೊರಟುಹೋದನು; ಹೋಗುತ್ತಿರುವಾಗ ಐಥಿಯೋಪ್ಯ ದೇಶದ ಒಬ್ಬ ಮನುಷ್ಯನನ್ನು ಕಂಡನು. ಅವನು ಕಂಚುಕಿಯೂ ಐಥಿಯೋಪ್ಯದವರ ರಾಣಿಯಾಗಿದ್ದ ಕಂದಾಕೆಯ ಕೈಕೆಳಗೆ ದೊಡ್ಡ ಅಧಿಕಾರಿಯೂ ಆಕೆಯ ಎಲ್ಲಾ ಖಜಾನೆಯ ಮೇಲ್ವಿಚಾರಕನೂ ಆಗಿದ್ದನು.",
			"28": "<sup>28</sup> ಅವನು ದೇವಾರಾಧನೆಗೋಸ್ಕರ ಯೆರೂಸಲೇಮಿಗೆ ಬಂದು ಹಿಂತಿರುಗಿ ಹೋಗುವಾಗ ತನ್ನ ರಥದಲ್ಲಿ ಕೂತುಕೊಂಡು ಯೆಶಾಯ ಪ್ರವಾದಿಯ ಗ್ರಂಥವನ್ನು ಓದುತ್ತಿದ್ದನು.",
			"29": "<sup>29</sup> ದೇವರಾತ್ಮನು ಫಿಲಿಪ್ಪನಿಗೆ - ನೀನು ಆ ರಥದ ಹತ್ತಿರ ಹೋಗಿ ಅದರ ಸಂಗಡಲೇ ನಡೆ ಎಂದು ಹೇಳಿದನು.",
			"30": "<sup>30</sup> ಫಿಲಿಪ್ಪನು ಓಡಿ ಹೋಗಿ ಆ ಮನುಷ್ಯನು ಯೆಶಾಯ ಪ್ರವಾದಿಯ ಗ್ರಂಥವನ್ನು ಓದುತ್ತಿರುವದನ್ನು ಕೇಳಿ - ಎಲೈ, ನೀನು ಓದುವದು ನಿನಗೆ ತಿಳಿಯುತ್ತದೋ? ಅನ್ನಲು",
			"31": "<sup>31</sup> ಅವನು - ಯಾರಾದರೂ ನನಗೆ ಅರ್ಥ ತಿಳಿಸಿಕೊಟ್ಟ ಹೊರತು ಅದು ನನಗೆ ಹೇಗೆ ತಿಳಿದೀತು ಎಂದು ಹೇಳಿ - ನೀನು ರಥವನ್ನು ಹತ್ತಿ ನನ್ನ ಬಳಿಯಲ್ಲಿ ಕೂತುಕೋ ಎಂಬದಾಗಿ ಫಿಲಿಪ್ಪನನ್ನು ಕೇಳಿಕೊಂಡನು.",
			"32": "<sup>32</sup> ಅವನು ಓದುತ್ತಿದ್ದ ಶಾಸ್ತ್ರವಚನವು ಯಾವದಂದರೆ - ವಧ್ಯಸ್ಥಾನಕ್ಕೆ ಒಯ್ಯಲ್ಪಡುವ ಕುರಿಯಂತೆ ಆತನು ಒಯ್ಯಲ್ಪಟ್ಟನು; ಕುರಿಮರಿಯು ಉಣ್ಣೆ ಕತ್ತರಿಸುವವನ ಮುಂದೆ ಮೌನವಾಗಿರುವಂತೆ ಆತನು ಬಾಯಿ ತೆರೆಯಲೇ ಇಲ್ಲ.",
			"33": "<sup>33</sup> ಆತನಿಗುಂಟಾದ ದೀನಾವಸ್ಥೆಯಲ್ಲಿ ನ್ಯಾಯವು ಇಲ್ಲದೆಹೋಯಿತು. ಆತನ ಸಕಾಲದವರನ್ನು ಹೇಳತಕ್ಕದ್ದೇನು? ಆತನ ಜೀವವನ್ನು ಭೂಮಿಯ ಮೇಲಿನಿಂದ ತೆಗೆದುಬಿಟ್ಟರಲ್ಲಾ ಎಂಬದೇ.",
			"34": "<sup>34</sup> ಕಂಚುಕಿಯು ಈ ವಚನವನ್ನು ಕುರಿತು - ಪ್ರವಾದಿಯು ಇದನ್ನು ಯಾರ ವಿಷಯದಲ್ಲಿ ಹೇಳಿದ್ದಾನೆ? ತನ್ನ ವಿಷಯದಲ್ಲಿಯೋ? ಮತ್ತೊಬ್ಬನ ವಿಷಯದಲ್ಲಿಯೋ? ದಯಮಾಡಿ ಹೇಳಬೇಕು ಎಂದು ಫಿಲಿಪ್ಪನನ್ನು ಕೇಳಲು",
			"35": "<sup>35</sup> ಫಿಲಿಪ್ಪನು ಉಪದೇಶಿಸುವದಕ್ಕೆ ತೊಡಗಿ ಅದೇ ವಚನವನ್ನು ಆಧಾರ ಮಾಡಿಕೊಂಡು ಅವನಿಗೆ ಯೇಸುವಿನ ವಿಷಯವಾದ ಸುವಾರ್ತೆಯನ್ನು ತಿಳಿಸಿದನು.",
			"36": "<sup>36</sup> ಅವರು ದಾರಿಯಲ್ಲಿ ಹೋಗುತ್ತಿರುವಾಗ ನೀರಿನ ಬಳಿಗೆ ಬಂದರು. ಕಂಚುಕಿಯು - ಅಗೋ, ನೀರು; ನನಗೆ ದೀಕ್ಷಾಸ್ನಾನವಾಗುವದಕ್ಕೆ ಅಡ್ಡಿ ಏನು ಎಂದು ಹೇಳಿ ರಥವನ್ನು ನಿಲ್ಲಿಸು ಎಂದು ಅಪ್ಪಣೆಕೊಟ್ಟನು;",
			"38": "<sup>38</sup> ಫಿಲಿಪ್ಪನು ಕಂಚುಕಿಯು ಇಬ್ಬರೂ ನೀರಿನೊಳಗೆ ಇಳಿದರು. ಫಿಲಿಪ್ಪನು ಅವನಿಗೆ ದೀಕ್ಷಾಸ್ನಾನ ಮಾಡಿಸಿದನು.",
			"39": "<sup>39</sup> ಅವರು ನೀರನ್ನು ಬಿಟ್ಟು ಮೇಲಕ್ಕೆ ಬಂದಾಗ ಕರ್ತನ ಆತ್ಮ ಫಿಲಿಪ್ಪನನ್ನು ಎತ್ತಿಕೊಂಡುಹೋಗಲಾಗಿ ಕಂಚುಕಿಯು ಸಂತೋಷವುಳ್ಳವನಾಗಿ ತನ್ನ ದಾರಿಯನ್ನು ಹಿಡಿದು ಅವನನ್ನು ಕಾಣಲೇ ಇಲ್ಲ.",
			"40": "<sup>40</sup> ತರುವಾಯ ಫಿಲಿಪ್ಪನು ಅಜೋತಿನಲ್ಲಿ ಕಾಣಿಸಿಕೊಂಡು ಅಲ್ಲಿಂದ ಕೈಸರೈಯದ ತನಕ ಎಲ್ಲಾ ಊರುಗಳಲ್ಲಿ ಸುವಾರ್ತೆಯನ್ನು ಸಾರುತ್ತಾ ಹೋದನು."
		},
		"9": {
			"1": "<sup>1</sup> ಸೌಲನು ಇನ್ನು ಕರ್ತನ ಶಿಷ್ಯರ ಮೇಲೆ ರೌದ್ರನಾಗಿದ್ದು ಬೆದರಿಕೆಯ ಮಾತುಗಳನ್ನಾಡುತ್ತಾ ಅವರನ್ನು ಸಂಹರಿಸಬೇಕೆಂದು ಆಶೆಪಡುತ್ತಾ ಮಹಾಯಾಜಕನ ಬಳಿಗೆ ಹೋಗಿ -",
			"2": "<sup>2</sup> ಆ ಮಾರ್ಗವನ್ನು ಹಿಡಿದವರು ಯಾರಾದರೂ ಸಿಕ್ಕಿದರೆ ಅವರು ಗಂಡಸರಾದರೂ ಸರಿಯೇ ಹೆಂಗಸರಾದರೂ ಸರಿಯೇ ನಾನು ಅವರಿಗೆ ಬೇಡಿಹಾಕಿಸಿ ಯೆರೂಸಲೇಮಿಗೆ ತರುವಂತೆ ದಮಸ್ಕದಲ್ಲಿರುವ ಆಯಾ ಸಭಾಮಂದಿರದವರಿಗೆ ನೀನು ಕಾಗದವನ್ನು ಕೊಡಬೇಕು ಎಂದು ಅವನನ್ನು ಬೇಡಿಕೊಂಡನು.",
			"3": "<sup>3</sup> ಅವನು ಪ್ರಯಾಣ ಮಾಡುತ್ತಾ ದಮಸ್ಕದ ಹತ್ತಿರಕ್ಕೆ ಬರಲು ಫಕ್ಕನೆ ಆಕಾಶದೊಳಗಿಂದ ಒಂದು ಬೆಳಕು ಅವನ ಸುತ್ತಲು ಮಿಂಚಿತು.",
			"4": "<sup>4</sup> ಅವನು ನೆಲಕ್ಕೆ ಬಿದ್ದು - ಸೌಲನೇ, ಸೌಲನೇ, ನನ್ನನ್ನು ಯಾಕೆ ಹಿಂಸೆ ಪಡಿಸುತ್ತೀ ಎಂದು ಹೇಳುವ ವಾಣಿಯನ್ನು ಕೇಳಿದನು.",
			"5": "<sup>5</sup> ಅವನು - ಕರ್ತನೇ, ನೀನಾರು ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ ಕರ್ತನು - ನೀನು ಹಿಂಸೆಪಡಿಸುವ ಯೇಸುವೇ ನಾನು.",
			"6": "<sup>6</sup> ನೀನೆದ್ದು ಊರೊಳಕ್ಕೆ ಹೋಗು, ನೀನು ಮಾಡಬೇಕಾದದ್ದು ಅಲ್ಲಿ ನಿನಗೆ ತಿಳಿಸಲ್ಪಡುವದು ಎಂದು ಹೇಳಿದನು.",
			"7": "<sup>7</sup> ಅವನ ಜೊತೆಯಲ್ಲಿ ಪ್ರಯಾಣಮಾಡುತ್ತಿದ್ದವರು ಧ್ವನಿಯನ್ನು ಮಾತ್ರ ಕೇಳಿ ಯಾರನ್ನೂ ಕಾಣದೆ ಮೂಕರಂತೆ ನಿಂತರು.",
			"8": "<sup>8</sup> ಸೌಲನು ನೆಲದಿಂದ ಎದ್ದು ಕಣ್ಣು ತೆರೆದಾಗ ಏನೂ ಕಾಣಿಸಲಿಲ್ಲ. ಆಗ ಅವರು ಅವನನ್ನು ಕೈಹಿಡಿದು ದಮಸ್ಕದೊಳಕ್ಕೆ ಕರೆದುಕೊಂಡು ಹೋದರು.",
			"9": "<sup>9</sup> ಅವನು ಮೂರು ದಿವಸ ಕಣ್ಣುಕಾಣದೆ ಏನೂ ತಿನ್ನಲಿಲ್ಲ; ಏನೂ ಕುಡಿಯಲಿಲ್ಲ.",
			"10": "<sup>10</sup> ದಮಸ್ಕದಲ್ಲಿ ಅನನೀಯನೆಂಬ ಒಬ್ಬ ಶಿಷ್ಯನಿದ್ದನು; ಕರ್ತನು ದರ್ಶನದಲ್ಲಿ - ಅನನೀಯನೇ, ಎಂದು ಅವನನ್ನು ಕರೆಯಲು ಅವನು - ಕರ್ತನೇ, ಇಗೋ, ಇದ್ದೇನೆ ಅಂದನು.",
			"11": "<sup>11</sup> ಕರ್ತನು ಅವನಿಗೆ - ನೀನೆದ್ದು ನೆಟ್ಟನೇಬೀದಿ ಎಂಬ ಬೀದಿಗೆ ಹೋಗಿ ಯೂದನ ಮನೆಯಲ್ಲಿ ತಾರ್ಸದ ಸೌಲನೆಂಬವನನ್ನು ವಿಚಾರಿಸು; ಅವನು ಪ್ರಾರ್ಥನೆ ಮಾಡುತ್ತಾನೆ.",
			"12": "<sup>12</sup> ಮತ್ತು ಅನನೀಯನೆಂಬ ಒಬ್ಬ ಮನುಷ್ಯನು ಒಳಗೆ ಬಂದು ತನಗೆ ತಿರಿಗಿ ಕಣ್ಣುಕಾಣುವಂತೆ ತನ್ನ ಮೇಲೆ ಕೈಯಿಡುವದನ್ನು ನೋಡಿದ್ದಾನೆ ಎಂದು ಹೇಳಿದನು.",
			"13": "<sup>13</sup> ಅದಕ್ಕೆ ಅನನೀಯನು - ಕರ್ತನೇ, ಆ ಮನುಷ್ಯನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ನಿನ್ನನ್ನು ನಂಬಿದ ದೇವಜನರಿಗೆ ಎಷ್ಟೋ ಕೇಡನ್ನುಂಟುಮಾಡಿದನೆಂದು ಅವನ ವಿಷಯವಾಗಿ ಅನೇಕರಿಂದ ಕೇಳಿದ್ದೇನೆ;",
			"14": "<sup>14</sup> ಮತ್ತು ಇಲ್ಲಿಯೂ ನಿನ್ನ ಹೆಸರನ್ನು ಸ್ಮರಿಸುವವರೆಲ್ಲರಿಗೆ ಬೇಡಿಹಾಕಿಸುವ ಅಧಿಕಾರವನ್ನು ಮಹಾಯಾಜಕರಿಂದ ಹೊಂದಿದ್ದಾನೆ ಅಂದನು.",
			"15": "<sup>15</sup> ಕರ್ತನು ಅವನಿಗೆ - ನೀನು ಹೋಗು; ಆ ಮನುಷ್ಯನು ಅನ್ಯಜನರಿಗೂ ಅರಸುಗಳಿಗೂ ಇಸ್ರಾಯೇಲ್ಯರಿಗೂ ನನ್ನ ಹೆಸರನ್ನು ತಿಳಿಸುವದಕ್ಕಾಗಿ ನಾನು ಆರಿಸಿಕೊಂಡ ಸಾಧನವಾಗಿದ್ದಾನೆ.",
			"16": "<sup>16</sup> ಅವನು ನನ್ನ ಹೆಸರಿನ ನಿಮಿತ್ತ ಎಷ್ಟು ಹಿಂಸೆ ಅನುಭವಿಸಬೇಕೆಂಬದನ್ನು ನಾನೇ ಅವನಿಗೆ ತೋರಿಸುವೆನು ಎಂದು ಹೇಳಿದನು.",
			"17": "<sup>17</sup> ಆಗ ಅನನೀಯನು ಹೊರಟು ಆ ಮನೆಯೊಳಗೆ ಹೋಗಿ ಅವನ ಮೇಲೆ ತನ್ನ ಕೈಗಳನ್ನಿಟ್ಟು - ಸಹೋದರನಾದ ಸೌಲನೇ, ನೀನು ಬಂದ ದಾರಿಯಲ್ಲಿ ನಿನಗೆ ಕಾಣಿಸಿಕೊಂಡ ಕರ್ತನಾದ ಯೇಸು ನಿನಗೆ ಕಣ್ಣುಕಾಣುವಂತೆಯೂ ನೀನು ಪವಿತ್ರಾತ್ಮಭರಿತನಾಗುವಂತೆಯೂ ನನ್ನನ್ನು ನಿನ್ನ ಬಳಿಗೆ ಕಳುಹಿಸಿದ್ದಾನೆ ಎಂದು ಹೇಳಿದನು.",
			"18": "<sup>18</sup> ಕೂಡಲೆ ಅವನ ಕಣ್ಣುಗಳಿಂದ ಪರೆಗಳಂತೆ ಏನೋ ಬಿದ್ದು ಅವನ ಕಣ್ಣು ಕಾಣಿಸಿದವು. ಅವನು ಎದ್ದು ದೀಕ್ಷಾಸ್ನಾನ ಮಾಡಿಸಿಕೊಂಡನು.",
			"19": "<sup>19</sup> ತರುವಾಯ ಊಟಮಾಡಿ ಬಲ ಹೊಂದಿದನು.",
			"20": "<sup>20</sup> ಮುಂದೆ ಅವನು ದಮಸ್ಕದಲ್ಲಿದ್ದ ಶಿಷ್ಯರ ಸಂಗಡ ಕೆಲವು ದಿವಸ ಇದ್ದು ತಡಮಾಡದೆ ಸಭಾಮಂದಿರಗಳಲ್ಲಿ ಯೇಸುವಿನ ವಿಷಯವಾಗಿ ಆತನೇ ದೇವಕುಮಾರನೆಂದು ಸಾರುವದಕ್ಕೆ ಪ್ರಾರಂಭಮಾಡಿದನು.",
			"21": "<sup>21</sup> ಕೇಳಿದವರೆಲ್ಲರು ಬೆರಗಾಗಿ - ಈ ಹೆಸರು ಹೇಳುವವರನ್ನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಹಾಳುಮಾಡಿದವನು ಇವನಲ್ಲವೇ. ಅಂಥವರನ್ನು ಬೇಡಿಹಾಕಿಸಿ ಮಹಾಯಾಜಕರ ಬಳಿಗೆ ತೆಗೆದುಕೊಂಡು ಹೋಗಬೇಕೆಂದೇ ಇಲ್ಲಿಗೆ ಬಂದನಲ್ಲವೇ ಎಂದು ಮಾತಾಡಿಕೊಂಡರು.",
			"22": "<sup>22</sup> ಆದರೆ ಸೌಲನು ಇನ್ನೂ ಅಧಿಕ ಸಾಮರ್ಥ್ಯವುಳ್ಳವನಾಗಿ ಯೇಸುವೇ ಕ್ರಿಸ್ತನೆಂದು ಸಿದ್ಧಾಂತಪಡಿಸಿ ದಮಸ್ಕದಲ್ಲಿದ್ದ ಯೆಹೂದ್ಯರನ್ನು ಉತ್ತರವಿಲ್ಲದವರಾಗ ಮಾಡಿದನು.",
			"23": "<sup>23</sup> ಅನೇಕ ದಿವಸಗಳು ಹೋದ ಮೇಲೆ ಯೆಹೂದ್ಯರು ಅವನನ್ನು ಕೊಲ್ಲಬೇಕೆಂದು ಆಲೋಚನೆಮಾಡಿಕೊಂಡರು.",
			"24": "<sup>24</sup> ಅವರ ಆಲೋಚನೆಯು ಸೌಲನಿಗೆ ತಿಳಿದುಬಂತು. ಅವನನ್ನು ಕೊಲ್ಲುವದಕ್ಕಾಗಿ ಅವರು ಹಗಲಿರುಳೂ ಪಟ್ಟಣದ ಬಾಗಿಲುಗಳನ್ನು ಕಾಯುತ್ತಿದ್ದರು.",
			"25": "<sup>25</sup> ಆದರೆ ಅವನ ಶಿಷ್ಯರು ರಾತ್ರಿಕಾಲದಲ್ಲಿ ಅವನನ್ನು ಕರೆದುಕೊಂಡುಹೋಗಿ ಒಂದು ಹೆಡಿಗೆಯಲ್ಲಿ ಕೂಡ್ರಿಸಿ ಗೋಡೆಯೊಳಗಿಂದ ಇಳಿಸಿದರು.",
			"26": "<sup>26</sup> ಅವನು ಯೆರೂಸಲೇಮಿಗೆ ಬಂದು ಶಿಷ್ಯರೊಳಗೆ ಸೇರಿಕೊಳ್ಳಬೇಕೆಂದು ಪ್ರಯತ್ನ ಮಾಡಿದಾಗ ಎಲ್ಲರು ಅವನನ್ನು ಶಿಷ್ಯನೆಂದು ನಂಬದೆ ಅವನಿಗೆ ಭಯಪಟ್ಟರು.",
			"27": "<sup>27</sup> ಆದರೆ ಬಾರ್ನಬನು ಅವನನ್ನು ಕೈಹಿಡಿದು ಅಪೊಸ್ತಲರ ಬಳಿಗೆ ಕರೆದುಕೊಂಡುಹೋಗಿ ಅವನು ದಾರಿಯಲ್ಲಿ ಕರ್ತನನ್ನು ಕಂಡದ್ದನ್ನೂ ಕರ್ತನು ಅವನ ಸಂಗಡ ಮಾತಾಡಿದ್ದನ್ನೂ ದಮಸ್ಕದೊಳಗೆ ಯೇಸುವಿನ ಹೆಸರಿನಲ್ಲಿ ಧೈರ್ಯದಿಂದ ಮಾತಾಡಿದ್ದನ್ನೂ ಅವರಿಗೆ ವಿವರವಾಗಿ ಹೇಳಿದನು.",
			"28": "<sup>28</sup> ಆಮೇಲೆ ಅವನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿದ್ದುಕೊಂಡು ಅವರಲ್ಲಿ ಬರುತ್ತಾ ಹೋಗುತ್ತಾ ಕರ್ತನ ಹೆಸರಿನಲ್ಲಿ ಧೈರ್ಯದಿಂದ ಬೋಧಿಸಿದನು.",
			"29": "<sup>29</sup> ಮತ್ತು ಗ್ರೀಕ್ ಭಾಷೆಯನ್ನಾಡುವ ಯೆಹೂದ್ಯರ ಸಂಗಡ ಮಾತಾಡಿ ತರ್ಕಿಸುತ್ತಿದ್ದನು. ಆದರೆ ಅವರು ಅವನನ್ನು ಕೊಲ್ಲುವದಕ್ಕೆ ಪ್ರಯತ್ನ ಮಾಡಿದರು.",
			"30": "<sup>30</sup> ಇದು ಸಹೋದರರಿಗೆ ತಿಳಿದು ಬರಲು ಅವರು ಅವನನ್ನು ಕೈಸರೈಯಕ್ಕೆ ಕರಕೊಂಡು ಹೋಗಿ ಅಲ್ಲಿಂದ ತಾರ್ಸಕ್ಕೆ ಕಳುಹಿಸಿದರು.",
			"31": "<sup>31</sup> ಹೀಗಿರಲಾಗಿ ಯೂದಾಯ ಗಲಿಲಾಯ ಸಮಾರ್ಯ ಸೀಮೆಗಳಲ್ಲಿದ್ದ ಸಭೆಯು ಸಮಾಧಾನ ಹೊಂದಿತು; ಮತ್ತು ಭಕ್ತಿಯಲ್ಲಿ ಬೆಳೆದು ಕರ್ತನ ಭಯದಲ್ಲಿ ನಡೆದು ಪವಿತ್ರಾತ್ಮನಿಂದ ಪ್ರೋತ್ಸಾಹವನ್ನು ಹೊಂದಿ ಹೆಚ್ಚುತ್ತಾ ಬಂತು.",
			"32": "<sup>32</sup> ಪೇತ್ರನು ಎಲ್ಲಾ ಕಡೆಯೂ ಸಂಚಾರಮಾಡುತ್ತಿರುವಾಗ ಲುದ್ದದಲ್ಲಿ ವಾಸಿಯಾಗಿದ್ದ ದೇವಜನರ ಬಳಿಗೂ ಬಂದನು..",
			"33": "<sup>33</sup> ಅಲ್ಲಿ ಪಾರ್ಶ್ವವಾಯುರೋಗಿಯಾಗಿ ಎಂಟು ವರುಷಗಳಿಂದ ಹಾಸಿಗೆಯ ಮೇಲೆ ಬಿದ್ದಿದ್ದ ಐನೇಯನೆಂಬ ಒಬ್ಬ ಮನುಷ್ಯನನ್ನು ಕಂಡನು.",
			"34": "<sup>34</sup> ಪೇತ್ರನು ಅವನಿಗೆ - ಐನೇಯನೇ, ಯೇಸು ಕ್ರಿಸ್ತನು ನಿನ್ನನ್ನು ವಾಸಿಮಾಡುತ್ತಾನೆ; ಎದ್ದು ನಿನ್ನ ಹಾಸಿಗೆಯನ್ನು ಹಾಸಿಕೋ ಎಂದು ಹೇಳಿದನು. ಕೂಡಲೆ ಅವನು ಎದ್ದನು.",
			"35": "<sup>35</sup> ಮತ್ತು ಲುದ್ದದಲ್ಲಿಯೂ ಸಾರೋನಪ್ರಾಂತ್ಯದಲ್ಲಿಯೂ ವಾಸವಾಗಿದ್ದವರೆಲ್ಲರು ಅವನನ್ನು ನೋಡಿದರು, ಮತ್ತು ಅವರು ಕರ್ತನ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡರು.",
			"36": "<sup>36</sup> ಯೊಪ್ಪದಲ್ಲಿ ತಬಿಥಾ ಎಂಬ ಒಬ್ಬ ಶಿಷ್ಯಳಿದ್ದಳು. ಆ ಹೆಸರಿಗೆ ಗ್ರೀಕ್‍ಭಾಷೆಯಲ್ಲಿ ದೊರ್ಕ ಎಂದರ್ಥ. ಆಕೆಯು ಸತ್ಕ್ರಿಯೆಗಳನ್ನೂ ದಾನಧರ್ಮಗಳನ್ನೂ ಬಹಳವಾಗಿ ಮಾಡುತ್ತಿದ್ದಳು.",
			"37": "<sup>37</sup> ಆ ಕಾಲದಲ್ಲಿ ಆಕೆ ರೋಗದಲ್ಲಿ ಬಿದ್ದು ಸತ್ತಳು, ಮತ್ತು ಆಕೆಯ ಶವವನ್ನು ತೊಳೆದು ಮೇಲಂತಸ್ತಿನಲ್ಲಿಟ್ಟರು.",
			"38": "<sup>38</sup> ಯೊಪ್ಪಕ್ಕೆ ಲುದ್ದವು ಹತ್ತಿರವಾಗಿರಲಾಗಿ ಪೇತ್ರನು ಅಲ್ಲಿ ಇದ್ದಾನೆಂದು ಶಿಷ್ಯರು ಕೇಳಿ ಇಬ್ಬರು ಮನುಷ್ಯರನ್ನು ಅವನ ಬಳಿಗೆ ಕಳುಹಿಸಿ - ತಡಮಾಡದೆ ನಮ್ಮ ತನಕ ಬರಬೇಕೆಂದು ಬೇಡಿಕೊಂಡರು.",
			"39": "<sup>39</sup> ಪೇತ್ರನು ಎದ್ದು ಅವರ ಜೊತೆಯಲ್ಲಿ ಹೋದನು. ಅವನು ಬಂದ ಕೂಡಲೆ ಅವರು ಅವನನ್ನು ಮೇಲಂತಸ್ತಿಗೆ ಕರೆದುಕೊಂಡು ಹೋದರು. ಅಲ್ಲಿ ವಿಧವೆಯರೆಲ್ಲರು ಅಳುತ್ತಾ ಅವನ ಹತ್ತಿರ ನಿಂತುಕೊಂಡು ದೊರ್ಕಳು ತಮ್ಮ ಸಂಗಡ ಇದ್ದಾಗ ಮಾಡಿಕೊಟ್ಟಿದ್ದ ಒಳಂಗಿಗಳನ್ನೂ ಮೇಲಂಗಿಗಳನ್ನೂ ತೋರಿಸಿದರು.",
			"40": "<sup>40</sup> ಪೇತ್ರನು ಅವರೆಲ್ಲರನ್ನು ಹೊರಕ್ಕೆ ಕಳುಹಿಸಿ ಮೊಣಕಾಲೂರಿ ಪ್ರಾರ್ಥನೆಮಾಡಿ ಶವದ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡು - ತಬಿಥಾ, ಏಳು ಅಂದನು. ಆಕೆಯು ಕಣ್ಣು ತೆರೆದು ಪೇತ್ರನನ್ನು ನೋಡಿ ಎದ್ದು ಕೂತುಕೊಂಡಳು.",
			"41": "<sup>41</sup> ಅವನು ಆಕೆಯನ್ನು ಕೈಕೊಟ್ಟು ಎಬ್ಬಿಸಿ ದೇವಜನರನ್ನೂ ವಿಧವೆಯರನ್ನೂ ಕರೆದು ಜೀವಿತಳಾದ ಆಕೆಯನ್ನು ಅವರೆದುರಿಗೆ ನಿಲ್ಲಿಸಿದನು.",
			"42": "<sup>42</sup> ಈ ಸಂಗತಿಯು ಯೊಪ್ಪದಲ್ಲೆಲ್ಲಾ ತಿಳಿಯಬಂದು ಅನೇಕರು ಕರ್ತನ ಮೇಲೆ ನಂಬಿಕೆಯಿಟ್ಟರು.",
			"43": "<sup>43</sup> ಆಮೇಲೆ ಪೇತ್ರನು ಯೊಪ್ಪದಲ್ಲಿ ಚರ್ಮಕಾರನಾದ ಸೀಮೋನನೆಂಬವನ ಬಳಿಯಲ್ಲಿ ಬಹಳ ದಿವಸದವರೆಗೂ ಇದ್ದನು."
		},
		"10": {
			"1": "<sup>1</sup> ಕೈಸರೈಯದಲ್ಲಿ ಇತಾಲ್ಯದ ಪಟಾಲಮೆನಿಸಿಕೊಳ್ಳುವ ಒಂದು ಪಟಾಲಮಿನ ಶತಾಧಿಪತಿಯಾದ ಕೊರ್ನೇಲ್ಯನೆಂಬ ಒಬ್ಬ ಮನುಷ್ಯನಿದ್ದನು.",
			"2": "<sup>2</sup> ಅವನು ಭಕ್ತನೂ ತನ್ನ ಮನೆಯವರೆಲ್ಲರ ಸಹಿತವಾಗಿ ದೇವರಿಗೆ ಭಯಪಡುವವನೂ ಆಗಿದ್ದು ಜನರಿಗೆ ಬಹಳವಾಗಿ ದಾನಧರ್ಮ ಮಾಡುತ್ತಾ ದೇವರಿಗೆ ನಿತ್ಯವೂ ಪ್ರಾರ್ಥನೆ ಮಾಡುತ್ತಾ ಇದ್ದನು.",
			"3": "<sup>3</sup> ಮಧ್ಯಾಹ್ನದ ಮೇಲೆ ಸುಮಾರು ಮೂರು ಗಂಟೆಗೆ ಅವನಿಗೆ ಒಂದು ದರ್ಶನ ಉಂಟಾಗಿ ಒಬ್ಬ ದೇವದೂತನು ತನ್ನ ಬಳಿಗೆ ಬಂದು - ಕೊರ್ನೇಲ್ಯನೇ ಎಂದು ಕರೆಯುವದನ್ನು ಅವನು ಪ್ರತ್ಯಕ್ಷವಾಗಿ ಕಂಡನು.",
			"4": "<sup>4</sup> ಅವನು ಆ ದೂತನನ್ನು ದೃಷ್ಟಿಸಿನೋಡಿ ಭಯಹಿಡಿದವನಾಗಿ - ಏನು ಸ್ವಾಮೀ ಎಂದು ಕೇಳಲು ದೂತನು ಅವನಿಗೆ - ನಿನ್ನ ಪ್ರಾರ್ಥನೆಗಳೂ ನಿನ್ನ ದಾನಧರ್ಮಗಳೂ ದೇವರ ಮುಂದೆ ಜ್ಞಾಪಕಾರ್ಥವಾಗಿ ಏರಿ ಬಂದವು.",
			"5": "<sup>5</sup> ಈಗ ನೀನು ಯೊಪ್ಪಕ್ಕೆ ಜನರನ್ನು ಕಳುಹಿಸಿ ಪೇತ್ರನೆನಿಸಿಕೊಳ್ಳುವ ಸೀಮೋನನನ್ನು ಕರೆಯಿಸಬೇಕು.",
			"6": "<sup>6</sup> ಅವನು ಚರ್ಮಕಾರನಾದ ಸೀಮೋನನ ಬಳಿಯಲ್ಲಿ ಇಳುಕೊಂಡಿದ್ದಾನೆ; ಈ ಸೀಮೋನನ ಮನೆಯು ಸಮುದ್ರದ ಬಳಿಯಲ್ಲಿ ಅದೆ ಎಂದು ಹೇಳಿದನು.",
			"7": "<sup>7</sup> ಕೊರ್ನೇಲ್ಯನು ತನ್ನ ಸಂಗಡ ಮಾತಾಡಿದ ದೇವದೂತನು ಹೊರಟುಹೋದ ಮೇಲೆ ತನ್ನ ಪರಿಚಾರಕರಲ್ಲಿ ಇಬ್ಬರನ್ನೂ ತನ್ನ ಹತ್ತಿರ ಯಾವಾಗಲೂ ಇರುತ್ತಿದ್ದ ಸಿಪಾಯಿಗಳಲ್ಲಿ ಒಬ್ಬ ದೇವಭಕ್ತನನ್ನೂ ಕರೆದು",
			"8": "<sup>8</sup> ಅವರಿಗೆ ಈ ಸಂಗತಿಗಳನ್ನೆಲ್ಲಾ ವಿವರವಾಗಿ ಹೇಳಿ ಅವರನ್ನು ಯೊಪ್ಪಕ್ಕೆ ಕಳುಹಿಸಿದನು.",
			"9": "<sup>9</sup> ಮರುದಿನ ಅವರು ಪ್ರಯಾಣಮಾಡಿ ಆ ಊರಿನ ಹತ್ತಿರಕ್ಕೆ ಬರುತ್ತಿರುವಾಗ ಪೇತ್ರನು ಸುಮಾರು ಮಧ್ಯಾಹ್ನದಲ್ಲಿ ಪ್ರಾರ್ಥನೆ ಮಾಡುವದಕ್ಕಾಗಿ ಮಾಳಿಗೆಯನ್ನು ಹತ್ತಿದನು.",
			"10": "<sup>10</sup> ಅವನು ಬಹಳ ಹಸಿದು ಊಟ ಮಾಡಬೇಕೆಂದಿದ್ದನು. ಊಟಕ್ಕೆ ಸಿದ್ಧವಾಗುತ್ತಿರುವಷ್ಟರಲ್ಲಿ",
			"11": "<sup>11</sup> ಅವನು ಧ್ಯಾನ ಪರವಶನಾಗಿ ಆಕಾಶವು ತೆರೆದಿರುವದನ್ನೂ ನಾಲ್ಕು ಮೂಲೆಗಳನ್ನು ಹಿಡಿದಿದ್ದ ದೊಡ್ಡ ಜೋಳಿಗೆಯಂತಿರುವ ಏನೋ ಒಂದು ವಸ್ತುವು ಭೂಮಿಯ ಮೇಲೆ ಇಳಿಯುವದನ್ನೂ ಕಂಡನು.",
			"12": "<sup>12</sup> ಅದರೊಳಗೆ ಸಕಲ ವಿಧವಾದ ಪಶುಗಳೂ ನೆಲದ ಮೇಲೆ ಹರಿದಾಡುವ ಕ್ರಿಮಿಕೀಟಗಳೂ ಅಂತರಿಕ್ಷದಲ್ಲಿ ಹಾರಾಡುವ ಹಕ್ಕಿಗಳೂ ಇದ್ದವು.",
			"13": "<sup>13</sup> ಆಗ - ಪೇತ್ರನೇ, ಎದ್ದು ಕೊಯ್ದು ತಿನ್ನು ಎಂದು ಅವನಿಗೆ ಒಂದು ವಾಣಿ ಕೇಳಿಸಿತು.",
			"14": "<sup>14</sup> ಅದಕ್ಕೆ ಪೇತ್ರನು - ಬೇಡವೇ ಬೇಡ ಸ್ವಾಮೀ, ನಾನು ಎಂದೂ ಹೊಲೆ ಪದಾರ್ಥವನ್ನಾಗಲಿ ಅಶುದ್ಧಪದಾರ್ಥವನ್ನಾಗಲಿ ತಿಂದವನಲ್ಲ ಅಂದನು.",
			"15": "<sup>15</sup> ದೇವರು ಶುದ್ಧಮಾಡಿದ್ದನ್ನು ನೀನು ಹೊಲೆ ಎನ್ನಬೇಡವೆಂದು ತಿರಿಗಿ ಎರಡನೆಯ ಸಾರಿ ಅವನಿಗೆ ವಾಣಿ ಕೇಳಿಸಿತು.",
			"16": "<sup>16</sup> ಹೀಗೆ ಮೂರು ಸಾರಿ ಆಯಿತು. ಆದ ಕೂಡಲೆ ಆ ವಸ್ತುವು ಆಕಾಶದೊಳಗೆ ಸೇರಿಕೊಂಡಿತು.",
			"17": "<sup>17</sup> ಪೇತ್ರನು ತನಗಾದ ದರ್ಶನವು ಏನಾಗಿರಬಹುದೆಂದು ತನ್ನಲ್ಲಿ ಕಳವಳ ಪಡುತ್ತಿರುವಾಗಲೇ ಕೊರ್ನೇಲ್ಯನು ಕಳುಹಿಸಿದ್ದ ಆ ಮನುಷ್ಯರು ಸೀಮೋನನ ಮನೆ ಯಾವದು ಎಂದು ಕೇಳಿಕೊಂಡು ಬಂದು ಆ ಮನೆಯ ಬಾಗಿಲಲ್ಲಿ ನಿಂತು -",
			"18": "<sup>18</sup> ಪೇತ್ರನೆನಿಸಿಕೊಳ್ಳುವ ಸೀಮೋನನು ಇಲ್ಲಿ ಇಳುಕೊಂಡಿದ್ದಾನೋ ಎಂದು ಕೂಗಿ ಕೇಳಿದರು.",
			"19": "<sup>19</sup> ಪೇತ್ರನು ಆ ದರ್ಶನದ ವಿಷಯದಲ್ಲಿ ಆಲೋಚನೆ ಮಾಡುತ್ತಿರಲು ದೇವರಾತ್ಮನು ಅವನಿಗೆ - ಅಗೋ, ಇಬ್ಬರು ಮನುಷ್ಯರು ನಿನ್ನನ್ನು ವಿಚಾರಿಸುತ್ತಾರೆ;",
			"20": "<sup>20</sup> ನೀನೆದ್ದು ಕೆಳಗಿಳಿದು ಏನೂ ಸಂಶಯಪಡದೆ ಅವರ ಜೊತೆಯಲ್ಲಿ ಹೋಗು, ನಾನೇ ಅವರನ್ನು ಕಳುಹಿಸಿದ್ದೇನೆ ಎಂದು ಹೇಳಿದನು.",
			"21": "<sup>21</sup> ಪೇತ್ರನು ಕೆಳಗಿಳಿದು ಆ ಮನುಷ್ಯರ ಬಳಿಗೆ ಬಂದು - ಇಗೋ, ನೀವು ವಿಚಾರಿಸುವವನು ನಾನೇ; ನೀವು ಬಂದ ಕಾರಣವೇನು ಎಂದು ಕೇಳಲು",
			"22": "<sup>22</sup> ಅವರು - ಕೊರ್ನೇಲ್ಯನೆಂಬ ಒಬ್ಬ ಶತಾಧಿಪತಿ ಇದ್ದಾನೆ. ಅವನು ನೀತಿವಂತನೂ ದೇವರಿಗೆ ಭಯಪಡುವವನೂ ಯೆಹೂದ್ಯ ಜನರೆಲ್ಲರಿಂದ ಒಳ್ಳೇ ಹೆಸರು ಹೊಂದಿದವನೂ ಆಗಿದ್ದಾನೆ; ನಿನ್ನನ್ನು ತನ್ನ ಮನೆಗೆ ಕರೇಕಳುಹಿಸಿಕೊಂಡು ನಿನ್ನಿಂದ ಬೋಧನೆಯನ್ನು ಕೇಳಬೇಕೆಂದು ದೇವದೂತನ ಮುಖಾಂತರವಾಗಿ ಅಪ್ಪಣೆ ಹೊಂದಿದನು ಎಂದು ಹೇಳಿದರು.",
			"23": "<sup>23</sup> ಪೇತ್ರನು ಇದನ್ನು ಕೇಳಿ ಅವರನ್ನು ಒಳಕ್ಕೆ ಕರೆದು ಉಪಚರಿಸಿದನು. ಮರುದಿನ ಅವನು ಎದ್ದು ಅವರ ಜೊತೆಯಲ್ಲಿ ಹೊರಟನು; ಯೊಪ್ಪದಲ್ಲಿದ್ದ ಸಹೋದರರಲ್ಲಿ ಕೆಲವರು ಅವನ ಕೂಡ ಹೋದರು.",
			"24": "<sup>24</sup> ಮರುದಿನ ಅವನು ಕೈಸರೈಯಕ್ಕೆ ಸೇರಿದನು. ಕೊರ್ನೇಲ್ಯನು ತನ್ನ ಬಂಧುಬಳಗದವರನ್ನೂ ಪ್ರಾಣಮಿತ್ರರನ್ನೂ ಕೂಡ ಕರಿಸಿ ಪೇತ್ರನನ್ನು ಎದುರುನೋಡುತ್ತಿದ್ದನು.",
			"25": "<sup>25</sup> ಅವನು ಬಂದಾಗ ಕೊರ್ನೇಲ್ಯನು ಅವನನ್ನು ಎದುರುಗೊಂಡು ಅವನ ಪಾದಕ್ಕೆ ಬಿದ್ದು ನಮಸ್ಕಾರ ಮಾಡಿದನು.",
			"26": "<sup>26</sup> ಆದರೆ ಪೇತ್ರನು - ಏಳಪ್ಪಾ, ನಾನೂ ಮನುಷ್ಯನು ಎಂದು ಹೇಳಿ",
			"27": "<sup>27</sup> ಅವನನ್ನು ಎತ್ತಿ ಅವನ ಸಂಗಡ ಸಂಭಾಷಣೆ ಮಾಡುತ್ತಾ ಮನೆಯೊಳಕ್ಕೆ ಬಂದನು.",
			"28": "<sup>28</sup> ಅಲ್ಲಿ ಬಹು ಜನರು ಕೂಡಿಬಂದಿರುವದನ್ನು ಕಂಡು ಅವರಿಗೆ - ಯೆಹೂದ್ಯರು ಅನ್ಯಜನರ ಕೂಡ ಹೊಕ್ಕು ಬಳಿಕೆಮಾಡುವದು ಯೆಹೂದ್ಯರ ಸಂಪ್ರದಾಯಕ್ಕೆ ವಿರುದ್ಧವಾಗಿದೆ ಎಂದು ನೀವು ಬಲ್ಲಿರಷ್ಟೆ. ನನಗಂತೂ ಯಾವ ಮನುಷ್ಯನನ್ನಾದರೂ ಹೊಲೆಯನು ಇಲ್ಲವೆ ಅಶುದ್ಧನು ಅನ್ನಬಾರದೆಂದು ದೇವರು ತೋರಿಸಿದ್ದಾನೆ.",
			"29": "<sup>29</sup> ಆದಕಾರಣ ನೀವು ನನ್ನನ್ನು ಕರೇಕಳುಹಿಸಿದಾಗ ನಾನು ಯಾವ ಎದುರು ಮಾತನ್ನೂ ಹೇಳದೆ ಬಂದೆನು. ಈಗ ನೀವು ನನ್ನನ್ನು ಕರಿಸಿದ ಉದ್ದೇಶವೇನು ನನಗೆ ತಿಳಿಸಬೇಕು ಎಂದು ಹೇಳಿದನು.",
			"30": "<sup>30</sup> ಅದಕ್ಕೆ ಕೊರ್ನೇಲ್ಯನು - ನಾನು ನಾಲ್ಕು ದಿವಸಗಳ ಹಿಂದೆ ಮಧ್ಯಾಹ್ನದ ಮೇಲೆ ಮೂರು ಗಂಟೆಗೆ ನಡೆಯತಕ್ಕ ದೇವರ ಪ್ರಾರ್ಥನೆಯನ್ನು ಇಷ್ಟು ಹೊತ್ತಿನವರೆಗೂ ನನ್ನ ಮನೆಯಲ್ಲಿ ಮಾಡುತ್ತಿದ್ದಾಗ ಹೊಳೆಯುವ ವಸ್ತ್ರವನ್ನು ಧರಿಸಿಕೊಂಡಿದ್ದ ಒಬ್ಬ ಮನುಷ್ಯನು ನನ್ನೆದುರಿಗೆ ನಿಂತುಕೊಂಡು -",
			"31": "<sup>31</sup> ಕೊರ್ನೇಲ್ಯನೇ, ನಿನ್ನ ಪ್ರಾರ್ಥನೆಯು ಕೇಳಿಬಂತು, ನಿನ್ನ ದಾನಧರ್ಮಗಳು ದೇವರ ಸನ್ನಿಧಾನದಲ್ಲಿ ನೆನಪಿಗೆ ಬಂದವು.",
			"32": "<sup>32</sup> ನೀನು ಯೊಪ್ಪಕ್ಕೆ ಕಳುಹಿಸಿ ಪೇತ್ರನೆನಿಸಿಕೊಳ್ಳುವ ಸೀಮೋನನನ್ನು ಕರಿಸಬೇಕು; ಅವನು ಚರ್ಮಕಾರನಾದ ಸೀಮೋನನ ಮನೆಯಲ್ಲಿ ಇಳುಕೊಂಡಿದ್ದಾನೆ; ಆ ಮನೆ ಸಮುದ್ರದ ಬಳಿಯಲ್ಲಿ ಅದೆ ಅಂದನು.",
			"33": "<sup>33</sup> ನಾನು ತಡಮಾಡದೆ ನಿನ್ನ ಹತ್ತರ ಕಳುಹಿಸಿದೆನು. ನೀನು ಬಂದದ್ದು ಉಪಕಾರವಾಯಿತು. ಹೀಗಿರುವದರಿಂದ ಕರ್ತನು ನಿನಗೆ ಅಪ್ಪಣೆ ಕೊಟ್ಟಿರುವ ಎಲ್ಲಾ ಮಾತುಗಳನ್ನು ಕೇಳುವದಕ್ಕೆ ನಾವೆಲ್ಲರು ಈಗ ದೇವರ ಸನ್ನಿಧಾನದಲ್ಲಿ ಕೂಡಿದ್ದೇವೆ ಅಂದನು.",
			"34": "<sup>34</sup> ಆಗ ಪೇತ್ರನು ಉಪದೇಶಮಾಡಲಾರಂಭಿಸಿ ಹೇಳಿದ್ದೇನಂದರೆ -",
			"35": "<sup>35</sup> ದೇವರು ಪಕ್ಷಪಾತಿಯಲ್ಲ, ಯಾವ ಜನರಲ್ಲಿಯಾದರೂ ದೇವರಿಗೆ ಭಯಪಟ್ಟು ನೀತಿಯನ್ನು ನಡಿಸುವವರು ಆತನಿಗೆ ಮೆಚ್ಚಿಗೆಯಾಗಿದ್ದಾರೆಂದು ಈಗ ಸಂದೇಹವಿಲ್ಲದೆ ನನಗೆ ತಿಳಿದುಬಂದಿದೆ.",
			"36": "<sup>36</sup> ಎಲ್ಲಾ ಜನಗಳಿಗೂ ಕರ್ತನಾಗಿರುವ ಯೇಸು ಕ್ರಿಸ್ತನ ಮೂಲಕ ದೇವರು ಸಮಾಧಾನದ ಶುಭಸಮಾಚಾರವನ್ನು ಪ್ರಕಟಿಸುತ್ತಾ ಇಸ್ರಾಯೇಲ್ ಜನರಿಗೆ ಅನುಗ್ರಹಿದ ವಾಕ್ಯವು ನಿಮಗೇ ತಿಳಿದಿರುವದು.",
			"37": "<sup>37</sup> ದೀಕ್ಷಾಸ್ನಾನ ಮಾಡಿಸಿಕೊಳ್ಳಬೇಕೆಂದು ಯೋಹಾನನು ಸಾರಿದ ತರುವಾಯ ಈ ವಾಕ್ಯವು ಗಲಿಲಾಯದಿಂದ ಪ್ರಾರಂಭವಾಗಿ ಯೂದಾಯದಲ್ಲೆಲ್ಲಾ ಪ್ರಬಲವಾಯಿತು.",
			"38": "<sup>38</sup> ದೇವರು ನಜರೇತಿನ ಯೇಸುವನ್ನು ಪವಿತ್ರಾತ್ಮದಿಂದಲೂ ಬಲದಿಂದಲೂ ಅಭಿಷೇಕಿಸಿದನು; ದೇವರು ಆತನ ಸಂಗಡ ಇದ್ದದರಿಂದ ಆತನು ಉಪಕಾರಗಳನ್ನು ಮಾಡುತ್ತಾ ಸೈತಾನನಿಂದ ಬಾಧಿಸಲ್ಪಡುತ್ತಿರುವವರೆಲ್ಲರನ್ನು ಗುಣಮಾಡುತ್ತಾ ಸಂಚರಿಸಿದನು; ಇದೆಲ್ಲಾ ನಿಮಗೆ ಗೊತ್ತಾಗಿರುವದಷ್ಟೆ.",
			"39": "<sup>39</sup> ಆತನು ಯೆಹೂದ್ಯರ ಸೀಮೆಯಲ್ಲಿಯೂ ಯೆರೂಸಲೇಮಿನಲ್ಲಿಯೂ ಮಾಡಿದ ಎಲ್ಲಾ ಕಾರ್ಯಗಳಿಗೆ ನಾವು ಸಾಕ್ಷಿಗಳಾಗಿದ್ದೇವೆ.",
			"40": "<sup>40</sup> ಆತನನ್ನು ಮರದ ಕಂಬಕ್ಕೆ ತೂಗಹಾಕಿ ಕೊಂದರು.",
			"41": "<sup>41</sup> ದೇವರು ಆತನನ್ನು ಮೂರನೆಯ ದಿನದಲ್ಲಿ ಎಬ್ಬಿಸಿ ಪ್ರತ್ಯಕ್ಷನಾಗುವಂತೆ ಮಾಡಿದನು; ಆತನು ಎಲ್ಲರಿಗೆ ಪ್ರತ್ಯಕ್ಷನಾಗದೆ ದೇವರು ಮುಂಚಿತವಾಗಿ ಆರಿಸಿಕೊಂಡಿದ್ದ ಸಾಕ್ಷಿಗಳಾದ ನಮಗೆ ಪ್ರತ್ಯಕ್ಷನಾದನು. ಆತನು ಸತ್ತವರೊಳಗಿಂದ ಜೀವಿತನಾಗಿ ಎದ್ದು ಬಂದ ಮೇಲೆ ನಾವು ಆತನ ಸಂಗಡ ಅನ್ನಪಾನಗಳನ್ನು ತಕ್ಕೊಂಡೆವು.",
			"42": "<sup>42</sup> ಆತನೇ ಜೀವಿತರಿಗೂ ಸತ್ತವರಿಗೂ ನ್ಯಾಯಾಧಿಪತಿಯಾಗಿ ದೇವರಿಂದ ನೇಮಕವಾದವನು ಎಂಬದನ್ನು ಜನರಿಗೆ ಸಾರಿ ಸಾಕ್ಷಿಹೇಳಬೇಕೆಂದು ದೇವರು ನಮಗೆ ಅಪ್ಪಣೆಕೊಟ್ಟನು.",
			"43": "<sup>43</sup> ಆತನಲ್ಲಿ ನಂಬಿಕೆಯಿಡುವ ಪ್ರತಿಯೊಬ್ಬನು ಆತನ ಹೆಸರಿನ ಮೂಲಕವಾಗಿ ಪಾಪಪರಿಹಾರವನ್ನು ಹೊಂದುವನೆಂದು ಆತನ ವಿಷಯದಲ್ಲಿ ಪ್ರವಾದಿಗಳೆಲ್ಲರು ಸಾಕ್ಷಿಹೇಳಿದ್ದಾರೆ ಅಂದನು.",
			"44": "<sup>44</sup> ಪೇತ್ರನು ಈ ಮಾತುಗಳನ್ನು ಇನ್ನೂ ಹೇಳುತ್ತಿರುವಾಗ ಅವನ ವಾಕ್ಯವನ್ನು ಕೇಳಿದವರೆಲ್ಲರ ಮೇಲೆ ಪವಿತ್ರಾತ್ಮವರವು ಇಳಿಯಿತು.",
			"45": "<sup>45</sup> ಅವರು ನಾನಾ ಭಾಷೆಗಳನ್ನಾಡುತ್ತಾ ದೇವರನ್ನು ಕೊಂಡಾಡುತ್ತಾ ಇರುವದನ್ನು ಪೇತ್ರನ ಸಂಗಡ ಬಂದಿದ್ದ ಯೆಹೂದ್ಯರಾದ ವಿಶ್ವಾಸಿಗಳು ಕೇಳಿದಾಗ - ಅನ್ಯಜನಗಳಿಗೂ ಪವಿತ್ರಾತ್ಮ ದಾನ ಮಾಡಲ್ಪಟ್ಟಿದೆಯಲ್ಲಾ ಎಂದು ಅತ್ಯಾಶ್ಚರ್ಯಪಟ್ಟರು. ನಡೆದ ಸಂಗತಿಯನ್ನು ಪೇತ್ರನು ನೋಡಿ -",
			"47": "<sup>47</sup> ನಮ್ಮ ಹಾಗೆಯೇ ಪವಿತ್ರಾತ್ಮವರವನ್ನು ಹೊಂದಿದ ಇವರಿಗೆ ನೀರಿನ ದೀಕ್ಷಾಸ್ನಾನವಾಗದಂತೆ ಯಾರಾದರೂ ಅಭ್ಯಂತರ ಮಾಡಾರೇ ಎಂದು ಹೇಳಿ",
			"48": "<sup>48</sup> ಅವರಿಗೆ - ನೀವು ಯೇಸು ಕ್ರಿಸ್ತನ ಹೆಸರಿನಲ್ಲಿ ದೀಕ್ಷಾಸ್ನಾನ ಮಾಡಿಸಿಕೊಳ್ಳಬೇಕೆಂದು ಅಪ್ಪಣೆಕೊಟ್ಟನು. ಆಮೇಲೆ ಅವರು ಅವನನ್ನು - ಇನ್ನೂ ಕೆಲವು ದಿವಸ ಇರಬೇಕೆಂದು ಬೇಡಿಕೊಂಡರು."
		},
		"11": {
			"1": "<sup>1</sup> ಅನ್ಯಜನರು ಸಹ ದೇವರ ವಾಕ್ಯವನ್ನು ಅಂಗೀಕರಿಸಿದರೆಂಬ ಸಮಾಚಾರವನ್ನು ಅಪೊಸ್ತಲರೂ ಯೂದಾಯದಲ್ಲಿದ್ದ ಸಹೋದರರೂ ಕೇಳಿದರು.",
			"2": "<sup>2</sup> ಪೇತ್ರನು ಯೆರೂಸಲೇಮಿಗೆ ಬಂದಾಗ ಸುನ್ನತಿಯವರು - ನೀನು ಸುನ್ನತಿಯಿಲ್ಲದವರಲ್ಲಿ ಸೇರಿ ಅವರ ಸಂಗಡ ಊಟಮಾಡಿದೆಯಲ್ಲಾ ಎಂದು ಅವನ ಕೂಡ ವಿವಾದಿಸಿದರು.",
			"4": "<sup>4</sup> ಪೇತ್ರನು ನಡೆದ ಸಂಗತಿಯನ್ನು ಅವರಿಗೆ ಮೊದಲಿಂದ ಕ್ರಮವಾಗಿ ವಿವರಿಸಿ ಹೇಳಿದ್ದೇನಂದರೆ -",
			"5": "<sup>5</sup> ನಾನು ಯೊಪ್ಪ ಪಟ್ಟಣದಲ್ಲಿ ಪ್ರಾರ್ಥನೆಮಾಡುತ್ತಿದ್ದಾಗ ಧ್ಯಾನಪರವಶನಾಗಿದ್ದ ನನಗೆ ಒಂದು ದರ್ಶನವಾಯಿತು; ಅದೇನಂದರೆ ಆಕಾಶದಿಂದ ನಾಲ್ಕು ಮೂಲೆಗಳನ್ನು ಹಿಡಿದಿದ್ದ ದೊಡ್ಡ ಜೋಳಿಗೆಯಂತಿರುವ ಒಂದು ವಸ್ತುವು ಇಳಿಸಲ್ಪಟ್ಟು ನಾನಿದ್ದಲ್ಲಿಗೆ ಬಂತು.",
			"6": "<sup>6</sup> ಅದೇನೆಂದು ತಿಳುಕೊಳ್ಳುವದಕ್ಕೆ ನಾನು ಚೆನ್ನಾಗಿ ನೋಡಿದಾಗ ನೆಲದ ಮೇಲೆ ತಿರುಗಾಡುವ ಪಶು ಮೃಗ ಕ್ರಿಮಿಕೀಟಗಳನ್ನೂ ಅಂತರಿಕ್ಷದಲ್ಲಿ ಹಾರಾಡುವ ಹಕ್ಕಿಗಳನ್ನೂ ಕಂಡೆನು.",
			"7": "<sup>7</sup> ಆಗ - ಪೇತ್ರನೇ, ಎದ್ದು ಕೊಯ್ದು ತಿನ್ನು ಎಂದು ನನಗೆ ಹೇಳುವ ವಾಣಿಯನ್ನು ಕೇಳಿದೆನು.",
			"8": "<sup>8</sup> ನಾನು - ಬೇಡವೇ ಬೇಡ, ಸ್ವಾಮೀ, ಹೊಲೆಯಾದ ಪದಾರ್ಥವಾಗಲಿ ಅಶುದ್ಧ ಪದಾರ್ಥವಾಗಲಿ ಎಂದೂ ನನ್ನ ಬಾಯೊಳಗೆ ಸೇರಿದ್ದಿಲ್ಲ ಅಂದೆನು.",
			"9": "<sup>9</sup> ಅದಕ್ಕೆ - ದೇವರು ಶುದ್ಧಮಾಡಿದ್ದನ್ನು ನೀನು ಹೊಲೆ ಎನ್ನಬಾರದು ಎಂಬದಾಗಿ ಎರಡನೆಯ ಸಾರಿ ಆಕಾಶವಾಣಿಯಾಯಿತು.",
			"10": "<sup>10</sup> ಹೀಗೆ ಮೂರು ಸಾರಿ ಆಯಿತು. ಆಮೇಲೆ ಎಲ್ಲವೂ ತಿರಿಗಿ ಆಕಾಶಕ್ಕೆ ಎತ್ತಲ್ಪಟ್ಟಿತು.",
			"11": "<sup>11</sup> ಆ ಕ್ಷಣದಲ್ಲೇ ಮೂರು ಮಂದಿ ಮನುಷ್ಯರು ನಾವಿದ್ದ ಮನೆಯ ಮುಂದೆ ನಿಂತಿದ್ದರು. ಅವರು ಕೈಸರೈಯದಿಂದ ನನ್ನ ಹತ್ತರಕ್ಕೆ ಕಳುಹಿಸಲ್ಪಟ್ಟವರು.",
			"12": "<sup>12</sup> ದೇವರಾತ್ಮನು - ನೀನು ಏನೂ ಭೇದಮಾಡದೆ ಅವರ ಜೊತೆಯಲ್ಲಿ ಹೋಗು ಎಂದು ನನಗೆ ಹೇಳಿದನು. ಈ ಆರು ಮಂದಿ ಸಹೋದರರು ಸಹ ನನ್ನ ಸಂಗಡ ಬಂದರು.",
			"13": "<sup>13</sup> ಆ ಮನುಷ್ಯನ ಮನೆಯೊಳಗೆ ನಾವು ಸೇರಲು ಅವನು ನಮಗೆ - ಒಬ್ಬ ದೇವದೂತನು ನನ್ನ ಮನೆಯಲ್ಲಿ ನಿಂತಿರುವದನ್ನು ಕಂಡೆನು. ಆ ದೂತನು - ನೀನು ಯೊಪ್ಪಕ್ಕೆ ಕಳುಹಿಸಿ ಪೇತ್ರನೆನಿಸಿಕೊಳ್ಳುವ ಸೀಮೋನನನ್ನು ಕರಿಸು;",
			"14": "<sup>14</sup> ಅವನು ನಿನಗೆ ಕೆಲವು ಮಾತುಗಳನ್ನು ಹೇಳುವನು, ಆ ಮಾತುಗಳಿಂದ ನಿನಗೂ ನಿನ್ನ ಮನೆಯವರೆಲ್ಲರಿಗೂ ರಕ್ಷಣೆಯಾಗುವದು ಎಂದು ಹೇಳಿದನೆಂಬದಾಗಿ ತಿಳಿಸಿದನು.",
			"15": "<sup>15</sup> ನಾನು ಮಾತಾಡುವದಕ್ಕೆ ಪ್ರಾರಂಭಿಸಿದಾಗ ಪವಿತ್ರಾತ್ಮ ವರವು ಮೊದಲು ನಮ್ಮ ಮೇಲೆ ಇಳಿದಂತೆ ಅವರ ಮೇಲೆಯೂ ಇಳಿಯಿತು.",
			"16": "<sup>16</sup> ಆಗ - ಯೋಹಾನನಂತೂ ನೀರಿನ ದೀಕ್ಷಾಸ್ನಾನಮಾಡಿಸಿದನು; ನಿಮಗಾದರೋ ಪವಿತ್ರಾತ್ಮದಲ್ಲಿ ಸ್ನಾನವಾಗುವದು ಎಂದು ಯೇಸುಸ್ವಾಮಿ ಹೇಳಿದ ಮಾತು ನನ್ನ ನೆನಪಿಗೆ ಬಂತು.",
			"17": "<sup>17</sup> ದೇವರು ಕರ್ತನಾಗಿರುವ ಯೇಸು ಕ್ರಿಸ್ತನಲ್ಲಿ ನಂಬಿಕೆಯಿಟ್ಟ ನಮಗೆ ಕೊಟ್ಟ ವರಕ್ಕೆ ಸರಿಯಾದ ವರವನ್ನು ಅವರಿಗೂ ಕೊಟ್ಟಿರಲಾಗಿ ದೇವರನ್ನು ತಡೆಯುವದಕ್ಕೆ ನಾನು ಶಕ್ತನೋ?",
			"18": "<sup>18</sup> ಹೀಗೆಂದ ಮಾತುಗಳನ್ನು ಆ ಸುನ್ನತಿಯವರು ಕೇಳಿ ಆಕ್ಷೇಪಣೆ ಮಾಡುವದನ್ನು ಬಿಟ್ಟು - ಹಾಗಾದರೆ ದೇವರು ಅನ್ಯಜನರಿಗೂ ಜೀವಕೊಡಬೇಕೆಂದು ಅವರ ಮನಸ್ಸುಗಳನ್ನು ತನ್ನ ಕಡೆಗೆ ತಿರುಗಿಸಿದ್ದಾನೆ ಎಂದು ದೇವರನ್ನು ಕೊಂಡಾಡಿದರು.",
			"19": "<sup>19</sup> ಸ್ತೆಫನನ ವಿಷಯದಲ್ಲಿ ಉಂಟಾದ ಹಿಂಸೆಯಿಂದ ಚದರಿಹೋದವರು ಯೆಹೂದ್ಯರಿಗೇ ಹೊರತು ಮತ್ತಾರಿಗೂ ವಾಕ್ಯವನ್ನು ಹೇಳದೆ ಫೊಯಿನಿಕೆ ಕುಪ್ರ ಅಂತಿಯೋಕ್ಯ ಪ್ರಾಂತ್ಯಗಳವರೆಗೂ ಸಂಚರಿಸಿದರು.",
			"20": "<sup>20</sup> ಅವರಲ್ಲಿ ಕುಪ್ರದ್ವೀಪದವರು ಕೆಲವರೂ ಕುರೇನ್ಯದವರು ಕೆಲವರೂ ಅಂತಿಯೋಕ್ಯಕ್ಕೆ ಬಂದು ಗ್ರೀಕರ ಸಂಗಡಲೂ ಮಾತಾಡಿ ಕರ್ತನಾದ ಯೇಸುವಿನ ವಿಷಯವಾದ ಸುವಾರ್ತೆಯನ್ನು ತಿಳಿಸಿದರು.",
			"21": "<sup>21</sup> ಕರ್ತನ ಹಸ್ತವು ಅವರಿಗೆ ಸಹಾಯವಾದ್ದರಿಂದ ಬಹು ಜನರು ನಂಬಿ ಕರ್ತನ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡರು.",
			"22": "<sup>22</sup> ಇವರ ವಿಷಯವಾದ ವರ್ತಮಾನವು ಯೆರೂಸಲೇಮಿನಲ್ಲಿದ್ದ ಸಭೆಯವರ ಕಿವಿಗೆ ಬಿದ್ದಾಗ ಅವರು ಬಾರ್ನಬನನ್ನು ಅಂತಿಯೋಕ್ಯಕ್ಕೆ ಕಳುಹಿಸಿದರು.",
			"23": "<sup>23</sup> ಅವನು ಒಳ್ಳೆಯವನೂ ಪವಿತ್ರಾತ್ಮಭರಿತನೂ ನಂಬಿಕೆಯಿಂದ ತುಂಬಿದವನೂ ಆಗಿದ್ದನು. ಆದಕಾರಣ ಅಲ್ಲಿಗೆ ಬಂದು ದೇವರ ಕೃಪಾಕಾರ್ಯವನ್ನು ನೋಡಿದಾಗ ಸಂತೋಷಪಟ್ಟು - ನೀವು ದೃಢಮನಸ್ಸಿನಿಂದ ಕರ್ತನಲ್ಲಿ ನೆಲೆಗೊಂಡಿರ್ರಿ ಎಂದು ಅವರೆಲ್ಲರಿಗೆ ಬುದ್ಧಿ ಹೇಳಿದನು.",
			"25": "<sup>25</sup> ಆಗ ಬಹುಮಂದಿ ಕರ್ತನಿಗೆ ಸೇರಿಕೊಂಡರು. ತರುವಾಯ ಬಾರ್ನಬನು ಸೌಲನನ್ನು ಹುಡುಕುವದಕ್ಕಾಗಿ ತಾರ್ಸಕ್ಕೆ ಹೋಗಿ",
			"26": "<sup>26</sup> ಅವನನ್ನು ಕಂಡು ಅಂತಿಯೋಕ್ಯಕ್ಕೆ ಕರೆದುಕೊಂಡು ಬಂದನು. ಆಮೇಲೆ ಅವರು ಪೂರಾ ಒಂದು ವರುಷ ಸಭೆಯವರ ಸಂಗಡ ಕೂಡಿದ್ದು ಬಹು ಜನರಿಗೆ ಉಪದೇಶಮಾಡಿದರು. ಅಂತಿಯೋಕ್ಯದಲ್ಲಿಯೇ ಶಿಷ್ಯರಿಗೆ ಕ್ರೈಸ್ತರೆಂಬ ಹೆಸರು ಮೊದಲು ಬಂತು.",
			"27": "<sup>27</sup> ಆ ಕಾಲದಲ್ಲಿ ಪ್ರವಾದಿಗಳಾಗಿದ್ದ ಕೆಲವರು ಯೆರೂಸಲೇಮಿನಿಂದ ಅಂತಿಯೋಕ್ಯಕ್ಕೆ ಬಂದರು.",
			"28": "<sup>28</sup> ಅವರಲ್ಲಿ ಅಗಬನೆಂಬವನೊಬ್ಬನು ಎದ್ದು ಲೋಕಕ್ಕೆಲ್ಲಾ ದೊಡ್ಡ ಕ್ಷಾಮ ಬರುವದು ಎಂದು ಪವಿತ್ರಾತ್ಮ ಪ್ರೇರಣೆಯಿಂದ ಸೂಚಿಸಿದನು. ಅದು ಕ್ಲೌದ್ಯಚಕ್ರವರ್ತಿಯ ಕಾಲದಲ್ಲಿ ಉಂಟಾಯಿತು;",
			"29": "<sup>29</sup> ಆಗ ಆ ಶಿಷ್ಯರಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬರು ಯೂದಾಯ ಸೀಮೆಯಲ್ಲಿ ವಾಸವಾಗಿದ್ದ ಸಹೋದರರಿಗೆ ತಮ್ಮ ತಮ್ಮ ಶಕ್ತ್ಯನುಸಾರ ದ್ರವ್ಯ ಸಹಾಯಮಾಡಬೇಕೆಂದು ನಿಶ್ಚಯಿಸಿಕೊಂಡರು.",
			"30": "<sup>30</sup> ಹಾಗೆಯೇ ಮಾಡಿ ಅದನ್ನು ಬಾರ್ನಬ ಸೌಲರ ಕೈಯಿಂದ ಸಭೆಯ ಹಿರಿಯರಿಗೆ ಕಳುಹಿಸಿದರು."
		},
		"12": {
			"1": "<sup>1</sup> ಆ ಸಮಯದಲ್ಲೇ ಅರಸನಾದ ಹೆರೋದನು ಸಭೆಯವರಲ್ಲಿ ಕೆಲವರನ್ನು ಹಿಂಸೆಪಡಿಸುವದಕ್ಕೆ ಕೈಹಾಕಿ",
			"2": "<sup>2</sup> ಯೋಹಾನನ ಅಣ್ಣನಾದ ಯಾಕೋಬನನ್ನು ಕತ್ತಿಯಿಂದ ಕೊಲ್ಲಿಸಿದನು.",
			"3": "<sup>3</sup> ಇದು ಯೆಹೂದ್ಯರಿಗೆ ಮೆಚ್ಚಿಕೆಯಾಗಿದೆ ಎಂದು ನೋಡಿ ಅಷ್ಟಕ್ಕೆ ನಿಲ್ಲದೆ ಪೇತ್ರನನ್ನೂ ಹಿಡಿಸಿದನು. ಆ ಕಾಲದಲ್ಲಿ ಹುಳಿಯಿಲ್ಲದ ರೊಟ್ಟಿಯ ಹಬ್ಬ ನಡೆಯುತ್ತಿತ್ತು.",
			"4": "<sup>4</sup> ಅವನನ್ನು ಪಸ್ಕಹಬ್ಬವಾದ ಮೇಲೆ ಜನರ ಮುಂದೆ ತರಿಸಬೇಕೆಂಬ ಯೋಚನೆಯಿಂದ ಸೆರೆಯಲ್ಲಿ ಹಾಕಿಸಿ ಅವನನ್ನು ಕಾಯಲಿಕ್ಕೆ ನಾಲ್ಕು ನಾಲ್ಕು ಸಿಪಾಯಿಗಳಿದ್ದ ನಾಲ್ಕು ಗುಂಪುಗಳ ವಶಕ್ಕೆ ಕೊಟ್ಟನು.",
			"5": "<sup>5</sup> ಪೇತ್ರನು ಸೆರೆಮನೆಯೊಳಗೆ ಕಾವಲಲ್ಲಿದ್ದಾಗ ಸಭೆಯವರು ಆತನಿಗೋಸ್ಕರ ಆಸಕ್ತಿಯಿಂದ ದೇವರಿಗೆ ಪ್ರಾರ್ಥನೆಮಾಡುತ್ತಿದ್ದರು.",
			"6": "<sup>6</sup> ಹೆರೋದನು ಅವನನ್ನು ಜನರ ಮುಂದೆ ತರಿಸಬೇಕೆಂದಿದ್ದ ದಿವಸದ ಹಿಂದಿನ ರಾತ್ರಿಯಲ್ಲಿ ಪೇತ್ರನು ಎರಡು ಸರಪಣಿಗಳಿಂದ ಕಟ್ಟಲ್ಪಟ್ಟವನಾಗಿ ಇಬ್ಬರು ಸಿಪಾಯಿಗಳ ನಡುವೆ ನಿದ್ದೆ ಮಾಡುತ್ತಿದ್ದನು. ಕಾವಲುಗಾರರು ಬಾಗಿಲ ಮುಂದೆ ನಿಂತು ಸೆರೆಮನೆಯನ್ನು ಕಾಯುತ್ತಿದ್ದರು.",
			"7": "<sup>7</sup> ಫಕ್ಕನೆ ಕರ್ತನ ದೂತನು ಪೇತ್ರನೆದುರಿಗೆ ನಿಂತನು. ಅವನಿದ್ದ ಕೋಣೆಯಲ್ಲಿ ಬೆಳಕು ಹೊಳೆಯಿತು. ಅವನು ಪೇತ್ರನನ್ನು ಪಕ್ಕೆ ತಟ್ಟಿ ಎಬ್ಬಿಸಿ - ತಟ್ಟನೆ ಏಳು ಅಂದನು. ಆಗಲೇ ಅವನ ಕೈಗಳಿಗೆ ಹಾಕಿದ್ದ ಸರಪಣಿಗಳು ಕಳಚಿಬಿದ್ದವು.",
			"8": "<sup>8</sup> ಆ ದೂತನು ಅವನಿಗೆ - ನಡುಕಟ್ಟಿಕೊಂಡು ನಿನ್ನ ಕೆರಗಳನ್ನು ಮೆಟ್ಟಿಕೋ ಎಂದು ಹೇಳಲು ಅವನು ಹಾಗೇ ಮಾಡಿದನು. ನಿನ್ನ ಮೇಲಂಗಿಯನ್ನು ಹಾಕಿಕೊಂಡು ನನ್ನ ಹಿಂದೆ ಬಾ ಅಂದನು.",
			"9": "<sup>9</sup> ಅವನು ಹೊರಗೆ ಬಂದು ಆ ದೂತನ ಹಿಂದೆಯೇ ಹೋಗುತ್ತಾ ದೂತನ ಮೂಲಕವಾಗಿ ನಡೆದ ಸಂಗತಿ ನಿಜವಾದದ್ದೆಂದು ತಿಳಿಯದೆ ತಾನು ನೋಡಿದ್ದು ಕನಸು ಎಂದು ನೆನಸಿದನು.",
			"10": "<sup>10</sup> ಅವರು ಮೊದಲನೆಯ ಎರಡನೆಯ ಕಾವಲುಗಳನ್ನು ದಾಟಿ ಪಟ್ಟಣಕ್ಕೆ ಹೋಗುವ ಕಬ್ಬಿಣದ ಬಾಗಿಲಿಗೆ ಬಂದಾಗ ಅದು ತನ್ನಷ್ಟಕ್ಕೆ ತಾನೇ ಅವರಿಗೆ ತೆರೆಯಿತು. ಅವರು ಹೊರಗೆ ಬಂದು ಒಂದು ಬೀದಿಯನ್ನು ದಾಟಿದರು; ಕೂಡಲೇ ಆ ದೂತನು ಅವನನ್ನು ಬಿಟ್ಟು ಹೋದನು.",
			"11": "<sup>11</sup> ಆಗ ಪೇತ್ರನು ಎಚ್ಚರವಾಗಿ - ಕರ್ತನು ತನ್ನ ದೂತನನ್ನು ಕಳುಹಿಸಿ ಹೆರೋದನ ಕೈಯಿಂದಲೂ ಯೆಹೂದ್ಯಜನರು ನನಗೆ ಮಾಡಬೇಕೆಂದಿದ್ದ ಕೇಡಿನಿಂದಲೂ ನನ್ನನ್ನು ಬಿಡಿಸಿದನೆಂದು ನನಗೀಗ ನಿಜವಾಗಿ ತಿಳಿದುಬಂದಿದೆ ಅಂದುಕೊಂಡನು.",
			"12": "<sup>12</sup> ತರುವಾಯ ಅವನು ಯೋಚನೆಮಾಡಿಕೊಂಡು ಮಾರ್ಕನೆನಿಸಿಕೊಳ್ಳುವ ಯೋಹಾನನ ತಾಯಿಯಾದ ಮರಿಯಳ ಮನೆಗೆ ಬಂದನು. ಅಲ್ಲಿ ಅನೇಕರು ಕೂಡಿಬಂದು ಪ್ರಾರ್ಥನೆಮಾಡುತ್ತಿದ್ದರು.",
			"13": "<sup>13</sup> ಪೇತ್ರನು ಬಾಗಿಲಿನ ಕದವನ್ನು ತಟ್ಟಲು ರೋದೆ ಎಂಬ ಒಬ್ಬ ಚಾಕರಿಯವಳು ನೋಡುವದಕ್ಕೆ ಬಂದಳು.",
			"14": "<sup>14</sup> ಅವಳು ಪೇತ್ರನ ಧ್ವನಿಯನ್ನು ಗೊತ್ತುಹಿಡಿದು ಸಂತೋಷದ ನಿಮಿತ್ತದಿಂದಲೇ ಬಾಗಿಲನ್ನು ತೆರೆಯದೆ ಒಳಕ್ಕೆ ಓಡಿಹೋಗಿ - ಪೇತ್ರನು ಬಾಗಿಲ ಮುಂದೆ ನಿಂತಿದ್ದಾನೆ ಎಂದು ತಿಳಿಸಿದಳು. ಅವರು ಅವಳಿಗೆ - ನಿನಗೆ ಹುಚ್ಚು ಹಿಡಿದದೆ ಎಂದು ಹೇಳಿದರು.",
			"15": "<sup>15</sup> ಆದರೆ ಅವಳು ತಾನು ಹೇಳಿದಂತೆಯೇ ಅದೆ ಎಂದು ದೃಢವಾಗಿ ಹೇಳುತ್ತಾ ಇರಲು ಅವನ ದೂತನಾಗಿರಬೇಕು ಅಂದರು.",
			"16": "<sup>16</sup> ಅಷ್ಟರಲ್ಲಿ ಪೇತ್ರನು ಇನ್ನೂ ತಟ್ಟುತ್ತಾ ಇರಲು ಅವರು ಬಾಗಿಲನ್ನು ತೆರೆದು ಅವನನ್ನು ಕಂಡು ಬೆರಗಾದರು.",
			"17": "<sup>17</sup> ಆದರೆ ಅವನು ಸುಮ್ಮಗಿರ್ರಿ ಎಂದು ಅವರಿಗೆ ಕೈಸನ್ನೆ ಮಾಡಿ ತನ್ನನ್ನು ಕರ್ತನು ಸೆರೆಮನೆಯೊಳಗಿಂದ ಹೊರಗೆ ಕರೆದುಕೊಂಡು ಬಂದ ರೀತಿಯನ್ನು ವಿವರಿಸಿ ಈ ಸಂಗತಿಗಳನ್ನು ಯಾಕೋಬನಿಗೂ ಸಹೋದರರೆಲ್ಲರಿಗೂ ತಿಳಿಸಿರೆಂದು ಹೇಳಿ ಬೇರೆ ಸ್ಥಳಕ್ಕೆ ಹೊರಟುಹೋದನು.",
			"18": "<sup>18</sup> ಬೆಳಗಾದ ಮೇಲೆ ಪೇತ್ರನು ಏನಾದನೆಂದು ಸಿಪಾಯಿಗಳಲ್ಲಿ ಬಹಳ ಕಳವಳ ಉಂಟಾಯಿತು.",
			"19": "<sup>19</sup> ಹೆರೋದನು ಅವನನ್ನು ಹುಡುಕಿಸಿ ಅವನು ಸಿಕ್ಕಲಿಲ್ಲವಾದದರಿಂದ ಕಾವಲುಗಾರರನ್ನು ವಿಚಾರಣೆಮಾಡಿ ಅವರಿಗೆ ಮರಣದಂಡನೆಯನ್ನು ವಿಧಿಸಿದನು. ಬಳಿಕ ಯೂದಾಯದಿಂದ ಕೈಸರೈಯಕ್ಕೆ ಹೋಗಿ ಅಲ್ಲಿ ಕೆಲವು ಕಾಲ ಇದ್ದನು.",
			"20": "<sup>20</sup> ತೂರ್ ಸೀದೋನ್ ಪಟ್ಟಣಗಳ ನಿವಾಸಿಗಳು ತಮ್ಮ ಮೇಲೆ ಹೆರೋದನು ಬಹಳವಾಗಿ ಕೋಪಿಸಿಕೊಂಡಿರುವದನ್ನು ನೋಡಿ ಒಂದೇ ಮನಸ್ಸಿನಿಂದ ಅವನ ಸನ್ನಿಧಿಗೆ ಬಂದು ಅರಸನ ಅಂತಃಪುರದ ಮೇಲಣ ಅಧಿಕಾರಿಯಾದ ಬ್ಲಾಸ್ತನನ್ನು ಒಲಿಸಿಕೊಂಡು ಸಮಾಧಾನವಾಗಬೇಕೆಂದು ಅರಸನನ್ನು ಬೇಡಿಕೊಂಡರು. ಯಾಕಂದರೆ ಅರಸನ ಸೀಮೆಯಿಂದಲೇ ಅವರ ಸೀಮೆಗೆ ದವಸಧಾನ್ಯ ಬರುತ್ತಿತ್ತು.",
			"21": "<sup>21</sup> ಗೊತ್ತುಮಾಡಿದ ಒಂದು ದಿನದಲ್ಲಿ ಹೆರೋದನು ರಾಜವಸ್ತ್ರವನ್ನು ಧರಿಸಿಕೊಂಡು ರಾಜಾಸನದ ಮೇಲೆ ಕೂತು ಅವರಿಗೆ ಉಪನ್ಯಾಸ ಮಾಡಿದನು.",
			"22": "<sup>22</sup> ಕೂಡಿದ್ದ ಜನರು - ಇದು ಮನುಷ್ಯನ ನುಡಿಯಲ್ಲ, ದೇವರ ನುಡಿಯೇ ಎಂದು ಆರ್ಭಟಿಸಿದರು.",
			"23": "<sup>23</sup> ಆ ಘನವನ್ನು ಅವನು ದೇವರಿಗೆ ಸಲ್ಲಿಸದೆ ಹೋದದರಿಂದ ಕರ್ತನ ದೂತನು ಆ ಕ್ಷಣದಲ್ಲೇ ಅವನನ್ನು ಬಡಿದನು; ಅವನು ಹುಳಬಿದ್ದು ಸತ್ತನು.",
			"24": "<sup>24</sup> ಆದರೆ ದೇವರ ವಾಕ್ಯವು ಹಬ್ಬಿ ಹೆಚ್ಚುತ್ತಾ ಬಂತು.",
			"25": "<sup>25</sup> ಬಾರ್ನಬ ಸೌಲರು ಮಾಡಬೇಕಾದ ಧರ್ಮಕಾರ್ಯವನ್ನು ತೀರಿಸಿ ಮಾರ್ಕನೆನಿಸಿಕೊಳ್ಳುವ ಯೋಹಾನನನ್ನು ಕರಕೊಂಡು ಯೆರೂಸಲೇಮಿನಿಂದ ಹಿಂತಿರಿಗಿ ಬಂದರು."
		},
		"13": {
			"1": "<sup>1</sup> ಅಂತಿಯೋಕ್ಯದಲ್ಲಿದ್ದ ಸಭೆಯೊಳಗೆ ಪ್ರವಾದಿಗಳೂ ಬೋಧಕರೂ ಇದ್ದರು; ಯಾರಾರಂದರೆ - ಬಾರ್ನಬ, ನೀಗರನೆಂಬ ಸಿಮೆಯೋನ, ಕುರೇನ್ಯದ ಲೂಕ್ಯ, ಉಪರಾಜನಾದ ಹೆರೋದನ ಸಾಕತಮ್ಮನಾದ ಮೆನಹೇನ, ಸೌಲ ಇವರೇ.",
			"2": "<sup>2</sup> ಇವರು ಕರ್ತನನ್ನು ಸೇವಿಸುತ್ತಾ ಉಪವಾಸ ಮಾಡುತ್ತಾ ಇದ್ದಾಗ ಪವಿತ್ರಾತ್ಮನು - ನಾನು ಬಾರ್ನಬ ಸೌಲರನ್ನು ಕರೆದ ಕೆಲಸಕ್ಕಾಗಿ ಅವರನ್ನು ಪ್ರತ್ಯೇಕಿಸಿರಿ ಎಂದು ಹೇಳಿದನು.",
			"3": "<sup>3</sup> ಆಗ ಅವರು ಉಪವಾಸವಿದ್ದು ಪ್ರಾರ್ಥನೆಮಾಡಿ ಆ ಇಬ್ಬರ ಮೇಲೆ ಹಸ್ತಾರ್ಪಣ ಮಾಡಿ ಅವರನ್ನು ಕಳುಹಿಸಿಕೊಟ್ಟರು.",
			"4": "<sup>4</sup> ಹೀಗೆ ಅವರು ಪವಿತ್ರಾತ್ಮನಿಂದ ಕಳುಹಿಸಿಕೊಂಡವರಾಗಿ ಸೆಲ್ಯೂಕ್ಯಕ್ಕೆ ಬಂದರು; ಅಲ್ಲಿಂದ ಸಮುದ್ರ ಪ್ರಯಾಣವಾಗಿ ಕುಪ್ರದ್ವೀಪಕ್ಕೆ ಹೋದರು.",
			"5": "<sup>5</sup> ಸಲಮೀಸಿಗೆ ಸೇರಿದಾಗ ಯೆಹೂದ್ಯರ ಸಭಾಮಂದಿರಗಳಲ್ಲಿ ದೇವರ ವಾಕ್ಯವನ್ನು ಪ್ರಸಿದ್ಧಿಪಡಿಸಿದರು. ಯೋಹಾನನು ಪರಿಚಾರಕನಾಗಿ ಅವರ ಸಂಗಡ ಇದ್ದನು.",
			"6": "<sup>6</sup> ಅವರು ದ್ವೀಪದಲ್ಲೆಲ್ಲಾ ಸಂಚಾರಮಾಡಿ ಪಾಫೋಸ್ ಎಂಬ ಊರಿನ ತನಕ ಬಂದು ಅಲ್ಲಿ ಸುಳ್ಳುಪ್ರವಾದಿಯೂ ಮಂತ್ರವಾದಿಯೂ ಆಗಿದ್ದ ಬಾರ್‍‍ಯೇಸು ಎಂಬ ಒಬ್ಬ ಯೆಹೂದ್ಯ ಮನುಷ್ಯನನ್ನು ಕಂಡರು.",
			"7": "<sup>7</sup> ಅವನು ಬುದ್ಧಿವಂತನಾಗಿದ್ದ ಸೆರ್ಗ್ಯಪೌಲನೆಂಬ ಅಧಿಪತಿಯ ಹತ್ತಿರದಲ್ಲಿದ್ದನು. ಅಧಿಪತಿಯು ಬಾರ್ನಬ ಸೌಲರನ್ನು ತನ್ನ ಬಳಿಗೆ ಕರಿಸಿ ದೇವರ ವಾಕ್ಯವನ್ನು ಕೇಳುವದಕ್ಕೆ ಅಪೇಕ್ಷೆ ಅದೆ ಎಂದು ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ಆದರೆ ಆ ಮಂತ್ರವಾದಿಯಾದ ಎಲುಮನು ಅವರಿಗೆ ಎದುರು ನಿಂತು ಅಧಿಪತಿಯು ಕ್ರಿಸ್ತನನ್ನು ನಂಬದ ಹಾಗೆ ಪ್ರಯತ್ನಿಸಿದನು. ಎಲುಮನೆಂಬ ಹೆಸರಿಗೆ ಮಂತ್ರವಾದಿ ಎಂಬದೇ ಅರ್ಥ.",
			"9": "<sup>9</sup> ಆಗ ಪೌಲನೆನಿಸಿಕೊಳ್ಳುವ ಸೌಲನು ಪವಿತ್ರಾತ್ಮಭರಿತನಾಗಿ ಅವನನ್ನು ದೃಷ್ಟಿಸಿ ನೋಡಿ -",
			"10": "<sup>10</sup> ಎಲೋ, ಎಲ್ಲಾ ಮೋಸದಿಂದಲೂ ಎಲ್ಲಾ ಕೆಟ್ಟತನದಿಂದಲೂ ತುಂಬಿರುವವನೇ, ಸೈತಾನನ ಮಗನೇ, ಎಲ್ಲಾ ನೀತಿಗೂ ವಿರೋಧಿಯೇ, ನೀನು ಕರ್ತನ ನೀಟಾದ ಮಾರ್ಗಗಳನ್ನು ಡೊಂಕುಮಾಡುವದನ್ನು ಬಿಡುವದಿಲ್ಲವೋ?",
			"11": "<sup>11</sup> ಇಗೋ, ಕರ್ತನು ನಿನ್ನ ಮೇಲೆ ಕೈ ಎತ್ತಿದ್ದಾನೆ; ನೀನು ಕುರುಡನಾಗಿ ಕೆಲವು ಕಾಲ ಸೂರ್ಯನನ್ನು ಕಾಣದೆ ಇರುವಿ ಎಂದು ಹೇಳಿದನು. ಆ ಕ್ಷಣವೇ ಅವನಿಗೆ ಕಣ್ಣು ಮೊಬ್ಬಾಗಿ ಕತ್ತಲೆ ಉಂಟಾಯಿತು; ಅವನು ಕೈಹಿಡಿಯುವವರನ್ನು ಹುಡುಕುತ್ತಾ ತಿರುಗಾಡಿದನು.",
			"12": "<sup>12</sup> ಅಧಿಪತಿಯು ಆ ಸಂಗತಿಯನ್ನು ನೋಡಿ ಕರ್ತನ ಬೋಧನೆಗೆ ಅತ್ಯಾಶ್ಚರ್ಯಪಟ್ಟು ನಂಬುವವನಾದನು.",
			"13": "<sup>13</sup> ತರುವಾಯ ಪೌಲನೂ ಅವನ ಜೊತೆಯಲ್ಲಿದ್ದವರೂ ಪಾಫೋಸೂರನ್ನು ಬಿಟ್ಟು ಸಮುದ್ರಪ್ರಯಾಣ ಮಾಡಿ ಪಂಫುಲ್ಯ ಸೀಮೆಗೆ ಸೇರಿದ ಪೆರ್ಗೆಗೆ ಬಂದರು; ಯೋಹಾನನು ಅವರನ್ನು ಬಿಟ್ಟು ಹಿಂತಿರುಗಿ ಯೆರೂಸಲೇಮಿಗೆ ಹೋದನು.",
			"14": "<sup>14</sup> ಆಮೇಲೆ ಅವರು ಪೆರ್ಗೆಯಿಂದ ಸಂಚಾರಮಾಡಿ ಪಿಸಿದ್ಯ ಸೀಮೆಗೆ ಸೇರಿದ ಅಂತಿಯೋಕ್ಯಕ್ಕೆ ಬಂದರು. ಸಬ್ಬತ್‍ದಿನದಲ್ಲಿ ಅವರು ಸಭಾಮಂದಿರಕ್ಕೆ ಹೋಗಿ ಕೂತುಕೊಂಡರು.",
			"15": "<sup>15</sup> ಧರ್ಮಶಾಸ್ತ್ರವು ಪ್ರವಾದಿಗಳ ಗ್ರಂಥವು ಇವುಗಳ ಪಾರಾಯಣವಾದ ಮೇಲೆ ಸಭಾಮಂದಿರದ ಯಜಮಾನರು - ಸಹೋದರರೇ, ಜನರಿಗೆ ಹೇಳತಕ್ಕ ಬುದ್ಧಿ ಮಾತೇನಾದರೂ ನಿಮಗಿದ್ದರೆ ಹೇಳಿರಿ ಎಂದು ಅವರಿಗೆ ಹೇಳಿ ಕಳುಹಿಸಿದರು.",
			"16": "<sup>16</sup> ಆಗ ಪೌಲನು ಎದ್ದು ಕೈಸನ್ನೆ ಮಾಡಿ ಹೇಳಿದ್ದೇನಂದರೆ -",
			"17": "<sup>17</sup> ಇಸ್ರಾಯೇಲ್ ಜನರೇ, ಮತ್ತು ಯೆಹೂದ್ಯ ಮತಾವಲಂಭಿಗಳೇ, ಕೇಳಿರಿ. ನಮ್ಮ ಇಸ್ರಾಯೇಲ್ ಜನರ ದೇವರು ನಮ್ಮ ಪಿತೃಗಳನ್ನು ಆರಿಸಿಕೊಂಡು ನಮ್ಮ ಜನರು ಐಗುಪ್ತದೇಶದಲ್ಲಿ ಪ್ರವಾಸವಾಗಿದ್ದಾಗ ಅವರನ್ನು ವೃದ್ಧಿಗೆ ತಂದು ಭುಜಬಲದಿಂದ ಆ ದೇಶದೊಳಗಿಂದ ಬರಮಾಡಿದನು.",
			"18": "<sup>18</sup> ಆತನು ಸುಮಾರು ನಾಲ್ವತ್ತು ವರುಷಗಳವರೆಗೂ ಅಡವಿಯಲ್ಲಿ ಅವರ ನಡಾವಳಿಯನ್ನು ಸಹಿಸಿಕೊಂಡು",
			"19": "<sup>19</sup> ಕಾನಾನ್ ದೇಶದಲ್ಲಿದ್ದ ಅನ್ಯಜನಗಳ ಏಳು ರಾಜ್ಯಗಳನ್ನು ನಿರ್ಮೂಲ ಮಾಡಿ ಆ ಜನರ ದೇಶವನ್ನು ಅವರಿಗೆ ಬಾಧ್ಯವಾಗಿ ಹಂಚಿಕೊಟ್ಟನು.",
			"20": "<sup>20</sup> ಇಷ್ಟರೊಳಗೆ ಸುಮಾರು ನಾನೂರೈವತ್ತು ವರುಷಗಳು ಗತಿಸಿದವು. ಇದಾದ ಮೇಲೆ ಪ್ರವಾದಿಯಾದ ಸಮುವೇಲನ ಕಾಲದವರೆಗೆ ನ್ಯಾಯಾಧಿಪತಿಗಳನ್ನು ಕೊಟ್ಟನು.",
			"21": "<sup>21</sup> ತರುವಾಯ ಅವರು ತಮಗೆ ಅರಸನು ಬೇಕೆಂದು ಕೇಳಿಕೊಳ್ಳಲು ದೇವರು ಅವರಿಗೆ ಬೆನ್ಯಾಮೀನನ ಕುಲದ ಕೀಷನ ಮಗನಾದ ಸೌಲನನ್ನು ಕೊಟ್ಟನು. ಅವನು ನಾಲ್ವತ್ತು ವರುಷ ಆಳಿದನು.",
			"22": "<sup>22</sup> ಆಮೇಲೆ ದೇವರು ಅವನನ್ನು ತೆಗೆದುಹಾಕಿ ದಾವೀದನನ್ನು ಅವರ ಮೇಲೆ ಅರಸನನ್ನಾಗಿ ಮಾಡಿ - ಇಷಯನ ಮಗನಾದ ದಾವೀದನು ನನಗೆ ಸಿಕ್ಕಿದನು, ಅವನು ನನಗೆ ಒಪ್ಪುವ ಮನುಷ್ಯನು, ಅವನು ನನ್ನ ಇಷ್ಟವನ್ನೆಲ್ಲಾ ನೆರವೇರಿಸುವನು ಎಂಬದಾಗಿ ಅವನ ವಿಷಯದಲ್ಲಿ ಸಾಕ್ಷಿಹೇಳಿದನು.",
			"23": "<sup>23</sup> ಅವನ ಸಂತಾನದಿಂದ ದೇವರು ತನ್ನ ವಾಗ್ದಾನದ ಪ್ರಕಾರ ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಒಬ್ಬ ರಕ್ಷಕನನ್ನು ಹುಟ್ಟಿಸಿದ್ದಾನೆ.",
			"24": "<sup>24</sup> ಆ ರಕ್ಷಕನೇ ಯೇಸು. ಆತನ ಆಗಮನಕ್ಕೆ ಮುಂಚೆ ಯೋಹಾನನು ಇಸ್ರಾಯೇಲ್ ಜನರೆಲ್ಲರಿಗೆ - ನೀವು ದೇವರ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡು ಸ್ನಾನ ಮಾಡಿಸಿಕೊಳ್ಳಬೇಕೆಂದು ಸಾರಿದನು.",
			"25": "<sup>25</sup> ಯೋಹಾನನು ತನ್ನ ಸೇವೆಯನ್ನು ಪೂರೈಸುತ್ತಿರುವಾಗ ಜನರಿಗೆ - ನನ್ನನ್ನು ಯಾರೆಂದು ಯೋಚಿಸುತ್ತೀರಿ? ನಾನು ಆತನಲ್ಲ, ಆದರೆ ನನ್ನ ಹಿಂದೆ ಒಬ್ಬನು ಬರುತ್ತಾನೆ, ಆತನ ಪಾದದ ಕೆರಗಳನ್ನು ಬಿಚ್ಚುವದಕ್ಕೆ ನಾನು ಯೋಗ್ಯನಲ್ಲ ಎಂದು ಹೇಳಿದನು.",
			"26": "<sup>26</sup> ಸಹೋದರರೇ, ಅಬ್ರಹಾಮನ ವಂಶಸ್ಥರೇ, ಮತ್ತು ನಿಮ್ಮಲ್ಲಿರುವ ಯೆಹೂದ್ಯ ಮತಾವಲಂಭಿಗಳೇ, ನಮಗೆ ಈ ರಕ್ಷಣೆಯ ವಾಕ್ಯವು ಕಳುಹಿಸಿ ಅದೆ.",
			"27": "<sup>27</sup> ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ವಾಸವಾಗಿರುವವರೂ ಅವರ ಅಧಿಕಾರಿಗಳೂ ಆತನನ್ನಾಗಲಿ ಪ್ರತಿ ಸಬ್ಬತ್ ದಿನದಲ್ಲಿ ಪಾರಾಯಣವಾಗುವ ಪ್ರವಾದಿಗಳ ವಾಕ್ಯಗಳನ್ನಾಗಲಿ ಗ್ರಹಿಸದೆ ಆತನನ್ನು ಅಪರಾಧಿಯೆಂದು ತೀರ್ಪುಮಾಡಿ ಆ ವಾಕ್ಯಗಳನ್ನೇ ನೆರವೇರಿಸಿದರು.",
			"28": "<sup>28</sup> ಮರಣದಂಡನೆಗೆ ಕಾರಣವೇನೂ ತಮಗೆ ಸಿಕ್ಕದಿದ್ದರೂ ಆತನನ್ನು ಕೊಲ್ಲಿಸಬೇಕೆಂದು ಪಿಲಾತನನ್ನು ಕೇಳಿಕೊಂಡರು.",
			"29": "<sup>29</sup> ಆತನ ವಿಷಯವಾಗಿ ಶಾಸ್ತ್ರದಲ್ಲಿ ಬರೆದಿರುವದನ್ನೆಲ್ಲಾ ನೆರವೇರಿಸಿದ ಮೇಲೆ ಆತನನ್ನು ಮರದ ಕಂಬದಿಂದ ಇಳಿಸಿ ಸಮಾಧಿಯಲ್ಲಿಟ್ಟರು.",
			"30": "<sup>30</sup> ಆದರೆ ದೇವರು ಆತನನ್ನು ಸತ್ತವರೊಳಗಿಂದ ಎಬ್ಬಿಸಿದನು.",
			"31": "<sup>31</sup> ಮತ್ತು ಆತನು ಗಲಿಲಾಯದಿಂದ ಯೆರೂಸಲೇಮಿಗೆ ತನ್ನ ಜೊತೆಯಲ್ಲಿ ಬಂದವರಿಗೆ ಅನೇಕ ದಿವಸಗಳ ಪರಿಯಂತರ ಕಾಣಿಸಿಕೊಂಡನು. ಅವರು ಈಗ ಜನರ ಮುಂದೆ ಆತನಿಗೆ ಸಾಕ್ಷಿಗಳಾಗಿದ್ದಾರೆ.",
			"32": "<sup>32</sup> ದೇವರು ನಮ್ಮ ಪಿತೃಗಳಿಗೆ ಮಾಡಿದ ವಾಗ್ದಾನವನ್ನು ನಮ್ಮ ಮಕ್ಕಳಿಗೋಸ್ಕರ ನೆರವೇರಿಸಿದ್ದಾನೆಂಬ ಶುಭಸಮಾಚಾರವನ್ನು ನಾವು ನಿಮಗೆ ಸಾರುವವರಾಗಿದ್ದೇವೆ. ಆತನು ಯೇಸುವನ್ನು ಎಬ್ಬಿಸಿದ್ದರಲ್ಲಿ - ನನಗೆ ನೀನು ಮಗನು, ನಾನೇ ಈ ಹೊತ್ತು ನಿನ್ನನ್ನು ಪಡೆದಿದ್ದೇನೆ ಎಂದು ಎರಡನೆಯ ಕೀರ್ತನೆಯಲ್ಲಿ ಬರೆದಿರುವ ಮಾತು ನೆರವೇರಿತು.",
			"34": "<sup>34</sup> ಇದಲ್ಲದೆ ದೇವರು ಯೇಸುವನ್ನು ಸತ್ತವರೊಳಗಿಂದ ಎಬ್ಬಿಸಿದ್ದರಿಂದ ಆತನು ಇನ್ನೆಂದಿಗೂ ಕೊಳೆಯುವ ಅವಸ್ಥೆಗೆ ಸೇರತಕ್ಕವನಲ್ಲವೆಂಬದರ ವಿಷಯದಲ್ಲಿ ದೇವರು ಹೇಳಿರುವದೇನಂದರೆ - ದಾವೀದನಿಗೆ ಖಂಡಿತವಾಗಿ ವಾಗ್ದಾನ ಮಾಡಿದ ಕೃಪಾವರಗಳನ್ನು ನಿಮಗೆ ಕೊಡುತ್ತೇನೆ, ಅವು ನಂಬಿಕೆಯಾದವುಗಳೇ ಎಂಬದು.",
			"35": "<sup>35</sup> ಅದಕ್ಕನುಸಾರವಾಗಿ ಆತನು - ನೀನು ನಿನ್ನ ಪ್ರಿಯನಿಗೆ ಕೊಳೆಯುವ ಅವಸ್ಥೆಯನ್ನು ನೋಡಗೊಡಿಸುವದಿಲ್ಲವೆಂದು ಬೇರೊಂದು ಕೀರ್ತನೆಯಲ್ಲಿ ಹೇಳಿದ್ದಾನೆ,",
			"36": "<sup>36</sup> ದಾವೀದನಾದರೋ ತನ್ನ ಸಕಾಲದವರಿಗೆ ಸೇವೆಮಾಡಿದನಂತರ ದೈವಸಂಕಲ್ಪದಿಂದ ನಿದ್ದೆಹೋಗಿ ತನ್ನ ಪಿತೃಗಳ ಬಳಿಯಲ್ಲಿ ಇಡಲ್ಪಟ್ಟು ಕೊಳೆಯುವ ಅವಸ್ಥೆಯನ್ನು ಅನುಭವಿಸಿದನು.",
			"37": "<sup>37</sup> ಆದರೆ ದೇವರು ಎಬ್ಬಿಸಿದಾತನು ಕೊಳೆಯುವ ಅವಸ್ಥೆಯನ್ನು ಅನುಭವಿಸಲಿಲ್ಲ.",
			"38": "<sup>38</sup> ಆದದರಿಂದ ಸಹೋದರರೇ, ಆತನ ಮೂಲಕವಾಗಿ ಪಾಪಪರಿಹಾರವು ದೊರೆಯುತ್ತದೆಂಬದು ನಿಮಗೆ ಸಾರೋಣವಾಗುತ್ತದೆಂದು ನಿಮಗೆ ತಿಳಿದಿರಲಿ.",
			"39": "<sup>39</sup> ಮೋಶೆಯ ಧರ್ಮಶಾಸ್ತ್ರದ ಮೂಲಕ ನೀವು ಬಿಡುಗಡೆಹೊಂದಲಾಗದ ಎಲ್ಲಾ ಪಾಪಗಳಿಂದ ನಂಬುವವರೆಲ್ಲರು ಆತನ ಮೂಲಕವಾಗಿ ಬಿಡುಗಡೆ ಹೊಂದಿ ನೀತಿವಂತರೆನಿಸಿಕೊಳ್ಳುತ್ತಾರೆ.",
			"40": "<sup>40</sup> ಹೀಗಿರಲಾಗಿ ಪ್ರವಾದಿಗಳ ಗ್ರಂಥದಲ್ಲಿ ಹೇಳಿರುವದು ನಿಮಗೆ ಸಂಭವಿಸದಂತೆ ನೋಡಿಕೊಳ್ಳಿರಿ. ಅದೇನಂದರೆ -",
			"41": "<sup>41</sup> ಎಲೈ ತಿರಸ್ಕಾರಮಾಡುವವರೇ, ಆಶ್ಚರ್ಯಪಡಿರಿ, ನಾಶವಾಗಿ ಹೋಗಿರಿ. ನಿಮ್ಮ ಕಾಲದಲ್ಲಿ ನಾನು ಒಂದು ಕಾರ್ಯವನ್ನು ಮಾಡುವೆನು; ಆ ಕಾರ್ಯವನ್ನು ಒಬ್ಬನು ನಿಮಗೆ ವಿವರಿಸಿದರೂ ನೀವು ಅದನ್ನು ಎಷ್ಟು ಮಾತ್ರಕ್ಕೂ ನಂಬುವದಿಲ್ಲ ಎಂಬದು ಎಂದು ಹೇಳಿದನು.",
			"42": "<sup>42</sup> ಪೌಲನೂ ಬಾರ್ನಬನೂ ಸಭಾಮಂದಿರವನ್ನು ಬಿಟ್ಟುಹೋಗುತ್ತಿರುವಾಗ ಜನರು ಈ ಮಾತುಗಳನ್ನು ಬರುವ ಸಬ್ಬತ್‍ದಿನದಲ್ಲಿಯೂ ತಮಗೆ ಹೇಳಬೇಕೆಂದು ಕೇಳಿಕೊಂಡರು.",
			"43": "<sup>43</sup> ಸಭೆಯು ಮುಗಿದ ತರುವಾಯ ಯೆಹೂದ್ಯರಲ್ಲಿಯೂ ದೈವಭಕ್ತರಾಗಿದ್ದ ಯೆಹೂದ್ಯ ಮತಾವಲಂಭಿಗಳಲ್ಲಿಯೂ ಅನೇಕರು ಪೌಲ ಬಾರ್ನಬರನ್ನು ಹಿಂಬಾಲಿಸಿದರು. ಇವರು ಅವರ ಸಂಗಡ ಮಾತಾಡಿ ದೇವರ ಕೃಪಾಶ್ರಯದಲ್ಲಿ ನೆಲೆಗೊಂಡಿರಬೇಕೆಂದು ಅವರನ್ನು ಪ್ರೋತ್ಸಾಹಪಡಿಸಿದರು.",
			"44": "<sup>44</sup> ಮುಂದಿನ ಸಬ್ಬತ್‍ದಿನದಲ್ಲಿ ಸ್ವಲ್ಪ ಕಡಿಮೆ ಊರೆಲ್ಲಾ ದೇವರ ವಾಕ್ಯವನ್ನು ಕೇಳುವದಕ್ಕೆ ಕೂಡಿಬಂತು.",
			"45": "<sup>45</sup> ಆದರೆ ಜನರು ಗುಂಪುಗುಂಪಾಗಿ ಬರುವದನ್ನು ನೋಡಿ ಯೆಹೂದ್ಯರು ಮತಾಭಿಮಾನದಿಂದ ತುಂಬಿದವರಾಗಿ ಪೌಲನು ಹೇಳಿದ ಮಾತುಗಳಿಗೆ ಎದುರ್ಚಂಡಿಸುತ್ತಾ ದೂಷಣೆ ಮಾಡುತ್ತಾ ಇದ್ದರು.",
			"46": "<sup>46</sup> ಆಗ ಪೌಲನೂ ಬಾರ್ನಬನೂ ಧೈರ್ಯದಿಂದ ಮಾತಾಡಿ - ದೇವರ ವಾಕ್ಯವನ್ನು ಮೊದಲು ನಿಮಗೇ ಹೇಳುವದು ಅವಶ್ಯವಾಗಿತ್ತು; ಆದರೆ ನೀವು ಅದನ್ನು ತಳ್ಳಿಬಿಟ್ಟು ನಿಮ್ಮನ್ನು ನಿತ್ಯಜೀವಕ್ಕೆ ಅಪಾತ್ರರೆಂದು ತೀರ್ಪುಮಾಡಿಕೊಂಡದ್ದರಿಂದ ಇಗೋ, ನಾವು ನಿಮ್ಮನ್ನು ಬಿಟ್ಟು ಅನ್ಯಜನರ ಕಡೆಗೆ ಹೋಗುತ್ತೇವೆ.",
			"47": "<sup>47</sup> ಹಾಗೆಯೇ ಕರ್ತನು ನಮಗೆ ಅಪ್ಪಣೆಕೊಟ್ಟಿದ್ದಾನೆ; ಹೇಗಂದರೆ - ನೀನು ಲೋಕದ ಕಟ್ಟಕಡೆಯವರೆಗೆ ರಕ್ಷಕನಾಗಿರುವಂತೆ ನಿನ್ನನ್ನು ಅನ್ಯಜನಾಂಗಗಳಿಗೂ ಬೆಳಕನ್ನಾಗಿ ನೇಮಿಸಿದ್ದೇನೆ ಎಂದು ದೇವರು ಹೇಳಿದ್ದಾನೆ ಅಂದರು.",
			"48": "<sup>48</sup> ಅಲ್ಲಿದ್ದ ಅನ್ಯಜನರು ಆ ಮಾತನ್ನು ಕೇಳಿ ಸಂತೋಷಪಟ್ಟು ದೇವರ ವಾಕ್ಯವನ್ನು ಹೊಗಳಿದರು; ಮತ್ತು ನಿತ್ಯಜೀವಕ್ಕೆ ನೇಮಿಸಲ್ಪಟ್ಟವರೆಲ್ಲರು ನಂಬಿದರು.",
			"49": "<sup>49</sup> ಕರ್ತನ ವಾಕ್ಯವು ಆ ಸೀಮೆಯ ಎಲ್ಲಾ ಕಡೆಗಳಲ್ಲಿ ಹಬ್ಬುತ್ತಾ ಬಂತು.",
			"50": "<sup>50</sup> ಆದರೆ ಯೆಹೂದ್ಯರು ತಮ್ಮ ಮತಕ್ಕೆ ಸೇರಿದ್ದ ಕುಲೀನ ಸ್ತ್ರೀಯರನ್ನೂ ಊರಿನ ಪ್ರಮುಖರನ್ನೂ ಪ್ರೇರಿಸಿ ಪೌಲ ಬಾರ್ನಬರ ಮೇಲೆ ಹಿಂಸೆಯನ್ನು ಎಬ್ಬಿಸಿ ಅವರನ್ನು ತಮ್ಮ ಮೇರೆಯಿಂದ ಆಚೆಗೆ ಅಟ್ಟಿದರು.",
			"51": "<sup>51</sup> ಇವರು ತಮ್ಮ ಕಾಲಿಗೆ ಹತ್ತಿದ್ದ ದೂಳನ್ನು ಅವರ ಮೇಲೆ ಝಾಡಿಸಿಬಿಟ್ಟು ಇಕೋನ್ಯಕ್ಕೆ ಹೋದರು.",
			"52": "<sup>52</sup> ಶಿಷ್ಯರಾದವರು ಸಂತೋಷಪೂರ್ಣರೂ ಪವಿತ್ರಾತ್ಮಭರಿತರೂ ಆದರು."
		},
		"14": {
			"1": "<sup>1</sup> ಇಕೋನ್ಯದಲ್ಲಿ ಪೌಲ ಬಾರ್ನಬರು ಅದೇ ಪ್ರಕಾರವಾಗಿ ಯೆಹೂದ್ಯರ ಸಭಾಮಂದಿರದೊಳಗೆ ಹೋಗಿ ಬಲವಾಗಿ ಮಾತಾಡಿದ್ದರಿಂದ ಯೆಹೂದ್ಯರಲ್ಲಿಯೂ ಗ್ರೀಕರಲ್ಲಿಯೂ ಬಹುಮಂದಿ ನಂಬುವವರಾದರು.",
			"2": "<sup>2</sup> ಆದರೆ ನಂಬದೆ ಹೋದ ಯೆಹೂದ್ಯರು ಅನ್ಯಜನರ ಮನಸ್ಸನ್ನು ಸಹೋದರರಿಗೆ ವಿರುದ್ಧವಾಗಿ ರೇಗಿಸಿ ಕೆಡಿಸಿದರು.",
			"3": "<sup>3</sup> ಹೀಗಿರಲಾಗಿ ಪೌಲ ಬಾರ್ನಬರು ಬಹುಕಾಲ ಅಲ್ಲಿದ್ದು ಕರ್ತನು ಅವರ ಕೈಯಿಂದ ಸೂಚಕ ಕಾರ್ಯಗಳೂ ಅದ್ಭುತಕಾರ್ಯಗಳೂ ಆಗುವಂತೆ ದಯಪಾಲಿಸಿ ತನ್ನ ಕೃಪಾವಾಕ್ಯಕ್ಕೆ ಸಾಕ್ಷಿಕೊಟ್ಟದ್ದರಿಂದ ಆತನ ಮೇಲೆ ಭರವಸವಿಟ್ಟು ಧೈರ್ಯದಿಂದ ಮಾತಾಡುತ್ತಿದ್ದರು.",
			"4": "<sup>4</sup> ಆಗ ಆ ಊರಿನ ಜನರಲ್ಲಿ ಭೇದವುಂಟಾಯಿತು; ಕೆಲವರು ಯೆಹೂದ್ಯರ ಪಕ್ಷದವರಾದರು, ಕೆಲವರು ಅಪೊಸ್ತಲರ ಪಕ್ಷದವರಾದರು.",
			"5": "<sup>5</sup> ಅನ್ಯಜನರೂ ಯೆಹೂದ್ಯರೂ ಕೂಡಿ ತಮ್ಮ ಅಧಿಪತಿಗಳ ಸಮ್ಮತಿಯಿಂದ ಅಪೊಸ್ತಲರನ್ನು ಅವಮಾನಪಡಿಸುವದಕ್ಕೂ ಕಲ್ಲೆಸೆದು ಕೊಲ್ಲುವದಕ್ಕೂ ಪ್ರವರ್ತಿಸಿದಾಗ",
			"6": "<sup>6</sup> ಅಪೊಸ್ತಲರು ಅದನ್ನು ತಿಳಿದು ಓಡಿಹೋಗಿ ಲುಕವೋನ್ಯದಲ್ಲಿದ್ದ ಲುಸ್ತ್ರ ದೆರ್ಬೆ ಎಂಬ ಊರುಗಳಿಗೂ",
			"7": "<sup>7</sup> ಅವುಗಳಿಗೆ ಸೇರಿರುವ ಸೀಮೆಗೂ ಹೋಗಿ ಅಲ್ಲಿ ಸುವಾರ್ತೆಯನ್ನು ಸಾರಿದರು.",
			"8": "<sup>8</sup> ಲುಸ್ತ್ರದೊಳಗೆ ಕಾಲುಗಳಲ್ಲಿ ಬಲವಿಲ್ಲದ ಒಬ್ಬ ಮನುಷ್ಯನು ಕೂತಿದ್ದನು. ಅವನು ಹುಟ್ಟುಕುಂಟನಾಗಿದ್ದು ಎಂದಿಗೂ ನಡೆಯದೆ ಇದ್ದವನು.",
			"9": "<sup>9</sup> ಪೌಲನು ಆಡುವ ಮಾತುಗಳನ್ನು ಅವನು ಕಿವಿಗೊಟ್ಟು ಕೇಳುತ್ತಿದ್ದನು. ಪೌಲನು ಅವನನ್ನು ಸ್ಥಿರವಾಗಿ ನೋಡಿ ವಾಸಿಯಾಗುವದಕ್ಕೆ ಬೇಕಾದ ನಂಬಿಕೆಯು ಅವನಲ್ಲಿ ಉಂಟೆಂದು ತಿಳಿದು -",
			"10": "<sup>10</sup> ನಿನ್ನ ಕಾಲೂರಿ ನೆಟ್ಟಗೆ ನಿಂತುಕೋ ಎಂದು ಮಹಾಧ್ವನಿಯಿಂದ ಹೇಳಿದನು. ಕೂಡಲೆ ಆ ಮನುಷ್ಯನು ಹಾರಿ ನಡೆದಾಡಿದನು.",
			"11": "<sup>11</sup> ಪೌಲನು ಮಾಡಿದ್ದನ್ನು ಗುಂಪುಕೂಡಿದ್ದ ಜನರು ನೋಡಿ - ದೇವತೆಗಳು ಮನುಷ್ಯರ ರೂಪದಿಂದ ನಮ್ಮ ಬಳಿಗೆ ಇಳಿದು ಬಂದರು ಎಂದು ಲುಕವೋನ್ಯ ಭಾಷೆಯಲ್ಲಿ ಕೂಗಾಡಿದರು.",
			"12": "<sup>12</sup> ಬಾರ್ನಬನನ್ನು ದ್ಯೌಸ್‍ದೇವರೆಂತಲೂ ಪೌಲನು ಮಾತಾಡುವದರಲ್ಲಿ ಮುಖ್ಯನಾದ್ದರಿಂದ ಅವನನ್ನು ಹೆರ್ಮೆದೇವರೆಂತಲೂ ಅಂದುಕೊಂಡರು.",
			"13": "<sup>13</sup> ಊರ ಮುಂದಿದ್ದ ದ್ಯೌಸ್‍ದೇವರ ದೇವಸ್ಥಾನದ ಪೂಜಾರಿಯು ಎತ್ತುಗಳನ್ನೂ ಹೂವಿನ ಹಾರಗಳನ್ನೂ ಊರ ಬಾಗಿಲಿನ ಹತ್ತರಕ್ಕೆ ತಂದು ಜನರೊಂದಿಗೆ ಅವರಿಗೆ ಬಲಿಕೊಡಬೇಕೆಂದಿದ್ದನು.",
			"14": "<sup>14</sup> ಇದನ್ನು ಕೇಳಿ ಅಪೊಸ್ತಲರಾದ ಬಾರ್ನಬ ಪೌಲರು ತಮ್ಮ ವಸ್ತ್ರಗಳನ್ನು ಹರಕೊಂಡು ಜನರ ಗುಂಪಿನೊಳಗೆ ಕೂಗುತ್ತಾ ನುಗ್ಗಿ -",
			"15": "<sup>15</sup> ಜನರೇ, ನೀವು ಮಾಡುವದು ಇದೇನು? ನಾವೂ ಮನುಷ್ಯರು, ನಿಮ್ಮಂಥ ಸ್ವಭಾವವುಳ್ಳವರು. ನೀವು ಈ ವ್ಯರ್ಥವಾದ ಕೆಲಸಗಳನ್ನು ಬಿಟ್ಟುಬಿಟ್ಟು ಭೂಮ್ಯಾಕಾಶಗಳನ್ನೂ ಸಮುದ್ರವನ್ನೂ ಅವುಗಳಲ್ಲಿರುವ ಸಮಸ್ತವನ್ನೂ ನಿರ್ಮಾಣಮಾಡಿದ ಜೀವಸ್ವರೂಪನಾದ ದೇವರ ಕಡೆಗೆ ತಿರುಗಿಕೊಳ್ಳಬೇಕೆಂದು ಸುವಾರ್ತೆಯನ್ನು ನಿಮಗೆ ಸಾರಿಹೇಳುವವರಾಗಿದ್ದೇವೆ.",
			"16": "<sup>16</sup> ಗತಿಸಿಹೋದ ಕಾಲದಲ್ಲಿ ಆತನು ಎಲ್ಲಾ ಜನಾಂಗಗಳನ್ನು ತಮ್ಮ ತಮ್ಮ ಮನಸ್ಸಿಗೆ ತೋಚಿದ ಮಾರ್ಗಗಳಲ್ಲಿ ನಡೆಯುವದಕ್ಕೆ ಬಿಟ್ಟನು.",
			"17": "<sup>17</sup> ಆದರೂ ಆತನು ತನ್ನ ವಿಷಯದಲ್ಲಿ ಸಾಕ್ಷಿಕೊಡದೆ ಇರಲಿಲ್ಲ; ನಿಮಗೆ ಆಕಾಶದಿಂದ ಮಳೆಯನ್ನೂ ಸುಗ್ಗೀಕಾಲಗಳನ್ನೂ ದಯಪಾಲಿಸಿ ಆಹಾರಕೊಟ್ಟು ನಿಮ್ಮ ಮನಸ್ಸುಗಳನ್ನು ಆನಂದದಿಂದ ತುಂಬಿಸಿ ಉಪಕಾರ ಮಾಡುತ್ತಾ ಬಂದವನು ಆತನೇ ಎಂದು ಹೇಳಿದರು.",
			"18": "<sup>18</sup> ಅವರು ಈ ಮಾತುಗಳನ್ನು ಹೇಳಿದರೂ ಜನರನ್ನು ತಮಗೆ ಬಲಿಕೊಡದಂತೆ ತಡೆಯುವದು ಕಷ್ಟವಾಗಿತ್ತು.",
			"19": "<sup>19</sup> ತರುವಾಯ ಅಂತಿಯೋಕ್ಯದಿಂದಲೂ ಇಕೋನ್ಯದಿಂದಲೂ ಯೆಹೂದ್ಯರು ಬಂದು ಜನರನ್ನು ಪ್ರೇರೇಪಿಸಿ ಪೌಲನನ್ನು ಕೊಲ್ಲುವದಕ್ಕೆ ಕಲ್ಲೆಸೆದು ಅವನು ಸತ್ತನೆಂದು ಭಾವಿಸಿ ಊರಹೊರಕ್ಕೆ ಎಳೆದುಬಿಟ್ಟರು.",
			"20": "<sup>20</sup> ಆದರೆ ಶಿಷ್ಯರು ಅವನ ಸುತ್ತಲು ನಿಂತುಕೊಂಡಿರುವಾಗ ಅವನು ಎದ್ದು ಊರೊಳಕ್ಕೆ ಹೋದನು. ಮರುದಿನ ಬಾರ್ನಬನ ಜೊತೆಯಲ್ಲಿ ದೆರ್ಬೆಗೆ ಹೊರಟನು.",
			"21": "<sup>21</sup> ಆ ಊರಿನಲ್ಲಿ ಅವರು ಸುವಾರ್ತೆಯನ್ನು ಸಾರಿ ಅನೇಕರನ್ನು ಶಿಷ್ಯರಾಗಿ ಮಾಡಿದ ಮೇಲೆ ಹಿಂತಿರಿಗಿ ಲುಸ್ತ್ರಕ್ಕೂ ಇಕೋನ್ಯಕ್ಕೂ ಅಂತಿಯೋಕ್ಯಕ್ಕೂ ಬಂದು ಶಿಷ್ಯರ ಮನಸ್ಸುಗಳನ್ನು ದೃಢಪಡಿಸಿದರು.",
			"22": "<sup>22</sup> ನಾವು ಬಹು ಸಂಕಟಗಳನ್ನು ತಾಳಿ ದೇವರ ರಾಜ್ಯದೊಳಗೆ ಸೇರಬೇಕೆಂಬದಾಗಿ ಎಚ್ಚರಿಕೆಯ ಮಾತನ್ನು ಹೇಳಿ ಕ್ರಿಸ್ತನಂಬಿಕೆಯಲ್ಲಿ ಸ್ಥಿರವಾಗಿರ್ರಿ ಎಂದು ಅವರನ್ನು ಧೈರ್ಯಗೊಳಿಸಿದರು.",
			"23": "<sup>23</sup> ಇದಲ್ಲದೆ ಪ್ರತಿಸಭೆಯಲ್ಲಿ ಸಭೆಯವರ ಸಮ್ಮತಿಯಿಂದ ಹಿರಿಯರನ್ನು ಗೊತ್ತು ಮಾಡಿ ನೇಮಿಸಿ ಉಪವಾಸವಿದ್ದು ಪ್ರಾರ್ಥನೆ ಮಾಡಿ ಅವರು ನಂಬಿದ್ದ ಕರ್ತನ ಕೈಗೆ ಅವರನ್ನು ಒಪ್ಪಿಸಿದರು.",
			"24": "<sup>24</sup> ತರುವಾಯ ಪಿಸಿದ್ಯಸೀಮೆಯನ್ನು ಹಾದು ಪಂಫುಲ್ಯಸೀಮೆಗೆ ಬಂದು",
			"25": "<sup>25</sup> ಅದಕ್ಕೆ ಸೇರಿರುವ ಪೆರ್ಗೆಯಲ್ಲಿ ಕರ್ತನ ವಾಕ್ಯವನ್ನು ಹೇಳಿದ ಮೇಲೆ ಅತಾಲ್ಯಕ್ಕೆ ಇಳಿದು ಅಲ್ಲಿಂದ ಸಮುದ್ರಪ್ರಯಾಣವಾಗಿ ಅಂತಿಯೋಕ್ಯಕ್ಕೆ ಮುಟ್ಟಿದರು.",
			"26": "<sup>26</sup> ಅವರು ನೆರವೇರಿಸಿ ಬಂದ ಕೆಲಸಕ್ಕೋಸ್ಕರ ದೇವರ ಕೃಪಾಶ್ರಯಕ್ಕೆ ಒಪ್ಪಿಸಲ್ಪಟ್ಟವರಾಗಿ ಆ ಊರಿಂದಲೇ ಹೊರಟಿದ್ದರು.",
			"27": "<sup>27</sup> ಅಲ್ಲಿ ಸಭೆಯನ್ನು ಕೂಡಿಸಿ ದೇವರು ತಮ್ಮೊಂದಿಗಿದ್ದು ಮಾಡಿದ್ದೆಲ್ಲವನ್ನೂ ಆತನು ಅನ್ಯಜನರಿಗೆ ನಂಬಿಕೆಯ ಬಾಗಿಲನ್ನು ತೆರೆದದ್ದನ್ನೂ ವಿವರವಾಗಿ ಹೇಳಿದರು.",
			"28": "<sup>28</sup> ತರುವಾಯ ಅವರು ಅಲ್ಲಿ ಶಿಷ್ಯರ ಸಂಗಡ ಬಹುಕಾಲ ಇದ್ದರು."
		},
		"15": {
			"1": "<sup>1</sup> ಬಳಿಕ ಕೆಲವರು ಯೂದಾಯದಿಂದ ಅಂತಿಯೋಕ್ಯಕ್ಕೆ ಬಂದು - ನೀವು ಮೋಶೆಯ ಗ್ರಂಥದಲ್ಲಿ ಹೇಳಿರುವ ನೇಮದ ಪ್ರಕಾರ ಸುನ್ನತಿಮಾಡಿಸಿಕೊಳ್ಳದಿದ್ದರೆ ನಿಮಗೆ ರಕ್ಷಣೆಯಾಗಲಾರದು ಎಂಬದಾಗಿ ಸಹೋದರರಿಗೆ ಉಪದೇಶಮಾಡುತ್ತಿದ್ದರು.",
			"2": "<sup>2</sup> ಅವರಿಗೂ ಪೌಲ ಬಾರ್ನಬರಿಗೂ ಭೇದವೂ ಮಹಾ ವಿವಾದವೂ ಉಂಟಾದಾಗ ಇದರ ವಿಷಯವಾಗಿ ಸಹೋದರರು ಪೌಲ ಬಾರ್ನಬರನ್ನೂ ತಮ್ಮಲ್ಲಿ ಬೇರೆ ಕೆಲವರನ್ನೂ ಯೆರೂಸಲೇಮಿನಲ್ಲಿದ್ದ ಅಪೊಸ್ತಲರ ಮತ್ತು ಸಭೆಯ ಹಿರಿಯರ ಬಳಿಗೆ ಹೋಗಬೇಕೆಂದು ನೇಮಿಸಿದರು.",
			"3": "<sup>3</sup> ಸಭೆಯವರು ಅವರನ್ನು ಸಾಗಕಳುಹಿಸಿದ ಮೇಲೆ ಅವರು ಫೊಯಿನಿಕೆ ಸಮಾರ್ಯ ಸೀಮೆಗಳನ್ನು ಹಾದು ಹೋಗುತ್ತಿರುವಾಗ ಅನ್ಯಜನರು ಕರ್ತನ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡ ಸಂಗತಿಯನ್ನು ಅಲ್ಲಿದ್ದ ಸಹೋದರರಿಗೆ ವಿವರವಾಗಿ ಹೇಳಿ ಅವರೆಲ್ಲರನ್ನು ಬಹಳವಾಗಿ ಸಂತೋಷಪಡಿಸಿದರು.",
			"4": "<sup>4</sup> ಅವರು ಯೆರೂಸಲೇಮಿಗೆ ಬಂದ ಮೇಲೆ ಸಭೆಯವರೂ ಅಪೊಸ್ತಲರೂ ಸಭೆಯ ಹಿರಿಯರೂ ಅವರನ್ನು ಸೇರಿಸಿಕೊಂಡಾಗ ದೇವರು ತಮ್ಮೊಂದಿಗಿದ್ದು ಮಾಡಿದ ಕಾರ್ಯಗಳನ್ನೆಲ್ಲಾ ಅವರಿಗೆ ತಿಳಿಸಿದರು.",
			"5": "<sup>5</sup> ಆದರೆ ಫರಿಸಾಯ ಮತದವರಲ್ಲಿ ಯೇಸುವನ್ನು ನಂಬಿದ್ದ ಕೆಲವರು ಎದ್ದು - ದೇವರ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡಿರುವ ಅನ್ಯಜನರಿಗೆ ಸುನ್ನತಿಮಾಡಿಸಬೇಕು ಮತ್ತು ಮೋಶೆಯ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಕೈಕೊಂಡು ನಡೆಯುವದಕ್ಕೆ ಅವರಿಗೆ ಅಪ್ಪಣೆಕೊಡಬೇಕು ಎಂದು ಹೇಳಿದರು.",
			"6": "<sup>6</sup> ಅಪೊಸ್ತಲರೂ ಸಭೆಯ ಹಿರಿಯರೂ ಈ ವಿಷಯವನ್ನು ಕುರಿತು ಆಲೋಚಿಸುವದಕ್ಕೆ ಕೂಡಿ ಬಂದಿರುವಾಗ ಬಹು ವಿವಾದವು ನಡೆದ ಮೇಲೆ",
			"7": "<sup>7</sup> ಪೇತ್ರನು ಎದ್ದು ಅವರಿಗೆ - ಸಹೋದರರೇ, ಅನ್ಯಜನರು ನನ್ನ ಬಾಯಿಂದ ಸುವಾರ್ತೆಯ ವಾಕ್ಯವನ್ನು ಕೇಳಿ ನಂಬಬೇಕೆಂದು ದೇವರು ಬಹಳ ದಿವಸಗಳ ಕೆಳಗೆ ನಿಮ್ಮೊಳಗಿಂದ ನನ್ನನ್ನು ಆರಿಸಿಕೊಂಡದ್ದು ನಿಮಗೇ ತಿಳಿದದೆ.",
			"8": "<sup>8</sup> ಮತ್ತು ಹೃದಯವನ್ನು ಬಲ್ಲವನಾಗಿರುವ ದೇವರು ಹೇಗೆ ಪವಿತ್ರಾತ್ಮವರವನ್ನು ನಮಗೆ ದಯಪಾಲಿಸಿದನೋ ಹಾಗೆಯೇ ಅವರಿಗೂ ದಯಪಾಲಿಸಿ ಅವರನ್ನು ತನ್ನ ಜನರೆಂದು ಸಾಕ್ಷಿಕೊಟ್ಟನು.",
			"9": "<sup>9</sup> ಇದಲ್ಲದೆ ಆತನು ನಮಗೂ ಅವರಿಗೂ ಏನೂ ಭೇದಮಾಡದೆ ಅವರ ಹೃದಯಗಳನ್ನೂ ನಂಬಿಕೆಯ ಮೂಲಕವಾಗಿ ಶುದ್ಧೀಕರಿಸಿದನು.",
			"10": "<sup>10</sup> ಹೀಗಿರುವದರಿಂದ ನಮ್ಮ ಪಿತೃಗಳಾಗಲಿ ನಾವಾಗಲಿ ಹೊರಲಾರದ ನೊಗವನ್ನು ನೀವು ಶಿಷ್ಯರ ಹೆಗಲಿನ ಮೇಲೆ ಹಾಕಿ ದೇವರನ್ನು ಪರೀಕ್ಷಿಸುವದು ಯಾಕೆ?",
			"11": "<sup>11</sup> ಕರ್ತನಾದ ಯೇಸುವಿನ ಕೃಪೆಯಿಂದಲೇ ನಾವು ರಕ್ಷಣೆ ಹೊಂದುವೆವೆಂಬದಾಗಿ ನಂಬುತ್ತೇವಲ್ಲಾ; ಹಾಗೆಯೇ ಅವರೂ ಹೊಂದುವರು ಎಂದು ಹೇಳಿದನು.",
			"12": "<sup>12</sup> ಗುಂಪುಕೂಡಿದ್ದವರೆಲ್ಲರು ಈ ಮಾತುಗಳನ್ನು ಕೇಳಿ ಮೌನವಾಗಿದ್ದು ಬಾರ್ನಬನೂ ಪೌಲನೂ ತಮ್ಮ ಮೂಲಕವಾಗಿ ದೇವರು ಅನ್ಯಜನರಲ್ಲಿ ಮಾಡಿದ್ದ ಎಲ್ಲಾ ಸೂಚಕಕಾರ್ಯಗಳನ್ನೂ ಅದ್ಭುತಕಾರ್ಯಗಳನ್ನೂ ವಿವರಿಸುವದನ್ನು ಕಿವಿಗೊಟ್ಟು ಕೇಳಿದರು.",
			"13": "<sup>13</sup> ಅವರು ಮಾತಾಡುವದನ್ನು ಮುಗಿಸಿದ ಮೇಲೆ ಯಾಕೋಬನು ಆ ಪ್ರಸ್ತಾಪವನ್ನು ಹಿಡಿದು ಹೇಳಿದ್ದೇನಂದರೆ -",
			"14": "<sup>14</sup> ಸಹೋದರರೇ, ನಾನು ಹೇಳುವದನ್ನು ಕೇಳಿರಿ - ದೇವರು ಮೊದಲಲ್ಲಿ ಅನ್ಯಜನರನ್ನು ಕಟಾಕ್ಷಿಸಿನೋಡಿ ತನ್ನ ಹೆಸರಿಗಾಗಿ ಅವರೊಳಗಿಂದ ಒಂದು ಪ್ರಜೆಯನ್ನು ಆರಿಸಿಕೊಂಡ ವಿಧವನ್ನು ಸಿಮೆಯೋನನು ವಿವರಿಸಿದನಷ್ಟೆ.",
			"15": "<sup>15</sup> ಇದಕ್ಕೆ ಪ್ರವಾದಿಗಳ ಮಾತುಗಳೂ ಒಪ್ಪುತ್ತವೆ. ಹೇಗಂದರೆ -",
			"16": "<sup>16</sup> ಇದಾದ ಮೇಲೆ ನಾನು ಹಿಂತಿರುಗಿ ಬಂದು ದಾವೀದನ ಬಿದ್ದುಹೋಗಿರುವ ಗುಡಾರವನ್ನು ಎತ್ತುವೆನು. ಅದರಲ್ಲಿ ಹಾಳಾದದ್ದನ್ನು ನಾನು ಸರಿಮಾಡಿಸಿ ಅದನ್ನು ನೆಟ್ಟಗೆ ನಿಲ್ಲಿಸುವೆನು.",
			"17": "<sup>17</sup> ಮನುಷ್ಯಜಾತಿಯ ಉಳಿದ ಜನಗಳೂ ಅಂದರೆ ದೇವಜನರೆನಿಸಿಕೊಳ್ಳುವ ಸಕಲ ಜನಾಂಗಗಳೂ ಕರ್ತನನ್ನು ಹುಡುಕುವವರಾಗಿರಬೇಕೆಂದು",
			"18": "<sup>18</sup> ಆದಿಯಿಂದ ಈ ಕಾರ್ಯಗಳನ್ನು ತಿಳಿಯಪಡಿಸುವ ಕರ್ತನು ನುಡಿಯುತ್ತಾನೆ ಎಂದು ಬರೆದದೆ.",
			"19": "<sup>19</sup> ಹೀಗಿರಲಾಗಿ ನನ್ನ ಅಭಿಪ್ರಾಯ ಏನಂದರೆ - ಅನ್ಯಜನರಲ್ಲಿ ದೇವರ ಕಡೆಗೆ ತಿರುಗಿಕೊಳ್ಳುವವರನ್ನು ತೊಂದರೆಪಡಿಸಬಾರದು.",
			"20": "<sup>20</sup> ಆದರೆ ವಿಗ್ರಹಕ್ಕೆ ನೈವೇದ್ಯಮಾಡಿದ್ದನ್ನೂ ಹಾದರವನ್ನೂ ಕುತ್ತಿಗೆಹಿಸುಕಿ ಕೊಂದದ್ದನ್ನೂ ರಕ್ತವನ್ನೂ ವಿಸರ್ಜಿಸಬೇಕೆಂದು ನಾವು ಅವರಿಗೆ ಕಾಗದವನ್ನು ಬರೆದು ಅಪ್ಪಣೆಕೊಡೋಣ.",
			"21": "<sup>21</sup> ಯಾಕಂದರೆ ಪುರಾತನಕಾಲದಿಂದ ಎಲ್ಲಾ ಪಟ್ಟಣಗಳಲ್ಲಿ ಮೋಶೆಯ ಗ್ರಂಥವನ್ನು ಬೋಧಿಸುವವರು ಇದ್ದಾರೆ; ಅದು ಪ್ರತಿ ಸಬ್ಬತ್‍ದಿನವೂ ಸಭಾಮಂದಿರಗಳಲ್ಲಿ ಪಾರಾಯಣವಾಗುತ್ತದಲ್ಲಾ ಎಂದು ಹೇಳಿದನು.",
			"22": "<sup>22</sup> ಆಗ ಅಪೊಸ್ತಲರೂ ಸಭೆಯ ಹಿರಿಯರೂ ಸರ್ವಸಭೆಯ ಸಮ್ಮತಿಯಿಂದ ತಮ್ಮಲ್ಲಿ ಕೆಲವರನ್ನು ಆರಿಸಿಕೊಂಡು ಪೌಲ ಬಾರ್ನಬರ ಜೊತೆಯಲ್ಲಿ ಅಂತಿಯೋಕ್ಯಕ್ಕೆ ಕಳುಹಿಸುವದು ಯುಕ್ತವೆಂದು ತೀರ್ಮಾನಿಸಿದರು. ಆದಕಾರಣ ಸಹೋದರರಲ್ಲಿ ಮುಖ್ಯರಾಗಿದ್ದ ಬಾರ್ಸಬ್ಬನೆನಿಸಿಕೊಳ್ಳುವ ಯೂದನನ್ನೂ ಸೀಲನನ್ನೂ ಆರಿಸಿಕೊಂಡು",
			"23": "<sup>23</sup> ಅವರ ಕೈಯಲ್ಲಿ ಬರೆದುಕೊಟ್ಟದ್ದೇನಂದರೆ - ಸಹೋದರರಾಗಿರುವ ಅಪೊಸ್ತಲರೂ ಹಿರಿಯರೂ ಅಂತಿಯೋಕ್ಯ ಸಿರಿಯ ಕಿಲಿಕ್ಯ ಸೀಮೆಗಳಲ್ಲಿ ವಾಸಿಸುವ ಅನ್ಯಜನರೊಳಗಿಂದ ಸಹೋದರರಾದವರಿಗೆ ಮಾಡುವ ವಂದನೆ;",
			"24": "<sup>24</sup> ಅದಾಗಿ ನಮ್ಮೊಳಗಿಂದ ಹೋದ ಕೆಲವರು ನಮ್ಮಿಂದ ಏನೂ ಅಪ್ಪಣೆಹೊಂದದೆ ತಮ್ಮ ಮಾತುಗಳಿಂದ ನಿಮ್ಮಲ್ಲಿ ಅಸಮಾಧಾನವನ್ನು ಹುಟ್ಟಿಸಿ ನಿಮ್ಮ ಮನಸ್ಸುಗಳನ್ನು ಕಳವಳಗೊಳಿಸಿದ್ದಾರೆಂಬದನ್ನು ಕೇಳಿದ್ದರಿಂದ ನಾವು ಕೆಲವರನ್ನು ಆರಿಸಿಕೊಂಡು",
			"25": "<sup>25</sup> ನಮ್ಮ ಕರ್ತನಾದ ಯೇಸು ಕ್ರಿಸ್ತನ ಹೆಸರಿನ ನಿಮಿತ್ತ ಜೀವದ ಹಂಗನ್ನು ತೊರೆದವರಾದ ನಮ್ಮ ಪ್ರಿಯ ಬಾರ್ನಬ ಪೌಲರ ಜೊತೆಯಲ್ಲಿ ನಿಮ್ಮ ಬಳಿಗೆ ಕಳುಹಿಸಿಕೊಡುವದು ಯುಕ್ತವೆಂದು ನಾವೆಲ್ಲರೂ ಏಕಮನಸ್ಸಾಗಿ ತೀರ್ಮಾನಿಸಿದೆವು.",
			"27": "<sup>27</sup> ಆದದರಿಂದ ಯೂದನನ್ನೂ ಸೀಲನನ್ನೂ ಕಳುಹಿಸಿದ್ದೇವೆ. ಅವರು ಈ ಸಂಗತಿಗಳನ್ನು ಬಾಯಿಮಾತಿನಿಂದಲೂ ನಿಮಗೆ ತಿಳಿಸುವರು.",
			"28": "<sup>28</sup> ವಿಗ್ರಹಕ್ಕೆ ನೈವೇದ್ಯಮಾಡಿದ್ದನ್ನೂ ರಕ್ತವನ್ನೂ ಕುತ್ತಿಗೆಹಿಸುಕಿ ಕೊಂದದ್ದನ್ನೂ ಹಾದರವನ್ನೂ ವಿಸರ್ಜಿಸುವದು ಅವಶ್ಯವಾಗಿದೆ. ಇದಕ್ಕೆ ಹೆಚ್ಚಿನ ಭಾರವನ್ನು ನಿಮ್ಮ ಮೇಲೆ ಹಾಕಬಾರದೆಂದು ಪವಿತ್ರಾತ್ಮನಿಗೂ ನಮಗೂ ವಿಹಿತವಾಗಿ ತೋರಿತು. ನೀವು ಎಚ್ಚರವಾಗಿದ್ದು ಇವುಗಳ ಗೊಡವೆಗೆ ಹೋಗದಿದ್ದರೆ ನಿಮಗೆ ಒಳ್ಳೇದಾಗುವದು. ಶುಭವಾಗಲಿ.",
			"30": "<sup>30</sup> ಅವರು ಅಪ್ಪಣೆ ತೆಗೆದುಕೊಂಡು ಅಂತಿಯೋಕ್ಯಕ್ಕೆ ಬಂದು ಮಂಡಲಿಯನ್ನು ಕೂಡಿಸಿ ಆ ಕಾಗದವನ್ನು ಅವರಿಗೆ ಒಪ್ಪಿಸಿದರು.",
			"31": "<sup>31</sup> ಅವರು ಓದಿ ಅದರಿಂದ ಧೈರ್ಯ ತಂದುಕೊಂಡು ಸಂತೋಷಪಟ್ಟರು.",
			"32": "<sup>32</sup> ಯೂದನೂ ಸೀಲನೂ ತಾವೇ ಪ್ರವಾದಿಗಳಾಗಿದ್ದದರಿಂದ ಸಹೋದರರನ್ನು ಅನೇಕ ಮಾತುಗಳಿಂದ ಪ್ರಬೋಧಿಸಿ ದೃಢಪಡಿಸಿದರು.",
			"33": "<sup>33</sup> ಅಲ್ಲಿ ಕೆಲವು ಕಾಲ ಕಳೆದನಂತರ ತಮ್ಮನ್ನು ಕಳುಹಿಸಿದ್ದವರ ಬಳಿಗೆ ತಿರಿಗಿ ಹೋಗುವದಕ್ಕೆ ಸಹೋದರರಿಂದ ಅಪ್ಪಣೆ ತೆಗೆದುಕೊಂಡರು.",
			"35": "<sup>35</sup> ಆದರೆ ಪೌಲನೂ ಬಾರ್ನಬನೂ ಅಂತಿಯೋಕ್ಯದಲ್ಲಿಯೇ ನಿಂತು ಬೇರೆ ಅನೇಕರೊಂದಿಗೆ ಉಪದೇಶಮಾಡುತ್ತಾ ಕರ್ತನ ವಾಕ್ಯವೆಂಬ ಸುವಾರ್ತೆಯನ್ನು ಸಾರುತ್ತಾ ಇದ್ದರು.",
			"36": "<sup>36</sup> ಕೆಲವು ದಿವಸಗಳಾದ ಮೇಲೆ ಪೌಲನು ಬಾರ್ನಬನಿಗೆ - ನಾವು ಕರ್ತನ ವಾಕ್ಯವನ್ನು ಸಾರಿದ ಎಲ್ಲಾ ಊರುಗಳಿಗೆ ತಿರಿಗಿ ಹೋಗಿ ಅಲ್ಲಿರುವ ಸಹೋದರರು ಹೇಗಿದ್ದಾರೆಂದು ನೋಡೋಣ ಬಾ ಎಂದು ಹೇಳಿದನು.",
			"37": "<sup>37</sup> ಬಾರ್ನಬನು ಮಾರ್ಕನೆನಿಸಿಕೊಳ್ಳುವ ಯೋಹಾನನನ್ನು ತಮ್ಮ ಸಂಗಡ ಕರೆದುಕೊಂಡುಹೋಗಬೇಕೆಂದಿದ್ದಾಗ",
			"38": "<sup>38</sup> ಪೌಲನು - ನಮ್ಮೊಂದಿಗೆ ಕೆಲಸಕ್ಕೆ ಬಾರದೆ ಪಂಫೂಲ್ಯದಲ್ಲಿ ನಮ್ಮನ್ನು ಬಿಟ್ಟವನನ್ನು ಕರೆದುಕೊಂಡು ಹೋಗುವದು ತಕ್ಕದಲ್ಲವೆಂದು ನೆನಸಿದನು.",
			"39": "<sup>39</sup> ಈ ವಿಷಯದಲ್ಲಿ ತೀಕ್ಷ್ಣ ವಾಗ್ವಾದವುಂಟಾಗಿ ಅವರು ಒಬ್ಬರನ್ನೊಬ್ಬರು ಅಗಲಿದರು. ಬಾರ್ನಬನು ಮಾರ್ಕನನ್ನು ಕರಕೊಂಡು ಸಮುದ್ರಮಾರ್ಗವಾಗಿ ಕುಪ್ರದ್ವೀಪಕ್ಕೆ ಹೋದನು.",
			"40": "<sup>40</sup> ಪೌಲನು ಸೀಲನನ್ನು ಆರಿಸಿಕೊಂಡು ಸಹೋದರರಿಂದ ಕರ್ತನ ಕೃಪಾಶ್ರಯಕ್ಕೆ ಒಪ್ಪಿಸಲ್ಪಟ್ಟವನಾಗಿ ಹೊರಟು",
			"41": "<sup>41</sup> ಸಿರಿಯ ಕಿಲಿಕ್ಯ ಸೀಮೆಗಳಲ್ಲಿ ಸಂಚರಿಸುತ್ತಾ ಸಭೆಗಳನ್ನು ದೃಢಪಡಿಸಿದನು."
		},
		"16": {
			"1": "<sup>1</sup> ಆಮೇಲೆ ಅವನು ದೆರ್ಬೆಗೂ ಲುಸ್ತ್ರಕ್ಕೂ ಬಂದನು. ಲುಸ್ತ್ರದಲ್ಲಿ ತಿಮೊಥೆಯನೆಂಬ ಒಬ್ಬ ಶಿಷ್ಯನಿದ್ದನು. ಅವನು ಕ್ರಿಸ್ತನನ್ನು ನಂಬಿದ್ದ ಒಬ್ಬ ಯೆಹೂದ್ಯ ಸ್ತ್ರೀಯ ಮಗನು. ಅವನ ತಂದೆ ಗ್ರೀಕನು.",
			"2": "<sup>2</sup> ಅವನ ವಿಷಯವಾಗಿ ಲುಸ್ತ್ರದಲ್ಲಿಯೂ ಇಕೋನ್ಯದಲ್ಲಿಯೂ ಇದ್ದ ಸಹೋದರರು ಒಳ್ಳೇ ಸಾಕ್ಷಿಹೇಳುತ್ತಿದ್ದರು.",
			"3": "<sup>3</sup> ಅವನು ತನ್ನ ಸಂಗಡ ಬರಬೇಕೆಂದು ಪೌಲನು ಅಪೇಕ್ಷಿಸಿ ಆಯಾ ಸ್ಥಳಗಳಲ್ಲಿದ್ದ ಯೆಹೂದ್ಯರ ನಿಮಿತ್ತವಾಗಿ ಅವನಿಗೆ ಸುನ್ನತಿ ಮಾಡಿಸಿದನು. ಯಾಕಂದರೆ ಅವನ ತಂದೆ ಗ್ರೀಕನೆಂದು ಎಲ್ಲರಿಗೂ ಗೊತ್ತಿತ್ತು.",
			"4": "<sup>4</sup> ಬಳಿಕ ಅವರು ಊರೂರುಗಳಲ್ಲಿ ಸಂಚಾರಮಾಡುತ್ತಿರುವಾಗ ಯೆರೂಸಲೇಮಿನಲ್ಲಿದ್ದ ಅಪೊಸ್ತಲರೂ ಸಭೆಯ ಹಿರಿಯರೂ ನಿರ್ಣಯಿಸಿದ್ದ ವಿಧಿಗಳನ್ನು ಅವರಿಗೆ ಒಪ್ಪಿಸಿ ಇವುಗಳನ್ನು ನೀವು ಅನುಸರಿಸಿ ನಡೆಯಬೇಕೆಂದು ಬೋಧಿಸಿದರು.",
			"5": "<sup>5</sup> ಸಭೆಗಳು ಕ್ರಿಸ್ತನಂಬಿಕೆಯಲ್ಲಿ ದೃಢವಾಗುತ್ತಾ ಸಂಖ್ಯೆಯಲ್ಲಿ ದಿನೇ ದಿನೇ ಹೆಚ್ಚುತ್ತಾ ಬಂದವು.",
			"6": "<sup>6</sup> ಆಸ್ಯಸೀಮೆಯಲ್ಲಿ ವಾಕ್ಯವನ್ನು ಹೇಳಬಾರದೆಂದು ಪವಿತ್ರಾತ್ಮನು ತಡೆದದ್ದರಿಂದ ಅವರು ಫ್ರುಗ್ಯ ಗಲಾತ್ಯ ಸೀಮೆಯನ್ನು ಹಾದುಹೋಗಿ",
			"7": "<sup>7</sup> ಮೂಸ್ಯಕ್ಕೆ ಎದುರಾಗಿ ಬಂದಾಗ ಬಿಥೂನ್ಯಕ್ಕೆ ಹೋಗುವ ಪ್ರಯತ್ನಮಾಡಿದರು. ಅಲ್ಲಿಯೂ ಯೇಸುವಿನ ಆತ್ಮನು ಅವರನ್ನು ಹೋಗಗೊಡಿಸಲಿಲ್ಲ.",
			"8": "<sup>8</sup> ಆಗ ಅವರು ಮೂಸ್ಯವನ್ನು ಸುಮ್ಮನೆ ದಾಟಿ ತ್ರೋವಕ್ಕೆ ಬಂದರು.",
			"9": "<sup>9</sup> ಅಲ್ಲಿ ರಾತ್ರಿಕಾಲದಲ್ಲಿ ಪೌಲನಿಗೆ ಒಂದು ದರ್ಶನವಾಯಿತು, ಏನಂದರೆ - ಮಕೆದೋನ್ಯ ದೇಶದವನಾದ ಒಬ್ಬ ಮನುಷ್ಯನು ನಿಂತುಕೊಂಡು - ನೀನು ಸಮುದ್ರವನ್ನು ದಾಟಿ ಮಕೆದೋನ್ಯಕ್ಕೆ ಬಂದು ನಮಗೆ ನೆರವಾಗಬೇಕೆಂದು ಅವನನ್ನು ಬೇಡಿಕೊಳ್ಳುತ್ತಿದ್ದನು.",
			"10": "<sup>10</sup> ಅವನಿಗೆ ಆ ದರ್ಶನವಾದ ಮೇಲೆ ಆ ಸೀಮೆಯವರಿಗೆ ಸುವಾರ್ತೆಯನ್ನು ಸಾರುವದಕ್ಕೆ ದೇವರು ನಮ್ಮನ್ನು ಕರೆದಿದ್ದಾನೆಂದು ನಾವು ನಿಶ್ಚಯಿಸಿಕೊಂಡು ಕೂಡಲೆ ಮಕೆದೋನ್ಯಕ್ಕೆ ಹೊರಟು ಹೋಗುವ ಪ್ರಯತ್ನಮಾಡಿದೆವು.",
			"11": "<sup>11</sup> ನಾವು ತ್ರೋವದಿಂದ ಸಮುದ್ರಪ್ರಯಾಣಮಾಡಿ ನೆಟ್ಟಗೆ ಸಮೊಥ್ರಾಕೆಗೆ ಬಂದು ಮರುದಿನ ನೆಯಾಪೊಲಿಗೆ ಸೇರಿ ಅಲ್ಲಿಂದ ಫಿಲಿಪ್ಪಿಗೆ ಮುಟ್ಟಿದೆವು.",
			"12": "<sup>12</sup> ಫಿಲಿಪ್ಪಿ ಎಂಬದು ಮಕೆದೋನ್ಯದಲ್ಲಿ ಪ್ರಧಾನ ಪಟ್ಟಣವೂ ರೋಮಾಪುರದವರ ಒಂದು ಪ್ರವಾಸಸ್ಥಾನವೂ ಆಗಿದೆ. ಈ ಪಟ್ಟಣದಲ್ಲಿ ನಾವು ಕೆಲವು ದಿವಸ ಇದ್ದೆವು.",
			"13": "<sup>13</sup> ನದೀತೀರದಲ್ಲಿ ದೇವರ ಪ್ರಾರ್ಥನೆ ನಡೆಯುವ ಸ್ಥಳ ಇರುವದೆಂದು ನೆನಸಿ ಸಬ್ಬತ್‍ದಿನದಲ್ಲಿ ಊರಬಾಗಿಲಿನ ಹೊರಗೆ ಹೋಗಿ ಅಲ್ಲಿ ಕೂಡಿಬಂದಿದ್ದ ಸ್ತ್ರೀಯರ ಸಂಗಡ ಕೂತುಕೊಂಡು ಮಾತಾಡಿದೆವು.",
			"14": "<sup>14</sup> ನಮ್ಮ ಮಾತುಗಳನ್ನು ಕೇಳಿದವರಲ್ಲಿ ಲುದ್ಯಳೆಂಬ ಒಬ್ಬ ಸ್ತ್ರೀಯು ಇದ್ದಳು. ಆಕೆ ಧೂಮ್ರವರ್ಣದ ವಸ್ತ್ರಗಳನ್ನು ಮಾರುವವಳೂ ಥುವತೈರ ಎಂಬ ಊರಿನವಳೂ ದೇವಭಕ್ತಳೂ ಆಗಿದ್ದಳು. ಪೌಲನು ಹೇಳಿದ ಮಾತುಗಳಿಗೆ ಲಕ್ಷ್ಯಕೊಡುವದಕ್ಕೆ ಕರ್ತನು ಆಕೆಯ ಹೃದಯವನ್ನು ತೆರೆದನು.",
			"15": "<sup>15</sup> ಆಕೆಯೂ ಆಕೆಯ ಮನೆಯವರೂ ದೀಕ್ಷಾಸ್ನಾನ ಮಾಡಿಸಿಕೊಂಡ ಮೇಲೆ ಆಕೆ - ನಾನು ಕರ್ತನನ್ನು ನಂಬಿದವಳೆಂದು ನಿಶ್ಚಯಿಸಿಕೊಂಡಿದ್ದರೆ ನನ್ನ ಮನೆಯಲ್ಲಿ ಬಂದು ಇರ್ರಿ ಎಂದು ಬೇಡಿಕೊಂಡು ನಮ್ಮನ್ನು ಬಲವಂತ ಮಾಡಿದಳು.",
			"16": "<sup>16</sup> ಬಳಿಕ ಪ್ರಾರ್ಥನೆ ನಡೆಯುವ ಆ ಸ್ಥಳಕ್ಕೆ ನಾವು ಹೋಗುತ್ತಿರುವಾಗ ಗಾರುಡಗಾತಿಯಾದ ಒಬ್ಬ ದಾಸಿಯು ನಮ್ಮೆದುರಿಗೆ ಬಂದಳು. ಅವಳು ಕಣಿ ಹೇಳುವದರಿಂದ ಅವಳ ಯಜಮಾನರಿಗೆ ಬಹು ಆದಾಯವಾಗುತ್ತಿತ್ತು.",
			"17": "<sup>17</sup> ಪೌಲನೂ ನಾವೂ ಹೋಗುತ್ತಿರುವಾಗ ಅವಳು ಹಿಂದೆ ಬಂದು - ಈ ಮನುಷ್ಯರು ಪರಾತ್ಪರನಾದ ದೇವರ ದಾಸರು; ನಿಮಗೆ ರಕ್ಷಣೆಯ ಮಾರ್ಗವನ್ನು ಸಾರುತ್ತಾರೆ ಎಂದು ಕೂಗುತ್ತಿದ್ದಳು.",
			"18": "<sup>18</sup> ಹೀಗೆ ಅನೇಕ ದಿವಸ ಮಾಡಿದ್ದರಿಂದ ಪೌಲನು ಬಹಳವಾಗಿ ಬೇಸರಗೊಂಡು ಹಿಂತಿರುಗಿ ಆ ದೆವ್ವಕ್ಕೆ - ಅವಳನ್ನು ಬಿಟ್ಟುಹೋಗು ಎಂದು ಯೇಸು ಕ್ರಿಸ್ತನ ಹೆಸರಿನಲ್ಲಿ ನಿನಗೆ ಅಪ್ಪಣೆಕೊಡುತ್ತೇನೆ ಎಂದು ಹೇಳಿದನು. ಅದೇ ಗಳಿಗೆಯಲ್ಲಿಯೇ ಅದು ಬಿಟ್ಟುಹೋಯಿತು.",
			"19": "<sup>19</sup> ಅವಳ ಯಜಮಾನರು ತಮ್ಮ ಆದಾಯದ ನಿರೀಕ್ಷೆ ತಪ್ಪಿತಲ್ಲಾ ಎಂದು ತಿಳಿದು ಪೌಲನನ್ನೂ ಸೀಲನನ್ನೂ ಹಿಡಿದು ಚಾವಡಿಗೆ ಅಧಿಕಾರಿಗಳ ಬಳಿಗೆ ಎಳಕೊಂಡು ಹೋದರು.",
			"20": "<sup>20</sup> ಅವರನ್ನು ಅಧಿಪತಿಗಳ ಮುಂದೆ ತಂದು - ಈ ಮನುಷ್ಯರು ನಮ್ಮ ಪಟ್ಟಣವನ್ನು ಬಹಳವಾಗಿ ಗಲಿಬಿಲಿ ಮಾಡುತ್ತಾರೆ;",
			"21": "<sup>21</sup> ಇವರು ಯೆಹೂದ್ಯರಾಗಿದ್ದು ರೋಮಾಯರಾದ ನಾವು ಅವಲಂಬಿಸಿ ನಡಿಸಕೂಡದಂಥ ಆಚಾರಗಳನ್ನು ಪ್ರಕಟಿಸುತ್ತಾರೆ ಅಂದರು.",
			"22": "<sup>22</sup> ಆಗ ದೊಂಬೀ ಜನರು ಕೂಡಿ ಅವರಿಗೆ ವಿರೋಧವಾಗಿ ಒಟ್ಟಾಗಿ ಎದ್ದರು; ಮತ್ತು ಅಧಿಪತಿಗಳು - ಇವರ ವಸ್ತ್ರಗಳನ್ನು ಹರಿದು ತೆಗೆದು ಚಡಿಗಳಿಂದ ಹೊಡೆಯಬೇಕೆಂದು ಅಪ್ಪಣೆಕೊಟ್ಟರು.",
			"23": "<sup>23</sup> ಅವರಿಗೆ ಬಹಳ ಪೆಟ್ಟುಗಳನ್ನು ಹೊಡಿಸಿದ ಮೇಲೆ ಸೆರೆಮನೆಯೊಳಗೆ ಹಾಕಿಸಿ - ಇವರನ್ನು ಭದ್ರವಾಗಿ ಕಾಯಬೇಕೆಂದು ಸೆರೆಯ ಯಜಮಾನನಿಗೆ ಖಂಡಿತವಾಗಿ ಹೇಳಿದರು.",
			"24": "<sup>24</sup> ಅವನು ಇಂಥಾ ಅಪ್ಪಣೆಯನ್ನು ಹೊಂದಿ ಅವರನ್ನು ಸೆರೆಮನೆಯ ಒಳಕೋಣೆಯಲ್ಲಿ ಹಾಕಿ ಅವರ ಕಾಲುಗಳಿಗೆ ಕೋಳವನ್ನು ಹಾಕಿ ಬಿಗಿಸಿದನು.",
			"25": "<sup>25</sup> ಮಧ್ಯರಾತ್ರಿಯಲ್ಲಿ ಪೌಲನೂ ಸೀಲನೂ ಪ್ರಾರ್ಥನೆ ಮಾಡುವವರಾಗಿ ದೇವರಿಗೆ ಸ್ತುತಿಪದಗಳನ್ನು ಹಾಡುತ್ತಿದ್ದರು; ಸೆರೆಯಲ್ಲಿದ್ದವರು ಲಕ್ಷ್ಯವಿಟ್ಟು ಕೇಳುತ್ತಿದ್ದರು.",
			"26": "<sup>26</sup> ಅಕಸ್ಮಾತ್ತಾಗಿ ಮಹಾಭೂಕಂಪವುಂಟಾಯಿತು; ಸೆರೆಮನೆಯ ಅಸ್ತಿವಾರಗಳು ಕದಲಿದವು. ಅದೇ ಕ್ಷಣದಲ್ಲಿ ಕದಗಳೆಲ್ಲಾ ತೆರೆದವು, ಎಲ್ಲರ ಬೇಡಿಗಳು ಕಳಚಿಬಿದ್ದವು.",
			"27": "<sup>27</sup> ಸೆರೆಯ ಯಜಮಾನನು ನಿದ್ದೆಯಿಂದ ಎಚ್ಚತ್ತು ಸೆರೆಮನೆಯ ಕದಗಳು ತೆರೆದಿರುವದನ್ನು ಕಂಡು ಸೆರೆಯಲ್ಲಿದ್ದವರು ಓಡಿಹೋದರೆಂದು ಭಾವಿಸಿ ಕತ್ತಿಯನ್ನು ಹಿರಿದು ಆತ್ಮಹತ್ಯ ಮಾಡಿಕೊಳ್ಳಬೇಕೆಂದಿದ್ದನು.",
			"28": "<sup>28</sup> ಆದರೆ ಪೌಲನು ಮಹಾಶಬ್ದದಿಂದ ಕೂಗಿ ಅವನಿಗೆ - ನೀನೇನೂ ಕೇಡು ಮಾಡಿಕೊಳ್ಳಬೇಡ, ನಾವೆಲ್ಲರೂ ಇಲ್ಲೇ ಇದ್ದೇವೆ ಎಂದು ಹೇಳಲು",
			"29": "<sup>29</sup> ಅವನು - ದೀಪ ತರಬೇಕೆಂದು ಕೂಗಿ ಒಳಕ್ಕೆ ಹಾರಿ ನಡುಗುತ್ತಾ ಪೌಲ ಸೀಲರ ಮುಂದೆ ಬಿದ್ದನು.",
			"30": "<sup>30</sup> ಮತ್ತು ಅವರನ್ನು ಹೊರಗೆ ಕರಕೊಂಡು ಬಂದು - ಸ್ವಾಮಿಗಳೇ, ನಾನು ರಕ್ಷಣೆಹೊಂದುವದಕ್ಕೆ ಏನು ಮಾಡಬೇಕೆಂದು ಕೇಳಲು",
			"31": "<sup>31</sup> ಅವರು - ಕರ್ತನಾದ ಯೇಸುವಿನ ಮೇಲೆ ನಂಬಿಕೆಯಿಡು, ಆಗ ನೀನು ರಕ್ಷಣೆಹೊಂದುವಿ, ನಿನ್ನ ಮನೆಯವರೂ ರಕ್ಷಣೆಹೊಂದುವರು ಎಂದು ಹೇಳಿ ಅವನಿಗೂ ಅವನ ಮನೆಯಲ್ಲಿದ್ದವರೆಲ್ಲರಿಗೂ ದೇವರ ವಾಕ್ಯವನ್ನು ತಿಳಿಸಿದರು.",
			"32": "<sup>32</sup> ಆಮೇಲೆ ಅವನು ರಾತ್ರಿಯ ಅದೇ ಗಳಿಗೆಯಲ್ಲಿ",
			"33": "<sup>33</sup> ಅವರನ್ನು ಕರಕೊಂಡುಹೋಗಿ ಅವರ ಗಾಯಗಳನ್ನು ತೊಳೆದು ಕೂಡಲೆ ತಾನು ತನ್ನವರೆಲ್ಲರ ಸಹಿತವಾಗಿ ದೀಕ್ಷಾಸ್ನಾನಮಾಡಿಸಿಕೊಂಡನು.",
			"34": "<sup>34</sup> ತರುವಾಯ ಅವರನ್ನು ತನ್ನ ಮನೆಗೆ ಕರಕೊಂಡು ಹೋಗಿ ಊಟಮಾಡಿಸಿದನು. ಅವನು ತನ್ನ ಮನೆಯವರೆಲ್ಲರ ಸಂಗಡ ನಂಬಿ ಉಲ್ಲಾಸಗೊಂಡನು.",
			"35": "<sup>35</sup> ಬೆಳಗಾದ ಮೇಲೆ ಅಧಿಪತಿಗಳು ಆ ಮನುಷ್ಯರನ್ನು ಬಿಟ್ಟುಬಿಡು ಎಂದು ಜವಾನರ ಕೈಯಲ್ಲಿ ಹೇಳಿಕಳುಹಿಸಿದರು.",
			"36": "<sup>36</sup> ಸೆರೇಯಜಮಾನನು ಈ ಮಾತನ್ನು ಪೌಲನಿಗೆ ತಿಳಿಸಿ - ಅಧಿಪತಿಗಳು ನಿಮ್ಮನ್ನು ಬಿಟ್ಟುಬಿಡಬೇಕೆಂದು ಹೇಳಿಕಳುಹಿಸಿದ್ದಾರೆ, ಆದದರಿಂದ ಈಗ ನೀವು ಸಮಾಧಾನದಿಂದ ಹೊರಟುಹೋಗಿರಿ ಎಂದು ಹೇಳಿದನು.",
			"37": "<sup>37</sup> ಅದಕ್ಕೆ ಪೌಲನು - ಅವರು ವಿಚಾರಣೆಮಾಡದೆ ರೋಮಾಪುರದ ಹಕ್ಕುದಾರರಾದ ನಮ್ಮನ್ನು ಎಲ್ಲರ ಮುಂದೆ ಹೊಡಿಸಿ ಸೆರೆಮನೆಯೊಳಗೆ ಹಾಕಿಸಿದರು; ಈಗ ನಮ್ಮನ್ನು ಗುಪ್ತವಾಗಿ ಹೊರಗೆ ಕಳುಹಿಸುತ್ತಾರೋ? ಹಾಗೆ ಎಂದಿಗೂ ಆಗಕೂಡದು; ಅವರೇ ಬಂದು ನಮ್ಮನ್ನು ಹೊರಗೆ ಕರಕೊಂಡುಹೋದರೆ ಸರಿ ಎಂದು ಹೇಳಿದನು.",
			"38": "<sup>38</sup> ಜವಾನರು ಈ ಮಾತುಗಳನ್ನು ಅಧಿಪತಿಗಳಿಗೆ ತಿಳಿಸಿದಾಗ ರೋಮಾಪುರದ ಹಕ್ಕುದಾರರು ಎಂಬ ಮಾತನ್ನು ಅವರು ಕೇಳಿ ಭಯಪಟ್ಟರು;",
			"39": "<sup>39</sup> ಮತ್ತು ಅವರ ಬಳಿಗೆ ಹೋಗಿ ವಿನಯವಾಗಿ ಮಾತಾಡಿ ಹೊರಕ್ಕೆ ಕರೆದುಕೊಂಡು ಬಂದು - ನೀವು ಊರನ್ನು ಬಿಟ್ಟುಹೋಗಬೇಕೆಂದು ಬೇಡಿಕೊಂಡರು.",
			"40": "<sup>40</sup> ಆಗ ಅವರು ಸೆರೆಮನೆಯೊಳಗಿಂದ ಹೊರಟು ಲುದ್ಯಳ ಮನೆಗೆ ಬಂದು ಸಹೋದರರನ್ನು ನೋಡಿ ಅವರನ್ನು ಧೈರ್ಯಗೊಳಿಸಿ ಹೊರಟುಹೋದರು."
		},
		"17": {
			"1": "<sup>1</sup> ಮುಂದೆ ಅವರು ಮಾರ್ಗಹಿಡಿದು ಅಂಫಿಪೊಲಿ ಅಪೊಲೋನ್ಯ ಎಂಬ ಊರುಗಳನ್ನು ದಾಟಿ ಥೆಸಲೋನಿಕಕ್ಕೆ ಬಂದರು. ಅಲ್ಲಿ ಯೆಹೂದ್ಯರದೊಂದು ಸಭಾಮಂದಿರವಿತ್ತು.",
			"2": "<sup>2</sup> ಪೌಲನು ತನ್ನ ಪದ್ಧತಿಯ ಪ್ರಕಾರ ಅಲ್ಲಿದ್ದವರ ಬಳಿಗೆ ಹೋಗಿ ಮೂರು ಸಬ್ಬತ್‍ದಿನಗಳಲ್ಲಿ ಶಾಸ್ತ್ರಾಧಾರದಿಂದ ಅವರ ಸಂಗಡ ವಾದಿಸಿ",
			"3": "<sup>3</sup> ಆಯಾ ವಚನಗಳ ಅರ್ಥವನ್ನು ಬಿಚ್ಚಿ - ಕ್ರಿಸ್ತನು ಬಾಧೆಯನ್ನನುಭವಿಸಿ ಸತ್ತು ಎದ್ದುಬರುವದು ಅಗತ್ಯವೆಂತಲೂ ನಾನು ನಿಮಗೆ ಪ್ರಸಿದ್ಧಿಪಡಿಸುವ ಯೇಸುವೇ ಆ ಕ್ರಿಸ್ತನೆಂತಲೂ ಸ್ಥಾಪಿಸಿದನು.",
			"4": "<sup>4</sup> ಅವರಲ್ಲಿ ಕೆಲವರೂ ದೇವಭಕ್ತರಾದ ಗ್ರೀಕರಲ್ಲಿ ದೊಡ್ಡ ಗುಂಪೂ ಪ್ರಮುಖ ಸ್ತ್ರೀಯರಲ್ಲಿ ಅನೇಕರೂ ಒಡಂಬಟ್ಟು ಪೌಲ ಸೀಲರನ್ನು ಸೇರಿಕೊಂಡರು.",
			"5": "<sup>5</sup> ಯೆಹೂದ್ಯರು ಹೊಟ್ಟೇಕಿಚ್ಚುಪಟ್ಟು ಪೇಟೆಯಲ್ಲಿ ತಿರುಗಾಡುವ ಕೆಲವು ಪೋಕರನ್ನು ಕರೆದುಕೊಂಡು ಬಂದು ಗುಂಪುಕೂಡಿಸಿ ಊರಲ್ಲಿ ಗದ್ದಲ ಎಬ್ಬಿಸಿ ಪೌಲ ಸೀಲರನ್ನು ಪಟ್ಟಣದ ಸಭೆಗೆದುರಿಗೆ ತರಬೇಕೆಂದು ಅವರನ್ನು ಹುಡುಕುತ್ತಾ ಯಾಸೋನನ ಮನೆಯ ಮೇಲೆ ಬಿದ್ದರು.",
			"6": "<sup>6</sup> ಅವರು ಸಿಕ್ಕದೆ ಹೋದದ್ದರಿಂದ ಆ ಜನರು ಯಾಸೋನನನ್ನೂ ಕೆಲವು ಮಂದಿ ಸಹೋದರರನ್ನೂ ಊರಿನ ಅಧಿಕಾರಿಗಳ ಬಳಿಗೆ ಎಳಕೊಂಡು ಹೋಗಿ - ಲೋಕವನ್ನು ಅಲ್ಲಕಲ್ಲೋಲ ಮಾಡಿದ ಆ ಮನುಷ್ಯರು ಇಲ್ಲಿಗೂ ಬಂದಿದ್ದಾರೆ,",
			"7": "<sup>7</sup> ಯಾಸೋನನು ಅವರನ್ನು ಸೇರಿಸಿಕೊಂಡಿದ್ದಾನೆ, ಅವರೆಲ್ಲರು ಯೇಸುವೆಂಬ ಬೇರೊಬ್ಬ ಅರಸನು ಇದ್ದಾನೆಂದು ಹೇಳಿ ಚಕ್ರವರ್ತಿಯ ಆಜ್ಞೆಗಳಿಗೆ ವಿರುದ್ಧವಾಗಿ ನಡೆಯುತ್ತಾರೆ ಎಂದು ಕೂಗಿದರು.",
			"8": "<sup>8</sup> ಪಟ್ಟಣದವರೂ ಪಟ್ಟಣದ ಅಧಿಕಾರಿಗಳೂ ಈ ಮಾತುಗಳನ್ನು ಕೇಳಿ ಕಳವಳಪಟ್ಟು",
			"9": "<sup>9</sup> ಯಾಸೋನ ಮೊದಲಾದವರಿಂದ ಹೊಣೆ ತೆಗೆದುಕೊಂಡರು; ತೆಗೆದುಕೊಂಡ ಮೇಲೆ ಅವರನ್ನು ಬಿಟ್ಟುಬಿಟ್ಟರು.",
			"10": "<sup>10</sup> ಕೂಡಲೇ ಸಹೋದರರು ರಾತ್ರಿಯಲ್ಲಿ ಪೌಲ ಸೀಲರನ್ನು ಬೆರೋಯಕ್ಕೆ ಕಳುಹಿಸಿಬಿಟ್ಟರು. ಅವರು ಅಲ್ಲಿಗೆ ಸೇರಿ ಯೆಹೂದ್ಯರ ಸಭಾಮಂದಿರದೊಳಕ್ಕೆ ಹೋದರು.",
			"11": "<sup>11</sup> ಆ ಸಭೆಯವರು ಥೆಸಲೋನಿಕದವರಿಗಿಂತ ಸದ್ಗುಣವುಳ್ಳವರಾಗಿದ್ದು ದೇವರ ವಾಕ್ಯವನ್ನು ಶುದ್ಧಮನಸ್ಸಿನಿಂದ ಅಂಗೀಕರಿಸಿ ಇವರು ಹೇಳುವ ಮಾತು ಹೌದೋ ಏನೋ ಎಂಬ ವಿಷಯದಲ್ಲಿ ಪ್ರತಿದಿನವೂ ಶಾಸ್ತ್ರಗ್ರಂಥಗಳನ್ನು ಶೋಧಿಸುತ್ತಿದ್ದರು.",
			"12": "<sup>12</sup> ಹೀಗಿರಲಾಗಿ ಅವರಲ್ಲಿ ಬಹಳ ಮಂದಿ ನಂಬಿದರು; ಅದಲ್ಲದೆ ಕುಲೀನರಾದ ಗ್ರೀಕ್‍ಹೆಂಗಸರಲ್ಲಿಯೂ ಗಂಡಸರಲ್ಲಿಯೂ ಅನೇಕರು ನಂಬಿದರು.",
			"13": "<sup>13</sup> ಆದರೆ ಪೌಲನು ಬೆರೋಯದಲ್ಲಿಯೂ ದೇವರ ವಾಕ್ಯವನ್ನು ಪ್ರಸಿದ್ಧಿಪಡಿಸುತ್ತಾನೆಂಬ ವರ್ತಮಾನವು ಥೆಸಲೋನಿಕದ ಯೆಹೂದ್ಯರಿಗೆ ತಿಳಿದುಬಂದಾಗ ಅವರು ಅಲ್ಲಿಗೂ ಬಂದು ಜನರ ಗುಂಪುಗಳನ್ನು ರೇಗಿಸಿ ಕಲಕಿದರು.",
			"14": "<sup>14</sup> ಕೂಡಲೇ ಸಹೋದರರು ಪೌಲನನ್ನು ಸಮುದ್ರದ ತನಕ ಹೋಗುವದಕ್ಕೆ ಕಳುಹಿಸಿಕೊಟ್ಟರು. ಆದರೆ ಸೀಲನೂ ತಿಮೊಥೆಯನೂ ಅಲ್ಲೇ ನಿಂತರು.",
			"15": "<sup>15</sup> ಪೌಲನನ್ನು ಸಾಗಕಳುಹಿಸಿದವರು ಅವನನ್ನು ಅಥೇನೆಯವರೆಗೂ ಕರೆದುಕೊಂಡುಹೋಗಿ - ಸೀಲನನ್ನೂ ತಿಮೊಥೆಯನನ್ನೂ ಕೂಡಿದ ಮಟ್ಟಿಗೆ ಬೇಗ ನನ್ನ ಬಳಿಗೆ ಬರಹೇಳಿರಿ ಎಂಬ ಅಪ್ಪಣೆಯನ್ನು ಅವನಿಂದ ಹೊಂದಿ ಹೊರಟುಬಂದರು.",
			"16": "<sup>16</sup> ಪೌಲನು ಅವರಿಗೋಸ್ಕರ ಅಥೇನೆಯಲ್ಲಿ ಕಾದುಕೊಂಡಿರುವಾಗ ಆ ಪಟ್ಟಣದಲ್ಲಿ ಎಲ್ಲೆಲ್ಲಿಯೂ ವಿಗ್ರಹಗಳೇ ಇರುವದನ್ನು ನೋಡಿ ಅವನ ಮನಸ್ಸು ಅವನೊಳಗೆ ಕುದಿಯಿತು.",
			"17": "<sup>17</sup> ಆದದರಿಂದ ಅವನು ಸಭಾಮಂದಿರದಲ್ಲಿ ಯೆಹೂದ್ಯರ ಮತ್ತು ಯೆಹೂದ್ಯ ಮತಾವಲಂಬಿಗಳ ಸಂಗಡಲೂ ಪ್ರತಿದಿನ ಪೇಟೆಯಲ್ಲಿ ಕಂಡುಬಂದವರ ಸಂಗಡಲೂ ವಾದಿಸಿದನು.",
			"18": "<sup>18</sup> ಇದಲ್ಲದೆ ಎಪಿಕೂರಿಯರು ಸ್ತೋಯಿಕರು ಎಂಬ ತತ್ವವಿಚಾರಕರಲ್ಲಿ ಕೆಲವರು ಅವನ ಸಂಗಡ ಚರ್ಚೆಮಾಡಿದರು. ಅವರಲ್ಲಿ ಕೆಲವರು - ಈ ಮಾತಾಳಿ ಏನು ಹೇಳಬೇಕೆಂದಿದ್ದಾನೆ? ಅಂದರು. ಅವನು ಯೇಸುವಿನ ವಿಷಯವಾಗಿಯೂ ಸತ್ತವರು ಎದ್ದುಬರುವರೆಂಬುವ ವಿಷಯವಾಗಿಯೂ ಸಾರುತ್ತಿದ್ದದರಿಂದ - ಇವನು ಅನ್ಯದೇಶದ ದೈವಗಳನ್ನು ಪ್ರಸಿದ್ಧಿಪಡಿಸುವವನಾಗಿ ತೋರುತ್ತಾನೆಂದು ಬೇರೆ ಕೆಲವರು ಹೇಳಿದರು.",
			"19": "<sup>19</sup> ಅವರು ಅವನನ್ನು ಹಿಡಿದು ಅರಿಯೊಪಾಗಕ್ಕೆ ಕರೆದುಕೊಂಡು ಹೋಗಿ - ನೀನು ಹೇಳುವ ಈ ನೂತನತರವಾದ ಉಪದೇಶವನ್ನು ನಾವು ತಿಳಿಯಬಹುದೇ?",
			"20": "<sup>20</sup> ಅಪೂರ್ವವಾದ ಸಂಗತಿಗಳನ್ನು ನಮಗೆ ಶ್ರುತಪಡಿಸುತ್ತೀಯಲ್ಲಾ; ಆದಕಾರಣ ಅವೇನಿದ್ದಾವು ತಿಳಿಯಬೇಕೆಂದು ನಮಗೆ ಅಪೇಕ್ಷೆಯದೆ ಅಂದರು.",
			"21": "<sup>21</sup> ಅಥೇನೆಯರೂ ಅಲ್ಲಿ ವಾಸವಾಗಿದ್ದ ಪರಸ್ಥಳದವರೂ ಹೊಸ ಹೊಸ ಸಂಗತಿಗಳನ್ನು ಹೇಳುವದಕ್ಕೂ ಕೇಳುವದಕ್ಕೂ ಹೊರತು ಬೇರೆ ಯಾವದಕ್ಕೂ ಸಮಯ ಕೊಡುತ್ತಿರಲಿಲ್ಲ.",
			"22": "<sup>22</sup> ಪೌಲನು ಅರಿಯೋಪಾಗದ ಮಧ್ಯದಲ್ಲಿ ನಿಂತುಕೊಂಡು ಹೇಳಿದ್ದೇನಂದರೆ - ಅಥೇನೆಯ ಜನರೇ, ನೀವು ಎಲ್ಲಾದರಲ್ಲೂ ಅತಿಭಕ್ತಿವಂತರೆಂದು ನನಗೆ ತೋರುತ್ತದೆ.",
			"23": "<sup>23</sup> ನಾನು ನಿಮ್ಮ ಪಟ್ಟಣದಲ್ಲಿ ತಿರುಗಾಡುತ್ತಾ ನೀವು ಪೂಜಿಸುವ ದೇವತಾಪ್ರತಿಮೆಗಳನ್ನು ಚೆನ್ನಾಗಿ ನೋಡುತ್ತಾ ಇದ್ದಾಗ ಒಂದು ಬಲಿಪೀಠವು ನನ್ನ ಕಣ್ಣಿಗೆ ಬಿತ್ತು; ಅದರ ಮೇಲೆ ತಿಳಿಯದ ದೇವರಿಗೆ ಎಂದು ಬರೆದಿತ್ತು. ಆದಕಾರಣ ನೀವು ಯಾವದನ್ನು ತಿಳಿಯದೆ ಪೂಜಿಸುತ್ತೀರೋ ಅದನ್ನೇ ನಿಮಗೆ ತಿಳಿಯಪಡಿಸುತ್ತೇನೆ, ಕೇಳಿರಿ.",
			"24": "<sup>24</sup> ಜಗತ್ತನ್ನೂ ಅದರಲ್ಲಿರುವ ಎಲ್ಲಾ ವಸ್ತುಗಳನ್ನೂ ಉಂಟುಮಾಡಿದ ದೇವರು ಭೂಮ್ಯಾಕಾಶಗಳ ಒಡೆಯನಾಗಿರುವದರಿಂದ ಆತನು ಕೈಯಿಂದ ಕಟ್ಟಿರುವ ಗುಡಿಗಳಲ್ಲಿ ವಾಸಮಾಡುವವನಲ್ಲ;",
			"25": "<sup>25</sup> ತಾನೇ ಎಲ್ಲರಿಗೂ ಜೀವಶ್ವಾಸ ಮುಂತಾದದ್ದೆಲ್ಲವನ್ನೂ ಕೊಡುವವನಾಗಿರಲಾಗಿ ಕೊರತೆಯಿದ್ದವನಂತೆ ಮನುಷ್ಯರ ಕೈಗಳಿಂದ ಸೇವೆಹೊಂದುವವನೂ ಅಲ್ಲ.",
			"26": "<sup>26</sup> ಆತನು ಒಬ್ಬನಿಂದಲೇ ಎಲ್ಲಾ ಜನಾಂಗದವರನ್ನು ಹುಟ್ಟಿಸಿ ಅವರವರು ಇರತಕ್ಕ ಕಾಲಗಳನ್ನೂ ಅವರವರ ನಿವಾಸಗಳ ಮೇರೆಗಳನ್ನೂ ನಿಷ್ಕರ್ಷಿಸಿ ಭೂಮಂಡಲದಲ್ಲೆಲ್ಲಾ ವಾಸಮಾಡಿ",
			"27": "<sup>27</sup> ಒಂದು ವೇಳೆ ಅವರು ತಡವಾಡಿ ಕಂಡುಕೊಂಡಾರೇನೋ ಎಂದು ತನ್ನನ್ನು ಹುಡುಕುವವರನ್ನಾಗಿ ಮಾಡಿದನು. ಆತನು ನಮ್ಮಲ್ಲಿ ಒಬ್ಬನಿಗೂ ದೂರವಾದವನಲ್ಲ.",
			"28": "<sup>28</sup> ಆತನಲ್ಲಿಯೇ ನಾವು ಜೀವಿಸುತ್ತೇವೆ, ಚಲಿಸುತ್ತೇವೆ, ಇರುತ್ತೇವೆ. ನಿಮ್ಮ ಕವಿಗಳಲ್ಲಿಯೂ ಕೆಲವರು - ನಾವು ಆತನ ಸಂತಾನದವರೇ ಎಂಬದಾಗಿ ಹೇಳಿದ್ದಾರೆ.",
			"29": "<sup>29</sup> ನಾವು ದೇವರ ಸಂತಾನದವರಾಗಿದ್ದ ಮೇಲೆ ದೈವವು ಮನುಷ್ಯನ ಶಿಲ್ಪವಿದ್ಯೆಯಿಂದಲೂ ಕಲ್ಪನೆಯಿಂದಲೂ ಕೆತ್ತಿರುವ ಚಿನ್ನ ಬೆಳ್ಳಿ ಕಲ್ಲುಗಳಿಗೆ ಸಮಾನವೆಂದು ನಾವು ಭಾವಿಸಬಾರದು.",
			"30": "<sup>30</sup> ಆ ಅಜ್ಞಾನಕಾಲಗಳನ್ನು ದೇವರು ಲಕ್ಷ್ಯಕ್ಕೆ ತರಲಿಲ್ಲ; ಈಗಲಾದರೋ ಆತನು ನಾಲ್ಕು ದಿಕ್ಕಿನಲ್ಲಿರುವ ಮನುಷ್ಯರೆಲ್ಲರೂ ತನ್ನ ಕಡೆಗೆ ತಿರುಗಿಕೊಳ್ಳಬೇಕೆಂದು ಅಪ್ಪಣೆಕೊಡುತ್ತಾನೆ.",
			"31": "<sup>31</sup> ಯಾಕಂದರೆ ಆತನು ನಿಷ್ಕರ್ಷೆಮಾಡಿದ ಪುರುಷನ ಕೈಯಿಂದ ನೀತಿಗನುಸಾರವಾಗಿ ಭೂಲೋಕದ ನ್ಯಾಯವಿಚಾರಣೆ ಮಾಡುವದಕ್ಕೆ ಒಂದು ದಿವಸವನ್ನು ಗೊತ್ತುಮಾಡಿದ್ದಾನೆ. ಅವನನ್ನು ಸತ್ತವರೊಳಗಿಂದ ಎಬ್ಬಿಸಿದ್ದರಲ್ಲಿ ಇದನ್ನು ನಂಬುವದಕ್ಕೆ ಎಲ್ಲರಿಗೂ ಆಧಾರ ಕೊಟ್ಟಿದ್ದಾನೆ ಅಂದನು.",
			"32": "<sup>32</sup> ಸತ್ತವರು ಎದ್ದುಬರುವ ವಿಷಯವನ್ನು ಕೇಳಿದಾಗ ಕೆಲವರು ಅಪಹಾಸ್ಯಮಾಡಿದರು; ಬೇರೆ ಕೆಲವರು - ನೀನು ಈ ವಿಷಯದಲ್ಲಿ ಹೇಳುವದನ್ನು ನಾವು ಇನ್ನೊಂದು ಸಾರಿ ಕೇಳುತ್ತೇವೆ ಅಂದರು.",
			"33": "<sup>33</sup> ಹೀಗಿರಲು ಪೌಲನು ಅವರ ಮಧ್ಯದಿಂದ ಹೊರಟುಹೋದನು.",
			"34": "<sup>34</sup> ಕೆಲವರು ಅವನನ್ನು ಅಂಟಿಕೊಂಡು ನಂಬಿದರು; ನಂಬಿದವರಲ್ಲಿ ಅರಿಯೊಪಾಗದ ಸಭೆಯವನಾದ ದಿಯೊನುಸ್ಯನೂ ದಾಮರಿಯೆಂಬಾಕೆಯೂ ಇನ್ನು ಕೆಲವರೂ ಇದ್ದರು."
		},
		"18": {
			"1": "<sup>1</sup> ಇದಾದ ಮೇಲೆ ಪೌಲನು ಅಥೇನೆಯನ್ನು ಬಿಟ್ಟು ಕೊರಿಂಥಕ್ಕೆ ಹೋದನು.",
			"2": "<sup>2</sup> ಪೊಂತದಲ್ಲಿ ಹುಟ್ಟಿದ ಅಕ್ವಿಲನೆಂಬ ಒಬ್ಬ ಯೆಹೂದ್ಯನನ್ನು ಅಲ್ಲಿ ಕಂಡನು. ಯೆಹೂದ್ಯರೆಲ್ಲರೂ ರೋಮಾಪುರವನ್ನು ಬಿಟ್ಟುಹೋಗಬೇಕೆಂದು ಕ್ಲೌದಿಯ ಚಕ್ರವರ್ತಿಯು ವಿಧಿಸಿದ ಕಾರಣ ಅಕ್ವಿಲನೂ ಅವನ ಹೆಂಡತಿಯಾದ ಪ್ರಿಸ್ಕಿಲ್ಲಳೂ ಕೆಲವು ದಿವಸಗಳ ಮುಂಚೆ ಇತಾಲ್ಯ ದೇಶದಿಂದ ಅಲ್ಲಿಗೆ ಬಂದಿದ್ದರು.",
			"3": "<sup>3</sup> ಪೌಲನು ಅವರ ಮನೆಗೆ ಹೋಗಿ ತಾನೂ ಅವರೂ ಒಂದೇ ಕಸಬಿನವರಾಗಿದ್ದದರಿಂದ ಅವರಲ್ಲಿದ್ದು ಅವರೊಂದಿಗೆ ಕೆಲಸಮಾಡುತ್ತಿದ್ದನು; ಗುಡಾರ ಮಾಡುವದು ಅವರ ಕಸಬಾಗಿತ್ತು.",
			"4": "<sup>4</sup> ಅವನು ಪ್ರತಿ ಸಬ್ಬತ್ ದಿನವೂ ಸಭಾಮಂದಿರದಲ್ಲಿ ಚರ್ಚಿಸಿ ಯೆಹೂದ್ಯರನ್ನೂ ಗ್ರೀಕರನ್ನೂ ಒಡಂಬಡಿಸುತ್ತಿದ್ದನು.",
			"5": "<sup>5</sup> ಸೀಲನೂ ತಿಮೊಥೆಯನೂ ಮಕೆದೋನ್ಯದಿಂದ ಬಂದಾಗ ಪೌಲನು ದೇವರ ವಾಕ್ಯವನ್ನು ಬೋಧಿಸುವದರಲ್ಲಿ ಅತ್ಯಾಸಕ್ತಿಯುಳ್ಳವನಾಗಿ ಯೇಸುವೇ ಬರಬೇಕಾದ ಕ್ರಿಸ್ತನೆಂದು ಯೆಹೂದ್ಯರಿಗೆ ಖಂಡಿತವಾಗಿ ಸಾಕ್ಷಿ ಹೇಳಿದನು.",
			"6": "<sup>6</sup> ಅವರು ಎದುರಿಸಿ ದೂಷಿಸಲಾಗಿ ಅವನು ತನ್ನ ವಸ್ತ್ರಗಳನ್ನು ಝಾಡಿಸಿ ಅವರಿಗೆ - ನಿಮ್ಮ ನಾಶನಕ್ಕೆ ನೀವೇ ಹೊಣೆ. ನಾನು ಇಂದಿನಿಂದ ಶುದ್ಧಮನಸ್ಸುಳ್ಳವನಾಗಿ ಅನ್ಯಜನರ ಬಳಿಗೆ ಹೋಗುತ್ತೇನೆ ಎಂದು ಹೇಳಿ",
			"7": "<sup>7</sup> ಆ ಸ್ಥಳವನ್ನು ಬಿಟ್ಟು ದೇವರಿಗೆ ಭಯಪಡುವವನಾಗಿದ್ದ ತೀತಯುಸ್ತನೆಂಬವನ ಮನೆಗೆ ಹೋದನು. ಅವನ ಮನೆ ಸಭಾಮಂದಿರದ ಮಗ್ಗುಲಲ್ಲೇ ಇತ್ತು.",
			"8": "<sup>8</sup> ಸಭಾಮಂದಿರದ ಅಧ್ಯಕ್ಷನಾದ ಕ್ರಿಸ್ಪನು ತನ್ನ ಮನೆಯವರೆಲ್ಲರ ಸಹಿತ ಕರ್ತನಲ್ಲಿ ನಂಬಿಕೆಯಿಟ್ಟನು. ಮತ್ತು ಕೊರಿಂಥದವರಲ್ಲಿ ಅನೇಕರು ಕೇಳಿ ನಂಬಿ ದೀಕ್ಷಾಸ್ನಾನ ಮಾಡಿಸಿಕೊಂಡರು.",
			"9": "<sup>9</sup> ಇದಲ್ಲದೆ ಕರ್ತನು ರಾತ್ರಿಕಾಲದಲ್ಲಿ ಪೌಲನಿಗೆ ದರ್ಶನಕೊಟ್ಟು - ನೀನು ಹೆದರಬೇಡ; ಸುಮ್ಮನಿರದೆ ಮಾತಾಡುತ್ತಲೇ ಇರು,",
			"10": "<sup>10</sup> ನಾನೇ ನಿನ್ನೊಂದಿಗಿದ್ದೇನೆ; ಯಾರೂ ನಿನ್ನ ಮೇಲೆ ಬಿದ್ದು ಕೇಡು ಮಾಡುವದಿಲ್ಲ; ಈ ಪಟ್ಟಣದಲ್ಲಿ ನನಗೆ ಬಹಳ ಮಂದಿ ಇದ್ದಾರೆ ಎಂದು ಹೇಳಿದನು.",
			"11": "<sup>11</sup> ಅವನು ಒಂದು ವರುಷ ಆರು ತಿಂಗಳು ಅಲ್ಲೇ ಇದ್ದುಕೊಂಡು ಆ ಜನರಿಗೆ ದೇವರ ವಾಕ್ಯವನ್ನು ಉಪದೇಶಮಾಡುತ್ತಿದ್ದನು.",
			"12": "<sup>12</sup> ಗಲ್ಲಿಯೋನನು ಅಖಾಯದ ಅಧಿಪತಿಯಾಗಿರಲು ಯೆಹೂದ್ಯರು ಒಗ್ಗಟ್ಟಾಗಿ ಪೌಲನ ಮೇಲೆ ಬಿದ್ದು ಅವನನ್ನು ನ್ಯಾಯಸ್ಥಾನದ ಮುಂದೆ ಹಿಡಿತಂದು -",
			"13": "<sup>13</sup> ಇವನು ದೇವರನ್ನು ಧರ್ಮಶಾಸ್ತ್ರಕ್ಕೆ ವಿರುದ್ಧವಾಗಿ ಪೂಜಿಸುವದಕ್ಕೆ ಜನರನ್ನು ಒಡಂಬಡಿಸುತ್ತಾನೆಂದು ದೂರು ಹೇಳಿದರು.",
			"14": "<sup>14</sup> ಪೌಲನು ಪ್ರತಿವಾದ ಮಾಡಬೇಕೆಂದಿದ್ದಾಗ ಗಲ್ಲಿಯೋನನು ಯೆಹೂದ್ಯರಿಗೆ - ಎಲೈ ಯೆಹೂದ್ಯರೇ, ಅನ್ಯಾಯವು ದುಷ್ಕಾರ್ಯವು ಇಂಥದೇನಾದರೂ ಇದ್ದ ಪಕ್ಷಕ್ಕೆ ನಾನು ನಿಮ್ಮ ಮಾತನ್ನು ಸಹನದಿಂದ ಕೇಳುವದು ನ್ಯಾಯವೇ.",
			"15": "<sup>15</sup> ಆದರೆ ನೀವು ಮಾಡುವ ವಿವಾದವು ಬೋಧನೆಯನ್ನೂ ಹೆಸರುಗಳನ್ನೂ ನಿಮ್ಮ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನೂ ಕುರಿತದ್ದಾಗಿರುವದರಿಂದ ಅವುಗಳನ್ನು ನೀವೇ ನೋಡಿಕೊಳ್ಳಿರಿ. ಇಂಥ ವಿಷಯಗಳನ್ನು ವಿಚಾರಣೆಮಾಡುವದಕ್ಕೆ ನನಗಂತೂ ಮನಸ್ಸಿಲ್ಲ ಎಂದು ಹೇಳಿ",
			"16": "<sup>16</sup> ಅವರನ್ನು ನ್ಯಾಯಸ್ಥಾನದ ಎದುರಿನಿಂದ ಹೊರಡಿಸಿಬಿಟ್ಟನು.",
			"17": "<sup>17</sup> ಆಗ ಜನರೆಲ್ಲರು ಸಭಾಮಂದಿರದ ಅಧ್ಯಕ್ಷನಾದ ಸೋಸ್ಥೆನನನ್ನು ಹಿಡಿದುಕೊಂಡು ನ್ಯಾಯಸ್ಥಾನದ ಮುಂದೆಯೇ ಹೊಡೆದರು. ಗಲ್ಲಿಯೋನನು ಅವುಗಳಲ್ಲಿ ಒಂದನ್ನೂ ಲಕ್ಷ್ಯಕ್ಕೆ ತರಲಿಲ್ಲ.",
			"18": "<sup>18</sup> ಪೌಲನು ಇನ್ನು ಅನೇಕ ದಿವಸ ಅಲ್ಲಿದ್ದ ಮೇಲೆ ಸಹೋದರರಿಂದ ಅಪ್ಪಣೆ ತೆಗೆದುಕೊಂಡು ತನಗೆ ದೀಕ್ಷೆ ಇದ್ದದರಿಂದ ಕೆಂಖ್ರೆಯದಲ್ಲಿ ಕ್ಷೌರಮಾಡಿಸಿಕೊಂಡು ಹಡಗನ್ನು ಹತ್ತಿ ಸಿರಿಯಕ್ಕೆ ಹೊರಟನು. ಪ್ರಿಸ್ಕಿಲ್ಲಳೂ ಅಕ್ವಿಲನೂ ಅವನ ಸಂಗಡ ಹೊರಟರು.",
			"19": "<sup>19</sup> ಎಫೆಸಕ್ಕೆ ಬಂದಾಗ ಅವರನ್ನು ಅಲ್ಲೇ ಬಿಟ್ಟನು. ತಾನು ಸಭಾಮಂದಿರದೊಳಕ್ಕೆ ಹೋಗಿ ಯೆಹೂದ್ಯರ ಸಂಗಡ ವಾದಿಸಿದನು.",
			"20": "<sup>20</sup> ಅವರು ಅವನನ್ನು ಇನ್ನೂ ಕೆಲವು ಕಾಲ ಇರಬೇಕೆಂದು ಕೇಳಿಕೊಂಡಾಗ ಅವನು ಒಪ್ಪದೆ -",
			"21": "<sup>21</sup> ದೇವರ ಚಿತ್ತವಾದರೆ ತಿರಿಗಿ ನಿಮ್ಮ ಬಳಿಗೆ ಬರುತ್ತೇನೆಂದು ಹೇಳಿ ಅವರ ಅಪ್ಪಣೆ ತೆಗೆದುಕೊಂಡು ಹೋದನು. ಆಮೇಲೆ ಎಫೆಸದಿಂದ ಸಮುದ್ರಪ್ರಯಾಣ ಮಾಡಿ",
			"22": "<sup>22</sup> ಕೈಸರೈಯದಲ್ಲಿ ಇಳಿದು ಯೆರೂಸಲೇಮಿಗೆ ಹೋಗಿ ಸಭೆಯನ್ನು ವಂದಿಸಿ ಅಂತಿಯೋಕ್ಯಕ್ಕೆ ಬಂದನು.",
			"23": "<sup>23</sup> ಪೌಲನು ಅಂತಿಯೋಕ್ಯದಲ್ಲಿ ಕೆಲವು ಕಾಲ ಇದ್ದು ತಿರಿಗಿ ಹೊರಟು ಕ್ರಮವಾಗಿ ಗಲಾತ್ಯ ಸೀಮೆಯಲ್ಲಿಯೂ ಫ್ರೂಗ್ಯದಲ್ಲಿಯೂ ಸಂಚಾರಮಾಡುತ್ತಾ ಶಿಷ್ಯರೆಲ್ಲರನ್ನು ದೃಢಪಡಿಸಿದನು.",
			"24": "<sup>24</sup> ಅಷ್ಟರೊಳಗೆ ಅಲೆಕ್ಸಾಂದ್ರಿಯದಲ್ಲಿ ಹುಟ್ಟಿದ ಅಪೊಲ್ಲೋಸನೆಂಬ ಒಬ್ಬ ಯೆಹೂದ್ಯನು ಎಫೆಸಕ್ಕೆ ಬಂದನು. ಅವನು ವಾಕ್ಚಾತುರ್ಯವುಳ್ಳವನು, ಶಾಸ್ತ್ರಗಳಲ್ಲಿ ಪ್ರವೀಣನು.",
			"25": "<sup>25</sup> ಅವನು ಕರ್ತನ ಮಾರ್ಗದ ವಿಷಯದಲ್ಲಿ ಉಪದೇಶ ಹೊಂದಿದವನಾಗಿದ್ದನು; ಮತ್ತು ಆಸಕ್ತ ಮನಸ್ಸುಳ್ಳವನಾಗಿದ್ದು ಯೋಹಾನನು ಮಾಡಿಸಿದ ದೀಕ್ಷಾಸ್ನಾನವನ್ನು ಮಾತ್ರ ಬಲ್ಲವನಾದಾಗ್ಯೂ ಯೇಸುವಿನ ಸಂಗತಿಗಳನ್ನು ಸೂಕ್ಷ್ಮವಾಗಿ ಹೇಳಿ ಉಪದೇಶಿಸಿದನು.",
			"26": "<sup>26</sup> ಅವನು ಸಭಾಮಂದಿರದಲ್ಲಿ ಧೈರ್ಯದಿಂದ ಮಾತಾಡುವದಕ್ಕೆ ಪ್ರಾರಂಭಿಸಿದನು. ಅವನ ಮಾತುಗಳನ್ನು ಪ್ರಿಸ್ಕಿಲ್ಲಳೂ ಅಕ್ವಿಲನೂ ಕೇಳಿ ಅವನನ್ನು ತಮ್ಮ ಮನೆಗೆ ಕರೆದುಕೊಂಡು ಹೋಗಿ ದೇವರ ಮಾರ್ಗವನ್ನು ಅವನಿಗೆ ಇನ್ನೂ ಸೂಕ್ಷ್ಮವಾಗಿ ವಿವರಿಸಿದರು.",
			"27": "<sup>27</sup> ಆಮೇಲೆ ಅಖಾಯಕ್ಕೆ ಹೋಗಬೇಕೆಂದು ಅವನಿಗೆ ಮನಸ್ಸಾದಾಗ ಸಹೋದರರು ಅವನನ್ನು ಪ್ರೋತ್ಸಾಹಗೊಳಿಸಿ ಅಲ್ಲಿದ್ದ ಶಿಷ್ಯರಿಗೆ ಇವನನ್ನು ಸೇರಿಸಿಕೊಳ್ಳಬೇಕೆಂದು ಪತ್ರಿಕೆಯನ್ನು ಬರೆದುಕೊಟ್ಟರು.",
			"28": "<sup>28</sup> ಅವನು ಹೋಗಿ ಯೇಸುವೇ ಕ್ರಿಸ್ತನೆಂದು ಶಾಸ್ತ್ರಾಧಾರದಿಂದ ತೋರಿಸಿಕೊಟ್ಟು ಎಲ್ಲರ ಮುಂದೆ ಬಲವಾಗಿ ಯೆಹೂದ್ಯರನ್ನು ಖಂಡಿಸಿ ದೇವರ ಕೃಪೆಯಿಂದ ನಂಬಿದ್ದವರಿಗೆ ಬಹಳ ಸಹಾಯಮಾಡಿದನು."
		},
		"19": {
			"1": "<sup>1</sup> ಅಪೊಲ್ಲೋಸನು ಕೊರಿಂಥದಲ್ಲಿದ್ದಾಗ ಪೌಲನು ಮಲೆನಾಡಿನಲ್ಲಿ ಸಂಚಾರಮಾಡಿ ಎಫೆಸಕ್ಕೆ ಬಂದು ಕೆಲವು ಮಂದಿ ಶಿಷ್ಯರನ್ನು ಕಂಡು -",
			"2": "<sup>2</sup> ನೀವು ನಂಬಿದಾಗ ಪವಿತ್ರಾತ್ಮವರವನ್ನು ಹೊಂದಿದಿರೋ ಎಂದು ಅವರನ್ನು ಕೇಳಲು ಅವರು ಅವನಿಗೆ - ಇಲ್ಲ, ಪವಿತ್ರಾತ್ಮವರವು ಉಂಟೆಂಬದನ್ನೇ ನಾವು ಕೇಳಲಿಲ್ಲ ಅಂದರು.",
			"3": "<sup>3</sup> ಅವನು - ನೀವು ಯಾವದನ್ನು ನಂಬಿ ದೀಕ್ಷಾಸ್ನಾನ ಮಾಡಿಸಿಕೊಂಡಿರಿ ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ ಅವರು - ಯೋಹಾನನ ಬೋಧನೆಯನ್ನು ನಂಬಿ ದೀಕ್ಷಾಸ್ನಾನ ಮಾಡಿಸಿಕೊಂಡೆವು ಅಂದರು.",
			"4": "<sup>4</sup> ಅದಕ್ಕೆ ಪೌಲನು - ಯೋಹಾನನು ದೇವರ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡವರಿಗೆ ದೀಕ್ಷಾಸ್ನಾನ ಮಾಡಿಸಿ ತನ್ನ ಹಿಂದೆ ಬರವಾತನಲ್ಲಿ ಅಂದರೆ ಯೇಸುವಿನಲ್ಲಿ ನಂಬಿಕೆಯಿಡಬೇಕೆಂಬದಾಗಿ ಜನರಿಗೆ ಹೇಳಿದನು ಎಂದು ಹೇಳಲು",
			"5": "<sup>5</sup> ಅವರು ಆ ಮಾತನ್ನು ಕೇಳಿ ಕರ್ತನಾದ ಯೇಸುವಿನ ಹೆಸರಿನಲ್ಲಿ ದೀಕ್ಷಾಸ್ನಾನ ಮಾಡಿಸಿಕೊಂಡರು.",
			"6": "<sup>6</sup> ಪೌಲನು ಅವರ ಮೇಲೆ ಕೈಗಳನ್ನಿಡಲು ಪವಿತ್ರಾತ್ಮವರವು ಅವರ ಮೇಲೆ ಬಂತು; ಅವರು ನಾನಾ ಭಾಷೆಗಳನ್ನಾಡಿದರು, ಪ್ರವಾದಿಸಿದರು.",
			"7": "<sup>7</sup> ಅವರು ಸುಮಾರು ಹನ್ನೆರಡು ಮಂದಿ ಗಂಡಸರು.",
			"8": "<sup>8</sup> ಪಟ್ಟಣದಲ್ಲಿ ಪೌಲನು ಸಭಾಮಂದಿರದೊಳಗೆ ಹೋಗಿ ಮೂರು ತಿಂಗಳು ಅಲ್ಲೇ ದೇವರ ರಾಜ್ಯದ ವಿಷಯಗಳನ್ನು ಕುರಿತು ವಾದಿಸುತ್ತಾ ಜನರನ್ನು ಒಡಂಬಡಿಸುತ್ತಾ ಧೈರ್ಯದಿಂದ ಮಾತಾಡಿದನು.",
			"9": "<sup>9</sup> ಕೆಲವರು ತಮ್ಮ ಮನಸ್ಸುಗಳನ್ನು ಕಠಿಣಮಾಡಿಕೊಂಡು ಒಡಂಬಡದೆ ಗುಂಪುಕೂಡಿದ ಜನರ ಮುಂದೆ ಈ ಮಾರ್ಗವು ಕೆಟ್ಟದ್ದೆಂದು ಹೇಳಲು ಅವನು ಅವರನ್ನು ಬಿಟ್ಟುಹೋಗಿ ಶಿಷ್ಯರನ್ನು ಬೇರೆಮಾಡಿ ತುರನ್ನನ ತರ್ಕಶಾಲೆಯಲ್ಲಿ ಪ್ರತಿದಿನವೂ ವಾದಿಸಿದನು.",
			"10": "<sup>10</sup> ಇದು ಎರಡು ವರುಷಗಳವರೆಗೂ ನಡೆದದ್ದರಿಂದ ಆಸ್ಯಸೀಮೆಯಲ್ಲಿ ವಾಸವಾಗಿದ್ದ ಯೆಹೂದ್ಯರೂ ಗ್ರೀಕರೂ ಎಲ್ಲರೂ ಕರ್ತನ ವಾಕ್ಯವನ್ನು ಕೇಳಿದರು.",
			"11": "<sup>11</sup> ದೇವರು ಪೌಲನ ಕೈಯಿಂದ ವಿಶೇಷವಾದ ಮಹತ್ಕಾರ್ಯಗಳನ್ನು ನಡಿಸುತ್ತಾ ಇದ್ದದರಿಂದ",
			"12": "<sup>12</sup> ಜನರು ಅವನ ಮೈಮೇಲಿನಿಂದ ಕೈವಸ್ತ್ರಗಳನ್ನೂ ಉಡಿವಸ್ತ್ರಗಳನ್ನೂ ತಂದು ರೋಗಿಗಳ ಮೇಲೆ ಹಾಕಲು ಅವರವರ ರೋಗಗಳು ವಾಸಿಯಾದವು, ದೆವ್ವಗಳೂ ಬಿಟ್ಟುಹೋದವು.",
			"13": "<sup>13</sup> ದೆವ್ವಬಿಡಿಸುವವರೆನಿಸಿಕೊಂಡು ದೇಶಸಂಚಾರಿಗಳಾದ ಯೆಹೂದ್ಯರಿದ್ದರು. ಅವರಲ್ಲಿ ಕೆಲವರು ದೆವ್ವಹಿಡಿದವರ ಮೇಲೆ - ಪೌಲನು ಸಾರುವ ಯೇಸುವಿನ ಆಣೆ ಎಂದು ಹೇಳಿ ಕರ್ತನಾದ ಯೇಸುವಿನ ಹೆಸರನ್ನು ಪ್ರಯೋಗಮಾಡುವದಕ್ಕೆ ತೊಡಗಿದರು.",
			"14": "<sup>14</sup> ಮಹಾಯಜಕ ಸ್ಕೇವನೆಂಬ ಒಬ್ಬ ಯೆಹೂದ್ಯನ ಏಳು ಮಂದಿ ಮಕ್ಕಳು ಹಾಗೆ ಮಾಡುತ್ತಿದ್ದರು.",
			"15": "<sup>15</sup> ಆದರೆ ದೆವ್ವವು ಅವರಲ್ಲಿ ಇಬ್ಬರಿಗೆ - ಯೇಸುವಿನ ಗುರುತು ನನಗುಂಟು, ಪೌಲನನ್ನೂ ಬಲ್ಲೆನು, ನೀವಾದರೆ ಯಾರು? ಎಂದು ಹೇಳಿತು;",
			"16": "<sup>16</sup> ಮತ್ತು ದೆವ್ವಹಿಡಿದಿದ್ದ ಆ ಮನುಷ್ಯನು ಅವರ ಮೇಲೆ ಹಾರಿ ಬಿದ್ದು ಅವರಿಬ್ಬರನ್ನೂ ಸೋಲಿಸಿ ಬಲಾತ್ಕರಿಸಿದ್ದರಿಂದ ಅವರು ಬೆತ್ತಲೆಯಾಗಿಯೂ ಗಾಯವುಳ್ಳವರಾಗಿಯೂ ಆ ಮನೆಯೊಳಗಿಂದ ಓಡಿಹೋದರು.",
			"17": "<sup>17</sup> ಈ ಸಂಗತಿಯು ಎಫೆಸದಲ್ಲಿ ವಾಸವಾಗಿದ್ದ ಎಲ್ಲಾ ಯೆಹೂದ್ಯರಿಗೂ ಗ್ರೀಕರಿಗೂ ತಿಳಿದುಬಂದಾಗ ಅವರೆಲ್ಲರಿಗೆ ಭಯಹಿಡಿಯಿತು.",
			"18": "<sup>18</sup> ಮತ್ತು ಕರ್ತನಾದ ಯೇಸುವಿನ ಹೆಸರು ಪ್ರಖ್ಯಾತಿಗೆ ಬಂತು. ಆತನನ್ನು ನಂಬಿದವರಲ್ಲಿ ಅನೇಕರು ಬಂದು ತಮ್ಮ ಕೃತ್ಯಗಳನ್ನು ಒಪ್ಪಿಕೊಂಡು ತಿಳಿಸಿದರು.",
			"19": "<sup>19</sup> ಇದಲ್ಲದೆ ಮಾಟ ಮಂತ್ರಗಳನ್ನು ನಡಿಸಿದವರಲ್ಲಿ ಅನೇಕರು ತಮ್ಮ ಪುಸ್ತಕಗಳನ್ನು ತಂದು ಕೂಡಿಸಿ ಎಲ್ಲರ ಮುಂದೆ ಸುಟ್ಟುಬಿಟ್ಟರು. ಅವುಗಳ ಕ್ರಯವನ್ನು ಲೆಕ್ಕಮಾಡಿ ಐವತ್ತು ಸಾವಿರ ಬೆಳ್ಳೀ ನಾಣ್ಯ ಆಯಿತೆಂದು ತಿಳಿದುಕೊಂಡರು.",
			"20": "<sup>20</sup> ಈ ರೀತಿಯಾಗಿ ಕರ್ತನ ವಾಕ್ಯವು ಬಹಳವಾಗಿ ಹೆಚ್ಚುತ್ತಾ ಪ್ರಬಲವಾಯಿತು.",
			"21": "<sup>21</sup> ಈ ಸಂಗತಿಗಳು ಆದ ಮೇಲೆ ಪೌಲನು ತಾನು ಮಕೆದೋನ್ಯದಲ್ಲಿಯೂ ಅಖಾಯದಲ್ಲಿಯೂ ಸಂಚಾರಮಾಡಿ ಯೆರೂಸಲೇಮಿಗೆ ಹೋಗಬೇಕೆಂದು ಮನಸ್ಸಿನಲ್ಲಿ ಉದ್ದೇಶಿಸಿ ಅಲ್ಲಿಗೆ ಹೋದ ಮೇಲೆ ರೋಮಾಪುರವನ್ನು ಸಹ ನೋಡಬೇಕೆಂದು ಹೇಳಿದನು.",
			"22": "<sup>22</sup> ಆದರೆ ಅವನು ತನಗೆ ಸೇವೆಮಾಡುವವರಲ್ಲಿ ಇಬ್ಬರಾದ ತಿಮೊಥೆಯನನ್ನೂ ಎರಸ್ತನನ್ನೂ ಮಕೆದೋನ್ಯಕ್ಕೆ ಕಳುಹಿಸಿ ತಾನು ಆಸ್ಯಸೀಮೆಯಲ್ಲಿ ಕೆಲವು ಕಾಲ ನಿಂತನು.",
			"23": "<sup>23</sup> ಆ ಕಾಲದಲ್ಲಿ ಕ್ರಿಸ್ತ ಮಾರ್ಗದ ವಿಷಯವಾಗಿ ಬಹಳ ಗಲಭೆ ಹುಟ್ಟಿತು.",
			"24": "<sup>24</sup> ಹೇಗಂದರೆ ದೇಮೇತ್ರಿಯನೆಂಬ ಒಬ್ಬ ಅಕ್ಕಸಾಲಿಗನು ಬೆಳ್ಳಿಯಿಂದ ಅರ್ತೆಮೀದೇವಿಯ ಗುಡಿಯಂತೆ ಸಣ್ಣ ಸಣ್ಣ ಗುಡಿಗಳನ್ನು ಮಾಡಿಸುವವನಾಗಿದ್ದು ಆ ಕಸಬಿನವರಿಗೆ ಬಹಳ ಕೆಲಸಕೊಡುತ್ತಿದ್ದನು.",
			"25": "<sup>25</sup> ಅವನು ಅವರನ್ನೂ ಆ ಕಸಬಿನ ಸಂಬಂಧವಾದ ಕೆಲಸದವರನ್ನೂ ಒಟ್ಟಿಗೆ ಕರಿಸಿ ಅವರಿಗೆ - ಜನರೇ, ಈ ಕೆಲಸದಿಂದ ನಮಗೆ ಐಶ್ವರ್ಯ ಉಂಟಾಗುತ್ತದೆಂದು ನೀವು ಬಲ್ಲಿರಿ.",
			"26": "<sup>26</sup> ಆದರೆ ಕೈಯಿಂದ ಮಾಡಿದ ಮೂರ್ತಿಗಳು ದೇವರುಗಳಲ್ಲವೆಂದು ಆ ಪೌಲನು ಹೇಳಿ ಎಫೆಸದಲ್ಲಿ ಮಾತ್ರವಲ್ಲದೆ ಸ್ವಲ್ಪ ಕಡಿಮೆ ಆಸ್ಯಸೀಮೆಯಲ್ಲೆಲ್ಲಾ ಬಹಳ ಜನರನ್ನು ಒಡಂಬಡಿಸಿ ತಿರುಗಿಸಿಬಿಟ್ಟಿದ್ದಾನೆ ಎಂಬದನ್ನು ನೀವು ನೋಡುತ್ತೀರಿ, ಕೇಳುತ್ತೀರಿ.",
			"27": "<sup>27</sup> ಇದರಿಂದ ನಮ್ಮ ಉದ್ಯೋಗಕ್ಕೆ ಅಪಕೀರ್ತಿ ಬರುವ ಹಾಗಿರುವದಲ್ಲದೆ ಅರ್ತೆಮೀ ಮಹಾದೇವಿಯ ದೇವಸ್ಥಾನವು ಗಣನೆಗೆ ಬಾರದೆಹೋಗುವ ಹಾಗೆಯೂ ಎಲ್ಲಾ ಆಸ್ಯಸೀಮೆಯೂ ಲೋಕವೂ ಪೂಜಿಸುವ ಈ ದೇವಿಯ ವೈಭವಕ್ಕೆ ಹಾನಿಬರುವ ಹಾಗೆಯೂ ಗಂಡಾಂತರವುಂಟೆಂದು ಹೇಳಿದನು.",
			"28": "<sup>28</sup> ಅವರು ಈ ಮಾತುಗಳನ್ನು ಕೇಳಿ ಕೋಪಾಕ್ರಾಂತರಾಗಿ - ಎಫೆಸದವರ ಅರ್ತೆಮೀದೇವಿ ಮಹಾದೇವಿ ಎಂದು ಆರ್ಭಟಿಸಿದರು.",
			"29": "<sup>29</sup> ಆಗ ಊರೆಲ್ಲಾ ಗಲಿಬಿಲಿಯಾಯಿತು. ಪ್ರಯಾಣದಲ್ಲಿ ಪೌಲನಿಗೆ ಜೊತೆಯವರಾಗಿದ್ದ ಮಕೆದೋನ್ಯದ ಗಾಯನನ್ನೂ ಅರಿಸ್ತಾರ್ಕನನ್ನೂ ಜನರು ಹಿಡಿದುಕೊಂಡು ಒಗ್ಗಟ್ಟಾಗಿ ನಾಟಕಶಾಲೆಯೊಳಗೆ ನುಗ್ಗಿದರು.",
			"30": "<sup>30</sup> ಪೌಲನು ಜನರೊಳಗೆ ಹೋಗಬೇಕೆಂದಿದ್ದಾಗ ಶಿಷ್ಯರು ಅವನನ್ನು ಬಿಡಲಿಲ್ಲ.",
			"31": "<sup>31</sup> ಇದಲ್ಲದೆ ಆಸ್ಯಸೀಮೆಯ ಅಧಿಕಾರಿಗಳಲ್ಲಿ ಕೆಲವರು ಅವನಿಗೆ ಸ್ನೇಹಿತರಾಗಿದ್ದದರಿಂದ ಅವನ ಬಳಿಗೆ ಜನರನ್ನು ಕಳುಹಿಸಿ - ನೀನು ನಾಟಕಶಾಲೆಯೊಳಗೆ ಹೋಗಿ ನಿನ್ನನ್ನು ಅಪಾಯಕ್ಕೆ ಗುರಿಮಾಡಿಕೊಳ್ಳಬೇಡ ಎಂದು ಬೇಡಿಕೊಂಡರು.",
			"32": "<sup>32</sup> ಸಭೆಯು ಗಲಿಬಿಲಿಯಾಯಿತು, ಮತ್ತು ಹೆಚ್ಚಾದ ಜನರಿಗೆ ತಾವು ಕೂಡಿ ಬಂದಿದ್ದ ಕಾರಣವು ಗೊತ್ತಿರಲಿಲ್ಲ. ಕೆಲವರು ಒಂದು ಬಗೆಯಾಗಿ ಮತ್ತೆ ಕೆಲವರು ಇನ್ನೊಂದು ಬಗೆಯಾಗಿ ಕೂಗುತ್ತಿದ್ದರು.",
			"33": "<sup>33</sup> ಯೆಹೂದ್ಯರು ಅಲೆಕ್ಸಾಂದ್ರನನ್ನು ಮುಂದಕ್ಕೆ ನೂಕಲು ಗುಂಪಿನಲ್ಲಿ ಕೆಲವರು ಅವನಿಗೆ ಸೂಚನೆ ಕೊಟ್ಟರು. ಆಗ ಅಲೆಕ್ಸಾಂದ್ರನು ಕೈಸನ್ನೆ ಮಾಡಿ ಕೂಡಿದ್ದ ಜನರಿಗೆ ಪ್ರತಿವಾದ ಮಾಡಬೇಕೆಂದಿದ್ದನು.",
			"34": "<sup>34</sup> ಆದರೆ ಅವನು ಯೆಹೂದ್ಯನೆಂದು ತಿಳಿದಾಗ ಎಲ್ಲರೂ ಒಂದೇ ಶಬ್ದದಿಂದ - ಎಫೆಸದವರ ಅರ್ತೆಮೀದೇವಿ ಮಹಾದೇವಿ ಎಂದು ಎರಡು ಗಂಟೆ ಹೊತ್ತು ಕೂಗಿದರು.",
			"35": "<sup>35</sup> ಕಡೆಗೆ ಪಟ್ಟಣದ ಯಜಮಾನನು ಜನರ ಗುಂಪನ್ನು ಸಮಾಧಾನಪಡಿಸಿ - ಎಫೆಸದವರೇ, ಎಫೆಸ ಪಟ್ಟಣದವರು ಅರ್ತೆಮೀ ಮಹಾದೇವಿಯ ದೇವಸ್ಥಾನಕ್ಕೂ ಆಕಾಶದಿಂದ ಬಿದ್ದ ಆಕೆಯ ಮೂರ್ತಿಗೂ ಪಾಲಕರಾಗಿದ್ದಾರೆಂಬದನ್ನು ತಿಳಿಯದಿರುವ ಮನುಷ್ಯನು ಇದ್ದಾನೇ?",
			"36": "<sup>36</sup> ಈ ಸಂಗತಿ ಅಲ್ಲವೆನ್ನುವದಕ್ಕೆ ಯಾರಿಂದಲೂ ಆಗದಿರುವದರಿಂದ ನೀವು ಶಾಂತಿಯಿಂದಿರಬೇಕು; ದುಡುಕಿನಿಂದ ಏನೂ ಮಾಡಬಾರದು.",
			"37": "<sup>37</sup> ನೀವು ತಂದಿರುವ ಈ ಮನುಷ್ಯರು ದೇವಸ್ಥಾನದಲ್ಲಿ ಕಳ್ಳತನ ಮಾಡುವವರಲ್ಲ, ನಮ್ಮ ದೇವಿಯನ್ನು ದೂಷಿಸುವವರೂ ಅಲ್ಲ.",
			"38": "<sup>38</sup> ಹೀಗಿರುವದರಿಂದ ದೇಮೇತ್ರಿಯನಿಗೂ ಅವನ ಪಕ್ಷವನ್ನು ಹಿಡಿದಿರುವ ಕಸಬಿನವರಿಗೂ ಯಾವನ ಮೇಲಾದರೂ ಒಂದು ವ್ಯವಹಾರವಿದ್ದರೆ ನ್ಯಾಯಸ್ಥಾನಗಳುಂಟು, ಅಧಿಪತಿಗಳಿದ್ದಾರೆ, ಒಬ್ಬರ ಮೇಲೊಬ್ಬರು ವ್ಯಾಜ್ಯವಾಡಿಕೊಳ್ಳಲಿ.",
			"39": "<sup>39</sup> ಏನಾದರೂ ಹೆಚ್ಚು ವಿಚಾರಣೆ ಬೇಕಾದರೆ ಅದು ನ್ಯಾಯವಾಗಿ ನೆರೆದ ಸಭೆಯಲ್ಲಿ ತೀರ್ಮಾನಿಸಲ್ಪಡಬೇಕು.",
			"40": "<sup>40</sup> ಈ ದಿನದಲ್ಲಿ ನ್ಯಾಯವಾದ ಕಾರಣವಿಲ್ಲದೆ ನೆರೆದ ಕೂಟದ ವಿಷಯದಲ್ಲಿ ಇದು ಒಂದು ದಂಗೆ ಎಂಬದಾಗಿ ನಮ್ಮ ಮೇಲೆ ತಪ್ಪುಹೊರಿಸುವದಕ್ಕೆ ಆಸ್ಪದವಾಯಿತು. ಈ ದೊಂಬಿಯನ್ನು ಕುರಿತು ವಿಚಾರಣೆನಡೆದರೆ ನಮ್ಮಿಂದ ಉತ್ತರಕೊಡುವದಕ್ಕೆ ಆಗುವದೇ ಇಲ್ಲ ಎಂದು ಹೇಳಿದನು.",
			"41": "<sup>41</sup> ಈ ಮಾತುಗಳನ್ನು ಹೇಳಿ ಸಭೆಯನ್ನು ಮುರಿದನು."
		},
		"20": {
			"1": "<sup>1</sup> ಗದ್ದಲವು ನಿಂತ ತರುವಾಯ ಪೌಲನು ಶಿಷ್ಯರನ್ನು ಕೂಡ ಕರೆಸಿ ಧೈರ್ಯಗೊಳಿಸಿ ಅವರ ಅಪ್ಪಣೆ ತೆಗೆದುಕೊಂಡು ಮಕೆದೋನ್ಯಕ್ಕೆ ಹೊರಟನು.",
			"2": "<sup>2</sup> ಆ ಸೀಮೆಯಲ್ಲಿ ಸಂಚಾರಮಾಡಿ ಅಲ್ಲಿಯವರನ್ನು ಅನೇಕ ಮಾತುಗಳಿಂದ ಧೈರ್ಯಪಡಿಸಿ ಗ್ರೀಸ್ ದೇಶಕ್ಕೆ ಬಂದನು.",
			"3": "<sup>3</sup> ಅಲ್ಲಿ ಮೂರು ತಿಂಗಳು ಕಳೆದ ಮೇಲೆ ಸಿರಿಯ ದೇಶಕ್ಕೆ ಸಮುದ್ರಮಾರ್ಗವಾಗಿ ಹೋಗಬೇಕೆಂದಿದ್ದಾಗ ಅವನಿಗೆ ವಿರುದ್ಧವಾಗಿ ಯೆಹೂದ್ಯರಲ್ಲಿ ಒಳಸಂಚು ಹುಟ್ಟಿದ್ದರಿಂದ ಅವನು ಮಕೆದೋನ್ಯದ ಮೇಲೆ ಹಿಂತಿರುಗಿ ಹೋಗುವದಕ್ಕೆ ತೀರ್ಮಾನಮಾಡಿದನು.",
			"4": "<sup>4</sup> ಪುರ್ರನ ಮಗನಾದ ಬೆರೋಯಪಟ್ಟಣದ ಸೋಪತ್ರನೂ ಥೆಸಲೋನಿಕದವರಲ್ಲಿ ಅರಿಸ್ತಾರ್ಕನೂ ಸೆಕುಂದನೂ ದೆರ್ಬೆಪಟ್ಟಣದ ಗಾಯನೂ ತಿಮೊಥೆಯನೂ ಆಸ್ಯಸೀಮೆಯವರಲ್ಲಿ ತುಖಿಕನೂ ತ್ರೊಫಿಮನೂ ಆಸ್ಯಸೀಮೆಯವರೆಗೆ ಅವನ ಜೊತೆಯಲ್ಲಿ ಹೋದರು.",
			"5": "<sup>5</sup> ಇವರು ಮುಂಚಿತವಾಗಿ ಹೋಗಿ ನಮಗೋಸ್ಕರ ತ್ರೋವದಲ್ಲಿ ಕಾದುಕೊಂಡರು.",
			"6": "<sup>6</sup> ಹುಳಿಯಿಲ್ಲದ ರೊಟ್ಟಿಯ ಹಬ್ಬದ ದಿವಸಗಳನ್ನು ಪೂರೈಸಿದ ಮೇಲೆ ನಾವು ಫಿಲಿಪ್ಪಿಯಿಂದ ಹೊರಟು ಹಡಗನ್ನು ಹತ್ತಿ ಐದು ದಿವಸದಲ್ಲಿ ತ್ರೋವದಲ್ಲಿ ಬಂದು ಅವರನ್ನು ಸೇರಿದೆವು. ಅಲ್ಲಿ ಏಳು ದಿವಸಗಳಿದ್ದೆವು.",
			"7": "<sup>7</sup> ವಾರದ ಮೊದಲನೆಯ ದಿವಸದಲ್ಲಿ ನಾವು ರೊಟ್ಟಿ ಮುರಿಯುವ ನಿಯಮಕ್ಕಾಗಿ ಕೂಡಿಬಂದಾಗ ಮರುದಿನ ಹೊರಡಬೇಕೆಂದಿದ್ದ ಪೌಲನು ಅವರಿಗೆ ಪ್ರಸಂಗಿಸುತ್ತಾ ಸರಿಹೊತ್ತಿನವರೆಗೂ ಉಪನ್ಯಾಸವನ್ನು ನಡಿಸಿದನು.",
			"8": "<sup>8</sup> ನಾವು ಕೂಡಿದ್ದ ಮೇಲಂತಸ್ತಿನಲ್ಲಿ ಅನೇಕ ದೀಪಗಳಿದ್ದವು.",
			"9": "<sup>9</sup> ಯೂತಿಖನೆಂಬ ಒಬ್ಬ ಯೌನವಸ್ಥನು ಕಿಟಿಕಿಯಲ್ಲಿ ಕೂತುಕೊಂಡು ಗಾಢನಿದ್ರೆಯಿಂದ ತೂಕಡಿಸಿದನು. ಪೌಲನು ಇನ್ನೂ ಪ್ರಸಂಗ ಮಾಡುತ್ತಾ ಇದ್ದಾಗ ಆ ಯೌವನಸ್ಥನು ನಿದ್ರೆಯಿಂದ ತೂಕಡಿಕೆ ಹೆಚ್ಚಾಗಿ ಮೂರನೆಯ ಅಂತಸ್ತಿನಿಂದ ಕೆಳಕ್ಕೆ ಬಿದ್ದನು; ಅವನನ್ನು ಎತ್ತಿನೋಡುವಾಗ ಸತ್ತಿದ್ದನು.",
			"10": "<sup>10</sup> ಆದರೆ ಪೌಲನು ಇಳಿದುಹೋಗಿ ಅವನ ಮೇಲೆ ಬಿದ್ದು ತಬ್ಬಿಕೊಂಡು - ಗೋಳಾಡಬೇಡಿರಿ, ಅವನ ಪ್ರಾಣ ಅವನಲ್ಲಿ ಅದೆ ಎಂದು ಹೇಳಿದನು.",
			"11": "<sup>11</sup> ಆಗ ಅವನು ಮೇಲಕ್ಕೆ ಹೋಗಿ ರೊಟ್ಟಿ ಮುರಿದು ಊಟಮಾಡಿ ಬಹಳ ಹೊತ್ತು ಬೆಳಗಾಗುವ ತನಕ ಸಲ್ಲಾಪಮಾಡಿ ಹಾಗೆ ಹೊರಟುಹೋದನು.",
			"12": "<sup>12</sup> ಅವರು ಜೀವಿತನಾದ ಆ ಹುಡುಗನನ್ನು ಕರೆದುಕೊಂಡು ಬರಲು ಅವರಿಗೆ ಬಹಳ ಆದರಣೆ ಉಂಟಾಯಿತು.",
			"13": "<sup>13</sup> ನಾವು ಮೊದಲು ಹೋಗಿ ಅಸ್ಸೊಸಿನಲ್ಲಿ ಪೌಲನನ್ನು ಹತ್ತಿಸಿಕೊಳ್ಳಬೇಕೆಂದು ಅಲ್ಲಿಗೆ ಸಮುದ್ರಪ್ರಯಾಣ ಮಾಡಿದೆವು. ಪೌಲನು ತಾನು ಕಾಲುನಡೆಯಾಗಿ ಹೋಗಬೇಕೆಂದು ಹಾಗೆಯೇ ನಮಗೆ ಅಪ್ಪಣೆ ಮಾಡಿದ್ದನು.",
			"14": "<sup>14</sup> ಅಸ್ಸೊಸಿನಲ್ಲಿ ಅವನು ನಮ್ಮನ್ನು ಕೂಡಿಕೊಂಡಾಗ ನಾವು ಅವನನ್ನು ಹತ್ತಿಸಿಕೊಂಡು ಮಿತಿಲೇನೆಗೆ ಬಂದೆವು.",
			"15": "<sup>15</sup> ಅಲ್ಲಿಂದ ಹೊರಟು ಮರುದಿನ ಖೀಯೊಸ್‍ ದ್ವೀಪಕ್ಕೆದುರಾಗಿ ಬಂದು ಅದರ ಮರುದಿನ ಸಾಮೊಸಿನ ಹತ್ತರ ಬಂದೆವು. ಅದರ ಮರುದಿನ ಮಿಲೇತಕ್ಕೆ ಸೇರಿದೆವು.",
			"16": "<sup>16</sup> ಪೌಲನು ತನಗೆ ಸಾಧ್ಯವಾದರೆ ಪಂಚಾಶತ್ತಮ ದಿವಸದ ಹಬ್ಬಕ್ಕೆ ಯೆರೂಸಲೇಮಿನಲ್ಲಿರಬೇಕೆಂದು ಅವಸರಪಡುತ್ತಾ ಇದ್ದದರಿಂದ ಆಸ್ಯಸೀಮೆಯಲ್ಲಿ ಕಾಲವನ್ನು ಕಳೆಯುವದಕ್ಕೆ ಮನಸ್ಸಿಲ್ಲದೆ ಎಫೆಸಪಟ್ಟಣವನ್ನು ದಾಟಿಹೋಗಬೇಕೆಂದು ತೀರ್ಮಾನಿಸಿಕೊಂಡಿದ್ದನು.",
			"17": "<sup>17</sup> ಅವನು ಮಿಲೇತದಿಂದ ಎಫೆಸಕ್ಕೆ ಹೇಳಿಕಳುಹಿಸಿ ಅಲ್ಲಿಯ ಸಭೆಯ ಹಿರಿಯರನ್ನು ಕರಿಸಿದನು.",
			"18": "<sup>18</sup> ಅವರು ಅವನ ಬಳಿಗೆ ಬಂದಾಗ ಅವರಿಗೆ ಅವನು ಹೇಳಿದ್ದೇನಂದರೆ - ನಾನು ಆಸ್ಯಸೀಮೆಯಲ್ಲಿ ಕಾಲಿಟ್ಟ ಮೊದಲನೆಯ ದಿವಸದಿಂದ ನಿಮ್ಮ ಮಧ್ಯೆ ಎಲ್ಲಾ ಕಾಲದಲ್ಲಿಯೂ ಹೇಗೆ ನಡಕೊಂಡೆನೆಂಬದನ್ನು ನೀವೇ ಬಲ್ಲಿರಿ.",
			"19": "<sup>19</sup> ನಾನು ಬಹು ನಮ್ರತೆಯಿಂದಲೂ ಕಣ್ಣೀರಿನಿಂದಲೂ ಕರ್ತನ ಸೇವೆಮಾಡುತ್ತಿದ್ದೆನು. ಇದರಲ್ಲಿ ಯೆಹೂದ್ಯರ ಒಳ ಸಂಚುಗಳಿಂದ ಸಂಭವಿಸಿದ ಕಷ್ಟಗಳನ್ನು ಸಹಿಸುತ್ತಿದ್ದೆನು.",
			"20": "<sup>20</sup> ನಾನು ನಿಮಗೆ ಹಿತಕರವಾದದ್ದೆಲ್ಲವನ್ನು ಹೇಳುವದಕ್ಕೂ ಸಭೆಯಲ್ಲಿಯೂ ಮನೆಮನೆಯಲ್ಲಿಯೂ ಉಪದೇಶಿಸುವದಕ್ಕೂ ಹಿಂತೆಗಿಯದೆ",
			"21": "<sup>21</sup> ಯೆಹೂದ್ಯರಿಗೂ ಗ್ರೀಕರಿಗೂ ದೇವರ ಕಡೆಗೆ ತಿರುಗಬೇಕೆಂತಲೂ ನಮ್ಮ ಕರ್ತನಾದ ಯೇಸುವಿನ ಮೇಲೆ ನಂಬಿಕೆಯಿಡಬೇಕೆಂತಲೂ ಖಂಡಿತವಾಗಿ ಬೋಧಿಸುವವನಾಗಿದ್ದೆನು; ಇದೆಲ್ಲಾ ನಿಮಗೇ ತಿಳಿದಿದೆ.",
			"22": "<sup>22</sup> ಇಗೋ, ನಾನು ಈಗ ಆತ್ಮನಿರ್ಬಂಧವುಳ್ಳವನಾಗಿ ಯೆರೂಸಲೇಮಿಗೆ ಹೋಗುತ್ತೇನೆ. ಅಲ್ಲಿ ನನಗೆ ಏನು ಸಂಭವಿಸುವದೋ ನಾನರಿಯೆ.",
			"23": "<sup>23</sup> ನಿನಗೆ ಬೇಡಿಗಳೂ ಸಂಕಟಗಳೂ ಕಾದುಕೊಂಡಿವೆ ಎಂದು ಪವಿತ್ರಾತ್ಮನು ಎಲ್ಲಾ ಪಟ್ಟಣಗಳಲ್ಲಿಯೂ ನನಗೆ ಖಂಡಿತವಾಗಿ ಹೇಳುತ್ತಾನೆ; ಇಷ್ಟು ಮಾತ್ರ ಬಲ್ಲೆನು.",
			"24": "<sup>24</sup> ಆದರೆ ಪ್ರಾಣವನ್ನು ಉಳಿಸಿಕೊಳ್ಳುವದೇ ವಿಶೇಷವೆಂದು ನಾನು ಎಣಿಸುವದಿಲ್ಲ. ದೇವರ ಕೃಪೆಯ ವಿಷಯವಾದ ಸುವಾರ್ತೆಯನ್ನು ಆಸಕ್ತಿಯಿಂದ ಪ್ರಕಟಿಸಬೇಕೆಂಬದಾಗಿ ಕರ್ತನಾದ ಯೇಸುವಿನಿಂದ ನಾನು ಹೊಂದಿರುವ ಸೇವೆಯೆಂಬ ಓಟವನ್ನು ಕಡೆಗಾಣಿಸುವದೊಂದೇ ನನ್ನ ಅಪೇಕ್ಷೆ.",
			"25": "<sup>25</sup> ಇಗೋ, ಇಷ್ಟು ದಿವಸ ನಿಮ್ಮಲ್ಲಿ ಸಂಚಾರಮಾಡಿ ದೇವರ ರಾಜ್ಯವನ್ನು ಸಾರಿದವನಾದ ನನ್ನ ಮುಖವನ್ನು ಇನ್ನು ಮೇಲೆ ನಿಮ್ಮಲ್ಲಿ ಒಬ್ಬರೂ ಕಾಣುವದಿಲ್ಲವೆಂದು ಬಲ್ಲೆನು.",
			"26": "<sup>26</sup> ಆದಕಾರಣ ನಾನು ಈಹೊತ್ತು ಪ್ರಮಾಣವಾಗಿ ಹೇಳುತ್ತೇನೆ, ನಿಮ್ಮಲ್ಲಿ ಯಾರಾದರೂ ನಾಶವಾದರೆ ಅದು ನನ್ನ ದೋಷವಲ್ಲ.",
			"27": "<sup>27</sup> ಯಾಕಂದರೆ ಒಂದನ್ನೂ ಮರೆಮಾಡದೆ ದೇವರ ಸಂಕಲ್ಪವನ್ನೆಲ್ಲಾ ನಿಮಗೆ ತಿಳಿಸಿದೆನು.",
			"28": "<sup>28</sup> ದೇವರು ಸ್ವರಕ್ತದಿಂದ ಸಂಪಾದಿಸಿಕೊಂಡ ಸಭೆಯನ್ನು ಪರಿಪಾಲಿಸುವದಕ್ಕಾಗಿ ಪವಿತ್ರಾತ್ಮನು ನಿಮ್ಮನ್ನೇ ಆ ಹಿಂಡಿನಲ್ಲಿ ಅಧ್ಯಕ್ಷರಾಗಿ ಇಟ್ಟಿರುವದರಿಂದ ನಿಮ್ಮ ವಿಷಯದಲ್ಲಿಯೂ ಎಲ್ಲಾ ಹಿಂಡಿನ ವಿಷಯದಲ್ಲಿಯೂ ಎಚ್ಚರಿಕೆಯಾಗಿರಿ.",
			"29": "<sup>29</sup> ನಾನು ಹೋದ ಮೇಲೆ ಕ್ರೂರವಾದ ತೋಳಗಳು ನಿಮ್ಮೊಳಗೆ ಬರುವವೆಂದು ನಾನು ಬಲ್ಲೆನು; ಅವು ಹಿಂಡನ್ನು ಕನಿಕರಿಸುವದಿಲ್ಲ.",
			"30": "<sup>30</sup> ಇದಲ್ಲದೆ ನಿಮ್ಮಲ್ಲಿಯೂ ಕೆಲವರು ಎದ್ದು ವ್ಯತ್ಯಾಸ ಬೋಧನೆಗಳನ್ನು ಮಾಡಿ ಯೇಸುವಿನ ಶಿಷ್ಯರನ್ನು ತಮ್ಮ ಹಿಂದೆ ಎಳಕೊಳ್ಳುವರು.",
			"31": "<sup>31</sup> ಆದಕಾರಣ ನಾನು ಕಣ್ಣೀರುಸುರಿಸುತ್ತಾ ಮೂರು ವರುಷ ಹಗಲಿರುಳು ಎಡೆಬಿಡದೆ ಪ್ರತಿಯೊಬ್ಬನಿಗೆ ಬುದ್ಧಿಹೇಳಿದೆನೆಂದು ನೀವು ಜ್ಞಾಪಕಮಾಡಿಕೊಂಡು ಎಚ್ಚರವಾಗಿರಿ.",
			"32": "<sup>32</sup> ನಾನೀಗ ನಿಮ್ಮನ್ನು ಕರ್ತನಿಗೂ ಆತನ ಕೃಪಾವಾಕ್ಯಕ್ಕೂ ಒಪ್ಪಿಸಿಕೊಡುತ್ತೇನೆ. ಆತನು ನಿಮ್ಮಲ್ಲಿ ಭಕ್ತಿವೃದ್ಧಿಯನ್ನುಂಟುಮಾಡುವದಕ್ಕೂ ಪವಿತ್ರರಾದವರೆಲ್ಲರಲ್ಲಿ ನಿಮಗೆ ಹಕ್ಕನ್ನು ಅನುಗ್ರಹಿಸುವದಕ್ಕೂ ಶಕ್ತನಾಗಿದ್ದಾನೆ.",
			"33": "<sup>33</sup> ನಾನು ಯಾರ ಬೆಳ್ಳಿಬಂಗಾರವನ್ನಾಗಲಿ ಉಡಿಗೆತೊಡಿಗೆಯನ್ನಾಗಲಿ ಬಯಸಲಿಲ್ಲ.",
			"34": "<sup>34</sup> ಈ ಕೈಗಳೇ ಕೆಲಸಮಾಡಿ ನನ್ನ ಕೊರತೆಗಳನ್ನೂ ನನ್ನ ಜೊತೆಯಲ್ಲಿದ್ದವರ ಕೊರತೆಗಳನ್ನೂ ನೀಗಿದ್ದನ್ನು ನೀವೇ ಬಲ್ಲಿರಿ.",
			"35": "<sup>35</sup> ಎಲ್ಲಾ ವಿಷಯಗಳಲ್ಲಿ ನಾನು ನಿಮಗೆ ಮಾದರಿ ತೋರಿಸಿದ್ದೇನೆ. ನೀವೂ ಹಾಗೆಯೇ ದುಡಿದು ಬಲವಿಲ್ಲದವರಿಗೆ ಉಪಕಾರಮಾಡಬೇಕು; ಮತ್ತು - ತೆಗೆದುಕೊಳ್ಳುವದಕ್ಕಿಂತ ಕೊಡುವದೇ ಹೆಚ್ಚಿನ ಭಾಗ್ಯವೆಂಬದಾಗಿ ಕರ್ತನಾದ ಯೇಸು ತಾನೇ ಹೇಳಿದ ಮಾತುಗಳನ್ನು ನೆನಪಿನಲ್ಲಿಟ್ಟುಕೊಂಡಿರಬೇಕು ಅಂದನು.",
			"36": "<sup>36</sup> ಈ ಮಾತುಗಳನ್ನು ಅವನು ಹೇಳಿದ ಮೇಲೆ ಮೊಣಕಾಲೂರಿಕೊಂಡು ಅವರೆಲ್ಲರ ಸಂಗಡ ಪ್ರಾರ್ಥನೆ ಮಾಡಿದನು. ಆಗ ಅವರೆಲ್ಲರು ಬಹಳವಾಗಿ ಅತ್ತರು.",
			"37": "<sup>37</sup> ನೀವು ಇನ್ನು ಮೇಲೆ ನನ್ನ ಮುಖವನ್ನು ಕಾಣುವದಿಲ್ಲವೆಂದು ಪೌಲನು ಹೇಳಿದ ಮಾತಿಗೆ ಅವರು ವಿಶೇಷವಾಗಿ ವ್ಯಥೆಪಟ್ಟು ಅವನ ಕೊರಳನ್ನು ತಬ್ಬಿಕೊಂಡು ಅವನಿಗೆ ಮುದ್ದಿಟ್ಟರು. ಆಮೇಲೆ ಅವರು ಅವನನ್ನು ಹಡಗಿಗೆ ಸಾಗಕಳುಹಿಸಿದರು."
		},
		"21": {
			"1": "<sup>1</sup> ನಾವು ಪ್ರಯಾಸದಿಂದ ಅವರನ್ನು ಬಿಟ್ಟು ಹಡಗನ್ನು ಹತ್ತಿದ ಮೇಲೆ ನೆಟ್ಟಗೆ ಹೋಗಿ ಕೋಸ್ ದ್ವೀಪಕ್ಕೆ ಸೇರಿದೆವು. ಮರುದಿನ ರೋದಕ್ಕೆ ಹೋಗಿ ಅಲ್ಲಿಂದ ಪತರಕ್ಕೆ ಬಂದೆವು.",
			"2": "<sup>2</sup> ಅಲ್ಲಿ ಫೊಯಿನೀಕೆಗೆ ಹೋಗುವ ಹಡಗನ್ನು ಕಂಡು ಹತ್ತಿ ಪ್ರಯಾಣವನ್ನು ಸಾಗಿಸಿದೆವು.",
			"3": "<sup>3</sup> ಮುಂದೆ ಕುಪ್ರದ್ವೀಪವನ್ನು ಕಂಡು ಅದನ್ನು ಎಡಗಡೆಗೆ ಬಿಟ್ಟು ಸಿರಿಯದೇಶದ ಕಡೆಗೆ ಸಾಗಿ ತೂರ್‍ಪಟ್ಟಣಕ್ಕೆ ಬಂದು ಇಳಿದೆವು. ಯಾಕಂದರೆ ಅಲ್ಲಿ ಸರಕನ್ನು ಇಳಿಸಬೇಕಾಗಿತ್ತು.",
			"4": "<sup>4</sup> ಅಲ್ಲಿ ಶಿಷ್ಯರನ್ನು ಹುಡುಕಿ ಕಂಡು ಏಳು ದಿವಸ ನಿಂತೆವು. ಅವರು ಆತ್ಮನ ಪ್ರೇರಣೆಯಿಂದ ಪೌಲನಿಗೆ - ನೀನು ಯೆರೂಸಲೇಮಿಗೆ ಕಾಲಿಡಬೇಡವೆಂದು ಹೇಳಿದರು.",
			"5": "<sup>5</sup> ಆ ದಿವಸಗಳನ್ನು ತೀರಿಸಿಕೊಂಡು ನಾವು ಹೊರಡುವಾಗ ಅವರೆಲ್ಲರೂ ಹೆಂಡರು ಮಕ್ಕಳು ಸಹಿತವಾಗಿ ಬಂದು ನಮ್ಮನ್ನು ಊರ ಹೊರಕ್ಕೆ ಸಾಗಕಳುಹಿಸಿದರು. ನಾವು ಸಮುದ್ರತೀರದಲ್ಲಿ ಮೊಣಕಾಲೂರಿಕೊಂಡು ಪ್ರಾರ್ಥನೆಮಾಡಿ",
			"6": "<sup>6</sup> ಒಬ್ಬರಿಗೊಬ್ಬರು ವಂದನೆಮಾಡಿ ಹಡಗನ್ನು ಹತ್ತಿದೆವು; ಅವರು ಹಿಂತಿರಿಗಿ ತಮ್ಮತಮ್ಮ ಮನೆಗಳಿಗೆ ಹೋದರು.",
			"7": "<sup>7</sup> ನಾವು ತೂರ್‍ಪಟ್ಟಣದಿಂದ ಹೊರಟು ಪ್ತೊಲೆಮಾಯಕ್ಕೆ ಸೇರಿ ಸಮುದ್ರಪ್ರಯಾಣವನ್ನು ಮುಗಿಸಿದೆವು. ಅಲ್ಲಿದ್ದ ಸಹೋದರರನ್ನು ವಂದಿಸಿ ಅವರ ಬಳಿಯಲ್ಲಿ ಒಂದು ದಿವಸ ಇದ್ದು",
			"8": "<sup>8</sup> ಮರುದಿನ ಹೊರಟು ಕೈಸರೈಯಕ್ಕೆ ಬಂದು ಸೌವಾರ್ತಿಕನಾದ ಫಿಲಿಪ್ಪನ ಮನೆಗೆ ಹೋಗಿ ಅವನ ಬಳಿಯಲ್ಲಿಯೇ ಇಳುಕೊಂಡೆವು. ಅವನು ಆ ಏಳ್ವರಲ್ಲಿ ಒಬ್ಬನು.",
			"9": "<sup>9</sup> ಅವನಿಗೆ ಮದುವೆಯಾಗದ ನಾಲ್ಕು ಮಂದಿ ಹೆಣ್ಣುಮಕ್ಕಳಿದ್ದರು; ಅವರು ಪ್ರವಾದಿಸುವವರಾಗಿದ್ದರು.",
			"10": "<sup>10</sup> ನಾವು ಅಲ್ಲಿ ಅನೇಕ ದಿವಸಗಳು ಇದ್ದ ಮೇಲೆ, ಅಗಬನೆಂಬ ಒಬ್ಬ ಪ್ರವಾದಿಯು ಯೂದಾಯದಿಂದ ನಮ್ಮ ಬಳಿಗೆ ಬಂದು",
			"11": "<sup>11</sup> ಪೌಲನ ನಡುಕಟ್ಟನ್ನು ತೆಗೆದು ತನ್ನ ಕೈಕಾಲುಗಳನ್ನು ಕಟ್ಟಿಕೊಂಡು - ಈ ನಡುಕಟ್ಟು ಯಾವನದೋ ಆ ಮನುಷ್ಯನನ್ನು ಯೆಹೂದ್ಯರು ಇದೇ ರೀತಿಯಾಗಿ ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಕಟ್ಟಿ ಅನ್ಯಜನರ ಕೈಗೆ ಒಪ್ಪಿಸಿಕೊಡುವರು, ಹೀಗೆ ಪವಿತ್ರಾತ್ಮನು ಹೇಳುತ್ತಾನೆಂಬದಾಗಿ ಹೇಳಿದನು.",
			"12": "<sup>12</sup> ಆ ಮಾತನ್ನು ಕೇಳಿದಾಗ ನಾವೂ ಆ ಸ್ಥಳದವರೂ - ನೀನು ಯೆರೂಸಲೇಮಿಗೆ ಹೋಗಬಾರದೆಂದು ಪೌಲನನ್ನು ಬೇಡಿಕೊಂಡೆವು.",
			"13": "<sup>13</sup> ಅದಕ್ಕೆ ಪೌಲನು - ನೀವು ಅತ್ತತ್ತು ಯಾಕೆ ನನ್ನನ್ನು ಎದೆ ಒಡಿಸುತ್ತೀರಿ? ನಾನು ಕರ್ತನಾದ ಯೇಸುವಿನ ಹೆಸರಿನ ನಿಮಿತ್ತವಾಗಿ ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಬೇಡೀಹಾಕಿಸಿಕೊಳ್ಳುವದಕ್ಕೆ ಮಾತ್ರವಲ್ಲದೆ ಸಾಯುವದಕ್ಕೂ ಸಿದ್ಧವಾಗಿದ್ದೇನೆ ಅಂದನು.",
			"14": "<sup>14</sup> ಅವನು ಒಡಂಬಡದೆ ಇದ್ದದರಿಂದ ಕರ್ತನ ಚಿತ್ತವಿದ್ದಂತೆ ಆಗಲಿ ಎಂದು ಹೇಳಿ ನಾವು ಸುಮ್ಮಗಾದೆವು.",
			"15": "<sup>15</sup> ಆ ದಿವಸಗಳಾದ ಮೇಲೆ ಸೌರಿಸಿಕೊಂಡು ಯೆರೂಸಲೇಮಿಗೆ ಹೊರಟೆವು.",
			"16": "<sup>16</sup> ಕೈಸರೈಯದಿಂದ ಕೆಲವು ಶಿಷ್ಯರು ನಮ್ಮ ಜೊತೆಯಲ್ಲಿ ಬಂದು ನಾವು ಇಳುಕೊಳ್ಳಬೇಕಾಗಿದ್ದವನ ಮನೆಯ ತನಕ ಕರಕೊಂಡು ಹೋದರು. ಆ ಮನೆಯವನು ಆದಿ ಶಿಷ್ಯರಲ್ಲಿ ಒಬ್ಬನಾದ ಕುಪ್ರದೇಶದ ಮ್ನಾಸೋನನೆಂಬವನು.",
			"17": "<sup>17</sup> ನಾವು ಯೆರೂಸಲೇಮಿಗೆ ಬಂದಾಗ ಸಹೋದರರು ನಮ್ಮನ್ನು ಸಂತೋಷದಿಂದ ಸೇರಿಸಿಕೊಂಡರು.",
			"18": "<sup>18</sup> ಮರುದಿನ ಪೌಲನು ನಮ್ಮನ್ನು ಕರೆದುಕೊಂಡು ಯಾಕೋಬನ ಬಳಿಗೆ ಹೋದನು. ಸಭೇಹಿರಿಯರೆಲ್ಲರು ಸಹ ಬಂದರು.",
			"19": "<sup>19</sup> ಪೌಲನು ಅವರನ್ನು ವಂದಿಸಿ ತನ್ನ ಸೇವೆಯ ಮೂಲಕವಾಗಿ ದೇವರು ಅನ್ಯಜನರಲ್ಲಿ ಮಾಡಿದ್ದ ಕಾರ್ಯಗಳನ್ನು ಒಂದೊಂದಾಗಿ ವಿವರಿಸಿದನು.",
			"20": "<sup>20</sup> ಅವರು ಇದನ್ನು ಕೇಳಿ ದೇವರನ್ನು ಕೊಂಡಾಡಿದರು. ಆಗ ಅವರು ಅವನಿಗೆ ಹೇಳಿದ್ದು - ಸಹೋದರನೇ, ಯೆಹೂದ್ಯರಲ್ಲಿ ಯೇಸುವನ್ನು ನಂಬಿರುವವರು ಎಷ್ಟೋ ಸಾವಿರ ಮಂದಿ ಇದ್ದಾರೆಂಬದನ್ನು ನೋಡುತ್ತೀಯಲ್ಲಾ. ಅವರೆಲ್ಲರೂ ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿ ಅಭಿಮಾನಿಗಳಾಗಿದ್ದಾರೆ.",
			"21": "<sup>21</sup> ನೀನು ಅನ್ಯಜನರಲ್ಲಿ ವಾಸವಾಗಿರುವ ಯೆಹೂದ್ಯರೆಲ್ಲರಿಗೆ - ನಿಮ್ಮ ಮಕ್ಕಳಿಗೆ ಸುನ್ನತಿಮಾಡಿಸಬೇಡಿರಿ, ನಿಮ್ಮ ಆಚಾರಗಳನ್ನು ಅನುಸರಿಸಿ ನಡೆಯಬೇಡಿರಿ ಎಂದು ಹೇಳಿ ಮೋಶೆಯ ಧರ್ಮವನ್ನು ತ್ಯಾಗಮಾಡಬೇಕೆಂಬದಾಗಿ ಬೋಧಿಸುತ್ತೀ ಎಂದು ನಿನ್ನ ವಿಷಯದಲ್ಲಿ ಬಹಳವಾಗಿ ಕೇಳಿದ್ದಾರೆ.",
			"22": "<sup>22</sup> ಹೀಗಿರುವಲ್ಲಿ ಏನು ಮಾಡಬೇಕು? ನೀನು ಬಂದಿರುವದನ್ನು ಹೇಗೂ ಕೇಳುವರು.",
			"23": "<sup>23</sup> ಆದಕಾರಣ ನಾವು ನಿನಗೆ ಹೇಳುವ ಕೆಲಸವನ್ನು ಮಾಡು. ನಮ್ಮಲ್ಲಿ ವ್ರತಹಿಡಿದವರು ನಾಲ್ಕುಮಂದಿ ಇದ್ದಾರೆ.",
			"24": "<sup>24</sup> ನೀನು ಅವರನ್ನು ಕರೆದುಕೊಂಡು ಹೋಗಿ ಅವರೊಡನೆ ನಿನ್ನನ್ನು ಶುದ್ಧಿಮಾಡಿಕೊಂಡು ಅವರು ತಮ್ಮ ವ್ರತವನ್ನು ತೀರಿಸಿಕೊಳ್ಳುವದಕ್ಕಾಗಿ ಅವರಿಗೋಸ್ಕರ ಹಣ ವೆಚ್ಚಮಾಡು. ಹೀಗೆ ಮಾಡಿದರೆ ಎಲ್ಲರೂ ನಿನ್ನ ವಿಷಯವಾಗಿ ತಾವು ಕೇಳಿದ ಸುದ್ದಿ ನಿಜವಲ್ಲವೆಂತಲೂ ನೀನಾದರೂ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಕೈಕೊಂಡು ನಡೆಯುತ್ತೀ ಎಂತಲೂ ತಿಳುಕೊಳ್ಳುವರು.",
			"25": "<sup>25</sup> ಅನ್ಯಜನರಲ್ಲಿ ಯೇಸುವನ್ನು ನಂಬಿರುವವರ ವಿಷಯವಾದರೋ ವಿಗ್ರಹಕ್ಕೆ ನೈವೇದ್ಯಮಾಡಿದ್ದನ್ನೂ ರಕ್ತವನ್ನೂ ಕುತ್ತಿಗೆಹಿಸುಕಿ ಕೊಂದದ್ದನ್ನೂ ಹಾದರವನ್ನೂ ಬಿಟ್ಟು ದೂರವಾಗಿರಬೇಕೆಂಬದಾಗಿ ನಾವು ತೀರ್ಮಾನಿಸಿ ಬರೆದೆವು ಎಂದು ಹೇಳಿದರು.",
			"26": "<sup>26</sup> ಆಗ ಪೌಲನು ಆ ಮನುಷ್ಯರನ್ನು ಕರೆದುಕೊಂಡು ಮರುದಿನ ಅವರೊಡನೆ ತನ್ನನ್ನು ಶುದ್ಧಿಮಾಡಿಕೊಂಡು ವ್ರತದ ದಿವಸಗಳು ಮುಗಿದವೆಂದು ತಿಳಿಸುವವನಾಗಿ ದೇವಾಲಯದೊಳಗೆ ಹೋದನು. ಪ್ರತಿಯೊಬ್ಬನಿಗೋಸ್ಕರ ಮಾಡಬೇಕಾದ ಅರ್ಪಣೆಯನ್ನು ಮುಂದೆ ಅರ್ಪಿಸಬೇಕಾಗಿತ್ತು.",
			"27": "<sup>27</sup> ಆ ಏಳು ದಿವಸಗಳು ತುಂಬುತ್ತಿರುವಾಗ ಆಸ್ಯಸೀಮೆಯಿಂದ ಬಂದಿದ್ದ ಯೆಹೂದ್ಯರು ಅವನನ್ನು ದೇವಾಲಯದಲ್ಲಿ ಕಂಡು ಗುಂಪುಕೂಡಿದ ಜನರೆಲ್ಲರನ್ನು ಕಲಕಿ ಅವನನ್ನು ಹಿಡಿದು -",
			"28": "<sup>28</sup> ಇಸ್ರಾಯೇಲ್ ಜನರೇ, ಸಹಾಯಮಾಡಿರಿ, ನಮ್ಮ ಜನರಿಗೂ ಧರ್ಮಶಾಸ್ತ್ರಕ್ಕೂ ಈ ಆಲಯಕ್ಕೂ ವಿರುದ್ಧವಾಗಿ ಎಲ್ಲೆಲ್ಲಿಯೂ ಎಲ್ಲರಿಗೂ ಬೋಧನೆ ಹೇಳುವ ಆ ಮನುಷ್ಯನು ಇವನೇ. ಇದಲ್ಲದೆ ಇವನು ಗ್ರೀಕರನ್ನು ದೇವಾಲಯದೊಳಗೆ ಕರೆದುಕೊಂಡು ಬಂದು ಈ ಪರಿಶುದ್ಧಸ್ಥಳವನ್ನು ಹೊಲೆಮಾಡಿದ್ದಾನೆ ಎಂದು ಕೂಗಿದರು.",
			"29": "<sup>29</sup> ಮುಂಚೆ ಅವರು ಎಫೆಸದ ತ್ರೊಫಿಮನನ್ನು ಅವನ ಸಂಗಡ ಪಟ್ಟಣದಲ್ಲಿ ಕಂಡಿದ್ದರಿಂದ ಅವನನ್ನು ಪೌಲನು ದೇವಾಲಯದೊಳಗೆ ಕರೆದುಕೊಂಡು ಬಂದನೆಂದು ಭಾವಿಸಿದರು.",
			"30": "<sup>30</sup> ಆಗ ಪಟ್ಟಣವೆಲ್ಲಾ ಕಲಕಿಹೋಯಿತು, ಜನರು ಎಲ್ಲಾ ಕಡೆಯಿಂದ ಓಡಿಬಂದು ಕೂಡಿದರು. ಮತ್ತು ಪೌಲನನ್ನು ಹಿಡಿದು ದೇವಾಲಯದ ಹೊರಗೆ ಎಳೆದುಕೊಂಡು ಬಂದರು. ಕೂಡಲೇ ಬಾಗಿಲುಗಳನ್ನು ಮುಚ್ಚಿದರು.",
			"31": "<sup>31</sup> ಅವರು ಅವನನ್ನು ಕೊಲ್ಲುವದಕ್ಕೆ ಪ್ರಯತ್ನಿಸುತ್ತಿದ್ದಾಗ ಯೆರೂಸಲೇಮಿನಲ್ಲೆಲ್ಲಾ ಗಲಿಬಿಲಿಯಾಯಿತೆಂದು ಪಟಾಲಮಿನ ಸಹಸ್ರಾಧಿಪತಿಗೆ ವರದಿ ಬಂತು.",
			"32": "<sup>32</sup> ಅವನು ತಕ್ಷಣವೇ ಸಿಪಾಯಿಗಳನ್ನೂ ಶತಾಧಿಪತಿಗಳನ್ನೂ ತೆಗೆದುಕೊಂಡು ಅವರ ಮೇಲೆ ಓಡಿಬಂದನು. ಅವರು ಸಹಸ್ರಾಧಿಪತಿಯನ್ನೂ ಸಿಪಾಯಿಗಳನ್ನೂ ನೋಡಿ ಪೌಲನನ್ನು ಹೊಡೆಯುವದನ್ನು ಬಿಟ್ಟರು.",
			"33": "<sup>33</sup> ಸಹಸ್ರಾಧಿಪತಿಯು ಹತ್ತರಕ್ಕೆ ಬಂದು ಅವನನ್ನು ಹಿಡಿದು ಅವನಿಗೆ ಜೋಡು ಬೇಡಿಯನ್ನು ಹಾಕಬೇಕೆಂದು ಅಪ್ಪಣೆಕೊಟ್ಟು -",
			"34": "<sup>34</sup> ಇವನಾರು? ಏನು ಮಾಡಿದ್ದಾನೆ ಎಂದು ಕೇಳಲು ಕೆಲವರು ಹೀಗೆ ಕೆಲವರು ಹಾಗೆ ಕೂಗುತ್ತಿರಲು ಗದ್ದಲದ ನಿಮಿತ್ತ ನಿಜ ಸ್ಥಿತಿಯನ್ನು ತಿಳಿಯಲಾರದೆ ಅವನನ್ನು ಕೋಟೆಯೊಳಗೆ ತೆಗೆದುಕೊಂಡು ಹೋಗಿರೆಂದು ಅಪ್ಪಣೆಕೊಟ್ಟನು.",
			"35": "<sup>35</sup> ಪೌಲನು ಮೆಟ್ಲುಗಳ ಮೇಲೆ ಬಂದಾಗ ಜನರ ನೂಕಾಟದ ನಿಮಿತ್ತ ಸಿಪಾಯಿಗಳು ಅವನನ್ನು ಹೊತ್ತುಕೊಂಡು ಹೋಗಬೇಕಾಯಿತು.",
			"36": "<sup>36</sup> ಯಾಕಂದರೆ ಗುಂಪಾಗಿ ಕೂಡಿದ ಜನರು ಹಿಂದಿನಿಂದ ಬಂದು ಅವನನ್ನು ಕೊಲ್ಲಿರಿ ಎಂದು ಕೂಗುತ್ತಿದ್ದರು.",
			"37": "<sup>37</sup> ಪೌಲನನ್ನು ಕೋಟೆಯೊಳಗೆ ಸೇರಿಸುವದರೊಳಗಾಗಿ ಅವನು ಆ ಸಹಸ್ರಾಧಿಪತಿಯನ್ನು - ನಿನಗೆ ಒಂದು ಮಾತು ಹೇಳುವದಕ್ಕೆ ನನಗೆ ಅಪ್ಪಣೆ ಆದೀತೇ? ಎಂದು ಕೇಳಲು ಅವನು - ಗ್ರೀಕ್ ಭಾಷೆ ನಿನಗೆ ಬರುತ್ತದೋ?",
			"38": "<sup>38</sup> ಕೆಲವು ದಿವಸಗಳ ಹಿಂದೆ ದಂಗೆ ಎಬ್ಬಿಸಿ ಆ ನಾಲ್ಕು ಸಾವಿರ ಮಂದಿ ಘಾತುಕರನ್ನು ಅಡವಿಗೆ ಕರೆದುಕೊಂಡು ಹೋದ ಆ ಐಗುಪ್ತ್ಯನು ನೀನೇ ಅಲ್ಲವೇ ಎಂದು ಕೇಳಿದನು.",
			"39": "<sup>39</sup> ಅದಕ್ಕೆ ಪೌಲನು - ನಾನು ಯೆಹೂದ್ಯನು, ತಾರ್ಸದವನು, ಕಿಲಿಕ್ಯಸೀಮೆಯ ಪ್ರಖ್ಯಾತವಾದ ಪಟ್ಟಣದವನು. ಈ ಜನರಿಗೆ ಕೆಲವು ಮಾತುಗಳನ್ನು ಹೇಳುವದಕ್ಕೆ ನನಗೆ ಅಪ್ಪಣೆಯಾಗಬೇಕೆಂದು ಕೇಳಿಕೊಳ್ಳುತ್ತೇನೆ ಅಂದನು.",
			"40": "<sup>40</sup> ಸಹಸ್ರಾಧಿಪತಿಯು ಅಪ್ಪಣೆ ಕೊಡಲು ಪೌಲನು ಮೆಟ್ಲುಗಳ ಮೇಲೆ ನಿಂತುಕೊಂಡು ಜನರಿಗೆ ಕೈಸನ್ನೆಮಾಡಿದನು. ಗದ್ದಲವು ಬಹಳ ಮಟ್ಟಿಗೆ ಶಾಂತವಾದ ಮೇಲೆ ಅವನು ಇಬ್ರಿಯ ಭಾಷೆಯಲ್ಲಿ -"
		},
		"22": {
			"1": "<sup>1</sup> ಸಹೋದರರೇ, ತಂದೆಗಳೇ, ಈಗ ನಾನು ನಿಮಗೆ ಮಾಡುವ ಪ್ರತಿವಾದವನ್ನು ಕೇಳಿರಿ ಎಂದು ಕೂಗಿ ಹೇಳಿದನು.",
			"2": "<sup>2</sup> ಅವನು ತಮ್ಮ ಸಂಗಡ ಇಬ್ರಿಯ ಭಾಷೆಯಲ್ಲಿ ಮಾತಾಡುವದನ್ನು ಕೇಳಿ ಅವರು ಮತ್ತೂ ನಿಶ್ಶಬ್ದವಾದರು. ಆಗ ಅವನು ಹೇಳಿದ್ದೇನಂದರೆ -",
			"3": "<sup>3</sup> ನಾನು ಯೆಹೂದ್ಯನು, ಕಿಲಿಕ್ಯದ ತಾರ್ಸದಲ್ಲಿ ಹುಟ್ಟಿದವನು, ಆದರೆ ಈ ಪಟ್ಟಣದಲ್ಲೇ ಬೆಳೆದವನು. ಗಮಲಿಯೇಲನ ಪಾದಸನ್ನಿಧಿಯಲ್ಲಿ ನಮ್ಮ ಪಿತೃಗಳ ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿ ಪೂರ್ಣ ಶಿಕ್ಷಿತನಾದೆನು; ನೀವೆಲ್ಲರೂ ಈಹೊತ್ತು ದೇವರ ವಿಷಯದಲ್ಲಿ ಅಭಿಮಾನಿಗಳಾಗಿರುವಂತೆಯೇ ನಾನು ಅಭಿಮಾನಿಯಾಗಿದ್ದೆನು.",
			"4": "<sup>4</sup> ಕ್ರಿಸ್ತ ಮಾರ್ಗದವರನ್ನು ಹಿಂಸಿಸುವವನಾಗಿ ಗಂಡಸರಿಗೂ ಹೆಂಗಸರಿಗೂ ಬೇಡೀಹಾಕಿಸುತ್ತಾ ಅವರನ್ನು ಸೆರೆಮನೆಗಳಿಗೆ ಕೊಡಿಸುತ್ತಾ ಇದ್ದೆನು, ಅವರ ಕೊಲೆಗಾದರೂ ಹಿಂತೆಗೆಯಲಿಲ್ಲ.",
			"5": "<sup>5</sup> ಈ ವಿಷಯದಲ್ಲಿ ಮಹಾಯಾಜಕನೂ ಹಿರೀಸಭೆಯವರೆಲ್ಲರೂ ಸಾಕ್ಷಿಗಳಾಗಿದ್ದಾರೆ. ಅವರಿಂದಲೇ ನಾನು ದಮಸ್ಕದಲ್ಲಿದ್ದ ಸಹೋದರರಿಗೆ ಕಾಗದಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಅಲ್ಲಿಗೆ ಹೋದವರನ್ನೂ ಬೇಡಿಗಳಿಂದ ದಂಡನೆಗಾಗಿ ಯೆರೂಸಲೇಮಿಗೆ ತರಬೇಕೆಂದು ಹೊರಟೆನು.",
			"6": "<sup>6</sup> ನಾನು ಪ್ರಯಾಣಮಾಡುತ್ತಾ ದಮಸ್ಕದ ಹತ್ತರಕ್ಕೆ ಬಂದಾಗ ಸುಮಾರು ಮಧ್ಯಾಹ್ನದಲ್ಲಿ ಫಕ್ಕನೆ ಆಕಾಶದೊಳಗಿಂದ ಒಂದು ದೊಡ್ಡ ಬೆಳಕು ನನ್ನ ಸುತ್ತಲು ಹೊಳೆಯಿತು, ನಾನು ನೆಲಕ್ಕೆ ಬಿದ್ದೆನು.",
			"7": "<sup>7</sup> ಆಗ - ಸೌಲನೇ, ಸೌಲನೇ, ನನ್ನನ್ನು ಯಾಕೆ ಹಿಂಸೆಪಡಿಸುತ್ತೀ? ಎಂದು ನನಗೆ ಹೇಳಿದ ಒಂದು ಶಬ್ದವನ್ನು ಕೇಳಿದೆನು.",
			"8": "<sup>8</sup> ಅದಕ್ಕೆ ನಾನು - ಕರ್ತನೇ, ನೀನಾರು? ಎಂದು ಕೇಳಲು ಆತನು - ನೀನು ಹಿಂಸೆಪಡಿಸುವ ನಜರೇತಿನ ಯೇಸುವೇ ನಾನು ಎಂದು ನನಗೆ ಹೇಳಿದನು.",
			"9": "<sup>9</sup> ನನ್ನ ಜೊತೆಯಲ್ಲಿದ್ದವರು ಬೆಳಕನ್ನು ಕಂಡರೇ ಹೊರತು ನನ್ನ ಸಂಗಡ ಮಾತಾಡಿದವನ ಶಬ್ದವನ್ನು ಕೇಳಲಿಲ್ಲ.",
			"10": "<sup>10</sup> ಆಗ ನಾನು - ಕರ್ತನೇ, ನಾನೇನು ಮಾಡಬೇಕು? ಎಂದು ಕೇಳಲು ಕರ್ತನು ನನಗೆ - ನೀನೆದ್ದು ದಮಸ್ಕದೊಳಕ್ಕೆ ಹೋಗು, ಮಾಡುವದಕ್ಕೆ ನಿನಗೆ ನೇಮಿಸಿರುವದೆಲ್ಲಾ ಅಲ್ಲಿ ತಿಳಿಸಲ್ಪಡುವದು ಎಂದು ಹೇಳಿದನು.",
			"11": "<sup>11</sup> ಆ ಬೆಳಕಿನ ಪ್ರಭಾವದಿಂದ ನನಗೆ ಕಣ್ಣುಕಾಣದೆ ಇದ್ದದರಿಂದ ನನ್ನ ಜೊತೆಯಲ್ಲಿದ್ದವರು ನನ್ನನ್ನು ಕೈಹಿಡಿದು ದಮಸ್ಕದೊಳಕ್ಕೆ ಕರೆದುಕೊಂಡು ಹೋದರು.",
			"12": "<sup>12</sup> ಅಲ್ಲಿ ಧರ್ಮಶಾಸ್ತ್ರಕ್ಕೆ ಸರಿಯಾಗಿ ನಡೆಯುವ ಸದ್ಭಕ್ತನೂ ಆ ಸ್ಥಳದ ಯೆಹೂದ್ಯರೆಲ್ಲರಿಂದ ಒಳ್ಳೆಯವನೆಂದು ಹೆಸರು ಹೊಂದಿದವನೂ",
			"13": "<sup>13</sup> ಆಗಿದ್ದ ಅನನೀಯನೆಂಬವನು ನನ್ನ ಬಳಿಗೆ ಬಂದು ನಿಂತು - ಸಹೋದರನಾದ ಸೌಲನೇ, ನಿನಗೆ ಕಣ್ಣುಕಾಣಿಸಲಿ ಎಂದು ಹೇಳಿದನು. ಹೇಳಿದಾಕ್ಷಣವೇ ನನಗೆ ಕಣ್ಣು ಕಾಣಿಸಿತು, ನಾನು ಅವನನ್ನು ನೋಡಿದೆನು.",
			"14": "<sup>14</sup> ಆಮೇಲೆ ಅವನು - ನಮ್ಮ ಪಿತೃಗಳ ದೇವರು ತನ್ನ ಚಿತ್ತವನ್ನು ನೀನು ತಿಳುಕೊಳ್ಳುವದಕ್ಕೂ ಆ ನೀತಿವಂತನನ್ನು ನೋಡುವದಕ್ಕೂ ಆತನ ಬಾಯಿಂದ ಒಂದು ಮಾತನ್ನು ಕೇಳುವದಕ್ಕೂ ನಿನ್ನನ್ನು ನೇಮಿಸಿದ್ದಾನೆ.",
			"15": "<sup>15</sup> ನೀನು ಕಂಡು ಕೇಳಿದ್ದರ ವಿಷಯದಲ್ಲಿ ಎಲ್ಲಾ ಮನುಷ್ಯರ ಮುಂದೆ ಆತನಿಗೆ ಸಾಕ್ಷಿಯಾಗಿರಬೇಕು.",
			"16": "<sup>16</sup> ಈಗ ನೀನೇಕೆ ಸಾವಕಾಶಮಾಡುತ್ತೀ? ಎದ್ದು ಆತನ ಹೆಸರನ್ನು ಹೇಳಿಕೊಳ್ಳುವವನಾಗಿ ದೀಕ್ಷಾಸ್ನಾನಮಾಡಿಸಿಕೊಂಡು ನಿನ್ನ ಪಾಪಗಳನ್ನು ತೊಳೆದುಕೋ ಅಂದನು.",
			"17": "<sup>17</sup> ಅನಂತರದಲ್ಲಿ ನಾನು ಯೆರೂಸಲೇಮಿಗೆ ಹಿಂತಿರುಗಿ ಬಂದು ದೇವಾಲಯದೊಳಗೆ ಪ್ರಾರ್ಥನೆಮಾಡುತ್ತಿದ್ದಾಗ ಧ್ಯಾನಪರವಶನಾಗಿ ಯೇಸುವನ್ನು ಕಂಡೆನು.",
			"18": "<sup>18</sup> ಆತನು - ನೀನು ತ್ವರೆಪಟ್ಟು ಬೇಗನೆ ಯೆರೂಸಲೇಮಿನಿಂದ ಹೊರಟುಹೋಗು, ನನ್ನ ವಿಷಯದಲ್ಲಿ ನೀನು ಹೇಳುವ ಸಾಕ್ಷಿಯನ್ನು ಅವರು ಅಂಗೀಕರಿಸುವದಿಲ್ಲ ಅಂದನು.",
			"19": "<sup>19</sup> ಅದಕ್ಕೆ ನಾನು - ಕರ್ತನೇ, ಎಲ್ಲಾ ಸಭಾಮಂದಿರಗಳಲ್ಲಿ ನಿನ್ನ ಮೇಲೆ ನಂಬಿಕೆಯಿಟ್ಟವರನ್ನು ನಾನು ಸೆರೆಮನೆಯಲ್ಲಿ ಹಾಕಿಸುತ್ತಾ ಹೊಡಿಸುತ್ತಾ ಇದ್ದೆನೆಂಬದನ್ನು ಅವರೇ ತಿಳಿದಿದ್ದಾರೆ;",
			"20": "<sup>20</sup> ಮತ್ತು ನಿನ್ನ ಸಾಕ್ಷಿಯಾದ ಸ್ತೆಫನನ ರಕ್ತವು ಚೆಲ್ಲಿಸಲ್ಪಟ್ಟಾಗ ನಾನೂ ಹತ್ತರ ನಿಂತು ಒಪ್ಪಿಕೊಂಡು ಅವನನ್ನು ಕೊಂದವರ ವಸ್ತ್ರಗಳನ್ನು ಕಾಯುತ್ತಿದ್ದೆನಲ್ಲಾ ಅಂದೆನು.",
			"21": "<sup>21</sup> ಆತನು - ನೀನು ಹೋಗು, ನಾನು ನಿನ್ನನ್ನು ದೂರಕ್ಕೆ ಅನ್ಯಜನರ ಬಳಿಗೆ ಕಳುಹಿಸುತ್ತೇನೆಂದು ಹೇಳಿದನು.",
			"22": "<sup>22</sup> ಈ ಮಾತಿನ ತನಕ ಅವರು ಕೇಳುತ್ತಿದ್ದರು; ಆಮೇಲೆ - ಇಂಥವನನ್ನು ಭೂಮಿಯಿಂದ ತೆಗೆದುಹಾಕಿಬಿಡು; ಇವನು ಬದುಕುವದು ಯುಕ್ತವಲ್ಲವೆಂದು ಕೂಗಿಹೇಳಿದರು.",
			"23": "<sup>23</sup> ಅವರು ಕೂಗುತ್ತಾ ತಮ್ಮ ವಸ್ತ್ರಗಳನ್ನು ಕಿತ್ತುಹಾಕುತ್ತಾ ದೂಳನ್ನು ತೂರುತ್ತಾ ಇರಲಾಗಿ",
			"24": "<sup>24</sup> ಯಾವ ಕಾರಣದಿಂದ ಹೀಗೆ ಅವನಿಗೆ ವಿರುದ್ಧವಾಗಿ ಕೂಗಾಡುತ್ತಾರೆಂಬದನ್ನು ಸಹಸ್ರಾಧಿಪತಿಯು ತಿಳುಕೊಳ್ಳುವದಕ್ಕಾಗಿ ಅವನನ್ನು ಕೋಟೆಯೊಳಗೆ ತಂದು ಕೊರಡೆಗಳಿಂದ ಹೊಡೆದು ವಿಚಾರಿಸಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"25": "<sup>25</sup> ಅವರು ಪೌಲನನ್ನು ಬೊಗ್ಗಿಸಿ ಬಾರುಗಳಿಂದ ಕಟ್ಟುವಾಗ ಅವನು ತನ್ನ ಹತ್ತಿರ ನಿಂತಿದ್ದ ಶತಾಧಿಪತಿಯನ್ನು - ರೋಮಾಪುರದ ಹಕ್ಕುದಾರನಾದ ಮನುಷ್ಯನನ್ನು ನ್ಯಾಯವಿಚಾರಣೆಮಾಡದೆ ಕೊರಡೆಗಳಿಂದ ಹೊಡಿಸುವದು ನಿಮಗೆ ನ್ಯಾಯವೋ? ಎಂದು ಕೇಳಿದನು.",
			"26": "<sup>26</sup> ಶತಾಧಿಪತಿಯು ಆ ಮಾತನ್ನು ಕೇಳಿ ಸಹಸ್ರಾಧಿಪತಿಯ ಬಳಿಗೆ ಹೋಗಿ - ನೀನು ಏನು ಮಾಡಬೇಕೆಂದಿದ್ದೀ? ಈ ಮನುಷ್ಯನು ರೋಮಾಪುರದ ಹಕ್ಕುದಾರನು ಎಂದು ಹೇಳಲು",
			"27": "<sup>27</sup> ಸಹಸ್ರಾಧಿಪತಿಯು ಪೌಲನ ಹತ್ತಿರಕ್ಕೆ ಬಂದು - ನೀನು ರೋಮಾಪುರದವರ ಹಕ್ಕುಳ್ಳವನೇನು? ನನಗೆ ಹೇಳು ಎಂದು ಅವನನ್ನು ಕೇಳಿದನು.",
			"28": "<sup>28</sup> ಅವನು, ಹೌದು ಅಂದನು. ಅದಕ್ಕೆ ಸಹಸ್ರಾಧಿಪತಿಯು - ನಾನು ಬಹಳ ಹಣಕೊಟ್ಟು ಆ ಹಕ್ಕನ್ನು ಕೊಂಡುಕೊಂಡೆನು ಅನ್ನಲು ಪೌಲನು - ನಾನಾದರೋ ಹಕ್ಕುದಾರನಾಗಿ ಹುಟ್ಟಿದವನು ಅಂದನು.",
			"29": "<sup>29</sup> ಅವನನ್ನು ಹೊಡೆದು ವಿಚಾರಿಸುವದಕ್ಕೆ ಬಂದವರು ಕೂಡಲೆ ಅವನನ್ನು ಬಿಟ್ಟರು. ಅದಲ್ಲದೆ ಅವನನ್ನು ರೋಮಾಪುರದ ಹಕ್ಕುದಾರನೆಂದು ಸಹಸ್ರಾಧಿಪತಿಗೆ ತಿಳಿದುಬಂದಾಗ ತಾನು ಅವನನ್ನು ಕಟ್ಟಿಸಿದ್ದರಿಂದ ಅವನಿಗೂ ಭಯಹಿಡಿಯಿತು.",
			"30": "<sup>30</sup> ಪೌಲನ ಮೇಲೆ ಯೆಹೂದ್ಯರು ಯಾವ ತಪ್ಪು ಹೊರಿಸುತ್ತಾರೆಂಬುವ ವಿಷಯದಲ್ಲಿ ನಿಜವಾದ ಸಂಗತಿಯನ್ನು ತಿಳಿಯಬೇಕೆಂದು ಅಪೇಕ್ಷಿಸಿ ಸಹಸ್ರಾಧಿಪತಿಯು ಮರುದಿನ ಅವನ ಬೇಡಿಗಳನ್ನು ಬಿಚ್ಚಿ ಮಹಾಯಾಜಕರೂ ಹಿರೀಸಭೆಯವರೆಲ್ಲರೂ ಕೂಡಿಬರುವದಕ್ಕೆ ಅಪ್ಪಣೆಕೊಟ್ಟು ಪೌಲನನ್ನು ಕರೆದುಕೊಂಡು ಹೋಗಿ ಅವರ ಮುಂದೆ ನಿಲ್ಲಿಸಿದನು."
		},
		"23": {
			"1": "<sup>1</sup> ಆಗ ಪೌಲನು ಹಿರೀಸಭೆಯನ್ನು ದೃಷ್ಟಿಸಿ ನೋಡಿ - ಸಹೋದರರೇ, ನಾನು ಈ ದಿನದವರೆಗೂ ಒಳ್ಳೇ ಮನಸ್ಸಾಕ್ಷಿಯಿಂದ ದೇವರ ಮುಂದೆ ನಡೆದುಕೊಂಡಿದ್ದೇನೆ ಅಂದನು.",
			"2": "<sup>2</sup> ಅದಕ್ಕೆ ಮಹಾಯಾಜಕನಾದ ಅನನೀಯನು - ಅವನ ಬಾಯ ಮೇಲೆ ಹೊಡೆಯಿರಿ ಎಂದು ಹತ್ತಿರದಲ್ಲಿ ನಿಂತಿದ್ದವರಿಗೆ ಅಪ್ಪಣೆಕೊಡಲು",
			"3": "<sup>3</sup> ಪೌಲನು ಅವನಿಗೆ - ಎಲೈ ಸುಣ್ಣಾ ಹಚ್ಚಿದ ಗೋಡೆಯೇ, ದೇವರು ನಿನ್ನನ್ನು ಹೊಡೆಯುವನು. ನೀನು ಧರ್ಮಶಾಸ್ತ್ರಾನುಸಾರವಾಗಿ ನನ್ನ ವಿಚಾರಣೆ ಮಾಡುವದಕ್ಕೆ ಕೂತುಕೊಂಡು ಧರ್ಮಶಾಸ್ತ್ರ ವಿರುದ್ಧವಾಗಿ ನನ್ನನ್ನು ಹೊಡೆಯುವದಕ್ಕೆ ಅಪ್ಪಣೆಕೊಡುತ್ತೀಯೋ? ಅಂದನು.",
			"4": "<sup>4</sup> ಹತ್ತರ ನಿಂತಿದ್ದವರು - ದೇವರು ನೇಮಿಸಿದ ಮಹಾಯಾಜಕನನ್ನು ನೀನು ಬೈಯುತ್ತೀಯಾ? ಅನ್ನಲು",
			"5": "<sup>5</sup> ಪೌಲನು - ಸಹೋದರರೇ, ಮಹಾಯಾಜಕನೆಂದು ನನಗೆ ತಿಳಿಯಲಿಲ್ಲ. ನಿನ್ನ ಜನರಲ್ಲಿ ಅಧಿಪತಿಯಾಗಿರುವವನ ವಿಷಯವಾಗಿ ಕೆಟ್ಟದ್ದೇನೂ ಆಡಬಾರದೆಂದು ಬರೆದದೆಯಷ್ಟೆ ಅಂದನು.",
			"6": "<sup>6</sup> ಸಭೆಯವರಲ್ಲಿ ಒಂದು ಪಾಲು ಸದ್ದುಕಾಯರೂ ಒಂದು ಪಾಲು ಫರಿಸಾಯರೂ ಇರುವದನ್ನು ಪೌಲನು ತಿಳಿದು - ಸಹೋದರರೇ, ನಾನು ಫರಿಸಾಯನು, ಫರಿಸಾಯರ ಮಗನು; ಸತ್ತವರೆದ್ದು ಬರುವರು ಎಂಬ ನಿರೀಕ್ಷೆಯ ವಿಷಯವಾಗಿ ನನ್ನನ್ನು ವಿಚಾರಣೆಮಾಡುತ್ತಾರೆ ಎಂದು ಹಿರೀಸಭೆಯಲ್ಲಿ ಕೂಗಿದನು.",
			"7": "<sup>7</sup> ಇದನ್ನು ಅವನು ಹೇಳಿದಾಗ ಫರಿಸಾಯರಿಗೂ ಸದ್ದುಕಾಯರಿಗೂ ಜಗಳ ಹುಟ್ಟಿತು; ಸಭೆಯಲ್ಲಿ ಭೇದವುಂಟಾಯಿತು.",
			"8": "<sup>8</sup> ಯಾಕಂದರೆ - ಸತ್ತವರು ಎದ್ದುಬರುವದಿಲ್ಲವೆಂತಲೂ ದೇವದೂತನಾಗಲಿ ದೇಹವಿಲ್ಲದ ಆತ್ಮವಾಗಲಿ ಇಲ್ಲವೆಂತಲೂ ಸದ್ದುಕಾಯರು ಹೇಳುವರು; ಆ ಎರಡೂ ಉಂಟೆಂದು ಫರಿಸಾಯರು ಒಪ್ಪಿಕೊಳ್ಳುವರು.",
			"9": "<sup>9</sup> ಆಗ ದೊಡ್ಡ ಕೂಗಾಟವಾಯಿತು. ಫರಿಸಾಯರ ಪಕ್ಷದವರಾದ ಶಾಸ್ತ್ರಿಗಳಲ್ಲಿ ಕೆಲವರು ಎದ್ದು - ಈ ಮನುಷ್ಯನಲ್ಲಿ ನಮಗೆ ಕೆಟ್ಟದ್ದೇನೂ ಕಾಣಬರುವದಿಲ್ಲ; ಆತ್ಮವಾಗಲಿ ದೇವದೂತನಾಗಲಿ ಅವನ ಸಂಗಡ ಮಾತಾಡಿದ್ದರೂ ಮಾತಾಡಿರಬಹುದು ಎಂದು ವಾಗ್ವಾದಮಾಡಿದರು.",
			"10": "<sup>10</sup> ಜಗಳವು ಬಹಳವಾದಾಗ ಅವರು ಪೌಲನನ್ನು ಎಳೆದಾಡಿ ಚೂರುಚೂರು ಮಾಡಾರೆಂದು ಸಹಸ್ರಾಧಿಪತಿಯು ಭಯಪಟ್ಟು ಸಿಪಾಯಿಗಳಿಗೆ - ನೀವು ಹೋಗಿ ಅವರ ಮಧ್ಯದಿಂದ ಅವನನ್ನು ಬಲವಂತವಾಗಿ ಹಿಡಿದು ಕೋಟೆಯೊಳಗೆ ತರಬೇಕೆಂದು ಆಜ್ಞೆಕೊಟ್ಟನು.",
			"11": "<sup>11</sup> ಆ ದಿನದ ರಾತ್ರಿ ಕರ್ತನು ಪೌಲನ ಬಳಿಯಲ್ಲಿ ನಿಂತುಕೊಂಡು - ಧೈರ್ಯದಿಂದಿರು; ನೀನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ನನ್ನ ಸಂಗತಿಯನ್ನೆಲ್ಲಾ ಸಾಕ್ಷಿಯಾಗಿ ಹೇಳಿದಂತೆಯೇ ರೋಮಾಪುರದಲ್ಲಿಯೂ ಸಾಕ್ಷಿಹೇಳಬೇಕಾಗುವದು ಅಂದನು.",
			"12": "<sup>12</sup> ಬೆಳಗಾದ ಮೇಲೆ ಯೆಹೂದ್ಯರು ಒಳಸಂಚು ಮಾಡಿ - ನಾವು ಪೌಲನನ್ನು ಕೊಲ್ಲುವ ತನಕ ಅನ್ನಪಾನವೇನೂ ಮುಟ್ಟುವದಿಲ್ಲವೆಂದು ಶಪಥಮಾಡಿಕೊಂಡರು.",
			"13": "<sup>13</sup> ಹೀಗೆ ಮಾಡಿದವರು ನಾಲ್ವತ್ತು ಮಂದಿಗಿಂತ ಹೆಚ್ಚಾಗಿದ್ದರು.",
			"14": "<sup>14</sup> ಇವರು ಮಹಾಯಾಜಕರ ಮತ್ತು ಸಭೇಹಿರಿಯರ ಬಳಿಗೆ ಹೋಗಿ - ನಾವು ಪೌಲನನ್ನು ಕೊಲ್ಲುವ ತನಕ ಬಾಯಲ್ಲಿ ಏನೂ ಹಾಕುವದಿಲ್ಲವೆಂದು ಕಠಿನ ಶಪಥವನ್ನು ಮಾಡಿಕೊಂಡಿದ್ದೇವೆ.",
			"15": "<sup>15</sup> ಆದದರಿಂದ ನೀವು ಸಭೆಯವರ ಸಹಿತ ಸಹಸ್ರಾಧಿಪತಿಯ ಬಳಿಗೆ ಹೋಗಿ ಪೌಲನ ಸಂಗತಿಯನ್ನು ಇನ್ನೂ ಸೂಕ್ಷ್ಮವಾಗಿ ವಿಮರ್ಶೆಮಾಡಬೇಕೆಂದು ನೆವಹೇಳಿ ಅವನನ್ನು ಕೋಟೆಯಿಂದ ನಿಮ್ಮ ಬಳಿಗೆ ಕರೆದುಕೊಂಡು ಬರುವ ಹಾಗೆ ಕೇಳಿಕೊಳ್ಳಬೇಕು. ನಾವಾದರೋ ಅವನು ಹತ್ತರ ಬರುವದಕ್ಕಿಂತ ಮುಂಚೆಯೇ ಅವನನ್ನು ಕೊಲ್ಲುವದಕ್ಕೆ ಸಿದ್ಧವಾಗಿದ್ದೇವೆ ಅಂದರು.",
			"16": "<sup>16</sup> ಅವರು ಹೊಂಚಿಕೊಂಡಿರುವದನ್ನು ಪೌಲನ ಸೋದರಳಿಯನು ಕೇಳಿ ಕೋಟೆಯೊಳಗೆ ಬಂದು ಪೌಲನಿಗೆ ತಿಳಿಸಿದನು.",
			"17": "<sup>17</sup> ಪೌಲನು ಶತಾಧಿಪತಿಗಳಲ್ಲಿ ಒಬ್ಬನನ್ನು ಕರೆಯಿಸಿ - ಈ ಯೌವನಸ್ಥನನ್ನು ಸಹಸ್ರಾಧಿಪತಿಯ ಬಳಿಗೆ ಕರೆದುಕೊಂಡು ಹೋಗು; ಅವನಿಗೆ ತಿಳಿಸಬೇಕಾದ ಒಂದು ಮಾತು ಅದೆ ಎಂದು ಹೇಳಿದನು.",
			"18": "<sup>18</sup> ಶತಾಧಿಪತಿ ಅವನನ್ನು ಸಹಸ್ರಾಧಿಪತಿಯ ಬಳಿಗೆ ಕರೆದುಕೊಂಡು ಹೋಗಿ - ಸೆರೆಯವನಾದ ಪೌಲನು ನನ್ನನ್ನು ಕರೆದು ಈ ಯೌವನಸ್ಥನನ್ನು ತಮ್ಮ ಬಳಿಗೆ ಕರೆದುಕೊಂಡು ಹೋಗುವಂತೆ ಬೇಡಿಕೊಂಡನು; ತಮಗೆ ತಿಳಿಸಬೇಕಾದ ಏನೋ ಒಂದು ಮಾತು ಅದೆಯಂತೆ ಎಂಬದಾಗಿ ಹೇಳಲು",
			"19": "<sup>19</sup> ಸಹಸ್ರಾಧಿಪತಿಯು ಅವನನ್ನು ಕೈಹಿಡಿದು ಒಂದು ಕಡೆಗೆ ಏಕಾಂತವಾಗಿ ಕರೆದುಕೊಂಡು ಹೋಗಿ - ನೀನು ನನಗೆ ಹೇಳಬೇಕಾದದ್ದು ಏನು? ಎಂದು ಕೇಳಿದನು.",
			"20": "<sup>20</sup> ಅವನು - ಯೆಹೂದ್ಯರು ಪೌಲನ ಸಂಗತಿಯನ್ನು ಇನ್ನೂ ಸೂಕ್ಷ್ಮವಾಗಿ ವಿಚಾರಣೆಮಾಡಬೇಕೆಂಬ ನೆವ ಹೇಳಿ ನೀನು ನಾಳೆ ಅವನನ್ನು ಹಿರೀಸಭೆಯ ಬಳಿಗೆ ಕರೆದುಕೊಂಡು ಬರಬೇಕೆಂದು ಕೇಳಿಕೊಳ್ಳುವದಕ್ಕೆ ಒಪ್ಪಂದ ಮಾಡಿಕೊಂಡಿದ್ದಾರೆ.",
			"21": "<sup>21</sup> ನೀನು ಅವರ ಮಾತಿಗೆ ಒಡಂಬಡಬೇಡ; ಯಾಕಂದರೆ ನಾಲ್ವತ್ತಕ್ಕಿಂತ ಹೆಚ್ಚು ಮಂದಿ ತಾವು ಅವನನ್ನು ಕೊಲ್ಲುವ ತನಕ ಅನ್ನಪಾನವೇನೂ ಮುಟ್ಟುವದಿಲ್ಲವೆಂದು ಶಪಥವನ್ನು ಮಾಡಿಕೊಂಡು ಅವನಿಗೋಸ್ಕರ ಹೊಂಚುಹಾಕುತ್ತಾ ಇದ್ದಾರೆ. ಈಗ ಅವರು ನಿನ್ನ ಅಪ್ಪಣೆಯನ್ನು ಎದುರುನೋಡುತ್ತಾ ಸಿದ್ಧವಾಗಿದ್ದಾರೆ ಅಂದನು.",
			"22": "<sup>22</sup> ಆಗ ಸಹಸ್ರಾಧಿಪತಿಯು ಆ ಯೌವನಸ್ಥನಿಗೆ - ಈ ಸಂಗತಿಗಳನ್ನು ನನಗೆ ಸೂಚಿಸಿದ್ದನ್ನು ನೀನು ಯಾರಿಗೂ ಹೇಳಬೇಡ ಎಂದು ಖಂಡಿತವಾಗಿ ಅಪ್ಪಣೆ ಕೊಟ್ಟು ಅವನನ್ನು ಕಳುಹಿಸಿಬಿಟ್ಟನು.",
			"23": "<sup>23</sup> ಆಮೇಲೆ ಅವನು ಶತಾಧಿಪತಿಗಳಲ್ಲಿ ಇಬ್ಬರನ್ನು ಕರೆಯಿಸಿ - ರಾತ್ರಿ ಒಂಭತ್ತು ಘಂಟೆಗೆ ಕೈಸರೈಯದ ತನಕ ಹೋಗುವಂತೆ ಇನ್ನೂರು ಮಂದಿ ಸಿಪಾಯಿಗಳನ್ನೂ ಎಪ್ಪತ್ತು ಮಂದಿ ಸವಾರರನ್ನೂ ಇನ್ನೂರು ಮಂದಿ ಭಲ್ಲೆಯವರನ್ನೂ ಸಿದ್ಧಮಾಡಿರಿ;",
			"24": "<sup>24</sup> ಮತ್ತು ಕುದುರೆಗಳನ್ನು ಸಿದ್ಧಮಾಡಿ ಪೌಲನನ್ನು ಹತ್ತಿಸಿ ದೇಶಾಧಿಪತಿಯಾದ ಫೇಲಿಕ್ಸನ ಬಳಿಗೆ ಸುರಕ್ಷಿತವಾಗಿ ಕರೆದುಕೊಂಡು ಹೋಗಬೇಕೆಂದು ಹೇಳಿದನು.",
			"25": "<sup>25</sup> ಇದಲ್ಲದೆ ಈ ಕೆಳಗಣ ಅಭಿಪ್ರಾಯದ ಒಂದು ಕಾಗದವನ್ನು ಬರೆದನು, ಏನಂದರೆ -",
			"26": "<sup>26</sup> ಮಹಾರಾಜರಾಜಶ್ರೀ ದೇಶಾಧಿಪತಿಯಾದ ಫೇಲಿಕ್ಸನಿಗೆ ಕ್ಲೌದ್ಯ ಲೂಸ್ಯನು ಮಾಡುವ ವಂದನೆ.",
			"27": "<sup>27</sup> ಯೆಹೂದ್ಯರು ಈ ಮನುಷ್ಯನನ್ನು ಹಿಡಿದು ಕೊಲ್ಲಬೇಕೆಂದಿದ್ದಾಗ ನಾನು ಸಿಪಾಯಿಗಳೊಂದಿಗೆ ಹೋಗಿ ಅವನನ್ನು ರೋಮಾಪುರದ ಹಕ್ಕುದಾರನೆಂದು ತಿಳಿದು ಅವನನ್ನು ತಪ್ಪಿಸಿದೆನು.",
			"28": "<sup>28</sup> ಇದಲ್ಲದೆ ಅವನ ಮೇಲೆ ತಪ್ಪು ಹೊರಿಸಿದ ಕಾರಣವನ್ನು ತಿಳಿಯಲಪೇಕ್ಷಿಸಿ ಅವನನ್ನು ಅವರ ಹಿರೀಸಭೆಗೆ ಕರೆದುಕೊಂಡು ಹೋದೆನು.",
			"29": "<sup>29</sup> ಅಲ್ಲಿ ಅವರು ತಮ್ಮ ಧರ್ಮಶಾಸ್ತ್ರವಿಷಯಗಳನ್ನು ಹಿಡಿದು ಅವನ ಮೇಲೆ ತಪ್ಪು ಹೊರಿಸಿದರೇ ಹೊರತು ಮರಣ ದಂಡನೆಗಾಗಲಿ ಬೇಡಿಗಾಗಲಿ ಆಧಾರವಾದ ಯಾವ ಅಪರಾಧವನ್ನೂ ಹೊರಿಸಲಿಲ್ಲವೆಂದು ನನಗೆ ಕಂಡುಬಂತು.",
			"30": "<sup>30</sup> ಈ ಮನುಷ್ಯನಿಗೆ ವಿರುದ್ಧವಾಗಿ ಒಳಸಂಚು ಹುಟ್ಟಿತೆಂದು ನನಗೆ ತಿಳಿದುಬಂದದರಿಂದ ಕೂಡಲೆ ನಾನು ಅವನನ್ನು ನಿನ್ನ ಬಳಿಗೆ ಕಳುಹಿಸಿ ತಪ್ಪುಹೊರಿಸುವವರಿಗೆ ನಿನ್ನ ಮುಂದೆಯೇ ಅವನಿಗೆ ವಿರುದ್ಧವಾಗಿ ಮಾತಾಡುವ ಹಾಗೆ ಅಪ್ಪಣೆ ಕೊಟ್ಟೆನು ಎಂಬದೇ.",
			"31": "<sup>31</sup> ಸಿಪಾಯಿಗಳು ತಮಗೆ ಅಪ್ಪಣೆಯಾದಂತೆ ರಾತ್ರಿ ಕಾಲದಲ್ಲಿ ಪೌಲನನ್ನು ಅಂತಿಪತ್ರಿಗೆ ಕರೆದುಕೊಂಡು ಹೋದರು.",
			"32": "<sup>32</sup> ಮರುದಿನ ಅವರು ಸವಾರರನ್ನು ಅವನ ಜೊತೆಯಲ್ಲಿ ಹೋಗುವಂತೆ ಮಾಡಿ ತಾವು ಕೋಟೆಗೆ ಹಿಂತಿರುಗಿ ಬಂದರು.",
			"33": "<sup>33</sup> ಸವಾರರು ಕೈಸರೈಯಕ್ಕೆ ಸೇರಿ ದೇಶಾಧಿಪತಿಗೆ ಕಾಗದವನ್ನು ಒಪ್ಪಿಸಿ ಪೌಲನನ್ನು ಅವನ ಮುಂದೆ ನಿಲ್ಲಿಸಿದರು.",
			"34": "<sup>34</sup> ದೇಶಾಧಿಪತಿಯು ಆ ಕಾಗದವನ್ನು ಓದಿ ಪೌಲನು ಯಾವ ಸೀಮೆಯವನು? ಎಂದು ಕೇಳಿ ಅವನು ಕಿಲಿಕ್ಯದವನೆಂದು ತಿಳಿದು -",
			"35": "<sup>35</sup> ನಿನ್ನ ಮೇಲೆ ತಪ್ಪುಹೊರಿಸುವವರು ಬಂದನಂತರ ನಿನ್ನ ಕಾರ್ಯವನ್ನು ಪೂರ್ಣವಾಗಿ ವಿಚಾರಿಸುತ್ತೇನೆ ಎಂದು ಹೇಳಿ ಅವನನ್ನು ಹೆರೋದನ ಅರಮನೆಯಲ್ಲಿ ಇಟ್ಟು ಕಾಯಬೇಕೆಂಬದಾಗಿ ಅಪ್ಪಣೆಮಾಡಿದನು."
		},
		"24": {
			"1": "<sup>1</sup> ಐದು ದಿವಸಗಳಾದ ಮೇಲೆ ಮಹಾಯಾಜಕನಾದ ಅನನೀಯನು ಸಭೇಹಿರಿಯರಲ್ಲಿ ಕೆಲವರನ್ನೂ ತೆರ್ತುಲ್ಲನೆಂಬ ಒಬ್ಬ ವಕೀಲನನ್ನೂ ಕರೆದುಕೊಂಡು ಬಂದು ಪೌಲನ ಮೇಲೆ ದೇಶಾಧಿಪತಿಗೆ ಫಿರಿಯಾದಿ ಹೇಳಿದನು.",
			"2": "<sup>2</sup> ಪೌಲನನ್ನು ಕರೆಯಿಸಿದ ನಂತರ ತೆರ್ತುಲ್ಲನು ಅವನ ಮೇಲೆ ತಪ್ಪುಹೊರಿಸುವದಕ್ಕೆ ಪ್ರಾರಂಭಿಸಿ ಹೀಗಂದನು - ಶ್ರೀಮತ್ ಮಹಾ ಫೇಲಿಕ್ಸನೇ, ನಿನ್ನ ಮೂಲಕವಾಗಿ ನಮಗೆ ಬಹು ಸಮಾಧಾನ ಉಂಟಾಗುವದರಿಂದಲೂ ನಿನ್ನ ಪರಾಂಬರಿಕೆಯಿಂದ ಈ ದೇಶದ ಜನರಿಗೆ ಎಲ್ಲಾ ವಿಧದಲ್ಲಿಯೂ ಎಲ್ಲಾ ಸ್ಥಳದಲ್ಲಿಯೂ",
			"3": "<sup>3</sup> ಸುಧಾರಣೆಗಳು ಆಗುವದರಿಂದಲೂ ನಾವು ಈ ಉಪಕಾರಗಳನ್ನು ಕೃತಜ್ಞತೆಯಿಂದ ಒಪ್ಪಿಕೊಳ್ಳುತ್ತೇವೆ.",
			"4": "<sup>4</sup> ಆದರೆ ನಿನ್ನನ್ನು ಹೆಚ್ಚಾಗಿ ಬೇಸರಗೊಳಿಸುವದಕ್ಕೆ ಮನಸ್ಸಿಲ್ಲದೆ ನಾವು ಸಂಕ್ಷೇಪವಾಗಿ ಹೇಳುವ ಮಾತುಗಳನ್ನು ದಯೆಯಿಂದ ಲಾಲಿಸಬೇಕೆಂದು ಬೇಡಿಕೊಳ್ಳುತ್ತೇನೆ.",
			"5": "<sup>5</sup> ಈ ಮನುಷ್ಯನು ಪೀಡೆಯಂತಿದ್ದು ಲೋಕದಲ್ಲಿ ಎಲ್ಲೆಲ್ಲಿಯೂ ಇರುವ ಎಲ್ಲಾ ಯೆಹೂದ್ಯರಲ್ಲಿ ದಂಗೆಯನ್ನು ಎಬ್ಬಿಸುವವನೆಂತಲೂ ನಜರೇತಿನವರ ಪಾಷಂಡಮತದಲ್ಲಿ ಪ್ರಮುಖನೆಂತಲೂ ಕಂಡೆವು.",
			"6": "<sup>6</sup> ಇದಲ್ಲದೆ ಇವನು ದೇವಾಲಯವನ್ನು ಹೊಲೆಮಾಡುವದಕ್ಕೆ ಪ್ರಯತ್ನಮಾಡಿದನು; ಆದಕಾರಣ ಇವನನ್ನು ನಾವು ಹಿಡಿದೆವು.",
			"8": "<sup>8</sup> ಇವನನ್ನು ನೀನೇ ವಿಚಾರಿಸಿದರೆ ನಾವು ಇವನ ಮೇಲೆ ಹೊರಿಸುವ ಈ ತಪ್ಪುಗಳೆಲ್ಲಾ ನಿಜವೋ ಸುಳ್ಳೋ ಇವನಿಂದಲೇ ತಿಳಿದುಕೊಳ್ಳಬಹುದು ಎಂದು ಹೇಳಿದನು.",
			"9": "<sup>9</sup> ಆಗ ಯೆಹೂದ್ಯರು ಈ ಸಂಗತಿಗಳು ನಿಜವೇ ಎಂದು ಹೇಳಿ ತಾವೂ ಆ ದೋಷಾರೋಪಣೆ ಮಾಡುವದರಲ್ಲಿ ಸೇರಿದರು.",
			"10": "<sup>10</sup> ದೇಶಾಧಿಪತಿಯು ಪೌಲನಿಗೆ - ನೀನು ಮಾತಾಡಬಹುದೆಂದು ಸನ್ನೆಮಾಡಲು ಅವನು ಪ್ರತ್ಯುತ್ತರವನ್ನು ಹೇಳಿದ್ದೇನಂದರೆ - ನೀನು ಅನೇಕ ವರುಷಗಳಿಂದ ಈ ದೇಶದ ಜನರಿಗೆ ನ್ಯಾಯಾಧಿಪತಿಯಾಗಿರುತ್ತೀ ಎಂದು ತಿಳಿದು ನಾನು ಧೈರ್ಯವಾಗಿ ಪ್ರತಿವಾದ ಮಾಡುತ್ತೇನೆ.",
			"11": "<sup>11</sup> ನಾನು ದೇವಾರಾಧನೆಮಾಡುವದಕ್ಕೆ ಯೆರೂಸಲೇಮಿಗೆ ಹೋಗಿ ಹನ್ನೆರಡು ದಿವಸ ಮಾತ್ರವಾಯಿತೆಂದು ನೀನು ತಿಳಿದುಕೊಳ್ಳಬಹುದು.",
			"12": "<sup>12</sup> ಅಲ್ಲಿ ದೇವಾಲಯದಲ್ಲಾಗಲಿ ಸಭಾಮಂದಿರಗಳಲ್ಲಾಗಲಿ ಪಟ್ಟಣದಲ್ಲಾಗಲಿ ನಾನು ಯಾರ ಸಂಗಡವಾದರೂ ವಾದಿಸುವದನ್ನು ಇಲ್ಲವೆ ಜನರ ಗುಂಪು ಕೂಡಿಸುವದನ್ನು ಇವರು ಕಾಣಲಿಲ್ಲ.",
			"13": "<sup>13</sup> ಇದಲ್ಲದೆ ಇವರು ಈಗ ನನ್ನ ಮೇಲೆ ಹೊರಿಸುವ ತಪ್ಪುಗಳನ್ನು ನಿಜವೆಂದು ನಿನಗೆ ತೋರಿಸಲಾರರು.",
			"14": "<sup>14</sup> ಒಂದನ್ನು ಮಾತ್ರ ನಿನ್ನ ಮುಂದೆ ಒಪ್ಪಿಕೊಳ್ಳುತ್ತೇನೆ. ಅದೇನಂದರೆ - ಇವರು ಪಾಷಂಡಮತವೆಂದು ಹೇಳುವ ಮಾರ್ಗಕ್ಕನುಸಾರವಾಗಿ ನಾನು ನಮ್ಮ ಪಿತೃಗಳ ದೇವರನ್ನು ಸೇವಿಸುವವನಾಗಿದ್ದೇನೆ. ಧರ್ಮಶಾಸ್ತ್ರಕ್ಕನುಗುಣವಾಗಿರುವ ಮತ್ತು ಪ್ರವಾದಿಗಳ ಗ್ರಂಥಗಳಲ್ಲಿ ಬರೆದಿರುವ ಎಲ್ಲಾ ವಿಷಯಗಳನ್ನು ನಂಬುತ್ತೇನೆ.",
			"15": "<sup>15</sup> ಇದಲ್ಲದೆ ನೀತಿವಂತರಿಗೂ ಅನೀತಿವಂತರಿಗೂ ಪುನರುತ್ಥಾನವಾಗುವದೆಂದು ಇವರು ದೇವರಲ್ಲಿ ನಿರೀಕ್ಷೆಯಿಟ್ಟಿರುವ ಪ್ರಕಾರವೇ ನಾನೂ ನಿರೀಕ್ಷೆಯುಳ್ಳವನಾಗಿದ್ದೇನೆ;",
			"16": "<sup>16</sup> ಇದರ ದೆಸೆಯಿಂದ ದೇವರ ವಿಷಯದಲ್ಲಿಯೂ ಮನುಷ್ಯರ ವಿಷಯದಲ್ಲಿಯೂ ನಾನು ನಿರ್ದೋಷಿ ಎಂದು ಸಾಕ್ಷಿ ಹೇಳುವ ಮನಸ್ಸು ನನಗೆ ಯಾವಾಗಲೂ ಇರಬೇಕೆಂದು ಅಭ್ಯಾಸಮಾಡಿಕೊಳ್ಳುತ್ತೇನೆ.",
			"17": "<sup>17</sup> ಕೆಲವು ವರುಷಗಳಾದ ಮೇಲೆ ನಾನು ನನ್ನ ಸ್ವದೇಶದವರಿಗೆ ಧರ್ಮದ್ರವ್ಯಗಳನ್ನು ತರುವದಕ್ಕೂ ಕಾಣಿಕೆಗಳನ್ನು ಒಪ್ಪಿಸುವದಕ್ಕೂ ಬಂದೆನು.",
			"18": "<sup>18</sup> ನಾನು ಶುದ್ಧಮಾಡಿಕೊಂಡವನಾಗಿ ಅವುಗಳನ್ನು ಒಪ್ಪಿಸುತ್ತಿರುವಲ್ಲಿ ನನ್ನನ್ನು ದೇವಾಲಯದಲ್ಲಿ ಕಂಡರು. ನನ್ನ ಬಳಿಯಲ್ಲಿ ಜನರ ಗುಂಪು ಇರಲಿಲ್ಲ, ಗದ್ದಲವೂ ಇರಲಿಲ್ಲ.",
			"19": "<sup>19</sup> ನನ್ನನ್ನು ಕಂಡವರು ಆಸ್ಯಸೀಮೆಯಿಂದ ಬಂದ ಕೆಲವು ಯೆಹೂದ್ಯರೇ; ನನ್ನ ಮೇಲೆ ಅವರಿಗೆ ಏನಾದರೂ ಇದ್ದರೆ ತಾವೇ ನಿನ್ನ ಮುಂದೆ ತಪ್ಪುಹೊರಿಸುವದಕ್ಕೆ ಇಲ್ಲಿ ಬರಬೇಕಾಗಿತ್ತು.",
			"20": "<sup>20</sup> ಇಲ್ಲವಾದ್ದರಿಂದ ನಾನು ಹಿರೀಸಭೆಯ ಎದುರಿನಲ್ಲಿ ನಿಂತಿದ್ದಾಗ ನನ್ನಲ್ಲಿ ಅಕ್ರಮವನ್ನೇನಾದರೂ ಕಂಡಿದ್ದರೆ ಇವರೇ ಹೇಳಲಿ.",
			"21": "<sup>21</sup> ನಾನು ಇವರ ನಡುವೆ ನಿಂತು - ಪುನರುತ್ಥಾನದ ವಿಷಯದಲ್ಲಿ ಈಹೊತ್ತು ನಿಮ್ಮಿಂದ ನನಗೆ ವಿಚಾರಣೆಯಾಗುತ್ತದೆ ಎಂದು ಕೂಗಿದ್ದನ್ನೇ ಹೊರತು ಬೇರೆ ಏನೂ ಹೇಳಲಾರರು ಅಂದನು.",
			"22": "<sup>22</sup> ಫೇಲಿಕ್ಸನು ಕ್ರಿಸ್ತ ಮಾರ್ಗವನ್ನು ತಕ್ಕ ಮಟ್ಟಿಗೆ ತಿಳಿದವನಾದರೂ ಅವರಿಗೆ - ಸಹಸ್ರಾಧಿಪತಿಯಾದ ಲೂಸ್ಯನು ಬಂದ ಮೇಲೆ ನಿಮ್ಮ ಕಾರ್ಯವನ್ನು ತೀರ್ಮಾನಿಸುತ್ತೇನೆಂದು ಹೇಳಿ ವಿಚಾರಣೆಯನ್ನು ತಡೆಮಾಡಿದನು.",
			"23": "<sup>23</sup> ಆಗ ಅವನು ಶತಾಧಿಪತಿಗೆ - ಪೌಲನು ಕಾವಲಲ್ಲಿರಬೇಕು, ಆದರೆ ಅವನು ಬಿಡುವಾಗಿರಲಿ, ಅವನನ್ನು ಉಪಚರಿಸುವದಕ್ಕೆ ಅವನ ಸ್ವಂತ ಜನರಲ್ಲಿ ಯಾರಿಗೂ ಅಡ್ಡಿಮಾಡಬಾರದೆಂದು ಅಪ್ಪಣೆಮಾಡಿದನು.",
			"24": "<sup>24</sup> ಕೆಲವು ದಿವಸಗಳಾದ ಮೇಲೆ ಫೇಲಿಕ್ಸನು ಯೆಹೂದ್ಯಳಾದ ದ್ರೂಸಿಲ್ಲಳೆಂಬ ಸ್ವಂತ ಹೆಂಡತಿಯೊಂದಿಗೆ ಬಂದು ಪೌಲನನ್ನು ಕರಿಸಿಕೊಂಡು ಕ್ರಿಸ್ತ ಯೇಸುವಿನಲ್ಲಿಡತಕ್ಕ ನಂಬಿಕೆಯ ವಿಷಯವಾಗಿ ಅವನು ಹೇಳಿದ ಮಾತುಗಳನ್ನು ಕೇಳಿದನು.",
			"25": "<sup>25</sup> ಪೌಲನು ಸುನೀತಿ ದಯೆ ಮುಂದಣ ನ್ಯಾಯವಿಚಾರಣೆ ಇವುಗಳ ವಿಷಯವಾಗಿ ಪ್ರಸ್ತಾಪಿಸುತ್ತಿದ್ದಾಗ ಫೇಲಿಕ್ಸನು ಭಯಗ್ರಸ್ತನಾಗಿ ಅವನಿಗೆ - ಸದ್ಯಕ್ಕೆ ಹೋಗು; ಸಮಯ ದೊರಕಿದಾಗ ನಿನ್ನನ್ನು ಕರಿಸುವೆನು ಎಂದು ಹೇಳಿದನು.",
			"26": "<sup>26</sup> ಇದಲ್ಲದೆ ತನಗೆ ಪೌಲನಿಂದ ಹಣ ಸಿಕ್ಕೀತೆಂಬ ನಿರೀಕ್ಷೆ ಅವನಿಗಿದ್ದ ಕಾರಣ ಅವನನ್ನು ಬಾರಿಬಾರಿಗೂ ಕರಿಸಿ ಅವನ ಸಂಗಡ ಸಲ್ಲಾಪಮಾಡುತ್ತಿದ್ದನು.",
			"27": "<sup>27</sup> ಎರಡು ವರುಷಗಳು ತುಂಬಿದ ಮೇಲೆ ಫೇಲಿಕ್ಸನ ಸ್ಥಾನಕ್ಕೆ ಪೋರ್ಕಿಯ ಫೆಸ್ತನು ಬಂದನು. ಫೇಲಿಕ್ಸನು ಯೆಹೂದ್ಯರ ಪ್ರೀತಿಯನ್ನು ಸಂಪಾದಿಸಬೇಕೆಂಬ ಕೋರಿಕೆಯಿಂದ ಪೌಲನನ್ನು ಹಾಗೆಯೇ ಸೆರೆಯಲ್ಲಿ ಬಿಟ್ಟುಹೋದನು."
		},
		"25": {
			"1": "<sup>1</sup> ಫೆಸ್ತನು ಸೀಮೆಯ ಕಾರ್ಯಭಾರವನ್ನು ವಹಿಸಿಕೊಂಡು ಮೂರು ದಿವಸಗಳ ತರುವಾಯ ಕೈಸರೈಯದಿಂದ ಯೆರೂಸಲೇಮಿಗೆ ಹೋದನು.",
			"2": "<sup>2</sup> ಅಲ್ಲಿ ಮಹಾಯಾಜಕನೂ ಯೆಹೂದ್ಯರಲ್ಲಿ ಪ್ರಮುಖರೂ ಅವನಿಗೆ ಪೌಲನ ಮೇಲಣ ಫಿರಿಯಾದಿಯನ್ನು ಹೇಳಿ -",
			"3": "<sup>3</sup> ನೀನು ದಯಮಾಡಿ ಅವನನ್ನು ಯೆರೂಸಲೇಮಿಗೆ ಕರತರಿಸಬೇಕೆಂದು ಪೌಲನ ಕೇಡಿಗಾಗಿ ಬಿನ್ನಹವನ್ನು ಮಾಡಿದರು. ಮಾರ್ಗದಲ್ಲಿ ಅವನನ್ನು ಕೊಲ್ಲುವದಕ್ಕಾಗಿ ಹೊಂಚುಹಾಕಿಕೊಂಡಿದ್ದರು.",
			"4": "<sup>4</sup> ಫೆಸ್ತನು ಅವರಿಗೆ - ಪೌಲನು ಕೈಸರೈಯದಲ್ಲಿ ಕಾವಲೊಳಗಿದ್ದಾನೆ, ನಾನೇ ಬೇಗ ಅಲ್ಲಿಗೆ ಹೊರಟುಹೋಗಬೇಕೆಂದಿದ್ದೇನೆ;",
			"5": "<sup>5</sup> ಆದಕಾರಣ ನಿಮ್ಮಲ್ಲಿ ಪ್ರಮುಖರು ನನ್ನೊಂದಿಗೆ ಬಂದು ಆ ಮನುಷ್ಯನಲ್ಲಿ ಅನುಚಿತವಾದದ್ದೇನಾದರೂ ಇದ್ದರೆ ಅವನ ಮೇಲೆ ತಪ್ಪುಹೊರಿಸಲಿ ಎಂದು ಹೇಳಿದನು.",
			"6": "<sup>6</sup> ಅವನು ಅವರಲ್ಲಿ ಎಂಟು ಹತ್ತಕ್ಕಿಂತ ಹೆಚ್ಚು ದಿವಸಗಳು ನಿಲ್ಲದೆ ಕೈಸರೈಯಕ್ಕೆ ಹೋಗಿ ಮರುದಿನ ನ್ಯಾಯಸ್ಥಾನದಲ್ಲಿ ಕೂತುಕೊಂಡು ಪೌಲನನ್ನು ತರಬೇಕೆಂದು ಅಪ್ಪಣೆಮಾಡಿದನು.",
			"7": "<sup>7</sup> ಅವನು ಬಂದ ಮೇಲೆ ಯೆರೂಸಲೇಮಿನಿಂದ ಬಂದಿದ್ದ ಯೆಹೂದ್ಯರು ಅವನ ಸುತ್ತಲು ನಿಂತುಕೊಂಡು ತಾವು ಸ್ಥಾಪಿಸಲಾರದ ಅನೇಕ ದೊಡ್ಡ ದೊಡ್ಡ ತಪ್ಪುಗಳನ್ನು ಹೊರಿಸುತ್ತಿರಲು",
			"8": "<sup>8</sup> ಪೌಲನು - ಯೆಹೂದ್ಯರ ಧರ್ಮಶಾಸ್ತ್ರದ ವಿಷಯದಲ್ಲಾಗಲಿ ದೇವಾಲಯದ ವಿಷಯದಲ್ಲಾಗಲಿ ಚಕ್ರವರ್ತಿಯ ವಿಷಯದಲ್ಲಾಗಲಿ ನಾನು ಯಾವ ತಪ್ಪನ್ನೂ ಮಾಡಲಿಲ್ಲವೆಂದು ಪ್ರತ್ಯುತ್ತರ ಹೇಳಿದನು.",
			"9": "<sup>9</sup> ಫೆಸ್ತನು ಯೆಹೂದ್ಯರ ಪ್ರೀತಿಯನ್ನು ಸಂಪಾದಿಸಿಕೊಳ್ಳಬೇಕೆಂದು ಅಪೇಕ್ಷಿಸಿ, ಪೌಲನನ್ನು - ನೀನು ಯೆರೂಸಲೇಮಿಗೆ ಹೋಗಿ ಅಲ್ಲಿ ಈ ಕಾರ್ಯಗಳ ವಿಷಯವಾಗಿ ನನ್ನ ಮುಂದೆ ವಿಚಾರಿಸಲ್ಪಡುವದಕ್ಕೆ ನಿನಗೆ ಇಷ್ಟವುಂಟೋ? ಎಂದು ಕೇಳಲು",
			"10": "<sup>10</sup> ಪೌಲನು - ನಾನು ಚಕ್ರವರ್ತಿಯ ನ್ಯಾಯಸ್ಥಾನದ ಮುಂದೆ ನಿಂತಿದ್ದೇನೆ; ಇಲ್ಲಿಯೇ ನನ್ನ ವಿಚಾರಣೆಯಾಗತಕ್ಕದ್ದು. ಯೆಹೂದ್ಯರಿಗೆ ನಾನು ಅನ್ಯಾಯವೇನೂ ಮಾಡಲಿಲ್ಲ; ಅದು ನಿನಗೂ ಚೆನ್ನಾಗಿ ತಿಳಿದೇ ಇದೆ.",
			"11": "<sup>11</sup> ನಾನು ಅನ್ಯಾಯ ಮಾಡಿದವನಾಗಿ ಮರಣದಂಡನೆಗೆ ಕಾರಣವಾದ ಯಾವದನ್ನಾದರೂ ನಡಿಸಿದ್ದಾದರೆ ಮರಣದಂಡನೆಯನ್ನು ಬೇಡವೆನ್ನುವದಿಲ್ಲ. ಇವರು ನನ್ನ ಮೇಲೆ ಹೊರಿಸುವ ತಪ್ಪುಗಳಲ್ಲಿ ಒಂದೂ ನಿಜವಲ್ಲದ ಮೇಲೆ ಇವರ ಮೇಲಣ ದಯೆಯಿಂದ ನನ್ನನ್ನು ಒಪ್ಪಿಸಿಕೊಡುವದಕ್ಕೆ ಒಬ್ಬರಿಗೂ ಅಧಿಕಾರವಿಲ್ಲ. ನಾನು ಚಕ್ರವರ್ತಿಯ ಎದುರಿನಲ್ಲಿ ಹೇಳಿಕೊಳ್ಳುತ್ತೇನೆ ಎನ್ನಲು",
			"12": "<sup>12</sup> ಫೆಸ್ತನು ತನ್ನ ಸಭೆಯವರ ಸಂಗಡ ಆಲೋಚನೆಮಾಡಿದ ಮೇಲೆ ಪೌಲನಿಗೆ - ಚಕ್ರವರ್ತಿಯ ಎದುರಿನಲ್ಲಿ ಹೇಳಿಕೊಳ್ಳುತ್ತೇನೆ ಅಂದಿಯಲ್ಲಾ, ಚಕ್ರವರ್ತಿಯ ಬಳಿಗೆ ಹೋಗಬೇಕು ಎಂದು ಹೇಳಿದನು.",
			"13": "<sup>13</sup> ಕೆಲವು ದಿವಸಗಳು ಗತಿಸಿದ ನಂತರ ಅಗ್ರಿಪ್ಪರಾಜನೂ ಬೆರ್ನಿಕೆರಾಣಿಯೂ ಫೆಸ್ತನ ದರ್ಶನಮಾಡಿಕೊಳ್ಳುವದಕ್ಕೆ ಕೈಸರೈಯಕ್ಕೆ ಬಂದರು.",
			"14": "<sup>14</sup> ಅವರು ಅನೇಕ ದಿವಸ ಅಲ್ಲಿ ನಿಂತಿರಲಾಗಿ ಫೆಸ್ತನು ಪೌಲನ ಸಂಗತಿಯನ್ನು ರಾಜನಿಗೆ ತಿಳಿಸಿ ಹೇಳಿದ್ದೇನಂದರೆ - ಫೇಲಿಕ್ಸನು ಬಿಟ್ಟುಹೋದ ಒಬ್ಬ ಸೆರೆಯವನು ಇದ್ದಾನೆ.",
			"15": "<sup>15</sup> ನಾನು ಯೆರೂಸಲೇಮಿನಲ್ಲಿದ್ದಾಗ ಯೆಹೂದ್ಯರ ಮಹಾಯಾಜಕರೂ ಸಭೇಹಿರಿಯರೂ ಅವನ ವಿಷಯವಾಗಿ ನನಗೆ ಫಿರ್ಯಾದಿ ಹೇಳಿ ಅವನಿಗೆ ವಿರುದ್ಧವಾಗಿ ತೀರ್ಪು ಆಗಬೇಕೆಂದು ಬೇಡಿಕೊಂಡರು.",
			"16": "<sup>16</sup> ನಾನು ಅವರಿಗೆ - ಪ್ರತಿವಾದಿಯು ವಾದಿಗಳಿಗೆ ಮುಖಾಮುಖಿಯಾಗಿ ನಿಂತು ತನ್ನ ಮೇಲೆ ಆರೋಪಿಸಿದ ದೋಷವಿಷಯದಲ್ಲಿ ಪ್ರತಿವಾದಮಾಡುವದಕ್ಕೆ ಆಸ್ಪದಕೊಡದೆ ಅವನನ್ನು ಒಪ್ಪಿಸಿಬಿಡುವದು ರೋಮಾಯರ ಪದ್ಧತಿಯಲ್ಲವೆಂದು ಹೇಳಿದೆನು.",
			"17": "<sup>17</sup> ಅವರು ಇಲ್ಲಿಗೆ ಕೂಡಿಬಂದಾಗ ನಾನು ಸ್ವಲ್ಪವಾದರೂ ತಡಮಾಡದೆ ಮರುದಿನವೇ ನ್ಯಾಯಸ್ಥಾನದ ಮೇಲೆ ಕೂತುಕೊಂಡು ಆ ಮನುಷ್ಯನನ್ನು ತರಬೇಕೆಂದು ಅಪ್ಪಣೆಕೊಟ್ಟೆನು.",
			"18": "<sup>18</sup> ತಪ್ಪುಹೊರಿಸುವವರು ನಿಂತುಕೊಂಡು ನಾನು ಭಾವಿಸಿದ್ದ ಅಪರಾಧಗಳಲ್ಲಿ ಒಂದನ್ನಾದರೂ ಅವನ ಮೇಲೆ ಹೊರಿಸದೆ",
			"19": "<sup>19</sup> ಅವರ ಮತದ ವಿಷಯದಲ್ಲಿಯೂ ಸತ್ತುಹೋದಂಥ ಯೇಸುವೆಂಬ ಒಬ್ಬನ ವಿಷಯದಲ್ಲಿಯೂ ಅವನ ಮೇಲೆ ಕೆಲವು ವಿವಾದದ ಮಾತುಗಳನ್ನು ತಂದರು. ಆ ಯೇಸು ಜೀವಿತನಾಗಿದ್ದಾನೆಂದು ಪೌಲನು ಹೇಳಿದನು.",
			"20": "<sup>20</sup> ಇಂಥ ಕಾರ್ಯಗಳನ್ನು ಹೇಗೆ ವಿಚಾರಣೆಮಾಡಬೇಕೋ ನನಗೆ ತೋರದೆ - ನೀನು ಯೆರೂಸಲೇಮಿಗೆ ಹೋಗಿ ಅಲ್ಲಿ ಇವುಗಳ ವಿಷಯವಾಗಿ ವಿಚಾರಣೆಹೊಂದುವದಕ್ಕೆ ನಿನಗೆ ಮನಸ್ಸುಂಟೋ? ಎಂದು ನಾನು ಕೇಳಲು",
			"21": "<sup>21</sup> ಪೌಲನು ಚಕ್ರವರ್ತಿಯ ಎದುರಿನಲ್ಲಿ ಹೇಳಿಕೊಳ್ಳುವ ಪರ್ಯಂತರ ತನ್ನನ್ನು ಕಾಯಬೇಕೆಂದು ಕೇಳಿಕೊಂಡಾಗ ನಾನು ಚಕ್ರವರ್ತಿಯ ಬಳಿಗೆ ಕಳುಹಿಸುವ ತನಕ ಅವನನ್ನು ಕಾಯುವದಕ್ಕೆ ಅಪ್ಪಣೆಕೊಟ್ಟೆನು ಎಂದು ಹೇಳಿದನು.",
			"22": "<sup>22</sup> ಅದಕ್ಕೆ ಅಗ್ರಿಪ್ಪನು - ಆ ಮನುಷ್ಯನು ಹೇಳಿಕೊಳ್ಳುವದನ್ನು ಕೇಳುವದಕ್ಕೆ ನನಗೂ ಮನಸ್ಸದೆ ಎಂದು ಹೇಳಲು ಫೆಸ್ತನು - ನಾಳೆ ಕೇಳಬಹುದು ಅಂದನು.",
			"23": "<sup>23</sup> ಮರುದಿನ ಅಗ್ರಿಪ್ಪರಾಜನೂ ಬೆರ್ನಿಕೆರಾಣಿಯೂ ಬಹು ಆಡಂಬರದಿಂದ ಬಂದು ಸಹಸ್ರಾಧಿಪತಿಗಳ ಮತ್ತು ಪಟ್ಟಣದ ಮುಖಂಡರ ಸಂಗಡ ಸಭಾಸ್ಥಾನದೊಳಗೆ ಸೇರಿದಾಗ ಫೆಸ್ತನು ಅಪ್ಪಣೆಕೊಡಲು ಪೌಲನನ್ನು ಕರತಂದರು.",
			"24": "<sup>24</sup> ಆಗ ಫೆಸ್ತನು - ಅಗ್ರಿಪ್ಪರಾಜನೇ, ನಮ್ಮ ಸಂಗಡ ಕೂಡಿಬಂದಿರುವ ಎಲ್ಲಾ ಜನರೇ, ಈ ಮನುಷ್ಯನನ್ನು ನೋಡುತ್ತೀರಲ್ಲಾ, ಇವನ ವಿಷಯದಲ್ಲಿ ಯೆಹೂದ್ಯರೆಲ್ಲರೂ - ಇವನು ಇನ್ನು ಮೇಲೆ ಬದುಕಬಾರದೆಂದು ಕೂಗುತ್ತಾ ಯೆರೂಸಲೇಮಿನಲ್ಲಿಯೂ ಇಲ್ಲಿಯೂ ನನ್ನನ್ನು ಬೇಡಿಕೊಂಡರು.",
			"25": "<sup>25</sup> ಇವನು ಮರಣದಂಡನೆಗೆ ಕಾರಣವಾದದ್ದೇನೂ ಮಾಡಲಿಲ್ಲವೆಂದು ನನಗೆ ಕಂಡುಬಂತು. ತಾನೇ ಚಕ್ರವರ್ತಿಗೆ ವಿಜ್ಞಾಪನೆಮಾಡಿಕೊಂಡದ್ದರಿಂದ ಇವನನ್ನು ಕಳುಹಿಸುವದಕ್ಕೆ ತೀರ್ಮಾನಿಸಿದೆನು.",
			"26": "<sup>26</sup> ಇವನ ವಿಷಯದಲ್ಲಿ ಮಹಾಸನ್ನಿಧಾನಕ್ಕೆ ಬರೆಯುವದಕ್ಕೆ ನಿಶ್ಚಯವಾದದ್ದೇನೂ ಇಲ್ಲ. ಸೆರೆಯವನ ಮೇಲೆ ಆರೋಪಿಸಿರುವ ದೋಷಗಳನ್ನು ಸೂಚಿಸದೆ ಅವನನ್ನು ಕಳುಹಿಸುವದು ಯುಕ್ತವಲ್ಲವೆಂದು ನನಗೆ ತೋರುತ್ತದೆ.",
			"27": "<sup>27</sup> ಆದದರಿಂದ ವಿಚಾರಣೆಯಾದ ಮೇಲೆ ಬರೆಯುವದಕ್ಕೆ ಏನಾದರೂ ಸಿಕ್ಕೀತೆಂದು ಇವನನ್ನು ನಿಮ್ಮ ಮುಂದೆ ಮುಖ್ಯವಾಗಿ ಅಗ್ರಿಪ್ಪರಾಜನೇ ನಿನ್ನ ಮುಂದೆ ಕರೆಯಿಸಿದ್ದೇನೆ ಅಂದನು."
		},
		"26": {
			"1": "<sup>1</sup> ಆಗ ಅಗ್ರಿಪ್ಪನು ಪೌಲನಿಗೆ - ನೀನು ನಿನ್ನ ಪಕ್ಷದಲ್ಲಿ ಮಾತಾಡಬಹುದು ಅನ್ನಲು ಪೌಲನು ಕೈಯೆತ್ತಿ ಪ್ರತಿವಾದ ಮಾಡಿದ್ದೇನಂದರೆ -",
			"2": "<sup>2</sup> ಅಗ್ರಿಪ್ಪರಾಜನೇ, ಯೆಹೂದ್ಯರು ನನ್ನ ಮೇಲೆ ಆರೋಪಿಸುವ ಎಲ್ಲಾ ದೋಷಗಳ ವಿಷಯವಾಗಿ ನಿನ್ನ ಎದುರಿನಲ್ಲಿ ನಾನು ಈಹೊತ್ತು ಪ್ರತಿವಾದ ಮಾಡಬೇಕಾಗಿರುವದರಿಂದ ನನ್ನನ್ನು ಧನ್ಯನೆಂದು ಎಣಿಸಿಕೊಳ್ಳುತ್ತೇನೆ.",
			"3": "<sup>3</sup> ಯಾಕಂದರೆ ಯೆಹೂದ್ಯರಲ್ಲಿರುವ ಎಲ್ಲಾ ಆಚಾರಗಳನ್ನೂ ವಿವಾದಗಳನ್ನೂ ನೀನು ಚೆನ್ನಾಗಿ ಬಲ್ಲವನಾಗಿರುತ್ತೀ. ನನ್ನ ಮಾತುಗಳನ್ನು ಸಹನದಿಂದ ಕೇಳಬೇಕೆಂದು ಬೇಡಿಕೊಳ್ಳುತ್ತೇನೆ.",
			"4": "<sup>4</sup> ನಾನು ಮೊದಲಿಂದಲೂ ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ನನ್ನ ದೇಶದ ಜನರೊಳಗಿದ್ದುಕೊಂಡು ಬಾಲ್ಯದಿಂದ ಬದುಕಿದ ಬಗೆಯು ಎಲ್ಲಾ ಯೆಹೂದ್ಯರಿಗೆ ತಿಳಿದೇ ಅದೆ;",
			"5": "<sup>5</sup> ನಮ್ಮ ಧರ್ಮದಲ್ಲಿ ಬಹು ಖಂಡಿತವಾದ ಮತವನ್ನನುಸರಿಸಿ ಫರಿಸಾಯನಾಗಿ ನಡಕೊಂಡೆನೆಂದು ಪ್ರಾರಂಭದಿಂದಲೂ ಅವರು ಬಲ್ಲರು; ಸಾಕ್ಷಿಹೇಳುವದಕ್ಕೆ ಅವರಿಗೆ ಮನಸ್ಸಿದ್ದರೆ ಹೇಳಬಹುದು.",
			"6": "<sup>6</sup> ಈಗಲೂ ದೇವರು ನಮ್ಮ ಪಿತೃಗಳಿಗೆ ಮಾಡಿದ ವಾಗ್ದಾನವು ನೆರವೇರುವದೆಂಬ ನಿರೀಕ್ಷೆಯನ್ನು ಕುರಿತೇ ವಿಚಾರಿಸಲ್ಪಡುವವನಾಗಿ ಇಲ್ಲಿ ನಿಂತಿದ್ದೇನೆ.",
			"7": "<sup>7</sup> ನಮ್ಮ ಹನ್ನೆರಡು ಕುಲದವರು ಹಗಲಿರುಳು ಆಸಕ್ತಿಯಿಂದ ದೇವರನ್ನು ಸೇವಿಸುತ್ತಾ ಆ ವಾಗ್ದಾನದ ಫಲವನ್ನು ಹೊಂದುವದಕ್ಕೆ ನಿರೀಕ್ಷಿಸುತ್ತಾ ಇದ್ದಾರೆ. ರಾಜಾ, ಆ ನಿರೀಕ್ಷೆಯ ವಿಷಯದಲ್ಲಿಯೇ ಯೆಹೂದ್ಯರು ನನ್ನ ಮೇಲೆ ದೋಷಾರೋಪಣೆಮಾಡುತ್ತಾರೆ.",
			"8": "<sup>8</sup> ದೇವರು ಸತ್ತವರನ್ನು ಎಬ್ಬಿಸಿದ್ದು ನಂಬತಕ್ಕದ್ದಲ್ಲವೆಂದು ನೀವು ಯಾಕೆ ತೀರ್ಮಾನಿಸುತ್ತೀರಿ?",
			"9": "<sup>9</sup> ನಜರೇತಿನ ಯೇಸುವಿನ ಹೆಸರಿಗೆ ವಿರುದ್ಧವಾಗಿ ಅನೇಕ ಕಾರ್ಯಗಳನ್ನು ನಡಿಸಬೇಕೆಂದು ನಾನೂ ಯೋಚಿಸಿಕೊಂಡಿದ್ದೆನು.",
			"10": "<sup>10</sup> ಯೆರೂಸಲೇಮಿನಲ್ಲಿ ಹಾಗೆಯೇ ನಡಿಸಿದೆನು. ಮಹಾಯಾಜಕರಿಂದ ಅಧಿಕಾರವನ್ನು ಪಡೆದು ದೇವಜನರಲ್ಲಿ ಅನೇಕರನ್ನು ಸೆರೆಮನೆಗಳಲ್ಲಿ ಇಡಿಸಿ ಅವರಿಗೆ ಮರಣದ ತೀರ್ಪಾದಾಗ ನನ್ನ ಸಮ್ಮತಿಯನ್ನು ಸೂಚಿಸಿದೆನು.",
			"11": "<sup>11</sup> ಎಲ್ಲಾ ಸಭಾಮಂದಿರಗಳಲ್ಲಿಯೂ ನಾನು ಅನೇಕಾವರ್ತಿ ಅವರನ್ನು ದಂಡಿಸಿ ಅವರಿಂದ ದೂಷಣೆಯ ಮಾತುಗಳನ್ನಾಡಿಸುವದಕ್ಕೆ ಪ್ರಯತ್ನಿಸಿದೆನು. ಇದಲ್ಲದೆ ಅವರ ಮೇಲೆ ಬಹು ಕೋಪಾವೇಶವುಳ್ಳವನಾಗಿ ಪರಪಟ್ಟಣಗಳ ತನಕ ಅವರನ್ನು ಹಿಂಸೆಪಡಿಸಿದೆನು.",
			"12": "<sup>12</sup> ಈ ಉದ್ದೇಶದಿಂದ ನಾನು ಮಹಾಯಾಜಕರಿಂದ ಅಧಿಕಾರವನ್ನೂ ಉದ್ಯೋಗವನ್ನೂ ಹೊಂದಿ ದಮಸ್ಕಕ್ಕೆ ಹೋಗುತ್ತಾ ನಾನು ದಾರಿಯಲ್ಲಿದ್ದಾಗ",
			"13": "<sup>13</sup> ರಾಜನೇ, ಮಧ್ಯಾಹ್ನದ ಹೊತ್ತಿನಲ್ಲಿ ಪರಲೋಕದಿಂದಾದ ಒಂದು ಬೆಳಕು ನನ್ನ ಸುತ್ತಲೂ ನನ್ನ ಜೊತೆಯಲ್ಲಿ ಪ್ರಯಾಣಮಾಡುತ್ತಿದ್ದವರ ಸುತ್ತಲೂ ಸೂರ್ಯನ ಹೊಳಪಿಗಿಂತ ಹೆಚ್ಚಾಗಿ ಹೊಳೆಯುವದನ್ನು ಕಂಡೆನು.",
			"14": "<sup>14</sup> ನಾವೆಲ್ಲರು ನೆಲಕ್ಕೆ ಬೀಳಲು - ಸೌಲನೇ, ಸೌಲನೇ, ನನ್ನನ್ನು ಯಾಕೆ ಹಿಂಸೆಪಡಿಸುತ್ತೀ? ಮುಳ್ಳುಗೋಲನ್ನು ಒದೆಯುವದು ನಿನಗೆ ಕಷ್ಟ ಎಂದು ಇಬ್ರಿಯ ಭಾಷೆಯಿಂದ ಹೇಳುವ ವಾಣಿಯನ್ನು ಕೇಳಿದೆನು.",
			"15": "<sup>15</sup> ಆಗ ನಾನು - ಕರ್ತನೇ, ನೀನಾರು? ಅನ್ನಲು ಕರ್ತನು - ನೀನು ಹಿಂಸೆಪಡಿಸುವ ಯೇಸುವೇ ನಾನು. ನೀನು ಎದ್ದು ನಿಂತುಕೋ.",
			"16": "<sup>16</sup> ನಿನ್ನನ್ನು ನನ್ನ ಸೇವಕನಾಗಿಯೂ ಸಾಕ್ಷಿಯಾಗಿಯೂ ನೇಮಿಸುವದಕ್ಕೋಸ್ಕರ ನಿನಗೆ ಕಾಣಿಸಿಕೊಂಡಿದ್ದೇನೆ. ನಾನು ಈಗಲೂ ಮುಂದೆ ನಿನಗೆ ಕೊಡಲಿಕ್ಕಿರುವ ದರ್ಶನಗಳಲ್ಲಿಯೂ ನಿನಗೆ ಕಾಣಿಸಿಕೊಂಡದ್ದನ್ನು ಕುರಿತು ನೀನು ಸಾಕ್ಷಿಯಾಗಿರಬೇಕು.",
			"17": "<sup>17</sup> ನಾನು ನಿನ್ನನ್ನು ಇಸ್ರಾಯೇಲ್ ಜನರ ಕೈಯೊಳಗಿಂದಲೂ ಅನ್ಯಜನರ ಕೈಯೊಳಗಿಂದಲೂ ಬಿಡಿಸುವೆನು.",
			"18": "<sup>18</sup> ಅವರು ಕತ್ತಲೆಯಿಂದ ಬೆಳಕಿಗೂ ಸೈತಾನನ ಅಧಿಕಾರದಿಂದ ದೇವರ ಕಡೆಗೂ ತಿರುಗಿಕೊಂಡು ನನ್ನಲ್ಲಿ ನಂಬಿಕೆಯಿಡುವದರಿಂದ ಪಾಪಪರಿಹಾರವನ್ನೂ ಪವಿತ್ರರಾದವರಲ್ಲಿ ಹಕ್ಕನ್ನೂ ಹೊಂದುವಂತೆ ಅವರ ಕಣ್ಣುಗಳನ್ನು ತೆರೆಯಬೇಕೆಂದು ಅವರ ಬಳಿಗೆ ನಾನು ನಿನ್ನನ್ನು ಕಳುಹಿಸುತ್ತೇನೆ ಅಂದನು.",
			"19": "<sup>19</sup> ಆದಕಾರಣ ಅಗ್ರಿಪ್ಪರಾಜನೇ, ಪರಲೋಕದಿಂದ ನನಗೆ ಉಂಟಾದ ಆ ದರ್ಶನಕ್ಕೆ ನಾನು ಅವಿಧೇಯನಾಗದೆ",
			"20": "<sup>20</sup> ಮೊದಲು ದಮಸ್ಕದವರಿಗೆ, ಆಮೇಲೆ ಯೆರೂಸಲೇಮಿನಲ್ಲಿಯೂ ಯೂದಾಯದ ಎಲ್ಲಾ ಸೀಮೆಯಲ್ಲಿಯೂ ಇರುವವರಿಗೆ ಮತ್ತು ಅನ್ಯಜನರಿಗೆ ಸಹ - ನೀವು ದೇವರ ಕಡೆಗೆ ತಿರುಗಿಕೊಳ್ಳಬೇಕೆಂತಲೂ ಮಾನಸಾಂತರಕ್ಕೆ ಯೋಗ್ಯವಾದ ಕೃತ್ಯಗಳನ್ನು ಮಾಡಬೇಕೆಂತಲೂ ಸಾರಿದೆನು.",
			"21": "<sup>21</sup> ಈ ಕಾರಣದಿಂದ ಯೆಹೂದ್ಯರು ದೇವಾಲಯದಲ್ಲಿ ನನ್ನನ್ನು ಹಿಡಿದು ಕೊಲ್ಲುವದಕ್ಕೆ ಪ್ರಯತ್ನಿಸಿದರು.",
			"22": "<sup>22</sup> ಆದರೆ ನಾನು ದೇವರಿಂದ ಸಹಾಯವನ್ನು ಪಡೆದು ಈ ದಿನದವರೆಗೂ ಸುರಕ್ಷಿತವಾಗಿದ್ದು ಚಿಕ್ಕವರಿಗೂ ದೊಡ್ಡವರಿಗೂ ಸಾಕ್ಷಿಹೇಳುವವನಾಗಿದ್ದೇನೆ. ಪ್ರವಾದಿಗಳೂ ಮೋಶೆಯೂ ಮುಂದೆ ಆಗುವವೆಂದು ತಿಳಿಸಿದ ಸಂಗತಿಗಳನ್ನೇ ಹೊರತು ಇನ್ನೇನೂ ಹೇಳುವವನಲ್ಲ.",
			"23": "<sup>23</sup> ಆ ಸಂಗತಿಗಳು ಏನಂದರೆ - ಕ್ರಿಸ್ತನು ಬಾಧೆಪಟ್ಟು ಸಾಯಬೇಕಾದವನು ಮತ್ತು ಆತನು ಸತ್ತವರೊಳಗಿಂದ ಮೊದಲನೆಯವನಾಗಿ ಎದ್ದು ಯೆಹೂದ್ಯರಿಗೂ ಅನ್ಯಜನರಿಗೂ ಬೆಳಕನ್ನು ಪ್ರಸಿದ್ಧಿಪಡಿಸುವವನಾಗಿರುವನು ಎಂಬದೇ.",
			"24": "<sup>24</sup> ಹೀಗೆ ಪೌಲನು ಪ್ರತಿವಾದ ಮಾಡುತ್ತಿದ್ದಾಗ ಫೆಸ್ತನು ಮಹಾಶಬ್ದದಿಂದ - ಪೌಲನೇ, ನೀನು ಮರುಳಾಗಿದ್ದೀ; ನೀನು ಬಹಳವಾಗಿ ಮಾಡುವ ಶಾಸ್ತ್ರವಿಚಾರವು ನಿನ್ನನ್ನು ಮರುಳುಗೊಳಿಸುತ್ತದೆ ಎಂದು ಹೇಳಿದನು.",
			"25": "<sup>25</sup> ಅದಕ್ಕೆ ಪೌಲನು - ಶ್ರೀಮನ್ ಮಹಾ ಫೆಸ್ತನೇ, ನಾನು ಮರುಳಾಗಿಲ್ಲ; ಸ್ವಸ್ಥಬುದ್ಧಿಯುಳ್ಳವನಾಗಿ ಸತ್ಯವಾದ ಮಾತುಗಳನ್ನಾಡುತ್ತೇನೆ.",
			"26": "<sup>26</sup> ಅಗ್ರಿಪ್ಪರಾಜನು ಈ ಸಂಗತಿಗಳನ್ನು ತಿಳಿದವನು; ಅವನ ಮುಂದೆ ಧೈರ್ಯವಾಗಿ ಮಾತಾಡುತ್ತೇನೆ. ಇವುಗಳಲ್ಲಿ ಒಂದಾದರೂ ಅವನಿಗೆ ಮರೆಯಾದದ್ದಲ್ಲವೆಂದು ನಂಬಿದ್ದೇನೆ, ಯಾಕಂದರೆ ಇದು ಮೂಲೆಯಲ್ಲಿ ನಡೆದ ಕಾರ್ಯವಲ್ಲ.",
			"27": "<sup>27</sup> ಅಗ್ರಿಪ್ಪರಾಜನೇ, ಪ್ರವಾದಿಗಳಲ್ಲಿ ನಿನಗೆ ನಂಬಿಕೆಯುಂಟೋ? ಉಂಟೆಂದು ಬಲ್ಲೆನು ಅಂದನು.",
			"28": "<sup>28</sup> ಅದಕ್ಕೆ ಅಗ್ರಿಪ್ಪನು - ಅಲ್ಪಪ್ರಯತ್ನದಿಂದ ನನ್ನನ್ನು ಕ್ರೈಸ್ತನಾಗುವದಕ್ಕೆ ಒಡಂಬಡಿಸುತ್ತೀಯಾ? ಎಂದು ಹೇಳಲು",
			"29": "<sup>29</sup> ಪೌಲನು - ಅಲ್ಪಪ್ರಯತ್ನದಿಂದಾಗಲಿ ಅಧಿಕಪ್ರಯತ್ನದಿಂದಾಗಲಿ ನೀನು ಮಾತ್ರವಲ್ಲದೆ ಈಹೊತ್ತು ನನ್ನ ಮಾತುಗಳನ್ನು ಕೇಳುವವರೆಲ್ಲರೂ ಈ ಬೇಡಿಗಳ ಹೊರತು ನನ್ನಂತೆ ಆಗಬೇಕೆಂದು ದೇವರನ್ನು ಪ್ರಾರ್ಥಿಸುತ್ತೇನೆ ಅಂದನು.",
			"30": "<sup>30</sup> ಆಮೇಲೆ ರಾಜನೂ ದೇಶಾಧಿಪತಿಯೂ ಬೆರ್ನಿಕೆಯೂ ಅವರ ಸಂಗಡ ಕೂತಿದ್ದವರೂ ಎದ್ದು ಆಚೆಗೆ ಹೋಗಿ -",
			"31": "<sup>31</sup> ಈ ಮನುಷ್ಯನು ಮರಣದಂಡನೆಗಾಗಲಿ ಬೇಡಿಗಾಗಲಿ ಆಧಾರವಾದದ್ದೇನೂ ಮಾಡುವವನಲ್ಲ ಎಂಬದಾಗಿ ತಮ್ಮತಮ್ಮೊಳಗೆ ಮಾತಾಡಿಕೊಳ್ಳುತ್ತಿದ್ದರು.",
			"32": "<sup>32</sup> ಅಗ್ರಿಪ್ಪನು ಫೆಸ್ತನಿಗೆ - ಈ ಮನುಷ್ಯನು ಚಕ್ರವರ್ತಿಯ ಎದುರಿನಲ್ಲಿ ಹೇಳಿಕೊಳ್ಳುತ್ತೇನೆಂದು ಹೇಳದೆ ಹೋಗಿದ್ದರೆ ಇವನನ್ನು ಬಿಡಬಹುದಾಗಿತ್ತು ಎಂದು ಹೇಳಿದನು."
		},
		"27": {
			"1": "<sup>1</sup> ನಾವು ಸಮುದ್ರಮಾರ್ಗವಾಗಿ ಇತಾಲ್ಯದೇಶಕ್ಕೆ ಹೋಗಬೇಕೆಂದು ತೀರ್ಮಾನವಾದ ಮೇಲೆ ಪೌಲನನ್ನೂ ಬೇರೆ ಕೆಲವು ಸೆರೆಯವರನ್ನೂ ಚಕ್ರವರ್ತಿಯ ಪಟಾಲಮಿಗೆ ಸೇರಿದ ಯೂಲ್ಯನೆಂಬ ಒಬ್ಬ ಶತಾಧಿಪತಿಗೆ ಒಪ್ಪಿಸಿದರು.",
			"2": "<sup>2</sup> ಆಗ ಅದ್ರಮಿತ್ತಿಯದಿಂದ ಬಂದು ಆಸ್ಯಸೀಮೆಯ ಕರಾವಳಿಯ ಸ್ಥಳಗಳಿಗೆ ಹೋಗುವದಕ್ಕಿದ್ದ ಒಂದು ಹಡಗನ್ನು ಹತ್ತಿ ಸಮುದ್ರಪ್ರಯಾಣವನ್ನು ಪ್ರಾರಂಭಿಸಿದೆವು. ಮಕೆದೋನ್ಯಕ್ಕೆ ಸೇರಿದ ಥೆಸಲೋನಿಕದ ಅರಿಸ್ತಾರ್ಕನು ನಮ್ಮ ಜೊತೆಯಲ್ಲಿದ್ದನು.",
			"3": "<sup>3</sup> ಮರುದಿನ ಸೀದೋನಿಗೆ ಮುಟ್ಟಿದೆವು. ಯೂಲ್ಯನು ಪೌಲನಿಗೆ ದಯೆಯನ್ನು ತೋರಿಸುವವನಾಗಿ ಅವನನ್ನು ಸ್ನೇಹಿತರ ಬಳಿಗೆ ಹೋಗಿ ಸತ್ಕಾರ ಹೊಂದುವದಕ್ಕೆ ಬಿಟ್ಟನು.",
			"4": "<sup>4</sup> ಅಲ್ಲಿಂದ ಹೊರಟು ಎದುರು ಗಾಳಿ ಬೀಸುತ್ತಿದ್ದದರಿಂದ ಕುಪ್ರದ್ವೀಪದ ಮರೆಯಲ್ಲಿ ಸಾಗಿ",
			"5": "<sup>5</sup> ಕಿಲಿಕ್ಯಕ್ಕೂ ಪಂಫೂಲ್ಯಕ್ಕೂ ಎದುರಾಗಿರುವ ಸಮುದ್ರವನ್ನು ದಾಟಿ ಲುಕೀಯ ಸೀಮೆಯಲ್ಲಿರುವ ಮುರಕ್ಕೆ ಬಂದೆವು.",
			"6": "<sup>6</sup> ಅಲ್ಲಿ ಅಲೆಕ್ಸಾಂದ್ರಿಯದಿಂದ ಬಂದು ಇತಾಲ್ಯದೇಶಕ್ಕೆ ಹೋಗುತ್ತಿದ್ದ ಒಂದು ಹಡಗನ್ನು ಶತಾಧಿಪತಿಯು ಕಂಡು ನಮ್ಮನ್ನು ಅದರ ಮೇಲೆ ಹತ್ತಿಸಿದನು.",
			"7": "<sup>7</sup> ಅನೇಕ ದಿವಸ ನಿಧಾನವಾಗಿ ಸಾಗುತ್ತಾ ಎಷ್ಟೋ ಪ್ರಯಾಸದಿಂದ ಕ್ನೀದಕ್ಕೆ ಎದುರಾಗಿ ಬಂದಾಗ ಗಾಳಿಯು ನಮ್ಮನ್ನು ಅಲ್ಲಿಗೆ ಮುಟ್ಟಗೊಡಿಸದೆ ಇದ್ದದರಿಂದ ಕ್ರೇತದ್ವೀಪದ ಮರೆಯಲ್ಲಿ ಸಾಗಿ ಸಲ್ಮೋನೆಗೆ ಎದುರಾಗಿ ಬಂದು",
			"8": "<sup>8</sup> ಪ್ರಯಾಸದಿಂದ ಆ ದ್ವೀಪದ ಕರಾವಳಿಯನ್ನು ಹಿಡಿದು ಹೋಗಿ ಚಂದರೇವುಗಳೆಂಬ ಸ್ಥಳಕ್ಕೆ ಸೇರಿದೆವು. ಅದರ ಹತ್ತರದಲ್ಲಿ ಲಸಾಯವೆಂಬ ಪಟ್ಟಣವು ಇತ್ತು.",
			"9": "<sup>9</sup> ಹೀಗೆ ಬಹುಕಾಲ ಕಳೆದುಹೋಯಿತು; ಮಹಾ ಉಪವಾಸದ ದಿವಸವು ಆಗಿಹೋಗಿತ್ತು. ಈ ಸಂದರ್ಭದಲ್ಲಿ ಸಮುದ್ರಪ್ರಯಾಣ ಮಾಡುವದು ಅಪಾಯಕರವಾಗಿದ್ದದರಿಂದ",
			"10": "<sup>10</sup> ಪೌಲನು - ಜನರೇ, ಈ ಪ್ರಯಾಣದಿಂದ ಸರಕಿಗೂ ಹಡಗಿಗೂ ಮಾತ್ರವಲ್ಲದೆ ನಮ್ಮ ಪ್ರಾಣಗಳಿಗೂ ಕಷ್ಟವೂ ಬಹು ನಷ್ಟವೂ ಸಂಭವಿಸುವದೆಂದು ನನಗೆ ತೋರುತ್ತದೆ ಎಂದು ಅವರನ್ನು ಎಚ್ಚರಿಸಿದನು.",
			"11": "<sup>11</sup> ಆದರೆ ಪೌಲನು ಹೇಳಿದ ಮಾತುಗಳಿಗಿಂತ ನಾವಿಕನೂ ಹಡಗಿನ ಯಜಮಾನನೂ ಹೇಳಿದ ಮಾತಿಗೆ ಶತಾಧಿಪತಿಯು ಹೆಚ್ಚಾಗಿ ಲಕ್ಷ್ಯಕೊಟ್ಟನು.",
			"12": "<sup>12</sup> ಆ ರೇವು ಹಿಮಕಾಲವನ್ನು ಕಳೆಯುವದಕ್ಕೆ ಅನುಕೂಲವಲ್ಲದ್ದರಿಂದ ಅಲ್ಲಿಂದ ಹೊರಟು ಸಾಧ್ಯವಾದರೆ ಹೇಗೂ ಫೊಯಿನಿಕ್ಸ ಊರನ್ನು ಸೇರಿ ಅಲ್ಲೇ ಆ ಹಿಮಕಾಲವನ್ನು ಕಳೆಯಬೇಕೆಂದು ಹೆಚ್ಚು ಜನರು ಆಲೋಚನೆ ಹೇಳಿದರು. ಫೊಯಿನಿಕ್ಸವು ಕ್ರೇತದ್ವೀಪದ ಒಂದು ರೇವು; ಈಶಾನ್ಯ ದಿಕ್ಕಿಗೂ ಆಗ್ನೇಯ ದಿಕ್ಕಿಗೂ ಅಭಿಮುಖವಾಗಿದೆ.",
			"13": "<sup>13</sup> ತೆಂಕಣ ಗಾಳಿ ಮೆಲ್ಲಗೆ ಬೀಸಲಾಗಿ ತಮ್ಮ ಉದ್ದೇಶವು ಸಾರ್ಥಕವಾಯಿತೆಂದು ಭಾವಿಸಿ ಲಂಗರ ಎತ್ತಿ ಕ್ರೇತದ್ವೀಪವನ್ನು ಅನುಸರಿಸಿ ತೀರದ ಮಗ್ಗುಲಲ್ಲೇ ಹೋಗುತ್ತಿದ್ದರು.",
			"14": "<sup>14</sup> ಸ್ವಲ್ಪ ಹೊತ್ತಿನ ಮೇಲೆ ಆ ದ್ವೀಪದ ಮೇಲಣಿಂದ ಈಶಾನ್ಯಪೂರ್ವವಾಯು ಎಂಬ ಹುಚ್ಚುಗಾಳಿಯು ಹೊಡೆಯಿತು.",
			"15": "<sup>15</sup> ಆ ಹೊಡೆತಕ್ಕೆ ಹಡಗು ಸಿಕ್ಕಿಕೊಂಡು ಗಾಳಿಗೆದುರಾಗಿ ನಿಲ್ಲುವದಕ್ಕೆ ಆಗದೆಹೋದದರಿಂದ ಗಾಳಿಗೆ ಆಸ್ಪದಕೊಟ್ಟು ನೂಕಿಸಿಕೊಂಡು ಹೋದೆವು.",
			"16": "<sup>16</sup> ಕ್ಲೌಡವೆಂಬ ಒಂದು ಸಣ್ಣ ದ್ವೀಪದ ಮರೆಯಲ್ಲಿ ಹಡಗನ್ನು ನಡಿಸಿಕೊಂಡು ಬಂದ ತರುವಾಯ ಹಡಗಿನ ದೋಣಿಯನ್ನು ಎತ್ತಿ ಭದ್ರಮಾಡಿಕೊಳ್ಳುವದು ನಮಗೆ ಪ್ರಯಾಸವಾಯಿತು.",
			"17": "<sup>17</sup> ಅದನ್ನು ಮೇಲಕ್ಕೆ ಎತ್ತಿದ ತರುವಾಯ ಹೊರಜಿಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಹಡಗಿನ ಕೆಳಭಾಗವನ್ನು ಬಿಗಿದರು. ಆಮೇಲೆ ಸುರ್ತಿಸ್ ಎಂಬ ಕಳ್ಳುಸುಬಿನಲ್ಲಿ ಸಿಕ್ಕಿಬಿದ್ದೇವೆಂದು ಭಯಪಟ್ಟು ಹಾಯಿಯನ್ನು ಸಡಿಲುಮಾಡಿ ನೂಕಿಸಿಕೊಂಡು ಹೋದೆವು.",
			"18": "<sup>18</sup> ಗಾಳಿಯು ನಮ್ಮನ್ನು ಅತ್ಯಂತವಾಗಿ ಹೊಯಿದಾಡಿಸಿದ್ದರಿಂದ ಅವರು ಮರುದಿನ ಸರಕನ್ನು ಬಿಸಾಡುತ್ತಿದ್ದರು.",
			"19": "<sup>19</sup> ಮೂರನೆಯ ದಿನದಲ್ಲಿ ಹಡಗಿನ ಅಡ್ಡಮರವನ್ನು ತಮ್ಮ ಕೈಯಿಂದಲೇ ಹೊರಗೆ ಎತ್ತಿ ಹಾಕಿದರು.",
			"20": "<sup>20</sup> ಅನೇಕ ದಿವಸಗಳ ತನಕ ಸೂರ್ಯನಾಗಲಿ ನಕ್ಷತ್ರಗಳಾಗಲಿ ನಮಗೆ ಕಾಣಿಸದೆ ದೊಡ್ಡ ಬಿರುಗಾಳಿ ನಮ್ಮ ಮೇಲೆ ಹೊಡೆದದ್ದರಿಂದ ತಪ್ಪಿಸಿಕೊಂಡೇವೆಂಬ ಎಲ್ಲಾ ನಿರೀಕ್ಷೆಯು ಅಂದಿನಿಂದ ಕಳೆದುಹೋಯಿತು.",
			"21": "<sup>21</sup> ಅವರು ಬಹುಕಾಲ ಊಟವಿಲ್ಲದೆ ಇದ್ದ ಮೇಲೆ ಪೌಲನು ಅವರ ಮಧ್ಯದಲ್ಲಿ ನಿಂತುಕೊಂಡು - ಎಲೈ ಜನರೇ, ನೀವು ಕ್ರೇತದಿಂದ ಹೊರಟು ಈ ಕಷ್ಟನಷ್ಟಗಳಿಗೆ ಗುರಿಯಾಗದಂತೆ ನನ್ನ ಮಾತನ್ನು ಕೇಳಬೇಕಾಗಿತ್ತು.",
			"22": "<sup>22</sup> ಈಗಲಾದರೂ ನೀವು ಧೈರ್ಯದಿಂದಿರಬೇಕೆಂದು ನಿಮಗೆ ಬುದ್ಧಿಹೇಳುತ್ತೇನೆ; ಹಡಗು ನಷ್ಟವಾಗುವದೇ ಹೊರತು ನಿಮ್ಮಲ್ಲಿ ಒಬ್ಬರಿಗೂ ಪ್ರಾಣನಷ್ಟವಾಗುವದಿಲ್ಲ.",
			"23": "<sup>23</sup> ನಾನು ಯಾರವನಾಗಿದ್ದೇನೋ ಯಾರನ್ನು ಸೇವಿಸುತ್ತೇನೋ ಆ ದೇವರಿಂದ ಬಂದ ಒಬ್ಬ ದೂತನು ಕಳೆದ ರಾತ್ರಿಯಲ್ಲಿ ನನ್ನ ಹತ್ತಿರ ನಿಂತು -",
			"24": "<sup>24</sup> ಪೌಲನೇ, ಭಯಪಡಬೇಡ, ನೀನು ಚಕ್ರವರ್ತಿಯ ಮುಂದೆ ನಿಲ್ಲಬೇಕು; ಇದಲ್ಲದೆ ನಿನ್ನ ಸಂಗಡ ಈ ಹಡಗಿನಲ್ಲಿ ಪ್ರಯಾಣಮಾಡುವವರೆಲ್ಲರ ಪ್ರಾಣ ದೇವರು ನಿನ್ನ ಮೇಲಣ ದಯೆಯಿಂದ ಉಳಿಸಿಕೊಟ್ಟಿದ್ದಾನೆಂದು ನನ್ನ ಸಂಗಡ ಹೇಳಿದನು.",
			"25": "<sup>25</sup> ಆದದರಿಂದ ಜನರೇ ಧೈರ್ಯವಾಗಿರ್ರಿ. ನನಗೆ ಹೇಳಲ್ಪಟ್ಟ ಪ್ರಕಾರವೇ ಆಗುವದೆಂದು ದೇವರನ್ನು ನಂಬುತ್ತೇನೆ.",
			"26": "<sup>26</sup> ಆದರೆ ನಾವು ಯಾವದೋ ಒಂದು ದ್ವೀಪದ ದಡವನ್ನು ತಾಕಬೇಕಾಗಿದೆ ಎಂದು ಹೇಳಿದನು.",
			"27": "<sup>27</sup> ಹದಿನಾಲ್ಕನೆಯ ರಾತ್ರಿ ಬಂದ ಮೇಲೆ ನಾವು ಆದ್ರಿಯ ಸಮುದ್ರದಲ್ಲಿ ಅತ್ತ ಇತ್ತ ಬಡಿಸಿಕೊಂಡು ಹೋಗುತ್ತಿರುವಾಗ ಸುಮಾರು ಮಧ್ಯರಾತ್ರಿಯಲ್ಲಿ ನಾವಿಕರು",
			"28": "<sup>28</sup> ಒಂದು ದೇಶದ ಹತ್ತಿರ ಬಂದೆವೆಂದು ನೆನಸಿ ಅಳತೇ ಗುಂಡನ್ನು ಇಳಿಸಿ ಇಪ್ಪತ್ತು ಮಾರುದ್ದವೆಂದು ಕಂಡರು. ಸ್ವಲ್ಪ ಹೊತ್ತಿನ ಮೇಲೆ ಅವರು ತಿರಿಗಿ ಅಳತೇ ಗುಂಡನ್ನು ಇಳಿಸಿ ನೋಡಲಾಗಿ ಹದಿನೈದು ಮಾರುದ್ದವೆಂದು ಕಂಡರು.",
			"29": "<sup>29</sup> ಬಂಡೇಸ್ಥಳವನ್ನು ತಾಕೇವೆಂದು ಭಯಪಟ್ಟು ಹಡಗಿನ ಹಿಂಭಾಗದಿಂದ ನಾಲ್ಕು ಲಂಗರಗಳನ್ನು ಬಿಟ್ಟು ಯಾವಾಗ ಬೆಳಗಾದೀತು ಎಂದು ಹಾರೈಸುತ್ತಿದ್ದರು.",
			"30": "<sup>30</sup> ಆದರೆ ನಾವಿಕರು ಮುಂಭಾಗದಲ್ಲಿ ಲಂಗರಗಳನ್ನು ಹಾಕಬೇಕೆಂಬುವ ನೆವದಿಂದ ದೋಣಿಯನ್ನು ಸಮುದ್ರದಲ್ಲಿ ಇಳಿಸಿ ಹಡಗನ್ನು ಬಿಟ್ಟು ತಪ್ಪಿಸಿಕೊಂಡು ಹೋಗುವದಕ್ಕೆ ಪ್ರಯತ್ನಿಸುತ್ತಿರುವಾಗ",
			"31": "<sup>31</sup> ಪೌಲನು ಶತಾಧಿಪತಿಗೂ ಸಿಪಾಯಿಗಳಿಗೂ - ಇವರು ಹಡಗಿನಲ್ಲಿ ನಿಲ್ಲದೆ ಹೋದರೆ ನೀವೂ ಉಳುಕೊಳ್ಳುವದಕ್ಕಾಗುವದಿಲ್ಲವೆಂದು ಹೇಳಿದನು.",
			"32": "<sup>32</sup> ಆಗ ಸಿಪಾಯಿಗಳು ದೋಣಿಯ ಹಗ್ಗಗಳನ್ನು ಕತ್ತರಿಸಿ ಅದು ಬಿದ್ದು ಹೋಗುವಂತೆ ಮಾಡಿದರು.",
			"33": "<sup>33</sup> ಬೆಳಗಾಗುತ್ತಿರುವಷ್ಟರಲ್ಲಿ ಪೌಲನು ಏನಾದರೂ ಆಹಾರವನ್ನು ತೆಗೆದುಕೊಳ್ಳಬೇಕೆಂದು ಎಲ್ಲರನ್ನು ಬೇಡಿಕೊಳ್ಳುವವನಾಗಿ - ನೀವು ಈಹೊತ್ತಿಗೆ ಹದಿನಾಲ್ಕು ದಿವಸದಿಂದ ಕಾದುಕೊಂಡು ಆಹಾರವೇನೂ ತೆಗೆದುಕೊಳ್ಳದೆ ಉಪವಾಸವಾಗಿಯೇ ಇದ್ದೀರಾದದರಿಂದ",
			"34": "<sup>34</sup> ಆಹಾರ ತೆಗೆದುಕೊಳ್ಳಬೇಕೆಂದು ನಿಮ್ಮನ್ನು ಬೇಡಿಕೊಳ್ಳುತ್ತೇನೆ. ಇದು ನಿಮ್ಮ ಪ್ರಾಣ ರಕ್ಷಣೆಗೆ ಅವಶ್ಯ. ನಿಮ್ಮಲ್ಲಿ ಯಾರ ತಲೆಯಿಂದಾದರೂ ಒಂದು ಕೂದಲೂ ಉದುರಿಹೋಗುವದಿಲ್ಲವೆಂದು ಹೇಳಿ",
			"35": "<sup>35</sup> ರೊಟ್ಟಿಯನ್ನು ತೆಗೆದುಕೊಂಡು ಎಲ್ಲರ ಮುಂದೆ ದೇವರ ಸ್ತೋತ್ರಮಾಡಿ ಅದನ್ನು ಮುರಿದು ತಿನ್ನುವದಕ್ಕೆ ಪ್ರಾರಂಭಿಸಿದನು.",
			"36": "<sup>36</sup> ಆಗ ಎಲ್ಲರು ಧೈರ್ಯತಂದುಕೊಂಡು ತಾವೂ ಆಹಾರವನ್ನು ತೆಗೆದುಕೊಂಡರು.",
			"37": "<sup>37</sup> ಆ ಹಡಗಿನಲ್ಲಿದ್ದ ನಾವೆಲ್ಲರು ಇನ್ನೂರ ಎಪ್ಪತ್ತಾರು ಮಂದಿ.",
			"38": "<sup>38</sup> ಸಾಕಾದಷ್ಟು ತಿಂದ ಮೇಲೆ ಗೋದಿಯನ್ನು ಸಮುದ್ರಕ್ಕೆ ಚೆಲ್ಲಿ ಹಡಗನ್ನು ಹಗುರ ಮಾಡಿದರು.",
			"39": "<sup>39</sup> ಬೆಳಗಾದ ಮೇಲೆ ಆ ದೇಶದ ಗುರುತನ್ನು ತಿಳಿಯದೆ ಉಸುಬಿನ ದಡವುಳ್ಳ ಒಂದು ಕೊಲ್ಲಿಯನ್ನು ನೋಡಿ ಆ ದಡದ ಮೇಲೆ ಹಡಗನ್ನು ನೂಕುವದಕ್ಕೆ ಸಾಧ್ಯವಾದೀತೆಂದು ಯೋಚಿಸಿ",
			"40": "<sup>40</sup> ಅವರು ಲಂಗರಗಳನ್ನು ಸರಿದು ಸಮುದ್ರದಲ್ಲೇ ಬಿಟ್ಟು ಚುಕ್ಕಾಣಿಗಳ ಕಟ್ಟುಗಳನ್ನು ಬಿಚ್ಚಿ ದೊಡ್ಡ ಹಾಯಿಯನ್ನು ಗಾಳಿಗೆ ಎತ್ತಿಕಟ್ಟಿ ಹಡಗನ್ನು ಆ ದಡಕ್ಕೆ ನಡಿಸುತ್ತಿದ್ದರು.",
			"41": "<sup>41</sup> ಆದರೆ ಮಧ್ಯದಲ್ಲಿ ಅವರಿಗೆ ಮರಳುದಿಬ್ಬ ಸಿಕ್ಕಿದಾಗ ಅದಕ್ಕೆ ನಾವೆಯನ್ನು ಹತ್ತಿಸಿದರು. ಮುಂಭಾಗವು ತಗಲಿಕೊಂಡು ಅಲ್ಲಾಡದೆ ನಿಂತಿತು. ಹಿಂಭಾಗವು ತೆರೆಗಳ ಹೊಡೆತದಿಂದ ಒಡೆದುಹೋಗುತ್ತಾ ಬಂತು.",
			"42": "<sup>42</sup> ಸೆರೆಯವರಲ್ಲಿ ಕೆಲವರು ಈಜಿ ತಪ್ಪಿಸಿಕೊಂಡಾರೆಂದು ಸಿಪಾಯಿಗಳು ಅವರನ್ನು ಕೊಲ್ಲಬೇಕೆಂಬದಾಗಿ ಆಲೋಚನೆ ಹೇಳಿದರು.",
			"43": "<sup>43</sup> ಆದರೆ ಶತಾಧಿಪತಿಯು ಪೌಲನನ್ನು ಉಳಿಸಬೇಕೆಂದು ಅಪೇಕ್ಷಿಸಿ ಅವರ ಆಲೋಚನೆಯನ್ನು ಬೇಡವೆಂದು - ಈಜಬಲ್ಲವರು ಹಡಗಿನಿಂದ ಧುಮುಕಿ ಮೊದಲಾಗಿ ತೀರಕ್ಕೆ ಹೋಗಬೇಕೆಂತಲೂ",
			"44": "<sup>44</sup> ಮಿಕ್ಕಾದವರಲ್ಲಿ ಕೆಲವರು ಹಲಿಗೆಗಳ ಮೇಲೆ ಕೆಲವರು ಹಡಗಿನ ತುಂಡುಗಳ ಮೇಲೆ ಹೋಗಬೇಕೆಂತಲೂ ಅಪ್ಪಣೆಕೊಟ್ಟನು. ಈ ರೀತಿಯಿಂದ ಎಲ್ಲರೂ ಸುರಕ್ಷಿತವಾಗಿ ತೀರಕ್ಕೆ ಸೇರಿದರು."
		},
		"28": {
			"1": "<sup>1</sup> ನಾವು ದಡಕ್ಕೆ ಸೇರಿದ ಮೇಲೆ ಆ ದ್ವೀಪದ ಹೆಸರು ಮೆಲೀತೆ ಎಂದು ನಮಗೆ ತಿಳಿದುಬಂತು.",
			"2": "<sup>2</sup> ಅನ್ಯಭಾಷೆಯವರಾದ ಆ ದ್ವೀಪದವರು ನಮಗೆ ಮಾಡಿದ ಉಪಕಾರವು ಅಷ್ಟಿಷ್ಟಲ್ಲ. ಮಳೆಯು ಆಗಲೇ ಹೊಯ್ಯುತ್ತಿದ್ದರಿಂದಲೂ ಚಳಿಯಾಗಿರುವದರಿಂದಲೂ ಅವರು ಬೆಂಕಿಯನ್ನು ಹೊತ್ತಿಸಿ ನಮ್ಮೆಲ್ಲರನ್ನು ಸೇರಿಸಿಕೊಂಡರು.",
			"3": "<sup>3</sup> ಪೌಲನು ಒಂದು ಹೊರೆ ಕಟ್ಟಿಗೆಯನ್ನು ಕೂಡಿಸಿ ಬೆಂಕಿಯ ಮೇಲೆ ಹಾಕಲು ಆ ಕಾವಿನ ದೆಸೆಯಿಂದ ಒಂದು ಹಾವು ಹೊರಗೆ ಬಂದು ಅವನ ಕೈಯನ್ನು ಬಿಗಿಯಾಗಿ ಹಿಡುಕೊಂಡಿತು.",
			"4": "<sup>4</sup> ಆ ಜಂತು ಅವನ ಕೈಯಿಂದ ಜೋತಾಡುವದನ್ನು ದ್ವೀಪದವರು ನೋಡಿ - ಈ ಮನುಷ್ಯನು ಕೊಲೆಪಾತಕನೇ ಸರಿ; ಸಮುದ್ರದಿಂದ ತಪ್ಪಿಸಿಕೊಂಡು ಬಂದರೂ ನ್ಯಾಯವು ಇವನನ್ನು ಬದುಕಗೊಡಿಸುವದಿಲ್ಲವೆಂದು ತಮ್ಮತಮ್ಮೊಳಗೆ ಮಾತಾಡಿಕೊಂಡರು.",
			"5": "<sup>5</sup> ಆದರೆ ಪೌಲನು ಆ ಜಂತುವನ್ನು ಬೆಂಕಿಯೊಳಕ್ಕೆ ಝಾಡಿಸಿಬಿಟ್ಟನು; ಅವನಿಗೆ ಏನೂ ಕೇಡಾಗಲಿಲ್ಲ.",
			"6": "<sup>6</sup> ಇವನು ಬಾತುಹೋದಾನು ಇಲ್ಲವೆ ಅಕಸ್ಮಾತ್ತಾಗಿ ಸತ್ತುಬಿದ್ದಾನು ಎಂದು ಅವರು ನೋಡಿಕೊಂಡೇ ಇದ್ದರು. ಬಹಳ ಹೊತ್ತು ನೋಡಿಕೊಂಡಿದ್ದ ಮೇಲೆ ಅವನಿಗೆ ವಿಕಾರವೇನೂ ಆಗಿಲ್ಲವೆಂದು ಕಂಡು ಬೇರೆ ಯೋಚನೆಯುಳ್ಳವರಾಗಿ - ಇವನು ಒಬ್ಬ ದೇವರು ಎಂದು ಹೇಳಿಕೊಂಡರು.",
			"7": "<sup>7</sup> ಆ ಸ್ಥಳದ ಸಮೀಪದಲ್ಲಿ ದ್ವೀಪದ ಮುಖ್ಯಸ್ಥನಾದ ಪೊಪ್ಲಿಯನೆಂಬವನಿಗೆ ಕೆಲವು ಭೂಮಿ ಇತ್ತು. ಅವನು ನಮ್ಮನ್ನು ಸೇರಿಸಿಕೊಂಡು ಮೂರು ದಿವಸ ಆದರದಿಂದ ಸತ್ಕರಿಸಿದನು.",
			"8": "<sup>8</sup> ಅವನ ತಂದೆಯು ಜ್ವರದಿಂದಲೂ ರಕ್ತಭೇದಿಯಿಂದಲೂ ಪೀಡಿತನಾಗಿ ಮಲಗಿದ್ದನು. ಪೌಲನು ಅವನ ಬಳಿಗೆ ಹೋಗಿ ದೇವರ ಪ್ರಾರ್ಥನೆ ಮಾಡಿ ಅವನ ಮೇಲೆ ಕೈಗಳನ್ನಿಟ್ಟು ಅವನನ್ನು ವಾಸಿಮಾಡಿದನು.",
			"9": "<sup>9</sup> ಇದಾದ ಮೇಲೆ ಆ ದ್ವೀಪದಲ್ಲಿದ್ದ ಮಿಕ್ಕಾದ ರೋಗಿಗಳು ಅವನ ಬಳಿಗೆ ಬಂದು ಸ್ವಸ್ಥರಾದರು.",
			"10": "<sup>10</sup> ಅವರು ನಮ್ಮನ್ನು ಬಹಳವಾಗಿ ಸನ್ಮಾನಿಸಿದ್ದಲ್ಲದೆ ನಾವು ಬಿಟ್ಟು ಹೊರಟಾಗ ನಮಗೆ ಅವಶ್ಯವಾದ ಪದಾರ್ಥಗಳನ್ನು ತಂದು ಹಡಗಿನಲ್ಲಿಟ್ಟರು.",
			"11": "<sup>11</sup> ಮೂರು ತಿಂಗಳಾದ ಮೇಲೆ ಅಲೆಕ್ಸಾಂದ್ರಿಯದಿಂದ ಬಂದು ಆ ದ್ವೀಪದಲ್ಲಿ ಹಿಮಕಾಲವನ್ನು ಕಳೆದಿದ್ದ ಒಂದು ಹಡಗನ್ನು ಹತ್ತಿ ಹೊರಟೆವು. ಆ ಹಡಗಿಗೆ ಅಶ್ವಿನೀದೇವತೆಗಳೆಂಬ ಚಿಹ್ನೆ.",
			"12": "<sup>12</sup> ನಾವು ಸುರಕೂಸಿಗೆ ಮುಟ್ಟಿ ಅಲ್ಲಿ ಮೂರು ದಿವಸ ನಿಂತೆವು.",
			"13": "<sup>13</sup> ಅಲ್ಲಿಂದ ನಾವು ಸುತ್ತಿಕೊಂಡು ಹೋಗಿ ರೇಗಿಯಕ್ಕೆ ಸೇರಿದೆವು. ಒಂದು ದಿನವಾದ ಮೇಲೆ ತೆಂಕಣಗಾಳಿ ಹುಟ್ಟಿದ್ದರಿಂದ ಎರಡನೆಯ ದಿನದಲ್ಲಿ ಪೊತಿಯೋಲಕ್ಕೆ ಬಂದೆವು.",
			"14": "<sup>14</sup> ಅಲ್ಲಿ ಕ್ರೈಸ್ತ ಸಹೋದರರು ಸಿಕ್ಕಿದರು. ಅವರು ನಮ್ಮನ್ನು ತಮ್ಮ ಬಳಿಯಲ್ಲಿ ಏಳು ದಿವಸ ಇರಬೇಕೆಂದು ಬೇಡಿಕೊಂಡರು. ತರುವಾಯ ರೋಮಾಪುರಕ್ಕೆ ಬಂದೆವು.",
			"15": "<sup>15</sup> ಅಲ್ಲಿದ್ದ ಸಹೋದರರು ನಮ್ಮ ಸಮಾಚಾರವನ್ನು ಕೇಳಿದಾಗ ನಮ್ಮನ್ನು ಎದುರುಗೊಳ್ಳುವದಕ್ಕಾಗಿ ಕೆಲವರು ಅಪ್ಪಿಯಪೇಟೆಯವರೆಗೂ ಕೆಲವರು ತ್ರಿಛತ್ರವೆಂಬ ಸ್ಥಳದವರೆಗೂ ಬಂದರು. ಪೌಲನು ಅವರನ್ನು ನೋಡಿ ದೇವರ ಸ್ತೋತ್ರವನ್ನು ಮಾಡಿ ಧೈರ್ಯಗೊಂಡನು.",
			"16": "<sup>16</sup> ನಾವು ರೋಮಾಪುರಕ್ಕೆ ಬಂದ ಮೇಲೆ ಪೌಲನು ತನ್ನನ್ನು ಕಾಯುತ್ತಿದ್ದ ಸಿಪಾಯಿಯೊಂದಿಗೆ ಪ್ರತ್ಯೇಕವಾಗಿರಬಹುದೆಂಬ ಅಪ್ಪಣೆಯನ್ನು ಹೊಂದಿದನು.",
			"17": "<sup>17</sup> ಮೂರು ದಿವಸಗಳಾದ ಮೇಲೆ ಪೌಲನು ಯೆಹೂದ್ಯರಲ್ಲಿ ಪ್ರಮುಖರನ್ನು ತನ್ನ ಬಳಿಗೆ ಕರೆಯಿಸಿದನು. ಅವರು ಕೂಡಿದ ಮೇಲೆ ಅವನು ಅವರಿಗೆ - ಸಹೋದರರೇ, ನಾನು ನಮ್ಮ ಜನರಿಗೂ ನಮ್ಮ ಪಿತೃಗಳ ಆಚಾರಗಳಿಗೂ ವಿರುದ್ಧವಾಗಿ ಏನೂ ಮಾಡದವನಾದರೂ ಯೆರೂಸಲೇಮಿನಿಂದ ರೋಮಾಯರ ಕೈಗೆ ಸೆರೆಯವನಾಗಿ ಒಪ್ಪಿಸಲ್ಪಟ್ಟೆನು.",
			"18": "<sup>18</sup> ಅವರು ನನ್ನನ್ನು ವಿಚಾರಣೆಮಾಡಿ ನನ್ನಲ್ಲಿ ಮರಣದಂಡನೆಗೆ ಕಾರಣವೇನೂ ಇಲ್ಲದ್ದರಿಂದ ನನ್ನನ್ನು ಬಿಡಿಸಬೇಕೆಂದಿದ್ದರು.",
			"19": "<sup>19</sup> ಅದಕ್ಕೆ ಯೆಹೂದ್ಯರು ಅಡ್ಡಮಾತಾಡಿದ್ದರಿಂದ ನಾನು ಚಕ್ರವರ್ತಿಯ ಎದುರಿನಲ್ಲಿ ಹೇಳಿಕೊಳ್ಳುತ್ತೇನೆಂದು ಹೇಳಬೇಕಾಗಿ ಬಂತು. ನನ್ನ ಸ್ವದೇಶದವರ ಮೇಲೆ ದೋಷಾರೋಪಣೆ ಮಾಡಬೇಕೆಂಬ ಅಭಿಪ್ರಾಯದಿಂದ ಅದನ್ನು ಹೇಳಲಿಲ್ಲ.",
			"20": "<sup>20</sup> ಈ ಕಾರಣದಿಂದ ನಾನು ನಿಮ್ಮನ್ನು ಕಂಡು ಮಾತಾಡಬೇಕೆಂದು ಕರೆಯಿಸಿದೆನು. ಇಸ್ರಾಯೇಲ್ ಜನರ ನಿರೀಕ್ಷೆಯ ನಿಮಿತ್ತವಾಗಿ ಈ ಬೇಡಿಯಿಂದ ಕಟ್ಟಲ್ಪಟ್ಟಿದ್ದೇನೆ ಎಂದು ಹೇಳಿದನು.",
			"21": "<sup>21</sup> ಅವರು ಅವನಿಗೆ - ನಿನ್ನ ವಿಷಯವಾಗಿ ನಮಗೆ ಯೂದಾಯದಿಂದ ಕಾಗದಗಳು ಬರಲಿಲ್ಲ. ಸಹೋದರರಲ್ಲಿ ಒಬ್ಬರೂ ಬಂದು ನಿನ್ನ ವಿಷಯವಾಗಿ ಕೆಟ್ಟದ್ದನ್ನು ತಿಳಿಸಲೂ ಇಲ್ಲ, ಮಾತಾಡಲೂ ಇಲ್ಲ.",
			"22": "<sup>22</sup> ಆದರೆ ನಿನ್ನ ಅಭಿಪ್ರಾಯವನ್ನು ನಿನ್ನಿಂದಲೇ ಕೇಳುವದು ನಮಗೆ ಯುಕ್ತವೆಂದು ತೋರುತ್ತದೆ. ಆ ಮತದ ವಿಷಯದಲ್ಲಿ ಜನರು ಎಲ್ಲೆಲ್ಲಿಯೂ ವಿರುದ್ಧವಾಗಿ ಮಾತಾಡುತ್ತಾರೆಂಬದೊಂದೇ ನಮಗೆ ಗೊತ್ತದೆ ಅಂದರು.",
			"23": "<sup>23</sup> ಅವರು ಅವನಿಗೆ ಒಂದು ದಿವಸವನ್ನು ಗೊತ್ತುಮಾಡಲು ಬಹುಮಂದಿ ಅವನ ಬಿಡಾರದಲ್ಲಿ ಅವನ ಬಳಿಗೆ ಬಂದರು. ಅವನು ಬೆಳಗಿನಿಂದ ಸಾಯಂಕಾಲದವರೆಗೂ ದೇವರ ರಾಜ್ಯವನ್ನು ಕುರಿತು ಪ್ರಮಾಣವಾಗಿ ಸಾಕ್ಷಿಹೇಳುತ್ತಾ ಮೋಶೆಯ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನೂ ಪ್ರವಾದಿಗಳ ಗ್ರಂಥಗಳನ್ನೂ ಆಧಾರಮಾಡಿಕೊಂಡು ಯೇಸುವಿನ ವಿಷಯದಲ್ಲಿ ಅವರನ್ನು ಒಡಂಬಡಿಸುತ್ತಾ ಇದ್ದನು.",
			"24": "<sup>24</sup> ಅವನು ಹೇಳಿದ ಮಾತುಗಳಿಗೆ ಕೆಲವರು ಒಪ್ಪಿಕೊಂಡರು; ಕೆಲವರು ನಂಬದೆ ಹೋದರು.",
			"25": "<sup>25</sup> ಅವರಲ್ಲಿ ಐಕಮತ್ಯವಿಲ್ಲದೆ ಇರುವಾಗ ಪೌಲನು ಅವರಿಗೆ - ಪವಿತ್ರಾತ್ಮನು ಪ್ರವಾದಿಯಾದ ಯೆಶಾಯನ ಬಾಯಿಂದ",
			"26": "<sup>26</sup> ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ವಿಹಿತವಾಗಿ ಹೇಳಿದ್ದೇನಂದರೆ - ನೀನು ಈ ಜನರ ಬಳಿಗೆ ಹೋಗಿ ಅವರಿಗೆ - ನೀವು ಕಿವಿಯಿದ್ದು ಕೇಳಿದರೂ ತಿಳುಕೊಳ್ಳುವದೇ ಇಲ್ಲ; ಕಣ್ಣಿದ್ದು ನೋಡಿದರೂ ಕಾಣುವದೇ ಇಲ್ಲ ಎಂಬದಾಗಿ ಹೇಳು.",
			"27": "<sup>27</sup> ಯಾಕಂದರೆ ಈ ಜನರ ಹೃದಯವು ಕೊಬ್ಬಿತು; ಇವರ ಕಿವಿ ಮಂದವಾಯಿತು; ಇವರು ಕಣ್ಣು ಮುಚ್ಚಿಕೊಂಡಿದ್ದಾರೆ; ತಾವು ಕಣ್ಣಿನಿಂದ ಕಂಡು ಕಿವಿಯಿಂದ ಕೇಳಿ ಹೃದಯದಿಂದ ತಿಳಿದು ನನ್ನ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡು ನನ್ನಿಂದ ಸ್ವಸ್ಥತೆಯನ್ನು ಹೇಗೂ ಹೊಂದಬಾರದೆಂದು ಮಾಡಿಕೊಂಡಿದ್ದಾರೆ ಎಂಬದೇ.",
			"28": "<sup>28</sup> ಆದಕಾರಣ ದೇವರಿಂದಾದ ಈ ರಕ್ಷಣೆಯು ಅನ್ಯಜನರಿಗೆ ಹೇಳಿಕಳುಹಿಸಲ್ಪಟ್ಟಿದೆ ಎಂದು ನಿಮಗೆ ಚೆನ್ನಾಗಿ ತಿಳಿದಿರಲಿ; ಅವರಾದರೂ ಕೇಳುವರು ಎಂದು ಹೇಳಿದನು.",
			"29": "<sup>29</sup> ಅವನು ಈ ಮಾತನ್ನು ಹೇಳಿದ ಮೇಲೆ ಅವರು ಹೊರಟುಹೋದರು.",
			"30": "<sup>30</sup> ತರುವಾಯ ಅವನು ತಾನೇ ಮಾಡಿಕೊಂಡ ಬಾಡಿಗೆಯ ಮನೆಯಲ್ಲಿ ಪೂರಾ ಎರಡು ವರುಷ ಇದ್ದು ತನ್ನ ಬಳಿಗೆ ಬರುವವರೆಲ್ಲರನ್ನು ಸೇರಿಸಿಕೊಂಡು",
			"31": "<sup>31</sup> ಯಾವ ಅಡ್ಡಿಯೂ ಇಲ್ಲದೆ ತುಂಬಾ ಧೈರ್ಯದಿಂದ ದೇವರ ರಾಜ್ಯವನ್ನು ಪ್ರಸಿದ್ಧಿಪಡಿಸುತ್ತಾ ಕರ್ತನಾದ ಯೇಸು ಕ್ರಿಸ್ತನ ವಿಷಯವಾಗಿ ಉಪದೇಶಮಾಡುತ್ತಾ ಇದ್ದನು."
		}
	}
};
module.exports = book;