var book = {
	"name": "ರೋಮಾಪುರದವರಿಗೆ",
	"numChapters": 16,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ಯೇಸು ಕ್ರಿಸ್ತನ ದಾಸನೂ ಅಪೊಸ್ತಲನಾಗುವದಕ್ಕೆ ಕರೆಯಲ್ಪಟ್ಟವನೂ ದೇವರ ಸುವಾರ್ತೆಯನ್ನು ಸಾರುವದಕ್ಕೆ ನೇಮಿಸಲ್ಪಟ್ಟವನೂ ಆಗಿರುವ ಪೌಲನು",
			"2": "<sup>2</sup> ಸುವಾರ್ತೆಯನ್ನು ಸಾರಿ ಹೇಳಿಸುತ್ತೇನೆಂದು ದೇವರು ಮುಂಚಿತವಾಗಿ ಪವಿತ್ರಶಾಸ್ತ್ರಗಳಲ್ಲಿ ತನ್ನ ಪ್ರವಾದಿಗಳ ಮೂಲಕ ಮಾತುಕೊಟ್ಟನು.",
			"3": "<sup>3</sup> ಈ ಸುವಾರ್ತೆಯು ದೇವರ ಮಗನೂ ನಮ್ಮ ಕರ್ತನೂ ಆಗಿರುವ ಯೇಸು ಕ್ರಿಸ್ತನ ವಿಷಯವಾದದ್ದು. ಆತನು ವಂಶಕ್ರಮದಿಂದ ದಾವೀದನ ಸಂತಾನದಲ್ಲಿ ಹುಟ್ಟಿದವನೂ,",
			"4": "<sup>4</sup> ಪವಿತ್ರವಾದ ಆತ್ಮವುಳ್ಳವನಾಗಿದ್ದು ಸತ್ತಮೇಲೆ ಜೀವಿತನಾಗಿ ಎದ್ದು ಬಂದ ಮಹತ್ಕಾರ್ಯದಿಂದ ದೇವಕುಮಾರನೆಂದು ನಿರ್ಣಯಿಸಲ್ಪಟ್ಟವನೂ ಆಗಿದ್ದಾನೆ.",
			"5": "<sup>5</sup> ಆತನ ಹೆಸರಿನ ಪ್ರಸಿದ್ಧಿಗಾಗಿ ಎಲ್ಲಾ ಅನ್ಯಜನಗಳಲ್ಲಿ ನಂಬಿಕೆಯೆಂಬ ವಿಧೇಯತ್ವ ಉಂಟಾಗುವದಕ್ಕೋಸ್ಕರ ನಾವು ಆತನ ಮೂಲಕವಾಗಿ ಕೃಪೆಯನ್ನೂ ಅಪೊಸ್ತಲತನವನ್ನೂ ಹೊಂದಿದೆವು.",
			"6": "<sup>6</sup> ಯೇಸು ಕ್ರಿಸ್ತನವರಾಗುವದಕ್ಕೆ ಕರೆಯಲ್ಪಟ್ಟ ನೀವು ಸಹ ಆ ಅನ್ಯಜನರೊಳಗಿನವರಾದ್ದರಿಂದ ನಿಮಗೆ ಬರೆಯುತ್ತೇನೆ.",
			"7": "<sup>7</sup> ರೋಮಾಪುರದಲ್ಲಿ ದೇವರಿಗೆ ಪ್ರಿಯರೂ ದೇವಜನರಾಗುವದಕ್ಕೆ ಕರೆಯಲ್ಪಟ್ಟವರೂ ಆಗಿರುವವರೆಲ್ಲರಿಗೆ ಬರೆಯುವದೇನಂದರೆ - ನಮ್ಮ ತಂದೆಯಾಗಿರುವ ದೇವರಿಂದಲೂ ಕರ್ತನಾದ ಯೇಸು ಕ್ರಿಸ್ತನಿಂದಲೂ ನಿಮಗೆ ಕೃಪೆಯೂ ಶಾಂತಿಯೂ ಆಗಲಿ.",
			"8": "<sup>8</sup> ಮೊದಲನೇದು, ನಿಮ್ಮ ನಂಬಿಕೆಯು ಲೋಕದಲ್ಲೆಲ್ಲಾ ಪ್ರಸಿದ್ಧಿಗೆ ಬಂದದ್ದರಿಂದ ನಿಮ್ಮೆಲ್ಲರ ವಿಷಯವಾಗಿ ಯೇಸು ಕ್ರಿಸ್ತನ ಮೂಲಕ ನನ್ನ ದೇವರಿಗೆ ಸ್ತೋತ್ರ ಮಾಡುತ್ತೇನೆ.",
			"9": "<sup>9</sup> ನಾನು ಪ್ರಾರ್ಥನೆ ಮಾಡುವಾಗೆಲ್ಲಾ ತಪ್ಪದೆ ನಿಮಗೋಸ್ಕರ ವಿಜ್ಞಾಪನೆಮಾಡಿ ಇನ್ನು ಮೇಲೆಯಾದರೂ ನಿಮ್ಮ ಬಳಿಗೆ ಬರುವದಕ್ಕೆ ದೇವರ ಚಿತ್ತದಿಂದ ನನಗೆ ಹೇಗೂ ಅನುಕೂಲವಾಗಬೇಕೆಂದು ಬೇಡಿಕೊಳ್ಳುತ್ತೇನೆ; ಇದಕ್ಕೆ ದೇವರೇ ನನ್ನ ಸಾಕ್ಷಿ; ಆತನ ಮಗನ ಸುವಾರ್ತೆಯನ್ನು ಸಾರುತ್ತಾ ಆತನನ್ನೇ ನನ್ನ ಆತ್ಮದಲ್ಲಿ ಆರಾಧಿಸುವವನಾಗಿದ್ದೇನೆ.",
			"11": "<sup>11</sup> ನನ್ನ ಮುಖಾಂತರವಾಗಿ ನಿಮಗೆ ಪಾರಮಾರ್ಥಿಕ ವರವೇನಾದರೂ ದೊರಕಿ ನೀವು ದೃಢವಾಗುವದಕ್ಕೋಸ್ಕರ ಅಂದರೆ ನಾನು ನಿಮ್ಮ ನಂಬಿಕೆಯಿಂದ ನೀವು ನನ್ನ ನಂಬಿಕೆಯಿಂದ ಸಹಾಯಹೊಂದಿ ಈ ಪ್ರಕಾರ ನಿಮ್ಮೊಂದಿಗೆ ನಾನು ಧೈರ್ಯಗೊಳ್ಳುವದಕ್ಕೋಸ್ಕರ ನಿಮ್ಮನ್ನು ನೋಡಬೇಕೆಂದು ಅಪೇಕ್ಷಿಸುತ್ತೇನೆ.",
			"13": "<sup>13</sup> ಸಹೋದರರೇ, ನನ್ನ ಕೆಲಸವು ಇತರ ಜನಗಳಲ್ಲಿ ಸಫಲವಾದಂತೆ ನಿಮ್ಮಲ್ಲಿಯೂ ಸಫಲವಾದೀತೆಂದು ನಿಮ್ಮ ಬಳಿಗೆ ಬರುವದಕ್ಕೆ ಅನೇಕಾವರ್ತಿ ಮನಸ್ಸು ಮಾಡಿಕೊಂಡಿದ್ದಾಗ್ಯೂ ಇಂದಿನವರೆಗೂ ಅಡ್ಡಿಯಾಯಿತೆಂದು ನಿಮಗೆ ತಿಳಿದಿರಬೇಕೆಂದು ನನ್ನ ಅಪೇಕ್ಷೆ.",
			"14": "<sup>14</sup> ಗ್ರೀಕರಿಗೂ ಇತರ ಜನಗಳಿಗೂ ಜ್ಞಾನಿಗಳಿಗೂ ಮೂಢರಿಗೂ ತೀರಿಸಬೇಕಾದ ಒಂದು ಋಣ ನನ್ನ ಮೇಲೆ ಅದೆ.",
			"15": "<sup>15</sup> ಹೀಗಿರುವದರಿಂದ ರೋಮಾಪುರದಲ್ಲಿರುವ ನಿಮಗೆ ಸಹ ಸುವಾರ್ತೆಯನ್ನು ಸಾರುವದಕ್ಕೆ ನಾನಂತೂ ಸಿದ್ಧವಾಗಿದ್ದೇನೆ. ಸುವಾರ್ತೆಯ ವಿಷಯದಲ್ಲಿ ನಾನು ನಾಚಿಕೊಳ್ಳುವವನಲ್ಲ.",
			"16": "<sup>16</sup> ಆ ಸುವಾರ್ತೆಯು ದೇವರ ಬಲಸ್ವರೂಪವಾಗಿದ್ದು ಮೊದಲು ಯೆಹೂದ್ಯರಿಗೆ ಆಮೇಲೆ ಗ್ರೀಕರಿಗೆ ಅಂತೂ ನಂಬುವವರೆಲ್ಲರಿಗೂ ರಕ್ಷಣೆ ಉಂಟುಮಾಡುವಂಥದಾಗಿದೆ.",
			"17": "<sup>17</sup> ಹೇಗಂದರೆ ದೇವರಿಂದ ದೊರಕುವ ನೀತಿಯು ಅದರಲ್ಲಿ ತೋರಿಬರುತ್ತದೆ. ನೀತಿವಂತನು ನಂಬಿಕೆಯಿಂದಲೇ ಬದುಕುವನೆಂಬ ಶಾಸ್ತ್ರೋಕ್ತಿಯ ಪ್ರಕಾರ ಆ ನೀತಿಯು ನಂಬಿಕೆಯ ಫಲವಾಗಿದ್ದು ನಂಬಿಕೆಯನ್ನು ಪ್ರಕಟಿಸುವಂಥದಾಗಿದೆ.",
			"18": "<sup>18</sup> ದುಷ್ಟತನವನ್ನು ನಡಿಸಿ ಸತ್ಯವನ್ನು ಅಣಗಿಸುವವರಾದ ಮನುಷ್ಯರ ಎಲ್ಲಾ ವಿಧವಾದ ಭಕ್ತಿಹೀನತೆಯ ಮೇಲೆಯೂ ದುಷ್ಟತನದ ಮೇಲೆಯೂ ದೇವರ ಕೋಪವು ಪರಲೋಕದಿಂದ ತೋರಿಬರುತ್ತದೆ.",
			"19": "<sup>19</sup> ಯಾಕಂದರೆ ದೇವರ ವಿಷಯವಾಗಿ ತಿಳಿಯಬಹುದಾದದ್ದು ಅವರ ಮನಸ್ಸಿಗೆ ಸ್ಪಷ್ಟವಾಗಿ ತಿಳಿದದೆ; ಅದನ್ನು ಅವರಿಗೆ ದೇವರೇ ಸ್ಪಷ್ಟವಾಗಿ ತಿಳಿಸಿದನು.",
			"20": "<sup>20</sup> ಹೇಗಂದರೆ ಕಣ್ಣಿಗೆ ಕಾಣದಿರುವ ಆತನ ಗುಣಲಕ್ಷಣಗಳು ಅಂದರೆ ಆತನ ನಿತ್ಯಶಕ್ತಿಯೂ ದೇವತ್ವವೂ ಜಗದುತ್ಪತ್ತಿ ಮೊದಲುಗೊಂಡು ಆತನು ಮಾಡಿದ ಸೃಷ್ಟಿಗಳ ಮೂಲಕ ಬುದ್ಧಿಗೆ ಗೊತ್ತಾಗಿ ಕಾಣಬರುತ್ತವೆ; ಹೀಗಿರುವದರಿಂದ ಅವರು ಉತ್ತರವಿಲ್ಲದವರಾಗಿದ್ದಾರೆ.",
			"21": "<sup>21</sup> ಯಾಕಂದರೆ ದೇವರ ವಿಷಯವಾಗಿ ಅವರಿಗೆ ತಿಳುವಳಿಕೆಯಿದ್ದರೂ ಅವರು ಆತನನ್ನು ದೇವರೆಂದು ಘನಪಡಿಸಲಿಲ್ಲ; ಆತನ ಉಪಕಾರಗಳನ್ನು ನೆನಸಿ ಆತನನ್ನು ಸ್ತುತಿಸಲಿಲ್ಲ. ಅವರು ವಿಚಾರ ಮಾಡಿಮಾಡಿ ಫಲ ಕಾಣಲಿಲ್ಲ; ವಿವೇಕವಿಲ್ಲದ ಅವರ ಮನಸ್ಸು ಕತ್ತಲಾಯಿತು.",
			"22": "<sup>22</sup> ತಾವು ಜ್ಞಾನಿಗಳೆಂದು ಹೇಳಿಕೊಂಡು ಹುಚ್ಚರಾದರು.",
			"23": "<sup>23</sup> ಲಯವಿಲ್ಲದ ದೇವರ ಮಹಿಮೆಯನ್ನು ಬಿಟ್ಟು ಅದಕ್ಕೆ ಬದಲಾಗಿ ಲಯವಾಗುವ ಮನುಷ್ಯ ಪಶು ಪಕ್ಷಿ ಸರ್ಪಾದಿಗಳ ಮೂರ್ತಿಯನ್ನು ಮಾಡಿಕೊಂಡರು.",
			"24": "<sup>24</sup> ಆದಕಾರಣ ಅವರು ಮನಸ್ಸಿನ ದುರಾಶೆಗಳಂತೆ ನಡೆದು ತಮ್ಮ ದೇಹಗಳನ್ನು ಮಾನಹೀನಮಾಡಿಕೊಳ್ಳಲಿ ಎಂದು ದೇವರು ಅವರನ್ನು ಹೊಲಸಾದ ನಡತೆಗೆ ಒಪ್ಪಿಸಿದನು.",
			"25": "<sup>25</sup> ಅವರು ಸತ್ಯದೇವರನ್ನು ಬಿಟ್ಟು ಅಸತ್ಯವಾದದ್ದನ್ನು ಹಿಡಿದುಕೊಂಡು ಸೃಷ್ಟಿಕರ್ತನನ್ನು ಪೂಜಿಸದೆ ಸೃಷ್ಟಿವಸ್ತುಗಳನ್ನೇ ಪೂಜಿಸಿ ಸೇವಿಸುವವರಾದರು. ಸೃಷ್ಟಿಕರ್ತನೊಬ್ಬನೇ ನಿರಂತರ ಸ್ತುತಿಹೊಂದತಕ್ಕವನು. ಆಮೆನ್.",
			"26": "<sup>26</sup> ಅವರು ಇಂಥದನ್ನು ಮಾಡಿದ್ದರಿಂದ ದೇವರು ಅವರನ್ನು ಕೇವಲ ತುಚ್ಫವಾದ ಕಾಮಾಭಿಲಾಷೆಗೆ ಒಪ್ಪಿಸಿದನು. ಹೇಗಂದರೆ ಅವರ ಹೆಂಗಸರು ಸ್ವಾಭಾವಿಕವಾದ ಭೋಗವನ್ನು ಬಿಟ್ಟು ಸ್ವಭಾವಕ್ಕೆ ವಿರುದ್ಧವಾದ ಭೋಗವನ್ನು ಅನುಸರಿಸಿದರು.",
			"27": "<sup>27</sup> ಅದರಂತೆ ಗಂಡಸರು ಸಹ ಸ್ವಾಭಾವಿಕವಾದ ಸ್ತ್ರೀಭೋಗವನ್ನು ಬಿಟ್ಟು ಒಬ್ಬರ ಮೇಲೊಬ್ಬರು ತಮ್ಮ ಕಾಮಾತುರದಿಂದ ತಾಪಪಡುತ್ತಾ ಗಂಡಸರ ಸಂಗಡ ಗಂಡಸರು ಕೇವಲ ಅವಲಕ್ಷಣವಾದದ್ದನ್ನು ನಡಿಸಿ ತಮ್ಮ ಭ್ರಾಂತಿಗೆ ತಕ್ಕ ಫಲವನ್ನು ತಮ್ಮಲ್ಲಿ ಹೊಂದುವವರಾದರು.",
			"28": "<sup>28</sup> ಇದಲ್ಲದೆ ದೇವರ ಜ್ಞಾನವು ಅವರಿಗೆ ಇಷ್ಟವಿಲ್ಲದ್ದರಿಂದ ಅಲ್ಲದ ಕೃತ್ಯಗಳನ್ನು ನಡಿಸುವವರಾಗುವಂತೆ ದೇವರು ಅವರನ್ನು ಅನಿಷ್ಟಭಾವಕ್ಕೆ ಒಪ್ಪಿಸಿದನು.",
			"29": "<sup>29</sup> ಹೇಗಂದರೆ ಅವರು ಸಕಲವಿಧವಾದ ಅನ್ಯಾಯ ದುರ್ಮಾರ್ಗತನ ಲೋಲುಪ್ತಿ ದುಷ್ಟತ್ವಗಳಿಂದಲೂ ಹೊಟ್ಟೇಕಿಚ್ಚು ಕೊಲೆ ಜಗಳ ಮೋಸ ಹಗೆತನಗಳಿಂದಲೂ ತುಂಬಿದವರಾದರು.",
			"30": "<sup>30</sup> ಅವರು ಕಿವಿ ಊದುವವರೂ ಚಾಡಿಹೇಳುವವರೂ ದೇವರನ್ನು ದ್ವೇಷಿಸುವವರೂ ಸೊಕ್ಕಿನವರೂ ಅಹಂಕಾರಿಗಳೂ ಬಡಾಯಿಕೊಚ್ಚುವವರೂ ಕೇಡನ್ನು ಕಲ್ಪಿಸುವವರೂ",
			"31": "<sup>31</sup> ತಂದೆತಾಯಿಗಳ ಮಾತನ್ನು ಕೇಳದವರೂ ವಿವೇಕವಿಲ್ಲದವರೂ ಮಾತಿಗೆ ತಪ್ಪುವವರೂ ಮಮತೆಯಿಲ್ಲದವರೂ ಕರುಣೆಯಿಲ್ಲದವರೂ ಆದರು.",
			"32": "<sup>32</sup> ಇಂಥವುಗಳನ್ನು ನಡಿಸುವವರು ಮರಣಕ್ಕೆ ಪಾತ್ರರಾಗಿದ್ದಾರೆಂಬ ದೇವವಿಧಿಯು ಅವರಿಗೆ ತಿಳಿದಿದ್ದರೂ ಅವರು ತಾವೇ ಅವುಗಳನ್ನು ಮಾಡುವದಲ್ಲದೆ ಮಾಡುವವರನ್ನೂ ಅನುಮೋದಿಸುತ್ತಾರೆ."
		},
		"2": {
			"1": "<sup>1</sup> ಆದದರಿಂದ ಎಲೈ ಮನುಷ್ಯನೇ, ಮತ್ತೊಬ್ಬರಲ್ಲಿ ದೋಷವೆಣಿಸುವ ನೀನು ಯಾವನಾದರೂ ಸರಿಯೇ, ಉತ್ತರ ಹೇಳುವದಕ್ಕೆ ನಿನಗೆ ಮಾರ್ಗವಿಲ್ಲ. ಹೇಗಂದರೆ ಮತ್ತೊಬ್ಬರಲ್ಲಿ ದೋಷವೆಣಿಸುವದರಿಂದ ನಿನ್ನನ್ನು ನೀನೇ ದೋಷಿಯೆಂದು ತೀರ್ಪುಮಾಡಿಕೊಂಡ ಹಾಗಾಯಿತು. ಮತ್ತೊಬ್ಬರಲ್ಲಿ ದೋಷವೆಣಿಸುವ ನೀನು ಆ ದೋಷಗಳನ್ನೇ ನಡಿಸುತ್ತೀಯಲ್ಲಾ.",
			"2": "<sup>2</sup> ಆದರೆ ಅಂಥವುಗಳನ್ನು ನಡಿಸುವವರ ವಿಷಯದಲ್ಲಿ ದೇವರು ಮಾಡುವ ತೀರ್ಪು ಸತ್ಯಕ್ಕನುಸಾರವಾಗಿಯೇ ಇರುವದೆಂದು ಬಲ್ಲೆವು.",
			"3": "<sup>3</sup> ಎಲೈ ಮನುಷ್ಯನೇ, ಅಂಥವುಗಳನ್ನು ನಡಿಸುವವರಲ್ಲಿ ದೋಷವೆಣಿಸಿ ಅವುಗಳನ್ನೇ ಮಾಡುತ್ತಿರುವ ನೀನು ದೇವರ ದಂಡನೆಯ ತೀರ್ಪಿಗೆ ತಪ್ಪಿಸಿಕೊಂಡೇನೆಂದು ಯೋಚಿಸುತ್ತೀಯಾ?",
			"4": "<sup>4</sup> ಆತನ ಅಪಾರವಾದ ದಯಾ ಸಹನ ದೀರ್ಘಶಾಂತಿಗಳನ್ನು ಅಸಡ್ಡೆಮಾಡುತ್ತೀಯಾ? ಮನಸ್ಸನ್ನು ಬೇರೆಮಾಡಿಕೊಳ್ಳುವದಕ್ಕೆ ದೇವರ ಉಪಕಾರವು ನಿನ್ನನ್ನು ಪ್ರೇರಿಸುತ್ತದೆಂಬದು ನಿನಗೆ ಗೊತ್ತಿಲ್ಲವೇ?",
			"5": "<sup>5</sup> ನೀನು ನಿನ್ನ ಮೊಂಡತನವನ್ನೂ ಪಶ್ಚಾತ್ತಾಪವಿಲ್ಲದ ಮನಸ್ಸನ್ನೂ ಅನುಸರಿಸುವದರಿಂದ ನಿನಗೋಸ್ಕರ ದೇವರ ಕೋಪವನ್ನು ಕೂಡಿಟ್ಟುಕೊಳ್ಳುತ್ತಾ ಇದ್ದೀ. ದೇವರ ಕೋಪವೂ ನ್ಯಾಯವಾದ ತೀರ್ಪು ಪ್ರಕಟವಾಗುವ ದಿವಸ ಬರುತ್ತದಷ್ಟೆ.",
			"6": "<sup>6</sup> ಆತನು ಪ್ರತಿಯೊಬ್ಬನಿಗೆ ಅವನವನ ಕೃತ್ಯಗಳಿಗೆ ತಕ್ಕ ಪ್ರತಿಫಲವನ್ನು ಕೊಡುವನು.",
			"7": "<sup>7</sup> ಯಾರು ಪ್ರಭಾವ ಮಾನ ನಿರ್ಲಯತ್ವಗಳನ್ನು ಹೊಂದಬೇಕೆಂದು ಒಳ್ಳೇದನ್ನು ಬೇಸರಗೊಳ್ಳದೆ ಮಾಡುತ್ತಾರೋ ಅವರಿಗೆ ನಿತ್ಯಜೀವವನ್ನು ಕೊಡುವನು.",
			"8": "<sup>8</sup> ಯಾರು ಸ್ವೇಚ್ಫೆಯುಳ್ಳವರಾಗಿ ಸತ್ಯವನ್ನು ಅನುಸರಿಸದೆ ಅನ್ಯಾಯವನ್ನು ಅನುಸರಿಸುತ್ತಾರೋ ಅವರಿಗೆ ಕೋಪ ರೌದ್ರಗಳು ಬರುವವು.",
			"9": "<sup>9</sup> ಕೆಟ್ಟದ್ದನ್ನು ನಡಿಸುವ ಪ್ರತಿಯೊಬ್ಬ ನರಪ್ರಾಣಿಗೆ ಕಷ್ಟವೂ ಸಂಕಟವೂ ಬರುವವು - ಯೆಹೂದ್ಯರಿಗೆ ಮೊದಲು, ಅನಂತರ ಗ್ರೀಕರಿಗೆ ಸಹ.",
			"10": "<sup>10</sup> ಆದರೆ ಒಳ್ಳೆದನ್ನು ನಡಿಸುವ ಪ್ರತಿಯೊಬ್ಬನಿಗೆ ಪ್ರಭಾವವೂ ಮಾನವೂ ಮನಶ್ಶಾಂತಿಯೂ ಉಂಟಾಗುವವು - ಯೆಹೂದ್ಯರಿಗೆ ಮೊದಲು, ಅನಂತರ ಗ್ರೀಕರಿಗೆ ಸಹ.",
			"11": "<sup>11</sup> ದೇವರಿಗೆ ಪಕ್ಷಪಾತವಿಲ್ಲ. ಧರ್ಮಶಾಸ್ತ್ರವಿಲ್ಲದವರಾಗಿ ಪಾಪ ಮಾಡಿದವರು ಧರ್ಮಶಾಸ್ತ್ರವಿಲ್ಲದವರಾಗಿ ನಾಶಹೊಂದುವರು; ಆದರೆ ಧರ್ಮಶಾಸ್ತ್ರವುಳ್ಳವರಾಗಿದ್ದು ಪಾಪಮಾಡಿದವರು ಧರ್ಮಶಾಸ್ತ್ರಕ್ಕನುಸಾರವಾಗಿ ತೀರ್ಪನ್ನು ಹೊಂದುವರು.",
			"13": "<sup>13</sup> ಧರ್ಮಶಾಸ್ತ್ರ ಕೇಳಿದ ಮಾತ್ರದಿಂದಲೇ ಯಾರೂ ದೇವರ ಸನ್ನಿಧಿಯಲ್ಲಿ ನೀತಿವಂತರಾಗುವದಿಲ್ಲ; ಅದನ್ನು ಅನುಸರಿಸುವವರೇ ನೀತಿವಂತರೆಂದು ನಿರ್ಣಯಿಸಲ್ಪಡುವರು.",
			"14": "<sup>14</sup> ಧರ್ಮಶಾಸ್ತ್ರವಿಲ್ಲದ ಅನ್ಯಜನರು ಸ್ವಾಭಾವಿಕವಾಗಿ ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿ ಹೇಳಿದಂತೆ ನಡೆದರೆ ಅವರು ಧರ್ಮಶಾಸ್ತ್ರವಿಲ್ಲದವರಾಗಿದ್ದರೂ ತಾವೇ ತಮಗೆ ಧರ್ಮಪ್ರಮಾಣವಾಗಿದ್ದಾರೆ;",
			"15": "<sup>15</sup> ಹೇಗಂದರೆ ಅವರು ಧರ್ಮಶಾಸ್ತ್ರದ ಮುಖ್ಯ ತಾತ್ಪರ್ಯ ತಮ್ಮ ಹೃದಯದಲ್ಲಿ ಬರೆದದೆ ಎಂಬದನ್ನು ತೋರ್ಪಡಿಸುತ್ತಾರೆ. ಇದಕ್ಕೆ ಅವರ ಮನಸ್ಸು ಸಹ ಸಾಕ್ಷಿ ನುಡಿಯುತ್ತದೆ; ಅವರ ಯೋಚನೆಗಳು ವಾದಿಪ್ರತಿವಾದಿಗಳಂತೆ - ಇದು ತಪ್ಪೆಂದು ತಪ್ಪಲ್ಲವೆಂದು ಸೂಚಿಸುತ್ತವೆ.",
			"16": "<sup>16</sup> ನಾನು ಸಾರುವ ಸುವಾರ್ತೆಯಲ್ಲಿ ಬೋಧಿಸಿರುವ ಪ್ರಕಾರ ದೇವರು ಯೇಸು ಕ್ರಿಸ್ತನ ಮೂಲಕವಾಗಿ ಮನುಷ್ಯರ ಗುಟ್ಟುಗಳನ್ನು ಹಿಡಿದು ವಿಚಾರಿಸುವ ದಿನವು ಬರುತ್ತದೆ. ಆ ದಿನದಲ್ಲಿ ಇದೆಲ್ಲಾ ತಿಳಿದುಬರುವದು.",
			"17": "<sup>17</sup> ನೀನು ಯೆಹೂದ್ಯನೆನಿಸಿಕೊಂಡವನೂ ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿ ಭರವಸವಿಟ್ಟವನೂ ದೇವರ ವಿಷಯವಾಗಿ ಹೊಗಳಿಕೊಳ್ಳುವವನೂ",
			"18": "<sup>18</sup> ಆತನ ಚಿತ್ತವನ್ನು ಬಲ್ಲವನೂ ಧರ್ಮಶಾಸ್ತ್ರದಿಂದ ಉಪದೇಶವನ್ನು ಹೊಂದಿ ಇದು ತಕ್ಕದ್ದು ಅದು ತಕ್ಕದ್ದಲ್ಲವೆಂದು ವಿವೇಚಿಸುವವನೂ ಆಗಿದ್ದೀ.",
			"19": "<sup>19</sup> ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿ ಜ್ಞಾನ ಸತ್ಯಗಳ ಸ್ವರೂಪವೇ ನನಗೆ ಇರುವದರಿಂದ ಕುರುಡರಿಗೆ ದಾರಿತೋರಿಸುವವನೂ",
			"20": "<sup>20</sup> ಕತ್ತಲೆಯಲ್ಲಿರುವವರಿಗೆ ಬೆಳಕೂ ತಿಳುವಳಿಕೆಯಿಲ್ಲದವರಿಗೆ ಶಿಕ್ಷಕನೂ ಬಾಲಕರಿಗೆ ಉಪಾಧ್ಯಾಯನೂ ಆಗಿದ್ದೇನೆಂದು ನಂಬಿಕೊಂಡಿದ್ದೀ.",
			"21": "<sup>21</sup> ಹೀಗಿರಲಾಗಿ ಮತ್ತೊಬ್ಬನಿಗೆ ಉಪದೇಶ ಮಾಡುವ ನೀನು ನಿನಗೆ ಉಪದೇಶಮಾಡಿಕೊಳ್ಳದೆ ಇದ್ದೀಯೋ? ಕದಿಯಬಾರದೆಂದು ಬೋಧಿಸುವ ನೀನು ಕದಿಯುತ್ತೀಯೋ?",
			"22": "<sup>22</sup> ಹಾದರ ಮಾಡಬಾರದೆಂದು ಹೇಳುವ ನೀನು ಹಾದರ ಮಾಡುತ್ತೀಯೋ? ಮೂರ್ತಿಗಳಲ್ಲಿ ಅಸಹ್ಯಪಡುತ್ತಿರುವ ನೀನು ದೇವಸ್ಥಾನದಲ್ಲಿ ಕಳ್ಳತನ ಮಾಡುತ್ತೀಯೋ?",
			"23": "<sup>23</sup> ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿ ಅಭಿಮಾನಿಯಾಗಿರುವ ನೀನು ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಮೀರಿ ನಡೆದು ದೇವರನ್ನು ಅವಮಾನಪಡಿಸುತ್ತೀಯೋ?",
			"24": "<sup>24</sup> ನಿಮ್ಮ ದೆಸೆಯಿಂದ ಅನ್ಯಜನರಲ್ಲಿ ದೇವರ ನಾಮವು ದೂಷಣೆಗೆ ಗುರಿಯಾಗುತ್ತದೆಂದು ಬರೆದದೆಯಲ್ಲಾ.",
			"25": "<sup>25</sup> ನೀನು ಧರ್ಮಶಾಸ್ತ್ರಾನುಸಾರವಾಗಿ ನಡೆಯುವವನಾದರೆ ಸುನ್ನತಿಯೆಂಬ ಸಂಸ್ಕಾರವು ಪ್ರಯೋಜನಕರವಾದದ್ದೇ ಸರಿ; ಆದರೆ ನೀನು ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಮೀರಿ ನಡೆಯುವವನಾಗಿದ್ದರೆ ನಿನಗೆ ಸುನ್ನತಿಯಿದ್ದರೂ ಇಲ್ಲದಂತಾಯಿತು.",
			"26": "<sup>26</sup> ಹೀಗಿರಲಾಗಿ ಸುನ್ನತಿಯಿಲ್ಲದವನು ಧರ್ಮಶಾಸ್ತ್ರದ ನೇಮಗಳ ಪ್ರಕಾರ ನಡೆದರೆ ಸುನ್ನತಿಯಿಲ್ಲದವನಾಗಿದ್ದರೂ ಸುನ್ನತಿಯಿದ್ದವನಂತೆ ಎಣಿಸಲ್ಪಡುವದಿಲ್ಲವೇ?",
			"27": "<sup>27</sup> ಅವನು ಶರೀರದಲ್ಲಿ ಸುನ್ನತಿಯಿಲ್ಲದವನಾಗಿದ್ದು ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿ ಹೇಳಿದ್ದನ್ನು ನೆರವೇರಿಸುವದರಲ್ಲಿ ಶಾಸ್ತ್ರವೂ ಸುನ್ನತಿಯೂ ಇದ್ದು ಧರ್ಮವನ್ನು ಮೀರಿ ನಡೆಯುವ ನಿನ್ನನ್ನು ದೋಷಿಯೆಂದು ನಿರ್ಣಯಿಸುವದಿಲ್ಲವೇ?",
			"28": "<sup>28</sup> ಹೊರಗೆ ಮಾತ್ರ ಯೆಹೂದ್ಯನಾಗಿರುವವನು ಯೆಹೂದ್ಯನಲ್ಲ; ಮತ್ತು ಹೊರಗೆ ಶರೀರದಲ್ಲಿ ಮಾತ್ರ ಮಾಡಿರುವ ಸುನ್ನತಿಯು ಸುನ್ನತಿಯಲ್ಲ.",
			"29": "<sup>29</sup> ಆದರೆ ಒಳಗೆ ಯೆಹೂದ್ಯನಾಗಿರುವವನೇ ಯೆಹೂದ್ಯನು; ಮತ್ತು ಹೃದಯದಲ್ಲಿ ಆಗುವ ಸುನ್ನತಿಯೇ ಸುನ್ನತಿ. ಇದು ಬಾಹ್ಯಾಚಾರಕ್ಕೆ ಸಂಬಂಧಪಟ್ಟದ್ದಲ್ಲ, ಆತ್ಮ ಸಂಬಂಧಪಟ್ಟದ್ದೇ; ಇಂಥಾ ಸುನ್ನತಿಯಿದ್ದವನಿಗೆ ಬರುವ ಹೊಗಳಿಕೆಯು ದೇವರಿಂದಲೇ ಹೊರತು ಮನುಷ್ಯರಿಂದ ಬರುವದಲ್ಲ."
		},
		"3": {
			"1": "<sup>1</sup> ಹಾಗಾದರೆ ಯೆಹೂದ್ಯರ ಶ್ರೇಷ್ಠತ್ವವೇನು? ಸುನ್ನತಿಯಿಂದ ಲಾಭವೇನು?",
			"2": "<sup>2</sup> ಎಲ್ಲಾ ವಿಷಯಗಳಲ್ಲಿಯೂ ಬಹಳ ಉಂಟು. ಮೊದಲನೇದಾಗಿ ದೈವೋಕ್ತಿಗಳು ಅವರ ವಶಕ್ಕೆ ಒಪ್ಪಿಸಲ್ಪಟ್ಟವು.",
			"3": "<sup>3</sup> ಅವರಲ್ಲಿ ಕೆಲವರು ನಂಬದೆ ಹೋಗಿದ್ದರೇನು? ಅವರು ನಂಬದೆ ಹೋದದರಿಂದ ದೇವರು ವಚನಕ್ಕೆ ತಪ್ಪುವವನಾದನೋ? ಎಂದಿಗೂ ಇಲ್ಲ;",
			"4": "<sup>4</sup> ಎಲ್ಲಾ ಮನುಷ್ಯರು ಸುಳ್ಳುಗಾರರಾದರೂ ದೇವರು ಸತ್ಯವಂತನೇ ಸರಿ. ನೀನು ಎಲ್ಲಾ ನುಡಿಗಳಲ್ಲಿ ನ್ಯಾಯಸ್ಥನೆಂದು ಕಾಣಿಸಿಕೊಳ್ಳಬೇಕು, ನಿನ್ನ ಮೇಲೆ ವ್ಯಾಜ್ಯ ನಡೆಯುವಾಗ ನೀನು ಗೆಲ್ಲಬೇಕು ಎಂದು ಬರೆದದೆ.",
			"5": "<sup>5</sup> ನಾವು ನೀತಿ ತಪ್ಪಿನಡೆಯುವದರಿಂದ ದೇವರಲ್ಲಿರುವ ನೀತಿ ಪ್ರಸಿದ್ಧಿಗೆ ಬರುವದಾದರೆ ನಾವು ಏನು ಹೇಳೋಣ? ಉಗ್ರದಂಡನೆಯನ್ನು ಮಾಡುವ ದೇವರು ಅನ್ಯಾಯಸ್ಥನೇನು? (ಈ ಮಾತನ್ನು ಲೋಕರೀತಿಯಾಗಿ ಆಡಿದ್ದೇನೆ.) ಎಂದಿಗೂ ಅಲ್ಲ.",
			"6": "<sup>6</sup> ದೇವರು ಅನ್ಯಾಯಸ್ಥನಾಗಿದ್ದರೆ ಲೋಕಕ್ಕೆ ಹೇಗೆ ನ್ಯಾಯತೀರಿಸಾನು?",
			"7": "<sup>7</sup> ನನ್ನ ಸುಳ್ಳಿನಿಂದ ದೇವರ ಸತ್ಯವು ಪ್ರಸಿದ್ಧಿಗೆ ಬಂದು ಆತನ ಮಹಿಮೆ ಹೆಚ್ಚಾದರೆ ಇನ್ನು ನನಗೆ ಪಾಪಿಯೆಂದು ತೀರ್ಪಾಗುವದು ಯಾಕೆ?",
			"8": "<sup>8</sup> ಮೇಲು ಬರುವಂತೆ ಕೇಡು ಮಾಡೋಣ ಎಂದು ಯಾಕೆ ಹೇಳಬಾರದು? ಕೆಲವರು ನಮ್ಮನ್ನು ಕುರಿತು - ಇವರು ಹೀಗೆ ಬೋಧನೆಮಾಡುವವರೆಂದು ದೂಷಿಸಿ ಹೇಳುತ್ತಾರಲ್ಲ; ಅಂಥವರಿಗೆ ದಂಡನೆಯ ತೀರ್ಪಾಗುವದು ನ್ಯಾಯ.",
			"9": "<sup>9</sup> ಹಾಗಾದರೆ ಏನು ಹೇಳಬೇಕು? ನಾವು ಅನ್ಯರಿಗಿಂತ ಉತ್ತಮಸ್ಥಾನದಲ್ಲಿದ್ದೇವೋ? ಎಷ್ಟು ಮಾತ್ರಕ್ಕೂ ಇಲ್ಲ. ಯೆಹೂದ್ಯರೇ ಆಗಲಿ ಗ್ರೀಕರೇ ಆಗಲಿ ಎಲ್ಲರೂ ಪಾಪಕ್ಕೆ ಒಳಬಿದ್ದವರೆಂದು ಮೊದಲೇ ತೋರಿಸಿಕೊಟ್ಟಿದ್ದೇವಲ್ಲಾ.",
			"10": "<sup>10</sup> ಇದು ಶಾಸ್ತ್ರದಲ್ಲಿ ಬರೆದಿರುವ ಮಾತುಗಳಿಗೆ ಸರಿಯಾಗಿದೆ; ಅವು ಯಾವವಂದರೆ - ನೀತಿವಂತನು ಇಲ್ಲ, ಒಬ್ಬನಾದರೂ ಇಲ್ಲ;",
			"11": "<sup>11</sup> ತಿಳುವಳಿಕೆಯುಳ್ಳವನು ಇಲ್ಲ, ದೇವರನ್ನು ಹುಡುಕುವವನು ಇಲ್ಲ.",
			"12": "<sup>12</sup> ಎಲ್ಲರೂ ದಾರಿತಪ್ಪಿ ನಡೆಯುತ್ತಾರೆ, ಎಲ್ಲರೂ ಕೆಲಸಕ್ಕೆ ಬಾರದವರಾದರು. ಒಳ್ಳೇದನ್ನು ಮಾಡುವವನು ಇಲ್ಲ, ಒಬ್ಬನಾದರೂ ಇಲ್ಲ.",
			"13": "<sup>13</sup> ಅವರ ಗಂಟಲು ತೆರೆದಿರುವ ಸಮಾಧಿಯಾಗಿದೆ. ಅವರು ನಾಲಿಗೆಯಿಂದ ವಂಚನೆಯ ಮಾತುಗಳನ್ನಾಡುತ್ತಾರೆ; ಅವರ ತುಟಿಗಳ ಹಿಂದೆ ಹಾವಿನ ವಿಷವದೆ.",
			"14": "<sup>14</sup> ಅವರ ಬಾಯಿ ಶಾಪದಿಂದಲೂ ಕ್ರೋಧದ ಮಾತುಗಳಿಂದಲೂ ತುಂಬಿ ಅದೆ.",
			"15": "<sup>15</sup> ಅವರ ಕಾಲುಗಳು ರಕ್ತವನ್ನು ಸುರಿಸಲು ಆತುರಪಡುತ್ತವೆ.",
			"16": "<sup>16</sup> ಅವರು ಹೋದ ದಾರಿಗಳಲ್ಲಿ ನಾಶ ಸಂಕಟಗಳು ಉಂಟಾಗುತ್ತವೆ.",
			"17": "<sup>17</sup> ಸಮಾಧಾನದ ಮಾರ್ಗವನ್ನೇ ಅರಿಯರು.",
			"18": "<sup>18</sup> ಅವರ ಕಣ್ಣೆದುರಿಗೆ ದೇವರ ಭಯವೇ ಇಲ್ಲ ಎಂಬವುಗಳೇ.",
			"19": "<sup>19</sup> ಧರ್ಮಶಾಸ್ತ್ರದ ನುಡಿಗಳೆಲ್ಲವು ಆ ಶಾಸ್ತ್ರಕ್ಕೆ ಒಳಗಾದವರಿಗೇ ಹೇಳಿವೆಯೆಂದು ಬಲ್ಲೆವಷ್ಟೆ; ಹೀಗೆ ಎಲ್ಲರ ಬಾಯಿ ಕಟ್ಟಿಹೋಗುವದು, ಲೋಕವೆಲ್ಲಾ ದೇವರ ಮುಂದೆ ಅಪರಾಧಿಯಾಗಿ ನಿಲ್ಲುವದು.",
			"20": "<sup>20</sup> ಯಾಕಂದರೆ ಯಾವನಾದರೂ ನೇಮನಿಷ್ಠೆಗಳನ್ನನುಸರಿಸಿ ದೇವರ ಸನ್ನಿಧಿಯಲ್ಲಿ ನೀತಿವಂತನೆಂದು ನಿರ್ಣಯಿಸಲ್ಪಡುವದಿಲ್ಲ. ಧರ್ಮಶಾಸ್ತ್ರದಿಂದ ಪಾಪದ ಅರುಹು ಉಂಟಾಗುತ್ತದಷ್ಟೆ.",
			"21": "<sup>21</sup> ಈಗಲಾದರೋ ದೇವರಿಂದ ದೊರಕುವ ನೀತಿಯು ಧರ್ಮಶಾಸ್ತ್ರನಿಯಮಗಳಿಲ್ಲದೆ ಪ್ರಕಟವಾಗಿದೆ. ಅದು ಮೋಶೆಯ ಧರ್ಮಶಾಸ್ತ್ರದಿಂದಲೂ ಪ್ರವಾದಿಗಳ ನುಡಿಗಳಿಂದಲೂ ಸಾಕ್ಷಿಗೊಂಡಿರುತ್ತದೆ.",
			"22": "<sup>22</sup> ದೇವರಿಂದಾಗುವ ಆ ನೀತಿಯು ಯಾವದಂದರೆ ಯೇಸು ಕ್ರಿಸ್ತನನ್ನು ನಂಬುವದರಿಂದಲೇ ನಂಬುವವರೆಲ್ಲರಿಗೆ ದೊರಕುವಂಥದು. ಹೆಚ್ಚುಕಡಿಮೆ ಏನೂ ಇಲ್ಲ.",
			"23": "<sup>23</sup> ಎಲ್ಲರೂ ಪಾಪಮಾಡಿ ದೇವರ ಮಹಿಮೆಯನ್ನು ಹೊಂದದೆ ಹೋಗಿದ್ದಾರೆ.",
			"24": "<sup>24</sup> ಅವರು ನೀತಿವಂತರೆಂದು ನಿರ್ಣಯ ಹೊಂದುವದು ದೇವರ ಉಚಿತಾರ್ಥವಾದ ಕೃಪೆಯಿಂದಲೇ ಕ್ರಿಸ್ತಯೇಸುವಿನಿಂದಾದ ಪಾಪ ವಿಮೋಚನೆಯ ಮೂಲಕವಾಗಿ ಆಗುವದು.",
			"25": "<sup>25</sup> ಈತನು ತನ್ನ ರಕ್ತವನ್ನು ಸುರಿಸಿ ನಂಬಿಕೆಯಿದ್ದವರಿಗಾಗಿ ಕೃಪಾಧಾರವಾಗಬೇಕೆಂದು ದೇವರು ಈತನನ್ನು ಮುಂದಿಟ್ಟನು.",
			"26": "<sup>26</sup> ದೇವರು ಹಿಂದಿನ ಕಾಲದ ಪಾಪಗಳನ್ನು ದಂಡಿಸದೆ ಸಹಿಸಿಕೊಂಡಿರಲಾಗಿ ತನ್ನ ನೀತಿಯನ್ನು ಈಗಿನ ಕಾಲದಲ್ಲಿ ಹೀಗೆ ತೋರಿಸಿ ತಾನು ನೀತಿಸ್ವರೂಪನಾಗಿಯೂ ಯೇಸುವಿನಲ್ಲಿ ನಂಬಿಕೆಯಿಡುವವರನ್ನು ನೀತಿವಂತರೆಂದು ನಿರ್ಣಯಿಸುವವನಾಗಿಯೂ ಕಾಣಿಸಿಕೊಂಡಿದ್ದಾನೆ.",
			"27": "<sup>27</sup> ಹಾಗಾದರೆ ಹೊಗಳಿಕೊಳ್ಳುವದಕ್ಕೆ ಆಸ್ಪದವೆಲ್ಲಿ? ಅದು ಇಲ್ಲದೆ ಹೋಯಿತು. ಯಾವ ಪ್ರಮಾಣದಿಂದ? ಕರ್ಮಮಾರ್ಗವನ್ನು ಅನುಸರಿಸುವದರಿಂದಲೋ? ಅಲ್ಲ; ವಿಶ್ವಾಸ ಮಾರ್ಗವನ್ನು ಅನುಸರಿಸುವದರಿಂದಲೇ.",
			"28": "<sup>28</sup> ಧರ್ಮಪ್ರಮಾಣಸಂಬಂಧವಾದ ಕರ್ಮಗಳಿಲ್ಲದೆ ನಂಬಿಕೆಯಿಂದಲೇ ಮನುಷ್ಯರು ನೀತಿವಂತರೆಂದು ನಿರ್ಣಯಿಸಲ್ಪಡುವರೆಂಬದಾಗಿ ನಿಶ್ಚಯ ಮಾಡಿಕೊಂಡಿದ್ದೇವಲ್ಲಾ.",
			"29": "<sup>29</sup> ದೇವರು ಯೆಹೂದ್ಯರಿಗೆ ಮಾತ್ರ ದೇವರಾಗಿದ್ದಾನೋ? ಅನ್ಯಜನಗಳಿಗೆ ಸಹ ದೇವರಲ್ಲವೇ? ಹೌದು. ದೇವರು ಒಬ್ಬನೇ ಆಗಿರಲಾಗಿ ಆತನು ಅನ್ಯಜನರಿಗೆ ಸಹ ದೇವರಾಗಿದ್ದಾನೆ.",
			"30": "<sup>30</sup> ಆತನು ಸುನ್ನತಿಯವರನ್ನು ನೀತಿವಂತರೆಂದು ನಿರ್ಣಯಿಸುವದಕ್ಕೆ ಅವರ ನಂಬಿಕೆಯೇ ಆಧಾರ; ಸುನ್ನತಿಯಿಲ್ಲದವರನ್ನೂ ಹಾಗೆಯೇ ನಿರ್ಣಯಿಸುವದಕ್ಕೆ ನಂಬಿಕೆಯೇ ಕಾರಣ.",
			"31": "<sup>31</sup> ಹಾಗಾದರೆ ನಂಬಿಕೆಯಿಂದ ಧರ್ಮಪ್ರಮಾಣವನ್ನು ನಿರರ್ಥಕಮಾಡುತ್ತೇವೋ? ಎಂದಿಗೂ ಇಲ್ಲ. ಧರ್ಮಪ್ರಮಾಣವನ್ನು ಸ್ಥಿರಪಡಿಸುತ್ತೇವೆ."
		},
		"4": {
			"1": "<sup>1</sup> ಹಾಗಾದರೆ ವಂಶಾನುಕ್ರಮವಾಗಿ ನಮಗೆ ಮೂಲಪಿತೃವಾಗಿರುವ ಅಬ್ರಹಾಮನು ಏನು ಪಡಕೊಂಡನೆಂದು ಹೇಳಬೇಕು?",
			"2": "<sup>2</sup> ಅಬ್ರಹಾಮನು ಪುಣ್ಯಕ್ರಿಯೆಗಳಿಂದಲೇ ನೀತಿವಂತನಾದ ಪಕ್ಷದಲ್ಲಿ ಹೊಗಳಿಕೊಳ್ಳುವದಕ್ಕೆ ಅವನಿಗೆ ಆಸ್ಪದವಿರುವದು. ಆದರೆ ದೇವರ ಮುಂದೆ ಹೊಗಳಿಕೊಳ್ಳುವದಕ್ಕೆ ಆಸ್ಪದವಿರುವದಿಲ್ಲ.",
			"3": "<sup>3</sup> ಇದರ ವಿಷಯದಲ್ಲಿ ಶಾಸ್ತ್ರವು ಹೇಳುವದನ್ನು ಆಲೋಚಿಸಿರಿ. ಅಬ್ರಹಾಮನು ದೇವರನ್ನು ನಂಬಿದನು; ಆ ನಂಬಿಕೆ ಅವನ ಲೆಕ್ಕಕ್ಕೆ ನೀತಿ ಎಂದು ಎಣಿಸಲ್ಪಟ್ಟಿತು ಎಂದು ಹೇಳುತ್ತದೆ.",
			"4": "<sup>4</sup> ಕೆಲಸ ಮಾಡಿದವನಿಗೆ ಬರುವ ಕೂಲಿಯು ದಯದಿಂದ ದೊರಕುವಂಥದೆಂದು ಎಣಿಕೆಯಾಗುವದಿಲ್ಲ; ಅದು ಸಲ್ಲತಕ್ಕದ್ದೇ ಎಂದು ಎಣಿಕೆಯಾಗುವದು.",
			"5": "<sup>5</sup> ಆದರೆ ಯಾವನು ಪುಣ್ಯಕ್ರಿಯೆಗಳನ್ನು ಮಾಡಿದವನಾಗಿರದೆ ಪಾಪಾತ್ಮರನ್ನು ನೀತಿವಂತರೆಂದು ನಿರ್ಣಯಿಸುವಾತನಲ್ಲಿ ನಂಬಿಕೆಯಿಡುತ್ತಾನೋ ಅವನ ನಂಬಿಕೆಯೇ ನೀತಿ ಎಂದು ಎಣಿಸಲ್ಪಡುವದು.",
			"6": "<sup>6</sup> ದೇವರು ಯಾವನನ್ನು ಪುಣ್ಯಕ್ರಿಯೆಗಳಿಲ್ಲದೆ ನೀತಿವಂತನೆಂದು ಎಣಿಸುತ್ತಾನೋ ಅವನು ಧನ್ಯನೆಂದು ದಾವೀದನು ಸಹ ಹೇಳುತ್ತಾನೆ. ಹೇಗಂದರೆ -",
			"7": "<sup>7</sup> ಯಾರ ಅಪರಾಧಗಳು ಪರಿಹಾರವಾಗಿವೆಯೋ, ಯಾರ ಪಾಪಗಳು ಕ್ಷಮಿಸಲ್ಪಟ್ಟಿವೆಯೋ ಅವರೇ ಧನ್ಯರು.",
			"8": "<sup>8</sup> ಕರ್ತನು ಯಾವನ ಪಾಪವನ್ನು ಲೆಕ್ಕಕ್ಕೆ ತರುವದಿಲ್ಲವೋ ಆ ಮನುಷ್ಯನೇ ಧನ್ಯನು.",
			"9": "<sup>9</sup> ಈ ಶುಭವಚನವು ಸುನ್ನತಿಯಿದ್ದವರಿಗೆ ಮಾತ್ರವೋ? ಸುನ್ನತಿಯಿಲ್ಲದವರಿಗೆ ಸಹ ಆಗುತ್ತದೋ? ನೋಡೋಣ. ಅಬ್ರಹಾಮನ ಲೆಕ್ಕಕ್ಕೆ ಅವನ ನಂಬಿಕೆಯೇ ನೀತಿಯೆಂದು ಎಣಿಸಲ್ಪಟ್ಟಿತೆಂಬದಾಗಿ ಹೇಳುತ್ತೇವಲ್ಲಾ.",
			"10": "<sup>10</sup> ಅದು ಯಾವಾಗ ಎಣಿಸಲ್ಪಟ್ಟಿತು? ಅವನಿಗೆ ಸುನ್ನತಿಯಾದ ಮೇಲೆಯೋ? ಸುನ್ನತಿಯಾಗುವದಕ್ಕೆ ಮೊದಲೋ? ಸುನ್ನತಿಯಾದ ಮೇಲೆ ಅಲ್ಲ, ಸುನ್ನತಿಯಾಗುವದಕ್ಕೆ ಮೊದಲೇ ಎಣಿಸಲ್ಪಟ್ಟಿತು.",
			"11": "<sup>11</sup> ಆಮೇಲೆ ಸುನ್ನತಿಯೆಂಬ ಸಂಸ್ಕಾರವನ್ನು ಗುರುತಾಗಿ ಹೊಂದಿದನು. ಇದು ಸುನ್ನತಿಯಾಗುವದಕ್ಕೆ ಮೊದಲೇ ಅವನಿಗಿದ್ದ ನಂಬಿಕೆಯಿಂದಾದ ನೀತಿಗೆ ಮುದ್ರೆಯಾಗಿತ್ತು. ಹೀಗಿರುವದರಿಂದ ಅವನು ನಂಬುವವರೆಲ್ಲರಿಗೂ, ಅವರು ಸುನ್ನತಿಯಿಲ್ಲದವರಾದಾಗ್ಯೂ ಮೂಲತಂದೆಯಾದನು; ಅವರೂ ನೀತಿವಂತರೆಂದು ಎಣಿಸಲ್ಪಡುವರು.",
			"12": "<sup>12</sup> ಇದಲ್ಲದೆ ಸುನ್ನತಿಯವರಲ್ಲಿ ಯಾರಾರು ಸುನ್ನತಿ ಹೊಂದಿದ್ದಲ್ಲದೆ ನಮ್ಮ ಪಿತೃವಾಗಿರುವ ಅಬ್ರಹಾಮನಿಗೆ ಸುನ್ನತಿಯಾಗುವದಕ್ಕೆ ಮುಂಚೆ ಇದ್ದ ನಂಬಿಕೆಯ ಹೆಜ್ಜೆಗಳನ್ನು ಹಿಡಿದು ನಡೆಯುತ್ತಾರೋ ಅವರಿಗೆ ಸಹ ಮೂಲ ತಂದೆಯಾಗಿದ್ದಾನೆ.",
			"13": "<sup>13</sup> ನೀನು ಲೋಕಕ್ಕೆ ಬಾಧ್ಯನಾಗುವಿ ಎಂಬ ವಾಗ್ದಾನವು ಅಬ್ರಹಾಮನಿಗಾಗಲಿ ಅವನ ಸಂತತಿಯವರಿಗಾಗಲಿ ಧರ್ಮಶಾಸ್ತ್ರದಿಂದ ಆದದ್ದಲ್ಲ. ನಂಬಿಕೆಯೆಂಬ ನೀತಿಯಿಂದ ಆದದ್ದೇ.",
			"14": "<sup>14</sup> ಧರ್ಮಶಾಸ್ತ್ರದವರು ಬಾಧ್ಯರಾಗಿದ್ದರೆ ನಂಬಿಕೆಯು ವ್ಯರ್ಥವಾಯಿತು, ವಾಗ್ದಾನವೂ ಬರೀದಾಯಿತು.",
			"15": "<sup>15</sup> ಯಾಕಂದರೆ ಧರ್ಮಶಾಸ್ತ್ರವು ಜನರನ್ನು ದೇವರ ಕೋಪಕ್ಕೆ ಗುರಿಮಾಡುವಂಥದು. ಧರ್ಮಶಾಸ್ತ್ರವಿಲ್ಲದ ಪಕ್ಷದಲ್ಲಿ ಮೀರೋಣವೆಂಬದೂ ಇಲ್ಲ.",
			"16": "<sup>16</sup> ಆದಕಾರಣ ಆ ಬಾಧ್ಯತೆಯು ಕೃಪೆಯಿಂದಲೇ ದೊರೆಯುವಂಥದಾಗಿರುವಂತೆ ಅದು ನಂಬಿಕೆಯ ಮೂಲಕ ಸಿಕ್ಕುತ್ತದೆ. ಹೀಗೆ ಆ ವಾಗ್ದಾನವು ಅಬ್ರಹಾಮನ ಸಂತತಿಯವರೆಲ್ಲರಿಗೂ, ಅಂದರೆ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಆಧಾರಮಾಡಿಕೊಂಡವರಿಗೆ ಮಾತ್ರವಲ್ಲದೆ ಅಬ್ರಹಾಮನಲ್ಲಿದ್ದಂಥ ನಂಬಿಕೆಯುಳ್ಳವರಿಗೆ ಸಹ ಸ್ಥಿರವಾಗಿ ನಿಲ್ಲುವದು.",
			"17": "<sup>17</sup> ನಾನು ನಿನ್ನನ್ನು ಅನೇಕ ಜನಾಂಗಗಳಿಗೆ ಮೂಲಪಿತೃವಾಗಿ ನೇಮಿಸಿದ್ದೇನೆಂದು ಶಾಸ್ತ್ರದಲ್ಲಿ ಬರೆದಿರುವ ಪ್ರಕಾರ ಅಬ್ರಹಾಮನು ನಮ್ಮೆಲ್ಲರಿಗೂ ದೇವರ ಸನ್ನಿಧಿಯಲ್ಲಿ ಮೂಲ ಪಿತೃವಾಗಿದ್ದಾನೆ. ದೇವರು ಸತ್ತವರನ್ನು ಬದುಕಿಸುವವನಾಗಿಯೂ ಇಲ್ಲದ್ದನ್ನು ಇರುವದಾಗಿ ಕರೆಯುವವನಾಗಿಯೂ ಇದ್ದಾನೆಂದು ಅಬ್ರಹಾಮನು ನಂಬಿದನು.",
			"18": "<sup>18</sup> ನಿನ್ನ ಸಂತಾನವು ನಕ್ಷತ್ರಗಳಷ್ಟಾಗುವದು ಎಂಬದಾಗಿ ತನಗೆ ಹೇಳಲ್ಪಟ್ಟ ಮಾತು ನೆರವೇರುವದಕ್ಕೆ ಮಾರ್ಗ ತೋರದೆ ಇರುವದರಿಂದ ಅಬ್ರಹಾಮನು ತಾನು ಅನೇಕ ಜನಾಂಗಗಳಿಗೆ ಮೂಲಪಿತೃವಾಗುವೆನೆಂಬ ನಿರೀಕ್ಷೆಗೆ ಆಸ್ಪದವಿಲ್ಲದಿರುವಾಗಲೂ ನಿರೀಕ್ಷಿಸಿ ನಂಬಿದನು.",
			"19": "<sup>19</sup> ಅವನು ಹೆಚ್ಚುಕಡಿಮೆ ನೂರು ವರುಷದವನಾಗಿದ್ದು ತನ್ನ ದೇಹವು ಆಗಲೇ ಮೃತಪ್ರಾಯವಾಯಿತೆಂದೂ ಸಾರಳಿಗೆ ಗರ್ಭಕಾಲ ಕಳೆದುಹೋಯಿತೆಂದೂ ಯೋಚಿಸಿದಾಗ್ಯೂ ಅವನ ನಂಬಿಕೆಯು ಕುಂದಲಿಲ್ಲ.",
			"20": "<sup>20</sup> ದೇವರು ಮಾಡಿದ ವಾಗ್ದಾನದ ವಿಷಯದಲ್ಲಿ ಅವನು ಅಪನಂಬಿಕೆಯಿಂದ ಚಂಚಲಚಿತ್ತನಾಗಲಿಲ್ಲ.",
			"21": "<sup>21</sup> ದೇವರನ್ನು ಘನಪಡಿಸುವವನಾಗಿ ಆತನು ತನ್ನ ವಾಗ್ದಾನವನ್ನು ನೆರವೇರಿಸುವದಕ್ಕೆ ಸಮರ್ಥನೆಂದು ಪೂರಾ ಭರವಸವಿಟ್ಟು ದೃಢನಂಬಿಕೆಯುಳ್ಳವನಾದನು.",
			"22": "<sup>22</sup> ಆದದರಿಂದ ಆ ನಂಬಿಕೆಯು ಅವನ ಲೆಕ್ಕಕ್ಕೆ ನೀತಿಯೆಂದು ಎಣಿಸಲ್ಪಟ್ಟಿತು.",
			"23": "<sup>23</sup> ಆದರೆ ಅದು ಅವನ ಲೆಕ್ಕಕ್ಕೆ ನೀತಿಯೆಂದು ಎಣಿಸಲ್ಪಟ್ಟಿತೆಂಬದು ಅವನಿಗೋಸ್ಕರ ಮಾತ್ರವಲ್ಲದೆ ನಮಗೋಸ್ಕರವೂ ಬರೆದಿರುವದು.",
			"24": "<sup>24</sup> ನಮ್ಮ ಕರ್ತನಾದ ಯೇಸುವನ್ನು ಸತ್ತವರೊಳಗಿಂದ ಎಬ್ಬಿಸಿದ ದೇವರನ್ನು ನಂಬುವ ನಮಗೂ ನಂಬಿಕೆಯು ನೀತಿಯೆಂದು ಎಣಿಸಲ್ಪಡುವದು.",
			"25": "<sup>25</sup> ದೇವರು ಆತನನ್ನು ನಮ್ಮ ಅಪರಾಧಗಳ ನಿಮಿತ್ತ ಮರಣಕ್ಕೆ ಒಪ್ಪಿಸಿಕೊಟ್ಟು ನಮಗೆ ನೀತಿವಂತರೆಂಬ ನಿರ್ಣಯವು ಉಂಟಾಗುವ ನಿಮಿತ್ತ ಜೀವದಿಂದ ಎಬ್ಬಿಸಿದನು."
		},
		"5": {
			"1": "<sup>1</sup> ಹೀಗಿರಲಾಗಿ ನಾವು ನಂಬಿಕೆಯಿಂದ ನೀತಿವಂತರಾದ ಕಾರಣ ನಮ್ಮ ಕರ್ತನಾದ ಯೇಸು ಕ್ರಿಸ್ತನ ಮೂಲಕ ದೇವರೊಂದಿಗೆ ಉಂಟಾದ ಸಮಾಧಾನದಲ್ಲಿ ಇರೋಣ.",
			"2": "<sup>2</sup> ನಾವು ಈಗ ನೆಲೆಗೊಂಡಿರುವ ದೇವರ ಕೃಪಾಶ್ರಯದಲ್ಲಿ ಆತನ ಮುಖಾಂತರವೇ ನಂಬಿಕೆಯಿಂದ ಪ್ರವೇಶವಾಯಿತು. ಮತ್ತು ದೇವರ ಮಹಿಮೆಯನ್ನು ಹೊಂದುವೆವೆಂಬ ಭರವಸದಿಂದ ಉಲ್ಲಾಸವಾಗಿದ್ದೇವೆ.",
			"3": "<sup>3</sup> ಇದು ಮಾತ್ರವಲ್ಲದೆ ನಮಗೆ ಉಂಟಾಗುವ ಉಪದ್ರವಗಳಲ್ಲಿಯೂ ಉಲ್ಲಾಸವಾಗಿದ್ದೇವೆ.",
			"4": "<sup>4</sup> ಯಾಕಂದರೆ ಉಪದ್ರವದಿಂದ ತಾಳ್ಮೆ ಹುಟ್ಟುತ್ತದೆ, ತಾಳ್ಮೆಯಿಂದ ಅನುಭವಸಿದ್ಧಿ ಹುಟ್ಟುತ್ತದೆ, ಅನುಭವದಿಂದ ನಿರೀಕ್ಷಣ ಹುಟ್ಟುತ್ತದೆಂದು ಬಲ್ಲೆವು.",
			"5": "<sup>5</sup> ಈ ನಿರೀಕ್ಷೆಯು ನಮ್ಮ ಆಶೆಯನ್ನು ಭಂಗಪಡಿಸುವದಿಲ್ಲ; ನಮಗೆ ಕೊಟ್ಟಿರುವ ಪವಿತ್ರಾತ್ಮನ ಮೂಲಕವಾಗಿ ದೇವರ ಪ್ರೀತಿರಸವು ನಮ್ಮ ಹೃದಯಗಳಲ್ಲಿ ಧಾರಾಳವಾಗಿ ಸುರಿದದೆಯಲ್ಲಾ.",
			"6": "<sup>6</sup> ನಾವು ಅಶಕ್ತರಾಗಿದ್ದಾಗಲೇ ಕ್ರಿಸ್ತನು ನೇಮಿತ ಕಾಲದಲ್ಲಿ ಭಕ್ತಿಹೀನರಿಗೋಸ್ಕರ ಪ್ರಾಣಕೊಟ್ಟನು.",
			"7": "<sup>7</sup> ನೀತಿವಂತರಿಗೋಸ್ಕರ ಯಾರಾದರೂ ಪ್ರಾಣಕೊಡುವದು ಅಪರೂಪ. ಉಪಕಾರಿಗೋಸ್ಕರ ಪ್ರಾಣಕೊಡುವದಕ್ಕೆ ಯಾವನಾದರೂ ಧೈರ್ಯಮಾಡಿದರೂ ಮಾಡಾನು.",
			"8": "<sup>8</sup> ಆದರೆ ನಾವು ಪಾಪಿಗಳಾಗಿದ್ದಾಗಲೂ ಕ್ರಿಸ್ತನು ನಮಗೋಸ್ಕರ ಪ್ರಾಣ ಕೊಟ್ಟದ್ದರಲ್ಲಿ ದೇವರು ನಮ್ಮ ಮೇಲೆ ತನಗಿರುವ ಪ್ರೀತಿಯನ್ನು ಸಿದ್ಧಾಂತಪಡಿಸಿದ್ದಾನೆ.",
			"9": "<sup>9</sup> ಈಗ ನಾವು ಆತನ ರಕ್ತದಿಂದ ನೀತಿವಂತರಾಗಿರಲಾಗಿ ಆತನ ಮೂಲಕ ಬರುವ ಕೋಪಕ್ಕೆ ತಪ್ಪಿಸಿಕೊಳ್ಳುವದು ಮತ್ತೂ ನಿಶ್ಚಯವಲ್ಲವೇ.",
			"10": "<sup>10</sup> ದೇವರಿಗೆ ವೈರಿಗಳಾಗಿದ್ದ ನಾವು ಆತನ ಮಗನ ಮರಣದ ಮೂಲಕ ಆತನೊಂದಿಗೆ ಸಮಾಧಾನವಾಗಿರಲಾಗಿ ಆತನ ಕೂಡ ಸಮಾಧಾನವಾದ ನಮಗೆ ಆ ಮಗನಿಗಿರುವ ಜೀವದಿಂದ ರಕ್ಷಣೆಯಾಗುವದು ಮತ್ತೂ ನಿಶ್ಚಯವಲ್ಲವೇ.",
			"11": "<sup>11</sup> ಇಷ್ಟು ಮಾತ್ರವೇ ಅಲ್ಲದೆ ನಾವು ನಮ್ಮ ಕರ್ತನಾದ ಯೇಸು ಕ್ರಿಸ್ತನ ಮೂಲಕ ಸಮಾಧಾನಸ್ಥಿತಿ ಹೊಂದಿದವರಾಗಿ ಆತನ ಮುಖಾಂತರ ದೇವರಲ್ಲಿ ಹರ್ಷಗೊಳ್ಳುತ್ತೇವೆ.",
			"12": "<sup>12</sup> ಈ ವಿಷಯ ಹೇಗಂದರೆ - ಒಬ್ಬ ಮನುಷ್ಯನಿಂದಲೇ ಪಾಪವೂ ಪಾಪದಿಂದ ಮರಣವೂ ಲೋಕದೊಳಗೆ ಸೇರಿದವು; ಎಲ್ಲರು ಪಾಪ ಮಾಡಿದ್ದರಿಂದ ಮರಣವು ಹೀಗೆ ಎಲ್ಲರಲ್ಲಿಯೂ ವ್ಯಾಪಿಸಿತು.",
			"13": "<sup>13</sup> ಧರ್ಮಶಾಸ್ತ್ರ ಉಂಟಾಗುವದಕ್ಕೆ ಮುಂಚೆಯೇ ಪಾಪವು ಲೋಕದಲ್ಲಿತ್ತು; ಆದರೆ ಧರ್ಮಶಾಸ್ತ್ರವಿಲ್ಲದಿರುವಾಗ ಪಾಪವು ಲೆಕ್ಕಕ್ಕೆ ಬರುವದಿಲ್ಲ.",
			"14": "<sup>14</sup> ಆದರೂ ಆದಾಮನ ಕಾಲದಿಂದ ಮೋಶೆಯ ಕಾಲದವರೆಗೂ ಮರಣದ ಆಳಿಕೆಯು ನಡೆಯುತ್ತಿತ್ತು. ಆದಾಮನು ದೇವಾಜ್ಞೆಮೀರಿ ಮಾಡಿದ ಪಾಪಕ್ಕೆ ಸಮಾನವಾದ ಪಾಪವನ್ನು ಮಾಡದೆ ಇದ್ದವರ ಮೇಲೆಯೂ ಅದು ನಡೆಯುತ್ತಿತ್ತು. ಆ ಆದಾಮನು ಬರಬೇಕಾದ ಒಬ್ಬಾತನಿಗೆ ಸೂಚಕಪುರುಷನಾಗಿದ್ದಾನೆ.",
			"15": "<sup>15</sup> ಆದರೆ ಆ ಅಪರಾಧದ ಸಂಗತಿಗೂ ದೇವರ ಕೃಪಾದಾನದ ಸಂಗತಿಗೂ ಬಹಳ ಹೆಚ್ಚುಕಡಿಮೆ ಉಂಟು. ಹೇಗಂದರೆ ಆ ಒಬ್ಬನ ಅಪರಾಧದಿಂದ ಎಲ್ಲಾ ಮನುಷ್ಯರು ಸತ್ತದ್ದು ನಿಶ್ಚಯವಾಗಿರಲಾಗಿ ದೇವರ ಕೃಪೆಯೂ ಯೇಸು ಕ್ರಿಸ್ತನೆಂಬ ಈ ಒಬ್ಬ ಪುರುಷನ ಕೃಪೆಯಿಂದ ಸಿಕ್ಕುವ ವರವೂ ಎಲ್ಲಾ ಮನುಷ್ಯರಿಗೆ ಹೇರಳವಾಗಿ ಸಿದ್ಧವಾಗಿರುವದು ಮತ್ತೂ ನಿಶ್ಚಯವಲ್ಲವೇ.",
			"16": "<sup>16</sup> ಇದಲ್ಲದೆ ಪಾಪಮಾಡಿದ ಒಬ್ಬನಿಂದಲೇ ದುಷ್ಫಲ ಬಂದಂತೆ ಈ ವರವು ಬರಲಿಲ್ಲ; ಹೇಗಂದರೆ ಒಬ್ಬನೇ ಮನುಷ್ಯನ ಪಾಪದ ದೆಸೆಯಿಂದ ಸಾಯಬೇಕೆಂಬ ನಿರ್ಣಯವು ಉಂಟಾಯಿತು, ಆದರೆ ದೇವರ ಕೃಪಾದಾನವು ಅನೇಕರ ಅಪರಾಧಗಳ ಕಾರಣದಿಂದ ಉಂಟಾಗಿ ಅವರನ್ನು ನೀತಿವಂತರೆಂದು ನಿರ್ಣಯಿಸುವಂಥದಾಗಿದೆ.",
			"17": "<sup>17</sup> ಒಬ್ಬನು ಮಾಡಿದ ಅಪರಾಧದಿಂದ ಮರಣವು ಉಂಟಾಗಿ ಆ ಒಬ್ಬನ ದೆಸೆಯಿಂದಲೇ ಆಳುವದಾದರೆ ದೇವರು ಧಾರಾಳವಾಗಿ ಅನುಗ್ರಹಿಸುವ ಕೃಪಾದಾನವನ್ನೂ ನೀತಿಯೆಂಬ ವರವನ್ನೂ ಹೊಂದಿದವರು ಯೇಸು ಕ್ರಿಸ್ತನೆಂಬ ಈ ಒಬ್ಬ ಪುರುಷನ ಮೂಲಕ ಜೀವಭರಿತರಾಗಿ ಆಳುವದು ಮತ್ತೂ ನಿಶ್ಚಯವಲ್ಲವೇ.",
			"18": "<sup>18</sup> ಹೀಗಿರಲಾಗಿ ಒಂದೇ ಅಪರಾಧದ ಮೂಲಕ ಎಲ್ಲಾ ಮನುಷ್ಯರಿಗೆ ಸಾಯಬೇಕೆಂಬ ನಿರ್ಣಯವು ಹೇಗೆ ಉಂಟಾಯಿತೋ ಹಾಗೆಯೇ ಒಂದೇ ಸತ್ಕಾರ್ಯದಿಂದ ಎಲ್ಲಾ ಮನುಷ್ಯರಿಗೆ ನೀತಿವಂತರೆಂಬ ನಿರ್ಣಯವು ಉಂಟಾಗಿ ಜೀವವನ್ನು ಫಲಿಸುತ್ತದೆ.",
			"19": "<sup>19</sup> ಒಬ್ಬನ ಅವಿಧೇಯತ್ವದಿಂದ ಎಲ್ಲರೂ ಹೇಗೆ ಪಾಪಿಗಳಾದರೋ ಹಾಗೆಯೇ ಒಬ್ಬನ ವಿಧೇಯತ್ವದಿಂದ ಎಲ್ಲರೂ ನೀತಿವಂತರಾಗುವರು.",
			"20": "<sup>20</sup> ಅಪರಾಧಗಳು ಹೆಚ್ಚಾಗಿ ಕಂಡುಬರುವ ಹಾಗೆ ಧರ್ಮಶಾಸ್ತ್ರವು ಮಧ್ಯೆ ಬಂತು. ಆದರೆ ಪಾಪವು ಹೆಚ್ಚಿದಲ್ಲೇ ಕೃಪೆಯು ಎಷ್ಟೋ ಹೆಚ್ಚಾಗಿ ಪ್ರಬಲವಾಯಿತು.",
			"21": "<sup>21</sup> ಹೀಗೆ ಪಾಪವು ಮರಣವನ್ನುಂಟುಮಾಡುತ್ತಾ ಅಧಿಕಾರವನ್ನು ನಡಿಸಿದ ಹಾಗೆಯೇ ದೇವರ ಕೃಪೆಯು ನಮ್ಮ ಕರ್ತನಾದ ಯೇಸು ಕ್ರಿಸ್ತನ ಮೂಲಕ ನೀತಿಯನ್ನು ಕೊಟ್ಟು ನಿತ್ಯಜೀವವನ್ನುಂಟುಮಾಡುತ್ತಾ ಅಧಿಕಾರವನ್ನು ನಡಿಸುವದು."
		},
		"6": {
			"1": "<sup>1</sup> ಹಾಗಾದರೆ ಏನು ಹೇಳೋಣ? ದೇವರ ಕೃಪೆಯು ಹೆಚ್ಚಲಿ ಎಂದು ನಾವು ಪಾಪದಲ್ಲಿ ಇನ್ನೂ ಇರಬೇಕೋ? ಎಂದಿಗೂ ಇರಬಾರದು.",
			"2": "<sup>2</sup> ಪಾಪದ ಪಾಲಿಗೆ ಸತ್ತವರಾದ ನಾವು ಇನ್ನೂ ಅದರಲ್ಲೇ ಬದುಕುವದು ಹೇಗೆ?",
			"3": "<sup>3</sup> ಕ್ರಿಸ್ತ ಯೇಸುವಿನಲ್ಲಿ ಸೇರುವದಕ್ಕೆ ದೀಕ್ಷಾಸ್ನಾನಮಾಡಿಸಿಕೊಂಡವರಾದ ನಾವೆಲ್ಲರು ಆತನ ಮರಣದಲ್ಲಿ ಪಾಲುಗಾರರಾಗುವದಕ್ಕೆ ದೀಕ್ಷಾಸ್ನಾನ ಮಾಡಿಸಿಕೊಂಡೆವೆಂದು ನಿಮಗೆ ತಿಳಿಯದೋ?",
			"4": "<sup>4</sup> ಹೀಗಿರಲಾಗಿ ನಾವು ದೀಕ್ಷಾಸ್ನಾನ ಮಾಡಿಸಿಕೊಂಡಾಗ ಆತನ ಮರಣದಲ್ಲಿ ಪಾಲುಗಾರರಾಗಿ ಆತನೊಂದಿಗೆ ಹೂಣಲ್ಪಟ್ಟೆವು. ಆದದರಿಂದ ಕ್ರಿಸ್ತನು ಸತ್ತು ತಂದೆಯ ಮಹಿಮೆಯಿಂದ ಜೀವಿತನಾಗಿ ಎಬ್ಬಿಸಲ್ಪಟ್ಟಂತೆಯೇ ನಾವು ಕೂಡ ಜೀವದಿಂದೆದ್ದು ಹೊಸಬರಾಗಿ ನಡಕೊಳ್ಳಬೇಕು.",
			"5": "<sup>5</sup> ಹೇಗಂದರೆ ನಾವು ಆತನ ಮರಣಕ್ಕೆ ಸದೃಶವಾದ ಮರಣವನ್ನು ಹೊಂದಿ ಆತನಲ್ಲಿ ಐಕ್ಯವಾಗಿದ್ದರೆ ಆತನ ಪುನರುತ್ಥಾನಕ್ಕೆ ಸದೃಶವಾದ ಪುನರುತ್ಥಾನವನ್ನೂ ಹೊಂದಿ ಆತನಲ್ಲಿ ಐಕ್ಯವಾಗುವೆವು.",
			"6": "<sup>6</sup> ಪಾಪಾಧೀನಸ್ವಭಾವವು ನಾಶವಾಗಿ ನಾವು ಇನ್ನೂ ಪಾಪದ ವಶದಲ್ಲಿರದಂತೆ ನಮ್ಮ ಪೂರ್ವಸ್ವಭಾವವು ಕ್ರಿಸ್ತನ ಕೂಡ ಶಿಲುಬೆಗೆ ಹಾಕಲ್ಪಟ್ಟಿತೆಂದು ತಿಳಿದಿದ್ದೇವೆ.",
			"7": "<sup>7</sup> ಸತ್ತವನು ಪಾಪದ ವಶದಿಂದ ಬಿಡುಗಡೆ ಹೊಂದಿದ್ದಾನಷ್ಟೆ.",
			"8": "<sup>8</sup> ಇದಲ್ಲದೆ ನಾವು ಕ್ರಿಸ್ತನೊಡನೆ ಸತ್ತ ಪಕ್ಷದಲ್ಲಿ ಆತನೊಡನೆ ಜೀವಿಸುವೆವೆಂದು ನಂಬುತ್ತೇವೆ.",
			"9": "<sup>9</sup> ಯಾಕಂದರೆ ಕ್ರಿಸ್ತನು ಸತ್ತ ಮೇಲೆ ಜೀವಿತನಾಗಿ ಎದ್ದು ಬಂದದರಿಂದ ಆತನು ಇನ್ನು ಮುಂದೆ ಸಾಯತಕ್ಕವನಲ್ಲ,",
			"10": "<sup>10</sup> ಮರಣವು ಇನ್ನೂ ಆತನನ್ನು ಆಳುವದಿಲ್ಲವೆಂದು ನಮಗೆ ತಿಳಿದದೆ. ಆತನು ಸತ್ತದ್ದು ಒಂದೇ ಸಾರಿ, ಅದು ಪಾಪದ ಪಾಲಿಗೆ; ಆತನು ಜೀವಿಸುವದು ದೇವರಿಗಾಗಿಯೇ.",
			"11": "<sup>11</sup> ಅದೇ ಪ್ರಕಾರ ನೀವು ಸಹ ನಿಮ್ಮನ್ನು ಕ್ರಿಸ್ತ ಯೇಸುವಿನ ಐಕ್ಯದಿಂದ ಪಾಪದ ಪಾಲಿಗೆ ಸತ್ತವರೂ ದೇವರಿಗಾಗಿ ಜೀವಿಸುವವರೂ ಎಂದು ಎಣಿಸಿಕೊಳ್ಳಿರಿ.",
			"12": "<sup>12</sup> ಹೀಗಿರುವದರಿಂದ ಸಾಯತಕ್ಕ ನಿಮ್ಮ ದೇಹದ ಮೇಲೆ ಪಾಪಗಳನ್ನು ಆಳಗೊಡಿಸಿ ನೀವು ದೇಹದ ದುರಾಶೆಗಳಿಗೆ ಒಳಪಡಬೇಡಿರಿ.",
			"13": "<sup>13</sup> ಇದು ಮಾತ್ರವಲ್ಲದೆ ನೀವು ನಿಮ್ಮ ಅಂಗಗಳನ್ನು ಪಾಪಕ್ಕೆ ಒಪ್ಪಿಸುವವರಾಗಿದ್ದು ದುಷ್ಟತ್ವವನ್ನು ನಡಿಸುವ ಸಾಧನಗಳಾಗಮಾಡಬೇಡಿರಿ; ಆದರೆ ಸತ್ತವರೊಳಗಿಂದ ಜೀವಿತರಾಗಿರಲಾಗಿ ನಿಮ್ಮನ್ನು ದೇವರಿಗೆ ಒಪ್ಪಿಸಿಕೊಟ್ಟು ನಿಮ್ಮ ಅಂಗಗಳನ್ನು ನೀತಿ ಕೃತ್ಯಗಳನ್ನು ನಡಿಸುವ ಸಾಧನಗಳನ್ನಾಗಿ ಆತನಿಗೆ ಸಮರ್ಪಿಸಿರಿ.",
			"14": "<sup>14</sup> ಯಾಕಂದರೆ ಪಾಪವು ನಿಮ್ಮ ಮೇಲೆ ಅಧಿಕಾರನಡಿಸದು; ನೀವು ಧರ್ಮಶಾಸ್ತ್ರಕ್ಕೆ ಅಧೀನರಲ್ಲ, ಕೃಪೆಗೆ ಅಧೀನರಾಗಿದ್ದೀರಷ್ಟೆ.",
			"15": "<sup>15</sup> ಹಾಗಾದರೇನು? ನಾವು ಧರ್ಮಶಾಸ್ತ್ರಾಧೀನರಲ್ಲ, ಕೃಪಾಧೀನರಾಗಿದ್ದೇವೆಂದು ಪಾಪವನ್ನು ಮಾಡಬಹುದೋ? ಎಂದಿಗೂ ಮಾಡಬಾರದು.",
			"16": "<sup>16</sup> ನೀವು ಯಾವನಿಗೆ ದಾಸರಂತೆ ವಿಧೇಯರಾಗುತ್ತೇವೆಂದು ನಿಮ್ಮನ್ನು ಒಪ್ಪಿಸಿಕೊಡುತ್ತೀರೋ ಆ ಯಜಮಾನನಿಗೆ ದಾಸರಾಗಿಯೇ ಇರುವಿರೆಂಬದು ನಿಮಗೆ ಗೊತ್ತಿಲ್ಲವೋ? ಪಾಪಕ್ಕೆ ದಾಸರಾದರೆ ಮರಣವೇ ಫಲ; [ನಂಬಿಕೆಯೆಂಬ] ವಿಧೇಯತ್ವಕ್ಕೆ ದಾಸರಾದರೆ ನೀತಿಯೇ ಫಲ.",
			"17": "<sup>17</sup> ಆದರೆ ನೀವು ಮುಂಚೆ ಪಾಪಕ್ಕೆ ದಾಸರಾಗಿದ್ದರೂ ನಿಮಗೆ ಕಲಿಸಲ್ಪಟ್ಟ ಬೋಧನೆಗೆ ನೀವು ಮನಃಪೂರ್ವಕವಾಗಿ ಅಧೀನರಾದದರಿಂದಲೂ",
			"18": "<sup>18</sup> ಪಾಪದಿಂದ ಬಿಡುಗಡೆಯನ್ನು ಹೊಂದಿ ನೀತಿಗೆ ದಾಸರಾದದರಿಂದಲೂ ದೇವರಿಗೆ ಸ್ತೋತ್ರವಾಗಲಿ.",
			"19": "<sup>19</sup> ನಿಮ್ಮ ನರಭಾವದ ಬಲಹೀನತೆಯ ದೆಸೆಯಿಂದ ಲೋಕರೀತಿಯಾಗಿ ಮಾತಾಡುತ್ತೇನೆ. ಅಧರ್ಮಮಾಡುವದಕ್ಕಾಗಿ ನಿಮ್ಮ ಅಂಗಗಳನ್ನು ಬಂಡುತನಕ್ಕೂ ಅಧರ್ಮಕ್ಕೂ ದಾಸರನ್ನಾಗಿ ಒಪ್ಪಿಸಿಕೊಟ್ಟಿದ್ದಂತೆಯೇ ಈಗ ಪರಿಶುದ್ಧರಾಗುವದಕ್ಕಾಗಿ ನಿಮ್ಮ ಅಂಗಗಳನ್ನು ನೀತಿಗೆ ದಾಸರನ್ನಾಗಿ ಒಪ್ಪಿಸಿಕೊಡಿರಿ.",
			"20": "<sup>20</sup> ನೀವು ಪಾಪಕ್ಕೆ ದಾಸರಾಗಿದ್ದಾಗ ನೀತಿಯ ಹಂಗಿನವರಾಗಿದ್ದಿಲ್ಲ.",
			"21": "<sup>21</sup> ಆದರೆ ನೀವು ಆಗ ಮಾಡಿದ ಕೃತ್ಯಗಳಿಂದ ನಿಮಗುಂಟಾದ ಫಲವೇನು? ಈಗ ಅವುಗಳ ವಿಷಯದಲ್ಲಿ ನಿಮಗೆ ನಾಚಿಕೆಯುಂಟಷ್ಟೆ. ಕಡೆಗೆ ಅವುಗಳಿಂದ ಬರುವದು ಮರಣವಲ್ಲವೇ.",
			"22": "<sup>22</sup> ಈಗಲಾದರೋ ನೀವು ಪಾಪದಿಂದ ಬಿಡುಗಡೆಯನ್ನು ಹೊಂದಿ ದೇವರಿಗೆ ದಾಸರಾದದರಿಂದ ಪರಿಶುದ್ಧರಾಗುವದೇ ನಿಮಗೆ ಫಲ; ಕಡೆಗೆ ದೊರೆಯುವಂಥದು ನಿತ್ಯಜೀವ.",
			"23": "<sup>23</sup> ಯಾಕಂದರೆ ಪಾಪವು ಕೊಡುವ ಸಂಬಳ ಮರಣ; ದೇವರ ಉಚಿತಾರ್ಥವರವು ನಮ್ಮ ಕರ್ತನಾದ ಕ್ರಿಸ್ತ ಯೇಸುವಿನಲ್ಲಿರುವ ನಿತ್ಯಜೀವ."
		},
		"7": {
			"1": "<sup>1</sup> ಸಹೋದರರೇ, ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ತಿಳಿದವರಿಗೆ ನಾನು ಹೇಳುವದೇನಂದರೆ - ಒಬ್ಬ ಮನುಷ್ಯನು ಜೀವದಿಂದಿರುವ ತನಕ ಮಾತ್ರ ಅವನ ಮೇಲೆ ಧರ್ಮಶಾಸ್ತ್ರದ ಅಧಿಕಾರವು ನಡೆಯುತ್ತದೆಂಬದು ನಿಮಗೆ ಗೊತ್ತಿಲ್ಲವೇ?",
			"2": "<sup>2</sup> ಅದಕ್ಕೆ ದೃಷ್ಟಾಂತ - ಗಂಡನು ಜೀವದಿಂದಿರುವ ತನಕ ಹೆಂಡತಿಯು ನ್ಯಾಯದ ಪ್ರಕಾರ ಅವನಿಗೆ ಬದ್ಧಳಾಗಿರುವಳು; ಗಂಡನು ಸತ್ತರೆ ಗಂಡನ ಹಂಗು ಆಕೆಗೆ ತಪ್ಪುವದು.",
			"3": "<sup>3</sup> ಹೀಗಿರಲಾಗಿ ಗಂಡನು ಜೀವದಿಂದಿರುವಾಗ ಆಕೆ ಬೇರೊಬ್ಬನನ್ನು ಸೇರಿದರೆ ವ್ಯಭಿಚಾರಿಣಿ ಎನಿಸಿಕೊಳ್ಳುವಳು. ಆದರೆ ಗಂಡನು ಸತ್ತ ಮೇಲೆ ಅವನ ಹಂಗು ತಪ್ಪಿದ್ದರಿಂದ ಆಕೆ ಮತ್ತೊಬ್ಬ ಗಂಡನನ್ನು ಮಾಡಿಕೊಂಡರೂ ವ್ಯಭಿಚಾರಿಣಿಯಲ್ಲ.",
			"4": "<sup>4</sup> ಹಾಗೆಯೇ ನನ್ನ ಸಹೋದರರೇ, ನೀವು ಸಹ ಕ್ರಿಸ್ತನ ದೇಹದ ಮೂಲಕವಾಗಿ ಧರ್ಮಶಾಸ್ತ್ರದ ಪಾಲಿಗೆ ಸತ್ತಿರಿ. ದೇವರಿಗೆ ಫಲವಾಗುವದಕ್ಕಾಗಿ ಮತ್ತೊಬ್ಬನನ್ನು ಅಂದರೆ ಸತ್ತು ಜೀವಿತನಾಗಿ ಎದ್ದಾತನನ್ನು ಸೇರಿಕೊಂಡಿರಿ.",
			"5": "<sup>5</sup> ನಾವು ಶರೀರಾಧೀನಸ್ವಭಾವವನ್ನು ಅನುಸರಿಸುತ್ತಿದ್ದಾಗ ದುರಾಶೆಗಳು ಧರ್ಮಶಾಸ್ತ್ರದಿಂದಲೇ ಪ್ರೇರಿತವಾಗಿ ನಮ್ಮ ಅಂಗಗಳಲ್ಲಿ ಯತ್ನೈಸುತ್ತಾ ಮರಣಕ್ಕೆ ಫಲವನ್ನು ಹುಟ್ಟಿಸುತ್ತಿದ್ದವು.",
			"6": "<sup>6</sup> ಈಗಲಾದರೋ ನಮ್ಮನ್ನು ವಶಮಾಡಿಕೊಂಡಿದ್ದ ಧರ್ಮಶಾಸ್ತ್ರದ ಪಾಲಿಗೆ ನಾವು ಸತ್ತಕಾರಣ ಆ ಧರ್ಮಶಾಸ್ತ್ರದಿಂದ ವಿಮುಕ್ತರಾಗಿದ್ದೇವೆ. ಹೀಗಿರಲಾಗಿ ಶಾಸ್ತ್ರದ ಹೊರಗಣ ಅರ್ಥಕ್ಕೆ ಅನುಕೂಲವಾದ ಹಳೇ ರೀತಿಯಲ್ಲಿ ನಾವು ದೇವರನ್ನು ಸೇವಿಸುವವರಲ್ಲ. ಪವಿತ್ರಾತ್ಮಪ್ರೇರಿತವಾದ ಹೊಸ ರೀತಿಯಲ್ಲಿ ಆತನನ್ನು ಸೇವಿಸುವವರಾಗಿದ್ದೇವೆ.",
			"7": "<sup>7</sup> ಹಾಗಾದರೆ ಏನು ಹೇಳೋಣ? ಧರ್ಮಶಾಸ್ತ್ರವು ಪಾಪಸ್ವರೂಪವೋ? ಎಂದಿಗೂ ಅಲ್ಲ. ಧರ್ಮಶಾಸ್ತ್ರದಿಂದಲೇ ಹೊರತು ಪಾಪವೆಂಬದು ಏನೋ ನನಗೆ ಗೊತ್ತಾಗುತ್ತಿರಲಿಲ್ಲ. ದೃಷ್ಟಾಂತವಾಗಿ, ಆಶಿಸಬಾರದೆಂದು ಧರ್ಮಶಾಸ್ತ್ರವು ಹೇಳದಿದ್ದರೆ ದುರಾಶೆಯೆಂದರೆ ಏನೋ ನನಗೆ ತಿಳಿಯುತ್ತಿದ್ದಿಲ್ಲ.",
			"8": "<sup>8</sup> ಆದರೆ ಪಾಪವು ಈ ಆಜ್ಞೆಯಿಂದ ಅನುಕೂಲ ಹೊಂದಿ ಸಕಲವಿಧವಾದ ದುರಾಶೆಗಳನ್ನು ನನ್ನಲ್ಲಿ ಹುಟ್ಟಿಸಿತು. ಧರ್ಮಶಾಸ್ತ್ರವು ಇಲ್ಲದಿರುವಾಗ ಪಾಪವು ಅಚೇತನವಾಗಿದೆ.",
			"9": "<sup>9</sup> ಮೊದಲು ನಾನು ಧರ್ಮಶಾಸ್ತ್ರವಿಲ್ಲದವನಾಗಿದ್ದು ಜೀವದಿಂದಿದ್ದೆನು. ಆಜ್ಞೆಯು ಬಂದಾಗ ಪಾಪಕ್ಕೆ ಜೀವ ಬಂತು, ನಾನು ಸತ್ತೆನು.",
			"10": "<sup>10</sup> ಜೀವಿಸುವದಕ್ಕಾಗಿ ಕೊಟ್ಟಿರುವ ಆಜ್ಞೆಯೇ ಮರಣಕ್ಕೆ ಕಾರಣವಾಯಿತೆಂದು ನನಗೆ ಕಂಡು ಬಂತು.",
			"11": "<sup>11</sup> ಹೇಗಂದರೆ ಪಾಪವು ಆಜ್ಞೆಯಿಂದ ಅನುಕೂಲಹೊಂದಿ ನನ್ನನ್ನು ವಂಚಿಸಿ ಆ ಆಜ್ಞೆಯ ಮೂಲಕವೇ ನನ್ನನ್ನು ಕೊಂದಿತು.",
			"12": "<sup>12</sup> ಹೀಗಿರಲಾಗಿ ಧರ್ಮಶಾಸ್ತ್ರವು ಪರಿಶುದ್ಧವಾದದ್ದು. ಮತ್ತು ಆಜ್ಞೆಯು ಪರಿಶುದ್ಧವೂ ನ್ಯಾಯವೂ ಹಿತವೂ ಆಗಿರುವಂಥದು ಸರಿ.",
			"13": "<sup>13</sup> ಹಾಗಾದರೆ ಹಿತವಾದದ್ದು ನನಗೆ ಮರಣಕ್ಕೆ ಕಾರಣವಾಯಿತೋ? ಹಾಗೆ ಎಂದಿಗೂ ಹೇಳಬಾರದು. ಪಾಪವೇ ಮರಣಕ್ಕೆ ಕಾರಣವಾದದ್ದು; ಅದು ಹಿತವಾದದರ ಮೂಲಕ ನನಗೆ ಮರಣವನ್ನು ಉಂಟುಮಾಡಿದ್ದರಿಂದ ಪಾಪವೇ ಎಂದು ಕಾಣಿಸಿಕೊಂಡಿತು; ಮತ್ತು ಆಜ್ಞೆಯ ಮೂಲಕ ಪಾಪವು ಕೇವಲ ಪಾಪಸ್ವರೂಪವೇ ಎಂದು ಕಾಣಬಂತು.",
			"14": "<sup>14</sup> ಧರ್ಮಶಾಸ್ತ್ರವು ಪಾರಮಾರ್ಥಿಕವಾದದ್ದೆಂದು ನಾವು ಬಲ್ಲೆವು. ಆದರೆ ನಾನು ದೇಹಧರ್ಮಕ್ಕೆ ಒಳಗಾದವನೂ ಪಾಪದ ಸ್ವಾಧೀನದಲ್ಲಿರುವದಕ್ಕೆ ಮಾರಲ್ಪಟ್ಟವನೂ ಆಗಿದ್ದೇನೆ.",
			"15": "<sup>15</sup> ಹೇಗಂದರೆ ನಾನು ಮಾಡುವದು ನನಗೇ ಗೊತ್ತಿಲ್ಲ; ಯಾವದನ್ನು ಮಾಡಬೇಕೆಂದು ನಾನು ಇಚ್ಫೈಸುತ್ತೇನೋ ಅದನ್ನು ನಡಿಸದೆ ನನಗೆ ಅಸಹ್ಯವಾದದ್ದನ್ನು ಮಾಡುತ್ತೇನೆ.",
			"16": "<sup>16</sup> ಆದರೆ ನಾನು ಮಾಡುವಂಥದಕ್ಕೆ ನನ್ನ ಮನಸ್ಸು ಒಪ್ಪದಿದ್ದರೆ ಧರ್ಮಶಾಸ್ತ್ರವು ಉತ್ತಮವಾದದ್ದೆಂದು ಒಪ್ಪಿಕೊಂಡ ಹಾಗಾಯಿತು.",
			"17": "<sup>17</sup> ಹೀಗಿರಲಾಗಿ ಆ ಅಸಹ್ಯವಾದದ್ದನ್ನು ಮಾಡುವವನು ಇನ್ನು ನಾನಲ್ಲ, ನನ್ನಲ್ಲಿ ನೆಲೆಗೊಂಡಿರುವ ಪಾಪವೇ ಅದನ್ನು ಮಾಡುತ್ತದೆ.",
			"18": "<sup>18</sup> ನನ್ನಲ್ಲಿ ಅಂದರೆ ನನ್ನ ಶರೀರಾಧೀನಸ್ವಭಾವದಲ್ಲಿ ಒಳ್ಳೇದೇನೂ ವಾಸವಾಗಿಲ್ಲವೆಂದು ನನಗೆ ತಿಳಿದದೆ. ಒಳ್ಳೇದನ್ನು ಮಾಡುವದಕ್ಕೆ ನನಗೇನೋ ಮನಸ್ಸುಂಟು; ಆದರೆ ಅದನ್ನು ಮಾಡುವದು ನನ್ನಿಂದಾಗದು.",
			"19": "<sup>19</sup> ನಾನು ಮೆಚ್ಚುವ ಒಳ್ಳೇ ಕಾರ್ಯವನ್ನು ಮಾಡದೆ ಮೆಚ್ಚದಿರುವ ಕೆಟ್ಟ ಕಾರ್ಯವನ್ನೇ ಮಾಡುವವನಾಗಿದ್ದೇನೆ.",
			"20": "<sup>20</sup> ಮಾಡಬಾರದೆನ್ನುವ ಕೆಲಸವನ್ನು ನಾನು ಮಾಡಿದರೆ ಅದನ್ನು ಮಾಡಿದವನು ಇನ್ನು ನಾನಲ್ಲ, ನನ್ನಲ್ಲಿ ನೆಲೆಗೊಂಡಿರುವ ಪಾಪವೇ ಅದನ್ನು ಮಾಡಿತು.",
			"21": "<sup>21</sup> ಹೀಗಿರಲಾಗಿ ಒಳ್ಳೇದನ್ನು ಮಾಡಬೇಕೆಂದಿರುವ ನನಗೆ ಕೆಟ್ಟದ್ದೇ ಸಿದ್ಧವಾಗಿದೆಯೆಂಬ ನಿಯಮ ನನಗೆ ಕಾಣಬರುತ್ತದೆ.",
			"22": "<sup>22</sup> ಅಂತರಾತ್ಮದೊಳಗೆ ದೇವರ ನಿಯಮದಲ್ಲಿ ಆನಂದ ಪಡುವವನಾಗಿದ್ದೇನೆ. ಆದರೆ ನನ್ನ ಅಂಗಗಳಲ್ಲಿ ಬೇರೊಂದು ನಿಯಮ ಉಂಟೆಂದು ನೋಡುತ್ತೇನೆ.",
			"23": "<sup>23</sup> ಅದು ನನ್ನ ಮನಸ್ಸಿನಲ್ಲಿರುವ ನಿಯಮಕ್ಕೆ ವಿರುದ್ಧವಾಗಿ ಕಾದಾಡಿ ನನ್ನನ್ನು ಸೆರೆಹಿಡಿದು ನನ್ನ ಅಂಗಗಳಲ್ಲಿರುವ ಪಾಪದ ನಿಯಮಕ್ಕೆ ವಶಮಾಡುವಂಥದಾಗಿದೆ.",
			"24": "<sup>24</sup> ಅಯ್ಯೋ, ನಾನು ಎಂಥ ದುರವಸ್ಥೆಯಲ್ಲಿ ಬಿದ್ದ ಮನುಷ್ಯನು! ಇಂಥ ಮರಣಕ್ಕೆ ಒಳಗಾದ ಈ ದೇಹದಿಂದ ನನ್ನನ್ನು ಬಿಡಿಸುವವನು ಯಾರು?",
			"25": "<sup>25</sup> ಬಿಡಿಸುವವನು ಇದ್ದಾನೆಂಬದಕ್ಕಾಗಿ ನಮ್ಮ ಕರ್ತನಾದ ಯೇಸು ಕ್ರಿಸ್ತನ ಮೂಲಕ ದೇವರಿಗೆ ಸ್ತೋತ್ರ. ಹೀಗಿರಲಾಗಿ ನನ್ನಷ್ಟಕ್ಕೆ ನಾನೇ ಮನಸ್ಸಿನಿಂದ ದೇವರ ನಿಯಮಕ್ಕೆ ಆಳಾಗಿಯೂ ಶರೀರಭಾವದಿಂದ ಪಾಪವೆಂಬ ನಿಯಮಕ್ಕೆ ಆಳಾಗಿಯೂ ನಡಕೊಳ್ಳುವವನಾಗಿದ್ದೇನೆ."
		},
		"8": {
			"1": "<sup>1</sup> ಆದದರಿಂದ ಕ್ರಿಸ್ತ ಯೇಸುವಿನಲ್ಲಿ ಇರುವವರಿಗೆ ಅಪರಾಧನಿರ್ಣಯವು ಈಗ ಇಲ್ಲವೇ ಇಲ್ಲ.",
			"2": "<sup>2</sup> ಯಾಕಂದರೆ ಜೀವವನ್ನುಂಟುಮಾಡುವ ಪವಿತ್ರಾತ್ಮನಿಂದಾದ ನಿಯಮವು ನಿನ್ನನ್ನು ಪಾಪಮರಣಗಳಿಗೆ ಕಾರಣವಾದ ನಿಯಮದಿಂದ ಕ್ರಿಸ್ತ ಯೇಸುವಿನ ಮೂಲಕ ಬಿಡಿಸಿತು.",
			"3": "<sup>3</sup> ಧರ್ಮಶಾಸ್ತ್ರವು ನಮ್ಮ ಶರೀರಾಧೀನಸ್ವಭಾವದ ನಿಮಿತ್ತ ನಿರ್ಬಲವಾಗಿ ಯಾವ ಕೆಲಸವನ್ನು ಮಾಡಲಾರದೆ ಇತ್ತೋ ಅದನ್ನು ದೇವರೇ ಮಾಡಿದನು. ಏನಂದರೆ ಆತನು ಪಾಪನಿವಾರಣೆಗಾಗಿ ತನ್ನ ಮಗನನ್ನು ಪಾಪಾಧೀನವಾದ ನರಭಾವದ ರೂಪದಲ್ಲಿ ಕಳುಹಿಸಿಕೊಟ್ಟು ನರಭಾವದಲ್ಲಿಯೇ ಪಾಪಕ್ಕೆ ಮರಣದಂಡನೆಯನ್ನು ವಿಧಿಸಿದನು.",
			"4": "<sup>4</sup> ಹೀಗಿರಲು ಶರೀರಭಾವಕ್ಕೆ ಅನುಸಾರವಾಗಿ ನಡೆಯದೆ ಪವಿತ್ರಾತ್ಮಾನುಸಾರವಾಗಿ ನಡೆಯುವವರಾದ ನಮ್ಮಲ್ಲಿ ಧರ್ಮಶಾಸ್ತ್ರದ ನಿಯಮವು ನೆರವೇರುವದಕ್ಕೆ ಮಾರ್ಗವಾಯಿತು.",
			"5": "<sup>5</sup> ಶರೀರಭಾವವನ್ನು ಅನುಸರಿಸುವವರು ಅದಕ್ಕೆ ಸಂಬಂಧಪಟ್ಟವುಗಳ ಮೇಲೆ ಮನಸ್ಸಿಡುತ್ತಾರೆ. ಪವಿತ್ರಾತ್ಮನನ್ನನುಸರಿಸುವವರು ಪವಿತ್ರಾತ್ಮನಿಗೆ ಸಂಬಂಧಪಟ್ಟವುಗಳ ಮೇಲೆ ಮನಸ್ಸಿಡುತ್ತಾರೆ.",
			"6": "<sup>6</sup> ಶರೀರಭಾವದವುಗಳ ಮೇಲೆ ಮನಸ್ಸಿಡುವದು ಮರಣ; ಪವಿತ್ರಾತ್ಮನವುಗಳ ಮೇಲೆ ಮನಸ್ಸಿಡುವದು ಜೀವವೂ ಮನಶ್ಶಾಂತಿಯೂ ಆಗಿದೆ.",
			"7": "<sup>7</sup> ಯಾಕಂದರೆ ಶರೀರಭಾವದವುಗಳ ಮೇಲೆ ಮನಸ್ಸಿಡುವದು ದೇವರಿಗೆ ಶತ್ರುತ್ವವು; ಅಂಥ ಮನಸ್ಸು ದೇವರ ನಿಯಮಕ್ಕೆ ಒಳಪಡುವದೂ ಇಲ್ಲ, ಒಳಪಡುವದಕ್ಕಾಗುವದೂ ಇಲ್ಲ.",
			"8": "<sup>8</sup> ಶರೀರಭಾವಾಧೀನರು ದೇವರಿಗೆ ಮೆಚ್ಚಿಕೆಯಾಗಿರಲಾರರು.",
			"9": "<sup>9</sup> ನೀವಾದರೋ, ನಿಮ್ಮಲ್ಲಿ ದೇವರ ಆತ್ಮನು ವಾಸವಾಗಿರುವದಾದರೆ ಶರೀರ ಭಾವಾಧೀನರಲ್ಲ, ದೇವರಾತ್ಮನಿಗೆ ಅಧೀನರಾಗಿದ್ದೀರಿ. ಯಾವನಿಗಾದರೂ ಕ್ರಿಸ್ತನ ಆತ್ಮನು ಇಲ್ಲದಿದ್ದರೆ ಅವನು ಕ್ರಿಸ್ತನವನಲ್ಲ.",
			"10": "<sup>10</sup> ಆದರೆ ಕ್ರಿಸ್ತನು ನಿಮ್ಮಲ್ಲಿದ್ದರೆ ದೇಹವು ಪಾಪದ ದೆಸೆಯಿಂದ ಸತ್ತದ್ದಾಗಿದ್ದರೂ ಆತ್ಮವು ನೀತಿಯ ದೆಸೆಯಿಂದ ಜೀವಸ್ವರೂಪವಾಗಿದೆ.",
			"11": "<sup>11</sup> ಯೇಸುವನ್ನು ಸತ್ತವರೊಳಗಿಂದ ಜೀವಿತನಾಗಿ ಎಬ್ಬಿಸಿದಾತನ ಆತ್ಮನು ನಿಮ್ಮಲ್ಲಿ ವಾಸವಾಗಿದ್ದರೆ ಕ್ರಿಸ್ತ ಯೇಸುವನ್ನು ಸತ್ತವರೊಳಗಿಂದ ಎಬ್ಬಿಸಿದಾತನು ನಿಮ್ಮಲ್ಲಿ ವಾಸವಾಗಿರುವ ತನ್ನ ಆತ್ಮನ ಮೂಲಕ ನಿಮ್ಮ ಮರ್ತ್ಯದೇಹಗಳನ್ನು ಸಹ ಬದುಕಿಸುವನು.",
			"12": "<sup>12</sup> ಹೀಗಿರಲಾಗಿ ಸಹೋದರರೇ, ನಾವು ಹಂಗಿನಲ್ಲಿದ್ದೇವೆ; ಆದರೆ [ಪವಿತ್ರಾತ್ಮಾನುಸಾರ ನಡೆಯುವ ಹಂಗಿನಲ್ಲಿದ್ದೇವೆಯೇ ಹೊರತು] ಶರೀರಭಾವವನ್ನು ಅನುಸರಿಸಿ ಬದುಕಬೇಕೆಂಬ ಹಂಗಿನಲ್ಲಿಲ್ಲ.",
			"13": "<sup>13</sup> ನೀವು ಶರೀರಭಾವವನ್ನು ಅನುಸರಿಸಿ ಬದುಕಿದರೆ ಸಾಯುವದು ನಿಶ್ಚಯ; ನೀವು ಪವಿತ್ರಾತ್ಮನಿಂದ ದೇಹದ ದುರಭ್ಯಾಸಗಳನ್ನು ನಾಶ ಮಾಡುವದಾದರೆ ಜೀವಿಸುವಿರಿ.",
			"14": "<sup>14</sup> ಯಾರಾರು ದೇವರ ಆತ್ಮನಿಂದ ನಡಿಸಿಕೊಳ್ಳುತ್ತಾರೋ ಅವರು ದೇವರ ಮಕ್ಕಳು.",
			"15": "<sup>15</sup> ನೀವು ತಿರಿಗಿ ಭಯದಲ್ಲಿ ಬೀಳುವ ಹಾಗೆ ದಾಸನ ಭಾವವನ್ನು ಹೊಂದಿದವರಲ್ಲ, ಮಗನ ಭಾವವನ್ನು ಹೊಂದಿದವರಾಗಿದ್ದೀರಿ. ಈ ಭಾವದಿಂದ ನಾವು ದೇವರನ್ನು ಅಪ್ಪಾ, ತಂದೆಯೇ, ಎಂದು ಕೂಗುತ್ತೇವೆ.",
			"16": "<sup>16</sup> ನಾವು ದೇವರ ಮಕ್ಕಳಾಗಿದ್ದೇವೆಂಬದಕ್ಕೆ ಪವಿತ್ರಾತ್ಮನೇ ನಮ್ಮ ಆತ್ಮದೊಂದಿಗೆ ಸಾಕ್ಷಿ ಹೇಳುತ್ತಾನೆ.",
			"17": "<sup>17</sup> ಮಕ್ಕಳಾಗಿದ್ದರೆ ಬಾಧ್ಯರಾಗಿದ್ದೇವೆ; ದೇವರಿಗೆ ಬಾಧ್ಯರು, ಕ್ರಿಸ್ತನೊಂದಿಗೆ ಬಾಧ್ಯರು. ಹೇಗಂದರೆ ಕ್ರಿಸ್ತನಿಗೆ ಸಂಭವಿಸಿದ ಬಾಧೆಗಳಲ್ಲಿ ನಾವು ಪಾಲುಗಾರರಾಗುವದಾದರೆ ಆತನ ಮಹಿಮೆಯಲ್ಲಿಯೂ ಪಾಲುಗಾರರಾಗುವೆವು.",
			"18": "<sup>18</sup> ನಮಗೋಸ್ಕರ ಮುಂದಿನ ಕಾಲದಲ್ಲಿ ಪ್ರತ್ಯಕ್ಷವಾಗುವ ಮಹಿಮಪದವಿಯನ್ನು ಆಲೋಚಿಸಿ ಈಗಿನ ಕಾಲದ ಕಷ್ಟಗಳು ಅಲ್ಪವೇ ಸರಿ ಎಂದು ಎಣಿಸುತ್ತೇನೆ.",
			"19": "<sup>19</sup> ದೇವಪುತ್ರರ ಮಹಿಮೆಯು ಯಾವಾಗ ಪ್ರತ್ಯಕ್ಷವಾದೀತೆಂದು ಜಗತ್ತು ಬಹು ಲವಲವಿಕೆಯಿಂದ ಎದುರುನೋಡುತ್ತಿರುವದು.",
			"20": "<sup>20</sup> ಜಗತ್ತು ವ್ಯರ್ಥತ್ವಕ್ಕೆ ಒಳಗಾಯಿತು; ಹೀಗೆ ಒಳಗಾದದ್ದು ಸ್ವೇಚ್ಫೆಯಿಂದಲ್ಲ, ಅದನ್ನು ಒಳಪಡಿಸಿದವನ ಸಂಕಲ್ಪದಿಂದಲೇ.",
			"21": "<sup>21</sup> ಆದರೂ ಅದಕ್ಕೊಂದು ನಿರೀಕ್ಷೆಯುಂಟು; ಏನಂದರೆ ಆ ಜಗತ್ತು ಕೂಡ ನಾಶದ ವಶದಿಂದ ಬಿಡುಗಡೆಯಾಗಿ ದೇವರ ಮಕ್ಕಳ ಮಹಿಮೆಯುಳ್ಳ ವಿಮೋಚನೆಯಲ್ಲಿ ಪಾಲುಹೊಂದುವದೇ.",
			"22": "<sup>22</sup> ಜಗತ್ತೆಲ್ಲಾ ಇಂದಿನವರೆಗೂ ನರಳುತ್ತಾ ಪ್ರಸವ ವೇದನೆಪಡುತ್ತಾ ಇರುತ್ತದೆಂದು ನಾವು ಬಲ್ಲೆವು.",
			"23": "<sup>23</sup> ಇದು ಮಾತ್ರವಲ್ಲದೆ ಪ್ರಥಮ ಫಲವಾಗಿರುವ ಪವಿತ್ರಾತ್ಮವರವನ್ನು ಹೊಂದಿದ ನಾವಾದರೂ ದೇವಪುತ್ರರ ಪದವಿಯನ್ನು ಅಂದರೆ ದೇಹಕ್ಕೆ ಬರಬೇಕಾದ ವಿಮೋಚನೆಯನ್ನು ಎದುರುನೋಡುತ್ತಾ ನಮ್ಮೊಳಗೆ ನರಳುತ್ತೇವೆ.",
			"24": "<sup>24</sup> ನಾವು ರಕ್ಷಣೆಯನ್ನು ಹೊಂದಿದವರಾದರೂ ಇನ್ನು ಎದುರುನೋಡುವವರಾಗಿದ್ದೇವೆ. ಎದುರುನೋಡುವ ಪದವಿ ಪ್ರತ್ಯಕ್ಷವಾಗಿದ್ದರೆ ಎದುರುನೋಡುವದೇಕೆ? ಪ್ರತ್ಯಕ್ಷವಾದದ್ದನ್ನು ಎದುರುನೋಡುವದುಂಟೇ?",
			"25": "<sup>25</sup> ಆದರೆ ಕಾಣದಿರುವದನ್ನು ನಾವು ಎದುರುನೋಡುವವರಾಗಿದ್ದರೆ ತಾಳ್ಮೆಯಿಂದ ಕಾದುಕೊಂಡಿರುವೆವು.",
			"26": "<sup>26</sup> ಹಾಗೆ ಪವಿತ್ರಾತ್ಮನು ಸಹ ನಮ್ಮ ಅಶಕ್ತಿಯನ್ನು ನೋಡಿ ಸಹಾಯಮಾಡುತ್ತಾನೆ. ಹೇಗಂದರೆ ನಾವು ತಕ್ಕ ಪ್ರಕಾರ ಏನು ಬೇಡಿಕೊಳ್ಳಬೇಕೋ ನಮಗೆ ಗೊತ್ತಿಲ್ಲದ್ದರಿಂದ ಪವಿತ್ರಾತ್ಮನು ತಾನೇ ಮಾತಿಲ್ಲದಂಥ ನರಳಾಟದಿಂದ ನಮಗೋಸ್ಕರ ಬೇಡಿಕೊಳ್ಳುತ್ತಾನೆ.",
			"27": "<sup>27</sup> ಆದರೆ ಹೃದಯಗಳನ್ನು ಶೋಧಿಸಿ ನೋಡುವಾತನಿಗೆ ಪವಿತ್ರಾತ್ಮನ ಮನೋಭಾವವು ಏನೆಂದು ತಿಳಿದದೆ; ಆ ಆತ್ಮನು ದೇವರ ಚಿತ್ತಾನುಸಾರವಾಗಿ ದೇವಜನರಿಗೋಸ್ಕರ ಬೇಡಿಕೊಳ್ಳುವನೆಂದು ಆತನು ಬಲ್ಲನು.",
			"28": "<sup>28</sup> ಇದಲ್ಲದೆ ದೇವರ ಸಂಕಲ್ಪದ ಮೇರೆಗೆ ಕರೆಯಲ್ಪಟ್ಟು ಆತನನ್ನು ಪ್ರೀತಿಸುವವರ ಹಿತಕ್ಕಾಗಿ ಎಲ್ಲಾ ಕಾರ್ಯಗಳು ಅನುಕೂಲವಾಗುತ್ತವೆ ಎಂದು ನಮಗೆ ಗೊತ್ತದೆ.",
			"29": "<sup>29</sup> ಯಾಕಂದರೆ ದೇವರು ತನ್ನ ಮಗನಿಗೆ ಅನೇಕ ಮಂದಿ ಸಹೋದರರಿದ್ದು ಅವರಲ್ಲಿ ಆತನೇ ಹಿರಿಯನಾಗಿರಬೇಕೆಂದು ಉದ್ದೇಶಿಸಿ ತಾನು ಯಾರನ್ನು ತನ್ನವರೆಂದು ಮೊದಲು ತಿಳುಕೊಂಡನೋ ಅವರನ್ನು ತನ್ನ ಮಗನ ಸಾರೂಪ್ಯವುಳ್ಳವರಾಗುವದಕ್ಕೆ ಮೊದಲೇ ನೇಮಿಸಿದನು.",
			"30": "<sup>30</sup> ಮತ್ತು ಯಾರನ್ನು ಮೊದಲು ನೇಮಿಸಿದನೋ ಅವರನ್ನು ಕರೆದನು; ಯಾರನ್ನು ಕರೆದನೋ ಅವರನ್ನು ನೀತಿವಂತರೆಂದು ನಿರ್ಣಯಿಸಿದನು; ಯಾರನ್ನು ನೀತಿವಂತರೆಂದು ನಿರ್ಣಯಿಸಿದನೋ ಅವರನ್ನು ಮಹಿಮಪದವಿಗೆ ಸೇರಿಸಿದನು.",
			"31": "<sup>31</sup> ಹಾಗಾದರೆ ಈ ವಿಷಯಗಳಲ್ಲಿ ನಾವು ಏನು ಹೇಳೋಣ? ದೇವರು ನಮ್ಮ ಕಡೆ ಇದ್ದರೆ ನಮ್ಮನ್ನು ಎದುರಿಸುವವರು ಯಾರು?",
			"32": "<sup>32</sup> ಸ್ವಂತ ಮಗನನ್ನು ಉಳಿಸಿಕೊಳ್ಳದೆ ಆತನನ್ನು ನಮ್ಮೆಲ್ಲರಿಗೋಸ್ಕರ ಒಪ್ಪಿಸಿಕೊಟ್ಟನಲ್ಲಾ; ಮಗನನ್ನು ಕೊಟ್ಟ ಮೇಲೆ ಸಮಸ್ತವನ್ನೂ ನಮಗೆ ದಯಪಾಲಿಸದೆ ಇರುವನೇ?",
			"33": "<sup>33</sup> ದೇವರು ಆದುಕೊಂಡವರ ಮೇಲೆ ಯಾರು ತಪ್ಪುಹೊರಿಸಾರು? ದೇವರೇ ನಮ್ಮನ್ನು ನೀತಿವಂತರೆಂದು ನಿರ್ಣಯಿಸುವವನಾಗಿದ್ದಾನೆ.",
			"34": "<sup>34</sup> ಅಪರಾಧಿಗಳೆಂದು ನಿರ್ಣಯಿಸುವವರು ಯಾರು? ಕ್ರಿಸ್ತ ಯೇಸು ಮರಣವನ್ನು ಹೊಂದಿದ್ದಲ್ಲದೆ ಜೀವಿತನಾಗಿ ಎದ್ದು ದೇವರ ಬಲಗಡೆಯಲ್ಲಿದ್ದು ನಮಗೋಸ್ಕರ ಬೇಡುವವನಾಗಿದ್ದಾನೆ.",
			"35": "<sup>35</sup> ಕ್ರಿಸ್ತನ ಪ್ರೀತಿಯಿಂದ ನಮ್ಮನ್ನು ಅಗಲಿಸುವವರು ಯಾರು? ಕಷ್ಟವೋ ಸಂಕಟವೋ ಹಿಂಸೆಯೋ ಅನ್ನವಿಲ್ಲದಿರುವದೋ ವಸ್ತ್ರವಿಲ್ಲದಿರುವದೋ ಗಂಡಾಂತರವೋ ಖಡ್ಗವೋ?",
			"36": "<sup>36</sup> ದೇವರೇ, ನಾವು ನಿನ್ನ ನಿಮಿತ್ತ ದಿನವೆಲ್ಲಾ ಕೊಲೆಗೆ ಗುರಿಯಾಗಿದ್ದೇವೆ; ಜನರು ನಮ್ಮನ್ನು ಕೊಯ್ಗುರಿಗಳಂತೆ ಎಣಿಸಿದರು. ಎಂಬದಾಗಿ ಬರೆದಿರುವಂತೆ ಆಗುತ್ತದಲ್ಲಾ.",
			"37": "<sup>37</sup> ಆದರೆ ನಮ್ಮನ್ನು ಪ್ರೀತಿಸಿದಾತನ ಮೂಲಕವಾಗಿ ನಾವು ಈ ಎಲ್ಲಾ ವಿಷಯಗಳಲ್ಲಿ ಪೂರ್ಣ ಜಯಶಾಲಿಗಳಾಗುತ್ತೇವೆ.",
			"38": "<sup>38</sup> ಹೇಗಂದರೆ ಮರಣವಾಗಲಿ ಜೀವವಾಗಲಿ ದೇವದೂತರಾಗಲಿ ದುರಾತ್ಮಗಳಾಗಲಿ ಈಗಿನ ಸಂಗತಿಗಳಾಗಲಿ ಮುಂದಣ ಸಂಗತಿಗಳಾಗಲಿ",
			"39": "<sup>39</sup> ಮಹತ್ವಗಳಾಗಲಿ ಮೇಲಣ ಲೋಕವಾಗಲಿ ಕೆಳಗಣ ಲೋಕವಾಗಲಿ ಬೇರೆ ಯಾವ ಸೃಷ್ಟಿಯಾಗಲಿ ನಮ್ಮನ್ನು ನಮ್ಮ ಕರ್ತನಾದ ಯೇಸು ಕ್ರಿಸ್ತನಲ್ಲಿ ತೋರಿಬಂದ ದೇವರ ಪ್ರೀತಿಯಿಂದ ಅಗಲಿಸಲಾರವೆಂದು ನನಗೆ ನಿಶ್ಚಯ ಉಂಟು."
		},
		"9": {
			"1": "<sup>1</sup> ನನಗೆ ಮಹಾದುಃಖವೂ ನನ್ನ ಹೃದಯದಲ್ಲಿ ಎಡೆಬಿಡದೆ ವೇದನೆಯೂ ಉಂಟು.",
			"2": "<sup>2</sup> ಈ ಮಾತನ್ನು ಸುಳ್ಳಾಡದೆ ಕ್ರಿಸ್ತನ ಮುಂದೆ ಸತ್ಯವಾಗಿ ಹೇಳುತ್ತೇನೆ; ಪವಿತ್ರಾತ್ಮಾಧೀನವಾಗಿರುವ ನನ್ನ ಮನಸ್ಸೇ ಸಾಕ್ಷಿ.",
			"3": "<sup>3</sup> ಶರೀರಸಂಬಂಧವಾಗಿ ಸ್ವಜನರಾಗಿರುವ ನನ್ನ ಸಹೋದರರಿಗೋಸ್ಕರ ನಾನೇ ಸಾಧ್ಯವಾದರೆ ಕ್ರಿಸ್ತನನ್ನು ಅಗಲಿ ಶಾಪಗ್ರಸ್ತನಾಗುವದಕ್ಕೆ ಒಪ್ಪಿಕೊಂಡೇನು.",
			"4": "<sup>4</sup> ಅವರು ಇಸ್ರಾಯೇಲನ ವಂಶದವರೂ ದೇವರು ಪುತ್ರರಾಗಿ ಸ್ವೀಕರಿಸಿದವರೂ ಆಗಿದ್ದಾರೆ. ಅವರಲ್ಲಿ ದೇವರ ತೇಜಸ್ಸಾನ್ನಿಧ್ಯವು ಇದೆ; ಅವರ ಸಂಗಡ ದೇವರು ಒಡಂಬಡಿಕೆಗಳನ್ನು ಮಾಡಿಕೊಂಡನು; ಅವರಿಗೆ ಧರ್ಮಶಾಸ್ತ್ರವೂ ದೇವಾಲಯದಲ್ಲಿ ನಡೆಯುವ ಆರಾಧನೆಯೂ ವಾಗ್ದಾನಗಳೂ ಕೊಡೋಣವಾದವು.",
			"5": "<sup>5</sup> ಅಬ್ರಹಾಮ ಇಸಾಕ ಯಾಕೋಬರು ಅವರ ಪಿತೃಗಳೇ. ಕ್ರಿಸ್ತನು ಶರೀರಸಂಬಂಧವಾಗಿ ಅವರ ವಂಶದಲ್ಲಿಯೇ ಹುಟ್ಟಿದವನು, ಆತನು ಎಲ್ಲಾದರ ಮೇಲೆ ಒಡೆಯನೂ ನಿರಂತರ ಸ್ತುತಿ ಹೊಂದತಕ್ಕ ದೇವರೂ ಆಗಿದ್ದಾನೆ. ಆಮೆನ್.",
			"6": "<sup>6</sup> ದೇವರು ಹೇಳಿದ ಮಾತು ತಪ್ಪಿತೆಂದು ನಾವು ಭಾವಿಸಕೂಡದು. ಯಾಕಂದರೆ ಇಸ್ರಾಯೇಲ್ ವಂಶದಲ್ಲಿ ಹುಟ್ಟಿದವರೆಲ್ಲರೂ ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲ;",
			"7": "<sup>7</sup> ಹಾಗೆಯೇ ಅಬ್ರಹಾಮನ ವಂಶದಲ್ಲಿ ಹುಟ್ಟಿದ ಮಾತ್ರಕ್ಕೆ ಅವರೆಲ್ಲರು ಮಕ್ಕಳೆಂದು ಎಣಿಸಲ್ಪಡುವದಿಲ್ಲ. ಆದರೆ - ಇಸಾಕನಿಂದ ಹುಟ್ಟುವವರೇ ನಿನ್ನ ಸಂತತಿ ಅನ್ನಿಸಿಕೊಳ್ಳುವರು ಎಂದು ದೇವರು ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ಆ ಮಾತಿನ ಅರ್ಥವೇನಂದರೆ ಶರೀರಸಂಬಂಧವಾಗಿ ಹುಟ್ಟಿದವರೆಲ್ಲರೂ ದೇವರ ಮಕ್ಕಳಲ್ಲ, ವಾಗ್ದಾನಸಂಬಂಧವಾಗಿ ಹುಟ್ಟಿದವರೇ ಅಬ್ರಹಾಮನ ಸಂತತಿಯವರೆಂದು ಎಣಿಸಲ್ಪಡುವರು ಎಂಬದು.",
			"9": "<sup>9</sup> ಮುಂದಿನ ವರುಷದ ಇದೇ ಕಾಲದಲ್ಲಿ ನಾನು ಬರುವೆನು, ಆಗ ಸಾರಳಿಗೆ ಮಗನು ಇರುವನು ಎಂಬ ಮಾತು ವಾಗ್ದಾನವಾಗಿದೆಯಷ್ಟೆ.",
			"10": "<sup>10</sup> ಇದು ಮಾತ್ರವಲ್ಲದೆ ರೆಬೆಕ್ಕಳು ಸಹ ನಮ್ಮ ಪಿತೃವಾದ ಇಸಾಕನಿಂದ ಅವಳಿ ಜವಳಿ ಮಕ್ಕಳಿಗೆ ಬಸುರಾದಾಗ ಅದರಂತೆಯೇ ಆಯಿತು.",
			"11": "<sup>11</sup> ಆ ಮಕ್ಕಳು ಒಬ್ಬ ತಂದೆಯ ಮಕ್ಕಳಾಗಿದ್ದರೂ ಅವರು ಇನ್ನು ಹುಟ್ಟದೆಯೂ ಒಳ್ಳೇದನ್ನಾಗಲಿ ಕೆಟ್ಟದ್ದನ್ನಾಗಲಿ ಮಾಡದೆಯೂ ಇದ್ದಾಗ - ಹಿರಿಯನು ಕಿರಿಯನಿಗೆ ಸೇವೆ ಮಾಡುವನೆಂಬದಾಗಿ ಆಕೆಗೆ ಹೇಳಲ್ಪಟ್ಟಿತು. ಇದರಿಂದ ದೇವರು ತನಗೆ ಮನಸ್ಸಿದ್ದವರನ್ನು ಆರಿಸಿಕೊಳ್ಳುತ್ತಾನೆಂಬ ಸಂಕಲ್ಪವು ಸ್ಥಿರವಾಗಿರಬೇಕೆಂದು ತೋರಿಸಿದನು. ಆ ಸಂಕಲ್ಪವು ಪುಣ್ಯಕ್ರಿಯೆಗಳ ಮೇಲೆ ಆಧಾರಗೊಳ್ಳದೆ ಕರೆಯುವಾತನ ಚಿತ್ತದ ಮೇಲೆಯೇ ಆಧಾರಗೊಂಡಿದೆ.",
			"13": "<sup>13</sup> ಇದಕ್ಕನುಸಾರವಾಗಿ - ಯಾಕೋಬನನ್ನು ಪ್ರೀತಿಸಿದನು, ಏಸಾವನನ್ನು ದ್ವೇಷಿಸಿದೆನು ಎಂದು ಬರೆದದೆ.",
			"14": "<sup>14</sup> ಹಾಗಾದರೆ ಏನು ಹೇಳೋಣ? ದೇವರಲ್ಲಿ ಅನ್ಯಾಯ ಉಂಟೋ? ಎಂದಿಗೂ ಇಲ್ಲ.",
			"15": "<sup>15</sup> ಯಾವನ ಮೇಲೆ ನನ್ನ ಕರುಣೆ ಉಂಟೋ ಅವನನ್ನು ಕರುಣಿಸುವೆನು, ಯಾವನ ಮೇಲೆ ನನ್ನ ಕನಿಕರವದೆಯೋ ಅವನಿಗೆ ಕನಿಕರ ತೋರಿಸುವೆನು ಎಂದು ದೇವರು ಮೋಶೆಗೆ ಹೇಳಿರುತ್ತಾನಷ್ಟೆ.",
			"16": "<sup>16</sup> ಆದದರಿಂದ ದೇವರ ದಯವು ಮನುಷ್ಯರಿಗೆ ಇಚ್ಫೈಸುವದರಿಂದಾಗಲಿ ಪ್ರಯತ್ನಿಸುವದರಿಂದಾಗಲಿ ದೊರೆಯದೆ ದೇವರು ಕರುಣಿಸುವದರಿಂದಲೇ ದೊರೆಯುತ್ತದೆ.",
			"17": "<sup>17</sup> ದೇವರು ಫರೋಹನನ್ನು ಕುರಿತು - ನಾನು ನಿನ್ನಲ್ಲಿ ನನ್ನ ಶಕ್ತಿಯನ್ನು ತೋರಿಸಿ ನನ್ನ ಹೆಸರನ್ನು ಲೋಕದಲ್ಲೆಲ್ಲಾ ಪ್ರಖ್ಯಾತಪಡಿಸಬೇಕೆಂಬ ಉದ್ದೇಶದಿಂದಲೇ ನಿನ್ನನ್ನು ಉನ್ನತಸ್ಥಾನಕ್ಕೆ ತಂದಿದ್ದೇನೆಂದು ಹೇಳಿದನೆಂಬದಾಗಿ ಶಾಸ್ತ್ರೋಕ್ತಿ ಉಂಟು.",
			"18": "<sup>18</sup> ದೇವರು ಯಾರನ್ನು ಕರುಣಿಸಿದರೂ ಯಾರನ್ನು ಕಠಿಣಪಡಿಸಿದರೂ ಅದೆಲ್ಲವು ಆತನ ಇಷ್ಟಾನುಸಾರವಾಗಿಯೇ ಇದೆ ಎಂಬದು ಇದರಿಂದ ತೋರಿಬರುತ್ತದೆ.",
			"19": "<sup>19</sup> ದೇವರ ಇಷ್ಟವನ್ನು ಎದುರಿಸುವವರು ಇಲ್ಲದಿರುವಲ್ಲಿ ಆತನು ತಪ್ಪುಹಿಡಿಯುವದು ಹೇಗೆ ಎಂದು ನನ್ನನ್ನು ಕೇಳುವಿ.",
			"20": "<sup>20</sup> ಎಲೋ ಮನುಷ್ಯನೇ, ಹಾಗನ್ನಬೇಡ; ದೇವರಿಗೆ ಎದುರುಮಾತಾಡುವದಕ್ಕೆ ನೀನು ಎಷ್ಟರವನು? ರೂಪಿಸಲ್ಪಟ್ಟದ್ದು ರೂಪಿಸಿದವನಿಗೆ - ನನ್ನನ್ನು ಹೀಗೇಕೆ ಮಾಡಿದೆ ಎಂದು ಕೇಳುವದುಂಟೇ?",
			"21": "<sup>21</sup> ಉತ್ತಮವಾದ ಬಳಕೆಗೆ ಒಂದು ಪಾತ್ರೆಯನ್ನೂ ಹೀನವಾದ ಬಳಕೆಗೆ ಮತ್ತೊಂದು ಪಾತ್ರೆಯನ್ನೂ ಒಂದೇ ಮುದ್ದೆಯಿಂದ ಮಾಡುವದಕ್ಕೆ ಕುಂಬಾರನಿಗೆ ಮಣ್ಣಿನ ಮೇಲೆ ಅಧಿಕಾರವಿಲ್ಲವೋ?",
			"22": "<sup>22</sup> ಆದರೆ ದೇವರು ತನ್ನ ಕೋಪವನ್ನು ತೋರಿಸಿ ತನ್ನ ಶಕ್ತಿಯನ್ನು ಪ್ರಸಿದ್ಧಿಪಡಿಸಬೇಕೆಂದಿದ್ದರೂ ಹಾಗೆ ಮಾಡದೆ ತನ್ನ ಕೋಪಕ್ಕೆ ಗುರಿಯಾದ ನಾಶನಪಾತ್ರರನ್ನು ಬಹು ಸೈರಣೆಯಿಂದ ಸೈರಿಸಿಕೊಂಡಿದ್ದಾನೆ.",
			"23": "<sup>23</sup> ಮತ್ತು ಪ್ರಭಾವ ಹೊಂದುವದಕ್ಕೆ ತಾನು ಮುಂದಾಗಿ ಸಿದ್ಧಮಾಡಿದ ಕರುಣಾಪಾತ್ರರಲ್ಲಿ ತನ್ನ ಮಹಿಮಾತಿಶಯವನ್ನು ತೋರ್ಪಡಿಸಿದ್ದಾನೆ.",
			"24": "<sup>24</sup> ಆತನು ಕರುಣಿಸಿದ ನಮ್ಮನ್ನು ಯೆಹೂದ್ಯರೊಳಗಿಂದ ಮಾತ್ರ ಕರೆಯದೆ ಹೋಶೇಯನ ಗ್ರಂಥದ ವಚನದಲ್ಲಿ ತಾನು ಸೂಚಿಸಿದಂತೆ ಅನ್ಯಜನರೊಳಗಿಂದ ಸಹ ಕರೆದನು.",
			"25": "<sup>25</sup> ಆ ವಚನವೇನಂದರೆ - ನನ್ನ ಜನವಲ್ಲದ್ದನ್ನು ನನ್ನ ಜನವೆಂದೂ ನನಗೆ ಅಪ್ರಿಯವಾದವಳನ್ನು ಪ್ರಿಯಳೆಂದೂ ಹೇಳುವೆನು.",
			"26": "<sup>26</sup> ಮತ್ತು ಯಾವ ಸ್ಥಳದಲ್ಲಿ - ನೀವು ನನ್ನ ಜನರಲ್ಲವೆಂದು ಅವರಿಗೆ ಹೇಳಲ್ಪಟ್ಟಿತೋ ಆ ಸ್ಥಳದಲ್ಲಿಯೇ ಅವರು ಜೀವವುಳ್ಳ ದೇವರ ಪುತ್ರರು ಎನಿಸಿಕೊಳ್ಳುವರು ಎಂಬದು.",
			"27": "<sup>27</sup> ಇದಲ್ಲದೆ ಯೆಶಾಯನು ಇಸ್ರಾಯೇಲ್ ಜನರ ವಿಷಯದಲ್ಲಿ - ಕರ್ತನು ಈ ದೇಶದವರ ವಿಷಯ ಪೂರ್ಣವಾಗಿಯೂ ತೀವ್ರವಾಗಿಯೂ ಲೆಕ್ಕವನ್ನು ತೀರಿಸುವನಾದದರಿಂದ ಇಸ್ರಾಯೇಲ್ಯರ ಸಂಖ್ಯೆಯು ಸಮುದ್ರದ ಉಸುಬಿನಂತೆ ಅಸಂಖ್ಯವಾಗಿದ್ದರೂ ಅವರಲ್ಲಿ ಒಂದು ಅಂಶ ಮಾತ್ರ ರಕ್ಷಣೆಹೊಂದುವದೆಂದು ಕೂಗಿ ಹೇಳುತ್ತಾನೆ.",
			"28": "<sup>28</sup> ಅದೇ ಅಭಿಪ್ರಾಯದಿಂದ ಯೆಶಾಯನು ಮತ್ತೊಂದು ವಚನದಲ್ಲಿ -",
			"29": "<sup>29</sup> ಸೇನಾಧೀಶ್ವರನಾದ ಕರ್ತನು ನಮಗೆ ಸಂತಾನವನ್ನು ಉಳಿಸದೆ ಹೋಗಿದ್ದರೆ ಸೊದೋಮಿನ ಗತಿಯೇ ನಮಗಾಗುತ್ತಿತ್ತು, ಗೊಮೋರದ ದುರ್ದಶೆಯೇ ಸಂಭವಿಸುತ್ತಿತ್ತು ಎಂದು ಹೇಳುತ್ತಾನೆ.",
			"30": "<sup>30</sup> ಹಾಗಾದರೆ ಏನು ಹೇಳೋಣ? ನೀತಿಯನ್ನು ಹೊಂದುವದಕ್ಕೆ ಪ್ರಯತ್ನಮಾಡದ ಅನ್ಯಜನರಿಗೆ ನೀತಿಯು ಅಂದರೆ ನಂಬಿಕೆಯಿಂದುಂಟಾಗುವ ನೀತಿಯು ದೊರಕಿತು.",
			"31": "<sup>31</sup> ಆದರೆ ಇಸ್ರಾಯೇಲ್ಯರು ನೀತಿಯನ್ನು ದೊರಕಿಸುವ ಧರ್ಮವನ್ನು ಹಿಡಿಯಬೇಕೆಂದು ಪ್ರಯತ್ನಿಸಿದರೂ ಆ ಧರ್ಮವನ್ನು ಹಿಡಿಯಲಾರದೆ ಹೋದರೆಂದು ಹೇಳಬೇಕು.",
			"32": "<sup>32</sup> ಅದಕ್ಕೆ ಕಾರಣವೇನು? ಅವರು ನಂಬಿಕೆಯನ್ನು ಆಧಾರಮಾಡಿಕೊಳ್ಳದೆ ನೇಮನಿಷ್ಠೆಗಳನ್ನು ಆಧಾರಮಾಡಿಕೊಂಡದ್ದೇ ಕಾರಣ.",
			"33": "<sup>33</sup> ಇಗೋ, ಚೀಯೋನಿನಲ್ಲಿ ಎಡವುವ ಕಲ್ಲನ್ನೂ ಮುಗ್ಗರಿಸುವ ಬಂಡೆಯನ್ನೂ ಇಡುತ್ತೇನೆಂತಲೂ ಅದರ ಮೇಲೆ ನಂಬಿಕೆಯಿಡುವವನು ಆಶಾಭಂಗಪಡುವದಿಲ್ಲವೆಂತಲೂ ಶಾಸ್ತ್ರದಲ್ಲಿ ಬರೆದದೆಯಲ್ಲಾ. ಆ ಎಡವುವ ಕಲ್ಲಿಗೆ ಅವರು ಎಡವಿಬಿದ್ದರು."
		},
		"10": {
			"1": "<sup>1</sup> ಸಹೋದರರೇ, ಇಸ್ರಾಯೇಲ್ಯರು ರಕ್ಷಣೆಹೊಂದಬೇಕೆಂಬದೇ ನನ್ನ ಮನೋಭಿಲಾಷೆಯೂ ದೇವರಿಗೆ ನಾನು ಮಾಡುವ ವಿಜ್ಞಾಪನೆಯೂ ಆಗಿದೆ.",
			"2": "<sup>2</sup> ದೇವರಲ್ಲಿ ಆಸಕ್ತರಾಗಿದ್ದಾರೆಂದು ನಾನು ಅವರ ವಿಷಯದಲ್ಲಿ ಸಾಕ್ಷಿಕೊಡುತ್ತೇನೆ; ಆದರೂ ಅವರ ಆಸಕ್ತಿ ಜ್ಞಾನಾನುಸಾರವಾದದ್ದಲ್ಲ.",
			"3": "<sup>3</sup> ಅವರು ದೇವರಿಂದ ದೊರಕುವ ನೀತಿಯನ್ನರಿಯದೆ ಸ್ವನೀತಿಯನ್ನೇ ಸ್ಥಾಪಿಸಬೇಕೆಂದು ಪ್ರಯತ್ನಿಸುತ್ತಾ ಇದ್ದದರಿಂದ ದೇವರ ನೀತಿಗೆ ಅಧೀನರಾಗಲಿಲ್ಲ.",
			"4": "<sup>4</sup> ನಂಬುವವರೆಲ್ಲರಿಗೆ ನೀತಿಯನ್ನು ದೊರಕಿಸಿರುವ ಕ್ರಿಸ್ತನಿಂದಲೇ ಕರ್ಮಮಾರ್ಗಕ್ಕೆ ಅಂತ್ಯವಾಯಿತು.",
			"5": "<sup>5</sup> ನೇಮನಿಷ್ಠೆಗಳಿಂದಾಗುವ ನೀತಿಯ ವಿಷಯದಲ್ಲಿ - ಅದನ್ನು ಅನುಸರಿಸಿದವನೇ ಅದರಿಂದ ಜೀವಿಸುವನೆಂದು ಮೋಶೆಯು ಬರೆಯುತ್ತಾನೆ.",
			"6": "<sup>6</sup> ಆದರೆ ನಂಬಿಕೆಯಿಂದುಂಟಾಗುವ ನೀತಿಯು ಏನು ಹೇಳುತ್ತದೆ? ಕ್ರಿಸ್ತನನ್ನು ಕೆಳಕ್ಕೆ ಕರೆದುಕೊಂಡು ಬರುವದಕ್ಕೆ ಯಾರು ಮೇಲಣ ಲೋಕಕ್ಕೆ ಏರಿಹೋದಾರು? ಎಂದಾಗಲಿ",
			"7": "<sup>7</sup> ಕ್ರಿಸ್ತನನ್ನು ಸತ್ತವರೊಳಗಿಂದ ಎಬ್ಬಿಸಿಕೊಂಡು ಬರುವದಕ್ಕೆ ಯಾರು ಪ್ರೇತಲೋಕಕ್ಕೆ ಇಳಿದುಹೋದಾರು? ಎಂದಾಗಲಿ",
			"8": "<sup>8</sup> ನಿನ್ನ ಮನಸ್ಸಿನಲ್ಲಿ ಅಂದುಕೊಳ್ಳಬೇಡ. ದೇವರ ವಾಕ್ಯವು ನಿನ್ನ ಸಮೀಪದಲ್ಲಿಯೇ ಇದೆ; ಅದು ನಿನ್ನ ಬಾಯಲ್ಲಿಯೂ ನಿನ್ನ ಹೃದಯದಲ್ಲಿಯೂ ಇದೆ ಅನ್ನುತ್ತದೆ. ಆ ವಾಕ್ಯವು ನಾವು ಸಾರುವ ನಂಬಿಕೆಯ ವಿಷಯವಾದ ವಾಕ್ಯವೇ.",
			"9": "<sup>9</sup> ಅದೇನಂದರೆ ನೀನು ಯೇಸುವನ್ನೇ ಕರ್ತನೆಂದು ಬಾಯಿಂದ ಅರಿಕೆಮಾಡಿಕೊಂಡು ದೇವರು ಆತನನ್ನು ಸತ್ತವರೊಳಗಿಂದ ಎಬ್ಬಿಸಿದನೆಂದು ಹೃದಯದಿಂದ ನಂಬಿದರೆ ನಿನಗೆ ರಕ್ಷಣೆಯಾಗುವದು ಎಂಬದೇ.",
			"10": "<sup>10</sup> ಹೃದಯದಿಂದ ನಂಬುವದರ ಮೂಲಕ ನೀತಿಯು ದೊರಕುತ್ತದೆ. ಬಾಯಿಂದ ಅರಿಕೆಮಾಡುವದರ ಮೂಲಕ ರಕ್ಷಣೆಯಾಗುತ್ತದೆ.",
			"11": "<sup>11</sup> ಆತನ ಮೇಲೆ ನಂಬಿಕೆಯಿಡುವ ಯಾವನಾದರೂ ಆಶಾಭಂಗಪಡುವದಿಲ್ಲವೆಂದು ಶಾಸ್ತ್ರವು ಹೇಳುತ್ತದೆಯಷ್ಟೆ.",
			"12": "<sup>12</sup> ಈ ವಿಷಯದಲ್ಲಿ ಯೆಹೂದ್ಯನಿಗೂ ಗ್ರೀಕನಿಗೂ ಹೆಚ್ಚು ಕಡಿಮೆ ಏನೂ ಇಲ್ಲ. ಎಲ್ಲರಿಗೂ ಒಬ್ಬನೇ ಕರ್ತ; ಆತನು ತನ್ನ ನಾಮವನ್ನು ಹೇಳಿಕೊಳ್ಳುವವರಿಗೆ ಹೇರಳವಾಗಿ ಕೊಡುವವನಾಗಿದ್ದಾನೆ.",
			"13": "<sup>13</sup> ಕರ್ತನ ನಾಮವನ್ನು ಹೇಳಿಕೊಳ್ಳುವವರೆಲ್ಲರಿಗೆ ರಕ್ಷಣೆಯಾಗುವದೆಂದು ಬರೆದದೆ.",
			"14": "<sup>14</sup> ಆದರೆ ತಾವು ಯಾವನನ್ನು ನಂಬಲಿಲ್ಲವೋ ಆತನ ನಾಮವನ್ನು ಹೇಳಿಕೊಳ್ಳುವದು ಹೇಗೆ? ಮತ್ತು ಆತನ ಸುದ್ದಿ ಕೇಳದಿರುವಲ್ಲಿ ಆತನನ್ನು ನಂಬುವದು ಹೇಗೆ? ಸಾರಿ ಹೇಳುವವನಿಲ್ಲದೆ ಕೇಳುವದು ಹೇಗೆ? ಸಾರುವವರು ಕಳುಹಿಸಲ್ಪಡದೆ ಸಾರುವದೆಲ್ಲಿ?",
			"15": "<sup>15</sup> ಇದಕ್ಕೆ ಸರಿಯಾಗಿ ಶುಭದ ಸುವಾರ್ತೆಯನ್ನು ಸಾರುವವರ ಪಾದಗಳು ಎಷ್ಟೋ ಅಂದವಾಗಿವೆ ಎಂದು ಬರೆದದೆ. ಆದರೂ ಆ ಶುಭವರ್ತಮಾನಕ್ಕೆ ಎಲ್ಲರೂ ಕಿವಿಗೊಡಲಿಲ್ಲ.",
			"16": "<sup>16</sup> ಈ ವಿಷಯದಲ್ಲಿ ಯೆಶಾಯನು - ಕರ್ತನೇ, ನಾವು ಸಾರಿದ ವಾರ್ತೆಯನ್ನು ಯಾರು ನಂಬಿದರು ಎಂದು ನುಡಿಯುತ್ತಾನೆ.",
			"17": "<sup>17</sup> ಆದಕಾರಣ ಸಾರಿದ ವಾರ್ತೆಯು ನಂಬಿಕೆಗೆ ಆಧಾರ, ಆ ವಾರ್ತೆಗೆ ಕ್ರಿಸ್ತನ ವಾಕ್ಯವೇ ಆಧಾರ.",
			"18": "<sup>18</sup> ಆದರೂ ಅವರ ಕಿವಿಗೆ ಬೀಳಲಿಲ್ಲವೇನು ಎಂದು ಕೇಳುತ್ತೇನೆ. ಬಿದ್ದದ್ದು ನಿಶ್ಚಯ. ಸಾರುವವರ ಧ್ವನಿಯು ಭೂಮಿಯಲ್ಲೆಲ್ಲಾ ಪ್ರಸರಿಸಿತು, ಅವರ ನುಡಿಗಳು ಲೋಕದ ಕಟ್ಟಕಡೆಯವರೆಗೂ ವ್ಯಾಪಿಸಿದವು.",
			"19": "<sup>19</sup> ಆದರೆ ಇಸ್ರಾಯೇಲ್ಯರು ಆ ವಾರ್ತೆಯನ್ನು ತಿಳುಕೊಳ್ಳಲಿಲ್ಲವೇನು ಎಂದು ಕೇಳುತ್ತೇನೆ. ಈ ವಿಷಯದಲ್ಲಿ - ಜನಾಂಗವಲ್ಲದವರ ಮೂಲಕ ನಿಮ್ಮಲ್ಲಿ ಹುರುಡುಹುಟ್ಟಿಸುವೆನು; ವಿವೇಕವಿಲ್ಲದ ಜನರ ಮೂಲಕ ನಿಮ್ಮನ್ನು ಸಿಟ್ಟಿಗೆಬ್ಬಿಸುವೆನು ಎಂದು ಮೊದಲು ಮೋಶೆಯು ಹೇಳುತ್ತಾನೆ.",
			"20": "<sup>20</sup> ಇದಲ್ಲದೆ ಯೆಶಾಯನು ಧೈರ್ಯವಾಗಿ ಮಾತಾಡಿ - ನನ್ನನ್ನು ಹುಡುಕದವರಿಗೂ ಸಿಕ್ಕಿದೆನು, ನನ್ನ ವಿಷಯ ವಿಚಾರಮಾಡದವರಿಗೂ ಪ್ರತ್ಯಕ್ಷನಾದೆನು ಎಂದು ಹೇಳುತ್ತಾನೆ.",
			"21": "<sup>21</sup> ಆದರೆ ಅವನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಕುರಿತು - ನನ್ನ ಮಾತಿಗೆ ಅವಿಧೇಯರಾಗಿ ಎದುರುಮಾತಾಡುವ ಜನರನ್ನು ನಾನು ದಿನವೆಲ್ಲಾ ಕೈಚಾಚಿ ಕರೆದೆನೆಂದು ಹೇಳುತ್ತಾನೆ."
		},
		"11": {
			"1": "<sup>1</sup> ಹಾಗಾದರೆ ದೇವರು ತನ್ನ ಪ್ರಜೆಯನ್ನು ಬೇಡವೆಂದು ತಳ್ಳಿಬಿಟ್ಟದ್ದುಂಟೇ ಎಂದು ಕೇಳುತ್ತೇನೆ. ಎಂದಿಗೂ ಇಲ್ಲ. ನಾನು ಸಹ ಇಸ್ರಾಯೇಲ್ಯನು, ಅಬ್ರಹಾಮನ ಸಂತತಿಯವನು, ಬೆನ್ಯಾಮೀನನ ಕುಲದವನು ಆಗಿದ್ದೇನಲ್ಲಾ.",
			"2": "<sup>2</sup> ದೇವರು ತಾನು ಮುಂದಾಗಿ ತನ್ನ ಜನರಾಗುವದಕ್ಕೆ ಗೊತ್ತುಮಾಡಿಕೊಂಡಿದ್ದ ಪ್ರಜೆಯನ್ನು ತಳ್ಳಿ ಬಿಡಲಿಲ್ಲ. ಎಲೀಯನ ಚರಿತ್ರೆಯಲ್ಲಿ ಶಾಸ್ತ್ರವು ಹೇಳುವ ಮಾತು ನಿಮಗೆ ತಿಳಿಯದೋ?",
			"3": "<sup>3</sup> ಅವನು ದೇವರ ಮುಂದೆ - ಕರ್ತನೇ, ಅವರು ನಿನ್ನ ಪ್ರವಾದಿಗಳನ್ನು ಸಂಹರಿಸಿದ್ದಾರೆ, ನಿನ್ನ ಯಜ್ಞವೇದಿಗಳನ್ನು ಒಡೆದುಹಾಕಿದ್ದಾರೆ, ನಾನೊಬ್ಬನೇ ಉಳಿದಿದ್ದೇನೆ, ನನ್ನ ಪ್ರಾಣವನ್ನೂ ತೆಗೆಯಬೇಕೆಂದಿದ್ದಾರೆ",
			"4": "<sup>4</sup> ಎಂದು ಇಸ್ರಾಯೇಲ್ಯರ ಮೇಲೆ ದೂರು ಹೇಳಿದ್ದಕ್ಕೆ ದೇವರ ಉತ್ತರವೇನಂದರೆ - ಬಾಳನ ವಿಗ್ರಹಕ್ಕೆ ಅಡ್ಡಬೀಳದೆ ಇರುವ ಏಳುಸಾವಿರ ಪುರುಷರನ್ನು ನನಗಾಗಿ ಉಳಿಸಿಕೊಂಡಿದ್ದೇನೆ ಎಂಬದೇ.",
			"5": "<sup>5</sup> ಅದರಂತೆ ಈಗಿನ ಕಾಲದಲ್ಲಿಯೂ ದೇವರು ಕೃಪೆಯಿಂದ ಆದುಕೊಂಡವರಾದ ಕೆಲವರು ಉಳಿದಿದ್ದಾರೆ.",
			"6": "<sup>6</sup> ಕೃಪೆಯಿಂದ ಆದುಕೊಂಡನು ಅಂದ ಮೇಲೆ ಪುಣ್ಯಕ್ರಿಯೆಗಳ ಕಾರಣದಿಂದ ಆದುಕೊಳ್ಳಲಿಲ್ಲವಷ್ಟೆ; ಹಾಗಲ್ಲದ ಪಕ್ಷಕ್ಕೆ ಕೃಪೆಯನ್ನು ಇನ್ನು ಕೃಪೆಯನ್ನುವದಕ್ಕಾಗುವದಿಲ್ಲ.",
			"7": "<sup>7</sup> ಇದರಿಂದ ತಿಳಿಯತಕ್ಕದ್ದೇನು? ಇಸ್ರಾಯೇಲ್ಯರು ಹುಡುಕಿದ್ದು ಅವರೆಲ್ಲರಿಗೆ ದೊರೆಯಲಿಲ್ಲ, ಅವರೊಳಗೆ ಆಯಲ್ಪಟ್ಟವರಿಗೆ ದೊರೆಯಿತು. ಮಿಕ್ಕವರು ಮೊಂಡರಾದರು.",
			"8": "<sup>8</sup> ದೇವರು ಅವರಿಗೆ ಜಡಭಾವವನ್ನು ಅಂದರೆ ಕಾಣದ ಕಣ್ಣುಗಳನ್ನೂ ಕೇಳದ ಕಿವಿಗಳನ್ನೂ ಕೊಟ್ಟನು; ಈ ಭಾವವು ಈ ಹೊತ್ತಿನವರೆಗೂ ಹಾಗೆಯೇ ಅದೆ ಎಂದು ಬರೆದ ಪ್ರಕಾರ ಅವರಿಗೆ ಆಯಿತು.",
			"9": "<sup>9</sup> ಇದಲ್ಲದೆ - ಅವರ ಊಟವೇ ಅವರಿಗೆ ಉರ್ಲೂ ಬೋನೂ ಎಡತಡೆಯೂ ಶಿಕ್ಷೆಯೂ ಆಗಲಿ;",
			"10": "<sup>10</sup> ಅವರ ಕಣ್ಣು ಮೊಬ್ಬಾಗಿ ಕಾಣದೆ ಹೋಗಲಿ; ಅವರ ಬೆನ್ನು ಯಾವಾಗಲೂ ಬೊಗ್ಗಿಕೊಂಡಿರುವಂತೆ ಮಾಡು ಎಂದು ದಾವೀದನು ಹೇಳುತ್ತಾನೆ.",
			"11": "<sup>11</sup> ಹಾಗಾದರೆ ಅವರು ಬಿದ್ದೇ ಹೋಗುವಂತೆ ಎಡವಿದರೇನು ಎಂದು ಕೇಳುತ್ತೇನೆ. ಹಾಗೆ ಎಂದಿಗೂ ಹೇಳಬಾರದು. ಅವರು ಬಿದ್ದುಹೋದದ್ದರಿಂದ ಕ್ರಿಸ್ತನಿಂದ ಉಂಟಾಗುವ ರಕ್ಷಣೆಯು ಅನ್ಯಜನರಿಗೆ ಉಂಟಾಯಿತು. ಇದರಿಂದ ದೇವರು ಅವರಲ್ಲಿ ಹುರುಡು ಹುಟ್ಟಿಸುತ್ತಾನೆ.",
			"12": "<sup>12</sup> ಅವರು ಬಿದ್ದುಹೋದದ್ದು ಸರ್ವಲೋಕದ ಭಾಗ್ಯಕ್ಕೂ ಅವರು ಸೋತು ಹೋದದ್ದು ಅನ್ಯಜನಗಳ ಭಾಗ್ಯಕ್ಕೂ ಮಾರ್ಗವಾಗಿರಲಾಗಿ ಅವರ ಪೂರ್ಣ ಪುನಃಸ್ಥಾಪನೆಯು ಎಷ್ಟೋ ಹೆಚ್ಚಾಗಿ ಭಾಗ್ಯಕ್ಕೆ ಕಾರಣವಾಗುವದು.",
			"13": "<sup>13</sup> ಅನ್ಯಜನರಾಗಿರುವ ನಿಮ್ಮ ಸಂಗಡ ಮಾತಾಡುತ್ತೇನೆ. ನಾನು ಅನ್ಯಜನರಿಗೆ ಅಪೊಸ್ತಲನಾಗಿರಲಾಗಿ ನನ್ನ ಉದ್ಯೋಗವನ್ನು ಗಣ್ಯತೆಗೆ ತರುತ್ತೇನೆ.",
			"14": "<sup>14</sup> ಹೀಗೆ ಒಂದು ವೇಳೆ ಸ್ವಜನರಲ್ಲಿ ಹುರುಡನ್ನು ಹುಟ್ಟಿಸಿ ಅವರಲ್ಲಿ ಸಹ ಕೆಲವರನ್ನು ರಕ್ಷಣೆಯ ಮಾರ್ಗಕ್ಕೆ ತಂದೇನು.",
			"15": "<sup>15</sup> ಯಾಕಂದರೆ ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ತಳ್ಳುವದರಿಂದ ಲೋಕವು ದೇವರ ಸಂಗಡ ಸಮಾಧಾನವಾಗುವದಕ್ಕೆ ಮಾರ್ಗವಾದ ಮೇಲೆ ಅವರನ್ನು ಸೇರಿಸಿಕೊಳ್ಳುವದರಿಂದ ಏನಾಗುವದು? ಸತ್ತವರು ಜೀವಿತರಾಗಿ ಎದ್ದುಬಂದಂತಾಗುವದಲ್ಲವೇ.",
			"16": "<sup>16</sup> ಕಣಕದಲ್ಲಿ ಮೊದಲನೆಯ ಉರುಳಿ ನೈವೇದ್ಯವಾದ ಮೇಲೆ ಕಣಕವೆಲ್ಲಾ ನೈವೇದ್ಯವಾದ ಹಾಗಾಯಿತು; ಬೇರು ದೇವರದಾಗಿದ್ದ ಮೇಲೆ ಕೊಂಬೆಗಳೂ ದೇವರವು.",
			"17": "<sup>17</sup> ಆದರೆ ಕೆಲವು ಕೊಂಬೆಗಳು ಮುರಿದುಹಾಕಲ್ಪಡಲು ಕಾಡೆಣ್ಣೇಮರದಂತಿರುವ ನೀನು ಅವುಗಳ ತಾವಿನಲ್ಲಿ ಕಸಿಕಟ್ಟಿಸಿಕೊಂಡವನಾಗಿ ಊರೆಣ್ಣೇಮರದ ರಸವತ್ತಾದ ಬೇರಿನಲ್ಲಿ ಪಾಲು ಹೊಂದಿದರೂ ಆ ಕೊಂಬೆಗಳನ್ನು ಕಡೆಗಣಿಸಿ ನಿನ್ನನ್ನು ಹೆಚ್ಚಿಸಿಕೊಳ್ಳಬೇಡ;",
			"18": "<sup>18</sup> ಹೆಚ್ಚಿಸಿಕೊಂಡರೂ ಆ ಬೇರಿಗೆ ನೀನು ಆಧಾರವಲ್ಲ, ಅದು ನಿನಗೆ ಆಧಾರವಾಗಿದೆಯಷ್ಟೆ.",
			"19": "<sup>19</sup> ಈ ಮಾತಿಗೆ - ನಾನು ಕಸಿಕಟ್ಟಿಸಿಕೊಳ್ಳುವಂತೆ ಕೊಂಬೆಗಳು ಮುರಿದುಹಾಕಲ್ಪಟ್ಟವಲ್ಲಾ ಎಂದು ನೀನು ಒಂದು ವೇಳೆ ಹೇಳಬಹುದು.",
			"20": "<sup>20</sup> ನೀನು ಹೇಳುವದು ನಿಜ; ಅವರು ನಂಬದೆಹೋದದರಿಂದ ಮುರಿದುಹಾಕಲ್ಪಟ್ಟರು, ನೀನು ನಿಂತಿರುವದು ನಂಬಿಕೆಯಿಂದಲೇ.",
			"21": "<sup>21</sup> ಗರ್ವಪಡಬೇಡ, ಭಯದಿಂದಿರು; ದೇವರು ಹುಟ್ಟುಕೊಂಬೆಗಳನ್ನು ಉಳಿಸದೆ ಇದ್ದ ಮೇಲೆ ನಿನ್ನನ್ನೂ ಉಳಿಸುವದಿಲ್ಲ.",
			"22": "<sup>22</sup> ಆದದರಿಂದ ದೇವರ ದಯೆಯನ್ನೂ ಕಾಠಿಣ್ಯವನ್ನೂ ನೋಡು; ಬಿದ್ದವರ ಕಡೆಗೆ ಕಾಠಿಣ್ಯ; ನಿನ್ನ ಕಡೆಗೆ ನೀನು ದೇವರ ದಯೆಯನ್ನು ಆಶ್ರಯಿಸಿಕೊಂಡೇ ಇದ್ದರೆ ಆತನ ದಯೆ; ಇಲ್ಲವಾದರೆ ನಿನ್ನನ್ನೂ ಕಡಿದುಹಾಕುವನು.",
			"23": "<sup>23</sup> ಇಸ್ರಾಯೇಲ್ಯರು ಕೂಡ ಇನ್ನು ಅಪನಂಬಿಕೆಯಲ್ಲಿ ನಿಲ್ಲದ ಪಕ್ಷಕ್ಕೆ ಕಸಿಕಟ್ಟಲ್ಪಡುವರು; ದೇವರು ಅವರನ್ನು ತಿರಿಗಿ ಕಸಿಕಟ್ಟುವದಕ್ಕೆ ಸಮರ್ಥನಾಗಿದ್ದಾನೆ.",
			"24": "<sup>24</sup> ನೀನು ಹುಟ್ಟು ಕಾಡುಮರದಿಂದ ಕಡಿದು ತೆಗೆಯಲ್ಪಟ್ಟು ನಿನಗೆ ಸಂಬಂಧಪಡದ ಊರು ಮರದಲ್ಲಿ ಕಸಿಕಟ್ಟಿಸಿಕೊಂಡವನಾದ ಮೇಲೆ ಅದರಲ್ಲಿ ಹುಟ್ಟಿದ ಕೊಂಬೆಗಳಾಗಿರುವ ಅವರು ಸ್ವಂತ ಮರದಲ್ಲಿ ಕಸಿಕಟ್ಟಲ್ಪಡುವದು ಎಷ್ಟೋ ಸಹಜವಾಗಿದೆಯಲ್ಲವೇ.",
			"25": "<sup>25</sup> ಸಹೋದರರೇ, ನಿಮ್ಮನ್ನು ನೀವೇ ಬುದ್ಧಿವಂತರೆಂಬದಾಗಿ ಎಣಿಸಿಕೊಳ್ಳದಂತೆ ಇದುವರೆಗೆ ಗುಪ್ತವಾಗಿದ್ದ ಒಂದು ಸಂಗತಿ ನಿಮಗೆ ತಿಳಿದಿರಬೇಕೆಂದು ಅಪೇಕ್ಷಿಸುತ್ತೇನೆ; ಅದೇನಂದರೆ ಇಸ್ರಾಯೇಲ್ಯರಲ್ಲಿ ಒಂದು ಪಾಲು ಜನರಿಗೆ ಉಂಟಾದ ಮೊಂಡತನವು ಯಾವಾಗಲೂ ಇರದೆ ಅನ್ಯಜನಗಳ ಸಮುದಾಯವು ದೇವರ ರಾಜ್ಯದಲ್ಲಿ ಸೇರುವ ತನಕ ಮಾತ್ರ ಇರುವದು;",
			"26": "<sup>26</sup> ಆಮೇಲೆ ಇಸ್ರಾಯೇಲ್ ಜನವೆಲ್ಲಾ ರಕ್ಷಣೆಹೊಂದುವದು. ಇದಕ್ಕೆ ಆಧಾರವಾಗಿ ಶಾಸ್ತ್ರದಲ್ಲಿ - ಬಿಡಿಸುವವನು ಚೀಯೋನಿನೊಳಗಿಂದ ಹೊರಟು ಬಂದು ಯಾಕೋಬನಲ್ಲಿರುವ ಭಕ್ತಿಹೀನತೆಯನ್ನು ನಿವಾರಣೆಮಾಡುವನು.",
			"27": "<sup>27</sup> ನಾನು ಅವರ ಸಂಗಡ ಮಾಡಿಕೊಂಡ ಈ ಒಡಂಬಡಿಕೆಯು ನಾನು ಅವರ ಪಾಪಗಳನ್ನು ಪರಿಹರಿಸುವಾಗ ನೆರವೇರುವದು ಎಂದು ಬರೆದದೆ.",
			"28": "<sup>28</sup> ಅವರು ಸುವಾರ್ತೆಯನ್ನು ಬೇಡವೆಂದದ್ದರಿಂದ ದೇವರು ನಿಮಗೆ ಹಿತವನ್ನು ಉಂಟುಮಾಡಿ ಅವರನ್ನು ಶತ್ರುಗಳೆಂದೆಣಿಸಿದ್ದಾನೆ. ಆದರೂ ಅವರು ತಾನು ಆರಿಸಿಕೊಂಡ ಅಬ್ರಹಾಮ ಇಸಾಕ ಯಾಕೋಬರೆಂಬ ಪಿತೃಗಳ ವಂಶಸ್ಥರಾಗಿರುವದರಿಂದ ಅವರನ್ನು ಪ್ರಿಯರೆಂದು ಎಣಿಸಿದ್ದಾನೆ.",
			"29": "<sup>29</sup> ದೇವರು ವರಗಳನ್ನು ಅನುಗ್ರಹಿಸಿದ್ದಕ್ಕೂ ಜನರನ್ನು ಕರೆದದ್ದಕ್ಕೂ ಪಶ್ಚಾತ್ತಾಪಪಡುವವನಲ್ಲ.",
			"30": "<sup>30</sup> ಯಾವ ಪ್ರಕಾರ ನೀವು ಪೂರ್ವದಲ್ಲಿ ಅವಿಧೇಯರಾಗಿದ್ದರೂ ಈಗ ಅವರ ಅವಿಧೇಯತೆಯ ದೆಸೆಯಿಂದ ಕರುಣೆಹೊಂದಿದ್ದೀರೋ",
			"31": "<sup>31</sup> ಅದೇ ಪ್ರಕಾರವಾಗಿ ಅವರು ಈಗ ಅವಿಧೇಯರಾಗಿದ್ದರೂ ನಿಮಗೆ ದೊರಕಿದ ಕರುಣೆಯನ್ನು ಹೊಂದುವರು.",
			"32": "<sup>32</sup> ಯಾಕಂದರೆ ದೇವರು ಎಲ್ಲರಿಗೂ ಕರುಣೆಯನ್ನು ತೋರಿಸಬೇಕೆಂಬ ಉದ್ದೇಶವುಳ್ಳವನಾಗಿದ್ದು ಎಲ್ಲರನ್ನೂ ಅವಿಧೇಯತೆಯೆಂಬ ದುಃಸ್ಥಿತಿಯಲ್ಲಿ ಕಟ್ಟಿಹಾಕಿದ್ದಾನೆ.",
			"33": "<sup>33</sup> ಆಹಾ, ದೇವರ ಐಶ್ವರ್ಯವೂ ಜ್ಞಾನವೂ ವಿವೇಕವೂ ಎಷ್ಟೋ ಅಗಾಧ! ಆತನ ತೀರ್ಮಾನಗಳು ಪರಿಶೋಧನೆಗೆ ಎಷ್ಟೋ ಅಗಮ್ಯ! ಆತನ ಮಾರ್ಗಗಳು ಕಂಡುಹಿಡಿಯುವದಕ್ಕೆ ಎಷ್ಟೋ ಅಸಾಧ್ಯ!",
			"34": "<sup>34</sup> ಕರ್ತನ ಮನಸ್ಸನ್ನು ತಿಳುಕೊಂಡವನಾರು?",
			"35": "<sup>35</sup> ಆತನಿಗೆ ಆಲೋಚನಾ ಕರ್ತನು ಯಾರು? ಮೊದಲು ಆತನಿಗೆ ಕೊಟ್ಟು ಪ್ರತಿಫಲವನ್ನು ತೆಗೆದುಕೊಳ್ಳುವವನು ಯಾರು?",
			"36": "<sup>36</sup> ಸಮಸ್ತವು ಆತನಿಂದ ಉತ್ಪತ್ತಿಯಾಗಿ ಆತನಿಂದ ನಡೆಯುತ್ತಾ ಆತನಿಗಾಗಿ ಇರುತ್ತದೆ; ಆತನಿಗೇ ಸದಾಕಾಲವೂ ಸ್ತೋತ್ರ. ಆಮೆನ್."
		},
		"12": {
			"1": "<sup>1</sup> ಆದದರಿಂದ ಸಹೋದರರೇ, ದೇವರ ಕನಿಕರವನ್ನು ನಿಮ್ಮ ನೆನಪಿಗೆ ತಂದು ನಿಮ್ಮನ್ನು ಬೇಡಿಕೊಳ್ಳುವದೇನಂದರೆ - ನೀವು ನಿಮ್ಮ ನಿಮ್ಮ ದೇಹಗಳನ್ನು ದೇವರಿಗೆ ಮೀಸಲಾಗಿಯೂ ಮೆಚ್ಚಿಗೆಯಾಗಿಯೂ ಇರುವ ಸಜೀವಯಜ್ಞವಾಗಿ ಅರ್ಪಿಸಿರಿ; ಇದೇ ನಿಮ್ಮ ವಿವೇಕಪೂರ್ವಕವಾದ ಆರಾಧನೆಯು.",
			"2": "<sup>2</sup> ಇಹಲೋಕದ ನಡವಳಿಕೆಯನ್ನು ಅನುಸರಿಸದೆ ನೂತನಮನಸ್ಸನ್ನು ಹೊಂದಿಕೊಂಡು ಪರಲೋಕಭಾವದವರಾಗಿರಿ. ಹೀಗಾದರೆ ದೇವರ ಚಿತ್ತಕ್ಕನುಸಾರವಾದದ್ದು ಅಂದರೆ ಉತ್ತಮವಾದದ್ದೂ ಮೆಚ್ಚಿಕೆಯಾದದ್ದೂ ದೋಷವಿಲ್ಲದ್ದೂ ಯಾವ ಯಾವದೆಂದು ವಿವೇಚಿಸಿ ತಿಳುಕೊಳ್ಳುವಿರಿ.",
			"3": "<sup>3</sup> ದೇವರು ನನಗೆ ಕೃಪೆಮಾಡಿದ ಸೇವೆಯನ್ನು ನಡಿಸಿ ನಿಮ್ಮಲ್ಲಿ ಒಬ್ಬೊಬ್ಬನಿಗೂ ಹೇಳುವದೇನಂದರೆ ಯಾರೂ ತನ್ನ ಯೋಗ್ಯತೆಗೆ ಮೀರಿ ತನ್ನನ್ನು ಭಾವಿಸಿಕೊಳ್ಳದೆ ದೇವರು ಒಬ್ಬೊಬ್ಬನಿಗೆ ಎಂಥೆಂಥ ವಿಶ್ವಾಸ ಬಲವನ್ನು ಕೊಟ್ಟನೋ ಅದಕ್ಕೆ ತಕ್ಕ ಹಾಗೆ ನ್ಯಾಯವಾದ ಅಭಿಪ್ರಾಯದಿಂದ ತನ್ನನ್ನು ಭಾವಿಸಿಕೊಳ್ಳಬೇಕು.",
			"4": "<sup>4</sup> ಯಾಕಂದರೆ ನಮಗೆ ಒಂದೇ ದೇಹದಲ್ಲಿ ಬಹಳ ಅಂಗಗಳಿರಲಾಗಿ ಆ ಎಲ್ಲಾ ಅಂಗಗಳಿಗೆ ಹೇಗೆ ಒಂದೇ ಕೆಲಸ ಇರುವದಿಲ್ಲವೋ",
			"5": "<sup>5</sup> ಹಾಗೆಯೇ ನಾವೆಲ್ಲರು ಒಟ್ಟಾಗಿ ಕ್ರಿಸ್ತನಲ್ಲಿ ಒಂದೇ ದೇಹವಾಗಿದ್ದು ಒಬ್ಬೊಬ್ಬರಾಗಿ ಪರಸ್ಪರ ಅಂಗಗಳಾಗಿದ್ದೇವೆ.",
			"6": "<sup>6</sup> ದೇವರು ನಮಗೆ ಕೃಪೆಮಾಡಿದ ಪ್ರಕಾರ ನಾವು ಬೇರೆ ಬೇರೆ ವರಗಳನ್ನು ಹೊಂದಿದ್ದೇವೆ. ಹೊಂದಿದ ವರವು ಪ್ರವಾದನೆಯ ರೂಪವಾಗಿದ್ದರೆ ನಮ್ಮ ವಿಶ್ವಾಸ ವರಕ್ಕೆ ತಕ್ಕ ಹಾಗೆ ಪ್ರವಾದನೆ ಹೇಳೋಣ.",
			"7": "<sup>7</sup> ಅದು ಸಭಾಸೇವೆಯ ರೂಪವಾಗಿದ್ದರೆ ಸಭಾಸೇವೆಯನ್ನು ಮಾಡುತ್ತಾ ಇರೋಣ.",
			"8": "<sup>8</sup> ಬೋಧಿಸುವವನು ಬೋಧಿಸುವದರಲ್ಲಿಯೂ ಬುದ್ಧಿ ಹೇಳುವವನು ಬುದ್ಧಿ ಹೇಳುವದರಲ್ಲಿಯೂ ನಿರತನಾಗಿರಲಿ. ದಾನಕೊಡುವವನು ಯಥಾರ್ಥಮನಸ್ಸಿನಿಂದ ಕೊಡಲಿ. ಹಿರೀತನ ನಡಿಸುವವನು ಆಸಕ್ತಿಯಿಂದ ನಡಿಸಲಿ. ಕಷ್ಟದಲ್ಲಿರುವವರಿಗೆ ಉಪಕಾರ ಮಾಡುವವನು ಸಂತೋಷವಾಗಿ ಮಾಡಲಿ.",
			"9": "<sup>9</sup> ನಿಮ್ಮ ಪ್ರೀತಿಯು ನಿಷ್ಕಪಟವಾಗಿರಲಿ. ಕೆಟ್ಟತನಕ್ಕೆ ಹೇಸಿಕೊಂಡು ಒಳ್ಳೇದನ್ನು ಬಿಗಿಯಾಗಿ ಹಿಡಿದುಕೊಳ್ಳಿರಿ.",
			"10": "<sup>10</sup> ಕ್ರೈಸ್ತ ಸಹೋದರರು ಅಣ್ಣತಮ್ಮಂದಿರೆಂದು ಒಬ್ಬರನ್ನೊಬ್ಬರು ಪ್ರೀತಿಸಿರಿ. ಮಾನಮರ್ಯಾದೆಯನ್ನು ತೋರಿಸುವದರಲ್ಲಿ ಒಬ್ಬರಿಗಿಂತ ಒಬ್ಬರು ಮುಂದಾಗಿರಿ.",
			"11": "<sup>11</sup> ಜಾಗ್ರತೆ ಬೇಕಾದಲ್ಲಿ ಆಲಸ್ಯವಾಗಿರದೆ ಆಸಕ್ತಚಿತ್ತರಾಗಿದ್ದು ಕರ್ತನ ಸೇವೆ ಮಾಡುವವರಾಗಿರ್ರಿ.",
			"12": "<sup>12</sup> ಕ್ರೈಸ್ತರು ನಿರೀಕ್ಷಿಸುವ ಮಹಾಪದವಿಯನ್ನು ನೆನಸಿ ಉಲ್ಲಾಸವಾಗಿರ್ರಿ. ಉಪದ್ರವದಲ್ಲಿ ಸೈರಣೆಯುಳ್ಳವರಾಗಿರ್ರಿ. ಬೇಸರಗೊಳ್ಳದೆ ಪ್ರಾರ್ಥನೆಮಾಡಿರಿ.",
			"13": "<sup>13</sup> ದೇವಜನರಿಗೆ ಕೊರತೆಬಂದಾಗ ಸಹಾಯಮಾಡಿರಿ. ಅತಿಥಿಸತ್ಕಾರವನ್ನು ಅಭ್ಯಾಸಿಸಿರಿ.",
			"14": "<sup>14</sup> ನಿಮ್ಮನ್ನು ಹಿಂಸಿಸುವವರಿಗೆ ಆಶೀರ್ವಾದಮಾಡಿರಿ; ಶಪಿಸದೆ ಆಶೀರ್ವದಿಸಿರಿ.",
			"15": "<sup>15</sup> ಸಂತೋಷಪಡುವವರ ಸಂಗಡ ಸಂತೋಷಪಡಿರಿ, ಅಳುವವರ ಸಂಗಡ ಅಳಿರಿ.",
			"16": "<sup>16</sup> ನಿಮ್ಮ ನಿಮ್ಮೊಳಗೆ ಏಕಮನಸ್ಸುಳ್ಳವರಾಗಿರ್ರಿ, ದೊಡ್ಡಸ್ತಿಕೆಯ ಮೇಲೆ ಮನಸ್ಸಿಡದೆ ದೀನರ ಸಂಗಡ ಬಳಿಕೆಯಾಗಿರ್ರಿ. ನಿಮ್ಮನ್ನು ನೀವೇ ಬುದ್ಧಿವಂತರೆಂದು ಎಣಿಸಿಕೊಳ್ಳಬೇಡಿರಿ.",
			"17": "<sup>17</sup> ಯಾರಿಗೂ ಅಪಕಾರಕ್ಕೆ ಅಪಕಾರವನ್ನು ಮಾಡಬೇಡಿರಿ. ಎಲ್ಲರ ದೃಷ್ಟಿಯಲ್ಲಿ ಯಾವದು ಗೌರವವಾದದ್ದೋ ಅದನ್ನೇ ಯೋಚಿಸಿ ಸಾಧಿಸಿರಿ.",
			"18": "<sup>18</sup> ಸಾಧ್ಯವಾದರೆ ನಿಮ್ಮಿಂದಾಗುವ ಮಟ್ಟಿಗೆ ಎಲ್ಲರ ಸಂಗಡ ಸಮಾಧಾನದಿಂದಿರಿ.",
			"19": "<sup>19</sup> ಪ್ರಿಯರೇ, ನೀವೇ ಮುಯ್ಯಿಗೆ ಮುಯ್ಯಿ ತೀರಿಸದೆ ಶಿಕ್ಷಿಸುವದನ್ನು ದೇವರಿಗೆ ಬಿಡಿರಿ. ಯಾಕಂದರೆ ಮುಯ್ಯಿಗೆ ಮುಯ್ಯಿ ತೀರಿಸುವದು ನನ್ನ ಕೆಲಸ, ನಾನೇ ಪ್ರತಿಫಲವನ್ನು ಕೊಡುವೆನು ಎಂದು ಕರ್ತನು ಹೇಳುತ್ತಾನೆಂಬದಾಗಿ ಬರೆದದೆ.",
			"20": "<sup>20</sup> ಹಾಗಾದರೆ ನಿನ್ನ ವೈರಿ ಹಸಿದಿದ್ದರೆ ಅವನಿಗೆ ಊಟಕ್ಕೆ ಬಡಿಸು; ಬಾಯಾರಿದ್ದರೆ ಕುಡಿಯುವದಕ್ಕೆ ಕೊಡು. ಹೀಗೆ ಮಾಡುವದರಿಂದ ಅವನ ತಲೆಯ ಮೇಲೆ ಕೆಂಡಗಳನ್ನು ಕೂಡಿಸಿಟ್ಟಂತಾಗುವದು.",
			"21": "<sup>21</sup> ಕೆಟ್ಟತನಕ್ಕೆ ಸೋತು ಹೋಗದೆ ಒಳ್ಳೇತನದಿಂದ ಕೆಟ್ಟತನವನ್ನು ಸೋಲಿಸು."
		},
		"13": {
			"1": "<sup>1</sup> ಪ್ರತಿ ಮನುಷ್ಯನು ತನ್ನ ಮೇಲಿರುವ ಅಧಿಕಾರಿಗಳಿಗೆ ಅಧೀನನಾಗಿರಲಿ; ಯಾಕಂದರೆ ದೇವರಿಂದ ಹೊರತು ಒಬ್ಬರಿಗೂ ಅಧಿಕಾರವಿರುವದಿಲ್ಲ; ಇರುವ ಅಧಿಕಾರಿಗಳು ದೇವರಿಂದ ನೇಮಿಸಲ್ಪಟ್ಟವರು.",
			"2": "<sup>2</sup> ಆದದರಿಂದ ಅಧಿಕಾರಕ್ಕೆ ಎದುರುಬೀಳುವವನು ದೇವರ ನೇಮಕವನ್ನು ಎದುರಿಸುತ್ತಾನೆ; ಎದುರಿಸುವವರು ಶಿಕ್ಷೆಗೊಳಗಾಗುವರು.",
			"3": "<sup>3</sup> ಕೆಟ್ಟಕೆಲಸ ಮಾಡುವವನಿಗೆ ಅಧಿಪತಿಯಿಂದ ಭಯವಿರುವದೇ ಹೊರತು ಒಳ್ಳೇ ಕೆಲಸ ಮಾಡುವವನಿಗೆ ಭಯವೇನೂ ಇಲ್ಲ. ನೀನು ಅಧಿಕಾರಿಗೆ ಭಯಪಡದೆ ಇರಬೇಕೆಂದು ಅಪೇಕ್ಷಿಸುತ್ತೀಯೋ? ಒಳ್ಳೇದನ್ನು ಮಾಡು; ಆಗ ಆ ಅಧಿಕಾರಿಯಿಂದಲೇ ನಿನಗೆ ಹೊಗಳಿಕೆಯುಂಟಾಗುವದು;",
			"4": "<sup>4</sup> ಅವನು ನಿನ್ನ ಹಿತಕ್ಕೋಸ್ಕರ ದೇವರ ಸೇವಕನಾಗಿದ್ದಾನಲ್ಲಾ. ಆದರೆ ನೀನು ಕೆಟ್ಟದ್ದನ್ನು ಮಾಡಿದರೆ ಭಯಪಡಬೇಕು; ಅವನು ಸುಮ್ಮನೆ ಕೈಯಲ್ಲಿ ಕತ್ತಿಯನ್ನು ಹಿಡಿದಿಲ್ಲ; ಅವನು ದೇವರ ಸೇವಕನಾಗಿದ್ದು ಕೆಟ್ಟದ್ದನ್ನು ನಡಿಸುವವನಿಗೆ ದೇವರ ದಂಡನೆಯನ್ನು ವಿಧಿಸುತ್ತಾನೆ.",
			"5": "<sup>5</sup> ಆದಕಾರಣ ದಂಡನೆಯಾದೀತೆಂದು ಮಾತ್ರವಲ್ಲದೆ ಮನಸ್ಸಿಗೆ ನ್ಯಾಯವಾಗಿ ತೋರುವದರಿಂದಲೂ ಅವನಿಗೆ ಅಧೀನನಾಗುವದು ಅವಶ್ಯ.",
			"6": "<sup>6</sup> ಈ ಕಾರಣದಿಂದಲೇ ನೀವು ಕಂದಾಯವನ್ನು ಕೂಡ ಕೊಡುತ್ತೀರಿ. ಯಾಕಂದರೆ ಕಂದಾಯ ಎತ್ತುವವರು ದೇವರ ಉದ್ಯೋಗಿಗಳಾಗಿದ್ದು ಆ ಕೆಲಸದಲ್ಲಿಯೇ ನಿರತರಾಗಿರುತ್ತಾರೆ.",
			"7": "<sup>7</sup> ಅವರವರಿಗೆ ಸಲ್ಲಿಸತಕ್ಕದ್ದನ್ನು ಸಲ್ಲಿಸಿರಿ; ಯಾರಿಗೆ ಕಂದಾಯವೋ ಅವರಿಗೆ ಕಂದಾಯವನ್ನು, ಯಾರಿಗೆ ಸುಂಕವೋ ಅವರಿಗೆ ಸುಂಕವನ್ನು, ಯಾರಿಗೆ ಭಯವೋ ಅವರಿಗೆ ಭಯವನ್ನು, ಯಾರಿಗೆ ಮರ್ಯಾದೆಯೋ ಅವರಿಗೆ ಮರ್ಯಾದೆಯನ್ನು ಸಲ್ಲಿಸಿರಿ.",
			"8": "<sup>8</sup> ಒಬ್ಬರನ್ನೊಬ್ಬರು ಪ್ರೀತಿಸಬೇಕೆಂಬ ಋಣವೇ ಹೊರತು ಬೇರೆ ಯಾವ ಸಾಲವೂ ನಿಮಗೆ ಇರಬಾರದು. ಮತ್ತೊಬ್ಬರನ್ನು ಪ್ರೀತಿಸುವವನು ಧರ್ಮಪ್ರಮಾಣವನ್ನೆಲ್ಲಾ ನೆರವೇರಿಸಿದ್ದಾನೆ.",
			"9": "<sup>9</sup> ಹೇಗಂದರೆ ವ್ಯಭಿಚಾರ ಮಾಡಬಾರದು, ನರಹತ್ಯಮಾಡಬಾರದು, ಕದಿಯಬಾರದು, ಪರರ ಸೊತ್ತನ್ನು ಆಶಿಸಬಾರದು ಈ ಮೊದಲಾದ ಎಲ್ಲಾ ಕಟ್ಟಳೆಗಳು - ನಿನ್ನ ನೆರೆಯವನನ್ನು ನಿನ್ನಂತೆಯೇ ಪ್ರೀತಿಸಬೇಕು ಎಂಬ ಒಂದೇ ಮಾತಿನಲ್ಲಿ ಅಡಕವಾಗಿವೆ.",
			"10": "<sup>10</sup> ಪ್ರೀತಿಯು ಮತ್ತೊಬ್ಬರಿಗೆ ಯಾವ ಕೇಡನ್ನೂ ಮಾಡುವದಿಲ್ಲ. ಆದಕಾರಣ ಪ್ರೀತಿಯಿಂದಲೇ ಧರ್ಮಪ್ರಮಾಣವು ನೆರವೇರುತ್ತದೆ.",
			"11": "<sup>11</sup> ಈಗಿನ ಕಾಲವು ನಿದ್ದೆಯಿಂದ ಎಚ್ಚರವಾಗತಕ್ಕ ಕಾಲವೆಂದು ಅರಿತು ಇದನ್ನೆಲ್ಲಾ ಮಾಡಿರಿ. ನಾವು ಕ್ರಿಸ್ತನನ್ನು ಮೊದಲು ನಂಬಿದ ಕಾಲದಲ್ಲಿ ಇದ್ದದ್ದಕ್ಕಿಂತ ಈಗ ನಮ್ಮ ವಿಮೋಚನೆಯು ಹತ್ತಿರವಾಯಿತು.",
			"12": "<sup>12</sup> ಇರುಳು ಬಹಳ ಮಟ್ಟಿಗೆ ಕಳೆಯಿತು; ಹಗಲು ಸಮೀಪವಾಯಿತು. ಕತ್ತಲೆಗೆ ಅನುಗುಣವಾದ ಕೃತ್ಯಗಳನ್ನು ಬಿಟ್ಟು ಬೆಳಕಿಗೆ ಅನುಗುಣವಾದ ಆಯುಧಗಳನ್ನು ಧರಿಸಿಕೊಳ್ಳೋಣ.",
			"13": "<sup>13</sup> ದುಂದೌತಣ ಕುಡಿಕತನಗಳಲ್ಲಿಯಾಗಲಿ ಕಾಮ ವಿಲಾಸ ನಿರ್ಲಜ್ಜಾಕೃತ್ಯಗಳಲ್ಲಿಯಾಗಲಿ ಜಗಳ ಹೊಟ್ಟೇಕಿಚ್ಚುಗಳಲ್ಲಿಯಾಗಲಿ ಕಾಲಕಳೆಯದೆ ಹಗಲು ಹೊತ್ತಿಗೆ ತಕ್ಕಹಾಗೆ ಮಾನಸ್ಥರಾಗಿ ನಡೆದುಕೊಳ್ಳೋಣ.",
			"14": "<sup>14</sup> ದೇಹದ ಆಶೆಗಳನ್ನು ಪೂರೈಸುವದಕ್ಕಾಗಿ ಚಿಂತಿಸದೆ ಕರ್ತನಾದ ಯೇಸು ಕ್ರಿಸ್ತನನ್ನು ಧರಿಸಿಕೊಳ್ಳಿರಿ."
		},
		"14": {
			"1": "<sup>1</sup> ನಂಬಿಕೆಯಲ್ಲಿ ದೃಢವಿಲ್ಲದವರನ್ನೂ ಸೇರಿಸಿಕೊಳ್ಳಿರಿ. ಆದರೆ ಸಂದೇಹಾಸ್ಪದವಾದ ಕಾರ್ಯಗಳನ್ನು ಅವರ ಮುಂದೆ ಚರ್ಚಿಸಬೇಡಿರಿ.",
			"2": "<sup>2</sup> ಒಬ್ಬನು ಯಾವದನ್ನಾದರೂ ತಿನ್ನಬಹುದೆಂದು ನಂಬುತ್ತಾನೆ; ನಂಬಿಕೆಯಲ್ಲಿ ದೃಢವಿಲ್ಲದವನು ಕಾಯಿಪಲ್ಯಗಳನ್ನು ಮಾತ್ರ ತಿನ್ನುತ್ತಾನೆ.",
			"3": "<sup>3</sup> ತಿನ್ನುವವನು ತಿನ್ನದವನನ್ನು ಹೀನೈಸಬಾರದು; ತಿನ್ನದವನು ತಿನ್ನುವವನನ್ನು ದೋಷಿಯೆಂದು ಎಣಿಸಬಾರದು. ದೇವರು ಇಬ್ಬರನ್ನೂ ಸೇರಿಸಿಕೊಂಡಿದ್ದಾನಲ್ಲಾ.",
			"4": "<sup>4</sup> ಮತ್ತೊಬ್ಬನ ಸೇವಕನ ವಿಷಯವಾಗಿ ತೀರ್ಪುಮಾಡುವದಕ್ಕೆ ನೀನು ಯಾರು? ಅವನು ನಿರ್ದೋಷಿಯಾಗಿ ನಿಂತರೂ ದೋಷಿಯಾಗಿ ಬಿದ್ದರೂ ಅದು ಅವನ ಯಜಮಾನನಿಗೇ ಸೇರಿದ್ದು. ಅವನು ನಿರ್ದೋಷಿಯಾಗಿ ನಿಲ್ಲಿಸಲ್ಪಡುವನು; ಅವನನ್ನು ನಿಲ್ಲಿಸುವದಕ್ಕೆ ಕರ್ತನು ಶಕ್ತನಾಗಿದ್ದಾನೆ.",
			"5": "<sup>5</sup> ಒಬ್ಬನು ಒಂದು ದಿನಕ್ಕಿಂತ ಮತ್ತೊಂದು ದಿನವನ್ನು ವಿಶೇಷವೆಂದೆಣಿಸುತ್ತಾನೆ; ಮತ್ತೊಬ್ಬನು ಎಲ್ಲಾ ದಿವಸಗಳನ್ನೂ ವಿಶೇಷವೆಂದೆಣಿಸುತ್ತಾನೆ; ಪ್ರತಿಯೊಬ್ಬನು ತನ್ನ ತನ್ನ ಮನಸ್ಸಿನಲ್ಲಿ ನಿಶ್ಚಯಿಸಿಕೊಂಡಿರಬೇಕು.",
			"6": "<sup>6</sup> ದಿನವನ್ನು ಗಣ್ಯಮಾಡುವವನು ಕರ್ತನಿಗಾಗಿ ಅದನ್ನು ಗಣ್ಯಮಾಡುತ್ತಾನೆ. ತಿನ್ನುವವನು ಕರ್ತನಿಗಾಗಿ ತಿನ್ನುತ್ತಾನೆ; ಅವನು ದೇವಸ್ತೋತ್ರ ಮಾಡುತ್ತಾನಲ್ಲಾ. ತಿನ್ನದವನು ಸಹ ಕರ್ತನಿಗಾಗಿಯೇ ತಿನ್ನದೆ ದೇವಸ್ತೋತ್ರ ಮಾಡುತ್ತಾನೆ.",
			"7": "<sup>7</sup> ನಮ್ಮಲ್ಲಿ ಒಬ್ಬನಾದರೂ ತನಗಾಗಿ ಬದುಕುವದೂ ಇಲ್ಲ, ತನಗಾಗಿ ಸಾಯುವದೂ ಇಲ್ಲ.",
			"8": "<sup>8</sup> ನಾವು ಬದುಕಿದರೆ ಕರ್ತನಿಗಾಗಿ ಬದುಕುತ್ತೇವೆ; ಸತ್ತರೆ ಕರ್ತನಿಗಾಗಿ ಸಾಯುತ್ತೇವೆ;",
			"9": "<sup>9</sup> ಬದುಕಿದರೂ ಸತ್ತರೂ ನಾವು ಕರ್ತನವರೇ. ಯಾಕಂದರೆ ಸತ್ತವರಿಗೂ ಜೀವಿಸುವವರಿಗೂ ಒಡೆಯನಾಗಿರಬೇಕೆಂತಲೇ ಕ್ರಿಸ್ತನು ಸತ್ತು ಜೀವಿತನಾದನು.",
			"10": "<sup>10</sup> ತಿನ್ನದವನೇ, ನಿನ್ನ ಸಹೋದರನ ವಿಷಯವಾಗಿ ನೀನು ತೀರ್ಪುಮಾಡುವದೇನು? ತಿನ್ನುವವನೇ, ನಿನ್ನ ಸಹೋದರನನ್ನು ನೀನು ಹೀನೈಸುವದೇನು? ನಾವೆಲ್ಲರೂ ದೇವರ ನ್ಯಾಯಾಸನದ ಮುಂದೆ ನಿಲ್ಲಬೇಕಲ್ಲಾ.",
			"11": "<sup>11</sup> ನನ್ನ ಜೀವದಾಣೆ, ಎಲ್ಲರೂ ನನಗೆ ಅಡ್ಡ ಬೀಳುವರು, ಎಲ್ಲರೂ ನನ್ನನ್ನು ದೇವರೆಂದು ಪ್ರತಿಜ್ಞೆ ಮಾಡುವರು ಎಂಬದಾಗಿ ಕರ್ತನು ಹೇಳಿದ್ದಾನೆ ಎಂದು ಬರೆದದೆ.",
			"12": "<sup>12</sup> ಹೀಗಿರಲಾಗಿ ನಮ್ಮಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬನು ತನ್ನ ತನ್ನ ವಿಷಯವಾಗಿ ದೇವರ ಮುಂದೆ ಉತ್ತರಕೊಡಬೇಕು.",
			"13": "<sup>13</sup> ಆದಕಾರಣ ಇನ್ನು ಮೇಲೆ ಒಬ್ಬರ ವಿಷಯದಲ್ಲೊಬ್ಬರು ತೀರ್ಪುಮಾಡದೆ ಇರೋಣ. ಅದಕ್ಕೆ ಬದಲಾಗಿ ಸಹೋದರನ ಎದುರಿಗೆ ಅಡ್ಡಿಯನ್ನಾಗಲಿ ಎಡತಡೆಯನ್ನಾಗಲಿ ಹಾಕಬಾರದೆಂದು ತೀರ್ಮಾನಿಸಿಕೊಳ್ಳಿರಿ.",
			"14": "<sup>14</sup> ಯಾವ ಪದಾರ್ಥವೂ ಸ್ವತಃ ಅಶುದ್ಧವಾದದ್ದಲ್ಲವೆಂದು ಬಲ್ಲೆನು, ಮತ್ತು ಕರ್ತನಾದ ಯೇಸುವಿನಲ್ಲಿದ್ದುಕೊಂಡು ದೃಢವಾಗಿ ನಂಬಿದ್ದೇನೆ; ಆದರೆ ಯಾವದಾದರೂ ಒಂದು ಪದಾರ್ಥವನ್ನು ಅಶುದ್ಧವೆಂದು ಒಬ್ಬನು ಭಾವಿಸಿದರೆ ಅವನಿಗೆ ಅದು ಅಶುದ್ಧ.",
			"15": "<sup>15</sup> ನೀನು ತೆಗೆದುಕೊಳ್ಳುವ ಆಹಾರದಿಂದ ನಿನ್ನ ಸಹೋದರನ ಮನಸ್ಸಿಗೆ ನೋವಾದರೆ ನೀನು ಪ್ರೀತಿಗೆ ತಕ್ಕಂತೆ ಇನ್ನು ನಡೆಯುವವನಲ್ಲ. ಯಾವನಿಗೋಸ್ಕರ ಕ್ರಿಸ್ತನು ಪ್ರಾಣಕೊಟ್ಟನೋ ಅವನನ್ನು ನಿನಗೆ ಇಷ್ಟವಾದ ಆಹಾರದ ನಿಮಿತ್ತ ಕೆಡಿಸಬಾರದು.",
			"16": "<sup>16</sup> ನಿಮಗಿರುವ ಮೇಲು ದೂಷಣೆಗೆ ಆಸ್ಪದವಾಗಬಾರದು.",
			"17": "<sup>17</sup> ಯಾಕಂದರೆ ತಿನ್ನುವದೂ ಕುಡಿಯುವದೂ ದೇವರ ರಾಜ್ಯವಲ್ಲ; ನೀತಿಯೂ ಸಮಾಧಾನವೂ ಪವಿತ್ರಾತ್ಮನಿಂದಾಗುವ ಆನಂದವೂ ಆಗಿದೆ.",
			"18": "<sup>18</sup> ಈ ಪ್ರಕಾರವಾಗಿ ಕ್ರಿಸ್ತನ ಸೇವೆಯನ್ನು ಮಾಡುವವನು ದೇವರಿಗೆ ಮೆಚ್ಚಿಕೆಯಾದವನಾಗಿಯೂ ಮನುಷ್ಯರಿಗೆ ಸಂಭಾವಿತನಾಗಿಯೂ ಇರುವನಷ್ಟೆ.",
			"19": "<sup>19</sup> ಆದದರಿಂದ ನಾವು ಸಮಾಧಾನಕ್ಕೂ ಪರಸ್ಪರ ಭಕ್ತಿವೃದ್ಧಿಗೂ ಅನುಕೂಲವಾಗಿರುವವುಗಳನ್ನು ಸಾಧಿಸಿಕೊಳ್ಳೋಣ.",
			"20": "<sup>20</sup> ದೇವರು ಕಟ್ಟಿದ್ದನ್ನು ನೀನು ಆಹಾರದ ನಿಮಿತ್ತವಾಗಿ ಕೆಡವಬೇಡ. ಎಲ್ಲಾ ಪದಾರ್ಥಗಳೂ ಶುದ್ಧವೇ; ಆದರೆ ತಿಂದು ಮತ್ತೊಬ್ಬರಿಗೆ ವಿಘ್ನವನ್ನುಂಟುಮಾಡುವದು ಕೆಟ್ಟದ್ದು.",
			"21": "<sup>21</sup> ಮಾಂಸ ತಿನ್ನುವದನ್ನಾಗಲಿ ದ್ರಾಕ್ಷಾರಸ ಕುಡಿಯುವದನ್ನಾಗಲಿ ನಿನ್ನ ಸಹೋದರನಿಗೆ ಅಡ್ಡಿಯನ್ನುಂಟುಮಾಡುವ ಬೇರೆ ಯಾವದನ್ನಾಗಲಿ ಬಿಟ್ಟುಬಿಡುವದೇ ಒಳ್ಳೇದು.",
			"22": "<sup>22</sup> ನಿನಗಿರುವ ನಂಬಿಕೆಯು ದೇವರಿಗೆ ಮಾತ್ರ ಗೋಚರವಾಗಿ ನಿನ್ನ ಮಟ್ಟಿಗೇ ಇರಲಿ. ತಾನು ಒಪ್ಪಿದ ಕೆಲಸದ ವಿಷಯದಲ್ಲಿ ಇದು ಸರಿಯೋ ಸರಿಯಲ್ಲವೋ ಎಂದು ಸಂಶಯ ಪಡದವನೇ ಧನ್ಯನು.",
			"23": "<sup>23</sup> ಸಂಶಯಪಟ್ಟು ಉಣ್ಣುವವನು ಸಂಶಯದಿಂದ ಉಣ್ಣುವ ಕಾರಣ ದೋಷಿಯಾಗಿದ್ದಾನೆ. ಯಾವದಕ್ಕೆ ನಂಬಿಕೆಯು ಆಧಾರವಿಲ್ಲವೋ ಅದು ಪಾಪ."
		},
		"15": {
			"1": "<sup>1</sup> ದೃಢವಾದ ನಂಬಿಕೆಯುಳ್ಳ ನಾವು ನಮ್ಮ ಸುಖವನ್ನು ನೋಡಿಕೊಳ್ಳದೆ ದೃಢವಿಲ್ಲದವರ ಅನುಮಾನಗಳನ್ನು ಸಹಿಸಿಕೊಳ್ಳಬೇಕು.",
			"2": "<sup>2</sup> ನಮ್ಮಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬನು ತನ್ನ ನೆರೆಯವನ ಹಿತವನ್ನೂ ಭಕ್ತಿವೃದ್ಧಿಯನ್ನೂ ಅಪೇಕ್ಷಿಸುವವನಾಗಿ ಅವನ ಸುಖವನ್ನು ನೋಡಿಕೊಳ್ಳಲಿ.",
			"3": "<sup>3</sup> ಯಾಕಂದರೆ ಕ್ರಿಸ್ತನು ಸಹ ತನ್ನ ಸುಖವನ್ನು ನೋಡಿಕೊಳ್ಳಲಿಲ್ಲ. ದೇವರೇ, ನಿನ್ನನ್ನು ದೂಷಿಸಿದವರ ದೂಷಣೆಗಳು ನನ್ನ ಮೇಲೆ ಬಂದವು ಎಂಬದಾಗಿ ಬರೆದಿರುವಂತೆ ಆತನಿಗೆ ಸಂಭವಿಸಿತಲ್ಲಾ.",
			"4": "<sup>4</sup> ಪೂರ್ವದಲ್ಲಿ ಬರೆದದ್ದೆಲ್ಲಾ ನಮ್ಮನ್ನು ಉಪದೇಶಿಸುವದಕ್ಕಾಗಿ ಬರೆಯಲ್ಪಟ್ಟಿತು. ನಾವು ಓದಿ ಸ್ಥಿರಚಿತ್ತವನ್ನೂ ಆದರಣೆಯನ್ನೂ ಹೊಂದಿ ರಕ್ಷಣೆಯ ನಿರೀಕ್ಷೆಯುಳ್ಳವರಾಗಿರುವಂತೆ ಆ ಗ್ರಂಥಗಳು ಬರೆಯಲ್ಪಟ್ಟವು.",
			"5": "<sup>5</sup> ಆ ಸ್ಥಿರಚಿತ್ತವನ್ನೂ ಆದರಣೆಯನ್ನೂ ಕೊಡುವ ದೇವರು ನೀವು ಕ್ರಿಸ್ತ ಯೇಸುವನ್ನು ಅನುಸರಿಸಿ ಒಂದೇ ಮನಸ್ಸುಳ್ಳವರಾಗಿರುವಂತೆ ನಿಮಗೆ ದಯಪಾಲಿಸಲಿ.",
			"6": "<sup>6</sup> ಹೀಗೆ ನೀವು ಏಕ ಮನಸ್ಸಿನಿಂದ ಒಮ್ಮುಖವಾಗಿ ನಮ್ಮ ಕರ್ತನಾದ ಯೇಸು ಕ್ರಿಸ್ತನ ತಂದೆಯಾದ ದೇವರನ್ನು ಕೊಂಡಾಡುವಿರಿ.",
			"7": "<sup>7</sup> ಆದದರಿಂದ ಕ್ರಿಸ್ತನು ನಿಮ್ಮನ್ನು ಸೇರಿಸಿಕೊಂಡಂತೆ ನೀವು ಸಹ ಒಬ್ಬರನ್ನೊಬ್ಬರು ಸೇರಿಸಿಕೊಂಡು ದೇವರ ಮಹಿಮೆಯನ್ನು ಪ್ರಕಾಶಪಡಿಸಿರಿ.",
			"8": "<sup>8</sup> ಯಾಕಂದರೆ ನಾನು ಹೇಳುವದೇನಂದರೆ - ಕ್ರಿಸ್ತನು ಪಿತೃಗಳಿಗೆ ಉಂಟಾದ ದೇವರ ವಾಗ್ದಾನಗಳನ್ನು ದೃಢಪಡಿಸಿ ಆತನ ಸತ್ಯವನ್ನು ತೋರಿಸುವದಕ್ಕಾಗಿ ಸುನ್ನತಿಯೆಂಬ ಸಂಸ್ಕಾರಕ್ಕೆ ಒಳಗಾದನು;",
			"9": "<sup>9</sup> ಜನಾಂಗಗಳಾದರೆ ಕಾರುಣ್ಯದ ನಿಮಿತ್ತ ದೇವರನ್ನು ಕೊಂಡಾಡುತ್ತಾರೆಂಬದೇ. ಇದಕ್ಕೆ ಸರಿಯಾಗಿ ಶಾಸ್ತ್ರದಲ್ಲಿಯೂ ಬರೆದದೆ. ಹೇಗಂದರೆ - ಈ ಕಾರಣದಿಂದ ಅನ್ಯಜನಗಳ ಮಧ್ಯದಲ್ಲಿ ನಿನ್ನನ್ನು ಸ್ತುತಿಸುವೆನು; ನಿನ್ನ ನಾಮವನ್ನು ಸಂಕೀರ್ತಿಸುವೆನು ಎಂದೂ",
			"10": "<sup>10</sup> ಇನ್ನೊಂದು ಸ್ಥಳದಲ್ಲಿ - ಜನಾಂಗಗಳಿರಾ, ದೇವರ ಜನರೊಡನೆ ಉಲ್ಲಾಸಪಡಿರಿ ಎಂದೂ",
			"11": "<sup>11</sup> ಬೇರೊಂದು ಸ್ಥಳದಲ್ಲಿ - ಸರ್ವಜನಾಂಗಗಳೇ, ಕರ್ತನನ್ನು ಕೀರ್ತಿಸಿರಿ, ಸಮಸ್ತಪ್ರಜೆಗಳೂ ಆತನನ್ನು ಸಂಕೀರ್ತಿಸಲಿ ಎಂದೂ ಬರೆದದೆ.",
			"12": "<sup>12</sup> ಮತ್ತೊಂದು ಸ್ಥಳದಲ್ಲಿ ಯೆಶಾಯನು ಹೇಳುವದೇನಂದರೆ - ಇಷಯನ ಅಂಕುರದವನು ಅಂದರೆ ಜನಾಂಗಗಳನ್ನು ಆಳತಕ್ಕವನು ಬರುತ್ತಾನೆ; ಜನಾಂಗಗಳು ಆತನನ್ನು ನಿರೀಕ್ಷಿಸುತ್ತಿರುವರು.",
			"13": "<sup>13</sup> ನಿರೀಕ್ಷೆಯ ಮೂಲನಾದ ದೇವರು ನಂಬಿಕೆಯಿಂದುಂಟಾಗುವ ಸಂತೋಷವನ್ನೂ ಮನಶ್ಶಾಂತಿಯನ್ನೂ ನಿಮಗೆ ಪರಿಪೂರ್ಣವಾಗಿ ದಯಪಾಲಿಸಿ ನೀವು ಪವಿತ್ರಾತ್ಮನ ಬಲಗೂಡಿದವರಾಗಿ ನಿರೀಕ್ಷೆಯಲ್ಲಿ ಅಭಿವೃದ್ಧಿಯನ್ನು ಹೊಂದುವಂತೆ ಅನುಗ್ರಹಿಸಲಿ.",
			"14": "<sup>14</sup> ನನ್ನ ಸಹೋದರರೇ, ನೀವಂತೂ ಒಳ್ಳೇತನದಿಂದ ಭರಿತರಾಗಿಯೂ ಸಕಲ ಜ್ಞಾನದಿಂದ ತುಂಬಿದವರಾಗಿಯೂ ಒಬ್ಬರಿಗೊಬ್ಬರು ಬುದ್ಧಿ ಹೇಳುವದಕ್ಕೆ ಶಕ್ತರಾಗಿಯೂ ಇದ್ದೀರೆಂದು ನಿಮ್ಮ ವಿಷಯದಲ್ಲಿ ನನಗೂ ಗೊತ್ತಿದೆ.",
			"15": "<sup>15</sup> ಆದರೂ ನಿಮಗೆ ಜ್ಞಾಪಕಕೊಡಬೇಕೆಂದು ಅಲ್ಲಲ್ಲಿ ಹೆಚ್ಚಾದ ಧೈರ್ಯದಿಂದ ಬರೆದಿದ್ದೇನೆ. ಯಾಕಂದರೆ ನಾನು ಅನ್ಯಜನರಿಗೋಸ್ಕರ ಯೇಸು ಕ್ರಿಸ್ತನ ಸೇವಕನಾಗುವದಕ್ಕೆ ನನಗೆ ದೇವರ ಅನುಗ್ರಹವಾಯಿತು. ನಾನು ದೇವರ ಸುವಾರ್ತೆಯ ಸೇವೆಯನ್ನು ಯಾಜಕಸೇವೆ ಎಂಬಂತೆ ನಡಿಸಿ ಅನ್ಯದೇಶಸ್ಥರೆಂಬ ಕಾಣಿಕೆಯು ಪವಿತ್ರಾತ್ಮನ ಮೂಲಕವಾಗಿ ಪವಿತ್ರವಾಗಿದ್ದು ದೇವರಿಗೆ ಸಮರ್ಪಕವಾಗುವಂತೆ ಯತ್ನೈಸುವವನಾಗಿದ್ದೇನೆ.",
			"17": "<sup>17</sup> ದೇವರ ಸೇವೆಯಲ್ಲಿ ಹೊಗಳಿಕೊಳ್ಳುವದಕ್ಕೆ ಕ್ರಿಸ್ತ ಯೇಸುವಿನಲ್ಲಿ ನನಗೆ ಕಾರಣವುಂಟು.",
			"18": "<sup>18</sup> ಕ್ರಿಸ್ತನು ಅನ್ಯಜನಗಳನ್ನು ತನಗೆ ಅಧೀನಮಾಡಿಕೊಳ್ಳುವದಕ್ಕಾಗಿ ನನ್ನ ಕೈಯಿಂದ ಮಾತಿನಲ್ಲಿಯೂ ಕ್ರಿಯೆಯಲ್ಲಿಯೂ ಸೂಚಕಕಾರ್ಯ ಅದ್ಭುತಕಾರ್ಯಗಳ ಬಲದಿಂದಲೂ ಪವಿತ್ರಾತ್ಮನ ಬಲದಿಂದಲೂ ಮಾಡಿಸಿದ ಕಾರ್ಯಗಳನ್ನೇ ಹೊರತು ಬೇರೆ ಯಾವ ಕಾರ್ಯಗಳನ್ನೂ ಹೇಳುವದಕ್ಕೆ ನನಗೆ ಧೈರ್ಯಸಾಲದು.",
			"19": "<sup>19</sup> ನಾನು ಯೆರೂಸಲೇಮ್ ಮೊದಲುಗೊಂಡು ಇಲ್ಲುರಿಕ ಸೀಮೆಯ ಪರ್ಯಂತರಕ್ಕೂ ಸುತ್ತಿ ಕ್ರಿಸ್ತನ ಸುವಾರ್ತೆಯ ಸಾರೋಣವನ್ನು ನೆರವೇರಿಸಿದ್ದೇನೆ.",
			"20": "<sup>20</sup> ಮತ್ತೊಬ್ಬರು ಹಾಕಿದ ಅಸ್ತಿವಾರದ ಮೇಲೆ ಕಟ್ಟಬಾರದೆಂದು ಮನಸ್ಸಿನಲ್ಲಿ ನೆನಸಿ ಇತರರು ಕ್ರಿಸ್ತನ ಹೆಸರನ್ನು ತಿಳಿಸಿದ ಕಡೆಯಲ್ಲಿ ಸುವಾರ್ತೆಯನ್ನು ಪ್ರಕಟಿಸುವದಿಲ್ಲವೆಂಬದಾಗಿ ನಿಷ್ಕರ್ಷೆಮಾಡಿಕೊಂಡೆನು.",
			"21": "<sup>21</sup> ಯಾರಿಗೆ ಆತನ ಸಮಾಚಾರ ಬಂದಿರಲಿಲ್ಲವೋ ಅವರೇ ಆತನನ್ನು ನೋಡುವರು; ಯಾರು ಆತನ ವರ್ತಮಾನವನ್ನು ಕೇಳಿರಲಿಲ್ಲವೋ ಅವರೇ ಗ್ರಹಿಸಿಕೊಳ್ಳುವರು ಎಂಬದಾಗಿ ಬರೆದಿರುವ ಪ್ರಕಾರ ನನ್ನ ಕೆಲಸವನ್ನು ನಡಿಸಿದ್ದೇನೆ.",
			"22": "<sup>22</sup> ಈ ಕಾರಣದಿಂದ ನಿಮ್ಮ ಬಳಿಗೆ ಬರುವದಕ್ಕೆ ನನಗೆ ಅನೇಕಾವರ್ತಿ ಅಭ್ಯಂತರವಾಯಿತು.",
			"23": "<sup>23</sup> ಆದರೆ ಇನ್ನು ಮೇಲೆ ಈ ಪ್ರಾಂತ್ಯಗಳಲ್ಲಿ ನನಗೆ ಅನುಕೂಲವಿಲ್ಲದ್ದರಿಂದಲೂ ನಿಮ್ಮ ಬಳಿಗೆ ಬರುವದಕ್ಕೆ ಅನೇಕ ವರುಷಗಳಿಂದ ಅಭಿಲಾಷೆ ಇರುವದರಿಂದಲೂ",
			"24": "<sup>24</sup> ನಾನು ಸ್ಪೇನ್ ದೇಶಕ್ಕೆ ಹೋಗುವಾಗ ನನ್ನ ಮಾರ್ಗದಲ್ಲಿ ನಿಮ್ಮ ದರ್ಶನವನ್ನು ಮಾಡಬೇಕೆಂಬ ಅಭಿಪ್ರಾಯ ಉಂಟು. ತರುವಾಯ ನಿಮ್ಮ ಸಹವಾಸದಲ್ಲಿ ನನಗೆ ಸ್ವಲ್ಪಮಟ್ಟಿಗೆ ಸಂತೃಪ್ತಿಯಾದ ಮೇಲೆ ನೀವು ಆ ದೇಶಕ್ಕೆ ನನ್ನ ಪ್ರಯಾಣವನ್ನು ಸಾಗಿಸುವಿರೆಂದು ನಂಬುತ್ತೇನೆ.",
			"25": "<sup>25</sup> ಆದರೆ ಈಗ ನಾನು ದೇವಜನರಿಗೆ ಒಂದು ಧರ್ಮಕಾರ್ಯವನ್ನು ಮಾಡುವವನಾಗಿ ಯೆರೂಸಲೇಮಿಗೆ ಹೋಗುತ್ತೇನೆ.",
			"26": "<sup>26</sup> ಯಾಕಂದರೆ ಯೆರೂಸಲೇಮಿನಲ್ಲಿರುವ ದೇವಜನರೊಳಗೆ ಬಡವರಿಗೋಸ್ಕರ ಸ್ವಲ್ಪ ಹಣದ ಸಹಾಯವನ್ನು ಮಾಡಬೇಕೆಂಬದಾಗಿ ಮಕೆದೋನ್ಯ ಅಖಾಯ ಸೀಮೆಗಳವರು ಇಷ್ಟಪಟ್ಟಿದ್ದಾರೆ.",
			"27": "<sup>27</sup> ಇದನ್ನು ಮಾಡುವದಕ್ಕೆ ಇವರು ಇಷ್ಟಪಟ್ಟದ್ದಲ್ಲದೆ ಇವರು ಅವರ ಹಂಗಿನವರಾಗಿದ್ದಾರೆ. ಹೇಗಂದರೆ ಅನ್ಯಜನರು ಅವರ ಆತ್ಮಸಂಬಂಧವಾದ ಸಂಪತ್ತಿಗೆ ಪಾಲುಗಾರರಾದ ಮೇಲೆ ಲೋಕಸಂಬಂಧವಾದ ಕಾರ್ಯಗಳಲ್ಲಿ ಅವರಿಗೆ ಸೇವೆಮಾಡುವ ಹಂಗಿನಲ್ಲಿದ್ದಾರಲ್ಲವೇ.",
			"28": "<sup>28</sup> ನಾನು ಈ ಕೆಲಸವನ್ನು ತೀರಿಸಿಕೊಂಡು ಈ ಪ್ರೀತಿಯ ಫಲವನ್ನು ಅಲ್ಲಿಯವರ ವಶಕ್ಕೆ ಯಥಾಕ್ರಮವಾಗಿ ಕೊಟ್ಟ ಮೇಲೆ ನಿಮ್ಮ ಮಾರ್ಗವಾಗಿ ಸ್ಪೇನ್ ದೇಶಕ್ಕೆ ಹೋಗುವೆನು.",
			"29": "<sup>29</sup> ಇದಲ್ಲದೆ ನಿಮ್ಮ ಬಳಿಗೆ ಬರುವಾಗ ಕ್ರಿಸ್ತನಿಂದಾಗುವ ವರಗಳನ್ನು ಹೇರಳವಾಗಿ ತರುವೆನೆಂದು ಬಲ್ಲೆನು.",
			"30": "<sup>30</sup> ಸಹೋದರರೇ, ನೀವು ನನಗೋಸ್ಕರ ದೇವರ ಮುಂದೆ ಮಾಡುವ ಪ್ರಾರ್ಥನೆಗಳಲ್ಲಿ ನನ್ನೊಂದಿಗೆ ಬಲೀಯವಾಗಿ ವಿಜ್ಞಾಪಿಸಿಕೊಳ್ಳಬೇಕೆಂದು ನಮ್ಮ ಕರ್ತನಾದ ಯೇಸು ಕ್ರಿಸ್ತನ ನಿಮಿತ್ತವಾಗಿಯೂ ಪವಿತ್ರಾತ್ಮನಿಂದಾಗುವ ಪ್ರೀತಿಯ ನಿಮಿತ್ತವಾಗಿಯೂ ನಿಮ್ಮನ್ನು ಬೇಡಿಕೊಳ್ಳುತ್ತೇನೆ.",
			"31": "<sup>31</sup> ದೇವರು ನನ್ನನ್ನು ಯೂದಾಯದಲ್ಲಿರುವ ಅವಿಧೇಯರ ಕೈಗೆ ತಪ್ಪಿಸಬೇಕೆಂತಲೂ ಯೆರೂಸಲೇಮಿಗೋಸ್ಕರ ನಾನು ಮಾಡುವಂಥ ಈ ಧರ್ಮಕಾರ್ಯವು ಅಲ್ಲಿರುವ ದೇವಜನರಿಗೆ ಹಿತಕರವಾಗಿ ತೋರಬೇಕೆಂತಲೂ ಪ್ರಾರ್ಥಿಸಿರಿ.",
			"32": "<sup>32</sup> ಹೀಗೆ ನಾನು ದೇವರ ಚಿತ್ತಾನುಸಾರ ಆನಂದವುಳ್ಳವನಾಗಿ ನಿಮ್ಮಲ್ಲಿಗೆ ಬಂದು ನಿಮ್ಮ ಸಂಗಡ ವಿಶ್ರಾಂತಿ ಹೊಂದಲಾಗುವದು.",
			"33": "<sup>33</sup> ಶಾಂತಿದಾಯಕನಾದ ದೇವರು ನಿಮ್ಮೆಲ್ಲರೊಂದಿಗಿರಲಿ. ಆಮೆನ್."
		},
		"16": {
			"1": "<sup>1</sup> ನಾನು ನಿಮ್ಮ ಬಳಿಗೆ ಕಳುಹಿಸುವ ನಮ್ಮ ಸಹೋದರಿಯಾದ ಫೊಯಿಬೆಯನ್ನು ಪ್ರೀತಿಯಿಂದ ಅಂಗೀಕರಿಸಿರಿ. ಆಕೆಯು ಕೆಂಕ್ರೆಯ ಪಟ್ಟಣದಲ್ಲಿರುವ ಸಭೆಯ ಸೇವಕಳಾಗಿದ್ದಾಳೆ;",
			"2": "<sup>2</sup> ನೀವು ಆಕೆಯನ್ನು ಕರ್ತನ ಶಿಷ್ಯಳೆಂದು ದೇವಜನರಿಗೆ ತಕ್ಕ ಹಾಗೆ ಸೇರಿಸಿಕೊಂಡು ಸಹಾಯವು ಯಾವ ವಿಷಯದಲ್ಲಿ ಬೇಕಾಗಿದ್ದರೂ ಸಹಾಯವನ್ನು ಮಾಡಿರಿ. ಆಕೆಯು ನನಗೂ ಅನೇಕರಿಗೂ ಸಹಾಯ ಮಾಡಿದವಳು.",
			"3": "<sup>3</sup> ಕ್ರಿಸ್ತ ಯೇಸುವಿನ ಸೇವೆಯಲ್ಲಿ ನನ್ನ ಜೊತೆಕೆಲಸದವರಾದ ಪ್ರಿಸ್ಕಳಿಗೂ ಅಕ್ವಿಲನಿಗೂ ನನ್ನ ವಂದನೆಗಳನ್ನು ಹೇಳಿರಿ.",
			"4": "<sup>4</sup> ಅವರು ನನ್ನ ಪ್ರಾಣದ ಸಂರಕ್ಷಣೆಗಾಗಿ ತಮ್ಮ ಪ್ರಾಣಗಳನ್ನು ಅಪಾಯಕ್ಕೆ ಗುರಿಮಾಡಿದರು. ನಾನು ಮಾತ್ರವೇ ಅಲ್ಲ, ಅನ್ಯಜನರ ಸಭೆಗಳವರೆಲ್ಲರೂ ಅವರ ಉಪಕಾರವನ್ನು ಸ್ಮರಿಸುತ್ತಾರೆ.",
			"5": "<sup>5</sup> ಅವರ ಮನೆಯಲ್ಲಿ ಕೂಡುವ ಸಭೆಗೆ ಸಹ ನನ್ನ ವಂದನೆಯನ್ನು ಹೇಳಿರಿ. ಆಸ್ಯ ಸೀಮೆಯಲ್ಲಿ ಕ್ರಿಸ್ತನಿಗೆ ಪ್ರಥಮಫಲವಾಗಿರುವ ನನ್ನ ಪ್ರಿಯನಾದ ಎಪೈನೆತನಿಗೆ ವಂದನೆ.",
			"6": "<sup>6</sup> ನಿಮಗೋಸ್ಕರ ಬಹು ಶ್ರಮೆಪಟ್ಟ ಮರಿಯಳಿಗೆ ವಂದನೆ.",
			"7": "<sup>7</sup> ನನ್ನ ಸ್ವಜನರೂ ಜೊತೆ ಸೆರೆಯವರೂ ಆಗಿರುವ ಅಂದ್ರೋನಿಕನಿಗೂ ಯೂನ್ಯನಿಗೂ ವಂದನೆ; ಅವರು ಅಪೊಸ್ತಲರಲ್ಲಿ ಪ್ರಸಿದ್ಧರಾಗಿದ್ದಾರೆ, ಮತ್ತು ನನಗಿಂತ ಮುಂಚೆ ಕ್ರಿಸ್ತನನ್ನು ನಂಬುವವರಾದರು.",
			"8": "<sup>8</sup> ಕರ್ತನಲ್ಲಿ ನನ್ನ ಪ್ರಿಯನಾದ ಅಂಪ್ಲಿಯಾತನಿಗೆ ವಂದನೆ.",
			"9": "<sup>9</sup> ಕ್ರಿಸ್ತನ ಸೇವೆಯಲ್ಲಿ ನಮ್ಮ ಜೊತೆ ಕೆಲಸದವನಾದ ಉರ್ಬಾನನಿಗೂ ನನ್ನ ಪ್ರಿಯನಾದ ಸ್ತಾಖುನಿಗೂ ವಂದನೆಗಳು.",
			"10": "<sup>10</sup> ಕ್ರಿಸ್ತನ ಸೇವೆಯಲ್ಲಿ ಪರಿಶೋಧಿತನಾದ ಅಪೆಲ್ಲನಿಗೆ ವಂದನೆ. ಅರಿಸ್ತೊಬೂಲನ ಮನೆಯವರಿಗೆ ವಂದನೆಗಳು.",
			"11": "<sup>11</sup> ನನ್ನ ಸ್ವಜನದವನಾದ ಹೆರೊಡಿಯೋನನಿಗೆ ವಂದನೆ. ನಾರ್ಕಿಸ್ಸನ ಮನೆಯವರೊಳಗೆ ಕ್ರಿಸ್ತನನ್ನು ನಂಬಿದವರಿಗೆ ವಂದನೆಗಳು.",
			"12": "<sup>12</sup> ಕರ್ತನ ಸೇವೆಯಲ್ಲಿ ಪ್ರಯಾಸಪಟ್ಟವರಾದ ತ್ರುಫೈನಳಿಗೂ ತ್ರುಫೋಸಳಿಗೂ ವಂದನೆಗಳು. ಕರ್ತನ ಸೇವೆಯಲ್ಲಿ ಬಹಳವಾಗಿ ಪ್ರಯಾಸಪಟ್ಟಿರುವ ಪ್ರಿಯ ಪೆರ್ಸೀಸಳಿಗೆ ವಂದನೆ.",
			"13": "<sup>13</sup> ಕರ್ತನ ಭಕ್ತರಲ್ಲಿ ಪ್ರಸಿದ್ಧನಾದ ರೂಫನಿಗೂ ನನಗೆ ತಾಯಿಯಂತಿರುವ ಅವನ ತಾಯಿಗೂ ವಂದನೆಗಳು.",
			"14": "<sup>14</sup> ಅಸುಂಕ್ರಿತನಿಗೂ ಪ್ಲೆಗೋನನಿಗೂ ಹೆರ್ಮೇಯನಿಗೂ ಪತ್ರೋಬನಿಗೂ ಹೆರ್ಮಾನನಿಗೂ ಅವರೊಂದಿಗಿರುವ ಸಹೋದರರಿಗೂ ವಂದನೆಗಳು.",
			"15": "<sup>15</sup> ಫಿಲೊಲೊಗನಿಗೂ ಯೂಲ್ಯಳಿಗೂ ನೇರ್ಯನಿಗೂ ಅವನ ತಂಗಿಗೂ ಒಲುಂಪನಿಗೂ ಇವರೊಂದಿಗಿರುವ ದೇವಜನರೆಲ್ಲರಿಗೂ ವಂದನೆಗಳು.",
			"16": "<sup>16</sup> ಪವಿತ್ರವಾದ ಮುದ್ದಿಟ್ಟು ಒಬ್ಬರನ್ನೊಬ್ಬರು ವಂದಿಸಿರಿ. ಕ್ರಿಸ್ತನ ಸಭೆಗಳೆಲ್ಲಾ ನಿಮ್ಮನ್ನು ವಂದಿಸುತ್ತವೆ.",
			"17": "<sup>17</sup> ಸಹೋದರರೇ, ನೀವು ಹೊಂದಿದ ಉಪದೇಶಕ್ಕೆ ವಿರುದ್ಧವಾಗಿ ಭೇದಗಳನ್ನೂ ವಿಘ್ನಗಳನ್ನೂ ನಿಮ್ಮಲ್ಲಿ ಉಂಟುಮಾಡುವವರನ್ನು ಗುರುತಿಟ್ಟು ಅವರನ್ನು ಬಿಟ್ಟು ತೊಲಗಿಹೋಗಿರಿ ಎಂದು ನಿಮ್ಮನ್ನು ಬೇಡಿಕೊಳ್ಳುತ್ತೇನೆ.",
			"18": "<sup>18</sup> ಅಂಥವರು ನಮ್ಮ ಕರ್ತನಾದ ಕ್ರಿಸ್ತನ ಸೇವೆಯನ್ನು ಮಾಡದೆ ತಮ್ಮ ಹೊಟ್ಟೆಯ ಸೇವೆಯನ್ನೇ ಮಾಡುವವರಾಗಿ ನಯದ ನುಡಿಗಳಿಂದಲೂ ಹೊಗಳಿಕೆಯ ಮಾತುಗಳಿಂದಲೂ ನಿಷ್ಕಪಟಿಗಳ ಹೃದಯಗಳನ್ನು ಮೋಸಗೊಳಿಸುತ್ತಾರೆ.",
			"19": "<sup>19</sup> ನಿಮ್ಮ ವಿಧೇಯತ್ವವು ಎಲ್ಲರಿಗೂ ಪ್ರಸಿದ್ಧವಾದದರಿಂದ ಇದನ್ನು ಬರೆದಿದ್ದೇನೆ; ನಿಮ್ಮ ವಿಷಯದಲ್ಲಿ ಆನಂದಪಡುತ್ತೇನೆ. ನೀವು ಒಳ್ಳೇತನದ ವಿಷಯದಲ್ಲಿ ಜಾಣರೂ ಕೆಟ್ಟತನದ ವಿಷಯದಲ್ಲಿ ಕಳಂಕವಿಲ್ಲದವರೂ ಆಗಿರಬೇಕೆಂದು ಅಪೇಕ್ಷಿಸುತ್ತೇನೆ.",
			"20": "<sup>20</sup> ಶಾಂತಿದಾಯಕನಾದ ದೇವರು ಶೀಘ್ರವಾಗಿ ಸೈತಾನನನ್ನು ನಿಮ್ಮ ಕಾಲುಗಳ ಕೆಳಗೆ ಹಾಕಿ ತುಳಿಸಿಬಿಡುವನು. ನಮ್ಮ ಕರ್ತನಾದ ಯೇಸು ಕ್ರಿಸ್ತನ ಕೃಪೆಯು ನಿಮ್ಮೊಂದಿಗಿರಲಿ.",
			"21": "<sup>21</sup> ನನ್ನ ಜೊತೆಕೆಲಸದವನಾದ ತಿಮೊಥೆಯನೂ ನನ್ನ ಸ್ವಜನರಾದ ಲೂಕ್ಯನೂ ಯಾಸೋನನೂ ಸೋಸಿಪತ್ರನೂ ನಿಮಗೆ ವಂದನೆಗಳನ್ನು ಹೇಳುತ್ತಾರೆ.",
			"22": "<sup>22</sup> ಈ ಪತ್ರಿಕೆಯನ್ನು ಬರೆದ ತೆರ್ತ್ಯನೆಂಬ ನಾನು ನಿಮ್ಮನ್ನು ಕರ್ತನಲ್ಲಿ ವಂದಿಸುತ್ತೇನೆ.",
			"23": "<sup>23</sup> ನನಗೂ ಸಮಸ್ತ ಸಭೆಗೂ ಅತಿಥಿಸತ್ಕಾರವನ್ನು ಮಾಡುತ್ತಿರುವ ಗಾಯನು ನಿಮ್ಮನ್ನು ವಂದಿಸುತ್ತಾನೆ. ಈ ಪಟ್ಟಣದ ಖಜಾನೆಯ ಮೇಲ್ವಿಚಾರಕನಾಗಿರುವ ಎರಸ್ತನೂ ಸಹೋದರನಾದ ಕ್ವರ್ತನೂ ನಿಮಗೆ ವಂದನೆಗಳನ್ನು ಹೇಳುತ್ತಾರೆ.",
			"25": "<sup>25</sup> ಅನಾದಿಯಾಗಿ ಗುಪ್ತವಾಗಿದ್ದ ಮರ್ಮವು ಈಗ ಪ್ರಕಾಶಕ್ಕೆ ಬಂದು ನಿತ್ಯನಾದ ದೇವರ ಅಪ್ಪಣೆಯ ಪ್ರಕಾರ ಪ್ರವಾದಿಗಳ ಗ್ರಂಥಗಳ ಮೂಲಕ ಅನ್ಯಜನರೆಲ್ಲರಿಗೆ ನಂಬಿಕೆಯೆಂಬ ವಿಧೇಯತ್ವವನ್ನು ಉಂಟುಮಾಡುವದಕ್ಕೋಸ್ಕರ ತಿಳಿಸಲ್ಪಟ್ಟಿದೆ. ಪ್ರಕಾಶಕ್ಕೆ ಬಂದಿರುವ ಈ ಮರ್ಮಕ್ಕೆ ಅನುಸಾರ ಅಂದರೆ ಯೇಸು ಕ್ರಿಸ್ತನ ವಿಷಯವಾದಂಥ ನಾನು ಸಾರುವಂಥ ಸುವಾರ್ತೆಗನುಸಾರ ನಿಮ್ಮನ್ನು ಸ್ಥಿರಪಡಿಸುವದಕ್ಕೆ ಶಕ್ತನಾಗಿರುವ",
			"27": "<sup>27</sup> ಜ್ಞಾನನಿಧಿಯಾದ ಒಬ್ಬನೇ ದೇವರಿಗೆ ಯೇಸು ಕ್ರಿಸ್ತನ ಮೂಲಕ ಯುಗಯುಗಾಂತರಗಳಲ್ಲಿಯೂ ಸ್ತೋತ್ರವಾಗಲಿ. ಆಮೆನ್."
		}
	}
};
