var book = {
	"name": "2 ಕೊರಿಂಥದವರಿಗೆ",
	"numChapters": 13,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ದೇವರ ಚಿತ್ತಾನುಸಾರ ಕ್ರಿಸ್ತ ಯೇಸುವಿನ ಅಪೊಸ್ತಲನಾದ ಪೌಲನೂ ಸಹೋದರನಾದ ತಿಮೊಥೆಯನೂ ಕೊರಿಂಥದಲ್ಲಿರುವ ದೇವರ ಸಭೆಯವರಿಗೂ ಅಖಾಯ ಸೀಮೆಯಲ್ಲೆಲ್ಲಾ ಇರುವ ದೇವಜನರೆಲ್ಲರಿಗೂ ಬರೆಯುವದೇನಂದರೆ -",
			"2": "<sup>2</sup> ನಮ್ಮ ತಂದೆಯಾದ ದೇವರಿಂದಲೂ ಕರ್ತನಾದ ಯೇಸು ಕ್ರಿಸ್ತನಿಂದಲೂ ನಿಮಗೆ ಕೃಪೆಯೂ ಶಾಂತಿಯೂ ಆಗಲಿ.",
			"3": "<sup>3</sup> ನಮ್ಮ ಕರ್ತನಾದ ಯೇಸು ಕ್ರಿಸ್ತನ ದೇವರೂ ತಂದೆಯೂ ಆಗಿರುವಾತನಿಗೆ ಸ್ತೋತ್ರವಾಗಲಿ; ಆತನು ಕನಿಕರವುಳ್ಳ ತಂದೆಯೂ ಸಕಲವಿಧವಾಗಿ ಸಂತೈಸುವ ದೇವರೂ ಆಗಿದ್ದು",
			"4": "<sup>4</sup> ನಮಗೆ ಸಂಭವಿಸುವ ಎಲ್ಲಾ ಸಂಕಟಗಳಲ್ಲಿ ನಮ್ಮನ್ನು ಸಂತೈಸುತ್ತಾನೆ; ಹೀಗೆ ದೇವರಿಂದ ನಮಗಾಗುವ ಆದರಣೆಯ ಮೂಲಕ ನಾವು ನಾನಾ ವಿಧವಾದ ಸಂಕಟಗಳಲ್ಲಿ ಬಿದ್ದಿರುವವರನ್ನು ಸಂತೈಸುವದಕ್ಕೆ ಶಕ್ತರಾಗುತ್ತೇವೆ.",
			"5": "<sup>5</sup> ಯಾಕಂದರೆ ಕ್ರಿಸ್ತನ ನಿಮಿತ್ತ ನಮಗೆ ಬಾಧೆಗಳು ಹೇಗೆ ಹೇರಳವಾಗಿ ಉಂಟಾಗುತ್ತವೆಯೋ ಹಾಗೆಯೇ ಆದರಣೆಯು ಕೂಡ ಕ್ರಿಸ್ತನ ಮೂಲಕ ಹೇರಳವಾಗಿ ಉಂಟಾಗುತ್ತದೆ.",
			"6": "<sup>6</sup> ನಮಗೆ ಸಂಕಟಬರುತ್ತದೋ ಅದರಿಂದ ನಿಮಗೆ ಧೈರ್ಯವೂ ರಕ್ಷಣೆಯೂ ಉಂಟಾಗುತ್ತದೆ; ನಮಗೆ ಸಂಕಟ ಪರಿಹಾರವಾಗುತ್ತದೋ ಅದರಿಂದ ನಿಮಗೆ ಆದರಣೆ ಆಗುತ್ತದೆ. ನಾವು ಅನುಭವಿಸುವಂಥ ಬಾಧೆಗಳನ್ನು ನೀವೂ ತಾಳ್ಮೆಯಿಂದ ಸಹಿಸಿಕೊಳ್ಳುವಂತೆ ಅದು ನಿಮ್ಮನ್ನು ಧೈರ್ಯಗೊಳಿಸುತ್ತದೆ.",
			"7": "<sup>7</sup> ನೀವು ಬಾಧೆಗಳಲ್ಲಿ ನಮ್ಮೊಂದಿಗೆ ಪಾಲುಗಾರರಾಗಿರುವ ಪ್ರಕಾರ ಆದರಣೆಯಲ್ಲಿಯೂ ಪಾಲುಗಾರರಾಗಿದ್ದೀರೆಂದು ನಮಗೆ ತಿಳಿದಿರುವದರಿಂದ ನಿಮ್ಮ ವಿಷಯದಲ್ಲಿ ನಮಗಿರುವ ನಿರೀಕ್ಷೆಯು ದೃಢವಾಗಿದೆ.",
			"8": "<sup>8</sup> ಸಹೋದರರೇ, ಆಸ್ಯ ಸೀಮೆಯಲ್ಲಿ ನಮಗೆ ಸಂಭವಿಸಿದ ಸಂಕಟವನ್ನು ಕುರಿತು ವಿಚಾರಿಸುತ್ತೀರೋ ಅದರಲ್ಲಿ ನಾವು ಬಲವನ್ನು ಮೀರಿದಂಥ ಅತ್ಯಧಿಕವಾದ ಭಾರದಿಂದ ಕುಗ್ಗಿಹೋಗಿ ಜೀವವುಳಿಯುವ ಮಾರ್ಗವನ್ನು ಕಾಣದವರಾದೆವೆಂಬದನ್ನು ನೀವು ತಿಳಿಯಬೇಕೆಂದು ಅಪೇಕ್ಷಿಸುತ್ತೇನೆ.",
			"9": "<sup>9</sup> ಮರಣವಾಗುತ್ತದೆಂಬ ನಿಶ್ಚಯವು ನಮ್ಮೊಳಗೆ ಉಂಟಾಯಿತು; ನಾವು ನಮ್ಮ ಮೇಲೆ ಭರವಸವಿಟ್ಟುಕೊಳ್ಳದೆ ಸತ್ತವರನ್ನು ಎಬ್ಬಿಸುವ ದೇವರ ಮೇಲೆಯೇ ಭರವಸವಿಡುವವರಾಗಬೇಕೆಂದು ಹೀಗಾಯಿತು.",
			"10": "<sup>10</sup> ಆತನು ನಮ್ಮನ್ನು ಎಂಥ ಭಯಂಕರಮರಣದಿಂದ ತಪ್ಪಿಸಿದನು; ಮುಂದೆಯೂ ತಪ್ಪಿಸುವನು. ನೀವು ನಮಗೋಸ್ಕರ ಪ್ರಾರ್ಥನೆ ಮಾಡುವವರಾಗಿ ಸಹಕಾರಿಗಳಾಗಿರುವದರಿಂದ ಆತನು ಇನ್ನು ಮೇಲೆಯೂ ತಪ್ಪಿಸುವನೆಂದು ಆತನಲ್ಲಿ ನಿರೀಕ್ಷೆಯಿಟ್ಟವರಾಗಿದ್ದೇವೆ. ಹೀಗೆ ಅನೇಕರ ವಿಜ್ಞಾಪನೆಗಳಿಂದ ನಮಗೆ ದೊರಕುವ ಉಪಕಾರಕ್ಕಾಗಿ ನಮ್ಮ ನಿಮಿತ್ತ ಅನೇಕರಿಂದ ದೇವರಿಗೆ ಕೃತಜ್ಞತಾಸ್ತುತಿ ಉಂಟಾಗುವದು.",
			"12": "<sup>12</sup> ನಾವು ಕೇವಲ ಮಾನುಷಜ್ಞಾನವನ್ನು ಬಳಸದೆ ದೇವರ ಕೃಪೆಯನ್ನು ಆಶ್ರಯಿಸಿ ಆತನಿಂದಾಗುವ ಪವಿತ್ರತ್ವವೂ ನಿಷ್ಕಪಟತ್ವವೂ ಉಳ್ಳವರಾಗಿ ಈ ಲೋಕದಲ್ಲಿ ಮುಖ್ಯವಾಗಿ ನಿಮ್ಮ ವಿಷಯದಲ್ಲಿ ನಡಕೊಂಡೆವೆಂದು ನಮ್ಮ ಮನಸ್ಸು ನಮಗೆ ಸಾಕ್ಷಿ ಹೇಳುತ್ತದೆ;",
			"13": "<sup>13</sup> ಇದೇ ನಮಗಿರುವ ಉಲ್ಲಾಸ. ನೀವು ನಮ್ಮ ಪತ್ರಿಕೆಗಳಲ್ಲಿ ಓದಿ ಒಪ್ಪಿಕೊಂಡ ಸಂಗತಿಗಳನ್ನೇ ಅಲ್ಲದೆ ಬೇರೆ ಯಾವದನ್ನೂ ನಾವು ನಿಮಗೆ ಬರೆಯಲಿಲ್ಲ.",
			"14": "<sup>14</sup> ನಾವು ನಿಮಗೆ ಉಲ್ಲಾಸಕಾರಣವಾಗಿದ್ದೇವೆಂಬದನ್ನು ನೀವು ಸ್ವಲ್ಪ ಮಟ್ಟಿಗೆ ಒಪ್ಪಿಕೊಂಡಂತೆಯೇ ಕಡೇ ತನಕ ಒಪ್ಪಿಕೊಳ್ಳುವಿರೆಂದು ನಿರೀಕ್ಷಿಸುತ್ತೇನೆ. ಹಾಗೆಯೇ ನೀವೂ ನಮ್ಮ ಕರ್ತನಾದ ಯೇಸುವಿನ ಪ್ರತ್ಯಕ್ಷತೆಯಲ್ಲಿ ನಮಗೆ ಉಲ್ಲಾಸಕಾರಣವಾಗುವಿರಿ ಎಂದು ನಿರೀಕ್ಷಿಸುತ್ತೇನೆ.",
			"15": "<sup>15</sup> ನಾನು ಇಂಥ ಭರವಸವುಳ್ಳವನಾಗಿದ್ದು ನಿಮಗೆ ಎರಡು ಸಾರಿ ನಮ್ಮ ಮೂಲಕ ಕೃಪಾವರ ಉಂಟಾಗಬೇಕೆಂದು ಯೋಚಿಸಿದ್ದೆನು.",
			"16": "<sup>16</sup> ಮೊದಲು ನಿಮ್ಮ ಬಳಿಗೆ ಬಂದು ನಿಮ್ಮ ಮಾರ್ಗವಾಗಿ ಮಕೆದೋನ್ಯಕ್ಕೆ ಹೋಗಿ ಆಮೇಲೆ ಮಕೆದೋನ್ಯವನ್ನು ಬಿಟ್ಟು ತಿರಿಗಿ ನಿಮ್ಮ ಬಳಿಗೆ ಬಂದು ನಿಮ್ಮಿಂದ ಯೂದಾಯಕ್ಕೆ ಸಾಗಕಳುಹಿಸಲ್ಪಡಬೇಕೆಂದು ಯೋಚಿಸಿದ್ದೆನು.",
			"17": "<sup>17</sup> ಇದನ್ನು ಯೋಚಿಸಿದಾಗ ಚಪಲಚಿತ್ತನಾಗಿದ್ದೆನೋ? ನಾನು ಯೋಚಿಸುವದನ್ನು ಕೇವಲ ಮಾನುಷಬುದ್ಧಿಯಿಂದ ಯೋಚಿಸಿ ಹೌದು ಹೌದು ಅಂದ ಮೇಲೆ ಅಲ್ಲ ಅಲ್ಲ ಅನ್ನುವವನಾಗಿದ್ದೇನೋ?",
			"18": "<sup>18</sup> ನಂಬಿಗಸ್ತನಾಗಿರುವ ದೇವರಾಣೆ, ನಾವು ನಿಮಗೆ ಹೇಳುವ ಮಾತು ಒಂದು ಸಾರಿ ಹೌದೆಂದು ಒಂದು ಸಾರಿ ಅಲ್ಲವೆಂದು ಇರುವದಿಲ್ಲ.",
			"19": "<sup>19</sup> ನಾವು ಅಂದರೆ ನಾನೂ ಸಿಲ್ವಾನನೂ ತಿಮೊಥೆಯನೂ ನಿಮ್ಮಲ್ಲಿ ಪ್ರಸಿದ್ಧಿಪಡಿಸಿದ ದೇವರ ಮಗನಾದ ಯೇಸು ಕ್ರಿಸ್ತನು ಹೌದೆಂತಲೂ ಅಲ್ಲವೆಂತಲೂ ಎರಡು ಪ್ರಕಾರವಾಗಿರಲಿಲ್ಲ; ಆತನಲ್ಲಿ ಹೌದೆಂಬದೇ ನೆಲೆಗೊಂಡಿದೆ.",
			"20": "<sup>20</sup> ನಿಶ್ಚಯವಾಗಿ ದೇವರ ವಾಗ್ದಾನಗಳು ಎಷ್ಟಿದ್ದರೂ ಕ್ರಿಸ್ತನಲ್ಲೇ ದೃಢವಾಗುತ್ತವೆ. ಆದಕಾರಣ ದೇವರ ಪ್ರಭಾವವು ಪ್ರಕಾಶವಾಗುವದಕ್ಕೋಸ್ಕರ ಆತನ ಮೂಲಕವಾಗಿ ನಾವು ಆಮೆನ್ ಎಂದು ಹೇಳುತ್ತೇವೆ.",
			"21": "<sup>21</sup> ನಮ್ಮನ್ನು ಅಭಿಷೇಕಿಸಿ ಕ್ರಿಸ್ತನ ಅನ್ಯೋನ್ಯತೆಯಲ್ಲಿ ಸೇರಿಸಿ ನಮ್ಮನ್ನು ನಿಮ್ಮೊಂದಿಗೆ ಸ್ಥಿರಪಡಿಸುವವನು ದೇವರೇ;",
			"22": "<sup>22</sup> ಆತನು ನಮ್ಮ ಮೇಲೆ ಮುದ್ರೆ ಹಾಕಿ ನಮ್ಮ ಹೃದಯಗಳಲ್ಲಿ ಪವಿತ್ರಾತ್ಮನನ್ನು ಸಂಚಕಾರವಾಗಿ ಅನುಗ್ರಹಿಸಿದ್ದಾನೆ.",
			"23": "<sup>23</sup> ನಿಮ್ಮ ಮೇಲೆ ಕನಿಕರವಿದ್ದದರಿಂದಲೇ ನಾನು ಕೊರಿಂಥಕ್ಕೆ ಬರಬೇಕೆಂಬ ಅಭಿಪ್ರಾಯವನ್ನು ಬಿಟ್ಟುಬಿಟ್ಟೆನೆಂದು ನನ್ನ ಜೀವದಾಣೆ ಹೇಳುತ್ತೇನೆ; ಅದಕ್ಕೆ ದೇವರೇ ಸಾಕ್ಷಿಯಾಗಿರಲಿ.",
			"24": "<sup>24</sup> ನಾವು ನಂಬಿಕೆಯ ವಿಷಯದಲ್ಲಿಯೂ ನಿಮ್ಮ ಮೇಲೆ ದೊರೆತನಮಾಡುವವರೆಂದು ನನ್ನ ತಾತ್ಪರ್ಯವಲ್ಲ; ನಿಮ್ಮ ಸಂತೋಷಕ್ಕೆ ನಾವು ಸಹಾಯಕರಾಗಿದ್ದೇವೆ; ನಂಬಿಕೆಯ ವಿಷಯದಲ್ಲಿ ದೃಢ ನಿಂತಿದ್ದೀರಿ."
		},
		"2": {
			"1": "<sup>1</sup> ಆದರೆ ನಾನು ತಿರಿಗಿ ನಿಮ್ಮ ಬಳಿಗೆ ದುಃಖದಿಂದ ಬರುವದಿಲ್ಲವೆಂದು ನನ್ನಲ್ಲಿ ತೀರ್ಮಾನಿಸಿಕೊಂಡೆನು.",
			"2": "<sup>2</sup> ನಾನು ನಿಮ್ಮನ್ನು ದುಃಖಪಡಿಸಿದರೆ ನನ್ನನ್ನು ಆನಂದಪಡಿಸುವದಕ್ಕೆ ಯಾರಿದ್ದಾರೆ? ನನ್ನಿಂದ ದುಃಖ ಹೊಂದಿದವರೇ ಹೊರತು ಬೇರೆ ಯಾರೂ ಇಲ್ಲವಲ್ಲಾ.",
			"3": "<sup>3</sup> ಆದಕಾರಣ ನಾನು ಬಂದಾಗ ನನ್ನನ್ನು ಸಂತೋಷಪಡಿಸತಕ್ಕವರಿಂದ ದುಃಖಹೊಂದಬಾರದೆಂದು ಯೋಚಿಸಿ ಆ ಸಂಗತಿಯನ್ನು ಪತ್ರಿಕೆಯ ಮೂಲಕವೇ ತಿಳಿಸಿದೆನು. ನನ್ನ ಸಂತೋಷವು ನಿಮ್ಮೆಲ್ಲರ ಸಂತೋಷವೇ ಎಂದು ತಿಳಿದು ನಿಮ್ಮೆಲ್ಲರ ವಿಷಯದಲ್ಲೂ ನನಗೆ ಭರವಸವಿತ್ತು.",
			"4": "<sup>4</sup> ನಿಮಗೆ ದುಃಖವಾಗಬೇಕೆಂತಲ್ಲ, ನಿಮ್ಮ ಮೇಲೆ ನನಗಿರುವ ಅಧಿಕವಾದ ಪ್ರೀತಿಯನ್ನು ನೀವು ತಿಳುಕೊಳ್ಳಬೇಕೆಂತಲೇ ನಾನು ಬಹಳ ಕಣ್ಣೀರು ಬಿಡುತ್ತಾ ಹೃದಯದ ಬಹುಸಂಕಟದಿಂದಲೂ ವ್ಯಾಕುಲದಿಂದಲೂ ನಿಮಗೆ ಬರೆದೆನು.",
			"5": "<sup>5</sup> ದುಃಖವನ್ನು ಉಂಟುಮಾಡಿರುವವನೊಬ್ಬನು ನಿಮ್ಮಲ್ಲಿ ಇದ್ದರೂ ಅವನು ನನ್ನನ್ನು ಮಾತ್ರ ದುಃಖಪಡಿಸದೆ ನಿಮ್ಮಲ್ಲಿ ಬಹು ಜನರನ್ನೂ ದುಃಖಪಡಿಸಿದ್ದಾನೆ; ಆ ದುಃಖವು ಎಲ್ಲರಲ್ಲಿಯೂ ಉಂಟಾಯಿತೆಂದು ನಾನು ಹೇಳುವದಿಲ್ಲ.",
			"6": "<sup>6</sup> ಇಂಥವನಿಗೆ ನಿಮ್ಮಲ್ಲಿ ಹೆಚ್ಚಾದ ಜನರಿಂದುಂಟಾದ ಆ ಶಿಕ್ಷೆಯೇ ಸಾಕು.",
			"7": "<sup>7</sup> ಇನ್ನೂ ಅವನನ್ನು ಶಿಕ್ಷಿಸದೆ ಮನ್ನಿಸಿರಿ, ಸಂತೈಸಿರಿ; ಇಲ್ಲವಾದರೆ ಅವನು ಅಧಿಕವಾದ ದುಃಖದಲ್ಲಿ ಮುಳುಗಿಹೋದಾನು.",
			"8": "<sup>8</sup> ಆದದರಿಂದ ಅವನಿಗೆ ನಿಮ್ಮ ಪ್ರೀತಿಯನ್ನು ನಿಶ್ಚಯಪಡಿಸಬೇಕೆಂದು ನಿಮ್ಮನ್ನು ಬೇಡಿಕೊಳ್ಳುತ್ತೇನೆ.",
			"9": "<sup>9</sup> ಸರ್ವವಿಷಯದಲ್ಲಿಯೂ ನೀವು ವಿಧೇಯರಾಗಿದ್ದೀರೋ ಏನೋ ನಿಮ್ಮ ಗುಣವನ್ನು ಪರೀಕ್ಷಿಸಿ ತಿಳುಕೊಳ್ಳಬೇಕೆಂಬುವ ಉದ್ದೇಶದಿಂದಲೇ ಮೊದಲು ಬರೆದೆನು.",
			"10": "<sup>10</sup> ನೀವು ಯಾರಿಗೆ ಯಾವದನ್ನು ಮನ್ನಿಸುತ್ತೀರೋ ನಾನು ಸಹ ಮನ್ನಿಸುತ್ತೇನೆ. ನಾನು ಯಾವದನ್ನಾದರೂ ಮನ್ನಿಸಿದ್ದರೆ ಕ್ರಿಸ್ತನ ಸನ್ನಿಧಾನದಲ್ಲಿ ನಿಮ್ಮ ನಿಮಿತ್ತವೇ ಮನ್ನಿಸಿದೆನು.",
			"11": "<sup>11</sup> ಸೈತಾನನು ನಮ್ಮನ್ನು ವಂಚಿಸಿ ನಷ್ಟಪಡಿಸಬಾರದು; ಅವನ ಯೋಚನೆಗಳನ್ನು ನಾವು ಅರಿಯದವರಲ್ಲವಲ್ಲಾ.",
			"12": "<sup>12</sup> ಕ್ರಿಸ್ತನ ಸುವಾರ್ತೆಯನ್ನು ಸಾರುವದಕ್ಕಾಗಿ ನಾನು ತ್ರೋವಕ್ಕೆ ಬರಲು ಕರ್ತನ ಕೃಪೆಯಿಂದ ನನಗೆ ಒಳ್ಳೇ ಸಂದರ್ಭ ಉಂಟಾಯಿತು.",
			"13": "<sup>13</sup> ಆದರೂ ನನ್ನ ಸಹೋದರನಾದ ತೀತನು ನನಗೆ ಸಿಕ್ಕಲಿಲ್ಲವಾದ ಕಾರಣ ನನ್ನ ಆತ್ಮಕ್ಕೆ ಉಪಶಮನವಿಲ್ಲದೆ ಅಲ್ಲಿದ್ದವರಿಂದ ಕಳುಹಿಸಿಕೊಂಡವನಾಗಿ ಹೊರಟು ಮಕೆದೋನ್ಯಕ್ಕೆ ಬಂದೆನು.",
			"14": "<sup>14</sup> ಕ್ರಿಸ್ತನ ಅನ್ಯೋನ್ಯತೆಯಲ್ಲಿ ನಮ್ಮನ್ನು ಯಾವಾಗಲೂ ಜಯೋತ್ಸವದೊಡನೆ ಮೆರಸುತ್ತಾ ಆತನ ವಿಷಯವಾದ ಜ್ಞಾನವೆಂಬ ಸುವಾಸನೆಯನ್ನು ನಮ್ಮ ಮೂಲಕ ಎಲ್ಲಾ ಸ್ಥಳಗಳಲ್ಲಿ ವ್ಯಾಪನಗೊಳಿಸುತ್ತಾ ಬರುವ ದೇವರಿಗೆ ಸ್ತೋತ್ರವು.",
			"15": "<sup>15</sup> ರಕ್ಷಣಾಮಾರ್ಗದಲ್ಲಿರುವವರಲ್ಲಿಯೂ ನಾಶನಮಾರ್ಗದಲ್ಲಿರುವವರಲ್ಲಿಯೂ ನಾವು ದೇವರ ಮುಂದೆ ಕ್ರಿಸ್ತನ ಪರಿಮಳವಾಗಿದ್ದೇವೆ.",
			"16": "<sup>16</sup> ನಾವು ನಾಶನಮಾರ್ಗದಲ್ಲಿರುವವರಿಗೆ ಮರಣದಿಂದ ಹುಟ್ಟಿ ಮರಣವನ್ನು ಹುಟ್ಟಿಸುವ ವಾಸನೆಯಾಗಿಯೂ ರಕ್ಷಣಾಮಾರ್ಗದಲ್ಲಿರುವವರಿಗೆ ಜೀವದಿಂದ ಹುಟ್ಟಿ ಜೀವವನ್ನು ಹುಟ್ಟಿಸುವ ವಾಸನೆಯಾಗಿಯೂ ಇದ್ದೇವೆ. ಇಂಥ ಕಾರ್ಯಗಳಿಗೆ ಯಾರು ಯೋಗ್ಯರು?",
			"17": "<sup>17</sup> ಆದರೆ ನಾವು ದೇವರ ವಾಕ್ಯವನ್ನು ಕಲಬೆರಿಕೆಮಾಡುವವರಾದ ಹೆಚ್ಚು ಪಾಲಿನ ಜನರ ಹಾಗಿರದೆ ನಿಷ್ಕಪಟಿಗಳಾಗಿ ದೇವರಿಂದ ಉಪದೇಶ ಹೊಂದಿದವರಿಗೆ ತಕ್ಕ ಹಾಗೆ ಕ್ರಿಸ್ತನ ಅನ್ಯೋನ್ಯತೆಯಲ್ಲಿದ್ದುಕೊಂಡು ದೇವರ ಸಮಕ್ಷಮದಲ್ಲಿಯೇ ಮಾತಾಡುತ್ತೇವೆ."
		},
		"3": {
			"1": "<sup>1</sup> ನಾವೇ ನಮ್ಮನ್ನು ತಿರಿಗಿ ಹೊಗಳಿಕೊಳ್ಳುವದಕ್ಕೆ ಪ್ರಾರಂಭಿಸುತ್ತೇವೋ? ಇಲ್ಲವೆ ಕೆಲವರಿಗೆ ಬೇಕಾಗಿರುವ ಪ್ರಕಾರ ನಿಮಗೆ ತೋರಿಸತಕ್ಕ ಯೋಗ್ಯತಾ ಪತ್ರಗಳು ನಮಗೆ ಅಗತ್ಯವೋ? ಅಥವಾ ಅಂಥ ಪತ್ರಗಳನ್ನು ನಿಮ್ಮಿಂದ ಪಡಕೊಳ್ಳಬೇಕೋ?",
			"2": "<sup>2</sup> ನೀವೇ ನಮ್ಮ ಯೋಗ್ಯತಾಪತ್ರ; ಅದು ನಮ್ಮ ಹೃದಯಗಳಲ್ಲೇ ಬರೆದದೆ; ಆ ಪತ್ರವನ್ನು ಎಲ್ಲರೂ ಬಲ್ಲರು, ಎಲ್ಲರೂ ಓದುತ್ತಾರೆ.",
			"3": "<sup>3</sup> ಕ್ರಿಸ್ತನು ನಮ್ಮ ಕೈಯಿಂದ ಬರಸಿಕೊಟ್ಟ ಪತ್ರವಾಗಿದ್ದೀರೆಂಬದು ಪ್ರತ್ಯಕ್ಷವಾಗಿಯೇ ಇದೆ; ಅದು ಮಸಿಯಿಂದ ಬರೆದದ್ದಲ್ಲ, ಜೀವವುಳ್ಳ ದೇವರ ಆತ್ಮನಿಂದಲೇ ಬರೆದದ್ದು; ಕಲ್ಲಿನ ಮೇಲೆಯಲ್ಲ, ಮನುಷ್ಯ ಹೃದಯಗಳೆಂಬ ಹಲಿಗೆಗಳ ಮೇಲೆ ಬರೆದದ್ದು.",
			"4": "<sup>4</sup> ಇಂಥ ಭರವಸವು ಕ್ರಿಸ್ತನ ಮೂಲಕವೇ ದೇವರ ಮುಂದೆ ನಮಗುಂಟು.",
			"5": "<sup>5</sup> ನಮ್ಮಿಂದಲೇ ಏನಾದರೂ ಉಂಟಾಯಿತೆಂದು ತೀರ್ಮಾನಿಸಿಕೊಳ್ಳುವದಕ್ಕೆ ನಮ್ಮಷ್ಟಕ್ಕೆ ನಾವೇ ಸಮರ್ಥರೆಂತಲ್ಲ; ನಮಗಿರುವ ಸಾಮರ್ಥ್ಯವು ದೇವರಿಂದಲೇ ಬಂದದ್ದು;",
			"6": "<sup>6</sup> ಆತನು ಹೊಸ ಒಡಂಬಡಿಕೆಗೆ ಸೇವಕರಾಗಿರುವ ಸಾಮರ್ಥ್ಯವನ್ನು ನಮಗೆ ಅನುಗ್ರಹಿಸಿದ್ದಾನೆ. ಈ ಒಡಂಬಡಿಕೆಯು ಲಿಖಿತ ರೂಪವಾದದ್ದಾಗಿರದೆ ದೇವರಾತ್ಮ ಸಂಬಂಧವಾದದ್ದು ಆಗಿದೆ. ಲಿಖಿತರೂಪವಾದ ಒಡಂಬಡಿಕೆಯು ಮರಣವನ್ನುಂಟುಮಾಡುತ್ತದೆ; ದೇವರಾತ್ಮ ಸಂಬಂಧವಾದದ್ದು ಜೀವವನ್ನುಂಟುಮಾಡುತ್ತದೆ.",
			"7": "<sup>7</sup> ಮರಣವಿಧಿಸುವ ನಿಯಮದ ಸೇವೆಯು ಕಲ್ಲಿನ ಮೇಲೆ ಲಿಖಿತವಾದ ಅಕ್ಷರಗಳಲ್ಲಿ ಆಧಾರಗೊಂಡಿದ್ದರೂ ಪ್ರಭಾವದಿಂದ ಕೂಡಿದ್ದಾಗಿತ್ತು. ಆ ಪ್ರಭಾವದಿಂದ ಮೋಶೆಯ ಮುಖಕ್ಕೆ ಬಂದ ಪ್ರಕಾಶವು ಕುಂದಿಹೋಗುವಂಥದಾಗಿದ್ದರೂ ಅದರ ನಿಮಿತ್ತ ಇಸ್ರಾಯೇಲ್ಯರು ಅವನ ಮುಖವನ್ನು ನೋಡಲಾರದೆ ಇದ್ದರು.",
			"8": "<sup>8</sup> ಹೀಗಿದ್ದ ಮೇಲೆ ದೇವರಾತ್ಮಸಂಬಂಧವಾದ ಸೇವೆಯು ಎಷ್ಟೋ ಹೆಚ್ಚಾಗಿ ಪ್ರಭಾವವುಳ್ಳದ್ದಾಗಿರಬೇಕು.",
			"9": "<sup>9</sup> ಅಪರಾಧನಿರ್ಣಯಕ್ಕೆ ಸಾಧನವಾಗಿರುವ ಸೇವೆಯು ಪ್ರಭಾವದಿಂದಿರಲಾಗಿ ನೀತಿಗೆ ಸಾಧನವಾಗಿರುವ ಸೇವೆಯು ಎಷ್ಟೋ ಅಧಿಕವಾದ ಪ್ರಭಾವವುಳ್ಳದ್ದಾಗಿರಬೇಕು.",
			"10": "<sup>10</sup> ಅಧಿಕವಾದ ಪ್ರಭಾವವುಳ್ಳದ್ದು ಬಂದದರಿಂದ ಮೊದಲಿದ್ದ ಪ್ರಭಾವವು ಅದರ ಬಳಿಯಲ್ಲಿ ಪ್ರಭಾವವಿಲ್ಲದ್ದಾಯಿತು.",
			"11": "<sup>11</sup> ಇಲ್ಲದೆಹೋಗುವಂಥದು ಪ್ರಭಾವವುಳ್ಳದ್ದಾಗಿದ್ದರೆ ಶಾಶ್ವತವಾಗಿ ಇರುವಂಥದು ಬಹು ಹೆಚ್ಚಾಗಿ ಪ್ರಭಾವವುಳ್ಳದ್ದಾಗಿರಬೇಕಲ್ಲವೇ.",
			"12": "<sup>12</sup> ಆದದರಿಂದ ನಾವು ಇಂಥ ಭರವಸವುಳ್ಳವರಾಗಿದ್ದು ಧಾರಾಳವಾಗಿ ಮಾತಾಡುತ್ತೇವೆ.",
			"13": "<sup>13</sup> ನಾವು ಮೋಶೆಯಂತೆ ಮಾಡುವದಿಲ್ಲ, ಗತಿಸಿಹೋಗುವಂಥದರ ಅಂತ್ಯವನ್ನು ಇಸ್ರಾಯೇಲ್ಯರು ಕಣ್ಣಿಟ್ಟು ನೋಡಬಾರದೆಂದು ಅವನು ತನ್ನ ಮುಖದ ಮೇಲೆ ಮುಸುಕುಹಾಕಿಕೊಳ್ಳುತ್ತಿದ್ದನು.",
			"14": "<sup>14</sup> ಆದರೆ ಅವರ ಬುದ್ಧಿ ಮಂದವಾಯಿತು; ಈ ದಿನದವರೆಗೂ ಹಳೇ ಒಡಂಬಡಿಕೆಯು ಪಾರಾಯಣವಾಗುವಾಗ ಅದು ಕ್ರಿಸ್ತನಲ್ಲಿ ಗತಿಸಿಹೋಗುತ್ತದೆಂಬದು ಅವರಿಗೆ ಪ್ರಕಟವಾಗದೆ ಇರುವದರಿಂದ ಅದೇ ಮುಸುಕು ಎತ್ತಲ್ಪಡದೆ ಇರುತ್ತದೆ.",
			"15": "<sup>15</sup> ಈ ದಿನದವರೆಗೂ ಮೋಶೆಯ ಗ್ರಂಥಪಾರಾಯಣವು ಆಗುವಾಗೆಲ್ಲಾ ಮುಸುಕು ಅವರ ಹೃದಯದ ಮೇಲೆ ಇರುತ್ತದೆ.",
			"16": "<sup>16</sup> ಅವರ ಹೃದಯವು ಕರ್ತನ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡಾಗಲೇ ಆ ಮುಸುಕು ತೆಗೆಯಲ್ಪಡುವದು.",
			"17": "<sup>17</sup> ಆ ಕರ್ತನು ದೇವರಾತ್ಮನೇ; ಕರ್ತನ ಆತ್ಮನು ಯಾರಲ್ಲಿದ್ದಾನೋ ಅವರಿಗೆ ಬಿಡುಗಡೆ ಉಂಟು.",
			"18": "<sup>18</sup> ನಾವೆಲ್ಲರೂ ಮುಸುಕುತೆಗೆದಿರುವ ಮುಖದಿಂದ ಕರ್ತನ ಪ್ರಭಾವವನ್ನು ದರ್ಪಣದಲ್ಲಿ ಕಾಣಿಸುತ್ತದೋ ಎಂಬಂತೆ ದೃಷ್ಟಿಸುವವರಾಗಿದ್ದು ಪ್ರಭಾವದಿಂದ ಅಧಿಕಪ್ರಭಾವಕ್ಕೆ ಹೋಗುತ್ತಾ ಆ ಪ್ರಭಾವದ ಸಾರೂಪ್ಯವುಳ್ಳವರೇ ಆಗುತ್ತೇವೆ; ಇದು ದೇವರಾತ್ಮನಾಗಿರುವ ಕರ್ತನ ಕೆಲಸಕ್ಕನುಸಾರವಾದದ್ದೇ."
		},
		"4": {
			"1": "<sup>1</sup> ಆದದರಿಂದ ನಾವು ದೇವರ ಕರುಣೆಯಿಂದ ಈ ಸೇವೆಯನ್ನು ಹೊಂದಿದವರಾಗಿರಲಾಗಿ ಧೈರ್ಯಗೆಟ್ಟು ಹಿಂದೆಗೆಯುವದಿಲ್ಲ.",
			"2": "<sup>2</sup> ನಾಚಿಕೆಯನ್ನು ಹುಟ್ಟಿಸುವ ಗುಪ್ತಕಾರ್ಯಗಳನ್ನು ನಾವು ಬಿಟ್ಟುಬಿಟ್ಟು ತಂತ್ರದಲ್ಲಿ ನಡೆಯದೆ ದೇವರ ವಾಕ್ಯವನ್ನು ಕೆಡಿಸದೆ ಸತ್ಯವನ್ನು ಪರಿಷ್ಕಾರವಾಗಿ ಬೋಧಿಸುತ್ತಾ ನಾವು ಸಜ್ಜನರೆಂದು ಪ್ರತಿಮನುಷ್ಯನ ಮನಸ್ಸು ದೇವರ ಮುಂದೆ ಒಪ್ಪಬೇಕಾದ ರೀತಿಯಲ್ಲಿ ನಡೆಯುತ್ತೇವೆ.",
			"3": "<sup>3</sup> ನಾವು ಸಾರುವ ಸುವಾರ್ತೆಯು ಕೆಲವರಿಗೆ ಮರೆಯಾಗಿರುವದಾದರೆ ನಾಶನಮಾರ್ಗದಲ್ಲಿರುವವರಿಗೇ ಮರೆಯಾಗಿರುವದು.",
			"4": "<sup>4</sup> ಇವರಲ್ಲಿ ದೇವರ ಪ್ರತಿರೂಪವಾಗಿರುವ ಕ್ರಿಸ್ತನ ಪ್ರಭಾವವನ್ನು ತೋರಿಸುವ ಸುವಾರ್ತೆಯ ಪ್ರಕಾಶವು ಉದಯವಾಗಬಾರದೆಂದು ಈ ಪ್ರಪಂಚದ ದೇವರು ನಂಬಿಕೆಯಿಲ್ಲದವರ ಮನಸ್ಸನ್ನು ಮಂಕುಮಾಡಿದನು.",
			"5": "<sup>5</sup> ನಮ್ಮನ್ನೇ ಪ್ರಸಿದ್ಧಿಪಡಿಸಿಕೊಳ್ಳದೆ ನಮ್ಮನ್ನು ಯೇಸುವಿನ ನಿಮಿತ್ತ ನಿಮ್ಮ ದಾಸರೆಂತಲೂ ಕ್ರಿಸ್ತೇಸುವನ್ನೇ ಕರ್ತನೆಂತಲೂ ಪ್ರಸಿದ್ಧಿಪಡಿಸುತ್ತೇವೆ.",
			"6": "<sup>6</sup> ಯಾಕಂದರೆ ಕತ್ತಲೆಯೊಳಗಿಂದ ಬೆಳಕು ಹೊಳೆಯಲಿ ಎಂದು ಹೇಳಿದ ದೇವರು ತಾನೇ ಯೇಸು ಕ್ರಿಸ್ತನ ಮುಖದಲ್ಲಿ ತೋರುವ ದೇವಪ್ರಭಾವಜ್ಞಾನವೆಂಬ ಪ್ರಕಾಶವು ಅನೇಕರಿಗೆ ಉಂಟಾಗುವದಕ್ಕಾಗಿ ನಮ್ಮ ಹೃದಯಗಳಲ್ಲಿ ಹೊಳೆದನು.",
			"7": "<sup>7</sup> ಬಲಾಧಿಕ್ಯವು ದೇವರದೇ ಹೊರತು ನಮ್ಮೊಳಗಿಂದ ಬಂದದ್ದಲ್ಲವೆಂದು ತೋರುವದಕ್ಕಾಗಿ ಈ ನಿಕ್ಷೇಪವು ಮಣ್ಣಿನ ಘಟಗಳಲ್ಲಿ ನಮಗುಂಟು.",
			"8": "<sup>8</sup> ಸರ್ವವಿಧದಲ್ಲಿಯೂ ನಮಗೆ ಇಕ್ಕಟ್ಟು ಇದ್ದರೂ ನಾವು ಅತಿ ಸಂಕಟ ಪಡುವವರಲ್ಲ; ನಾವು ದಿಕ್ಕು ಕಾಣದವರಾಗಿದ್ದರೂ ಕೇವಲ ದೆಸೆಗೆಟ್ಟವರಲ್ಲ;",
			"9": "<sup>9</sup> ಹಿಂಸೆ ಪಡುವವರಾಗಿದ್ದರೂ ಕೈಬಿಡಲ್ಪಟ್ಟವರಲ್ಲ; ಕೆಡವಲ್ಪಟ್ಟವರಾಗಿದ್ದರೂ ಪ್ರಾಣನಷ್ಟಪಡುವವರಲ್ಲ;",
			"10": "<sup>10</sup> ಯೇಸುವಿನ ಜೀವವು ನಮ್ಮ ದೇಹದಲ್ಲಿ ಉಂಟೆಂದು ತೋರಿಬರುವದಕ್ಕಾಗಿ ಯೇಸುವಿನ ಮರಣಾವಸ್ಥೆಯನ್ನು ನಾವು ಯಾವಾಗಲೂ ದೇಹದಲ್ಲಿ ಅನುಭವಿಸುತ್ತಾ ತಿರುಗಾಡುತ್ತೇವೆ.",
			"11": "<sup>11</sup> ಯೇಸುವಿನ ಜೀವವು ನಮ್ಮ ಮರ್ತ್ಯಶರೀರದಲ್ಲಿ ಉಂಟೆಂದು ತೋರಿಬರುವದಕ್ಕಾಗಿ ಬದುಕಿರುವ ನಾವು ಯೇಸುವಿನ ನಿಮಿತ್ತ ಯಾವಾಗಲೂ ಮರಣಕ್ಕೆ ಒಪ್ಪಿಸಲ್ಪಡುತ್ತಾ ಇದ್ದೇವೆ.",
			"12": "<sup>12</sup> ಹೀಗೆ ನಮ್ಮಲ್ಲಿ ಮರಣವು ಪ್ರವರ್ತಿಸುತ್ತದೆ; ಆದರೆ ನಿಮ್ಮಲ್ಲಿ ಜೀವವು ಪ್ರವರ್ತಿಸುತ್ತದೆ.",
			"13": "<sup>13</sup> ಹೀಗಿದ್ದರೂ - ನಂಬಿದೆನು, ಆದದರಿಂದ ಮಾತಾಡಿದೆನು ಎಂಬ ಶಾಸ್ತ್ರೋಕ್ತಿಯಲ್ಲಿ ಕಾಣುವ ನಂಬಿಕೆಯ ಭಾವವನ್ನೇ ಹೊಂದಿ ನಾವೂ ನಂಬಿದವರು, ಆದದರಿಂದ ಮಾತಾಡುತ್ತೇವೆ.",
			"14": "<sup>14</sup> ಕರ್ತನಾದ ಯೇಸುವನ್ನು ಎಬ್ಬಿಸಿದಾತನು ನಮ್ಮನ್ನು ಸಹ ಯೇಸುವಿನೊಂದಿಗೆ ಎಬ್ಬಿಸಿ ನಿಮ್ಮ ಜೊತೆಯಲ್ಲಿ ತನ್ನ ಮುಂದೆ ನಿಲ್ಲಿಸುವನೆಂದು ತಿಳಿದವರಾಗಿದ್ದೇವೆ.",
			"15": "<sup>15</sup> ಆ ಬಾಧೆಗಳೆಲ್ಲಾ ನಿಮ್ಮ ಹಿತಕ್ಕಾಗಿಯೇ ನಮಗೆ ಸಂಭವಿಸುತ್ತವೆ. ಅವುಗಳಲ್ಲಿ ಅಧಿಕವಾಗಿ ದೊರಕುವ ದೈವಕೃಪೆಯು ಬಹುಜನರೊಳಗೆ ಕೃತಜ್ಞತೆಯನ್ನು ಹುಟ್ಟಿಸುವದರಿಂದ ದೇವರಿಗೆ ಹೆಚ್ಚಾದ ಸ್ತೋತ್ರವನ್ನು ಉಂಟುಮಾಡುವದು.",
			"16": "<sup>16</sup> ಆದದರಿಂದ ನಾವು ಧೈರ್ಯಗೆಡುವದಿಲ್ಲ. ನಮ್ಮ ದೇಹವು ನಾಶವಾಗುತ್ತಾ ಇದ್ದರೂ ನಮ್ಮ ಆಂತರ್ಯವು ದಿನೇದಿನೇ ಹೊಸದಾಗುತ್ತಾ ಬರುತ್ತದೆ.",
			"17": "<sup>17</sup> ಹೇಗಂದರೆ ಕ್ಷಣಮಾತ್ರವಿರುವ ನಮ್ಮ ಹಗುರವಾದ ಸಂಕಟವು ಅತ್ಯಂತಾಧಿಕವಾದ ಪ್ರತಿಫಲವನ್ನು ಉಂಟುಮಾಡಿ ನಮಗೆ ನಿರಂತರವಾಗಿರುವ ಗೌರವವಾದ ಪ್ರಭಾವವನ್ನು ದೊರಕಿಸುತ್ತದೆ.",
			"18": "<sup>18</sup> ನಾವು ಕಾಣುವಂಥದನ್ನು ಲಕ್ಷಿಸದೆ ಕಾಣದಿರುವಂಥದನ್ನು ಲಕ್ಷಿಸುವವರಾಗಿದ್ದೇವೆ. ಕಾಣುವಂಥದು ಸ್ವಲ್ಪಕಾಲ ಮಾತ್ರ ಇರುವದು; ಕಾಣದಿರುವಂಥದು ಸದಾಕಾಲವೂ ಇರುವದು."
		},
		"5": {
			"1": "<sup>1</sup> ಭೂಮಿಯ ಮೇಲಿರುವ ನಮ್ಮ ಮನೆಯು ಅಂದರೆ ನಮ್ಮ ದೇಹವೆಂಬ ಗುಡಾರವು ಕಿತ್ತುಹಾಕಲ್ಪಟ್ಟರೂ ದೇವರಿಂದುಂಟಾದ ಒಂದು ಕಟ್ಟಡವು ಪರಲೋಕದಲ್ಲಿ ನಮಗುಂಟೆಂದು ಬಲ್ಲೆವು. ಅದು ಕೈಯಿಂದ ಕಟ್ಟಿದ ಮನೆಯಾಗಿರದೆ ನಿತ್ಯವಾಗಿರುವಂಥದಾಗಿದೆ.",
			"2": "<sup>2</sup> ನಾವು ಈಗಿನ ದೇಹದಲ್ಲಿರುವವರೆಗೂ ನರಳುತ್ತೇವೆ. ಪರಲೋಕದಿಂದ ದೊರಕುವ ನಮ್ಮ ನಿವಾಸವನ್ನು ನಾವು ದೇಹದ ಮೇಲೆ ಧರಿಸಿಕೊಳ್ಳಬೇಕೆಂದು ಅಪೇಕ್ಷಿಸುತ್ತೇವೆ,",
			"3": "<sup>3</sup> ಅಂದರೆ ಆ ದಿನದಲ್ಲಿ ನಾವು ಇನ್ನೂ ದೇಹದಲ್ಲಿಯೇ ಇದ್ದರೆ ಆ ನಿವಾಸವನ್ನು ದೇಹದ ಮೇಲೆಯೇ ಧರಿಸಿಕೊಳ್ಳುವೆವು.",
			"4": "<sup>4</sup> ಈ ಗುಡಾರದಲ್ಲಿರುವವರಾದ ನಾವು ಭಾರ ಹೊತ್ತುಕೊಂಡವರಾಗಿ ನರಳುತ್ತೇವೆ; ಇದು ಕಳಚಿ ಹೋಗಬೇಕೆಂದು ನಮ್ಮ ಇಷ್ಟವಲ್ಲ; ಮರ್ತ್ಯವಾದದ್ದು ನುಂಗಿಹೋಗಿ ಜೀವವೊಂದೇ ಉಳಿಯುವಂತೆ ಈ ಗುಡಾರದ ಮೇಲೆ ಆ ನಿವಾಸವನ್ನು ಧರಿಸಿಕೊಳ್ಳಬೇಕೆಂಬದೇ ನಮ್ಮ ಇಷ್ಟ.",
			"5": "<sup>5</sup> ಆ ಸ್ಥಿತಿಗಾಗಿ ನಮ್ಮನ್ನು ಸಿದ್ಧಮಾಡಿರುವವನು ದೇವರೇ; ಆತನು ಪವಿತ್ರಾತ್ಮನನ್ನು ಸಂಚಕಾರವಾಗಿ ಅನುಗ್ರಹಿಸಿದ್ದಾನೆ.",
			"6": "<sup>6</sup> ಹೀಗಿರುವದರಿಂದ ನಾವು ಯಾವಾಗಲೂ ಧೈರ್ಯವುಳ್ಳವರಾಗಿದ್ದೇವೆ.",
			"7": "<sup>7</sup> ನಾವು ನೋಡುವವರಾಗಿ ನಡೆಯದೆ ನಂಬುವವರಾಗಿಯೇ ನಡೆಯುತ್ತೇವಾದದರಿಂದ ದೇಹದಲ್ಲಿ ವಾಸಿಸುವವರೆಗೂ ಕರ್ತನಿಗೆ ದೂರದಲ್ಲಿರುವ ಪ್ರವಾಸಿಗಳಾಗಿದ್ದೇವೆಂಬದನ್ನು ಬಲ್ಲೆವು.",
			"8": "<sup>8</sup> ಇದನ್ನು ಆಲೋಚಿಸಿ ನಾವು ಧೈರ್ಯವುಳ್ಳವರಾಗಿದ್ದು ದೇಹವನ್ನು ಬಿಟ್ಟು ಕರ್ತನ ಬಳಿಯಲ್ಲಿರುವದೇ ಉತ್ತಮವೆಂದು ಎಣಿಸುತ್ತೇವೆ.",
			"9": "<sup>9</sup> ಆದದರಿಂದ ನಾವು ದೇಹವೆಂಬ ಮನೆಯಲ್ಲಿದ್ದರೂ ಸರಿಯೇ, ಅದನ್ನು ಬಿಟ್ಟಿದ್ದರೂ ಸರಿಯೇ, ಕರ್ತನಿಗೆ ಮೆಚ್ಚುವವರಾಗಿರಬೇಕೆಂಬದೇ ನಮ್ಮ ಗುರಿಯಾಗಿದೆ.",
			"10": "<sup>10</sup> ಯಾಕಂದರೆ ಪ್ರತಿಯೊಬ್ಬನು ತನ್ನ ದೇಹದ ಮೂಲಕ ನಡಿಸಿದ ಒಳ್ಳೇದಕ್ಕಾಗಲಿ ಕೆಟ್ಟದ್ದಕ್ಕಾಗಲಿ ಸರಿಯಾಗಿ ಪ್ರತಿಫಲವನ್ನು ಹೊಂದುವದಕ್ಕೋಸ್ಕರ ನಾವೆಲ್ಲರೂ ಕ್ರಿಸ್ತನ ನ್ಯಾಯಾಸನದ ಮುಂದೆ ಯಥಾಸ್ಥಿತಿಯಲ್ಲಿ ಕಾಣಿಸಿಕೊಳ್ಳಬೇಕು.",
			"11": "<sup>11</sup> ಕರ್ತನ ಭಯವು ನಮಗಿರುವದರಿಂದ ನಾವು ಮನುಷ್ಯರನ್ನು ಒಡಂಬಡಿಸುತ್ತೇವೆ. ನಾವು ಯಥಾರ್ಥರೆಂಬದು ದೇವರಿಗೆ ಗೊತ್ತುಂಟು; ನಿಮ್ಮ ಮನಸ್ಸಿಗೆ ಕೂಡ ಗೊತ್ತಾಗಿದೆ ಎಂದು ನಂಬಿಕೊಂಡಿದ್ದೇನೆ.",
			"12": "<sup>12</sup> ನಾವು ನಮ್ಮನ್ನು ತಿರಿಗಿ ನಿಮ್ಮ ಮುಂದೆ ಹೊಗಳಿಕೊಳ್ಳುವದಿಲ್ಲ; ಆದರೆ ಯಾರು ಹೃದಯದ ವಿಷಯದಲ್ಲಿ ಹಿಗ್ಗದೆ ತೋರಿಕೆಯ ವಿಷಯದಲ್ಲಿ ಮಾತ್ರ ಹಿಗ್ಗುತ್ತಾರೋ ಅವರಿಗೆ ಪ್ರತ್ಯುತ್ತರ ಹೇಳುವದಕ್ಕೆ ನಿಮಗೆ ಆಧಾರವಿರಬೇಕೆಂದು ನಮ್ಮ ವಿಷಯದಲ್ಲಿ ಹಿಗ್ಗುವದಕ್ಕೆ ನಿಮಗೆ ಆಸ್ಪದಕೊಡುತ್ತೇವೆ.",
			"13": "<sup>13</sup> ನಮಗೆ ಬುದ್ಧಿಪರವಶವಾಗಿದ್ದರೆ ಅದು ದೇವರ ಮಹಿಮೆಗಾಗಿಯೇ ಅದೆ; ನಮಗೆ ಸ್ವಸ್ಥಬುದ್ಧಿ ಇದ್ದರೆ ಅದು ನಿಮ್ಮ ಪ್ರಯೋಜನಕ್ಕಾಗಿ ಅದೆ.",
			"14": "<sup>14</sup> ಕ್ರಿಸ್ತನ ಪ್ರೀತಿಯು ನಮಗೆ ಒತ್ತಾಯಮಾಡುತ್ತದೆ; ಎಲ್ಲರಿಗೋಸ್ಕರ ಒಬ್ಬನು ಸತ್ತದ್ದರಿಂದ ಎಲ್ಲರೂ ಸತ್ತಂತಾಯಿತೆಂದು ನಿಶ್ಚಯಿಸಿಕೊಂಡೆವು.",
			"15": "<sup>15</sup> ಜೀವಿಸುವವರು ಇನ್ನು ಮೇಲೆ ತಮಗಾಗಿ ಜೀವಿಸದೆ ತಮಗೋಸ್ಕರ ಸತ್ತು ಎದ್ದು ಬಂದಾತನಿಗಾಗಿ ಜೀವಿಸಬೇಕೆಂತಲೇ ಆತನು ಎಲ್ಲರಿಗೋಸ್ಕರ ಸತ್ತನು.",
			"16": "<sup>16</sup> ಹೀಗಿರಲಾಗಿ ಇಂದಿನಿಂದ ನಾವು ಯಾರನ್ನೂ ಶರೀರಸಂಬಂಧವಾಗಿ ಅರಿತುಕೊಳ್ಳುವದಿಲ್ಲ; ಕ್ರಿಸ್ತನನ್ನು ಕೂಡ ನಾವು ಶರೀರಸಂಬಂಧವಾಗಿ ತಿಳಿದಿದ್ದರೂ ಇನ್ನು ಮುಂದೆ ಆತನನ್ನು ಹಾಗೆ ತಿಳುಕೊಳ್ಳುವದಿಲ್ಲ.",
			"17": "<sup>17</sup> ಹೀಗಿರಲಾಗಿ ಯಾವನಾದರೂ ಕ್ರಿಸ್ತನಲ್ಲಿದ್ದರೆ ಅವನು ನೂತನಸೃಷ್ಟಿಯಾದನು. ಇಗೋ, ಪೂರ್ವಸ್ಥಿತಿ ಹೋಗಿ ಎಲ್ಲಾ ನೂತನವಾಯಿತು.",
			"18": "<sup>18</sup> ಇದೆಲ್ಲಾ ದೇವರಿಂದಲೇ ಉಂಟಾದದ್ದು. ಆತನು ಕ್ರಿಸ್ತನ ಮೂಲಕ ನಮ್ಮನ್ನು ತನಗೆ ಸಮಾಧಾನಪಡಿಸಿಕೊಂಡು ಸಮಾಧಾನವಿಷಯವಾದ ಸೇವೆಯನ್ನು ನಮಗೆ ಅನುಗ್ರಹಿಸಿದ್ದಾನೆ;",
			"19": "<sup>19</sup> ಆ ಸಮಾಧಾನವಾಕ್ಯವೇನಂದರೆ ದೇವರು ಮನುಷ್ಯರ ಅಪರಾಧಗಳನ್ನು ಅವರ ಲೆಕ್ಕಕ್ಕೆ ಹಾಕದೆ ಲೋಕವನ್ನು ಕ್ರಿಸ್ತನಲ್ಲಿ ತನಗೆ ಸಮಾಧಾನಪಡಿಸಿಕೊಳ್ಳುತ್ತಿದ್ದನೆಂಬದೇ. ಅದನ್ನು ಸಾರುವ ಸೇವೆಗೆ ನಮ್ಮನ್ನು ನೇಮಿಸಿದ್ದಾನೆ.",
			"20": "<sup>20</sup> ನಾವು ಕ್ರಿಸ್ತನ ರಾಯಭಾರಿಗಳಾಗಿದ್ದೇವೆ. ದೇವರೇ ನಮ್ಮ ಮೂಲಕ ಬುದ್ಧಿ ಹೇಳುವ ಹಾಗಾಯಿತು. ದೇವರೊಂದಿಗೆ ಸಮಾಧಾನವಾಗಿರೆಂದು ಕ್ರಿಸ್ತನ ಹೆಸರಿನಲ್ಲಿ ನಿಮ್ಮನ್ನು ಬೇಡಿಕೊಳ್ಳುತ್ತೇವೆ.",
			"21": "<sup>21</sup> ನಾವು ಆತನಲ್ಲಿ ದೇವರಿಗೆ ಸಮರ್ಪಕರಾದ ನೀತಿಸ್ವರೂಪಿಗಳಾಗುವಂತೆ ದೇವರು ಪಾಪಜ್ಞಾನವಿಲ್ಲದ ಆತನನ್ನು ನಮಗೋಸ್ಕರ ಪಾಪ ಸ್ವರೂಪಿಯಾಗ ಮಾಡಿದನು."
		},
		"6": {
			"1": "<sup>1</sup> ನಾವು ಆತನೊಂದಿಗೆ ಕೆಲಸ ನಡಿಸುವವರಾಗಿದ್ದು - ನೀವು ಹೊಂದಿದ ದೇವರ ಕೃಪೆಯನ್ನು ವ್ಯರ್ಥ ಮಾಡಿಕೊಳ್ಳಬೇಡಿರೆಂದು ಎಚ್ಚರಿಸುತ್ತೇವೆ.",
			"2": "<sup>2</sup> ಪ್ರಸನ್ನತೆಯ ಕಾಲದಲ್ಲಿ ನಿನ್ನ ಮನವಿಯನ್ನು ಕೇಳಿದೆನು, ರಕ್ಷಣೆಯ ದಿನದಲ್ಲಿ ನಿನಗೆ ಸಹಾಯ ಮಾಡಿದೆನು ಎಂದು ದೇವರು ಹೇಳುತ್ತಾನಲ್ಲಾ. ಈಗಲೇ ಆ ಸುಪ್ರಸನ್ನತೆಯಕಾಲ; ಇದೇ ಆ ರಕ್ಷಣೆಯ ದಿನ.",
			"3": "<sup>3</sup> ನಿಂದೆಗೆ ಅವಕಾಶಕೊಡದೆ ಎಲ್ಲಾ ಸಂಗತಿಗಳಲ್ಲಿ ದೇವರ ಸೇವಕರೆಂದು ನಮ್ಮನ್ನು ಸಮ್ಮತರಾಗ ಮಾಡಿಕೊಳ್ಳುತ್ತೇವೆ. ನಾವು ಸಂಕಟಗಳಲ್ಲಿಯೂ ಕೊರತೆಗಳಲ್ಲಿಯೂ ಇಕ್ಕಟ್ಟುಗಳಲ್ಲಿಯೂ ಪೆಟ್ಟುಗಳಲ್ಲಿಯೂ ಸೆರೆಮನೆಗಳಲ್ಲಿಯೂ",
			"5": "<sup>5</sup> ಕಲಹಗಳಲ್ಲಿಯೂ ಕಷ್ಟವಾದ ಕೆಲಸಗಳಲ್ಲಿಯೂ ನಿದ್ದೆಗೇಡುಗಳಲ್ಲಿಯೂ ಉಪವಾಸಗಳಲ್ಲಿಯೂ ಬಹು ತಾಳ್ಮೆಯನ್ನು ತೋರಿಸುತ್ತೇವೆ.",
			"6": "<sup>6</sup> ಶುದ್ಧಮನಸ್ಸು ಜ್ಞಾನ ದೀರ್ಘಶಾಂತಿ ದಯೆ ಪವಿತ್ರಾತ್ಮ ಕಪಟವಿಲ್ಲದ ಪ್ರೀತಿ",
			"7": "<sup>7</sup> ಸತ್ಯವಾಕ್ಯ ದೇವರ ಮಹತ್ವ ಇವುಗಳಿಂದ ಕೂಡಿದವರಾಗಿದ್ದೇವೆ. ಎಡಬಲಗೈಗಳಲ್ಲಿ ನೀತಿಯೆಂಬ ಆಯುಧಗಳನ್ನು ಧರಿಸಿಕೊಂಡವರೂ",
			"8": "<sup>8</sup> ಮಾನ ಅವಮಾನ ಕೀರ್ತಿ ಅಪಕೀರ್ತಿಗಳನ್ನು ಹೊಂದಿದವರೂ ಆಗಿದ್ದೇವೆ.",
			"9": "<sup>9</sup> ಮೋಸಗಾರರೆನಿಸಿಕೊಂಡರೂ ಸತ್ಯವಂತರೂ, ಅಜ್ಞಾತರೆನಿಸಿಕೊಂಡರೂ ವಿಜ್ಞಾತರೂ, ಸಾಯುವವರಾಗಿ ತೋರಿದರೂ ಬದುಕುವವರೂ ಆಗಿದ್ದೇವೆ. ಶಿಕ್ಷೆ ಹೊಂದುವವರಾಗಿದ್ದರೂ ಕೊಲ್ಲಲ್ಪಡದವರೂ,",
			"10": "<sup>10</sup> ದುಃಖಪಡುವವರಾಗಿದ್ದರೂ ಯಾವಾಗಲೂ ಸಂತೋಷಪಡುವವರೂ, ಬಡವರಾಗಿದ್ದರೂ ಅನೇಕರಿಗೆ ಐಶ್ವರ್ಯವನ್ನುಂಟುಮಾಡುವವರೂ, ಏನೂ ಇಲ್ಲದವರಾಗಿದ್ದರೂ ಎಲ್ಲಾ ಇದ್ದವರೂ ಆಗಿದ್ದೇವೆ.",
			"11": "<sup>11</sup> ಕೊರಿಂಥದವರೇ, ನಾವು ನಿಮ್ಮೊಡನೆ ಧಾರಾಳವಾಗಿ ಮಾತಾಡುತ್ತಾ ಇದ್ದೇವೆ; ನಮ್ಮ ಹೃದಯವು ನಿಮ್ಮ ವಿಷಯದಲ್ಲಿ ವಿಶಾಲವಾಯಿತು.",
			"12": "<sup>12</sup> ನಿಮ್ಮ ಮೇಲಿರುವ ನಮ್ಮ ಪ್ರೀತಿ ಸಂಕೋಚವಾದದ್ದಲ್ಲ; ನಿಮ್ಮ ಪ್ರೀತಿಯೇ ಸಂಕೋಚವಾದದ್ದು.",
			"13": "<sup>13</sup> ನಿಮ್ಮ ವಿಷಯದಲ್ಲಿ ನಮ್ಮ ಹೃದಯವು ವಿಶಾಲವಾಗಿರುವದರಿಂದ ನಮ್ಮ ವಿಷಯದಲ್ಲಿ ನಿಮ್ಮ ಹೃದಯವನ್ನೂ ವಿಶಾಲಮಾಡಿಕೊಳ್ಳಿರಿ. ನೀವು ನನ್ನ ಮಕ್ಕಳೆಂದು ಭಾವಿಸಿ ಹೀಗೆ ಹೇಳುತ್ತೇನೆ.",
			"14": "<sup>14</sup> ನೀವು ಕ್ರಿಸ್ತನಂಬಿಕೆಯಿಲ್ಲದವರೊಂದಿಗೆ ಸೇರಿ ಇಜ್ಜೋಡಾಗಬೇಡಿರಿ. ಧರ್ಮಕ್ಕೂ ಅಧರ್ಮಕ್ಕೂ ಜೊತೆಯೇನು?",
			"15": "<sup>15</sup> ಬೆಳಕಿಗೂ ಕತ್ತಲೆಗೂ ಐಕ್ಯವೇನು? ಕ್ರಿಸ್ತನಿಗೂ ಸೈತಾನನಿಗೂ ಒಡನಾಟವೇನು? ನಂಬುವವನಿಗೂ ನಂಬದೆ ಇರುವವನಿಗೂ ಪಾಲುಗಾರಿಕೆ ಏನು?",
			"16": "<sup>16</sup> ದೇವರ ಮಂದಿರಕ್ಕೂ ವಿಗ್ರಹಗಳಿಗೂ ಒಪ್ಪಿಗೆ ಏನು? ನಾವು ಜೀವಸ್ವರೂಪನಾದ ದೇವರ ಮಂದಿರವಾಗಿದ್ದೇವಲ್ಲಾ. ಇದರ ಸಂಬಂಧವಾಗಿ ದೇವರು ನಾನು ಅವರಲ್ಲಿ ವಾಸಿಸುತ್ತಾ ತಿರುಗಾಡುವೆನು, ನಾನು ಅವರಿಗೆ ದೇವರಾಗಿರುವೆನು, ಅವರು ನನಗೆ ಪ್ರಜೆಯಾಗಿರುವರು ಎಂದು ಹೇಳಿದ್ದಾನೆ.",
			"17": "<sup>17</sup> ಆದದರಿಂದ ಅನ್ಯಜನರ ಮಧ್ಯದಲ್ಲಿಂದ ಹೊರಟು ಬಂದು ಪ್ರತ್ಯೇಕವಾಗಿರಿ; ಅಶುದ್ಧವಾದ ಯಾವದನ್ನೂ ಮುಟ್ಟದಿರಿ ಎಂದು ಕರ್ತನು ಹೇಳುತ್ತಾನೆ.",
			"18": "<sup>18</sup> ಇದಲ್ಲದೆ - ನಾನು ನಿಮ್ಮನ್ನು ಸೇರಿಸಿಕೊಂಡು ನಿಮಗೆ ತಂದೆಯಾಗಿರುವೆನು; ನೀವು ನನಗೆ ಕುಮಾರ ಕುಮಾರಿಯರು ಆಗಿರುವಿರೆಂದು ಸರ್ವಶಕ್ತನಾದ ಕರ್ತನು ಹೇಳುತ್ತಾನೆ."
		},
		"7": {
			"1": "<sup>1</sup> ಪ್ರಿಯರೇ, ಈ ವಾಗ್ದಾನಗಳು ನಮಗಿರುವದರಿಂದ ನಾವು ಶರೀರಾತ್ಮಗಳ ಕಲ್ಮಶವನ್ನು ತೊಲಗಿಸಿ ನಮ್ಮನ್ನು ಶುಚಿಮಾಡಿಕೊಂಡು ದೇವರ ಭಯದಿಂದ ಕೂಡಿದವರಾಗಿ ಪವಿತ್ರತ್ವವನ್ನು ಸಿದ್ಧಿಗೆ ತರುವದಕ್ಕೆ ಪ್ರಯತ್ನಿಸೋಣ.",
			"2": "<sup>2</sup> ನಿಮ್ಮ ಹೃದಯಗಳಲ್ಲಿ ನಮಗೆ ಸ್ಥಳಕೊಡಿರಿ; ನಾವು ನಿಮ್ಮಲ್ಲಿ ಯಾರಿಗೂ ಅನ್ಯಾಯಮಾಡಲಿಲ್ಲ, ಯಾರನ್ನೂ ಕೆಡಿಸಲಿಲ್ಲ, ಯಾರನ್ನೂ ವಂಚಿಸಲಿಲ್ಲ.",
			"3": "<sup>3</sup> ನಿಮ್ಮ ಮೇಲೆ ತಪ್ಪುಹೊರಿಸಬೇಕೆಂದು ನಾನು ಇದನ್ನು ಹೇಳುವದಿಲ್ಲ. ಕೂಡಾ ಸಾಯುವದಕ್ಕೂ ಕೂಡಾ ಬದುಕುವದಕ್ಕೂ ನೀವು ನಮ್ಮ ಹೃದಯದಲ್ಲಿದ್ದೀರೆಂದು ಮೊದಲೇ ಹೇಳಿದ್ದೇನಲ್ಲಾ.",
			"4": "<sup>4</sup> ನಿಮ್ಮಲ್ಲಿ ನನಗೆ ಬಹಳ ಭರವಸ ಉಂಟು; ನಿಮ್ಮ ವಿಷಯದಲ್ಲಿ ನನ್ನ ಉತ್ಸಾಹ ಬಹಳ; ಪರಿಪೂರ್ಣ ಆದರಣೆ ನನಗಾಯಿತು; ನಮಗಾಗುವ ಎಲ್ಲಾ ಸಂಕಟಗಳಲ್ಲಿ ಅತ್ಯಧಿಕವಾದ ಸಂತೋಷದಿಂದ ತುಂಬಿದ್ದೇನೆ.",
			"5": "<sup>5</sup> ನಾವು ಮಕೆದೋನ್ಯಕ್ಕೆ ಬಂದಾಗ ನಮ್ಮ ಮನಸ್ಸಿಗೆ ಏನೂ ಉಪಶಮನವಾಗಲಿಲ್ಲ, ಎಲ್ಲಾ ವಿಷಯಗಳಲ್ಲಿಯೂ ನಮಗೆ ಸಂಕಟವಿತ್ತು; ಹೊರಗೆ ಕಲಹ, ಒಳಗೆ ಭಯ.",
			"6": "<sup>6</sup> ಆದರೆ ದೀನಾವಸ್ಥೆಯಲ್ಲಿರುವವರನ್ನು ಸಂತೈಸುವ ದೇವರು ತೀತನ ಬರುವಿಕೆಯಿಂದ ನಮ್ಮನ್ನು ಸಂತೈಸಿದನು.",
			"7": "<sup>7</sup> ಅವನ ಬರುವಿಕೆಯಿಂದ ಮಾತ್ರವಲ್ಲದೆ ಅವನು ತಾನು ನಿಮ್ಮಲ್ಲಿ ಬಹಳ ಆದರಣೆಯನ್ನು ಹೊಂದಿದೆನೆಂದು ಹೇಳಿದ್ದರಿಂದಲೂ ನಮ್ಮನ್ನು ಸಂತೈಸಿದನು. ಅವನು ನಿಮ್ಮ ಹಂಬಲ, ನಿಮ್ಮ ಗೋಳಾಟ, ನನ್ನ ವಿಷಯವಾದ ನಿಮ್ಮ ಆಸಕ್ತಿ ಇವುಗಳನ್ನು ನಮಗೆ ತಿಳಿಸಿದಾಗ ನಾನು ಹೆಚ್ಚಾಗಿ ಸಂತೋಷಪಟ್ಟೆನು.",
			"8": "<sup>8</sup> ನಾನು ಬರೆದ ಪತ್ರಿಕೆಯು ನಿಮ್ಮನ್ನು ದುಃಖಪಡಿಸಿದ್ದರೂ ನನಗೆ ಪಶ್ಚಾತ್ತಾಪವಿಲ್ಲ. ಅದು ನಿಮ್ಮನ್ನು ಒಂದು ಗಳಿಗೆ ಹೊತ್ತಾದರೂ ದುಃಖಪಡಿಸಿತೆಂದು ನಾನು ಮೊದಲು ಪಶ್ಚಾತ್ತಾಪಪಡುತ್ತಿದ್ದರೂ ಈಗ ಸಂತೋಷಪಡುತ್ತೇನೆ.",
			"9": "<sup>9</sup> ನಿಮಗೆ ದುಃಖವಾಯಿತೆಂದು ನಾನು ಸಂತೋಷಪಡದೆ ನೀವು ದುಃಖಪಟ್ಟು ಬೇರೆ ಮನಸ್ಸನ್ನು ಹೊಂದಿದ್ದಕ್ಕಾಗಿ ಸಂತೋಷಪಡುತ್ತೇನೆ. ನೀವು ದೇವರ ಚಿತ್ತಾನುಸಾರವಾಗಿ ದುಃಖಿಸಿದ್ದರಿಂದ ನಮ್ಮಿಂದ ಯಾವದರಲ್ಲಿಯೂ ನಷ್ಟಪಡಲಿಲ್ಲವಲ್ಲಾ.",
			"10": "<sup>10</sup> ದೇವರ ಚಿತ್ತಾನುಸಾರವಾಗಿರುವ ದುಃಖವು ಮಾನಸಾಂತರವನ್ನುಂಟುಮಾಡಿ ರಕ್ಷಣೆಗೆ ಕಾರಣವಾಗಿದೆ; ಆ ಮಾನಸಾಂತರದಲ್ಲಿ ಪಶ್ಚಾತ್ತಾಪಕ್ಕೆ ಆಸ್ಪದವಿಲ್ಲ; ಆದರೆ ಲೋಕದವರಿಗಿರುವಂಥ ದುಃಖವು ಮರಣವನ್ನುಂಟುಮಾಡುತ್ತದೆ.",
			"11": "<sup>11</sup> ನೀವು ದೇವರ ಚಿತ್ತಾನುಸಾರವಾಗಿ ಪಟ್ಟ ದುಃಖವು ನಿಮಗೆ ಎಂಥ ತಹತಹವನ್ನು ಉಂಟುಮಾಡಿತು ನೋಡಿರಿ. ನೀವು ನಿರ್ದೋಷಿಗಳೆಂದು ಸ್ಥಾಪಿಸುವದಕ್ಕೆ ಎಷ್ಟೋ ಪ್ರಯಾಸಪಟ್ಟಿರಿ, ಎಷ್ಟೋ ಮನೋವ್ಯಥೆಯನ್ನು ಅನುಭವಿಸಿದಿರಿ; ಎಂಥ ಭಯವನ್ನು ಎಂಥ ಹಂಬಲವನ್ನು ತೋರಿಸಿದಿರಿ; ಮಾನಹಾನಿಗಾಗಿ ಎಷ್ಟೋ ರೋಷಪಟ್ಟು ಶಿಕ್ಷೆಮಾಡಿದಿರಿ. ನೀವು ಆ ಕಾರ್ಯಕ್ಕೆ ಸೇರಿದವರಲ್ಲವೆಂಬದನ್ನು ಎಲ್ಲಾ ವಿಧದಲ್ಲಿಯೂ ತೋರಿಸಿದಿರಿ.",
			"12": "<sup>12</sup> ನಾನು ನಿಮಗೆ ಬರೆದಾಗ ಅನ್ಯಾಯಮಾಡಿದವನಿಗೆ ದಂಡನೆಯೂ ಅನ್ಯಾಯಹೊಂದಿದವನಿಗೆ ನ್ಯಾಯವೂ ಆಗಬೇಕೆಂಬುವ ಕಾರಣದಿಂದ ಬರೆಯಲಿಲ್ಲ; ನಮಗೋಸ್ಕರ ನಿಮಗಿರುವ ಆಸಕ್ತಿಯು ದೇವರ ಮುಂದೆ ನಿಮಗೆ ವ್ಯಕ್ತವಾಗಬೇಕೆಂಬುವ ಉದ್ದೇಶದಿಂದಲೇ ಬರೆದೆನು.",
			"13": "<sup>13</sup> ಆದಕಾರಣ ನಮ್ಮ ಮನಸ್ಸಿಗೆ ಆದರಣೆ ಉಂಟಾಯಿತು. ಅಷ್ಟು ಮಾತ್ರವೇ ಅಲ್ಲ, ತೀತನ ಮನಸ್ಸಿಗೆ ನಿಮ್ಮೆಲ್ಲರಿಂದ ಉಪಶಮನವಾದ ಕಾರಣ ನಾವು ಅವನ ಸಂತೋಷವನ್ನು ನೋಡಿ ಮತ್ತೂ ಹೆಚ್ಚಾಗಿ ಸಂತೋಷಪಟ್ಟೆವು.",
			"14": "<sup>14</sup> ಯಾಕಂದರೆ ನಾನು ಅವನ ಮುಂದೆ ನಿಮ್ಮನ್ನು ಹೊಗಳಿದ್ದರಲ್ಲಿ ನಾಚಿಕೆಪಡಬೇಕಾಗಿ ಬರಲಿಲ್ಲ. ನಾವು ನಿಮಗೆ ಹೇಳಿದ ಎಲ್ಲಾ ಮಾತುಗಳು ಹೇಗೆ ಸತ್ಯವಾಗಿದ್ದವೋ ಹಾಗೆ ನಾವು ತೀತನ ಮುಂದೆ ಆಡಿದ ಹೊಗಳಿಕೆಯ ಮಾತುಗಳೂ ಸತ್ಯವೆಂದು ಕಂಡುಬಂದವು.",
			"15": "<sup>15</sup> ನೀವು ಮನೋಭೀತಿಯಿಂದ ನಡುಗುವವರಾಗಿ ಅವನನ್ನು ಸೇರಿಸಿಕೊಂಡದ್ದರಲ್ಲಿ ನಿಮ್ಮೆಲ್ಲರ ವಿಧೇಯತೆಯು ತೋರಬಂತು. ಅವನು ಅದನ್ನು ಜ್ಞಾಪಕಮಾಡಿಕೊಳ್ಳುವಾಗ ನಿಮ್ಮ ಮೇಲೆ ಅವನ ಕನಿಕರವು ಹೆಚ್ಚಾಗಿದೆ.",
			"16": "<sup>16</sup> ಸರ್ವ ವಿಷಯದಲ್ಲೂ ನಿಮ್ಮನ್ನು ಕುರಿತು ನನಗೆ ಭರವಸವಿರುವದರಿಂದ ಸಂತೋಷ ಪಡುತ್ತೇನೆ."
		},
		"8": {
			"1": "<sup>1</sup> ಸಹೋದರರೇ, ಮಕೆದೋನ್ಯದ ಸಭೆಗಳಲ್ಲಿ ದೇವರ ಕೃಪೆಯು ತೋರಿದ ಬಗೆಯನ್ನು ನಿಮಗೆ ತಿಳಿಸುತ್ತೇವೆ.",
			"2": "<sup>2</sup> ಆ ಸಭೆಗಳವರು ಬಹಳ ಹಿಂಸೆ ತಾಳುವವರಾದರೂ ಮತ್ತು ವಿಪರೀತವಾದ ಬಡತನದಲ್ಲಿದ್ದರೂ ಬಹು ಆನಂದದಿಂದ ತುಂಬಿದವರಾಗಿ ಅತ್ಯಂತ ಔದಾರ್ಯವುಳ್ಳವರಾದರು.",
			"3": "<sup>3</sup> ಅವರು ಶಕ್ತ್ಯನುಸಾರವಾಗಿ ಮಾತ್ರ ಕೊಡದೆ ಶಕ್ತಿಯನ್ನು ಮೀರಿ ತಮ್ಮಷ್ಟಕ್ಕೆ ತಾವೇ ಕೊಟ್ಟರು; ಇದಕ್ಕೆ ನಾನು ಸಾಕ್ಷಿ.",
			"4": "<sup>4</sup> ದೇವಜನರಿಗೆ ಸಹಾಯಮಾಡುವ ಕೆಲಸದಲ್ಲಿ ತಾವು ಪಾಲುಗಾರರಾಗುವಂತೆ ಅಪ್ಪಣೆಯಾಗಬೇಕೆಂದು ನಮ್ಮನ್ನು ಬಹಳವಾಗಿ ಬೇಡಿಕೊಂಡರು.",
			"5": "<sup>5</sup> ನಾವು ನೆನಸಿದ್ದ ಪ್ರಕಾರವಾಗಿ ಅವರು ಕೊಡದೆ ಮೊದಲು ತಮ್ಮನ್ನೇ ಕರ್ತನಿಗೆ ಒಪ್ಪಿಸಿಕೊಟ್ಟರು; ಅನಂತರ ದೇವರ ಚಿತ್ತಾನುಸಾರವಾಗಿ ನಮಗೂ ತಮ್ಮನ್ನು ಒಪ್ಪಿಸಿದರು.",
			"6": "<sup>6</sup> ಹೀಗಿರಲಾಗಿ ಈ ಧರ್ಮಕಾರ್ಯವನ್ನು ಮಾಡುವದಕ್ಕೆ ಮೊದಲು ನಿಮ್ಮನ್ನು ಪ್ರೇರೇಪಿಸಿದ ತೀತನು ತಿರಿಗಿ ನಿಮ್ಮ ಬಳಿಗೆ ಬಂದು ಅದನ್ನು ತೀರಿಸಬೇಕೆಂದು ನಾವು ಅವನನ್ನು ಕೇಳಿಕೊಂಡಿದ್ದೇವೆ.",
			"7": "<sup>7</sup> ನೀವು ದೇವರ ಮೇಲಣ ನಂಬಿಕೆ ವಾಕ್ಚಾತುರ್ಯ ಜ್ಞಾನ ಸಕಲವಿಧವಾದ ಆಸಕ್ತಿ ನಮ್ಮ ಪ್ರೇರಣೆಯಿಂದ ನಿಮ್ಮಲ್ಲಿ ಹುಟ್ಟಿದ ಪ್ರೀತಿ ಇವೇ ಮೊದಲಾದ ಎಲ್ಲಾ ವಿಷಯಗಳಲ್ಲಿ ಹೇಗೆ ಸಮೃದ್ಧರಾಗಿದ್ದೀರೋ ಹಾಗೆಯೇ ಈ ಧರ್ಮಕಾರ್ಯದಲ್ಲಿಯೂ ಸಮೃದ್ಧರಾಗಿರಿ.",
			"8": "<sup>8</sup> ಇದನ್ನು ಆಜ್ಞಾರೂಪವಾಗಿ ಹೇಳದೆ ಇತರರ ಆಸಕ್ತಿಯನ್ನು ತೋರಿಸಿಕೊಟ್ಟು ನಿಮ್ಮ ಪ್ರೀತಿಯೂ ಯಥಾರ್ಥವಾದದ್ದೆಂಬದನ್ನು ಪರೀಕ್ಷೆಯಿಂದ ತಿಳಿದುಕೊಳ್ಳಬೇಕೆಂದು ಹೇಳುತ್ತೇನೆ.",
			"9": "<sup>9</sup> ನಮ್ಮ ಕರ್ತನಾದ ಯೇಸು ಕ್ರಿಸ್ತನ ಕೃಪೆಯು ನಿಮಗೆ ಗೊತ್ತಾಗಿದೆಯಲ್ಲಾ; ಆತನು ಐಶ್ವರ್ಯವಂತನಾಗಿದ್ದು ತಾನು ಬಡತನದಲ್ಲಿ ಸೇರುವದರಿಂದ ನೀವು ಐಶ್ವರ್ಯವಂತರಾಗಬೇಕೆಂದು ನಿಮಗೋಸ್ಕರ ಬಡವನಾದನು.",
			"10": "<sup>10</sup> ಈ ಧರ್ಮಕಾರ್ಯವನ್ನು ಕುರಿತು ನನ್ನ ಅಭಿಪ್ರಾಯವನ್ನು ತಿಳಿಸುತ್ತೇನೆ; ಈ ಕಾರ್ಯವನ್ನು ಒಂದು ವರುಷದ ಹಿಂದೆ ನಡಿಸುವದಕ್ಕೆ ತೊಡಗುವದರಲ್ಲಿಯೂ ನಡಿಸಬೇಕೆಂದು ಮನಸ್ಸು ಮಾಡುವದರಲ್ಲಿಯೂ ನೀವು ಅವರಿಗಿಂತ ಮೊದಲಿನವರಾದದರಿಂದ ನಮಗೆ ಅಷ್ಟು ಮಾತ್ರ ಹೇಳುವದು ವಿಹಿತವಾಗಿದೆ.",
			"11": "<sup>11</sup> ಈ ಕಾರ್ಯವನ್ನು ಪ್ರಾರಂಭಿಸುವದಕ್ಕೆ ಹೇಗೆ ನಿಮ್ಮಲ್ಲಿ ಸಿದ್ಧಮನಸ್ಸು ಇತ್ತೋ ಹಾಗೆಯೇ ನಿಮ್ಮ ನಿಮ್ಮ ಶಕ್ತ್ಯನುಸಾರವಾಗಿ ನೆರವೇರಿಸಿರಿ.",
			"12": "<sup>12</sup> ಒಬ್ಬನು ಕೊಡುವದಕ್ಕೆ ಮನಸ್ಸುಳ್ಳವನಾಗಿರುವಲ್ಲಿ ಅವನು ತನ್ನಲ್ಲಿ ಇರುವದಕ್ಕೆ ಅನುಸಾರವಾಗಿ ಕೊಟ್ಟರೆ ಅದು ಸಮರ್ಪಕವಾಗಿರುವದು; ಅವನಿಗೆ ಇಲ್ಲದ್ದನ್ನು ದೇವರು ಕೇಳಿಕೊಳ್ಳುವದಿಲ್ಲ.",
			"13": "<sup>13</sup> ಇತರರಿಗೆ ಕಷ್ಟಪರಿಹಾರವೂ ನಿಮಗೆ ಕಷ್ಟವೂ ಉಂಟಾಗಬೇಕೆಂದು ನನ್ನ ತಾತ್ಪರ್ಯವಲ್ಲ;",
			"14": "<sup>14</sup> ಸಮಾನತ್ವವಿರಬೇಕೆಂಬದೇ ನನ್ನ ತಾತ್ಪರ್ಯ. ಸದ್ಯಕ್ಕೆ ನಿಮ್ಮ ಸಮೃದ್ಧಿಯು ಅವರ ಕೊರತೆಯನ್ನು ನೀಗಿಸುತ್ತದೆ. ಮುಂದೆ ಅವರ ಸಮೃದ್ಧಿಯು ನಿಮ್ಮ ಕೊರತೆಯನ್ನು ನೀಗಿಸುವದು; ಹೀಗೆ ಸಮಾನತ್ವವುಂಟಾಗುವದು.",
			"15": "<sup>15</sup> ಬಹಳ ಕೂಡಿಸಿದವನಿಗೆ ಏನೂ ಹೆಚ್ಚಾಗಲಿಲ್ಲ, ಸ್ವಲ್ಪವಾಗಿ ಕೂಡಿಸಿದವನಿಗೆ ಏನೂ ಕಡಿಮೆಯಾಗಲಿಲ್ಲ ಎಂದು ಶಾಸ್ತ್ರದಲ್ಲಿ ಬರೆದಿರುವ ಪ್ರಕಾರವಾಗುವದು.",
			"16": "<sup>16</sup> ನಿಮ್ಮ ವಿಷಯವಾಗಿ ನನಗಿರುವ ಹಿತಚಿಂತನೆಯನ್ನು ದೇವರು ತೀತನ ಹೃದಯದಲ್ಲಿಯೂ ಹುಟ್ಟಿಸಿದ್ದಕ್ಕಾಗಿ ಆತನಿಗೆ ಸ್ತೋತ್ರವಾಗಲಿ.",
			"17": "<sup>17</sup> ಅವನು ನಿಮ್ಮ ಬಳಿಗೆ ಹೋಗಬೇಕೆಂದು ನಾವು ಕೇಳಿಕೊಂಡಾಗ ಅವನು ಒಪ್ಪಿದ್ದಲ್ಲದೆ ಅತ್ಯಾಸಕ್ತನಾಗಿದ್ದು ತನ್ನಷ್ಟಕ್ಕೆ ತಾನೇ ನಿಮ್ಮ ಬಳಿಗೆ ಬರುವದಕ್ಕೆ ಹೊರಟಿದ್ದಾನೆ.",
			"18": "<sup>18</sup> ಅವನ ಜೊತೆಯಲ್ಲಿ ಮತ್ತೊಬ್ಬ ಸಹೋದರನನ್ನು ಕಳುಹಿಸಿದ್ದೇವೆ; ಈ ಸಹೋದರನು ಸುವಾರ್ತಾಸೇವೆಯಲ್ಲಿ ಎಲ್ಲಾ ಸಭೆಗಳೊಳಗೆ ಕೀರ್ತಿಪಡಕೊಂಡವನಾಗಿದ್ದಾನೆ.",
			"19": "<sup>19</sup> ಇದು ಮಾತ್ರವಲ್ಲದೆ ನಾವು ನಡಿಸುವ ಈ ಧರ್ಮಕಾರ್ಯ ಸಂಬಂಧವಾಗಿ ಸಭೆಗಳವರು ಈತನನ್ನು ನಮ್ಮ ಸಂಗಡ ಪ್ರಯಾಣಮಾಡುವಂತೆ ಗೊತ್ತುಮಾಡಿದ್ದಾರೆ. ಅವರು ಹೀಗೆ ಮಾಡಿದ್ದರಿಂದ ಕರ್ತನಿಗೆ ಘನವೂ ನಮ್ಮಲ್ಲಿ ಸಿದ್ಧಮನಸ್ಸೂ ಉಂಟಾಗುವದು.",
			"20": "<sup>20</sup> ನಾವು ಪಾರುಪತ್ಯಮಾಡುವ ಈ ದ್ರವ್ಯಸಂಚಯದ ವಿಷಯದಲ್ಲಿ ಒಬ್ಬರೂ ನಮ್ಮ ಮೇಲೆ ತಪ್ಪುಹೊರಿಸುವದಕ್ಕೆ ಆಸ್ಪದವಿರಬಾರದು.",
			"21": "<sup>21</sup> ಯಾಕಂದರೆ ಕರ್ತನ ದೃಷ್ಟಿಯಲ್ಲಿ ಮಾತ್ರವಲ್ಲದೆ ಮನುಷ್ಯರ ದೃಷ್ಟಿಯಲ್ಲಿಯೂ ಗೌರವವಾದದ್ದನ್ನು ಯೋಚನೆಗೆ ತಂದುಕೊಳ್ಳುವವರಾಗಿದ್ದೇವೆ.",
			"22": "<sup>22</sup> ಇವರಿಬ್ಬರ ಸಂಗಡ ನಮ್ಮ ಸಹೋದರರಲ್ಲಿ ಮತ್ತೊಬ್ಬನನ್ನು ಕಳುಹಿಸಿದ್ದೇವೆ; ನಾವು ಅನೇಕ ಸಮಯಗಳಲ್ಲಿಯೂ ಅನೇಕ ಕಾರ್ಯಗಳಲ್ಲಿಯೂ ಅವನನ್ನು ಪರೀಕ್ಷಿಸಿ ಆಸಕ್ತನೆಂದು ತಿಳುಕೊಂಡಿದ್ದೇವೆ; ಈಗಲಾದರೋ ಅವನು ನಿಮ್ಮಲ್ಲಿಟ್ಟಿರುವ ವಿಶೇಷ ಭರವಸದಿಂದ ಇನ್ನೂ ಬಹು ಹೆಚ್ಚಾಗಿ ಆಸಕ್ತಿಯುಳ್ಳವನಾಗಿದ್ದಾನೆ.",
			"23": "<sup>23</sup> ತೀತನನ್ನು ಕುರಿತು ಕೇಳುತ್ತೀರೋ, ಅವನು ನನ್ನ ಪಾಲುಗಾರನೂ ನಿಮ್ಮ ಪ್ರಯೋಜನಕ್ಕಾಗಿ ನನಗೆ ಸಹಕಾರಿಯೂ ಆಗಿದ್ದಾನೆ; ಆ ಸಹೋದರರನ್ನು ಕುರಿತು ಕೇಳುತ್ತೀರೋ, ಅವರು ಸಭೆಗಳಿಂದ ಕಳುಹಿಸಲ್ಪಟ್ಟವರೂ ಕ್ರಿಸ್ತನ ಮಹಿಮೆಯನ್ನು ಪ್ರಕಾಶಪಡಿಸುವವರೂ ಆಗಿದ್ದಾರೆ.",
			"24": "<sup>24</sup> ಆದಕಾರಣ ನಿಮ್ಮ ಪ್ರೀತಿಯು ಯಥಾರ್ಥವಾದದ್ದೆಂಬದನ್ನೂ ನಿಮ್ಮನ್ನು ನಾವು ಹೊಗಳಿದ್ದು ನ್ಯಾಯವೆಂಬದನ್ನೂ ಇವರಿಗೂ ಸಭೆಗಳಿಗೂ ತೋರಿಸಿಕೊಡಿರಿ."
		},
		"9": {
			"1": "<sup>1</sup> ದೇವಜನರಿಗೋಸ್ಕರ ನಡೆಯುವ ಸಹಾಯಕಾರ್ಯವನ್ನು ಕುರಿತು ನಿಮಗೆ ಬರೆಯುವದಕ್ಕೆ ಅವಶ್ಯವಿಲ್ಲ.",
			"2": "<sup>2</sup> ನಿಮ್ಮ ಮನಸ್ಸು ಸಿದ್ಧವಾಗಿದೆ ಎಂಬದು ನನಗೆ ಗೊತ್ತುಂಟು. ಒಂದು ವರುಷದ ಹಿಂದೆ ಅಖಾಯದವರು ಸಹಾಯಮಾಡುವದಕ್ಕೆ ಸಿದ್ಧವಾಗಿದ್ದರೆಂದು ಮಕೆದೋನ್ಯದವರ ಮುಂದೆ ನಿಮ್ಮನ್ನು ಹೊಗಳುತ್ತಾ ಇದ್ದೇನೆ. ಮತ್ತು ನಿಮ್ಮ ಆಸಕ್ತಿಯು ಅವರಲ್ಲಿ ಬಹುಜನರನ್ನು ಪ್ರೇರೇಪಿಸಿತು.",
			"3": "<sup>3</sup> ಆದರೂ ನಾವು ನಿಮ್ಮನ್ನು ಹೊಗಳಿದ್ದು ನಿರಾಧಾರವಾಗದೆ ನಾನು ಹೇಳಿದ ಪ್ರಕಾರವೇ ನೀವು ಸಿದ್ಧವಾಗಿರಬೇಕೆಂದು ಆ ಸಹೋದರರನ್ನು ಕಳುಹಿಸಿದ್ದೇನೆ.",
			"4": "<sup>4</sup> ಸಿದ್ಧವಾಗದಿದ್ದರೆ ಒಂದು ವೇಳೆ ಮಕೆದೋನ್ಯದವರಲ್ಲಿ ಯಾರಾದರೂ ನನ್ನ ಸಂಗಡ ಬಂದು ನೀವು ಸಿದ್ಧವಾಗಲಿಲ್ಲವೆಂಬದನ್ನು ಕಾಣುವಾಗ ನಮಗೆ ಇಂಥ ಭರವಸವಿದ್ದದ್ದಕ್ಕೆ ನಾವು ನಾಚಿಕೆಪಡಬೇಕಾದೀತು; ನಿಮಗೂ ನಾಚಿಕೆಯಾಗುವದೆಂದು ನಾವು ಬೇರೆ ಹೇಳಬೇಕೇ.",
			"5": "<sup>5</sup> ಹೀಗಿರಲಾಗಿ ಈ ಸಹೋದರರು ಮುಂದಾಗಿ ನಿಮ್ಮ ಬಳಿಗೆ ಬಂದು ಕೊಡುತ್ತೇವೆಂದು ನೀವು ಮೊದಲು ಹೇಳಿದ ಧರ್ಮದ್ರವ್ಯವನ್ನು ಸಿದ್ಧಪಡಿಸಬೇಕೆಂಬದಾಗಿ ಇವರನ್ನು ಬೇಡಿಕೊಳ್ಳುವದು ಅವಶ್ಯವೆಂದು ನನಗೆ ತೋಚಿತು. ಹೀಗೆ ಆ ಹಣವು ಸುಲಿಗೆಯಂತೆ ಬಾರದೆ ಧರ್ಮಾರ್ಥವಾಗಿಯೇ ಸಿದ್ಧವಾಗುವದು.",
			"6": "<sup>6</sup> ಆದರೆ ಸ್ವಲ್ಪವಾಗಿ ಬಿತ್ತುವವನು ಪೈರನ್ನು ಸ್ವಲ್ಪವಾಗಿ ಕೊಯ್ಯುವನು; ಹೆಚ್ಚಾಗಿ ಬಿತ್ತುವವನು ಹೆಚ್ಚಾಗಿ ಕೊಯ್ಯುವನು ಎಂದು ತಿಳುಕೊಳ್ಳಿರಿ.",
			"7": "<sup>7</sup> ಪ್ರತಿಯೊಬ್ಬನು ತನ್ನ ತನ್ನ ಹೃದಯದಲ್ಲಿ ನಿರ್ಣಯಿಸಿಕೊಂಡ ಪ್ರಕಾರ ಕೊಡಲಿ; ದುಃಖದಿಂದಾಗಲಿ ಬಲಾತ್ಕಾರದಿಂದಾಗಲಿ ಯಾರೂ ಕೊಡಬಾರದು; ಯಾಕಂದರೆ ಸಂತೋಷವಾಗಿ ಕೊಡುವವನ ಮೇಲೆ ದೇವರಿಗೆ ಪ್ರೀತಿಯುಂಟು.",
			"8": "<sup>8</sup> ದೇವರು ಸಕಲವಿಧವಾದ ದಾನಗಳನ್ನು ನಿಮಗೆ ಧಾರಾಳವಾಗಿ ಅನುಗ್ರಹಿಸುವದಕ್ಕೆ ಶಕ್ತನಾದ್ದರಿಂದ ನೀವು ಯಾವಾಗಲೂ ಎಲ್ಲಾ ವಿಷಯಗಳಲ್ಲಿ ಪರಿಪೂರ್ಣತೆಯುಳ್ಳವರಾಗಿ ಸಕಲಸತ್ಕಾರ್ಯಗಳನ್ನು ಹೇರಳವಾಗಿ ಮಾಡುವವರಾಗಿರಬೇಕು.",
			"9": "<sup>9</sup> ಉಪಕಾರಿಯ ವಿಷಯದಲ್ಲಿ - ಅವನು ಬಡವರಿಗೆ ಧಾರಾಳವಾಗಿ ಕೊಟ್ಟನು; ಅವನ ನೀತಿಯ ಫಲವು ಸದಾಕಾಲವೂ ಇರುವದು ಎಂದು ಶಾಸ್ತ್ರದಲ್ಲಿ ಬರೆದದೆಯಲ್ಲಾ.",
			"10": "<sup>10</sup> ಬಿತ್ತುವವನಿಗೆ ಬೀಜವನ್ನೂ ಉಣ್ಣುವವನಿಗೆ ಆಹಾರವನ್ನೂ ಕೊಡುವಾತನು ನಿಮಗೂ ಬಿತ್ತುವದಕ್ಕೆ ಬೀಜವನ್ನು ಕೊಟ್ಟು ಹೆಚ್ಚಿಸಿ ನಿಮ್ಮ ಧರ್ಮಕಾರ್ಯಗಳಿಂದಾಗುವ ಫಲಗಳನ್ನು ವೃದ್ಧಿಪಡಿಸುವನು.",
			"11": "<sup>11</sup> ಹೀಗೆ ನೀವು ಎಲ್ಲಾ ವಿಷಯಗಳಲ್ಲಿ ಐಶ್ವರ್ಯವಂತರಾಗಿದ್ದು ಉದಾರವಾಗಿ ಕೊಡ ಶಕ್ತರಾಗುವಿರಿ; ಇದಲ್ಲದೆ ನಿಮ್ಮ ದಾನಗಳು ನಮ್ಮ ಕೈಯಿಂದ ಮತ್ತೊಬ್ಬರಿಗೆ ಸೇರಿದಾಗ ದೇವರಿಗೆ ಕೃತಜ್ಞತಾಸ್ತುತಿಯನ್ನುಂಟುಮಾಡುವದು.",
			"12": "<sup>12</sup> ಹೇಗಂದರೆ ನೀವು ನಡಿಸುವ ಈ ಧರ್ಮಕಾರ್ಯವು ದೇವಜನರ ಕೊರತೆಗಳನ್ನು ನೀಗುವದು ಮಾತ್ರವಲ್ಲದೆ ಅನೇಕರಿಂದ ದೇವರಿಗೆ ಕೃತಜ್ಞತಾಸ್ತುತಿಯನ್ನು ಹುಟ್ಟಿಸುವದು.",
			"13": "<sup>13</sup> ಈ ಸಹಾಯದಿಂದ ತೋರಿಬಂದ ನಿಮ್ಮ ಯೋಗ್ಯಭಾವವನ್ನು ಅವರು ನೋಡುವಾಗ ನೀವು ಮಾಡಿದ್ದ ಪ್ರತಿಜ್ಞೆಗೆ ಸರಿಯಾಗಿ ಕ್ರಿಸ್ತನ ಸುವಾರ್ತೆಯನ್ನು ಅನುಸರಿಸಿದ್ದರಿಂದಲೂ ತಮಗೂ ಎಲ್ಲರಿಗೂ ನೀವು ಉದಾರವಾಗಿ ಸಹಾಯಮಾಡಿದ್ದರಿಂದಲೂ ದೇವರನ್ನು ಕೊಂಡಾಡುವರು.",
			"14": "<sup>14</sup> ಇದಲ್ಲದೆ ನಿಮಗೋಸ್ಕರ ವಿಜ್ಞಾಪನೆಮಾಡಿ ದೇವರ ಅತಿಶಯವಾದ ಕೃಪೆಯು ನಿಮ್ಮಲ್ಲಿ ಇರುವದರಿಂದ ನಿಮ್ಮ ಪರಿಚಿತಿಮಾಡಬೇಕೆಂದು ಕೋರುವರು.",
			"15": "<sup>15</sup> ವರ್ಣಿಸಲಶಕ್ಯವಾದ ದೇವರ ವರಕ್ಕಾಗಿ ಆತನಿಗೆ ಸ್ತೋತ್ರ."
		},
		"10": {
			"1": "<sup>1</sup> ಪೌಲನು ನಮ್ಮೆದುರಿನಲ್ಲಿರುವಾಗ ದೀನನಾಗಿ ನಡಕೊಳ್ಳುವವನೂ ದೂರದಲ್ಲಿರುವಾಗ ನಮ್ಮನ್ನು ಕುರಿತು ದಿಟ್ಟತನತೋರಿಸುವವನೂ ಆಗಿದ್ದಾನೆ ಎಂದು ಹೇಳುವವರು ನಿಮ್ಮಲ್ಲಿದ್ದಾರಲ್ಲಾ. ಆ ಪೌಲನೆಂಬ ನಾನು ಕ್ರಿಸ್ತನ ಶಾಂತಮನಸ್ಸನ್ನೂ ಸಾತ್ವಿಕತ್ವವನ್ನೂ ನೆನಪಿಗೆ ತಂದುಕೊಂಡು ನಿಮಗೆ ಖಂಡಿತವಾಗಿ ಹೇಳುವದೇನಂದರೆ",
			"2": "<sup>2</sup> ಯಾರು ನಮ್ಮನ್ನು ಲೋಕಾನುಸಾರಿಗಳೆಂದು ಎಣಿಸುತ್ತಾರೋ ಅವರಿಗೆ ದಿಟ್ಟವಾದ ಧೈರ್ಯವನ್ನು ತೋರಿಸಬೇಕೆಂದು ಯೋಚಿಸುತ್ತಾ ಇದ್ದೇನೆ. ನಾವು ನಿಮ್ಮಲ್ಲಿಗೆ ಬಂದಿರುವಾಗ ಅಂಥ ದಿಟ್ಟತನವನ್ನು ತೋರಿಸುವದಕ್ಕೆ ಅವಕಾಶ ಉಂಟಾಗಬಾರದೆಂದು ನಿಮ್ಮನ್ನು ಬೇಡಿಕೊಳ್ಳುತ್ತೇನೆ.",
			"3": "<sup>3</sup> ನಾವು ಲೋಕದಲ್ಲಿದ್ದರೂ ಲೋಕಾನುಸಾರವಾಗಿ ಯುದ್ಧಮಾಡುವವರಲ್ಲ.",
			"4": "<sup>4</sup> ನಾವು ಉಪಯೋಗಿಸುವ ಆಯುಧಗಳು ಲೋಕಸಂಬಂಧವಾದ ಆಯುಧಗಳಲ್ಲ; ಅವು ದೇವರ ಎಣಿಕೆಯಲ್ಲಿ ಬಲವಾಗಿದ್ದು ಕೋಟೆಗಳನ್ನು ಕೆಡವಿಹಾಕುವಂಥವುಗಳಾಗಿವೆ.",
			"5": "<sup>5</sup> ನಾವು ವಿತರ್ಕಗಳನ್ನೂ ದೇವಜ್ಞಾನವನ್ನು ವಿರೋಧಿಸುವದಕ್ಕೆ ಏರಿಸಲ್ಪಟ್ಟಿರುವ ಉನ್ನತವಾದ ಎಲ್ಲಾ ಕೊತ್ತಲಗಳನ್ನೂ ಕೆಡವಿಹಾಕಿ ಎಲ್ಲಾ ಯೋಚನೆಗಳನ್ನು ಕ್ರಿಸ್ತನಿಗೆ ವಿಧೇಯವಾಗುವಂತೆ ಸೆರೆಹಿಡಿದು",
			"6": "<sup>6</sup> ನಿಮ್ಮ ವಿಧೇಯತ್ವವು ಪರಿಪೂರ್ಣವಾದ ಮೇಲೆ ಎಲ್ಲಾ ಅವಿಧೇಯತ್ವಕ್ಕೆ ಶಿಕ್ಷೆ ಮಾಡುವದಕ್ಕೆ ಸಿದ್ಧರಾಗಿದ್ದೇವೆ.",
			"7": "<sup>7</sup> ನೀವು ಮುಖದ ಮುಂದೆ ಇರುವಂಥದನ್ನು ನೋಡಿರಿ. ಯಾವನಾದರೂ ತನ್ನನ್ನು ಕ್ರಿಸ್ತನವನೆಂದು ನಂಬಿಕೊಂಡರೆ ಅವನು ತಿರಿಗಿ ಆಲೋಚನೆ ಮಾಡಿಕೊಂಡು ತಾನು ಹೇಗೆ ಕ್ರಿಸ್ತನವನೋ ಹಾಗೆ ನಾವೂ ಕ್ರಿಸ್ತನವರೆಂದು ತಿಳುಕೊಳ್ಳಲಿ.",
			"8": "<sup>8</sup> ನಮಗಿರುವ ಅಧಿಕಾರವನ್ನು ಕುರಿತು ನಾವು ಒಂದು ವೇಳೆ ಹೆಚ್ಚಾಗಿ ಹೊಗಳಿಕೊಂಡರೂ ಹಾಗೆ ಹೊಗಳಿಕೊಳ್ಳುವದರಲ್ಲಿ ನಾಚಿಕೆಪಡುವದಕ್ಕೆ ಕಾರಣವಾಗುವದಿಲ್ಲ. ಆದರೆ ನಿಮ್ಮನ್ನು ಕೆಡವಿಹಾಕುವದಕ್ಕಲ್ಲ ಕಟ್ಟುವದಕ್ಕಾಗಿಯೇ ಕರ್ತನು ಈ ಅಧಿಕಾರವನ್ನು ನಮಗೆ ಕೊಟ್ಟನು.",
			"9": "<sup>9</sup> ಪತ್ರಿಕೆಗಳಿಂದ ನಿಮ್ಮನ್ನು ಹೆದರಿಸುವವನಾಗಿ ಕಾಣಿಸಿಕೊಳ್ಳುವದಕ್ಕೆ ನನಗೆ ಮನಸ್ಸಿಲ್ಲ.",
			"10": "<sup>10</sup> ಅವನಿಂದ ಬಂದ ಪತ್ರಿಕೆಗಳು ಗೌರವವಾದವುಗಳೂ ಬಲವುಳ್ಳವುಗಳೂ ಆಗಿವೆ; ಅವನು ಸಾಕ್ಷಾತ್ತಾಗಿ ಬಂದರೆ ಅವನು ನಿರ್ಬಲನು, ಅವನ ಮಾತು ಗಣನೆಗೆ ಬಾರದ್ದು ಅನ್ನುತ್ತಾರೆ.",
			"11": "<sup>11</sup> ಅಂಥವರು - ನಾವು ದೂರದಲ್ಲಿರುವಾಗ ಪತ್ರಿಕೆಗಳ ಮೂಲಕ ಮಾತಿನಲ್ಲಿ ಎಂಥವರಾಗಿದ್ದೇವೋ ಹತ್ತಿರದಲ್ಲಿರುವಾಗ ಕಾರ್ಯದಲ್ಲಿಯೂ ಅಂಥವರಾಗಿಯೇ ಇದ್ದೇವೆಂದು ತಿಳುಕೊಳ್ಳಲಿ.",
			"12": "<sup>12</sup> ತಮ್ಮನ್ನು ತಾವೇ ಹೊಗಳಿಕೊಳ್ಳುವ ಕೆಲವರಲ್ಲಿ ನಮ್ಮನ್ನು ಸೇರಿಸಿಕೊಳ್ಳುವದಕ್ಕಾಗಲಿ ಅವರಿಗೆ ಹೋಲಿಸಿಕೊಳ್ಳುವದಕ್ಕಾಗಲಿ ನಮಗೆ ಧೈರ್ಯಸಾಲದು; ಅವರಂತೂ ತಮ್ಮ ತಮ್ಮೊಳಗೆ ತಮ್ಮನ್ನು ಅಳತೆಮಾಡಿಕೊಂಡು ತಮತಮಗೆ ಹೋಲಿಸಿಕೊಂಡು ವಿವೇಕವಿಲ್ಲದವರಾಗಿದ್ದಾರೆ.",
			"13": "<sup>13</sup> ನಾವಾದರೋ ಮೇರೆದಪ್ಪಿ ಹೊಗಳಿಕೊಳ್ಳದೆ ದೇವರು ನಮಗೆ ನೇಮಿಸಿದಂಥ ಮೇರೆಯೊಳಗಿದ್ದು ಹೊಗಳಿಕೊಳ್ಳುತ್ತೇವೆ. ಈ ಮೇರೆಯೊಳಗಿದ್ದು ನಿಮ್ಮ ಪರ್ಯಂತರಕ್ಕೂ ಬಂದಿದ್ದೇವೆ.",
			"14": "<sup>14</sup> ನಾವು ಕ್ರಿಸ್ತನ ಸುವಾರ್ತೆಯನ್ನು ಸಾರುತ್ತಾ ಇತರರಿಗಿಂತ ಮೊದಲು ನಿಮ್ಮ ಬಳಿಗೆ ಬಂದದರಿಂದ ನಿಮ್ಮ ಮೇಲೆ ಅಧಿಕಾರವಿಲ್ಲದವರಂತೆ ಮೇರೆಯನ್ನು ಅತಿಕ್ರಮಿಸಿದವರಲ್ಲ.",
			"15": "<sup>15</sup> ನಾವು ಮೇರೆದಪ್ಪಿ ಮತ್ತೊಬ್ಬರ ಪ್ರಯಾಸದ ಫಲಗಳನ್ನು ತಕ್ಕೊಂಡು ಇವು ನಮ್ಮವು ಎಂದು ಹಿಗ್ಗುವವರಲ್ಲ; ಆದರೆ ನಿಮ್ಮ ನಂಬಿಕೆಯು ಹೆಚ್ಚಿದ ಹಾಗೆಲ್ಲಾ ನಿಮ್ಮ ಮೂಲಕ ನಮ್ಮ ಮೇರೆಯೊಳಗೆ ಇನ್ನೂ ಅಭಿವೃದ್ಧಿಹೊಂದಿ",
			"16": "<sup>16</sup> ನಿಮಗೆ ಆಚೆ ಇರುವ ಸೀಮೆಗಳಲ್ಲಿ ಸುವಾರ್ತೆಯನ್ನು ಸಾರುವೆವೆಂಬ ನಿರೀಕ್ಷೆ ನಮಗುಂಟು. ಮತ್ತೊಬ್ಬರ ಮೇರೆಯಲ್ಲಿ ಸಿದ್ಧವಾಗಿ ನಮಗೆ ಸಿಕ್ಕಿದ ಫಲವನ್ನು ಕುರಿತು ನಾವು ಹೆಚ್ಚಳಪಡುವದಿಲ್ಲ.",
			"17": "<sup>17</sup> ಹೆಚ್ಚಳಪಡುವವನು ಕರ್ತನಲ್ಲಿಯೇ ಹೆಚ್ಚಳಪಡಲಿ.",
			"18": "<sup>18</sup> ತನ್ನನ್ನು ತಾನೇ ಹೊಗಳಿಕೊಳ್ಳುವವನು ಯೋಗ್ಯನಲ್ಲ; ಕರ್ತನು ಯಾರನ್ನು ಹೊಗಳುತ್ತಾನೋ ಅವನೇ ಯೋಗ್ಯನು."
		},
		"11": {
			"1": "<sup>1</sup> ನಾನು ನನ್ನನ್ನು ಹೊಗಳಿಕೊಳ್ಳುವದರಲ್ಲಿ ಸ್ವಲ್ಪ ಬುದ್ಧಿಹೀನತೆಯಿಂದ ಮಾತಾಡುತ್ತೇನೆ; ನೀವು ಅದನ್ನು ಸಹಿಸಿಕೊಳ್ಳುತ್ತೀರೇನೋ. ನನ್ನನ್ನು ಸಹಿಸಿಕೊಳ್ಳಬೇಕೆಂದು ನಿಮ್ಮನ್ನು ಬೇಡಿಕೊಳ್ಳುತ್ತೇನೆ.",
			"2": "<sup>2</sup> ಯಾಕಂದರೆ ದೇವರಲ್ಲಿರುವಂಥ ಚಿಂತೆಯಿಂದಲೇ ನಾನು ನಿಮ್ಮ ವಿಷಯದಲ್ಲಿ ಚಿಂತಿಸುತ್ತೇನೆ. ನಿಮ್ಮನ್ನು ಕ್ರಿಸ್ತನೆಂಬ ಒಬ್ಬನೇ ಪುರುಷನಿಗೆ ಶುದ್ಧ ಕನ್ಯೆಯಂತೆ ಒಪ್ಪಿಸಬೇಕೆಂದು ನಿಮ್ಮನ್ನು ಆತನಿಗೆ ನಿಶ್ಚಯಮಾಡಿದೆನಲ್ಲಾ.",
			"3": "<sup>3</sup> ಆದರೆ ಹೇಗೆ ಹವ್ವಳು ಸರ್ಪದ ಕುಯುಕ್ತಿಗೆ ಒಳಬಿದ್ದು ಮೋಸಹೋದಳೋ ಹಾಗೆಯೇ ನಿಮ್ಮ ಮನಸ್ಸು ಕ್ರಿಸ್ತನ ವಿಷಯದಲ್ಲಿರಬೇಕಾದ ಯಥಾರ್ಥತ್ವವನ್ನೂ ಪಾತಿವ್ರತ್ಯವನ್ನೂ ಬಿಟ್ಟು ಕೆಟ್ಟುಹೋದೀತೆಂದು ನನಗೆ ಭಯವುಂಟು.",
			"4": "<sup>4</sup> ಯಾಕಂದರೆ ನಾವು ಪ್ರಸಿದ್ಧಿಪಡಿಸದಿದ್ದ ಮತ್ತೊಬ್ಬ ಯೇಸುವನ್ನು ನಿಮ್ಮ ಬಳಿಗೆ ಬರುವವರು ಪ್ರಕಟಿಸುವಾಗಲೂ ನೀವು ಹೊಂದದೆ ಇದ್ದ ಬೇರೆ ವಿಧವಾದ ಆತ್ಮಪ್ರೇರಣೆಯನ್ನು ಹೊಂದುವಾಗಲೂ ಅವಲಂಬಿಸದೆಯಿದ್ದ ಬೇರೊಂದು ಸುವಾರ್ತೆಯನ್ನು ಕೇಳುವಾಗಲೂ ನೀವು ಸಹಿಸಿಕೊಳ್ಳುತ್ತಿರುವದು ಬಹು ಆಶ್ಚರ್ಯ.",
			"5": "<sup>5</sup> ಅತಿಶ್ರೇಷ್ಠರಾದ ಅಪೊಸ್ತಲರು ಅನ್ನಿಸಿಕೊಳ್ಳುವ ಅವರಿಗಿಂತ ನಾನು ಒಂದರಲ್ಲಾದರೂ ಕಡಿಮೆಯಾದವನಲ್ಲವೆಂದು ಎಣಿಸುತ್ತೇನೆ.",
			"6": "<sup>6</sup> ನಾನು ವಾಕ್ಚಾತುರ್ಯದಲ್ಲಿ ನಿಪುಣನಲ್ಲದಿದ್ದರೂ ಜ್ಞಾನದಲ್ಲಿ ನಿಪುಣನಾಗಿದ್ದೇನೆ; ಇದನ್ನು ಎಲ್ಲಾ ಕಾರ್ಯಗಳಲ್ಲಿ ಎಲ್ಲರ ಮುಂದೆ ನಿಮ್ಮ ಪ್ರಯೋಜನಕ್ಕಾಗಿ ತೋರ್ಪಡಿಸಿದ್ದೇವೆ.",
			"7": "<sup>7</sup> ಏನು, ನೀವು ಅಭಿವೃದ್ಧಿಹೊಂದಬೇಕೆಂದು ನನ್ನನ್ನು ನಾನೇ ತಗ್ಗಿಸಿಕೊಂಡು ದೇವರ ಸುವಾರ್ತೆಯನ್ನು ನಿಮಗೆ ಉಚಿತಾರ್ಥವಾಗಿ ಸಾರಿದ್ದು ಪಾಪಕೃತ್ಯವೋ?",
			"8": "<sup>8</sup> ನಿಮ್ಮ ಸೇವೆಮಾಡುವದಕ್ಕೋಸ್ಕರ ನಾನು ಇತರ ಸಭೆಗಳಿಂದ ಸಂಬಳ ತೆಗೆದುಕೊಂಡು ಅವುಗಳನ್ನು ಸುಲುಕೊಂಡೆನು.",
			"9": "<sup>9</sup> ನಾನು ನಿಮ್ಮಲ್ಲಿದ್ದಾಗ ಖರ್ಚಿಗೆ ಏನೂ ಇಲ್ಲದ ಸಮಯದಲ್ಲಿ ಯಾರ ಮೇಲೆಯೂ ಭಾರ ಹಾಕಲಿಲ್ಲ; ಮಕೆದೋನ್ಯದಿಂದ ಬಂದ ಸಹೋದರರು ನನಗೆ ಬೇಕಾದದ್ದೆಲ್ಲವನ್ನೂ ಕೊಟ್ಟರು. ನಾನು ನಿಮಗೆ ಯಾವದರಲ್ಲಿಯೂ ಭಾರವಾಗಿರಬಾರದೆಂದು ನೋಡಿಕೊಳ್ಳುತ್ತಿದ್ದೆನು, ಇನ್ನು ಮೇಲೆಯೂ ನೋಡಿಕೊಳ್ಳುವೆನು.",
			"10": "<sup>10</sup> ಈ ನನ್ನ ಹೊಗಳಿಕೆಯನ್ನು ಅಖಾಯದ ಪ್ರಾಂತ್ಯಗಳಲ್ಲಿ ಒಬ್ಬರೂ ನಿಲ್ಲಿಸಲಾರರೆಂದು ಕ್ರಿಸ್ತನ ಮುಂದೆ ಸತ್ಯವಾಗಿ ಹೇಳುತ್ತೇನೆ.",
			"11": "<sup>11</sup> ಯಾಕೆ ನಿಮ್ಮಿಂದ ಏನೂ ತೆಗೆದುಕೊಳ್ಳುವದಿಲ್ಲ? ನಿಮ್ಮ ಮೇಲೆ ಪ್ರೀತಿ ಇಲ್ಲದ್ದರಿಂದಲೋ? ದೇವರೇ ಬಲ್ಲ.",
			"12": "<sup>12</sup> ನನ್ನನ್ನು ನಿಂದಿಸುವದಕ್ಕೆ ಆಸ್ಪದವನ್ನು ಹುಡುಕುವವರಿಗೆ ಯಾವ ಆಸ್ಪದವೂ ಸಿಕ್ಕದಂತೆ ನಾನು ಮಾಡುವದನ್ನು ಇನ್ನು ಮುಂದೆಯೂ ಮಾಡುವೆನು. ಅವರು ಯಾವದರಲ್ಲಿ ಹಿಗ್ಗುತ್ತಾರೋ ಅದರಲ್ಲಿ ನಮ್ಮಂತೆಯೇ ನಡೆಯುವವರಾಗಿ ಕಂಡು ಬರಲಿ.",
			"13": "<sup>13</sup> ಆದರೆ ಅಂಥವರು ಸುಳ್ಳು ಅಪೊಸ್ತಲರೂ ಮೋಸಗಾರರಾದ ಕೆಲಸದವರೂ ಕ್ರಿಸ್ತನ ಅಪೊಸ್ತಲರಾಗಿ ಕಾಣಿಸುವದಕ್ಕೆ ವೇಷ ಹಾಕಿಕೊಳ್ಳುವವರೂ ಆಗಿದ್ದಾರೆ.",
			"14": "<sup>14</sup> ಇದೇನೂ ಆಶ್ಚರ್ಯವಲ್ಲ; ಸೈತಾನನು ತಾನೇ ಪ್ರಕಾಶರೂಪವುಳ್ಳ ದೇವದೂತನ ವೇಷವನ್ನು ಹಾಕಿಕೊಳ್ಳುವಾಗ",
			"15": "<sup>15</sup> ಅವನ ಸೇವಕರು ಸಹ ನೀತಿಗೆ ಸೇವಕರಾಗಿ ಕಾಣಿಸುವದಕ್ಕೆ ವೇಷಹಾಕಿಕೊಳ್ಳುವದು ದೊಡ್ಡದಲ್ಲ. ಅವರ ಅಂತ್ಯಾವಸ್ಥೆಯು ಅವರ ಕೃತ್ಯಗಳಿಗೆ ತಕ್ಕ ಹಾಗೆಯೇ ಆಗುವದು.",
			"16": "<sup>16</sup> ನನ್ನನ್ನು ಬುದ್ಧಿಹೀನನೆಂತ ಯಾರೂ ನೆನಸಬಾರದೆಂದು ತಿರಿಗಿ ಹೇಳುತ್ತೇನೆ; ಹಾಗೆ ನೆನಸಿದರೂ ನನ್ನನ್ನು ಬುದ್ಧಿಹೀನನಾಗಿಯಾದರೂ ಸೇರಿಸಿಕೊಳ್ಳಿರಿ; ಯಾಕಂದರೆ ನಾನು ಸಹ ಅಲ್ಪಸ್ವಲ್ಪ ಹೊಗಳಿಕೊಳ್ಳಬೇಕೆಂದಿದ್ದೇನೆ.",
			"17": "<sup>17</sup> ನಾನು ಈಗ ಆಡುವ ಮಾತುಗಳನ್ನು ಕರ್ತನನ್ನು ಅನುಸರಿಸುವವನಾಗಿ ಆಡದೆ ಭರವಸದಿಂದ ತನ್ನನ್ನು ಹೊಗಳಿಕೊಳ್ಳುವ ಬುದ್ಧಿಹೀನನಂತೆ ಆಡುತ್ತೇನೆ.",
			"18": "<sup>18</sup> ಅನೇಕರು ಲೋಕಸಂಬಂಧವಾದ ಕಾರ್ಯಗಳಲ್ಲಿ ಹೊಗಳಿಕೊಳ್ಳುವದರಿಂದ ನಾನೂ ಹೊಗಳಿಕೊಳ್ಳುತ್ತೇನೆ.",
			"19": "<sup>19</sup> ನೀವು ಬುದ್ಧಿವಂತರಾಗಿದ್ದು ಬುದ್ಧಿಹೀನರನ್ನು ಸಂತೋಷದಿಂದ ಸಹಿಸಿಕೊಳ್ಳುತ್ತೀರಲ್ಲಾ.",
			"20": "<sup>20</sup> ಒಬ್ಬನು ನಿಮ್ಮನ್ನು ತನಗೆ ವಶಮಾಡಿಕೊಂಡರೂ ಒಬ್ಬನು ನಿಮ್ಮನ್ನು ನುಂಗಿಬಿಟ್ಟರೂ ಒಬ್ಬನು ನಿಮ್ಮನ್ನು ಮರಳುಗೊಳಿಸಿ ಹಿಡಿದರೂ ಒಬ್ಬನು ತನ್ನನ್ನು ಹೆಚ್ಚಿಸಿಕೊಂಡರೂ ಒಬ್ಬನು ನಿಮ್ಮ ಮುಖದ ಮೇಲೆ ಹೊಡೆದರೂ ಸಹಿಸಿಕೊಳ್ಳುತ್ತೀರಲ್ಲಾ.",
			"21": "<sup>21</sup> ನಾವು ಬಲವಿಲ್ಲದವರಾಗಿದ್ದೆವೆಂಬಂತೆ ಇದನ್ನು ನನ್ನ ಅವಮಾನಕ್ಕೆ ಹೇಳಿದ್ದರೂ ಯಾವ ಆಧಾರದಿಂದ ಯಾವನಾದರೂ ಧೈರ್ಯವಾಗಿ ನಡಕೊಳ್ಳುತ್ತಾನೋ ಅದರಿಂದಲೇ ನಾನೂ ಧೈರ್ಯವುಳ್ಳವನಾಗಿದ್ದೇನೆ.",
			"22": "<sup>22</sup> ಇದನ್ನು ಬುದ್ಧಿಹೀನತೆಯಿಂದ ಹೇಳುತ್ತೇನೆ. ಅವರು ಇಬ್ರಿಯರೋ ನಾನೂ ಇಬ್ರಿಯನು; ಅವರು ಇಸ್ರಾಯೇಲ್ಯರೋ ನಾನೂ ಇಸ್ರಾಯೇಲ್ಯನು; ಅವರು ಅಬ್ರಹಾಮನ ವಂಶದವರೋ ನಾನೂ ಅದೇ ವಂಶದವನು;",
			"23": "<sup>23</sup> ಅವರು ಕ್ರಿಸ್ತನ ಸೇವಕರೋ ಅವರಿಗಿಂತ ನಾನು ಹೆಚ್ಚಾಗಿ ಸೇವೆಮಾಡುವವನಾಗಿದ್ದೇನೆ. ಬುದ್ಧಿಸ್ವಾಧೀನವಿಲ್ಲದವನಾಗಿ ಮಾತಾಡುತ್ತೇನೆ. ಆತನ ಸೇವೆಯಲ್ಲಿ ಅವರಿಗಿಂತ ಹೆಚ್ಚಾಗಿ ಪ್ರಯಾಸಪಟ್ಟೆನು, ಹೆಚ್ಚಾಗಿ ಸೆರೆಮನೆಗಳೊಳಗೆ ಬಿದ್ದೆನು; ಮಿತಿಮೀರಿ ಪೆಟ್ಟುಗಳನ್ನು ತಿಂದೆನು, ಅನೇಕಸಾರಿ ಮರಣದ ಬಾಯೊಳಗೆ ಸಿಕ್ಕಿಕೊಂಡೆನು.",
			"24": "<sup>24</sup> ಐದುಸಾರಿ ಯೆಹೂದ್ಯರಿಂದ ನನಗೆ ಒಂದು ಕಡಿಮೆ ನಾಲ್ವತ್ತು ಏಟುಗಳು ಬಿದ್ದವು;",
			"25": "<sup>25</sup> ಮೂರು ಸಾರಿ ಸರಕಾರದವರು ಚಡಿಗಳಿಂದ ನನ್ನನ್ನು ಹೊಡಿಸಿದರು; ಒಂದು ಸಾರಿ ಜನರು ನನ್ನನ್ನು ಕೊಲ್ಲುವದಕ್ಕೆ ಕಲ್ಲೆಸೆದರು; ಮೂರು ಸಾರಿ ನಾನಿದ್ದ ಹಡಗವು ಒಡೆದು ಹೋಯಿತು; ಒಂದು ರಾತ್ರಿ ಒಂದು ಹಗಲು ಸಮುದ್ರದ ನೀರಿನಲ್ಲಿ ಕಳೆದೆನು.",
			"26": "<sup>26</sup> ಆತನ ಸೇವೆಯಲ್ಲಿ ಎಷ್ಟೋ ಪ್ರಯಾಣಗಳನ್ನು ಮಾಡಿದೆನು; ನದಿಗಳ ಅಪಾಯಗಳೂ ಕಳ್ಳರ ಅಪಾಯಗಳೂ ಸ್ವಂತ ಜನರಿಂದ ಅಪಾಯಗಳೂ ಅನ್ಯಜನರಿಂದ ಅಪಾಯಗಳೂ ಪಟ್ಟಣದಲ್ಲಿ ಅಪಾಯಗಳೂ ಕಾಡಿನಲ್ಲಿ ಅಪಾಯಗಳೂ ಸಮುದ್ರದಲ್ಲಿ ಅಪಾಯಗಳೂ ಸುಳ್ಳುಸಹೋದರರೊಳಗೆ ಇರುವಾಗ ಅಪಾಯಗಳೂ ನನಗೆ ಸಂಭವಿಸಿದವು.",
			"27": "<sup>27</sup> ಪ್ರಯಾಸ ಪರಿಶ್ರಮಗಳಿಂದ ಕೆಲಸನಡಿಸಿ ಅನೇಕ ಸಾರಿ ನಿದ್ದೆಗೆಟ್ಟು ಹಸಿವೆ ಬಾಯಾರಿಕೆಗಳನ್ನು ಪಟ್ಟು ಅನೇಕ ಸಾರಿ ಉಪವಾಸವಾಗಿಯೂ ಚಳಿಯಲ್ಲಿಯೂ ವಸ್ತ್ರವಿಲ್ಲದೆಯೂ ಇದ್ದು ಆತನನ್ನು ಸೇವಿಸಿದ್ದೇನೆ.",
			"28": "<sup>28</sup> ಇನ್ನೂ ಬೇರೆ ಸಂಗತಿಗಳಲ್ಲದೆ ಎಲ್ಲಾ ಸಭೆಗಳ ವಿಷಯವಾದ ಚಿಂತೆಯು ದಿನದಿನ ನನ್ನನ್ನು ಪೀಡಿಸುತ್ತದೆ.",
			"29": "<sup>29</sup> ಯಾವನಾದರೂ ಬಲವಿಲ್ಲದವನಾದರೆ ನಾನು ಅವನೊಂದಿಗೆ ಬಲವಿಲ್ಲದವನಾಗದೆ ಇರುವೆನೋ? ಯಾವನಾದರೂ ಪಾಪದಲ್ಲಿ ಸಿಕ್ಕಿಕೊಂಡರೆ ನಾನು ತಾಪಪಡುವದಿಲ್ಲವೋ?",
			"30": "<sup>30</sup> ನಾನು ಹೊಗಳಿಕೊಳ್ಳಬೇಕಾದರೆ ನನ್ನ ಬಲಹೀನತೆಯನ್ನು ತೋರ್ಪಡಿಸುವ ವಿಷಯಗಳಲ್ಲಿ ಹೊಗಳಿಕೊಳ್ಳುವೆನು.",
			"31": "<sup>31</sup> ನಾನು ಸುಳ್ಳಾಡುವದಿಲ್ಲವೆಂದು ಕರ್ತನಾದ ಯೇಸುವಿನ ತಂದೆಯೂ ನಿರಂತರ ಸ್ತುತಿಹೊಂದತಕ್ಕವನೂ ಆಗಿರುವ ದೇವರೇ ಬಲ್ಲನು.",
			"32": "<sup>32</sup> ದಮಸ್ಕದಲ್ಲಿ ಅರಸನಾದ ಅರೇತನ ಅಧೀನದಲ್ಲಿದ್ದ ಅಧಿಪತಿಯು ನನ್ನನ್ನು ಹಿಡಿಯಬೇಕೆಂದು ದಮಸ್ಕದವರ ಪಟ್ಟಣವನ್ನು ಕಾಯುತ್ತಿರಲು",
			"33": "<sup>33</sup> ನಾನು ಒಂದು ಕಲ್ಲಿಯಲ್ಲಿ ಕೂತು ಗೋಡೆಯಲ್ಲಿದ್ದ ಕಿಟಕಿಯೊಳಗಿಂದ ಇಳಿಸಲ್ಪಟ್ಟು ಅವನ ಕೈಯಿಂದ ತಪ್ಪಿಸಿಕೊಂಡೆನು."
		},
		"12": {
			"1": "<sup>1</sup> ಹೊಗಳಿಕೊಳ್ಳುವದು ವಿಹಿತವಲ್ಲದಿದ್ದರೂ ಅದು ನನಗೆ ಅವಶ್ಯವಾಗಿದೆ. ಆದದರಿಂದ ಕರ್ತನು ನನಗೆ ದಯಪಾಲಿಸಿದ ದರ್ಶನಗಳನ್ನು ಕುರಿತೂ ತಿಳಿಯಪಡಿಸಿದ ರಹಸ್ಯಗಳನ್ನು ಕುರಿತೂ ಹೇಳುತ್ತೇನೆ.",
			"2": "<sup>2</sup> ಕ್ರಿಸ್ತನಲ್ಲಿದ್ದ ಒಬ್ಬ ಮನುಷ್ಯನನ್ನು ಬಲ್ಲೆನು, ಅವನು ಹದಿನಾಲ್ಕು ವರುಷಗಳ ಹಿಂದೆ ಮೂರನೆಯ ಆಕಾಶದ ಪರಿಯಂತರಕ್ಕೂ ಒಯ್ಯಲ್ಪಟ್ಟನು. ಅವನು ದೇಹಸಹಿತನಾಗಿ ಒಯ್ಯಲ್ಪಟ್ಟನೋ ದೇಹರಹಿತನಾಗಿ ಒಯ್ಯಲ್ಪಟ್ಟನೋ ನಾನರಿಯೆ, ದೇವರೇ ಬಲ್ಲನು.",
			"3": "<sup>3</sup> ಅಂಥ ಮನುಷ್ಯನು ಪರದೈಸಕ್ಕೆ ಒಯ್ಯಲ್ಪಟ್ಟು ಮನುಷ್ಯರು ನುಡಿಯಲಶಕ್ಯವಾದ ಹೇಳಬಾರದ ಮಾತುಗಳನ್ನು ಕೇಳಿದನೆಂದು ಬಲ್ಲೆನು; ಆ ಸಮಯದಲ್ಲಿ ಅವನು ದೇಹ ಸಹಿತನಾಗಿದ್ದನೋ ದೇಹರಹಿತನಾಗಿದ್ದನೋ ನಾನರಿಯೆ, ದೇವರೇ ಬಲ್ಲನು.",
			"5": "<sup>5</sup> ಅಂಥವನನ್ನು ಕುರಿತು ಹೆಚ್ಚಳಪಡುವೆನು, ನನ್ನನ್ನು ಕುರಿತಾದರೋ ನನ್ನ ನಿರ್ಬಲಾವಸ್ಥೆಗಳಲ್ಲಿಯೇ ಹೊರತು ಬೇರೆ ಹೆಚ್ಚಳಪಡುವದಿಲ್ಲ.",
			"6": "<sup>6</sup> ಹೊಗಳಿಕೊಳ್ಳುವದಕ್ಕೆ ನನಗೆ ಒಂದು ವೇಳೆ ಮನಸ್ಸಿದ್ದರೂ ಹಾಗೆ ಹೊಗಳಿಕೊಳ್ಳುವದರಲ್ಲಿ ನಾನು ಬುದ್ಧಿಹೀನನಾಗುವದಿಲ್ಲ. ಯಾಕಂದರೆ ಸತ್ಯವನ್ನೇ ಹೇಳುತ್ತಿರುವೆನು. ಆದರೂ ಯಾವನೂ ನನ್ನಲ್ಲಿ ಕಾಣುವದಕ್ಕಿಂತಲೂ ನನ್ನಿಂದ ಕೇಳುವದಕ್ಕಿಂತಲೂ ಹೆಚ್ಚಾಗಿ ನನ್ನನ್ನು ಕುರಿತು ಎಣಿಸಬಾರದು.",
			"7": "<sup>7</sup> ಈ ಕಾರಣದಿಂದಲೂ ನನಗೆ ತಿಳಿಸಲ್ಪಟ್ಟ ರಹಸ್ಯಗಳು ಬಹು ವಿಶೇಷವಾಗಿರುವದರಿಂದಲೂ ನಾನು ಹೊಗಳಿಕೊಳ್ಳದೆ ಸುಮ್ಮನಿರುತ್ತೇನೆ. ನಾನು ನನ್ನನ್ನು ಅತಿಶಯವಾಗಿ ಹೆಚ್ಚಿಸಿಕೊಳ್ಳಬಾರದೆಂದು ಒಂದು ಶೂಲ ನನ್ನ ಶರೀರದಲ್ಲಿ ನಾಟಿದೆಯೋ ಎಂಬಂತೆ ನನ್ನನ್ನು ಗುದ್ದುವದಕ್ಕೆ ಸೈತಾನನ ದೂತರಲ್ಲಿ ಒಬ್ಬನು ನನ್ನ ಬಳಿಗೆ ಕಳುಹಿಸಲ್ಪಟ್ಟನು; ನಾನು ಅತಿಶಯವಾಗಿ ಹೆಚ್ಚಿಸಿಕೊಳ್ಳಬಾರದೆಂತಲೇ ಇದಾಯಿತು.",
			"8": "<sup>8</sup> ಈ ಪೀಡೆಯ ವಿಷಯದಲ್ಲಿ ಅದು ನನ್ನನ್ನು ಬಿಟ್ಟುಹೋಗಬೇಕೆಂದು ಮೂರು ಸಾರಿ ಕರ್ತನನ್ನು ಬೇಡಿಕೊಂಡೆನು.",
			"9": "<sup>9</sup> ಅದಕ್ಕಾತನು - ನನ್ನ ಕೃಪೆಯೇ ನಿನಗೆ ಸಾಕು; ಬಲಹೀನತೆಯಲ್ಲಿಯೇ ಬಲವು ಪೂರ್ಣಸಾಧಕವಾಗುತ್ತದೆ ಎಂದು ನನಗೆ ಹೇಳಿದ್ದಾನೆ. ಹೀಗಿರಲಾಗಿ ಕ್ರಿಸ್ತನ ಬಲವು ನನ್ನಲ್ಲಿ ನೆಲಸಿಕೊಂಡಿರಬೇಕೆಂದು ನನಗುಂಟಾಗುವ ನಿರ್ಬಲಾವಸ್ಥೆಗಳ ವಿಷಯದಲ್ಲಿಯೇ ಬಹುಸಂತೋಷವಾಗಿ ಹೆಚ್ಚಳಪಡುವೆನು.",
			"10": "<sup>10</sup> ಆದದರಿಂದ ಕ್ರಿಸ್ತನ ನಿಮಿತ್ತ ನನಗೆ ನಿರ್ಬಲಾವಸ್ಥೆಯೂ ತಿರಸ್ಕಾರವೂ ಕೊರತೆಯೂ ಹಿಂಸೆಯೂ ಇಕ್ಕಟ್ಟೂ ಸಂಭವಿಸಿದಾಗ ಸಂತುಷ್ಟನಾಗಿದ್ದೇನೆ. ನಾನು ಯಾವಾಗ ನಿರ್ಬಲನಾಗಿದ್ದೇನೋ ಆವಾಗಲೇ ಬಲವುಳ್ಳವನಾಗಿದ್ದೇನೆ.",
			"11": "<sup>11</sup> ನಾನು ಹೀಗೆ ಬರೆದು ಬುದ್ಧಿಹೀನನಾದೆನು; ಅದಕ್ಕೆ ನೀವೇ ನನ್ನನ್ನು ಬಲವಂತ ಮಾಡಿದಿರಿ. ನಿಮ್ಮಿಂದಲೇ ನನಗೆ ಹೊಗಳಿಕೆಯು ಉಂಟಾಗಬೇಕಾಗಿತ್ತು; ಯಾಕಂದರೆ ನಾನು ಕೇವಲ ಅಲ್ಪನಾದರೂ ಅತಿ ಶ್ರೇಷ್ಠರಾದ ಅಪೊಸ್ತಲರು ಅನ್ನಿಸಿಕೊಳ್ಳುವ ಆ ಜನರಿಗಿಂತ ಒಂದರಲ್ಲಿಯಾದರೂ ಕಡಿಮೆಯಾಗಲಿಲ್ಲ.",
			"12": "<sup>12</sup> ನಾನು ಹಿಂಸೆಯನ್ನು ಸ್ಥಿರಚಿತ್ತದಿಂದ ತಾಳಿಕೊಳ್ಳುವದರಲ್ಲಿಯೂ ಸೂಚಕಕಾರ್ಯಗಳನ್ನೂ ಅದ್ಭುತಕಾರ್ಯಗಳನ್ನೂ ಮಹತ್ಕಾರ್ಯಗಳನ್ನೂ ನಡಿಸಿದ್ದರಲ್ಲಿಯೂ ಅಪೊಸ್ತಲನಿಗೆ ಇರತಕ್ಕ ಲಕ್ಷಣಗಳು ನಿಮ್ಮ ಮಧ್ಯದಲ್ಲಿ ತೋರಿದವು.",
			"13": "<sup>13</sup> ನನ್ನನ್ನು ಸಂರಕ್ಷಿಸುವ ಭಾರವನ್ನು ನಾನು ನಿಮ್ಮ ಮೇಲೆ ಹಾಕಲಿಲ್ಲವೆಂಬುವ ಒಂದೇ ವಿಷಯದಲ್ಲಿ ಹೊರತು ಇನ್ನಾವ ವಿಷಯದಲ್ಲಿಯೂ ನಿಮ್ಮನ್ನು ಮಿಕ್ಕಾದ ಸಭೆಗಳವರಿಗಿಂತ ಕಡಿಮೆ ಮಾಡಲಿಲ್ಲವಲ್ಲಾ. ನಾನು ಮಾಡಿದ ಈ ಅನ್ಯಾಯವನ್ನು ಕ್ಷಮಿಸಿರಿ.",
			"14": "<sup>14</sup> ಇಗೋ, ನಾನು ನಿಮ್ಮ ಬಳಿಗೆ ಬರುವದಕ್ಕೆ ಸಿದ್ಧವಾಗಿರುವದು ಇದು ಮೂರನೆಯ ಸಾರಿ; ಮತ್ತು ನಿಮಗೆ ಭಾರವಾಗಿರುವದಿಲ್ಲ; ನಾನು ನಿಮ್ಮ ಸೊತ್ತನ್ನು ಆಶಿಸದೆ ನಿಮ್ಮನ್ನೇ ಆಶಿಸುತ್ತೇನೆ. ಮಕ್ಕಳು ತಂದೆತಾಯಿಗಳಿಗೋಸ್ಕರ ದ್ರವ್ಯವನ್ನು ಕೂಡಿಸಿಡುವದು ಧರ್ಮವಲ್ಲ, ತಂದೆತಾಯಿಗಳು ಮಕ್ಕಳಿಗೋಸ್ಕರ ಕೂಡಿಸಿಡುವದೇ ಧರ್ಮ.",
			"15": "<sup>15</sup> ನಾನಂತೂ ನನಗಿರುವದನ್ನು ನಿಮ್ಮ ಆತ್ಮಸಂರಕ್ಷಣೆಗೋಸ್ಕರ ಅತಿಸಂತೋಷದಿಂದ ವೆಚ್ಚಮಾಡುತ್ತೇನೆ, ನನ್ನನ್ನೇ ವೆಚ್ಚಮಾಡಿಕೊಳ್ಳುತ್ತೇನೆ. ನಾನು ನಿಮ್ಮನ್ನು ಹೆಚ್ಚಾಗಿ ಪ್ರೀತಿಸಿದರೆ ನೀವು ನನ್ನನ್ನು ಕಡಿಮೆಯಾಗಿ ಪ್ರೀತಿಸುತ್ತೀರೋ?",
			"16": "<sup>16</sup> ಒಂದು ವೇಳೆ ನೀವು - ಅದಿರಲಿ, ತಾನು ನಮಗೆ ಭಾರವಾಗದೆ ಹೋದರೂ ಯುಕ್ತಿಯುಳ್ಳವನಾಗಿದ್ದು ನಮ್ಮನ್ನು ಉಪಾಯದಿಂದ ಸುಲುಕೊಂಡನೆಂದು ಹೇಳೀರಿ.",
			"17": "<sup>17</sup> ನಾನು ನಿಮ್ಮ ಬಳಿಗೆ ಕಳುಹಿಸಿದವರಲ್ಲಿ ಯಾವನ ಮೂಲಕವಾಗಿಯಾಗಲಿ ನಿಮ್ಮನ್ನು ವಂಚಿಸಿ ಏನಾದರೂ ತೆಗೆದುಕೊಂಡೆನೋ?",
			"18": "<sup>18</sup> ನಿಮ್ಮ ಬಳಿಗೆ ಹೋಗುವದಕ್ಕೆ ನಾನು ತೀತನನ್ನು ಬೇಡಿಕೊಂಡು ಅವನ ಜೊತೆಯಲ್ಲಿ ಆ ಸಹೋದರನನ್ನು ಕಳುಹಿಸಿಕೊಟ್ಟೆನಷ್ಟೆ. ತೀತನು ನಿಮ್ಮನ್ನು ವಂಚಿಸಿ ಏನಾದರೂ ತೆಗೆದುಕೊಂಡನೋ; ನಾವಿಬ್ಬರೂ ಒಬ್ಬ ಆತ್ಮನಿಂದಲೇ ಪ್ರೇರಿತರಾಗಿ ಒಂದೇ ಹೆಜ್ಜೆಜಾಡಿನಲ್ಲಿ ನಡೆಯಲಿಲ್ಲವೋ?",
			"19": "<sup>19</sup> ನಾವು ಇಷ್ಟು ಹೊತ್ತು ನಿಮ್ಮ ಮುಂದೆ ಪ್ರತಿವಾದ ಮಾಡುವವರೆಂದು ನೆನಸುತ್ತೀರೋ? ನಿಮ್ಮ ಮುಂದೆಯಲ್ಲ, ದೇವರ ಸನ್ನಿಧಾನದಲ್ಲಿಯೇ ನಾವು ಕ್ರಿಸ್ತನಲ್ಲಿದ್ದು ಮಾತಾಡುವವರಾಗಿದ್ದೇವೆ. ಪ್ರಿಯರೇ, ನಾವು ಮಾತಾಡುವದೆಲ್ಲಾ ನಿಮ್ಮ ಭಕ್ತಿವೃದ್ಧಿಗೋಸ್ಕರವೇ ಆಗಿದೆ.",
			"20": "<sup>20</sup> ನಾನು ಬಂದಾಗ ಒಂದು ವೇಳೆ ನೀವು ನನ್ನ ಇಷ್ಟದ ಪ್ರಕಾರ ಇರುವದಿಲ್ಲವೇನೋ, ನಾನು ನಿಮ್ಮ ಇಷ್ಟದ ಪ್ರಕಾರ ತೋರುವದಿಲ್ಲವೇನೋ, ಒಂದು ವೇಳೆ ನಿಮ್ಮಲ್ಲಿ ಜಗಳ ಹೊಟ್ಟೆಕಿಚ್ಚು ದ್ವೇಷ ಸ್ವಾರ್ಥಬುದ್ಧಿ ಚಾಡಿಹೇಳುವದು ಕಿವಿಯೂದುವದು ಉಬ್ಬಿಕೊಳ್ಳುವದು ಕಲಹ ಎಬ್ಬಿಸುವದು ಇವುಗಳು ಕಾಣಬರುವದೋ ಏನೋ ನನಗೆ ಸಂಶಯ ಉಂಟು.",
			"21": "<sup>21</sup> ನಾನು ತಿರಿಗಿ ಬಂದಾಗ ನನ್ನ ದೇವರು ನಿಮ್ಮ ವಿಷಯದಲ್ಲಿ ನನ್ನನ್ನು ನಾಚಿಕೆಗೆ ಗುರಿಮಾಡಾನೆಂತಲೂ, ಪೂರ್ವದಲ್ಲಿ ಬಂಡುತನ ಹಾದರತನ ಪೋಕರಿತನಗಳನ್ನು ನಡಿಸಿ ಪಶ್ಚಾತ್ತಾಪ ಪಡದಿರುವ ಅನೇಕರ ವಿಷಯವಾಗಿ ನಾನು ದುಃಖಪಡಬೇಕಾದೀತೆಂತಲೂ ನನಗೆ ಭಯ ಉಂಟು."
		},
		"13": {
			"1": "<sup>1</sup> ನಾನು ನಿಮ್ಮ ಬಳಿಗೆ ಬರುವದು ಇದು ಮೂರನೆಯ ಸಾರಿ. ಇಬ್ಬರು ಮೂವರು ಸಾಕ್ಷಿಗಳ ಬಾಯಿಂದ ಪ್ರತಿಯೊಂದು ಸಂಗತಿ ಸ್ಥಾಪಿತವಾಗಬೇಕು.",
			"2": "<sup>2</sup> ನಾನು ಎರಡನೆಯ ಸಾರಿ ನಿಮ್ಮಲ್ಲಿದ್ದಾಗ - ತಿರಿಗಿ ಬಂದರೆ ನಿಮ್ಮನ್ನು ಶಿಕ್ಷಿಸದೆ ಬಿಡುವದಿಲ್ಲವೆಂದು ಹೇಗೆ ಹೇಳಿದೆನೋ ಹಾಗೆಯೇ ಈಗಲೂ ನಿಮಗೆ ದೂರದಲ್ಲಿದ್ದು ಪೂರ್ವಪಾಪಕೃತ್ಯಗಳನ್ನು ಇನ್ನೂ ನಡಿಸುತ್ತಿರುವವರಿಗೂ ಮಿಕ್ಕಾದವರೆಲ್ಲರಿಗೂ ಹೇಳುತ್ತೇನೆ.",
			"3": "<sup>3</sup> ಕ್ರಿಸ್ತನು ನನ್ನಲ್ಲಿ ಮಾತಾಡುತ್ತಾನೆಂಬದಕ್ಕೆ ನಿಮಗೆ ಬೇಕಾದ ಪ್ರಮಾಣವು ಆಗ ತೋರಿಬರುವದು. ಆತನು ನಿಮ್ಮನ್ನು ಕುರಿತು ನಿರ್ಬಲನಾಗಿರದೆ ಬಲವಾದ ಕೆಲಸಗಳನ್ನು ನಿಮ್ಮಲ್ಲಿ ನಡಿಸುವವನಾಗಿದ್ದಾನೆ.",
			"4": "<sup>4</sup> ಆತನು ಬಲಹೀನಾವಸ್ಥೆಯಲ್ಲಿದ್ದದರಿಂದ ಶಿಲುಬೆಗೆ ಹಾಕಲ್ಪಟ್ಟನು ನಿಜ; ಆದರೂ ದೇವರ ಬಲದಿಂದ ಬದುಕುತ್ತಾನೆ. ನಾವು ಸಹ ಆತನ ಬಲಹೀನಾವಸ್ಥೆಯಲ್ಲಿ ಪಾಲುಗಾರರಾಗಿದ್ದೇವೆ; ಆದರೂ ಆತನೊಂದಿಗೆ ದೇವರ ಬಲದಿಂದ ಬದುಕುವವರಾಗಿದ್ದೇವೆ; ಇದು ನಿಮಗೆ ತೋರಿಬರುವದು.",
			"5": "<sup>5</sup> ಕ್ರಿಸ್ತನಂಬಿಕೆಯಲ್ಲಿ ಇದ್ದೀರೋ ಇಲ್ಲವೋ ಎಂದು ನಿಮ್ಮನ್ನು ನೀವೇ ಪರೀಕ್ಷಿಸಿಕೊಳ್ಳಿರಿ, ನಿಮ್ಮನ್ನು ಪರಿಶೋಧಿಸಿಕೊಳ್ಳಿರಿ. ಏನು, ಯೇಸು ಕ್ರಿಸ್ತನು ನಿಮ್ಮಲ್ಲಿದ್ದಾನೆಂಬದು ನಿಮ್ಮನ್ನು ಕುರಿತು ನಿಮಗೆ ಚೆನ್ನಾಗಿ ತಿಳಿಯುವದಿಲ್ಲವೋ? ಆತನು ನಿಮ್ಮಲ್ಲಿಲ್ಲದಿದ್ದರೆ ನೀವು ಅಯೋಗ್ಯರೇ.",
			"6": "<sup>6</sup> ನಾವಂತೂ ಅಯೋಗ್ಯರಲ್ಲವೆಂದು ನಿಮಗೆ ಗೊತ್ತಾಗುವದೆಂಬದಾಗಿ ನಿರೀಕ್ಷಿಸುತ್ತೇನೆ.",
			"7": "<sup>7</sup> ನೀವು ಕೆಟ್ಟದ್ದೇನೂ ಮಾಡಬಾರದೆಂದು ನಾವು ದೇವರನ್ನು ಪ್ರಾರ್ಥಿಸುತ್ತೇವೆ; ಇದರಲ್ಲಿ ನಾವೇ ಯೋಗ್ಯರಾಗಿ ತೋರಿಬರಬೇಕೆಂಬದು ನಮ್ಮ ಉದ್ದೇಶವಲ್ಲ; ನಾವು ಅಯೋಗ್ಯರೆನಿಸಿಕೊಂಡರೂ ನೀವು ಒಳ್ಳೇದನ್ನು ಮಾಡುವವರಾಗಬೇಕೆಂಬದೇ ನಮ್ಮ ಉದ್ದೇಶ.",
			"8": "<sup>8</sup> ಸತ್ಯಕ್ಕೆ ವಿರುದ್ಧವಾಗಿ ನಾವೇನೂ ಮಾಡಲಾರೆವು; ಸತ್ಯಾಭಿವೃದ್ಧಿಗೋಸ್ಕರವೇ ಸಮಸ್ತವನ್ನು ಮಾಡುತ್ತೇವೆ.",
			"9": "<sup>9</sup> ನಾವು ಬಲಹೀನರಾಗಿದ್ದರೂ ನೀವು ಬಲಿಷ್ಠರಾಗಿದ್ದ ಪಕ್ಷಕ್ಕೆ ಸಂತೋಷವಾಗಿದ್ದೇವೆ. ಇದಲ್ಲದೆ ನೀವು ಪೂರ್ಣಕ್ರಮಕ್ಕೆ ಬರಬೇಕೆಂದು ಪ್ರಾರ್ಥಿಸುತ್ತೇವೆ.",
			"10": "<sup>10</sup> ಇದಕ್ಕಾಗಿಯೇ ನಾನು ನಿಮ್ಮಲ್ಲಿ ಇಲ್ಲದಿರುವಾಗ ಈ ಮಾತುಗಳನ್ನು ಬರೆದಿದ್ದೇನೆ. ನಿಮ್ಮನ್ನು ಕೆಡವಿಹಾಕುವದಕ್ಕಲ್ಲ, ಕಟ್ಟುವದಕ್ಕಾಗಿ ಕರ್ತನು ನನಗೆ ಕೊಟ್ಟಿರುವ ಅಧಿಕಾರದ ಪ್ರಯೋಗದಲ್ಲಿ ನಿಮ್ಮ ಬಳಿಗೆ ಬಂದಾಗ ಕಾಠಿಣ್ಯವನ್ನು ತೋರಿಸುವದಕ್ಕೆ ಅವಕಾಶವಿರಬಾರದೆಂದು ಅಪೇಕ್ಷಿಸುತ್ತೇನೆ.",
			"11": "<sup>11</sup> ಕಡೇ ಮಾತೇನಂದರೆ ಸಹೋದರರೇ ಸಂತೋಷಪಡಿರಿ, ಕ್ರಮಪಡಿಸಿಕೊಳ್ಳಿರಿ; ಧೈರ್ಯವುಳ್ಳವರಾಗಿರಿ, ಒಂದೇ ಮನಸ್ಸುಳ್ಳವರಾಗಿರಿ, ಸಮಾಧಾನದಿಂದಿರಿ; ಆಗ ಪ್ರೀತಿಯನ್ನೂ ಶಾಂತಿಯನ್ನೂ ಕೊಡುವ ದೇವರು ನಿಮ್ಮ ಸಂಗಡ ಇರುವನು.",
			"12": "<sup>12</sup> ಪವಿತ್ರವಾದ ಮುದ್ದಿಟ್ಟು ಒಬ್ಬರನ್ನೊಬ್ಬರು ವಂದಿಸಿರಿ.",
			"13": "<sup>13</sup> ದೇವಜನರೆಲ್ಲರೂ ನಿಮಗೆ ವಂದನೆ ಹೇಳುತ್ತಾರೆ.",
			"14": "<sup>14</sup> ನಮ್ಮ ಕರ್ತನಾದ ಯೇಸು ಕ್ರಿಸ್ತನ ಕೃಪೆಯೂ ದೇವರ ಪ್ರೀತಿಯೂ ಪವಿತ್ರಾತ್ಮನ ಅನ್ಯೋನ್ಯತೆಯೂ ನಿಮ್ಮೆಲ್ಲರ ಸಂಗಡವಿರಲಿ."
		}
	}
};
module.exports = book;