var book = {
	"name": "ಎಫೆಸದವರಿಗೆ",
	"numChapters": 6,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ದೇವರ ಚಿತ್ತಾನುಸಾರವಾಗಿ ಕ್ರಿಸ್ತ ಯೇಸುವಿನ ಅಪೊಸ್ತಲನಾದ ಪೌಲನು ಎಫೆಸದಲ್ಲಿರುವ ದೇವಜನರಿಗೂ ಕ್ರಿಸ್ತ ಯೇಸುವಿನಲ್ಲಿ ನಂಬಿಕೆಯಿಟ್ಟಿರುವವರಿಗೂ ಬರೆಯುವದೇನಂದರೆ -",
			"2": "<sup>2</sup> ನಮ್ಮ ತಂದೆಯಾದ ದೇವರಿಂದಲೂ ಕರ್ತನಾದ ಯೇಸು ಕ್ರಿಸ್ತನಿಂದಲೂ ನಿಮಗೆ ಕೃಪೆಯೂ ಶಾಂತಿಯೂ ಆಗಲಿ.",
			"3": "<sup>3</sup> ನಮ್ಮ ಕರ್ತನಾದ ಯೇಸು ಕ್ರಿಸ್ತನ ದೇವರೂ ತಂದೆಯೂ ಆಗಿರುವಾತನಿಗೆ ಸ್ತೋತ್ರ. ಆತನು ಪರಲೋಕದಲ್ಲಿನ ಸಕಲ ಆತ್ಮೀಯವರಗಳನ್ನು ನಮಗೆ ಕ್ರಿಸ್ತ ಯೇಸುವಿನಲ್ಲಿ ಅನುಗ್ರಹಿಸಿದ್ದಾನೆ.",
			"4": "<sup>4</sup> ಹೇಗಂದರೆ ನಾವು ಪ್ರೀತಿಯಲ್ಲಿ ನಡೆದು ಆತನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಪರಿಶುದ್ಧರೂ ದೋಷವಿಲ್ಲದವರೂ ಆಗಿರಬೇಕೆಂದು ನಮ್ಮನ್ನು ಜಗದುತ್ಪತ್ತಿಗೆ ಮುಂಚೆ ಕ್ರಿಸ್ತನಲ್ಲಿ ಆರಿಸಿಕೊಂಡನು.",
			"5": "<sup>5</sup> ಆತನು ತನ್ನ ಕೃಪೆಯನ್ನು ಪ್ರಖ್ಯಾತಿಗೆ ತರಬೇಕೆಂದು ನಮ್ಮನ್ನು ಯೇಸು ಕ್ರಿಸ್ತನ ಮೂಲಕ ತನ್ನ ಪುತ್ರರನ್ನಾಗಿ ಸ್ವೀಕರಿಸುವದಕ್ಕೆ ದಯಾಪೂರ್ವಕವಾದ ತನ್ನ ಚಿತ್ತಾನುಸಾರವಾಗಿ ಮೊದಲೇ ಸಂಕಲ್ಪಮಾಡಿದ್ದನು. ಈ ಕೃಪಾದಾನವು ಆತನ ಪ್ರಿಯನಲ್ಲಿಯೇ ನಮಗೆ ದೊರೆಯಿತು.",
			"7": "<sup>7</sup> ಈತನು ನಮಗೋಸ್ಕರ ತನ್ನ ರಕ್ತವನ್ನು ಸುರಿಸಿದ್ದರಿಂದ ನಮ್ಮ ಅಪರಾಧಗಳು ಪರಿಹಾರವಾಗಿ ನಮಗೆ ಬಿಡುಗಡೆಯಾಯಿತು. ಇದು ದೇವರ ಕೃಪಾತಿಶಯವೇ.",
			"8": "<sup>8</sup> ಆತನು ರಹಸ್ಯವಾದ ತನ್ನ ಸಂಕಲ್ಪವನ್ನು ನಮಗೆ ತಿಳಿಯಪಡಿಸುವದರ ಮೂಲಕ ವಿಶೇಷವಾದ ಜ್ಞಾನವನ್ನೂ ಬುದ್ಧಿಯನ್ನೂ ಕೊಟ್ಟು ಆ ಕೃಪೆಯನ್ನು ಇನ್ನೂ ಹೆಚ್ಚಾಗಿ ನಮಗೆ ತೋರಿಸಿದ್ದಾನೆ.",
			"9": "<sup>9</sup> ತಾನು ಕಾಲವು ಪರಿಪೂರ್ಣವಾದಾಗ ನಿರ್ವಹಿಸಬೇಕಾದ ಒಂದು ಕೃಪೆಯುಳ್ಳ ಸಂಕಲ್ಪವನ್ನು ಆತನು ಕ್ರಿಸ್ತನಲ್ಲಿ ಮೊದಲೇ ನಿಷ್ಕರ್ಷೆಮಾಡಿಕೊಂಡಿದ್ದನು. ಅದೇನಂದರೆ ಭೂಪರಲೋಕಗಳಲ್ಲಿರುವ ಸಮಸ್ತವನ್ನೂ ಕ್ರಿಸ್ತನಲ್ಲಿ ಒಂದಾಗಿ ಕೂಡಿಸಬೇಕೆಂಬದೇ. ಕ್ರಿಸ್ತನಲ್ಲಿಯೇ ಸಮಸ್ತವು ಒಂದಾಗುವದು.",
			"11": "<sup>11</sup> ಇದಲ್ಲದೆ ಆತನಲ್ಲಿ ನಾವು ದೇವರ ಸ್ವಕೀಯ ಪ್ರಜೆಯಾದೆವು, ಬರಬೇಕಾದ ಕ್ರಿಸ್ತನನ್ನು ಎದುರುನೋಡುತ್ತಿದ್ದ ನಾವು ತನ್ನ ಮಹಿಮೆಯನ್ನು ಪ್ರಖ್ಯಾತಿಪಡಿಸಬೇಕೆಂದು ಸಮಸ್ತಕಾರ್ಯಗಳನ್ನು ತನ್ನ ಇಷ್ಟದಂತೆ ನಡಿಸುವ ದೇವರು ತನ್ನ ಸಂಕಲ್ಪದ ಮೇರೆಗೆ ನಮ್ಮನ್ನು ಆದಿಯಲ್ಲಿಯೇ ಆರಿಸಿಕೊಂಡನು.",
			"13": "<sup>13</sup> ನಿಮ್ಮ ರಕ್ಷಣೆಯ ವಿಷಯವಾದ ಸುವಾರ್ತೆಯೆಂಬ ಸತ್ಯವಾಕ್ಯವನ್ನು ಕೇಳಿ ಕ್ರಿಸ್ತನಲ್ಲಿ ನಂಬಿಕೆಯಿಟ್ಟವರಾದ ನೀವು ಸಹ ವಾಗ್ದಾನಮಾಡಲ್ಪಟ್ಟ ಪವಿತ್ರಾತ್ಮನೆಂಬ ಮುದ್ರೆಯನ್ನು ಹೊಂದಿದಿರಿ.",
			"14": "<sup>14</sup> ದೇವರು ಸಂಪಾದಿಸಿಕೊಂಡ ಜನರಿಗೆ ವಿಮೋಚನೆಯಾಗುವದೆಂಬದಕ್ಕೆ ಪವಿತ್ರಾತ್ಮನು ನಮ್ಮ ಬಾಧ್ಯತೆಗೆ ಸಂಚಕಾರವಾಗಿದ್ದಾನೆ. ಆ ವಿಮೋಚನೆಯು ದೇವರ ಮಹಿಮೆಗೆ ಎಷ್ಟೋ ಪ್ರಖ್ಯಾತಿಯನ್ನು ಉಂಟುಮಾಡುವದು.",
			"15": "<sup>15</sup> ಹೀಗಿರಲಾಗಿ ಕರ್ತನಾದ ಯೇಸುವಿನಲ್ಲಿ ನೀವು ಇಟ್ಟಿರುವ ನಂಬಿಕೆಯನ್ನು ಕುರಿತೂ ದೇವಜನರೆಲ್ಲರಿಗೆ ನೀವು ತೋರಿಸುವ ಪ್ರೀತಿಯನ್ನು ಕುರಿತೂ ಕೇಳಿ ನಾನು ನಿಮ್ಮ ನಿಮಿತ್ತವಾಗಿ ಎಡೆಬಿಡದೆ ದೇವರಿಗೆ ಸ್ತೋತ್ರಮಾಡುತ್ತೇನೆ.",
			"16": "<sup>16</sup> ನನ್ನ ಪ್ರಾರ್ಥನೆಗಳಲ್ಲಿ ನಿಮಗೋಸ್ಕರ ವಿಜ್ಞಾಪನೆಮಾಡಿ -",
			"17": "<sup>17</sup> ನಮ್ಮ ಕರ್ತನಾದ ಯೇಸು ಕ್ರಿಸ್ತನ ದೇವರೂ ಪ್ರಭಾವಸ್ವರೂಪನಾದ ತಂದೆಯೂ ಆಗಿರುವಾತನು ತನ್ನ ವಿಷಯವಾಗಿ ತಿಳುವಳಿಕೆಯನ್ನು ಕೊಟ್ಟು ಇದುವರೆಗೆ ಗುಪ್ತವಾಗಿದ್ದ ಸತ್ಯಾರ್ಥಗಳನ್ನು ತಿಳುಕೊಳ್ಳುವ ಜ್ಞಾನವುಳ್ಳ ಆತ್ಮವನ್ನು ನಿಮಗೆ ದಯಪಾಲಿಸಬೇಕೆಂದು ಪ್ರಾರ್ಥಿಸುತ್ತೇನೆ.",
			"18": "<sup>18</sup> ಆತನು ನಿಮ್ಮ ಮನೋನೇತ್ರಗಳನ್ನು ಬೆಳಗಿಸಿ ಆತನಿಂದ ಕರಿಸಿಕೊಂಡವರು ನಿರೀಕ್ಷಿಸುವ ಪದವಿ ಎಂಥದೆಂಬದನ್ನೂ ದೇವಜನರೆಂಬ ಆತನ ಸ್ವಾಸ್ಥ್ಯದ ಮಹಿಮಾತಿಶಯವು ಎಂಥದೆಂಬದನ್ನೂ",
			"19": "<sup>19</sup> ನಂಬುವವರಾದ ನಮ್ಮಲ್ಲಿ ಆತನು ಸಾಧಿಸುವ ಪರಾಕ್ರಮವು ಎಷ್ಟು ಅತಿಶಯವಾದದ್ದೆಂಬದನ್ನೂ ನೀವು ತಿಳುಕೊಳ್ಳುವಂತೆ ಅನುಗ್ರಹಿಸಲಿ.",
			"20": "<sup>20</sup> ಆತನ ಬಲಾತಿಶಯವು ಎಷ್ಟು ಮಹತ್ತಾದದ್ದೆಂಬದು ಕ್ರಿಸ್ತನಲ್ಲಿ ತೋರಿಬಂದಿದೆ. ಹೇಗಂದರೆ ಆತನು ಸತ್ತಿದ್ದ ಕ್ರಿಸ್ತನನ್ನು ಬದುಕಿಸಿ",
			"21": "<sup>21</sup> ಪರಲೋಕದೊಳಗೆ ಸಕಲ ರಾಜ್ಯತ್ವ ಅಧಿಕಾರ ಮಹತ್ವ ಪ್ರಭುತ್ವಾದಿಗಳ ಮೇಲೆಯೂ ಈ ಲೋಕದಲ್ಲಿ ಮಾತ್ರವಲ್ಲದೆ ಬರುವ ಲೋಕದಲ್ಲಿ ಸಹ ಹೆಸರುಗೊಂಡವರೆಲ್ಲರ ಮೇಲೆಯೂ ತನ್ನ ಬಲಗಡೆಯಲ್ಲಿ ಕೂಡ್ರಿಸಿಕೊಂಡನು.",
			"22": "<sup>22</sup> ಆತನು ಸಮಸ್ತವನ್ನೂ ಕ್ರಿಸ್ತನ ಪಾದಗಳ ಕೆಳಗೆ ಹಾಕಿದನು. ಇದಲ್ಲದೆ ಆತನನ್ನು ಎಲ್ಲಾದರ ಮೇಲೆ ಇರಿಸಿ ಸಭೆಗೆ ಶಿರಸ್ಸಾಗಿ ನೇಮಿಸಿದನು.",
			"23": "<sup>23</sup> ಸಭೆಯು ಆತನ ದೇಹವಾಗಿದೆ; ಎಲ್ಲವನ್ನೂ ಎಲ್ಲಾ ಸಂಬಂಧದಲ್ಲಿ ವ್ಯಾಪಿಸುವಾತನಿಂದ ಅದು ಪರಿಪೂರ್ಣತೆಯುಳ್ಳದ್ದಾಗಿದೆ."
		},
		"2": {
			"1": "<sup>1</sup> ಆತನು ಅಪರಾಧಗಳ ಮತ್ತು ಪಾಪಗಳ ದೆಸೆಯಿಂದ ಸತ್ತವರಾಗಿದ್ದ ನಿಮ್ಮನ್ನು ಸಹ ಬದುಕಿಸಿದನು.",
			"2": "<sup>2</sup> ನೀವು ಪೂರ್ವದಲ್ಲಿ ಅಪರಾಧಗಳನ್ನೂ ಪಾಪಗಳನ್ನೂ ಮಾಡುವವರಾಗಿದ್ದು ಇಹಲೋಕಾಚಾರಕ್ಕೆ ಅನುಸಾರವಾಗಿ ನಡೆದುಕೊಂಡಿರಿ; ವಾಯುಮಂಡಲದಲ್ಲಿ ಅಧಿಕಾರನಡಿಸುವ ಅಧಿಪತಿಗೆ, ಅಂದರೆ ನಂಬಲೊಲ್ಲದವರನ್ನು ಅವಿಧೇಯತೆಗೆ ಈಗ ಪ್ರೇರೇಪಿಸುವ ಆತ್ಮನಿಗೆ ಅನುಸಾರವಾಗಿ ನಡೆದುಕೊಂಡಿರಿ.",
			"3": "<sup>3</sup> ನಾವೆಲ್ಲರೂ ಪೂರ್ವದಲ್ಲಿ ಅವಿಧೇಯರಾಗಿದ್ದು ಶರೀರಭಾವದ ಆಶೆಗಳಿಗೆ ಅಧೀನರಾಗಿ ಶರೀರಕ್ಕೂ ಮನಸ್ಸಿಗೂ ಸಂಬಂಧಪಟ್ಟ ಇಚ್ಫೆಗಳನ್ನು ನೆರವೇರಿಸುತ್ತಾ ನಡೆದು ಮಿಕ್ಕಾದವರಂತೆ ಸ್ವಭಾವಸಿದ್ಧವಾಗಿ ದೇವರ ಕೋಪಕ್ಕೆ ಗುರಿಯಾಗಿದ್ದೆವು.",
			"4": "<sup>4</sup> ಆದರೆ ಕರುಣಾನಿಧಿಯಾಗಿರುವ ದೇವರು ನಮ್ಮ ಮೇಲೆ ಮಹಾ ಪ್ರೀತಿಯನ್ನಿಟ್ಟು",
			"5": "<sup>5</sup> ಅಪರಾಧಗಳ ದೆಸೆಯಿಂದ ಸತ್ತವರಾಗಿದ್ದ ನಮ್ಮನ್ನು ಕ್ರಿಸ್ತನೊಂದಿಗೆ ಬದುಕಿಸಿದನು. (ಕೃಪೆಯಿಂದಲೇ ರಕ್ಷಣೆ ಹೊಂದಿದವರಾಗಿದ್ದೀರಿ.)",
			"6": "<sup>6</sup> ಬದುಕಿಸಿದ್ದಲ್ಲದೆ ತಾನು ಕ್ರಿಸ್ತ ಯೇಸುವಿನಲ್ಲಿ ನಮಗೆ ಮಾಡುವ ಉಪಕಾರದ ಮೂಲಕ ತನ್ನ ಅಪಾರವಾದ ಕೃಪಾತಿಶಯವನ್ನು",
			"7": "<sup>7</sup> ಮುಂದಣ ಯುಗಗಳಲ್ಲಿ ತೋರಿಸಬೇಕೆಂದು ಕ್ರಿಸ್ತ ಯೇಸುವಿನಲ್ಲಿರುವ ನಮ್ಮನ್ನು ಆತನೊಂದಿಗೆ ಎಬ್ಬಿಸಿ ಪರಲೋಕದಲ್ಲಿ ಆತನೊಂದಿಗೆ ಕೂಡ್ರಿಸಿದ್ದಾನೆ.",
			"8": "<sup>8</sup> ನಂಬಿಕೆಯ ಮೂಲಕ ಕೃಪೆಯಿಂದಲೇ ರಕ್ಷಣೆ ಹೊಂದಿದವರಾಗಿದ್ದೀರಿ. ಆ ರಕ್ಷಣೆಯು ನಿಮ್ಮಿಂದುಂಟಾದದ್ದಲ್ಲ, ಅದು ದೇವರ ವರವೇ.",
			"9": "<sup>9</sup> ಅದು ಪುಣ್ಯಕ್ರಿಯೆಗಳಿಂದ ಉಂಟಾದದ್ದಲ್ಲ; ಆದದರಿಂದ ಹೊಗಳಿಕೊಳ್ಳುವದಕ್ಕೆ ಯಾರಿಗೂ ಆಸ್ಪದವಿಲ್ಲ.",
			"10": "<sup>10</sup> ನಾವು ಆತನ ನಿರ್ಮಾಣ; ಸತ್ಕಾರ್ಯಗಳನ್ನು ಮಾಡುವದಕ್ಕಾಗಿಯೇ ಕ್ರಿಸ್ತ ಯೇಸುವಿನಲ್ಲಿ ಸೃಷ್ಟಿಸಲ್ಪಟ್ಟೆವು. ನಾವು ಸತ್ಕಾರ್ಯಗಳನ್ನು ನಡಿಸುವವರಾಗಿ ಬದುಕಬೇಕೆಂದು ದೇವರು ನಮ್ಮನ್ನು ಮೊದಲೇ ನೇಮಿಸಿದನು.",
			"11": "<sup>11</sup> ನೀವು ಜನ್ಮದಿಂದ ಅನ್ಯಜನರಾಗಿದ್ದೀರಿ; ಶರೀರದಲ್ಲಿ ಕೈಯಿಂದ ಸುನ್ನತಿಮಾಡಿಸಿಕೊಂಡು ಸುನ್ನತಿಯವರೆನಿಸಿಕೊಳ್ಳುವವರು ನಿಮ್ಮನ್ನು ಸುನ್ನತಿಯಿಲ್ಲದವರೆಂದು ಹೇಳುತ್ತಾರೆ.",
			"12": "<sup>12</sup> ನೀವು ಪೂರ್ವಕಾಲದಲ್ಲಿ ಕ್ರಿಸ್ತನನ್ನು ಸೇರದವರಾಗಿದ್ದು ಇಸ್ರಾಯೇಲ್ಯ ಹಕ್ಕಿನಲ್ಲಿ ಪಾಲಿಲ್ಲದವರೂ [ಅಬ್ರಹಾಮನಿಗೆ ಉಂಟಾದ] ವಾಗ್ದಾನಕ್ಕೆ ಸಂಬಂಧವಾದ ಒಡಂಬಡಿಕೆಗಳಲ್ಲಿ ಸೇರದವರೂ ಈ ಲೋಕದಲ್ಲಿ ಯಾವ ನಿರೀಕ್ಷೆಯಿಲ್ಲದವರೂ ದೇವರನ್ನರಿಯದವರೂ ಆಗಿದ್ದಿರೆಂದು ಜ್ಞಾಪಕಮಾಡಿಕೊಳ್ಳಿರಿ.",
			"13": "<sup>13</sup> ಈಗಲಾದರೋ ಮೊದಲು ದೂರವಾಗಿದ್ದ ನೀವು ಕ್ರಿಸ್ತ ಯೇಸುವನ್ನು ಸೇರಿದವರಾಗಿದ್ದು ಆತನ ರಕ್ತದ ಮೂಲಕ ಸಮೀಪಸ್ಥರಾದಿರಿ.",
			"14": "<sup>14</sup> ನಿಮ್ಮನ್ನೂ ನಮ್ಮನ್ನೂ ಒಂದು ಮಾಡಿದವನಾದ ಆತನೇ ನಮಗೆ ಸಮಾಧಾನಕರ್ತೃವಾಗಿದ್ದಾನೆ. ಆತನು ತನ್ನ ಶರೀರವನ್ನು ಸಮರ್ಪಿಸಿದ್ದರಲ್ಲಿ ವಿಧಿರೂಪವಾದ ಆಜ್ಞೆಗಳುಳ್ಳ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ತೆಗೆದುಹಾಕಿ ಇದ್ದ ದ್ವೇಷವನ್ನು ಮುಗಿಸಿ ನಮ್ಮನ್ನು ಅಗಲಿಸಿದ ಅಡ್ಡಗೋಡೆಯನ್ನು ಕೆಡವಿಹಾಕಿದನು.",
			"15": "<sup>15</sup> ಆತನು ಸಮಾಧಾನಮಾಡುವವನಾಗಿ ಉಭಯರನ್ನೂ ತನ್ನಲ್ಲಿ ಒಬ್ಬ ನೂತನಪುರುಷನನ್ನಾಗಿ ನಿರ್ಮಿಸಿದ್ದಾನೆ;",
			"16": "<sup>16</sup> ಇದ್ದ ದ್ವೇಷವನ್ನು ತನ್ನ ಶಿಲುಬೆಯ ಮೇಲೆ ಕೊಂದು ಆ ಶಿಲುಬೆಯ ಮೂಲಕ ಉಭಯರನ್ನೂ ಒಂದೇ ದೇಹದಂತಾಗ ಮಾಡಿ ದೇವರೊಂದಿಗೆ ಸಮಾಧಾನಪಡಿಸಿದ್ದಾನೆ.",
			"17": "<sup>17</sup> ಇದಲ್ಲದೆ ಆತನು ಬಂದು ದೂರವಾಗಿದ್ದ ನಿಮಗೂ ಸಮಾಧಾನವನ್ನು ಮತ್ತು ಸಮೀಪವಾಗಿದ್ದವರಿಗೂ ಸಮಾಧಾನವನ್ನು ಸಾರಿದನು.",
			"18": "<sup>18</sup> ಆತನ ಮೂಲಕ ನಾವೂ ನೀವೂ ಒಬ್ಬ ಆತ್ಮನನ್ನೇ ಹೊಂದಿದವರಾಗಿ ತಂದೆಯ ಬಳಿಗೆ ಪ್ರವೇಶಿಸುವದಕ್ಕೆ ಮಾರ್ಗವಾಯಿತು.",
			"19": "<sup>19</sup> ಹೀಗಿರಲಾಗಿ ನೀವು ಇನ್ನು ಮೇಲೆ ಪರದೇಶದವರೂ ಅನ್ಯರೂ ಆಗಿರದೆ ದೇವಜನರೊಂದಿಗೆ ಒಂದೇ ಸಂಸ್ಥಾನದವರೂ ದೇವರ ಮನೆಯವರೂ ಆಗಿದ್ದೀರಿ.",
			"20": "<sup>20</sup> ಅಪೊಸ್ತಲರೂ ಪ್ರವಾದಿಗಳೂ ಎಂಬ ಅಸ್ತಿವಾರದ ಮೇಲೆ ನೀವು ಮಂದಿರದೋಪಾದಿಯಲ್ಲಿ ಕಟ್ಟಲ್ಪಟ್ಟಿದ್ದೀರಿ.",
			"21": "<sup>21</sup> ಯೇಸು ಕ್ರಿಸ್ತನೇ ಮುಖ್ಯವಾದ ಮೂಲೆಗಲ್ಲು; ಆತನಲ್ಲಿ ಕಟ್ಟಡದ ಎಲ್ಲಾ ಭಾಗಗಳು ಒಂದಕ್ಕೊಂದು ಹೊಂದಿಕೆಯಾಗಿ ಕಟ್ಟಡವು ವೃದ್ಧಿಯಾಗುತ್ತಾ ಕರ್ತನಲ್ಲಿ ಪರಿಶುದ್ಧ ದೇವಾಲಯವಾಗುತ್ತದೆ.",
			"22": "<sup>22</sup> ಆತನಲ್ಲಿ ನೀವು ಸಹ ಪವಿತ್ರಾತ್ಮನ ಮೂಲಕವಾಗಿ ದೇವರಿಗೆ ನಿವಾಸಸ್ಥಾನವಾಗುವದಕ್ಕಾಗಿ ನಮ್ಮ ಸಂಗಡ ಕಟ್ಟಲ್ಪಡುತ್ತಾ ಇದ್ದೀರಿ."
		},
		"3": {
			"1": "<sup>1</sup> ಹೀಗಿರುವದರಿಂದ ಅನ್ಯಜನರಾಗಿರುವ ನಿಮ್ಮ ನಿಮಿತ್ತ ಕ್ರಿಸ್ತ ಯೇಸುವಿನ ಸೆರೆಯವನಾದ ಪೌಲನೆಂಬ ನಾನು ನಿಮಗೋಸ್ಕರ ಪ್ರಾರ್ಥಿಸುತ್ತೇನೆ.",
			"2": "<sup>2</sup> ನಿಮಗೋಸ್ಕರ ನಿರ್ವಹಿಸುವದಕ್ಕಾಗಿ ದೇವರು ನನಗೆ ಕೃಪೆಯಾಗಿ ಕೊಟ್ಟ ಕೆಲಸವನ್ನೂ ಇದುವರೆಗೆ ಗುಪ್ತವಾಗಿದ್ದ ಒಂದು ಸಂಗತಿ ದೈವಪ್ರಕಟನೆಯಿಂದ ನನಗೆ ತಿಳಿಸಲ್ಪಟ್ಟಿತೆಂಬದನ್ನೂ ನೀವು ಕೇಳಿದ್ದೀರಲ್ಲಾ.",
			"4": "<sup>4</sup> ಅದನ್ನು ಕುರಿತು ನಾನು ಹಿಂದೆ ಸಂಕ್ಷೇಪವಾಗಿ ಬರೆದಿದ್ದೇನೆ; ಬರೆದದ್ದನ್ನು ನೀವು ಓದಿ ನೋಡಿದರೆ ಕ್ರಿಸ್ತನ ವಿಷಯವಾದ ಮರ್ಮವನ್ನು ಕುರಿತು ನನಗಿರುವ ಗ್ರಹಿಕೆಯನ್ನು ನೀವು ತಿಳುಕೊಳ್ಳಬಹುದು.",
			"5": "<sup>5</sup> ಆ ಮರ್ಮವು ಈ ಕಾಲದಲ್ಲಿ ದೇವರ ಪರಿಶುದ್ಧ ಅಪೊಸ್ತಲರಿಗೂ ಪ್ರವಾದಿಗಳಿಗೂ ಪವಿತ್ರಾತ್ಮನಿಂದ ತಿಳಿಸಲ್ಪಟ್ಟಂತೆ ಬೇರೆ ಕಾಲಗಳಲ್ಲಿದ್ದ ಜನರಿಗೆ ತಿಳಿಸಲ್ಪಡಲಿಲ್ಲ.",
			"6": "<sup>6</sup> ಅದು ಯಾವದಂದರೆ, ಅನ್ಯಜನರು ಸುವಾರ್ತೆಯ ಮೂಲಕ ಕ್ರಿಸ್ತ ಯೇಸುವಿನಲ್ಲಿರುವವರಾಗಿ ಯೆಹೂದ್ಯರೊಂದಿಗೆ ಬಾಧ್ಯರೂ ಒಂದೇ ದೇಹದೊಳಗಣ ಅಂಗಗಳೂ ಅಬ್ರಹಾಮನಿಗುಂಟಾದ ವಾಗ್ದಾನದಲ್ಲಿ ಪಾಲುಗಾರರೂ ಆಗಿದ್ದಾರೆಂಬದೇ.",
			"7": "<sup>7</sup> ದೇವರು ತನ್ನ ಶಕ್ತಿಯ ಪ್ರಯೋಗದಲ್ಲಿ ನನಗೆ ಉಚಿತಾರ್ಥವಾಗಿ ಅನುಗ್ರಹಿಸಿದ ಕೆಲಸವನ್ನನುಸರಿಸಿ ನಾನು ಈ ಸುವಾರ್ತೆಗೆ ಸೇವಕನಾದೆನು.",
			"8": "<sup>8</sup> ನಾನು ಕ್ರಿಸ್ತನ ಅಪ್ರಮೇಯವಾದ ಐಶ್ವರ್ಯದ ವಿಷಯವಾದ ಶುಭಸಮಾಚಾರವನ್ನು ಅನ್ಯಜನರಿಗೆ ಪ್ರಸಿದ್ಧಿಪಡಿಸುವ ಹಾಗೆಯೂ ಸಮಸ್ತವನ್ನು ಸೃಷ್ಟಿಸಿದ ದೇವರಲ್ಲಿ ಆದಿಯಿಂದ ಮರೆಯಾಗಿದ್ದ ಮರ್ಮವು ಪ್ರಕಟವಾಗುವ ವಿಧ ಎಂಥದೆಂದು ತಿಳಿಸುವ ಹಾಗೆಯೂ ದೇವಜನರೊಳಗೆ ಅತ್ಯಲ್ಪನಾದ ನನಗೆ ಅನುಗ್ರಹಿಸೋಣವಾಯಿತು.",
			"10": "<sup>10</sup> ದೇವರು ನಮ್ಮ ಕರ್ತನಾದ ಕ್ರಿಸ್ತ ಯೇಸುವಿನಲ್ಲಿ ಅನಾದಿಕಾಲದಿಂದ ಮಾಡಿದ ಸಂಕಲ್ಪದ ಮೇರೆಗೆ ತನ್ನ ನಾನಾ ವಿಧವಾದ ಜ್ಞಾನವು ಪರಲೋಕದಲ್ಲಿ ರಾಜತ್ವಗಳಿಗೂ ಅಧಿಕಾರಗಳಿಗೂ ಈಗ ಕ್ರೈಸ್ತ ಸಭೆಯ ಮೂಲಕ ಗೊತ್ತಾಗಬೇಕೆಂಬದನ್ನು ಉದ್ದೇಶಿಸಿದ್ದನು.",
			"12": "<sup>12</sup> ದೇವರ ಸಾನ್ನಿಧ್ಯ ಸೇರುವದಕ್ಕೆ ನಮಗಿರುವ ಭರವಸವುಳ್ಳ ಧೈರ್ಯವು ಕ್ರಿಸ್ತನಲ್ಲಿ ಇಟ್ಟಿರುವ ನಂಬಿಕೆಯ ಮೂಲಕ ಆತನಲ್ಲಿಯೇ ನಮಗೆ ಉಂಟಾಯಿತು.",
			"13": "<sup>13</sup> ಆದದರಿಂದ ನಿಮ್ಮ ನಿಮಿತ್ತ ನನಗೆ ಉಂಟಾದ ಕಷ್ಟಗಳನ್ನು ನೋಡಿ ನೀವು ಧೈರ್ಯಗೆಡಬಾರದೆಂದು ನಿಮ್ಮನ್ನು ಬೇಡಿಕೊಳ್ಳುತ್ತೇನೆ. ಈ ಕಷ್ಟಗಳು ನಿಮಗೆ ಘನವನ್ನು ಉಂಟುಮಾಡುತ್ತವಷ್ಟೆ.",
			"14": "<sup>14</sup> ಹೀಗಿರಲಾಗಿ ಯಾವ ತಂದೆಯಿಂದ ಭೂಪರಲೋಕಗಳಲ್ಲಿರುವ ಪ್ರತಿಜನವೂ ಹೆಸರು ತೆಗೆದುಕೊಳ್ಳುತ್ತದೋ ಆ ತಂದೆಯ ಮುಂದೆ ನಾನು ಮೊಣಕಾಲೂರಿಕೊಂಡು -",
			"16": "<sup>16</sup> ನೀವು ದೇವರಾತ್ಮನ ಮೂಲಕ ಆಂತರ್ಯದಲ್ಲಿ ವಿಶೇಷಬಲ ಹೊಂದಿದವರಾಗುವ ಹಾಗೆಯೂ",
			"17": "<sup>17</sup> ಕ್ರಿಸ್ತನು ನಿಮ್ಮ ನಂಬಿಕೆಯ ಮೂಲಕ ನಿಮ್ಮ ಹೃದಯಗಳಲ್ಲಿ ವಾಸಮಾಡುವ ಹಾಗೆಯೂ ಆತನು ತನ್ನ ಮಹಿಮಾತಿಶಯದ ಪ್ರಕಾರ ಅನುಗ್ರಹಿಸಲಿ;",
			"18": "<sup>18</sup> ನೀವು ದೇವರ ಪ್ರೀತಿಯಲ್ಲಿ ನೆಲೆಗೊಂಡು ನಿಂತು ಅದರ ಅಗಲ ಉದ್ದ ಎತ್ತರ ಆಳ ಎಷ್ಟೆಂಬದನ್ನು ದೇವಜನರೆಲ್ಲರೊಂದಿಗೆ ಗ್ರಹಿಸಲೂ",
			"19": "<sup>19</sup> ಜ್ಞಾನಕ್ಕೆ ಮೀರುವ ಕ್ರಿಸ್ತನ ಪ್ರೀತಿಯನ್ನು ತಿಳುಕೊಳ್ಳಲೂ ಪೂರ್ಣಶಕ್ತರಾಗಿದ್ದು ದೇವರ ಸಂಪೂರ್ಣತೆಯ ಮಟ್ಟಿಗೂ ಪರಿಪೂರ್ಣರಾಗುವ ಹಾಗೆ ನಿಮಗೆ ದಯಪಾಲಿಸಲಿ ಎಂದು ಆತನನ್ನು ಬೇಡಿಕೊಳ್ಳುತ್ತೇನೆ.",
			"20": "<sup>20</sup> ನಮ್ಮಲ್ಲಿ ಕಾರ್ಯಸಾಧಿಸುವ ತನ್ನ ಶಕ್ತಿಯ ಪ್ರಕಾರ ನಾವು ಬೇಡುವದಕ್ಕಿಂತಲೂ ಯೋಚಿಸುವದಕ್ಕಿಂತಲೂ",
			"21": "<sup>21</sup> ಅತ್ಯಧಿಕವಾದದ್ದನ್ನು ಮಾಡಲು ಶಕ್ತನಾದ ದೇವರಿಗೆ ಸಭೆಯಲ್ಲಿಯೂ ಕ್ರಿಸ್ತ ಯೇಸುವಿನಲ್ಲಿಯೂ ತಲತಲಾಂತರಕ್ಕೂ ಯುಗಯುಗಾಂತರಕ್ಕೂ ಸ್ತೋತ್ರ. ಆಮೆನ್."
		},
		"4": {
			"1": "<sup>1</sup> ನೀವು ದೇವರಿಂದ ಕರೆಯಲ್ಪಟ್ಟವರಾದ ಕಾರಣ ಯೋಗ್ಯರಾಗಿ ನಡೆದುಕೊಳ್ಳಬೇಕೆಂದು ಕರ್ತನ ಸೇವೆಯಲ್ಲಿ ಸೆರೆಯವನಾದ ನಾನು ನಿಮ್ಮನ್ನು ಪ್ರಬೋಧಿಸುತ್ತೇನೆ.",
			"2": "<sup>2</sup> ನೀವು ಪೂರ್ಣ ವಿನಯ ಸಾತ್ವಿಕತ್ವಗಳಿಂದಲೂ ದೀರ್ಘಶಾಂತಿಯಿಂದಲೂ ಕೂಡಿದವರಾಗಿ",
			"3": "<sup>3</sup> ಪ್ರೀತಿಯಿಂದ ಒಬ್ಬರನ್ನೊಬ್ಬರು ಸಹಿಸಿಕೊಳ್ಳಿರಿ. ಸಮಾಧಾನವೆಂಬ ಬಂಧನದಿಂದ ಕಟ್ಟಲ್ಪಟ್ಟವರಾಗಿದ್ದು ಪವಿತ್ರಾತ್ಮನಿಂದುಂಟಾಗುವ ಐಕ್ಯವನ್ನು ಕಾಪಾಡಿಕೊಳ್ಳುವದಕ್ಕೆ ಆಸಕ್ತರಾಗಿರಿ.",
			"4": "<sup>4</sup> ನೀವೆಲ್ಲರೂ ಒಂದೇ ದೇಹಕ್ಕೆ ಸೇರಿದವರು, ಒಬ್ಬನೇ ಆತ್ಮನನ್ನು ಹೊಂದಿದವರು,",
			"5": "<sup>5</sup> ಒಂದೇ ಪದವಿಯನ್ನು ನಿರೀಕ್ಷಿಸುವದಕ್ಕೆ ಕರೆಯಲ್ಪಟ್ಟವರು; ನಿಮ್ಮೆಲ್ಲರಿಗೂ ಕರ್ತನು ಒಬ್ಬನೇ, ಭರವಸವು ಒಂದೇ, ಸ್ನಾನದೀಕ್ಷೆ ಒಂದೇ.",
			"6": "<sup>6</sup> ಎಲ್ಲರಿಗೂ ತಂದೆಯಾಗಿರುವ ದೇವರು ಒಬ್ಬನೇ; ಆತನು ಎಲ್ಲರ ಮೇಲಿರುವವನೂ ಎಲ್ಲರ ಮುಖಾಂತರ ಕಾರ್ಯನಡಿಸುವವನೂ ಎಲ್ಲರಲ್ಲಿ ವಾಸಿಸುವವನೂ ಆಗಿದ್ದಾನೆ.",
			"7": "<sup>7</sup> ಆದರೆ ಕ್ರಿಸ್ತನು ನಮ್ಮಲ್ಲಿ ಒಬ್ಬೊಬ್ಬನಿಗೆ ಅನುಗ್ರಹಿಸಿದ ಪ್ರಕಾರವೇ ಅವನವನಿಗೆ ಕೃಪಾವರವು ದೊರಕಿದೆ.",
			"8": "<sup>8</sup> ಆದದರಿಂದ - ಆತನು ಉನ್ನತಸ್ಥಾನಕ್ಕೆ ಏರಿದಾಗ ತಾನು ಜಯಿಸಿದ್ದ ಬಹುಜನರನ್ನು ಸೆರೆಹಿಡುಕೊಂಡು ಹೋಗಿ ಮನುಷ್ಯರಿಗೆ ದಾನಗಳನ್ನು ಮಾಡಿದನು ಎಂಬದಾಗಿ ಪ್ರವಾದಿಯು ಹೇಳುತ್ತಾನೆ.",
			"9": "<sup>9</sup> (ಏರಿಹೋದನೆಂದು ಹೇಳಿದ್ದರಲ್ಲಿ ಭೂಮಿಯ ಅಧೋಭಾಗಕ್ಕೆ ಇಳಿದಿದ್ದನೆಂತಲೂ ಹೇಳಿದ ಹಾಗಾಯಿತಲ್ಲಾ.",
			"10": "<sup>10</sup> ಇಳಿದು ಬಂದವನು ಮೇಲಣ ಎಲ್ಲಾ ಲೋಕಗಳಿಗಿಂತ ಉನ್ನತವಾಗಿ ಏರಿಹೋದವನೇ. ಆದದರಿಂದ ಸಮಸ್ತ ಲೋಕಗಳನ್ನು ತುಂಬಿದವನಾದನು.)",
			"11": "<sup>11</sup> ಆತನು ಕೆಲವರನ್ನು ಅಪೊಸ್ತಲರನ್ನಾಗಿಯೂ ಕೆಲವರನ್ನು ಪ್ರವಾದಿಗಳನ್ನಾಗಿಯೂ ಕೆಲವರನ್ನು ಸೌವಾರ್ತಿಕರನ್ನಾಗಿಯೂ ಕೆಲವರನ್ನು ಸಭಾಪಾಲಕರನ್ನಾಗಿಯೂ ಉಪದೇಶಿಗಳನ್ನಾಗಿಯೂ ಅನುಗ್ರಹಿಸಿದನು.",
			"12": "<sup>12</sup> ದೇವಜನರನ್ನು ಯೋಗ್ಯಸ್ಥಿತಿಗೆ ತರುವ ಕೆಲಸಕ್ಕೋಸ್ಕರವೂ ಸಭೆಯ ಸೇವೆಗೋಸ್ಕರವೂ ಕ್ರಿಸ್ತನ ದೇಹವು ಅಭಿವೃದ್ಧಿಯಾಗುವದಕ್ಕೋಸ್ಕರವೂ ಆತನು ಇವರನ್ನು ಅನುಗ್ರಹಿಸಿದನು.",
			"13": "<sup>13</sup> ನಾವೆಲ್ಲರು ನಂಬಿಕೆಯಿಂದಲೂ ದೇವಕುಮಾರನ ವಿಷಯವಾದ ಜ್ಞಾನದಿಂದಲೂ ಉಂಟಾಗುವ ಐಕ್ಯವನ್ನು ಹೊಂದಿ ಪ್ರವೀಣತೆಗೆ ಬಂದವರಾಗಿ ಕ್ರಿಸ್ತನ ಪರಿಪೂರ್ಣತೆಯೆಂಬ ಪ್ರಮಾಣವನ್ನು ಮುಟ್ಟುವ ತನಕ",
			"14": "<sup>14</sup> ನಾವು ಇನ್ನು ಮೇಲೆ ಕೂಸುಗಳಾಗಿರಬಾರದು; ದುರ್ಜನರ ವಂಚನೆಗೂ ದುರ್ಬೋಧಕರ ಕುಯುಕ್ತಿಗೂ ಒಳಬಿದ್ದು ನಾನಾ ಉಪದೇಶಗಳಿಂದ ಕಂಗೆಟ್ಟು ಗಾಳಿಯಿಂದ ಅತ್ತಿತ್ತ ನೂಕಿಸಿಕೊಂಡು ಹೋಗುವವರ ಹಾಗಿರಬಾರದು.",
			"15": "<sup>15</sup> ಪ್ರೀತಿಯಿಂದ ಸತ್ಯವನ್ನನುಸರಿಸುತ್ತಾ ಬೆಳೆದು ಎಲ್ಲಾ ವಿಷಯಗಳಲ್ಲಿಯೂ ಕ್ರಿಸ್ತನ ಐಕ್ಯವನ್ನು ಹೊಂದುತ್ತಾ ಬರಬೇಕು.",
			"16": "<sup>16</sup> ಆತನೇ ಶಿರಸ್ಸು; ದೇಹವೆಲ್ಲಾ ಆತನ ದೊರೆತನದಲ್ಲಿದ್ದು ತನ್ನಲ್ಲಿರುವ ಎಲ್ಲಾ ನರಗಳಿಂದ ಬಿಗಿಯಾಗಿ ಜೋಡಿಸಲ್ಪಟ್ಟು ಪ್ರತಿ ಅಂಗದಿಂದ ಅದರದರ ಶಕ್ತಿಯ ಪ್ರಕಾರ ಸಹಾಯ ಹೊಂದಿ ಪ್ರೀತಿಯಿಂದ ಐಕ್ಯವಾಗಿದ್ದು ಕ್ಷೇಮಾಭಿವೃದ್ಧಿಯನ್ನು ಹೊಂದುತ್ತದೆ.",
			"17": "<sup>17</sup> ಆದದರಿಂದ ಅನ್ಯಜನರು ನಡೆದುಕೊಳ್ಳುವ ಪ್ರಕಾರ ನೀವು ಇನ್ನು ಮೇಲೆ ನಡೆದುಕೊಳ್ಳಬಾರದೆಂದು ಕರ್ತನಲ್ಲಿರುವವನಾಗಿ ನಿಮಗೆ ಖಂಡಿತವಾಗಿ ಹೇಳುತ್ತೇನೆ.",
			"18": "<sup>18</sup> ಅವರು ನಿಷ್ಪ್ರಯೋಜನವಾದ ಬುದ್ಧಿಯುಳ್ಳವರಾಗಿ ನಡೆದುಕೊಳ್ಳುತ್ತಾರೆ; ಅವರ ಮನಸ್ಸು ಮೊಬ್ಬಾಗಿ ಹೋಗಿದೆ, ಅವರು ತಮ್ಮ ಹೃದಯದ ಕಾಠಿಣ್ಯದ ನಿಮಿತ್ತದಿಂದಲೂ ತಮ್ಮಲ್ಲಿರುವ ಅಜ್ಞಾನದ ನಿಮಿತ್ತದಿಂದಲೂ ದೇವರಿಂದಾಗುವ ಜೀವಕ್ಕೆ ಅನ್ಯರಾಗಿದ್ದಾರೆ.",
			"19": "<sup>19</sup> ಅವರು ತಮ್ಮ ದುಃಸ್ಥಿತಿಗಾಗಿ ಸ್ವಲ್ಪವೂ ಚಿಂತಿಸದೆ ತಮ್ಮನ್ನು ಬಂಡುತನಕ್ಕೆ ಒಪ್ಪಿಸಿಕೊಟ್ಟು ಎಲ್ಲಾ ವಿಧವಾದ ಅಶುದ್ಧ ಕೃತ್ಯಗಳನ್ನು ಅತ್ಯಾಶೆಯಿಂದ ನಡಿಸುವವರಾಗಿದ್ದಾರೆ.",
			"20": "<sup>20</sup> ನೀವಾದರೋ ಕ್ರಿಸ್ತನ ಧರ್ಮವನ್ನು ಅಂಥದೆಂದು ಕಲಿತುಕೊಳ್ಳಲಿಲ್ಲ.",
			"21": "<sup>21</sup> ಆತನನ್ನೇ ಕೇಳಿದಿರಲ್ಲಾ, ಆತನಲ್ಲಿಯೇ ಉಪದೇಶವನ್ನು ಹೊಂದಿದಿರಲ್ಲಾ.",
			"22": "<sup>22</sup> ಯೇಸುವಿನಲ್ಲಿರುವ ಸತ್ಯೋಪದೇಶವು ಯಾವದಂದರೆ - ನೀವು ನಿಮ್ಮ ಹಿಂದಿನ ನಡತೆಯನ್ನು ಅನುಸರಿಸದೆ ಪೂರ್ವಸ್ವಭಾವವನ್ನು ತೆಗೆದುಹಾಕಿಬಿಡಬೇಕು; ಅದು ಮೋಸಕರವಾದ ದುರಾಶೆಗಳಿಂದ ಕೆಟ್ಟುಹೋಗುವಂಥದು.",
			"23": "<sup>23</sup> ನೀವು ನಿಮ್ಮ ಆಂತರ್ಯದಲ್ಲಿ ಹೊಸಬರಾಗಿ ನೂತನ ಸ್ವಭಾವವನ್ನು ಧರಿಸಿಕೊಳ್ಳಿರಿ.",
			"24": "<sup>24</sup> ಆ ಸ್ವಭಾವವು ದೇವರ ಹೋಲಿಕೆಯ ಮೇರೆಗೆ ಸತ್ಯಾನುಗುಣವಾದ ನೀತಿಯುಳ್ಳದ್ದಾಗಿಯೂ ದೇವಭಯವುಳ್ಳದ್ದಾಗಿಯೂ ನಿರ್ಮಿಸಲ್ಪಟ್ಟಿದೆ.",
			"25": "<sup>25</sup> ಆದಕಾರಣ ಸುಳ್ಳಾಡುವದನ್ನು ಬಿಟ್ಟುಬಿಟ್ಟು ಪ್ರತಿಯೊಬ್ಬನು ತನ್ನ ನೆರೆಯವನ ಸಂಗಡ ಸತ್ಯವನ್ನೇ ಆಡಲಿ; ಯಾಕಂದರೆ ನಾವು ಒಬ್ಬರಿಗೊಬ್ಬರು ಅಂಗಗಳಾಗಿದ್ದೇವಲ್ಲಾ.",
			"26": "<sup>26</sup> ಕೋಪಮಾಡಬೇಕಾದರೂ ಪಾಪಮಾಡಬೇಡಿರಿ; ಸೂರ್ಯನು ಮುಳುಗುವದಕ್ಕಿಂತ ಮುಂಚೆ ನಿಮ್ಮ ಸಿಟ್ಟು ತೀರಲಿ;",
			"27": "<sup>27</sup> ಸೈತಾನನಿಗೆ ಅವಕಾಶಕೊಡಬೇಡಿರಿ.",
			"28": "<sup>28</sup> ಕಳವು ಮಾಡುವವನು ಇನ್ನು ಮೇಲೆ ಕಳವುಮಾಡದೆ ಕೈಯಿಂದ ಯಾವದೊಂದು ಒಳ್ಳೇ ಉದ್ಯೋಗವನ್ನು ಮಾಡಿ ದುಡಿಯಲಿ; ಆಗ ಕಷ್ಟದಲ್ಲಿರುವವರಿಗೆ ಕೊಡುವದಕ್ಕೆ ಅವನಿಂದಾಗುವದು.",
			"29": "<sup>29</sup> ನಿಮ್ಮ ಬಾಯೊಳಗಿಂದ ಯಾವ ಕೆಟ್ಟ ಮಾತೂ ಹೊರಡಬಾರದು; ಭಕ್ತಿಯನ್ನು ವೃದ್ಧಿಮಾಡುವಂಥ ಕಾಲೋಚಿತವಾದ ಮಾತು ಇದ್ದರೆ ಕೇಳುವವರ ಹಿತಕ್ಕಾಗಿ ಅದನ್ನು ಆಡಿರಿ.",
			"30": "<sup>30</sup> ದೇವರ ಪವಿತ್ರಾತ್ಮನನ್ನು ದುಃಖಪಡಿಸಬೇಡಿರಿ; ಆತನಲ್ಲಿಯೇ ವಿಮೋಚನೆಯ ದಿನಕ್ಕಾಗಿ ಮುದ್ರೆ ಹೊಂದಿದ್ದೀರಲ್ಲಾ.",
			"31": "<sup>31</sup> ಎಲ್ಲಾ ದ್ವೇಷ ಕೋಪ ಕ್ರೋಧ ಕಲಹ ದೂಷಣೆ ಇವುಗಳನ್ನೂ ಸಕಲವಿಧವಾದ ದುಷ್ಟತನವನ್ನೂ ನಿಮ್ಮಿಂದ ದೂರಮಾಡಿರಿ.",
			"32": "<sup>32</sup> ಒಬ್ಬರಿಗೊಬ್ಬರು ಉಪಕಾರಿಗಳಾಗಿಯೂ ಕರುಣೆಯುಳ್ಳವರಾಗಿಯೂ ಕ್ಷಮಿಸುವವರಾಗಿಯೂ ಇರ್ರಿ."
		},
		"5": {
			"1": "<sup>1</sup> ದೇವರು ನಿಮಗೆ ಕ್ರಿಸ್ತನಲ್ಲಿ ಕ್ಷಮಿಸಿದನಲ್ಲಾ; ಆದದರಿಂದ ದೇವರ ಪ್ರಿಯರಾದ ಮಕ್ಕಳಿಗೆ ತಕ್ಕ ಹಾಗೆ ಆತನನ್ನು ಅನುಸರಿಸುವವರಾಗಿರಿ.",
			"2": "<sup>2</sup> ಕ್ರಿಸ್ತನು ನಿಮ್ಮನ್ನು ಪ್ರೀತಿಸಿ ನಮ್ಮೆಲ್ಲರಿಗೋಸ್ಕರ ತನ್ನನ್ನೇ ದೇವರಿಗೆ ಸುಗಂಧವಾಸನೆಯಾದ ಕಾಣಿಕೆಯಾಗಿಯೂ ಯಜ್ಞವಾಗಿಯೂ ಸಮರ್ಪಿಸಿಕೊಂಡ ಪ್ರಕಾರ ನೀವೂ ಪ್ರೀತಿಯಲ್ಲಿ ನಡೆದುಕೊಳ್ಳಿರಿ.",
			"3": "<sup>3</sup> ಜಾರತ್ವ ಯಾವ ವಿಧವಾದ ಬಂಡುತನ ದ್ರವ್ಯಾಶೆ ಇವುಗಳ ಸುದ್ದಿಯಾದರೂ ನಿಮ್ಮಲ್ಲಿ ಇರಬಾರದು; ಇವುಗಳಿಗೆ ದೂರವಾಗಿರುವದೇ ದೇವಜನರಿಗೆ ಯೋಗ್ಯವಾದದ್ದು.",
			"4": "<sup>4</sup> ಹೊಲಸು ಮಾತು ಹುಚ್ಚು ಮಾತು ಕುಚೋದ್ಯ ಮಾತು ಇವು ಬೇಡ, ಅಯುಕ್ತವಾಗಿವೆ;",
			"5": "<sup>5</sup> ಇವುಗಳನ್ನು ಬಿಟ್ಟು ದೇವರಿಗೆ ಉಪಕಾರಸ್ತುತಿ ಮಾಡುವದು ಉತ್ತಮ. ಜಾರರು ದುರಾಚಾರಿಗಳು ವಿಗ್ರಹಾರಾಧಕರಂತಿರುವ ಲೋಭಿಗಳು ಇವರಲ್ಲಿ ಒಬ್ಬರಿಗಾದರೂ ಕ್ರಿಸ್ತನ ಮತ್ತು ದೇವರ ರಾಜ್ಯದಲ್ಲಿ ಬಾಧ್ಯತೆ ಇಲ್ಲವೇ ಇಲ್ಲವೆಂದು ನೀವು ಚೆನ್ನಾಗಿ ಬಲ್ಲಿರಲ್ಲವೇ.",
			"6": "<sup>6</sup> ಹುರುಳಿಲ್ಲದ ಮಾತುಗಳನ್ನಾಡುವವರಿಗೆ ಕಿವಿಗೊಟ್ಟು ಮೋಸಹೋಗಬೇಡಿರಿ; ಅಂಥ ಕೃತ್ಯಗಳ ನಿಮಿತ್ತದಿಂದ ದೇವರ ಕೋಪವು ಆತನಿಗೆ ಅವಿಧೇಯರಾಗಿರುವವರ ಮೇಲೆ ಬರುತ್ತದೆ.",
			"7": "<sup>7</sup> ನೀವು ಅವರೊಂದಿಗೆ ಪಾಲುಗಾರರಾಗಬೇಡಿರಿ.",
			"8": "<sup>8</sup> ನೀವು ಹಿಂದಿನ ಕಾಲದಲ್ಲಿ ಕತ್ತಲೆಯಾಗಿದ್ದಿರಿ, ಆದರೆ ಈಗ ನೀವು ಕ್ರಿಸ್ತನಲ್ಲಿದ್ದು ಬೆಳಕಾಗಿದ್ದೀರಿ.",
			"9": "<sup>9</sup> ಬೆಳಕಿನವರಂತೆ ನಡೆದುಕೊಳ್ಳಿರಿ. ಬೆಳಕಿನ ಫಲವು ಉಪಕಾರದಲ್ಲಿಯೂ ನೀತಿಯಲ್ಲಿಯೂ ಸತ್ಯದಲ್ಲಿಯೂ ಕಾಣಿಸಿಕೊಳ್ಳುತ್ತದೆ.",
			"10": "<sup>10</sup> ಕರ್ತನಿಗೆ ಮೆಚ್ಚಿಗೆಯಾದದ್ದು ಏನೆಂಬದನ್ನು ಪರಿಶೋಧಿಸಿ ತಿಳುಕೊಳ್ಳಿರಿ.",
			"11": "<sup>11</sup> ಕತ್ತಲೆಗೆ ಸಂಬಂಧವಾದ ಕೃತ್ಯಗಳಿಂದ ಯಾವ ಪ್ರಯೋಜನವೂ ಬರಲಾರದು; ಅವುಗಳಲ್ಲಿ ಪಾಲುಗಾರರಾಗಿರದೆ ಅವುಗಳನ್ನು ಬೈಲಿಗೆ ತಂದು ಖಂಡಿಸಿರಿ.",
			"12": "<sup>12</sup> ಆ ಜನರು ರಹಸ್ಯವಾಗಿ ನಡಿಸುವ ಕೆಲಸಗಳನ್ನು ಕುರಿತು ಮಾತಾಡುವದಾದರೂ ನಾಚಿಕೆಗೆ ಕಾರಣವಾಗಿದೆ.",
			"13": "<sup>13</sup> ಬೆಳಕಿನಿಂದ ಎಲ್ಲಾ ಕೃತ್ಯಗಳ ನಿಜಗುಣ ತೋರಿಬರುತ್ತದೆ; ಅದರಿಂದ ಕೆಟ್ಟದ್ದು ಕೆಟ್ಟದ್ದೇ ಎಂದು ಕಾಣಿಸುವದು. ಪ್ರಕಟವಾಗಿರುವದೆಲ್ಲಾ ಬೆಳಕಿಗೆ ಸಂಬಂಧವಾದದ್ದು.",
			"14": "<sup>14</sup> ಆದದರಿಂದ - ನಿದ್ರೆ ಮಾಡುವವನೇ, ಎಚ್ಚರವಾಗು! ಸತ್ತವರನ್ನು ಬಿಟ್ಟು ಏಳು! ಕ್ರಿಸ್ತನು ನಿನಗೆ ಪ್ರಕಾಶ ಕೊಡುವನು ಎಂದು ಹೇಳಿಯದೆ.",
			"15": "<sup>15</sup> ಆದಕಾರಣ ನೀವು ನಡಕೊಳ್ಳುವ ರೀತಿಯನ್ನು ಕುರಿತು ಚೆನ್ನಾಗಿ ನೋಡಿಕೊಳ್ಳಿರಿ. ಜ್ಞಾನವಿಲ್ಲದರಾಗಿರದೆ ಜ್ಞಾನವಂತರಾಗಿರ್ರಿ.",
			"16": "<sup>16</sup> ಈ ದಿನಗಳು ಕೆಟ್ಟವುಗಳಾಗಿವೆ; ಆದದರಿಂದ ಕಾಲವನ್ನು ಸುಮ್ಮನೆ ಕಳಕೊಳ್ಳದೆ ಅದನ್ನು ಬೆಲೆಯುಳ್ಳದ್ದೆಂದು ಉಪಯೋಗಿಸಿಕೊಳ್ಳಿರಿ.",
			"17": "<sup>17</sup> ಮತ್ತು ಬುದ್ಧಿಹೀನರಾಗಿ ನಡೆಯದೆ ಕರ್ತನ ಚಿತ್ತವೇನೆಂಬದನ್ನು ವಿಚಾರಿಸಿ ತಿಳಿದವರಾಗಿರ್ರಿ.",
			"18": "<sup>18</sup> ಮದ್ಯಪಾನ ಮಾಡಿ ಮತ್ತರಾಗಬೇಡಿರಿ; ಅದರಿಂದ ಪಟಿಂಗತನವು ಹುಟ್ಟುತ್ತದೆ.",
			"19": "<sup>19</sup> ಆದರೆ ಪವಿತ್ರಾತ್ಮಭರಿತರಾಗಿದ್ದು ಕೀರ್ತನೆಗಳಿಂದಲೂ ಆತ್ಮಸಂಬಂಧವಾದ ಪದಗಳಿಂದಲೂ ಒಬ್ಬರಿಗೊಬ್ಬರು ಮಾತಾಡಿಕೊಳ್ಳುತ್ತಾ ನಿಮ್ಮ ಹೃದಯಗಳಲ್ಲಿ ಕರ್ತನಿಗೆ ಗಾನಮಾಡುತ್ತಾ ಕೀರ್ತನೆ ಹಾಡುತ್ತಾ",
			"20": "<sup>20</sup> ಯಾವಾಗಲೂ ಎಲ್ಲಾ ಕಾರ್ಯಗಳಿಗೋಸ್ಕರ ನಮ್ಮ ಕರ್ತನಾದ ಯೇಸು ಕ್ರಿಸ್ತನ ಹೆಸರಿನಲ್ಲಿ ತಂದೆಯಾದ ದೇವರಿಗೆ ಸ್ತೋತ್ರ ಮಾಡುತ್ತಾ ಕ್ರಿಸ್ತನಿಗೆ ಭಯಪಡುವವರಾಗಿದ್ದು",
			"21": "<sup>21</sup> ಒಬ್ಬರಿಗೊಬ್ಬರು ವಿನಯವುಳ್ಳವರಾಗಿ ನಡೆದುಕೊಳ್ಳಿರಿ.",
			"22": "<sup>22</sup> ಸ್ತ್ರೀಯರೇ, ನೀವು ಕರ್ತನಿಗೆ ಹೇಗೋ ಹಾಗೆಯೇ ನಿಮ್ಮನಿಮ್ಮ ಗಂಡಂದಿರಿಗೆ ಅಧೀನರಾಗಿರ್ರಿ.",
			"23": "<sup>23</sup> ಕ್ರಿಸ್ತನು ಸಭೆಗೆ ತಲೆಯಾಗಿರುವ ಪ್ರಕಾರವೇ ಗಂಡನು ಹೆಂಡತಿಗೆ ತಲೆಯಾಗಿದ್ದಾನೆ. ಕ್ರಿಸ್ತನೋ ಸಭೆಯೆಂಬ ದೇಹಕ್ಕೆ ರಕ್ಷಕನಾಗಿದ್ದಾನೆ.",
			"24": "<sup>24</sup> ಅದಿರಲಿ; ಸಭೆಯು ಕ್ರಿಸ್ತನಿಗೆ ಹೇಗೆ ಅಧೀನವಾಗಿದೆಯೋ ಹಾಗೆಯೇ ಸ್ತ್ರೀಯರು ತಮ್ಮತಮ್ಮ ಗಂಡಂದಿರಿಗೆ ಎಲ್ಲಾ ವಿಷಯಗಳಲ್ಲಿ ಅಧೀನರಾಗಿರಬೇಕು.",
			"25": "<sup>25</sup> ಪುರುಷರೇ, ಕ್ರಿಸ್ತನು ಸಭೆಯನ್ನು ಪ್ರೀತಿಸಿದ ಪ್ರಕಾರವೇ ನಿಮ್ಮನಿಮ್ಮ ಹೆಂಡತಿಯರನ್ನು ಪ್ರೀತಿಸಿರಿ.",
			"26": "<sup>26</sup> ಆತನು ಅದನ್ನು ಪ್ರತಿಷ್ಠೆಪಡಿಸುವದಕ್ಕಾಗಿ ತನ್ನನ್ನು ಒಪ್ಪಿಸಿಕೊಟ್ಟನು.",
			"27": "<sup>27</sup> ಅದನ್ನು ಕಳಂಕ ಸುಕ್ಕು ಮುಂತಾದ್ದೊಂದೂ ಇಲ್ಲದ ಕನ್ನಿಕೆಯೋ ಎಂಬಂತೆ ಪರಿಶುದ್ಧವೂ ನಿರ್ದೋಷವೂ ಮಹಿಮೆಯುಳ್ಳದ್ದೂ ಆಗಿರುವ ಸಭೆಯನ್ನಾಗಿ ತನ್ನೆದುರಿನಲ್ಲಿ ನಿಲ್ಲಿಸಿಕೊಳ್ಳಬೇಕೆಂದು ವಾಕ್ಯೋಪದೇಶ ಸಹಿತವಾದ ಜಲಸ್ನಾನವನ್ನು ಮಾಡಿಸಿ ಶುದ್ಧಮಾಡಿದನು.",
			"28": "<sup>28</sup> ಹಾಗೆಯೇ ಪುರುಷರು ಸಹ ಸ್ವಂತ ಶರೀರವನ್ನು ಪ್ರೀತಿಸಿಕೊಳ್ಳುವ ಪ್ರಕಾರವೇ ತಮ್ಮ ಹೆಂಡತಿಯರನ್ನು ಪ್ರೀತಿಸುವ ಹಂಗಿನವರಾಗಿದ್ದಾರೆ. ತನ್ನ ಹೆಂಡತಿಯನ್ನು ಪ್ರೀತಿಸುವವನು ತನ್ನನ್ನೇ ಪ್ರೀತಿಸಿಕೊಳ್ಳುವವನಾಗಿದ್ದಾನೆ.",
			"29": "<sup>29</sup> ಯಾರೂ ಎಂದೂ ಸ್ವಶರೀರವನ್ನು ಹಗೆಮಾಡಿದ್ದಿಲ್ಲ; ಎಲ್ಲರೂ ತಮ್ಮ ಶರೀರಗಳನ್ನು ಪೋಷಿಸಿ ಸಂರಕ್ಷಿಸುತ್ತಾರೆ.",
			"30": "<sup>30</sup> ಯಾವ ದೇಹಕ್ಕೆ ನಾವು ಅಂಗಗಳಾಗಿದ್ದೇವೋ ಸಭೆಯೆಂಬ ಆ ದೇಹವನ್ನು ಕ್ರಿಸ್ತನು ಹಾಗೆಯೇ ಪೋಷಿಸಿ ಸಂರಕ್ಷಿಸುತ್ತಾನಲ್ಲಾ.",
			"31": "<sup>31</sup> ಇದರ ನಿಮಿತ್ತದಿಂದ ಪುರುಷನು ತಂದೆತಾಯಿಗಳನ್ನು ಬಿಟ್ಟು ತನ್ನ ಹೆಂಡತಿಯನ್ನು ಸೇರಿಕೊಳ್ಳುವನು; ಅವರಿಬ್ಬರೂ ಒಂದೇ ಶರೀರವಾಗಿರುವರು ಎಂದು ಬರೆದದೆ.",
			"32": "<sup>32</sup> ನಾನು ಕ್ರಿಸ್ತನಿಗೂ ಸಭೆಗೂ ಇರುವ ಸಂಬಂಧವನ್ನು ಕುರಿತು ಮಾತಾಡುತ್ತಾ ಇದ್ದೇನೆ; ಇದುವರೆಗೆ ಗುಪ್ತವಾಗಿದ್ದ ಈ ಸತ್ಯಾರ್ಥವು ಬಹು ಗಂಭೀರವಾದದ್ದು.",
			"33": "<sup>33</sup> ಅದಿರಲಿ; ನಿಮ್ಮಲ್ಲಿ ಪ್ರತಿಪುರುಷನು ತನ್ನನ್ನು ಪ್ರೀತಿಸಿಕೊಳ್ಳುವಂತೆಯೇ ತನ್ನ ಹೆಂಡತಿಯನ್ನೂ ಪ್ರೀತಿಸಬೇಕು; ಪ್ರತಿ ಹೆಂಡತಿಯು ತನ್ನ ಗಂಡನಿಗೆ ಭಯಭಕ್ತಿಯಿಂದ ನಡೆದುಕೊಳ್ಳಬೇಕು."
		},
		"6": {
			"1": "<sup>1</sup> ಮಕ್ಕಳೇ, ನೀವು ಕರ್ತನಲ್ಲಿರುವವರಿಗೆ ತಕ್ಕ ಹಾಗೆ ನಿಮ್ಮ ತಂದೆತಾಯಿಗಳ ಮಾತನ್ನು ಕೇಳಬೇಕು; ಇದು ಧರ್ಮ.",
			"2": "<sup>2</sup> ವಾಗ್ದಾನ ಸಹಿತವಾದ ಮೊದಲನೆಯ ಆಜ್ಞೆಯನ್ನು ಕೇಳಿರಿ - ನಿನ್ನ ತಂದೆತಾಯಿಗಳನ್ನು ಸನ್ಮಾನಿಸಬೇಕು;",
			"3": "<sup>3</sup> ಸನ್ಮಾನಿಸಿದರೆ ನಿನಗೆ ಮೇಲಾಗುವದು, ನೀನು ಭೂಮಿಯ ಮೇಲೆ ಬಹುಕಾಲ ಬದುಕುವಿ.",
			"4": "<sup>4</sup> ತಂದೆಗಳೇ, ನಿಮ್ಮ ಮಕ್ಕಳಿಗೆ ಕೋಪವನ್ನೆಬ್ಬಿಸದೆ ಕರ್ತನಿಗೆ ಮೆಚ್ಚಿಗೆಯಾಗಿರುವ ಬಾಲಶಿಕ್ಷೆಯನ್ನೂ ಬಾಲೋಪದೇಶವನ್ನೂ ಮಾಡುತ್ತಾ ಅವರನ್ನು ಸಾಕಿ ಸಲಹಿರಿ.",
			"5": "<sup>5</sup> ದಾಸತ್ವದಲ್ಲಿರುವವರೇ, ಈ ಲೋಕದಲ್ಲಿ ನಿಮಗೆ ಯಜಮಾನರಾಗಿರುವವರಿಗೆ ಕ್ರಿಸ್ತನಿಗೆಂದು ಮನೋಭೀತಿಯಿಂದ ನಡುಗುವವರಾಗಿಯೂ ಸರಳಹೃದಯರಾಗಿಯೂ ವಿಧೇಯರಾಗಿರ್ರಿ.",
			"6": "<sup>6</sup> ಮನುಷ್ಯರನ್ನು ಮೆಚ್ಚಿಸುವವರು ಮಾಡುವ ಪ್ರಕಾರ ಯಜಮಾನರು ನೋಡುತ್ತಿರುವಾಗ ಮಾತ್ರ ಸೇವೆಮಾಡದೆ ಕ್ರಿಸ್ತನ ದಾಸರಿಗೆ ತಕ್ಕ ಹಾಗೆ ದೇವರ ಚಿತ್ತವನ್ನು ಮನಃಪೂರ್ವಕವಾಗಿ ನಡಿಸಿರಿ.",
			"7": "<sup>7</sup> ಮನುಷ್ಯರಿಗೋಸ್ಕರವಲ್ಲ, ಕರ್ತನಿಗೋಸ್ಕರ ಸೇವೆಮಾಡುತ್ತೇವೆಂದು ಚಿತ್ತಪೂರ್ವಕವಾಗಿ ಸೇವೆಮಾಡಿರಿ.",
			"8": "<sup>8</sup> ಯಾಕಂದರೆ ಒಬ್ಬನು ದಾಸನಾಗಲಿ ಸ್ವತಂತ್ರನಾಗಲಿ ತಾನು ಯಾವ ಸತ್ಕಾರ್ಯವನ್ನು ಮಾಡುತ್ತಾನೋ ಅದರ ಪ್ರತಿಫಲವನ್ನು ಕರ್ತನಿಂದ ಹೊಂದುವನೆಂಬದನ್ನು ನೀವು ತಿಳಿದವರಾಗಿಯೇ ಇದ್ದೀರಿ.",
			"9": "<sup>9</sup> ಯಜಮಾನರೇ, ನಿಮ್ಮ ಕೈಕೆಳಗಿರುವ ದಾಸರ ವಿಷಯದಲ್ಲಿ ಅದೇ ರೀತಿಯಾಗಿ ನಡೆಯಿರಿ. ಪರಲೋಕದಲ್ಲಿ ನಿಮಗೂ ಅವರಿಗೂ ಯಜಮಾನನಾಗಿರುವಾತನು ಇದ್ದಾನೆಂತಲೂ ಆತನಲ್ಲಿ ಪಕ್ಷಪಾತವಿಲ್ಲವೆಂತಲೂ ತಿಳಿದು ನಿಮ್ಮ ದಾಸರನ್ನು ಬೆದರಿಸುವ ಪದ್ಧತಿಯನ್ನು ಬಿಟ್ಟುಬಿಡಿರಿ.",
			"10": "<sup>10</sup> ಕಡೇ ಮಾತೇನಂದರೆ, ನೀವು ಕರ್ತನನ್ನೂ ಆತನ ಅತ್ಯಧಿಕವಾದ ಶಕ್ತಿಯನ್ನೂ ಆಶ್ರಯಿಸಿಕೊಂಡವರಾಗಿ ಬಲಗೊಳ್ಳಿರಿ.",
			"11": "<sup>11</sup> ಸೈತಾನನ ತಂತ್ರೋಪಾಯಗಳನ್ನು ನೀವು ಎದುರಿಸಿ ನಿಲ್ಲುವದಕ್ಕೆ ಶಕ್ತರಾಗುವಂತೆ ದೇವರು ದಯಪಾಲಿಸುವ ಸರ್ವಾಯುಧಗಳನ್ನು ಧರಿಸಿಕೊಳ್ಳಿರಿ.",
			"12": "<sup>12</sup> ನಾವು ಹೋರಾಡುವದು ಮನುಷ್ಯ ಮಾತ್ರದವರ ಸಂಗಡವಲ್ಲ; ರಾಜತ್ವಗಳ ಮೇಲೆಯೂ ಅಧಿಕಾರಿಗಳ ಮೇಲೆಯೂ ಈ ಅಂಧಕಾರದ ಲೋಕಾಧಿಪತಿಗಳ ಮೇಲೆಯೂ ಆಕಾಶಮಂಡಲದಲ್ಲಿರುವ ದುರಾತ್ಮಗಳ ಸೇನೆಯ ಮೇಲೆಯೂ ನಾವು ಹೋರಾಡುವವರಾಗಿದ್ದೇವೆ.",
			"13": "<sup>13</sup> ಆದದರಿಂದ ಕಠಿನ ಯುದ್ಧವು ನಡೆಯುವ ದಿವಸದಲ್ಲಿ ಆ ವೈರಿಗಳನ್ನು ಎದುರಿಸುವದಕ್ಕೂ ಮಾಡಬೇಕಾದದ್ದೆಲ್ಲವನ್ನು ಮಾಡಿ ಜಯಶಾಲಿಗಳಾಗಿ ನಿಲ್ಲುವದಕ್ಕೂ ಶಕ್ತರಾಗುವಂತೆ ದೇವರು ದಯಪಾಲಿಸುವ ಸರ್ವಾಯುಧಗಳನ್ನು ತೆಗೆದುಕೊಳ್ಳಿರಿ.",
			"14": "<sup>14</sup> ಸತ್ಯವೆಂಬ ನಡುಕಟ್ಟನ್ನು ಕಟ್ಟಿಕೊಂಡು ನೀತಿಯೆಂಬ ವಜ್ರಕವಚವನ್ನು ಧರಿಸಿಕೊಂಡು",
			"15": "<sup>15</sup> ಸಮಾಧಾನದ ವಿಷಯವಾದ ಸುವಾರ್ತೆಯನ್ನು ತಿಳಿಸುವದರಲ್ಲಿ ಸಿದ್ಧವಾದ ಮನಸ್ಸೆಂಬ ಕೆರಗಳನ್ನು ಮೆಟ್ಟಿಕೊಂಡು ನಿಲ್ಲಿರಿ.",
			"16": "<sup>16</sup> ಮತ್ತು ನಂಬಿಕೆಯೆಂಬ ಗುರಾಣಿಯನ್ನು ಹಿಡುಕೊಳ್ಳಿರಿ; ಅದರಿಂದ ನೀವು ಕೆಡುಕನ ಅಗ್ನಿಬಾಣಗಳನ್ನೆಲ್ಲಾ ಆರಿಸುವದಕ್ಕೆ ಶಕ್ತರಾಗುವಿರಿ.",
			"17": "<sup>17</sup> ಇದಲ್ಲದೆ ರಕ್ಷಣೆಯೆಂಬ ಶಿರಸ್ತ್ರಾಣವನ್ನು ಇಟ್ಟುಕೊಂಡು ಪವಿತ್ರಾತ್ಮನು ಕೊಡುವ ದೇವರ ವಾಕ್ಯವೆಂಬ ಕತ್ತಿಯನ್ನು ಹಿಡಿಯಿರಿ.",
			"18": "<sup>18</sup> ನೀವು ಪವಿತ್ರಾತ್ಮಪ್ರೇರಿತರಾಗಿ ಎಲ್ಲಾ ಸಮಯಗಳಲ್ಲಿ ಸಕಲವಿಧವಾದ ಪ್ರಾರ್ಥನೆಯಿಂದಲೂ ವಿಜ್ಞಾಪನೆಯಿಂದಲೂ ದೇವರನ್ನು ಪ್ರಾರ್ಥಿಸಿರಿ. ಇದರಲ್ಲಿ ಪೂರ್ಣ ಸ್ಥಿರಚಿತ್ತರಾಗಿದ್ದು ದೇವಜನರೆಲ್ಲರ ವಿಷಯದಲ್ಲಿ ವಿಜ್ಞಾಪನೆಮಾಡುತ್ತಾ ಎಚ್ಚರವಾಗಿರ್ರಿ.",
			"19": "<sup>19</sup> ನನಗೋಸ್ಕರ ಸಹ ಪ್ರಾರ್ಥನೆಮಾಡಿರಿ. ನಾನು ಬಾಯಿ ತೆರೆಯುವಾಗ ಪೂರ್ವಕಾಲದಲ್ಲಿ ಗುಪ್ತವಾಗಿದ್ದ ಸುವಾರ್ತಾಸತ್ಯಾರ್ಥವನ್ನು ಭಯವಿಲ್ಲದೆ ತಿಳಿಸುವದಕ್ಕೆ ಬೇಕಾದ ಮಾತನ್ನು ದೇವರು ನನಗೆ ಅನುಗ್ರಹಿಸಬೇಕೆಂದು ಪ್ರಾರ್ಥಿಸಿರಿ.",
			"20": "<sup>20</sup> ಆ ಸತ್ಯಾರ್ಥದ ನಿಮಿತ್ತವೇ ರಾಯಭಾರಿಯಾದ ನಾನು ಬೇಡಿಯಲ್ಲಿ ಬಿದ್ದಿದ್ದೇನಲ್ಲಾ, ಅದರ ವಿಷಯದಲ್ಲಿ ಧೈರ್ಯವಾಗಿ ಮಾತಾಡುವ ಹಂಗಿನಲ್ಲಿದ್ದೇನೆ; ಹಾಗೆಯೇ ನಾನು ಮಾತಾಡಬೇಕೆಂದು ನನಗೋಸ್ಕರ ಬೇಡಿಕೊಳ್ಳಿರಿ.",
			"21": "<sup>21</sup> ನನ್ನ ಸಂಗತಿಗಳು ನಿಮಗೆ ಸಹ ಗೊತ್ತಾಗುವ ಹಾಗೆ ಪ್ರಿಯ ಸಹೋದರನೂ ಕರ್ತನಲ್ಲಿ ನಂಬಿಗಸ್ತನಾದ ಸೇವಕನೂ ಆಗಿರುವ ತುಖಿಕನು ನಿಮಗೆ ಎಲ್ಲವನ್ನೂ ತಿಳಿಸುವನು.",
			"22": "<sup>22</sup> ನೀವು ನಮ್ಮ ಸಮಾಚಾರವನ್ನು ತಿಳುಕೊಳ್ಳುವ ಹಾಗೆಯೂ ಅವನು ನಿಮ್ಮ ಹೃದಯಗಳನ್ನು ಸಂತೈಸುವ ಹಾಗೆಯೂ ಅವನನ್ನು ನಿಮ್ಮ ಬಳಿಗೆ ಕಳುಹಿಸಿದ್ದೇನೆ.",
			"23": "<sup>23</sup> ತಂದೆಯಾದ ದೇವರಿಂದಲೂ ಕರ್ತನಾದ ಯೇಸು ಕ್ರಿಸ್ತನಿಂದಲೂ ಶಾಂತಿಯೂ ನಂಬಿಕೆಯಿಂದ ಕೂಡಿದ ಪ್ರೀತಿಯೂ ಸಹೋದರರಿಗೆ ಆಗಲಿ.",
			"24": "<sup>24</sup> ನಮ್ಮ ಕರ್ತನಾದ ಯೇಸು ಕ್ರಿಸ್ತನನ್ನು ನಿರಂತರವಾದ ಪ್ರೀತಿಯಿಂದ ಪ್ರೀತಿಸುವವರೆಲ್ಲರ ಮೇಲೆ ದೇವರ ಕೃಪೆಯು ಇರಲಿ."
		}
	}
};
module.exports = book;