var book = {
	"name": "ಕೊಲೊಸ್ಸೆಯವರಿಗೆ",
	"numChapters": 4,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ದೇವರ ಚಿತ್ತಾನುಸಾರವಾಗಿ ಕ್ರಿಸ್ತ ಯೇಸುವಿನ ಅಪೊಸ್ತಲನಾದ ಪೌಲನೂ ಸಹೋದರನಾದ ತಿಮೊಥೆಯನೂ",
			"2": "<sup>2</sup> ಕೊಲೊಸ್ಸೆಯಲ್ಲಿರುವ ದೇವಜನರಿಗೆ ಅಂದರೆ ಕ್ರಿಸ್ತನಲ್ಲಿರುವ ನಂಬಿಗಸ್ತರಾದ ಸಹೋದರರಿಗೆ ಬರೆಯುವದೇನಂದರೆ - ನಮ್ಮ ತಂದೆಯಾದ ದೇವರಿಂದ ನಿಮಗೆ ಕೃಪೆಯೂ ಶಾಂತಿಯೂ ಆಗಲಿ.",
			"3": "<sup>3</sup> ಕ್ರಿಸ್ತ ಯೇಸುವಿನಲ್ಲಿ ನೀವು ಇಟ್ಟ ನಂಬಿಕೆಯ ವಿಷಯವಾಗಿಯೂ ದೇವಜನರ ಮೇಲಿರುವ ನಿಮ್ಮ ಪ್ರೀತಿಯ ವಿಷಯವಾಗಿಯೂ ನಾವು ಕೇಳಿ ಪರಲೋಕದಲ್ಲಿ ನಿಮಗೋಸ್ಕರ ಸಿದ್ಧವಾಗಿರುವ ಮಹಾಪದವಿಯನ್ನು ನೆನಸುತ್ತಾ ನಾವು ಪ್ರಾರ್ಥನೆಮಾಡುವಾಗೆಲ್ಲಾ ನಿಮ್ಮ ನಿಮಿತ್ತವಾಗಿ ನಮ್ಮ ಕರ್ತನಾದ ಯೇಸು ಕ್ರಿಸ್ತನ ತಂದೆಯಾಗಿರುವ ದೇವರಿಗೆ ಸ್ತೋತ್ರಮಾಡುತ್ತೇವೆ. ನೀವು ಆ ಪದವಿಯನ್ನು ಕುರಿತು ನಿಮ್ಮ ಬಳಿಗೆ ಬಂದಿರುವ ಸುವಾರ್ತೆಯ ಸತ್ಯವಾಕ್ಯದಿಂದ ಮೊದಲೇ ಕೇಳಿದಿರಿ.",
			"6": "<sup>6</sup> ನೀವು ದೇವರ ಕೃಪೆಯನ್ನು ಕುರಿತು ಸತ್ಯಾರ್ಥವನ್ನು ಕೇಳಿ ತಿಳುಕೊಂಡ ದಿವಸದಿಂದ ಆ ಸುವಾರ್ತೆಯು ನಿಮ್ಮಲ್ಲಿ ಫಲಕೊಟ್ಟು ವೃದ್ಧಿಯಾಗುತ್ತಾ ಬಂದ ಪ್ರಕಾರ ಲೋಕದಲ್ಲೆಲ್ಲಾ ಹಬ್ಬಿ ಫಲಕೊಟ್ಟು ವೃದ್ಧಿಯಾಗುತ್ತಾ ಬರುತ್ತದೆ.",
			"7": "<sup>7</sup> ನಮ್ಮ ಪ್ರಿಯ ಜೊತೆಯ ದಾಸನಾದ ಎಪಫ್ರನಿಂದ ಆ ಸತ್ಯೋಪದೇಶವನ್ನು ಕಲಿತುಕೊಂಡಿರಿ. ಅವನು ನಿಮಗೋಸ್ಕರ ಕ್ರಿಸ್ತನ ನಂಬಿಗಸ್ತನಾದ ಸೇವಕನು.",
			"8": "<sup>8</sup> ಪವಿತ್ರಾತ್ಮಪ್ರೇರಿತವಾದ ನಿಮ್ಮ ಪ್ರೀತಿಯ ವಿಷಯದಲ್ಲಿ ನನಗೆ ತಿಳಿಸಿದವನು ಅವನೇ.",
			"9": "<sup>9</sup> ಆದಕಾರಣ ನಾವು ನಿಮ್ಮ ಸುದ್ದಿಯನ್ನು ಕೇಳಿದ ದಿವಸದಿಂದ ನಿಮಗೋಸ್ಕರ ಪ್ರಾರ್ಥಿಸುವದನ್ನು ಬಿಡದೆ ನೀವು ಸಕಲ ಆತ್ಮೀಯ ಜ್ಞಾನವನ್ನೂ ಗ್ರಹಿಕೆಯನ್ನೂ ಹೊಂದಿ",
			"10": "<sup>10</sup> ಕರ್ತನ ಚಿತ್ತದ ವಿಷಯವಾದ ತಿಳುವಳಿಕೆಯಿಂದ ತುಂಬಿಕೊಂಡು ಆತನಿಗೆ ಯೋಗ್ಯರಾಗಿ ನಡೆದು ಎಲ್ಲಾ ವಿಧದಲ್ಲಿ ಆತನನ್ನು ಸಂತೋಷಪಡಿಸುವವರಾಗಿರಬೇಕೆಂತಲೂ ನೀವು ಸಕಲ ಸತ್ಕಾರ್ಯವೆಂಬ ಫಲವನ್ನು ಕೊಡುತ್ತಾ ದೇವಜ್ಞಾನದಿಂದ ಅಭಿವೃದ್ಧಿಯಾಗುತ್ತಿರಬೇಕೆಂತಲೂ",
			"11": "<sup>11</sup> ಆತನ ಮಹಿಮಶಕ್ತಿಯ ಪ್ರಕಾರ ಪರಿಪೂರ್ಣ ಬಲಹೊಂದಿ ಬಲಿಷ್ಠರಾಗಿ ಆನಂದಪೂರ್ವಕವಾದ ತಾಳ್ಮೆಯನ್ನೂ ದೀರ್ಘಶಾಂತಿಯನ್ನೂ ಯಾವಾಗಲೂ ತೋರಿಸುವವರಾಗಿರಬೇಕೆಂತಲೂ",
			"12": "<sup>12</sup> ಬೆಳಕಿನ ರಾಜ್ಯದೊಳಗೆ ದೇವಜನರ ಬಾಧ್ಯತೆಯಲ್ಲಿ ಪಾಲುಗಾರರಾಗುವದಕ್ಕೆ ನಿಮ್ಮನ್ನು ಯೋಗ್ಯರನ್ನಾಗಿ ಮಾಡಿದ ತಂದೆಗೆ ಕೃತಜ್ಞತಾಸ್ತುತಿ ಮಾಡುವವರಾಗಿರಬೇಕೆಂತಲೂ ದೇವರನ್ನು ಬೇಡಿಕೊಳ್ಳುತ್ತೇವೆ.",
			"13": "<sup>13</sup> ದೇವರು ನಮ್ಮನ್ನು ಅಂಧಕಾರದ ದೊರೆತನದಿಂದ ಬಿಡಿಸಿ ತನ್ನ ಪ್ರಿಯ ಕುಮಾರನ ರಾಜ್ಯದೊಳಗೆ ಸೇರಿಸಿದನು.",
			"14": "<sup>14</sup> ಆ ಕುಮಾರನಲ್ಲಿ ನಮ್ಮ ಪಾಪಗಳು ಪರಿಹಾರವಾಗಿ ನಮಗೆ ಬಿಡುಗಡೆಯಾಯಿತು.",
			"15": "<sup>15</sup> ಆತನು ಅದೃಶ್ಯನಾದ ದೇವರ ಪ್ರತಿರೂಪನೂ ಸೃಷ್ಟಿಗೆಲ್ಲಾ ಜ್ಯೇಷ್ಠಪುತ್ರನ ಸ್ಥಾನಹೊಂದಿದವನೂ ಆಗಿದ್ದಾನೆ.",
			"16": "<sup>16</sup> ಭೂಪರಲೋಕಗಳಲ್ಲಿರುವ ದೃಶ್ಯಾದೃಶ್ಯವಾದವುಗಳೆಲ್ಲವೂ ಸಿಂಹಾಸನಗಳಾಗಲಿ ಪ್ರಭುತ್ವಗಳಾಗಲಿ ದೊರೆತನಗಳಾಗಲಿ ಅಧಿಕಾರಗಳಾಗಲಿ ಆತನಲ್ಲಿ ಸೃಷ್ಟಿಸಲ್ಪಟ್ಟವು. ಸರ್ವವು ಆತನ ಮುಖಾಂತರವಾಗಿಯೂ ಆತನಿಗೋಸ್ಕರವಾಗಿಯೂ ಸೃಷ್ಟಿಸಲ್ಪಟ್ಟಿತು.",
			"17": "<sup>17</sup> ಆತನು ಎಲ್ಲಕ್ಕೂ ಮೊದಲು ಇದ್ದವನು; ಆತನು ಸಮಸ್ತಕ್ಕೂ ಆಧಾರ ಭೂತನು.",
			"18": "<sup>18</sup> ಸಭೆಯೆಂಬ ದೇಹಕ್ಕೆ ಆತನು ಶಿರಸ್ಸು; ಆತನು ಆದಿಸಂಭೂತನು. ಎಲ್ಲಾದರಲ್ಲಿ ಪ್ರಮುಖನಾಗುವಂತೆ ಸತ್ತವರೊಳಗಿಂದ ಮೊದಲು ಎದ್ದು ಬಂದವನು ಆತನೇ.",
			"19": "<sup>19</sup> ತಂದೆಯಾದ ದೇವರು ಆತನಲ್ಲಿ ತನ್ನ ಸರ್ವಸಂಪೂರ್ಣತೆಯು ವಾಸವಾಗಿರಬೇಕೆಂತಲೂ",
			"20": "<sup>20</sup> ಆತನು ಶಿಲುಬೆಯ ಮೇಲೆ ಸುರಿಸಿದ ರಕ್ತದಿಂದ ತಾನು ಸಮಾಧಾನವನ್ನು ಉಂಟುಮಾಡಿ ಭೂಪರಲೋಕಗಳಲ್ಲಿರುವ ಸಮಸ್ತವನ್ನೂ ಆತನ ಮೂಲಕ ತನಗೆ ಸಂಧಾನಪಡಿಸಿಕೊಳ್ಳಬೇಕೆಂತಲೂ ನಿಷ್ಕರ್ಷೆಮಾಡಿದನು.",
			"21": "<sup>21</sup> ಇದಲ್ಲದೆ ನೀವು ಹಿಂದಿನ ಕಾಲದಲ್ಲಿ ಅನ್ಯರೂ ದುಷ್ಕೃತ್ಯಗಳನ್ನು ಮಾಡುವವರಾಗಿ ದ್ವೇಷಮನಸ್ಸುಳ್ಳವರೂ ಆಗಿದ್ದರೂ",
			"22": "<sup>22</sup> ಆತನು ನಿಮ್ಮನ್ನು ಸಹ ತನ್ನ ಕುಮಾರನ ಸ್ಥೂಲ ದೇಹದಲ್ಲಿ ಉಂಟಾದ ಮರಣದ ಮೂಲಕ ಈಗ ಸಂಧಾನಪಡಿಸಿಕೊಂಡಿದ್ದಾನೆ; ಮತ್ತು ನಿಮ್ಮನ್ನು ಪರಿಶುದ್ಧರನ್ನಾಗಿಯೂ ನಿರ್ದೋಷಿಗಳನ್ನಾಗಿಯೂ ನಿರಪರಾಧಿಗಳನ್ನಾಗಿಯೂ ತನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನಿಲ್ಲಿಸಬೇಕೆಂದಿದ್ದಾನೆ.",
			"23": "<sup>23</sup> ನೀವು ಕೇಳಿದಂಥ ಆಕಾಶದ ಕೆಳಗಿರುವ ಸರ್ವಸೃಷ್ಟಿಗೆ ಸಾರಲ್ಪಟ್ಟಂಥ ಸುವಾರ್ತೆಯಿಂದ ಉಂಟಾದ ನಿರೀಕ್ಷೆಯನ್ನು ಬಿಟ್ಟು ತೊಲಗಿಹೋಗದೆ ಅಸ್ತಿವಾರದ ಮೇಲೆ ನಿಂತು ಸ್ಥಿರವಾಗಿದ್ದು ನಂಬಿಕೆಯಲ್ಲಿ ನೆಲೆಗೊಂಡಿರುವದಾದರೆ ಆ ಪದವಿ ನಿಮಗೆ ಪ್ರಾಪ್ತವಾಗುವದು.",
			"24": "<sup>24</sup> ಪೌಲನೆಂಬ ನಾನು ಈ ಸುವಾರ್ತೆಗೆ ಸೇವಕನಾದೆನು. ನಾನು ಈಗ ನಿಮಗೋಸ್ಕರ ಅನುಭವಿಸುತ್ತಿರುವ ಬಾಧೆಗಳಲ್ಲಿ ಸಂತೋಷಪಟ್ಟು ಕ್ರಿಸ್ತನ ಸಂಕಟಗಳೊಳಗೆ ಇನ್ನೂ ಉಳಿದದ್ದನ್ನು ಸಭೆಯೆಂಬ ಆತನ ದೇಹಕ್ಕೋಸ್ಕರ ನನ್ನ ಶರೀರದಲ್ಲಿ ಅನುಭವಿಸಿ ತೀರಿಸುತ್ತೇನೆ.",
			"25": "<sup>25</sup> ನಾನು ಸಭೆಗೆ ಸೇವಕನಾದೆನು. ದೇವರು ತನ್ನ ವಾಕ್ಯವನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ತಿಳಿಸುವ ಕೆಲಸವನ್ನು ನಿಮ್ಮ ಪ್ರಯೋಜನಕ್ಕೋಸ್ಕರ ನನಗೆ ದಯಪಾಲಿಸಿದನು.",
			"26": "<sup>26</sup> ಆ ವಾಕ್ಯವು ಹಿಂದಿನ ಯುಗಗಳಿಂದಲೂ ತಲತಲಾಂತರಗಳಿಂದಲೂ ಮರೆಯಾಗಿತ್ತು; ಈಗಿನ ಕಾಲದಲ್ಲಿ ದೇವಜನರಿಗೆ ತಿಳಿಸಲ್ಪಟ್ಟಿದೆ.",
			"27": "<sup>27</sup> ಅನ್ಯಜನಗಳ ವಿಷಯವಾದ ಈ ಮರ್ಮದ ಮಹಿಮಾತಿಶಯವು ಎಷ್ಟೆಂಬದನ್ನು ದೇವರು ತನ್ನ ಜನರಿಗೆ ತಿಳಿಸಲಿಕ್ಕೆ ಮನಸ್ಸು ಮಾಡಿಕೊಂಡನು. ಈ ಮರ್ಮವು ಏನಂದರೆ ಕ್ರಿಸ್ತನು ನಿಮ್ಮಲ್ಲಿದ್ದು ಪ್ರಭಾವದ ನಿರೀಕ್ಷೆಗೆ ಆಧಾರಭೂತನಾಗಿರುವದೇ.",
			"28": "<sup>28</sup> ನಾವು ಆತನನ್ನು ಪ್ರಸಿದ್ಧಿಪಡಿಸುವವರಾಗಿ ಸಕಲರಿಗೂ ಬುದ್ಧಿಹೇಳುತ್ತಾ ಸರ್ವರಿಗೂ ಪೂರ್ಣಜ್ಞಾನವನ್ನು ಉಪದೇಶಿಸುತ್ತಾ ದೇವರ ಮುಂದೆ ಎಲ್ಲರನ್ನು ಕ್ರಿಸ್ತನಲ್ಲಿ ಪ್ರವೀಣರನ್ನಾಗಿ ನಿಲ್ಲಿಸುವದಕ್ಕೆ ಪ್ರಯತ್ನಿಸುತ್ತಿದ್ದೇವೆ.",
			"29": "<sup>29</sup> ನನ್ನಲ್ಲಿ ಕಾರ್ಯಸಾಧಿಸುವ ದೇವರ ಬಲವನ್ನು ಪ್ರಯೋಗಮಾಡಿ ಇದಕ್ಕೋಸ್ಕರವೇ ಹೋರಾಡುತ್ತೇನೆ, ಪ್ರಯಾಸಪಡುತ್ತೇನೆ."
		},
		"2": {
			"1": "<sup>1</sup> ನಿಮಗೋಸ್ಕರವೂ ಲವೊದಿಕೀಯದವರಿಗೋಸ್ಕರವೂ ನನ್ನನ್ನು ಕಣ್ಣಾರೆ ಕಾಣದಿರುವವರೆಲ್ಲರಿಗೋಸ್ಕರವೂ ನನಗೆ ಬಹಳ ಹೋರಾಟ ಉಂಟೆಂಬದು ನಿಮಗೆ ತಿಳಿದಿರಬೇಕೆಂದು ಅಪೇಕ್ಷಿಸುತ್ತೇನೆ.",
			"2": "<sup>2</sup> ಹೇಗಂದರೆ ನೀವೆಲ್ಲರು ಪ್ರೀತಿಯಿಂದ ಹೊಂದಿಕೆಯಾಗಿದ್ದು ಬುದ್ಧಿಪೂರ್ವಕ ನಿಶ್ಚಯವೆಂಬ ಭಾಗ್ಯವನ್ನು ಪಡೆದು ದೇವರು ತಿಳಿಸಿರುವ ಮರ್ಮವನ್ನು ಅಂದರೆ ತನ್ನಲ್ಲೇ ಜ್ಞಾನವಿದ್ಯಾಸಂಬಂಧವಾದ ನಿಕ್ಷೇಪಗಳನ್ನೆಲ್ಲಾ ಅಡಗಿಸಿಕೊಂಡಿರುವ ಕ್ರಿಸ್ತನನ್ನು ತಿಳುಕೊಂಡವರಾಗಿ ಹೃದಯದಲ್ಲಿ ದೃಢವಾಗಿರಬೇಕೆಂಬ ಕುತೂಹಲವು ನನಗುಂಟು.",
			"4": "<sup>4</sup> ಯಾರಾದರೂ ರಂಜನೆಯಾದ ಮಾತುಗಳಿಂದ ನಿಮ್ಮನ್ನು ಮೋಸಗೊಳಿಸಿಯಾರೆಂಬ ಭಯ ನನಗಿರುವದರಿಂದ ಇದನ್ನು ಹೇಳಿದ್ದೇನೆ.",
			"5": "<sup>5</sup> ನಾನು ಶರೀರದಿಂದ ನಿಮ್ಮ ಬಳಿಯಲ್ಲಿ ಇಲ್ಲದಿದ್ದರೂ ಆತ್ಮದಿಂದ ನಿಮ್ಮೊಂದಿಗಿದ್ದು ನೀವು ಕ್ರಮವಾಗಿ ನಡೆಯುವದನ್ನೂ ಕ್ರಿಸ್ತನಂಬಿಕೆಯಲ್ಲಿ ದೃಢವಾಗಿ ನಿಂತಿರುವದನ್ನೂ ನೋಡಿ ಸಂತೋಷಪಡುತ್ತೇನೆ.",
			"6": "<sup>6</sup> ಆದದರಿಂದ ನೀವು ಕರ್ತನಾದ ಯೇಸುವೆಂಬ ಕ್ರಿಸ್ತನನ್ನು ಅಂಗೀಕರಿಸಿದಂತೆಯೇ ಆತನಲ್ಲಿದ್ದವರಾಗಿ ನಡೆದುಕೊಳ್ಳಿರಿ.",
			"7": "<sup>7</sup> ಆತನಲ್ಲಿ ಬೇರೂರಿಕೊಂಡು ಭಕ್ತಿವೃದ್ಧಿಯನ್ನು ಹೊಂದಿ ನಿಮಗೆ ಬೋಧಿಸಲ್ಪಟ್ಟ ಉಪದೇಶದ ಪ್ರಕಾರವೇ ಕ್ರಿಸ್ತ ನಂಬಿಕೆಯಲ್ಲಿ ನೆಲೆಗೊಂಡು ದೇವರಿಗೆ ಹೆಚ್ಚೆಚ್ಚಾಗಿ ಸ್ತೋತ್ರಮಾಡುವವರಾಗಿರಿ.",
			"8": "<sup>8</sup> ಕ್ರಿಸ್ತನನ್ನು ಅನುಸರಿಸದೆ ಮನುಷ್ಯರ ಸಂಪ್ರದಾಯಗಳನ್ನೂ ಪ್ರಾಪಂಚಿಕ ಬಾಲಬೋಧೆಯನ್ನೂ ಅನುಸರಿಸುವವರು ನಿಮ್ಮಲ್ಲಿ ಬಂದು ಮೋಸವಾದ ನಿರರ್ಥಕ ತತ್ವಜ್ಞಾನಬೋಧೆಯಿಂದ ನಿಮ್ಮ ಮನಸ್ಸನ್ನು ಕೆಡಿಸಿ ನಿಮ್ಮನ್ನು ವಶಮಾಡಿಕೊಂಡಾರು, ಎಚ್ಚರಿಕೆಯಾಗಿರ್ರಿ.",
			"9": "<sup>9</sup> ಕ್ರಿಸ್ತನಲ್ಲಿಯೇ ದೇವರ ಸರ್ವಸಂಪೂರ್ಣತೆಯು ಅವತರಿಸಿ ವಾಸಮಾಡುತ್ತದೆ,",
			"10": "<sup>10</sup> ಮತ್ತು ನೀವು ಆತನಲ್ಲಿದ್ದುಕೊಂಡೇ ಪರಿಪೂರ್ಣತೆಯನ್ನು ಹೊಂದಿದವರಾಗಿದ್ದೀರಿ.",
			"11": "<sup>11</sup> ಆತನು ಎಲ್ಲಾ ದೊರೆತನಗಳಿಗೂ ಅಧಿಕಾರಿಗಳಿಗೂ ಶಿರಸ್ಸು. ನೀವು ಆತನಲ್ಲಿ ಸುನ್ನತಿಯನ್ನೂ ಹೊಂದಿದಿರಿ; ಈ ಸುನ್ನತಿಯು ಕೈಯಿಂದ ಮಾಡಿದ್ದಲ್ಲ, ಇದು ಪಾಪಾಧೀನಸ್ವಭಾವವನ್ನು ವಿಸರ್ಜಿಸುವದೇ; ಇದೇ ಕ್ರಿಸ್ತೀಯ ಸುನ್ನತಿ;",
			"12": "<sup>12</sup> ನೀವು ದೀಕ್ಷಾಸ್ನಾನದಲ್ಲಿ ಕ್ರಿಸ್ತನೊಂದಿಗೆ ಹೂಣಲ್ಪಟ್ಟದ್ದರಿಂದಲೇ ಆ ಸುನ್ನತಿಯನ್ನು ಹೊಂದಿದಿರಿ. ಇದಲ್ಲದೆ ಆತನನ್ನು ಸತ್ತವರೊಳಗಿಂದ ಎಬ್ಬಿಸಿದ ದೇವರ ಸಾಮರ್ಥ್ಯದಲ್ಲಿ ನಂಬಿಕೆಯಿಡುವದರ ಮೂಲಕ ಅದರಲ್ಲಿ ಆತನ ಕೂಡ ಎದ್ದು ಬಂದಿರಿ.",
			"13": "<sup>13</sup> ಅಪರಾಧಗಳನ್ನು ಮಾಡುವದರಿಂದಲೂ ಸುನ್ನತಿಯಿಲ್ಲದ ಶರೀರಭಾವದಿಂದಲೂ ಸತ್ತವರಾಗಿದ್ದ ನಿಮ್ಮನ್ನು ಸಹ ದೇವರು ಕ್ರಿಸ್ತನೊಂದಿಗೆ ಬದುಕಿಸಿದ್ದಾನೆ.",
			"14": "<sup>14</sup> ಆತನು ನಮ್ಮ ಅಪರಾಧಗಳನ್ನೆಲ್ಲಾ ಕ್ಷಮಿಸಿ ನಮ್ಮ ಮೇಲೆ ದೋಷಾರೋಪಣೆಮಾಡಿದಂಥ ಆಜ್ಞಾರೂಪವಾಗಿದ್ದಂಥ ಪತ್ರವನ್ನು ಕೆಡಿಸಿ ಅದನ್ನು ಶಿಲುಬೆಗೆ ಜಡಿದು ಇಲ್ಲದಂತಾಗ ಮಾಡಿದನು.",
			"15": "<sup>15</sup> ಆತನು ದೊರೆತನಗಳನ್ನೂ ಅಧಿಕಾರಗಳನ್ನೂ ನಿರಾಯುಧರನ್ನಾಗಿ ಮಾಡಿ ಜಯೋತ್ಸವ ಮಾಡುತ್ತಾನೋ ಎಂಬಂತೆ ಶಿಲುಬೆಯ ಮೇಲೆ ಅವುಗಳನ್ನು ಜಯಿಸಲ್ಪಟ್ಟವುಗಳನ್ನಾಗಿ ಮೆರಸುತ್ತಾ ತೋರಿಸಿದನು.",
			"16": "<sup>16</sup> ಹೀಗಿರುವದರಿಂದ ತಿಂದು ಕುಡಿಯುವ ವಿಷಯದಲ್ಲಿಯೂ ಹಬ್ಬ ಅಮವಾಸ್ಯೆ ಸಬ್ಬತು ಎಂಬಿವುಗಳ ವಿಷಯದಲ್ಲಿಯೂ ನಿಮ್ಮನ್ನು ದೋಷಿಗಳೆಂದು ಯಾರೂ ಎಣಿಸಬಾರದು.",
			"17": "<sup>17</sup> ಇವು ಮುಂದೆ ಬರಬೇಕಾಗಿದ್ದ ಕಾರ್ಯಗಳ ಛಾಯೆಯಾಗಿವೆ; ಇವುಗಳ ನಿಜಸ್ವರೂಪವು ಕ್ರಿಸ್ತನೇ.",
			"18": "<sup>18</sup> ಕೆಲವರು ಅತಿ ವಿನಯದಲ್ಲಿಯೂ ದೇವದೂತರ ಪೂಜೆಯಲ್ಲಿಯೂ ಆಸಕ್ತರಾಗಿದ್ದಾರೆ,",
			"19": "<sup>19</sup> ತಮಗೆ ದರ್ಶನಗಳಾದವೆಂದು ಕೊಚ್ಚಿಕೊಳ್ಳುತ್ತಾರೆ, ಮತ್ತು ಪ್ರಾಪಂಚಿಕ ಬುದ್ಧಿಯಿಂದ ನಿರಾಧಾರವಾಗಿ ಉಬ್ಬಿಕೊಂಡಿದ್ದಾರೆ, ಆದರೆ ಕ್ರಿಸ್ತನೆಂಬ ಶಿರಸ್ಸಿನ ಹೊಂದಿಕೆಯನ್ನು ಬಿಟ್ಟವರಾಗಿದ್ದಾರೆ. ಇಂಥವರು ನಿಮಗೆ ದೊರಕಿರುವ ಬಿರುದನ್ನು ಅಪಹರಿಸುವದಕ್ಕೆ ಅವಕಾಶಕೊಡಬೇಡಿರಿ. ಆ ಶಿರಸ್ಸಿನಿಂದಲೇ ದೇಹವೆಲ್ಲಾ ಕೀಲುನರಗಳ ಮೂಲಕ ಬೇಕಾದ ಸಹಾಯವನ್ನು ಹೊಂದಿ ಒಂದಾಗಿ ಕೂಡಿಸಲ್ಪಟ್ಟು ದೇವರು ಕೊಡುವ ವೃದ್ಧಿಯಿಂದ ಅಭಿವೃದ್ಧಿಯಾಗುತ್ತಾ ಬರುತ್ತದೆ.",
			"20": "<sup>20</sup> ಪ್ರಾಪಂಚಿಕ ಬಾಲಬೋಧೆಯ ಪಾಲಿಗೆ ನೀವು ಕ್ರಿಸ್ತನೊಂದಿಗೆ ಸತ್ತವರಾಗಿದ್ದ ಮೇಲೆ ಇನ್ನೂ ಪ್ರಾಪಂಚಿಕರಾಗಿ ಬದುಕುವವರಂತೆ ಮನುಷ್ಯ ಕಲ್ಪಿತ ಆಜ್ಞೆಗಳನ್ನೂ ಉಪದೇಶಗಳನ್ನೂ ಅನುಸರಿಸಿ -",
			"21": "<sup>21</sup> ಇದನ್ನು ಹಿಡಿಯಬೇಡ, ಇದನ್ನು ರುಚಿನೋಡಬೇಡ,",
			"22": "<sup>22</sup> ಅದನ್ನು ಮುಟ್ಟಬೇಡ ಎನ್ನುವ ನಿಬಂಧನೆಗಳಿಗೆ ನಿಮ್ಮನ್ನು ಒಳಗಾಗಮಾಡಿಕೊಳ್ಳುವದೇಕೆ? ಆ ಪದಾರ್ಥಗಳೆಲ್ಲವೂ ಬಳಸುವದರಲ್ಲಿ ನಾಶವಾಗುತ್ತವೆ.",
			"23": "<sup>23</sup> ಅಂಥ ಉಪದೇಶಗಳು ಸ್ವಕಲ್ಪಿತಾಚಾರವನ್ನೂ ಅತಿವಿನಯವನ್ನೂ ದೇಹದಂಡನೆಯನ್ನೂ ಬೋಧಿಸುತ್ತಾ ಹೆಸರಿಗೆ ಮಾತ್ರ ಜ್ಞಾನವೆನಿಸಿಕೊಳ್ಳುತ್ತವೆಯಲ್ಲದೆ ಶಾರೀರಿಕ ಇಚ್ಫೆಗಳನ್ನು ನಿಗ್ರಹಿಸುವದರಲ್ಲಿ ಯಾವ ಪ್ರಯೋಜನಕ್ಕೂ ಬರುವದಿಲ್ಲ."
		},
		"3": {
			"1": "<sup>1</sup> ಆದಕಾರಣ ನೀವು ಕ್ರಿಸ್ತನೊಂದಿಗೆ ಎಬ್ಬಿಸಲ್ಪಟ್ಟಿರುವದರಿಂದ ಮೇಲಿರುವವುಗಳನ್ನು ಹುಡುಕಿರಿ; ಅಲ್ಲಿ ಕ್ರಿಸ್ತನು ದೇವರ ಬಲಗಡೆಯಲ್ಲಿ ಆಸನಾರೂಢನಾಗಿದ್ದಾನೆ.",
			"2": "<sup>2</sup> ಮೇಲಿರುವಂಥವುಗಳ ಮೇಲೆ ಮನಸ್ಸಿಡಿರಿ, ಭೂಸಂಬಂಧವಾದವುಗಳ ಮೇಲೆ ಇಡಬೇಡಿರಿ.",
			"3": "<sup>3</sup> ಯಾಕಂದರೆ ನೀವು ಸತ್ತಿರಲ್ಲಾ, ನಿಮ್ಮ ಜೀವವು ಕ್ರಿಸ್ತನೊಂದಿಗೆ ದೇವರಲ್ಲಿ ಮರೆಯಾಗಿಟ್ಟದೆ.",
			"4": "<sup>4</sup> ನಮಗೆ ಜೀವವಾಗಿರುವ ಕ್ರಿಸ್ತನು ಪ್ರತ್ಯಕ್ಷನಾಗುವಾಗ ನೀವು ಸಹ ಆತನ ಜೊತೆಯಲ್ಲಿ ಪ್ರಭಾವದಿಂದ ಕೂಡಿದವರಾಗಿ ಪ್ರತ್ಯಕ್ಷರಾಗುವಿರಿ.",
			"5": "<sup>5</sup> ಆದದರಿಂದ ನಿಮ್ಮಲ್ಲಿರುವ ಭೂಸಂಬಂಧವಾದ ಭಾವಗಳನ್ನು ಸಾಯಿಸಿರಿ. ಜಾರತ್ವ ಬಂಡುತನ ಕಾಮಾಭಿಲಾಷೆ ದುರಾಶೆ ವಿಗ್ರಹಾರಾಧನೆಗೆ ಸಮವಾಗಿರುವ ಲೋಭ ಇವುಗಳನ್ನು ವಿಸರ್ಜಿಸಿಬಿಡಿರಿ;",
			"6": "<sup>6</sup> ಇವುಗಳ ನಿಮಿತ್ತ ದೇವರ ಕೋಪವು ಉಂಟಾಗುತ್ತದೆ.",
			"7": "<sup>7</sup> ಪೂರ್ವದಲ್ಲಿ ನೀವು ಸಹ ಈ ದುರ್ಭಾವಗಳ ಅಧೀನದಲ್ಲಿದ್ದು ಈ ಕೆಲಸಗಳನ್ನು ನಡಿಸುತ್ತಿದ್ದಿರಿ.",
			"8": "<sup>8</sup> ಈಗಲಾದರೋ ಕ್ರೋಧ ಕೋಪ ಮತ್ಸರ ದೂಷಣೆ ಬಾಯಿಂದ ಹೊರಡುವ ದುರ್ಭಾಷೆ ಇವುಗಳನ್ನು ವಿಸರ್ಜಿಸಿಬಿಡಿರಿ.",
			"9": "<sup>9</sup> ಒಬ್ಬರಿಗೊಬ್ಬರು ಸುಳ್ಳಾಡಬೇಡಿರಿ; ನೀವು ಪೂರ್ವಸ್ವಭಾವವನ್ನು ಅದರ ಕೃತ್ಯಗಳ ಕೂಡ ತೆಗೆದಿಟ್ಟು ನೂತನಸ್ವಭಾವವನ್ನು ಧರಿಸಿಕೊಂಡಿದ್ದೀರಲ್ಲವೇ.",
			"10": "<sup>10</sup> ಈ ಸ್ವಭಾವವು ಅದನ್ನು ಸೃಷ್ಟಿಸಿದಾತನ ಹೋಲಿಕೆಯ ಮೇರೆಗೆ ದಿನೇದಿನೇ ನೂತನವಾಗುತ್ತಾ ಪೂರ್ಣಜ್ಞಾನವನ್ನು ಉಂಟುಮಾಡುತ್ತದೆ.",
			"11": "<sup>11</sup> ಇದನ್ನು ಧರಿಸಿಕೊಂಡಿರುವದರಲ್ಲಿ ಗ್ರೀಕನು ಯೆಹೂದ್ಯನು ಎಂಬ ಭೇದವಿಲ್ಲ; ಸುನ್ನತಿ ಮಾಡಿಸಿಕೊಂಡವರು ಸುನ್ನತಿಮಾಡಿಸಿಕೊಳ್ಳದವರು ಎಂಬ ಭೇದವಿಲ್ಲ; ಮ್ಲೇಚ್ಫ ಹೂಣ ಎಂಬ ಹೆಸರುಗಳಿಲ್ಲ; ಆಳು ಒಡೆಯ ಎಂಬ ಭೇದವಿಲ್ಲ. ಆದರೆ ಕ್ರಿಸ್ತನೇ ಸಮಸ್ತರಲ್ಲಿಯೂ ಸಮಸ್ತವೂ ಆಗಿರುವನು.",
			"12": "<sup>12</sup> ಹೀಗಿರಲಾಗಿ ನೀವು ದೇವರಿಂದ ಆರಿಸಿಕೊಂಡವರೂ ಪ್ರತಿಷ್ಠಿತರೂ ಪ್ರಿಯರೂ ಆಗಿರುವದರಿಂದ ಕನಿಕರ ದಯೆ ದೀನಭಾವ ಸಾತ್ವಿಕತ್ವ ದೀರ್ಘಶಾಂತಿ ಎಂಬ ಸದ್ಗುಣಗಳನ್ನು ಧರಿಸಿಕೊಳ್ಳಿರಿ.",
			"13": "<sup>13</sup> ಮತ್ತೊಬ್ಬನ ಮೇಲೆ ತಪ್ಪುಹೊರಿಸುವದಕ್ಕೆ ಕಾರಣವಿದ್ದರೂ ತಪ್ಪುಹೊರಿಸದೆ ಒಬ್ಬರಿಗೊಬ್ಬರು ಸೈರಿಸಿಕೊಂಡು ಕ್ಷಮಿಸಿರಿ. ಕರ್ತನು ನಿಮ್ಮನ್ನು ಕ್ಷಮಿಸಿದಂತೆಯೇ ನೀವೂ ಕ್ಷಮಿಸಿರಿ.",
			"14": "<sup>14</sup> ಇದೆಲ್ಲಾದರ ಮೇಲೆ ಪ್ರೀತಿಯನ್ನು ಧರಿಸಿಕೊಳ್ಳಿರಿ. ಅದು ಸಮಸ್ತವನ್ನು ಸಂಪೂರ್ಣಮಾಡುವ ಬಂಧವಾಗಿದೆ.",
			"15": "<sup>15</sup> ಕ್ರಿಸ್ತನಿಂದಾದ ಸಮಾಧಾನವು ನಿಮ್ಮ ಹೃದಯಗಳಲ್ಲಿ ಯಜಮಾನನಂತಿದ್ದು ತೀರ್ಪುಹೇಳಲಿ; ನೀವು ಒಂದೇ ದೇಹಕ್ಕೆ ಸೇರಿದವರಾದದರಿಂದ ಸಮಾಧಾನದಿಂದಿರುವದಕ್ಕಾಗಿ ಕರೆಯಲ್ಪಟ್ಟಿರಿ. ಇದಲ್ಲದೆ ಕೃತಜ್ಞತೆಯುಳ್ಳವರಾಗಿರ್ರಿ.",
			"16": "<sup>16</sup> ಕ್ರಿಸ್ತನ ವಾಕ್ಯವು ನಿಮ್ಮಲ್ಲಿ ಸಮೃದ್ಧಿಯಾಗಿ ವಾಸಿಸಲಿ; ಸಕಲಜ್ಞಾನದಿಂದ ಕೂಡಿದವರಾಗಿ ಒಬ್ಬರಿಗೊಬ್ಬರು ಉಪದೇಶಮಾಡಿಕೊಳ್ಳಿರಿ, ಬುದ್ಧಿಹೇಳಿಕೊಳ್ಳಿರಿ; ಕರ್ತನ ಕೃಪೆಯನ್ನು ನೆನಸಿಕೊಳ್ಳುವವರಾಗಿ ಕೀರ್ತನೆಗಳಿಂದಲೂ ಸಂಗೀತಗಳಿಂದಲೂ ಆತ್ಮಸಂಬಂಧವಾದ ಹಾಡುಗಳಿಂದಲೂ ನಿಮ್ಮ ಹೃದಯಗಳಲ್ಲಿ ದೇವರಿಗೆ ಗಾನಮಾಡಿರಿ.",
			"17": "<sup>17</sup> ನೀವು ನುಡಿಯಿಂದಾಗಲಿ ನಡೆಯಿಂದಾಗಲಿ ಏನು ಮಾಡಿದರೂ ಅದೆಲ್ಲವನ್ನೂ ಕರ್ತನಾದ ಯೇಸುವಿನ ಹೆಸರಿನಲ್ಲಿಯೇ ಮಾಡಿರಿ. ಆತನ ಮೂಲಕ ತಂದೆಯಾದ ದೇವರಿಗೆ ಕೃತಜ್ಞತಾಸ್ತುತಿಯನ್ನು ಸಲ್ಲಿಸಿರಿ.",
			"18": "<sup>18</sup> ಸ್ತ್ರೀಯರೇ, ನಿಮ್ಮ ಗಂಡಂದಿರಿಗೆ ಅಧೀನರಾಗಿರಿ; ಇದು ಕರ್ತನಲ್ಲಿರುವವರಿಗೆ ಯೋಗ್ಯವಾಗಿದೆ.",
			"19": "<sup>19</sup> ಪುರುಷರೇ, ನಿಮ್ಮ ಹೆಂಡತಿಯರನ್ನು ಪ್ರೀತಿಸಿರಿ, ಅವರಿಗೆ ನಿಷ್ಠುರವಾಗಿರಬೇಡಿರಿ.",
			"20": "<sup>20</sup> ಮಕ್ಕಳೇ, ಎಲ್ಲಾ ವಿಷಯಗಳಲ್ಲಿ ನಿಮ್ಮ ತಂದೆತಾಯಿಗಳ ಮಾತನ್ನು ಕೇಳಿರಿ;",
			"21": "<sup>21</sup> ಇದು ಕರ್ತನ ದೃಷ್ಟಿಯಲ್ಲಿ ಮೆಚ್ಚಿಕೆಯಾಗಿದೆ. ತಂದೆಗಳೇ, ನಿಮ್ಮ ಮಕ್ಕಳನ್ನು ಕೆಣಕಿ ಅವರಿಗೆ ಮನಗುಂದಿಸಬೇಡಿರಿ.",
			"22": "<sup>22</sup> ದಾಸತ್ವದಲ್ಲಿರುವವರೇ, ಈ ಲೋಕದಲ್ಲಿನ ನಿಮ್ಮ ಯಜಮಾನರಿಗೆ ಎಲ್ಲಾ ವಿಷಯಗಳಲ್ಲಿ ವಿಧೇಯರಾಗಿರಿ; ಮನುಷ್ಯರನ್ನು ಮೆಚ್ಚಿಸುವವರು ಮಾಡುವ ಪ್ರಕಾರ ನಿಮ್ಮ ಯಜಮಾನರು ನೋಡುತ್ತಿರುವಾಗ ಮಾತ್ರ ಸೇವೆ ಮಾಡದೆ ಕರ್ತನಿಗೆ ಭಯಪಡುವವರಾಗಿ ಸರಳಮನಸ್ಸಿನಿಂದ ಕೆಲಸಮಾಡಿರಿ.",
			"23": "<sup>23</sup> ನೀವು ಯಾವ ಕೆಲಸವನ್ನು ಮಾಡಿದರೂ ಅದನ್ನು ಮನುಷ್ಯರಿಗೋಸ್ಕರವೆಂದು ಮಾಡದೆ ಕರ್ತನಿಗೋಸ್ಕರವೇ ಎಂದು ಮನಃಪೂರ್ವಕವಾಗಿ ಮಾಡಿರಿ;",
			"24": "<sup>24</sup> ಕರ್ತನಿಂದ ಪರಲೋಕ ಬಾಧ್ಯತೆಯೆಂಬ ಪ್ರತಿಫಲವನ್ನು ಹೊಂದುವೆವೆಂದು ತಿಳಿದಿದ್ದೀರಲ್ಲಾ. ನೀವು ಕರ್ತನಾದ ಕ್ರಿಸ್ತನಿಗೇ ದಾಸರಾಗಿದ್ದೀರಿ.",
			"25": "<sup>25</sup> ಅನ್ಯಾಯಮಾಡುವವನು ತಾನು ಮಾಡಿದ ಅನ್ಯಾಯಕ್ಕೆ ತಕ್ಕ ಪ್ರತಿಫಲವನ್ನು ಹೊಂದುವನಷ್ಟೆ; ಪಕ್ಷಪಾತವಿರುವದಿಲ್ಲ."
		},
		"4": {
			"1": "<sup>1</sup> ಯಜಮಾನರೇ, ಪರಲೋಕದಲ್ಲಿ ನಿಮಗೂ ಯಜಮಾನನೊಬ್ಬನಿದ್ದಾನೆಂದು ತಿಳಿದು ನಿಮ್ಮ ದಾಸರಿಗೆ ನ್ಯಾಯವಾದದ್ದನ್ನೂ ಸರಿಯಾದದ್ದನ್ನೂ ಮಾಡಿರಿ.",
			"2": "<sup>2</sup> ಪ್ರಾರ್ಥನೆಯನ್ನು ತಪ್ಪದೆ ಮಾಡುವವರಾಗಿ ಅದರಲ್ಲಿ ಎಚ್ಚರವಾಗಿದ್ದು ದೇವರಿಗೆ ಕೃತಜ್ಞತಾಸ್ತುತಿ ಮಾಡಿರಿ.",
			"3": "<sup>3</sup> ಇದಲ್ಲದೆ ನಮಗೋಸ್ಕರವೂ ಪ್ರಾರ್ಥನೆ ಮಾಡಿರಿ. ಕ್ರಿಸ್ತನ ವಿಷಯದಲ್ಲಿ ದೇವರು ತಿಳಿಯಪಡಿಸಿದ ಸತ್ಯಾರ್ಥವನ್ನು ಪ್ರಸಂಗಿಸುವದಕ್ಕೆ ಆತನು ಅನುಕೂಲವಾದ ಸಂದರ್ಭವನ್ನು ನಮಗೆ ದಯಪಾಲಿಸಿ ನಾನು ಅದನ್ನು ಹೇಳಬೇಕಾದ ರೀತಿಯಲ್ಲಿ ತಿಳಿಸುವಂತೆ ಅನುಗ್ರಹಮಾಡಬೇಕೆಂದು ಬೇಡಿಕೊಳ್ಳಿರಿ; ಆ ಸತ್ಯಾರ್ಥದ ನಿಮಿತ್ತವೇ ನಾನು ಸೆರೆಯಲ್ಲಿದ್ದೇನಲ್ಲಾ.",
			"5": "<sup>5</sup> ಸಮಯವನ್ನು ಸುಮ್ಮನೆ ಕಳಕೊಳ್ಳದೆ ಅದನ್ನು ಬೆಲೆಯುಳ್ಳದ್ದೆಂದು ಉಪಯೋಗಿಸಿ ಹೊರಗಿನವರ ಮುಂದೆ ಜ್ಞಾನವುಳ್ಳವರಾಗಿ ನಡೆದುಕೊಳ್ಳಿರಿ.",
			"6": "<sup>6</sup> ನಿಮ್ಮ ಸಂಭಾಷಣೆ ಯಾವಾಗಲೂ ಇಂಪಾಗಿಯೂ ರಸವತ್ತಾಗಿಯೂ ಇರಲಿ; ಹೀಗೆ ನೀವು ಯಾರಾರಿಗೆ ಯಾವಾವ ರೀತಿಯಲ್ಲಿ ಉತ್ತರಹೇಳಬೇಕೋ ಅದನ್ನು ತಿಳಿದುಕೊಳ್ಳುವಿರಿ.",
			"7": "<sup>7</sup> ಪ್ರಿಯ ಸಹೋದರನೂ ನಂಬಿಗಸ್ತನಾದ ಸೇವಕನೂ ಕರ್ತನಲ್ಲಿ ಜೊತೆಯ ದಾಸನೂ ಆಗಿರುವ ತುಖಿಕನು ನನ್ನ ಸಂಗತಿಗಳನ್ನೆಲ್ಲಾ ನಿಮಗೆ ತಿಳಿಸುವನು.",
			"8": "<sup>8</sup> ನೀವು ನನ್ನ ಸಮಾಚಾರವನ್ನು ತಿಳುಕೊಳ್ಳುವ ಹಾಗೆಯೂ ಅವನು ನಿಮ್ಮ ಹೃದಯಗಳನ್ನು ಸಂತೈಸುವ ಹಾಗೆಯೂ",
			"9": "<sup>9</sup> ಅವನನ್ನು ನಂಬಿಗಸ್ತನಾದ ಪ್ರಿಯ ಸಹೋದರನಾಗಿರುವ ನಿಮ್ಮ ಊರಿನ ಒನೇಸಿಮನ ಜೊತೆಯಲ್ಲಿ ಕಳುಹಿಸಿದ್ದೇನೆ. ಅವರು ಇಲ್ಲಿ ನಡೆಯುವ ಸಂಗತಿಗಳನ್ನೆಲ್ಲಾ ನಿಮಗೆ ತಿಳಿಸುವರು.",
			"10": "<sup>10</sup> ನನ್ನ ಜೊತೆಸೆರೆಯವನಾದ ಅರಿಸ್ತಾರ್ಕನೂ ಬಾರ್ನಬನಿಗೆ ದಾಯಾದನಾಗಿರುವ ಮಾರ್ಕನೂ ನಿಮಗೆ ವಂದನೆ ಹೇಳುತ್ತಾರೆ. ಮಾರ್ಕನನ್ನು ಕುರಿತು ಅಪ್ಪಣೆಗಳನ್ನು ಹೊಂದಿದಿರಲ್ಲಾ; ಅವನು ನಿಮ್ಮ ಬಳಿಗೆ ಬಂದರೆ ಅವನನ್ನು ಸೇರಿಸಿಕೊಳ್ಳಿರಿ.",
			"11": "<sup>11</sup> ಯೂಸ್ತನೆನಿಸಿಕೊಳ್ಳುವ ಯೇಸು ಸಹ ನಿಮಗೆ ವಂದನೆ ಹೇಳುತ್ತಾನೆ. ಸುನ್ನತಿಯವರೊಳಗೆ ಇವರು ಮಾತ್ರವೇ ದೇವರ ರಾಜ್ಯಾಭಿವೃದ್ಧಿಗಾಗಿ ನನ್ನ ಜೊತೆಗೆಲಸದವರಾಗಿದ್ದಾರೆ; ಇವರಿಂದ ನನಗೆ ಉಪಶಮನ ಉಂಟಾಯಿತು.",
			"12": "<sup>12</sup> ಕ್ರಿಸ್ತ ಯೇಸುವಿನ ದಾಸನಾಗಿರುವ ನಿಮ್ಮ ಊರಿನ ಎಪಫ್ರನು ನಿಮಗೆ ವಂದನೆ ಹೇಳುತ್ತಾನೆ; ಇವನು ಪ್ರಾರ್ಥನೆ ಮಾಡುವಾಗೆಲ್ಲಾ ನಿಮಗೋಸ್ಕರ ಹೋರಾಡಿ ನೀವು ಪ್ರವೀಣರಾಗಿಯೂ ಎಲ್ಲಾ ವಿಷಯಗಳಲ್ಲಿ ದೇವರ ಚಿತ್ತವನ್ನು ಕುರಿತು ಪೂರ್ಣ ನಿಶ್ಚಯವುಳ್ಳವರಾಗಿಯೂ ನಿಂತಿರಬೇಕೆಂದು ವಿಜ್ಞಾಪನೆ ಮಾಡುತ್ತಾನೆ.",
			"13": "<sup>13</sup> ಇವನು ನಿಮಗೋಸ್ಕರವೂ ಲವೊದಿಕೀಯದವರಿಗೋಸ್ಕರವೂ ಹಿರಿಯಾಪೊಲಿಯವರಿಗೋಸ್ಕರವೂ ಬಹಳ ಪ್ರಯಾಸ ಪಡುತ್ತಾನೆಂದು ಸಾಕ್ಷಿ ಹೇಳುತ್ತೇನೆ.",
			"14": "<sup>14</sup> ಪ್ರಿಯ ವೈದ್ಯನಾಗಿರುವ ಲೂಕನು ಮತ್ತು ದೇಮನು ನಿಮಗೆ ವಂದನೆ ಹೇಳುತ್ತಾರೆ.",
			"15": "<sup>15</sup> ಲವೊದಿಕೀಯದಲ್ಲಿರುವ ಸಹೋದರರಿಗೂ ನುಂಫಳಿಗೂ ಆಕೆಯ ಮನೆಯಲ್ಲಿ ಕೂಡುವ ಸಭೆಯವರಿಗೂ ವಂದನೆ ಹೇಳಿರಿ.",
			"16": "<sup>16</sup> ನಿಮ್ಮಲ್ಲಿ ಈ ಪತ್ರಿಕೆಯನ್ನು ಓದಿಸಿಕೊಂಡ ತರುವಾಯ ಲವೊದಿಕೀಯದವರ ಸಭೆಯಲ್ಲಿಯೂ ಓದಿಸಿರಿ; ಮತ್ತು ನಾನು ಬರೆದ ಪತ್ರಿಕೆಯನ್ನು ಲವೊದಿಕೀಯದಿಂದ ತರಿಸಿ ನೀವೂ ಓದಿಕೊಳ್ಳಿರಿ.",
			"17": "<sup>17</sup> ಅರ್ಖಿಪ್ಪನಿಗೆ - ನೀನು ಕರ್ತನಿಂದ ಹೊಂದಿರುವ ಸೇವೆಯನ್ನು ನೆರವೇರಿಸುವದಕ್ಕೆ ಎಚ್ಚರವಾಗಿರಬೇಕೆಂದು ಹೇಳಿರಿ.",
			"18": "<sup>18</sup> ಇದು ಪೌಲನೆಂಬ ನಾನು ಸ್ವಂತ ಕೈಯಿಂದ ಬರೆದ ವಂದನೆ. ನಾನು ಸೆರೆಯಲ್ಲಿದ್ದೇನೆಂಬದನ್ನು ಜ್ಞಾಪಕ ಮಾಡಿಕೊಳ್ಳಿರಿ. ಕೃಪೆಯು ನಿಮ್ಮೊಂದಿಗಿರಲಿ."
		}
	}
};
