var book = {
	"name": "ಇಬ್ರಿಯರಿಗೆ",
	"numChapters": 13,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ಪುರಾತನಕಾಲದಲ್ಲಿ ನಮ್ಮ ಪಿತೃಗಳ ಸಂಗಡ ಪ್ರವಾದಿಗಳ ಬಾಯಿಂದ ಭಾಗಭಾಗವಾಗಿಯೂ ವಿಧವಿಧವಾಗಿಯೂ ಮಾತಾಡಿದ ದೇವರು",
			"2": "<sup>2</sup> ಈ ಅಂತ್ಯದಿನಗಳಲ್ಲಿ ನಮ್ಮ ಸಂಗಡ ಮಗನ ಮುಖಾಂತರ ಮಾತಾಡಿದ್ದಾನೆ. ಈತನನ್ನು ಎಲ್ಲಕ್ಕೂ ಬಾಧ್ಯನನ್ನಾಗಿ ನೇಮಿಸಿದನು. ಈತನ ಮೂಲಕವೇ ಜಗತ್ತನ್ನು ಉಂಟುಮಾಡಿದನು.",
			"3": "<sup>3</sup> ಈತನು ದೇವರ ಪ್ರಭಾವದ ಪ್ರಕಾಶವೂ ಆತನ ತತ್ವದ ಮೂರ್ತಿಯೂ ತನ್ನ ಶಕ್ತಿಯ ವಾಕ್ಯದಿಂದ ಸಮಸ್ತಕ್ಕೆ ಆಧಾರವೂ ಆಗಿದ್ದು ಪಾಪವಿಶುದ್ಧಿಮಾಡಿದ ಮೇಲೆ ಉನ್ನತಲೋಕದೊಳಗೆ ಮಹತ್ವವುಳ್ಳ ದೇವರ ಬಲಗಡೆಯಲ್ಲಿ ಆಸನಾರೂಢನಾದನು.",
			"4": "<sup>4</sup> ಈತನು ದೇವದೂತರಿಗಿಂತಲೂ ಉತ್ತಮನಾದನು; ಅವರಿಗಿಂತ ಶ್ರೇಷ್ಠವಾದ ಹೆಸರನ್ನು ಹೊಂದಿದನಲ್ಲಾ; ಹೇಗಂದರೆ -",
			"5": "<sup>5</sup> ನನಗೆ ನೀನು ಮಗನು; ನಾನೇ ಈಹೊತ್ತು ನಿನ್ನನ್ನು ಪಡೆದಿದ್ದೇನೆ. ಎಂತಲೂ ನಾನು ಅವನಿಗೆ ತಂದೆಯಾಗಿರುವೆನು; ಅವನು ನನಗೆ ಮಗನಾಗಿರುವನು ಎಂತಲೂ ದೇವರು ತನ್ನ ದೂತರೊಳಗೆ ಯಾವನಿಗಾದರೂ ಎಂದಾದರೂ ಹೇಳಿದ್ದುಂಟೋ?",
			"6": "<sup>6</sup> ಇದಲ್ಲದೆ ಆತನು ತನ್ನ ಚೊಚ್ಚಲ ಮಗನನ್ನು ಭೂಲೋಕದೊಳಗೆ ತಿರಿಗಿ ಬರಮಾಡುವಾಗ ದೇವರ ದೂತರೆಲ್ಲರೂ ಆತನಿಗೆ ಅಡ್ಡ ಬೀಳಲಿ ಎಂದು ಹೇಳುತ್ತಾನೆ.",
			"7": "<sup>7</sup> ದೂತರ ವಿಷಯದಲ್ಲಿ ಹೇಳುವದೇನೆಂದರೆ - ದೇವರು ತನ್ನ ದೂತರನ್ನು ಗಾಳಿಗಳನ್ನಾಗಿಯೂ ತನ್ನ ಸೇವಕರನ್ನು ಅಗ್ನಿಜ್ವಾಲೆಯನ್ನಾಗಿಯೂ ಮಾಡುತ್ತಾನೆ ಎಂಬದು.",
			"8": "<sup>8</sup> ಮಗನ ವಿಷಯದಲ್ಲಿಯಾದರೋ - ದೇವರೇ, ನಿನ್ನ ಸಿಂಹಾಸನವು ಯುಗಯುಗಾಂತರಗಳಲ್ಲಿಯೂ ಇರುವದು; ನ್ಯಾಯದಂಡವೇ ನಿನ್ನ ರಾಜದಂಡವಾಗಿದೆ;",
			"9": "<sup>9</sup> ನೀನು ಧರ್ಮವನ್ನು ಪ್ರೀತಿಸಿದಿ, ಅಧರ್ಮವನ್ನು ದ್ವೇಷಿಸಿದಿ; ಆದದರಿಂದ ದೇವರು, ನಿನ್ನ ದೇವರೇ, ನಿನ್ನನ್ನು ನಿನ್ನ ಜೊತೆಗಾರರಿಗಿಂತ ಉನ್ನತಸ್ಥಾನಕ್ಕೆ ಏರಿಸಿ ಪರಮಾನಂದತೈಲದಿಂದ ಅಭಿಷೇಕಿಸಿದ್ದಾನೆ ಎಂತಲೂ",
			"10": "<sup>10</sup> ಕರ್ತನೇ, ಆದಿಯಲ್ಲಿ ನೀನು ಭೂಮಿಗೆ ಅಸ್ತಿವಾರವನ್ನು ಹಾಕಿದಿ, ಗಗನಮಂಡಲವು ನಿನ್ನ ಕೈಕೆಲಸವಾಗಿದೆ;",
			"11": "<sup>11</sup> ಅವು ನಾಶವಾಗುವವು, ಆದರೆ ನೀನು ಶಾಶ್ವತವಾಗಿರುತ್ತೀ; ಅವೆಲ್ಲವೂ ವಸ್ತ್ರದಂತೆ ಹಳೇವಾಗುವವು,",
			"12": "<sup>12</sup> ಅವುಗಳನ್ನು ಮೇಲಂಗಿಯಂತೆಯೂ ವಸ್ತ್ರದಂತೆಯೂ ಮಡಿಸುತ್ತೀ, ಮತ್ತು ಅವು ಮಾರ್ಪಡುವವು; ನೀನಾದರೋ ಏಕರೀತಿಯಾಗಿದ್ದೀ. ನಿನ್ನ ವರುಷಗಳು ಮುಗಿದುಹೋಗುವದಿಲ್ಲ ಎಂತಲೂ ಹೇಳುತ್ತಾನೆ.",
			"13": "<sup>13</sup> ಆದರೆ ದೇವದೂತರಲ್ಲಿ ಯಾವ ವಿಷಯದಲ್ಲಿಯಾದರೂ - ನಾನು ನಿನ್ನ ವಿರೋಧಿಗಳನ್ನು ನಿನಗೆ ಪಾದಪೀಠವಾಗ ಮಾಡುವತನಕ ನನ್ನ ಬಲಗಡೆಯಲ್ಲಿ ಕೂತುಕೊಂಡಿರು ಎಂಬದಾಗಿ ಎಂದಾದರೂ ಹೇಳಿದ್ದಾನೋ?",
			"14": "<sup>14</sup> ಇವರೆಲ್ಲರು ರಕ್ಷಣೆಯನ್ನು ಬಾಧ್ಯವಾಗಿ ಹೊಂದಬೇಕಾಗಿರುವವರ ಸೇವೆಗೋಸ್ಕರ ಕಳುಹಿಸಲ್ಪಡುವ ಊಳಿಗದ ಆತ್ಮಗಳಲ್ಲವೋ?"
		},
		"2": {
			"1": "<sup>1</sup> ಆದದರಿಂದ ನಾವು ಕೇಳಿದ ಸಂಗತಿಗಳಿಗೆ ತಪ್ಪಿಹೋದೇವೆಂದು ಭಯಪಟ್ಟು ಅವುಗಳಿಗೆ ಹೆಚ್ಚಾಗಿ ಲಕ್ಷ್ಯಕೊಡುವವರಾಗಿರಬೇಕು.",
			"2": "<sup>2</sup> ಯಾಕಂದರೆ ದೇವದೂತರ ಮೂಲಕ ಹೇಳಲ್ಪಟ್ಟ ವಾಕ್ಯವು ಸ್ಥಿರವಾಗಿದ್ದು ಅದನ್ನು ಮೀರಿ ಮಾಡಿದ ಪ್ರತಿಯೊಂದು ತಪ್ಪಿಗೂ ಅವಿಧೇಯತ್ವಕ್ಕೂ ನ್ಯಾಯವಾದ ಪ್ರತಿಫಲವುಂಟಾದ ಮೇಲೆ [ನಮ್ಮ ಮುಂದಿಟ್ಟಿರುವ]",
			"3": "<sup>3</sup> ಈ ಅತ್ಯಂತ ವಿಶೇಷ ರಕ್ಷಣೆಯನ್ನು ನಾವು ಅಲಕ್ಷ್ಯಮಾಡಿದರೆ ತಪ್ಪಿಸಿಕೊಳ್ಳುವದು ಹೇಗೆ? ಇದು ಕರ್ತನಿಂದ ಮೊದಲು ಹೇಳಲ್ಪಟ್ಟಿತು; ಆತನಿಂದ ಕೇಳಿದವರು ಇದನ್ನು ನಮಗೆ ಸ್ಥಿರಪಡಿಸಿದರು;",
			"4": "<sup>4</sup> ಮತ್ತು ದೇವರು ಅವರ ಕೈಯಿಂದ ಸೂಚಕಕಾರ್ಯಗಳನ್ನೂ ಅದ್ಭುತಕಾರ್ಯಗಳನ್ನೂ ನಾನಾ ವಿಧವಾದ ಮಹತ್ಕಾರ್ಯಗಳನ್ನೂ ನಡಿಸಿ ಪವಿತ್ರಾತ್ಮವರಗಳನ್ನು ತನ್ನ ಚಿತ್ತಾನುಸಾರವಾಗಿ ಅವರಿಗೆ ಅನುಗ್ರಹಿಸಿ ಅವರ ಮಾತಿಗೆ ಸಾಕ್ಷಿಕೊಡುತ್ತಿದ್ದನು.",
			"5": "<sup>5</sup> ನಾವು ಪ್ರಸ್ತಾಪಿಸುವ ಮುಂದಣ ಸಾಮ್ರಾಜ್ಯವನ್ನು ಆತನು ದೇವದೂತರಿಗೆ ಅಧೀನ ಮಾಡಲಿಲ್ಲವಲ್ಲಾ.",
			"6": "<sup>6</sup> ಶಾಸ್ತ್ರದ ಒಂದು ಭಾಗದಲ್ಲಿ - ಮನುಷ್ಯನು ಎಷ್ಟು ಮಾತ್ರದವನು; ಅವನನ್ನು ನೀನು ಯಾಕೆ ನೆನಸಬೇಕು? ಮಾನವನು ಎಷ್ಟರವನು; ಅವನಲ್ಲಿ ಯಾಕೆ ಲಕ್ಷ್ಯವಿಡಬೇಕು?",
			"7": "<sup>7</sup> ಅವನನ್ನು ದೇವದೂತರಿಗಿಂತ ಸ್ವಲ್ಪವೇ ಕಡಿಮೆಯಾಗಿ ಮಾಡಿದಿಯಲ್ಲಾ; ಪ್ರಭಾವವನ್ನೂ ಮಾನವನ್ನೂ ಅವನಿಗೆ ಕಿರೀಟವಾಗಿ ಇಟ್ಟಿ;",
			"8": "<sup>8</sup> ಎಲ್ಲವನ್ನೂ ಅವನ ಪಾದಗಳ ಕೆಳಗೆ ಹಾಕಿ ಅವನಿಗೆ ಅಧೀನಮಾಡಿದ್ದೀಯಲ್ಲವೇ ಎಂದು ಸ್ಪಷ್ಟವಾಗಿ ಹೇಳಿದನು. ಆತನು ಎಲ್ಲವನ್ನೂ ಮನುಷ್ಯನಿಗೆ ಅಧೀನಮಾಡಿದನೆಂಬದರಲ್ಲಿ ಅವನಿಗೆ ಒಂದನ್ನಾದರೂ ಅಧೀನಮಾಡದೆ ಬಿಡಲಿಲ್ಲವೆಂದು ಹೇಳಿದ ಹಾಗಾಯಿತು; ಆದರೆ ಎಲ್ಲವೂ ಅವನಿಗೆ ಅಧೀನವಾಗಿರುವದನ್ನು ನಾವು ಇನ್ನೂ ಕಾಣುವದಿಲ್ಲ.",
			"9": "<sup>9</sup> ಆದರೂ ದೇವದೂತರಿಗಿಂತ ಸ್ವಲ್ಪ ಕಡಿಮೆಯಾಗಿ ಮಾಡಲ್ಪಟ್ಟ ಒಬ್ಬಾತನು, ಅಂದರೆ ಯೇಸುವು, ಪ್ರಭಾವವನ್ನೂ ಮಾನವನ್ನೂ ಕಿರೀಟವಾಗಿ ಹೊಂದಿರುವದನ್ನು ನಾವು ನೋಡುತ್ತೇವೆ. ಮೃತಪಟ್ಟದ್ದರಿಂದಲೇ ಮಾನ ಪ್ರಭಾವಗಳನ್ನು ಹೊಂದಿದನು. ಆತನು ದೇವರ ಕೃಪೆಯಿಂದ ಎಲ್ಲರಿಗೋಸ್ಕರ ಮರಣವನ್ನು ಅನುಭವಿಸಲೇಬೇಕಾಗಿತ್ತು.",
			"10": "<sup>10</sup> ಸಮಸ್ತವು ಯಾವನಿಗೋಸ್ಕರವೂ ಯಾವನಿಂದಲೂ ಉಂಟಾಯಿತೋ ಆತನು ಬಹು ಮಂದಿ ಪುತ್ರರನ್ನು ಪ್ರಭಾವಕ್ಕೆ ಸೇರಿಸುವಲ್ಲಿ ಅವರ ರಕ್ಷಣಾಕರ್ತನನ್ನು ಬಾಧೆಗಳ ಮೂಲಕ ಸಿದ್ಧಿಗೆ ತರುವದು ಆತನಿಗೆ ಯುಕ್ತವಾಗಿತ್ತು.",
			"11": "<sup>11</sup> ಯಾಕಂದರೆ ಪವಿತ್ರ ಮಾಡುವಾತನಿಗೂ ಪವಿತ್ರರಾಗುವವರೆಲ್ಲರಿಗೂ ಒಬ್ಬನೇ ತಂದೆ ಇದ್ದಾನೆ. ಈ ಕಾರಣದಿಂದ ಪವಿತ್ರ ಮಾಡುವಾತನು ಪವಿತ್ರರಾಗುವವರನ್ನು ಸಹೋದರರೆನ್ನುವದಕ್ಕೆ ನಾಚಿಕೆಪಡದೆ -",
			"12": "<sup>12</sup> ನಿನ್ನ ನಾಮಮಹಿಮೆಯನ್ನು ನನ್ನ ಸಹೋದರರಿಗೆ ತಿಳಿಸುವೆನು; ಸಭಾಮಧ್ಯದಲ್ಲಿ ನಿನಗೆ ಸ್ತುತಿಪದಗಳನ್ನು ಹಾಡುವೆನು ಎಂತಲೂ",
			"13": "<sup>13</sup> ನಾನೂ ದೇವರ ಮೇಲೆ ಭರವಸವಿಟ್ಟವನಾಗಿರುವೆನು ಎಂತಲೂ ಇಗೋ, ನಾನೂ ದೇವರು ನನಗೆ ದಯಪಾಲಿಸಿರುವ ಮಕ್ಕಳೂ ಇದ್ದೇವೆ ಎಂತಲೂ ಹೇಳುತ್ತಾನೆ.",
			"14": "<sup>14</sup> ಇದಲ್ಲದೆ ಮಕ್ಕಳು ರಕ್ತ ಮಾಂಸಧಾರಿಗಳಾಗಿರುವದರಿಂದ ಆತನೂ ಅವರಂತೆಯೇ ಆದನು. ತನ್ನ ಮರಣದಿಂದಲೇ ಮರಣಾಧಿಕಾರಿಯನ್ನು ಅಂದರೆ ಸೈತಾನನನ್ನು ಅಡಗಿಸಿಬಿಡುವದಕ್ಕೂ,",
			"15": "<sup>15</sup> ಮರಣಭಯದ ದೆಸೆಯಿಂದ ತಮ್ಮ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ದಾಸತ್ವದೊಳಗಿದ್ದವರನ್ನು ಬಿಡಿಸುವದಕ್ಕೂ ಅವರಂತೆ ರಕ್ತಮಾಂಸಧಾರಿಯಾದನು.",
			"16": "<sup>16</sup> ಆತನು ದೇವದೂತರನ್ನು ಹಿಡಿದವನೆಂದು ಬರೆದಿಲ್ಲ; ಅಬ್ರಹಾಮನ ಸಂತತಿಯನ್ನು ಕೈಹಿಡಿದಿದ್ದಾನಷ್ಟೆ.",
			"17": "<sup>17</sup> ಆದದರಿಂದ ಆತನು ಎಲ್ಲಾ ವಿಷಯಗಳಲ್ಲಿ ತನ್ನ ಸಹೋದರರಿಗೆ ಸಮಾನನಾಗಬೇಕಾಗಿ ಬಂತು. ಹೀಗೆ ಆತನು ಜನರ ಪಾಪಗಳನ್ನು ನಿವಾರಣಮಾಡುವದಕ್ಕಾಗಿ ದೇವರ ಕಾರ್ಯಗಳಲ್ಲಿ ಕರುಣೆಯೂ ನಂಬಿಕೆಯೂ ಉಳ್ಳ ಮಹಾಯಾಜಕನಾದನು.",
			"18": "<sup>18</sup> ತಾನೇ ಶೋಧಿಸಲ್ಪಟ್ಟು ಬಾಧೆಯನ್ನು ಅನುಭವಿಸಿರುವದರಿಂದ ಶೋಧಿಸಲ್ಪಡುವವರಿಗೆ ಸಹಾಯಮಾಡುವದಕ್ಕೆ ಶಕ್ತನಾಗಿದ್ದಾನೆ."
		},
		"3": {
			"1": "<sup>1</sup> ಆದದರಿಂದ ದೇವಜನರಾದ ಸಹೋದರರೇ, ಪರಲೋಕಸ್ವಾಸ್ಥ್ಯಕ್ಕಾಗಿ ನನ್ನೊಂದಿಗೆ ಕರೆಯಲ್ಪಟ್ಟವರೇ, ನಾವು ಪ್ರತಿಜ್ಞೆಮಾಡಿ ಒಪ್ಪಿಕೊಂಡಿರುವ ದೇವಪ್ರೇಷಿತನೂ ಮಹಾಯಾಜಕನೂ ಆಗಿರುವ ಯೇಸುವನ್ನು ಲಕ್ಷ್ಯವಿಟ್ಟು ಯೋಚಿಸಿರಿ.",
			"2": "<sup>2</sup> ಮೋಶೆಯು ದೇವರ ಮನೆಯಲ್ಲೆಲ್ಲಾ ನಂಬಿಗಸ್ತನಾಗಿದ್ದಂತೆಯೇ ಯೇಸುವೂ ತನ್ನನ್ನು ನೇಮಿಸಿದಾತನಿಗೆ ನಂಬಿಗಸ್ತನಾಗಿದ್ದಾನೆ.",
			"3": "<sup>3</sup> ಮನೆಗಿಂತ ಮನೆಕಟ್ಟಿದವನಿಗೆ ಹೆಚ್ಚಾದ ಮಾನವಿರುವದರಿಂದ ಮೋಶೆಗಿಂತ ಯೇಸುವು ಹೆಚ್ಚಾದ ಮಾನಕ್ಕೆ ಯೋಗ್ಯನೆಂದೆಣಿಸಲ್ಪಟ್ಟಿದ್ದಾನೆ.",
			"4": "<sup>4</sup> ಪ್ರತಿ ಮನೆಯನ್ನು ಯಾರೋ ಒಬ್ಬನು ಕಟ್ಟಿರುವನು; ಸಮಸ್ತವನ್ನು ಕಟ್ಟಿದಾತನು ದೇವರೇ.",
			"5": "<sup>5</sup> ಮೋಶೆಯು ಸೇವಕನಾಗಿ ದೇವರ ಮನೆಯಲ್ಲೆಲ್ಲಾ ನಂಬಿಗಸ್ತನಾಗಿದ್ದನು; ಅವನು ಮುಂದೆ ಪ್ರಕಟವಾಗಬೇಕಾಗಿದ್ದ ಸಂಗತಿಗಳಿಗೆ ಸಾಕ್ಷಿಯಾಗಿದ್ದನು.",
			"6": "<sup>6</sup> ಕ್ರಿಸ್ತನಾದರೋ ಮಗನಾಗಿ ದೇವರ ಮನೆಯ ಮೇಲೆ ಅಧಿಕಾರಿಯಾಗಿದ್ದಾನೆ; ನಾವು ನಮ್ಮ ಧೈರ್ಯವನ್ನೂ ನಮ್ಮ ನಿರೀಕ್ಷೆಯಿಂದುಂಟಾಗುವ ಉತ್ಸಾಹವನ್ನೂ ಕಡೇ ತನಕ ದೃಢವಾಗಿ ಹಿಡಿದುಕೊಂಡವರಾದರೆ ನಾವೇ ದೇವರ ಮನೆಯವರು.",
			"7": "<sup>7</sup> ಆದದರಿಂದ ಪವಿತ್ರಾತ್ಮನು ಹೇಳುವ ಪ್ರಕಾರ - ನೀವು ಈಹೊತ್ತು ದೇವರ ಶಬ್ದಕ್ಕೆ ಕಿವಿಗೊಟ್ಟರೆ",
			"8": "<sup>8</sup> ನಿಮ್ಮ ಹಿರಿಯರು ದೇವರ ಕೋಪವನ್ನೆಬ್ಬಿಸಿದ ಸ್ಥಳದಲ್ಲಿ ಅರಣ್ಯದೊಳಗೆ ದೇವರನ್ನು ಪರೀಕ್ಷಿಸಿದ ದಿನದಲ್ಲಿ ಮಾಡಿದಂತೆ ನಿಮ್ಮ ಹೃದಯಗಳನ್ನು ಕಠಿಣಮಾಡಿಕೊಳ್ಳಬೇಡಿರಿ.",
			"9": "<sup>9</sup> ಅವರು ಅರಣ್ಯದಲ್ಲಿ ನನ್ನನ್ನು ಪರೀಕ್ಷಿಸಿ ಶೋಧಿಸಿ ನಾಲ್ವತ್ತು ವರುಷ ನಾನು ನಡಿಸುತ್ತಿದ್ದ ಕೃತ್ಯಗಳನ್ನು ನೋಡಿದರು.",
			"10": "<sup>10</sup> ಆದದರಿಂದ ನಾನು ಆ ಸಂತತಿಯವರ ಮೇಲೆ ಬಹಳವಾಗಿ ಕೋಪಿಸಿಕೊಂಡು - ಅವರು ಯಾವಾಗಲೂ ಹೃದಯದಲ್ಲಿ ತಪ್ಪಿಹೋಗುವವರು ಎಂದು ಹೇಳಿದೆನು. ಆದರೆ ಅವರು ನನ್ನ ಮಾರ್ಗವನ್ನು ತಿಳುಕೊಳ್ಳಲಿಲ್ಲ.",
			"11": "<sup>11</sup> ಹೀಗಿರಲು ಇವರು ನನ್ನ ವಿಶ್ರಾಂತಿಯಲ್ಲಿ ಸೇರುವದೇ ಇಲ್ಲವೆಂದು ಕೋಪಗೊಂಡು ಪ್ರಮಾಣಮಾಡಿದೆನು.",
			"12": "<sup>12</sup> ಸಹೋದರರೇ, ನೋಡಿಕೊಳ್ಳಿರಿ, ಜೀವಸ್ವರೂಪನಾದ ದೇವರನ್ನು ಬಿಟ್ಟುಹೋಗುವ ಅಪನಂಬಿಕೆಯುಳ್ಳ ಕೆಟ್ಟ ಹೃದಯವು ನಿಮ್ಮೊಳಗೆ ಯಾವನಲ್ಲಿಯೂ ಇರಬಾರದು.",
			"13": "<sup>13</sup> ನಿಮ್ಮಲ್ಲಿ ಒಬ್ಬರಾದರೂ ಪಾಪದಿಂದ ಮೋಸಹೋಗಿ ಕಠಿನರಾಗದಂತೆ ಈಹೊತ್ತು ಎಂಬ ಕಾಲವು ಇರುವ ತನಕ ಪ್ರತಿನಿತ್ಯವೂ ಒಬ್ಬರನ್ನೊಬ್ಬರು ಎಚ್ಚರಿಸಿರಿ.",
			"14": "<sup>14</sup> ಮೊದಲಿಂದಿರುವ ಭರವಸವನ್ನು ಅಂತ್ಯದವರೆಗೂ ದೃಢವಾಗಿ ಹಿಡಿದುಕೊಳ್ಳುವ ಪಕ್ಷಕ್ಕೆ ಕ್ರಿಸ್ತನಲ್ಲಿ ಪಾಲುಗಾರರಾಗಿದ್ದೇವಲ್ಲಾ!",
			"15": "<sup>15</sup> ನೀವು ಈಹೊತ್ತು ದೇವರ ಶಬ್ದಕ್ಕೆ ಕಿವಿಗೊಟ್ಟರೆ, ನಿಮ್ಮ ಹಿರಿಯರು ದೇವರ ಕೋಪವನ್ನೆಬ್ಬಿಸಿದ ಸ್ಥಳದಲ್ಲಿ ಮಾಡಿದಂತೆ ನಿಮ್ಮ ಹೃದಯಗಳನ್ನು ಕಠಿನಮಾಡಿಕೊಳ್ಳಬೇಡಿರಿ",
			"16": "<sup>16</sup> ಎಂಬದರಲ್ಲಿ ಕೇಳಿ ಕೋಪವನ್ನೆಬ್ಬಿಸಿದವರು ಯಾರು? ಮೋಶೆಯ ಕೈಕೆಳಗಿದ್ದು ಐಗುಪ್ತದೊಳಗಿಂದ ಹೊರಟುಬಂದವರೆಲ್ಲರೂ ಅಲ್ಲವೇ.",
			"17": "<sup>17</sup> ಮತ್ತು ನಾಲ್ವತ್ತು ವರುಷ ಆತನು ಯಾರ ಮೇಲೆ ಬಹಳವಾಗಿ ಕೋಪಿಸಿಕೊಂಡನು? ಪಾಪ ಮಾಡಿದವರ ಮೇಲೆ ಅಲ್ಲವೇ.",
			"18": "<sup>18</sup> ಅವರ ಶವಗಳು ಅರಣ್ಯದಲ್ಲಿ ಬಿದ್ದವಲ್ಲಾ. ನನ್ನ ವಿಶ್ರಾಂತಿಯಲ್ಲಿ ನೀವು ಸೇರುವದೇ ಇಲ್ಲವೆಂದು ಯಾರನ್ನು ಕುರಿತು ಪ್ರಮಾಣಮಾಡಿದನು? ಅವಿಧೇಯರನ್ನು ಕುರಿತಲ್ಲವೇ.",
			"19": "<sup>19</sup> ಅವರು ಸೇರಲಾರದೆ ಹೋದದ್ದು ಅಪನಂಬಿಕೆಯಿಂದಲೇ ಎಂದು ತಿಳಿದುಕೊಳ್ಳುತ್ತೇವೆ."
		},
		"4": {
			"1": "<sup>1</sup> ಹೀಗಿರಲಾಗಿ ದೇವರ ವಿಶ್ರಾಂತಿಯಲ್ಲಿ ಸೇರಬಹುದೆಂಬ ವಾಗ್ದಾನವು ಇನ್ನೂ ಇರುವಲ್ಲಿ ನಿಮ್ಮೊಳಗೆ ಯಾವನಾದರೂ ಆ ವಾಗ್ದಾನದ ಫಲವನ್ನು ಹೊಂದದೆ ತಪ್ಪುವವನಾದಾನೋ ಎಂದು ನಾವು ಭಯಪಡೋಣ.",
			"2": "<sup>2</sup> ಅವರಿಗೆ ಶುಭವರ್ತಮಾನವು ಸಾರೋಣವಾದಂತೆಯೇ ನಮಗೂ ಸಾರೋಣವಾಯಿತು; ಆದರೆ ಆ ಕಾಲದಲ್ಲಿ ಕೇಳಿದವರು ನಂಬದೆಹೋದ ಕಾರಣ ಆ ವಾಕ್ಯದಿಂದ ಅವರಿಗೆ ಪ್ರಯೋಜನವಾಗಲಿಲ್ಲ.",
			"3": "<sup>3</sup> ದೇವರ ಕೆಲಸಗಳು ಲೋಕಾದಿಯಿಂದ ಮುಗಿದು ಹೋಗಿ [ಆತನ ವಿಶ್ರಾಂತಿಯು ಸಿದ್ಧವಾಗಿದ್ದರೂ] ಆತನು - ನನ್ನ ವಿಶ್ರಾಂತಿಯಲ್ಲಿ ಇವರು ಸೇರುವದೇ ಇಲ್ಲವೆಂದು ಕೋಪಗೊಂಡು ಪ್ರಮಾಣಮಾಡಿದೆನು ಎಂಬದಾಗಿ ಹೇಳಿದೆನಷ್ಟೆ. ನಂಬಿರುವ ನಾವಾದರೋ ಆ ವಿಶ್ರಾಂತಿಯಲ್ಲಿ ಸೇರುತ್ತಲೇ ಇದ್ದೇವೆ.",
			"4": "<sup>4</sup> ಒಂದು ಸ್ಥಳದಲ್ಲಿ ಏಳನೆಯ ದಿವಸವನ್ನು ಕುರಿತು - ದೇವರು ತನ್ನ ಸೃಷ್ಟಿ ಕಾರ್ಯಗಳನ್ನೆಲ್ಲಾ ಮುಗಿಸಿಬಿಟ್ಟು ಏಳನೆಯ ದಿನದಲ್ಲಿ ವಿಶ್ರಮಿಸಿಕೊಂಡನೆಂದು ಬರೆದದೆ.",
			"5": "<sup>5</sup> ಆದರೆ ಈ ಸ್ಥಳದಲ್ಲಿ - ಇವರು ನನ್ನ ವಿಶ್ರಾಂತಿಯಲ್ಲಿ ಸೇರುವದೇ ಇಲ್ಲವೆಂದು ಹೇಳಿದ್ದಾನೆ.",
			"6": "<sup>6</sup> ಹೀಗಿರಲಾಗಿ ಆ ವಿಶ್ರಾಂತಿಯಲ್ಲಿ ಕೆಲವರು ಸೇರಬೇಕೆಂಬುವ ಮಾತು ಇನ್ನೂ ಇರುವದರಿಂದಲೂ ಮೊದಲು ಶುಭವರ್ತಮಾನವನ್ನು ಕೇಳಿದವರು ಅವಿಧೇಯರಾದ ಕಾರಣ ಅದರಲ್ಲಿ ಸೇರದೆಹೋದದ್ದರಿಂದಲೂ",
			"7": "<sup>7</sup> ಬಹುಕಾಲ ಹೋದನಂತರ ಆತನು ದಾವೀದನ ಬಾಯಿಂದ ಮಾತಾಡಿ ಈಹೊತ್ತೇ ಎಂದು ಒಂದಾನೊಂದು ದಿವಸವನ್ನು ಗೊತ್ತು ಮಾಡುತ್ತಾನೆ; ಹೇಗಂದರೆ - ನೀವು ಈಹೊತ್ತು ದೇವರ ಶಬ್ದಕ್ಕೆ ಕಿವಿಗೊಟ್ಟರೆ ನಿಮ್ಮ ಹೃದಯಗಳನ್ನು ಕಠಿನಮಾಡಿಕೊಳ್ಳಬೇಡಿರಿ ಎಂದು ಮೇಲ್ಕಂಡ ವಚನದಲ್ಲಿ ಹೇಳುತ್ತಾನಷ್ಟೆ.",
			"8": "<sup>8</sup> ಯೆಹೋಶುವನು ಅವರನ್ನು ಆ ವಿಶ್ರಾಂತಿಯಲ್ಲಿ ಸೇರಿಸಿದ್ದಾದರೆ ತರುವಾಯ ಮತ್ತೊಂದು ದಿವಸವನ್ನು ಕುರಿತು ಹೇಳೋಣವಾಗುತ್ತಿರಲಿಲ್ಲ.",
			"9": "<sup>9</sup> ಆದದರಿಂದ ದೇವರ ಜನರು ಅನುಭವಿಸುವದಕ್ಕಿರುವ ಸಬ್ಬತೆಂಬ ವಿಶ್ರಾಂತಿಯು ಇನ್ನೂ ಉಂಟು.",
			"10": "<sup>10</sup> ಹೇಗಂದರೆ ದೇವರು ತನ್ನ ಕೆಲಸಗಳನ್ನು ಮುಗಿಸಿ ಹೇಗೆ ವಿಶ್ರಮಿಸಿಕೊಂಡನೋ ಹಾಗೆಯೇ ಆತನ ವಿಶ್ರಾಂತಿಯಲ್ಲಿ ಸೇರಿರುವವನು ಸಹ ತನ್ನ ಕೆಲಸಗಳನ್ನು ಮುಗಿಸಿ ವಿಶ್ರಮಿಸಿಕೊಂಡಿದ್ದಾನೆ.",
			"11": "<sup>11</sup> ಆದದರಿಂದ ನಾವು ಆ ವಿಶ್ರಾಂತಿಯಲ್ಲಿ ಸೇರುವದಕ್ಕೆ ಪ್ರಯಾಸಪಡೋಣ; ನಮ್ಮಲ್ಲಿ ಒಬ್ಬರಾದರೂ ಅವರ ಅವಿಧೇಯತ್ವವನ್ನು ಅನುಸರಿಸುವವರಾಗಬಾರದು.",
			"12": "<sup>12</sup> ಯಾಕಂದರೆ ದೇವರ ವಾಕ್ಯವು ಸಜೀವವಾದದ್ದು, ಕಾರ್ಯಸಾಧಕವಾದದ್ದು, ಯಾವ ಇಬ್ಬಾಯಿಕತ್ತಿಗಿಂತಲೂ ಹದವಾದದ್ದು, ಪ್ರಾಣಆತ್ಮಗಳನ್ನೂ ಕೀಲುಮಜ್ಜೆಗಳನ್ನೂ ವಿಭಾಗಿಸುವಷ್ಟು ಮಟ್ಟಿಗೂ ತೂರಿಹೋಗುವಂಥದು, ಹೃದಯದ ಆಲೋಚನೆಗಳನ್ನೂ ಉದ್ದೇಶಗಳನ್ನೂ ವಿವೇಚಿಸುವಂಥದು ಆಗಿದೆ.",
			"13": "<sup>13</sup> ನಾವು ಯಾವಾತನಿಗೆ ಲೆಕ್ಕ ಒಪ್ಪಿಸಬೇಕಾಗಿದೆಯೋ ಆತನ ದೃಷ್ಟಿಗೆ ಸಮಸ್ತವೂ ಮುಚ್ಚುಮರೆಯಿಲ್ಲದ್ದಾಗಿಯೂ ಬೈಲಾದದ್ದಾಗಿಯೂ ಅದೆ. ಆತನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಅಗೋಚರವಾಗಿರುವ ಸೃಷ್ಟಿಯು ಒಂದೂ ಇಲ್ಲ.",
			"14": "<sup>14</sup> ಆಕಾಶಮಂಡಲಗಳನ್ನು ದಾಟಿಹೋದ ದೇವಕುಮಾರನಾದ ಯೇಸುವೆಂಬ ಶ್ರೇಷ್ಠ ಮಹಾಯಾಜಕನು ನಮಗಿರುವದರಿಂದ ನಾವು ಮಾಡಿರುವ ಪ್ರತಿಜ್ಞೆಯನ್ನು ಬಿಡದೆ ಹಿಡಿಯೋಣ.",
			"15": "<sup>15</sup> ಯಾಕಂದರೆ ನಮಗಿರುವ ಮಹಾಯಾಜಕನು ನಮ್ಮ ನಿರ್ಬಲಾವಸ್ಥೆಯನ್ನು ಕುರಿತು ಅನುತಾಪವಿಲ್ಲದವನಲ್ಲ; ಆತನು ಸರ್ವ ವಿಷಯಗಳಲ್ಲಿ ನಮ್ಮ ಹಾಗೆ ಶೋಧನೆಗೆ ಗುರಿಯಾದನು, ಪಾಪ ಮಾತ್ರ ಮಾಡಲಿಲ್ಲ.",
			"16": "<sup>16</sup> ಆದದರಿಂದ ನಾವು ಕರುಣೆಯನ್ನು ಹೊಂದುವಂತೆಯೂ ಆತನ ದಯೆಯಿಂದ ಸಮಯೋಚಿತವಾದ ಸಹಾಯವು ನಮಗೆ ದೊರೆಯುವಂತೆಯೂ ಧೈರ್ಯದಿಂದ ಕೃಪಾಸನದ ಮುಂದೆ ಬರೋಣ."
		},
		"5": {
			"1": "<sup>1</sup> ಪ್ರತಿಯೊಬ್ಬ ಮಹಾಯಾಜಕನು ಮನುಷ್ಯರೊಳಗಿಂದ ಆರಿಸಲ್ಪಟ್ಟವನಾಗಿದ್ದು ಮನುಷ್ಯರಿಗೋಸ್ಕರ ದೇವರ ಕಾರ್ಯಗಳನ್ನು ಜರಗಿಸುವದಕ್ಕಾಗಿ ನೇಮಿಸಲ್ಪಡುತ್ತಾನಲ್ಲಾ. ಅವನು ಅವರಿಗೋಸ್ಕರ ಕಾಣಿಕೆಗಳನ್ನೂ ಪಾಪನಿವಾರಣಕ್ಕಾಗಿ ಯಜ್ಞಗಳನ್ನೂ ಸಮರ್ಪಿಸಬೇಕು.",
			"2": "<sup>2</sup> ತಾನೂ ಬಲಹೀನನಾಗಿರುವದರಿಂದ ಅವನು ಜ್ಞಾನವಿಲ್ಲದವರನ್ನೂ ಮಾರ್ಗತಪ್ಪಿ ನಡೆಯುವವರನ್ನೂ ತಾಳ್ಮೆಯಿಂದ ಸಹಿಸಿಕೊಳ್ಳುವದಕ್ಕೆ ಶಕ್ತನಾಗಿರುವನು.",
			"3": "<sup>3</sup> ಆ ಬಲಹೀನತೆಯ ಕಾರಣದಿಂದ ಅವನು ಜನರಿಗೋಸ್ಕರ ಹೇಗೋ ಹಾಗೆಯೇ ತನಗೋಸ್ಕರವೂ ಪಾಪನಿವಾರಣಾರ್ಥವಾಗಿ ಯಜ್ಞಗಳನ್ನು ಮಾಡಬೇಕಾದವನಾಗಿದ್ದಾನೆ.",
			"4": "<sup>4</sup> ಇದಲ್ಲದೆ ಯಾವನೂ ತನ್ನಷ್ಟಕ್ಕೆ ತಾನೇ ಈ ಗೌರವವನ್ನು ವಹಿಸಿಕೊಳ್ಳದೆ ದೇವರಿಂದ ನೇಮಿಸಲ್ಪಟ್ಟು ವಹಿಸಿಕೊಳ್ಳುತ್ತಾನೆ; ಆರೋನನು ಸಹ ದೇವರಿಂದಲೇ ನೇಮಿಸಲ್ಪಟ್ಟು ಆ ಗೌರವವನ್ನು ವಹಿಸಿಕೊಂಡನಲ್ಲಾ.",
			"5": "<sup>5</sup> ಹಾಗೆಯೇ ಕ್ರಿಸ್ತನು ಸಹ ತನ್ನನ್ನು ಘನಪಡಿಸಿಕೊಂಡು ತನ್ನನ್ನು ತಾನೇ ಮಹಾಯಾಜಕನನ್ನಾಗಿ ಮಾಡಿಕೊಳ್ಳಲಿಲ್ಲ; ಆದರೆ - ನನಗೆ ನೀನು ಮಗನು; ನಾನೇ ಈಹೊತ್ತು ನಿನ್ನನ್ನು ಪಡೆದಿದ್ದೇನೆ",
			"6": "<sup>6</sup> ಎಂದು ಹೇಳಿದಾತನೇ ಈ ಗೌರವವನ್ನು ಆತನಿಗೆ ಕೊಟ್ಟನು. ಆತನು ಇನ್ನೊಂದು ಸ್ಥಳದಲ್ಲಿ - ನೀನು ಸದಾಕಾಲವೂ ಮೆಲ್ಕಿಜೆದೇಕನ ತರಹದ ಯಾಜಕನು ಎಂದು ಹೇಳುತ್ತಾನೆ.",
			"7": "<sup>7</sup> ಕ್ರಿಸ್ತನು ತಾನು ಭೂಲೋಕದಲ್ಲಿದ್ದಾಗ ಮರಣಕ್ಕೆ ತಪ್ಪಿಸಿ ಕಾಪಾಡ ಶಕ್ತನಾಗಿರುವಾತನಿಗೆ ಬಲವಾಗಿ ಕೂಗುತ್ತಾ ಕಣ್ಣೀರನ್ನು ಸುರಿಸುತ್ತಾ ಪ್ರಾರ್ಥನೆ ವಿಜ್ಞಾಪನೆಗಳನ್ನು ಸಮರ್ಪಿಸಿ ದೇವರ ಮೇಲಣ ಭಯಭಕ್ತಿಯ ನಿಮಿತ್ತ ಕೇಳಲ್ಪಟ್ಟನು.",
			"8": "<sup>8</sup> ಹೀಗೆ ಆತನು ಮಗನಾಗಿದ್ದರೂ ಅನುಭವಿಸಿದ ಬಾಧೆಗಳಿಂದಲೇ ವಿಧೇಯತೆಯನ್ನು ಕಲಿತುಕೊಂಡನು.",
			"9": "<sup>9</sup> ಇದಲ್ಲದೆ ಆತನು ಸಿದ್ಧಿಗೆ ಬಂದು ದೇವರಿಂದ ಮೆಲ್ಕಿಜೆದೇಕನ ತರಹದ ಮಹಾಯಾಜಕನೆನಿಸಿಕೊಂಡವನಾಗಿ ತನಗೆ ವಿಧೇಯರಾಗಿರುವವರೆಲ್ಲರಿಗೂ ನಿರಂತರವಾದ ರಕ್ಷಣೆಗೆ ಕಾರಣನಾದನು.",
			"11": "<sup>11</sup> ಈ ವಿಷಯದಲ್ಲಿ ನಾವು ಹೇಳಬೇಕಾದ ಮಾತು ಬಹಳ ಉಂಟು, ಆದರೆ ನಿಮ್ಮ ಕಿವಿಗಳು ಮಂದವಾದದರಿಂದ ಅದನ್ನು ವಿವರಿಸುವದು ಕಷ್ಟವಾಗಿದೆ.",
			"12": "<sup>12</sup> ಕಾಲವನ್ನು ನೋಡಿದರೆ ನೀವು ಇಷ್ಟರೊಳಗೆ ಬೋಧಕರಾಗಿರಬೇಕಾಗಿದ್ದರೂ ಒಬ್ಬನು ನಿಮಗೆ ದೈವೋಕ್ತಿಗಳ ಮೂಲಪಾಠಗಳನ್ನು ತಿರಿಗಿ ಕಲಿಸಿಕೊಡಬೇಕಾಗಿದೆ; ನೀವು ಹಾಲು ಕುಡಿಯತಕ್ಕವರೇ ಹೊರತು ಗಟ್ಟಿಯಾದ ಆಹಾರವನ್ನು ತಿನ್ನತಕ್ಕವರಲ್ಲ.",
			"13": "<sup>13</sup> ಹಾಲು ಬೇಕಾದವನು ಕೂಸಿನಂತಿದ್ದು ನೀತಿವಾಕ್ಯದಲ್ಲಿ ಅನುಭವವಿಲ್ಲದವನಾಗಿದ್ದಾನೆ.",
			"14": "<sup>14</sup> ಗಟ್ಟಿಯಾದ ಆಹಾರವು ಪ್ರಾಯಸ್ಥರಿಗೋಸ್ಕರ ಅಂದರೆ ಜ್ಞಾನೇಂದ್ರಿಯಗಳನ್ನು ಸಾಧನೆಯಿಂದ ಶಿಕ್ಷಿಸಿಕೊಂಡು ಇದು ಒಳ್ಳೇದು ಅದು ಕೆಟ್ಟದ್ದು ಎಂಬ ಭೇದವನ್ನು ತಿಳಿದವರಿಗೋಸ್ಕರವಾಗಿದೆ."
		},
		"6": {
			"1": "<sup>1</sup> ಆದದರಿಂದ ನಿರ್ಜೀವಕರ್ಮಗಳ ಮೇಲಣ ನಂಬಿಕೆಯನ್ನು ಬಿಟ್ಟುಬಿಟ್ಟು ದೇವರಲ್ಲಿಯೇ ನಂಬಿಕೆಯಿಡಬೇಕಾದದ್ದು,",
			"2": "<sup>2</sup> ಸ್ನಾನ, ಹಸ್ತಾರ್ಪಣಗಳ ವಿಷಯವಾದ ಉಪದೇಶ, ಸತ್ತವರಿಗೆ ಪುನರುತ್ಥಾನವೂ ನಿತ್ಯವಾದ ನ್ಯಾಯತೀರ್ಪೂ ಉಂಟೆಂಬದು ಇವುಗಳನ್ನು ಪದೇಪದೇ ಅಸ್ತಿವಾರವಾಗಿ ಹಾಕದೆ ಕ್ರಿಸ್ತನ ವಿಷಯವಾದ ಪ್ರಥಮ ಬೋಧನೆಯನ್ನು ಕುರಿತು ಇನ್ನೂ ಮಾತಾಡದೆ ಪೂರ್ಣವಾದ ತಿಳುವಳಿಕೆಗೆ ಸಾಗುತ್ತಾ ಹೋಗೋಣ.",
			"3": "<sup>3</sup> ಮತ್ತು ದೇವರ ಚಿತ್ತವಾದರೆ ಹೀಗೆ ಸಾಗುತ್ತಾ ಹೋಗುವೆವು.",
			"4": "<sup>4</sup> ಒಂದು ಸಾರಿ ಜ್ಞಾನಪ್ರಕಾಶದಲ್ಲಿ ಸೇರಿ ಪರಲೋಕದಿಂದಾದ ದಾನದ ಅನುಭವವನ್ನು ಹೊಂದಿ",
			"5": "<sup>5</sup> ಪವಿತ್ರಾತ್ಮವರದಲ್ಲಿ ಪಾಲುಗಾರರಾಗಿ ದೇವರ ವಾಕ್ಯದ ಶ್ರೇಷ್ಠತೆಯನ್ನೂ ಮುಂದಣ ಯುಗದ ಮಹತ್ವವನ್ನೂ ಅನುಭವಿಸಿದವರು ಭ್ರಷ್ಟರಾದರೆ",
			"6": "<sup>6</sup> ಅವರಲ್ಲಿ ತಿರಿಗಿ ಮಾನಸಾಂತರವನ್ನು ಹುಟ್ಟಿಸುವದು ಅಸಾಧ್ಯ; ಯಾಕಂದರೆ ಅವರು ತಮ್ಮ ಪಾಲಿಗೆ ದೇವರ ಮಗನನ್ನು ಪುನಃ ಶಿಲುಬೆಗೆ ಹಾಕುವವರೂ ಆತನನ್ನು ಎಲ್ಲರ ಮುಂದೆ ಅವಮಾನಪಡಿಸುವವರೂ ಆಗಿದ್ದಾರೆ.",
			"7": "<sup>7</sup> ಭೂಮಿಯು ತನ್ನ ಮೇಲೆ ಅನೇಕಾವರ್ತಿ ಸುರಿಯುವ ಮಳೆಯನ್ನು ಹೀರಿಕೊಂಡು ಅದು ಯಾರ ನಿಮಿತ್ತವಾಗಿ ವ್ಯವಸಾಯ ಮಾಡಲ್ಪಡುತ್ತದೋ ಅವರಿಗೆ ಅನುಕೂಲವಾದ ಬೆಳೆಯನ್ನು ಕೊಟ್ಟರೆ ದೇವರ ಆಶೀರ್ವಾದವನ್ನು ಹೊಂದುತ್ತದೆ;",
			"8": "<sup>8</sup> ಆದರೆ ಅದು ಮುಳ್ಳುಗಿಡಗಳನ್ನೂ ಕಳೆಗಳನ್ನೂ ಬೆಳಸಿದರೆ ಅಯೋಗ್ಯವೆನಿಸಿಕೊಂಡು ಶಾಪಕ್ಕೆ ಗುರಿಯಾಗುತ್ತದೆ, ಅದರ ಅಂತ್ಯವು ಸುಡಲ್ಪಡುವದೇ.",
			"9": "<sup>9</sup> ಆದರೆ ಪ್ರಿಯರೇ, ಈ ರೀತಿಯಾಗಿ ನಾವು ಮಾತಾಡಿದರೂ ನೀವು ಇದಕ್ಕಿಂತ ಉತ್ತಮವಾಗಿಯೂ ರಕ್ಷಣಕರವಾಗಿಯೂ ಇರುವ ಸ್ಥಿತಿಯಲ್ಲಿದ್ದೀರೆಂದು ದೃಢವಾಗಿ ನಂಬಿದ್ದೇನೆ.",
			"10": "<sup>10</sup> ನೀವು ದೇವಜನರಿಗೆ ಉಪಚಾರ ಮಾಡಿದಿರಿ, ಇನ್ನೂ ಮಾಡುತ್ತಾ ಇದ್ದೀರಿ. ಈ ಕೆಲಸವನ್ನೂ ಇದರಲ್ಲಿ ನೀವು ದೇವರ ನಾಮದ ವಿಷಯವಾಗಿ ತೋರಿಸಿದ ಪ್ರೀತಿಯನ್ನೂ ಆತನು ಮರೆಯುವದಕ್ಕೆ ಅನ್ಯಾಯಸ್ಥನಲ್ಲ.",
			"11": "<sup>11</sup> ನೀವು ಉಪಚಾರಮಾಡುವದರಲ್ಲಿ ಯಾವ ಆಸಕ್ತಿಯನ್ನು ತೋರಿಸಿದ್ದೀರೋ ನಿಮ್ಮ ನಿರೀಕ್ಷೆ ದೃಢಮಾಡಿಕೊಂಡು ಕಡೇ ತನಕ ಹಿಡಿಯುವದರಲ್ಲಿಯೂ ನಿಮ್ಮಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬರು ಅದೇ ಆಸಕ್ತಿಯನ್ನು ತೋರಿಸಬೇಕೆಂದು ಅಪೇಕ್ಷಿಸುತ್ತೇವೆ.",
			"12": "<sup>12</sup> ನೀವು ಮಂದಮತಿಗಳಾಗಿರದೆ ಯಾರು ವಾಗ್ದಾನಗಳನ್ನು ನಂಬಿ ಅವುಗಳ ಫಲಕ್ಕೋಸ್ಕರ ಬಹು ದಿವಸಗಳವರೆಗೂ ಕಾದಿದ್ದು ಆ ಫಲವನ್ನು ಹೊಂದುತ್ತಾರೋ ಅವರನ್ನು ಅನುಸರಿಸುವವರಾಗಬೇಕೆಂದು ಕೋರುತ್ತೇನೆ.",
			"13": "<sup>13</sup> ದೇವರು ಅಬ್ರಹಾಮನಿಗೆ ವಾಗ್ದಾನಮಾಡಿದಾಗ ತನಗಿಂತ ಹೆಚ್ಚಿನವನ ಆಣೆಯಿಡುವದಕ್ಕಾಗದೆ ಇದ್ದದರಿಂದ ತನ್ನಾಣೆಯಿಟ್ಟು -",
			"14": "<sup>14</sup> ನಿಜವಾಗಿ ನಿನ್ನನ್ನು ಆಶೀರ್ವದಿಸೇ ಆಶೀರ್ವದಿಸುವೆನು, ನಿನ್ನನ್ನು ಹೆಚ್ಚಿಸೇ ಹೆಚ್ಚಿಸುವೆನು ಎಂದು ಹೇಳಿದನಷ್ಟೆ.",
			"15": "<sup>15</sup> ಆ ವಾಗ್ದಾನದ ಫಲಕ್ಕೋಸ್ಕರ ಅಬ್ರಹಾಮನು ಬಹು ದಿವಸ ಕಾದುಕೊಂಡಿದ್ದು ಆ ಫಲವನ್ನು ದೇವರು ಹೇಳಿದ ಹಾಗೆಯೇ ಹೊಂದಿದನು.",
			"16": "<sup>16</sup> ಮನುಷ್ಯರು ತಮಗಿಂತ ಹೆಚ್ಚಿನವನ ಆಣೆಯಿಡುತ್ತಾರಷ್ಟೆ; ಮಾತಿಗೆ ಆಣೆಯಿಟ್ಟು ಸ್ಥಿರಪಡಿಸಿದ ಮೇಲೆ ಅವರೊಳಗೆ ವಿವಾದವೇನೂ ಇರುವದಿಲ್ಲ.",
			"17": "<sup>17</sup> ಹಾಗೆಯೇ ದೇವರು ತನ್ನ ಸಂಕಲ್ಪವು ನಿಶ್ಚಲವಾದದ್ದೆಂಬದನ್ನು ವಾಗ್ದಾನದ ಫಲಕ್ಕೆ ಬಾಧ್ಯರಾಗುವವರಿಗೆ ಬಹು ಸ್ಪಷ್ಟವಾಗಿ ತೋರಿಸಬೇಕೆಂದು ಆಣೆಯಿಟ್ಟು ತನ್ನ ಮಾತನ್ನು ಸ್ಥಿರಪಡಿಸಿದನು.",
			"18": "<sup>18</sup> ಆಶ್ರಯವನ್ನು ಹೊಂದಬೇಕೆಂದು ಓಡಿಬಂದು ನಮ್ಮ ಮುಂದೆ ಇಟ್ಟಿರುವ ನಿರೀಕ್ಷೆಯನ್ನು ಹಿಡಿದುಕೊಂಡವರಾದ ನಮಗೆ ಎರಡು ನಿಶ್ಚಲವಾದ ಆಧಾರಗಳು ಇರುವ ಕಾರಣ ಬಲವಾದ ಧೈರ್ಯವುಂಟಾಯಿತು.",
			"19": "<sup>19</sup> ಈ ಆಧಾರಗಳನ್ನು ಕೊಟ್ಟದ್ದರಲ್ಲಿ ದೇವರು ಸುಳ್ಳಾಡಿರಲಾರನು. ಆ ನಿರೀಕ್ಷೆಯು ನಮ್ಮ ಪ್ರಾಣಕ್ಕೆ ಲಂಗರದ ಹಾಗಿದ್ದು ಭರವಸಕ್ಕೆ ಯೋಗ್ಯವಾದದ್ದೂ ಸ್ಥಿರವಾದದ್ದೂ ಆಗಿದೆ. ಅದು ತೆರೆಯೊಳಗಣ ದೇವಸಾನ್ನಿಧ್ಯವನ್ನು ಪ್ರವೇಶಿಸುವಂಥದು.",
			"20": "<sup>20</sup> ಯೇಸು ಸದಾಕಾಲವೂ ಮೆಲ್ಕಿಜೆದೇಕನ ತರಹದ ಮಹಾಯಾಜಕನಾಗಿದ್ದು ನಮಗೋಸ್ಕರ ಮುಂದಾಗಿ ಹೋಗಿ ಆ ಸಾನ್ನಿಧ್ಯವನ್ನು ಪ್ರವೇಶಿಸಿದ್ದಾನೆ."
		},
		"7": {
			"1": "<sup>1</sup> ಈ ಮೆಲ್ಕಿಜೆದೇಕನು ಸಾಲೇಮಿನ ಅರಸನೂ ಪರಾತ್ಪರನಾದ ದೇವರ ಯಾಜಕನೂ ಆಗಿದ್ದನು; ರಾಜರನ್ನು ಹೊಡೆದು ಹಿಂತಿರುಗಿ ಬರುತ್ತಿದ್ದ ಅಬ್ರಹಾಮನನ್ನು ಎದುರುಗೊಂಡು ಅವನನ್ನು ಆಶೀರ್ವದಿಸಿದನು;",
			"2": "<sup>2</sup> ಅವನಿಗೆ ಅಬ್ರಹಾಮನು ತಾನು ಜಯಿಸಿಕೊಂಡು ಬಂದಿದ್ದ ಎಲ್ಲಾ ವಸ್ತುಗಳಲ್ಲಿ ಹತ್ತನೆಯ ಒಂದು ಭಾಗವನ್ನು ಕೊಟ್ಟನು. ಅವನ ಹೆಸರಿನ ಅರ್ಥವು ನೀತಿರಾಜ ಎಂಬದು; ಇದಲ್ಲದೆ ಅವನು ಸಾಲೇಮಿನರಾಜ ಅಂದರೆ ಸಮಾಧಾನರಾಜ;",
			"3": "<sup>3</sup> ಅವನಿಗೆ ತಂದೆಯೂ ತಾಯಿಯೂ ವಂಶಾವಳಿಯೂ ಇಲ್ಲ, ಜನ್ಮದ ಆರಂಭವೂ ಆಯುಷ್ಯದ ಅಂತ್ಯವೂ ಇಲ್ಲ. ಅವನು ದೇವರ ಮಗನಿಗೆ ಸಮಾನ ಮಾಡಲ್ಪಟ್ಟಿದ್ದಾನೆ. ಅವನು ನಿರಂತರವಾಗಿ ಯಾಜಕನಾಗಿರುವವನು.",
			"4": "<sup>4</sup> ಈ ಮನುಷ್ಯನು ಎಷ್ಟು ದೊಡ್ಡವನೆಂದು ಆಲೋಚಿಸಿರಿ. ನಮ್ಮ ಮೂಲಪಿತೃವಾದ ಅಬ್ರಹಾಮನು ತಾನು ಸುಲುಕೊಂಡು ಬಂದ ಶ್ರೇಷ್ಠವಸ್ತುಗಳಲ್ಲಿ ಹತ್ತನೆಯ ಒಂದು ಭಾಗವನ್ನು ಅವನಿಗೆ ಕೊಟ್ಟನಲ್ಲಾ.",
			"5": "<sup>5</sup> ಲೇವಿಯ ಕುಲದವರಲ್ಲಿ ಯಾಜಕೋದ್ಯೋಗವನ್ನು ಹೊಂದುವವರು ಜನರಿಂದ ಅಂದರೆ ಅಬ್ರಹಾಮನ ವಂಶಸ್ಥರಾಗಿರುವ ತಮ್ಮ ಸಹೋದರರಿಂದಲೇ ದಶಮಭಾಗಗಳನ್ನು ತೆಗೆದುಕೊಳ್ಳುವದಕ್ಕೆ ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿ ಅಪ್ಪಣೆ ಉಂಟು.",
			"6": "<sup>6</sup> ಆದರೆ ಅವರ ವಂಶಾವಳಿಗೆ ಸೇರದೆ ಇರುವ ಮೆಲ್ಕಿಜೆದೇಕನು ಅಬ್ರಹಾಮನಿಂದಲೇ ದಶಮಭಾಗಗಳನ್ನು ತಕ್ಕೊಂಡದ್ದಲ್ಲದೆ ದೇವರಿಂದ ವಾಗ್ದಾನಗಳನ್ನು ಹೊಂದಿದವನಾದ ಅವನಿಗೆ ಆಶೀರ್ವಾದ ಕೊಟ್ಟನು.",
			"7": "<sup>7</sup> ಆಶೀರ್ವಾದ ಹೊಂದುವವನಿಗಿಂತ ಆಶೀರ್ವದಿಸುವವನು ದೊಡ್ಡವನು ಎಂಬದು ವಿವಾದವಿಲ್ಲದ ಮಾತಷ್ಟೆ.",
			"8": "<sup>8</sup> ಇದಲ್ಲದೆ ಲೇವಿಯರ ಕ್ರಮವನ್ನು ನೋಡಿದರೆ ಸಾಯತಕ್ಕ ಮನುಷ್ಯರು ದಶಮಭಾಗಗಳನ್ನು ತಕ್ಕೊಳ್ಳುತ್ತಾರೆ; ಆದರೆ ಮೆಲ್ಕಿಜೆದೇಕನ ಕ್ರಮವನ್ನು ನೋಡಿದರೆ ಜೀವಿಸುತ್ತಿದ್ದಾನೆಂದು ಸಾಕ್ಷಿಹೊಂದಿರುವವನು ತಕ್ಕೊಳ್ಳುತ್ತಾನೆ.",
			"9": "<sup>9</sup> ಇದು ಮಾತ್ರವಲ್ಲದೆ ದಶಮಭಾಗಗಳನ್ನು ತಕ್ಕೊಳ್ಳುವ ಲೇವಿಯೂ ಕೂಡ ಅಬ್ರಹಾಮನ ಮೂಲಕ ದಶಮಭಾಗಗಳನ್ನು ಕೊಟ್ಟ ಹಾಗಾಯಿತೆಂದು ಹೇಳಬಹುದು;",
			"10": "<sup>10</sup> ಹೇಗಂದರೆ ಮೆಲ್ಕಿಜೆದೇಕನು ಲೇವಿಯ ಮೂಲಪುರುಷನನ್ನು ಎದುರುಗೊಂಡಾಗ ಲೇವಿಯು ತತ್ವರೂಪವಾಗಿ ಇವನೊಳಗೆ ಇದ್ದನು.",
			"11": "<sup>11</sup> ಇಸ್ರಾಯೇಲ್ಯರಿಗೆ ಉಂಟಾದ ಧರ್ಮಶಾಸ್ತ್ರವು ಲೇವಿಕ ಯಾಜಕತ್ವದ ಮೇಲೆಯೇ ಆಧಾರಗೊಂಡಿದೆಯಷ್ಟೆ. ಆ ಯಾಜಕತ್ವದಿಂದ ಸಂಪೂರ್ಣಸಿದ್ಧಿ ಉಂಟಾಗಿದ್ದರೆ ಆರೋನನ ತರಹದವನಾಗಿರದೆ ಮೆಲ್ಕಿಜೆದೇಕನ ತರಹದ ಬೇರೊಬ್ಬ ಯಾಜಕನು ಬರುವದಕ್ಕೆ ಅವಶ್ಯವೇನಿತ್ತು?",
			"12": "<sup>12</sup> ಯಾಜಕತ್ವವು ಬೇರೆಯಾದರೆ ಧರ್ಮಶಾಸ್ತ್ರವು ಕೂಡ ಬೇರೆಯಾಗುವದು ಅಗತ್ಯ.",
			"13": "<sup>13</sup> ಮೇಲ್ಕಂಡ ಮಾತು ಯಾವಾತನ ವಿಷಯದಲ್ಲಿ ಹೇಳಿದೆಯೋ ಆತನು ಬೇರೊಂದು ಕುಲದಲ್ಲಿ ಹುಟ್ಟಿದವನು; ಆ ಕುಲದವರಲ್ಲಿ ಒಬ್ಬರೂ ಯಜ್ಞವೇದಿಯ ಬಳಿಯಲ್ಲಿ ಸೇವೆಮಾಡಿದ್ದಿಲ್ಲ.",
			"14": "<sup>14</sup> ನಮ್ಮ ಕರ್ತನು ಯೆಹೂದ ಕುಲದಲ್ಲಿ ಜನಿಸಿದವನೆಂಬದು ಪ್ರಸಿದ್ಧವಾಗಿದೆಯಷ್ಟೆ; ಈ ಕುಲದ ವಿಷಯದಲ್ಲಿ ಮೋಶೆಯು ಯಾಜಕರ ಸಂಬಂಧವಾಗಿ ಒಂದು ಮಾತನ್ನಾದರೂ ಹೇಳಲಿಲ್ಲ.",
			"15": "<sup>15</sup> ಇದಲ್ಲದೆ ಬೇರೊಬ್ಬ ಯಾಜಕನು ಶರೀರಧರ್ಮ ವಿಷಯವಾದ ನಿಯಮದ ಪ್ರಕಾರವಾಗಿರದೆ ಮೆಲ್ಕಿಜೆದೇಕನ ಸಾದೃಶ್ಯದಲ್ಲಿ ನಿರ್ಲಯವಾದ ಜೀವದ ಶಕ್ತಿಯುಳ್ಳವನಾಗಿದ್ದು ಬರಬೇಕಾದದರಿಂದ ನಮ್ಮ ಸಿದ್ಧಾಂತ ಮತ್ತೂ ಸ್ವಷ್ಟವಾಯಿತು.",
			"17": "<sup>17</sup> ಆತನ ವಿಷಯದಲ್ಲಿ - ನೀನು ಸದಾಕಾಲವೂ ಮೆಲ್ಕಿಜೆದೇಕನ ತರಹದ",
			"18": "<sup>18</sup> ಯಾಜಕನಾಗಿದ್ದೀ ಎಂದು ಹೇಳಿಯದೆಯಲ್ಲಾ.",
			"19": "<sup>19</sup> ಧರ್ಮಶಾಸ್ತ್ರವು ಯಾವದನ್ನೂ ಸಿದ್ಧಿಗೆ ತಾರದೆ ಇರುವದರಿಂದ ಮೊದಲಿದ್ದ ನಿಯಮವು ನಿರ್ಬಲವಾಗಿಯೂ ನಿಷ್ಪ್ರಯೋಜಕವಾಗಿಯೂ ಇದ್ದು ರದ್ದಾಗಿಹೋಯಿತು. ಅದಕ್ಕೆ ಬದಲಾಗಿ ಉತ್ತಮವಾಗಿರುವ ಒಂದು ನಿರೀಕ್ಷಾಧಾರವು ಪ್ರಾಬಲ್ಯಕ್ಕೆ ಬಂತು. ಈ ನಿರೀಕ್ಷಾಧಾರವು ನಮ್ಮನ್ನು ದೇವರ ಸನ್ನಿಧಿಗೆ ಸೇರಿಸುವಂಥದು.",
			"20": "<sup>20</sup> ಇದಲ್ಲದೆ ಲೇವಿಯರು ಆಣೆಯಿಲ್ಲದೆ ಯಾಜಕರಾದರು; ಆತನಾದರೋ ಆಣೆಯೊಡನೆ ಯಾಜಕನಾಗಿ ಮಾಡಲ್ಪಟ್ಟನು. ದೇವರು ಆತನಿಗೆ - ನೀನು ಸದಾಕಾಲವೂ ಯಾಜಕನಾಗಿದ್ದೀ ಎಂದು ಕರ್ತನೆಂಬ ನಾನು ಆಣೆಯಿಟ್ಟು ನುಡಿದೆನು; ಪಶ್ಚಾತ್ತಾಪಪಡುವದಿಲ್ಲ ಎಂಬದಾಗಿ ಹೇಳಿದನಲ್ಲಾ.",
			"22": "<sup>22</sup> ಯೇಸು ಆಣೆಯೊಡನೆಯೇ ಯಾಜಕನಾದದರಿಂದ ಎಷ್ಟೋ ಶ್ರೇಷ್ಠವಾದ ಒಡಂಬಡಿಕೆಗೆ ಹೊಣೆಗಾರನಾದನು.",
			"23": "<sup>23</sup> ಲೇವಿಯರು ಶಾಶ್ವತವಾಗಿ ಉದ್ಯೋಗ ನಡಿಸುವದಕ್ಕೆ ಮರಣವು ಅಡ್ಡಿಯಾದದರಿಂದ ಅವರಲ್ಲಿ ಯಾಜಕರಾದವರು ಅನೇಕರು;",
			"24": "<sup>24</sup> ಆತನಾದರೋ ಸದಾಕಾಲವಿರುವದರಿಂದ ಆತನ ಯಾಜಕತ್ವವು ಮತ್ತೊಬ್ಬರಿಗೆ ಹೋಗುವಂಥದಲ್ಲ.",
			"25": "<sup>25</sup> ಆದಕಾರಣ ಆತನು ತನ್ನ ಮೂಲಕ ದೇವರ ಬಳಿಗೆ ಬರುವವರನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ರಕ್ಷಿಸುವದಕ್ಕೆ ಶಕ್ತನಾಗಿದ್ದಾನೆ; ಅವರಿಗೋಸ್ಕರ ವಿಜ್ಞಾಪನೆಮಾಡುವದಕ್ಕೆ ಯಾವಾಗಲೂ ಬದುಕುವವನಾಗಿದ್ದಾನೆ.",
			"26": "<sup>26</sup> ಇಂಥವನೇ ನಮಗೆ ಬೇಕಾದ ಮಹಾಯಾಜಕನು. ಈತನು ಪರಿಶುದ್ಧನೂ ನಿರ್ದೋಷಿಯೂ ನಿಷ್ಕಳಂಕನೂ ಪಾಪಿಗಳಲ್ಲಿ ಸೇರದೆ ಪ್ರತ್ಯೇಕವಾಗಿರುವವನೂ ಆಕಾಶಮಂಡಲಗಳಿಗಿಂತ ಉನ್ನತದಲ್ಲಿರುವವನೂ ಆಗಿರುವನು.",
			"27": "<sup>27</sup> ಮೊದಲು ತಮ್ಮ ಪಾಪಪರಿಹಾರಕ್ಕಾಗಿ ಆಮೇಲೆ ಜನರ ಪಾಪಪರಿಹಾರಕ್ಕಾಗಿ ಸಮರ್ಪಣೆಮಾಡುವ ಲೇವಿಕ ಮಹಾಯಾಜಕರಂತೆ ಈತನು ಪ್ರತಿನಿತ್ಯವೂ ಸಮರ್ಪಿಸಬೇಕಾದ ಅವಶ್ಯವಿಲ್ಲ. ಈತನು ತನ್ನನ್ನೇ ಸಮರ್ಪಿಸಿಕೊಂಡು ಒಂದೇ ಸಾರಿ ಆ ಕೆಲಸವನ್ನು ಮಾಡಿ ಮುಗಿಸಿದನು.",
			"28": "<sup>28</sup> ಧರ್ಮಶಾಸ್ತ್ರವು ನಿರ್ಬಲರಾದ ಮನುಷ್ಯರನ್ನು ಮಹಾಯಾಜಕರನ್ನಾಗಿ ನೇಮಿಸುತ್ತದೆ; ಆದರೆ ಧರ್ಮಶಾಸ್ತ್ರದ ತರುವಾಯ ಆಣೆಯೊಡನೆ ಉಂಟಾದ ವಾಕ್ಯವು ಸದಾಕಾಲಕ್ಕೂ ಸರ್ವಸಂಪೂರ್ಣನಾಗಿರುವ ಮಗನನ್ನೇ ನೇಮಿಸುತ್ತದೆ."
		},
		"8": {
			"1": "<sup>1</sup> ನಾವು ಈಗ ಹೇಳುವ ಸಂಗತಿಗಳಲ್ಲಿ ಮುಖ್ಯವಾದ ಮಾತೇನಂದರೆ - ಪರಲೋಕದೊಳಗೆ ಮಹತ್ವವುಳ್ಳ ದೇವರ ಸಿಂಹಾಸನದ ಬಲಗಡೆಯಲ್ಲಿ ಆಸನಾರೂಢನಾದಂಥ ಮಹಾಯಾಜಕನು ನಮಗಿದ್ದಾನೆ.",
			"2": "<sup>2</sup> ಈತನು ಪವಿತ್ರಸ್ಥಾನದಲ್ಲಿ ಅಂದರೆ ಮನುಷ್ಯನು ಹಾಕದೆ ಕರ್ತನೇ ಹಾಕಿದ ನಿಜವಾದ ದೇವದರ್ಶನಗುಡಾರದಲ್ಲಿ ಯಾಜಕೋದ್ಯೋಗ ನಡಿಸುವವನಾಗಿದ್ದಾನೆ.",
			"3": "<sup>3</sup> ಪ್ರತಿಯೊಬ್ಬ ಮಹಾಯಾಜಕನು ಕಾಣಿಕೆಗಳನ್ನೂ ಯಜ್ಞಗಳನ್ನೂ ಸಮರ್ಪಿಸುವದಕ್ಕೆ ನೇಮಕವಾಗಿರುತ್ತಾನಷ್ಟೆ ; ಆದದರಿಂದ ಸಮರ್ಪಿಸುವದಕ್ಕೆ ಈ ಮಹಾಯಾಜಕನಿಗೂ ಏನಾದರೂ ಇರಬೇಕು.",
			"4": "<sup>4</sup> ಈತನು ಇನ್ನೂ ಭೂಮಿಯ ಮೇಲೆ ಇದ್ದದ್ದೇ ಆದರೆ ಯಾಜಕನಾಗಿರುತ್ತಿರಲಿಲ್ಲ; ಯಾಕಂದರೆ ಧರ್ಮನಿಯಮದ ಪ್ರಕಾರ ಕಾಣಿಕೆಗಳನ್ನು ಸಮರ್ಪಿಸುವವರು ಇದ್ದೇ ಇರುತ್ತಾರೆ;",
			"5": "<sup>5</sup> ಅವರು ಪರಲೋಕದಲ್ಲಿರುವ ದೇವಾಲಯದ ಪ್ರತಿರೂಪವೂ ಛಾಯೆಯೂ ಆಗಿರುವ ಆಲಯದಲ್ಲಿ ಯಾಜಕತ್ವವನ್ನು ನಡಿಸುವವರು. ಮೋಶೆಯು ದೇವದರ್ಶನಗುಡಾರವನ್ನು ಮಾಡುವದಕ್ಕಿದ್ದಾಗ ಬೆಟ್ಟದಲ್ಲಿ ನಾನು ನಿನಗೆ ತೋರಿಸಿದ ಮಾದರಿಯ ಪ್ರಕಾರವೇ ಎಲ್ಲವನ್ನು ಮಾಡಿಸಬೇಕು ಎಂಬ ದೈವೋಕ್ತಿಯು ಅವನಿಗೆ ಉಂಟಾಯಿತಲ್ಲಾ.",
			"6": "<sup>6</sup> ಆದರೆ ಯೇಸು ಅದಕ್ಕಿಂತ ಶ್ರೇಷ್ಠವಾದ ಯಾಜಕೋದ್ಯೋಗವನ್ನು ಹೊಂದಿದವನಾಗಿದ್ದಾನೆ; ಉತ್ತಮ ವಾಗ್ದಾನಗಳ ಮೇಲೆ ಸ್ಥಾಪಿತವಾದ ಉತ್ತಮವಾದ ಒಡಂಬಡಿಕೆಗೆ ಮಧ್ಯಸ್ಥನಾಗಿದ್ದಾನೆ.",
			"7": "<sup>7</sup> ಆ ಮೊದಲನೆಯ ಒಡಂಬಡಿಕೆಯು ದೋಷವಿಲ್ಲದ್ದಾಗಿದ್ದರೆ ಎರಡನೆಯ ಒಡಂಬಡಿಕೆಗೆ ಅವಕಾಶವು ಇರುತ್ತಿರಲಿಲ್ಲ.",
			"8": "<sup>8</sup> ಆದರೆ ದೇವರು ಆ ಒಡಂಬಡಿಕೆಗೆ ಸೇರಿದವರ ಮೇಲೆ ತಪ್ಪುಹೊರಿಸಿ ಹೀಗೆಂದನು - ಇಗೋ, ನಾನು ಇಸ್ರಾಯೇಲ್‍ವಂಶದವರೊಂದಿಗೂ ಯೆಹೂದವಂಶದವರೊಂದಿಗೂ ಹೊಸದಾಗಿರುವ ಒಂದು ಒಡಂಬಡಿಕೆಯನ್ನು ಮಾಡಿಕೊಳ್ಳುವ ದಿನಗಳು ಬರುವವು ಎಂದು ಕರ್ತನು ಹೇಳುತ್ತಾನೆ;",
			"9": "<sup>9</sup> ಈ ಒಡಂಬಡಿಕೆಯು ನಾನು ಇವರ ಪಿತೃಗಳನ್ನು ಕೈಹಿಡಿದು ಐಗುಪ್ತದೇಶದೊಳಗಿಂದ ಕರಕೊಂಡು ಬಂದ ಕಾಲದಲ್ಲಿ ಅವರ ಸಂಗಡ ಮಾಡಿಕೊಂಡ ಒಡಂಬಡಿಕೆಯಂಥದಲ್ಲ; ಅವರು ನನ್ನ ಒಡಂಬಡಿಕೆಯಲ್ಲಿ ಸ್ಥಿರವಾಗಿ ನಿಲ್ಲಲಿಲ್ಲವಾದದರಿಂದ ನಾನು ಅವರ ಚಿಂತೆಯನ್ನು ಬಿಟ್ಟೆನೆಂದು ಕರ್ತನು ಹೇಳುತ್ತಾನೆ.",
			"10": "<sup>10</sup> ಆ ದಿನಗಳು ಬಂದ ಮೇಲೆ ನಾನು ಇಸ್ರಾಯೇಲ್‍ವಂಶದವರೊಂದಿಗೆ ಮಾಡಿಕೊಳ್ಳುವ ಒಡಂಬಡಿಕೆಯು ಹೀಗಿರುವದು - ನನ್ನ ಆಜ್ಞೆಗಳನ್ನು ಅವರ ಮನಸ್ಸಿನಲ್ಲಿ ಇಡುವೆನು, ಅವರ ಹೃದಯದ ಮೇಲೆ ಅವುಗಳನ್ನು ಬರೆಯುವೆನು; ನಾನು ಅವರಿಗೆ ದೇವರಾಗಿರುವೆನು, ಅವರು ನನಗೆ ಪ್ರಜೆಯಾಗಿರುವರು;",
			"11": "<sup>11</sup> ಒಂದೇ ಊರಿನವರೂ ಅಣ್ಣತಮ್ಮಂದಿರೂ ಒಬ್ಬರಿಗೊಬ್ಬರು - ಕರ್ತನ ಜ್ಞಾನವನ್ನು ಪಡೆಯಿರಿ ಎಂದು ಬೋಧಿಸಬೇಕಾಗಿರುವದಿಲ್ಲ; ಚಿಕ್ಕವರು ಮೊದಲುಗೊಂಡು ದೊಡ್ಡವರ ತನಕ ಎಲ್ಲರೂ ನನ್ನ ಜ್ಞಾನವನ್ನು ಪಡೆದಿರುವರು;",
			"12": "<sup>12</sup> ನಾನು ಅವರ ದುಷ್ಕೃತ್ಯಗಳ ವಿಷಯವಾಗಿ ಕ್ಷಮೆಯುಳ್ಳವನಾಗಿರುವೆನು, ಅವರ ಪಾಪಗಳನ್ನು ನನ್ನ ನೆನಪಿಗೆ ಎಂದಿಗೂ ತರುವದಿಲ್ಲ ಎಂದು ಕರ್ತನು ನುಡಿಯುತ್ತಾನೆ.",
			"13": "<sup>13</sup> ಇಲ್ಲಿ ಹೊಸ ಒಡಂಬಡಿಕೆಯೆಂದು ಹೇಳಿದ್ದರಲ್ಲಿ ಮೊದಲಿದ್ದದ್ದನ್ನು ಹಳೇದಾಗಿ ಮಾಡಿದ್ದಾನೆ. ಆದರೆ ಹಳೇದಾಗುತ್ತಾ ಮುದಿಯಾಗುವಂಥದಕ್ಕೆ ಅಂತ್ಯವು ಸಮೀಪವಾಗಿದೆ."
		},
		"9": {
			"1": "<sup>1</sup> ಹೀಗಿದ್ದರೂ ಮೊದಲನೆಯ ಒಡಂಬಡಿಕೆಯಲ್ಲಿ ದೇವಾರಾಧನೆಯ ನಿಯಮಗಳಿದ್ದವು; ಮತ್ತು ದೇವಾಲಯವಿತ್ತು, ಅದು ಇಹಲೋಕ ಸಂಬಂಧವಾದದ್ದು.",
			"2": "<sup>2</sup> ಅಂದರೆ ಒಂದು ಗುಡಾರವು ಕಟ್ಟಲ್ಪಟ್ಟಿತು; ಅದರ ಮೊದಲನೆಯ ಭಾಗದಲ್ಲಿ ದೀಪಸ್ತಂಭ, ಮೇಜು, ಸಮರ್ಪಿಸಿದ ರೊಟ್ಟಿ ಇವುಗಳಿದ್ದವು; ಅದಕ್ಕೆ ಪವಿತ್ರಸ್ಥಾನ ಎಂದು ಹೆಸರು.",
			"3": "<sup>3</sup> ಎರಡನೆಯ ತೆರೆಯ ಆಚೆಯಲ್ಲಿ ಅತಿಪವಿತ್ರ ಸ್ಥಾನವೆನಿಸಿಕೊಳ್ಳುವ ಇನ್ನೊಂದು ಭಾಗವಿತ್ತು;",
			"4": "<sup>4</sup> ಅದರಲ್ಲಿ ಚಿನ್ನದ ಧೂಪಾರತಿ, ಒಳಗೂ ಹೊರಗೂ ಚಿನ್ನದ ತಗಡನ್ನು ಮಡಾಯಿಸಿದ್ದ ಒಡಂಬಡಿಕೆಯ ಮಂಜೂಷ ಇವುಗಳಿದ್ದವು; ಆ ಮಂಜೂಷದೊಳಗೆ ಮನ್ನಾ ಇಟ್ಟಿದ್ದ ಚಿನ್ನದ ಪಾತ್ರೆಯೂ ಆರೋನನ ಚಿಗುರಿದ ಕೋಲೂ ಒಡಂಬಡಿಕೆ ಬರೆದಿದ್ದ ಕಲ್ಲಿನ ಹಲಿಗೆಗಳೂ ಇದ್ದವು;",
			"5": "<sup>5</sup> ಅದರ ಮೇಲೆ ದೇವರ ಪ್ರಭಾವವನ್ನು ಸೂಚಿಸುವ ಕೆರೂಬಿಗಳು ಕೃಪಾಸನವನ್ನು ಆಚ್ಫಾದಿಸಿಕೊಂಡಿದ್ದವು. ಸದ್ಯಕ್ಕೆ ಈ ವಿಷಯಗಳನ್ನು ಒಂದೊಂದಾಗಿ ವಿವರಿಸುವದಕ್ಕೆ ಆಗುವದಿಲ್ಲ.",
			"6": "<sup>6</sup> ಇವುಗಳು ಹೀಗೆ ಸಿದ್ಧಮಾಡಲ್ಪಟ್ಟ ಮೇಲೆ ಯಾಜಕರು ಯಾವಾಗಲೂ ಗುಡಾರದ ಮುಂದಣ ಭಾಗದೊಳಗೆ ಹೋಗಿ ದೇವಾರಾಧನೆಯನ್ನು ನಡಿಸುತ್ತಾರೆ.",
			"7": "<sup>7</sup> ಆದರೆ ಎರಡನೆಯ ಭಾಗದೊಳಗೆ ಮಹಾಯಾಜಕನೊಬ್ಬನೇ ವರುಷಕ್ಕೆ ಒಂದೇ ಸಾರಿ ಹೋಗುತ್ತಾನೆ, ಅವನು ರಕ್ತವನ್ನು ತಕ್ಕೊಳ್ಳದೆ ಹೋಗುವದಿಲ್ಲ; ಆ ರಕ್ತವನ್ನು ತನಗೋಸ್ಕರವೂ ಜನರ ತಪ್ಪುಗಳಿಗೋಸ್ಕರವೂ ಸಮರ್ಪಿಸುತ್ತಾನೆ.",
			"8": "<sup>8</sup> ಈ ಏರ್ಪಾಟಿನಿಂದ ಗುಡಾರದಲ್ಲಿ ಮುಂದಣ ಭಾಗವು ಇರುವ ತನಕ ದೇವರ ಸಮಕ್ಷಮಕ್ಕೆ ಹೋಗುವ ಮಾರ್ಗವು ಇದುವರೆಗೂ ಪ್ರಕಟವಾಗಲಿಲ್ಲವೆಂಬದನ್ನು ಪವಿತ್ರಾತ್ಮನು ವ್ಯಕ್ತಪಡಿಸುತ್ತಾನೆ.",
			"9": "<sup>9</sup> ಆ ಗುಡಾರವು ಈಗಿನ ಕಾಲದವರಿಗೋಸ್ಕರ ಒಂದು ಒಳಗಣ ಅರ್ಥವನ್ನು ಸೂಚಿಸುತ್ತದೆ; ಏನಂದರೆ ಅದರ ಕ್ರಮದ ಮೇರೆಗೆ ಸಮರ್ಪಣೆಯಾಗುವ ಕಾಣಿಕೆಗಳೂ ಯಜ್ಞಗಳೂ ದೇವಾರಾಧನೆ ಮಾಡುವವರನ್ನು ಮನಸ್ಸಾಕ್ಷಿಯು ಒಪ್ಪುವಂತೆ ಸಿದ್ಧಿಗೆ ತರಲಾರದೆ",
			"10": "<sup>10</sup> ಅನ್ನಪಾನಾದಿಗಳೂ ವಿವಿಧ ಸ್ನಾನಗಳೂ ಸಹಿತವಾಗಿ ದೇಹಕ್ಕೆ ಮಾತ್ರ ಸಂಬಂಧಪಟ್ಟ ನಿಯಮಗಳಾಗಿದ್ದು ತಿದ್ದುಪಾಟಿನ ಕಾಲದವರೆಗೆ ಮಾತ್ರ ನೇಮಕವಾದವು ಎಂಬದು.",
			"11": "<sup>11</sup> ಆದರೆ ಕ್ರಿಸ್ತನು ಈಗ ದೊರೆತಿರುವ ಮೇಲುಗಳನ್ನು ಕುರಿತು ಮಹಾಯಾಜಕನಾಗಿ ಬಂದು ಕೈಯಿಂದ ಕಟ್ಟಲ್ಪಡದಂಥ ಅಂದರೆ ಈ ಸೃಷ್ಟಿಗೆ ಸಂಬಂಧಪಡದಂಥ ಘನವಾಗಿಯೂ ಉತ್ಕೃಷ್ಟವಾಗಿಯೂ ಇರುವ ಗುಡಾರದಲ್ಲಿ ಸೇವೆಯನ್ನು ಮಾಡುವವನಾಗಿ",
			"12": "<sup>12</sup> ಹೋತಗಳ ಮತ್ತು ಹೋರಿಕರಗಳ ರಕ್ತವನ್ನು ತೆಗೆದುಕೊಳ್ಳದೆ ಸ್ವಂತ ರಕ್ತವನ್ನೇ ತೆಗೆದುಕೊಂಡು ನಮಗೋಸ್ಕರ ನಿತ್ಯವಿಮೋಚನೆಯನ್ನು ಸಂಪಾದಿಸಿಕೊಂಡವನಾಗಿ ಒಂದೇ ಸಾರಿ ದೇವರ ಸನ್ನಿಧಾನಕ್ಕೆ ಪ್ರವೇಶಿಸಿದನು.",
			"13": "<sup>13</sup> ಹೋತಹೋರಿಗಳ ರಕ್ತವೂ ಹೊಲೆಯಾದವರ ಮೇಲೆ ಚೆಲ್ಲುವ ಕಡಸಿನ ಬೂದಿಯೂ ಶರೀರದ ಹೊಲೆಯನ್ನು ಹೋಗಲಾಡಿಸಿ ಪವಿತ್ರಮಾಡುವದಾದರೆ",
			"14": "<sup>14</sup> ನಿತ್ಯಾತ್ಮನಿಂದ ತನ್ನನ್ನು ತಾನೇ ನಿರ್ದೋಷಿಯನ್ನಾಗಿ ದೇವರಿಗೆ ಸಮರ್ಪಿಸಿಕೊಂಡ ಕ್ರಿಸ್ತನ ರಕ್ತವು ಎಷ್ಟೋ ಹೆಚ್ಚಾಗಿ ನಮ್ಮನ್ನು ನಿರ್ಜೀವಕರ್ಮಗಳಿಂದ ಬಿಡಿಸಿ ನಾವು ಜೀವವುಳ್ಳ ದೇವರನ್ನು ಆರಾಧಿಸುವವರಾಗುವಂತೆ ನಮ್ಮ ಮನಸ್ಸನ್ನು ಶುದ್ಧೀಕರಿಸುವದಲ್ಲವೇ.",
			"15": "<sup>15</sup> ಮತ್ತು ಈ ಕಾರಣದಿಂದ ಆತನು ಒಂದು ಹೊಸ ಒಡಂಬಡಿಕೆಗೆ ಮಧ್ಯಸ್ಥನಾಗಿದ್ದಾನೆ. ಮೊದಲನೆಯ ಒಡಂಬಡಿಕೆಯ ಕಾಲದಲ್ಲಿ ನಡೆದ ಅಕ್ರಮಗಳ ಪರಿಹಾರಕ್ಕಾಗಿ ಆತನು ಮರಣವನ್ನು ಅನುಭವಿಸಿದ್ದರಿಂದ ದೇವರಿಂದ ಕರೆಯಿಸಿಕೊಂಡವರು ವಾಗ್ದಾನವಾಗಿದ್ದ ನಿತ್ಯಬಾಧ್ಯತೆಯನ್ನು ಹೊಂದುವದಕ್ಕೆ ಆತನ ಮೂಲಕ ಮಾರ್ಗವಾಯಿತು.",
			"16": "<sup>16</sup> ಮರಣಶಾಸನವು ಇರುವಲ್ಲಿ ಅದನ್ನು ಬರೆಯಿಸಿದವನ ಮರಣವು ಪ್ರಸಿದ್ಧವಾಗಿರಬೇಕಷ್ಟೆ.",
			"17": "<sup>17</sup> ಮರಣಶಾಸನವು ಮರಣವಾದ ಮೇಲೆ ನಡೆಯುವದೇ ಹೊರತು ಬರೆಯಿಸಿದವನು ಜೀವದಿಂದಿರುವಾಗ ಎಂದಿಗೂ ಸಲ್ಲುವದಿಲ್ಲ.",
			"18": "<sup>18</sup> ಹೀಗಿರಲಾಗಿ ಮೊದಲನೆಯ ಒಡಂಬಡಿಕೆಯಾದರೂ ರಕ್ತವಿಲ್ಲದೆ ಸ್ಥಾಪಿತವಾಗಲಿಲ್ಲ.",
			"19": "<sup>19</sup> ಮೋಶೆಯು ದೇವರ ವಿಧಿಗಳನ್ನೆಲ್ಲಾ ಧರ್ಮಶಾಸ್ತ್ರದ ಪ್ರಕಾರ ಜನರೆಲ್ಲರಿಗೆ ಹೇಳಿದ ಮೇಲೆ ನೀರು, ಕೆಂಪು ಉಣ್ಣೆ, ಹಿಸ್ಸೋಪು ಕಡ್ಡಿ ಇವುಗಳೊಂದಿಗೆ ಹೋರಿಕರಗಳ ಮತ್ತು ಹೋತಗಳ ರಕ್ತವನ್ನು ತೆಗೆದುಕೊಂಡು -",
			"20": "<sup>20</sup> ಇದು ದೇವರು ನಿಮಗೋಸ್ಕರ ವಿಧಿಸಿದ ಒಡಂಬಡಿಕೆಯನ್ನು ಸ್ಥಿರಪಡಿಸುವ ರಕ್ತವಾಗಿದೆ ಎಂದು ಹೇಳಿ ಅದನ್ನು ಗ್ರಂಥದ ಮೇಲೆಯೂ ಎಲ್ಲಾ ಜನರ ಮೇಲೆಯೂ ಪ್ರೋಕ್ಷಿಸಿದನು.",
			"21": "<sup>21</sup> ಇದಲ್ಲದೆ ಗುಡಾರದ ಮೇಲೆಯೂ ದೇವರ ಸೇವೆಗೆ ಬೇಕಾಗಿರುವ ಎಲ್ಲಾ ಉಪಕರಣಗಳ ಮೇಲೆಯೂ ಅದೇ ರೀತಿಯಾಗಿ ರಕ್ತವನ್ನು ಪ್ರೋಕ್ಷಿಸಿದನು.",
			"22": "<sup>22</sup> ಧರ್ಮಶಾಸ್ತ್ರದ ಪ್ರಕಾರ ಸ್ವಲ್ಪಕಡಿಮೆ ಎಲ್ಲಾ ವಸ್ತುಗಳು ರಕ್ತದಿಂದಲೇ ಶುದ್ಧೀಕರಿಸಲ್ಪಡುವವು; ರಕ್ತಧಾರೆಯಿಲ್ಲದೆ ಪಾಪಪರಿಹಾರ ಉಂಟಾಗುವದಿಲ್ಲ.",
			"23": "<sup>23</sup> ಪರಲೋಕವಸ್ತುಗಳಿಗೆ ಪ್ರತಿರೂಪವಾಗಿರುವ ವಸ್ತುಗಳ ಶುದ್ಧೀಕರಣಕ್ಕಾಗಿ ಇಂಥಾ ಯಜ್ಞಗಳು ಅವಶ್ಯವಾದರೂ ಸಾಕ್ಷಾತ್ ಪರಲೋಕದವುಗಳಿಗೆ ಇವುಗಳಿಗಿಂತ ಉತ್ತಮವಾದ ಯಜ್ಞಗಳು ಬೇಕು.",
			"24": "<sup>24</sup> ಯಾಕಂದರೆ ಕ್ರಿಸ್ತನು ನಿಜವಾದ ದೇವಾಲಯಕ್ಕೆ ಅನುರೂಪ ಮಾತ್ರವಾದದ್ದಾಗಿಯೂ ಕೈಯಿಂದ ಕಟ್ಟಿದ್ದಾಗಿಯೂ ಇರುವ ಆಲಯದಲ್ಲಿ ಪ್ರವೇಶಿಸದೆ ದೇವರ ಸಮ್ಮುಖದಲ್ಲಿ ನಮಗೋಸ್ಕರ ಈಗ ಕಾಣಿಸಿಕೊಳ್ಳುವದಕ್ಕೆ ಪರಲೋಕದಲ್ಲಿಯೇ ಪ್ರವೇಶಿಸಿದನು.",
			"25": "<sup>25</sup> ಇದಲ್ಲದೆ ಮಹಾಯಾಜಕನು ವರುಷ ವರುಷವೂ ಅನ್ಯ ರಕ್ತವನ್ನು ತೆಗೆದುಕೊಂಡು ಪವಿತ್ರಾಲಯದಲ್ಲಿ ಪ್ರವೇಶಿಸುವ ಪ್ರಕಾರ ಆತನು ತನ್ನನ್ನು ಅನೇಕ ಸಾರಿ ಸಮರ್ಪಿಸುವದಕ್ಕೆ ಪರಲೋಕದಲ್ಲಿ ಪ್ರವೇಶಿಸಲಿಲ್ಲ;",
			"26": "<sup>26</sup> ಹಾಗೆ ಸಮರ್ಪಿಸಬೇಕಾಗಿದ್ದ ಪಕ್ಷದಲ್ಲಿ ಆತನು ಲೋಕಾದಿಯಿಂದ ಎಷ್ಟೋ ಸಾರಿ ಬಾಧೆಪಟ್ಟು ಸಾಯಬೇಕಾಗಿತ್ತು. ಆದರೆ ಒಂದೇ ಸಾರಿ ಯುಗಗಳ ಸಮಾಪ್ತಿಯಲ್ಲೇ ಆತನು ಪಾಪನಿವಾರಣೆ ಮಾಡಬೇಕೆಂಬ ಉದ್ದೇಶದಿಂದ ತನ್ನನ್ನು ಯಜ್ಞಮಾಡಿಕೊಳ್ಳುವವನಾಗಿ ಪ್ರತ್ಯಕ್ಷನಾದನು.",
			"27": "<sup>27</sup> ಒಂದೇ ಸಾರಿ ಸಾಯುವದೂ ಆಮೇಲೆ ನ್ಯಾಯತೀರ್ಪೂ ಮನುಷ್ಯರಿಗೆ ಹೇಗೆ ನೇಮಕವಾಗಿದೆಯೋ ಹಾಗೆಯೇ ಕ್ರಿಸ್ತನು ಸಹ ಬಹುಜನರ ಪಾಪಗಳನ್ನು ಹೊತ್ತುಕೊಳ್ಳುವದಕ್ಕೋಸ್ಕರ ಒಂದೇ ಸಾರಿ ಸಮರ್ಪಿತನಾದನು,",
			"28": "<sup>28</sup> ಮತ್ತು ತನ್ನನ್ನು ನಿರೀಕ್ಷಿಸಿಕೊಂಡಿರುವವರಿಗೆ ರಕ್ಷಣೆಯನ್ನುಂಟು ಮಾಡುವದಕ್ಕೋಸ್ಕರ ಪಾಪಸಂಬಂಧವಿಲ್ಲದವನಾಗಿ ಎರಡನೆಯ ಸಾರಿ ಕಾಣಿಸಿಕೊಳ್ಳುವನು."
		},
		"10": {
			"1": "<sup>1</sup> ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿ ಮುಂದೆ ಬರಬೇಕಾಗಿದ್ದ ಮೇಲುಗಳ ಛಾಯೆಯೇ ಹೊರತು ಅವುಗಳ ನಿಜ ಸ್ವರೂಪವಲ್ಲವಾದದರಿಂದ ಆ ಧರ್ಮಶಾಸ್ತ್ರವು ವರುಷ ವರುಷಕ್ಕೆ ಪದೇ ಪದೇ ಅರ್ಪಿತವಾಗುವ ಒಂದೇ ವಿಧವಾದ ಯಜ್ಞಗಳನ್ನು ಅರ್ಪಿಸಿ ದೇವರ ಸಮೀಪಕ್ಕೆ ಬರುವವರನ್ನು ಎಂದಿಗೂ ಸಿದ್ಧಿಗೆ ತರಲಾರದು.",
			"2": "<sup>2</sup> ತಂದಿದ್ದ ಪಕ್ಷದಲ್ಲಿ ಆ ಯಜ್ಞ ಸಮರ್ಪಣೆಯು ನಿಂತುಹೋಗುತ್ತಿತ್ತಲ್ಲಾ. ಯಾಕಂದರೆ ದೇವಾರಾಧನೆ ಮಾಡುವವರು ಒಂದೇ ಸಾರಿ ಶುದ್ಧೀಕರಿಸಲ್ಪಟ್ಟ ಮೇಲೆ ಅವರಿಗೆ ಪಾಪವುಂಟೆಂದು ಅವರ ಮನಸ್ಸು ಇನ್ನೂ ಸಾಕ್ಷಿಕೊಡುವದು ಹೇಗೆ?",
			"3": "<sup>3</sup> ಆದರೆ ಆ ಯಜ್ಞಗಳಿಂದ ಪ್ರತಿವರುಷದಲ್ಲಿಯೂ ಪಾಪಗಳ ಜ್ಞಾಪಕವಾಗುವದುಂಟು;",
			"4": "<sup>4</sup> ಹೋರಿಗಳ ಮತ್ತು ಹೋತಗಳ ರಕ್ತದಿಂದ ಪಾಪಗಳು ಪರಿಹಾರವಾಗುವದು ಅಸಾಧ್ಯವಾಗಿದೆಯಲ್ಲಾ.",
			"5": "<sup>5</sup> ಆದದರಿಂದ ಕ್ರಿಸ್ತನು ಭೂಲೋಕದೊಳಗೆ ಬರುವಾಗ - [ದೇವರೇ], ಯಜ್ಞನೈವೇದ್ಯಗಳು ನಿನಗೆ ಇಷ್ಟವಾಗಿರಲಿಲ್ಲ, ನನಗೆ ದೇಹವನ್ನು ಸಿದ್ಧಮಾಡಿಕೊಟ್ಟಿ;",
			"6": "<sup>6</sup> ಸರ್ವಾಂಗಹೋಮಗಳಲ್ಲಿಯೂ ದೋಷಪರಿಹಾರಕಯಜ್ಞಗಳಲ್ಲಿಯೂ ನೀನು ಸಂತೋಷಪಡಲಿಲ್ಲ;",
			"7": "<sup>7</sup> ಆಗ ನಾನು - ಇಗೋ, ದೇವರೇ, ನಿನ್ನ ಚಿತ್ತವನ್ನು ನೆರವೇರಿಸುವದಕ್ಕೆ ಬಂದಿದ್ದೇನೆ. ಗ್ರಂಥದ ಸುರುಳಿಯಲ್ಲಿ ನನ್ನನ್ನು ಕುರಿತು ಬರೆದದೆ ಎಂದು ಹೇಳಿದೆನು ಅನ್ನುತ್ತಾನೆ.",
			"8": "<sup>8</sup> ಧರ್ಮಶಾಸ್ತ್ರದ ಪ್ರಕಾರ ಸಮರ್ಪಣೆಯಾಗುತ್ತಾ ಬರುವ ಯಜ್ಞನೈವೇದ್ಯಗಳೂ ಸರ್ವಾಂಗಹೋಮಗಳೂ ದೋಷಪರಿಹಾರಕಯಜ್ಞಗಳೂ ನಿನಗೆ ಇಷ್ಟವಾಗಿರಲಿಲ್ಲ. ನೀನು ಅವುಗಳಲ್ಲಿ ಸಂತೋಷಪಡುವದಿಲ್ಲ ಎಂದು ಮೇಲೆ ಹೇಳಿದ ತರುವಾಯ -",
			"9": "<sup>9</sup> ಇಗೋ, ನಿನ್ನ ಚಿತ್ತವನ್ನು ನೆರವೇರಿಸುವದಕ್ಕೆ ಬಂದಿದ್ದೇನೆ ಎಂದು ಹೇಳಿದ್ದಾನೆ. ಆತನು ಎರಡನೆಯದನ್ನು ಸ್ಥಾಪಿಸುವದಕ್ಕಾಗಿ ಮೊದಲನೆಯದನ್ನು ತೆಗೆದುಬಿಡುತ್ತಾನೆ.",
			"10": "<sup>10</sup> ಯೇಸು ಕ್ರಿಸ್ತನು ಒಂದೇ ಸಾರಿ ದೇಹ ಸಮರ್ಪಣೆಮಾಡಿ ದೇವರ ಚಿತ್ತವನ್ನು ನೆರವೇರಿಸಿದ್ದರಿಂದಲೇ ನಾವು ಶುದ್ಧರಾದೆವು.",
			"11": "<sup>11</sup> ಇದಲ್ಲದೆ ಆ ಯಾಜಕರೆಲ್ಲರು ದಿನಾಲು ಸೇವೆಮಾಡುತ್ತಾ ಎಂದಿಗೂ ಪಾಪನಿವಾರಣೆ ಮಾಡಲಾರದಂಥ ಒಂದೇ ವಿಧವಾದ ಯಜ್ಞಗಳನ್ನು ಪದೇಪದೇ ಸಮರ್ಪಿಸುತ್ತಾ ನಿಂತುಕೊಂಡಿರುವರು.",
			"12": "<sup>12</sup> ಆದರೆ ಈ ಯಾಜಕನು ಪಾಪನಿವಾರಣೆಗೋಸ್ಕರ ನಿರಂತರವಾಗಿ ನಿಲ್ಲುವ ಒಂದೇ ಯಜ್ಞವನ್ನು ಸಮರ್ಪಿಸಿ ದೇವರ ಬಲಗಡೆಯಲ್ಲಿ ಕೂತುಕೊಂಡನು.",
			"13": "<sup>13</sup> ಅಂದಿನಿಂದ ತನ್ನ ವಿರೋಧಿಗಳು ತನ್ನ ಪಾದಪೀಠವಾಗಿ ಹಾಕಲ್ಪಡುವ ತನಕ ಆತನು ಕಾದಿರುವನು.",
			"14": "<sup>14</sup> ಪವಿತ್ರರಾಗುತ್ತಿರುವವರನ್ನು ಒಂದೇ ಸಮರ್ಪಣೆಯಿಂದ ನಿರಂತರವಾಗಿ ಸಿದ್ಧಿಗೆ ತಂದಿದ್ದಾನಷ್ಟೆ.",
			"15": "<sup>15</sup> ಪವಿತ್ರಾತ್ಮನು ಸಹ ನಮಗೆ ಸಾಕ್ಷಿಕೊಡುವವನಾಗಿ -",
			"16": "<sup>16</sup> ಆ ದಿನಗಳು ಬಂದ ಮೇಲೆ ನಾನು ಅವರ ಸಂಗಡ ಮಾಡಿಕೊಳ್ಳುವ ಒಡಂಬಡಿಕೆಯು ಹೀಗಿರುವದು - ನನ್ನ ಆಜ್ಞೆಗಳನ್ನು ಅವರ ಹೃದಯದಲ್ಲಿ ಇಡುವೆನು, ಅವರ ಮನಸ್ಸಿನ ಮೇಲೆ ಅವುಗಳನ್ನು ಬರೆಯುವೆನು ಎಂದು ಕರ್ತನು ನುಡಿಯುತ್ತಾನೆ ಎಂಬದಾಗಿ ಹೇಳಿದ ಮೇಲೆ ಇನ್ನು -",
			"17": "<sup>17</sup> ಅವರ ಪಾಪಗಳನ್ನೂ ಅವರ ದುಷ್ಕೃತ್ಯಗಳನ್ನೂ ನನ್ನ ನೆನಪಿಗೆ ಎಂದಿಗೂ ತರುವದಿಲ್ಲ ಎಂದು ಹೇಳುತ್ತಾನೆ.",
			"18": "<sup>18</sup> ಪಾಪಗಳು ಪರಿಹಾರವಾದಲ್ಲಿ ಇನ್ನೂ ಅವುಗಳ ವಿಷಯದಲ್ಲಿ ಸಮರ್ಪಣೆಮಾಡುವದು ಅವಶ್ಯವಿಲ್ಲ.",
			"19": "<sup>19</sup> ಹೀಗಿರುವಲ್ಲಿ ಸಹೋದರರೇ, ಯೇಸು ನಮಗೋಸ್ಕರ ಪ್ರತಿಷ್ಠಿಸಿದ ಜೀವವುಳ್ಳ ಹೊಸ ದಾರಿಯಲ್ಲಿ ಆತನ ರಕ್ತದ ಮೂಲಕ ಆತನ ಶರೀರವೆಂಬ ತೆರೆಯ ಮುಖಾಂತರ ದೇವರ ಸಮಕ್ಷಮದಲ್ಲಿ ಪ್ರವೇಶಿಸುವದಕ್ಕೆ ನಮಗೆ ಧೈರ್ಯವುಂಟಾಯಿತು.",
			"21": "<sup>21</sup> ದೇವರ ಮನೆಯ ಮೇಲೆ ಅಧಿಕಾರಿಯಾಗಿರುವ ಶ್ರೇಷ್ಠನಾದ ಯಾಜಕನು ನಮಗಿದ್ದಾನೆ.",
			"22": "<sup>22</sup> ಆದಕಾರಣ ನೀನಪರಾಧಿಯೆಂದು ನಮ್ಮ ಮನಸ್ಸು ನಮಗೆ ಸಾಕ್ಷಿಹೇಳದಂತೆ ನಾವು ಹೃದಯವನ್ನು ಪ್ರೋಕ್ಷಿಸಿಕೊಂಡು ದೇಹವನ್ನು ತಿಳಿನೀರಿನಿಂದ ತೊಳೆದುಕೊಂಡು ಪರಿಪೂರ್ಣವಾದ ನಂಬಿಕೆಯುಳ್ಳವರಾಗಿಯೂ ಯಥಾರ್ಥ ಹೃದಯವುಳ್ಳವರಾಗಿಯೂ ದೇವರ ಬಳಿಗೆ ಬರೋಣ.",
			"23": "<sup>23</sup> ನಮ್ಮ ನಿರೀಕ್ಷೆಯನ್ನು ಕುರಿತು ನಾವು ಮಾಡಿದ ಪ್ರತಿಜ್ಞೆಯನ್ನು ನಿಶ್ಚಂಚಲವಾಗಿ ಪರಿಗ್ರಹಿಸೋಣ; ವಾಗ್ದಾನ ಮಾಡಿದಾತನು ನಂಬಿಗಸ್ತನು.",
			"24": "<sup>24</sup> ನಾವು ಪರಸ್ಪರ ಹಿತಚಿಂತಕರಾಗಿದ್ದು ಪ್ರೀತಿಸುತ್ತಿರಬೇಕೆಂತಲೂ ಸತ್ಕಾರ್ಯಮಾಡಬೇಕೆಂತಲೂ ಒಬ್ಬರನ್ನೊಬ್ಬರು ಪ್ರೇರೇಪಿಸೋಣ.",
			"25": "<sup>25</sup> ಸಭೆಯಾಗಿ ಕೂಡಿಕೊಳ್ಳುವದನ್ನು ಕೆಲವರು ರೂಢಿಯಾಗಿ ಬಿಟ್ಟಿರುವ ಪ್ರಕಾರ ನಾವು ಬಿಟ್ಟುಬಿಡದೆ ಒಬ್ಬರನ್ನೊಬ್ಬರು ಎಚ್ಚರಿಸೋಣ. ಕರ್ತನ ಪ್ರತ್ಯಕ್ಷತೆಯ ದಿನವು ಸಮೀಪಿಸುತ್ತಾ ಬರುತ್ತದೆ ಎಂದು ನೀವು ನೋಡುವದರಿಂದ ಇದನ್ನು ಮತ್ತಷ್ಟು ಮಾಡಿರಿ.",
			"26": "<sup>26</sup> ಯಾಕಂದರೆ ಸತ್ಯದ ಪರಿಜ್ಞಾನವನ್ನು ನಾವು ಹೊಂದಿದ ಮೇಲೆ ಬೇಕೆಂದು ಪಾಪಮಾಡಿದರೆ ಪಾಪಪರಿಹಾರಕ್ಕಾಗಿ ಇನ್ನಾವ ಯಜ್ಞವೂ ಇರುವದಿಲ್ಲ;",
			"27": "<sup>27</sup> ಅತ್ಯಂತ ಭಯದಿಂದ ಎದುರುನೋಡತಕ್ಕ ನ್ಯಾಯತೀರ್ಪೂ ದೇವರ ವಿರೋಧಿಗಳನ್ನು ದಹಿಸುವ ತೀಕ್ಷ್ಣವಾದ ಅಗ್ನಿಯೂ ಇವೇ ನಮ್ಮ ಮುಂದೆ ಇರುವವು.",
			"28": "<sup>28</sup> ಮೋಶೆಯ ವಿಧಿಗಳನ್ನು ಅಸಡ್ಡೆ ಮಾಡಿದವನಿಗೆ ಇಬ್ಬರು ಇಲ್ಲವೆ ಮೂವರು ಸಾಕ್ಷಿಗಳ ಮಾತಿನ ಮೇಲೆ ಕನಿಕರವಿಲ್ಲದೆ ಮರಣದಂಡನೆ ಆಗುತ್ತದಷ್ಟೆ.",
			"29": "<sup>29</sup> ಯಾವನು ದೇವಕುಮಾರನನ್ನು ತುಳಿದು ಒಡಂಬಡಿಕೆಯನ್ನು ಸ್ಥಿರಪಡಿಸಿದಂಥ ತನ್ನನ್ನು ಪವಿತ್ರಮಾಡಿದಂಥ ರಕ್ತವನ್ನು ಅಶುದ್ಧವೆಂದೆಣಿಸಿ ದೇವರ ಕೃಪಾವರವಾಗಿರುವ ಆತ್ಮನನ್ನು ತಿರಸ್ಕಾರ ಮಾಡಿದ್ದಾನೋ ಅವನು ಇನ್ನೂ ಎಷ್ಟೋ ಕ್ರೂರವಾದ ದಂಡನೆಗೆ ಪಾತ್ರನಾಗಬೇಕೆಂಬದನ್ನು ಯೋಚಿಸಿರಿ.",
			"30": "<sup>30</sup> ಮುಯ್ಯಿಗೆ ಮುಯ್ಯಿ ತೀರಿಸುವದು ನನ್ನ ಕೆಲಸ, ನಾನೇ ಪ್ರತಿಫಲವನ್ನು ಕೊಡುವೆನೆಂದು ಹೇಳಿದಾತನನ್ನು ಬಲ್ಲೆವು; ಇದಲ್ಲದೆ ಕರ್ತನು ತನ್ನ ಜನರ ನ್ಯಾಯವನ್ನು ವಿಚಾರಿಸುವನೆಂತಲೂ ಹೇಳಿಯದೆ.",
			"31": "<sup>31</sup> ಜೀವಸ್ವರೂಪನಾದ ದೇವರ ಕೈಯಲ್ಲಿ ಸಿಕ್ಕಿಬೀಳುವದು ಭಯಂಕರವಾದದ್ದು.",
			"32": "<sup>32</sup> ಹೀಗಿರಲಾಗಿ ನೀವು ಜ್ಞಾನಪ್ರಕಾಶದಲ್ಲಿ ಸೇರಿ ಕಷ್ಟಾನುಭವವೆಂಬ ಬಲು ಹೋರಾಟವನ್ನು ಸಹಿಸಿಕೊಂಡ ಹಿಂದಿನ ದಿನಗಳನ್ನು ನೆನಪಿಗೆ ತಂದುಕೊಳ್ಳಿರಿ;",
			"33": "<sup>33</sup> ಕೆಲವು ಸಾರಿ ನೀವು ನಿಂದೆಗಳನ್ನೂ ಸಂಕಟಗಳನ್ನೂ ಅನುಭವಿಸಿ ಹಾಸ್ಯಕ್ಕೆ ಗುರಿಯಾದಿರಿ; ಕೆಲವು ಸಾರಿ ಅಂಥವುಗಳನ್ನು ಅನುಭವಿಸಿದವರಲ್ಲಿ ಸೇರಿದವರಾಗಿದ್ದು ಕಷ್ಟಾನುಭವಿಗಳಾದಿರಿ.",
			"34": "<sup>34</sup> ಬೇಡಿಗಳನ್ನು ಹಾಕಿಸಿಕೊಂಡವರ ಕಷ್ಟವನ್ನು ನೋಡಿ ಅವರ ಸಂಗಡ ನೀವು ದುಃಖ ಪಟ್ಟದ್ದಲ್ಲದೆ ನಮಗೆ ಉತ್ತಮವಾಗಿಯೂ ಸ್ಥಿರವಾಗಿಯೂ ಇರುವ ಆಸ್ತಿಯುಂಟೆಂದು ಚೆನ್ನಾಗಿ ಅರಿತುಕೊಂಡು ನಿಮ್ಮ ಸೊತ್ತನ್ನು ಸುಲುಕೊಳ್ಳುವವರಿಗೆ ಸಂತೋಷದಿಂದ ಬಿಟ್ಟಿರಿ.",
			"35": "<sup>35</sup> ಹಾಗಾದರೆ ನಿಮ್ಮ ಧೈರ್ಯವನ್ನು ಬಿಟ್ಟುಬಿಡಬೇಡಿರಿ; ಅದಕ್ಕೆ ಮಹಾ ಪ್ರತಿಫಲ ಉಂಟು.",
			"36": "<sup>36</sup> ದೇವರ ಚಿತ್ತವನ್ನು ನೆರವೇರಿಸಿ ವಾಗ್ದಾನದ ಫಲವನ್ನು ಹೊಂದಬೇಕಾದರೆ ನಿಮಗೆ ತಾಳ್ಮೆಬೇಕು.",
			"37": "<sup>37</sup> ಬರುವಾತನು ಇನ್ನು ಸ್ಪಲ್ಪಕಾಲದಲ್ಲಿ ಬರುವನು, ತಡಮಾಡುವದಿಲ್ಲ;",
			"38": "<sup>38</sup> ಆದರೆ ನನ್ನವನಾಗಿರುವ ನೀತಿವಂತನು ನಂಬಿಕೆಯಿಂದಲೇ ಬದುಕುವನು; ಅವನು ಹಿಂದೆಗೆದರೆ ಅವನಲ್ಲಿ ನನಗೆ ಸಂತೋಷವಿರುವದಿಲ್ಲ ಎಂದು ಬರೆದದೆ.",
			"39": "<sup>39</sup> ನಾವಾದರೋ ಹಿಂದೆಗೆದವರಾಗಿ ನಾಶವಾಗುವವರಲ್ಲ, ನಂಬುವವರಾಗಿ ಪ್ರಾಣರಕ್ಷಣೆಯನ್ನು ಹೊಂದುವವರಾಗಿದ್ದೇವೆ."
		},
		"11": {
			"1": "<sup>1</sup> ನಂಬಿಕೆಯೋ ನಾವು ನಿರೀಕ್ಷಿಸುವವುಗಳ ವಿಷಯವಾಗಿ ಭರವಸದಿಂದಿರುವದೂ ಕಣ್ಣಿಗೆ ಕಾಣದವುಗಳನ್ನು ನಿಜವೆಂದು ತಿಳುಕೊಳ್ಳುವದೂ ಆಗಿದೆ.",
			"2": "<sup>2</sup> ನಮ್ಮ ಪೂರ್ವಿಕರು ನಂಬಿಕೆಯುಳ್ಳವರಾಗಿದ್ದದರಿಂದಲೇ ಒಳ್ಳೇ ಹೆಸರನ್ನು ಹೊಂದಿದರು.",
			"3": "<sup>3</sup> ಲೋಕಗಳು ದೇವರ ಮಾತಿನಿಂದ ನಿರ್ಮಿತವಾದವೆಂದು ನಂಬಿಕೆಯಿಂದಲೇ ನಾವು ತಿಳಿದುಕೊಂಡು ಕಾಣಿಸುವ ಈ ಜಗತ್ತು ದೃಶ್ಯ ವಸ್ತುಗಳಿಂದ ಉಂಟಾಗಲಿಲ್ಲವೆಂದು ಗ್ರಹಿಸುತ್ತೇವೆ.",
			"4": "<sup>4</sup> ನಂಬಿಕೆಯಿಂದಲೇ ಹೇಬೆಲನು ಕಾಯಿನನ ಯಜ್ಞಕ್ಕಿಂತ ಶ್ರೇಷ್ಠವಾದ ಯಜ್ಞವನ್ನು ದೇವರಿಗೆ ಸಮರ್ಪಿಸಿದನು. ಅದರ ಮೂಲಕ ತಾನು ನೀತಿವಂತನೆಂದು ಸಾಕ್ಷಿಹೊಂದಿದನು; ದೇವರು ಅವನ ಕಾಣಿಕೆಗಳನ್ನು ಅಂಗೀಕಾರಮಾಡಿದ್ದೇ ಆ ಸಾಕ್ಷಿ. ಅವನು ಸತ್ತಿದ್ದರೂ ಅವನ ನಂಬಿಕೆಯ ಮೂಲಕ ಇನ್ನೂ ಮಾತಾಡುತ್ತಾನೆ.",
			"5": "<sup>5</sup> ಹನೋಕನು ಮರಣವನ್ನು ಅನುಭವಿಸದೆ ಒಯ್ಯಲ್ಪಟ್ಟದ್ದು ನಂಬಿಕೆಯಿಂದಲೇ; ಅವನನ್ನು ದೇವರು ತೆಗೆದುಕೊಂಡು ಹೋದದರಿಂದ ಅವನು ಯಾರಿಗೂ ಸಿಕ್ಕಲಿಲ್ಲ; ಅವನು ಒಯ್ಯಲ್ಪಡುವದಕ್ಕಿಂತ ಮೊದಲು ದೇವರಿಗೆ ಮೆಚ್ಚಿಕೆಯಾದವನಾಗಿದ್ದನೆಂದು ಸಾಕ್ಷಿ ಉಂಟು.",
			"6": "<sup>6</sup> ಆದರೆ ನಂಬಿಕೆಯಿಲ್ಲದೆ ದೇವರನ್ನು ಮೆಚ್ಚಿಸುವದು ಅಸಾಧ್ಯ; ದೇವರ ಬಳಿಗೆ ಬರುವವನು ದೇವರು ಇದ್ದಾನೆ, ಮತ್ತು ತನ್ನನ್ನು ಹುಡುಕುವವರಿಗೆ ಪ್ರತಿಫಲವನ್ನು ಕೊಡುತ್ತಾನೆ ಎಂದು ನಂಬುವದು ಅವಶ್ಯ.",
			"7": "<sup>7</sup> ನಂಬಿಕೆಯಿಂದಲೇ ನೋಹನು ಅದುವರೆಗೆ ಕಾಣದಿದ್ದ ಸಂಗತಿಗಳ ವಿಷಯವಾಗಿ ದೈವೋಕ್ತಿಯನ್ನು ಹೊಂದಿ ಭಯಭಕ್ತಿಯುಳ್ಳವನಾಗಿ ತನ್ನ ಮನೆಯವರ ಸಂರಕ್ಷಣೆಗೋಸ್ಕರ ನಾವೆಯನ್ನು ಕಟ್ಟಿ ಸಿದ್ಧಮಾಡಿದನು. ಅದರಿಂದ ಅವನು ಲೋಕದವರನ್ನು ದಂಡನೆಗೆ ಪಾತ್ರರೆಂದು ನಿರ್ಣಯಿಸಿಕೊಂಡು ನಂಬಿಕೆಯ ಫಲವಾದ ನೀತಿಗೆ ಬಾಧ್ಯನಾದನು.",
			"8": "<sup>8</sup> ನಂಬಿಕೆಯಿಂದಲೇ ಅಬ್ರಹಾಮನು ಕರೆಯಲ್ಪಟ್ಟ ಕೂಡಲೆ ಕರೆದಾತನ ಮಾತನ್ನು ಕೇಳಿ ತಾನು ಬಾಧ್ಯವಾಗಿ ಹೊಂದಬೇಕಾಗಿದ್ದ ಸ್ಥಳಕ್ಕೆ ಹೊರಟು ಹೋದನು; ತಾನು ಹೋಗಬೇಕಾದ ಸ್ಥಳವು ಯಾವದೆಂದು ತಿಳಿಯದೆ ಹೊರಟನು.",
			"9": "<sup>9</sup> ನಂಬಿಕೆಯಿಂದಲೇ ಅವನು ವಾಗ್ದಾತ್ತದೇಶಕ್ಕೆ ಬಂದಾಗ ಅಲ್ಲಿ ಅನ್ಯದೇಶದಲ್ಲಿ ಇದ್ದವನಂತೆ ಡೇರೆಗಳಲ್ಲಿ ಇದ್ದುಕೊಂಡು ಪ್ರವಾಸಿಯಾಗಿ ಬದುಕಿದನು. ಅದೇ ವಾಗ್ದಾನಕ್ಕೆ ಸಹಬಾಧ್ಯರಾಗಿದ್ದ ಇಸಾಕನೂ ಯಾಕೋಬನೂ ಅವನಂತೆಯೇ ಡೇರೆಗಳಲ್ಲಿ ವಾಸಿಸಿದರು.",
			"10": "<sup>10</sup> ಯಾಕಂದರೆ ಅವನು ಶಾಶ್ವತವಾದ ಅಸ್ತಿವಾರಗಳುಳ್ಳ ಪಟ್ಟಣವನ್ನು ಅಂದರೆ ದೇವರು ಸಂಕಲ್ಪಿಸಿ ನಿರ್ಮಿಸಿದ ಪಟ್ಟಣವನ್ನು ಎದುರುನೋಡುತ್ತಿದ್ದನು.",
			"11": "<sup>11</sup> ಇದಲ್ಲದೆ ಸಾರಳು ವಾಗ್ದಾನಮಾಡಿದಾತನನ್ನು ನಂಬತಕ್ಕವನೆಂದೆಣಿಸಿ ತಾನು ಪ್ರಾಯ ಮೀರಿದವಳಾಗಿದ್ದರೂ ನಂಬಿಕೆಯಿಂದಲೇ ಗರ್ಭವತಿಯಾಗುವದಕ್ಕೆ ಶಕ್ತಿಯನ್ನು ಹೊಂದಿದಳು.",
			"12": "<sup>12</sup> ಆದದರಿಂದ ಮೃತಪ್ರಾಯನಾಗಿದ್ದ ಒಬ್ಬನಿಂದ ಆಕಾಶದ ನಕ್ಷತ್ರಗಳಂತೆಯೂ ಸಮುದ್ರತೀರದಲ್ಲಿರುವ ಉಸುಬಿನಂತೆಯೂ ಅಸಂಖ್ಯವಾಗಿ ಮಕ್ಕಳು ಹುಟ್ಟಿದರು.",
			"13": "<sup>13</sup> ಇವರೆಲ್ಲರು ವಾಗ್ದಾನದ ಫಲಗಳನ್ನು ಹೊಂದದೆ ಅವುಗಳನ್ನು ದೂರದಿಂದ ನೋಡಿ ಉಲ್ಲಾಸದೊಡನೆ ವಂದಿಸಿ ನಂಬಿಕೆಯುಳ್ಳವರಾಗಿ ಮೃತರಾದರು. ತಾವು ಭೂಮಿಯ ಮೇಲೆ ಪರದೇಶದವರೂ ಪ್ರವಾಸಿಗಳೂ ಆಗಿದ್ದೇವೆಂದು ಒಪ್ಪಿಕೊಂಡರು.",
			"14": "<sup>14</sup> ಇಂಥ ಮಾತುಗಳನ್ನಾಡುವವರು ತಾವು ಸ್ವದೇಶವನ್ನು ಹುಡುಕುವವರೆಂಬರ್ಥವನ್ನು ವ್ಯಕ್ತಪಡಿಸುತ್ತಾರೆ.",
			"15": "<sup>15</sup> ತಾವು ಹೊರಟುಬಂದಿದ್ದ ದೇಶದ ಮೇಲೆ ಮನಸ್ಸಿಟ್ಟವರಾಗಿದ್ದರೆ ತಿರಿಗಿ ಅಲ್ಲಿಗೆ ಹೋಗುವದಕ್ಕೆ ಅವರಿಗೆ ಯಾವಾಗಲೂ ಅವಕಾಶವಿತ್ತು.",
			"16": "<sup>16</sup> ಆದರೆ ಅವರು ಪರಲೋಕವೆಂಬ ಉತ್ತಮದೇಶವನ್ನು ಹಾರೈಸುವವರು. ಆದದರಿಂದ ದೇವರು ಅವರ ದೇವರೆನಿಸಿಕೊಳ್ಳುವದಕ್ಕೆ ನಾಚಿಕೊಳ್ಳದೆ ಅವರಿಗೋಸ್ಕರ ಪಟ್ಟಣವನ್ನು ಸಿದ್ಧಮಾಡಿದ್ದಾನೆ.",
			"17": "<sup>17</sup> ಅಬ್ರಹಾಮನು ಪರಿಶೋಧಿತನಾಗಿ ನಂಬಿಕೆಯಿಂದಲೇ ಇಸಾಕನನ್ನು ಸಮರ್ಪಿಸಿದನು. ಇಸಾಕನು ಅವನ ಏಕಪುತ್ರನಾಗಿದ್ದರೂ ಮತ್ತು ಇಸಾಕನಿಂದ ಹುಟ್ಟುವವರೇ ನಿನ್ನ ಸಂತತಿ ಅನ್ನಿಸಿಕೊಳ್ಳುವರೆಂದು ದೇವರು ಅವನಿಗೆ ಹೇಳಿದ್ದರೂ ಆ ವಾಗ್ದಾನಗಳನ್ನು ಹೊಂದಿದ ಅವನು ಆ ಒಬ್ಬನೇ ಮಗನನ್ನು ಸಮರ್ಪಿಸುವದಕ್ಕಿದ್ದನು.",
			"19": "<sup>19</sup> ತನ್ನ ಮಗನು ಸತ್ತರೂ ದೇವರು ಅವನನ್ನು ಬದುಕಿಸ ಸಮರ್ಥನಾಗಿದ್ದಾನೆಂದು ತಿಳುಕೊಂಡನು. ಮತ್ತು ಸತ್ತವರೊಳಗಿಂದಲೇ ಜೀವಿತನಾಗಿ ಬಂದವನಂತೆ ಅವನನ್ನು ಹೊಂದಿದನು.",
			"20": "<sup>20</sup> ನಂಬಿಕೆಯಿಂದಲೇ ಇಸಾಕನು ಯಾಕೋಬನನ್ನೂ ಏಸಾವನನ್ನೂ ಆಶೀರ್ವದಿಸಿದಾಗ ಮುಂದೆ ಸಂಭವಿಸಬೇಕಾದ ಸಂಗತಿಗಳನ್ನು ಸೂಚಿಸಿದನು.",
			"21": "<sup>21</sup> ಯಾಕೋಬನು ತಾನು ಸಾಯುತ್ತಿರುವಾಗ ನಂಬಿಕೆಯಿಂದಲೇ ಯೋಸೇಫನ ಮಕ್ಕಳಿಬ್ಬರನ್ನು ಆಶೀರ್ವದಿಸಿದನು; ತನ್ನ ಕೋಲಿನ ಮೇಲೆ ಬಾಗಿಕೊಂಡು ದೇವರಿಗೆ ನಮಸ್ಕಾರಮಾಡಿದನು.",
			"22": "<sup>22</sup> ಯೋಸೇಫನು ತೀರಿಹೋಗುವಾಗ ಇಸ್ರಾಯೇಲ್ಯರು ಐಗುಪ್ತದೇಶದಿಂದ ಹೊರಡುವದನ್ನು ಕುರಿತು ನಂಬಿಕೆಯಿಂದಲೇ ಮಾತಾಡಿ ತನ್ನ ಶವವನ್ನು ತಕ್ಕೊಂಡು ಹೋಗುವ ವಿಷಯದಲ್ಲಿ ಅಪ್ಪಣೆಕೊಟ್ಟನು.",
			"23": "<sup>23</sup> ಮೋಶೆ ಹುಟ್ಟಿದಾಗ ಅವನ ತಂದೆ ತಾಯಿಗಳು ಕೂಸು ಸುಂದರವಾಗಿದೆ ಎಂದು ನೋಡಿ ಅರಸನ ಅಪ್ಪಣೆಗೆ ಭಯಪಡದೆ ನಂಬಿಕೆಯಿಂದಲೇ ಅವನನ್ನು ಮೂರು ತಿಂಗಳು ಬಚ್ಚಿಟ್ಟರು.",
			"24": "<sup>24</sup> ಮೋಶೆಯು ದೊಡ್ಡವನಾದ ಮೇಲೆ ಫರೋಹನ ಕುಮಾರ್ತೆಯ ಮಗನೆನಿಸಿಕೊಳ್ಳುವದು ಬೇಡವೆಂದದ್ದು ನಂಬಿಕೆಯಿಂದಲೇ.",
			"25": "<sup>25</sup> ಸ್ವಲ್ಪಕಾಲ ಪಾಪಭೋಗಗಳನ್ನನುಭವಿಸುವದಕ್ಕಿಂತ ದೇವರ ಜನರೊಂದಿಗೆ ಕಷ್ಟವನ್ನನುಭವಿಸುವದೇ ಒಳ್ಳೇದೆಂದು ತೀರ್ಮಾನಿಸಿಕೊಂಡನು.",
			"26": "<sup>26</sup> ಐಗುಪ್ತದೇಶದ ಸರ್ವೈಶ್ವರ್ಯಕ್ಕಿಂತಲೂ ಕ್ರಿಸ್ತನ ನಿಮಿತ್ತವಾಗಿ ಉಂಟಾಗುವ ನಿಂದೆಯೇ ಶ್ರೇಷ್ಠ ಭಾಗ್ಯವೆಂದೆಣಿಸಿಕೊಂಡನು; ಯಾಕಂದರೆ ಬರುವ ಪ್ರತಿಫಲದ ಮೇಲೆ ಕಣ್ಣಿಟ್ಟಿದ್ದನು.",
			"27": "<sup>27</sup> ಅವನು ಅರಸನ ರೌದ್ರಕ್ಕೆ ಭಯಪಡದೆ ಐಗುಪ್ತದೇಶವನ್ನು ಬಿಟ್ಟುಹೋದದ್ದು ನಂಬಿಕೆಯಿಂದಲೇ. ಅವನು ಅದೃಶ್ಯನಾಗಿರುವಾತನನ್ನು ದೃಷ್ಟಿಸುವವನೋ ಎಂಬಂತೆ ದೃಢಚಿತ್ತನಾಗಿದ್ದನು.",
			"28": "<sup>28</sup> ಚೊಚ್ಚಲಮಕ್ಕಳನ್ನು ಸಂಹರಿಸುವವನು ಇಸ್ರಾಯೇಲ್ಯರನ್ನು ಮುಟ್ಟದಂತೆ ಅವನು ಪಸ್ಕವೆಂಬ ಊಟವನ್ನೂ ರಕ್ತಪ್ರೋಕ್ಷಣಾಚಾರವನ್ನೂ ನೇಮಿಸಿದ್ದು ನಂಬಿಕೆಯಿಂದಲೇ.",
			"29": "<sup>29</sup> ನಂಬಿಕೆಯಿಂದಲೇ ಇಸ್ರಾಯೇಲ್ಯರು ಕೆಂಪುಸಮುದ್ರವನ್ನು ಒಣಭೂಮಿಯನ್ನು ದಾಟುವಂತೆ ದಾಟಿದರು; ಐಗುಪ್ತದೇಶದವರು ಅದನ್ನು ದಾಟುವದಕ್ಕೆ ಪ್ರಯತ್ನಿಸಿ ಮುಳುಗಿಹೋದರು.",
			"30": "<sup>30</sup> ನಂಬಿಕೆಯಿಂದಲೇ ಅವರು ಏಳು ದಿವಸಗಳ ತನಕ ಎರಿಕೋ ಪಟ್ಟಣದ ಗೋಡೆಗಳನ್ನು ಸುತ್ತಿದ ಮೇಲೆ ಅವು ಬಿದ್ದವು.",
			"31": "<sup>31</sup> ನಂಬಿಕೆಯಿಂದಲೇ ರಹಾಬಳೆಂಬ ಸೂಳೆಯು ಗೂಢಚಾರರನ್ನು ಸಮಾಧಾನವಾಗಿ ಸೇರಿಸಿಕೊಂಡು ನಂಬದವರೊಂದಿಗೆ ನಾಶವಾಗಲಿಲ್ಲ.",
			"32": "<sup>32</sup> ಇನ್ನೂ ಏನು ಹೇಳಬೇಕು? ಗಿಡಿಯೋನ್ ಬಾರಾಕ್ ಸಂಸೋನ್ ಎಫ್‍ಥ ದಾವೀದ್ ಸಮುವೇಲ್ ಎಂಬವರ ವೃತ್ತಾಂತವನ್ನೂ ಪ್ರವಾದಿಗಳ ವೃತ್ತಾಂತವನ್ನೂ ವಿವರವಾಗಿ ಹೇಳಬೇಕಾದರೆ ನನಗೆ ಸಮಯ ಸಾಲದು.",
			"33": "<sup>33</sup> ನಂಬಿಕೆಯ ಮೂಲಕ ಅವರು ರಾಜ್ಯಗಳನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡರು; ನೀತಿಯನ್ನು ನಡಿಸಿದರು; ವಾಗ್ದಾನಗಳನ್ನು ಪಡಕೊಂಡರು;",
			"34": "<sup>34</sup> ಸಿಂಹಗಳ ಬಾಯಿ ಕಟ್ಟಿದರು; ಬೆಂಕಿಯ ಬಲವನ್ನು ಆರಿಸಿದರು; ಕತ್ತಿಯ ಬಾಯಿಗೆ ತಪ್ಪಿಸಿಕೊಂಡರು; ನಿರ್ಬಲರಾಗಿದ್ದು ಬಲಿಷ್ಠರಾದರು; ಯುದ್ಧದಲ್ಲಿ ಪರಾಕ್ರಮಶಾಲಿಗಳಾದರು; ಪರರ ದಂಡುಗಳನ್ನು ಓಡಿಸಿಬಿಟ್ಟರು.",
			"35": "<sup>35</sup> ಸ್ತ್ರೀಯರು ಸತ್ತುಹೋಗಿದ್ದ ತಮ್ಮವರನ್ನು ಪುನರುತ್ಥಾನದಿಂದ ತಿರಿಗಿ ಹೊಂದಿದರು. ಕೆಲವರು ತಾವು ಯಾತನೆಯ ಯಂತ್ರಕ್ಕೆ ಕಟ್ಟಲ್ಪಟ್ಟಾಗ ಶ್ರೇಷ್ಠ ಪುನರುತ್ಥಾನವನ್ನು ಹೊಂದುವದಕ್ಕೋಸ್ಕರ ಬಿಡುಗಡೆ ಬೇಡವೆಂದು ಹೇಳಿ ಮುರಿಸಿಕೊಂಡು ಸತ್ತರು.",
			"36": "<sup>36</sup> ಬೇರೆ ಕೆಲವರು ಅಪಹಾಸ್ಯ ಕೊರಡೆಯ ಪೆಟ್ಟು ಬೇಡಿ ಸೆರೆಮನೆ ಇವುಗಳನ್ನು ಅನುಭವಿಸಿದರು.",
			"37": "<sup>37</sup> ಕೆಲವರನ್ನು ಜನರು ಕಲ್ಲೆಸೆದು ಕೊಂದರು; ಕೆಲವರನ್ನು ಗರಗಸದಿಂದ ಕೊಯ್ದು ಕೊಂದರು; ದೇವದ್ರೋಹಿಗಳಾಗಿ ಪ್ರಾಣವನ್ನು ಉಳಿಸಿಕೊಳ್ಳಿರೆಂದು ಕೆಲವರನ್ನು ಪ್ರೇರೇಪಿಸಿದರು; ಕೆಲವರನ್ನು ಕತ್ತಿಯಿಂದ ಕೊಂದರು. ಕೆಲವರು ಕೊರತೆ ಹಿಂಸೆ ಬಾಧೆ ಇವುಗಳನ್ನು ಅನುಭವಿಸುವವರಾಗಿದ್ದು ಕುರಿ ಮೇಕೆಗಳ ಚರ್ಮಗಳನ್ನು ಉಟ್ಟುಕೊಂಡವರಾಗಿ ತಿರುಗಾಡಿದರು.",
			"38": "<sup>38</sup> ಇಂಥವರಿಗೆ ಈ ಲೋಕವು ಯೋಗ್ಯಸ್ಥಳವಲ್ಲ; ಅವರು ತಮ್ಮ ದೇಶದ ಕಾಡು ಬೆಟ್ಟ ಗವಿ ಕುಣಿಗಳಲ್ಲಿ ಅಲೆಯುವವರಾಗಿದ್ದರು.",
			"39": "<sup>39</sup> ಇವರೆಲ್ಲರೂ ನಂಬಿಕೆಯ ಮೂಲಕ ಒಳ್ಳೇ ಹೆಸರನ್ನು ಸಂಪಾದಿಸಿಕೊಂಡಿದ್ದರೂ ವಾಗ್ದಾನದ ಫಲವನ್ನು ಹೊಂದಲಿಲ್ಲ;",
			"40": "<sup>40</sup> ದೇವರು ನಮಗೋಸ್ಕರ ಶ್ರೇಷ್ಠವಾದ ಭಾಗ್ಯವನ್ನು ಏರ್ಪಡಿಸಿ ನಾವಿಲ್ಲದೆ ಅವರು ಸಿದ್ಧಿಗೆ ಬರಬಾರದೆಂದು ಸಂಕಲ್ಪಿಸಿದನು."
		},
		"12": {
			"1": "<sup>1</sup> ಆದಕಾರಣ ಇಷ್ಟುಮಂದಿ ಸಾಕ್ಷಿಯವರು ಮೇಘದೋಪಾದಿಯಲ್ಲಿ ನಮ್ಮ ಸುತ್ತಲು ಇರುವದರಿಂದ ನಮಗೆ ಅಭ್ಯಂತರ ಮಾಡುವ ಎಲ್ಲಾ ಭಾರವನ್ನೂ ಹತ್ತಿಕೊಳ್ಳುವ ಪಾಪವನ್ನೂ ನಾವು ಸಹ ತೆಗೆದಿಟ್ಟು",
			"2": "<sup>2</sup> ನಂಬಿಕೆಯನ್ನು ಹುಟ್ಟಿಸುವವನೂ ಪೂರೈಸುವವನೂ ಆಗಿರುವ ಯೇಸುವಿನ ಮೇಲೆ ದೃಷ್ಟಿಯಿಟ್ಟು ನಮಗೆ ನೇಮಕವಾದ ಓಟವನ್ನು ಸ್ಥಿರಚಿತ್ತದಿಂದ ಓಡೋಣ. ಆತನು ತನ್ನ ಮುಂದೆ ಇಟ್ಟಿದ್ದ ಸಂತೋಷಕ್ಕೋಸ್ಕರ ಅವಮಾನವನ್ನು ಅಲಕ್ಷ್ಯಮಾಡಿ ಶಿಲುಬೆಯ ಮರಣವನ್ನು ಸಹಿಸಿಕೊಂಡು ದೇವರ ಸಿಂಹಾಸನದ ಬಲಗಡೆಯಲ್ಲಿ ಆಸನಾರೂಢನಾಗಿದ್ದಾನೆ.",
			"3": "<sup>3</sup> ನೀವು ಮನಗುಂದಿದವರಾಗಿ ಬೇಸರಗೊಳ್ಳದಂತೆ ಆತನನ್ನು ಆಲೋಚಿಸಿರಿ. ಆತನು ಪಾಪಿಗಳಿಂದ ಎಷ್ಟೋ ವಿರೋಧವನ್ನು ಸಹಿಸಿಕೊಂಡನು.",
			"4": "<sup>4</sup> ನೀವು ಪಾಪಕ್ಕೆ ವಿರೋಧವಾಗಿ ಹೋರಾಡುವದರಲ್ಲಿ ಪ್ರಾಣಾಪಾಯದ ತನಕ ಇನ್ನೂ ಅದನ್ನು ಎದುರಿಸಲಿಲ್ಲ.",
			"5": "<sup>5</sup> ಮಕ್ಕಳಿಗೆ ಹೇಳುವಂತೆ ನಿಮಗೆ ಹೇಳಿದ ಎಚ್ಚರಿಕೆಯ ಮಾತನ್ನು ಮರೆತುಬಿಟ್ಟಿದ್ದೀರೋ? ಏನಂದರೆ - ಮಗನೇ, ಕರ್ತನ ಶಿಕ್ಷೆಯನ್ನು ತಾತ್ಸಾರ ಮಾಡಬೇಡ; ಆತನು ನಿನ್ನನ್ನು ಗದರಿಸುವಾಗ ಬೇಸರಗೊಳ್ಳಬೇಡ;",
			"6": "<sup>6</sup> ಕರ್ತನು ತಾನು ಪ್ರೀತಿಸುವವನನ್ನೇ ಶಿಕ್ಷಿಸುತ್ತಾನೆ; ತಾನು ಸೇರಿಸಿಕೊಳ್ಳುವ ಪ್ರತಿಯೊಬ್ಬ ಮಗನನ್ನು ಹೊಡೆಯುತ್ತಾನೆ ಎಂಬದು.",
			"7": "<sup>7</sup> ನೀವು ಶಿಕ್ಷಿತರಾಗುವದಕ್ಕಾಗಿಯೇ ನಿಮಗೆ ಶಿಕ್ಷೆಯಾಗುತ್ತದೆಂದು ತಿಳಿದು ಅದನ್ನು ಸಹಿಸಿಕೊಳ್ಳಿರಿ; ದೇವರು ನಿಮ್ಮನ್ನು ಮಕ್ಕಳೆಂದು ನಡಿಸುತ್ತಾನೆ.",
			"8": "<sup>8</sup> ತಂದೆಯಿಂದ ಶಿಕ್ಷೆಹೊಂದದ ಮಗನೆಲ್ಲಿ? ಮಕ್ಕಳೆಲ್ಲರೂ ಹೊಂದುವ ಶಿಕ್ಷೆಯು ನಿಮಗುಂಟಾಗದಿದ್ದರೆ ನೀವು ಹಾದರದಿಂದ ಹುಟ್ಟಿದವರೇ ಹೊರತು ಪುತ್ರರಲ್ಲ.",
			"9": "<sup>9</sup> ಇದು ಮಾತ್ರವಲ್ಲದೆ ನಮ್ಮನ್ನು ಶಿಕ್ಷಿಸಿದಂಥ ಶರೀರ ಸಂಬಂಧವಾದ ತಂದೆಗಳನ್ನು ಸನ್ಮಾನಿಸಿದೆವಷ್ಟೆ; ನಮ್ಮ ಆತ್ಮಗಳಿಗೆ ತಂದೆಯಾಗಿರುವಾತನಿಗೆ ನಾವು ಇನ್ನೂ ಹೆಚ್ಚಾಗಿ ಒಳಪಟ್ಟು ಜೀವಿಸಬೇಕಲ್ಲವೇ.",
			"10": "<sup>10</sup> ಆ ತಂದೆಗಳು ಕೆಲವು ದಿವಸಗಳ ಪ್ರಯೋಜನವನ್ನು ಲಕ್ಷ್ಯಕ್ಕೆ ತೆಗೆದುಕೊಂಡು ತಮ್ಮ ಮನಸ್ಸಿಗೆ ತೋರಿದಂತೆ ನಮ್ಮನ್ನು ಶಿಕ್ಷಿಸಿದರು; ಆತನಾದರೋ ನಾವು ತನ್ನ ಪರಿಶುದ್ಧತೆಯಲ್ಲಿ ಪಾಲುಗಾರರಾಗಬೇಕೆಂದು ನಮ್ಮ ಹಿತಕ್ಕಾಗಿಯೇ ಶಿಕ್ಷಿಸುತ್ತಾನೆ.",
			"11": "<sup>11</sup> ಯಾವ ಶಿಕ್ಷೆಯಾದರೂ ತತ್ಕಾಲಕ್ಕೆ ಸಂತೋಷಕರವಾಗಿ ತೋಚದೆ ದುಃಖಕರವಾಗಿ ತೋಚುತ್ತದೆ; ಆದರೂ ತರುವಾಯ ಅದು ಶಿಕ್ಷೆ ಹೊಂದಿದವರಿಗೆ ನೀತಿಯೆಂಬ ಫಲವನ್ನು ಕೊಟ್ಟು ಮನಸ್ಸಿಗೆ ಸಮಾಧಾನವನ್ನು ಉಂಟುಮಾಡುತ್ತದೆ.",
			"12": "<sup>12</sup> ಆದದರಿಂದ ಜೋಲುಬಿದ್ದ ಕೈಗಳನ್ನೂ ನಡುಗುವ ಮೊಣಕಾಲುಗಳನ್ನೂ ಸುಧಾರಿಸಿಕೊಳ್ಳಿರಿ;",
			"13": "<sup>13</sup> ನಿಮ್ಮ ಕಾಲುಗಳಿಂದ ನೆಟ್ಟಗೆ ಮುಂದೆ ನಡೆಯಿರಿ. ಹೀಗೆ ಮಾಡಿದರೆ ಕುಂಟಕಾಲು ಉಳುಕಿಹೋಗದೆ ವಾಸಿಯಾಗುವದು.",
			"14": "<sup>14</sup> ಎಲ್ಲರ ಸಂಗಡ ಸಮಾಧಾನದಿಂದಿರುವದಕ್ಕೂ ಪರಿಶುದ್ಧತೆಯನ್ನು ಹೊಂದುವದಕ್ಕೂ ಪ್ರಯತ್ನಮಾಡಿರಿ;",
			"15": "<sup>15</sup> ಪರಿಶುದ್ಧತೆಯಿಲ್ಲದೆ ಯಾವನೂ ಕರ್ತನನ್ನು ಕಾಣುವದಿಲ್ಲ. ನಿಮ್ಮಲ್ಲಿ ಯಾವನಾದರೂ ದೇವರ ಕೃಪೆಗೆ ತಪ್ಪಿ ಹಿಂಜರಿಯದಂತೆ ಯಾವ ವಿಷವುಳ್ಳ ಬೇರೂ ನಿಮ್ಮಲ್ಲಿ ಚಿಗುರಿ ಅಸಮಾಧಾನವನ್ನು ಹುಟ್ಟಿಸಿ ಸಭೆಯವರನ್ನು ಕೆಡಿಸದಂತೆ",
			"16": "<sup>16</sup> ಯಾವ ಜಾರನಾಗಲಿ ಏಸಾವನಂಥ ಪ್ರಾಪಂಚಿಕನಾಗಲಿ ನಿಮ್ಮಲ್ಲಿ ಇರದಂತೆ ಜಾಗ್ರತೆಯಿಂದ ನೋಡಿಕೊಳ್ಳಿರಿ. ಆ ಏಸಾವನು ಒಂದೇ ಒಂದು ಊಟಕ್ಕೋಸ್ಕರ ತನ್ನ ಚೊಚ್ಚಲತನದ ಹಕ್ಕನ್ನು ಕೊಟ್ಟುಬಿಟ್ಟನು;",
			"17": "<sup>17</sup> ಅನಂತರದಲ್ಲಿ ತನ್ನ ತಂದೆಯ ಆಶೀರ್ವಾದವನ್ನು ಬಾಧ್ಯವಾಗಿ ಹೊಂದಬೇಕೆಂದು ಕಣ್ಣೀರು ಸುರಿಸುತ್ತಾ ಬೇಡಿಕೊಂಡರೂ, ನಡೆದದ್ದನ್ನು ಇಲ್ಲದ ಹಾಗೆ ಮಾಡುವದಕ್ಕೆ ಮಾರ್ಗವಿಲ್ಲದೆ ನಿರಾಕರಿಸಲ್ಪಟ್ಟನೆಂದು ಬಲ್ಲಿರಿ.",
			"18": "<sup>18</sup> ನೀವು ಮುಟ್ಟುವದಕ್ಕೆ ಸಾಧ್ಯವಾದಂಥ ಮತ್ತು ಬೆಂಕಿಹತ್ತಿದಂಥ ಬೆಟ್ಟಕ್ಕೂ ಮೋಡದ ಮೊಬ್ಬಿಗೂ ಕತ್ತಲೆಗೂ ಬಿರುಗಾಳಿಗೂ ತುತೂರಿಯ ಧ್ವನಿಗೂ ಮಾತುಗಳ ಶಬ್ದಕ್ಕೂ ಬಂದವರಲ್ಲ.",
			"19": "<sup>19</sup> ಆ ಶಬ್ದವನ್ನು ಕೇಳಿದವರು ತಮಗೆ ಇನ್ನು ಒಂದು ಮಾತನ್ನಾದರೂ ಹೇಳಬಾರದೆಂದು ಬೇಡಿಕೊಂಡರು;",
			"20": "<sup>20</sup> ಯಾಕಂದರೆ - ಒಂದು ಮೃಗವಾದರೂ ಬೆಟ್ಟಕ್ಕೆ ಮುಟ್ಟಿದರೆ ಅದನ್ನು ಕಲ್ಲೆಸೆದು ಕೊಲ್ಲಬೇಕೆಂಬ ವಿಧಿಯನ್ನು ತಾಳಲಾರದೆ ಇದ್ದರು.",
			"21": "<sup>21</sup> ಇದಲ್ಲದೆ ಅಲ್ಲಿ ತೋರಿದ್ದು ಅಷ್ಟು ಭಯಂಕರವಾಗಿದ್ದದರಿಂದ ಮೋಶೆಯು - ನನಗೆ ಬಹು ಭಯವಾಗುತ್ತದೆ, ನಡುಗುತ್ತೇನೆ ಎಂದು ಹೇಳಿದನು.",
			"22": "<sup>22</sup> ಆದರೆ ನೀವು ಚೀಯೋನ್ ಪರ್ವತಕ್ಕೂ ಜೀವಸ್ವರೂಪನಾದ ದೇವರ ಪಟ್ಟಣವಾಗಿರುವ ಪರಲೋಕದ ಯೆರೂಸಲೇಮಿಗೂ ಉತ್ಸವಸಂಘದಲ್ಲಿ ಕೂಡಿರುವ ಕೋಟ್ಯಾನುಕೋಟಿ ದೇವದೂತರ ಬಳಿಗೂ",
			"23": "<sup>23</sup> ಪರಲೋಕದಲ್ಲಿ ಹೆಸರು ಬರಸಿಕೊಂಡಿರುವ ಚೊಚ್ಚಲಮಕ್ಕಳ ಸಭೆಗೂ ಎಲ್ಲರಿಗೆ ನ್ಯಾಯಾಧಿಪತಿಯಾಗಿರುವ ದೇವರ ಬಳಿಗೂ ಸಿದ್ಧಿಗೆ ಬಂದಿರುವ ನೀತಿವಂತರ ಆತ್ಮಗಳ ಬಳಿಗೂ",
			"24": "<sup>24</sup> ಹೊಸ ಒಡಂಬಡಿಕೆಗೆ ಮಧ್ಯಸ್ಥನಾಗಿರುವ ಯೇಸುವಿನ ಬಳಿಗೂ ಹೇಬೆಲನ ರಕ್ತಕ್ಕಿಂತ ಹಿತಕರವಾಗಿ ಮಾತಾಡುವ ಪ್ರೋಕ್ಷಣ ರಕ್ತದ ಬಳಿಗೂ ಬಂದಿದ್ದೀರಿ.",
			"25": "<sup>25</sup> ನೀವು ಮಾತಾಡುತ್ತಿರುವಾತನ ಮಾತನ್ನು ಕೇಳಲೊಲ್ಲೆವೆಂದು ಹೇಳಬಾರದು. ಭೂಮಿಯ ಮೇಲೆ ದೈವೋಕ್ತಿಗಳನ್ನಾಡಿದವನಿಗೆ ಕಿವಿಗೊಡ ಮನಸ್ಸಿಲ್ಲದೆ ಇದ್ದ ಇಸ್ರಾಯೇಲ್ಯರು ದಂಡನೆಗೆ ತಪ್ಪಿಸಿಕೊಳ್ಳದಿದ್ದರೆ ಪರಲೋಕದಿಂದ ಮಾತಾಡುವವನಿಗೆ ನಾವು ಕಿವಿಗೊಡ ಮನಸ್ಸಿಲ್ಲದೆ ತೊಲಗಿದರೆ ಹೇಗೆ ತಪ್ಪಿಸಿಕೊಂಡೇವು?",
			"26": "<sup>26</sup> ಆತನ ಧ್ವನಿಯು ಆ ಕಾಲದಲ್ಲಿ ಭೂಮಿಯನ್ನು ಕದಲಿಸಿತು; ಈಗಲಾದರೋ ಆತನು - ಇನ್ನೊಂದೇ ಸಾರಿ ನಾನು ಭೂಮಿಯನ್ನು ಮಾತ್ರವಲ್ಲದೆ ಪರಲೋಕವನ್ನೂ ನಡುಗಿಸುವೆನೆಂದು ವಾಗ್ದಾನಮಾಡಿದ್ದಾನೆ.",
			"27": "<sup>27</sup> ಇನ್ನೊಂದೇ ಸಾರಿ ಎಂಬೀ ಮಾತನ್ನು ಯೋಚಿಸಿದರೆ ಕದಲಿಸಿರುವ ವಸ್ತುಗಳು ನಿರ್ಮಿತವಾದವುಗಳಾದದರಿಂದ ತೆಗೆದುಹಾಕಲ್ಪಡಬೇಕೆಂಬದು ಸ್ಪಷ್ಟವಾಗುತ್ತದೆ; ಆಗ ಕದಲಿಸದೆ ಇರುವ ವಸ್ತುಗಳು ಸ್ಥಿರವಾಗಿ ನಿಲ್ಲುವವು.",
			"28": "<sup>28</sup> ಆದಕಾರಣ ಯಾರೂ ಕದಲಿಸಲಾರದ ರಾಜ್ಯವನ್ನು ಹೊಂದುವವರಾದ ನಾವು ಕೃತಜ್ಞತೆಯುಳ್ಳವರಾಗಿದ್ದು ಆತನಿಗೆ ಸಮರ್ಪಕವಾದ ಆರಾಧನೆಯನ್ನು ಭಕ್ತಿಯಿಂದಲೂ ಭಯದಿಂದಲೂ ಮಾಡೋಣ;",
			"29": "<sup>29</sup> ಯಾಕಂದರೆ ನಮ್ಮ ದೇವರು ದಹಿಸುವ ಅಗ್ನಿಯಾಗಿದ್ದಾನೆ."
		},
		"13": {
			"1": "<sup>1</sup> ಸಹೋದರ ಸ್ನೇಹವು ನೆಲೆಯಾಗಿರಲಿ.",
			"2": "<sup>2</sup> ಅತಿಥಿಸತ್ಕಾರಮಾಡುವದನ್ನು ಮರೆಯಬೇಡಿರಿ; ಅದನ್ನು ಮಾಡುವಲ್ಲಿ ಕೆಲವರು ತಿಳಿಯದೆ ದೇವದೂತರನ್ನೂ ಸತ್ಕರಿಸಿದ್ದಾರೆ.",
			"3": "<sup>3</sup> ಸೆರೆಯವರ ಸಂಗಡ ನೀವೂ ಜೊತೆ ಸೆರೆಯವರೆಂದು ಭಾವಿಸಿಕೊಂಡು ಅವರನ್ನು ಜ್ಞಾಪಕಮಾಡಿಕೊಳ್ಳಿರಿ. ನಿಮಗೂ ಅನ್ಯಾಯ ಸಂಭವಿಸೀತೆಂದು ತಿಳಿದು ಅನ್ಯಾಯ ಅನುಭವಿಸುವವರನ್ನು ನೆನಸಿರಿ. ದಾಂಪತ್ಯವು ಮಾನ್ಯವಾದದ್ದೆಂದು ಎಲ್ಲರೂ ಎಣಿಸಬೇಕು;",
			"4": "<sup>4</sup> ಗಂಡಹೆಂಡರ ಸಂಬಂಧವು ನಿಷ್ಕಳಂಕವಾಗಿರಬೇಕು. ಜಾರರಿಗೂ ವ್ಯಭಿಚಾರಿಗಳಿಗೂ ದೇವರು ನ್ಯಾಯತೀರಿಸುವನೆಂದು ತಿಳುಕೊಳ್ಳಿರಿ.",
			"5": "<sup>5</sup> ದ್ರವ್ಯಾಶೆಯಿಲ್ಲದವರಾಗಿರಿ; ನಿಮಗಿರುವವುಗಳಲ್ಲಿ ತೃಪ್ತರಾಗಿರಿ; ನಾನು ನಿನ್ನನ್ನು ಎಂದಿಗೂ ಕೈಬಿಡುವದಿಲ್ಲ, ಎಂದಿಗೂ ತೊರೆಯುವದಿಲ್ಲವೆಂದು ದೇವರು ತಾನೇ ಹೇಳಿದ್ದಾನೆ.",
			"6": "<sup>6</sup> ಆದದರಿಂದ - ಕರ್ತನು ನನ್ನ ಸಹಾಯಕನು, ಭಯಪಡೆನು, ಮನುಷ್ಯನು ನನಗೆ ಏನು ಮಾಡಾನು? ಎಂದು ನಾವು ಧೈರ್ಯವಾಗಿ ಹೇಳಬಲ್ಲೆವು. ನಿಮಗೆ ದೇವರ ವಾಕ್ಯವನ್ನು ತಿಳಿಸಿದ ನಿಮ್ಮ ಸಭಾನಾಯಕರನ್ನು ಜ್ಞಾಪಕಮಾಡಿಕೊಳ್ಳಿರಿ;",
			"7": "<sup>7</sup> ಅವರು ಯಾವ ರೀತಿಯಿಂದ ನಡೆದುಕೊಂಡು ಪ್ರಾಣಬಿಟ್ಟರೆಂಬದನ್ನು ಆಲೋಚಿಸಿರಿ; ಅವರ ನಂಬಿಕೆಯನ್ನು ಅನುಸರಿಸಿರಿ.",
			"8": "<sup>8</sup> ಯೇಸು ಕ್ರಿಸ್ತನು ನಿನ್ನೆ ಇದ್ದ ಹಾಗೆ ಈಹೊತ್ತೂ ಇದ್ದಾನೆ, ನಿರಂತರವೂ ಹಾಗೆಯೇ ಇರುವನು.",
			"9": "<sup>9</sup> ನಾನಾವಿಧವಾದ ಅನ್ಯೋಪದೇಶಗಳ ಸೆಳವಿಗೆ ಸಿಕ್ಕಬೇಡಿರಿ. ದೇವರ ಕೃಪೆಯನ್ನು ಆತುಕೊಂಡು ಮನಸ್ಸನ್ನು ದೃಢಮಾಡಿಕೊಳ್ಳುವದು ಉತ್ತಮವೇ; ಭೋಜನಪದಾರ್ಥಗಳನ್ನು ವಿಶೇಷಿಸುವದರಿಂದ ಅದು ಆಗುವದಿಲ್ಲ. ಭೋಜನಪದಾರ್ಥಗಳನ್ನು ವಿಶೇಷಿಸಿ ನಡೆದವರು ಏನೂ ಪ್ರಯೋಜನ ಹೊಂದಲಿಲ್ಲ.",
			"10": "<sup>10</sup> ನಮಗೊಂದು ಯಜ್ಞವೇದಿ ಉಂಟು, ಈ ವೇದಿಯ ಪದಾರ್ಥಗಳನ್ನು ತಿನ್ನುವದಕ್ಕೆ ಯೆಹೂದ್ಯರ ಗುಡಾರದಲ್ಲಿ ಸೇವೆಯನ್ನು ನಡಿಸುವವರಿಗೆ ಹಕ್ಕಿಲ್ಲ.",
			"11": "<sup>11</sup> ಮಹಾಯಾಜಕನು ದೋಷಪರಿಹಾರಕಯಜ್ಞದ ಪಶುಗಳ ರಕ್ತವನ್ನು ತೆಗೆದುಕೊಂಡು ಪವಿತ್ರಸ್ಥಾನದೊಳಗೆ ಹೋಗಲು ಆ ಪಶುಗಳ ದೇಹಗಳು ಪಾಳೆಯದ ಆಚೆ ಸುಡಲ್ಪಡುತ್ತವಲ್ಲಾ.",
			"12": "<sup>12</sup> ಅದರಂತೆ ಯೇಸು ಕೂಡ ಸ್ವಂತ ರಕ್ತದಿಂದ ತನ್ನ ಜನರನ್ನು ಪವಿತ್ರಪಡಿಸುವದಕ್ಕೋಸ್ಕರ ಪಟ್ಟಣದ ಹೊರಗೆ ಸತ್ತನು.",
			"13": "<sup>13</sup> ಆದದರಿಂದ ನಾವು ಆತನ ನಿಮಿತ್ತ ಉಂಟಾಗುವ ನಿಂದೆಯನ್ನು ತಾಳಿಕೊಳ್ಳುವವರಾಗಿ ಪಾಳೆಯದ ಆಚೆ ಆತನ ಬಳಿಗೆ ಹೊರಟುಹೋಗೋಣ.",
			"14": "<sup>14</sup> ಯಾಕಂದರೆ ಇಹದಲ್ಲಿ ಶಾಶ್ವತವಾದ ಪಟ್ಟಣವು ನಮಗಿಲ್ಲ; ಇನ್ನು ಮುಂದೆ ಆಗುವ ಪಟ್ಟಣವನ್ನು ಹಾರೈಸುತ್ತಾ ಇದ್ದೇವೆ.",
			"15": "<sup>15</sup> ಆದದರಿಂದ ಆತನ ಮೂಲಕವಾಗಿಯೇ ದೇವರಿಗೆ ಸ್ತೋತ್ರಯಜ್ಞವನ್ನು ಎಡೆಬಿಡದೆ ಸಮರ್ಪಿಸೋಣ. ಆತನು ಕರ್ತನೆಂದು ಬಾಯಿಂದ ಪ್ರತಿಜ್ಞೆಮಾಡುವದೇ ನಾವು ಅರ್ಪಿಸುವ ಯಜ್ಞವಾಗಿದೆ.",
			"16": "<sup>16</sup> ಇದಲ್ಲದೆ ಪರೋಪಕಾರವನ್ನೂ ಧರ್ಮಮಾಡುವದನ್ನೂ ಮರೆಯಬೇಡಿರಿ; ಇವೇ ದೇವರಿಗೆ ಸಮರ್ಪಕವಾದ ಯಜ್ಞಗಳು.",
			"17": "<sup>17</sup> ನಿಮ್ಮ ಸಭಾನಾಯಕರ ಮಾತನ್ನು ಕೇಳಿರಿ, ಅವರಿಗೆ ಅಧೀನರಾಗಿರಿ. ಅವರು ಲೆಕ್ಕ ಒಪ್ಪಿಸಬೇಕಾದವರಾಗಿ ನಿಮ್ಮ ಆತ್ಮಗಳನ್ನು ಕಾಯುವವರಾಗಿದ್ದಾರೆ. ಅವರು ವ್ಯಸನಪಡದೆ ಸಂತೋಷದಿಂದ ಇದನ್ನು ಮಾಡುವಂತೆ ನೋಡಿರಿ; ಅವರು ವ್ಯಸನದಿಂದಿರುವದು ನಿಮಗೆ ಪ್ರಯೋಜನಕರವಾದದ್ದಲ್ಲ.",
			"18": "<sup>18</sup> ನಮಗೋಸ್ಕರ ಪ್ರಾರ್ಥಿಸಿರಿ. ನಾವು ಎಲ್ಲಾ ವಿಷಯಗಳಲ್ಲಿ ಸಜ್ಜನರಾಗಿ ನಡೆದುಕೊಳ್ಳಬೇಕೆಂದು ಅಪೇಕ್ಷಿಸುವವರಾಗಿದ್ದು ನಮ್ಮ ಮನಸ್ಸಿನಿಂದ ಒಳ್ಳೇ ಸಾಕ್ಷಿ ಹೊಂದಿದ್ದೇವೆಂದು ನಿಶ್ಚಯಿಸಿಕೊಂಡಿದ್ದೇವೆ.",
			"19": "<sup>19</sup> ನಮಗೋಸ್ಕರ ಪ್ರಾರ್ಥನೆಮಾಡಬೇಕೆಂದು ನಿಮ್ಮನ್ನು ಬಹು ವಿಶೇಷವಾಗಿ ಬೇಡಿಕೊಳ್ಳುತ್ತೇನೆ, ಯಾಕಂದರೆ ನಿಮ್ಮ ಪ್ರಾರ್ಥನೆಗಳ ಮೂಲಕ ನಾನು ಅತಿ ಶೀಘ್ರವಾಗಿ ನಿಮ್ಮ ಬಳಿಗೆ ತಿರಿಗಿ ಬಂದೇನು.",
			"20": "<sup>20</sup> ಶಾಶ್ವತವಾದ ಒಡಂಬಡಿಕೆಯನ್ನು ನಿಶ್ಚಯಪಡಿಸುವದಕ್ಕಾಗಿ ತನ್ನ ರಕ್ತವನ್ನು ಸುರಿಸಿಕೊಂಡು ಸಭೆಯೆಂಬ ಹಿಂಡಿಗೆ ಮಹಾಪಾಲಕನಾಗಿರುವ ನಮ್ಮ ಕರ್ತನಾದ ಯೇಸುವನ್ನು ಸತ್ತವರೊಳಗಿಂದ ಬರಮಾಡಿದ ಶಾಂತಿದಾಯಕನಾದ ದೇವರು,",
			"21": "<sup>21</sup> ನೀವು ತನ್ನ ಚಿತ್ತವನ್ನು ನೆರವೇರಿಸುವ ಹಾಗೆ ಸಕಲ ಸತ್ಕಾರ್ಯಗಳಿಗೆ ಬೇಕಾದ ಅನುಕೂಲತೆಯನ್ನು ನಿಮಗೆ ಅನುಗ್ರಹಿಸಲಿ. ಆತನು ಯೇಸು ಕ್ರಿಸ್ತನ ಮೂಲಕ ತನಗೆ ಸಮರ್ಪಕವಾದದ್ದನ್ನು ನಮ್ಮಲ್ಲಿ ನಡಿಸಲಿ. ಯುಗಯುಗಾಂತರಗಳಲ್ಲಿಯೂ ಆತನಿಗೆ ಸ್ತೋತ್ರ. ಆಮೆನ್.",
			"22": "<sup>22</sup> ಸಹೋದರರೇ, ಈ ಎಚ್ಚರಿಕೆಯ ಮಾತನ್ನು ಸಹಿಸಿಕೊಳ್ಳಬೇಕೆಂದು ನಿಮ್ಮನ್ನು ಬೇಡಿಕೊಳ್ಳುತ್ತೇನೆ; ನಿಮಗೆ ಸಂಕ್ಷೇಪವಾಗಿ ಬರೆದಿದ್ದೇನೆ.",
			"23": "<sup>23</sup> ನಮ್ಮ ಸಹೋದರನಾದ ತಿಮೊಥೆಯನಿಗೆ ಬಿಡುಗಡೆಯಾಯಿತೆಂಬದು ನಿಮಗೆ ತಿಳಿದಿರಲಿ. ಅವನು ಬೇಗ ಬಂದರೆ ನಾನೂ ಅವನೊಂದಿಗೆ ಬಂದು ನಿಮ್ಮನ್ನು ಕಾಣುವೆನು.",
			"24": "<sup>24</sup> ನಿಮ್ಮ ಸಭಾನಾಯಕರೆಲ್ಲರಿಗೂ ದೇವಜನರೆಲ್ಲರಿಗೂ ವಂದನೆ ಹೇಳಿರಿ. ಇತಾಲ್ಯ ದೇಶದಿಂದ ಬಂದವರು ನಿಮಗೆ ವಂದನೆ ಹೇಳುತ್ತಾರೆ.",
			"25": "<sup>25</sup> ಕೃಪೆಯು ನಿಮ್ಮೆಲ್ಲರೊಂದಿಗೆ ಇರಲಿ."
		}
	}
};
module.exports = book;