var book = {
	"name": "1 ಪೇತ್ರನು",
	"numChapters": 5,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ಯೇಸು ಕ್ರಿಸ್ತನ ಅಪೊಸ್ತಲನಾದ ಪೇತ್ರನು ಪೊಂತ ಗಲಾತ್ಯ ಕಪ್ಪದೋಕ್ಯ ಆಸ್ಯ ಬಿಥೂನ್ಯ ಎಂಬ ಸೀಮೆಗಳಲ್ಲಿ ಚದರಿರುವಂಥ ಪ್ರವಾಸಿಗಳಾದ ದೇವಜನರಿಗೆ ಅಂದರೆ -",
			"2": "<sup>2</sup> ಪವಿತ್ರಾತ್ಮನಿಂದ ಪ್ರತಿಷ್ಠಿಸಲ್ಪಟ್ಟವರಾಗಿ ದೇವರಿಗೆ ವಿಧೇಯರಾಗುವದಕ್ಕೂ ಯೇಸು ಕ್ರಿಸ್ತನ ರಕ್ತದಿಂದ ಪ್ರೋಕ್ಷಿತರಾಗುವದಕ್ಕೂ ತಂದೆಯಾದ ದೇವರ ಭವಿಷ್ಯದ್ ಜ್ಞಾನಾನುಸಾರವಾಗಿ ಆರಿಸಿಕೊಳ್ಳಲ್ಪಟ್ಟವರಿಗೆ ಬರೆಯುವದೇನಂದರೆ - ನಿಮಗೆ ಕೃಪೆಯೂ ಶಾಂತಿಯೂ ಹೆಚ್ಚೆಚ್ಚಾಗಿ ದೊರೆಯಲಿ.",
			"3": "<sup>3</sup> ನಮ್ಮ ಕರ್ತನಾದ ಯೇಸು ಕ್ರಿಸ್ತನ ತಂದೆಯೂ ದೇವರೂ ಆಗಿರುವಾತನಿಗೆ ಸ್ತೋತ್ರವಾಗಲಿ. ಆತನು ಯೇಸು ಕ್ರಿಸ್ತನನ್ನು ಸತ್ತವರೊಳಗಿಂದ ಎಬ್ಬಿಸಿದ್ದರಲ್ಲಿ ತನ್ನ ಮಹಾ ಕರುಣಾನುಸಾರವಾಗಿ ನಮ್ಮನ್ನು ತಿರಿಗಿ ಜೀವಿಸುವಂತೆ ಮಾಡಿ ಜೀವಕರವಾದ ನಿರೀಕ್ಷೆಯನ್ನು ನಮ್ಮಲ್ಲಿ ಹುಟ್ಟಿಸಿ",
			"4": "<sup>4</sup> ನಮ್ಮನ್ನು ಲಯ ಕಳಂಕ ಕ್ಷಯಗಳಿಲ್ಲದ ಬಾಧ್ಯತೆಯನ್ನು ಎದುರುನೋಡುವವರನ್ನಾಗಿ ಮಾಡಿದನು. ಆ ಬಾಧ್ಯತೆಯು ನಿಮಗೋಸ್ಕರ ಪರಲೋಕದಲ್ಲಿ ಇಟ್ಟಿರುವದು.",
			"5": "<sup>5</sup> ಅಂತ್ಯಕಾಲದಲ್ಲಿ ಪ್ರತ್ಯಕ್ಷವಾಗುವದಕ್ಕೆ ಸಿದ್ಧವಾಗಿರುವ ರಕ್ಷಣೆಯು ನಂಬುವವರಾದ ನಿಮಗೆ ದೊರೆಯಬೇಕೆಂದು ದೇವರು ನಿಮ್ಮನ್ನು ತನ್ನ ಬಲದಿಂದ ಕಾಯುತ್ತಾನೆ.",
			"6": "<sup>6</sup> ನೀವು ಸದ್ಯಕ್ಕೆ ಸ್ವಲ್ಪಕಾಲ ದೇವರ ಚಿತ್ತಾನುಸಾರ ನಾನಾ ಕಷ್ಟಗಳಲ್ಲಿದ್ದು ದುಃಖಿಸುವವರಾಗಿದ್ದರೂ ಆ ಪದವಿಯನ್ನು ಆಲೋಚಿಸಿ ಹರ್ಷಿಸುವವರಾಗಿದ್ದೀರಿ.",
			"7": "<sup>7</sup> ಬಂಗಾರವು ನಾಶವಾಗುವಂಥದಾಗಿದ್ದರೂ ಅದನ್ನು ಬೆಂಕಿಯಲ್ಲಿ ಪುಟಾಹಾಕಿ ಶೋಧಿಸುವದುಂಟಷ್ಟೆ. ಬಂಗಾರಕ್ಕಿಂತ ಅಮೂಲ್ಯವಾಗಿರುವ ನಿಮ್ಮ ನಂಬಿಕೆಯು ಈ ಕಷ್ಟಗಳಿಂದ ಶೋಧಿತವಾಗಿ ಯೇಸು ಕ್ರಿಸ್ತನು ಪ್ರತ್ಯಕ್ಷನಾಗುವಾಗ ನಿಮಗೆ ಕೀರ್ತಿ ಪ್ರಭಾವ ಮಾನಗಳನ್ನು ಉಂಟುಮಾಡುವದು.",
			"8": "<sup>8</sup> ನೀವು ಆತನನ್ನು ಕಣ್ಣಾರೆ ನೋಡಲಿಲ್ಲವಾದರೂ ಆತನನ್ನು ಪ್ರೀತಿಸುತ್ತೀರಿ. ನೀವು ಈಗ ಆತನನ್ನು ಕಾಣದಿದ್ದರೂ ಆತನಲ್ಲಿ ನಂಬಿಕೆಯಿಟ್ಟು ನಿಮ್ಮ ನಂಬಿಕೆಯ ಅಂತ್ಯಫಲವಾಗಿರುವ ಆತ್ಮರಕ್ಷಣೆಯನ್ನು ಹೊಂದುವವರಾಗಿ ಹೇಳಲಶಕ್ಯವಾದಂಥ ಪ್ರಭಾವವುಳ್ಳ ಸಂತೋಷದಿಂದ ಹರ್ಷಿಸುತ್ತೀರಿ.",
			"10": "<sup>10</sup> ದೇವರು ನಿಮಗೆ ತೋರಿಸಿರುವ ಕೃಪೆಯನ್ನು ಕುರಿತು ಮುಂತಿಳಿಸಿದ ಪ್ರವಾದಿಗಳು ಈ ರಕ್ಷಣೆಯ ವಿಷಯದಲ್ಲಿ ಸೂಕ್ಷ್ಮವಾಗಿ ವಿಚಾರಿಸಿ ಪರಿಶೋಧನೆ ಮಾಡಿದರು.",
			"11": "<sup>11</sup> ತಮ್ಮಲ್ಲಿದ್ದ ಕ್ರಿಸ್ತನ ಆತ್ಮನು ಕ್ರಿಸ್ತನಿಗೆ ಬರಬೇಕಾದ ಬಾಧೆಗಳನ್ನೂ ಅವುಗಳ ತರುವಾಯ ಉಂಟಾಗುವ ಪ್ರಭಾವವನ್ನೂ ಮುಂದಾಗಿ ತಿಳಿಸಿದಾಗ ಆತನು ಯಾವ ಕಾಲವನ್ನು ಇಲ್ಲವೆ ಎಂಥ ಕಾಲವನ್ನು ಸೂಚಿಸುವನೆಂಬದನ್ನು ಅವರು ಪರಿಶೋಧನೆ ಮಾಡಿದರು.",
			"12": "<sup>12</sup> ಈ ಸಂಗತಿಗಳನ್ನು ಮುಂತಿಳಿಸುವದರಲ್ಲಿ ತಮಗೋಸ್ಕರವಲ್ಲ ನಿಮಗೋಸ್ಕರವೇ ಸೇವೆ ಮಾಡುವವರಾಗಿದ್ದರೆಂಬದು ಅವರಿಗೆ ಪ್ರಕಟವಾಯಿತು. ಅವರು ಮುಂದಾಗಿ ಹೇಳಿದ ಸಂಗತಿಗಳೇ ಈಗ ಸಂಭವಿಸಿದವು ಎಂಬ ವರ್ತಮಾನವು ಪರಲೋಕದಿಂದ ಕಳುಹಿಸಲ್ಪಟ್ಟ ಪವಿತ್ರಾತ್ಮನ ಬಲದಿಂದ ನಿಮಗೆ ಸುವಾರ್ತೆಯನ್ನು ಸಾರಿದವರ ಮೂಲಕ ನಿಮಗೆ ಪ್ರಸಿದ್ಧಿಮಾಡಲ್ಪಟ್ಟಿದೆ. ದೇವದೂತರಿಗೂ ಈ ಸಂಗತಿಗಳನ್ನು ಲಕ್ಷ್ಯವಿಟ್ಟು ನೋಡಬೇಕೆಂಬ ಅಪೇಕ್ಷೆ ಉಂಟು.",
			"13": "<sup>13</sup> ಆದದರಿಂದ ನೀವು ಮನಸ್ಸಿನ ನಡುವನ್ನು ಕಟ್ಟಿಕೊಂಡು ಸ್ವಸ್ಥಚಿತ್ತರಾಗಿದ್ದು ಯೇಸು ಕ್ರಿಸ್ತನು ಪ್ರತ್ಯಕ್ಷನಾಗುವಾಗ ನಿಮಗೆ ದೊರಕುವ ಭಾಗ್ಯದ ಮೇಲೆ ನಿಮ್ಮ ನಿರೀಕ್ಷೆಯನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ಇಡಿರಿ.",
			"14": "<sup>14</sup> ನೀವು ಮುಂಚೆ ಅಜ್ಞಾನಿಗಳಾಗಿದ್ದಾಗ ನಿಮಗಿದ್ದ ದುರಾಶೆಗಳನ್ನು ಅನುಸರಿಸಿ ನಡೆಯುವವರಾಗಿರದೆ",
			"15": "<sup>15</sup> ನಿಮ್ಮನ್ನು ಕರೆದಾತನು ಪರಿಶುದ್ಧನಾಗಿರುವ ಪ್ರಕಾರವೇ ನೀವೂ ವಿಧೇಯರಿಗೆ ತಕ್ಕಂತೆ ನಿಮ್ಮ ಎಲ್ಲಾ ನಡವಳಿಕೆಯಲ್ಲಿ ಪರಿಶುದ್ಧರಾಗಿರಿ.",
			"16": "<sup>16</sup> ನಾನು ಪರಿಶುದ್ಧನಾಗಿರುವದರಿಂದ ನೀವೂ ಪರಿಶುದ್ಧರಾಗಿರಬೇಕು ಎಂದು ಬರೆದದೆಯಲ್ಲಾ.",
			"17": "<sup>17</sup> ಪ್ರತಿಯೊಬ್ಬನ ಕೆಲಸವನ್ನು ನೋಡಿ ಪಕ್ಷಪಾತವಿಲ್ಲದೆ ತೀರ್ಪುಮಾಡುವಾತನನ್ನು ನೀವು ತಂದೆಯೆಂದು ಕರೆಯುವವರಾಗಿರಲಾಗಿ ಈ ಲೋಕದಲ್ಲಿ ನಿಮ್ಮ ಪ್ರವಾಸಕಾಲವನ್ನು ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿ ಕಳೆಯಿರಿ.",
			"18": "<sup>18</sup> ನಿಮ್ಮ ಹಿರಿಯರು ಕಲಿಸಿಕೊಟ್ಟ ವ್ಯರ್ಥವಾದ ನಡವಳಿಕೆಯಿಂದ ನಿಮಗೆ ಬಿಡುಗಡೆಯಾದದ್ದು ಬೆಳ್ಳಿಬಂಗಾರ ಮೊದಲಾದ ನಶಿಸಿಹೋಗುವ ವಸ್ತುಗಳಿಂದಲ್ಲ,",
			"19": "<sup>19</sup> ಪೂರ್ಣಾಂಗವಾದ ನಿಷ್ಕಳಂಕ ಯಜ್ಞದ ಕುರಿಯ ರಕ್ತದಿಂದಲೋ ಎಂಬಂತೆ ಕ್ರಿಸ್ತನ ಅಮೂಲ್ಯವಾದ ರಕ್ತದಿಂದಲೇ ಎಂದು ಬಲ್ಲಿರಲ್ಲವೇ.",
			"20": "<sup>20</sup> ಆತನು ಜಗದುತ್ಪತ್ತಿಗೆ ಮೊದಲೇ ಗೊತ್ತು ಮಾಡಲ್ಪಟ್ಟು ಅಂತ್ಯಕಾಲದಲ್ಲಿ ನಿಮಗಾಗಿ ಪ್ರತ್ಯಕ್ಷನಾದನು.",
			"21": "<sup>21</sup> ಆತನನ್ನು ಸತ್ತವರೊಳಗಿಂದ ಎಬ್ಬಿಸಿ ಆತನಿಗೆ ಪ್ರಭಾವವನ್ನು ಕೊಟ್ಟ ದೇವರಲ್ಲಿ ನೀವು ಆತನ ಮೂಲಕ ನಂಬಿಕೆಯಿಟ್ಟವರಾದಿರಷ್ಟೆ. ಹೀಗಿರಲಾಗಿ ನಿಮ್ಮ ನಂಬಿಕೆಯೂ ನಿರೀಕ್ಷೆಯೂ ದೇವರಲ್ಲಿಯೇ ನೆಲೆಗೊಂಡವೆ.",
			"22": "<sup>22</sup> ನೀವು ಸತ್ಯೋಪದೇಶಕ್ಕೆ ವಿಧೇಯರಾಗಿದ್ದು ನಿಮ್ಮ ಆತ್ಮಗಳನ್ನು ಶುದ್ಧಮಾಡಿಕೊಂಡದ್ದರಿಂದ ನಿಷ್ಕಪಟವಾದ ಸಹೋದರ ಸ್ನೇಹವುಳ್ಳವರಾಗಿರತಕ್ಕದ್ದು. ಹೀಗಿರಲಾಗಿ ಒಬ್ಬರನ್ನೊಬ್ಬರು ಹೃದಯಪೂರ್ವಕವಾಗಿಯೂ ಯಥಾರ್ಥವಾಗಿಯೂ ಪ್ರೀತಿಸಿರಿ.",
			"23": "<sup>23</sup> ನೀವು ಪುನರ್ಜನ್ಮ ಹೊಂದಿದವರಾಗಿದ್ದೀರಲ್ಲಾ; ಆ ಜನ್ಮವು ನಾಶವಾಗುವ ಬೀಜದಿಂದ ಉಂಟಾದದ್ದಲ್ಲ, ನಾಶವಾಗದ ಬೀಜದಿಂದಲೇ ಉಂಟಾದದ್ದು. ಅದು ದೇವರ ಸದಾಜೀವವುಳ್ಳ ವಾಕ್ಯದ ಮೂಲಕವಾಗಿ ಉಂಟಾಯಿತು.",
			"24": "<sup>24</sup> ನರಜಾತಿಯೆಲ್ಲಾ ಹುಲ್ಲಿನ ಹಾಗಿದೆ, ಅದರ ಪ್ರಭಾವವೆಲ್ಲಾ ಹುಲ್ಲಿನ ಹೂವಿನಂತದೆ. ಹುಲ್ಲು ಒಣಗಿಹೋಗುವದು, ಹೂವು ಉದುರಿಹೋಗುವದು;",
			"25": "<sup>25</sup> ಕರ್ತನ ಮಾತೋ ಸದಾಕಾಲವೂ ಇರುವದು ಎಂದು ಹೇಳಿದೆಯಷ್ಟೆ. ಆ ಮಾತು ಯಾವದಂದರೆ ನಿಮಗೆ ಸಾರಲ್ಪಟ್ಟ ಸುವಾರ್ತಾವಾಕ್ಯವೇ."
		},
		"2": {
			"1": "<sup>1</sup> ಆದಕಾರಣ ಎಲ್ಲಾ ಕೆಟ್ಟತನವನ್ನೂ ಎಲ್ಲಾ ವಂಚನೆಯನ್ನೂ ಕಪಟವನ್ನೂ ಹೊಟ್ಟೆಕಿಚ್ಚನ್ನೂ",
			"2": "<sup>2</sup> ಎಲ್ಲಾ ತರದ ನಿಂದೆಯನ್ನೂ ವಿಸರ್ಜಿಸಿ ಹಸುಮಕ್ಕಳಂತೆ ಪಾರಮಾರ್ಥಿಕವಾದ ಶುದ್ಧ ಹಾಲನ್ನು ಬಯಸಿರಿ; ಅದರಿಂದ ಬೆಳೆಯುತ್ತಾ ರಕ್ಷಣೆಯನ್ನು ಹೊಂದುವಿರಿ; ಕರ್ತನು ದಯಾಳುವೆಂದು ನೀವು ಅನುಭವದಿಂದ ತಿಳಿದುಕೊಂಡಿದ್ದೀರಲ್ಲವೋ?",
			"3": "<sup>3</sup> ನೀವು ಜೀವವುಳ್ಳ ಕಲ್ಲಾಗಿರುವ ಆತನನ್ನು ಸೇರಿಕೊಂಡಿದ್ದೀರಿ.",
			"4": "<sup>4</sup> ಆ ಕಲ್ಲನ್ನು ಮನುಷ್ಯರು ನಿರಾಕರಿಸಿದರೂ ದೇವರು ಅದನ್ನು ಆಯಲ್ಪಟ್ಟದ್ದೂ ಮಾನ್ಯವಾದದ್ದೂ ಎಂದು ಎಣಿಸಿದನು.",
			"5": "<sup>5</sup> ನೀವು ಸಹ ಜೀವವುಳ್ಳ ಕಲ್ಲುಗಳಾಗಿದ್ದು ಆತ್ಮ ಸಂಬಂಧವಾದ ಮಂದಿರವಾಗಲಿಕ್ಕೆ ಕಟ್ಟಲ್ಪಡುತ್ತಾ ಇದ್ದೀರಿ, ಮತ್ತು ಯೇಸು ಕ್ರಿಸ್ತನ ಮೂಲಕ ದೇವರಿಗೆ ಸಮರ್ಪಕವಾದ ಆತ್ಮೀಯಯಜ್ಞಗಳನ್ನು ಸಮರ್ಪಿಸುವದಕ್ಕೆ ಪವಿತ್ರ ಯಾಜಕವರ್ಗದವರಾಗಿದ್ದೀರಿ.",
			"6": "<sup>6</sup> ಇಗೋ, ಚೀಯೋನಿನಲ್ಲಿ ಮೂಲೆಗಲ್ಲನ್ನು ಇಡುತ್ತೇನೆ. ಅದು ಆಯಲ್ಪಟ್ಟದ್ದು, ಮಾನ್ಯವಾದದ್ದು; ಅದರ ಮೇಲೆ ನಂಬಿಕೆಯಿಡುವವನು ಆಶಾಭಂಗಪಡುವದೇ ಇಲ್ಲ ಎಂದು ಶಾಸ್ತ್ರದಲ್ಲಿ ಬರೆದದೆ.",
			"7": "<sup>7</sup> ಆದದರಿಂದ ನಂಬುವವರಾದ ನಿಮಗೇ ಈ ಮಾನವುಂಟು. ನಂಬದೆಯಿರುವವರಿಗೋ - ಮನೆಕಟ್ಟುವವರು ಬೇಡವೆಂದು ಬಿಟ್ಟ ಕಲ್ಲೇ ಮುಖ್ಯವಾದ ಮೂಲೆಗಲ್ಲಾಯಿತು ಎಂತಲೂ",
			"8": "<sup>8</sup> ಅದು ಎಡವುವ ಕಲ್ಲು ಮುಗ್ಗರಿಸುವ ಬಂಡೆ ಎಂತಲೂ ಬರೆದಿರುವ ಮಾತು ಸರಿಬೀಳುತ್ತದೆ. ಅವರು ದೇವರ ವಾಕ್ಯವನ್ನು ನಂಬಲೊಲ್ಲದೆ ಇರುವದರಿಂದ ಆ ಕಲ್ಲನ್ನು ಎಡವಿ ಬೀಳುತ್ತಾರೆ; ಅದಕ್ಕಾಗಿಯೇ ಅವರು ನೇಮಕವಾದರು.",
			"9": "<sup>9</sup> ನೀವಾದರೋ ನಿಮ್ಮನ್ನು ಕತ್ತಲೆಯೊಳಗಿಂದ ಕರೆದು ತನ್ನ ಆಶ್ಚರ್ಯಕರವಾದ ಬೆಳಕಿನಲ್ಲಿ ಸೇರಿಸಿದಾತನ ಗುಣಾತಿಶಯಗಳನ್ನು ಪ್ರಚಾರಮಾಡುವವರಾಗುವಂತೆ ದೇವರಾದುಕೊಂಡ ಜನಾಂಗವೂ ರಾಜವಂಶಸ್ಥರಾದ ಯಾಜಕರೂ ಮೀಸಲಾದ ಜನವೂ ದೇವರ ಸ್ವಕೀಯ ಪ್ರಜೆಯೂ ಆಗಿದ್ದೀರಿ.",
			"10": "<sup>10</sup> ಮೊದಲು ನೀವು ಪ್ರಜೆಯಾಗಿರಲಿಲ್ಲ, ಈಗ ದೇವರ ಪ್ರಜೆಯಾಗಿದ್ದೀರಿ; ಮೊದಲು ವಾತ್ಸಲ್ಯ ಹೊಂದಿರಲಿಲ್ಲ, ಈಗ ವಾತ್ಸಲ್ಯ ಹೊಂದಿದವರಾಗಿದ್ದೀರಿ.",
			"11": "<sup>11</sup> ಪ್ರಿಯರೇ, ಪ್ರವಾಸಿಗಳೂ ಪರದೇಶಸ್ಥರೂ ಆಗಿರುವ ನೀವು ನಿಮ್ಮ ಆತ್ಮದ ಮೇಲೆ ಯುದ್ಧಮಾಡುವ ಶಾರೀರಿಕ ಅಭಿಲಾಷೆಗಳಿಗೆ ದೂರವಾಗಿರಬೇಕೆಂದು ನಿಮ್ಮನ್ನು ಎಚ್ಚರಿಸುತ್ತೇನೆ.",
			"12": "<sup>12</sup> ನಿಮ್ಮ ನಡವಳಿಕೆಯು ಅನ್ಯಜನರ ಮಧ್ಯದಲ್ಲಿ ಯೋಗ್ಯವಾಗಿರಲಿ; ಆಗ ಅವರು ಯಾವ ವಿಷಯದಲ್ಲಿ ನಿಮ್ಮನ್ನು ಅಕ್ರಮಗಾರರೆಂದು ನಿಂದಿಸುತ್ತಾರೋ ಆ ವಿಷಯದಲ್ಲಿಯೇ ನಿಮ್ಮ ಸತ್ಕ್ರಿಯೆಗಳನ್ನು ಕಣ್ಣಾರೆ ಕಂಡು ವಿಚಾರಣೆಯ ದಿನದಲ್ಲಿ ದೇವರನ್ನು ಕೊಂಡಾಡುವರು.",
			"13": "<sup>13</sup> ಮನುಷ್ಯರು ನೇಮಿಸಿರುವ ಯಾವ ಅಧಿಕಾರಕ್ಕಾದರೂ ಕರ್ತನ ನಿಮಿತ್ತ ಅಧೀನರಾಗಿರಿ.",
			"14": "<sup>14</sup> ಅರಸನು ಸರ್ವಾಧಿಕಾರಿ ಎಂತಲೂ ಬೇರೆ ಅಧಿಪತಿಗಳು ಕೆಟ್ಟನಡತೆಯುಳ್ಳವರನ್ನು ದಂಡಿಸುವದಕ್ಕೂ ಒಳ್ಳೇ ನಡತೆಯುಳ್ಳವರನ್ನು ಪ್ರೋತ್ಸಾಹಪಡಿಸುವದಕ್ಕೂ ಅರಸನಿಂದ ಕಳುಹಿಸಲ್ಪಟ್ಟವರೆಂತಲೂ ತಿಳಿದು ಅವರಿಗೆ ಅಧೀನರಾಗಿರಿ.",
			"15": "<sup>15</sup> ತಿಳಿಯದೆ ಮಾತಾಡುವ ಮೂಢಜನರ ಬಾಯನ್ನು ನೀವು ಒಳ್ಳೇ ನಡತೆಯಿಂದ ಕಟ್ಟಬೇಕೆಂಬದೇ ದೇವರ ಚಿತ್ತ.",
			"16": "<sup>16</sup> ಸ್ವತಂತ್ರರಂತೆ ನಡೆದುಕೊಳ್ಳಿರಿ; ಆದರೆ ಕೆಟ್ಟತನವನ್ನು ಮರೆಮಾಜುವದಕ್ಕೆ ನಿಮ್ಮ ಸ್ವಾತಂತ್ರ್ಯವನ್ನು ಉಪಯೋಗಿಸಬೇಡಿರಿ;",
			"17": "<sup>17</sup> ನೀವು ದೇವರ ದಾಸರಾಗಿದ್ದೀರಲ್ಲಾ. ಎಲ್ಲರನ್ನೂ ಸನ್ಮಾನಿಸಿರಿ. ಸಹೋದರರನ್ನು ಪ್ರೀತಿಸಿರಿ. ದೇವರಿಗೆ ಭಯಪಡಿರಿ. ಅರಸನನ್ನು ಸನ್ಮಾನಿಸಿರಿ.",
			"18": "<sup>18</sup> ಸೇವಕರೇ, ನಿಮ್ಮ ಯಜಮಾನರಿಗೆ ಪೂರ್ಣಭಯದಿಂದ ಅಧೀನರಾಗಿರಿ. ಒಳ್ಳೆಯವರೂ ಸಾತ್ವಿಕರೂ ಆಗಿರುವವರಿಗೆ ಮಾತ್ರವಲ್ಲದೆ ವಕ್ರಬುದ್ಧಿಯುಳ್ಳವರಿಗೂ ವಿಧೇಯರಾಗಿರಿ.",
			"19": "<sup>19</sup> ಒಬ್ಬನು ಅನ್ಯಾಯವಾಗಿ ಬಾಧೆಪಡುವವನಾಗಿದ್ದು ದೇವರು ನೋಡುತ್ತಾನೆಂದು ಅರಿತು ಆ ಕಷ್ಟವನ್ನು ಸಹಿಸಿಕೊಂಡರೆ ಅದು ಶ್ಲಾಘ್ಯವಾಗಿದೆ.",
			"20": "<sup>20</sup> ತಪ್ಪುಮಾಡಿ ಗುದ್ದು ತಿನ್ನುವದರಲ್ಲಿ ನೀವು ತಾಳ್ಮೆಯಿಂದಿದ್ದರೆ ಅದರಿಂದೇನು ಕೀರ್ತಿ? ಆದರೆ ಒಳ್ಳೇದನ್ನು ಮಾಡಿ ಬಾಧೆಪಡುವದರಲ್ಲಿ ನೀವು ತಾಳ್ಮೆಯಿಂದಿದ್ದರೆ ಅದು ದೇವರ ಮುಂದೆ ಶ್ಲಾಘ್ಯವಾಗಿದೆ.",
			"21": "<sup>21</sup> ಇದಕ್ಕಾಗಿಯೇ ನೀವು ಕರೆಯಲ್ಪಟ್ಟಿರಿ; ಕ್ರಿಸ್ತನು ಸಹ ನಿಮಗೋಸ್ಕರ ಬಾಧೆಯನ್ನನುಭವಿಸಿ ನೀವು ತನ್ನ ಹೆಜ್ಜೆಯ ಜಾಡಿನಲ್ಲಿ ನಡೆಯಬೇಕೆಂದು ಮಾದರಿಯನ್ನು ತೋರಿಸಿಹೋದನು.",
			"22": "<sup>22</sup> ಆತನು ಯಾವ ಪಾಪವನ್ನೂ ಮಾಡಲಿಲ್ಲ, ಆತನ ಬಾಯಲ್ಲಿ ಯಾವ ವಂಚನೆಯೂ ಸಿಕ್ಕಲಿಲ್ಲ; ಬೈಯುವವರನ್ನು ಆತನು ಪ್ರತಿಯಾಗಿ ಬೈಯಲಿಲ್ಲ;",
			"23": "<sup>23</sup> ಆತನು ಬಾಧೆಯನ್ನನುಭವಿಸಿದಾಗ ಯಾರನ್ನೂ ಬೆದರಿಸದೆ ನ್ಯಾಯವಾಗಿ ತೀರ್ಪುಮಾಡುವಾತನಿಗೆ ತನ್ನ ಕಾರ್ಯವನ್ನು ಒಪ್ಪಿಸಿದನು.",
			"24": "<sup>24</sup> ನಾವು ಪಾಪದ ಪಾಲಿಗೆ ಸತ್ತು ನೀತಿವಂತರಾಗಿ ಜೀವಿಸುವಂತೆ ಆತನು ನಮ್ಮ ಪಾಪಗಳನ್ನು ತಾನೇ ತನ್ನ ದೇಹದಲ್ಲಿ ಹೊತ್ತುಕೊಂಡು ಮರಣದ ಕಂಬವನ್ನು ಏರಿದನು; ಆತನ ಬಾಸುಂಡೆಗಳಿಂದ ನಿಮಗೆ ಗುಣವಾಯಿತು.",
			"25": "<sup>25</sup> ನೀವು ಕುರಿಗಳಂತೆ ದಾರಿತಪ್ಪಿ ತೊಳಲುವವರಾಗಿದ್ದಿರಿ, ಆದರೆ ಈಗ ನೀವು ತಿರುಗಿಕೊಂಡು ನಿಮ್ಮ ಆತ್ಮಗಳನ್ನು ಕಾಯುವ ಕುರುಬನೂ ಅಧ್ಯಕ್ಷನೂ ಆಗಿರುವಾತನ ಬಳಿಗೆ ಬಂದಿದ್ದೀರಿ."
		},
		"3": {
			"1": "<sup>1</sup> ಅದೇ ರೀತಿಯಾಗಿ ಸ್ತ್ರೀಯರೇ, ನಿಮ್ಮ ಗಂಡಂದಿರಿಗೆ ಅಧೀನರಾಗಿರಿ.",
			"2": "<sup>2</sup> ಅವರಲ್ಲಿ ಕೆಲವರು ದೇವರ ವಾಕ್ಯಕ್ಕೆ ಅವಿಧೇಯರಾಗಿದ್ದರೂ",
			"3": "<sup>3</sup> ನೀವು ನಿರ್ಮಲರಾಗಿಯೂ ಭಯಭರಿತರಾಗಿಯೂ ನಡೆದುಕೊಳ್ಳುವದನ್ನು ಅವರು ನೋಡಿ ವಾಕ್ಯೋಪದೇಶವಿಲ್ಲದೆ ತಮ್ಮ ಹೆಂಡತಿಯರಾದ ನಿಮ್ಮ ನಡತೆಯಿಂದಲೇ ಸನ್ಮಾರ್ಗಕ್ಕೆ ಬಂದಾರು.",
			"4": "<sup>4</sup> ಜಡೆಹೆಣೆದುಕೊಳ್ಳುವದು ಚಿನ್ನದ ಒಡವೆಗಳನ್ನು ಇಟ್ಟುಕೊಳ್ಳುವದು ವಸ್ತ್ರಗಳನ್ನು ಧರಿಸಿಕೊಳ್ಳುವದು ಈ ಮೊದಲಾದ ಹೊರಗಣ ಶೃಂಗಾರವೇ ನಿಮ್ಮ ಅಲಂಕಾರವಾಗಿರಬಾರದು.",
			"5": "<sup>5</sup> ಸಾತ್ವಿಕವಾದ ಶಾಂತಮನಸ್ಸು ಎಂಬ ಒಳಗಣ ಭೂಷಣವೇ ನಿಮ್ಮ ಅಲಂಕಾರವಾಗಿರಲಿ. ಇದು ಶಾಶ್ವತವಾದದ್ದೂ ದೇವರ ದೃಷ್ಟಿಗೆ ಬಹು ಬೆಲೆಯುಳ್ಳದ್ದೂ ಆಗಿದೆ. ಪೂರ್ವಕಾಲದಲ್ಲಿ ದೇವರ ಮೇಲೆ ನಿರೀಕ್ಷೆಯಿಟ್ಟ ಭಕ್ತೆಯರಾದ ಸ್ತ್ರೀಯರು ಸಹ ಹೀಗೆಯೇ ತಮ್ಮನ್ನು ಅಲಂಕರಿಸಿಕೊಂಡರು. ಅವರು ತಮ್ಮ ತಮ್ಮ ಗಂಡಂದಿರಿಗೆ ಅಧೀನರಾಗಿದ್ದು ಯಾವ ಭೀತಿಗೂ ಗಾಬರಿಪಡದೆ ಒಳ್ಳೇದನ್ನೇ ಮಾಡುವವರಾಗಿದ್ದರು.",
			"6": "<sup>6</sup> ಸಾರಳು ಹಾಗೆಯೇ ಅಬ್ರಹಾಮನಿಗೆ ವಿಧೇಯಳಾಗಿದ್ದು ಅವನನ್ನು ಯಜಮಾನ ಎಂದು ಕರೆದಳು ಎಂದು ಬರೆದದೆ. ನೀವು ಸಾರಳ ಕುಮಾರ್ತೆಗಳಾಗಿದ್ದೀರಲ್ಲಾ.",
			"7": "<sup>7</sup> ಅದೇ ರೀತಿಯಾಗಿ ಪುರುಷರೇ, ಸ್ತ್ರೀಯು ಪುರುಷನಿಗಿಂತ ಬಲಹೀನಳೆಂಬದನ್ನು ಜ್ಞಾಪಕಮಾಡಿಕೊಂಡು ನಿಮ್ಮ ಹೆಂಡತಿಯರ ಸಂಗಡ ವಿವೇಕದಿಂದ ಒಗತನ ಮಾಡಿರಿ. ಅವರು ಜೀವವರಕ್ಕೆ ನಿಮ್ಮೊಂದಿಗೆ ಬಾಧ್ಯರಾಗಿದ್ದಾರೆಂದು ತಿಳಿದು ಅವರಿಗೆ ಮಾನವನ್ನು ಸಲ್ಲಿಸಿರಿ. ಹೀಗೆ ನಡೆದರೆ ನಿಮ್ಮ ಪ್ರಾರ್ಥನೆಗಳಿಗೆ ಅಡ್ಡಿಯಿರುವದಿಲ್ಲ.",
			"8": "<sup>8</sup> ಕಡೆಗೆ ನೀವೆಲ್ಲರೂ ಏಕಮನಸ್ಸುಳ್ಳವರಾಗಿರಿ; ಪರರ ಸುಖದುಃಖಗಳಲ್ಲಿ ಸೇರುವವರಾಗಿರಿ; ಅಣ್ಣತಮ್ಮಂದಿರಂತೆ ಒಬ್ಬರನ್ನೊಬ್ಬರು ಪ್ರೀತಿಸಿರಿ; ಕರುಣೆಯೂ ದೀನಭಾವವೂ ಉಳ್ಳವರಾಗಿರಿ.",
			"9": "<sup>9</sup> ಅಪಕಾರಕ್ಕೆ ಅಪಕಾರವನ್ನು ನಿಂದೆಗೆ ನಿಂದೆಯನ್ನು ಮಾಡದೆ ಆಶೀರ್ವದಿಸಿರಿ. ಇದಕ್ಕಾಗಿ ದೇವರು ನಿಮ್ಮನ್ನು ಕರೆದನಲ್ಲಾ; ಹೀಗೆ ಮಾಡುವದಾದರೆ ನೀವು ಆಶೀರ್ವಾದವನ್ನು ಬಾಧ್ಯವಾಗಿ ಹೊಂದುವಿರಿ.",
			"10": "<sup>10</sup> ಜೀವದಲ್ಲಿ ಸಂತೋಷಪಟ್ಟು ಸುದಿನಗಳನ್ನು ನೋಡುವದಕ್ಕೆ ಇಷ್ಟವುಳ್ಳವನು ಕೆಟ್ಟದ್ದನ್ನು ನುಡಿಯದಂತೆ ತನ್ನ ನಾಲಿಗೆಯನ್ನೂ ವಂಚನೆಯ ಮಾತುಗಳನ್ನಾಡದಂತೆ ತುಟಿಗಳನ್ನೂ ಬಿಗಿಹಿಡಿಯಲಿ.",
			"11": "<sup>11</sup> ಅವನು ಕೆಟ್ಟದ್ದನ್ನು ಬಿಟ್ಟು ಒಳ್ಳೇದನ್ನು ಮಾಡಲಿ; ಸಮಾಧಾನವನ್ನು ಹಾರೈಸಿ ಅದಕ್ಕೋಸ್ಕರ ಪ್ರಯತ್ನಪಡಲಿ.",
			"12": "<sup>12</sup> ಯಾಕಂದರೆ ಕರ್ತನು ನೀತಿವಂತರನ್ನು ಕಟಾಕ್ಷಿಸುತ್ತಾನೆ, ಆತನು ಅವರ ವಿಜ್ಞಾಪನೆಗಳಿಗೆ ಕಿವಿಗೊಡುತ್ತಾನೆ. ಕೆಡುಕರಿಗೋ ಕರ್ತನು ಕೋಪದ ಮುಖವುಳ್ಳವನಾಗಿರುತ್ತಾನೆ ಎಂದು ಬರೆದದೆ.",
			"13": "<sup>13</sup> ನೀವು ಒಳ್ಳೇದನ್ನೇ ಮಾಡುವದರಲ್ಲಿ ಆಸಕ್ತರಾಗಿದ್ದರೆ ನಿಮಗೆ ಕೇಡು ಮಾಡುವವರು ಯಾರಿದ್ದಾರೆ?",
			"14": "<sup>14</sup> ಒಂದು ವೇಳೆ ನೀತಿಯ ನಿಮಿತ್ತವೇ ಬಾಧೆಪಟ್ಟರೆ ನೀವು ಧನ್ಯರೇ.",
			"15": "<sup>15</sup> ಅವರ ಬೆದರಿಸುವಿಕೆಗೆ ಹೆದರದೆ ಕಳವಳಪಡದೆ ಕ್ರಿಸ್ತನನ್ನು ಕರ್ತನೆಂದು ನಿಮ್ಮ ಹೃದಯಗಳಲ್ಲಿ ಪ್ರತಿಷ್ಠೆಪಡಿಸಿರಿ. ನಿಮ್ಮಲ್ಲಿರುವ ನಿರೀಕ್ಷೆಗೆ ಆಧಾರವೇನೆಂದು ಕೇಳುವವರೆಲ್ಲರಿಗೆ ಉತ್ತರ ಹೇಳುವದಕ್ಕೆ ಯಾವಾಗಲೂ ಸಿದ್ಧವಾಗಿರಿ; ಆದರೆ ಅದನ್ನು ಸಾತ್ವಿಕತ್ವದಿಂದಲೂ ಮನೋಭೀತಿಯಿಂದಲೂ ಹೇಳಿರಿ.",
			"16": "<sup>16</sup> ಒಳ್ಳೇ ಮನಸ್ಸಾಕ್ಷಿಯುಳ್ಳವರಾಗಿರಿ; ಆಗ ಕ್ರಿಸ್ತನಲ್ಲಿರುವ ನಿಮ್ಮ ಒಳ್ಳೇ ನಡವಳಿಕೆಯನ್ನು ಕುರಿತು ಬೈಯುವವರು ನಿಮ್ಮನ್ನು ನಿಂದಿಸುವದಕ್ಕೆ ನಾಚಿಕೆಪಡುವರು.",
			"17": "<sup>17</sup> ಕೆಟ್ಟ ನಡತೆಯುಳ್ಳವರಾಗಿ ಬಾಧೆಪಡುವದಕ್ಕಿಂತ ಒಳ್ಳೇ ನಡತೆಯುಳ್ಳವರಾಗಿಯೇ ದೇವರ ಚಿತ್ತವಿದ್ದರೆ ಬಾಧೆಪಡುವದು ಲೇಸು.",
			"18": "<sup>18</sup> ಕ್ರಿಸ್ತನು ಸಹ ನೀತಿವಂತನಾಗಿದ್ದು ಅನೀತಿವಂತರಿಗೋಸ್ಕರ ಪ್ರಾಣಕೊಟ್ಟು ನಮ್ಮನ್ನು ದೇವರ ಬಳಿಗೆ ಸೇರಿಸುವದಕ್ಕಾಗಿ ಒಂದೇ ಸಾರಿ ಪಾಪನಿವಾರಣಕ್ಕೋಸ್ಕರ ಬಾಧೆಪಟ್ಟು ಸತ್ತನು. ಆತನು ಶರೀರಸಂಬಂಧದಲ್ಲಿ ಕೊಲ್ಲಲ್ಪಟ್ಟನು, ಆತ್ಮಸಂಬಂಧದಲ್ಲಿ ತಿರಿಗಿ ಬದುಕುವವನಾದನು.",
			"19": "<sup>19</sup> ಇದಲ್ಲದೆ ಆತನು ಆತ್ಮರೂಪನಾಗಿ ಸೆರೆಯಲ್ಲಿದ್ದ ಆತ್ಮಗಳ ಬಳಿಗೆ,",
			"20": "<sup>20</sup> ಅಂದರೆ ಪೂರ್ವಕಾಲದಲ್ಲಿ ನೋಹನು ನಾವೆಯನ್ನು ಕಟ್ಟಿಸುತ್ತಿರಲು ದೇವರು ದೀರ್ಘಶಾಂತಿಯಿಂದ ಕಾದಿದ್ದಾಗ ಆತನಿಗೆ ಅವಿಧೇಯರಾಗಿದ್ದವರ ಬಳಿಗೆ ಹೋಗಿ ಸುವಾರ್ತೆಯನ್ನು ಸಾರಿದನು. ಆ ನಾವೆಯೊಳಗೆ ಕೆಲವರು ಅಂದರೆ ಎಂಟೇ ಜನರು ಸೇರಿ ನೀರಿನ ಮೂಲಕ ರಕ್ಷಣೆಹೊಂದಿದರು.",
			"21": "<sup>21</sup> ಆ ನೀರಿಗೆ ಅನುರೂಪವಾದ ದೀಕ್ಷಾಸ್ನಾನವು ಯೇಸು ಕ್ರಿಸ್ತನ ಪುನರುತ್ಥಾನದ ಮೂಲಕ ಈಗ ನಮ್ಮನ್ನು ರಕ್ಷಿಸುತ್ತದೆ; ಅದು ಮೈಕೊಳೆಯನ್ನು ಹೋಗಲಾಡಿಸುವಂಥದಲ್ಲ, ಒಳ್ಳೇ ಮನಸ್ಸಾಕ್ಷಿ ಬೇಕೆಂದು ದೇವರಿಗೆ ವಿಜ್ಞಾಪಿಸಿಕೊಳ್ಳುವಂಥದೇ.",
			"22": "<sup>22</sup> ಆತನು ಪರಲೋಕಕ್ಕೆ ಹೋಗಿ ದೇವರ ಬಲಗಡೆಯಲ್ಲಿದ್ದಾನೆ; ದೇವದೂತರೂ ಅಧಿಕಾರಿಗಳೂ ಮಹತ್ವಗಳೂ ಆತನ ಸ್ವಾಧೀನವಾಗಿವೆ."
		},
		"4": {
			"1": "<sup>1</sup> ಕ್ರಿಸ್ತನು ಶರೀರದಲ್ಲಿ ಬಾಧೆಪಟ್ಟದ್ದರಿಂದ ನೀವು ಸಹ ಆತನಿಗಿದ್ದ ಭಾವವನ್ನೇ ಹಿಡಿದುಕೊಳ್ಳಿರಿ.",
			"2": "<sup>2</sup> ಯಾಕಂದರೆ ಶರೀರದಲ್ಲಿ ಬಾಧೆಪಟ್ಟವನು ಪಾಪದ ವಶದಿಂದ ತಪ್ಪಿಸಿಕೊಂಡವನಾಗಿ ಉಳಿದಿರುವ ತನ್ನ ಜೀವಮಾನಕಾಲದಲ್ಲಿ ಇನ್ನೂ ಮನುಷ್ಯರ ಅಭಿಲಾಷೆಗಳ ಪ್ರಕಾರ ಬದುಕದೆ ದೇವರ ಚಿತ್ತದ ಪ್ರಕಾರ ಬದುಕುವದಕ್ಕೆ ಪ್ರಯತ್ನಮಾಡುವನು.",
			"3": "<sup>3</sup> ನೀವು ಬಂಡುತನ ದುರಾಶೆ ಕುಡಿಕತನ ದುಂದೌತಣ ಮದ್ಯಪಾನಗೋಷ್ಠಿ ಅಸಹ್ಯವಾದ ವಿಗ್ರಹಾರಾಧನೆ ಈ ಮೊದಲಾದವುಗಳನ್ನು ನಡಿಸುವದರಲ್ಲಿಯೂ ಅನ್ಯಜನರಿಗೆ ಇಷ್ಟವಾದ ದುಷ್ಕೃತ್ಯಗಳನ್ನು ಮಾಡುವದರಲ್ಲಿಯೂ ಕಳೆದುಹೋದ ಕಾಲವೇ ಸಾಕು.",
			"4": "<sup>4</sup> ತಾವು ಮಾಡುವ ಅಪರಿಮಿತವಾದ ಪಟಿಂಗತನದಲ್ಲಿ ನೀವು ಸೇರದೆ ಇದ್ದದ್ದಕ್ಕೆ ಅವರು ಆಶ್ಚರ್ಯಪಟ್ಟು ನಿಮ್ಮನ್ನು ದೂಷಿಸುತ್ತಾರೆ.",
			"5": "<sup>5</sup> ಅವರು ಬದುಕುವವರಿಗೂ ಸತ್ತವರಿಗೂ ನ್ಯಾಯತೀರಿಸುವದಕ್ಕೆ ಸಿದ್ಧವಾಗಿರುವಾತನಿಗೆ ಉತ್ತರಕೊಡಬೇಕು.",
			"6": "<sup>6</sup> ಸತ್ತಿರುವವರು ಮನುಷ್ಯ ಜಾತಿಗೆ ಬರುವ ಮರಣವೆಂಬ ತೀರ್ಪನ್ನು ಶರೀರಿಗಳಾಗಿದ್ದಾಗ ಹೊಂದಿದರು; ಅವರು ಆತ್ಮ ಸಂಬಂಧವಾಗಿ ದೇವರ ಜೀವದಲ್ಲಿ ಪಾಲುಗಾರರಾಗುವಂತೆ ಅವರಿಗೂ ಸುವಾರ್ತೆಯು ತಿಳಿಸಲ್ಪಟ್ಟತು.",
			"7": "<sup>7</sup> ಎಲ್ಲವುಗಳ ಅಂತ್ಯವು ಹತ್ತಿರವಾಗಿದೆ; ಆದದರಿಂದ ನೀವು ಜಿತೇಂದ್ರಿಯರಾಗಿಯೂ ಪ್ರಾರ್ಥನೆಗೆ ಸಿದ್ಧವಾಗಿರುವಂತೆ ಸ್ವಸ್ಥಚಿತ್ತರಾಗಿಯೂ ಇರ್ರಿ.",
			"8": "<sup>8</sup> ಮೊಟ್ಟ ಮೊದಲು ನಿಮ್ಮನಿಮ್ಮೊಳಗೆ ಯಥಾರ್ಥವಾದ ಪ್ರೀತಿಯಿರಲಿ; ಪ್ರೀತಿಯು ಬಹು ಪಾಪಗಳನ್ನು ಮುಚ್ಚುತ್ತದೆ.",
			"9": "<sup>9</sup> ಗುಣುಗುಟ್ಟದೆ ಒಬ್ಬರಿಗೊಬ್ಬರು ಅತಿಥಿಸತ್ಕಾರ ಮಾಡಿರಿ.",
			"10": "<sup>10</sup> ನೀವೆಲ್ಲರು ದೇವರ ವಿವಿಧ ಕೃಪೆಯ ವಿಷಯದಲ್ಲಿ ಒಳ್ಳೇ ಮನೆವಾರ್ತೆಯವರಾಗಿದ್ದು ಪ್ರತಿಯೊಬ್ಬನು ತಾನು ಹೊಂದಿದ ಕೃಪಾವರವನ್ನು ಎಲ್ಲರ ಸೇವೆಯಲ್ಲಿ ಉಪಯೋಗಿಸಲಿ.",
			"11": "<sup>11</sup> ಒಬ್ಬನು ಬೋಧಿಸುವವನಾದರೆ ದೈವೋಕ್ತಿಗಳನ್ನು ನುಡಿಯುವವನಾಗಿ ಬೋಧಿಸಲಿ. ಒಬ್ಬನು ಸಭೆಯ ಸೇವೆ ಮಾಡುವವನಾದರೆ ದೇವರಿಂದ ಶಕ್ತಿಯನ್ನು ಹೊಂದಿದವನಾಗಿ ಮಾಡಲಿ. ಇದರಿಂದ ಎಲ್ಲಾದರಲ್ಲಿ ಯೇಸು ಕ್ರಿಸ್ತನ ಮೂಲಕ ದೇವರಿಗೆ ಸ್ತೋತ್ರ ಉಂಟಾಗುವದು. ಯೇಸು ಕ್ರಿಸ್ತನಿಗೆ ಆಧಿಪತ್ಯವೂ ಘನವೂ ಯುಗಯುಗಾಂತರಗಳಲ್ಲಿಯೂ ಇರುವವು. ಆಮೆನ್.",
			"12": "<sup>12</sup> ಪ್ರಿಯರೇ, ನೀವು ಪರಿಶೋಧನೆಗಾಗಿ ಪುಟಕ್ಕೆ ಹಾಕಲ್ಪಟ್ಟದ್ದಕ್ಕೆ ಆಶ್ಚರ್ಯಪಡಬೇಡಿರಿ; ವಿಪರೀತವಾದ ಸಂಗತಿ ಸಂಭವಿಸಿತೆಂದು ಯೋಚಿಸಬೇಡಿರಿ.",
			"13": "<sup>13</sup> ಆದರೆ ನೀವು ಎಷ್ಟರ ಮಟ್ಟಿಗೂ ಕ್ರಿಸ್ತನ ಬಾಧೆಗಳಲ್ಲಿ ಪಾಲುಗಾರರಾಗಿದ್ದೀರೋ ಅಷ್ಟರ ಮಟ್ಟಿಗೆ ಸಂತೋಷವುಳ್ಳವರಾಗಿರ್ರಿ; ಆತನ ಮಹಿಮೆಯ ಪ್ರತ್ಯಕ್ಷತೆಯಲ್ಲಿಯೂ ನೀವು ಸಂತೋಷಪಟ್ಟು ಉಲ್ಲಾಸಗೊಳ್ಳುವಿರಿ.",
			"14": "<sup>14</sup> ನೀವು ಕ್ರಿಸ್ತನ ಹೆಸರಿನ ನಿಮಿತ್ತ ನಿಂದೆಗೆ ಗುರಿಯಾದರೆ ಧನ್ಯರೇ; ತೇಜೋಮಯವಾದ ಆತ್ಮನಾಗಿರುವ ದೇವರಾತ್ಮನು ನಿಮ್ಮಲ್ಲಿ ನೆಲೆಗೊಂಡಿದ್ದಾನಲ್ಲಾ.",
			"15": "<sup>15</sup> ನಿಮ್ಮಲ್ಲಿ ಯಾವನಾದರೂ ಕೊಲೆಗಾರನು ಕಳ್ಳನು ದುಷ್ಟನು ಪರಕಾರ್ಯಗಳಲ್ಲಿ ತಲೆಹಾಕುವವನು ಆಗಿದ್ದು ಶಿಕ್ಷಾಪಾತ್ರನಾಗಬಾರದು.",
			"16": "<sup>16</sup> ಆದರೆ ಕ್ರೈಸ್ತನಾಗಿ ಬಾಧೆಪಟ್ಟರೆ ಅವನು ನಾಚಿಕೆಪಡದೆ ಆ ಹೆಸರಿನಿಂದಲೇ ದೇವರನ್ನು ಘನಪಡಿಸಲಿ.",
			"17": "<sup>17</sup> ನ್ಯಾಯವಿಚಾರಣೆಯ ಸಮಯ ಬಂದದೆ; ಆ ವಿಚಾರಣೆಯು ದೇವರ ಮನೆಯಲ್ಲಿಯೇ ಪ್ರಾರಂಭವಾಯಿತಲ್ಲಾ. ಅದು ನಮ್ಮಲ್ಲಿ ಪ್ರಾರಂಭವಾಗಲು ದೇವರ ಸುವಾರ್ತೆಯನ್ನು ನಂಬಲೊಲ್ಲದವರ ಗತಿ ಏನಾಗಬಹುದು?",
			"18": "<sup>18</sup> ನೀತಿವಂತನೇ ರಕ್ಷಣೆಹೊಂದುವದು ಕಷ್ಟವಾದರೆ ಭಕ್ತಿಹೀನನೂ ಪಾಪಿಷ್ಠನೂ ನಿಲ್ಲುವದು ಹೇಗೆ?",
			"19": "<sup>19</sup> ಹೀಗಿರಲಾಗಿ ದೇವರ ಚಿತ್ತಾನುಸಾರ ಬಾಧೆಪಡುವವರು ಒಳ್ಳೇದನ್ನು ಮಾಡುವವರಾಗಿದ್ದು ತಮ್ಮ ಆತ್ಮಗಳನ್ನು ನಂಬಿಗಸ್ತನಾದ ಸೃಷ್ಟಿ ಕರ್ತನಿಗೆ ಒಪ್ಪಿಸಲಿ."
		},
		"5": {
			"1": "<sup>1</sup> ಸಭೆಯ ಹಿರಿಯರೇ, ಜೊತೆಹಿರಿಯವನಾದ ನಾನು ಕ್ರಿಸ್ತನ ಬಾಧೆಗಳನ್ನು ಕಣ್ಣಾರೆ ಕಂಡವನೂ ಇನ್ನು ಮುಂದೆ ಪ್ರತ್ಯಕ್ಷವಾಗುವ ಪ್ರಭಾವದಲ್ಲಿ ಪಾಲುಗಾರನೂ ಆಗಿದ್ದು ನಿಮ್ಮನ್ನು ಎಚ್ಚರಿಸಿ ಹೇಳುವದೇನಂದರೆ -",
			"2": "<sup>2</sup> ನಿಮ್ಮಲ್ಲಿರುವ ದೇವರ ಮಂದೆಯನ್ನು ಕಾಯಿರಿ. ಬಲಾತ್ಕಾರದಿಂದಲ್ಲ ದೇವರ ಚಿತ್ತದ ಪ್ರಕಾರ ಇಷ್ಟಪೂರ್ವಕವಾಗಿಯೂ ನೀಚವಾದ ದ್ರವ್ಯಾಶೆಯಿಂದಲ್ಲ ಸಿದ್ಧಮನಸ್ಸಿನಿಂದಲೂ ಮೇಲ್ವಿಚಾರಣೆಮಾಡಿರಿ.",
			"3": "<sup>3</sup> ದೇವರು ನಿಮ್ಮ ವಶದಲ್ಲಿಟ್ಟಿರುವ ಸಭೆಗಳ ಮೇಲೆ ದೊರೆತನ ಮಾಡುವವರಂತೆ ನಡೆಯದೆ ಮಂದೆಗೆ ಮಾದರಿಯಾಗಿಯೇ ನಡೆದುಕೊಳ್ಳಿರಿ.",
			"4": "<sup>4</sup> ಹಿರೀ ಕುರುಬನು ಪ್ರತ್ಯಕ್ಷನಾಗುವಾಗ ನೀವು ದೇವಪ್ರಭಾವವೆಂಬ ಎಂದಿಗೂ ಬಾಡದ ಜಯಮಾಲೆಯನ್ನು ಹೊಂದುವಿರಿ.",
			"5": "<sup>5</sup> ಯೌವನಸ್ಥರೇ, ಅದೇ ರೀತಿಯಾಗಿ ಹಿರಿಯರಿಗೆ ಅಧೀನರಾಗಿರಿ. ನೀವೆಲ್ಲರೂ ದೀನಮನಸ್ಸೆಂಬ ವಸ್ತ್ರದಿಂದ ಸೊಂಟಾಕಟ್ಟಿಕೊಂಡು ಒಬ್ಬರಿಗೊಬ್ಬರು ಸೇವೆಮಾಡಿರಿ. ದೇವರು ಅಹಂಕಾರಿಗಳನ್ನು ಎದುರಿಸುತ್ತಾನೆ, ದೀನರಿಗಾದರೋ ಕೃಪೆಯನ್ನು ಅನುಗ್ರಹಿಸುತ್ತಾನೆ.",
			"6": "<sup>6</sup> ಹೀಗಿರುವದರಿಂದ ದೇವರ ತ್ರಾಣವುಳ್ಳ ಹಸ್ತದ ಕೆಳಗೆ ನಿಮ್ಮನ್ನು ತಗ್ಗಿಸಿಕೊಳ್ಳಿರಿ; ಆತನು ತಕ್ಕ ಕಾಲದಲ್ಲಿ ನಿಮ್ಮನ್ನು ಮೇಲಕ್ಕೆ ತರುವನು.",
			"7": "<sup>7</sup> ನಿಮ್ಮ ಚಿಂತೆಯನ್ನೆಲ್ಲಾ ಆತನ ಮೇಲೆ ಹಾಕಿರಿ, ಆತನು ನಿಮಗೋಸ್ಕರ ಚಿಂತಿಸುತ್ತಾನೆ.",
			"8": "<sup>8</sup> ಸ್ವಸ್ಥಚಿತ್ತರಾಗಿರಿ, ಎಚ್ಚರವಾಗಿರಿ; ನಿಮ್ಮ ವಿರೋಧಿಯಾಗಿರುವ ಸೈತಾನನು ಗರ್ಜಿಸುವ ಸಿಂಹದೋಪಾದಿಯಲ್ಲಿ ಯಾರನ್ನು ನುಂಗಲಿ ಎಂದು ಹುಡುಕುತ್ತಾ ತಿರುಗುತ್ತಾನೆ.",
			"9": "<sup>9</sup> ನೀವು ನಂಬಿಕೆಯಲ್ಲಿ ದೃಢವಾಗಿದ್ದು ಅವನನ್ನು ಎದುರಿಸಿರಿ; ಲೋಕದಲ್ಲಿರುವ ನಿಮ್ಮ ಸಹೋದರರೂ ಅಂಥ ಬಾಧೆಗಳನ್ನೇ ಅನುಭವಿಸುತ್ತಿದ್ದಾರೆಂದು ನಿಮಗೆ ತಿಳಿದದೆಯಲ್ಲಾ.",
			"10": "<sup>10</sup> ಕ್ರಿಸ್ತನಲ್ಲಿ ನಿಮ್ಮನ್ನು ತನ್ನ ನಿತ್ಯಪ್ರಭಾವಕ್ಕೆ ಕರೆದ ಕೃಪಾಪೂರ್ಣನಾದ ದೇವರು ತಾನೇ ನೀವು ಸ್ವಲ್ಪಕಾಲ ಬಾಧೆಪಟ್ಟ ಮೇಲೆ ನಿಮ್ಮನ್ನು ಯೋಗ್ಯಸ್ಥಿತಿಗೆ ತಂದು ನೆಲೆಗೊಳಿಸಿ ಬಲಪಡಿಸುವನು.",
			"11": "<sup>11</sup> ಆತನಿಗೆ ಆಧಿಪತ್ಯವು ಯುಗಯುಗಾಂತರಗಳಲ್ಲಿಯೂ ಇರಲಿ. ಆಮೆನ್.",
			"12": "<sup>12</sup> ನಿಮ್ಮನ್ನು ಎಚ್ಚರಿಸುವದಕ್ಕೂ ಇದೇ ದೇವರ ನಿಜವಾದ ಕೃಪೆ ಎಂದು ಸಾಕ್ಷಿ ಹೇಳುವದಕ್ಕೂ ನಾನು ಸಂಕ್ಷೇಪವಾಗಿ ಬರೆದು ನಂಬಿಗಸ್ತನಾದ ಸಹೋದರನೆಂದು ನಾನು ಎಣಿಸುವ ಸಿಲ್ವಾನನ ಕೈಯಲ್ಲಿ ಇದನ್ನು ಕಳುಹಿಸಿದ್ದೇನೆ. ಈ ನಿಜವಾದ ಕೃಪೆಯಲ್ಲಿ ನಿಲ್ಲಿರಿ.",
			"13": "<sup>13</sup> ದೇವರು ನಿಮ್ಮೊಂದಿಗೆ ಆರಿಸಿಕೊಂಡಂಥ ಬಾಬೆಲಿನಲ್ಲಿರುವ ಸಭೆಯು ನಿಮಗೆ ವಂದನೆ ಹೇಳುತ್ತದೆ.",
			"14": "<sup>14</sup> ನನ್ನ ಮಗನಾದ ಮಾರ್ಕನು ಸಹ ವಂದನೆ ಹೇಳುತ್ತಾನೆ. ಪ್ರೀತಿಯಿಂದ ಮುದ್ದಿಟ್ಟು ಒಬ್ಬರನ್ನೊಬ್ಬರು ವಂದಿಸಿರಿ. ಕ್ರಿಸ್ತನಲ್ಲಿರುವ ನಿಮ್ಮೆಲ್ಲರಿಗೆ ಶಾಂತಿಯಿರಲಿ."
		}
	}
};
module.exports = book;