var book = {
	"name": "ಧರ್ಮೋಪದೇಶಕಾಂಡ",
	"numChapters": 34,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ಜೋರ್ಡನ್ ನದಿಯ ಆಚೆ, ಪಾರಾನಿಗೂ, ತೋಫೆಲ್, ಲಾಬಾನ್, ಹಚೇರೋತ್, ದೀಜಾಹಾಬ್ ಎಂಬ ಊರುಗಳಿಗೂ ನಡುವೆ, ಮರುಭೂಮಿಯಲ್ಲಿ ಅರಾಬಾ ಎಂಬ ತಗ್ಗಾದ ಪ್ರದೇಶದಲ್ಲಿ ಸೂಫಿಗೆ ಎದುರಾಗಿ ಮೋಶೆ ಇಸ್ರಯೇಲರಿಗೆ ಮಾಡಿದ ಉಪನ್ಯಾಸಗಳು ಇವು.",
			"2": "<sup>2</sup> (ಹೋರೇಬಿನಿಂದ ಸೇಯಿರ್ ಬೆಟ್ಟಗಳ ಮಾರ್ಗವಾಗಿ ಕಾದೇಶ್ ಬರ್ನೇಯದವರೆಗೆ ಹನ್ನೊಂದು ದಿನದ ಪ್ರಯಾಣ.)",
			"3": "<sup>3</sup> ಮೋಶೆ ಹೆಷ್ಬೋನಿನಲ್ಲಿದ್ದ ಅಮೋರಿಯರ ಅರಸ ಸೀಹೋನನನ್ನು ಮತ್ತು ಅಷ್ಟಾರೋತ್, ಎದ್ರೈ ಎಂಬ ಪಟ್ಟಣಗಳಲ್ಲಿದ್ದ ಬಾಷಾನಿನ ಅರಸ ಓಗನನ್ನು ಜಯಿಸಿದನು. ತರುವಾಯ,",
			"4": "<sup>4</sup> ಅಂದರೆ ಇಸ್ರಯೇಲರು ಈಜಿಪ್ಟ್ ದೇಶವನ್ನು ಬಿಟ್ಟ ನಲವತ್ತನೆಯ ವರ್ಷದ ಹನ್ನೊಂದನೆಯ ತಿಂಗಳಿನ ಮೊದಲನೆಯ ದಿನ ಸರ್ವೇಶ್ವರ ಸ್ವಾಮಿ ತನಗೆ ಆಜ್ಞಾಪಿಸಿದ್ದನ್ನೆಲ್ಲ ಇಸ್ರಯೇಲರಿಗೆ ತಿಳಿಸಿದನು.",
			"5": "<sup>5</sup> ಜೋರ್ಡನ್ ನದಿಯ ಆಚೆ ಮೋವಾಬ್ಯರ ದೇಶದಲ್ಲಿ ಮೋಶೆ ಈ ಮುಂದಣ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ವಿವರಿಸುವುದಕ್ಕೆ ಪ್ರಾರಂಭಿಸಿದನು:",
			"6": "<sup>6</sup> “ಹೋರೇಬಿನಲ್ಲಿ ನಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಮಗೆ, ‘ನೀವು ಈ ಬೆಟ್ಟದ ಬಳಿ ವಾಸಿಸಿದ್ದು ಸಾಕು;",
			"7": "<sup>7</sup> ಈಗ ಹಿಂದಿರುಗಿ ಅಮೋರಿಯರು ಇರುವ ಮಲೆನಾಡಿಗೂ ಅದರ ಸುತ್ತಮುತ್ತಲಿನ ಪ್ರದೇಶಕ್ಕೂ ಪ್ರಯಾಣಮಾಡಿರಿ. ಅಲ್ಲಿನ ಕಣಿವೆ, ತಪ್ಪಲು, ಇಳಕಲು ಪ್ರದೇಶಗಳಿಗೂ ದಕ್ಷಿಣಸೀಮೆ, ಸಮುದ್ರತೀರ ಎಂಬ ನಾಡುಗಳಿಗೂ ಕಾನಾನ್ಯರ ನಾಡು, ಲೆಬನೋನ್ ಪರ್ವತ ಮತ್ತು ಯೂಫ್ರೆಟಿಸ್ ಮಹಾನದಿಯವರೆಗೆ ವಿಸ್ತರಿಸಿಕೊಂಡಿರುವ ಪ್ರದೇಶಗಳಿಗೂ ತೆರಳಿರಿ.",
			"8": "<sup>8</sup> ಆ ನಾಡನ್ನು ನಿಮಗೆ ಕೊಟ್ಟಿದ್ದೇನೆ. ಸರ್ವೇಶ್ವರನೆಂಬ ನಾನು ನಿಮ್ಮ ಪಿತೃಗಳಾದ ಅಬ್ರಹಾಮ, ಇಸಾಕ, ಯಕೋಬರಿಗೂ ಅವರ ಸಂತತಿಯವರಿಗೂ ಆ ನಾಡನ್ನು ಪ್ರಮಾಣಪೂರ್ವಕವಾಗಿ ವಾಗ್ದಾನ ಮಾಡಿದ್ದೆನು. ಅದನ್ನು ಸೇರಿ ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳಿ,’ ಎಂದು ಆಜ್ಞಾಪಿಸಿದರು.",
			"9": "<sup>9</sup> “ಆ ಕಾಲದಲ್ಲಿ ನಾನು ನಿಮ್ಮನ್ನು ಸಂಬೋಧಿಸಿ, ‘ನಿಮ್ಮ ಭಾರವನ್ನು ನಾನೊಬ್ಬನೇ ಹೊರುವುದು ಅಸಾಧ್ಯ;",
			"10": "<sup>10</sup> ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮನ್ನು ಸಂಖ್ಯೆಯಲ್ಲಿ ಹೆಚ್ಚಿಸಿದ್ದಾರೆ; ನೀವು ಆಕಾಶದ ನಕ್ಷತ್ರಗಳಷ್ಟಾಗಿದ್ದೀರಿ;",
			"11": "<sup>11</sup> ನೀವು ಈಗ ಇರುವುದಕ್ಕಿಂತ ಇನ್ನೂ ಸಾವಿರದಷ್ಟಾಗುವಂತೆ ನಿಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮನ್ನು ಹೆಚ್ಚಿಸಲಿ; ಅವರೇ ವಾಗ್ದಾನಮಾಡಿದ ಪ್ರಕಾರ ನಿಮ್ಮನ್ನು ಆಶೀರ್ವದಿಸಲಿ;",
			"12": "<sup>12</sup> ಆದರೆ ನಿಮ್ಮ ಸಮಸ್ಯೆಗಳನ್ನೂ ಹೊಣೆಯನ್ನೂ ವ್ಯಾಜ್ಯಗಳನ್ನೂ ನಾನೊಬ್ಬನೇ ಸಹಿಸುವುದು ಹೇಗೆ?",
			"13": "<sup>13</sup> ಆದುದರಿಂದ ನೀವು ಪ್ರತಿಯೊಂದು ಕುಲದಿಂದ ಜ್ಞಾನಿಗಳೂ, ವಿವೇಕಿಗಳೂ, ಪ್ರಖ್ಯಾತರೂ ಆದ ವ್ಯಕ್ತಿಗಳನ್ನು ಆರಿಸಿಕೊಳ್ಳಿ; ನಾನು ಅವರನ್ನು ನಿಮಗೆ ಅಧಿಪತಿಗಳನ್ನಾಗಿ ನೇಮಿಸುತ್ತೇನೆ,’ ಎಂದು ಹೇಳಿದೆ.",
			"14": "<sup>14</sup> ಅದಕ್ಕೆ ನೀವು, ‘ನಿಮ್ಮ ಆಲೋಚನೆಯಂತೆ ನಡೆಯುವುದು ವಿಹಿತವಾದದ್ದೇ’ ಎಂದು ಉತ್ತರಕೊಟ್ಟಿರಿ.",
			"15": "<sup>15</sup> ಆಗ ನಾನು ನಿಮ್ಮಲ್ಲಿ ಪ್ರಸಿದ್ಧರಾದ ಬುದ್ಧಿವಂತರನ್ನು ಕರೆಯಿಸಿ ಒಂದೊಂದು ಕುಲದಲ್ಲಿ ಸಾವಿರ ಮಂದಿಯ ಮೇಲೆ, ನೂರು ಮಂದಿಯ ಮೇಲೆ, ಐವತ್ತು ಮಂದಿಯ ಮೇಲೆ ಹಾಗು ಹತ್ತು ಮಂದಿಯ ಮೇಲೆ ಅಧಿಕಾರಿಗಳನ್ನಾಗಿ ಹಾಗು ಉಪಾಧಿಕಾರಿಗಳನ್ನಾಗಿ ನೇಮಿಸಿದೆ.",
			"16": "<sup>16</sup> ಆ ನ್ಯಾಯಾಧಿಪತಿಗಳಿಗೆ ನಾನು ಆಗ ಕೊಟ್ಟ ಸೂಚನೆ ಇದು: ‘ನೀವು ಸರ್ವಕುಲದವರ ವ್ಯಾಜ್ಯಗಳನ್ನು ವಿಚಾರಿಸಬೇಕು. ಅದು ಇಸ್ರಯೇಲರ ವ್ಯಾಜ್ಯವಾಗಿರಬಹುದು. ಅನ್ಯರೊಡನೆ ವ್ಯಾಜ್ಯವಾಗಿರಬಹುದು. ಎಲ್ಲವನ್ನು ನೀವು ನ್ಯಾಯದ ಪ್ರಕಾರವೇ ತೀರ್ಮಾನಿಸಬೇಕು.",
			"17": "<sup>17</sup> ನ್ಯಾಯ ವಿಚಾರಿಸುವಾಗ ಮುಖದಾಕ್ಷಿಣ್ಯ ಮಾಡಬಾರದು; ಅಧಿಕಾರಿಗೂ ಅಲ್ಪರಿಗೂ ಸಮನಾಗಿ ಕಿವಿಗೊಡಿ; ನೀವು ದೇವರ ಹೆಸರಿನಲ್ಲಿ ನ್ಯಾಯ ತೀರಿಸುವುದರಿಂದ ಯಾವ ಮನುಷ್ಯನಿಗೂ ಹೆದರಬೇಡಿ. ನೀವು ತೀರಿಸಲಿಕ್ಕಾಗದ ವ್ಯಾಜ್ಯಗಳನ್ನು ನನ್ನ ಬಳಿಗೆ ತನ್ನಿ; ನಾನೇ ಅವುಗಳನ್ನು ತೀರಿಸುತ್ತೇನೆ,’ ಎಂದು ಹೇಳಿದೆ.",
			"18": "<sup>18</sup> ಹೀಗೆ, ನೀವು ಮಾಡಬೇಕಾದ ಎಲ್ಲ ಕಾರ್ಯಗಳ ವಿಷಯದಲ್ಲೂ ಆಗಲೇ ನಿಮಗೆ ಆಜ್ಞೆಕೊಟ್ಟೆ.",
			"19": "<sup>19</sup> “ನಾವು ಹೋರೇಬಿನಿಂದ ಹೊರಟು ನಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಆಜ್ಞಾಪಿಸಿದಂತೆ ಅಮೋರಿಯರ ಮಲೆನಾಡಿನ ಮಾರ್ಗವನ್ನು ಹಿಡಿದು, ನೀವು ನೋಡಿದ ಆ ಘೋರ ಮಹಾ ಅರಣ್ಯದಲ್ಲಿ ನಡೆದು, ಕಾದೇಶ್ ಬರ್ನೇಯಕ್ಕೆ ಸೇರಿದೆವು.",
			"20": "<sup>20</sup> ಆಗ ನಾನು, ‘ನಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಮಗೆ ಕೊಡುವ ಅಮೋರಿಯರ ಮಲೆನಾಡಿನ ಹತ್ತಿರಕ್ಕೆ ಬಂದಿದ್ದೀರಿ;",
			"21": "<sup>21</sup> ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಆ ನಾಡನ್ನು ನಿಮಗೇ ಕೊಟ್ಟಿದ್ದಾರೆ; ನಿಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಹೇಳಿದಂತೆ ಅದನ್ನು ಏರಿ ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳಿ; ನೀವು ದಿಗಿಲುಪಡದೆ ಧೈರ್ಯವಾಗಿಯೇ ಇರಬೇಕು,’ ಎಂದು ಹೇಳಿದೆ.",
			"22": "<sup>22</sup> “ಆಗ ನೀವೆಲ್ಲರೂ ನನ್ನ ಬಳಿಗೆ ಬಂದು, ‘ನಮಗಿಂತ ಮುಂದಾಗಿ ಜನರನ್ನು ಕಳುಹಿಸುತ್ತೇವೆ; ಅವರು ನಮ್ಮ ಪರವಾಗಿ ಆ ನಾಡನ್ನು ಪರೀಕ್ಷಿಸಿನೋಡಿ, ನಾವು ಹತ್ತಿ ಹೋಗಬೇಕಾದ ದಾರಿಯ ಬಗ್ಗೆ ಹಾಗು ಸೇರಬೇಕಾದ ಊರುಗಳ ಬಗ್ಗೆ ನಮಗೆ ಸಮಾಚಾರ ತರಲಿ’, ಎಂದು ಹೇಳಿದಿರಿ.",
			"23": "<sup>23</sup> ಅದು ಒಳ್ಳೇ ಆಲೋಚನೆಯೆಂದು ನಾನು ತಿಳಿದು, ನಿಮ್ಮಲ್ಲಿ ಕುಲಕ್ಕೆ ಒಬ್ಬೊಬ್ಬನ ಮೇರೆಗೆ, ಹನ್ನೆರಡು ಮಂದಿಯನ್ನು ಆರಿಸಿಕೊಂಡೆ.",
			"24": "<sup>24</sup> ಅವರು ಹೋಗಿ ಆ ಮಲೆನಾಡನ್ನು ಹತ್ತಿ ಎಷ್ಕೋಲೆಂಬ ಕಣಿವೆಯ ಬಳಿಗೆ ಬಂದು, ಆ ಪ್ರದೇಶವನ್ನು ಸಂಚರಿಸಿ ನೋಡಿ,",
			"25": "<sup>25</sup> ಅಲ್ಲಿನ ಹಣ್ಣುಹಂಪಲುಗಳಲ್ಲಿ ಕೆಲವನ್ನು ತಂದು ತೋರಿಸಿದರು; ನಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಮಗೆ ಕೊಡುವ ಆ ನಾಡು ಒಳ್ಳೆಯ ನಾಡೆಂದು ನಮಗೆ ತಿಳಿಸಿದರು.",
			"26": "<sup>26</sup> ಆದರೂ ನೀವು ಅಲ್ಲಿಗೆ ಹತ್ತಿಹೋಗದೆ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಆಜ್ಞೆಯನ್ನು ಉಲ್ಲಂಘಿಸಿದಿರಿ;",
			"27": "<sup>27</sup> ನಿಮ್ಮ ನಿಮ್ಮ ಡೇರೆಗಳಲ್ಲಿ ಗೊಣಗುಟ್ಟಿದಿರಿ; ‘ಸರ್ವೇಶ್ವರ ನಮ್ಮನ್ನು ಈಜಿಪ್ಟ್ ದೇಶದಿಂದ ಬರಮಾಡಿದ್ದು ಹಗೆತನದಿಂದಲೇ; ಅಮೋರಿಯರ ಕೈಗೆ ಒಪ್ಪಿಸಿ ಸಂಹರಿಸಬೇಕೆಂದೇ.",
			"28": "<sup>28</sup> ನಾವು ಹೋಗಬೇಕಾದ ಆ ಸೀಮೆ ಎಂಥದೆಂದು ಆಲೋಚಿಸಿ ನೋಡಬೇಕು; ಅಲ್ಲಿಗೆ ಹೋಗಿದ್ದ ನಮ್ಮ ಸಹೋದರರು ಬಂದು ಆ ನಾಡಿನ ಜನರು ನಮಗಿಂತ ಬಲಿಷ್ಠರೂ ಎತ್ತರವಾಗಿಯೂ ಇದ್ದಾರೆ. ಅವರಿರುವ ಪಟ್ಟಣಗಳು ದೊಡ್ಡವು, ಆಕಾಶವನ್ನು ಮುಟ್ಟುವಂಥ ಕೋಟೆಕೊತ್ತಲುಳ್ಳವು; ಅಲ್ಲಿ ‘ಅನಾಕಿಮ್’ ವಂಶಸ್ಥರನ್ನು ಕಂಡೆವೆಂದು ಹೇಳಿ ನಮ್ಮ ಧೈರ್ಯವನ್ನು ಕೆಡಿಸಿದ್ದಾರೆ,’ ಎಂದುಕೊಳ್ಳುತ್ತಿದ್ದಿರಿ.",
			"29": "<sup>29</sup> “ಅದಕ್ಕೆ ನಾನು, ‘ಕಳವಳಪಡಬೇಡಿ, ಅವರಿಗೆ ಭಯಪಡಬೇಡಿ.",
			"30": "<sup>30</sup> ನಿಮ್ಮ ಮುಂದುಗಡೆಯಲ್ಲೇ ಹೋಗುವ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಈಜಿಪ್ಟ್ ದೇಶದಲ್ಲೂ ನೀವು ನೋಡಿದ ಮರುಭೂಮಿಯಲ್ಲೂ ಪ್ರತ್ಯಕ್ಷರಾಗಿ ನಿಮ್ಮ ಪರವಾಗಿ ಯುದ್ಧಮಾಡಿದಂತೆಯೇ, ಈಗಲೂ ನಿಮ್ಮ ಕಡೆಯವರಾಗಿದ್ದು ಯುದ್ಧಮಾಡುವರು.",
			"31": "<sup>31</sup> ನೀವು ಈ ಸ್ಥಳಕ್ಕೆ ಸೇರುವ ಪರ್ಯಂತರ ನಿಮ್ಮ ಪ್ರಯಾಣದಲ್ಲೆಲ್ಲಾ ಒಬ್ಬ ತಂದೆ ತನ್ನ ಮಗನನ್ನು ಹೇಗೋ ಹಾಗೆಯೇ, ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮನ್ನು ಹೊತ್ತುತಂದರಲ್ಲವೇ?” ಎಂದು ನಿಮಗೆ ಹೇಳಿದೆ.",
			"32": "<sup>32</sup> “ಆದರೂ ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನನ್ನು ನಂಬಲೇ ಇಲ್ಲ.",
			"33": "<sup>33</sup> ನೀವು ಹೋಗಬೇಕಾದ ದಾರಿಯನ್ನು ತೋರಿಸುವುದಕ್ಕೂ ಕಂಡು ಇಳಿಯಬೇಕಾದ ಸ್ಥಳಗಳನ್ನು ಗೊತ್ತುಮಾಡುವುದಕ್ಕೂ ಇರುಳಲ್ಲಿ ಬೆಂಕಿಯಲ್ಲೂ ಹಗಲಲ್ಲಿ ಮೇಘದಲ್ಲೂ ನಿಮ್ಮ ಮುಂದುಗಡೆ ಹೋದ ದೇವರನ್ನು ನೀವು ನಂಬಲಿಲ್ಲ.",
			"34": "<sup>34</sup> “ಸರ್ವೇಶ್ವರ ನಿಮ್ಮ ಮಾತುಗಳನ್ನು ಕೇಳಿ ಕೋಪಗೊಂಡರು. ಅವರು ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ವಾಗ್ದಾನ ಮಾಡಿದ ಒಳ್ಳೇ ನಾಡನ್ನು ಈ ದುಷ್ಟಜನರಲ್ಲಿ ಯಾರೂ ನೋಡುವುದಿಲ್ಲವೆಂದು ಪ್ರಮಾಣಮಾಡಿದರು.",
			"36": "<sup>36</sup> ‘ಯೆಫುನ್ನೆಯ ಮಗ ಕಾಲೇಬನೊಬ್ಬನೇ ಸರ್ವೇಶ್ವರನಾದ ನನ್ನನ್ನು ಮನಃಪೂರ್ವಕವಾಗಿ ಅನುಸರಿಸಿದ್ದರಿಂದ ಅವನೇ ಅದನ್ನು ನೋಡುವನು; ಅವನು ಸಂಚರಿಸಿದ ಪ್ರದೇಶವನ್ನು ಅವನಿಗೆ ಹಾಗು ಅವನ ಸಂತತಿ ಅವರಿಗೆ ಕೊಡುವೆನು,’ ಎಂದು ಹೇಳಿದರು.",
			"37": "<sup>37</sup> ಅದು ಮಾತ್ರವಲ್ಲದೆ, ಸರ್ವೇಶ್ವರ ನಿಮ್ಮ ದೆಸೆಯಿಂದ ನನ್ನ ಮೇಲೆಯೂ ಕೋಪಮಾಡಿದರು. ‘ನೀನೂ ಆ ನಾಡಿಗೆ ಸೇರುವುದಿಲ್ಲ;",
			"38": "<sup>38</sup> ಆದರೆ ನಿನ್ನ ಮುಂದೆ ನಿಂತಿರುವ ನೂನನ ಮಗ ಯೆಹೋಶುವನು ಅಲ್ಲಿಗೆ ಸೇರುವನು. ಅವನೇ ಇಸ್ರಯೇಲರಿಗೆ ಆ ನಾಡನ್ನು ಸ್ವಾಧೀನಪಡಿಸುವನು; ಆದುದರಿಂದ ಅವನನ್ನು ಧೈರ್ಯಗೊಳಿಸು.",
			"39": "<sup>39</sup> ಆದರೆ ಪರರ ಪಾಲಾಗುವರೆಂದು ನೀವು ಹೇಳಿದ ನಿಮ್ಮ ಮನೆಯವರು ಹಾಗು ಒಳ್ಳೆಯದು, ಕೆಟ್ಟದು ಎಂಬುದನ್ನು ಅರಿಯದ ನಿಮ್ಮ ಹಸುಳೆಗಳು ಅಲ್ಲಿಗೆ ಸೇರುವರು; ಅವರಿಗೇ ಆ ನಾಡನ್ನು ಕೊಡುವೆನು; ಅವರು ಅದನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವರು.",
			"40": "<sup>40</sup> ನೀವಾದರೋ ಹಿಂದಿರುಗಿ ಕೆಂಪು ಸಮುದ್ರದ ಮಾರ್ಗವನ್ನು ಹಿಡಿದು ಮರುಭೂಮಿಗೆ ಹೊರಟುಹೋಗಿ’ ಎಂದು ಆಜ್ಞಾಪಿಸಿದರು.",
			"41": "<sup>41</sup> “ಅದಕ್ಕೆ ನೀವು, ‘ನಾವು ಸರ್ವೇಶ್ವರನಿಗೆ ದ್ರೋಹಿಗಳಾದೆವು; ಆದರೂ ನಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಆಜ್ಞಾಪಿಸಿದಂತೆ ನಾವೇ ಈಗ ಆ ಮಲೆನಾಡನ್ನು ಹತ್ತಿ ಯುದ್ಧಮಾಡುತ್ತೇವೆ,’ ಎಂದು ಉತ್ತರಕೊಟ್ಟಿರಿ; ನೀವೆಲ್ಲರು ನಿಮ್ಮ ನಿಮ್ಮ ಆಯುಧಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಆ ಮಲೆನಾಡನ್ನು ಹತ್ತಿ ಜಯಿಸುವುದು ಅಲ್ಪಕಾರ್ಯವೆಂದು ಭಾವಿಸಿ ಹೊರಡುವುದಕ್ಕಿದ್ದಿರಿ.",
			"42": "<sup>42</sup> ಆದರೆ ಸರ್ವೇಶ್ವರ ನನಗೆ, ‘ನಾನು ಇವರ ಮಧ್ಯೆ ಇಲ್ಲವಾದುದರಿಂದ ಇವರು ಸೋತುಹೋಗುವರು; ಯುದ್ಧಮಾಡಲೂಬಾರದು, ಆ ಮಲೆನಾಡಿಗೆ ಹೋಗಲೂಬಾರದು ಎಂದು ಇವರಿಗೆ ಆಜ್ಞಾಪಿಸು,’ ಎಂದರು.",
			"43": "<sup>43</sup> “ನಾನು ಆ ಮಾತನ್ನು ನಿಮಗೆ ತಿಳಿಸಿದರೂ ನೀವು ಕಿವಿಗೊಡದೆ ಸರ್ವೇಶ್ವರನ ಆಜ್ಞೆಯನ್ನು ತಾತ್ಸಾರಮಾಡಿ ಸೊಕ್ಕಿನಿಂದ ಆ ಬೆಟ್ಟವನ್ನು ಹತ್ತಿದಿರಿ.",
			"44": "<sup>44</sup> ಆಗ ಬೆಟ್ಟದಲ್ಲಿದ್ದ ಅಮೋರಿಯರು ನಿಮಗೆ ವಿರುದ್ಧ ಹೊರಟು ಜೇನುನೊಣಗಳಂತೆ ನಿಮ್ಮನ್ನು ಮುತ್ತಿ ಸೇಯೀರಿನಲ್ಲಿ ಹೊರ್ಮದವರೆಗೂ ಬೆನ್ನಟ್ಟಿ ಸಂಹರಿಸಿದರು.",
			"45": "<sup>45</sup> ನೀವು ಹಿಂದಿರುಗಿ ಬಂದು ಸರ್ವೇಶ್ವರನ ಮುಂದೆ ಗೋಳಾಡಿದಿರಿ. ಆಗ ಅವರು ನಿಮ್ಮ ಮೊರೆಯನ್ನು ಕೇಳಲೂ ಇಲ್ಲ, ನಿಮ್ಮನ್ನು ರಕ್ಷಿಸಲೂ ಇಲ್ಲ.",
			"46": "<sup>46</sup> “ಆಮೇಲೆ ನೀವು ಕಾದೇಶಿನಲ್ಲಿ ಬಹುಕಾಲ ವಾಸವಾಗಿದ್ದಿರಿ."
		},
		"2": {
			"1": "<sup>1</sup> “ಆಗ ಸರ್ವೇಶ್ವರ ನನಗೆ ಆಜ್ಞಾಪಿಸಿದಂತೆ ನಾವು ಹಿಂದಿರುಗಿ ಮರುಭೂಮಿಗೆ ಹೊರಟು ಕೆಂಪುಸಮುದ್ರದ ಮಾರ್ಗದಲ್ಲಿ ನಡೆದು ಅನೇಕ ದಿನಗಳು ಸೇಯೀರ್ ಮಲೆನಾಡನ್ನು ಸುತ್ತುತ್ತಿದ್ದೆವು.",
			"2": "<sup>2</sup> ಆಮೇಲೆ ಸರ್ವೇಶ್ವರ ನನಗೆ,",
			"3": "<sup>3</sup> ‘ನೀವು ಈ ಮಲೆನಾಡನ್ನು ಸುತ್ತಿದ್ದು ಸಾಕು; ಉತ್ತರದಿಕ್ಕಿಗೆ ತಿರುಗಿಕೊಳ್ಳಿ.",
			"4": "<sup>4</sup> ಇಸ್ರಯೇಲರಿಗೆ ಹೀಗೆ ಆಜ್ಞಾಪಿಸು; ಸೇಯೀರ್ ನಾಡಿನಲ್ಲಿ ವಾಸವಾಗಿರುವ ನಿಮ್ಮ ಬಂಧುಗಳಾದ ಏಸಾವನ ವಂಶದವರ ನಾಡನ್ನು ದಾಟುವುದಕ್ಕಿದ್ದೀರಿ. ಅವರು ನಿಮಗೆ ಅಂಜುವರು; ನೀವು ಅವರ ಸಂಗಡ ಯುದ್ಧಕ್ಕೆ ಹೋಗದೆ ಬಹಳ ಜಾಗರೂಕತೆಯಿಂದಿರಬೇಕು.",
			"5": "<sup>5</sup> ನಾನು ಸೇಯೀರ್ ಮಲೆನಾಡನ್ನು ಏಸಾವ್ಯರಿಗೇ ಸ್ವದೇಶವಾಗಿ ಕೊಟ್ಟಿದ್ದೇನೆ. ಆದುದರಿಂದ ಅದರಲ್ಲಿ ನಿಮಗೆ ಹೆಜ್ಜೆಯಿಡುವಷ್ಟು ನೆಲವನ್ನಾದರೂ ಕೊಡುವುದಿಲ್ಲ.",
			"6": "<sup>6</sup> ನೀವು ಅಲ್ಲಿ ಕಾಸುಕೊಟ್ಟು ಆಹಾರಪದಾರ್ಥಗಳನ್ನು ಕೊಂಡು ತಿನ್ನಬೇಕು; ನೀರನ್ನೂ ಕೊಂಡುಕೊಂಡು ಕುಡಿಯಬೇಕು,’ ಎಂದರು.",
			"7": "<sup>7</sup> “ನೀವು ಕೈಹಾಕಿದ ಎಲ್ಲ ಕೆಲಸಗಳನ್ನೂ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಸಫಲಪಡಿಸಿದ್ದಾರೆ. ಈ ದೊಡ್ಡ ಮರುಭೂಮಿಯಲ್ಲಿ ನೀವು ಸಂಚರಿಸುತ್ತಿರುವಾಗಲೆಲ್ಲ ಅವರು ನಿಮ್ಮನ್ನು ಪರಾಂಬರಿಸುತ್ತಾ ಬಂದರು. ಈ ನಲವತ್ತು ವರ್ಷ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮೊಂದಿಗೆ ಇದ್ದುದರಿಂದಲೆ ನಿಮಗೆ ಏನೂ ಕಡಿಮೆಯಾಗಲಿಲ್ಲ,” ಎಂದು ಹೇಳಿದನು.",
			"8": "<sup>8</sup> “ಆದಕಾರಣ ನಾವು ಸೇಯೀರಿನಲ್ಲಿರುವ ನಮ್ಮ ಬಂಧುಗಳಾದ ಏಸಾವ್ಯರ ಕಡೆಯಿಂದ ಓರೆಯಾಗಿ ಪ್ರಯಾಣಮಾಡಿ ಅರಾಬದ ಮಾರ್ಗವನ್ನು ಬಿಟ್ಟು ಏಲತ್, ಎಚ್ಯೋನ್ಗೆಬೆರ್ ಎಂಬ ಪಟ್ಟಣಗಳಿಂದ ಪ್ರಯಾಣಮಾಡಿ ಮೋವಾಬ್ಯರ ಅಡವಿಯ ದಾರಿಯಲ್ಲಿ ನಡೆದೆವು.",
			"9": "<sup>9</sup> ಆಗ ಸರ್ವೇಶ್ವರ ನನಗೆ, ‘ನೀವು ಮೋವಾಬ್ಯರಿಗೆ ವೈರಿಗಳಾಗಿ ನಡೆದು ಅವರೊಡನೆ ಯುದ್ಧಮಾಡಬಾರದು. ಲೋಟನ ವಂಶದವರಾದ ಅವರಿಗೆ ನಾನು ಆರ್ ಎಂಬ ಪ್ರದೇಶವನ್ನು ಸ್ವದೇಶವಾಗುವುದಕ್ಕೆ ಕೊಟ್ಟಿರುವುದರಿಂದ ಅದರಲ್ಲಿ ನಿಮಗೆ ಸೊತ್ತನ್ನು ಕೊಡುವುದಿಲ್ಲ. ನೀವು ಎದ್ದು ಜೆರೆದ್ ಹಳ್ಳವನ್ನು ದಾಟಬೇಕು.’ ಎಂದು ಆಜ್ಞಾಪಿಸಿದರು.",
			"10": "<sup>10</sup> (ತತ್ಪೂರ್ವದಲ್ಲಿ ಏಮಿಯರು ಆ ನಾಡಿನಲ್ಲಿ ವಾಸವಾಗಿದ್ದರು. ಅವರು ಬಲಿಷ್ಠರೂ ಬಹುಮಂದಿಯೂ ಅನಾಕಿಮರಂತೆ ಎತ್ತರವಾದವರೂ ಆಗಿದ್ದರು.",
			"11": "<sup>11</sup> ಅವರು ಅನಾಕಿಮರಂತೆ ರೆಫಾಯರಿಗೆ ಸೇರಿದವರೆಂದು ಹೇಳುವುದುಂಟು; ಆದರೆ ಮೋವಾಬ್ಯರು ಅವರನ್ನು ಏಮಿಯರೆಂದು ಹೇಳುತ್ತಾರೆ.",
			"12": "<sup>12</sup> ಅದೇ ಪೂರ್ವಕಾಲದಲ್ಲಿ ಹೋರಿಯರು ಸೇಯೀರಿನಲ್ಲಿ ವಾಸವಾಗಿದ್ದರು. ತರುವಾಯ ಇಸ್ರಯೇಲರು ತಮಗೆ ಸರ್ವೇಶ್ವರ ಕೊಟ್ಟ ನಾಡನ್ನು ಹೇಗೆ ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡರೋ ಹಾಗೆಯೇ ಏಸಾವ್ಯರು ಬಂದು ಹೋರಿಯರನ್ನು ಸಂಹರಿಸಿ ಅವರ ನಾಡಿನಲ್ಲಿ ವಾಸಮಾಡಿದರು.)",
			"13": "<sup>13</sup> “ಆಗ ನಾವು ಜೆರೆದ್ ಹಳ್ಳವನ್ನು ದಾಟಿದೆವು. ನಾವು ಕಾದೇಶ್ ಬರ್ನೇಯದಿಂದ ಹೊರಟು ಜೆರೆದ್ ಹಳ್ಳವನ್ನು ದಾಟಿದವರೆಗೆ ಮೂವತ್ತೆಂಟು ವರ್ಷಕಾಲ ಆಯಿತು. ಸರ್ವೇಶ್ವರ ಪ್ರಮಾಣಪೂರ್ವಕವಾಗಿ ಹೇಳಿದ್ದಂತೆ ಅಷ್ಟರಲ್ಲಿ ಆ ತಲೆಯ ಸೈನಿಕರೆಲ್ಲರು ಸತ್ತುಹೋಗಿದ್ದರು.",
			"15": "<sup>15</sup> ಸರ್ವೇಶ್ವರನ ಹಸ್ತವು ಅವರಿಗೆ ವಿರೋಧವಾಗಿದ್ದುದರಿಂದ ಪಾಳೆಯದಲ್ಲಿ ಒಬ್ಬನೂ ಉಳಿಯದಂತೆ ನಾಶವಾದರು.",
			"16": "<sup>16</sup> “ಆ ಸೈನಿಕರೆಲ್ಲರು ಸತ್ತುಹೋದ ನಂತರ ಸರ್ವೇಶ್ವರ ನನಗೆ,",
			"18": "<sup>18</sup> ‘ನೀವು ಈಗ ಮೋವಾಬ್ಯರ ಪ್ರಾಂತ್ಯವಾಗಿರುವ ಆರ್ ಎಂಬ ಪ್ರದೇಶವನ್ನು ದಾಟುವಿರಿ.",
			"19": "<sup>19</sup> ಮುಂದೆ ನೀವು ಅಮ್ಮೋನಿಯರ ಹತ್ತಿರಕ್ಕೆ ಬರುವಾಗ ಅವರಿಗೆ ವೈರಿಗಳಾಗಿ ನಡೆದು ಯುದ್ಧಮಾಡಬೇಡಿ. ನಾನು ಆ ನಾಡನ್ನು ಲೋಟನ ವಂಶದವರಾದ ಅಮ್ಮೋನಿಯರಿಗೆ ಸೊತ್ತಾಗಿ ಕೊಟ್ಟಿದ್ದೇನೆ. ಆದುದರಿಂದ ನಿಮಗೆ ಅದರಲ್ಲಿ ಕಿಂಚಿತ್ತು ಭಾಗವನ್ನೂ ಕೊಡುವುದಿಲ್ಲ.",
			"20": "<sup>20</sup> (ಪೂರ್ವಕಾಲದಲ್ಲಿ ರೆಫಾಯರು ಆ ನಾಡಿನಲ್ಲಿ ವಾಸವಾಗಿದ್ದುದರಿಂದ ಅದು ಕೂಡ ರೆಫಾಯರ ನಾಡೆನಿಸಿಕೊಳ್ಳುತ್ತದೆ. ಅಮ್ಮೋನಿಯರು ಅವರನ್ನು ಜಂಜುಮ್ಯರೆಂದು ಕರೆಯುತ್ತಾರೆ;",
			"21": "<sup>21</sup> ಅವರು ಬಲಿಷ್ಠರು, ಬಹುಜನರು ಹಾಗು ಅನಾಕಿಮರಂತೆ ಎತ್ತರದವರೂ ಆಗಿದ್ದರು.",
			"22": "<sup>22</sup> ಆದರೆ ಹೇಗೆ ಸರ್ವೇಶ್ವರ ಸೇಯೀರಿನಲ್ಲಿರುವ ಏಸಾವ್ಯರ ಪರವಾಗಿ ಹೋರಿಯರನ್ನು ಹೊರದೂಡಿದಾಗ ಏಸಾವ್ಯರು ಆ ನಾಡನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡು ಇಂದಿನವರೆಗೂ ಅಲ್ಲೇ ವಾಸವಾಗಿದ್ದಾರೋ,",
			"23": "<sup>23</sup> ಮತ್ತು ಹೇಗೆ ಕಫ್ತೋರಿಂದ ಬಂದ ಕಫ್ತೋರ್ಯರು ಗಾಜಾ ಪಟ್ಟಣದವರೆಗೂ ಹಳ್ಳಿಗಳಲ್ಲಿದ್ದ ಅವ್ವಿಯರನ್ನು ನಾಶಮಾಡಿ ಅವರ ಸ್ಥಳದಲ್ಲಿ ವಾಸಮಾಡಿದರೋ, ಹಾಗೆಯೇ ಸರ್ವೇಶ್ವರ ರೆಫಾಯರನ್ನು ಅಮ್ಮೋನಿಯರ ಬಳಿಯಿಂದ ಹೊರದೂಡಿದರು; ಅಮ್ಮೋನಿಯರು ಆ ನಾಡನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡು ಅದರಲ್ಲಿ ವಾಸಿಸಿದರು.)",
			"24": "<sup>24</sup> ಈಗ ನೀವು ಹೊರಟು ಅರ್ನೋನ್ ಹಳ್ಳವನ್ನು ದಾಟಿರಿ. ಅಮೋರಿಯನಾಗಿರುವ ಹೆಷ್ಬೋನಿನ ಅರಸ ಸೀಹೋನನು ನಿಮ್ಮಿಂದ ಸೋತುಹೋಗಿ ಅವನ ರಾಜ್ಯ ನಿಮಗೇ ವಶವಾಗಬೇಕೆಂದು ನಿಮಗೆ ಅನುಗ್ರಹಿಸಿದ್ದೇನೆ. ಅವನೊಡನೆ ಯುದ್ಧಮಾಡಿ ಆ ನಾಡನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವುದಕ್ಕೆ ಪ್ರಾರಂಭಿಸಿರಿ.",
			"25": "<sup>25</sup> ಲೋಕದಲ್ಲಿರುವ ಎಲ್ಲ ಜನಗಳಿಗೂ ಇಂದಿನಿಂದ ನಿಮ್ಮ ಬಗ್ಗೆ ದಿಗಿಲೂ ಹೆದರಿಕೆಯೂ ಉಂಟಾಗುವಂತೆ ಮಾಡುತ್ತೇನೆ. ಅವರು ನಿಮ್ಮ ಸುದ್ದಿಯನ್ನು ಕೇಳಿದ ಮಾತ್ರಕ್ಕೆ ಗಡಗಡನೆ ನಡುಗಿ ಸಂಕಟಪಡುವರು,’ ಎಂದು ಹೇಳಿದರು.",
			"26": "<sup>26</sup> “ಆಗ ನಾನು ಕೆದೇಮೋತಿನ ಮರುಭೂಮಿಯಿಂದ ಹೆಷ್ಬೋನಿನ ಅರಸ ಸೀಹೋನನ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿದೆ.",
			"27": "<sup>27</sup> ‘ನಿನ್ನ ನಾಡನ್ನು ದಾಟಿಹೋಗುವುದಕ್ಕೆ ನಮಗೆ ಅಪ್ಪಣೆಯಾಗಬೇಕು. ನಾವು ಎಡಬಲಕ್ಕೆ ತಿರುಗದೆ ದಾರಿಹಿಡಿದು ನಡೆದುಹೋಗುವೆವು.",
			"28": "<sup>28</sup> ನಮ್ಮಿಂದ ಕ್ರಯ ತೆಗೆದುಕೊಂಡು ಆಹಾರಪದಾರ್ಥಗಳನ್ನೂ ಕುಡಿಯಲು ನೀರನ್ನೂ ಕೊಡಿ.",
			"29": "<sup>29</sup> ನಾವು ಜೋರ್ಡನ್ ಹೊಳೆಯನ್ನು ದಾಟಿ, ನಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಮಗೆ ಕೊಡುವ ನಾಡನ್ನು ಸೇರಬೇಕೆಂದಿದ್ದೇವೆ. ಸೇಯೀರಿನಲ್ಲಿರುವ ಏಸಾವ್ಯರು ಹಾಗು ಆರ್ ಪ್ರದೇಶದಲ್ಲಿರುವ ಮೋವಾಬ್ಯರು ನಮಗೆ ದಾರಿಕೊಟ್ಟಂತೆ ನೀನೂ ಕೊಡಬೇಕು. ಕಾಲ್ನಡೆಯಾಗಿ ನಿನ್ನ ನಾಡನ್ನು ದಾಟಿಹೋಗುವುದಕ್ಕೆ ನಿನ್ನ ಅಪ್ಪಣೆಯಾಗಬೇಕೇ ಹೊರತು ಬೇರೆ ಏನೂ ಬೇಕಿಲ್ಲ,’ ಎಂದು ವಿನಯಪೂರ್ವಕವಾದ ಮಾತುಗಳಿಂದ ಹೇಳಿಸಿದೆ.",
			"30": "<sup>30</sup> ಆದರೆ ಸರ್ವೇಶ್ವರ ಅವನಿಗೆ ಮೂರ್ಖಬುದ್ಧಿಯನ್ನು ಕೊಟ್ಟು, ಹಟಮಾರಿಯನ್ನಾಗಿಸಿದ್ದರಿಂದ ಅವನು ಸಮ್ಮತಿಸಲಿಲ್ಲ. ನಿಮ್ಮಿಂದ ಅವನು ಸೋತುಹೋಗಬೇಕೆಂಬುದೇ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಸಂಕಲ್ಪವಾಗಿತ್ತು. ಅದು ಈಗಾಗಲೇ ನೆರವೇರಿದೆ.",
			"31": "<sup>31</sup> “ತರುವಾಯ ಸರ್ವೇಶ್ವರ ನನಗೆ, ‘ಈಗ ನಾನು ಸೀಹೋನನನ್ನೂ ಅವನ ನಾಡನ್ನೂ ನಿಮಗೆ ವಶಪಡಿಸುವುದಕ್ಕೆ ಪ್ರಾರಂಭಿಸಿದ್ದೇನೆ; ಅದನ್ನು ಸ್ವಾಧೀನ ಮಾಡಿಕೊಳ್ಳಲು ಶುರುಮಾಡಿರಿ,’ ಎಂದು ಹೇಳಿದರು.",
			"32": "<sup>32</sup> ಸೀಹೋನನು ತನ್ನ ಜನರೆಲ್ಲರ ಸಮೇತ ನಮ್ಮೊಡನೆ ಯುದ್ಧಮಾಡಲು ಯಹಜಿಗೆ ಹೊರಟುಬಂದ.",
			"33": "<sup>33</sup> ನಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಅವನನ್ನು ನಮ್ಮಿಂದ ಪರಾಜಯ ಪಡಿಸಿದರು. ನಾವು ಅವನನ್ನು, ಅವನ ಮಕ್ಕಳನ್ನು ಹಾಗು ಜನರೆಲ್ಲರನ್ನು ಸದೆಬಡಿದೆವು.",
			"34": "<sup>34</sup> ಆ ಕಾಲದಲ್ಲಿ ನಾವು ಅವನ ಎಲ್ಲ ಪಟ್ಟಣಗಳನ್ನೂ ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡು, ಅವುಗಳಲ್ಲಿದ್ದ ಗಂಡಸರನ್ನು, ಹೆಂಗಸರನ್ನು ಹಾಗು ಮಕ್ಕಳನ್ನು ನಿಶ್ಯೇಷವಾಗಿ ಹತಮಾಡಿದೆವು; ಒಬ್ಬನನ್ನೂ ಉಳಿಸಲಿಲ್ಲ.",
			"35": "<sup>35</sup> ಪಶುಗಳನ್ನು ಮಾತ್ರ ಉಳಿಸಿ ನಮ್ಮ ಸ್ವಂತಕ್ಕಾಗಿ ತೆಗೆದುಕೊಂಡು, ಊರುಗಳನ್ನು ಸೂರೆಮಾಡಿಬಿಟ್ಟೆವು.",
			"36": "<sup>36</sup> ಅರ್ನೋನ್ ಕಣಿವೆಯ ಅಂಚಿನಲ್ಲಿರುವ ಅರೋಯೇರ್ ಪಟ್ಟಣ ಮತ್ತು ಕಣಿವೆಯಲ್ಲೇ ಇರುವ ಪಟ್ಟಣ ಇವುಗಳನ್ನು ಮೊದಲುಗೊಂಡು ಗಿಲ್ಯಾದ್ ಸೀಮೆಯವರೆಗೆ ಯಾವ ಪಟ್ಟಣವನ್ನು ಜಯಿಸಲೂ ನಮಗೆ ಅಸಾಧ್ಯವಾಗಲಿಲ್ಲ. ನಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಅವುಗಳೆಲ್ಲ ನಮ್ಮ ಕೈಗೆ ಸಿಕ್ಕುವಂತೆ ಮಾಡಿದರು.",
			"37": "<sup>37</sup> ಅಮ್ಮೋನಿಯರ ನಾಡನ್ನು ಮಾತ್ರ, ಅಂದರೆ ಯಬ್ಬೋಕ್ ಹೊಳೆಯ ಬಳಿಯಲ್ಲಿರುವ ಪ್ರದೇಶ ಮತ್ತು ಮಲೆನಾಡಿನ ಊರುಗಳು ಇವೇ ಮೊದಲಾದ ಸ್ಥಳಗಳನ್ನು ನೀವು ಅತಿಕ್ರಮಿಸಲಿಲ್ಲ; ಅಲ್ಲಿಗೆ ಹೋಗಲೇಬಾರದೆಂದು ನಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಆಜ್ಞಾಪಿಸಿದ್ದರು."
		},
		"3": {
			"1": "<sup>1</sup> “ತರುವಾಯ ನಾವು ಹಿಂದಿರುಗಿ ಬಾಷಾನಿನ ಮಾರ್ಗವನ್ನು ಹಿಡಿದು ಹತ್ತಿಹೋದೆವು. ಆಗ ಬಾಷಾನಿನ ಅರಸ ಓಗನು ತನ್ನ ಜನರೆಲ್ಲರ ಸಮೇತ ನಮಗೆ ವಿರುದ್ಧ ಯುದ್ಧಮಾಡುವುದಕ್ಕೆ ಎದ್ರೈವೂರಿಗೆ ಹೊರಟು ಬಂದನು.",
			"2": "<sup>2</sup> ಆಗ ಸರ್ವೇಶ್ವರ ನನಗೆ, ‘ಅವನಿಗೆ ಭಯಪಡಬೇಡ; ನಾನು ಅವನನ್ನು, ಅವನ ಸಮಸ್ತ ಜನರನ್ನು ಹಾಗು ನಾಡನ್ನು ನಿನ್ನ ಕೈವಶಮಾಡಿದ್ದೇನೆ; ನೀನು ಹೆಷ್ಬೋನಿನಲ್ಲಿದ್ದ ಅಮೋರಿಯರ ಅರಸ ಸೀಹೋನನಿಗೆ ಮಾಡಿದಂತೆಯೇ ಇವನಿಗೂ ಮಾಡು,’ ಎಂದು ಆಜ್ಞಾಪಿಸಿದರು.",
			"3": "<sup>3</sup> “ಬಾಷಾನಿನ ಅರಸ ಓಗನು ಹಾಗು ಅವನ ಜನರೆಲ್ಲರು ನಮ್ಮಿಂದ ಸೋತುಹೋಗುವಂತೆ ನಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಮಾಡಿದರು. ಅವರಲ್ಲಿ ಒಬ್ಬರಾದರು ಉಳಿಯದಂತೆ ಅವರನ್ನು ಹತಮಾಡಿದೆವು.",
			"4": "<sup>4</sup> ಅಂದು ಅವನ ಎಲ್ಲ ಪಟ್ಟಣಗಳನ್ನು ಜಯಿಸಿದೆವು; ನಾವು ಜಯಿಸದ ಕೋಟೆ ಒಂದೂ ಇರಲಿಲ್ಲ; ಬಾಷಾನಿನಲ್ಲಿರುವ ಓಗನ ರಾಜ್ಯದಲ್ಲಿ ಅಂದರೆ, ಅರ್ಗೋಬ್ ಎಂಬ ಸಮಸ್ತ ಪ್ರದೇಶದಲ್ಲಿ ಅರವತ್ತು ಪಟ್ಟಣಗಳನ್ನು ತೆಗೆದುಕೊಂಡೆವು.",
			"5": "<sup>5</sup> ಅವೆಲ್ಲವು ಎತ್ತರವಾದ ಪೌಳಿಗೋಡೆಗಳಿಂದ, ಕದಗಳಿಂದ ಹಾಗು ಅಗುಳಿಗಳಿಂದ ಸುಭದ್ರವಾದ ಪಟ್ಟಣಗಳಾಗಿದ್ದವು. ಅವುಗಳಲ್ಲದೆ ಅನೇಕ ಹಳ್ಳಿಪಳ್ಳಿಗಳು ಇದ್ದವು.",
			"6": "<sup>6</sup> ನಾವು ಹೆಷ್ಬೋನಿನ ಅರಸನಾದ ಸೀಹೋನನ ಪಟ್ಟಣಗಳಲ್ಲಿದ್ದ ಗಂಡಸರನ್ನು, ಹೆಂಗಸರನ್ನು, ಮಕ್ಕಳನ್ನು ನಿಶ್ಯೇಷವಾಗಿ ನಾಶಮಾಡಿದೆವು.",
			"7": "<sup>7</sup> ಪಶುಪ್ರಾಣಿಗಳನ್ನು ಮಾತ್ರ ಉಳಿಸಿ ಸ್ವಂತ ಉಪಯೋಗಕ್ಕೆ ತೆಗೆದುಕೊಂಡು ಊರುಗಳನ್ನೆಲ್ಲ ಸೂರೆಮಾಡಿಬಿಟ್ಟವು.",
			"8": "<sup>8</sup> “ಆ ಕಾಲದಲ್ಲಿ ನಾವು ಅರ್ನೋನ್ ನದಿ ಮೊದಲುಗೊಂಡು ಹೆರ್ಮೋನ್ ಪರ್ವತದವರೆಗೂ ಜೋರ್ಡನ್ ನದಿಯ ಈಚೆ ಇರುವ ದೇಶವನ್ನೆಲ್ಲಾ ಅಮೋರಿಯರ ಇಬ್ಬರು ಅರಸರಿಂದ ತೆಗೆದುಕೊಂಡೆವು.",
			"9": "<sup>9</sup> (ಹೆರ್ಮೋನ್ ಪರ್ವತಕ್ಕೆ ಸಿದೋನ್ಯರು ಸಿರ್ಯೋನೆಂದೂ ಅಮೋರಿಯರು ಸೆನೀರ್ ಎಂದೂ ಹೆಸರಿಟ್ಟುಕೊಂಡಿದ್ದರು.)",
			"10": "<sup>10</sup> ಹೀಗೆ ಮೀಶೋರೆಂಬ ಎತ್ತರವಾದ ಬೈಲುಸೀಮೆಯ ಎಲ್ಲಾ ಪಟ್ಟಣಗಳನ್ನೂ ಗಿಲ್ಯಾದ್ ಸೀಮೆಯನ್ನೂ ಓಗನ ರಾಜ್ಯವಾದ ಸಲ್ಕಾ, ಎದ್ರೈ ಎಂಬ ಪಟ್ಟಣಗಳಿಂದ ಕೂಡಿರುವ ಬಾಷಾನ್ ಸೀಮೆಯನ್ನೂ ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡೆವು.",
			"11": "<sup>11</sup> (ರೆಫಾಯರೊಳಗೆ ಬಾಷಾನಿನ ಓಗನೊಬ್ಬನೇ ಉಳಿದಿದ್ದನು. ಅವನ ಮಂಚ ಕಬ್ಬಿಣದ್ದು; ಅಮ್ಮೋನಿಯರ ರಬ್ಬಾ ಎಂಬ ಪಟ್ಟಣದಲ್ಲಿ ಅದನ್ನು ಈಗಲೂ ನೋಡಬಹುದು. ಪುರುಷನ ಕೈಯಳತೆಯ ಪ್ರಕಾರ ಅದರ ಉದ್ದ ಒಂಭತ್ತು ಮೊಳ, ಅಗಲ ನಾಲ್ಕು ಮೊಳ.)",
			"12": "<sup>12</sup> “ಆ ಕಾಲದಲ್ಲಿ ನಾವು ಆ ನಾಡನ್ನೆಲ್ಲ ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡೆವು. ಅರ್ನೋನ್ ಹಳ್ಳದ ಬಳಿಯಲ್ಲಿರುವ ಅರೋಯೇರ್ ಮೊದಲುಗೊಂಡು ಗಿಲ್ಯಾದ್ ಸೀಮೆಯ ಮಧ್ಯದವರೆಗೂ ಇರುವ ಪ್ರದೇಶವನ್ನೆಲ್ಲಾ ಅದರ ಪಟ್ಟಣಗಳ ಸಹಿತವಾಗಿ ನಾನು ರೂಬೇನ್ಯರಿಗೂ ಗಾದ್ಯರಿಗೂ ಕೊಟ್ಟೆನು.",
			"13": "<sup>13</sup> ಗಿಲ್ಯಾದಿನ ಮಿಕ್ಕ ಭಾಗವನ್ನೂ ಓಗನ ರಾಜ್ಯ ಆಗಿದ್ದ ಅರ್ಗೋಬ್ ಎನಿಸಿಕೊಳ್ಳುವ ಸಮಸ್ತ ಬಾಷಾನ್ ನಾಡನ್ನೂ ಮನಸ್ಸೆಕುಲದ ಅರ್ಧ ಜನರಿಗೆ ಕೊಟ್ಟೆನು.",
			"14": "<sup>14</sup> (ಬಾಷಾನ್ ನಾಡು ರೆಫಾಯರ ನಾಡೆನಿಸಿಕೊಳ್ಳುತ್ತದೆ. ಮನಸ್ಸೆಯ ವಂಶಸ್ಥನಾದ ಯಾಯೀರನು ಗೆಷೂರ್ಯರ ಮತ್ತು ಮಾಕಾತ್ಯರ ಮೇರೆಯವರೆಗೆ ಅರ್ಗೋಬ್ ಎಂಬ ಪ್ರದೇಶವನ್ನೆಲ್ಲಾ ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡು ಬಾಷಾನಿಗೆ ಸೇರಿರುವ ಆ ಪ್ರಾಂತ್ಯಕ್ಕೆ ಯಾಯೀರನ ಗ್ರಾಮಗಳೆಂದು ತನ್ನ ಹೆಸರಿಟ್ಟನು. ಈ ಹೆಸರು ಇಂದಿನವರೆಗೂ ಉಂಟು).",
			"15": "<sup>15</sup> ನಾನು ಗಿಲ್ಯಾದ್ ನಾಡನ್ನು ಮಾಕೀರನಿಗೂ",
			"16": "<sup>16</sup> ಗಿಲ್ಯಾದ್ ಮೊದಲುಗೊಂಡು (ದಕ್ಷಿಣ ಕಡೆಯಲ್ಲಿ) ಅರ್ನೋನ್ ತಗ್ಗಿನವರೆಗೂ (ಪೂರ್ವ ಕಡೆಯಲ್ಲಿ) ಯಬ್ಬೋಕ್ ಹೊಳೆಯವರೆಗೂ ಇರುವ ಪ್ರದೇಶವನ್ನು ರೂಬೇನ್ಯರಿಗೂ ಗಾದ್ಯರಿಗೂ ಕೊಟ್ಟೆ. ಅರ್ನೋನ್ ತಗ್ಗಿನ ಮಧ್ಯಭಾಗವೇ ಅವರ ದಕ್ಷಿಣ ಎಲ್ಲೆ. ಅವರಿಗೂ ಅಮ್ಮೋನಿಯರಿಗೂ ನಡುವೆ ಇರುವ ಯಬ್ಬೋಕ್ ಹೊಳೆಯು ಅವರ ಪೂರ್ವದಿಕ್ಕಿನ ಎಲ್ಲೆ.",
			"17": "<sup>17</sup> ಪಶ್ಚಿಮದಲ್ಲಿ ಅವರ ಪ್ರದೇಶ ಜೋರ್ಡನ್ ನದಿಯವರೆಗೆ ಹರಡಿತ್ತು. ಉತ್ತರದಲ್ಲಿ ಕಿನ್ನೆರೆತ್ ಸರೋವರ ಮೊದಲ್ಗೊಂಡು ದಕ್ಷಿಣದಲ್ಲಿ ಲವಣ ಸಮುದ್ರದವರೆಗೆ ವಿಸ್ತರಿಸಿತ್ತು. ಅದು ಪೂರ್ವದಲ್ಲಿ ಪಿಸ್ಗಾ ಬೆಟ್ಟದ ಬುಡದವರೆಗೆ ವ್ಯಾಪಿಸಿತ್ತು.",
			"18": "<sup>18</sup> “ಆ ಕಾಲದಲ್ಲಿ ನಾನು ನಿಮಗೆ, ‘ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಈ ಪ್ರದೇಶವನ್ನೇ ನಿಮಗೆ ಸೊತ್ತಾಗಿ ಕೊಟ್ಟಿದ್ದಾರೆ. ಆದರೆ ನಿಮ್ಮ ಯೋಧರೆಲ್ಲರು ಯುದ್ಧಸನ್ನದ್ಧರಾಗಿ ನಿಮ್ಮ ಸಹೋದರರಾದ ಇಸ್ರಯೇಲರ ಮುಂದೇ ಹೊರಟು ನದಿ ದಾಟಿ ಹೋಗಬೇಕು.",
			"19": "<sup>19</sup> ನಿಮಗೆ ಬಹಳ ದನಕುರಿಗಳುಂಟೆಂಬುದನ್ನು ನಾನು ಬಲ್ಲೆ. ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಜೋರ್ಡನ್ ನದಿಯ ಆಚೆ ನಿಮ್ಮ ಸಹೋದರರಿಗೆ ಕೊಡುವ ನಾಡು ಅವರಿಗೆ ಸ್ವಾಧೀನವಾಗುವವರೆಗೆ ನಿಮ್ಮ ಮಡದಿ ಮಕ್ಕಳೂ ದನಕುರಿಗಳೂ ನಾನು ನಿಮಗೆ ಕೊಟ್ಟಿರುವ ಪಟ್ಟಣಗಳಲ್ಲೇ ಇರಲಿ.",
			"20": "<sup>20</sup> ಅನಂತರ ನಿಮ್ಮಂತೆ ನಿಮ್ಮ ಸಹೋದರರಿಗೂ ಸರ್ವೇಶ್ವರನಿಂದ ವಿಶ್ರಾಂತಿ ದೊರಕಿದಾಗ ನಿಮ್ಮಲ್ಲಿ ಪ್ರತಿ ಒಬ್ಬನೂ ನಾನು ಕೊಟ್ಟ ಸೊತ್ತಿಗೆ ತಿರುಗಿ ಬರಬಹುದು’ ಎಂದು ಆಜ್ಞಾಪಿಸಿದೆ.",
			"21": "<sup>21</sup> “ಆ ಕಾಲದಲ್ಲಿ ನಾನು ಯೆಹೋಶುವನಿಗೆ, ‘ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಆ ಇಬ್ಬರು ಅರಸರಿಗೆ ಮಾಡಿದ್ದನ್ನು ನೀನೇ ನೋಡಿರುವೆ. ನೀನು ನದಿ ದಾಟಿಹೋಗುವ ಎಲ್ಲ ರಾಜ್ಯಗಳನ್ನೂ ಅವರು ಹಾಗೆಯೇ ನಾಶಮಾಡುವರು.",
			"22": "<sup>22</sup> ನೀವು ಅವರಿಗೆ ಭಯಪಡಬೇಡಿ; ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಸ್ವಾಮಿಯೇ ನಿಮ್ಮ ಕಡೆಯವರಾಗಿ ಯುದ್ಧಮಾಡುವರು’ ಎಂದು ಆಜ್ಞಾಪಿಸಿದೆ.",
			"23": "<sup>23</sup> :ಆ ಕಾಲದಲ್ಲಿ ನಾನು ಸರ್ವೇಶ್ವರ ಸ್ವಾಮಿಗೆ, ‘ಸ್ವಾಮಿ ಸರ್ವೇಶ್ವರಾ, ನೀವು ನಿಮ್ಮ ಮಹತ್ವವನ್ನೂ ಭುಜಬಲವನ್ನೂ ನಿಮ್ಮ ದಾಸನಿಗೆ ತೋರಿಸಲಾರಂಭಿಸಿದಿರಿ. ನೀವು ನಡೆಸುವ ಈ ಮಹತ್ಕಾರ್ಯಗಳಿಗೆ ಸಮಾನವಾದ ಕಾರ್ಯಗಳನ್ನು ಆಕಾಶದಲ್ಲಾಗಲಿ ಭೂಮಿಯಲ್ಲಾಗಲಿ ಬೇರೆ ಯಾವ ದೇವರು ತಾನೆ ನಡೆಸಬಲ್ಲನು?",
			"25": "<sup>25</sup> ಸರ್ವೇಶ್ವರಾ, ನಾನು ಕೂಡ ಈ ನದಿಯನ್ನು ದಾಟಿ, ಆಚೆಯಿರುವ ಒಳ್ಳೆಯ ನಾಡನ್ನು ಅಂದರೆ, ಆ ಅಂದವಾದ ಮಲೆನಾಡನ್ನೂ ಲೆಬನೋನ್ ಪರ್ವತವನ್ನೂ ನೋಡುವುದಕ್ಕೆ ಅಪ್ಪಣೆಯಾಗಲಿ’ ಎಂದು ಭಿನ್ನಯಿಸಿದೆ.",
			"26": "<sup>26</sup> ಆದರೆ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮ ನಿಮಿತ್ತ ನನ್ನ ಮೇಲೆ ಕೋಪಗೊಂಡರು. ನನ್ನ ಮನವಿಯನ್ನು ಕೇಳದೆ, ‘ಸಾಕು; ಇದರ ಬಗ್ಗೆ ಇನ್ನು ನನ್ನ ಸಂಗಡ ಮಾತಾಡಬೇಡ; ನೀನು ಜೋರ್ಡನ್ ನದಿಯನ್ನು ದಾಟಕೂಡದು.",
			"27": "<sup>27</sup> ಆದರೆ ಪಿಸ್ಗಾ ಬೆಟ್ಟವನ್ನು ಹತ್ತಿ, ತುದಿಯಲ್ಲಿ ನಿಂತು, ಚತುರ್ದಿಕ್ಕುಗಳಲ್ಲಿ ಸುತ್ತಲೂ ಇರುವ ನಾಡನ್ನು ಕಣ್ಣು ತುಂಬಾ ನೋಡಬಹುದು.",
			"28": "<sup>28</sup> ಯೆಹೋಶುವನೇ ಈ ಜನರ ನಾಯಕನಾಗಿ ಹೋಗಿ, ನೀನು ನೋಡುವ ನಾಡನ್ನು ಅವರಿಗೆ ಸ್ವಾಧೀನಪಡಿಸುವನು. ಅವನಿಗೇ ಈ ಅಧಿಕಾರವನ್ನು ಕೊಡಬೇಕು; ಅವನು ದೃಢನಾಗುವಂತೆ ಅವನನ್ನು ಧೈರ್ಯಪಡಿಸಬೇಕು,’ ಎಂದು ಉತ್ತರಕೊಟ್ಟರು.",
			"29": "<sup>29</sup> “ಆಗ ನಾವು ಬೇತ್‍ಪೆಗೋರಿಗೆ ಎದುರಾಗಿರುವ ಕಣಿವೆಯಲ್ಲಿ ವಾಸವಾಗಿದ್ದೆವು.”"
		},
		"4": {
			"1": "<sup>1</sup> “ಇಸ್ರಯೇಲರೇ, ಕೇಳಿ; ನೀವು ಜೀವದಿಂದುಳಿದು ನಿಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ಕೊಡುವ ನಾಡನ್ನು ಸೇರಿ ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳಬೇಕಾದರೆ ನಾನು ಈಗ ಬೋಧಿಸುವ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ಗಮನದಿಂದ ಅನುಸರಿಸಬೇಕು.",
			"2": "<sup>2</sup> ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಕೊಟ್ಟ ಆಜ್ಞೆಗಳನ್ನೇ ನಾನು ನಿಮಗೆ ತಿಳಿಸುತ್ತಾ ಇದ್ದೇನೆ. ಈ ಆಜ್ಞೆಗಳನ್ನು ನೀವು ಕೈಗೊಳ್ಳಬೇಕೇ ಹೊರತು ಅವುಗಳಿಗೆ ಏನೂ ಕೂಡಿಸಬಾರದು, ಅವುಗಳಿಂದ ಏನೂ ತೆಗೆದುಬಿಡಬಾರದು.",
			"3": "<sup>3</sup> ಪೆಗೋರದ ಬಾಳ್ ದೇವತೆಯ ವಿಷಯದಲ್ಲಿ ಸರ್ವೇಶ್ವರ ಮಾಡಿದ ಕಾರ್ಯವನ್ನು ನೀವು ನೋಡಿದ್ದೀರಷ್ಟೆ; ಆ ಪೆಗೋರದ ಬಾಳನನ್ನು ಅನುಸರಿಸಿದವರೆಲ್ಲರನ್ನು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮ ಮಧ್ಯೆಯಿಂದ ನಾಶಮಾಡಿದರಲ್ಲವೆ?",
			"4": "<sup>4</sup> ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನನ್ನು ಅವಲಂಭಿಸಿದ ನೀವಾದರೋ ಇಂದಿನವರೆಗೂ ಉಳಿದುಕೊಂಡಿದ್ದೀರಿ.",
			"5": "<sup>5</sup> “ನೀವು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವುದಕ್ಕೆ ಹೋಗುವ ನಾಡಿನಲ್ಲಿ ಅನುಸರಿಸಬೇಕಾದ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ನನ್ನ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನನಗೆ ಆಜ್ಞಾಪಿಸಿದಂತೆಯೇ ನಿಮಗೆ ಬೋಧಿಸಿದ್ದೇನೆ.",
			"6": "<sup>6</sup> ಇವುಗಳನ್ನು ಕೈಗೊಂಡು ಅನುಸರಿಸಿರಿ. ಅನುಸರಿಸಿದ್ದೇ ಆದರೆ ಅನ್ಯಜನಗಳು ನಿಮ್ಮನ್ನು ಜ್ಞಾನಿಗಳೂ ವಿವೇಕಿಗಳೂ ಎಂದು ತಿಳಿಯುವರು; ಈ ಆಜ್ಞೆಗಳ ಬಗ್ಗೆ ಅವರು ಅರಿತುಕೊಂಡಾಗ, ‘ಈ ದೊಡ್ಡ ಜನಾಂಗ ಎಂಥಾ ಜ್ಞಾನವಿವೇಕವುಳ್ಳ ಜನಾಂಗ’, ಎಂದು ಮಾತಾಡಿಕೊಳ್ಳುವರು.",
			"7": "<sup>7</sup> ನಾವು ಮೊರೆಯಿಡುವಾಗಲೆಲ್ಲಾ ನಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಮಗೆ ಸಮೀಪವಾಗಿರುವಂತೆ ಬೇರೆ ಯಾವ ಜನಾಂಗಕ್ಕೆ, ಅದೆಷ್ಟೇ ದೊಡ್ಡದಾಗಿರಲಿ, ಯಾವ ದೇವರು ಹೀಗೆ ಸಮೀಪವಾಗಿರುತ್ತಾರೆ?",
			"8": "<sup>8</sup> ನಾನು ಈ ದಿನ ನಿಮ್ಮ ಮುಂದಿಡುವ ಇಂಥ ನ್ಯಾಯಯುತವಾದ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ಒಳಗೊಂಡ ಈ ಧರ್ಮಶಾಸ್ತ್ರಕ್ಕೆ ಸಮಾನವಾದುದು ಬೇರೆ ಯಾವ ಜನಾಂಗಕ್ಕೆ ಉಂಟು?",
			"9": "<sup>9</sup> ಹೀಗಿರುವಲ್ಲಿ, ನೀವು ಬಹಳ ಜಾಗರೂಕತೆಯಿಂದಿರಿ; ನಿಮ್ಮ ಕಣ್ಣುಗಳಿಂದಲೇ ನೋಡಿದ ಘಟನೆಗಳನ್ನು ಎಷ್ಟು ಮಾತ್ರಕ್ಕೂ ಮರೆಯದಿರಿ; ಜೀವಮಾನಪರ್ಯಂತ ಇವುಗಳನ್ನು ನೆನಪಿನಲ್ಲಿ ಇಡಿ; ನಿಮ್ಮ ಮಕ್ಕಳಿಗೂ ಮೊಮ್ಮಕ್ಕಳಿಗೂ ತಿಳಿಸುತ್ತಾ ಹೋಗಿ,",
			"10": "<sup>10</sup> ಹೋರೇಬಿನಲ್ಲಿ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನೀವು ನಿಂತಿದ್ದಾಗ ಅವರು ನನಗೆ, ‘ಜನರೆಲ್ಲರನ್ನು ನನ್ನ ಹತ್ತಿರಕ್ಕೆ ಕರೆ; ಅವರೇ ನನ್ನ ಆಜ್ಞೆಗಳನ್ನು ಕೇಳಿಸಿಕೊಳ್ಳಲಿ; ಆಗ ಅವರು ತಮ್ಮ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ನನ್ನಲ್ಲಿ ಭಯಭಕ್ತಿಯಿಂದಿರಲು ಕಲಿತುಕೊಳ್ಳುವರು; ಅಂತೆಯೇ ತಮ್ಮ ಮಕ್ಕಳಿಗೂ ಕಲಿಸಿಕೊಡುವರು,’ ಎಂದರು.",
			"11": "<sup>11</sup> ಅಂದು ನೀವು ಹತ್ತಿರ ಬಂದು ಆ ಬೆಟ್ಟದ ಬುಡದಲ್ಲಿ ನಿಂತುಕೊಂಡಿರಿ. ಬೆಟ್ಟವು ಬೆಂಕಿಯಿಂದ ಪ್ರಜ್ವಲಿಸುತ್ತಿರಲು ಅಂತರಿಕ್ಷದವರೆಗೂ ಕರಿಮೋಡವೂ ಕಾರ್ಗತ್ತಲೂ ಆವರಿಸಿದ್ದವು.",
			"12": "<sup>12</sup> ಆ ಅಗ್ನಿಜ್ವಾಲೆಯಿಂದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮ ಸಂಗಡ ಮಾತಾಡಿದರು. ಆ ವಾಣಿಯ ಶಬ್ದವನ್ನು ನೀವು ಕೇಳಿದಿರಿ. ಆಕಾರ ಯಾವುದೂ ಕಾಣಿಸಲಿಲ್ಲ, ಸ್ವರ ಮಾತ್ರ ಕೇಳಿಸಿತು.",
			"13": "<sup>13</sup> ಆಗ ಅವರು ಹತ್ತು ಆಜ್ಞೆಗಳಿಂದ ಕೂಡಿದ ಒಡಂಬಡಿಕೆಯನ್ನು ಘೋಷಿಸಿದರು, ಅವನ್ನು ಪಾಲಿಸಬೇಕೆಂದರು, ಅವುಗಳನ್ನು ಎರಡು ಕಲ್ಲಿನ ಹಲಗೆಗಳಲ್ಲಿ ಬರೆದರು.",
			"14": "<sup>14</sup> ನೀವು ನದಿದಾಟಿ ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವ ನಾಡಿನಲ್ಲಿ ಅನುಸರಿಸಬೇಕಾದ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ನಿಮಗೆ ಬೋಧಿಸಬೇಕೆಂದು ಅಂದೇ ನನಗೆ ಆಜ್ಞಾಪಿಸಿದರು.",
			"15": "<sup>15</sup> “ಹೋರೇಬಿನಲ್ಲಿ” ಸರ್ವೇಶ್ವರ ಅಗ್ನಿಜ್ವಾಲೆಯೊಳಗಿಂದ ನಿಮ್ಮ ಸಂಗಡ ಮಾತನಾಡಿದರು. ಆಗ ನಿಮಗೆ ಯಾವ ಆಕಾರವೂ ಕಾಣಿಸಲಿಲ್ಲ. ಆದುದರಿಂದ ಬಹಳ ಎಚ್ಚರಿಕೆಯಿಂದಿರಿ.",
			"16": "<sup>16</sup> ನೀವು ಭ್ರಷ್ಟರಾಗದಂತೆ ವಿಗ್ರಹಗಳನ್ನು ಮಾಡಿಕೊಳ್ಳಬೇಡಿ. ಸ್ತ್ರೀಪುರುಷರ ರೂಪದಲ್ಲಾಗಲಿ,",
			"17": "<sup>17</sup> ಭೂಮಿಯ ಮೇಲೆ ಸಂಚರಿಸುವ ಮೃಗದ ರೂಪದಲ್ಲಾಗಲಿ, ಆಕಾಶದಲ್ಲಿ ಹಾರಾಡುವ ಪಕ್ಷಿಯ ರೂಪದಲ್ಲಾಗಲಿ,",
			"18": "<sup>18</sup> ನೆಲದ ಮೇಲೆ ಹರಿದಾಡುವ ಕ್ರಿಮಿಕೀಟಗಳ ರೂಪದಲ್ಲಾಗಲಿ, ಭೂಮಿಯ ಕೆಳಗಣ ಮೀನಿನ ರೂಪದಲ್ಲಾಗಲಿ ದೇವರ ಸ್ವರೂಪವೆಂದು ವಿಗ್ರಹಗಳನ್ನು ಮಾಡಿಕೊಳ್ಳಬೇಡಿ.",
			"19": "<sup>19</sup> ನೀವು ಆಕಾಶದ ಕಡೆಗೆ ಕಣ್ಣೆತ್ತಿ ನೋಡುವಾಗ ಭ್ರಮೆಗೊಂಡು, ಸೂರ್ಯ, ಚಂದ್ರ, ನಕ್ಷತ್ರಗಳೆನಿಸಿಕೊಳ್ಳುವ ಆಕಾಶದ ಪರಿವಾರಗಳನ್ನು ಆರಾಧಿಸಬಾರದು, ಪೂಜಿಸಬಾರದು. ಏಕೆಂದರೆ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಅವುಗಳನ್ನು ಜಗದ ಬೇರೆ ಎಲ್ಲ ಜನಾಂಗಗಳಿಗಾಗಿ ಕೊಟ್ಟಿದ್ದಾರೆ.",
			"20": "<sup>20</sup> ಆದರೆ ನಿಮ್ಮನ್ನು ತಮ್ಮ ಸ್ವಕೀಯ ಜನರನ್ನಾಗಿಸಿಕೊಳ್ಳಲು ಸಂಕಲ್ಪಿಸಿ ಕಬ್ಬಿಣ ಕರಗಿಸುವ ಕುಲುಮೆಯಂತಿದ್ದ ಆ ಈಜಿಪ್ಟ್ ದೇಶದಿಂದ ನಿಮ್ಮನ್ನು ಕರೆದುತಂದು ಇದ್ದಾರೆ ಆ ಸರ್ವೇಶ್ವರ. ಅಂತೆಯೇ ನೀವು ಇಂದಿಗೂ ಅವರ ಸ್ವಂತ ಜನರಾಗಿದ್ದೀರಿ.",
			"21": "<sup>21</sup> “ಇದಲ್ಲದೆ, ಸರ್ವೇಶ್ವರ ನಿಮ್ಮ ದೆಸೆಯಿಂದ ನನ್ನ ಮೇಲೆ ಕೋಪಗೊಂಡರು. ನಾನು ಜೋರ್ಡನ್ ನದಿಯನ್ನು ದಾಟಿ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ಸ್ವದೇಶವನ್ನಾಗಿ ಕೊಡುವ ಆ ಚೆಲುವ ನಾಡನ್ನು ನಾನು ಸೇರಬಾರದೆಂದೂ ಇಲ್ಲಿಯೇ ಸಾಯಬೇಕೆಂದೂ ಖಂಡಿತವಾಗಿ ಹೇಳಿದ್ದಾರೆ.",
			"22": "<sup>22</sup> ನೀವಾದರೋ ನದಿ ದಾಟಿ ಆ ಸುಂದರ ನಾಡನ್ನು ಸೇರಿ ಸ್ವಾಧೀನ ಮಾಡಿಕೊಳ್ಳುವಿರಿ.",
			"23": "<sup>23</sup> “ಎಚ್ಚರಿಕೆಯಾಗಿರಿ; ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮ ಸಂಗಡ ಮಾಡಿಕೊಂಡ ಒಡಂಬಡಿಕೆಯನ್ನು ಮರೆತುಬಿಡಬೇಡಿ. ಅವರು ಪೂಜಿಸಬೇಡಿರೆಂದು ನಿಷೇಧಿಸಿದ ಯಾವ ವಿಗ್ರಹ ವಸ್ತುಗಳನ್ನೂ ಮಾಡಿಕೊಳ್ಳಬೇಡಿ.",
			"24": "<sup>24</sup> ಏಕೆಂದರೆ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಕಬಳಿಸಿಬಿಡುವ ಅಗ್ನಿಯಂಥವರು; ತಮಗೆ ಸಲ್ಲಬೇಕಾದ ಗೌರವವನ್ನು ಮತ್ತೊಬ್ಬನಿಗೆ ಸಲ್ಲಗೊಡದ ದೇವರು.",
			"25": "<sup>25</sup> “ನೀವು ಮಕ್ಕಳನ್ನೂ ಮೊಮ್ಮಕ್ಕಳನ್ನೂ ಪಡೆಯುವಷ್ಟು ಕಾಲ ಆ ನಾಡಿನಲ್ಲಿದ್ದು, ಯಾವುದಾದರೊಂದು ವಿಗ್ರಹವನ್ನು ಮಾಡಿಕೊಂಡು ದ್ರೋಹಿಗಳಾಗಿ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ಕೆಟ್ಟದ್ದನ್ನು ಮಾಡಿ ಅವರನ್ನು ಸಿಟ್ಟುಗೊಳಿಸಿದ್ದೇ ಆದರೆ",
			"26": "<sup>26</sup> ಇಗೋ, ಇಂದೇ ನಿಮಗೆ ವಿರುದ್ಧ ಭೂಮ್ಯಾಕಾಶಗಳನ್ನು ಸಾಕ್ಷಿಗಳಾಗಿಟ್ಟು ಹೇಳುತ್ತಿದ್ದೇನೆ: ನೀವು ಜೋರ್ಡನ್ ನದಿಯನ್ನು ದಾಟಿ ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವ ಆ ನಾಡಿನಲ್ಲಿ ಉಳಿಯದೆ ಬೇಗನೆ ನಾಶವಾಗಿ ಹೋಗುವಿರಿ.",
			"27": "<sup>27</sup> ಸರ್ವೇಶ್ವರ ನಿಮ್ಮನ್ನು ಅನ್ಯಜನಗಳ ನಡುವೆ ಚದರಿಸಿಬಿಡುವರು. ಹೀಗೆ ಓಡಿಸಿಬಿಡಲು, ಆ ಅನ್ಯನಾಡಿನ ಜನರ ನಡುವೆ ನೀವು ಸ್ವಲ್ಪ ಮಂದಿ ಮಾತ್ರ ಅಳಿದುಳಿಯುವಿರಿ.",
			"28": "<sup>28</sup> ಅಲ್ಲಿ ಮಾನವರ ಕೈಯಿಂದ ಮಾಡಲಾದ ಮರ ಮತ್ತು ಕಲ್ಲಿನ ದೇವರುಗಳನ್ನು ಪೂಜಿಸುವಿರಿ; ಅವುಗಳಿಂದ ನೋಡುವುದಕ್ಕಾಗಲಿ, ಕೇಳುವುದಕ್ಕಾಗಲಿ, ತಿನ್ನಲಿಕ್ಕಾಗಲಿ, ಮೂಸುವುದಕ್ಕಾಗಲಿ ಆಗದು.",
			"29": "<sup>29</sup> “ಅಲ್ಲಿಂದಾದರೂ ನೀವು ಪೂರ್ಣಹೃದಯದಿಂದ, ಪೂರ್ಣಮನಸ್ಸಿನಿಂದ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನನ್ನು ಅರಸಿದರೆ ಅವರು ನಿಮಗೆ ಸಿಗುವರು.",
			"30": "<sup>30</sup> ಮೇಲೆ ಹೇಳಿದ ಎಲ್ಲ ಕಷ್ಟಸಂಕಟಗಳು ನಿಮಗೆ ಸಂಭವಿಸಿ, ಕೊನೆಗೆ ನೀವು ಅವರಿಗೆ ಅಭಿಮುಖರಾಗಿ ಅವನ ಮಾತಿಗೆ ಕಿವಿಗೊಡುವಿರಿ.",
			"31": "<sup>31</sup> ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ದಯಾಮಯ ದೇವರು. ಅವರು ನಿಮ್ಮನ್ನು ಅಲಕ್ಷ್ಯ ಮಾಡುವುದಿಲ್ಲ. ವಿನಾಶಕ್ಕೆ ಬಿಟ್ಟುಬಿಡುವುದಿಲ್ಲ. ನಿಮ್ಮ ಪೂರ್ವಜರ ಸಂಗಡ ಅವರು ಪ್ರಮಾಣಪೂರ್ವಕವಾಗಿ ಮಾಡಿಕೊಂಡ ಒಡಂಬಡಿಕೆಯನ್ನು ಮರೆತುಬಿಡುವುದಿಲ್ಲ.",
			"32": "<sup>32</sup> “ದೇವರು ಮಾನವರನ್ನು ಸೃಷ್ಟಿಸಿ ಭೂಮಿಯ ಮೇಲಿರಿಸಿದ ದಿನ ಮೊದಲ್ಗೊಂಡು ಇಂದಿನವರೆಗೆ, ಒಂದು ದಿಕ್ಕಿನಿಂದ ಮತ್ತೊಂದು ದಿಕ್ಕಿನವರೆಗೆ, ಅಂಥ ಅದ್ಭುತಕಾರ್ಯ ನಡೆದದ್ದುಂಟೇ? ಅಂಥ ಸುದ್ದಿಯನ್ನಾದರೂ ಕೇಳಿದ್ದುಂಟೇ? ನೀವೇ ವಿಚಾರಿಸಿಕೊಳ್ಳಿ.",
			"33": "<sup>33</sup> ದೇವರು ಅಗ್ನಿಜ್ವಾಲೆಯೊಳಗಿಂದ ಮಾತಾಡಿದ ಸ್ವರ ಕೇಳಿಸಿತಲ್ಲವೆ? ಬೇರೆ ಯಾವ ಜನರಾದರು ದೇವರ ಸ್ವರ ಕೇಳಿ ಬದುಕಿದ್ದುಂಟೇ?",
			"34": "<sup>34</sup> ಬೇರೆ ಯಾವ ದೇವರು ತಾನೆ ಪರಿಶೋಧನೆ, ಪವಾಡ, ಮಹತ್ಕಾರ್ಯ, ಯುದ್ಧ, ಭುಜಪರಾಕ್ರಮ, ಶಿಕ್ಷಾಹಸ್ತ, ಭಯಂಕರಕಾರ್ಯ ಇವುಗಳನ್ನು ಪ್ರಯೋಗಿಸಿ ಒಂದು ಜನಾಂಗವನ್ನು ಮತ್ತೊಂದು ಜನಾಂಗದ ಕೈಯಿಂದ ತಪ್ಪಿಸಲು ಪ್ರಯತ್ನಿಸಿದ್ದಾರೆ? ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಸ್ವಾಮಿಯಾದರೊ ಈಜಿಪ್ಟಿನಲ್ಲಿ ನಿಮ್ಮ ಪರವಾಗಿ, ಇದನ್ನೆಲ್ಲ ನಿಮ್ಮ ಕಣ್ಮುಂದೆಯೇ, ನಡೆಸಿದರಲ್ಲವೆ?",
			"35": "<sup>35</sup> ಸರ್ವೇಶ್ವರ ಸ್ವಾಮಿಯೊಬ್ಬರೇ ದೇವರು, ಬೇರೆ ದೇವರೇ ಇಲ್ಲವೆಂದು ನೀವು ಅರಿತುಕೊಳ್ಳುವುದಕ್ಕಾಗಿ ಇದನ್ನೆಲ್ಲಾ ನಿಮಗೆ ಮಾತ್ರ ತೋರಿಸಲಾಗಿದೆ.",
			"36": "<sup>36</sup> ನೀವು ಕಲಿತುಕೊಳ್ಳಬೇಕೆಂದೇ ಸರ್ವೇಶ್ವರ ಆಕಾಶದಿಂದ ತಮ್ಮ ಸ್ವರ ನಿಮಗೆ ಕೇಳಿಸುವ ಹಾಗೆ ಮಾಡಿದರು; ತಾವಿದ್ದ ಮಹಾ ಅಗ್ನಿಜ್ವಾಲೆಯನ್ನು ಭೂಮಿಯ ಮೇಲೆ ನೀವು ನೋಡುವಂತೆ ಮಾಡಿದರು; ಆ ಅಗ್ನಿಜ್ವಾಲೆಯ ಒಳಗಿಂದ ಅವರು ಆಡಿದ ಮಾತುಗಳನ್ನು ನೀವು ಕೇಳಿದಿರಿ.",
			"37": "<sup>37</sup> ನಿಮ್ಮ ಪಿತೃಗಳನ್ನು ಪ್ರೀತಿಸಿ, ತರುವಾಯ ಅವರ ಸಂತತಿಯಾದ ನಿಮ್ಮನ್ನೂ ಆರಿಸಿಕೊಂಡರು.",
			"38": "<sup>38</sup> ಈಗ ನಮಗೆ ತಿಳಿದಿರುವಂತೆ ನಿಮಗಿಂತ ದೊಡ್ಡವೂ ಬಲಿಷ್ಠವೂ ಆದ ಜನಾಂಗಗಳನ್ನು ಹೊರಡಿಸಿ ಅವರ ನಾಡಲ್ಲೆ ನಿಮ್ಮನ್ನು ಸೇರಿಸಿ, ಆ ನಾಡನ್ನು ನಿಮಗೇ ಸ್ವಂತ ನಾಡನ್ನಾಗಿ ಕೊಡಲು ಸಂಕಲ್ಪಿಸಿದ್ದಾರೆ. ತಾವೇ ನಿಮ್ಮೊಂದಿಗಿದ್ದು, ತಮ್ಮ ಅಪಾರ ಸಾಮರ್ಥ್ಯದಿಂದ ನಿಮ್ಮನ್ನು ಈಜಿಪ್ಟ್ ದೇಶದಿಂದ ಕರೆದುತಂದಿದ್ದಾರೆ.",
			"39": "<sup>39</sup> ನೀವು ಇದನ್ನೆಲ್ಲಾ ಆಲೋಚಿಸಿ, ಮೇಲೆ ಆಕಾಶದಲ್ಲಾಗಲಿ, ಕೆಳಗೆ ಭೂಮಿಯಲ್ಲಾಗಲಿ ಸರ್ವೇಶ್ವರಸ್ವಾಮಿ ಒಬ್ಬರೇ ದೇವರು, ಬೇರೆ ಯಾವ ದೇವರೂ ಇಲ್ಲವೆಂಬುದನ್ನು ಈಗ ಗ್ರಹಿಸಿಕೊಂಡು ಜ್ಞಾಪಕದಲ್ಲಿ ಇಡಬೇಕು.",
			"40": "<sup>40</sup> ನಾನು ಇಂದು ನಿಮಗೆ ತಿಳಿಸಿದ ಅವರ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ಅನುಸರಿಸಿ ನಡೆಯಿರಿ. ಆಗ ನಿಮಗೂ ನಿಮ್ಮ ಸಂತತಿಗೂ ಶುಭವುಂಟಾಗುವುದು; ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ಶಾಶ್ವತವಾಗಿ ಕೊಡುವ ನಾಡಿನಲ್ಲಿ ನೀವು ದೀರ್ಘಕಾಲ ಬಾಳುವಿರಿ.”",
			"41": "<sup>41</sup> ಆ ಕಾಲದಲ್ಲಿ ಮೋಶೆ ಜೋರ್ಡನ್ ನದಿಯ ಪೂರ್ವದಿಕ್ಕಿನಲ್ಲಿ ಮೂರು ಆಶ್ರಯ ನಗರಗಳನ್ನು ಗೊತ್ತುಮಾಡಿದನು.",
			"42": "<sup>42</sup> ಪೂರ್ವಕಲ್ಪಿತ ಯಾವ ದ್ವೇಷವೂ ಇಲ್ಲದೆ, ಕೈತಪ್ಪಿ, ಮತ್ತೊಬ್ಬನನ್ನು ಕೊಂದವನು ಆ ನಗರಗಳಲ್ಲಿ ಒಂದಕ್ಕೆ ಓಡಿಹೋಗಿ ಬದುಕಿಕೊಳ್ಳಲಿ ಎಂದು ಆ ನಗರಗಳನ್ನು ನೇಮಿಸಿದನು.",
			"43": "<sup>43</sup> ರೂಬೇನ್ಯರಿಗಾಗಿ ಮೀಶೋರ್ ಎಂಬ ಎತ್ತರವಾದ ಬಯಲು ಸೀಮೆಯ ಮರುಭೂಮಿಯಲ್ಲಿ ಇದ್ದ ಬೆಚೆರ್, ಗಾದ್ಯರಿಗಾಗಿ ಗಿಲ್ಯಾದಿನಲ್ಲಿದ್ದ ರಾಮೋತ್, ಹಾಗು ಮನಸ್ಸೆಯವರಿಗಾಗಿ ಬಾಷಾನಿನಲ್ಲಿದ್ದ ಗೋಲಾನ್ ಇವೇ ಆ ಆಶ್ರಯನಗರಗಳು.",
			"44": "<sup>44</sup> ಮೋಶೆ ಇಸ್ರಯೇಲರಿಗೆ ಕೊಟ್ಟ ಧರ್ಮಶಾಸ್ತ್ರ:",
			"45": "<sup>45</sup> ಇಸ್ರಯೇಲರು ಈಜಿಪ್ಟ್ ದೇಶದಿಂದ ಬಂದಾಗ ಮೋಶೆ ಅವರಿಗೆ ತಿಳಿಸಿದ ಆಜ್ಞಾವಿಧಿನಿರ್ಣಯಗಳು ಇವು:",
			"46": "<sup>46</sup> ಜೋರ್ಡನ್ ನದಿಯ ಆಚೆ ಬೇತ್ ಪೆಗೋರಿಗೆ ಎದುರಾಗಿರುವ ಕಣಿವೆಯ ಹೆಷ್ಬೋನಿನಲ್ಲಿ ವಾಸಿಸಿದ ಅಮೋರಿಯರ ಅರಸ ಸೀಹೋನನ ದೇಶದಲ್ಲಿ ಇದು ನಡೆಯಿತು. ಮೋಶೆಯೂ ಇಸ್ರಯೇಲರೂ ಈಜಿಪ್ಟಿನಿಂದ ಬಂದಾಗ ಆ ಸೀಹೋನನನ್ನು ಜಯಿಸಿದ್ದರು.",
			"47": "<sup>47</sup> ಅವನ ರಾಜ್ಯವನ್ನೂ ಬಾಷಾನ್ ದೇಶದ ಅರಸ ಓಗನ ರಾಜ್ಯವನ್ನೂ ಜೋರ್ಡನ್ ನದಿಯ ಆಚೆ ಪೂರ್ವದಿಕ್ಕಿನಲ್ಲಿದ್ದ ಅಮೋರಿಯರ ಇಬ್ಬರು ಅರಸರ ರಾಜ್ಯಗಳನ್ನೂ ಗೆದ್ದುಕೊಂಡಿದ್ದರು.",
			"48": "<sup>48</sup> ಅರ್ನೋನ್ ತಗ್ಗಿನ ಅಂಚಿನಲ್ಲಿರುವ ಅರೋಯೇರ್ ಪಟ್ಟಣ ಮೊದಲ್ಗೊಂಡು ಹೆರ್ಮೋನ್ ಎಂಬ ಸೀಯೋನ್ ಪರ್ವತದವರೆಗೂ",
			"49": "<sup>49</sup> ಅರಾಬ ಎಂಬ ತಗ್ಗಿನಲ್ಲಿ ಪಿಸ್ಗಾ ಬೆಟ್ಟದ ಬುಡದಲ್ಲಿ ಇರುವ ಅರಾಬದ ಸಮುದ್ರದವರೆಗೆ ಜೋರ್ಡನ್ ನದಿಯ ಆಚೆ ಇರುವ ಭಾಗವನ್ನೆಲ್ಲಾ ಸ್ವಾಧೀನ ಮಾಡಿಕೊಂಡಿದ್ದರು."
		},
		"5": {
			"1": "<sup>1</sup> ಮೋಶೆ ಇಸ್ರಯೇಲರನ್ನು ಕರೆದು ಹೀಗೆ ಸಂಬೋಧಿಸಿದನು: “ಇಸ್ರಯೇಲರೇ, ನಾನು ಈಗ ನಿಮಗೆ ತಿಳಿಸಲಿರುವ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ಕಿವಿಗೊಟ್ಟು ಕೇಳಿ; ಇವುಗಳನ್ನು ಗ್ರಹಿಸಿಕೊಂಡು ಅನುಸರಿಸಿ ನಡೆಯಿರಿ.",
			"2": "<sup>2</sup> ನಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಹೋರೇಬಿನಲ್ಲಿ ನಮ್ಮೊಂದಿಗೆ ಒಂದು ಒಡಂಬಡಿಕೆಯನ್ನು ಮಾಡಿಕೊಂಡರು.",
			"3": "<sup>3</sup> ನಮ್ಮ ಪೂರ್ವಿಕರ ಸಂಗಡ ಈ ಒಡಂಬಡಿಕೆಯನ್ನು ಮಾಡದೆ ಈಗ ಇಲ್ಲಿ ಜೀವದಿಂದಿರುವ ನಮ್ಮ ಸಂಗಡವೇ ಇದನ್ನು ಮಾಡಿದರು.",
			"4": "<sup>4</sup> ಸರ್ವೇಶ್ವರ ಆ ಬೆಟ್ಟದ ಮೇಲೆ ಅಗ್ನಿಜ್ವಾಲೆಯೊಳಗಿಂದ ಮುಖಾಮುಖಿಯಾಗಿ ನಮ್ಮೊಂದಿಗೆ ಮಾತಾಡಿದರು.",
			"5": "<sup>5</sup> ನೀವು ಆ ಅಗ್ನಿಜ್ವಾಲೆಯನ್ನು ನೋಡಿ ಭಯಪಟ್ಟಿರಿ; ಬೆಟ್ಟವನ್ನು ಹತ್ತದೆ ಹೋದಿರಿ! ಆಗ ನಾನು ಸರ್ವೇಶ್ವರನಿಗೂ ನಿಮಗೂ ನಡುವೆ ನಿಂತು ಅವರು ನುಡಿದ ಮಾತುಗಳನ್ನು ನಿಮಗೆ ತಿಳಿಸಿದೆನು. ಅವರು ನುಡಿದದ್ದು ಇದು:",
			"6": "<sup>6</sup> ‘ನಾನೇ ನಿನ್ನ ದೇವರಾದ ಸರ್ವೇಶ್ವರ; ಗುಲಾಮತನದ ನಾಡಾಗಿದ್ದು, ಈಜಿಪ್ಟಿನಿಂದ ನಿನ್ನನ್ನು ಬಿಡುಗಡೆ ಮಾಡಿದವನು.",
			"7": "<sup>7</sup> ನಾನಲ್ಲದೆ ನಿನಗೆ ಬೇರೆ ದೇವರುಗಳು ಇರಕೂಡದು.",
			"8": "<sup>8</sup> ‘ಆಕಾಶದಲ್ಲಾಗಲಿ, ಭೂಮಿಯಲ್ಲಾಗಲಿ, ಭೂಮಿಯ ಕೆಳಗಿನ ನೀರಿನಲ್ಲಾಗಲಿ ಇರುವ ಯಾವುದರ ರೂಪವನ್ನೂ ಅಥವಾ ವಿಗ್ರಹವನ್ನೂ ಮಾಡಿಕೊಳ್ಳಬೇಡ.",
			"9": "<sup>9</sup> ಅವುಗಳಿಗೆ ಅಡ್ಡಬೀಳಬೇಡ. ಅವುಗಳನ್ನು ಆರಾಧಿಸಬೇಡ. ಏಕೆಂದರೆ ನಾನೇ ನಿನ್ನ ದೇವರಾದ ಸರ್ವೇಶ್ವರ, ನನಗೆ ಸಲ್ಲತಕ್ಕ ಗೌರವವನ್ನು ನಾನು ಮತ್ತೊಬ್ಬನಿಗೆ ಸಲ್ಲಗೊಡಿಸುವುದಿಲ್ಲ. ನನ್ನನ್ನು ದ್ವೇಷಿಸುವವರನ್ನು ದಂಡಿಸುತ್ತೇನೆ; ಹೆತ್ತವರ ದೋಷಫಲವನ್ನು ಅವರ ಮಕ್ಕಳ ಮೇಲೆ ಮೂರು ನಾಲ್ಕು ತಲೆಗಳವರೆಗೆ ಬರಮಾಡುತ್ತೇನೆ.",
			"10": "<sup>10</sup> ನನ್ನನ್ನು ಪ್ರೀತಿಸಿ ನನ್ನ ಆಜ್ಞೆಗಳನ್ನು ಕೈಗೊಳ್ಳುವವರಿಗಾದರೋ ಸಾವಿರ ತಲೆಗಳವರೆಗೆ ದಯೆತೋರಿಸುತ್ತೇನೆ.",
			"11": "<sup>11</sup> ‘ನಿನ್ನ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಹೆಸರನ್ನು ದುರುಪಯೋಗಿಸಿಕೊಳ್ಳಬೇಡ. ಏಕೆಂದರೆ ಸರ್ವೇಶ್ವರನಾದ ನಾನು ನನ್ನ ಹೆಸರನ್ನು ದುರುಪಯೋಗಿಸಿಕೊಳ್ಳುವವನನ್ನು ಶಿಕ್ಷಿಸದೆ ಬಿಡುವುದಿಲ್ಲ.",
			"12": "<sup>12</sup> ‘ನಿನ್ನ ದೇವರೂ ಸರ್ವೇಶ್ವರನೂ ಆದ ನಾನು ಆಜ್ಞಾಪಿಸಿದಂತೆ ನೀನು ವಿಶ್ರಾಂತಿ ದಿನವನ್ನು ಪವಿತ್ರವಾಗಿ ಆಚರಿಸು.",
			"13": "<sup>13</sup> ಆರು ದಿನಗಳು ದುಡಿದು ನಿನ್ನ ಕೆಲಸವನ್ನೆಲ್ಲ ಮಾಡು.",
			"14": "<sup>14</sup> ಏಳನೆಯ ದಿನ ನಿನ್ನ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ಮೀಸಲಾದ ವಿಶ್ರಾಂತಿ ದಿನ. ಅಂದು ನೀನು ಯಾವ ಕೆಲಸವನ್ನೂ ಮಾಡಬಾರದು. ನಿನ್ನ ಗಂಡುಹೆಣ್ಣು ಮಕ್ಕಳು, ನಿನ್ನ ಗಂಡುಹೆಣ್ಣು ಆಳುಗಳು, ಎತ್ತುಕತ್ತೆಗಳು, ಪಶುಪ್ರಾಣಿಗಳು, ಹಾಗು ನಿನ್ನ ಊರಲ್ಲಿರುವ ಅನ್ಯದೇಶದವರು ಯಾವ ಕೆಲಸವನ್ನೂ ಮಾಡಬಾರದು. ನಿನ್ನಂತೆಯೇ ನಿನ್ನ ಗಂಡಾಳು ಹೆಣ್ಣಾಳುಗಳಿಗೂ ವಿಶ್ರಾಂತಿ ದೊರೆಯಬೇಕು.",
			"15": "<sup>15</sup> ಈಜಿಪ್ಟ್ ದೇಶದಲ್ಲಿ ನೀವೂ ಗುಲಾಮತನದಲ್ಲಿದ್ದುದನ್ನೂ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಭುಜಪರಾಕ್ರಮದಿಂದ ಹಾಗು ಶಿಕ್ಷಾಹಸ್ತದಿಂದ ನಿಮ್ಮನ್ನು ಬಿಡುಗಡೆಮಾಡಿದ್ದನ್ನೂ ನೆನಪಿಗೆ ತಂದುಕೊಳ್ಳಿ. ನೀವು ವಿಶ್ರಾಂತಿ ದಿನವನ್ನು ಆಚರಿಸಬೇಕೆಂಬುದನ್ನು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಇದಕ್ಕಾಗಿಯೇ ಆಜ್ಞಾಪಿಸಿದ್ದಾರೆ.",
			"16": "<sup>16</sup> ‘ನಿನ್ನ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಆಜ್ಞಾಪಿಸಿದಂತೆ, ನಿನ್ನ ತಂದೆತಾಯಿಗಳನ್ನು ಗೌರವಿಸು; ಗೌರವಿಸಿದರೆ ನಿನ್ನ ದೇವರೂ ಸರ್ವೇಶ್ವರನೂ ಆದ ನಾನು ನಿನಗೆ ಅನುಗ್ರಹಿಸುವ ನಾಡಿನಲ್ಲಿ ಬಹುಕಾಲ ಸುಕ್ಷೇಮವಾಗಿ ಬಾಳುವೆ.",
			"17": "<sup>17</sup> ‘ಕೊಲೆ ಮಾಡಬೇಡ;",
			"18": "<sup>18</sup> ‘ವ್ಯಭಿಚಾರ ಮಾಡಬೇಡ;",
			"19": "<sup>19</sup> ‘ಕದಿಯಬೇಡ;",
			"20": "<sup>20</sup> ‘ನೆರೆಯವನ ವಿರುದ್ಧ ಸುಳ್ಳುಸಾಕ್ಷಿ ಹೇಳಬೇಡ;",
			"21": "<sup>21</sup> ‘ನೆರೆಯವನ ಹೆಂಡತಿಯನ್ನು ಅಪೇಕ್ಷಿಸಬೇಡ; ಅವನ ಮನೆ, ಹೊಲ, ಗಂಡಾಳು, ಹೆಣ್ಣಾಳು, ಎತ್ತು, ಕತ್ತೆ ಮುಂತಾದ ಯಾವುದನ್ನೂ ಬಯಸಬಾರದು,.",
			"22": "<sup>22</sup> “ಈ ಮಾತುಗಳನ್ನು ಸರ್ವೇಶ್ವರ ಆ ಬೆಟ್ಟದ ಮೇಲೆ ಬೆಂಕಿ, ಮೋಡ, ಕಾರ್ಗತ್ತಲು, ಇವುಗಳ ಮಧ್ಯೆ ನಿಮ್ಮ ಸರ್ವಸಮೂಹಕ್ಕೆ ಕೇಳಿಸುವಂತೆ ಮಹಾಸ್ವರದಿಂದ ನುಡಿದರು; ಬೇರೆ ಏನನ್ನೂ ಕೂಡಿಸದೆ, ಈ ಮಾತುಗಳನ್ನು ಎರಡು ಕಲ್ಲಿನ ಹಲಗೆಗಳಲ್ಲಿ ಬರೆದು ನನಗೆ ಕೊಟ್ಟರು.",
			"23": "<sup>23</sup> “ಆ ಬೆಟ್ಟ ಬೆಂಕಿಯಿಂದ ಪ್ರಜ್ವಲಿಸುತ್ತಿರುವಾಗ, ಆ ಕತ್ತಲೊಳಗಿಂದ ದೇವರ ಸ್ವರ ಕೇಳಿ ಬಂದಾಗ, ನೀವು, ನಿಮ್ಮ ಕುಲಾಧಿಪತಿಗಳು, ಹಿರಿಯರು ನನ್ನ ಬಳಿಗೆ ಬಂದಿರಿ.",
			"24": "<sup>24</sup> ‘ಇಗೋ, ನಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ತಮ್ಮ ಪ್ರತಿಭೆಯನ್ನೂ ಮಹಿಮೆಯನ್ನೂ ನಮಗೆ ತೋರಿಸಿದ್ದಾರೆ; ಅಗ್ನಿಜ್ವಾಲೆಯೊಳಗಿಂದ ತಮ್ಮ ಸ್ವರವನ್ನು ನಮಗೆ ಕೇಳಿಸಿದ್ದಾರೆ; ದೇವರು ಮನುಷ್ಯನೊಡನೆ ಮಾತಾಡಿದ್ದನ್ನೂ, ಆದರೂ ಆ ಮನುಷ್ಯ ಸಾಯದೆ ಬದುಕಿರುವುದನ್ನೂ ಇಂದು ನಾವು ಕಂಡೆವು.",
			"25": "<sup>25</sup> ಹಾಗಿದ್ದರೂ ಈಗ ನಾವೇಕೆ ಪ್ರಾಣಾಪಾಯಕ್ಕೆ ಗುರಿಯಾಗಬೇಕು? ಬಹುಶಃ ಈ ಘೋರ ಬೆಂಕಿ ನಮ್ಮನ್ನು ದಹಿಸಿಬಿಡಬಹುದು; ನಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಸ್ವರವನ್ನು ನಾವು ಮತ್ತೆ ಕೇಳಿದರೆ ಸಾಯಬಹುದು.",
			"26": "<sup>26</sup> ಅಗ್ನಿಜ್ವಾಲೆಯೊಳಗಿಂದ ಮಾತಾಡುವ ಚೈತನ್ಯಸ್ವರೂಪರಾದ ದೇವರ ಸ್ವರವನ್ನು ನಮ್ಮಂತೆ ಕೇಳಿಯೂ ಬದುಕಿರುವ ಮಾನವ ಯಾರಿದ್ದಾನೆ?",
			"27": "<sup>27</sup> ನೀನೇ ಹತ್ತಿರಕ್ಕೆ ಹೋಗಿ, ನಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಆಜ್ಞೆಗಳನ್ನೆಲ್ಲಾ ತಿಳಿದುಕೊಂಡು ಬಂದು ನಮಗೆ ತಿಳಿಸು; ತಿಳಿಸಿದಾಗ ನಾವು ಅದರಂತೆ ನಡೆಯುತ್ತೇವೆ’ ಎಂದು ಹೇಳಿದಿರಿ.",
			"28": "<sup>28</sup> “ನೀವು ನನಗೆ ಹೇಳಿದ ಈ ಮಾತುಗಳನ್ನು ಸರ್ವೇಶ್ವರಸ್ವಾಮಿ ಕೇಳಿ ನನಗೆ ಹೀಗೆಂದರು: ‘ಈ ಜನರು ನಿನಗೆ ಹೇಳಿದ ಮಾತುಗಳನ್ನು ಕೇಳಿದ್ದೇನೆ; ಅವರು ಹೇಳಿದ್ದು ಸರಿಯಾಗಿದೆ.",
			"29": "<sup>29</sup> ನನ್ನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿ, ನನ್ನ ಆಜ್ಞೆಗಳನ್ನು ಅನುಸರಿಸುವ ಮನಸ್ಸು ಸದಾ ಅವರಲ್ಲಿದ್ದರೆ ಎಷ್ಟೋ ಒಳ್ಳೆಯದು; ಆಗ ಅವರಿಗೂ ಅವರ ಸಂತತಿಗೂ ಯಾವಾಗಲೂ ಶುಭವುಂಟಾಗುವುದು.",
			"30": "<sup>30</sup> ನೀನು ಹೋಗಿ ಅವರಿಗೆ, ‘ನಿಮ್ಮ ನಿಮ್ಮ ಡೇರೆಗಳಿಗೆ ಹಿಂದಿರುಗಿರಿ’, - ಎಂದು ಆಜ್ಞಾಪಿಸು.",
			"31": "<sup>31</sup> ನೀನು ಇಲ್ಲೇ ನನ್ನ ಬಳಿ ಇರು; ಅವರಿಗೆ ಬೋಧಿಸಬೇಕಾದ ಎಲ್ಲ ಆಜ್ಞೆಗಳನ್ನೂ ವಿಧಿನಿರ್ಣಯಗಳನ್ನೂ ನಿನಗೆ ತಿಳಿಸುತ್ತೇನೆ. ನಾನು ಅವರಿಗೆ ಸ್ವಂತಕ್ಕಾಗಿ ಕೊಡುವ ನಾಡಿನಲ್ಲಿ ಅವರು ಅವುಗಳನ್ನು ಅನುಸರಿಸಬೇಕು,’ ಎಂದು ಹೇಳಿದರು.",
			"32": "<sup>32</sup> “ಆದುದರಿಂದ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಆಜ್ಞಾಪಿಸಿದಂತೆಯೇ ನಡೆದುಕೊಳ್ಳಿ, ಎಡಕ್ಕಾಗಲಿ ಬಲಕ್ಕಾಗಲಿ ತಿರುಗಿಕೊಳ್ಳಬೇಡಿ. ಬದಲಿಗೆ, ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಆಜ್ಞಾಪಿಸಿದ ಮಾರ್ಗದಲ್ಲೇ ಮುನ್ನಡೆಯಿರಿ.",
			"33": "<sup>33</sup> ಆಗ, ನೀವು ಸ್ವಾಧೀನಪಡಿಸಿಕೊಳ್ಳಲಿರುವ ನಾಡಿನಲ್ಲಿ ದೀರ್ಘಕಾಲ ಸುಕ್ಷೇಮದಿಂದ ಬಾಳುವಿರಿ.”"
		},
		"6": {
			"1": "<sup>1</sup> “ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಸ್ವಾಮಿ ನಿಮಗೆ ಬೋಧಿಸಲು ನನಗೆ ಆಜ್ಞಾಪಿಸಿದ ಈ ಧರ್ಮೋಪದೇಶವನ್ನೂ ವಿಧಿನಿರ್ಣಯಗಳನ್ನೂ ನೀವು ನದಿದಾಟಿ ಸ್ವಾಧೀನಪಡಿಸಿಕೊಳ್ಳಲಿರುವ ಆ ನಾಡಿನಲ್ಲಿ ಅನುಸರಿಸಬೆಕು.",
			"2": "<sup>2</sup> ನೀವು ಹಾಗು ನಿಮ್ಮ ಪುತ್ರಪೌತ್ರಾದಿ ಸಂತತಿಯವರು ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿರಬೇಕು; ನಾನು ಈಗ ಬೋಧಿಸುವ ಅವರ ವಿಧಿನಿರ್ಣಯಗಳನ್ನೆಲ್ಲಾ ಅನುಸರಿಸಬೇಕು; ಆಗ ನೀವು ದೀರ್ಘಕಾಲ ಬಾಳುವಿರಿ. ಅದಕ್ಕಾಗಿಯೇ ಇವುಗಳನ್ನು ಆಜ್ಞಾಪಿಸಿದ್ದಾರೆ.",
			"3": "<sup>3</sup> ಆದುದರಿಂದ ಇಸ್ರಯೇಲರೇ, ನಿಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ವಾಗ್ದಾನ ಮಾಡಿದ ಪ್ರಕಾರ, ಹಾಲೂಜೇನೂ ಹರಿಯುವ ಆ ನಾಡಿನಲ್ಲಿ ನಿಮಗೆ ಶುಭವುಂಟಾಗುವಂತೆ, ನೀವು ಬಹಳವಾಗಿ ಹೆಚ್ಚಿ, ಅಭಿವೃದ್ಧಿಯಾಗುವಂತೆ, ಈ ಆಜ್ಞೆಗಳನ್ನು ಲಕ್ಷ್ಯವಿಟ್ಟು ಕೇಳಿ ಅನುಸರಿಸಿರಿ.",
			"4": "<sup>4</sup> “ಇಸ್ರಯೇಲ್ ಜನಾಂಗವೇ ಕೇಳು; ನಿನ್ನ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಸ್ವಾಮಿ ಒಬ್ಬರೇ ದೇವರು.",
			"5": "<sup>5</sup> ನಿನ್ನ ಪೂರ್ಣಹೃದಯದಿಂದ, ಪೂರ್ಣಪ್ರಾಣದಿಂದ, ಪೂರ್ಣಶಕ್ತಿಯಿಂದ ನಿನ್ನ ದೇವರಾದ ಸರ್ವೇಶ್ವರನನ್ನು ಪ್ರೀತಿಸು.",
			"6": "<sup>6</sup> ಈ ದಿನ ನಾನು ನಿನಗೆ ತಿಳಿಸುವ ಮಾತುಗಳು ನಿಮ್ಮ ಹೃದಯದಲ್ಲಿ ನಾಟಿರಲಿ.",
			"7": "<sup>7</sup> ಇವುಗಳನ್ನು ನಿನ್ನ ಮಕ್ಕಳಿಗೆ ಮನದಟ್ಟಾಗಿಸು; ಮನೆಯಲ್ಲಿರುವಾಗಲು, ಪ್ರಯಾಣದಲ್ಲಿರುವಾಗಲು, ಮಲಗುವಾಗಲು, ಏಳುವಾಗಲು ಇವುಗಳನ್ನು ಕುರಿತು ಪಾಠಹೇಳು.",
			"8": "<sup>8</sup> ಜ್ಞಾಪಕಾರ್ಥವಾಗಿ ಅವುಗಳನ್ನು ನಿನ್ನ ಕೈಗೆ ಕಟ್ಟಿಕೊ; ಜ್ಞಾಪಕ ಪಟ್ಟಿಯಂತೆ ಹಣೆಗೆ ತೊಟ್ಟುಕೊ.",
			"9": "<sup>9</sup> ನಿನ್ನ ಮನೆ ಬಾಗಿಲಿನ ನಿಲುವುಪಟ್ಟಿಗಳಲ್ಲೂ ತಲೆಬಾಗಿಲಿನ ಮೇಲೂ ಅವುಗಳನ್ನು ಬರೆ.",
			"10": "<sup>10</sup> “ನಿನ್ನ ಪಿತೃಗಳಾದ ಅಬ್ರಹಾಮ್, ಇಸಾಕ್, ಯಕೋಬರಿಗೆ ನಿನ್ನ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಪ್ರಮಾಣಮಾಡಿದ ನಾಡಿಗೆ ನಿನ್ನನ್ನು ಸೇರಿಸಿದಾಗ,",
			"11": "<sup>11</sup> ನೀನು ಕಟ್ಟದ ಸುಂದರವಾದ ದೊಡ್ಡ ಪಟ್ಟಣಗಳನ್ನು, ನೀನು ಕೂಡಿಸದ ಉತ್ತಮೋತ್ತಮ ವಸ್ತುಗಳಿಂದ ತುಂಬಿದ ಮನೆಗಳನ್ನು, ನೀನು ತೋಡದ ನೀರುಗುಂಡಿಗಳನ್ನು, ನೀನು ಬೆಳಸದ ದ್ರಾಕ್ಷಿತೋಟಗಳನ್ನು ಹಾಗು ಎಣ್ಣೆಮರಗಳನ್ನು ಅನುಭವಿಸುತ್ತಾ ತೃಪ್ತನಾಗಿ ಇರುವೆ.",
			"12": "<sup>12</sup> ಈಜಿಪ್ಟ್ ದೇಶದಲ್ಲಿ ಗುಲಾಮನಾಗಿದ್ದ ನಿನ್ನನ್ನು ಬಿಡುಗಡೆ ಮಾಡಿದ ಸರ್ವೇಶ್ವರನನ್ನು ಆಗ ಮರೆತುಬಿಡದಂತೆ ಎಚ್ಚರಿಕೆಯಿಂದಿರು.",
			"13": "<sup>13</sup> ನಿನ್ನ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವನಾಗಿರು; ಅವರಿಗೇ ಸೇವೆಮಾಡು; ಅವರ ಹೆಸರು ಹೇಳಿ ಪ್ರಮಾಣಮಾಡು.",
			"14": "<sup>14</sup> ಸುತ್ತಮುತ್ತಲಿರುವ ಜನಾಂಗಗಳು ಹಿಂಬಾಲಿಸುವ ದೇವರುಗಳನ್ನು ನೀನು ಆರಾಧಿಸಬಾರದು.",
			"15": "<sup>15</sup> ನಿಮ್ಮ ಮಧ್ಯೆಯಿರುವ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ತಮಗೆ ಸಲ್ಲಬೇಕಾದ ಗೌರವವನ್ನು ಇನ್ನೊಬ್ಬನಿಗೆ ಸಲ್ಲಗೊಡಿಸದ ದೇವರು; ಅವರು ನಿಮ್ಮ ಮೇಲೆ ಕೋಪಗೊಂಡರೆ ನಿಮ್ಮನ್ನು ಭೂಮಿಯ ಮೇಲೆ ಉಳಿಯದಂತೆ ನಾಶಮಾಡಬಹುದು.",
			"16": "<sup>16</sup> “ಮಸ್ಸದಲ್ಲಿ ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ಮಾಡಿದಂತೆ ಇನ್ನು ಮುಂದೆ ಮಾಡಬೇಡಿ.",
			"17": "<sup>17</sup> ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ನೇಮಿಸಿದ ಆಜ್ಞಾವಿಧಿನಿರ್ಣಯಗಳನ್ನು ಅನುಸರಿಸಲೇಬೇಕು.",
			"18": "<sup>18</sup> ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ಯಾವುದು ನ್ಯಾಯವೋ, ಯೋಗ್ಯವೋ ಅದನ್ನೇ ಮಾಡಬೇಕು. ಹೀಗೆ ನಡೆದರೆ ನಿಮಗೆ ಶುಭ ಉಂಟಾಗುವುದು ಮತ್ತು ಸರ್ವೇಶ್ವರ ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ಪ್ರಮಾಣಪೂರ್ವಕವಾಗಿ ಕೊಟ್ಟ ಉತ್ತಮ ನಾಡನ್ನು ನೀವು ಸೇರಿ ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವಿರಿ.",
			"19": "<sup>19</sup> ಅಲ್ಲದೆ, ಅವರೇ ಮಾತುಕೊಟ್ಟಿರುವಂತೆ ನಿಮ್ಮ ಶತ್ರುಗಳನ್ನು ನಿಮ್ಮ ಮುಂದೆಯೇ ತೊಲಗಿಸುವರು.",
			"20": "<sup>20</sup> “ಇನ್ನು ಮುಂದೆ ನಿಮ್ಮ ಮಕ್ಕಳು, ‘ಈ ಆಜ್ಞಾವಿಧಿನಿರ್ಣಯಗಳನ್ನು ನಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಏಕೆ ನೇಮಿಸಿದರು?’ ಎಂದು ವಿಚಾರಿಸುವರು.",
			"21": "<sup>21</sup> ಆಗ ನೀವು ಅವರಿಗೆ, ‘ನಾವು ಈಜಿಪ್ಟಿನಲ್ಲಿ ಫರೋಹನಿಗೆ ಗುಲಾಮರಾಗಿ ಇದ್ದೆವು. ಸರ್ವೇಶ್ವರ ತಮ್ಮ ಭುಜಬಲದಿಂದ ನಮ್ಮನ್ನು ಬಿಡುಗಡೆ ಮಾಡಿದರು;",
			"22": "<sup>22</sup> ಭಯಂಕರ ಆದ ಮಹತ್ಕಾರ್ಯಗಳಿಂದ ಮತ್ತು ಸೂಚಕಕಾರ್ಯಗಳಿಂದ ಈಜಿಪ್ಟಿನವರನ್ನೂ ಫರೋಹನನ್ನೂ ಅವನ ಮನೆಯವರನ್ನೂ ನಮ್ಮ ಕಣ್ಣೆದುರಿನಲ್ಲೇ ಶಿಕ್ಷಿಸಿದರು.",
			"23": "<sup>23</sup> ನಮ್ಮ ಪಿತೃಗಳಿಗೆ ಪ್ರಮಾಣಪೂರ್ವಕವಾಗಿ ಕೊಟ್ಟ ನಾಡಿಗೆ ನಮ್ಮನ್ನು ಸೇರಿಸುವುದಕ್ಕೆ ಈಜಿಪ್ಟಿನಿಂದ ನಮ್ಮನ್ನು ಬರಮಾಡಿದರು.",
			"24": "<sup>24</sup> ಈ ಕಾರಣದಿಂದಲೇ ಈ ನಿಯಮಗಳನ್ನೆಲ್ಲಾ ಆಚರಿಸಬೇಕೆಂದು ನಮಗೆ ಅಪ್ಪಣೆಮಾಡಿದ್ದಾರೆ. ಸದಾ ಶುಭವುಂಟಾಗಬೇಕಾದರೆ, ಅವರು ಎಂದಿನಂತೆ ನಮ್ಮ ಪ್ರಾಣಗಳನ್ನು ಕಾಪಾಡಬೇಕಾದರೆ ನಾವು ನಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿರಬೇಕು.",
			"25": "<sup>25</sup> ನಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿರೂಪಿಸಿದ ಈ ಧರ್ಮೋಪದೇಶವನ್ನೆಲ್ಲಾ ನಾವು ಅನುಸರಿಸಿದರೆ ಅವರ ದೃಷ್ಟಿಯಲ್ಲಿ ನೀತಿವಂತರೆಂದು ಪರಿಗಣಿತರಾಗುವೆವು,’ ಎಂದು ನೀವು ಉತ್ತರಕೊಡಬೇಕು."
		},
		"7": {
			"1": "<sup>1</sup> “ನೀವು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವುದಕ್ಕೆ ಹೋಗುವ ನಾಡಿಗೆ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮನ್ನು ಸೇರಿಸಿ, ಸಂಖ್ಯೆಯಲ್ಲೂ ಶಕ್ತಿಯಲ್ಲೂ ನಿಮ್ಮನ್ನು ಮೀರುವ ಜನಾಂಗಗಳನ್ನು ಅಲ್ಲಿಂದ ನಿಮ್ಮ ಮುಂದೆಯೆ ಹೊರಡಿಸುವರು.",
			"2": "<sup>2</sup> ಅಂಥ ಏಳು ಜನಾಂಗಗಳು ಯಾವುವೆಂದರೆ ಹಿತ್ತಿಯರು, ಗಿರ್ಗಾಷಿಯರು, ಅಮೋರಿಯರು, ಕಾನಾನ್ಯರು, ಪೆರಿಜ್ಜೀಯರು, ಹಿವ್ವಿಯರು ಹಾಗು ಯೆಬೂಸಿಯರು. ಅವರನ್ನು ನೀವು ಸೋಲಿಸಿ ವಿನಾಶ ಶಾಪಗ್ರಸ್ತರನ್ನಾಗಿಸಬೇಕು. ಅವರ ಸಂಗಡ ಒಪ್ಪಂದಮಾಡಿಕೊಳ್ಳಬಾರದು, ಅವರನ್ನು ಕನಿಕರಿಸಬಾರದು,",
			"3": "<sup>3</sup> ಅವರೊಡನೆ ಬೀಗತನ ಮಾಡಬಾರದು; ಅವರ ಮಕ್ಕಳಿಗೆ ಹೆಣ್ಣುಗಳನ್ನು ಕೊಡಲೂಬಾರದು, ಅವರಿಂದ ತರಲೂಬಾರದು.",
			"4": "<sup>4</sup> ಹಾಗೆ ಮಾಡಿದರೆ ಅವರು ನಿಮ್ಮ ಮಕ್ಕಳನ್ನು ಸರ್ವೇಶ್ವರನಿಂದ ವಿಮುಖಗೊಳಿಸಿ ಇತರ ದೇವರುಗಳನ್ನು ಪೂಜಿಸುವಂತೆ ಮಾಡಬಹುದು. ಆಗ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮ ಮೇಲೆ ಕೋಪಗೊಂಡು ಬೇಗನೆ ನಿಮ್ಮನ್ನು ನಾಶಮಾಡುವರು.",
			"5": "<sup>5</sup> ಆದುದರಿಂದ ನೀವು ಹೀಗೆ ಮಾಡಬೇಕು; ಅವರ ಯಜ್ಞವೇದಿಗಳನ್ನು ಕೆಡವಬೇಕು, ಅವರ ಪವಿತ್ರಶಿಲೆಗಳನ್ನು ಒಡೆಯಬೇಕು, ಅಶೇರವೆಂಬ ವಿಗ್ರಹಸ್ತಂಭಗಳನ್ನು ಬಡಿದು ಅವರ ದೇವತಾಪ್ರತಿಮೆಗಳನ್ನು ಬೆಂಕಿಯಿಂದ ಸುಟ್ಟುಬಿಡಬೇಕು.",
			"6": "<sup>6</sup> ಏಕೆಂದರೆ ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ಮೀಸಲಾದ ಜನರು; ಜಗದಲ್ಲಿರುವ ಸಮಸ್ತ ಜನಾಂಗಗಳಲ್ಲಿ ನಿಮ್ಮನ್ನೇ ಸ್ವಕೀಯಜನರಾಗಲು ಅವರು ಆಯ್ದುಕೊಂಡಿದ್ದಾರೆ.",
			"7": "<sup>7</sup> “ನೀವು ಸಂಖ್ಯೆಯಲ್ಲಿ ಜನಾಂಗಗಳಿಗೆಲ್ಲಾ ಹೆಚ್ಚುಮಂದಿ ಎಂದು ಪ್ರೀತಿಸಿ, ಆಯ್ದುಕೊಳ್ಳಲಿಲ್ಲ; ನಿಜವಾಗಿಯೂ ನೀವು ಎಲ್ಲಾ ಜನಾಂಗಗಳಿಗಿಂತ ಅಲ್ಪಸಂಖ್ಯಾತರು.",
			"8": "<sup>8</sup> ಆದರೂ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮನ್ನು ಪ್ರೀತಿಸಿ, ತಾವು ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ಮಾಡಿದ ಪ್ರಮಾಣವನ್ನು ನೆರವೇರಿಸಬೇಕೆಂದು, ಈಜಿಪ್ಟಿನ ರಾಜ ಫರೋಹನ ಕೈಕೆಳಗೆ ಗುಲಾಮರಾಗಿದ್ದ ನಿಮ್ಮನ್ನು ಬಿಡುಗಡೆಮಾಡಿ, ತಮ್ಮ ಭುಜಬಲ ಪ್ರಯೋಗಿಸಿ, ಆ ದೇಶದಿಂದ ಬರಮಾಡಿದ್ದಾರೆ.",
			"9": "<sup>9</sup> ಆದುದರಿಂದ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರಸ್ವಾಮಿಯೊಬ್ಬರೇ ದೇವರೆಂದು ತಿಳಿದುಕೊಳ್ಳಬೇಕು. ಅವರೇ ನಂಬಿಕಸ್ತರಾದ ದೇವರು; ತಮ್ಮನ್ನು ಪ್ರೀತಿಸಿ ತಮ್ಮ ಆಜ್ಞೆಗಳನ್ನು ಅನುಸರಿಸುವವರಿಗೆ ತಾವು ಮಾಡಿದ ವಾಗ್ದಾನವನ್ನು ನೆರವೇರಿಸಿ, ತಮ್ಮ ಅಚಲಪ್ರೀತಿಯನ್ನು ಅವರ ಸಂತತಿಯವರಲ್ಲಿ ಸಾವಿರ ತಲೆಗಳವರೆಗೂ ತೋರಿಸುವರು.",
			"10": "<sup>10</sup> ತಮ್ಮನ್ನು ತಿರಸ್ಕರಿಸಿದವರಿಗೆ ಪ್ರತಿಯಾಗಿ ನಾಶವನ್ನು ಉಂಟುಮಾಡುವರು. ಇದನ್ನು ನೀವು ತಿಳಿದುಕೊಳ್ಳಬೇಕು. ಹಗೆಯವರ ವಿಷಯದಲ್ಲಿ ಅವರು ತಡಮಾಡದೆ ಆಗಲೇ ಮುಯ್ಯಿತೀರಿಸುವರು.",
			"11": "<sup>11</sup> ಆದಕಾರಣ ನಾನು ಈ ದಿನ ನಿಮಗೆ ಬೋಧಿಸುವ ಧರ್ಮೋಪದೇಶವನ್ನೂ ಆಜ್ಞಾವಿಧಿಗಳನ್ನೂ ನೀವು ಅನುಸರಿಸಿ ನಡೆಯಬೇಕು.",
			"12": "<sup>12</sup> “ಈ ವಿಧಿಗಳನ್ನು ನೀವು ಲಕ್ಷ್ಯವಿಟ್ಟು ಅನುಸರಿಸಿದರೆ, ಅದಕ್ಕೆ ಪ್ರತಿಯಾಗಿ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ಪ್ರಮಾಣ ಪೂರ್ವಕವಾಗಿ ಮಾಡಿದ ವಾಗ್ದಾನವನ್ನು ನೆರವೇರಿಸುವರು, ನಿಮಗೆ ಕೃಪೆತೋರುವರು,",
			"13": "<sup>13</sup> ನಿಮ್ಮನ್ನು ಅಚಲವಾಗಿ ಪ್ರೀತಿಸಿ, ಅಭಿವೃದ್ಧಿಗೊಳಿಸುವರು; ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ಪ್ರಮಾಣಮಾಡಿಕೊಟ್ಟ ನಾಡಿನಲ್ಲಿ ನಿಮ್ಮ ಸಂತಾನವನ್ನು, ವ್ಯವಸಾಯವನ್ನು, ದವಸಧಾನ್ಯ, ದ್ರಾಕ್ಷಾರಸ, ಎಣ್ಣೆ ಇವುಗಳನ್ನು ಹಾಗು ದನಕುರಿಗಳ ಮಂದೆಯನ್ನು ವೃದ್ಧಿಗೊಳಿಸುವರು.",
			"14": "<sup>14</sup> ನೀವು ಎಲ್ಲ ಜನಗಳಿಗಿಂತ ಹೆಚ್ಚಾಗುವಿರಿ, ದೇವರ ಅನುಗ್ರಹವನ್ನು ಹೊಂದುವಿರಿ. ನಿಮ್ಮಲ್ಲಿ ಸ್ತ್ರೀಪುರುಷರಲ್ಲಾಗಲಿ, ಹೆಣ್ಣುಗಂಡು ಪಶುಪ್ರಾಣಿಗಳಲ್ಲಾಗಲಿ, ಬಂಜೆತನ ಎಂಬುದು ಇರುವುದಿಲ್ಲ.",
			"15": "<sup>15</sup> ನಿಮಗೆ ಯಾವ ವ್ಯಾಧಿಯೂ ತಗಲದಂತೆ ಸರ್ವೇಶ್ವರ ನೋಡಿಕೊಳ್ಳುವರು. ನೀವೇ ಅನುಭವದಿಂದ ಅರಿತಿರುವಂತೆ ಈಜಿಪ್ಟ್ ದೇಶದಲ್ಲಿ ಪ್ರಬಲ ಆಗಿರುವ ಕ್ರೂರವ್ಯಾಧಿಗಳನ್ನು ನಿಮಗೆ ಬರಗೊಡಿಸಲಿಲ್ಲ; ಅವುಗಳನ್ನು ನಿಮ್ಮ ದ್ವೇಷಿಗಳ ಮೇಲೆ ಮಾತ್ರ ಬರಮಾಡುವರು.",
			"16": "<sup>16</sup> ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರಾ, ನಿಮ್ಮಿಂದ ಪರಾಜಯಪಡಿಸುವ ಜನರುಗಳನ್ನೆಲ್ಲಾ ನೀವು ಕನಿಕರಿಸದೆ ನಾಶಮಾಡಬೇಕು. ಅವರ ದೇವರುಗಳನ್ನು ಪೂಜಿಸಲೇಬಾರದು; ಪೂಜಿಸಿದರೆ ಅವು ನಿಮಗೆ ಉರುಲಾಗುವುವು.",
			"17": "<sup>17</sup> ‘ಆ ಜನರು ನಮಗಿಂತ ಹೆಚ್ಚುಮಂದಿ ಇದ್ದಾರೆ; ಅವರನ್ನು ಹೊರದೂಡಲು ನಮ್ಮಿಂದ ಹೇಗಾದೀತು? ಎಂದುಕೊಳ್ಳುತ್ತೀರೋ?",
			"18": "<sup>18</sup> ಅವರಿಗೆ ಹೆದರಬೇಡಿ. ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಫರೋಹನಿಗೂ ಈಜಿಪ್ಟರೆಲ್ಲರಿಗೂ ಮಾಡಿದ್ದನ್ನು ನೆನಪಿಗೆ ತಂದುಕೊಳ್ಳಿ.",
			"19": "<sup>19</sup> ಆಗ ನೀವೇ ಕಣ್ಣಾರೆ ನೋಡಿದಂತೆ ಸರ್ವೇಶ್ವರ ವಿಶೇಷ ಪರಿಶೋಧನೆ, ಪವಾಡ, ಮಹತ್ಕಾರ್ಯ, ಭುಜಪರಾಕ್ರಮ, ಶಿಕ್ಷೆ ಇವುಗಳನ್ನು ಪ್ರಯೋಗಿಸಿ ನಿಮ್ಮನ್ನು ಬಿಡುಗಡೆಮಾಡಿದರಲ್ಲವೆ? ನೀವು ಹೆದರಿಕೊಳ್ಳುವ ಆ ಎಲ್ಲ ಜನಾಂಗಗಳಿಗೂ ಅವರು ಹಾಗೆಯೇ ಮಾಡುವರು.",
			"20": "<sup>20</sup> ಅದು ಮಾತ್ರವಲ್ಲದೆ, ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಕಣಜದ ಹುಳುಗಳನ್ನು ಅವರ ನಡುವೆ ಕಳುಹಿಸುವರು; ಮರೆ ಆಗಿ ಉಳಿದುಕೊಂಡವರು ಆ ಹುಳಗಳಿಂದ ನಾಶವಾಗಿ ನಿಮಗೆ ಕಾಣದೆಹೋಗುವರು.",
			"21": "<sup>21</sup> ನೀವು ಆ ಜನರಿಗೆ ಹೆದರಿಕೊಳ್ಳಬೇಡಿ.",
			"22": "<sup>22</sup> ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮ ಮಧ್ಯೆ ಇದ್ದಾರೆ. ಅವರು ಭಯಭಕ್ತಿಗೆ ಪಾತ್ರರಾದ ಮಹಾದೇವರು. ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಆ ಜನಾಂಗಗಳನ್ನು ನಿಮ್ಮ ಮುಂದೆಯೇ ಸ್ವಲ್ಪ ಸ್ವಲ್ಪವಾಗಿ ಹೊರಡಿಸುವರು. ನೀವು ಬಹು ಶೀಘ್ರವಾಗಿ ಅವರನ್ನು ನಿರ್ಮೂಲಮಾಡಕೂಡದು; ಮಾಡಿದರೆ ಕಾಡುಮೃಗಗಳು ಹೆಚ್ಚಿ ನಿಮಗೆ ತೊಂದರೆಯನ್ನುಂಟುಮಾಡುವುವು.",
			"23": "<sup>23</sup> ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಅವರನ್ನು ನಿಮ್ಮಿಂದ ಸೋಲಿಸಿ, ಬಹಳವಾಗಿ ಗಲಿಬಿಲಿ ಉಂಟುಮಾಡಿ ಕಡೆಗೆ ಅವರನ್ನು ಇಲ್ಲದಂತೆ ಮಾಡುವರು.",
			"24": "<sup>24</sup> ಈ ಜನರ ಅರಸುಗಳು ನಿಮ್ಮ ಕೈಗೆಸಿಕ್ಕುವಂತೆ ಮಾಡುವರು. ನೀವು ಅವರ ಹೆಸರುಗಳೇ ಭೂಮಿಯ ಮೇಲೆ ಉಳಿಯದಂತೆ ಮಾಡಬೇಕು. ಯಾರೂ ನಿಮ್ಮ ಮುಂದೆ ನಿಲ್ಲರು; ಎಲ್ಲರನ್ನು ನೀವು ನಾಶಮಾಡುವಿರಿ.",
			"25": "<sup>25</sup> “ಆ ಜನರು ದೇವರೆಂದು ಮಾಡಿಕೊಂಡ ಮರದ ವಿಗ್ರಹಗಳನ್ನು ನೀವು ಬೆಂಕಿಯಿಂದ ಸುಟ್ಟುಬಿಡಬೇಕು. ಆ ವಿಗ್ರಹಗಳ ಮೇಲಿರುವ ಬೆಳ್ಳಿಬಂಗಾರವನ್ನು ನೀವು ಬಯಸಬಾರದು. ಅದನ್ನು ತೆಗೆದುಕೊಂಡರೆ ಅದೇ ನಿಮಗೆ ಉರುಲಾಗುವದು. ಅದು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ಹೇಯವಾದುದು; ಅವರಿಂದ ಶಾಪಗ್ರಸ್ತವಾದುದು.",
			"26": "<sup>26</sup> ನೀವು ಅಂಥ ವಸ್ತುವನ್ನು ಮನೆಯೊಳಕ್ಕೆ ತಂದು ಅದರಂತೆಯೇ ನಾಶಕ್ಕೆ ಗುರಿಯಾಗಬಾರದು. ಅದು ಶಾಪಗ್ರಸ್ತವಾದ್ದರಿಂದ ಅಸಹ್ಯಪಟ್ಟು ಅದನ್ನು ಮುಟ್ಟಲೂಕೂಡದು."
		},
		"8": {
			"1": "<sup>1</sup> “ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ಧರ್ಮೋಪದೇಶವನ್ನೆಲ್ಲಾ ನೀವು ಅನುಸರಿಸಬೇಕು. ಅನುಸರಿಸಿದರೆ ನೀವು ಬಾಳಿ ಅಭಿವೃದ್ಧಿಯಾಗುವಿರಿ. ಸರ್ವೇಶ್ವರ ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ಪ್ರಮಾಣರೂಪವಾಗಿ ಕೊಟ್ಟ ನಾಡನ್ನು ಸೇರಿ ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವಿರಿ.",
			"2": "<sup>2</sup> “ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮನ್ನು ಈ ನಲವತ್ತು ವರ್ಷ ಮರುಭೂಮಿಯಲ್ಲಿ ನಡೆಸಿದ್ದನ್ನು ಹಾಗು ನೀವು ಅವರ ಆಜ್ಞೆಗಳನ್ನು ಕೈಕೊಳ್ಳುವವರೋ ಅಲ್ಲವೋ ಎಂದು ತಿಳಿದುಕೊಳ್ಳುವುದಕ್ಕೆ ನಿಮ್ಮನ್ನು ಕಷ್ಟಕ್ಕೆ ಒಳಪಡಿಸಿ ನಿಮ್ಮ ಮನೋಭಾವವನ್ನು ಪರೀಕ್ಷಿಸಿದ್ದನ್ನು ನೆನಪಿಗೆ ತಂದುಕೊಳ್ಳಿರಿ.",
			"3": "<sup>3</sup> ಮಾನವರು ಆಹಾರ ಮಾತ್ರದಿಂದಲ್ಲ, ಸರ್ವೇಶ್ವರ ಆಡುವ ಪ್ರತಿಯೊಂದು ಮಾತಿನಿಂದಲೂ ಜೀವಿಸುತ್ತಾರೆಂಬುದು ನಿಮಗೆ ತಿಳಿಯುವಂತೆ ಅವರು ನಿಮ್ಮನ್ನು ಕಷ್ಟಕ್ಕೆ ಒಳಪಡಿಸಿದರು. ಹಸಿವೆಯಿಂದ ಬಳಲಿಸಿದರು. ನಿಮಗೂ ನಿಮ್ಮ ಪೂರ್ವಿಕರಿಗೂ ಗೊತ್ತಿಲ್ಲದ ಮನ್ನವನ್ನು ಕೊಟ್ಟು ಪೋಷಿಸಿದರು.",
			"4": "<sup>4</sup> ಈ ನಲವತ್ತು ವರ್ಷ ನಿಮ್ಮ ಮೈಮೇಲಿದ್ದ ಉಡುಪು ಹರಿದುಹೋಗಲಿಲ್ಲ; ನಿಮ್ಮ ಕಾಲುಗಳು ಊದುಹೋಗಲಿಲ್ಲ.",
			"5": "<sup>5</sup> ಒಬ್ಬ ತಂದೆ ತನ್ನ ಮಗನನ್ನು ಹೇಗೊ ಹಾಗೆಯೇ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮನ್ನು ಶಿಕ್ಷಿಸುತ್ತಾ ಬಂದರೆಂಬುದನ್ನು ನೀವು ಆಲೋಚಿಸಿ ನೋಡಬೇಕು.",
			"6": "<sup>6</sup> ಅವರ ಆಜ್ಞೆಗಳನ್ನು ಅನುಸರಿಸುತ್ತಾ ಅವರು ಹೇಳಿದ ಮಾರ್ಗದಲ್ಲೇ ನಡೆದು ಅವರಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿರಬೇಕು.",
			"7": "<sup>7</sup> “ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಉತ್ತಮವಾದ ನಾಡಿಗೆ ನಿಮ್ಮನ್ನು ಸೇರಿಸುತ್ತಾರೆ. ಆ ನಾಡಿನ ಹಳ್ಳಗಳಲ್ಲಿ ನೀರು ಯಾವಾಗಲೂ ಹರಿಯುತ್ತಿರುತ್ತದೆ; ತಗ್ಗುಗಳಲ್ಲಾಗಲಿ, ಗುಡ್ಡಗಳಲ್ಲಿ ಆಗಲಿ, ಎಲ್ಲಾ ಕಡೆಯು ಬಾವಿಗಳಿಂದಲೂ ಬುಗ್ಗೆಗಳಿಂದಲೂ ನೀರು ಉಕ್ಕುತ್ತಿರುತ್ತದೆ.",
			"8": "<sup>8</sup> ಆ ನಾಡಿನಲ್ಲಿ ಗೋದಿ, ಜವೆಗೋದಿ, ದ್ರಾಕ್ಷಿ, ಅಂಜೂರ, ದಾಳಿಂಬೆ ಇವುಗಳು ಬೆಳೆಯುತ್ತವೆ; ಎಣ್ಣೆಮರಗಳು ಹಾಗು ಜೇನುತುಪ್ಪ ಸಿಕ್ಕುತ್ತವೆ.",
			"9": "<sup>9</sup> ಅಲ್ಲಿ ನೀವು ದುರ್ಭಿಕ್ಷವನ್ನೇ ಕಾಣದೆ, ಸಮೃದ್ಧಿ ಆಗಿ ಉಂಡು, ಯಾವ ಕೊರತೆಯೂ ಇಲ್ಲದೆ ಇರುವಿರಿ. ಆ ನಾಡಿನಲ್ಲಿ ಕಬ್ಬಿಣದ ಕಲ್ಲು ಸಿಕ್ಕುತ್ತದೆ. ಅಲ್ಲಿಯ ಬೆಟ್ಟಗಳಲ್ಲಿ ತಾಮ್ರದ ಗಣಿಗಳಿವೆ.",
			"10": "<sup>10</sup> ನೀವು ಹೊಟ್ಟೆತುಂಬ ಉಂಡು ಸುಖದಿಂದಿರುವಾಗ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ಇಂಥ ಉತ್ತಮ ನಾಡನ್ನು ಕೊಟ್ಟದ್ದಕ್ಕಾಗಿ ಅವರನ್ನು ಸ್ತುತಿಸಬೇಕು.",
			"11": "<sup>11</sup> “ಎಚ್ಚರಿಕೆ, ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಆಜ್ಞಾವಿಧಿನಿರ್ಣಯಗಳನ್ನು ಕೈಕೊಳ್ಳದವರೂ ಅವರನ್ನು ಮರೆತುಬಿಡುವವರೂ ಆಗಬೇಡಿ.",
			"12": "<sup>12</sup> ನೀವು ಹೊಟ್ಟೆ ತುಂಬ ಉಂಡು, ಸುಖವಾಗಿದ್ದು, ಒಳ್ಳೊಳ್ಳೆಯ ಮನೆಗಳನ್ನು ಕಟ್ಟಿಸಿಕೊಂಡು, ಅವುಗಳಲ್ಲಿ ವಾಸವಾಗಿರುವ ಕಾಲದಲ್ಲಿ,",
			"13": "<sup>13</sup> ನಿಮ್ಮ ದನಕರುಗಳು, ಆಡುಕುರಿಗಳು, ಬೆಳ್ಳಿಬಂಗಾರ ಹಾಗು ಆಸ್ತಿಯೆಲ್ಲವೂ ಹೆಚ್ಚುತ್ತಿರುವಾಗ,",
			"14": "<sup>14</sup> ನೀವು ಗರ್ವಗೊಂಡು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನನ್ನು ಮರೆತುಬಿಟ್ಟೀರಿ!",
			"15": "<sup>15</sup> ಅವರು ನಿಮ್ಮನ್ನು ಕಷ್ಟಕ್ಕೆ ಒಳಪಡಿಸಿ ಪರೀಕ್ಷಿಸಿದ ನಂತರ ನಿಮಗೆ ಒಳಿತನ್ನು ಉಂಟುಮಾಡಬೇಕೆಂಬ ಉದ್ದೇಶದಿಂದ ಈಜಪ್ಟ್ ದೇಶದಲ್ಲಿ ಗುಲಾಮರಾಗಿದ್ದ ನಿಮ್ಮನ್ನು ಬಿಡುಗಡೆ ಮಾಡಿದರು. ವಿಷಸರ್ಪಗಳೂ ಚೇಳುಗಳೂ ಇದ್ದ ಆ ಘೋರವಾದ ಮಹಾಮರುಭೂಮಿಯನ್ನೂ ನೀರು ಬತ್ತಿಹೋಗಿದ್ದ ಭೂಮಿಗಳನ್ನೂ ದಾಟಿಸಿದರು; ಗಟ್ಟಿಯಾದ ಬಂಡೆಯೊಳಗಿಂದ ನೀರನ್ನು ಹೊರಡಿಸಿಕೊಟ್ಟರು. ನಿಮ್ಮ ಪೂರ್ವಿಕರಿಗೆ ತಿಳಿಯದೆ ಇದ್ದ ಮನ್ನವನ್ನು ಕೊಟ್ಟು ಪೋಷಿಸಿದರು. ಅಂಥವರನ್ನು ಮರೆತು ಮನಸ್ಸಿನಲ್ಲೇ",
			"17": "<sup>17</sup> ‘ಈ ಭಾಗ್ಯ ನಮ್ಮ ಸಾಮರ್ಥ್ಯ ಸಾಹಸಗಳಿಂದಲೇ ನಮಗೆ ಲಭಿಸಿತು’ ಎಂದುಕೊಂಡೀರಿ ಎಚ್ಚರಿಕೆ!",
			"18": "<sup>18</sup> ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರಸ್ವಾಮಿಯೇ ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ಪ್ರಮಾಣಪೂರ್ವಕವಾಗಿ ಮಾಡಿದ ವಾಗ್ದಾನವನ್ನು ಈಗ ನಿಮಗೆ ಮನಮುಟ್ಟುವ ರೀತಿಯಲ್ಲಿ ನೆರವೇರಿಸಿದ್ದಾರೆ. ನಿಮಗೆ ಇಷ್ಟು ಭಾಗ್ಯವನ್ನು ಸಂಪಾದಿಸುವ ಸಾಮರ್ಥ್ಯವನ್ನು ಕೊಟ್ಟಿದ್ದಾರೆ ಎಂಬುದನ್ನು ಜ್ಞಾಪಕಮಾಡಿಕೊಳ್ಳಿ.",
			"19": "<sup>19</sup> ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನನ್ನು ಮರೆತು, ಇತರ ದೇವರುಗಳನ್ನು ಅವಲಂಬಿಸಿ, ಪೂಜಿಸಿದರೆ ನೀವು ತಪ್ಪದೆ ನಾಶವಾಗಿಹೋಗುವಿರೆಂದು ನಿಮ್ಮನ್ನು ಈಗ ಖಂಡಿತವಾಗಿ ಎಚ್ಚರಿಸುತ್ತೇನೆ.",
			"20": "<sup>20</sup> ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಮಾತನ್ನು ಕೇಳದೆಹೋದರೆ, ಅವರಿಂದ ನಿಮ್ಮ ಮುಂದೆಯೇ ನಾಶವಾಗಿಹೋಗುವ ಅನ್ಯಜನಾಂಗಗಳಂತೆ ನೀವೂ ನಾಶವಾಗಿಹೋಗುವಿರಿ."
		},
		"9": {
			"1": "<sup>1</sup> “ಇಸ್ರಯೇಲರೇ ಕೇಳಿ : ನಿಮಗಿಂತ ಮಹಾಬಲಿಷ್ಠ ಜನಾಂಗಗಳನ್ನು ಹಾಗು ಗಗನ ಮುಟ್ಟುವ ಕೋಟೆಕೊತ್ತಲುಗಳುಳ್ಳ ಪಟ್ಟಣಗಳನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳಲು, ನೀವು ಈ ಜೋರ್ಡನ್ ನದಿಯನ್ನು ಇಂದು ದಾಟಲಿದ್ದೀರಿ.",
			"2": "<sup>2</sup> ಆ ನಾಡಿನವರು ಅನಾಕ್ಯರೆಂಬ ಬಲಿಷ್ಠರಾದ ಎತ್ತರದ ಜನರು. ಅವರ ವಿಷಯ ನಿಮಗೆ ತಿಳಿದೇ ಇದೆ; ‘ಅನಾಕ್ಯರ ಮುಂದೆ ನಿಲ್ಲಬಲ್ಲವರು ಯಾರಿದ್ದಾರೆ’ ಎಂಬ ಮಾತನ್ನು ಕೇಳಿದ್ದೀರಷ್ಟೆ.",
			"3": "<sup>3</sup> ಆದುದರಿಂದ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ತಾವೇ, ವಿರೋಧಿಗಳನ್ನು ದಹಿಸುವ ಬೆಂಕಿಯೋಪಾದಿಯಲ್ಲಿ ನಿಮ್ಮ ಮುಂದೆ ಹೋಗುತ್ತಾರೆಂದು ಈಗ ತಿಳಿದುಕೊಳ್ಳಿ. ಅವರೇ ಆ ವಿರೋಧಿಗಳನ್ನು ನಾಶಮಾಡುವರು; ನಿಮ್ಮ ಮುಂದೆ ಆ ವಿರೋಧಿಗಳು ಸೋತುಹೋಗುವಂತೆ ಮಾಡುವರು. ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ಹೇಳಿದಂತೆ ನೀವು ಅವರನ್ನು ಹೊರಡಿಸಿ ಬೇಗನೆ ನಾಶಮಾಡುವಿರಿ.",
			"4": "<sup>4</sup> “ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಅವರನ್ನು ನಿಮ್ಮ ಬಳಿಯಿಂದ ಹೊರಡಿಸಿದನಂತರ ನೀವು ಮನಸ್ಸಿನಲ್ಲೇ, ‘ಸರ್ವೇಶ್ವರ ನಮ್ಮನ್ನು ಈ ನಾಡಿಗೆ ಕರೆತಂದು ಅದನ್ನು ನಮಗೆ ಸ್ವಾಧೀನಪಡಿಸಿದ್ದು ನಮ್ಮ ಪುಣ್ಯಫಲವೇ’ ಎಂದುಕೊಳ್ಳಬಾರದು. ಆ ಜನಾಂಗಗಳ ದುರ್ನಡತೆಯ ಕಾರಣದಿಂದಲೇ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮ ಬಳಿಯಿಂದ ಅವರನ್ನು ಹೊರದಬ್ಬುತ್ತಾರೆ.",
			"5": "<sup>5</sup> ನೀವು ಅವರ ನಾಡಿಗೆ ಬಂದು ಅದನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವುದಕ್ಕೆ ನಿಮ್ಮ ಪುಣ್ಯವೇ ಆಗಲಿ, ನಿಮ್ಮ ಸುಸ್ವಭಾವವೇ ಆಗಲಿ, ಕಾರಣವಲ್ಲ. ಆ ಜನಾಂಗಗಳ ದುರ್ನಡತೆಯ ಕಾರಣ ಹಾಗು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮ ಪಿತೃಗಳಾದ ಅಬ್ರಹಾಮ್, ಇಸಾಕ್, ಯಕೋಬರಿಗೆ ಪ್ರಮಾಣಪೂರ್ವಕವಾಗಿ ಮಾಡಿದ ವಾಗ್ದಾನವನ್ನು ನೆರವೇರಿಸಬೇಕೆಂಬ ಕಾರಣ ಅವರನ್ನು ನಿಮ್ಮ ಬಳಿಯಿಂದ ಹೊರಡಿಸಿಬಿಡುತ್ತಾರೆ.",
			"6": "<sup>6</sup> ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಆ ಉತ್ತಮ ನಾಡನ್ನು ನಿಮಗೆ ಸ್ವದೇಶವಾಗಿ ಕೊಡುವುದು ನಿಮ್ಮ ಪುಣ್ಯಫಲವಲ್ಲವೆಂದು ಚೆನ್ನಾಗಿ ತಿಳಿದುಕೊಳ್ಳಿ. ನೀವು ಅವರ ಆಜ್ಞೆಗೆ ಮಣಿಯದ ಜನರೇ ಆಗಿದ್ದೀರಿ.",
			"7": "<sup>7</sup> “ನೀವು ಮರುಭೂಮಿಯಲ್ಲಿ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ಕೋಪ ಹುಟ್ಟಿಸಿದ್ದನ್ನು ನೆನಪುಮಾಡಿಕೊಳ್ಳಿ. ಅದನ್ನು ಮರೆಯಬೇಡಿ. ನೀವು ಈಜಿಪ್ಟ್ ದೇಶವನ್ನು ಬಿಟ್ಟಂದಿನಿಂದ ಈ ಸ್ಥಳಕ್ಕೆ ಬಂದ ದಿನದವರೆಗೂ ಸರ್ವೇಶ್ವರನ ಆಜ್ಞೆಗಳನ್ನು ಧಿಕ್ಕರಿಸುತ್ತಾ ಬಂದಿದ್ದೀರಿ.",
			"8": "<sup>8</sup> ಉದಾಹರಣೆಗೆ, ಹೋರೇಬಿನಲ್ಲಿ ನೀವು ಸರ್ವೇಶ್ವರನಿಗೆ ಕೋಪವನ್ನು ಹುಟ್ಟಿಸಿದಿರಿ. ಆಗ ಅವರು ಬಲು ಸಿಟ್ಟುಗೊಂಡು ನಿಮ್ಮನ್ನು ಧ್ವಂಸಮಾಡಬೇಕೆಂದಿದ್ದರು.",
			"9": "<sup>9</sup> ಸರ್ವೇಶ್ವರ ನಿಮ್ಮ ಸಂಗಡ ಮಾಡಿದ ಒಡಂಬಡಿಕೆಯನ್ನು ಬರೆದ ಆ ಕಲ್ಲಿನ ಹಲಗೆಗಳನ್ನು ತೆಗೆದುಕೊಳ್ಳುವುದಕ್ಕೆ ನಾನು ಬೆಟ್ಟವನ್ನು ಹತ್ತಿದಾಗ ಅನ್ನಪಾನಗಳನ್ನು ಬಿಟ್ಟು ಹಗಲಿರುಳು ನಲವತ್ತು ದಿನ ಆ ಬೆಟ್ಟದಲ್ಲಿದ್ದೆ.",
			"10": "<sup>10</sup> ಆಗ ಸರ್ವೇಶ್ವರ ತಮ್ಮ ಕೈಯಿಂದಲೇ ಬರೆದ ಆ ಎರಡು ಕಲ್ಲಿನ ಹಲಗೆಗಳನ್ನು ನನ್ನ ವಶಕ್ಕೆ ಕೊಟ್ಟರು. ನೀವು ಸಭೆಕೂಡಿದ ದಿನದಲ್ಲಿ ಸರ್ವೇಶ್ವರ ಆ ಬೆಟ್ಟದ ಮೇಲೆ ಅಗ್ನಿಜ್ವಾಲೆಯೊಳಗಿಂದ ನಿಮ್ಮ ಸಂಗಡ ಹೇಳಿದ ಮಾತುಗಳು ಆ ಹಲಗೆಗಳಲ್ಲಿದ್ದವು.",
			"11": "<sup>11</sup> ಆ ನಲವತ್ತು ದಿನಗಳು ಕಳೆದ ಮೇಲೆ ಸರ್ವೇಶ್ವರ ತಮ್ಮ ನಿಬಂಧನೆಯಿದ್ದ ಆ ಎರಡು ಕಲ್ಲಿನ ಹಲಗೆಗಳನ್ನು ನನಗೆ ಕೊಟ್ಟು, ‘ನೀನು ಬೇಗ ಇಲ್ಲಿಂದ ಇಳಿದುಹೋಗು;",
			"12": "<sup>12</sup> ನೀನು ಈಜಿಪ್ಟ್ ದೇಶದಿಂದ ಕರೆದುಕೊಂಡು ಬಂದ ನಿನ್ನ ಜನರು ಕೆಟ್ಟುಹೋದರು; ನಾನು ಅವರಿಗೆ ಆಜ್ಞಾಪಿಸಿದ ಮಾರ್ಗವನ್ನು ಅವರು ಬೇಗನೆ ಬಿಟ್ಟುಹೋಗಿ, ತಮಗೆ ಲೋಹವಿಗ್ರಹವನ್ನು ಮಾಡಿಸಿಕೊಂಡಿದ್ದಾರೆ’ ಎಂದು ಹೇಳಿದರು.",
			"13": "<sup>13</sup> “ಅದಲ್ಲದೆ ಅವರು ನನಗೆ, ‘ಈ ಜನರ ಸ್ವಭಾವವನ್ನು ನಾನು ನೋಡುತ್ತಾ ಬಂದಿದ್ದೇನೆ;",
			"14": "<sup>14</sup> ಇವರು ನನ್ನ ಆಜ್ಞೆಗೆ ಬಗ್ಗದವರು. ಬಿಡು, ನಾನು ಅವರನ್ನು ನಾಶಮಾಡಿ ಅವರ ಹೆಸರನ್ನು ಭೂಮಿಯ ಮೇಲೆ ಉಳಿಯದಂತೆ ಮಾಡುವೆನು. ತರುವಾಯ ಅವರಿಗಿಂತಲೂ ಮಹಾಬಲಿಷ್ಠ ಜನಾಂಗವೊಂದು ನಿನ್ನಿಂದುಂಟಾಗುವಂತೆ ಮಾಡುವೆನು,’ ಎಂದು ಹೇಳಿದರು.",
			"15": "<sup>15</sup> ಆಗ ನಾನು ಆ ಎರಡು ಆಜ್ಞಾಶಾಸನಗಳನ್ನು ನನ್ನ ಎರಡು ಕೈಗಳಲ್ಲಿ ಹಿಡಿದುಕೊಂಡು ಬೆಂಕಿಯಿಂದ ಉರಿಯುತ್ತಿದ್ದ ಆ ಬೆಟ್ಟದಿಂದ ಇಳಿದುಬಂದು ನೋಡಿದೆ.",
			"16": "<sup>16</sup> ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ದ್ರೋಹಿಗಳಾಗಿ ಲೋಹದ ಹೋರಿಕರುವನ್ನು ಮಾಡಿಸಿಕೊಂಡು ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ಆಜ್ಞಾಪಿಸಿದ್ದ ಮಾರ್ಗವನ್ನು ಬೇಗನೆ ಬಿಟ್ಟುಹೋದದ್ದು ನನಗೆ ಕಂಡುಬಂದಿತು.",
			"17": "<sup>17</sup> ಅದನ್ನು ನೋಡಿ ನಾನು ಕೈಯಲ್ಲಿದ್ದ ಆ ಎರಡು ಶಿಲಾಶಾಸನಗಳನ್ನು ನಿಮ್ಮ ಎದುರಿನಲ್ಲಿಯೇ ನೆಲಕ್ಕೆ ಹಾಕಿ ಒಡೆದುಬಿಟ್ಟೆ.",
			"18": "<sup>18</sup> “ನೀವು ಹೀಗೆ ಅಪರಾಧಮಾಡಿ, ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ಕೆಟ್ಟದ್ದನ್ನು ನಡೆಸಿ, ಅವರನ್ನು ಕೋಪಗೊಳಿಸಿದ ಕಾರಣ ನಾನು ಮೊದಲಿನಂತೆ ಅನ್ನಪಾನಗಳನ್ನು ಬಿಟ್ಟು ನಲವತ್ತು ದಿನಗಳು ಹಗಲಿರುಳು ಸರ್ವೇಶ್ವರನ ಸನ್ನಿಧಿಯಲ್ಲೇ ಬಿದ್ದಿದ್ದೆ.",
			"19": "<sup>19</sup> ಸರ್ವೇಶ್ವರ ನಿಮ್ಮ ಮೇಲೆ ಬಹು ಕೋಪಗೊಂಡು ನಿಮ್ಮನ್ನು ನಾಶಮಾಡಬೇಕೆಂದು ಆಲೋಚಿಸಿದ್ದರಿಂದ ನಾನು ಹೆದರಿದೆ. ಆದರೆ ಆ ಕಾಲದಲ್ಲೂ ಅವರು ನನ್ನ ಮನವಿಯನ್ನು ಕೇಳಿದರು.",
			"20": "<sup>20</sup> ಅದಲ್ಲದೆ, ಸರ್ವೇಶ್ವರ ಆರೋನನ ಮೇಲೆ ಬಹಳ ಸಿಟ್ಟುಗೊಂಡು, ಅವನನ್ನೂ ನಾಶಮಾಡಬೇಕೆಂದಿದ್ದರು. ಆದುದರಿಂದ ನಾನು ಅವನ ಪರವಾಗಿಯೂ ಆ ಕಾಲದಲ್ಲಿ ಪ್ರಾರ್ಥಿಸಿದೆ.",
			"21": "<sup>21</sup> ಮತ್ತು ನಿಮ್ಮ ಪಾಪಕ್ಕೆ ಕಾರಣವಾದ ಆ ಹೋರಿಕರುವನ್ನು ನಾನು ತೆಗೆದುಕೊಂಡು, ಬೆಂಕಿಯಿಂದ ಸುಟ್ಟು, ಒಡೆದು, ಅರೆದು, ಧೂಳುಮಾಡಿ ಬೆಟ್ಟದಿಂದ ಹರಿಯುವ ಹಳ್ಳದಲ್ಲಿ ಬಿಸಾಡಿಬಿಟ್ಟೆ.",
			"22": "<sup>22</sup> “ತಬೇರಾ, ಮಸ್ಸಾ, ಕಿಬ್ರೋತ್ ಹತಾವಾ ಎಂಬ ಸ್ಥಳಗಳಲ್ಲೂ ನೀವು ಸರ್ವೇಶ್ವರನಿಗೆ ಕೋಪವನ್ನು ಹುಟ್ಟಿಸಿದಿರಿ.",
			"23": "<sup>23</sup> ಅದೂ ಅಲ್ಲದೆ, ಕಾದೇಶ್ ಬರ್ನೇಯದಲ್ಲಿ ಸರ್ವೇಶ್ವರ ನಿಮಗೆ, ‘ಬೆಟ್ಟದ ಸೀಮೆಯನ್ನು ಹತ್ತಿ ನಾನು ನಿಮಗೆ ಕೊಟ್ಟಿರುವ ನಾಡನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳಿ,’ ಎಂದು ಅಪ್ಪಣೆ ಕೊಟ್ಟಾಗಲೂ ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಆ ಆಜ್ಞೆಯನ್ನು ಧಿಕ್ಕರಿಸಿ ಅವರನ್ನು ನಂಬದೆ ಅವರ ಮಾತನ್ನು ಅಲಕ್ಷ್ಯಮಾಡಿದಿರಿ.",
			"24": "<sup>24</sup> ನನಗೆ ನಿಮ್ಮ ಪರಿಚಯವಾದಂದಿನಿಂದಲೂ ನೀವು ಸರ್ವೇಶ್ವರನ ಆಜ್ಞೆಯನ್ನು ಉಲ್ಲಂಘಿಸುತ್ತಾ ಬಂದಿದ್ದೀರಿ.",
			"25": "<sup>25</sup> “ಸರ್ವೇಶ್ವರ ನಿಮ್ಮನ್ನು ನಾಶಮಾಡಬೇಕೆಂದಿದ್ದುದರಿಂದ ನಾನು ಆ ನಲವತ್ತು ದಿನವೂ ಹಗಲಿರುಳು ಅವರ ಸನ್ನಿಧಿಯಲ್ಲೇ ಬಿದ್ದಿದ್ದೆ.",
			"26": "<sup>26</sup> ನಾನು ಅವರನ್ನು ಬೇಡಿಕೊಳ್ಳುತ್ತಾ, ‘ಸರ್ವೇಶ್ವರಾ, ದೇವರೇ, ನೀವು ನಿಮ್ಮ ಮಹಿಮೆಯಿಂದ ರಕ್ಷಿಸಿ, ನಿಮ್ಮ ಭುಜಬಲ ಪ್ರಯೋಗಿಸಿ ಈಜಿಪ್ಟಿನಿಂದ ಬಿಡುಗಡೆಮಾಡಿದ ನಿಮ್ಮ ಸ್ವಕೀಯ ಜನರನ್ನು ನಾಶಮಾಡಬೇಡಿ.",
			"27": "<sup>27</sup> ಇವರ ಮೊಂಡುತನ, ದುಷ್ಟತನ, ಪಾಪ ಇವುಗಳನ್ನು ಮನಸ್ಸಿಗೆ ತೆಗೆದುಕೊಳ್ಳದೆ ನಿಮ್ಮ ದಾಸರಾದ ಅಬ್ರಹಾಮ್, ಇಸಾಕ್, ಯಕೋಬರನ್ನು ನೆನಪಿಗೆ ತಂದುಕೊಳ್ಳಿ.",
			"28": "<sup>28</sup> ನೀವು ಯಾವ ದೇಶದಿಂದ ನಮ್ಮನ್ನು ಬರಮಾಡಿದಿರೋ ಆ ದೇಶದವರು, ‘ಸರ್ವೇಶ್ವರ ಇಸ್ರಯೇಲರಿಗೆ ವಾಗ್ದಾನಮಾಡಿದ ನಾಡಿಗೆ ಅವರನ್ನು ಸೇರಿಸಲಾರದೆ ಹಗೆಮಾಡಿ, ಮರುಭೂಮಿಯಲ್ಲಿ ಕೊಲ್ಲುವುದಕ್ಕೇ ಕರೆದುಕೊಂಡು ಹೋದರು’ ಎಂದು ಹೇಳುವುದಕ್ಕೆ ಆಸ್ಪದವಾದೀತು.",
			"29": "<sup>29</sup> ಮಹಾಶಕ್ತಿಯಿಂದಲೂ ಭುಜಬಲದಿಂದಲೂ ನೀವು ಬಿಡಿಸಿದ ನಿಮ್ಮ ಸ್ವಕೀಯ ಜನ ಹಾಗು ಸೊತ್ತು ಇವರಲ್ಲವೇ? ಎಂದು ಬಿನ್ನೈಸಿದೆ."
		},
		"10": {
			"1": "<sup>1</sup> “ಆ ಕಾಲದಲ್ಲಿ ಸರ್ವೇಶ್ವರ ನನಗೆ, ‘ನೀನು ಮೊದಲಿನ ಕಲ್ಲಿನ ಹಲಗೆಗಳಂತೆ ಬೇರೆ ಎರಡು ಹಲಗೆಗಳನ್ನು ಸಿದ್ಧಪಡಿಸಿಕೊಂಡು ಬೆಟ್ಟವನ್ನು ಹತ್ತಿ ನನ್ನ ಬಳಿಗೆ ಬಾ ಮತ್ತು ಒಂದು ಮರದ ಮಂಜೂಷವನ್ನು ಮಾಡಿಸಿಕೊಳ್ಳಬೇಕು.",
			"2": "<sup>2</sup> ನೀನು ಒಡೆದುಬಿಟ್ಟ ಆ ಮೊದಲಿನ ಹಲಗೆಗಳಲ್ಲಿ ಬರೆದಿದ್ದ ಮಾತುಗಳನ್ನು ನಾನು ಈ ಹಲಗೆಗಳ ಮೇಲೆ ಬರೆಯುವೆನು. ತರುವಾಯ ನೀನು ಅವುಗಳನ್ನು ಆ ಮಂಜೂಷದಲ್ಲಿಡಬೇಕು’ ಎಂದು ಆಜ್ಞಾಪಿಸಿದರು.",
			"3": "<sup>3</sup> ಆದಕಾರಣ, ನಾನು ಜಾಲೀಮರದಿಂದ ಮಂಜೂಷವನ್ನು ಮಾಡಿಸಿ, ಮೊದಲಿನ ಕಲ್ಲಿನ ಹಲಗೆಗಳಂತೆ ಬೇರೆ ಎರಡು ಹಲಗೆಗಳನ್ನು ಸಿದ್ಧಪಡಿಸಿ, ಆ ಎರಡು ಹಲಗೆಗಳನ್ನು ಕೈಯಲ್ಲಿ ಹಿಡಿದುಕೊಂಡು, ಬೆಟ್ಟವನ್ನು ಹತ್ತಿದೆ.",
			"4": "<sup>4</sup> ಸರ್ವೇಶ್ವರ ಬೆಟ್ಟದ ಮೇಲೆ ಅಗ್ನಿಜ್ವಾಲೆಯೊಳಗಿಂದ, ನೀವು ಸಭೆಕೂಡಿದ ದಿನದಲ್ಲಿ, ನಿಮಗೆ ಹೇಳಿದ ಮಾತುಗಳನ್ನು ಅಂದರೆ, ಆ ಹತ್ತು ಆಜ್ಞೆಗಳನ್ನು ಅವರು ಮೊದಲಿನಂತೆಯೇ ಆ ಹಲಗೆಗಳ ಮೇಲೆ ಬರೆದರು.",
			"5": "<sup>5</sup> ಸರ್ವೇಶ್ವರ ಹಲಗೆಗಳನ್ನು ನನ್ನ ವಶಕ್ಕೆ ಕೊಟ್ಟನಂತರ ನಾನು ಬೆಟ್ಟದಿಂದ ಇಳಿದುಬಂದೆ; ನನ್ನಿಂದ ಸಿದ್ಧ ಆಗಿದ್ದ ಮಂಜೂಷದಲ್ಲಿ ಅವರ ಅಪ್ಪಣೆಯ ಮೇರೆಗೆ ಅವುಗಳನ್ನು ಇಟ್ಟೆ; ಅವು ಇಂದಿನವರೆಗೂ ಅದರಲ್ಲೇ ಇವೆ.",
			"6": "<sup>6</sup> (ಇಸ್ರಯೇಲರು ಯಾಕಾನ್ಯರ ಬಾವಿಗಳ ಬಳಿಯಿಂದ ಪ್ರಯಾಣಮಾಡಿ ಮೋಸೇರಕ್ಕೆ ಬಂದರು. ಅಲ್ಲಿ ಆರೋನನು ಸಾಯಲಾಗಿ ಅವನನ್ನು ಸಮಾಧಿಮಾಡಿದರು. ಅವನ ಮಗ ಎಲ್ಲಾಜಾರನು ಅವನಿಗೆ ಬದಲಾಗಿ ಮಹಾಯಾಜಕನಾದನು.",
			"7": "<sup>7</sup> ಅಲ್ಲಿಂದ ಅವರು ಗುದ್ಗೋದಕ್ಕೂ ಗುದ್ಗೋದದಿಂದ ನೀರಿನ ಹಳ್ಳಗಳುಳ್ಳ ಯೊಟ್ಬಾತಕ್ಕೂ ಪ್ರಯಾಣಮಾಡಿದರು.)",
			"8": "<sup>8</sup> “ಆ ಕಾಲದಲ್ಲಿ ಸರ್ವೇಶ್ವರ ಲೇವಿಕುಲದವರನ್ನು ಪ್ರತ್ಯೇಕಿಸಿ ತಮ್ಮ ಒಡಂಬಡಿಕೆಯ ಮಂಜೂಷವನ್ನು ಹೊರುವುದಕ್ಕು, ತಮ್ಮ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸೇವೆಮಾಡುವುದಕ್ಕು ಹಾಗು ತಮ್ಮ ಹೆಸರಿನಲ್ಲಿ ಜನರನ್ನು ಆಶೀರ್ವದಿಸುವುದಕ್ಕು ಅವರನ್ನು ನೇಮಿಸಿದರು. ಅವರು ಇಂದಿನವರೆಗೂ ಆ ಕೆಲಸವನ್ನು ನಡೆಸುತ್ತಾರೆ.",
			"9": "<sup>9</sup> ಆದುದರಿಂದ ಮಿಕ್ಕ ಇಸ್ರಯೇಲರಿಗೆ ದೊರಕಿದಂತೆ ಲೇವಿಯರಿಗೆ ಸ್ವಂತವಾದ ಭೂಸ್ವಾಸ್ತ್ಯ ದೊರಕಲಿಲ್ಲ. ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಅವರಿಗೆ ಹೇಳಿದಂತೆ ಸರ್ವೇಶ್ವರಸ್ವಾಮಿಯೇ ಅವರ ಸೊತ್ತು.",
			"10": "<sup>10</sup> “ನಾನು ಮುಂಚಿನಂತೆ ಹಗಲಿರುಳು ನಲವತ್ತು ದಿನವೂ ಬೆಟ್ಟದ ಮೇಲೆ ಇದ್ದೆ. ಸರ್ವೇಶ್ವರ ಆ ಕಾಲದಲ್ಲೂ ನನ್ನ ಪ್ರಾರ್ಥನೆಯನ್ನು ಕೇಳಿ, ನಿಮ್ಮನ್ನು ನಾಶಮಾಡಬೇಕೆಂಬ ಆಲೋಚನೆಯನ್ನು ಬಿಟ್ಟುಬಿಟ್ಟರು.",
			"11": "<sup>11</sup> ತರುವಾಯ ಸರ್ವೇಶ್ವರ ನನಗೆ, ‘ನೀನು ಈ ಜನರ ಮುಂದುಗಡೆ ಹೊರಡು; ನಾನು ಇವರ ಪಿತೃಗಳಿಗೆ ಪ್ರಮಾಣ ಮಾಡಿಕೊಟ್ಟ ನಾಡನ್ನು ಇವರು ಸೇರಿ ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳಲಿ,’ ಎಂದು ಆಜ್ಞಾಪಿಸಿದರು.",
			"12": "<sup>12</sup> “ಆದುದರಿಂದ ಇಸ್ರಯೇಲರೇ, ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಲ್ಲಿ ಭಯಭಕ್ತಿ ಉಳ್ಳವರಾಗಿರಬೇಕು; ಎಲ್ಲ ವಿಷಯಗಳಲ್ಲೂ ಅವರು ಹೇಳುವ ಮಾರ್ಗದಲ್ಲೇ ನಡೆಯಬೇಕು; ಅವರನ್ನು ಪ್ರೀತಿಸುತ್ತಾ ಸಂಪೂರ್ಣವಾದ ಹೃದಯದಿಂದಲೂ ಮನಸ್ಸಿನಿಂದಲೂ ಸೇವೆಮಾಡಬೇಕು;",
			"13": "<sup>13</sup> ನಾನು ನಿಮ್ಮ ಒಳಿತಿಗಾಗಿ ಈಗ ಬೋಧಿಸುವ ಅವರ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ಅನುಸರಿಸುತ್ತಾ ಬರಬೇಕು. ಇಷ್ಟನ್ನೇ ಹೊರತು, ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮಿಂದ ಬೇರೇನನ್ನು ಕೇಳಿಕೊಳ್ಳುವುದಿಲ್ಲ.",
			"14": "<sup>14</sup> ಇಗೋ ಕೇಳಿ: ಉನ್ನತೋನ್ನತವಾದ ಆಕಾಶಮಂಡಲವು, ಭೂಮಿಯು ಹಾಗು ಅದರಲ್ಲಿರುವ ಎಲ್ಲವು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರಸ್ವಾಮಿಯವೇ.",
			"15": "<sup>15</sup> ಆದರೂ ಅವರು ನಿಮ್ಮ ಪಿತೃಗಳಲ್ಲಿ ಇಷ್ಟವುಳ್ಳವರಾಗಿ ಅವರನ್ನು ಪ್ರೀತಿಸಿದರು. ಈಗ ನಿಮ್ಮ ಅನುಭವಕ್ಕೆ ಬಂದಿರುವಂತೆ, ಅವರ ತರುವಾಯ ಅವರ ಸಂತತಿಯವರಾದ ನಿಮ್ಮನ್ನೇ ಎಲ್ಲ ಜನಾಂಗಗಳಿಂದ ಆರಿಸಿಕೊಂಡರು.",
			"16": "<sup>16</sup> ಆದುದರಿಂದ ಅವರ ಆಜ್ಞೆಗೆ ಮಣಿಯದ ನಿಮ್ಮ ದುಷ್ಟಸ್ವಭಾವವನ್ನು ಬಿಟ್ಟು ಬಿಟ್ಟು ನಿಮ್ಮ ಹೃದಯದಲ್ಲೇ ಸುನ್ನತಿಮಾಡಿಕೊಳ್ಳಿ.",
			"17": "<sup>17</sup> ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ದೇವಾದಿದೇವರು, ಸರ್ವೇಶ್ವರಾಧಿ ಸರ್ವೇಶ್ವರ. ಅವರು ಪರಮ ದೇವರೂ ಪರಾಕ್ರಮಿಯೂ ಭಯಂಕರರೂ ಆಗಿದ್ದಾರೆ. ಅವರು ಮುಖದಾಕ್ಷಿಣ್ಯ ನೋಡುವವರಲ್ಲ, ಲಂಚ ತೆಗೆದುಕೊಳ್ಳುವವರಲ್ಲ.",
			"18": "<sup>18</sup> ಅನಾಥರಿಗೆ ಮತ್ತು ವಿಧವೆಯರಿಗೆ ನ್ಯಾಯ ದೊರಕಿಸುತ್ತಾರೆ; ಪರದೇಷಿಗಳಾದವರಿಗೆ ಪ್ರೀತಿಯಿಂದ ಅನ್ನ ವಸ್ತ್ರಗಳನ್ನು ನೀಡುತ್ತಾರೆ.",
			"19": "<sup>19</sup> ಈಜಿಪ್ಟ್ ದೇಶದಲ್ಲಿ ನೀವೇ ಪರದೇಶಿಗಳಾಗಿ ಇದ್ದುದನ್ನು ಜ್ಞಾಪಿಸಿಕೊಂಡು ಪರದೇಶದವರಲ್ಲಿ ಪ್ರೀತಿಯಿಡಿ.",
			"20": "<sup>20</sup> ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿ ಅವರಿಗೇ ಸೇವೆಸಲ್ಲಿಸಿ. ಅವರನ್ನು ಹೊಂದಿಕೊಂಡು ಅವರ ಹೆಸರಿನ ಮೇಲೆಯೇ ಪ್ರಮಾಣಮಾಡಬೇಕು.",
			"21": "<sup>21</sup> ಅವರೊಬ್ಬರೇ ನಿಮ್ಮ ಸ್ತುತಿಸ್ತೋತ್ರಕ್ಕೆ ಪಾತ್ರರು; ಅವರು ನಿಮ್ಮ ದೇವರು; ನೀವು ನೋಡಿದ ಆ ಮಹಾಭಯಂಕರವಾದ ಮಹತ್ಕಾರ್ಯಗಳನ್ನು ನಿಮ್ಮ ಪರವಾಗಿ ನಡೆಸಿದವರು ಅವರೇ.",
			"22": "<sup>22</sup> ನಿಮ್ಮ ಪಿತೃಗಳು, ಎಪ್ಪತ್ತುಮಂದಿ ಮಾತ್ರ. ಈಜಿಪ್ಟ್ ದೇಶಕ್ಕೆ ಹೋದರು; ಈಗಲಾದರೋ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮನ್ನು ಆಕಾಶದ ನಕ್ಷತ್ರಗಳಷ್ಟು ಅಸಂಖ್ಯರಾಗುವಂತೆ ಮಾಡಿದ್ದಾರೆ."
		},
		"11": {
			"1": "<sup>1</sup> “ಆದುದರಿಂದ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಲ್ಲಿ ಪ್ರೀತಿಯಿಟ್ಟು, ಅವರ ನಿಯಮಗಳನ್ನು ಕೈಕೊಂಡು ಅವರ ಆಜ್ಞಾವಿಧಿನಿರ್ಣಯಗಳನ್ನು ಯಾವಾಗಲೂ ಅನುಸರಿಸಬೇಕು.",
			"2": "<sup>2</sup> “ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಪ್ರಯೋಗಿಸಿದ ಶಿಕ್ಷಣಕ್ರಮವನ್ನೂ ಮಹಿಮೆಯನ್ನೂ ಭುಜಬಲವನ್ನೂ ಶಿಕ್ಷಾಹಸ್ತವನ್ನೂ",
			"3": "<sup>3</sup> ಅಂದರೆ, ಅವರು ಈಜಿಪ್ಟ್ ದೇಶದಲ್ಲಿ ಫರೋಹನನ್ನೂ ಅವನ ದೇಶವನ್ನೂ ಶಿಕ್ಷಿಸುವುದಕ್ಕಾಗಿ ನಡೆಸಿದ ಸೂಚಕ ಮತ್ತು ಮಹತ್ಕಾರ್ಯಗಳನ್ನೂ;",
			"4": "<sup>4</sup> ಈಜಿಪ್ಟಿನವರು ರಥಾಶ್ವಬಲಗಳಿಂದ ಕೂಡಿದಂಥ ದಂಡಿನ ಸಮೇತ ನಿಮ್ಮನ್ನು ಹಿಂದಟ್ಟಿ ಬಂದಾಗ ಕೆಂಪುಸಮುದ್ರದ ನೀರನ್ನು ಅವರ ಮೇಲೆ ಬರಮಾಡಿ ಸಂಪೂರ್ಣವಾಗಿ ನಾಶಮಾಡಿದ್ದನ್ನೂ;",
			"5": "<sup>5</sup> ನೀವು ಈ ಸ್ಥಳಕ್ಕೆ ಸೇರುವ ತನಕ ನಿಮಗೆ ಮರುಭೂಮಿಯಲ್ಲಿ ಮಾಡಿದ ಉಪಕಾರಗಳನ್ನೂ;",
			"6": "<sup>6</sup> ರೂಬೇನನ ಮೊಮ್ಮಕ್ಕಳೂ ಎಲೀಯಾಬನ ಮಕ್ಕಳೂ ಆದ ದಾತಾನ್ ಅಬೀರಾಮರು ದಂಗೆಯೆದ್ದಾಗ ಭೂಮಿ ಬಾಯ್ದೆರೆದು ಅವರನ್ನೂ ಅವರ ಮನೆಯವರನ್ನೂ ಡೇರೆಗಳನ್ನೂ ಅವರಿಗೆ ಸೇರಿದ ಎಲ್ಲ ಪಶುಪ್ರಾಣಿಗಳನ್ನೂ ಇಸ್ರಯೇಲರ ನಡುವೆ ನುಂಗಿಬಿಟ್ಟದ್ದನ್ನೂ ಈಗ ಜ್ಞಾಪಕಮಾಡಿಕೊಳ್ಳಿ. ಈ ಕಾರ್ಯಗಳನ್ನು ನೋಡದೆ ಹಾಗು ತಿಳಿಯದೆ ಇರುವ ನಿಮ್ಮ ಮಕ್ಕಳಿಗೆ ನಾನು ಈ ಮಾತನ್ನು ಹೇಳುತ್ತಿಲ್ಲ;",
			"7": "<sup>7</sup> ಸರ್ವೇಶ್ವರ ನಡೆಸಿದ ಆ ವಿಶೇಷಕಾರ್ಯಗಳನ್ನು ಪ್ರತ್ಯಕ್ಷವಾಗಿ ನೋಡಿದವರಾದ ನಿಮಗೇ ಹೇಳುತ್ತಾ ಇದ್ದೇನೆ.",
			"8": "<sup>8</sup> “ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ಈ ಧರ್ಮೋಪದೇಶವನ್ನೆಲ್ಲ ನೀವು ಅನುಸರಿಸಬೇಕು. ಆಗ ನೀವು ಬಲವುಳ್ಳವರಾಗಿ ಈ ನದಿ ದಾಟಿ ಆಚೆಯಿರುವ ನಾಡನ್ನು ಸೇರಿ ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವಿರಿ;",
			"9": "<sup>9</sup> ಸರ್ವೇಶ್ವರ ನಿಮ್ಮ ಪಿತೃಗಳಿಗೂ ಅವರ ಸಂತತಿಗೂ ಕೊಡುತ್ತೇನೆಂದು ಪ್ರಮಾಣಮಾಡಿದ ಆ ನಾಡಿನಲ್ಲಿ ನೀವು ಬಹುಕಾಲ ಬಾಳುವಿರಿ; ಅದು ಹಾಲೂ ಜೇನೂ ಹರಿಯುವ ನಾಡು.",
			"10": "<sup>10</sup> ನೀವು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳಲು ಹೋಗುವ ನಾಡು ನೀವು ಬಿಟ್ಟುಬಂದ ಈಜಿಪ್ಟ್ ದೇಶದ ಹಾಗಲ್ಲ. ಈಜಿಪ್ಟಿನಲ್ಲಿ ನೀವು ಹೊಲಗದ್ದೆಗಳಲ್ಲಿ ಬೀಜ ಬಿತ್ತಿದ ಮೇಲೆ ಕಾಯಿಪಲ್ಯಗಳ ತೋಟವನ್ನು ಸಾಗುವಳಿಮಾಡುವ ರೀತಿಯಲ್ಲಿ ಏತವನ್ನು ತುಳಿದು ನೀರುಕಟ್ಟುತ್ತಿದ್ದಿರಿ.",
			"11": "<sup>11</sup> ನೀವು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವುದಕ್ಕೆ ಹೋಗುವ ನಾಡೋ ಹಳ್ಳದಿಣ್ಣೆಗಳ ನಾಡು; ಆಕಾಶದಿಂದ ಮಳೆಬಿದ್ದಂತೆ ನೀರುದೊರಕುತ್ತದೆ.",
			"12": "<sup>12</sup> ಅದು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಪರಾಂಬರಿಸುವ ನಾಡು; ಸಂವತ್ಸರದ ಪ್ರಾರಂಭ ಮೊದಲುಗೊಂಡು ಕೊನೆಯವರೆಗೂ ಅವರು ಅದನ್ನು ಸದಾ ಕಟಾಕ್ಷಿಸುವರು.",
			"13": "<sup>13</sup> ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ಆಜ್ಞೆಗಳಿಗೆ ಲಕ್ಷ್ಯಕೊಟ್ಟು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನನ್ನು ಸಂಪೂರ್ಣ ಹೃದಯದಿಂದಲೂ ಮನಸ್ಸಿನಿಂದಲೂ ಪ್ರೀತಿಸಿ ಅವರಿಗೆ ಸೇವೆಸಲ್ಲಿಸಿದರೆ,",
			"14": "<sup>14</sup> ನಿಮಗೆ ಗೋದಿ, ದ್ರಾಕ್ಷಿ, ಎಣ್ಣೇಕಾಯಿ ಇವುಗಳ ಬೆಳೆ ಚೆನ್ನಾಗಿ ಆಗುವುದು. ನಿಮ್ಮ ಭೂಮಿಗೆ ಬೇಕಾದ ಮುಂಗಾರು ಹಿಂಗಾರುಗಳನ್ನು ಅವರು ಸರಿಯಾಗಿ ಸುರಿಸುವರು.",
			"15": "<sup>15</sup> ಅಡವಿಯಲ್ಲಿ ದನಕರುಗಳಿಗೆ ಬೇಕಾದಷ್ಟು ಹುಲ್ಲನ್ನು ಕೊಡುವರು; ನೀವು ಆಹಾರದಿಂದ ತೃಪ್ತರಾಗುವಿರಿ.",
			"16": "<sup>16</sup> ಆದರೆ ನೀವು ಎಚ್ಚರಿಕೆಯಿಂದಿರಬೇಕು; ಮರುಳುಗೊಂಡು ಸರ್ವೇಶ್ವರ ಹೇಳಿದ ಮಾರ್ಗವನ್ನು ಬಿಟ್ಟು, ಇತರ ದೇವರುಗಳನ್ನು ಅವಲಂಬಿಸಿ, ಪೂಜಿಸಿದರೆ,",
			"17": "<sup>17</sup> ಅವರು ನಿಮ್ಮ ಮೇಲೆ ಸಿಟ್ಟಗೊಂಡು ಮಳೆಬಾರದಂತೆ ಆಕಾಶವನ್ನು ಮುಚ್ಚಿಡಬಹುದು. ಆಗ ಭೂಮಿಯಲ್ಲಿ ಬೆಳೆಯಾಗದೆ ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ಕೊಡುವ ಆ ಉತ್ತಮನಾಡಿನಲ್ಲಿ ನೀವು ಉಳಿಯದೆ ಬೇಗ ನಾಶವಾಗಿಹೋಗುವಿರಿ.",
			"18": "<sup>18</sup> “ಆದುದರಿಂದ ನೀವು ಈ ನನ್ನ ಮಾತುಗಳನ್ನು ನಿಮ್ಮ ಹೃದಯದಲ್ಲೂ ಮನಸ್ಸಿನಲ್ಲೂ ಇಟ್ಟುಕೊಳ್ಳಬೇಕು; ಇವುಗಳನ್ನು ಜ್ಞಾಪಕಾರ್ಥವಾಗಿ ಕೈಗೆ ಕಟ್ಟಿಕೊಳ್ಳಬೇಕು; ಇವು ನಿಮ್ಮ ಹಣೆಗೆ ಕಟ್ಟಿಕೊಳ್ಳುವ ಜ್ಞಾಪಕದ ಪಟ್ಟಿಯಂತಿರಬೇಕು.",
			"19": "<sup>19</sup> ನೀವು ಮನೆಯಲ್ಲಿರುವಾಗಲೂ ಪ್ರಯಾಣದಲ್ಲಿರುವಾಗಲೂ ಮಲಗುವಾಗಲೂ ಏಳುವಾಗಲೂ ಇವುಗಳ ವಿಷಯದಲ್ಲಿ ಮಾತಾಡುತ್ತಾ ನಿಮ್ಮ ಮಕ್ಕಳಿಗೆ ಇವುಗಳನ್ನು ಮನದಟ್ಟಾಗಿಸಬೇಕು.",
			"20": "<sup>20</sup> ನಿಮ್ಮ ಮನೇ ಬಾಗಿಲಿನ ನಿಲುವುಪಟ್ಟಿಗಳ ಹಾಗು ಹೆಬ್ಬಾಗಿಲುಗಳ ಮೇಲೆಯೂ ಇವುಗಳನ್ನೂ ಬರೆಯಬೇಕು.",
			"21": "<sup>21</sup> ಹೀಗೆ ಮಾಡಿದರೆ ಭೂಮಿಯ ಮೇಲೆ ಆಕಾಶ ಎಷ್ಟುಕಾಲ ನಿಂತಿರುವುದೋ ಅಷ್ಟು ಕಾಲವೂ ನೀವು ಹಾಗು ನಿಮ್ಮ ಸಂತತಿಯವರು ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ಸರ್ವೇಶ್ವರ ಪ್ರಮಾಣಪೂರ್ವಕವಾಗಿ ವಾಗ್ದಾನಮಾಡಿದ ಆ ನಾಡಿನಲ್ಲಿ ಬಾಳುವಿರಿ.",
			"22": "<sup>22</sup> “ನಾನು ಬೋಧಿಸುವ ಈ ಧರ್ಮೋಪದೇಶವನ್ನೆಲ್ಲ ನೀವು ಅನುಸರಿಸುವವರಾಗಿ, ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನನ್ನು ಪ್ರೀತಿಸಿ, ಎಲ್ಲ ವಿಷಯಗಳಲ್ಲೂ ಅವರು ಹೇಳುವ ಮಾರ್ಗದಲ್ಲೇ ನಡೆದು, ಅವರನ್ನೇ ಹೊಂದಿಕೊಂಡಿದ್ದರೆ,",
			"23": "<sup>23</sup> ಅವರು ಆ ಜನಾಂಗಗಳನ್ನೆಲ್ಲ ನಿಮ್ಮ ಬಳಿಯಿಂದ ಹೊರದೂಡುವರು; ನಿಮಗಿಂತಲೂ ಮಹಾಬಲಿಷ್ಠಜನಾಂಗಗಳ ನಾಡನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವಿರಿ.",
			"24": "<sup>24</sup> ನೀವು ಹೆಜ್ಜೆಯಿಡುವ ಎಲ್ಲ ಸ್ಥಳಗಳೂ ನಿಮ್ಮದಾಗುವುವು; ಮರುಭೂಮಿ ಮೊದಲುಗೊಂಡು ಲೆಬನೋನ್ ಪರ್ವತದವರೆಗೂ ಮತ್ತು ಯೂಫ್ರೆಟಿಸ್ ನದಿಯಿಂದ ಪಶ್ಚಿಮ ಸಮುದ್ರದವರೆಗೂ ನಿಮ್ಮ ನಾಡು ವ್ಯಾಪಿಸುವುದು.",
			"25": "<sup>25</sup> ಯಾರೂ ನಿಮ್ಮ ಮುಂದೆ ನಿಲ್ಲುವುದಿಲ್ಲ. ನೀವು ಕಾಲಿಡುವ ಎಲ್ಲ ಪ್ರದೇಶಗಳ ಜನಗಳಿಗೂ ನಿಮ್ಮಿಂದ ದಿಗಿಲೂ ಹೆದರಿಕೆಯೂ ಉಂಟಾಗುವ ಹಾಗೆ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ತಾನೇ ಹೇಳಿರುವಂತೆ ಮಾಡುವರು.",
			"26": "<sup>26</sup> “ಇಗೋ ನೋಡಿ, ಈ ದಿನ ಆಶೀರ್ವಾದವನ್ನೂ ಶಾಪವನ್ನೂ ನಿಮ್ಮ ಮುಂದೆ ಇಡುತ್ತಾ ಇದ್ದೇನೆ.",
			"27": "<sup>27</sup> ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಆಜ್ಞೆಗಳಿಗೆ ನೀವು ವಿಧೇಯರಾಗಿ ನಡೆದರೆ, ಆಶೀರ್ವಾದ ನಿಮ್ಮದಾಗುವುದು.",
			"28": "<sup>28</sup> ಈ ಆಜ್ಞೆಗಳಿಗೆ ವಿಧೇಯರಾಗದೆ, ನಾನು ಈಗ ಬೋಧಿಸುವ ಮಾರ್ಗವನ್ನು ಬಿಟ್ಟು ನಿಮಗೆ ಗೊತ್ತಿಲ್ಲದ ಇತರ ದೇವರುಗಳನ್ನು ಅವಲಂಬಿಸಿದರೆ, ನಿಮಗೆ ಶಾಪ ತಗಲುವುದು.",
			"29": "<sup>29</sup> “ನೀವು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವುದಕ್ಕೆ ಹೋಗುವ ನಾಡಿಗೆ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮನ್ನು ಸೇರಿಸಿದ ನಂತರ ನೀವು ಗೆರಿಜ್ಜೀಮ್ ಬೆಟ್ಟದ ಮೇಲೆ ಆಶೀರ್ವಾದವನ್ನೂ ಏಬಾಲ್ ಬೆಟ್ಟದ ಮೇಲೆ ಶಾಪವನ್ನೂ ಪ್ರಕಟಿಸಬೇಕು.",
			"30": "<sup>30</sup> ಆ ಬೆಟ್ಟಗಳು ಜೋರ್ಡನ್ ನದಿಯ ಆಚೆ, ಪಡುವಣ ದಾರಿಯ ಪಶ್ಚಿಮದಲ್ಲಿ ಕಾನಾನ್ಯರು ವಾಸಿಸುವ ಅರಾಬಾ ಪ್ರದೇಶದಲ್ಲಿ, ಗಿಲ್ಗಾಲಿಗೆ ಎದುರಾಗಿ ಮೋರೆ ಎಂಬ ವೃಕ್ಷದ ಬಳಿಯಲ್ಲಿ ಇವೆ.",
			"31": "<sup>31</sup> “ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ಕೊಡುವ ನಾಡನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವುದಕ್ಕೆ ನೀವು ಜೋರ್ಡನ್ ನದಿಯನ್ನು ದಾಟಬೇಕಾಗಿದೆ. ನೀವು ಆ ನಾಡನ್ನು ವಶಪಡಿಸಿಕೊಂಡು, ಅದರಲ್ಲಿ ವಾಸಮಾಡುವಾಗ,",
			"32": "<sup>32</sup> ನಾನು ಈಗ ನಿಮಗೆ ತಿಳಿಸುವ ಎಲ್ಲ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ಅನುಸರಿಸಿ ನಡೆಯಲೇಬೇಕು."
		},
		"12": {
			"1": "<sup>1</sup> “ನಿಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ಸ್ವದೇಶವಾಗುವುದಕ್ಕೆ ಕೊಡುವ ನಾಡಿನಲ್ಲೆ ನೀವು ಜೀವಮಾನ ಪರಿಯಂತರ ಅನುಸರಿಸಬೇಕಾದ ಆಜ್ಞಾವಿಧಿಗಳು ಇವು:",
			"2": "<sup>2</sup> “ನೀವು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವ ನಾಡಿನ ಜನಾಂಗಗಳು ದೊಡ್ಡ ಬೆಟ್ಟಗಳ ಮೇಲೂ ದಿಣ್ಣೆಗಳ ಮೇಲೂ ಹರಡಿಕೊಂಡ ಮರಗಳ ಕೆಳಗೂ ತಮ್ಮ ದೇವರುಗಳನ್ನು ಪೂಜಿಸುತ್ತಾರೆ; ಆ ಸ್ಥಳಗಳನ್ನೆಲ್ಲಾ ನೀವು ಅಗತ್ಯವಾಗಿ ನಾಶಮಾಡಬೇಕು.",
			"3": "<sup>3</sup> ಅವರ ಬಲಿಪೀಠಗಳನ್ನು ಕೆಡವಬೇಕು; ಅವರ ಕಲ್ಲುಕಂಬಗಳನ್ನು ಒಡೆಯಬೇಕು; ಅಶೇರ ಎಂಬ ವಿಗ್ರಹಸ್ತಂಭಗಳನ್ನು ಸುಟ್ಟು, ಅವರ ದೇವತಾ ಪ್ರತಿಮೆಗಳನ್ನು ಪುಡಿಪುಡಿಮಾಡಿ ಆ ದೇವರುಗಳ ಹೆಸರೇ ಉಳಿಯದಂತೆ ಮಾಡಿಬಿಡಬೇಕು.",
			"4": "<sup>4</sup> “ಅವರು ಅನುಸರಿಸುವ ರೀತಿಯಲ್ಲಿ ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನನ್ನು ಪೂಜಿಸಬಾರದು.",
			"5": "<sup>5</sup> ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ತಮ್ಮ ಹೆಸರನ್ನು ಸ್ಥಾಪಿಸಿಕೊಳ್ಳಲು ಹಾಗು ತಾವು ವಾಸಮಾಡಲು ನಿಮ್ಮ ಎಲ್ಲಾ ಕುಲಗಳಿಂದ ಆರಿಸಿಕೊಳ್ಳುವ ಸ್ಥಳದಲ್ಲೇ ನೀವು ಅವರ ದರ್ಶನಕ್ಕಾಗಿ ಸಭೆಸೇರಬೇಕು.",
			"6": "<sup>6</sup> ನೀವು ಸಮರ್ಪಿಸುವ ದಹನಬಲಿ ಮುಂತಾದ ಯಜ್ಞಗಳ ಪಶುಪ್ರಾಣಿಗಳನ್ನು, ನಿಮ್ಮ ಬೆಳೆಯಲ್ಲಿ ದಶಮಾಂಶವನ್ನು ಪ್ರತ್ಯೇಕಿಸುವ ಪದಾರ್ಥಗಳನ್ನು ಮತ್ತು ಹರಕೇಮುಡುಪುಗಳನ್ನು, ಕಾಣಿಕೆಗಳನ್ನು ಹಾಗು ದನಕುರಿಗಳ ಚೊಚ್ಚಲು ಮರಿಗಳನ್ನು ಆ ಸ್ಥಳಕ್ಕೆ ಮಾತ್ರ ತರಬೇಕು.",
			"7": "<sup>7</sup> ಅಲ್ಲಿಯೇ ಅವರ ಸನ್ನಿಧಿಯಲ್ಲಿ ಊಟಮಾಡಿ, ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮ ಪ್ರಯತ್ನಗಳನ್ನು ಕೈಗೂಡುವಂತೆ ಮಾಡಿದ್ದಕ್ಕಾಗಿ ನೀವೂ ನಿಮ್ಮ ಮನೆಗಳವರೂ ಸಂತೋಷದಿಂದಿರಬೇಕು.",
			"8": "<sup>8</sup> “ನಾವು ಈಗ ಈ ಸ್ಥಳದಲ್ಲಿ ವರ್ತಿಸುವಂತೆ ಎಲ್ಲರೂ ನಡೆದುಕೊಳ್ಳಬೇಕೇ ವಿನಃ ತಮ್ಮ ತಮ್ಮ ಮನಸ್ಸಿಗೆ ತೋರಿದಂತೆ ವರ್ತಿಸಕೂಡದು;",
			"9": "<sup>9</sup> ಏಕೆಂದರೆ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ಕೊಡುವ ನಾಡನ್ನು ಸೇರಿ, ನೀವು ಇನ್ನು ವಿಶ್ರಾಂತಿಹೊಂದಿಲ್ಲ.",
			"10": "<sup>10</sup> ಆದರೆ ನೀವು ಜೋರ್ಡನ್ ನದಿಯನ್ನು ದಾಟಿ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ಸೊತ್ತಾಗಿ ಕೊಡುವ ಆ ನಾಡಿನಲ್ಲಿ ಮನೆಮಾಡಿಕೊಂಡಿರುವಾಗ ನಿಮ್ಮ ಸುತ್ತಲು ಶತ್ರುಗಳು ಯಾರೂ ಇಲ್ಲದಂತೆ ಸರ್ವೇಶ್ವರ ಮಾಡುವರು;",
			"11": "<sup>11</sup> ಆಗ ನೀವು ನಿರ್ಭಯರಾಗಿ ನಾನು ನಿಮಗೆ ಆಜ್ಞಾಪಿಸಿದ ಎಲ್ಲಾ ಕಾಣಿಕೆಗಳನ್ನು ಅಂದರೆ, ದಹನಬಲಿ ಮುಂತಾದ ಯಜ್ಞಪಶುಗಳನ್ನು, ಬೆಳೆಯ ದಶಮಾಂಶಗಳನ್ನು, ಸರ್ವೇಶ್ವರನಿಗಾಗಿ ಪ್ರತ್ಯೇಕಿಸುವ ಪದಾರ್ಥಗಳನ್ನು, ಹರಕೆಮಾಡಿದ ವಿಶೇಷ ಕಾಣಿಕೆಗಳನ್ನು, ಇವುಗಳನ್ನೆಲ್ಲ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ತಮ್ಮ ನಾಮಸ್ಥಾಪನೆಗಾಗಿ ಆರಿಸಿಕೊಳ್ಳುವ ಸ್ಥಳಕ್ಕೇ ತರಬೇಕು.",
			"12": "<sup>12</sup> ನೀವು, ನಿಮ್ಮ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳು, ದಾಸದಾಸಿಯರು ಹಾಗು ನಿಮ್ಮೊಡನೆ ಸೊತ್ತನ್ನು ಹೊಂದದೆ ಇರುವ ನಿಮ್ಮ ಊರಿನ ಲೇವಿಯರು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸಂತೋಷವಾಗಿರಬೇಕು.",
			"13": "<sup>13</sup> “ನಿಮಗೆ ಇಷ್ಟಬಂದ ಸ್ಥಳಗಳಲ್ಲಿ ದಹನಬಲಿಗಳನ್ನು ಸಮರ್ಪಿಸಬಾರದು. ಎಚ್ಚರಿಕೆ!",
			"14": "<sup>14</sup> ನಿಮ್ಮ ಯಾವುದಾದರೊಂದು ಕುಲದಲ್ಲಿ ಸರ್ವೇಶ್ವರ ಆರಿಸಿಕೊಳ್ಳುವ ಸ್ಥಳದಲ್ಲೇ ನಿಮ್ಮ ದಹನಬಲಿಗಳನ್ನು ಸಮರ್ಪಿಸಬೇಕು; ಅಲ್ಲಿಯೇ ನಾನು ಆಜ್ಞಾಪಿಸುವ ಎಲ್ಲಾ ಆಚಾರಗಳನ್ನು ನಡೆಸಬೇಕು.",
			"15": "<sup>15</sup> ಆದರೆ ನೀವು ಎಲ್ಲ ಊರುಗಳಲ್ಲಿ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮನ್ನು ಅಭಿವೃದ್ಧಿಪಡಿಸುವ ಮೇರೆಗೆ ಪಶುಪ್ರಾಣಿಗಳನ್ನು ಇಷ್ಟಾನುಸಾರ ಕೊಯ್ದು ಊಟಮಾಡಬಹುದು. ಶುದ್ಧರೂ ಅಶುದ್ಧರೂ ಜಿಂಕೆದುಪ್ಪಿಗಳ ಮಾಂಸವನ್ನು ತಿನ್ನುವ ಪ್ರಕಾರ ಇವುಗಳನ್ನೂ ತಿನ್ನಬಹುದು.",
			"16": "<sup>16</sup> ರಕ್ತವನ್ನು ಮಾತ್ರ ಸೇವಿಸಬಾರದು; ಅದನ್ನು ನೀರಿನಂತೆ ಭೂಮಿಯ ಮೇಲೆ ಸುರಿದುಬಿಡಬೇಕು.",
			"17": "<sup>17</sup> “ಧಾನ್ಯ, ದ್ರಾಕ್ಷಿ, ಎಣ್ಣೇಕಾಯಿ ಈ ಬೆಳೆಗಳ ದಶಮಾಂಶಗಳನ್ನು, ದನಕುರಿಗಳ ಚೊಚ್ಚಲು ಮರಿಗಳನ್ನು, ಹರಕೆಮಾಡಿದ ಪದಾರ್ಥಗಳನ್ನು, ಕಾಣಿಕೆಗಳನ್ನು ಹಾಗು ಯಾಜಕರಿಗಾಗಿ ಪ್ರತ್ಯೇಕಿಸಿದ ಪದಾರ್ಥಗಳನ್ನು ನಿಮ್ಮ ನಿಮ್ಮ ಊರುಗಳಲ್ಲಿ ತಿನ್ನಬಾರದು.",
			"18": "<sup>18</sup> ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಆರಿಸಿಕೊಳ್ಳುವ ಸ್ಥಳದಲ್ಲಿ, ಅವರ ಸನ್ನಿಧಿಯಲ್ಲೇ ಅವುಗಳನ್ನು ಊಟಮಾಡಬೇಕು. ನಿಮ್ಮ ಎಲ್ಲ ಪ್ರಯತ್ನಗಳು ಕೈಗೂಡಿದ್ದಕ್ಕಾಗಿ ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸಂತೋಷದಿಂದಿರಬೇಕು; ನೀವು, ನಿಮ್ಮ ಗಂಡುಹೆಣ್ಣು ಮಕ್ಕಳು, ಆಳುಗಳು ಹಾಗು ನಿಮ್ಮ ಊರಲ್ಲಿರುವ ಲೇವಿಯರು ಇಂಥವುಗಳನ್ನು ಊಟಮಾಡಬೇಕು.",
			"19": "<sup>19</sup> ನೀವು ನಿಮ್ಮ ನಾಡಿನಲ್ಲಿ ಇರುವ ತನಕ ಲೇವಿಯರನ್ನು ಕೈಬಿಡಬಾರದು; ಇದು ನೆನಪಿರಲಿ.",
			"20": "<sup>20</sup> “ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ವಾಗ್ದಾನ ಮಾಡಿದಂತೆ ನಿಮ್ಮ ರಾಜ್ಯವನ್ನು ವಿಸ್ತರಿಸಿದಾಗ ನೀವು ಮಾಂಸಾಹಾರವನ್ನು ಮಾಡುವುದಕ್ಕೆ ಇಷ್ಟಪಟ್ಟರೆ ಮಾಡಬಹುದು.",
			"21": "<sup>21</sup> ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ತಮ್ಮ ಹೆಸರನ್ನು ಸ್ಥಾಪಿಸಲು ಆರಿಸಿಕೊಳ್ಳುವ ಸ್ಥಳ ನಿಮಗೆ ದೂರವಾದರೆ, ಅವರು ನಿಮಗೆ ಅನುಗ್ರಹಿಸಿದ ದನಕುರಿಗಳಲ್ಲಿ ಬೇಕಾದಷ್ಟನ್ನು, ನಾನು ಅಪ್ಪಣೆಕೊಟ್ಟ ಮೇರೆಗೆ, ನೀವು ಕೊಯ್ದು ನಿಮ್ಮ ಊರಲ್ಲೇ ಊಟಮಾಡಬಹುದು.",
			"22": "<sup>22</sup> ಜಿಂಕೆ ದುಪ್ಪಿಗಳನ್ನು ಶುದ್ಧರೂ ಅಶುದ್ಧರೂ ತಿನ್ನುವ ಪ್ರಕಾರ ದನಕುರಿಗಳನ್ನೂ ತಿನ್ನಬಹುದು.",
			"23": "<sup>23</sup> “ಆದರೆ ರಕ್ತ ಜೀವಾಧಾರವಾದುದರಿಂದ ಮಾಂಸದೊಡನೆ ರಕ್ತವನ್ನು ಸೇವಿಸಲೇಕೂಡದೆಂಬುದನ್ನು ಜ್ಞಾಪಕದಲ್ಲಿಡಿ. ಮಾಂಸದೊಡನೆ ಅದರ ಜೀವಾಧಾರವನ್ನು ತಿನ್ನಬಾರದು.",
			"24": "<sup>24</sup> ಅದನ್ನು ಭುಜಿಸದೆ ನೀರಿನಂತೆ ಭೂಮಿಯ ಮೇಲೆ ಸುರಿದುಬಿಡಬೇಕು.",
			"25": "<sup>25</sup> ನೀವು ಅದನ್ನು ಸೇವಿಸದೆ ಸರ್ವೇಶ್ವರನಿಗೆ ಮೆಚ್ಚಿಗೆಯಾಗಿರುವುದನ್ನೇ ಮಾಡಿದರೆ ನಿಮಗೂ ನಿಮ್ಮ ಸಂತತಿಯವರಿಗೂ ಶುಭವುಂಟಾಗುವುದು.",
			"26": "<sup>26</sup> “ಆದರೆ ನಿಮ್ಮ ಬಳಿಯಿರುವ ದೇವರ ವಸ್ತುಗಳನ್ನು ಹಾಗು ಹರಕೆಮಾಡಿದ್ದನ್ನೂ ಸರ್ವೇಶ್ವರ ಆರಿಸಿಕೊಂಡ ಸ್ಥಳಕ್ಕೇ ತೆಗೆದುಕೊಂಡು ಹೋಗಬೇಕು.",
			"27": "<sup>27</sup> ದಹನಬಲಿಗಳನ್ನು ಸಮರ್ಪಿಸುವಾಗ ರಕ್ತಮಾಂಸಗಳೆರಡನ್ನೂ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಬಲಿಪೀಠದಲ್ಲಿ ದಹಿಸಿಬಿಡಬೇಕು. ಮಿಕ್ಕಬಲಿಗಳನ್ನು ಅರ್ಪಿಸುವಾಗ ರಕ್ತವನ್ನು ಸರ್ವೇಶ್ವರನ ಬಲಿಪೀಠದ ಮೇಲೆ ಸುರಿದುಬಿಟ್ಟು, ಮಾಂಸವನ್ನು ಊಟಮಾಡಬೇಕು.",
			"28": "<sup>28</sup> “ನಾನು ಬೋಧಿಸುವ ಈ ಎಲ್ಲಾ ಆಜ್ಞೆಗಳಿಗೆ ನೀವು ಚೆನ್ನಾಗಿ ಕಿವಿಗೊಟ್ಟು, ಅವುಗಳನ್ನು ಅನುಸರಿಸಿದರೆ ನಿಮಗೂ ನಿಮ್ಮ ಸಂತತಿಯವರಿಗೂ ಸದಾಕಾಲ ಶುಭವುಂಟಾಗುವುದು. ಏಕೆಂದರೆ ಹೀಗೆ ಮಾಡುವುದರಿಂದ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ಸರಿಯಾದುದನ್ನೂ ಸೂಕ್ತವಾದುದನ್ನೂ ಮಾಡುತ್ತೀರಿ.",
			"29": "<sup>29</sup> “ನೀವು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳಲು ಹೋಗುವ ನಾಡಿನ ಜನಾಂಗಗಳನ್ನು, ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮ ಮುಂದೆ ನಾಶಮಾಡಿದ ತರುವಾಯ, ನೀವು ಅವರ ನಾಡನ್ನು ತೆಗೆದುಕೊಂಡು ಅದರಲ್ಲಿ ವಾಸವಾಗಿರುವಿರಿ.",
			"30": "<sup>30</sup> ಆಗ ನೀವು ಭ್ರಮೆಗೊಂಡು, ನಿಮ್ಮ ಮುಂದೆ ನಾಶವಾದವರ ದುಷ್ಟ ಪದ್ಧತಿಗಳನ್ನು ಅನುಸರಿಸದಂತೆ ಜಾಗರೂಕರಾಗಿರಿ; ‘ಈ ನಾಡಿನ ಜನರು ತಮ್ಮ ದೇವರುಗಳನ್ನು ಹೇಗೆ ಪೂಜಿಸುತ್ತಿದ್ದರೋ ಹಾಗೆಯೇ ನಾವೂ ಪೂಜಿಸುವೆವು’ ಎಂದು ಹೇಳಿಕೊಂಡು ಅವರ ದೇವರುಗಳ ಬಗ್ಗೆ ಎಷ್ಟು ಮಾತ್ರವೂ ಕೇಳಿತಿಳಿದುಕೊಳ್ಳಬೇಡಿ.",
			"31": "<sup>31</sup> ಸರ್ವೇಶ್ವರನಿಗೆ ಅಸಹ್ಯವಾಗಿರುವ ಹಲವು ಹೇಸಿಗೆ ಕೆಲಸಗಳನ್ನು ಅವರು ತಮ್ಮ ದೇವತೆಗಳಿಗೆ ಮಾಡುತ್ತಾರೆ; ತಮ್ಮ ಗಂಡುಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ತಮ್ಮ ದೇವತೆಗಳಿಗೆ ಬೆಂಕಿಯಲ್ಲಿ ಸುಟ್ಟುಬಿಡುತ್ತಾರೆ. ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ಮಾಡುವ ಸೇವೆ ಹಾಗಿರಬಾರದು."
		},
		"13": {
			"1": "<sup>1</sup> “ನಿಮ್ಮ ಮಧ್ಯೆ ಯಾವ ಪ್ರವಾದಿಯೆ ಆಗಲಿ, ಕನಸುಗಾರನೇ ಆಗಲಿ, ಎದ್ದು ಬಂದು ನಿಮಗೆ ಗೊತ್ತಿಲ್ಲದ ಇತರ ದೇವರುಗಳನ್ನು ಸೂಚನೆಮಾಡಿ",
			"2": "<sup>2</sup> ‘ಈ ದೇವತೆಗಳನ್ನು ಅವಲಂಬಿಸಿ ಪೂಜಿಸೋಣ’ ಎಂದು ಬೋಧಿಸಬಹುದು; ಆ ಬೋಧನೆಯನ್ನು ರುಜುವಾತು ಪಡಿಸಲು ಒಂದು ಅದ್ಭುತವನ್ನಾಗಲಿ, ಮಹತ್ಕಾರ್ಯವನ್ನಾಗಲಿ ತೋರಿಸಿಕೊಡುತ್ತೇನೆಂದು ಹೇಳಬಹುದು.",
			"3": "<sup>3</sup> ಅವನು ಹೇಳಿದಂತೆಯೇ ನಡೆದರೂ ನೀವು ಅವನ ಮಾತಿಗೆ ಕಿವಿಗೊಡಬಾರದು. ಏಕೆಂದರೆ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನನ್ನು ನೀವು ಪೂರ್ಣ ಹೃದಯದಿಂದ, ಪೂರ್ಣ ಮನಸ್ಸಿನಿಂದ ಪ್ರೀತಿಸುತ್ತೀರೊ ಇಲ್ಲವೋ ಎಂಬುದನ್ನು ತಿಳಿದುಕೊಳ್ಳುವುದಕ್ಕೆ ನಿಮ್ಮನ್ನು ಪರೀಕ್ಷಿಸುತ್ತಾರೆ.",
			"4": "<sup>4</sup> ಸರ್ವೇಶ್ವರ ಹೇಳುವ ಮಾರ್ಗದಲ್ಲೇ ನೀವು ನಡೆದು, ಅವರಲ್ಲಿಯೇ ಭಯಭಕ್ತಿ ಯಳ್ಳವರಾಗಿ, ಅವರ ಆಜ್ಞೆಗಳನ್ನೇ ಅನುಸರಿಸಿ, ಅವರಿಗೇ ವಿಧೇಯರಾಗಿ, ಅವರ ಸೇವೇಮಾಡುತ್ತಾ ಅವರನ್ನೇ ಹೊಂದಿಕೊಂಡಿರಬೇಕು.",
			"5": "<sup>5</sup> ಆ ಪ್ರವಾದಿಗೆ ಅಥವಾ ಆ ಕನಸುಗಾರನಿಗೆ ಮರಣಶಿಕ್ಷೆಯಾಗಬೇಕು. ಗುಲಾಮಗಿರಿಯಲ್ಲಿ ಇದ್ದ ನಿಮ್ಮನ್ನು ಬಿಡುಗಡೆಮಾಡಿ ಈಜಿಪ್ಟ್ ದೇಶದಿಂದ ಕರೆದುತಂದ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ವಿರೋಧವಾಗಿ ಅವನು ದ್ರೋಹದ ಮಾತುಗಳನ್ನಾಡಿ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಹೇಳಿದ ಮಾರ್ಗದಿಂದ ನಿಮ್ಮನ್ನು ತಪ್ಪಿಸಬೇಕು ಎಂದಿದ್ದರೆ, ಅಂಥವನನ್ನು ಕೊಲ್ಲಿಸಿ, ಆ ದುಷ್ಟತ್ವವನ್ನು ನಿಮ್ಮ ಮಧ್ಯೆಯಿಂದ ತೊಡೆದುಹಾಕಬೇಕು.",
			"6": "<sup>6</sup> “ಒಡಹುಟ್ಟಿದ ಅಣ್ಣತಮ್ಮಂದಿರಾಗಲಿ, ಮಗನಾಗಲಿ, ಮಗಳಾಗಲಿ, ಪ್ರಾಣಪ್ರಿಯಳಾದ ಹೆಂಡತಿಯಾಗಲಿ, ಆಪ್ತಮಿತ್ರನಾಗಲಿ, ನಿಮಗೆ ಅಥವಾ ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ಗೊತ್ತಿಲ್ಲದ ಇತರ ದೇವರುಗಳನ್ನು ಪೂಜಿಸೋಣ ಬನ್ನಿ’ ಎಂದು ಒಳ ಒಳಗೇ ನಿಮಗೆ ಬೋಧಿಸಬಹುದು.",
			"7": "<sup>7</sup> ಆ ದೇವರುಗಳು ಹತ್ತಿರವಿರುವ ಜನಾಂಗಗಳ ದೇವರುಗಳಾಗಿದ್ದರೂ ದೂರವಾದವರ ದೇವರುಗಳಾಗಿದ್ದರೂ ಭೂಲೋಕದ ಯಾವ ಭಾಗದವರ ದೇವರುಗಳಾಗಿದ್ದರೂ",
			"8": "<sup>8</sup> ನೀವು ಸಮ್ಮತಿಸಲೂ ಬಾರದು; ಕಿವಿಗೊಡಲೂ ಬಾರದು. ಅಂಥವನನ್ನು ಕನಿಕರಿಸಕೂಡದು, ತಪ್ಪಿಸಕೂಡದು, ಬಚ್ಚಿಡಕೂಡದು; ಬದಲಿಗೆ ಕೊಲ್ಲಿಸಲೇಬೇಕು.",
			"9": "<sup>9</sup> ಆ ಶಿಕ್ಷೆಯನ್ನು ನಡೆಸುವಾಗ ತಪ್ಪು ಕಂಡವನೇ ಮೊದಲು ಕಲ್ಲೆಸೆಯಬೇಕು; ತರುವಾಯ ಜನರೆಲ್ಲರೂ ಎಸೆಯಲಿ.",
			"10": "<sup>10</sup> ಗುಲಾಮಗಿರಿಯಲ್ಲಿದ್ದ ನಿಮ್ಮನ್ನು ಬಿಡುಗಡೆಮಾಡಿ ಈಜಿಪ್ಟ್ ದೇಶದಿಂದ ಕರೆದುತಂದ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಆಶ್ರಯದಿಂದ ಅವನು ನಿಮ್ಮನ್ನು ತಪ್ಪಿಸಬೇಕೆಂದು ಇದ್ದುದರಿಂದ ಅಂಥವನನ್ನು ಕಲ್ಲು ಎಸೆದು ಕೊಲ್ಲಬೇಕು.",
			"11": "<sup>11</sup> ಇದನ್ನು ಇಸ್ರಯೇಲರೆಲ್ಲರು ಕೇಳಿ, ಭಯಪಟ್ಟು, ಅಂಥ ದುಷ್ಕಾರ್ಯವನ್ನು ಮುಂದೆ ಮಾಡರು.",
			"12": "<sup>12</sup> “ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮ ನಿವಾಸಕ್ಕಾಗಿ ಕೊಡುವ ಯಾವುದಾದರೂ ಒಂದು ಊರಿನ ವಿಷಯವನ್ನು ತೆಗೆದುಕೊಳ್ಳೋಣ.",
			"13": "<sup>13</sup> ಅಲ್ಲಿ ವಾಸವಾಗಿರುವ ಇಸ್ರಯೇಲರಲ್ಲಿ ಕೆಲವು ಮಂದಿ ದುಷ್ಟರು, ನಿಮಗೆ ಗೊತ್ತಿಲ್ಲದ ಇತರ ದೇವರುಗಳನ್ನು ತಮ್ಮ ಊರಿನವರಿಗೆ ಸೂಚಿಸಬಹುದು. ‘ಆ ದೇವರುಗಳನ್ನು ಪೂಜಿಸೋಣ ಬನ್ನಿ’ ಎಂದು ಹೇಳಿ ಅವರನ್ನು ಸನ್ಮಾರ್ಗದಿಂದ ತಪ್ಪಿಸಬಹುದು.",
			"14": "<sup>14</sup> ಆ ಸುದ್ದಿಯನ್ನು ನೀವು ಕೇಳಿದರೆ, ಆ ಸಂಗತಿಯನ್ನು ಚೆನ್ನಾಗಿ ವಿಚಾರಿಸಿ ತಿಳಿದುಕೊಳ್ಳಬೇಕು. ಅಂಥ ಅಸಹ್ಯಕಾರ್ಯ ಇಸ್ರಯೇಲರಲ್ಲಿ ನಡೆದದ್ದು ನಿಜವೆಂದು ತಿಳಿದುಬಂದರೆ,",
			"15": "<sup>15</sup> ಆ ಊರನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ಹಾಳುಮಾಡಬೇಕು. ಅದರಲ್ಲಿ ಇರುವ ಎಲ್ಲ ಜನರನ್ನೂ ಜಾನುವಾರುಗಳನ್ನೂ ಕತ್ತಿಯಿಂದ ಸಂಹರಿಸಿಬಿಡಬೇಕು.",
			"16": "<sup>16</sup> ಅದರಲ್ಲಿ ಇರುವ ಎಲ್ಲ ಸಾಮಗ್ರಿಗಳನ್ನು ಗ್ರಾಮಮಧ್ಯದಲ್ಲಿ ಕೂಡಿಸಿ, ಊರನ್ನೂ, ಅದರಲ್ಲಿರುವ ಎಲ್ಲ ಸಾಮಾನುಗಳನ್ನೂ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ನಿಶ್ಯೇಷವಾಗಿ ಸುಟ್ಟುಬಿಡಬೇಕು. ಅದು ಪುನಃ ಯಾವಾಗಲೂ ಹಾಳುದಿಬ್ಬವಾಗಿರಬೇಕು. ಅದನ್ನು ಪುನಃ ಕಟ್ಟಬಾರದು.",
			"17": "<sup>17</sup> ಆ ಶಾಪಗ್ರಸ್ತ ವಸ್ತುಗಳಲ್ಲಿ ನೀವು ಸ್ವಲ್ಪವನ್ನಾದರೂ ತೆಗೆದುಕೊಳ್ಳಬಾರದು.",
			"18": "<sup>18</sup> ನೀವು ಹೀಗೆ ನಡೆದು, ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಮಾತಿಗೆ ಕಿವಿಗೊಟ್ಟು, ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ಅವರ ಎಲ್ಲ ಆಜ್ಞೆಗಳನ್ನು ಕೈಗೊಂಡು ಅವರ ದೃಷ್ಟಿಗೆ ಸರಿಯಾದದ್ದನ್ನು ಮಾಡಿದರೆ, ಅವರು ರೋಷಾಗ್ನಿಯನ್ನು ತಡೆದು, ನಿಮಗೆ ದಯೆಯನ್ನು ತೋರಿಸಿ, ನಿಮ್ಮನ್ನು ಕರುಣಿಸುವರು; ತಾವು ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ವಾಗ್ದಾನಮಾಡಿದಂತೆ ನಿಮ್ಮ ಸಂತತಿಯನ್ನು ಅಭಿವೃದ್ಧಿಗೊಳಿಸುವರು."
		},
		"14": {
			"1": "<sup>1</sup> “ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಮಕ್ಕಳು. ಆದುದರಿಂದ ಸತ್ತವರಿಗಾಗಿ ನಿಮ್ಮ ದೇಹವನ್ನು ಗಾಯಮಾಡಿಕೊಳ್ಳಬಾರದು; ನಿಮ್ಮ ಮುಂದಲೆಯನ್ನು ಬೋಳಿಸಿಕೊಳ್ಳಬಾರದು.",
			"2": "<sup>2</sup> ಏಕೆಂದರೆ ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ಮೀಸಲು ಜನ. ಅವರು ಜಗದಲ್ಲಿರುವ ಸಮಸ್ತಜನಾಂಗಗಳಲ್ಲಿ ನಿಮ್ಮನ್ನೇ ಸ್ವಕೀಯಜನರಾಗುವುದಕ್ಕೆ ಆರಿಸಿಕೊಂಡಿದ್ದಾರೆ.",
			"3": "<sup>3</sup> “ನಿಷಿದ್ಧವಾಗಿರುವ ಯಾವುದನ್ನೂ ನೀವು ತಿನ್ನಕೂಡದು.",
			"4": "<sup>4</sup> ನೀವು ತಿನ್ನಬಹುದಾದ ಪಶುಜಾತಿಗಳು",
			"5": "<sup>5</sup> ಯಾವುವೆಂದರೆ - ದನ, ಕುರಿ, ಆಡು, ದುಪ್ಪಿ, ಜಿಂಕೆ, ಸಾರಂಗ, ಕಾಡುಮೇಕೆ, ಚಿಗರಿ, ಕಡವೆ, ಕೊಂಡಗುರಿ ಇವುಗಳೇ.",
			"6": "<sup>6</sup> ಯಾವ ಪ್ರಾಣಿಯ ಕಾಲ್ಗೊರಸು ಸೀಳಿದೆಯೋ, ಅದು ಮೆಲುಕು ಹಾಕುವಂಥದ್ದಾದರೆ ಅದರ ಮಾಂಸವನ್ನು ತಿನ್ನಬಹುದು.",
			"7": "<sup>7</sup> ಆದರೆ ಯಾವ ಪ್ರಾಣಿ ಮೆಲುಕು ಹಾಕಿದರೂ ಗೊರಸು ಸೀಳಿರುವುದಿಲ್ಲವೋ ಮತ್ತು ಯಾವ ಪ್ರಾಣಿಯ ಗೊರಸು ಸೀಳಿದ್ದರೂ ಮೆಲುಕು ಹಾಕುವುದಿಲ್ಲವೋ ಅವುಗಳ ಮಾಂಸವನ್ನು ನೀವು ತಿನ್ನಬಾರದು. ಉದಾಹರಣೆಗೆ, ಒಂಟೆ, ಮೊಲ, ಬೆಟ್ಟದ ಮೊಲ ಇವುಗಳು ಮೆಲುಕು ಹಾಕುವಂಥವುಗಳಾದರೂ ಸೀಳುಗೊರಸು ಇಲ್ಲವಾದ ಕಾರಣ ಅವು ನಿಮಗೆ ಅಶುದ್ಧ.",
			"8": "<sup>8</sup> ಹಂದಿಯ ಗೊರಸು ಸೀಳಿದ್ದರೂ ಅದು ಮೆಲುಕು ಹಾಕುವುದಿಲ್ಲವಾದುದರಿಂದ ಅದು ನಿಮಗೆ ಅಶುದ್ಧ. ಇವುಗಳ ಮಾಂಸವನ್ನು ನೀವು ತಿನ್ನಕೂಡದು; ಇವುಗಳ ಹೆಣವನ್ನು ಮುಟ್ಟಕೂಡದು.",
			"9": "<sup>9</sup> “ಜಲಜಂತುಗಳಲ್ಲಿ ನೀವು ತಿನ್ನಬಹುದಾದವುಗಳು ಯಾವುವೆಂದರೆ - ಯಾವ ಜಾತಿಯ ಪ್ರಾಣಿಗೆ ರೆಕ್ಕೆ ಇದ್ದು ಮೈಯೆಲ್ಲಾ ಪರೆಪರೆ ಆಗಿರುವುದೋ ಅದರ ಮಾಂಸವನ್ನು ತಿನ್ನಬಹುದು.",
			"10": "<sup>10</sup> ಯಾವ ಜಾತಿಗೆ ರೆಕ್ಕೆಯೂ ಪರೆಪರೆಯಾದ ಮೈಯೂ ಇರುವುದಿಲ್ಲವೋ ಅವುಗಳ ಮಾಂಸವನ್ನು ನೀವು ತಿನ್ನಕೂಡದು; ಅದು ನಿಮಗೆ ಅಶುದ್ಧ.",
			"11": "<sup>11</sup> “ಎಲ್ಲ ಶುದ್ಧಪಕ್ಷಿಗಳ ಮಾಂಸವನ್ನು ತಿನ್ನಬಹುದು.",
			"12": "<sup>12</sup> ತಿನ್ನಕೂಡದವುಗಳು ಯಾವುವು ಎಂದರೆ - ಗರುಡ,",
			"13": "<sup>13</sup> ಬೆಟ್ಟದ ಹದ್ದು, ಕ್ರೌಂಚ, ಗಿಡುಗ, ಹಕ್ಕಿಸಾಲೆ, ಸಕಲವಿಧವಾದ ಹದ್ದು,",
			"14": "<sup>14</sup> ಸಕಲವಿಧವಾದ ಕಾಗೆ, ಉಷ್ಟ್ರಪಕ್ಷಿ, ಉಲೂಕ, ಕಡಲ್ಹಕ್ಕಿ, ಸಕಲವಿಧವಾದ ಡೇಗೆ,",
			"16": "<sup>16</sup> ಗೂಬೆ, ಹೆಗ್ಗೂಬೆ, ಕರೇಟು, ಚೀಲಮೂಗಿ, ರಣಹದ್ದು,",
			"18": "<sup>18</sup> ನೀರುಕಾಗೆ, ಕೊಕ್ಕರೆ, ಸಕಲವಿಧವಾದ ಬಕ, ಹೆಡೆಹಕ್ಕಿ, ಕಣ್ಣಕಪಡಿ.",
			"19": "<sup>19</sup> “ರೆಕ್ಕೆಯುಳ್ಳ ಕ್ರಿಮಿಕೀಟಗಳೆಲ್ಲವು ನಿಮಗೆ ಅಶುದ್ಧ; ಅವುಗಳನ್ನು ತಿನ್ನಕೂಡದು.",
			"20": "<sup>20</sup> ರೆಕ್ಕೆ ಉಳ್ಳ ಪಕ್ಷಿಗಳಲ್ಲಿ ಶುದ್ಧಜಾತಿಯವುಗಳನ್ನು ಮಾತ್ರ ತಿನ್ನಬಹುದು.",
			"21": "<sup>21</sup> “ಸತ್ತುಬಿದ್ದದ್ದನ್ನು ನೀವು ತಿನ್ನಕೂಡದು; ನಿಮ್ಮ ಊರಲ್ಲಿರುವ ಪರದೇಶಿಯರಿಗೆ ಅದನ್ನು ತಿನ್ನುವುದಕ್ಕೆ ಕೊಡಬಹುದು, ಅನ್ಯರಿಗೆ ಮಾರಬಹುದು; ನೀವಾದರೋ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ಪ್ರತಿಷ್ಠಿತರಾದವರು. “ಆಡುಮರಿಯ ಮಾಂಸವನ್ನು ಅದರ ತಾಯಿಯ ಹಾಲಿನಲ್ಲಿ ಬೇಯಿಸಕೂಡದು.",
			"22": "<sup>22</sup> “ಪ್ರತಿ ವರ್ಷದ ನಿಮ್ಮ ಹೊಲದ ಬೆಳೆಯಲ್ಲಿ ದಶಮಭಾಗವನ್ನು ಪ್ರತ್ಯೇಕಿಸಬೇಕು.",
			"23": "<sup>23</sup> ಧಾನ್ಯ, ದ್ರಾಕ್ಷಿ, ಎಣ್ಣೆ ಇವುಗಳಲ್ಲಿ ದಶಮಭಾಗವನ್ನು ಹಾಗು ದನಕುರಿಗಳ ಚೊಚ್ಚಲು ಮರಿಗಳನ್ನು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಸನ್ನಿಧಿಯಲ್ಲಿ, ಅವರು ತಮ್ಮ ಹೆಸರನ್ನು ಸ್ಥಾಪಿಸಿಕೊಳ್ಳುವುದಕ್ಕೆ ಆರಿಸಿಕೊಳ್ಳುವ ಸ್ಥಳದಲ್ಲೇ, ನೀವು ತಿನ್ನಬೇಕು. ಹೀಗೆ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಲ್ಲೇ ಯಾವಾಗಲು ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿ ಇರುವುದನ್ನು ಅಭ್ಯಾಸಮಾಡಿಕೊಳ್ಳುವಿರಿ.",
			"24": "<sup>24</sup> “ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಆರಿಸಿಕೊಳ್ಳುವ ಸ್ಥಳ ನಿಮಗೆ ದೂರವಾಗಿದ್ದು, ಆ ಕಾಣಿಕೆಗಳನ್ನು ಅಷ್ಟು ದೂರ ಹೊತ್ತುಕೊಂಡು ಹೋಗುವುದು ಅಸಾಧ್ಯವಾದರೆ, ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ಅಭಿವೃದ್ಧಿಯನ್ನುಂಟುಮಾಡುವ ಕಾಲದಲ್ಲಿ ಅವುಗಳನ್ನು ಮಾರಿಬಿಡಬಹುದು.",
			"25": "<sup>25</sup> ಹೀಗೆ ಮಾರಿ, ಬಂದ ಹಣವನ್ನು ಸರ್ವೇಶ್ವರ ಆರಿಸಿಕೊಂಡ ಸ್ಥಳಕ್ಕೆ ತೆಗೆದುಕೊಂಡು ಹೋಗಿ,",
			"26": "<sup>26</sup> ಅಲ್ಲಿ ನಿಮ್ಮ ಇಷ್ಟಾನುಸಾರ ದನ, ಕುರಿ, ದ್ರಾಕ್ಷಾರಸ, ಮದ್ಯ ಮೊದಲಾದವುಗಳನ್ನು ಕೊಂಡುಕೊಂಡು ನೀವೂ ನಿಮ್ಮ ಮನೆಯವರೂ ನಿಮ್ಮ ಊರಿನ ಲೇವಿಯರೂ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಊಟಮಾಡಿ ಸಂತೋಷವಾಗಿರಬೇಕು.",
			"27": "<sup>27</sup> ಲೇವಿಯರಿಗೆ ನಿಮ್ಮ ಹಾಗೆ ಯಾವ ಸ್ವಂತ ಸೊತ್ತು ಇಲ್ಲವಾದುದರಿಂದ ನೀವು ಅವರನ್ನು ಕೈಬಿಡಬಾರದು.",
			"28": "<sup>28</sup> “ಪ್ರತಿ ಮೂರನೆಯ ವರ್ಷದ ಬೆಳೆಯ ದಶಮಭಾಗವನ್ನು ತಂದು ನೀವಿರುವ ಊರಲ್ಲಿಯೇ ಕೂಡಿಸಬೇಕು.",
			"29": "<sup>29</sup> ಆಗ ಊರಲ್ಲಿರುವ ಅನ್ಯದೇಶದವರು, ತಾಯಿತಂದೆಯಿಲ್ಲದವರು, ವಿಧವೆಯರು ಹಾಗು ನಿಮ್ಮ ಹಾಗೆ ಸ್ವಂತ ಸೊತ್ತು ಹೊಂದದೆಯಿರುವ ಲೇವಿಯರೂ ಉಂಡು ಸಂತೋಷವಾಗಿರುವರು. ನೀವು ಹೀಗೆ ನಡೆದುಕೊಂಡರೆ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮ ಎಲ್ಲ ಪ್ರಯತ್ನಗಳನ್ನು ಸಫಲಗೊಳಿಸುವರು."
		},
		"15": {
			"1": "<sup>1</sup> “ಏಳು ಸಂವತ್ಸರಗಳು ಕಳೆದನಂತರ ಬಡವರಿಗೆ ಬಿಡುಗಡೆ ಸಿಗಬೇಕು.",
			"2": "<sup>2</sup> ಹೇಗೆಂದರೆ, ಸರ್ವೇಶ್ವರನೇ ನೇಮಿಸಿದ ಬಿಡುಗಡೆಯ ಸಂವತ್ಸರ ಪ್ರಕಟವಾಗಿದೆ. ಆದುದರಿಂದ ಸಾಲಕೊಟ್ಟವನು ತೆಗೆದುಕೊಂಡವನಿಗೆ ಆ ಸಾಲವನ್ನು ಬಿಟ್ಟುಬಿಡಬೇಕು; ಅವನು ಸ್ವದೇಶದವನಿಗೆ ಕೊಟ್ಟ ಸಾಲವನ್ನು ಕೇಳಬಾರದು.",
			"3": "<sup>3</sup> ಅನ್ಯದೇಶದವರಿಗೆ ಕೊಟ್ಟ ಸಾಲವನ್ನು ಕೇಳಿ ತೆಗೆದುಕೊಳ್ಳಬಹುದೇ ಹೊರತು ಸ್ವದೇಶದವನಿಗೆ ಕೊಟ್ಟದ್ದನ್ನು ಕೇಳಕೂಡದು.",
			"4": "<sup>4</sup> “ಆದರೆ ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಮಾತಿಗೆ ಕಿವಿಗೊಟ್ಟು, ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ಈ ಧರ್ಮೋಪದೇಶವನ್ನೆಲ್ಲಾ ಅನುಸರಿಸಿದರೆ, ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ಸ್ವದೇಶವಾಗಿ ಕೊಡುವ ನಾಡಿನಲ್ಲಿ ನಿಮ್ಮನ್ನು ಅಭಿವೃದ್ಧಿಪಡಿಸುವರು. ಈ ಕಾರಣ, ನಿಮ್ಮಲ್ಲಿ ಬಡವರೇ ಇರುವುದಿಲ್ಲ.",
			"6": "<sup>6</sup> ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ವಾಗ್ದಾನಮಾಡಿದಂತೆ ನಿಮ್ಮನ್ನು ಅಭಿವೃದ್ಧಿಪಡಿಸುವರು. ನೀವು ಅನೇಕ ಜನಗಳಿಗೆ ಸಾಲಕೊಡುವಿರೇ ಹೊರತು ಅವರಿಂದ ಸಾಲ ತೆಗೆದುಕೊಳ್ಳುವುದಿಲ್ಲ; ಅನೇಕ ಜನಾಂಗಗಳ ಮೇಲೆ ದೊರೆತನ ಮಾಡುವಿರೇ ಹೊರತು ಅವರು ನಿಮ್ಮ ಮೇಲೆ ದೊರೆತನ ಮಾಡುವುದಿಲ್ಲ.",
			"7": "<sup>7</sup> “ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ಕೊಡುವ ನಾಡಿನ ಯಾವ ಊರಲ್ಲಿಯಾದರೂ ಸ್ವದೇಶದವನಾದ ಬಡವನು ಇದ್ದರೆ, ನೀವು ಆ ಬಡ ಸಹೋದರನ ಬಗ್ಗೆ ಮನಸ್ಸನ್ನು ಕಠಿಣಮಾಡಿಕೊಳ್ಳಬೇಡಿ. ಅವನಿಗೆ ಸಹಾಯಮಾಡದೆ ಇರಬೇಡಿ.",
			"8": "<sup>8</sup> ನೀವು ಕೈದೆರೆದು ಅವನಿಗೆ ಅಗತ್ಯವಾಗಿ ಬೇಕಾದುದನ್ನು ಕೊಟ್ಟು ಸಹಾಯಮಾಡಬೇಕು.",
			"9": "<sup>9</sup> ಬಿಡುಗಡೆಯಾಗುವ ಏಳನೆಯ ವರ್ಷ ಸಮೀಪಿಸಿಬಿಟ್ಟಿತು ಎಂದು ನೀಚವಾದ ಆಲೋಚನೆಮಾಡಿ, ಬಡ ಸಹೋದರನ ಬಗ್ಗೆ ಮೋರೆ ಕರ್ರಗೆಮಾಡಿಕೊಂಡು, ಅವನಿಗೆ ಏನೂ ಕೊಡದೆ ಇರಬಾರದು, ಎಚ್ಚರಿಕೆ! ಹಾಗೆ ಸಹಾಯವನ್ನು ನಿರಾಕರಿಸಿದರೆ, ಅವನು ನಿಮ್ಮ ಬಗ್ಗೆ ಸರ್ವೇಶ್ವರನಿಗೆ ಮೊರೆ ಇಡುವನು; ಆಗ ನೀವು ದೋಷಿಗಳಾಗುತ್ತೀರಿ.",
			"10": "<sup>10</sup> ಕೊಡುವಾಗ ನೀವು ಬೇಸರಗೊಳ್ಳದೆ ಉದಾರ ಮನಸ್ಸಿನಿಂದಲೇ ಕೊಡಬೇಕು. ಇದರಿಂದ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮ ಕೆಲಸಕಾರ್ಯಗಳಲ್ಲೂ ಪ್ರಯತ್ನಗಳಲ್ಲೂ ನಿಮ್ಮನ್ನು ಅಭಿವೃದ್ಧಿಪಡಿಸುವರು.",
			"11": "<sup>11</sup> ನಾಡಿನಲ್ಲಿ ಯಾವಾಗಲೂ ಬಡವರು ಇದ್ದೇ ಇರುವರು; ಆದುದರಿಂದ ನೀವು ಸ್ವದೇಶದವರಾದ ಬಡವರಿಗೂ ಗತಿಯಿಲ್ಲದವರಿಗೂ ಕೈನೀಡಿ ಸಹಾಯಮಾಡಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿದ್ದೇನೆ.",
			"12": "<sup>12</sup> “ಸ್ವದೇಶದವರಲ್ಲಿ ಯಾವ ಹಿಬ್ರಿಯ ಪುರುಷನಾಗಲಿ, ಸ್ತ್ರೀಯಾಗಲಿ ನಿಮಗೆ ಗುಲಾಮರಾಗಿ ಮಾರಲ್ಪಟ್ಟಿದ್ದರೆ ಅಂಥವರು ಆರು ವರ್ಷಗಳವರೆಗೂ ನಿಮ್ಮ ಸೇವೆ ಮಾಡಲಿ, ಏಳನೆಯ ವರ್ಷದಲ್ಲಿ ನೀವು ಅವರನ್ನು ಬಿಟ್ಟುಬಿಡಬೇಕು.",
			"13": "<sup>13</sup> ಬಿಡುವಾಗ ಬರಿಗೈಯಲ್ಲಿ ಕಳುಹಿಸಿಬಿಡಬಾರದು.",
			"14": "<sup>14</sup> ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ಅನುಗ್ರಹಿಸಿರುವ ಮೇರೆಗೆ ನಿಮಗಿರುವ ಆಡುಕುರಿಗಳು, ದವಸಧಾನ್ಯ, ದ್ರಾಕ್ಷರಸ ಇವುಗಳಲ್ಲಿ ಉದಾರವಾಗಿ ಕೊಟ್ಟುಕಳುಹಿಸಬೇಕು.",
			"15": "<sup>15</sup> ಈಜಿಪ್ಟ್ ದೇಶದಲ್ಲಿ ನೀವೇ ಗುಲಾಮರಾಗಿದ್ದಿರಿ; ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮನ್ನು ಬಿಡುಗಡೆ ಮಾಡಿದರೆಂಬುದನ್ನು ಜ್ಞಾಪಕಮಾಡಿಕೊಳ್ಳಿ. ಇದಕ್ಕಾಗಿಯೇ, ನಾನೀಗ ಈ ಆಜ್ಞೆಯನ್ನು ನಿಮಗೆ ಕೊಟ್ಟಿದ್ದೇನೆ.",
			"16": "<sup>16</sup> “ಒಂದು ವೇಳೆ ಅಂಥ ಗುಲಾಮನು ನಿಮ್ಮ ಬಳಿ ಸುಖವಾಗಿದ್ದು, ನಿಮ್ಮನ್ನೂ ನಿಮ್ಮ ಮನೆಯವರನ್ನೂ ಪ್ರೀತಿಸಿ, ತಾನು ಬಿಡುಗಡೆ ಆಗಿ ಹೋಗಲು ಒಲ್ಲೆನೆಂದು ಹೇಳಬಹುದು;",
			"17": "<sup>17</sup> ಆಗ ನೀವು ದಬ್ಬಳದಿಂದ ಅವನ ಕಿವಿಯನ್ನು ಚುಚ್ಚಿ ಕದಕ್ಕೆ ಸಿಕ್ಕಿಸಬೇಕು. ಆ ದಿನ ಮೊದಲ್ಗೊಂಡು ಅವನು ನಿಮಗೆ ಶಾಶ್ವತ ಗುಲಾಮನಾಗಿರುತ್ತಾನೆ. ಅದೇ ರೀತಿಯಲ್ಲಿ ಗುಲಾಮಸ್ತ್ರೀಯರ ವಿಷಯದಲ್ಲೂ ನಡೆದುಕೊಳ್ಳಬೇಕು.",
			"18": "<sup>18</sup> “ಗುಲಾಮನನ್ನು ಬಿಡುಗಡೆ ಮಾಡುವುದು ಕಷ್ಟವೆಂದು ಹೇಳಿಕೊಳ್ಳಬಾರದು. ಅವನು ಆರು ವರ್ಷಗಳು ನಿಮ್ಮ ಗುಲಾಮನಾಗಿದ್ದು ಕೂಲಿಯಾಳಿಗಿಂತ ಎರಡರಷ್ಟು ಪ್ರಯೋಜನವನ್ನು ಉಂಟುಮಾಡಿದನಲ್ಲವೆ? ನೀವು ಹೀಗೆ ನಡೆದುಕೊಂಡರೆ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮ ಎಲ್ಲ ಪ್ರಯತ್ನಗಳನ್ನು ಸಫಲಗೊಳಿಸುವರು.",
			"19": "<sup>19</sup> “ದನಗಳಲ್ಲೂ ಆಡುಕುರಿಗಳಲ್ಲೂ ಚೊಚ್ಚಲಾದ ಗಂಡನ್ನು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗಾಗಿ ಪ್ರತಿಷ್ಠಿಸಬೇಕು. ಚೊಚ್ಚಲಾದ ಹೋರಿಯಿಂದ ಕೆಲಸಮಾಡಿಸಬಾರದು; ಚೊಚ್ಚಲಾದ ಕುರಿಯ ಉಣ್ಣೆಯನ್ನು ಕತ್ತರಿಸಬಾರದು.",
			"20": "<sup>20</sup> ಪ್ರತಿ ವರ್ಷ ನೀವೂ ನಿಮ್ಮ ಮನೆಯವರೂ ಅವುಗಳನ್ನು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಆರಿಸಿಕೊಳ್ಳುವ ಸ್ಥಳದಲ್ಲೆ, ಅವರ ಸನ್ನಿಧಿಯಲ್ಲೇ ಕೊಯ್ದು ತಿನ್ನಬೇಕು.",
			"21": "<sup>21</sup> “ಅದು ಕುಂಟಾಗಿ, ಕುರುಡಾಗಿ, ಇಲ್ಲವೆ ಬೇರೆ ವಿಧದಲ್ಲಿ ದೋಷವುಳ್ಳದ್ದಾಗಿದ್ದರೆ ಅದನ್ನು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ಬಲಿಯಾಗಿ ಸಮರ್ಪಿಸಕೂಡದು;",
			"22": "<sup>22</sup> ಅದನ್ನು ನಿಮ್ಮ ಊರಲ್ಲೇ ತಿನ್ನಬೇಕು. ಶುದ್ಧರೂ ಅಶುದ್ಧರೂ ಜಿಂಕೆದುಪ್ಪಿಗಳ ಮಾಂಸವನ್ನು ತಿನ್ನುವ ಪ್ರಕಾರ ಇವುಗಳನ್ನೂ ತಿನ್ನಬಹುದು.",
			"23": "<sup>23</sup> ನೀವು ರಕ್ತವನ್ನು ಮಾತ್ರ ಭುಜಿಸಬಾರದು; ಅದನ್ನು ನೀರಿನಂತೆ ಭೂಮಿಯ ಮೇಲೆ ಸುರಿದುಬಿಡಬೇಕು."
		},
		"16": {
			"1": "<sup>1</sup> “ನೀವು ಚೈತ್ರಮಾಸದಲ್ಲಿ ಪಾಸ್ಕಹಬ್ಬವನ್ನು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಗೌರವಾರ್ಥ ಆಚರಿಸಬೇಕು. ಆ ಮಾಸದಲ್ಲೇ ರಾತ್ರಿವೇಳೆಯಲ್ಲಿ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮನ್ನು ಈಜಿಪ್ಟ್ ದೇಶದಿಂದ ಬಿಡುಗಡೆ ಮಾಡಿದ್ದು.",
			"2": "<sup>2</sup> ಆಡುಕುರಿಗಳಲ್ಲಾಗಲಿ, ದನಗಳಲ್ಲಿ ಆಗಲಿ ಪಾಸ್ಕದ ಪಶುವನ್ನು, ಸರ್ವೇಶ್ವರ ತಮ್ಮ ನಾಮಸ್ಥಾಪನೆಗಾಗಿ ಆರಿಸಿಕೊಳ್ಳುವ ಸ್ಥಳದಲ್ಲೇ ಅವರಿಗಾಗಿ ನೀವು ಅದನ್ನು ವಧಿಸಬೇಕು.",
			"3": "<sup>3</sup> ಅದರೊಂದಿಗೆ ಹುಳಿಹಾಕಿದ ರೊಟ್ಟಿಯನ್ನು ತಿನ್ನಬಾರದು; ಏಕೆಂದರೆ ನೀವು ರೊಟ್ಟಿಗೆ ಹುಳಿಹಾಕುವುದಕ್ಕೆ ಅವಕಾಶವಿಲ್ಲದೆ ಅವಸರದಿಂದ ಈಜಿಪ್ಟನ್ನು ಬಿಟ್ಟುಬಂದಿರಿ. ಈಜಿಪ್ಟ್ ದೇಶದಿಂದ ನಿಮಗೆ ಬಿಡುಗಡೆಯಾದ ದಿನವು ನಿಮ್ಮ ಜೀವಮಾನವೆಲ್ಲಾ ನೆನಪಿನಲ್ಲಿರುವಂತೆ, ಕಷ್ಟವನ್ನು ಸೂಚಿಸುವ ಹುಳಿಯಿಲ್ಲದ ರೊಟ್ಟಿಗಳನ್ನು ನೀವು ಏಳು ದಿವಸಗಳವರೆಗೂ ತಿನ್ನಬೇಕು.",
			"4": "<sup>4</sup> ಆ ಏಳು ದಿವಸವೂ ನಿಮ್ಮ ನಾಡಿನಲ್ಲೆಲ್ಲಾ ಹುಳಿಬೆರೆತ ಯಾವ ಪದಾರ್ಥವೂ ನಿಮ್ಮ ಬಳಿ ಇರಬಾರದು. ಮೊದಲನೆಯ ದಿನದ ಸಂಜೆಯಲ್ಲಿ ನೀವು ವಧಿಸಿದ ಪಶುಮಾಂಸದಲ್ಲಿ ಸ್ವಲ್ಪವನ್ನಾದರೂ ಮರುದಿನದವರೆಗೆ ಉಳಿಸಬಾರದು.",
			"5": "<sup>5</sup> “ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ಕೊಡುವ ಯಾವ ಊರಲ್ಲೂ, ಪಾಸ್ಕದಪಶುವನ್ನು ವಧಿಸಬಾರದು;",
			"6": "<sup>6</sup> ಅವರು ತಮ್ಮ ನ್ಯಾಯಸ್ಥಾಪನೆಗಾಗಿ ಆರಿಸಿಕೊಳ್ಳುವ ಸ್ಥಳದಲ್ಲೇ ನೀವು ಈಜಿಪ್ಟ್ ದೇಶದಿಂದ ಹೊರಟುಬಂದ ಕಾಲವಾದ ಸೂರ್ಯಾಸ್ತಮಾನಕಾಲದಲ್ಲಿ, ಅದನ್ನು ವಧಿಸಬೇಕು.",
			"7": "<sup>7</sup> ಆ ಸ್ಥಳದಲ್ಲೇ ನೀವು ಅದನ್ನು ಅಡಿಗೆಮಾಡಿ ತಿನ್ನಬೇಕು. ಮರುದಿವಸ ನಿಮ್ಮ ನಿಮ್ಮ ಮನೆಗಳಿಗೆ ನೀವು ಹೊರಟುಹೋಗಬಹುದು.",
			"8": "<sup>8</sup> ಆರು ದಿವಸ ನೀವು ಹುಳಿಯಿಲ್ಲದ ರೊಟ್ಟಿಗಳನ್ನು ತಿನ್ನಬೇಕು. ಏಳನೆಯ ದಿನ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಗೌರವಾರ್ಥ ಸಭೆಸೇರಬೇಕು. ಆ ದಿನದಲ್ಲಿ ಯಾವ ದುಡಿಮೆಯನ್ನೂ ಮಾಡಕೂಡದು.",
			"9": "<sup>9</sup> “ಬೆಳೆ ಕೊಯ್ಯವುದಕ್ಕೆ ಕುಡುಗೋಲು ಹಾಕುವ ಕಾಲ ಮೊದಲ್ಗೊಂಡು ಏಳು ವಾರಗಳನ್ನು ಎಣಿಸಿ,",
			"10": "<sup>10</sup> ಆ ಏಳು ವಾರಗಳಾದನಂತರ, ಪಂಚಾಶತ್ತಮ ದಿನದ ಜಾತ್ರೆಯನ್ನು ಆಚರಿಸಬೇಕು. ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ಅನುಗ್ರಹಿಸಿದ ಬೆಳೆಗೆ ತಕ್ಕಷ್ಟು ಕಾಣಿಕೆಯನ್ನು ನೀವು ಆಗ ಅವರಿಗಾಗಿ ತರಬೇಕು.",
			"11": "<sup>11</sup> ಮತ್ತು ಅವರು ತಮ್ಮ ನಾಮಸ್ಥಾಪನೆಗಾಗಿ ಆರಿಸಿಕೊಳ್ಳುವ ಸ್ಥಳದಲ್ಲಿ ನೀವು, ನಿಮ್ಮ ಗಂಡುಹೆಣ್ಣು ಮಕ್ಕಳು, ಗಂಡುಹೆಣ್ಣು ಆಳುಗಳು, ನಿಮ್ಮ ಊರಲ್ಲಿರುವ ಲೇವಿಯರು, ಪರದೇಶದವರು, ತಾಯಿತಂದೆಯಿಲ್ಲದವರು ಹಾಗು ವಿಧವೆಯರು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸಂಭ್ರಮದಿಂದಿರಬೇಕು.",
			"12": "<sup>12</sup> ಈಜಿಪ್ಟ್ ದೇಶದಲ್ಲಿ ಗುಲಾಮರಾಗಿದ್ದೆವೆಂಬುದನ್ನು ನೀವು ನೆನಪಿಗೆ ತಂದುಕೊಂಡು ಈ ವಿಧಿಗಳನ್ನು ಆಚರಿಸಬೇಕು.",
			"13": "<sup>13</sup> “ನೀವು ಕಣದ ಕೆಲಸವನ್ನೂ ಆಲೆಯ ಕೆಲಸವನ್ನೂ ಪೂರೈಸಿ, ಬೆಳೆಯನ್ನು ಮನೆಗೆ ತಂದಾಗ, ಏಳು ದಿನಗಳವರೆಗೂ ಪರ್ಣಕುಠೀರಗಳ ಜಾತ್ರೆಯನ್ನು ಆಚರಿಸಬೇಕು.",
			"14": "<sup>14</sup> ಅದರಲ್ಲಿ ನೀವೂ ನಿಮ್ಮ ಗಂಡುಹೆಣ್ಣು ಮಕ್ಕಳೂ ಗಂಡುಹೆಣ್ಣು ಆಳುಗಳೂ ನಿಮ್ಮ ಊರಲ್ಲಿರುವ ಲೇವಿಯರೂ ಪರದೇಶದವರೂ ತಾಯಿತಂದೆಯಿಲ್ಲದವರೂ ಹಾಗು ವಿಧವೆಯರೂ ಸಂಭ್ರಮದಿಂದಿರಬೇಕು.",
			"15": "<sup>15</sup> ನಿಮ್ಮ ಸರ್ವೇಶ್ವರ ಆರಿಸಿಕೊಳ್ಳುವ ಸ್ಥಳದಲ್ಲಿ ನೀವು ಏಳು ದಿನವೂ ಆ ಜಾತ್ರೆಯನ್ನು ಆಚರಿಸಬೇಕು. ನಿಮ್ಮ ವ್ಯವಸಾಯವನ್ನೂ ಬೇರೆ ಎಲ್ಲಾ ಕೆಲಸಗಳನ್ನೂ ಅವರು ಸಫಲಗೊಳಿಸಿದ್ದರಿಂದ ನೀವು ಬಹಳ ಆನಂದದಿಂದಿರಬೇಕು.",
			"16": "<sup>16</sup> “ಹುಳಿಯಿಲ್ಲದ ರೊಟ್ಟಿಗಳ ಜಾತ್ರೆಯಲ್ಲಿ, ಪಂಚಾಶತ್ತಮ ದಿನದ ಜಾತ್ರೆಯಲ್ಲಿ, ಕುಟೀರಗಳ ಜಾತ್ರೆಯಲ್ಲಿ, ಹೀಗೆ ವರ್ಷಕ್ಕೆ ಮೂರಾವರ್ತಿ ನಿಮ್ಮಲ್ಲಿರುವ ಗಂಡಸರೆಲ್ಲರೂ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಸನ್ನಿಧಿಯಲ್ಲಿ, ಅವರು ಆರಿಸಿಕೊಳ್ಳುವ ಸ್ಥಳದಲ್ಲಿ ಕಾಣಿಸಿಕೊಳ್ಳಬೇಕು. ಕೈಯಲ್ಲಿ ಕಾಣಿಕೆಯಿಲ್ಲದೆ ಒಬ್ಬರೂ ಅವರ ಸನ್ನಿಧಿಗೆ ಬರಕೂಡದು.",
			"17": "<sup>17</sup> ಪ್ರತಿಯೊಬ್ಬನು ತನಗೆ ಸರ್ವೇಶ್ವರ ಅನುಗ್ರಹಿಸಿದ ಆದಾಯದ ಮೇರೆಗೆ ತನ್ನ ಶಕ್ತಿಗೆ ಅನುಸಾರ ಕೊಡಬೇಕು.",
			"18": "<sup>18</sup> “ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮ ಪ್ರತಿಯೊಂದು ಕುಲಕ್ಕೆ ಕೊಡುವ ಎಲ್ಲ ಊರುಗಳಲ್ಲಿ ನ್ಯಾಯಾಧಿಪತಿಗಳನ್ನೂ ಅಧಿಕಾರಿಗಳನ್ನೂ ನೀವು ನೇಮಿಸಬೇಕು. ಅಂಥವರು ಜನರಿಗೆ ನ್ಯಾಯವನ್ನು ವಿಚಾರಿಸಿ, ಸರಿಯಾದ ತೀರ್ಪುಕೊಡಬೇಕು.",
			"19": "<sup>19</sup> “ನೀವು ನ್ಯಾಯವನ್ನು ಬಿಟ್ಟು ತೀರ್ಮಾನ ಮಾಡಬಾರದು; ಪಕ್ಷಪಾತ ಕೂಡದು; ಲಂಚ ತೆಗೆದುಕೊಳ್ಳಬಾರದು. ಲಂಚವು ಬುದ್ಧಿವಂತರನ್ನೂ ಕುರುಡರನ್ನಾಗಿಸುತ್ತದೆ; ನಿರಪರಾಧಿಗಳ ನ್ಯಾಯವನ್ನು ಕೆಡಿಸುತ್ತದೆ.",
			"20": "<sup>20</sup> ನೀವು ನ್ಯಾಯವನ್ನು ಮಾತ್ರ ಅನುಸರಿಸಬೇಕು. ಹಾಗೆ ನಡೆದರೆ, ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಕೊಡುವ ನಾಡನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡು ಬಾಳುವಿರಿ.",
			"21": "<sup>21</sup> “ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗಾಗಿ ನೀವು ಕಟ್ಟಿಸಿಕೊಳ್ಳುವ ಬಲಿಪೀಠದ ಬಳಿ ಅಶೇರವೆಂಬ ವಿಗ್ರಹಸ್ತಂಭವನ್ನು, ಅದು ಯಾವ ವಿಧವಾದ ಮರದಿಂದ ಮಾಡಿರಲಿ, ಅದನ್ನು ನೆಡಬಾರದು.",
			"22": "<sup>22</sup> ಕಲ್ಲಿನ ಕಂಬವನ್ನೂ ನಿಲ್ಲಿಸಬಾರದು; ಇದು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ಹೇಯವಾದುದು."
		},
		"17": {
			"1": "<sup>1</sup> “ಯಾವುದಾದರೊಂದು ಕುಂದುಕೊರತೆಯಾಗಲಿ, ದುರ್ಲಕ್ಷಣವಾಗಲಿ ಇರುವ ದನಕುರಿಗಳನ್ನು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ಸಮರ್ಪಿಸಬಾರದು. ಅಂಥವುಗಳನ್ನು ಅವರು ಸಹಿಸರು.",
			"2": "<sup>2</sup> “ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ಕೊಡುವ ಊರುಗಳೊಂದರಲ್ಲಿ ಒಬ್ಬ ಪುರುಷ ಅಥವಾ ಸ್ತ್ರೀ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ನಿಬಂಧನೆಯನ್ನು ಮೀರಿದ್ದಾರೆ ಎಂದೂ,",
			"3": "<sup>3</sup> ಅನ್ಯ ದೇವರುಗಳನ್ನಾಗಲಿ, ಪೂಜಿಸಬಾರದೆಂದು ನಿಷೇಧಿಸಿರುವ ಸೂರ್ಯಚಂದ್ರ ನಕ್ಷತ್ರಗಳನ್ನಾಗಲಿ, ಪೂಜಿಸಿ, ಆರಾಧಿಸಿ ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ದುಷ್ಟವಾದುದನ್ನು ನಡೆಸಿದ್ದಾರೆ ಎಂದೂ",
			"4": "<sup>4</sup> ನಿಮಗೆ ತಿಳಿದರೆ ಅದನ್ನು ನೀವು ಸೂಕ್ಷ್ಮವಾಗಿ ವಿಚಾರಣೆ ಮಾಡಬೇಕು. ಆಗ ಇಸ್ರಯೇಲರಲ್ಲಿ ಯಾರಿಂದಲಾದರೂ ಆ ನಿಷಿದ್ಧಕಾರ್ಯ ನಡೆದದ್ದು ನಿಜವೆಂದು ತಿಳಿದುಬಂದರೆ,",
			"5": "<sup>5</sup> ಆ ಪುರುಷನನ್ನು ಅಥವಾ ಆ ಸ್ತ್ರೀಯನ್ನು ಹಿಡಿದು, ಊರ ಹೊರಗೆ ತಂದು, ಕಲ್ಲುಬೀರಿ ಕೊಲ್ಲಬೇಕು.",
			"6": "<sup>6</sup> ಒಬ್ಬನೇ ಒಬ್ಬ ಸಾಕ್ಷಿಯ ಮಾತಿನ ಮೇಲೆ ಯಾರಿಗೂ ಮರಣಶಿಕ್ಷೆಯಾಗಬಾರದು; ಮರಣಶಿಕ್ಷೆಯನ್ನು ವಿಧಿಸುವುದಕ್ಕೆ ಇಬ್ಬರು ಇಲ್ಲವೆ ಮೂವರು ಸಾಕ್ಷಿಗಳು ಬೇಕು.",
			"7": "<sup>7</sup> ಅಪರಾಧಿಯನ್ನು ಕೊಲ್ಲುವುದಕ್ಕೆ ಸಾಕ್ಷಿಗಳೇ ಮೊದಲು ಕಲ್ಲನ್ನು ಎಸೆಯಬೇಕು. ತರುವಾಯ ಜನರೆಲ್ಲರೂ ಹಾಕಲಿ. ಹೀಗೆಮಾಡಿ ನೀವು ಆ ದುಷ್ಟತ್ವವನ್ನು ನಿಮ್ಮ ಮಧ್ಯೆಯಿಂದ ತೆಗೆದುಹಾಕಬೇಕು.",
			"8": "<sup>8</sup> “ವಿಧವಿಧವಾದ ಜೀವಹತ್ಯ, ಹಕ್ಕುಬಾಧ್ಯತೆ, ಹೊಡೆದಾಟ ಇವುಗಳ ವಿಷಯದಲ್ಲಿ ಅನುಮಾನ, ಚರ್ಚೆಗಳು ನಿಮ್ಮ ಊರಲ್ಲಿ ಉಂಟಾಗಬಹುದು. ಆಗ ಸರಿಯಾಗಿ ವಿವೇಚಿಸುವುದು ನಿಮ್ಮ ಶಕ್ತಿಗೆ ಮೀರಿದ ಪಕ್ಷಕ್ಕೆ ನೀವು ಊರಿನಿಂದ ಹೊರಟು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಆರಿಸಿಕೊಳ್ಳುವ ಸ್ಥಳದಲ್ಲಿ",
			"9": "<sup>9</sup> ಯಾಜಕಸೇವೆಮಾಡುವ ಲೇವಿಯರ ಮತ್ತು ಆ ಕಾಲದ ನ್ಯಾಯಾಧಿಪತಿಯ ಬಳಿಗೆ ಹೋಗಿ ವಿಚಾರಿಸಬೇಕು. ನೀವು ಕೊಡಬೇಕಾದ ತೀರ್ಪನ್ನು ಅವರು ತಿಳಿಸುವರು.",
			"10": "<sup>10</sup> ಸರ್ವೇಶ್ವರ ಆರಿಸಿಕೊಂಡ ಆ ಸ್ಥಳದಲ್ಲಿ ಅವರು ನಿಮಗೆ ತಿಳಿಸುವ ಮಾತಿನ ಪ್ರಕಾರ ನೀವು ಮಾಡಬೇಕು. ಅವರು ಬೋಧಿಸುವುದನ್ನೇ ನೀವು ಅನುಸರಿಸಬೇಕು.",
			"11": "<sup>11</sup> ಅವರು ಕೊಡುವ ಆದೇಶದ ಪ್ರಕಾರ ಹಾಗು ಅವರು ಹೇಳಿಕೊಡುವ ತೀರ್ಪಿನಂತೆ ನೀವು ಮಾಡಬೇಕು; ಅವರು ತಿಳಿಸುವ ಮಾತನ್ನು ಬಿಟ್ಟು ಎಡಬಲಕ್ಕೆ ತಿರುಗಬಾರದು.",
			"12": "<sup>12</sup> ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸೇವೆಮಾಡುವ ಯಾಜಕರ ಅಥವಾ ನ್ಯಾಯಾಧಿಪತಿಯ ಮಾತನ್ನು ಯಾವನು ಅಹಂಕಾರದಿಂದ ನಿರಾಕರಿಸುತ್ತಾನೋ ಅವನಿಗೆ ಮರಣಶಿಕ್ಷೆ ಆಗಬೇಕು. ಹೀಗೆ ಮಾಡಿ ನೀವು ಆ ದುಷ್ಟತ್ವವನ್ನು ಇಸ್ರಯೇಲರ ಮಧ್ಯದಿಂದ ತೆಗೆದುಹಾಕಬೇಕು.",
			"13": "<sup>13</sup> ಜನರೆಲ್ಲರು ಇದನ್ನು ಕೇಳಿ, ಭಯಪಟ್ಟು, ಇನ್ನು ಅಹಂಕಾರದಿಂದ ನಡೆಯರು.",
			"14": "<sup>14</sup> “ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ, ನಿಮಗೆ ಕೊಡುವ ನಾಡನ್ನು ನೀವು ಸೇರಿ ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡು, ಅದರಲ್ಲಿ ವಾಸವಾಗಿರುವಾಗ ‘ಸುತ್ತಲಿರುವ ಎಲ್ಲ ಜನಾಂಗಗಳಂತೆ ನಾವೂ ಒಬ್ಬ ಅರಸನನ್ನು ನೇಮಿಸಿಕೊಳ್ಳೋಣ’ ಎಂದು ಹೇಳಿಕೊಳ್ಳಬಹುದು.",
			"15": "<sup>15</sup> ಆಗ ನೀವು ಅಗತ್ಯವಾಗಿ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಆರಿಸಿಕೊಂಡವನನ್ನೇ ನೇಮಿಸಿಕೊಳ್ಳಬೇಕು. ನಿಮ್ಮ ಬಂಧುಗಳಾದ ಸ್ವಜನರಲ್ಲೇ ಒಬ್ಬನನ್ನು ಅರಸನನ್ನಾಗಿ ನೇಮಿಸಿಕೊಳ್ಳಬೇಕೆ ಹೊರತು ಅನ್ಯದೇಶದವನನ್ನು ನೇಮಿಸಬಾರದು.",
			"16": "<sup>16</sup> “ಅವನು ಕುದುರೆಯ ದಂಡನ್ನು ಹೆಚ್ಚಿಸಿಕೊಳ್ಳಬಾರದು. ಕುದುರೆಗಳನ್ನು ಸಂಪಾದಿಸಿಕೊಳ್ಳುವುದಕ್ಕಾಗಿ ತನ್ನ ಪ್ರಜೆಗಳನ್ನು ಈಜಿಪ್ಟ್ ದೇಶಕ್ಕೆ ಹೋಗಗೊಡಿಸಬಾರದು. ನೀವು ಇನ್ನು ಆ ದೇಶಕ್ಕೆ ಹೋಗಲೇಕೂಡದೆಂದು ಸರ್ವೇಶ್ವರ ಆಜ್ಞಾಪಿಸಿದ್ದಾರೆ.",
			"17": "<sup>17</sup> ಅವನು ಅನೇಕ ಸ್ತ್ರೀಯರನ್ನು ಮದುವೆಮಾಡಿಕೊಳ್ಳಬಾರದು; ಮಾಡಿಕೊಂಡರೆ ಅವನ ಮನಸ್ಸು ಸರ್ವೇಶ್ವರನಿಗೆ ವಿಮುಖವಾಗಲು ಅವಕಾಶವಾಗುವುದು. ಅವನು ಬೆಳ್ಳಿಬಂಗಾರವನ್ನು ವಿಶೇಷವಾಗಿ ಗಳಿಸಿಕೊಳ್ಳಬಾರದು.",
			"18": "<sup>18</sup> “ಅವನು ರಾಜ್ಯಾಧಿಕಾರಕ್ಕೆ ಬಂದಾಗ ಯಾಜಕ ಸೇವೆಮಾಡುವ ಲೇವಿಯರ ವಶದಲ್ಲಿ ಇರುವ ಈ ಧರ್ಮಶಾಸ್ತ್ರಗ್ರಂಥದ ಒಂದು ಪ್ರತಿಯನ್ನು ತನಗಾಗಿ ಪುಸ್ತಕರೂಪವಾಗಿ ಬರೆಯಿಸಿಕೊಳ್ಳಬೇಕು.",
			"19": "<sup>19</sup> ಅವನು ಗರ್ವದಿಂದ ತನ್ನ ಸ್ವದೇಶದವರನ್ನು ಹೀಯಾಳಿಸಬಾರದು; ಸರ್ವೇಶ್ವರನ ಆಜ್ಞೆಯನ್ನು ಮೀರಿ ಎಡಬಲಕ್ಕೆ ತಿರುಗಿಕೊಳ್ಳಬಾರದು; ದೇವರಾದ ಸರ್ವೇಶ್ವರನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವನಾಗಿರಬೇಕು; ಈ ಧರ್ಮಶಾಸ್ತ್ರದ ಎಲ್ಲ ವಾಕ್ಯಗಳನ್ನೂ ವಿಧಿಗಳನ್ನೂ ಅನುಸರಿಸಲು ಅಭ್ಯಾಸಮಾಡಿಕೊಳ್ಳಬೇಕು; ಈ ಕಾರಣ ಈ ಗ್ರಂಥ ಅವನ ಬಳಿಯಲ್ಲೇ ಇರಬೇಕು. ಅವನು ತನ್ನ ಜೀವಮಾನವೆಲ್ಲಾ ಇದನ್ನು ಓದಿಕೊಳ್ಳುತ್ತಾ ಇರಬೇಕು.",
			"20": "<sup>20</sup> ಹೀಗೆ ಮಾಡಿದರೆ ಅವನೂ ಅವನ ಸಂತತಿಯವರೂ ದೀರ್ಘಕಾಲ ಇಸ್ರಯೇಲರ ಮೇಲೆ ರಾಜ್ಯಾಧಿಕಾರ ನಡೆಸುವರು."
		},
		"18": {
			"1": "<sup>1</sup> “ಯಾಜಕಸೇವೆ ಮಾಡುವ ಲೇವಿಯರಿಗೆ ಹಾಗು ಲೇವಿಯಕುಲದ ಸರ್ವರಿಗೆ ಇತರ ಇಸ್ರಯೇಲರಂತೆ ಸ್ವಂತಕ್ಕಾಗಿ ಭೂಸ್ವತ್ತು ದೊರೆಯುವುದಿಲ್ಲ. ಸರ್ವೇಶ್ವರನಿಗೆ ಸಮರ್ಪಿತವಾದ ಬಲಿದ್ರವ್ಯಗಳು ಹಾಗು ಎಲ್ಲ ಮೀಸಲಾಗಿಟ್ಟವುಗಳೇ ಅವರಿಗೆ ಜೀವನಾಧಾರ.",
			"2": "<sup>2</sup> ಸ್ವದೇಶದವರಿಗೆ ದೊರಕುವಂತೆ ಅವರಿಗೆ ಸೊತ್ತು ದೊರಕುವುದಿಲ್ಲ. ಸರ್ವೇಶ್ವರ ಸ್ವಾಮಿಯೇ ಲೇವಿಯರ ಸೊತ್ತು. ಇದನ್ನು ಸ್ವಾಮಿಯೇ ಅವರಿಗೆ ಹೇಳಿದ್ದಾರೆ.",
			"3": "<sup>3</sup> “ಜನರಿಂದ ಯಾಜಕರು ಹೊಂದಬೇಕಾದವುಗಳು ಯಾವುವೆಂದರೆ : ದನಕರುಗಳನ್ನು ಹಾಗು ಆಡುಕುರಿಗಳನ್ನು ಕೊಯ್ದು ಬಲಿದಾನ ಮಾಡುವವರೆಲ್ಲರು ಆ ಪಶುಗಳ ಮುಂದೊಡೆಯನ್ನೂ ಎರಡು ದವಡೆಗಳನ್ನೂ ಕೋಷ್ಠವನ್ನೂ ಯಾಜಕರಿಗೆ ಕೊಡಬೇಕು.",
			"4": "<sup>4</sup> ಧಾನ್ಯ, ದ್ರಾಕ್ಷಾರಸ, ಎಣ್ಣೆ ಇವುಗಳ ಪ್ರಥಮಫಲಗಳನ್ನು ಹಾಗು ಮೊದಲನೆಯ ಸಾರಿ ಕತ್ತರಿಸುವ ಕುರಿಗಳ ಉಣ್ಣೆಯನ್ನು ಅವರಿಗೆ ಕೊಡಬೇಕು.",
			"5": "<sup>5</sup> ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮ ಎಲ್ಲ ಕುಲಗಳಲ್ಲಿ ಲೇವಿಯನನ್ನು, ಇವನ ತರುವಾಯ ಇವನ ಸಂತತಿಯವರನ್ನು, ತಮ್ಮ ಸನ್ನಿಧಿಯಲ್ಲಿ ಶಾಶ್ವತವಾಗಿ ಸೇವೆಮಾಡುವುದಕ್ಕೆ ತಾವೇ ನೇಮಿಸಿಕೊಂಡು ಇದ್ದಾರಲ್ಲವೆ?",
			"6": "<sup>6</sup> “ಇಸ್ರಯೇಲರ ಯಾವುದಾದರೊಂದು ಊರಲ್ಲಿ ಇಳಿದುಕೊಂಡಿದ್ದ ಒಬ್ಬ ಲೇವಿಯನು ಸ್ವಂತ ಮನಸ್ಸಿನಿಂದ ಆ ಊರನ್ನು ಬಿಟ್ಟು ಸರ್ವೇಶ್ವರ ಆರಿಸಿಕೊಂಡ ಸ್ಥಳಕ್ಕೆ ಬರಬಹುದು.",
			"7": "<sup>7</sup> ಅಲ್ಲೇ ಸರ್ವೇಶ್ವರನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸೇವೆಮಾಡುವ ತನ್ನ ಸ್ವಕುಲದವರಂತೆ ತನ್ನ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಹೆಸರನ್ನು ಹೇಳಿ ತಾನೂ ಸೇವೆ ನಡೆಸಬಹುದು;",
			"8": "<sup>8</sup> ಅಂಥವನ ಕೈಯಲ್ಲಿ ಪಿತ್ರಾರ್ಜಿತ ಸೊತ್ತನ್ನು ಮಾರಿದ ಹಣವಿದ್ದರೂ ಮಿಕ್ಕ ಲೇವಿಯರೊಂದಿಗೆ ಸಮವಾದ ಪಾಲಿಗೆ ಬಾಧ್ಯನಾಗಿರುವನು.",
			"9": "<sup>9</sup> “ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ಕೊಡುವ ನಾಡನ್ನು ನೀವು ಸೇರಿದಾಗ ಅಲ್ಲಿರುವ ಜನಗಳು ಮಾಡುವ ಖಂಡನೀಯ ಆಚಾರಗಳನ್ನು ಅನುಸರಿಸಲೇಬಾರದು.",
			"10": "<sup>10</sup> ಮಕ್ಕಳನ್ನು ಆಹುತಿಕೊಡುವವರು, ಕಣಿಹೇಳುವವರು, ಶಕುನ ನೋಡುವವರು, ಯಂತ್ರಮಂತ್ರಗಳನ್ನು ಮಾಡುವವರು,",
			"11": "<sup>11</sup> ಮಾಟಗಾರರು, ತಂತ್ರಗಾರರು, ಸತ್ತವರನ್ನು ವಿಚಾರಿಸುವವರು, ಬೇತಾಳಿಕರು, ಪ್ರೇತಸಿದ್ಧರು ಇಂಥವರು ಯಾರೂ ನಿಮ್ಮಲ್ಲಿ ಇರಬಾರದು.",
			"12": "<sup>12</sup> ಇಂಥ ಕೆಲಸಗಳನ್ನು ನಡೆಸುವವರು ಸರ್ವೇಶ್ವರನಿಗೆ ಅಸಹ್ಯರಾಗಿದ್ದಾರೆ. ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಇಂಥ ಹೇಯಕೃತ್ಯಗಳನ್ನು ಮಾಡುವ ಆ ಜನಾಂಗಗಳನ್ನು ನಿಮ್ಮಿಂದ ದೂರ ಹೊರದೂಡಿಸಿಬಿಡುತ್ತಾರೆ.",
			"13": "<sup>13</sup> ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಸೇವೆಯ ವಿಷಯದಲ್ಲಿ ಸದಾಚಾರವುಳ್ಳವರು ಆಗಿರಬೇಕು.",
			"14": "<sup>14</sup> ನೀವು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವ ಆ ನಾಡಿನ ಜನಾಂಗಗಳು ಶಕುನಗಳನ್ನು ನೋಡುತ್ತಾರೆ, ಯಂತ್ರಮಂತ್ರಗಳನ್ನು ಮಾಡುತ್ತಾರೆ. ನೀವಾದರೋ ಹಾಗೆ ಮಾಡುವುದಕ್ಕೆ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಅನುಮತಿ ಇಲ್ಲ.",
			"15": "<sup>15</sup> “ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮ ಸಹೋದರರಲ್ಲಿ, ನನ್ನಂಥ ಒಬ್ಬ ಪ್ರವಾದಿಯನ್ನು ನಿಮಗೆ ಏರ್ಪಡಿಸುವರು. ಅವನಿಗೆ ಮಾತ್ರ ಕಿವಿಗೊಡಬೇಕು.",
			"16": "<sup>16</sup> ಹೋರೇಬಿನಲ್ಲಿ ನೀವು ಸಭೆಕೂಡಿದ ದಿನದಂದು ಇದನ್ನೇ ನೀವು ಕೋರಿಕೊಂಡಿರಿ; ‘ನಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಸ್ವರವನ್ನು ಇನ್ನು ಕೇಳಲು ಹಾಗು ಈ ಘೋರವಾದ ಅಗ್ನಿಜ್ವಾಲೆಯನ್ನು ಇನ್ನು ನೋಡಲು ನಮಗೆ ಮನಸ್ಸಿಲ್ಲ; ಹಾಗೇನಾದರು ಕೇಳಿ, ನೋಡಿದರೆ ಸತ್ತೇವು’ ಎಂದು ನೀವು ಹೇಳಿದಿರಿ.",
			"17": "<sup>17</sup> ಆಗ ಸರ್ವೇಶ್ವರ ನನಗೆ, ‘ಇವರು ಹೇಳಿದ್ದು ಸರಿಯಾಗಿದೆ;",
			"18": "<sup>18</sup> ಇವರ ಸ್ವದೇಶದವರಲ್ಲಿ ನಿನ್ನಂಥ ಪ್ರವಾದಿಯನ್ನು ಅವರಿಗೆ ಏರ್ಪಡಿಸುವೆನು; ಅವನ ಬಾಯಿಂದ ನನ್ನ ಮಾತುಗಳನ್ನು ನುಡಿಸುವೆನು; ನಾನು ಅವನಿಗೆ ಆಜ್ಞಾಪಿಸಿದ್ದನ್ನೆಲ್ಲಾ ಅವನು ಅವರಿಗೆ ತಿಳಿಸುವನು.",
			"19": "<sup>19</sup> ಅವನು ನನ್ನ ಹೆಸರಿನಲ್ಲಿ ಹೇಳಿದ ಮಾತುಗಳಿಗೆ ಯಾರು ಕಿವಿಕೊಡುವುದಿಲ್ಲವೋ ಅಂಥವರನ್ನು ನಾನು ಶಿಕ್ಷಿಸುವೆನು.",
			"20": "<sup>20</sup> ಆದರೆ ಯಾವ ಪ್ರವಾದಿ ನನ್ನಿಂದ ಅಧಿಕಾರ ಹೊಂದದೆ ನಾನು ಪ್ರೇರಣೆಮಾಡದ ಮಾತುಗಳನ್ನು ಸರ್ವೇಶ್ವರನ ಮಾತೆಂದು ಜನರಿಗೆ ತಿಳಿಸುವನೋ, ಇಲ್ಲವೆ ಇತರ ದೇವರುಗಳ ಹೆಸರಿನಲ್ಲಿ ಮಾತಾಡುವನೋ, ಅವನಿಗೆ ಮರಣಶಿಕ್ಷೆಯಾಗಬೇಕು,’ ಎಂದು ಹೇಳಿದರು.",
			"21": "<sup>21</sup> “ಪ್ರವಾದಿ ಹೇಳಿದ ಮಾತು ಸರ್ವೇಶ್ವರನ ಮಾತಲ್ಲವೆಂದು ತಿಳಿದುಕೊಳ್ಳುವುದು ಹೇಗೆ ಎಂದುಕೊಳ್ಳುತ್ತೀರೋ?",
			"22": "<sup>22</sup> ಪ್ರವಾದಿ ಸರ್ವೇಶ್ವರನ ಮಾತೆಂದು ಹೇಳಿ, ಮುಂತಿಳಿಸಿದ ಸಂಗತಿ ನಡೆಯದೆ ಹೋದರೆ, ಅವನ ಮಾತು ಸರ್ವೇಶ್ವರನದಲ್ಲವೆಂದು ನೀವು ತಿಳಿದುಕೊಳ್ಳಬೇಕು. ಅವನು ಅಧಿಕಾರವಿಲ್ಲದೆ ಮಾತಾಡಿದವನು; ಅವನಿಗೆ ಹೆದರಬಾರದು."
		},
		"19": {
			"1": "<sup>1</sup> “ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ಕೊಡುವ ನಾಡಿನಲ್ಲಿರುವ ಜನಾಂಗಗಳನ್ನು ಅಲ್ಲಿಂದ ತೆಗೆದುಹಾಕಿಬಿಡುವರು. ತರುವಾಯ ನೀವು ಆ ನಾಡನ್ನು ಸ್ವಾಧೀನಕ್ಕೆ ತೆಗೆದುಕೊಳ್ಳುವಿರಿ.",
			"2": "<sup>2</sup> ಅವರ ಗ್ರಾಮಗಳಲ್ಲೂ ಮನೆಗಳಲ್ಲೂ ವಾಸವಾಗಿರುವಾಗ ಆ ನಿಮ್ಮ ನಾಡಿನ ಮಧ್ಯೆ ನೀವು ಮೂರು ಆಶ್ರಯ ನಗರಗಳನ್ನು ಗೊತ್ತುಮಾಡಬೇಕು.",
			"3": "<sup>3</sup> ನರಹತ್ಯಮಾಡಿದವರು ಆ ಪಟ್ಟಣಗಳಿಗೆ ಓಡಿಹೋಗುವುದಕ್ಕೆ ಅನುಕೂಲವಾಗುವಂತೆ ನೀವು ಅವುಗಳ ದಾರಿಯನ್ನು ಸರಿಪಡಿಸಬೇಕು. ಮತ್ತು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ಸ್ವದೇಶವಾಗುವುದಕ್ಕೆ ಕೊಡುವ ಆ ನಾಡನ್ನು ಮೂರು ಭಾಗಮಾಡಬೇಕು.",
			"4": "<sup>4</sup> “ಆ ಆಶ್ರಯನಗರಗಳ ವಿವರ ಹೀಗಿದೆ: ಯಾವನಾದರು ದ್ವೇಷವಿಲ್ಲದೆ, ಕೈತಪ್ಪಿ, ಮತ್ತೊಬ್ಬನನ್ನು ಕೊಂದರೆ, ಅವನು ಆ ನಗರಗಳಲ್ಲಿ ಒಂದಕ್ಕೆ ಓಡಿಹೋಗಿ ಬದುಕಿಕೊಳ್ಳಬಹುದು.",
			"5": "<sup>5</sup> ಉದಾಹರಣೆಗೆ, ಒಬ್ಬ ವ್ಯಕ್ತಿ ಕಟ್ಟಿಗೆಯನ್ನು ಕಡಿಯಲು ಮತ್ತೊಬ್ಬನ ಜೊತೆಯಲ್ಲಿ ಅಡವಿಗೆ ಹೋದನೆಂದು ಇಟ್ಟುಕೊಳ್ಳೋಣ. ಅಲ್ಲಿ ಒಂದು ಮರವನ್ನು ಕೊಡಲಿಯಿಂದ ಹೊಡೆಯುತ್ತಿರುವಾಗ ಕೊಡಲಿ ಕಾವಿನಿಂದ ಜಾರಿ ಆ ಮತ್ತೊಬ್ಬನಿಗೆ ತಗಲಿ ಅವನು ಸತ್ತರೆ, ಹೊಡೆದವನು ಆ ನಗರಗಳಲ್ಲಿ ಒಂದಕ್ಕೆ ಓಡಿಹೋಗಿ ಬದುಕಿಕೊಳ್ಳಬಹುದು.",
			"6": "<sup>6</sup> ಸತ್ತವನಲ್ಲಿ ಅವನಿಗೆ ಮೊದಲಿನಿಂದಲೂ ದ್ವೇಷವಿರಲಿಲ್ಲ; ಆದುದರಿಂದ ಅವನು ಮರಣಶಿಕ್ಷೆಗೆ ಪಾತ್ರನಲ್ಲ; ಆದರೂ ಹತ್ಯ ಮಾಡಿದವನಿಗೆ ಮುಯ್ಯಿ ತೀರಿಸುವ ಹಂಗುಳ್ಳ ಸಮೀಪಬಂಧು ಕೋಪದಿಂದ ಕೆಂಡ ಕಾರುತ್ತಾ ಅವನನ್ನು ಹಿಂದಟ್ಟಬಹುದು. ಮಾರ್ಗ ಮಧ್ಯದಲ್ಲೇ ಅವನನ್ನು ಹಿಡಿದು ಕೊಂದುಹಾಕಬಹುದು.",
			"7": "<sup>7</sup> ಈ ಕಾರಣದಿಂದಲೇ ನೀವು ಆ ಮೂರು ಆಶ್ರಯ ನಗರಗಳನ್ನು ಗೊತ್ತುಮಾಡಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿ ಇದ್ದೇನೆ.",
			"8": "<sup>8</sup> “ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ಈ ಇಡೀ ಧರ್ಮೋಪದೇಶವನ್ನು ನೀವು ಅನುಸರಿಸಬೇಕು. ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನನ್ನು ಪ್ರೀತಿಸಿ ಅವರು ಹೇಳುವ ಮಾರ್ಗದಲ್ಲೇ ಯಾವಾಗಲು ನಡೆಯಬೇಕು. ಆಗ ಅವರು ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ವಾಗ್ದಾನಮಾಡಿದಂತೆ ನಿಮ್ಮ ರಾಜ್ಯವನ್ನು ವಿಸ್ತರಿಸಿ, ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ಸೂಚಿಸಿದ ಪ್ರದೇಶವನ್ನೆಲ್ಲಾ ಕೊಡುವರು. ಆಗ ಈ ಮೂರು ನಗರಗಳಲ್ಲದೆ ಇನ್ನೂ ಮೂರು ನಗರಗಳನ್ನು ಗೊತ್ತುಮಾಡಬೇಕು.",
			"10": "<sup>10</sup> ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ಸ್ವದೇಶವಾಗಲೆಂದು ಕೊಡುವ ನಾಡಿನಲ್ಲಿ ನಿರಪರಾಧಿಗೆ ಮರಣ ಶಿಕ್ಷೆಯಾಗಬಾರದು; ಅಂಥವನಿಗೆ ಮರಣಶಿಕ್ಷೆ ಆದರೆ ಆ ರಕ್ತದೋಷ ನಿಮ್ಮದಾಗಿಯೇ ಇರುವುದು.",
			"11": "<sup>11</sup> “ಆದರೆ ಒಬ್ಬನು ಇನ್ನೊಬ್ಬನಲ್ಲಿ ದ್ವೇಷವನ್ನೇ ಸಾಧಿಸಿ, ಸಮಯನೋಡಿ ಅವನ ಮೇಲೆ ಬಿದ್ದು, ಅವನನ್ನು ಹೊಡೆದು ಕೊಂದು ತರುವಾಯ ಈ ನಗರಗಳಲ್ಲೊಂದಕ್ಕೆ ಓಡಿಹೋಗಬಹುದು. ಆಗ",
			"12": "<sup>12</sup> ಆ ಊರಿನ ಹಿರಿಯರು ಅವನನ್ನು ಅಲ್ಲಿಂದ ಹಿಡಿದು ತರಿಸಿ ಅವನಿಗೆ ಮರಣಶಿಕ್ಷೆಯಾಗುವಂತೆ ಮುಯ್ಯಿತೀರಿಸುವ ಹಂಗುಳ್ಳವನ ಕೈಗೆ ಕೊಡಬೇಕು.",
			"13": "<sup>13</sup> ನೀವು ಅವನನ್ನು ಕನಿಕರಿಸಬಾರದು. ನಿಮಗೆ ಶುಭವುಂಟಾಗುವಂತೆ ನಿರಪರಾಧಿಯ ಪ್ರಾಣವನ್ನು ತೆಗೆದವನು ಇಸ್ರಯೇಲರಲ್ಲಿ ಉಳಿಯದಂತೆ ಮಾಡಬೇಕು.",
			"14": "<sup>14</sup> “ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ಸ್ವದೇಶವಾಗಲೆಂದು ಕೊಡುವ ನಾಡಿನಲ್ಲಿ ನಿಮ್ಮ ಪಾಲಿಗೆ ಬಂದ ಸೊತ್ತಿನಲ್ಲಿ ನೆರೆಯವನ ಭೂಮಿಯ ಪೂರ್ವಕಾಲದ ಗಡಿಕಲ್ಲನ್ನು ಒತ್ತರಿಸಬಾರದು.",
			"15": "<sup>15</sup> “ಯಾವ ದೋಷದ ಅಥವಾ ಅಪರಾಧದ ನಿಷಯದಲ್ಲೂ ಒಬ್ಬನೇ ಒಬ್ಬನ ಸಾಕ್ಷಿಯನ್ನು ನೀವು ಅಂಗೀಕರಿಸಬಾರದು. ನಡೆದ ಘಟನೆ ರುಜುವಾತಾಗಲು ಇಬ್ಬರು ಇಲ್ಲವೆ ಮೂವರು ಸಾಕ್ಷಿಗಳು ಬೇಕು.",
			"16": "<sup>16</sup> “ಯಾವನಾದರು ನ್ಯಾಯವಿರುದ್ಧವಾಗಿ ಬಂದು ಮತ್ತೊಬ್ಬನನ್ನು ಅಪರಾಧಿ ಎಂದು ಸಾಕ್ಷಿ ಹೇಳಿದರೆ,",
			"17": "<sup>17</sup> ವ್ಯಾಜ್ಯವಾಡುವ ಆ ಇಬ್ಬರೂ ಸರ್ವೇಶ್ವರನ ಸನ್ನಿಧಿಯಲ್ಲಿ, ಯಾಜಕರ ಮುಂದೆ ಹಾಗು ಆಗ ಇರುವ ನ್ಯಾಯಾಧಿಪತಿಗಳ ಮುಂದೆ ನಿಲ್ಲಬೇಕು.",
			"18": "<sup>18</sup> ನ್ಯಾಯಾಧಿಪತಿಗಳು ಚೆನ್ನಾಗಿ ವಿಚಾರಣೆಮಾಡಿದ ಮೇಲೆ ಆ ವ್ಯಕ್ತಿಯ ಸಾಕ್ಷಿ ಸುಳ್ಳೆಂದು ತೋರಿಬಂದರೆ,",
			"19": "<sup>19</sup> ಅವನು ಆ ಮತ್ತೊಬ್ಬನಿಗೆ ಮಾಡಿಸಬೇಕೆಂದಿದ್ದ ಶಿಕ್ಷೆಯನ್ನು ಅವನಿಗೇ ವಿಧಿಸಬೇಕು. ಹೀಗೆ, ನೀವು ಆ ದುಷ್ಟತ್ವವನ್ನು ನಿಮ್ಮ ಮಧ್ಯೆಯಿಂದ ತೆಗೆದುಹಾಕಬೇಕು.",
			"20": "<sup>20</sup> ಉಳಿದವರು ಆ ಸಂಗತಿಯನ್ನು ಕೇಳಿ ಭಯಪಟ್ಟು, ಇನ್ನು ಮುಂದೆ ಇಂಥ ದುಷ್ಟತ್ವವನ್ನು ನಡೆಸರು.",
			"21": "<sup>21</sup> ನೀವು ಅಂಥವರನ್ನು ಕನಿಕರಿಸಬಾರದು; ಪ್ರಾಣಕ್ಕೆ ಪ್ರತಿಯಾಗಿ ಪ್ರಾಣವನ್ನು, ಕಣ್ಣಿಗೆ ಪ್ರತಿಯಾಗಿ ಕಣ್ನನ್ನು, ಹಲ್ಲಿಗೆ ಪ್ರತಿಯಾಗಿ ಹಲ್ಲನ್ನು, ಕೈಗೆ ಪ್ರತಿಯಾಗಿ ಕೈಯನ್ನು, ಕಾಲಿಗೆ ಪ್ರತಿಯಾಗಿ ಕಾಲನ್ನು ಅವನಿಂದ ತೆಗೆದುಬಿಡಬೇಕು."
		},
		"20": {
			"1": "<sup>1</sup> “ನೀವು ಶತ್ರುಗಳ ವಿರುದ್ಧ ಯುದ್ಧಕ್ಕೆ ಹೊರಟಾಗ ಅವರ ಕಡೆ ಕುದುರೆಗಳು, ರಥಗಳು ಹಾಗು ನಿಮಗಿಂತ ಹೆಚ್ಚಾದ ಕಾಲ್ಬಲವು ಇರುವುದನ್ನು ಕಂಡರೆ, ಹೆದರಬೇಡಿ. ಏಕೆಂದರೆ ಈಜಿಪ್ಟ್ ದೇಶದಿಂದ ನಿಮ್ಮನ್ನು ಬಿಡಿಸಿದ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮೊಂದಿಗೆ ಇದ್ದಾರೆ.",
			"2": "<sup>2</sup> “ಕಾಳಗ ಸಮೀಪಿಸಿದಾಗ ಯಾಜಕನು ಮುಂದೆ ಬಂದು ಯೋಧರಿಗೆ, ‘ಇಸ್ರಯೇಲರೇ, ಕೇಳಿ; ನೀವು ಈಗ ಶತ್ರುಗಳೊಡನೆ ಯುದ್ಧಮಾಡುವ ಕಾಲ ಸಮೀಪಿಸಿದೆ; ನೀವು ಎದೆಗೆಡಬಾರದು, ದಿಗಿಲುಪಡಬಾರದು, ನಡುಗಬಾರದು, ಶತ್ರುಗಳಿಗೆ ಭಯಪಡಬಾರದು.",
			"4": "<sup>4</sup> ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನೇ ನಿಮ್ಮ ಸಂಗಡ ಇದ್ದು ಶತ್ರುಗಳೊಡನೆ ನಿಮ್ಮ ಪರವಾಗಿ ಯುದ್ಧಮಾಡಿ, ನಿಮಗೆ ಜಯವನ್ನುಂಟುಮಾಡುವರು’ ಎಂದು ಹೇಳಬೇಕು",
			"5": "<sup>5</sup> “ಮತ್ತು ಸೇನಾಧಿಪತಿಗಳು ಅವರಿಗೆ, ‘ನಿಮ್ಮಲ್ಲಿ ಯಾವನಾದರು ಹೊಸ ಮನೆಯನ್ನು ಕಟ್ಟಿಸಿಕೊಂಡು ಇನ್ನು ಗೃಹಪ್ರವೇಶಮಾಡದಿದ್ದರೆ, ಅವನು ಮನೆಗೆ ಹೋಗಲಿ; ಅವನು ಕಾಳಗದಲ್ಲಿ ಸತ್ತರೆ ಮತ್ತೊಬ್ಬನು ಆ ಮನೆಯಲ್ಲಿ ಗೃಹಪ್ರವೇಶ ಮಾಡಿಕೊಂಡಾನು.",
			"6": "<sup>6</sup> ಯಾವನಾದರು ದ್ರಾಕ್ಷೀತೋಟವನ್ನು ಮಾಡಿ ಅದರ ಹಣ್ಣುಗಳನ್ನು ಇನ್ನೂ ಅನುಭೋಗಿಸದಿದ್ದರೆ, ಅವನು ಮನೆಗೆ ಹೋಗಲಿ; ಅವನು ಕಾಳಗದಲ್ಲಿ ಸತ್ತರೆ ಮತ್ತೊಬ್ಬನು ಆ ತೋಟದ ಹಣ್ಣುಗಳನ್ನು ಅನುಭೋಗಿಸಾನು.",
			"7": "<sup>7</sup> ಯಾವನಾದರೂ ತಾನು ಮದುವೆಮಾಡಿಕೊಂಡ ಹೆಣ್ಣನ್ನು ಇನ್ನು ಸೇರಿಸಿಕೊಳ್ಳದಿದ್ದರೆ, ಅವನು ಮನೆಗೆ ಹೋಗಲಿ; ಅವನು ಕಾಳಗದಲ್ಲಿ ಸತ್ತರೆ ಮತ್ತೊಬ್ಬನು ಅವಳನ್ನು ಸೇರಿಸಿಕೊಂಡಾನು,’ ಎಂದು ಹೇಳಬೇಕು.",
			"8": "<sup>8</sup> ಮತ್ತು ‘ಯಾವನಾದರೂ ಧೈರ್ಯ ಕಡಿಮೆಯಾಗಿ ಅಂಜಿಕೊಂಡರೆ ಅವನೂ ಮನೆಗೆ ಹೋಗಲಿ; ಅವನು ದಿಗಿಲುಪಡುವುದರಿಂದ ಅವನ ಜೊತೆಗಾರರೂ ದಿಗಿಲುಪಟ್ಟಾರು’, ಎಂದು ಹೇಳಬೇಕು.",
			"9": "<sup>9</sup> ಸೇನಾಪತಿಗಳು ಯೋಧರಿಗೆ ಈ ರೀತಿ ಹೇಳಿದ ನಂತರ ದಂಡಿನವರ ಮೇಲೆ ನಾಯಕರನ್ನು ನೇಮಿಸಬೇಕು.",
			"10": "<sup>10</sup> “ನೀವು ಒಂದು ಪಟ್ಟಣವನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳಲು ಅದರ ಹತ್ತಿರಕ್ಕೆ ಬಂದಾಗ, ಮೊದಲು ಅದು ಯುದ್ಧವಿಲ್ಲದೆ ಸಮಾಧಾನದಿಂದಲೇ ಶರಣಾಗಬಹುದೆಂದು ಪ್ರಕಟಿಸಬೇಕು.",
			"11": "<sup>11</sup> ಅದರಲ್ಲಿರುವವರು ಒಪ್ಪಿ ಬಾಗಿಲನ್ನು ನಿಮಗೆ ತೆರೆದುಕೊಟ್ಟರೆ ಅವರೆಲ್ಲರೂ ನಿಮಗೆ ಬಿಟ್ಟೀ ಕೆಲಸವನ್ನು ಮಾಡುವ ಗುಲಾಮರಾಗಬೇಕು.",
			"12": "<sup>12</sup> ಅವರು ನಿಮ್ಮ ಮಾತಿಗೆ ಸಮ್ಮತಿಸದೆ, ಪ್ರತಿಭಟಿಸಿದರೆ ಆಗ ನೀವು ಆ ಪಟ್ಟಣಕ್ಕೆ ಮುತ್ತಿಗೆ ಹಾಕಬೇಕು.",
			"13": "<sup>13</sup> ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಆ ಪಟ್ಟಣವನ್ನು ನಿಮಗೆ ವಶಪಡಿಸಿದಾಗ ಅದರಲ್ಲಿರುವ ಗಂಡಸರೆಲ್ಲರನ್ನೂ ಕನಿಕರಿಸದೆ ಸಂಹಾರ ಮಾಡಬೇಕು.",
			"14": "<sup>14</sup> ಹೆಂಗಸರನ್ನೂ ಮಕ್ಕಳನ್ನೂ ದನಗಳನ್ನೂ ಊರಲ್ಲಿರುವ ಆಸ್ತಿಯನ್ನೂ ನೀವು ಸ್ವಂತಕ್ಕೆ ತೆಗೆದುಕೊಳ್ಳಬಹುದು. ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ವಶಪಡಿಸಿದ ಶತ್ರುಗಳ ಆಸ್ತಿಯನ್ನು ನೀವೇ ಅನುಭೋಗಿಸಬಹುದು.",
			"15": "<sup>15</sup> ಈ ನಾಡಿನ ಜನಾಂಗಗಳಿಗೆ ಸೇರದ ಹಾಗು ದೂರದ ಜನಾಂಗಗಳ ಪಟ್ಟಣಗಳ ವಿಷಯದಲ್ಲಿ ನೀವು ಹಾಗೆಯೆ ನಡೆದುಕೊಳ್ಳಬೇಕು.",
			"16": "<sup>16</sup> ಆದರೆ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ಸ್ವಂತಕ್ಕಾಗಿ ಕೊಡುವ ಈ ಜನಾಂಗಗಳ ಪಟ್ಟಣಗಳ ವಿಷಯದಲ್ಲಿ ಹಾಗೆ ಮಾಡದೆ ಉಸಿರಾಡುವ ಒಬ್ಬರನ್ನಾದರೂ ಉಳಿಸಬಾರದು.",
			"17": "<sup>17</sup> ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ಅಪ್ಪಣೆಕೊಟ್ಟಂತೆ ಇವರನ್ನು ಅಂದರೆ, ಹಿತ್ತಿಯರು, ಅಮೋರಿಯರು, ಕಾನಾನ್ಯರು, ಪೆರಿಜ್ಜೀಯರು, ಹಿವ್ವಿಯರು, ಯೆಬೂಸಿಯರು ಇವರೆಲ್ಲರನ್ನೂ ವಿನಾಶ ಶಾಪಗ್ರಸ್ತರನ್ನಾಗಿ ಮಾಡಿಬಿಡಬೇಕು.",
			"18": "<sup>18</sup> ಇಲ್ಲವಾದರೆ ಅವರು ತಮ್ಮ ದೇವರುಗಳಿಗಾಗಿ ಆಚರಿಸುವ ನಿಷಿದ್ಧಾಚಾರಗಳನ್ನು ನಿಮಗೆ ಕಲಿಸಿಕೊಡಬಹುದು; ಮತ್ತು ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ದ್ರೋಹಿಗಳಾಗಬಹುದು.",
			"19": "<sup>19</sup> “ನೀವು ಒಂದು ಪಟ್ಟಣವನ್ನು ಸ್ವಾಧೀನ ಮಾಡಿಕೊಳ್ಳಲು ಅದಕ್ಕೆ ಬಹುದಿವಸ ಮುತ್ತಿಗೆ ಹಾಕಬೇಕಾಗಬಹುದು; ಅದಕ್ಕೆ ಸೇರಿದ ಮರಗಳನ್ನು ಹಾಳುಮಾಡಬಾರದು; ಅವುಗಳಿಗೆ ಕೊಡಲಿಯನ್ನು ಹಾಕಲೇಬಾರದು. ಅವುಗಳ ಹಣ್ಣನ್ನು ತಿನ್ನಬಹುದೇ ಹೊರತು ಮರಗಳನ್ನು ಕಡಿಯಬಾರದು. ಅಡವಿಯ ಮರಗಳು ಶತ್ರುಗಳೇನು? ಅವುಗಳೊಡನೆ ಯುದ್ಧಮಾಡಬಹುದೇ?",
			"20": "<sup>20</sup> ತಿನ್ನತಕ್ಕ ಹಣ್ಣು ಕೊಡುವುದಿಲ್ಲ ಎಂದು ನೀವು ತಿಳಿದ ಮರಗಳನ್ನು ಮಾತ್ರ ಕಡಿದು ಹಾಳುಮಾಡಬಹುದು. ಅವುಗಳಿಂದ ಯುದ್ಧ ಯಂತ್ರಗಳನ್ನು ಮಾಡಿಕೊಂಡು ನಿಮಗೆ ವಿರುದ್ಧ ಆಗಿರುವ ಆ ಪಟ್ಟಣ ಬೀಳುವ ತನಕ ಅದಕ್ಕೆ ಮುತ್ತಿಗೆ ಹಾಕಬೇಕು."
		},
		"21": {
			"1": "<sup>1</sup> “ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ಸ್ವದೇಶವಾಗಲೆಂದು ಕೊಡುವ ನಾಡಿನಲ್ಲಿ ಹತನಾದ ಒಬ್ಬ ವ್ಯಕ್ತಿಯ ಶವವು ಅಡವಿಯಲ್ಲಿ ಬಿದ್ದಿರುವುದನ್ನು ನೀವು ಕಂಡಾಗ ಮತ್ತು ಅವನನ್ನು ಕೊಂದವನು ಯಾರೆಂಬುದು ತಿಳಿಯದೆಹೋದರೆ",
			"2": "<sup>2</sup> ನಿಮ್ಮ ಹಿರಿಯರೂ ನ್ಯಾಯಾಧಿಪತಿಗಳೂ ಬಂದು ಹತನಾದವನ ಶವದ ಸುತ್ತಲಿರುವ ಊರುಗಳಲ್ಲಿ ಯಾವುದು ಹತ್ತಿರವೆಂದು ತಿಳಿದುಕೊಳ್ಳಲು ಅಳತೆಮಾಡಬೇಕು.",
			"3": "<sup>3</sup> ಯಾವ ಊರು ಹತ್ತಿರವಾಗಿದೆಯೋ ಆ ಊರಿನ ಹಿರಿಯರು ಇನ್ನೂ ನೊಗಕ್ಕೆ ಕಟ್ಟದ ಹಾಗು ಯಾವ ಕೆಲಸವನ್ನೂ ಮಾಡದ ಒಂದು ಕಡಸನ್ನು ತೆಗೆದುಕೊಳ್ಳಬೇಕು;",
			"4": "<sup>4</sup> ಎಂದಿಗೂ ವ್ಯವಸಾಯವಿಲ್ಲದಂಥ ನೀರು ಯಾವಾಗಲೂ ಹರಿಯುವಂಥ ತಗ್ಗಿಗೆ ಹೋಗಿ ಅಲ್ಲಿ ಅದರ ಕುತ್ತಿಗೆಯನ್ನು ಮುರಿದು ಕೊಲ್ಲಬೇಕು.",
			"5": "<sup>5</sup> ಲೇವಿಕುಲದವರಾದ ಯಾಜಕರಲ್ಲಿ ಕೆಲವರು ಹತ್ತಿರ ಇರಬೇಕು. ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಅವರನ್ನೇ ತಮ್ಮ ಸಾನ್ನಿಧ್ಯಸೇವೆಯನ್ನು ಮಾಡುವುದಕ್ಕೆ ಹಾಗು ತಮ್ಮ ಹೆಸರಿನಲ್ಲಿ ಜನರನ್ನು ಆಶೀರ್ವದಿಸುವುದಕ್ಕೆ ಆರಿಸಿಕೊಂಡಿದ್ದಾರಲ್ಲವೆ? ಸಂದೇಹಕರವಾದ ಎಲ್ಲ ವ್ಯಾಜ್ಯಗಳ ಮತ್ತು ಹೊಡೆದಾಟಗಳ ವಿಷಯದಲ್ಲಿ ಅವರೇ ತೀರ್ಮಾನಿಸಬೇಕು.",
			"6": "<sup>6</sup> ಹತನಾದವನ ಶವಕ್ಕೆ ಹತ್ತಿರವಿರುವ ಗ್ರಾಮದ ಹಿರಿಯರು, ಆ ತಗ್ಗಿನಲ್ಲಿ ಕುತ್ತಿಗೆ ಮುರಿದ ಆ ಕಡಸಿನ ಮೇಲೆ ಕೈಗಳನ್ನು ತೊಳೆದುಕೊಳ್ಳಬೇಕು.",
			"7": "<sup>7</sup> ಹಾಗೆ ಮಾಡುವಾಗ, ‘ನಮ್ಮ ಕೈಗಳು ಈ ಹತ್ಯವನ್ನು ಮಾಡಲಿಲ್ಲ; ನಮ್ಮ ಕಣ್ಣುಗಳು ನೋಡಲಿಲ್ಲ;",
			"8": "<sup>8</sup> ಸರ್ವೇಶ್ವರಾ, ನೀವು ಬಿಡುಗಡೆ ಮಾಡಿದ ನಿಮ್ಮ ಜನರನ್ನು ಕ್ಷಮಿಸಬೇಕು; ಅನ್ಯಾಯ ಆದ ನರಹತ್ಯ ದೋಷಫಲವು ನಿಮ್ಮ ಜನರಾದ ಇಸ್ರಯೇಲರಿಗೆ ತಗಲದಿರಲಿ’ ಎಂದು ಹೇಳಬೇಕು.",
			"9": "<sup>9</sup> ಆಗ ಆ ರಕ್ತಾಪರಾಧಕ್ಕೆ ಕ್ಷಮೆ ದೊರಕುವುದು. ಹೀಗೆ ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ಸರಿಯಾದುದನ್ನು ಮಾಡುವುದರಿಂದ ನೀವೇ ಅನ್ಯಾಯವಾದ ನರಹತ್ಯ ದೋಷವನ್ನು ನಿಮ್ಮ ಮಧ್ಯೆಯಿಂದ ತೆಗೆದುಹಾಕುವಿರಿ.",
			"10": "<sup>10</sup> “ನೀವು ಶತ್ರುಗಳ ವಿರುದ್ಧ ಯುದ್ಧಮಾಡಿ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಅನುಗ್ರಹದಿಂದ ಅವರನ್ನು ಸೋಲಿಸಿದಿರಿ ಎಂದು ಇಟ್ಟುಕೊಳ್ಳೋಣ.",
			"11": "<sup>11</sup> ಆಗ ಹಿಡಿದ ಸೆರೆಯವರಲ್ಲಿ ಒಬ್ಬ ಸುಂದರ ಸ್ತ್ರೀಯನ್ನು ನಿಮ್ಮಲ್ಲಿ ಯಾರಾದರು ಕಂಡು ಮೋಹಿಸಿ ಮದುವೆಮಾಡಿಕೊಳ್ಳಬೇಕೆಂದು ಅಪೇಕ್ಷಿಸಿದರೆ, ಅವಳನ್ನು ಮನೆಗೆ ಕರೆದುಕೊಂಡು ಬರಲಿ;",
			"12": "<sup>12</sup> ತರುವಾಯ ಅವಳು ತಲೆ ಕ್ಷೌರಮಾಡಿಸಿಕೊಂಡು, ಉಗುರುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು, ಸೆರೆಯ ಬಟ್ಟೆಗಳನ್ನು ತೆಗೆದಿಟ್ಟು",
			"13": "<sup>13</sup> ಅವನ ಮನೆಯಲ್ಲಿ ಒಂದು ತಿಂಗಳವರೆಗೆ ತನ್ನ ತಾಯಿತಂದೆಗಳ ವಿಯೋಗದ ನಿಮಿತ್ತ ಹಂಬಲಿಸಲಿ. ಆಮೇಲೆ ಅವನು ಅವಳನ್ನು ಹೆಂಡತಿಯನ್ನಾಗಿ ಮಾಡಿಕೊಳ್ಳಬಹುದು.",
			"14": "<sup>14</sup> ತರುವಾಯ ಅವಳು ಅವನಿಗೆ ಇಷ್ಟವಾಗದೆಹೋದರೆ ಅವನು ಅವಳನ್ನು ಅವಳು ಆಶಿಸುವಲ್ಲಿಗೆ ಕಳುಹಿಸಿಬಿಡಬೇಕು. ಅವಳನ್ನು ಕೂಡಿದ್ದರಿಂದ ಹಣಕ್ಕೆ ಮಾರಲೂಬಾರದು, ಊಳಿಗದವಳಂತೆ ನಡೆಸಲೂಬಾರದು.",
			"15": "<sup>15</sup> “ಒಬ್ಬನಿಗೆ ಇಬ್ಬರು ಹೆಂಡತಿಯರಿದ್ದು ಅವರಲ್ಲಿ ಒಬ್ಬಳನ್ನು ಪ್ರೀತಿಸಿ ಮತ್ತೊಬ್ಬಳನ್ನು ತಿರಸ್ಕರಿಸುವ ಸಂದರ್ಭದಲ್ಲಿ ಅವರಿಬ್ಬರೂ ಅವನಿಂದ ಮಕ್ಕಳನ್ನು ಪಡೆದಿದ್ದರೆ, ಚೊಚ್ಚಲು ಮಗನು ತಿರಸ್ಕರಿಸಲ್ಪಟ್ಟವಳಲ್ಲಿಯೇ ಹುಟ್ಟಿದ್ದರೂ,",
			"16": "<sup>16</sup> ತಂದೆ ತನ್ನ ಆಸ್ತಿಯನ್ನು ಮಕ್ಕಳಿಗೆ ಸೊತ್ತಾಗಿ ಕೊಡುವಾಗ ಆ ತಿರಸ್ಕರಿಸಲ್ಪಟ್ಟವಳ ಮಗನನ್ನು ತಳ್ಳಿಬಿಟ್ಟು, ಪ್ರೀತಿಯ ಹೆಂಡತಿಯ ಮಗನನ್ನೇ ಚೊಚ್ಚಲವನೆಂದು ಭಾವಿಸಕೂಡದು.",
			"17": "<sup>17</sup> ತಿರಸ್ಕರಿಸಲ್ಪಟ್ಟವಳ ಮಗನೇ ಚೊಚ್ಚಲವನೆಂದು ಒಪ್ಪಿ, ಅವನಿಗೆ ಎರಡು ಭಾಗಗಳನ್ನು ಕೊಡಬೇಕು. ಅವನೇ ತಂದೆಯ ಪ್ರಥಮಫಲ, ಅವನೇ ಚೊಚ್ಚಲುತನದ ಹಕ್ಕಿಗೆ ಬಾಧ್ಯಸ್ತ.",
			"18": "<sup>18</sup> “ಒಬ್ಬ ಮಗನು ತಂದೆತಾಯಿಗಳ ಆಜ್ಞೆಗೆ ಒಳಗಾಗದೆ, ಶಿಕ್ಷಿಸಿದ್ದರೂ ಮೊಂಡನೂ ಅವಿಧೇಯನೂ ಆಗಿ, ಅವರ ಮಾತನ್ನು ಕೇಳದೆಹೋದರೆ,",
			"19": "<sup>19</sup> ತಂದೆತಾಯಿಗಳು ಅವನನ್ನು ಹಿಡಿದು ಊರಬಾಗಿಲಿಗೆ ಹಿರಿಯರ ಮುಂದೆ ತರಬೇಕು.",
			"20": "<sup>20</sup> ಅಲ್ಲಿ ಅವರಿಗೆ, ‘ಈ ನಮ್ಮ ಮಗ ನಮ್ಮ ಮಾತನ್ನು ಕೇಳುವುದೇ ಇಲ್ಲ, ಆಜ್ಞೆಗೆ ಒಳಗಾಗುವುದಿಲ್ಲ; ಇವನು ಮೊಂಡ, ಕುಡುಕ,ತುಂಟ’ ಎಂದು ಸಾಕ್ಷಿ ಹೇಳಬೇಕು.",
			"21": "<sup>21</sup> ಅಗ ಊರಿನವರೆಲ್ಲರು ಅವನನ್ನು ಕಲ್ಲೆಸೆದು ಕೊಲ್ಲಬೇಕು. ಹೀಗೆ ನೀವು ಆ ದುಷ್ಟತ್ವವನ್ನು ನಿಮ್ಮ ಮಧ್ಯೆಯಿಂದ ತೆಗೆದು ಹಾಕಿಬಿಡಬೇಕು. ಇಸ್ರಯೇಲರೆಲ್ಲರು ಕೇಳಿ ಭಯಪಡುವರು.",
			"22": "<sup>22</sup> “ಮರಣಶಿಕ್ಷೆಗೆ ಪಾತ್ರನಾದ ಅಪರಾಧಿಯನ್ನು ಗಲ್ಲುಮರಕ್ಕೇರಿಸಿದ ಮೇಲೆ",
			"23": "<sup>23</sup> ಅವನ ಶವವನ್ನು ನೀವು ರಾತ್ರಿಯೆಲ್ಲಾ ಆ ಮರದ ಮೇಲೆ ಇರಿಸಬಾರದು; ಅದನ್ನು ಅದೇ ದಿನ ನೆಲದಲ್ಲಿ ಹೂಣಬೇಕು. ಏಕೆಂದರೆ ಮರಕ್ಕೆ ತೂಗಹಾಕಲಾದವನು ದೇವರ ಶಾಪವನ್ನು ಹೊಂದಿದವನು. ಹೀಗೆ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ಸ್ವದೇಶವಾಗಲೆಂದು ಕೊಡುವ ನಾಡನ್ನು ನೀವೇ ಅಪವಿತ್ರಗೊಳಿಸಬಾರದು."
		},
		"22": {
			"1": "<sup>1</sup> “ಸ್ವದೇಶದವನ ಎತ್ತಾಗಲಿ, ಕುರಿ ಆಗಲಿ ದಾರಿತಪ್ಪಿಹೋಗಿರುವುದನ್ನು ನೀವು ಕಂಡರೆ, ಕಾಣದವರಂತೆ ಅದನ್ನು ಬಿಟ್ಟುಹೋಗಬಾರದು; ಅವನ ಬಳಿಗೆ ಅಟ್ಟಿಕೊಂಡು ಹೋಗಿ ಕೊಡಲೇಬೇಕು.",
			"2": "<sup>2</sup> ಅವನು ನಿಮಗೆ ದೂರ ಆಗಿದ್ದರೆ, ಇಲ್ಲವೆ ಅವನು ಯಾರು ಎಂದು ನಿಮಗೆ ತಿಳಿಯದೆಹೋದರೆ, ಆ ಪ್ರಾಣಿಯನ್ನು ನಿಮ್ಮ ಮನೆಗೆ ಹೊಡೆದುಕೊಂಡು ಬಂದು ಕಾಯಬೇಕು; ಅವನು ಅದನ್ನು ಹುಡುಕುತ್ತಾ ಬಂದಾಗ, ನೀವು ಅದನ್ನು ಅವನಿಗೆ ಮರಳಿಕೊಡಬೇಕು.",
			"3": "<sup>3</sup> ಅವನ ಕತ್ತೆಯಾಗಲಿ, ಬಟ್ಟೆಯಾಗಲಿ, ಅವನು ಕಳೆದುಕೊಂಡ ಯಾವ ವಸ್ತುವೇ ಆಗಲಿ ನಿಮಗೆ ಸಿಕ್ಕಿದರೆ ಹಾಗೆಯೇ ಮಾಡಬೇಕು; ಕಾಣದವರಂತೆ ಇರಲೇಬಾರದು.",
			"4": "<sup>4</sup> “ಸ್ವದೇಶದವನ ಕತ್ತೆಯಾಗಲಿ, ಎತ್ತಾಗಲಿ ದಾರಿಯಲ್ಲಿ ಬಿದ್ದಿರುವುದನ್ನು ನೀವು ಕಂಡರೆ ಕಾಣದವರಂತೆ ಹೋಗದೆ, ಅದನ್ನು ಎಬ್ಬಿಸಲು ಅವನಿಗೆ ಸಹಾಯಮಾಡಬೇಕು.",
			"5": "<sup>5</sup> “ಹೆಂಗಸರು ಗಂಡಸರಂತೆ, ಗಂಡಸರು ಹೆಂಗಸರಂತೆ ಬಟ್ಟೆ ಹಾಕಬಾರದು; ಹಾಗೆ ಮಾಡುವವರು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ಹೇಯರು.",
			"6": "<sup>6</sup> “ನೀವು ದಾರಿಯಲ್ಲಿ ಹೋಗುವಾಗ ಮರದ ಮೇಲಾಗಲಿ ನೆಲದ ಮೇಲಾಗಲಿ ಒಂದು ಹಕ್ಕಿಯ ಗೂಡನ್ನು ಕಂಡು ಅದರೊಳಗೆ ಮರಿಗಳು ಅಥವಾ ಮೊಟ್ಟೆಗಳು ಇದ್ದು ಅವುಗಳ ಮೇಲೆ ತಾಯಿ ಹೊದಗಿರುವುದನ್ನು ನೋಡಿದರೆ, ಮರಿಗಳನ್ನಾಗಲಿ ತಾಯಿಯನ್ನಾಗಲಿ ಹಿಡಿದುಕೊಳ್ಳಬಾರದು.",
			"7": "<sup>7</sup> ನೀವು ಹೀಗೆ ನಡೆದುಕೊಂಡರೆ, ನಿಮಗೆ ಶ್ರೇಯಸ್ಸುಂಟಾಗುವುದು, ಮತ್ತು ಬಹುಕಾಲ ಬಾಳುವಿರಿ.",
			"8": "<sup>8</sup> “ನೀವು ಹೊಸ ಮನೆಯನ್ನು ಕಟ್ಟಿಸಿಕೊಳ್ಳುವಾಗ ನಿಮ್ಮ ಮನೆಯ ಮಾಳಿಗೆಯ ಮೇಲೆ ಸುತ್ತಲೂ ಸಣ್ಣ ಕೈಗೋಡೆಯನ್ನು ಕಟ್ಟಿಸಬೇಕು. ಇಲ್ಲದಿದ್ದರೆ ಒಬ್ಬನು ಆ ಮಾಳಿಗೆಯಿಂದ ಬಿದ್ದು, ಅವನ ಜೀವಹತ್ಯದೋಷ ನಿಮ್ಮ ಮನೆಯ ಮೇಲೆ ಬರುವುದು.",
			"9": "<sup>9</sup> “ದ್ರಾಕ್ಷಿತೋಟಗಳಲ್ಲಿ ಬೇರೆ ವಿಧವಾದ ಬಿತ್ತನೆ ಮಾಡಬಾರದು; ಹಾಗೆ ಮಾಡಿದರೆ ಆ ಬೆಳೆಗೂ ಮತ್ತು ದ್ರಾಕ್ಷಿಬೆಳೆಗೂ ಹೀಗೆ ಆ ತೋಟದ ಬೆಳೆಗೂ ಗತಿಕೇಡಾಗುವುದು.",
			"10": "<sup>10</sup> ಎತ್ತನ್ನೂ ಕತ್ತೆಯನ್ನೂ ಜೊತೆಮಾಡಿ ನೇಗಿಲಿಗೆ ಕಟ್ಟಬಾರದು.",
			"11": "<sup>11</sup> ನಾರು ಮತ್ತು ಉಣ್ಣೆ ಎರಡೂ ಕೂಡಿಸುವ ಬಟ್ಟೆಯನ್ನು ಹಾಕಿಕೊಳ್ಳಬಾರದು.",
			"12": "<sup>12</sup> “ನೀವು ಹೊದ್ದುಕೊಳ್ಳುವ ಬಟ್ಟೆಯ ನಾಲ್ಕು ಮೂಲೆಗಳಲ್ಲಿ ಗೊಂಡೆಗಳನ್ನು ಕಟ್ಟಬೇಕು.",
			"13": "<sup>13</sup> “ಒಬ್ಬನು ತಾನು ಮದುವೆಮಾಡಿಕೊಂಡ ಹೆಂಡತಿಯನ್ನು ದ್ವೇಷಿಸಿ",
			"14": "<sup>14</sup> ಅವಳ ಬಗ್ಗೆ ಇಲ್ಲಸಲ್ಲದ ಮಾತುಗಳನ್ನು ಹೇಳಿ ‘ನಾನು ಇವಳನ್ನು ಮದುವೆಮಾಡಿಕೊಂಡೆ; ಆದರೆ ಕೂಡಿದಾಗ ಇವಳು ಕನ್ನಿಕೆಯಲ್ಲವೆಂದು ತಿಳಿದುಬಂತು’ ಎಂದು ಹೇಳಿ",
			"15": "<sup>15</sup> ಅವಳ ಮೇಲೆ ಆಪಾದನೆ ತರಬಹುದು. ಅವಳ ತಾಯಿತಂದೆಗಳು ತಮ್ಮ ಮಗಳು ವಿವಾಹಕ್ಕೆ ಮುಂಚೆ ಪುರುಷ ಸಂಪರ್ಕ ಇಲ್ಲದವಳು ಎಂಬುದಕ್ಕೆ ಆಧಾರವನ್ನು ಊರಬಾಗಿಲಿಗೆ ಹಿರಿಯರ ಮುಂದೇ ತಂದು ತೋರಿಸಬೇಕು.",
			"16": "<sup>16</sup> ತಂದೆ ಅವರಿಗೆ, ‘ನಾನು ನನ್ನ ಮಗಳನ್ನು ಈ ಪುರುಷನಿಗೆ ಮದುವೆಮಾಡಿಕೊಟ್ಟೆ; ಆಮೇಲೆ ಇವನು ಅವಳನ್ನು ದ್ವೇಷಿಸಿ",
			"17": "<sup>17</sup> ಪರಪುರುಷ ಸಂಪರ್ಕಮಾಡಿದವಳೆಂದು ನಿರಾಧಾರವಾದ ಮಾತುಗಳನ್ನಾಡಿ ಅವಳನ್ನು ಅವಮಾನಪಡಿಸುತ್ತಾನೆ. ಇಗೋ, ನನ್ನ ಮಗಳು ವಿವಾಹಕ್ಕೆ ಮುಂಚೆ ಪುರುಷಸಂಪರ್ಕ ಮಾಡಿದವಳಲ್ಲ ಎಂಬುದಕ್ಕೆ ಇದೇ ಪ್ರಮಾಣ’ ಎಂದು ಹೇಳಿ ಅವಳ ಹಚ್ಚಡವನ್ನು ಹಿರಿಯರ ಮುಂದೆ ಇಡಬೇಕು.",
			"18": "<sup>18</sup> ಇಸ್ರಯೇಲಳಾದ ಮಹಿಳೆಯ ಬಗ್ಗೆ ಆ ವ್ಯಕ್ತಿ ನಿರಾಧಾರವಾದ ತಪ್ಪು ಹೊರಿಸಿದ್ದರಿಂದ",
			"19": "<sup>19</sup> ಆ ಊರಿನ ಹಿರಿಯರು ಅವನನ್ನು ಹಿಡಿಸಿ, ನೂರು ಬೆಳ್ಳಿ ನಾಣ್ಯದ ದಂಡವನ್ನು ವಿಧಿಸಿ, ಆ ಹಣವನ್ನು ಅವಳ ತಂದೆಗೆ ಕೊಡಿಸಬೇಕು; ಮತ್ತು ಅವನು ಆ ಮಹಿಳೆಯನ್ನು ಹೆಂಡತಿಯನ್ನಾಗಿಯೇ ಇಟ್ಟುಕೊಳ್ಳಬೇಕು; ಅವನ ಜೀವಮಾನಕಾಲವೆಲ್ಲಾ ಅವಳನ್ನು ಪರಿತ್ಯಾಗಮಾಡುವುದಕ್ಕೆ ಅವನಿಗೆ ಅಧಿಕಾರ ಇರುವುದಿಲ್ಲ.",
			"20": "<sup>20</sup> “ಆದರೆ ಆ ಮಹಿಳೆ ವಿವಾಹಕ್ಕೆ ಮುಂಚೆ ಪುರುಷಸಂಪರ್ಕ ಮಾಡಿದವಳೇ ಎಂದು ತಿಳಿದುಬಂದರೆ",
			"21": "<sup>21</sup> ಅವಳು ತಂದೆಯ ಮನೆಯಲ್ಲೇ ಸೂಳೆತನಮಾಡಿ ಇಸ್ರಯೇಲರಲ್ಲಿ ದುರಾಚಾರವನ್ನು ನಡೆಸಿದ್ದರಿಂದ ಅವಳನ್ನು ತಂದೆಯ ಮನೆಯ ಬಾಗಿಲಿಗೆ ಬರಮಾಡಬೇಕು; ಊರಿನವರೆಲ್ಲರು ಕಲ್ಲೆಸೆದು ಅವಳನ್ನು ಕೊಲ್ಲಬೇಕು. ಹೀಗೆ ಇಂಥ ಕೇಡನ್ನು ನಿಮ್ಮ ಮಧ್ಯೆಯಿಂದ ತೆಗೆದುಹಾಕಬೇಕು.",
			"22": "<sup>22</sup> “ಒಬ್ಬನು ಪರನ ಹೆಂಡತಿಯೊಡನೆ ವ್ಯಭಿಚಾರ ಮಾಡಿ ಸಿಕ್ಕಿಬಿದ್ದರೆ ಆ ಸ್ತ್ರೀಪುರುಷ ಇಬ್ಬರಿಗೂ ಮರಣಶಿಕ್ಷೆಯಾಗಬೇಕು. ಹೀಗೆ ನೀವು ಇಂಥ ಕೇಡನ್ನು ಇಸ್ರಯೇಲರಿಂದ ತೆಗೆದುಹಾಕಬೇಕು.",
			"23": "<sup>23</sup> “ಒಬ್ಬನಿಗೆ ನಿಶ್ಚಿತಳಾದ ಹೆಣ್ಣನ್ನು ಯಾವನಾದರು, ಊರೊಳಗೆ ಮರುಳುಗೊಳಿಸಿ, ಸಂಗಮಿಸಿದರೆ,",
			"24": "<sup>24</sup> ನೀವು ಅವರಿಬ್ಬರನ್ನೂ ಊರ ಬಾಗಿಲಿನ ಹೊರಕ್ಕೆ ತರಿಸಿ ಕಲ್ಲೆಸೆದು ಕೊಲ್ಲಬೇಕು. ಒಬ್ಬನಿಗೆ ನಿಶ್ಚಿತಳಾದ ಸ್ತ್ರೀಯನ್ನು ಕೆಡಿಸಿದ್ದರಿಂದ ಆ ಪುರುಷನಿಗೂ ಊರಲ್ಲಿದ್ದು ಕೂಗಿಕೊಳ್ಳದೆಹೋದುದರಿಂದ ಆ ಮಹಿಳೆಗೂ ಮರಣಶಿಕ್ಷೆ ಆಗಬೇಕು. ಹೀಗೆ ನೀವು ಇಂಥ ಕೇಡನ್ನು ನಿಮ್ಮ ಮಧ್ಯೆಯಿಂದ ತೆಗೆದುಹಾಕಬೇಕು.",
			"25": "<sup>25</sup> “ಆದರೆ ಆ ವ್ಯಕ್ತಿ ನಿಶ್ಚಿತಳಾದ ಆ ಮಹಿಳೆಯನ್ನು ಅಡವಿಯಲ್ಲಿ ಬಲಾತ್ಕಾರದಿಂದ ಸಂಗಮಿಸಿದ್ದರೆ ಆ ವ್ಯಕ್ತಿಗೆ ಮಾತ್ರ ಮರಣಶಿಕ್ಷೆ ಆಗಬೇಕು.",
			"26": "<sup>26</sup> ಆ ಮಹಿಳೆಗೆ ಯಾವ ಶಿಕ್ಷೆಯೂ ಆಗಬಾರದು. ಅವಳಲ್ಲಿ ಮರಣಶಿಕ್ಷೆಗೆ ಪಾತ್ರ ಆದ ಅಪರಾಧವೇನೂ ಇಲ್ಲ. ಒಬ್ಬ ವ್ಯಕ್ತಿ ಮತ್ತೊಬ್ಬನ ಮೇಲೆ ಬಿದ್ದು ಕೊಲ್ಲುವುದು ಹೇಗೋ ಹಾಗೆಯೇ ಇದು ಕೂಡ;",
			"27": "<sup>27</sup> ಆ ಮಹಿಳೆ ಅಡವಿಯಲ್ಲೇ ಅವನ ಕೈಗೆ ಸಿಕ್ಕಿದ್ದರಿಂದ ಅವಳು ಕೂಡಿಕೊಂಡಿದ್ದರೂ ತಪ್ಪಿಸುವವರು ಇರಲಿಲ್ಲ.",
			"28": "<sup>28</sup> “ಯಾರಿಗೂ ನಿಶ್ಚಯವಾಗದೆ ಇರುವ ಕನ್ಯೆಯೊಡನೆ ಒಬ್ಬನು ಬಲಾತ್ಕಾರ ಸಂಭೋಗಮಾಡಿ ಸಿಕ್ಕಿಬಿದ್ದರೆ",
			"29": "<sup>29</sup> ಅವನು ಅವಳನ್ನು ಮಾನಭಂಗಪಡಿಸಿದ್ದರಿಂದ ಅವಳ ತಂದೆಗೆ ಐವತ್ತು ಬೆಳ್ಳಿನಾಣ್ಯ ಕೊಟ್ಟು ಅವಳನ್ನು ಹೆಂಡತಿಯನ್ನಾಗಿ ಮಾಡಿಕೊಳ್ಳಬೇಕು. ಅವನು ಬದುಕುವ ತನಕ ಅವಳನ್ನು ಪರಿತ್ಯಾಗ ಮಾಡುವುದಕ್ಕೆ ಅವನಿಗೆ ಅಧಿಕಾರವಿರುವುದಿಲ್ಲ.",
			"30": "<sup>30</sup> “ತಂದೆಗೆ ಹೆಂಡತಿಯಾದವಳನ್ನು ಸಂಗಮಿಸಲೇಬಾರದು; ಅದು ತಂದೆಗೆ ಅಪಮಾನಪಡಿಸಿದ ಹಾಗಾಗುವುದು."
		},
		"23": {
			"1": "<sup>1</sup> “ಬೀಜಹೊಡಿಸಿಕೊಂಡಾಗಲಿ, ಜನನ ಇಂದ್ರಿಯ ಕತ್ತರಿಸಿಕೊಂಡಾಗಲಿ ನಪುಂಸಕರಾದವರು ಸರ್ವೇಶ್ವರನ ಸಭೆಯಲ್ಲಿ ಸೇರಬಾರದು.",
			"2": "<sup>2</sup> ಅಗಮ್ಯಾಗಮನದಿಂದುಂಟಾದ ಸಂತತಿಯವರು ಹತ್ತನೆಯ ತಲೆಯವರಾದರೂ ಸರ್ವೇಶ್ವರನ ಸಭೆಗೆ ಸೇರಬಾರದು.",
			"3": "<sup>3</sup> “ಅಮ್ಮೋನಿಯರಾಗಲಿ ಮೋವಾಬ್ಯರಾಗಲಿ ಸರ್ವೇಶ್ವರನ ಸಭೆಗೆ ಎಂದೆಂದಿಗೂ ಸೇರಬಾರದು. ಅವರ ಸಂತತಿಯವರು ಹತ್ತನೆಯ ತಲೆಯವರಾದರೂ ಸರ್ವೇಶ್ವರನ ಸಭೆಗೆ ಸೇರಬಾರದು.",
			"4": "<sup>4</sup> ಏಕೆಂದರೆ ನೀವು ಈಜಿಪ್ಟ್ ದೇಶದಿಂದ ಬಂದಾಗ ಅವರು ಅನ್ನಪಾನಗಳನ್ನು ತಂದು ನಿಮ್ಮನ್ನು ಎದುರುಗೊಳ್ಳಲಿಲ್ಲ; ನಿಮ್ಮನ್ನು ಶಪಿಸುವುದಕ್ಕಾಗಿ ಬೆಯೋರನ ಮಗ ಬಿಳಾಮನಿಗೆ ಹಣಕೊಟ್ಟು ಎರಡು ನದಿಗಳ ಮಧ್ಯದಲ್ಲಿರುವ ಅರಾಮಿನ ಪೆತೋರೂರಿನಿಂದ ಅವನನ್ನು ಕರೆಸಿದರು.",
			"5": "<sup>5</sup> ಆದರೂ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮನ್ನು ಪ್ರೀತಿಸಿದ್ದರಿಂದ ಬಿಳಾಮನ ಮಾತಿಗೆ ಸಮ್ಮತಿಸದೆ, ಅವನಿಂದ ಶಾಪವನ್ನು ನುಡಿಸದೆ, ಆಶೀರ್ವಾದವನ್ನೇ ಹೇಳಿಸಿದರು.",
			"6": "<sup>6</sup> ನಿಮ್ಮ ಜೀವಮಾನಕಾಲವನ್ನೆಲ್ಲಾ ಅವರ ಯೋಗಕ್ಷೇಮವನ್ನು ಬಯಸಲೇಬಾರದು.",
			"7": "<sup>7</sup> “ಎದೋಮ್ಯರನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ನಿಷೇಧಿಸಬಾರದು; ಅವರು ನಿಮ್ಮ ಬಂಧುಜನಗಳು. ಈಜಿಪ್ಟರನ್ನೂ ಸಂಪೂರ್ಣವಾಗಿ ನಿಷೇಧಿಸಬಾರದು; ಅವರ ದೇಶದಲ್ಲಿ ನೀವು ಪ್ರವಾಸಿಗಳಾಗಿದ್ದಿರಲ್ಲವೆ?",
			"8": "<sup>8</sup> ಅವರ ಸಂತತಿಯವರಲ್ಲಿ ಮೂರನೆಯ ತಲೆಯವರು ಸರ್ವೇಶ್ವರನ ಸಭೆಗೆ ಸೇರಬಹುದು.",
			"9": "<sup>9</sup> “ನೀವು ಶತ್ರುಗಳ ವಿರುದ್ಧ ಯುದ್ಧಕ್ಕೆ ಹೊರಟು ಪಾಳೆಯದಲ್ಲಿರುವಾಗ ಯಾವ ಅಶುಚಿಯೂ ಆಗದಂತೆ ಎಚ್ಚರಿಕೆಯಿಂದಿರಬೇಕು.",
			"10": "<sup>10</sup> ರಾತ್ರಿಕಾಲದಲ್ಲಿ ವೀರ್ಯಸ್ಖಲನದಿಂದ ಯಾವನಾದರು ಅಶುದ್ಧನಾದರೆ ಅವನು ಪಾಳೆಯದೊಳಗೆ ಇರದೆ ಹೊರಗೆ ಇರಬೇಕು.",
			"11": "<sup>11</sup> ಅವನು ಸಂಜೆವೇಳೆಯಲ್ಲಿ ಸ್ನಾನಮಾಡಿ ಸೂರ್ಯನು ಮುಳುಗಿದ ಮೇಲೆ ಪಾಳೆಯದೊಳಗೆ ಬರಬಹುದು.",
			"12": "<sup>12</sup> “ಪಾಳೆಯದ ಹೊರಗೆ ಪಾಯಖಾನೆಗಾಗಿ ಒಂದು ಸ್ಥಳವನ್ನು ಗೊತ್ತುಮಾಡಬೇಕು.",
			"13": "<sup>13</sup> ಯುದ್ಧದ ಸಾಮಾನುಗಳಲ್ಲದೆ ಸಲಕೆ ನಿಮ್ಮ ಬಳಿಯಲ್ಲೇ ಇರಬೇಕು; ನೀವು ಬಹಿರ್ದೆಶೆಗೆ ಹೋದಾಗ ಆ ಸಲಕೆಯಿಂದ ಅಗೆದು ಕಲ್ಮಷವನ್ನು ಮುಚ್ಚಿಬಿಡಬೇಕು.",
			"14": "<sup>14</sup> ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರಾ ನಿಮ್ಮನ್ನು ಕಾಪಾಡಲು, ಶತ್ರುಗಳನ್ನು ನಿಮ್ಮ ಕೈವಶಮಾಡಲು ನಿಮ್ಮ ಪಾಳೆಯದೊಳಗೆ ಸಂಚಾರಮಾಡುತ್ತಾರೆ; ನಿಮ್ಮಲ್ಲಿ ಅಶುಚಿಯೇನಾದರೂ ಕಂಡುಬಂದರೆ ಅವರು ನಿಮ್ಮನ್ನು ಬಿಟ್ಟುಹೋಗದಂತೆ ಪಾಳೆಯವು ನಿರ್ಮಲವಾಗಿರಬೇಕು.",
			"15": "<sup>15</sup> “ತಪ್ಪಿಸಿಕೊಂಡ ಒಬ್ಬ ಗುಲಾಮನು ನಿಮ್ಮಲ್ಲಿ ಇರುವುದಕ್ಕೆ ಬಂದರೆ ಅವನನ್ನು ಅವನ ದಣಿಗೆ ಮತ್ತೆ ವಶಪಡಿಸಬಾರದು.",
			"16": "<sup>16</sup> ನಿಮ್ಮ ಊರುಗಳಲ್ಲಿ ಯಾವ ಸ್ಥಳವು ಅವನಿಗೆ ಇಷ್ಟವಾಗಿದೆಯೋ ಅಲ್ಲೇ ಅವನು ವಾಸವಾಗಿರಬಹುದು; ಅವನನ್ನು ನೀವು ಶೋಷಣೆಗೆ ಗುರಿಪಡಿಸಬಾರದು.",
			"17": "<sup>17</sup> “ಇಸ್ರಯೇಲರಲ್ಲಿ ಯಾವ ಸ್ತ್ರೀಯೂ ಗುಡಿವೇಶ್ಯೆಯಾಗಬಾರದು; ಅಂತೆಯೇ ಯಾವ ಪುರುಷನೂ ಅಂಥ ವೇಶ್ಯೆತನಕ್ಕೆ ಇಳಿಯಬಾರದು.",
			"18": "<sup>18</sup> ಸೂಳೆತನದಿಂದಾಗಲಿ ಜಾರತ್ವದಿಂದಾಗಲಿ ಸಂಪಾದಿಸಿದ ಹಣವನ್ನು ಹರಕೆಯಾಗಿ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಮಂದಿರದೊಳಗೆ ತರಲೇಬಾರದು; ಈ ಎರಡೂ ಅವರಿಗೆ ಅಸಹ್ಯವಾದುವು.",
			"19": "<sup>19</sup> “ಹಣವನ್ನಾಗಲಿ, ಆಹಾರಪದಾರ್ಥಗಳನ್ನಾಗಲಿ, ಬೇರೆ ಯಾವುದನ್ನೇ ಆಗಲಿ",
			"20": "<sup>20</sup> ಪರದೇಶದವನಿಗೆ ಬಡ್ಡಿಗೆ ಕೊಡಬಹುದೇ ಹೊರತು ಸ್ವದೇಶದವನಿಗೆ ಕೊಡಬಾರದು. ಹೀಗೆ ನಡೆದರೆ ನೀವು ಸ್ವದೇಶವಾಗಿ ಪಡೆಯಲಿರುವ ನಾಡಿನಲ್ಲಿ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮ ಎಲ್ಲ ಪ್ರಯತ್ನಗಳನ್ನು ಕೈಗೂಡಿಸಿ ಅಭಿವೃದ್ಧಿಪಡಿಸುವರು.",
			"21": "<sup>21</sup> “ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ಹರಕೆಮಾಡಿದ ಮೇಲೆ ಅದನ್ನು ತಡಮಾಡದೆ ತೀರಿಸಬೇಕು; ಅವರು ತಪ್ಪದೆ ಅದನ್ನು ವಿಚಾರಿಸುವರು; ತೀರಿಸದೆ ಹೋಗುವುದು ಪಾಪ.",
			"22": "<sup>22</sup> ನೀವು ಹರಕೆಮಾಡದಿದ್ದರೆ ದೋಷವೇನೂ ಇಲ್ಲ.",
			"23": "<sup>23</sup> ಆದರೆ ಬಾಯಿಂದ ನುಡಿದದ್ದನ್ನು ನೆರವೇರಿಸಲೇಬೇಕು. ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ಬಾಯಿಂದ ಹರಕೆಮಾಡಿಕೊಂಡಂತೆಯೇ ಅದನ್ನು ಒಪ್ಪಿಸಿಬಿಡಬೇಕು.",
			"24": "<sup>24</sup> “ಮತ್ತೊಬ್ಬನ ದ್ರಾಕ್ಷಿತೋಟದಲ್ಲಿ ನೀವು ಹಾದುಹೋಗುವಾಗ ಇಷ್ಟಾನುಸಾರ ಹಣ್ಣುಗಳನ್ನು ತಿನ್ನಬಹುದು. ಆದರೆ ಬುಟ್ಟಿಯಲ್ಲಿ ಹಾಕಿಕೊಂಡು ಹೋಗಬಾರದು.",
			"25": "<sup>25</sup> ಮತ್ತೊಬ್ಬನ ಬೆಳೆಯ ನಡುವೆ ಹಾದುಹೋಗುವಾಗ ತೆನೆಗಳನ್ನು ಕೈಯಿಂದ ಮುರಿದುಕೊಳ್ಳಬಹುದು; ಆದರೆ ಆ ಬೆಳೆಗೆ ಕುಡುಗೋಲು ಹಾಕಬಾರದು."
		},
		"24": {
			"1": "<sup>1</sup> “ಒಬ್ಬನು ತಾನು ಮದುವೆಮಾಡಿಕೊಂಡ ಮಹಿಳೆಯಲ್ಲಿ ಏನೋ ಅವಲಕ್ಷಣವನ್ನು ಕಂಡು, ಅವಳಲ್ಲಿ ಸಂತೋಷಪಡದೆ, ತ್ಯಾಗಪತ್ರವನ್ನು ಬರೆದುಕೊಟ್ಟು ಅವಳನ್ನು ಮನೆಯಿಂದ ಕಳುಹಿಸಿಬಿಟ್ಟನೆಂದು ಇಟ್ಟುಕೊಳ್ಳೋಣ.",
			"2": "<sup>2</sup> ಅವಳು ಮತ್ತೊಬ್ಬನನ್ನು ಮದುವೆ ಆಗಿ ಅವನಿಂದಲೂ ತಿರಸ್ಕರಿಸಲ್ಪಟ್ಟು ತ್ಯಾಗಪತ್ರವನ್ನು ಹೊಂದಿ ಕಳುಹಿಸಲ್ಪಟ್ಟರೆ ಅಥವಾ ಈ ಎರಡನೆಯ ಗಂಡನು ಸತ್ತುಹೋದರೆ,",
			"4": "<sup>4</sup> ಅವಳನ್ನು ಕಳುಹಿಸಿಬಿಟ್ಟ ಮೊದಲನೆಯ ಗಂಡ ಅವಳನ್ನು ಪುನಃ ಪರಿಗ್ರಹಿಸಬಾರದು; ಅವಳು ಅಶುದ್ಧಳಾದವಳು; ಅವಳನ್ನು ಪುನಃ ಪರಿಗ್ರಹಿಸುವುದು ಸರ್ವೇಶ್ವರನಿಗೆ ಹೇಯಕಾರ್ಯ. ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ಸ್ವದೇಶವಾಗಲೆಂದು ಕೊಡುವ ನಾಡಿಗೆ ದೋಷವುಂಟಾಗಲು ಅವಕಾಶಕೊಡಬೇಡಿ.",
			"5": "<sup>5</sup> “ಹೊಸದಾಗಿ ಮದುವೆಮಾಡಿಕೊಂಡವನು ಸೈನ್ಯದವರೊಡನೆ ಯುದ್ಧಕ್ಕೆ ಹೋಗಬಾರದು; ಭಾರವಾದ ಯಾವ ಕೆಲಸವನ್ನು ಅವನಿಗೆ ನೇಮಿಸಬಾರದು. ಅವನು ಒಂದು ವರ್ಷದವರೆಗೆ ಬಿಡುವಾಗಿ ಮನೆಯಲ್ಲಿ ಇದ್ದುಕೊಂಡು, ಪರಿಗ್ರಹಿಸಿದ ಹೆಂಡತಿಯೊಡನೆ ಸುಖವಾಗಿರಲಿ.",
			"6": "<sup>6</sup> “ಬೀಸುವ ಕಲ್ಲನ್ನು ಪೂರ್ತಿಯಾಗಲಿ, ಅರ್ಧವಾಗಲಿ ಒತ್ತೆ ತೆಗೆದುಕೊಳ್ಳಬಾರದು; ಅದು ಜೀವನಾಧಾರವನ್ನೇ ಒತ್ತೆಯಾಗಿ ತೆಗೆದುಕೊಂಡ ಹಾಗಾಗುವುದು.",
			"7": "<sup>7</sup> “ಒಬ್ಬನು ಸ್ವದೇಶದವನಾದ ಇಸ್ರಯೇಲನನ್ನು ಕದ್ದು ಅವನನ್ನು ಗುಲಾಮನಾಗಿ ನಡೆಸಿಕೊಂಡದ್ದು ಅಥವಾ ಮಾರಿಬಿಟ್ಟದ್ದು ತಿಳಿದುಬಂದರೆ ಕದ್ದವನಿಗೆ ಮರಣಶಿಕ್ಷೆಯಾಗಬೇಕು. ಹೀಗೆ ಅಂಥ ದುಷ್ಟತ್ವವನ್ನು ನಿಮ್ಮ ಮಧ್ಯೆಯಿಂದ ತೆಗೆದುಹಾಕಿಬಿಡಬೇಕು.",
			"8": "<sup>8</sup> “ಕುಷ್ಟರೋಗಿಗಳ ವಿಷಯದಲ್ಲಿ ಯಾಜಕರಾದ ಲೇವಿಯರು ಬೋಧಿಸುವಂತೆಯೇ ಮಾಡುವುದಕ್ಕೆ ನೀವು ಜಾಗರೂಕರಾಗಿರಬೇಕು; ನಾನು ಅವರಿಗೆ ಆಜ್ಞಾಪಿಸಿದ ನಿಯಮಗಳನ್ನೇ ನೀವು ಅನುಸರಿಸಬೇಕು.",
			"9": "<sup>9</sup> ನೀವು ಈಜಿಪ್ಟ್ ದೇಶದಿಂದ ಬಂದಾಗ ದಾರಿಯಲ್ಲಿ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಮಿರ್ಯಾಮಳಿಗೆ ಮಾಡಿದ್ದನ್ನು ನೆನಪಿಗೆ ತಂದುಕೊಳ್ಳಿ.",
			"10": "<sup>10</sup> “ಮತ್ತೊಬ್ಬನಿಗೆ ಸಾಲಕೊಡುವಾಗ ಒತ್ತೆಯನ್ನು ತೆಗೆದುಕೊಳ್ಳುವುದಕ್ಕೆ ಅವನ ಮನೆ ಒಳಕ್ಕೆ ಹೋಗದೆ ಹೊರಗೆ ಇರಬೇಕು.",
			"11": "<sup>11</sup> ಸಾಲ ತೆಗೆದುಕೊಂಡವನೇ ಒತ್ತೆಯ ಸಾಮಾನನ್ನು ತಂದುಕೊಡಬೇಕು.",
			"12": "<sup>12</sup> ಅವನು ಕೇವಲ ಬಡತನದಿಂದ ತನ್ನ ಕಂಬಳಿಯನ್ನೇ ಒತ್ತೆಯಾಗಿ ಇಟ್ಟ ಸಂದರ್ಭದಲ್ಲಿ",
			"13": "<sup>13</sup> ಹೊತ್ತುಮುಳುಗಿದಾಗ ಅದನ್ನು ಹಿಂದಕ್ಕೆ ಕೊಡಬೇಕು; ಅದನ್ನು ಇಟ್ಟುಕೊಂಡು ನೀವು ರಾತ್ರಿ ಮಗಲಬಾರದು; ಅವನು ಅದನ್ನು ಹೊದ್ದುಕೊಂಡು ಮಲಗಿಕೊಳ್ಳಬೇಕಲ್ಲವೆ? ಅದಲ್ಲದೆ, ಅವನು ನಿಮ್ಮನ್ನು ಹರಸುವನು, ಮತ್ತು ನೀವು ಮಾಡಿದ್ದು ಧರ್ಮಕಾರ್ಯವೆಂದು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ತಿಳಿದುಕೊಳ್ಳುವರು.",
			"14": "<sup>14</sup> “ಸ್ವದೇಶದವರಲ್ಲೇ ಆಗಲಿ, ನಿಮ್ಮಲ್ಲಿ ಇರುವ ಅನ್ಯದೇಶದವರಲ್ಲೇ ಆಗಲಿ ಗತಿಯಿಲ್ಲದ ಬಡ ಕೂಲಿಯವನಿಗೆ ನೀವು ಏನೂ ಅನ್ಯಾಯ ಮಾಡಬಾರದು;",
			"15": "<sup>15</sup> ದಿನದ ಕೂಲಿಯನ್ನು ಆ ದಿನದಲ್ಲೇ ಹೊತ್ತುಮುಳುಗುವುದಕ್ಕೆ ಮುಂಚಿತವಾಗಿ ಅವನಿಗೆ ಕೊಟ್ಟುಬಿಡಬೇಕು. ಅವನಿಗೆ ಬೇರೆ ಗತಿಯಿಲ್ಲದ್ದರಿಂದ ಅದನ್ನೇ ಎದುರು ನೋಡುತ್ತಿರುತ್ತಾನೆ. ನೀವು ಕೊಡದಿದ್ದರೆ ಅವನು ಸರ್ವೇಶ್ವರನಿಗೆ ಮೊರೆಯಿಡುವನು; ಆಗ ನೀವು ದೋಷಿಗಳಾಗುವಿರಿ.",
			"16": "<sup>16</sup> “ಮಕ್ಕಳ ಪಾಪಕ್ಕಾಗಿ ತಂದೆಗೂ, ತಂದೆಯ ಪಾಪಕ್ಕಾಗಿ ಮಕ್ಕಳಿಗೂ ಮರಣಶಿಕ್ಷೆಯಾಗಬಾರದು. ಪ್ರತಿಯೊಬ್ಬನೂ ತನ್ನ ಪಾಪಫಲವನ್ನು ತಾನೇ ಅನುಭವಿಸಬೇಕು.",
			"17": "<sup>17</sup> “ನೀವು ಪರದೇಶಿಯ ಅಥವಾ ದಿಕ್ಕಿಲ್ಲದವನ ವ್ಯಾಜ್ಯವನ್ನು ವಿಚಾರಿಸುವಾಗ ನ್ಯಾಯವನ್ನು ಬಿಟ್ಟು ತೀರ್ಪುಕೊಡಬಾರದು. ವಿಧವೆಯಿಂದ ಉಡುವ ಬಟ್ಟೆಯನ್ನು ಒತ್ತೆಯಿಡಿಸಿಕೊಳ್ಳಬಾರದು.",
			"18": "<sup>18</sup> ನೀವೇ ಈಜಿಪ್ಟ್ ದೇಶದಲ್ಲಿ ದಾಸರಾಗಿದ್ದಾಗ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮನ್ನುಬಿಡಿಸಿದರೆಂಬುದನ್ನು ನೆನಪುಮಾಡಿಕೊಳ್ಳಿ; ಅದಕ್ಕಾಗಿಯೇ ಇದನ್ನು ಆಜ್ಞಾಪಿಸಿದ್ದೇನೆ.",
			"19": "<sup>19</sup> “ನೀವು ಬೆಳೆಯನ್ನು ಕೊಯ್ಯುವಾಗ ಒಂದು ಸಿವುಡನ್ನು ಹೊಲದಲ್ಲೇ ಮರೆತುಬಂದರೆ ಅದನ್ನು ತರಲು ಹಿಂದಕ್ಕೆ ಹೋಗಬಾರದು; ಪರದೇಶಿ, ತಾಯಿತಂದೆಯಿಲ್ಲದ ವ್ಯಕ್ತಿ, ವಿಧವೆ, ಇಂಥವರಿಗಾಗಿ ಅದನ್ನು ಬಿಟ್ಟುಬಿಡಿ; ನೀವು ಹೀಗೆ ನಡೆದುಕೊಂಡರೆ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮ ಕೆಲಸಕಾರ್ಯಗಳಲ್ಲಿ ಕೈಗೂಡಿಸಿ ನಿಮ್ಮನ್ನು ಅಭಿವೃದ್ಧಿಪಡಿಸುವರು.",
			"20": "<sup>20</sup> ಎಣ್ಣೆಯಮರಗಳ ರೆಂಬೆಗಳಿಂದ ಕಾಯಿಗಳನ್ನು ಉದುರಿಸಿದ ಮೇಲೆ ಪುನಃ ಉದುರಿಸುವುದಕ್ಕೆ ಹೋಗಬಾರದು; ಮಿಕ್ಕ ಕಾಯಿಗಳನ್ನು ಪರದೇಶಿ, ತಾಯಿತಂದೆಯಿಲ್ಲದ ವ್ಯಕ್ತಿ, ವಿಧವೆ, ಇಂಥವರಿಗಾಗಿ ಅವುಗಳನ್ನು ಬಿಟ್ಟುಬಿಡಿ.",
			"21": "<sup>21</sup> ದ್ರಾಕ್ಷಿತೋಟದ ಬೆಳೆಯನ್ನು ಕೂಡಿಸಿಕೊಳ್ಳುವಾಗ ಹಕ್ಕಲಾಯಬಾರದು; ಪರದೇಶಿ, ತಾಯಿತಂದೆಯಿಲ್ಲದ ವ್ಯಕ್ತಿ, ವಿಧವೆ, ಇಂಥವರಿಗಾಗಿ ಅದನ್ನು ಬಿಟ್ಟುಬಿಡಿ.",
			"22": "<sup>22</sup> ನೀವೆ ಈಜಿಪ್ಟ್ ದೇಶದಲ್ಲಿ ಗುಲಾಮರಾಗಿದ್ದದ್ದು ಜ್ಞಾಪಕದಲ್ಲಿರಬೇಕು; ಅದಕ್ಕಾಗಿಯೇ ಇದನ್ನು ನಿಮಗೆ ಆಜ್ಞಾಪಿಸುತ್ತಿದ್ದೇನೆ."
		},
		"25": {
			"1": "<sup>1</sup> “ಇಬ್ಬರು ವ್ಯಾಜ್ಯವಾಡುತ್ತಾ ನ್ಯಾಯಾಧಿಪತಿಗಳ ಬಳಿಗೆ ಬಂದರೆ ನ್ಯಾಯಾಧಿಪತಿಗಳು ತಪ್ಪಿಲ್ಲದವನನ್ನು ನಿರಪರಾಧಿ ಎಂದೂ ತಪ್ಪುಳ್ಳವನನ್ನು ಅಪರಾಧಿ ಎಂದೂ ತೀರ್ಮಾನಿಸಬೇಕು.",
			"2": "<sup>2</sup> ಅಪರಾಧಿಗೆ ಪೆಟ್ಟಿನ ಶಿಕ್ಷೆ ತೀರ್ಮಾನವಾದರೆ ನ್ಯಾಯಾಧಿಪತಿ ಅವನನ್ನು ಮಲಗಿಸಿ, ಅವನ ಅಪರಾಧಕ್ಕೆ ಅನುಸಾರವಾಗಿ ಪೆಟ್ಟುಗಳನ್ನು ತನ್ನ ಮುಂದೆಯೇ ಹೊಡಿಸಿ ಲೆಕ್ಕಿಸಬೇಕು.",
			"3": "<sup>3</sup> ಪೆಟ್ಟುಗಳ ಶಿಕ್ಷೆ ನಾಲ್ವತ್ತಕ್ಕಿಂತ ಹೆಚ್ಚಿರಬಾರದು. ಅಷ್ಟಕ್ಕಿಂತ ಮೀರಿ ನೀವು ಹೆಚ್ಚು ಪೆಟ್ಟುಗಳನ್ನು ಹೊಡಿಸಿದರೆ ನಿಮ್ಮ ಸ್ವದೇಶದವನನ್ನು ಕೇವಲ ನೀಚನನ್ನಾಗಿ ನೀವು ಕಂಡಂತಾಗುವುದು.",
			"4": "<sup>4</sup> “ಕಣತುಳಿಯುವ ಎತ್ತಿನ ಬಾಯನ್ನು ಕಟ್ಟಬಾರದು.”",
			"5": "<sup>5</sup> “ಅಣ್ಣತಮ್ಮಂದಿರು ಒಂದೇ ಕುಟುಂಬದಲ್ಲಿರುವಾಗ ಅಣ್ಣನು ಮಗನಿಲ್ಲದೆ ಸತ್ತರೆ ಅವನ ಹೆಂಡತಿ ಬೇರೆ ಪುರುಷನನ್ನು ಮದುವೆ ಆಗಬಾರದು. ಅವಳ ಮೈದುನ ಅವಳನ್ನು ಹೆಂಡತಿಯಾಗಿ ತೆಗೆದುಕೊಂಡು ಮೈದುನ ಧರ್ಮವನ್ನು ನೆರವೇರಿಸಬೇಕು.",
			"6": "<sup>6</sup> ಅವಳಲ್ಲಿ ಹುಟ್ಟುವ ಚೊಚ್ಚಲುಮಗನನ್ನು ಸತ್ತವನ ಮಗನೆಂದೇ ಎಣಿಸಬೇಕು; ಹೀಗೆ ಮಾಡುವುದರಿಂದ ಸತ್ತವನ ಹೆಸರು ಇಸ್ರಯೇಲರಲ್ಲಿ ಅಳಿದು ಹೋಗುವುದಿಲ್ಲ.",
			"7": "<sup>7</sup> ತಮ್ಮನು ಅಣ್ಣನ ಹೆಂಡತಿಯನ್ನು ಪರಿಗ್ರಹಿಸಲೊಲ್ಲದೆ ಹೋದರೆ ಅವಳು ಚಾವಡಿಗೆ ಹೋಗಿ ಹಿರಿಯರಿಗೆ, ‘ನನ್ನ ಗಂಡನ ತಮ್ಮನು ತನ್ನ ಅಣ್ಣನ ಹೆಸರನ್ನು ಉಳಿಸಲೊಲ್ಲನು; ಮೈದುನ ಧರ್ಮವನ್ನು ನಡಿಸುವುದಿಲ್ಲ ಎನ್ನುತ್ತಾನೆ’ ಎಂದು ತಿಳಿಸಬೇಕು.",
			"8": "<sup>8</sup> ಆಗ ಆ ಊರಿನ ಹಿರಿಯರು ಅವನನ್ನು ಕರೆಸಿ ವಿಚಾರಿಸಿದಲ್ಲಿ ಅವನು ಅವರ ಮುಂದೆ ನಿಂತು, ‘ಈಕೆಯನ್ನು ಪರಿಗ್ರಹಿಸುವುದಕ್ಕೆ ನನಗೆ ಇಷ್ಟವಿಲ್ಲ’ ಎಂದು ಹೇಳುವ ಪಕ್ಷಕ್ಕೆ",
			"9": "<sup>9</sup> ಅವನ ಅತ್ತಿಗೆ ಹಿರಿಯರ ಎದುರಿನಲ್ಲಿ ಅವನ ಕೆರವನ್ನು ಅವನ ಕಾಲಿನಿಂದ ತೆಗೆದುಬಿಟ್ಟು, ಅವನ ಮುಖದ ಮೇಲೆ ಉಗುಳಿ, ‘ಅಣ್ಣನಿಗೆ ಸಂತಾನವನ್ನು ಹುಟ್ಟಿಸಲೊಲ್ಲದವರೆಲ್ಲರಿಗೂ ಇಂಥ ಅವಮಾನವಾಗಲಿ,’ ಎಂದು ಹೇಳಬೇಕು.",
			"10": "<sup>10</sup> ಆ ಮನುಷ್ಯನ ಮನೆಯವರಿಗೆ, ‘ಕೆರಬಿಚ್ಚಿಸಿಕೊಂಡವನ ಮನೆಯವರು’ ಎಂಬ ಹೆಸರು ಬರುವುದು.",
			"11": "<sup>11</sup> “ಇಬ್ಬರು ಗಂಡಸರು ಜಗಳವಾಡುತ್ತಿರುವಾಗ ಅವರಲ್ಲಿ ಒಬ್ಬನ ಹೆಂಡತಿ ಬಂದು ತನ್ನ ಗಂಡನನ್ನು ಬಿಡಿಸಬೇಕೆಂದು ಆ ಇನ್ನೊಬ್ಬನ ಜನನೇಂದ್ರಿಯವನ್ನು ಹಿಡಿದುಕೊಂಡರೆ,",
			"12": "<sup>12</sup> ಅವಳ ಕೈ ಕಡಿದುಹಾಕಿಸಬೇಕು; ಅವಳಿಗೆ ಕನಿಕರ ತೋರಿಸಬಾರದು.",
			"13": "<sup>13</sup> “ಹೆಚ್ಚುಕಡಿಮೆಯಾಗಿರುವ ಎರಡು ವಿಧವಾದ ಕಳ್ಳತೂಕದ ಬಟ್ಟುಗಳನ್ನು ನಿಮ್ಮ ಚೀಲದಲ್ಲಿಟ್ಟುಕೊಳ್ಳಬೇಡಿ.",
			"14": "<sup>14</sup> ಹೆಚ್ಚುಕಡಿಮೆಯಾದ ಎರಡು ವಿಧವಾದ ಕಳ್ಳಸೇರುಗಳು ನಿಮ್ಮ ಮನೆಯಲ್ಲಿರಬಾರದು.",
			"15": "<sup>15</sup> ನಿಮ್ಮ ತೂಕದ ಬಟ್ಟೂ, ನಿಮ್ಮ ಅಳತೆಯ ಸೇರೂ ನ್ಯಾಯವಾಗಿಯೇ ಇರಬೇಕು. ನೀವು ಹೀಗೆ ನಡೆದುಕೊಂಡರೆ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ಕೊಡುವ ನಾಡಿನಲ್ಲಿ ಬಹುಕಾಲ ಬಾಳುವಿರಿ.",
			"16": "<sup>16</sup> ನ್ಯಾಯ ವಿರುದ್ಧವಾದ ಅಳತೆತೂಕಗಳನ್ನು ಮಾಡುವವರೆಲ್ಲರು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ಹೇಯವಾಗಿದ್ದಾರೆ.",
			"17": "<sup>17</sup> “ನೀವು ಈಜಿಪ್ಟ್ ದೇಶದಿಂದ ಬರುವಾಗ ದಾರಿಯಲ್ಲಿ ಅಮಾಲೇಕ್ಯರು ನಿಮ್ಮನ್ನು ಎದುರಿಸಿದ್ದನ್ನು ಜ್ಞಾಪಿಸಿಕೊಳ್ಳಿ.",
			"18": "<sup>18</sup> ಅವರು ದೇವರಲ್ಲಿ ಸ್ವಲ್ಪವೂ ಭಯಭಕ್ತಿಯಿಲ್ಲದವರು; ನೀವು ದಣಿದು ಬಳಲಿದ್ದಾಗ ನಿಮ್ಮವರಲ್ಲಿ ಹಿಂದೆಬಿದ್ದ ಬಲಹೀನರನ್ನು ಸಂಹಾರಮಾಡಿದರು.",
			"19": "<sup>19</sup> ಆದಕಾರಣ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ಸ್ವದೇಶವಾಗಲೆಂದು ಕೊಡುವ ನಾಡಿನಲ್ಲಿ ನಿಮ್ಮನ್ನು ಸೇರಿಸಿ, ಸುತ್ತಲು ಯಾವ ಶತ್ರುಗಳೂ ಇಲ್ಲದಂತೆ ಮಾಡಿದಾಗ, ಜಗದಲ್ಲಿ ಅಮಾಲೇಕ್ಯರ ಹೆಸರೇ ಉಳಿಯದಂತೆ ಮಾಡಬೇಕು; ಇದನ್ನು ಮರೆಯಬೇಡಿ."
		},
		"26": {
			"1": "<sup>1</sup> “ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ಸ್ವದೇಶವಾಗಲು ಕೊಡುವ ನಾಡನ್ನು ನೀವು ಸೇರಿ ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡು ಅದರಲ್ಲಿ ವಾಸಿಸುವಾಗ",
			"2": "<sup>2</sup> ಅವರು ದಯಪಾಲಿಸಿದ ಹೊಲದ ಎಲ್ಲ ಬೆಳೆಗಳಲ್ಲೂ ಕೆಲವು ಪ್ರಥಮ ಫಲಗಳನ್ನು ಪುಟ್ಟಿಯಲ್ಲಿಟ್ಟು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ತಮ್ಮ ನಾಮಸ್ಥಾಪನೆಗಾಗಿ ಆರಿಸಿಕೊಳ್ಳುವ ಸ್ಥಳಕ್ಕೆ ತೆಗೆದುಕೊಂಡುಹೋಗಿರಿ.",
			"3": "<sup>3</sup> ಆ ಕಾಲದಲ್ಲಿ ಇರುವ ಮಹಾಯಾಜಕನಿಗೆ, ‘ಸರ್ವೇಶ್ವರ ನಮ್ಮ ಪಿತೃಗಳಿಗೆ ಪ್ರಮಾಣಮಾಡಿಕೊಟ್ಟ ನಾಡಿನಲ್ಲಿ ನಾನು ಸೇರಿದ್ದಾಯಿತೆಂದು ನಾವು ಸೇವೆಮಾಡುವ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಈಗ ಒಪ್ಪಿಕೊಳ್ಳುತ್ತೇನೆ’ ಎಂದು ಹೇಳಬೇಕು.",
			"4": "<sup>4</sup> “ಯಾಜಕನು ಆ ಪುಟ್ಟಿಯನ್ನು ನಿಮ್ಮ ಕೈಯಿಂದ ತೆಗೆದುಕೊಂಡು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಬಲಿಪೀಠದ ಮುಂದೆ ಇಡುವನು.",
			"5": "<sup>5</sup> ಆಗ ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಸನ್ನಿಧಿಯಲ್ಲಿ, ಅರಾಮ್ಯನಾದ ನಮ್ಮ ಮೂಲಪಿತೃವು ಗತಿಯಿಲ್ಲದವನಾಗಿ ಸ್ವಲ್ಪ ಮಂದಿಯೊಡನೆ ಈಜಿಪ್ಟ್ ದೇಶಕ್ಕೆ ಹೋಗಿ ಅಲ್ಲಿ ಇಳಿದುಕೊಂಡನು. ಅಲ್ಲಿ ಅವನ ಸಂತಾನದವರು ಹೆಚ್ಚಿ ಮಹಾಬಲಿಷ್ಠ ಜನಾಂಗವಾದರು.",
			"6": "<sup>6</sup> ಈಜಿಪ್ಟರು ನಮ್ಮನ್ನು ಉಪದ್ರವಪಡಿಸಿ, ಬಾಧಿಸಿ, ನಮ್ಮಿಂದ ಕಠಿಣವಾಗಿ ದುಡಿಸಿಕೊಂಡರು.",
			"7": "<sup>7</sup> ಆಗ ನಾವು ನಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ಮೊರೆಯಿಟ್ಟೆವು; ಅವರು ಆಲಿಸಿ ನಮ್ಮ ದುರವಸ್ಥೆಯನ್ನೂ ಕಷ್ಟ ಉಪದ್ರವಗಳನ್ನೂ ನೋಡಿ,",
			"8": "<sup>8</sup> ಭುಜಬಲ, ಶಿಕ್ಷಾಹಸ್ತ, ಮಹಾಭೀತಿ, ಮಹತ್ಕಾರ್ಯ, ಸೂಚಕಕಾರ್ಯ, ಇವುಗಳನ್ನು ಪ್ರಯೋಗಿಸಿ ನಮ್ಮನ್ನು ಬಿಡಿಸಿದರು.",
			"9": "<sup>9</sup> ಈಜಿಪ್ಟ್ ದೇಶದಿಂದ ಕರೆದುತಂದು ಹಾಲೂಜೇನೂ ಹರಿಯುವ ಈ ನಾಡನ್ನು ನಮಗೆ ಕೊಟ್ಟಿದ್ದಾರೆ.",
			"10": "<sup>10</sup> ಆದುದರಿಂದ ಸರ್ವೇಶ್ವರಾ, ನೀವು ಅನುಗ್ರಹಿಸಿರುವ ಭೂಮಿಯ ಬೆಳೆಯಲ್ಲಿ ಪ್ರಥಮಫಲಗಳನ್ನು ನಾವು ಕಾಣಿಕೆಯಾಗಿ ತಂದಿದ್ದೇವೆ; ಸ್ವೀಕರಿಸಬೇಕು’ ಎಂದು ಹೇಳಿ ಆ ಪ್ರಥಮಫಲಗಳನ್ನು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಸನ್ನಿಧಿಯಲ್ಲಿಟ್ಟು ಅವರನ್ನು ಆರಾಧಿಸಬೇಕು.",
			"11": "<sup>11</sup> ಸರ್ವೇಶ್ವರ ನಿಮಗೂ ನಿಮ್ಮ ಮನೆಯವರಿಗೂ ಉಂಟುಮಾಡಿದ ಎಲ್ಲ ಸುಖ ಸಂತೋಷಗಳಿಗಾಗಿ ನೀವೂ ಲೇವಿಯರೂ ನಿಮ್ಮ ಮಧ್ಯೆಯಿರುವ ಪರದೇಶಿಗಳೂ ಸಂಭ್ರಮದಿಂದಿರಬೇಕು.",
			"12": "<sup>12</sup> “ದಶಮಾಂಶವನ್ನು ಕೊಡಬೇಕಾದ ಮೂರನೆಯ ಸಂವತ್ಸರದಲ್ಲಿ ನಿಮಗುಂಟಾದ ಆದಾಯದ ದಶಮ ಭಾಗವನ್ನು ಪ್ರತ್ಯೇಕಿಸಿದಾಗ ನಿಮ್ಮ ನಿಮ್ಮ ಊರುಗಳಲ್ಲಿರುವ ಲೇವಿಯರು, ಪರದೇಶಿಗಳು, ತಾಯಿತಂದೆಯಿಲ್ಲದವರು ಹಾಗು ವಿಧವೆಯರೂ ಉಂಡು ಸಂತೋಷವಾಗಿರುವಂತೆ ಅದನ್ನು ಅವರಿಗೆ ಕೊಡಬೇಕು.",
			"13": "<sup>13</sup> ಆಗ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಸನ್ನಿಧಿಯಲ್ಲಿ, ‘ನಾವು ಮೀಸಲಾದ ವಸ್ತುಗಳನ್ನು ಮನೆಯಿಂದ ತೆಗೆದುಬಿಟ್ಟಿದ್ದೇವೆ; ನೀವು ಆಜ್ಞಾಪಿಸಿದ ಮೇರೆಗೆ ಅವುಗಳನ್ನು ಲೇವಿಯನಿಗೆ, ಪರದೇಶಿಗೆ, ತಾಯಿತಂದೆ ಇಲ್ಲದ ವ್ಯಕ್ತಿಗೆ, ವಿಧವೆಗೆ ಇಂಥವರಿಗೆ ಕೊಟ್ಟು ಇದ್ದೇವೆ, ನಿಮ್ಮ ಆಜ್ಞೆಗಳಲ್ಲಿ ಒಂದನ್ನೂ ಮೀರಲಿಲ್ಲ, ಮರೆಯಲೂ ಇಲ್ಲ.",
			"14": "<sup>14</sup> ನಾವು ಸತ್ತವರಿಗಾಗಿ ದುಃಖಿಸುವ ಕಾಲದಲ್ಲೆಲ್ಲಾ ಅದರಲ್ಲಿ ಏನೂ ತಿನ್ನಲಿಲ್ಲ; ಅಶುದ್ಧರಾಗಿ ಅದನ್ನು ತೆಗೆದು ಹಾಕಲಿಲ್ಲ; ಸತ್ತವರಿಗಾಗಿ ಅದರಲ್ಲಿ ಏನೂ ಕೊಡಲಿಲ್ಲ; ನಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಮಾತಿನಲ್ಲಿ ಲಕ್ಷ್ಯವಿಟ್ಟು ನೀವು ಆಜ್ಞಾಪಿಸಿದ್ದನ್ನೆಲ್ಲಾ ಅನುಸರಿಸಿದ್ದೇವೆ.",
			"15": "<sup>15</sup> ನೀವು ನಿಮ್ಮ ಪರಿಶುದ್ಧ ವಾಸಸ್ಥಾನವಾಗಿರುವ ಪರಲೋಕದಿಂದ ಕೆಳಗಿರುವ ನಮ್ಮನ್ನು ಕಟಾಕ್ಷಿಸಿ, ನಿಮ್ಮ ಜನರಾದ ಇಸ್ರಯೇಲರನ್ನು ಹಾಗು ಹಾಲೂಜೇನೂ ಹರಿಯುವ ನಾಡೆಂದು ನೀವು ನಮ್ಮ ಪಿತೃಗಳಿಗೆ ವಾಗ್ದಾನಮಾಡಿದ ಮೇರೆಗೆ ನಮಗೆ ಕೊಟ್ಟಿರುವ ಈ ಭೂಮಿಯನ್ನು ಆಶೀರ್ವದಿಸಿ, ದೇವರೇ,’ ಎಂದು ಹೇಳಬೇಕು.",
			"16": "<sup>16</sup> “ಈ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ನೀವು ಅನುಸರಿಸಬೇಕೆಂದು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ಇಂದು ಆಜ್ಞಾಪಿಸಿದ್ದಾರೆ. ಆದುದರಿಂದ ನೀವು ಪೂರ್ಣ ಹೃದಯದಿಂದಲೂ ಮನಸ್ಸಿನಿಂದಲೂ ಇವುಗಳನ್ನು ಕೈಕೊಂಡು ನಡೆಯಬೇಕು.",
			"17": "<sup>17</sup> ನಮಗೆ ಸರ್ವೇಶ್ವರನೇ ದೇವರಾಗಿರುವರೆಂದು ಹಾಗು ಅವರು ಹೇಳಿದ ಮಾರ್ಗದಲ್ಲೇ ನಡೆದು ಅವರ ಆಜ್ಞಾವಿಧಿನಿರ್ಣಯಗಳನ್ನು ಕೈಕೊಂಡು ಅವರ ಮಾತಿಗೆ ಲಕ್ಷ್ಯಕೊಡುವೆವೆಂದೂ ನೀವು ಈಗ ಒಡಂಬಟ್ಟಿದ್ದೀರಿ.",
			"18": "<sup>18</sup> ಸರ್ವೇಶ್ವರ ಸ್ವಾಮಿ ಆದರೋ ನಿಮ್ಮ ವಿಷಯದಲ್ಲಿ, ‘ಇವರು ನನ್ನ ಆಜ್ಞೆಗಳನ್ನೆಲ್ಲ ಅನುಸರಿಸುವವರಾದರೆ ನನ್ನ ವಾಗ್ದಾನಾನುಸಾರ ನನಗೆ ಸ್ವಕೀಯಜನರಾಗಿರುವರು.",
			"19": "<sup>19</sup> ನಾನು ನಿರ್ಮಿಸಿದ ಬೇರೆ ಎಲ್ಲಾ ಜನಾಂಗಗಳಿಗಿಂತ ಇವರಿಗೆ ಹೆಚ್ಚಾದ ಕೀರ್ತಿಘನಮಾನಗಳನ್ನುಂಟು ಮಾಡುವೆನು ಹಾಗು ಇವರು ನನ್ನ ಮಾತಿನ ಮೇರೆಗೆ ತಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ಮೀಸಲಾದ ಜನರಾಗಲು ಒಡಂಬಟ್ಟಿದ್ದಾರೆ,’ ಎನ್ನುವರು,” ಎಂದು ಹೇಳಿದನು."
		},
		"27": {
			"1": "<sup>1</sup> ಮೋಶೆ ಇಸ್ರಯೇಲರ ಹಿರಿಯರ ಸಹಿತ ಜನರಿಗೆ, “ನಾನು ಈಗ ಆಜ್ಞಾಪಿಸಿದ ಧರ್ಮೋಪದೇಶವನ್ನೆಲ್ಲಾ ನೀವು ಅನುಸರಿಸಬೇಕು.",
			"2": "<sup>2</sup> ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ಕೊಡುವ ನಾಡನ್ನು ಪ್ರವೇಶಿಸುವುದಕ್ಕೆ ನೀವು ಜೋರ್ಡನ್ ನದಿಯನ್ನು ದಾಟುವಾಗ ಅಂದರೆ, ನಿಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ತಾವು ವಾಗ್ದಾನ ಮಾಡಿದಂತೆ ನಿಮಗೆ ಕೊಡುವ ಹಾಲೂ ಜೇನೂ ಹರಿಯುವ ನಾಡನ್ನು ಪ್ರವೇಶಿಸುವುದಕ್ಕೆ ನೀವು ಜೋರ್ಡನ್ ನದಿಯನ್ನು ದಾಟುವಾಗ, ನೀವು ದೊಡ್ಡ ಕಲ್ಲುಗಳನ್ನು ನಿಲ್ಲಿಸಬೇಕು. ಅವುಗಳಿಗೆ ಗಿಲಾವು ಮಾಡಿಸಿ ಈ ಧರ್ಮಶಾಸ್ತ್ರದ ವಾಕ್ಯಗಳನ್ನೆಲ್ಲಾ ಅವುಗಳ ಮೇಲೆ ಬರೆಯಿಸಬೇಕು.",
			"4": "<sup>4</sup> ನೀವು ಜೋರ್ಡನ್ ನದಿಯನ್ನು ದಾಟಿದನಂತರ ನಾನು ಈಗ ಆಜ್ಞಾಪಿಸಿದ ಕಲ್ಲುಗಳನ್ನು ಏಬಾಲ್ ಬೆಟ್ಟದ ಮೇಲೆ ನಿಲ್ಲಿಸಿ ಅವುಗಳಿಗೆ ಗಿಲಾವು ಮಾಡಿಸಬೇಕು",
			"5": "<sup>5</sup> “ಅಲ್ಲಿ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ನೀವು ಕಲ್ಲುಗಳಿಂದ ಬಲಿಪೀಠವನ್ನು ಕಟ್ಟಿಸಬೇಕು.",
			"6": "<sup>6</sup> ಉಳಿ ಮುಂತಾದುದನ್ನು ಉಪಯೋಗಿಸದೆ ಹುಟ್ಟು ಕಲ್ಲುಗಳಿಂದಲೇ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಬಲಿಪೀಠವನ್ನು ಕಟ್ಟಿಸಬೇಕು.",
			"7": "<sup>7</sup> ಅಲ್ಲಿ ದಹನಬಲಿಗಳನ್ನೂ ಶಾಂತಿಸಮಾಧಾನ ಬಲಿಗಳನ್ನೂ ಸಮರ್ಪಿಸಿ ಸಹಭೋಜನ ಮಾಡಿ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸಂಭ್ರಮದಿಂದಿರಬೇಕು.",
			"8": "<sup>8</sup> ನೀವು ಆ ಕಲ್ಲುಗಳ ಮೇಲೆ ಈ ಧರ್ಮಶಾಸ್ತ್ರದ ವಾಕ್ಯಗಳನ್ನೆಲ್ಲಾ ವಿರಳವಾಗಿ ಹಾಗು ಸ್ಪಷ್ಟವಾಗಿ ಬರೆಸಬೇಕು,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"9": "<sup>9</sup> ಯಾಜಕರಾದ ಲೇವಿಯರೊಡನೆ ಮೋಶೆ ಇಸ್ರಯೇಲರೆಲ್ಲರಿಗೆ, “ಇಸ್ರಯೇಲರೇ, ನಿಶ್ಯಬ್ದವಾಗಿದ್ದು ಕೇಳಿರಿ: ನೀವು ಈಗ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಜನರಾದಿರಿ.",
			"10": "<sup>10</sup> ಆದುದರಿಂದ ಈಗ ನಾನು ನಿಮಗೆ ಹೇಳುವ ಆತನ ಮಾತುಗಳಿಗೆ ಕಿವಿಗೊಟ್ಟು ಆತನ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ನೀವು ಅನುಸರಿಸಬೇಕು,” ಎಂದು ಹೇಳಿದನು.",
			"11": "<sup>11</sup> ಮೋಶೆ ಆ ದಿನದಲ್ಲಿ ಜನರಿಗೆ ಆಜ್ಞಾಪಿಸಿದ್ದೇನೆಂದರೆ,",
			"12": "<sup>12</sup> “ನೀವು ಜೋರ್ಡನ್ ನದಿಯನ್ನು ದಾಟಿದ ಮೇಲೆ ಸಿಮೆಯೋನ್, ಲೇವಿ, ಯೆಹೂದ, ಇಸ್ಸಾಕಾರ್, ಜೋಸೆಫ್, ಬೆನ್ಯಾಮೀನ್ ಎಂಬೀ ಕುಲಗಳವರು ಗೆರಿಜ್ಜೀಮ್ ಬೆಟ್ಟದ ಮೇಲೆ ನಿಂತುಕೊಂಡು ಜನರಿಗೆ ಆಶೀರ್ವಚನಗಳನ್ನು ಉಚ್ಚರಿಸಬೇಕು.",
			"13": "<sup>13</sup> ರೂಬೇನ್, ಗಾದ್, ಆಶೇರ್, ಜೆಬುಲೂನ್, ದಾನ್, ನಫ್ತಾಲಿ ಎಂಬೀ ಕುಲಗಳವರು ಏಬಾಲ್ ಬೆಟ್ಟದ ಮೇಲೆ ನಿಂತುಕೊಂಡು ಶಾಪೋಕ್ತಿಗಳನ್ನು ಉಚ್ಚರಿಸಬೇಕು.",
			"14": "<sup>14</sup> “ಲೇವಿಯರು ಗಟ್ಟಿಯಾದ ಸ್ವರದಿಂದ ಇಸ್ರಯೇಲರೆಲ್ಲರಿಗೆ,",
			"15": "<sup>15</sup> ‘ಶಿಲ್ಪಿಯ ಕೆಲಸವಾಗಿ ಇರುವ ಮರದ ವಿಗ್ರಹ ಹಾಗು ಲೋಹವಿಗ್ರಹ ಸರ್ವೇಶ್ವರನಿಗೆ ಹೇಯವಾದದ್ದು. ಆದುದರಿಂದ ಅವನ್ನು ಮಾಡಿಸಿ ಗೋಪ್ಯವಾಗಿ ನಿಲ್ಲಿಸಿಕೊಂಡವನು ಶಾಪಗ್ರಸ್ತನು,’ ಎನ್ನಲು ಜನರೆಲ್ಲರು, ‘ಆಮೆನ್’ ಎನ್ನಬೇಕು.",
			"16": "<sup>16</sup> “ಲೇವಿಯರು, ‘ತಂದೆತಾಯಿಗಳನ್ನು ಅವಮಾನಪಡಿಸಿದವನು ಶಾಪಗ್ರಸ್ತ,’ ಎನ್ನಲು ಜನರೆಲ್ಲರು, ‘ಆಮೆನ್’, ಎನ್ನಬೇಕು.",
			"17": "<sup>17</sup> “ಅವರು, ‘ಮತ್ತೊಬ್ಬನ ಗಡಿಮೇರೆಯನ್ನು ಸರಿಸಿದವನು ಶಾಪಗ್ರಸ್ತ,’ ಎನ್ನಲು ಜನರೆಲ್ಲರು, ‘ಆಮೆನ್’, ಎನ್ನಬೇಕು.",
			"18": "<sup>18</sup> “ಅವರು, ‘ಕುರುಡರಿಗೆ ದಾರಿತಪ್ಪಿಸಿದವನು ಶಾಪಗ್ರಸ್ತ,’ ಎನ್ನಲು ಜನರೆಲ್ಲರು, ‘ಆಮೆನ್’, ಎನ್ನಬೇಕು.",
			"19": "<sup>19</sup> “ಅವರು, ‘ಪರದೇಶಿ, ತಾಯಿತಂದೆ ಇಲ್ಲದ ವ್ಯಕ್ತಿ, ಇವರ ವ್ಯಾಜ್ಯದಲ್ಲಿ ನ್ಯಾಯಬಿಟ್ಟು ತೀರ್ಪು ಹೇಳಿದವನು ಶಾಪಗ್ರಸ್ತ,’ ಎನ್ನಲು ಜನರೆಲ್ಲರು, ‘ಆಮೆನ್’, ಎನ್ನಬೇಕು",
			"20": "<sup>20</sup> ‘ಅವರು, ‘ಮಲತಾಯಿಯನ್ನು ಸಂಗಮಿಸಿ ತಂದೆಗೆ ಅವಮಾನಪಡಿಸಿದವನು ಶಾಪಗ್ರಸ್ತ,’ ಎನ್ನಲು ಜನರೆಲ್ಲರು, ‘ಆಮೆನ್’, ಎನ್ನಬೇಕು.",
			"21": "<sup>21</sup> “ಅವರು, ‘ಪಶುಸಂಗಮ ಮಾಡಿದವನು ಶಾಪಗ್ರಸ್ತ,’ ಎನ್ನಲು ಜನರೆಲ್ಲರು, ‘ಆಮೆನ್’, ಎನ್ನಬೇಕು.",
			"22": "<sup>22</sup> “ಅವರು, ‘ತಂದೆಯ ಮಗಳನ್ನಾಗಲಿ ತಾಯಿಯ ಮಗಳನ್ನಾಗಲಿ ಸಂಗಮಿಸಿದವನು ಶಾಪಗ್ರಸ್ತ,’ ಎನ್ನಲು ಜನರೆಲ್ಲರು, ‘ಆಮೆನ್’, ಎನ್ನಬೇಕು.",
			"23": "<sup>23</sup> “ಅವರು, ‘ಅತ್ತೆಯನ್ನು ಸಂಗಮಿಸಿದವನು ಶಾಪಗ್ರಸ್ತ,’ ಎನ್ನಲು ಜನರೆಲ್ಲರು, ‘ಆಮೆನ್’, ಎನ್ನಬೇಕು.",
			"24": "<sup>24</sup> “ಅವರು, ‘ರಹಸ್ಯವಾಗಿ ನರಹತ್ಯ ಮಾಡಿದವನು ಶಾಪಗ್ರಸ್ತ,’ ಎನ್ನಲು ಜನರೆಲ್ಲರು, ‘ಆಮೆನ್’, ಎನ್ನಬೇಕು.",
			"25": "<sup>25</sup> “ಅವರು, ‘ಹಣತೆಗೆದುಕೊಂಡು ನಿರಪರಾಧಿಯನ್ನು ಕೊಂದವನು ಶಾಪಗ್ರಸ್ತ,’ ಎನ್ನಲು ಜನರೆಲ್ಲರು, ‘ಆಮೆನ್’, ಎನ್ನಬೇಕು.",
			"26": "<sup>26</sup> “ಅವರು, ಈ ಧರ್ಮಶಾಸ್ತ್ರ ವಾಕ್ಯಗಳಿಗೆ ಒಡಂಬಟ್ಟು ಕೈಕೊಳ್ಳದೆ ಇರುವವನು ಶಾಪಗ್ರಸ್ತ,’ ಎನ್ನಲು ಜನರೆಲ್ಲರು, ‘ಆಮೆನ್’, ಎನ್ನಬೇಕು."
		},
		"28": {
			"1": "<sup>1</sup> “ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಮಾತನ್ನು ಶ್ರದ್ಧೆಯಿಂದ ಕೇಳಿ, ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ಅವರ ಆಜ್ಞೆಗಳನ್ನೆಲ್ಲಾ ಅನುಸರಿಸಿ ನಡೆದರೆ, ಅವರು ಜಗದ ಎಲ್ಲ ಜನಾಂಗಗಳಿಗಿಂತ ನಿಮ್ಮನ್ನು ಉನ್ನತಸ್ಥಿತಿಗೆ ತರುವರು.",
			"2": "<sup>2</sup> ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಮಾತಿಗೆ ಕಿವಿಗೊಟ್ಟು ನಡೆದರೆ ಈ ಕೆಳಕಂಡ ಶುಭಗಳೆಲ್ಲ ನಿಮಗೆ ಪ್ರಾಪ್ತವಾಗುವುವು:",
			"3": "<sup>3</sup> “ನಿಮಗೆ ಊರಲ್ಲೂ ಅಡವಿಯಲ್ಲೂ ಶುಭವುಂಟಾಗುವುದು.",
			"4": "<sup>4</sup> ನಿಮ್ಮ ಸಂತಾನಕ್ಕೂ ವ್ಯವಸಾಯಗಳಿಗೂ ದನಕುರಿ ಮುಂತಾದ ಪಶುಗಳಿಗೂ ಶುಭವುಂಟಾಗುವುದು.",
			"5": "<sup>5</sup> ನಿಮ್ಮ ಧಾನ್ಯಕ್ಕೂ ಅದರಿಂದ ಮಾಡುವ ಭೋಜನಕ್ಕೂ ಶುಭ ಉಂಟಾಗುವುದು.",
			"6": "<sup>6</sup> ನೀವು ಕೆಲಸಕ್ಕೆ ಹೋಗುವಾಗಲೂ ಬರುವಾಗಲೂ ಶುಭವುಂಟಾಗುವುದು.",
			"7": "<sup>7</sup> “ನಿಮ್ಮ ಮೇಲೆ ಎರಗುವ ಶತ್ರುಗಳು ನಿಮ್ಮಿಂದ ಸೋತುಹೋಗುವಂತೆ ಸರ್ವೇಶ್ವರ ಮಾಡುವರು; ಅವರು ಒಂದೇ ದಾರಿಯಿಂದ ನಿಮ್ಮ ಮೇಲೆ ಬಂದರೂ ಏಳು ದಾರಿಗಳಿಂದ ಓಡಿಹೋಗುವರು.",
			"8": "<sup>8</sup> “ನಿಮ್ಮ ಕಣಜಗಳು ಯಾವಾಗಲೂ ತುಂಬಿ ಇರುವಂತೆ ಹಾಗು ನಿಮ್ಮ ಪ್ರಯತ್ನಗಳೆಲ್ಲಾ ಸಫಲವಾಗುವಂತೆ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ಶುಭವನ್ನು ಅನುಗ್ರಹಿಸುವರು. ಅವರು ನಿಮಗೆ ಕೊಡುವ ನಾಡಿನಲ್ಲಿ ನಿಮಗೆ ಶುಭವನ್ನೇ ಉಂಟುಮಾಡುವರು.",
			"9": "<sup>9</sup> “ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಆಜ್ಞೆಗಳನ್ನು ಅನುಸರಿಸಿ, ಅವರು ಹೇಳಿದ ಮಾರ್ಗದಲ್ಲೇ ನಡೆದರೆ ಅವರು ವಾಗ್ದಾನಮಾಡಿದಂತೆ ನಿಮ್ಮನ್ನು ತಮ್ಮ ಮೀಸಲಾದ ಜನರನ್ನಾಗಿ ನೆಲೆಗೊಳಿಸುವರು.",
			"10": "<sup>10</sup> ಜಗದ ಜನರೆಲ್ಲರು ನಿಮ್ಮನ್ನು ಸರ್ವೇಶ್ವರನ ಜನರೆಂದು ತಿಳಿದುಕೊಂಡು ನಿಮಗೆ ಭಯಪಡುವರು.",
			"11": "<sup>11</sup> “ಸರ್ವೇಶ್ವರ ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ಪ್ರಮಾಣ ಮಾಡಿದ ಮೇರೆಗೆ, ನಿಮಗೆ ಕೊಡುವ ನಾಡಿನಲ್ಲಿ ಅವರು ನಿಮಗೆ ಸಂತಾನ, ಪಶು, ವ್ಯವಸಾಯಗಳ ಸಮೃದ್ಧಿಯನ್ನುಂಟುಮಾಡುವರು.",
			"12": "<sup>12</sup> ಸರ್ವೇಶ್ವರ ಆಕಾಶದಲ್ಲಿರುವ ತಮ್ಮ ಜಲನಿಧಿಯನ್ನು ತೆರೆದು ನಿಮ್ಮ ನಾಡಿನ ಮೇಲೆ, ಬೆಳೆಗೆ ಬೇಕಾದ ಹಾಗೆ ಮಳೆಯನ್ನು ಸುರಿಸಿ, ನಿಮ್ಮ ಎಲ್ಲ ವ್ಯವಸಾಯವನ್ನೂ ಫಲಭರಿತವಾಗಿ ಮಾಡುವರು. “ನೀವು ಅನೇಕ ಜನಗಳಿಗೆ ಸಾಲಕೊಡುವಿರೇ ಹೊರತು ಸಾಲತೆಗೆದುಕೊಳ್ಳುವುದಿಲ್ಲ.",
			"13": "<sup>13</sup> ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ಮಾತುಗಳನ್ನು ನೀವು ಬಿಟ್ಟು ಎಡಬಲಕ್ಕೆ ಹೋಗದೆ, ಬೇರೆ ದೇವರುಗಳನ್ನು ಅವಲಂಬಿಸದೆ, ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಆಜ್ಞೆಗಳಲ್ಲೇ ಲಕ್ಷ್ಯವಿಟ್ಟು ಅವುಗಳನ್ನೇ ಅನುಸರಿಸಿ ನಡೆದರೆ ಅವರು ಇತರರಿಗೆ ನಿಮ್ಮನ್ನು ಅಧೀನಮಾಡದೆ, ಎಲ್ಲರಿಗೂ ಶಿರಸ್ಸನ್ನಾಗಿಯೇ ಮಾಡುವರು; ನೀವು ಎಲ್ಲರಿಗಿಂತಲೂ ಮೇಲಿನವರಾಗುವಿರೇ ಹೊರತು ಕೆಳಗಿನವರಾಗಿ ಇರುವುದಿಲ್ಲ.",
			"15": "<sup>15</sup> “ಆದರೆ ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಮಾತಿಗೆ ಕಿವಿಗೊಡದೆ, ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ಅವರ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ಅನುಸರಿಸದೆ ಹೋದರೆ, ಈ ಕೆಳಕಂಡ ಅಶುಭಗಳು ನಿಮಗೆ ಪ್ರಾಪ್ತವಾಗುವುವು:",
			"16": "<sup>16</sup> “ನಿಮಗೆ ಊರಲ್ಲೂ ಅಡವಿಯಲ್ಲೂ ಅಶುಭವುಂಟಾಗುವುದು.",
			"17": "<sup>17</sup> ನಿಮ್ಮ ಧಾನ್ಯಗಳಿಗೂ ಅವುಗಳಿಂದಾದ ಭೋಜನಗಳಿಗೂ ಅಶುಭ ಉಂಟಾಗುವುದು.",
			"18": "<sup>18</sup> ನಿಮ್ಮ ಸಂತಾನಗಳಿಗೆ, ವ್ಯವಸಾಯಗಳಿಗೆ, ಹಾಗು ದನಕುರಿಗಳಿಗೆ ಅಶುಭ ಉಂಟಾಗುವುದು.",
			"19": "<sup>19</sup> ನೀವು ಕೆಲಸಕ್ಕೆ ಹೋಗುವಾಗಲೂ ಬರುವಾಗಲೂ ಅಶುಭವುಂಟಾಗುವುದು.",
			"20": "<sup>20</sup> “ನೀವು ದುರ್ನಡತೆಯುಳ್ಳವರಾಗಿ ಸರ್ವೇಶ್ವರನನ್ನು ಬಿಟ್ಟದ್ದರಿಂದ ಅವರು ನಿಮ್ಮ ಎಲ್ಲ ಪ್ರಯತ್ನಗಳಲ್ಲೂ ವಿಪತ್ತು, ಕಳವಳ, ಶಾಪ, ಇವುಗಳನ್ನುಂಟುಮಾಡುತ್ತಾ ನಿಮ್ಮನ್ನು ಬೇಗನೆ ನಾಶಮಾಡುವರು.",
			"21": "<sup>21</sup> ವ್ಯಾಧಿ ನಿಮಗೆ ಹತ್ತಿಕೊಂಡೇ ಇರುವಂತೆ ಮಾಡಿ ನೀವು ಸ್ವಾಧೀನ ಮಾಡಿಕೊಳ್ಳಲು ಹೋಗುವ ನಾಡಿನಲ್ಲಿ ನಿಮ್ಮಲ್ಲಿ ಒಬ್ಬರೂ ಉಳಿಯದಂತೆ ಸರ್ವೇಶ್ವರ ಮಾಡುವರು.",
			"22": "<sup>22</sup> ನಿಮ್ಮನ್ನು ಕ್ಷಯರೋಗ, ಚಳಿಜ್ವರ, ಉರಿತ, ಉಷ್ಣಜ್ವರ ಇವುಗಳಿಂದಲೂ, ನಾಡನ್ನು ಕ್ಷಾಮದಿಂದಲೂ, ಬೆಳೆಯನ್ನು ಕಾಡಿನ ಬಿಸಿಗಾಳಿಗಳಿಂದಲೂ ಪೀಡಿಸುವರು. ನೀವು ಸಾಯುವ ತನಕ ಈ ಪೀಡೆಗಳು ನಿಮ್ಮನ್ನು ಬೆನ್ನತ್ತುವುವು.",
			"23": "<sup>23</sup> ಆಮೇಲೆ ಆಕಾಶ ಮಳೆ ಸುರಿಸದೆ ತಾಮ್ರದಂತೆಯೂ ಕೆಳಗೆ ಭೂಮಿ ಬೆಳೆಕೊಡದೆ ಕಬ್ಬಿಣದಂತೆಯೂ ಇರುವುವು.",
			"24": "<sup>24</sup> ನಿಮ್ಮ ನಾಡಿನ ಮೇಲೆ ಆಕಾಶದಿಂದ ಮಳೆಗೆ ಬದಲಾಗಿ ಧೂಳಿಯನ್ನೂ ಉಸುಬನ್ನೂ ಸರ್ವೇಶ್ವರ ಸುರಿಸುವರು; ಇದರಿಂದ ನೀವು ನಾಶವಾಗಿ ಹೋಗುವಿರಿ.",
			"25": "<sup>25</sup> ಶತ್ರುಗಳಿಂದ ನೀವುಪರಾಜಯವನ್ನು ಹೊಂದುವಂತೆ ಸರ್ವೇಶ್ವರ ಮಾಡುವರು; ನೀವು ಒಂದೇ ದಾರಿಯಿಂದ ಅವರ ಮೇಲೆ ದಾಳಿಮಾಡಲು ಹೋಗಿ ಏಳು ದಾರಿ ಹಿಡಿದು ಓಡಿಹೋಗುವಿರಿ. ಜಗದ ರಾಜ್ಯಗಳೆಲ್ಲವೂ ಇದನ್ನು ಕಂಡು ಬೆರಗಾಗುವುವು.",
			"26": "<sup>26</sup> ನಿಮ್ಮ ಹೆಣಗಳು ಪಕ್ಷಿಗಳಿಗೂ ಕಾಡುಮೃಗಗಳಿಗೂ ಆಹಾರವಾಗುವುವು; ಅವುಗಳನ್ನು ಬೆದರಿಸಿ ಓಡಿಸುವುದಕ್ಕೂ ಯಾರೂ ಇರುವುದಿಲ್ಲ.",
			"27": "<sup>27</sup> “ಈಜಿಪ್ಟರನ್ನು ಬಾಧಿಸಿದಂತೆ ಹುಣ್ಣು, ಬಾವು, ತುರಿಕಜ್ಜಿ ಮುಂತಾದ ವಾಸಿಯಾಗದ ರೋಗಗಳಿಂದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮನ್ನು ಬಾಧಿಸುವರು.",
			"28": "<sup>28</sup> ಹುಚ್ಚುತನ, ಕುರುಡುತನ, ಬೆಪ್ಪುತನ ಇವುಗಳಿಂದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮನ್ನು ಪೀಡಿಸುವರು.",
			"29": "<sup>29</sup> ಕುರುಡರಂತೆ ನಡುಮಧ್ಯಾಹ್ನದಲ್ಲೂ ಕತ್ತಲಾಯಿತೆಂದು ನೀವು ತಡವರಿಸುವಿರಿ; ನೀವು ಮಾಡುವ ಕೆಲಸ ಯಾವುದೂ ಕೈಗೂಡುವುದಿಲ್ಲ. ಅನ್ಯರು ನಿಮ್ಮನ್ನು ನಿರಂತರವಾಗಿ ಪೀಡಿಸುತ್ತಾ ನಿಮ್ಮ ಸೊತ್ತನ್ನು ಸೂರೆಮಾಡುತ್ತಾ ಇರುವರು. ನಿಮಗೆ ಸಹಾಯ ಮಾಡಲು ಯಾರೂ ಇರುವುದಿಲ್ಲ.",
			"30": "<sup>30</sup> “ನೀವು ಮದುವೆಮಾಡಿಕೊಂಡ ಮಹಿಳೆಯನ್ನು ಮತ್ತೊಬ್ಬನು ಮಾನಭಂಗಪಡಿಸುವನು; ನೀವು ಕಟ್ಟಿಕೊಂಡ ಮನೆಯಲ್ಲಿ ಮತ್ತೊಬ್ಬನು ವಾಸಮಾಡುವನು; ನೀವು ಮಾಡಿದ ದ್ರಾಕ್ಷಿತೋಟದ ಬೆಳೆ ನಿಮಗೆ ದೊರಕದು.",
			"31": "<sup>31</sup> ನಿಮ್ಮ ಪಶುಗಳನ್ನು ನಿಮ್ಮ ಕಣ್ಣಮುಂದೆಯೇ ಕೊಯ್ಯುವರು; ಆದರೆ ಅವುಗಳ ಮಾಂಸ ನಿಮಗೆ ಸಿಕ್ಕದು. ನಿಮ್ಮ ಕತ್ತೆಯನ್ನು ನಿಮ್ಮೆದುರಿಗೇ ಬಲಾತ್ಕಾರದಿಂದ ಹಿಡಿದುಕೊಂಡು ಹೋಗುವರು. ಆದರೆ ಅದನ್ನು ನಿಮಗೆ ಹಿಂದಕ್ಕೆ ಕೊಡುವುದಿಲ್ಲ. ನಿಮ್ಮ ಆಡುಕುರಿಗಳು ಶತ್ರುಗಳ ಪಾಲಾಗುವುವು. ನಿಮಗೆ ರಕ್ಷಕರು ಯಾರೂ ಇರುವುದಿಲ್ಲ.",
			"32": "<sup>32</sup> ನಿಮ್ಮ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ನಿಮ್ಮ ಮುಂದೆಯೇ ಹಿಡಿದು ಅನ್ಯರಿಗೆ ವಶಪಡಿಸುವರು; ನೀವು ಹಗಲೆಲ್ಲಾ ಅವರನ್ನು ಕಾಣಬೇಕೆಂದು ಹಂಬಲಿಸುತ್ತಾ ಕಂಗೆಡುವಿರಿ; ಆದರೆ ನಿಮ್ಮ ಯತ್ನವೇನೂ ಸಾಗುವುದಿಲ್ಲ.",
			"33": "<sup>33</sup> ನಿಮಗೆ ಗುರುತೇ ಇಲ್ಲದ ಜನಾಂಗದವರು ಬಂದು ನಿಮ್ಮ ನಾಡಿನ ಬೆಳೆಯನ್ನೂ ನಿಮ್ಮ ಕಷ್ಟಾರ್ಜಿತವನ್ನೂ ತಿಂದುಬಿಡುವರು. ನೀವಾದರೋ ನಿರಂತರ ಹಿಂಸೆಗೂ ಬಲಾತ್ಕಾರಕ್ಕೂ ಗುರಿಯಾಗುವಿರಿ.",
			"34": "<sup>34</sup> ನಿಮ್ಮ ಮುಂದೆ ನಡೆಯುವ ದೃಶ್ಯಗಳನ್ನು ನೋಡಿ ಹುಚ್ಚರಾಗಿಹೋಗುವಿರಿ.",
			"35": "<sup>35</sup> “ನಿಮ್ಮ ಮೊಣಕಾಲುಗಳಲ್ಲೂ ತೊಡೆಗಳಲ್ಲೂ ಮತ್ತು ಅಂಗಾಲು ಮೊದಲ್ಗೊಂಡು ನಡುನೆತ್ತಿಯವರೆಗೂ ವಾಸಿಯಾಗದ ಕೆಟ್ಟಕೆಟ್ಟ ಹುಣ್ಣುಗಳನ್ನು ಸರ್ವೇಶ್ವರ ಹುಟ್ಟಿಸಿ ಬಾಧಿಸುವರು;",
			"36": "<sup>36</sup> ನಿಮಗೂ ನಿಮ್ಮ ಪಿತೃಗಳಿಗೂ ಗೊತ್ತಿಲ್ಲದ ಜನಾಂಗದವರ ದೇಶಕ್ಕೆ ನಿಮ್ಮನ್ನೂ ನೀವು ನೇಮಿಸಿಕೊಳ್ಳುವ ಅರಸರನ್ನೂ ಒಯ್ಯಿಸುವರು; ಅಲ್ಲಿ ನೀವು ಮರದ ದೇವರುಗಳನ್ನೂ ಕಲ್ಲಿನ ದೇವರುಗಳನ್ನೂ ಪೂಜಿಸುವಿರಿ.",
			"37": "<sup>37</sup> ಸರ್ವೇಶ್ವರ ನಿಮ್ಮನ್ನು ಒಯ್ಯಿಸುವ ಜನಾಂಗಗಳಲ್ಲಿ ನೀವು ಭೀಕರತೆಗೂ ನಿಂದೆಲಾವಣಿಗೂ ಪರಿಹಾಸ್ಯಕ್ಕೂ ಗುರಿಯಾಗುವಿರಿ.",
			"38": "<sup>38</sup> “ಹೊಲದಲ್ಲಿ ನೀವು ಎಷ್ಟು ಬೀಜ ಬಿತ್ತಿದರೂ ಮಿಡತೆಯ ದಂಡುಬಂದು ಅದನ್ನು ತಿಂದುಬಿಡುವುದು; ನಿಮಗೆ ದೊರೆಯುವ ಬೆಳೆ ಅತ್ಯಲ್ಪವಾಗುವುದು;",
			"39": "<sup>39</sup> ದ್ರಾಕ್ಷಾವ್ಯವಸಾಯವನ್ನು ನೀವು ಎಷ್ಟುಮಾಡಿದರೂ ಅದರ ಹಣ್ಣುಗಳನ್ನು ಹುಳುಗಳೇ ತಿಂದುಹಾಕುವುವು; ನೀವು ಕೂಡಿಸಿಕೊಳ್ಳಲು ನಿಮಗೆ ಬಿಡವು. ಅವುಗಳ ರಸವನ್ನು ರುಚಿನೋಡಗೊಡಿಸವು.",
			"40": "<sup>40</sup> ನಿಮ್ಮ ದೇಶಪ್ರದೇಶಗಳಲ್ಲಿ ಎಣ್ಣೇಮರಗಳು ಎಷ್ಟಿದ್ದರೂ ಅವುಗಳ ಕಾಯಿಗಳು ಉದುರಿಹೋಗುವುವು; ಮೈಗೆ ಹಚ್ಚಿಕೊಳ್ಳಲು ಎಣ್ಣೆ ಸಿಗದು.",
			"41": "<sup>41</sup> ನಿಮಗೆ ಗಂಡುಹೆಣ್ಣು ಮಕ್ಕಳು ಎಷ್ಟು ಹುಟ್ಟಿದರೂ ಅವರು ಸೆರೆಯವರಾಗಿ ಒಯ್ಯಲ್ಪಡುವರು; ನಿಮ್ಮ ಬಳಿ ಅವರು ಇರರು.",
			"42": "<sup>42</sup> ನಿಮ್ಮ ಗಿಡಮರಗಳೂ ಪಚ್ಚೆಪೈರುಗಳೂ ಮಿಡತೆಯ ಪಾಲಾಗುವುವು.",
			"43": "<sup>43</sup> ನಿಮ್ಮ ಮಧ್ಯೆಯಿರುವ ಅನ್ಯರು ನಿಮಗಿಂತಲೂ ಹೆಚ್ಚೆಚ್ಚಾಗಿ ಅಭಿವೃದ್ಧಿಗೆ ಬರುವರು; ನೀವೋ ಕಡಿಮೆ ಆಗುತ್ತಾ ಹೀನಸ್ಥಿತಿಗೆ ಇಳಿಯುವಿರಿ.",
			"44": "<sup>44</sup> ಅವರು ನಿಮಗೆ ಸಾಲಕೊಡುವರೇ ಹೊರತು ನೀವು ಅವರಿಗೆ ಕೊಡುವುದಿಲ್ಲ. ನೀವು ಅವರಿಗೆ ಅಧೀನರಾಗುವಿರಿ; ಅವರು ನಿಮಗೆ ಶಿರಸ್ಸಾಗುವರು.",
			"45": "<sup>45</sup> “ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಮಾತಿಗೆ ಕಿವಿಗೊಡದೆ ಅವರು ನೇಮಿಸಿದ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ಅನುಸರಿಸದೆ ಹೋದುದರಿಂದ ಈ ಎಲ್ಲಾ ಅಶುಭಗಳು ನಿಮಗೆ ಪ್ರಾಪ್ತವಾಗಿ, ನಿಮ್ಮನ್ನು ಹಿಂದಟ್ಟಿ ಹಿಡಿದು, ಕಡೆಗೆ ನಾಶಮಾಡುವುವು.",
			"46": "<sup>46</sup> ಇವು ನಿಮಗೂ ನಿಮ್ಮ ಸಂತತಿಯವರಿಗೂ ನಿರಂತರವಾಗಿ ಪ್ರಾಪ್ತವಾಗುವುವು; ಸರ್ವರಿಗೆ ಬೆರಗನ್ನೂ ಎಚ್ಚರಿಕೆಯನ್ನೂ ಉಂಟುಮಾಡುವುವು.",
			"47": "<sup>47</sup> “ನಿಮಗೆ ಸರ್ವಸಮೃದ್ಧಿಯುಂಟಾದ ಕಾಲದಲ್ಲಿ ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ಹರ್ಷಾನಂದದಿಂದ ಸೇವೆಮಾಡದೆಹೋದಿರಿ.",
			"48": "<sup>48</sup> ಆದುದರಿಂದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮ ಮೇಲೆ ಶತ್ರುಗಳನ್ನು ಬರಮಾಡುವರು; ಆಗ ನೀವು ಹಸಿವು ಬಾಯಾರಿಕೆಗಳಿಗೆ ಗುರಿಯಾಗಿ ಬಟ್ಟೆಬರೆ ಏನೂ ಇಲ್ಲದೆ, ಆ ಶತ್ರುಗಳಿಗೇ ಸೇವಕರಾಗಬೇಕಾಗುವುದು. ಕಬ್ಬಿಣದ ನೊಗವನ್ನು ಹೇರಿಸಿ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮನ್ನು ನಾಶಮಾಡುವರು;",
			"49": "<sup>49</sup> ನಿಮಗೆ ತಿಳಿಯದ ಭಾಷೆಯನ್ನಾಡುವ ಒಂದು ಜನಾಂಗ, ರಣಹದ್ದು ಹೇಗೆ ದೂರದಿಂದ ಹಾರಿಬರುವುದೊ ಹಾಗೆಯೇ ದೂರದಿಂದ ನಿಮ್ಮ ಮೇಲೆ ಬರುವಂತೆ ಮಾಡುವರು.",
			"50": "<sup>50</sup> ಆ ಜನಾಂಗದವರು ಕ್ರೂರಮುಖವುಳ್ಳವರು; ನೀವು ವೃದ್ಧರೆಂದು ಮರ್ಯಾದೆ ತೋರಿಸುವುದಿಲ್ಲ, ಚಿಕ್ಕವರೆಂದು ಕನಿಕರಿಸುವುದಿಲ್ಲ.",
			"51": "<sup>51</sup> ಅವರು ನಿಮ್ಮ ದನಗಳನ್ನೂ ಬೆಳೆಗಳನ್ನೂ ತಿಂದುಬಿಡುವರು. ನಿಮಗೆ ಧಾನ್ಯವನ್ನಾಗಲಿ, ದ್ರಾಕ್ಷಾರಸವನ್ನಾಗಲಿ, ಎಣ್ಣೆಯನ್ನಾಗಲಿ, ದನಕುರಿಗಳ ಸಂತಾನವನ್ನಾಗಲಿ, ಉಳಿಸದೆ ನಿಮ್ಮನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ನಾಶಮಾಡುವರು.",
			"52": "<sup>52</sup> ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ಕೊಟ್ಟಿರುವ ನಾಡಿನ ಪಟ್ಟಣಗಳಿಗೆಲ್ಲಾ ಅವರು ಮುತ್ತಿಗೆ ಹಾಕುವರು; ನೀವು ನೆಚ್ಚಿಕೊಳ್ಳುವ ಎತ್ತರವಾದ ಕೊತ್ತಲುಗಳುಳ್ಳ ಗೋಡೆಗಳನ್ನು ಕೆಡವಿಬಿಡುವರು.",
			"53": "<sup>53</sup> “ಶತ್ರುಗಳು ನಿಮ್ಮ ಪಟ್ಟಣಗಳಿಗೆ ಮುತ್ತಿಗೆ ಹಾಕಿ ನಿಮ್ಮನ್ನು ಎಂಥ ಇಕ್ಕಟ್ಟಿಗೆ ಸಿಕ್ಕಿಸುವರೆಂದರೆ, ನೀವು ನಿಮ್ಮ ಸಂತಾನವನ್ನೇ, ಅಂದರೆ, ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ಕೊಟ್ಟಿರುವ ಗಂಡುಹೆಣ್ಣು ಮಕ್ಕಳ ಮಾಂಸವನ್ನೇ ತಿನ್ನುವಿರಿ.",
			"54": "<sup>54</sup> ಶತ್ರುಗಳು ನಿಮ್ಮ ಪಟ್ಟಣಗಳಿಗೆಲ್ಲಾ ಮುತ್ತಿಗೆ ಹಾಕಿ, ನಿಮ್ಮನ್ನು ಭೀಕರವಾದ ಬಿಕ್ಕಟ್ಟಿಗೆ ಸಿಕ್ಕಿಸುವರು. ಆಗ ನಿಮ್ಮಲ್ಲಿ ಮೃದುಸ್ವಭಾವಿ ಹಾಗು ಅತಿ ಸುಕುಮಾರ ಆದವನೂ ಕೂಡ ತನಗೇ ತಿನ್ನಲಿಕ್ಕೆ ಏನೂ ಉಳಿಯದೆ, ತನ್ನ ಮಕ್ಕಳ ಮಾಂಸವನ್ನು ತಾನೇ ತಿನ್ನಬೇಕೆಂದು, ಅಣ್ಣತಮ್ಮಂದಿರಿಗೂ ಪ್ರಾಣಪ್ರಿಯ ಹೆಂಡತಿಗೂ ಉಳಿದ ಮಕ್ಕಳಿಗೂ ಮೋರೆಸಿಂಡರಿಸಿಕೊಂಡು ಆ ಮಾಂಸದಲ್ಲಿ ಅವರಿಗೆ ಸ್ವಲ್ಪವನ್ನಾದರೂ ಕೊಡದೆ ಹೋಗುವನು.",
			"56": "<sup>56</sup> ಶತ್ರುಗಳು ನಿಮ್ಮ ಪಟ್ಟಣಗಳಿಗೆ ಮುತ್ತಿಗೆ ಹಾಕಿ ನಿಮ್ಮನ್ನು ಆ ಭೀಕರ ಇಕ್ಕಟ್ಟಿಗೆ ಸಿಕ್ಕಿಸಿದ ಕಾಲದಲ್ಲಿ ಅಂಗಾಲನ್ನೂ ನೆಲಕ್ಕೆ ಇಡದಷ್ಟು ಕೋಮಲ ಹಾಗು ಸುಕುಮಾರಿಯಾದ ಸ್ತ್ರೀ ತಿನ್ನಲಿಕ್ಕೆ ಏನೂ ಇಲ್ಲದವಳಾಗಿ, ತಾನು ಆಗಲೇ ಹೆತ್ತ ಮಗುವನ್ನೂ ಅದರ ಮೇಲಣ ಮಾಸನ್ನೂ ಗುಟ್ಟಾಗಿ ತಿನ್ನಲಾಶಿಸುವಳು; ಪ್ರಾಣಪ್ರಿಯರಾದ ಗಂಡನಿಗು, ಮಗನಿಗು, ಮಗಳಿಗು ಕೊಡಲಿಚ್ಛಿಸದೆ ಮೋರೆಯನ್ನು ಸಿಂಡರಿಸಿಕೊಳ್ಳುವಳು.",
			"58": "<sup>58</sup> “ಈ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುವ ಧರ್ಮಶಾಸ್ತ್ರ ವಾಕ್ಯಗಳನ್ನೆಲ್ಲಾ ನೀವು ಅನುಸರಿಸಲೊಲ್ಲದೆ, ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನೆಂಬ ಮಹಾಮಹಿಮೆಯುಳ್ಳ ಪೂಜ್ಯನಾಮದಲ್ಲಿ ನೀವು ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿರದೇ ಹೋದರೆ",
			"59": "<sup>59</sup> ಅವರು ನಿಮಗೂ ನಿಮ್ಮ ಸಂತತಿಯವರಿಗೂ ದೀರ್ಘಕಾಲವಿರುವ ಬಹುಭಯಂಕರವಾದ ಉಪದ್ರವಗಳನ್ನು ಬರಮಾಡುವರು. ದೀರ್ಘಕಾಲ ವಾಸಿಯಾಗದ ಘೋರವ್ಯಾಧಿಗಳಿಂದ ನಿಮ್ಮನ್ನು ಬಾಧಿಸುವರು.",
			"60": "<sup>60</sup> ನೀವು ಅಂಜಿಕೊಳ್ಳುತ್ತಿದ್ದ ಈಜಿಪ್ಟ್ ದೇಶದ ರೋಗಗಳನ್ನೆಲ್ಲ ಮತ್ತೆ ನಿಮ್ಮ ಮೇಲೆ ಬರಮಾಡುವರು; ಅವು ನಿಮಗೆ ಅಂಟಿಕೊಂಡೇ ಇರುವುವು.",
			"61": "<sup>61</sup> ಇಷ್ಟುಮಾತ್ರವಲ್ಲದೆ, ಈ ಧರ್ಮಶಾಸ್ತ್ರಗ್ರಂಥದಲ್ಲಿ ಬರೆಯದೆ ಇರುವ ವಿಧವಿಧವಾದ ರೋಗಗಳನ್ನೂ ವ್ಯಾಧಿಗಳನ್ನೂ ನಿಮ್ಮ ಮೇಲೆ ಬರಮಾಡಿ ನಿಮ್ಮನ್ನು ನಾಶಮಾಡುವರು.",
			"62": "<sup>62</sup> ನಕ್ಷತ್ರಗಳಷ್ಟು ಅಸಂಖ್ಯರಾಗಿರುವ ನೀವು, ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಮಾತಿನಲ್ಲಿ ಲಕ್ಷ್ಯವಿಡದೆ ಹೋದುದರಿಂದ ಸ್ವಲ್ಪ ಮಂದಿಯೇ ಉಳಿಯುವಿರಿ.",
			"63": "<sup>63</sup> ಸರ್ವೇಶ್ವರ ಈಗ ನಿಮ್ಮನ್ನು ಅಭಿವೃದ್ಧಿಪಡಿಸುವುದರಲ್ಲೂ ಹೆಚ್ಚಿಸುವುದರಲ್ಲೂ ಹೇಗೆ ಸಂತೋಷಪಡುತ್ತಾರೋ ಹಾಗೆಯೇ ಆಗ ನಿಮ್ಮನ್ನು ಕೆಡಿಸಿ ನಾಶಮಾಡುವುದರಲ್ಲಿ ಸಂತೋಷಪಡುವರು. ನೀವು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವುದಕ್ಕೆ ಹೋಗುವ ನಾಡಿನೊಳಗಿಂದ ನಿಮ್ಮನ್ನು ಕಿತ್ತುಹಾಕುವರು.",
			"64": "<sup>64</sup> “ಭೂಮಂಡಲದ ಚತುರ್ದಿಕ್ಕುಗಳಲ್ಲಿರುವ ಎಲ್ಲ ಜನಾಂಗಗಳಲ್ಲಿಯೂ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮನ್ನು ಚದುರಿಸುವರು; ಅಲ್ಲಿ ನಿಮಗಾಗಲಿ ನಿಮ್ಮ ಪಿತೃಗಳಿಗಾಗಲಿ ಗೊತ್ತಿಲ್ಲದ ಮರದ ದೇವರುಗಳನ್ನೂ ಕಲ್ಲಿನ ದೇವರುಗಳನ್ನೂ ಪೂಜಿಸುವಿರಿ.",
			"65": "<sup>65</sup> ಆ ದೇಶಗಳಲ್ಲಿ ನಿಮಗೆ ಯಾವ ವಿಶ್ರಾಂತಿಯೂ ದೊರೆಯುವುದಿಲ್ಲ. ಸ್ವಂತದ್ದು ಎಂದು ಹೇಳಿಕೊಳ್ಳಲು ಅಂಬೆಗಾಲಿಡುವಷ್ಟು ಸ್ಥಳಸಿಕ್ಕುವುದಿಲ್ಲ. ನೀವು ಗಡಗಡನೆ ನಡುಗುವ ಹೃದಯವುಳ್ಳವರಾಗಿ, ದಿಕ್ಕುತೋರದೆ ಕಂಗೆಟ್ಟವರಾಗಿ ಹಾಗು ಮನಗುಂದಿದವರಾಗಿ ಇರುವಂತೆ ಸರ್ವೇಶ್ವರ ಮಾಡುವರು.",
			"66": "<sup>66</sup> ಬದುಕುವೆವೋ ಇಲ್ಲವೋ ಎಂಬುದಾಗಿ ಅನುಮಾನಪಟ್ಟು ನೀವು ಹಗಲಿರುಳು ಪ್ರಾಣಭಯದಲ್ಲಿರುವಿರಿ; ಪ್ರಾಣದ ಮೇಲಣ ನಂಬಿಕೆಯನ್ನು ಬಿಟ್ಟೇಬಿಡುವಿರಿ.",
			"67": "<sup>67</sup> ಮನಸ್ಸಿನಲ್ಲಿ ನಿರಂತರ ಪ್ರಾಣಭಯವಿರುವುದರಿಂದ ಹಾಗು ಕಣ್ಣಿನಿಂದ ಭಯಂಕರವಾದ ಸಂಗತಿಗಳನ್ನು ನೋಡುವುದರಿಂದ ನೀವು ಹೊತ್ತಾರೆಯಲ್ಲೇ, ‘ಅಯ್ಯೋ, ಅಯ್ಯೋ, ಸಾಯಂಕಾಲ ಯಾವಾಗ ಬರುವುದೋ?’ ಎಂದೂ ಸಾಯಂಕಾಲದಲ್ಲಿ, ‘ಅಯ್ಯೋ, ಅಯ್ಯೋ, ಮರುದಿನ ಯಾವಾಗ ಬರುವುದೋ?’ ಎಂದೂ ಕೊರಗುವಿರಿ.",
			"68": "<sup>68</sup> ನೀವು ಈಜಿಪ್ಟ್ ದೇಶವನ್ನು ಮರಳಿ ಎಂದಿಗೂ ನೋಡುವುದಿಲ್ಲವೆಂದು ಸರ್ವೇಶ್ವರ ಹೇಳಿದ್ದರೂ ಅವರು ನಿಮ್ಮನ್ನು ಹಡಗುಗಳನ್ನೇರಿಸಿ ಅಲ್ಲಿಗೆ ತಿರುಗಿ ಹೋಗಮಾಡುವರು. ಅಲ್ಲಿ ನೀವು ಶತ್ರುಗಳಿಗೆ ನಿಮ್ಮನ್ನೇ ಗಂಡು ಹೆಣ್ಣು ಎನ್ನದೆ ಗುಲಾಮರನ್ನಾಗಿ ಮಾರಿಕೊಳ್ಳಬೇಕೆಂದು ಅಪೇಕ್ಷಿಸಿದರೂ ಯಾರೂ ನಿಮ್ಮನ್ನು ಕೊಂಡುಕೊಳ್ಳುವುದಿಲ್ಲ.”"
		},
		"29": {
			"1": "<sup>1</sup> ಸರ್ವೇಶ್ವರಸ್ವಾಮಿ ಹೋರೇಬಿನಲ್ಲಿ ಇಸ್ರಯೇಲರೊಡನೆ ಮಾಡಿದ ಒಡಂಬಡಿಕೆ ಅಲ್ಲದೆ ಅವರು ಮೋವಾಬ್ ನಾಡಿನಲ್ಲಿ ಅವರೊಡನೆ ಮಾಡಬೇಕೆಂದು ಮೋಶೆಗೆ ಆಜ್ಞಾಪಿಸಿದ ಒಡಂಬಡಿಕೆಯ ವಚನಗಳು ಇವು:",
			"2": "<sup>2</sup> ಮೋಶೆ ಇಸ್ರಯೇಲರೆಲ್ಲರನ್ನು ಕರೆದು ಹೀಗೆಂದನು, “ಸರ್ವೇಶ್ವರ ಈಜಿಪ್ಟ್ ದೇಶದಲ್ಲಿ ನಿಮ್ಮ ಕಣ್ಣಮುಂದೆ ಫರೋಹನಿಗೂ ಅವನ ಪರಿವಾರದವರಿಗೂ",
			"3": "<sup>3</sup> ಅವನ ಇಡೀ ನಾಡಿಗೂ ಮಾಡಿದವುಗಳನ್ನೆಲ್ಲಾ ನೀವು ನೋಡಿದ್ದೀರಿ. ಮಹಾಪರಿಶೋಧನೆ, ಪವಾಡ, ಮಹತ್ಕಾರ್ಯ ಇವುಗಳನ್ನು ಕಣ್ಣಾರೆ ನೋಡಿದ್ದೀರಿ.",
			"4": "<sup>4</sup> ಆದರೂ ಗ್ರಹಿಸುವ ಬುದ್ಧಿ, ನೋಡುವ ಕಣ್ಣು, ಕೇಳುವ ಕಿವಿ ಇವುಗಳನ್ನು ಸರ್ವೇಶ್ವರ ಇಂದಿನವರೆಗೂ ಅಯ್ಯೋ ನಿಮಗೆ ಅನುಗ್ರಹಿಸಲಿಲ್ಲ!",
			"5": "<sup>5</sup> ನಲವತ್ತು ವರ್ಷ ನಾನು ನಿಮ್ಮನ್ನು ಮರುಭೂಮಿಯಲ್ಲಿ ನಡೆಸಿಕೊಂಡು ಬಂದಿದ್ದೇನೆ. ನಿಮ್ಮ ಮೈಮೇಲಿದ್ದ ಉಡುಪಾಗಲಿ, ಕಾಲಲ್ಲಿದ್ದ ಕೆರವಾಗಲಿ ಸವೆದುಹೋಗಲಿಲ್ಲ.",
			"6": "<sup>6</sup> ನೀವು ರೊಟ್ಟಿ ತಿನ್ನಲಿಲ್ಲ; ದ್ರಾಕ್ಷಾರಸವನ್ನಾಗಲಿ ಯಾವ ಮದ್ಯವನ್ನಾಗಲಿ ಕುಡಿಯಲೇ ಇಲ್ಲ. ಸರ್ವೇಶ್ವರನೇ ನಿಮ್ಮ ದೇವರು ಎಂಬುದು ನಿಮಗೆ ತಿಳಿಯಬೇಕೆಂಬ ಉದ್ದೇಶದಿಂದ ಹೀಗಾಯಿತು.",
			"7": "<sup>7</sup> ನೀವು ಈ ಸ್ಥಳಕ್ಕೆ ಬಂದಾಗ ಹೆಷ್ಬೋನಿನ ಅರಸ ಸೀಹೋನನೂ ಬಾಷಾನಿನ ಅರಸ ಓಗನೂ ನಮ್ಮೊಡನೆ ಯುದ್ಧಮಾಡುವುದಕ್ಕೆ ಬಂದರು.",
			"8": "<sup>8</sup> ನಾವು ಅವರನ್ನು ಸೋಲಿಸಿ ಅವರ ರಾಜ್ಯಗಳನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡು ರೂಬೇನ್ಯರಿಗೂ ಗಾದ್ಯರಿಗೂ ಮನಸ್ಸೆಕುಲದವರಲ್ಲಿ ಅರ್ಧಜನರಿಗೂ ಸೊತ್ತಾಗಿ ಕೊಟ್ಟೆವಷ್ಟೆ.",
			"9": "<sup>9</sup> ಈ ಸಂಗತಿಗಳನ್ನೆಲ್ಲಾ ನೀವು ಜ್ಞಾಪಕಮಾಡಿಕೊಂಡು ಈ ಒಡಂಬಡಿಕೆಯ ವಾಕ್ಯಗಳನ್ನು ಅನುಸರಿಸಿ ನಡೆಯಬೇಕು; ಆಗ ನೀವು ನಡೆಸುವ ಕೆಲಸಕಾರ್ಯಗಳಲ್ಲಿ ಜಾಣರಾಗಿ ಅಭಿವೃದ್ಧಿ ಹೊಂದುವಿರಿ.",
			"10": "<sup>10</sup> “ಇಸ್ರಯೇಲ ಜನರೇ, ನಿಮ್ಮ ಮುಖ್ಯಸ್ಥರು,",
			"11": "<sup>11</sup> ಗೋತ್ರದವರು, ಹಿರಿಯರು, ಅಧಿಕಾರಿಗಳು, ಹೆಂಗಸರು, ಮಕ್ಕಳು ನಿಮ್ಮ ಪಾಳೆಯದಲ್ಲಿ ಇರುವ ಅನ್ಯರಾದ ಕಟ್ಟಿಗೆಯವರು, ನೀರಿನವರು ಮುಂತಾದ ಆಳುಗಳು,",
			"12": "<sup>12</sup> ನೀವೆಲ್ಲರೂ ಈ ದಿನ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೆಶ್ವರನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನಿಂತಿರುವುದಕ್ಕೆ ಕಾರಣವೇನೆಂದರೆ - ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮಗೆ ಹೇಳಿದಂತೆ ಹಾಗು ನಿಮ್ಮ ಪಿತೃಗಳಾದ ಅಬ್ರಹಾಮ್, ಇಸಾಕ್, ಯಕೋಬರಿಗೆ ವಾಗ್ದಾನಮಾಡಿದಂತೆ ನಿಮ್ಮನ್ನು ತಮಗೆ ಸ್ವಕೀಯ ಜನರನ್ನಾಗಿಯೂ ತಮ್ಮನ್ನು ನಿಮಗೆ ದೇವರನ್ನಾಗಿಯೂ ಸ್ಥಾಪಿಸಿಕೊಳ್ಳುವುದಕ್ಕಾಗಿ ಈಗ ನಿಮ್ಮೊಡನೆ ಪ್ರಮಾಣಪೂರ್ವಕವಾದ ಒಂದು ಒಡಂಬಡಿಕೆಯನ್ನು ಮಾಡಿಕೊಳ್ಳಬೇಕೆಂದಿದ್ದಾರೆ; ನೀವು ಅದಕ್ಕೆ ಸೇರಲು ಕೂಡಿಬಂದಿದ್ದೀರಿ.",
			"14": "<sup>14</sup> “ನಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸಿದ್ಧರಾಗಿ ನಿಂತಿರುವ ನಮ್ಮೊಡನೆ ಮಾತ್ರವಲ್ಲದೆ, ಮುಂದೆ ಹುಟ್ಟುವ ನಮ್ಮ ಸಂತತಿಯವರೊಡನೆಯೂ ಪ್ರಮಾಣಪೂರ್ವಕವಾದ ಈ ಒಡಂಬಡಿಕೆಯನ್ನು ಮಾಡಿಕೊಳ್ಳಬೇಕೆಂದಿದ್ದಾರೆ.",
			"16": "<sup>16</sup> ನಾವು ಈಜಿಪ್ಟ್ ದೇಶದಲ್ಲಿ ವಾಸಮಾಡಿದ್ದು, ಅನೇಕ ಜನಾಂಗಗಳ ಪ್ರದೇಶಗಳನ್ನು ದಾಟಿಬಂದದ್ದು ನಿಮಗೆ ತಿಳಿದೇ ಇದೆ.",
			"17": "<sup>17</sup> ಆ ಜನಾಂಗಗಳಲ್ಲಿ ನಡೆಯುವ ಹೇಯಾಚಾರಗಳನ್ನೂ ಅವರು ಮರ, ಕಲ್ಲು, ಬೆಳ್ಳಿ, ಬಂಗಾರಗಳಿಂದ ಮಾಡಿಕೊಂಡು ಪೂಜಿಸುವ ಬೊಂಬೆಗಳನ್ನೂ ನೀವು ಖಂಡಿತವಾಗಿ ನೋಡಿದ್ದೀರಿ.",
			"18": "<sup>18</sup> “ಎಚ್ಚರಿಕೆಯಿಂದಿರಿ! ನಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನನ್ನು ಬಿಟ್ಟು, ಆ ಜನಾಂಗಗಳ ದೇವರುಗಳನ್ನು ಅವಲಂಬಿಸುವ ಯಾವ ಕುಲವಾಗಲಿ, ಕುಟುಂಬವಾಗಲಿ, ಸ್ತ್ರೀಪುರುಷರಾಗಲಿ ನಿಮ್ಮಲ್ಲಿ ಇರಲೇಬಾರದು; ನಿಮ್ಮಲ್ಲಿ ಯಾವ ವಿಷಲತೆಯ ಬೇರೂ ಇರಬಾರದು.",
			"19": "<sup>19</sup> ಶಾಪದಿಂದ ಕೂಡಿರುವ ಈ ಪ್ರತಿಜ್ಞೆಯನ್ನು ಕೇಳಿದ ಯಾವನಾದರು ತನ್ನೊಳಗೆ, ‘ನಾನು ಹಟಹಿಡಿದು ಅವಿಧೇಯನಾದರೂ ನನಗೆ ಕ್ಷೇ‍ಮವಾಗಿಯೇ ಇರುವುದು’ ಎಂದುಕೊಂಡು ಹಿಗ್ಗಬಾರದು. ಹಾಗೆ ಮಾಡುವವನು ದೋಷಿಗಳಿಗೂ ನಿರ್ದೋಷಿಗಳಿಗೂ ನಾಶವನ್ನುಂಟು ಮಾಡುವನು.",
			"20": "<sup>20</sup> ಸರ್ವೇಶ್ವರ ಅಂಥವನನ್ನು ಎಂದಿಗೂ ಕ್ಷಮಿಸರು; ಬದಲಿಗೆ ಅವನ ಮೇಲೆ ಸಿಟ್ಟುಗೊಂಡು, ತಮ್ಮ ಗೌರವವನ್ನು ಕಾಪಾಡಿಕೊಳ್ಳುವವರಾಗಿ, ಈ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುವ ಎಲ್ಲಾ ಶಾಪೋಕ್ತಿಗಳಿಗೂ ಅವನನ್ನು ಗುರಿಪಡಿಸುವರು. ಅವನ ಹೆಸರನ್ನು ಭೂಮಿಯ ಮೇಲೆ ಇಲ್ಲದಂತೆ ಮಾಡುವರು.",
			"21": "<sup>21</sup> ಈ ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿ ಬರೆದಿರುವ ನಿಬಂಧನೆಯನ್ನು ಮೀರುವವರಿಗೆ ಉಂಟಾಗುವ ಶಾಪಗಳು ಅವನಲ್ಲಿ ನೆರವೇರಲೆಂದು ಸರ್ವೇಶ್ವರ ಅವನನ್ನು ಇಸ್ರಯೇಲರ ಎಲ್ಲ ಕುಲಗಳಿಂದ ಪ್ರತ್ಯೇಕಿಸಿ, ದಂಡಿಸುವರು.",
			"22": "<sup>22</sup> “ಮುಂದೆ ಹುಟ್ಟುವ ನಿಮ್ಮ ಸಂತತಿಯವರೂ ದೇಶಾಂತರಗಳಿಂದ ಬರುವ ಅನ್ಯರೂ ನಿಮ್ಮ ನಾಡಿಗೆ ಸರ್ವೇಶ್ವರ ಉಂಟುಮಾಡಿದ ರೋಗೋಪದ್ರವಗಳನ್ನು ಕಂಡು ಆಶ್ಚರ್ಯಪಡುವರು.",
			"23": "<sup>23</sup> ಎಲ್ಲ ಜನಾಂಗಗಳವರೂ ನಿಮ್ಮ ಆ ನಾಡು ಯಾವ ವ್ಯವಸಾಯವೂ ಇಲ್ಲದೆ, ಹುಲ್ಲಾದರೂ ಬೆಳೆಯದೆ, ಹಾಳುಬಿದ್ದಿರುವುದನ್ನು ನೋಡುವರು. ಸರ್ವೇಶ್ವರ ಮಹಾಕೋಪದಿಂದ ಕೆಡವಿದ ಸೊದೋಮ್, ಗೊಮೋರ, ಅದ್ಮಾ, ಚೆಬೋಯಿಮ್ ಎಂಬ ಪಟ್ಟಣಗಳ ಪ್ರದೇಶದಂತೆ ಸುಟ್ಟುಹೋಗಿರುವುದನ್ನೂ ಎಲ್ಲ ಕಡೆಗಳಲ್ಲೂ ಗಂಧಕ ಉಪ್ಪುಗಳಿಂದ ತುಂಬಿರುವುದನ್ನೂ ನೋಡುವರು.",
			"24": "<sup>24</sup> ‘ಸರ್ವೇಶ್ವರ ಈ ನಾಡಿಗೆ ಹೀಗೆ ಮಾಡಿದ್ದೇಕೆ? ಇಂಥ ಕೋಪಾಗ್ನಿಗೆ ಕಾರಣ ಏನಿರಬಹುದು?’ ಎಂದು ವಿಚಾರಿಸುವರು.",
			"25": "<sup>25</sup> ಅದಕ್ಕೆ ಜನರು, ‘ಈ ನಾಡಿನವರ ಪಿತೃಗಳ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಅವರನ್ನು ಈಜಿಪ್ಟ್ ದೇಶದಿಂದ ಬಿಡಿಸಿ ಅವರೊಡನೆ ಮಾಡಿದ ಒಡಂಬಡಿಕೆಯನ್ನು ಅವರು ಉಲ್ಲಂಘಿಸಿದರು.",
			"26": "<sup>26</sup> ತಮಗೆ ನೇಮಕವಾಗದಿದ್ದ ಹಾಗು ಗೊತ್ತಿಲ್ಲದಿದ್ದ ಇತರ ದೇವರುಗಳನ್ನು ಅವಲಂಬಿಸಿ ಪೂಜಿಸಿದರು.",
			"27": "<sup>27</sup> ಆದುದರಿಂದ ಸರ್ವೇಶ್ವರ ಅವರ ನಾಡಿನ ಮೇಲೆ ಕೋಪಗೊಂಡು ಈ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುವ ಶಾಪಗಳನ್ನು ಅವರ ಮೇಲೆ ಬರಮಾಡಿದ್ದಾರೆ;",
			"28": "<sup>28</sup> ಮಹಾಕೋಪೋದ್ರೇಕದಿಂದ ಆ ಜನರನ್ನು ನಾಡಿನಿಂದ ಕಿತ್ತುಹಾಕಿ ಈಗ ಅನುಭವಕ್ಕೆ ಬಂದಿರುವಂತೆ ದೇಶಾಂತರ ಅಟ್ಟಿಬಿಟ್ಟಿ ಇದ್ದಾರೆ’ ಎಂದು ಉತ್ತರಕೊಡುವರು.",
			"29": "<sup>29</sup> “ರಹಸ್ಯವಾದವುಗಳು ನಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ಸೇರಿದವು. ಆದರೆ ಅವರಿಂದ ಪ್ರಕಟವಾದವುಗಳು ಸದಾ ನಮ್ಮ ಮತ್ತು ನಮ್ಮ ಸಂತತಿಯವರ ಪಾಲಾಗಿರುವುವು; ಆದುದರಿಂದ ನಾವು ಈ ಧರ್ಮಶಾಸ್ತ್ರನಿಯಮಗಳನ್ನು ಅನುಸರಿಸಿ ನಡೆಯಬೇಕಾದ ಹಂಗಿನಲ್ಲಿದ್ದೇವೆ."
		},
		"30": {
			"1": "<sup>1</sup> “ನಾನು ನಿಮ್ಮ ಮುಂದೆ ಇಟ್ಟಿರುವ ಆಶೀರ್ವಾದ ಹಾಗು ಶಾಪ ನಿಮ್ಮ ಅನುಭವಕ್ಕೆ ಬರುವುವು; ಆಗ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮನ್ನು ಚದುರಿಸಿರುವ ದೇಶಗಳಲ್ಲಿ ನೀವು ಇವುಗಳನ್ನು ನೆನಪಿಗೆ ತಂದುಕೊಳ್ಳಬೇಕು.",
			"2": "<sup>2</sup> ನಿಮ್ಮ ಮಕ್ಕಳು ಸಹಿತವಾಗಿ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಆಶ್ರಯಕ್ಕೆ ಮರಳಿಬಂದು ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ಅವರ ಮಾತುಗಳಿಗೆ ಪೂರ್ಣ ಹೃದಯದಿಂದಲೂ ಮನಸ್ಸಿನಿಂದಲೂ ವಿಧೇಯರಾಗಬೇಕು;",
			"3": "<sup>3</sup> ಆಗ, ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ದುರವಸ್ಥೆಯಿಂದ ನಿಮ್ಮನ್ನು ತಪ್ಪಿಸಿ, ನಿಮ್ಮನ್ನು ಕನಿಕರಿಸಿ, ನಿಮ್ಮನ್ನು ಚದುರಿಸಿದ ಎಲ್ಲ ದೇಶಗಳಿಂದ ಮತ್ತೆ ಕೂಡಿಸುವರು.",
			"4": "<sup>4</sup> ನಿಮ್ಮಲ್ಲಿ ಯಾರಾದರು ಭೂಲೋಕದ ಕಟ್ಟಕಡೆಯವರೆಗೆ ಒಯ್ಯಲ್ಪಟ್ಟಿದ್ದರೂ ಅಲ್ಲಿಂದ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಅವರನ್ನು ಕರೆದುತರುವರು.",
			"5": "<sup>5</sup> ನಿಮ್ಮ ಪಿತೃಗಳಿಗೆ ಸ್ವಂತವಾಗಿದ್ದ ನಾಡಿನಲ್ಲಿ ಸೇರಿಸುವರು. ಅದು ನಿಮಗೂ ಸ್ವದೇಶವಾಗುವಂತೆ ಮಾಡಿ ನಿಮಗೆ ಒಳಿತನ್ನುಂಟುಮಾಡುವರು; ಪಿತೃಗಳಿಗಿಂತಲೂ ಅಧಿಕವಾಗಿ ನಿಮ್ಮನ್ನು ಹೆಚ್ಚಿಸುವರು.",
			"6": "<sup>6</sup> ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮ ಮತ್ತು ನಿಮ್ಮ ಸಂತತಿಯವರ ಅಂತರಂಗಕ್ಕೇ ಸುನ್ನತಿ ಮಾಡುವರು. ಆಗ ನೀವು ಅವರನ್ನು ಸಂಪೂರ್ಣ ಹೃದಯದಿಂದಲೂ ಮನಸ್ಸಿನಿಂದಲೂ ಪ್ರೀತಿಸಿ ಬದುಕಿಬಾಳುವಿರಿ.",
			"7": "<sup>7</sup> ನಿಮ್ಮ ದೇವರಾದಸರ್ವೇಶ್ವರ ಆ ಶಾಪಗಳನ್ನೆಲ್ಲಾ ನಿಮ್ಮನ್ನು ದ್ವೇಷಿಸಿ, ಹಿಂಸಿಸಿದ ನಿಮ್ಮ ಶತ್ರುಗಳ ಮೇಲೆಯೇ ಬರಮಾಡುವರು.",
			"8": "<sup>8</sup> ನೀವಾದರೋ ಸರ್ವೇಶ್ವರನ ಮಾತಿಗೆ ಪುನಃ ವಿಧೇಯರಾಗಿ, ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವ ಅವರ ಆಜ್ಞೆಗಳಂತೆ ನಡೆಯುವಿರಿ;",
			"9": "<sup>9</sup> ಮತ್ತು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮ ಸಂತಾನ, ಪಶು ಹಾಗು ವ್ಯವಸಾಯಗಳನ್ನು ಹೆಚ್ಚಿಸಿ ನಿಮ್ಮ ಎಲ್ಲಾ ಪ್ರಯತ್ನಗಳನ್ನು ಸಫಲಪಡಿಸಿ, ನಿಮಗೆ ಒಳಿತನ್ನುಂಟುಮಾಡುವರು. ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ನುಡಿಗೆ ವಿಧೇಯರಾಗಿ ಈ ಧರ್ಮಶಾಸ್ತ್ರದ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ಅನುಸರಿಸಿ ಪೂರ್ಣಹೃದಯದಿಂದಲೂ ಮನಸ್ಸಿನಿಂದಲೂ ಅವರ ಕಡೆಗೆ ತಿರುಗಿಕೊಳ್ಳುವಿರಿ. ಅವರು ನಿಮ್ಮ ಪಿತೃಗಳ ವಿಷಯದಲ್ಲಿ ಸಂತೋಷಪಟ್ಟ ಹಾಗೆಯೇ ನಿಮ್ಮ ವಿಷಯದಲ್ಲೂ ಮತ್ತೆ ಸಂತೋಷಪಟ್ಟು ನಿಮಗೆ ಒಳಿತನ್ನುಂಟುಮಾಡುವರು.",
			"11": "<sup>11</sup> “ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸಿದ ಧರ್ಮೋಪದೇಶ ನಿಮಗೆ ಗ್ರಹಿಸುವುದಕ್ಕೆ ಕಷ್ಟ ಆದುದಲ್ಲ, ಸಾಧಿಸುವುದಕ್ಕೆ ಅಸಾಧ್ಯವಾದದ್ದೂ ಅಲ್ಲ.",
			"12": "<sup>12</sup> ಇದು ಮೇಲಣ ಲೋಕದಲ್ಲಿರುವ ಮಾತಲ್ಲ, ‘ನಮಗಾಗಿ ಮೇಲಣ ಲೋಕವನ್ನು ಏರಿ ಅದನ್ನು ತಿಳಿದುಕೊಂಡು ಬಂದು ನಮಗೆ ತಿಳಿಸುವಷ್ಟು ಶಕ್ತರು ಯಾರಿದ್ದಾರೆ? ಇದ್ದಿದ್ದರೆ ನಾವು ಕೈಗೊಳ್ಳುತ್ತಿದ್ದೆವು’ ಎಂದುಕೊಳ್ಳುವುದಕ್ಕೆ ಅವಕಾಶವೇನೂ ಇಲ್ಲ.",
			"13": "<sup>13</sup> ಇದು ಸಮುದ್ರದ ಆಚೆ ಇರುವ ಮಾತಲ್ಲ; ‘ನಮಗಾಗಿ ಸಮುದ್ರವನ್ನು ದಾಟಿ ಅದನ್ನು ತೆಗೆದುಕೊಂಡು ಬಂದು ನಮಗೆ ತಿಳಿಸುವಷ್ಟು ಶಕ್ತರು ಯಾರಿದ್ದಾರೆ? ಇದ್ದಿದ್ದರೆ ನಾವು ಕೈಗೊಳ್ಳುತ್ತಿದ್ದೆವು’ ಎಂದುಕೊಳ್ಳುವುದಕ್ಕೆ ಆಸ್ಪದವೇನೂ ಇಲ್ಲ.",
			"14": "<sup>14</sup> ಅನುಸರಿಸುವುದಕ್ಕೆ ಸಾಧ್ಯವಾಗುವಂತೆ ಈ ವಾಕ್ಯ ನಿಮಗೆ ಸಮೀಪದಲ್ಲಿ ಇದೆ; ಇದು ನಿಮ್ಮ ಬಾಯಲ್ಲೇ ಇದೆ; ಹೃದಯದಲ್ಲೇ ಇದೆ.",
			"15": "<sup>15</sup> “ಇಗೋ ನೋಡಿ: ನಾನು ಶುಭವನ್ನೂ ಅಶುಭವನ್ನೂ ಜೀವವನ್ನೂ ಸಾವನ್ನೂ ಈಗ ನಿಮ್ಮ ಮುಂದೆ ಇಟ್ಟಿದ್ದೇನೆ.",
			"16": "<sup>16</sup> ನಾನು ಈಗ ನಿಮಗೆ ಬೋಧಿಸುವಂತೆ ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನನ್ನು ಪ್ರೀತಿಸಿ, ಅವರು ಹೇಳಿದ ಮಾರ್ಗದಲ್ಲಿ ನಡೆದು, ಅವರ ಆಜ್ಞಾವಿಧಿನಿರ್ಣಯಗಳನ್ನು ಅನುಸರಿಸಿದರೆ ನೀವು ಬದುಕಿಬಾಳುವಿರಿ, ಅಭಿವೃದ್ಧಿಯಾಗುವಿರಿ, ಮತ್ತು ನೀವು ಸ್ವಾಧೀನ ಮಾಡಿಕೊಳ್ಳಲು ಹೋಗುವ ನಾಡಿನಲ್ಲಿ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮನ್ನು ಆಶೀರ್ವದಿಸುವರು.",
			"17": "<sup>17</sup> ಆದರೆ ನೀವು ಅವರನ್ನು ಬಿಟ್ಟು, ಅವರ ಮಾತನ್ನು ಕೇಳಲೊಲ್ಲದೆ, ಮೋಸಹೋಗಿ,",
			"18": "<sup>18</sup> ಇತರ ದೇವರುಗಳನ್ನು ಪೂಜಿಸಿ ಅವಲಂಭಿಸಿದರೆ ನೀವು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳಲು ಜೋರ್ಡನ್ ನದಿಯನ್ನು ದಾಟಿಹೋಗುವ ನಾಡಿನಲ್ಲಿ ಬಹುಕಾಲ ಇರದೆ ನಾಶ ಆಗಿ ಹೋಗುವಿರಿ. ಇದನ್ನು ನಾನು ಈಗ ನಿಮಗೆ ಖಂಡಿತವಾಗಿ ತಿಳಿಸುತ್ತೇನೆ.",
			"19": "<sup>19</sup> ನಾನು ಸಾವುಜೀವಗಳನ್ನೂ ಶಾಪಾಶೀರ್ವಾದಗಳನ್ನೂ ಈಗ ನಿಮ್ಮ ಮುಂದೆ ಇಟ್ಟಿದ್ದೇನೆ; ಇದಕ್ಕೆ ಭೂಮಿ ಆಕಾಶಗಳೇ ಸಾಕ್ಷಿಗಳಾಗಿರಲಿ; ಆದುದರಿಂದ ನೀವೂ ನಿಮ್ಮ ಸಂತತಿಯವರೂ ಬದುಕಿಬಾಳುವಂತೆ ಜೀವವನ್ನೇ ಆರಿಸಿಕೊಳ್ಳಿ;",
			"20": "<sup>20</sup> ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನನ್ನು ಪ್ರೀತಿಸಿ ಅವರ ಮಾತಿಗೆ ವಿಧೇಯರಾಗಿರಿ; ಅವರನ್ನು ಹೊಂದಿಕೊಂಡೇ ಇರಿ. ಸರ್ವೇಶ್ವರ ನಿಮ್ಮ ಪಿತೃಗಳಾದ ಅಬ್ರಹಾಮ್, ಇಸಾಕ್, ಯಕೋಬರಿಗೆ ಪ್ರಮಾಣಮಾಡಿ ಕೊಟ್ಟ ನಾಡಿನಲ್ಲಿ ನೀವು ಬದುಕಿ ಬಾಳುವುದಕ್ಕೂ ಬಹುಕಾಲ ಇರುವುದಕ್ಕೂ ಅವರೇ ಆಧಾರ.”"
		},
		"31": {
			"1": "<sup>1</sup> ಮೋಶೆ ಹೋಗಿ ಇಸ್ರಯೇಲರೆಲ್ಲರಿಗೆ ಈ ಮಾತುಗಳನ್ನು ತಿಳಿಸಿದನು:",
			"2": "<sup>2</sup> “ನಾನು ಈಗ ನೂರಿಪ್ಪತ್ತು ವರ್ಷದವನಾಗಿದ್ದೇನೆ; ವಿಶೇಷವಾದ ಕಾರ್ಯಗಳನ್ನು ನಿರ್ವಹಿಸುವುದು ಇನ್ನು ನನ್ನಿಂದಾಗದು; ಸರ್ವೇಶ್ವರ ನನಗೆ, ‘ನೀನು ಈ ಜೋರ್ಡನ್ ನದಿಯನ್ನು ದಾಟಕೂಡದು’ ಎಂದು ಆಜ್ಞಾಪಿಸಿದ್ದಾರೆ.",
			"3": "<sup>3</sup> ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ತಾವೇ ನಿಮ್ಮ ಸೇನಾ ನಾಯಕರಾಗಿ ನದಿಯನ್ನು ದಾಟಿಹೋಗುವರು; ಅವರು ಆ ಜನಾಂಗಗಳನ್ನು ನಿಮ್ಮ ಎದುರಿನಲ್ಲಿ ನಿಲ್ಲದಂತೆ ನಾಶಮಾಡುವರು. ಆಗ ನೀವು ಅವರ ನಾಡನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವಿರಿ. ಸರ್ವೇಶ್ವರ ಆಜ್ಞಾಪಿಸಿದಂತೆ ಯೆಹೋಶುವನು ಕೂಡ ನಿಮ್ಮ ನಾಯಕನಾಗಿ ನದಿಯನ್ನು ದಾಟಿಹೋಗುವನು.",
			"4": "<sup>4</sup> ಸರ್ವೇಶ್ವರ, ಅಮೋರಿಯರ ಅರಸರಾದ ಸೀಹೋನ್ ಹಾಗು ಓಗರನ್ನು ನಾಶಮಾಡಿ ಅವರ ದೇಶವನ್ನು ನಿಮಗೆ ಸ್ವಾಧೀನಪಡಿಸಿದಂತೆ ಆ ಜನಾಂಗಗಳನ್ನೂ ನಾಶಮಾಡಿ ಅವರ ದೇಶವನ್ನು ನಿಮಗೆ ವಶಪಡಿಸುವರು.",
			"5": "<sup>5</sup> ಸರ್ವೇಶ್ವರ ಅವರನ್ನು ನಿಮ್ಮಿಂದ ಪರಾಜಯಪಡಿಸಿದಾಗ ನೀವು ಅವರ ವಿಷಯದಲ್ಲಿ ನನ್ನ ಆಜ್ಞೆಗಳನ್ನೆಲ್ಲಾ ಅನುಸರಿಸಿ ನಡೆದುಕೊಳ್ಳಬೇಕು.",
			"6": "<sup>6</sup> ನೀವು ಶೂರರಾಗಿ ಧೈರ್ಯದಿಂದಿರಿ; ಅವರಿಗೆ ಅಂಜಬೇಡಿ, ಕಳವಳಪಡಬೇಡಿ. ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನೇ ನಿಮ್ಮ ಸಂಗಡ ಇರುತ್ತಾರೆ; ಅವರು ನಿಮ್ಮನ್ನು ಕೈಬಿಡುವುದಿಲ್ಲ, ಅಪಜಯಕ್ಕೆ ಗುರಿಪಡಿಸುವುದೇ ಇಲ್ಲ,” ಎಂದು ಹೇಳಿದನು.",
			"7": "<sup>7</sup> ಅನಂತರ ಮೋಶೆ ಯೆಹೋಶುವನನ್ನು ಕರೆದು ಇಸ್ರಯೇಲರೆಲ್ಲರ ಮುಂದೆ, “ಸರ್ವೇಶ್ವರ ಇವರ ಪಿತೃಗಳಿಗೆ ಪ್ರಮಾಣಮಾಡಿಕೊಟ್ಟ ನಾಡಿಗೆ ನೀನೇ ಇವರೊಡನೆ ಹೋಗಿ ಅದನ್ನು ಅವರಿಗೆ ಸ್ವಾಧೀನಪಡಿಸಬೇಕು. ಆದುದರಿಂದ ಶೂರನಾಗಿರು, ಧೈರ್ಯದಿಂದಿರು;",
			"8": "<sup>8</sup> ಸರ್ವೇಶ್ವರ ತಾವೇ ನಿನ್ನ ಮುಂದುಗಡೆ ಹೋಗುವರು; ಅವರೇ ನಿನ್ನ ಸಂಗಡ ಇರುವರು; ನಿನ್ನನ್ನು ಅಪಜಯಕ್ಕೆ ಗುರಿಪಡಿಸುವುದೇ ಇಲ್ಲ, ಕೈಬಿಡುವುದಿಲ್ಲ. ಅಂಜಬೇಡ; ಧೈರ್ಯದಿಂದಿರು,” ಎಂದು ಹೇಳಿದನು.",
			"9": "<sup>9</sup> ಮೋಶೆ ಈ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಬರೆದು ಸರ್ವೇಶ್ವರನ ಆಜ್ಞಾಶಾಸನಗಳ ಮಂಜೂಷವನ್ನು ಹೊರುವ ಲೇವಿಯರಾದ ಯಾಜಕರ ಮತ್ತು ಹಿರಿಯರ ವಶಕ್ಕೆ ಕೊಟ್ಟನು.",
			"10": "<sup>10</sup> ಅವರಿಗೆ, “ಪ್ರತಿ ಏಳು ವರ್ಷಗಳು ಪೂರ್ತಿಯಾದಾಗ, ಬಿಡುಗಡೆಯ ಸಂವತ್ಸರದ ನೇಮಕ ಕಾಲದಲ್ಲಿ ಅಂದರೆ, ಪರ್ಣಕುಟೀರ ಜಾತ್ರೆಯಲ್ಲಿ",
			"11": "<sup>11</sup> ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಆರಿಸಿಕೊಂಡ ಸ್ಥಳದಲ್ಲಿ, ಇಸ್ರಯೇಲರೆಲ್ಲರು ಅವರ ಸನ್ನಿಧಿಗೆ ಕೂಡಿಬಂದಾಗ ಅವರೆಲ್ಲರಿಗೂ ಕೇಳಿಸುವಂತೆ ನೀವು ಈ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಅವರ ಮುಂದೆ ಓದಬೇಕು.",
			"12": "<sup>12</sup> ಜನರೆಲ್ಲರು ಈ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಕೇಳಿ ತಿಳಿದುಕೊಳ್ಳುವಂತೆ ಸ್ತ್ರೀ ಪುರುಷರನ್ನೂ ಮಕ್ಕಳನ್ನೂ ನಿಮ್ಮ ಊರುಗಳಲ್ಲಿರುವ ಅನ್ಯರನ್ನೂ ಕೂಡಿಸಬೇಕು. ಆಗ ಅವರು ತಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗುವರು. ಈ ಧರ್ಮಶಾಸ್ತ್ರದ ನಿಯಮಗಳನ್ನು ಅನುಸರಿಸಿ ನಡೆಯುವರು.",
			"13": "<sup>13</sup> ಸರ್ವೇಶ್ವರನ ಮಹತ್ಕಾರ್ಯಗಳನ್ನು ನೋಡದಿರುವ ನಿಮ್ಮ ಸಂತತಿಯವರು ಸಹ ಇವನ್ನು ಕೇಳಿ, ನೀವು ಜೋರ್ಡನ್ ನದಿಯನ್ನು ದಾಟಿ ಸ್ವಾಧೀನ ಮಾಡಿಕೊಳ್ಳಲು ಹೋಗುವ ನಾಡಿನಲ್ಲಿ ವಾಸವಾಗಿರುವವರೆಗೂ, ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿರಲು ಕಲಿತುಕೊಳ್ಳುವರು,” ಎಂದು ಹೇಳಿದನು.",
			"14": "<sup>14</sup> ಸರ್ವೇಶ್ವರ ಸ್ವಾಮಿ, ಮೋಶೆಗೆ, “ನಿನ್ನ ಮರಣದ ಕಾಲ ಸಮೀಪವಾಯಿತು; ಆದಕಾರಣ ನೀನು ಯೆಹೋಶುವನನ್ನು ಕರೆದುಕೊಂಡು ಬಂದು, ದೇವದರ್ಶನದ ಗುಡಾರದೊಳಗೆ ನನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನಿಲ್ಲಬೇಕು; ಆಗ ನಾನು ಅವನಿಗೆ ಅಧಿಕಾರವನ್ನು ಕೊಡುವೆನು,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದರು. ಮೋಶೆಯೂ ಯೆಹೋಶುವನೂ ದೇವದರ್ಶನದ ಗುಡಾರದೊಳಗೆ ಸರ್ವೇಶ್ವರನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಬಂದು ನಿಂತುಕೊಂಡರು.",
			"15": "<sup>15</sup> ಅಲ್ಲಿ ಸರ್ವೇಶ್ವರ ಮೇಘಸ್ತಂಭದಲ್ಲಿ ಕಾಣಿಸಿದರು; ಆ ಮೇಘಸ್ತಂಭವು ಗುಡಾರದ ಬಾಗಿಲಿನ ಬಳಿಯಲ್ಲಿ ನಿಂತಿತು.",
			"16": "<sup>16</sup> ಸರ್ವೇಶ್ವರ ಮೋಶೆಗೆ, “ನೀನು ಮೃತನಾಗಿ ಪಿತೃಗಳಲ್ಲಿಗೆ ಸೇರಿದ ಮೇಲೆ ಈ ಜನರು ನನ್ನನ್ನು ಬಿಟ್ಟು, ನಾನು ಅವರೊಡನೆ ಮಾಡಿಕೊಂಡ ಒಡಂಬಡಿಕೆಯನ್ನು ಮೀರಿ, ದೇವದ್ರೋಹಿಗಳಾಗಿ ತಾವು ಹೋಗುವ ನಾಡಿನಲ್ಲಿರುವ ಅನ್ಯದೇವತೆಗಳನ್ನು ಪೂಜಿಸುವರು.",
			"17": "<sup>17</sup> ಆಗ ನಾನು ಅವರ ಮೇಲೆ ಬಹಳ ಸಿಟ್ಟುಗೊಂಡು, ಅವರನ್ನು ಬಿಟ್ಟು, ಅವರಿಗೆ ವಿಮುಖನಾಗುವೆನು. ಆದುದರಿಂದ ಅವರು ಸಂಹಾರಕ್ಕೆ ಗುರಿಯಾಗುವರು; ಅನೇಕ ಕಷ್ಟಗಳೂ ವಿಪತ್ತುಗಳೂ ಅವರಿಗೆ ಒದಗುವುವು. ಆಗ ಅವರು, ‘ನಮ್ಮ ದೇವರು, ನಮ್ಮ ಮಧ್ಯೆ ಇಲ್ಲದೆ ಹೋದುದರಿಂದಲೇ ಈ ವಿಪತ್ತುಗಳೆಲ್ಲಾ ನಮಗೆ ಸಂಭವಿಸಿವೆ,’ ಎಂದುಕೊಳ್ಳುವರು.",
			"18": "<sup>18</sup> ಅವರು ನನ್ನನ್ನು ಬಿಟ್ಟು, ಇತರ ದೇವರುಗಳನ್ನು ಆಶ್ರಯಿಸಿ, ಬಹಳ ದುಷ್ಕೃತ್ಯಗಳನ್ನು ನಡೆಸುವುದರಿಂದ ನಾನು ಆ ಕಾಲದಲ್ಲಿ ಖಂಡಿತವಾಗಿ ಅವರಿಗೆ ವಿಮುಖನಾಗುವೆನು.",
			"19": "<sup>19</sup> “ಆದಕಾರಣ ನೀನು ಈ ಗೀತೆಯನ್ನು ಬರೆದು ಇಸ್ರಯೇಲರಿಗೆ ಕಲಿಸಿಕೊಡು; ಈ ಗೀತೆ ಇಸ್ರಯೇಲರಿಗೆ ವಿರೋಧವಾದ, ನನಗೆ ಪರವಾದ ಸಾಕ್ಷಿಯಾಗಿರುವಂತೆ ಇದನ್ನು ಅವರಿಗೆ ಬಾಯಿಪಾಠ ಮಾಡಿಸು.",
			"20": "<sup>20</sup> ನಾನು ಪ್ರಮಾಣಮಾಡಿ ಕೊಟ್ಟ ನಾಡಿಗೆ ಇವರು ಸೇರುವುದಕ್ಕಿಂತ ಮುಂಚಿತವಾಗಿಯೇ ಇವರು ಈಗಲೂ ಮಾಡುವ ದುರಾಲೋಚನೆಗಳನ್ನು ನಾನು ಬಲ್ಲೆ. ನಾನು ಇವರ ಪಿತೃಗಳಿಗೆ ಪ್ರಮಾಣಮಾಡಿದಂತೆ ಹಾಲೂ ಜೇನೂ ಹರಿಯುವ ನಾಡಿಗೆ ಇವರನ್ನು ಸೇರಿಸಿದ ಮೇಲೆ ಇವರು ಚೆನ್ನಾಗಿ ಉಂಡು ಕೊಬ್ಬಿದವರಾದಾಗ, ಇತರ ದೇವರುಗಳನ್ನು ಅವಲಂಬಿಸಿ, ಪೂಜಿಸಿ, ನನ್ನನ್ನು ತಾತ್ಸಾರಮಾಡಿ, ನನ್ನ ನಿಬಂಧನೆಯನ್ನು ಮೀರುವರು. ಅನಂತರ ಅನೇಕ ಕಷ್ಟಗಳೂ ವಿಪತ್ತುಗಳೂ ಸಂಭವಿಸಿದಾಗ ಈ ಗೀತೆ ಇವರ ಸಂತತಿಯವರ ಬಾಯಲ್ಲಿದ್ದು ಅವರ ಮುಂದೆ ಸಾಕ್ಷಿಕೊಡುವುದು,” ಎಂದು ಹೇಳಿದರು.",
			"22": "<sup>22</sup> ಆದಕಾರಣ ಮೋಶೆ ಆ ದಿನದಲ್ಲೇ ಈ ಗೀತೆಯನ್ನು ಬರೆದು ಇಸ್ರಯೇಲರಿಗೆ ಕಲಿಸಿದನು.",
			"23": "<sup>23</sup> ಆಗ ಸರ್ವೇಶ್ವರಸ್ವಾಮಿ ನೂನನ ಮಗ ಯೆಹೋಶುವನಿಗೆ, “ನಾನು ಇಸ್ರಯೇಲರಿಗೆ ಪ್ರಮಾಣಮಾಡಿಕೊಟ್ಟ ನಾಡಿಗೆ ನೀನೇ ಅವರನ್ನು ಸೇರಿಸಬೇಕು; ಆದುದರಿಂದ ಶೂರನಾಗಿರು, ಧೈರ್ಯದಿಂದಿರು; ನಾನೇ ನಿನ್ನೊಂದಿಗೆ ಇರುವೆನು,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದರು.",
			"24": "<sup>24</sup> ಮೋಶೆ ಈ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಗ್ರಂಥರೂಪವಾಗಿ ಬರೆದು ಪೂರೈಸಿದನು; ಬಳಿಕ ಸರ್ವೇಶ್ವರನ ಆಜ್ಞಾಶಾಸನಗಳ ಮಂಜೂಷವನ್ನು ಹೊರುವ ಲೇವಿಯರಿಗೆ,",
			"25": "<sup>25</sup> “ನೀವು ಈ ಧರ್ಮಶಾಸ್ತ್ರ ಪುಸ್ತಕವನ್ನು ನಿಮ್ಮ ವಶಕ್ಕೆ ತೆಗೆದುಕೊಂಡು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಆಜ್ಞಾಶಾಸನಗಳುಳ್ಳ ಮಂಜೂಷದ ಬಳಿಯಲ್ಲೇ ಇಡಿ. ಇಸ್ರಯೇಲರೇ, ಅಲ್ಲಿ ಇದು ನಿಮಗೆ ವಿರೋಧವಾಗಿ ಸಾಕ್ಷಿಕೊಡಲಿ.",
			"27": "<sup>27</sup> ಏಕೆಂದರೆ ನೀವು ಹಟಮಾರಿಗಳು, ಆಜ್ಞೆಗೆ ಒಳಗಾಗದವರೆಂಬುದು ನನಗೆ ತಿಳಿದೇ ಇದೆ. ನಾನು ಇನ್ನೂ ಜೀವದಿಂದಿರುವಾಗಲೇ ನೀವು ಸರ್ವೇಶ್ವರನಿಗೆ ವಿರುದ್ಧ ಪ್ರತಿಭಟಿಸಿದ್ದೀರಿ; ನಾನು ಹೋದ ತರುವಾಯ ನೀವು ಪ್ರತಿಭಟಿಸುವುದು ಮತ್ತಷ್ಟು ನಿಶ್ಚಯ.",
			"28": "<sup>28</sup> ನಿಮ್ಮ ಕುಲಗಳ ಹಿರಿಯರನ್ನೂ ಅಧಿಪತಿಗಳನ್ನೂ ನನ್ನ ಬಳಿಗೆ ಸೇರಿಸಿರಿ. ನಾನು ಈ ಮಾತುಗಳನ್ನು ಅವರಿಗೆ ತಿಳಿಸಿ, ಅವರಿಗೆ ವಿರುದ್ಧ ಸಾಕ್ಷಿಗಳಾಗುವುದಕ್ಕೆ ಭೂಮ್ಯಾಕಾಶಗಳನ್ನೇ ಕರೆಯುವೆನು.",
			"29": "<sup>29</sup> ನಾನು ಹೋದ ಮೇಲೆ, ನೀವು ದ್ರೋಹಿಗಳಾಗಿ, ನಾನು ಬೋಧಿಸಿದ ಮಾರ್ಗವನ್ನು ತಪ್ಪಿಹೋಗುವಿರಿ; ಅನಂತರ ನೀವು ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ಕೆಟ್ಟದ್ದನ್ನು ನಡೆಸಿ, ದುರಾಚಾರಿಗಳಾಗುವಿರಿ; ಅವರನ್ನು ಕೋಪಗೊಳಿಸುವುದರಿಂದ ನಿಮಗೆ ಆಪತ್ತುಗಳು ಬಂದೊದಗುವುದೆಂದು ನಾನು ಬಲ್ಲೆ,” ಎಂದು ಹೇಳಿದನು.",
			"30": "<sup>30</sup> ಆಗ ಮೋಶೆ ಇಸ್ರಯೇಲರ ಸರ್ವಸಮೂಹಕ್ಕೆ ಕೇಳಿಸುವಂತೆ ಈ ಗೀತಾವಚನಗಳನ್ನು ಹೇಳಿದನು:"
		},
		"32": {
			"1": "<sup>1</sup> ಆಕಾಶಮಂಡಲವೇ, ಕೇಳು ನಾ ಹೇಳುವುದನು ಭೂಮಂಡಲವೇ, ಆಲಿಸು ನನ್ನ ಮಾತುಗಳನು.",
			"2": "<sup>2</sup> ನನ್ನ ಉಪದೇಶ ಹಸಿಹುಲ್ಲಿನ ಮೇಲೆ ಮೆಲ್ಲನೆ ಸುರಿವ ತುಂತುರುಗಳಂತೆ ನನ್ನ ಬೋಧೆ ಕಾಯಿಪಲ್ಯಗಳ ಮೇಲೆ ಮಂಜಿನಂತೆ ಬೀಳುವ ಹದಮಳೆಯಂತೆ.",
			"3": "<sup>3</sup> ಪ್ರಕಟಿಸುವೆನು ನಾನು ಸರ್ವೇಶ್ವರನ ನಾಮಮಹತ್ವವನು ಕೊಂಡಾಡಿ ನೀವು ಆ ದೇವನ ಮಹಾಮಹಿಮೆಯನು.",
			"4": "<sup>4</sup> “ನಿಮಗೆ ಪೊರೆಬಂಡೆ ಆತ, ಆತನ ಕಾರ್ಯ ದೋಷರಹಿತ ಆತನ ಮಾರ್ಗ ನ್ಯಾಯಯುತ, ಆ ದೇವ ನಂಬಿಕಸ್ತ. ಆತ ನಿರ್ವಂಚಕ, ಯಥಾರ್ಥನು ಹಾಗು ನೀತಿವಂತ.”",
			"5": "<sup>5</sup> ಅವರಾದರೋ ದ್ರೋಹಿಗಳು, ಮಕ್ಕಳೆನಿಸಿಕೊಳ್ಳಲು ಅಯೋಗ್ಯರು ವಕ್ರಬುದ್ಧಿಯುಳ್ಳವರು, ಮೂರ್ಖಜಾತಿಯವರು.",
			"6": "<sup>6</sup> ದುರಾಚಾರಿಗಳೇ, ಅವಿವೇಕಿಗಳೇ, ಸರ್ವೇಶ್ವರನ ಬಗ್ಗೆ ಹೀಗೆ ವರ್ತಿಸಬಹುದೇ? ಆತ ನಿಮ್ಮನ್ನು ಸೃಷ್ಟಿಸಿದ ತಂದೆಯಲ್ಲವೆ? ನಿಮ್ಮನ್ನೊಂದು ರಾಷ್ಟ್ರವಾಗಿ ಸ್ಥಾಪಿಸಿದವನಲ್ಲವೆ?",
			"7": "<sup>7</sup> ನೆನಪಿಗೆ ತಂದುಕೊಳ್ಳಿ ಆ ಪೂರ್ವಕಾಲವನು ಆಲೋಚಿಸಿ ನೋಡಿ ಪೂರ್ವಿಕರ ಚರಿತೆಯನು. ವಿಚಾರಿಸಿದರೆ ತಿಳಿಸುವರು ನಿಮ್ಮ ನಿಮ್ಮ ತಂದೆಗಳು ಕೇಳಿದರೆ ವಿವರಿಸುವರು ನಿಮ್ಮ ನಿಮ್ಮ ಹಿರಿಯರು.",
			"8": "<sup>8</sup> ಪರಾತ್ಪರ ದೇವರು ಜನಾಂಗಗಳನು ಬೇರೆಬೇರೆಮಾಡಿದಾಗ, ಅವರವರಿಗೆ ಸ್ವದೇಶಗಳನು ವಿಂಗಡಿಸಿಕೊಟ್ಟಾಗ, ದೇವಕುವರರ ಸಂಖ್ಯಾನುಸಾರ ಪ್ರದೇಶಗಳನು ಗೊತ್ತುಮಾಡಿದಾಗ.",
			"9": "<sup>9</sup> ಇಸ್ರಯೇಲರು ಮಾತ್ರ ಸ್ವಜನರಾದರು ಸರ್ವೇಶ್ವರನಿಗೆ, ಸ್ವಕೀಯ ಪ್ರಜೆಯಾದರು ಆ ಯಕೋಬ ವಂಶಜರು ಆತನಿಗೆ.",
			"10": "<sup>10</sup> ಬರಿದಾದ ಭೀಕರ ಮರುಭೂಮಿಯಲಿ ಅವರನು ಕಂಡು ಕಣ್ಣಗುಡ್ಡೆಯಂತೆ ಕಾಪಾಡಿದ ಪ್ರೀತಿಯಿಂದವರನು ಅಪ್ಪಿಕೊಂಡು.",
			"11": "<sup>11</sup> ಗೂಡಿನಿಂದ ಮರಿಗಳನು ಹೊರಡಿಸಿದಾ ಗರುಡಪಕ್ಷಿ ಹಾರಾಡಿ ಅವು ಬೀಳದಂತೆ ಕಾಯುತ್ತದೆ ರೆಕ್ಕೆಗಳನು ಚಾಚಿ. ಅಂತೆಯೆ ಸರ್ವೇಶ್ವರ ಕಾಪಾಡಿದ ಇಸ್ರಯೇಲರನು ಕೈಚಾಚಿ.",
			"12": "<sup>12</sup> ಅವರನು ನಡೆಸಿದವನು ಸರ್ವೇಶ್ವರನೇ, ಅನ್ಯದೇವರಾರೂ ಇಲ್ಲ, ಆತನೊರ್ವನೇ.",
			"13": "<sup>13</sup> ಹತ್ತಿಸಿದವರನು ಎತ್ತರದ ಪ್ರದೇಶಗಳಿಗೆ ಇತ್ತನವರಿಗೆ ಅಲ್ಲಿಯೆ ಬೆಳೆದ ಪೈರುಫಸಲನೆ. ಗಿಟ್ಟಿತವರಿಗೆ ಗುಡ್ಡದ ಜೇನು, ಗಿರಿಯ ಎಣ್ಣೆಯು.",
			"14": "<sup>14</sup> ಆಹಾರಕ್ಕಿತ್ತು, ಆಕಳಿನ ಮೊಸರೋಗರ ಆಡುಕುರಿಗಳ ಕ್ಷೀರ ಬಾಷಾನಿನ ಪುಷ್ಟಪಶು, ಹೋತ, ಕೊಬ್ಬಿದ ಕುರಿಟಗರಿನ ಮಾಂಸ, ಉತ್ತಮವಾದ ಗೋಧೂಮ; ಪಾನಕ್ಕಿತ್ತು ರಕ್ತಗೆಂಪಾದ ದ್ರಾಕ್ಷಾರಸ.",
			"15": "<sup>15</sup> “ತೃಪ್ತಿಯಾಗಿ ತಿಂದನು ಯಕೋಬನು, ಕೊಬ್ಬಿಹೋದನು ಆ ಯೆಶೂರನು; ತೊರೆದುಬಿಟ್ಟನು ತನ್ನ ಪೊರೆಬಂಡೆಯನು ಮರೆತುಬಿಟ್ಟನು ತನ್ನ ಸೃಷ್ಟಿಕರ್ತನನು.",
			"16": "<sup>16</sup> ರೇಗಿಸಿದರಾತನನು ಅನ್ಯದೇವರುಗಳನು ಪೂಜಿಸುತ ಸಿಟ್ಟಿಗೇರಿಸಿದರು ನಿಷಿದ್ಧಾಚಾರಗಳನು ನಡೆಸುತ.",
			"17": "<sup>17</sup> ಬಲಿಯನರ್ಪಿಸಿದರು ನೂತನಾ ದೇವತೆಗಳಿಗೆ, ದೇವರಲ್ಲದಾ ಅಸುರರಿಗೆ, ಪಿತೃಗಳು ಭಜಿಸದಾ, ತಮಗೇ ತಿಳಿಯದಾ ಹೊಸ ದೇವತೆಗಳಿಗೆ.",
			"18": "<sup>18</sup> ಎಲೈ ಇಸ್ರಯೇಲರೇ, ಮರೆತಿರಾ ಜನ್ಮವಿತ್ತ ಪೊರೆಬಂಡೆಯನು ಸ್ಮರಿಸಲಿಲ್ಲ ನೀವು ಹೆತ್ತತಾಯಂತಿದ್ದಾ ದೇವರನು.",
			"19": "<sup>19</sup> ಇದ ಕಂಡು ಸರ್ವೇಶ್ವರ ಬೇಸರಗೊಂಡ ತನ್ನಾ ಕುವರಕುವರಿಯರ ನಡತೆಯ ನೋಡಿ ನೊಂದುಕೊಂಡ.",
			"20": "<sup>20</sup> ಎಂದೇ ಇಂತೆಂದುಕೊಂಡನು; ಇವರಿಗೆ ವಿಮುಖನಾಗುವೆನು, ಇವರಿಗೊದಗಲಿರುವ ಗತಿಯನು ನೋಡುವೆನು. ಸತ್ಯವರಿತೂ ಅನುಸರಿಸದೆ ಹೋದರು ಈ ವಿದ್ರೋಹ ಮಕ್ಕಳಂತವರು.",
			"21": "<sup>21</sup> ‘ರೇಗಿಸಿದರೆನ್ನನು ದೇವರಲ್ಲದವುಗಳ ಮೂಲಕ, ಸಿಟ್ಟೇರಿಸಿದರು ಅಚೇತ ವಿಗ್ರಹಗಳ ಮೂಲಕ. ನಾನವರಲಿ ಅಸೂಯೆ ಹುಟ್ಟಿಸುವೆನು ಜನಾಂಗವಲ್ಲದವರ ಮುಖಾಂತರ, ನಾನವರನು ಸಿಟ್ಟಿಗೆಬ್ಬಿಸುವೆನು ಸಭ್ಯರಲ್ಲದಾ ಜನರ ಮುಖಾಂತರ.",
			"22": "<sup>22</sup> ‘ಉರಿದೇಳುತ್ತಿದೆ ಕೆಳಲೋಕದ ತನಕ ಎನ್ನ ಕೋಪಾಗ್ನಿ ದಹಿಸಿಬಿಡುವುದದು ಭೂಮಿಯನು ಬೆಳೆಸಹಿತವಾಗಿ ಭಸ್ಮಮಾಡುವುದದು ಬೆಟ್ಟಗಳನು ಬುಡಸಮೇತವಾಗಿ.",
			"23": "<sup>23</sup> ‘ಉಪದ್ರವಗಳನು ಬರಮಾಡುವೆನು ಒಂದರ ಮೇಲೊಂದಾಗಿ ಬಿಲ್ಲುಬಾಣಗಳನು ಪ್ರಯೋಗಿಸುವೆನು ಅವರಿಗೆ ವಿರುದ್ಧವಾಗಿ.",
			"24": "<sup>24</sup> ಕ್ಷಯಿಸಿಹೋಗುವರವರು ಕ್ಷಾಮಡಾಮರದಿಂದ ಸಾಯುವರು ಶಾಪದಿಂದ, ಅಂಟುರೋಗಗಳಿಂದ, ನಾ ಕಳುಹಿಸುವ ಕಾಡುಮೃಗ, ವಿಷಸರ್ಪಗಳಿಂದ.",
			"25": "<sup>25</sup> ಹೊರಗೆ ಕತ್ತಿ, ಒಳಗೆ ಭಯಭೀತಿ ಇವರಲ್ಲಿರುವುವು. ಯುವಕ ಯುವತಿಯರು, ಮಕ್ಕಳು ಮುದುಕರು ಕತ್ತಿಕಠಾರಿಗಳಿಗೆ ತುತ್ತಾಗುವರು ಇವರೆಲ್ಲರು.",
			"26": "<sup>26</sup> ನಾಶ ಮಾಡುತ್ತಿದ್ದೆ ನಾನವರನು ಸಂಪೂರ್ಣವಾಗಿ ಅವರಿಲ್ಲದಂತೆ ಮಾಡುತ್ತಿದ್ದೆ ಯಾರೊಬ್ಬರ ನೆನಪಿನಲಿ.",
			"27": "<sup>27</sup> ಆದರೆ ತಪ್ಪು ತಿಳಿಯುತ್ತಿದ್ದರು ಅವರ ವಿರೋಧಿಗಳು; ‘ಇದಾಯಿತು ನಮ್ಮ ಶಕ್ತಿಯಿಂದ, ಸರ್ವೇಶ್ವರನಿಂದಲ್ಲ’ವೆಂದುಕೊಂಡು ಈ ಕಾರಣ ಹಿಂತೆಗೆದುಕೊಂಡೆನು ನನ್ನ ನಿಗದಿನಿರ್ಣಯವನು.",
			"28": "<sup>28</sup> “ಆಲೋಚನೆಯಿಲ್ಲದ ಜನರಿವರು ಸ್ವಲ್ಪವಾದರೂ ವಿವೇಕವಿಲ್ಲದವರು.",
			"29": "<sup>29</sup> ಜ್ಞಾನವಿದ್ದಿದ್ದರೆ ಗ್ರಹಿಸುತ್ತಿದ್ದರು ಈ ಸಂಗತಿಗಳನು ತಿಳಿಯುತ್ತಿದ್ದರು, ಅಂತ್ಯದೊಳು ತಮಗಾಗುವ ದುರವಸ್ಥೆಯನು.",
			"30": "<sup>30</sup> ಒಬ್ಬನಿಂದ ಸಾವಿರ ಮಂದಿ ಸೋತುಹೋದುದು’ ಒಬ್ಬರಿಗಂಜಿ ಹತ್ತುಸಾವಿರ ಓಡಿಹೋದುದು, ಅವರ ಪೊರೆಬಂಡೆಯಾತ ಅವರನು ವೈರಿಗೊಪ್ಪಿಸಿದ್ದರಿಂದಲ್ಲವೆ? ಅವರ ಸರ್ವೇಶ್ವರನು ಅವರನು ಕೈಬಿಟ್ಟುದರಿಂದಲ್ಲವೆ?",
			"31": "<sup>31</sup> ‘ನಮ್ಮಾಶ್ರಯ ದುರ್ಗ ಅವರ ಆಶ್ರಯದುರ್ಗದಂತಲ್ಲ’ ಎಂದು ಅವರ ಶತ್ರುಗಳೇ ಒಪ್ಪಿಕೊಳ್ಳುತ್ತಾರಲ್ಲವೆ?",
			"32": "<sup>32</sup> ಅವರೋ ಸೊದೋಮ್ಯರೆಂಬ ದ್ರಾಕ್ಷಾಲತೆ ಅವರೋ ಗೊಮೋರ್ಯರೆಂಬ ತೋಟದಬೆಳೆ.",
			"33": "<sup>33</sup> ಅವುಗಳ ಹಣ್ಣಿನ ಗೊಂಚಲು ವಿಷ ಅವುಗಳ ರಸ ಮರಣಕರ ಸರ್ಪವಿಷ.",
			"34": "<sup>34</sup> “ಇಸ್ರಯೇಲನನು ಇಟ್ಟಿದ್ದೇನೆ ಮುದ್ರೆಹಾಕಿ ಉಗ್ರಾಣದೊಳು ಇಟ್ಟಿದ್ದೇನೆ ಭದ್ರವಾಗಿ.",
			"35": "<sup>35</sup> ಅವನ ಶತ್ರುಗಳಿಗೆ ಮುಯ್ಯಿತೀರಿಸುವುದು ನನ್ನ ಕೆಲಸ; ಸಮೀಪಿಸಿತವರಿಗೆ ಜಾರಿಬೀಳುವ ಸಮಯ, ವಿಪತ್ಕಾಲ; ಬೇಗಬರುವುದು ಅವರಿಗೆ ಸಿದ್ಧವಾದ ದುರ್ಗತಿಕಾಲ!",
			"36": "<sup>36</sup> ಸ್ವತಂತ್ರರು, ಪರತಂತ್ರರು ನಿರಾಶ್ರಯರಾದಾಗ ಅವರೆಲ್ಲರು ನಿಶ್ಯೇಷರಾದವರು ಎಂದು ತಿಳಿದುಬಂದಾಗ ದೊರಕಿಸುವನು ಸರ್ವೇಶ್ವರನು ನ್ಯಾಯವನು ತನ್ನ ಪ್ರಜೆಗೆ ತೋರುವನು ಅನುಕಂಪವನು ತನ್ನ ಭಕ್ತಾದಿಗಳಿಗೆ.",
			"37": "<sup>37</sup> ಆಗ ನುಡಿವನು ಸ್ವಾಮಿ ಸರ್ವೇಶ್ವರ ಇಂತೆಂದು; ಎಲ್ಲಿಗೆ ಹೋದರು ಅವರು ಪೂಜಿಸುತ್ತಿದ್ದ ದೇವರುಗಳು? ಎಲ್ಲಿರುವನು ಅವರು ಆಶ್ರಯಿಸಿಕೊಂಡಿದ್ದ ದುರ್ಗವಾದವನು.",
			"38": "<sup>38</sup> ಅವರು ಬಲಿಕೊಟ್ಟ ಕೊಬ್ಬು ಮಾಂಸವನು ತಿಂದವರೆಲ್ಲಿ? ಸಮರ್ಪಿಸಿದ ಪಾನಗಳನು ಕುಡಿದ ದೇವತೆಗಳೆಲ್ಲಿ? ಅವರೇ ನಿಮಗೆ ನೆರವಾಗಲಿ, ನಿಮ್ಮನೀಗ ಕಾಯಲಿ!",
			"39": "<sup>39</sup> “ಇರುವಾತನು ನಾನೇ, ನಾನೊಬ್ಬನೇ ಬದುಕಿಸುವವನು, ಕೊಲ್ಲುವವನು ನಾನೇ, ಗಾಯಗೊಳಿಸುವವನು, ವಾಸಿಮಾಡುವವನು ನಾನೇ. ಇಂದಾದರು ತಿಳಿಯಿರಿ: ನನ್ನ ವಿನಾ ದೇವರಿಲ್ಲ ನನ್ನ ಕೈಯಿಂದ ತಪ್ಪಿಸಬಲ್ಲ ಶಕ್ತನಾರೂ ಇಲ್ಲ.",
			"40": "<sup>40</sup> ಆಕಾಶದತ್ತ ಕೈಯೆತ್ತಿ ಪ್ರಮಾಣಮಾಡುವೆ, ಚಿರಂಜೀವಿಯಾದೆನ್ನ ಮೇಲೆ ಆಣೆಯಿಟ್ಟು ಹೇಳುವೆ:",
			"41": "<sup>41</sup> ಥಳಥಳಿಸುವ ಕತ್ತಿಯನು ಹದಮಾಡಿ ನ್ಯಾಯಸ್ಥಾಪಿಸುವೆನು ಮುನ್ನುಗ್ಗಿ. ಮುಯ್ಯಿ ತೀರಿಸುವೆನು ನನ್ನ ಶತ್ರುಗಳಿಗೆ ಪ್ರತಿದಂಡಿಸುವೆನು ನನ್ನ ದ್ವೇಷಿಸಿದವಗೆ.",
			"42": "<sup>42</sup> ಮತ್ತವಾಗುವುವು ನನ್ನ ಬಾಣಗಳು ರಕ್ತಕುಡಿದು; ನನ್ನ ಕತ್ತಿಯು ತಿನ್ನುವುದು ಸತ್ತವರ, ಖೈದಿಗಳ ರಕ್ತಮಾಂಸವನು; ಚೆಂಡಾಡುವುವು ಶತ್ರುವೀರರ ತಲೆಬುರುಡೆಗಳು.",
			"43": "<sup>43</sup> “ಜನಾಂಗಗಳೇ ಕೊಂಡಾಡಿರಿ ದೇವಜನರೊಡನೆ! ತನ್ನ ಭಕ್ತಾದಿಗಳ ರಕ್ತ ಚೆಲ್ಲಿದಾ ಶತ್ರುಗಳಿಗೆ ದಂಡಿಸಿ ಮುಯ್ಯಿತೀರಿಸುವವನು ಸರ್ವೇಶ್ವರನೇ. ತನ್ನ ಜನರಾ ನಾಡಿಗೆ ದೋಷಪರಿಹಾರಮಾಡುವವನು ಆತನೇ.”",
			"44": "<sup>44</sup> ಜನರಿಗೆ ಕೇಳಿಸುವಂತೆ ಹೇಳಿದರು ಈ ಗೀತವಚನಗಳನು ಮೋಶೆಯು ಮತ್ತು ನೂನನ ಮಗನಾದ ಯೆಹೋಶುವನು.",
			"45": "<sup>45</sup> ಮೋಶೆ ಈ ಮಾತುಗಳನ್ನೆಲ್ಲ ಇಸ್ರಯೇಲರಿಗೆ ಹೇಳಿ ಮುಗಿಸಿದನಂತರ ಅವರಿಗೆ,",
			"46": "<sup>46</sup> “ನಾನು ಈಗ ನಿಮಗೆ ಹೇಳಿದ ಎಚ್ಚರಿಕೆಯ ಮಾತುಗಳನ್ನೆಲ್ಲ ಮನಸ್ಸಿನಲ್ಲಿಟ್ಟುಕೊಳ್ಳಿರಿ; ನಿಮ್ಮ ಸಂತತಿಯವರಿಗೆ ಈ ಧರ್ಮಶಾಸ್ತ್ರದ ನಿಯಮಗಳನ್ನು ಅನುಸರಿಸಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿರಿ.",
			"47": "<sup>47</sup> ಇದು ನಿರರ್ಥಕವೆಂದು ಭಾವಿಸಬೇಡಿ; ಇದರಿಂದ ನೀವು ಬಾಳುವಿರಿ; ನೀವು ಜೋರ್ಡನ್ ನದಿಯನ್ನು ದಾಟಿ ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳಲು ಹೋಗುವ ನಾಡಿನಲ್ಲಿ ಇದನ್ನು ಅನುಸರಿಸುವುದರಿಂದಲೇ ಬಹುಕಾಲ ಇರುವಿರಿ,” ಎಂದು ಹೇಳಿದನು.",
			"48": "<sup>48</sup> ಆ ದಿನದಲ್ಲೇ ಸರ್ವೇಶ್ವರ ಮೋಶೆಗೆ, “ನೀನು ಈ ಅಬಾರೀಮ್ ಬೆಟ್ಟಗಳನ್ನು ಅಂದರೆ, ಮೋವಾಬ್ಯರ ದೇಶದಲ್ಲಿ ಜೆರಿಕೋ ಪಟ್ಟಣಕ್ಕೆ ಎದುರಾಗಿರುವ ನೆಬೋ ಬೆಟ್ಟವನ್ನು ಹತ್ತಿ ನಾನು ಇಸ್ರಯೇಲರಿಗೆ ಸ್ವದೇಶವಾಗಲೆಂದು ಕೊಡುವ ಕಾನಾನ್ ನಾಡನ್ನು ನೋಡು.",
			"50": "<sup>50</sup> ಅನಂತರ, ನಿನ್ನ ಅಣ್ಣನಾದ ಆರೋನನು ಹೇಗೆ ಹೋರೆಂಬ ಬೆಟ್ಟದಲ್ಲಿ ದೇಹವನ್ನು ಬಿಟ್ಟು ತನ್ನ ಪಿತೃಗಳಲ್ಲಿಗೆ ಸೇರಿದನೋ ಹಾಗೆಯೇ ನೀನೂ ಈ ಬೆಟ್ಟದಲ್ಲಿ ದೇಹವನ್ನು ಬಿಟ್ಟು ಪಿತೃಗಳಲ್ಲಿಗೆ ಸೇರಬೇಕು.",
			"51": "<sup>51</sup> ನೀವಿಬ್ಬರೂ ಚಿನ್ ಮರುಭೂಮಿಯಲ್ಲಿನ ಮೆರೀಬಾ ಕಾದೇಶಿನಲ್ಲಿ ನೀರು ಹೊರಟು ಬಂದ ಸ್ಥಳದ ಬಳಿಯಲ್ಲಿ, ಇಸ್ರಯೇಲರ ಮಧ್ಯೆ ನನಗೆ ವಿರೋಧವಾಗಿ ದ್ರೋಹಮಾಡಿದಿರಿ; ಅವರ ಮುಂದೆ ನನ್ನ ಗೌರವವನ್ನು ಕಾಪಾಡದೆಹೋದಿರಿ. ಆದುದರಿಂದಲೇ ಹೀಗೆ ಆಗಬೇಕು.",
			"52": "<sup>52</sup> ನಾನು ಇಸ್ರಯೇಲರಿಗೆ ಕೊಡುವ ನಾಡನ್ನು ನೀನು ದೂರದಿಂದ ನೋಡಬಹುದೇ ಹೊರತು, ಅದರಲ್ಲಿ ಪ್ರವೇಶಿಸಕೂಡದು,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದರು."
		},
		"33": {
			"1": "<sup>1</sup> ದೇವಪುರುಷನದ ಮೋಶೆ ದೇಹವನ್ನು ಬಿಡುವುದಕ್ಕಿಂತ ಮುಂಚೆ ಇಸ್ರಯೇಲರನ್ನು ಕುರಿತು ಹೇಳಿದ ಆಶೀರ್ವಚನಗಳಿವು:",
			"2": "<sup>2</sup> ಸರ್ವೇಶ್ವರ ಸೀನಾಯ್ ಬೆಟ್ಟದಿಂದ ಬಂದರು, ಸೇಯೀರ್ ಎಂಬ ಸೀಮೆಯೊಳಗಿಂದ ಉದಯಿಸಿದರು, ಪಾರಾನ್ ಎಂಬ ಪರ್ವತದಿಂದ ಪ್ರಜ್ವಲಿಸಿದರು, ಲಕ್ಷಾಂತರ ಸಂತರ ಮಧ್ಯೆಯಿಂದ ದಯಮಾಡಿದರು. ಅಗ್ನಿಸದೃಶವಾದ ಧರ್ಮಶಾಸ್ತ್ರ ಅವರ ಬಲಪಾರ್ಶ್ವದಲ್ಲಿತ್ತು.",
			"3": "<sup>3</sup> ಹೌದು, ಆತ ಪ್ರೀತಿಸುತ್ತಾನೆ ತನ್ನ ಪ್ರಜೆಯನ್ನು, ಆಶ್ರಯ ನೀಡುತ್ತಾನೆ ತನ್ನ ಭಕ್ತರೆಲ್ಲರಿಗು. ಎಂದೇ ಆತನ ಆಜ್ಞೆಗಳಿಗೆ ತಲೆಬಾಗುವೆವು, ಆತನ ಪಾದಚರಣದಲೆ ಕುಳಿತಿರುವೆವು.",
			"4": "<sup>4</sup> ಯಕೋಬ ಸಂತತಿಯಾದ ನಮಗೆ ಇತ್ತನು ಮೋಶೆ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಸೊತ್ತಾಗಿ",
			"5": "<sup>5</sup> ಇಸ್ರಯೇಲರ ಕುಲಗಳು ಒಂದಾದಾಗ ಜನನಾಯಕರು ಒಟ್ಟಾಗಿ ಕೂಡಿದಾಗ ಯೆಶುರೂನಿನಲಿ ಸರ್ವೇಶ್ವರನೇ ಅರಸನಾದ.",
			"6": "<sup>6</sup> ರೂಬೇನ್ ಕುಲ ಕುರಿತು ಮೋಶೆ ನುಡಿದದ್ದು: “ರೂಬೇನ್ ಅಳಿಯದೆ ಉಳಿಯಲಿ ಅವನ ಮಕ್ಕಳು ಅಸಂಖ್ಯಾತರಾಗದಿರಲಿ.”",
			"7": "<sup>7</sup> ಯೆಹೂದ ಕುಲ ಕುರಿತು ಮೋಶೆ ಹೇಳಿದ್ದು: “ಸರ್ವೇಶ್ವರಾ, ಯೆಹೂದನ ಮೊರೆಯನ್ನಾಲಿಸು ಬಂಧುಗಳೊಡನೆ ಅವನನ್ನು ಮರಳಿ ಸೇರಿಸು ತನ್ನ ಹಕ್ಕುಬಾಧ್ಯತೆಗಳನ್ನು ಕಾದಿರಿಸಿಕೊಳ್ಳಲು ಶತ್ರುಗಳ ಕೈಯಿಂದ ಅವನನ್ನು ತಪ್ಪಿಸು.”",
			"8": "<sup>8</sup> ಲೇವಿಯ ಕುಲ ಕುರಿತು ಮೋಶೆ ನುಡಿದದ್ದು: “ಸರ್ವೇಶ್ವರಾ, ನೀಡಿವನಿಗೆ ನಿನ್ನ ವಿಧಿ ತಿಳಿಸುವ ಊರಿಮ್, ನಿನ್ನ ಭಕ್ತನಾದ ಇವನ ವಶದಲ್ಲಿರಲಿ ಆ ತುಮ್ಮೀಮ್. ನೀನಿವನನ್ನು ಪರೀಕ್ಷಿಸಿದೆ ಮಸ್ಸದಲ್ಲಿ ವಿವಾದಿಸಿದೆ ಜಲ ಹೊರಹೊಮ್ಮಿದ ಮೆರೀಬದಲ್ಲಿ.",
			"9": "<sup>9</sup> ನಿನ್ನ ಆಜ್ಞೆಗಳನ್ನು ಕೈಗೊಳ್ಳಲೆಂದು ನಿನ್ನ ಒಡಂಬಡಿಕೆಯನು ಕಾದಿರಿಸಲೆಂದು ‘ತಂದೆತಾಯಿಗಳ ಪರಿಚಯವಿಲ್ಲ ಅಣ್ಣತಮ್ಮಂದಿರ ಅರಿವಿಲ್ಲ ಸ್ವಂತ ಮಕ್ಕಳ ಗುರುತಿಲ್ಲ’ ಎಂದನಿವನು.",
			"10": "<sup>10</sup> ತಿಳಿಸುವನಿವನು ನಿನ್ನ ನಿರ್ಣಯವನು ಯಕೋಬನಿಗೆ ಕಲಿಸುವನು ನಿನ್ನ ಧರ್ಮಶಾಸ್ತ್ರವನು ಇಸ್ರಯೇಲನಿಗೆ. ಧೂಪಾರತಿ ಎತ್ತುವನು ನಿನ್ನ ಸನ್ನಿಧಿಯಲಿ ದಹನಬಲಿ ಸಮರ್ಪಿಸುವನು ನಿನ್ನ ಬಲಿಪೀಠದಲಿ.",
			"11": "<sup>11</sup> ಸರ್ವೇಶ್ವರಾ, ವೃದ್ಧಿಪಡಿಸು ಇವನ ಆಸ್ತಿಯನು ಸಮರ್ಪಕವಾಗಿರಲಿ ನಿನಗೆ ಇವನ ಸೇವೆಯು ಇವನ ಶತ್ರುಗಳ ನಡುವನ್ನು ಮುರಿ ಮರಲಿ ಏಳದಂತೆ ಮಾಡು ಇವನ ವೈರಿ.”",
			"12": "<sup>12</sup> ಬೆನ್ಯಾಮೀನ್ ಕುಲ ಕುರಿತು ಮೋಶೆ ನುಡಿದದ್ದು : “ಸರ್ವೇಶ್ವರನಿಗೆ ಪ್ರಿಯನಾದ ಇವನು ನಿರ್ಭಯವಾಗಿ ವಾಸಮಾಡುವನು. ಇವನನ್ನಾತ ತಬ್ಬಿರುವನು ದಿನವಿಡೀ ತನ್ನ ತೋಳತೆಕ್ಕೆಯಲಿ.”",
			"13": "<sup>13</sup> ಜೋಸೆಫ್ ಕುಲ ಕುರಿತು ಮೋಶೆ ನುಡಿದದ್ದು : “ಇವನ ಪ್ರಾಂತ್ಯ ಆಶೀರ್ವದಿತವಾಗಲಿ ಸರ್ವೇಶ್ವರನಿಂದ ಮೇಲಣ ಆಕಾಶದ ಮಂಜಿನಿಂದ ಕೆಳಗಿನ ಸಾಗರದ ಒರತೆಗಳಿಂದ;",
			"14": "<sup>14</sup> ಸೂರ್ಯನಿಂದ ಫಲಿಸುವ ಪಸಲುಗಳಿಂದ ತಿಂಗಳು ತಿಂಗಳು ಆಗುವ ಬೆಳೆಗಳಿಂದ;",
			"15": "<sup>15</sup> ಪುರಾತನ ಬೆಟ್ಟಗಳಿಂದ ಒದಗುವ ಉತ್ಪನ್ನಗಳಿಂದ ಶಾಶ್ವತ ಪರ್ವತಗಳಿಂದ ಸಿಗುವ ಒಳಿತುಗಳಿಂದ.",
			"16": "<sup>16</sup> ಸೋದರರಿಗೆ ಸಾಮ್ರಾಟನಾದ ಜೋಸೆಫನ ಪಾಲಿಗೆ ಬರಲಿ ಇವನ ಕುಲದ ಮೇಲೆ ನೆಲಸಲಿ ನೆಲಬೆಳೆಸುವ ಫಲಗಳಲಿ ಶ್ರೇಷ್ಠವಾದುದೆಲ್ಲವು ಮುಳ್ಳುಪೊದೆಯಲಿ ಕಾಣಿಸಿಕೊಂಡಾತನ ದಯೆಯು!",
			"17": "<sup>17</sup> ಜೋಸೆಫನದು ಜೇಷ್ಠಸಂತತಿಯು ಗೂಳಿಯ ಗಾಂಭೀರ್ಯವೂ ಅವನ ಕೊಂಬುಗಳು, ಕಾಡುಕೋಣದ ಕೊಂಬುಗಳು ಅವುಗಳಿಂದ ಇರಿದು ಓಡಿಸಬಲ್ಲನು ಜಗದ ಜನಾಂಗಗಳನು! ಇವನಂಥವರು ಎಫ್ರಯಿಮ್ ಕುಲದ ಕೋಟ್ಯಾಂತರ ಜನರು ಇವನಂಥವರು ಮನಸ್ಸೆಕುಲದ ಲಕ್ಷಾಂತರ ಮಂದಿಗಳು.”",
			"18": "<sup>18</sup> ಜೆಬುಲೂನ್ ಮತ್ತು ಇಸ್ಸಾಕಾರ ಕುಲ ಕುರಿತು ಮೋಶೆ ನುಡಿದದ್ದು: “ಜೆಬುಲೂನೇ, ಸಂತೋಷವಾಗಿರು ನಿನ್ನ ಪಯಣಗಳಲ್ಲಿ. ಇಸ್ಸಾಕಾರನೇ, ಆನಂದವಾಗಿರು ನಿನ್ನ ಪಾಳೆಯಗಳಲ್ಲಿ.",
			"19": "<sup>19</sup> ಇವರು ಕರೆದುತರುವರು ಅನ್ಯಜನಗಳನು ಮಲೆನಾಡಿಗೆ ಅರ್ಪಿಸತರುವರು ನ್ಯಾಯಯುತವಾದ ಬಲಿಗಳನು ಅಲ್ಲಿಗೆ. ಸವಿವರು ಕಡಲ ಸಿರಿಯನು ನೆಲದಲ್ಲಡಗಿರುವ ಸಂಪದವನು.”",
			"20": "<sup>20</sup> ಗಾದ್ ಕುಲ ಕುರಿತು ಮೋಶೆ ನುಡಿದದ್ದು: “ಗಾದ್ಯರ ಪ್ರಾಂತ್ಯವನು ವಿಸ್ತರಿಸಿದ ಸರ್ವೇಶ್ವರನಿಗೆ ಸ್ತೋತ್ರವು! ಸಿಂಹದಂತೆ ಹೊಂಚುಹಾಕಿ ಮುರಿವನು ಗಾದನು ಶತ್ರುಗಳ ಭುಜವನು, ಶಿರವನು.",
			"21": "<sup>21</sup> ತನಗೆಂದು ತೆಗೆದುಕೊಂಡನು ನಾಡಿನ ಮೊದಲಭಾಗವನು ದೊರಕಿತಲ್ಲಿ ಅವನಿಗೆ ಮುಖ್ಯಸ್ಥನಿಗಾಗುವ ಸ್ವಾಸ್ತ್ಯವು. ಜನಾಧಿಪತಿಗಳ ಜೊತೆಬಂದು ನೆರವೇರಿಸಿದನು ಸರ್ವೇಶ್ವರನ ಆಣತಿಯನು; ಇಸ್ರಯೇಲನೊಡನೆ ಸ್ಥಾಪಿಸಿದನು ಸರ್ವೇಶ್ವರನ ನ್ಯಾಯನೀತಿಯನು.”",
			"22": "<sup>22</sup> ದಾನ್ ಕುಲ ಕುರಿತು ಮೋಶೆ ನುಡಿದದ್ದು: “ದಾನನು ಬಾಷಾನ್ ದೇಶದಿಂದ ಧಾವಿಸಿಬಂದ ಯುವಸಿಂಹನು!”",
			"23": "<sup>23</sup> ನಫ್ತಾಲಿ ಕುಲ ಕುರಿತು ಮೋಶೆ ನುಡಿದದ್ದು: “ಎಲೈ ನಫ್ತಾಲಿ, ಸರ್ವೇಶ್ವರನ ದಯೆಹೊಂದಿ ನೀ ತೃಪ್ತನಾದೆ ಆತನ ಆಶೀರ್ವಾದದಿಂದ ಸಮೃದ್ಧಿಯುಂಟು ನಿನಗೆ ಸಮುದ್ರದ ದಕ್ಷಿಣಪ್ರದೇಶ ಸೊತ್ತಾಗಲಿ ನಿನಗೆ.”",
			"24": "<sup>24</sup> ಆಶೇರ್ ಕುಲ ಕುರಿತು ಮೋಶೆ ನುಡಿದದ್ದು: “ಕುಲೋತ್ತಮನಾದ ಆಶೇರನು ಭಾಗ್ಯಹೊಂದಲಿ ಎಲ್ಲದರಲಿ ಸೋದರರಲ್ಲಿ ಶ್ರೇಷ್ಠತೆಯನು ಪಡೆಯಲಿ ಎಣ್ಣೆಯಲಿ ಪಾದಸ್ನಾನ ಮಾಡುವಂತಾಗಲಿ.",
			"25": "<sup>25</sup> ನಿನ್ನ ಕೋಟೆಯ ಬಾಗಿಲು ಕಬ್ಬಿಣದವು ಅವುಗಳ ಅಗುಳಿಗಳು ಕಂಚಿನವು ನೀನಿರುವಷ್ಟು ಕಾಲ ನಿನಗಿರುವುದು ಬಲವು.”",
			"26": "<sup>26</sup> ಇಸ್ರಯೇಲಿನ ಜನರೇ, ನಿಮ್ಮ ದೇವರಿಗೆ ಸಮಾನನಾರೂ ಇಲ್ಲ, ಆತ ಬರುವನು ಆಕಾಶವನ್ನೇರಿ, ಮೇಘಾರೂಢನಾಗಿ ಮಹಾಗಾಂಭೀರ್ಯದಿಂದ ಬರುವನು ನಿಮ್ಮ ನೆರವಿಗಾಗಿ.",
			"27": "<sup>27</sup> ಆದಿಯಿಂದ ದೇವರೇ ನಿಮಗೆ ಶ್ರೀನಿವಾಸ ಆತನ ಹಸ್ತವೇ ನಿಮಗೆ ನಿತ್ಯಾಧಾರ! ಶತ್ರುಗಳನು ಹೊರಡಿಸುವನು ನಿಮ್ಮ ಬಳಿಯಿಂದ ಅವರನು ಸಂಹರಿಸಲು ಆಜ್ಞಾಪಿಸಿಹನು ಆತ.",
			"28": "<sup>28</sup> ವಾಸಿಸಿರಿ ಇಸ್ರಯೇಲರೇ, ನಿರ್ಭಯರಾಗಿ ಯಕೋಬನ ಸಂತತಿಯೇ, ಸುರಕ್ಷಿತವಾಗಿ ಆಗಸದಿಂದ ಮಳೆಸುರಿಯುವ ನಾಡಿನಲ್ಲಿ ಧಾನ್ಯ, ದ್ರಾಕ್ಷಾರಸ ಸಮೃದ್ಧಿಯಾಗಿರುವಲ್ಲಿ!",
			"29": "<sup>29</sup> ಇಸ್ರಯೇಲ್, ನೀನು ಎಷ್ಟೋ ಧನ್ಯ! ಯಾರಿಗಿದೆ ನಿನಗಿರುವಂತೆ ಭಾಗ್ಯ! ಜಯಗಳಿಸಿರುವೆ ಸರ್ವೇಶ್ವರನ ಅನುಗ್ರಹದಿಂದ. ಆತನೇ ನಿನ್ನ ಕಾಪಾಡುವ ಗುರಾಣಿ ನಿನ್ನ ಗೌರವವನ್ನು ಕಾಯುವ ಕತ್ತಿ. ಎಂದೇ ಮುದುರಿಕೊಳ್ಳುವರು ಶತ್ರುಗಳು ನಿನ್ನ ಮುಂದೆ ಜಯಶೀಲನಾಗಿ ನೀ ಮೆರೆವೆ ಅವರ ಮಲೆನಾಡಿನಲ್ಲೆ."
		},
		"34": {
			"1": "<sup>1</sup> ಮೋಶೆ ಮೋವಾಬ್ಯರ ಬಯಲು ನಾಡಿನಿಂದ ಜೆರಿಕೋ ಪಟ್ಟಣಕ್ಕೆ ಎದುರಾಗಿ ಇರುವ ನೆಬೋ ಪರ್ವತಕ್ಕೆ ಹೋಗಿ, ಪಿಸ್ಗಾ ಎಂಬ ಬೆಟ್ಟದ ಶಿಖರವನ್ನು ಹತ್ತಿದನು. ಆಗ ಸರ್ವೆಶ್ವರ ಕಾನಾನ್ ನಾಡೆಲ್ಲವನ್ನು ಅಂದರೆ, ದಾನ್ ಪಟ್ಟಣದವರೆಗಿದ್ದ ಗಿಲ್ಯಾದ್ ಪ್ರಾಂತ್ಯ,",
			"2": "<sup>2</sup> ನಫ್ತಾಲಿ ಪ್ರದೇಶ, ಎಫ್ರಯಿಮ್ ಮನಸ್ಸೆ ಕುಲಗಳವರ ಪ್ರಾಂತ್ಯ, ಪಶ್ಚಿಮ ಸಮುದ್ರದವರೆಗಿದ್ದ ಜುದೇಯನಾಡು,",
			"3": "<sup>3</sup> ದಕ್ಷಿಣ ಪ್ರದೇಶ, ಚೋಗರೂರಿನ ತನಕ ಇದ್ದ ಜೆರಿಕೋ ಖರ್ಜೂರಗಳ ಪಟ್ಟಣದ ಸುತ್ತಲಿನ ಬಯಲು, ಇದನ್ನೆಲ್ಲಾ ಅವನಿಗೆ ತೋರಿಸಿದರು.",
			"4": "<sup>4</sup> ಅನಂತರ, “ನಾನು ಅಬ್ರಹಾಮ್, ಇಸಾಕ್, ಯಕೋಬರಿಗೆ ಪ್ರಮಾಣಮಾಡಿ ಅವರ ಸಂತತಿಯವರಿಗೆ ಕೊಡುವೆನೆಂದು ವಾಗ್ದಾನ ಮಾಡಿದ ನಾಡು ಇದೇ; ಇದನ್ನು ನಿನಗೆ ಪ್ರತ್ಯಕ್ಷವಾಗಿ ತೋರಿಸಿದ್ದೇನೆ. ಆದರೆ ನೀನು ನದಿದಾಟಿ ಅಲ್ಲಿಗೆ ಹೋಗಕೂಡದು,” ಎಂದು ಹೇಳಿದರು.",
			"5": "<sup>5</sup> ಸರ್ವೇಶ್ವರನ ಮಾತಿನಂತೆ ಅವರ ದಾಸ ಮೋಶೆ ಅಲ್ಲೇ ಮೋವಾಬ್ಯರ ದೇಶದಲ್ಲಿ ಮೃತನಾದನು.",
			"6": "<sup>6</sup> ಮೋವಾಬ್ಯರ ದೇಶದಲ್ಲಿ ಬೇತ್ಪೆಗೋರಿಗೆ ಎದುರಾಗಿರುವ ಕಣಿವೆಯಲ್ಲಿ ಅವನ ದೇಹವನ್ನು ಸಮಾಧಿಮಾಡಿದರು. ಅವನ ಸಮಾಧಿ ಎಲ್ಲಿದೆಯೆಂದು ಇಂದಿನವರೆಗೆ ಯಾರಿಗೂ ತಿಳಿಯದು.",
			"7": "<sup>7</sup> ಮೋಶೆ ಸಾಯುವಾಗ ನೂರಿಪ್ಪತ್ತು ವರ್ಷದವನಾಗಿದ್ದನು. ಅವನ ಕಣ್ಣು ಮೊಬ್ಬಾಗಲಿಲ್ಲ, ಅವನ ಜೀವಕಳೆ ಕುಂದಿಹೋಗಿರಲಿಲ್ಲ.",
			"8": "<sup>8</sup> ಮೋವಾಬ್ಯರ ಬಯಲಿನಲ್ಲಿ ಇಸ್ರಯೇಲರು ಮೋಶೆಗಾಗಿ ಮೂವತ್ತು ದಿನ ದುಃಖಿಸಿದರು. ಅಲ್ಲಿಗೆ ಮೋಶೆಯ ಸಂತಾಪದ ದಿನಗಳು ಮುಗಿದವು.",
			"9": "<sup>9</sup> ಮೋಶೆ ನೂನನ ಮಗನಾದ ಯೆಹೋಶುವನ ಮೇಲೆ ಹಸ್ತಕ್ಷೇಪಮಾಡಿದ್ದರಿಂದ ಅವನು ಜ್ಞಾನವರಸಂಪನ್ನನಾದನು. ಸರ್ವೇಶ್ವರ ಮೋಶೆಗೆ ಕೊಟ್ಟ ಆಜ್ಞೆಗೆ ಅನುಸಾರ ಇಸ್ರಯೇಲರು ಯೆಹೋಶುವನ ಮಾತಿನ ಪ್ರಕಾರ ನಡೆದರು.",
			"10": "<sup>10</sup> ಸರ್ವೇಶ್ವರ ಮೋಶೆಯ ಸಂಗಡ ಮುಖಾಮುಖಿಯಾಗಿ ಮಾತಾಡಿದರು.",
			"11": "<sup>11</sup> ಈಜಿಪ್ಟ್ ದೇಶದಲ್ಲಿ ಫರೋಹನ ಮುಂದೆ ಹಾಗು ಅವನ ಪ್ರಜಾಪರಿವಾರದವರ ಮುಂದೆ ಅವನು ವಿಧವಿಧವಾದ ಪವಾಡಗಳನ್ನೂ ಮಹತ್ಕಾರ್ಯಗಳನ್ನೂ ನಡೆಸಿದನು; ಸರ್ವೇಶ್ವರ ಅವನನ್ನು ಅದಕ್ಕಾಗಿಯೇ ಕಳುಹಿಸಿದ್ದರು.",
			"12": "<sup>12</sup> ಇಸ್ರಯೇಲರ ಕಣ್ಮುಂದೆ ವಿಶೇಷ ಭುಜಪರಾಕ್ರಮವನ್ನೂ ಆಶ್ಚರ್ಯಕರವಾದ ಕಾರ್ಯಗಳನ್ನೂ ನಡೆಸಿದನು. ಇಂಥವನಿಗೆ ಸಮಾನನಾದ ಮತ್ತೊಬ್ಬ ಪ್ರವಾದಿ ಈಚೆಗೆ ಇಸ್ರಯೇಲರಲ್ಲಿ ಹುಟ್ಟಲೇ ಇಲ್ಲ."
		}
	}
};
module.exports = book;