var book = {
	"name": "೧ ಅರಸುಗಳು",
	"numChapters": 22,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ಅರಸ ದಾವೀದನು ಹಣ್ಣು ಹಣ್ಣು ಮುದುಕನಾದ; ಎಷ್ಟು ಕಂಬಳಿಗಳನ್ನು ಹೊದಿಸಿದರೂ ಅವನಿಗೆ ಬೆಚ್ಚಗಾಗುತ್ತಿರಲಿಲ್ಲ.",
			"2": "<sup>2</sup> ಆದುದರಿಂದ ಅವನ ಸೇವಕರು, “ಒಡೆಯರೇ, ಅರಸರೇ, ತಮ್ಮ ಸನ್ನಿಧಿಯಲ್ಲಿದ್ದುಕೊಂಡು ತಮಗೆ ಶುಶ್ರೂಷೆಮಾಡಲು ಒಬ್ಬ ಕನ್ನಿಕೆಯನ್ನು ಹುಡುಕಬೇಕಾಗಿದೆ. ಆಕೆ ನಮ್ಮ ಒಡೆಯರೂ ಅರಸರೂ ಆದ ತಮ್ಮ ಮಗ್ಗುಲಲ್ಲೇ ಮಲಗಿ ತಮ್ಮನ್ನು ಬೆಚ್ಚಗಿರಿಸುವಳು,” ಎಂದು ಹೇಳಿದರು.",
			"3": "<sup>3</sup> ಅದರಂತೆಯೇ, ಇಸ್ರಯೇಲರ ಪ್ರಾಂತ್ಯಗಳಲೆಲ್ಲಾ ಅತಿ ಸುಂದರಿಯಾದ ಒಬ್ಬ ಹುಡುಗಿಯನ್ನು ಹುಡುಕುವುದಕ್ಕೆ ಹೋದರು. ಶೂನೇಮ್ ಊರಿನ ಅಬೀಷಗ್ ಎಂಬವಳನ್ನು ಕಂಡುಹಿಡಿದು ಆಕೆಯನ್ನು ಅರಸನ ಬಳಿಗೆ ಕರೆದುಕೊಂಡು ಬಂದರು.",
			"4": "<sup>4</sup> ಬಹಳ ಚೆಲುವೆ ಆದ ಆಕೆ ಅರಸನನ್ನು ಶುಶ್ರೂಷೆ ಮಾಡುವ ದಾದಿಯಾದಳು. ಆದರೆ ಅರಸ ಅಬೀಷಗಳನ್ನು ಕೂಡಲಿಲ್ಲ.",
			"5": "<sup>5</sup> ಈಗಾಗಲೇ ಅಬ್ಷಾಲೋಮನು ಸತ್ತುಹೋಗಿದ್ದನು. ದಾವೀದನಿಗೆ ಹಗ್ಗೀತಳಲ್ಲಿ ಹುಟ್ಟಿದ ಅದೋನಿಯನೇ ಜೀವದಿಂದ ಉಳಿದವರಲ್ಲಿ ಹಿರಿಯ ಮಗ. ಅವನು ಬಹಳ ಸುಂದರನು. ದಾವೀದನು ಯಾವುದೇ ವಿಷಯದಲ್ಲಿ ಅವನನ್ನು ಎಂದೂ ಗದರಿಸಿರಲಿಲ್ಲ. ತಾನೇ ರಾಜನಾಗಬೇಕೆಂಬ ಆಕಾಂಕ್ಷೆ ಅವನದು. ಎಂದೇ ತನಗೋಸ್ಕರ ರಥ, ರಥಾಶ್ವಗಳನ್ನು ಹಾಗು ಐವತ್ತು ಮಂದಿ ಬೆಂಗಾವಲಿಗರನ್ನು ನೇಮಿಸಿಕೊಂಡಿದ್ದನು.",
			"7": "<sup>7</sup> ಜೆರೂಯಳ ಮಗ ಯೋವಾಬನನ್ನೂ ಯಾಜಕ ಎಬ್ಯಾತಾರನನ್ನೂ ತನ್ನ ಕಡೆಗೆ ಒಲಿಸಿಕೊಂಡಿದ್ದನು. ಇವರು ಅವನಿಗೆ ಬೆಂಬಲ ವ್ಯಕ್ತಪಡಿಸಿದ್ದರು.",
			"8": "<sup>8</sup> ಆದರೆ ಯಾಜಕ ಚಾದೋಕ್, ಯೆಹೋಯಾದಾವನ ಮಗ ಬೆನಾಯ, ಪ್ರವಾದಿ ನಾತಾನ್, ಶಿಮ್ಮೀ, ರೇಗೀ ಎಂಬವರೂ ದಾವೀದನ ವಿಶೇಷ ಕಾವಲಾಳುಗಳೂ ಅದೋನಿಯನನ್ನು ಬೆಂಬಲಿಸಲಿಲ್ಲ.",
			"9": "<sup>9</sup> ಒಮ್ಮೆ ಅದೋನೀಯನು ರೋಗೆಲ್ ಬುಗ್ಗೆಯ ಬಳಿಯಿರುವ ಜೋಹೆಲೆತ್ ಬಂಡೆಯ ಮೇಲೆ ಕುರಿ, ಹೋರಿ ಮೊದಲಾದ ಕೊಬ್ಬಿದ ಪಶುಗಳನ್ನು ಬಲಿದಾನ ಮಾಡಿಸಿದನು; ಅರಸುಕುಮಾರರಾದ ತನ್ನ ಸಹೋದರರನ್ನು ಹಾಗು ಆಸ್ಥಾನದ ಎಲ್ಲ ಯೆಹೂದ್ಯ ಅಧಿಕಾರಿಗಳನ್ನು ಔತಣಕ್ಕೆ ಆಮಂತ್ರಿಸಿದನು.",
			"10": "<sup>10</sup> ಆದರೆ, ಪ್ರವಾದಿ ನಾತಾನನನ್ನಾಗಲಿ, ಬೆನಾಯನನ್ನಾಗಲಿ, ವಿಶೇಷ ಕಾವಲಾಳುಗಳನ್ನಾಗಲಿ, ತಮ್ಮನಾದ ಸೊಲೊಮೋನನನ್ನಾಗಲಿ ಕರೆಯಲಿಲ್ಲ.",
			"11": "<sup>11</sup> ಆಗ ನಾತಾನನು ಸೊಲೊಮೋನನ ತಾಯಿಯಾದ ಬತ್ಷೆಬೆಗೆ, “ಹಗ್ಗೀತಳ ಮಗ ಅದೋನೀಯನು, ನಮ್ಮ ಒಡೆಯ ದಾವೀದರಿಗೆ ತಿಳಿಯದೆಯೇ ಅರಸನಾದನೆಂಬುದನ್ನು ನೀನು ಕೇಳಿರಬೇಕಲ್ಲವೆ?",
			"12": "<sup>12</sup> ಈಗ ನಿನ್ನ ಮತ್ತು ನಿನ್ನ ಮಗ ಸೊಲೊಮೋನನ ಜೀವವನ್ನು ಉಳಿಸಿಕೊಳ್ಳಬೇಕಾದರೆ ನನ್ನ ಆಲೋಚನೆಯನ್ನು ಕೇಳು;",
			"13": "<sup>13</sup> ನೀನು ಅರಸ ದಾವೀದನ ಬಳಿಗೆ ಹೋಗಿ, ‘ನನ್ನ ಒಡೆಯರಾದ ಅರಸರು ತಮ್ಮ ತರುವಾಯ ನನ್ನ ಮಗನಾದ ಸೊಲೊಮೋನನೇ ಆಳಬೇಕು, ಅವನೇ ತಮ್ಮ ಸಿಂಹಾಸನದ ಮೇಲೆ ಕುಳಿತುಕೊಳ್ಳಬೇಕು ಎಂದು ತಮ್ಮ ದಾಸಿಯಾದ ನನಗೆ ಪ್ರಮಾಣಮಾಡಿ ಹೇಳಿದಿರಲ್ಲವೇ? ಹೀಗಿದ್ದ ಮೇಲೆ ಅದೋನೀಯನು ಅರಸನಾದುದು ಹೇಗೆ?’ ಎಂದು ಕೇಳು.",
			"14": "<sup>14</sup> ನೀನು ಅರಸನೊಡನೆ ಮಾತಾಡುತ್ತಿರುವಾಗ ನಾನೂ ಬಂದು ನಿನ್ನ ಮಾತು ಸತ್ಯವೆಂದು ಅನುಮೋದಿಸುವೆನು,” ಎಂದನು.",
			"15": "<sup>15</sup> ಅರಸನು ಬಹಳ ಮುದುಕನಾಗಿದ್ದರಿಂದ, ಶೂನೇಮ್ಯಳಾದ ಅಬೀಷಗಳಿಂದ ಶುಶ್ರೂಷೆ ಪಡೆಯುತ್ತಾ ಒಳಗಿನ ಕೋಣೆಯಲ್ಲಿಯೇ ಇರುತ್ತ ಇದ್ದನು.",
			"16": "<sup>16</sup> ಬತ್ಷೆಬೆ ಅಲ್ಲಿಗೆ ಹೋಗಿ ಅರಸನಿಗೆ ಬಾಗಿ ನಮಸ್ಕರಿಸಿದಳು. ಅರಸನು, “ಏನು, ನಿನಗೇನು ಬೇಕು?” ಎಂದು ಆಕೆಯನ್ನು ವಿಚಾರಿಸಿದನು.",
			"17": "<sup>17</sup> ಆಗ ಆಕೆ, “ನನ್ನ ಒಡೆಯರು ತಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಮೇಲೆ ಆಣೆಯಿಟ್ಟು ತಮ್ಮ ದಾಸಿಯಾದ ನನಗೆ, ‘ನನ್ನ ತರುವಾಯ ನಿನ್ನ ಮಗನಾದ ಸೊಲೊಮೋನನು ಅರಸನಾಗಬೇಕು; ನಿಶ್ಚಯವಾಗಿ ಅವನೇ ನನ್ನ ಸಿಂಹಾಸನದ ಮೇಲೆ ಕೂತುಕೊಳ್ಳಬೇಕು,’ ಎಂಬುದಾಗಿ ಹೇಳಿದಿರಲ್ಲವೆ?",
			"18": "<sup>18</sup> ಆದರೆ ಈಗ ಅದೋನೀಯನು ನಮ್ಮ ಒಡೆಯರೂ ಅರಸರೂ ಆದ ನಿಮಗೂ ತಿಳಿಯದೆಯೇ ರಾಜನಾಗಿಬಿಟ್ಟಿದ್ದಾನೆ.",
			"19": "<sup>19</sup> ಹೋರಿ, ಕುರಿ ಮೊದಲಾದ ಹಲವು ಕೊಬ್ಬಿದ ಪಶುಗಳನ್ನು ಬಲಿಯಾಗಿ ಅರ್ಪಿಸಿದ್ದಾನೆ; ನಿಮ್ಮ ಸೇವಕ ಸೊಲೊಮೋನನನ್ನು ಬಿಟ್ಟು, ಎಲ್ಲಾ ರಾಜಪುತ್ರರನ್ನು, ಯಾಜಕ ಎಬ್ಯಾತಾರನನ್ನು ಹಾಗು ಸೇನಾಪತಿ ಯೋವಾಬನನ್ನು ಔತಣಕ್ಕೆ ಕರೆದಿದ್ದಾನೆ.",
			"20": "<sup>20</sup> ನನ್ನ ಒಡೆಯರೂ ಅರಸರೂ ಆದ ನಿಮ್ಮ ತರುವಾಯ ಸಿಂಹಾಸನದ ಮೇಲೆ ಕುಳಿತುಕೊಳ್ಳತಕ್ಕವರು ಯಾರೆಂಬುದನ್ನು ತಾವೇ ಗೊತ್ತುಮಾಡಬೇಕೆಂದು ಇಸ್ರಯೇಲರೆಲ್ಲರು ಕುತೂಹಲದಿಂದ ಕಾದುಕೊಂಡಿದ್ದಾರೆ.",
			"21": "<sup>21</sup> ನನ್ನ ಒಡೆಯರೂ ಅರಸರೂ ಆದ ತಾವು ಹಾಗೇ ಮಾಡದೆ, ಸತ್ತು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಿಕೊಂಡರೆ, ನಾನೂ ನನ್ನ ಮಗನೂ ದೋಷಾರೋಪಣೆಗೆ ಗುರಿಯಾಗುತ್ತೇವೆ,” ಎಂದು ಹೇಳಿದಳು.",
			"22": "<sup>22</sup> ಆಕೆ ಹೀಗೆ ಅರಸನ ಸಂಗಡ ಮಾತಾಡುತ್ತಿರುವಾಗಲೇ ಪ್ರವಾದಿ ನಾತಾನನು ಅಲ್ಲಿಗೆ ಬಂದನು.",
			"23": "<sup>23</sup> ಸೇವಕರು ನಾತಾನನು ಬಂದಿರುವ ಸಂಗತಿಯನ್ನು ಅರಸನಿಗೆ ತಿಳಿಸಿದರು. ನಾತಾನನು ಅರಸನ ಮುಂದೆ ಹೋಗಿ ಸಾಷ್ಟಾಂಗ ನಮಸ್ಕಾರ ಮಾಡಿದನು.",
			"24": "<sup>24</sup> “ನನ್ನ ಒಡೆಯರೂ ಅರಸರೂ ಆದ ನೀವು ಅದೋನೀಯನಿಗೆ ಅರಸನಾಗುವುದಕ್ಕೂ ನಿಮ್ಮ ಸಿಂಹಾಸನದ ಮೇಲೆ ಕುಳಿತುಕೊಳ್ಳುವುದಕ್ಕೂ ಅಪ್ಪಣೆಕೊಟ್ಟಿರುವಂತೆ ಕಾಣುತ್ತದೆ.",
			"25": "<sup>25</sup> ಅವನು ಇಲ್ಲಿಂದ ಇಳಿದುಹೋಗಿ ಹೋರಿ, ಕುರಿ ಮುಂತಾದ ಹಲವು ಕೊಬ್ಬಿದ ಪಶುಗಳನ್ನು ಬಲಿಯಾಗಿ ಅರ್ಪಿಸಿದ್ದಾನೆ. ರಾಜಪುತ್ರರೆಲ್ಲರನ್ನು, ಸೇನಾಪತಿಗಳನ್ನು ಹಾಗು ಯಾಜಕ ಎಬ್ಯಾತಾರನನ್ನು ಔತಣಕ್ಕೆ ಕರೆದಿದ್ದಾನೆ. ಅವರು ಅವನ ಪಂಕ್ತಿಯಲ್ಲೇ ಅನ್ನಪಾನಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಅರಸನಾದ ಅದೋನೀಯನು ಚಿರಂಜೀವಿಯಾಗಿ ಇರಲಿ!’ ಎಂದು ಕೂಗುತ್ತಿದ್ದಾರೆ.",
			"26": "<sup>26</sup> ಆದರೆ ಅವನು ನಿಮ್ಮ ಸೇವಕನಾದ ನನ್ನನ್ನು, ಯಾಜಕ ಚಾದೋಕ್, ಯೆಹೋಯಾದಾವನ ಮಗ ಬೆನಾಯ ಎಂಬವರನ್ನು ಹಾಗು ನಿಮ್ಮ ಸೇವಕ ಸೊಲೊಮೋನನನ್ನು ಔತಣಕ್ಕೆ ಕರೆಯಲಿಲ್ಲ.",
			"27": "<sup>27</sup> ಈ ಕಾರ್ಯ ನಮ್ಮ ಒಡೆಯರೂ ಅರಸರೂ ಆದ ನಿಮ್ಮಿಂದಲೇ ನಡೆದಿರುವುದಾದರೆ, ನಿಮ್ಮ ತರುವಾಯ ಸಿಂಹಾಸನದ ಮೇಲೆ ಕುಳಿತುಕೊಳ್ಳತಕ್ಕವರು ಯಾರೆಂಬುದನ್ನು ನಿಮ್ಮ ಸೇವಕನಾದ ನನಗೆ ತಿಳಿಸಬಾರದಾಗಿತ್ತೇ?” ಎಂದು ಹೇಳಿದನು.",
			"28": "<sup>28</sup> ಅರಸ ದಾವೀದನು ಬತ್ಷೆಬೆಯನ್ನು ಕರೆಯಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿದನು. ಆಕೆ ಬಂದು ಅರಸನ ಮುಂದೆ ನಿಂತಳು. ಅರಸ ಆಕೆಗೆ,",
			"29": "<sup>29</sup> “ನನ್ನ ಪ್ರಾಣವನ್ನು ಎಲ್ಲಾ ಇಕ್ಕಟ್ಟುಗಳಿಂದ ಬಿಡಿಸಿ ಕಾಪಾಡಿದ ಸರ್ವೇಶ್ವರನಾಣೆ,",
			"30": "<sup>30</sup> ನನ್ನ ತರುವಾಯ ನನ್ನ ಮಗ ಸೊಲೊಮೋನನೇ ಅರಸನಾಗಿ ನನ್ನ ಸಿಂಹಾಸನದ ಮೇಲೆ ಕುಳಿತುಕೊಳ್ಳಬೇಕು. ನಾನು ನಿನಗೆ ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಹೆಸರಿನಲ್ಲಿ ಮಾಡಿದ ಈ ಪ್ರಮಾಣವನ್ನು ಈ ದಿನವೇ ನೆರವೇರಿಸುತ್ತೇನೆ,” ಎಂದನು.",
			"31": "<sup>31</sup> ಆಗ ಬತ್ಷೆಬೆ ಅರಸನಿಗೆ ಸಾಷ್ಟಾಂಗ ನಮಸ್ಕಾರ ಮಾಡಿದಳು, “ನನ್ನೊಡೆಯರಾದ ದಾವೀದರಸರು ಸುದೀರ್ಘಕಾಲ ಬಾಳಲಿ!” ಎಂದಳು.",
			"32": "<sup>32</sup> ಅನಂತರ ಅರಸ ದಾವೀದನು ತನ್ನ ಸೇವಕರಿಗೆ, “ಯಾಜಕ ಚಾದೋಕ್, ಪ್ರವಾದಿ ನಾತಾನ್ ಹಾಗು ಯೆಹೋಯಾದಾವನ ಮಗ ಬೆನಾಯ ಇವರನ್ನು ಕರೆಯಿರಿ,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"33": "<sup>33</sup> ಅವರು ಸನ್ನಿಧಿಗೆ ಬರಲು ಅರಸನು ಅವರಿಗೆ, “ನೀವು ನನ್ನ ಸೇವಕರನ್ನು ಕರೆದುಕೊಂಡು, ನನ್ನ ಮಗ ಸೊಲೊಮೋನನನ್ನು ನನ್ನ ಹೇಸರಗತ್ತೆಯ ಮೇಲೆ ಕುಳ್ಳಿರಿಸಿಕೊಂಡು, ಗೀಹೋನ್ ಬುಗ್ಗೆಗೆ ಹೋಗಿರಿ.",
			"34": "<sup>34</sup> ಅಲ್ಲಿ ಯಾಜಕ ಚಾದೋಕನು ಹಾಗು ಪ್ರವಾದಿ ನಾತಾನನು ಅವನನ್ನು ಇಸ್ರಯೇಲರ ಅರಸನನ್ನಾಗಿ ಅಭಿಷೇಕಿಸಲಿ. ಅನಂತರ ಕೊಂಬನ್ನೂದಿರಿ. ಎಲ್ಲರು ‘ಅರಸ ಸೊಲೊಮೋನನು ಚಿರಂಜೀವಿಯಾಗಿರಲಿ!’ ಎಂದು ಘೋಷಿಸಲಿ.",
			"35": "<sup>35</sup> ಆಮೇಲೆ ನೀವು ಅವನ ಹಿಂದೆ ಬನ್ನಿ; ಅವನು ಅರಮನೆಯನ್ನು ಪ್ರವೇಶಿಸಿ ನನ್ನ ಸಿಂಹಾಸನದ ಮೇಲೆ ಕುಳಿತುಕೊಂಡು ನನಗೆ ಬದಲಾಗಿ ಆಳಬೇಕು. ನಾನು ಇಸ್ರಯೇಲರ ಮತ್ತು ಯೆಹೂದ್ಯರ ಮೇಲೆ ಅವನನ್ನೇ ಅಧಿಪತಿಯನ್ನಾಗಿ ನೇಮಿಸಿದ್ದೇನೆ,” ಎಂದನು.",
			"36": "<sup>36</sup> ಯೆಹೋಯಾದಾವನ ಮಗ ಬೆನಾಯನು ಅರಸನಿಗೆ, “ಹಾಗೆಯೇ ಆಗಲಿ; ನನ್ನ ಒಡೆಯರ ಹಾಗು ಅರಸರ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಇದನ್ನು ಸ್ಥಿರಪಡಿಸಲಿ.",
			"37": "<sup>37</sup> ಸರ್ವೇಶ್ವರಸ್ವಾಮಿ ನನ್ನ ಒಡೆಯರೂ ಅರಸರೂ ಆದ ನಿಮ್ಮ ಸಂಗಡ ಇದ್ದ ಹಾಗೆ ಸೊಲೊಮೋನನ ಸಂಗಡವೂ ಇದ್ದು, ರಾಜ್ಯವನ್ನು ನನ್ನ ಒಡೆಯರೂ ಅರಸರೂ ಆದ ನಿಮ್ಮ ಕಾಲದಲ್ಲಿ ಇದ್ದುದಕ್ಕಿಂತ ಹೆಚ್ಚು ಅಭಿವೃದ್ಧಿಮಾಡಲಿ!” ಎಂದು ನುಡಿದನು.",
			"38": "<sup>38</sup> ಆಗ ಯಾಜಕ ಚಾದೋಕ್, ಪ್ರವಾದಿ ನಾತಾನ್ ಹಾಗು ಯೆಹೋಯಾದಾವನ ಮಗ ಬೆನಾಯ ಎಂಬವರು ‘ಕೆರೇತ್ಯ’, ‘ಪೆಲೇತ್ಯ’ ಎಂಬ ಕಾವಲುದಂಡುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು, ಸೊಲೊಮೋನನನ್ನು ಅರಸ ದಾವೀದನ ಹೇಸರಗತ್ತೆಯ ಮೇಲೆ ಕುಳ್ಳಿರಿಸಿ, ಗೀಹೋನಿಗೆ ಕರೆದುಕೊಂಡು ಹೋದರು.",
			"39": "<sup>39</sup> ಯಾಜಕ ಚಾದೋಕನು ದೇವದರ್ಶನದ ಗುಡಾರದಲ್ಲಿದ್ದ ಎಣ್ಣೆಯ ಕೊಂಬನ್ನು ತೆಗೆದುಕೊಂಡು ಸೊಲೊಮೋನನನ್ನು ಅಭಿಷೇಕಿಸಿದನು. ಕೂಡಲೆ ಕೊಂಬನ್ನೂದುತ್ತಾ ಜನರೆಲ್ಲರು, “ಅರಸನಾದ ಸೊಲೊಮೋನನು ಚಿರಂಜೀವಿಯಾಗಿರಲಿ!” ಎಂದು ಘೋಷಿಸಿದರು.",
			"40": "<sup>40</sup> ತರುವಾಯ ಅವರೆಲ್ಲರು ಅವನನ್ನು ಹಿಂಬಾಲಿಸಿ, ಕೊಳಲೂದುತ್ತಾ ಹರ್ಷಾನಂದದಿಂದ ಆರ್ಭಟಿಸುತ್ತಾ, ಹಿಂದಿರುಗಿ ಬಂದರು. ಅವರ ಜಯಕಾರದ ಕೂಗು ಭೂಮಿಯನ್ನೇ ನಡುಗಿಸುವಂತಿತ್ತು!",
			"41": "<sup>41</sup> ಅದೋನೀಯನೂ ಅವನ ಸಂಗಡ ಇದ್ದ ಅತಿಥಿಗಳೂ ಭೋಜನವನ್ನು ಮುಗಿಸುವಷ್ಟರಲ್ಲೇ ಅವರಿಗೆ ಈ ಆರ್ಭಟ ಕೇಳಿಸಿತು. ಕೊಂಬಿನ ಧ್ವನಿ ಕೇಳಿದಾಗ ಯೋವಾಬನು, “ಪಟ್ಟಣದಲ್ಲಿ ಇಂಥ ಗದ್ದಲವೇಕೆ?” ಎಂದು ವಿಚಾರಿಸುತ್ತಿರುವಾಗಲೇ",
			"42": "<sup>42</sup> ಯಾಜಕ ಎಬ್ಯಾತಾರನ ಮಗ ಯೋನಾತಾನನು ಅಲ್ಲಿಗೆ ಬಂದನು. ಅದೋನೀಯನು ಅವನಿಗೆ, “ನಿನ್ನಂಥ ಯೋಗ್ಯಪುರುಷ ಶುಭಸಮಾಚಾರವನ್ನೇ ತಂದಿರಬೇಕು, ಒಳಗೆ ಬಾ,” ಎಂದನು.",
			"43": "<sup>43</sup> ಯೋನಾತಾನನು, “ಶುಭಸಮಾಚಾರವಲ್ಲ! ನಮ್ಮೊಡೆಯನೂ ಅರಸನೂ ಆದ ದಾವೀದನು ಸೊಲೊಮೋನನನ್ನು ಅರಸನನ್ನಾಗಿ ಮಾಡಿದ್ದಾನೆ.",
			"44": "<sup>44</sup> ಯಾಜಕ ಚಾದೋಕ್, ಪ್ರವಾದಿ ನಾತಾನ್ ಹಾಗು ಯೆಹೋಯಾದಾವನ ಮಗ ಬೆನಾಯ ಇವರನ್ನೂ ಕೆರೇತ್ಯ-ಪೆಲೇತ್ಯರನ್ನೂ ಅರಸರು ಅವನ ಜೊತೆಯಲ್ಲಿ ಕಳುಹಿಸಿದ್ದಾರೆ. ಅವರು ಅವನನ್ನು ಅರಸನ ಹೇಸರಗತ್ತೆಯ ಮೇಲೆ ಕುಳ್ಳಿರಿಸಿದ್ದಾರೆ.",
			"45": "<sup>45</sup> ಯಾಜಕ ಚಾದೋಕನೂ ಪ್ರವಾದಿ ನಾತಾನನೂ ಗೀಹೋನಿನ ಬಳಿಯಲ್ಲಿ ಅವನಿಗೆ ರಾಜಾಭಿಷೇಕ ಮಾಡಿದ್ದಾರೆ. ಅಲ್ಲಿಂದ ಅವರು ಹರ್ಷಾನಂದದಿಂದ ಆರ್ಭಟಿಸುತ್ತಾ ಪಟ್ಟಣಕ್ಕೆ ಬಂದಿದ್ದಾರೆ. ಇದರಿಂದಲೇ ಪಟ್ಟಣದಲ್ಲಿ ಗದ್ದಲವುಂಟಾಗಿದೆ; ನೀವು ಕೇಳಿದ ಆರ್ಭಟವು ಇದೇ.",
			"46": "<sup>46</sup> ಇದಲ್ಲದೆ, ಸೊಲೊಮೋನನು ರಾಜಸಿಂಹಾಸನದ ಮೇಲೆ ಕುಳಿತುಕೊಂಡಿದ್ದಾನೆ.",
			"47": "<sup>47</sup> ಅರಸ ದಾವೀದನ ಸೇವಕರು ನಮ್ಮ ಒಡೆಯನೂ ಅರಸನೂ ಆದ ದಾವೀದನ ಮುಂದೆ ಬಂದು, ‘ನಿಮ್ಮ ದೇವರು ನಿಮ್ಮ ಹೆಸರಿಗಿಂತಲೂ ಸೊಲೊಮೋನನ ಹೆಸರನ್ನು ಪ್ರಸಿದ್ಧಿಗೆ ತರಲಿ! ರಾಜ್ಯವನ್ನು ನಿಮ್ಮ ಕಾಲದಲ್ಲಿ ಇದ್ದುದಕ್ಕಿಂತ ಅವನ ಕಾಲದಲ್ಲಿ ಹೆಚ್ಚು ಅಭಿವೃದ್ಧಿಗೊಳಿಸಲಿ!’ ಎಂದು ಅವನನ್ನು ಹರಸಿದ್ದಾರೆ. ಅರಸನು ತನ್ನ ಹಾಸಿಗೆಯ ಮೇಲೆ ಬಿದ್ದುಕೊಂಡು,",
			"48": "<sup>48</sup> ‘ನಾನು ಕಣ್ಣಾರೆ ಕಾಣುವಂತೆ ಇಂದು ನನ್ನ ಸಿಂಹಾಸನಕ್ಕೆ ಒಬ್ಬ ಉತ್ತರಾಧಿಕಾರಿಯನ್ನು ದಯಪಾಲಿಸಿದ ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ಸ್ತೋತ್ರವಾಗಲಿ!’ ಎಂದು ಹೇಳಿದ್ದಾನೆ,” ಎಂದನು.",
			"49": "<sup>49</sup> ಆಗ ಅದೋನೀಯನಿಂದ ಔತಣಕ್ಕೆ ಆಮಂತ್ರಿತರಾಗಿದ್ದವರೆಲ್ಲರು ಭಯಪಟ್ಟು ತಮ್ಮ ತಮ್ಮ ಮನೆಗಳಿಗೆ ಹೊರಟುಹೋದರು.",
			"50": "<sup>50</sup> ಅದೋನೀಯನು ಸೊಲೊಮೋನನಿಗೆ ಹೆದರಿ, ಓಡಿಹೋಗಿ ಬಲಿಪೀಠದ ಕೊಂಬುಗಳನ್ನು ಹಿಡಿದನು.",
			"51": "<sup>51</sup> ದೂತರು ಬಂದು ಸೊಲೊಮೋನನಿಗೆ, “ಅದೋನೀಯನು ನಿನಗೆ ಹೆದರಿ ಬಲಿಪೀಠದ ಕೊಂಬುಗಳನ್ನು ಹಿಡಿದುಕೊಂಡಿದ್ದಾನೆ. ಅರಸ ಸೊಲೊಮೋನನು ನನ್ನನ್ನು ಕತ್ತಿಯಿಂದ ಸಂಹರಿಸುವುದಿಲ್ಲವೆಂದು ಈ ದಿನ ನನಗೆ ಪ್ರಮಾಣ ಮಾಡಬೇಕೆಂದು ಬೇಡಿಕೊಳ್ಳುತ್ತಾನೆ,” ಎಂದು ತಿಳಿಸಿದರು.",
			"52": "<sup>52</sup> ಆಗ ಸೊಲೊಮೋನನು, “ಅದೋನೀಯನು ಸತ್ಪುರುಷನಾಗಿ ನಡೆಯುವುದಾದರೆ ಅವನ ಕೂದಲುಗಳಲ್ಲಿ ಒಂದನ್ನೂ ನೆಲಕ್ಕೆ ಬೀಳಗೊಡಿಸುವುದಿಲ್ಲ; ಆದರೆ ಅವನಲ್ಲಿ ಕೆಟ್ಟತನ ಕಂಡುಬರುವುದಾದರೆ, ಅವನು ಖಂಡಿತವಾಗಿ ಸಾಯುವನು,” ಎಂದು ಹೇಳಿದನು.",
			"53": "<sup>53</sup> ಆಳುಗಳನ್ನು ಕಳುಹಿಸಿ ಅವನನ್ನು ಬಲಿಪೀಠದ ಬಳಿಯಿಂದ ಕರೆದುತರುವಂತೆ ಮಾಡಿದನು. ಅವನು ಬಂದು, ಅರಸ ಸೊಲೊಮೋನನಿಗೆ ಸಾಷ್ಟಾಂಗ ನಮಸ್ಕಾರ ಮಾಡಿದನು. ಸೊಲೊಮೋನನು ಅವನಿಗೆ ಮನೆಗೆ ಹೋಗಲು ಅಪ್ಪಣೆಕೊಟ್ಟನು."
		},
		"2": {
			"1": "<sup>1</sup> ದಾವೀದನ ಮರಣಸಮಯ ಸಮೀಪಿಸಿದಾಗ ಅವನು ತನ್ನ ಮಗ ಸೊಲೊಮೋನನಿಗೆ ಹೀಗೆಂದು ಆಜ್ಞಾಪಿಸಿದನು:",
			"2": "<sup>2</sup> “ಜಗದ ಜನರೆಲ್ಲರು ಹೋಗುವ ದಾರಿಯನ್ನು ನಾನು ಈಗ ಹಿಡಿಯಬೇಕು; ನೀನು ಧೈರ್ಯದಿಂದಿರು. ನಿನ್ನ ಸಾಮರ್ಥ್ಯವನ್ನು ತೋರಿಸು.",
			"3": "<sup>3</sup> ನಿನ್ನ ದೇವರಾದ ಸರ್ವೇಶ್ವರಸ್ವಾಮಿಯ ಕಟ್ಟಳೆಯನ್ನು ಕೈಗೊಂಡು ಅವರ ಮಾರ್ಗದಲ್ಲೇ ನಡೆದುಕೋ; ಮೋಶೆಯ ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿ ಬರೆದಿರುವ ಅವರ ಆಜ್ಞಾನಿಯಮ-ವಿಧಿನಿರ್ಣಯಗಳನ್ನು ಪಾಲಿಸು. ಹೀಗೆ ಮಾಡುವುದಾದರೆ ನೀನು ಏನು ಮಾಡಿದರೂ ಎಲ್ಲಿಗೆ ಹೋದರೂ ಕೃತಾರ್ಥನಾಗುವೆ.",
			"4": "<sup>4</sup> ಇದಲ್ಲದೆ ಸರ್ವೇಶ್ವರ, ‘ನಿನ್ನ ಸಂತಾನದವರು ಪೂರ್ಣಮನಸ್ಸಿನಿಂದಲೂ ಪೂರ್ಣಪ್ರಾಣದಿಂದಲೂ ನನಗೆ ನಂಬಿಗಸ್ತರಾಗಿ ನಡೆದುಕೊಳ್ಳುವುದರಲ್ಲಿ ಜಾಗರೂಕರಾಗಿದ್ದರೆ ಅವರು ಇಸ್ರಯೇಲ್ ಸಿಂಹಾಸನದ ಮೇಲೆ ಕುಳಿತುಕೊಳ್ಳುವುದು ತಪ್ಪದು,’ ಎಂದು ನನಗೆ ಮಾಡಿದ ವಾಗ್ದಾನವನ್ನು ಅವರು ಸ್ಥಿರಪಡಿಸುವರು.",
			"5": "<sup>5</sup> “ಚೆರೂಯಳ ಮಗ ಯೋವಾಬನು ನನಗೆ ಮಾಡಿರುವುದನ್ನು ನೀನು ಬಲ್ಲೆ; ಅವನು ಇಬ್ಬರು ಇಸ್ರಯೇಲ್ ಸೇನಾಪತಿಗಳನ್ನು ಅಂದರೆ, ನೇರನ ಮಗ ಅಬ್ನೇರನನ್ನೂಯೆತೆರನ ಮಗ ಅಮಾಸನನ್ನೂ ಕೊಂದುಹಾಕಿದನು. ಯುದ್ಧಕಾಲದಲ್ಲಿಯೋ ಎಂಬಂತೆ ಸಮಾಧಾನಕಾಲದಲ್ಲಿಯೂ ರಕ್ತಸುರಿಸಿ ಆ ಕಲೆಯನ್ನು ತನ್ನ ನಡುಕಟ್ಟಿಗೂ ಪಾದರಕ್ಷೆಗಳಿಗೂ ಹಚ್ಚಿಕೊಂಡನು.",
			"6": "<sup>6</sup> ಅವನಿಗೇನು ಮಾಡಬೇಕೆಂಬುದು ಬುದ್ಧಿವಂತನಾದ ನಿನಗೆ ಗೊತ್ತಿದೆ; ಅವನ ಮುದಿತಲೆ ಸಮಾಧಿಯನ್ನು ಸಮಾಧಾನದಿಂದ ಸೇರದಂತೆ ಮಾಡು.",
			"7": "<sup>7</sup> “ಆದರೆ ಗಿಲ್ಯಾದ್ಯನಾದ ಬರ್ಜಿಲ್ಲೈಯ ಮಕ್ಕಳಿಗೆ ಉಪಕಾರಮಾಡು; ಅವರು ದಿನನಿತ್ಯವೂ ನಿನ್ನ ಪಂಕ್ತಿಯಲ್ಲಿ ಊಟಮಾಡುತ್ತಿರಬೇಕು. ಏಕೆಂದರೆ ನಾನು ನಿನ್ನ ಸಹೋದರ ಅಬ್ಷಾಲೋಮನಿಂದ ತಪ್ಪಿಸಿಕೊಂಡು ಓಡಿಹೋದಾಗ ಅವರು ನನ್ನನ್ನು ಉಪಚರಿಸಿದರು.",
			"8": "<sup>8</sup> ಬೆನ್ಯಾಮೀನ್ಯನಾದ ಗೇರನ ಮಗನೂ ಬಹುರೀಮ್ ಊರಿನವನೂ ಆದ ಶಿಮ್ಮಿ ನಿನ್ನ ಬಳಿ ಇರುತ್ತಾನೆ; ನಾನು ಮಹನಯಿಮಿಗೆ ಹೋದಾಗ ಅವನು ನನ್ನನ್ನು ಬಹುಕ್ರೂರವಾಗಿ ಶಪಿಸಿದನು; ಆದರೂ ಅವನು ನನ್ನನ್ನು ಎದುರುಗೊಳ್ಳುವುದಕ್ಕೆ ಜೋರ್ಡನಿಗೆ ಬಂದಾಗ ನಾನು ಅವನಿಗೆ, ‘ನಿನ್ನನ್ನು ಕೊಲ್ಲುವುದಿಲ್ಲ,’ ಎಂಬುದಾಗಿ ಸರ್ವೇಶ್ವರನ ಹೆಸರಿನಲ್ಲಿ ಪ್ರಮಾಣಮಾಡಿದೆ.",
			"9": "<sup>9</sup> ನೀನಾದರೋ ಅವನನ್ನು ಶಿಕ್ಷಿಸದೆ ಬಿಡಬೇಡ; ನೀನು ಬುದ್ಧಿವಂತ; ಅವನ ಮುದಿತಲೆ ರಕ್ತಮಯವಾಗಿ ಸಮಾಧಿ ಸೇರುವಂತೆ ಏನು ಮಾಡಬೇಕೆಂದು ನಿನಗೆ ಗೊತ್ತಿದೆ.”",
			"10": "<sup>10</sup> ಅನಂತರ ದಾವೀದನು ಮರಣಹೊಂದಿ ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಿದನು. ಅವನ ಶವವನ್ನು ದಾವೀದನಗರದಲ್ಲಿ ಸಮಾಧಿ ಮಾಡಿದರು.",
			"11": "<sup>11</sup> ದಾವೀದನು ಇಸ್ರಯೇಲರನ್ನು ಆಳಿದ್ದು ನಾಲ್ವತ್ತು ವರ್ಷ; ಹೆಬ್ರೋನಿನಲ್ಲಿ ಏಳು ವರ್ಷ, ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಮೂವತ್ತಮೂರು ವರ್ಷ ಆಳಿದನು.",
			"12": "<sup>12</sup> ಸೊಲೊಮೋನನು ತನ್ನ ತಂದೆ ದಾವೀದನ ಸಿಂಹಾಸನವನ್ನು ಏರಿದನು. ಅವನ ರಾಜ್ಯ ಸಮೃದ್ಧಿಯಾಗಿ ಬೆಳೆಯಿತು.",
			"13": "<sup>13</sup> ಹಗ್ಗೀತಳ ಮಗ ಅದೋನೀಯನು ಸೊಲೊಮೋನನ ತಾಯಿಯಾದ ಬತ್ಷೆಬೆಯ ದರ್ಶನಕ್ಕೆ ಬಂದನು. ಆಕೆ ಅವನನ್ನು, “ನೀನು ಸಮಾಧಾನದಿಂದ ಬಂದಿರುತ್ತೀಯೋ?” ಎಂದು ಕೇಳಿದಳು. ಅವನು, “ಹೌದು, ಸಮಾಧಾನದಿಂದ ಬಂದಿದ್ದೇನೆ,” ಎಂದನು.",
			"14": "<sup>14</sup> ಆಮೇಲೆ ಅವನು, “ನಿಮಗೊಂದು ಮಾತು ಹೇಳಬೇಕೆಂದು ಬಂದಿದ್ದೇನೆ,” ಎನ್ನಲು ಆಕೆ, “ಹೇಳು,” ಎಂದಳು.",
			"15": "<sup>15</sup> ಆಗ ಅವನು, “ರಾಜ್ಯ ನನಗೆ ಬರಬೇಕಾಗಿತ್ತು; ನಾನೇ ಅರಸನಾಗುವೆನೆಂದು ಇಸ್ರಯೇಲರೆಲ್ಲರು ಎದುರು ನೋಡುತ್ತಿದ್ದರೆಂಬುದು ನಿನಗೆ ತಿಳಿದ ವಿಷಯ. ಆದರೆ ಅದು ತಪ್ಪಿ ನನ್ನ ತಮ್ಮನಿಗೆ ಹೋಯಿತು. ಅದು ಸರ್ವೇಶ್ವರನಿಂದಲೇ ಅವನಿಗೆ ದೊರಕಿತು.",
			"16": "<sup>16</sup> ಅದಿರಲಿ, ನನ್ನದೊಂದು ಬಿನ್ನಹವಿದೆ; ಅದನ್ನು ತಿರಸ್ಕರಿಸಬೇಡಿ,” ಎಂದನು. ಅದಕ್ಕೆ ಬತ್ಷೆಬೆ, “ಅದೇನು ಹೇಳು,” ಎಂದಳು.",
			"17": "<sup>17</sup> ಅವನು, “ದಯವಿಟ್ಟು ಶೂನೇಮ್ಯಳಾದ ಅಬೀಷಗಳನ್ನು ನನಗೆ ಹೆಂಡತಿಯನ್ನಾಗಿ ಕೊಡಬೇಕೆಂದು ಅರಸ ಸೊಲೊಮೋನನಿಗೆ ಹೇಳಿ; ಅವನು ನಿಮ್ಮ ಮಾತನ್ನು ತಳ್ಳುವುದಿಲ್ಲ,” ಎಂದನು.",
			"18": "<sup>18</sup> ಬತ್ಷೆಬೆ, “ಒಳ್ಳೇದು, ನಾನು ನಿನ್ನ ಪರವಾಗಿ ಸನ್ನಿಧಿಯಲ್ಲಿ ಅರಿಕೆಮಾಡುತ್ತೇನೆ,” ಎಂದು ಹೇಳಿದಳು.",
			"19": "<sup>19</sup> ಆಕೆ ಅದೋನೀಯನ ಪರವಾಗಿ ಮಾತಾಡಲು ಅರಸ ಸೊಲೊಮೋನನ ಬಳಿಗೆ ಬರುತ್ತಿರುವಾಗ ಅರಸನೇ ಎದ್ದುಹೋಗಿ ಆಕೆಯನ್ನು ಎದುರುಗೊಂಡು ನಮಸ್ಕರಿಸಿದನು. ಅನಂತರ ತಾನು ಸಿಂಹಾಸನದ ಮೇಲೆ ಕುಳಿತು ರಾಜಮಾತೆಗಾಗಿ ಒಂದು ಆಸನವನ್ನು ತರಿಸಿದನು. ಆಕೆ ಅವನ ಬಲಗಡೆಯಲ್ಲಿ ಕುಳಿತುಕೊಂಡಳು.",
			"20": "<sup>20</sup> “ಒಂದು ಸಣ್ಣ ಬಿನ್ನಹವಿದೆ, ತಿರಸ್ಕರಿಸಬೇಡ,” ಎಂದು ಹೇಳಿದಳು. ಅರಸನು, “ತಾಯೇ, ಏನು ಬೇಕು, ಕೇಳಿ; ನಾನು ಆಗದು ಎನ್ನುವುದಿಲ್ಲ,” ಎಂದನು.",
			"21": "<sup>21</sup> ಆಕೆ, “ಶೂನೇಮ್ಯಳಾದ ಅಬೀಷಗಳು ನಿನ್ನ ಅಣ್ಣ ಅದೋನೀಯನಿಗೆ ಹೆಂಡತಿಯಾಗಲು ಅಪ್ಪಣೆಯಾಗಲಿ,” ಎಂದಳು.",
			"22": "<sup>22</sup> ಆಗ ಅರಸ ಸೊಲೊಮೋನನು ತನ್ನ ತಾಯಿಗೆ, “ಅದೋನೀಯನಿಗಾಗಿ ಶೂನೇಮ್ಯಳಾದ ಅಬೀಷಗಳನ್ನು ಮಾತ್ರ ಕೇಳುವುದೇಕೆ? ಅವನಿಗೆ ರಾಜ್ಯವನ್ನೂ ಕೊಡಬೇಕೆಂದು ಕೇಳು; ಅವನು ನನ್ನ ಅಣ್ಣನಲ್ಲವೇ? ಯಾಜಕ ಎಬ್ಯಾತಾರನೂ ಚೆರೂಯಳ ಮಗ ಯೋವಾಬನೂ ಅವನ ಪಕ್ಷದವರಾಗಿದ್ದಾರೆ,” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"23": "<sup>23</sup> ಇದಲ್ಲದೆ, ಅರಸ ಸೊಲೊಮೋನನು, “ಸರ್ವೇಶ್ವರನಾಣೆ, ಅದೋನೀಯನ ಈ ಮಾತಿನ ನಿಮಿತ್ತ ನಾನು ಅವನನ್ನು ಮರಣಕ್ಕೆ ಪಾತ್ರನೆಂದು ಎಣಿಸದಿದ್ದರೆ ದೇವರು ನನಗೆ ಬೇಕಾದುದನ್ನು ಮಾಡಲಿ!",
			"24": "<sup>24</sup> ನನ್ನ ತಂದೆ ದಾವೀದನ ಸಿಂಹಾಸನದ ಮೇಲೆ ನನ್ನನ್ನು ಕುಳ್ಳಿರಿಸಿ, ಅಭಿವೃದ್ಧಿಗೊಳಿಸಿ, ತಾವು ವಾಗ್ದಾನ ಮಾಡಿದಂತೆ ನನಗೆ ಮನೆತನವನ್ನು ಕಟ್ಟಿದ ಸರ್ವೇಶ್ವರನಾಣೆ, ಅದೋನೀಯನು ಈ ದಿನವೇ ಸಾಯಬೇಕು,” ಎಂದು ಹೇಳಿದನು.",
			"25": "<sup>25</sup> ಅಂತೆಯೇ ಯೆಹೋಯಾದಾವನ ಮಗ ಬೆನಾಯನಿಗೆ ಆಜ್ಞಾಪಿಸಿದನು. ಅವನು ಹೋಗಿ ಅದೋನೀಯನನ್ನು ಹೊಡೆದು ಕೊಂದುಹಾಕಿದನು.",
			"26": "<sup>26</sup> ಬಳಿಕ ಅರಸ ಸೊಲೊಮೋನನು ಯಾಜಕನಾದ ಎಬ್ಯಾತಾರನಿಗೆ, “ನೀನು ಅಣತೋತಿನಲ್ಲಿರುವ ನಿನ್ನ ಮನೆಗೆ ಹೋಗು; ನೀನು ಮರಣಕ್ಕೆ ಪಾತ್ರನು. ಆದರೆ ನೀನು ಸ್ವಾಮಿ ಸರ್ವೇಶ್ವರನ ಮಂಜೂಷವನ್ನು ಹೊತ್ತುಕೊಂಡು ನನ್ನ ತಂದೆ ದಾವೀದನೊಡನೆ ಸಂಚರಿಸುತ್ತಾ ಅವರ ಕಷ್ಟ-ದುಃಖಗಳಲ್ಲಿ ಪಾಲುಗಾರನಾಗಿದ್ದುದರಿಂದ",
			"27": "<sup>27</sup> ಈ ದಿನ ನಿನ್ನನ್ನು ಕೊಲ್ಲಿಸುವುದಿಲ್ಲ,” ಎಂದು ಹೇಳಿದನು. ಆದರೆ ಅವನನ್ನು ಸರ್ವೇಶ್ವರನ ಯಾಜಕವೃತ್ತಿಯಿಂದ ತೆಗೆದುಬಿಟ್ಟನು. ಹೀಗೆ ಸರ್ವೇಶ್ವರ ಶಿಲೋವಿನಲ್ಲಿದ್ದ ಏಲಿಯ ಮನೆಯನ್ನು ಕುರಿತು ಭವಿಷ್ಯ ಹೇಳಿದ ಮಾತು ನೆರವೇರಿತು.",
			"28": "<sup>28</sup> ಈ ಸುದ್ದಿ ಯೋವಾಬನಿಗೆ ಮುಟ್ಟಿತು. ಅವನು ಕೂಡಲೆ ಸರ್ವೇಶ್ವರನ ಗುಡಾರಕ್ಕೆ ಓಡಿಹೋಗಿ ಬಲಿಪೀಠದ ಕೊಂಬುಗಳನ್ನು ಹಿಡಿದುಕೊಂಡನು. (ಅವನು ಅಬ್ಷಾಲೋಮನ ವಿರೋಧವಾಗಿದ್ದರೂ ಅದೋನೀಯನಿಗೆ ಪರವಾಗಿದ್ದನು.)",
			"29": "<sup>29</sup> ಯೋವಾಬನು ಸರ್ವೇಶ್ವರನ ಗು಼ಡಾರಕ್ಕೆ ಓಡಿಹೋಗಿ ಬಲಿಪೀಠದ ಹತ್ತಿರ ಇದ್ದಾನೆ ಎಂಬ ಸುದ್ದಿ ಅರಸ ಸೊಲೊಮೋನನಿಗೆ ತಲುಪಿತು. ಅವನು ಯೆಹೋಯಾದಾವನ ಮಗ ಬೆನಾಯನಿಗೆ, “ಹೋಗಿ ಅವನನ್ನು ಕೊಂದುಹಾಕು,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"30": "<sup>30</sup> ಬೆನಾಯನು ಸರ್ವೇಶ್ವರನ ಗುಡಾರಕ್ಕೆ ಹೋಗಿ ಯೋವಾಬನಿಗೆ, “ನೀನು ಹೊರಗೆ ಬರಬೇಕೆಂದು ಅರಸರು ಆಜ್ಞಾಪಿಸುತ್ತಾರೆ,” ಎಂದನು. ಆದರೆ ಅವನು, “ಬರುವುದಿಲ್ಲ, ನಾನು ಇಲ್ಲಿಯೇ ಸಾಯುತ್ತೇನೆ,” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು. ಬೆನಾಯನು ಅರಸನ ಬಳಿಗೆ ಹೋಗಿ ಯೋವಾಬನು ಹೇಳಿದ್ದನ್ನು ತಿಳಿಸಿದನು.",
			"31": "<sup>31</sup> ಅರಸನು, “ಅವನು ಹೇಳಿದಂತೆ ಮಾಡು; ಅವನನ್ನು ಕೊಂದು ಅವನ ಶವವನ್ನು ಸಮಾಧಿಮಾಡು. ಯೋವಾಬನು ನಿಷ್ಕಾರಣವಾಗಿ ರಕ್ತಸುರಿಸಿದುದರಿಂದ ನನಗೂ ನನ್ನ ತಂದೆಯ ಮನೆಯವರಿಗೂ ಹತ್ತಿರುವ ದೋಷವನ್ನು ಈ ಪ್ರಕಾರ ಪರಿಹರಿಸು.",
			"32": "<sup>32</sup> ಸರ್ವೇಶ್ವರ ಈ ರಕ್ತ ಅಪರಾಧವನ್ನು ಅವನ ತಲೆಯ ಮೇಲೆಯೇ ಬರಮಾಡಲಿ; ಅವನು ನನ್ನ ತಂದೆ ದಾವೀದನಿಗೆ ತಿಳಿಯದೆ ತನಗಿಂತ ಉತ್ತಮರೂ ನೀತಿವಂತರೂ ಆದ ಇಬ್ಬರು ವ್ಯಕ್ತಿಗಳನ್ನು ಅಂದರೆ, ಇಸ್ರಯೇಲ್ ಸೇನಾಪತಿಯೂ ನೇರನ ಮಗನೂ ಆದ ಅಬ್ನೇರನನ್ನೂ ಯೆಹೂದ ಸೇನಾಪತಿಯೂ ಯೆತೆರನ ಮಗನೂ ಆದ ಅಮಾಸನನ್ನೂ ಕೊಂದನಲ್ಲವೆ?",
			"33": "<sup>33</sup> ಈ ಇಬ್ಬರನ್ನೂ ವಧಿಸಿದ ಪಾಪವು ಯೋವಾಬನ ತಲೆಯ ಮೇಲೂ ಅವನ ಸಂತಾನದವರ ತಲೆಯ ಮೇಲೂ ಇರಲಿ; ಆದರೆ ದಾವೀದನಿಗೂ ಅವನ ಸಿಂಹಾಸನವನ್ನೇರುವ ಸಂತಾನದವರಿಗೂ ಸರ್ವೇಶ್ವರನಿಂದ ನಿತ್ಯಸೌಭಾಗ್ಯ ದೊರಕಲಿ!” ಎಂದನು.",
			"34": "<sup>34</sup> ಯೆಹೋಯಾದಾವನ ಮಗ ಬೆನಾಯನು ಹೋಗಿ ಯೋವಾಬನನ್ನು ಕೊಂದನು. ಅವನ ಶವಕ್ಕೆ ಮರುಭೂಮಿಯಲ್ಲಿ ಇದ್ದ ಅವನ ಮನೆಯ ನಿವೇಶನದಲ್ಲಿ ಸಮಾಧಿಯಾಯಿತು.",
			"35": "<sup>35</sup> ಅರಸನು ಯೋವಾಬನಿಗೆ ಬದಲಾಗಿ ಯೆಹೋಯಾದಾವನ ಮಗ ಬೆನಾಯನನ್ನು ಸೈನ್ಯಾಧಿಪತಿಯನ್ನಾಗಿ ಹಾಗು ಎಬ್ಯಾತಾರನಿಗೆ ಬದಲಾಗಿ ಚಾದೋಕನನ್ನು ಯಾಜಕನನ್ನಾಗಿ ನೇಮಿಸಿದನು.",
			"36": "<sup>36</sup> ತರುವಾಯ ಅರಸನು ಶಿಮ್ಮಿಯನ್ನು ಕರೆಯಿಸಿ, “ನೀನು ಒಂದು ಮನೆಯನ್ನು ಕಟ್ಟಿಸಿಕೊಂಡು ಜೆರುಸಲೇಮಿನಲ್ಲೇ ವಾಸಿಸಬೇಕು. ಇದನ್ನು ಬಿಟ್ಟು ಎಲ್ಲಿಗೂ ಹೋಗಬಾರದು.",
			"37": "<sup>37</sup> ನೀನು ಇದನ್ನು ಬಿಟ್ಟು ಕಿದ್ರೋನ್ ಹಳ್ಳದ ಆಚೆಗೆ ಹೋದೆಯಾದರೆ, ಅದೇ ದಿವಸ ನಿನಗೆ ಮರಣಶಿಕ್ಷೆಯಾಗುವುದೆಂದು ತಿಳಿದುಕೋ; ಮತ್ತು ಆ ರಕ್ತಾಪರಾಧವು ನಿನ್ನ ತಲೆಯ ಮೇಲೆಯೇ ಇರುವುದು,” ಎಂದು ಹೇಳಿದನು.",
			"38": "<sup>38</sup> ಅದಕ್ಕೆ ಶಿಮ್ಮಿಯು, “ಒಳ್ಳೆಯದು; ನನ್ನ ಒಡೆಯರೂ ಅರಸರೂ ಆದ ನೀವು ಹೇಳಿದಂತೆಯೇ ಸೇವಕನಾದ ನಾನು ಮಾಡುವೆನು,” ಎಂದು ಉತ್ತರಕೊಟ್ಟು ಬಹಳ ದಿನಗಳವರೆಗೆ ಜೆರುಸಲೇಮಿನಲ್ಲೇ ವಾಸಿಸುತ್ತಿದ್ದನು.",
			"39": "<sup>39</sup> ಮೂರು ವರ್ಷಗಳಾದನಂತರ ಶಿಮ್ಮಿಯ ಇಬ್ಬರು ಗುಲಾಮರು, ಗತ್ ಊರಿನ ಅರಸನೂ ಮಾಕನ ಮಗನೂ ಆದ ಆಕೀಷನ ಬಳಿಗೆ ಓಡಿಹೋದರು. ಗುಲಾಮರು ಗತ್ ಊರಿನಲ್ಲಿ ಇರುವುದು ಶಿಮ್ಮಿಗೆ ಗೊತ್ತಾಯಿತು.",
			"40": "<sup>40</sup> ಅವನು ಕತ್ತೆಗೆ ತಡಿಹಾಕಿಸಿ ತನ್ನ ಗುಲಾಮರನ್ನು ಹುಡುಕುವುದಕ್ಕಾಗಿ ಗತ್ ಊರಿನ ಅರಸನಾದ ಆಕೀಷನ ಬಳಿಗೆ ಹೋಗಿ ಅವರನ್ನು ಗತ್ ಊರಿನಿಂದ ಹಿಡಿದುಕೊಂಡು ಬಂದನು.",
			"41": "<sup>41</sup> ಶಿಮ್ಮಿ ಜೆರುಸಲೇಮಿನಿಂದ ಗತ್ ಊರಿಗೆ ಹೋಗಿ ಬಂದದ್ದು ಅರಸ ಸೊಲೊಮೋನನಿಗೆ ತಿಳಿಯಿತು. ಅವನು ಶಿಮ್ಮಿಯನ್ನು ಕರೆಯಿಸಿ,",
			"42": "<sup>42</sup> “ನೀನು ಈ ಊರನ್ನು ಬಿಟ್ಟು ಎಲ್ಲಿಗಾದರು ಹೋದರೆ ಅದೇ ದಿನದಲ್ಲಿ ನಿನಗೆ ಮರಣಶಿಕ್ಷೆಯಾಗುವುದೆಂದು ಖಂಡಿತವಾಗಿ ಹೇಳಿ ಸರ್ವೇಶ್ವರನ ಹೆಸರಿನಲ್ಲಿ ನಿನ್ನಿಂದ ಪ್ರಮಾಣ ತೆಗೆದುಕೊಂಡನಲ್ಲವೆ? ಆಗ ನೀನು, ‘ಒಳ್ಳೆಯದು, ಹಾಗೆಯೇ ಮಾಡುತ್ತೇನೆ,’ ಎಂದು ಹೇಳಿದೆಯಲ್ಲವೆ?",
			"43": "<sup>43</sup> ಹಾಗಾದರೆ, ಸರ್ವೇಶ್ವರನ ಹೆಸರಿನಲ್ಲಿ ನೀನು ಮಾಡಿದ ಪ್ರಮಾಣವನ್ನೂ ನಾನು ನಿನಗೆ ಕೊಟ್ಟ ಅಪ್ಪಣೆಯನ್ನೂ ಏಕೆ ಕೈಗೊಳ್ಳಲಿಲ್ಲ?” ಎಂದನು.",
			"44": "<sup>44</sup> ಇದಲ್ಲದೆ, ಅರಸನು ಅವನಿಗೆ, “ನೀನು ನನ್ನ ತಂದೆ ದಾವೀದನಿಗೆ ವಿರುದ್ಧ ಮಾಡಿದ ಪಾಪಕ್ಕೆ ಮನವೇ ಸಾಕ್ಷಿ; ಸರ್ವೇಶ್ವರ ಅದರ ಫಲವನ್ನು ನಿನ್ನ ತಲೆಯ ಮೇಲೆ ಬರಮಾಡುವರು.",
			"45": "<sup>45</sup> ಆದರೆ ಸೊಲೊಮೋನ ರಾಜನಿಗೆ ಆಶೀರ್ವಾದ ಲಭಿಸಲಿ! ದಾವೀದನ ಸಿಂಹಾಸನ ಸರ್ವೇಶ್ವರನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸದಾಕಾಲವೂ ಇರಲಿ!” ಎಂದು ಹೇಳಿದನು.",
			"46": "<sup>46</sup> ಬಳಿಕ ಯೆಹೋಯಾದವನ ಮಗ ಬೆನಾಯನಿಗೆ ಅವನನ್ನು ಸಂಹರಿಸಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿದನು. ಅಂತೆಯೇ ಇವನು ಹೊರಗೆ ಹೋಗಿ ಅವನ ಮೇಲೆ ಬಿದ್ದು ಅವನನ್ನು ಕೊಂದುಹಾಕಿದನು. ಹೀಗೆ ಸೊಲೊಮೋನನ ರಾಜ್ಯಾಧಿಕಾರ ಸುಭದ್ರವಾಯಿತು."
		},
		"3": {
			"1": "<sup>1</sup> ಸೊಲೊಮೋನನು ಈಜಿಪ್ಟಿನ ಅರಸ ಫರೋಹನ ಮಗಳನ್ನು ಮದುವೆಮಾಡಿಕೊಂಡು ಅವನ ಅಳಿಯನಾದ. ತನ್ನ ಅರಮನೆಯನ್ನು, ಸರ್ವೇಶ್ವರನ ಮಹಾದೇವಾಲಯವನ್ನು ಹಾಗು ಜೆರುಸಲೇಮಿನ ಸುತ್ತಣ ಗೋಡೆಯನ್ನು ಕಟ್ಟಿಮುಗಿಸುವ ತನಕ ಆಕೆಯನ್ನು ದಾವೀದನಗರದಲ್ಲೇ ಇರಿಸಿಕೊಂಡನು.",
			"2": "<sup>2</sup> ಅಲ್ಲಿಯವರೆಗೂ ಸರ್ವೇಶ್ವರಸ್ವಾಮಿಯ ನಾಮಕ್ಕೋಸ್ಕರ ಆಲಯವೇ ಇರಲಿಲ್ಲ. ಆದುದರಿಂದ ಜನರು ಪೂಜಾಸ್ಥಳಗಳಲ್ಲಿ ಬಲಿಯರ್ಪಣೆ ಮಾಡುತ್ತಿದ್ದರು.",
			"3": "<sup>3</sup> ಸೊಲೊಮೋನನು ಸರ್ವೇಶ್ವರನನ್ನು ಪ್ರೀತಿಸಿ, ತನ್ನ ತಂದೆ ದಾವೀದನ ವಿಧಿಗಳನ್ನು ಕೈಗೊಳ್ಳುತ್ತಿದ್ದರೂ ಆ ಪೂಜಾಸ್ಥಳಗಳಲ್ಲೇ ಬಲಿಯರ್ಪಿಸುತ್ತಿದ್ದನು; ಅಲ್ಲಿಯೇ ಧೂಪಾರತಿ ಎತ್ತುತ್ತಿದ್ದನು.",
			"4": "<sup>4</sup> ಒಮ್ಮೆ ಅರಸ ಸೊಲೊಮೋನನು ಬಲಿಯರ್ಪಿಸುವುದಕ್ಕಾಗಿ ಪೂಜಾಸ್ಥಳಗಳಲ್ಲೇ ಪ್ರಾಮುಖ್ಯವಾದ ಗಿಬ್ಯೋನಿಗೆ ಹೋದನು. ಅಲ್ಲಿನ ಪೀಠದ ಮೇಲೆ ಸಹಸ್ರಬಲಿಗಳನ್ನು ಸಮರ್ಪಿಸಿದಾಗ,",
			"5": "<sup>5</sup> ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಆ ರಾತ್ರಿ ಕನಸಿನಲ್ಲಿ ಅವನಿಗೆ ಕಾಣಿಸಿಕೊಂಡರು; “ನಿನಗೆ ಯಾವ ವರಬೇಕು ಕೇಳಿಕೋ,” ಎಂದು ಹೇಳಿದರು.",
			"6": "<sup>6</sup> ಸೊಲೊಮೋನನು, “ನಿಮಗೆ ಪ್ರಾಮಾಣಿಕನಾಗಿ ನೀತಿಯಿಂದಲೂ ಯಥಾರ್ಥಚಿತ್ತದಿಂದಲೂ ನಡೆದುಕೊಂಡ ನಿಮ್ಮ ದಾಸನೂ ನನ್ನ ತಂದೆಯೂ ಆದ ದಾವೀದನಿಗೆ ನೀವು ಮಹಾಕೃಪೆಯನ್ನು ತೋರಿಸಿದಿರಿ; ಆ ಕೃಪೆಯನ್ನು ಮುಂದುವರಿಸುತ್ತಾ ಈಗ ಅವರ ಸಿಂಹಾಸನಕ್ಕೆ ಒಬ್ಬ ಮಗನನ್ನು ಅನುಗ್ರಹಿಸಿ ಅದನ್ನು ಸಂಪೂರ್ಣಗೊಳಿಸಿದ್ದೀರಿ.",
			"7": "<sup>7</sup> ನನ್ನ ದೇವರಾದ ಸರ್ವೇಶ್ವರಾ, ನನ್ನ ತಂದೆಗೆ ಬದಲಾಗಿ ನಿಮ್ಮಿಂದ ಅರಸನಾಗಿ ನೇಮಕಗೊಂಡಿರುವ ನಿಮ್ಮ ದಾಸನಾದ ನಾನು ಇನ್ನೂ ಚಿಕ್ಕವನು.",
			"8": "<sup>8</sup> ವ್ಯವಹಾರಜ್ಞಾನ ಇಲ್ಲದವನು; ನಿಮ್ಮ ದಾಸನಾದ ನಾನು ಅಸಂಖ್ಯಾತ ಮಹಾಜನಾಂಗವಾದ ನಿಮ್ಮ ಸ್ವಕೀಯ ಪ್ರಜೆಯ ಮಧ್ಯೆ ಇದ್ದೇನೆ.",
			"9": "<sup>9</sup> ಆದುದರಿಂದ ಅದನ್ನು ಆಳುವುದಕ್ಕೂ ನ್ಯಾಯಾನ್ಯಾಯಗಳನ್ನು ಕಂಡುಹಿಡಿಯುವುದಕ್ಕೂ ನನಗೆ ವಿವೇಕವನ್ನು ದಯಪಾಲಿಸಿರಿ. ಈ ಮಹಾಜನಾಂಗವನ್ನು ಆಳಲು ಯಾರೂ ಸಮರ್ಥರಲ್ಲ,” ಎಂದು ಬೇಡಿಕೊಂಡನು.",
			"10": "<sup>10</sup> ಸೊಲೊಮೋನನ ಈ ಬಿನ್ನಹವನ್ನು ಸರ್ವೇಶ್ವರನಾದ ದೇವರು ಮೆಚ್ಚಿದರು.",
			"11": "<sup>11</sup> ಅವರು ಆತನಿಗೆ, “ನೀನು ದೀರ್ಘಾಯುಷ್ಯವನ್ನಾಗಲಿ, ಸಿರಿಸಂಪತ್ತನ್ನಾಗಲಿ, ಶತ್ರುವಿನಾಶವನ್ನಾಗಲಿ ಕೇಳಿಕೊಳ್ಳಲಿಲ್ಲ. ನ್ಯಾಯನಿರ್ಣಯಿಸುವುದಕ್ಕೆ ಬೇಕಾದ ವಿವೇಕವನ್ನು ಬೇಡಿಕೊಂಡೆ.",
			"12": "<sup>12</sup> ಆದ್ದರಿಂದ ನಿನ್ನ ಬಿನ್ನಹವನ್ನು ನೆರವೇರಿಸಿದ್ದೇನೆ. ನೋಡು, ನಿನಗೆ ಜ್ಞಾನವನ್ನೂ ವಿವೇಕವನ್ನೂ ಅನುಗ್ರಹಿಸಿದ್ದೇನೆ; ನಿನ್ನಂಥ ಜ್ಞಾನಿಯು ಹಿಂದೆ ಇರಲಿಲ್ಲ, ಮುಂದೆಯೂ ಇರುವುದಿಲ್ಲ.",
			"13": "<sup>13</sup> ಇದಲ್ಲದೆ, ನೀನು ಕೇಳಿದಂಥದ್ದನ್ನೂ ನಿನಗೆ ಅನುಗ್ರಹಿಸಿದ್ದೇನೆ. ನಿನ್ನ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ಸಿರಿಸಂಪತ್ತಿನಲ್ಲಿಯೂ ಘನತೆ ಗೌರವದಲ್ಲಿಯೂ ನಿನಗೆ ಸಮಾನವಾದ ಅರಸನು ಇನ್ನೊಬ್ಬನಿರುವುದಿಲ್ಲ.",
			"14": "<sup>14</sup> ನೀನು ನಿನ್ನ ತಂದೆ ದಾವೀದನಂತೆ ನನ್ನ ಮಾರ್ಗದಲ್ಲೇ ನಡೆದು ನನ್ನ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ಕೈಗೊಳ್ಳುವುದಾದರೆ ನಿನ್ನ ಆಯುಷ್ಯವನ್ನೂ ಹೆಚ್ಚಿಸುವೆನು,” ಎಂದರು.",
			"15": "<sup>15</sup> ಸೊಲೊಮೋನನು ನಿದ್ರೆಯಿಂದ ಎಚ್ಚೆತ್ತಾಗ, ಅದು ಕನಸೆಂದು ತಿಳಿದುಕೊಂಡನು. ಅವನು ಜೆರುಸಲೇಮಿಗೆ ಬಂದನಂತರ ಸರ್ವೇಶ್ವರನ ಒಡಂಬಡಿಕೆಯ ಮಂಜೂಷದ ಮುಂದೆ ನಿಂತು ದಹನಬಲಿಗಳನ್ನೂ ಶಾಂತಿಸಮಾಧಾನದ ಬಲಿಗಳನ್ನೂ ಸಮರ್ಪಿಸಿ, ತನ್ನ ಎಲ್ಲಾ ಪರಿವಾರದವರಿಗೆ ಔತಣವನ್ನು ಏರ್ಪಡಿಸಿದನು.",
			"16": "<sup>16</sup> ಒಂದು ದಿನ ಇಬ್ಬರು ವೇಶ್ಯೆಯರು ಅರಸನ ಸನ್ನಿಧಿಗೆ ಬಂದರು.",
			"17": "<sup>17</sup> ಅವರಲ್ಲಿ ಒಬ್ಬಳು ಅರಸನಿಗೆ, “ಒಡೆಯಾ, ಕೇಳಿ; ನಾನೂ ಇವಳೂ ಒಂದೇ ಮನೆಯಲ್ಲಿ ವಾಸಿಸುತ್ತೇವೆ. ಇವಳು ಮನೆಯಲ್ಲಿದ್ದಾಗಲೇ ನಾನು ಒಂದು ಮಗವನ್ನು ಹೆತ್ತೆ.",
			"18": "<sup>18</sup> ಮೂರನೆಯ ದಿನದಲ್ಲಿ ಇವಳೂ ಹೆತ್ತಳು. ನಾವಿಬ್ಬರೂ ಒಟ್ಟಿಗೆ ಇದ್ದೆವು; ನಮ್ಮಿಬ್ಬರ ಹೊರತು ಆ ಮನೆಯಲ್ಲಿ ಯಾರೂ ಇರಲಿಲ್ಲ.",
			"19": "<sup>19</sup> ಇವಳು ರಾತ್ರಿಯಲ್ಲಿ ತನ್ನ ಕೂಸಿನ ಮೇಲೆ ಹೊರಳಿದ್ದರಿಂದ ಅದು ಸತ್ತಿತು.",
			"20": "<sup>20</sup> ಮಧ್ಯರಾತ್ರಿಯಲ್ಲಿ ಇವಳೆದ್ದು, ನಿಮ್ಮ ಸೇವಕಳಾದ ನಾನು ಗಾಢನಿದ್ರೆಯಲ್ಲಿದ್ದಾಗ, ನನ್ನ ಮಗುವನ್ನು ನನ್ನ ಬಳಿಯಿಂದ ತೆಗೆದು ತನ್ನ ಮಗ್ಗುಲಲ್ಲಿ ಇಟ್ಟುಕೊಂಡಳು. ಸತ್ತುಹೋದ ತನ್ನ ಮಗುವನ್ನು ನನ್ನ ಮಗ್ಗುಲಲ್ಲಿಟ್ಟಳು.",
			"21": "<sup>21</sup> ನಾನು ಬೆಳಿಗ್ಗೆ ಎದ್ದು ಮಗುವಿಗೆ ಮೊಲೆಕುಡಿಸಬೇಕೆಂದಿರುವಾಗ ಅದು ಸತ್ತಿತ್ತು. ಆದರೆ ಅದನ್ನು ಬೆಳಕಿನಲ್ಲಿ ನೋಡಿದಾಗ ಅದು ನಾನು ಹೆತ್ತ ಕೂಸಾಗಿರಲಿಲ್ಲ,” ಎಂದು ಹೇಳಿದಳು.",
			"22": "<sup>22</sup> ಆಗ ಎರಡನೆಯವಳು, “ಹಾಗಲ್ಲ, ಬದುಕಿರುವವನು ನನ್ನ ಮಗ; ಸತ್ತಿರುವವನು ನಿನ್ನ ಮಗ,” ಎಂದು ನುಡಿದಳು. ಮೊದಲನೆಯವಳು ಮತ್ತೆ, “ಇಲ್ಲ ಸತ್ತಿರುವವನು ನಿನ್ನ ಮಗ ಬದುಕಿರುವವನು ನನ್ನ ಮಗ,” ಎಂದಳು. ಹೀಗೆ ಅವರು ಅರಸನ ಮುಂದೆ ವಾದಿಸುತ್ತಾ ಇದ್ದರು.",
			"23": "<sup>23</sup> ಆಗ ಅರಸನು, “ಜೀವದಿಂದಿರುವ ಕೂಸು ನನ್ನದು, ಸತ್ತಿರುವುದು ನಿನ್ನದು,’ ಎಂದು ಒಬ್ಬಳು ಹೇಳುತ್ತಾಳೆ; ಇನ್ನೊಬ್ಬಳು, ‘ಇಲ್ಲ, ಸತ್ತಿರುವುದು ನಿನ್ನದು, ಜೀವದಿಂದಿರುವುದು ನನ್ನದು,’ ಎನ್ನುತ್ತಾಳೆ.",
			"24": "<sup>24</sup> ನನಗೊಂದು ಕತ್ತಿಯನ್ನು ತಂದುಕೊಡಿ,” ಎಂದು ಸೇವಕರಿಗೆ ಹೇಳಿದನು.",
			"25": "<sup>25</sup> ಅವರು ತಂದರು. ಅರಸನು ಅವರಿಗೆ, “ಜೀವದಿಂದಿರುವ ಕೂಸನ್ನು ಕಡಿದು, ಎರಡು ಭಾಗಮಾಡಿ, ಅರ್ಧವನ್ನು ಅವಳಿಗೂ ಅರ್ಧವನ್ನು ಇವಳಿಗೂ ಕೊಡಿ,” ಎಂದು ಅಪ್ಪಣೆಮಾಡಿದನು.",
			"26": "<sup>26</sup> ಆಗ, ಜೀವದಿಂದಿದ್ದ ಕೂಸಿನ ನಿಜವಾದ ತಾಯಿಗೆ ಕೂಸಿನ ವಿಷಯದಲ್ಲಿ ಕರುಳುಕರಗಿತು; “ನನ್ನೊಡೆಯಾ, ಬೇಡಿ; ಬದುಕಿರುವ ಕೂಸನ್ನು ಅವಳಿಗೇ ಕೊಟ್ಟುಬಿಡಿ; ಅದನ್ನು ಕೊಲ್ಲಿಸಬೇಡಿ,” ಎಂದು ಬೇಡಿಕೊಂಡಳು; ಎರಡನೆಯವಳು, “ಅದು ನನಗೂ ಬೇಡ, ನಿನಗೂ ಬೇಡ, ಕಡಿಯಲಿ,” ಎಂದು ಕೂಗಿದಳು.",
			"27": "<sup>27</sup> ಕೂಡಲೆ ಅರಸನು, “ಬದುಕಿರುವ ಕೂಸನ್ನು ಕೊಲ್ಲಬೇಡಿ, ಅದನ್ನು ಆ ಸ್ತ್ರೀಗೆ ಕೊಡಿ; ಅವಳೇ ಅದರ ತಾಯಿ,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"28": "<sup>28</sup> ಇಸ್ರಯೇಲರೆಲ್ಲರೂ ಅರಸನ ಈ ತೀರ್ಪನ್ನು ಕೇಳಿದರು. ನ್ಯಾಯನಿರ್ಣಯಿಸುವುದಕ್ಕೆ ಈತನಲ್ಲಿ ದೇವದತ್ತ ಜ್ಞಾನವಿದೆ ಎಂದು ತಿಳಿದು ಅವನ ಬಗ್ಗೆ ಅಪಾರ ಗೌರವ ಉಳ್ಳವರಾದರು."
		},
		"4": {
			"1": "<sup>1</sup> ಸೊಲೊಮೋನರಾಜನು ಸರ್ವ ಇಸ್ರಯೇಲರ ಮೇಲೆ ಅರಸನಾಗಿದ್ದನು.",
			"2": "<sup>2</sup> ಅವನ ಮುಖ್ಯ ಪದಾಧಿಕಾರಿಗಳು ಇವರು : ಚಾದೋಕನ ಮಗ ಅಜರ್ಯನು - ಯಾಜಕನು;",
			"3": "<sup>3</sup> ಶೀಷನ ಮಕ್ಕಳಾದ ಎಲೀಹೋರೆಫ್ ಹಾಗು ಅಹೀಯಾಹು ಎಂಬುವರು ಕಾರ್ಯದರ್ಶಿಗಳು; ಅಹೀಲೂದನ ಮಗ ಯೆಹೋಷಾಫಾಟನು ಅವನ ಮಂತ್ರಿ;",
			"4": "<sup>4</sup> ಯೆಹೋಯಾದಾವನ ಮಗ ಬೆನಾಯನು - ಸೇನಾಪತಿ; ಎಬ್ಯಾತಾರ ಹಾಗು ಚಾದೋಕ - ಯಾಜಕರು;",
			"5": "<sup>5</sup> ನಾತಾನನ ಮಗ ಅಜರ್ಯನು - ಪ್ರದೇಶಾಧಿಪತಿಗಳ ಮುಖ್ಯಸ್ಥ; ನಾತಾನನ ಮಗ ಜಾಬೂದನು ಯಾಜಕ ಹಾಗು ಅರಸನ ಮಿತ್ರ;",
			"6": "<sup>6</sup> ಅಹೀಷಾರನು - ರಾಜ್ಯಗೃಹಾಧಿಪತಿ; ಅಬ್ದನ ಮಗನಾದ ಅದೋನೀರಾಮನು - ಬಿಟ್ಟೀ ಕೆಲಸಮಾಡಿಸುವವರ ಮುಖ್ಯಸ್ಥ.",
			"7": "<sup>7</sup> ಸರ್ವ ಇಸ್ರಯೇಲರ ಮೇಲೆ ಸೊಲೊಮೋನನು ಹನ್ನೆರಡು ಜನ ಜಿಲ್ಲಾಧಿಕಾರಿಗಳನ್ನು ನೇಮಿಸಿದ್ದನು. ಅವರು ತಮ್ಮ ಜಿಲ್ಲೆಗಳಿಂದ ಅರಸನಿಗೂ ಅರಮನೆಯವರಿಗೂ ಬೇಕಾದ ಆಹಾರ ಪದಾರ್ಥಗಳನ್ನು ಪೂರೈಸುತ್ತಿದ್ದರು. ಪ್ರತಿಯೊಬ್ಬನೂ ವರ್ಷದಲ್ಲಿ ಒಂದೊಂದು ತಿಂಗಳು ಪೂರೈಸಬೇಕಾಗಿತ್ತು.",
			"8": "<sup>8</sup> ಅವರ ಹೆಸರುಗಳು ಹಾಗು ಜಿಲ್ಲೆಗಳ ಹೆಸರುಗಳು ಇವು:",
			"9": "<sup>9</sup> ಬಿನ್ಹೂರ - ಎಫ್ರಯಿಮಿನ ಮಲೆನಾಡಿನ ಪ್ರದೇಶ;",
			"10": "<sup>10</sup> ಬಿನ್ ದೆಕೆರ್ - ಮಾಕಚ್, ಶಾಲ್ಬೀಮ್, ಬೀತ್ ಷೆಮೆಷ್, ಏಲೋನ್, ಬೇತ್ ಹಾನಾನ್;",
			"11": "<sup>11</sup> ಬಿನ್ ಹೆಸೆದ್ - ಅರುಬ್ಬೋತ್ (ಸೋಕೋಹೇಫೆರ್ ಎಂಬ ಪ್ರದೇಶಗಳು ಅವನ ವಶದಲ್ಲಿದ್ದವು).",
			"12": "<sup>12</sup> ಬಿನ್ ಅಬೀನಾದಾಬ್ - ನಾಪೋತ್ ದೋರ್ (ಇವನು ಸೊಲೊಮೋನನ ಮಗಳು ಟಾಫತಳನ್ನು ವಿವಾಹವಾಗಿದ್ದನು);",
			"13": "<sup>13</sup> ಅಹೀಲೂದನ ಮಗ ಬಾಣಾ - ತಾಣಕ್, ಮೆಗಿದ್ದೋ, ಚಾರೆತಾನಿನ ಬಳಿಯಲ್ಲಿರುವ ಜೆಸ್ರೀಲಿನ ಅಡಿಯಲ್ಲಿದ್ದ ಬೇತ್‍ಷೆಯಾನಿನಿಂದ ಆಬೇಲ್ ಮೆಹೋಲದವರೆಗೂ ಇದ್ದ, ಯೊಕ್ಮೆಯಾನಿನ ಆಚೆಗೆ ವಿಸ್ತರಿಸಿಕೊಂಡಿದ್ದ ಬೇತ್‍ಷೆಯಾನಿನ ಎಲ್ಲಾ ಪ್ರದೇಶ;",
			"14": "<sup>14</sup> ಬಿನ್ ಗೆಬೆರ್ - ರಾಮೋತ್ ಗಿಲ್ಯಾದ್ (ಗಿಲ್ಯಾದಿನಲ್ಲಿ ಮನಸ್ಸೆಯ ಮಗ ಯಾಯೀರನ ಗ್ರಾಮಗಳಿಗೂ ಬಾಷಾನಿನಲ್ಲಿ ಪೌಳಿಗೋಡೆಗಳಿಂದಲೂ ಕಬ್ಬಿಣದ ಅಗುಳಿಗಳಿಂದಲೂ ಭದ್ರಮಾಡಲ್ಪಟ್ಟ ಅರವತ್ತು ಪಟ್ಟಣಗಳಿದ್ದ ಅರ್ಗೋಬ ಪ್ರದೇಶ); ಇದ್ದೋವಿನ ಮಗ ಅಹೀನಾದಾಬನು - ಮಹನಯಿಮ್;",
			"15": "<sup>15</sup> ಅಹೀಮಾಚ - ನಫ್ತಾಲಿ ಪ್ರದೇಶ (ಇವನು ಸೊಲೊಮೋನನ ಮಗಳು ಬಾಸೆಮತಳನ್ನು ವಿವಾಹವಾಗಿದ್ದನು);",
			"16": "<sup>16</sup> ಹೂಷೈಯ ಮಗ ಬಾಣ - ಅಶೇರ ಹಾಗು ಅಲೋತ್;",
			"17": "<sup>17</sup> ಫಾರೂಹನ ಮಗ ಯೆಹೋಷಾಫಾಟನು - ಇಸ್ಸಾಕಾರ ಪ್ರದೇಶ;",
			"18": "<sup>18</sup> ಏಲನ ಮಗ ಶಿಮ್ಮಿ - ಬೆನ್ಯಾಮೀನರ ಪ್ರದೇಶ;",
			"19": "<sup>19</sup> ಉರಿಯನ ಮಗ ಗೆಬೆರ್ - ಗಿಲ್ಯಾದ (ಅಮೋರಿಯರ ಅರಸ ಸೀಹೋನ್, ಬಾಷಾನಿನ ಅರಸ ಓಗ್ ಇವರ ಪ್ರದೇಶ) ಪ್ರದೇಶಕ್ಕೆಲ್ಲಾ ಅವನೊಬ್ಬನೇ ಜಿಲ್ಲಾಧಿಕಾರಿಯಾಗಿದ್ದನು.",
			"20": "<sup>20</sup> ಇಸ್ರಯೇಲ್ ಹಾಗು ಯೆಹೂದ್ಯರು ಸಮುದ್ರ ತೀರದ ಮರಳಿನಷ್ಟು ಅಸಂಖ್ಯಾತರಾದರು; ಅನ್ನ ಪಾನಗಳಲ್ಲಿ ಸಂತೃಪ್ತರಾಗಿ ಸಂತೋಷದಿಂದ ಬಾಳುತ್ತಿದ್ದರು.",
			"21": "<sup>21</sup> ಯೂಫ್ರೆಟಿಸ್ ನದಿ ಮೊದಲ್ಗೊಂಡು ಫಿಲಿಷ್ಟಿಯರ ಮತ್ತು ಈಜಿಪ್ಟಿಯರ ದೇಶಗಳವರೆಗೂ ಇರುವ ಎಲ್ಲ ರಾಜ್ಯಗಳ ಪ್ರಜೆಗಳನ್ನು ಸೊಲೊಮೋನನು ಆಳುತ್ತಿದ್ದನು. ಆ ದೇಶದವರು, ತಮ್ಮ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ಅಧೀನರಾಗಿದ್ದು, ಕಪ್ಪಕಾಣಿಕೆಗಳನ್ನು ಸಲ್ಲಿಸುತ್ತಿದ್ದರು.",
			"22": "<sup>22</sup> ಸೊಲೊಮೋನನ ಅರಮನೆಗೆ ದಿನಂಪ್ರತಿ ಬೇಕಾಗಿದ್ದ ಆಹಾರಪದಾರ್ಥಗಳ ಪಟ್ಟಿ: ಐದು ಸಾವಿರ ಲೀಟರ್ ಗೋದಿಯ ಹಿಟ್ಟು, ಹತ್ತು ಸಾವಿರ ಲೀಟರ್ ಜವೆಗೋದಿಯ ಹಿಟ್ಟು;",
			"23": "<sup>23</sup> ಹತ್ತು ಕಟ್ಟಿ ಮೇಯಿಸಿದ ಎತ್ತುಗಳು, ಇಪ್ಪತ್ತು ಬಿಟ್ಟು ಮೇಯಿಸಿದ ಎತ್ತುಗಳು, ನೂರು ಕುರಿಗಳು; ಇವುಗಳ ಜೊತೆಗೆ, ದುಪ್ಪಿ, ಜಿಂಕೆ, ಸಾರಂಗ, ಕೊಬ್ಬಿದ ಕೋಳಿಗಳು.",
			"24": "<sup>24</sup> ಅವನು ಯೂಫ್ರೆಟಿಸ್ ನದಿಯ ಈಚೆಯಲ್ಲಿ ತಿಪ್ಸಹು ಮೊದಲ್ಗೊಂಡು ಗಾಜದವರೆಗಿರುವ ಈಚೆಯ ಎಲ್ಲಾ ರಾಜರಿಗೂ ಅಧಿಪತಿಯಾಗಿದ್ದನು. ಸುತ್ತಮುತ್ತಲಿನ ರಾಜರೊಡನೆ ಶಾಂತಿಸಮಾಧಾನದಿಂದಿದ್ದನು.",
			"25": "<sup>25</sup> ಸೊಲೊಮೋನನ ಆಳ್ವಿಕೆಯಲ್ಲೆಲ್ಲಾ ದಾನ್ ಪಟ್ಟಣದಿಂದ ಬೇರ್ಷೆಬದವರೆಗಿರುವ ಸಮಸ್ತ ಇಸ್ರಯೇಲರು ಹಾಗು ಯೆಹೂದ್ಯರು ತಮ್ಮ ತಮ್ಮ ದ್ರಾಕ್ಷಾಲತೆ, ಅಂಜೂರದಗಿಡ, ಇವುಗಳ ನೆರಳಿನಲ್ಲಿ ವಾಸಿಸುತ್ತಾ ಸುರಕ್ಷಿತರಾಗಿದ್ದರು.",
			"26": "<sup>26</sup> ಸೊಲೊಮೋನನ ಲಾಯಗಳಲ್ಲಿ ನಾಲ್ವತ್ತು ಸಾವಿರ ರಥಾಶ್ವಗಳಿಗೆ ಸ್ಥಳವಿತ್ತು; ಅವನಿಗೆ ಹನ್ನೆರಡು ಸಾವಿರ ಮಂದಿ ರಾಹುತರಿದ್ದರು.",
			"27": "<sup>27</sup> ಮೇಲೆ ಹೇಳಿದ ಜಿಲ್ಲಾಧಿಕಾರಿಗಳು ತಮತಮಗೆ ನೇಮಕವಾದ ತಿಂಗಳಲ್ಲಿ ಅರಸ ಸೊಲೊಮೋನನಿಗೂ ಅವನ ಪಂಕ್ತಿಯಲ್ಲಿ ಊಟಕ್ಕೆ ಕುಳಿತುಕೊಳ್ಳುತ್ತಿದ್ದ ಎಲ್ಲರಿಗೂ ಬೇಕಾಗುವ ಆಹಾರ ಪದಾರ್ಥಗಳನ್ನು ಯಾವ ಕೊರತೆಯೂ ಇಲ್ಲದೆ ಒದಗಿಸುತ್ತಿದ್ದರು.",
			"28": "<sup>28</sup> ಇದಲ್ಲದೆ, ಅವರು ರಥಾಶ್ವಗಳಿಗಾಗಿ ಹಾಗು ಸವಾರಿಕುದುರೆಗಳಿಗಾಗಿ ನೇಮಕವಾದಷ್ಟು ಜವೆಗೋದಿಯನ್ನೂ ಹುಲ್ಲನ್ನೂ ಕುದುರೆಗಳಿದ್ದ ಸ್ಥಳಕ್ಕೆ ತಂದು ಒಪ್ಪಿಸುತ್ತಿದ್ದರು.",
			"29": "<sup>29</sup> ದೇವರು ಸೊಲೊಮೋನನಿಗೆ ಸಮುದ್ರತೀರದ ಮರಳಿನಷ್ಟು ಅಪರಿಮಿತವಾದ ಜ್ಞಾನವಿವೇಕಗಳನ್ನೂ ಮನೋವಿಶಾಲತೆಯನ್ನೂ ಅನುಗ್ರಹಿಸಿದ್ದರು.",
			"30": "<sup>30</sup> ಅವನ ಜ್ಞಾನ ಮೂಡಣದೇಶದವರೆಲ್ಲರ ಜ್ಞಾನಕ್ಕಿಂತಲು, ಈಜಿಪ್ಟರ ಸರ್ವಜ್ಞಾನಕ್ಕಿಂತಲು ಮಿಗಿಲಾಗಿತ್ತು.",
			"31": "<sup>31</sup> ಅವನು ಜೆರಹನ ಮಗನಾದ ಏತಾನ್, ಮಾಹೋಲನ ಮಕ್ಕಳಾದ ಹೇಮಾನ್, ಕಲ್ಕೋಲ್, ದರ್ದ ಮೊದಲಾದ ಎಲ್ಲರಿಗಿಂತಲೂ ಜ್ಞಾನಿಯಾಗಿದ್ದನು. ಸುತ್ತಮುತ್ತಲಿನ ಜನಾಂಗಗಳಲ್ಲೆಲ್ಲಾ ಅವನ ಹೆಸರು ಪ್ರಸಿದ್ಧವಾಯಿತು.",
			"32": "<sup>32</sup> ಅವನು ನುಡಿದ ಜ್ಞಾನೋಕ್ತಿಗಳು ಮೂರು ಸಾವಿರ, ರಚಿಸಿದ ಗೀತೆಗಳು ಸಾವಿರದ ಐದು.",
			"33": "<sup>33</sup> ಅವನು, ಲೆಬನೋನಿನ ದೇವದಾರುವೃಕ್ಷ ಮೊದಲ್ಗೊಂಡು ಗೋಡೆಯಲ್ಲಿ ಬೆಳೆಯುವ ಹಿಸ್ಸೋಪ್ ಗಿಡದವರೆಗಿರುವ ಎಲ್ಲಾ ವನಸ್ಪತಿಗಳನ್ನು ಕುರಿತು, ಎಲ್ಲಾ ಪಶುಪಕ್ಷಿ, ಜಲಜಂತು, ಕ್ರಿಮಿಕೀಟಗಳನ್ನು ಕುರಿತು ಪ್ರಸ್ತಾಪಿಸಬಲ್ಲವನಾಗಿದ್ದನು.",
			"34": "<sup>34</sup> ಸೊಲೊಮೋನನ ಜ್ಞಾನವಿಶೇಷವನ್ನು ಕುರಿತು ಕೇಳಿದ ಸರ್ವಜನಾಂಗಗಳ ಭೂಪಾಲರಲ್ಲಿ ಅನೇಕರು ಅವನ ಜ್ಞಾನವಾಕ್ಯಗಳನ್ನು ಕೇಳುವುದಕ್ಕಾಗಿ ಬರುತ್ತಿದ್ದರು."
		},
		"5": {
			"1": "<sup>1</sup> ದಾವೀದನಿಗೆ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ಆಪ್ತಮಿತ್ರನಾಗಿದ್ದ ಟೈರಿನ ಅರಸನಾದ ಹೀರಾಮನು, ಸೊಲೊಮೋನನು ತಂದೆಗೆ ಬದಲಾಗಿ ಪಟ್ಟಾಭಿಷೇಕ ಪಡೆದಿದ್ದಾನೆಂದು ಕೇಳಿ, ಅವನ ಬಳಿಗೆ ತನ್ನ ಸೇವಕರನ್ನು ಕಳುಹಿಸಿದನು.",
			"2": "<sup>2</sup> ಸೊಲೊಮೋನನು ದೂತರ ಮುಖಾಂತರ ಹೀರಾಮನಿಗೆ,",
			"3": "<sup>3</sup> “ಸರ್ವೇಶ್ವರಸ್ವಾಮಿಯ ಅನುಗ್ರಹದಿಂದ ನಮ್ಮ ತಂದೆ ದಾವೀದನಿಗೆ ಎಲ್ಲಾ ಶತ್ರುಗಳು ವಶವಾದರು. ಅಲ್ಲಿಯವರೆಗೆ ಅವರು ಸುತ್ತಲೂ ಯುದ್ಧಗಳನ್ನು ನಡೆಸಬೇಕಾಗಿತ್ತು. ಆದ್ದರಿಂದ ತಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಹೆಸರಿಗೆ ಆಲಯವನ್ನು ಕಟ್ಟಿಸಲಾರದೆ ಹೋದರು. ಇದು ನಿಮಗೆ ತಿಳಿದ ವಿಷಯ.",
			"4": "<sup>4</sup> ನನಗಾದರೋ ನನ್ನ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಎಲ್ಲ ಕಡೆಗಳಲ್ಲೂ ಸಮಾಧಾನವನ್ನು ಅನುಗ್ರಹಿಸಿದ್ದಾರೆ; ನನ್ನನ್ನು ವಿರೋಧಿಸುವವನು ಒಬ್ಬನೂ ಇಲ್ಲ; ಆಪತ್ತು ವಿಪತ್ತು ದೂರವಾಗಿವೆ.",
			"5": "<sup>5</sup> ಸರ್ವೇಶ್ವರ ನನ್ನ ತಂದೆ ದಾವೀದನಿಗೆ, ‘ನಾನು ನಿನ್ನ ಸ್ಥಾನದಲ್ಲಿ ಸಿಂಹಾಸನದ ಮೇಲೆ ಕುಳ್ಳಿರಿಸುವ ನಿನ್ನ ಮಗ, ನನ್ನ ಹೆಸರಿಗೆ ಒಂದು ಆಲಯವನ್ನು ಕಟ್ಟಲಿ,’ ಎಂದು ಹೇಳಿದ್ದರು. ಆದುದರಿಂದ ನಾನು ನನ್ನ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ಒಂದು ಆಲಯವನ್ನು ಕಟ್ಟಬೇಕೆಂದಿರುತ್ತೇನೆ.",
			"6": "<sup>6</sup> ನೀನು ನನಗೋಸ್ಕರ ಲೆಬನೋನಿನ ದೇವದಾರು ವೃಕ್ಷಗಳನ್ನು ಕೊಯ್ಯಬೇಕೆಂದು ನಿನ್ನ ಆಳುಗಳಿಗೆ ಆಜ್ಞಾಪಿಸು; ಅವರ ಜೊತೆಯಲ್ಲಿ ನನ್ನ ಆಳುಗಳನ್ನೂ ಕಳುಹಿಸುವೆನು. ಮತ್ತು ನಿನ್ನ ಆಳುಗಳಿಗೆ ನೀನು ಹೇಳುವಷ್ಟು ಸಂಬಳವನ್ನು ಕೊಡುತ್ತೇನೆ. ಸಿದೋನ್ಯರಂತೆ ಮರಕೊಯ್ಯುವುದರಲ್ಲಿ ಜಾಣರು ನಮ್ಮಲ್ಲಿರುವುದಿಲ್ಲವೆಂದು ನಿನಗೆ ಗೊತ್ತಿದೆಯಲ್ಲವೆ?” ಎಂದು ಹೇಳಿಸಿದನು.",
			"7": "<sup>7</sup> ಹೀರಾಮನು ಸೊಲೊಮೋನನ ಮಾತುಗಳನ್ನು ಕೇಳಿದಾಗ ಬಹಳವಾಗಿ ಸಂತೋಷಪಟ್ಟನು. “ಈ ಮಹಾಜನಾಂಗವನ್ನು ಆಳುವುದಕ್ಕಾಗಿ ದಾವೀದನಿಗೆ ಒಬ್ಬ ಬುದ್ಧಿವಂತ ಮಗನನ್ನು ಅನುಗ್ರಹಿಸಿದ ಸರ್ವೇಶ್ವರನಿಗೆ ಸ್ತೋತ್ರವಾಗಲಿ!” ಎಂದು ನುಡಿದನು.",
			"8": "<sup>8</sup> ಇದೂ ಅಲ್ಲದೆ, ಅವನು ಸೊಲೊಮೋನನಿಗೆ, “ನೀನು ಹೇಳಿ ಕಳುಹಿಸಿದ್ದನ್ನು, ಕೇಳಿದೆನು; ದೇವದಾರು ವೃಕ್ಷಗಳನ್ನೂ ತುರಾಯಿಮರಗಳನ್ನೂ ಕುರಿತು ನಿನ್ನ ಅಪೇಕ್ಷೆಯನ್ನು ನೆರವೇರಿಸುವೆನು.",
			"9": "<sup>9</sup> ನನ್ನ ಸೇವಕರು ಅವುಗಳನ್ನು ಲೆಬನೋನಿನಿಂದ ಸಮುದ್ರಕ್ಕೆ ತೆಗೆದುಕೊಂಡು ಹೋಗುವರು. ನಾನು ಅವುಗಳನ್ನು ತೆಪ್ಪವನ್ನಾಗಿ ಕಟ್ಟಿಸಿ ಸಮುದ್ರಮಾರ್ಗವಾಗಿ ನೀನು ನೇಮಿಸುವ ಸ್ಥಳಕ್ಕೆ ಕಳುಹಿಸಿ ಅಲ್ಲೆ ಬಿಚ್ಚಿಸುವೆನು; ನೀನು ಅವುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು, ಅವುಗಳಿಗೆ ಬದಲಾಗಿ ನನ್ನ ಮನೆಯವರಿಗೆ ಆಹಾರಪದಾರ್ಥಗಳನ್ನು ಕೊಡಬೇಕೆಂಬುದೇ ನನ್ನ ಅಪೇಕ್ಷೆ,” ಎಂದು ಹೇಳಿಕಳುಹಿಸಿದನು.",
			"10": "<sup>10</sup> ಹೀಗೆ ಹೀರಾಮನು ಸೊಲೊಮೋನನಿಗೆ ಬೇಕಾದಷ್ಟು ದೇವದಾರಿನ ಮರಗಳನ್ನೂ ತುರಾಯಿಮರಗಳನ್ನೂ ಕೊಡುತ್ತಿದ್ದನು.",
			"11": "<sup>11</sup> ಸೊಲೊಮೋನನಾದರೋ ಪ್ರತಿವರ್ಷ ಅವನ ಮನೆಯವರ ಆಹಾರಕ್ಕಾಗಿ ಎರಡು ಸಾವಿರ ಮೆಟ್ರಿಕ್ ಟನ್ ಗೋದಿಯನ್ನೂ ನಾಲ್ಕು ಸಾವಿರ ಲೀಟರ್ ಅಪ್ಪಟ ಎಣ್ಣೆಯನ್ನೂ ಕೊಡುತ್ತಿದ್ದನು.",
			"12": "<sup>12</sup> ಸರ್ವೇಶ್ವರ ವಾಗ್ದಾನಮಾಡಿದಂತೆ ಸೊಲೊಮೋನನಿಗೆ ಜ್ಞಾನವನ್ನು ಅನುಗ್ರಹಿಸಿದ್ದರು. ಸೊಲೊಮೋನನಿಗೂ ಹೀರಾಮನಿಗೂ ಸಮಾಧಾನವಿತ್ತು; ಅವರು ಒಂದು ಒಪ್ಪಂದ ಮಾಡಿಕೊಂಡಿದ್ದರು.",
			"13": "<sup>13</sup> ಅರಸ ಸೊಲೊಮೋನನು, ಇಸ್ರಯೇಲರಲ್ಲಿ ಮೂವತ್ತು ಸಾವಿರ ಮಂದಿಯನ್ನು ಬಿಟ್ಟಿಕೆಲಸಕ್ಕೆಂದು ಹಿಡಿದು,",
			"14": "<sup>14</sup> ಅವರಲ್ಲಿ ತಿಂಗಳೊಂದಕ್ಕೆ ಹತ್ತುಸಾವಿರ ಮಂದಿಯಂತೆ ಲೆಬನೋನಿಗೆ ಕಳುಹಿಸುತ್ತಿದ್ದನು. ಅವರು ಒಂದು ತಿಂಗಳು ಲೆಬನೋನಿನಲ್ಲಿದ್ದರೆ, ಎರಡು ತಿಂಗಳು ಮನೆಯಲ್ಲಿರುತ್ತಿದ್ದರು. ಅದೋನೀರಾಮನು ಇವರ ಮುಖ್ಯಸ್ಥನಾಗಿದ್ದನು.",
			"15": "<sup>15</sup> ಇದಲ್ಲದೆ, ಸೊಲೊಮೋನನಿಗೆ ಎಪ್ಪತ್ತು ಸಾವಿರಮಂದಿ ಹೊರೆಹೊರುವವರೂ ಎಂಬತ್ತು ಸಾವಿರಮಂದಿ ಕಲ್ಲುಗಣಿಯಲ್ಲಿ ಕೆಲಸಮಾಡುವವರೂ ಇದ್ದರು.",
			"16": "<sup>16</sup> ಈ ಕೆಲಸದವರ ಮೇಲ್ವಿಚಾರಣೆಗಾಗಿ ಅವನು ಮೂರು ಸಾವಿರದ ಮುನ್ನೂರುಮಂದಿ ಮೇಸ್ತ್ರಿಗಳನ್ನು ನೇಮಿಸಿದ್ದನು.",
			"17": "<sup>17</sup> ಕೆತ್ತಿದ ಕಲ್ಲುಗಳಿಂದ ಹಾಕಬೇಕಾದ ದೇವಾಲಯದ ಅಸ್ತಿವಾರಕ್ಕಾಗಿ ಆಳುಗಳು, ಸೊಲೊಮೋನನ ಅಪ್ಪಣೆಯಂತೆ, ಶ್ರೇಷ್ಠವಾದ ದೊಡ್ಡ ಕಲ್ಲುಗಳನ್ನು ಕೊರೆಯುತ್ತಿದ್ದರು.",
			"18": "<sup>18</sup> ಸೊಲೊಮೋನನ ಮತ್ತು ಹೀರಾಮನ ಶಿಲ್ಪಿಗಳೂ ಗೆಬಾಲ್ಯರೂ ಅವುಗಳನ್ನು ಕೆತ್ತುತ್ತಿದ್ದರು. ದೇವಾಲಯ ಕಟ್ಟುವುದಕ್ಕೆ ಬೇಕಾದ ಕಲ್ಲು, ಮರಗಳನ್ನು ಸಿದ್ಧಪಡಿಸಿದವರು ಇವರೇ."
		},
		"6": {
			"1": "<sup>1</sup> ಇಸ್ರಯೇಲರು ಈಜಿಪ್ಟ್ ದೇಶವನ್ನು ಬಿಟ್ಟ ನಾನೂರ ಎಂಬತ್ತನೆಯ ವರ್ಷದಲ್ಲಿ ಅಂದರೆ, ಇಸ್ರಯೇಲರ ಅರಸನಾದ ಸೊಲೊಮೋನನ ಆಳ್ವಿಕೆಯ ನಾಲ್ಕನೆಯ ವರ್ಷದ ಎರಡನೆಯ ತಿಂಗಳಾದ ವೈಶಾಖ ಮಾಸದಲ್ಲಿ ಅರಸ ಸೊಲೊಮೋನನು ಸರ್ವೇಶ್ವರಸ್ವಾಮಿಯ ಆಲಯವನ್ನು ಕಟ್ಟುವುದಕ್ಕೆ ಪ್ರಾರಂಭಿಸಿದನು.",
			"2": "<sup>2</sup> ಅವನು ಸರ್ವೇಶ್ವರನಿಗೆ ಕಟ್ಟಿಸಿದ ದೇವಾಲಯದ ಉದ್ದ 27 ಮೀಟರ್, ಅಗಲ 9 ಮೀಟರ್, ಎತ್ತರ 13:5 ಮೀಟರ್.",
			"3": "<sup>3</sup> ದೇವಾಲಯದ ಪರಿಶುದ್ಧ ಸ್ಥಳದ ಮುಂದುಗಡೆಯಲ್ಲಿ ಒಂದು ಮಂಟಪವಿತ್ತು; ಅದರ ಉದ್ದಳತೆ ದೇವಾಲಯದ ಅಗಲಕ್ಕೆ ಸರಿಯಾಗಿ 4:5 ಮೀಟರ್, ಅಗಲಳತೆ 9 ಮೀಟರ್.",
			"4": "<sup>4</sup> ಇದಲ್ಲದೆ, ಅವನು ಆಲಯಕ್ಕೆ ತೆರೆಯಲಾಗದ ಜಾಲಂಧರ ಕಿಟಕಿಗಳನ್ನಿಡಿಸಿದನು.",
			"5": "<sup>5</sup> ದೇವಾಲಯದ ಗೋಡೆಯ ಸುತ್ತಲೂ ಅಂದರೆ, ಪರಿಶುದ್ಧಸ್ಥಳ ಹಾಗು ಮಹಾಪರಿಶುದ್ಧಸ್ಥಳ ಇವುಗಳ ಗೋಡೆಯ ಸುತ್ತಲೂ ಇನ್ನೊಂದು ಗೋಡೆಯನ್ನು ಕಟ್ಟಿಸಿ, ಇವುಗಳ ನಡುವೆ ಅಂತಸ್ತುಗಳುಳ್ಳ ಕೊಠಡಿಗಳನ್ನು ಮಾಡಿಸಿದನು.",
			"6": "<sup>6</sup> ಕೆಳಗಿನ ಕೊಠಡಿಗಳ ಅಗಲ 2:2 ಮೀಟರ್; ಮೊದಲನೆಯ ಅಂತಸ್ತಿನ ಕೊಠಡಿಗಳ ಅಗಲ 2:7 ಮೀಟರ್; ಎರಡನೆಯ ಅಂತಸ್ತಿನ ಕೊಠಡಿಗಳ ಅಗಲ 3:1 ಮೀಟರ್. ಅಂತಸ್ತಿನ ತೊಲೆಗಳನ್ನಿಡುವಾಗ ದೇವಾಲಯದ ಗೋಡೆಯಲ್ಲಿ ತೂತಾಗಬಾರದು ಎಂದು ಗೋಡೆಯ ಹೊರ ಮೈಯನ್ನು ಸೋಪಾನಾಕಾರವಾಗಿ ಮಾಡಿಸಿದನು.",
			"7": "<sup>7</sup> ದೇವಾಲಯವನ್ನು ಕಟ್ಟುವಾಗ, ಕಲ್ಲುಗಳನ್ನು ಕಲ್ಲುಗಣಿಯಲ್ಲಿಯೇ ಸಿದ್ಧಮಾಡಿಕೊಂಡು ಬಂದು ಕಟ್ಟಿದ್ದರಿಂದ, ಕಟ್ಟುವ ದಿವಸಗಳಲ್ಲೆಲ್ಲಾ ಸುತ್ತಿಗೆ, ಉಳಿ, ಮುಂತಾದ ಕಬ್ಬಿಣದ ಸಾಮಾನುಗಳ ಶಬ್ದವೂ ಕೇಳಿಸಲಿಲ್ಲ.",
			"8": "<sup>8</sup> ಕೆಳಗಿನ ಕೊಠಡಿಗಳಿಗೆ ಹೋಗುವ ಬಾಗಿಲು ಆಲಯದ ಬಲಪಾರ್ಶ್ವದಲ್ಲಿತ್ತು. ಅಲ್ಲಿಂದ ಮೊದಲನೆಯ ಅಂತಸ್ತಿಗೂ ಎರಡನೆಯ ಅಂತಸ್ತಿಗೂ ಹೋಗಬೇಕಾದರೆ ವಕ್ರಸೋಪಾನಗಳಿದ್ದವು.",
			"9": "<sup>9</sup> ಈ ಪ್ರಕಾರ ಅವನು ಮನೆಯನ್ನು ಕಟ್ಟಿಸಿ, ದೇವದಾರಿನ ತೊಲೆ ಹಲಿಗೆಗಳಿಂದ ಮಾಳಿಗೆಯನ್ನು ಮಾಡಿಸಿ, ಕೆಲಸ ಮುಗಿಸಿದನು.",
			"10": "<sup>10</sup> ಅವನು ದೇವಾಲಯದ ಸುತ್ತಲೂ ಒಂದರ ಮೇಲೊಂದು 2:2 ಮೀಟರುಗಳಂತೆ ಎತ್ತರವಾದ ಕೊಠಡಿಗಳನ್ನು ಕಟ್ಟಿಸಿದನು. ಇವು ದೇವದಾರಿನ ತೊಲೆಗಳಿಂದ ದೇವಾಲಯಕ್ಕೆ ಹೊಂದಿಕೊಂಡಿದ್ದವು.",
			"11": "<sup>11</sup> ಸರ್ವೇಶ್ವರಸ್ವಾಮಿ ಸೊಲೊಮೋನನಿಗೆ, “ನೀನು ಈಗ ಒಂದು ಆಲಯವನ್ನು ಕಟ್ಟಿಸುತ್ತಿರುವೆ. ನನ್ನ ನಿಯಮವಿಧಿಗಳನ್ನು ಅನುಸರಿಸಿ ನನ್ನ ಆಜ್ಞೆಗಳನ್ನು ಕೈಗೊಂಡು, ನೀನು ನಡೆಯುವುದಾದರೆ, ನಿನ್ನನ್ನು ಕುರಿತು ನಿನ್ನ ತಂದೆ ದಾವೀದನಿಗೆ ನಾನು ಮಾಡಿದ ವಾಗ್ದಾನವನ್ನು ನೆರವೇರಿಸುವೆನು.",
			"13": "<sup>13</sup> ನನ್ನ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಯೇಲರನ್ನು ಕೈಬಿಡದೆ ಅವರ ಮಧ್ಯದಲ್ಲೇ ವಾಸಿಸುವೆನು,” ಎಂದು ಹೇಳಿದರು.",
			"14": "<sup>14</sup> ಹೀಗೆ ಸೊಲೊಮೋನನು ದೇವಾಲಯವನ್ನು ಕಟ್ಟಿಮುಗಿಸಿದನು.",
			"15": "<sup>15</sup> ದೇವಾಲಯದ ಗೋಡೆಗಳ ಒಳಮೈಯನ್ನು ನೆಲದಿಂದ ಮಾಳಿಗೆಯವರೆಗೆ ದೇವದಾರಿನ ಹಲಗೆಗಳಿಂದ ಹೊದಿಸಿದನು. ನೆಲಕ್ಕೆ ತುರಾಯಿಮರದ ಹಲಗೆಗಳನ್ನು ಹಾಕಿಸಿದನು.",
			"16": "<sup>16</sup> ಇದಲ್ಲದೆ, ದೇವಾಲಯದೊಳಗೆ ಹಿಂದಿನ 9 ಮೀಟರ್ ಸ್ಥಳವನ್ನು ಬಿಟ್ಟು ನೆಲದಿಂದ ತೊಲೆಗಳವರೆಗೆ ದೇವದಾರಿನ ಹಲಗೆಗಳಿಂದ ಒಂದು ಗೋಡೆಯನ್ನು ಮಾಡಿಸಿ ಅದರ ಹಿಂದಿನ ಭಾಗವನ್ನು ಗರ್ಭಗುಡಿ, ಅಥವಾ ಮಹಾಪರಿಶುದ್ಧ ಸ್ಥಳವೆಂದು ಪ್ರತ್ಯೇಕಿಸಿದನು.",
			"17": "<sup>17</sup> ದೇವಾಲಯದ ಪರಿಶುದ್ಧ ಸ್ಥಳವೆನಿಸಿಕೊಳ್ಳುವ ಮುಂದಿನ ಭಾಗ 18 ಮೀಟರ್ ಉದ್ದವಿತ್ತು.",
			"18": "<sup>18</sup> ದೇವಾಲಯದೊಳಗೆ ಕಲ್ಲು ಕಾಣಿಸದಂತೆ, ಗೋಡೆಯನ್ನೆಲ್ಲಾ ಬಳ್ಳಿಗಳೂ ಹೂವುಗಳೂ ಕೆತ್ತಿರುವ ದೇವದಾರಿನ ಹಲಗೆಗಳಿಂದ ಹೊದಿಸಿದನು.",
			"19": "<sup>19</sup> ಸರ್ವೇಶ್ವರನ ಒಡಂಬಡಿಕೆಯ ಮಂಜೂಷವನ್ನು ಇಡುವುದಕ್ಕಾಗಿ ದೇವಾಲಯದೊಳಗೆ ಒಂದು ಭಾಗವನ್ನು ‘ಗರ್ಭಗುಡಿ’ ಎಂದು ಪ್ರತ್ಯೇಕಿಸಿದನು.",
			"20": "<sup>20</sup> ಆ ಗರ್ಭಗುಡಿ 9 ಮೀಟರ್ ಉದ್ದ, 9 ಮೀಟರ್ ಅಗಲ, 9 ಮೀಟರ್ ಎತ್ತರ ಇತ್ತು. ಅದನ್ನು ಚೊಕ್ಕ ಬಂಗಾರದ ತಗಡಿನಿಂದಲೂ ವೇದಿಯನ್ನು ದೇವದಾರುಮರದಿಂದಲೂ ಹೊದಿಸಿದನು.",
			"21": "<sup>21</sup> ಸೊಲೊಮೋನನು ದೇವಾಲಯದ ಒಳಮೈಯನ್ನು ಚೊಕ್ಕಬಂಗಾರದ ತಗಡಿನಿಂದ ಹೊದಿಸಿದನು. ಬಂಗಾರದ ತಗಡಿನಿಂದ ಹೊದಿಸಲಾಗಿದ್ದ ಮಹಾಪರಿಶುದ್ಧ ಸ್ಥಳದ ಎದುರಿನಲ್ಲಿ ಒಂದು ಗೋಡೆಯಿಂದ ಇನ್ನೊಂದು ಗೋಡೆಗೆ ಬಂಗಾರದ ಸರಪಣಿಗಳನ್ನು ಕಟ್ಟಿಸಿದನು.",
			"22": "<sup>22</sup> ದೇವಾಲಯದ ಎಲ್ಲಾ ಗೋಡೆಗಳನ್ನೂ ಮಹಾಪರಿಶುದ್ಧ ಸ್ಥಳಕ್ಕೆ ಸೇರಿದ ಪೀಠವನ್ನೂ ಪೂರ್ಣವಾಗಿ ಬಂಗಾರದ ತಗಡಿನಿಂದ ಹೊದಿಸಿದನು.",
			"23": "<sup>23</sup> ಇದಲ್ಲದೆ ಅವನು ಎಣ್ಣೇಮರದಿಂದ 4:4 ಮೀಟರ್ ಎತ್ತರವಾದ ಎರಡು ‘ಕೆರೂಬಿ’ಗಳನ್ನು ಮಾಡಿಸಿ ಅವುಗಳನ್ನು ಗರ್ಭಗುಡಿಯಲ್ಲಿರಿಸಿದನು.",
			"24": "<sup>24</sup> ಪ್ರತಿಯೊಂದು ಕೆರೂಬಿಯ ರೆಕ್ಕೆಗಳು 2:2 ಮೀಟರ್ ಉದ್ದವಿದ್ದವು. ಎರಡು ರೆಕ್ಕೆಗಳ ಅಂತರವು ತುದಿಯಿಂದ ತುದಿಗೆ 4:4 ಮೀಟರ್",
			"25": "<sup>25</sup> ಇನ್ನೊಂದು ಕೆರೂಬಿಯ ರೆಕ್ಕೆಗಳ ಅಂತರವೂ 4:4: ಮೀಟರ್. ಎರಡೂ ಕೆರೂಬಿಗಳ ಅಳತೆಯೂ ಆಕಾರವೂ ಒಂದೇ ಆಗಿತ್ತು.",
			"26": "<sup>26</sup> ಅವೆರಡೂ ಒಂದೊಂದು 4:4 ಮೀಟರ್ ಎತ್ತರವಾಗಿದ್ದವು.",
			"27": "<sup>27</sup> ಅವನು ಅವುಗಳನ್ನು ದೇವಾಲಯದ ಗರ್ಭಗುಡಿಯಲ್ಲಿರಿಸಿದನು. ಅವುಗಳ ರೆಕ್ಕೆಗಳು ಚಾಚಿದವುಗಳಾಗಿದ್ದುದರಿಂದ ಮೊದಲನೆಯ ಕೆರೂಬಿಯ ಒಂದು ರೆಕ್ಕೆ ಈಚೆಯ ಗೋಡೆಗೂ ಎರಡನೆಯ ಕೆರೂಬಿಯ ಒಂದು ರೆಕ್ಕೆ ಆಚೆಯ ಗೋಡೆಗೂ ತಗುಲಿದ್ದವು; ಅವುಗಳ ಬೇರೆ ಎರಡು ರೆಕ್ಕೆಗಳು ಮನೆಯ ಮಧ್ಯದಲ್ಲಿ ಒಂದನ್ನೊಂದು ತಗುಲಿ ಇದ್ದವು.",
			"28": "<sup>28</sup> ಈ ಕೆರೂಬಿಗಳು ಬಂಗಾರದ ತಗಡುಗಳಿಂದ ಹೊದಿಸಲ್ಪಟ್ಟಿದ್ದವು.",
			"29": "<sup>29</sup> ದೇವಾಲಯದ ಎಲ್ಲಾ ಗೋಡೆಗಳಲ್ಲಿ ಹೊರಗೂ ಒಳಗೂ ಕೆರೂಬಿ, ಖರ್ಜೂರವೃಕ್ಷ, ಹೂವು, ಇವುಗಳ ಚಿತ್ರಗಳನ್ನು ಕೆತ್ತಿಸಿದನು.",
			"30": "<sup>30</sup> ದೇವಾಲಯದ ಒಳಗೆ ಮತ್ತು ಹೊರಗಿನ ನೆಲವನ್ನು ಬಂಗಾರದ ತಗಡುಗಳಿಂದ ಹೊದಿಸಿದನು.",
			"31": "<sup>31</sup> ಗರ್ಭಗುಡಿಯ ಬಾಗಿಲಿಗೆ ಎಣ್ಣೇಮರದ ಕದಗಳನ್ನು ಹಚ್ಚಿಸಿದನು. ಚೌಕಟ್ಟು ಪಂಚಕೋನಗಳುಳ್ಳದ್ದಾಗಿತ್ತು.",
			"32": "<sup>32</sup> ಎಣ್ಣೇಮರದ ಆ ಎರಡು ಕದಗಳಲ್ಲಿಯೂ ಕೆರೂಬಿ, ಖರ್ಜೂರವೃಕ್ಷ, ಹೂವು, ಇವುಗಳ ಚಿತ್ರಗಳನ್ನು ಕೆತ್ತಿಸಿ, ಕದಗಳಿಗೆ ಬಂಗಾರದ ತಗಡನ್ನು ಹೊದಿಸಿ, ಚಿತ್ರಗಳಿದ್ದಲ್ಲಿ ಆ ತಗಡನ್ನು ಬಡಿಸಿದನು.",
			"33": "<sup>33</sup> ಇದಲ್ಲದೆ, ಪರಿಶುದ್ಧಸ್ಥಳದ ಬಾಗಲಿಗೆ ಎಣ್ಣೇಮರದಿಂದ ಚತುಷ್ಕೋನದ ಚೌಕಟ್ಟನ್ನು ಮಾಡಿಸಿ,",
			"34": "<sup>34</sup> ಅದಕ್ಕೆ ತುರಾಯಿಮರದ ಎರಡು ಕದಗಳನ್ನು ಹಚ್ಚಿಸಿದನು. ಪ್ರತಿಯೊಂದು ಕದವು ಎರಡು ಭಾಗವುಳ್ಳದ್ದಾಗಿ ಮಡಚುವುದಕ್ಕೆ ಬರುತ್ತಿತ್ತು.",
			"35": "<sup>35</sup> ಈ ಕದಗಳಲ್ಲಿ ಕೆರೂಬಿ, ಖರ್ಜೂರವೃಕ್ಷ, ಹೂವು, ಇವುಗಳ ಚಿತ್ರಗಳನ್ನು ಕೆತ್ತಿಸಿ ಅವುಗಳಿಗೆ ಬಂಗಾರದ ತಗಡನ್ನು ಹೊದಿಸಿ ಕೆತ್ತನೆ ಇದ್ದಲ್ಲಿ ಆ ತಗಡನ್ನು ಬಡಿಸಿದನು.",
			"36": "<sup>36</sup> ದೇವಾಲಯದ ಪ್ರಾಕಾರದಲ್ಲಿ ಮೂರು ಸಾಲು ಕಲ್ಲಿನ ಕಂಬಗಳನ್ನೂ ಒಂದು ಸಾಲು ದೇವದಾರುವಿನ ಕಂಬಗಳನ್ನೂ ಇರಿಸಿದನು.",
			"37": "<sup>37</sup> ಸೊಲೊಮೋನನ ಆಳ್ವಿಕೆಯ ನಾಲ್ಕನೆಯ ವರ್ಷದ ವೈಶಾಖ ಮಾಸದಲ್ಲಿ ಸರ್ವೇಶ್ವರನ ಆಲಯಕ್ಕೆ ಅಸ್ತಿವಾರ ಹಾಕಲಾಯಿತು.",
			"38": "<sup>38</sup> ಹನ್ನೊಂದನೆಯ ವರ್ಷದ ಎಂಟನೆಯ ತಿಂಗಳಾದ ಕಾರ್ತಿಕ ಮಾಸದಲ್ಲಿ ದೇವಾಲಯವೂ ಅದರ ಎಲ್ಲಾ ಕಟ್ಟಡಗಳೂ ಯೋಜನೆಯ ಪ್ರಕಾರ ಸಿದ್ಧವಾದವು. ದೇವಾಲಯವನ್ನು ಕಟ್ಟುವುದಕ್ಕೆ ಏಳು ವರ್ಷ ಹಿಡಿಯಿತು."
		},
		"7": {
			"1": "<sup>1</sup> ಸೊಲೊಮೋನನು ತನ್ನ ಅರಮನೆಯನ್ನು ಹದಿಮೂರು ವರ್ಷಗಳಲ್ಲಿ ಕಟ್ಟಿಸಿ ಮುಗಿಸಿದನು.",
			"2": "<sup>2</sup> ಅವನು ಕಟ್ಟಿಸಿದ ‘ಲೆಬನೋನಿನ ತೋಪು’ ಎನಿಸಿಕೊಳ್ಳುವ ಮಂದಿರ 44 ಮೀಟರ್ ಉದ್ದ, 22 ಮೀಟರ್ ಅಗಲ, 13:5 ಮೀಟರ್ ಎತ್ತರವಾಗಿತ್ತು. ಅದರ ಮಾಳಿಗೆಗೆ ದೇವದಾರಿನ ಮೂರು ಸಾಲು ಕಂಬಗಳು ಅವುಗಳ ಮೇಲೆ ಇಡಲಾದ ದೇವದಾರಿನ ತೊಲೆಗಳೂ ಆಧಾರವಾಗಿ ಇದ್ದವು.",
			"3": "<sup>3</sup> ಸ್ತಂಭಗಳ ಮೇಲಿರುವ ತೊಲೆಗಳ ಮೇಲೆ ದೇವದಾರಿನ ಹಲಿಗೆಗಳಿಂದ ಮಾಡಿದ ಮಾಳಿಗೆಯಿತ್ತು. ಸ್ತಂಭಗಳು ಪ್ರತಿಯೊಂದು ಸಾಲಿನಲ್ಲಿ ಹದಿನೈದರಂತೆ ಒಟ್ಟು ನಲ್ವತ್ತೈದು ಇದ್ದವು.",
			"4": "<sup>4</sup> ಬೆಳಕು ಎದುರೆದುರಾಗಿ ಬೀಳುವಂತೆ ಎರಡು ಗೋಡೆಗಳಿಗೆ ಮೂರು ಮೂರು ಸಾಲು ಬೆಳಕುಕಿಂಡಿಗಳಿದ್ದವು.",
			"5": "<sup>5</sup> ಎಲ್ಲಾ ದ್ವಾರಗಳೂ ಬೆಳಕುಕಿಂಡಿಗಳೂ ಚತುಷ್ಕೋನಾಕಾರವುಳ್ಳವುಗಳು. ಮೂರು ಮೂರು ಸಾಲು ಬೆಳಕುಕಿಂಡಿಗಳು ಎದುರೆದುರಾಗಿದ್ದವು.",
			"6": "<sup>6</sup> ಇದಲ್ಲದೆ, ಸೊಲೊಮೋನನು ಒಂದು ಕಂಬಮಂಟಪವನ್ನು ಕಟ್ಟಿಸಿದನು. ಅದರ ಉದ್ದ 22 ಮೀಟರ್; ಅಗಲ 13:5 ಮೀಟರ್, ಅದರ ಮುಂದುಗಡೆಯಲ್ಲಿ ಒಂದು ಪಡಸಾಲೆಯಿತ್ತು; ಆ ಪಡಸಾಲೆಗೆ ಕಂಬಗಳೂ ಮೆಟ್ಟಲುಗಳೂ ಇದ್ದವು.",
			"7": "<sup>7</sup> ಇದಲ್ಲದೆ ಅವನು ಸಿಂಹಾಸನ ಮಂದಿರವನ್ನು ಕಟ್ಟಿಸಿದನು. ಅವನು ಅಲ್ಲಿ ನ್ಯಾಯತೀರಿಸುತ್ತಿದ್ದುದರಿಂದ ಅದಕ್ಕೆ ನ್ಯಾಯಮಂದಿರ ಎಂದು ಹೆಸರಾಯಿತು. ಇದರ ಎಲ್ಲಾ ಗೋಡೆಗಳು ನೆಲದಿಂದ ಮಾಳಿಗೆಯವರೆಗೆ ದೇವದಾರುವಿನ ಹಲಗೆಗಳಿಂದ ಹೊದಿಸಲಾಗಿದ್ದವು.",
			"8": "<sup>8</sup> ನ್ಯಾಯಮಂದಿರದ ಹಿಂದಿನ ಪ್ರಾಕಾರದಲ್ಲಿ ತನಗಾಗಿ ಅರಮನೆಯನ್ನೂ ತನ್ನ ಹೆಂಡತಿಯಾದ ಫರೋಹನ ಮಗಳಿಗಾಗಿ ಇನ್ನೊಂದು ಮನೆಯನ್ನೂ ಕಟ್ಟಿಸಿದನು. ನ್ಯಾಯಮಂದಿರದ ಹಾಗೆಯೇ ಇವುಗಳನ್ನು ಕಟ್ಟಿಸಿದನು.",
			"9": "<sup>9</sup> ಈ ಎಲ್ಲಾ ಮಂದಿರಗಳನ್ನೂ ಅವುಗಳ ಸುತ್ತುಗೋಡೆಯನ್ನೂ ದೊಡ್ಡ ಪ್ರಾಕಾರದ ಸುತ್ತುಗೋಡೆಯನ್ನೂ ಅವುಗಳ ಸುತ್ತುಗೋಡೆಯನ್ನೂ ದೊಡ್ಡ ಪ್ರಾಕಾರದ ಸುತ್ತುಗೋಡೆಯನ್ನೂ ಅಸ್ತಿವಾರದ ಮೇಲಿನಿಂದ ಕಡೇ ವರಸೆಯವರೆಗೆ, ಅಳತೆಯ ಮೇರೆಗೆ ಕೆತ್ತಲ್ಪಟ್ಟಂಥ, ಎರಡು ಮಗ್ಗಲಿಗೂ ಗರಗಸದಿಂದ ಕೊಯ್ಯಲ್ಪಟ್ಟಂಥ, ಶ್ರೇಷ್ಠವಾದ ಕಲ್ಲುಗಳಿಂದ ಕಟ್ಟಿದನು.",
			"10": "<sup>10</sup> ಅಸ್ತಿವಾರಕ್ಕೆ ಪ್ರಯೋಗಿಸಿದ ಶ್ರೇಷ್ಠವಾದ ದೊಡ್ಡಕಲ್ಲುಗಳು ಎಂಟು ಹತ್ತು ಮೊಳ ಉದ್ದವಾಗಿದ್ದವು.",
			"11": "<sup>11</sup> ಅಸ್ತಿವಾರದ ಮೇಲಣ ಭಾಗಕ್ಕೆ ಅಳತೆಯ ಮೇರೆಗೆ ಕೆತ್ತಿದ ಶ್ರೇಷ್ಠವಾದ ಕಲ್ಲುಗಳನ್ನೂ ದೇವದಾರಿನ ಮರವನ್ನೂ ಉಪಯೋಗಿಸಿದರು.",
			"12": "<sup>12</sup> ಒಳಗಣ ಪ್ರಾಕಾರವೆನಿಸಿಕೊಳ್ಳುವ ಸರ್ವೇಶ್ವರನ ಆಲಯದ ಪ್ರಾಕಾರಕ್ಕೂ ಅರಮನೆಯ ಪ್ರಾಕಾರಕ್ಕೂ ಹೇಗೋ ಹಾಗೆಯೇ ಎಲ್ಲಾ ಮಂದಿರಗಳ ಸುತ್ತಲಿರುವ ದೊಡ್ಡ ಪ್ರಾಕಾರಕ್ಕೆ ಮೂರು ಸಾಲು ಕಲ್ಲಿನ ಕಂಬಗಳೂ ಒಂದು ಸಾಲು ದೇವದಾರಿನ ಮರದ ಕಂಬಗಳೂ ಇದ್ದವು.",
			"13": "<sup>13</sup> ಅರಸ ಸೊಲೊಮೋನನು ಟೈರ್ ಪಟ್ಟಣದಿಂದ ಹೀರಾಮ್ ಎಂಬವನನ್ನು ಕರೆಸಿದನು.",
			"14": "<sup>14</sup> ಇವನು ನಫ್ತಾಲಿಕುಲದ ಒಬ್ಬ ವಿಧವೆಯಲ್ಲಿ ಟೈರ್ ಪಟ್ಟಣದ ಒಬ್ಬ ಕಂಚುಗಾರನಿಗೆ ಹುಟ್ಟಿದವನು. ಜಾಣನೂ ತಾಮ್ರದ ಕೆಲಸದಲ್ಲಿ ನಿಪುಣನೂ ಅನುಭವಶಾಲಿಯೂ ಆಗಿದ್ದ ಇವನು ಅರಸನ ಬಳಿಗೆ ಬಂದು, ಅವನ ಎಲ್ಲಾ ಕೆಲಸವನ್ನು ಮಾಡಿದನು.",
			"15": "<sup>15</sup> ಅವನು ಎರಡು ಕಂಚಿನ ಕಂಬಗಳನ್ನು ಮಾಡಿದನು. ಅವು 8 ಮೀಟರ್ ಎತ್ತರವಿದ್ದವು. ಅವುಗಳ ಸುತ್ತಳತೆಯು 5:3 ಮೀಟರ್.",
			"16": "<sup>16</sup> ಇದಲ್ಲದೆ ಅವನು ತಾಮ್ರವನ್ನು ಎರಕಹೊಯ್ದು ಕಂಬಗಳ ಮೇಲೆ ಇಡುವುದಕ್ಕಾಗಿ 2:2 ಮೀಟರ್ ಎತ್ತರವಾದ ಎರಡು ಕುಂಭಗಳನ್ನೂ",
			"17": "<sup>17</sup> ಒಂದೊಂದಕ್ಕೆ ಏಳೇಳರಂತೆ ಕುಂಭದ ಮೇಲೆ ಹಾಕುವುದಕ್ಕಾಗಿ ತಾಮ್ರದ ಸರಿಗೆಯ ಜಾಲರಿಗಳನ್ನೂ ಮಾಡಿದನು;",
			"18": "<sup>18</sup> ಇದಲ್ಲದೆ, ಅವನು ತಾಮ್ರದ ದಾಳಿಂಬೆ ಹಣ್ಣುಗಳನ್ನು ಮಾಡಿ, ಅವುಗಳನ್ನು ಕಂಬಗಳ ಮೇಲಣ ಪ್ರತಿಯೊಂದು ಕುಂಭದ ಜಾಲರಿಯ ಸುತ್ತಲೂ ಎರಡೆರಡು ಸಾಲಾಗಿ ಸಿಕ್ಕಿಸಿದನು.",
			"19": "<sup>19</sup> ಕಂಬಗಳ ಮೇಲಿರುವ ಕುಂಭಗಳ ಒಡಲು 1:8 ಮೀಟರ್ ಗಳವರೆಗೆ ಕಮಲಾಕಾರವುಳ್ಳದ್ದಾಗಿತ್ತು.",
			"20": "<sup>20</sup> ಆ ಕುಂಭಗಳ ಮೇಲಿನ ಭಾಗದಲ್ಲಿ ಒಡಲಿನ ಸುತ್ತಲೂ ಜಾಲರಿಯ ಹೊರಗೆ ದಾಳಿಂಬೆಹಣ್ಣಿನ ಆಕಾರವುಳ್ಳ ಗುಂಡುಗಳಿದ್ದವು. ಪ್ರತಿಯೊಂದು ಕುಂಭದ ಸುತ್ತಲೂ ಇನ್ನೂರು ಗುಂಡುಗಳನ್ನು ಸಾಲಾಗಿ ಸಿಕ್ಕಿಸಿದನು.",
			"21": "<sup>21</sup> ತರುವಾಯ ಅವನು ಆ ಎರಡು ಕಂಬಗಳನ್ನೂ ದೇವಾಲಯದ ಮಂಟಪದ ಬಳಿಯಲ್ಲಿ ನಿಲ್ಲಿಸಿ, ಬಲಗಡೆಯ ಕಂಬಕ್ಕೆ ಯಾಕೀನ್ ಎಂದೂ ಎಡಗಡೆಯ ಕಂಬಕ್ಕೆ ಬೋವಜ್ ಎಂದು ಹೆಸರಿಟ್ಟನು.",
			"22": "<sup>22</sup> ಅವನು ಕಂಬಗಳ ಮೇಲೆ ಕಮಲ ಅಲಂಕಾರವನ್ನು ಮಾಡಿ ಅವುಗಳ ಕೆಲಸವನ್ನು ಮುಗಿಸಿದನು.",
			"23": "<sup>23</sup> ಅನಂತರ ಅವನು ‘ಕಡಲು’ ಎನಿಸಿಕೊಳ್ಳುವ ಎರಕದ ಒಂದು ಕೊಳವನ್ನು ಮಾಡಿದನು. ಅದರ ಬಾಯಿ ಚಕ್ರಾಕಾರವಾಗಿಯೂ ಅಂಚಿನಿಂದ ಅಂಚಿಗೆ 4:4 ಮೀಟರ್ ಉದ್ದ ಇತ್ತು. ಅದರ ಎತ್ತರ 2:2 ಮೀಟರ್, ಸುತ್ತಳತೆ 13:2 ಮೀಟರ್,",
			"24": "<sup>24</sup> ಅದನ್ನು ಎರಕ ಹೊಯ್ಯುವಾಗ ಅದರ ಅಂಚಿನ ಕೆಳಗೆ ಸುತ್ತಲೂ ಹತ್ತತ್ತು ಮೊಳ ಉದ್ದವಾದ ಬಳ್ಳಿಗಳನ್ನು ಎರಡು ಸಾಲಾಗಿ ಎರಕಹೊಯ್ದಿದ್ದನು.",
			"25": "<sup>25</sup> ಹನ್ನೆರಡು ಎರಕದ ಹೋರಿಗಳು ಅದನ್ನು ಹೊತ್ತಿದ್ದವು. ಅವುಗಳಲ್ಲಿ ಮೂರು ಉತ್ತರ ದಿಕ್ಕಿಗೂ ಮೂರು ಪಶ್ಚಿಮ ದಿಕ್ಕಿಗೂ ಮೂರು ದಕ್ಷಿಣ ದಿಕ್ಕಿಗೂ ಮೂರು ಪೂರ್ವ ದಿಕ್ಕಿಗೂ ಮುಖಮಾಡಿಕೊಂಡಿದ್ದವು. ಕಡಲು ಅವುಗಳ ಬೆನ್ನಿನ ಮೇಲೆ ಇತ್ತು. ಅವುಗಳ ಹಿಂಭಾಗ ಒಳಗಡೆ ಇತ್ತು.",
			"26": "<sup>26</sup> ಕೊಳದ ತಗಡು ನಾಲ್ಕು ಬೆರಳು ದಪ್ಪವಾಗಿತ್ತು. ಅದರ ಅಂಚು ಕಮಲಾಕಾರದ ಬಟ್ಟಲನ್ನು ಹೋಲುತ್ತಿತ್ತು. ಅದು ಸುಮಾರು ನಾಲ್ವತ್ತು ಸಾವಿರ ಲೀಟರ್ ನೀರನ್ನು ಹಿಡಿಯುತ್ತಿತ್ತು.",
			"27": "<sup>27</sup> ಇದಲ್ಲದೆ, ಹೀರಾಮನು ಹತ್ತು ಕಂಚಿನ ಪೀಠಗಳನ್ನು ಮಾಡಿದನು. ಪ್ರತಿಯೊಂದು 1:8 ಮೀಟರ್ ಉದ್ದ, 1:8 ಮೀಟರ್ ಅಗಲ, 1:3 ಮೀಟರ್ ಎತ್ತರ ಇತ್ತು.",
			"28": "<sup>28</sup> ಅವುಗಳನ್ನು ಮಾಡಿದ ವಿಧಾನ ಹೇಗೆಂದರೆ: ಅವುಗಳಲ್ಲಿ ನಿಲುವು ಪಟ್ಟಿಗಳಿಂದ ಕೂಡಿದ ಅಡ್ಡಪಟ್ಟಿಗಳಿದ್ದವು.",
			"29": "<sup>29</sup> ಆ ಅಡ್ಡಪಟ್ಟಿಗಳ ಮೇಲೆ ಸಿಂಹ, ಹೋರಿ, ಕೆರೂಬಿ, ಇವುಗಳ ಚಿತ್ರಗಳಿದ್ದವು; ನಿಲುವುಪಟ್ಟಿಗಳಿಗೂ ಇದೇ ಅಲಂಕಾರವಿತ್ತು; ಸಿಂಹ, ಹೋರಿ, ಇವುಗಳ ಮೇಲೂ ಕೆಳಗೂ ತೋರಣಚಿತ್ರಗಳಿದ್ದವು.",
			"30": "<sup>30</sup> ಪ್ರತಿಯೊಂದು ಪೀಠಕ್ಕೆ ನಾಲ್ಕು ತಾಮ್ರದ ಗಾಲಿಗಳೂ ಅಚ್ಚುಗಳೂ ಇದ್ದವು. ಪ್ರತಿಯೊಂದು ನಿಲುವುಪಟ್ಟಿಯ ಮೇಲಿನ ತುದಿಯಲ್ಲಿ ಎರಕಹೊಯ್ದ ತಾಮ್ರದ ಹಿಡಿಗಳಿದ್ದವು; ಅವು ಪೀಠದ ಮೇಲಿನ ನೀರಿನ ಬಾನೆಯನ್ನು ಹಿಡಿಯುತ್ತಿದ್ದವು. ಅವುಗಳ ಹೊರ ಮೈಗೆ ತೋರಣಚಿತ್ರಗಳಿದ್ದವು.",
			"31": "<sup>31</sup> ಈ ಬಾನೆಯನ್ನು ಇಡುವುದಕ್ಕಾಗಿ ಪೀಠದ ಮೇಲಿನ ಭಾಗದಲ್ಲಿ ಒಂದು ಅಥವಾ ಒಂದುವರೆ ಮೊಳ ಎತ್ತರವಾದ ಬಾಯಿ ಇತ್ತು. ಚಕ್ರಾಕಾರವಾಗಿದ್ದ ಈ ಬಾಯಿಯು ಒಂದೂವರೆ ಮೊಳ ಅಗಲವಾಗಿತ್ತು. ಇದರ ಮೇಲೆಯೂ ಚಿತ್ರಗಳಿದ್ದವು. ಇದಕ್ಕೆ ಆಧಾರವಾದ ಅಡ್ಡಪಟ್ಟಿಗಳು ಚಕ್ರಾಕಾರವಾಗಿರದೆ ಚತುಷ್ಕೋನದವುಗಳಾಗಿದ್ದವು.",
			"32": "<sup>32</sup> ಮೇಲೆ ಕಂಡ ನಾಲ್ಕು ಗಾಲಿಗಳು ಅಡ್ಡಪಟ್ಟಿಗಳ ಕೆಳಗೆ ಇದ್ದವು. ಇರಸೂ ಪೀಠವೂ ಅಖಂಡವಾಗಿದ್ದವು; ಪ್ರತಿಯೊಂದು ಗಾಲಿಯೂ ಒಂದುವರೆ ಮೊಳ ಎತ್ತರವಿತ್ತು.",
			"33": "<sup>33</sup> ಅವು ಸಾಧಾರಣವಾದ ಬಂಡಿಗಳ ಗಾಲಿಗಳಂತೆಯೇ ಇದ್ದವು. ಆದರೆ ಅವುಗಳ ಕಟ್ಟು, ಅರ, ಕುಂಭ, ಇವುಗಳೂ ಅವುಗಳಿಗೆ ಸಂಬಂಧಪಟ್ಟ ಇರಸೂ ತಾಮ್ರದವುಗಳು.",
			"34": "<sup>34</sup> ಪ್ರತಿಯೊಂದು ಪೀಠದ ತುದಿಯಲ್ಲಿದ್ದ ನಾಲ್ಕು ಹಿಡಿಗಳು ಪೀಠದೊಡನೆ ಅಖಂಡವಾಗಿದ್ದವು.",
			"35": "<sup>35</sup> ಪೀಠದ ಮೇಲಿನ ಭಾಗದಲ್ಲಿದ್ದ ಚಕ್ರಾಕಾರ ಆದ ಬಾಯಿಯು ಅರ್ಧಮೊಳ ಎತ್ತರವಾಗಿತ್ತು. ಪೀಠದ ಮೇಲಿನ ಭಾಗದಲ್ಲಿದ್ದ ಹಿಡಿಗಳೂ ಅಡ್ಡಪಟ್ಟಿಗಳೂ ಅದರೊಡನೆ ಅಖಂಡವಾಗಿದ್ದವು;",
			"36": "<sup>36</sup> ಆ ಅಡ್ಡಪಟ್ಟಿಗಳಿಗೂ ಹಿಡಿಗಳ ಹೊರಮೈಗೂ ಸ್ಥಳವಿದ್ದ ಪ್ರಕಾರ ಕೆರೂಬಿ, ಸಿಂಹ, ಖರ್ಜೂರವೃಕ್ಷ, ಇವುಗಳ ಚಿತ್ರಗಳನ್ನೂ ಈ ಚಿತ್ರಗಳ ಸುತ್ತಲೂ ತೋರಣಚಿತ್ರಗಳನ್ನೂ ಮಾಡಿದನು.",
			"37": "<sup>37</sup> ಲೋಹಗಳನ್ನು ಒಂದೇ ಸಾರಿ ಕರಗಿಸಿ ಆ ಹತ್ತು ಪೀಠಗಳನ್ನು ಮಾಡಿದನು. ಅವುಗಳ ಆಕಾರವೂ ಅಳತೆಯೂ ಒಂದೇ.",
			"38": "<sup>38</sup> ಇದಲ್ಲದೆ ಹೀರಾಮನು 800 ಲೀಟರ್ ನೀರು ಹಿಡಿಯುವ ನಾಲ್ಕು ಮೊಳ ಅಗಲವಾದ ಹತ್ತು ಬಾನೆಗಳನ್ನು ಮಾಡಿ ಪ್ರತಿ ಒಂದು ಪೀಠದ ಮೇಲೆ ಒಂದೊಂದು ಬಾನೆಯನ್ನು ಇಟ್ಟನು.",
			"39": "<sup>39</sup> ಐದು ಪೀಠಗಳನ್ನು ದೇವಾಲಯದ ಬಲಗಡೆಯಲ್ಲಿಯೂ ಐದು ಪೀಠಗಳನ್ನು ಎಡಗಡೆಯಲ್ಲಿಯೂ ಇಟ್ಟನು. ಕಂಚಿನ ಕಡಲನ್ನು ದೇವಾಲಯದ ಬಲಗಡೆಯಲ್ಲಿ, ಅಂದರೆ ಪ್ರಾಕಾರದ ಆಗ್ನೇಯ ದಿಕ್ಕಿನಲ್ಲಿರಿಸಿದನು.",
			"40": "<sup>40</sup> ಇದಲ್ಲದೆ ಹೀರಾಮನು ಬಟ್ಟಲುಗಳನ್ನೂ ಸಲಿಕೆಗಳನ್ನೂ ಬೋಗುಣಿಗಳನ್ನೂ ಮಾಡಿದನು. ಹೀರಾಮನು ಸೊಲೊಮೋನನ ಅಪ್ಪಣೆಯಂತೆ ದೇವಾಲಯಕ್ಕಾಗಿ ಮಾಡಿದ ಒಟ್ಟು ಸಾಮಾನುಗಳು ಯಾವುವೆಂದರೆ :-",
			"41": "<sup>41</sup> ಎರಡು ಕಂಬಗಳು; ಅವುಗಳ ಮೇಲಿನ ಎರಡು ಕುಂಭಗಳು, ಕುಂಭಗಳ ಮೇಲೆ ಹಾಕಲು ಎರಡು ಜಾಲರಿಗಳು;",
			"42": "<sup>42</sup> ಜಾಲರಿಗಳ ಮೇಲೆ ಎರಡೆರಡು ಸಾಲಾಗಿ ಸಿಕ್ಕಿಸಲು ತಾಮ್ರದ ನಾನೂರು ದಾಳಿಂಬೆ ಹಣ್ಣುಗಳು;",
			"43": "<sup>43</sup> ಹತ್ತು ಪೀಠಗಳು, ಅವುಗಳ ಮೇಲಿದ್ದ ಹತ್ತು ನೀರಿನ ಬಾನೆಗಳು;",
			"44": "<sup>44</sup> ಕಂಚಿನ ಕಡಲು, ಅದನ್ನು ಹೊರುವ ಹನ್ನೆರಡು ಎರಕದ ಹೋರಿಗಳು;",
			"45": "<sup>45</sup> ಹಂಡೆ, ಸಲಿಕೆ ಹಾಗು ಬೋಗುಣಿಗಳು. ಹೀರಾಮನು ಅರಸ ಸೊಲೊಮೋನನ ಅಪ್ಪಣೆಯಂತೆ ಸರ್ವೇಶ್ವರನ ಆಲಯಕ್ಕಾಗಿ ಈ ಎಲ್ಲ ಸಾಮಗ್ರಿಗಳನ್ನು ಒಪ್ಪಹಾಕಿದ ತಾಮ್ರದಿಂದ ಮಾಡಿದನು.",
			"46": "<sup>46</sup> ಅರಸನು ಜೋರ್ಡನಿನ ತಗ್ಗಿನಲ್ಲಿ ಸುಕ್ಕೋತಿಗೂ ಚಾರೆತಾನಿಗೂ ಮಧ್ಯದಲ್ಲಿರುವ ಮಣ್ಣು ನೆಲದ ಕುಲುಮೆಯಲ್ಲಿ ಎರಕಹೊಯ್ಸಿದನು.",
			"47": "<sup>47</sup> ಸೊಲೊಮೋನನು ತಾನು ಮಾಡಿಸಿದ ಸಾಮಗ್ರಿಗಳು ಅನೇಕವಾಗಿದ್ದುದರಿಂದ ಅವುಗಳ ತಾಮ್ರದ ತೂಕ ಇಷ್ಟೆಂಬುದನ್ನು ನಿರ್ಧರಿಸಲಾಗಲಿಲ್ಲ.",
			"48": "<sup>48</sup> ಸೊಲೊಮೋನನು ಸರ್ವೇಶ್ವರನ ಆಲಯಕ್ಕಾಗಿ ಮಾಡಿಸಿದ ಒಳಗಿನ ಸಾಮಗ್ರಿಗಳು ಯಾವುವೆಂದರೆ :- ಬಂಗಾರದ ಧೂಪವೇದಿಕೆ, ನೈವೇದ್ಯವಾದ ರೊಟ್ಟಿಗಳನ್ನಿಡುವುದಕ್ಕಾಗಿ ಬಂಗಾರದ ಮೇಜು,",
			"49": "<sup>49</sup> ಮಹಾಪರಿಶುದ್ಧ ಸ್ಥಳದ ಎದುರಿಗೆ ಎಡಗಡೆಯಲ್ಲೂ ಬಲಗಡೆಯಲ್ಲೂ ಐದೈದರಂತೆ ಇಡುವುದಕ್ಕಾಗಿ ಚೊಕ್ಕಬಂಗಾರದಿಂದ ಮಾಡಿದ ಪುಷ್ಪಾಲಂಕಾರವುಳ್ಳ ಹತ್ತು ದೀಪವೃಕ್ಷಗಳು, ಬಂಗಾರದ ಹಣತೆ ತಂಡಸುಗಳು,",
			"50": "<sup>50</sup> ಚೊಕ್ಕಬಂಗಾರದ ಬಟ್ಟಲುಗಳು, ಕತ್ತರಿಗಳು, ಬೋಗುಣಿಗಳು, ಧೂಪಾರತಿಗಳು, ಅಗ್ಗಿಷ್ಟಿಕೆಗಳು, ದೇವಾಲಯದ ಮಹಾಪರಿಶುದ್ಧ ಸ್ಥಳವೆನಿಸಿಕೊಳ್ಳುವ ಗರ್ಭಗುಡಿಯ ಕದಗಳಿಗೂ ಪರಿಶುದ್ಧಸ್ಥಳದ ಕದಗಳಿಗೂ ಇರುವ ಬಂಗಾರದ ತಿರುಗಣಿಗಳು,",
			"51": "<sup>51</sup> ಸೊಲೊಮೋನನು ಸರ್ವೇಶ್ವರನ ಆಲಯಕ್ಕೆ ಬೇಕಾದ ಎಲ್ಲಾ ಸಾಮಗ್ರಿಗಳನ್ನು ಸಿದ್ಧಮಾಡಿಸಿದ ನಂತರ ತನ್ನ ತಂದೆ ದಾವೀದನು ಸರ್ವೇಶ್ವರನಿಗೆ ಹರಕೆಹೊತ್ತು ಪ್ಟತಿಷ್ಠಿಸಿದ ಬೆಳ್ಳಿಬಂಗಾರವನ್ನೂ ಸಾಮಗ್ರಿಗಳನ್ನೂ ದೇವಾಲಯದ ಭಂಡಾರಕ್ಕೆ ಸೇರಿಸಿದನು."
		},
		"8": {
			"1": "<sup>1</sup> ಸರ್ವೇಶ್ವರನ ಒಡಂಬಡಿಕೆಯ ಮಂಜೂಷವನ್ನು ದಾವೀದನಗರವಾದ ಸಿಯೋನಿನಿಂದ ದೇವಾಲಯಕ್ಕೆ ತರುವುದಕ್ಕಾಗಿ, ಇಸ್ರಯೇಲರ ಹಿರಿಯರು, ಕುಲಗಳ ನಾಯಕರು, ಇವರೇ ಮೊದಲಾದ ಎಲ್ಲ ಇಸ್ರಯೇಲ್ ಪ್ರಮುಖ ವ್ಯಕ್ತಿಗಳು ಜೆರುಸಲೇಮಿಗೆ, ತನ್ನ ಆಸ್ಥಾನಕ್ಕೆ, ಬರಬೇಕೆಂದು ಅರಸ ಸೊಲೊಮೋನನು ಆಜ್ಞಾಪಿಸಿದನು.",
			"2": "<sup>2</sup> ಅಂತೆಯೇ ಆ ಇಸ್ರಯೇಲರೆಲ್ಲರು ಏಳನೆಯ ತಿಂಗಳಾದ ಅಶ್ವಯುಜ ಮಾಸದಲ್ಲಿ ಜಾತ್ರೆಯಾಗಿ ಅರಸ ಸೊಲೊಮೋನನ ಬಳಿಗೆ ಕೂಡಿಬಂದರು.",
			"3": "<sup>3</sup> ಇಸ್ರಯೇಲರ ಹಿರಿಯರೆಲ್ಲರು ಹೀಗೆ ಕೂಡಿಬಂದಿರಲು ಯಾಜಕರು ಸರ್ವೇಶ್ವರನ ಮಂಜೂಷವನ್ನು ಹೊತ್ತುಕೊಂಡರು.",
			"4": "<sup>4</sup> ಉಳಿದ ಯಾಜಕರೂ ಲೇವಿಯರೂ ದೇವದರ್ಶನದ ಗುಡಾರ ಹಾಗು ಅದರಲ್ಲಿದ್ದ ಪವಿತ್ರವಸ್ತುಗಳನ್ನು ಹೊತ್ತರು.",
			"5": "<sup>5</sup> ಅರಸ ಸೊಲೊಮೋನನು ಹಾಗು ಅಲ್ಲಿ ಕೂಡಿಬಂದಿದ್ದ ಎಲ್ಲ ಇಸ್ರಯೇಲರೂ ಮಂಜೂಷದ ಮುಂದೇ ಅಸಂಖ್ಯವಾದ ಕುರಿದನಗಳನ್ನು ಬಲಿದಾನಮಾಡಿದರು.",
			"6": "<sup>6</sup> ಯಾಜಕರು ಸರ್ವೇಶ್ವರನ ಒಡಂಬಡಿಕೆಯ ಮಂಜೂಷವನ್ನು ಹೊತ್ತುಕೊಂಡು ಬಂದು ಮಹಾಪರಿಶುದ್ಧ ಸ್ಥಳವೆನಿಸಿಕೊಳ್ಳುವ ಗರ್ಭಗುಡಿಯಲ್ಲಿ, ಕೆರೂಬಿಗಳ ರೆಕ್ಕೆಗಳ ಕೆಳಗೆ, ಅದಕ್ಕೆ ನೇಮಕವಾದ ಸ್ಥಳದಲ್ಲಿಟ್ಟರು.",
			"7": "<sup>7</sup> ಕೆರೂಬಿಗಳ ರೆಕ್ಕೆಗಳು ಮಂಜೂಷವಿದ್ದ ಸ್ಥಳದ ಮೇಲೆ ಚಾಚಿದವುಗಳಾಗಿದ್ದುದರಿಂದ ಮಂಜೂಷವು ಹಾಗು ಅದರ ಕೋಲುಗಳು ಪೂರ್ಣವಾಗಿ ಅವುಗಳ ನೆರಳಿನಲ್ಲಿದ್ದವು.",
			"8": "<sup>8</sup> ಆ ಕೋಲುಗಳು ಬಹಳ ಉದ್ದವಾಗಿದ್ದುದರಿಂದ ಅವುಗಳ ತುದಿಗಳು ಗರ್ಭಗುಡಿಯ ಎದುರಿನಲ್ಲಿರುವ ಪರಿಶುದ್ಧ ಸ್ಥಳದಲ್ಲಿ ನಿಂತವರಿಗೆ ಕಾಣಿಸುತ್ತಿದ್ದವು. ಆದರೆ ಹೊರಗೆ ನಿಂತವರಿಗೆ ಕಾಣಿಸುತ್ತಿರಲಿಲ್ಲ. ಅವು ಇಂದಿನವರೆಗೂ ಅಲ್ಲಿಯೇ ಇರುತ್ತವೆ.",
			"9": "<sup>9</sup> ಮಂಜೂಷದಲ್ಲಿ ಎರಡು ಶಿಲಾಶಾಸನಗಳನ್ನು ಬಿಟ್ಟರೆ, ಬೇರೇನೂ ಇರಲಿಲ್ಲ. ಸರ್ವೇಶ್ವರ ಈಜಿಪ್ಟಿನಿಂದ ಬಂದ ಇಸ್ರಯೇಲರೊಡನೆ ಹೋರೇಬ್ ಬೆಟ್ಟದ ಬಳಿ ಒಡಂಬಡಿಕೆ ಮಾಡಿಕೊಂಡ ಮೇಲೆ, ಮೋಶೆ ಆ ಶಾಸನಗಳನ್ನು ಅದರಲ್ಲಿ ಇಟ್ಟಿದ್ದನು.",
			"10": "<sup>10</sup> ಯಾಜಕರು ಆ ಪರಿಶುದ್ಧ ಸ್ಥಳದಿಂದ ಹೊರಗೆ ಬಂದಕೂಡಲೆ ಮೇಘವು ಸರ್ವೇಶ್ವರನ ಆಲಯವನ್ನು ತುಂಬಿಕೊಂಡಿತು.",
			"11": "<sup>11</sup> ಸರ್ವೇಶ್ವರನ ತೇಜಸ್ಸಿನಿಂದ ವ್ಯಾಪ್ತವಾಗಿದ್ದ ಆ ಮೇಘವು ಆಲಯವನ್ನು ಆವರಿಸಿಕೊಂಡದ್ದರಿಂದ ಯಾಜಕರು ಅಲ್ಲಿ ನಿಂತು ಸೇವೆಮಾಡಲಾಗಲಿಲ್ಲ.",
			"12": "<sup>12</sup> ಆಗ ಸೊಲೊಮೋನನು ಹೀಗೆಂದು ಪ್ರಾರ್ಥಿಸಿದನು : “ಹೇ ಸರ್ವೇಶ್ವರ, ರವಿಯನು ಆಗಸದಲಿ ಸ್ಥಿರವಿರಿಸಿದ ನೀವು ಆರಿಸಿಕೊಂಡಿರಿ, ಕರಿಮೋಡದಲಿ ವಾಸಿಸಲು ನಾ ನಿರ್ಮಿಸಿರುವ ಈ ಭವ್ಯಮಂದಿರ ನಿಮಗಾಗಲಿ ಶಾಶ್ವತವಾದ ನಿವಾಸ.”",
			"14": "<sup>14</sup> ಇದಾದ ಮೇಲೆ, ಎದ್ದುನಿಂತಿದ್ದ ಇಸ್ರಯೇಲ್ ಸಮಾಜದ ಕಡೆಗೆ ಅರಸನು ತಿರುಗಿಕೊಂಡು ಅವರನ್ನು ಆಶೀರ್ವದಿಸಿದನು.",
			"15": "<sup>15</sup> “ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ಸ್ತೋತ್ರವಾಗಲಿ! ಅವರು ನನ್ನ ತಂದೆ ದಾವೀದನಿಗೆ ಬಾಯಿಂದ ನುಡಿದದ್ದನ್ನು ಈಗ ಕೈಯಿಂದ ನೆರವೇರಿಸಿದ್ದಾರೆ.",
			"16": "<sup>16</sup> ‘ನಾನು ನನ್ನ ಜನರಾದ ಇಸ್ರಯೇಲರನ್ನು ಈಜಿಪ್ಟಿನಿಂದ ಬರಮಾಡಿದಂದಿನಿಂದ ನನ್ನ ನಾಮದ ನಿವಾಸಕ್ಕಾಗಿ ಆಲಯ ಸ್ಥಾನವನ್ನಾಗಲಿ, ಇಸ್ರಯೇಲ್ ಕುಲಗಳ ಯಾವ ಪಟ್ಟಣವನ್ನಾಗಲಿ ಆರಿಸಿಕೊಳ್ಳಲಿಲ್ಲ. ಆದರೆ ನನ್ನ ಪ್ರಜೆ ಇಸ್ರಯೇಲರನ್ನು ಆಳುವುದಕ್ಕೆ ದಾವೀದನನ್ನು ಆರಿಸಿಕೊಂಡೆ’ ಎಂದು ಹೇಳಿದ್ದರು.",
			"17": "<sup>17</sup> ನನ್ನ ತಂದೆ ದಾವೀದನು ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಹೆಸರಿಗಾಗಿ ಒಂದು ಆಲಯವನ್ನು ಕಟ್ಟಬೇಕೆಂದು ಮನಸ್ಸುಮಾಡಿದಾಗ,",
			"18": "<sup>18</sup> ‘ನೀನು ನನ್ನ ಹೆಸರಿಗಾಗಿ ಒಂದು ಮನೆಯನ್ನು ಕಟ್ಟುವುದಕ್ಕೆ ಮನಸ್ಸುಮಾಡಿದ್ದು ಒಳ್ಳೆಯದೇ ಸರಿ.",
			"19": "<sup>19</sup> ಆದರೆ ಅದನ್ನು ಕಟ್ಟಬೇಕಾದವನು ನೀನಲ್ಲ; ನಿನ್ನಿಂದ ಹುಟ್ಟುವ ಮಗನು ನನ್ನ ಹೆಸರಿಗಾಗಿ ಒಂದು ಆಲಯವನ್ನು ಕಟ್ಟಬೇಕು’ ಎಂದು ಹೇಳಿದರು.",
			"20": "<sup>20</sup> ಸರ್ವೇಶ್ವರ ತಾವು ಕೊಟ್ಟ ಮಾತನ್ನು ನೆರವೇರಿಸಿದ್ದಾರೆ. ಅವರ ವಾಗ್ದಾನದಂತೆ ನಾನು ನನ್ನ ತಂದೆ ದಾವೀದರ ಸ್ಥಾನದಲ್ಲಿ ಇಸ್ರಯೇಲ್ ಸಿಂಹಾಸನದ ಮೇಲೆ ಅರಸನಾಗಿ ಕುಳಿತುಕೊಂಡಿದ್ದೇನೆ; ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಹೆಸರಿಗೋಸ್ಕರ ಈ ಆಲಯವನ್ನು ಕಟ್ಟಿಸಿದ್ದೇನೆ;",
			"21": "<sup>21</sup> ಇದಲ್ಲದೆ, ಈಜಿಪ್ಟಿನಿಂದ ಬಿಡುಗಡೆಹೊಂದಿದ ನಮ್ಮ ಪಿತೃಗಳಿಗೆ ಸರ್ವೆಶ್ವರನಿಂದ ದೊರೆತ ನಿಬಂಧನಶಾಸನಗಳಿರುವ ಮಂಜೂಷಕ್ಕೆ ಪ್ರತ್ಯೇಕ ಸ್ಥಳವನ್ನು ಗುರುತಿಸಿ ಅಲ್ಲಿ ಅದನ್ನು ಇಟ್ಟಿದ್ದೇನೆ,” ಎಂದು ಹೇಳಿದನು.",
			"22": "<sup>22</sup> ಬಳಿಕ ಸೊಲೊಮೋನನು ಇಸ್ರಯೇಲರೆಲ್ಲರ ಎದುರಿನಲ್ಲಿ ಸರ್ವೇಶ್ವರನ ಬಲಿಪೀಠದ ಮುಂದೆ ನಿಂತು, ಆಕಾಶದ ಕಡೆಗೆ ಕೈಗಳನ್ನೆತ್ತಿ, ಹೀಗೆ ಪ್ರಾರ್ಥಿಸಿದನು:",
			"23": "<sup>23</sup> “ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರಾ, ಭೂಲೋಕದಲ್ಲೂ ಪರಲೋಕದಲ್ಲೂ ನಿಮಗೆ ಸಮಾನರಾದ ದೇವರಿಲ್ಲ; ಪೂರ್ಣಮನಸ್ಸಿನಿಂದ ನಿಮಗೆ ಪ್ರಾಮಾಣಿಕರಾಗಿ ನಡೆದುಕೊಳ್ಳುವಂಥ ಭಕ್ತರ ಬಗ್ಗೆ ನಿಮ್ಮ ಒಡಂಬಡಿಕೆಯನ್ನೂ ಕೃಪೆಯನ್ನೂ ನೆರವೇರಿಸುವವರು ನೀವು.",
			"24": "<sup>24</sup> ನಿಮ್ಮ ದಾಸನೂ ನನ್ನ ತಂದೆಯೂ ಆದ ದಾವೀದನಿಗೆ ಕೊಟ್ಟ ಮಾತನ್ನು ಈಡೇರಿಸಿದ್ದೀರಿ; ನಿಮ್ಮ ಬಾಯಿ ನುಡಿದದ್ದನ್ನು ನಿಮ್ಮ ಕೈ ಈಗ ನೆರವೇರಿಸಿತು.",
			"25": "<sup>25</sup> ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರಾ, ನೀವು ನನ್ನ ತಂದೆ ದಾವೀದನಿಗೆ, ನಿನ್ನ ಸಂತಾನದವರು ನಿನ್ನಂತೆ ಜಾಗರೂಕತೆಯಿಂದ ನನ್ನ ಮಾರ್ಗದಲ್ಲೇ ನಡೆದುಕೊಳ್ಳುವುದಾದರೆ, ಅವರು ಇಸ್ರಯೇಲ್ ಸಿಂಹಾಸನದ ಮೇಲೆ ಕೂತುಕೊಳ್ಳುವುದು ತಪ್ಪದು; ಎಂಬುದಾಗಿ ವಾಗ್ದಾನಮಾಡಿದ್ದೀರಿ; ಅದನ್ನು ನೆರವೇರಿಸಿರಿ.",
			"26": "<sup>26</sup> ಇಸ್ರಯೇಲ್ ದೇವರೇ, ನೀವು ನಿಮ್ಮ ದಾಸನೂ ನನ್ನ ತಂದೆಯೂ ಆದ ದಾವೀದನಿಗೆ ನುಡಿದದ್ದೆಲ್ಲವೂ ಸಾರ್ಥಕವಾಗಲಿ!",
			"27": "<sup>27</sup> “ದೇವರು ನಿಜವಾಗಿ ಭೂಲೋಕದಲ್ಲಿ ವಾಸಿಸುವರೋ? ಆಕಾಶವೂ, ಉನ್ನತೋನ್ನತ ಆದ ಆಕಾಶವೂ, ನಿಮ್ಮ ವಾಸಕ್ಕೆ ಸಾಲದು; ಹೀಗಿರುವಲ್ಲಿ ನಾನು ಕಟ್ಟಿಸಿದ ಈ ಮಂದಿರವು ಹೇಗೆ ತಾನೆ ಸಾಕಾದೀತು?",
			"28": "<sup>28</sup> ಆದರೂ ನನ್ನ ದೇವರೇ, ಸರ್ವೇಶ್ವರಾ, ನಿಮ್ಮ ದಾಸನ ಪ್ರಾರ್ಥನೆಗೂ ವಿಜ್ಞಾಪನೆಗೂ ಕಿವಿಗೊಡಿ; ಈ ದಿನ ನಿಮ್ಮನ್ನು ಪ್ರಾರ್ಥಿಸುತ್ತಿರುವ ನಿಮ್ಮ ದಾಸನ ಮೊರೆಯನ್ನು ಆಲಿಸಿರಿ.",
			"29": "<sup>29</sup> ಈ ಸ್ಥಳವನ್ನು ಕುರಿತು, ‘ನನ್ನ ನಾಮಪ್ರಭಾವವು ಇಲ್ಲಿ ವಾಸಿಸುವುದು’ ಎಂದು ಹೇಳಿದವರೇ, ನಿಮ್ಮ ಕಟಾಕ್ಷವು ಹಗಲಿರುಳೂ ಈ ಮಂದಿರದ ಮೇಲಿರಲಿ; ಇಲ್ಲಿ ನಿಮ್ಮ ದಾಸನು ನಿಮ್ಮನ್ನು ಪ್ರಾರ್ಥಿಸುವಾಗಲೆಲ್ಲಾ ಅವನಿಗೆ ಸದುತ್ತರವನ್ನು ದಯಪಾಲಿಸಿರಿ.",
			"30": "<sup>30</sup> ನಿಮ್ಮ ದಾಸನಾದ ನಾನಾಗಲಿ, ನಿಮ್ಮ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಯೇಲರಾಗಲಿ, ಈ ಸ್ಥಳದ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡು ನಿಮ್ಮನ್ನು ಪ್ರಾರ್ಥಿಸಿದರೆ ನಿಮ್ಮ ನಿವಾಸವಾಗಿರುವ ಪರಲೋಕದಿಂದ ನಮ್ಮ ಬಿನ್ನಹವನ್ನು ಕೇಳಿ, ಕ್ಷಮೆಯನ್ನು ಅನುಗ್ರಹಿಸಿರಿ.",
			"31": "<sup>31</sup> “ನೆರೆಯವನಿಗೆ ವಿರುದ್ಧ ತಪ್ಪುಮಾಡಿದವ ಎಂಬ ಸಂಶಯಕ್ಕೆ ಗುರಿಯಾದ ಒಬ್ಬ ವ್ಯಕ್ತಿ ತಾನು ನಿರ್ದೋಷಿಯೆಂದು ಪ್ರಮಾಣಮಾಡಬೇಕಾದಾಗ, ಅಂಥವನು ಈ ಆಲಯಕ್ಕೆ ಬಂದು, ನಿಮ್ಮ ಪೀಠದ ಮುಂದೆ ನಿಂತು ಪ್ರಮಾಣಮಾಡಿದರೆ,",
			"32": "<sup>32</sup> ಪರಲೋಕದಲ್ಲಿರುವ ನೀವು ಅದನ್ನು ಕೇಳಿ ನಿಮ್ಮ ದಾಸರಾದ ಭಕ್ತರ ವ್ಯಾಜ್ಯವನ್ನು ತೀರಿಸಿರಿ; ದುಷ್ಟನನ್ನು ಖಂಡಿಸಿ ಅವನ ತಪ್ಪನ್ನು ಅವನ ತಲೆಯ ಮೇಲೆಯೇ ಹೊರಿಸಿರಿ; ಅವನು ಅಪರಾಧಿಯೆಂದು ತೋರಿಸಿಕೊಡಿ; ನೀತಿವಂತನಿಗೆ ಅವನ ನೀತಿಯ ಫಲವನ್ನು ಅನುಗ್ರಹಿಸಿ ಅವನು ನೀತಿವಂತನೆಂದು ತೋರಿಸಿಕೊಡಿ..",
			"33": "<sup>33</sup> “ತಮ್ಮ ಪಾಪಗಳ ಸಲುವಾಗಿ ಶತ್ರುಗಳ ಮುಂದೆ ತಲೆತಗ್ಗಿಸಬೇಕಾಗುವ ನಿಮ್ಮ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಯೇಲರು ನಿಮ್ಮ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡು, ನಿಮ್ಮ ನಾಮವನ್ನು ಎತ್ತಿ ಈ ಆಲಯದಲ್ಲಿ ನಿಮಗೆ ವಿಜ್ಞಾಪನೆಯನ್ನೂ ಪ್ರಾರ್ಥನೆಯನ್ನೂ ಮಾಡಿದರೆ,",
			"34": "<sup>34</sup> ಪರಲೋಕದಲ್ಲಿರುವ ನೀವು ಆಲಿಸಿ, ಅವರ ಪಾಪಗಳನ್ನು ಕ್ಷಮಿಸಿ, ಅವರ ಪಿತೃಗಳಿಗೆ ನೀವು ಕೊಟ್ಟ ಈ ನಾಡಿಗೆ ಅವರನ್ನು ಮರಳಿಬರಮಾಡಿ.",
			"35": "<sup>35</sup> “ಅವರ ಪಾಪಗಳ ನಿಮಿತ್ತ ಆಕಾಶವು ಮಳೆಗರೆಯದೆ ತೆರೆ ಮುಚ್ಚಿಕೊಂಡಾಗ, ಅವರು ತಮ್ಮ ಪಾಪಗಳನ್ನು ಬಿಟ್ಟು, ತಮ್ಮನ್ನು ತಗ್ಗಿಸಿದವರು ನೀವೇ ಎಂದು ನಿಮ್ಮ ನಾಮವನ್ನು ಎತ್ತಿ ಈ ಆಲಯದ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡು ಪ್ರಾರ್ಥಿಸಿದರೆ,",
			"36": "<sup>36</sup> ಪರಲೋಕದಲ್ಲಿರುವ ನೀವು ಆಲಿಸಿ ನಿಮ್ಮ ಭಕ್ತರೂ ಪ್ರಜೆಗಳೂ ಆದ ಇಸ್ರಯೇಲರ ಪಾಪಗಳನ್ನು ಕ್ಷಮಿಸಿರಿ; ಅವರು ನಡೆಯಬೇಕಾದ ಮಾರ್ಗವನ್ನು ತೋರಿಸಿರಿ; ನಿಮ್ಮ ಪ್ರಜೆಗೆ ಸ್ವಂತ ಸೊತ್ತಾಗಿ ಕೊಟ್ಟ ನಾಡಿಗೆ ಮಳೆಯನ್ನು ಅನುಗ್ರಹಿಸಿರಿ.",
			"37": "<sup>37</sup> “ನಾಡಿಗೆ ಕ್ಷಾಮ, ಘೋರವ್ಯಾಧಿ, ಬಿಸಿಗಾಳಿ, ಬೂದಿ, ಮಿಡತೆ, ಚಿಟ್ಟೇಹುಳು, ಪಟ್ಟಣಗಳಿಗೆ ಶತ್ರುಗಳ ಮುತ್ತಿಗೆ, ಇಂಥ ಯಾವ ಉಪದ್ರವದಿಂದಾಗಲಿ, ವ್ಯಾಧಿಯಿಂದಾಗಲಿ, ಬಾಧೆ ಉಂಟಾದರೆ,",
			"38": "<sup>38</sup> ಮನಸ್ಸಾಕ್ಷಿ ಪೀಡಿತರಾದ ಇಡೀ ಇಸ್ರಯೇಲರಾಗಲಿ, ಅವರಲ್ಲಿ ಒಬ್ಬನಾಗಲಿ, ಈ ಆಲಯದ ಕಡೆಗೆ ಕೈಯೆತ್ತಿ ನಿಮಗೆ ಪ್ರಾರ್ಥನೆಯನ್ನೂ ವಿಜ್ಞಾಪನೆಯನ್ನೂ ಮಾಡಿದರೆ",
			"39": "<sup>39</sup> ಮನುಷ್ಯರ ಅಂತರಂಗಗಳನ್ನು ಬಲ್ಲಂಥ ನೀವು, ನಿಮ್ಮ ನಿವಾಸವಾಗಿರುವ ಪರಲೋಕದಿಂದ ಆಲಿಸಿರಿ; ಅವರಿಗೆ ಪಾಪಪರಿಹಾರವನ್ನು ಅನುಗ್ರಹಿಸಿರಿ; ನೀವೊಬ್ಬರೇ ಎಲ್ಲಾ ಮಾನವರ ಹೃದಯಗಳನ್ನು ಬಲ್ಲವನಾಗಿರುವುದರಿಂದ ಪ್ರತಿಯೊಬ್ಬನಿಗೂ ಅವನವನು ಹಿಡಿಯುವ ಮಾರ್ಗಕ್ಕೆ ತಕ್ಕ ಹಾಗೆ ಫಲವನ್ನು ಕೊಡಿ.",
			"40": "<sup>40</sup> ಆಗ ನಮ್ಮ ಪಿತೃಗಳಿಗೆ ನೀವು ಕೊಟ್ಟ ಈ ನಾಡಿನಲ್ಲಿ ಅವರು ವಾಸಿಸುವ ಕಾಲವೆಲ್ಲಾ ನಿಮ್ಮಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿರುವರು.",
			"41": "<sup>41</sup> “ನಿಮ್ಮ ನಾಮಮಹತ್ತು, ಭುಜಬಲ, ಶಿಕ್ಷಾಹಸ್ತ, ಇವುಗಳ ಸಂದೇಶ ಪರರಾಜ್ಯಗಳವರಿಗೂ ಗೊತ್ತಾಗುವುದು.",
			"42": "<sup>42</sup> ನಿಮ್ಮ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಯೇಲರಿಗೆ ಸೇರದವನಾದ ಒಬ್ಬ ಪರದೇಶೀಯನು ನಿಮ್ಮ ನಾಮಸ್ತುತಿಗಾಗಿ ದೂರದೇಶದಿಂದ ಬಂದು ಈ ಆಲಯದ ಮುಂದೆ ನಿಂತು, ನಿಮ್ಮನ್ನು ಪ್ರಾರ್ಥಿಸಿದರೆ,",
			"43": "<sup>43</sup> ನಿಮ್ಮ ನಿವಾಸವಾಗಿರುವ ಪರಲೋಕದಿಂದ ಅವನ ಪ್ರಾರ್ಥನೆಯನ್ನು ಕೇಳಿ ಅವನು ಬೇಡಿಕೊಂಡದ್ದನ್ನು ಅನುಗ್ರಹಿಸಿರಿ; ಆಗ ಲೋಕದ ಎಲ್ಲಾ ಜನರು ನಿಮ್ಮ ನಾಮಮಹತ್ತನ್ನು ತಿಳಿದು, ನಿಮ್ಮ ಜನರಾದ ಇಸ್ರಯೇಲರಂತೆ ನಿಮ್ಮಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿ, ನಾನು ನಿಮ್ಮ ಹೆಸರಿಗಾಗಿ ಈ ಆಲಯವನ್ನು ಕಟ್ಟಿಸಿದೆನೆಂದು ತಿಳಿದುಕೊಳ್ಳುವರು.",
			"44": "<sup>44</sup> “ನೀವು ನಿಮ್ಮ ಜನರನ್ನು ಶತ್ರುಗಳೊಡನೆ ಯುದ್ಧಮಾಡುವುದಕ್ಕೆ ಎಲ್ಲಿಗಾದರೂ ಕಳುಹಿಸಿದಾಗ ಅವರು ಅಲ್ಲಿಂದ ನೀವು ಆರಿಸಿಕೊಂಡ ಪಟ್ಟಣದ ಕಡೆಗೂ, ನಾನು ನಿಮ್ಮ ಹೆಸರಿಗಾಗಿ ಕಟ್ಟಿಸಿದ ಈ ಆಲಯದ ಕಡೆಗೂ ತಿರುಗಿಕೊಂಡು, ಸರ್ವೇಶ್ವರನಾದ ನಿಮ್ಮನ್ನು ಪ್ರಾರ್ಥಿಸಿದರೆ,",
			"45": "<sup>45</sup> ಪರಲೋಕದಲ್ಲಿರುವ ನೀವು ಅವರ ವಿಜ್ಞಾಪನೆಯನ್ನೂ ಪ್ರಾರ್ಥನೆಯನ್ನೂ ಕೇಳಿ ಅವರಿಗೆ ಜಯವನ್ನು ನೀಡಿರಿ.",
			"46": "<sup>46</sup> “ಅವರು ನಿಮಗೆ ವಿರುದ್ಧ ಪಾಪಮಾಡಬಹುದು. ಪಾಪಮಾಡದ ವ್ಯಕ್ತಿ ಒಬ್ಬನೂ ಇಲ್ಲ. ನೀವು ಅವರ ಮೇಲೆ ಕೋಪಗೊಂಡು ಅವರನ್ನು ಶತ್ರುಗಳ ಕೈಗೆ ಒಪ್ಪಿಸಿದಾಗ, ಮತ್ತು ಆ ಶತ್ರುಗಳು ಅವರನ್ನು ಸೆರೆಹಿಡಿದು ದೂರದಲ್ಲಾಗಲಿ ಸಮೀಪದಲ್ಲಾಗಲಿ ಇರುವ ತಮ್ಮ ದೇಶಕ್ಕೆ ಅವರನ್ನು ಒಯ್ದಾಗ,",
			"47": "<sup>47</sup> ಅವರು ಸೆರೆಯವರಾಗಿ ಬಿದ್ದುಕೊಂಡಿರುವ ದೇಶದಲ್ಲಿ ಪಶ್ಚಾತ್ತಾಪಪಟ್ಟು,",
			"48": "<sup>48</sup> ನಿಮ್ಮ ಅನುಗ್ರಹದಿಂದ ತಮ್ಮ ಪಿತೃಗಳಿಗೆ ದೊರಕಿದ ನಾಡಿನ ಕಡೆಗೂ ನೀವು ಆರಿಸಿಕೊಂಡ ಪಟ್ಟಣದ ಕಡೆಗೂ ನಾನು ನಿಮ್ಮ ಹೆಸರಿಗಾಗಿ ಕಟ್ಟಿಸಿದ ಈ ಆಲಯದ ಕಡೆಗೂ ತಿರುಗಿಕೊಂಡು, ‘ನಾವು ನಿಮ್ಮ ಆಜ್ಞೆಗಳನ್ನು ಮೀರಿ ಪಾಪಮಾಡಿ ದ್ರೋಹಿಗಳಾದೆವು’ ಎಂದು ಒಪ್ಪಿಕೊಂಡು ಪೂರ್ಣಮನಸ್ಸಿನಿಂದ ಹಾಗು ಪೂರ್ಣಪ್ರಾಣದಿಂದ, ನಿಮ್ಮನ್ನು ಪ್ರಾರ್ಥಿಸಿದರೆ,",
			"49": "<sup>49</sup> ನೀವು ನಿಮ್ಮ ನಿವಾಸವಾಗಿರುವ ಪರಲೋಕದಿಂದ ಅವರ ವಿಜ್ಞಾಪನೆಯನ್ನೂ ಪ್ರಾರ್ಥನೆಯನ್ನೂ ಆಲಿಸಿರಿ; ಅವರಿಗೆ ದಯೆಯನ್ನು ತೋರಿಸಿರಿ.",
			"50": "<sup>50</sup> ಕಬ್ಬಿಣ ಕರಗಿಸುವ ಕುಲುಮೆಯೋಪಾದಿಯಲ್ಲಿದ್ದ ಈಜಿಪ್ಟ್ ದೇಶದಿಂದ ನೀವು ಬಿಡಿಸಿದ ಈ ಜನರು ನಿಮ್ಮ ಸ್ವಕೀಯಜನರಾಗಿದ್ದಾರೆಂಬುದನ್ನು ನೆನಸಿ,",
			"51": "<sup>51</sup> ಅವರು ನಿಮಗೆ ವಿರುದ್ಧ ಮಾಡಿದ ಎಲ್ಲಾ ಅಪರಾಧದ್ರೋಹಗಳನ್ನು ಕ್ಷಮಿಸಿರಿ; ಅವರನ್ನು ಸೆರೆಗೊಯ್ದವರ ಮನಸ್ಸಿನಲ್ಲಿ ಅವರ ಮೇಲೆ ದಯೆಹುಟ್ಟಿಸಿರಿ.",
			"52": "<sup>52</sup> “ಹೇ ಸರ್ವೇಶ್ವರಾ, ನಿಮ್ಮ ದಾಸನಾದ ಅರಸನ ಮೇಲೆಯೂ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಯೇಲರ ಮೇಲೆಯೂ ಕಟಾಕ್ಷವಿಟ್ಟು, ಅವರು ಪ್ರಾರ್ಥಿಸುವಾಗೆಲ್ಲಾ, ಅವರ ವಿಜ್ಞಾಪನೆಗಳನ್ನು ಆಲಿಸಿರಿ.",
			"53": "<sup>53</sup> ಹೇ ಸರ್ವೇಶ್ವರಾ, ಸರ್ವೇಶ್ವರಾ, ನೀವು ನಮ್ಮ ಪಿತೃಗಳನ್ನು ಈಜಿಪ್ಟ್ ದೇಶದಿಂದ ಬಿಡಿಸುವಾಗ ಅವರಿಗೆ, ‘ನಾನು ನಿಮ್ಮನ್ನು ಎಲ್ಲಾ ಜನಾಂಗಗಳೊಳಗಿಂದ ಪ್ರತ್ಯೇಕಿಸಿ ಸ್ವಕೀಯ ಜನರನ್ನಾಗಿ ಮಾಡಿಕೊಂಡಿದ್ದೇನೆ’ ಎಂದು ಮೋಶೆಯ ಮುಖಾಂತರ ಹೇಳಿದಿರಲ್ಲವೇ!” ಎಂದು ದೇವರನ್ನು ಪ್ರಾರ್ಥಿಸಿದನು.",
			"54": "<sup>54</sup> ಆಕಾಶದ ಕಡೆಗೆ ಕೈಯೆತ್ತಿ ಸರ್ವೇಶ್ವರನ ಬಲಿಪೀಠದ ಮುಂದೆ ಮೊಣಕಾಲೂರಿದ್ದ ಸೊಲೊಮೋನನು ಈ ಪ್ರಕಾರ ಸರ್ವೇಶ್ವರನನ್ನು ಪ್ರಾರ್ಥಿಸಿದ ನಂತರ ಎದ್ದು ನಿಂತನು.",
			"55": "<sup>55</sup> ಇಸ್ರಯೇಲ್ ಸರ್ವಸಮಾಜದವರನ್ನು ಆಶೀರ್ವದಿಸುತ್ತಾ ಗಟ್ಟಿಯಾಗಿ ಹೀಗೆಂದನು:",
			"56": "<sup>56</sup> “ತಾವು ವಾಗ್ದಾನ ಮಾಡಿದಂತೆ ತಮ್ಮ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಯೇಲರಿಗೆ ವಿಶ್ರಾಂತಿಯನ್ನು ಅನುಗ್ರಹಿಸಿದ ಸರ್ವೇಶ್ವರನಿಗೆ ಸ್ತೋತ್ರವಾಗಲಿ! ಅವರು ತಮ್ಮ ದಾಸ ಮೋಶೆಯ ಮುಖಾಂತರ ಮಾಡಿದ ಅತಿಶ್ರೇಷ್ಠ ವಾಗ್ದಾನಗಳಲ್ಲಿ ಒಂದೂ ತಪ್ಪಿಹೋಗಲಿಲ್ಲ.",
			"57": "<sup>57</sup> ನಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಮ್ಮ ಪೂರ್ವಜರೊಂದಿಗೆ ಇದ್ದ ಹಾಗೆ ನಮ್ಮೊಂದಿಗೂ ಇರಲಿ! ಅವರು ನಮ್ಮನ್ನು ಕೈಬಿಡದಿರಲಿ, ತಳ್ಳದಿರಲಿ;",
			"58": "<sup>58</sup> ನಮ್ಮ ಪೂರ್ವಜರಿಗೆ ಕೊಡಲಾದ ಆಜ್ಞಾನಿಯಮ ವಿಧಿಗಳನ್ನು ನಾವು ಕೈಗೊಂಡು, ದೈವಮಾರ್ಗದಲ್ಲಿ ನಡೆಯುವಂತೆ ನಮ್ಮ ಮನಸ್ಸನ್ನು ತಮ್ಮ ಕಡೆಗೆ ತಿರುಗಿಸಿಕೊಳ್ಳಲಿ.",
			"59": "<sup>59</sup> ನಾನು ಈಗ ನಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ಮಾಡಿದ ಪ್ರಾರ್ಥನೆಯು ಹಗಲಿರುಳು ಅವರ ಜ್ಞಾಪಕದಲ್ಲಿರಲಿ.",
			"60": "<sup>60</sup> ಭೂಲೋಕದ ಜನರೆಲ್ಲರೂ ಸರ್ವೇಶ್ವರನ ಹೊರತು ಬೇರೆ ದೇವರಿಲ್ಲವೆಂಬುದನ್ನು ತಿಳಿದುಕೊಳ್ಳುವಂತೆ, ಅವರು ಅವಶ್ಯವಿರುವಾಗೆಲ್ಲಾ ತಮ್ಮ ದಾಸನ ಹಾಗು ಪ್ರಜೆಗಳಾದ ಇಸ್ರಯೇಲರ ನ್ಯಾಯವನ್ನು ಸ್ಥಾಪಿಸುತ್ತಾ ಬರಲಿ.",
			"61": "<sup>61</sup> ನೀವಾದರೋ ಈಗಿನಂತೆ ಮುಂದೆಯೂ ನಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಲ್ಲಿ ಪೂರ್ಣಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿ ಅವರ ವಿಧಿಗಳನ್ನು ಅನುಸರಿಸಿ ಆತನ ಆಜ್ಞೆಗಳನ್ನು ಕೈಗೊಳ್ಳಿರಿ.”",
			"62": "<sup>62</sup> ಅರಸನು ಮತ್ತು ಇಸ್ರಯೇಲರೆಲ್ಲರು ಸರ್ವೇಶ್ವರನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಬಲಿದಾನ ಮಾಡಿದರು.",
			"63": "<sup>63</sup> ಸೊಲೊಮೋನನು ಸರ್ವೇಶ್ವರನಿಗೆ ಅರ್ಪಿಸಿದ ಶಾಂತಿಸಮಾಧಾನ ಬಲಿಗಾಗಿ ವಧಿಸಿದ ಹೋರಿಗಳು ಇಪ್ಪತ್ತೆರಡು ಸಾವಿರ; ಕುರಿಗಳು ಒಂದು ಲಕ್ಷದ ಇಪ್ಪತ್ತುಸಾವಿರ; ಈ ಪ್ರಕಾರ ಅರಸನೂ ಇಸ್ರಯೇಲರೆಲ್ಲರೂ ಸರ್ವೇಶ್ವರನ ಆಲಯವನ್ನು ಪ್ರತಿಷ್ಠಿಸಿದರು.",
			"64": "<sup>64</sup> ಸರ್ವೇಶ್ವರನ ಆಲಯದ ಮುಂದಿರುವ ತಾಮ್ರದ ಬಲಿಪೀಠ ಈ ಎಲ್ಲಾ ದಹನಬಲಿದ್ರವ್ಯಗಳನ್ನೂ ಧಾನ್ಯನೈವೇದ್ಯಗಳನ್ನೂ‌ ಸಮಾಧಾನ ಬಲಿಯ ಕೊಬ್ಬನ್ನೂ ಹಿಡಿಸುವಷ್ಟು ದೊಡ್ಡದಾಗಿರಲಿಲ್ಲ. ಆದುದರಿಂದ ಸೊಲೊಮೋನನು ಆ ದಿವಸ ದೇವಾಲಯದ ಮುಂದಿನ ಪ್ರಾಕಾರದ ಮಧ್ಯಸ್ಥಳವನ್ನು ಪ್ರತಿಷ್ಠಿಸಿ ಅಲ್ಲಿಯೂ ಇವುಗಳನ್ನೆಲ್ಲ ಸಮರ್ಪಿಸಿದನು.",
			"65": "<sup>65</sup> ಹೀಗೆ ಸೊಲೊಮೋನನು ಹಬ್ಬವನ್ನು ಆಚರಿಸಿದನು. ಅಂತೆಯೇ ಹಮಾತಿನ ದಾರಿ ಇಂದ ಈಜಿಪ್ಟಿನ ಹಳ್ಳದವರೆಗಿರುವ ಪ್ರಾಂತ್ಯಗಳಿಂದ ಮಹಾಸಮೂಹವಾಗಿ ಕೂಡಿಬಂದಿದ್ದ ಎಲ್ಲ ಇಸ್ರಯೇಲರು ಆಚರಿಸಿದರು; ಎರಡು ವಾರ, ಅಂದರೆ ಹದಿನಾಲ್ಕು ದಿವಸಗಳವರೆಗೆ, ನಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಹಬ್ಬಮಾಡಿದರು.",
			"66": "<sup>66</sup> ಎಂಟನೆಯ ದಿವಸ ಜನರಿಗೆ ಹೋಗುವುದಕ್ಕೆ ಅಪ್ಪಣೆಯಾಯಿತು. ಅವರು ಅರಸನನ್ನು ವಂದಿಸಿ, ಸರ್ವೇಶ್ವರ ತಮ್ಮ ದಾಸ ದಾವೀದನಿಗೂ ತಮ್ಮ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಯೇಲರಿಗೂ ಮಾಡಿದ ಸರ್ವೋಪಕಾರಗಳನ್ನು ಸ್ಮರಿಸಿ, ಆನಂದಚಿತ್ತರಾಗಿ ಹರ್ಷಿಸುತ್ತಾ, ತಮ್ಮ ತಮ್ಮ ನಿವಾಸಗಳಿಗೆ ತೆರಳಿದರು."
		},
		"9": {
			"1": "<sup>1</sup> ಸೊಲೊಮೋನನು ಸರ್ವೇಶ್ವರನ ಆಲಯವನ್ನು, ತನ್ನ ಅರಮನೆಯನ್ನು ಹಾಗು ತನಗೆ ಇಷ್ಟವಾದ ಬೇರೆ ಎಲ್ಲಾ ಮಂದಿರಗಳನ್ನೂ ಕಟ್ಟಿಮುಗಿಸಿದನು.",
			"2": "<sup>2</sup> ಅನಂತರ ಸರ್ವೇಶ್ವರ, ಗಿಬ್ಯೋನಿನಲ್ಲಿ ಪ್ರತ್ಯಕ್ಷರಾದಂತೆ, ಅವನಿಗೆ ಎರಡನೆಯ ಸಾರಿ ಪ್ರತ್ಯಕ್ಷರಾದರು.",
			"3": "<sup>3</sup> ಅವನಿಗೆ, “ನೀನು ನನಗೆ ಮಾಡಿದ ಪ್ರಾರ್ಥನೆಯನ್ನೂ ವಿಜ್ಞಾಪನೆಯನ್ನೂ ಕೇಳಿದ್ದೇನೆ. ನನ್ನ ನಾಮಮಹತ್ತು ನೀನು ಕಟ್ಟಿಸಿದ ಆಲಯದಲ್ಲಿ ಸದಾ ಇರುವಂತೆ ಅದನ್ನು ನನಗೋಸ್ಕರ ಪ್ರತಿಷ್ಠಿಸಿಕೊಂಡಿದ್ದೇನೆ; ನನ್ನ ದೃಷ್ಟಿಯೂ ಮನಸ್ಸೂ ಪ್ರತಿದಿನ ಅದರ ಮೇಲಿರುವುವು.",
			"4": "<sup>4</sup> ನೀನು ನಿನ್ನ ತಂದೆ ದಾವೀದನಂತೆ ಪೂರ್ಣಮನಸ್ಸಿನಿಂದಲೂ ಯಥಾರ್ಥಚಿತ್ತದಿಂದಲೂ ನಡೆದುಕೊಂಡು, ನನ್ನ ಆಜ್ಞಾವಿಧಿನ್ಯಾಯಗಳನ್ನು ಕೈಗೊಳ್ಳುತ್ತಾ ಬಂದರೆ, ಇಸ್ರಯೇಲರಲ್ಲಿ ನಿನ್ನ ಸಿಂಹಾಸನವನ್ನು ಸದಾ ಸ್ಥಿರಪಡಿಸುವೆನು;",
			"5": "<sup>5</sup> ಇಸ್ರಯೇಲ್ ಸಿಂಹಾಸನದ ಮೇಲೆ ನಿನ್ನ ಸಂತಾನದವರು ತಪ್ಪದೆ ಕುಳಿತುಕೊಳ್ಳುವರು ಎಂಬುದಾಗಿ ನಿನ್ನ ತಂದೆ ದಾವೀದನಿಗೆ ನಾನು ಮಾಡಿದ ವಾಗ್ದಾನವನ್ನು ನೆರವೇರಿಸುವೆನು.",
			"6": "<sup>6</sup> ಆದರೆ ನೀವಾಗಲಿ, ನಿಮ್ಮ ಮಕ್ಕಳಾಗಲಿ, ನನ್ನ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ಕೈಗೊಳ್ಳದೆ, ನನ್ನನ್ನು ಬಿಟ್ಟು ಅನ್ಯದೇವತೆಗಳನ್ನು ಹಿಂಬಾಲಿಸಿ, ಅವುಗಳಿಗೆ ಕೈಮುಗಿದರೆ,",
			"7": "<sup>7</sup> ನಾನು ಕೊಟ್ಟ ನಾಡಿನಿಂದ ಇಸ್ರಯೇಲರನ್ನು ತೆಗೆದುಹಾಕುವೆನು; ನನ್ನ ಹೆಸರಿಗಾಗಿ ಪ್ರತಿಷ್ಠಿಸಿಕೊಂಡ ಆಲಯವನ್ನು ನಿರಾಕರಿಸಿಬಿಡುವೆನು. ಇಸ್ರಯೇಲರು ಎಲ್ಲಾ ಜನಾಂಗಗಳವರ ಲಾವಣಿಗೂ ನಿಂದೆಪರಿಹಾಸ್ಯಕ್ಕೂ ಗುರಿಯಾಗುವರು.",
			"8": "<sup>8</sup> ಈ ಮಹಾಮಂದಿರದ ಮಾರ್ಗವಾಗಿ ಹಾದುಹೋಗುವವರು ಅದನ್ನು ನೋಡಿ ಚಕಿತರಾಗಿ, ‘ಅಬ್ಬಬ್ಬಾ, ಇದೇನು! ಸರ್ವೇಶ್ವರ ನಾಡಿಗೂ ಈ ಗುಡಿಗೂ ಹೀಗೇಕೆ ಮಾಡಿದರು?’ ಎಂದು ಕೇಳುವರು.",
			"9": "<sup>9</sup> ಆಗ ಅವರಿಗೆ, ‘ಈ ನಾಡಿನವರು ತಮ್ಮ ಪೂರ್ವಜರನ್ನು ಈಜಿಪ್ಟಿನಿಂದ ಬರಮಾಡಿದ ದೇವರಾದ ಸರ್ವೇಶ್ವರನನ್ನು ಬಿಟ್ಟು ಅನ್ಯದೇವತೆಗಳನ್ನು ಅವಲಂಬಿಸಿ ಅವುಗಳಿಗೆ ಅಡ್ಡಬಿದ್ದು ಆರಾಧಿಸಿದ್ದರಿಂದಲೇ ಸರ್ವೇಶ್ವರ ಈ ಎಲ್ಲಾ ಆಪತ್ತು-ವಿಪತ್ತುಗಳನ್ನು ಬರಮಾಡಿದ್ದಾರೆ’ ಎಂಬ ಉತ್ತರಸಿಕ್ಕುವುದು,” ಎಂದರು.",
			"10": "<sup>10</sup> ಅರಸ ಸೊಲೊಮೋನನು ಇಪ್ಪತ್ತು ವರ್ಷಗಳಲ್ಲಿ ಸರ್ವೇಶ್ವರನ ಆಲಯವನ್ನು ಹಾಗು ತನ್ನ ಅರಮನೆಯನ್ನು ಕಟ್ಟಿ ಮುಗಿಸಿದನು.",
			"11": "<sup>11</sup> ತನಗೆ ಬೇಕಾದಷ್ಟು ದೇವದಾರು ಮರಗಳನ್ನು, ತುರಾಯಿ ಮರಗಳನ್ನು ಹಾಗು ಬಂಗಾರವನ್ನು ಕೊಟ್ಟಿದ್ದ ಟೈರಿನ ಅರಸ ಹೀರಾಮನಿಗೆ, ಗಲಿಲೇಯ ಪ್ರಾಂತ್ಯದಲ್ಲಿನ ಇಪ್ಪತ್ತು ಪಟ್ಟಣಗಳನ್ನು ಕೊಟ್ಟನು.",
			"12": "<sup>12</sup> ಹೀರಾಮನು ಟೈರಿನಿಂದ ಬಂದು ಆ ಪಟ್ಟಣಗಳನ್ನು ನೋಡಿದನು. ಅವು ಅವನ ಮನಸ್ಸಿಗೆ ಹಿಡಿಸಲಿಲ್ಲ.",
			"13": "<sup>13</sup> ಆದುದರಿಂದ ಅವನು ಸೊಲೊಮೋನನಿಗೆ, “ಸಹೋದರನೇ, ನೀನು ನನಗೆ ಇದೆಂಥ ಪಟ್ಟಣಗಳನ್ನು ಕೊಟ್ಟಿರುವೆ!” ಎಂದು ಹೇಳಿ ಅವುಗಳಿಗೆ ‘ಕಾಬೂಲ್ ದೇಶ’ ಎಂದು ಹೆಸರಿಟ್ಟನು. ಅವುಗಳಿಗೆ ಇಂದಿನವರೆಗೂ ಇದೇ ಹೆಸರಿರುತ್ತದೆ.",
			"14": "<sup>14</sup> ಹೀರಾಮನು ಸೊಲೊಮೋನನಿಗೆ, ನಾಲ್ಕು ಸಾವಿರ ಕಿಲೋಗ್ರಾಂ ಬಂಗಾರವನ್ನು ಕೊಟ್ಟನು.",
			"15": "<sup>15</sup> ಸರ್ವೇಶ್ವರನ ಆಲಯ, ಅರಮನೆ, ಮಿಲ್ಲೋಕೋಟೆ, ಜೆರುಸಲೇಮಿನ ಪೌಳಿಗೋಡೆ, ಹಾಚೋರ್, ಮೆಗಿದ್ದೋ ಹಾಗು ಗೆಜೆರ್ ಇವುಗಳನ್ನು ಸೊಲೊಮೋನನು ಬಿಟ್ಟೀಕೆಲಸದವರಿಂದ ಕಟ್ಟಿಸಿದನು.",
			"16": "<sup>16</sup> ಈಜಿಪ್ಟಿನ ಅರಸ ಫರೋಹನು, ಗೆಜೆರ್ ಪಟ್ಟಣವನ್ನು ಹಿಡಿದು ಅದನ್ನು ವಶಮಾಡಿಕೊಂಡು, ಸೊಲೊಮೋನನ ಹೆಂಡತಿಯಾದ ತನ್ನ ಮಗಳಿಗೆ ಉಡುಗೊರೆಯಾಗಿ ಕೊಟ್ಟಿದ್ದನು.",
			"17": "<sup>17</sup> ಗೆಜೆರ್ ಪಟ್ಟಣವನ್ನು ಬಿಟ್ಟು ಕೆಳಗಿನ ಬೇತ್-ಹೋರೋನ್,",
			"18": "<sup>18</sup> ಬಾಲಾತ್, ಮರುಭೂಮಿಯಲ್ಲಿ ಇರುವ ತಾಮರ್,",
			"19": "<sup>19</sup> ಉಗ್ರಾಣ ಪಟ್ಟಣಗಳು, ಯುದ್ಧರಥಗಳನ್ನಿರಿಸುವ ಪಟ್ಟಣಗಳು ಹಾಗು ರಾಹುತರ ಪಟ್ಟಣಗಳು ಇವುಗಳನ್ನೆಲ್ಲಾ ಕಟ್ಟಿಸಿದವನು ಸೊಲೊಮೋನನೇ. ಜೆರುಸಲೇಮಿನಲ್ಲೂ ಲೆಬನೋನಿನಲ್ಲೂ ತನ್ನ ರಾಜ್ಯದ ಎಲ್ಲಾ ಪ್ರಾಂತ್ಯಗಳಲ್ಲೂ ತನಗೆ ಇಷ್ಟವಾದುವುಗಳನ್ನೆಲ್ಲಾ ಅವನು ಕಟ್ಟಿಸಿದನು.",
			"20": "<sup>20</sup> ಇಸ್ರಯೇಲರು ಸಂಹರಿಸದೆ ಉಳಿಸಿದ ಅನ್ಯಜನರನ್ನೂ ಕಾನಾನ್ ದೇಶದಲ್ಲಿ ಉಳಿದಿದ್ದ ಅಮೋರಿಯ, ಹಿತ್ತಿಯ, ಪೆರಿಜ್ಜೀಯ, ಹಿವ್ವೀಯ,",
			"21": "<sup>21</sup> ಯೆಬೂಸಿಯ ಮೊದಲಾದ ಇತರ ಜನಾಂಗಗಳ ಸಂತಾನದವರೆಲ್ಲರನ್ನು ಬಿಟ್ಟೀ ಕೆಲಸಕ್ಕಾಗಿ ಇಟ್ಟಿದ್ದನು. ಇಂದಿನವರೆಗೂ ಅವರು ಬಿಟ್ಟೀ ಕೆಲಸದವರಾಗಿಯೇ ಇರುತ್ತಾರೆ.",
			"22": "<sup>22</sup> ಆದರೆ ಅವನು ಇಸ್ರಯೇಲರನ್ನು ಬಿಟ್ಟಿಹಿಡಿಯಲಿಲ್ಲ; ಅವರನ್ನು ಸೈನಿಕರನ್ನಾಗಿ, ಪರಿವಾರದವರನ್ನಾಗಿ, ಅಧಿಪತಿಗಳನ್ನಾಗಿ, ಸರದಾರರನ್ನಾಗಿ, ಹಾಗೂ ರಥಾಶ್ವಬಲಗಳ ನಾಯಕರನ್ನಾಗಿ ನೇಮಿಸಿಕೊಂಡನು.",
			"23": "<sup>23</sup> ಇದಲ್ಲದೆ, ಅವನು ಬಿಟ್ಟೀಕೆಲಸ ಮಾಡುವವರ ಮೇಲೆ ಐನೂರೈವತ್ತು ಮಂದಿ ಇಸ್ರಯೇಲರನ್ನು ಅಧಿಕಾರಿಗಳನ್ನಾಗಿ ನೇಮಿಸಿದನು.",
			"24": "<sup>24</sup> ಫರೋಹನ ಮಗಳು (ದಾವೀದನಗರದಿಂದ) ತನಗೋಸ್ಕರ ಕಟ್ಟಿಸಿದ್ದ ಮಂದಿರಕ್ಕೆ ಬಂದಳು; ಅನಂತರ ಸೊಲೊಮೋನನು ಮಿಲ್ಲೋಕೋಟೆಯನ್ನು ಕಟ್ಟಿಸಿದನು.",
			"25": "<sup>25</sup> ಸೊಲೊಮೋನನು ಸರ್ವೇಶ್ವರನ ಆಲಯದ ಮುಂದೆ ಸರ್ವೇಶ್ವರನಿಗಾಗಿ ತಾನು ಕಟ್ಟಿಸಿದ ಬಲಿಪೀಠದ ಮೇಲೆ ವರ್ಷಕ್ಕೆ ಮೂರು ಸಾರಿ ದಹನಬಲಿ ಹಾಗು ಶಾಂತಿಸಮಾಧಾನಬಲಿಗಳನ್ನು ಸಮರ್ಪಿಸಿ ಧೂಪಾರತಿ ಎತ್ತುತ್ತಿದ್ದನು. ಹೀಗೆ ಅವನು ಮಹಾದೇವಾಲಯವನ್ನು ಕಟ್ಟಿಸಿಮುಗಿಸಿದನು.",
			"26": "<sup>26</sup> ಎದೋಮ್ ದೇಶದಲ್ಲಿ ಎಲೋತಿನ ಹತ್ತಿರ ಕೆಂಪುಸಮುದ್ರದ ತೀರದಲ್ಲಿರುವ ಎಚ್ಯೋನ್ಗೆಬೆರಿನಲ್ಲಿ ಅರಸ ಸೊಲೊಮೋನನು ಹಡಗುಗಳನ್ನು ಕಟ್ಟಿಸಿದನು.",
			"27": "<sup>27</sup> ಹೀರಾಮನು ಸಮುದ್ರಪ್ರಯಾಣದಲ್ಲಿ ನಿಪುಣರಾದ ತನ್ನ ನಾವಿಕರನ್ನು ಸೊಲೊಮೋನನ ಸೇವಕರೊಡನೆ ಆ ಹಡಗುಗಳಲ್ಲಿ ಕಳುಹಿಸಿದನು.",
			"28": "<sup>28</sup> ಅವರು ಓಫೀರಿಗೆ ಪ್ರಯಾಣಮಾಡಿ ಅಲ್ಲಿಂದ ಅರಸನಾದ ಸೊಲೊಮೋನನಿಗೆ ಹದಿನಾಲ್ಕು ಸಾವಿರ ಕಿಲೋಗ್ರಾಂಗೂ ಹೆಚ್ಚಿನ ಬಂಗಾರವನ್ನು ತೆಗೆದುಕೊಂಡು ಬಂದರು."
		},
		"10": {
			"1": "<sup>1</sup> ಸರ್ವೇಶ್ವರನ ನಾಮಮಹತ್ತಿನಿಂದ ಸೊಲೊಮೋನನ ಕೀರ್ತಿ ಹರಡಿತು. ಇದನ್ನು ಕೇಳಿದ ಶೆಬದ ರಾಣಿ ಅವನನ್ನು ಒಗಟುಗಳಿಂದ ಪರೀಕ್ಷಿಸುವುದಕ್ಕೆ ಬಂದಳು.",
			"2": "<sup>2</sup> ಆಕೆ ಸುಗಂಧದ್ರವ್ಯ, ಅಪರಿಮಿತವಾದ ಬಂಗಾರ, ರತ್ನ, ಇವುಗಳನ್ನು ಒಂಟೆಗಳ ಮೇಲೆ ಹೇರಿಸಿಕೊಂಡು ಮಹಾಪರಿವಾರದೊಡನೆ ಜೆರುಸಲೇಮಿಗೆ ಬಂದಳು. ತನ್ನ ಮನಸ್ಸಿನಲ್ಲಿ ಗೊತ್ತುಮಾಡಿಕೊಂಡಿದ್ದ ಎಲ್ಲಾ ವಿಷಯಗಳನ್ನು ಕುರಿತು ಸೊಲೊಮೋನನೊಡನೆ ಸಂಭಾಷಿಸಿದಳು.",
			"3": "<sup>3</sup> ಸೊಲೊಮೋನನು ಆಕೆಯ ಎಲ್ಲಾ ಪ್ರಶ್ನೆಗಳಿಗೆ ಉತ್ತರಕೊಟ್ಟನು; ಅರಸನಿಗೆ ಬಿಡಿಸಲು ಕ್ಲಿಷ್ಟವಾದ ಒಗಟು ಒಂದೂ ಇರಲಿಲ್ಲ.",
			"4": "<sup>4</sup> ಶೆಬದ ರಾಣಿ ಸೊಲೊಮೋನನ ಹಲವಿಧವಾದ ಜ್ಞಾನೋಕ್ತಿಗಳನ್ನು ಕೇಳಿದಳು;",
			"5": "<sup>5</sup> ಅವನು ಕಟ್ಟಿಸಿದ ಅರಮನೆ, ಅವನ ಭೋಜನಪೀಠದ ಆಹಾರ, ಆ ಪೀಠದ ಸುತ್ತಲಿರುವ ಉದ್ಯೋಗಸ್ಥರ ಆಸನಗಳು, ಅವನ ಪರಿಚಾರಕರ ಸೇವಾಕ್ರಮ, ಅವರ ಉಡುಪುಗಳು, ಅವನ ಪಾನಕಗಳು ಇವುಗಳನ್ನು ಮತ್ತು ಸರ್ವೇಶ್ವರನ ಆಲಯದಲ್ಲಿ ಅವನು ಸಮರ್ಪಿಸುತ್ತಿದ್ದ ದಹನಬಲಿಗಳನ್ನು ನೋಡಿದಳು. ಇವುಗಳಿಂದ ಆಶ್ಚರ್ಯಚಕಿತಳಾಗಿ,",
			"6": "<sup>6</sup> “ನಾನು ನನ್ನ ದೇಶದಲ್ಲಿ ನಿಮ್ಮ ಜ್ಞಾನವನ್ನೂ ಸಾಧನೆಗಳನ್ನೂ ಕುರಿತು ಕೇಳಿದ್ದು ಸತ್ಯವಾಗಿದೆ.",
			"7": "<sup>7</sup> ನಾನಾಗಿ ಇಲ್ಲಿಗೆ ಬಂದು ಕಣ್ಣಾರೆ ನೋಡುವುದಕ್ಕೆ ಮೊದಲು ಜನರು ಹೇಳಿದ ಸುದ್ದಿಯನ್ನು ನಂಬಲಿಲ್ಲ; ಈಗ ನೋಡಿದರೆ ನಿಮ್ಮ ಜ್ಞಾನವೈಭವಗಳು, ನಾನು ಕೇಳಿದ್ದಕ್ಕಿಂತ ಮಿಗಿಲಾಗಿವೆ. ಜನರು ನನಗೆ ಇದರಲ್ಲಿ ಅರ್ಧವನ್ನಾದರೂ ವರದಿ ಮಾಡಿರಲಿಲ್ಲ.",
			"8": "<sup>8</sup> ನಿಮ್ಮ ಪ್ರಜೆಗಳು ಭಾಗ್ಯವಂತರು! ಸದಾ ನಿಮ್ಮ ಸನ್ನಿಧಿಯಲ್ಲಿದ್ದು ನಿಮ್ಮ ಜ್ಞಾನವಾಕ್ಯಗಳನ್ನು ಕೇಳುವ ನಿಮ್ಮ ಸೇವಕರು ಧನ್ಯರು!",
			"9": "<sup>9</sup> ನಿಮ್ಮನ್ನು ಮೆಚ್ಚಿ ಇಸ್ರಯೇಲ್ ಸಿಂಹಾಸನದ ಮೇಲೆ ಕುಳ್ಳಿರಿಸಿದ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ಸ್ತೋತ್ರವಾಗಲಿ! ಅವರು ಇಸ್ರಯೇಲರನ್ನು ಸದಾ ಪ್ರೀತಿಸುವವರಾಗಿರುವುದರಿಂದ ಆ ಜನರ ನೀತಿನ್ಯಾಯಗಳನ್ನು ಸ್ಥಾಪಿಸುವುದಕ್ಕಾಗಿ ನಿಮ್ಮನ್ನು ಅರಸನನ್ನಾಗಿ ನೇಮಿಸಿದ್ದಾರೆ,” ಎಂದು ಹೇಳಿದಳು.",
			"10": "<sup>10</sup> ಆಕೆ ಅರಸನಿಗೆ ನಾಲ್ಕು ಸಾವಿರ ಕಿಲೋಗ್ರಾಂಗೂ ಹೆಚ್ಚು ಬಂಗಾರವನ್ನು, ಅಪರಿಮಿತ ಸುಗಂಧದ್ರವ್ಯವನ್ನು ಹಾಗು ರತ್ನಗಳನ್ನು ಕೊಟ್ಟಳು. ಶೆಬದ ರಾಣಿ ಅರಸ ಸೊಲೊಮೋನನಿಗೆ ಕೊಟ್ಟಷ್ಟು ಸುಗಂಧದ್ರವ್ಯವನ್ನು ಬೇರೆ ಯಾರೂ ಈ ನಾಡಿಗೆ ಕೊಟ್ಟಿರಲಿಲ್ಲ.",
			"11": "<sup>11</sup> ಹೀರಾಮನ ಹಡಗುಗಳು ಓಫೀರ್ ದೇಶದಿಂದ ಬಂಗಾರ ಮಾತ್ರವಲ್ಲ ಸುಗಂಧದ ಮರವನ್ನೂ ರತ್ನಗಳನ್ನೂ ರಾಶಿರಾಶಿಯಾಗಿ ತಂದವು.",
			"12": "<sup>12</sup> ಅರಸನು ಸುಗಂಧದ ಮರದಿಂದ ದೇವಾಲಯಕ್ಕೆ ಹಾಗು ಅರಮನೆಗಳಿಗೆ ಮೆಟ್ಟಲುಗಳನ್ನು ವಾದ್ಯಗಾರರಿಗಾಗಿ ಕಿನ್ನರಿಗಳನ್ನು ಹಾಗು ಸ್ವರಮಂಡಲಗಳನ್ನು ಮಾಡಿಸಿದನು. ಆಗ ಬಂದಷ್ಟು ಸುಗಂಧದಮರ ಇಂದಿನವರೆಗೂ ಬರಲಿಲ್ಲ, ಮತ್ತು ಯಾರೂ ಅಷ್ಟನ್ನು ಕಾಣಲೇ ಇಲ್ಲ.",
			"13": "<sup>13</sup> ಅರಸ ಸೊಲೊಮೋನನು ಶೆಬದ ರಾಣಿಗೆ ತಾನಾಗಿ ರಾಜಮರ್ಯಾದೆಯಿಂದ ಕೊಟ್ಟ ವಸ್ತುಗಳಲ್ಲದೆ, ಆಕೆ ಕೇಳಿದವುಗಳನ್ನೆಲ್ಲಾ ಕೊಟ್ಟುಬಿಟ್ಟನು. ಅನಂತರ ಆಕೆ ತನ್ನ ಪರಿವಾರದೊಡನೆ ಸ್ವದೇಶಕ್ಕೆ ಹೊರಟುಹೋದಳು.",
			"14": "<sup>14</sup> ಸೊಲೊಮೋನನಿಗೆ ದೇಶಾಂತರ ವ್ಯಾಪಾರಿಗಳು, ವರ್ತಕರು, ಅರೇಬಿಯದ ಅರಸರು ಹಾಗು ಪ್ರದೇಶಾಧಿಪತಿಗಳು ತರುತ್ತಿದ್ದ ಬಂಗಾರವಲ್ಲದೆ",
			"15": "<sup>15</sup> ಪ್ರತಿವರ್ಷ ಸುಮಾರು ಇಪ್ಪತ್ತಮೂರು ಸಾವಿರ ಕಿಲೋಗ್ರಾಂ ಬಂಗಾರ ದೊರಕುತ್ತಿತ್ತು.",
			"16": "<sup>16</sup> ಅರಸ ಸೊಲೊಮೋನನು ಬಂಗಾರದ ತಗಡಿನಿಂದ ಇನ್ನೂರು ಗುರಾಣಿಗಳನ್ನು ಮಾಡಿಸಿದನು; ಪ್ರತಿಯೊಂದು ಗುರಾಣಿಗೆ ಸುಮಾರು ಏಳು ಕಿಲೋಗ್ರಾಂ ಬಂಗಾರ ಹಿಡಿಯಿತು.",
			"17": "<sup>17</sup> ಅವನು ಬಂಗಾರದ ತಗಡಿನಿಂದ ಮುನ್ನೂರು ಖೇಡ್ಯಗಳನ್ನೂ ಮಾಡಿಸಿದನು. ಪ್ರತಿಯೊಂದು ಖೇಡ್ಯಕ್ಕೆ ಎರಡು ಕಿಲೋಗ್ರಾಂ ಬಂಗಾರ ಹಿಡಿಯಿತು. ಇವುಗಳನ್ನು ‘ಲೆಬನೋನಿನ ತೋಪು’ ಎನಿಸಿಕೊಳ್ಳುವ ಮಂದಿರದಲ್ಲಿ ಇರಿಸಿದನು.",
			"18": "<sup>18</sup> ಇದಲ್ಲದೆ, ಅರಸನು ದೊಡ್ಡದಾದ ಒಂದು ದಂತಸಿಂಹಾಸನವನ್ನು ಮಾಡಿಸಿ ಅದನ್ನು ಚೊಕ್ಕಬಂಗಾರದ ತಗಡಿನಿಂದ ಹೊದಿಸಿದನು;",
			"19": "<sup>19</sup> ಅದಕ್ಕೆ ಆರು ಮೆಟ್ಟಲುಗಳಿದ್ದವು. ಅದರ ಹಿಂಭಾಗದ ತುದಿ ಚಕ್ರಾಕಾರವಾಗಿತ್ತು. ಆಸನಕ್ಕೆ ಎರಡು ಕೈಗಳೂ, ಅವುಗಳ ಹತ್ತಿರ ಎರಡು ಸಿಂಹಗಳೂ ಇದ್ದವು.",
			"20": "<sup>20</sup> ಆರು ಮೆಟ್ಟಲುಗಳ ಎರಡು ಕಡೆಗಳಲ್ಲೂ ಒಟ್ಟು ಹನ್ನೆರಡು ಸಿಂಹಗಳು ನಿಂತಿದ್ದವು; ಇಂಥ ಸಿಂಹಾಸನವು ಬೇರೆ ಯಾವ ರಾಜ್ಯದಲ್ಲಿಯೂ ಇರಲಿಲ್ಲ.",
			"21": "<sup>21</sup> ಅರಸ ಸೊಲೊಮೋನನ ಎಲ್ಲಾ ಪಾನಪಾತ್ರೆಗಳು ಬಂಗಾರದವುಗಳು; ‘ಲೆಬನೋನಿನ ತೋಪು’ ಎನಿಸಿಕೊಳ್ಳುವ ಮಂದಿರದ ಸಾಮಾನುಗಳು ಚೊಕ್ಕ ಬಂಗಾರದವುಗಳು. ಸೊಲೊಮೋನನ ಕಾಲದಲ್ಲಿ ಬೆಳ್ಳಿಗೆ ಬೆಲೆಯಿರಲಿಲ್ಲ. ಆದುದರಿಂದ ಅವನಲ್ಲಿ ಬೆಳ್ಳಿಯ ಸಾಮಾನು ಒಂದಾದರೂ ಕಾಣಿಸಲಿಲ್ಲ.",
			"22": "<sup>22</sup> ಹೀರಾಮನ ನಾವೆಗಳ ಜೊತೆಯಲ್ಲಿ ಸೊಲೊಮೋನನ ತಾರ್ಷಿಷ್ ಹಡಗುಗಳು ಹೋಗಿ ಮೂರು ವರ್ಷಕ್ಕೊಮ್ಮೆ ಬಂಗಾರ, ಬೆಳ್ಳಿ, ದಂತ, ಕಪಿ, ನವಿಲು ಇವುಗಳನ್ನು ತರುತ್ತಿದ್ದವು.",
			"23": "<sup>23</sup> ಈ ಪ್ರಕಾರ ಅರಸನಾದ ಸೊಲೊಮೋನನು ಐಶ್ವರ್ಯದಲ್ಲಿಯೂ ಜ್ಞಾನದಲ್ಲಿಯೂ ಭೂಲೋಕದ ಎಲ್ಲಾ ಅರಸರಿಗಿಂತ ಮಿಗಿಲಾಗಿ ಇದ್ದನು.",
			"24": "<sup>24</sup> ಭೂಲೋಕದವರೆಲ್ಲರೂ, ದೇವರು ಅವನಿಗೆ ಅನುಗ್ರಹಿಸಿದ್ದ ಜ್ಞಾನವಾಕ್ಯಗಳನ್ನು ಕೇಳುವುದಕ್ಕಾಗಿ ಅವನ ದರ್ಶನಕ್ಕೆ ಬರುತ್ತಿದ್ದರು.",
			"25": "<sup>25</sup> ಅವರೆಲ್ಲರೂ ಅವನಿಗೆ ವರ್ಷ ವರ್ಷ ಬೆಳ್ಳಿ ಬಂಗಾರದ ಸಾಮಾನು, ಉಡುಪು, ಯುದ್ಧಾಯುಧ, ಸುಗಂಧದ್ರವ್ಯ, ಕುದುರೆ, ಹೇಸರಗತ್ತೆ ಇವುಗಳನ್ನು ಕಾಣಿಕೆಯಾಗಿ ಕೊಡುತ್ತಿದ್ದರು.",
			"26": "<sup>26</sup> ಸೊಲೊಮೋನನು ರಥಗಳನ್ನೂ ರಾಹುತರನ್ನೂ ಸಂಗ್ರಹಿಸುತ್ತಿದ್ದನು. ಅವನ ರಥಗಳು ಸಾವಿರದ ನಾನೂರು; ರಾಹುತರು ಹನ್ನೆರಡು ಸಾವಿರ. ಇವುಗಳಲ್ಲಿ ಕೆಲವನ್ನು ಜೆರುಸಲೇಮಿನಲ್ಲಿ ತನ್ನ ಬಳಿಯಲ್ಲೇ ಇರಿಸಿದ್ದನು; ಉಳಿದವುಗಳನ್ನು ರಥಗಳಿಗಾಗಿಯೇ ನೇಮಿಸಿದ್ದ ಪಟ್ಟಣಗಳಲ್ಲಿ ಇರಿಸಿದ್ದನು.",
			"27": "<sup>27</sup> ಅವನ ಕಾಲದಲ್ಲಿ ಜೆರುಸಲೇಮಿನಲ್ಲಿದ್ದ ಬೆಳ್ಳಿಯು ಕಲ್ಲಿನಂತೆ ಹಾಗು ದೇವದಾರು ಮರಗಳು ಇಳಕಲ್ಲಿನ ಪ್ರದೇಶದಲ್ಲಿ ಬೆಳೆಯುವ ಅತ್ತಿಮರಗಳಂತೆ ವಿಪುಲವಾಗಿದ್ದವು.",
			"28": "<sup>28</sup> ಅರಸ ಸೊಲೊಮೋನನ ಕುದುರೆಗಳು ಈಜಿಪ್ಟ್ ದೇಶದವುಗಳು; ಅವನ ವರ್ತಕರು ಅವುಗಳನ್ನು ಹಿಂಡುಗಟ್ಟಳೆಯಾಗಿ ಕೊಂಡುಕೊಂಡು ಬರುತ್ತಿದ್ದರು.",
			"29": "<sup>29</sup> ಈಜಿಪ್ಟ್ ದೇಶದಿಂದ ರಥಗಳನ್ನೂ ಕುದುರೆಗಳನ್ನೂ ತರಿಸಬೇಕಾದರೆ ಪ್ರತಿಯೊಂದು ರಥಕ್ಕೆ ಆರುನೂರು ಬೆಳ್ಳಿನಾಣ್ಯಗಳನ್ನೂ ಕುದುರೆಗೆ ನೂರೈವತ್ತು ಬೆಳ್ಳಿನಾಣ್ಯಗಳನ್ನೂ ಕೊಡಬೇಕಾಗಿತ್ತು. ಅವು ಹಿತ್ತಿಯರ ಮತ್ತು ಸಿರಿಯಾದವರ ಎಲ್ಲಾ ಅರಸರಿಗೆ ಇವರ ಮುಖಾಂತರವೇ ಮಾರಾಟವಾಗುತ್ತಿದ್ದವು."
		},
		"11": {
			"1": "<sup>1</sup> ಅರಸ ಸೊಲೊಮೋನನು ಫರೋಹನ ಮಗಳನ್ನಲ್ಲದೆ, ಮೋವಾಬ್ಯರು, ಅಮ್ಮೋನಿಯರು, ಎದೋಮ್ಯರು, ಚೀದೋನ್ಯರು, ಹಿತ್ತಿಯರು ಎಂಬೀ ಜನಾಂಗಗಳ ಮಹಿಳೆಯರನ್ನೂ ಮೋಹಿಸಿದನು.",
			"2": "<sup>2</sup> ಸರ್ವೇಶ್ವರ ಈ ಜನಾಂಗಗಳ ವಿಷಯದಲ್ಲಿ ಇಸ್ರಯೇಲರಿಗೆ, “ನಿಮಗೂ ಅವರಿಗೂ ಕೊಟ್ಟುಕೊಳ್ಳುವ ಸಂಬಂಧ ಇರಕೂಡದು; ಹೀಗೆ ವಿವಾಹ ಸಂಬಂಧವಾಗುವುದಾದರೆ ಅವರು ನಿಮ್ಮ ಮನಸ್ಸನ್ನು ತಮ್ಮ ದೇವತೆಗಳ ಕಡೆಗೆ ತಿರುಗಿಸಬಹುದು,” ಎಂದು ಎಚ್ಚರಿಸಿದ್ದರು. ಆದರೂ ಸೊಲೊಮೋನನು ಅವರ ಮಹಿಳೆಯರಲ್ಲಿ ಅನುರಕ್ತನಾಗಿದ್ದನು.",
			"3": "<sup>3</sup> ಅವನಿಗೆ ರಾಜವಂಶದವರಾದ ಏಳುನೂರು ಮಂದಿ ಪತ್ನಿಯರಲ್ಲದೆ ಮುನ್ನೂರು ಮಂದಿ ಉಪಪತ್ನಿಯರಿದ್ದರು. ಈ ಮಹಿಳೆಯರು ಅವನ ಮನಸ್ಸನ್ನು ಕೆಡಿಸಿಬಿಟ್ಟರು.",
			"4": "<sup>4</sup> ಅವನು ವೃದ್ಧನಾದಾಗ ಇವರು ಅವನ ಹೃದಯವನ್ನು ಅನ್ಯದೇವತೆಗಳ ಕಡೆಗೆ ತಿರುಗಿಸಿದರು. ಈ ಕಾರಣ ಅವನು ತನ್ನ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಲ್ಲಿಟ್ಟಿದ್ದ ಯಥಾರ್ಥ ಭಕ್ತಿಯನ್ನು ಕಳೆದುಕೊಂಡನು. ತನ್ನ ತಂದೆಯಾದ ದಾವೀದನಂತೆ ನಡೆಯಲಿಲ್ಲ.",
			"5": "<sup>5</sup> ಸಿದೋನ್ಯರ ಅಷ್ಟೋರೆತ್ ದೇವತೆಯನ್ನೂ ಅಮ್ಮೋನಿಯರ ಮಿಲ್ಕೋಮ್ ಎಂಬ ಅಸಹ್ಯ ವಿಗ್ರಹವನ್ನೂ ಆರಾಧಿಸತೊಡಗಿದನು.",
			"6": "<sup>6</sup> ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾದನು; ಅವನ ತಂದೆ ದಾವೀದನು ಪೂರ್ಣಮನಸ್ಸಿನಿಂದ ಸರ್ವೇಶ್ವರನನ್ನು ಸೇವೆಮಾಡಿದಂತೆ ಅವನು ಸೇವೆಮಾಡಲಿಲ್ಲ.",
			"7": "<sup>7</sup> ಅವನು ಜೆರುಸಲೇಮಿನ ಮೂಡಲಲ್ಲಿರುವ ಗುಡ್ಡದಲ್ಲಿ ಮೋವಾಬ್ಯರ ಕೆಮೋಷ್ ಎಂಬ ಅಸಹ್ಯ ವಿಗ್ರಹಕ್ಕೂ ಪೂಜಾಸ್ಥಳಗಳನ್ನು ಏರ್ಪಡಿಸಿದನು.",
			"8": "<sup>8</sup> ಈ ಪ್ರಕಾರ ಅನ್ಯಜನಾಂಗಗಳಿಂದ ಬಂದ ತನ್ನ ಹೆಂಡತಿಯರು ತಮ್ಮ ದೇವತೆಗಳಿಗೆ ಧೂಪಾರತಿ ಎತ್ತುವುದಕ್ಕೂ ಬಲಿ ಅರ್ಪಿಸುವುದಕ್ಕೂ ಅನುಕೂಲ ಮಾಡಿಕೊಟ್ಟನು.",
			"9": "<sup>9</sup> ಸೊಲೊಮೋನನಿಗೆ ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಎರಡು ಸಾರಿ ಕಾಣಿಸಿಕೊಂಡು,",
			"10": "<sup>10</sup> ಅನ್ಯದೇವತೆಗಳನ್ನು ಪೂಜಿಸಲೇಬಾರದೆಂದು ಆಜ್ಞಾಪಿಸಿದ್ದರೂ ಅವನು ಅವರ ಆಜ್ಞೆಗಳನ್ನು ಮೀರಿದನು.",
			"11": "<sup>11</sup> ಆದ್ದರಿಂದ ಸರ್ವೇಶ್ವರ ಅವನ ಮೇಲೆ ಕೋಪಗೊಂಡು, “ನೀನು ಹೀಗೆ ಮಾಡಿ ನನ್ನ ನಿಬಂಧನೆಗಳನ್ನು ಮೀರಿದ್ದರಿಂದ ನಿನ್ನ ರಾಜ್ಯವನ್ನು ಕಿತ್ತುಕೊಂಡು ನಿನ್ನ ದಾಸನಿಗೆ ಕೊಡುವೆನು.",
			"12": "<sup>12</sup> ಆದರೂ ನಾನು ನಿನ್ನ ತಂದೆ ದಾವೀದನನ್ನು ಸ್ಮರಿಸಿ, ನಿನ್ನ ಮಗನ ಕೈಯಿಂದ ಅದನ್ನು ಕಿತ್ತುಕೊಳ್ಳುವೆನು.",
			"13": "<sup>13</sup> ಆದರೆ ಇಡೀ ಸಾಮ್ರಾಜ್ಯವನ್ನು ಕಿತ್ತುಕೊಳ್ಳುವುದಿಲ್ಲ; ನನ್ನ ದಾಸ ದಾವೀದನ ನಿಮಿತ್ತ ಹಾಗು ನಾನು ಆರಿಸಿಕೊಂಡ ಜೆರುಸಲೇಮ್ ಪಟ್ಟಣದ ನಿಮಿತ್ತ ನಿನ್ನ ಮಗನಿಗೆ ಒಂದು ಕುಲವನ್ನು ಉಳಿಸುವೆನು,” ಎಂದು ಸರ್ವೇಶ್ವರ ಹೇಳಿದರು.",
			"14": "<sup>14</sup> ಎದೋಮ್ಯನೂ ರಾಜಪುತ್ರನೂ ಆದ ಹದದನನ್ನು ಸೊಲೊಮೋನನಿಗೆ ವಿರುದ್ಧ ದಂಗೆಯೇಳುವಂತೆ ಮಾಡಿದರು ಸರ್ವೇಶ್ವರ.",
			"15": "<sup>15</sup> ದಾವೀದನಿಗೂ ಎದೋಮ್ಯರಿಗೂ ಯುದ್ಧ ನಡೆಯುತ್ತಿರುವಾಗ ಸೇನಾಪತಿಯಾದ ಯೋವಾಬನು ಇಸ್ರಯೇಲ್ ಸರ್ವಸೈನ್ಯದೊಡನೆ ಅಲ್ಲಿಗೆ ಹೋಗಿ ಹತರಾದ ಇಸ್ರಯೇಲರನ್ನು ಸಮಾಧಿಮಾಡಿದನು.",
			"16": "<sup>16</sup> ಆರು ತಿಂಗಳು ಅಲ್ಲಿಯೇ ಇದ್ದು ಎದೋಮ್ಯರ ಎಲ್ಲ ಗಂಡಸರನ್ನು ಸಂಹರಿಸಿದನು.",
			"17": "<sup>17</sup> ಆ ಸಮಯದಲ್ಲಿ ಬಾಲಕನಾದ ಹದದನು ತನ್ನ ತಂದೆಯ ಸೇವಕರಾದ ಕೆಲವು ಮಂದಿ ಎದೋಮ್ಯರ ಜೊತೆಯಲ್ಲಿ ಈಜಿಪ್ಟಿಗೆ ಓಡಿಹೋಗಬೇಕೆಂದು ಹೊರಟನು.",
			"18": "<sup>18</sup> ಅವರು ಮೊದಲು ಮಿದ್ಯಾನಿಗೂ ಆಮೇಲೆ ಪಾರಾನಿಗೂ ಬಂದರು. ಅಲ್ಲಿಂದ ಕೆಲವುಮಂದಿ ಪಾರಾನಿನ ಜನರನ್ನು ತಮ್ಮ ಜೊತೆಯಲ್ಲಿ ಕರೆದುಕೊಂಡು ಈಜಿಪ್ಟಿನ ಅರಸ ಫರೋಹನ ಬಳಿಗೆ ಬಂದರು. ಫರೋಹನು ಹದದನಿಗೆ ಆಹಾರವನ್ನು, ಮನೆಯನ್ನು ಹಾಗು ಭೂಮಿಯನ್ನು ಕೊಟ್ಟನು.",
			"19": "<sup>19</sup> ಹದದನು ಫರೋಹನ ವಿಶೇಷ ದಯೆಗೆ ಪಾತ್ರನಾದನು. ಫರೋಹನು ತನ್ನ ಹೆಂಡತಿಯೂ ರಾಣಿಯೂ ಆದ ತಖ್ಪೆನೇಸ್ ಎಂಬಾಕೆಯ ತಂಗಿಯನ್ನು ಇವನಿಗೆ ಮದುವೆ ಮಾಡಿಕೊಟ್ಟನು.",
			"20": "<sup>20</sup> ಆಕೆಯಲ್ಲಿ ಇವನಿಗೆ ಗೆನುಬತ್ ಎಂಬ ಮಗನು ಹುಟ್ಟಿದನು. ತಖ್ಪೆನೇಸಳು ಆ ಹುಡುಗನನ್ನು ಫರೋಹನ ಮನೆಯಲ್ಲೇ ಸಾಕಿದಳು; ಅಲ್ಲಿ ಇವನು ಫರೋಹನ ಕುಮಾರರ ಜೊತೆಯಲ್ಲಿದ್ದನು.",
			"21": "<sup>21</sup> ದಾವೀದನು ಸತ್ತು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಿದನೆಂದೂ ಸೇನಾಪತಿಯಾದ ಯೋವಾಬನು ಮೃತನಾದನೆಂದೂ ಈಜಿಪ್ಟಿನಲ್ಲಿದ್ದ ಹದದನು ಕೇಳಿದನು. ಫರೋಹನಿಗೆ ಅವನು, “ನಾನು ಸ್ವದೇಶಕ್ಕೆ ಹೋಗಬೇಕೆಂದಿರುತ್ತೇನೆ; ಅಪ್ಪಣೆಕೊಡಿ,” ಎಂದನು.",
			"22": "<sup>22</sup> ಫರೋಹನು, “ನನ್ನ ಬಳಿಯಲ್ಲಿ ನಿನಗೇನು ಕೊರತೆಯಾಯಿತು? ಸ್ವದೇಶಕ್ಕೆ ಹೋಗಬೇಕೆಂದು ಏಕೆ ಆಶಿಸುತ್ತಿ?” ಎಂದು ಕೇಳಿದನು. ಅವನು, “ಏನೂ ಕೊರತೆಯಾಗಿಲ್ಲ; ಆದರೂ ನನಗೆ ಹೋಗುವುದಕ್ಕೆ ಅಪ್ಪಣೆಯಾಗಬೇಕು,” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು. ಹದದನು ತನ್ನ ಊರಿಗೆ ಮರಳಿದನು.",
			"23": "<sup>23</sup> ಸೊಲೊಮೋನನಿಗೆ ವಿರುದ್ಧ ದೇವರು ಎಬ್ಬಿಸಿದ ಎರಡನೆಯ ವೈರಿ ಎಲ್ಯಾದಾವನ ಮಗ ರೆಜೋನ್ ಎಂಬವನು.",
			"24": "<sup>24</sup> ದಾವೀದನು ಚೋಬದವರನ್ನು ಸಂಹರಿಸಿದಾಗ ಈ ರೆಜೋನನು ಚೋಬದ ಅರಸನೂ ತನ್ನ ಒಡೆಯನೂ ಆದ ಹದದೆಜೆರನನ್ನು ಬಿಟ್ಟು ಓಡಿಹೋಗಿ, ಕೆಲವು ಮಂದಿಯನ್ನು ಕೂಡಿಸಿಕೊಂಡು, ತಾನು ಅವರ ನಾಯಕನಾಗಿ, ಅವರೊಡನೆ ದಮಸ್ಕಕ್ಕೆ ಬಂದು ಅಲ್ಲಿ ಅರಸನಾದನು.",
			"25": "<sup>25</sup> ಸೊಲೊಮೋನನ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ಇಸ್ರಯೇಲರಿಗೆ ಮಹಾಬಾಧಕನಾದ ಹದದನ ಜೊತೆಗೆ ಇವನೂ ವೈರಿಯಾಗಿದ್ದನು. ಇವನು ಸಿರಿಯಾ ದೇಶದಲ್ಲಿ ಅರಸನಾಗಿ ಇಸ್ರಯೇಲರನ್ನು ಬಹಳವಾಗಿ ದ್ವೇಷಿಸಿದನು.",
			"26": "<sup>26</sup> ಅರಸ ಸೊಲೊಮೋನನಿಗೆ ವಿರುದ್ಧ ದಂಗೆ ಎದ್ದವರಲ್ಲಿ, ಅವನ ಸೇವಕನೂ ಚರೇದ ಊರಿನ ಎಫ್ರಯೀಮ್ಯನಾದ ನೆಬಾಟನ ಮಗನೂ ಆಗಿದ್ದ, ಯಾರೊಬ್ಬಾಮ ಎಂಬವನು ಮತ್ತೊಬ್ಬನು. ಚೆರೂಗಳೆಂಬ ವಿಧವೆ ಅವನ ತಾಯಿ.",
			"27": "<sup>27</sup> ಅವನು ದಂಗೆಯೆದ್ದ ವಿವರಣೆ ಹೀಗಿದೆ : ಸೊಲೊಮೋನನು ಮಿಲ್ಲೋಕೋಟೆಯನ್ನು ಕಟ್ಟಿಸಿ ತನ್ನ ತಂದೆ ದಾವೀದನ ಪಟ್ಟಣದ ದುರ್ಬಲ ಸ್ಥಳವನ್ನು ಭದ್ರಪಡಿಸುತ್ತಿರುವಾಗ",
			"28": "<sup>28</sup> ಯೌವನಸ್ಥನಾದ ಯಾರೊಬ್ಬಾಮನು ಬಹುಸಾಮರ್ಥ್ಯದಿಂದ ಕೆಲಸಮಾಡುತ್ತಿರುವುದನ್ನು ಕಂಡು ಅವನನ್ನು ಜೋಸೆಫ್ಯರ ಎಲ್ಲಾ ಆಳುಗಳ ಮೇಲೆ ಮೇಸ್ತ್ರಿಯನ್ನಾಗಿ ನೇಮಿಸಿದನು.",
			"29": "<sup>29</sup> ಒಂದು ದಿವಸ ಯಾರೊಬ್ಬಾಮನು ಜೆರುಸಲೇಮಿನಿಂದ ಎಲ್ಲಿಗೋ ಹೋಗುತ್ತಿರುವಾಗ ಅಡವಿಯಲ್ಲಿ ಶಿಲೋವಿನವನಾದ ಅಹೀಯನೆಂಬ ಪ್ರವಾದಿ ಅವನಿಗೆ ಎದುರಾದನು. ಇವನು ಹೊಸ ಬಟ್ಟೆಯನ್ನು ಹೊದ್ದುಕೊಂಡಿದ್ದನು. ಅಲ್ಲಿ ಇವರಿಬ್ಬರ ಹೊರತಾಗಿ ಬೇರೆ ಯಾರೂ ಇರಲಿಲ್ಲ.",
			"30": "<sup>30</sup> ಆಗ ಅಹೀಯನು, ತಾನು ಹೊದ್ದುಕೊಂಡಿದ್ದ ಹೊಸ ಬಟ್ಟೆಯನ್ನು ತೆಗೆದುಕೊಂಡು, ಅದನ್ನು ಹರಿದು ಹನ್ನೆರಡು ತುಂಡುಮಾಡಿ,",
			"31": "<sup>31</sup> ಯಾರೊಬ್ಬಾಮನಿಗೆ, “ನೀನು ಇವುಗಳಲ್ಲಿ ಹತ್ತು ತುಂಡುಗಳನ್ನು ತೆಗೆದುಕೋ. ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೆಶ್ವರ ರಾಜ್ಯವನ್ನು ಸೊಲೊಮೋನನಿಂದ ಕಿತ್ತುಕೊಂಡು",
			"32": "<sup>32</sup> ತನ್ನ ದಾಸ ದಾವೀದನ ನಿಮಿತ್ತ ಹಾಗು ತಾವು ಇಸ್ರಯೇಲರ ಎಲ್ಲಾ ಕುಲಗಳಿಂದ ಆರಿಸಿಕೊಂಡ ಜೆರುಸಲೇಮ್ ಪಟ್ಟಣದ ನಿಮಿತ್ತ ಅವನಿಗೆ ಒಂದೇ ಒಂದು ಕುಲವನ್ನು ಉಳಿಸಿ, ಬೇರೆ ಹತ್ತು ಕುಲಗಳನ್ನು ನಿನಗೆ ಕೊಡುತ್ತೇನೆಂದು ಹೇಳಿದ್ದಾರೆ.",
			"33": "<sup>33</sup> ಇದಲ್ಲದೆ, ಅವರು ನಿನಗೆ, ‘ಸೊಲೊಮೋನನೂ ಅವನ ಮನೆಯವರೂ ನನ್ನನ್ನು ಬಿಟ್ಟು ಸಿದೋನ್ಯರ ದೇವತೆಯಾದ ಅಷ್ಟೋರೆತ್, ಮೋವಾಬ್ಯರ ದೇವತೆಯಾದ ಕೆಮೋಷ್, ಅಮ್ಮೋನಿಯರ ದೇವತೆಯಾದ ಮಿಲ್ಕೋಮ್ ಇವುಗಳನ್ನು ಆರಾಧಿಸುತ್ತಿದ್ದಾರೆ. ಅವರ ತಂದೆ ದಾವೀದನು ನನ್ನ ನಿಯಮವಿಧಿಗಳನ್ನು ಕೈಕೊಂಡು ನನ್ನನ್ನು ಮೆಚ್ಚಿಸಿ ನಡೆದಂತೆ ಅವರು ನಡೆಯದೆಹೋದದ್ದೂ ಇದಕ್ಕೆ ಕಾರಣ.",
			"34": "<sup>34</sup> ಆದರೂ ನಾನು ಸೊಲೊಮೋನನಿಂದ ಸಾಮ್ರಾಜ್ಯವನ್ನೆಲ್ಲಾ ಕಿತ್ತುಕೊಳ್ಳುವುದಿಲ್ಲ; ನನ್ನ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ಕೈಕೊಂಡವನೂ ನಾನು ಆರಿಸಿಕೊಂಡ ದಾಸನೂ ಆದ ದಾವೀದನ ನಿಮಿತ್ತ ಅವನನ್ನು ಅವನ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ಅರಸನನ್ನಾಗಿಯೇ ಇರಿಸುವೆನು.",
			"35": "<sup>35</sup> ಆದರೆ, ಅವನ ಮಗನ ಕೈಯಿಂದ ಅದನ್ನು ಕಿತ್ತುಕೊಂಡು ನಿನಗೆ ಹತ್ತು ಕುಲಗಳನ್ನು ಕೊಡುವೆನು.",
			"36": "<sup>36</sup> ನಾನು ನನ್ನ ಹೆಸರಿಗಾಗಿ ಆರಿಸಿಕೊಂಡ ಜೆರುಸಲೇಮ್ ಪಟ್ಟಣದಲ್ಲಿ ನನ್ನ ದಾಸ ದಾವೀದನ ದೀಪ ನನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಉರಿಯುತ್ತಲೇ ಇರುವಂತೆ ಅವನ ಮಗನಿಗೆ ಒಂದು ಕುಲವನ್ನು ಉಳಿಸುವೆನು.",
			"37": "<sup>37</sup> ನಾನು ನಿನ್ನನ್ನು ಇಸ್ರಯೇಲರ ಅರಸನನ್ನಾಗಿ ನೇಮಿಸುವೆನು; ನೀನು ಅವರನ್ನು ಇಷ್ಟದ ಪ್ರಕಾರ ಆಳಬಹುದು.",
			"38": "<sup>38</sup> ನನ್ನ ದಾಸ ದಾವೀದನು ನನ್ನ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ಕೈಕೊಂಡಂತೆ, ನೀನೂ ನನ್ನ ಆಜ್ಞೆಗಳನ್ನು ಕೈಗೊಂಡು, ನನ್ನ ಮಾರ್ಗದಲ್ಲಿ ನಡೆದು ನನ್ನನ್ನು ಮೆಚ್ಚಿಸಿದರೆ, ನಾನು ನಿನ್ನ ಸಂಗಡ ಇದ್ದು ದಾವೀದನ ಸಂತಾನದಂತೆ ನಿನ್ನ ಸಂತಾನವನ್ನೂ ಸ್ಥಿರಪಡಿಸುವೆನು; ಇಸ್ರಯೇಲ್ ರಾಜ್ಯ ನಿನ್ನ ಪಾಲಾಗುವುದು.",
			"39": "<sup>39</sup> ದಾವೀದನ ಸಂತಾನದವರನ್ನು ಅವರ ಪಾಪದ ನಿಮಿತ್ತ ತಲೆ ತಗ್ಗಿಸುವಂತೆ ಮಾಡುವೆನು. ಆದರೆ ಸದಾಕಾಲಕ್ಕೂ ಅಲ್ಲ, ಎಂದು ಹೇಳಿದ್ದಾರೆ,” ಎಂದನು.",
			"40": "<sup>40</sup> ಸೊಲೊಮೋನನು ಯಾರೊಬ್ಬಾಮನನ್ನು ಕೊಲ್ಲುವುದಕ್ಕೆ ಪ್ರಯತ್ನಿಸಿದಾಗ ಅವನು ತಪ್ಪಿಸಿಕೊಂಡು, ಈಜಿಪ್ಟಿನ ಅರಸ ಶೀಶಕನ ಬಳಿಗೆ ಹೋಗಿ, ಸೊಲೊಮೋನನು ಜೀವದಿಂದಿರುವವರೆಗೆ ಅಲ್ಲಿಯೇ ಇದ್ದನು.",
			"41": "<sup>41</sup> ಸೊಲೊಮೋನನ ಉಳಿದ ಸಾಧನೆಗಳನ್ನು ಹಾಗು ಅವನ ಜ್ಞಾನವನ್ನು ಕುರಿತು ‘ಸೊಲೊಮೋನನ ಚರಿತ್ರೆ’ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತದೆ.",
			"42": "<sup>42</sup> ಅವನು ಜೆರುಸಲೇಮಿನಲ್ಲಿದ್ದುಕೊಂಡು, ಎಲ್ಲಾ ಇಸ್ರಯೇಲರನ್ನು ನಾಲ್ವತ್ತು ವರ್ಷ ಆಳಿ, ನಿಧನನಾಗಿ, ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಿದನು.",
			"43": "<sup>43</sup> ಅವನ ಶವವನ್ನು ತಂದೆಯಾದ ದಾವೀದನ ಪಟ್ಟಣದಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಅವನ ಸ್ಥಾನದಲ್ಲಿ ಅವನ ಮಗ ರೆಹಬ್ಬಾಮನು ಅರಸನಾದನು."
		},
		"12": {
			"1": "<sup>1</sup> ರೆಹಬ್ಬಾಮನು ಶೆಕೆಮಿಗೆ ಹೋದನು. ಅಲ್ಲಿ ಇಸ್ರಯೇಲರೆಲ್ಲರೂ ಅವನನ್ನು ಅರಸನನ್ನಾಗಿ ಮಾಡುವುದಕ್ಕೆ ಕೂಡಿಬಂದಿದ್ದರು.",
			"2": "<sup>2</sup> ಅರಸ ಸೊಲೊಮೋನನಿಂದ ತಪ್ಪಿಸಿಕೊಂಡು ಈಜಿಪ್ಟಿಗೆ ಓಡಿಹೋಗಿದ್ದ ನೆಬಾಟನ ಮಗ ಯಾರೊಬ್ಬಾಮನನ್ನು ಕರೆಸಿಕೊಂಡಿದ್ದರು.",
			"3": "<sup>3</sup> ಯಾರೊಬ್ಬಾಮನು ನಡೆದ ಸಂಗತಿಗಳನ್ನು ಕೇಳಿ, ಎಲ್ಲಾ ಇಸ್ರಯೇಲರೊಡನೆ ರೆಹಬ್ಬಾಮನ ಬಳಿಗೆ ಹೋಗಿ,",
			"4": "<sup>4</sup> “ನಿಮ್ಮ ತಂದೆ ನಮ್ಮ ಮೇಲೆ ಭಾರವಾದ ನೊಗವನ್ನು ಹೇರಿದ್ದರು; ಅವರು ನೇಮಿಸಿದ್ದ ಬಿಟ್ಟೀ ಕೆಲಸವನ್ನು ನೀನು ಕಡಿಮೆಮಾಡಿ, ನಮ್ಮ ಮೇಲಿರುವ ಭಾರವನ್ನು ಹಗುರಮಾಡುವುದಾದರೆ ನಾವು ನಿನಗೆ ಸೇವೆಮಾಡುತ್ತೇವೆ,” ಎಂದನು.",
			"5": "<sup>5</sup> ಅವನು, “ನೀವು ಈಗ ಹೋಗಿ ಮೂರು ದಿನಗಳಾದ ನಂತರ ಮತ್ತೆ ಬನ್ನಿ,” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು. ಜನರು ಹೊರಟುಹೋದರು.",
			"6": "<sup>6</sup> ಆಗ ಅರಸ ರೆಹಬ್ಬಾಮನು ತನ್ನ ತಂದೆ ಸೊಲೊಮೋನನ ಕಾಲದಲ್ಲಿ ಮಂತ್ರಿಗಳಾಗಿದ್ದ ಹಿರಿಯರನ್ನು ಕೂಡಿಸಿ, ತಾನು ಜನರಿಗೆ ಕೊಡಬೇಕಾದ ಉತ್ತರದ ಬಗ್ಗೆ ಅವರ ಆಲೋಚನೆಯನ್ನು ಕೇಳಿದನು.",
			"7": "<sup>7</sup> ಅವರು, “ನೀವು ಈ ದಿನ ಜನರ ಮಾತನ್ನು ಕೇಳಿ ಅವರಿಗೆ ವಿನಯರಾಗಿ, ಒಳ್ಳೆಯ ಉತ್ತರಕೊಟ್ಟರೆ ಅವರು ಯಾವಾಗಲೂ ನಿಮಗೆ ಅಧೀನರಾಗಿರುವರು,” ಎಂದರು.",
			"8": "<sup>8</sup> ಆದರೆ ಅವನು ಆ ಹಿರಿಯರ ಆಲೋಚನೆಯನ್ನು ನಿರಾಕರಿಸಿದನು. ತನ್ನ ಸಂಗಡ ಬೆಳೆದು ತನ್ನ ಮಂತ್ರಿಗಳಾದ ಯೌವನಸ್ಥರನ್ನು ಕರೆಸಿದನು.",
			"9": "<sup>9</sup> ನನ್ನ ತಂದೆ ಹೇರಿದ ನೊಗವನ್ನು ಹಗುರ ಮಾಡಬೇಕೆಂದು ಬೇಡಿಕೊಳ್ಳುವ ಈ ಜನರಿಗೆ ಯಾವ ಉತ್ತರವನ್ನು ಕೊಡಬೇಕು? ಈ ವಿಷಯದಲ್ಲಿ ನಿಮ್ಮ ಆಲೋಚನೆ ಏನು?” ಎಂದು ಕೇಳಿದನು.",
			"10": "<sup>10</sup> ಅವರು ಅವನಿಗೆ, “ನಿಮ್ಮ ತಂದೆ ನಮ್ಮ ಮೇಲೆ ಹೇರಿರುವ ನೊಗವನ್ನು ಹಗುರ ಮಾಡು ಎಂದು ನಿಮ್ಮನ್ನು ಬೇಡಿಕೊಂಡ ಜನರಿಗೆ ನೀವು, ‘ನನ್ನ ತಂದೆಯ ಸೊಂಟಕ್ಕಿಂತ ನನ್ನ ಈ ಕಿರಿಬೆರಳು ದಪ್ಪ;",
			"11": "<sup>11</sup> ನನ್ನ ತಂದೆ ನಿಮ್ಮ ಮೇಲೆ ಭಾರವಾದ ನೊಗವನ್ನು ಹೇರಿರುವುದು ನಿಜ. ಆದರೆ ನಾನು ಅದಕ್ಕೆ ಇನ್ನೂ ಹೆಚ್ಚಿನ ಭಾರವನ್ನು ಕೂಡಿಸುತ್ತೇನೆ; ಅವರು ನಿಮ್ಮನ್ನು ಬಾರುಕೋಲುಗಳಿಂದ ಹೊಡೆದದ್ದು ನಿಜ; ನಾನಾದರೋ ನಿಮ್ಮನ್ನು ಮುಳ್ಳುಕೊರಡೆಗಳಿಂದ ದಂಡಿಸುವೆನು,’ ಎಂಬುದಾಗಿ ಹೇಳಬೇಕು,” ಎಂದು ಉತ್ತರಕೊಟ್ಟರು.",
			"12": "<sup>12</sup> ಯಾರೊಬ್ಬಾಮನೂ ಜನರೆಲ್ಲರೂ ಅರಸ ರೆಹಬ್ಬಾಮನ ಅಪ್ಪಣೆಯಂತೆ ಮೂರನೆಯ ದಿನ ಪುನಃ ಅವನ ಬಳಿಗೆ ಬಂದರು.",
			"13": "<sup>13</sup> ಅರಸನು ಅವರಿಗೆ ಕಠಿಣವಾದ ಉತ್ತರವನ್ನು ಕೊಟ್ಟನು. ಅವನು ಹಿರಿಯರ ಆಲೋಚನೆಯನ್ನು ನಿರಾಕರಿಸಿ, ಯೌವನಸ್ಥರ ಆಲೋಚನೆಗನುಸಾರ ಜನರಿಗೆ,",
			"14": "<sup>14</sup> “ನನ್ನ ತಂದೆ ನಿಮ್ಮ ಮೇಲೆ ಭಾರವಾದ ನೊಗವನ್ನು ಹೇರಿರುವುದು ನಿಜ; ಆದರೆ ನಾನು ಅದಕ್ಕೆ ಇನ್ನೂ ಹೆಚ್ಚಿನ ಭಾರವನ್ನು ಕೂಡಿಸುವೆನು; ಅವರು ನಿಮ್ಮನ್ನು ಬಾರುಕೋಲುಗಳಿಂದ ಹೊಡೆದದ್ದು ನಿಜ; ನಾನಾದರೋ ಮುಳ್ಳುಕೊರಡೆಗಳಿಂದ ದಂಡಿಸುವೆನು,” ಎಂದು ನುಡಿದನು.",
			"15": "<sup>15</sup> ಅವನು ಜನರ ಮಾತನ್ನು ಕೇಳದೆಹೋದದ್ದು ಸರ್ವೇಶ್ವರನಿಂದಲೇ ಸಂಭವಿಸಿತ್ತು. ಹೀಗೆ ಸರ್ವೇಶ್ವರ ಶಿಲೋವಿನವನಾದ ಅಹೀಯನ ಮುಖಾಂತರ ನೆಬಾಟನ ಮಗ ಯಾರೊಬ್ಬಾಮನಿಗೆ ಹೇಳಿಸಿದ ಮಾತು ನೆರವೇರಿತು.",
			"16": "<sup>16</sup> ಅರಸನು ತಮ್ಮ ಮಾತನ್ನು ಲಕ್ಷಿಸಲಿಲ್ಲ ಎಂದು ನೋಡಿ ಇಸ್ರಯೇಲರೆಲ್ಲರೂ ಅವನಿಗೆ, “ದಾವೀದನಿಗೂ ನಮಗೂ ಏನೂ ಸಂಬಂಧವಿಲ್ಲ; ಜೆಸ್ಸೆಯ ಮಗನಿಗೂ ನಮಗೂ ಯಾವ ಬಾಧ್ಯತೆಯೂ ಇಲ್ಲ; ಇಸ್ರಯೇಲರೇ, ನಿಮ್ಮ ನಿಮ್ಮ ನಿವಾಸಗಳಿಗೆ ತೆರಳಿರಿ; ದಾವೀದನವರು ತಮ್ಮ ಕುಲವನ್ನು ತಾವೇ ನೋಡಿಕೊಳ್ಳಲಿ!” ಎಂದು ಹೇಳಿ ತಮ್ಮ ಮನೆಗಳಿಗೆ ಹೊರಟುಹೋದರು.",
			"17": "<sup>17</sup> ಜುದೇಯ ಪ್ರಾಂತ್ಯದಲ್ಲಿದ್ದ ಇಸ್ರಯೇಲರಾದರೋ ರೆಹಬ್ಬಾಮನ ಅಧೀನದಲ್ಲಿದ್ದರು.",
			"18": "<sup>18</sup> ರೆಹಬ್ಬಾಮನು ಬಿಟ್ಟೀ ಕೆಲಸದವರ ಮೇಲ್ವಿಚಾರಕನಾದ ಅದೋರಾಮನನ್ನು ಇಸ್ರಯೇಲರ ಬಳಿಗೆ ಕಳುಹಿಸಿದಾಗ ಅವರು ಅವನನ್ನು ಕಲ್ಲೆಸೆದು ಕೊಂದರು. ಅರಸ ರೆಹಬ್ಬಾಮನು ಬೇಗನೇ ರಥವನ್ನೇರಿ ಜೆರುಸಲೇಮಿಗೆ ಓಡಿಹೋದನು.",
			"19": "<sup>19</sup> ಇಸ್ರಯೇಲರು ಅಂದಿನಿಂದ ಇಂದಿನವರೆಗೂ ದಾವೀದನ ಕುಟುಂಬದವರೊಡನೆ ಸಮಾಧಾನವಾಗಲೇ ಇಲ್ಲ.",
			"20": "<sup>20</sup> ಯಾರೊಬ್ಬಾಮನು ಹಿಂದಿರುಗಿ ಬಂದು ಇದ್ದಾನೆಂಬ ಸಮಾಚಾರ ಇಸ್ರಯೇಲರಿಗೆ ಗೊತ್ತಾದಾಗ ಅವರೆಲ್ಲರೂ ಅವನನ್ನು ನೆರೆದ ಸಭೆಯ ಮುಂದೆ ಕರೆಸಿ ತಮ್ಮ ಅರಸನನ್ನಾಗಿ ನೇಮಿಸಿಕೊಂಡರು. ದಾವೀದನ ಕುಟುಂಬದವರನ್ನು ಯೆಹೂದಕುಲವೇ ಹೊರತು ಬೇರೆ ಯಾವ ಕುಲವೂ ಹಿಂಬಾಲಿಸಲಿಲ್ಲ.",
			"21": "<sup>21</sup> ಸೊಲೊಮೋನನ ಮಗ ರೆಹಬ್ಬಾಮನು ಜೆರುಸಲೇಮನ್ನು ಮುಟ್ಟಿದನಂತರ ಇಸ್ರಯೇಲರಿಗೆ ವಿರುದ್ಧ ಯುದ್ಧಮಾಡುವುದಕ್ಕೂ ರಾಜ್ಯವನ್ನು ಮರಳಿ ತನಗೆ ವಶಪಡಿಸಿಕೊಳ್ಳುವುದಕ್ಕೂ ಯೆಹೂದ ಬೆನ್ಯಾಮೀನ್ ಕುಲಗಳಿಂದ ಒಂದು ಲಕ್ಷದ ಎಂಬತ್ತು ಸಾವಿರ ಮಂದಿ ಶ್ರೇಷ್ಠ ಸೈನಿಕರನ್ನು ಕೂಡಿಸಿದನು.",
			"22": "<sup>22</sup> ಆಗ ದೈವಪುರುಷನಾದ ಶೆಮಾಯನಿಗೆ ಈ ದೈವವಾಣಿ ಕೇಳಿಸಿತು.",
			"23": "<sup>23</sup> “ನೀನು ಹೋಗಿ ಸೊಲೊಮೋನನ ಮಗನೂ ಜುದೇಯದ ಅರಸನೂ ಆದ ರೆಹಬ್ಬಾಮನಿಗೂ ಯೆಹೂದ ಬೆನ್ಯಾಮೀನ್ ಕುಲಗಳವರಿಗೂ ಹಾಗು ಉಳಿದ ಜನರಿಗೂ ಹೀಗೆಂದು ಹೇಳು: ಸರ್ವೇಶ್ವರನ ಮಾತನ್ನು ಕೇಳಿರಿ:",
			"24": "<sup>24</sup> ನೀವು ನಿಮ್ಮ ಸಹೋದರರಾದ ಇಸ್ರಯೇಲರೊಡನೆ ಯುದ್ಧಮಾಡಲು ಹೋಗಬಾರದು; ಎಲ್ಲರೂ ಹಿಂದಿರುಗಿ ಹೋಗಿರಿ; ಈ ವಿಭಜನಾಕಾರ್ಯ ಸರ್ವೇಶ್ವರನಿಂದಾಗಿದೆ, ಎಂದು ಹೇಳಬೇಕು.” ಅಂತೆಯೇ ಆ ಜನರು ಸರ್ವೇಶ್ವರನ ಮಾತನ್ನು ಕೇಳಿ ಹಿಂದಿರುಗಿ ಹೋದರು.",
			"25": "<sup>25</sup> ಯಾರೊಬ್ಬಾಮನು ಎಫ್ರಯಿಮ್ ಮಲೆನಾಡಿನ ಶೆಕೆಮ್ ಪಟ್ಟಣವನ್ನು ಭದ್ರಪಡಿಸಿ ಅಲ್ಲಿ ವಾಸಿಸಿದನು. ಅಲ್ಲಿಂದ ಪೆನೂವೇಲಿಗೆ ಹೋಗಿ ಅದನ್ನೂ ಭದ್ರಪಡಿಸಿದನು.",
			"26": "<sup>26</sup> ಅವನು ತನ್ನಲ್ಲೇ, “ರಾಜ್ಯವು ಮತ್ತೆ ದಾವೀದನ ಕುಟುಂಬದವರಿಗೆ ಆಗುವುದೋ ಏನೋ;",
			"27": "<sup>27</sup> ಜನರು ಜೆರುಸಲೇಮಿನಲ್ಲಿರುವ ಸರ್ವೇಶ್ವರನ ಆಲಯಕ್ಕೆ ಬಲಿಯರ್ಪಣೆಗಾಗಿ ಹೋದರೆ ಅವರ ಮನಸ್ಸು ಅವರ ಒಡೆಯನೂ ಯೆಹೂದದ ಅರಸನೂ ಆದ ರೆಹಬ್ಬಾಮನ ಕಡೆಗೆ ತಿರುಗಬಹುದು; ಅವರು ನನ್ನನ್ನು ಕೊಂದು ಅವನ ಬಳಿಗೆ ಹೋಗಬಹುದು,” ಎಂದುಕೊಂಡನು.",
			"28": "<sup>28</sup> ಬಹಳವಾಗಿ ಆಲೋಚಿಸಿ, ಕಡೆಗೆ ಬಂಗಾರದ ಎರಡು ಹೋರಿಕರುಗಳ ಮೂರ್ತಿಗಳನ್ನು ಮಾಡಿಸಿ, ಇಸ್ರಯೇಲರಿಗೆ, “ನೀವು ಜಾತ್ರೆಗಾಗಿ ಜೆರುಸಲೇಮಿಗೆ ಹೋದದ್ದು ಇನ್ನು ಸಾಕು. ಇಗೋ, ನಿಮ್ಮನ್ನು ಈಜಿಪ್ಟಿನಿಂದ ಕರೆದುತಂದ ದೇವರುಗಳು ಇಲ್ಲಿರುತ್ತವೆ,” ಎಂದು ಹೇಳಿ",
			"29": "<sup>29</sup> ಅವುಗಳಲ್ಲಿ ಒಂದನ್ನು ಬೇತೇಲಿನಲ್ಲಿರಿಸಿದನು; ಇನ್ನೊಂದನ್ನು ದಾನಿಗೆ ಕಳುಹಿಸಿದನು.",
			"30": "<sup>30</sup> ಇದು ಪಾಪಕ್ಕೆ ಕಾರಣವಾಯಿತು. ಜನರು ಈ ಎರಡನೆಯ ವಿಗ್ರಹವನ್ನು ಮೆರವಣಿಗೆಯಿಂದ ದಾನಿಗೆ ಒಯ್ದರು.",
			"31": "<sup>31</sup> ಇದಲ್ಲದೆ, ಅವನು ಪೂಜಾಗಿರಿಗಳ ಮೇಲೆ ಗುಡಿಗಳನ್ನು ಕಟ್ಟಿಸಿ, ಲೇವಿಯರಲ್ಲದ ಜನಸಾಮಾನ್ಯರನ್ನು ಯಾಜಕರನ್ನಾಗಿ ನೇಮಿಸಿದನು.",
			"32": "<sup>32</sup> ಅವನು ಎಂಟನೆಯ ತಿಂಗಳ ಹದಿನೈದನೆಯ ದಿವಸ, ನಾಡಿನ ಜಾತ್ರೆಗೆ ಸಮನಾದ ಜಾತ್ರೆ ಬೇತೇಲಿನಲ್ಲಿ ನಡೆಯಬೇಕೆಂದು ಅಪ್ಪಣೆ ಮಾಡಿದನು. ತಾನೂ ಅಲ್ಲಿಗೆ ಹೋಗಿ ಅಲ್ಲಿ ನಿಲ್ಲಿಸಿದ್ದ ಹೋರಿಕರುಗಳ ಮೂರ್ತಿಗಳಿಗೆ, ಪೀಠದ ಮೇಲೆ ಬಲಿ ಅರ್ಪಿಸಿದನು. ತಾನು ಏರ್ಪಡಿಸಿದ ಪೂಜಾಸ್ಥಳಗಳ ಯಾಜಕರನ್ನು ಬೇತೇಲಿನ ದೇವಸ್ಥಾನದ ಸೇವೆಗೆ ನೇಮಿಸಿದನು.",
			"33": "<sup>33</sup> ಎಂಟನೆಯ ತಿಂಗಳ ಹದಿನೈದನೆಯ ದಿನ ಇಸ್ರಯೇಲರು ಹಬ್ಬವನ್ನಾಚರಿಸಬೇಕೆಂದು ಯಾರೊಬ್ಬಾಮನು ಸ್ವೇಚ್ಛೆಯಿಂದ ಗೊತ್ತುಮಾಡಿದನು. ಆ ಸಮಯಕ್ಕೆ ತಾನೂ ಬೇತೇಲಿನ ಬಲಿಪೀಠದ ಬಳಿಗೆ ಹೋಗಿ ಧೂಪಾರತಿ ಎತ್ತಲು ಅದರ ಮೆಟ್ಟಲುಗಳನ್ನು ಹತ್ತುತ್ತಿದ್ದನು."
		},
		"13": {
			"1": "<sup>1</sup> ಜುದೇಯ ನಾಡಿನವನಾದ ಒಬ್ಬ ದೈವಭಕ್ತನು ಸರ್ವೇಶ್ವರನಿಂದ ಕಳುಹಿಸಲ್ಪಟ್ಟವನಾಗಿ ಬೇತೇಲಿಗೆ ಬಂದನು. ಯಾರೊಬ್ಬಾಮನು ಧೂಪಾರತಿ ಎತ್ತಲು ಪೀಠದ ಹತ್ತಿರ ನಿಂತುಕೊಂಡನು.",
			"2": "<sup>2</sup> ಆ ಭಕ್ತನು ಸರ್ವೇಶ್ವರನ ಅಪ್ಪಣೆಯಿಂದ ಆ ಪೀಠವನ್ನು ಉದ್ದೇಶಿಸಿ, “ಎಲೈ ಪೀಠವೇ, ಪೀಠವೇ, ದಾವೀದನ ಸಂತಾನದಲ್ಲಿ ಯೋಷೀಯ ಎಂಬೊಬ್ಬನು ಹುಟ್ಟುವನು. ಅವನು, ನಿನ್ನ ಮೇಲೆ ಧೂಪಸುಡುವ ಪೂಜಾಸ್ಥಳಗಳ ಯಾಜಕರನ್ನು ಹಿಡಿದು, ಅವರನ್ನು ನಿನ್ನ ಮೇಲೆಯೇ ಬಲಿಕೊಡುವನು. ಮನುಷ್ಯರ ಎಲುಬುಗಳನ್ನು ನಿನ್ನ ಮೇಲೆ ಸುಡಲಾಗುವುದು ಎಂದು ಸರ್ವೇಶ್ವರ ಹೇಳುತ್ತಾರೆ,” ಎಂದನು.",
			"3": "<sup>3</sup> ಇದಲ್ಲದೆ, “ನನ್ನ ಮಾತು ಸರ್ವೇಶ್ವರನದು ಎಂಬುದಕ್ಕೆ ಈ ಪೀಠವು ಸೀಳಿ ಅದರ ಮೇಲಿನ ಬೂದಿ ಬಿದ್ದುಹೋಗುವುದೇ ಗುರುತಾಗಿರುವುದು,” ಎಂದು ಹೇಳಿದನು.",
			"4": "<sup>4</sup> ಬೇತೇಲಿನ ಪೀಠದ ಬಳಿ ನಿಂತಿದ್ದ ಅರಸನು ಆ ದೈವಭಕ್ತನು ಪೀಠಕ್ಕೆ ವಿರೋಧವಾಗಿ ನುಡಿದದ್ದನ್ನು ಕೇಳಿ ಕೈಚಾಚಿ, “ಅವನನ್ನು ಹಿಡಿಯಿರಿ,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು. ಕೂಡಲೆ ಅವನ ಕೈ ಒಣಗಿಹೋಯಿತು. ಅವನು ಅದನ್ನು ಹಿಂತೆಗೆದುಕೊಳ್ಳಲಾಗಲಿಲ್ಲ.",
			"5": "<sup>5</sup> ಬಲಿಪೀಠವು ಸೀಳಿ ಅದರ ಮೇಲಿನ ಬೂದಿ ಬಿದ್ದುಹೋಯಿತು. ಹೀಗೆ ಆ ದೈವಭಕ್ತನು ಸರ್ವೇಶ್ವರನ ಅಪ್ಪಣೆಯಿಂದ ಹೇಳಿದ ಗುರುತು ನೆರವೇರಿತು.",
			"6": "<sup>6</sup> ಅರಸನು ಆ ಭಕ್ತನಿಗೆ, “ನಿನ್ನ ದೇವರಾದ ಸರ್ವೇಶ್ವರನು ಪ್ರಸನ್ನನಾಗುವಂತೆ ಬೇಡಿಕೊ; ನಾನು ನನ್ನ ಕೈಯನ್ನು ಹಿಂತೆಗೆಯುವುದಕ್ಕಾಗುವ ಹಾಗೆ ಅವರನ್ನು ನನ್ನ ಪರವಾಗಿ ಪ್ರಾರ್ಥಿಸು,” ಎಂದು ಕೇಳಿದನು. ಅಂತೆಯೇ ಆ ದೈವಭಕ್ತನು ಸರ್ವೇಶ್ವರನನ್ನು ಬೇಡಿಕೊಂಡನು; ಅರಸನ ಕೈ ವಾಸಿಯಾಗಿ ಮೊದಲಿನಂತೆ ಆಯಿತು.",
			"7": "<sup>7</sup> ಆಗ ಅರಸನು, “ನನ್ನ ಮನೆಗೆ ಬಾ; ಸ್ವಲ್ಪ ಊಟಮಾಡಿ ಸುಧಾರಿಸಿಕೊ. ನಾನು ನಿನಗೆ ಬಹುಮಾನವನ್ನು ಕೊಡುತ್ತೇನೆ,” ಎಂದನು.",
			"8": "<sup>8</sup> ಆ ದೈವಭಕ್ತನು, “ನೀವು ನನಗೆ ನಿಮ್ಮ ಆಸ್ತಿಯಲ್ಲಿ ಅರ್ಧ ಕೊಟ್ಟರೂ ನಾನು ನಿಮ್ಮ ಸಂಗಡ ಬರುವುದಿಲ್ಲ. ಇಲ್ಲಿ ಅನ್ನಪಾನಗಳನ್ನು ತೆಗೆದುಕೊಳ್ಳುವುದಿಲ್ಲ.",
			"9": "<sup>9</sup> ಇಲ್ಲಿ ಅನ್ನಪಾನಗಳನ್ನು ತೆಗೆದುಕೊಳ್ಳಬಾರದೆಂದೂ ಹಿಂದಿರುಗಿ ಹೋಗುವಾಗ ಬೇರೆ ದಾರಿಯಿಂದ ಹೋಗಬೇಕೆಂದೂ ನನಗೆ ಸರ್ವೇಶ್ವರನ ಅಪ್ಪಣೆಯಾಗಿದೆ,” ಎಂದನು.",
			"10": "<sup>10</sup> ಅನಂತರ ಅವನು ಆ ದಾರಿಯನ್ನು ಬಿಟ್ಟು ಇನ್ನೊಂದು ದಾರಿಯನ್ನು ಹಿಡಿದು ಹೊರಟನು.",
			"11": "<sup>11</sup> ಬೇತೇಲಿನಲ್ಲಿ ಒಬ್ಬ ಮುದುಕ ಪ್ರವಾದಿ ವಾಸವಾಗಿದ್ದನು. ಅವನ ಮಕ್ಕಳು ಬಂದು ಆ ದೈವಭಕ್ತನು ಆ ದಿನ ಬೇತೇಲಿನಲ್ಲಿ ಮಾಡಿದ್ದನ್ನೂ ಅವನು ಅರಸನಿಗೆ ಹೇಳಿದ್ದನ್ನೂ ತಂದೆಗೆ ತಿಳಿಸಿದರು.",
			"12": "<sup>12</sup> “ಅವನು ಯಾವ ದಾರಿಯಿಂದ ಹೋದ?” ಎಂದು ತಂದೆ ಅವರನ್ನು ಕೇಳಲು, ಅವರು ಜುದೇಯ ನಾಡಿನ ಆ ದೈವಭಕ್ತ ಹೋದ ದಾರಿಯನ್ನು ತೋರಿಸಿದರು.",
			"13": "<sup>13</sup> ಆಗ ಅವನು ತನ್ನ ಮಕ್ಕಳಿಗೆ, “ಕತ್ತೆಗೆ ತಡಿಹಾಕಿರಿ,” ಎಂದು ಆಜ್ಞಾಪಿಸಲು ಅವರು ಹಾಕಿದರು. ಅವನು ಕತ್ತೆಯ ಮೇಲೆ ಕುಳಿತುಕೊಂಡು",
			"14": "<sup>14</sup> ಆ ದೈವಭಕ್ತನ ಹಿಂದೆಯೇ ಹೋಗಿ ಏಲಾವೃಕ್ಷದ ಕೆಳಗೆ ಕುಳಿತಿದ್ದ ಅವನನ್ನು ಕಂಡು, “ಜುದೇಯದಿಂದ ಬಂದ ದೈವಭಕ್ತನು ನೀನೋ?” ಎಂದು ಕೇಳಿದನು. ಅವನು ಹೌದೆಂದನು.",
			"15": "<sup>15</sup> ಆಗ ಆ ಮುದುಕನು, “ನೀನು ನನ್ನ ಮನೆಗೆ ಬಂದು ಊಟಮಾಡು,” ಎಂದು ಹೇಳಿದನು.",
			"16": "<sup>16</sup> ಅದಕ್ಕೆ ಅವನು, “ನಾನು ನಿನ್ನ ಸಂಗಡ ಬರಕೂಡದು; ಈ ಸ್ಥಳದಲ್ಲಿ ಅನ್ನಪಾನಗಳನ್ನು ತೆಗೆದುಕೊಳ್ಳಬಾರದೆಂದೂ ಬಂದ ದಾರಿಯನ್ನು ಬಿಟ್ಟು ಬೇರೆ ದಾರಿಯಿಂದ ಹಿಂದಿರುಗಿ ಹೋಗಬೇಕೆಂದೂ ನನಗೆ ಸರ್ವೇಶ್ವರನ ಅಪ್ಪಣೆಯಾಗಿದೆ,” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"18": "<sup>18</sup> ಆಗ ಆ ಮುದುಕನು, “ನಾನೂ ನಿನ್ನಂತೆ ಪ್ರವಾದಿಯಾಗಿದ್ದೇನೆ; ಒಬ್ಬ ದೇವದೂತನು ನನಗೆ ಕಾಣಿಸಿಕೊಂಡು ನಿನ್ನನ್ನು ಕರೆದುಕೊಂಡು ಬರಬೇಕೆಂದು ಸರ್ವೆಶ್ವರನ ಹೆಸರಿನಲ್ಲಿ ಆಜ್ಞಾಪಿಸಿದ್ದಾನೆ,” ಎಂದು ಹೇಳಿದನು. ಆದರೆ ಈ ಮಾತು ಸುಳ್ಳಾಗಿತ್ತು.",
			"19": "<sup>19</sup> ದೈವಭಕ್ತನು ಹಿಂದಿರುಗಿ ಅವನ ಮನೆಗೆ ಹೋಗಿ ಅನ್ನಪಾನಗಳನ್ನು ತೆಗೆದುಕೊಂಡನು.",
			"20": "<sup>20</sup> ಅವರು ಊಟಮಾಡುತ್ತಿರುವಾಗ ದೈವಭಕ್ತನನ್ನು ಹಿಂದಕ್ಕೆ ಕರೆದುತಂದ ಆ ಪ್ರವಾದಿಗೆ ಸರ್ವೇಶ್ವರ ಒಂದು ಸಂದೇಶವನ್ನು ದಯಪಾಲಿಸಿದರು.",
			"21": "<sup>21</sup> ಅವನು ಜುದೇಯ ನಾಡಿನ ಆ ದೈವಭಕ್ತನಿಗೆ, “ನಿನ್ನ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿನಗೆ ಈ ಸ್ಥಳದಲ್ಲಿ ಅನ್ನಪಾನಗಳನ್ನು ತೆಗೆದುಕೊಳ್ಳಬಾರದೆಂದು ಆಜ್ಞಾಪಿಸಿದರೂ ನೀನು ಅವರ ಆಜ್ಞೆಯನ್ನು ಮೀರಿ, ಅವಿಧೇಯನಾಗಿ",
			"22": "<sup>22</sup> ಅನ್ನಪಾನಗಳನ್ನು ತೆಗೆದುಕೊಳ್ಳುವುದಕ್ಕಾಗಿ ಹಿಂದಿರುಗಿ ಬಂದುದರಿಂದ ನಿನ್ನ ಶವ ನಿನ್ನ ಕುಟುಂಬ ಸ್ಮಶಾನಭೂಮಿಯನ್ನು ಸೇರುವುದೇ ಇಲ್ಲವೆಂದು ಸರ್ವೇಶ್ವರ ಹೇಳುತ್ತಾರೆ,” ಎಂದನು.",
			"23": "<sup>23</sup> ಅನ್ನಪಾನಗಳನ್ನು ತೆಗೆದುಕೊಂಡನಂತರ ಆ ಮುದುಕ ಪ್ರವಾದಿ ತಾನು ಕರೆದುತಂದ ಪ್ರವಾದಿಯನ್ನು ಕಳುಹಿಸಲು ಕತ್ತೆಗೆ ತಡಿಹಾಕಿಸಿದನು.",
			"24": "<sup>24</sup> ಆ ದೈವಭಕ್ತನು ಸ್ವಲ್ಪ ದೂರಹೋದ ನಂತರ ಒಂದು ಸಿಂಹವು ಅವನಿಗೆ ಎದುರಾಗಿ ಬಂದು ಅವನನ್ನು ಕೊಂದುಹಾಕಿತು. ಅವನ ಶವ ದಾರಿಯಲ್ಲಿಯೇ ಬಿದ್ದಿತ್ತು; ಕತ್ತೆಯೂ ಸಿಂಹವೂ ಶವದ ಹತ್ತಿರ ನಿಂತುಕೊಂಡಿದ್ದವು.",
			"25": "<sup>25</sup> ಹಾದುಹೋಗುವವರು ಆ ಶವವು ದಾರಿಯಲ್ಲಿ ಬಿದ್ದು ಇರುವುದನ್ನೂ ಸಿಂಹವು ಅದರ ಬಳಿಯಲ್ಲೆ ನಿಂತಿರುವುದನ್ನೂ ಕಂಡು ಮುದುಕ ಪ್ರವಾದಿಯ ಊರಿಗೆ ಬಂದು ಅದನ್ನು ತಿಳಿಸಿದರು.",
			"26": "<sup>26</sup> ಅವನನ್ನು ಹಿಂದಕ್ಕೆ ಕರೆದುಕೊಂಡು ಹೋಗಿದ್ದ ಪ್ರವಾದಿ, ಈ ಸುದ್ದಿಯನ್ನು ಕೇಳಿ, “ಆ ಶವವು ದೈವಭಕ್ತನದೇ ಆಗಿರಬೇಕು. ಅವನು ಸರ್ವೇಶ್ವರನ ಆಜ್ಞೆಗೆ ಅವಿಧೇಯನಾದುದರಿಂದ ಸರ್ವೇಶ್ವರ ಅವನ ವಿಷಯದಲ್ಲಿ ತಾವು ಹೇಳಿದ ಮಾತು ನೆರವೇರುವಂತೆ ಅವನನ್ನು ಸಿಂಹದ ಬಾಯಿಗೆ ಒಪ್ಪಿಸಿದರು. ಅದು ಅವನ ಮೇಲೆ ಹಾರಿ ಅವನನ್ನು ಕೊಂದುಬಿಟ್ಟಿತು,” ಎಂದು ಹೇಳಿ,",
			"27": "<sup>27</sup> ಕತ್ತೆಗೆ ತಡಿಹಾಕಬೇಕೆಂದು ಮಕ್ಕಳಿಗೆ ಆಜ್ಞಾಪಿಸಿದನು. ಅವರು ಹಾಕಿದರು.",
			"28": "<sup>28</sup> ಅವನು ಹೋಗಿ ಶವವು ದಾರಿಯಲ್ಲಿ ಬಿದ್ದಿರುವುದನ್ನೂ ಕತ್ತೆಯೂ ಸಿಂಹವೂ ಅದರ ಬಳಿಯಲ್ಲಿ ನಿಂತಿರುವುದನ್ನೂ ಕಂಡನು. ಸಿಂಹವು ಶವವನ್ನು ತಿನ್ನಲಿಲ್ಲ, ಕತ್ತೆಯನ್ನು ಕೊಲ್ಲಲೂ ಇಲ್ಲ.",
			"29": "<sup>29</sup> ಪ್ರವಾದಿಯು ಆ ದೈವಭಕ್ತನ ಶವವನ್ನು ಹೂಳಿ ಗೋಳಾಡಬೇಕೆಂದು, ಕತ್ತೆಯ ಮೇಲೆ ಹಾಕಿ,",
			"30": "<sup>30</sup> ತನ್ನ ಊರಿಗೆ ತೆಗೆದುಕೊಂಡು ಬಂದು, ತನ್ನ ಸ್ಮಶಾನ ಭೂಮಿಯಲ್ಲಿ ಅದನ್ನು ಸಮಾಧಿಮಾಡಿದನು. ಅವರು, “ಅಯ್ಯೋ, ನನ್ನ ಸಹೋದರನೇ,” ಎಂದು ಗೋಳಾಡಿದರು.",
			"31": "<sup>31</sup> ಅನಂತರ ಅವನು ತನ್ನ ಮಕ್ಕಳಿಗೆ, “ನಾನು ಸತ್ತಾಗ ನನ್ನ ಶವವನ್ನು ಆ ದೈವಭಕ್ತನ ಶವ ಇರುವಲ್ಲೇ ಸಮಾಧಿಮಾಡಿರಿ; ನನ್ನ ಎಲುಬುಗಳನ್ನು ಅವನ ಎಲುಬುಗಳಿರುವಲ್ಲೇ ಇಡಿ.",
			"32": "<sup>32</sup> ಅವನು ಸರ್ವೇಶ್ವರನ ಅಪ್ಪಣೆಯಿಂದ ಬೇತೇಲಿನ ಬಲಿಪೀಠಕ್ಕೆ ವಿರುದ್ಧ ಹಾಗು ಸಮಾರಿಯದ ಪಟ್ಟಣಗಳಲ್ಲಿರುವ ಎಲ್ಲಾ ಪೂಜಾಸ್ಥಳಗಳಿಗೆ ವಿರುದ್ಧ ಹೇಳಿದ ಮಾತುಗಳು ನಿಸ್ಸಂದೇಹವಾಗಿ ನೆರವೇರುವುವು,” ಎಂದನು.",
			"33": "<sup>33</sup> ಇಷ್ಟಾದರೂ ಯಾರೊಬ್ಬಾಮನು ತನ್ನ ದುರ್ಮಾರ್ಗವನ್ನು ಬಿಡದೆ ಪದೇ ಪದೇ ಜನಸಾಮಾನ್ಯರನ್ನು ಉನ್ನತಸ್ಥಾನಗಳಿಗೆ ನೇಮಿಸಿದನು; ಮನಸ್ಸಿಗೆ ಬಂದವರನ್ನು ಪೂಜಾಸ್ಥಳಗಳ ಯಾಜಕರನ್ನಾಗಿ ಪ್ರತಿಷ್ಠಿಸಿದನು.",
			"34": "<sup>34</sup> ಈ ಕಾರಣ ಯಾರೊಬ್ಬಾಮನ ಕುಟುಂಬದವರು ಪಾಪಿಗಳಾಗಿ ಭೂಲೋಕದಿಂದ ವಿಸರ್ಜಿತರಾಗಿ ನಿರ್ನಾಮವಾದರು."
		},
		"14": {
			"1": "<sup>1</sup> ಆ ಸಮಯದಲ್ಲಿ ಯಾರೊಬ್ಬಾಮನ ಮಗ ಅಬೀಯನು ಅಸ್ವಸ್ಥನಾದನು.",
			"2": "<sup>2</sup> ಆಗ ಯಾರೊಬ್ಬಾಮನು ತನ್ನ ಹೆಂಡತಿಗೆ, “ನೀನು ಎದ್ದು ನನ್ನ ಹೆಂಡತಿಯೆಂದು ಯಾರಿಗೂ ಗೊತ್ತಾಗದಂತೆ ವೇಷಮಾರ್ಪಡಿಸಿಕೊಂಡು ಶಿಲೋವಿಗೆ ಹೋಗು. ನಾನು ಈ ಜನರಿಗೆ ಅರಸನಾಗಬೇಕೆಂದು ನನಗೆ ಮುಂತಿಳಿಸಿದ ಪ್ರವಾದಿ ಅಹೀಯನು ಅಲ್ಲಿರುತ್ತಾನೆ.",
			"3": "<sup>3</sup> ನೀನು ಹತ್ತು ರೊಟ್ಟಿಗಳನ್ನೂ ಸಿಹಿಪದಾರ್ಥಗಳನ್ನೂ ಒಂದು ಕುಪ್ಪಿ ಜೇನುತುಪ್ಪವನ್ನೂ ತೆಗೆದುಕೊಂಡು ಅವನ ಬಳಿಗೆ ಹೋಗು; ಹುಡುಗನಿಗೆ ಏನಾಗುವುದೆಂದು ಅವನು ತಿಳಿಸುವನು,” ಎಂದು ಹೇಳಿದನು.",
			"4": "<sup>4</sup> ಆಕೆ ಅವನು ಹೇಳಿದಂತೆಯೇ ಮಾಡಿ ಶಿಲೋವಿನಲ್ಲಿದ್ದ ಅಹೀಯನ ಮನೆಗೆ ಹೋದಳು. ಅಹೀಯನಿಗೆ ನೋಡಲಾಗುತ್ತಿರಲಿಲ್ಲ; ಮುಪ್ಪಿನಿಂದ ಅವನ ಕಣ್ಣು ಮೊಬ್ಬಾಗಿದ್ದವು.",
			"5": "<sup>5</sup> ಆದರೆ ಸರ್ವೇಶ್ವರ ಅವನಿಗೆ, ಯಾರೊಬ್ಬಾಮನ ಹೆಂಡತಿ ಅಸ್ವಸ್ಥನಾದ ತನ್ನ ಮಗನ ವಿಷಯದಲ್ಲಿ ದೈವೋತ್ತರವನ್ನು ಕೇಳುವುದಕ್ಕೆ ತನ್ನನ್ನು ಅನ್ಯಳೆಂದು ತೋರ್ಪಡಿಸಿಕೊಂಡು ಬರುತ್ತಾಳೆ; ಆಕೆಗೆ ಇಂಥಿಂಥ ಉತ್ತರಕೊಡು, ಎಂದು ತಿಳಿಸಿದ್ದರು.",
			"6": "<sup>6</sup> ಆಕೆ ಅಹೀಯನ ಮನೆಯನ್ನು ಪ್ರವೇಶಿಸುವಾಗಲೇ ಅಹೀಯನು ಆಕೆಯ ಕಾಲುಸಪ್ಪಳವನ್ನು ಕೇಳಿ, “ಯಾರೊಬ್ಬಾಮನ ಹೆಂಡತಿಯೇ, ಬಾ; ಏಕೆ ನೀನು ಅನ್ಯಳೆಂದು ನಟಿಸುತ್ತಿರುವೆ? ನಿನಗೆ ಕಠಿಣವಾದ ಉತ್ತರವನ್ನು ಕೊಡಬೇಕೆಂದು ನನಗೆ ಅಪ್ಪಣೆಯಾಗಿದೆ.",
			"7": "<sup>7</sup> ನೀನು ಹೋಗಿ ಯಾರೊಬ್ಬಾಮನಿಗೆ ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಅಪ್ಪಣೆಯನ್ನು ತಿಳಿಸು. ಅವರು ಅವನಿಗೆ, ‘ನಾನು ನಿನ್ನನ್ನು ಜನಸಾಮಾನ್ಯರ ಮಧ್ಯದಿಂದ ಉನ್ನತ ಸ್ಥಾನಕ್ಕೆ ಏರಿಸಿದೆ; ನಿನ್ನನ್ನು ನನ್ನ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಯೇಲರ ನಾಯಕನನ್ನಾಗಿ ಮಾಡಿದೆ.",
			"8": "<sup>8</sup> ರಾಜ್ಯವನ್ನು ದಾವೀದನ ಕುಟುಂಬದವರಿಂದ ಕಿತ್ತು ನಿನಗೆ ಕೊಟ್ಟೆ. ಆದರೂ ನೀನು ನನ್ನ ದಾಸ ದಾವೀದನಂತೆ ಆಜ್ಞೆಗಳನ್ನು ಕೈಗೊಂಡು, ಪೂರ್ಣಮನಸ್ಸಿನಿಂದ ನನ್ನನ್ನು ಮೆಚ್ಚಿಸಲಿಲ್ಲ; ನನ್ನನ್ನು ಹಿಂಬಾಲಿಸಿ ನಡೆಯಲಿಲ್ಲ.",
			"9": "<sup>9</sup> ನೀನು ನಿನ್ನ ಎಲ್ಲಾ ಪೂರ್ವಾಧಿಕಾರಿಗಳಿಗಿಂತಲೂ ದುಷ್ಟನಾದೆ; ನನ್ನನ್ನು ತಿರಸ್ಕರಿಸಿ ಅನ್ಯದೇವತೆಗಳನ್ನೂ ಎರಕದ ವಿಗ್ರಹಗಳನ್ನೂ ಪೂಜಿಸಿ ನನಗೆ ಕೋಪವನ್ನೆಬ್ಬಿಸಿದೆ.",
			"10": "<sup>10</sup> ಆದುದರಿಂದ ಯಾರೊಬ್ಬಾಮನೇ, ಕೇಳು; ನಾನು ನಿನ್ನ ಮನೆಯವರ ಮೇಲೆ ಕೇಡನ್ನು ಬರಮಾಡುವೆನು; ನಿನ್ನ ಕುಟುಂಬದ ಗಂಡಸರಲ್ಲಿ, ಸ್ವತಂತ್ರರಾಗಲಿ, ಪರತಂತ್ರರಾಗಲಿ, ಎಲ್ಲರನ್ನೂ ಇಸ್ರಯೇಲರ ಮಧ್ಯೆಯಿಂದ ಸಂಹರಿಸಿಬಿಡುವೆನು. ಕಸವನ್ನು ಗುಡಿಸಿ ಎಸೆಯುವಂತೆ ನಾನು ನಿನ್ನ ಮನೆಯವರನ್ನು ಎಸೆದುಬಿಡುವೆನು; ಅವರು ನಿರ್ನಾಮವಾಗುವರು.",
			"11": "<sup>11</sup> ಅವರಲ್ಲಿ ಪಟ್ಟಣದೊಳಗೆ ಸಾಯುವಂಥವರನ್ನು ನಾಯಿಗಳೂ, ಅಡವಿಯಲ್ಲಿ ಸಾಯುವಂಥವರನ್ನು ಪಕ್ಷಿಗಳೂ, ತಿಂದುಬಿಡುವವು,’ ಎಂದು ಹೇಳುತ್ತಾರೆ. ಇದು ಸರ್ವೇಶ್ವರನ ಮಾತು.",
			"12": "<sup>12</sup> “ನೀನು ನಿನ್ನ ಮನೆಗೆ ಹೋಗು; ನೀನು ಪಟ್ಟಣದೊಳಗೆ ಕಾಲಿಡುವಷ್ಟರಲ್ಲಿ ಹುಡುಗನು ಸಾಯುವನು.",
			"13": "<sup>13</sup> ಇಸ್ರಯೇಲರೆಲ್ಲರೂ ಅವನಿಗಾಗಿ ಗೋಳಾಡಿ ಅವನ ಶವವನ್ನು ಸಮಾಧಿಮಾಡುವರು. ಯಾರೊಬ್ಬಾಮನ ಮನೆಯವರೊಳಗೆ ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ಒಳ್ಳೆಯವನಾಗಿರುವುದರಿಂದ ಇವನೊಬ್ಬನೇ ಸಮಾಧಿಹೊಂದುವನು.",
			"14": "<sup>14</sup> ಸರ್ವೇಶ್ವರ, ಇಸ್ರಯೇಲರ ಮೇಲೆ ಬೇರೊಬ್ಬ ಅರಸನನ್ನು ನೇಮಿಸುವರು. ಅವನು ಕೂಡಲೆ ಯಾರೊಬ್ಬಾಮನ ಮನೆಯವರನ್ನು ಸಂಹರಿಸಿಬಿಡುವನು. ಇನ್ನು ಏನೇನು ಸಂಭವಿಸುವುದೋ!",
			"15": "<sup>15</sup> ಸರ್ವೇಶ್ವರ ಇಸ್ರಯೇಲರನ್ನು ಹೊಡೆಯುವರು. ಆಗ ಅವರು ನೀರಿನಲ್ಲಿರುವ ಆಪು ಹುಲ್ಲೋ ಎಂಬಂತೆ ಹೊಯ್ದಾಡುವರು. ಅಶೇರ ವಿಗ್ರಹಸ್ತಂಭಗಳನ್ನು ಮಾಡಿಕೊಂಡು ಸರ್ವೇಶ್ವರನಿಗೆ ಕೋಪವನ್ನೆಬ್ಬಿಸಿದ ಅವರನ್ನು, ಅವರ ಪಿತೃಗಳಿಗೆ ಕೊಟ್ಟ ನಾಡಿನಿಂದ ಬೇರುಸಹಿತ ಕಿತ್ತುಹಾಕಿ ಯೂಫ್ರೆಟಿಸ್ ನದಿಯ ಆಚೆಯಲ್ಲಿರುವ ದೇಶದಲ್ಲೆಲ್ಲಾ ಚದರಿಸಿಬಿಡುವರು.",
			"16": "<sup>16</sup> ಯಾರೊಬ್ಬಾಮನ ಪಾಪಗಳ ನಿಮಿತ್ತ ಹಾಗು ಅವನ ಪ್ರೇರಣೆಯಿಂದ ಇಸ್ರಯೇಲರು ಮಾಡಿದ ಪಾಪಗಳ ನಿಮಿತ್ತ ಸರ್ವೇಶ್ವರ ಅವರನ್ನು ಶತ್ರುಗಳಿಗೆ ಒಪ್ಪಿಸುವರು,” ಎಂದು ಆಕೆಗೆ ಹೇಳಿದನು.",
			"17": "<sup>17</sup> ಯಾರೊಬ್ಬಾಮನ ಹೆಂಡತಿ ತಿರ್ಚಾ ಊರಿಗೆ ಹೊರಟುಹೋಗಿ ತನ್ನ ಮನೆಯ ಹೊಸ್ತಿಲಲ್ಲಿ ಕಾಲಿಟ್ಟಕೂಡಲೆ ಹುಡುಗನು ಸತ್ತನು.",
			"18": "<sup>18</sup> ಸರ್ವೇಶ್ವರ ಪ್ರವಾದಿಯಾದ ಅಹೀಯನ ಮುಖಾಂತರ ತಿಳಿಸಿದಂತೆ ಎಲ್ಲಾ ಇಸ್ರಯೇಲರು ಅವನಿಗಾಗಿ ಗೋಳಾಡಿ ಅವನ ಶವವನ್ನು ಸಮಾಧಿಮಾಡಿದರು.",
			"19": "<sup>19</sup> ಯಾರೊಬ್ಬಾಮನ ಇತರ ಕಾರ್ಯಕಲಾಪಗಳು, ಅವನ ಯುದ್ಧಗಳು ಹಾಗು ರಾಜ್ಯಭಾರಗಳ ವಿವರವು, ‘ಇಸ್ರಯೇಲ್ ರಾಜರ ಇತಿಹಾಸ’ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"20": "<sup>20</sup> ಅವನು ಇಪ್ಪತ್ತೆರಡು ವರ್ಷ ಆಳಿ ಮೃತನಾಗಿ ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಿದನು. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗ ನಾದಾಬನು ಅರಸನಾದನು.",
			"21": "<sup>21</sup> ಸೊಲೊಮೋನನ ಮಗ ರೆಹಬ್ಬಾಮನು ಜುದೇಯದ ಅರಸನಾದನು. ಅವನು ಪಟ್ಟಕ್ಕೆ ಬಂದಾಗ ಅವನಿಗೆ ನಾಲ್ವತ್ತೊಂದು ವರ್ಷ ವಯಸ್ಸು. ಸರ್ವೆಶ್ವರ ತಮ್ಮ ಹೆಸರನ್ನು ನೆಲೆಗೊಳಿಸುವುದಕ್ಕಾಗಿ, ಇಸ್ರಯೇಲರ ಎಲ್ಲಾ ಕುಲಗಳಿಂದ ಆರಿಸಿಕೊಂಡ ಜೆರುಸಲೇಮ್ ಪಟ್ಟಣದಲ್ಲಿ ಅವನು ಹದಿನೇಳು ವರ್ಷ ಆಳಿದನು. ಅಮ್ಮೋನಿಯಳಾದ ನಯಮಾ ಎಂಬಾಕೆ ಅವನ ತಾಯಿ.",
			"22": "<sup>22</sup> ಯೆಹೂದ್ಯರು ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಗಳಾದರು. ತಮ್ಮ ಪಿತೃಗಳಿಗಿಂತಲೂ ದುಷ್ಟರಾಗಿ ಸರ್ವೇಶ್ವರನನ್ನು ರೇಗಿಸಿದರು.",
			"23": "<sup>23</sup> ಪಿತೃಗಳಂತೆಯೇ ಅವರೂ ತಮಗಾಗಿ ಪೂಜಾಸ್ಥಳಗಳನ್ನು ಏರ್ಪಡಿಸಿಕೊಂಡರು. ಪ್ರತಿಯೊಂದು ದಿಣ್ಣೆಯ ಮೇಲೆ ಹಾಗು ಚೆನ್ನಾಗಿ ಬೆಳೆದಿದ್ದ ಪ್ರತಿಯೊಂದು ಮರದ ಕೆಳಗೆ, ಕಲ್ಲಿನ ಕಂಬಗಳನ್ನೂ ಅಶೇರ ವಿಗ್ರಹಸ್ತಂಭಗಳನ್ನೂ ನಿಲ್ಲಿಸಿದರು.",
			"24": "<sup>24</sup> ಇದಲ್ಲದೆ, ಅವರ ನಾಡಿನಲ್ಲಿ ವೇಶ್ಯಾವೃತ್ತಿಯನ್ನು ಅನುಸರಿಸುತ್ತಿದ್ದ ಗಂಡಸರೂ ಹೆಂಗಸರೂ ಇದ್ದರು. ಅವರ ನಾಡಿನಿಂದ ಸರ್ವೇಶ್ವರ ಹೊರಡಿಸಿಬಿಟ್ಟಿದ್ದ ಅನ್ಯಜನಾಂಗಗಳ ದುರಾಚಾರಗಳನ್ನೆಲ್ಲಾ ಅವರೂ ನಡೆಸುತ್ತಿದ್ದರು.",
			"25": "<sup>25</sup> ರೆಹಬ್ಬಾಮನ ಆಳ್ವಿಕೆಯ ಐದನೆಯ ವರ್ಷದಲ್ಲಿ ಈಜಿಪ್ಟಿನ ಅರಸ ಶೀಶಕನು ಜೆರುಸಲೇಮಿನ ಮೇಲೆ ದಾಳಿಮಾಡಿದನು.",
			"26": "<sup>26</sup> ಸರ್ವೇಶ್ವರನ ಆಲಯದ ಮತ್ತು ಅರಮನೆಯ ಎಲ್ಲಾ ದ್ರವ್ಯವನ್ನು ಹಾಗು ಸೊಲೊಮೋನನು ಮಾಡಿಸಿದ ಬಂಗಾರದ ಗುರಾಣಿಗಳನ್ನು ದೋಚಿಕೊಂಡು ಹೋದನು.",
			"27": "<sup>27</sup> ಅರಸ ರೆಹಬ್ಬಾಮನು ಅವುಗಳಿಗೆ ಬದಲಾಗಿ ತಾಮ್ರದ ಗುರಾಣಿಗಳನ್ನು ಮಾಡಿಸಿ ಅವುಗಳನ್ನು ಅರಮನೆಯ ದ್ವಾರಪಾಲಕರಾಗಿದ್ದ ಮೈಗಾವಲಿನವರ ದಳವಾಯಿಗಳಿಗೆ ಒಪ್ಪಿಸಿದ್ದನು.",
			"28": "<sup>28</sup> ಅರಸನು ಸರ್ವೇಶ್ವರನ ಆಲಯಕ್ಕೆ ಹೋಗುವಾಗಲೆಲ್ಲಾ ಅವರು ಆ ಗುರಾಣಿಗಳನ್ನು ಹಿಡಿದುಕೊಳ್ಳುತ್ತಿದ್ದರು. ಅಲ್ಲಿಂದ ಹಿಂದಕ್ಕೆ ಬಂದ ಮೇಲೆ ಅವುಗಳನ್ನು ತಮ್ಮ ಕೋಣೆಯಲ್ಲಿಡುತ್ತಿದ್ದರು.",
			"29": "<sup>29</sup> ರೆಹಬ್ಬಾಮನ ಉಳಿದ ಚರಿತ್ರೆ ಹಾಗು ಅವನ ಎಲ್ಲಾ ಕೃತ್ಯಗಳು ಯೆಹೂದ ರಾಜರ ಇತಿಹಾಸ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"30": "<sup>30</sup> ರೆಹಬ್ಬಾಮನಿಗೂ ಯಾರೊಬ್ಬಾಮನಿಗೂ ಯಾವಾಗಲೂ ಯುದ್ಧ ನಡೆಯುತ್ತಿತ್ತು.",
			"31": "<sup>31</sup> ರೆಹಬ್ಬಾಮನು ಸತ್ತು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಲು ಅವನ ಶವವನ್ನು ದಾವೀದನಗರದೊಳಗೆ ಅವನ ಹಿರಿಯರ ಸ್ಮಶಾನಭೂಮಿಯಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಅನಂತರ ಅವನ ಮಗ ಅಬೀಯಾಮನು ಅವನ ಸ್ಥಾನದಲ್ಲಿ ಅರಸನಾದನು."
		},
		"15": {
			"1": "<sup>1</sup> ನೆಬಾಟನ ಮಗ ಯಾರೊಬ್ಬಾಮನ ಆಳ್ವಿಕೆಯ ಹದಿನೆಂಟನೆಯ ವರ್ಷದಲ್ಲಿ ಅಬೀಯಾಮನು",
			"2": "<sup>2</sup> ಜುದೇಯದ ಅರಸನಾಗಿ ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಮೂರು ವರ್ಷ ಆಳಿದನು. ಅಬೀಷಾಲೋಮನ ಮಗಳಾದ ಮಾಕಾ ಎಂಬಾಕೆ ಅವನ ತಾಯಿ.",
			"3": "<sup>3</sup> ತನ್ನ ತಂದೆ ಮಾಡಿದ ಪಾಪಕೃತ್ಯಗಳನ್ನು ಇವನೂ ಮಾಡಿದನು. ಇವನ ಪೂರ್ವಜ ದಾವೀದನು ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ಪೂರ್ಣಮನಸ್ಸಿನಿಂದ ಸೇವೆಸಲ್ಲಿಸಿದಂತೆ ಇವನು ಸೇವೆಸಲ್ಲಿಸಲಿಲ್ಲ.",
			"4": "<sup>4</sup> ಹಿತ್ತಿಯನಾದ ಊರೀಯನ ವಿಷಯವೊಂದನ್ನು ಬಿಟ್ಟರೆ, ಬೇರೆ ಯಾವ ವಿಷಯದಲ್ಲೂ ತಮ್ಮ ಆಜ್ಞೆಯನ್ನು ಮೀರದೆ, ಜೀವದಿಂದಿರುವವರೆಗೂ ತಮ್ಮ ಚಿತ್ತಕ್ಕನುಸಾರ ನಡೆದ ದಾವೀದನ ನಿಮಿತ್ತ, ಸರ್ವೇಶ್ವರಸ್ವಾಮಿ ಜೆರುಸಲೇಮಿನಲ್ಲಿನ ಇವನ ದೀಪವನ್ನು ಆರಿಸಲಿಲ್ಲ.",
			"5": "<sup>5</sup> ಜೆರುಸಲೇಮನ್ನೂ ಇವನ ಸಂತಾನವನ್ನೂ ಉಳಿಸಿದರು.",
			"6": "<sup>6</sup> (ರೆಹಬ್ಬಾಮನಿಗೂ ಯಾರೊಬ್ಬಾಮನಿಗೂ ಯಾವಾಗಲೂ ಯುದ್ಧ ನಡೆಯುತ್ತಿತ್ತು.)",
			"7": "<sup>7</sup> ಅಬೀಯಾಮನ ಉಳಿದ ಚರಿತ್ರೆ ಹಾಗು ಅವನ ಎಲ್ಲಾ ಕೃತ್ಯಗಳು ಯೆಹೂದರಾಜರ ಇತಿಹಾಸ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಲಿಖಿತವಾಗಿವೆ. ಇವನಿಗೂ ಯಾರೊಬ್ಬಾಮನಿಗೂ ಯುದ್ಧ ನಡೆಯುತ್ತಿತ್ತು.",
			"8": "<sup>8</sup> ಅಬೀಯಾಮನು ಸತ್ತು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಲು ಅವನ ಶವವನ್ನು ದಾವೀದನಗರದಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಅವನ ಸ್ಥಾನದಲ್ಲಿ ಅವನ ಮಗ ಆಸನು ಅರಸನಾದನು.",
			"9": "<sup>9</sup> ಇಸ್ರಯೇಲರ ಅರಸ ಯಾರೊಬ್ಬಾಮನ ಆಳ್ವಿಕೆಯ ಇಪ್ಪತ್ತನೆಯ ವರ್ಷದಲ್ಲಿ ಆಸನು ಜುದೇಯದ ಅರಸನಾದನು.",
			"10": "<sup>10</sup> ಇವನು ಜೆರುಸಲೇಮಿನಲ್ಲಿ ನಾಲ್ವತ್ತೊಂದು ವರ್ಷ ಆಳಿದನು. ಅಬ್ಷಾಲೋಮನ ಮಗಳಾದ ಮಾಕಾ ಎಂಬಾಕೆ ಇವನ ತಾಯಿ.",
			"11": "<sup>11</sup> ಇವನು ತನ್ನ ಪೂರ್ವಜ ದಾವೀದನಂತೆ ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ಒಳ್ಳೆಯವನಾಗಿ ನಡೆದನು.",
			"12": "<sup>12</sup> ವೇಶ್ಯೆಯರಾಗಿ ವರ್ತಿಸುತ್ತಿದ್ದ, ಹೆಂಗಸರು ಹಾಗು ಗಂಡಸರನ್ನೆಲ್ಲಾ ನಾಡಿನಿಂದ ಹೊರಡಿಸಿ ತನ್ನ ಹಿರಿಯರು ಮಾಡಿಸಿಟ್ಟಿದ್ದ ವಿಗ್ರಹಗಳನ್ನೆಲ್ಲಾ ತೆಗೆದು ಹಾಕಿದನು.",
			"13": "<sup>13</sup> ಇವನ ತಾಯಿ ಮಾಕಳು ಅಶೇರ ದೇವತೆಯ ಒಂದು ಅಸಹ್ಯವಾದ ಮೂರ್ತಿಯನ್ನು ಮಾಡಿಸಿದ್ದರಿಂದ ಆಸನು ಆಕೆಯನ್ನು ‘ರಾಜಮಾತೆ’ ಎಂಬ ಪದವಿಯಿಂದ ತೆಗೆದುಹಾಕಿ ಆ ಮೂರ್ತಿಯನ್ನು ಕಡಿದು ಕಿದ್ರೋನ್ ಹಳ್ಳದ ಹತ್ತಿರ ಸುಡಿಸಿದನು.",
			"14": "<sup>14</sup> ಇವನು ನಾಡಿನಲ್ಲಿದ್ದ ಪೂಜಾಸ್ಥಳಗಳನ್ನು ಹಾಳುಮಾಡದಿದ್ದರೂ ತನ್ನ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ಯಥಾರ್ಥಮನಸ್ಸಿನಿಂದ ಸರ್ವೇಶ್ವರನಿಗೆ ಪ್ರಾಮಾಣಿಕನಾಗಿ ನಡೆದುಕೊಂಡನು.",
			"15": "<sup>15</sup> ಇದಲ್ಲದೆ, ತನ್ನ ತಂದೆಯೂ ತಾನೂ ಪ್ರತಿಷ್ಠಿಸಿದ್ದ ಬೆಳ್ಳಿಬಂಗಾರವನ್ನೂ ಪಾತ್ರೆಗಳನ್ನೂ ಸರ್ವೇಶ್ವರನ ಆಲಯದಲ್ಲಿ ತಂದಿಟ್ಟನು.",
			"16": "<sup>16</sup> ಇವನಿಗೂ ಇಸ್ರಯೇಲರ ಅರಸ ಬಾಷನಿಗೂ ಯಾವಾಗಲೂ ಯುದ್ಧ ನಡೆಯುತ್ತಿತ್ತು.",
			"17": "<sup>17</sup> ಇಸ್ರಯೇಲರ ಅರಸ ಬಾಷನು ಯೆಹೂದ್ಯರಿಗೆ ವಿರುದ್ಧ ಯುದ್ಧಮಾಡಿ ಯಾರೂ ಯೆಹೂದ್ಯರ ಅರಸ ಆಸನ ಬಳಿಗೆ ಹೋಗಿ ಬರುವುದಕ್ಕಾಗದಂತೆ ‘ರಾಮ್’ ಎಂಬ ಕೋಟೆಯನ್ನು ಕಟ್ಟಿಸಿದನು.",
			"18": "<sup>18</sup> ಆಗ ಆಸನು ಸರ್ವೇಶ್ವರನ ಆಲಯದ ಮತ್ತು ಅರಮನೆಯ ಭಂಡಾರಗಳಲ್ಲಿ ಉಳಿದಿದ್ದ ಬೆಳ್ಳಿಬಂಗಾರವನ್ನು ತೆಗೆದುಕೊಂಡು ಅದನ್ನು ದೂತರ ಮುಖಾಂತರ ದಮಸ್ಕದಲ್ಲಿದ್ದ ಸಿರಿಯಾದವರ ಅರಸನೂ ಹೆಜ್ಯೋನನ ಮೊಮ್ಮಗನೂ ಟಬ್ರಿಮ್ಮೋನನ ಮಗನೂ ಆದ ಬೆನ್ಹದದನಿಗೆ ಕೊಟ್ಟು ಕಳುಹಿಸಿದನು.",
			"19": "<sup>19</sup> ಅಲ್ಲದೆ ಅವನಿಗೆ, “ನನಗೂ ನಿನಗೂ, ನನ್ನ ತಂದೆಗೂ ನಿನ್ನ ತಂದೆಗೂ ಒಂದು ಒಪ್ಪಂದವಿದೆಯಲ್ಲವೆ? ಇಗೋ, ನಿನಗೆ ಬೆಳ್ಳಿಬಂಗಾರವನ್ನು ಕಾಣಿಕೆಯಾಗಿ ಕಳುಹಿಸಿದ್ದೇನೆ. ಇಸ್ರಯೇಲರ ಅರಸ ಬಾಷನು ನನ್ನ ನಾಡಿನಿಂದ ಸೈನ್ಯವನ್ನು ಹಿಂದಕ್ಕೆ ತೆಗೆದುಕೊಂಡು ನನ್ನನ್ನು ಬಿಟ್ಟುಹೋಗುವಂತೆ, ಅವನ ಸಂಗಡ ನೀನು ಮಾಡಿಕೊಂಡಿರುವ ಒಪ್ಪಂದವನ್ನು ಮುರಿದುಬಿಡು,” ಎಂದು ಹೇಳಿಸಿದನು.",
			"20": "<sup>20</sup> ಬೆನ್ಹದದನು ಅರಸ ಆಸನ ಮಾತನ್ನು ಕೇಳಿ, ತನ್ನ ಸೈನ್ಯಾಧಿಪತಿಗಳನ್ನು ಯುದ್ಧಕ್ಕಾಗಿ ಇಸ್ರಯೇಲರ ಪಟ್ಟಣಗಳಿಗೆ ಕಳುಹಿಸಿದನು. ಇವರು ಇಯ್ಯೋನ್, ದಾನ್, ಅಬೇಲ್ಬೇತ್ಮಾಕಾ, ಕಿನ್ನೆರೋತ್ ಪ್ರದೇಶ, ನಫ್ತಾಲ್ಯರ ಎಲ್ಲಾ ಪ್ರಾಂತ್ಯ ಇವುಗಳನ್ನು ಹಾಳುಮಾಡಿದರು.",
			"21": "<sup>21</sup> ಬಾಷನು ಈ ಸುದ್ದಿಯನ್ನು ಕೇಳಿದಾಗ ರಾಮಕೋಟೆಯನ್ನು ಕಟ್ಟಿಸುವುದನ್ನು ಬಿಟ್ಟು ತಿರ್ಚಕ್ಕೆ ಹೋಗಿ ಅಲ್ಲೇ ವಾಸಿಸಿದನು.",
			"22": "<sup>22</sup> ಅನಂತರ ಅರಸನಾದ ಆಸನು ಯೆಹೂದ್ಯರಲ್ಲಿ ಒಬ್ಬನನ್ನೂ ಬಿಡದೆ ಎಲ್ಲರನ್ನೂ ಕರೆಸಿ ಬಾಷನು ರಾಮಕೋಟೆಗಾಗಿ ತರಿಸಿದ್ದ ಕಲ್ಲು ಮರಗಳನ್ನು ಇವರ ಮುಖಾಂತರ ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ಬೆನ್ಯಾಮೀನ್ಯರ ಗೆಬ, ಮಿಚ್ಪೆ ಎಂಬ ಪಟ್ಟಣಗಳನ್ನು ಭದ್ರಪಡಿಸಿದನು.",
			"23": "<sup>23</sup> ಆಸನ ಉಳಿದ ಚರಿತ್ರೆ, ಅವನ ಪರಾಕ್ರಮಕೃತ್ಯಗಳು, ಪಟ್ಟಣಗಳನ್ನು ಕಟ್ಟಿಸಿದ ಸಂಗತಿ ಹಾಗು ಅವನ ಇತರ ಕಾರ್ಯಕಲಾಪಗಳು ಜುದೇಯ ರಾಜರ ಇತಿಹಾಸ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ. ಮುಪ್ಪಿನಲ್ಲಿ ಅವನ ಕಾಲುಗಳಿಗೆ ರೋಗ ತಗಲಿತು.",
			"24": "<sup>24</sup> ಅವನು ಮೃತನಾಗಿ ತನ್ನ ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಿದನು. ಅವನ ಶವವನ್ನು ಅವನ ಪೂರ್ವಜ ದಾವೀದನ ಪಟ್ಟಣದೊಳಗೆ ಕುಟುಂಬ ಸ್ಮಶಾನಭೂಮಿಯಲ್ಲಿ ಸಮಾಧಿ ಮಾಡಿದರು. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗ ಯೆಹೋಷಾಫಾಟನು ಅರಸನಾದನು.",
			"25": "<sup>25</sup> ಯೆಹೂದ್ಯರ ಅರಸ ಆಸನ ಆಳ್ವಿಕೆಯ ಎರಡನೆಯ ವರ್ಷದಲ್ಲಿ ಯಾರೊಬ್ಬಾಮನ ಮಗ ನಾದಾಬನು ಇಸ್ರಯೇಲರ ಅರಸನಾಗಿ ಎರಡು ವರ್ಷ ಆಳಿದನು.",
			"26": "<sup>26</sup> ಇವನು ಇಸ್ರಯೇಲರನ್ನು ಪಾಪಕ್ಕೆ ಪ್ರೇರಿಸಿದ ತನ್ನ ತಂದೆಯ ಮಾರ್ಗದಲ್ಲಿ ನಡೆದು ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾದನು.",
			"27": "<sup>27</sup> ಇವನು ಇಸ್ರಯೇಲರೊಡನೆ ಫಿಲಿಷ್ಟಿಯರ ವಶದಲ್ಲಿದ್ದ ಗಿಬ್ಬೆತೋನ್ ಎಂಬ ಪಟ್ಟಣಕ್ಕೆ ಮುತ್ತಿಗೆ ಹಾಕಿದಾಗ ಇಸ್ಸಾಕಾರನ ಕುಲದವನೂ ಅಹೀಯನ ಮಗನೂ ಆದ ಬಾಷನೆಂಬವನು ಇವನಿಗೆ ವಿರುದ್ಧ ಒಳಸಂಚು ಮಾಡಿ ಇವನನ್ನು ಕೊಂದನು.",
			"28": "<sup>28</sup> ಬಾಷನು ಜುದೇಯದ ಅರಸನಾದ ಆಸನ ಆಳ್ವಿಕೆಯ ಮೂರನೆಯ ವರ್ಷದಲ್ಲಿ ಇವನನ್ನು ಕೊಂದು ಇವನಿಗೆ ಬದಲಾಗಿ ತಾನೇ ಅರಸನಾದನು.",
			"29": "<sup>29</sup> ಅರಸನಾದ ಕೂಡಲೆ, ಯಾರೊಬ್ಬಾಮನ ಮನೆಯವರಲ್ಲಿ ಒಬ್ಬನನ್ನೂ ಜೀವದಿಂದುಳಿಸದೆ ಎಲ್ಲರನ್ನೂ ಸಂಹರಿಸಿದನು. ಹೀಗೆ ಸರ್ವೇಶ್ವರ ಶಿಲೋವಿನ ಪ್ರವಾದಿ ಅಹೀಯನ ಮುಖಾಂತರ ಹೇಳಿಸಿದ ಮಾತು ನೆರವೇರಿತು.",
			"30": "<sup>30</sup> ಯಾರೊಬ್ಬಾಮನು ತಾನು ಪಾಪಮಾಡಿದ್ದಲ್ಲದೆ ಇಸ್ರಯೇಲರನ್ನೂ ಪಾಪಕ್ಕೆ ಪ್ರೇರಿಸಿ ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರನನ್ನು ರೇಗಿಸಿದ್ದರಿಂದ ಹೀಗಾಯಿತು.",
			"31": "<sup>31</sup> ನಾದಾಬನ ಕಾರ್ಯಕಲಾಪಗಳು ಇಸ್ರಯೇಲ್ ರಾಜರ ಇತಿಹಾಸ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"32": "<sup>32</sup> ಆಸನಿಗೂ ಇಸ್ರಯೇಲರ ಅರಸನಾದ ಬಾಷನಿಗೂ ಯಾವಾಗಲೂ ಯುದ್ಧ ನಡೆಯುತ್ತಿತ್ತು.",
			"33": "<sup>33</sup> ಜುದೇಯದ ಅರಸ ಆಸನ ಆಳ್ವಿಕೆಯ ಮೂರನೆಯ ವರ್ಷದಲ್ಲಿ ಅಹೀಯನ ಮಗ ಬಾಷನು ಇಸ್ರಯೇಲರ ಅರಸನಾಗಿ ತಿರ್ಚದಲ್ಲಿ ಇದ್ದುಕೊಂಡು ಇಪ್ಪತ್ತನಾಲ್ಕು ವರ್ಷ ಆಳಿದನು.",
			"34": "<sup>34</sup> ಇವನು ಇಸ್ರಯೇಲರನ್ನು ಪಾಪಕ್ಕೆ ಪ್ರಚೋದಿಸಿದ ಯಾರೊಬ್ಬಾಮನ ಮಾರ್ಗದಲ್ಲಿ ನಡೆದು ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾದನು."
		},
		"16": {
			"1": "<sup>1</sup> ಆಗ ಸರ್ವೇಶ್ವರಸ್ವಾಮಿ ಹನಾನೀಯನ ಮಗ ಯೇಹುವಿನ ಮುಖಾಂತರ ಬಾಷನಿಗೆ ಹೀಗೆ ಹೇಳಿಸಿದರು:",
			"2": "<sup>2</sup> “ನಾನು ನಿನ್ನನ್ನು ಧೂಳಿನಿಂದ ಎತ್ತಿ ನನ್ನ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಯೇಲರ ನಾಯಕನನ್ನಾಗಿ ಮಾಡಿದೆ. ಆದರೂ ನೀನು ಯಾರೊಬ್ಬಾಮನ ಮಾರ್ಗದಲ್ಲೇ ನಡೆದು, ನನ್ನ ಜನರಾದ ಇಸ್ರಯೇಲರನ್ನು ಪಾಪಕ್ಕೆ ಪ್ರೇರಿಸಿದೆ. ಅವರ ಪಾಪದ ಮೂಲಕ ನನಗೆ ಕೋಪವನ್ನೆಬ್ಬಿಸಿದೆ.",
			"3": "<sup>3</sup> ಆದ್ದರಿಂದ ನಿನ್ನನ್ನೂ ನಿನ್ನ ಮನೆಯವರನ್ನೂ ಅಳಿಸಿಬಿಡುವೆನು; ನೆಬಾಟನ ಮಗ ಯಾರೊಬ್ಬಾಮನ ಮನೆಗಾದ ಗತಿ ನಿನ್ನ ಮನೆಗೂ ಆಗುವುದು.",
			"4": "<sup>4</sup> ನಿನ್ನ ಕುಟುಂಬದವರಲ್ಲಿ ಪಟ್ಟಣದೊಳಗೆ ಸಾಯುವವರನ್ನು ನಾಯಿಗಳು ಹಾಗು ಅಡವಿಯಲ್ಲಿ ಸಾಯುವವರನ್ನು ಪಕ್ಷಿಗಳು ತಿಂದುಬಿಡುವುವು\".",
			"5": "<sup>5</sup> ಬಾಷನ ಉಳಿದ ಚರಿತ್ರೆ ಹಾಗು ಅವನ ಪರಾಕ್ರಮ ಸಾಧನೆಗಳು ಇಸ್ರಯೇಲ್ ರಾಜರ ಇತಿಹಾಸ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"6": "<sup>6</sup> ಅವನು ನಿಧನನಾಗಿ ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಲು ಅವನ ಶವವನ್ನು ತಿರ್ಚದಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಅವನ ಸ್ಥಾನದಲ್ಲಿ ಅವನ ಮಗ ಏಲನು ಅರಸನಾದನು.",
			"7": "<sup>7</sup> ಬಾಷನು ಯಾರೊಬ್ಬಾಮನ ಮನೆಯವರಂತೆ ತನ್ನ ದುಷ್ಕೃತ್ಯಗಳಿಂದ ಸರ್ವೇಶ್ವರನನ್ನು ರೇಗಿಸಿ ಅವರ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾದನು. ಆದ್ದರಿಂದಲೂ ಅವನು ಯಾರೊಬ್ಬಾಮನ ಮನೆಯವರನ್ನು ನಿರ್ನಾಮಗೊಳಿಸಿದ್ದರಿಂದಲೂ ಸರ್ವೇಶ್ವರನು ಹನಾನೀಯ ಮಗ ಯೇಹುವಿನ ಮುಖಾಂತರ ಅವನಿಗೂ ಅವನ ಕುಟುಂಬಕ್ಕೂ ಸಂಭವಿಸಲಿರುವ ದುರ್ಗತಿಯನ್ನು ಮುಂತಿಳಿಸಿದ್ದರು.",
			"8": "<sup>8</sup> ಯೆಹೂದ್ಯರ ಅರಸ ಆಸನ ಆಳ್ವಿಕೆಯ ಇಪ್ಪತ್ತಾರನೆಯ ವರ್ಷದಲ್ಲಿ ಬಾಷನ ಮಗ ಏಲನು ತಿರ್ಚದಲ್ಲಿ ಇಸ್ರಯೇಲರ ಅರಸನಾಗಿ ಎರಡು ವರ್ಷ ಆಳಿದನು.",
			"9": "<sup>9</sup> ಅವನ ಸೇವಕನೂ ಅವನ ರಥಬಲದ ಅರ್ಧಭಾಗಕ್ಕೆ ಅಧಿಪತಿಯೂ ಆಗಿದ್ದ ಜಿಮ್ರಿ ಎಂಬವನು ಅವನಿಗೆ ವಿರುದ್ಧ ಒಳಸಂಚು ಮಾಡಿದನು. ಒಮ್ಮೆ ಅರಸನು ತಿರ್ಚದಲ್ಲಿ ತನ್ನ ಮನೆಯ ಉಗ್ರಾಣಿಕನಾದ ಅರ್ಚನೆಂಬವನ ಮನೆಯಲ್ಲಿ ಕುಡಿದು ಮತ್ತನಾಗಿದ್ದನು.",
			"10": "<sup>10</sup> ಆಗ ಜಿಮ್ರಿ ಅಲ್ಲಿಗೆ ಹೋಗಿ ಅವನನ್ನು ಕೆಡವಿ ಕೊಂದುಹಾಕಿದನು. ಅವನಿಗೆ ಬದಲಾಗಿ ತಾನೇ ಅರಸನಾದನು. ಇದು ಜುದೇಯದ ಅರಸ ಆಸನ ಆಳ್ವಿಕೆಯ ಇಪ್ಪತ್ತೇಳನೆಯ ವರ್ಷದಲ್ಲಿ ನಡೆಯಿತು.",
			"11": "<sup>11</sup> ಇವನು ಸಿಂಹಾಸನದಲ್ಲಿ ಕುಳಿತು ಆಳತೊಡಗಿದ ಕೂಡಲೆ ಬಾಷನ ಮನೆಯವರನ್ನೂ ಅವನ ಬಂಧುಮಿತ್ರರನ್ನೂ ಉಳಿಸಲಿಲ್ಲ.",
			"12": "<sup>12</sup> ಬಾಷನೂ ಅವನ ಮಗ ಏಲನೂ ತಾವು ಪಾಪಮಾಡಿದ್ದಲ್ಲದೆ ಇಸ್ರಯೇಲರನ್ನೂ ಪಾಪಕ್ಕೆ ಪ್ರಚೋದಿಸಿದರು. ತಮ್ಮ ವಿಗ್ರಹಗಳಿಂದ ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರನನ್ನು ರೇಗಿಸಿದರು.",
			"13": "<sup>13</sup> ಈ ಕಾರಣ ಪ್ರವಾದಿ ಯೇಹುವಿನ ಮುಖಾಂತರ ಸರ್ವೇಶ್ವರ ಬಾಷನ ಮನೆಗೆ ದುರ್ಗತಿಯನ್ನು ಮುಂತಿಳಿಸಿದ್ದರು. ಜಿಮ್ರಿಯು ಬಾಷನ ಮನೆಯವರೆಲ್ಲರನ್ನೂ ನಿರ್ನಾಮಗೊಳಿಸಿದಾಗ ಆ ಮಾತು ನೆರವೇರಿತು.",
			"14": "<sup>14</sup> ಏಲನ ಉಳಿದ ಚರಿತ್ರೆ ಹಾಗು ಅವನ ಕಾರ್ಯಕಲಾಪಗಳು ಇಸ್ರಯೇಲ್ ರಾಜರ ಇತಿಹಾಸ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಲಿಖಿತವಾಗಿವೆ.",
			"15": "<sup>15</sup> ಯೆಹೂದ್ಯರ ಅರಸ ಆಸನ ಆಳ್ವಿಕೆಯ ಇಪ್ಪತ್ತೇಳನೆಯ ವರ್ಷದಲ್ಲಿ ಜಿಮ್ರಿ ಎಂಬವನು ತಿರ್ಚದಲ್ಲಿ ಇಸ್ರಯೇಲರ ಅರಸನಾಗಿ ಏಳು ದಿವಸ ಆಳಿದನು. ಆ ಸಮಯದಲ್ಲಿ ಇಸ್ರಯೇಲರು ಫಿಲಿಷ್ಟಿಯರ ವಶದಲ್ಲಿದ್ದ ಗಿಬ್ಬೆತೋನ್ ಎಂಬ ಪಟ್ಟಣಕ್ಕೆ ಮುತ್ತಿಗೆ ಹಾಕಿದ್ದರು.",
			"16": "<sup>16</sup> ಜಿಮ್ರಿ, ಅರಸನಿಗೆ ವಿರುದ್ಧ ಒಳಸಂಚುಮಾಡಿ ಅವನನ್ನು ಕೊಂದುಹಾಕಿದನೆಂಬ ಸುದ್ದಿ ಪಾಳೆಯದಲ್ಲಿದ್ದ ಇಸ್ರಯೇಲರಿಗೆ ಮುಟ್ಟಿತು. ಅವರು ಅದೇ ದಿವಸ, ಅಲ್ಲಿಯೇ, ಸೈನ್ಯಾಧಿಪತಿಯಾದ ಒಮ್ರಿಯನ್ನು ತಮ್ಮ ಅರಸನನ್ನಾಗಿ ನೇಮಿಸಿಕೊಂಡರು.",
			"17": "<sup>17</sup> ಇವನೂ ಇವನ ಜೊತೆಯಲ್ಲಿದ್ದ ಇಸ್ರಯೇಲರೆಲ್ಲರೂ ಗಿಬ್ಬೆತೋನನ್ನು ಬಿಟ್ಟುಹೋಗಿ ತಿರ್ಚಕ್ಕೆ ಮುತ್ತಿಗೆ ಹಾಕಿದರು.",
			"18": "<sup>18</sup> ಪಟ್ಟಣ ಅವರ ವಶವಾದದ್ದನ್ನು ಜಿಮ್ರಿ ಕಂಡು, ಅರಮನೆಯ ಗರ್ಭಗೃಹವನ್ನು ಹೊಕ್ಕು ಅರಮನೆಗೆ ಬೆಂಕಿಹೊತ್ತಿಸಿ ಸುಟ್ಟುಕೊಂಡು ಸತ್ತನು.",
			"19": "<sup>19</sup> ಅವನು ಯಾರೊಬ್ಬಾಮನಂತೆ ತಾನು ಪಾಪಮಾಡಿದ್ದಲ್ಲದೆ ಇಸ್ರಯೇಲರ ಪಾಪಕ್ಕೆ ಕಾರಣವಾಗಿದ್ದನು. ಹೀಗೆ ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾಗಿದ್ದುದರಿಂದ ಈ ದುರ್ಗತಿ ಸಂಭವಿಸಿತು.",
			"20": "<sup>20</sup> ಜಿಮ್ರಿಯ ಉಳಿದ ಚರಿತ್ರೆ ಹಾಗು ಅವನ ಒಳಸಂಚು ಇಸ್ರಯೇಲ್ ರಾಜರ ಇತಿಹಾಸ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಲಿಖಿತವಾಗಿವೆ.",
			"21": "<sup>21</sup> ಆ ಕಾಲದಲ್ಲಿ ಇಸ್ರಯೇಲರಲ್ಲಿ ಎರಡು ಪಂಗಡಗಳಿದ್ದವು; ಒಂದು ಪಂಗಡದವರು ಗೀನತನ ಮಗನಾದ ತಿಬ್ನಿಯನ್ನು ಹಿಂಬಾಲಿಸಿ ಅರಸನನ್ನಾಗಿ ಮಾಡಿಕೊಳ್ಳಬೇಕೆಂದಿದ್ದರು; ಇನ್ನೊಂದು ಪಂಗಡದವರು ಒಮ್ರಿಯನ್ನು ಹಿಂಬಾಲಿಸಿದರು.",
			"22": "<sup>22</sup> ಆದರೆ ಒಮ್ರಿಯ ಪಂಗಡದವರು ಗೀನತನ ಮಗ ತಿಬ್ನಿಯ ಪಂಗಡದವರನ್ನು ಸೋಲಿಸಿದರು. ತಿಬ್ನಿಯು ಸತ್ತುಹೋದನು; ಒಮ್ರಿಯೇ ಆಳತೊಡಗಿದನು.",
			"23": "<sup>23</sup> ಯೆಹೂದ್ಯರ ಅರಸ ಆಸನ ಆಳ್ವಿಕೆಯ ಮೂವತ್ತೊಂದನೆಯ ವರ್ಷದಲ್ಲಿ ಒಮ್ರಿ ಇಸ್ರಯೇಲರ ಅರಸನಾಗಿ ಹನ್ನೆರಡು ವರ್ಷ ಆಳಿದನು. ಆರು ವರ್ಷಗಳವರೆಗೆ ತಿರ್ಚವೇ ಅವನ ರಾಜಧಾನಿಯಾಗಿತ್ತು.",
			"24": "<sup>24</sup> ಅನಂತರ ಶೆಮೆರ್ ಎಂಬವನಿಗೆ ಆರು ಸಾವಿರ ಬೆಳ್ಳಿನಾಣ್ಯಗಳನ್ನು ಕೊಟ್ಟು ಅವನಿಂದ ಸಮಾರಿಯ ಎಂಬ ಗುಡ್ಡವನ್ನು ಕೊಂಡುಕೊಂಡನು. ಅದರ ಮೇಲೆ ಒಂದು ಪಟ್ಟಣವನ್ನು ಕಟ್ಟಿಸಿ ಅದಕ್ಕೆ ಆ ಭೂಮಿಯ ಒಡೆಯನಾಗಿದ್ದ ಶೆಮೆರಿನ ಜ್ಞಾಪಕಾರ್ಥವಾಗಿ ಸಮಾರಿಯ ಎಂಬ ಹೆಸರಿಟ್ಟನು.",
			"25": "<sup>25</sup> ಇವನು ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿ ಹಾಗು ತನ್ನ ಪೂರ್ವಾಧಿಕಾರಿಗಳೆಲ್ಲರಿಗಿಂತಲೂ ದುಷ್ಟನು ಆಗಿದ್ದನು.",
			"26": "<sup>26</sup> ಇಸ್ರಯೇಲರು ವಿಗ್ರಹಗಳಿಂದ ತಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನನ್ನು ರೇಗಿಸಿದಂತೆ ಅವರನ್ನು ಪಾಪಕ್ಕೆ ಪ್ರಚೋದಿಸಿದ ನೆಬಾಟನ ಮಗ ಯಾರೊಬ್ಬಾಮನ ಮಾರ್ಗದಲ್ಲೇ ಇವನೂ ನಡೆದನು.",
			"27": "<sup>27</sup> ಇವನ ಉಳಿದ ಚರಿತ್ರೆ, ಪರಾಕ್ರಮ ಹಾಗು ಕಾರ್ಯಕಲಾಪಗಳು ಇಸ್ರಯೇಲ್ ರಾಜರ ಇತಿಹಾಸ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಲಿಖಿತವಾಗಿವೆ.",
			"28": "<sup>28</sup> ಒಮ್ರಿ ಸತ್ತು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಿದಾಗ ಇವನ ಶವವನ್ನು ಸಮಾರಿಯ ಪಟ್ಟಣದಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಇವನ ಸ್ಥಾನದಲ್ಲಿ ಇವನ ಮಗ ಅಹಾಬನು ಅರಸನಾದನು.",
			"29": "<sup>29</sup> ಯೆಹೂದ್ಯರ ಅರಸ ಆಸನ ಆಳ್ವಿಕೆಯ ಮೂವತ್ತೆಂಟನೆಯ ವರ್ಷದಲ್ಲಿ ಒಮ್ರಿಯ ಮಗ ಅಹಾಬನು ಇಸ್ರಯೇಲರ ಅರಸನಾದನು; ಸಮಾರಿಯ ಪಟ್ಟಣದಲ್ಲಿ ಇಪ್ಪತ್ತೆರಡು ವರ್ಷ ಆಳಿದನು.",
			"30": "<sup>30</sup> ಇವನು ತನ್ನ ಹಿಂದಿನ ಅಧಿಕಾರಿಗಳೆಲ್ಲರಿಗಿಂತಲೂ ದುಷ್ಟನು. ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿ.",
			"31": "<sup>31</sup> ಇವನು ನೆಬಾಟನ ಮಗ ಯಾರೊಬ್ಬಾಮನ ದುರ್ಮಾರ್ಗದಲ್ಲಿ ನಡೆದದ್ದು ಸಾಲದೆಂದು, ಸಿದೋನ್ಯರ ಅರಸ ಎತ್ಬಾಳನ ಮಗಳು ಈಜೆಬೆಲ್ ಎಂಬಾಕೆಯನ್ನು ಮದುವೆ ಮಾಡಿಕೊಂಡು ಬಾಳ್ ದೇವರನ್ನು ಪೂಜಿಸತೊಡಗಿದನು.",
			"32": "<sup>32</sup> ಬಾಳ್ ದೇವರಿಗಾಗಿ ಸಮಾರಿಯದಲ್ಲಿ ಒಂದು ಗುಡಿ ಕಟ್ಟಿಸಿ, ಅದರಲ್ಲಿ ಬಲಿಪೀಠವನ್ನು ಕಟ್ಟಿಸಿದನು;",
			"33": "<sup>33</sup> ಇದಲ್ಲದೆ, ಅಶೇರ ವಿಗ್ರಹಸ್ತಂಭವನ್ನು ನಿಲ್ಲಿಸಿದನು; ಹೀಗೆ ಇನ್ನು ಎಷ್ಟೋ ಅಧರ್ಮ ಕೆಲಸಗಳನ್ನು ಮಾಡಿ, ಮುಂದೆಯಿದ್ದ ಎಲ್ಲ ಇಸ್ರಯೇಲ್ ರಾಜರಿಗಿಂತಲೂ ಹೆಚ್ಚಾಗಿ ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರನನ್ನು ರೇಗಿಸಿದನು.",
			"34": "<sup>34</sup> ಇವನ ಕಾಲದಲ್ಲೆ ಬೇತೇಲಿನವನಾದ ಹೀಯೇಲನು ಜೆರಿಕೋಪಟ್ಟಣವನ್ನು ಕಟ್ಟಿಸಿದನು. ಅವನು ಅದಕ್ಕೆ ಅಸ್ತಿವಾರ ಹಾಕುವಾಗ ಹಿರಿಯ ಮಗನಾದ ಅಬೀರಾಮನನ್ನು ಹಾಗು ಅದಕ್ಕೆ ಬಾಗಿಲುಗಳನ್ನಿಡುವಾಗ ಕಿರಿಯ ಮಗನಾದ ಸೆಗೂಬನನ್ನೂ ಕಳೆದುಕೊಂಡನು. ಹೀಗೆ ಸರ್ವೇಶ್ವರ ನೂನನ ಮಗ ಯೆಹೋಶುವನಿಂದ ಹೇಳಿಸಿದ ಮಾತು ನೆರವೇರಿತು."
		},
		"17": {
			"1": "<sup>1</sup> ಗಿಲ್ಯಾದಿನ ಪ್ರವಾಸಿಗಳಲ್ಲಿ ತಿಷ್ಬೀಯನಾದ ಎಲೀಯ ಎಂಬವನು ಅಹಾಬನಿಗೆ, “ನಾನು ಸನ್ನಿಧಿಸೇವೆ ಮಾಡುತ್ತಿರುವ ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಾಣೆ, ನಾನು ಸೂಚಿಸಿದ ಹೊರತು, ಇಂದಿನಿಂದ ಕೆಲವು ವರ್ಷಗಳವರೆಗೆ ಮಳೆಯಾಗಲಿ, ಮಂಜಾಗಲಿ ಬೀಳುವುದಿಲ್ಲ,” ಎಂದನು.",
			"2": "<sup>2</sup> ಸರ್ವೇಶ್ವರಸ್ವಾಮಿ ಎಲೀಯನಿಗೆ, “ನೀನು ಈ ಸ್ಥಳವನ್ನು ಬಿಟ್ಟು ಪೂರ್ವದಿಕ್ಕಿಗೆ ಹೋಗು; ಜೋರ್ಡನ್ ನದಿಯ ಆಚೆಯಿರುವ ಕೆರೀತ್ ಹಳ್ಳದಲ್ಲಿ ಅಡಗಿಕೋ.",
			"4": "<sup>4</sup> ಆ ಹಳ್ಳದ ನೀರು ನಿನಗೆ ಪಾನವಾಗಿರುವುದು; ನಿನಗೆ ಆಹಾರ ತಂದುಕೊಡಬೇಕೆಂದು ಕಾಗೆಗಳಿಗೆ ಆಜ್ಞಾಪಿಸಿದ್ದೇನೆ,” ಎಂದು ಹೇಳಿದರು.",
			"5": "<sup>5</sup> ಸರ್ವೇಶ್ವರನ ಅಪ್ಪಣೆಯಂತೆ ಜೋರ್ಡನಿನ ಪೂರ್ವಕ್ಕಿರುವ ಕೆರೀತ್ ಹಳ್ಳದಲ್ಲಿ ಅವನು ವಾಸಿಸಿದನು.",
			"6": "<sup>6</sup> ಕಾಗೆಗಳು ಅವನಿಗೆ ಬೆಳಿಗ್ಗೆ ಹಾಗು ಸಂಜೆ ರೊಟ್ಟಿ, ಮಾಂಸ, ಇವುಗಳನ್ನು ತಿನ್ನಲು ತಂದುಕೊಡುತ್ತಿದ್ದವು. ಹಳ್ಳದ ನೀರು ಅವನಿಗೆ ಪಾನವಾಗಿತ್ತು.",
			"7": "<sup>7</sup> ಕೆಲವು ದಿವಸಗಳಾದ ನಂತರ ನಾಡಿನಲ್ಲಿ ಮಳೆಯಿಲ್ಲದ್ದರಿಂದ ಹಳ್ಳವು ಬತ್ತಿಹೋಯಿತು.",
			"8": "<sup>8</sup> ಆಗ ಸರ್ವೇಶ್ವರ ಅವನಿಗೆ: “ನೀನು ಇಲ್ಲಿಂದ ಸಿದೋನ್ಯರ ಸರೆಪ್ತಾ ಊರಿಗೆ ಹೊರಟುಹೋಗಿ ಅಲ್ಲಿ ವಾಸಿಸು. ನಿನ್ನನ್ನು ಪೋಷಿಸಬೇಕೆಂದು ಅಲ್ಲಿನ ಒಬ್ಬ ವಿಧವೆಗೆ ಆಜ್ಞಾಪಿಸಿದ್ದೇನೆ,” ಎಂದರು.",
			"10": "<sup>10</sup> ಅವನು ಅಲ್ಲಿಂದ ಹೊರಟು ಸರೆಪ್ತಾದ ಊರು ಬಾಗಿಲಿನ ಸಮೀಪಕ್ಕೆ ಬಂದನು. ಸೌದೆ ಕೂಡಿಸುತ್ತಿದ್ದ ಒಬ್ಬ ವಿಧವೆಯನ್ನು ಕಂಡು ಆಕೆಯನ್ನು ಕರೆದು, “ದಯವಿಟ್ಟು ಕುಡಿಯುವುದಕ್ಕೆ ಒಂದು ತಂಬಿಗೆಯಲ್ಲಿ ನೀರು ತೆಗೆದುಕೊಂಡು ಬಾ,” ಎಂದು ಹೇಳಿದನು.",
			"11": "<sup>11</sup> ಆಕೆ ಹೋಗುತ್ತಿರುವಾಗ ಮತ್ತೆ ಆಕೆಯನ್ನು ಕರೆದು, “ನೀನು ಬರುವಾಗ ನನಗೆ ಒಂದು ತುಂಡು ರೊಟ್ಟಿಯನ್ನು ಸಹ ತೆಗೆದುಕೊಂಡು ಬಾ,” ಎಂದನು.",
			"12": "<sup>12</sup> ಆಕೆ, “ನಿನ್ನ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಾಣೆ, ನನ್ನ ಹತ್ತಿರ ರೊಟ್ಟಿಯಿಲ್ಲ. ಮಡಕೆಯಲ್ಲಿ ಒಂದು ಹಿಡಿ ಹಿಟ್ಟು, ಕುಡಿಕೆಯಲ್ಲಿ ಸ್ವಲ್ಪ ಎಣ್ಣೆ, ಇವುಗಳ ಹೊರತು ಬೇರೇನೂ ಇಲ್ಲ. ಈಗ ಸೌದೆ ಆಯ್ದುಕೊಂಡು ಹೋಗಿ ನನಗೂ ನನ್ನ ಮಗನಿಗೂ ರೊಟ್ಟಿ ಮಾಡುತ್ತೇನೆ. ಅದನ್ನು ತಿಂದ ಮೇಲೆ ನಾವು ಸಾಯಬೇಕೇ ಹೊರತು ಬೇರೆ ಗತಿಯಿಲ್ಲ,” ಎಂದು ಉತ್ತರ ಕೊಟ್ಟಳು.",
			"13": "<sup>13</sup> ಆಗ ಎಲೀಯನು, “ಹೆದರಬೇಡ, ನೀನು ಹೇಳಿದಂತೆ ಮಾಡು; ಆದರೆ ಮೊದಲು ಅದರಿಂದ ನನಗೆ ಒಂದು ಚಿಕ್ಕ ರೊಟ್ಟಿಯನ್ನು ಮಾಡಿಕೊಂಡು ಬಾ; ತರುವಾಯ ನಿನಗೂ ನಿನ್ನ ಮಗನಿಗೂ ಮಾಡಿಕೋ.",
			"14": "<sup>14</sup> ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನಿನಗೆ, “ನಾನು ನಾಡಿಗೆ ಮಳೆಯನ್ನು ಕಳುಹಿಸುವವರೆಗೆ ನಿನ್ನ ಮಡಕೆಯಲ್ಲಿರುವ ಹಿಟ್ಟು ತೀರುವುದಿಲ್ಲ, ಕುಡಿಕೆಯಲ್ಲಿರುವ ಎಣ್ಣೆ ಮುಗಿದುಹೋಗುವುದಿಲ್ಲ,” ಎಂದು ಹೇಳುತ್ತಾರೆ ಎಂದನು.",
			"15": "<sup>15</sup> ಆಕೆ ಹೋಗಿ ಅವನು ಹೇಳಿದಂತೆಯೇ ಮಾಡಿದಳು. ಆಕೆಯೂ ಆಕೆಯ ಮನೆಯವರೂ ಎಲೀಯನೂ ಅದನ್ನು ಅನೇಕ ದಿವಸಗಳವರೆಗೆ ಊಟಮಾಡಿದರು.",
			"16": "<sup>16</sup> ಸರ್ವೇಶ್ವರ ಎಲೀಯನ ಮುಖಾಂತರ ಹೇಳಿದಂತೆ ಮಡಕೆಯಲ್ಲಿದ್ದ ಹಿಟ್ಟು ತೀರಲಿಲ್ಲ; ಕುಡಿಕೆಯಲ್ಲಿದ್ದ ಎಣ್ಣೆ ಮುಗಿದು ಹೋಗಲಿಲ್ಲ.",
			"17": "<sup>17</sup> ಕೆಲವು ದಿನಗಳಾದ ನಂತರ ಆ ಮನೆಯಜಮಾನಿಯ ಮಗನು ಅಸ್ಪಸ್ಥನಾದನು; ಅವನ ರೋಗ ಹೆಚ್ಚುತ್ತಾ ಕೊನೆಗೆ ಉಸಿರಾಡುವುದು ನಿಂತುಹೋಯಿತು.",
			"18": "<sup>18</sup> ಆಗ ಆಕೆ ಎಲೀಯನಿಗೆ, “ದೈವಪುರುಷನೇ, ನನ್ನ ವಿರುದ್ಧ ನಿನಗೇನಿದೆ? ನೀನು ನನ್ನ ಪಾಪವನ್ನು ನೆನಪಿಗೆ ತಂದು ನನ್ನ ಮಗನನ್ನು ಸಾಯಿಸುವುದಕ್ಕೆ ಬಂದಿಯೋ?” ಎಂದಳು.",
			"19": "<sup>19</sup> ಆದರೆ ಎಲೀಯನು, “ನಿನ್ನ ಮಗನನ್ನು ನನಗೆ ಕೊಡು,” ಎಂದು ಹೇಳಿ ಅವನನ್ನು ಆಕೆಯ ಮಡಿಲಿನಿಂದ ತೆಗೆದುಕೊಂಡು, ತಾನು ವಾಸವಾಗಿದ್ದ ಮೇಲಿನ ಕೋಣೆಗೆ ಹೋಗಿ, ತನ್ನ ಮಂಚದ ಮೇಲೆ ಮಲಗಿಸಿದನು.",
			"20": "<sup>20</sup> ಅನಂತರ ಅವನು, “ನನ್ನ ದೇವರಾದ ಸರ್ವೇಶ್ವರಾ, ನನಗೆ ಆಶ್ರಯವಿತ್ತ ಈ ವಿಧವೆಯ ಮಗನನ್ನು ನೀವು ಸಾಯಿಸಿ ಆಕೆಗೂ ಕೇಡನ್ನುಂಟುಮಾಡಿದ್ದೇಕೆ?” ಎಂದು ಹೇಳಿ,",
			"21": "<sup>21</sup> ಹುಡುಗನ ಮೇಲೆ ಮೂರುಸಾರಿ ಬೋರಲು ಬಿದ್ದು, “ನನ್ನ ದೇವರಾದ ಸರ್ವೇಶ್ವರಾ, ಈ ಹುಡುಗನ ಪ್ರಾಣ ತಿರುಗಿ ಬರುವಂತೆ ಮಾಡಿ,” ಎಂದು ಮೊರೆಯಿಟ್ಟನು.",
			"22": "<sup>22</sup> ಸರ್ವೇಶ್ವರ ಅವನ ಪ್ರಾರ್ಥನೆಯನ್ನು ಕೇಳಿದರು. ಹುಡುಗನ ಪ್ರಾಣ ತಿರುಗಿಬಂದು ಅವನು ಉಜ್ಜೀವಿಸಿದನು.",
			"23": "<sup>23</sup> ಎಲೀಯನು ಆ ಹುಡುಗನನ್ನು ಅಲ್ಲಿಂದ ಕೆಳಗೆ ತೆಗೆದುಕೊಂಡುಹೋಗಿ ಅವನ ತಾಯಿಗೆ ಒಪ್ಪಿಸಿ, “ಇಗೋ, ನೋಡು; ನಿನ್ನ ಮಗ ಜೀವಂತನಾಗಿದ್ದಾನೆ,” ಎಂದನು.",
			"24": "<sup>24</sup> ಆಗ ಆಕೆ, “ನೀವು ದೈವಪುರುಷರೆಂದೂ ನಿಮ್ಮ ಬಾಯಿಂದ ಬಂದ ಸರ್ವೇಶ್ವರನ ಮಾತು ಸತ್ಯವೆಂದೂ ಈಗ ನನಗೆ ಗೊತ್ತಾಯಿತು,” ಎಂದು ಹೇಳಿದಳು."
		},
		"18": {
			"1": "<sup>1</sup> ಅನೇಕ ದಿವಸಗಳಾದನಂತರ ಎಲೀಯನಿಗೆ ಸರ್ವೇಶ್ವರನ ವಾಣಿ ಉಂಟಾಯಿತು. ಬರಗಾಲದ ಮೂರನೆಯವರ್ಷದಲ್ಲಿ ಸರ್ವೇಶ್ವರ ಅವನಿಗೆ, “ನೀನು ಹೋಗಿ ಅಹಾಬನನ್ನು ಕಾಣು; ನಾನು ನಾಡಿಗೆ ಮಳೆಕೊಡುತ್ತೇನೆ,” ಎಂದರು.",
			"2": "<sup>2</sup> ಎಲೀಯನು ಅಹಾಬನನ್ನು ನೋಡುವುದಕ್ಕೆ ಹೊರಟನು. ಆಗ ಸಮಾರಿಯದಲ್ಲಿ ಕ್ಷಾಮ ಬಹಳ ಭೀಕರವಾಗಿತ್ತು.",
			"3": "<sup>3</sup> ಆದುದರಿಂದ ಅಹಾಬನು ತನ್ನ ಉಗ್ರಾಣಿಕನಾದ ಓಬದ್ಯ ಎಂಬವನನ್ನು ಕರೆಯಿಸಿದನು.",
			"4": "<sup>4</sup> (ಈಜೆಬೆಲಳು ಸರ್ವೇಶ್ವರನ ಪ್ರವಾದಿಗಳನ್ನು ಸಂಹರಿಸುತ್ತಿದ್ದಾಗ ಈ ಓಬದ್ಯನು ಸರ್ವೇಶ್ವರನಲ್ಲಿ ಬಹು ಭಯಭಕ್ತಿಯುಳ್ಳವನಾಗಿ ನೂರು ಮಂದಿ ಪ್ರವಾದಿಗಳನ್ನು ಕರೆದುಕೊಂಡುಹೋಗಿ ಅವರನ್ನು ಐವತ್ತೈವತ್ತು ಮಂದಿಯನ್ನಾಗಿ, ಗವಿಗಳಲ್ಲಿ ಅಡಗಿಸಿಟ್ಟು, ಅವರಿಗೆ ಅನ್ನಪಾನಗಳನ್ನು ಕೊಟ್ಟು ಸಾಕುತ್ತಿದ್ದನು.)",
			"5": "<sup>5</sup> ಅಹಾಬನು ಅವನಿಗೆ, “ನಾವು ದೇಶಸಂಚಾರಮಾಡುತ್ತಾ ಎಲ್ಲಾ ಹಳ್ಳ ಬುಗ್ಗೆಗಳಿಗೆ ಹೋಗಿ ಅವುಗಳಲ್ಲಿ ಹುಲ್ಲು ಸಿಕ್ಕುತ್ತದೋ ನೋಡೋಣ; ಸಿಕ್ಕುವುದಾದರೆ ನಮ್ಮ ಕುದುರೆಗಳೂ ಹೇಸರಕತ್ತೆಗಳೂ ಉಳಿಯುವುವು; ಆಗ ಅವುಗಳನ್ನು ಕೊಲ್ಲುವ ಅವಶ್ಯಕತೆ ಇರುವುದಿಲ್ಲ,” ಎಂದು ಹೇಳಿ ಸಂಚಾರಕ್ಕಾಗಿ ನಾಡನ್ನು ಎರಡು ಭಾಗಮಾಡಿ, ಒಂದು ಭಾಗಕ್ಕೆ ಓಬದ್ಯನನ್ನು ಕಳುಹಿಸಿ, ಇನ್ನೊಂದು ಭಾಗಕ್ಕೆ ತಾನೇ ಹೊರಟುಹೋದನು.",
			"7": "<sup>7</sup> ಓಬದ್ಯನು ಪ್ರಯಾಣಮಾಡುತ್ತಿರುವಾಗ ಎಲೀಯನು ತಟ್ಟನೆ ಅವನಿಗೆ ಎದುರಾದನು. ಓಬದ್ಯನು ಅವನ ಗುರುತು ಹಿಡಿದು ಸಾಷ್ಟಾಂಗ ನಮಸ್ಕಾರ ಮಾಡಿದನು. “ತಾವು ನನ್ನ ಸ್ವಾಮಿ ಎಲೀಯರೋ?’ ಎಂದು ಕೇಳಿದನು.",
			"8": "<sup>8</sup> ಎಲೀಯನು, “ಹೌದು; ನೀನು ಹೋಗಿ ನಿನ್ನ ಒಡೆಯನಿಗೆ, ‘ಎಲೀಯನು ಬಂದಿದ್ದಾನೆಂದು’ ಹೇಳು,” ಎಂದನು.",
			"9": "<sup>9</sup> ಅದಕ್ಕೆ ಓಬದ್ಯನು, “ಅಹಾಬನು ನನ್ನನ್ನು ಕೊಲ್ಲುವ ಹಾಗೆ ತಾವು ನನ್ನನ್ನು ಅವನ ಕೈಗೆ ಒಪ್ಪಿಸುವುದೇಕೆ? ನಾನೇನು ಪಾಪಮಾಡಿದೆ?",
			"10": "<sup>10</sup> ತಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಾಣೆ, ನನ್ನ ಒಡೆಯ ಸೇವಕರನ್ನು ಅಟ್ಟಿ ನಿಮ್ಮನ್ನು ಹುಡುಕದ ಜನಾಂಗವಾಗಲಿ, ರಾಜ್ಯವಾಗಲಿ ಒಂದೂ ಇಲ್ಲ. ಆ ಜನಾಂಗ, ರಾಜ್ಯಗಳವರು, ‘ಎಲೀಯನು ನಮ್ಮಲ್ಲಿರುವುದಿಲ್ಲ,’ ಎಂದು ಹೇಳಿದಾಗ ಅವನು ಅವರಿಂದ ಪ್ರಮಾಣ ಮಾಡಿಸಿದನು.",
			"11": "<sup>11</sup> ಹೀಗಿದ್ದರೂ ತಾವು ನನಗೆ, “ಎಲೀಯನು ಬಂದಿದ್ದಾನೆಂದು ನಿನ್ನ ಒಡೆಯನಿಗೆ ತಿಳಿಸು,” ಎಂಬುದಾಗಿ ಆಜ್ಞಾಪಿಸಬಹುದೆ?",
			"12": "<sup>12</sup> ನಾನು ತಮ್ಮನ್ನು ಬಿಟ್ಟು ಹೊರಟ ಕೂಡಲೆ, ಸರ್ವೇಶ್ವರನ ಆತ್ಮ ತಮ್ಮನ್ನು ನನಗೆ ಗೊತ್ತಿಲ್ಲದ ಬೇರೊಂದು ಸ್ಥಳಕ್ಕೆ ಒಯ್ಯುವುದು. ನಾನು ಹೋಗಿ ಅಹಾಬನಿಗೆ ಹೇಳುವಷ್ಟರಲ್ಲಿ ತಾವು ಅವನಿಗೆ ಸಿಕ್ಕದೆಹೋದರೆ ಅವನು ನನ್ನನ್ನು ಕೊಂದುಹಾಕುವನು. ನಾನು ಚಿಕ್ಕಂದಿನಿಂದ ಸರ್ವೇಶ್ವರನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವನಲ್ಲವೇ?",
			"13": "<sup>13</sup> ಈಜೆಬೆಲಳು ಸರ್ವೇಶ್ವರನ ಪ್ರವಾದಿಗಳನ್ನು ಸಂಹರಿಸುತ್ತಿರುವಾಗ ನಾನು ಮಾಡಿದ್ದು ನನ್ನ ಸ್ವಾಮಿಯಾದ ತಮಗೆ ತಿಳಿಸಲಾಗಿದೆ ಅಲ್ಲವೆ? ಅವರಲ್ಲಿ ನೂರು ಮಂದಿಯನ್ನು ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ಐವತ್ತೈವತ್ತು ಮಂದಿಯನ್ನಾಗಿ ಗವಿಗಳಲ್ಲಿ ಅಡಗಿಸಿಟ್ಟು, ಅನ್ನಪಾನಗಳನ್ನು ಕೊಟ್ಟು ಸಾಕಿದೆನಲ್ಲವೇ?",
			"14": "<sup>14</sup> ಹೀಗಿದ್ದರೂ ತಾವು ನನಗೆ, ‘ಎಲೀಯನು ಬಂದಿದ್ದಾನೆಂಬುದಾಗಿ’ ನಿನ್ನ ಒಡೆಯನಿಗೆ ತಿಳಿಸು,” ಎಂದು ಆಜ್ಞಾಪಿಸುತ್ತೀರಿ; ಅವನು ನನ್ನನ್ನು ಕೊಂದೇ ಕೊಲ್ಲುವನಲ್ಲವೇ?’ ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"15": "<sup>15</sup> ಆಗ ಎಲೀಯನು, “ನಾನು ಸನ್ನಿಧಿಸೇವೆಮಾಡುತ್ತಿರುವ ಸರ್ವಶಕ್ತನಾಣೆ, ಈ ದಿನ ನಾನು ಅಹಾಬನಿಗೆ ಹೇಗೂ ಕಾಣಿಸಿಕೊಳ್ಳಲೇಬೇಕು,” ಎಂದನು.",
			"16": "<sup>16</sup> ಓಬದ್ಯನು ಅಹಾಬನ ಬಳಿಗೆ ಹೋಗಿ ಅವನಿಗೆ ಈ ಮಾತನ್ನು ತಿಳಿಸಿದನು. ಅಹಾಬನು ಎಲೀಯನನ್ನು ಎದುರುಗೊಳ್ಳುವುದಕ್ಕಾಗಿ ಹೊರಟುಬಂದು,",
			"17": "<sup>17</sup> ಅವನನ್ನು ಕಂಡಕೂಡಲೆ, “ಇಸ್ರಯೇಲರಿಗೆ ಆಪತ್ತನ್ನು ಬರಮಾಡಿದವನೇ, ನೀನು ಬಂದಿರುವೆಯಾ?’ ಎಂದನು.",
			"18": "<sup>18</sup> ಅದಕ್ಕೆ ಎಲೀಯನು, “ಅವರಿಗೆ ಆಪತ್ತನ್ನು ಬರಮಾಡಿದವನು ನಾನಲ್ಲ; ಸರ್ವೇಶ್ವರನ ಆಜ್ಞೆಗಳನ್ನು ಉಲ್ಲಂಘಿಸಿ ಬಾಳನ ವಿಗ್ರಹಗಳನ್ನು ಪೂಜಿಸಿದ ನೀನೂ ನಿನ್ನ ಮನೆಯವರೂ ಅದಕ್ಕೆ ಕಾರಣರು.",
			"19": "<sup>19</sup> ನೀನು ಈಗ ಎಲ್ಲ ಇಸ್ರಯೇಲರನ್ನು, ಈಜೆಬೆಲಳಿಂದ ಪೋಷಣೆ ಹೊಂದುವ ಬಾಳನ ನಾನೂರೈವತ್ತುಮಂದಿ ಪ್ರವಾದಿಗಳನ್ನು ಹಾಗೂ ಅಶೇರದೇವತೆಯ ನಾನೂರೈವತ್ತುಮಂದಿ ಪ್ರವಾದಿಗಳನ್ನು ಕರ್ಮೆಲ್ ಬೆಟ್ಟಕ್ಕೆ ಕರೆಯಿಸು,” ಎಂದು ಹೇಳಿದನು.",
			"20": "<sup>20</sup> ಅಹಾಬನು ಇಸ್ರಯೇಲರನ್ನೂ ಎಲ್ಲಾ ಪ್ರವಾದಿಗಳನ್ನೂ ಅಲ್ಲಿಗೆ ಕರೆಯಿಸಿದನು.",
			"21": "<sup>21</sup> ಎಲೀಯನು ಜನರೆಲ್ಲರ ಬಳಿಗೆ ಹೋಗಿ, “ನೀವು ಎಷ್ಟರವರೆಗೆ ಎರಡು ಮನಸ್ಸುಳ್ಳವರಾಗಿರುವಿರಿ? ಸರ್ವೇಶ್ವರಸ್ವಾಮಿ ನಿಮಗೆ ದೇವರಾಗಿದ್ದರೆ ಅವರನ್ನೇ ಹಿಂಬಾಲಿಸಿರಿ; ಬಾಳನು ದೇವರಾಗಿದ್ದರೆ ಅವನನ್ನೇ ಹಿಂಬಾಲಿಸಿರಿ,” ಎಂದು ಹೇಳಿದನು. ಜನರು ಇದಕ್ಕೆ ಏನೂ ಉತ್ತರಕೊಡದೆ ಸುಮ್ಮನಿರುವುದನ್ನು ಕಂಡು, ಅವನು ಮತ್ತೆ ಅವರಿಗೆ,",
			"22": "<sup>22</sup> “ಸರ್ವೇಶ್ವರನ ಪ್ರವಾದಿಗಳಲ್ಲಿ ನಾನೊಬ್ಬನೇ ಉಳಿದಿದ್ದೇನೆ; ಬಾಳನ ಪ್ರವಾದಿಗಳಲ್ಲಿ ನಾನೂರೈವತ್ತುಮಂದಿ ಇದ್ದಾರೆ.",
			"23": "<sup>23</sup> ಅವರು ಎರಡು ಹೋರಿಗಳನ್ನು ನಮ್ಮ ಬಳಿಗೆ ತರಲಿ; ಅವುಗಳಲ್ಲೊಂದನ್ನು ಆರಿಸಿಕೊಂಡು, ಕಡಿದು ತುಂಡುಮಾಡಿ ಕಟ್ಟಿಗೆಯ ಮೇಲಿಡಲಿ; ಆದರೆ ಬೆಂಕಿ ಹೊತ್ತಿಸಬಾರದು. ನಾನೂ ಇನ್ನೊಂದು ಹೋರಿಯನ್ನು ಹಾಗೆಯೇ ಕಡಿದು ಬೆಂಕಿ ಹೊತ್ತಿಸದೆ ಕಟ್ಟಿಗೆಯ ಮೇಲಿಡುವೆನು.",
			"24": "<sup>24</sup> ನೀವು ನಿಮ್ಮ ದೇವರ ಹೆಸರು ಹೇಳಿ ಪ್ರಾರ್ಥಿಸಿರಿ. ಅನಂತರ ನಾನು ಸರ್ವೇಶ್ವರನ ಹೆಸರು ಹೇಳಿ ಪ್ರಾರ್ಥಿಸುವೆನು. ಅವರಿಬ್ಬರಲ್ಲಿ ಯಾರು ಆಲಿಸಿ ಬೆಂಕಿಯನ್ನು ಕಳುಹಿಸುವನೋ ಅವನೇ ದೇವರೆಂದು ನಿಶ್ಚಯಿಸೋಣ,” ಎಂದನು. ಜನರೆಲ್ಲರು, “ಸರಿ, ನೀನು ಹೇಳಿದಂತೆಯೇ ಆಗಲಿ,” ಎಂದು ಉತ್ತರಕೊಟ್ಟರು.",
			"25": "<sup>25</sup> ಆಗ ಎಲೀಯನು ಬಾಳನ ಪ್ರವಾದಿಗಳಿಗೆ, “ನೀವು ಹೆಚ್ಚುಮಂದಿ ಇರುವುದರಿಂದ ಮೊದಲು ನೀವೇ ಒಂದು ಹೋರಿಯನ್ನು ಆರಿಸಿಕೊಂಡು, ಅದನ್ನು ಸಿದ್ಧಪಡಿಸಿ, ನಿಮ್ಮ ದೇವರ ಹೆಸರು ಹೇಳಿ ಪ್ರಾರ್ಥಿಸಿರಿ; ಆದರೆ ಬೆಂಕಿಯನ್ನು ಹೊತ್ತಿಸಕೂಡದು,” ಎಂದನು.",
			"26": "<sup>26</sup> ಅಂತೆಯೇ ತರಲಾದ ಹೋರಿಗಳಲ್ಲಿ ಒಂದನ್ನು ಅವರು ತೆಗೆದುಕೊಂಡು ಸಿದ್ಧಪಡಿಸಿದರು. ತಮ್ಮ ದೇವರಾದ ಬಾಳನ ಹೆಸರು ಹೇಳಿ, “ಬಾಳನೇ, ನಮಗೆ ಕಿವಿಗೊಡು,” ಎಂದು ಹೊತ್ತಾರೆಯಿಂದ ಮಧ್ಯಾಹ್ನದವರೆಗೆ ಕೂಗಿದರು. ಆದರೆ ಯಾವ ವಾಣಿಯೂ ಕೇಳಿಸಲಿಲ್ಲ; ಅವರು ಪೀಠದ ಸುತ್ತಲೂ ಕುಣಿದಾಡಿದರು. ಆದರೂ ಯಾರೂ ಉತ್ತರಕೊಡಲಿಲ್ಲ.",
			"27": "<sup>27</sup> ಮಧ್ಯಾಹ್ನವಾದನಂತರ ಎಲೀಯನು ಅವರನ್ನು ಪರಿಹಾಸ್ಯ ಮಾಡಿದನು: “ಗಟ್ಟಿಯಾಗಿ ಕೂಗಿರಿ; ಅವನು ದೇವರಲ್ಲವೆ? ಈಗ ಒಂದು ವೇಳೆ ಅವನು ಧ್ಯಾನದಲ್ಲಿ ಇರಬಹುದು; ಇಲ್ಲವೇ, ಯಾವುದೋ ಕೆಲಸದಲ್ಲಿ ಅಥವಾ ಪ್ರಯಾಣದಲ್ಲಿ ಇರಬೇಕು; ಇಲ್ಲದಿದ್ದರೆ, ನಿದ್ರೆಮಾಡುತ್ತಿರಬೇಕು; ಎಚ್ಚರವಾಗಲಿ,” ಎಂದು ಹೇಳಿದನು.",
			"28": "<sup>28</sup> ಅವರು ಗಟ್ಟಿಯಾಗಿ ಕೂಗಿ ತಮ್ಮ ಪದ್ಧತಿಯ ಪ್ರಕಾರ ಈಟಿಕತ್ತಿಗಳಿಂದ ರಕ್ತಸೋರುವಷ್ಟು ಗಾಯಮಾಡಿಕೊಂಡರು.",
			"29": "<sup>29</sup> ಮಧ್ಯಾಹ್ನದಿಂದ ನೈವೇದ್ಯ ಸಮರ್ಪಣೆಯ ಹೊತ್ತಿನವರೆಗೂ ಪರವಶರಾಗಿ ಕೂಗುತ್ತಿದ್ದರು. ಆದರೂ ಯಾವ ವಾಣಿಯೂ ಕೇಳಿಸಲಿಲ್ಲ; ಯಾವನೂ ಅವರಿಗೆ ಉತ್ತರಕೊಡಲಿಲ್ಲ; ಅವರನ್ನು ಯಾವನೂ ಲಕ್ಷಿಸಲೂ ಇಲ್ಲ.",
			"30": "<sup>30</sup> ಅನಂತರ ಎಲೀಯನು ಜನರೆಲ್ಲರನ್ನು ಹತ್ತಿರಕ್ಕೆ ಕರೆದನು; ಅವರು ಬಂದರು. ಹಾಳಾಗಿದ್ದ ಅಲ್ಲಿನ ಸರ್ವೇಶ್ವರನ ಪೀಠವನ್ನು ದುರಸ್ತು ಮಾಡಿಸಿದನು.",
			"31": "<sup>31</sup> ಸರ್ವೇಶ್ವರನಿಂದ ‘ಇಸ್ರಯೇಲ್’ ಎಂಬ ಹೆಸರು ಹೊಂದಿದ ಯಕೋಬನ ಮಕ್ಕಳಿಂದ ಉತ್ಪತ್ತಿಯಾದ ಕುಲಗಳ ಸಂಖ್ಯೆಗೆ ಸರಿಯಾಗಿ ಹನ್ನೆರಡು ಕಲ್ಲುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು,",
			"32": "<sup>32</sup> ಸರ್ವೇಶ್ವರನ ಹೆಸರಿಗಾಗಿ ಒಂದು ಪೀಠವನ್ನು ಕಟ್ಟಿಸಿದನು. ಅದರ ಸುತ್ತಲೂ ಇಪ್ಪತ್ತು ಸೇರು ಬೀಜವರೀ ನೆಲವನ್ನು ಅಗಿಸಿ ಕಾಲುವೆ ಮಾಡಿಸಿದನು.",
			"33": "<sup>33</sup> ಕಟ್ಟಿಗೆಯನ್ನು ಪೀಠದ ಮೇಲೆ ಪೇರಿಸಿ, ಹೋರಿಯನ್ನು ವಧಿಸಿ ತುಂಡುಮಾಡಿ, ಅದರ ಮೇಲೆ ಇಟ್ಟನು. ಅನಂತರ ಜನರಿಗೆ, “ನಾಲ್ಕು ಕೊಡ ನೀರು ತಂದು ಬಲಿಮಾಂಸದ ಮೇಲೆಯೂ ಕಟ್ಟಿಗೆಯ ಮೇಲೆಯೂ ಹೊಯ್ಯಿರಿ,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"34": "<sup>34</sup> ಅವರು ಅಂತೆಯೇ ತೆಗೆದುಕೊಂಡು ಬಂದರು. “ಇನ್ನೊಂದು ಸಾರಿ ತನ್ನಿ,” ಎಂದು ಹೇಳಿದನು. ಅವರು ಇನ್ನೊಮ್ಮೆ ತಂದರು. ಅವನು ಮೂರನೆಯ ಸಾರಿ ಅದೇ ಪ್ರಕಾರ ಆಜ್ಞಾಪಿಸಲು ಅವರು ಮತ್ತೊಮ್ಮೆ ತಂದು ಸುರಿದರು.",
			"35": "<sup>35</sup> ನೀರು ವೇದಿಯ ಸುತ್ತಲೂ ಹರಿಯಿತು. ಇದಲ್ಲದೆ, ಅವನು ಕಾಲುವೆಯನ್ನೂ ನೀರಿನಿಂದ ತುಂಬಿಸಿದನು.",
			"36": "<sup>36</sup> ಸಂಧ್ಯಾನೈವೇದ್ಯದ ಹೊತ್ತಿಗೆ, ಪ್ರವಾದಿ ಎಲೀಯನು ಪೀಠದ ಹತ್ತಿರ ಬಂದು, “ಅಬ್ರಹಾಮ್, ಇಸಾಕ್, ಇಸ್ರಯೇಲರ ದೇವರೇ, ಸರ್ವೇಶ್ವರಾ, ನೀವೊಬ್ಬರೇ ಇಸ್ರಯೇಲರ ದೇವರಾಗಿರುತ್ತೀರಿ ಎಂಬುದನ್ನು, ನಾನು ನಿನ್ನ ದಾಸನಾಗಿರುತ್ತೇನೆಂಬುದನ್ನು ಹಾಗು ಇದನ್ನೆಲ್ಲಾ ನಿಮ್ಮ ಅಪ್ಪಣೆಯ ಮೇರೆಗೆ ಮಾಡಿದೆನೆಂಬುದನ್ನು ಈ ದಿನ ತೋರಿಸಿಕೊಡಿ.",
			"37": "<sup>37</sup> ನಮಗೆ ಕಿವಿಗೊಡಿ; ಸರ್ವೇಶ್ವರಾ ನಮಗೆ ಕಿವಿಗೊಡಿ; ಸರ್ವೇಶ್ವರನಾದ ನೀವೊಬ್ಬರೇ ದೇವರು ಹಾಗು ಈ ಜನರ ಮನಸ್ಸನ್ನು ನಿಮ್ಮಕಡೆಗೆ ತಿರುಗಿಸಿಕೊಳ್ಳುವವರು ಆಗಿರುತ್ತೀರಿ ಎಂಬುದನ್ನು ಇವರಿಗೆ ತಿಳಿಯಪಡಿಸಿ,” ಎಂದು ಪ್ರಾರ್ಥಿಸಿದನು.",
			"38": "<sup>38</sup> ಕೂಡಲೆ ಸರ್ವೇಶ್ವರನ ಕಡೆಯಿಂದ ಬೆಂಕಿ ಇಳಿದುಬಂದು ಬಲಿಮಾಂಸವನ್ನೂ ಕಟ್ಟಿಗೆ ಕಲ್ಲುಮಣ್ಣುಗಳನ್ನೂ ದಹಿಸಿಬಿಟ್ಟಿತು. ಕಾಲುವೆಯಲ್ಲಿದ್ದ ನೀರನ್ನೆಲ್ಲಾ ಹೀರಿಬಿಟ್ಟಿತು.",
			"39": "<sup>39</sup> ಜನರೆಲ್ಲರೂ ಅದನ್ನು ಕಂಡು ಬೋರಲಬಿದ್ದರು; “ಸರ್ವೇಶ್ವರನೇ ದೇವರು, ಸರ್ವೇಶ್ವರನೇ ದೇವರು,” ಎಂದು ಕೂಗಿದರು.",
			"40": "<sup>40</sup> ಆಗ ಎಲೀಯನು ಅವರಿಗೆ, “ಬಾಳನ ಪ್ರವಾದಿಗಳೆಲ್ಲರನ್ನು ಹಿಡಿಯಿರಿ; ಅವರಲ್ಲಿ ಒಬ್ಬನೂ ತಪ್ಪಿಸಿಕೊಳ್ಳಬಾರದು,” ಎಂದು ಆಜ್ಞಾಪಿಸಲು ಅವರು ಹಿಡಿದರು. ಅವನು ಅವರನ್ನು ಕೀಷೋನ್ ಹಳ್ಳಕ್ಕೆ ಒಯ್ದು ಕೊಲ್ಲಿಸಿದನು.",
			"41": "<sup>41</sup> ಅನಂತರ ಎಲೀಯನು ಆಹಾಬನಿಗೆ, “ನೀನು ಮೇಲೆ ಹೋಗಿ ಅನ್ನಪಾನಗಳನ್ನು ತೆಗೆದುಕೋ; ದೊಡ್ಡ ಮಳೆಯ ಶಬ್ದವು ಕೇಳಿಸುತ್ತದೆ,” ಎಂದನು.",
			"42": "<sup>42</sup> ಅಹಾಬನು ಅನ್ನಪಾನಗಳನ್ನು ತೆಗೆದುಕೊಳ್ಳಲು ಹೋದಾಗ, ಎಲೀಯನು ಕರ್ಮೆಲಿನ ತುದಿಗೆ ಹೋಗಿ, ಅಲ್ಲಿ ನೆಲದ ಮೇಲೆ ಬಿದ್ದುಕೊಂಡು, ಮೊಣಕಾಲಿನ ಮಧ್ಯೆ ತಲೆಯನ್ನಿಟ್ಟನು.",
			"43": "<sup>43</sup> ಅವನು ತನ್ನ ಸೇವಕನಿಗೆ, “ನೀನು ಮೇಲೆ ಹೋಗಿ ಸಮುದ್ರದ ಕಡೆಗೆ ನೋಡು,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು. ಸೇವಕನು ಹೋಗಿ, ನೋಡಿಬಂದು ಏನೂ ತೋರುವುದಿಲ್ಲವೆಂದು ಹೇಳಿದನು. ಹೀಗೆ ಅವನನ್ನು ಏಳುಸಾರಿ ಕಳುಹಿಸಿದನು.",
			"44": "<sup>44</sup> ಏಳನೆಯ ಸಾರಿ ಆ ಸೇವಕನು, “ಅಂಗೈಯಷ್ಟು ಚಿಕ್ಕದಾದ ಮೋಡವು ಸಮುದ್ರದಿಂದ ಏರಿ ಬರುತ್ತಲಿದೆ,” ಎಂದು ತಿಳಿಸಿದನು. ಆಗ ಎಲೀಯನು ಅವನಿಗೆ, “ನೀನು ಹೋಗಿ ಅಹಾಬನಿಗೆ, ‘ಬೇಗನೆ ರಥವನ್ನು ಹೂಡಿಸಿಕೊಂಡು, ಮಳೆಯು ನಿನ್ನನ್ನು ತಡೆಯದಂತೆ ಬೆಟ್ಟವನ್ನಿಳಿದು ಹೋಗು’ ಎಂದು ಹೇಳು,” ಎಂದನು.",
			"45": "<sup>45</sup> ತುಸು ಹೊತ್ತಿನಲ್ಲಿಯೇ ಆಕಾಶವು ಕಾರ್ಮೋಡಗಾಳಿಗಳಿಂದ ತುಂಬಿ ದೊಡ್ಡ ಮಳೆ ಪ್ರಾರಂಭವಾಯಿತು. ಅಹಾಬನು ರಥದಲ್ಲಿ ಕುಳಿತುಕೊಂಡು ಜೆಸ್ರೀಲಿಗೆ ಹೋದನು.",
			"46": "<sup>46</sup> ಸರ್ವೇಶ್ವರನ ಹಸ್ತ ಎಲೀಯನ ಸಂಗಡ ಇದ್ದುದರಿಂದ ಅವನು ನಡುಕಟ್ಟಿಕೊಂಡು ಅಹಾಬನ ಮುಂದೆ ಓಡುತ್ತಾ ಜೆಸ್ರೀಲನ್ನು ಸೇರಿದನು."
		},
		"19": {
			"1": "<sup>1</sup> ಎಲೀಯನು ಪ್ರವಾದಿಗಳನ್ನೆಲ್ಲ ಕತ್ತಿಯಿಂದ ಸಂಹರಿಸಿದ್ದನ್ನೂ ಅವನು ಮಾಡಿದ ಬೇರೆ ಎಲ್ಲಾ ಕಾರ್ಯಗಳನ್ನೂ ಅಹಾಬನು ಈಜೆಬೆಲಳಿಗೆ ತಿಳಿಸಿದನು.",
			"2": "<sup>2</sup> ಆಕೆ ಎಲೀಯನ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿ, “ನೀನು ಪ್ರವಾದಿಗಳ ಪ್ರಾಣವನ್ನು ತೆಗೆದಂತೆ ನಾಳೆ ಇಷ್ಟುಹೊತ್ತಿಗೆ ನಾನು ನಿನ್ನ ಪ್ರಾಣವನ್ನು ತೆಗೆಯದೆಹೋದರೆ, ದೇವತೆಗಳು ನನಗೆ ಬೇಕಾದದ್ದನ್ನು ಮಾಡಲಿ!” ಎಂದು ಹೇಳಿಕಳುಹಿಸಿದಳು.",
			"3": "<sup>3</sup> ಅವನು ಅದನ್ನು ಕೇಳಿದೊಡನೆ ಪ್ರಾಣರಕ್ಷಣೆಗಾಗಿ ಅಲ್ಲಿಂದ ಹೊರಟು ಜುದೇಯದ ಬೇರ್ಷೆಬಕ್ಕೆ ಬಂದು ಅಲ್ಲಿ ತನ್ನ ಸೇವಕನನ್ನು ಬೀಳ್ಕೊಟ್ಟನು.",
			"4": "<sup>4</sup> ತರುವಾಯ ತಾನೊಬ್ಬನೇ, ಮರುಭೂಮಿಯಲ್ಲಿ ಒಂದು ದಿವಸದ ಪ್ರಯಾಣದಷ್ಟು ದೂರಹೋಗಿ, ಒಂದು ಜಾಲೀಗಿಡದ ಕೆಳಗೆ ಕುಳಿತುಕೊಂಡು ಮರಣವನ್ನು ಅಪೇಕ್ಷಿಸಿದನು. “ಸರ್ವೇಶ್ವರಾ, ನನಗೆ ಸಾಕಾಯಿತು; ನನ್ನ ಪ್ರಾಣವನ್ನು ತೆಗೆದುಬಿಡಿ; ನನ್ನ ಪೂರ್ವಜರಿಗಿಂತ ನಾನು ಉತ್ತಮನಲ್ಲ,” ಎಂದು ದೇವರನ್ನು ಪ್ರಾರ್ಥಿಸಿದನು.",
			"5": "<sup>5</sup> ಬಳಿಕ ಅದೇ ಗಿಡದ ಕೆಳಗೆ ಮಲಗಿಕೊಂಡು ನಿದ್ರೆಮಾಡಿದನು. ಕೂಡಲೆ ಒಬ್ಬ ದೇವದೂತನು ಅವನನ್ನು ತಟ್ಟಿ, “ಎದ್ದು ಊಟಮಾಡು,” ಎಂದು ಹೇಳಿದನು.",
			"6": "<sup>6</sup> ಎಲೀಯನು ಎದ್ದು ಸುತ್ತಲೂ ನೋಡಿದನು. ಕೆಂಡದ ಮೇಲೆ ಸುಟ್ಟ ರೊಟ್ಟಿಯೂ ಒಂದು ತಂಬಿಗೆ ನೀರೂ ತನ್ನ ತಲೆಯ ಹತ್ತಿರ ಇದ್ದುವು. ಅವುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ತಿಂದು, ಕುಡಿದು ಪುನಃ ಮಲಗಿದನು.",
			"7": "<sup>7</sup> ಸರ್ವೇಶ್ವರನ ದೂತನು ಎರಡನೆಯ ಸಾರಿ ಬಂದು ಅವನನ್ನು ತಟ್ಟಿ, “ಎದ್ದು ಊಟಮಾಡು; ನೀನು ನಿನ್ನ ಶಕ್ತಿಮೀರುವಷ್ಟು ಪ್ರಯಾಣ ಮಾಡಬೇಕಾಗಿದೆ,” ಎಂದನು.",
			"8": "<sup>8</sup> ಅವನು ಎದ್ದು, ತಿಂದು, ಕುಡಿದು ಅದರ ಬಲದಿಂದ ನಾಲ್ವತ್ತು ದಿವಸ ಹಗಲಿರುಳು ಪ್ರಯಾಣ ಮಾಡಿದನು;",
			"9": "<sup>9</sup> ದೇವಗಿರಿಯಾದ ಹೋರೇಬನ್ನು ಮುಟ್ಟಿ ಅಲ್ಲಿನ ಒಂದು ಗವಿಯಲ್ಲಿ ಇಳಿದುಕೊಂಡನು. ಆಗ ಅವನಿಗೆ ಸರ್ವೇಶ್ವರನಿಂದ, “ಎಲೀಯನೇ, ಇಲ್ಲೇನು ಮಾಡುತ್ತಿರುವೆ?” ಎಂಬ ವಾಣಿ ಕೇಳಿಸಿತು.",
			"10": "<sup>10</sup> ಅದಕ್ಕೆ ಅವನು, “ಸರ್ವಶಕ್ತರಾದ ದೇವರೇ, ಸರ್ವೇಶ್ವರಾ, ಇಸ್ರಯೇಲರು ನಿಮ್ಮ ನಿಬಂಧನೆಯನ್ನು ಮೀರಿದ್ದಾರೆ; ಬಲಿಪೀಠಗಳನ್ನು ಕೆಡವಿಹಾಕಿದ್ದಾರೆ; ಪ್ರವಾದಿಗಳನ್ನು ಕತ್ತಿಯಿಂದ ಸಂಹರಿಸಿದ್ದಾರೆ; ನಾನೊಬ್ಬನೇ ಉಳಿದು ನಿಮ್ಮ ಗೌರವವನ್ನು ಕಾಪಾಡುವುದರಲ್ಲಿ ಆಸಕ್ತನಾಗಿ ಇದ್ದೆನು; ಆದರೆ ಅವರು ನನ್ನ ಪ್ರಾಣವನ್ನೂ ತೆಗೆಯಬೇಕೆಂದಿದ್ದಾರೆ,” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"11": "<sup>11</sup> ಆಗ ಇನ್ನೊಮ್ಮೆ, “ನೀನು ಹೊರಗೆ ಬಂದು ಬೆಟ್ಟದ ಮೇಲೆ ಸರ್ವೇಶ್ವರನ ಮುಂದೆ ನಿಲ್ಲು,” ಎಂದು ವಾಣಿಯಾಯಿತು; ಆಹಾ! ಸರ್ವೇಶ್ವರ ಅಲ್ಲೇ ಹಾದುಹೋದರು, ಅವರ ಮುಂದೆ ಪರ್ವತಗಳನ್ನು ಭೇದಿಸಿ ಬಂಡೆಗಳನ್ನು ಪುಡಿಪುಡಿಮಾಡುವಂಥ ದೊಡ್ಡ ಬಿರುಗಾಳಿ ಬೀಸಿತು; ಸರ್ವೇಶ್ವರ ಅದರಲ್ಲಿ ಇರಲಿಲ್ಲ. ತರುವಾಯ ಭೂಕಂಪವುಂಟಾಯಿತು; ಅದರಲ್ಲೂ ಅವರಿರಲಿಲ್ಲ.",
			"12": "<sup>12</sup> ಭೂಕಂಪವಾದನಂತರ ಸಿಡಿಲು ಉಂಟಾಯಿತು; ಅದರಲ್ಲಿಯೂ ಸರ್ವೇಶ್ವರ ಇರಲಿಲ್ಲ. ಕಡೆಗೊಂದು ಮೆಲುದನಿ!",
			"13": "<sup>13</sup> ಅದನ್ನು ಕೇಳಿದ ಕೂಡಲೆ ಎಲೀಯನು ತನ್ನ ಕಂಬಳಿಯಿಂದ ಮುಖವನ್ನು ಮುಚ್ಚಿಕೊಂಡು ಹೋಗಿ ಗವಿಯ ದ್ವಾರದಲ್ಲಿ ನಿಂತನು. ಆಗ, “ಎಲೀಯನೇ, ನೀನು ಇಲ್ಲೇನು ಮಾಡುತ್ತಿರುವೆ?” ಎಂದು ವಾಣಿ ಕೇಳಿಸಿತು.",
			"14": "<sup>14</sup> ಅದಕ್ಕೆ ಅವನು, “ಸೇನಾಧೀಶ್ವರರಾದ ಸರ್ವೇಶ್ವರಾ, ಇಸ್ರಯೇಲರು ನಿಮ್ಮ ನಿಬಂಧನೆಯನ್ನು ಮೀರಿದ್ದಾರೆ; ಬಲಿಪೀಠಗಳನ್ನು ಕೆಡವಿಹಾಕಿದ್ದಾರೆ; ಪ್ರವಾದಿಗಳನ್ನು ಕತ್ತಿಯಿಂದ ಸಂಹರಿಸಿದ್ದಾರೆ; ನಾನೊಬ್ಬನೇ ಉಳಿದು ನಿಮ್ಮ ಗೌರವವನ್ನು ಕಾಪಾಡುವುದರಲ್ಲಿ ಆಸಕ್ತನಾಗಿದ್ದೆನು; ಆದರೆ ಅವರು ನನ್ನ ಪ್ರಾಣವನ್ನೂ ತೆಗೆಯಬೇಕೆಂದು ಇದ್ದಾರೆ,” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"15": "<sup>15</sup> ಆಗ ಸರ್ವೇಶ್ವರ, “ನೀನು ಬಂದ ದಾರಿಯಲ್ಲೇ ಹಿಂದಿರುಗಿ ದಮಸ್ಕದ ಮರುಭೂಮಿಗೆ ಹೋಗು; ಅಲ್ಲಿಂದ ಪಟ್ಟಣದೊಳಗೆ ಹೋಗಿ ಹಜಾಯೇಲನನ್ನು ಸಿರಿಯಾದವರ ಅರಸನನ್ನಾಗಿ ಅಭಿಷೇಕಿಸು;",
			"16": "<sup>16</sup> ನಿಂಷಿಯ ಮಗ ಯೇಹುವನ್ನು, ಇಸ್ರಯೇಲರ ಅರಸನನ್ನಾಗಿ ಅಭಿಷೇಕಿಸು. ಅಬೇಲ್ ಮಹೋಲದವನೂ ಶಾಫಾಟನ ಮಗನೂ ಆದ ಎಲೀಷನನ್ನು ನಿನ್ನ ಉತ್ತರಾಧಿಕಾರಿಯಾದ ಪ್ರವಾದಿಯನ್ನಾಗಿ ಅಭಿಷೇಕಿಸು.",
			"17": "<sup>17</sup> ಹಜಾಯೇಲನ ಕತ್ತಿಗೆ ತಪ್ಪಿಸಿಕೊಂಡವರನ್ನು ಯೇಹುವು ಕೊಲ್ಲುವನು; ಇವನ ಕತ್ತಿಗೆ ತಪ್ಪಿಸಿಕೊಂಡವರನ್ನು ಎಲೀಷನು ಕೊಲ್ಲುವನು.",
			"18": "<sup>18</sup> ಆದರೆ ಬಾಳನ ವಿಗ್ರಹಕ್ಕೆ ಅಡ್ಡಬೀಳದೆಯೂ ಅದನ್ನು ಮುದ್ದಿಡದೆಯೂ ಇರುವ ಏಳುಸಾವಿರ ಮಂದಿ ಇಸ್ರಯೇಲರನ್ನು ಉಳಿಸುವೆನು,” ಎಂದು ಹೇಳಿದನು.",
			"19": "<sup>19</sup> ಎಲೀಯನು ಅಲ್ಲಿಂದ ಹೊರಟುಹೋಗಿ ಶಾಫಾಟನ ಮಗನಾದ ಎಲೀಷನನ್ನು ಕಂಡನು. ಅವನು ಹೊಲವನ್ನು ಉಳುವುದಕ್ಕೆ ಹನ್ನೆರಡು ಜೋಡಿ ಎತ್ತುಗಳನ್ನು ತಂದು ಹನ್ನೆರಡನೆಯ ಜೋಡಿಯಿಂದ ತಾನಾಗಿ ಉಳುತ್ತಿದ್ದನು. ಎಲೀಯನು ಅಲ್ಲಿಂದ ಹಾದುಹೋಗುವಾಗ ತನ್ನ ಕಂಬಳಿಯನ್ನು ಅವನ ಮೇಲೆ ಹಾಕಿದನು.",
			"20": "<sup>20</sup> ಕೂಡಲೆ ಅವನು ಎತ್ತುಗಳನ್ನು ಬಿಟ್ಟು ಓಡುತ್ತಾ ಬಂದು ಎಲೀಯನಿಗೆ, “ನನ್ನ ತಂದೆತಾಯಿಗಳನ್ನು ಮುದ್ದಿಟ್ಟು ಬರುವುದಕ್ಕೆ ಅಪ್ಪಣೆಯಾಗಲಿ; ಅನಂತರ ನಿನ್ನನ್ನು ಹಿಂಬಾಲಿಸುವೆನು,” ಎಂದನು. ಅದಕ್ಕೆ ಎಲೀಯನು, “ಹೋಗಿ ಬಾ; ನಾನು ನಿನಗೆ ಮಾಡಬೇಕಾದದ್ದನ್ನು ಮಾಡಿದ್ದೇನೆ,” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"21": "<sup>21</sup> ಎಲೀಷನು ಹಿಂದಿರುಗಿಹೋಗಿ ತಾನು ಉಳುತ್ತಿದ್ದ ಜೋಡಿ ಎತ್ತುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ವಧಿಸಿ ಮಾಂಸವನ್ನು ನೊಗದ ಕಟ್ಟಿಗೆಯಿಂದ ಬೇಯಿಸಿ, ಜನರಿಗೆ ಔತಣಮಾಡಿಸಿದನು. ಅನಂತರ ಅವನು ಎದ್ದು ಎಲೀಯನನ್ನು ಹಿಂಬಾಲಿಸಿ ಅವನ ಶಿಷ್ಯನಾದನು."
		},
		"20": {
			"1": "<sup>1</sup> ಸಿರಿಯಾದವರ ಅರಸ ಬೆನ್ಹದದನು ತನ್ನ ಇಡೀ ಸೈನ್ಯವನ್ನು ಕೂಡಿಸಿಕೊಂಡು ರಥಾಶ್ವಬಲಗಳಿಂದ ಕೂಡಿದ ಮೂವತ್ತು ಎರಡು ಮಂದಿ ರಾಜರೊಡನೆ ಹೊರಟು ಬಂದು, ಸಮಾರಿಯ ಪಟ್ಟಣಕ್ಕೆ ಮುತ್ತಿಗೆ ಹಾಕಿ ಯುದ್ಧಮಾಡಿದನು.",
			"2": "<sup>2</sup> ಆ ಪಟ್ಟಣದಲ್ಲಿದ್ದ ಇಸ್ರಯೇಲರ ಅರಸ ಅಹಾಬನ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿದನು.",
			"3": "<sup>3</sup> “ನಿನ್ನ ಬೆಳ್ಳಿಬಂಗಾರವು ನನ್ನದು; ನಿನ್ನ ಪ್ರಿಯ ಸತಿಸುತರರೂ ನನ್ನವರೇ ಎಂದು ತಿಳಿದುಕೋ,” ಎಂಬುದಾಗಿ ಆ ದೂತರಿಂದ ಹೇಳಿಸಿದನು.",
			"4": "<sup>4</sup> ಅದಕ್ಕೆ ಇಸ್ರಯೇಲರ ಆ ಅರಸನು, “ನನ್ನ ಒಡೆಯನಾದ ಅರಸರೇ, ನೀವು ಹೇಳಿದಂತೆ ನಾನು ನಿಮ್ಮವನೇ; ನನಗಿರುವುದೆಲ್ಲವೂ ನಿಮ್ಮದೇ,” ಎಂದು ಉತ್ತರಕೊಟ್ಟು ಕಳುಹಿಸಿದನು.",
			"5": "<sup>5</sup> ಆ ದೂತರು ಮರಳಿ ಅಹಾಬನ ಬಳಿಗೆ ಬಂದು, “ನಿನ್ನ ಬೆಳ್ಳಿಬಂಗಾರವನ್ನೂ ಸತಿಸುತರನ್ನೂ ನನಗೆ ಒಪ್ಪಿಸಬೇಕೆಂದು ನಿನಗೆ ಹೇಳಿಕಳುಹಿಸಿದೆನಲ್ಲವೆ?",
			"6": "<sup>6</sup> ನಾಳೆ ಇಷ್ಟುಹೊತ್ತಿಗೆ ನನ್ನ ಆಳುಗಳನ್ನು ಕಳುಹಿಸುವೆನು; ಅವರು ನಿನ್ನ ಮತ್ತು ನಿನ್ನ ಅಧಿಕಾರಿಗಳ ಮನೆಗಳನ್ನು ಶೋಧಿಸಿ, ನಿಮಗೆ ಅಮೂಲ್ಯವಾದುವುಗಳನ್ನೆಲ್ಲಾ ತೆಗೆದುಕೊಳ್ಳುವರು ಎಂಬುದಾಗಿ ಬೆನ್ಹದದನು ತಿಳಿಸುತ್ತಾರೆ,” ಎಂದು ಹೇಳಿದರು.",
			"7": "<sup>7</sup> ಆಗ ಇಸ್ರಯೇಲರ ಆ ಅರಸನು ನಾಡಿನ ಎಲ್ಲ ಹಿರಿಯರನ್ನು ಕರೆಸಿ ಅವರಿಗೆ, “ನೋಡಿದಿರೋ, ಅವನು ನಮಗೆಂಥ ಕೇಡು ಬಗೆಯುತ್ತಾನೆ; ‘ನಿನ್ನ ಬೆಳ್ಳಿಬಂಗಾರವನ್ನೂ ಸತಿಸುತರನ್ನೂ ನನಗೆ ಕೊಡು’ ಎಂದು ಹೇಳಿಕಳುಹಿಸಿದ್ದಕ್ಕೆ ನಾನು ಒಪ್ಪಿದರೂ ಅವನಿಗೆ ತೃಪ್ತಿಯಾಗಲಿಲ್ಲ,” ಎಂದು ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ಹಿರಿಯರೆಲ್ಲರೂ ಜನಸಾಮಾನ್ಯರೂ ಅವನಿಗೆ, “ನೀವು ಈ ಮಾತಿಗೆ ಒಪ್ಪಬೇಡಿ,” ಎಂದರು.",
			"9": "<sup>9</sup> ಆದುದರಿಂದ ಅವನು ಬಂದಂಥ ದೂತರ ಮುಖಾಂತರ ಬೆನ್ಹದದನಿಗೆ, “ನನ್ನ ಒಡೆಯರೇ, ಅರಸರೇ, ನಾನು ನಿಮ್ಮ ಮೊದಲನೆಯ ಮಾತಿಗೆ ಒಪ್ಪಿ ಅದರಂತೆ ಮಾಡಲು ಸಿದ್ಧನಾಗಿರುತ್ತೇನೆ; ಆದರೆ ಎರಡನೆಯ ಮಾತಿಗೆ ಒಪ್ಪಲಾರೆ,” ಎಂದು ಹೇಳಿಕಳುಹಿಸಿದನು ದೂತರು ಬಂದು ಈ ಮಾತುಗಳನ್ನು ಬೆನ್ಹದದನಿಗೆ ತಿಳಿಸಿದರು.",
			"10": "<sup>10</sup> ಆಗ ಅವರು ಅಹಾಬನಿಗೆ, “ಸಮಾರಿಯ ಪಟ್ಟಣವನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ನಾಶಮಾಡುವೆನು; ಲೆಕ್ಕವಿಲ್ಲದ ನನ್ನ ಸೈನಿಕರು ಅದರಲ್ಲಿ ಒಂದು ಹಿಡಿ ಧೂಳನ್ನೂ ಬಿಡುವುದಿಲ್ಲ; ಬಿಟ್ಟರೆ ದೇವತೆಗಳು ನನಗೆ ಬೇಕಾದದ್ದನ್ನು ಮಾಡಲಿ!” ಎಂದು ಹೇಳಿಕಳುಹಿಸಿದನು.",
			"11": "<sup>11</sup> ಬಂದ ದೂತರಿಗೆ ಇಸ್ರಯೇಲರ ಅರಸನು, “ಯುದ್ಧಕ್ಕಾಗಿ ನಡುಕಟ್ಟನ್ನು ಬಿಗಿದುಕೊಳ್ಳುವವನು ಅದನ್ನು ಬಿಚ್ಚಿಡುವ ಜಯಶಾಲಿಯಂತೆ ಕೊಚ್ಚಿಕೊಳ್ಳಬಾರದು ಎಂದು ಅವನಿಗೆ ಹೇಳಿರಿ,” ಎಂದನು.",
			"12": "<sup>12</sup> ತನ್ನ ರಾಜರುಗಳ ಸಂಗಡ ಡೇರೆಗಳಲ್ಲಿ ಮದ್ಯಪಾನಮಾಡುತ್ತಿದ್ದ ಬೆನ್ಹದದನು ಈ ಉತ್ತರವನ್ನು ಕೇಳುತ್ತಲೇ, ಪಟ್ಟಣಕ್ಕೆ ಲಗ್ಗೆ ಹತ್ತುವಂತೆ ಸೈನಿಕರಿಗೆ ಆಜ್ಞಾಪಿಸಿದನು. ಅವರು ಹಾಗೆಯೇ ಮಾಡಿದರು.",
			"13": "<sup>13</sup> ಆಗ ಒಬ್ಬ ಪ್ರವಾದಿ ಇಸ್ರಯೇಲರ ಅರಸ ಅಹಾಬನ ಬಳಿಗೆ ಬಂದು ಅವನಿಗೆ, “ಈ ಮಹಾಸಮೂಹವನ್ನು ನೋಡಿದಿಯೋ? ‘ನಾನೇ ಸರ್ವೇಶ್ವರ’ ಎಂಬುದು ನಿನಗೆ ಮನದಟ್ಟಾಗುವಂತೆ ಈ ದಿನವೇ ಇವರನ್ನೆಲ್ಲಾ ನಿನ್ನ ಕೈಗೊಪ್ಪಿಸುವೆನು, ಎನ್ನುತ್ತಾರೆ ಸರ್ವೇಶ್ವರ,” ಎಂದು ಹೇಳಿದನು.",
			"14": "<sup>14</sup> ಅಹಾಬನು, “ಇದು ಯಾರ ಮುಖಾಂತರ ಆಗುವುದು?” ಎಂದು ಕೇಳಿದ್ದಕ್ಕೆ ಅವನು, “ಪ್ರದೇಶಾಧಿಪತಿಗಳ ಸೇವಕರ ಮುಖಾಂತರ ಆಗುವುದೆಂದು ಸರ್ವೇಶ್ವರ ತಿಳಿಸುತ್ತಾರೆ,” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು. ಅಹಾಬನು ಮುಂದುವರಿದು, “ಯುದ್ಧ ಪ್ರಾರಂಭಮಾಡತಕ್ಕವರಾರು?” ಎಂದು ಕೇಳಲು ಅವನು, “ನೀನೇ” ಎಂದು ಉತ್ತರಿಸಿದನು.",
			"15": "<sup>15</sup> ಅಹಾಬನು ಪ್ರದೇಶಾಧಿಪತಿಗಳ ಆಳುಗಳನ್ನು ಲೆಕ್ಕಿಸಿದಾಗ ಇನ್ನೂರ ಮೂವತ್ತೆರಡು ಮಂದಿ ಇದ್ದರು; ಇಸ್ರಯೇಲರ ಸೈನ್ಯವನ್ನು ಲೆಕ್ಕಿಸಿದಾಗ ಏಳು ಸಾವಿರ ಮಂದಿಯಿದ್ದರು.",
			"16": "<sup>16</sup> ಮಧ್ಯಾಹ್ನದಲ್ಲಿ, ಬೆನ್ಹದದನು ತನ್ನ ಸಹಾಯಕ್ಕಾಗಿ ಬಂದ ಮೂವತ್ತೆರಡು ಮಂದಿ ರಾಜರ ಸಂಗಡ ಮದ್ಯಪಾನಮಾಡಿ ಮತ್ತನಾಗಿ, ಡೇರೆಯಲ್ಲಿ ಕುಳಿತುಕೊಂಡಿದ್ದಾಗ ಇವರು ಪಟ್ಟಣದಿಂದ ಹೊರಗೆ ಬಂದರು.",
			"17": "<sup>17</sup> ಪ್ರದೇಶಾಧಿಪತಿಗಳ ಆಳುಗಳು ಮೊದಲು ಬಂದರು, “ಸಮಾರಿಯದ ಜನರು ಹೊರಗೆ ಬಂದಿರುತ್ತಾರೆ” ಎಂದು ಬೆನ್ಹದದನ ಕಾವಲುಗಾರರು ಅವನಿಗೆ ತಿಳಿಸಿದರು. ಅವನು,",
			"18": "<sup>18</sup> “ಅವರು ಯುದ್ಧಕ್ಕಾಗಿಯೇ ಬಂದಿರಲಿ, ಸಮಾಧಾನಕ್ಕಾಗಿಯೇ ಬಂದಿರಲಿ, ಅವರನ್ನು ಸಜೀವಿಗಳನ್ನಾಗಿ ಹಿಡಿದುತನ್ನಿ,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"19": "<sup>19</sup> ಮೊದಲು ಹೊರಟು ಬಂದ ಪ್ರದೇಶಾಧಿಪತಿಗಳ ಆಳುಗಳ ಹಿಂದೆಯೇ ಇಸ್ರಯೇಲ್ ಸೈನ್ಯದವರೂ ಬಂದರು.",
			"20": "<sup>20</sup> ಅವರಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬನು ತನ್ನ ಎದುರಿಗೆ ಬಂದ ವಿರೋಧಿಯನ್ನು ಕೊಲ್ಲುತ್ತಾ ಮುಂದೆ ನಡೆದನು; ಸಿರಿಯಾದವರು ಓಡಿಹೋಗತೊಡಗಿದರು. ಇಸ್ರಯೇಲರು ಅವರನ್ನು ಹಿಂದಟ್ಟಿದರು. ಅರಸ ಬೆನ್ಹದದನು ಹಾಗು ಕೆಲವು ಮಂದಿ ರಾಹುತರು ಕುದುರೆಗಳನ್ನೇರಿ ಓಡಿಹೋಗಿ ತಪ್ಪಿಸಿಕೊಂಡರು.",
			"21": "<sup>21</sup> ಇಸ್ರಯೇಲರ ಅರಸನು ಹೊರಟುಬಂದು ಸಿರಿಯಾದವರ ರಥಾಶ್ವಬಲಗಳನ್ನು ಸೋಲಿಸಿ, ಮಹಾಸಮೂಹವನ್ನು ಸಂಹರಿಸಿದನು.",
			"22": "<sup>22</sup> ಆಗ ಆ ಪ್ರವಾದಿ ಮತ್ತೆ ಇಸ್ರಯೇಲರ ಅರಸನ ಬಳಿಗೆ ಬಂದನು. “ಸಿರಿಯಾದವರ ಅರಸನು ಮುಂದಿನ ವರ್ಷ ಇನ್ನೊಮ್ಮೆ ನಿನ್ನ ಮೇಲೆ ದಾಳಿಮಾಡಬರುವನು; ಆದುದರಿಂದ ನೀನು ಹೋಗಿ ನಿನ್ನನ್ನು ಬಲಪಡಿಸಿಕೋ; ಜಾಗರೂಕನಾಗಿದ್ದು ನೀನು ಮಾಡತಕ್ಕದ್ದೇನೆಂಬುದನ್ನು ಪರ್ಯಾಲೋಚಿಸು,” ಎಂದು ಎಚ್ಚರಿಸಿದನು.",
			"23": "<sup>23</sup> ಸಿರಿಯಾದವರ ಅರಸನ ಮಂತ್ರಿಗಳು ತಮ್ಮ ಒಡೆಯನಿಗೆ, “ಇಸ್ರಯೇಲರ ದೇವರು ಬೆಟ್ಟಗಳ ದೇವರಾಗಿರುವುದರಿಂದ ಅವರು ನಮ್ಮನ್ನು ಸೋಲಿಸಿದರು; ನಾವು ಅವರೊಡನೆ ಬಯಲಿನಲ್ಲಿ ಯುದ್ಧಮಾಡುವುದಾದರೆ ಖಂಡಿತವಾಗಿ ಜಯಹೊಂದುವೆವು.",
			"24": "<sup>24</sup> ನೀವು ಆಯಾ ಸ್ಥಳಗಳಲ್ಲಿರುವ ರಾಜರನ್ನು ತೆಗೆದುಹಾಕಿ, ಅವರಿಗೆ ಬದಲಾಗಿ ಪ್ರದೇಶಾಧಿಪತಿಗಳನ್ನು ನೇಮಿಸಿರಿ.",
			"25": "<sup>25</sup> ನಮ್ಮ ಸೈನ್ಯದಿಂದ ನಷ್ಟವಾಗಿಹೋದಷ್ಟು ಜನರನ್ನೂ ಕುದುರೆಗಳನ್ನೂ ರಥಗಳನ್ನೂ ಮತ್ತೆ ಕೂಡಿಸಿ ಅವರೊಡನೆ ಬಯಲಿನಲ್ಲಿ ಯುದ್ಧಮಾಡೋಣ; ನಮಗೆ ಹೇಗೂ ಜಯಸಿಕ್ಕುವುದು ನಿಶ್ಚಯ,” ಎಂದು ಹೇಳಿದರು. ಅವನು ಅದರ ಅಂತೆಯೇ ಮಾಡಿದನು.",
			"26": "<sup>26</sup> ಒಂದು ವರ್ಷ ದಾಟಿದನಂತರ ಬೆನ್ಹದದನು ಸಿರಿಯಾದವರನ್ನು ಕೂಡಿಸಿಕೊಂಡು ಇಸ್ರಯೇಲರೊಡನೆ ಯುದ್ಧಮಾಡುವುದಕ್ಕಾಗಿ ಅಫೇಕಕ್ಕೆ ಬಂದನು.",
			"27": "<sup>27</sup> ಇಸ್ರಯೇಲರು ಸಹ ಆಹಾರ ಸಾಮಗ್ರಿಗಳನ್ನು ಒದಗಿಸಿಕೊಂಡು ಅವರಿಗೆ ವಿರುದ್ಧ ಕೂಡಿಬಂದರು. ಸಿರಿಯಾದವರು ನಾಡಿನಲ್ಲೆಲ್ಲಾ ವ್ಯಾಪಿಸಿಕೊಂಡಿದ್ದರು. ಆಡುಮರಿಗಳ ಎರಡು ಹಿಂಡುಗಳಂತಿದ್ದ ಇಸ್ರಯೇಲರು ಅವರ ಎದುರಿನಲ್ಲೇ ಪಾಳೆಯಮಾಡಿಕೊಂಡರು.",
			"28": "<sup>28</sup> ಆಗ ದೈವಪುರುಷನೊಬ್ಬನು ಇಸ್ರಯೇಲರ ಅಹಾಬನ ಬಳಿಗೆ ಬಂದು, “ಸರ್ವೇಶ್ವರ ಹೀಗೆನ್ನುತ್ತಾರೆ, ‘ಸರ್ವೇಶ್ವರ ಕಣಿವೆಗಳ ದೇವರಲ್ಲ, ಬೆಟ್ಟಗಳ ದೇವರಾಗಿ ಮಾತ್ರ ಇರುತ್ತಾರೆಂದುಕೊಂಡು ಬಂದಿರುವ ಈ ಸಿರಿಯಾದವರ ಮಹಾಸೈನ್ಯವನ್ನು ನಿನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸುವೆನು; ಇದರಿಂದ ನಾನೇ ಸರ್ವೇಶ್ವರ ಎಂದು ನಿನಗೆ ಗೊತ್ತಾಗುವುದು,” ಎಂದು ಹೇಳಿದನು.",
			"29": "<sup>29</sup> ಈ ಎರಡು ಸೈನ್ಯಗಳೂ ಏಳುದಿನಗಳವರೆಗೆ ಎದುರುಬದುರಾಗಿ ಪಾಳೆಯಮಾಡಿಕೊಂಡಿದ್ದವು; ಏಳನೆಯ ದಿನದಲ್ಲಿ ಯುದ್ಧಪ್ರಾರಂಭವಾಯಿತು. ಇಸ್ರಯೇಲರು ಆ ಒಂದೇ ದಿನದಲ್ಲಿ ಸಿರಿಯಾದವರ ಒಂದು ಲಕ್ಷಮಂದಿ ಕಾಲಾಳುಗಳನ್ನು ವಧಿಸಿದರು.",
			"30": "<sup>30</sup> ಉಳಿದ ಇಪ್ಪತ್ತೇಳು ಸಾವಿರಮಂದಿ ಸೈನಿಕರು ಅಫೇಕ ಪಟ್ಟಣವನ್ನು ಹೊಕ್ಕರು. ಆದರೆ ಆ ಪಟ್ಟಣದ ಗೋಡೆ ಅವರ ಮೇಲೆ ಬಿದ್ದುದರಿಂದ ಅವರೂ ಸತ್ತರು. ಬೆನ್ಹದದನೂ ಆ ಪಟ್ಟಣವನ್ನು ಹೊಕ್ಕು, ಒಂದು ಮನೆಯ ಒಳಕೋಣೆಯಲ್ಲಿ ಅಡಗಿಕೊಂಡನು.",
			"31": "<sup>31</sup> ಆಗ ಅವನ ಅಧಿಕಾರಿಗಳು, “ಇಸ್ರಯೇಲರ ಅರಸರು ದಯೆಯುಳ್ಳವರೆಂದು ನಾವು ಕೇಳಿದ್ದೇವೆ; ಆದುದರಿಂದ ಸೊಂಟಕ್ಕೆ ಗೋಣೀತಟ್ಟು ಕಟ್ಟಿಕೊಂಡು, ಕುತ್ತಿಗೆಗೆ ಹಗ್ಗ ಸುತ್ತಿಕೊಂಡು ಆ ಅರಸನ ಬಳಿಗೆ ಹೋಗುವುದಕ್ಕೆ ನಮಗೆ ಅಪ್ಪಣೆಯಾಗಲಿ; ಹೀಗೆ ಮಾಡಿದರೆ ಅವನು ತಮ್ಮ ಜೀವವನ್ನು ಉಳಿಸಬಹುದು,” ಎಂದು ಹೇಳಿದರು.",
			"32": "<sup>32</sup> ಅವರು ಸೊಂಟಕ್ಕೆ ಗೋಣೀತಟ್ಟು ಕಟ್ಟಿಕೊಂಡು ಕುತ್ತಿಗೆಗೆ ಹಗ್ಗ ಸುತ್ತಿಕೊಂಡು ಇಸ್ರಯೇಲರ ಅರಸನ ಬಳಿಗೆ ಬಂದು, “ಜೀವದಾನ ಮಾಡಬೇಕೆಂದು ನಿಮ್ಮ ಸೇವಕನಾದ ಬೆನ್ಹದದನು ವಿಜ್ಞಾಪಿಸುತ್ತಾನೆ,” ಎಂದರು. ಆಗ ಅವನು, “ಬೆನ್ಹದದನು ಇನ್ನೂ ಜೀವದಿಂದಿರುತ್ತಾನೋ? ಅವನು ನನ್ನ ಸಹೋದರನು,” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"33": "<sup>33</sup> ಅವರು ಈ ಮಾತು ಶುಭವಚನವೆಂದು ನೆನೆಸಿ, ಕೂಡಲೆ ಅವನ ಮಾತನ್ನೇ ಅನುಮೋದಿಸಿ, “ಬೆನ್ಹದದನು ನಿಮ್ಮ ಸಹೋದರನೇ ಹೌದು,” ಎಂದರು. ಅವನು ಅವರಿಗೆ, “ಹೋಗಿ ಬೆನ್ಹದದನನ್ನು ಕರೆದುಕೊಂಡು ಬನ್ನಿ,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು. ಅವನು ಬಂದಾಗ ಅವನನ್ನು ತನ್ನ ರಥದಲ್ಲೇ ಕುಳ್ಳಿರಿಸಿಕೊಂಡನು.",
			"34": "<sup>34</sup> ಬೆನ್ಹದದನು ಅವನಿಗೆ, “ನನ್ನ ತಂದೆ, ನಿನ್ನ ತಂದೆಯಿಂದ ಕಿತ್ತುಕೊಂಡ ಪಟ್ಟಣಗಳನ್ನು ನಾನು ಹಿಂದಕ್ಕೆ ಕೊಡುತ್ತೇನೆ; ಅವನು ಸಮಾರಿಯಾದಲ್ಲಿ ಮಾಡಿದಂತೆ ನೀನೂ ನನಗಾಗಿ ದಮಸ್ಕದಲ್ಲಿ ಕೆಲವು ವ್ಯಾಪಾರ ಕೇಂದ್ರಗಳನ್ನು ಸ್ಥಾಪಿಸಿಕೊಳ್ಳಬಹುದು,” ಎಂದು ಹೇಳಿದನು. ಅಹಾಬನು, “ನಾನು ಈ ಒಪ್ಪಂದದ ಮೇಲೆ ನಿನ್ನನ್ನು ಬಿಟ್ಟುಬಿಡುತ್ತೇನೆ,” ಎಂದು ಹೇಳಿ, ಅವನಿಂದ ಪ್ರಮಾಣಮಾಡಿಸಿ ಕಳುಹಿಸಿಬಿಟ್ಟನು.",
			"35": "<sup>35</sup> ಪ್ರವಾದಿಮಂಡಲಿಯವರಲ್ಲಿ ಒಬ್ಬನು, ಸರ್ವೇಶ್ವರನಿಂದ ಪ್ರೇರಿತನಾಗಿ ತನ್ನ ಜೊತೆಗಾರನೊಬ್ಬನಿಗೆ, “ನನ್ನನ್ನು ಹೊಡೆ,” ಎಂದು ಹೇಳಿದನು. ಅದಕ್ಕೆ ಅವನು ಒಪ್ಪಲಿಲ್ಲ.",
			"36": "<sup>36</sup> ಆಗ ಆ ಪ್ರವಾದಿ ಅವನಿಗೆ, “ನೀನು ಸರ್ವೇಶ್ವರನ ಮಾತನ್ನು ಕೇಳದೆಹೋದುದರಿಂದ ನನ್ನನ್ನು ಬಿಟ್ಟು ಹೊರಟಕೂಡಲೆ ಒಂದು ಸಿಂಹವು ಬಂದು ನಿನ್ನನ್ನು ಕೊಲ್ಲುವುದು,” ಎಂದನು. ಅಂತೆಯೇ ಅವನು ಇವನನ್ನು ಬಿಟ್ಟುಹೋದ ಕೂಡಲೆ, ಒಂದು ಸಿಂಹವು ಬಂದು ಅವನನ್ನು ಕೊಂದುಹಾಕಿತು.",
			"37": "<sup>37</sup> ಅನಂತರ ಆ ಪ್ರವಾದಿ ಇನ್ನೊಬ್ಬನನ್ನು ನೋಡಿ, “ನನ್ನನ್ನು ಹೊಡೆ,” ಎಂದು ಬೇಡಿಕೊಂಡನು. ಅವನು ಇವನನ್ನು ಗಾಯವಾಗುವಷ್ಟು ಹೊಡೆದನು.",
			"38": "<sup>38</sup> ಆಗ ಇವನು ತನ್ನ ಗುರುತು ತಿಳಿಯಬಾರದೆಂದು ರುಮಾಲಿನಿಂದ ಕಣ್ಣುಗಳನ್ನು ಮುಚ್ಚಿಕೊಂಡು, ಅರಸನು ಬರುವ ದಾರಿಯಲ್ಲಿ ಕಾದು ನಿಂತನು.",
			"39": "<sup>39</sup> ಅರಸನು ಹಾದುಹೋಗುವಾಗ ಅವನಿಗೆ, “ನಿಮ್ಮ ಸೇವಕನಾದ ನಾನು ಯುದ್ಧಕ್ಕೆ ಹೋಗಿದ್ದಾಗ ರಣರಂಗದಲ್ಲಿ ಒಬ್ಬನು, ಇನ್ನೊಬ್ಬನನ್ನು ಹಿಡಿದುಕೊಂಡು ನನ್ನ ಬಳಿಗೆ ಬಂದು, ‘ನೀನು ಇವನನ್ನು ಕಾಯಬೇಕು, ಇವನು ತಪ್ಪಿಸಿಕೊಂಡರೆ ಇವನ ಪ್ರಾಣಕ್ಕೆ ನಿನ್ನ ಪ್ರಾಣವು ನನ್ನದಾಗಿರುವುದು; ಇಲ್ಲವೆ ನೀನು ನನಗೆ ಮೂರು ಸಾವಿರ ಬೆಳ್ಳಿನಾಣ್ಯವನ್ನು ಕೊಡಬೇಕು’ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"40": "<sup>40</sup> ನಾನು ಬೇರೆ ಕೆಲಸದಲ್ಲಿದ್ದಾಗ ಆ ವ್ಯಕ್ತಿ ತಪ್ಪಿಸಿಕೊಂಡು ಹೋದನು,” ಎಂದು ಮೊರೆಯಿಟ್ಟನು. ಇಸ್ರಯೇಲರ ಅರಸನು ಇವನಿಗೆ, “ನಿನಗೆ ಆ ತೀರ್ಪು ಸರಿಯಾಗಿದೆ; ತೀರ್ಮಾನಿಸಿದವನು ನೀನೇ ಅಲ್ಲವೇ?’ ಎಂದು ನುಡಿದನು.",
			"41": "<sup>41</sup> ಕೂಡಲೆ ಆ ಪ್ರವಾದಿ ಕಣ್ಣುಗಳ ಮೇಲಿನಿಂದ ರುಮಾಲನ್ನು ತೆಗೆದನು. ಅವನು ಪ್ರವಾದಿಗಳಲ್ಲೊಬ್ಬನೆಂದು ಇಸ್ರಯೇಲರ ಅರಸನಿಗೆ ಗೊತ್ತು ಆಯಿತು.",
			"42": "<sup>42</sup> ಆಗ ಆ ಪ್ರವಾದಿ ಅರಸನಿಗೆ, “ಸಂಹರಿಸಬೇಕೆಂದು ಹೇಳಿ ನಾನು ನಿನ್ನ ಕೈಗೆ ಒಪ್ಪಿಸಿದ ವ್ಯಕ್ತಿಯನ್ನು ನೀನು ಹೋಗಿಬಿಟ್ಟದ್ದರಿಂದ ಅವನ ಪ್ರಾಣಕ್ಕೆ ಬದಲಾಗಿ ನಿನ್ನ ಪ್ರಾಣವು ಹೋಗುವುದು; ನಿನ್ನ ಪ್ರಜೆಗಳು ಅವನ ಪ್ರಜೆಗಳಾಗುವರು, ಎನ್ನುತ್ತಾರೆ ಸರ್ವೇಶ್ವರ,” ಎಂದು ಹೇಳಿದನು.",
			"43": "<sup>43</sup> ಇಸ್ರಯೇಲರ ಅರಸನು ಸಿಟ್ಟಿನಿಂದ, ಗಂಟುಮೋರೆ ಮಾಡಿಕೊಂಡು, ಸಮಾರಿಯದಲ್ಲಿ ಇದ್ದ ತನ್ನ ಮನೆಗೆ ಹೋದನು."
		},
		"21": {
			"1": "<sup>1</sup> ಅನಂತರ ಸಂಭವಿಸಿದ ಘಟನೆಗಳು ಇವು; ಜೆಸ್ರೀಲಿನಲ್ಲಿ, ಸಮಾರಿಯದ ಅರಸ ಅಹಾಬನ ಅರಮನೆಯ ಹತ್ತಿರ ಜೆಸ್ರೀಲಿನವನಾದ ನಾಬೋತನೆಂಬ ವ್ಯಕ್ತಿಗೆ ಒಂದು ದ್ರಾಕ್ಷೀತೋಟವಿತ್ತು.",
			"2": "<sup>2</sup> ಅಹಾಬನು ಆ ನಾಬೋತನಿಗೆ, “ನಿನ್ನ ದ್ರಾಕ್ಷೀತೋಟವನ್ನು ನನಗೆ ಕೊಡು; ಅದು ನನ್ನ ಅರಮನೆಯ ಹತ್ತಿರವಿರುವುದರಿಂದ ಅದನ್ನು ಕಾಯಿಪಲ್ಯದ ತೋಟವನ್ನಾಗಿ ಮಾಡಿಕೊಳ್ಳುತ್ತೇನೆ; ಅದಕ್ಕೆ ಬದಲಾಗಿ ನಿನಗೆ ಅದಕ್ಕಿಂತ ಉತ್ತಮವಾದ ತೋಟವನ್ನು ಕೊಡುತ್ತೇನೆ; ಅದು ಬೇಡವಾದರೆ ಕ್ರಯವನ್ನು ಕೊಡುತ್ತೇನೆ,” ಎಂದು ಹೇಳಿದನು.",
			"3": "<sup>3</sup> ಅದಕ್ಕೆ ನಾಬೋತನು, “ಪಿತ್ರಾರ್ಜಿತ ಸೊತ್ತನ್ನು ನಿಮಗೆ ಮಾರದಂತೆ ಸರ್ವೇಶ್ವರ ನನ್ನನ್ನು ತಡೆಯಲಿ!” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"4": "<sup>4</sup> ಪಿತ್ರಾರ್ಜಿತ ಸೊತ್ತನ್ನು ನಿನಗೆ ಕೊಡುವುದಿಲ್ಲವೆಂದು ನಾಬೋತನು ಹೇಳಿದ್ದರಿಂದ ಅಹಾಬನು ಸಿಟ್ಟುಗೊಂಡನು; ಗಂಟುಮೋರೆ ಮಾಡಿಕೊಂಡು ಮನೆಗೆ ಹೋಗಿ, ಊಟಮಾಡಲೊಲ್ಲದೆ ಮಂಚದ ಮೇಲೆ ಮಲಗಿ, ಗೋಡೆಯ ಕಡೆಗೆ ಮುಖ ತಿರುಗಿಸಿಕೊಂಡನು.",
			"5": "<sup>5</sup> ಆಗ ಅವನ ಹೆಂಡತಿ ಈಜೆಬೆಲಳು ಅವನ ಬಳಿಗೆ ಬಂದು, “ನೀವೇಕೆ ಊಟಮಾಡುವುದಿಲ್ಲ? ನಿಮಗೆ ಯಾವ ಚಿಂತೆ?” ಎಂದು ಕೇಳಿದಳು.",
			"6": "<sup>6</sup> ಅವನು, “ನಾನು ಜೆಸ್ರೀಲಿನವನಾದ ನಾಬೋತನಿಗೆ, ‘ನಿನ್ನ ದ್ರಾಕ್ಷೀತೋಟವನ್ನು ನನಗೆ ಮಾರಿಬಿಡು; ಹಣ ಬೇಡವಾದರೆ ನಿನಗೆ ಬೇರೊಂದು ದ್ರಾಕ್ಷೀತೋಟವನ್ನು ಕೊಡುತ್ತೇನೆ ಎಂದು ಹೇಳಿದೆ; ಆದರೆ ಅವನು ಕೊಡುವುದಿಲ್ಲ ಎಂದುಬಿಟ್ಟ,” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"7": "<sup>7</sup> ಆಗ ಅವನ ಹೆಂಡತಿ ಈಜೆಬೆಲಳು, “ಇಸ್ರಯೇಲರ ಅರಸರಾದ ನೀವು ಹೀಗೆ ವರ್ತಿಸುವುದೇ! ಎದ್ದು ಊಟಮಾಡಿ, ಸಂತೋಷದಿಂದಿರಿ; ನಾನು ನಿಮಗೆ ಜೆಸ್ರೀಲಿನವನಾದ ನಾಬೋತನ ದ್ರಾಕ್ಷೀತೋಟವನ್ನು ಕೊಡುತ್ತೇನೆ,” ಎಂದು ನುಡಿದಳು.",
			"8": "<sup>8</sup> ಅಂತೆಯೇ ಅಹಾಬನ ಹೆಸರಿನಲ್ಲಿ ಒಂದು ಪತ್ರವನ್ನು ಬರೆದು, ಅದಕ್ಕೆ ಅವನ ಮುದ್ರೆ ಹಾಕಿ, ಅದನ್ನು ನಾಬೋತನ ಊರಿನಲ್ಲಿದ್ದ ಎಲ್ಲಾ ಪ್ರಧಾನಪುರುಷರಿಗೂ ಹಿರಿಯರಿಗೂ ಕಳುಹಿಸಿದಳು.",
			"9": "<sup>9</sup> ಅದರಲ್ಲಿ, “ಎಲ್ಲರೂ ಉಪವಾಸ ಮಾಡಬೇಕೆಂದು ಪ್ರಕಟಿಸಿರಿ: ನಾಬೋತನನ್ನು ನೆರೆದ ಸಭೆಯ ಮುಂದೆ ನಿಲ್ಲಿಸಿರಿ;",
			"10": "<sup>10</sup> ಅವನು ದೇವರನ್ನೂ ಅರಸನನ್ನೂ ಶಪಿಸಿದವನು ಎಂಬುದಾಗಿ ಇಬ್ಬರು ದುಷ್ಟಮನುಷ್ಯರಿಂದ ಅವನಿಗೆ ವಿರೋಧವಾಗಿ ಸಾಕ್ಷಿ ಹೇಳಿಸಿರಿ; ಅವನನ್ನು ಹೊರಗೆ ಒಯ್ದು, ಕಲ್ಲೆಸೆದು ಕೊಲ್ಲಿರಿ,” ಎಂದು ಬರೆದಿದ್ದಳು.",
			"11": "<sup>11</sup> ಅವನ ಊರಿನ ಹಿರಿಯರೂ ಪ್ರಧಾನಪುರುಷರೂ ಈಜೆಬೆಲಳು ಕಳುಹಿಸಿದ ಪತ್ರದಲ್ಲಿ ಬರೆದಿದ್ದಂತೆಯೇ ಮಾಡಿದರು.",
			"12": "<sup>12</sup> ಉಪವಾಸವನ್ನು ಪ್ರಕಟಿಸಿದರು, ನೆರೆದ ಸಭೆಯ ಮುಂದೆ ನಾಬೋತನನ್ನು ನಿಲ್ಲಿಸಿದರು;",
			"13": "<sup>13</sup> ತರುವಾಯ ಇಬ್ಬರು ದುಷ್ಟರು ಬಂದು ಅವನ ಮುಂದೆ ಕುಳಿತರು. ಇವನು ದೇವರನ್ನೂ ಅರಸನನ್ನೂ ಶಪಿಸಿದ್ದಾನೆಂಬುದಾಗಿ ಜನರ ಎದುರಿನಲ್ಲೆ ಅವನಿಗೆ ವಿರುದ್ಧ ಸಾಕ್ಷಿಹೇಳಿದರು. ಜನರು ಅವನನ್ನು ಊರಿನ ಹೊರಗೆ ಒಯ್ದು ಕಲ್ಲೆಸೆದು ಕೊಂದರು.",
			"14": "<sup>14</sup> ನಾಬೋತನು ಕಲ್ಲೆಸೆತದಿಂದ ಸತ್ತ ಸಮಾಚಾರವನ್ನು ಆ ಹಿರಿಯರು ಈಜೆಬೆಲಳಿಗೆ ಮುಟ್ಟಿಸಿದರು.",
			"15": "<sup>15</sup> ಆಕೆ ಇದನ್ನು ಕೇಳಿ ಅಹಾಬನಿಗೆ, “ನೀವು ಹೋಗಿ ನಾಬೋತನು ಮಾರುವುದಿಲ್ಲವೆಂದು ಹೇಳಿದ ದ್ರಾಕ್ಷೀತೋಟವನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳಿ. ಅವನು ಜೀವದಿಂದಿಲ್ಲ, ಸತ್ತನು,” ಎಂದು ಹೇಳಿದಳು.",
			"16": "<sup>16</sup> ಜೆಸ್ರೀಲಿನವನಾದ ನಾಬೋತನ ಮರಣವಾರ್ತೆಯನ್ನು ಅಹಾಬನು ಕೇಳಿ, ದ್ರಾಕ್ಷೀತೋಟವನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವುದಕ್ಕೆ ಹೋದನು.",
			"17": "<sup>17</sup> ಆಗ ತಿಷ್ಬೀಯನಾದ ಎಲೀಯನಿಗೆ ಸರ್ವೇಶ್ವರನ ವಾಣಿವುಂಟಾಯಿತು.",
			"18": "<sup>18</sup> “ನೀನು ಹೋಗಿ ಸಮಾರಿಯಲ್ಲಿ ವಾಸಿಸುವ ಇಸ್ರಯೇಲ್ ಅರಸನನ್ನು ಕಾಣು; ಅವನು ಈಗ ನಾಬೋತನ ದ್ರಾಕ್ಷೀತೋಟವನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವುದಕ್ಕಾಗಿ ಅಲ್ಲಿಗೆ ಹೋಗಿದ್ದಾನೆ;",
			"19": "<sup>19</sup> ಅವನಿಗೆ, “ನೀನು ಕೊಲೆಮಾಡಿ, ಸೊತ್ತನ್ನು ಸಂಪಾದಿಸಿಕೊಂಡೆಯಲ್ಲವೇ? ನಾಯಿಗಳು ನಾಬೋತನ ರಕ್ತವನ್ನು ನೆಕ್ಕಿದ ಸ್ಥಳದಲ್ಲೇ ನಿನ್ನ ರಕ್ತವನ್ನೂ ನೆಕ್ಕುವುವು, ಎನ್ನುತ್ತಾರೆ ಸರ್ವೇಶ್ವರ’ ಎಂದು ಹೇಳು.”",
			"20": "<sup>20</sup> ಅಹಾಬನು ಎಲೀಯನನ್ನು ನೋಡಿ, “ಎಲೈ ವೈರಿಯೇ, ನೀನು ನನ್ನನ್ನು ಕಂಡುಹಿಡಿದೆಯಾ?” ಎಂದು ಕೇಳಿದನು. ಅವನು, “ಹೌದು, ಕಂಡುಹಿಡಿದೆ, ನೀನು ನಿನ್ನನ್ನು ಪಾಪಕ್ಕೆ ಮಾರಿಬಿಟ್ಟು, ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾದೆ.",
			"21": "<sup>21</sup> ಸರ್ವೇಶ್ವರ ನಿನ್ನನ್ನು ಕುರಿತು, ‘ನಾನು ನಿನ್ನ ಮೈಮೇಲೆ ಕೇಡನ್ನು ಬರಮಾಡಿ, ನಿನ್ನನ್ನು ಕಸದಂತೆ ತೆಗೆದುಹಾಕುವೆನು; ನಿನ್ನ ಸಂತಾನದ ಗಂಡಸರಲ್ಲಿ ಸ್ವತಂತ್ರರಾಗಲಿ, ಪರತಂತ್ರರಾಗಲಿ ಎಲ್ಲರನ್ನೂ ಇಸ್ರಯೇಲರ ಮಧ್ಯೆಯಿಂದ ಕಡಿದುಬಿಡುವೆನು.",
			"22": "<sup>22</sup> ನೀನು ಇಸ್ರಯೇಲರನ್ನು ಪಾಪಕ್ಕೆ ಪ್ರೇರಿಸಿ, ನನಗೆ ಕೋಪವನ್ನೆಬ್ಬಿಸಿದ್ದರಿಂದ ನೆಬಾಟನ ಮಗ ಯಾರೊಬ್ಬಾಮನ ಮನೆಗೂ ಅಹೀಯನ ಮಗ ಬಾಷನ ಮನೆಗೂ ಆದ ಗತಿ ನಿನ್ನ ಮನೆಗೂ ಆಗುವುದು,’ ಎನ್ನುತ್ತಾರೆ.",
			"23": "<sup>23</sup> ಇದಲ್ಲದೆ ಈಜೆಬೆಲಳನ್ನು ಕುರಿತು ಅವರು, ‘ನಾಯಿಗಳು ಜೆಸ್ರೀಲ್ ಪಟ್ಟಣದ ಗೋಡೆಯ ಬಳಿಯಲ್ಲಿ ಆಕೆಯ ಶವವನ್ನು ತಿನ್ನುವುವು;",
			"24": "<sup>24</sup> ಅಹಾಬನ ಮನೆಯವರಲ್ಲಿ ಊರೊಳಗೆ ಸಾಯುವಂಥವರನ್ನು ನಾಯಿಗಳೂ ಅಡವಿಯಲ್ಲಿ ಸಾಯುವಂಥವರನ್ನು ಪಕ್ಷಿಗಳೂ ತಿಂದುಬಿಡುವುವು’ ಎಂದು ಹೇಳುತ್ತಾರೆ,” ಎಂದನು.",
			"25": "<sup>25</sup> ಹೆಂಡತಿ ಈಜೆಬೆಲಳಿಂದ ಪ್ರಚೋದಿತನಾಗಿ ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ಕೆಟ್ಟದ್ದನ್ನು ಮಾಡುವುದಕ್ಕೆ ತನ್ನನ್ನೇ ಮಾರಿಬಿಟ್ಟ ಅಹಾಬನಂಥ ದುಷ್ಟನು ಇನ್ನೊಬ್ಬನಿರಲಿಲ್ಲ.",
			"26": "<sup>26</sup> ಸರ್ವೇಶ್ವರ ಇಸ್ರಯೇಲರ ನಾಡಿನಿಂದ ಓಡಿಸಿಬಿಟ್ಟ ಅಮೋರಿಯರಂತೆ ವಿಗ್ರಹಗಳನ್ನು ಪೂಜಿಸಿದ ಅಹಾಬನ ನಡತೆ ಬಹಳ ಅಸಹ್ಯವಾಗಿತ್ತು.",
			"27": "<sup>27</sup> ಅಹಾಬನು ಎಲೀಯನ ಮಾತುಗಳನ್ನು ಕೇಳಿ ತನ್ನ ಬಟ್ಟೆಯನ್ನು ದುಃಖದಿಂದ ಹರಿದುಕೊಂಡನು; ಹಗಲಿರುಳು ಮೈಮೇಲೆ ಗೋಣಿತಟ್ಟನ್ನು ಹಾಕಿಕೊಂಡು ಉಪವಾಸ ಮಾಡುತ್ತಾ ದೀನಮನಸ್ಸಿನಿಂದ ವರ್ತಿಸಿದನು.",
			"28": "<sup>28</sup> ಆದುದರಿಂದ ಸರ್ವೇಶ್ವರ ತಿಷ್ಬೀಯನಾದ ಎಲೀಯನಿಗೆ,",
			"29": "<sup>29</sup> “ಅಹಾಬನು ನನ್ನ ಮುಂದೆ ತನ್ನನ್ನೇ ತಗ್ಗಿಸಿಕೊಂಡದ್ದನ್ನು ನೋಡಿದಿಯಲ್ಲವೇ? ಅವನು ಹೀಗೆ ತನ್ನನ್ನು ತಗ್ಗಿಸಿಕೊಂಡದ್ದರಿಂದ ನಾನು ಮುಂತಿಳಿಸಿದ ಕೇಡನ್ನು ಅವನ ಜೀವಮಾನದಲ್ಲಿ ಬರಗೊಡಿಸುವುದಿಲ್ಲ. ಅವನ ಮಗನ ಕಾಲದಲ್ಲಿ ಅವನ ಮನೆಯವರ ಮೇಲೆ ಬರಮಾಡುವೆನು,” ಎಂದು ಹೇಳಿದರು."
		},
		"22": {
			"1": "<sup>1</sup> ಮೂರು ವರ್ಷಗಳವರೆಗೆ ಇಸ್ರಯೇಲರಿಗೂ ಸಿರಿಯಾದವರಿಗೂ ಯುದ್ಧ ನಡೆಯಲಿಲ್ಲ.",
			"2": "<sup>2</sup> ಮೂರನೆಯ ವರ್ಷದಲ್ಲಿ ಯೆಹೂದ್ಯರ ಅರಸ ಯೆಹೋಷಾಫಾಟನು ಇಸ್ರಯೇಲರ ಅರಸನ ಬಳಿಗೆ ಬಂದನು.",
			"3": "<sup>3</sup> ಆಗ ಇಸ್ರಯೇಲರ ಅರಸನು ತನ್ನ ಸೇವಕರಿಗೆ, “ಗಿಲ್ಯಾದಿನ ರಾಮೋತ್ ಪಟ್ಟಣ ನಮ್ಮದೆಂದು ನಿಮಗೆ ಗೊತ್ತುಂಟಲ್ಲವೆ; ಅದನ್ನು ಇಷ್ಟರವರೆಗೂ ಸಿರಿಯಾದವರ ಅರಸನಿಂದ ತಿರುಗಿ ತೆಗೆದುಕೊಳ್ಳದೆ ಸುಮ್ಮನೆ ಕುಳಿತಿರುತ್ತೇವೆ,” ಎಂದು ಹೇಳಿದನು.",
			"4": "<sup>4</sup> ಯೆಹೋಷಾಫಾಟನನ್ನು, “ಯುದ್ಧಮಾಡುವುದಕ್ಕೆ ನೀನೂ ನಮ್ಮ ಜೊತೆಯಲ್ಲಿ ರಾಮೋತ್ ಗಿಲ್ಯಾದಿಗೆ ಬರುತ್ತಿಯೋ?’ ಎಂದು ಕೇಳಿದನು. ಅವನು, “ನಾನೂ ನೀನೂ, ನನ್ನ ಜನರೂ ನಿನ್ನ ಜನರೂ, ನನ್ನ ಕುದುರೆಗಳೂ ನಿನ್ನ ಕುದುರೆಗಳೂ ಒಂದೇ ಅಲ್ಲವೇ?” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"5": "<sup>5</sup> ಮಾತ್ರವಲ್ಲ, “ಸರ್ವೇಶ್ವರನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಈಗಲೆ ವಿಚಾರಿಸು,” ಎಂದು ಇಸ್ರಯೇಲರ ಅರಸನನ್ನು ಬೇಡಿಕೊಂಡನು.",
			"6": "<sup>6</sup> ಆಗ ಇಸ್ರಯೇಲರ ಅರಸನು ತನ್ನ ರಾಜ್ಯದಲ್ಲಿದ್ದ ಸುಮಾರು ನಾನೂರು ಮಂದಿ ಪ್ರವಾದಿಗಳನ್ನು ಕೂಡಿಸಿ, “ನಾನು ರಾಮೋತ್ ಗಿಲ್ಯಾದಿನ ಮೇಲೆ ಯುದ್ಧಕ್ಕೆ ಹೋಗಬಹುದೋ? ಬಾರದೋ?” ಎಂದು ಕೇಳಿದನು. ಅವರು, “ಹೋಗಬಹುದು, ಸರ್ವೇಶ್ವರ ಅದನ್ನು ಕೈಗೆ ಒಪ್ಪಿಸುವರು,” ಎಂದರು.",
			"7": "<sup>7</sup> ಆದರೆ ಯೆಹೋಷಾಫಾಟನು ಇಸ್ರಯೇಲರ ಅರಸನನ್ನು, “ಸರ್ವೇಶ್ವರನ ಪ್ರವಾದಿಗಳಲ್ಲಿ ಇವರ ಹೊರತಾಗಿ ನಾವು ವಿಚಾರಿಸಬಹುದಾದ ಬೇರೆ ಪ್ರವಾದಿಯಿಲ್ಲವೇ?’ ಎಂದು ಕೇಳಿದನು.",
			"8": "<sup>8</sup> ಅವನು, “ನಮಗಾಗಿ ಸರ್ವೇಶ್ವರನ ಸನ್ನಿಧಿಯಲ್ಲಿ ವಿಚಾರಿಸಬಲ್ಲವನಾದ ಇನ್ನೊಬ್ಬ ಪ್ರವಾದಿಯಿರುತ್ತಾನೆ; ಅವನು ಇಮ್ಲನ ಮಗನಾದ ಮೀಕಾಯೆಹು ಎಂಬವನು. ಆದರೆ ನಾನು ಅವನನ್ನು ಹಗೆಮಾಡುತ್ತೇನೆ; ಅವನು ನನ್ನನ್ನು ಕುರಿತು ಯಾವಾಗಲೂ ಶುಭವನ್ನಲ್ಲ, ಅಶುಭವನ್ನೇ ಮುಂತಿಳಿಸುತ್ತಾನೆ,” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು. ಅದಕ್ಕೆ ಯೆಹೋಷಾಫಾಟನು, “ಅರಸರು ಹಾಗನ್ನಬಾರದು,” ಎಂದನು.",
			"9": "<sup>9</sup> ಆಗ ಇಸ್ರಯೇಲರ ಅರಸನು ಒಬ್ಬ ಕಂಚುಕಿಯನ್ನು ಕರೆದು, “ಬೇಗ ಹೋಗಿ ಇಮ್ಲನ ಮಗ ಮೀಕಾಯೆಹುವನ್ನು ಕರೆದುಕೊಂಡು ಬಾ,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"10": "<sup>10</sup> ಇಸ್ರಯೇಲರ ಅರಸನೂ ಯೆಹೂದ್ಯರ ಅರಸ ಯೆಹೋಷಾಫಾಟನೂ ರಾಜವಸ್ತ್ರಗಳನ್ನು ಧರಿಸಿಕೊಂಡು ಸಮಾರಿಯ ಪಟ್ಟಣದ ಹೆಬ್ಬಾಗಿಲಿನ ಹತ್ತಿರವಿರುವ ಬಯಲಿನಲ್ಲಿ ತಮ್ಮ ತಮ್ಮ ಆಸನಗಳ ಮೇಲೆ ಕುಳಿತುಕೊಂಡರು. ಎಲ್ಲ ಪ್ರವಾದಿಗಳು ಅವರ ಮುಂದೆ ಪರವಶರಾಗಿ ಮಾತಾಡತೊಡಗಿದರು.",
			"11": "<sup>11</sup> ಅವರಲ್ಲಿ ಕೆನಾನನ ಮಗ ಚಿದ್ಕೀಯ ಎಂಬವನು ಕಬ್ಬಿಣದ ಕೊಂಬುಗಳನ್ನು ಮಾಡಿಸಿ ತಲೆಗೆ ಕಟ್ಟಿಕೊಂಡು ಬಂದು, “ಕೊಂಬುಗಳಿಂದಲೋ ಎಂಬಂತೆ ನೀವು ಸಿರಿಯಾದವರನ್ನು ಇರಿದು ಕೊಂದುಹಾಕುವಿರಿ,” ಎಂದನು.",
			"12": "<sup>12</sup> ಉಳಿದ ಪ್ರವಾದಿಗಳೂ ಇದೇ ತರದ ಮಾತುಗಳನ್ನು ನುಡಿದು, “ರಾಮೋತ್ ಗಿಲ್ಯಾದಿಗೆ ಹೋಗಿ; ನೀವು ಕೃತಾರ್ಥರಾಗಿ ಬರುವಿರಿ; ಸರ್ವೇಶ್ವರ ಅದನ್ನು ಅರಸರ ಕೈಗೆ ಒಪ್ಪಿಸುವರು,” ಎಂದರು.",
			"13": "<sup>13</sup> ಇತ್ತ ಮೀಕಾಯೆಹುವನ್ನು ಕರೆಯುವುದಕ್ಕೆ ಬಂದಿದ್ದ ದೂತನು, “ನೋಡಿ, ಎಲ್ಲಾ ಪ್ರವಾದಿಗಳು ಏಕಮನಸ್ಸಿನಿಂದ ಅರಸರಿಗೆ ಶುಭವನ್ನೇ ಮುಂದೆ ತಿಳಿಸುತ್ತಾರೆ; ದಯವಿಟ್ಟು ನೀವೂ ಅವರಂತೆ ಶುಭವನ್ನೇ ಮುಂತಿಳಿಸಿ,” ಎಂದು ಹೇಳಿದನು.",
			"14": "<sup>14</sup> ಅದಕ್ಕೆ ಮೀಕಾಯೆಹುವು, “ಸರ್ವೇಶ್ವರನಾಣೆ, ಸರ್ವೇಶ್ವರ ಹೇಳುವುದನ್ನೇ ನುಡಿಯುತ್ತೇನೆ,” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"15": "<sup>15</sup> ಅವನು ಅರಸನ ಬಳಿಗೆ ಬಂದಾಗ ಅರಸನು, “ಮೀಕಾಯೆಹುವೇ, ನಾವು ರಾಮೋತ್ ಗಿಲ್ಯಾದಿನ ಮೇಲೆ ಯುದ್ಧಕ್ಕೆ ಹೋಗಬಹುದೋ ಬಾರದೋ?” ಎಂದು ಕೇಳಿದನು. ಅವನು, “ಹೋಗಬಹುದು, ಕೃತಾರ್ಥರಾಗಿ ಬರುವಿರಿ; ಸರ್ವೇಶ್ವರ ಪಟ್ಟಣವನ್ನು ಅರಸರ ಕೈಗೆ ಒಪ್ಪಿಸುವರು,” ಎಂದು ಹೇಳೀದನು.",
			"16": "<sup>16</sup> ಆಗ ಅರಸನು ಅವನಿಗೆ, “ಸತ್ಯವನ್ನೇ ತಿಳಿಸಬೇಕೆಂದು ಸರ್ವೇಶ್ವರನ ಹೆಸರಿನಲ್ಲಿ ನಿನ್ನಿಂದ ಎಷ್ಟುಸಾರಿ ಪ್ರಮಾಣಮಾಡಿಸಬೇಕು?” ಎಂದನು.",
			"17": "<sup>17</sup> ಆಗ ಅವನು, “ಇಸ್ರಯೇಲರೆಲ್ಲರೂ ಕುರುಬನಿಲ್ಲದ ಕುರಿಗಳಂತೆ ಬೆಟ್ಟಗಳಲ್ಲಿ ಚದರಿಹೋದದ್ದನ್ನು ಕಂಡೆ; ಆಗ ಸರ್ವೇಶ್ವರ, ‘ಇವರು ಒಡೆಯನಿಲ್ಲದವರಾಗಿರುತ್ತಾರೆ; ಸಮಾಧಾನದಿಂದ ತಮ್ಮ ತಮ್ಮ ಮನೆಗಳಿಗೆ ಹೋಗಲಿ,’ ಎಂದರು” ಎಂಬುದಾಗಿ ಉತ್ತರಕೊಟ್ಟನು.",
			"18": "<sup>18</sup> ಆಗ ಇಸ್ರಯೇಲರ ಅರಸನು ಯೆಹೋಷಾಫಾಟನಿಗೆ, “ಇವನು ನನಗೆ ಯಾವಾಗಲೂ ಶುಭವನ್ನಲ್ಲ, ಅಶುಭವನ್ನೇ ಮುಂತಿಳಿಸುತ್ತಾನೆಂದು ನಾನು ನಿನಗೆ ಹೇಳಲಿಲ್ಲವೇ?” ಎಂದನು.",
			"19": "<sup>19</sup> ಅದಕ್ಕೆ ಮೀಕಾಯೆಹುವು, “ಅದಿರಲಿ, ಸರ್ವೇಶ್ವರನ ವಾಕ್ಯವನ್ನು ಕೇಳು; ಸರ್ವೇಶ್ವರ ತಮ್ಮ ಸಿಂಹಾಸನದ ಮೇಲೆ ಕುಳಿತುಕೊಂಡದ್ದನ್ನೂ ಪರಲೋಕಸೈನ್ಯಗಳು ಅವರ ಎಡಬಲಗಡೆಗಳಲ್ಲಿ ನಿಂತದ್ದನ್ನೂ ಕಂಡೆ.",
			"20": "<sup>20</sup> ಸರ್ವೇಶ್ವರ ತಮ್ಮ ಹತ್ತಿರ ನಿಂತವರನ್ನು, “ಅಹಾಬನು ಹತನಾಗಿ ಬೀಳುವಂತೆ ಅವನನ್ನು ರಾಮೋತ್ ಗಿಲ್ಯಾದಿನ ಯುದ್ಧಕ್ಕೆ ಯಾರು ಪ್ರೇರಿಸುವಿರಿ?” ಎಂದು ಕೇಳಿದಾಗ ಒಬ್ಬನು ಒಂದು ವಿಧವಾಗಿಯೂ ಇನ್ನೊಬ್ಬನು ಇನ್ನೊಂದು ವಿಧವಾಗಿಯೂ ಉತ್ತರಕೊಟ್ಟರು.",
			"21": "<sup>21</sup> ಕಡೆಗೆ ಒಂದು ಆತ್ಮವು ಸರ್ವೇಶ್ವರನ ಮುಂದೆ ಬಂದು ನಿಂತು, ‘ನಾನು ಹೋಗಿ ಅವನನ್ನು ಪ್ರೇರಿಸುವೆನು,’ ಎಂದಿತು.",
			"22": "<sup>22</sup> ‘ಹೇಗೆ ಪ್ರೇರಿಸುವೆ?’ ಎಂದು ಸರ್ವೇಶ್ವರ ಕೇಳಿದರು. ಅದು, ‘ನಾನು ಅಸತ್ಯವಾಡುವ ಆತ್ಮವಾಗಿ ಅವನ ಎಲ್ಲ ಪ್ರವಾದಿಗಳಲ್ಲಿ ಸೇರುವೆನು,’ ಎಂದು ಉತ್ತರಕೊಟ್ಟಿತು. ಆಗ ಸರ್ವೇಶ್ವರ, ‘ಹೋಗಿ ಅದರ ಅಂತೆಯೇ ಮಾಡು; ಅವನನ್ನು ಪ್ರೇರಿಸಿ ಸಫಲನಾಗುವೆ,’ ಎಂದರು.",
			"23": "<sup>23</sup> ನೋಡು, ಸರ್ವೇಶ್ವರ ನಿನ್ನ ವಿಷಯದಲ್ಲಿ ಕೇಡು ನುಡಿದು ನಿನ್ನ ಎಲ್ಲಾ ಪ್ರವಾದಿಗಳಲ್ಲಿ ಅಸತ್ಯವನ್ನಾಡುವ ಆತ್ಮವನ್ನು ಕಳುಹಿಸಿದ್ದಾರೆ,” ಎಂದು ಹೇಳಿದನು.",
			"24": "<sup>24</sup> ಆಗ ಕೆನಾನನ ಮಗ ಚಿದ್ಕೀಯನು ಮೀಕಾಯೆಹುವಿನ ಬಳಿಗೆ ಬಂದು ಅವನ ಕೆನ್ನೆಗೆ ಒಂದು ಪೆಟ್ಟುಕೊಟ್ಟು, “ಸರ್ವೇಶ್ವರನ ಆತ್ಮ ನನ್ನನ್ನು ಬಿಟ್ಟು ನಿನ್ನೊಂದಿಗೆ ಮಾತಾಡುವುದಕ್ಕೆ ಯಾವ ಮಾರ್ಗವಾಗಿ ಬಂದಿತು?” ಎಂದನು.",
			"25": "<sup>25</sup> ಮೀಕಾಯೆಹುವು, “ನೀನು ಅಡಗಿಕೊಳ್ಳುವುದಕ್ಕೆ ಒಳಗಿನ ಕೋಣೆಗೆ ಹೋಗುವ ದಿನದಲ್ಲಿ ಅದು ನಿನಗೆ ಗೊತ್ತಾಗುವುದು,” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"26": "<sup>26</sup> ಆಗ ಇಸ್ರಯೇಲರ ಅರಸನು ಸೇವಕರಿಗೆ, “ಮೀಕಾಯೆಹುವನ್ನು ಹಿಡಿದುಕೊಂಡು ಹೋಗಿ ಪುರಾಧಿಕಾರಿಯಾದ ಆಮೋನನಿಗೂ ರಾಜಪುತ್ರನಾದ ಯೋವಾಷನಿಗೂ ಒಪ್ಪಿಸಿರಿ;",
			"27": "<sup>27</sup> ಅವರಿಗೆ, ‘ನಾನು ಸುರಕ್ಷಿತನಾಗಿ ಹಿಂದಿರುಗುವವರೆಗೂ ಇವನನ್ನು ಸೆರೆಯಲ್ಲಿಟ್ಟು ಸೆರೆಮನೆಯ ಅನ್ನಪಾನಗಳನ್ನೇ ಕೊಟ್ಟು ಕರಗಿಸಬೇಕು,’ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"28": "<sup>28</sup> ಮೀಕಾಯೆಹುವು ಅರಸನಿಗೆ, “ನೀನು ಸುರಕ್ಷಿತನಾಗಿ ಬರುವುದಾದರೆ ನಾನು ನುಡಿದದ್ದು ಸರ್ವೇಶ್ವರನ ಮಾತಲ್ಲವೆಂದು ತಿಳಿದುಕೋ,” ಎಂದು ಹೇಳಿ, “ಮಹಾಜನರೇ, ನನ್ನ ಮಾತನ್ನು ಗಮನದಲ್ಲಿಡಿ,” ಎಂದು ಕೂಗಿದನು.",
			"29": "<sup>29</sup> ಇಸ್ರಯೇಲರ ಅರಸನು ಹಾಗು ಯೆಹೂದ್ಯರ ಅರಸ ಯೆಹೋಷಾಫಾಟನು ರಾಮೋತ್ ಗಿಲ್ಯಾದಿಗೆ ವಿರುದ್ಧ ಯುದ್ಧಕ್ಕೆ ಹೊರಟರು.",
			"30": "<sup>30</sup> ಇಸ್ರಯೇಲರ ಅರಸನು ಯೆಹೋಷಾಫಾಟನಿಗೆ, “ನಾನು ವೇಷಹಾಕಿಕೊಂಡು ರಣರಂಗಕ್ಕೆ ಬರುತ್ತೇನೆ; ನೀನಾದರೋ ನಿನ್ನ ರಾಜವಸ್ತ್ರಗಳನ್ನು ಧರಿಸಿಕೊಂಡೇ ಬಾ;” ಎಂದು ಹೇಳಿ ವೇಷಹಾಕಿಕೊಂಡು ರಣರಂಗಕ್ಕೆ ಹೋದನು.",
			"31": "<sup>31</sup> ಸಿರಿಯಾದವರ ಅರಸನು ತನ್ನ ರಥಬಲದ ಮೂವತ್ತೆರಡು ಮಂದಿ ಅಧಿಪತಿಗಳಿಗೆ, “ನೀವು ಶತ್ರುಗಳ ಸಾಧಾರಣ ಸೈನಿಕರನ್ನೂ ಅಧಿಪತಿಗಳನ್ನೂ ಬಿಟ್ಟು ಇಸ್ರಯೇಲರ ಅರಸನಿಗೇ ಗುರಿ ಇಡಿ,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದ್ದನು.",
			"32": "<sup>32</sup> ಅವರು ಯೆಹೋಷಾಫಾಟನನ್ನು ಕಂಡಾಗ ಅವನೇ ಇಸ್ರಯೇಲರ ಅರಸನೆಂದು ನೆನಸಿ, ಅವನಿಗೆ ವಿರುದ್ಧ ಯುದ್ಧಮಾಡಲು ಬಂದರು.",
			"33": "<sup>33</sup> ಅವನು ಕೂಗಿಕೊಂಡನು. ಆಗ ಇವನು ಇಸ್ರಯೇಲರ ಅರಸನಲ್ಲವೆಂಬುದು ರಥಬಲದ ಅಧಿಪತಿಗಳಿಗೆ ಗೊತ್ತಾಯಿತು. ಅವರು ಅವನನ್ನು ಬಿಟ್ಟು ಹಿಂದಿರುಗಿದರು.",
			"34": "<sup>34</sup> ಸಿರಿಯಾದವರ ಸೈನಿಕನೊಬ್ಬನು ಯಾರಿಗೂ ಗುರಿಯಿಡದೆ ಬಾಣವನ್ನೆಸೆದನು. ಆ ಬಾಣ ಇಸ್ರಯೇಲರ ಅರಸನಿಗೆ, ಅವನ ಕವಚದ ಸಂದಿನಲ್ಲಿ ಬಂದು ತಾಕಿತು. ಆಗ ಅವನು ತನ್ನ ಸಾರಥಿಗೆ, “ರಥವನ್ನು ತಿರುಗಿಸಿ ನನ್ನನ್ನು ರಣರಂಗದಿಂದ ಆಚೆಗೆ ತೆಗೆದುಕೊಂಡು ಹೋಗು; ನನಗೆ ದೊಡ್ಡ ಗಾಯವಾಗಿದೆ,” ಎಂದು ಹೇಳಿದನು.",
			"35": "<sup>35</sup> ಆ ದಿವಸ ಯುದ್ಧ ಬಹುಘೋರವಾಗಿತ್ತು. ಆದ್ದರಿಂದ ಅರಸನು ಸಿರಿಯಾದವರ ಎದುರಿಗೆ ತನ್ನ ರಥದಲ್ಲೇ ಆತುಕೊಂಡು ನಿಲ್ಲಬೇಕಾಯಿತು. ಸಾಯಂಕಾಲವಾದಾಗ ಅವನು ಸತ್ತನು. ಗಾಯದಿಂದ ರಕ್ತವು ಸುರಿದು ಸುರಿದು ರಥದ ಅಡಿಯಲ್ಲಿ ನಿಂತಿತ್ತು.",
			"36": "<sup>36</sup> ಸೂರ್ಯಾಸ್ತಮವಾದ ಕೂಡಲೆ, “ಪ್ರತಿಯೊಬ್ಬನು ತನ್ನ ಪ್ರಾಂತ್ಯಕ್ಕೂ ಪಟ್ಟಣಕ್ಕೂ ಹೋಗಲಿ,” ಎಂಬ ಕೂಗು ಇಸ್ರಯೇಲರ ಸೈನ್ಯದಲ್ಲಿ ಹಬ್ಬಿಕೊಂಡಿತು.",
			"37": "<sup>37</sup> ಅರಸನ ಶವವನ್ನು ಸಮಾರಿಯಕ್ಕೆ ತಂದು ಅಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು.",
			"38": "<sup>38</sup> ವೇಶ್ಯೆಯರು ಸ್ನಾನಮಾಡುವ ಸಮಾರಿಯದ ಕೆರೆಯಲ್ಲಿ ಅವನ ರಥವನ್ನು ತೊಳೆಯುತ್ತಿದ್ದಾಗ, ನಾಯಿಗಳು ಬಂದು ಅದರಲ್ಲಿದ್ದ ರಕ್ತವನ್ನು ನೆಕ್ಕಿದವು, ಹೀಗೆ ಸರ್ವೇಶ್ವರ ಹೇಳಿದ ಮಾತು ನೆರವೇರಿತು.",
			"39": "<sup>39</sup> ಅಹಾಬನ ಉಳಿದ ಚರಿತ್ರೆ, ಅವನು ಕಟ್ಟಿಸಿದ ದಂತಮಂದಿರ, ಪಟ್ಟಣಗಳು, ಇವುಗಳ ವಿವರ, ಹಾಗು ಅವನ ಬೇರೆ ಕಾರ್ಯಸಾಧನೆಗಳು ಇಸ್ರಯೇಲರಾಜರ ಇತಿಹಾಸ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"40": "<sup>40</sup> ಅಹಾಬನು ಸತ್ತು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಿದನು. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗ ಅಹಜ್ಯ ಎಂಬವನು ಅರಸನಾದನು.",
			"41": "<sup>41</sup> ಇಸ್ರಯೇಲರ ಅರಸ ಅಹಾಬನ ನಾಲ್ಕನೆಯ ವರ್ಷದಲ್ಲಿ ಆಸನ ಮಗ ಯೆಹೋಷಾಫಾಟನು ಯೆಹೂದ್ಯರ ಅರಸನಾದನು.",
			"42": "<sup>42</sup> ಅವನು ಅರಸನಾದಾಗ ಮೂವತ್ತೈದು ವರ್ಷದವನಾಗಿ ಇದ್ದನು. ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಇಪ್ಪತ್ತೈದು ವರ್ಷ ಆಳಿದನು. ಶಿಲ್ಹಿಯ ಮಗಳಾದ ಅಜೂಬಳೆಂಬ ಆಕೆ ಇವನ ತಾಯಿ.",
			"43": "<sup>43</sup> ತನ್ನ ತಂದೆ ಆಸನ ಮಾರ್ಗದಲ್ಲಿ ಇವನು ತಪ್ಪದೆ ನಡೆಯುತ್ತಾ, ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ಒಳ್ಳೆಯವನಾಗಿ ಇದ್ದನು. ಆದರೂ ಪೂಜಾಸ್ಥಳಗಳನ್ನು ಹಾಳುಮಾಡಲಿಲ್ಲ. ಜನರು ಆ ಸ್ಥಳಗಳಲ್ಲಿ ಬಲಿಕೊಡುತ್ತಾ, ಧೂಪಾರತಿ ಎತ್ತುತ್ತಾ ಇದ್ದರು.",
			"44": "<sup>44</sup> ಇವನು ಇಸ್ರಯೇಲರ ಅರಸನೊಡನೆ ಸಮಾಧಾನದಿಂದಿದ್ದನು.",
			"45": "<sup>45</sup> ಇವನ ಉಳಿದ ಚರಿತ್ರೆ ಹಾಗು ಇವನು ಯುದ್ಧಗಳಲ್ಲಿ ನಡಿಸಿದ ಶೂರಕೃತ್ಯಗಳ ವಿವರ ಜುದೇಯ ರಾಜರ ಇತಿಹಾಸ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಲಿಖಿತವಾಗಿವೆ.",
			"46": "<sup>46</sup> ಇವನು ತನ್ನ ತಂದೆ ಆಸನ ಕಾಲದಲ್ಲಿ ತಪ್ಪಿಸಿಕೊಂಡ ಹೆಣ್ಣು-ಗಂಡು ವೇಶ್ಯೆಯರನ್ನು ನಾಡಿನಿಂದ ತೆಗೆದುಹಾಕಿದನು.",
			"47": "<sup>47</sup> ಆ ಕಾಲದಲ್ಲಿ ಎದೋಮ್ ದೇಶದಲ್ಲಿ ಅರಸನಿರಲಿಲ್ಲ; ಇವನ ಪ್ರತಿನಿಧಿಯೇ ಅದನ್ನು ಆಳುತ್ತಿದ್ದನು.",
			"48": "<sup>48</sup> ಇದಲ್ಲದೆ, ಯೆಹೋಷಾಫಾಟನು ಓಫೀರಿನಿಂದ ಬಂಗಾರ ತರುವುದಕ್ಕಾಗಿ ತಾರ್ಷೀಷ್ ಹಡಗುಗಳನ್ನು ಮಾಡಿಸಿದನು.",
			"49": "<sup>49</sup> ಆಗ ಅಹಾಬನ ಮಗ ಅಹಜ್ಯನು, “ನಿನ್ನ ಜನರ ಜೊತೆಯಲ್ಲಿ ನನ್ನ ಜನರೂ ಸಮುದ್ರಪ್ರಯಾಣ ಮಾಡುವುದಕ್ಕೆ ಅಪ್ಪಣೆಯಾಗಲಿ,” ಎಂದು ಬೇಡಿಕೊಂಡನು. ಅವನು ಅದಕ್ಕೆ ಒಪ್ಪಲಿಲ್ಲ. ಆದರೆ ಆ ಹಡಗುಗಳು ಎಚ್ಯೋನ್ಗೆಬೆರಿನಲ್ಲಿ ಒಡೆದುಹೋದುದರಿಂದ ಓಫೀರನ್ನು ಮುಟ್ಟಲಿಲ್ಲ.",
			"50": "<sup>50</sup> ಯೆಹೋಷಾಫಾಟನು ಮೃತನಾಗಿ ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಿದನು. ಅವನ ಶವವನ್ನು ಅವನ ಪೂರ್ವಜ ದಾವೀದನ ಪಟ್ಟಣದೊಳಗೆ ಕುಟುಂಬ ಸ್ಮಶಾನಭೂಮಿಯಲ್ಲಿ ಸಮಾಧಿ ಮಾಡಿದರು. ಅವನ ಸ್ಥಾನದಲ್ಲಿ ಅವನ ಮಗ ಯೆಹೋರಾಮನು ಅರಸನಾದನು.",
			"51": "<sup>51</sup> ಯೆಹೂದ್ಯರ ಅರಸ ಯೆಹೋಷಾಫಾಟನ ಆಳ್ವಿಕೆಯ ಹದಿನೇಳನೆಯ ವರ್ಷದಲ್ಲಿ ಅಹಾಬನ ಮಗ ಅಹಜ್ಯ ಎಂಬುವನು ಅರಸನಾಗಿ ಸಮಾರಿಯದಲ್ಲಿ ಎರಡು ವರ್ಷ ಆಳಿದನು.",
			"52": "<sup>52</sup> ಇವನು ಇಸ್ರಯೇಲರನ್ನು ಪಾಪಕ್ಕೆ ಪ್ರಚೋದಿಸಿ ನೆಬಾಟನ ಮಗ ಯಾರೊಬ್ಬಾಮನ ಮತ್ತು ತನ್ನ ತಂದೆತಾಯಿಗಳ ಮಾರ್ಗದಲ್ಲಿ ನಡೆದು ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾದನು.",
			"53": "<sup>53</sup> ತನ್ನ ತಂದೆಯಂತೆ ಬಾಳ್ ದೇವರನ್ನು ಪೂಜಿಸಿ ಇಸ್ರಯೇಲರ ದೇವರಾದ ಸರ್ವೇಶ್ವರನನ್ನು ರೇಗಿಸಿದನು."
		}
	}
};
module.exports = book;