var book = {
	"name": "೨ ಅರಸುಗಳು",
	"numChapters": 25,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ಅಹಾಬನು ಮರಣಹೊಂದಿದ ನಂತರ ಮೋವಾಬ್ಯರು ಇಸ್ರಯೇಲರ ವಿರುದ್ಧ ದಂಗೆ ಎದ್ದರು.",
			"2": "<sup>2</sup> ಅಹಜ್ಯನು ಸಮಾರಿಯದಲ್ಲಿದ್ದ ತನ್ನ ಮೇಲುಪ್ಪರಿಗೆಯ ಕಿಟಕಿಯೊಂದರಿಂದ ಬಿದ್ದು ಅಸ್ವಸ್ಥನಾಗಿದ್ದನು. ಅವನು ತನ್ನ ಸೇವಕರನ್ನು ಕರೆದು, “ನೀವು ಎಕ್ರೋನಿನ ದೇವರಾದ ‘ಬಾಳ್ಜೆಬೂಬ’ನ ಸನ್ನಿಧಿಗೆ ಹೋಗಿ ನಾನು ಈ ಅಸ್ವಸ್ಥತೆಯಿಂದ ಗುಣವಾಗುವೆನೋ ಇಲ್ಲವೋ, ಎಂದು ವಿಚಾರಿಸಿ ಬನ್ನಿ,” ಎಂದು ಹೇಳಿ ಕಳುಹಿಸಿದನು.",
			"3": "<sup>3</sup> ಆದರೆ ಸರ್ವೇಶ್ವರ ಸ್ವಾಮಿಯ ದೂತನು ತಿಷ್ಬೀಯನಾದ ಎಲೀಯನಿಗೆ, “ನೀನು ಹೋಗಿ ಸಮಾರಿಯದ ಅರಸನ ಆ ಸೇವಕರನ್ನು ಭೇಟಿಯಾಗು. ಅವರಿಗೆ, ‘ನೀವು ಎಕ್ರೋನಿನ ದೇವರಾದ ಬಾಳ್ಜೆಬೂಬನನ್ನು ವಿಚಾರಿಸುವ ಅಗತ್ಯವಾದರೂ ಏನು? ಇಸ್ರಯೇಲರಲ್ಲಿ ದೇವರಿಲ್ಲವೆ?",
			"4": "<sup>4</sup> ಈ ಕಾರಣ ಅಹಜ್ಯನು ತಾನು ಹಿಡಿದ ಹಾಸಿಗೆಯಿಂದ ಏಳದೆ ಸಾಯಲೇಬೇಕು ಎನ್ನುತ್ತಾರೆ ಸರ್ವೇಶ್ವರ’ ಎಂದು ಹೇಳು,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದರು. ಎಲೀಯನು ಹಾಗೆಯೇ ಮಾಡಿದನು.",
			"5": "<sup>5</sup> ದೂತರು ಅರಸನ ಬಳಿಗೆ ಹಿಂದಿರುಗಿ ಬಂದಾಗ ಅರಸನು, “ನೀವು ಹಿಂದಿರುಗಿದ್ದೇಕೆ?” ಎಂದು ಕೇಳಿದನು.",
			"6": "<sup>6</sup> ಅದಕ್ಕೆ ಅವರು, “ಒಬ್ಬ ವ್ಯಕ್ತಿ ನಮ್ಮನ್ನು ಎದುರುಗೊಂಡು, “ನಿಮ್ಮನ್ನು ಕಳುಹಿಸಿದ ಅರಸನ ಬಳಿಗೆ ಹಿಂದಿರುಗಿರಿ. ಸರ್ವೇಶ್ವರನ ಹೆಸರಿನಲ್ಲಿ ಅವನಿಗೆ ಹೀಗೆಂದು ಹೇಳಿರಿ: ‘ನೀವು ಎಕ್ರೋನಿನ ದೇವರಾದ ಬಾಳ್ಜೆಬೂಬನನ್ನು ವಿಚಾರಿಸುವ ಅಗತ್ಯವಾದರೂ ಏನು? ಇಸ್ರಯೇಲರಲ್ಲಿ ದೇವರಿಲ್ಲವೆ? ಹೀಗೆ ಮಾಡಿದ್ದರಿಂದ ನೀವು ಹಿಡಿದ ಹಾಸಿಗೆಯಿಂದ ಏಳದೆ ಸಾಯಲೇಬೇಕು ಎಂಬುದಾಗಿ ಹೇಳಿರಿ’ ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು,” ಎಂದು ಉತ್ತರಕೊಟ್ಟರು.",
			"7": "<sup>7</sup> ಅದಕ್ಕೆ ಅರಸನು, “ನಿಮ್ಮನ್ನು ಭೇಟಿಯಾಗಿ ಹೀಗೆಂದು ನುಡಿದ ಆ ವ್ಯಕ್ತಿ ಹೇಗಿದ್ದನು?” ಎಂದು ವಿಚಾರಿಸಿದನು.",
			"8": "<sup>8</sup> ಅವರು, “ಅವನು ಕಂಬಳಿಯನ್ನು ಹೊದ್ದುಕೊಂಡಿದ್ದ; ಅವನ ಸೊಂಟಕ್ಕೆ ತೊಗಲಿನ ನಡುಕಟ್ಟು ಇತ್ತು,” ಎಂದರು. ಆಗ ಅರಸನು, “ಆ ವ್ಯಕ್ತಿ ತಿಷ್ಬೀಯನಾದ ಎಲೀಯನೇ ಆಗಿರಬೇಕು,” ಎಂದುಕೊಂಡನು.",
			"9": "<sup>9</sup> ಬಳಿಕ ಅರಸನು ಎಲೀಯನನ್ನು ಕರೆದುತರುವುದಕ್ಕಾಗಿ ಒಬ್ಬ ಪಂಚಾಶದಧಿಪತಿಯನ್ನು ಅವನ ಐವತ್ತುಮಂದಿ ಸಿಪಾಯಿಗಳೊಡನೆ ಕಳುಹಿಸಿದನು. ಇವನು ಬಂದು ಎಲೀಯನು ಬೆಟ್ಟದ ತುದಿಯಲ್ಲಿ ಕುಳಿತಿರುವುದನ್ನು ಕಂಡನು. “ದೈವಪುರುಷರೇ, ಇಳಿದು ಬನ್ನಿ, ಅರಸರು ತಮ್ಮನ್ನು ಕರೆಯುತ್ತಿದ್ದಾರೆ,” ಎಂದು ಆಮಂತ್ರಿಸಿದನು.",
			"10": "<sup>10</sup> ಅದಕ್ಕೆ ಎಲೀಯನು, “ನಾನು ದೈವಪುರುಷನಾಗಿ ಇರುವುದು ನಿಜವಾದರೆ ಆಕಾಶದಿಂದ ಬೆಂಕಿಬಿದ್ದು ನಿನ್ನನ್ನೂ ನಿನ್ನ ಐವತ್ತುಮಂದಿ ಸಿಪಾಯಿಗಳನ್ನೂ ದಹಿಸಿಬಿಡಲಿ,” ಎಂದನು. ಕೂಡಲೆ ಆಕಾಶದಿಂದ ಬೆಂಕಿಬಿದ್ದು ಅವನನ್ನೂ ಅವನ ಐವತ್ತುಮಂದಿ ಸಿಪಾಯಿಗಳನ್ನೂ ದಹಿಸಿಬಿಟ್ಟಿತು.",
			"11": "<sup>11</sup> ಆಮೇಲೆ ಅರಸನು ಇನ್ನೊಬ್ಬ ಪಂಶಾಶದಧಿಪತಿಯನ್ನು ಅವನ ಐವತ್ತುಮಂದಿ ಸಿಪಾಯಿಗಳ ಸಮೇತ ಕಳುಹಿಸಿದನು. ಇವನು ಬಂದು ಎಲೀಯನಿಗೆ, “ದೈವಪುರುಷರೇ, ಬೇಗನೆ ಇಳಿದು ಬನ್ನಿ; ಅರಸರು ನಿಮ್ಮನ್ನು ಕರೆಯುತ್ತಾರೆ,” ಎಂದು ಹೇಳಿದನು.",
			"12": "<sup>12</sup> ಅದಕ್ಕೆ ಎಲೀಯನು, “ನಾನು ದೈವಪುರುಷ ಹೌದಾದರೆ ಆಕಾಶದಿಂದ ಬೆಂಕಿಬಿದ್ದು ನಿನ್ನನ್ನೂ ನಿನ್ನ ಐವತ್ತುಮಂದಿ ಸಿಪಾಯಿಗಳನ್ನೂ ದಹಿಸಿಬಿಡಲಿ,” ಎಂದನು. ಕೂಡಲೆ ದೇವರು ಆಕಾಶದಿಂದ ಕಳುಹಿಸಿದ ಬೆಂಕಿ ಬಂದು ಅವನನ್ನೂ ಅವನ ಐವತ್ತುಮಂದಿ ಸಿಪಾಯಿಗಳನ್ನೂ ಸುಟ್ಟುಬಿಟ್ಟಿತು.",
			"13": "<sup>13</sup> ಅರಸನು ಮತ್ತೊಬ್ಬ ಪಚಾಶದಧಿಪತಿಯನ್ನು ಅವನ ಐವತ್ತುಮಂದಿ ಸಿಪಾಯಿಗಳೊಡನೆ ಕಳುಹಿಸಿದನು. ಇವನು ಬಂದು, ಎಲೀಯನ ಮುಂದೆ ಮೊಣಕಾಲೂರಿ, “ದೈವಪುರುಷರೇ, ನನ್ನ ಮತ್ತು ನನ್ನ ಸೇವಕರಾದ ಈ ಐವತ್ತುಮಂದಿಯ ಪ್ರಾಣ ನಿಮ್ಮ ದೃಷ್ಟಿಯಲ್ಲಿ ಮೌಲ್ಯವುಳ್ಳದ್ದಾಗಿರಲಿ;",
			"14": "<sup>14</sup> ಆಕಾಶದಿಂದ ಬೆಂಕಿಬಿದ್ದು ನನಗೆ ಮುಂದೆ ಬಂದ ಆ ಇಬ್ಬರು ಪಂಚಾಶದಧಿಪತಿಗಳನ್ನೂ ಅವರ ಸಿಪಾಯಿಗಳನ್ನೂ ದಹಿಸಿಬಿಟ್ಟಿತಲ್ಲವೆ? ನನ್ನ ಪ್ರಾಣವಾದರೂ ನಿಮ್ಮ ದೃಷ್ಟಿಯಲ್ಲಿ ಮೌಲ್ಯವುಳ್ಳದೆಂದು ಮಾನ್ಯವಾಗಲಿ,” ಎಂದು ಬೇಡಿಕೊಂಡನು.",
			"15": "<sup>15</sup> ಆಗ ಸರ್ವೇಶ್ವರನ ದೂತನು ಎಲೀಯನಿಗೆ, “ನೀನು ಇವನ ಸಂಗಡ ಹೋಗು; ಹೆದರಬೇಡ,” ಎಂದು ಹೇಳಿದನು. ಆದ್ದರಿಂದ ಎಲೀಯನು ಎದ್ದು ಇವನ ಜೊತೆಯಲ್ಲೇ ಅರಸನ ಬಳಿಗೆ ಹೋದನು.",
			"16": "<sup>16</sup> ಅರಸನಿಗೆ, “ನೀವು ಎಕ್ರೋನಿನ ದೇವರಾದ ಬಾಳ್ಜೆಬೂಬನನ್ನು ವಿಚಾರಿಸುವುದಕ್ಕೆ ದೂತರನ್ನು ಕಳುಹಿಸಿದ್ದೇಕೆ? ಇಸ್ರಯೇಲರಲ್ಲಿ ದೇವರಿಲ್ಲವೆ? ಹೀಗೆ ಮಾಡಿದ್ದರಿಂದ ನೀವು ಹಿಡಿದ ಹಾಸಿಗೆಯಿಂದ ಏಳದೆ ಸಾಯಲೇಬೇಕು ಎಂಬುದಾಗಿ ಸರ್ವೇಶ್ವರ ಹೇಳಿದ್ದಾರೆ,” ಎಂದನು.",
			"17": "<sup>17</sup> ಸರ್ವೇಶ್ವರ ಎಲೀಯನ ಮುಖಾಂತರ ಮುಂತಿಳಿಸಿದಂತೆಯೇ ಅರಸನು ಸತ್ತುಹೋದನು. ಅವನಿಗೆ ಮಕ್ಕಳಿರಲಿಲ್ಲ. ಅವನ ಸ್ಥಾನದಲ್ಲಿ ಅವನ ತಮ್ಮ ಯೋರಾಮನು, ಯೆಹೂದ್ಯರ ಅರಸ ಯೆಹೋಷಾಫಾಟನ ಮಗ ಯೆಹೋರಾಮನ ಅಳ್ವಿಕೆಯ ಎರಡನೆಯ ವರ್ಷದಲ್ಲಿ, ಅರಸನಾದನು.",
			"18": "<sup>18</sup> ಅಹಜ್ಯನ ಉಳಿದ ಚರಿತ್ರೆ ಹಾಗು ಕಾರ್ಯಕಲಾಪಗಳು ಇಸ್ರಯೇಲ್ ಅರಸುಗಳ ಇತಿಹಾಸ ಗ್ರಂಥದಲ್ಲಿ ಲಿಖಿತವಾಗಿವೆ."
		},
		"2": {
			"1": "<sup>1</sup> ಸರ್ವೇಶ್ವರಸ್ವಾಮಿ ಎಲೀಯನನ್ನು ಸುಂಟರಗಾಳಿಯಲ್ಲಿ ಪರಲೋಕಕ್ಕೆ ಸೇರಿಸುವ ಸಮಯ ಬಂದಿತು. ಆಗ ಎಲೀಯನು ಎಲೀಷನೊಡನೆ ಗಿಲ್ಗಾಲನ್ನು ಬಿಟ್ಟು ಹೊರಟನು.",
			"2": "<sup>2</sup> ದಾರಿಯಲ್ಲಿ ಎಲೀಯನು ಎಲೀಷನಿಗೆ, “ದಯವಿಟ್ಟು ನೀನು ಇಲ್ಲೇ ಇರು; ಸರ್ವೇಶ್ವರ ನನಗೆ ಬೇತೇಲಿಗೆ ಹೋಗಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿದ್ದಾರೆ,” ಎಂದನು. ಎಲೀಷನು, “ಸರ್ವೇಶ್ವರನ ಆಣೆ, ನಿಮ್ಮ ಜೀವದಾಣೆ, ನಾನು ನಿಮ್ಮನ್ನು ಬಿಟ್ಟಿರಲಾರೆ,” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು. ಬಳಿಕ ಅವರಿಬ್ಬರೂ ಬೇತೇಲಿಗೆ ಹೋದರು.",
			"3": "<sup>3</sup> ಬೇತೇಲಿನ ಪ್ರವಾದಿಮಂಡಲಿಯವರು ಎಲೀಷನನ್ನು ಭೇಟಿಯಾದರು. “ಸರ್ವೇಶ್ವರ ಈ ದಿನ ನಿಮ್ಮ ಗುರುವನ್ನು ನಿಮ್ಮ ಬಳಿಯಿಂದ ಮೇಲಕ್ಕೆ ತೆಗೆದುಕೊಳ್ಳುವರೆಂಬುದು ನಿಮಗೆ ಗೊತ್ತೇ?” ಎಂದು ಕೇಳಿದರು. ಅದಕ್ಕೆ ಎಲೀಷನು, “ನನಗೆ ಗೊತ್ತಿದೆ; ಆದರೆ ನೀವು ಸುಮ್ಮನಿರಿ,” ಎಂದನು.",
			"4": "<sup>4</sup> ಎಲೀಯನು ಎಲೀಷನಿಗೆ, “ದಯವಿಟ್ಟು ಇಲ್ಲೇ ಇರು; ಸರ್ವೇಶ್ವರ ನನಗೆ ಜೆರಿಕೋವಿಗೆ ಹೋಗಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿದ್ದಾರೆ,” ಎಂದು ಹೇಳಿದನು. ಆದರೆ ಎಲೀಷನು, “ಸರ್ವೇಶ್ವರನಾಣೆ, ನಿಮ್ಮ ಜೀವದಾಣೆ, ನಾನು ನಿಮ್ಮನ್ನು ಬಿಟ್ಟಿರುವುದಿಲ್ಲ,” ಎಂದು ಉತ್ತರಕೊಟ್ಟು ಅವನ ಜೊತೆಯಲ್ಲೇ ಜೆರಿಕೋವಿಗೆ ಹೋದನು.",
			"5": "<sup>5</sup> ಅಲ್ಲಿನ ಪ್ರವಾದಿಮಂಡಲಿಯವರು ಎಲೀಷನನ್ನು ಭೇಟಿಯಾಗಿ, “ಸರ್ವೇಶ್ವರ ಈ ದಿನ ನಿಮ್ಮ ಗುರುವನ್ನು ನಿಮ್ಮ ಬಳಿಯಿಂದ ಮೇಲಕ್ಕೆ ತೆಗೆದುಕೊಳ್ಳುವರು ಎಂಬುದು ನಿಮಗೆ ತಿಳಿದಿದೆಯೊ?” ಎಂದು ಕೇಳಿದರು. ಅದಕ್ಕೆ ಅವನು, “ತಿಳಿದಿದೆ; ಆದರೆ ನೀವು ಸುಮ್ಮನಿರಿ,” ಎಂದು ಉತ್ತರಿಸಿದನು.",
			"6": "<sup>6</sup> ಎಲೀಯನು ಮತ್ತೆ ಎಲೀಷನಿಗೆ, “ದಯವಿಟ್ಟು ನೀನು ಇಲ್ಲೆಯಿರು; ಸರ್ವೇಶ್ವರ ನನಗೆ ಜೋರ್ಡನಿಗೆ ಹೋಗಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿ ಇದ್ದಾರೆ,” ಎಂದನು. ಆಗ ಅವನು, “ಸರ್ವೇಶ್ವರನ ಆಣೆ, ನಿಮ್ಮ ಜೀವದಾಣೆ, ನಾನು ನಿಮ್ಮನ್ನು ಬಿಟ್ಟಿರಲಾಗದು,” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು. ಆದುದರಿಂದ ಇಬ್ಬರೂ ಹೊರಟು ಜೋರ್ಡನ್ ನದಿಯ ದಡಕ್ಕೆ ಬಂದರು.",
			"7": "<sup>7</sup> ಪ್ರವಾದಿಮಂಡಲಿಯವರಲ್ಲಿ ಐವತ್ತುಮಂದಿ ಇವರ ಹಿಂದಿನಿಂದಲೇ ಬಂದು ಸ್ವಲ್ಪ ದೂರದಲ್ಲಿ ನಿಂತುಕೊಂಡರು.",
			"8": "<sup>8</sup> ಅನಂತರ ಎಲೀಯನು ತನ್ನ ಕಂಬಳಿಯನ್ನು ಮಡಿಚಿ, ಅದರಿಂದ ನೀರನ್ನು ಹೊಡೆದನು. ನೀರು ಇಬ್ಭಾಗವಾಯಿತು; ಅವರಿಬ್ಬರೂ ಒಣ ನೆಲದ ಮೇಲೆ ದಾಟಿಹೋದರು.",
			"9": "<sup>9</sup> ಅವರು ಆಚೆ ದಡಕ್ಕೆ ಸೇರಿದ ಮೇಲೆ ಎಲೀಯನು ಎಲೀಷನನ್ನು, “ನಿನ್ನನ್ನು ಬಿಟ್ಟುಹೋಗುವ ಮೊದಲು ನಾನು ನಿನಗೋಸ್ಕರ ಏನು ಮಾಡಬೇಕನ್ನುತ್ತಿ ಹೇಳು?” ಎಂದು ಕೇಳಿದನು. ಅದಕ್ಕೆ ಎಲೀಷನು, “ನಿಮಗಿರುವ ಆತ್ಮಶಕ್ತಿಯಲ್ಲಿ ನನಗೆ ಎರಡು ಪಾಲನ್ನು ಅನುಗ್ರಹಿಸಿ,” ಎಂದು ಬೇಡಿಕೊಂಡನು.",
			"10": "<sup>10</sup> ಆಗ ಎಲೀಯನು, “ನೀನು ದುರ್ಲಭವಾದದ್ದನ್ನು ಕೇಳಿಕೊಂಡೆ; ಆದರೂ ನನ್ನನ್ನು ನಿನ್ನ ಬಳಿಯಿಂದ ತೆಗೆಯಲಾಗುವಾಗ ನೀನು ನನ್ನನ್ನು ನೋಡುವುದಾದರೆ ಅದು ದೊರಕುವುದು; ಇಲ್ಲವಾದರೆ ದೊರಕುವುದಿಲ್ಲ,” ಎಂದನು.",
			"11": "<sup>11</sup> ಅವರು ಮಾತನಾಡುತ್ತಾ ಮುಂದೆ ಹೋಗುತ್ತಿರುವಾಗ ಫಕ್ಕನೆ ಅಗ್ನಿಮಯವಾದ ರಥರಥಾಶ್ವಗಳು ನಡುವೆ ಬಂದು ಅವರನ್ನು ವಿಂಗಡಿಸಿದವು. ಎಲೀಯನು ಸುಂಟರಗಾಳಿಯಲ್ಲಿ ಪರಲೋಕಕ್ಕೆ ಹೋದನು.",
			"12": "<sup>12</sup> ಎಲೀಷನು ಅದನ್ನು ನೋಡುತ್ತಾ, “ನನ್ನ ತಂದೆಯೇ, ನನ್ನ ತಂದೆಯೇ, ಇಸ್ರಯೇಲರಿಗೆ ರಥರಥಾಶ್ವಗಳಾಗಿದ್ದವರೇ,” ಎಂದು ಕೂಗಿಕೊಂಡನು. ಎಲೀಯನು ತನಗೆ ಕಾಣಿಸದೆ ಹೋದಮೇಲೆ ದುಃಖದಿಂದ ತನ್ನ ಬಟ್ಟೆಗಳನ್ನು ಹರಿದು ಎರಡು ತುಂಡುಮಾಡಿದನು.",
			"13": "<sup>13</sup> ಅನಂತರ ಅವನು ಮೇಲಿನಿಂದ ಬಿದ್ದ ಎಲೀಯನ ಕಂಬಳಿಯನ್ನು ತೆಗೆದುಕೊಂಡು ಜೋರ್ಡನ್ ನದೀತೀರಕ್ಕೆ ಬಂದನು.",
			"14": "<sup>14</sup> “ಎಲೀಯನ ದೇವರಾದ ಸರ್ವೇಶ್ವರನೆಲ್ಲಿ?” ಎಂದುಕೊಂಡು ಆ ಕಂಬಳಿಯಿಂದ ನೀರನ್ನು ಹೊಡೆದನು. ಅದು ಇಬ್ಭಾಗ ಆಯಿತು. ಅವನು ದಾಟಿಹೋದನು.",
			"15": "<sup>15</sup> ದೂರದಲ್ಲಿ ನಿಂತಿದ್ದ ಜೆರಿಕೋವಿನ ಪ್ರವಾದಿಮಂಡಲಿಯವರು ಇದನ್ನು ಕಂಡು, “ಎಲೀಯನಿಗಿದ್ದ ಆತ್ಮಶಕ್ತಿ ಎಲೀಷನ ಮೇಲೆ ಬಂದು ನೆಲಸಿದೆ,” ಎಂದು ತಿಳಿದುಕೊಂಡರು. ಅವನನ್ನು ಎದುರುಗೊಂಡು ಅವನಿಗೆ ಸಾಷ್ಟಾಂಗ ನಮಸ್ಕಾರ ಮಾಡಿದರು.",
			"16": "<sup>16</sup> ಅನಂತರ ಅವರು ಅವನಿಗೆ, “ನಿಮ್ಮ ಸೇವಕರಾದ ನಮ್ಮಲ್ಲಿ ಐವತ್ತುಮಂದಿ ಬಲಿಷ್ಠ ಜನರಿದ್ದಾರೆ; ನಿಮ್ಮ ಯಜಮಾನರನ್ನು ಹುಡುಕಲು ಹೋಗುವುದಕ್ಕೆ ಅವರಿಗೆ ಅಪ್ಪಣೆಯಾಗಲಿ. ಸರ್ವೇಶ್ವರನ ಆತ್ಮ ಅವರನ್ನು ಎತ್ತಿಕೊಂಡುಹೋಗಿ ಒಂದು ಬೆಟ್ಟದ ಮೇಲಾಗಲಿ, ತಗ್ಗಿನಲ್ಲಾಗಲಿ ಇಟ್ಟಿರಬಹುದು,” ಎಂದು ಹೇಳಿದರು. ಅವನು, “ನೀವು ಕಳುಹಿಸಬೇಡಿ,” ಎಂದನು.",
			"17": "<sup>17</sup> ಆದರೆ ಅವರು ಅವನನ್ನು ಒತ್ತಾಯಪಡಿಸಿದ್ದರಿಂದ ಅವನು ಬೇಸರಗೊಂಡು ಕಳುಹಿಸುವುದಕ್ಕೆ ಅಪ್ಪಣೆಕೊಟ್ಟನು. ಅವರು ಐವತ್ತುಮಂದಿಯನ್ನು ಕಳುಹಿಸಿದರು. ಇವರು ಮೂರು ದಿವಸ ಹುಡುಕಿದರೂ ಎಲೀಯನನ್ನು ಕಾಣಲಿಲ್ಲ.",
			"18": "<sup>18</sup> ಇನ್ನೂ ಜೆರಿಕೋವಿನಲ್ಲೇ ಇದ್ದ ಎಲೀಷನ ಬಳಿಗೆ ಅವರು ಬಂದರು. ಅವನು, “’ಹೋಗಬೇಡಿ,’ ಎಂದು ನಾನು ಹೇಳಲಿಲ್ಲವೇ?’ ಎಂದನು.",
			"19": "<sup>19</sup> ಜೆರಿಕೋವಿನ ಜನರು ಎಲೀಷ, “ನಮ್ಮ ಒಡೆಯರಾದ ನಿಮಗೆ ಗೊತ್ತಿರುವಂತೆ ಈ ಊರು ಕಟ್ಟಿಸಿರುವ ಸ್ಥಳ ಚೆನ್ನಾಗಿದೆ. ಆದರೆ ನೀರು ಕೆಟ್ಟದ್ದು. ಆದುದರಿಂದ ಗರ್ಭಪಾತ ಹೆಚ್ಚು,” ಎಂದು ಹೇಳಿದರು.",
			"20": "<sup>20</sup> ಆಗ ಎಲೀಷನು, “ಒಂದು ಹೊಸ ಮಡಕೆಯಲ್ಲಿ ಉಪ್ಪು ಹಾಕಿ ಅದನ್ನು ತಂದುಕೊಡಿ,” ಎಂದನು. ಅವರು ತಂದುಕೊಟ್ಟರು.",
			"21": "<sup>21</sup> ಅವನು ಬುಗ್ಗೆಗೆ ಹೋಗಿ ಆ ನೀರಿನಲ್ಲಿ ಉಪ್ಪುಹಾಕಿದನು; “’ಇನ್ನು ಮುಂದೆ ಈ ನೀರಿನಿಂದ ಮರಣವಾಗಲಿ ಗರ್ಭಪಾತವಾಗಲಿ ಸಂಭವಿಸದಂತೆ ಇದರಲ್ಲಿದ್ದ ದೋಷವನ್ನೆಲ್ಲಾ ಪರಿಹರಿಸಿದ್ದೇನೆ,’ ಎಂಬುದಾಗಿ ಸರ್ವೇಶ್ವರ ನುಡಿದಿದ್ದಾರೆ,” ಎಂದನು.",
			"22": "<sup>22</sup> ಕೂಡಲೆ ನೀರಿನಲ್ಲಿದ್ದ ದೋಷವೆಲ್ಲಾ ಪರಿಹಾರವಾಯಿತು. ಎಲೀಷನ ವಾಕ್ಯಬಲದಿಂದ ಅದು ಇಂದಿನವರೆಗೂ ಹಾಗೆಯೇ ಶುದ್ಧವಾಗಿದೆ.",
			"23": "<sup>23</sup> ಎಲೀಷನು ಅಲ್ಲಿಂದ ಬೇತೇಲಿಗೆ ಹೊರಟನು. ಅಲ್ಲಿನ ಗುಡ್ಡವನ್ನು ಹತ್ತುತ್ತಿರುವಾಗ ಆ ಊರಿನ ಹುಡುಗರು ಹೊರಗೆ ಬಂದರು. “ಬೋಳು ತಲೆಯವನೇ, ಏರು; ಬೋಳುತಲೆಯವನೇ ಏರು,” ಎಂದು ಕೂಗಿ ಅವನನ್ನು ಅಣಕಿಸಿದರು.",
			"24": "<sup>24</sup> ಎಲೀಷನು ಅವರ ಕಡೆಗೆ ತಿರುಗಿ, ಸರ್ವೇಶ್ವರನ ಹೆಸರಿನಲ್ಲಿ ಅವರನ್ನು ಶಪಿಸಿದನು. ಕೂಡಲೆ ಕಾಡಿನಿಂದ ಎರಡು ಹೆಣ್ಣುಕರಡಿಗಳು ಬಂದು ಆ ಹುಡುಗರಲ್ಲಿ ನಾಲ್ವತ್ತೆರಡು ಮಂದಿಯನ್ನು ಸೀಳಿಬಿಟ್ಟವು.",
			"25": "<sup>25</sup> ಎಲೀಷನು ಅಲ್ಲಿಂದ ಕಾರ್ಮೆಲ್ ಬೆಟ್ಟಕ್ಕೆ ಹೋಗಿ ಅನಂತರ ಸಮಾರಿಯಕ್ಕೆ ಬಂದನು."
		},
		"3": {
			"1": "<sup>1</sup> ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಯೆಹೋಷಾಫಾಟನ ಆಳ್ವಿಕೆಯ ಹದಿನೆಂಟನೆಯ ವರ್ಷದಲ್ಲಿ ಅಹಾಬನ ಮಗನಾದ ಯೋರಾಮನು ಇಸ್ರಯೇಲರ ಅರಸನಾಗಿ ಸಮಾರಿಯದಲ್ಲಿ ಹನ್ನೆರಡು ವರ್ಷ ಆಳಿದನು.",
			"2": "<sup>2</sup> ಇವನು ತನ್ನ ತಂದೆ ತಾಯಿಗಳಷ್ಟು ದುಷ್ಟನಾಗಿರಲಿಲ್ಲ. ಆದರೂ ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾಗಿದ್ದನು. ತನ್ನ ತಂದೆ ಬಾಳ್ ದೇವತೆಯ ಗೌರವಾರ್ಥ ನಿಲ್ಲಿಸಿದ ಕಲ್ಲಿನ ಕಂಬವನ್ನು ತೆಗೆದುಹಾಕಿದನು.",
			"3": "<sup>3</sup> ಆದರೂ ಇಸ್ರಯೇಲರನ್ನು ಪಾಪಕ್ಕೆ ಪ್ರೇರಿಸಿದ ನೆಬಾಟನ ಮಗ ಯಾರೊಬ್ಬಾಮನ ಮಾರ್ಗವನ್ನು ಬಿಡಲೇ ಇಲ್ಲ.",
			"4": "<sup>4</sup> ಅನೇಕ ಕುರಿಹಿಂಡುಗಳುಳ್ಳವನಾಗಿದ್ದ ಮೋವಾಬ್ಯರ ಅರಸ ಮೇಷನೆಂಬವನು ಇಸ್ರಯೇಲರ ಅರಸನಿಗೆ ಒಂದು ಲಕ್ಷ ಕುರಿಗಳ ಮತ್ತು ಒಂದು ಲಕ್ಷ ಟಗರುಗಳ ಉಣ್ಣೆಯನ್ನು ಕಪ್ಪವಾಗಿ ಕೊಡುತ್ತಿದ್ದನು.",
			"5": "<sup>5</sup> ಆದರೆ ಅಹಾಬನು ಮರಣ ಹೊಂದಿದ ನಂತರ ಇವನು ಇಸ್ರಯೇಲರ ಅರಸನಿಗೆ ವಿರುದ್ಧ ದಂಗೆಯೆದ್ದನು.",
			"6": "<sup>6</sup> ಆದುದರಿಂದ ಅರಸನಾದ ಯೋರಾಮನು ಒಡನೆ ಇಸ್ರಯೇಲರನ್ನು ಕೂಡಿಸಿಕೊಂಡು ಸಮಾರಿಯದಿಂದ ಹೊರಟನು;",
			"7": "<sup>7</sup> ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಯೆಹೋಷಾಫಾಟನಿಗೆ ದೂತರ ಮುಖಾಂತರ, “ಮೋವಾಬ್ಯರ ಅರಸನು ನನಗೆ ವಿರುದ್ಧ ದಂಗೆ ಎದ್ದಿದ್ದಾನೆ; ಮೋವಾಬರೊಡನೆ ಯುದ್ಧಮಾಡುವುದಕ್ಕೆ ನೀನೂ ನನ್ನ ಜೊತೆಯಲ್ಲಿ ಬರುತ್ತೀಯೋ?” ಎಂದು ಹೇಳಿ ಕಳುಹಿಸಿದನು. ಅದಕ್ಕೆ ಯೆಹೋಷಾಫಾಟನು, “ಬರುತ್ತೇನೆ; ನಾನೂ ನೀನೂ, ನನ್ನ ಜನರೂ ನಿನ್ನ ಜನರೂ ನನ್ನ ಕುದುರೆಗಳೂ ನಿನ್ನ ಕುದುರೆಗಳೂ ಒಂದೇ ಅಲ್ಲವೇ?” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"8": "<sup>8</sup> ಅದೂ ಅಲ್ಲದೆ, “ಯಾವ ಮಾರ್ಗವಾಗಿ ಬರಬೇಕು?” ಎಂದು ವಿಚಾರಿಸಿದನು. ಅದಕ್ಕೆ ಯೋರಾಮನು ಎದೋಮ್ಯರ ಅರಣ್ಯಮಾರ್ಗವಾಗಿ ಬರಬೇಕೆಂದನು.",
			"9": "<sup>9</sup> ಆಗ ಇಸ್ರಯೇಲ್, ಜುದೇಯ ಹಾಗು ಎದೋಮ್ ರಾಜ್ಯಗಳ ಅರಸರು ಹೊರಟು ಸುತ್ತಾದ ದಾರಿಯಿಂದ ಏಳು ದಿವಸ ಪ್ರಯಾಣ ಮಾಡಿದರು. ಮಾರ್ಗದಲ್ಲಿ ಅವರ ಸೈನಿಕರಿಗೂ ಅವರು ತಂದಿದ್ದ ಪಶುಗಳಿಗೂ ನೀರು ಸಿಕ್ಕದೆಹೋಯಿತು.",
			"10": "<sup>10</sup> ಆದ್ದರಿಂದ ಇಸ್ರಯೇಲರ ಅರಸನು, “ಅಯ್ಯೋ, ಮೂರು ಮಂದಿ ಅರಸರಾದ ನಮ್ಮನ್ನು ಮೋವಾಬ್ಯರ ಕೈಗೆ ಒಪ್ಪಿಸುವುದಕ್ಕಾಗಿ ಸರ್ವೇಶ್ವರ ಇಲ್ಲಿಗೆ ಬರಮಾಡಿದ್ದಾರೆ,” ಎಂದು ಪರಿತಪಿಸಿದನು.",
			"11": "<sup>11</sup> ಆದರೆ ಯೆಹೋಷಾಫಾಟನು, “ಸರ್ವೇಶ್ವರನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನಮ್ಮ ಪರವಾಗಿ ವಿಚಾರಿಸಬಲ್ಲ ಪ್ರವಾದಿ ಇಲ್ಲಿ ಯಾರೂ ಇಲ್ಲವೇ?” ಎಂದು ಕೇಳಿದನು. ಆಗ ಇಸ್ರಯೇಲ್ ಅರಸನ ಸೇವಕರಲ್ಲೊಬ್ಬನು, “ಶಾಫಾಟನ ಮಗನೂ ಎಲೀಯನ ಕೈಗೆ ನೀರು ಕೊಡುತ್ತಿದ್ದವನೂ ಆದ ಎಲೀಷನೆಂಬ ಪ್ರವಾದಿ ಇಲ್ಲಿರುತ್ತಾನೆ,” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"12": "<sup>12</sup> ಯೆಹೋಷಾಫಾಟನು, “ಅವನು ಹೇಗೂ ಸರ್ವೇಶ್ವರನ ಉತ್ತರವನ್ನು ತಿಳಿಸುವನು,” ಎಂದದ್ದರಿಂದ ಇಸ್ರಯೇಲ್ ಹಾಗು ಎದೋಮ್ ರಾಜ್ಯಗಳ ಅರಸರು ಯೆಹೋಷಾಫಾಟನೊಡನೆ ಎಲೀಷನ ಬಳಿಗೆ ಬಂದರು.",
			"13": "<sup>13</sup> ಎಲೀಷನು ಇಸ್ರಯೇಲರ ಅರಸನಿಗೆ, “ನನಗೂ ನಿನಗೂ ಏನು ಸಂಬಂಧ? ನಿನ್ನ ತಂದೆತಾಯಿಗಳ ಪ್ರವಾದಿಗಳ ಬಳಿಗೆ ಹೋಗು,” ಎಂದು ಹೇಳಿದನು. ಅದಕ್ಕೆ ಇಸ್ರಯೇಲರ ಅರಸನು, “ಹಾಗೆನ್ನಬೇಡಿ; ಸರ್ವೇಶ್ವರ ಈ ಮೂರು ಮಂದಿ ಅರಸರಾದ ನಮ್ಮನ್ನು ಮೋವಾಬ್ಯರ ಕೈಗೆ ಒಪ್ಪಿಸುವುದಕ್ಕಾಗಿ ಇಲ್ಲಿಗೆ ಬರಮಾಡಿದ್ದಾರೆ ಅಲ್ಲವೇ?’ ಎಂದನು.",
			"14": "<sup>14</sup> ಆಗ ಎಲೀಷನು, “ನಾನು ಸನ್ನಿಧಿಸೇವೆ ಮಾಡುತ್ತಿರುವ ಸರ್ವೇಶ್ವರನಾಣೆ, ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಯೆಹೋಷಾಫಾಟನು ನಿಮ್ಮ ಜೊತೆಯಲ್ಲಿರದಿದ್ದರೆ ನಾನು ನಿಮ್ಮನ್ನು ನೋಡುತ್ತಿರಲಿಲ್ಲ; ಲಕ್ಷಿಸುತ್ತಲೂ ಇರಲಿಲ್ಲ.",
			"15": "<sup>15</sup> ಈಗ ಒಬ್ಬ ವಾದ್ಯಗಾರನನ್ನು ನನ್ನ ಬಳಿಗೆ ಕರೆದುಕೊಂಡು ಬನ್ನಿ,” ಎಂದು ಹೇಳಿದನು. (ವಾದ್ಯಗಳ ಸ್ವರವನ್ನು ಕೇಳುವಾಗಲೆಲ್ಲಾ ಎಲೀಷನಲ್ಲಿ ಸರ್ವೇಶ್ವರನ ಶಕ್ತಿ ಪ್ರಾಪ್ತವಾಗುತ್ತಿತ್ತು).",
			"16": "<sup>16</sup> ಅನಂತರ ಎಲೀಷನು ಇಸ್ರಯೇಲರ ಅರಸನಿಗೆ, “ಈ ಹಳ್ಳದ ತುಂಬಾ ಗುಂಡಿಗಳನ್ನು ಮಾಡಿರಿ;",
			"17": "<sup>17</sup> ಗಾಳಿಮಳೆಗಳು ಕಾಣದಿದ್ದರೂ ಇದು ನೀರಿನಿಂದ ತುಂಬುವುದು. ನೀವೂ ನಿಮ್ಮ ಕುರಿದನಗಳೂ ಎಲ್ಲಾ ಪಶುಪ್ರಾಣಿಗಳೂ ಕುಡಿಯಬಹುದು ಎಂಬುದಾಗಿ ಸರ್ವೇಶ್ವರ ನುಡಿಯುತ್ತಾರೆ.",
			"18": "<sup>18</sup> ಅವರು ತಮ್ಮ ದೃಷ್ಟಿಯಲ್ಲಿ ಅಲ್ಪವಾಗಿರುವ ಈ ಕಾರ್ಯ ಮಾತ್ರವಲ್ಲ, ಮೋವಾಬ್ಯರನ್ನೂ ನಿಮ್ಮ ಕೈಗೆ ಒಪ್ಪಿಸುವರು.",
			"19": "<sup>19</sup> ನೀವು ಅವರ ಕೋಟೆಕೊತ್ತಲುಗಳುಳ್ಳ ಪಟ್ಟಣಗಳನ್ನೂ ವಿಶೇಷವಾದ ಎಲ್ಲಾ ಊರುಗಳನ್ನೂ ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳುವಿರಿ. ಎಲ್ಲ ಹಣ್ಣಿನ ಮರಗಳನ್ನು ಕಡಿದುಹಾಕಿ, ಬಾವಿಗಳನ್ನು ಮುಚ್ಚಿಬಿಟ್ಟು, ಹೊಲಗಳನ್ನು ಕಲ್ಲು ತುಂಬಿಸಿ ಹಾಳುಮಾಡುವಿರಿ,” ಎಂದು ನುಡಿದನು.",
			"20": "<sup>20</sup> ಪ್ರಾತಃಕಾಲದ ನೈವೇದ್ಯವನ್ನು ಸಮರ್ಪಿಸುವ ಹೊತ್ತಿನಲ್ಲಿ ಫಕ್ಕನೆ ಎದೋಮಿನ ಕಡೆಯಿಂದ ನೀರು ಬಂದು ನಾಡಿನಲ್ಲೆಲ್ಲಾ ತುಂಬಿಕೊಂಡಿತು.",
			"21": "<sup>21</sup> ಅರಸುಗಳು ತಮಗೆ ವಿರುದ್ಧ ಯುದ್ಧಕ್ಕೆ ಬಂದಿದ್ದಾರೆಂಬುದನ್ನು ಮೋವಾಬ್ಯರು ಕೇಳಿದರು. ಆಯುಧಗಳನ್ನು ಧರಿಸಲು ಶಕ್ತರಾದ ಎಲ್ಲ ಯೌವನಸ್ಥರನ್ನೂ ಪ್ರಾಯಸ್ಥರನ್ನೂ ಕೂಡಿಸಿಕೊಂಡು ತಮ್ಮ ದೇಶದ ಗಡಿಗೆ ಬಂದರು.",
			"22": "<sup>22</sup> ಬೆಳಿಗ್ಗೆ ಎದ್ದು ನೋಡುವಾಗ ಎದುರಿಗಿದ್ದ ನೀರು ಇವರ ದೃಷ್ಟಿಗೆ ಬಿದ್ದಿತು. ಸೂರ್ಯಪ್ರಕಾಶದಿಂದ ಆ ನೀರು ರಕ್ತದಂತೆ ಕೆಂಪಾಗಿ ಕಾಣಿಸುತ್ತಿತ್ತು.",
			"23": "<sup>23</sup> ಆದ್ದರಿಂದ, “ಅದು ರಕ್ತ; ಅರಸರು ತಮ್ಮ ತಮ್ಮೊಳಗೆ ಜಗಳವಾಡಿ ಹತರಾಗಿರಬೇಕು; ಮೋವಾಬ್ಯರೇ, ಏಳಿ, ಸುಲಿಗೆಗೆ ಹೋಗೋಣ,” ಎಂದು ಕೂಗಿಕೊಂಡು ಇಸ್ರಯೇಲರ ಪಾಳೆಯಕ್ಕೆ ಹೋದರು.",
			"24": "<sup>24</sup> ಆದರೆ ಇಸ್ರಯೇಲರು ಎದ್ದು ಮೋವಾಬ್ಯರನ್ನು ಸೋಲಿಸಿ ಓಡಿಸಿಬಿಟ್ಟರು. ಇದಲ್ಲದೆ, ಅವರು ಮೋವಾಬ್ಯರನ್ನು ಸಂಹರಿಸುತ್ತಾ, ಅವರ ದೇಶದೊಳಗೆ ನುಗ್ಗಿದರು;",
			"25": "<sup>25</sup> ಎಲ್ಲಾ ಪಟ್ಟಣಗಳನ್ನು ಕೆಡವಿ, ಹೊಲಗಳನ್ನು ಕಲ್ಲುಗಳಿಂದ ತುಂಬಿಸಿ, ಬಾವಿಗಳನ್ನು ಮುಚ್ಚಿ, ಹಣ್ಣಿನ ಮರಗಳನ್ನು ಕಡಿದುಹಾಕಿದರು. ಕೀರ್ಹರೆಷೆತ್ ಎಂಬ ಒಂದೇ ಒಂದು ಮಾರ್ಗ ಉಳಿಯಿತು. ಕವಣೆ ಹೊಡೆಯುವವರು ಅದನ್ನೂ ಸುತ್ತಿಕೊಂಡು ಕಲ್ಲೆಸೆದರು.",
			"26": "<sup>26</sup> ಮೋವಾಬ್ಯರ ಅರಸನು ತಾನು ಯುದ್ಧದಲ್ಲಿ ಗೆಲ್ಲಲಾರೆನೆಂದು ತಿಳಿದು ಏಳುನೂರು ಮಂದಿ ಯೋಧರೊಡನೆ ಸೈನ್ಯದೊಳಗೆ ನುಗ್ಗಿ ಎದೋಮ್ಯರ ಅರಸನ ಕಡೆಗೆ ಹೋಗಪ್ರಯತ್ನಿಸಿದನು; ಆದರೆ ಆಗದೆ ಹೋಯಿತು.",
			"27": "<sup>27</sup> ಆಗ ಅವನು ತನಗೆ ಬದಲಾಗಿ ಅರಸನಾಗತಕ್ಕ ತನ್ನ ಚೊಚ್ಚಲ ಮಗನನ್ನು ಹಿಡಿದು, ವಧಿಸಿ, ಗೋಡೆಯ ಮೇಲೆ ದಹನಬಲಿ ಮಾಡಿದನು. ಇದರಿಂದ ಮೋವಾಬ್ಯರಿಗೆ ಇಸ್ರಯೇಲರ ಮೇಲೆ ಕೋಪೋದ್ರೇಕವುಂಟಾಯಿತು. ಅವರು ಅವನನ್ನು ಬಿಟ್ಟು ತಮ್ಮ ದೇಶಕ್ಕೆ ಹೊರಟುಹೋದರು."
		},
		"4": {
			"1": "<sup>1</sup> ಒಂದು ದಿನ ಪ್ರವಾದಿಮಂಡಲಿಯವರಲ್ಲಿ ಒಬ್ಬನ ಹೆಂಡತಿ ಎಲೀಷನನ್ನು ಭೇಟಿಯಾದಳು. ಅವನಿಗೆ, “ನಿಮ್ಮ ಸೇವಕನಾದ ನನ್ನ ಗಂಡ ಮರಣಹೊಂದಿದನು. ಅವನು ಸರ್ವೇಶ್ವರನಲ್ಲಿ ಭಯಭಕ್ತಿ ಉಳ್ಳವನಾಗಿದ್ದನೆಂಬುದು ನಿಮಗೆ ತಿಳಿದ ವಿಷಯ. ಸಾಲಕೊಟ್ಟವನು ನನ್ನ ಇಬ್ಬರು ಮಕ್ಕಳನ್ನು ಗುಲಾಮರನ್ನಾಗಿ ತೆಗೆದುಕೊಂಡು ಹೋಗುವುದಕ್ಕೆ ಬಂದಿದ್ದಾನೆ,” ಎಂದು ಮೊರೆಯಿಟ್ಟಳು.",
			"2": "<sup>2</sup> ಎಲೀಷನು, “ನಾನು ನಿನಗೇನು ಮಾಡಬೇಕೆನ್ನುತ್ತೀ? ನಿನ್ನ ಮನೆಯಲ್ಲಿ ಏನಿದೆ, ಹೇಳು,” ಎಂದನು. ಅದಕ್ಕೆ ಆಕೆ, “ನಿಮ್ಮ ದಾಸಿಯ ಮನೆಯಲ್ಲಿ ಒಂದು ಮೊಗೆ ಎಣ್ಣೆ ಹೊರತಾಗಿ ಏನೂ ಇಲ್ಲ,” ಎಂದು ಉತ್ತರಕೊಟ್ಟಳು.",
			"3": "<sup>3</sup> ಆಗ ಎಲೀಷನು, “ಹೋಗಿ ನಿನ್ನ ನೆರೆಯವರಿಂದ ಸಿಕ್ಕುವಷ್ಟು ಬರಿದಾದ ಪಾತ್ರೆಗಳನ್ನು ಕೇಳಿ ತೆಗೆದುಕೊಂಡು ಬಾ.",
			"4": "<sup>4</sup> ಅನಂತರ ನಿನ್ನ ಮಕ್ಕಳನ್ನು ಒಳಗೆ ಕರೆದುಕೊಂಡು ಬಾಗಿಲನ್ನು ಮುಚ್ಚಿ ಎಣ್ಣೆಯನ್ನು ಆ ಪಾತ್ರೆಗಳಿಗೆ ಹೊಯ್ಯಿ, ತುಂಬಿದ ಆ ಪಾತ್ರೆಗಳನ್ನೆಲ್ಲಾ ಒಂದು ಕಡೆಯಿಡು,” ಎಂದು ಹೇಳಿದನು.",
			"5": "<sup>5</sup> ಆಕೆ ಹೋಗಿ ಮಕ್ಕಳನ್ನು ಒಳಗೆ ಕರೆದುಕೊಂಡು ಬಾಗಿಲನ್ನು ಮುಚ್ಚಿ, ಅವರು ಮುಂದಿಟ್ಟ ಪಾತ್ರೆಗಳಲ್ಲೆಲ್ಲಾ ಎಣ್ಣೆ ಹೊಯ್ದಳು.",
			"6": "<sup>6</sup> ಪಾತ್ರೆಗಳು ತುಂಬಿದಾಗ, ಆಕೆ ತನ್ನ ಮಗನಿಗೆ, “ಇನ್ನೊಂದು ಪಾತ್ರೆಯನ್ನು ತಂದಿಡು,” ಅನ್ನಲು ಅವನು, “ಪಾತ್ರೆಗಳು ತೀರಿಹೋದುವು,” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು. ಕೂಡಲೆ ಎಣ್ಣೆ ಉಕ್ಕುವುದೂ ನಿಂತುಹೋಯಿತು.",
			"7": "<sup>7</sup> ತರುವಾಯ ಆಕೆ ಆ ದೈವಪುರುಷನ ಹತ್ತಿರ ಬಂದು ನಡೆದದ್ದನ್ನೆಲ್ಲಾ ತಿಳಿಸಿದಳು. ಅವನು ಆಕೆಗೆ, “ಹೋಗಿ ಆ ಎಣ್ಣೆಯನ್ನು ಮಾರಿ ಸಾಲ ತೀರಿಸು; ಉಳಿದ ಹಣದಿಂದ ನೀನೂ ನಿನ್ನ ಮಕ್ಕಳೂ ಜೀವನ ಮಾಡಿರಿ,” ಎಂದನು.",
			"8": "<sup>8</sup> ಒಂದು ಸಾರಿ ಎಲೀಷನು ಶೂನೇಮಿಗೆ ಹೋದನು. ಅಲ್ಲಿ ಒಬ್ಬ ಶ್ರೀಮಂತ ಮಹಿಳೆ ಇದ್ದಳು. ಆಕೆ ಅವನನ್ನು ತನ್ನ ಮನೆಯಲ್ಲಿ ಊಟಮಾಡಬೇಕೆಂದು ಒತ್ತಾಯಪಡಿಸಿದಳು. ಅಂದಿನಿಂದ ಅವನು ಆ ಮಾರ್ಗವಾಗಿ ಹೋಗುವಾಗಲೆಲ್ಲಾ ಆ ಮನೆಯಲ್ಲೇ ಊಟಮಾಡುತ್ತಿದ್ದನು.",
			"9": "<sup>9</sup> ಆ ಮಹಿಳೆ ತನ್ನ ಗಂಡನಿಗೆ, “ಯಾವಾಗಲೂ ಈ ದಾರಿಯಿಂದ ಹೋಗುತ್ತಾ ಬರುತ್ತಾ ಇರುವ ಆ ವ್ಯಕ್ತಿ ಒಬ್ಬ ಸಂತನು ಹಾಗು ದೈವಪುರುಷನು ಆಗಿರುತ್ತಾನೆಂದು ನನಗೆ ಗೊತ್ತಾಯಿತು.",
			"10": "<sup>10</sup> ಆದುದರಿಂದ ನಾವು ಅವನಿಗಾಗಿ ಮಾಳಿಗೆಯ ಮೇಲೆ ಒಂದು ಸಣ್ಣ ಕೋಣೆಯನ್ನು ಕಟ್ಟಿಸೋಣ. ಅದರಲ್ಲಿ ಒಂದು ಮಂಚ, ಮೇಜು, ಕುರ್ಚಿ, ದೀಪಸ್ತಂಭ ಇವುಗಳನ್ನು ಇಡೋಣ; ಅವನು ಇಲ್ಲಿಗೆ ಬಂದಾಗೆಲ್ಲಾ ಅದರಲ್ಲಿ ವಾಸಮಾಡಲಿ,” ಎಂದು ಹೇಳಿದಳು.",
			"11": "<sup>11</sup> ಒಂದು ದಿನ ಎಲೀಷನು ಅಲ್ಲಿಗೆ ಬಂದು ಆ ಕೋಣೆಯೊಳಗೆ ಪ್ರವೇಶಿಸಿ ದಣಿವಾರಿಸಿಕೊಂಡನು.",
			"12": "<sup>12</sup> ಅನಂತರ ಆ ಶೂನೇಮ್ಯಳನ್ನು ಕರೆಯಬೇಕೆಂದು ತನ್ನ ಸೇವಕನಾದ ಗೇಹಜಿಗೆ ಆಜ್ಞಾಪಿಸಿದನು. ಅವನು ಹೋಗಿ ಆಕೆಯನ್ನು ಕರೆಯಲು ಆಕೆ ಬಂದು ಮುಂದೆ ನಿಂತಳು.",
			"13": "<sup>13</sup> ಆಗ ಎಲೀಷನು ಗೇಹಜಿಯ ಮುಖಾಂತರ ಆಕೆಯನ್ನು, “ನೀನು ಕಷ್ಟಪಟ್ಟು ನಮಗೆ ಇಷ್ಟೆಲ್ಲಾ ಉಪಕಾರಮಾಡಿದೆ; ನಾನು ನಿನಗೆ ಯಾವ ಪ್ರತ್ಯುಪಕಾರ ಮಾಡಲಿ? ನಿನ್ನ ಪರವಾಗಿ ಅರಸನ ಮುಂದಾಗಲಿ, ಸೇನಾಪತಿಯ ಮುಂದಾಗಲಿ ಮಾತಾಡಬೇಕಾದರೆ ಹೇಳು,” ಎಂದು ಕೇಳಿದನು. ಆಕೆ, “ನಾನು ಸ್ವಕುಲದವರ ಮಧ್ಯೆ ಸುಖವಾಗಿದ್ದೇನೆ,” ಎಂದು ಉತ್ತರಕೊಟ್ಟಳು.",
			"14": "<sup>14</sup> ಆಮೇಲೆ ಎಲೀಷನು ಗೇಹಜಿಯನ್ನು, “ನಾವು ಆಕೆಗೆ ಯಾವ ಉಪಕಾರ ಮಾಡಬಹುದು?” ಎಂದು ಕೇಳಿದನು. ಅವನು, “ಆಕೆಗೆ ಮಗನಿಲ್ಲ, ಗಂಡನು ಮುದುಕನಾಗಿದ್ದಾನೆ,” ಎಂದನು.",
			"15": "<sup>15</sup> ಇದನ್ನು ಕೇಳಿ ಎಲೀಷನು ಆ ಮಹಿಳೆಯನ್ನು ಕರೆಯಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿದನು. ಅವನು ಹೋಗಿ ಕರೆದನು. ಆಕೆ ಬಂದು ಬಾಗಿಲಿನ ಹತ್ತಿರ ನಿಂತಳು.",
			"16": "<sup>16</sup> ಎಲೀಷನು ಆಕೆಗೆ, “ನೀನು ಬರುವ ವರ್ಷ ಇದೇ ಕಾಲದಲ್ಲಿ ಒಬ್ಬ ಮಗನನ್ನು ಅಪ್ಪಿಕೊಂಡಿರುವೆ,” ಎಂದನು. ಅದಕ್ಕೆ ಆಕೆ, “ದೈವಪುರುಷರೇ, ಒಡೆಯರೇ, ಹೀಗೆ ಹೇಳಿ ನಿಮ್ಮ ದಾಸಿಯನ್ನು ವಂಚಿಸಬೇಡಿ,” ಎಂದು ನುಡಿದಳು.",
			"17": "<sup>17</sup> ಆ ಮಹಿಳೆ ಗರ್ಭಿಣಿಯಾಗಿ, ಎಲೀಷನು ಹೇಳಿದಂತೆ ಮುಂದಿನ ವರ್ಷ, ಅದೇ ಕಾಲದಲ್ಲಿ ಒಬ್ಬ ಮಗನನ್ನು ಹೆತ್ತಳು.",
			"18": "<sup>18</sup> ಹುಡುಗನು ದೊಡ್ಡವನಾದ ಮೇಲೆ, ಒಂದು ದಿನ ಹೊಲದಲ್ಲಿ ಬೆಳೆ ಕೊಯ್ಯುವವರ ಸಂಗಡ ಇದ್ದ ತನ್ನ ತಂದೆಯ ಬಳಿಗೆ ಹೋದನು.",
			"19": "<sup>19</sup> ಅಲ್ಲಿದ್ದಾಗ ಅವನು ಇದ್ದಕ್ಕಿದ್ದ ಹಾಗೆ, “ಅಪ್ಪಾ, ನನ್ನ ತಲೆ ಸಿಡಿಯುತ್ತಿದೆ, ಸಿಡಿಯುತ್ತಿದೆ,” ಎಂದು ಕೂಗಿಕೊಂಡನು. ತಂದೆ ಒಬ್ಬ ಸೇವಕನನ್ನು ಕರೆದು, “ಇವನನ್ನು ತಾಯಿಯ ಹತ್ತಿರ ತೆಗೆದುಕೊಂಡು ಹೋಗು,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"20": "<sup>20</sup> ಸೇವಕನು ಹುಡುಗನನ್ನು ಅವನ ತಾಯಿಗೆ ತಂದೊಪ್ಪಿಸಿದನು. ಹುಡುಗನು ಮಧ್ಯಾಹ್ನದವರೆಗೂ ತಾಯಿಯ ತೊಡೆಯ ಮೇಲೆಯೇ ಇದ್ದು ಅನಂತರ ಸತ್ತನು.",
			"21": "<sup>21</sup> ಕೂಡಲೆ ಆಕೆ ಅವನನ್ನು ದೈವಪುರುಷನ ಕೋಣೆಗೆ ತೆಗೆದುಕೊಂಡು ಹೋಗಿ, ಅವನ ಮಂಚದ ಮೇಲೆ ಮಲಗಿಸಿ, ಬಾಗಿಲು ಮುಚ್ಚಿ, ಗಂಡನನ್ನು ಕರೆಯಿಸಿದಳು.",
			"22": "<sup>22</sup> “ದಯವಿಟ್ಟು ನನಗಾಗಿ ಒಬ್ಬ ಸೇವಕನನ್ನೂ ಒಂದು ಕತ್ತೆಯನ್ನೂ ಕಳುಹಿಸಿ; ನಾನು ಬೇಗನೆ ಆ ದೈವಪುರುಷನ ಬಳಿಗೆ ಹೋಗಿ ಬರುತ್ತೇನೆ,” ಎಂದು ಕೇಳಿದಳು.",
			"23": "<sup>23</sup> ಅದಕ್ಕೆ ಅವನು, “ಇಂದು ಅಮಾವಾಸ್ಯೆಯೂ ಅಲ್ಲ, ಸಬ್ಬತ್ತೂ ಅಲ್ಲ, ಈ ದಿನ ಹೋಗಿ ಪ್ರಯೋಜನವಿಲ್ಲ,” ಎಂದನು.",
			"24": "<sup>24</sup> ಆಕೆ, “ಚಿಂತೆಯಿಲ್ಲ,” ಎಂದು ಹೇಳಿ ಕತ್ತೆಗೆ ತಡಿಹಾಕಿಸಿ, ಕುಳಿತುಕೊಂಡು ಸೇವಕನಿಗೆ, “ಹೊಡೆ, ಓಡಿಸುತ್ತಾ ಹೋಗು; ನಾನು ಅಪ್ಪಣೆ ಮಾಡುವವರೆಗೂ ನಿಲ್ಲಿಸಬೇಡ,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದಳು.",
			"25": "<sup>25</sup> ಹೀಗೆ ಆಕೆ ಪ್ರಯಾಣ ಮಾಡಿ ಕರ್ಮೆಲ್ ಬೆಟ್ಟದ ಮೇಲಿದ್ದ ದೈವಪುರುಷನ ಬಳಿಗೆ ಬಂದಳು. ಅವನು ಈಕೆಯನ್ನು ದೂರದಿಂದಲೇ ಕಂಡು ತನ್ನ ಸೇವಕನಾದ ಗೇಹಜಿಗೆ,",
			"26": "<sup>26</sup> “ಅಗೋ, ಅಲ್ಲಿ ಶೂನೇಮ್ಯಳು; ನೀನು ಓಡಿಹೋಗಿ ಆಕೆಯನ್ನು ಭೇಟಿಯಾಗು; ‘ನಿನಗೂ ನಿನ್ನ ಗಂಡನಿಗೂ ಮಗನಿಗೂ ಕ್ಷೇಮವೇ?’ ಎಂದು ವಿಚಾರಿಸು,” ಎಂಬುದಾಗಿ ಆಜ್ಞಾಪಿಸಿ ಕಳುಹಿಸಿದನು. ಆಕೆ ಆ ಸೇವಕನಿಗೆ, “ಕ್ಷೇಮ,” ಎಂದು ಉತ್ತರಕೊಟ್ಟು, ಬೆಟ್ಟದ ಮೇಲಿದ್ದ ದೈವಪುರುಷನ ಬಳಿಗೆ ಬಂದು, ಅವನ ಪಾದಗಳನ್ನು ಹಿಡಿದಳು.",
			"27": "<sup>27</sup> ಗೇಹಜಿಯು ಆಕೆಯನ್ನು ದೂಡುವುದಕ್ಕಾಗಿ ಹತ್ತಿರ ಬಂದನು. ದೈವಪುರುಷನು, “ಬಿಡು, ತಡೆಯಬೇಡ; ಆಕೆಗೆ ಮನಸ್ಸಿನಲ್ಲಿ ತೀವ್ರ ದುಃಖವಿರುವ ಹಾಗೆ ತೋರುತ್ತದೆ. ಸರ್ವೇಶ್ವರ ಆಕೆಯ ದುಃಖವನ್ನು ನನಗೆ ತಿಳಿಸಲಿಲ್ಲ; ಮರೆಮಾಡಿದ್ದಾರೆ,” ಎಂದು ಹೇಳಿದನು.",
			"28": "<sup>28</sup> ಆಕೆ ದೈವಪುರುಷನಿಗೆ, “ನನಗೆ ಒಬ್ಬ ಮಗ ಬೇಕೆಂದು ಒಡೆಯರಾದ ನಿಮ್ಮನ್ನು ನಾನು ಬೇಡಿಕೊಂಡೆನೆ? ನನ್ನನ್ನು ವಂಚಿಸಬಾರದೆಂದು ಮೊರೆಯಿಟ್ಟೆನಲ್ಲವೆ?” ಎಂದಳು.",
			"29": "<sup>29</sup> ಆಗ ಎಲೀಷನು ಗೇಹಜಿಗೆ, “ನೀನು ಹೋಗಿ, ನಡುಕಟ್ಟಿಕೊಂಡು, ನನ್ನ ಕೋಲನ್ನು ತೆಗೆದುಕೊಂಡು ಹೋಗಿ, ಆ ಹುಡುಗನ ಮುಖದ ಮೇಲಿಡು; ಹೋಗುವಾಗ ದಾರಿಯಲ್ಲಿ ಯಾರನ್ನೂ ವಂದಿಸಬೇಡ; ಯಾವ ವಂದನೆಯನ್ನೂ ಸ್ವೀಕರಿಸಬೇಡ,\" ಎಂದು ಹೇಳಿ ಕಳುಹಿಸಿದನು.",
			"30": "<sup>30</sup> ಆದರೂ ಆ ಹುಡುಗನ ತಾಯಿ, “ಸರ್ವೇಶ್ವರನಾಣೆ, ನಿಮ್ಮ ಜೀವದಾಣೆ, ನಾನು ನಿಮ್ಮನ್ನು ಬಿಟ್ಟುಹೋಗುವುದಿಲ್ಲ,” ಎಂದಳು. ಆದ್ದರಿಂದ ಎಲೀಷನು ಆಕೆಯ ಜೊತೆಯಲ್ಲೇ ಹೊರಟನು.",
			"31": "<sup>31</sup> ಅವರ ಮುಂದಾಗಿ ಹೋದ ಗೇಹಜಿಯು ಕೋಲನ್ನು ಹುಡುಗನ ಮುಖದ ಮೇಲಿಟ್ಟರೂ ಶಬ್ದವಿರಲಿಲ್ಲ, ಜೀವದ ಲಕ್ಷಣವಿರಲಿಲ್ಲ. ಆದುದರಿಂದ ಅವನು ಹಿಂದಿರುಗಿ ಬಂದು ಎಲೀಷನನ್ನು ಎದುರುಗೊಂಡು, “ಹುಡುಗನು ಎಚ್ಚರವಾಗಲಿಲ್ಲ,” ಎಂದನು.",
			"32": "<sup>32</sup> ಎಲೀಷನು ಆ ಮನೆಯನ್ನು ಮುಟ್ಟಿದಾಗ, ಸತ್ತ ಹುಡುಗನನ್ನು ತನ್ನ ಮಂಚದ ಮೇಲೆ ಇಟ್ಟಿರುವುದನ್ನು ಕಂಡನು.",
			"33": "<sup>33</sup> ಯಾರೂ ಒಳಗೆ ಬಾರದಂತೆ ಆ ಕೋಣೆಯ ಬಾಗಿಲನ್ನು ಮುಚ್ಚಿಕೊಂಡು ಸರ್ವೇಶ್ವರನನ್ನು ಪ್ರಾರ್ಥಿಸಿದನು.",
			"34": "<sup>34</sup> ಅನಂತರ ಹುಡುಗನ ಬಾಯಿ, ಕಣ್ಣು, ಕೈಗಳಿಗೆ ತನ್ನ ಬಾಯಿ, ಕಣ್ಣು, ಕೈಗಳು ಮುಟ್ಟುವಂತೆ ಅವನ ಮೇಲೆ ಬೋರಲು ಬಿದ್ದನು. ಆದ್ದರಿಂದ ಹುಡುಗನ ದೇಹ ಬೆಚ್ಚಗಾಯಿತು.",
			"35": "<sup>35</sup> ಆಮೇಲೆ ಹುಡುಗನನ್ನು ಬಿಟ್ಟು ಎದ್ದು ಮನೆಯನ್ನು ತುಸುಹೊತ್ತು ಅತ್ತಿತ್ತ ಅಡ್ಡಾಡಿ, ತಿರುಗಿ ಅವನ ಮೇಲೆ ಬೋರಲು ಬೀಳಲು, ಹುಡುಗನು ಏಳು ಸಾರಿ ಸೀತು ಕಣ್ತೆರೆದನು.",
			"36": "<sup>36</sup> ಆಗ ಎಲೀಷನು ಗೇಹಜಿಗೆ, “ಶೂನೇಮ್ಯಳನ್ನು ಕರೆ,” ಎಂದು ಆಜ್ಞಾಪಿಸಲು, ಅವನು ಆಕೆಯನ್ನು ಕರೆದನು. ಆಕೆ ಬಂದಾಗ ಎಲೀಷನು, “ನಿನ್ನ ಮಗನನ್ನುತೆಗೆದುಕೋ,” ಎಂದು ಹೇಳಿದನು.",
			"37": "<sup>37</sup> ಆಕೆ ಹತ್ತಿರ ಬಂದು ಅವನ ಪಾದಗಳಿಗೆ ಬಿದ್ದು, ಸಾಷ್ಟಾಂಗ ನಮಸ್ಕಾರ ಮಾಡಿ, ಮಗನನ್ನು ತೆಗೆದುಕೊಂಡು ಹೋದಳು.",
			"38": "<sup>38</sup> ಎಲೀಷನು ಪುನಃ ಗಿಲ್ಗಾಲಿಗೆ ಹೋದನು. ಆಗ ನಾಡಿನಲ್ಲಿ ಬರವಿತ್ತು. ಪ್ರವಾದಿಮಂಡಲಿಯವರು ತನ್ನ ಮುಂದೆ ಕೂಡಿಬರಲು ಅವನು ತನ್ನ ಸೇವಕನಿಗೆ, “ದೊಡ್ಡ ಹಂಡೆಯನ್ನು ತೆಗೆದುಕೊಂಡು ಇವರಿಗೆ ಅಡಿಗೆಮಾಡು,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"39": "<sup>39</sup> ಅವರಲ್ಲಿ ಒಬ್ಬನು ಯಾವುದಾದರೊಂದು ತರಕಾರಿ ಸಿಕ್ಕೀತೆಂದು ಕಾಡಿಗೆ ಹೋಗಿ, ಅಲ್ಲಿ ಒಂದು ಕಾಡುಬಳ್ಳಿಯನ್ನು ಕಂಡು, ಉಡಿತುಂಬಾ ಅದರ ಕಾಯಿಗಳನ್ನು ಕಿತ್ತುಕೊಂಡುಬಂದು, ತುಂಡು ತುಂಡುಮಾಡಿ ಆ ಪಾತ್ರೆಯೊಳಗೆ ಹಾಕಿದನು. ಅದು ಎಂಥ ಕಾಯಿಯೆಂಬುದು ಯಾರಿಗೂ ಗೊತ್ತಿರಲಿಲ್ಲ.",
			"40": "<sup>40</sup> ಜನರಿಗೆ ಅದನ್ನೇ ಊಟಕ್ಕೆ ಬಡಿಸಿದರು; ಅವರು ಅದನ್ನು ಬಾಯೊಳಗೆ ಹಾಕಿದ ಕೂಡಲೆ ತಿನ್ನಲಾರದೆ, “ದೈವಪುರುಷನೇ, ಪಾತ್ರೆಯಲ್ಲಿ ವಿಷ!” ಎಂದು ಕೂಗಿದರು.",
			"41": "<sup>41</sup> ಆಗ ಅವನು, “ಹಿಟ್ಟನ್ನು ತಂದುಕೊಡಿ,” ಎಂದು ಹೇಳಿದನು; ಅವರು ತರಲು ಅದನ್ನು ಆ ಪಾತ್ರೆಯೊಳಗೆ ಹಾಕಿ, “ಈಗ ಇದನ್ನು ಬಡಿಸಬಹುದು,” ಎಂದನು. ಕೂಡಲೆ ಪಾತ್ರೆಯಲ್ಲಿದ್ದ ವಿಷವೆಲ್ಲಾ ಹೋಗಿತ್ತು.",
			"42": "<sup>42</sup> ಒಂದು ದಿನ ಬಾಳ್‍ಷಾಲಿಷಾ ಊರಿನ ಒಬ್ಬ ವ್ಯಕ್ತಿ, ಇಪ್ಪತ್ತು ಜವೆಗೋದಿಯ ರೊಟ್ಟಿಗಳನ್ನೂ ಒಂದು ಚೀಲ ತುಂಬಾ ಹಸೀ ತೆನೆಗಳನ್ನೂ ಪ್ರಥಮಫಲದ ಕಾಣಿಕೆಯಾಗಿ ತೆಗೆದುಕೊಂಡು ಬಂದು ದೈವಪುರುಷನಿಗೆ ಸಮರ್ಪಿಸಿದನು. ಎಲೀಷನು ತನ್ನ ಸೇವಕನಿಗೆ, “ಇದನ್ನು ಜನರಿಗೆ ಕೊಡು; ಅವರು ಊಟಮಾಡಲಿ,” ಎಂದು ಹೇಳಿದನು.",
			"43": "<sup>43</sup> ಅದಕ್ಕೆ ಅವನು, “ಇಷ್ಟನ್ನು ನೂರುಮಂದಿಗೆ ಹೇಗೆ ಬಡಿಸಲಿ,” ಎಂದನು. ಎಲೀಷನು, “ಇರಲಿ, ಕೊಡು; ಅವರು ಊಟಮಾಡಲಿ. ‘ಅವರು ಅದನ್ನು ಊಟಮಾಡಿ ತೃಪ್ತರಾಗಿ ಇನ್ನೂ ಉಳಿಸಿಕೊಳ್ಳುವರು’ ಎಂಬುದು ಸರ್ವೇಶ್ವರನ ನುಡಿ,” ಎಂದು ಹೇಳಿದನು.",
			"44": "<sup>44</sup> ಸೇವಕನು ಬಡಿಸಿದನು. ಸರ್ವೇಶ್ವರನು ನುಡಿದಂತೆಯೇ ಜನರು ಊಟಮಾಡಿ, ತೃಪ್ತರಾಗಿ ಇನ್ನೂ ಉಳಿಸಿಕೊಂಡರು."
		},
		"5": {
			"1": "<sup>1</sup> ಸಿರಿಯಾದ ರಾಜನಿಗೆ ನಾಮಾನ್ ಎಂಬೊಬ್ಬ ಸೇನಾಪತಿಯಿದ್ದನು. ಸರ್ವೇಶ್ವರಸ್ವಾಮಿ ಇವನ ಮುಂಖಾತರ ಸಿರಿಯಾದವರಿಗೆ ಜಯವನ್ನು ಅನುಗ್ರಹಿಸಿದ್ದರು. ಆದ್ದರಿಂದ ರಾಜನು ಇವನನ್ನು ಮಹಾಪುರುಷನೆಂದೂ ಸನ್ಮಾನ್ಯನೆಂದೂ ಗೌರವಿಸುತ್ತಿದ್ದನು. ಆದರೆ ಪರಾಕ್ರಮಶಾಲಿಯಾದ ಇವನು ಭೀಕರ ಚರ್ಮರೋಗದಿಂದ ನರಳುತ್ತಿದ್ದನು.",
			"2": "<sup>2</sup> ಸಿರಿಯಾದವರು, ಒಮ್ಮೆ ಸುಲಿಗೆಗಾಗಿ ಇಸ್ರಯೇಲರ ಪ್ರಾಂತ್ಯಕ್ಕೆ ಹೋಗಿಬರುವಾಗ, ಅಲ್ಲಿನ ಒಬ್ಬ ಹುಡುಗಿಯನ್ನು ಹಿಡಿದುಕೊಂಡು ಬಂದರು. ಆಕೆ ನಾಮಾನನ ಹೆಂಡತಿಗೆ ದಾಸಿಯಾದಳು.",
			"3": "<sup>3</sup> ಒಂದು ದಿನ ಆಕೆ ತನ್ನ ಯಜಮಾನಿಗೆ, “ನಮ್ಮ ದಣಿಯವರು ಸಮಾರಿಯದಲ್ಲಿರುವ ಪ್ರವಾದಿಯ ಹತ್ತಿರ ಇದ್ದಿದ್ದರೆ ಎಷ್ಟೋ ಒಳ್ಳೇದಾಗುತ್ತಿತ್ತು; ಅವರು ಇವರನ್ನು ಈ ಚರ್ಮರೋಗದಿಂದ ಗುಣಪಡಿಸುತ್ತಿದ್ದರು,” ಎಂದು ಹೇಳಿದಳು.",
			"4": "<sup>4</sup> ನಾಮಾನನು ರಾಜನ ಸನ್ನಿಧಿಗೆ ಹೋಗಿ, ಇಸ್ರಯೇಲ್ ನಾಡಿನ ಹುಡುಗಿ ಹೇಳಿದ್ದನ್ನು ತಿಳಿಸಿದನು.",
			"5": "<sup>5</sup> ಅವನು, “ನೀನು ಅಲ್ಲಿಗೆ ಹೋಗಿ ಬಾ; ನಾನು ನಿನ್ನ ಕೈಯಲ್ಲಿ ಇಸ್ರಯೇಲರ ಅರಸನಿಗೆ ಒಂದು ಪತ್ರವನ್ನು ಕೊಡುತ್ತೇನೆ,” ಎಂದನು. ನಾಮಾನನು ಮೂವತ್ತು ಸಾವಿರ ಬೆಳ್ಳಿ, ಆರು ಸಾವಿರ ಬಂಗಾರ ನಾಣ್ಯಗಳನ್ನೂ, ಹತ್ತು ದುಸ್ತು ಬಟ್ಟೆಗಳನ್ನೂ ತೆಗೆದುಕೊಂಡು ಸಮಾರಿಯಕ್ಕೆ ಹೋದನು;",
			"6": "<sup>6</sup> ತಂದ ಪತ್ರವನ್ನು ಇಸ್ರಯೇಲರ ಅರಸನಿಗೆ ಕೊಟ್ಟನು. ಅದರಲ್ಲಿ, “ನನ್ನ ಸೇವಕ ನಾಮಾನನನ್ನು ನಿಮ್ಮ ಬಳಿಗೆ ಕಳುಹಿಸಿದ್ದೇನೆ; ನೀವು ಅವನ ಚರ್ಮರೋಗವನ್ನು ವಾಸಿಮಾಡತಕ್ಕದ್ದೆಂದು ಈ ಪತ್ರದಿಂದ ತಿಳಿದುಕೊಳ್ಳಿ,” ಎಂಬುದಾಗಿ ಬರೆದಿತ್ತು.",
			"7": "<sup>7</sup> ಇಸ್ರಯೇಲರ ಅರಸನು ಈ ಪತ್ರವನ್ನು ಓದಿದ ಕೂಡಲೆ, ಸಿಟ್ಟಿನಿಂದ ಬಟ್ಟೆಗಳನ್ನು ಹರಿದುಕೊಂಡು, ತನ್ನ ಪರಿವಾರದವರಿಗೆ, “ತಾನು ಕಳುಹಿಸಿದ ವ್ಯಕ್ತಿಯನ್ನು ಚರ್ಮರೋಗದಿಂದ ನಾನು ವಾಸಿಮಾಡಬೇಕಂತೆ! ಇದು ಎಂಥ ಅಪ್ಪಣೆ; ನಾನೇನು ದೇವರೋ? ಜೀವದಾನ ಮಾಡುವುದಕ್ಕಾಗಲಿ ಜೀವಹರಣ ಮಾಡುವುದಕ್ಕಾಗಲಿ ನನಗೆ ಸಾಮರ್ಥ್ಯವುಂಟೇ? ಇವನು ನನ್ನೊಡನೆ ಯುದ್ಧಮಾಡುವುದಕ್ಕೆ ನೆಪ ಹುಡುಕುತ್ತಾನಲ್ಲದೆ ಮತ್ತೇನು? ನೀವೇ ಆಲೋಚಿಸಿ ನೋಡಿ,” ಎಂದು ಹೇಳೀದನು.",
			"8": "<sup>8</sup> ಇಸ್ರಯೇಲರ ಅರಸನು ಸಿಟ್ಟಿನಿಂದ ತನ್ನ ಬಟ್ಟೆಗಳನ್ನು ಹರಿದುಕೊಂಡನೆಂಬುದನ್ನು ದೈವಪುರುಷ ಎಲೀಷನು ಕೇಳಿದನು. ದೂತರ ಮುಖಾಂತರ ಅವನಿಗೆ, “ನೀವು ನಿಮ್ಮ ಬಟ್ಟೆಗಳನ್ನು ಹರಿದುಕೊಂಡದ್ದೇಕೆ? ಅವನನ್ನು ನನ್ನ ಬಳಿಗೆ ಕಳುಹಿಸಿ; ಇಸ್ರಯಲರಲ್ಲಿ ಒಬ್ಬ ಪ್ರವಾದಿ ಇದ್ದಾನೆಂಬುದು ಅವನಿಗೆ ಗೊತ್ತಾಗಲಿ,” ಎಂದು ಹೇಳಿ ಕಳುಹಿಸಿದನು.",
			"9": "<sup>9</sup> ನಾಮಾನನು ರಥರಥಾಶ್ವಗಳೊಡನೆ ಎಲೀಷನ ಮನೆಗೆ ಹೋಗಿ ಬಾಗಿಲಿನ ಮುಂದೆ ನಿಂತನು.",
			"10": "<sup>10</sup> ಎಲೀಷನು ಅವನಿಗೆ, “ಹೋಗಿ ಜೋರ್ಡನ್ ನದಿಯಲ್ಲಿ ಏಳು ಸಾರಿ ಸ್ನಾನಮಾಡು; ಆಗ ನಿನ್ನ ದೇಹ ಮೊದಲಿದ್ದಂತಾಗುವುದು; ನೀನು ಶುದ್ಧನಾಗುವೆ,” ಎಂದು ಹೇಳಿಕಳುಹಿಸಿದನು.",
			"11": "<sup>11</sup> ನಾಮಾನನು ಇದನ್ನು ಕೇಳಿ ಕೋಪಗೊಂಡನು . “ಇದೇನು ! ಇವನು ಖಂಡಿತ ವಾಗಿ ಹೊರಗೆಬಂದು ನಿಂತು, ತನ್ನ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನ ಹೆಸರು ಹೇಳಿ, ನನ್ನ ಚರ್ಮ ದ ಮೇಲೆ ಕೈಯಾಡಿಸಿ , ರೋಗ ವಾಸಿ ಮಾಡುವನೆಂದು ನೆನಸಿದೆ .",
			"12": "<sup>12</sup> ದಮಸ್ಕದ ಅಬಾನಾ ಹಾಗು ಪರ್ಪರ್ ಎಂಬ ನದಿಗಳು ಇಸ್ರಯೇಲರ ಎಲ್ಲಾ ನದಿ ಹೊಳೆಗಳಿಗಿಂತ ಉತ್ತಮವಾಗಿವೆಯಲ್ಲವೇ? ಸ್ನಾನದಿಂದ ವಾಸಿಯಾಗುವ ಹಾಗಿದ್ದರೆ ನಾನು ಅವುಗಳಲ್ಲಿಯೇ ಸ್ನಾನಮಾಡಬಹುದಿತ್ತಲ್ಲವೇ?” ಎಂದು ಹೇಳಿ, ಬಹಳ ಸಿಟ್ಟಿನಿಂದ ಹೊರಟುಹೋದನು.",
			"13": "<sup>13</sup> ಆಗ ಅವನ ಸೇವಕರು ಹತ್ತಿರಬಂದು, “ಯಜಮಾನರೇ, ಪ್ರವಾದಿ ಒಂದು ಕಠಿಣವಾದ ಕೆಲಸವನ್ನು ಹೇಳಿದ್ದರೆ ಅದನ್ನು ಮಾಡುತ್ತಿದ್ದಿರಲ್ಲವೆ? ಹಾಗಾದರೆ ‘ಸ್ನಾನಮಾಡಿ, ಶುದ್ಧರಾಗುವಿರಿ’ ಎಂದು ಹೇಳಿದರೆ ಏಕೆ ಅದರಂತೆ ಮಾಡಬಾರದು?” ಎಂದರು.",
			"14": "<sup>14</sup> ಅವನು ಅಂತೆಯೇ ಜೋರ್ಡನಿಗೆ ಹೋಗಿ, ಏಳು ಸಾರಿ ಅದರಲ್ಲಿ ಮುಳುಗಿ ಎದ್ದನು. ಕೂಡಲೆ ದೈವಪುರುಷನ ಮಾತಿಗನುಸಾರ ಅವನ ದೇಹ ಒಂದು ಮಗುವಿನ ದೇಹದಂತೆ ಶುದ್ಧವಾಯಿತು.",
			"15": "<sup>15</sup> ಅನಂತರ ನಾಮಾನನು ತನ್ನ ಪರಿವಾರದವರೊಡನೆ ಹಿಂದಿರುಗಿ ದೈವಪುರುಷನ ಬಳಿಗೆ ಹೋಗಿ ಅವನ ಮುಂದೆ ನಿಂತನು. “ಇಸ್ರಯೇಲ್ ನಾಡಿನಲ್ಲಿರುವ ದೇವರ ಹೊರತು, ಲೋಕದಲ್ಲಿ ಬೇರೆ ದೇವರು ಇಲ್ಲವೇ ಇಲ್ಲವೆಂಬುದು ಈಗ ನನಗೆ ಗೊತ್ತಾಯಿತು; ತಾವು ದಯವಿಟ್ಟು ಈ ಕಾಣಿಕೆಯನ್ನು ಅಂಗೀಕರಿಸಬೇಕು,” ಎಂದು ಹೇಳಿದನು.",
			"16": "<sup>16</sup> ಅದಕ್ಕೆ ಎಲೀಷನು, “ನಾನು ಸನ್ನಿಧಿಸೇವೆ ಮಾಡುತ್ತಿರುವ ಸರ್ವೇಶ್ವರನಾಣೆ, ನಿನ್ನಿಂದ ಏನೂ ತೆಗೆದುಕೊಳ್ಳುವುದಿಲ್ಲ,” ಎಂದನು. ನಾಮಾನನು ಎಷ್ಟು ಒತ್ತಾಯಪಡಿಸಿದರೂ ಅವನು ತೆಗೆದುಕೊಳ್ಳಲೇ ಇಲ್ಲ.",
			"17": "<sup>17</sup> ಆಗ ಅವನು ಎಲೀಷನಿಗೆ, “ತಾವು ಏನನ್ನೂ ಸ್ವೀಕರಿಸದಿದ್ದರೆ ತಮ್ಮ ಸೇವಕನಾದ ನನಗೆ ಎರಡು ಹೇಸರಕತ್ತೆಗಳು ಹೊರುವಷ್ಟು ಮಣ್ಣನ್ನು ಕೊಡಿಸಬೇಕೆಂದು ಬೇಡಿಕೊಳ್ಳುತ್ತೇನೆ. ನಾನು ಇನ್ನು ಮುಂದೆ ಎಲ್ಲಾ ದೇವತೆಗಳನ್ನು ಬಿಟ್ಟು ಸರ್ವೇಶ್ವರಸ್ವಾಮಿ ಒಬ್ಬರಿಗೇ ದಹನಬಲಿಗಳನ್ನು ಸಮರ್ಪಿಸಬೇಕೆಂದಿರುತ್ತೇನೆ.",
			"18": "<sup>18</sup> ಆದರೆ ಈ ವಿಷಯದಲ್ಲಿ ಸರ್ವೇಶ್ವರ ನನ್ನನ್ನು ಕ್ಷಮಿಸಲಿ; ನನ್ನ ಒಡೆಯ ಆರಾಧನೆಗೋಸ್ಕರ ರಿಮ್ಮೋನನ ದೇವಸ್ಥಾನಕ್ಕೆ ಹೋಗಿ, ನನ್ನ ಕೈಹಿಡಿದು, ಆ ದೇವತೆಗೆ ನಮಸ್ಕಾರ ಮಾಡುವಾಗ ನಾನೂ ನಮಸ್ಕಾರ ಮಾಡಬೇಕಾಗುವುದು. ಈ ಒಂದು ವಿಷಯದಲ್ಲಿ ಮಾತ್ರ ಸರ್ವೇಶ್ವರ ನನಗೆ ಕ್ಷಮೆಯನ್ನು ಅನುಗ್ರಹಿಸಬೇಕು,” ಎಂದನು.",
			"19": "<sup>19</sup> ಎಲೀಷನು, “ಸಮಾಧಾನದಿಂದ ಹೋಗು,” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು. ಅವನು ಹೊರಟುಹೋದನು.",
			"20": "<sup>20</sup> ನಾಮಾನನು ಸ್ವಲ್ಪದೂರಕ್ಕೆ ಹೋದನಂತರ ದೈವಪುರುಷನಾದ ಎಲೀಷನ ಸೇವಕ ಗೇಹಜಿಯು ಮನಸ್ಸಿನಲ್ಲೇ, “ನನ್ನ ಯಜಮಾನ ಆ ಸಿರಿಯಾದ ನಾಮಾನನಿಂದ ಏನೂ ತೆಗೆದುಕೊಳ್ಳದೆ ಅವನನ್ನು ಹಾಗೆಯೇ ಕಳುಹಿಸಿಬಿಟ್ಟರಲ್ಲಾ! ಸರ್ವೇಶ್ವರನಾಣೆ, ನಾನು ಅವನ ಹಿಂದೆ ಓಡುತ್ತಾ ಹೋಗಿ, ಅವನಿಂದ ಸ್ವಲ್ಪವನ್ನಾದರೂ ಬಾಚಿಕೊಂಡು ಬರುವೆನು,” ಎಂದುಕೊಂಡು ಹೊರಟನು.",
			"21": "<sup>21</sup> ನಾಮಾನನು ತನ್ನ ಹಿಂದೆ ಓಡುತ್ತಾ ಬರುವ ಗೇಹಜಿಯನ್ನು ಕಂಡು, ರಥದಿಂದಿಳಿದು, ಅವನನ್ನು ಎದುರುಗೊಂಡನು, “ಕ್ಷೇಮವೇ,” ಎಂದು ಕೇಳಿದನು.",
			"22": "<sup>22</sup> ಅವನು, “ಕ್ಷೇಮ; ಎಫ್ರಯಿಮ್ ಗುಡ್ಡಪ್ರದೇಶದಿಂದ ಪ್ರವಾದಿಮಂಡಲಿಯವರಾದ ಇಬ್ಬರು ಯೌವನಸ್ಥರು ಬಂದಿರುತ್ತಾರೆ. ಆದುದರಿಂದ ಅವರಿಗಾಗಿ ಬೇಗನೆ ಮೂರುಸಾವಿರ ಬೆಳ್ಳಿಯ ನಾಣ್ಯಗಳನ್ನೂ ಎರಡು ದುಸ್ತು ಬಟ್ಟೆಗಳನ್ನೂ ತೆಗೆದುಕೊಂಡು ಬರಬೇಕೆಂಬುದಾಗಿ ನನ್ನ ಯಜಮಾನ ನನ್ನನ್ನು ನಿಮ್ಮ ಬಳಿಗೆ ಕಳುಹಿಸಿದರು,” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"23": "<sup>23</sup> ನಾಮಾನನು, “ಆರು ಸಾವಿರ ನಾಣ್ಯವನ್ನಾದರೂ ತೆಗೆದುಕೊಳ್ಳಬಾರದೇ,” ಎಂದು ಅವನನ್ನು ಒತ್ತಾಯಪಡಿಸಿ, ಅದನ್ನು ಎರಡು ಚೀಲಗಳಲ್ಲಿ ಹಾಕಿಸಿ, ಆ ಚೀಲಗಳನ್ನೂ ಎರಡು ದುಸ್ತು ಬಟ್ಟೆಗಳನ್ನೂ ಇಬ್ಬರು ಸೇವಕರ ಮೇಲೆ ಹೊರಿಸಿ, ಅವರನ್ನು ಗೇಹಜಿಯ ಸಂಗಡ ಕಳುಹಿಸಿದನು. ಅವರು ಹೊತ್ತುಕೊಂಡು ಇವನ ಮುಂದೆ ನಡೆದರು.",
			"24": "<sup>24</sup> ಗುಡ್ಡವನ್ನು ಮುಟ್ಟಿದಾಗ, ಗೇಹಜಿಯು ಅವುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು, ಮನೆಯಲ್ಲಿ ಅಡಗಿಸಿಬಿಟ್ಟು, ಆಳುಗಳನ್ನು ಕಳುಹಿಸಿಬಿಟ್ಟನು.",
			"25": "<sup>25</sup> ಅವರು ಹೋದನಂತರ, ಇವನು ತನ್ನ ಯಜಮಾನನ ಬಳಿಗೆ ಬಂದನು. ಎಲೀಷನು ಅವನನ್ನು, “ಗೇಹಜಿಯೇ, ಎಲ್ಲಿಗೆ ಹೋಗಿ ಬಂದೆ?” ಎಂದು ಕೇಳಿದನು. ಅದಕ್ಕೆ ಅವನು, “ನಿಮ್ಮ ಸೇವಕನಾದ ನಾನು ಎಲ್ಲಿಯೂ ಹೋಗಲಿಲ್ಲ,” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"26": "<sup>26</sup> ಆಗ ಎಲೀಷನು, “ಒಬ್ಬನು ರಥದಿಂದಿಳಿದು ಬಂದು ನಿನ್ನನ್ನು ಎದುರುಗೊಂಡದ್ದು ನನ್ನ ಜ್ಞಾನದೃಷ್ಟಿಗೆ ಕಾಣಿಸಲಿಲ್ಲವೆಂದು ನೆನಸುತ್ತೀಯೋ? ದ್ರವ್ಯ, ಬಟ್ಟೆಗಳು, ಎಣ್ಣೇಮರದ ತೋಪುಗಳು, ದ್ರಾಕ್ಷೀತೋಟಗಳು, ಕುರಿದನಗಳು, ಗಂಡಾಳು, ಹೆಣ್ಣಾಳುಗಳು, ಇವನ್ನು ಸಂಪಾದಿಸುವುದಕ್ಕೆ ಇದು ಸಮಯವೇ?",
			"27": "<sup>27</sup> ನಾಮಾನನ ಚರ್ಮರೋಗವು ನಿನ್ನನ್ನೂ ನಿನ್ನ ಸಂತಾನದವರನ್ನೂ ಸದಾಕಾಲ ಬಾಧಿಸುವುದು,” ಎಂದನು. ಕೂಡಲೆ ಅವನಿಗೆ ಆ ರೋಗ ಹತ್ತಿತು. ಅವನು ಹಿಮದಂತೆ ಬಿಳುಪಾಗಿ, ಅಲ್ಲಿಂದ ಹೊರಟುಹೋದನು."
		},
		"6": {
			"1": "<sup>1</sup> ಒಂದು ಸಾರಿ ಪ್ರವಾದಿಮಂಡಲಿಯವರು ಎಲೀಷನಿಗೆ, “ನಾವು ನಿನ್ನ ಜೊತೆಯಲ್ಲಿ ವಾಸಿಸುತ್ತಿರುವ ಈ ಸ್ಥಳ ಬಹಳ ಇಕ್ಕಟ್ಟಾಗಿದೆ.",
			"2": "<sup>2</sup> ಆದುದರಿಂದ ನಾವು ಜೋರ್ಡನಿಗೆ ಹೋಗಿ ಅಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬನೂ ಒಂದೊಂದು ದಿಮ್ಮಿಯನ್ನು ಕಡಿದುಕೊಂಡು ಬಂದು ನಮಗೆ ಇರಲು ಒಂದು ಮನೆಯನ್ನು ಕಟ್ಟಿಕೊಳ್ಳೋಣ,” ಎಂದರು. ಅವನು ಅವರಿಗೆ, “ಹೋಗಬಹುದು,” ಎಂದು ಹೇಳಿದನು.",
			"3": "<sup>3</sup> ಅವರಲ್ಲೊಬ್ಬನು, “ದಯವಿಟ್ಟು ತಾವೂ ತಮ್ಮ ಸೇವಕರಾದ ನಮ್ಮ ಜೊತೆಯಲ್ಲಿ ಬರಬೇಕು,” ಎಂದು ಬೇಡಿಕೊಂಡನು. ಎಲೀಷನು, “ಬರುತ್ತೇನೆ,” ಎಂದು ಹೇಳಿ ಅವರೊಡನೆ ಹೊರಟನು.",
			"4": "<sup>4</sup> ಅವರು ಜೋರ್ಡನನ್ನು ಮುಟ್ಟಿ ಮರಗಳನ್ನು ಕಡಿಯುತ್ತಿರುವಾಗ ಒಬ್ಬನ ಕೊಡಲಿ, ಕಾವು ತಪ್ಪಿ, ನೀರೊಳಗೆ ಬಿದ್ದಿತು.",
			"5": "<sup>5</sup> ಆದುದರಿಂದ ಅವನು, “ಅಯ್ಯೋ! ಗುರುವೇ, ನಾನು ಅದನ್ನು ಸಾಲವಾಗಿ ತಂದಿದ್ದೆ,” ಎಂದು ಸಂತಾಪಿಸಿದನು.",
			"6": "<sup>6</sup> ದೈವಪುರುಷನು, “ಅದು ಎಲ್ಲಿ ಬಿದ್ದಿತು?” ಎಂದು ಕೇಳಿದನು; ಅದು ಬಿದ್ದ ಸ್ಥಳವನ್ನು ಅವನು ತೋರಿಸಿದನು. ಆಗ ದೈವಪುರುಷನು ಆ ಮರದಿಂದ ಒಂದು ತುಂಡು ಕಟ್ಟಿಗೆಯನ್ನು ಕಡಿದು ಅಲ್ಲಿ ಹಾಕಿದನು; ಕೂಡಲೆ ಆ ಕೊಡಲಿ ತೇಲಾಡಿತು.",
			"7": "<sup>7</sup> ಎಲೀಷನು, “ಅದನ್ನು ತೆಗೆದುಕೋ,” ಎಂದು ಆಜ್ಞಾಪಿಸಲು ಆ ವ್ಯಕ್ತಿ ಕೈಚಾಚಿ ಅದನ್ನು ತೆಗೆದುಕೊಂಡನು.",
			"8": "<sup>8</sup> ಸಿರಿಯಾದ ರಾಜನು ಇಸ್ರಯೇಲರಿಗೆ ವಿರುದ್ಧ ಯುದ್ಧಕ್ಕೆ ಬಂದನು. ಅವನು ಯುದ್ಧದಲ್ಲಿ ಹೊಂಚುಹಾಕತಕ್ಕ ಸ್ಥಳವನ್ನು ತನ್ನ ಸೇನಾಪತಿಗಳೊಡನೆ ಗೊತ್ತುಮಾಡುತ್ತಿದ್ದನು.",
			"9": "<sup>9</sup> ಆದರೆ ದೈವಪುರುಷನು, ಪ್ರತೀಸಾರಿ ಇಸ್ರಯೇಲರ ಅರಸನಿಗೆ, “ಜಾಗರೂಕತೆಯಿಂದಿರಿ! ನೀವು ಇಂಥಿಂಥ ಸ್ಥಳದಲ್ಲಿ ಹಾಯಬಾರದು, ಅಲ್ಲಿ ಸಿರಿಯಾದವರು ಅಡಗಿಕೊಂಡಿದ್ದಾರೆ,” ಎಂದು ತಿಳಿಸುತ್ತಿದ್ದನು.",
			"10": "<sup>10</sup> ಹೀಗೆ ಅವನು ಹಲವು ಸಾರಿ ಸಿರಿಯಾದ ಅರಸನ ಕೈಗೆ ಸಿಕ್ಕದಂತೆ ತಪ್ಪಿಸಿಕೊಂಡನು.",
			"11": "<sup>11</sup> ಈ ಕಾರಣ ಸಿರಿಯಾದ ಅರಸನು ಕಳವಳಗೊಂಡು, ತನ್ನ ಸೇನಾಪತಿಗಳನ್ನು ಕರೆದು, “ನಮ್ಮವರಲ್ಲಿ ಇಸ್ರಯೇಲರ ಅರಸನ ಪಕ್ಷದವರು ಯಾರೋ ಇದ್ದಾರೆ; ನನಗೆ ನೀವು ಯಾರೂ ತಿಳಿಸುವುದಿಲ್ಲವೇ?’ ಎಂದು ಕೇಳಿದನು.",
			"12": "<sup>12</sup> ಆಗ ಅವರಲ್ಲೊಬ್ಬನು, “ನನ್ನ ಒಡೆಯರಾದ ಅರಸೇ, ಹಾಗಲ್ಲ; ಇಸ್ರಯೇಲರಲ್ಲಿ ಎಲೀಷನೆಂಬೊಬ್ಬ ಪ್ರವಾದಿಯಿದ್ದಾನೆ; ನೀವು ನಿಮ್ಮ ಮಲಗುವ ಕೋಣೆಯಲ್ಲಿ ಆಡುವ ಮಾತುಗಳನ್ನು ಸಹ ಅವನು ಅರಿತುಕೊಂಡು ಎಲ್ಲವನ್ನೂ ಇಸ್ರಯೇಲರ ಅರಸನಿಗೆ ತಿಳಿಸುತ್ತಾನೆ,” ಎಂದನು.",
			"13": "<sup>13</sup> ಅರಸನು ಇದನ್ನು ಕೇಳಿ, “ಅವನು ಎಲ್ಲಿರುತ್ತಾನೆಂದು ವಿಚಾರಿಸಿರಿ, ನಾನು ಅವನನ್ನು ಹಿಡಿಯುತ್ತೇನೆ,” ಎಂದು ಹೇಳಿದನು.",
			"14": "<sup>14</sup> ಆ ಪ್ರವಾದಿ ದೋತಾನಿನಲ್ಲಿರುತ್ತಾನೆಂದು ತಿಳಿದುಕೊಂಡು ರಥರಥಾಶ್ವಸಹಿತವಾದ ಮಹಾಸೈನ್ಯವನ್ನು ಅಲ್ಲಿಗೆ ಕಳುಹಿಸಿದನು. ಸೈನ್ಯದವರು ರಾತ್ರಿಯಲ್ಲೇ ಆ ಪಟ್ಟಣವನ್ನು ಸಮೀಪಿಸಿ, ಅದಕ್ಕೆ ಮುತ್ತಿಗೆ ಹಾಕಿದರು.",
			"15": "<sup>15</sup> ದೈವಪುರುಷನ ಸೇವಕನು ಬೆಳಿಗ್ಗೆ ಎದ್ದು ಹೊರಗೆ ಹೋದಾಗ ರಥರಥಾಶ್ವಸಹಿತವಾದ ಮಹಾಸೈನ್ಯವು ಬಂದು, ಪಟ್ಟಣದ ಸುತ್ತಲೂ ನಿಂತಿರುವುದನ್ನು ಕಂಡು, ತನ್ನ ಯಜಮಾನನಿಗೆ, “ಅಯ್ಯೋ! ಗುರುವೇ, ಏನು ಮಾಡೋಣ?” ಎಂದನು.",
			"16": "<sup>16</sup> ಆಗ ಎಲೀಷನು, “ಹೆದರಬೇಡ; ಅವರ ಕಡೆ ಇರುವವರಿಗಿಂತಲೂ ನಮ್ಮ ಕಡೆ ಇರುವವರು ಹೆಚ್ಚಾಗಿದ್ದಾರೆ,” ಎಂದು ಹೇಳಿ,",
			"17": "<sup>17</sup> “ಹೇ ಸರ್ವೇಶ್ವರಾ, ಇವನು ನೋಡುವಂತೆ ಇವನ ಕಣ್ಣುಗಳನ್ನು ತೆರೆಯಿರಿ,” ಎಂದು ಪ್ರಾರ್ಥಿಸಿದನು. ಸರ್ವೇಶ್ವರ ಅವನ ಕಣ್ಣುಗಳನ್ನು ತೆರೆದರು. ಆಗ ಎಲೀಷನ ರಕ್ಷಣೆಗಾಗಿ ಸುತ್ತಣ ಗುಡ್ಡಗಳಲ್ಲಿ ಬಂದು ನಿಂತಿದ್ದ ಅಗ್ನಿಮಯವಾದ ರಥರಥಾಶ್ವಗಳು ಆ ಸೇವಕನಿಗೆ ಕಂಡವು.",
			"18": "<sup>18</sup> ಶತ್ರುಗಳು ಸಮೀಪಕ್ಕೆ ಬಂದಾಗ ಎಲೀಷನು, “ಸ್ವಾಮೀ, ಈ ಜನರನ್ನು ಕುರುಡರನ್ನಾಗಿ ಮಾಡಿ,” ಎಂದು ಸರ್ವೇಶ್ವರನನ್ನು ಪ್ರಾರ್ಥಿಸಿದನು. ಅವರು ಅವನ ಮೊರೆಯನ್ನು ಆಲಿಸಿ ಆ ಜನರನ್ನು ಕುರುಡರನ್ನಾಗಿ ಮಾಡಿದರು.",
			"19": "<sup>19</sup> ಆಗ ಎಲೀಷನು, “ನೀವು ಹೋಗಬೇಕೆಂದಿದ್ದ ಪಟ್ಟಣವೂ ದಾರಿಯೂ ಇದಲ್ಲ; ನನ್ನ ಜೊತೆಯಲ್ಲೇ ಬನ್ನಿ, ನೀವು ಹುಡುಕುತ್ತಿರುವ ವ್ಯಕ್ತಿಯ ಬಳಿಗೆ ನಿಮ್ಮನ್ನು ಕರೆದುಕೊಂಡು ಹೋಗುತ್ತೇನೆ,” ಎಂದು ಹೇಳಿ ಅವರನ್ನು ಸಮಾರಿಯಕ್ಕೆ ಕರೆದುಕೊಂಡು ಹೋದನು.",
			"20": "<sup>20</sup> ಅವರು ಸಮಾರಿಯದೊಳಗೆ ಬಂದಾಗ ಎಲೀಷನು, “ಇವರು ನೋಡುವಂತೆ ಇವರ ಕಣ್ಣುಗಳನ್ನು ತೆರೆಯಿರಿ,” ಎಂದು ಸರ್ವೇಶ್ವರನನ್ನು ಬೇಡಿಕೊಂಡನು. ಅಂತೆಯೇ ಸರ್ವೇಶ್ವರ ಅವರ ಕಣ್ಣುಗಳನ್ನು ತೆರೆದರು. ಆಗ ಅವರಿಗೆ ತಾವು ಸಮಾರಿಯದಲ್ಲಿದ್ದೇವೆಂದು ತಿಳಿದುಬಂದಿತು.",
			"21": "<sup>21</sup> ಇಸ್ರಯೇಲರ ಅರಸನು ಅವರನ್ನು ಕಂಡು ಎಲೀಷನಿಗೆ, ‘ಅಪ್ಪಾ ಗುರುವೇ, ಇವರನ್ನು ಸಂಹರಿಸಲೇ?, ಸಂಹರಿಸಲೇ?’ ಎಂದು ಕೇಳಿದನು.",
			"22": "<sup>22</sup> ಅವನು, “ಬೇಡ; ನೀನು ಸ್ವಾಧೀನಪಡಿಸಿಕೊಂಡು ಸೆರೆಯಾಗಿ ತಂದವರನ್ನು ಕತ್ತಿಬಿಲ್ಲುಗಳಿಂದ ಸಂಹರಿಸುವೆಯೋ? ಅವರಿಗೆ ಅನ್ನಪಾನಗಳನ್ನು ಕೊಡು; ಉಂಡುಕುಡಿದು ತಮ್ಮ ಯಜಮಾನನ ಬಳಿಗೆ ಹಿಂದಿರುಗಿ ಹೋಗಲಿ,” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"23": "<sup>23</sup> ಆಗ ಅರಸನು ಅವರಿಗಾಗಿ ಒಂದು ದೊಡ್ಡ ಔತಣವನ್ನು ಮಾಡಿಸಿ, ಅವರು ಅನ್ನಪಾನಗಳನ್ನು ತೆಗೆದುಕೊಂಡ ಮೇಲೆ, ಅವರನ್ನು ಅವರ ಯಜಮಾನನ ಬಳಿಗೆ ಕಳುಹಿಸಿದನು. ಅಂದಿನಿಂದ ಸುಲಿಗೆಮಾಡುವ ಸಿರಿಯಾದ ಗುಂಪುಗಳು ಇಸ್ರಯೇಲರ ಪ್ರಾಂತದೊಳಗೆ ಮತ್ತೆ ಬರಲಿಲ್ಲ.",
			"24": "<sup>24</sup> ಆಮೇಲೆ ಸಿರಿಯಾದ ರಾಜ ಬೆನ್ಹದದನು ತನ್ನ ಸೈನ್ಯವನ್ನೆಲ್ಲಾ ಕೂಡಿಸಿಕೊಂಡು ಬಂದು ಸಮಾರಿಯಕ್ಕೆ ಮುತ್ತಿಗೆ ಹಾಕಿದನು.",
			"25": "<sup>25</sup> ಆಗ ಸಮಾರಿಯ ಪಟ್ಟಣದಲ್ಲಿ ಘೋರ ಕ್ಷಾಮವಿತ್ತು. ಮುತ್ತಿಗೆಯ ನಿಮಿತ್ತ ಅದು ಮತ್ತಷ್ಟು ಘೋರವಾಗಿ ಒಂದು ಕತ್ತೆಯ ತಲೆಯನ್ನು ಒಂಬತ್ತು ಬೆಳ್ಳಿ ನಾಣ್ಯಗಳಿಗೂ, ಇನ್ನೂರು ಗ್ರಾಂ ಕಾಡು ಈರುಳ್ಳಿಯನ್ನು ಐದು ಬೆಳ್ಳಿ ನಾಣ್ಯಗಳಿಗೂ ಮಾರಲಾಗುತ್ತಿತ್ತು.",
			"26": "<sup>26</sup> ಒಂದು ದಿನ ಇಸ್ರಯೇಲರ ಅರಸನು ಕೋಟೆಯ ಮೇಲೆ ತಿರುಗಾಡುತ್ತಿರುವಾಗ ಒಬ್ಬ ಮಹಿಳೆ, “ಅರಸರೇ, ನಮ್ಮ ಒಡೆಯರೇ, ನನ್ನನ್ನು ಕಾಪಾಡಿ,” ಎಂದು ಮೊರೆಯಿಟ್ಟಳು.",
			"27": "<sup>27</sup> ಅರಸನು ಆಕೆಗೆ, “ಸರ್ವೇಶ್ವರಸ್ವಾಮಿ ನಿನ್ನನ್ನು ಕಾಪಾಡದಿದ್ದರೆ ನಾನು ಹೇಗೆ ಕಾಪಾಡಲು ಸಾಧ್ಯ? ಕಣದಲ್ಲಾಗಲಿ, ದ್ರಾಕ್ಷೀ ಆಲೆಯಲ್ಲಾಗಲಿ ಏನಾದರೂ ಇದೆಯೇ?’ ಎಂದು ಕೇಳಿ,",
			"28": "<sup>28</sup> “ನಿನಗಿರುವ ದುಃಖ ಯಾವುದು?” ಎಂದನು. ಆಗ ಆಕೆ, “ಈ ಸ್ತ್ರೀ ನನಗೆ, ‘ನಿನ್ನ ಮಗನನ್ನು ತೆಗೆದುಕೊಂಡು ಬಾ; ನಾವು ಈ ದಿನ ಅವನನ್ನು ತಿಂದುಬಿಡೋಣ; ನಾಳೆ ನನ್ನ ಮಗನನ್ನು ತಿನ್ನೋಣ,’ ಎಂದು ಹೇಳಿದ್ದರಿಂದ ನಾವಿಬ್ಬರೂ ನನ್ನ ಮಗನನ್ನು ಕೊಂದು ಬೇಯಿಸಿ ತಿಂದುಬಿಟ್ಟೆವು.",
			"29": "<sup>29</sup> ಮರುದಿನ ನಾನು ಈಕೆಗೆ, ‘ಈ ದಿನ ನಿನ್ನ ಮಗನನ್ನು ತೆಗೆದುಕೊಂಡು ಬಾ; ಅವನನ್ನು ಕೊಂದು ತಿನ್ನೋಣ,’ ಎಂದಾಗ ಈಕೆ ಅವನನ್ನು ಎಲ್ಲಿಯೋ ಅಡಗಿಸಿಬಿಟ್ಟಳು,” ಎಂದು ಹೇಳಿದಳು.",
			"30": "<sup>30</sup> ಕೋಣೆಯ ಮೇಲೆ ತಿರುಗಾಡುತ್ತಿದ್ದ ಅರಸನು ಆ ಮಹಿಳೆಯ ಮಾತನ್ನು ಕೇಳಿದೊಡನೆ ಸಿಟ್ಟಿನಿಂದ ತನ್ನ ಬಟ್ಟೆಗಳನ್ನು ಹರಿದುಕೊಂಡನು. ಆಗ ಅವನ ಮೈಮೇಲೆ ಗೋಣಿತಟ್ಟು ಇರುವುದು ಜನರಿಗೆ ಕಂಡಿತು.",
			"31": "<sup>31</sup> ಇದಲ್ಲದೆ ಅವನು, “ನಾನು ಈ ದಿನ ಶಾಫಾಟನ ಮಗ ಎಲೀಷನ ತಲೆಯನ್ನು ಹಾರಿಸದಿದ್ದರೆ ದೇವರು ನನಗೆ ಬೇಕಾದದ್ದನ್ನು ಮಾಡಲಿ!” ಎಂದು ಆಣೆಯಿಟ್ಟುಕೊಂಡನು.",
			"32": "<sup>32</sup> ಎಲೀಷನು ಊರಿನ ಹಿರಿಯರೊಡನೆ ತನ್ನ ಮನೆಯಲ್ಲಿ ಕುಳಿತುಕೊಂಡಿದ್ದನು. ಅರಸನು ತನ್ನ ಹತ್ತಿರ ಇದ್ದ ಒಬ್ಬ ಆಳನ್ನು ಕಳುಹಿಸಿದನು. ಅವನು ಇನ್ನೂ ದೂರದಲ್ಲಿರುವಾಗಲೇ ಎಲೀಷನು ಹಿರಿಯರಿಗೆ, “ನೋಡಿ, ಆ ಕೊಲೆಗಾರನ ಮಗನು ನನ್ನ ತಲೆಯನ್ನು ಹಾರಿಸುವುದಕ್ಕೆ ಆಳನ್ನು ಕಳುಹಿಸಿದ್ದಾನೆ. ಆಳು ಇಲ್ಲಿಗೆ ಬಂದ ಕೂಡಲೆ ನೀವು ಬಾಗಿಲನ್ನು ಮುಚ್ಚಿ ಅವನು ಒಳಗೆ ಬಾರದಂತೆ ಅದನ್ನು ಒತ್ತಿಹಿಡಿಯಿರಿ; ಅವನ ಹಿಂದೆಯೇ ಬರುತ್ತಿರುವ ಅವನೊಡೆಯನ ಕಾಲು ಸಪ್ಪಳ ಕೇಳಿಸುತ್ತಿದೆಯಲ್ಲವೆ?” ಎಂದು ಹೇಳಿದನು.",
			"33": "<sup>33</sup> ಅವನು ಅವರೊಡನೆ ಮಾತಾಡುತ್ತಿರುವಾಗಲೇ ಆ ಆಳೂ ಅವನ ಹಿಂದಿನಿಂದ ಅರಸನೂ ಅಲ್ಲಿಗೆ ಬಂದರು. ಅರಸನು ಎಲೀಷನಿಗೆ, “ನೋಡು, ಈ ಆಪತ್ತು ಸರ್ವೇಶ್ವರನಿಂದಲೇ ಬಂದದ್ದು. ಇನ್ನು ಮುಂದೆ ನಾನೇಕೆ ಆತನನ್ನು ನಂಬಿ ನಿರೀಕ್ಷಿಸಿಕೊಂಡಿರಬೇಕು?” ಎಂದನು."
		},
		"7": {
			"1": "<sup>1</sup> ಆಗ ಎಲೀಷನು, “ಸರ್ವೇಶ್ವರಸ್ವಾಮಿಯ ಈ ವಾಕ್ಯವನ್ನು ಕೇಳು: ‘ನಾಳೆ ಇಷ್ಟೇಹೊತ್ತಿಗೆ ಸಮಾರಿಯಪಟ್ಟಣದ ಬಾಗಿಲಿನಲ್ಲಿ ಗೋದಿಹಿಟ್ಟು ನಾಣ್ಯಕ್ಕೆ ಮೂರು ಕಿಲೋಗ್ರಾಂನಂತೆಯೂ ಜವೆಗೋದಿಯು ನಾಣ್ಯಕ್ಕೆ ಆರು ಕಿಲೋಗ್ರಾಂನಂತೆಯೂ ಮಾರಲಾಗುವುದು’ ಎನ್ನುತ್ತಾರೆ,” ಎಂದು ಹೇಳಿದನು.",
			"2": "<sup>2</sup> ಇದನ್ನು ಕೇಳಿ ಅರಸನ ಹಸ್ತಕನಾದ ಸರದಾರನು ದೈವಪುರುಷನಿಗೆ, “ಸರ್ವೇಶ್ವರ ಆಕಾಶದಲ್ಲಿ ದ್ವಾರಗಳನ್ನು ಮಾಡಿದರೂ ಇದು ಸಂಭವಿಸಲಾರದು,” ಎಂದನು. ಅದಕ್ಕೆ ಎಲೀಷನು, “ಅದು ಸಂಭವಿಸುವುದನ್ನು ನೀನು ಕಣ್ಣಾರೆ ಕಾಣುವೆ; ಆದರೆ ಅದನ್ನು ಅನುಭವಿಸಲಾರೆ,” ಎಂದು ಹೇಳಿದನು.",
			"3": "<sup>3</sup> ಊರುಬಾಗಿಲಿನ ಹತ್ತಿರ ನಾಲ್ಕುಮಂದಿ ಕುಷ್ಠರೋಗಿಗಳಿದ್ದರು. ಅವರು ತಮ್ಮೊಳಗೇ, “ನಾವು ಸಾಯುವ ತನಕ ಇಲ್ಲೇ ಕುಳಿತಿರಬೇಕೇ?",
			"4": "<sup>4</sup> ಪಟ್ಟಣದೊಳಗೆ ಕ್ಷಾಮ ಇರುವುದರಿಂದ ಅಲ್ಲಿಗೆ ಹೋದರೂ ಸಾಯಬೇಕು. ಇಲ್ಲಿಯೇ ಕುಳಿತಿದ್ದರೂ ಸಾಯಬೇಕು. ಆದುದರಿಂದ ಹೊರಟುಹೋಗಿ ಸಿರಿಯಾದವರ ಪಾಳೆಯವನ್ನು ಸೇರೋಣ. ಅವರು ನಮ್ಮನ್ನು ಉಳಿಸುವುದಾದರೆ ಉಳಿಸಲಿ, ಕೊಲ್ಲುವುದಾದರೆ ಕೊಲ್ಲಲಿ,” ಎಂದು ಮಾತಾಡಿಕೊಂಡರು.",
			"5": "<sup>5</sup> ಸಾಯಂಕಾಲವಾದಾಗ ಅವರು ಸಿರಿಯಾದವರ ಪಾಳೆಯಕ್ಕೆ ಹೊರಟರು; ಪಾಳೆಯದ ಅಂಚಿಗೆ ಬಂದಾಗ ಅಲ್ಲಿ ಒಬ್ಬನೂ ಕಾಣಿಸಲಿಲ್ಲ.",
			"6": "<sup>6</sup> ಸರ್ವೇಶ್ವರ ಸಿರಿಯಾದ ಪಾಳೆಯದವರಿಗೆ ರಥರಥಾಶ್ವಸಹಿತವಾದ ಮಹಾಸೈನ್ಯಘೋಷವು ಕೇಳಿಸುವಂತೆ ಮಾಡಿದ್ದರು. ಆದ್ದರಿಂದ ಅವರು, “ಇಸ್ರಯೇಲರ ಅರಸನು ಹಿತ್ತಿಯ ಹಾಗು ಈಜಿಪ್ಟ್ ಅರಸರಿಗೆ ಹಣಕೊಟ್ಟು ಅವರನ್ನು ನಮಗೆ ವಿರುದ್ಧ ಕರೆದುತಂದಿದ್ದಾನೆ,” ಎಂದುಕೊಂಡು,",
			"7": "<sup>7</sup> ತಮ್ಮ ಗುಡಾರಗಳನ್ನೂ ಕತ್ತೆಕುದುರೆಗಳನ್ನೂ ಪಾಳೆಯದಲ್ಲಿದ್ದುದೆಲ್ಲವನ್ನೂ ಬಿಟ್ಟು, ತಮ್ಮ ಪ್ರಾಣರಕ್ಷಣೆಗಾಗಿ, ಅದೇ ಸಾಯಂಕಾಲದಲ್ಲಿ ಓಡಿಹೋಗಿದ್ದರು.",
			"8": "<sup>8</sup> ಆ ಕುಷ್ಠರೋಗಿಗಳು ಪಾಳೆಯದ ಅಂಚಿಗೆ ಬಂದಾಗ ಮೊದಲನೆಯ ಡೇರೆಯನ್ನು ಹೊಕ್ಕು ಉಂಡು, ಕುಡಿದು ಅದರಲ್ಲಿದ್ದ ಬೆಳ್ಳಿ ಬಂಗಾರವನ್ನೂ ಬಟ್ಟೆಗಳನ್ನೂ ತೆಗೆದುಕೊಂಡು ಬಚ್ಚಿಟ್ಟರು. ಇನ್ನೊಂದು ಡೇರೆಯನ್ನು ಹೊಕ್ಕು ಅಲ್ಲಿಂದಲೂ ತೆಗೆದುಕೊಂಡು ಅಡಗಿಸಿ ಇಟ್ಟರು.",
			"9": "<sup>9</sup> ಅನಂತರ ಅವರು, “ಇದು ಶುಭವಾರ್ತೆಯ ದಿವಸ; ನಾವು ಇದನ್ನು ಪ್ರಕಟಿಸದಿರುವುದು ಒಳ್ಳೆಯದಲ್ಲ. ಬೆಳಗಾಗುವವರೆಗೆ ತಡಮಾಡಿದರೆ ಶಿಕ್ಷೆಗೆ ಪಾತ್ರರಾಗಬಹುದು. ಆದುದರಿಂದ ಹೋಗಿ ಅರಮನೆಯವರಿಗೆ ಈ ಸಂಗತಿಯನ್ನು ತಿಳಿಸೋಣ,” ಎಂದು ಮಾತಾಡಿಕೊಂಡರು.",
			"10": "<sup>10</sup> ಅಲ್ಲಿಂದ ಹೊರಟು ಊರಬಾಗಿಲಿಗೆ ಬಂದು, ಕಾವಲುಗಾರರಿಗೆ, “ನಾವು ಸಿರಿಯಾದವರ ಪಾಳೆಯಕ್ಕೆ ಹೋಗಿದ್ದೆವು; ಅಲ್ಲಿ ನಮಗೆ ಯಾರೂ ಕಾಣಿಸಲಿಲ್ಲ, ಮನುಷ್ಯಶಬ್ದವೇ ಕೇಳಿಸಲಿಲ್ಲ. ಕತ್ತೆಕುದುರೆಗಳನ್ನು ಅಲ್ಲಲ್ಲಿ ಕಟ್ಟಲಾಗಿದೆ. ಡೇರೆಗಳು ಇದ್ದ ಹಾಗೆಯೇ ಇರುತ್ತವೆ,” ಎಂದು ಕೂಗಿ ತಿಳಿಸಿದರು.",
			"11": "<sup>11</sup> ದ್ವಾರಪಾಲಕರು ಕೂಡಲೆ ಅರಮನೆಯವರನ್ನು ಕೂಗಿ ಅವರಿಗೆ ಈ ಸಮಾಚಾರವನ್ನು ಮುಟ್ಟಿಸಿದರು.",
			"12": "<sup>12</sup> ಅರಸನು ರಾತ್ರಿಯಲ್ಲೇ ಎದ್ದು ತನ್ನ ಪರಿವಾರದವರಿಗೆ, “ಸಿರಿಯಾದವರ ಹಂಚಿಕೆಯನ್ನು ಹೇಳುತ್ತೇನೆ ಕೇಳಿ; ನಾವು ಹಸಿವೆಯಿಂದ ಸಾಯುವವರಾಗಿದ್ದೇವೆ ಎಂಬುದನ್ನು ಅವರು ಬಲ್ಲರು. ಆದುದರಿಂದ ಪಾಳೆಯವನ್ನು ಬಿಟ್ಟುಹೋಗಿ ಅಡವಿಯಲ್ಲಿ ಅಡಗಿಕೊಂಡಿದ್ದಾರೆ; ನಾವು ಪಟ್ಟಣದಿಂದ ಹೊರಗೆ ಹೋದ ಕೂಡಲೆ ನಮ್ಮನ್ನು ಸಜೀವಿಗಳನ್ನಾಗಿಯೇ ಹಿಡಿದು ಪಟ್ಟಣವನ್ನು ಪ್ರವೇಶಿಸಬೇಕೆಂದಿದ್ದಾರೆ,” ಎಂದನು.",
			"13": "<sup>13</sup> ಆಗ ಅವರಲ್ಲೊಬ್ಬನು ಅರಸನಿಗೆ, “ರಾಹುತರು ಪಟ್ಟಣದಲ್ಲುಳಿದಿರುವ ಕುದುರೆಗಳಲ್ಲಿ ನಾಲ್ಕೈದು ಕುದುರೆಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ನೋಡುವುದಕ್ಕೆ ಹೋಗಲಿ; ಅವರಿಗೆ ಪಟ್ಟಣದಲ್ಲಿ ಉಳಿದಿರುವ ಇಸ್ರಯೇಲರ ಗತಿಯಾಗಲಿ ಅಥವಾ ಸತ್ತುಹೋದವರ ಗತಿಯಾಗಲಿ ಸಂಬವಿಸುವದಷ್ಟೆ,” ಎಂದು ಹೇಳಿದನು.",
			"14": "<sup>14</sup> ಕೂಡಲೆ ಅರಸನು ಎರಡು ಜೋಡಿ ಕುದುರೆಗಳನ್ನು ತರಿಸಿ, ಸಿರಿಯಾದವರ ಸೈನ್ಯವೆಲ್ಲಿರುತ್ತದೆಂಬುದನ್ನು ನೋಡಿಬರುವುದಕ್ಕಾಗಿ, ರಾಹುತರನ್ನು ಕಳುಹಿಸಿದನು.",
			"15": "<sup>15</sup> ಅವರು ಜೋರ್ಡನ್ ನದಿಯವರೆಗೂ ಹೋಗಿ, ದಾರಿಯಲ್ಲೆಲ್ಲಾ ತಮ್ಮ ಬಟ್ಟೆಬರೆಗಳನ್ನೂ ಸಾಮಾನು ಸರಂಜಾಮುಗಳನ್ನೂ ಬಿಸಾಡಿರುವುದನ್ನು ಕಂಡು, ಹಿಂದಿರುಗಿ ಬಂದು ಅರಸನಿಗೆ ತಿಳಿಸಿದರು.",
			"16": "<sup>16</sup> ಆಗ ಜನರು ಹೊರಗೆ ಹೋಗಿ, ಸಿರಿಯಾದವರ ಪಾಳೆಯವನ್ನು ಸೂರೆಮಾಡಿಬಿಟ್ಟರು. ಸರ್ವೇಶ್ವರನ ಮಾತಿನಂತೆ ಗೋದಿಹಿಟ್ಟು ನಾಣ್ಯಕ್ಕೆ ಮೂರು ಕಿಲೋಗ್ರಾಂನಂತೆಯೂ ಜವೆಗೋದಿಯು ನಾಣ್ಯಕ್ಕೆ ಆರು ಕಿಲೋಗ್ರಾಂನಂತೆಯೂ ಮಾರಲಾಗುತ್ತಿತ್ತು.",
			"17": "<sup>17</sup> ಅರಸನು ತನ್ನ ಹಸ್ತಕನಾದ ಸರದಾರನನ್ನು ಊರ ದ್ವಾರಪಾಲಕರ ನಾಯಕನನ್ನಾಗಿ ಕಳುಹಿಸಿದ್ದನು. ಅವನು ಜನರ ತುಳಿತಕ್ಕೆ ಸಿಕ್ಕಿ ಸತ್ತನು. ಹೀಗೆ ಆ ದೈವಪುರುಷನು ಅರಸನ ಜೊತೆಯಲ್ಲಿ ತನ್ನ ಬಳಿಗೆ ಬಂದಿದ್ದ ಆ ಸರದಾರನಿಗೆ ಹೇಳಿದ ಮಾತು ನೆರವೇರಿತು.",
			"18": "<sup>18</sup> ಅವನು ಅರಸನಿಗೆ, “ನಾಳೆ ಇಷ್ಟು ಹೊತ್ತಿಗೆ ಸಮಾರಿಯ ಪಟ್ಟಣದ ಬಾಗಿಲಿನಲ್ಲಿ ಜವೆಗೋದಿ ನಾಣ್ಯಕ್ಕೆ ಆರು ಕಿಲೋಗ್ರಾಂನಂತೆಯೂ ಗೋದಿಹಿಟ್ಟು ನಾಣ್ಯಕ್ಕೆ ಮೂರು ಕಿಲೋಗ್ರಾಂನಂತೆಯೂ ಮಾರಲಾಗುವುದು,” ಎಂದು ಹೇಳಿದಾಗ",
			"19": "<sup>19</sup> ಆ ಸರದಾರನು, “ಸರ್ವೇಶ್ವರ ಆಕಾಶದಲ್ಲಿ ದ್ವಾರಗಳನ್ನು ಮಾಡಿದರೂ ಇದು ಸಂಭವಿಸಲಾರದು,” ಎಂದದ್ದರಿಂದ ದೈವಪುರುಷನು ಅವನಿಗೆ, “ನೀನು ಅದನ್ನು ಕಣ್ಣಾರೆ ಕಾಣುವೆ; ಆದರೆ ಅದನ್ನು ಅನುಭವಿಸಲಾರೆ,” ಎಂದು ನುಡಿದಿದ್ದನು.",
			"20": "<sup>20</sup> ಊರು ಬಾಗಿಲಿನಲ್ಲಿ ಜನರಿಂದ ತುಳಿತಕ್ಕೆ ಸಿಕ್ಕಿ ಅವನು ಸತ್ತದ್ದರಿಂದ ಈ ಮಾತು ನೆರವೇರಿತು."
		},
		"8": {
			"1": "<sup>1</sup> ಎಲೀಷನು ತಾನು ಬದುಕಿಸಿದ ಹುಡುಗನ ತಾಯಿಗೆ, “ಸರ್ವೇಶ್ವರಸ್ವಾಮಿ ಈ ನಾಡಿಗೆ ಏಳು ವರ್ಷಗಳ ಬರಗಾಲವನ್ನು ಕಳುಹಿಸಲಿದ್ದಾರೆ. ಆದುದರಿಂದ ನೀನು ನಿನ್ನ ಮನೆಯವರೊಡನೆ ಯಾವುದಾದರೊಂದು ಪರದೇಶಕ್ಕೆ ಹೋಗಿ ಅಲ್ಲಿ ವಾಸಿಸು,” ಎಂದು ಭವಿಷ್ಯ ಹೇಳಿದನು.",
			"2": "<sup>2</sup> ಆಕೆ ದೈವಪುರುಷನ ಅಪ್ಪಣೆಯಂತೆ ತನ್ನ ಮನೆಯವರೊಡನೆ ಸ್ವಂತನಾಡನ್ನು ಬಿಟ್ಟುಹೋಗಿ ಫಿಲಿಷ್ಟಿಯರ ದೇಶದಲ್ಲಿ ವಾಸವಾಗಿದ್ದಳು.",
			"3": "<sup>3</sup> ಏಳು ವರ್ಷಗಳಾದನಂತರ ಆಕೆ ಅಲ್ಲಿಂದ ಸ್ವಂತ ನಾಡಿಗೆ ಬಂದು, ಪರರ ಕೈವಶವಾಗಿದ್ದ ತನ್ನ ಹೊಲಮನೆಗಳಿಗಾಗಿ ಮೊರೆಯಿಡಲು ಅರಸನ ಬಳಿಗೆ ಬಂದಳು.",
			"4": "<sup>4</sup> ಆ ಸಮಯದಲ್ಲೇ ಅರಸನು, ದೈವಪುರುಷನ ಸೇವಕನಾದ ಗೇಹಜಿಗೆ, “ಎಲೀಷನು ಮಾಡಿದ ಎಲ್ಲಾ ಅದ್ಭುತಕಾರ್ಯಗಳನ್ನು ನನಗೆ ವಿವರಿಸು,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದ್ದನು.",
			"5": "<sup>5</sup> ಎಲೀಷನು ಸತ್ತ ಹುಡುಗನಿಗೆ ಜೀವದಾನಮಾಡಿದ ಸಂಗತಿಯನ್ನು ಗೇಹಜಿಯು ವಿವರಿಸುತ್ತಿರುವಾಗಲೇ, ಜೀವದಾನಹೊಂದಿದ ಹುಡುಗನ ತಾಯಿ ತನ್ನ ಹೊಲಮನೆಗಳಿಗಾಗಿ ಮೊರೆಯಿಡಲು ಅರಸನ ಬಳಿಗೆ ಬಂದಳು. ಕೂಡಲೆ ಗೇಹಜಿಯು, “ಅರಸರೇ, ನನ್ನ ಒಡೆಯರೇ, ಎಲೀಷನು ಬದುಕಿಸಿದ ಹುಡುಗನು ಇವನೇ; ಈಕೆ ಇವನ ತಾಯಿ,” ಎಂದು ಹೇಳಿದನು.",
			"6": "<sup>6</sup> ಅರಸನು ಈ ಸಂಗತಿಯ ವಿಷಯವಾಗಿ ಆ ಸ್ತ್ರೀಯನ್ನೇ ವಿಚಾರಿಸಿದನು. ಆಕೆ ಅದರಂತೆಯೇ ಹೇಳಿದಳು. ಆಗ ಅರಸನು ಒಬ್ಬ ಕಂಚುಕಿಯನ್ನು ಕರೆದು, “ಹೋಗಿ ಈಕೆಯ ಹೊಲಮನೆಗಳನ್ನೂ ಈಕೆ ಈ ನಾಡನ್ನು ಬಿಟ್ಟಂದಿನಿಂದ ಇಂದಿನವರೆಗೂ ಈಕೆಗೆ ಸಲ್ಲತಕ್ಕ ಹೊಲದ ಆದಾಯವನ್ನೂ ಕೊಡಿಸು,” ಎಂದು ಆಜ್ಞಾಪಿಸಿ ಅವನನ್ನು ಆಕೆಯ ಜೊತೆಯಲ್ಲೇ ಕಳುಹಿಸಿದನು.",
			"7": "<sup>7</sup> ಒಮ್ಮೆ ಎಲೀಷನು ದಮಸ್ಕಕ್ಕೆ ಹೋದನು. ಆಗ ಸಿರಿಯಾದ ಅರಸನಾದ ಬೆನ್ಹದದನು ಅಸ್ವಸ್ಥನಾಗಿದ್ದನು. ದೈವಪುರುಷ ಬಂದಿದ್ದಾನೆಂಬ ಸುದ್ದಿಯನ್ನು ಕೇಳಿ,",
			"8": "<sup>8</sup> ಅವನು ಹಜಾಯೇಲನನ್ನು ಕರೆದು, “ನೀನು ಕಾಣಿಕೆಯನ್ನು ತೆಗೆದುಕೊಂಡು ದೈವಪುರುಷನ ಬಳಿಗೆ ಹೋಗಿ ನನಗೆ ಗುಣ ಆಗುವುದೋ ಇಲ್ಲವೋ ಎಂಬುದನ್ನು ಅವನ ಮುಖಾಂತರ ಸರ್ವೇಶ್ವರನ ಸನ್ನಿಧಿಯಲ್ಲಿ ವಿಚಾರಿಸು,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"9": "<sup>9</sup> ಹಜಾಯೇಲನು ದಮಸ್ಕದ ಶ್ರೇಷ್ಠವಸ್ತುಗಳಲ್ಲಿ, ನಾಲ್ವತ್ತು ಒಂಟೆಗಳು ಹೊರುವಷ್ಟನ್ನು ತೆಗೆದುಕೊಂಡು, ಎಲೀಷನ ಬಳಿಗೆ ಹೋಗಿ ಅವನ ಮುಂದೆ ನಿಂತು, “ನಿಮ್ಮ ಕುಮಾರ, ಸಿರಿಯಾದವರ ಅರಸನಾದ ಬೆನ್ಹದದರು, ತಮಗೆ ಗುಣವಾಗುವುದೋ ಇಲ್ಲವೋ ಎಂಬುದನ್ನು ವಿಚಾರಿಸುವುದಕ್ಕಾಗಿ ನನ್ನನ್ನು ನಿಮ್ಮ ಬಳಿಗೆ ಕಳುಹಿಸಿದ್ದಾರೆ,” ಎಂದು ಹೇಳಿದನು.",
			"10": "<sup>10</sup> ಆಗ ದೈವಪುರುಷನಾದ ಎಲೀಷನು, “ಖಂಡಿತವಾಗಿ ಗುಣವಾಗುವುದು ಎಂಬುದಾಗಿ ಬೆನ್ಹದದನಿಗೆ ಹೇಳು; ಆದರೂ ಅವನು ಸತ್ತುಹೋಗುವುದು ನಿಶ್ಚಯವೆಂದು ಸರ್ವೇಶ್ವರ ನನಗೆ ತಿಳಿಸಿದ್ದಾರೆ,”",
			"11": "<sup>11</sup> ಎಂದು ಹೇಳಿ ಬಿಗಿ ಮುಖದಿಂದ, ಸ್ಥಿರದೃಷ್ಟಿಯಿಂದ ಹಜಾಯೇಲನನ್ನೇ ದಿಟ್ಟಿಸಿ ನೋಡಿ ಅಳತೊಡಗಿದನು.",
			"12": "<sup>12</sup> ಹಜಾಯೇಲನು, “ನನ್ನೊಡೆಯಾ, ಏಕೆ ಅಳುತ್ತೀರಿ?” ಎಂದು ಕೇಳಿದನು. ಅದಕ್ಕೆ ಅವನು, “ನೀನು ಇಸ್ರಯೇಲರಿಗೆ ಎಷ್ಟು ಕೇಡು ಮಾಡುವೆಯೆಂಬುದು ನನಗೆ ಪ್ರಕಟವಾಯಿತು. ನೀನು ಅವರ ಕೋಣೆಗಳಿಗೆ ಬೆಂಕಿ ಹೊತ್ತಿಸುವೆ; ಯೌವನಸ್ಥರನ್ನು ಕತ್ತಿಯಿಂದ ಸಂಹರಿಸುವೆ; ಮಕ್ಕಳನ್ನು ಬಂಡೆಗೆ ಅಪ್ಪಳಿಸುವೆ; ಗರ್ಭಿಣಿಯರ ಹೊಟ್ಟೆಯನ್ನು ಸೀಳುವೆ,” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"13": "<sup>13</sup> ಹಜಾಯೇಲನು ಎಲೀಷನನ್ನು, “ನಿಮ್ಮ ಸೇವಕನಾದ ನಾನು ನಾಯಿಯಂತಿರುತ್ತೇನಷ್ಟೆ; ನನ್ನಿಂದ ಇಂಥ ಮಹಾಕಾರ್ಯವಾಗುವುದು ಹೇಗೆ?” ಎಂದು ಕೇಳಿದನು. ಅವನು, “ನೀನು ಸಿರಿಯಾದವರ ರಾಜನಾಗುವೆಯೆಂದು ಸರ್ವೇಶ್ವರ ನನಗೆ ತಿಳಿಸಿದ್ದಾರೆ,” ಎಂದನು.",
			"14": "<sup>14</sup> ಹಜಾಯೇಲನು ಎಲೀಷನನ್ನು ಬಿಟ್ಟು ತನ್ನ ಯಜಮಾನನ ಬಳಿಗೆ ಹೋದಾಗ ಅವನು, “ಎಲೀಷ ಏನು ಹೇಳಿದರು?” ಎಂದು ಕೇಳಿದನು. ಅದಕ್ಕೆ ಇವನು, “ನಿಮಗೆ ಖಂಡಿತವಾಗಿ ಗುಣವಾಗುವುದೆಂದು ಹೇಳಿದರು,” ಎಂಬುದಾಗಿ ಉತ್ತರಕೊಟ್ಟನು.",
			"15": "<sup>15</sup> ಮರುದಿನ ಬೆಳಿಗ್ಗೆ ಇವನು ಒಂದು ಹೊದಿಕೆಯನ್ನು ನೀರಿನಲ್ಲಿ ತೋಯಿಸಿ ಅರಸನ ಮುಖದ ಮೇಲೆ ಹಾಕಿದನು; ಅವನು ಸತ್ತನು. ಹಜಾಯೇಲನು ಅವನ ಸ್ಥಾನದಲ್ಲಿ ಅರಸನಾದನು.",
			"16": "<sup>16</sup> ಅಹಾಬನ ಮಗನೂ ಇಸ್ರಯೇಲರ ಅರಸನೂ ಆದ ಯೋರಾಮನ ಆಳ್ವಿಕೆಯ ಐದನೆಯ ವರ್ಷದಲ್ಲಿ ಜುದೇಯದ ರಾಜನಾದ ಯೆಹೋಷಾಫಾಟನ ಮಗ ಯೆಹೋರಾಮನು ಆಳತೊಡಗಿದನು.",
			"17": "<sup>17</sup> ಇವನು ಪಟ್ಟಕ್ಕೆ ಬಂದಾಗ ಮೂವತ್ತೆರಡು ವರ್ಷದವನಾಗಿದ್ದನು. ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಎಂಟು ವರ್ಷ ಆಳಿದನು.",
			"18": "<sup>18</sup> ಇವನು ಅಹಾಬನ ಮಗಳನ್ನು ಮದುವೆಮಾಡಿಕೊಂಡದ್ದರಿಂದ ಅಹಾಬನ ಕುಟುಂಬದವರಾದ ಇಸ್ರಯೇಲ್ ಅರಸರ ಹೆಜ್ಜೆಯಲ್ಲೇ ನಡೆದು ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾದನು.",
			"19": "<sup>19</sup> ಆದರೂ ಸರ್ವೇಶ್ವರ ಜುದೇಯದ ರಾಜ್ಯವನ್ನು ನಾಶಮಾಡಲಿಲ್ಲ. “ನಿನ್ನನ್ನು ಮತ್ತು ನಿನ್ನ ಸಂತಾನದವರ ದೀಪವನ್ನು ಎಂದೂ ನಂದಿಸುವುದಿಲ್ಲ,” ಎಂಬುದಾಗಿ ತಮ್ಮ ದಾಸ ದಾವೀದನಿಗೆ ಮಾಡಿದ ಪ್ರಮಾಣವನ್ನು ಸ್ಮರಿಸಿ ಅದನ್ನು ಉಳಿಸಿದರು.",
			"20": "<sup>20</sup> ಇವನ ಕಾಲದಲ್ಲಿ ಎದೋಮ್ಯರು ಯೆಹೂದ್ಯರಿಗೆ ವಿರುದ್ಧ ದಂಗೆ ಎದ್ದು ಸ್ವತಂತ್ರರಾಗಿ ತಮಗೆ ಒಬ್ಬ ಅರಸನನ್ನು ನೇಮಿಸಿಕೊಂಡರು.",
			"21": "<sup>21</sup> ಆಗ ಯೆಹೋರಾಮನು ತನ್ನ ಎಲ್ಲಾ ರಥಬಲಸಹಿತವಾಗಿ ಜೋರ್ಡನ್ ಹೊಳೆಯನ್ನು ದಾಟಿ ಚಾಯೀರಿಗೆ ಹೋದನು. ಎದೋಮ್ಯರು ಬಂದು ಅವನನ್ನು ಸುತ್ತಿಕೊಂಡರು. ಅವನು ರಾತ್ರಿಯಲ್ಲೆದ್ದು ಅವರನ್ನೂ ಅವರ ರಥಬಲದ ಅಧಿಪತಿಗಳನ್ನೂ ಸೋಲಿಸಿ ಪಾರಾದನು. ಅವನ ಸೈನ್ಯದವರೂ ತಮ್ಮ ತಮ್ಮ ನಿವಾಸಗಳಿಗೆ ಓಡಿಹೋದರು.",
			"22": "<sup>22</sup> ಹೀಗೆ ಎದೋಮ್ಯರು ಯೆಹೂದ್ಯರಿಗೆ ವಿರುದ್ಧ ದಂಗೆ ಎದ್ದರು. ಅವರು ಅಂದಿನಿಂದ ಇಂದಿನವರೆಗೂ ಸ್ವತಂತ್ರರಾಗಿರುತ್ತಾರೆ. ಅದೇ ಕಾಲದಲ್ಲಿ ಲಿಬ್ನದವರೂ ಸ್ವತಂತ್ರರಾದರು.",
			"23": "<sup>23</sup> ಯೆಹೋರಾಮನ ಉಳಿದ ಚರಿತ್ರೆ ಹಾಗು ಕಾರ್ಯಕಲಾಪಗಳು ಜುದೇಯದ ರಾಜರ ಇತಿಹಾಸ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಲಿಖಿತವಾಗಿವೆ.",
			"24": "<sup>24</sup> ಅವನು ನಿಧನನಾಗಿ ಪಿತೃಗಳ ಬಳಿ ಸೇರಲು ಅವನ ಶವವನ್ನು ದಾವೀದನಗರದೊಳಗೆ ಕುಟುಂಬ ಸ್ಮಶಾನ ಭೂಮಿಯಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಅವನ ಸ್ಥಾನದಲ್ಲಿ ಅವನ ಮಗ ಅಹಜ್ಯನು ಅರಸನಾದನು.",
			"25": "<sup>25</sup> ಅಹಾಬನ ಮಗನೂ ಇಸ್ರಯೇಲರ ಅರಸನೂ ಆದ ಯೋರಾಮನ ಆಳ್ವಿಕೆಯ ಹನ್ನೆರಡನೆಯ ವರ್ಷದಲ್ಲಿ ಜುದೇಯದ ಅರಸನಾದ ಯೆಹೋರಾಮನ ಮಗ ಅಹಜ್ಯನು ಅರಸನಾದನು.",
			"26": "<sup>26</sup> ಇವನು ಪಟ್ಟಕ್ಕೆ ಬಂದಾಗ ಇಪ್ಪತ್ತೆರಡು ವರ್ಷದ ವಯಸ್ಸಿನವನಾಗಿದ್ದನು. ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಒಂದು ವರ್ಷ ಆಳಿದನು. ಇಸ್ರಯೇಲರ ಅರಸನಾದ ಒಮ್ರಿಯನ ಮೊಮ್ಮಗಳಾದ ಅತಲ್ಯ ಎಂಬಾಕೆ ಇವನ ತಾಯಿ.",
			"27": "<sup>27</sup> ಇವನಿಗೂ ಅಹಾಬನ ಕುಟುಂಬದವರಿಗೂ ಬೀಗತನ ಇದ್ದುದರಿಂದ ಇವನು ಅವರ ಹೆಜ್ಜೆಯಲ್ಲೇ ನಡೆದು ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾದನು.",
			"28": "<sup>28</sup> ಇದಲ್ಲದೆ, ಇವನು ಸಿರಿಯಾದವರ ಅರಸನಾದ ಹಜಾಯೇಲನೊಡನೆ ಯುದ್ಧಮಾಡುವುದಕ್ಕಾಗಿ ಅಹಾಬನ ಮಗ ಯೋರಾಮನ ಜೊತೆಯಲ್ಲಿ ರಾಮೋತ್ ಗಿಲ್ಯಾದಿಗೆ ಹೋದನು.",
			"29": "<sup>29</sup> ಸಿರಿಯಾದವರು ಯೋರಾಮನನ್ನು ಗಾಯಪಡಿಸಿದರು. ಯುದ್ಧದಲ್ಲಾದ ಗಾಯಗಳನ್ನು ಮಾಗಿಸಿಕೊಳ್ಳುವುದಕ್ಕಾಗಿ ಅವನು ಜೆಸ್ರೀಲಿಗೆ ಬಂದನು. ಅವನು ಅಸ್ವಸ್ಥನಾಗಿದ್ದುದರಿಂದ ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಯೆಹೋರಾಮನ ಮಗ ಅಹಜ್ಯನು ಅವನನ್ನು ನೋಡುವುದಕ್ಕಾಗಿ ಅಲ್ಲಿಗೆ ಹೋದನು."
		},
		"9": {
			"1": "<sup>1</sup> ಪ್ರವಾದಿ ಎಲೀಷನು ಪ್ರವಾದಿಮಂಡಲಿಯವರಲ್ಲಿ ಒಬ್ಬನನ್ನು ಕರೆದು, “ಈ ಎಣ್ಣೆಯ ಕುಪ್ಪಿಯನ್ನು ತೆಗೆದುಕೊಂಡು ರಾಮೋತ್ ಗಿಲ್ಯಾದಿಗೆ ಹೋಗಲು ಸಿದ್ಧನಾಗು.",
			"2": "<sup>2</sup> ಆ ಊರನ್ನು ಮುಟ್ಟಿದನಂತರ ನಿಂಷಿಯ ಮೊಮ್ಮಗನೂ ಯೆಹೋಷಾಫಾಟನ ಮಗನೂ ಆದ ಯೇಹುವು ಎಲ್ಲಿರುತ್ತಾನೆಂದು ವಿಚಾರಿಸು. ಅವನು ಸಿಕ್ಕಿದಾಗ ಅವನನ್ನು ಅವನ ಜೊತೆಗಾರರ ಗುಂಪಿನಿಂದ ಪ್ರತ್ಯೇಕವಾಗಿ ಕರೆದುಕೊಂಡು ಒಳಗಿನ ಕೋಣೆಗೆ ಹೋಗು.",
			"3": "<sup>3</sup> ತರುವಾಯ ಕುಪ್ಪಿಯಲ್ಲಿರುವ ಈ ಎಣ್ಣೆಯನ್ನು ಅವನ ತಲೆಯ ಮೇಲೆ ಹೊಯ್ದು, ‘ನಾನು ನಿನ್ನನ್ನು ಇಸ್ರಯೇಲರ ಅರಸನಾಗುವುದಕ್ಕೆ ಅಭಿಷೇಕಿಸಿದ್ದೇನೆಂದು ಸರ್ವೇಶ್ವರ ನುಡಿದಿದ್ದಾರೆ,’ ಎಂಬುದಾಗಿ ಹೇಳಿ ಕೂಡಲೆ ಬಾಗಿಲು ತೆರೆದು ಓಡಿಹೋಗು,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"4": "<sup>4</sup> ಆಗ ಪ್ರವಾದಿಯ ಸೇವಕನಾದ ಆ ಯೌವನಸ್ಥನು ರಾಮೋತ್ ಗಿಲ್ಯಾದಿಗೆ ಹೋಗಿ,",
			"5": "<sup>5</sup> ಸೈನ್ಯಾಧಿಪತಿಗಳೆಲ್ಲಾ ಒಂದು ಕಡೆಯಲ್ಲಿ ಕೂಡಿರುವುದನ್ನು ಕಂಡು, ಅವರನ್ನು ಸಮೀಪಿಸಿ, “ಸೇನಾಪತಿಯೇ, ನಿಮಗೊಂದು ಮಾತು ಹೇಳಬೇಕಾಗಿದೆ,” ಎಂದನು. ಯೇಹುವು, “ನಮ್ಮಲ್ಲಿ ಯಾರಿಗೆ?” ಎಂದು ಕೇಳಿದನು. ಅವನು, “ಸೇನಾಪತಿಯಾದ ನಿಮಗೇ ಹೇಳಬೇಕಾಗಿದೆ,” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"6": "<sup>6</sup> ಯೇಹುವು ಎದ್ದು ಒಳಗಿನ ಕೋಣೆಗೆ ಹೋಗಲು, ಆ ಯೌವನಸ್ಥನು ಇವನ ತಲೆಯ ಮೇಲೆ ಎಣ್ಣೆಯನ್ನು ಹೊಯ್ದು, “ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಮಾತನ್ನು ಕೇಳು; ಅವರು ನಿನಗೆ, ‘ನಾನು ನಿನ್ನನ್ನು ನನ್ನ ಪ್ರಜೆಯಾದ ಇಸ್ರಯೇಲರ ಅರಸನಾಗುವುದಕ್ಕೆ ಅಭಿಷೇಕಿಸಿದ್ದೇನೆ.",
			"7": "<sup>7</sup> ನೀನು ನಿನ್ನ ಯಜಮಾನನಾದ ಅಹಾಬನ ಮನೆಯವರನ್ನು ಸಂಹರಿಸಿಬಿಡು; ಆಗ ನನ್ನ ಸೇವಕರಾದ ಪ್ರವಾದಿಗಳೇ ಮೊದಲಾದ ಸರ್ವೇಶ್ವರನ ಭಕ್ತರ ರಕ್ತವನ್ನು ಸುರಿಸಿದ್ದಕ್ಕಾಗಿ ನಾನು ಈಜೆಬೆಲಳಿಗೆ ಮುಯ್ಯಿ ತೀರಿಸಿದಂತಾಗುವುದು.",
			"8": "<sup>8</sup> ಅಹಾಬನ ಕುಟುಂಬದವರೆಲ್ಲಾ ನಿರ್ನಾಮವಾಗಬೇಕು. ಅವನ ಸಂತಾನದ ಗಂಡಸರಲ್ಲಿ ಸ್ವತಂತ್ರರಾಗಿರಲಿ, ಪರತಂತ್ರರಾಗಿರಲಿ ಎಲ್ಲರನ್ನೂ ಇಸ್ರಯೇಲರ ಮಧ್ಯೆಯಿಂದ ತೆಗೆದುಹಾಕುವೆನು.",
			"9": "<sup>9</sup> ನೆಬಾಟನ ಮಗ ಯಾರೊಬ್ಬಾಮನ ಮನೆಗೂ ಅಹೀಯನ ಮಗ ಬಾಷನ ಮನೆಗೂ ಆದ ಗತಿಯೇ ಅಹಾಬನ ಮನೆಗೂ ಆಗುವುದು.",
			"10": "<sup>10</sup> ಈಜೆಬೆಲಳ ಶವವು ಸಮಾಧಿಯನ್ನು ಸೇರುವುದಿಲ್ಲ; ನಾಯಿಗಳು ಅದನ್ನು ಜೆಸ್ರೀಲಿನ ಹೊಲದಲ್ಲಿ ತಿಂದುಬಿಡುವುವು;’ ಎನ್ನುತ್ತಾರೆ,” ಎಂದು ಹೇಳಿ ಬಾಗಿಲು ತೆರೆದು ಓಡಿಹೋದನು.",
			"11": "<sup>11</sup> ಯೇಹುವು ಮರಳಿ ತನ್ನ ಒಡೆಯನ ಸೇವಕರ ಹತ್ತಿರ ಬಂದನು. ಅವರು ಅವನನ್ನು, “ಶುಭವಾರ್ತೆಯೋ? ಆ ಹುಚ್ಚನು ನಿನ್ನ ಬಳಿಗೆ ಬಂದದ್ದೇಕೆ?” ಎಂದು ಕೇಳಿದರು. ಅದಕ್ಕೆ ಅವನು, “ಆ ಮನುಷ್ಯ ಎಂಥವನು, ಅವನ ಮಾತು ಎಂಥದು ಎಂಬುದು ನಿಮಗೆ ಗೊತ್ತಲ್ಲವೆ?” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"12": "<sup>12</sup> ಆದರೆ ಅವರು, “ಅದು ಸುಳ್ಳು; ಅವನು ಹೇಳಿದ್ದನ್ನು ತಿಳಿಸು,” ಎಂದು ಅವನನ್ನು ಒತ್ತಾಯಪಡಿಸಿದರು. ಆದ್ದರಿಂದ ಅವನು, “ನಿನ್ನನ್ನು ಇಸ್ರಯೇಲರ ಅರಸನಾಗುವುದಕ್ಕೆ ಅಭಿಷೇಕಿಸಿದ್ದಾನೆಂಬುದಾಗಿ ಸರ್ವೇಶ್ವರ ನುಡಿದಿದ್ದಾರೆ ಎಂಬೀ ಮೊದಲಾದ ಮಾತುಗಳನ್ನು ಹೇಳಿದನು,” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"13": "<sup>13</sup> ಕೂಡಲೆ ಅವರು ಅವನಿಗಾಗಿ ಮೆಟ್ಟಲುಗಳ ಮೇಲೆ ತಮ್ಮ ಬಟ್ಟೆಗಳನ್ನು ಹಾಸಿ, ತುತೂರಿಯನ್ನು ಊದಿಸಿ, “ಯೇಹುವು ಅರಸನಾಗಿದ್ದಾನೆ,” ಎಂದು ಆರ್ಭಟಿಸಿದರು.",
			"14": "<sup>14</sup> ಹೀಗೆ ನಿಂಷಿಯ ಮೊಮ್ಮಗನೂ ಯೆಹೋಷಾಫಾಟನ ಮಗನೂ ಆದ ಯೇಹುವು ಯೋರಾಮನಿಗೆ ಒಳಸಂಚು ಮಾಡಿದನು. (ಸಿರಿಯಾದವರ ಅರಸನಾದ ಹಜಾಯೇಲನು ರಾಮೋತ್ ಗಿಲ್ಯಾದಿಗೆ ಮುತ್ತಿಗೆ ಹಾಕಿದನು. ಯೋರಾಮನು ಅದನ್ನು ಕಾಪಾಡುವುದಕ್ಕಾಗಿ ಎಲ್ಲಾ ಇಸ್ರಯೇಲರ ಸಹಿತ ಹೋಗಿ, ಹಜಾಯೇಲನೊಡನೆ ಯುದ್ಧಮಾಡಿದನು.",
			"15": "<sup>15</sup> ಆಗ ಗಾಯಗೊಂಡು ಅದನ್ನು ಮಾಗಿಸಿಕೊಳ್ಳುವುದಕ್ಕಾಗಿ ಹಿಂದಿರುಗಿ ಜೆಸ್ರೀಲಿಗೆ ಬಂದಿದ್ದನು.) ಯೇಹುವು ತನ್ನ ಜೊತೆಗಾರನಿಗೆ, “ನೀವು ನನ್ನವರಾಗಿದ್ದರೆ, ಈ ಸುದ್ದಿ ಜೆಸ್ರೀಲನ್ನು ಮುಟ್ಟದಂತೆ, ಒಬ್ಬನನ್ನೂ ಈ ಪಟ್ಟಣದಿಂದ ಹೊರಗೆ ಬಿಡಬೇಡಿ,” ಎಂದು ಹೇಳಿದನು.",
			"16": "<sup>16</sup> ಅನಂತರ ಅವನು ರಥದಲ್ಲಿ ಕುಳಿತು ಜೆಸ್ರೀಲಿಗೆ ಹೋಗುವುದಕ್ಕಾಗಿ ಹೊರಟನು. ಅಲ್ಲಿ ಅಸ್ವಸ್ಥನಾದ ಯೋರಾಮನೂ ಅವನನ್ನು ನೋಡುವುದಕ್ಕೆ ಬಂದಿದ್ದ ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಅಹಜ್ಯನೂ ಇದ್ದರು.",
			"17": "<sup>17</sup> ಜೆಸ್ರೀಲಿನ ಗೋಪುರದಲ್ಲಿದ್ದ ಕಾವಲುಗಾರನು ಯೇಹುವಿನ ಗುಂಪಿನವರನ್ನು ಕಂಡು, “ಜನರ ಒಂದು ಗುಂಪು ಕಾಣಿಸುತ್ತಿದೆ,” ಎಂದು ಯೋರಾಮನಿಗೆ ತಿಳಿಸಿದನು. ಯೋರಾಮನು, “ನೀನು ಒಬ್ಬ ರಾಹುತನನ್ನು ಕರೆದು, ಬರುತ್ತಿರುವವರು ಶುಭವಾರ್ತೆಯನ್ನು ತರುತ್ತಾರೋ ಇಲ್ಲವೋ ಎಂದು ಕೇಳುವುದಕ್ಕಾಗಿ ಕಳುಹಿಸು,” ಎಂಬುದಾಗಿ ಆಜ್ಞಾಪಿಸಿದನು.",
			"18": "<sup>18</sup> ರಾಹುತನು ಯೇಹುವನ್ನು ಎದುರುಗೊಂಡು, “ಅರಸರು ‘ಶುಭವಾರ್ತೆಯುಂಟೊ?’ ಎಂದು ಕೇಳುತ್ತಾರೆ,” ಎನ್ನಲು ಅವನು, “ಶುಭವಾರ್ತೆಯಿಂದ ನಿನಗೇನು? ನನ್ನ ಹಿಂದೆ ಬಾ,” ಎಂದನು. ಕಾವಲುಗಾರನು ಅರಸನಿಗೆ, “ರಾಹುತನು ಆ ಗುಂಪನ್ನು ಮುಟ್ಟಿದನು; ಆದರೆ ತಿರುಗಿಬರುವುದು ಕಾಣಿಸುವುದಿಲ್ಲ,” ಎಂದು ತಿಳಿಸಿದಾಗ ಅರಸನು ಇನ್ನೊಬ್ಬ ರಾಹುತನನ್ನು ಕಳುಹಿಸಿದನು.",
			"19": "<sup>19</sup> ಇವನು ಹೋಗಿ ಯೇಹುವನ್ನು ಎದುರುಗೊಂಡು, “ಅರಸರು ‘ಶುಭವಾರ್ತೆಯುಂಟೊ?’ ಎಂದು ಕೇಳುತ್ತಾರೆ,” ಎನ್ನಲು ಅವನು, “ಶುಭವಾರ್ತೆಯಿಂದ ನಿನಗೇನು?, ನನ್ನ ಹಿಂದೆ ಬಾ,” ಎಂದನು.",
			"20": "<sup>20</sup> ಕಾವಲುಗಾರನು ಪುನಃ ಅರಸನಿಗೆ, “ಎರಡನೆಯವನೂ ಆ ಗುಂಪನ್ನು ಮುಟ್ಟಿದನು; ಆದರೆ ಹಿಂದಿರುಗಿ ಬರುವುದು ಕಾಣಿಸುವುದೇ ಇಲ್ಲ. ರಥದಲ್ಲಿ ಕುಳಿತಿರುವವನು ಕುದುರೆಗಳನ್ನು ಹುಚ್ಚುಹಿಡಿದವನಂತೆ ಓಡಿಸುವುದನ್ನು ನೋಡಿದರೆ ಅವನು, ನಿಂಷಿಯ ಮೊಮ್ಮಗನಾದ ಯೇಹುವಾಗಿರಬೇಕು,” ಎಂದು ಹೇಳಿದನು.",
			"21": "<sup>21</sup> ಯೋರಾಮನು ರಥಹೂಡಬೇಕೆಂದು ತನ್ನ ಸೇವಕರಿಗೆ ಆಜ್ಞಾಪಿಸಿದನು. ಅವರು ಹೂಡಿದರು. ಆಗ ಇಸ್ರಯೇಲರ ಅರಸನಾದ ಇವನೂ ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಅಹಜ್ಯನೂ ತಮ್ಮ ತಮ್ಮ ರಥಗಳಲ್ಲಿ ಕುಳಿತುಕೊಂಡು, ಯೇಹುವನ್ನು ಎದುರುಗೊಳ್ಳುವುದಕ್ಕಾಗಿ ಹೊರಟು, ಜೆಸ್ರೀಲಿನವನಾದ ನಾಬೋತನ ಹೊಲದಲ್ಲಿ ಅವನನ್ನು ಸಂಧಿಸಿದರು.",
			"22": "<sup>22</sup> ಯೋರಾಮನು ಅವನನ್ನು ಕಂಡ ಕೂಡಲೆ, “ಯೇಹುವೇ, ಶುಭವೋ?’ ಎಂದು ಕೇಳಿದನು. ಅದಕ್ಕೆ ಅವನು, “ನಿನ್ನ ತಾಯಿಯ ದೇವದ್ರೋಹವೂ ಮಂತ್ರತಂತ್ರವೂ ಪ್ರಬಲವಾಗಿರುವಲ್ಲಿ ಶುಭವೆಲ್ಲಿಂದ ಬರುವುದು?” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"23": "<sup>23</sup> ಒಡನೆ ಯೋರಾಮನು ತನ್ನ ರಥವನ್ನು ತಿರುಗಿಸಿ ಅಹಜ್ಯನಿಗೆ, “ಅಹಜ್ಯನೇ, ಇದು ಒಳಸಂಚು,” ಎಂದು ಹೇಳಿ ಓಡಿಹೋದನು.",
			"24": "<sup>24</sup> ಯೇಹುವು ಪೂರ್ಣಬಲದಿಂದ ಬಿಲ್ಲನ್ನು ಬಗ್ಗಿಸಿ ಯೋರಾಮನ ಬೆನ್ನಿಗೆ ಬಾಣವನ್ನೆಸೆದನು; ಅದು ಅವನ ಹೃದಯದಿಂದ ಹೊರಗೆ ಬಂದಿತು. ಅವನು ರಥದಲ್ಲೇ ಕುಸಿದುಬಿದ್ದನು.",
			"25": "<sup>25</sup> ಆಗ ಯೇಹುವು ತನ್ನ ಜೊತೆಯಲ್ಲಿದ್ದ ಬಿದ್ಕರನೆಂಬ ಸೇನಾಪತಿಗೆ, “ಯೋರಾಮನ ಶವವನ್ನು ಎತ್ತಿ ಜೆಸ್ರೀಲಿನವನಾದ ನಾಬೋತನ ಹೊಲದಲ್ಲಿ ಹಾಕು. ಒಂದು ದಿನ ನಾವಿಬ್ಬರೂ ಕುದುರೆ ಹತ್ತಿ ಇವನ ತಂದೆಯಾದ ಅಹಾಬನ ಹಿಂದಿನಿಂದ ಹೋಗುತ್ತಿರುವಾಗ",
			"26": "<sup>26</sup> ಅಹಾಬನಿಗೆ ಸರ್ವೇಶ್ವರ, ‘ನನ್ನ ಮಾತನ್ನು ಕೇಳು, ನೀನು ನಿನ್ನೆ ಸುರಿಸಿದ ನಾಬೋತನ ಮತ್ತು ಅವನ ಮಕ್ಕಳ ರಕ್ತವನ್ನು ನಿಶ್ಚಯವಾಗಿ ನೋಡಿದ್ದೇನೆ. ನೀನು ಅವರ ರಕ್ತವನ್ನು ಸುರಿಸಿದ ಹೊಲದಲ್ಲೇ ನಿನಗೆ ಮುಯ್ಯಿತೀರಿಸುವೆನು,’ ಎಂದು ಪ್ರವಾದಿಯಿಂದ ಹೇಳಿಸಿದ್ದು ನಿನಗೆ ನೆನಪಿರುತ್ತದಲ್ಲವೆ?’ ಎಂದಿದ್ದರು. ಸರ್ವೇಶ್ವರನ ಆ ನುಡಿ ನೆರವೇರುವಂತೆ ಇವನ ಶವವನ್ನು ಆ ಹೊಲದಲ್ಲೇ ಹಾಕಿಬಿಡು,” ಎಂದು ಹೇಳಿದನು.",
			"27": "<sup>27</sup> ಜುದೇಯದ ಅರಸನಾದ ಅಹಜ್ಯನು ಇದನ್ನೆಲ್ಲಾ ಕಂಡು ಬೇತ್‍ಹಗ್ಗನಿನ ಮಾರ್ಗವಾಗಿ ಓಡಿಹೋದನು. ಯೇಹುವು ಅವನನ್ನು ಹಿಂದಟ್ಟಿ, “ಅವನನ್ನೂ ರಥದಲ್ಲಿಯೇ ಹೊಡೆಯಿರಿ,” ಎಂದು ಕೂಗಲು ಅವನ ಜನರು ಇಬ್ಲೆಯಾಮಿನ ಬಳಿಯಲ್ಲಿರುವ ಗೂರ್ ಗಟ್ಟದ ಮೇಲೆ ಅವನನ್ನು ಗಾಯಪಡಿಸಿದರು. ಅಹಜ್ಯನು ಮೆಗಿದ್ದೋವಿಗೆ ಓಡಿಹೋಗಿ ಅಲ್ಲಿ ಸತ್ತನು.",
			"28": "<sup>28</sup> ಅವನ ಸೇವಕರು ಅವನ ಶವವನ್ನು ರಥದಲ್ಲಿ ಹಾಕಿಕೊಂಡು ಜೆರುಸಲೇಮಿನಲ್ಲಿರುವ ದಾವೀದ ನಗರಕ್ಕೆ ತಂದು ಅದನ್ನು ಅವನ ಕುಟುಂಬ ಸ್ಮಶಾನಭೂಮಿಯಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು.",
			"29": "<sup>29</sup> ಅಹಜ್ಯನು ಅಹಾಬನ ಮಗನಾದ ಯೋರಾಮನ ಆಳ್ವಿಕೆಯ ಹನ್ನೊಂದನೆಯ ವರ್ಷದಲ್ಲಿ ಯೆಹೂದ್ಯರ ಅರಸನಾಗಿದ್ದನು.",
			"30": "<sup>30</sup> ಯೇಹುವು ಜೆಸ್ರೀಲಿಗೆ ಬಂದನು. ಈಜೆಬೆಳು ಅದನ್ನು ಕೇಳಿ ತಲೆಯನ್ನು ಆಭರಣಗಳಿಂದ ಅಲಂಕರಿಸಿಕೊಂಡು, ಕಣ್ಣಿಗೆ ಕಾಡಿಗೆ ಹಚ್ಚಿಕೊಂಡು, ಕಿಟಕಿಯಿಂದ ನೋಡುತ್ತಾ ನಿಂತಳು.",
			"31": "<sup>31</sup> ಅನಂತರ ಅರಮನೆಯ ಪ್ರಾಕಾರದೊಳಗೆ ಬಂದು ಯೇಹುವನ್ನು ಕಂಡು, “ಯಜಮಾನನನ್ನು ಕೊಂದ ಜಿಮ್ರಿಗೆ ಸಮಾನನೇ, ಕ್ಷೇಮವೇ?” ಎಂದು ಕೇಳಿದಳು.",
			"32": "<sup>32</sup> ಅವನು ಕಣ್ಣೆತ್ತಿ ಕಿಟಕಿಯ ಕಡೆಗೆ ನೋಡಿ, “ಅಲ್ಲಿ ನನ್ನ ಪಕ್ಷದವರು ಯಾರು?” ಎಂದು ಕೂಗಿದನು. ಕೂಡಲೆ ಆ ಕಿಟಕಿಯಿಂದ ಇಬ್ಬರು ಮೂವರು ಕಂಚುಕಿಗಳು ಅವನ ಕಡೆಗೆ ನೋಡಿದರು.",
			"33": "<sup>33</sup> ಅವನು ಅವರಿಗೆ, “ಆಕೆಯನ್ನು ಕೆಳಗೆ ದೊಬ್ಬಿರಿ,” ಎಂದು ಆಜ್ಞಾಪಿಸಲು ಅವರು ದೊಬ್ಬಿದರು. ಆಕೆಯ ರಕ್ತ ಗೋಡೆಗೂ ಕುದುರೆಗಳಿಗೂ ಸಿಡಿಯಿತು. ಅವನು ಆಕೆಯ ಶವವನ್ನು ತುಳಿಸಿಬಿಟ್ಟನು.",
			"34": "<sup>34</sup> ಅನಂತರ ಅವನು ಅರಮನೆಯೊಳಗೆ ಹೋಗಿ, ಅನ್ನಪಾನಗಳನ್ನು ತೆಗೆದುಕೊಂಡು, ತನ್ನ ಸೇವಕರಿಗೆ, “ಆ ಶಾಪಗ್ರಸ್ತ ಸ್ತ್ರೀಯ ಶವವನ್ನು ಸರಿಯಾಗಿ ಸಮಾಧಿಮಾಡಿರಿ; ಆಕೆ ರಾಜಪುತ್ರಿಯಾಗಿರುತ್ತಾಳಷ್ಟೆ,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"35": "<sup>35</sup> ಸೇವಕರು ಶವವನ್ನು ಸಮಾಧಿಮಾಡುವುದಕ್ಕಾಗಿ ಹೋದರು. ಆದರೆ ಅವರಿಗೆ ಆಕೆಯ ತಲೆಬುರುಡೆ, ಕೈಕಾಲುಗಳು ಹೊರತಾಗಿ ಬೇರೇನೂ ಸಿಕ್ಕಲಿಲ್ಲ.",
			"36": "<sup>36</sup> ಅವರು ಹಿಂದಿರುಗಿ, ಯೇಹುವಿನ ಹತ್ತಿರ ಬಂದು, ಆ ಸಂಗತಿಯನ್ನು ತಿಳಿಸಿದರು. ಅವನು ಅವರಿಗೆ, “’ನಾಯಿಗಳು ಈಜೆಬೆಲಳ ದೇಹಮಾಂಸವನ್ನು ಜೆಸ್ರೀಲಿನ ಹೊಲದಲ್ಲಿ ತಿಂದುಬಿಡುವುವು;",
			"37": "<sup>37</sup> ಆಕೆಯ ಶವ ಆ ಹೊಲಕ್ಕೆ ಗೊಬ್ಬರವಾಗುವುದು; ಇದು ಈಜೆಬೆಲಳ ಶವವೆಂದು ಯಾರಿಗೂ ಗುರುತು ಸಿಕ್ಕದ ಹಾಗಾಗುವುದು’ ಎಂಬುದಾಗಿ ತಿಷ್ಬೀಯನೂ ತಮ್ಮ ದಾಸನೂ ಆದ ಎಲೀಯನ ಮುಖಾಂತರ ಸರ್ವೇಶ್ವರ ಹೇಳಿಸಿದ ಮಾತು ಈಗ ನೆರವೇರಿತು,” ಎಂದನು."
		},
		"10": {
			"1": "<sup>1</sup> ಸಮಾರಿಯ ಪಟ್ಟಣದಲ್ಲಿ ಅಹಾಬನ ಸಂತಾನದ ಎಪ್ಪತ್ತು ಮಂದಿ ರಾಜಪುತ್ರರಿದ್ದರು. ಯೇಹುವು ಆ ಪಟ್ಟಣದಲ್ಲಿದ್ದ ಜೆಸ್ರೀಲಿನ ಅಧಿಕಾರಿಗಳಾದ ಹಿರಿಯರಿಗೂ ರಾಜಪುತ್ರಪಾಲಕರಿಗೂ ಪತ್ರಗಳನ್ನು ಬರೆದು ಅವುಗಳನ್ನು ದೂತರ ಮುಖಾಂತರ ಕಳುಹಿಸಿದನು.",
			"2": "<sup>2</sup> ಅವುಗಳಲ್ಲಿ, “ನಿಮ್ಮ ವಶದಲ್ಲಿ ನಿಮ್ಮ ಯಜಮಾನನ ಮಕ್ಕಳೂ ರಥಾಶ್ವಬಲಗಳೂ ಕೋಟೆಕೊತ್ತಲುಗಳುಳ್ಳ ಪಟ್ಟಣವೂ ಯುದ್ಧಾಯುಧಗಳೂ ಇರುತ್ತವಷ್ಟೆ.",
			"3": "<sup>3</sup> ಆ ಪತ್ರವು ನಿಮಗೆ ಮುಟ್ಟಿದೊಡನೆ ಆ ರಾಜಪುತ್ರರಲ್ಲಿ ಉತ್ತಮನೂ ಸಮರ್ಥನೂ ಆದ ಒಬ್ಬನನ್ನು ಆರಿಸಿಕೊಂಡು, ಅವನನ್ನು ಅವನ ತಂದೆಯ ಸಿಂಹಾಸನದ ಮೇಲೆ ಕುಳ್ಳಿರಿಸಿ, ನಿಮ್ಮ ಯಜಮಾನನ ಕುಟುಂಬದವರ ಪರವಾಗಿ ಯುದ್ಧಮಾಡಿ,” ಎಂಬುದಾಗಿ ಬರೆದಿತ್ತು.",
			"4": "<sup>4</sup> ಅವರು ಬಹಳವಾಗಿ ಭಯಪಟ್ಟರು. ಇಬ್ಬರು ರಾಜರೂ ಅವನ ಮುಂದೆ ನಿಲ್ಲಲಾರದೆಹೋದರು. ನಾವು ನಿಲ್ಲುವುದು ಹೇಗೆ?” ಎಂದುಕೊಂಡರು.",
			"5": "<sup>5</sup> ಅನಂತರ ರಾಜಗೃಹಾಧಿಪತಿ, ಪುರಾಧಿಕಾರಿ, ಹಿರಿಯರು, ರಾಜಪುತ್ರಪಾಲಕರು ಇವರು ಎಲ್ಲರು ಕೂಡಿ ಯೇಹುವಿಗೆ, “ನಾವು ನಿಮ್ಮ ಆಜ್ಞಾಧಾರಕರಾದ ಸೇವಕರು, ನಾವಾಗಿ ನಮಗೊಬ್ಬ ಅರಸನನ್ನು ನೇಮಿಸಿಕೊಳ್ಳುವುದಿಲ್ಲ; ನಿಮ್ಮ ಚಿತ್ತವೇ ನೆರವೇರಲಿ,” ಎಂದು ಹೇಳಿಕಳುಹಿಸಿದರು.",
			"6": "<sup>6</sup> ಆಗ ಯೇಹುವು ಇನ್ನೊಂದು ಪತ್ರದ ಮೂಲಕ ಅವರಿಗೆ, “ನೀವು ನನ್ನ ಪಕ್ಷದವರಾಗಿದ್ದು ನನ್ನ ಆಜ್ಞಾನುಸಾರವಾಗಿ ನಡೆದುಕೊಳ್ಳಲು ಮನಸ್ಸುಳ್ಳವರಾಗಿದ್ದರೆ, ನಾಳೆ ಇಷ್ಟು ಹೊತ್ತಿಗೆ, ನಿಮ್ಮ ಯಜಮಾನನ ಸಂತಾನದವರ ತಲೆಗಳೊಡನೆ ಬನ್ನಿರಿ,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು. ಎಪ್ಪತ್ತುಮಂದಿ ರಾಜಪುತ್ರರು ಪಟ್ಟಣದ ಪ್ರಧಾನಪುರುಷರ ಪರಾಂಬರಿಕೆಯಲ್ಲಿದ್ದರು.",
			"7": "<sup>7</sup> ಪ್ರಧಾನಪುರುಷರು ಪತ್ರವನ್ನು ಓದಿದೊಡನೆ ಆ ಎಪ್ಪತ್ತು ಮಂದಿಯನ್ನು ಹಿಡಿದುಕೊಂಡು ಅವರ ತಲೆಗಳನ್ನು ಪುಟ್ಟಿಗಳಲ್ಲಿ ಹಾಕಿ ಜೆಸ್ರೀಲಿನಲ್ಲಿದ್ದ ಯೇಹುವಿಗೆ ಕಳುಹಿಸಿದರು.",
			"8": "<sup>8</sup> ರಾಜಪುತ್ರರ ತಲೆಗಳನ್ನು ತಂದಿದ್ದಾರೆಂಬ ಸಮಾಚಾರ ಯೇಹುವಿಗೆ ಮುಟ್ಟಿದಾಗ ಅವನು; “ಬೆಳಗಾಗುವ ತನಕ ಅವುಗಳನ್ನು ಹೆಬ್ಬಾಗಿಲಿನ ಹತ್ತಿರ ಎರಡು ರಾಶಿಗಳಾಗಿ ಹಾಕಬೇಕು,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"9": "<sup>9</sup> ಮರುದಿನ ಬೆಳಿಗ್ಗೆ ಅವನು ಅವುಗಳ ಹತ್ತಿರ ಹೋಗಿ ಎಲ್ಲಾ ಜನರ ಎದುರಿಗೆ ನಿಂತು, “ನೀವು ನಿರಪರಾಧಿಗಳು; ನಾನಾದರೋ ನನ್ನ ಯಜಮಾನನಿಗೆ ವಿರೋಧವಾಗಿ ಒಳಸಂಚುಮಾಡಿ ಅವನನ್ನು ಕೊಂದವನು; ಆದರೆ ಇವರನ್ನೆಲ್ಲಾ ಹತಿಸಿದವರಾರು?",
			"10": "<sup>10</sup> ಇಗೋ, ಅಹಾಬನ ಮನೆಯವರನ್ನುಕುರಿತು ಸರ್ವೇಶ್ವರ ಹೇಳಿದ ಮಾತುಗಳೆಲ್ಲಾ ತಪ್ಪದೆ ನೆರವೇರುವುದೆಂದು ತಿಳಿದುಕೊಳ್ಳಿರಿ. ಸರ್ವೇಶ್ವರ ತಮ್ಮ ದಾಸನಾದ ಎಲೀಯನ ಮುಖಾಂತರ ಹೇಳಿದ್ದನ್ನು ನೆರವೇರಿಸಿದ್ದಾರಷ್ಟೇ,” ಎಂದು ಹೇಳಿದನು.",
			"11": "<sup>11</sup> ಅನಂತರ ಅವನು ಜೆಸ್ರೀಲಿನಲ್ಲಿ ಉಳಿದ ಅಹಾಬನ ಕುಟುಂಬದವರನ್ನೂ ಅವನ ಸರದಾರರು, ಆಪ್ತಮಿತ್ರರು, ಪುರೋಹಿತರು ಇವರನ್ನೂ ಸಂಹರಿಸಿದನು’ ಒಬ್ಬನನ್ನೂ ಉಳಿಸಲಿಲ್ಲ.",
			"12": "<sup>12</sup> ಆಮೇಲೆ ಯೇಹುವು ಸಮಾರಿಯಕ್ಕೆ ಹೊರಟುಹೋದನು. ದಾರಿಯಲ್ಲಿ ಕುರುಬರ ಛತ್ರಕ್ಕೆ ಬಂದಾಗ ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಅಹಜ್ಯನ ಸಹೋದರರನ್ನು ಕಂಡು, “ನೀವು ಯಾರು?” ಎಂದು ಕೇಳಿದನು.",
			"13": "<sup>13</sup> ಅದಕ್ಕೆ ಅವರು, “ನಾವು ಅಹಜ್ಯನ ಸಹೋದರರು; ರಾಜಪುತ್ರರನ್ನೂ ರಾಜಮಾತೆಯ ಮಕ್ಕಳನ್ನೂ ವಂದಿಸುವುದಕ್ಕಾಗಿ ಇಲ್ಲಿಗೆ ಬಂದಿದ್ದೇವೆ,” ಎಂದು ಉತ್ತರಕೊಟ್ಟರು.",
			"14": "<sup>14</sup> ಆಗ ಯೇಹುವು ತನ್ನ ಸೇವಕರಿಗೆ, “ಅವರನ್ನು ಸಜೀವಿಗಳಾಗಿ ಹಿಡಿಯಿರಿ,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು. ಅವರು ಅವರನ್ನು ಹಿಡಿದು ವಧಿಸಿ ಆ ಛತ್ರದ ಬಾವಿಯಲ್ಲಿ ಹಾಕಿದರು. ಅವರು ನಾಲ್ವತ್ತೆರಡು ಮಂದಿಯಿದ್ದರು; ಅವರಲ್ಲಿ ಒಬ್ಬನನ್ನೂ ಬಿಡಲಿಲ್ಲ.",
			"15": "<sup>15</sup> ಯೇಹುವು ಅಲ್ಲಿಂದ ಮುಂದೆ ಹೋಗುತ್ತಿರುವಾಗ ತನ್ನನ್ನು ಎದುರುಗೊಳ್ಳುವುದಕ್ಕಾಗಿ ಬಂದ ರೇಕಾಬನ ಮಗನಾದ ಯೆಹೋನಾದಾಬನನ್ನು ಕಂಡು ವಂದಿಸಿದನು. “ನಿನ್ನ ವಿಷಯದಲ್ಲಿ ನನ್ನ ಮನಸ್ಸು ಯಥಾರ್ಥವಾಗಿರುವಂತೆ ನಿನ್ನ ಮನಸ್ಸು ಯಥಾರ್ಥವಾಗಿ ಇರುತ್ತದೋ?’ ಎಂದು ಕೇಳಿದನು. ಅವನು, “ಹೌದು,” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು. ಆಗ ಯೇಹುವು, “ಹಾಗಾದರೆ ನಿನ್ನ ಕೈಕೊಡು,” ಎನ್ನಲು ಅವನು ಕೈಕೊಟ್ಟನು. ಯೇಹುವು ಅವನನ್ನು ತನ್ನ ರಥದಲ್ಲಿ ಕುಳ್ಳಿರಿಸಿಕೊಂಡನು.",
			"16": "<sup>16</sup> “ನನ್ನ ಜೊತೆಯಲ್ಲಿ ಬಂದು ಸರ್ವೇಶ್ವರನಲ್ಲಿ ನನಗಿರುವ ಶ್ರದ್ಧೆಯನ್ನು ನೋಡು,” ಎಂದು ಹೇಳಿ ಅವನನ್ನು ಕೈಹಿಡಿದು ತನ್ನ ರಥದಲ್ಲಿ ಕರೆದುಕೊಂಡು ಹೋದನು.",
			"17": "<sup>17</sup> ಸಮಾರಿಯವನ್ನು ಸೇರಿದ ನಂತರ ಅಲ್ಲಿ ಉಳಿದಿದ್ದ ಅಹಾಬನ ಸಂತಾನದವರನ್ನೆಲ್ಲಾ ಸಂಹರಿಸಿಬಿಟ್ಟನು. ಹೀಗೆ ಎಲೀಯನ ಮುಖಾಂತರ ಸರ್ವೇಶ್ವರ ಹೇಳಿಸಿದ ಮಾತು ನೆರವೇರಿತು.",
			"18": "<sup>18</sup> ತರುವಾಯ ಅವನು ಎಲ್ಲಾ ಜನರನ್ನು ಕೂಡಿಸಿ ಅವರಿಗೆ, “ಅಹಾಬನು ಬಾಳ್ ದೇವತೆಯನ್ನು ಸ್ವಲ್ಪ ಮಾತ್ರ ಪೂಜಿಸಿದನು; ಯೇಹುವಾದರೋ ಅಧಿಕವಾಗಿ ಪೂಜಿಸಬೇಕೆಂದು ಇರುತ್ತಾನೆ.",
			"19": "<sup>19</sup> ಆದುದರಿಂದ ನೀವು ಬಾಳ್ ದೇವತೆಯ ಎಲ್ಲಾ ಪ್ರವಾದಿಗಳನ್ನೂ ಭಕ್ತರನ್ನೂ ಯಾಜಕರನ್ನೂ ನನ್ನ ಬಳಿಗೆ ಕರೆದುಕೊಂಡು ಬನ್ನಿ; ನಾನು ಬಾಳ್ ದೇವತೆಗಾಗಿ ಮಹಾಬಲಿಯನ್ನು ಸಮರ್ಪಿಸಬೇಕೆಂದಿರುತ್ತೇನೆ. ಆದುದರಿಂದ ಅವರಲ್ಲಿ ಒಬ್ಬನೂ ಬಾರದಿರಕೂಡದು, ಬಾರದವನು ಸಾಯಬೇಕು,” ಎಂದು ಹೇಳಿದನು. ಬಾಳ್ ದೇವತೆಯ ಭಕ್ತರನ್ನು ಸಂಹರಿಸಬೇಕೆಂದು ಈ ಯುಕ್ತಿಯನ್ನು ಮಾಡಿದನು.",
			"20": "<sup>20</sup> ಇದಲ್ಲದೆ ಅವನು ದೂತರ ಮುಖಾಂತರ, “ಬಾಳನ ಎಲ್ಲಾ ಭಕ್ತರೂ ತಮ್ಮನ್ನು ಶುದ್ಧಪಡಿಸಿಕೊಂಡು ಜಾತ್ರೆಗೆ ಬರಬೇಕು,” ಎಂದು ಇಸ್ರಯೇಲರ ಸಮಸ್ತ ಪ್ರಾಂತ್ಯಗಳಲ್ಲಿ ಪ್ರಕಟಿಸಿದನು.",
			"21": "<sup>21</sup> ಆಗ ಬಾಳ್ ದೇವತೆಯ ಭಕ್ತರಲ್ಲಿ ಒಬ್ಬನೂ ತಪ್ಪದೆ ಎಲ್ಲರೂ ಕೂಡಿಬಂದುದರಿಂದ ಬಾಳನ ದೇವಸ್ಥಾನ ಸಂಪೂರ್ಣವಾಗಿ ತುಂಬಿತು.",
			"22": "<sup>22</sup> ಯೇಹುವು ವಸ್ತ್ರಾಗಾರದ ಅಧಿಪತಿಗೆ, “ಬಾಳ್ ದೇವತೆಯ ಭಕ್ತರೆಲ್ಲರಿಗೂ ಪೂಜಾವಸ್ತ್ರಗಳನ್ನು ಕೊಡು,” ಎಂದು ಆಜ್ಞಾಪಿಸಲು ಅವನು ತಂದುಕೊಟ್ಟನು.",
			"23": "<sup>23</sup> ಅನಂತರ ಯೇಹುವು ರೇಕಾಬನ ಮಗ ಯೆಹೋನಾದಾಬನೊಡನೆ ದೇವಸ್ಥಾನವನ್ನು ಪ್ರವೇಶಿಸಿ ಬಾಳ್ ದೇವತೆಯ ಭಕ್ತರಿಗೆ, “ಸರ್ವೇಶ್ವರನ ಭಕ್ತರು ನಿಮ್ಮ ಮಧ್ಯೆ ಬಂದಿರುತ್ತಾರೋ ವಿಚಾರಿಸಿ ನೋಡಿ; ಬಾಳನ ಭಕ್ತರು ಮಾತ್ರ ಇಲ್ಲಿರಬೇಕು,” ಎಂದು ಹೇಳಿದನು.",
			"24": "<sup>24</sup> ಆಮೇಲೆ ಅವರು ದಹನಬಲಿ ಯಜ್ಞಗಳನ್ನು ಸಮರ್ಪಿಸುವುದಕ್ಕೆ ಹೋದರು. ಇದಕ್ಕೆ ಮೊದಲೇ ಅವನು ದೇವಸ್ಥಾನದ ಹೊರಗೆ ಎಂಬತ್ತು ಮಂದಿಯನ್ನು ಕಾಯುವುದಕ್ಕಾಗಿ ಇರಿಸಿ ಅವರಿಗೆ, “ನಾನು ಯಾರನ್ನು ನಿಮ್ಮ ಕೈಗೆ ಸಿಕ್ಕುವ ಹಾಗೆ ಮಾಡುವೆನೋ ಅವರಲ್ಲಿ ಒಬ್ಬನನ್ನೂ ಬಿಡಬೇಡಿ; ಬಿಟ್ಟರೆ ಅವನ ಪ್ರಾಣಕ್ಕೆ ಬದಲಾಗಿ ನಿಮ್ಮ ಪ್ರಾಣವು ಹೋಗುವುದು,” ಎಂಬುದಾಗಿ ಆಜ್ಞಾಪಿಸಿದ್ದನು.",
			"25": "<sup>25</sup> ದಹನಬಲಿಯನ್ನು ಅರ್ಪಿಸಿದ ಕೂಡಲೆ ಯೇಹುವು ಕಾವಲುಗಾರರಿಗೂ ಸೇನಾಪತಿಗಳಿಗೂ, “ಒಳಗೆ ಹೋಗಿ ಅವರನ್ನೆಲ್ಲಾ ಸಂಹರಿಸಿರಿ; ಒಬ್ಬನೂ ತಪ್ಪಿಸಿಕೊಳ್ಳಕೂಡದು,” ಎಂದು ಹೇಳಿದನು. ಅವರು ಒಳಗೆ ಹೋಗಿ ಅವರನ್ನು ಕತ್ತಿಯಿಂದ ಕೊಂದು ಅವರ ಶವಗಳನ್ನು ಹೊರಗೆ ಹಾಕಿದರು.",
			"26": "<sup>26</sup> ಬಾಳ್ ದೇವತೆಯ ದೇವಸ್ಥಾನಕ್ಕೆ ಸೇರಿದ ಕೇರಿಯನ್ನು ಪ್ರವೇಶಿಸಿ ಅಲ್ಲಿನ ವಿಗ್ರಹಸ್ತಂಭಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಬಂದು ಸುಟ್ಟುಬಿಟ್ಟರು.",
			"27": "<sup>27</sup> ಇದಲ್ಲದೆ ಬಾಳ್ ದೇವತೆಯ ವಿಗ್ರಹಸ್ತಂಭಗಳನ್ನೂ ದೇವಸ್ಥಾನವನ್ನೂ ಕೆಡವಿ ಹಾಳುಮಾಡಿದರು. ಅಂದಿನಿಂದ ಇಂದಿನವರೆಗೂ ಆ ಸ್ಥಳವನ್ನು ಶೌಚಕ್ಕಾಗಿ ಉಪಯೋಗಿಸುತ್ತಾರೆ.",
			"28": "<sup>28</sup> ಹೀಗೆ ಯೇಹುವು ಇಸ್ರಯೇಲರಲ್ಲಿ ಬಾಳನ ಪೂಜೆಯನ್ನು ನಿಲ್ಲಿಸಿದನು.",
			"29": "<sup>29</sup> ಆದರೂ ಅವನು ಇಸ್ರಯೇಲರನ್ನು ಪಾಪಕ್ಕೆ ಪ್ರಚೋದಿಸಿದ ನೆಬಾಟನ ಮಗ ಯಾರೊಬ್ಬಾಮನ ಮಾರ್ಗವನ್ನು ಕೈಬಿಡದೆ, ಬೇತೇಲ್, ದಾನ್ ಎಂಬ ಊರುಗಳಲ್ಲಿದ್ದ ಚಿನ್ನದ ಹೋರಿಕರುಗಳನ್ನು ಪೂಜಿಸುತ್ತಿದ್ದನು.",
			"30": "<sup>30</sup> ಸರ್ವೇಶ್ವರ ಯೇಹುವಿಗೆ, “ನೀನು ನನ್ನ ದೃಷ್ಟಿಗೆ ಸರಿಯಾದದ್ದನ್ನು ನಡೆಸಿ ನನ್ನ ಮೆಚ್ಚಿಕೆಯನ್ನು ಪಡೆದೆ; ಅಹಾಬನ ಮನೆಯವರನ್ನು ಕುರಿತು ನನ್ನ ಚಿತ್ತವನ್ನು ನೆರವೇರಿಸಿದೆ. ಆದುದರಿಂದ ನಿನ್ನ ಸಂತಾನದವರು ನಾಲ್ಕೆನೆಯ ತಲೆಯವರೆಗೂ ಇಸ್ರಯೇಲ್ ಸಿಂಹಾಸನದ ಮೇಲೆ ಕುಳಿತುಕೊಳ್ಳುವರು,” ಎಂದು ಹೇಳಿದರು.",
			"31": "<sup>31</sup> ಆದರೆ ಅವನು ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಪೂರ್ಣಮನಸ್ಸಿನಿಂದ ಕೈಕೊಳ್ಳುವುದಕ್ಕೆ ಪ್ರಯತ್ನಿಸಲಿಲ್ಲ; ಇಸ್ರಯೇಲರನ್ನು ಪಾಪಕ್ಕೆ ಪ್ರೇರಿಸಿದ ಯಾರೊಬ್ಬಾಮನ ಮಾರ್ಗವನ್ನು ಬಿಡಲಿಲ್ಲ.",
			"32": "<sup>32</sup> ಈ ಕಾಲದಲ್ಲಿ ಇಸ್ರಯೇಲರನ್ನು ಸರ್ವೇಶ್ವರ ಕುಗ್ಗಿಸತೊಡಗಿದರು.",
			"33": "<sup>33</sup> ಹಜಾಯೇಲನು ಬಂದು ಜೋರ್ಡನಿನ ಪೂರ್ವದಿಕ್ಕಿಗೂ, ಅರ್ನೋನ್ ತಗ್ಗಿನ ಅರೋಯೇರ್ ಪಟ್ಟಣದ ಉತ್ತರದಿಕ್ಕಿಗೂ, ಇರುವ ಗಿಲ್ಯಾದ್, ಬಾಷಾನ್, ಪ್ರಾಂತ್ಯಗಳಲ್ಲಿ ವಾಸವಾಗಿದ್ದ ಗಾದ್, ರೂಬೇನ್, ಮನಸ್ಸೆ ಕುಲಗಳವನ್ನೂ ಬೇರೆ ಎಲ್ಲಾ ಪ್ರಾಂತ್ಯಗಳಲ್ಲಿರುವ ಇಸ್ರಯೇಲರನ್ನೂ ಹಿಂಸಿಸಿದನು.",
			"34": "<sup>34</sup> ಯೇಹುವಿನ ಉಳಿದ ಚರಿತ್ರೆಯೂ ಅವನ ಶೂರಕೃತ್ಯಗಳ ವಿವರವೂ ಇಸ್ರಯೇಲ್ ರಾಜರ ಇತಿಹಾಸ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"35": "<sup>35</sup> ಅವನು ನಿಧನನಾಗಿ ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಲು ಅವನ ಶವವನ್ನು ಸಮಾರಿಯದಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗನಾದ ಯೆಹೋವಾಹಾಜನು ಅರಸನಾದನು.",
			"36": "<sup>36</sup> ಯೇಹುವು ಸಮಾರಿಯ ಪಟ್ಟಣದಲ್ಲಿದ್ದುಕೊಂಡು ಇಸ್ರಯೇಲರನ್ನು ಆಳಿದ್ದು ಇಪ್ಪತ್ತೆಂಟು ವರ್ಷ."
		},
		"11": {
			"1": "<sup>1</sup> ಅಹಜ್ಯನು ಮರಣಹೊಂದಿದನೆಂಬುದನ್ನು ಅವನ ತಾಯಿ ಅತಲ್ಯಳು ಕೇಳಿದಾಗ ರಾಜಸಂತಾನದವರನ್ನೆಲ್ಲಾ ಬೇಗನೆ ಸಂಹರಿಸಿಬಿಟ್ಟಳು.",
			"2": "<sup>2</sup> ಆದರೆ ಅರಸ ಯೆಹೋರಾಮನ ಮಗಳೂ ಅಹಜ್ಯನ ತಂಗಿಯೂ ಆದ ಯೆಹೋಷೆಬ ಎಂಬಾಕೆ, ಯಾರಿಗೂ ತಿಳಿಯದಂತೆ ತನ್ನ ಅಣ್ಣನ ಮಗ ಯೆಹೋವಾಷನನ್ನು, ಹತವಾಗುವುದಕ್ಕಿದ್ದ ರಾಜಪುತ್ರರ ಮಧ್ಯೆಯಿಂದ, ಅವನ ದಾದಿಯೊಡನೆ ತೆಗೆದುಕೊಂಡು ಹೋಗಿ, ಮಲಗುವ ಕೋಣೆಯಲ್ಲಿ ಅಡಗಿಸಿಬಿಟ್ಟಳು.",
			"3": "<sup>3</sup> ಹೀಗೆ ಅವನು ಅತಲ್ಯಳಿಂದ ಹತವಾಗದೆ ತಪ್ಪಿಸಿಕೊಂಡು, ಆರು ವರ್ಷಗಳವರೆಗೂ ಯೆಹೋಷೆಬಳೊಡನೆ ಗುಪ್ತವಾಗಿ ಸರ್ವೇಶ್ವರನ ಆಲಯದಲ್ಲಿದ್ದನು. ಈ ಆರು ವರ್ಷಗಳಲ್ಲಿ ಅತಲ್ಯಳೇ ನಾಡನ್ನು ಆಳುತ್ತಿದ್ದಳು.",
			"4": "<sup>4</sup> ಏಳನೆಯ ವರ್ಷದಲ್ಲಿ ಯೆಹೋಯಾದಾವನು ‘ಕಾರಿ’ ಎಂಬ ಸಿಪಾಯಿಗಳ ಮತ್ತು ಕಾವಲುದಂಡಿನವರ ಶತಾಧಿಪತಿಗಳನ್ನು ತನ್ನ ಹತ್ತಿರ ಸರ್ವೇಶ್ವರನ ಆಲಯಕ್ಕೆ ಕರೆಯಿಸಿದನು. ಅಲ್ಲಿ ಅವನು ಅವರ ಸಂಗಡ ಪ್ರಮಾಣಪೂರ್ವಕವಾದ ಒಪ್ಪಂದವನ್ನು ಮಾಡಿಕೊಂಡನಂತರ ರಾಜಕುಮಾರನನ್ನು ತೋರಿಸಿದನು.",
			"5": "<sup>5</sup> ಅನಂತರ ಅವರಿಗೆ, “ಸಬ್ಬತ್ ದಿನದಲ್ಲಿ ಮನೆಗೆ ಹೋಗುವ ಸೈನ್ಯದ ಮೂರರಲ್ಲೊಂದು ಭಾಗ ಅರಮನೆಯನ್ನು ಕಾಯಬೇಕು;",
			"6": "<sup>6</sup> ಇನ್ನೊಂದು ಭಾಗ ಸೂರ್ ಬಾಗಿಲಿನಲ್ಲಿಯೂ ಮತ್ತೊಂದು ಭಾಗ ಕಾವಲುದಂಡಿನ ಹಿಂದಣ ಬಾಗಿಲಿನಲ್ಲಿಯೂ ಇರಬೇಕು. ಈ ಪ್ರಕಾರ ನೀವು ಅರಮನೆಯನ್ನು ಭದ್ರವಾಗಿ ಕಾಯಬೇಕು.",
			"7": "<sup>7</sup> ಅದೂ ಅಲ್ಲದೆ, ಸಬ್ಬತ್ ದಿನದಲ್ಲಿ ಕಾಯುವುದಕ್ಕಾಗಿ ಬರುವ ನಿಮ್ಮ ಇಮ್ಮಡಿಯಾದ ಸೈನ್ಯವು ಸರ್ವೇಶ್ವರನ ಆಲಯದಲ್ಲಿ ಅರಸನನ್ನು ಕಾಯಬೇಕು.",
			"8": "<sup>8</sup> ನೀವು ಕೈಯಲ್ಲಿ ಆಯುಧಗಳನ್ನು ಹಿಡಿದುಕೊಂಡು, ಅವನ ಸುತ್ತಲೂ ನಿಂತು, ನಿಮ್ಮ ಗುಂಪಿನಲ್ಲಿ ನುಗ್ಗುವಂಥವನನ್ನು ಸಂಹರಿಸಬೇಕು. ಅರಸನು ಹೊರಗೆ ಹೋಗಿ ಬರುವಾಗಲೆಲ್ಲಾ ನೀವು ಅವನ ಜೊತೆಯಲ್ಲೇ ಇರಬೇಕು; ಇದು ನನ್ನ ಅಪ್ಪಣೆ,” ಎಂದು ಹೇಳಿದನು.",
			"9": "<sup>9</sup> ಯಾಜಕನಾದ ಯೆಹೋಯಾದವನು ಆಜ್ಞಾಪಿಸಿದಂತೆ ಶತಾಧಿಪತಿಗಳು ಸಬ್ಬತ್ ದಿನದಲ್ಲಿ ಮನೆಗೆ ಹೋಗತಕ್ಕ ಮತ್ತು ಕಾಯುವುದಕ್ಕಾಗಿ ಬರತಕ್ಕ ಸಿಪಾಯಿಗಳನ್ನು ಕರೆದುಕೊಂಡು ಅವನ ಬಳಿಗೆ ಬಂದರು.",
			"10": "<sup>10</sup> ಅವರು ಆ ಶತಾಧಿಪತಿಗಳಿಗೆ ಸರ್ವೇಶ್ವರನ ಆಲಯದಲ್ಲಿ ಇಡಲಾಗಿದ್ದ ದಾವೀದನ ಬರ್ಜಿಯನ್ನೂ ಗುರಾಣಿಗಳನ್ನೂ ಕೊಟ್ಟನು.",
			"11": "<sup>11</sup> ಕಾವಲುದಂಡಿನವರು ಆಯುಧ ಹಿಡಿದುಕೊಂಡು ಅರಸನನ್ನು ಕಾಯುವುದಕ್ಕಾಗಿ ದೇವಾಲಯದ ದಕ್ಷಿಣ ದಿಕ್ಕಿನ ಮೂಲೆಯಿಂದ ಬಲಿಪೀಠದವರೆಗು, ಅಲ್ಲಿಂದ ಉತ್ತರ ದಿಕ್ಕಿನ ಮೂಲೆಯವರೆಗು, ಸಾಲಾಗಿ ನಿಂತರು.",
			"12": "<sup>12</sup> ಯೆಹೋಯಾದವನು ರಾಜಪುತ್ರನನ್ನು ಹೊರಗೆ ಕರೆದುಕೊಂಡು ಬಂದು, ಅವನ ತಲೆಯ ಮೇಲೆ ಕಿರೀಟವನ್ನಿಟ್ಟು, ಕೈಗೆ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಕೊಟ್ಟು, ಅವನಿಗೆ ರಾಜ್ಯಾಭಿಷೇಕ ಮಾಡಿದನು. ಕೂಡಲೆ ಜನರು ಚಪ್ಪಾಳೆಹೊಡೆದು, ‘ಅರಸನು ಚಿರಂಜೀವಿಯಾಗಿರಲಿ!’ ಎಂದು ಜಯಕಾರ ಮಾಡಿದರು.",
			"13": "<sup>13</sup> ಅತಲ್ಯಳು ಕಾವಲುದಂಡಿನವರ ಮತ್ತು ಇತರ ಜನರ ಗದ್ದಲವನ್ನು ಕೇಳಿ, ಅವರು ಕೂಡಿದ್ದ ಸರ್ವೇಶ್ವರನ ಆಲಯಕ್ಕೆ ಬಂದಳು.",
			"14": "<sup>14</sup> ಅಲ್ಲಿ ಅರಸನು ಪದ್ಧತಿಯ ಪ್ರಕಾರ ಸ್ತಂಭದ ಬಳಿಯಲ್ಲಿ ನಿಂತಿದ್ದನು. ಅಧಿಪತಿಗಳೂ ತುತೂರಿಗಳನ್ನು ಊದುವವರೂ ಅರಸನ ಹತ್ತಿರ ಇದ್ದರು. ಜನಸಾಮಾನ್ಯರೆಲ್ಲರು ಸಂತೋಷದಿಂದ ಕೊಂಬೂದುತ್ತಿದ್ದರು. ಇದನ್ನು ಕಂಡ ಕೂಡಲೆ ಆಕೆ ಕೋಪದಿಂದ ಬಟ್ಟೆಗಳನ್ನು ಹರಿದುಕೊಂಡು, “ದ್ರೋಹ! ದ್ರೋಹ!” ಎಂದು ಕೂಗಿದಳು.",
			"15": "<sup>15</sup> ಆಗ ಯಾಜಕನಾದ ಯೆಹೋಯಾದಾವನು ಸೇನಾನಿಗಳಾದ ಶತಾಧಿಪತಿಗಳಿಗೆ, “ಈಕೆಯನ್ನು ಸರ್ವೇಶ್ವರನ ಆಲಯದಲ್ಲಿ ಕೊಲ್ಲಬೇಡಿ; ಎರಡು ಸಾಲು ಸಿಪಾಯಿಗಳು ಈಕೆಯನ್ನು ನಡುವೆ ಮಾಡಿ, ಹೊರಗೆ ಕರೆದುಕೊಂಡು ಹೋಗಲಿ; ಮತ್ತು ಈಕೆಯನ್ನು ಹಿಂಬಾಲಿಸುವಂಥವರನ್ನು ಕತ್ತಿಯಿಂದ ಕೊಲ್ಲಿರಿ,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"16": "<sup>16</sup> ಅವರು ಆಕೆಯನ್ನು ಹಿಡಿದು ಕುದುರೆಗಳ ಬಾಗಿಲಿನಿಂದ ಅರಮನೆಗೆ ಒಯ್ದು ಅಲ್ಲಿ ಹತಿಸಿದರು.",
			"17": "<sup>17</sup> ಅನಂತರ ಯೆಹೋಯಾದಾವನ ಪ್ರೇರಣೆಯಿಂದ ಅರಸನೂ ಪ್ರಜೆಗಳೂ ತಾವು ಸರ್ವೇಶ್ವರನ ಪ್ರಜೆಗಳಾಗಿರುವುದಾಗಿ ಪ್ರಮಾಣಮಾಡಿದರು. ಇದಲ್ಲದೆ, ರಾಜನಿಗೂ ಪ್ರಜೆಗಳಿಗೂ ಒಂದು ಒಪ್ಪಂದವಾಯಿತು.",
			"18": "<sup>18</sup> ಯೆಹೂದ್ಯರೆಲ್ಲರೂ ಬಾಳ್ ದೇವತೆಯ ದೇವಸ್ಥಾನಕ್ಕೆ ಹೋಗಿ ಬಾಳನ ಪೂಜಾರಿಯಾದ ಮತ್ತಾನನನ್ನು ಪೀಠಗಳ ಮುಂದೆಯೇ ಕೊಂದು, ಆ ದೇವಸ್ಥಾನವನ್ನೂ ಅದರಲ್ಲಿದ್ದ ಪೀಠ ಹಾಗು ವಿಗ್ರಹಗಳನ್ನೂ ಸಂಪೂರ್ಣವಾಗಿ ಹಾಳುಮಾಡಿದರು. ಯೆಹೋಯಾದಾವನು ದೇವಾಲಯಕ್ಕೆ ಕಾವಲಿಟ್ಟನು;",
			"19": "<sup>19</sup> ಬಳಿಕ ಶತಾಧಿಪತಿಗಳು, ‘ಕಾರಿ’ ಎನಿಸಿಕೊಳ್ಳುವ ಸೈನ್ಯ, ಕಾವಲುದಂಡು ಹಾಗು ಜನಸಾಮಾನ್ಯರು ಇವರೊಡನೆ ಅರಸನನ್ನು ಸರ್ವೇಶ್ವರನ ಆಲಯದಿಂದ ಕಾವಲುದಂಡಿನವರ ಬಾಗಿಲಿನ ಮಾರ್ಗವಾಗಿ, ಅರಮನೆಗೆ ಕರೆದುಕೊಂಡು ಹೋಗಿ, ರಾಜಸಿಂಹಾಸನದ ಮೇಲೆ ಕುಳ್ಳಿರಿಸಿದನು.",
			"20": "<sup>20</sup> ನಾಡಿನವರೆಲ್ಲರೂ ಬಹಳವಾಗಿ ಸಂತೋಷಪಟ್ಟರು. ಪಟ್ಟಣದಲ್ಲಿ ಶಾಂತಿಯಿತ್ತು; ಅತಲ್ಯಳನ್ನು ಅರಮನೆಯಲ್ಲೇ ಕತ್ತಿಯಿಂದ ಸಂಹರಿಸಿದರು.",
			"21": "<sup>21</sup> ಹೀಗೆ ಯೆಹೋವಾಷನು ಅರಸನಾದಾಗ ಅವನಿಗೆ ಏಳು ವರ್ಷ ವಯಸ್ಸು."
		},
		"12": {
			"1": "<sup>1</sup> ಯೆಹೋವಾಷನು ಯೇಹುವಿನ ಆಳ್ವಿಕೆಯ ಏಳನೆಯ ವರ್ಷದಲ್ಲಿ ಅರಸನಾಗಿ ಜೆರುಸಲೇಮಿನಲ್ಲಿ ನಲವತ್ತು ವರ್ಷ ಆಳಿದನು. ಬೇರ್ಷೆಬದವಳಾದ ಚಿಬ್ಯಳು ಅವನ ತಾಯಿ.",
			"2": "<sup>2</sup> ಯಾಜಕನಾದ ಯೆಹೋಯಾದವನು ಅವನಿಗೆ ಬೋಧಕನಾಗಿದ್ದನು. ಆಗ ಅವನು ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ಒಳ್ಳೆಯವನಾಗಿಯೇ ನಡೆದುಕೊಳ್ಳುತ್ತಿದ್ದನು.",
			"3": "<sup>3</sup> ಆದರೆ ಅವನು ಪೂಜಾಸ್ಥಳಗಳನ್ನು ತೆಗೆದುಹಾಕಲಿಲ್ಲ. ಆದುದರಿಂದ ಜನರು ಅವುಗಳಲ್ಲಿ ಬಲಿಗಳನ್ನೂ ಧೂಪಗಳನ್ನೂ ಅರ್ಪಿಸುತ್ತಿದ್ದರು.",
			"4": "<sup>4</sup> ಯೆಹೋವಾಷನು ಯಾಜಕರಿಗೆ, “ಸರ್ವೇಶ್ವರನ ಆಲಯಕ್ಕೆ ಸೇರುವ ಎಲ್ಲಾ ಪರಿಶುದ್ಧ ದ್ರವ್ಯವನ್ನು, ಅಂದರೆ ಜನಗಣತಿಯಲ್ಲಿ ಎಣಿಕೆಯಾದ ಪ್ರತಿಯೊಬ್ಬನು ತಂದುಕೊಡುವ ಹಣ, ದೇವರಿಗೆ ಪ್ರತಿಷ್ಟಿತನಾದ ವ್ಯಕ್ತಿ ತನ್ನ ಪ್ರಾಣವನನ್ನು ಬಿಡಿಸಿಕೊಳ್ಳುವುದಕ್ಕಾಗಿ ತೆರುವ ಹಣ, ಜನರು ಸರ್ವೇಶ್ವರನ ಆಲಯಕ್ಕೆ ಸ್ವೇಚ್ಛೆಯಿಂದ ತಂದು ಒಪ್ಪಿಸುವ ಹಣ, ಇವುಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ದೇವಾಲಯವು ಎಲ್ಲೆಲ್ಲಿ ಶಿಥಿಲವಾಗಿ ಇರುತ್ತದೆ ಎಂದು ನೋಡಿ ಅದನ್ನು ಸರಿಮಾಡುವುದಕ್ಕಾಗಿ ವಿನಿಯೋಗಿಸಿರಿ.",
			"5": "<sup>5</sup> ಪ್ರತಿಯೊಬ್ಬನೂ ತನ್ನ ತನ್ನ ಪರಿಚಿತರಿಂದಲೇ ಹಣ ತೆಗೆದುಕೊಳ್ಳಬೇಕು,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"6": "<sup>6</sup> ಯಾಜಕರು ಅರಸನಾದ ಯೆಹೋವಾಷನ ಆಳ್ವಿಕೆಯ ಇಪ್ಪತ್ತಮೂರನೆಯ ವರ್ಷದವರೆಗೂ ದೇವಾಲಯದ ದುರಸ್ತುಕಾರ್ಯವನ್ನು ಕೈಗೊಂಡಿರಲಿಲ್ಲ.",
			"7": "<sup>7</sup> ಆದುದರಿಂದ ಅವನು ಯೆಹೋಯಾದಾವ ಮೊದಲಾದ ಯಾಜಕರನ್ನು ಕರೆದು, “ನೀವು ಈವರೆಗೂ ದೇವಾಲಯವನ್ನೇಕೆ ದುರಸ್ತು ಮಾಡಲಿಲ್ಲ? ಇನ್ನು ಮುಂದೆ ನಿಮ್ಮ ಪರಿಚಿತರು ತಂದುಕೊಡುವ ಹಣವನ್ನು ನಿಮ್ಮ ಕೈಯಲ್ಲಿಟ್ಟುಕೊಳ್ಳದೆ. ಕೂಡಲೆ ಅದನ್ನು ದೇವಾಲಯದ ದುರಸ್ತಿಗಾಗಿ ಕೊಡಿ,” ಎಂದು ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ಆಗ ಯಾಜಕರು, “ನಾವು ಇನ್ನು ಮುಂದೆ ಜನರಿಂದ ಹಣ ತೆಗೆದುಕೊಳ್ಳುವುದಿಲ್ಲ, ದೇವಾಲಯದ ದುರಸ್ತಿಗಾಗಿ ಕೈಹಾಕುವುದಿಲ್ಲ,” ಎಂಬುದಾಗಿ ಮಾತುಕೊಟ್ಟರು.",
			"9": "<sup>9</sup> ಯಾಜಕನಾದ ಯೆಹೋಯಾದಾವನು ಒಂದು ಪೆಟ್ಟಿಗೆಯನ್ನು ತೆಗೆದುಕೊಂಡು ಬಂದು, ಅದರ ಮುಚ್ಚಳದಲ್ಲಿ ತೂತುಮಾಡಿ, ಅದನ್ನು ಸರ್ವೇಶ್ವರನ ಆಲಯದ ಬಾಗಿಲಿನ ಬಲಗಡೆಯಲ್ಲಿ, ಬಲಿಪೀಠದ ಹತ್ತಿರ ಇಟ್ಟನು. ದ್ವಾರಪಾಲಕರಾದ ಯಾಜಕರು ಸರ್ವೇಶ್ವರನ ಆಲಯಕ್ಕೆ ತರಲಾದ ಹಣವನ್ನೆಲ್ಲಾ ಅದರಲ್ಲಿಯೇ ಹಾಕಿಸುತ್ತಿದ್ದರು.",
			"10": "<sup>10</sup> ಪೆಟ್ಟಿಗೆಯಲ್ಲಿ ತಕ್ಕಷ್ಟು ಹಣಕೂಡಿದೆಯೆಂದು ಕಂಡುಬಂದಾಗೆಲ್ಲಾ, ರಾಜಲೇಖಕನು ಹಾಗು ಮಹಾಯಾಜಕನು ಬಂದು ಅದನ್ನು ಚೀಲಗಳಲ್ಲಿ ಹಾಕಿ, ತೂಕಮಾಡಿ, ಸರ್ವೇಶ್ವರನ ಆಲಯದ ಹಣವು ಇಷ್ಟಿಷ್ಟು ಆಯಿತೆಂದು ಬರೆದುಕೊಂಡು,",
			"11": "<sup>11</sup> ಅದನ್ನು ಆಲಯದ ಕೆಲಸವನ್ನು ನಡಿಸುವ ಮೇಸ್ತ್ರಿಗಳಿಗೆ ಒಪ್ಪಿಸುತ್ತಿದ್ದರು; ಇವರು ಅದನ್ನು ಅಲ್ಲಿ ಕೆಲಸಮಾಡುವ ಬಡಗಿ, ಶಿಲ್ಪಿ, ಉಪ್ಪಾರ, ಕಲ್ಲುಕುಟಿಕ ಅವರ ಸಂಬಳಕ್ಕಾಗಿಯೂ",
			"12": "<sup>12</sup> ಮರ, ಕೆತ್ತಿದ ಕಲ್ಲು, ಇವುಗಳನ್ನು ಕೊಂಡುಕೊಳ್ಳುವುದಕ್ಕಾಗಿಯೂ ಉಪಯೋಗಿಸುತ್ತಿದ್ದರು.",
			"13": "<sup>13</sup> ಸರ್ವೇಶ್ವರನ ಆಲಯಕ್ಕೆ ತರಲಾದ ಹಣವನ್ನು ದುರಸ್ತುಕಾರ್ಯದವರ ಸಂಬಳಕ್ಕಾಗಿ ಉಪಯೋಗಿಸಿದರೇ ಹೊರತು,",
			"14": "<sup>14</sup> ಸರ್ವೇಶ್ವರನ ಆಲಯದ ಬೆಳ್ಳಿ ಬಟ್ಟಲು, ಕತ್ತರಿ, ಬೋಗುಣಿ, ತುತೂರಿ, ಬೆಳ್ಳಿ, ಬಂಗಾರದ ಪಾತ್ರೆ, ಇವುಗಳನ್ನು ಮಾಡುವುದಕ್ಕಾಗಿ ಉಪಯೋಗಿಸಲಿಲ್ಲ.",
			"15": "<sup>15</sup> ಕೆಲಸಗಾರರಿಗೆ ಸಂಬಳಕೊಡುವ ಮೇಸ್ತ್ರಿಗಳು ನಂಬಿಕಸ್ತರಾಗಿದ್ದುದರಿಂದ ಅವರ ವಶಕ್ಕೆ ಕೊಡಲಾದ ಹಣದ ಲೆಕ್ಕವನ್ನು ಯಾರೂ ಕೇಳಲಿಲ್ಲ.",
			"16": "<sup>16</sup> ಅಪರಾಧಪ್ರಾಯಶ್ಚಿತ್ತಕ್ಕಾಗಿ ಹಾಗು ದೋಷಪರಿಹಾರಕ್ಕಾಗಿ ತರಲಾದ ಹಣ ಯಾಜಕರ ಭಾಗವಾಗಿತ್ತು. ಅದು ಸರ್ವೇಶ್ವರನ ಆಲಯಕ್ಕೆ ಸೇರುತ್ತಿರಲಿಲ್ಲ.",
			"17": "<sup>17</sup> ಇದೇ ಕಾಲದಲ್ಲಿ ಸಿರಿಯಾದವರ ಅರಸನಾದ ಹಜಾಯೇಲನು ಬಂದು ಗತ್ ಊರಿಗೆ ಮುತ್ತಿಗೆಹಾಕಿ ಅದನ್ನು ಸ್ವಾಧೀನಪಡಿಸಿಕೊಂಡು ಅಲ್ಲಿಂದ ಜೆರುಸಲೇಮಿಗೆ ದಾಳಿಯಿಡಲು ಹೊರಟನು.",
			"18": "<sup>18</sup> ಜುದೇಯದ ಅರಸ ಯೆಹೋವಾಷನು ಇದನ್ನು ಕೇಳಿ, ತಾನು ಮತ್ತು ತನ್ನ ಪೂರ್ವಿಕರಾದ ಯೆಹೋಷಾಫಾಟ್, ಯೆಹೋರಾಮ್, ಅಹಜ್ಯಾ ಎಂಬ ಯೆಹೂದರಾಜರು ದೇವಸ್ಥಾನಕ್ಕೆ ಕಾಣಿಕೆಯಾಗಿ ಕೊಟ್ಟ ಒಡವೆಗಳನ್ನು ಹಾಗು ಸರ್ವೇಶ್ವರನ ಆಲಯದ ಮತ್ತು ಅರಮನೆ ಭಂಡಾರಗಳಲ್ಲಿ ಇದ್ದ ಬಂಗಾರದ ವಸ್ತುಗಳನ್ನು ಸಿರಿಯಾದವರ ಅರಸನಾದ ಹಜಾಯೇಲನಿಗೆ ಕಳುಹಿಸಿದನು. ಆಗ ಅವನು ಜೆರುಸಲೇಮನ್ನು ಬಿಟ್ಟುಹೋದನು.",
			"19": "<sup>19</sup> ಯೆಹೋವಾಷನ ಉಳಿದ ಚರಿತ್ರೆ ಹಾಗು ಅವನ ಕಾರ್ಯಕಲಾಪಗಳು ಜುದೇಯ ರಾಜರ ಇತಿಹಾಸ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಲಿಖಿತವಾಗಿವೆ.",
			"20": "<sup>20</sup> ಯೆಹೋವಾಷನ ಉದ್ಯೋಗಸ್ಥರು ಅವನಿಗೆ ವಿರೋಧವಾಗಿ ಒಳಸಂಚು ಮಾಡಿದರು.",
			"21": "<sup>21</sup> ಅವರಲ್ಲಿ ಶಿಮೆಯಾತನ ಮಗ ಯೋಜಾಕಾರ್, ಶೋಮೇರನ ಮಗ ಯೆಹೋಜಾಬಾದ್ ಎಂಬವರು ಸಿಲ್ಲಾ ಊರಿಗೆ ಹೋಗುವ ಗುಟ್ಟಾದ ದಾರಿಯಲ್ಲಿರುವ ಮಿಲ್ಲೋಕೋಟೆಯಲ್ಲಿ ಅವನನ್ನು ಕೊಂದರು. ಅವನ ಜನರು ಅವನ ಶವವನ್ನು ದಾವೀದನಗರದೊಳಗೆ ಅವನ ಕುಟುಂಬ ಸ್ಮಶಾನಭೂಮಿಯಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗ ಅಮಚ್ಯನು ಅರಸನಾದನು."
		},
		"13": {
			"1": "<sup>1</sup> ಜುದೇಯದ ಅರಸನೂ ಅಹಜ್ಯನ ಮಗನೂ ಆದ ಯೆಹೋವಾಷನ ಆಳ್ವಿಕೆಯ ಇಪ್ಪತ್ತಮೂರನೆಯ ವರ್ಷದಲ್ಲಿ, ಯೇಹುವಿನ ಮಗ ಯೆಹೋವಾಹಾಜನು, ಇಸ್ರಯೇಲರ ಅರಸನಾಗಿ ಸಮಾರಿಯದಲ್ಲಿ ಹದಿನೇಳು ವರ್ಷ ಆಳಿದನು.",
			"2": "<sup>2</sup> ಅವನು ಇಸ್ರಯೇಲರನ್ನು ಪಾಪಕ್ಕೆ ಪ್ರೇರಿಸಿದ ನೆಬಾಟನ ಮಗ ಯಾರೊಬ್ಬಾಮನ ದುರ್ಮಾರ್ಗವನ್ನು ಕೈಬಿಡದೆ ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾದನು.",
			"3": "<sup>3</sup> ಆದುದರಿಂದ ಸರ್ವೇಶ್ವರಸ್ವಾಮಿ ಇಸ್ರಯೇಲರ ಮೇಲೆ ಕೋಪಗೊಂಡು, ಅವರನ್ನು ಸಿರಿಯಾದವರ ಅರಸನಾದ ಹಜಾಯೇಲನ ಕೈಗೂ ಅವನ ಮಗನಾದ ಬೆನ್ಹದದನ ಕೈಗೂ ಒಪ್ಪಿಸಿದರು. ಇಸ್ರಯೇಲರು ಯೆಹೋವಾಹಾಜನ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ಸಿರಿಯಾದವರ ಕೈಕೆಳಗಿದ್ದರು.",
			"4": "<sup>4</sup> ಯೆಹೋವಾಹಾಜನು ಸರ್ವೇಶ್ವರನಿಗೆ ಮೊರೆ ಇಟ್ಟನು. ಅವರು ಅವನ ಮೊರೆಗೆ ಓಗೊಟ್ಟು, ಸಿರಿಯಾದವರ ಅರಸನಿಂದ ಬಹಳವಾಗಿ ಶೋಷಿತರಾದ ಇಸ್ರಯೇಲರ ಮೇಲೆ ಕಟಾಕ್ಷವಿಟ್ಟು, ಅವರಿಗೆ ಒಬ್ಬ ವಿಮೋಚಕನನ್ನು ಅನುಗ್ರಹಿಸಿದರು.",
			"5": "<sup>5</sup> ಅವನ ಮುಖಾಂತರವಾಗಿ ಅವರು ಸಿರಿಯಾದವರ ಕೈಯಿಂದ ಬಿಡುಗಡೆಯಾಗಿ, ಮುಂದಿನಂತೆ ತಮ್ಮ ತಮ್ಮ ಮನೆಗಳಲ್ಲಿ ಸುರಕ್ಷಿತರಾಗಿ ವಾಸಿಸುವವರಾದರು.",
			"6": "<sup>6</sup> ಆದರೂ ಅವರು ತಮ್ಮನ್ನು ಪಾಪಕ್ಕೆ ಪ್ರೇರಿಸಿದ ಯಾರೊಬ್ಬಾಮನ ಮನೆಯವರ ದುರ್ಮಾರ್ಗದಲ್ಲಿ ನಡೆದರು. ಅದನ್ನು ಬಿಡಲೇ ಇಲ್ಲ. ಸಮಾರಿಯದಲ್ಲಿ ಅಶೇರ ವಿಗ್ರಹಸ್ಥಂಭವು ಇನ್ನೂ ಉಳಿದಿತ್ತು.",
			"7": "<sup>7</sup> ಯೆಹೋವಾಹಾಜನಿಗೆ ಐವತ್ತುಮಂದಿ ರಾಹುತರು, ಹತ್ತು ರಥಗಳು, ಹತ್ತುಸಾವಿರ ಮಂದಿ ಕಾಲಾಳುಗಳು ಮಾತ್ರ ಉಳಿದಿದ್ದರು. ಸಿರಿಯಾದವರ ಅರಸನು ಬೇರೆ ಎಲ್ಲರನ್ನೂ ಸಂಹರಿಸಿ, ಕಣದ ಧೂಳಿಪಟದಂತೆ ಮಾಡಿಬಿಟ್ಟನು.",
			"8": "<sup>8</sup> ಯೆಹೋವಾಹಾಜನ ಉಳಿದ ಚರಿತ್ರೆ ಹಾಗು ಅವನ ಶೂರಕೃತ್ಯಗಳ ವಿವರ ಇಸ್ರಯೇಲ್ ರಾಜರ ಇತಿಹಾಸ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"9": "<sup>9</sup> ಅವನು ನಿಧನನಾಗಿ ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಲು ಅವನ ಶವವನ್ನು ಸಮಾರಿಯದಲ್ಲಿ ಸಮಾಧಿ ಮಾಡಿದರು. ಅವನ ಸ್ಥಾನದಲ್ಲಿ ಅವನ ಮಗ ಯೋವಾಷನು ಅರಸನಾದನು.",
			"10": "<sup>10</sup> ಜುದೇಯದ ಅರಸನಾದ ಯೆಹೋವಾಷಾನ ಆಳ್ವಿಕೆಯ ಮೂವತ್ತೇಳನೆಯ ವರ್ಷದಲ್ಲಿ ಯೆಹೋವಾಹಾಜನ ಮಗ ಯೋವಾಷನು ಇಸ್ರಯೇಲರ ಅರಸನಾಗಿ ಸಮಾರಿಯದಲ್ಲಿ ಹದಿನಾರು ವರ್ಷ ಆಳಿದನು.",
			"11": "<sup>11</sup> ಇವನು ಇಸ್ರಯೇಲರನ್ನು ಪಾಪಕ್ಕೆ ಪ್ರೇರಿಸಿದ ನೆಬಾಟನ ಮಗನಾದ ಯಾರೊಬ್ಬಾಮನ ದುರ್ಮಾರ್ಗವನ್ನು ಬಿಡದೆ ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾದನು.",
			"12": "<sup>12</sup> ಇವನ ಉಳಿದ ಚರಿತ್ರೆ, ಶೂರಕೃತ್ಯಗಳ ಹಾಗು ಇವನು ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಅಮಚ್ಯನು ಎಂಬವನೊಡನೆ ನಡೆಸಿದ ಯುದ್ಧದ ವಿವರವು ಇಸ್ರಯೇಲ್ ರಾಜರ ಇತಿಹಾಸ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"13": "<sup>13</sup> ಇವನು ನಿಧನನಾಗಿ ಪಿತೃಗಳ ಬಳಿ ಸೇರಲು ಇವನ ಶವವನ್ನು ಸಮಾರಿಯದೊಳಗೆ ಇಸ್ರಯೇಲ್ ರಾಜಸ್ಮಶಾನದಲ್ಲಿ ಸಮಾಧಿ ಮಾಡಿದರು. ಇವನ ತರುವಾಯ ಯಾರೊಬ್ಬಾಮನು ಸಿಂಹಾಸನವನ್ನೇರಿದನು.",
			"14": "<sup>14</sup> ಎಲೀಷನು ಮಾರಕರೋಗದಿಂದ ನರಳುತ್ತಿದ್ದನು. ಇಸ್ರಯೇಲರ ಅರಸ ಯೋವಾಷನು ಇದನ್ನು ಕೇಳಿ ಅವನ ಬಳಿಗೆ ಹೋಗಿ, “ತಂದೆಯೇ, ನನ್ನ ತಂದೆಯೇ, ಇಸ್ರಯೇಲರಿಗೆ ರಥಾರಥಾಶ್ವಬಲಗಳಾಗಿ ಇದ್ದವರೇ,” ಎಂದು ಗೋಳಿಡುತ್ತಾ ಅಧೋಮುಖನಾಗಿ ಬಿದ್ದು ಅತ್ತು ಪ್ರಲಾಪಿಸಿದನು.",
			"15": "<sup>15</sup> ಆಗ ಎಲೀಷನು ಇಸ್ರಯೇಲರ ಅರಸನಿಗೆ, “ಬಿಲ್ಲನ್ನೂ ಬಾಣಗಳನ್ನೂ ತರಿಸಿ,” ಎಂದು ಹೇಳಿದನು; ಅವನು ತರಿಸಿದನು.",
			"16": "<sup>16</sup> ಆಗ, “ಬಿಲ್ಲನ್ನು ಹಿಡಿದುಕೋ,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು. ಅವನು ಹಿಡಿದುಕೊಳ್ಳಲು ತನ್ನ ಕೈಯನ್ನು ಅವನ ಕೈ ಮೇಲೆ ಇಟ್ಟು,",
			"17": "<sup>17</sup> “ಪೂರ್ವದಿಕ್ಕಿಗಿರುವ ಕಿಟಕಿಯನ್ನು ತೆರೆ,” ಎಂದನು. ಅವನು ತೆರೆದನು. ಅನಂತರ, “ಬಾಣವನ್ನೆಸೆ,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು. ಅವನು ಎಸೆಯಲು, “ಇದು ಜಯಪ್ರದವಾದ ಸರ್ವೇಶ್ವರನ ಬಾಣ; ಸಿರಿಯಾದವರನ್ನು ಜಯಿಸುವ ಬಾಣ. ಆ ಸಿರಿಯಾದವರನ್ನು ನೀನು ಅಫೇಕದಲ್ಲಿ ಸೋಲಿಸಿ ಸಂಹರಿಸಿಬಿಡುವೆ,” ಎಂದು ಹೇಳಿದನು.",
			"18": "<sup>18</sup> ಇದಲ್ಲದೆ, ಎಲೀಷನು ಇಸ್ರಯೇಲರ ಅರಸನಿಗೆ, “ಬಾಣಗಳನ್ನು ತೆಗೆದುಕೋ,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದಾಗ ಅವನು ತೆಗೆದುಕೊಳ್ಳಲು, “ಇವುಗಳಿಂದ ನೆಲವನ್ನು ಸೀಳು,” ಎಂದನು. ಅವನು ಮೂರು ಸಾರಿ ಬಾಣಬಿಟ್ಟು ಸುಮ್ಮನೆ ನಿಂತನು.",
			"19": "<sup>19</sup> ಆಗ ದೈವಪುರುಷನು ಅವನ ಮೇಲೆ ಸಿಟ್ಟುಗೊಂಡು, “ನೀನು ಐದಾರು ಸಾರಿ ಬಾಣಬಿಡಬೇಕಾಗಿತ್ತು. ಹಾಗೆ ಮಾಡಿದ್ದರೆ ಸಿರಿಯಾದವರು ನಿರ್ನಾಮವಾಗಿ ಹೋಗುವವರೆಗೂ ಅವರ ಮೇಲೆ ನಿನಗೆ ಜಯ ದೊರಕುತ್ತಿತ್ತು. ನೀನು ಮೂರು ಸಾರಿ ಮಾತ್ರ ಬಾಣಬಿಟ್ಟದ್ದರಿಂದ ಅವರನ್ನು ಮೂರು ಸಾರಿ ಮಾತ್ರ ಸೋಲಿಸುವೆ,” ಎಂದು ಹೇಳಿದನು.",
			"20": "<sup>20</sup> ಎಲೀಷನು ಮೃತನಾದನು. ಅವನ ಶವವನ್ನು ಸಮಾಧಿಮಾಡಿದರು. ಮೋವಾಬ್ಯರು ಪ್ರತಿವರ್ಷದ ಪ್ರಾರಂಭದಲ್ಲಿ ಸುಲಿಗೆಮಾಡುವುದಕ್ಕಾಗಿ ಗುಂಪುಗುಂಪಾಗಿ ಬರುತ್ತಿದ್ದರು.",
			"21": "<sup>21</sup> ಒಂದು ದಿನ, ಜನರು ಒಬ್ಬ ಸತ್ತವನನ್ನು ಸಮಾಧಿ ಮಾಡುವುದಕ್ಕೆ ಹೋದಾಗ, ಮೋವಾಬ್ಯರ ಗುಂಪು ಬರುತ್ತಿರುವುದನ್ನು ಕಂಡು, ಶವವನ್ನು ಎಲೀಷನ ಸಮಾಧಿಯಲ್ಲೆ ಬಿಸಾಡಿ ಓಡಿಹೋದರು. ಸತ್ತ ವ್ಯಕ್ತಿಯ ಶವ ಎಲೀಷನ ಎಲುಬುಗಳಿಗೆ ತಗುಲಿದ ಕೂಡಲೆ, ಆ ವ್ಯಕ್ತಿ ಉಜ್ಜೀವಿಸಿ ಎದ್ದುನಿಂತನು.",
			"22": "<sup>22</sup> ಸಿರಿಯಾದವರ ಅರಸನಾದ ಹಜಾಯೇಲನು ಯೆಹೋವಾಹಾಜನ ಆಳ್ವಿಕೆಯಲ್ಲೆಲ್ಲಾ ಇಸ್ರಯೇಲರನ್ನು ಪೀಡಿಸುತ್ತಿದ್ದನು.",
			"23": "<sup>23</sup> ಆದರೂ ಸರ್ವೇಶ್ವರ ಅವರನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ಹಾಳುಮಾಡಗೊಡಲಿಲ್ಲ. ಅವರನ್ನು ಈ ಕಾಲದಲ್ಲಿಯೂ ತಮ್ಮ ಸನ್ನಿಧಿಯಿಂದ ತಳ್ಳಿಬಿಡಲಿಲ್ಲ. ತಾವು ಅಬ್ರಹಾಮ್, ಇಸಾಕ್, ಯಕೋಬರೊಡನೆ ಮಾಡಿಕೊಂಡ ಒಡಂಬಡಿಕೆಯನ್ನು ಸ್ಮರಿಸಿ, ಅವರಿಗೆ ದಯೆತೋರಿಸಿದರು; ಕರುಳುಕರಗಿದವರಾಗಿ ಅವರಿಗೆ ಪ್ರಸನ್ನರಾದರು.",
			"24": "<sup>24</sup> ಸಿರಿಯಾದವರ ಅರಸನಾದ ಹಜಾಯೇಲನು ಮರಣಹೊಂದಲು ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗ ಬೆನ್ಹದದನು ಅರಸನಾದನು.",
			"25": "<sup>25</sup> ಯೆಹೋವಾಹಾಜನ ಮಗ ಯೋವಾಷನು, ತನ್ನ ತಂದೆಯ ಕಾಲದಲ್ಲಿ ಹಜಾಯೇಲನು ಯುದ್ಧಮಾಡಿ ಕಿತ್ತುಕೊಂಡಿದ್ದ ಪಟ್ಟಣಗಳನ್ನು, ಬೆನ್ಹದದನಿಂದ ತಿರುಗಿ ತೆಗೆದುಕೊಂಡನು. ಅವನನ್ನು ಮೂರು ಸಾರಿ ಸೋಲಿಸಿ, ಇಸ್ರಯೇಲರ ಎಲ್ಲಾ ಪಟ್ಟಣಗಳನ್ನು ಹಿಂದಕ್ಕೆ ಪಡೆದನು."
		},
		"14": {
			"1": "<sup>1</sup> ಇಸ್ರಯೇಲರ ಅರಸನಾದ ಯೆಹೋವಾಹಾಜನ ಮಗ ಯೋವಾಷನ ಆಳ್ವಿಕೆಯ ಎರಡನೆಯ ವರ್ಷದಲ್ಲಿ ಜುದೇಯದ ಅರಸನಾದ ಯೆಹೋವಾಷನ ಮಗ ಅಮಚ್ಯ ಎಂಬವನು ಅರಸನಾದನು.",
			"2": "<sup>2</sup> ಪಟ್ಟಕ್ಕೆ ಬಂದಾಗ ಇವನಿಗೆ ಇಪ್ಪತ್ತೈದು ವರ್ಷ ವಯಸ್ಸು. ಇವನು ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಇಪ್ಪತ್ತೊಂಬತ್ತು ವರ್ಷ ಆಳಿದನು. ಜೆರುಸಲೇಮಿನವಳಾದ ಯೆಹೋವದ್ದೀನ್ ಎಂಬಾಕೆ ಇವನ ತಾಯಿ.",
			"3": "<sup>3</sup> ಇವನು ತನ್ನ ತಂದೆ ಯೆಹೋವಾಷನ ಸನ್ಮಾರ್ಗದಲ್ಲಿ ತಪ್ಪದೆ ನಡೆದು ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ಒಳ್ಳೆಯವನಾದನು. ಆದರೂ ತನ್ನ ಪೂರ್ವಜ ದಾವೀದನಷ್ಟು ಉತ್ತಮನಾಗಿರಲಿಲ್ಲ.",
			"4": "<sup>4</sup> ಇವನ ಕಾಲದಲ್ಲೂ ಪೂಜಾಸ್ಥಳಗಳು ಹಾಗೆಯೇ ಇದ್ದವು. ಜನರು ಅವುಗಳಲ್ಲಿ ಬಲಿಗಳನ್ನೂ ಧೂಪಗಳನ್ನೂ ಅರ್ಪಿಸುತ್ತಿದ್ದರು.",
			"5": "<sup>5</sup> ಇವನು ರಾಜ್ಯವನ್ನು ಭದ್ರಪಡಿಸಿಕೊಂಡ ಕೂಡಲೆ ತನ್ನ ತಂದೆಯನ್ನು ಕೊಂದ ಉದ್ಯೋಗಸ್ಥರಿಗೆ ಮರಣ ವಿಧಿಸಿದನು.",
			"6": "<sup>6</sup> ಆದರೆ, “ಮಕ್ಕಳು ಮಾಡಿದ ಪಾಪಕ್ಕೆ ತಂದೆಗಾಗಲಿ, ತಂದೆ ಮಾಡಿದ ಪಾಪದ ನಿಮಿತ್ತ ಮಕ್ಕಳಿಗಾಗಲಿ ಮರಣಶಿಕ್ಷೆಯಾಗಬಾರದು; ಪ್ರತಿಯೊಬ್ಬನೂ ತನ್ನ ಪಾಪಫಲವನ್ನು ತಾನೇ ಅನುಭವಿಸಬೇಕು,” ಎಂಬುದಾಗಿ ಮೋಶೆಯ ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿರುವ ಸರ್ವೇಶ್ವರನ ಆಜ್ಞೆಯನ್ನು ನೆನಪುಮಾಡಿಕೊಂಡು ಅವರ ಮಕ್ಕಳನ್ನು ಕೊಲ್ಲಿಸಲಿಲ್ಲ.",
			"7": "<sup>7</sup> ಇದಲ್ಲದೆ, ಉಪ್ಪಿನ ಕಣಿವೆಯಲ್ಲಿ ಎದೋಮ್ಯರ ಹತ್ತು ಸಾವಿರ ಮಂದಿ ಸೈನಿಕರನ್ನು ಸದೆಬಡಿದು ಅವರಿಂದ ‘ಸೆಲ’ ದುರ್ಗವನ್ನು ಕಿತ್ತುಕೊಂಡು ಅದಕ್ಕೆ ಯೊಕ್ತೆಯೇಲ್ ಎಂಬ ಹೆಸರಿಟ್ಟನು. ಅದಕ್ಕೆ ಇಂದಿನವರೆಗೂ ಇದೇ ಹೆಸರಿರುತ್ತದೆ.",
			"8": "<sup>8</sup> ಅನಂತರ ಅಮಚ್ಯನು ಯೇಹುವಿನ ಮೊಮ್ಮಗನೂ ಯೆಹೋವಾಹಾಜನ ಮಗನೂ ಇಸ್ರಯೇಲರ ಅರಸನೂ ಆದ ಯೋವಾಷನ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿದನು. ಅವನಿಗೆ, “ನಾವು ಒಬ್ಬರನ್ನೊಬ್ಬರು ಎದುರುಗೊಳ್ಳೋಣ ಬಾ,” ಎಂದು ಹೇಳಿಸಿದನು.",
			"9": "<sup>9</sup> ಆಗ ಇಸ್ರಯೇಲರ ಅರಸನಾದ ಯೋವಾಷ ಜುದೇಯದ ಅರಸನಾದ ಅಮಚ್ಯನಿಗೆ, “ಲೆಬನೋನಿನ ಮುಳ್ಳುಗಿಡವು ಅಲ್ಲಿನ ದೇವದಾರು ಮರಕ್ಕೆ, ‘ನನ್ನ ಮಗನಿಗೆ ನಿನ್ನ ಮಗಳನ್ನು ಕೊಡು,’ ಎಂದು ಹೇಳಿಕಳುಹಿಸಿತು. ತುಸು ಹೊತ್ತಾದ ಮೇಲೆ ಲೆಬನೋನಿನ ಒಂದು ಕಾಡುಮೃಗ ಅಲ್ಲಿಂದ ಹಾದುಹೋಗುತ್ತಿರುವಾಗ ಆ ಮುಳ್ಳುಗಿಡವನ್ನು ತುಳಿದುಬಿಟ್ಟಿತು.",
			"10": "<sup>10</sup> ನೀನು ಎದೋಮ್ಯರನ್ನು ಸೋಲಿಸಿದ್ದರಿಂದ ಬಹಳವಾಗಿ ಉಬ್ಬಿಕೊಂಡಿರುವೆ; ಆ ಕೀರ್ತಿ ಸಾಕೆಂದು ನೆನಸಿ ಸುಮ್ಮನೆ ಮನೆಯಲ್ಲೇ ಕುಳಿತುಕೋ. ನನ್ನನ್ನು ಕೆಣಕಿ ನಿನ್ನ ಮೇಲೆಯೂ ನಿನ್ನ ರಾಜ್ಯದ ಮೇಲೆಯೂ ಏಕೆ ಕೇಡನ್ನು ಬರಮಾಡಿಕೊಳ್ಳುವೆ?” ಎಂದು ಉತ್ತರಕೊಟ್ಟು ಕಳುಹಿಸಿದನು.",
			"11": "<sup>11</sup> ಅಮಚ್ಯನು ಇದನ್ನು ಲಕ್ಷಿಸಲಿಲ್ಲವಾದುದರಿಂದ ಇಸ್ರಯೇಲರ ಅರಸನಾದ ಯೋವಾಷನು ಯುದ್ಧಕ್ಕೆ ಹೊರಟನು; ಅವರು ಜುದೇಯ ಪ್ರಾಂತ್ಯದ ಬೇತ್ಷೆಮೆಷಿನಲ್ಲಿ ಒಬ್ಬರನ್ನೊಬ್ಬರು ಎದುರುಗೊಂಡರು.",
			"12": "<sup>12</sup> ಯೆಹೂದ್ಯರು ಇಸ್ರಯೇಲರಿಂದ ಸೋಲು ಸವಿದು ತಮ್ಮ ತಮ್ಮ ನಿವಾಸಗಳಿಗೆ ಓಡಿಹೋದರು.",
			"13": "<sup>13</sup> ಇಸ್ರಯೇಲರ ಅರಸ ಯೋವಾಷನು, ಅಹಜ್ಯನ ಮೊಮ್ಮಗನೂ ಯೆಹೋವಾಷನ ಮಗನೂ ಜುದೇಯದ ಅರಸನೂ ಆದ ಅಮತ್ಯನನ್ನು, ಬೇತ್ಷೆಮೆಷಿನಲ್ಲಿ ಹಿಡಿದು, ಅವನನ್ನು ಜೆರುಸಲೇಮಿಗೆ ಕರೆದುಕೊಂಡುಬಂದು, ಎಫ್ರಯಿಮ್ ಬಾಗಿಲಿಗೂ ಮೂಲೆಯ ಬಾಗಿಲಿಗೂ ಮಧ್ಯದಲ್ಲಿದ್ದ ಜೆರುಸಲೇಮಿನ ನಾನೂರು ಮೊಳ ಗೋಡೆಯನ್ನು ಕೆಡವಿಬಿಟ್ಟನು.",
			"14": "<sup>14</sup> ಇದಲ್ಲದೆ, ಅವನು ಸರ್ವೇಶ್ವರನ ಆಲಯದ ಮತ್ತು ಅರಮನೆಯ ಭಂಡಾರಗಳಲ್ಲಿ ಸಿಕ್ಕಿದ ಎಲ್ಲಾ ಬೆಳ್ಳಿ ಬಂಗಾರವನ್ನೂ ಪಾತ್ರೆಗಳನ್ನೂ ತೆಗೆದುಕೊಂಡು, ಕೆಲವರನ್ನು ಒತ್ತೆಯಾಳುಗಳಾಗಿ ಸೆರೆಹಿಡಿದು, ಸಮಾರಿಯಕ್ಕೆ ಹಿಂದಿರುಗಿದನು.",
			"15": "<sup>15</sup> ಯೋವಾಷನ ಉಳಿದ ಚರಿತ್ರೆ, ಅವನ ಶೂರಕೃತ್ಯಗಳ ಮತ್ತು ಜುದೇಯದ ರಾಜನಾದ ಅಮಚ್ಯನೊಡನೆ ನಡೆಸಿದ ಯುದ್ಧದ ವಿವರ, ಇವು ಇಸ್ರಯೇಲ್ ರಾಜರ ಇತಿಹಾಸ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"16": "<sup>16</sup> ಅವನು ನಿಧನನಾಗಿ ಪಿತೃಗಳ ಬಳಿ ಸೇರಲು ಅವನ ಶವವನ್ನು ಸಮಾರಿಯದೊಳಗೆ ಇಸ್ರಯೇಲರ ರಾಜಸ್ಮಶಾನಭೂಮಿಯಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗ ಯಾರೊಬ್ಬಾಮನು ಅರಸನಾದನು.",
			"17": "<sup>17</sup> ಇಸ್ರಯೇಲರ ಅರಸನೂ ಯೆಹೋವಾಹಾಜನ ಮಗನೂ ಆದ ಯೋವಾಷನು ಸತ್ತನಂತರ, ಜುದೇಯದ ಅರಸನೂ ಯೆಹೋವಾಷನ ಮಗನೂ ಆದ ಅಮಚ್ಯನು ಇನ್ನೂ ಹದಿನೈದು ವರ್ಷ ಬದುಕಿದ್ದನು.",
			"18": "<sup>18</sup> ಅವನ ಉಳಿದ ಚರಿತ್ರೆ ಜುದೇಯ ರಾಜರ ಇತಿಹಾಸ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತದೆ.",
			"19": "<sup>19</sup> ಜೆರುಸಲೇಮಿನವರು ಅವನಿಗೆ ವಿರುದ್ಧ ಒಳಸಂಚು ಮಾಡಿದ್ದರಿಂದ ಅವನು ಲಾಕೀಷಿಗೆ ಓಡಿಹೋದನು; ಆದರೆ ಅವರು ಅಲ್ಲಿಗೆ ಆಳುಗಳನ್ನು ಕಳುಹಿಸಿ ಅವನನ್ನು ಕೊಲ್ಲಿಸಿದರು.",
			"20": "<sup>20</sup> ಅವನ ಜನರು ಅವನ ಶವವನ್ನು ಕುದುರೆಗಳ ಮೇಲೆ ಜೆರುಸಲೇಮಿಗೆ ತಂದು ದಾವೀದ ನಗರದೊಳಗೆ ಅವನ ಕುಟುಂಬ ಸ್ಮಶಾನಭೂಮಿಯಲ್ಲಿ ಸಮಾಧಿ ಮಾಡಿದರು.",
			"21": "<sup>21</sup> ಅನಂತರ ಎಲ್ಲಾ ಯೆಹೂದ್ಯರು ಕೂಡಿ ಹದಿನಾರು ವರ್ಷದವನಾದ ಅಜರ್ಯನನ್ನು ಅವನ ತಂದೆ ಅಮಚ್ಯನಿಗೆ ಬದಲಾಗಿ ಅರಸನನ್ನಾಗಿ ಮಾಡಿದರು.",
			"22": "<sup>22</sup> ಅರಸನು ಮೃತನಾಗಿ ಪಿತೃಗಳ ಬಳಿ ಸೇರಿದ ಮೇಲೆ, ಇವನು ಏಲತ್ ಎಂಬ ಪಟ್ಟಣವನ್ನು ಮತ್ತೆ ಜುದೇಯ ರಾಜ್ಯಕ್ಕೆ ಸೇರಿಸಿ, ಭದ್ರಪಡಿಸಿದನು.",
			"23": "<sup>23</sup> ಜುದೇಯದ ಅರಸನೂ ಯೆಹೋವಾಷನ ಮಗನೂ ಆದ ಅಮಚ್ಯನ ಆಳ್ವಿಕೆಯ ಹದಿನೈದನೆಯ ವರ್ಷದಲ್ಲಿ, ಇಸ್ರಯೇಲರ ಅರಸನಾದ ಯೋವಾಷನ ಮಗ ಯಾರೊಬ್ಬಾಮನು, ಅರಸನಾಗಿ ಸಮಾರಿಯದಲ್ಲಿ ನಾಲ್ವತ್ತೊಂದು ವರ್ಷ ಆಳಿದನು.",
			"24": "<sup>24</sup> ಇವನು ಇಸ್ರಯೇಲರನ್ನು ಪಾಪಕ್ಕೆ ಪ್ರಚೋದಿಸಿದ ನೆಬಾಟನ ಮಗನಾದ ಯಾರೊಬ್ಬಾಮನ ದುರ್ಮಾರ್ಗವನ್ನು ಬಿಡದೆ ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾದನು.",
			"25": "<sup>25</sup> ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರ, ತನ್ನ ಸೇವಕನೂ ಗತ್ ಹೇಫೆರಿವನಾದ ಅಮಿತ್ತೈಯ ಮಗನೂ ಆದ ಯೋನಾ ಎಂಬ ಪ್ರವಾದಿಯ ಮುಖಾಂತರ ಮುಂತಿಳಿಸಿದಂತೆ, ಹಮಾತಿನ ದಾರಿಯಿಂದ ಅರಾಬಾ ತಗ್ಗಿನ ಸಮುದ್ರದವರೆಗಿದ್ದ ಇಸ್ರಯೇಲರ ಮೇರೆಯನ್ನು ತಿರುಗಿ ತೆಗೆದುಕೊಂಡವನು ಇವನೇ.",
			"26": "<sup>26</sup> “ಇಸ್ರಯೇಲರು ಘೋರಕಷ್ಟವನ್ನು ಅನುಭವಿಸುತ್ತಿರುತ್ತಾರೆ. ಅವರಲ್ಲಿದ್ದ ಸ್ವತಂತ್ರರೂ ಪರತಂತ್ರರೂ ನಾಶವಾಗಿದ್ದಾರೆ, ಅವರನ್ನು ರಕ್ಷಿಸುವವನು ಒಬ್ಬನೂ ಇಲ್ಲ,” ಎಂಬುದನ್ನು ಸರ್ವೇಶ್ವರ ನೋಡಿದರು.",
			"27": "<sup>27</sup> ಆ ಜನರನ್ನು ಭೂಲೋಕದಿಂದ ನಿರ್ನಾಮಮಾಡುವುದಕ್ಕೆ ಮನಸ್ಸಿಲ್ಲದೆ, ಯೋವಾಷನ ಮಗ ಯಾರೊಬ್ಬಾಮನ ಮುಖಾಂತರ ಅವರನ್ನು ರಕ್ಷಿಸಿದರು.",
			"28": "<sup>28</sup> ಯಾರೊಬ್ಬಾಮನ ಉಳಿದ ಚರಿತ್ರೆ, ಅವನು ಯುದ್ಧದಲ್ಲಿ ನಡೆಸಿದ ಶೂರಕಾರ್ಯ ಮತ್ತು ಮೊದಲು ಯೆಹೂದ್ಯರ ಸ್ವಾಧೀನದಲ್ಲಿದ್ದ ದಮಸ್ಕ, ಹಮಾತ್ ಎಂಬ ಪಟ್ಟಣಗಳು ಇವನಿಂದ ಇಸ್ರಯೇಲ್ ರಾಜ್ಯಕ್ಕೆ ವಶವಾದ ವಿವರ, ಇವೆಲ್ಲವು ಇಸ್ರಯೇಲ್ ರಾಜರ ಇತಿಹಾಸ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"29": "<sup>29</sup> ಅವನು ನಿಧನನಾಗಿ ಇಸ್ರಯೇಲ್ ರಾಜರಾಗಿದ್ದ ತನ್ನ ಪಿತೃಗಳ ಬಳಿ ಸೇರಿದನು. ಅವನ ಸ್ಥಾನದಲ್ಲಿ ಅವನ ಮಗ ಜೆಕರ್ಯನು ಅರಸನಾದನು."
		},
		"15": {
			"1": "<sup>1</sup> ಇಸ್ರಯೇಲರ ಅರಸ ಯಾರೊಬ್ಬಾಮನ ಆಳ್ವಿಕೆಯ ಇಪ್ಪತ್ತೇಳನೆಯ ವರ್ಷದಲ್ಲಿ ಅಮಚ್ಯನ ಮಗ ಅಜರ್ಯನು ಜುದೇಯದ ಅರಸನಾದನು.",
			"2": "<sup>2</sup> ಪಟ್ಟಕ್ಕೆ ಬಂದಾಗ ಇವನಿಗೆ ಹದಿನಾರು ವರ್ಷ ವಯಸ್ಸು. ಆ ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಐವತ್ತೆರಡು ವರ್ಷ ಆಳಿದನು. ಜೆರುಸಲೇಮಿನವಳಾದ ಯೆಕೊಲ್ಯ ಎಂಬಾಕೆ ಇವನ ತಾಯಿ.",
			"3": "<sup>3</sup> ಇವನು ತನ್ನ ತಂದೆಯಾದ ಅಮಚ್ಯನಂತೆ ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ಒಳ್ಳೆಯವನಾಗಿ ನಡೆದನು.",
			"4": "<sup>4</sup> ಆದರೆ ಇವನ ಕಾಲದಲ್ಲಿಯೂ ಪೂಜಾಸ್ಥಳಗಳು ಹಾಗೆಯೇ ಉಳಿದಿದ್ದವು. ಜನರು ಅವುಗಳಲ್ಲಿ ಬಲಿಗಳನ್ನೂ ಧೂಪಗಳನ್ನೂ ಅರ್ಪಿಸುತ್ತಿದ್ದರು.",
			"5": "<sup>5</sup> ಸರ್ವೇಶ್ವರಸ್ವಾಮಿ ಇವನನ್ನು ಕುಷ್ಠರೋಗದಿಂದ ಶಿಕ್ಷಿಸಿದರು. ಆದುದರಿಂದ ಇವನು ಜೀವದಿಂದಿರುವವರೆಗೂ ಪ್ರತ್ಯೇಕವಾದ ಮನೆಯಲ್ಲಿ ವಾಸಿಸಬೇಕಾಯಿತು. ರಾಜಗೃಹಾಧಿಪತ್ಯವನ್ನೂ ಪ್ರಜಾಪಾಲನೆಯನ್ನೂ ಇವನ ಮಗ ಯೋತಾಮನು ನೋಡಿಕೊಳ್ಳುತ್ತಿದ್ದನು.",
			"6": "<sup>6</sup> ಅಜರ್ಯನ ಉಳಿದ ಚರಿತ್ರೆ ಹಾಗು ಅವನ ಕಾರ್ಯಕಲಾಪಗಳು ಜುದೇಯ ರಾಜರ ಇತಿಹಾಸ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"7": "<sup>7</sup> ಅವನು ಸತ್ತು ಪಿತೃಗಳ ಬಳಿ ಸೇರಿದಾಗ ಅವನ ಶವವನ್ನು ದಾವೀದನಗರದೊಳಗೆ ರಾಜಕುಟುಂಬದ ಸ್ಮಶಾನಭೂಮಿಯಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಅವನ ಸ್ಥಾನದಲ್ಲಿ ಅವನ ಮಗ ಯೋತಾಮನು ಅರಸನಾದನು.",
			"8": "<sup>8</sup> ಜುದೇಯದ ಅರಸನಾದ ಅಜರ್ಯನ ಆಳ್ವಿಕೆಯ ಮೂವತ್ತೆಂಟನೆಯ ವರ್ಷದಲ್ಲಿ ಯಾರೊಬ್ಬಾಮನ ಮಗ ಜೆಕರ್ಯ ಎಂಬವನು ಇಸ್ರಯೇಲರ ಅರಸನಾಗಿ ಸಮಾರಿಯದಲ್ಲಿ ಆರು ತಿಂಗಳು ಆಳಿದನು.",
			"9": "<sup>9</sup> ಅವನು ಇಸ್ರಯೇಲರನ್ನು ಪಾಪಕ್ಕೆ ಪ್ರೇರಿಸಿದ ನೆಬಾಟನ ಮಗ ಯಾರೊಬ್ಬಾಮನ ದುರ್ಮಾರ್ಗವನ್ನು ಬಿಡದೆ ತನ್ನ ಪೂರ್ವಿಕರಂತೆ ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾದನು.",
			"10": "<sup>10</sup> ಯಾಬೇಷನ ಮಗ ಶಲ್ಲೂಮನು ಅವರಿಗೆ ವಿರುದ್ಧ ಒಳಸಂಚುಮಾಡಿ ಪ್ರಜೆಗಳ ಮುಂದೆಯೇ ಅವನನ್ನು ಕೊಂದು ಅವನಿಗೆ ಬದಲಾಗಿ ತಾನೇ ಅರಸನಾದನು.",
			"11": "<sup>11</sup> ಜೆಕರ್ಯನ ಉಳಿದ ಚರಿತ್ರೆ ಇಸ್ರಯೇಲ್ ರಾಜರ ಇತಿಹಾಸ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಲಿಖಿತವಾಗಿದೆ.",
			"12": "<sup>12</sup> ನಿನ್ನ ಸಂತಾನದವರು ನಾಲ್ಕನೆಯ ತಲೆಯವರೆಗೂ ಇಸ್ರಯೇಲ್ ಸಿಂಹಾಸನದ ಮೇಲೆ ಕುಳಿತುಕೊಳ್ಳುವರು ಎಂಬುದಾಗಿ ಯೇಹುವಿಗೆ ಸರ್ವೇಶ್ವರ ಮುಂತಿಳಿಸಿದ ಮಾತು ನೆರವೇರಿತು.",
			"13": "<sup>13</sup> ಜುದೇಯದ ಅರಸ ಉಜ್ಜೀಯನ ಆಳ್ವಿಕೆಯ ಮೂವತ್ತೊಂಬತ್ತನೆಯ ವರ್ಷದಲ್ಲಿ ಯಾಬೇಷನ ಮಗ ಶಲ್ಲೂಮನು ಇಸ್ರಯೇಲರ ಅರಸನಾಗಿ ಸಮಾರಿಯದಲ್ಲಿ ಒಂದು ತಿಂಗಳು ಆಳಿದನು.",
			"14": "<sup>14</sup> ಗಾದಿಯ ಮಗ ಮೆನಹೇಮನೆಂಬವನು ತಿರ್ಚದಿಂದ ಸಮಾರಿಯಕ್ಕೆ ಬಂದು ಅಲ್ಲಿದ್ದ ಯಾಬೇಷನ ಮಗ ಶಲ್ಲೂಮನನ್ನು ಕೊಂದು ಅವನ ಸ್ಥಾನದಲ್ಲಿ ತಾನೇ ಅರಸನಾದನು.",
			"15": "<sup>15</sup> ಶಲ್ಲೂಮನ ಉಳಿದ ಚರಿತ್ರೆ ಹಾಗು ಅವನ ಒಳಸಂಚಿನ ವಿವರ ಇಸ್ರಯೇಲ್ ರಾಜರ ಇತಿಹಾಸ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"16": "<sup>16</sup> ಮೆನಹೇಮನು ತಿರ್ಚದಿಂದ ತಿಪ್ಸಹುವಿಗೆ ಹೋದಾಗ ಅಲ್ಲಿಯವರು ತಮ್ಮ ಊರುಬಾಗಿಲನ್ನು ಅವನಿಗೆ ತೆರೆಯಲಿಲ್ಲ. ಆದುದರಿಂದ ಅವನು ಅದನ್ನೂ ಅದಕ್ಕೆ ಸೇರಿದ ಪ್ರದೇಶವನ್ನೂ ಹಾಳುಮಾಡಿದನು. ಅದರ ನಿವಾಸಿಗಳನ್ನು ಸಂಹರಿಸಿದನು. ಅದರಲ್ಲಿದ್ದ ಎಲ್ಲಾ ಗರ್ಭಿಣಿಯರ ಹೊಟ್ಟೆ ಸೀಳಿಸಿದನು.",
			"17": "<sup>17</sup> ಜುದೇಯದ ಅರಸನಾದ ಅಜರ್ಯನ ಆಳ್ವಿಕೆಯ ಮೂವತ್ತೊಂಬತ್ತನೆಯ ವರ್ಷದಲ್ಲಿ ಗಾದಿಯ ಮಗ ಮೆನಹೇಮನು ಇಸ್ರಯೇಲರ ಅರಸನಾಗಿ ಸಮಾರಿಯದಲ್ಲಿ ಹತ್ತು ವರ್ಷ ಆಳಿದನು.",
			"18": "<sup>18</sup> ಇವನು ಜೀವದಿಂದಿರುವವರೆಗೂ ಇಸ್ರಯೇಲರನ್ನು ಪಾಪಕ್ಕೆ ಪ್ರಚೋದಿಸಿದ ನೆಬಾಟನ ಮಗ ಯಾರೊಬ್ಬಾಮನ ದುರ್ಮಾರ್ಗವನ್ನು ಬಿಡದೆ ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾದನು.",
			"19": "<sup>19</sup> ಅಸ್ಸೀರಿಯಾ ದೇಶದ ಅರಸನಾದ ಪೂಲನೆಂಬವನು ಇಸ್ರಯೇಲ್ ದೇಶಕ್ಕೆ ವಿರುದ್ಧ ದಾಳಿಯಿಟ್ಟಾಗ, ಮೆನಹೇಮನು ಅವನ ಮುಖಾಂತರ ತನ್ನ ಅರಸುತನವನ್ನು ದೃಢಪಡಿಸಿಕೊಳ್ಳುವುದಕ್ಕಾಗಿ, ಅವನಿಗೆ ಮೂವತ್ತನಾಲ್ಕು ಸಾವಿರ ಕಿಲೋಗ್ರಾಂ ಬೆಳ್ಳಿಯನ್ನು ಕೊಟ್ಟನು.",
			"20": "<sup>20</sup> ಅಸ್ಸೀರಿಯದ ಅರಸನಿಗೆ ಆ ಹಣವನ್ನು ಕೊಡುವುದಕ್ಕಾಗಿ ಇಸ್ರಯೇಲರ ಪ್ರತಿಯೊಬ್ಬ ಐಶ್ವರ್ಯವಂತನಿಂದ ಐವತ್ತು ಬೆಳ್ಳಿ ನಾಣ್ಯಗಳನ್ನು ತೆಗೆದುಕೊಂಡನು. ಆ ಹಣ ಸಿಕ್ಕಿದ ನಂತರ ಅಸ್ಸೀರಿಯದ ಅರಸನು ಅಲ್ಲಿ ನಿಲ್ಲದೆ ಹಿಂದಿರುಗಿ ಹೋದನು.",
			"21": "<sup>21</sup> ಮೆನಹೇಮನ ಉಳಿದ ಚರಿತ್ರೆ ಹಾಗು ಕಾರ್ಯಕಲಾಪಗಳು ಇಸ್ರಯೇಲ್ ರಾಜರ ಇತಿಹಾಸ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಲಿಖಿತವಾಗಿವೆ.",
			"22": "<sup>22</sup> ಅವನು ಮೃತನಾಗಿ ಪಿತೃಗಳ ಬಳಿ ಸೇರಿದನು. ಅವನ ಮಗ ಪೆಕಹ್ಯನು ಅರಸನಾದನು.",
			"23": "<sup>23</sup> ಜುದೇಯದ ಅರಸ ಅಜರ್ಯನ ಆಳ್ವಿಕೆಯ ಐವತ್ತೆಂಟನೆಯ ವರ್ಷದಲ್ಲಿ ಮೆನಹೇಮನ ಮಗನಾದ ಪೆಕಹ್ಯ ಎಂಬವನು ಇಸ್ರಯೇಲರ ಅರಸನಾಗಿ ಸಮಾರಿಯದಲ್ಲಿ ಎರಡು ವರ್ಷ ಆಳಿದನು.",
			"24": "<sup>24</sup> ಇವನು ಇಸ್ರಯೇಲರನ್ನು ಪಾಪಕ್ಕೆ ಪ್ರೇರಿಸಿದ ನೆಬಾಟನ ಮಗ ಯಾರೊಬ್ಬಾಮನ ದುರ್ಮಾರ್ಗವನ್ನು ಬಿಡದೆ ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾದನು.",
			"25": "<sup>25</sup> ಇವನ ಸರದಾರನೂ ರೆಮಲ್ಯನ ಮಗನೂ ಆಗಿದ್ದ ಪೆಕಹ್ಯನು ಗಿಲ್ಯಾದಿನ ಐವತ್ತು ಮಂದಿ ಜನರನ್ನು ಕೂಡಿಸಿಕೊಂಡು ಇವನಿಗೆ ವಿರುದ್ಧ ಒಳಸಂಚುಮಾಡಿ ಸಮಾರಿಯದಲ್ಲಿರುವ ಅರಮನೆಯ ಗರ್ಭಗೃಹದಲ್ಲೇ ಇವನನ್ನು ಮತ್ತು ಅರ್ಗೋಬ್, ಅರ್ಯೇ ಎಂಬವರನ್ನು ಕೊಂದು ಇವನಿಗೆ ಬದಲಾಗಿ ತಾನೇ ಅರಸನಾದನು.",
			"26": "<sup>26</sup> ಪೆಕಹ್ಯನ ಉಳಿದ ಚರಿತ್ರೆ ಹಾಗು ಕಾರ್ಯಕಲಾಪಗಳು ಇಸ್ರಯೇಲ್ ರಾಜರ ಇತಿಹಾಸ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"27": "<sup>27</sup> ಜುದೇಯದ ಅರಸ ಅಜರ್ಯನ ಆಳ್ವಿಕೆಯ ಐವತ್ತೆರಡನೆಯ ವರ್ಷದಲ್ಲಿ ರೆಮಲ್ಯನ ಮಗನಾದ ಪೆಕಹ್ಯನು ಇಸ್ರಯೇಲರ ಅರಸನಾಗಿ ಸಮಾರಿಯದಲ್ಲಿ ಇಪ್ಪತ್ತು ವರ್ಷ ಆಳಿದನು.",
			"28": "<sup>28</sup> ಇವನು ಇಸ್ರಯೇಲರನ್ನು ಪಾಪಕ್ಕೆ ಪ್ರೇರಿಸಿದ ನೆಬಾಟನ ಮಗ ಯಾರೊಬ್ಬಾಮನ ದುರ್ಮಾರ್ಗವನ್ನು ಬಿಡದೆ, ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾದನು.",
			"29": "<sup>29</sup> ಇಸ್ರಯೇಲರ ಅರಸನಾದ ಪೆಕಹನ ಕಾಲದಲ್ಲಿ ಅಸ್ಸೀರಿಯಾ ದೇಶದ ಅರಸನಾದ ತಿಗ್ಲತ್ಪಿಲೆಸೆರನೆಂಬವನು ಬಂದು ಇಯ್ಯೋನ್, ಅಬೇಲ್ಬೇತ್ಮಾಕಾ, ಯಾನೋಹ, ಕೆದೆಷ್, ಹಾಚೋರ್ ಮೊದಲಾದ ನಫ್ತಾಲಿಯ ಊರುಗಳನ್ನೂ ಮತ್ತು ಗಿಲ್ಯಾದ್ ಹಾಗು ಗಲಿಲೇಯ ಪ್ರಾಂತ್ಯಗಳನ್ನೂ ಸ್ವಾಧೀನಪಡಿಸಿಕೊಂಡು, ಅವುಗಳ ನಿವಾಸಿಗಳನ್ನು ಅಸ್ಸೀರಿಯಾ ದೇಶಕ್ಕೆ ಸೆರೆಯಾಗಿ ತೆಗೆದುಕೊಂಡು ಹೋದನು.",
			"30": "<sup>30</sup> ಆಗ ಏಲನ ಮಗ ಹೋಶೇಯನು, ರೆಮಲ್ಯನ ಮಗ ಪೆಕಹನಿಗೆ ವಿರುದ್ಧ ಒಳಸಂಚು ಮಾಡಿ, ಇವನನ್ನು ಉಜ್ಜೀಯನ ಮಗ ಯೋತಾಮನ ಆಳ್ವಿಕೆಯ ಇಪ್ಪತ್ತನೆಯ ವರ್ಷದಲ್ಲಿ ಕೊಂದು, ಇವನ ಸ್ಥಾನದಲ್ಲಿ ತಾನೇ ಅರಸನಾದನು.",
			"31": "<sup>31</sup> ಪೆಕಹನ ಉಳಿದ ಚರಿತ್ರೆ ಹಾಗು ಕಾರ್ಯಕಲಾಪಗಳನ್ನು ಇಸ್ರಯೇಲ್ ರಾಜರ ಇತಿಹಾಸ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಓದಬಹುದು.",
			"32": "<sup>32</sup> ಇಸ್ರಯೇಲರ ಅರಸನೂ ರೆಮಲ್ಯನ ಮಗನೂ ಆದ ಪೆಕಹನ ಆಳ್ವಿಕೆಯ ಎರಡನೆಯ ವರ್ಷದಲ್ಲಿ ಜುದೇಯದ ರಾಜನಾದ ಉಜ್ಜೀಯನ ಮಗ ಯೋತಾಮನು ಆಳತೊಡಗಿದನು.",
			"33": "<sup>33</sup> ಇವನು ಪಟ್ಟಕ್ಕೆ ಬಂದಾಗ ಇಪ್ಪತ್ತೈದು ವರ್ಷದವನಾಗಿದ್ದನು. ಇವನು ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಹದಿನಾರು ವರ್ಷ ಆಳಿದನು. ಚಾದೋಕನ ಮಗಳಾದ ಯೆರೂಷಾ ಇವನ ತಾಯಿ.",
			"34": "<sup>34</sup> ಇವನು ತನ್ನ ತಂದೆ ಉಜ್ಜೀಯನಂತೆ ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ಒಳ್ಳೆಯವನಾಗಿದ್ದನು;",
			"35": "<sup>35</sup> ಆದರೆ ಇವನ ಕಾಲದಲ್ಲೂ ಪೂಜಾಸ್ಥಳಗಳು ಇನ್ನು ಹಾಗೆಯೇ ಇದ್ದವು. ಆದುದರಿಂದ ಜನರು ಅವುಗಳಲ್ಲಿ ಬಲಿಗಳನ್ನೂ ಧೂಪಗಳನ್ನೂ ಸಮರ್ಪಿಸುತ್ತಿದ್ದರು. ಸರ್ವೇಶ್ವರನ ಆಲಯಕ್ಕೆ ಮೇಲಣ ಹೆಬ್ಬಾಗಿಲನ್ನು ಇಡಿಸಿದವನು ಇವನೇ.",
			"36": "<sup>36</sup> ಯೋತಾಮನ ಉಳಿದ ಚರಿತ್ರೆ ಹಾಗು ಕಾರ್ಯಕಲಾಪಗಳು ಜುದೇಯದ ರಾಜರ ಇತಿಹಾಸ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"37": "<sup>37</sup> ಸರ್ವೇಶ್ವರ ಆ ಕಾಲದಲ್ಲಿ ಸಿರಿಯಾದ ಅರಸ ರೆಚೀನನನ್ನೂ ರೆಮಲ್ಯನ ಮಗ ಪೆಕಹನನ್ನೂ ಯೆಹೂದ್ಯರಿಗೆ ವಿರುದ್ಧ ಕಳುಹಿಸತೊಡಗಿದರು.",
			"38": "<sup>38</sup> ಯೋತಾಮನು ಸತ್ತು ಪಿತೃಗಳ ಬಳಿ ಸೇರಲು ಅವನ ಶವವನ್ನು ಅವನ ಪೂರ್ವಜನಾದ ದಾವೀದನ ಪಟ್ಟಣದೊಳಗೆ ಕುಟುಂಬ ಸ್ಮಶಾನದಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಅವನ ಸ್ಥಾನದಲ್ಲಿ ಅವನ ಮಗ ಅಹಾಜನು ಅರಸನಾದನು."
		},
		"16": {
			"1": "<sup>1</sup> ರೆಮಲ್ಯನ ಮಗ ಪೆಕಹನ ಆಳ್ವಿಕೆಯ ಹದಿನೇಳನೆಯ ವರ್ಷದಲ್ಲಿ ಜುದೇಯದ ರಾಜನಾದ ಯೋತಾಮನ ಮಗ ಆಹಾಜನು ಆಳತೊಡಗಿದನು.",
			"2": "<sup>2</sup> ಪಟ್ಟಕ್ಕೆ ಬಂದಾಗ ಇವನಿಗೆ ಇಪ್ಪತ್ತು ವರ್ಷ ವಯಸ್ಸು. ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಹದಿನಾರು ವರ್ಷ ಆಳಿದನು. ಇವನು ತನ್ನ ದೇವರಾದ ಸರ್ವೇಶ್ವರಸ್ವಾಮಿಯ ದೃಷ್ಟಿಯಲ್ಲಿ ಒಳ್ಳೆಯವನಾಗಿರಲಿಲ್ಲ. ತನ್ನ ಪೂರ್ವಜ ದಾವೀದನ ಮಾರ್ಗವನ್ನು ಬಿಟ್ಟು",
			"3": "<sup>3</sup> ಇಸ್ರಯೇಲ್ ರಾಜರ ಮಾರ್ಗದಲ್ಲೇ ನಡೆದನು; ಇದಲ್ಲದೆ ಇಸ್ರಯೇಲರ ನಾಡಿನಿಂದ ಸರ್ವೇಶ್ವರ ಓಡಿಸಿಬಿಟ್ಟ ಜನಾಂಗಗಳ ಅಸಹ್ಯ ಆಚಾರಗಳನ್ನು ಅನುಸರಿಸಿ ತನ್ನ ಮಗನನ್ನೆ ಬಲಿಯಗ್ನಿಪರೀಕ್ಷೆಗೆ ಗುರಿಮಾಡಿದನು.",
			"4": "<sup>4</sup> ಪೂಜಾಸ್ಥಳಗಳಲ್ಲೂ ದಿಣ್ಣೆಗಳ ಮೇಲೂ ಎಲ್ಲಾ ಹಸಿರು ಮರಗಳ ಕೆಳಗೂ ಯಜ್ಞಧೂಪಗಳನ್ನು ಸಮರ್ಪಿಸಿದನು.",
			"5": "<sup>5</sup> ಇವನ ಕಾಲದಲ್ಲಿ ಸಿರಿಯಾದವರ ಅರಸನಾದ ರೆಚೀನ್ ಮತ್ತು ಇಸ್ರಯೇಲರ ಅರಸನೂ ರೆಮಲ್ಯನ ಮಗನೂ ಆದ ಪೆಕಹ ಎಂಬವರು ಕೂಡಿಕೊಂಡು ಜೆರುಸಲೇಮಿಗೆ ವಿರುದ್ಧ ದಾಳಿಮಾಡಿ ಆಹಾಜನನ್ನು ಸುತ್ತಿಕೊಂಡರು. ಆದರೆ ಅವನನ್ನು ಜಯಿಸಲು ಅವರಿಂದ ಆಗಲಿಲ್ಲ.",
			"6": "<sup>6</sup> ಇದೇ ಕಾಲದಲ್ಲಿ ಸಿರಿಯಾದವರ ಅರಸನಾದ ರೆಚೀನನು ಏಲತ್ ಪಟ್ಟಣವನ್ನು ಸಿರಿಯಾ ರಾಜ್ಯಕ್ಕೆ ಸೇರಿಸಿಕೊಂಡು ಅದರಲ್ಲಿದ್ದ ಯೆಹೂದ್ಯರನ್ನೆಲ್ಲಾ ಓಡಿಸಿದನು. ಎದೋಮ್ಯರು ಬಂದು ಅಲ್ಲಿ ವಾಸಿಸಿದರು. ಇಂದಿನವರೆಗೂ ಅದರಲ್ಲಿ ಅವರೇ ವಾಸವಾಗಿದ್ದಾರೆ.",
			"7": "<sup>7</sup> ಆಹಾಜನು ಅಸ್ಸೀರಿಯಾ ದೇಶದ ಅರಸನಾದ ತಿಗ್ಲತ್ಪೆಲೆಸರನ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿ, “ನಾನು ನಿಮ್ಮ ಸೇವಕ ಹಾಗು ಕುವರ; ನೀವು ಬಂದು ನನ್ನನ್ನು, ನನಗೆ ವಿರುದ್ಧ ದಂಡೆತ್ತಿ ಬಂದಿರುವ ಸಿರಿಯಾದವರ ಮತ್ತು ಇಸ್ರಯೇಲರ ಅರಸುಗಳ ಕೈಗೆ ಸಿಕ್ಕದಂತೆ, ಕಾಪಾಡಿ,” ಎಂದು ಹೇಳಿಸಿದನು.",
			"8": "<sup>8</sup> ಇದಲ್ಲದೆ ಆಹಾಜನು ಸರ್ವೇಶ್ವರನ ಆಲಯದಲ್ಲೂ ಅರಮನೆಯ ಭಂಡಾರದಲ್ಲೂ ಇದ್ದ ಬೆಳ್ಳಿ ಬಂಗಾರವನ್ನು ತೆಗೆದುಕೊಂಡು ಅಸ್ಸೀರಿಯಾದ ಅರಸನಿಗೆ ಕಪ್ಪವಾಗಿ ಕಳುಹಿಸಿದನು.",
			"9": "<sup>9</sup> ಅವನು ಇವನ ಮಾತಿಗೆ ಒಪ್ಪಿ, ದಮಸ್ಕ ಪಟ್ಟಣಕ್ಕೆ ಮುತ್ತಿಗೆ ಹಾಕಿ, ರೆಚೀನನನ್ನು ಕೊಂದು, ನಿವಾಸಿಗಳನ್ನು ಸೆರೆಹಿಡಿದು ಕೀರ್ ಪ್ರಾಂತ್ಯಕ್ಕೆ ಒಯ್ದನು.",
			"10": "<sup>10</sup> ಅರಸ ಆಹಾಜನು ಅಸ್ಸೀರಿಯದ ಅರಸ ತಿಗ್ಲತ್ಪಿಲೆಸರನ ದರ್ಶನಕ್ಕಾಗಿ ದಮಸ್ಕಕ್ಕೆ ಹೋದನು; ಅಲ್ಲಿದ್ದ ಬಲಿಪೀಠವನ್ನು ಕಂಡು ಅದರ ಒಂದು ಚಿತ್ರವನ್ನೂ ಅದರ ಎಲ್ಲಾ ಅಲಂಕಾರದ ನಕ್ಷೆಯನ್ನೂ ಬರೆಯಿಸಿ ಯಾಜಕ ಊರೀಯನಿಗೆ ಕಳುಹಿಸಿದನು.",
			"11": "<sup>11</sup> ಅರಸ ಆಹಾಜನು ದಮಸ್ಕದಿಂದ ಬರುವಷ್ಟರಲ್ಲಿ ಊರೀಯನು ಅರಸನಿಂದ ತನಗೆ ಬಂದ ಮಾದರಿಯ ಪ್ರಕಾರ ಒಂದು ಪೂಜಾಪೀಠವನ್ನು ಮಾಡಿಸಿಬಿಟ್ಟಿದ್ದನು.",
			"12": "<sup>12</sup> ಅರಸನು ಹಿಂದಿರುಗಿ ಬಂದ ಮೇಲೆ ಆ ಬಲಿಪೀಠವನ್ನು ಕಂಡು ಅದರ ಸಮೀಪಕ್ಕೆ ಹೋಗಿ",
			"13": "<sup>13</sup> ತಾನು ತಂದ ದಹನಬಲಿ ದ್ರವ್ಯ, ಧಾನ್ಯನೈವೇದ್ಯ, ಪಾನದ್ರವ್ಯ ಇವುಗಳನ್ನು ಸಮರ್ಪಿಸಿ ಶಾಂತಿಸಮಾಧಾನ ಬಲಿಗಾಗಿ ವಧಿಸಿದ ಪಶುಗಳ ರಕ್ತವನ್ನು ಅದರ ಮೇಲೆ ಚಿಮುಕಿಸಿದನು.",
			"14": "<sup>14</sup> ಇದಲ್ಲದೆ, ಸರ್ವೇಶ್ವರನ ಆಲಯಕ್ಕೂ ತನ್ನ ಪೂಜಾಪೀಠಕ್ಕೂ ಮಧ್ಯೆ, ಆವರೆಗೂ ಸರ್ವೇಶ್ವರನ ಸೇವೆಗೆ ಉಪಯೋಗಿಸಲಾಗುತ್ತಿದ್ದ ತಾಮ್ರಪೀಠವನ್ನು ಆಲಯದ ಎದುರಿನಿಂದ ತೆಗೆಯಿಸಿ, ತನ್ನ ಪೂಜಾಪೀಠದ ಉತ್ತರ ದಿಕ್ಕಿನಲ್ಲಿ ಇಡಿಸಿದನು.",
			"15": "<sup>15</sup> ಯಾಜಕ ಊರೀಯನಿಗೆ, “ಪ್ರಾತಃಕಾಲದ ದಹನಬಲಿಯನ್ನು, ಸಾಯಂಕಾಲದ ನೈವೇದ್ಯವನ್ನು ಅರಸನು ತರುವ ದಹನಬಲಿ ದ್ರವ್ಯ, ಧಾನ್ಯನೈವೇದ್ಯ, ಇವುಗಳನ್ನು ಮತ್ತು ಜನರು ತರುವ ದಹನಬಲಿ ದ್ರವ್ಯ, ಧಾನ್ಯನೈವೇದ್ಯ, ಪಾನದ್ರವ್ಯ ಇವುಗಳನ್ನು ಈ ಮಹಾ ಪೂಜಾಪೀಠದ ಮೇಲೆ ಸಮರ್ಪಿಸಬೇಕು; ಮತ್ತು ದಹನಬಲಿ ಯಜ್ಞಗಳಿಗಾಗಿ ವಧಿಸುವ ಪಶುಗಳ ರಕ್ತವನ್ನು ಇದರ ಮೇಲೆಯೇ ಚಿಮುಕಿಸಬೇಕು. ದೈವೋತ್ತರಗಳನ್ನು ತಿಳಿದುಕೊಳ್ಳುವುದಕ್ಕಾಗಿ ತಾಮ್ರಪೀಠವನ್ನು ಉಪಯೋಗಿಸುವೆನು,” ಎಂದು ಹೇಳಿದನು.",
			"16": "<sup>16</sup> ಯಾಜಕ ಊರೀಯನು ಅರಸನು ಹೇಳಿದಂತೆಯೇ ಮಾಡಿದನು.",
			"17": "<sup>17</sup> ಅರಸನಾದ ಆಹಾಜನು ಅಸ್ಸೀರಿಯದ ಅರಸನ ನಿಮಿತ್ತ ಪೀಠಗಳ ಪಟ್ಟಿಗಳನ್ನು ಕತ್ತರಿಸಿದನು; ಅವುಗಳ ಮೇಲಿದ್ದ ನೀರಿನ ತೊಟ್ಟಿಗಳನ್ನು ತೆಗೆದಿಟ್ಟನು; ಕಂಚಿನ ಕಡಲೆಂಬ ದೊಡ್ಡ ಪಾತ್ರೆಯನ್ನು ತಾಮ್ರದ ಹೋರಿಗಳ ಮೇಲಿನಿಂದ ಇಳಿಸಿ ಕಲ್ಲುಕಟ್ಟೆಗಳ ಮೇಲೆ ಇಡಿಸಿದನು.",
			"18": "<sup>18</sup> ಸರ್ವೇಶ್ವರನ ಆಲಯದ ಪ್ರಾಕಾರದೊಳಗೆ ಸಬ್ಬತ್ ದಿನದ ಆರಾಧನೆಗಾಗಿ ರಾಜರಿಗಾಗಿ ಕಟ್ಟಲಾಗಿದ್ದ ಮಂಟಪವನ್ನು ಹಾಗು ಅರಮನೆಯಿಂದ ದೇವಸ್ಥಾನಕ್ಕೆ ಹೋಗುವ ಹೊರಗಿನ ಬಾಗಿಲನ್ನೂ ಬದಲಾಯಿಸಿದವನು ಈ ಆಹಾಜನೇ.",
			"19": "<sup>19</sup> ಆಹಾಜನ ಉಳಿದ ಚರಿತ್ರೆ ಹಾಗು ಕೃತ್ಯಗಳು ಜುದೇಯ ರಾಜರ ಇತಿಹಾಸ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಲಿಖಿತವಾಗಿವೆ.",
			"20": "<sup>20</sup> ಅವನು ಸತ್ತು ಪಿತೃಗಳ ಬಳಿ ಸೇರಿದನು. ಅವನ ಶವವನ್ನು ದಾವೀದನಗರದೊಳಗೆ ಅವನ ಕುಟುಂಬ ಸ್ಮಶಾನದಲ್ಲಿ ಸಮಾಧಿ ಮಾಡಿದರು. ಅವನ ಸ್ಥಾನದಲ್ಲಿ ಅವನ ಮಗ ಹಿಜ್ಕೀಯನು ಅರಸನಾದನು."
		},
		"17": {
			"1": "<sup>1</sup> ಜುದೇಯದ ಅರಸನಾದ ಆಹಾಜನ ಆಳ್ವಿಕೆಯ ಹನ್ನೆರಡನೆಯ ವರ್ಷದಲ್ಲಿ ಏಲನ ಮಗ ಹೋಶೇಯನು ಇಸ್ರಯೇಲರ ಅರಸನಾಗಿ ಸಮಾರಿಯದಲ್ಲಿ ಒಂಬತ್ತು ವರ್ಷ ಆಳಿದನು.",
			"2": "<sup>2</sup> ಇವನು ತನ್ನ ಪೂರ್ವಿಕರಾದ ಇಸ್ರಯೇಲಿನ ಅರಸರಷ್ಟು ಕೆಟ್ಟವನಾಗಿರಲಿಲ್ಲ. ಆದರೂ ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾದನು.",
			"3": "<sup>3</sup> ಅಸ್ಸೀರಿಯದ ಅರಸ ಶಲ್ಮನೆಸರನು ಇವನಿಗೆ ವಿರುದ್ಧ ದಂಡೆತ್ತಿ ಬಂದಾಗ ಇವನು ಇವನಿಗೆ ಅಧೀನನಾಗಿ ಕಪ್ಪಕೊಡುವವನಾದನು.",
			"4": "<sup>4</sup> ಕೆಲವು ವರ್ಷಗಳಾದನಂತರ, ಇವನು ಪ್ರತಿ ವರ್ಷವೂ ಕೊಡಬೇಕಾದ ಕಪ್ಪವನ್ನು ಕೊಡದೆಹೋದನು; ಅದು ಮಾತ್ರವಲ್ಲ, ಈಜಿಪ್ಟಿನ ಅರಸನಾದ ಸೋ ಎಂಬವನ ಬಳಿಗೆ ರಾಯಭಾರಿಗಳನ್ನು ಕಳುಹಿಸಿದ್ದನು. ಈ ಕಾರಣ ಅಸ್ಸೀರಿಯದ ಅರಸನು ಇವನನ್ನು ದ್ರೋಹಿಯೆಂದು ತಿಳಿದು, ಬಂಧಿಸಿ ಸೆರೆಯಲ್ಲಿಟ್ಟನು.",
			"5": "<sup>5</sup> ಇದಲ್ಲದೆ, ಈ ಅಸ್ಸೀರಿಯದ ಅರಸನು ದೇಶವನ್ನೆಲ್ಲಾ ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡು ಸಮಾರಿಯಕ್ಕೆ ಬಂದು ಮೂರು ವರ್ಷಗಳವರೆಗೂ ಅದಕ್ಕೆ ಮುತ್ತಿಗೆಹಾಕಿದನು.",
			"6": "<sup>6</sup> ಅದು ಹೋಶೇಯನ ಆಳ್ವಿಕೆಯ ಒಂಬತ್ತನೆಯ ವರ್ಷದಲ್ಲಿ ಅವನ ಸ್ವಾಧೀನವಾಯಿತು. ಅವನು ಎಲ್ಲಾ ಇಸ್ರಯೇಲರನ್ನು ಅಸ್ಸೀರಿಯ ದೇಶಕ್ಕೆ ಒಯ್ದು ಹಲಹು ಎಂಬ ಪ್ರಾಂತ್ಯದಲ್ಲಿ, ಹಾಬೋರ್ ನದಿಯಿರುವ ಗೋಜಾನ್ ಪ್ರಾಂತ್ಯದಲ್ಲಿ ಹಾಗು ಮೇದ್ಯರ ಪಟ್ಟಣಗಳಲ್ಲಿ ಇರಿಸಿದನು.",
			"7": "<sup>7</sup> ಇಸ್ರಯೇಲರ ಈ ದುರ್ಗತಿಗೆ ಅವರ ದುರ್ನಡತೆಯೇ ಕಾರಣ: ಹೇಗೆಂದರೆ, ಅವರು ತಮ್ಮನ್ನು ಈಜಿಪ್ಟಿನ ಅರಸನಾದ ಫರೋಹನ ಕೈಯಿಂದ ಬಿಡಿಸಿ ಅವನ ರಾಜ್ಯದಿಂದ ಹೊರತಂದ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ವಿರುದ್ಧ ಪಾಪಮಾಡಿ ಅನ್ಯದೇವತೆಗಳನ್ನು ಪೂಜಿಸಿದರು.",
			"8": "<sup>8</sup> ತಮ್ಮ ನಾಡಿನಿಂದ ಸರ್ವೇಶ್ವರ ಹೊರಡಿಸಿಬಿಟ್ಟಿದ್ದ ಜನಾಂಗಗಳ ಮತ್ತು ಇಸ್ರಯೇಲ್ ರಾಜರ ದುರಾಚಾರಗಳನ್ನು ಅವರು ಅನುಸರಿಸಿದರು.",
			"9": "<sup>9</sup> ತಮ್ಮ ದೇವರಾದ ಆ ಸರ್ವೇಶ್ವರನಿಗೆ ವಿರೋಧವಾದ ಕೃತ್ಯಗಳನ್ನು ಗುಪ್ತವಾಗಿ ನಡೆಸಿದರು; ಕಾವಲುಗಾರರ ಗೋಪುರವುಳ್ಳ ಚಿಕ್ಕ ಊರು ಮೊದಲುಗೊಂಡು ಕೋಟೆಕೊತ್ತಲುಗಳುಳ್ಳ ಪಟ್ಟಣದವರೆಗಿರುವ ಎಲ್ಲಾ ಊರುಗಳಲ್ಲಿ ತಮಗಾಗಿ ಪೂಜಾಸ್ಥಳಗಳನ್ನು ಏರ್ಪಡಿಸಿಕೊಂಡರು;",
			"10": "<sup>10</sup> ಎಲ್ಲಾ ದಿಣ್ಣೆಗಳ ಮೇಲೆ ಮತ್ತು ಎಲ್ಲಾ ಹಸಿರು ಮರಗಳ ಕೆಳಗೆ ಕಲ್ಲುಗಂಬ, ಅಶೇರುವಿಗ್ರಹಸ್ಥಂಭ ಇವುಗಳನ್ನು ಸ್ಥಾಪಿಸಿದರು.",
			"11": "<sup>11</sup> ತಮ್ಮ ನಾಡಿನಿಂದ ಸರ್ವೇಶ್ವರ ಹೊರಡಿಸಿಬಿಟ್ಟಿದ್ದ ಅನ್ಯಜನಾಂಗಗಳಂತೆ ಎಲ್ಲಾ ಪೂಜಾಸ್ಥಳಗಳಲ್ಲಿ ಧೂಪಾರತಿಯೆತ್ತಿ ತಮ್ಮ ದುಷ್ಕೃತ್ಯಗಳಿಂದ ಅವರನ್ನು ರೇಗಿಸಿದರು;",
			"12": "<sup>12</sup> ಸರ್ವೇಶ್ವರನ ಆಜ್ಞೆಯನ್ನು ಮೀರಿ ವಿಗ್ರಹಗಳನ್ನು ಆರಾಧಿಸಿದರು.",
			"13": "<sup>13</sup> ದರ್ಶಿನಿಗಳೆನಿಸಿಕೊಂಡ ತಮ್ಮ ಪ್ರವಾದಿಗಳ ಮುಖಾಂತರ ಇಸ್ರಯೇಲ ಯೆಹೂದ್ಯರಿಗೆ, “ನೀವು ನಿಮ್ಮ ದುರಾಚಾರವನ್ನು ಬಿಟ್ಟು ನಾನು ನಿಮ್ಮ ಪೂರ್ವಜರಿಗೂ ನನ್ನ ದಾಸರಾದ ಪ್ರವಾದಿಗಳ ಮುಖಾಂತರ ನಿಮಗೂ ಕೊಟ್ಟಂಥ ನನ್ನ ಎಲ್ಲಾ ಆಜ್ಞಾವಿಧಿನಿಯಮಗಳನ್ನು ಕೈಕೊಂಡು ನಡೆಯಿರಿ,” ಎಂದು ಖಂಡಿತವಾಗಿ ಹೇಳಿದರು.",
			"14": "<sup>14</sup> ಆದರೆ ಅವರು, ದೇವರಾದ ಸರ್ವೇಶ್ವರನನ್ನು ನಂಬದೆ, ಅವರ ಆಜ್ಞೆಗಳಿಗೆ ಮಣಿಯದೆಯಿದ್ದ ತಮ್ಮ ಹಿರಿಯರಂತೆ, ಕಿವಿಗೊಡದೆ ಹೋದರು.",
			"15": "<sup>15</sup> ಸರ್ವೇಶ್ವರ ತಮ್ಮ ಪೂರ್ವಜರಿಗೆ ಕೊಟ್ಟ ವಿಧಿನಿಬಂಧನೆಗಳನ್ನೂ ತಮಗೆ ಹೇಳಿಸಿದ ಬುದ್ಧಿವಾಕ್ಯಗಳನ್ನೂ ತಿರಸ್ಕರಿಸಿ, ಮಿಥ್ಯಾದೇವತೆಗಳನ್ನು ಪೂಜಿಸಿ ನಿಸ್ಸಾರ್ಥಕರಾದರು. ಸುತ್ತಣ ಜನಾಂಗಗಳನ್ನು ಅನುಸರಿಸಬಾರದೆಂದು ಸರ್ವೇಶ್ವರ ಆಜ್ಞಾಪಿಸಿದ್ದರೂ ಕೇಳದೆ ಆ ಜನಾಂಗಗಳನ್ನೇ ಅನುಸರಿಸಿದರು.",
			"16": "<sup>16</sup> ತಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಆಜ್ಞೆಗಳನ್ನೆಲ್ಲಾ ಮೀರಿ, ಎರಡು ಎರಕದ ಹೋರಿಕರುಗಳ ಮೂರ್ತಿಗಳನ್ನು ಮಾಡಿಕೊಂಡರು; ಅಶೇರ ವಿಗ್ರಹಸ್ಥಂಭಗಳನ್ನು ನಿಲ್ಲಿಸಿಕೊಂಡರು; ಆಕಾಶಸೈನ್ಯ ಹಾಗು ಬಾಳ್ ದೇವತೆಗಳನ್ನು ಪೂಜಿಸಿದರು;",
			"17": "<sup>17</sup> ತಮ್ಮ ಗಂಡುಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ಬಲಿಯಗ್ನಿಪರೀಕ್ಷೆಗೆ ಗುರಿಮಾಡಿದರು. ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ಕೆಟ್ಟವುಗಳನ್ನು ಮಾಡಿದರು. ಕಣಿಹೇಳುವುದು, ಯಂತ್ರಮಂತ್ರಗಳನ್ನು ಮಾಡುವುದು, ಮೊದಲಾದ ದುಷ್ಕೃತ್ಯಗಳಿಗೆ ತಮ್ಮನ್ನೇ ಮಾರಿಬಿಟ್ಟು ಸರ್ವೇಶ್ವರನಿಗೆ ಕೋಪವನ್ನೆಬ್ಬಿಸಿದರು.",
			"18": "<sup>18</sup> ಈ ಪ್ರಕಾರ ನಡೆದುದರಿಂದ ಸರ್ವೇಶ್ವರ ಅವರ ಮೇಲೆ ಕಡುಕೋಪಗೊಂಡು ಯೆಹೂದಕುಲದವರ ಹೊರತು, ಉಳಿದ ಎಲ್ಲಾ ಕುಲಗಳವರನ್ನು ತಮ್ಮ ಸನ್ನಿಧಿಯಿಂದ ತಳ್ಳಿಬಿಟ್ಟರು.",
			"19": "<sup>19</sup> ಜುದೇಯದವರು ಸಹ ತಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಆಜ್ಞೆಯನ್ನು ಕೈಕೊಳ್ಳದೆ ಇಸ್ರಯೇಲರ ಪದ್ಧತಿಯನ್ನು ಅನುಸರಿಸಿದ್ದರಿಂದ,",
			"20": "<sup>20</sup> ಇಸ್ರಯೇಲ್ ಕುಲಗಳಲ್ಲಿ ಉಳಿದಿದ್ದ ಇವರನ್ನೂ ಹೇಸಿ, ತಮ್ಮ ಸನ್ನಿಧಿಯಿಂದ ತಳ್ಳಿಬಿಟ್ಟು, ಸೂರೆಮಾಡುವವರಿಗೆ ಒಪ್ಪಿಸಿ, ಶಿಕ್ಷಿಸಿದರು.",
			"21": "<sup>21</sup> ಇಸ್ರಯೇಲ್ ರಾಜ್ಯವನ್ನು ದಾವೀದ ಸಂತಾನದವರ ಕೈಯಿಂದ ಕಿತ್ತುಕೊಂಡ ನಂತರ, ಇಸ್ರಯೇಲರು ನೆಬಾಟನ ಮಗ ಯಾರೊಬ್ಬಾಮನನ್ನು ಅರಸನನ್ನಾಗಿ ಮಾಡಿಕೊಂಡರು. ಅವನು ಅವರನ್ನು ಸರ್ವೇಶ್ವರನ ಭಕ್ತಿಮಾರ್ಗದಿಂದ ತಪ್ಪಿಸಿ ಮಹಾಪಾಪಕ್ಕೆ ಒಳಪಡಿಸಿದನು.",
			"22": "<sup>22</sup> ಅವರು ಯಾರೊಬ್ಬಾಮನ ದುರ್ಮಾರ್ಗದಲ್ಲೇ ನಡೆಯುವುದನ್ನು ಬಿಡಲಿಲ್ಲವಾದುದರಿಂದ",
			"23": "<sup>23</sup> ಸರ್ವೇಶ್ವರ, ತಮ್ಮ ದಾಸರಾದ ಎಲ್ಲಾ ಪ್ರವಾದಿಗಳ ಮುಖಾಂತರ ಭವಿಷ್ಯ ನುಡಿದಂತೆ, ಅವರನ್ನು ತಮ್ಮ ಸನ್ನಿಧಿಯಿಂದ ತಳ್ಳಿಬಿಟ್ಟರು. ಅವರು ಸೆರೆಯವರಾಗಿ ತಮ್ಮ ನಾಡಿನಿಂದ ಅಸ್ಸೀರಿಯಾ ದೇಶಕ್ಕೆ ಒಯ್ಯಲ್ಪಟ್ಟು ಇಂದಿನವರೆಗೂ ಅಲ್ಲೇ ಇರುತ್ತಾರೆ.",
			"24": "<sup>24</sup> ಅಸ್ಸೀರಿಯಾದ ಅರಸನು ಸಮಾರಿಯಾ ಪಟ್ಟಣಗಳಲ್ಲಿ ವಾಸವಾಗಿದ್ದ ಇಸ್ರಯೇಲರನ್ನು ಸೆರೆಯಾಗಿ ಒಯ್ದನಂತರ ಅವರಿಗೆ ಬದಲಾಗಿ ಆ ಪಟ್ಟಣಗಳಿಗೆ ಬಾಬೆಲ್, ಕೂತಾ, ಅವ್ವಾ, ಹಮಾತ್, ಸೆಫರ್ವಯಿಮ್ ಎಂಬ ಊರುಗಳವರನ್ನು ಕಳುಹಿಸಿದನು. ಅವರು ಸಮಾರಿಯಾ ನಾಡನ್ನು ಸ್ವತಂತ್ರಿಸಿಕೊಂಡು ಅದರ ಪಟ್ಟಣಗಳಲ್ಲಿ ವಾಸಿಸಿದರು.",
			"25": "<sup>25</sup> ಅವರಿಗೆ ಆರಂಭದಲ್ಲಿ ಸರ್ವೇಶ್ವರನಲ್ಲಿ ಭಯಭಕ್ತಿ ಇರಲಿಲ್ಲ. ಆದ್ದರಿಂದ ಸರ್ವೇಶ್ವರ ಸಿಂಹಗಳನ್ನು ಕಳುಹಿಸಿದರು; ಅವು ಅವರಲ್ಲಿ ಅನೇಕರನ್ನು ಕೊಂದವು.",
			"26": "<sup>26</sup> ಅಸ್ಸೀರಿಯದ ರಾಜನ ಸೇವಕರು ಅವನಿಗೆ, “ನೀವು ಸಮಾರಿಯಾ ಪಟ್ಟಣಗಳಿಗೆ ಕಳುಹಿಸಿದ ಆಯಾ ಊರುಗಳ ಜನರು, ಆ ನಾಡಿನ ದೇವರಿಗೆ ಸೇವೆಮಾಡುವ ರೀತಿಯನ್ನು ಅರಿಯದವರಾಗಿದ್ದಾರೆ. ಆದುದರಿಂದ ಅಲ್ಲಿನ ದೇವರು ಸಿಂಹಗಳನ್ನು ಕಳುಹಿಸಿ ಅವರನ್ನು ಸಂಹರಿಸುತ್ತಿರುತ್ತಾನೆ,” ಎಂದು ತಿಳಿಸಿದರು.",
			"27": "<sup>27</sup> ಆಗ ಅವನು, “ಅಲ್ಲಿಂದ ಇಲ್ಲಿಗೆ ಸೆರೆಯವರಾಗಿ ಬಂದಿರುವ ಯಾಜಕರಲ್ಲೊಬ್ಬನನ್ನು ಅಲ್ಲಿಗೆ ಕಳುಹಿಸಿರಿ. ಅವನು ಹೋಗಿ ಅಲ್ಲಿ ವಾಸವಾಗಿದ್ದು ಆ ನಾಡಿನ ದೇವರಿಗೆ ಸೇವೆಮಾಡುವ ರೀತಿಯನ್ನು ಅವರಿಗೆ ಕಲಿಸಲಿ,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"28": "<sup>28</sup> ಅವರು ಸಮಾರಿಯಾದಿಂದ ಸೆರೆಯವರಾಗಿ ಬಂದಿದ್ದ ಒಬ್ಬ ಯಾಜಕನನ್ನು ಅಲ್ಲಿಗೆ ಕಳುಹಿಸಿದರು. ಇವನು ಬೇತೇಲಿನಲ್ಲಿ ವಾಸವಾಗಿದ್ದು, ಜನರಿಗೆ ಸರ್ವೇಶ್ವರನ ಭಕ್ತಿಯನ್ನು ಬೋಧಿಸಿದನು.",
			"29": "<sup>29</sup> ಆದರೂ ಆ ಜನಾಂಗಗಳವರು ತಮ್ಮ ದೇವತೆವಿಗ್ರಹಗಳನ್ನು ಮಾಡಿಕೊಂಡು ತಾವು ವಾಸಿಸುವ ಊರುಗಳೊಳಗೆ ಸಮಾರಿಯದವರು ಏರ್ಪಡಿಸಿದ್ದ ಪೂಜಾಸ್ಥಳಗಳಲ್ಲಿ ಅವುಗಳನ್ನಿಟ್ಟು ಪೂಜಿಸುತ್ತಿದ್ದರು.",
			"30": "<sup>30</sup> ಬಾಬಿಲೋನಿನವರು ಸುಕ್ಕೋತ್ಬೆನೋತ್, ಕೂತದವರು ನೇರ್ಗಲ್,",
			"31": "<sup>31</sup> ಹಮಾತಿನವರು ಅಷೀಮಾ, ಅವ್ವೀಯರು ನಿಭಜ್ ಮತ್ತು ತರ್ತಕ್ ಎಂಬ ದೇವತೆಗಳನ್ನು ಪೂಜಿಸುತ್ತಿದ್ದರು. ಸೆಫರ್ವಯಿಮಿನವರು ತಮ್ಮ ಕುಲದೇವತೆಗಳಾಗಿರುವ ಅದ್ರೆಮ್ಮೆಲೆಕ್ ಹಾಗು ಅನಮೆಲೆಕ್ ಎಂಬುವುಗಳಿಗಾಗಿ ತಮ್ಮ ಮಕ್ಕಳನ್ನು ಬಲಿಯಗ್ನಿ ಪರೀಕ್ಷೆಗೆ ಗುರಿಮಾಡುತ್ತಿದ್ದರು.",
			"32": "<sup>32</sup> ಅವರು ಸರ್ವೇಶ್ವರನ ಭಕ್ತರಾಗಿದ್ದರೂ ತಮ್ಮಲ್ಲೆ ಯಾರ್ಯಾರನ್ನೋ ಉನ್ನತ ಪೂಜಾಸ್ಥಳಗಳ ಯಾಜಕರನ್ನಾಗಿ ನೇಮಿಸಿಕೊಂಡು, ಅವರ ಮುಖಾಂತರ ಪೂಜಾಸ್ಥಳದ ಗುಡಿಗಳಲ್ಲಿ ಆರಾಧನೆ ನಡೆಸುತ್ತಿದ್ದರು.",
			"33": "<sup>33</sup> ಸರ್ವೇಶ್ವರನ ಭಕ್ತರಾಗಿದ್ದ ಅವರು ತಾವು ಬಿಟ್ಟುಬಂದ ದೇಶಗಳ ಪದ್ಧತಿಯ ಪ್ರಕಾರ ತಮ್ಮ ಕುಲದೇವತೆಗಳ ಆರಾಧನೆಯನ್ನೂ ಮಾಡುತ್ತಿದ್ದರು.",
			"34": "<sup>34</sup> ಆ ಕಾಲದ ಪದ್ಧತಿಯನ್ನು ಇಂದಿಗೂ ಮುಂದುವರಿಸುತ್ತಿದ್ದಾರೆ. ಸರ್ವೇಶ್ವರನಲ್ಲಿ ಭಯಭಕ್ತಿ ಇಸ್ರಯೇಲರಿಗಿಲ್ಲ; ಅವರಿಂದಲೇ ಇಸ್ರಯೇಲನೆಂಬ ಹೆಸರು ಹೊಂದಿದ ಯಕೋಬನ ಸಂತಾನದವರಿಗೆ ಕೊಡಲಾದ ಆಜ್ಞಾನಿಯಮವಿಧಿ ಧರ್ಮಶಾಸ್ತ್ರಗಳನ್ನು ಅವರು ಅನುಸರಿಸಲಿಲ್ಲ.",
			"35": "<sup>35</sup> ಇಸ್ರಯೇಲರಿಗೆ ಸರ್ವೇಶ್ವರ, “ನೀವು ಅನ್ಯದೇವತೆಗಳನ್ನು ಆರಾಧಿಸಬಾರದು, ಅವುಗಳಿಗೆ ಕೈಮುಗಿಯಲೂಬಾರದು. ಸೇವೆಮಾಡಬಾರದು, ಬಲಿ ಅರ್ಪಿಸಲೂಬಾರದು.",
			"36": "<sup>36</sup> ನಿಮ್ಮನ್ನು ಮಹಾಶಕ್ತಿ ಹಾಗು ಭುಜಪರಾಕ್ರಮಗಳ ಮೂಲಕ ಈಜಿಪ್ಟ್ ದೇಶದಿಂದ ಕರೆದುಕೊಂಡು ಬಂದ ಸರ್ವೇಶ್ವರನಾದ ನನ್ನಲ್ಲೇ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿ ನನಗೊಬ್ಬನಿಗೇ ಕೈಮುಗಿದು ಬಲಿಯರ್ಪಿಸಬೇಕು.",
			"37": "<sup>37</sup> ನಾನು ನಿಮಗೆ ಬರೆಯಿಸಿಕೊಟ್ಟ ಆಜ್ಞಾನಿಯಮವಿಧಿ ಧರ್ಮಶಾಸ್ತ್ರಗಳನ್ನು ತಪ್ಪದೆ ಕೈಗೊಳ್ಳಬೇಕು.",
			"38": "<sup>38</sup> ಅನ್ಯದೇವತೆಗಳನ್ನು ಪೂಜಿಸಬಾರದು; ನನ್ನ ನಿಬಂಧನೆಯನ್ನು ಮರೆತು ಅನ್ಯದೇವತೆಗಳನ್ನು ಪೂಜಿಸಬಾರದು.",
			"39": "<sup>39</sup> ನಿಮ್ಮ ದೇವರಾಗಿರುವ ಸರ್ವೇಶ್ವರನಾದ ನನ್ನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿರಬೇಕು; ಆಗ ನಿಮ್ಮನ್ನು ಯಾವ ಶತ್ರುಗಳ ಕೈಗೂ ಸಿಕ್ಕದಂತೆ ತಪ್ಪಿಸುವೆನು,” ಎಂದು ಹೇಳಿ ಅವರೊಡನೆ ಒಡಂಬಡಿಕೆ ಮಾಡಿಕೊಂಡರು.",
			"40": "<sup>40</sup> ಆದರೆ ಅವರು ಅದನ್ನು ಲಕ್ಷಿಸದೆ ತಮ್ಮ ಹಿಂದಿನ ಪದ್ಧತಿಗಳನ್ನೇ ಅನುಸರಿಸಿದರು.",
			"41": "<sup>41</sup> ಆ ಜನಾಂಗಗಳವರು ಸರ್ವೇಶ್ವರನ ಭಕ್ತರಾಗಿದ್ದರೂ ತಮ್ಮ ವಿಗ್ರಹಗಳನ್ನು ಆರಾಧಿಸುತ್ತಿದ್ದರು. ಅವರ ಸಂತಾನದವರು ಇಂದಿನವರೆಗೂ ಹಾಗೆಯೇ ಮಾಡುತ್ತಿದ್ದಾರೆ."
		},
		"18": {
			"1": "<sup>1</sup> ಇಸ್ರಯೇಲರ ಅರಸನೂ ಏಲನ ಮಗನೂ ಆದ ಹೋಶೇಯನ ಆಳ್ವಿಕೆಯ ಮೂರನೆಯ ವರ್ಷದಲ್ಲಿ ಜುದೇಯ ರಾಜನಾದ ಆಹಾಜನ ಮಗ ಹಿಜ್ಕೀಯನು ಆಳತೊಡಗಿದನು.",
			"2": "<sup>2</sup> ಇವನು ಪಟ್ಟಕ್ಕೆ ಬಂದಾಗ ಇಪ್ಪತ್ತೈದು ವರ್ಷದವನು. ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಇಪ್ಪತ್ತೊಂಬತ್ತು ವರ್ಷ ಆಳಿದನು. ಜೆಕರ್ಯನ ಮಗಳಾದ ಅಬೀ ಎಂಬಾಕೆ ಇವನ ತಾಯಿ.",
			"3": "<sup>3</sup> ಇವನು ಎಲ್ಲಾ ವಿಷಯಗಳಲ್ಲೂ ತನ್ನ ಪೂರ್ವಜ ದಾವೀದನಂತೆ ಸರ್ವೇಶ್ವರನ ಚಿತ್ತಾನುಸಾರ ನಡೆದನು.",
			"4": "<sup>4</sup> ಪೂಜಾಸ್ಥಳಗಳನ್ನು ಹಾಳುಮಾಡಿದನು. ಕಲ್ಲುಗಂಬಗಳನ್ನು ಒಡೆದುಹಾಕಿದನು. ಅಶೇರ ವಿಗ್ರಹಸ್ಥಂಭಗಳನ್ನು ಕೆಡವಿಬಿಟ್ಟನು. ಮೋಶೆ ಮಾಡಿಸಿದ ತಾಮ್ರಸರ್ಪವನ್ನು ಚೂರುಚೂರು ಮಾಡಿದನು. ಇಸ್ರಯೇಲರು ಅದಕ್ಕೆ ಆವರೆಗೂ ಧೂಪಸುಡುತ್ತಿದ್ದರು. ಅದಕ್ಕೆ ‘ನೆಹುಷ್ಟಾನ್’ ಎಂಬ ಹೆಸರಿತ್ತು.",
			"5": "<sup>5</sup> ಇವನು ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರಸ್ವಾಮಿಯಲ್ಲೆ ಭರವಸೆಯಿಟ್ಟಿದ್ದನು. ಯೆಹೂದ್ಯರಲ್ಲಿ ಇವನಿಗೆ ಸಮಾನನಾದ ಅರಸನು ಹಿಂದೆಯೂ ಅನಂತರವೂ ಇರಲಿಲ್ಲ.",
			"6": "<sup>6</sup> ಇದಲ್ಲದೆ, ಇವನು ಸರ್ವೇಶ್ವರನನ್ನೇ ಹೊಂದಿಕೊಂಡು ಅವರನ್ನು ಬಿಡದೆ ಅನುಸರಿಸುತ್ತಾ, ಅವರು ಮೋಶೆಯ ಮುಖಾಂತರ ಅನುಗ್ರಹಿಸಿದ ಆಜ್ಞೆಗಳನ್ನು ಕೈಗೊಂಡನು.",
			"7": "<sup>7</sup> ಸರ್ವೇಶ್ವರ ಇವನ ಸಂಗಡ ಇದ್ದುದರಿಂದ ಎಲ್ಲಿಗೆ ಹೋದರೂ ಕೃತಾರ್ಥನಾಗಿ ಬರುತ್ತಿದ್ದನು. ಇವನು ಅಸ್ಸೀರಿಯದ ಅರಸನಿಗೆ ವಿರುದ್ಧ ದಂಗೆಯೆದ್ದು ಸ್ವತಂತ್ರನಾದನು.",
			"8": "<sup>8</sup> ಗಾಜಪ್ರಾಂತ್ಯದವರೆಗೆ ವಾಸವಾಗಿದ್ದ ಫಿಲಿಷ್ಟಿಯರನ್ನು ಸೋಲಿಸಿ ಕಾವಲುಗಾರರ ಗೋಪುರವಿದ್ದ ಚಿಕ್ಕ ಹಳ್ಳಿ ಮೊದಲುಗೊಂಡು ಕೋಟೆಕೊತ್ತಲುಗಳುಳ್ಳ ಮಹಾನಗರಗಳವರೆಗೂ ಎಲ್ಲಾ ಊರುಗಳನ್ನು ಹಾಳುಮಾಡಿದನು.",
			"9": "<sup>9</sup> ಇಸ್ರಯೇಲರ ಅರಸನೂ ಏಲನ ಮಗನೂ ಆದ ಹೋಶೇಯನ ಆಳ್ವಿಕೆಯ ಏಳನೆಯ ವರ್ಷದಲ್ಲಿ ಅಂದರೆ ಹಿಜ್ಕೀಯನ ಆಳ್ವಿಕೆಯ ನಾಲ್ಕನೆಯ ವರ್ಷದಲ್ಲಿ ಅಸ್ಸೀರಿಯದ ಅರಸನಾದ ಶಲ್ಮನೆಸೆರನು ಸಮಾರಿಯಕ್ಕೆ ವಿರುದ್ಧ ದಂಡೆತ್ತಿಬಂದಿದ್ದನು.",
			"10": "<sup>10</sup> ಮೂರು ವರ್ಷಗಳವರೆಗೂ ಅದಕ್ಕೆ ಮುತ್ತಿಗೆ ಹಾಕಿದ್ದನು. ಅದು ಅವನಿಗೆ ಇಸ್ರಯೇಲರ ಅರಸನಾದ ಹೋಶೇಯನ ಆಳ್ವಿಕೆಯ ಆರನೆಯ ವರ್ಷದಲ್ಲಿ ಸ್ವಾಧೀನವಾಯಿತು.",
			"11": "<sup>11</sup> ಅವನು ಇಸ್ರಯೇಲರನ್ನು ಸೆರೆಹಿಡಿದು ಹಲಹು, ಹಾಬೋರ್ ನದಿಯಿರುವ ಗೋಜಾನ್ ಪ್ರಾಂತ್ಯ, ಮೇದ್ಯರ ಪಟ್ಟಣಗಳು ಇವುಗಳಿಗೆ ಕಳುಹಿಸಿದನು.",
			"12": "<sup>12</sup> ಇಸ್ರಯೇಲರು ತಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಮಾತನ್ನು ಕೇಳದೆಹೋದದ್ದೂ ಅವರ ನಿಬಂಧನೆಯನ್ನು ಉಲ್ಲಂಘಿಸಿದ್ದೂ ಅವರ ದಾಸ ಮೋಶೆಯ ಆಜ್ಞೆಗಳಿಗೆ ಅವಿಧೇಯರಾಗಿ ನಡೆದದ್ದೂ ಇದಕ್ಕೆ ಕಾರಣಗಳಾಗಿದ್ದವು.",
			"13": "<sup>13</sup> ಅರಸ ಹಿಜ್ಕೀಯನ ಆಳ್ವಿಕೆಯ ಹದಿನಾಲ್ಕನೆಯ ವರ್ಷದಲ್ಲಿ, ಅಸ್ಸೀರಿಯದ ಅರಸನಾದ ಸನ್ಹೇರೀಬನು ಬಂದು ಜುದೇಯ ಪ್ರಾಂತ್ಯದ ಸುಭದ್ರ ಪಟ್ಟಣಗಳನ್ನೆಲ್ಲಾ ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡನು.",
			"14": "<sup>14</sup> ಆಗ ಜುದೇಯದ ಅರಸನಾದ ಹಿಜ್ಕೀಯನು ಲಾಕೀಷಿನಲ್ಲಿದ್ದ ಅಸ್ಸೀರಿಯದ ಅರಸನ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿ, “ನಾನು ಅಪರಾಧಿ; ತಾವು ನನ್ನನ್ನು ಬಿಟ್ಟುಹೋಗಬೇಕು; ತಾವು ನನಗೆ ವಿಧಿಸುವಷ್ಟನ್ನೂ ಕೊಡುತ್ತೇನೆ,” ಎಂದು ಹೇಳಿ ಕಳುಹಿಸಿದನು. ಅಸ್ಸೀರಿಯದ ಅರಸನು ಹತ್ತು ಸಾವಿರ ಕಿಲೋಗ್ರಾಂ ಬೆಳ್ಳಿಯನ್ನೂ ಒಂದು ಸಾವಿರ ಕಿಲೋಗ್ರಾಂ ಬಂಗಾರವನ್ನೂ ಕೊಡಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"15": "<sup>15</sup> ಆಗ ಹಿಜ್ಕೀಯನು ಸರ್ವೇಶ್ವರನ ಆಲಯದಲ್ಲಿಯೂ ಅರಮನೆಯ ಭಂಡಾರದಲ್ಲಿಯೂ ಸಿಕ್ಕಿದ ಬೆಳ್ಳಿಯನ್ನೂ",
			"16": "<sup>16</sup> ತಾನು ಸರ್ವೇಶ್ವರನ ಆಲಯದ ಕದಗಳಿಗೂ ಕಂಬಗಳಿಗೂ ಹೊದಿಸಿದ್ದ ಬಂಗಾರದ ತಗಡನ್ನೂ ತೆಗೆದುಕೊಂಡು ಅಸ್ಸೀರಿಯದ ಅರಸನಿಗೆ ಕಳುಹಿಸಿದನು.",
			"17": "<sup>17</sup> ಅಸ್ಸೀರಿಯದ ಅರಸನು ಲಾಕೀಷಿನಿಂದ ಮಹಾ ಸೈನ್ಯಸಹಿತರಾದ ತರ್ತಾನ್, ರಬ್ಸಾರೀಸ್, ರಬ್ಷಾಕೆ ಎಂಬವರನ್ನು ಜೆರುಸಲೇಮಿನಲ್ಲಿದ್ದ ಹಿಜ್ಕೀಯನ ಬಳಿಗೆ ಕಳುಹಿಸಿದನು. ಇವರು ದಿಣ್ಣೆ ಹತ್ತಿ ಜೆರುಸಲೇಮಿಗೆ ಬಂದು, ಅಗಸರ ಹೊಲದ ಮೇಲೆ ಹಾದುಹೋಗುವ ರಾಜಮಾರ್ಗದ ಹತ್ತಿರವಿರುವ, ಕೆರೆಯ ಕಾಲುವೆಯ ಬಳಿ, ಪಾಳೆಯ ಮಾಡಿಕೊಂಡು ಅರಸನನ್ನು ಕೂಗಿ ಕರೆದರು.",
			"18": "<sup>18</sup> ಆಗ ಹಿಲ್ಕೀಯನ ಮಗನೂ ರಾಜಗೃಹಾಧಿಪತಿಯೂ ಆದ ಎಲ್ಯಾಕೀಮ್, ಕಾರ್ಯದರ್ಶಿಯಾದ ಶೆಬ್ನ ಮತ್ತು ಆಸಾಫನ ಮಗನೂ ಮಂತ್ರಿಯೂ ಆಗಿದ್ದ ಯೋವ ಎಂಬವರು ಅಲ್ಲಿಗೆ ಬಂದರು.",
			"19": "<sup>19</sup> ರಬ್ಷಾಕೆಯು ಅವರಿಗೆ, “ನೀವು ಹೋಗಿ ಅಸ್ಸೀರಿಯದ ಮಹಾರಾಜನ ಈ ಮಾತುಗಳನ್ನು ಹಿಜ್ಕೀಯನಿಗೆ ತಿಳಿಸಿರಿ: ‘ನಿನ್ನ ಭರವಸೆಗೆ ಆಧಾರವಾದರೂ ಏನು?",
			"20": "<sup>20</sup> ಬರಿ ಬಾಯಿಮಾತೊಂದರಿಂದ ಯುದ್ಧಕ್ಕೆ ಬೇಕಾದ ವಿವೇಕ ಹಾಗು ಶಕ್ತಿ ಉಂಟೆಂದು ಅರಿತುಕೊಂಡಿರುವೆ. ನೀನು ಯಾರನ್ನು ನಂಬಿಕೊಂಡು ನನಗೆ ವಿರೋಧವಾಗಿ ಎದ್ದಿರುವೆ?",
			"21": "<sup>21</sup> ಮುರಿದ ಜೊಂಡಿಗೆ ಸಮಾನವಾದ ಈಜಿಪ್ಟಿನ ಮೇಲೆ ನಿನಗೆ ಭರವಸೆಯಿದೆಯಷ್ಟೆ. ಅಂಥ ಜೊಂಡನ್ನು ಊರಿಕೊಳ್ಳುವವನ ಕೈಯನ್ನೇ ಚುಚ್ಚಿ ತಿವಿಯುವ ಕೋಲು ಅದು. ಈಜಿಪ್ಟಿನ ರಾಜ ಫರೋಹನಲ್ಲಿ ಭರವಸೆಯಿಟ್ಟವರಿಗೆ ಆಗುವ ಗತಿ ಇದೇ.’",
			"22": "<sup>22</sup> “ಬಹುಶಃ, ನೀವು ‘ನಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನನ್ನೇ ನಂಬಿಕೊಂಡಿದ್ದೇವೆ,’ ಎಂದು ಹೇಳಬಹುದು. ಜೆರುಸಲೇಮಿನ ಬಲಿಪೀಠದ ಮುಂದೆಯೇ ಆರಾಧನೆಮಾಡಬೇಕು ಎಂದು ಯೆಹೂದ್ಯರಿಗೂ ಮತ್ತು ಜೆರುಸಲೇಮಿನವರಿಗೂ ಹಿಜ್ಕೀಯನು ಆಜ್ಞಾಪಿಸಿ ಆ ಸರ್ವೇಶ್ವರ ಸ್ವಾಮಿಯ ಪೂಜಾಸ್ಥಳಗಳನ್ನೂ ಬೇರೆ ಎಲ್ಲಾ ಬಲಿಪೀಠಗಳನ್ನೂ ಹಾಳುಮಾಡಿದನಲ್ಲವೆ?",
			"23": "<sup>23</sup> ಆದುದರಿಂದ ಬಾ, ಬಂದು ನನ್ನ ಒಡೆಯರಾದ ಅಸ್ಸೀರಿಯದ ಅರಸನೊಂದಿಗೆ ಒಂದು ಒಪ್ಪಂದ ಮಾಡಿಕೊ. ಅವನು ನಿನಗೆ ಎರಡು ಸಾವಿರ ಕುದುರೆಗಳನ್ನು ಕೊಡುವನು; ಅವುಗಳ ಮೇಲೆ ಕೂರಿಸುವಷ್ಟು ಮಂದಿ ಸವಾರರು ನಿನಗಿದ್ದಾರೋ?",
			"24": "<sup>24</sup> ಇದು ಕೂಡ ನಿನಗೆ ಅಸಾಧ್ಯವಾದರೆ ನನ್ನ ಒಡೆಯನ ಸೇನಾಪತಿಗಳಲ್ಲಿ ಅತ್ಯಲ್ಪನನ್ನಾದರೂ ಸೋಲಿಸುವುದು ಹೇಗೆ ತಾನೆ ಸಾಧ್ಯ? ರಥಾಶ್ವಬಲಗಳಿಗಾಗಿ ಈಜಿಪ್ಟಿನವರನ್ನು ನೀನು ನಂಬಿಕೊಂಡಿರುವಂತೆ ಕಾಣುತ್ತದೆ.",
			"25": "<sup>25</sup> ಈ ನಾಡನ್ನು, ಹಾಳುಮಾಡುವುದಕ್ಕೆ ಸರ್ವೇಶ್ವರಸ್ವಾಮಿಯ ಒಪ್ಪಿಗೆಯಿಲ್ಲದೆ ಇಲ್ಲಿಗೆ ನಾನು ಬಂದೆನೆಂದು ನೆನಸುತ್ತೀಯೋ? ‘ಇಲ್ಲಿಗೆ ಬಂದು ಇದನ್ನು ಹಾಳುಮಾಡಿಬಿಡು’ ಎಂದು ಆ ಸರ್ವೇಶ್ವರಸ್ವಾಮಿಯೇ ನನಗೆ ಆಜ್ಞೆ ಮಾಡಿದ್ದಾರೆ, ಎಂಬ ಈ ಮಾತುಗಳು ಅರಸನವು,” ಎಂದನು.",
			"26": "<sup>26</sup> ಆಗ ಹಿಲ್ಕೀಯನ ಮಗ ಎಲ್ಯಾಕೀಮ್, ಶೆಬ್ನ ಮತ್ತು ಯೋವ ಎಂಬವರು ರಬ್ಷಾಕೆಗೆ, “ದಯವಿಟ್ಟು ನಿಮ್ಮ ಸೇವಕರಾದ ನಮ್ಮೊಡನೆ ಅರಾಮಾಯಿಕ್ ಭಾಷೆಯಲ್ಲಿ ಮಾತನಾಡಿ. ಅದು ನಮಗೆ ಅರ್ಥವಾಗುತ್ತದೆ. ಆದರೆ ಹಿಬ್ರೂ ಭಾಷೆಯಲ್ಲಿ ಮಾತನಾಡಬೇಡಿ. ಅದು ಪೌಳಿಗೋಡೆಯ ಮೇಲಿರುವವರಿಗೂ ಅರ್ಥವಾಗುತ್ತದೆ,” ಎಂದರು.",
			"27": "<sup>27</sup> ಅದಕ್ಕೆ ರಬ್ಷಾಕೆಯು, “ನನ್ನ ಯಜಮಾನರು, ನಿಮ್ಮ ಸಂಗಡವಾಗಲಿ ಮತ್ತು ನಿಮ್ಮ ಒಡೆಯರ ಸಂಗಡವಾಗಲಿ ಮಾತನಾಡಬೇಕೆಂದು ನನ್ನನ್ನು ಕಳುಹಿಸಲಿಲ್ಲ. ಈ ಗೋಡೆಯ ಮೇಲೆ ಕುಳಿತಿರುವ ಜನರ ಸಂಗಡ ಮಾತನಾಡುವುದಕ್ಕಾಗಿಯೇ ಕಳುಹಿಸಿದ್ದಾರೆ. ಈ ಜನರು ನಿಮ್ಮೊಡನೆ ಇದ್ದರೆ ನಿಮ್ಮ ಹಾಗೆ ಇವರು ಕೂಡ ತಮ್ಮ ಸ್ವಂತ ಮಲಮೂತ್ರವನ್ನು ತಿಂದು ಕುಡಿಯಬೇಕಾಗುತ್ತದೆ,” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"28": "<sup>28</sup> ಬಳಿಕ ಆ ದಳಪತಿಯು ಆ ಗೋಡೆಯ ಮೇಲೆ ನಿಂತಿದ್ದವರನ್ನು ಉದ್ದೇಶಿಸಿ ಹಿಬ್ರೂ ಭಾಷೆಯಲ್ಲೇ, “ನೀವು ಅಸ್ಸೀರಿಯದ ರಾಜಾಧಿರಾಜನ ಮಾತನ್ನು ಕೇಳಿರಿ;",
			"29": "<sup>29</sup> ಹಿಜ್ಕೀಯನ ಮಾತನ್ನು ಕೇಳಿ ಮೋಸಹೋಗಬೇಡಿ; ನಿಮ್ಮನ್ನು ಬಿಡಿಸಲು ಅವನಿಂದಾಗದು.",
			"30": "<sup>30</sup> ಹಿಜ್ಕೀಯನು ನಿಮಗೆ, ‘ಸರ್ವೇಶ್ವರ ಸ್ವಾಮಿಯಲ್ಲೇ ನಂಬಿಕೆಯಿಡಿ; ಅವರು ನಿಮ್ಮನ್ನು ಹೇಗಾದರೂ ಮಾಡಿ ರಕ್ಷಿಸುವರು; ಈ ಪಟ್ಟಣವು ಅಸ್ಸೀರಿಯದ ರಾಜನ ವಶವಾಗುವುದಿಲ್ಲ,’ ಎಂಬುದಾಗಿ ಹೇಳಿದರೆ ಅವನಿಗೆ ಕಿವಿಗೊಡಬೇಡಿ, ಒಪ್ಪಿಕೊಳ್ಳಬೇಡಿ.",
			"31": "<sup>31</sup> ಅಸ್ಸೀರಿಯದ ಅರಸನಾದ ನನ್ನ ಮಾತನ್ನು ಕೇಳಿ; ನನ್ನೊಡನೆ ಒಪ್ಪಂದಮಾಡಿಕೊಂಡು ನನ್ನ ಆಶ್ರಯವನ್ನು ಸೇರಿರಿ, ಆಗ ನಿಮ್ಮಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬನು ತನ್ನ ತನ್ನ ಅಂಜೂರಮರದ ಹಾಗು ದ್ರಾಕ್ಷಾಲತೆಯ ಹಣ್ಣುಗಳನ್ನು ತಿನ್ನುವನು; ತನ್ನ ಬಾವಿಯ ನೀರನ್ನೇ ಕುಡಿಯುವನು.",
			"32": "<sup>32</sup> ಸ್ವಲ್ಪಕಾಲವಾದ ನಂತರ ನಾನು ಬಂದು ನಿಮ್ಮನ್ನು ಇನ್ನೊಂದು ನಾಡಿಗೆ ಕರೆದುಕೊಂಡು ಹೋಗುವೆನು. ಅಲ್ಲಿ ಧಾನ್ಯ, ದ್ರಾಕ್ಷಾರಸ, ಆಹಾರ, ದ್ರಾಕ್ಷೀತೋಟ, ಎಣ್ಣೇಮರ, ಜೇನುತುಪ್ಪ ಸಮೃದ್ಧಿಯಾಗಿರುವುವು. ನಿಮ್ಮ ನಾಡಿಗೆ ಸರಿಸಮಾನವಾಗಿರುವುವು; ನೀವು ಸಾಯುವುದಿಲ್ಲ, ಬದುಕುವಿರಿ; ‘ಸರ್ವೇಶ್ವರ ನಮ್ಮನ್ನು ರಕ್ಷಿಸುವರು’ ಎಂಬ ನಂಬಿಕೆಯನ್ನು ಹಿಜ್ಕೀಯನು ನಿಮ್ಮಲ್ಲಿ ಮೂಡಿಸದಂತೆ ಎಚ್ಚರಿಕೆಯಾಗಿರಿ.",
			"33": "<sup>33</sup> ಯಾವ ರಾಷ್ಟ್ರದ ದೇವರುಗಳು ತಾನೇ ಅಸ್ಸೀರಿಯದ ಅರಸನ ಕೈಯಿಂದ ತಮ್ಮ ನಾಡನ್ನು ಬಿಡಿಸಿಕೊಂಡಿದ್ದಾರೆ?",
			"34": "<sup>34</sup> ಹಮಾತ್, ಅರ್ಪಾದ್, ಸೆಫರ್ವಯಿಮ್, ಹೇನ, ಇವ್ವಾ, ಎಂಬ ಊರುಗಳ ದೇವರುಗಳು ಏನಾದರು? ಅವರು ಸಮಾರಿಯವನ್ನು ನನ್ನ ಕೈಯಿಂದ ತಪ್ಪಿಸಿ ಬಿಡಿಸಿಕೊಂಡರೋ?",
			"35": "<sup>35</sup> ಯಾವ ರಾಷ್ಟ್ರದ ದೇವರುಗಳೂ ತಮ್ಮ ನಾಡನ್ನು ನನ್ನ ಕೈಯಿಂದ ಬಿಡಿಸಲಾಗದೆ ಹೋದಮೇಲೆ ಜೆರುಸಲೇಮನ್ನು ನನ್ನ ಕೈಗೆ ಸಿಕ್ಕದಂತೆ ತಪ್ಪಿಸಿ ಕಾಪಾಡಲು ಸರ್ವೇಶ್ವರನಿಗೆ ಸಾಧ್ಯವೋ?”, ಎಂದು ಕೇಳಿದನು.",
			"36": "<sup>36</sup> ಆ ಸೇನಾಧಿಪತಿಗೆ ಯಾವ ಉತ್ತರವನ್ನು ಕೊಡಬಾರದೆಂದು ಅರಸನು ತನ್ನ ಪ್ರಜೆಗಳಿಗೆ ಆಜ್ಞಾಪಿಸಿದ್ದರಿಂದ ಅವರು ಏನನ್ನೂ ಹೇಳದೆ ಸುಮ್ಮನಿದ್ದರು.",
			"37": "<sup>37</sup> ಬಳಿಕ ಹಿಲ್ಕೀಯನ ಮಗನೂ ರಾಜಗೃಹಾಧಿಪತಿಯೂ ಅಗಿದ್ದ ಎಲ್ಯಾಕೀಮ್, ಕಾರ್ಯದರ್ಶಿ ಶೆಬ್ನ, ಆಸಾಫನ ಮಗನೂ ಮಂತ್ರಿಯೂ ಆಗಿದ್ದ ಯೋವ, ಎಂಬವರು ಸಿಟ್ಟಿನಿಂದ ತಮ್ಮ ಬಟ್ಟೆಗಳನ್ನು ಹರಿದುಕೊಂಡು ಅರಸ ಹಿಜ್ಕೀಯನ ಬಳಿಗೆ ಬಂದು ಅವನಿಗೆ ರಬ್ಷಾಕೆಯ ಮಾತುಗಳನ್ನು ತಿಳಿಸಿದರು."
		},
		"19": {
			"1": "<sup>1</sup> ಈ ವರದಿಯನ್ನು ಕೇಳಿದ ಹಿಜ್ಕೀಯನು ಕೋಪದಿಂದ ತನ್ನ ಬಟ್ಟೆಯನ್ನು ಹರಿದುಕೊಂಡು, ಗೋಣಿತಟ್ಟನ್ನು ಕಟ್ಟಿಕೊಂಡು ಸರ್ವೇಶ್ವರನ ಆಲಯಕ್ಕೆ ಹೋದನು.",
			"2": "<sup>2</sup> ಇದಲ್ಲದೆ, ಅವನು ರಾಜಗೃಹಾಧಿಪತಿಯಾದ ಎಲ್ಯಾಕೀಮ್, ಕಾರ್ಯದರ್ಶಿ ಶೆಬ್ನ, ಹಿರಿಯ ಯಾಜಕರು, ಇವರನ್ನು ಕರೆಯಿಸಿ, “ನೀವು ಗೋಣಿತಟ್ಟನ್ನು ಕಟ್ಟಿಕೊಂಡು, ಆಮೋಚನ ಮಗನೂ ಪ್ರವಾದಿಯೂ ಆದ ಯೆಶಾಯನ ಬಳಿಗೆ ಹೋಗಿ,",
			"3": "<sup>3</sup> ಅವನಿಗೆ ಹೀಗೆಂದು ಹೇಳಿ: ‘ಈ ದಿನ ನಮಗೆ ಮಹಾಸಂಕಟದ ದಿನ, ದಂಡನೆಯ ದಿನ, ನಿಂದೆ ಅವಮಾನದ ದಿನ; ಹೆರಿಗೆಯ ಕಾಲ ಬಂದಿದೆ. ಆದರೆ ಹೆರುವುದಕ್ಕೆ ಶಕ್ತಿಸಾಲದು.",
			"4": "<sup>4</sup> ಜೀವಸ್ವರೂಪರಾದ ದೇವರನ್ನು ದೂಷಿಸುವುದಕ್ಕಾಗಿ ತನ್ನ ಯಜಮಾನ ಅಸ್ಸೀರಿಯದ ರಾಜನಿಂದ ಕಳುಹಿಸಲಾಗಿದ್ದ ರಬ್ಷಾಕೆಯ ನಿಂದಾವಾಕ್ಯಗಳನ್ನು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಕೇಳಿದ್ದಾರೆ. ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ತಾವು ಕೇಳಿದ ಆ ವಾಕ್ಯಗಳ ನಿಮಿತ್ತ ಮುಯ್ಯಿತೀರಿಸಾರು; ಹೀಗಿರಲು ಉಳಿದಿರುವ ಸ್ವಲ್ಪ ಜನರಿಗಾಗಿ ದೇವರನ್ನು ನೀವು ಪ್ರಾರ್ಥನೆ ಮಾಡಿ’ ಎಂದು ಹಿಜ್ಕೀಯ ಕೇಳಿಕೊಂಡಿದ್ದಾರೆ ಎಂದು ತಿಳಿಸಿ,” ಎಂದನು.",
			"5": "<sup>5</sup> ಅರಸ ಹಿಜ್ಕೀಯನ ಸೇವಕರು ತನ್ನ ಬಳಿಗೆ ಬಂದು ವರದಿಮಾಡಿದ ಮೇಲೆ ಯೆಶಾಯನು,",
			"6": "<sup>6</sup> “ನೀವು ಹಿಂದಿರುಗಿ ಹೋಗಿ ನಿಮ್ಮ ರಾಜನಿಗೆ, ‘ಅಸ್ಸೀರಿಯದ ಅರಸನ ಸೇವಕರು ನನ್ನನ್ನು ದೂಷಿಸಿ ಆಡಿದ ಮಾತುಗಳನ್ನು ಕೇಳಿದ್ದೇನೆ. ಅವುಗಳಿಗಾಗಿ ನೀನು ಹೆದರಬೇಕಾಗಿಲ್ಲ.",
			"7": "<sup>7</sup> ಅವನ ಮೇಲೆ ಭಯಾತ್ಮನನ್ನು ಬರಮಾಡುವೆನು. ಅವನು ಒಂದು ಸುದ್ದಿಯನ್ನು ಕೇಳಿ ಸ್ವದೇಶಕ್ಕೆ ಹಿಂದಿರುಗಿ ಅಲ್ಲೇ ಕತ್ತಿಗೆ ತುತ್ತಾಗುವಂತೆ ಮಾಡುವೆನು, ಇದು ಸರ್ವೇಶ್ವರನ ನುಡಿ, ಎಂದು ತಿಳಿಸಿರಿ,” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"8": "<sup>8</sup> ರಬ್ಷಾಕೆಯು ಹಿಂದಿರುಗಿ ಹೋಗುವಾಗ ದಾರಿಯಲ್ಲಿ, ಅಸ್ಸೀರಿಯದ ಅರಸನು ಲಾಕೀಷನನ್ನು ಬಿಟ್ಟು ಲಿಬ್ನಕ್ಕೆ ಹೋಗಿ ಆ ರಾಜ್ಯದ ವಿರುದ್ಧ ಯುದ್ಧಮಾಡುತ್ತಿರುವುದಾಗಿ ಸಮಾಚಾರ ಪಡೆದನು. ಆದುದರಿಂದ ಅವನು ಅಲ್ಲಿಗೆ ತೆರಳಿದನು.",
			"9": "<sup>9</sup> ಅಷ್ಟರಲ್ಲಿ ಸುಡಾನಿನ ರಾಜ ತಿರ್ಹಾಕನು ತನಗೆ ವಿರುದ್ಧವಾಗಿ ಹೊರಟಿದ್ದಾನೆ ಎಂಬ ಸುದ್ದಿ ಅಸ್ಸೀರಿಯದ ಅರಸನಿಗೆ ಮುಟ್ಟಿತು. ಆಗ ಅವನು ಜುದೇಯದ ಅರಸನಾದ ಹಿಜ್ಕೀಯನಿಗೆ,",
			"10": "<sup>10</sup> “ಜೆರುಸಲೇಮ್ ನನ್ನ ವಶವಾಗುವುದಿಲ್ಲವೆಂದು ನೀನು ನಂಬುವ ದೇವರು ನಿನಗೆ ಹೇಳಿ ನಿನ್ನನ್ನು ಮೋಸಗೊಳಿಸಬಹುದು.",
			"11": "<sup>11</sup> ಅಸ್ಸೀರಿಯದ ಅರಸನು ಎಲ್ಲಾ ರಾಜ್ಯಗಳನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ನಾಶಪಡಿಸಿದ್ದಾನೆಂಬ ಸಮಾಚಾರ ನಿನಗೆ ಮುಟ್ಟಿರಬೇಕು, ಹೀಗಿರುವಲ್ಲಿ ನೀನು ಮಾತ್ರ ತಪ್ಪಿಸಿಕೊಳ್ಳುವಿಯೋ?",
			"12": "<sup>12</sup> ನನ್ನ ತಂದೆತಾತಂದಿರು ಗೋಜಾನ್, ಖಾರನ್, ರೆಚೆಫ್ ಎಂಬ ನಗರಗಳ ಜನರನ್ನು ಮತ್ತು ತೆಲಸ್ಸಾರ್ ಪ್ರಾಂತ್ಯದಲ್ಲಿರುವ ಎದೆನಿನ ಜನರನ್ನು ನಾಶಮಾಡಿದರು. ಆಗ ಅವರ ದೇವರುಗಳು ಅವರನ್ನು ರಕ್ಷಿಸಲಾಗಲಿಲ್ಲ.",
			"13": "<sup>13</sup> ಹಮಾತ್, ಅರ್ಪಾದ್, ಸೆಫರ್ವಯಿಮ್, ಹೇನ್ ಮತ್ತು ಇವ್ವಾ ಎಂಬ ನಗರಗಳ ಅರಸರ ಗತಿ ಏನಾಯಿತು?” ಎಂದು ಪತ್ರ ಬರೆದು ದೂತರ ಸಮೇತ ಕಳುಹಿಸಿದನು.",
			"14": "<sup>14</sup> ಹಿಜ್ಕೀಯನು, ಆ ದೂತರು ತಂದ ಪತ್ರವನ್ನು ತೆಗೆದುಕೊಂಡು ಓದಿದ ನಂತರ, ದೇವಾಲಯಕ್ಕೆ ಹೋಗಿ ಅದನ್ನು ಸರ್ವೇಶ್ವರ ಸ್ವಾಮಿಯ ಮುಂದೆ ತೆರೆದಿಟ್ಟು, ಹೀಗೆಂದು ಪ್ರಾರ್ಥನೆ ಮಾಡಿದನು:",
			"15": "<sup>15</sup> “ಇಸ್ರಯೇಲಿನ ದೇವರೇ, ಸರ್ವೇಶ್ವರಾ, ಕೆರೂಬಿಗಳ ಮೇಲೆ ಆಸೀನಾರೂಢರಾಗಿರುವವರೇ, ಎಲ್ಲಾ ಭೂರಾಜ್ಯಗಳನ್ನು ಆಳುವ ದೇವರು ನೀವೊಬ್ಬರೇ; ಪರಲೋಕ ಭೂಲೋಕಗಳನ್ನುಂಟುಮಾಡಿದವರು ನೀವೇ.",
			"16": "<sup>16</sup> ಸರ್ವೇಶ್ವರಾ, ಕಿವಿಗೊಟ್ಟು ಕೇಳಿ; ಸರ್ವೇಶ್ವರಾ, ಕಣ್ಣಿಟ್ಟು ನೋಡಿ: ಸನ್ಹೇರೀಬನು ಜೀವಸ್ವರೂಪ ದೇವರಾದ ನಿಮ್ಮನ್ನು ನಿಂದಿಸುವುದಕ್ಕಾಗಿ ಹೇಳಿಕಳುಹಿಸಿದ ಈ ಮಾತುಗಳನ್ನೆಲ್ಲಾ ಮನಸ್ಸಿಗೆ ತಂದುಕೊಳ್ಳಿ.",
			"17": "<sup>17</sup> ಸರ್ವೇಶ್ವರಾ, ಅಸ್ಸೀರಿಯದ ರಾಜರು ಸಕಲ ರಾಷ್ಟ್ರಗಳನ್ನೂ ಹಾಗೂ ಅವರ ನಾಡುಗಳನ್ನೂ ಹಾಳುಮಾಡಿದ್ದಾರೆ.",
			"18": "<sup>18</sup> ಅವರ ದೇವರುಗಳನ್ನೂ ಬೆಂಕಿಗೆ ಹಾಕಿದ್ದು ನಿಜ. ಅವು ದೇವರುಗಳಲ್ಲ, ಕೇವಲ ಮನುಷ್ಯರು ಕೆತ್ತಿದ ಕಲ್ಲುಮರಗಳ ಬೊಂಬೆಗಳಷ್ಟೆ. ಆದುದರಿಂದಲೇ ಅವು ಅವರಿಂದ ಹಾಳಾದವು.",
			"19": "<sup>19</sup> ನಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರಾ, ನೀವೇ ಏಕೈಕ ದೇವರು ಎಂಬುದನ್ನು ಭೂಮಿಯ ರಾಷ್ಟ್ರಗಳೆಲ್ಲವೂ ತಿಳಿದುಕೊಳ್ಳುವಂತೆ ನಮ್ಮನ್ನು ಈ ಅಸ್ಸೀರಿಯನ ಕೈಯಿಂದ ಬಿಡಿಸಿರಿ.”",
			"20": "<sup>20</sup> ಆಗ ಆಮೋಚನ ಮಗನಾದ ಯೆಶಾಯನು ಹಿಜ್ಕೀಯನಿಗೆ ಹೇಳಿಕಳುಹಿಸಿದ್ದೇನೆಂದರೆ: ” ಇಸ್ರಯೇಲಿನ ದೇವರಾದ ಸರ್ವೇಶ್ವರಸ್ವಾಮಿಯ ಈ ಮಾತುಗಳನ್ನು ಕೇಳು: ನೀನು ಅಸ್ಸೀರಿಯದ ರಾಜನಾದ ಸನ್ಹೇರೀಬನ ವಿಷಯವಾಗಿ ಮಾಡಿದ ಬಿನ್ನಹಗಳು ಸರ್ವೇಶ್ವರಸ್ವಾಮಿಗೆ ಮುಟ್ಟಿವೆ. ಅವರು ಅವನನ್ನು ಕುರಿತು ಹೀಗೆಂದಿದ್ದಾರೆ:",
			"21": "<sup>21</sup> ‘ನಿನ್ನನ್ನು ಪರಿಹಾಸ್ಯಮಾಡಿ ತಿರಸ್ಕರಿಸುತಿಹಳು ಕನ್ಯೆಯಾದ ಸಿಯೋನಿನ ಕುವರಿಯು; ನಿನ್ನ ಹಿಂದೆ ತಲೆಯಾಡಿಸಿ ಮೂದಲಿಸುತಿಹಳು ಜೆರುಸಲೇಮಿನ ಆ ಕುವರಿಯು.",
			"22": "<sup>22</sup> ನೀನು ನಿಂದಿಸಿ ದೂಷಿಸುತ್ತಿರುವುದು ಯಾರನ್ನು? ಕಿರಿಚಿ ಹೀಯಾಳಿಸುತ್ತಿರುವುದು ಯಾರನ್ನು? ಗರ್ವದಿಂದ ದುರುಗುಟ್ಟಿ ನೋಡಿದುದು ಯಾರನ್ನು? ಇಸ್ರಯೇಲರ ಸದಮಲಸ್ವಾಮಿಯನ್ನು ಅಲ್ಲವೇನು?",
			"23": "<sup>23</sup> ನೀನು ನಿನ್ನ ದೂತರ ಮೂಲಕ ಸರ್ವೇಶ್ವರನನ್ನು ನಿಂದಿಸಿರುವೆ, ಅಲ್ಲದೆ ಈ ಮಾತುಗಳನ್ನಾಡಿ ಜಂಬಕೊಚ್ಚಿಕೊಂಡಿರುವೆ: ‘ಹತ್ತಿದ್ದೇನೆ ಗಿರಿಶಿಖರಗಳನ್ನು ರಥಸಮೂಹದೊಡನೆ ಸೇರಿದ್ದೇನೆ ಲೆಬನೋನಿನ ದುರ್ಗಮ ಸ್ಥಳಗಳನ್ನೇ! ಕಡಿದಿದ್ದೇನೆ ಅದರ ಎತ್ತರವಾದ ದೇವದಾರು ವೃಕ್ಷಗಳನ್ನೇ ಕತ್ತರಿಸಿದ್ದೇನೆ ಅದರ ಶ್ರೇಷ್ಠವಾದ ತುರಾಯಿ ಮರಗಳನ್ನೇ ಒಳಹೊಕ್ಕಿದ್ದೇನೆ ಅಲ್ಲಿನ ದೂರದ ನಿವಾಸಸ್ಥಳಗಳನ್ನೇ, ಶಿಖರವನ್ನೇ, ಪ್ರವೇಶಿಸಿದ್ದೇನೆ ದಟ್ಟವಾದ ಆ ಕಾಡು ಮೇಡುಗಳನ್ನೇ!",
			"24": "<sup>24</sup> ಪರನಾಡುಗಳಲ್ಲಿ ಬಾವಿ ತೋಡಿ ನೀರು ಕುಡಿದಿದ್ದೇನೆ, ನನ್ನ ಅಂಗಾಲಿನಿಂದಲೇ ಈಜಿಪ್ಟಿನ ನದಿಗಳನ್ನೆಲ್ಲಾ ಬತ್ತಿಸಿದ್ದೇನೆ!’",
			"25": "<sup>25</sup> “ಕೇಳು, ಇದನ್ನೆಲ್ಲಾ ಬಹುಕಾಲದ ಹಿಂದೆ ಗೊತ್ತುಮಾಡಿದವನು ನಾನೇ, ಪೂರ್ವಕಾಲದಲ್ಲಿ ನಿರ್ಣಯಿಸಿದುದನ್ನು ಈಗ ನೆರವೇರಿಸಿದ್ದೇನೆ. ಕೋಟೆ ನಗರಗಳನ್ನು ನೀನು ಹಾಳುದಿಬ್ಬಗಳನ್ನಾಗಿಸಿದೆ. ಆದರೆ ಸಾಧ್ಯವಾಯಿತಿದು ನನ್ನಿಂದಲೇ; ನಿನಗಿದು ತಿಳಿಯದೆ ಹೋಯಿತೆ?",
			"26": "<sup>26</sup> ಬಲವಿಲ್ಲದವರಾದರು ಆ ನಗರವಾಸಿಗಳು ನನ್ನ ನಿಮಿತ್ತವೆ, ಸಮಾನರಾದರು ಹೊಲದ ಸಸಿಗೆ, ಎಳೆಗರಿಕೆಗೆ, ಮಾಳಿಗೆ ಮೇಲಿನ ಹುಲ್ಲಿಗೆ; ಬೆಳೆಯುವುದಕ್ಕೆ ಮೊದಲೆ ತುತ್ತಾದರು ಬಿರುಗಾಳಿಯ ಹೊಡೆತಕ್ಕೆ!",
			"27": "<sup>27</sup> ನನಗೆ ಗೊತ್ತಿದೆ ನೀ ಕೂರುವುದು, ನೀ ಹೋಗಿಬರುವುದು ನನಗೆ ತಿಳಿದಿದೆ ನೀ ನನ್ನ ಮೇಲೆ ರೌದ್ರಾವೇಶಗೊಂಡಿರುವುದು.",
			"28": "<sup>28</sup> ನನ್ನ ವಿರುದ್ಧ ನಿನಗಿರುವ ಕ್ರೋಧ, ಗರ್ವ ತಿಳಿದಿದೆ ನನಗೆ ಎಂದೇ ಹಾಕುವೆ ದಾರ ಕಡಿವಾಣ ಮೂಗಿಗೆ, ಬಾಯಿಗೆ; ನೀ ಬಂದ ದಾರಿಯಿಂದಲೇ ಅಟ್ಟುವೆ ನಿನ್ನನ್ನು ಹಿಂದಕ್ಕೆ.”",
			"29": "<sup>29</sup> ಬಳಿಕ ಯೆಶಾಯನು ರಾಜ ಹಿಜ್ಕೀಯನಿಗೆ ಹೀಗೆಂದನು: “ಈ ಮಾತುಗಳು ನೆರವೇರುತ್ತವೆಂಬುದಕ್ಕೆ ಸೂಚನೆ ಇದು: ಈ ವರ್ಷದಲ್ಲಿ ನೀವು ಕೂಳೆಬೆಳೆಯನ್ನು, ಮುಂದಿನ ವರ್ಷದಲ್ಲಿ ತನ್ನಷ್ಟಕ್ಕೆ ತಾನೇ ಅದು ಬೆಳೆದುದನ್ನು, ಆದರೆ ಮೂರನೆಯ ವರ್ಷ ಹೊಲಗಳಲ್ಲಿ ಬಿತ್ತಿ ಕೊಯ್ದುದನ್ನು, ದ್ರಾಕ್ಷಿತೋಟದಲ್ಲಿ ವ್ಯವಸಾಯ ಮಾಡಿ ಶೇಖರಿಸಿದ್ದನ್ನು ಅನುಭವಿಸಿರಿ.",
			"30": "<sup>30</sup> ಅಳಿದು ಉಳಿದ ಯೆಹೂದ್ಯರು ನಾಡಿನಲ್ಲಿ ಬೇರೂರಿ ನೆಲೆಗೊಳ್ಳುವರು; ಬೆಳೆದು ಅಭಿವೃದ್ಧಿಯಾಗುವರು.",
			"31": "<sup>31</sup> ಅಳಿದುಳಿದವರು ಜೆರುಸಲೇಮಿನಲ್ಲೇ ಹರಡಿಕೊಳ್ಳುವರು; ಸಿಯೋನ್ ಪರ್ವತದಲ್ಲಿ ಅಭಿವೃದ್ಧಿಯಾಗುವರು. ಸರ್ವೇಶ್ವರಸ್ವಾಮಿಯ ಆಗ್ರಹವೇ ಇದನ್ನು ಸಾಧಿಸುವುದು.",
			"32": "<sup>32</sup> “ಅಸ್ಸೀರಿಯದ ಅರಸನನ್ನು ಕುರಿತು ಸರ್ವೇಶ್ವರ ಹೇಳಿರುವ ಮಾತಿದು: ‘ಅವನು ಪಟ್ಟಣವನ್ನು ಸಮೀಪಿಸುವುದಿಲ್ಲ, ಅದರತ್ತ ಬಾಣವನ್ನೆಸೆಯುವುದಿಲ್ಲ, ಕವಚಧಾರಿಗಳನ್ನು ಕಳುಹಿಸುವುದಿಲ್ಲ. ಅದರ ಆಕ್ರಮಣಕ್ಕಾಗಿ ಮಣ್ಣುದಿಬ್ಬಗಳನ್ನು ಎಬ್ಬಿಸುವುದಿಲ್ಲ.",
			"33": "<sup>33</sup> ಅವನು ಬಂದ ದಾರಿಯಿಂದಲೇ ಹಿಂದಿರುಗಿ ಹೋಗುವನು; ಈ ಪಟ್ಟಣಕ್ಕೆ ಕಾಲಿಡನು.",
			"34": "<sup>34</sup> ನನಗಾಗಿಯೂ ನನ್ನ ದಾಸ ದಾವೀದನಿಗಾಗಿಯೂ ಈ ಪಟ್ಟಣವನ್ನು ಉಳಿಸಿ ಕಾಪಾಡುವೆನು,’ ಎಂದಿದ್ದಾರೆ\".",
			"35": "<sup>35</sup> ಅದೇ ರೀತಿಯಲ್ಲಿ ಸರ್ವೇಶ್ವರಸ್ವಾಮಿಯ ದೂತನು ಹೊರಟುಬಂದು ಅಸ್ಸೀರಿಯರ ಪಾಳೆಯದಲ್ಲಿ 185,000 ಮಂದಿ ಸೈನಿಕರನ್ನು ಮರಣಕ್ಕೆ ಈಡುಮಾಡಿದನು; ಅಸ್ಸೀರಿಯರು ಬೆಳಿಗ್ಗೆ ಎದ್ದು ನೋಡುವಾಗ ಪಾಳೆಯ ತುಂಬ ಹೆಣಗಳು ಇದ್ದವು.",
			"36": "<sup>36</sup> ಆದ್ದರಿಂದ ಅಸ್ಸೀರಿಯರ ಅರಸನಾದ ಸನ್ಹೇರೀಬನು ಹಿಂದಿರುಗಿದನು. ನಿನೆವೆ ಪಟ್ಟಣಕ್ಕೆ ಹೋಗಿ ಅಲ್ಲೆ ವಾಸಿಸಿದನು.",
			"37": "<sup>37</sup> ಒಂದು ದಿನ ಗುಡಿಗೆ ಹೋಗಿ ತನ್ನ ದೇವರಾದ ನಿಸ್ರೋಕನನ್ನು ಆರಾಧನೆ ಮಾಡುತ್ತಿರುವಾಗ, ಅದ್ರಮ್ಮೆಲೆಕ್ ಮತ್ತು ಸರೆಚೆರ್ ಎಂಬವರು ಅವನನ್ನು ಕತ್ತಿಯಿಂದ ಕೊಂದು ಅರರಾಟ್ ದೇಶಕ್ಕೆ ಪಲಾಯನ ಗೈದರು. ಅವನ ಸ್ಥಾನದಲ್ಲಿ ಅವನ ಮಗ ಏಸರ್ ಹದ್ದೋನನು ಅರಸನಾದನು."
		},
		"20": {
			"1": "<sup>1</sup> ಆ ದಿನಗಳಲ್ಲಿ ಹಿಜ್ಕೀಯನು ಮಾರಕ ರೋಗದಿಂದ ನರಳುತ್ತಿದ್ದನು. ಆಗ ಆಮೋಚನ ಮಗನೂ ಪ್ರವಾದಿಯೂ ಆದ ಯೆಶಾಯನು ಅವನ ಬಳಿಗೆ ಬಂದು, “ನಿನ್ನ ಸಂಸಾರದ ವಿಷಯವಾಗಿ ವ್ಯವಸ್ಥೆಮಾಡು; ಏಕೆಂದರೆ, ‘ನೀನು ಉಳಿಯುವ ಹಾಗಿಲ್ಲ, ಸಾಯಬೇಕಾಗಿದೆ,’ ಎಂಬುದಾಗಿ ಸರ್ವೇಶ್ವರ ಹೇಳಿದ್ದಾರೆ,” ಎಂದು ಹೇಳಿದನು.",
			"2": "<sup>2</sup> ಇದನ್ನು ಕೇಳಿದೊಡನೆ ಹಿಜ್ಕೀಯನು ಮುಖವನ್ನು ಗೋಡೆಯ ಕಡೆಗೆ ತಿರುಗಿಸಿ,",
			"3": "<sup>3</sup> “ಸರ್ವೇಶ್ವರಾ, ನಾನು ಶ್ರದ್ಧೆಯಿಂದಲೂ ಪ್ರಾಮಾಣಿಕತೆಯಿಂದಲೂ ನಡೆದುಕೊಂಡಿದ್ದೇನೆ. ನಿಮ್ಮ ದೃಷ್ಟಿಯಲ್ಲಿ ಒಳ್ಳೆಯವನಾಗಿದ್ದುದನ್ನು ನೆನಪಿಗೆ ತಂದುಕೊಳ್ಳಿ,” ಎಂದು ಬಹಳವಾಗಿ ಕಣ್ಣೀರಿಡುತ್ತಾ ಪ್ರಾರ್ಥಿಸಿದನು.",
			"4": "<sup>4</sup> ಯೆಶಾಯನು ಅರಮನೆಯ ಮಧ್ಯಪ್ರಾಕಾರವನ್ನು ದಾಟುವುದಕ್ಕೆ ಮೊದಲೇ ಸರ್ವೇಶ್ವರ ಅವನಿಗೆ,",
			"5": "<sup>5</sup> “ನೀನು ಹಿಂದಿರುಗಿ ಹೋಗಿ ನನ್ನ ಪ್ರಜೆಗಳ ಒಡೆಯನಾಗಿರುವ ಹಿಜ್ಕೀಯನಿಗೆ, “ನಾನು ನಿನ್ನ ಪ್ರಾರ್ಥನೆಯನ್ನು ಕೇಳಿದ್ದೇನೆ; ನಿನ್ನ ಕಣ್ಣೀರನ್ನು ನೋಡಿದ್ದೇನೆ; ನೀನು ಗುಣಹೊಂದಿ ಮೂರನೆಯ ದಿನ ನನ್ನ ಆಲಯಕ್ಕೆ ಬರುವೆ.",
			"6": "<sup>6</sup> ಅದಲ್ಲದೆ ನಿನ್ನ ಆಯುಷ್ಯಕ್ಕೆ ಹದಿನೈದು ವರ್ಷಗಳನ್ನು ಕೂಡಿಸುತ್ತೇನೆ. ನಿನ್ನನ್ನೂ ಈ ಪಟ್ಟಣವನ್ನೂ ಅಸ್ಸೀರಿಯದ ಅರಸನ ಕೈಗೆ ಬೀಳದಂತೆ ತಪ್ಪಿಸುವೆನು. ನನಗಾಗಿ ಹಾಗು ನನ್ನ ದಾಸ ದಾವೀದನಿಗಾಗಿ ಈ ಪಟ್ಟಣವನ್ನು ಉಳಿಸಿ ಕಾಪಾಡುವೆನು’ ಎಂಬುದಾಗಿ ನಿನ್ನ ಪೂರ್ವಜ ದಾವೀದನ ದೇವರಾಗಿರುವ ಸರ್ವೇಶ್ವರ ಹೇಳುತ್ತಾರೆ ಎಂದು ತಿಳಿಸು,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದರು.",
			"7": "<sup>7</sup> ಯೆಶಾಯನು ಒಂದು ಅಂಜೂರದ ಹಣ್ಣಿನ ಉಂಡೆಯನ್ನು ತರಿಸಿ ಅದನ್ನು ಕುರುವಿನ ಮೇಲೆ ಇಡಿಸಿದಾಗ ಹಿಜ್ಕೀಯನು ಗುಣಹೊಂದಿದನು.",
			"8": "<sup>8</sup> ಹಿಜ್ಕೀಯನು ಯೆಶಾಯನನ್ನು, “ಸರ್ವೇಶ್ವರ ನನ್ನನ್ನು ಗುಣಪಡಿಸುವರೆಂಬುದಕ್ಕೂ ನಾನು ಮೂರನೆ ದಿನ ಅವರ ಆಲಯಕ್ಕೆ ಹೋಗುವೆನೆಂಬುದಕ್ಕೂ ಗುರುತೇನು?” ಎಂದು ಕೇಳಿದನು.",
			"9": "<sup>9</sup> ಅವನು, “ಸರ್ವೇಶ್ವರ ನುಡಿದದ್ದನ್ನು ನೆರವೇರಿಸುವರು ಎಂಬುದಕ್ಕೆ ಸರ್ವೇಶ್ವರನಿಂದ ಗುರುತು ಕೇಳುತ್ತಿರುವೆ; ಹಾಗಾದರೆ ನೆರಳು ಹತ್ತು ಮೆಟ್ಟಲು ಮುಂದೆ ಹೋಗಬೇಕೋ ಹಿಂದೆ ಬರಬೇಕೋ ಹೇಳು,” ಎಂದನು.",
			"10": "<sup>10</sup> ಹಿಜ್ಕೀಯನು, “ನೆರಳು ಮುಂದೆ ಹೋಗುವುದು ಸುಲಭ; ಆದುದರಿಂದ ಹತ್ತು ಮೆಟ್ಟಲು ಹಿಂದೆ ಬರುವಂತೆ ಮಾಡು,” ಎಂದು ಹೇಳಿದನು.",
			"11": "<sup>11</sup> ಆಗ ಪ್ರವಾದಿ ಯೆಶಾಯನು ಸರ್ವೇಶ್ವರನಿಗೆ ಮೊರೆಯಿಡಲು ಅವರು ಆಹಾಜನ ಸೋಪಾನಪಂಕ್ತಿಯಲ್ಲಿ ಹತ್ತು ಮೆಟ್ಟಲು ಮುಂದೆ ಹೋಗಿದ್ದ ನೆರಳನ್ನು ಹಿಂದೆ ಬರಮಾಡಿದರು.",
			"12": "<sup>12</sup> ಅದೇ ಕಾಲದಲ್ಲಿ ಬಲದಾನನ ಮಗನೂ ಬಾಬಿಲೋನಿಯದ ಅರಸನೂ ಆದ ಬೆರೋದಕ ಬಲದಾನ ಎಂಬವನು ಹಿಜ್ಕೀಯನು ಅಸ್ವಸ್ಥನಾಗಿದ್ದನೆಂದು ಕೇಳಿ ದೂತರ ಮುಖಾಂತರ ಅವನಿಗೆ ಪತ್ರವನ್ನೂ ಉಡುಗೊರೆಯನ್ನೂ ಕಳುಹಿಸಿದನು.",
			"13": "<sup>13</sup> ಹಿಜ್ಕೀಯನು ಬಂದಂಥ ದೂತರ ಮಾತನ್ನು ಕೇಳಿ, ಅವರಿಗೆ ಬೆಳ್ಳಿ, ಬಂಗಾರ, ಸುಗಂಧದ್ರವ್ಯ, ಪರಿಮಳತೈಲ, ಮೊದಲಾದ ಪದಾರ್ಥಗಳಿರುವ ಮನೆಯನ್ನೂ ಆಯುಧಶಾಲೆಯನ್ನೂ ತನ್ನ ಭಂಡಾರದಲ್ಲಿದ್ದದ್ದೆಲ್ಲವನ್ನೂ ತೋರಿಸಿದನು. ಅವನ ಅರಮನೆಯಲ್ಲೂ ರಾಜ್ಯದಲ್ಲೂ ಅವರಿಗೆ ತೋರಿಸದಿದ್ದ ವಸ್ತು ಒಂದೂ ಇರಲಿಲ್ಲ.",
			"14": "<sup>14</sup> ಆಗ ಪ್ರವಾದಿ ಯೆಶಾಯನು ಹಿಜ್ಕೀಯನ ಬಳಿಗೆ ಬಂದು, “ಆ ಮನುಷ್ಯರು ಎಲ್ಲಿಯವರು, ಏನು ಹೇಳಿದರು?” ಎಂದು ಕೇಳಿದನು. ಅದಕ್ಕೆ ಹಿಜ್ಕೀಯನು, “ಅವರು ಬಹುದೂರದ ದೇಶವಾದ ಬಾಬಿಲೋನಿನಿಂದ ಬಂದವರು,” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"15": "<sup>15</sup> ಯೆಶಾಯನು ಮತ್ತೆ ಅವನನ್ನು, “ಅವರು ನಿನ್ನ ಅರಮನೆಯಲ್ಲಿ ಏನು ನೋಡಿದರು?” ಎಂದು ಕೇಳಲು ಹಿಜ್ಕೀಯನು, “ಅರಮನೆಯಲ್ಲಿರುವುದೆಲ್ಲವನ್ನೂ ನೋಡಿದರು. ನನ್ನ ಭಂಡಾರದಲ್ಲಿ ನಾನು ಅವರಿಗೆ ತೋರಿಸದಿದ್ದ ಒಡವೆ ವಸ್ತು ಒಂದೂ ಇಲ್ಲ,” ಎಂದನು.",
			"16": "<sup>16</sup> ಆಗ ಅವನು ಹಿಜ್ಕೀಯನಿಗೆ, “ಸರ್ವೇಶ್ವರನ ಮಾತನ್ನು ಕೇಳು:",
			"17": "<sup>17</sup> ‘ನಿನ್ನ ಪೂರ್ವಜರ ಕಾಲದಿಂದ ಇಂದಿನವರೆಗೆ ಅರಮನೆಯಲ್ಲಿ ಸಂಗ್ರಹವಾದದ್ದೆಲ್ಲವನ್ನು ಬಾಬಿಲೋನಿಯಾಕ್ಕೆ ಕೊಂಡೊಯ್ಯುವ ದಿನ ಬರುವುದು; ಇಲ್ಲೇನೂ ಉಳಿಯುವುದಿಲ್ಲ.",
			"18": "<sup>18</sup> ಬಾಬಿಲೋನಿನ ಅರಸರು ಬಂದು ನೀನು ಪಡೆದ ಮಕ್ಕಳನ್ನು ತೆಗೆದುಕೊಂಡುಹೋಗಿ ಅವರನ್ನು ತಮ್ಮ ಅರಮನೆಯಲ್ಲಿ ಕಂಚುಕಿಗಳನ್ನಾಗಿ ನೇಮಿಸಿಕೊಳ್ಳುವರು,’ ಎನ್ನುತ್ತಾರೆ,” ಎಂದು ಹೇಳಿದನು.",
			"19": "<sup>19</sup> ‘ನನ್ನ ಜೀವಮಾನದಲ್ಲಿ ಹೇಗೂ ಸೌಭಾಗ್ಯವಿದ್ದರೆ ಸಾಕು’ ಎಂದುಕೊಂಡ ಹಿಜ್ಕೀಯನು ಯೆಶಾಯನಿಗೆ, “ನೀವು ತಿಳಿಸಿದ ಸರ್ವೇಶ್ವರನ ಮಾತು ಹಿತಕರವಾಗಿದೆ,” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"20": "<sup>20</sup> ಹಿಜ್ಕೀಯನ ಉಳಿದ ಚರಿತ್ರೆ, ಅವನ ಪರಾಕ್ರಮಕಾರ್ಯ ಹಾಗು ಅವನು ಕೆರೆಕಾಲುವೆಗಳನ್ನು ಮಾಡಿಸಿ ಊರೊಳಗೆ ನೀರನ್ನು ತಂದ ವಿವರ, ಇವು ಜುದೇಯ ರಾಜರ ಇತಿಹಾಸ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"21": "<sup>21</sup> ಅವನು ಮೃತನಾಗಿ ಪಿತೃಗಳ ಬಳಿ ಸೇರಿದನು. ಅವನ ಸ್ಥಾನದಲ್ಲಿ ಅವನ ಮಗ ಮನಸ್ಸೆ ಅರಸನಾದನು."
		},
		"21": {
			"1": "<sup>1</sup> ಮನಸ್ಸೆ ಅರಸನಾದಾಗ ಅವನಿಗೆ ಹನ್ನೆರಡು ವರ್ಷ ವಯಸ್ಸು. ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಐವತ್ತೈದು ವರ್ಷ ಆಳಿದನು; ಅವನ ತಾಯಿಯ ಹೆಸರು ಹೆಫ್ಚಿಬಾ.",
			"2": "<sup>2</sup> ಸರ್ವೇಶ್ವರ ಇಸ್ರಯೇಲರ ನಾಡಿನಿಂದ ಹೊರಡಿಸಿಬಿಟ್ಟ ಅನ್ಯಜನಾಂಗಗಳ ಅಸಹ್ಯಕಾರ್ಯಗಳನ್ನು ಅವನು ಅನುಸರಿಸಿ ಆ ಸರ್ವೇಶ್ವರನ ಚಿತ್ತಕ್ಕೆ ವಿರುದ್ಧವಾಗಿ ನಡೆದನು.",
			"3": "<sup>3</sup> ತನ್ನ ತಂದೆ ಹಿಜ್ಕೀಯನು ತೆಗೆದುಹಾಕಿದ್ದ ಪೂಜಾಸ್ಥಳಗಳನ್ನು ಮತ್ತೆ ಸ್ಥಾಪಿಸಿ ಇಸ್ರಯೇಲರ ಅರಸನಾದ ಅಹಾಬನಂತೆ ಬಾಳ್ ದೇವತೆಗಾಗಿ ಬಲಿಪೀಠಗಳನ್ನು ಕಟ್ಟಿಸಿ, ಅಶೇರ ವಿಗ್ರಹಸ್ತಂಭವನ್ನು ನಿಲ್ಲಿಸಿ, ನಕ್ಷತ್ರಮಂಡಲಕ್ಕೆ ಕೈಮುಗಿದು, ಆರಾಧಿಸಿದನು.",
			"4": "<sup>4</sup> ಸರ್ವೇಶ್ವರ ತಮ್ಮ ಹೆಸರನ್ನು ಸ್ಥಾಪಿಸುವುದಕ್ಕಾಗಿ ಆರಿಸಿಕೊಂಡ ಜೆರುಸಲೇಮಿನ ದೇವಾಲಯದ ಎರಡು ಪ್ರಾಕಾರಗಳಲ್ಲಿಯೂ",
			"5": "<sup>5</sup> ಸರ್ವ ನಕ್ಷತ್ರಮಂಡಲಕ್ಕಾಗಿ ಬಲಿಪೀಠಗಳನ್ನು ಕಟ್ಟಿಸಿದನು.",
			"6": "<sup>6</sup> ಇದಲ್ಲದೆ ಅವನು ತನ್ನ ಮಗನನ್ನು ಬಲಿಯಗ್ನಿಪರೀಕ್ಷೆಗೆ ಗುರಿಯಾಗಿಸಿದನು. ಕಣಿ ಹೇಳಿಸುವುದು, ತಂತ್ರಮಂತ್ರಗಳನ್ನು ಮಾಡಿಸುವುದು, ಭೂತಪ್ರೇತಗಳನ್ನು ವಿಚಾರಿಸುವವರನ್ನು ಸಂಪರ್ಕಿಸುವುದು ಇವೇ ಮೊದಲಾದ ದುಷ್ಕೃತ್ಯಗಳಿಂದ ಸರ್ವೇಶ್ವರನಿಗೆ ಕೋಪವನ್ನೆಬ್ಬಿಸಿದನು.",
			"7": "<sup>7</sup> ತಾನು ಮಾಡಿಸಿದ ಅಶೇರ ವಿಗ್ರಹಸ್ತಂಭವನ್ನು ದೇವಾಲಯದಲ್ಲಿ ಇಡಿಸಿದನು. ಸರ್ವೇಶ್ವರ ಆ ಆಲಯದಲ್ಲಿ ದಾವೀದನಿಗೂ ಅವನ ಮಗ ಸೊಲೊಮೋನನಿಗೂ, “ಇಸ್ರಯೇಲರು ತಮಗೆ ಮೋಶೆಯ ಮುಖಾಂತರ ಕೊಡಲಾದ ನನ್ನ ಧರ್ಮಶಾಸ್ತ್ರದ ನಿಬಂಧನೆಗಳನ್ನು ಅನುಸರಿಸಿ ನಡೆಯುವುದಾದರೆ",
			"8": "<sup>8</sup> ನನ್ನ ನಾಮಮಹತ್ತು ಈ ದೇವಾಲಯದಲ್ಲು, ಇಸ್ರಯೇಲರ ಊರುಗಳಲ್ಲು ಹಾಗು ನನಗೆ ಪ್ರಿಯವಾಗಿರುವ ಜೆರುಸಲೇಮಿನಲ್ಲು ಸದಾಕಾಲ ಇರುವುದು. ಇಸ್ರಯೇಲರು ಇನ್ನು ಮುಂದೆ ದೇಶಭ್ರಷ್ಟರಾಗಿ ಅಲೆದಾಡದೆ ತಮ್ಮ ಪಿತೃಗಳಿಗೆ ಕೊಡಲಾದ ನಾಡಿನಲ್ಲಿಯೇ ವಾಸವಾಗಿರುವಂತೆ ಮಾಡುವೆನು,” ಎಂದು ಹೇಳಿದ್ದರು.",
			"9": "<sup>9</sup> ಆದರೆ ಇಸ್ರಯೇಲರು ಅವರ ಮಾತನ್ನು ಕೇಳದೆ, ಮನಸ್ಸೆಯಿಂದ ಪ್ರೇರಿತರಾಗಿ, ತಮ್ಮ ಮುಂದೆಯೇ ಸರ್ವೇಶ್ವರನಿಂದ ಸಂಹೃತರಾದ ಅನ್ಯಜನಾಂಗಗಳಿಗಿಂತಲೂ ದುಷ್ಟರಾದರು.",
			"10": "<sup>10</sup> ಆಗ ಸರ್ವೇಶ್ವರ ತನ್ನ ದಾಸರಾದ ಪ್ರವಾದಿಗಳ ಮುಖಾಂತರ.",
			"11": "<sup>11</sup> “ಯೆಹೂದ್ಯರ ಅರಸನಾದ ಮನಸ್ಸೆಯು, ಹಿಂದೆ ಇದ್ದ ಅಮೋರಿಯರ ಕೃತ್ಯಗಳಿಗಿಂತ ಅಸಹ್ಯವಾದ ಕೃತ್ಯಗಳನ್ನು ನಡೆಸಿದ್ದಾನೆ. ವಿಗ್ರಹಗಳಿಂದ ಯೆಹೂದ್ಯರನ್ನು ಪಾಪಕ್ಕೆ ಪ್ರೇರಿಸಿದ್ದಾನೆ;",
			"12": "<sup>12</sup> ಆದ್ದರಿಂದ ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಎಂಬ ನಾನು ಜೆರುಸಲೇಮಿನವರ ಮೇಲೂ ಜುದೇಯದವರ ಮೇಲೂ ಕೇಡನ್ನು ಬರಮಾಡುವೆನು; ಅದನ್ನು ಕೇಳುವವರ ಎರಡು ಕಿವಿಗಳೂ ಬಿರಿಯುವುವು.",
			"13": "<sup>13</sup> ನೂಲು, ಮಟ್ಟಗೋಲು ಇವುಗಳಿಂದಲೋ ಎಂಬಂತೆ ಸಮಾರಿಯವನ್ನೂ ಅಹಾಬನ ಮನೆಯನ್ನೂ ನೆಲಸಮ ಮಾಡಿಬಿಟ್ಟೆನಲ್ಲವೆ? ಜೆರುಸಲೇಮನ್ನೂ ಅವುಗಳಂತೆಯೇ ಮಾಡುವೆನು. ಒಬ್ಬನು ಪಾತ್ರೆಯನ್ನು ಉಜ್ಜಿ ತಲೆ ಕೆಳಗಾಗಿ ಇಡುವಂತೆ ನಾನು ಜೆರುಸಲೇಮನ್ನು ಉಜ್ಜಿ ತಲೆ ಕೆಳಗಾಗಿ ಮಾಡುವೆನು.",
			"14": "<sup>14</sup> ನನ್ನ ಪ್ರಜೆಗಳಲ್ಲಿ ಉಳಿದಿರುವವರನ್ನು ತಳ್ಳಿಬಿಟ್ಟು ಶತ್ರುಗಳ ಕೈಗೆ ಒಪ್ಪಿಸುವೆನು. ಅವರ ಶತ್ರುಗಳೆಲ್ಲಾ ಬಂದು ಅವರನ್ನು ಸುಲಿದು ಸೂರೆಮಾಡುವರು.",
			"15": "<sup>15</sup> ಅವರು ಈಜಿಪ್ಟ್ ದೇಶವನ್ನು ಬಿಟ್ಟುಬಂದ ಪಿತೃಗಳ ಕಾಲ ಮೊದಲ್ಗೊಂಡು ಇಂದಿನವರೆಗೂ ತಮ್ಮ ಪಾಪಗಳಿಂದ ನನ್ನನ್ನು ರೇಗಿಸುತ್ತಾ ಬಂದದ್ದೇ ಇದಕ್ಕೆ ಕಾರಣ,” ಎಂದು ಹೇಳಿಸಿದರು.",
			"16": "<sup>16</sup> ಮನಸ್ಸೆಯು ಯೆಹೂದ್ಯರನ್ನು ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ಕೆಟ್ಟದ್ದಾಗಿರುವುದನ್ನು ಮಾಡಲು ಪ್ರೇರಿಸಿದ್ದಲ್ಲದೆ, ನಿರಪರಾಧಿಗಳ ವಧೆಯಿಂದ ಜೆರುಸಲೇಮನ್ನೆಲ್ಲಾ ರಕ್ತಮಯವಾಗಿಸಿದನು.",
			"17": "<sup>17</sup> ಮನಸ್ಸೆಯ ಉಳಿದ ಚರಿತ್ರೆ ಅವನ ಕಾರ್ಯಕಲಾಪಗಳು ಹಾಗು ಅವನು ಮಾಡಿದ ಪಾಪಕೃತ್ಯಗಳು ಜುದೇಯ ರಾಜರ ಇತಿಹಾಸ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಲಿಖಿತವಾಗಿವೆ.",
			"18": "<sup>18</sup> ಅವನು ಸತ್ತು ಪಿತೃಗಳ ಬಳಿ ಸೇರಿದಾಗ ಅವನ ಶವವನ್ನು ಅವನ ಅರಮನೆಯಿರುವ ಉಜ್ಜನ ವನದಲ್ಲಿ ಸಮಾಧಿ ಮಾಡಿದರು. ಅವನ ಸ್ಥಾನದಲ್ಲಿ ಅವನ ಮಗ ಆಮೋನನು ಅರಸನಾದನು.",
			"19": "<sup>19</sup> ಆಮೋನನು ಅರಸನಾದಾಗ ಇಪ್ಪತ್ತೆರಡು ವರ್ಷ ವಯಸ್ಸು. ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಎರಡು ವರ್ಷ ಆಳಿದನು. ಯೊಟ್ಟಾ ಊರಿನವಳೂ ಹಾರೂಚನ ಮಗಳೂ ಆದ ಮೆಷುಲ್ಲೆಮೆತ್ ಎಂಬಾಕೆ ಅವನ ತಾಯಿ.",
			"20": "<sup>20</sup> ಅವನು ತನ್ನ ತಂದೆ ಮನಸ್ಸೆಯ ದುರ್ಮಾರ್ಗದಲ್ಲೇ ನಡೆದು ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾದನು.",
			"21": "<sup>21</sup> ತನ್ನ ಪೂರ್ವಜರ ದೇವರಾದ ಸರ್ವೇಶ್ವರನನ್ನೂ ಅವರು ಮೆಚ್ಚುವ ಮಾರ್ಗವನ್ನೂ ಬಿಟ್ಟು ತನ್ನ ತಂದೆಯ ಮಾರ್ಗದಲ್ಲೇ ನಡೆದನು.",
			"22": "<sup>22</sup> ಅವನು ಪೂಜಿಸಿದ ವಿಗ್ರಹಗಳನ್ನು ತಾನೂ ಪೂಜಿಸಿ ಅವುಗಳಿಗೆ ಅಡ್ಡಬಿದ್ದನು.",
			"23": "<sup>23</sup> ಅವನ ಸೇವಕರು ಅವನಿಗೆ ವಿರುದ್ಧ ಒಳಸಂಚು ಮಾಡಿ ಅವನನ್ನು ಅರಮನೆಯಲ್ಲಿಯೇ ಕೊಂದರು.",
			"24": "<sup>24</sup> ಆದರೆ ನಾಡಿನ ಜನರು ಒಳಸಂಚು ಮಾಡಿದವರನ್ನೆಲ್ಲಾ ಕೊಂದುಹಾಕಿ ಆಮೋನನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗ ಯೋಷೀಯನನ್ನು ಅರಸನನ್ನಾಗಿ ಮಾಡಿದರು.",
			"25": "<sup>25</sup> ಆಮೋನನ ಉಳಿದ ಚರಿತ್ರೆ ಹಾಗು ಅವನ ಕೃತ್ಯಗಳು ಜುದೇಯ ರಾಜರ ಇತಿಹಾಸ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ.",
			"26": "<sup>26</sup> ಅವನ ಶವವನ್ನು ಉಜ್ಜನ ವನದಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಅಲ್ಲಿ ಅವನು ಮೊದಲೇ ತನಗಾಗಿ ಒಂದು ಸಮಾಧಿಯನ್ನು ಸಿದ್ಧಪಡಿಸಿಟ್ಟಿದ್ದನು. ಅವನ ಸ್ಥಾನದಲ್ಲಿ ಅವನ ಮಗ ಯೋಷೀಯನು ಅರಸನಾದನು."
		},
		"22": {
			"1": "<sup>1</sup> ಯೋಷೀಯನು ಅರಸನಾದಾಗ ಅವನಿಗೆ ಎಂಟು ವರ್ಷ ವಯಸ್ಸು. ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಅವನು ಆಳಿದ್ದು ಮೂವತ್ತೊಂದು ವರ್ಷ. ಬೊಚ್ಕತೂರಿನವಳೂ ಅದಾಯನ ಮಗಳೂ ಆದ ಯೆದೀದಾ ಎಂಬಾಕೆ ಅವನ ತಾಯಿ.",
			"2": "<sup>2</sup> ಅವನು ಎಡಕ್ಕಾಗಲಿ ಬಲಕ್ಕಾಗಲಿ ವಾಲದೆ ತನ್ನ ಪೂರ್ವಜ ದಾವೀದನ ಮಾರ್ಗದಲ್ಲಿ ನೇರವಾಗಿ ನಡೆದು ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ಒಳ್ಳೆಯವನಾಗಿದ್ದನು.",
			"3": "<sup>3</sup> ಯೋಷೀಯನು ತನ್ನ ಆಳ್ವಿಕೆಯ ಹದಿನೆಂಟನೆಯ ವರ್ಷದಲ್ಲಿ ಅಚೆಲ್ಯನ ಮಗನೂ ಮೆಷುಲ್ಲಾಮನ ಮೊಮ್ಮಗನೂ ಆದ ಶಾಫಾನನೆಂಬ ಲೇಖಕನನ್ನು ಕರೆದು,",
			"4": "<sup>4</sup> “ನೀನು ಪ್ರಧಾನ ಯಾಜಕ ಹಿಲ್ಕೀಯನ ಬಳಿಗೆ ಹೋಗಿ ಅವನಿಗೆ, ‘ದ್ವಾರಪಾಲಕರು ಸರ್ವೇಶ್ವರನ ಆಲಯಕ್ಕೆ ಬರುವ ಜನರಿಂದ ಸಂಗ್ರಹಿಸಿದ ಹಣವನ್ನು ಲೆಕ್ಕಮಾಡಿ ಆಲಯದ ಕೆಲಸ ಮಾಡಿಸುವ ಮೇಸ್ತ್ರಿಗಳಿಗೆ ಅದನ್ನು ಒಪ್ಪಿಸು,’ ಎಂದು ಹೇಳು.",
			"5": "<sup>5</sup> ಮೇಸ್ತ್ರಿಗಳು ಅದನ್ನು ಸರ್ವೇಶ್ವರನ ಆಲಯದ ದುರಸ್ತುಮಾಡುವ ಬಡಗಿ, ಶಿಲ್ಪಿ, ಉಪ್ಪಾರ, ಮೊದಲಾದವರ ಸಂಬಳಕ್ಕಾಗಿಯೂ ದೇವಾಲಯಕ್ಕೆ ಬೇಕಾದ ಮರ, ಕೆತ್ತಿದ ಕಲ್ಲು, ಇವುಗಳನ್ನು ಕೊಂಡುಕೊಳ್ಳುವುದಕ್ಕಾಗಿಯೂ ಉಪಯೋಗಿಸಲಿ.",
			"7": "<sup>7</sup> ಅವರು ನಂಬಿಗಸ್ತರಾಗಿರುವುದರಿಂದ ಅವರ ವಶಕ್ಕೆ ಕೊಡಲಾಗುವ ಹಣದ ಲೆಕ್ಕವನ್ನು ಕೇಳಬಾರದು,” ಎಂದು ಆಜ್ಞಾಪಿಸಿ ಅವನನ್ನು ಸರ್ವೇಶ್ವರನ ಆಲಯಕ್ಕೆ ಕಳುಹಿಸಿದನು.",
			"8": "<sup>8</sup> ಪ್ರಧಾನ ಯಾಜಕ ಹಿಲ್ಕೀಯನು ಲೇಖಕ ಶಾಫಾನನಿಗೆ, “ಸರ್ವೇಶ್ವರನ ಆಲಯದಲ್ಲಿ ನನಗೆ ಧರ್ಮೋಪದೇಶ ಗ್ರಂಥವು ಸಿಕ್ಕಿರುತ್ತದೆ,” ಎಂದು ಹೇಳಿ ಅದನ್ನು ಅವನ ಕೈಯಲ್ಲಿ ಕೊಟ್ಟನು; ಶಾಫಾನನು ಓದಿದನು.",
			"9": "<sup>9</sup> ಅನಂತರ ಅವನು ಹಿಂದಿರುಗಿ ಅರಸನ ಬಳಿಗೆ ಹೋಗಿ, “ನಿಮ್ಮ ಸೇವಕರು ದೇವಾಲಯಲ್ಲಿ ಸಂಗ್ರಹವಾದ ಹಣವನ್ನು ತೆಗೆದು ದೇವಾಲಯದ ಕೆಲಸವನ್ನು ಮಾಡಿಸುವ ಮೇಸ್ತ್ರಿಗಳಿಗೆ ಒಪ್ಪಿಸಿದರು;",
			"10": "<sup>10</sup> ಇದಲ್ಲದೆ ಯಾಜಕ ಹಿಲ್ಕೀಯನು ನನಗೆ ಒಂದು ಗ್ರಂಥವನ್ನು ಕೊಟ್ಟಿದ್ದಾನೆ,” ಎಂದು ಹೇಳಿ ಅದನ್ನು ಅರಸನ ಮುಂದೆ ಓದಿದನು.",
			"11": "<sup>11</sup> ಅರಸನು ಧರ್ಮೋಪದೇಶ ಗ್ರಂಥವಾಕ್ಯಗಳನ್ನು ಕೇಳಿದಾಗ,",
			"12": "<sup>12</sup> ದುಃಖಾವೇಶದಿಂದ ಬಟ್ಟೆಗಳನ್ನು ಹರಿದುಕೊಂಡು, ಯಾಜಕ ಹಿಲ್ಕೀಯನು ಶಾಫಾನನ ಮಗ ಅಹೀಕಾಮ್, ಮೀಕಾಯನ ಮಗ ಅಕ್ಬೋರ್, ಲೇಖಕ ಶಾಫಾನ್ ಹಾಗು ತನ್ನ ಹಸ್ತಕನಾದ ಅಸಾಯ ಎಂಬುವರಿಗೆ,",
			"13": "<sup>13</sup> “ನಮ್ಮ ಪೂರ್ವಜರು ನಮಗೆ ಸಿಕ್ಕಿರುವ ಈ ಗ್ರಂಥವಾಕ್ಯಗಳಿಗೆ ಕಿವಿಗೊಡದೆ ಹಾಗು ಅವುಗಳನ್ನು ಕೈಕೊಳ್ಳದೆಹೋದುದರಿಂದ ಸರ್ವೇಶ್ವರನ ಉಗ್ರಕೋಪಕ್ಕೆ ಪಾತ್ರರಾಗಿದ್ದೇವೆ. ಆದುದರಿಂದ ನೀವು ನನಗಾಗಿ ಜನರಿಗಾಗಿ ಹಾಗು ಎಲ್ಲಾ ಯೆಹೂದ್ಯರಿಗಾಗಿ ಸರ್ವೇಶ್ವರನ ಬಳಿಗೆ ಹೋಗಿ ಈ ಗ್ರಂಥವಾಕ್ಯಗಳ ವಿಷಯವಾಗಿ ವಿಚಾರಿಸಿರಿ,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"14": "<sup>14</sup> ಆಗ ಯಾಜಕ ಹಿಲ್ಕೀಯನು, ಅಹೀಕಾಮ್, ಅಕ್ಬೋರ್, ಶಾಫಾನ್, ಅಸಾಯ ಎಂಬವರು ಹುಲ್ದ ಎಂಬ ಪ್ರವಾದಿನಿಯ ಬಳಿಗೆ ಹೋಗಿ ಆಕೆಯ ಹತ್ತಿರ ವಿಚಾರಿಸಿದರು. ಹರ್ಹಸನ ಮೊಮ್ಮಗನೂ ತಿಕ್ವನ ಮಗನೂ ರಾಜವಸ್ತ್ರಾಗಾರದ ಅಧಿಪತಿಯೂ ಆಗಿದ್ದ ಶಲ್ಲೂಮನು ಈಕೆಯ ಗಂಡನು. ಈಕೆ ಜೆರುಸಲೇಮಿನ ಎರಡನೇ ಕೇರಿಯಲ್ಲಿ ವಾಸವಾಗಿದ್ದಳು.",
			"15": "<sup>15</sup> ಹುಲ್ದಳು ಅವರಿಗೆ, “ನಿಮ್ಮನ್ನು ಕಳುಹಿಸಿದವರಿಗೆ ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಮಾತುಗಳನ್ನು ತಿಳಿಸಿರಿ;",
			"16": "<sup>16</sup> ‘ಈ ನಾಡಿನ ಮೇಲೆ ಹಾಗು ಜನರ ಮೇಲೆ ಜುದೇಯದ ಅರಸನು ಓದಿಸಿದ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುವ ಎಲ್ಲಾ ಶಿಕ್ಷೆಯನ್ನು ಬರಮಾಡುವೆನು.",
			"17": "<sup>17</sup> ಅವರು ನನ್ನನ್ನು ಬಿಟ್ಟು ಅನ್ಯದೇವತೆಗಳಿಗೆ ಧೂಪಾರತಿಯೆತ್ತಿ ತಮ್ಮ ದುಷ್ಕೃತ್ಯಗಳಿಂದ ನನ್ನನ್ನು ರೇಗಿಸಿದ್ದಾರೆ. ಆದ್ದರಿಂದ ನನ್ನ ಕೋಪಾಗ್ನಿ ಈ ನಾಡಿನ ಮೇಲೆ ಉರಿಯಹತ್ತಿ ಆರಿಹೋಗುವುದೇ ಇಲ್ಲ,’ ಎನ್ನುತ್ತಾರೆ ಸರ್ವೇಶ್ವರ.",
			"18": "<sup>18</sup> ತಾನು ಕೇಳಿದ ವಾಕ್ಯಗಳ ವಿಷಯವಾಗಿ ಸರ್ವೇಶ್ವರನ ಬಳಿಯಲ್ಲಿ ವಿಚಾರಿಸುವುದಕ್ಕೆ ನಿಮ್ಮನ್ನು ಕಳುಹಿಸಿದ ಜುದೇಯದ ಅರಸರಿಗೆ ನೀವು ತಿಳಿಸಬೇಕಾದ ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಮಾತು ಏನೆಂದರೆ,",
			"19": "<sup>19</sup> ‘ನಾನು ಈ ನಾಡನ್ನೂ ನಿವಾಸಿಗಳನ್ನೂ ಶಾಪವಿಸ್ಮಯಗಳಿಗೆ ಗುರಿಮಾಡುವೆನೆಂಬುದನ್ನು ನೀನು ಕೇಳಿದಾಗ, ದುಃಖಪಟ್ಟು ನನ್ನ ಮುಂದೆ ತಗ್ಗಿಸಿಕೊಂಡದ್ದರಿಂದಲೂ, ಬಟ್ಟೆಗಳನ್ನು ಹರಿದುಕೊಂಡು ಕಣ್ಣೀರು ಸುರಿಸಿದ್ದರಿಂದಲೂ ನಿನ್ನನ್ನು ಲಕ್ಷಿಸಿದೆನು.",
			"20": "<sup>20</sup> ನಾನು ಈ ನಾಡಿಗೆ ಬರಮಾಡುವ ಶಿಕ್ಷೆಗಳಲ್ಲಿ ನೀನು ಒಂದನ್ನೂ ನೋಡದೆ ಸಮಾಧಾನದಿಂದ ಮೃತಿಹೊಂದಿ ಸಮಾಧಿ ಸೇರುವಂತೆ ಅನುಗ್ರಹಿಸುವೆನು,’ ಎಂಬುದಾಗಿ ತಿಳಿಸಿರಿ,” ಎಂದು ಹೇಳಿದಳು. ಅವರು ಹಿಂದಿರುಗಿ ಬಂದು ಅರಸನಿಗೆ ಆ ಮಾತುಗಳನ್ನು ತಿಳಿಸಿದರು."
		},
		"23": {
			"1": "<sup>1</sup> ಅನಂತರ ಅರಸನು ದೂತರ ಮುಖಾಂತರ ಜೆರುಸಲೇಮಿನ ಮತ್ತು ಜುದೇಯ ಪ್ರಾಂತ್ಯದ ಬೇರೆ ಎಲ್ಲಾ ಊರುಗಳ ಹಿರಿಯರನ್ನು ತನ್ನ ಬಳಿಗೆ ಕರೆಯಿಸಿದನು.",
			"2": "<sup>2</sup> ಜೆರುಸಲೇಮಿನವರನ್ನೂ ಪ್ರವಾದಿಗಳನ್ನೂ ಬೇರೆ ಎಲ್ಲಾ ಯೆಹೂದ್ಯರನ್ನೂ ಯಾಜಕರನ್ನೂ ಪ್ರವಾದಿಗಳನ್ನೂ ಕರೆದುಕೊಂಡು ಸರ್ವೇಶ್ವರನ ಆಲಯಕ್ಕೆ ಹೋದನು. ಚಿಕ್ಕವರೂ ದೊಡ್ಡವರೂ ಎಲ್ಲರೂ ಅವನನ್ನು ಹಿಂಬಾಲಿಸಿದರು. ಅಲ್ಲಿ ಅವನು ಎಲ್ಲರಿಗೂ ಕೇಳಿಸುವಂತೆ ಸರ್ವೇಶ್ವರನ ಆಲಯದಲ್ಲಿ ಸಿಕ್ಕಿದ ನಿಬಂಧನ ಗ್ರಂಥವನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ಓದಿಸಿದನು.",
			"3": "<sup>3</sup> ಕಂಬದ ಬಳಿಯಲ್ಲಿ ನಿಂತು ತಾನು ಸರ್ವೇಶ್ವರನ ಮಾರ್ಗದಲ್ಲಿ ನಡೆಯುವುದಾಗಿಯೂ ಆತನ ಆಜ್ಞಾನಿಯಮವಿಧಿಗಳನ್ನು ಪೂರ್ಣಮನಸ್ಸಿನಿಂದಲೂ ಪೂರ್ಣಪ್ರಾಣದಿಂದಲೂ ಕೈಕೊಳ್ಳುವುದಾಗಿಯೂ ಮತ್ತು ನಿಬಂಧನ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುವ ಎಲ್ಲ ವಾಕ್ಯಗಳನ್ನು ನೆರವೇರಿಸುವುದಾಗಿಯೂ ಸರ್ವೇಶ್ವರನಿಗೆ ಪ್ರಮಾಣ ಮಾಡಿದನು. ಎಲ್ಲಾ ಜನರೂ ಹಾಗೆಯೇ ಪ್ರಮಾಣ ಮಾಡಿದರು.",
			"4": "<sup>4</sup> ತರುವಾಯ ಅವನು ಪ್ರಧಾನ ಯಾಜಕ ಹಿಲ್ಕೀಯನ, ಮುಖ್ಯ ಯಾಜಕರ ಹಾಗು ದ್ವಾರಪಾಲಕರ ಮುಖಾಂತರ ಬಾಳ್, ಅಶೇರ ಎಂಬ ದೇವತೆಗಳಿಗಾಗಿ ಹಾಗು ಆಕಾಶಸೈನ್ಯಗಳಿಗಾಗಿ ಉಪಯೋಗಿಸುತ್ತಿದ್ದ ಎಲ್ಲಾ ವಸ್ತುಗಳನ್ನು ಸರ್ವೇಶ್ವರನ ಆಲಯದಿಂದ ತರಿಸಿ, ಜೆರುಸಲೇಮಿನ ಹೊರಗಿರುವ ಕಿದ್ರೋನ್ ಬೈಲಿನಲ್ಲಿ ಅವುಗಳನ್ನು ಸುಡಿಸಿ, ಆ ಬೂದಿಯನ್ನು ಬೇತೇಲಿಗೆ ಕಳುಹಿಸಿದನು.",
			"5": "<sup>5</sup> ಅದಲ್ಲದೆ, ಜುದೇಯ ಪ್ರಾಂತ್ಯದ ಪಟ್ಟಣಗಳಲ್ಲೂ ಜೆರುಸಲೇಮಿನ ಸುತ್ತಣ ಪ್ರದೇಶದಲ್ಲೂ ಇದ್ದ ಪೂಜಾಸ್ಥಳಗಳ ವಿಗ್ರಹಗಳಿಗೂ ಬಾಳ್ ದೇವತೆಗೂ ಸೂರ್ಯ ಚಂದ್ರ ನಕ್ಷತ್ರಗಳೆನಿಸಿಕೊಳ್ಳುವ ಆಕಾಶಸೈನ್ಯಕ್ಕೂ ಧೂಪಾರತಿಯೆತ್ತುವುದಕ್ಕಾಗಿ ಜುದೇಯದ ರಾಜರಿಂದ ನೇಮಿತರಾಗಿದ್ದ ಎಲ್ಲಾ ಪೂಜಾರಿಗಳನ್ನು ತೆಗೆದುಹಾಕಿದನು.",
			"6": "<sup>6</sup> ಸರ್ವೇಶ್ವರನ ಆಲಯದಲ್ಲಿದ್ದ ಅಶೇರವಿಗ್ರಹಸ್ತಂಭವನ್ನು ತರಿಸಿ, ಜೆರುಸಲೇಮಿನ ಹೊರಗಿರುವ ಕಿದ್ರೋನ್ ಹಳ್ಳಕ್ಕೆ ತೆಗೆದುಕೊಂಡು ಹೋಗಿ, ಅಲ್ಲಿ ಅದನ್ನು ಸುಡಿಸಿ, ಪುಡುಪುಡಿಮಾಡಿಸಿ, ಆ ಪುಡಿಯನ್ನು ಸಾರ್ವಜನಿಕ ಸ್ಮಶಾನದಲ್ಲಿ ಹಾಕಿಸಿದನು.",
			"7": "<sup>7</sup> ಸರ್ವೇಶ್ವರನ ಆಲಯದ ಪ್ರಾಕಾರದೊಳಗಿದ್ದ ವೇಶ್ಯಾವೃತ್ತಿಯವರ ಮನೆಗಳನ್ನು ಕೆಡವಿಸಿದನು. ಆ ಮನೆಗಳಲ್ಲಿ ಸ್ತ್ರೀಯರು ಅಶೇರ ದೇವತೆಗಾಗಿ ಬಟ್ಟೆಬರೆಗಳನ್ನು ನೇಯುತ್ತಿದ್ದರು.",
			"8": "<sup>8</sup> ಅನಂತರ ಗೆಬದಿಂದ ಬೇರ್ಷೆಬದವರೆಗಿರುವ ಜುದೇಯದ ಪಟ್ಟಣಗಳ ಯಾಜಕರನ್ನೆಲ್ಲಾ ಕರೆಯಿಸಿಕೊಂಡು ಅವರು ಧೂಪಸುಡುತ್ತಿದ್ದ ಪೂಜಾಸ್ಥಳಗಳನ್ನು ಹೊಲೆಮಾಡಿದನು. ಪುರಾಧಿಕಾರಿ ಯೆಹೋಶುವನ ಬಾಗಿಲಿನ ಎಡಗಡೆಯಲ್ಲಿದ್ದ ದ್ವಾರದೇವತೆಗಳ ಪೂಜಾ ಸ್ಥಳಗಳನ್ನು ಕೆಡವಿಬಿಟ್ಟನು.",
			"9": "<sup>9</sup> ಆ ಪೂಜಾಸ್ಥಳಗಳಿಂದ ಬಂದ ಯಾಜಕರಿಗೆ, ಇತರ ಯಾಜಕರೊಡನೆ ಹುಳಿಯಿಲ್ಲದ ರೊಟ್ಟಿಗಳಲ್ಲಿ ಪಾಲುಸಿಕ್ಕುತ್ತಿದ್ದರೂ ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಇದ್ದ ಸರ್ವೇಶ್ವರನ ಬಲಿಪೀಠದ ಸಮೀಪಕ್ಕೆ ಹೋಗುವುದಕ್ಕೆ ಅಪ್ಪಣೆಯಿರಲಿಲ್ಲ.",
			"10": "<sup>10</sup> ತಮ್ಮ ಗಂಡುಹೆಣ್ಣುಮಕ್ಕಳನ್ನು ಯಾರೂ ಮೋಲೆಕನಿಗಾಗಿ ಬಲಿಯಗ್ನಿಪರೀಕ್ಷೆಗೆ ಈಡುಮಾಡದಂತೆ ಬೆನ್ಹಿನ್ನೋಮ್ ಕಣಿವೆಯಲ್ಲಿದ್ದ ತೋಫೆತ್ ಎಂಬ ಸ್ಥಳವನ್ನು ಹೊಲೆಮಾಡಿದನು.",
			"11": "<sup>11</sup> ಜುದೇಯದ ರಾಜರು ಸೂರ್ಯದೇವತೆಗಾಗಿ ಪ್ರತಿಷ್ಠಿಸಿದ್ದ ಕುದುರೆಗಳನ್ನು ತೆಗೆದುಹಾಕಿ ರಥಗಳನ್ನು ಸುಡಿಸಿಬಿಟ್ಟನು. ಅವು ಸರ್ವೇಶ್ವರನ ಆಲಯದ ಕಂಚುಕಿಯಾದ ನಾತಾನ್ ಮೆಲೆಕನ ಕೋಣೆಯ ಹತ್ತಿರ ಇಡಲಾಗಿದ್ದವು.",
			"12": "<sup>12</sup> ಇದಲ್ಲದೆ ಜುದೇಯದ ಅರಸರು ದೇವಸ್ಥಾನದ ಮಾಳಿಗೆಯ ಮೇಲೆ ಆಹಾಜನ ಉಪ್ಪರಿಗೆಯ ಹತ್ತಿರ ಕಟ್ಟಿಸಿದ್ದ ಬಲಿಪೀಠಗಳನ್ನು ಹಾಗು ಮನಸ್ಸೆಯು ಸರ್ವೇಶ್ವರನ ಆಲಯದ ಎರಡು ಪ್ರಾಕಾರಗಳಲ್ಲಿ ಕಟ್ಟಿಸಿದ್ದ ಬಲಿಪೀಠಗಳನ್ನು ಕೆಡವಿ ಪುಡಿಪುಡಿಮಾಡಿ ಅವುಗಳ ಧೂಳನ್ನು ಕಿದ್ರೋನ್ ಹಳ್ಳದಲ್ಲಿ ಹಾಕಿಸಿದನು.",
			"13": "<sup>13</sup> ಇಸ್ರಯೇಲರ ಅರಸ ಸೊಲೊಮೋನನು ಸಿದೋನ್ಯರ ಅಷ್ಟೋರೆತ್, ಮೋವಾಬ್ಯರ ಕೆಮೋಷ್, ಅಮ್ಮೋನಿಯರ ಮಿಲ್ಕೋಮ್ ಎಂಬ ಅಸಹ್ಯ ವಿಗ್ರಹಗಳಿಗಾಗಿ ಜೆರುಸಲೇಮಿನ ಎದುರಿನಲ್ಲೂ ಎಘ್ನಪರ್ವತದ ದಕ್ಷಿಣದಿಕ್ಕಿನಲ್ಲೂ ಸ್ಥಾಪಿಸಿದ ಪೂಜಾಸ್ಥಳಗಳನ್ನು ಇವನು ಹೊಲೆಮಾಡಿದನು.",
			"14": "<sup>14</sup> ಕಲ್ಲುಗಂಬಗಳನ್ನು ಒಡೆದುಹಾಕಿ, ಅಶೇರ ವಿಗ್ರಹಸ್ತಂಭಗಳನ್ನು ಕಡಿದುಬಿಟ್ಟು, ಅವುಗಳಿದ್ದ ಸ್ಥಳವನ್ನು ಮನುಷ್ಯರ ಎಲುಬುಗಳಿಂದ ತುಂಬಿಸಿದನು.",
			"15": "<sup>15</sup> ಇದಲ್ಲದೆ ಇಸ್ರಯೇಲರನ್ನು ಪಾಪಕ್ಕೆ ಪ್ರೇರಿಸಿದ ನೆಬಾಟನ ಮಗ ಯಾರೊಬ್ಬಾಮನು ಬೇತೇಲಿನಲ್ಲಿ ಏರ್ಪಡಿಸಿದ ಪೂಜಾಸ್ಥಳವನ್ನೂ ಅದರಲ್ಲಿದ್ದ ಬಲಿಪೀಠವನ್ನೂ ಕೆಡವಿಬಿಟ್ಟನು. ಪೂಜಾಸ್ಥಳವನ್ನೂ ಅದರಲ್ಲಿದ್ದ ಅಶೇರ ವಿಗ್ರಹಸ್ತಂಭವನ್ನೂ ಸುಟ್ಟು ಬೂದಿಮಾಡಿದನು.",
			"16": "<sup>16</sup> ಅವನು ಹಿಂದಿರುಗಿ ನೋಡಿದಾಗ ಅಲ್ಲಿನ ಗುಡ್ಡದಲ್ಲಿ ಸಮಾಧಿಗಳನ್ನು ಕಂಡು ಅವುಗಳಲ್ಲಿದ್ದ ಎಲುಬುಗಳನ್ನು ತರಿಸಿ, ಆ ಬಲಿಪೀಠದ ಮೇಲೆ ಸುಡಿಸಿ ಅದನ್ನು ಹೊಲೆಮಾಡಿದನು. ಹೀಗೆ ದೈವಪುರುಷನೊಬ್ಬನು ಪ್ರಕಟಿಸಿದ ಸರ್ವೇಶ್ವರನ ವಾಕ್ಯ ನೆರವೇರಿತು.",
			"17": "<sup>17</sup> ಇದಲ್ಲದೆ, ಅವನು ಒಂದು ಸಮಾಧಿ ಶಿಲೆಯನ್ನು ಕಂಡು ಅದೇನೆಂದು ವಿಚಾರಿಸಿದನು. ಆ ಊರಿನವರು, “ನೀವು ಈಗ ಬೇತೇಲಿನ ಬಲಿಪೀಠಕ್ಕೆ ಮಾಡಿದ್ದನ್ನು ಮುಂತಿಳಿಸಿದ್ದ ಜುದೇಯ ನಾಡಿನವನಾದ ದೈವಪುರುಷನ ಸಮಾಧಿ ಇದು,” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"18": "<sup>18</sup> ಆಗ ಅವನು, “ಬಿಡಿ, ಆ ಪುರುಷನ ಎಲುಬುಗಳನ್ನು ಯಾರೂ ಮುಟ್ಟಬಾರದು,” ಎಂದು ಹೇಳಿದನು. ಆದುದರಿಂದ ಅವರು ಅವನ ಮತ್ತು ಸಮಾರಿಯ ನಾಡಿನ ಪ್ರವಾದಿಯ ಎಲುಬುಗಳನ್ನು ಮುಟ್ಟಲಿಲ್ಲ.",
			"19": "<sup>19</sup> ಇಸ್ರಯೇಲ್ ರಾಜರು ಸಮಾರಿಯ ಪಟ್ಟಣಗಳಲ್ಲಿ ಕಟ್ಟಿಸಿದಂಥ ಹಾಗು ಸರ್ವೇಶ್ವರನ ಕೋಪಕ್ಕೆ ಕಾರಣವಾಗಿದ್ದಂಥ ಪೂಜಾಸ್ಥಳಗಳಲ್ಲಿದ್ದ ಗುಡಿಗಳಿಗೆ ಬೇತೇಲಿಗಾದ ಗತಿಯೇ ಆಯಿತು.",
			"20": "<sup>20</sup> ಯೋಷೀಯನು ಅವುಗಳನ್ನು ಕೆಡವಿಬಿಟ್ಟು, ಅವುಗಳ ಪೂಜಾರಿಗಳನ್ನು ಬಲಿಪೀಠಗಳ ಮೇಲೆ ವಧಿಸಿ ಮನುಷ್ಯರ ಎಲುಬುಗಳನ್ನು ಅವುಗಳ ಮೇಲೆ ಸುಡಿಸಿ ಜೆರುಸಲೇಮಿಗೆ ಹಿಂದಿರುಗಿ ಹೋದನು.",
			"21": "<sup>21</sup> ಅನಂತರ ಅರಸನು ಜನರೆಲ್ಲರಿಗೆ, ನಿಬಂಧನ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುವ ಪ್ರಕಾರ ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೋಸ್ಕರ ಪಾಸ್ಕಹಬ್ಬವನ್ನು ಆಚರಿಸಬೇಕು ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"22": "<sup>22</sup> ಇಂಥ ಪಾಸ್ಕಹಬ್ಬವು ಇಸ್ರಯೇಲರನ್ನು ಪಾಲಿಸಿದ ನ್ಯಾಯಸ್ಥಾಪಕರ ಕಾಲದಲ್ಲಾಗಲಿ ಇಸ್ರಯೇಲರ ಮತ್ತು ಜುದೇಯದ ಅರಸರ ಕಾಲದಲ್ಲಾಗಲಿ ನಡೆದಿರಲಿಲ್ಲ.",
			"23": "<sup>23</sup> ಈ ಹಬ್ಬವನ್ನು ಅರಸ ಯೋಷೀಯನ ಆಳ್ವಿಕೆಯ ಹದಿನೆಂಟನೆಯ ವರ್ಷದಲ್ಲಿ ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಸರ್ವೇಶ್ವರನಿಗೋಸ್ಕರ ಆಚರಿಸಲಾಯಿತು.",
			"24": "<sup>24</sup> ಇದಲ್ಲದೆ, ಅವನು ಭೂತಪ್ರೇತಗಳನ್ನು ವಿಚಾರಿಸುವವರನ್ನೂ ಬೈತಾಳಿಕರನ್ನೂ ಜೆರುಸಲೇಮ್, ಜುದೇಯ ಪ್ರಾಂತ್ಯಗಳಲ್ಲಿದ್ದ ಎಲ್ಲಾ ತೆರಫೀಮ್ ಎಂಬ ಮನೆದೇವರುಗಳು, ಮೂರ್ತಿಗಳು ಹಾಗು ಎಲ್ಲ ಅಸಹ್ಯ ವಿಗ್ರಹಗಳನ್ನು ತೆಗೆದುಹಾಕಿ, ಯಾಜಕ ಹಿಲ್ಕೀಯನಿಗೆ ಸರ್ವೇಶ್ವರನ ಆಲಯದಲ್ಲಿ ಸಿಕ್ಕಿದ ಧರ್ಮೋಪದೇಶ ಗ್ರಂಥದ ವಾಕ್ಯಗಳನ್ನು ನೆರವೇರಿಸಿದನು.",
			"25": "<sup>25</sup> ಸರ್ವೇಶ್ವರನಿಗೆ ಅಭಿಮುಖನಾಗಿ, ಪೂರ್ಣಮನಸ್ಸಿನಿಂದಲೂ ಪೂರ್ಣಪ್ರಾಣದಿಂದಲೂ ಪೂರ್ಣಶಕ್ತಿಯಿಂದಲೂ ಮೋಶೆಯ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಅನುಸರಿಸಿದ ಈ ಯೋಷೀಯನಿಗೆ ಸಮಾನನಾದ ಅರಸನು ಹಿಂದೆಯೂ ಅನಂತರವೂ ಇರಲಿಲ್ಲ.",
			"26": "<sup>26</sup> ಆದರೂ ಮನಸ್ಸೆಯ ದುಷ್ಕೃತ್ಯಗಳ ನಿಮಿತ್ತ ಯೆಹೂದ್ಯರ ಮೇಲಿದ್ದ ಸರ್ವೇಶ್ವರನ ಉಗ್ರಕೋಪ ಇಳಿದಿರಲಿಲ್ಲ.",
			"27": "<sup>27</sup> ಸರ್ವೇಶ್ವರ, “ಇಸ್ರಯೇಲರನ್ನು ತೆಗೆದುಹಾಕಿದಂತೆ ಯೆಹೂದ್ಯರನ್ನೂ ನನ್ನ ಸನ್ನಿಧಿಯಿಂದ ತೆಗೆದುಹಾಕುವೆನು; ನಾನು ಆರಿಸಿಕೊಂಡ ಜೆರುಸಲೇಮ್ ಪಟ್ಟಣವನ್ನೂ ನನ್ನ ನಾಮಮಹತ್ತಿಗಾಗಿ ಸ್ವೀಕರಿಸಿಕೊಂಡಾ ದೇವಾಲಯವನ್ನೂ ತಿರಸ್ಕರಿಸುವೆನು,” ಎಂದು ಹೇಳಿದರು.",
			"28": "<sup>28</sup> ಯೋಷೀಯನ ಉಳಿದ ಚರಿತ್ರೆ ಹಾಗು ಅವನ ಕಾರ್ಯಕಲಾಪಗಳು ಜುದೇಯ ರಾಜರ ಇತಿಹಾಸ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತದೆ.",
			"29": "<sup>29</sup> ಅವನ ಕಾಲದಲ್ಲಿ ಈಜಿಪ್ಟಿನ ಅರಸ ಫರೋಹ ನೆಕೋ ಎಂಬವನು ಅಸ್ಸೀರಿಯದ ಅರಸನಿಗೆ ವಿರುದ್ಧ ಯುದ್ಧಮಾಡುವುದಕ್ಕಾಗಿ ಯೂಫ್ರಟಿಸ್ ನದಿಯ ಬಳಿಗೆ ಹೋದನು. ಅವನು ಅಲ್ಲಿಗೆ ಹೋಗುತ್ತಿರುವಾಗ ಯೋಷೀಯನು ಮೆಗಿದ್ದೋವಿನಲ್ಲಿ ಅವನನ್ನು ಎದುರುಗೊಂಡು, ಕೂಡಲೆ ಅವನಿಂದ ಹತನಾದನು.",
			"30": "<sup>30</sup> ಅವನ ಸೇವಕರು ಅವನ ಶವವನ್ನು ರಥದಲ್ಲಿ ಹಾಕಿಕೊಂಡು ಮೆಗಿದ್ದೋವಿನಿಂದ ಜೆರುಸಲೇಮಿಗೆ ತಂದು ಅವನ ಸ್ವಂತ ಸಮಾಧಿಯಲ್ಲಿ ಇಟ್ಟರು. ಜನರು ಅವನ ಸ್ಥಾನದಲ್ಲಿ ಅವನ ಮಗ ಯೆಹೋವಾಹಾಜನನ್ನು ಅಭಿಷೇಕಿಸಿ ಅರಸನನ್ನಾಗಿ ಮಾಡಿದರು.",
			"31": "<sup>31</sup> ಯೆಹೋವಾಹಾಜನು ಅರಸನಾದಾಗ ಅವನಿಗೆ ಇಪ್ಪತ್ತಮೂರು ವರ್ಷ ವಯಸ್ಸು. ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಮೂರು ತಿಂಗಳು ಆಳಿದನು. ಲಿಬ್ನದವಳೂ ಯೆರೆಮೀಯನ ಮಗಳೂ ಆದ ಹಮೂಟಲ್ ಎಂಬಾಕೆ ಇವನ ತಾಯಿ.",
			"32": "<sup>32</sup> ಇವನು ತನ್ನ ಪೂರ್ವಜರಂತೆ ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾದನು.",
			"33": "<sup>33</sup> ಫರೋಹ ನೆಕೋವನು ಇವನನ್ನು ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಬಹುದಿನಗಳವರೆಗೆ ಆಳಗೊಡಲಿಲ್ಲ. ಅವನು ಹಮಾತ್ ದೇಶದ ದಿಬ್ಲಾ ಎಂಬ ಊರಲ್ಲಿ ಇವನನ್ನು ಸೆರೆಯಲ್ಲಿಟ್ಟನು. ಯೆಹೂದ್ಯರು ಅವನಿಗೆ ಮೂರು ಸಾವಿರದ ನಾಲ್ಕುನೂರು ಕಿಲೋಗ್ರಾಂ ಬೆಳ್ಳಿಯನ್ನೂ ಮೂವತ್ತನಾಲ್ಕು ಕಿಲೋಗ್ರಾಂ ಬಂಗಾರವನ್ನೂ ದಂಡ ತೆರಬೇಕಾಯಿತು.",
			"34": "<sup>34</sup> ಫರೋಹ ನೆಕೋವನು, ಯೋಷೀಯನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗ ಎಲ್ಯಾಕೀಮನನ್ನು ಅರಸನನ್ನಾಗಿ ನೇಮಿಸಿ ಅವನಿಗೆ ಯೆಹೋಯಾಕೀಮನೆಂಬ ಹೊಸ ಹೆಸರನ್ನಿಟ್ಟು, ಯೆಹೋವಾಹಾಜನನ್ನು ಈಜಿಪ್ಟ್ ದೇಶಕ್ಕೆ ತೆಗೆದುಕೊಂಡು ಹೋದನು; ಯೆಹೋವಾಹಾಜನು ಅಲ್ಲೆ ಮೃತನಾದನು.",
			"35": "<sup>35</sup> ಫರೋಹನು ಗೊತ್ತುಮಾಡಿದಷ್ಟು ಬೆಳ್ಳಿಬಂಗಾರವನ್ನು ಕೊಡುವುದಕ್ಕಾಗಿ ಯೆಹೋಯಾಕೀಮನು ತನ್ನ ನಾಡಿನಲ್ಲಿ ತೆರಿಗೆಯನ್ನು ವಿಧಿಸಿದನು. ತೆರಿಗೆ ಕೊಡಬೇಕಾದ ಪ್ರತಿಯೊಬ್ಬನು ತನಗೆ ನೇಮಕವಾದಷ್ಟು ಬೆಳ್ಳಿಬಂಗಾರವನ್ನು ನಾಡಿನ ಜನರಿಂದ ಕೂಡಿಸಿ ಫರೋಹನಿಗೆ ತಂದೊಪ್ಪಿಸಿದನು.",
			"36": "<sup>36</sup> ಯೆಹೋಯಾಕೀಮನು ಅರಸನಾದಾಗ ಅವನಿಗೆ ಇಪ್ಪತ್ತೈದು ವರ್ಷ ವಯಸ್ಸು. ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಹನ್ನೊಂದು ವರ್ಷ ಆಳಿದನು. ರೂಮದವಳೂ ಪೆದಾಯನ ಮಗಳೂ ಆದ ಜೆಬೂದಾ ಎಂಬಾಕೆ ಅವನ ತಾಯಿ.",
			"37": "<sup>37</sup> ಅವನು ತನ್ನ ಪೂರ್ವಜರಂತೆ ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾಗಿದ್ದನು. ಅವನ ಕಾಲದಲ್ಲಿ ಬಾಬಿಲೋನಿಯದ ಅರಸ ನೆಬೂಕದ್ನೆಚ್ಚರನು ಬಂದನು."
		},
		"24": {
			"1": "<sup>1</sup> ಯೆಹೋಯಾಕೀಮನು ಅವನಿಗೆ ಅಧೀನನಾಗಿ ಮೂರು ವರ್ಷಗಳಾದ ನಂತರ ಅವನಿಗೆ ವಿರುದ್ಧ ದಂಗೆಯೆದ್ದನು.",
			"2": "<sup>2</sup> ಸರ್ವೇಶ್ವರ, ತನ್ನ ದಾಸರಾದ ಪ್ರವಾದಿಗಳ ಮುಖಾಂತರ ಮುಂತಿಳಿಸಿದ ಪ್ರಕಾರ ಜುದೇಯ ರಾಜ್ಯವನ್ನು ಹಾಳುಮಾಡುವುದಕ್ಕಾಗಿ ಬಾಬಿಲೋನಿಯ, ಸಿರಿಯ, ಮೋಬಾವ, ಅಮ್ಮೋನ್ ದೇಶೀಯರಾದ ಸುಲಿಗೆಗಾರರನ್ನು ಕಳುಹಿಸಿದರು.",
			"3": "<sup>3</sup> ನಿರಪರಾಧಿಗಳ ಹತ್ಯೆಯಿಂದ ಜೆರುಸಲೇಮನ್ನು ರಕ್ತಮಯವಾಗಿಸಿದ್ದ ಮನಸ್ಸೆಯ ದುಷ್ಕೃತ್ಯಗಳನ್ನು ಸರ್ವೇಶ್ವರ ಕ್ಷಮಿಸದೆ ಹೋದರು;",
			"4": "<sup>4</sup> ಅವುಗಳಿಗಾಗಿ ಯೆಹೂದ್ಯರನ್ನು ತನ್ನ ಸನ್ನಿಧಿಯಿಂದ ತೆಗೆದುಹಾಕಬೇಕೆಂದಿದ್ದುದರಿಂದ ಸರ್ವೇಶ್ವರನ ಅಪ್ಪಣೆಯ ಪ್ರಕಾರ ಈ ಶಿಕ್ಷೆ ಅವರಿಗೆ ವಿಧಿಸಲಾಯಿತು.",
			"5": "<sup>5</sup> ಯೆಹೋಯಾಕೀಮನ ಉಳಿದ ಚರಿತ್ರೆ ಹಾಗು ಕಾರ್ಯಕಲಾಪಗಳು ಜುದೇಯ ರಾಜರ ಇತಿಹಾಸ ಎಂಬ ಗ್ರಂಥದಲ್ಲಿ ಲಿಖಿತವಾಗಿವೆ.",
			"6": "<sup>6</sup> ಅವನು ಮೃತನಾಗಿ ಪಿತೃಗಳ ಬಳಿ ಸೇರಿದನು. ಅವನ ಸ್ಥಾನದಲ್ಲಿ ಅವನ ಮಗ ಯೆಹೋಯಾಖೀನನು ಅರಸನಾದನು.",
			"7": "<sup>7</sup> ಬಾಬಿಲೋನಿನ ಅರಸನು ಈಜಿಪ್ಟಿನ ಪ್ರವಾಹಕ್ಕೂ ಯೂಫ್ರಟಿಸ್ ನದಿಗೂ ಮಧ್ಯದಲ್ಲಿದ್ದ ಈಜಿಪ್ಟ್ ರಾಜನ ಎಲ್ಲಾ ದೇಶವನ್ನು ಸ್ವಾಧೀನ ಮಾಡಿಕೊಂಡನು. ಅಂದಿನಿಂದ ಈಜಿಪ್ಟಿನ ಅರಸನು ತನ್ನ ದೇಶದಿಂದ ಈಚೆಗೆ ಬರಲೇ ಇಲ್ಲ.",
			"8": "<sup>8</sup> ಯೆಹೋಯಾಖೀನನು ಅರಸನಾದಾಗ ಹದಿನೆಂಟು ವರ್ಷದವನಾಗಿದ್ದು ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಮೂರು ತಿಂಗಳು ಆಳಿದನು. ಜೆರುಸಲೇಮಿನ ಎಲ್ನಾತಾನನ ಮಗಳಾದ ನೆಹುಷ್ಟಾ ಎಂಬಾಕೆ ಇವನ ತಾಯಿ.",
			"9": "<sup>9</sup> ಇವನು ತನ್ನ ತಂದೆಯಂತೆ ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾಗಿದ್ದನು.",
			"10": "<sup>10</sup> ಅವನ ಕಾಲದಲ್ಲಿ ಬಾಬಿಲೋನಿನ ಅರಸ ನೆಬೂಕದ್ನೆಚ್ಚರನ ಸೈನ್ಯದವರು ಜೆರುಸಲೇಮಿಗೆ ದಂಡೆತ್ತಿ ಬಂದು ಅದಕ್ಕೆ ಮುತ್ತಿಗೆ ಹಾಕಿದರು.",
			"11": "<sup>11</sup> ಅಷ್ಟರಲ್ಲಿ ನೆಬೂಕದ್ನೆಚ್ಚರನೂ ಬಂದನು.",
			"12": "<sup>12</sup> ಆಗ ಜುದೇಯದ ಅರಸ ಯೆಹೋಯಾಖೀನನು, ತನ್ನ ತಾಯಿ, ಪರಿವಾರದವರು, ಸೇನಾಪತಿಗಳು, ಕಂಚುಕಿಗಳು ಇವರೊಡನೆ ಅವನ ಬಳಿಗೆ ಹೋದನು. ಅವನು ಇವನನ್ನು ತನ್ನ ಆಳ್ವಿಕೆಯ ಎಂಟನೆಯ ವರ್ಷದಲ್ಲಿ ಸೆರೆಹಿಡಿದನು.",
			"13": "<sup>13</sup> ಇದಲ್ಲದೆ, ಸರ್ವೇಶ್ವರ ಮುಂತಿಳಿಸಿದ ಪ್ರಕಾರ, ಅವನು ಸರ್ವೇಶ್ವರನ ಆಲಯದ ಮತ್ತು ಅರಮನೆಯ ಭಂಡಾರಗಳಲ್ಲಿದ್ದ ದ್ರವ್ಯವನ್ನೆಲ್ಲಾ ದೋಚಿಕೊಂಡು ಹೋದನು. ಇಸ್ರಯೇಲರ ಅರಸನಾದ ಸೊಲೊಮೋನನು ಸರ್ವೇಶ್ವರನ ಆಲಯಕ್ಕಾಗಿ ಮಾಡಿಸಿದ್ದ ಬಂಗಾರದ ಎಲ್ಲಾ ಆಭರಣಗಳನ್ನು ಮುರಿದುಬಿಟ್ಟನು.",
			"14": "<sup>14</sup> ಜೆರುಸಲೇಮಿನಲ್ಲಿದ್ದ ಎಲ್ಲಾ ಪದಾಧಿಕಾರಿಗಳನ್ನೂ ಯೋಧರನ್ನೂ ಕಮ್ಮಾರರು ಮೊದಲಾದ ಕೈಗಾರಿಕೆಯವರನ್ನೂ ಒಟ್ಟಾರೆ ಹತ್ತು ಸಾವಿರ ಜನರನ್ನು ಸೆರೆಗೊಯ್ದನು. ನಾಡಿನ ಜನರಲ್ಲಿ ಕೇವಲ ಬಡವರನ್ನು ಹೊರತಾಗಿ ಬೇರೆ ಯಾರನ್ನೂ ಬಿಡಲಿಲ್ಲ.",
			"15": "<sup>15</sup> ಅವನು ಯೆಹೋಯಾಖೀನನ್ನೂ ಅವನ ತಾಯಿ, ಹೆಂಡತಿಯರು, ಕಂಚುಕಿಗಳನ್ನೂ ನಾಡಿನ ಪ್ರಧಾನ ಪುರುಷರು ಇವರನ್ನೂ,",
			"16": "<sup>16</sup> ಏಳು ಸಾವಿರ ಮಂದಿ ಯೋಧರನ್ನೂ ಸಾವಿರ ಮಂದಿ ಕಮ್ಮಾರರು ಮೊದಲಾದ ಕೈಗಾರಿಕೆಯವರನ್ನೂ ಜೆರುಸಲೇಮಿನಿಂದ ಬಾಬಿಲೋನಿಗೆ ಒಯ್ದನು. ಅವರೆಲ್ಲರು ಪುಷ್ಟರೂ ರಣವೀರರೂ ಆಗಿದ್ದರು.",
			"17": "<sup>17</sup> ಬಾಬಿಲೋನಿನ ಅರಸನು ಯೆಹೋಯಾಖೀನನಿಗೆ ಬದಲಾಗಿ ಅವನ ಚಿಕ್ಕಪ್ಪನಾದ ಮತ್ತನ್ಯ ಎಂಬುವನನ್ನು ಅರಸನನ್ನಾಗಿ ನೇಮಿಸಿ ಅವನಿಗೆ ‘ಚಿದ್ಕೀಯ’ ಎಂಬ ಹೆಸರಿಟ್ಟನು.",
			"18": "<sup>18</sup> ಚಿದ್ಕೀಯನು ಇಪ್ಪತ್ತೊಂದು ವರ್ಷ ತುಂಬಿದ ಮೇಲೆ ಅರಸನಾಗಿ ಜೆರುಸಲೇಮನ್ನು ಹನ್ನೊಂದು ವರ್ಷ ಆಳಿದನು. ಅವನ ತಾಯಿ ಲಿಬ್ನದ ಯೆರೆಮೀಯನ ಮಗಳಾದ ಹಮೂಟಲ್ ಎಂಬಾಕೆ.",
			"19": "<sup>19</sup> ಚಿದ್ಕೀಯನು ಯೆಹೋಯಾಕೀಮನಂತೆ ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ದ್ರೋಹಿಯಾಗಿ ಇದ್ದನು.",
			"20": "<sup>20</sup> ಸರ್ವೇಶ್ವರಸ್ವಾಮಿ ಬಹಳ ಕೋಪಗೊಂಡು ಜೆರುಸಲೇಮಿನವರ ಮೇಲೂ ಬೇರೆ ಎಲ್ಲ ಯೆಹೂದ್ಯರ ಮೇಲೂ ಇದನ್ನೆಲ್ಲ ಬರಮಾಡಿ, ಕಡೆಗೆ ಅವರನ್ನು ತಮ್ಮ ಸನ್ನಿಧಿಯಿಂದ ತಳ್ಳಿಬಿಟ್ಟರು."
		},
		"25": {
			"1": "<sup>1</sup> ಚಿದ್ಕೀಯನು ಬಾಬಿಲೋನಿನ ಅರಸನಾದ ನೆಬೂಕದ್ನೆಚ್ಚರನ ವಿರುದ್ಧ ದಂಗೆ ಎದ್ದನು. ಇವನು‍ ಚಿದ್ಕೀಯನ ಆಳ್ವಿಕೆಯ ಒಂಬತ್ತನೆಯ ವರ್ಷದ ಹತ್ತನೆಯ ತಿಂಗಳಿನ ಹತ್ತನೆಯ ದಿನ ಸರ್ವಸೈನ್ಯ ಸಮೇತ ಜೆರುಸಲೇಮಿಗೆ ಬಂದನು. ಅಲ್ಲಿ ಪಾಳೆಯ ಮಾಡಿಕೊಂಡು ಅದರ ಸುತ್ತಲು ಮಣ್ಣಿನ ದಿಬ್ಬವನ್ನು ಎಬ್ಬಿಸಿದನು.",
			"2": "<sup>2</sup> ಚಿದ್ಕೀಯನ ಆಳ್ವಿಕೆಯ ಹನ್ನೊಂದನೆಯ ವರ್ಷದಲ್ಲಿ ಅದಕ್ಕೆ ಮುತ್ತಿಗೆ ಹಾಕಿದನು.",
			"3": "<sup>3</sup> ಘೋರಕ್ಷಾಮದ ಕಾರಣ ನಗರದವರಿಗೆ ಆಹಾರ ಸಿಕ್ಕದೆ ಹೋಯಿತು. ನಗರಕ್ಕೆ ಮುತ್ತಿಗೆಹಾಕಿದ್ದ ಬಾಬಿಲೋನಿಯರು ಹನ್ನೊಂದನೆಯ ತಿಂಗಳಿನ ಒಂಬತ್ತನೆಯ ದಿವಸ ಪೌಳಿಗೋಡೆಯಲ್ಲಿ ಒಂದು ದ್ವಾರವನ್ನು ಮಾಡಿದರು.",
			"4": "<sup>4</sup> ಒಳಗಿದ್ದ ಅರಸನೂ ಅವನ ಎಲ್ಲ ಸೈನಿಕರೂ ಅದೇ ರಾತ್ರಿ, ಅರಸನ ತೋಟದ ಬಳಿಯಿದ್ದ ಬಾಗಿಲಿನ ಮೂಲಕ, ನಗರದಿಂದ ಓಡಿಹೋದರು. ಆ ಬಾಗಿಲು ಎರಡು ಗೋಡೆಗಳ ಮಧ್ಯೆಯಿತ್ತು. ಅರಸನು ಅರಾಬಾ ಎಂಬ ಕಣಿವೆ ಪ್ರದೇಶದ ಮಾರ್ಗವಾಗಿ ಓಡಿಹೋದನು.",
			"5": "<sup>5</sup> ಬಾಬಿಲೋನಿಯದ ಸೈನಿಕರು ಅವನನ್ನು ಹಿಂದಟ್ಟಿ ಜೆರಿಕೋವಿನ ಬಯಲಿನಲ್ಲಿ ಹಿಡಿದರು. ಅಷ್ಟರಲ್ಲಿ ಅವನ ಸೈನಿಕರೆಲ್ಲರು ಅವನನ್ನು ಬಿಟ್ಟು ಚದರಿಹೋಗಿದ್ದರು.",
			"6": "<sup>6</sup> ಅನಂತರ ಬಾಬಿಲೋನಿಯರು ಅವನನ್ನು ರಿಬ್ಲದಲ್ಲಿದ್ದ ತಮ್ಮ ಅರಸನ ಬಳಿಗೆ ಎಳೆದು ತಂದು ಅವನಿಗೆ ಶಿಕ್ಷೆಯನ್ನು ವಿಧಿಸಿದರು.",
			"7": "<sup>7</sup> ಇವನ ಇಬ್ಬರು ಮಕ್ಕಳನ್ನು ಅವನ ಕಣ್ಣೆದುರಿಗೇ ವಧಿಸಿ, ಅವನಿಗೆ ಬೇಡಿಹಾಕಿ ಎರಡು ಕಣ್ಣುಗಳನ್ನೂ ಕಿತ್ತು ಬಾಬಿಲೋನಿಗೆ ತೆಗೆದುಕೊಂಡು ಹೋದರು.",
			"8": "<sup>8</sup> ಐದನೆಯ ತಿಂಗಳಿನ ಏಳನೆಯ ದಿನ, ಅಂದರೆ ಬಾಬಿಲೋನಿನ ಅರಸ ನೆಬೂಕದ್ನೆಚ್ಚರನ ಆಳ್ವಿಕೆಯ ಹತ್ತೊಂಬತ್ತನೆಯ ವರ್ಷದಲ್ಲಿ, ಬಾಬಿಲೋನಿನ ಅರಸನ ಸೇವಕನೂ ರಕ್ಷಾದಳದ ಅಧಿಪತಿಯೂ ಆಗಿದ್ದ ನೆಬೂಜರದಾನ್ ಎಂಬವನು ಜೆರುಸಲೇಮಿಗೆ ಬಂದನು.",
			"9": "<sup>9</sup> ಸರ್ವೇಶ್ವರನ ಆಲಯವನ್ನೂ ಅರಮನೆಯನ್ನೂ ಜೆರುಸಲೇಮಿನ ಎಲ್ಲ ದೊಡ್ಡ ಮನೆಗಳನ್ನೂ ಸುಟ್ಟುಬಿಟ್ಟನು.",
			"10": "<sup>10</sup> ಅವನ ಜೊತೆಯಲ್ಲಿ ಬಂದಿದ್ದ ಬಾಬಿಲೋನಿಯದ ಸೈನ್ಯದವರು ಜೆರುಸಲೇಮಿನ ಸುತ್ತುಗೋಡೆಗಳನ್ನೆಲ್ಲ ಕೆಡವಿದರು.",
			"11": "<sup>11</sup> ರಕ್ಷಾದಳದ ಅಧಿಪತಿಯಾದ ನೆಬೂಜರದಾನನು ನಗರದಲ್ಲಿ ಉಳಿದವರನ್ನು, ಮೊದಲೇ ಬಾಬಿಲೋನಿನ ಅರಸನಿಗೆ ಮರೆಹೊಕ್ಕವರನ್ನು ಹಾಗು ಬೇರೆ ಜನರೆಲ್ಲರನ್ನು ಸೆರೆಗೆ ಒಯ್ದನು.",
			"12": "<sup>12</sup> ಹೊಲಗಳನ್ನು ಮತ್ತು ದ್ರಾಕ್ಷೀತೋಟಗಳನ್ನು ಮಾಡುವುದಕ್ಕಾಗಿ ನಾಡಿನ ಜನರಲ್ಲಿ ಕೇವಲ ದರಿದ್ರರನ್ನು ಮಾತ್ರ ಬಿಟ್ಟುಹೋದನು.",
			"13": "<sup>13</sup> ಬಾಬಿಲೋನಿಯದವರು ಸರ್ವೇಶ್ವರನ ಆಲಯದಲ್ಲಿದ್ದ ಕಂಚಿನ ಕಂಬಗಳನ್ನೂ ಚಕ್ರದ ಬಂಡಿಗಳನ್ನೂ, ಕಡಲಿನ ಆಕಾರದ ಕಂಚಿನ ಪಾತ್ರೆಯನ್ನೂ ಒಡೆದು ಅವುಗಳ ಕಂಚನ್ನೆಲ್ಲ ಕೊಂಡೊಯ್ದರು.",
			"14": "<sup>14</sup> ಅದು ಮಾತ್ರವಲ್ಲ, ದೇವಾಲಯದ ಸೇವೆಗಾಗಿ ಉಪಯೋಗಿಸುತ್ತಿದ್ದ ಬೋಗುಣಿ, ಸಲಿಕೆ, ಕತ್ತರಿ, ಸಾಂಬ್ರಾಣಿಕಳಸ ಮುಂತಾದ ಕಂಚಿನ ಸಾಮಾನುಗಳನ್ನೂ ಬಾಬಿಲೋನಿಗೆ ತೆಗೆದುಕೊಂಡು ಹೋದರು.",
			"15": "<sup>15</sup> ರಕ್ಷಾದಳದ ನಾಯಕನು ಬೆಳ್ಳಿ ಬಂಗಾರದ ಅಗ್ಗಿಷ್ಟಿಕೆ, ಬಟ್ಟಲು, ಮೊದಲಾದುವುಗಳನ್ನು ದೋಚಿಕೊಂಡನು.",
			"16": "<sup>16</sup> ಸೊಲೊಮೋನನು ಸರ್ವೇಶ್ವರನ ಆಲಯಕ್ಕಾಗಿ ಮಾಡಿಸಿದ್ದ ಎರಡು ಕಂಬಗಳು, ಕಡಲಿನ ಆಕಾರದ ಪಾತ್ರೆ, ಚಕ್ರದ ಬಂಡಿಗಳು ಇವುಗಳನ್ನು ಮಾಡುವುದಕ್ಕೆ ಎಷ್ಟೋ ಕಂಚು ಹಿಡಿದಿತ್ತು.",
			"17": "<sup>17</sup> ಪ್ರತಿಯೊಂದು ಕಂಬ ಹದಿನೆಂಟು ಮೊಳ ಎತ್ತರವಿತ್ತು. ಪ್ರತಿಯೊಂದರ ಮೇಲೆ ಮೂರು ಮೊಳ ಎತ್ತರವಾದ ಒಂದು ಕಂಚಿನ ಕುಂಭವಿತ್ತು. ಆ ಕುಂಭಗಳ ಮೇಲೆ ಸುತ್ತಲೂ ಕಂಚಿನ ಜಾಲರಿ ಹಾಗು ದಾಳಿಂಬೆ ಹಣ್ಣುಗಳು ಇದ್ದವು.",
			"18": "<sup>18</sup> ರಕ್ಷಾದಳದ ನಾಯಕ ನೆಬೂಜರದಾನನು ಹಿಡಿದುಕೊಂಡು ಹೋದ ಜನರಲ್ಲಿ ಪ್ರಧಾನ ಯಾಜಕ ಸೆರಾಯನು, ಮುಖ್ಯ ಯಾಜಕ ಚೆಫನ್ಯನು, ಮೂರು ಮಂದಿ ದ್ವಾರಪಾಲಕರು ಸೇರಿದ್ದರು.",
			"19": "<sup>19</sup> ಸೈನ್ಯ ಸಂಬಂಧವಾದ ಎಲ್ಲ ವ್ಯವಸ್ಥೆಯನ್ನು, ನಗರದಲ್ಲಿ ಉಳಿದಿದ್ದ ಹಾಗು ಅರಸನಿಗೆ ಆಪ್ತರಾಗಿದ್ದ ಐದು ಮಂದಿ ಮಂತ್ರಿಗಳನ್ನು, ಯುದ್ಧಕ್ಕೆ ಹೋಗತಕ್ಕವರನ್ನು ಪಟ್ಟಿಮಾಡುವ ಸೇನಾಪತಿಯ ಲೇಖಕನನ್ನು ಹಾಗು ನಗರದಲ್ಲಿದ್ದ ಅರವತ್ತು ಮಂದಿ ರೈತರನ್ನು ಹಿಡಿದುಕೊಂಡು ಹೋದನು.",
			"20": "<sup>20</sup> ನೆಬೂಜರದಾನನು ಇವರನ್ನೆಲ್ಲ ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ಹಮಾತ್ ಪ್ರದೇಶದ ರಿಬ್ಲದಲ್ಲಿದ್ದ ಬಾಬಿಲೋನಿನ ಅರಸನಿಗೆ ಒಪ್ಪಿಸಿದನು.",
			"21": "<sup>21</sup> ಅವನು ಅವರನ್ನು ಅಲ್ಲಿಯೇ ಕೊಲ್ಲಿಸಿದನು. ಹೀಗೆ ಯೆಹೂದ್ಯರು ಸೆರೆಯಾಳುಗಳಾಗಿ ತಮ್ಮ ನಾಡನ್ನೇ ಬಿಟ್ಟು ಹೋಗಬೇಕಾಯಿತು.",
			"22": "<sup>22</sup> ಬಾಬಿಲೋನಿನ ಅರಸ ನೆಬೂಕದ್ನೆಚ್ಚರನು ತಾನು ಜುದೇಯ ದೇಶದಲ್ಲಿ ಉಳಿಸಿದ ಜನರ ಮೇಲೆ, ಅಹೀಕಾಮನ ಮಗನೂ ಶಾಫಾನನ ಮೊಮ್ಮಗನೂ ಆದ ಗೆದಲ್ಯ ಎಂಬವನನ್ನು ರಾಜ್ಯಪಾಲನನ್ನಾಗಿ ನೇಮಿಸಿದನು.",
			"23": "<sup>23</sup> ಅಳಿದು ಉಳಿದಿದ್ದ ಯೆಹೂದ ಸೇನಾಪತಿಗಳು, ಗೆದಲ್ಯನು ಬಾಬಿಲೋನಿನ ಅರಸನಿಂದ ರಾಜ್ಯಪಾಲನಾಗಿ ನೇಮಕವಾಗಿದ್ದಾನೆಂಬ ಸಮಾಚಾರವನ್ನು ಕೇಳಿ, ತಮ್ಮ ಜನರೊಡನೆ ಮಿಚ್ಪದಲ್ಲಿ ವಾಸವಾಗಿದ್ದ ಅವನ ಬಳಿಗೆ ಬಂದರು. ಅವರ ಹೆಸರುಗಳು ಇವು: ನೆತನ್ಯನ ಮಗ ಇಷ್ಮಾಯೇಲನು, ಕಾರೇಹನ ಮಗ ಯೋಹಾನಾನ್, ನೆಟೋಫದವನೂ ತನ್ಹುಮೆತನ ಮಗನೂ ಆದ ಸೆರಾಯನು, ಮಾಕಾ ಊರಿನವನಾದ ಯಾಜನ್ಯನು.",
			"24": "<sup>24</sup> ಗೆದಲ್ಯನು ಇವರಿಗೂ ಇವರ ಜನರಿಗೂ, “ಬಾಬಿಲೋನಿಯಾದ ಸೈನ್ಯದವರಿಗೆ ಹೆದರಬೇಡಿ; ಬಾಬಿಲೋನಿನ ಅರಸನಿಗೆ ಅಧೀನರಾಗಿದ್ದು ಇಲ್ಲಿಯೇ ವಾಸಿಸಿರಿ; ಆಗ ನಿಮಗೆ ಶುಭವಾಗುವುದು,” ಎಂದು ಆಣೆಯಿಟ್ಟು ಹೇಳಿದನು.",
			"25": "<sup>25</sup> ಆದರೆ ಏಳನೆಯ ತಿಂಗಳಿನಲ್ಲಿ ರಾಜವಂಶದವನೂ ಎಲೀಷಮನ ಮೊಮ್ಮಗನೂ ನೆತನ್ಯನ ಮಗನೂ ಆದ ಇಷ್ಮಾಯೇಲನು ಹತ್ತುಮಂದಿ ಆಳುಗಳೊಡನೆ ಬಂದು ಗೆದಲ್ಯನನ್ನು, ಅವನ ಸಂಗಡ ಮಿಚ್ಪದಲ್ಲಿದ್ದ ಯೆಹೂದ್ಯರನ್ನು ಹಾಗು ಬಾಬಿಲೋನಿಯಾದವರನ್ನು ಸಂಹರಿಸಿದನು.",
			"26": "<sup>26</sup> ಆಗ ಚಿಕ್ಕವರು, ದೊಡ್ಡವರು, ಸೇನಾಪತಿಗಳು, ಎಲ್ಲರು ಬಾಬಿಲೋನಿಯಾದವರಿಗೆ ಹೆದರಿ ಈಜಿಪ್ಟಿಗೆ ಓಡಿಹೋದರು.",
			"27": "<sup>27</sup> ಎವೀಲ್ಮೆರೋದಕನು ತಾನು ಪಟ್ಟಕ್ಕೆ ಬಂದ ಮೊದಲನೆಯ ವರ್ಷದಲ್ಲಿ ಅಂದರೆ, ಜುದೇಯದ ಅರಸ ಯೆಹೋಯಾಖೀನನು ಸೆರೆಗೆ ಸಿಕ್ಕಿದ ಮೂವತ್ತೇಳನೆಯ ವರ್ಷದ ಹನ್ನೆರಡನೆಯ ತಿಂಗಳಿನ ಇಪ್ಪತ್ತೇಳನೆಯ ದಿನದಲ್ಲಿ, ಯೆಹೋಯಾಖೀನನನ್ನು ಸೆರೆಯಿಂದ ಬಿಡಿಸಿ ಉದ್ಧಾರ ಮಾಡಿದನು.",
			"28": "<sup>28</sup> ಅದು ಮಾತ್ರವಲ್ಲ, ಅವನೊಡನೆ ಪ್ರೀತಿಯಿಂದ ಮಾತಾಡಿ, ತನ್ನ ಸಂಗಡ ಬಾಬಿಲೋನಿನಲ್ಲಿದ್ದ ಎಲ್ಲ ಅರಸುಗಳಲ್ಲಿ ಅವನಿಗೇ ಉನ್ನತ ಸ್ಥಾನವನ್ನು ಕೊಟ್ಟನು.",
			"29": "<sup>29</sup> ಯೆಹೋಯಾಖೀನನು ಸೆರೆಯ ಬಟ್ಟೆಗಳನ್ನು ತೆಗೆದುಹಾಕಿ, ಜೀವದಿಂದ ಇರುವವರೆಗೂ, ಅರಸನ ಪಂಕ್ತಿಯಲ್ಲೇ ಊಟಮಾಡುತ್ತಿದ್ದನು.",
			"30": "<sup>30</sup> ಅವನ ಜೀವಿತಕಾಲವೆಲ್ಲ, ಅವನಿಗೆ ಬೇಕಾಗಿ ಇದ್ದ ಎಲ್ಲ ಪದಾರ್ಥಗಳು ಅರಸನಿಂದಲೇ ಪ್ರತಿನಿತ್ಯವೂ ದೊರಕುತ್ತಿದ್ದವು."
		}
	}
};
module.exports = book;