var book = {
	"name": "೨ ಪೂರ್ವಕಾಲದ ಇತಿಹಾಸ",
	"numChapters": 36,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ದಾವೀದನ ಮಗ ಸೊಲೊಮೋನನು ತನ್ನ ರಾಜ್ಯದ ಮೇಲೆ ಸಂಪೂರ್ಣ ಪ್ರಭುತ್ವ ಹೊಂದಿದನು. ಅವನ ದೇವರಾದ ಸರ್ವೇಶ್ವರಸ್ವಾಮಿ ಅವನನ್ನು ಹರಸಿ ಮಹಾ ಬಲಾಢ್ಯನಾಗುವಂತೆ ಮಾಡಿದರು.",
			"2": "<sup>2</sup> ಅವನು ತನ್ನ ಸಹಸ್ರಾಧಿಪತಿಗಳನ್ನು, ಶತಾಧಿಪತಿಗಳನ್ನು, ಸರಕಾರದ ಎಲ್ಲ ಪದಾಧಿಕಾರಿಗಳನ್ನು ಹಾಗೂ ಇಸ್ರಯೇಲ್ ಕುಲಗೋತ್ರಗಳ ನಾಯಕರನ್ನು ಬರಮಾಡಿ, ಅವರ ಸಮೇತ ಗಿಬ್ಯೋನಿನಲ್ಲಿದ್ದ ಪೂಜಾಸ್ಥಳಕ್ಕೆ ಹೋದನು.",
			"3": "<sup>3</sup> ಸರ್ವೇಶ್ವರನ ದಾಸನಾದ ಮೋಶೆ ಮರುಭೂಮಿಯಲ್ಲಿ ಮಾಡಿಸಿದ್ದ ದೇವದರ್ಶನದ ಗುಡಾರ ಅಲ್ಲಿಯೇ ಇತ್ತು.",
			"4": "<sup>4</sup> ದಾವೀದನು ದೇವರ ಮಂಜೂಷವನ್ನು, ಈಗಾಗಲೇ ಕಿರ್ಯತ್ಯಾರೀಮಿನಿಂದ ತಾನು ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಅದಕ್ಕೋಸ್ಕರ ಸಿದ್ಧಮಾಡಿದ್ದ ಗುಡಾರಕ್ಕೆ, ತೆಗೆದುಕೊಂಡು ಬಂದಿದ್ದನು.",
			"5": "<sup>5</sup> ಆದರೂ ಊರೀಯನ ಮಗನೂ ಹೂರನ ಮೊಮ್ಮಗನೂ ಆದ ಬೆಚಲೇಲನು ಮಾಡಿಸಿದ್ದ ತಾಮ್ರದ ಬಲಿಪೀಠ, ಅಲ್ಲೇ ಗಿಬ್ಯೋನಿನಲ್ಲಿ ಸರ್ವೇಶ್ವರನ ಗುಡಾರದ ಮುಂದೆ ಇತ್ತು. ಈ ಕಾರಣ ಸೊಲೊಮೋನನೂ ಅವನ ಸಮಾಜದವರೂ ಅಲ್ಲಿಗೆ ದರ್ಶನಕ್ಕಾಗಿ ಹೋಗುತ್ತಿದ್ದರು.",
			"6": "<sup>6</sup> ಸೊಲೊಮೋನನು, ದೇವದರ್ಶನದ ಗುಡಾರದ ಬಳಿ ಸರ್ವೇಶ್ವರನ ಮುಂದಿದ್ದ ಆ ತಾಮ್ರದ ಪೀಠದ ಮೇಲೆ, ಸಾವಿರ ದಹನಬಲಿಗಳನ್ನು ಅರ್ಪಿಸಿದನು.",
			"7": "<sup>7</sup> ಅದೇ ರಾತ್ರಿ ದೇವರು ಸೊಲೊಮೋನನಿಗೆ ದರ್ಶನಕೊಟ್ಟು, “ನಿನಗೆ ಯಾವ ವರಬೇಕು, ಕೇಳಿಕೋ,” ಎಂದು ಹೇಳಿದರು.",
			"8": "<sup>8</sup> ಸೊಲೊಮೋನನು, “ನೀವು ನನ್ನ ತಂದೆ ದಾವೀದನಿಗೆ ಮಹಾಕೃಪೆಯನ್ನು ತೋರಿಸಿದಿರಿ. ಅವರ ಸ್ಥಾನದಲ್ಲಿ ನನ್ನನ್ನು ಅರಸನನ್ನಾಗಿ ನೇಮಿಸಿದ್ದೀರಿ.",
			"9": "<sup>9</sup> ಹೀಗೆ ಇರಲಾಗಿ ದೇವರಾದ ಸರ್ವೇಶ್ವರಾ, ನೀವು ನನ್ನ ತಂದೆ ದಾವೀದನಿಗೆ ನುಡಿದದ್ದು ಸಾರ್ಥಕವಾಗಲಿ; ಧರೆಯ ಧೂಳಿನಷ್ಟು ಅಸಂಖ್ಯವಾದ ಜನಾಂಗದ ಮೇಲೆ ನನ್ನನ್ನು ಅರಸನನ್ನಾಗಿ ನೇಮಿಸಿದ್ದೀರಿ.",
			"10": "<sup>10</sup> ನಿಮ್ಮ ಪ್ರಜೆಯಾದ ಈ ಮಹಾಜನಾಂಗವನ್ನು ಆಳುವುದಕ್ಕೆ ಸಮರ್ಥರಾರು! ನಾನು ಈ ಜನರ ನಾಯಕನಾಗಿ ಇವರನ್ನು ಪರಿಪಾಲಿಸುವುದಕ್ಕೆ ಬೇಕಾದ ಜ್ಞಾನ ವಿವೇಕಗಳನ್ನು ನನಗೆ ಅನುಗ್ರಹಿಸಿರಿ,” ಎಂದು ದೇವರನ್ನು ಬೇಡಿಕೊಂಡನು.",
			"11": "<sup>11</sup> ಆಗ ದೇವರು ಸೊಲೊಮೋನನಿಗೆ, “ನೀನು ಘನಧನೈಶ್ವರ್ಯಗಳನ್ನಾಗಲಿ, ಶತ್ರುಗಳ ವಿನಾಶವನ್ನಾಗಲಿ, ದೀರ್ಘಾಯುಷ್ಯವನ್ನಾಗಲಿ ಕೇಳಿಕೊಳ್ಳಲಿಲ್ಲ. ಬದಲಿಗೆ ನಾನು ಯಾರ ಮೇಲೆ ನಿನ್ನನ್ನು ಅರಸನನ್ನಾಗಿ ಮಾಡಿದೆನೋ ಆ ನನ್ನ ಪ್ರಜೆಗಳನ್ನು ಪಾಲಿಸುವುದಕ್ಕೆ ಬೇಕಾದ ಜ್ಞಾನ ವಿವೇಕಗಳ ಮೇಲೆ ಮನಸ್ಸಿಟ್ಟು ಅವುಗಳನ್ನು ನಿನಗಾಗಿ ಬೇಡಿಕೊಂಡೆ. ಆದುದರಿಂದ ಅವು ನಿನಗೆ ದೊರಕುವುವು.",
			"12": "<sup>12</sup> ಇದಲ್ಲದೆ, ನಾನು ನಿನಗೆ ಘನಧನೈಶ್ವರ್ಯಗಳನ್ನೂ ಅನುಗ್ರಹಿಸುತ್ತೇನೆ. ಇಂಥ ಘನಧನೈಶ್ವರ್ಯಗಳು ನಿನಗಿಂತ ಮೊದಲಿದ್ದ ಅರಸರಲ್ಲಿ ಯಾರಿಗೂ ಇರಲಿಲ್ಲ, ನಿನ್ನ ಅನಂತರದವರಿಗೂ ಇರುವುದಿಲ್ಲ,” ಎಂದರು.",
			"13": "<sup>13</sup> ಆಮೇಲೆ ಅರಸ ಸೊಲೊಮೋನನು ಗಿಬ್ಯೋನಿನ ಪೂಜಾಸ್ಥಳದಲ್ಲಿದ್ದ ದೇವದರ್ಶನದ ಗುಡಾರದಿಂದ ಜೆರುಸಲೇಮಿಗೆ ಬಂದು ಇಸ್ರಯೇಲ್ ರಾಜ್ಯವನ್ನು ಆಳಿದನು.",
			"14": "<sup>14</sup> ರಥಗಳನ್ನೂ ರಾಹುತರನ್ನೂ ಸಂಗ್ರಹಿಸಿದನು. ಅವನಿಗಿದ್ದ ರಥಗಳು ಸಾವಿರದ ನಾನೂರು; ರಾಹುತರು ಹನ್ನೆರಡು ಸಾವಿರ. ಇವುಗಳಲ್ಲಿ ಕೆಲವನ್ನು ಜೆರುಸಲೇಮಿನಲ್ಲೇ ಇಟ್ಟುಕೊಂಡನು. ಉಳಿದವುಗಳನ್ನು ರಥಗಳಿಗಾಗಿಯೇ ನೇಮಿಸಿದ್ದ ಪಟ್ಟಣಗಳಲ್ಲಿ ಇರಿಸಿದನು.",
			"15": "<sup>15</sup> ಅವನ ಕಾಲದಲ್ಲಿ ಬೆಳ್ಳಿಬಂಗಾರ ಕಲ್ಲಿನಂತೆಯೂ, ದೇವದಾರು ಮರಗಳು ಇಳಕಲಿನ ಪ್ರದೇಶದಲ್ಲಿ ಬೆಳೆಯುವ ಅತ್ತಿಮರಗಳಂತೆಯೂ ಜೆರುಸಲೇಮಿನಲ್ಲಿ ವಿಫುಲವಾಗಿದ್ದವು.",
			"16": "<sup>16</sup> ಅರಸ ಸೊಲೊಮೋನನ ಕುದುರೆಗಳು ಈಜಿಪ್ಟ್ ದೇಶದವುಗಳು. ಅವನ ವರ್ತಕರು ಅವುಗಳನ್ನು ಹಿಂಡುಹಿಂಡಾಗಿ ಕೊಂಡುಕೊಂಡು ಬರುತ್ತಿದ್ದರು.",
			"17": "<sup>17</sup> ರಥಕ್ಕೆ ಆರುನೂರು ಬೆಳ್ಳಿನಾಣ್ಯಗಳಂತೆ, ಕುದುರೆಗೆ ನೂರೈವತ್ತು ಬೆಳ್ಳಿನಾಣ್ಯಗಳಂತೆ ಕೊಟ್ಟು, ರಥಗಳನ್ನೂ ಕುದುರೆಗಳನ್ನೂ ಈಜಿಪ್ಟ್ ದೇಶದಿಂದ ತರಿಸುತ್ತಿದ್ದರು; ಹಿತ್ತಿಯರ ಮತ್ತು ಸಿರಿಯಾದವರ ಎಲ್ಲಾ ಅರಸರು ಇವರ ಮುಖಾಂತರವೇ ತರಿಸುತ್ತಿದ್ದರು."
		},
		"2": {
			"1": "<sup>1</sup> ಸೊಲೊಮೋನನು ಸರ್ವೇಶ್ವರನ ಹೆಸರಿಗಾಗಿ ಒಂದು ಅರಮನೆಯನ್ನು ಕಟ್ಟಿಸಬೇಕೆಂದು ಮನಸ್ಸುಮಾಡಿದನು.",
			"2": "<sup>2</sup> ಇಪ್ಪತ್ತು ಸಾವಿರ ಮಂದಿ ಹೊರೆಹೊರುವವರೂ ಎಂಬತ್ತು ಸಾವಿರ ಮಂದಿ ಕಲ್ಲುಗಣಿಯಲ್ಲಿ ಕೆಲಸಮಾಡುವವರನ್ನೂ ಇವರ ಮೇಲ್ವಿಚಾರಣೆಗಾಗಿ ಮೂರು ಸಾವಿರದ ಆರುನೂರು ಮಂದಿ ಮೇಸ್ತ್ರಿಗಳನ್ನೂ ಎಣಿಸಿ ನೇಮಿಸಿದನು.",
			"3": "<sup>3</sup> ಇದಲ್ಲದೆ, ಟೈರಿನ ಅರಸ ಹೂರಾಮನಿಗೆ ದೂತರ ಮುಖಾಂತರ, “ನನ್ನ ತಂದೆ ದಾವೀದ ತಮ್ಮ ವಾಸಕ್ಕೆ ಅರಮನೆಯನ್ನು ಕಟ್ಟಿಸಿಕೊಳ್ಳುವುದಕ್ಕಾಗಿ ನೀನು ದೇವದಾರು ಮರಗಳನ್ನು ಕಳುಹಿಸಿದೆ.",
			"4": "<sup>4</sup> ಈಗ ನಾನು ನನ್ನ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಹೆಸರಿಗಾಗಿ ಒಂದು ಆಲಯವನ್ನು ಕಟ್ಟಿಸಬೇಕೆಂದಿದ್ದೇನೆ. ಇಸ್ರಯೇಲರಿಗಿರುವ ಶಾಶ್ವತ ನಿಯಮದ ಪ್ರಕಾರ ಅವರ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸುಗಂಧ ದ್ರವ್ಯಗಳಿಂದ ಧೂಪಾರತಿ ಎತ್ತಬೇಕಾಗಿದೆ. ಪ್ರತಿನಿತ್ಯ ನೈವೇದ್ಯದ ರೊಟ್ಟಿಗಳನ್ನಿಡಬೇಕಾಗಿದೆ; ಬೆಳಿಗ್ಗೆ, ಸಂಜೆ, ಸಬ್ಬತ್‍ದಿನ, ಅಮಾವಾಸ್ಯೆ ಹಾಗೂ ನಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಜಾತ್ರೆಯ ವೇಳೆಗಳಲ್ಲಿ ದಹನಬಲಿಗಳನ್ನು ಅರ್ಪಿಸಬೇಕಾಗಿದೆ. ಇದಕ್ಕಾಗಿ ಒಂದು ಆಲಯವನ್ನು ಅವರಿಗೆ ಪ್ರತಿಷ್ಠಿಸಬೇಕೆಂದಿದ್ದೇನೆ.",
			"5": "<sup>5</sup> ನಮ್ಮ ದೇವರು ಎಲ್ಲಾ ದೇವರುಗಳಿಗಿಂತ ದೊಡ್ಡವರು! ನಾನು ಕಟ್ಟಿಸುವ ಆಲಯವೂ ದೊಡ್ಡದಾಗಿರಬೇಕು.",
			"6": "<sup>6</sup> ಅವರಿಗೆ ಆಲಯವನ್ನು ಕಟ್ಟುವುದಕ್ಕೆ ಯಾರೂ ಶಕ್ತರಲ್ಲ; ಆಕಾಶವೂ ಉನ್ನತೋನ್ನತವಾದ ಆಕಾಶವೂ ಅವರ ವಾಸಕ್ಕೆ ಸಾಲದಿರುವಲ್ಲಿ ಅವರಿಗಾಗಿ ಆಲಯವನ್ನು ಕಟ್ಟುವುದಕ್ಕೆ ಯಾರು ತಾನೆ ಶಕ್ತರು? ನಾನು ಅವರ ಸನ್ನಿಧಿಯಲ್ಲಿ ಧೂಪಾರತಿ ಎತ್ತುವ ಒಂದು ಸ್ಥಳವನ್ನು ಕಟ್ಟಿಸಬಲ್ಲೆನೇ ಹೊರತು, ಅವರ ವಾಸಕ್ಕಾಗಿ ಆಲಯವನ್ನು ಕಟ್ಟಿಸುವುದಕ್ಕೆ ನಾನು ಎಷ್ಟು ಮಾತ್ರಕ್ಕೂ ಶಕ್ತನಲ್ಲ.",
			"7": "<sup>7</sup> ಹೀಗಿರಲು, ಬಂಗಾರ, ಬೆಳ್ಳಿ, ತಾಮ್ರ, ಕಬ್ಬಿಣ ಇವುಗಳ ಕೆಲಸಮಾಡುವುದರಲ್ಲಿ, ಊದಾ, ಕೆಂಪು, ನೀಲವರ್ಣಗಳ ಬಟ್ಟೆಯನ್ನು ನೇಯುವುದರಲ್ಲಿ ಹಾಗೂ ಚಿತ್ರ ಕೆತ್ತುವುದರಲ್ಲಿ ನಿಪುಣನಾದ ಒಬ್ಬ ವ್ಯಕ್ತಿಯನ್ನು ನನ್ನ ಬಳಿಗೆ ಕಳುಹಿಸು; ನನ್ನ ತಂದೆ ಗೊತ್ತುಮಾಡಿದಂಥ ಮತ್ತು ನನ್ನೊಡನೆ ಜುದೇಯದಲ್ಲೂ, ಜೆರುಸಲೇಮಿನಲ್ಲೂ ಇರುವಂಥ ಕಲಾಕುಶಲರೊಂದಿಗೆ ಅವನು ಕೆಲಸಮಾಡಲಿ.",
			"8": "<sup>8</sup> ಅಂತೆಯೇ ಲೆಬನೋನಿನಿಂದ ದೇವದಾರು ಮರವನ್ನೂ ತುರಾಯಿಮರವನ್ನೂ ಸುಗಂಧದ ಮರವನ್ನೂ ನನಗೆ ಕಳುಹಿಸಬೇಕು. ನಿನ್ನ ಆಳುಗಳು ಲೆಬನೋನಿನ ಮರಗಳನ್ನು ಕಡಿಯುವುದರಲ್ಲಿ ಜಾಣರೆಂದು ನನಗೆ ಗೊತ್ತಿದೆ. ನಿನ್ನ ಆಳುಗಳ ಜೊತೆಯಲ್ಲಿ ನನ್ನ ಆಳುಗಳೂ ಕೆಲಸಮಾಡುವರು.",
			"9": "<sup>9</sup> ಬೃಹದಾಕಾರವಾದ ಹಾಗೂ ಭವ್ಯವಾದ ದೇವಾಲಯವನ್ನು ನಾನು ಕಟ್ಟಿಸಬೇಕಾಗಿದೆ. ಆದುದರಿಂದ ಹೆಚ್ಚು ಮರವನ್ನು ಸಿದ್ಧಪಡಿಸಬೇಕು.",
			"10": "<sup>10</sup> ಮರ ಕಡಿಯುವ ನಿನ್ನ ಆಳುಗಳಿಗಾಗಿ ಎರಡು ಸಾವಿರ ಮೆಟ್ರಿಕ್ ಟನ್ ಗೋದಿ, ಎರಡು ಸಾವಿರ ಮೆಟ್ರಿಕ್ ಟನ್ ಜವೆಗೋದಿ, ನಾಲ್ಕು ಲಕ್ಷ ಲೀಟರ್ ದ್ರಾಕ್ಷಾರಸ ಹಾಗೂ ನಾಲ್ಕು ಲಕ್ಷ ಲೀಟರ್ ಎಣ್ಣೆ ಇವುಗಳನ್ನು ಕೊಡುವೆನು,” ಎಂದು ಹೇಳಿಸಿದನು.",
			"11": "<sup>11</sup> ಟೈರಿನ ಅರಸ ಹೂರಾಮನು ಪತ್ರದ ಮೂಲಕ ಸೊಲೊಮೋನನಿಗೆ, “ಸರ್ವೇಶ್ವರ ತಮ್ಮ ಪ್ರಜೆಗಳನ್ನು ಪ್ರೀತಿಸುವವರು. ಆದುದರಿಂದ ನಿನ್ನನ್ನು ಆ ಜನರ ಅರಸನನ್ನಾಗಿ ನೇಮಿಸಿದ್ದಾರೆ.",
			"12": "<sup>12</sup> ಭೂಮ್ಯಾಕಾಶಗಳನ್ನು ಉಂಟುಮಾಡಿದ ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ತಮಗಾಗಿ ಒಂದು ದೇವಾಲಯವನ್ನೂ ರಾಜನಿಗೋಸ್ಕರ ಒಂದು ಅರಮನೆಯನ್ನೂ ಕಟ್ಟಿಸತಕ್ಕ ಬುದ್ಧಿ, ಜ್ಞಾನ ಹಾಗೂ ವಿವೇಕಗಳುಳ್ಳ ಒಬ್ಬ ಮಗನನ್ನು ಅರಸನಾದ ದಾವೀದನಿಗೆ ಕೊಟ್ಟಿದ್ದಾರೆ; ಅವರಿಗೆ ಸ್ತೋತ್ರವಾಗಲಿ!",
			"13": "<sup>13</sup> ನಾನು ಈಗ ಜ್ಞಾನಿಯೂ ಕಲಾಪ್ರವೀಣನೂ ಆದ ಒಬ್ಬ ವ್ಯಕ್ತಿಯನ್ನು ಕಳುಹಿಸುತ್ತೇನೆ; ಇವನ ಹೆಸರು ಹೂರಾಮಾಬೀ.",
			"14": "<sup>14</sup> ಇವನು ದಾನ್ ಕುಲದ ಒಬ್ಬ ಸ್ತ್ರೀಯ ಮಗ; ಅವನ ತಂದೆ ಟೈರಿನವನು. ಬಂಗಾರ, ಬೆಳ್ಳಿ, ತಾಮ್ರ, ಕಬ್ಬಿಣ, ಕಲ್ಲು, ಮರ ಇವುಗಳ ಕೆಲಸವನ್ನು ಮಾಡುವುದಕ್ಕೂ ಊದಾ, ನೀಲಿ, ಕೆಂಪುವರ್ಣಗಳುಳ್ಳ ನಾರುಬಟ್ಟೆಗಳನ್ನು ನೇಯುವುದಕ್ಕೂ ಎಲ್ಲಾ ತರದ ಕೆತ್ತನೆಯ ಕೆಲಸ ಮಾಡುವುದಕ್ಕೂ ತನಗೆ ಒಪ್ಪಿಸಲಾಗುವ ಎಲ್ಲಾ ಕಲಾತ್ಮಕ ಕೆಲಸಗಳನ್ನು ನಡೆಸುವುದಕ್ಕೂ ಇವನು ಸಮರ್ಥನು. ನೀನೂ ನಿನ್ನ ತಂದೆ ದಾವೀದನೂ ಗೊತ್ತುಮಾಡಿದ ಕಲಾಕುಶಲರೊಡನೆ ಇವನು ಕೆಲಸಮಾಡಲಿ.",
			"15": "<sup>15</sup> ಈಗ ನನ್ನ ಒಡೆಯರಾದ ತಾವು ಮಾತುಕೊಟ್ಟ ಮೇರೆಗೆ ಗೋದಿ, ಜವೆಗೋದಿ, ಎಣ್ಣೆ, ದ್ರಾಕ್ಷಾರಸ ಇವುಗಳನ್ನು ತನ್ನ ಸೇವಕರಿಗೆ ಕಳುಹಿಸಬೇಕು.",
			"16": "<sup>16</sup> ನಾವಾದರೋ ತಮಗೆ ಬೇಕಾದ ಎಲ್ಲಾ ಮರಗಳನ್ನು ಲೆಬನೋನಿನಲ್ಲಿ ಕಡಿದು, ತೆಪ್ಪಗಳಾಗಿ ಕಟ್ಟಿ, ಸಮುದ್ರಮಾರ್ಗವಾಗಿ ಜೊಪ್ಪಕ್ಕೆ ಕಳುಹಿಸುವೆವು; ಅಲ್ಲಿಂದ ನೀವು ಅವುಗಳನ್ನು ಜೆರುಸಲೇಮಿಗೆ ತರಿಸಿಕೊಳ್ಳಬಹುದು,” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"17": "<sup>17</sup> ಸೊಲೊಮೋನನು, ತನ್ನ ತಂದೆ ದಾವೀದನು ಮಾಡಿಸಿದ ಜನಗಣತಿಯ ಆಧಾರದಿಂದ, ಇಸ್ರಯೇಲ್ ನಾಡಿನಲ್ಲಿ ಪ್ರವಾಸಿಗಳಾಗಿದ್ದ ಎಲ್ಲಾ ಅನ್ಯಜನರನ್ನು ಲೆಕ್ಕಿಸಿದನು. ಅವರು ಒಂದು ಲಕ್ಷ ಐವತ್ತಮೂರು ಸಾವಿರದ ಆರುನೂರು ಮಂದಿ ಇದ್ದರು.",
			"18": "<sup>18</sup> ಅವರಲ್ಲಿ ಹೊರೆಹೊರುವುದಕ್ಕಾಗಿ ಎಪ್ಪತ್ತು ಸಾವಿರ ಮಂದಿಯನ್ನೂ ಕಲ್ಲುಗಣಿಗಳಲ್ಲಿ ಕೆಲಸಮಾಡುವುದಕ್ಕಾಗಿ ಎಂಬತ್ತು ಸಾವಿರ ಮಂದಿಯನ್ನೂ ಕೆಲಸದವರ ಮೇಲ್ವಿಚಾರಣೆಗಾಗಿ ಮೂರು ಸಾವಿರದ ಆರುನೂರು ಮಂದಿಯನ್ನೂ ನೇಮಿಸಿದನು."
		},
		"3": {
			"1": "<sup>1</sup> ಸೊಲೊಮೋನನು ಜೆರುಸಲೇಮ್ ಪಟ್ಟಣದಲ್ಲಿ ತನ್ನ ತಂದೆ ದಾವೀದನಿಗೆ ದೇವದರ್ಶನ ಉಂಟಾದ ಮೋರೀಯಾ ಗುಡ್ಡದಲ್ಲಿ, ಸರ್ವೇಶ್ವರನ ಆಲಯವನ್ನು ಕಟ್ಟಿಸ ತೊಡಗಿದನು. ಈ ಮೊದಲು, ಯೆಬೂಸಿಯನಾದ ಒರ್ನಾನನ ಕಣವಾಗಿದ್ದ ಆ ಸ್ಥಳವನ್ನು, ದಾವೀದನು ಇದಕ್ಕಾಗಿ ಸಿದ್ಧಮಾಡಿದ್ದನು.",
			"2": "<sup>2</sup> ಸೊಲೊಮೋನನು ತನ್ನ ಆಳ್ವಿಕೆಯ ನಾಲ್ಕನೆಯ ವರ್ಷದ ಎರಡನೆಯ ತಿಂಗಳಿನ ಎರಡನೆಯ ದಿನದಲ್ಲಿ ಕಟ್ಟಡದ ಕೆಲಸವನ್ನು ಪ್ರಾರಂಭಿಸಿದನು.",
			"3": "<sup>3</sup> ಅವನು ಕಟ್ಟಿಸಿದ ದೇವಾಲಯದ ಅಸ್ತಿವಾರದ ಉದ್ದ ಇಪ್ಪತ್ತೇಳು ಮೀಟರ್ ಹಾಗೂ ಅಗಲ ಒಂಬತ್ತು ಮೀಟರ್.",
			"4": "<sup>4</sup> ದೇವಾಲಯದ ಮುಂದುಗಡೆಯಲ್ಲಿದ್ದ ಮಂಟಪದ ಉದ್ದ ಆಲಯದ ಅಗಲಕ್ಕೆ ಸರಿಯಾಗಿ ಒಂಬತ್ತು ಮೀಟರ್, ಎತ್ತರ ಇಪ್ಪತ್ತನಾಲ್ಕು ಮೀಟರ್, ಸೊಲೊಮೋನನು ದೇವಾಲಯದ ಒಳಮೈಯನ್ನು ಚೊಕ್ಕಬಂಗಾರದ ತಗಡಿನಿಂದ ಹೊದಿಸಿದನು.",
			"5": "<sup>5</sup> ದೊಡ್ಡ ಕೋಣೆಯನ್ನು ತುರಾಯಿ ಮರದ ಹಲಗೆಗಳಿಂದಲೂ ಆ ಹಲಗೆಗಳನ್ನು ತೋರಣ, ಖರ್ಜೂರಗಳ ಚಿತ್ರಗಳುಳ್ಳ ಚೊಕ್ಕ ಬಂಗಾರದ ತಗಡಿನಿಂದಲೂ ಹೊದಿಸಿ, ಅದನ್ನು ರತ್ನಗಳಿಂದ ಅಲಂಕರಿಸಿದನು.",
			"6": "<sup>6</sup> ಆ ಬಂಗಾರ ಪರ್ವಯಿಮ್ ದೇಶದ್ದು.",
			"7": "<sup>7</sup> ಹೀಗೆ ಆಲಯದಲ್ಲಿನ ತೊಲೆ, ಹೊಸ್ತಿಲು, ಗೋಡೆ, ಕದ ಇವುಗಳನ್ನು ಬಂಗಾರದ ತಗಡಿನಿಂದ ಹೊದಿಸಿ, ಗೋಡೆಗಳ ಮೇಲೆ ಕೆರೂಬಿಗಳ ಚಿತ್ರಗಳನ್ನು ಕೆತ್ತಿಸಿದನು.",
			"8": "<sup>8</sup> ಇದಲ್ಲದೆ, ಅವನು ‘ಮಹಾಪರಿಶುದ್ಧ ಸ್ಥಳವನ್ನು’ ಮಾಡಿಸಿದನು.ಅದರ ಉದ್ದ ಆಲಯದ ಅಗಲಕ್ಕೆ ಸರಿಯಾಗಿ ಒಂಬತ್ತು ಮೀಟರ್; ಅದರ ಅಗಲ ಒಂಬತ್ತು ಮೀಟರ್. ಅದನ್ನು ಸುಮಾರು 20 ಮೆಟ್ರಿಕ್ ಟನ್ ಚೊಕ್ಕ ಬಂಗಾರದ ತಗಡಿನಿಂದ ಹೊದಿಸಿದನು.",
			"9": "<sup>9</sup> ಬಂಗಾರದ ಮೊಳೆಗಳ ತೂಕ 570 ಕಿಲೋಗ್ರಾಂ. ಮೇಲುಪ್ಪರಿಗೆಗಳನ್ನು ಬಂಗಾರದ ತಗಡಿನಿಂದ ಹೊದಿಸಿದನು.",
			"10": "<sup>10</sup> ಮಹಾಪರಿಶುದ್ಧ ಸ್ಥಳದಲ್ಲಿ ಎರಡು ಕೆರೂಬಿಗಳ ಪ್ರತಿಮೆಗಳನ್ನು ಮಾಡಿಸಿಟ್ಟು ಅವುಗಳನ್ನೂ ಬಂಗಾರದ ತಗಡಿನಿಂದ ಹೊದಿಸಿದನು.",
			"11": "<sup>11</sup> ಕೆರೂಬಿಗಳ ರೆಕ್ಕೆಗಳು ಒಟ್ಟಾಗಿ ಒಂಬತ್ತು ಮೀಟರ್ ಉದ್ದವಿದ್ದವು. (ಮೊದಲನೆಯ ಕೆರೂಬಿಯ) ಒಂದು ರೆಕ್ಕೆ 2:2 ಮೀಟರ್ ಉದ್ದವಾಗಿದ್ದು ಮನೆಯ ಗೋಡೆಗೆ ತಗಲುತ್ತಿತ್ತು. 2:2 ಮೀಟರ್ ಉದ್ದವಾದ ಇನ್ನೊಂದು ರೆಕ್ಕೆಯು ಎರಡನೆಯ ಕೆರೂಬಿಯ ರೆಕ್ಕೆಗೆ ತಗಲುತ್ತಿತ್ತು.",
			"12": "<sup>12</sup> ಅದರಂತೆಯೇ ಎರಡನೆಯ ಕೆರೂಬಿಯ 2:2 ಮೀಟರ್ ಉದ್ದವಾದ ಒಂದು ರೆಕ್ಕೆ ಮನೆಯ ಗೋಡೆಗೂ ಇನ್ನೊಂದು ರೆಕ್ಕೆ ಮೊದಲನೆಯ ಕೆರೂಬಿಯ ರೆಕ್ಕೆಗೂ ತಗಲುತ್ತಿದ್ದವು.",
			"13": "<sup>13</sup> ಈ ಕೆರೂಬಿಗಳು ತಮ್ಮ ರೆಕ್ಕೆಗಳನ್ನು ಒಂಬತ್ತು ಮೀಟರ್ ಉದ್ದಕ್ಕೆ ಚಾಚಿ ಕಾಲೂರಿ ನಿಂತುಕೊಂಡು ಪರಿಶುದ್ಧ ಸ್ಥಳದ ಕಡೆಗೆ ಮುಖ ಮಾಡಿದ್ದವು.",
			"14": "<sup>14</sup> ಸೊಲೊಮೋನನು ಪರದೆಯನ್ನು ನೀಲಿ, ಊದಾ, ಕೆಂಪು ವರ್ಣಗಳುಳ್ಳ ನಾರಿನಿಂದ ಮಾಡಿಸಿದನು. ಅದರಲ್ಲಿ ಕೆರೂಬಿಗಳನ್ನು ಕಸೂತಿ ಹಾಕಿಸಿದನು.",
			"15": "<sup>15</sup> ಇದಲ್ಲದೆ ಸೊಲೊಮೋನನು ದೇವಾಲಯದ ಮುಂದೆ ಎರಡು ಕಂಬಗಳನ್ನು ನಿಲ್ಲಿಸಿದನು. ಅವು 5:5 ಮೀಟರ್ ಎತ್ತರ ಇದ್ದವು. ಅವುಗಳ ತಲೆಗಳ ಮೇಲಣ ಕುಂಭಗಳು 2:2 ಮೀಟರ್ ಎತ್ತರವಿದ್ದವು.",
			"16": "<sup>16</sup> ಗರ್ಭಗುಡಿಯ ಸರಪಣಿಯಂಥ ಸರಪಣಿಗಳನ್ನು ಮಾಡಿಸಿ, ಆ ಕಂಬಗಳ ಮೇಲಣ ಕುಂಭಗಳಿಗೆ ಸಿಕ್ಕಿಸಿ, ಆ ಸರಪಣಿಗಳಲ್ಲಿ ನೂರು ನೂರು ತಾಮ್ರದ ದಾಳಿಂಬೆ ಹಣ್ಣುಗಳನ್ನು ಕಟ್ಟಿಸಿದನು.",
			"17": "<sup>17</sup> ಆ ಕಂಬಗಳನ್ನು, ದೇವಾಲಯದ ಮುಂಭಾಗದಲ್ಲಿ ಒಂದನ್ನು ಬಲಗಡೆಗೂ, ಇನ್ನೊಂದನ್ನು ಎಡಗಡೆಗೂ ನಿಲ್ಲಿಸಿ, ಬಲಗಡೆಯದಕ್ಕೆ ‘ಯಾಕೀನ್’ ಎಂದೂ ಎಡಗಡೆಯದಕ್ಕೆ ‘ಬೋವಜ್’ ಎಂದೂ ಹೆಸರಿಟ್ಟನು."
		},
		"4": {
			"1": "<sup>1</sup> ಇದಲ್ಲದೆ, ಸೊಲೊಮೋನನು ತಾಮ್ರದ ಬಲಿಪೀಠವನ್ನು ಮಾಡಿಸಿದನು. ಅದರ ಉದ್ದ ಒಂಬತ್ತು ಮೀಟರ್, ಅಗಲ ಒಂಬತ್ತು ಮೀಟರ್, ಎತ್ತರ 4:5 ಮೀಟರ್,",
			"2": "<sup>2</sup> ಅನಂತರ ಅವನು ‘ಕಂಚಿನ ಕಡಲು’ ಎನಿಸಿಕೊಳ್ಳುವ ಎರಕದ ಒಂದು ದೊಡ್ಡ ಪಾತ್ರೆಯನ್ನು ಮಾಡಿಸಿದನು. ಅದರ ಬಾಯಿ ಚಕ್ರಾಕಾರವಾಗಿಯೂ ಅಂಚಿನಿಂದ ಅಂಚಿಗೆ 4:4 ಮೀಟರ್ ಇತ್ತು. ಅದರ ಎತ್ತರ 2:2 ಮೀಟರ್, ಸುತ್ತಳತೆ 13:2 ಮೀಟರ್,",
			"3": "<sup>3</sup> ಅದನ್ನು ಎರಕಹೊಯ್ಯುವಾಗ ಅದರ (ಅಂಚಿನ) ಕೆಳಗೆ ಸುತ್ತಲೂ ಮೊಳಕ್ಕೆ ಹತ್ತರಂತೆ ಹೋರಿಗಳ ಚಿತ್ರಗಳನ್ನು ಎರಡು ಸಾಲಾಗಿ ಎರಕ ಹೊಯ್ಸಿದನು.",
			"4": "<sup>4</sup> ಹನ್ನೆರಡು ಎರಕದ ಹೋರಿಗಳು ಅದನ್ನು ಹೊತ್ತಿದ್ದವು. ಅವುಗಳಲ್ಲಿ ಮೂರು ಉತ್ತರ ದಿಕ್ಕಿಗೂ ಮೂರು ಪಶ್ಚಿಮ ದಿಕ್ಕಿಗೂ ಮೂರು ದಕ್ಷಿಣ ದಿಕ್ಕಿಗೂ ಮೂರು ಪೂರ್ವ ದಿಕ್ಕಿಗೂ ಮುಖ ಮಾಡಿಕೊಂಡಿದ್ದವು. ಕಂಚಿನ ಕಡಲು ಅವುಗಳ ಬೆನ್ನಿನ ಮೇಲಿತ್ತು. ಅವುಗಳ ಹಿಂಭಾಗವು ಒಳಗಡೆಗಿತ್ತು.",
			"5": "<sup>5</sup> ಆ ಪಾತ್ರೆಯ ತಗಡು ನಾಲ್ಕು ಬೆರಳು ದಪ್ಪವಾಗಿತ್ತು. ಅದರ ಅಂಚು ಕಮಲಾಕಾರದ ಬಟ್ಟಲನ್ನು ಹೋಲುತ್ತಿತ್ತು. ಅದು ಅರವತ್ತು ಸಾವಿರ ಲೀಟರ್ ನೀರನ್ನು ಹಿಡಿಯುತ್ತಿತ್ತು.",
			"6": "<sup>6</sup> ಇದಲ್ಲದೆ ಅವನು ಹತ್ತು ನೀರಿನ ಬಾನೆಗಳನ್ನು ಮಾಡಿಸಿ ದಹನಬಲಿ ಸಮರ್ಪಣೆಯ ಸಾಮಾನುಗಳನ್ನು ತೊಳೆದು ಶುದ್ಧಮಾಡುವುದಕ್ಕಾಗಿ ಐದನ್ನು ಬಲಗಡೆಯಲ್ಲಿಯೂ ಐದನ್ನು ಎಡಗಡೆಯಲ್ಲಿಯೂ ಇಡಿಸಿದನು; ಕಡಲು ಎನಿಸಿಕೊಳ್ಳುವ ಪಾತ್ರೆ ಯಾಜಕರ ಸ್ನಾನಕ್ಕಾಗಿ ಇತ್ತು.",
			"7": "<sup>7</sup> ಅದೂ ಅಲ್ಲದೆ ನೇಮಕವಾದ ಮಾದರಿಯಂತೆ ಬಂಗಾರದ ಹತ್ತು ದೀಪಸ್ತಂಭಗಳನ್ನು ಮಾಡಿಸಿ, ಐದನ್ನು ದೇವಾಲಯದೊಳಗೆ ಬಲಗಡೆಯಲ್ಲೂ ಐದನ್ನು ಎಡಗಡೆಯಲ್ಲೂ ಇಡಿಸಿದನು.",
			"8": "<sup>8</sup> ಹತ್ತು ಮೇಜುಗಳನ್ನು ಮಾಡಿಸಿ ದೇವಾಲಯದೊಳಗೆ ಬಲಗಡೆ ಐದನ್ನೂ ಎಡಗಡೆ ಐದನ್ನೂ ಇಡಿಸಿ ನೂರು ಬಂಗಾರದ ಬೋಗುಣಿಗಳನ್ನು ಮಾಡಿಸಿದನು.",
			"9": "<sup>9</sup> ಮತ್ತೆ, ಯಾಜಕರ ಪ್ರಾಕಾರವನ್ನೂ ಮಹಾಪ್ರಾಕಾರವನ್ನೂ ಮಾಡಿಸಿ ಮಹಾಪ್ರಾಕಾರಕ್ಕೆ ಬಾಗಿಲುಗಳನ್ನೂ ಇಟ್ಟನು. ಅವುಗಳ ಕದಗಳನ್ನು ಕಂಚಿನ ತಗಡಿನಿಂದ ಹೊದಿಸಿದನು.",
			"10": "<sup>10</sup> ಕಡಲು ಎನಿಸಿಕೊಳ್ಳುವ ಪಾತ್ರೆಯನ್ನು ದೇವಾಲಯದ ಬಲಗಡೆಯಲ್ಲಿ ಅಂದರೆ, ಪ್ರಾಕಾರದ ಆಗ್ನೇಯ ದಿಕ್ಕಿನಲ್ಲಿ ಇರಿಸಿದನು.",
			"11": "<sup>11</sup> ಇದಲ್ಲದೆ, ಹೂರಾಮನು ಹಂಡೆಗಳನ್ನೂ ಸಲಿಕೆಗಳನ್ನೂ ಬೋಗುಣಿಗಳನ್ನೂ ಮಾಡಿದನು. ಈ ಹೂರಾಮನು ಸೊಲೊಮೋನನ ಅಪ್ಪಣೆಯಂತೆ ದೇವಾಲಯಕ್ಕಾಗಿ ಮಾಡಿದ ಸಾಮಾನುಗಳು ಇವು:",
			"12": "<sup>12</sup> ಎರಡು ಕಂಬಗಳು, ಅವುಗಳ ಮೇಲಣ ಕುಂಭಾಕಾರದ ಎರಡು ತಲೆಗಳು, ಕುಂಭಗಳ ಮೇಲೆ ಹಾಕುವುದಕ್ಕಾಗಿ ಎರಡು ಜಾಲರಿಗಳು,",
			"13": "<sup>13</sup> ಕಂಬಗಳ ತುದಿಯಲ್ಲಿರುವ ಕುಂಭಗಳ ಜಾಲರಿಗಳ ಮೇಲೆ ಎರಡೆರಡು ಸಾಲಾಗಿ ಸಿಕ್ಕಿಸುವುದಕ್ಕಾಗಿ ತಾಮ್ರದ ನಾನೂರು ದಾಳಿಂಬೆ ಹಣ್ಣುಗಳು, ಪೀಠಗಳು,",
			"14": "<sup>14</sup> ಅವುಗಳ ಮೇಲಣ ಬಾನೆಗಳು, ಕಡಲಕೊಳ ಎನಿಸಿಕೊಳ್ಳುವ ಎರಕದ ಪಾತ್ರೆ,",
			"15": "<sup>15</sup> ಅದನ್ನು ಹೊರುವ ಹನ್ನೆರಡು ಎರಕದ ಹೋರಿಗಳು,",
			"16": "<sup>16</sup> ಹಂಡೆ, ಸಲಿಕೆ ಹಾಗೂ ಮುಳ್ಳು, ಹೀಗೆ ಹೂರಾಮಾಬೀಮನು ಅರಸ ಸೊಲೊಮೋನನ ಅಪ್ಪಣೆಯ ಮೇರೆಗೆ ಸರ್ವೇಶ್ವರನ ಆಲಯಕ್ಕಾಗಿ ಅದರ ಎಲ್ಲಾ ಸಾಮಾನುಗಳನ್ನು ಒಪ್ಪಹಾಕಿದ ಕಂಚಿನಿಂದ ಮಾಡಿ ಮುಗಿಸಿದನು.",
			"17": "<sup>17</sup> ಅರಸನು ಜೋರ್ಡನ್ ತಗ್ಗಿನಲ್ಲಿ ಸುಕ್ಕೋತಿಗೂ ಚೆರೇದಕ್ಕೂ ಮಧ್ಯದಲ್ಲಿರುವ ಕುಲುಮೆಯಲ್ಲಿ ಎರಕ ಹೊಯ್ಸಿದನು.",
			"18": "<sup>18</sup> ಸೊಲೊಮೋನನು ಮಾಡಿಸಿದ ಸಾಮಾನುಗಳು ಅನೇಕವಾಗಿದ್ದರಿಂದ ಅವುಗಳ ತಾಮ್ರದ ತೂಕ ಎಷ್ಟೆಂಬುದು ಗೊತ್ತಾಗದೆ ಹೋಯಿತು.",
			"19": "<sup>19</sup> ಅವನು ದೇವಾಲಯಕ್ಕಾಗಿ ಮಾಡಿಸಿದ ಒಳಗಿನ ಸಾಮಗ್ರಿ ಇವು: ಬಂಗಾರದ ಧೂಪವೇದಿ, ನೈವೇದ್ಯವಾದ ರೊಟ್ಟಿಗಳನ್ನಿಡತಕ್ಕ ಮೇಜುಗಳು,",
			"20": "<sup>20</sup> ನಿಯಮದ ಪ್ರಕಾರ ಮಹಾಪರಿಶುದ್ಧ ಸ್ಥಳದ ಮುಂದೆ ಉರಿಯುತ್ತಿರುವುದಕ್ಕಾಗಿ ಪುಷ್ಪಾಲಂಕಾರವುಳ್ಳ ಬಂಗಾರದ ದೀಪಸ್ತಂಭಗಳು,",
			"21": "<sup>21</sup> ಉತ್ಕೃಷ್ಟವಾದ ಬಂಗಾರದಿಂದ ಮಾಡಿದ ಅವುಗಳ ಹಣತೆ, ತಂಡಸ,",
			"22": "<sup>22</sup> ಚೊಕ್ಕ ಬಂಗಾರದ ಕತ್ತರಿ, ಬೋಗುಣಿ, ಧೂಪಾರತಿ, ಅಗ್ಗಿಷ್ಟಿಕೆ, ದೇವಾಲಯದ ಮಹಾಪರಿಶುದ್ಧ ಸ್ಥಳದೊಳಗಿನ ಬಾಗಿಲಿನ ಬಂಗಾರದ ಕದಗಳು, ಪರಿಶುದ್ಧ ಸ್ಥಳದ ಬಂಗಾರದ ಕದಗಳು."
		},
		"5": {
			"1": "<sup>1</sup> ಸೊಲೊಮೋನನು ಸರ್ವೇಶ್ವರಸ್ವಾಮಿಯ ಆಲಯಕ್ಕೆ ಬೇಕಾದ ಎಲ್ಲಾ ಸಾಮಾನುಗಳನ್ನು ಸಿದ್ಧಪಡಿಸಿದ ನಂತರ ತನ್ನ ತಂದೆ ದಾವೀದನು ಸರ್ವೇಶ್ವರನಿಗೆ ಹರಕೆಹೊತ್ತು ಮುಡಿಪಾಗಿಟ್ಟ ಬೆಳ್ಳಿಬಂಗಾರವನ್ನೂ ಎಲ್ಲ ಸಲಕರಣೆಗಳನ್ನೂ ದೇವಾಲಯದ ಭಂಡಾರಕ್ಕೆ ಸೇರಿಸಿದನು.",
			"2": "<sup>2</sup> ಅನಂತರ ಸೊಲೊಮೋನನು ದಾವೀದ ನಗರವಾದ ಸಿಯೋನಿನಿಂದ ಸರ್ವೇಶ್ವರನ ಒಡಂಬಡಿಕೆಯ ಮಂಜೂಷವನ್ನು ಮೇಲೆ ತರುವುದಕ್ಕಾಗಿ ಇಸ್ರಯೇಲರ ಹಿರಿಯರು, ಕುಲಾಧಿಪತಿಗಳು, ಇವರೇ ಮೊದಲಾದ ಎಲ್ಲಾ ಇಸ್ರಯೇಲ್ ಗಣ್ಯವ್ಯಕ್ತಿಗಳನ್ನು ಜೆರುಸಲೇಮಿಗೆ ಕರೆಸಿಕೊಂಡನು.",
			"3": "<sup>3</sup> ಇಸ್ರಯೇಲ್ಯರೆಲ್ಲರೂ ಏಳನೆಯ ತಿಂಗಳಿನ ಜಾತ್ರೆಗಾಗಿ ಅರಸನ ಬಳಿಗೆ ಕೂಡಿಬಂದರು.",
			"4": "<sup>4</sup> ಅವರ ಹಿರಿಯರೆಲ್ಲರೂ ಕೂಡಿಬಂದಿರಲಾಗಿ ಲೇವಿಯರು ಸರ್ವೇಶ್ವರನ ಮಂಜೂಷವನ್ನು ಹೊತ್ತುಕೊಂಡರು.",
			"5": "<sup>5</sup> ಯಾಜಕರಾದ ಲೇವಿಯರು ದೇವದರ್ಶನದ ಗುಡಾರವನ್ನೂ ಅದರಲ್ಲಿದ್ದ ಎಲ್ಲ ಪರಿಶುದ್ಧ ಉಪಕರಣಗಳನ್ನೂ ಹೊತ್ತುಕೊಂಡುಹೋದರು.",
			"6": "<sup>6</sup> ಅರಸ ಸೊಲೊಮೋನನೂ ಅಲ್ಲಿ ಕೂಡಿಬಂದಿದ್ದ ಎಲ್ಲ ಇಸ್ರಯೇಲರೂ ಮಂಜೂಷದ ಮುಂದೆ ಅಸಂಖ್ಯಾತ ಕುರಿದನಗಳನ್ನು ಬಲಿದಾನ ಮಾಡಿದರು.",
			"7": "<sup>7</sup> ಯಾಜಕರು ಸರ್ವೇಶ್ವರನ ಒಡಂಬಡಿಕೆಯ ಮಂಜೂಷವನ್ನು ತೆಗೆದುಕೊಂಡು ಬಂದು ಮಹಾಪರಿಶುದ್ಧ ಸ್ಥಳವಾದ ಗರ್ಭಗುಡಿಯಲ್ಲಿ, ಕೆರೂಬಿಗಳ ರೆಕ್ಕೆಗಳ ಕೆಳಗೆ, ಅದಕ್ಕೆಂದು ನೇಮಕವಾದ ಸ್ಥಳದಲ್ಲಿಟ್ಟರು.",
			"8": "<sup>8</sup> ಕೆರೂಬಿಗಳ ರೆಕ್ಕೆಗಳು ಮಂಜೂಷವಿದ್ದ ಸ್ಥಳದ ಮೇಲೆ ಚಾಚಿದ್ದವು. ಆದುದರಿಂದ ಮಂಜೂಷವೂ ಅದರ ಕೋಲುಗಳೂ ಪೂರ್ಣವಾಗಿ ಅವುಗಳ ನೆರಳಿನಲ್ಲಿದ್ದವು.",
			"9": "<sup>9</sup> ಮಂಜೂಷಕ್ಕಿಂತಲೂ ಉದ್ದವಾಗಿದ್ದ ಆ ಕೋಲುಗಳ ತುದಿಗಳು ಗರ್ಭಗುಡಿಯ ಎದುರಿನಲ್ಲಿ ನಿಂತವರಿಗೆ ಕಾಣಿಸುತ್ತಿದ್ದವು. ಆದರೆ ಹೊರಗೆ ನಿಂತವರಿಗೆ ಕಾಣಿಸುತ್ತಿರಲಿಲ್ಲ; ಮಂಜೂಷವು ಇಂದಿನವರೆಗೂ ಅಲ್ಲೇ ಇರುತ್ತದೆ.",
			"10": "<sup>10</sup> ಅದರಲ್ಲಿ ಎರಡು ಕಲ್ಲಿನ ಹಲಗೆಗಳು ಹೊರತಾಗಿ ಬೇರೇನೂ ಇರಲಿಲ್ಲ. ಈಜಿಪ್ಟ್ ದೇಶದಿಂದ ಬಂದ ಇಸ್ರಯೇಲರೊಡನೆ, ಹೋರೇಬ್‍ಬೆಟ್ಟದ ಬಳಿ, ಸರ್ವೇಶ್ವರ ಒಡಂಬಡಿಕೆ ಮಾಡಿಕೊಂಡ ಮೇಲೆ, ಮೋಶೆ ಅವುಗಳನ್ನು ಅದರಲ್ಲಿಟ್ಟಿದ್ದನು.",
			"11": "<sup>11</sup> ಕೂಡಿಬಂದಿದ್ದ ಯಾಜಕರು, ವರ್ಗವ್ಯತ್ಯಾಸವಿಲ್ಲದೆ ಎಲ್ಲರೂ ತಮ್ಮನ್ನು ಶುದ್ಧಪಡಿಸಿಕೊಂಡಿದ್ದರು.",
			"12": "<sup>12</sup> ನಾರುಮಡಿಗಳನ್ನು ಧರಿಸಿಕೊಂಡು ತಾಳ, ಸ್ವರಮಂಡಲ, ಕಿನ್ನರಿ, ಇವುಗಳನ್ನು ಹಿಡಿದುಕೊಂಡು, ಲೇವಿಯ ಗಾಯಕರಾದ ಆಸಾಫ್, ಹೇಮಾನ್, ಯೆದುತೂನರು ಹಾಗೂ ಇವರ ಮಕ್ಕಳೂ ಸಹೋದರರೂ ತುತೂರಿಗಳನ್ನು ಊದತಕ್ಕ ನೂರಿಪ್ಪತ್ತು ಮಂದಿ ಯಾಜಕರೂ ಬಲಿಪೀಠದ ಪೂರ್ವದಿಕ್ಕಿನಲ್ಲಿ ನಿಂತುಕೊಂಡರು.",
			"13": "<sup>13</sup> ಮಂಜೂಷವನ್ನು ಒಳಗಿಟ್ಟ ಯಾಜಕರು ದೇವಾಲಯದಿಂದ ಹೊರಗೆ ಬಂದ ಕೂಡಲೆ ಒಕ್ಕೊರಲಿನಿಂದ ಸ್ವರವೆತ್ತಿ ಸರ್ವೇಶ್ವರನನ್ನು ಕೀರ್ತಿಸುವುದಕ್ಕಾಗಿ, ತುತೂರಿ ಊದುವವರೂ ಗಾಯನಮಾಡುವವರೂ ಅಲ್ಲಿ ನಿಂತಿದ್ದರು. ತುತೂರಿ, ತಾಳ ಮೊದಲಾದ ವಾದ್ಯಗಳ ಧ್ವನಿ ಹಾಗೂ ‘ಸರ್ವೇಶ್ವರ ಒಳ್ಳೆಯವರು, ಅವರ ಅಚಲ ಪ್ರೀತಿ ಶಾಶ್ವತವಾದುದು’ ಎಂದು ಕೃತಜ್ಞತಾಸ್ತುತಿ ಮಾಡುವವರ ಸ್ವರ ಕೇಳಿಸಿದೊಡನೆ ಮೇಘವೊಂದು ಸರ್ವೇಶ್ವರನ ಆಲಯದಲ್ಲಿ ತುಂಬಿಕೊಂಡಿತು.",
			"14": "<sup>14</sup> ಸರ್ವೇಶ್ವರನ ತೇಜಸ್ಸಿನಿಂದ ವ್ಯಾಪ್ತವಾಗಿದ್ದ ಆ ಮೇಘವು ದೇವಾಲಯದಲ್ಲಿ ತುಂಬಿಕೊಂಡದ್ದರಿಂದ ಯಾಜಕರು ಅಲ್ಲಿ ನಿಂತು ಸೇವೆಮಾಡಲಾರದೆ ಹೋದರು."
		},
		"6": {
			"1": "<sup>1</sup> ಆಗ ಸೊಲೊಮೋನನು ಹೀಗೆಂದು ಪ್ರಾರ್ಥಿಸಿದನು: “ಹೇ ಸರ್ವೇಶ್ವರಾ, ರವಿಯನ್ನು ಆಗಸದಲ್ಲಿ ಸ್ಥಿರವಾಗಿರಿಸಿದ ನೀವು ಆರಿಸಿಕೊಂಡಿರಿ ಕರಿಮೋಡದಲಿ ವಾಸಿಸಲು.",
			"2": "<sup>2</sup> ನಾ ನಿರ್ಮಿಸಿರುವ ಈ ಭವ್ಯ ಮಂದಿರ ನಿಮಗಾಗಲಿ ಶಾಶ್ವತ ನಿವಾಸ.”",
			"3": "<sup>3</sup> ಇದಾದ ಮೇಲೆ ಎದ್ದು ನಿಂತಿದ್ದ ಇಸ್ರಯೇಲ್ ಸಮಾಜದ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡು ಅವರನ್ನು ಆಶೀರ್ವದಿಸಿದನು.",
			"4": "<sup>4</sup> “ಇಸ್ರಯೇಲಿನ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ಸ್ತೋತ್ರವಾಗಲಿ! ನನ್ನ ತಂದೆ ದಾವೀದನಿಗೆ ಅವರು ಬಾಯಿಂದ ನುಡಿದದ್ದನ್ನು ಈಗ ಕೈಯಿಂದ ನೆರವೇರಿಸಿದ್ದಾರೆ.",
			"5": "<sup>5</sup> ಅವರು, ‘ನನ್ನ ಜನರಾದ ಇಸ್ರಯೇಲರನ್ನು ಈಜಿಪ್ಟಿನಿಂದ ಬರಮಾಡಿದಂದಿನಿಂದ ನನ್ನ ನಾಮದ ನಿವಾಸಕ್ಕಾಗಿ ಇಸ್ರಯೇಲ್ ಕುಲಗಳಲ್ಲಿ ಯಾವ ಪಟ್ಟಣವನ್ನೂ ಆಲಯಸ್ಥಾನವನ್ನಾಗಿ ಆರಿಸಿಕೊಳ್ಳಲಿಲ್ಲ; ನನ್ನ ಪ್ರಜೆ ಇಸ್ರಯೇಲರನ್ನು ಆಳುವುದಕ್ಕೆ ಯಾವ ವ್ಯಕ್ತಿಯನ್ನು ಆರಿಸಿಕೊಂಡದ್ದೂ ಇಲ್ಲ.",
			"6": "<sup>6</sup> ಆದರೆ ಈಗ ನನ್ನ ನಾಮಸ್ಥಾಪನೆಗಾಗಿ ಜೆರುಸಲೇಮನ್ನೂ ನನ್ನ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಯೇಲರನ್ನು ಆಳುವುದಕ್ಕೆ ದಾವೀದನನ್ನೂ ಆರಿಸಿಕೊಂಡಿದ್ದೇನೆ’, ಎಂದು ಹೇಳಿದ್ದರು.",
			"7": "<sup>7</sup> “ನನ್ನ ತಂದೆ ದಾವೀದನು ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಹೆಸರಿಗಾಗಿ ಒಂದು ಆಲಯವನ್ನು ಕಟ್ಟಬೇಕೆಂದು ಮನಸ್ಸು ಮಾಡಿದಾಗ,",
			"8": "<sup>8</sup> ‘ನೀನು ನನ್ನ ಹೆಸರಿಗಾಗಿ ಒಂದು ಮನೆಯನ್ನು ಕಟ್ಟುವುದಕ್ಕೆ ಮನಸ್ಸು ಮಾಡಿದ್ದು ಒಳ್ಳೆಯದೇ ಸರಿ.",
			"9": "<sup>9</sup> ಆದರೂ ಅದನ್ನು ಕಟ್ಟಬೇಕಾದವನು ನೀನಲ್ಲ. ನಿನ್ನಿಂದ ಹುಟ್ಟುವ ಮಗನು ಅದನ್ನು ನನ್ನ ಹೆಸರಿಗಾಗಿ ಕಟ್ಟಬೇಕು,’ ಎಂದು ಹೇಳಿದ್ದರು.",
			"10": "<sup>10</sup> ತಾವು ಕೊಟ್ಟ ಮಾತನ್ನು ಸರ್ವೇಶ್ವರ ನೆರವೇರಿಸಿದ್ದಾರೆ. ಅವರ ವಾಗ್ದಾನದಂತೆ ನಾನು ನನ್ನ ತಂದೆ ದಾವೀದನ ಸ್ಥಾನದಲ್ಲಿ ಕುಳಿತುಕೊಂಡಿದ್ದೇನೆ. ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಹೆಸರಿಗಾಗಿ ಈ ಆಲಯವನ್ನು ಕಟ್ಟಿಸಿದ್ದೇನೆ.",
			"11": "<sup>11</sup> ಇದಲ್ಲದೆ ಇಸ್ರಯೇಲರಿಗೆ ಸರ್ವೇಶ್ವರನಿಂದ ದೊರಕಿದ ನಿಬಂಧನಶಾಸನಗಳಿರುವ ಮಂಜೂಷವನ್ನು ಅದರಲ್ಲಿಟ್ಟಿದ್ದೇನೆ,” ಎಂದು ಹೇಳಿದನು.",
			"12": "<sup>12</sup> ಸೊಲೊಮೋನನು ಇಸ್ರಯೇಲರೆಲ್ಲರ ಎದುರಿನಲ್ಲಿ ಸರ್ವೇಶ್ವರನ ಬಲಿಪೀಠದ ಮುಂದೆ ಕೈಗಳನ್ನೆತ್ತಿಕೊಂಡು ಪ್ರಾರ್ಥನಾ ಭಂಗಿಯಲ್ಲಿ ನಿಂತನು.",
			"13": "<sup>13</sup> ಅವನು 2:2 ಮೀಟರ್ ಉದ್ದ, 2:2 ಮೀಟರ್ ಅಗಲ, 1:3 ಮೀಟರ್ ಎತ್ತರವಾಗಿರುವ ಒಂದು ಕಂಚಿನ ಪೀಠವನ್ನು ಮಾಡಿಸಿ ಪ್ರಾಕಾರದ ಮಧ್ಯದಲ್ಲಿರಿಸಿದನು.ಇಸ್ರಯೇಲ್ ಸರ್ವಸಮೂಹದ ಎದುರಿನಲ್ಲಿ ಅದನ್ನು ಹತ್ತಿ ಅದರ ಮೇಲೆ ಮೊಣಕಾಲೂರಿಕೊಂಡು, ಆಕಾಶದ ಕಡೆಗೆ ಕೈಗಳನ್ನೆತ್ತಿ, ಹೀಗೆ ಪ್ರಾರ್ಥಿಸಿದನು:",
			"14": "<sup>14</sup> “ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರಾ, ಭೂಲೋಕದಲ್ಲೂ ಪರಲೋಕದಲ್ಲೂ ನಿಮಗೆ ಸಮಾನರಾದ ದೇವರಿಲ್ಲ; ಪೂರ್ಣಮನಸ್ಸಿನಿಂದ ನಿಮಗೆ ಪ್ರಾಮಾಣಿಕರಾಗಿ ನಡೆದುಕೊಳ್ಳುವಂಥ ಭಕ್ತರ ಬಗ್ಗೆ ನೀವು ನಿಮ್ಮ ಒಡಂಬಡಿಕೆಯನ್ನು ನೆರವೇರಿಸಿ ಕೃಪೆಯನ್ನು ತೋರಿಸುತ್ತೀರಿ.",
			"15": "<sup>15</sup> ನಿಮ್ಮ ದಾಸನೂ ನನ್ನ ತಂದೆಯೂ ಆದ ದಾವೀದನಿಗೆ ಕೊಟ್ಟ ಮಾತನ್ನು ಈಡೇರಿಸಿದ್ದೀರಿ; ನಿಮ್ಮ ಬಾಯಿ ನುಡಿದದ್ದನ್ನು ನಿಮ್ಮ ಕೈ ಈಗ ನೆರವೇರಿಸಿತು.",
			"16": "<sup>16</sup> ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರಾ, ನೀವು ನನ್ನ ತಂದೆ ದಾವೀದನಿಗೆ, ‘ನಿನ್ನ ಸಂತಾನದವರು ನಿನ್ನಂತೆ ಜಾಗರೂಕತೆಯಿಂದ ನನ್ನ ಕಟ್ಟಳೆಯನ್ನು ಅನುಸರಿಸಿ ನನ್ನ ಮಾರ್ಗದಲ್ಲಿ ನಡೆದುಕೊಂಡರೆ ಅವರು ಇಸ್ರಯೇಲ್ ಸಿಂಹಾಸನದ ಮೇಲೆ ಕುಳಿತುಕೊಳ್ಳುವುದು ತಪ್ಪದು’ ಎಂಬುದಾಗಿ ವಾಗ್ದಾನ ಮಾಡಿದ್ದೀರಿ; ಅದನ್ನು ನೆರವೇರಿಸಿರಿ.",
			"17": "<sup>17</sup> ಇಸ್ರಯೇಲ್ ದೇವರೇ, ಸರ್ವೇಶ್ವರಾ, ನೀವು ನಿಮ್ಮ ದಾಸ ದಾವೀದನಿಗೆ ನುಡಿದದ್ದೆಲ್ಲವೂ ಸಾರ್ಥಕವಾಗಲಿ!",
			"18": "<sup>18</sup> “ದೇವರು ನಿಜವಾಗಿ ಮಾನವರೊಡನೆ ಭೂಲೋಕದಲ್ಲಿ ವಾಸಿಸುವರೋ? ಆಕಾಶವೂ ಉನ್ನತೋನ್ನತವಾದ ಆಕಾಶವೂ ನಿಮ್ಮ ವಾಸಕ್ಕೆ ಸಾಲವು; ಹೀಗಿರುವಲ್ಲಿ ನಾನು ಕಟ್ಟಿಸಿದ ಈ ಮಂದಿರವು ಹೇಗೆ ತಾನೆ ಸಾಕಾದೀತು?",
			"19": "<sup>19</sup> ಆದರೂ ನನ್ನ ದೇವರೇ, ಸರ್ವೇಶ್ವರಾ, ನಿಮ್ಮ ದಾಸನ ಪ್ರಾರ್ಥನೆಗೂ ವಿಜ್ಞಾಪನೆಗೂ ಕಿವಿಗೊಡಿ; ನಿಮ್ಮನ್ನು ಪ್ರಾರ್ಥಿಸುತ್ತಿರುವ ನಿಮ್ಮ ದಾಸನ ಮೊರೆಯನ್ನು ಆಲಿಸಿರಿ.",
			"20": "<sup>20</sup> ಈ ಸ್ಥಳವನ್ನು ಕುರಿತು, ‘ನನ್ನ ನಾಮದ ಮಹಿಮೆ ಇಲ್ಲಿ ನೆಲೆಸುವುದು’ ಎಂದು ಹೇಳಿದ್ದೀರಿ, ನಿಮ್ಮ ಕೃಪಾಕಟಾಕ್ಷವು ಹಗಲಿರುಳೂ ಈ ಮಂದಿರದ ಮೇಲಿರಲಿ; ಇಲ್ಲಿ ನಿಮ್ಮ ದಾಸನು ಪ್ರಾರ್ಥಿಸುವಾಗಲೆಲ್ಲಾ ಅವನಿಗೆ ಸದುತ್ತರವನ್ನು ದಯಪಾಲಿಸಿರಿ.",
			"21": "<sup>21</sup> ನಿಮ್ಮ ದಾಸನಾದ ನಾನಾಗಲಿ, ನಿಮ್ಮ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಯೇಲರಾಗಲಿ, ಈ ಸ್ಥಳದ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡು ನಿಮ್ಮನ್ನು ಪ್ರಾರ್ಥಿಸುವಾಗ ನಿಮ್ಮ ನಿವಾಸವಾಗಿರುವ ಪರಲೋಕದಿಂದ ನಮ್ಮ ಬಿನ್ನಹಗಳನ್ನು ಕೇಳಿ, ಕ್ಷಮೆಯನ್ನು ಅನುಗ್ರಹಿಸಿರಿ.",
			"22": "<sup>22</sup> “ನೆರೆಯವನಿಗೆ ವಿರುದ್ಧ ತಪ್ಪುಮಾಡಿದವನು ಎಂಬ ಸಂಶಯಕ್ಕೆ ಗುರಿಯಾದ ಒಬ್ಬ ವ್ಯಕ್ತಿ ತಾನು ನಿರ್ದೋಷಿಯೆಂದು ಪ್ರಮಾಣಮಾಡಬೇಕಾದಾಗ, ಅವನು ಈ ಆಲಯಕ್ಕೆ ಬಂದು, ನಿಮ್ಮ ಪೀಠದ ಮುಂದೆ ನಿಂತು ಪ್ರಮಾಣಮಾಡಿದರೆ,",
			"23": "<sup>23</sup> ನೀವು ಪರಲೋಕದಿಂದ ಅದನ್ನು ಕೇಳಿ ನಿಮ್ಮ ಭಕ್ತರ ವ್ಯಾಜ್ಯವನ್ನು ತೀರಿಸಿರಿ. ದುಷ್ಟನನ್ನು ಖಂಡಿಸಿ ಅವನ ತಪ್ಪನ್ನು ಅವನ ಮೇಲೆಯೇ ಹೊರಿಸಿರಿ; ಅವನು ಅಪರಾಧಿಯೆಂದು ತೋರಿಸಿಕೊಡಿ. ನೀತಿವಂತನಿಗೆ ಅವನ ನೀತಿಯ ಫಲವನ್ನು ಅನುಗ್ರಹಿಸಿ, ಅವನು ನೀತಿವಂತನೆಂದು ತೋರಿಸಿಕೊಡಿ.",
			"24": "<sup>24</sup> “ತಮ್ಮ ಪಾಪಗಳ ನಿಮಿತ್ತ ಶತ್ರುಗಳ ಮುಂದೆ ತಲೆತಗ್ಗಿಸಬೇಕಾಗುವ ನಿಮ್ಮ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಯೇಲರು ನಿಮ್ಮ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡು, ನಿಮ್ಮ ನಾಮವನ್ನು ಎತ್ತಿ, ಈ ಆಲಯಕ್ಕೆ ಬಂದು, ನಿಮ್ಮ ಸನ್ನಿಧಿಯಲ್ಲಿ ವಿಜ್ಞಾಪನೆಯನ್ನೂ ಪ್ರಾರ್ಥನೆಯನ್ನೂ ಮಾಡಿದರೆ,",
			"25": "<sup>25</sup> ನೀವು ಪರಲೋಕದಿಂದ ಆಲಿಸಿ ಅವರ ಪಾಪಗಳನ್ನು ಕ್ಷಮಿಸಿ, ಅವರಿಗೂ ಅವರ ಪಿತೃಗಳಿಗೂ ನೀವು ಕೊಟ್ಟ ನಾಡಿಗೆ ಅವರನ್ನು ಮರಳಿ ಬರಮಾಡಿ.",
			"26": "<sup>26</sup> “ಅವರ ಪಾಪಗಳ ನಿಮಿತ್ತ ನೀವು ಆಕಾಶವನ್ನು ಮಳೆಗರೆಯದಂತೆ ಮುಚ್ಚಿ, ಅವರನ್ನು ತಗ್ಗಿಸಿದಾಗ, ಅವರು ತಮ್ಮ ಪಾಪಗಳನ್ನು ಬಿಟ್ಟು ತಮ್ಮನ್ನು ತಗ್ಗಿಸಿದವರು ನೀವೇ ಎಂದು ನಿಮ್ಮ ನಾಮವನ್ನು ಎತ್ತಿ, ಈ ಆಲಯದ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡು, ನಿಮ್ಮನ್ನು ಪ್ರಾರ್ಥಿಸಿದರೆ,",
			"27": "<sup>27</sup> ಪರಲೋಕದಲ್ಲಿರುವ ನೀವು ಆಲಿಸಿ ನಿಮ್ಮ ಭಕ್ತರೂ ಪ್ರಜೆಗಳೂ ಆದ ಇಸ್ರಯೇಲರ ಪಾಪಗಳನ್ನು ಕ್ಷಮಿಸಿ, ಅವರು ನಡೆಯಬೇಕಾದ ಸನ್ಮಾರ್ಗವನ್ನು ತೋರಿಸಿ, ನಿಮ್ಮ ಪ್ರಜೆಗೆ ಸ್ವಂತ ಸೊತ್ತಾಗಿ ಕೊಟ್ಟ ಈ ನಾಡಿಗೆ ಮಳೆಯನ್ನು ಅನುಗ್ರಹಿಸಿರಿ.",
			"28": "<sup>28</sup> “ನಾಡಿಗೆ ಕ್ಷಾಮ, ಘೋರವ್ಯಾಧಿ, ಬೆಳೆಗೆ ಬಿಸಿಗಾಳಿ, ಬೂದಿ, ಮಿಡತೆ, ಚಿಟ್ಟೆಹುಳ, ಪಟ್ಟಣಗಳಿಗೆ ಶತ್ರುಗಳ ಮುತ್ತಿಗೆ, ಇಂಥ ಯಾವ ಉಪದ್ರವದಿಂದಾಗಲಿ, ವ್ಯಾಧಿಯಿಂದಾಗಲಿ, ಬಾಧೆಯುಂಟಾದರೆ",
			"29": "<sup>29</sup> ಎಲ್ಲಾ ಇಸ್ರಯೇಲರಾಗಲಿ, ಅವರಲ್ಲೊಬ್ಬನಾಗಲಿ, ತಾವು ಅನುಭವಿಸುತ್ತಿರುವ ಉಪದ್ರವ, ದುಃಖಗಳ ನಿಮಿತ್ತ ಈ ಆಲಯದ ಕಡೆಗೆ ಕೈಯೆತ್ತಿ ನಿಮಗೆ ಪ್ರಾರ್ಥನೆಯನ್ನೂ ವಿಜ್ಞಾಪನೆಯನ್ನೂ ಮಾಡಿದರೆ,",
			"30": "<sup>30</sup> ಮನುಷ್ಯರ ಹೃದಯಗಳನ್ನು ಬಲ್ಲಂಥ ನೀವು ನಿಮ್ಮ ನಿವಾಸವಾಗಿರುವ ಪರಲೋಕದಿಂದ ಆಲಿಸಿರಿ; ಅವರಿಗೆ ಪಾಪಪರಿಹಾರವನ್ನು ಅನುಗ್ರಹಿಸಿರಿ. ಮನುಷ್ಯರ ಅಂತರಂಗಗಳನ್ನು ಬಲ್ಲವರು ನೀವೊಬ್ಬರೇ. ಪ್ರತಿಯೊಬ್ಬನಿಗೂ ಅವನವನು ಹಿಡಿಯುವ ಮಾರ್ಗಕ್ಕೆ ತಕ್ಕಹಾಗೆ ಫಲವನ್ನು ಕೊಡಿ.",
			"31": "<sup>31</sup> ಆಗ ನಮ್ಮ ಪಿತೃಗಳಿಗೆ ನೀವು ಕೊಟ್ಟ ಈ ನಾಡಿನಲ್ಲಿ ಅವರು ವಾಸಿಸುವ ಕಾಲವೆಲ್ಲಾ ನಿಮ್ಮಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿ ನಿಮ್ಮ ಮಾರ್ಗದಲ್ಲೇ ನಡೆಯುವರು.",
			"32": "<sup>32</sup> “ನಿಮ್ಮ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಯೇಲರಿಗೆ ಸೇರದಂಥ ಒಬ್ಬ ಪರದೇಶಿಯು ನಿಮ್ಮ ನಾಮಮಹತ್ತು, ಭುಜಬಲ, ಶಿಕ್ಷಾಹಸ್ತ ಇವುಗಳ ಸಂದೇಶವನ್ನು ಕೇಳಿ ದೂರದೇಶದಿಂದ ಬಂದು, ಈ ಆಲಯದ ಮುಂದೆ ನಿಂತು, ನಿಮ್ಮನ್ನು ಪ್ರಾರ್ಥಿಸಿದರೆ ನಿಮ್ಮ ನಿವಾಸವಾಗಿರುವ",
			"33": "<sup>33</sup> ಪರಲೋಕದಿಂದ ಅವನ ಪ್ರಾರ್ಥನೆಯನ್ನು ಕೇಳಿ ಅವನು ಬೇಡಿಕೊಂಡದ್ದನ್ನು ಅನುಗ್ರಹಿಸಿರಿ. ಆಗ ಲೋಕದ ಎಲ್ಲ ಜನರೂ ನಿಮ್ಮ ನಾಮಮಹತ್ತನ್ನು ತಿಳಿದು, ನಿಮ್ಮ ಜನರಾದ ಇಸ್ರಯೇಲರಂತೆ, ನಿಮ್ಮಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿ ನಾನು ನಿಮ್ಮ ಹೆಸರಿಗಾಗಿ ಈ ಆಲಯವನ್ನು ಕಟ್ಟಿಸಿದ್ದೇನೆಂದು ತಿಳಿದುಕೊಳ್ಳುವರು.",
			"34": "<sup>34</sup> “ನೀವು ನಿಮ್ಮ ಜನರನ್ನು ಶತ್ರುಗಳೊಡನೆ ಯುದ್ಧಮಾಡುವುದಕ್ಕೆ ಎಲ್ಲಿಗಾದರೂ ಕಳುಹಿಸಿದಾಗ ಅವರು ಅಲ್ಲಿಂದ ನೀವು ಆರಿಸಿಕೊಂಡ ಪಟ್ಟಣದ ಕಡೆಗೂ ನಾನು ನಿಮ್ಮ ಹೆಸರಿಗಾಗಿ ಕಟ್ಟಿಸಿದ ಆ ಆಲಯದ ಕಡೆಗೂ ತಿರುಗಿಕೊಂಡು ನಿಮ್ಮನ್ನು ಪ್ರಾರ್ಥಿಸಿದರೆ,",
			"35": "<sup>35</sup> ನೀವು ಪರಲೋಕದಿಂದ ಅವರ ವಿಜ್ಞಾಪನೆಯನ್ನೂ ಪ್ರಾರ್ಥನೆಯನ್ನೂ ಕೇಳಿ ಅವರಿಗೆ ಜಯವನ್ನು ನೀಡಿರಿ.",
			"36": "<sup>36</sup> “ಅವರು ನಿಮಗೆ ವಿರುದ್ಧ ಪಾಪಮಾಡಬಹುದು. ಪಾಪಮಾಡದ ವ್ಯಕ್ತಿ ಒಬ್ಬನೂ ಇಲ್ಲ. ನೀವು ಅವರ ಮೇಲೆ ಕೋಪಗೊಂಡು ಅವರನ್ನು ಶತ್ರುಗಳ ಕೈಗೆ ಒಪ್ಪಿಸಿದಾಗ ಮತ್ತು ಆ ಶತ್ರುಗಳು ಅವರನ್ನು ಸೆರೆಹಿಡಿದು ದೂರದಲ್ಲಾಗಲಿ, ಸಮೀಪದಲ್ಲಾಗಲಿ ಇರುವ ದೇಶಕ್ಕೆ ಒಯ್ದಾಗ,",
			"37": "<sup>37</sup> ಅವರು ಸೆರೆಯವರಾಗಿ ಬಿದ್ದುಕೊಂಡಿರುವ ದೇಶದಲ್ಲಿ ಪಶ್ಚಾತ್ತಾಪಪಟ್ಟು, ನಿಮ್ಮ ಅನುಗ್ರಹದಿಂದ",
			"38": "<sup>38</sup> ತಮ್ಮ ಪಿತೃಗಳಿಗೆ ದೊರಕಿದ ನಾಡಿನ ಕಡೆಗೆ, ನೀವು ಆರಿಸಿಕೊಂಡ ಪಟ್ಟಣದ ಕಡೆಗೆ, ನಾನು ನಿಮ್ಮ ಹೆಸರಿಗಾಗಿ ಕಟ್ಟಿಸಿದ ಈ ಆಲಯದ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡು, ‘ನಾವು ನಿಮ್ಮ ಆಜ್ಞೆಗಳನ್ನು ಮೀರಿ ಪಾಪಮಾಡಿ ದ್ರೋಹಿಗಳಾದೆವು’ ಎಂದು ಒಪ್ಪಿಕೊಂಡು ಪೂರ್ಣಮನಸ್ಸಿನಿಂದ ಹಾಗೂ ಪೂರ್ಣಪ್ರಾಣದಿಂದ ನಿಮ್ಮನ್ನು ಪ್ರಾರ್ಥಿಸಿದರೆ,",
			"39": "<sup>39</sup> ನೀವು ನಿಮ್ಮ ನಿವಾಸವಾಗಿರುವ ಪರಲೋಕದಿಂದ ಅವರ ವಿಜ್ಞಾಪನೆಯನ್ನೂ ಪ್ರಾರ್ಥನೆಯನ್ನೂ ಆಲಿಸಿ, ಅವರಿಗೆ ದಯೆ ತೋರಿಸಿ, ನಿಮಗೆ ವಿರುದ್ಧ ಪಾಪಮಾಡಿದ ನಿಮ್ಮ ಪ್ರಜೆಗೆ ಕ್ಷಮೆಯನ್ನು ಅನುಗ್ರಹಿಸಿರಿ.",
			"40": "<sup>40</sup> “ನನ್ನ ದೇವರೇ, ಕಟಾಕ್ಷವಿಡಿ, ಈ ಸ್ಥಳದಲ್ಲಿ ಮಾಡುವ ಪ್ರಾರ್ಥನೆಗೆ ಕಿವಿಗೊಡಿ.",
			"41": "<sup>41</sup> ದೇವರಾದ ಸರ್ವೇಶ್ವರಾ, ಎದ್ದು ಬನ್ನಿ, ನಿಮ್ಮ ತೇಜೋಮಯ ಮಂಜೂಷದೊಡನೆ ನಿಮ್ಮ ವಾಸಸ್ಥಾನದೊಳಗೆ ಸೇರಿಕೊಳ್ಳಿ. ದೇವರಾದ ಸರ್ವೇಶ್ವರಾ, ನಿಮ್ಮ ಯಾಜಕರು ರಕ್ಷಣೆಯೆಂಬ ಕವಚವನ್ನು ಹೊದ್ದುಕೊಳ್ಳಲಿ, ನಿಮ್ಮ ಭಕ್ತರು ಸೌಭಾಗ್ಯದಲ್ಲಿ ಹರ್ಷಿಸಲಿ.",
			"42": "<sup>42</sup> ದೇವರಾದ ಸರ್ವೇಶ್ವರಾ, ನಿಮ್ಮಿಂದ ಅಭಿಷೇಕ ಆದವನನ್ನು ತಳ್ಳಿಬಿಡಬೇಡಿ. ನಿಮ್ಮ ದಾಸ ದಾವೀದನಿಗೆ ಅನುಗ್ರಹಿಸಿದ ಕೃಪೆಯನ್ನು ನೆನಪುಮಾಡಿಕೊಳ್ಳಿ.”"
		},
		"7": {
			"1": "<sup>1</sup> ಸೊಲೊಮೋನನು ತನ್ನ ಪ್ರಾರ್ಥನೆಯನ್ನು ಮುಗಿಸಿದ ಕೂಡಲೆ ಆಕಾಶದಿಂದ ಬೆಂಕಿ ಇಳಿದು ಬಂದು ದಹನಬಲಿದ್ರವ್ಯಗಳನ್ನೂ ಬಲಿಮಾಂಸವನ್ನೂ ದಹಿಸಿಬಿಟ್ಟಿತು; ಸರ್ವೇಶ್ವರನ ತೇಜಸ್ಸು ಆಲಯದಲ್ಲಿ ತುಂಬಿಕೊಂಡಿತು.",
			"2": "<sup>2</sup> ಸರ್ವೇಶ್ವರನ ಆ ತೇಜಸ್ಸು ಆಲಯದಲ್ಲಿ ತುಂಬಿಕೊಂಡಿದ್ದರಿಂದ ಯಾಜಕರು ಒಳಗೆ ಪ್ರವೇಶಿಸಲಾರದೆ ಹೋದರು.",
			"3": "<sup>3</sup> ಇಸ್ರಯೇಲರೆಲ್ಲರು ಬೆಂಕಿ ಇಳಿದು ಬರುವುದನ್ನೂ ಸರ್ವೇಶ್ವರನ ತೇಜಸ್ಸು ಆಲಯದಲ್ಲಿರುವುದನ್ನೂ ನೋಡಿ ನೆಲಗಟ್ಟಿನ ಮೇಲೆ ಸಾಷ್ಟಾಂಗವೆರಗಿ ಆರಾಧಿಸಿದರು; “ಸರ್ವೇಶ್ವರ ಒಳ್ಳೆಯವರು; ಅವರ ಅಚಲ ಪ್ರೀತಿ ಶಾಶ್ವತ” ಎಂದು ಕೃತಜ್ಞತಾಸ್ತುತಿ ಮಾಡಿದರು.",
			"4": "<sup>4</sup> ಅರಸನೂ ಜನರೆಲ್ಲರೂ ಸರ್ವೇಶ್ವರನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಬಲಿದಾನಮಾಡಿದರು.",
			"5": "<sup>5</sup> ಅರಸ ಸೊಲೊಮೋನನು ಬಲಿಯಾಗಿ ವಧಿಸಿದ ಹೋರಿಗಳು ಇಪ್ಪತ್ತೆರಡು ಸಾವಿರ, ಕುರಿಗಳು ಲಕ್ಷದ ಇಪ್ಪತ್ತು ಸಾವಿರ. ಈ ಪ್ರಕಾರ ಅರಸನೂ ಇಸ್ರಯೇಲರೆಲ್ಲರೂ ದೇವಾಲಯವನ್ನು ಪ್ರತಿಷ್ಠಿಸಿದರು.",
			"6": "<sup>6</sup> ಯಾಜಕರೂ ಲೇವಿಯರೂ ತಮ್ಮ ತಮ್ಮ ಸ್ಥಳಗಳಲ್ಲಿ ನಿಂತರು. ದಾವೀದನು ಸರ್ವೇಶ್ವರನ ಗಾಯನಸೇವೆಗಾಗಿ ಮಾಡಿಸಿದ ವಾದ್ಯಗಳು ಈ ಲೇವಿಯರ ಕೈಯಲ್ಲಿ ಇದ್ದವು. ಇವರು ರಾಜಸೇವೆಯಲ್ಲಿದ್ದುಕೊಂಡು ದೇವಾರಾಧನೆ ನಡೆಸುತ್ತಾ, “ಸರ್ವೇಶ್ವರನಿಗೆ ಕೃತಜ್ಞತಾ ಸ್ತುತಿಯನ್ನು ಸಮರ್ಪಿಸಿರಿ; ಅವರ ಅಚಲಪ್ರೀತಿ ಶಾಶ್ವತ” ಎಂಬುದಾಗಿ ಆ ವಾದ್ಯಗಳೊಡನೆ ಭಜಿಸುತ್ತಿದ್ದರು. ಯಾಜಕರು ಇವರ ಎದುರಾಗಿ ನಿಂತು ತುತೂರಿಯೂದುತ್ತಿದ್ದರು. ಇಸ್ರಯೇಲರೆಲ್ಲರೂ ನಿಂತಿದ್ದರು.",
			"7": "<sup>7</sup> ಸೊಲೊಮೋನನು ಮಾಡಿಸಿದ್ದ ಕಂಚಿನ ಬಲಿಪೀಠವು, ಸಮರ್ಪಣೆಯಾದ ದಹನಬಲಿದ್ರವ್ಯಗಳನ್ನೂ ಧಾನ್ಯನೈವೇದ್ಯಗಳನ್ನೂ ಶಾಂತಿಸಮಾಧಾನಬಲಿಯ ಕೊಬ್ಬನ್ನೂ ಹಿಡಿಯಲಾಗಲಿಲ್ಲ. ಆದ್ದರಿಂದ ಅವನು ಸರ್ವೇಶ್ವರನ ಆಲಯದ ಮುಂದಿನ ಪ್ರಾಕಾರದ ಮಧ್ಯಸ್ಥಳವನ್ನು ಪ್ರತಿಷ್ಠಿಸಿ, ಅಲ್ಲಿ ಇವುಗಳನ್ನೆಲ್ಲಾ ಸಮರ್ಪಿಸಿದನು.",
			"8": "<sup>8</sup> ಈ ಪ್ರಕಾರ ಸೊಲೊಮೋನನು ಮತ್ತು ಹಮಾತಿನ ದಾರಿಯಿಂದ ಈಜಿಪ್ಟಿನ ಹಳ್ಳದವರೆಗಿರುವ ಪ್ರಾಂತ್ಯಗಳಿಂದ ಮಹಾಸಮೂಹವಾಗಿ ಕೂಡಿಬಂದಿದ್ದ ಇಸ್ರಯೇಲರೆಲ್ಲರು ಅದೇ ಸಮಯದಲ್ಲಿ ಏಳು ದಿನಗಳವರೆಗೂ ಜಾತ್ರೆ ನಡೆಸಿದರು.",
			"9": "<sup>9</sup> ತರುವಾಯ ಎಂಟನೆಯ ದಿನದಲ್ಲಿ ಸಭೆಸೇರಿದರು. ಹೀಗೆ ಏಳು ದಿವಸ ಬಲಿಪೀಠ ಪ್ರತಿಷ್ಠೆಯನ್ನೂ ಇನ್ನು ಏಳು ದಿವಸ ಜಾತ್ರೆಯನ್ನೂ ನಡೆಸಿದರು.",
			"10": "<sup>10</sup> ಏಳನೆಯ ತಿಂಗಳಿನ ಇಪ್ಪತ್ತಮೂರನೆಯ ದಿನ ಅರಸನು ಜನರಿಗೆ ಅಪ್ಪಣೆಕೊಟ್ಟನು; ಅವರು ದಾವೀದ - ಸೊಲೊಮೋನರಿಗೂ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಯೇಲರಿಗೂ ಸರ್ವೇಶ್ವರ ಮಾಡಿದ ಉಪಕಾರಗಳನ್ನು ನೆನಸಿಕೊಂಡು, ಆನಂದಭರಿತರಾಗಿ ಹರ್ಷಿಸುತ್ತಾ, ತಮ್ಮ ತಮ್ಮ ನಿವಾಸಗಳಿಗೆ ಹಿಂದಿರುಗಿದರು.",
			"11": "<sup>11</sup> ಸೊಲೊಮೋನನು ಸರ್ವೇಶ್ವರನ ಆಲಯವನ್ನೂ ತನ್ನ ಅರಮನೆಯನ್ನೂ ಕಟ್ಟಿಸಿ, ಆ ಕಟ್ಟಡಗಳ ವಿಷಯವಾಗಿ ತನ್ನ ಮನಸ್ಸಿನಲ್ಲಿ ಇದ್ದ ಯೋಜನೆಯನ್ನು ಈಡೇರಿಸಿದನು.",
			"12": "<sup>12</sup> ಬಳಿಕ ಸರ್ವೇಶ್ವರ ಅವನಿಗೆ ರಾತ್ರಿಯಲ್ಲಿ ಪ್ರತ್ಯಕ್ಷರಾಗಿ, “ನೀನು ನನಗೆ ಮಾಡಿದ ಪ್ರಾರ್ಥನೆಯನ್ನು ಆಲಿಸಿ, ಈ ಮನೆಯನ್ನು ಬಲಿಯರ್ಪಣಾ ಮಂದಿರಕ್ಕಾಗಿ ಆರಿಸಿಕೊಂಡಿದ್ದೇನೆ.",
			"13": "<sup>13</sup> ನಾನು ಆಕಾಶವನ್ನು ಮಳೆಗರೆಯದಂತೆ ಮುಚ್ಚುವಾಗಲು, ನಾಡನ್ನು ನಾಶಮಾಡುವುದಕ್ಕೆ ಮಿಡತೆಗಳನ್ನು ಕಳುಹಿಸುವಾಗಲು ಹಾಗೂ ನನ್ನ ಪ್ರಜೆಯ ಮೇಲೆ ಘೋರವ್ಯಾಧಿಯನ್ನು ಬರಮಾಡುವಾಗಲು",
			"14": "<sup>14</sup> ನನ್ನವರೆಂದು ಹೆಸರುಗೊಂಡ ನನ್ನ ಪ್ರಜೆಗಳು ತಮ್ಮನ್ನೇ ತಗ್ಗಿಸಿಕೊಂಡು, ನನ್ನ‍ನ್ನು ಪ್ರಾರ್ಥಿಸಿ, ನನ್ನ ದರ್ಶನವನ್ನು ಬಯಸುವುದಾದರೆ ನಾನು ಪರಲೋಕದಿಂದ ಆಲಿಸಿ, ಅವರ ಪಾಪಗಳನ್ನು ಕ್ಷಮಿಸಿ, ಅವರ ನಾಡಿಗೆ ಆರೋಗ್ಯಭಾಗ್ಯವನ್ನು ದಯಪಾಲಿಸುವೆನು.",
			"15": "<sup>15</sup> ಇನ್ನು ಮುಂದೆ ಈ ಸ್ಥಳದಲ್ಲಿ ಪ್ರಾರ್ಥಿಸುವವರನ್ನು ಕಟಾಕ್ಷಿಸಿ ಅವರ ವಿಜ್ಞಾಪನೆಗೆ ಕಿವಿಗೊಡುವೆನು.",
			"16": "<sup>16</sup> ನನ್ನ ನಾಮ ಮಹಿಮೆ ಈ ಆಲಯದಲ್ಲಿ ಶಾಶ್ವತವಾಗಿರುವಂತೆ ಇದನ್ನು ನನಗಾಗಿ ಆರಿಸಿ ಪ್ರತಿಷ್ಠಿಸಿಕೊಂಡಿದ್ದೇನೆ. ನನ್ನ ದೃಷ್ಟಿಯೂ ಮನಸ್ಸೂ ಸದಾ ಅಲ್ಲಿ ಇರುವುವು.",
			"17": "<sup>17</sup> ನೀನು ನಿನ್ನ ತಂದೆಯಾದ ದಾವೀದನಂತೆ ನನ್ನ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ಕೈಗೊಂಡು ನನಗೆ ವಿಧೇಯನಾಗಿ ನಡೆದುಕೊಂಡರೆ",
			"18": "<sup>18</sup> ನಿನ್ನ ರಾಜ್ಯಸಿಂಹಾಸನವನ್ನು ಸ್ಥಿರಪಡಿಸುವೆನು; ‘ಇಸ್ರಯೇಲರಲ್ಲಿ ನಿನ್ನ ಸಂತಾನದವರು ತಪ್ಪದೆ ರಾಜ್ಯಾಧಿಕಾರ ನಡೆಸುವರು’ ಎಂಬುದಾಗಿ ನಿನ್ನ ತಂದೆ ದಾವೀದನಿಗೆ ಮಾಡಿದ ವಾಗ್ದಾನವನ್ನು ನೆರವೇರಿಸುವೆನು.",
			"19": "<sup>19</sup> ಆದರೆ ನೀವು ದ್ರೋಹಿಗಳಾಗಿ, ನನ್ನ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ಉಲ್ಲಂಘಿಸಿ, ಅನ್ಯದೇವತೆಗಳನ್ನು ಹಿಂಬಾಲಿಸಿ, ಅವುಗಳಿಗೆ ಕೈ ಮುಗಿಯುವುದಾದರೆ,",
			"20": "<sup>20</sup> ನಾನು ಇಸ್ರಯೇಲರಿಗೆ ಕೊಟ್ಟ ನನ್ನ ನಾಡಿನಿಂದ ನಿಮ್ಮನ್ನು ಕಿತ್ತುಹಾಕುವೆನು; ನನ್ನ ಹೆಸರಿಗೆ ಪ್ರತಿಷ್ಠಿಸಿಕೊಂಡ ಈ ಆಲಯವನ್ನು ನಿರಾಕರಿಸಿಬಿಡುವೆನು; ಎಲ್ಲ ಜನಾಂಗಗಳವರು ಪರಿಹಾಸ್ಯದಿಂದ ಲಾವಣಿ ಕಟ್ಟುವುದಕ್ಕೂ ನಿಂದಿಸುವುದಕ್ಕೂ ಇದು ಆಸ್ಪದವಾಗುವಂತೆ ಮಾಡುವೆನು.",
			"21": "<sup>21</sup> ಉನ್ನತವಾದ ಈ ಮಂದಿರದ ಮಾರ್ಗವಾಗಿ ಹೋಗುವವರು ಇದನ್ನು ನೋಡಿ ವಿಸ್ಮಿತರಾಗಿ, ‘ಸರ್ವೇಶ್ವರ ಈ ನಾಡನ್ನೂ ಈ ಆಲಯವನ್ನೂ ಹೀಗೇಕೆ ಮಾಡಿಬಿಟ್ಟರು?’ ಎಂದು ಕೇಳುವರು.",
			"22": "<sup>22</sup> ಆಗ ಅವರಿಗೆ, ‘ಈ ನಾಡಿನವರು ತಮ್ಮನ್ನು ಈಜಿಪ್ಟ್ ದೇಶದಿಂದ ಬರಮಾಡಿದ ತಮ್ಮ ಪೂರ್ವಜರ ದೇವರಾದ ಸರ್ವೇಶ್ವರನನ್ನು ಬಿಟ್ಟು, ಅನ್ಯದೇವತೆಗಳನ್ನು ಅವಲಂಭಿಸಿ, ಅವುಗಳಿಗೆ ಅಡ್ಡಬಿದ್ದು ಸೇವೆಸಲ್ಲಿಸಿದ್ದರಿಂದಲೇ ಅವರು ಈ ಎಲ್ಲಾ ಆಪತ್ತುಗಳನ್ನು ಅವರಿಗೆ ಬರಮಾಡಿ ಇದ್ದಾರೆ,’ ಎಂಬ ಉತ್ತರ ಸಿಕ್ಕುವುದು,” ಎಂದರು."
		},
		"8": {
			"1": "<sup>1</sup> ಸೊಲೊಮೋನನು ಇಪ್ಪತ್ತು ವರ್ಷಗಳಲ್ಲಿ ಸರ್ವೇಶ್ವರನ ಆಲಯವನ್ನೂ ತನ್ನ ಅರಮನೆಯನ್ನೂ ಕಟ್ಟಿಸಿ ಮುಗಿಸಿದನು.",
			"2": "<sup>2</sup> ಹೂರಾಮನಿಂದ ತನಗೆ ದೊರಕಿದ ಪಟ್ಟಣಗಳನ್ನು ಭದ್ರಪಡಿಸಿ ಅವುಗಳಲ್ಲಿ ಇಸ್ರಯೇಲರು ನೆಲಸುವಂತೆ ಮಾಡಿದನು.",
			"3": "<sup>3</sup> ಅನಂತರ ಅವನು ಚೋಬರಾಜ್ಯದ ಹಮಾತಿಗೆ ಹೋಗಿ ಅದನ್ನು ಸ್ವಾಧೀನ ಮಾಡಿಕೊಂಡನು.",
			"4": "<sup>4</sup> ಮರುಭೂಮಿಯಲ್ಲಿದ್ದ ತದ್ಮೋರ್ ಪಟ್ಟಣವನ್ನೂ ಹಮಾತಿನಲ್ಲಿ ತಾನು ಕಟ್ಟಿಸಿದ ಎಲ್ಲಾ ಉಗ್ರಾಣದ ಪಟ್ಟಣಗಳನ್ನೂ ಸುಭದ್ರಪಡಿಸಿದನು.",
			"5": "<sup>5</sup> ಮೇಲಿನ ಮತ್ತು ಕೆಳಗಿನ ಬೇತ್ಹೋರೋನ್ ಎಂಬ ಊರುಗಳಿಗೆ ಪೌಳಿಗೋಡೆ, ಕದ, ಅಗುಳಿ ಇವುಗಳನ್ನು ಏರ್ಪಡಿಸಿ ಕೋಟೆಗಳನ್ನಾಗಿ ಮಾರ್ಪಡಿಸಿದನು.",
			"6": "<sup>6</sup> ಬಾಲಾ ತೂರು, ತನ್ನ ಎಲ್ಲ ಉಗ್ರಾಣದ ಪಟ್ಟಣಗಳು, ಯುದ್ಧರಥಗಳನ್ನಿರಿಸುವ ಪಟ್ಟಣ, ರಾಹುತರ ಪಟ್ಟಣ ಇವುಗಳನ್ನೂ ಕಟ್ಟಿಸಿದನು. ಜೆರುಸಲೇಮಿನಲ್ಲೂ ಲೆಬನೋನಿನಲ್ಲೂ ತನ್ನ ರಾಜ್ಯದ ಎಲ್ಲಾ ಪ್ರಾಂತಗಳಲ್ಲೂ ತನಗೆ ಇಷ್ಟವಾದುವುಗಳನ್ನೆಲ್ಲಾ ಕಟ್ಟಿಸಿದನು.",
			"7": "<sup>7</sup> ಇಸ್ರಯೇಲರಿಂದ ಸಂಹೃತರಾಗದೆ ಕಾನಾನ್ ನಾಡಿನಲ್ಲಿ ಅಳಿದುಳಿದ ಹಿತ್ತಿಯ, ಅಮೋರಿಯ, ಪೆರಿಜೀಯ, ಹಿವ್ವಿಯ ಹಾಗೂ ಯೆಬೂಸಿಯ ಮೊದಲಾದ ಅನ್ಯಜನಾಂಗಗಳ ಸಂತಾನದವರೆಲ್ಲರನ್ನೂ ಬಿಟ್ಟಿಹಿಡಿದನು.",
			"8": "<sup>8</sup> ಅವರು ಇಂದಿನವರೆಗೂ ಬಿಟ್ಟೀಕೆಲಸದವರಾಗಿರುತ್ತಾರೆ.",
			"9": "<sup>9</sup> ಆದರೆ ಸೊಲೊಮೋನನು ತನ್ನ ಕೆಲಸಕ್ಕಾಗಿ ಇಸ್ರಯೇಲರನ್ನು ಬಿಟ್ಟಿಹಿಡಿಯಲಿಲ್ಲ. ಅವರನ್ನು ಸೈನಿಕರನ್ನಾಗಿ, ಅಧಿಪತಿಗಳನ್ನಾಗಿ, ಸರದಾರರನ್ನಾಗಿ ಹಾಗೂ ರಥಾಶ್ವಬಲಗಳ ನಾಯಕರನ್ನಾಗಿ ನೇಮಿಸಿಕೊಂಡನು.",
			"10": "<sup>10</sup> ಇಂಥ ಇನ್ನೂರೈವತ್ತು ಮಂದಿ ಮುಖ್ಯಾಧಿಪತಿಗಳು ಅರಸ ಸೊಲೊಮೋನನ ಆಳುಗಳ ಮೇಲೆ ಅಧಿಕಾರಿಗಳಾಗಿದ್ದರು.",
			"11": "<sup>11</sup> “ಯಾವ ಯಾವ ಸ್ಥಳಗಳಲ್ಲಿ ಸರ್ವೇಶ್ವರನ ಮಂಜೂಷವಿತ್ತೋ ಅವು ಪವಿತ್ರಸ್ಥಾನಗಳು; ಆದುದರಿಂದ ಇಸ್ರಯೇಲರ ಅರಸನಾಗಿದ್ದ ದಾವೀದನ ಮನೆ ಪವಿತ್ರವಾದುದು; ಅಲ್ಲಿ ಫರೋಹನ ಮಗಳಾದ ನನ್ನ ಹೆಂಡತಿ ವಾಸಿಸಬಾರದು,” ಎಂದುಕೊಂಡು ಸೊಲೊಮೋನನು ಆಕೆಯನ್ನು ದಾವೀದನಗರದಿಂದಾಚೆ ಆಕೆಗಾಗಿಯೇ ತಾನು ಕಟ್ಟಿಸಿದ್ದ ಮಂದಿರದಲ್ಲಿ ಇರಿಸಿದನು.",
			"12": "<sup>12</sup> ಆ ಕಾಲದಲ್ಲಿ ಸೊಲೊಮೋನನು ತಾನು ಸರ್ವೇಶ್ವರನಿಗಾಗಿ ಮಂಟಪದ ಮುಂದೆ ಕಟ್ಟಿಸಿದ ಸರ್ವೇಶ್ವರನ ಬಲಿಪೀಠದ ಮೇಲೆ,",
			"13": "<sup>13</sup> ಮೋಶೆಯ ಆಜ್ಞೆಯಂತೆ, ಸಬ್ಬತ್‍ದಿನ, ಅಮಾವಾಸ್ಯೆ, ಇವುಗಳಲ್ಲೂ ಹುಳಿರಹಿತ ರೊಟ್ಟಿಗಳ ಜಾತ್ರೆ, ಪಂಚಾಶತ್ತಮ ದಿನದ ಜಾತ್ರೆ, ಪರ್ಣಕುಟೀರಗಳ ಜಾತ್ರೆ ಎಂಬೀ ಮೂರು ವಾರ್ಷಿಕ ಜಾತ್ರೆಗಳಲ್ಲೂ ಆಯಾ ದಿವಸಗಳಿಗೆ ನೇಮಕವಾದ ದಹನಬಲಿಗಳನ್ನು ಸಮರ್ಪಿಸುತ್ತಿದ್ದನು.",
			"14": "<sup>14</sup> ಇದಲ್ಲದೆ, ತನ್ನ ತಂದೆ ದಾವೀದನ ವಿಧಿಗೆ ಅನುಸಾರವಾಗಿ ಯಾಜಕ ವರ್ಗಗಳವರನ್ನೂ ಲೇವಿಯರನ್ನೂ ಅವರವರ ಸೇವಾವೃತ್ತಿಗೆ ನೇಮಿಸಿದನು. ಲೇವಿಯರು ಆಯಾ ದಿನದ ನೇಮದ ಪ್ರಕಾರ ದೇವಭಜನೆಮಾಡಿ ಯಾಜಕರ ಕೈಕೆಳಗೆ ಸೇವೆಮಾಡಬೇಕಾಗಿತ್ತು. ದ್ವಾರಪಾಲಕರು ತಮ್ಮ ತಮ್ಮ ವರ್ಗಗಳ ಸರದಿಯ ಮೇಲೆ ಆಯಾ ಬಾಗಿಲುಗಳನ್ನು ಕಾಯಬೇಕಾಗಿತ್ತು. ಇದು ದೈವಪುರುಷ ದಾವೀದರಾಜನ ಅಪ್ಪಣೆ ಆಗಿತ್ತು.",
			"15": "<sup>15</sup> ಅವನು ಯಾಜಕರನ್ನೂ ಲೇವಿಯರನ್ನೂ ಅರಮನೆಯ ಭಂಡಾರಗಳನ್ನೂ ಕುರಿತು ಕೊಟ್ಟ ಅಪ್ಪಣೆಯನ್ನು ಅವರು ಕಿಂಚಿತ್ತಾದರೂ ಮೀರಲಿಲ್ಲ.",
			"16": "<sup>16</sup> ಈ ಪ್ರಕಾರ ಅಸ್ತಿವಾರ ಮೊದಲುಗೊಂಡು ಸಮಾಪ್ತಿಯವರೆಗೂ ಸೊಲೊಮೋನನ ಎಲ್ಲಾ ಕೆಲಸವೂ ಮುಗಿದು ಸರ್ವೇಶ್ವರನ ಆಲಯ ಸಿದ್ಧವಾಯಿತು.",
			"17": "<sup>17</sup> ಆಮೇಲೆ, ಸೊಲೊಮೋನನು ಎದೋಮ್ ದೇಶದ ಸಮುದ್ರತೀರದಲ್ಲಿರುವ ಎಚ್ಯೋನ್ಗೆಬೆರ್ ಹಾಗೂ ಏಲೋತ್ ಎಂಬ ಊರುಗಳಿಗೆ ಹೋದನು.",
			"18": "<sup>18</sup> ಹೂರಾಮನು ಅವನಿಗೆ ತನ್ನ ಆಳುಗಳ ಮುಖಾಂತರ ಹಡಗುಗಳನ್ನು ಕಳುಹಿಸಿದ್ದಲ್ಲದೆ, ಸಮುದ್ರ ಪ್ರಯಾಣದಲ್ಲಿ ನಿಪುಣರಾದ ನಾವಿಕರನ್ನೂ ಕಳುಹಿಸಿದನು. ಅವರು ಸೊಲೊಮೋನನ ಸೇವಕರೊಡನೆ ಓಫೀರಿಗೆ ಪ್ರಯಾಣಮಾಡಿ ಅಲ್ಲಿಂದ ಅರಸ ಸೊಲೊಮೋನನಿಗೆ 15,000 ಕಿಲೋಗ್ರಾಂಗಿಂತ ಹೆಚ್ಚು ಬಂಗಾರವನ್ನೂ ತಂದುಕೊಟ್ಟರು."
		},
		"9": {
			"1": "<sup>1</sup> ಶೆಬಾದ ರಾಣಿ ಸೊಲೊಮೋನನ ಕೀರ್ತಿಯನ್ನು ಕೇಳಿ, ಅವನನ್ನು ಒಗಟುಗಳಿಂದ ಪರೀಕ್ಷಿಸುವುದಕ್ಕೆ ಜೆರುಸಲೇಮಿಗೆ ಬಂದಳು. ಆಕೆ ಸುಗಂಧದ್ರವ್ಯ, ಅಪರಿಮಿತವಾದ ಬಂಗಾರ, ರತ್ನ ಇವುಗಳನ್ನು ಒಂಟೆಗಳ ಮೇಲೆ ಹೇರಿಸಿಕೊಂಡು ಮಹಾಪರಿವಾರದೊಡನೆ ಸೊಲೊಮೋನನ ಬಳಿಗೆ ಬಂದಳು. ತನ್ನ ಮನಸ್ಸಿನಲ್ಲಿ ಗೊತ್ತುಮಾಡಿಕೊಂಡಿದ್ದ ಎಲ್ಲಾ ವಿಷಯಗಳನ್ನು ಕುರಿತು ಅವನೊಡನೆ ಸಂಭಾಷಿಸಿದಳು.",
			"2": "<sup>2</sup> ಸೊಲೊಮೋನನು ಆಕೆಯ ಪ್ರಶ್ನೆಗಳಿಗೆಲ್ಲಾ ಉತ್ತರಕೊಟ್ಟನು. ಅವುಗಳಲ್ಲಿ ಬಗೆಹರಿಸಲು ಅವನಿಗೆ ಅಸಾಧ್ಯವಾದದ್ದು ಒಂದೂ ಇರಲಿಲ್ಲ; ಎಲ್ಲವುಗಳಿಗೂ ಉತ್ತರಕೊಟ್ಟನು.",
			"3": "<sup>3</sup> ಆ ಶೆಬಾದ ರಾಣಿ ಸೊಲೊಮೋನನ ಜ್ಞಾನ, ಅವನು ಕಟ್ಟಿಸಿದ ಅರಮನೆ,",
			"4": "<sup>4</sup> ಅವನ ಭೋಜನಪೀಠದ ಆಹಾರ, ಆ ಪೀಠದ ಸುತ್ತಲಿದ್ದ ಉದ್ಯೋಗಸ್ಥರ ಆಸನಗಳು, ಅವನ ಪರಿಚಾರಕರ ಸೇವಾಕ್ರಮ ಮತ್ತು ಅವರ ಉಡುಪುಗಳು, ಅವನ ಪಾನಸೇವಕರು, ಅವರ ಉಡುಪುಗಳು ಇವುಗಳನ್ನೆಲ್ಲಾ ನೋಡಿದಳು. ಅಂತೆಯೇ ಅವನು ಸರ್ವೇಶ್ವರನ ಆಲಯದಲ್ಲಿ ಸಮರ್ಪಿಸುತ್ತಿದ್ದ ದಹನ ಬಲಿಗಳನ್ನೂ ಗಮನಿಸಿದಳು.",
			"5": "<sup>5</sup> ಇವುಗಳಿಂದ ಆಶ್ಚರ್ಯಚಕಿತಳಾಗಿ, ಅವನಿಗೆ, “ನಾನು ನನ್ನ ದೇಶದಲ್ಲಿ ನಿಮ್ಮ ಜ್ಞಾನವನ್ನೂ ಸಾಧನೆಗಳನ್ನೂ ಕುರಿತು ಕೇಳಿದ್ದು ಸತ್ಯವಾಗಿದೆ.",
			"6": "<sup>6</sup> ನಾನಾಗಿ ಇಲ್ಲಿಗೆ ಬಂದು ಕಣ್ಣಾರೆ ನೋಡುವುದಕ್ಕಿಂತ ಮೊದಲು ಜನರು ಹೇಳಿದ ಸುದ್ದಿಯನ್ನು ನಂಬಲಿಲ್ಲ; ಈಗ ನೋಡಿದರೆ, ನಿಮ್ಮ ಜ್ಞಾನಾತಿಶಯವು ನಾನು ಕೇಳಿದ್ದಕ್ಕಿಂತ ಹೆಚ್ಚಾಗಿದೆ; ಜನರು ನನಗೆ ಇದರಲ್ಲಿ ಅರ್ಧವನ್ನಾದರೂ ಹೇಳಲಿಲ್ಲ.",
			"7": "<sup>7</sup> ನಿಮ್ಮ ಪ್ರಜೆಗಳು ಹಾಗೂ ಸದಾ ನಿಮ್ಮ ಸನ್ನಿಧಿಯಲ್ಲಿ ಇದ್ದುಕೊಂಡು ನಿಮ್ಮ ಜ್ಞಾನವಾಕ್ಯಗಳನ್ನು ಕೇಳುವ ಸೇವಕರು ಧನ್ಯರು!",
			"8": "<sup>8</sup> ನಿಮ್ಮನ್ನು ಮೆಚ್ಚಿ, ತಮ್ಮ ಸಮ್ಮುಖದಲ್ಲಿ ಅರಸನಾಗಿಸಲು, ತಮ್ಮ ಸಿಂಹಾಸನದ ಮೇಲೆ ನಿಮ್ಮನ್ನು ಕುಳ್ಳಿರಿಸಿದ ಆ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ಸ್ತೋತ್ರವಾಗಲಿ! ನಿಮ್ಮ ದೇವರು ಇಸ್ರಯೇಲರನ್ನು ಪ್ರೀತಿಸಿ ಅವರನ್ನು ನಿರಂತರವಾಗಿ ಸ್ಥಿರಗೊಳಿಸಬೇಕೆಂದಿದ್ದಾರೆ. ಆದ್ದರಿಂದಲೇ ಈ ಜನರ ನೀತಿನ್ಯಾಯಗಳನ್ನು ಸ್ಥಾಪಿಸುವುದಕ್ಕಾಗಿ ನಿಮ್ಮನ್ನೇ ಅರಸನನ್ನಾಗಿ ನೇಮಿಸಿದ್ದಾರೆ,” ಎಂದು ಹೇಳಿದಳು.",
			"9": "<sup>9</sup> ಆಕೆ ಅರಸನಿಗೆ 4000 ಕಿಲೋಗ್ರಾಂಗೂ ಹೆಚ್ಚು ಬಂಗಾರವನ್ನೂ ಅಪರಿಮಿತ ಸುಗಂಧದ್ರವ್ಯವನ್ನೂ ರತ್ನಗಳನ್ನೂ ಕೊಟ್ಟಳು. ಶೆಬಾದ ರಾಣಿ ಅರಸ ಸೊಲೊಮೋನನಿಗೆ ಕೊಟ್ಟ ಸುಗಂಧದ್ರವ್ಯಕ್ಕೆ ಸರಿಸಮಾನವಾದದ್ದು ಯಾವುದೂ ಇರಲಿಲ್ಲ.",
			"10": "<sup>10</sup> ಹೂರಾಮನ ಮತ್ತು ಸೊಲೊಮೋನನ ಆಳುಗಳು ಓಫೀರ್‍ದೇಶದ ಬಂಗಾರವನ್ನಲ್ಲದೆ ಸುಗಂಧದ ಮರವನ್ನೂ ರತ್ನಗಳನ್ನೂ ತಂದರು.",
			"11": "<sup>11</sup> ಅರಸನು ಸುಗಂಧದ ಮರದಿಂದ ದೇವಾಲಯ ಹಾಗೂ ಅರಮನೆಗಳಿಗೆ ಸೋಪಾನಗಳನ್ನೂ ಗಾಯಕರಿಗಾಗಿ ಕಿನ್ನರಿಗಳನ್ನೂ ಸ್ವರಮಂಡಲಗಳನ್ನೂ ಮಾಡಿಸಿದನು. ಇಂಥ ಸುಗಂಧದ ಮರವನ್ನು ಹಿಂದೆಂದೂ ಜುದೇಯ ನಾಡಿನಲ್ಲಿ ಕಂಡಿರಲಿಲ್ಲ.",
			"12": "<sup>12</sup> ಅರಸ ಸೊಲೊಮೋನನು ಶೆಬಾದ ರಾಣಿಯಿಂದ ತನಗೆ ದೊರಕಿದ ಬಹುಮಾನಕ್ಕೆ ಪ್ರತಿಯಾಗಿ ಆಕೆಗೆ ತಾನು ಕೊಟ್ಟ ವಸ್ತುಗಳನ್ನಲ್ಲದೆ ಆಕೆ ಕೇಳಿದವುಗಳನ್ನೆಲ್ಲಾ ಕೊಟ್ಟುಬಿಟ್ಟನು. ಅನಂತರ ಆಕೆ ತನ್ನ ಪರಿವಾರದವರೊಡನೆ ಸ್ವದೇಶಕ್ಕೆ ಹೊರಟುಹೋದಳು.",
			"13": "<sup>13</sup> ಸೊಲೊಮೋನನಿಗೆ ದೇಶಾಂತರ ವ್ಯಾಪಾರಿಗಳು, ವರ್ತಕರು, ಅರೇಬಿಯ ಅರಸರು ಹಾಗೂ ದೇಶಾಧಿಪತಿಗಳು ತರುತ್ತಿದ್ದ ಬೆಳ್ಳಿಬಂಗಾರವಲ್ಲದೆ,",
			"14": "<sup>14</sup> ಪ್ರತಿವರ್ಷ ಸುಮಾರು 23,000 ಕಿಲೋಗ್ರಾಂ ಬಂಗಾರ ದೊರಕುತ್ತಿತ್ತು.",
			"15": "<sup>15</sup> ಅರಸ ಸೊಲೊಮೋನನು ಬಂಗಾರದ ತಗಡಿನಿಂದ ಇನ್ನೂರು ಗುರಾಣಿಗಳನ್ನು ಮಾಡಿಸಿದನು; ಪ್ರತಿಯೊಂದು ಗುರಾಣಿಗೆ ಸುಮಾರು ಏಳು ಕಿಲೋಗ್ರಾಂ ಬಂಗಾರ ಹಿಡಿಯಿತು.",
			"16": "<sup>16</sup> ಇದಲ್ಲದೆ ಅವನು ಬಂಗಾರದ ತಗಡಿನಿಂದ ಮುನ್ನೂರು ಖೇಡ್ಯಗಳನ್ನು ಮಾಡಿಸಿದನು; ಪ್ರತಿಯೊಂದು ಖೇಡ್ಯಕ್ಕೆ ಎರಡು ಕಿಲೋಗ್ರಾಂ ಬಂಗಾರ ಹಿಡಿಯಿತು. ಅವನು ಇವುಗಳನ್ನು ‘ಲೆಬನೋನಿನ ತೋಪು’ ಎನಿಸಿಕೊಳ್ಳುವ ಮಂದಿರದಲ್ಲಿ ಇರಿಸಿದನು.",
			"17": "<sup>17</sup> ಇದಲ್ಲದೆ ಅರಸನು ದೊಡ್ಡದಾದ ಒಂದು ದಂತ ಸಿಂಹಾಸನವನ್ನು ಮಾಡಿಸಿ, ಅದನ್ನು ಚೊಕ್ಕ ಬಂಗಾರದ ತಗಡಿನಿಂದ ಹೊದಿಸಿದನು.",
			"18": "<sup>18</sup> ಅದಕ್ಕೆ ಹೊಂದಿಕೆಯಾಗಿ ಆರು ಮೆಟ್ಟಿಲುಗಳೂ ಬಂಗಾರದ ಪಾದಪೀಠವೂ ಇದ್ದವು.",
			"19": "<sup>19</sup> ಆರು ಮೆಟ್ಟಿಲುಗಳ ಎರಡು ಕಡೆಗಳಲ್ಲೂ ಒಟ್ಟು ಹನ್ನೆರಡು ಸಿಂಹಗಳು ನಿಂತಿದ್ದವು. ಇಂಥ ಸಿಂಹಾಸನವು ಬೇರೆ ಯಾವ ರಾಜ್ಯದಲ್ಲಿಯೂ ಇರಲಿಲ್ಲ.",
			"20": "<sup>20</sup> ಅರಸ ಸೊಲೊಮೋನನ ಎಲ್ಲಾ ಪಾನಪಾತ್ರೆಗಳು ಬಂಗಾರದವುಗಳು. ‘ಲೆಬನೋನಿನ ತೋಪು’ ಎನಿಸಿಕೊಳ್ಳುವ ಮಂದಿರದ ಎಲ್ಲಾ ಸಾಮಗ್ರಿಗಳು ಚೊಕ್ಕ ಬಂಗಾರದವುಗಳು. ಸೊಲೊಮೋನನ ಕಾಲದಲ್ಲಿ ಬೆಳ್ಳಿಗೆ ಬೆಲೆಯೇ ಇರಲಿಲ್ಲ.",
			"21": "<sup>21</sup> ಅರಸನ ಹಡಗುಗಳು ಹೂರಾಮನ ನಾವಿಕರ ಜೊತೆಯಲ್ಲಿ ತಾರ್ಷೀಷಿಗೆ ಹೋಗಿ ಮೂರು ವರ್ಷಕ್ಕೊಮ್ಮೆ ಬಂಗಾರ, ಬೆಳ್ಳಿ, ದಂತ, ಕಪಿ, ನವಿಲು ಇವುಗಳನ್ನು ತರುತ್ತಿದ್ದವು.",
			"22": "<sup>22</sup> ಈ ಪ್ರಕಾರ ಅರಸ ಸೊಲೊಮೋನನು ಐಶ್ವರ್ಯದಲ್ಲೂ ಜ್ಞಾನದಲ್ಲೂ ಭೂಲೋಕದ ಎಲ್ಲಾ ಅರಸರಿಗಿಂತ ಮಿಗಿಲಾಗಿದ್ದನು.",
			"23": "<sup>23</sup> ದೇವರು ಅವನಿಗೆ ಅನುಗ್ರಹಿಸಿದ ಜ್ಞಾನವಾಕ್ಯಗಳನ್ನು ಕೇಳುವುದಕ್ಕಾಗಿ ಭೂರಾಜರೆಲ್ಲರು ಅವನ ದರ್ಶನಕ್ಕೆ ಬರುತ್ತಿದ್ದರು.",
			"24": "<sup>24</sup> ಅವರೆಲ್ಲರು ಅವನಿಗೆ ವರ್ಷ ವರ್ಷವೂ ಬೆಳ್ಳಿ ಬಂಗಾರದ ಸಾಮಾನು, ಉಡುಪು, ಯುದ್ಧಾಯುಧ, ಸುಗಂಧದ್ರವ್ಯ, ಕುದುರೆ, ಹೇಸರಗತ್ತೆ ಇವುಗಳನ್ನು ಕಾಣಿಕೆಯಾಗಿ ಕೊಡುತ್ತಿದ್ದರು.",
			"25": "<sup>25</sup> ಸೊಲೊಮೋನನ ಅಶ್ವಸಹಿತವಾದ ರಥಗಳು ನಾಲ್ಕು ಸಾವಿರ ಹಾಗೂ ರಾಹುತರು ಹನ್ನೆರಡು ಸಾವಿರ. ಇವುಗಳನ್ನು ಜೆರುಸಲೇಮಿನಲ್ಲಿ ತನ್ನ ಬಳಿಯಲ್ಲೂ ರಥಗಳಿಗಾಗಿ ನೇಮಿಸಿದ್ದ ಪಟ್ಟಣಗಳಲ್ಲೂ ಇರಿಸಿದ್ದನು.",
			"26": "<sup>26</sup> ಅವನು ಯೂಫ್ರೆಟಿಸ್ ನದಿ ಮೊದಲುಗೊಂಡು ಫಿಲಿಷ್ಟಿಯರ ಮತ್ತು ಈಜಿಪ್ಟರ ದೇಶಗಳವರೆಗೂ ಇರುವ ಎಲ್ಲ ರಾಜರನ್ನು ಆಳುತ್ತಿದ್ದನು.",
			"27": "<sup>27</sup> ಅರಸನ ಕಾಲದಲ್ಲಿ ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಬೆಳ್ಳಿ, ಕಲ್ಲಿನಂತೆಯೂ ದೇವದಾರು ಮರಗಳು, ಇಳಕಲಿನ ಪ್ರದೇಶದಲ್ಲಿ ಬೆಳೆಯುವ ಅತ್ತಿಮರಗಳಂತೆಯೂ ವಿಫುಲವಾಗಿ ಇದ್ದವು.",
			"28": "<sup>28</sup> ಸೊಲೊಮೋನನಿಗಾಗಿ ಈಜಿಪ್ಟ್ ಮುಂತಾದ ದೇಶಗಳಿಂದ ಕುದುರೆಗಳನ್ನು ತರುತ್ತಿದ್ದರು.",
			"29": "<sup>29</sup> ಸೊಲೊಮೋನನ ಉಳಿದ ಪೂರ್ವೋತ್ತರ ಚರಿತ್ರೆಯನ್ನು, ಪ್ರವಾದಿ ನಾತಾನನ ಚರಿತ್ರೆ ಶೀಲೋವಿನವನಾದ ಅಹೀಯನ ಪ್ರವಾದನೆ ಹಾಗೂ ದಿವ್ಯದರ್ಶಿಯಾದ ಇದ್ದೋವನು ನೆಬಾಟನ ಮಗ ಯಾರೊಬ್ಬಾಮನನ್ನು ಕುರಿತು ನುಡಿದ ದರ್ಶನೋಕ್ತಿ ಎಂಬ ಗ್ರಂಥಗಳಲ್ಲಿ ಬರೆದಿಡಲಾಗಿ ಇದೆ.",
			"30": "<sup>30</sup> ಸೊಲೊಮೋನನು ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಇದ್ದುಕೊಂಡು ಎಲ್ಲ ಇಸ್ರಯೇಲರನ್ನು ನಾಲ್ವತ್ತು ವರ್ಷ ಆಳಿದನಂತರ ನಿಧನನಾಗಿ ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಿದನು.",
			"31": "<sup>31</sup> ಅವನ ಶವವನ್ನು ಅವನ ತಂದೆ ದಾವೀದನ ಪಟ್ಟಣದಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗ ರೆಹಬ್ಬಾಮನು ಅರಸನಾದನು."
		},
		"10": {
			"1": "<sup>1</sup> ರೆಹಬ್ಬಾಮನು ಶೆಕೆಮಿಗೆ ಹೋದನು. ಅಲ್ಲಿ ಇಸ್ರಯೇಲರೆಲ್ಲರು ಅವನನ್ನು ಅರಸನನ್ನಾಗಿ ಮಾಡುವುದಕ್ಕೆ ಕೂಡಿಬಂದಿದ್ದರು.",
			"2": "<sup>2</sup> ಇತ್ತ ಅರಸ ಸೊಲೊಮೋನನಿಗೆ ತಪ್ಪಿಸಿಕೊಂಡು ಈಜಿಪ್ಟಿಗೆ ಓಡಿಹೋಗಿದ್ದ ನೆಬಾಟನ ಮಗ ಯಾರೊಬ್ಬಾಮನು ನಡೆದ ಸಂಗತಿಗಳನ್ನು ಕೇಳಿದೊಡನೆ ಈಜಿಪ್ಟಿನಿಂದ ಹಿಂದಿರುಗಿ ಬಂದಿದ್ದನು.",
			"3": "<sup>3</sup> ಇಸ್ರಯೇಲರು ಅವನನ್ನೂ ತಮ್ಮ ಬಳಿಗೆ ಕರೆಸಿಕೊಂಡರು. ಅವನು ಎಲ್ಲ ಇಸ್ರಯೇಲರೊಡನೆ ರೆಹಬ್ಬಾಮನ ಬಳಿಗೆ ಹೋಗಿ,",
			"4": "<sup>4</sup> “ನಿಮ್ಮ ತಂದೆ ನಮ್ಮ ಮೇಲೆ ಭಾರವಾದ ನೊಗವನ್ನು ಹೇರಿದ್ದರು; ಅವರು ನೇಮಿಸಿದ್ದ ಬಿಟ್ಟೀ ಕೆಲಸವನ್ನು ನೀನು ಕಡಿಮೆಮಾಡಿ, ನಮ್ಮ ಮೇಲಿರುವ ಈ ಭಾರವಾದ ನೊಗವನ್ನು ಹಗುರಮಾಡುವುದಾದರೆ ನಾವು ನಿಮಗೆ ಸೇವೆಮಾಡುತ್ತೇವೆ,” ಎಂದನು. ಅವನು,",
			"5": "<sup>5</sup> “ನೀವು ಮೂರು ದಿನಗಳಾದ ನಂತರ ಮತ್ತೆ ಬನ್ನಿ,” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು; ಜನರು ಹೊರಟುಹೋದರು.",
			"6": "<sup>6</sup> ಆಗ ಅರಸ ರೆಹಬ್ಬಾಮನು ತನ್ನ ತಂದೆ ಸೊಲೊಮೋನನ ಕಾಲದಲ್ಲಿ ಮಂತ್ರಿಗಳಾಗಿದ್ದ ಹಿರಿಯರನ್ನು ಕೂಡಿಸಿ, ತಾನು ಜನರಿಗೆ ಕೊಡಬೇಕಾದ ಉತ್ತರದ ಬಗ್ಗೆ ಅವರ ಆಲೋಚನೆಯನ್ನು ಕೇಳಿದನು.",
			"7": "<sup>7</sup> ಅವರು, “ನೀವು ಈ ಜನರಿಗೆ ದಯೆತೋರಿಸಿ, ಅವರನ್ನು ಮೆಚ್ಚಿಸುವ ಒಳ್ಳೆಯ ಉತ್ತರಕೊಟ್ಟರೆ ಅವರು ಯಾವಾಗಲೂ ನಿಮಗೆ ಅಧೀನರಾಗಿರುವರು,” ಎಂದರು.",
			"8": "<sup>8</sup> ಆದರೆ ಅವನು ಆ ಹಿರಿಯರ ಆಲೋಚನೆಯನ್ನು ನಿರಾಕರಿಸಿದನು. ತನ್ನ ಸಂಗಡವೇ ಬೆಳೆದು ತನಗೆ ಮಂತ್ರಿಗಳಾದ ಯೌವನಸ್ಥರನ್ನು ಕರೆಸಿದನು.",
			"9": "<sup>9</sup> “ನನ್ನ ತಂದೆ ಹೇರಿದ ನೊಗವನ್ನು ಹಗುರಮಾಡಬೇಕೆಂದು ಬೇಡಿಕೊಳ್ಳುವ ಈ ಜನರಿಗೆ ಯಾವ ಉತ್ತರವನ್ನು ಕೊಡಬೇಕು? ಈ ವಿಷಯದಲ್ಲಿ ನಿಮ್ಮ ಆಲೋಚನೆ ಏನು?” ಎಂದು ಕೇಳಿದನು.",
			"10": "<sup>10</sup> ಅವರು ಅವನಿಗೆ, “ನಿಮ್ಮ ತಂದೆ ನಮ್ಮ ಮೇಲೆ ಹೇರಿರುವ ಭಾರವಾದ ನೊಗವನ್ನು ಹಗುರಮಾಡು ಎಂಬುದಾಗಿ ನಿಮ್ಮನ್ನು ಬೇಡಿಕೊಂಡ ಜನರಿಗೆ ನೀವು, ‘ನನ್ನ ತಂದೆಯ ಸೊಂಟಕ್ಕಿಂತ ನನ್ನ ಕಿರಿಬೆರಳು ದಪ್ಪ;",
			"11": "<sup>11</sup> ನನ್ನ ತಂದೆ ನಿಮ್ಮ ಮೇಲೆ ಭಾರವಾದ ನೊಗವನ್ನು ಹೇರಿರುವುದು ನಿಜ; ಆದರೆ ನಾನು ಅದಕ್ಕೆ ಇನ್ನೂ ಹೆಚ್ಚಿನ ಭಾರವನ್ನು ಕೂಡಿಸುತ್ತೇನೆ; ಅವರು ನಿಮ್ಮನ್ನು ಬಾರುಕೋಲುಗಳಿಂದ ಹೊಡೆದದ್ದು ನಿಜ; ನಾನಾದರೋ ನಿಮ್ಮನ್ನು ಮುಳ್ಳು ಕೊರಡೆಗಳಿಂದ ದಂಡಿಸುವೆನು’ ಎಂಬುದಾಗಿ ಹೇಳಬೇಕು\", ಎಂದು ಉತ್ತರ ಕೊಟ್ಟರು.",
			"12": "<sup>12</sup> ಯಾರೊಬ್ಬಾಮನೂ ಜನರೆಲ್ಲರೂ ಅರಸ ರೆಹಬ್ಬಾಮನ ಅಪ್ಪಣೆಯಂತೆ ಮೂರನೆಯ ದಿನ ಪುನಃ ಅವನ ಬಳಿಗೆ ಬಂದರು.",
			"13": "<sup>13</sup> ಅರಸನು ಅವರಿಗೆ ಕಠಿಣವಾದ ಉತ್ತರವನ್ನು ಕೊಟ್ಟನು; ಅವನು ಹಿರಿಯರ ಆಲೋಚನೆಯನ್ನು ನಿರಾಕರಿಸಿದನು.",
			"14": "<sup>14</sup> ಯೌವನಸ್ಥರ ಆಲೋಚನೆಗನುಸಾರವಾಗಿ ಜನರಿಗೆ, “ನನ್ನ ತಂದೆ ನಿಮ್ಮ ಮೇಲೆ ಭಾರವಾದ ನೊಗವನ್ನು ಹೇರಿರುವುದು ನಿಜ; ಆದರೆ ನಾನು ಅದಕ್ಕೆ ಇನ್ನೂ ಹೆಚ್ಚಿನ ಭಾರವನ್ನು ಕೂಡಿಸುವೆನು. ಅವರು ನಿಮ್ಮನ್ನು ಬಾರುಕೋಲುಗಳಿಂದ ಹೊಡೆದದ್ದು ನಿಜ, ನಾನಾದರೋ ನಿಮ್ಮನ್ನು ಮುಳ್ಳು ಕೊರಡೆಗಳಿಂದ ದಂಡಿಸುವೆನು,” ಎಂದು ನುಡಿದನು.",
			"15": "<sup>15</sup> ಅರಸನು ಜನರ ಮಾತನ್ನು ಕೇಳದೆ ಹೋದದ್ದು ದೈವಯೋಗದಿಂದಲೇ. ಈ ಪ್ರಕಾರ ಸರ್ವೇಶ್ವರ ಶಿಲೋವಿನವನಾದ ಅಹೀಯನ ಮುಖಾಂತರ ನೆಬಾಟನ ಮಗ ಯಾರೊಬ್ಬಾಮನಿಗೆ ಹೇಳಿಸಿದ ಮಾತು ನೆರವೇರಿತು.",
			"16": "<sup>16</sup> ತಮ್ಮ ಮಾತನ್ನು ಅರಸನು ಲಕ್ಷಿಸಲಿಲ್ಲ ಎಂದು ತಿಳಿದು, ಇಸ್ರಯೇಲರೆಲ್ಲರು ಅವನಿಗೆ, “ದಾವೀದನಿಗೂ ನಮಗೂ ಇನ್ನು ಮೇಲೆ ಏನೂ ಸಂಬಂಧವಿಲ್ಲ, ಜೆಸ್ಸೆಯ ಮಗನಿಗೂ ನಮಗೂ ಯಾವ ಬಾಧ್ಯತೆಯೂ ಇಲ್ಲ. ಇಸ್ರಯೇಲರೇ, ನಿಮ್ಮ ನಿಮ್ಮ ನಿವಾಸಗಳಿಗೆ ತೆರಳಿರಿ; ದಾವೀದನ ಕಡೆಯವರೇ, ನಿಮ್ಮ ಕುಲವನ್ನು ನೀವೇ ನೋಡಿಕೊಳ್ಳಿ,” ಎಂದು ಪ್ರತಿಭಟಿಸಿ ತಮ್ಮ ತಮ್ಮ ನಿವಾಸಗಳಿಗೆ ಹೊರಟುಹೋದರು.",
			"17": "<sup>17</sup> ಜುದೇಯ ಪ್ರಾಂತ್ಯದಲ್ಲಿದ್ದ ಇಸ್ರಯೇಲರಾದರೋ ಅರಸ ರೆಹಬ್ಬಾಮನ ಅಧೀನದಲ್ಲೆ ಉಳಿದರು.",
			"18": "<sup>18</sup> ರೆಹಬ್ಬಾಮನು ಬಿಟ್ಟೀಕೆಲಸದವರ ಮೇಲ್ವಿಚಾರಕರಾದ ಹದೋರಾಮನನ್ನು ಇಸ್ರಯೇಲರ ಬಳಿಗೆ ಕಳುಹಿಸಿದಾಗ, ಅವರು ಇವನನ್ನು ಕಲ್ಲೆಸೆದು ಕೊಂದರು. ಆಗ ಅರಸ ರೆಹಬ್ಬಾಮನು ಬೇಗನೆ ರಥವನ್ನೇರಿ ಜೆರುಸಲೇಮಿಗೆ ಓಡಿಹೋದನು.",
			"19": "<sup>19</sup> ಇಸ್ರಯೇಲರು ಅಂದಿನಿಂದ ಇಂದಿನವರೆಗೂ ದಾವೀದನ ಕುಟುಂಬದವರೊಡನೆ ಸಮಾಧಾನವಾಗಲೇ ಇಲ್ಲ."
		},
		"11": {
			"1": "<sup>1</sup> ರೆಹಬ್ಬಾಮನು ಜೆರುಸಲೇಮನ್ನು ಮುಟ್ಟಿದ ನಂತರ ಇಸ್ರಯೇಲರಿಗೆ ವಿರುದ್ಧ ಯುದ್ಧಮಾಡುವುದಕ್ಕೂ ಅವರ ರಾಜ್ಯವನ್ನು ಮರಳಿ ವಶಪಡಿಸಿಕೊಳ್ಳುವುದಕ್ಕೂ ಯೆಹೂದ ಬೆನ್ಯಾಮೀನ್ ಕುಲಗಳಿಂದ ಲಕ್ಷದ ಎಂಬತ್ತು ಸಾವಿರ ಮಂದಿ ಶ್ರೇಷ್ಟ ಸೈನಿಕರನ್ನು ಕೂಡಿಸಿದನು.",
			"2": "<sup>2</sup> ಆಗ ದೈವಪುರುಷನಾದ ಶೆಮಾಯನಿಗೆ ಸರ್ವೇಶ್ವರನ ಈ ವಾಣಿ ಕೇಳಿಸಿತು:",
			"3": "<sup>3</sup> “ನೀನು ಹೋಗಿ ಸೊಲೊಮೋನನ ಮಗನೂ ಜುದೇಯದ ಅರಸನೂ ಆದ ರೆಹಬ್ಬಾಮನಿಗೂ ಯೆಹೂದ ಬೆನ್ಯಾಮೀನ್ ಕುಲಗಳ ಇಸ್ರಯೇಲರೆಲ್ಲರಿಗೂ,",
			"4": "<sup>4</sup> ‘ಸರ್ವೇಶ್ವರ ಇಂತೆನ್ನುತ್ತಾರೆ: ನೀವು ನಿಮ್ಮ ಸಹೋದರರೊಡನೆ ಯುದ್ಧಮಾಡುವುದಕ್ಕೆ ಹೋಗಬಾರದು. ಎಲ್ಲರು ಹಿಂದಿರುಗಿಹೋಗಿರಿ; ಈ ವಿಭಜನಾಕಾರ್ಯ ಸರ್ವೇಶ್ವರನಿಂದಾಗಿದೆ’ ಎಂದು ಹೇಳಬೇಕು.” ಅಂತೆಯೇ ಆ ಜನರು ಸರ್ವೇಶ್ವರನ ಮಾತನ್ನು ಕೇಳಿ ಯಾರೊಬ್ಬಾಮನಿಗೆ ವಿರೋಧವಾಗಿ ಹೊರಡದೆ ಹಿಂದಿರುಗಿ ಹೋದರು.",
			"5": "<sup>5</sup> ರೆಹಬ್ಬಾಮನು ಜೆರುಸಲೇಮಿನಲ್ಲಿ ವಾಸವಾಗಿದ್ದು ಜುದೇಯದ ಈ ಕೆಳಕಂಡ ಪಟ್ಟಣಗಳನ್ನು ಭದ್ರಪಡಿಸಿದನು:",
			"6": "<sup>6</sup> ಬೆತ್ಲೆಹೇಮ್, ಏತಾಮ್, ತೆಕೋವ,",
			"7": "<sup>7</sup> ಬೇತ್ಚೂರ್, ಸೋಕೋ, ಅದುಲ್ಲಾಮ್, ಗತ್‍ಊರು,",
			"9": "<sup>9</sup> ಮಾರೇಷ, ಜೀಫ್, ಅದೋರೈಮ್, ಲಾಕೀಷ್,",
			"10": "<sup>10</sup> ಅಜೇಕ, ಚೊರ್ಗ, ಅಯ್ಯಾಲೋನ್ ಹಾಗೂ ಹೆಬ್ರೋನ್. ಕೋಟೆಕೊತ್ತಲಗಳಿಂದ ಕೂಡಿದ್ದ ಈ ಪಟ್ಟಣಗಳು ಯೆಹೂದ-ಬೆನ್ಯಾಮೀನ್ ಕುಲ ಪ್ರಾಂತ್ಯಗಳಾಗಿದ್ದವು.",
			"11": "<sup>11</sup> ಅವನುಈ ಕೋಟೆಗಳನ್ನು ಬಲಪಡಿಸಿ, ನಾಯಕರ ವಶಕ್ಕೆ ಕೊಟ್ಟು, ಪ್ರತಿ ಒಂದರಲ್ಲಿ ಆಹಾರಪದಾರ್ಥ, ಎಣ್ಣೆ, ದ್ರಾಕ್ಷಾರಸ ಇವುಗಳನ್ನೂ ಗುರಾಣಿಬರ್ಜಿಗಳನ್ನೂ ಸಂಗ್ರಹಿಸಿಟ್ಟನು.",
			"12": "<sup>12</sup> ಹೀಗೆ ಇವುಗಳನ್ನು ಮತ್ತಷ್ಟು ಸುಭದ್ರಪಡಿಸಿದನು.",
			"13": "<sup>13</sup> ಯೆಹೂದ, ಬೆನ್ಯಾಮೀನ್ ಕುಲಗಳವರು ಅವನ ಸ್ವಾಧೀನದಲ್ಲಿದ್ದುದಲ್ಲದೆ ಬೇರೆ ಎಲ್ಲ ಇಸ್ರಯೇಲ್ ಪ್ರಾಂತ್ಯಗಳಲ್ಲಿದ್ದ ಯಾಜಕರು ಹಾಗೂ ಲೇವಿಯರು ಅವನೊಡನೆ ಸೇರಿಕೊಂಡರು.",
			"14": "<sup>14</sup> ಲೇವಿಯರು ಸರ್ವೇಶ್ವರನಿಗೆ ಯಾಜಕ ಸೇವೆಮಾಡದಂತೆ ಯಾರೊಬ್ಬಾಮನು ಮತ್ತು ಅವನ ಮಕ್ಕಳು ಅವರನ್ನು ಬಹಿಷ್ಕರಿಸಿ ಇದ್ದರು.",
			"15": "<sup>15</sup> ತಾವೇ ಏರ್ಪಡಿಸಿದ್ದ ಪೂಜಾಸ್ಥಳಗಳಿಗಾಗಿ ಹಾಗೂ ಅಜದೇವತೆ, ಹೋರಿಕರುಗಳ ಮೂರ್ತಿಪೂಜೆಗಾಗಿ ಬೇರೆ ಪೂಜಾರಿಗಳನ್ನು ನೇಮಿಸಿಕೊಂಡಿದ್ದರು. ಆದುದರಿಂದ ಅವರು ಗೋಮಾಳಗಳಿದ್ದ ತಮ್ಮ ಸೊತ್ತುಗಳನ್ನು ಬಿಟ್ಟು ಜುದೇಯನಾಡಿಗೂ ಜೆರುಸಲೇಮಿಗೂ ಬಂದರು.",
			"16": "<sup>16</sup> ಇಸ್ರಯೇಲರ ಎಲ್ಲ ಕುಲಗಳಲ್ಲಿ ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ದರ್ಶನವನ್ನು ಬಯಸುತ್ತಿದ್ದವರು ತಮ್ಮ ಪೂರ್ವಜರ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ಬಲಿಯರ್ಪಿಸುವುದಕ್ಕಾಗಿ ಆ ಲೇವಿಯರನ್ನು ಹಿಂಬಾಲಿಸಿ ಜೆರುಸಲೇಮಿಗೆ ಬಂದರು.",
			"17": "<sup>17</sup> ಇವರೆಲ್ಲರು ಮೊದಲನೆಯ ಮೂರು ವರ್ಷಗಳಲ್ಲಿ ದಾವೀದ ಹಾಗೂ ಸೊಲೊಮೋನರ ಮಾರ್ಗದಲ್ಲೇ ನಡೆಯುತ್ತಿದ್ದರು. ಎಂದೇ ಜುದೇಯರಾಜ್ಯವು ಆ ವರ್ಷಗಳಲ್ಲಿ ವೃದ್ಧಿಯಾಗಿ, ಸೊಲೊಮೋನನ ಮಗ ರೆಹಬ್ಬಾಮನು ಬಲಿಷ್ಠನಾದನು.",
			"18": "<sup>18</sup> ರೆಹಬ್ಬಾಮನು, ದಾವೀದನ ಮಗ ಯೆರೀಮೋತನಿಗೆ ಜೆಸ್ಸೆಯ ಮೊಮ್ಮಗಳೂ ಎಲೀಯಾಬನ ಮಗಳೂ ಆದ ಅಬೀಹೈಲಳಲ್ಲಿ ಹುಟ್ಟಿದ ಮಹ್ಲತ್ ಎಂಬಾಕೆಯನ್ನು ಹೆಂಡತಿಯನ್ನಾಗಿ ತೆಗೆದುಕೊಂಡನು.",
			"19": "<sup>19</sup> ಆಕೆ ಅವನಿಂದ ಯೆಗೂಷ್, ಶೆಮರ್ಯ, ಜಾಹಮ್ ಎಂಬ ಗಂಡು ಮಕ್ಕಳನ್ನು ಹೆತ್ತಳು.",
			"20": "<sup>20</sup> ಅನಂತರ ಅವನು ಅಬ್ಷಾಲೋಮನ ಮಗಳಾದ ಮಾಕ ಎಂಬಾಕೆಯನ್ನು ಮದುವೆ ಮಾಡಿಕೊಂಡನು; ಆಕೆ ಅವನಿಂದ ಅಬೀಯ, ಅತ್ತೈ, ಜೀಜ, ಶೆಲೋಮೀತ್ ಎಂಬುವರನ್ನು ಹೆತ್ತಳು.",
			"21": "<sup>21</sup> ರೆಹಬ್ಬಾಮನು ತನ್ನ ಎಲ್ಲಾ ಪತ್ನಿಯರಲ್ಲಿಯೂ ಉಪಪತ್ನಿಯರಲ್ಲಿಯೂ ಅಬ್ಷಾಲೋಮನ ಮಗಳಾದ ಮಾಕಳನ್ನು ಹೆಚ್ಚಾಗಿ ಪ್ರೀತಿಸುತ್ತಿದ್ದನು. ಅವನಿಗೆ ಹದಿನೆಂಟು ಮಂದಿ ಪತ್ನಿಯರೂ ಅರವತ್ತು ಮಂದಿ ಉಪಪತ್ನಿಯರೂ ಇದ್ದರು. ಇವರಲ್ಲಿ ಅವನಿಗೆ ಇಪ್ಪತ್ತ ಎಂಟು ಮಂದಿ ಗಂಡುಮಕ್ಕಳೂ ಅರವತ್ತು ಮಂದಿ ಹೆಣ್ಣು ಮಕ್ಕಳೂ ಹುಟ್ಟಿದರು.",
			"22": "<sup>22</sup> ರೆಹಬ್ಬಾಮನು ಮಾಕಳ ಮಗನಾದ ಅಬೀಯನನ್ನು ಅರಸನನ್ನಾಗಿ ಮಾಡಬೇಕೆಂದು ಅವನನ್ನು ಅವನ ಅಣ್ಣತಮ್ಮಂದಿರಲ್ಲಿ ಪ್ರಧಾನನನ್ನಾಗಿಯೂ ನೇಮಿಸಿದನು.",
			"23": "<sup>23</sup> ತನ್ನ ಉಳಿದ ಎಲ್ಲ ಮಕ್ಕಳನ್ನು ಜಾಣತನದಿಂದ ವಿಂಗಡಿಸಿ ಯೆಹೂದ-ಬೆನ್ಯಾಮೀನ್ ಕುಲಪ್ರದೇಶಗಳಲ್ಲಿ ಇದ್ದ ಕೋಟೆ ಕೊತ್ತಲುಗಳಿಂದ ಕೂಡಿದ ಆಯಾ ಪಟ್ಟಣಗಳಲ್ಲಿಟ್ಟು, ಅವರಿಗೆ ಬೇಕಾದಷ್ಟು ಜೀವನಾಂಶವನ್ನು ಏರ್ಪಡಿಸಿ, ಹಲವಾರು ಹೆಂಡತಿಯರನ್ನೂ ಗೊತ್ತುಮಾಡಿಕೊಟ್ಟನು."
		},
		"12": {
			"1": "<sup>1</sup> ರೆಹಬ್ಬಾಮನು ತನ್ನ ರಾಜ್ಯಾಧಿಕಾರವನ್ನು ಸುಭದ್ರಪಡಿಸಿಕೊಂಡ ಮೇಲೆ ಅವನೂ ಅವನ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಯೇಲರೆಲ್ಲರೂ ಸರ್ವೇಶ್ವರನ ಧರ್ಮೋಪದೇಶವನ್ನು ಬಿಟ್ಟುಬಿಟ್ಟರು;",
			"2": "<sup>2</sup> ಸರ್ವೇಶ್ವರನಿಗೆ ದ್ರೋಹಿಗಳಾದರು. ಅರಸ ರೆಹಬ್ಬಾಮನ ಆಳ್ವಿಕೆಯ ಐದನೆಯ ವರ್ಷದಲ್ಲಿ",
			"3": "<sup>3</sup> ಈಜಿಪ್ಟಿನ ಅರಸ ಶೀಶಕನು, ಸಾವಿರದ ಇನ್ನೂರು ರಥಗಳನ್ನೂ ಅರವತ್ತು ಸಾವಿರ ಮಂದಿ ರಾಹುತರನ್ನೂ ಈಜಿಪ್ಟಿನ, ಲಿಬ್ಯದ, ಸುಕ್ಕೀಯದ ಹಾಗೂ ಕೂಷ್ಯದ ಅಸಂಖ್ಯ ಸೈನಿಕರನ್ನೂ ಕೂಡಿಸಿಕೊಂಡು ಜೆರುಸಲೇಮಿಗೆ ಹೊರಟುಬಂದನು.",
			"4": "<sup>4</sup> ಜುದೇಯ ನಾಡಿನ ಕೋಟೆಕೊತ್ತಲುಗಳಿಂದ ಕೂಡಿದ ಪಟ್ಟಣಗಳನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡ ನಂತರ ಜೆರುಸಲೇಮನ್ನು ಸಮೀಪಿಸಿದನು.",
			"5": "<sup>5</sup> ಆಗ ಪ್ರವಾದಿ ಶೆಮಾಯನು ರೆಹಬ್ಬಾಮನ ಬಳಿಗೆ ಹಾಗೂ ಶೀಶಕನಿಗೆ ಹೆದರಿ ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಕೂಡಿದ್ದ ಯೆಹೂದ್ಯ ಪ್ರಧಾನರ ಬಳಿಗೆ ಬಂದು, “ನೀವು ನನ್ನನ್ನು ಬಿಟ್ಟದ್ದರಿಂದ ನಾನು ನಿಮ್ಮನ್ನು ಶೀಶಕನ ಕೈಗೆ ಬಿಟ್ಟುಬಿಟ್ಟಿದ್ದೇನೆ,” ಎನ್ನುತ್ತಾರೆ ಸರ್ವೇಶ್ವರ ಎಂದು ಹೇಳಿದನು.",
			"6": "<sup>6</sup> ಆಗ ಅರಸನೂ ಇಸ್ರಯೇಲ್ ಪ್ರಧಾನರೂ, ಸರ್ವೇಶ್ವರಸ್ವಾಮಿ ನ್ಯಾಯವಂತರೆಂದು ಒಪ್ಪಿಕೊಂಡು ತಮ್ಮನ್ನೇ ತಗ್ಗಿಸಿಕೊಂಡರು.",
			"7": "<sup>7</sup> ಸರ್ವೇಶ್ವರ ಇದನ್ನು ನೋಡಿ ಶೆಮಾಯನಿಗೆ, “ಇವರು ತಮ್ಮನ್ನೇ ತಗ್ಗಿಸಿಕೊಂಡದ್ದರಿಂದ ಇವರನ್ನು ಸಂಹರಿಸುವುದಿಲ್ಲ; ಸ್ವಲ್ಪಕಾಲದಲ್ಲೇ ಇವರಿಗೆ ರಕ್ಷಣೆಯನ್ನು ಅನುಗ್ರಹಿಸುವೆನು; ಶೀಶಕನ ಮುಖಾಂತರ ನನ್ನ ರೌದ್ರವನ್ನು ಜೆರುಸಲೇಮಿನ ಮೇಲೆ ಸುರಿದುಬಿಡುವುದಿಲ್ಲ.",
			"8": "<sup>8</sup> ಆದರೂ ನನ್ನ ಸೇವೆಗೂ ಅನ್ಯರಾಜ್ಯಗಳ ಸೇವೆಗೂ ಇರುವ ವ್ಯತ್ಯಾಸ ಇವರಿಗೆ ಗೊತ್ತಾಗುವಂತೆ ಇವರು ಶೀಶಕನಿಗೆ ದಾಸರಾಗಬೇಕು,” ಎಂದು ಹೇಳಿದರು.",
			"9": "<sup>9</sup> ಹೀಗೆ ಜೆರುಸಲೇಮಿಗೆ ವಿರೋಧವಾಗಿ ಬಂದ ಈಜಿಪ್ಟಿನ ರಾಜ ಶೀಶಕನು ಸರ್ವೇಶ್ವರನ ಆಲಯದ ಮತ್ತು ಅರಮನೆಯ ಎಲ್ಲ ದ್ರವ್ಯವನ್ನೂ ಸೊಲೊಮೋನನು ಮಾಡಿಸಿದ್ದ ಬಂಗಾರದ ಗುರಾಣಿಗಳನ್ನೂ ದೋಚಿಕೊಂಡು ಹೋದನು.",
			"10": "<sup>10</sup> ಅರಸ ರೆಹಬ್ಬಾಮನು ಅವುಗಳಿಗೆ ಬದಲಾಗಿ ಕಂಚಿನ ಗುರಾಣಿಗಳನ್ನು ಮಾಡಿಸಿ, ಅವುಗಳನ್ನು ಅರಮನೆಯ ದ್ವಾರಪಾಲಕರಾಗಿದ್ದ ಮೈಗಾವಲಿನವರ ದಳವಾಯಿಗಳಿಗೆ ಒಪ್ಪಿಸಿದನು.",
			"11": "<sup>11</sup> ಅರಸನು ಸರ್ವೇಶ್ವರನ ಆಲಯಕ್ಕೆ ಹೋಗುವಾಗಲೆಲ್ಲಾ ಮೈಗಾವಲಿನವರು ಅವುಗಳನ್ನು ಹಿಡಿದುಕೊಂಡು ಹೋಗುತ್ತಿದ್ದರು; ಅಲ್ಲಿಂದ ಹಿಂದಕ್ಕೆ ಬಂದ ಮೇಲೆ ಅವುಗಳನ್ನು ತಮ್ಮ ಕೋಣೆಯಲ್ಲಿ ಇಡುತ್ತಿದ್ದರು.",
			"12": "<sup>12</sup> ರೆಹಬ್ಬಾಮನು ತನ್ನನ್ನು ತಗ್ಗಿಸಿಕೊಂಡದ್ದರಿಂದ ಹಾಗೂ ಯೆಹೂದ್ಯರಲ್ಲಿ ಕೆಲವು ಸುಲಕ್ಷಣಗಳು ತೋರುತ್ತಿದ್ದುದರಿಂದ ಸರ್ವೇಶ್ವರಸ್ವಾಮಿ ರೆಹಬ್ಬಾಮನ ಮೇಲಿದ್ದ ಕೋಪವನ್ನು ತೊರೆದುಬಿಟ್ಟರು; ಅವನನ್ನು ಪೂರ್ತಿಯಾಗಿ ಹಾಳುಮಾಡಲಿಲ್ಲ.",
			"13": "<sup>13</sup> ಅರಸ ರೆಹಬ್ಬಾಮನು ಜೆರುಸಲೇಮಿನಲ್ಲಿ ತನ್ನ ರಾಜ್ಯಾಧಿಕಾರವನ್ನು ಸ್ಥಿರಪಡಿಸಿಕೊಂಡು ಪಟ್ಟಕ್ಕೆ ಬಂದದ್ದು ತನ್ನ ನಲವತ್ತೊಂದನೆಯ ವರ್ಷದಲ್ಲಿ. ಸರ್ವೇಶ್ವರ ಎಲ್ಲಾ ಕುಲಪ್ರಾಂತ್ಯಗಳಿಂದ ತಮ್ಮ ಹೆಸರಿಗಾಗಿ ಆರಿಸಿಕೊಂಡ ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಅವನು ಹದಿನೇಳು ವರ್ಷ ಆಳಿದನು. ಅಮ್ಮೋನಿಯಳಾದ ನಯಮಾ ಎಂಬಾಕೆ ಅವನ ತಾಯಿ.",
			"14": "<sup>14</sup> ಅವನು ಸರ್ವೇಶ್ವರನಿಗೆ ವಿಧೇಯನಾಗಿ ನಡೆಯಲು ಮನಸ್ಸುಮಾಡದೆ ದ್ರೋಹಿಯಾದನು.",
			"15": "<sup>15</sup> ರೆಹಬ್ಬಾಮನ ಪೂರ್ವೋತ್ತರ ಕೃತ್ಯಗಳು ಪ್ರವಾದಿಯಾದ ಶೆಮಾಯ ಹಾಗೂ ದರ್ಶಿಯಾದ ಇದ್ದೋ ಎಂಬುವರ ಚರಿತ್ರೆಗಳ ವಂಶಾವಳಿ ಭಾಗದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ. ರೆಹಬ್ಬಾಮನಿಗೂ ಯಾರೊಬ್ಬಾಮನಿಗೂ ಯಾವಾಗಲೂ ಯುದ್ಧ ನಡೆಯುತ್ತಿತ್ತು.",
			"16": "<sup>16</sup> ರೆಹಬ್ಬಾಮನು ಮೃತನಾಗಿ ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಲು ಅವನ ಶವಕ್ಕೆ ದಾವೀದ ನಗರದಲ್ಲಿ ಸಮಾಧಿಯಾಯಿತು. ಅವನ ಮಗ ಅಬೀಯನು ಅವನಿಗೆ ಬದಲಾಗಿ ಅರಸನಾದನು."
		},
		"13": {
			"1": "<sup>1</sup> ಅರಸ ಯಾರೊಬ್ಬಾಮನ ಆಳ್ವಿಕೆಯ ಹದಿನೆಂಟನೆಯ ವರ್ಷದಲ್ಲಿ ಅಬೀಯನು ಜುದೇಯದ ಅರಸನಾಗಿ",
			"2": "<sup>2</sup> ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಮೂರು ವರ್ಷ ಆಳಿದನು. ಗಿಬೆಯದ ಊರೀಯೇಲನ ಮಗಳಾದ ಮೀಕಾಯ ಎಂಬಾಕೆ ಇವನ ತಾಯಿ. ಇವನಿಗೂ ಯಾರೊಬ್ಬಾಮನಿಗೂ ಯುದ್ಧ ನಡೆಯಿತು.",
			"3": "<sup>3</sup> ಅಬೀಯನು ನಾಲ್ಕು ಲಕ್ಷ ಮಂದಿ ರಣವೀರರನ್ನು ಆರಿಸಿಕೊಂಡು ಬಂದು ಯುದ್ಧ ಪ್ರಾರಂಭಿಸಿದನು. ಯಾರೊಬ್ಬಾಮನು ಎಂಟು ಲಕ್ಷ ಶ್ರೇಷ್ಠಯೋಧರೊಡನೆ ಅವನಿಗೆ ವಿರೋಧವಾಗಿ ಬಂದು ವ್ಯೂಹಕಟ್ಟಿದನು.",
			"4": "<sup>4</sup> ಆಗ ಅಬೀಯನು ಎಫ್ರಯಿಮ್ ಪರ್ವತಪ್ರದೇಶದಲ್ಲಿರುವ ಚೆಮಾರೈಮೆಂಬ ಬೆಟ್ಟದಲ್ಲಿ ನಿಂತು, “ಯಾರೊಬ್ಬಾಮನೇ, ಎಲ್ಲಾ ಇಸ್ರಯೇಲರೇ, ನನ್ನ ಮಾತಿಗೆ ಕಿವಿಗೊಡಿ;",
			"5": "<sup>5</sup> ಮುರಿಯಲಾಗದ ಒಡಂಬಡಿಕೆಯಿಂದ ದಾವೀದನಿಗೂ ಅವನ ಸಂತಾನದವರಿಗೂ ಇಸ್ರಯೇಲರ ಮೇಲೆ ಸರ್ವೇಶ್ವರ ಶಾಶ್ವತವಾದ ಅರಸುತನವನ್ನು ಕೊಟ್ಟಿದ್ದಾರೆಂದು ನಿಮಗೆ ಗೊತ್ತಿಲ್ಲವೇ?",
			"6": "<sup>6</sup> ಹೀಗೆ ಇದ್ದರೂ ದಾವೀದನ ಮಗ ಸೊಲೊಮೋನನ ಸೇವೆಯಲ್ಲಿದ್ದ, ನೆಬಾಟನ ಮಗ ಯಾರೊಬ್ಬಾಮನು ದಂಗೆಯೆದ್ದು ತನ್ನ ದಣಿಗೆ ವಿರುದ್ಧ ದ್ರೋಹವೆಸಗಿದ;",
			"7": "<sup>7</sup> ಕಾಕಪೋಕರೂ ದುಷ್ಟರೂ ಆದ ಜನರನ್ನು ಕೂಡಿಕೊಂಡ ಎಳೇಪ್ರಾಯದವನೂ ಮುಗ್ಧನೂ ತಮ್ಮೆದುರಿನಲ್ಲಿ ನಿಲ್ಲಲು ಅಶಕ್ತನೂ ಆದ ಸೊಲೊಮೋನನ ಮಗ ರೆಹಬ್ಬಾಮನನ್ನು ಇವರು ಪ್ರತಿಭಟಿಸಿ ಬಲಗೊಂಡರು.",
			"8": "<sup>8</sup> ಈಗ ನೀವು ದೊಡ್ಡ ಗುಂಪಾಗಿ ಇರುವುದರಿಂದ ಹಾಗು ಯಾರೊಬ್ಬಾಮನು ನಿಮಗೆ ದೇವರುಗಳೆಂದು ಮಾಡಿಕೊಟ್ಟ ಬಂಗಾರದ ಹೋರಿಕರುಗಳ ಮೂರ್ತಿಗಳು ನಿಮ್ಮಲ್ಲಿರುವುದರಿಂದ, ದಾವೀದನ ಸಂತಾನದವರ ಕೈಯಲ್ಲಿರುವ ಸರ್ವೇಶ್ವರನ ರಾಜ್ಯಕ್ಕೆ ವಿರುದ್ಧ ದಂಗೆಯೆದ್ದು ಗೆಲ್ಲಬಹುದೆಂದು ನೆನಸುತ್ತೀರೋ?",
			"9": "<sup>9</sup> ನೀವು ಸರ್ವೇಶ್ವರನ ಯಾಜಕರಾದ ಆರೋನನ ಸಂತಾನದವರನ್ನೂ ಲೇವಿಯರನ್ನೂ ಓಡಿಸಿಬಿಟ್ಟು ಅನ್ಯದೇಶಗಳವರಂತೆ ನಿಮಗಾಗಿ ಪೂಜಾರಿಗಳನ್ನು ನೇಮಿಸಿಕೊಂಡಿದ್ದೀರಿ; ತಾನು ಪೂಜಾರಿಯಾಗಿ ಪ್ರತಿಷ್ಠಿತನಾಗಬೇಕೆಂದು ಒಂದು ಎಳೇ ಹೋರಿಯನ್ನೂ ಏಳು ಟಗರುಗಳನ್ನೂ ತೆಗೆದುಕೊಂಡು ಬಂದ ಪ್ರತಿಯೊಬ್ಬನೂ ದೇವರಲ್ಲದವುಗಳಿಗೆ ಪೂಜಾರಿಯಾದನಷ್ಟೆ!",
			"10": "<sup>10</sup> ನಮಗಾದರೋ ಸರ್ವೇಶ್ವರನೊಬ್ಬನೇ ದೇವರು; ನಾವು ಅವರನ್ನು ಬಿಡಲಿಲ್ಲ. ಸರ್ವೇಶ್ವರನ ಆರಾಧನೆ ನಡೆಸಲು ಆರೋನನ ಸಂತಾನದವರಾದ ಯಾಜಕರು ಹಾಗೂ ತಮಗೆ ನೇಮಕವಾದ ಕೆಲಸದಲ್ಲಿ ನಿರತರಾದ ಲೇವಿಯರು ನಮಗಿರುತ್ತಾರೆ.",
			"11": "<sup>11</sup> ಈ ಯಾಜಕರು ಪ್ರತಿದಿನ ಬೆಳಿಗ್ಗೆ ಹಾಗೂ ಸಂಜೆ ಸರ್ವೇಶ್ವರನಿಗಾಗಿ ದಹನಬಲಿಗಳನ್ನು ಸಮರ್ಪಿಸುತ್ತಾ, ಸುಗಂಧದ್ರವ್ಯಗಳ ಧೂಪಾರತಿ ಎತ್ತುತ್ತಾ, ಚೊಕ್ಕಬಂಗಾರದ ಮೇಜಿನ ಮೇಲೆ ನೈವೇದ್ಯವಾದ ರೊಟ್ಟಿಗಳನ್ನಿಡುತ್ತಾ, ಪ್ರತೀ ಸಾಯಂಕಾಲ ಬಂಗಾರದ ದೀಪಸ್ತಂಭದ ದೀಪಗಳನ್ನು ಹಚ್ಚುತ್ತಾ ಇರುತ್ತಾರೆ. ಹೀಗೆ ನಾವು ನಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಕಟ್ಟಳೆಗಳನ್ನು ಕೈಕೊಳ್ಳುತ್ತೇವೆ; ನೀವಾದರೋ ಅವರನ್ನು ತೊರೆದುಬಿಟ್ಟವರು.",
			"12": "<sup>12</sup> ಇಗೋ, ದೇವರೇ ನಾಯಕರಾಗಿ ನಮ್ಮೊಂದಿಗಿರುತ್ತಾರೆ; ನಿಮಗೆ ವಿರುದ್ಧ ಯುದ್ಧ ಕಹಳೆಗಳನ್ನು ಮೊಳಗಿಸುವ ಯಾಜಕರೂ ನಮ್ಮೊಂದಿಗೆ ಇರುತ್ತಾರೆ. ಇಸ್ರಯೇಲರೇ, ನಿಮ್ಮ ಪೂರ್ವಜರ ದೇವರಾದ ಸರ್ವೇಶ್ವರನೊಡನೆ ಯುದ್ಧಮಾಡಬೇಡಿ; ನೀವು ಜಯಿಸಲಾರಿರಿ,” ಎಂದು ಕೂಗಿ ಹೇಳಿದನು.",
			"13": "<sup>13</sup> ಯಾರೊಬ್ಬಾಮನು ಯೆಹೂದ್ಯರ ಹಿಂದೆ ಹೊಂಚುಹಾಕುವುದಕ್ಕಾಗಿ ತನ್ನ ಸೈನ್ಯದ ಒಂದು ಗುಂಪನ್ನು ಕಳುಹಿಸಿದನು. ಹೀಗೆ ಶತ್ರುಸೈನ್ಯ ಯೆಹೂದ್ಯರ ಮುಂದೆಯೂ ಹೊಂಚುಹಾಕುವವರು ಅವರ ಹಿಂದೆಯೂ ಇದ್ದರು.",
			"14": "<sup>14</sup> ಯೆಹೂದ್ಯರು ತಮ್ಮ ಮುಂದೆಯೂ ಹಿಂದೆಯೂ ಯುದ್ಧ ಪ್ರಾರಂಭವಾಗಿರುವುದನ್ನು ಕಂಡು ಸರ್ವೇಶ್ವರನಿಗೆ ಅಭಿಮುಖರಾಗಿ ಮೊರೆಯಿಟ್ಟರು; ಯಾಜಕರು ಕಹಳೆಗಳನ್ನು ಊದಿದರು.",
			"15": "<sup>15</sup> ಅನಂತರ ಜನರು ಆರ್ಭಟಿಸಿದರು. ಅವರು ಆರ್ಭಟಿಸಿದ ಕೂಡಲೆ ದೇವರು ಯಾರೊಬ್ಬಾಮನನ್ನೂ ಎಲ್ಲಾ ಇಸ್ರಯೇಲರನ್ನೂ ಅಬೀಯನಿಂದ ಹಾಗೂ ಯೆಹೂದ್ಯರಿಂದ ಅಪಜಯಗೊಳಿಸಿದರು.",
			"16": "<sup>16</sup> ಹೀಗಾಗಿ ಇಸ್ರಯೇಲರು ಯೆಹೂದ್ಯರ ಎದುರಿನಿಂದ ಓಡಿಹೋದರು. ದೇವರು ಅವರನ್ನು ಯೆಹೂದ್ಯರ ಕೈಗೆ ಒಪ್ಪಿಸಿದರು.",
			"17": "<sup>17</sup> ಅಬೀಯನೂ ಅವನ ಜನರೂ ಅವರಲ್ಲಿ ಅನೇಕಾನೇಕರನ್ನು ಸದೆಬಡಿದರು. ಇಸ್ರಯೇಲ್ ಯೋಧರಲ್ಲಿ ಹತರಾಗಿ ಬಿದ್ದವರು ಐದು ಲಕ್ಷ ಮಂದಿ .",
			"18": "<sup>18</sup> ಹೀಗೆ ಇಸ್ರಯೇಲರು ಆ ಕಾಲದಲ್ಲಿ ಸೋಲು ಸವಿದರು; ಯೆಹೂದ್ಯರು ತಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಲ್ಲಿ ಭರವಸೆಯಿಟ್ಟದ್ದರಿಂದ ಜಯಶೀಲರಾದರು.",
			"19": "<sup>19</sup> ಅಬೀಯನು ಯಾರೊಬ್ಬಾಮನನ್ನು ಹಿಂದಟ್ಟಿ, ಅವನಿಂದ ಬೇತೇಲ್, ಯೆಷಾನಾ, ಎಫ್ರೋನ್ ಎಂಬ ಪಟ್ಟಣಗಳನ್ನೂ ಅವುಗಳಿಗೆ ಸೇರಿದ ಗ್ರಾಮಗಳನ್ನೂ ಕಿತ್ತುಕೊಂಡನು.",
			"20": "<sup>20</sup> ಯಾರೊಬ್ಬಾಮನು ಅಬೀಯನ ಜೀವಮಾನದಲ್ಲಿ ಮತ್ತೆ ತಲೆಯೆತ್ತಲೇ ಇಲ್ಲ. ಕಾಲಕ್ರಮೇಣ, ಸರ್ವೇಶ್ವರನ ದಂಡನೆಯಿಂದ ಅವನು ಸತ್ತನು.",
			"21": "<sup>21</sup> ಇತ್ತ ಅಬೀಯನು ಪ್ರಬಲನಾದನು. ಅವನಿಗೆ ಹದಿನಾಲ್ಕು ಮಂದಿ ಹೆಂಡತಿಯರೂ ಇಪ್ಪತ್ತೆರಡು ಮಂದಿ ಗಂಡುಮಕ್ಕಳೂ ಹದಿನಾರು ಮಂದಿ ಹೆಣ್ಣುಮಕ್ಕಳೂ ಇದ್ದರು.",
			"22": "<sup>22</sup> ಅಬೀಯನ ಉಳಿದ ಚರಿತ್ರೆ ಹಾಗೂ ನಡೆನುಡಿಗಳು ಪ್ರವಾದಿ ಇದ್ದೋವಿನ ವ್ಯಾಖ್ಯಾನದಲ್ಲಿ ಲಿಖಿತವಾಗಿವೆ."
		},
		"14": {
			"1": "<sup>1</sup> ಅಬೀಯನು ಮೃತನಾಗಿ ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಿದನು. ಅವನ ಶವವನ್ನು ದಾವೀದನಗರದಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಅವನ ಸ್ಥಾನದಲ್ಲಿ ಅವನ ಮಗ ಆಸನು ಅರಸನಾದನು. ಇವನ ಕಾಲದಲ್ಲಿ, ನಾಡಿನಲ್ಲಿ ಹತ್ತು ವರ್ಷ ಶಾಂತಿಸಮಾಧಾನವಿತ್ತು.",
			"2": "<sup>2</sup> ಆಸನು ತನ್ನ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ಒಳ್ಳೆಯವನಾಗಿಯೂ ನೀತಿವಂತನಾಗಿಯೂ ನಡೆದನು.",
			"3": "<sup>3</sup> ಇವನು ಅನ್ಯದೇವತೆಗಳ ಯಜ್ಞವೇದಿಗಳನ್ನೂ ಪೂಜಾಸ್ಥಳಗಳನ್ನೂ ತೆಗೆದುಹಾಕಿಸಿದನು. ಕಲ್ಲುಕಂಬಗಳನ್ನು ಒಡೆದು ಹಾಕಿಸಿದನು. ಅಶೇರ ವಿಗ್ರಹಸ್ತಂಭಗಳನ್ನು ಕೆಡವಿಬಿಟ್ಟನು.",
			"4": "<sup>4</sup> ಯೆಹೂದ್ಯರಿಗೆ, “ನೀವು ನಿಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಸರ್ವೇಶ್ವರನನ್ನೇ ಆಶ್ರಯಿಸಿಕೊಂಡು ಧರ್ಮಶಾಸ್ತ್ರವಿಧಿಗಳನ್ನು ಕೈಗೊಳ್ಳಿರಿ,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"5": "<sup>5</sup> ಜುದೇಯದ ಎಲ್ಲ ಪಟ್ಟಣಗಳಿಂದ ಪೂಜಾಸ್ಥಳಗಳನ್ನೂ ಸೂರ್ಯಸ್ತಂಭಗಳನ್ನೂ ತೆಗೆದು ಹಾಕಿಸಿದನು. ಇವನ ಆಳ್ವಿಕೆಯಲ್ಲಿ ರಾಜ್ಯದೊಳಗೆ ಸಮಾಧಾನ ವಿತ್ತು.",
			"6": "<sup>6</sup> ಸರ್ವೇಶ್ವರನ ಅನುಗ್ರಹದಿಂದ ಶತ್ರುಭಯ ತಪ್ಪಿ, ಆ ವರ್ಷಗಳಲ್ಲಿ ಯಾವ ಯುದ್ಧವೂ ಇಲ್ಲದೆ, ನಾಡಿನಲ್ಲಿ ಸಮಾಧಾನವಿದ್ದುದರಿಂದ ಇವನು ಜುದೇಯದಲ್ಲಿ ಕೋಟೆಕೊತ್ತಲಗಳುಳ್ಳ ಪಟ್ಟಣಗಳನ್ನು ಕಟ್ಟಿಸಿದನು.",
			"7": "<sup>7</sup> ಇವನು ಯೆಹೂದ್ಯರಿಗೆ, “ನಾವು ನಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನನ್ನು ಆಶ್ರಯಿಸಿಕೊಂಡ ಕಾರಣ, ಅವರ ಅನುಗ್ರಹದಿಂದ ಸುತ್ತಣ ವೈರಿಗಳ ಭಯ ತಪ್ಪಿ, ನಾಡು ಈವರೆಗೆ ನಿರಾತಂಕವಾಗಿದೆ. ಆದುದರಿಂದ ನಾವು ಪಟ್ಟಣಗಳನ್ನು ಕಟ್ಟಿ ಅವುಗಳನ್ನು ಸುತ್ತಣ ಗೋಡೆ, ಬುರುಜು, ಬಾಗಿಲು, ಅಗುಳಿ ಇವುಗಳಿಂದ ಭದ್ರಪಡಿಸೋಣ,” ಎಂದನು. ಅವರು ಅವುಗಳನ್ನು ಚೆನ್ನಾಗಿ ಕಟ್ಟಿ ಮುಗಿಸಿದರು.",
			"8": "<sup>8</sup> ಆಸನಿಗೆ ಸೈನ್ಯವಿತ್ತು; ಅದರಲ್ಲಿ ಗುರಾಣಿ ಬರ್ಜಿಗಳನ್ನು ಹಿಡಿದ ಮೂರು ಲಕ್ಷ ಮಂದಿ ಯೆಹೂದ್ಯರಿದ್ದರು. ಖೇಡ್ಯ ಹಿಡಿದವರೂ ಬಿಲ್ಲುಗಾರರೂ ಆದ ಎರಡು ಲಕ್ಷದ ಎಂಬತ್ತು ಸಾವಿರ ಮಂದಿ ಬೆನ್ಯಾಮೀನ್ಯರೂ ಇದ್ದರು. ಇವರೆಲ್ಲರು ರಣವೀರರು.",
			"9": "<sup>9</sup> ಸುಡಾನಿನ ಜೆರಹನು ಹತ್ತು ಲಕ್ಷ ಸೈನ್ಯವನ್ನೂ ಮುನ್ನೂರು ರಥಗಳನ್ನೂ ತೆಗೆದುಕೊಂಡು ಇಸ್ರಯೇಲರಿಗೆ ವಿರುದ್ಧ ಯುದ್ಧಕ್ಕೆ ಹೊರಟು ಮಾರೇಷಕ್ಕೆ ಬಂದನು.",
			"10": "<sup>10</sup> ಆಸನು ಅವನನ್ನು ಎದುರುಗೊಳ್ಳಲು ಹೋದನು. ಮಾರೇಷದ ಬಳಿಯಲ್ಲಿದ್ದ ಚೆಫಾತಾ ಬಯಲಿನಲ್ಲಿ ಉಭಯರೂ ವ್ಯೂಹಕಟ್ಟಿದರು.",
			"11": "<sup>11</sup> ಆಸನು ತನ್ನ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ, “ಸರ್ವೇಶ್ವರಾ, ಬಲಿಷ್ಠನು ಬಲಹೀನನ ಮೇಲೆ ಬೀಳುವಾಗ, ನಿಮ್ಮ ಹೊರತು ರಕ್ಷಕನಿಲ್ಲ. ನಿಮ್ಮಲ್ಲಿ ಭರವಸೆ ಇಟ್ಟು ನಿಮ್ಮ ಹೆಸರಿನಲ್ಲಿ ಈ ಮಹಾ ಸೇನೆಗೆ ವಿರುದ್ಧ ಯುದ್ಧಕ್ಕೆ ಬಂದಿದ್ದೇವೆ. ಸರ್ವೇಶ್ವರಾ, ನಮ್ಮ ದೇವರು ನೀವು; ನರರು ನಿಮ್ಮನ್ನು ಎದುರಿಸಿ ಗೆಲ್ಲಬಾರದು,” ಎಂದು ಮೊರೆ ಇಟ್ಟನು.",
			"12": "<sup>12</sup> ಸರ್ವೇಶ್ವರ ಅರಸನಿಂದಲೂ ಯೆಹೂದ್ಯರಿಂದಲೂ ಸುಡಾನರಿಗೆ ಅಪಜಯ ಉಂಟಾಗುವಂತೆ ಮಾಡಿದರು.",
			"13": "<sup>13</sup> ಅವರು ಸೋತು ಓಡಿಹೋದಾಗ ಆಸನೂ ಅವನ ಜನರೂ ಗೆರಾರಿನವರೆಗೂ ಅವರನ್ನು ಹಿಂದಟ್ಟಿ ಸಂಹರಿಸಿದರು. ಅವರಲ್ಲಿ ಯಾರೂ ಜೀವದಿಂದುಳಿಯಲಿಲ್ಲ. ಸರ್ವೇಶ್ವರನ ಮತ್ತು ಅವರ ಸೈನ್ಯದ ಮುಂದೆ ಸುಡಾನರು ಭಿನ್ನಭಿನ್ನರಾದರು. ಇಸ್ರಯೇಲರಿಗೆ ಅಪರಿಮಿತವಾದ ಕೊಳ್ಳೆಸಿಕ್ಕಿತು.",
			"14": "<sup>14</sup> ಇದಲ್ಲದೆ ಸರ್ವೇಶ್ವರನ ಭಯದಿಂದ ತಬ್ಬಿಬ್ಬಾದ ಗೆರಾರಿನ ಸುತ್ತಣ ಪಟ್ಟಣಗಳನ್ನು ಅವರು ವಶಪಡಿಸಿಕೊಂಡು, ಅವುಗಳನ್ನೆಲ್ಲಾ ಸುಲಿಗೆಮಾಡಿದರು. ಅವುಗಳಲ್ಲೂ ದೊಡ್ಡ ಕೊಳ್ಳೆ ಸಿಕ್ಕಿತು.",
			"15": "<sup>15</sup> ದನಕಾಯುವವರ ಗುಡಾರಗಳ ಮೇಲೆ ದಾಳಿಮಾಡಿ ಎಷ್ಟೋ ಒಂಟೆಕುರಿಗಳನ್ನು ಜೆರುಸಲೇಮಿಗೆ ಹೊಡೆದುಕೊಂಡು ಬಂದರು."
		},
		"15": {
			"1": "<sup>1</sup> ಓದೇದನ ಮಗ ಅಜರ್ಯನ ಮೇಲೆ ದೇವರ ಆತ್ಮವು ಬಂದಿತು.",
			"2": "<sup>2</sup> ಅವನು ಆಸನನ್ನು ಭೇಟಿಯಾಗಲು ಬಂದು, ಅವನಿಗೆ, “ಆಸ ರಾಜನೇ, ಎಲ್ಲಾ ಯೆಹೂದ ಹಾಗೂ ಬೆನ್ಯಾಮೀನ್ ಕುಲಗಳವರೇ, ಕಿವಿಗೊಡಿ; ನೀವು ಸರ್ವೇಶ್ವರನನ್ನು ಹೊಂದಿಕೊಂಡಿರುವ ತನಕ ಅವರೂ ನಿಮ್ಮೊಂದಿಗಿರುವರು; ನೀವು ಅವರನ್ನು ಅರಸಿದರೆ ನಿಮಗೆ ಸಿಕ್ಕುವರು. ಅವರನ್ನು ಬಿಟ್ಟರೆ ಅವರೂ ನಿಮ್ಮನ್ನು ಬಿಟ್ಟುಬಿಡುವರು.",
			"3": "<sup>3</sup> ಇಸ್ರಯೇಲರಿಗೆ ಬಹುಕಾಲದವರೆಗೆ ನಿಜವಾದ ದೇವರು, ಬೋಧಿಸುವ ಯಾಜಕರು ಹಾಗೂ ಧರ್ಮಶಾಸ್ತ್ರ ಇರಲಿಲ್ಲ.",
			"4": "<sup>4</sup> ಅವರು ತಮ್ಮ ಇಕ್ಕಟ್ಟಿನಲ್ಲಿ ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಕಡೆಗೆ ತಿರುಗಿಕೊಂಡರು; ಹುಡುಕಿದಾಗ ಸರ್ವೇಶ್ವರ ಅವರಿಗೆ ಸಿಕ್ಕಿದರು.",
			"5": "<sup>5</sup> ಆ ಕಾಲದಲ್ಲಿ ಯಾರಿಗೂ ನಿರ್ಭಯವಾಗಿ ತಿರುಗಾಡುವುದಕ್ಕೆ ಆಗುತ್ತಾ ಇರಲಿಲ್ಲ. ನಾಡಿನ ನಿವಾಸಿಗಳೆಲ್ಲರೂ ಕಳವಳಗೊಂಡಿದ್ದರು.",
			"6": "<sup>6</sup> ಒಂದು ಜನಾಂಗ ಇನ್ನೊಂದು ಜನಾಂಗವನ್ನೂ ಒಂದು ಪಟ್ಟಣದವರು ಇನ್ನೊಂದು ಪಟ್ಟಣದವರನ್ನೂ ಹಾಳುಮಾಡುತ್ತಿದ್ದರು. ದೇವರು ನಾನಾ ತರದ ಕಷ್ಟದಿಂದ ಅವರನ್ನು ತಳಮಳಗೊಳಿಸಿದ್ದರು.",
			"7": "<sup>7</sup> ನೀವಾದರೋ ಸ್ಥಿರಚಿತ್ತರಾಗಿರಿ; ನಿಮ್ಮ ಕೈಗಳು ಜೋಲುಬೀಳದಿರಲಿ. ನಿಮ್ಮ ಪ್ರಯತ್ನಕ್ಕೆ ಫಲತಪ್ಪದು,” ಎಂದು ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ಆಸನು ಓದೇದನ ಮಗ ಅಜರ್ಯನ ಈ ಪ್ರವಾದನೋಕ್ತಿಗಳನ್ನು ಕೇಳಿ ಧೈರ್ಯಗೊಂಡನು. ಜುದೇಯ ಹಾಗೂ ಬೆನ್ಯಾಮೀನ್ ಪ್ರಾಂತ್ಯಗಳಲ್ಲೂ ತಾನು ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡಿದ್ದ ಎಫ್ರಯಿಮ್ ಪರ್ವತ ಪ್ರದೇಶದ ಪಟ್ಟಣಗಳಲ್ಲೂ ಇದ್ದ ಅಸಹ್ಯ ಪ್ರತಿಮೆಗಳನ್ನು ತೆಗೆದು ಹಾಕಿಸಿದನು; ಸರ್ವೇಶ್ವರನ ಆಲಯಕ್ಕೆ ಸೇರಿದ ಮಂಟಪದ ಮುಂದಿದ್ದ ಬಲಿಪೀಠವನ್ನು ದುರಸ್ತಿ ಮಾಡಿಸಿದನು.",
			"9": "<sup>9</sup> ಆಮೇಲೆ ಆಸನು ಎಲ್ಲಾ ಯೆಹೂದ ಹಾಗೂ ಬೆನ್ಯಾಮೀನ್ ಕುಲಗಳವರನ್ನು ಬರಮಾಡಿದನು. ದೇವರಾದ ಸರ್ವೇಶ್ವರ ತನ್ನೊಂದಿಗೆ ಇರುವುದನ್ನು ನೋಡಿ, ತನ್ನ ಬಳಿಗೆ ಗುಂಪುಗುಂಪಾಗಿ ಕೂಡಿಬಂದು, ಜುದೇಯ ನಾಡಿನಲ್ಲಿ ನೆಲಸಿದ್ದ ಎಫ್ರಯಿಮ್, ಮನಸ್ಸೆ ಹಾಗೂ ಸಿಮೆಯೋನ್ ಕುಲಗಳವರನ್ನು ಸಹ ಕರೆಸಿಕೊಂಡನು.",
			"10": "<sup>10</sup> ಅವರು ಆಸನ ಆಳ್ವಿಕೆಯ ಹದಿನೈದನೆಯ ವರ್ಷದ ಮೂರನೆಯ ತಿಂಗಳಿನಲ್ಲಿ ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಕೂಡಿಬಂದರು.",
			"11": "<sup>11</sup> ತಾವು ತಂದ ಕೊಳ್ಳೆಯಿಂದ ಏಳು ನೂರು ಹೋರಿಗಳನ್ನೂ ಏಳು ಸಾವಿರ ಕುರಿಗಳನ್ನೂ ಸರ್ವೇಶ್ವರನಿಗೆ ಆ ದಿನ ಬಲಿದಾನ ಮಾಡಿದರು.",
			"12": "<sup>12</sup> ಇದಲ್ಲದೆ, ಅವರು ಪೂರ್ಣಹೃದಯದಿಂದಲೂ ಪೂರ್ಣಪ್ರಾಣದಿಂದಲೂ ತಮ್ಮ ಪೂರ್ವಜರ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ಭಕ್ತರಾಗಿರುವೆವೆಂದು ಪ್ರಮಾಣಮಾಡಿದರು.",
			"13": "<sup>13</sup> ಇದಲ್ಲದೆ, “ತಮ್ಮಲ್ಲಿ ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಭಕ್ತಿಯನ್ನು ತೊರೆದುಬಿಡುವವರು ಚಿಕ್ಕವರಾಗಲಿ ದೊಡ್ಡವರಾಗಲಿ, ಗಂಡಸರಾಗಲಿ ಹೆಂಗಸರಾಗಲಿ, ಅವರೆಲ್ಲರನ್ನು ಕೊಲ್ಲಲಾಗುವುದು” ಎಂದು,",
			"14": "<sup>14</sup> ತುತ್ತೂರಿ ಕೊಂಬುಗಳನ್ನು ಊದಿಸಿ, ದೊಡ್ಡ ಘೋಷಣೆಗಳೊಡನೆ, ವಾಗ್ದಾನ ಮಾಡಿದರು.",
			"15": "<sup>15</sup> ಈ ಪ್ರಮಾಣದ ನಿಮಿತ್ತ ಯೆಹೂದ್ಯರೆಲ್ಲರಿಗೆ ಸಂತೋಷವಾಯಿತು. ಅವರು ಪೂರ್ಣಮನಸ್ಸಿನಿಂದ ಪ್ರಮಾಣಮಾಡಿ ತುಂಬಾ ಲವಲವಿಕೆಯಿಂದ ಸರ್ವೇಶ್ವರನನ್ನು ಬಯಸಿದ ಕಾರಣ, ಸರ್ವೇಶ್ವರ ಅವರಿಗೆ ಪ್ರಸನ್ನರಾಗಿ ಎಲ್ಲಾ ಕಡೆಗಳಲ್ಲೂ ಶಾಂತಿಸಮಾಧಾನವನ್ನು ಅನುಗ್ರಹಿಸಿದರು.",
			"16": "<sup>16</sup> ಅರಸನಾದ ಆಸನ ತಾಯಿ ಮಾಕಳು ಅಶೇರ ದೇವತೆಯ ಒಂದು ಅಸಹ್ಯ ಮೂರ್ತಿಯನ್ನು ಮಾಡಿಸಿದ್ದಳು. ಆದ್ದರಿಂದ ಅವನು ಆಕೆಯನ್ನು ‘ರಾಜಮಾತೆ’ ಎಂಬ ಪದವಿಯಿಂದ ತೆಗೆದುಹಾಕಿದನು. ಆ ಮೂರ್ತಿಯನ್ನು ತೆಗೆಸಿ ಕಿದ್ರೋನ್ ಹಳ್ಳದ ಬಳಿಯಲ್ಲಿ ಚೂರುಚೂರು ಮಾಡಿ ಸುಡಿಸಿದನು.",
			"17": "<sup>17</sup> ಇಸ್ರಯೇಲ್ ಪ್ರಾಂತ್ಯದಲ್ಲಿದ್ದ ಪೂಜಾಸ್ಥಳಗಳನ್ನು ಹಾಳುಮಾಡದೆ ಇದ್ದರೂ ಆಸನು ತನ್ನ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ಯಥಾರ್ಥಚಿತ್ತನಾಗಿ ನಡೆದುಕೊಂಡನು.",
			"18": "<sup>18</sup> ತಾನೂ ತನ್ನ ತಂದೆಯೂ ತಾನೂ ಪ್ರತಿಷ್ಠಿಸಿದ ಬೆಳ್ಳಿ ಬಂಗಾರವನ್ನೂ ಪಾತ್ರೆಗಳನ್ನೂ ದೇವಾಲಯದಲ್ಲಿ ತಂದಿಟ್ಟನು.",
			"19": "<sup>19</sup> ಅವನ ಆಳ್ವಿಕೆಯ ಮೂವತ್ತ ಐದನೆಯ ವರ್ಷದವರೆಗೂ ಯುದ್ಧವಿರಲಿಲ್ಲ."
		},
		"16": {
			"1": "<sup>1</sup> ಆಸನ ಆಳ್ವಿಕೆಯ ಮೂವತ್ತಾರನೆಯ ವರ್ಷದಲ್ಲಿ ಇಸ್ರಯೇಲ್ ರಾಜ ಬಾಷನು ಯೆಹೂದ್ಯರಿಗೆ ವಿರೋಧವಾಗಿ ಹೊರಟುಬಂದನು. ಯಾರೂ ಯೆಹೂದ್ಯರ ಅರಸ ಆಸನ ಬಳಿಗೆ ಹೋಗಿಬರುವುದಕ್ಕಾಗದಂತೆ ರಾಮಕೋಟೆಯನ್ನು ಕಟ್ಟಿಸಿದನು.",
			"2": "<sup>2</sup> ಆಗ ಆಸನು ಸರ್ವೇಶ್ವರನಾಲಯದ ಮತ್ತು ಅರಮನೆಯ ಭಂಡಾರಗಳಿಂದ ಬೆಳ್ಳಿಬಂಗಾರವನ್ನು ತೆಗೆದುಕೊಂಡು ದಮಸ್ಕದಲ್ಲಿದ್ದ ಸಿರಿಯಾದ ಅರಸ ಬೆನ್ಹದದನಿಗೆ ಕಳುಹಿಸಿ,",
			"3": "<sup>3</sup> “ನನಗೂ ನಿನಗೂ ನನ್ನ ತಂದೆಗೂ ನಿನ್ನ ತಂದೆಗೂ ಒಂದು ಒಪ್ಪಂದ ಇದೆಯಲ್ಲವೆ? ಇಗೋ, ನಿನಗೆ ಬೆಳ್ಳಿಬಂಗಾರವನ್ನು ಕಳುಹಿಸಿದ್ದೇನೆ; ಇಸ್ರಯೇಲರ ಅರಸ ಬಾಷನು ನನ್ನನ್ನು ಬಿಟ್ಟುಹೋಗುವಂತೆ ಮಾಡು; ಅವನ ಸಂಗಡ ನೀನು ಮಾಡಿಕೊಂಡಿರುವ ಒಪ್ಪಂದವನ್ನು ಮುರಿದುಬಿಡು,” ಎಂದು ಹೇಳಿಕಳುಹಿಸಿದನು.",
			"4": "<sup>4</sup> ಬೆನ್ಹದದನು ಅರಸನಾದ ಆಸನ ಮಾತನ್ನು ಕೇಳಿ, ತನ್ನ ಸೈನ್ಯಾಧಿಪತಿಗಳನ್ನು ಇಸ್ರಯೇಲರ ಪಟ್ಟಣಗಳಿಗೆ ವಿರುದ್ಧ ಕಳುಹಿಸಿದನು; ಅವರು ಇಯ್ಯೋನ್, ದಾನ್, ಆಬೇಲ್ಮಯಿಮ್ ಇವುಗಳನ್ನೂ ನಫ್ತಾಲ್ಯರ ಪಟ್ಟಣಗಳ ಎಲ್ಲ ಉಗ್ರಾಣಗಳನ್ನೂ ಹಾಳುಮಾಡಿದರು.",
			"5": "<sup>5</sup> ಬಾಷನು ಈ ಸುದ್ದಿಯನ್ನು ಕೇಳಿದಾಗ ರಾಮಕೋಟೆ ಕಟ್ಟಿಸುವ ಕೆಲಸವನ್ನು ನಿಲ್ಲಿಸಿಬಿಟ್ಟನು.",
			"6": "<sup>6</sup> ಅನಂತರ ಅರಸ ಆಸನು ಎಲ್ಲಾ ಯೆಹೂದ್ಯರನ್ನು ಕರೆಸಿ ಬಾಷನು ರಾಮಕೋಟೆಗಾಗಿ ತರಿಸಿದ್ದ ಕಲ್ಲುಮರಗಳನ್ನು ಇವರ ಮುಖಾಂತರ ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ಗೆಬ, ಮಿಚ್ಪ ಎಂಬ ಪಟ್ಟಣಗಳನ್ನು ಭದ್ರಪಡಿಸಿದನು.",
			"7": "<sup>7</sup> ಆ ಕಾಲದಲ್ಲಿ ದರ್ಶಿಯಾದ ಹನಾನಿಯು ಯೆಹೂದ್ಯರ ಅರಸ ಆಸನ ಬಳಿಗೆ ಬಂದು, “ನೀವು ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನನ್ನು ಆಶ್ರಯಿಸಿಕೊಳ್ಳದೆ ಸಿರಿಯಾದ ಅರಸನನ್ನು ಆಶ್ರಯಿಸಿಕೊಂಡದ್ದರಿಂದ ಸಿರಿಯಾ ರಾಜನ ಸೈನ್ಯವು ನಿಮ್ಮ ಕೈಗೆ ಬೀಳದಂತೆ ನುಣಿಚಿಕೊಂಡಿತು.",
			"8": "<sup>8</sup> ಸುದಾನ್ ಹಾಗೂ ಲಿಬಿಯರ ಸೈನ್ಯ ಅಪರಿಮಿತ ರಥಾಶ್ವಬಲಗಳುಳ್ಳ ಮಹಾ ಸೈನ್ಯವಲ್ಲವೇ? ನೀವು ಸರ್ವೇಶ್ವರನನ್ನು ಆಶ್ರಯಿಸಿಕೊಂಡದ್ದರಿಂದ ಅವರು ಅದನ್ನು ನಿಮ್ಮ ಕೈಗೆ ಒಪ್ಪಿಸಿಕೊಟ್ಟರು.",
			"9": "<sup>9</sup> ಸರ್ವೇಶ್ವರ ಭೂಲೋಕದ ಎಲ್ಲಾ ಕಡೆ ದೃಷ್ಟಿಹರಿಸುತ್ತಾ ತಮ್ಮ ಕಡೆಗೆ ಯಥಾರ್ಥ ಮನಸ್ಸುಳ್ಳವರ ರಕ್ಷಣೆಗಾಗಿ ತಮ್ಮ ಪ್ರತಾಪವನ್ನು ತೋರ್ಪಡಿಸುತ್ತಾರೆ. ನೀವು ಈ ಕಾರ್ಯದಲ್ಲಿ ಬುದ್ಧಿಹೀನರಾಗಿ ನಡೆದುಕೊಂಡಿದ್ದೀರಿ; ಇಂದಿನಿಂದ ನಿಮಗೆ ಯುದ್ಧಗಳು ಇದ್ದೇ ಇರುತ್ತವೆ,” ಎಂದು ಹೇಳಿದನು.",
			"10": "<sup>10</sup> ಆಸನು ಈ ಮಾತುಗಳಿಂದ ಬೇಸರಗೊಂಡು, ದರ್ಶಿಯ ಮೇಲೆ ಕುಪಿತನಾಗಿ, ಅವನನ್ನು ಸೆರೆಮನೆಯಲ್ಲಿಡಿಸಿ ಕೋಳಹಾಕಿದನು. ಇದಲ್ಲದೆ, ಜನರಲ್ಲಿ ಕೆಲವರನ್ನು ಪೀಡಿಸಿದನು.",
			"11": "<sup>11</sup> ಆಸನ ಪೂರ್ವೋತ್ತರ ಚತರಿತ್ರೆಯನ್ನು ಯೆಹೂದ ಮತ್ತು ಇಸ್ರಯೇಲ್ ರಾಜರ ಇತಿಹಾಸ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತದೆ.",
			"12": "<sup>12</sup> ಅವನ ಆಳ್ವಿಕೆಯ ಮೂವತ್ತೊಂಬತ್ತನೆಯ ವರ್ಷದಲ್ಲಿ ಅವನ ಕಾಲುಗಳಿಗೆ ಬಹು ಕಠಿಣವಾದ ರೋಗ ತಗಲಿತು. ಈ ರೋಗದಲ್ಲೂ ಅವನು ಸರ್ವೇಶ್ವರನ ಸಹಾಯವನ್ನು ಕೋರಲಿಲ್ಲ; ವೈದ್ಯರ ಸಹಾಯವನ್ನು ಮಾತ್ರ ಕೋರಿದನು.",
			"13": "<sup>13</sup> ಅವನು ತನ್ನ ಆಳ್ವಿಕೆಯ ನಲವತ್ತ ಒಂದನೆಯ ವರ್ಷದಲ್ಲಿ ತೀರಿಕೊಂಡು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಿದನು.",
			"14": "<sup>14</sup> ಅವನ ಶವವನ್ನು ಬುಕ್ಕಿಟ್ಟುಗಾರನ ಕಸಬಿನ ಮೇರೆಗೆ ಮಿಶ್ರಣ ಮಾಡಲ್ಪಟ್ಟ ತರತರದ ಸುಗಂಧದ್ರವ್ಯಗಳಿಂದ ತುಂಬಿರುವ ಹಾಸಿಗೆಯ ಮೇಲಿಟ್ಟು, ಅವನು ತನಗಾಗಿ ದಾವೀದನಗರದಲ್ಲಿ ತೋಡಿಸಿದ್ದ ಸಮಾಧಿಯಲ್ಲಿ ಅದನ್ನು ಇಟ್ಟರು. ಹೇರಳವಾಗಿ ಧೂಪಹಾಕಿ ಸಂತಾಪ ಸೂಚಿಸಿದರು."
		},
		"17": {
			"1": "<sup>1</sup> ಆಸನಿಗೆ ಅನಂತರ ಅವನ ಮಗ ಯೇಹೋಷಾಫಾಟನು ಅರಸನಾದನು. ಇಸ್ರಯೇಲರಿಂದ ಅಪಾಯವಾಗದಂತೆ ಇವನು ತನ್ನನ್ನೇ ಭದ್ರಪಡಿಸಿಕೊಂಡನು.",
			"2": "<sup>2</sup> ಜುದೇಯದ ಕೋಟೆಕೊತ್ತಲುಗಳುಳ್ಳ ಎಲ್ಲ ಪಟ್ಟಣಗಳಲ್ಲೂ ಸೈನ್ಯವನ್ನಿರಿಸಿದನು. ಜುದೇಯ ನಾಡಿನಲ್ಲೂ ಹಾಗೂ ತನ್ನ ತಂದೆ ಆಸನು ಕಿತ್ತುಕೊಂಡಿದ್ದ ಎಫ್ರಯಿಮರ ಪಟ್ಟಣಗಳಲ್ಲೂ ಅಧಿಕಾರಿಗಳನ್ನು ನೇಮಿಸಿದನು.",
			"3": "<sup>3</sup> ಬಾಳ್‍ದೇವರುಗಳನ್ನು ಅವಲಂಬಿಸದೆ, ತನ್ನ ಪೂರ್ವಿಕನಾದ ದಾವೀದನ ಮೊದಲನೇ ನಡತೆಯ ಪ್ರಕಾರ ಇವನು ನಡೆಯುತ್ತಿದ್ದುದರಿಂದ ಸರ್ವೇಶ್ವರ ಇವನ ಸಂಗಡ ಇದ್ದರು.",
			"4": "<sup>4</sup> ಇವನು ಇಸ್ರಯೇಲರಂತೆ ನಡೆಯದೆ, ತನ್ನ ಪಿತೃವಿನ ದೇವರಲ್ಲಿ ಭಕ್ತಿಯಿಟ್ಟು ಅವರ ಆಜ್ಞೆಗಳನ್ನು ಅನುಸರಿಸಿದನು.",
			"5": "<sup>5</sup> ಆದುದರಿಂದ ಸರ್ವೇಶ್ವರ ಇವನ ರಾಜ್ಯಾಧಿಕಾರವನ್ನು ಸ್ಥಿರಪಡಿಸಿದರು; ಯೆಹೂದ್ಯರೆಲ್ಲರೂ ಯೆಹೋಷಾಫಾಟನಿಗೆ ಕಾಣಿಕೆ ಕೊಡುತ್ತಿದ್ದುದರಿಂದ ಇವನ ಧನಘನತೆಗಳು ಹೆಚ್ಚಾದವು.",
			"6": "<sup>6</sup> ಇವನು ಸರ್ವೇಶ್ವರನ ಮಾರ್ಗದಲ್ಲಿ ನಡೆದು, ಧೈರ್ಯದಿಂದ ಜುದೇಯ ನಾಡಿನಲ್ಲಿದ್ದ ಪೂಜಾಸ್ಥಳಗಳನ್ನೂ ಆಶೇರ ವಿಗ್ರಹಸ್ತಂಭಗಳನ್ನೂ ತೆಗೆದುಹಾಕಿಸಿದನು.",
			"7": "<sup>7</sup> ಇವನು ತನ್ನ ಆಳ್ವಿಕೆಯ ಮೂರನೆಯ ವರ್ಷದಲ್ಲಿ ತನ್ನ ಸರದಾರರಾದ ಬೆನ್ಹೈಲ್, ಓಬದ್ಯ, ಜೆಕರ್ಯ, ನೆತನೇಲ್, ಮೀಕಾಯ ಇವರನ್ನೂ",
			"8": "<sup>8</sup> ಇವರ ಜೊತೆಯಲ್ಲಿ ಶೆಮಾಯ, ನೆತನ್ಯ, ಜೆಬದ್ಯ, ಅಸಾಹೇಲ್, ಶೆಮೀರಾಮೋತ್, ಯೆಹೋನಾತಾನ್, ಅದೋನೀಯ, ಟೋಬೀಯ, ಟೋಬದೋನೀಯ ಎಂಬ ಲೇವಿಯರನ್ನೂ ಎಲೀಷಾಮಾ, ಯೆಹೋರಾಮ್ ಎಂಬ ಯಾಜಕರನ್ನೂ ಧರ್ಮೋಪದೇಶ ಮಾಡುವುದಕ್ಕಾಗಿ ಜುದೇಯ ನಾಡಿನ ಪಟ್ಟಣಗಳಿಗೆ ಕಳುಹಿಸಿದನು.",
			"9": "<sup>9</sup> ಇವರು ಸರ್ವೇಶ್ವರನ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ತೆಗೆದುಕೊಂಡು ಜುದೇಯ ನಾಡಿನಲ್ಲೆಲ್ಲಾ ಸಂಚಾರಮಾಡಿ ಜನರಿಗೆ ಬೋಧಿಸಿದರು.",
			"10": "<sup>10</sup> ಜುದೇಯದ ಸುತ್ತಲಿನ ದೇಶಗಳ ರಾಜ್ಯಗಳವರೆಗೆ ಸರ್ವೇಶ್ವರನ ಭಯವಿತ್ತು. ಆದುದರಿಂದ ಅವರು ಯೆಹೋಷಾಫಾಟನೊಡನೆ ಯುದ್ಧಕ್ಕೆ ಬರಲಿಲ್ಲ.",
			"11": "<sup>11</sup> ಫಿಲಿಷ್ಟಿಯರಲ್ಲಿ ಕೆಲವರು ಯೆಹೋಷಾಫಾಟನಿಗೆ ಕಾಣಿಕೆಯನ್ನೂ ಕಪ್ಪವನ್ನಾಗಿ ಬೆಳ್ಳಿಯನ್ನೂ ತಂದುಕೊಡುತ್ತಿದ್ದರು. ಅರೇಬಿಯರು ತಮ್ಮ ಪಶುಪ್ರಾಣಿಗಳಲ್ಲಿ ಏಳು ಸಾವಿರದ ಏಳುನೂರು ಟಗರುಗಳನ್ನೂ ಏಳು ಸಾವಿರದ ಏಳುನೂರು ಹೋತಗಳನ್ನೂ ಕೊಡುತ್ತಿದ್ದರು.",
			"12": "<sup>12</sup> ಹೀಗೆ ಯೆಹೋಷಾಫಾಟನು ಅತ್ಯಧಿಕವಾಗಿ ಅಭಿವೃದ್ಧಿಹೊಂದಿ ಜುದೇಯ ನಾಡಿನಲ್ಲಿ ಕೋಟೆಗಳನ್ನು ಮತ್ತು ಉಗ್ರಾಣದ ಪಟ್ಟಣಗಳನ್ನು ಕಟ್ಟಿಸಿದನು.",
			"13": "<sup>13</sup> ಜುದೇಯದ ಪಟ್ಟಣಗಳಲ್ಲಿ ದವಸಧಾನ್ಯಗಳ ದೊಡ್ಡ ಮಳಿಗೆಯಿತ್ತು. ಜೆರುಸಲೇಮಿನಲ್ಲಿ ರಣವೀರರಾದ ಯೋಧರು ಇದ್ದರು:",
			"14": "<sup>14</sup> ಗೋತ್ರಗಳ ಪ್ರಕಾರ ಲೆಕ್ಕಿತರಾದ ಈ ಸೇನಾನಾಯಕರ ಕ್ರಮಬದ್ಧ ಪಟ್ಟಿಯಿದು; ಅದ್ನ - ಇವನು ಯೆಹೂದ್ಯರಲ್ಲಿ ಮೂರು ಲಕ್ಷಮಂದಿ ರಣವೀರರ ಅಧಿಪತಿ;",
			"15": "<sup>15</sup> ಯೆಹೋಹಾನಾನ್ - ಇವನು ಎರಡು ಲಕ್ಷದ ಎಂಬತ್ತು ಸಾವಿರ ಮಂದಿ ರಣವೀರರ ಅಧಿಪತಿ;",
			"16": "<sup>16</sup> ಅಮಸ್ಯ - ಜಿಕ್ರಿಯ ಮಗನಾದ ಇವನು ಎರಡು ಲಕ್ಷ ಮಂದಿ ರಣವೀರರೊಡನೆ ಸ್ವೇಚ್ಛೆಯಿಂದ ತನ್ನನ್ನೇ ಸರ್ವೇಶ್ವರನಿಗೆ ಪ್ರತಿಷ್ಠಿಸಿಕೊಂಡಿದ್ದನು;",
			"17": "<sup>17</sup> ಎಲ್ಯಾದ - ಇವನು ಬೆನ್ಯಾಮೀನರಲ್ಲಿ ಬಿಲ್ಲುಗುರಾಣಿಗಳನ್ನು ಹಿಡಿದುಕೊಂಡ ಎರಡು ಲಕ್ಷ ಸೈನಿಕರ ನಾಯಕ ಹಾಗೂ ರಣವೀರ;",
			"18": "<sup>18</sup> ಯೆಹೋಜಾಬಾದ್ - ಇವನು ಒಂದು ಲಕ್ಷ ಎಂಬತ್ತು ಸಾವಿರ ಮಂದಿ ಯುದ್ಧಸನ್ನದ್ಧರ ನಾಯಕ.",
			"19": "<sup>19</sup> ಅರಸನಿಗೆ ತನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸೇವೆಮಾಡುತ್ತಿದ್ದ ಈ ಯೋಧರಲ್ಲದೆ ಕೋಟೆಕೊತ್ತಲಗಳಿದ್ದ ಬೇರೆ ಪಟ್ಟಣಗಳಲ್ಲಿಯೂ ಸೈನಿಕರಾಗಿ ಇದ್ದರು."
		},
		"18": {
			"1": "<sup>1</sup> ಯೆಹೋಷಾಫಾಟನಿಗೆ ಧನಘನತೆ ಬಹಳವಾಗಿತ್ತು. ಅವನು ಆಹಾಬನೊಡನೆ ಬೀಗತನಮಾಡಿ ಕೆಲವು ವರ್ಷಗಳಾದ ನಂತರ ಅವನ ಬಳಿಗೆ ಸಮಾರಿಯಕ್ಕೆ ಹೋದನು.",
			"2": "<sup>2</sup> ಆಹಾಬನು ಅವನಿಗೂ ಅವನೊಡನೆ ಬಂದ ಜನರಿಗೂ ಅನೇಕಾನೇಕ ದನಕುರಿಗಳನ್ನು ವಧಿಸಿ ಔತಣವನ್ನೇರ್ಪಡಿಸಿದನು. ರಾಮೋತ್ ಗಿಲ್ಯಾದಿಗೆ ಮುತ್ತಿಗೆ ಹಾಕಲು ತನ್ನ ಸಂಗಡ ಬರಬೇಕೆಂದು ಅವನನ್ನು ಪ್ರೇರಿಸಿದನು.",
			"3": "<sup>3</sup> ಇಸ್ರಯೇಲರ ಅರಸ ಆಹಾಬನು ಯೆಹೂದ್ಯರ ಅರಸ ಯೆಹೋಷಾಫಾಟನನ್ನು, “ನನ್ನ ಜೊತೆಯಲ್ಲಿ ರಾಮೋತ್‍ಗಿಲ್ಯಾದಿಗೆ ಬರುತ್ತೀಯಾ?” ಎಂದು ಕೇಳಿದನು. ಅವನು, “ನಾನೂ ನೀನೂ ನನ್ನ ಜನರೂ ನಿನ್ನ ಜನರೂ ಒಂದೆ ಅಲ್ಲವೇ? ನಿನ್ನ ಜೊತೆಯಲ್ಲಿ ಯುದ್ಧಕ್ಕೆ ಖಂಡಿತ ಬರುತ್ತೇನೆ,” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"4": "<sup>4</sup> ಅನಂತರ, “ಸರ್ವೇಶ್ವರನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಈ ಬಗ್ಗೆ ವಿಚಾರಿಸು,” ಎಂದು ಇಸ್ರಯೇಲರ ಅರಸನನ್ನು ಕೇಳಿಕೊಂಡನು.",
			"5": "<sup>5</sup> ಆಗ ಇಸ್ರಯೇಲರ ಅರಸನು ತನ್ನ ರಾಜ್ಯದಲ್ಲಿದ್ದ ನಾನೂರು ಮಂದಿ ಪ್ರವಾದಿಗಳನ್ನು ಸೇರಿಸಿ, “ನಾವು ರಾಮೋತ್‍ಗಿಲ್ಯಾದಿನ ಮೇಲೆ ಯುದ್ಧಕ್ಕೆ ಹೋಗಬಹುದೋ? ಬಾರದೋ?\", ಎಂದು ಕೇಳಿದನು. ಅವರು, “ಹೋಗಬಹುದು, ದೇವರು ಅದನ್ನು ಅರಸನ ಕೈಗೆ ಒಪ್ಪಿಸುವರು,” ಎಂದರು.",
			"6": "<sup>6</sup> ಆದರೆ ಯೆಹೋಷಾಫಾಟನು ಇಸ್ರಯೇಲರ ಅರಸನನ್ನು, “ಸರ್ವೇಶ್ವರನ ಪ್ರವಾದಿಗಳಲ್ಲಿ ಇವರ ಹೊರತಾಗಿ ನಾವು ವಿಚಾರಿಸಬಹುದಾದ ಬೇರೆ ಪ್ರವಾದಿ ಇಲ್ಲವೇ?” ಎಂದು ಕೇಳಿದನು. ಅವನು,",
			"7": "<sup>7</sup> “ನಮಗಾಗಿ ಸರ್ವೇಶ್ವರನ ಸನ್ನಿಧಿಯಲ್ಲಿ ವಿಚಾರಿಸಬಲ್ಲವನಾದ ಇನ್ನೊಬ್ಬ ಪ್ರವಾದಿ ಇರುತ್ತಾನೆ; ಅವನು ಇಮ್ಲನ ಮಗನಾದ ಮೀಕಾಯೆಹು ಎಂಬವನು. ಆದರೆ ನಾನು ಅವನನ್ನು ದ್ವೇಷಿಸುತ್ತೇನೆ; ಅವನು ನನ್ನನ್ನು ಕುರಿತು ಯಾವಾಗಲೂ ಶುಭವನ್ನಲ್ಲ, ಅಶುಭವನ್ನೇ ಮುಂತಿಳಿಸುತ್ತಾನೆ,” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು. ಅದಕ್ಕೆ ಯೆಹೋಷಾಫಾಟನು, “ಅರಸರು ಹಾಗೆನ್ನಬಾರದು,” ಎಂದನು.",
			"8": "<sup>8</sup> ಆಗ ಇಸ್ರಯೇಲರ ಅರಸನು ಒಬ್ಬ ಕಂಚುಕಿಯನ್ನು ಕರೆದು, “ಬೇಗ ಹೋಗಿ ಇಮ್ಲನ ಮಗ ಮೀಕಾಯೆಹುವನ್ನು ಕರೆದುಕೊಂಡು ಬಾ,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"9": "<sup>9</sup> ಇಸ್ರಯೇಲರ ಅರಸನೂ ಯೆಹೂದ್ಯರ ಅರಸ ಯೆಹೋಷಾಫಾಟನೂ ರಾಜವಸ್ತ್ರಗಳನ್ನು ಧರಿಸಿಕೊಂಡು ಸಮಾರಿಯ ಪಟ್ಟಣದ ಹೆಬ್ಬಾಗಿಲಿನ ಹತ್ತಿರವಿರುವ ಬಯಲಿನಲ್ಲಿ ತಮ್ಮ ತಮ್ಮ ಆಸನಗಳ ಮೇಲೆ ಕುಳಿತುಕೊಂಡರು; ಎಲ್ಲ ಪ್ರವಾದಿಗಳು ಅವರ ಮುಂದೆ ಪರವಶರಾಗಿ ಮಾತಾಡತೊಡಗಿದರು.",
			"10": "<sup>10</sup> ಅವರಲ್ಲಿ ಕೆನಾನನ ಮಗ ಚಿದ್ಕೀಯ ಎಂಬವನು ಕಬ್ಬಿಣದ ಕೊಂಬುಗಳನ್ನು ಮಾಡಿಸಿ ತಲೆಗೆ ಕಟ್ಟಿಕೊಂಡು ಬಂದು, “ಕೊಂಬುಗಳಿಂದಲೋ ಎಂಬಂತೆ ನೀವು ಸಿರಿಯಾದವರನ್ನು ಇರಿದು ಕೊಂದುಹಾಕುವಿರಿ, ಎಂಬುದಾಗಿ ಸರ್ವೇಶ್ವರ ಹೇಳುತ್ತಾರೆ,” ಎಂದನು.",
			"11": "<sup>11</sup> ಉಳಿದ ಪ್ರವಾದಿಗಳೂ ಇದೇ ತರದ ಮಾತುಗಳನ್ನು ನುಡಿದು, “ರಾಮೋತ್‍ಗಿಲ್ಯಾದಿಗೆ ಹೋಗಿ, ನೀವು ಕೃತಾರ್ಥರಾಗಿ ಬರುವಿರಿ; ಸರ್ವೇಶ್ವರ ಅದನ್ನು ಅರಸರ ಕೈಗೆ ಒಪ್ಪಿಸುವರು,” ಎಂದರು.",
			"12": "<sup>12</sup> ಇತ್ತ ಮೀಕಾಯೆಹುವನ್ನು ಕರೆಯಲು ಬಂದಿದ್ದ ದೂತನು, “ನೋಡಿ ಎಲ್ಲ ಪ್ರವಾದಿಗಳು ಏಕ ಮನಸ್ಸಿನಿಂದ ಅರಸರಿಗೆ ಶುಭವನ್ನೇ ಮುಂತಿಳಿಸುತ್ತಿರುತ್ತಾರೆ. ದಯವಿಟ್ಟು ನೀವೂ ಅವರಂತೆ ಶುಭವನ್ನೇ ಮುಂತಿಳಿಸಿ,” ಎಂದು ಹೇಳಿದನು.",
			"13": "<sup>13</sup> ಅದಕ್ಕೆ ಮೀಕಾಯೆಹುವು, “ಸರ್ವೇಶ್ವರನಾಣೆ, ನನ್ನ ದೇವರು ಹೇಳುವುದನ್ನು ಮಾತ್ರ ನುಡಿಯುತ್ತೇನೆ,” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"14": "<sup>14</sup> ಅವನು ಅರಸನ ಬಳಿಗೆ ಬಂದಾಗ ಅರಸನು, “ಮೀಕಾಯೆಹುವೇ, ನಾವು ರಾಮೋತ್‍ಗಿಲ್ಯಾದಿನ ಮೇಲೆ ಯುದ್ಧಕ್ಕೆ ಹೋಗಬಹುದೋ? ಬಾರದೋ?” ಎಂದು ಕೇಳಿದನು. ಅವನು “ಹೋಗಬಹುದು, ಕೃತಾರ್ಥರಾಗಿ ಬರುವಿರಿ; ವೈರಿಗಳು ನಿಮ್ಮ ಕೈವಶವಾಗುವರು,” ಎಂದು ಹೇಳಿದನು.",
			"15": "<sup>15</sup> ಆಗ ಅರಸನು ಅವನಿಗೆ, “ಸತ್ಯವನ್ನೇ ತಿಳಿಸಬೇಕೆಂದು ಸರ್ವೇಶ್ವರನ ಹೆಸರಿನಲ್ಲಿ ನಾನು ನಿನ್ನಿಂದ ಎಷ್ಟು ಸಾರಿ ಪ್ರಮಾಣ ಮಾಡಿಸಬೇಕು?” ಎಂದನು.",
			"16": "<sup>16</sup> ಆಗ ಅವನು, “ಇಸ್ರಯೇಲರೆಲ್ಲರೂ ಕುರುಬನಿಲ್ಲದ ಕುರಿಗಳಂತೆ ಬೆಟ್ಟಗಳಲ್ಲಿ ಚದರಿಹೋದದ್ದನ್ನು ಕಂಡೆ; ಆಗ ಸರ್ವೇಶ್ವರ ಇವರು ಒಡೆಯನಿಲ್ಲದವರಾಗಿ ಇರುತ್ತಾರೆ; ಸಮಾಧಾನದಿಂದ ತಮ್ಮ ತಮ್ಮ ಮನೆಯೊಳಗೆ ಹೋಗಲಿ ಎಂದರು,” ಎಂಬುದಾಗಿ ಉತ್ತರ ಕೊಟ್ಟನು.",
			"17": "<sup>17</sup> ಆಗ ಇಸ್ರಯೇಲರ ಅರಸು ಯೆಹೋಷಾಫಾಟನಿಗೆ, “ಇವನು ನನಗೆ ಯಾವಾಗಲೂ ಶುಭವನ್ನಲ್ಲ, ಅಶುಭವನ್ನೇ ಮುಂತಿಳಿಸುತ್ತಾನೆಂದು ನಾನು ನಿನಗೆ ಹೇಳಲಿಲ್ಲವೇ?” ಎಂದನು.",
			"18": "<sup>18</sup> ಅದಕ್ಕೆ ಮೀಕಾಯೆಹುವು, “ಅದಿರಲಿ, ಸರ್ವೇಶ್ವರನ ವಾಕ್ಯವನ್ನು ಕೇಳಿರಿ; ಸರ್ವೇಶ್ವರ ತಮ್ಮ ಸಿಂಹಾಸನದ ಮೇಲೆ ಕುಳಿತುಕೊಂಡದ್ದನ್ನೂ ಪರಲೋಕಸೈನ್ಯಗಳು ಅವರ ಎಡಬಲಗಡೆಗಳಲ್ಲಿ ನಿಂತದ್ದನ್ನೂ ಕಂಡೆ.",
			"19": "<sup>19</sup> ಸರ್ವೇಶ್ವರ ತಮ್ಮ ಹತ್ತಿರ ನಿಂತವರನ್ನು, ‘ಇಸ್ರಯೇಲರ ಅರಸ ಅಹಾಬನು ಹತನಾಗಿ ಬೀಳುವಂತೆ ಅವನನ್ನು ರಾಮೋತ್‍ಗಿಲ್ಯಾದಿನ ಯುದ್ಧಕ್ಕೆ ಯಾರು ಪ್ರೇರೇಪಿಸಬಲ್ಲಿರಿ?’ ಎಂದು ಕೇಳಿದಾಗ ಒಬ್ಬನು ಒಂದು ವಿಧವಾಗಿಯೂ ಇನ್ನೊಬ್ಬನು ಇನ್ನೊಂದು ವಿಧವಾಗಿಯೂ ಉತ್ತರಕೊಟ್ಟರು.",
			"20": "<sup>20</sup> ಕಡೆಗೆ ಒಂದು ಆತ್ಮ ಸರ್ವೇಶ್ವರನ ಮುಂದೆ ಬಂದು ನಿಂತು, ‘ನಾನು ಹೋಗಿ ಅವನನ್ನು ಪ್ರೇರೇಪಿಸುವೆನು’ ಎಂದಿತು. ‘ಹೇಗೆ ಪ್ರೇರೇಪಿಸುವೆ?’ ಎಂದು ಸರ್ವೇಶ್ವರ ಕೇಳಿದರು. ಅದು,",
			"21": "<sup>21</sup> ‘ನಾನು ಅಸತ್ಯವನ್ನಾಡುವ ಆತ್ಮವಾಗಿ ಅವನ ಎಲ್ಲ ಪ್ರವಾದಿಗಳಲ್ಲಿ ಸೇರುವೆನು’, ಎಂದು ಉತ್ತರಕೊಟ್ಟಿತು. ಆಗ ಸರ್ವೇಶ್ವರ ಅದಕ್ಕೆ, ‘ಹೋಗಿ ಅದರಂತೆಯೇ ಮಾಡು; ಅವನನ್ನು ಪ್ರೇರೇಪಿಸಿ ಸಫಲನಾಗುವೆ’, ಎಂದರು.",
			"22": "<sup>22</sup> ನೋಡು, ಸರ್ವೇಶ್ವರ ನಿನ್ನ ವಿಷಯದಲ್ಲಿ ಕೇಡು ನುಡಿದು, ನಿನ್ನ ಪ್ರವಾದಿಗಳಲ್ಲಿ ಅಸತ್ಯವನ್ನಾಡುವ ಆತ್ಮವನ್ನೇ ಕಳುಹಿಸಿದ್ದಾರೆ,” ಎಂದು ಹೇಳಿದನು.",
			"23": "<sup>23</sup> ಆಗ ಕೆನಾನನ ಮಗ ಚಿದ್ಕೀಯನು ಮೀಕಾಯೆಹುವಿನ ಬಳಿಗೆ ಬಂದು ಅವನ ಕೆನ್ನೆಗೆ ಒಂದು ಪೆಟ್ಟುಕೊಟ್ಟು, “ಸರ್ವೇಶ್ವರನ ಆತ್ಮ ನನ್ನನ್ನು ಬಿಟ್ಟು ನಿನ್ನೊಂದಿಗೆ ಮಾತಾಡುವುದಕ್ಕೆ ಯಾವ ಮಾರ್ಗವಾಗಿ ಬಂದಿತು?” ಎಂದನು. ಮೀಕಾಯೆಹುವು,",
			"24": "<sup>24</sup> “ನೀನು ಅಡಗಿಕೊಳ್ಳುವುದಕ್ಕೆ ಒಳಗಿನ ಕೋಣೆಗೆ ಹೋಗುವ ದಿನದಲ್ಲಿ ಅದು ನಿನಗೆ ಗೊತ್ತಾಗುವುದು,” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"25": "<sup>25</sup> ಆಗ ಇಸ್ರಯೇಲರ ಅರಸನು ಸೇವಕರಿಗೆ, “ಮೀಕಾಯೆಹುವನ್ನು ಹಿಡಿದುಕೊಂಡು ಹೋಗಿ ಪುರಾಧಿಕಾರಿಯಾದ ಆಮೋನನಿಗೂ ರಾಜಪುತ್ರನಾದ ಯೋವಾಷನಿಗೂ ಒಪ್ಪಿಸಿರಿ; ಅವರಿಗೆ,",
			"26": "<sup>26</sup> ‘ನಾನು, ಸುರಕ್ಷಿತನಾಗಿ ಹಿಂದಿರುಗುವವರೆಗೂ ಇವನನ್ನು ಸೆರೆಯಲ್ಲಿಟ್ಟು ಸೆರೆಮನೆಯ ಅನ್ನಪಾನಗಳನ್ನೇ ಕೊಟ್ಟು ಕುಗ್ಗಿಸಬೇಕೆಂದು ಅರಸರು ಹೇಳಿದ್ದಾರೆ’ ಎಂದು ತಿಳಿಸಿರಿ,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"27": "<sup>27</sup> ಮೀಕಾಯೆಹುವು ಅರಸನಿಗೆ, “ನೀನು ಸುರಕ್ಷಿತವಾಗಿ ಬರುವುದಾದರೆ, ನಾನು ನುಡಿದದ್ದು ಸರ್ವೇಶ್ವರನ ಮಾತಲ್ಲ,” ಎಂದು ಹೇಳಿ, “ಮಹಾಜನರೇ, ನನ್ನ ಮಾತನ್ನು ಗಮನದಲ್ಲಿಡಿ,” ಎಂದು ಕೂಗಿದನು.",
			"28": "<sup>28</sup> ಇಸ್ರಯೇಲರ ಅರಸನು ಹಾಗೂ ಯೆಹೂದ್ಯರ ಅರಸ ಯೆಹೋಷಾಫಾಟನೂ ರಾಮೋತ್‍ಗಿಲ್ಯಾದಿಗೆ ವಿರುದ್ಧ ಯುದ್ಧಕ್ಕೆ ಹೊರಟರು.",
			"29": "<sup>29</sup> ಇಸ್ರಯೇಲರ ಅರಸನು ಯೆಹೋಷಾಫಾಟನಿಗೆ, ‘ನಾನು ವೇಷಹಾಕಿಕೊಂಡು ರಣರಂಗಕ್ಕೆ ಬರುತ್ತೇನೆ; ನೀನಾದರೋ ನಿನ್ನ ರಾಜವಸ್ತ್ರಗಳನ್ನು ಧರಿಸಿಕೊಂಡೇ ಬಾ,” ಎಂದು ಹೇಳಿ ವೇಷಹಾಕಿಕೊಂಡನು; ತರುವಾಯ ರಣರಂಗಕ್ಕೆ ಹೋದರು.",
			"30": "<sup>30</sup> ಸಿರಿಯಾದವರ ಅರಸನು ತನ್ನ ರಥಬಲದ ಅಧಿಪತಿಗಳಿಗೆ, “ನೀವು ಶತ್ರುಗಳ ಸಾಧಾರಣಸೈನಿಕರನ್ನೂ ಅಧಿಪತಿಗಳನ್ನೂ ಬಿಟ್ಟು ಇಸ್ರಯೇಲರ ಅರಸನಿಗೇ ಗುರಿಯಿಡಿ\", ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"31": "<sup>31</sup> ರಥಬಲದ ಅಧಿಪತಿಗಳು ಯೆಹೋಷಾಫಾಟನನ್ನು ಕಂಡಾಗ ಅವನೇ ಇಸ್ರಯೇಲರ ಅರಸನೆಂದು ನೆನಸಿ, ಅವನಿಗೆ ವಿರುದ್ಧ ಯುದ್ಧಮಾಡುವುದಕ್ಕೆ ಅವನನ್ನು ಸುತ್ತಿಕೊಂಡರು. ಯೆಹೋಷಾಫಾಟನು ಕೂಗಿಕೊಂಡನು. ಆಗ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಅವನಿಗೆ ನೆರವಾಗಿ, ಶತ್ರುಗಳನ್ನು ಅವನ ಕಡೆಯಿಂದ ತೊಲಗಿಸಿದರು.",
			"32": "<sup>32</sup> ಅವನು ಇಸ್ರಯೇಲರ ಅರಸನಲ್ಲವೆಂಬುದು ರಥಬಲದ ಅಧಿಪತಿಗಳಿಗೆ ಗೊತ್ತಾದಾಗ ಅವರು ಅವನನ್ನು ಬಿಟ್ಟು ಹಿಂದಿರುಗಿದರು.",
			"33": "<sup>33</sup> ಸಿರಿಯಾದವರ ಒಬ್ಬ ಸೈನಿಕನು ಯಾರಿಗೂ ಗುರಿಯಿಡದೆ ಬಾಣವನ್ನೆಸೆದನು. ಆ ಬಾಣ ಇಸ್ರಯೇಲರ ಅರಸನಿಗೆ, ಅವನ ಕವಚದ ಸಂದಿನಲ್ಲಿ ತಾಕಿತು. ಆಗ ಅವನು ತನ್ನ ಸಾರಥಿಗೆ, “ರಥವನ್ನು ತಿರುಗಿಸಿ ನನ್ನನ್ನು ರಣರಂಗದಿಂದ ಆಚೆಗೆ ತೆಗೆದುಕೊಂಡು ಹೋಗು; ನನಗೆ ದೊಡ್ಡ ಗಾಯವಾಗಿ ಇದೆ,” ಎಂದು ಹೇಳಿದನು.",
			"34": "<sup>34</sup> ಆ ದಿವಸ ಯುದ್ಧ ಬಹುಘೋರವಾಗಿತ್ತು. ಆದ್ದರಿಂದ ಇಸ್ರಯೇಲರ ಅರಸನು ಸಾಯಂಕಾಲದವರೆಗೂ ಸಿರಿಯಾದವರ ಎದುರಿಗೆ ತನ್ನ ರಥದಲ್ಲೇ ಆತುಕೊಂಡಿರಬೇಕಾಯಿತು.ಸೂರ್ಯಾಸ್ತಮಾನವಾದಾಗ ಅವನು ಸತ್ತನು."
		},
		"19": {
			"1": "<sup>1</sup> ಜುದೇಯದ ಅರಸ ಯೆಹೋಷಾಫಾಟನು ಸುರಕ್ಷಿತವಾಗಿ ತನ್ನ ಮನೆಗೆ ಹಿಂದಿರುಗಿದನು.",
			"2": "<sup>2</sup> ಅವನು ಜೆರುಸಲೇಮಿಗೆ ಬಂದಾಗ ಹನಾನೀಯನ ಮಗ ಯೇಹೂ ಎಂಬ ದರ್ಶಿಯು ಅವನನ್ನು ಎದುರುಗೊಂಡು, “ನೀವು ಕೆಟ್ಟವನಿಗೆ ಸಹಾಯಮಾಡಬಹುದೇ? ಸರ್ವೇಶ್ವರನ ಹಗೆಗಾರರನ್ನು ಪ್ರೀತಿಸಬಹುದೇ? ನೀವು ಹೀಗೆ ಮಾಡಿದ್ದರಿಂದ ಸರ್ವೇಶ್ವರನ ಕೋಪ ನಿಮ್ಮ ಮೇಲಿರುತ್ತದೆ.",
			"3": "<sup>3</sup> ಆದರೂ ನೀವು ನಾಡಿನಿಂದ ಅಶೇರ ವಿಗ್ರಹಸ್ತಂಭಗಳನ್ನು ತೆಗೆದುಹಾಕಿದ್ದೀರಿ. ಸರ್ವೇಶ್ವರನ ಭಕ್ತಿ ನಿಮ್ಮಲ್ಲಿ ಬೇರೂರಿದೆ, ನಿಮ್ಮಲ್ಲಿ ಸುಶೀಲತೆ ಉಂಟೆಂದು ತಿಳಿದುಬಂದಿದೆ,” ಎಂಬುದಾಗಿ ಹೇಳಿದನು.",
			"4": "<sup>4</sup> ಯೆಹೋಷಾಫಾಟನು ಜೆರುಸಲೇಮಿನಲ್ಲಿ ವಾಸವಾಗಿದ್ದು, ಇನ್ನೊಮ್ಮೆ ಬೇರ್ಷೆಬದಿಂದ ಎಫ್ರಯಿಮ್ ಪರ್ವತದವರೆಗೂ ಸಂಚರಿಸಿ, ತನ್ನ ಪ್ರಜೆಗಳನ್ನು ಅವರ ಪೂರ್ವಜರ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಕಡೆಗೆ ತಿರುಗಿಸಿದನು.",
			"5": "<sup>5</sup> ಜುದೇಯ ನಾಡಿನ ಪ್ರತಿಯೊಂದು ಕೋಟೆಕೊತ್ತಲಗಳುಳ್ಳ ಪಟ್ಟಣದಲ್ಲಿ ನ್ಯಾಯಾಧಿಪತಿಗಳನ್ನು ನೇಮಿಸಿದನು.",
			"6": "<sup>6</sup> ಅವರಿಗೆ, “ನೀವು ಹೇಗೆ ಕಾರ್ಯನಿರ್ವಹಿಸುತ್ತೀರಿ ಎಂಬುದರ ಬಗ್ಗೆ, ಎಚ್ಚರಿಕೆ! ನೀವು ನ್ಯಾಯ ನಿರ್ಣಯಿಸುವುದು ಮನುಷ್ಯರಿಗಾಗಿ ಅಲ್ಲ; ಸರ್ವೇಶ್ವರನಿಗಾಗಿ, ನ್ಯಾಯವಿಚಾರಣೆ ನಡೆಯುವಾಗ ಸರ್ವೇಶ್ವರ ನಿಮ್ಮ ಮಧ್ಯೆ ಇರುತ್ತಾರೆ.",
			"7": "<sup>7</sup> ಹೀಗಿರುವಲ್ಲಿ, ನಿಮಗೆ ಅವರ ಭಯಭಕ್ತಿಯಿರಲಿ. ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಲ್ಲಿ ಅನ್ಯಾಯ, ಮುಖದಾಕ್ಷಿಣ್ಯ ಹಾಗು ಲಂಚಕೋರತನ ಇಲ್ಲ. ಆದುದರಿಂದ ಜಾಗರೂಕತೆಯಿಂದ ಕೆಲಸಮಾಡಿ,” ಎಂದು ಎಚ್ಚರಿಸಿದನು.",
			"8": "<sup>8</sup> ಇದಲ್ಲದೆ ಯೆಹೋಷಾಫಾಟನು ತನ್ನ ಪರಿವಾರದೊಡನೆ ಜೆರುಸಲೇಮಿಗೆ ಹಿಂದಿರುಗಿ ಬಂದು, ಲೇವಿಯರಿಂದ, ಯಾಜಕರ ಹಾಗು ಇಸ್ರಯೇಲ್ ಗೋತ್ರಪ್ರಧಾನರಿಂದ ಕೆಲವರನ್ನು ಆರಿಸಿ, ಸರ್ವೇಶ್ವರನ ಧರ್ಮಶಾಸ್ತ್ರಕ್ಕೆ ಸಂಬಂಧಪಟ್ಟ ವ್ಯಾಜ್ಯಗಳನ್ನೂ ಬೇರೆ ವ್ಯಾಜ್ಯಗಳನ್ನೂ ವಿಚಾರಿಸಿ ತೀರ್ಪುನೀಡಲು ಜೆರುಸಲೇಮಿನಲ್ಲೇ ಇರಿಸಿದನು. ಅವರಿಗೆ,",
			"9": "<sup>9</sup> “ನೀವು ಸರ್ವೇಶ್ವರನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿ ನಂಬಿಕೆಯಿಂದಲೂ ಯಥಾರ್ಥ ಮನಸ್ಸಿನಿಂದಲೂ ಕಾರ್ಯನಿರ್ವಹಿಸತಕ್ಕದ್ದು.",
			"10": "<sup>10</sup> ಜುದೇಯದ ಪಟ್ಟಣಗಳಲ್ಲಿ ವಾಸಿಸುವ ನಿಮ್ಮ ಸಹೋದರರಲ್ಲಿ ಜೀವಹತ್ಯ ಸಂಬಂಧದಲ್ಲಿಯಾಗಲಿ ಧರ್ಮಶಾಸ್ತ್ರದ ಆಯಾ ಆಜ್ಞಾವಿಧಿನ್ಯಾಯಗಳ ಸಂಬಂಧದಲ್ಲಿಯಾಗಲಿ ವ್ಯಾಜ್ಯವುಂಟಾಗಿ, ಅದು ನಿಮ್ಮ ಮುಂದೆ ವಿಚಾರಣೆಗೆ ಬಂದರೆ, ನಿಮ್ಮ ಸಹೋದರರು ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ಅಪರಾಧಿಗಳಾಗದಂತೆ ಹಾಗೂ ನೀವು ಅವರೊಂದಿಗೆ ದೇವಕೋಪಕ್ಕೆ ಗುರಿಯಾಗದಂತೆ ಅವರನ್ನು ಎಚ್ಚರಿಸಿರಿ. ಹೀಗೆ ಮಾಡುವುದಾದರೆ ನೀವು ನಿರ್ದೋಷಿಗಳಾಗುವಿರಿ.",
			"11": "<sup>11</sup> ಇಗೋ, ಸರ್ವೇಶ್ವರನ ಕಾರ್ಯಸಂಬಂಧವಾದ ಎಲ್ಲ ವಿಚಾರಣೆಗಳಿಗೂ ಮಹಾಯಾಜಕ ಅಮರ್ಯನು ನಿಮ್ಮ ಅಧ್ಯಕ್ಷನು; ರಾಜಕೀಯ ಕಾರ್ಯಗಳ ಸಂಬಂಧವಾದ ಎಲ್ಲ ವಿಚಾರಣೆಗಳಿಗೂ ಇಷ್ಮಾಯೇಲನ ಮಗನೂ ಯೆಹೂದ ಕುಲದ ಪ್ರಮುಖನೂ ಆದ ಜೆಬದ್ಯನು ಅಧ್ಯಕ್ಷನು; ಲೇವಿಯರು ನ್ಯಾಯಸ್ಥಾನದ ಲೇಖಕರು. ಧೈರ್ಯದಿಂದ ಕೆಲಸಮಾಡಿ. ಸರ್ವೇಶ್ವರಸ್ವಾಮಿ ಸಜ್ಜನರ ಸಹಾಯಕರು,” ಎಂದು ಹೇಳಿದನು."
		},
		"20": {
			"1": "<sup>1</sup> ಸ್ವಲ್ಪಕಾಲವಾದ ಮೇಲೆ ಮೋವಾಬ್ಯರು, ಅಮ್ಮೋನಿಯರು ಹಾಗು ಮೆಗೂನ್ಯರಲ್ಲಿ ಕೆಲವರು ಯೆಹೋಷಾಫಾಟನಿಗೆ ವಿರುದ್ಧ ಯುದ್ಧಕ್ಕೆ ಬಂದರು.",
			"2": "<sup>2</sup> ದೂತರು ಬಂದು ಯೆಹೋಷಾಫಾಟನಿಗೆ, “ಮಹಾಸೈನ್ಯ ಸಮೂಹವೊಂದು ನಿನಗೆ ವಿರೋಧವಾಗಿ ಲವಣಸಮುದ್ರದ ಆಚೆಯಲ್ಲಿರುವ ಆರಾಮ್ ಪ್ರಾಂತ್ಯದ ಕಡೆಯಿಂದ ಬಂದು ಈಗ ಏಂಗೆದಿಯೆನಿಸಿಕೊಳ್ಳುವ ಹಚೆಚೋನ್‍ತಾಮಾರಿನಲ್ಲಿ ಇರುತ್ತಾರೆ,” ಎಂದು ತಿಳಿಸಿದರು.",
			"3": "<sup>3</sup> ಯೆಹೋಷಾಫಾಟನು ಇದನ್ನು ಕೇಳಿ ಭಯಪಟ್ಟು, ಸರ್ವೇಶ್ವರನನ್ನೇ ಆಶ್ರಯಿಸಿಕೊಳ್ಳಬೇಕೆಂದು ನಿರ್ಣಯಿಸಿಕೊಂಡನು. ಯೆಹೂದ್ಯರೆಲ್ಲರೂ ಉಪವಾಸಮಾಡಬೇಕೆಂದು ಪ್ರಕಟಿಸಿದನು.",
			"4": "<sup>4</sup> ಆಗ ಯೆಹೂದ್ಯರು ಸರ್ವೇಶ್ವರನ ಸಹಾಯವನ್ನು ಕೇಳಿಕೊಳ್ಳುವುದಕ್ಕಾಗಿ ತಮ್ಮ ಎಲ್ಲಾ ಪಟ್ಟಣಗಳಿಂದ ಆತನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಕೂಡಿಬಂದರು.",
			"5": "<sup>5</sup> ಯೆಹೂದ್ಯರೂ ಜೆರುಸಲೇಮಿನವರೂ ಸರ್ವೇಶ್ವರನ ಆಲಯದ ಹೊಸ ಪ್ರಾಕಾರದಲ್ಲಿ ಸಭೆಯಾಗಿ ನೆರೆದುಬಂದಾಗ, ಯೆಹೋಷಾಫಾಟನು ಅದರ ಮುಂದೆ ನಿಂತು,",
			"6": "<sup>6</sup> “ಸರ್ವೇಶ್ವರಾ, ನಮ್ಮ ಪಿತೃಗಳ ದೇವರೇ, ಪರಲೋಕದಲ್ಲಿ ದೇವರಾಗಿ ಇರುವವರು ನೀವಲ್ಲವೇ? ನೀವು ಜನಾಂಗಗಳ ಎಲ್ಲ ರಾಜ್ಯಗಳನ್ನೂ ಆಳುತ್ತಿದ್ದೀರಿ. ನಿಮ್ಮ ಹಸ್ತದಲ್ಲಿ ಬಲಪರಾಕ್ರಮಗಳಿವೆ; ನಿಮ್ಮೆದುರಿನಲ್ಲಿ ಯಾರೂ ನಿಲ್ಲಲಾರರು.",
			"7": "<sup>7</sup> ನಮ್ಮ ದೇವರಾದ ನೀವು ನಿಮ್ಮ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಯೇಲರ ಎದುರಿನಿಂದ ಈ ನಾಡಿನ ನಿವಾಸಿಗಳನ್ನು ಹೊರದೂಡಿಸಿ, ಇದನ್ನು ಮಿತ್ರ ಅಬ್ರಹಾಮನ ಸಂತಾನದವರಿಗೆ ಶಾಶ್ವತ ಸೊತ್ತಾಗಿ ಕೊಟ್ಟಿರಲ್ಲವೆ?",
			"8": "<sup>8</sup> ಅವರು ಈ ನಾಡಿನಲ್ಲಿ ನೆಲೆಗೊಂಡು ಇದರಲ್ಲಿ ನಿಮ್ಮ ಹೆಸರಿಗಾಗಿ ಒಂದು ಪವಿತ್ರಾಲಯವನ್ನು ಕಟ್ಟಿದರು.",
			"9": "<sup>9</sup> ತಮ್ಮ ಮೇಲೆ ಖಡ್ಗವಿಧಿ, ಘೋರವ್ಯಾಧಿ, ಕ್ಷಾಮಡಾಮರ, ಮೊದಲಾದ ಆಪತ್ತುಗಳು ಬರುವಾಗ, ನಿಮ್ಮ ನಾಮಮಹಿಮೆ ಇರುವ ಈ ಆಲಯದ ಮುಂದೆಯೂ ನಿಮ್ಮ ಮುಂದೆಯೂ ನಿಂತು, ತಮ್ಮ ಇಕ್ಕಟ್ಟಿನಲ್ಲಿ ನಿಮಗೆ ಮೊರೆ ಇಡುವುದಾದರೆ ನೀವು ಆಲಿಸಿ ರಕ್ಷಿಸುವಿರಿ ಎಂದುಕೊಂಡರಷ್ಟೆ.",
			"10": "<sup>10</sup> ಈ ಅಮ್ಮೋನಿಯರನ್ನು, ಮೋವಾಬ್ಯರನ್ನು ಹಾಗೂ ಸೇಯಿರ ಪರ್ವತಪ್ರದೇಶದವರನ್ನು ನೋಡಿರಿ; ಇಸ್ರಯೇಲರು ಈಜಿಪ್ಟ್ ದೇಶದಿಂದ ಬರುತ್ತಿದ್ದಾಗ, ಇವರ ದೇಶದಲ್ಲಿ ನುಗ್ಗಬಾರದೆಂದು ನಿಮ್ಮಿಂದ ಅಪ್ಪಣೆ ಹೊಂದಿ, ಇವರನ್ನು ಸಂಹರಿಸದೆ ಬೇರೆ ಮಾರ್ಗವಾಗಿ ಹೋದರಲ್ಲವೆ?”",
			"11": "<sup>11</sup> ಈಗ ಅವರು ಉಪಕಾರಕ್ಕೆ ಅಪಕಾರಮಾಡಿ, ನೀವು ನಮಗೆ ಅನುಗ್ರಹಿಸಿದ ಸೊತ್ತಿನಿಂದ ನಮ್ಮನ್ನು ಹೊರತಳ್ಳುವುದಕ್ಕಾಗಿ, ನಮ್ಮ ಮೇಲೆ ಯುದ್ಧಕ್ಕೆ ಬಂದಿರುತ್ತಾರೆ.",
			"12": "<sup>12</sup> ನಮ್ಮ ದೇವರೇ, ಅವರನ್ನು ದಂಡಿಸದೆ ಬಿಡುವಿರೋ? ನಮ್ಮ ಮೇಲೆ ಬಂದಿರುವ ಈ ಮಹಾಸಮೂಹದ ಮುಂದೆ ನಿಲ್ಲುವುದಕ್ಕೆ ನಮ್ಮಲ್ಲಿ ಬಲವಿಲ್ಲ; ಏನು ಮಾಡಬೇಕೆಂದೂ ತಿಳಿಯದು; ನಮ್ಮ ಕಣ್ಣುಗಳು ನಿಮ್ಮನ್ನೇ ನೋಡುತ್ತಿವೆ,” ಎಂದು ಪ್ರಾರ್ಥಿಸಿದನು.",
			"13": "<sup>13</sup> ಎಲ್ಲ ಯೆಹೂದ್ಯರೂ ಅವರ ಮಡದಿಮಕ್ಕಳೂ ಸರ್ವೇಶ್ವರನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನಿಂತಿದ್ದರು.",
			"14": "<sup>14</sup> ಆಗ ಆ ಸಮೂಹದೊಳಗೆ ಆಸಾಫನ ವಂಶದವನೂ ಲೇವಿಯನೂ ಆದ ಯಹಜೀಯೇಲನ ಮೇಲೆ ಸರ್ವೇಶ್ವರನ ಆತ್ಮ ಇಳಿದು ಬಂದಿತು. ಇವನು ಮತ್ತನ್ಯನಿಗೆ ಹುಟ್ಟಿದ ಯೆಗೀಯೇಲನ ಮರಿಮಗ, ಬೆನಾಯನ ಮೊಮ್ಮಗ, ಹಾಗೂ ಜೆಕರ್ಯನ ಮಗ.",
			"15": "<sup>15</sup> ಅವನು , “ಜುದೇಯದ ಹಾಗು ಜೆರುಸಲೇಮಿನ ಎಲ್ಲ ಜನರೇ, ಅರಸ ಯೆಹೋಷಾಫಾಟನೇ, ಸರ್ವೇಶ್ವರ ಸ್ವಾಮಿ ಹೇಳುವುದನ್ನು ಕೇಳಿರಿ: ಈ ಮಹಾಸೈನ್ಯಸಮೂಹದ ನಿಮಿತ್ತ ಕಳವಳಗೊಳ್ಳಬೇಡಿ; ಹೆದರಬೇಡಿ. ಈ ಯುದ್ಧ ನಿಮ್ಮದಲ್ಲ, ದೇವರದೇ.",
			"16": "<sup>16</sup> ನಾಳೆ ಬೆಳಿಗ್ಗೆ ಅವರಿಗೆ ವಿರೋಧವಾಗಿ ಹೊರಡಿರಿ; ಇಗೋ ಅವರು ಹಚ್ಚೀಚ್‍ಗಟ್ಟದ ಮಾರ್ಗವಾಗಿ ಬರುತ್ತಾರೆ. ಯೆರುವೇಲ್ ಮರುಭೂಮಿಯ ಮುಂದಿರುವ ಕಣಿವೆಯ ತುದಿಯಲ್ಲಿ ಅವರನ್ನು ಸಂಧಿಸುವಿರಿ.",
			"17": "<sup>17</sup> ಈ ಸಾರಿ ನೀವು ಯುದ್ಧಮಾಡುವುದು ಅವಶ್ಯವಿಲ್ಲ. ಯೆಹೂದ್ಯರೇ, ಜೆರುಸಲೇಮಿನವರೇ, ಸುಮ್ಮನೆ ನಿಂತುಕೊಂಡು ಸರ್ವೇಶ್ವರ ನಿಮಗಾಗಿ ನಡೆಸುವ ರಕ್ಷಣಾಕಾರ್ಯವನ್ನು ನೋಡಿರಿ; ಹೆದರಬೇಡಿ, ಕಳವಳಗೊಳ್ಳಬೇಡಿ. ನಾಳೆ ಅವರೆದುರಿಗೆ ಹೊರಡಿರಿ; ಸರ್ವೇಶ್ವರ ನಿಮ್ಮೊಂದಿಗೆ ಇರುವರು!” ಎಂದು ಹೇಳಿದನು.",
			"18": "<sup>18</sup> ಆಗ ಯೆಹೋಷಾಫಾಟನು ನೆಲದ ಮಟ್ಟಿಗೂ ತಲೆಬಾಗಿದನು. ಎಲ್ಲಾ ಯೆಹೂದ್ಯರೂ ಜೆರುಸಲೇಮಿನವರೂ ಸರ್ವೇಶ್ವರನ ಮುಂದೆ ಅಡ್ಡಬಿದ್ದು ನಮಸ್ಕರಿಸಿದರು.",
			"19": "<sup>19</sup> ಆಮೇಲೆ ಲೇವಿಯರಲ್ಲಿ ಕೆಹಾತ್ಯರೂ ಕೋರಹಿಯರೂ ಎದ್ದು, ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರನನ್ನು ಮಹಾಸ್ವರದಿಂದ ಕೀರ್ತಿಸಿದರು.",
			"20": "<sup>20</sup> ಅವರು ಮರುದಿನ ಬೆಳಿಗ್ಗೆ ಎದ್ದು ತೆಕೋವ ಮರುಭೂಮಿಗೆ ಹೊರಟರು. ಹೊರಡುವಾಗ ಯೆಹೋಷಾಫಾಟನು ಎದ್ದು ನಿಂತು, “ಯೆಹೂದ್ಯರೇ, ಜೆರುಸಲೇಮಿನವರೇ, ನನ್ನ ಮಾತನ್ನು ಕೇಳಿರಿ; ಸರ್ವೇಶ್ವರನಲ್ಲಿ ಭರವಸೆ ಇಡಿ, ಆಗ ಸುರಕ್ಷಿತರಾಗುವಿರಿ; ಸರ್ವೇಶ್ವರನ ಪ್ರವಾದಿಗಳನ್ನು ನಂಬಿರಿ, ಆಗ ಸಾರ್ಥಕರಾಗುವಿರಿ,” ಎಂದು ಹೇಳಿದನು.",
			"21": "<sup>21</sup> ಆಮೇಲೆ ಜನರ ಸಮ್ಮತಿಯಿಂದ ಸರ್ವೇಶ್ವರನಿಗಾಗಿ ಗಾಯನಮಾಡುವುದಕ್ಕಾಗಿ ಕೆಲವರನ್ನು ಆರಿಸಿಕೊಂಡನು. ಅವರಿಗೆ, “ಪರಿಶುದ್ಧತ್ವವೆಂಬ ಭೂಷಣದೊಡನೆ ಯೋಧರ ಮುಂದೆ ಹೋಗುತ್ತಾ, “ಸರ್ವೇಶ್ವರನಿಗೆ ಕೃತಜ್ಞತಾ ಸ್ತುತಿಮಾಡಿರಿ, ಅವರ ಅಚಲಪ್ರೀತಿ ಶಾಶ್ವತ’ ಎಂದು ಭಜಿಸಿರಿ,” ಎಂಬುದಾಗಿ ಆಜ್ಞಾಪಿಸಿದನು.",
			"22": "<sup>22</sup> ಅವರು ಉತ್ಸಾಹಧ್ವನಿಯಿಂದ ಕೀರ್ತಿಸುವುದಕ್ಕೆ ಪ್ರಾರಂಭಿಸಿದರು. ಆಗ ಸರ್ವೇಶ್ವರ ಯೆಹೂದ್ಯರಿಗೆ ವಿರುದ್ಧ ಬಂದ ಅಮ್ಮೋನಿಯರನ್ನೂ ಮೋವಾಬ್ಯರನ್ನೂ ಸೇಯೀರ್‍ಪರ್ವತದವರನ್ನೂ ನಶಿಸುವುದಕ್ಕಾಗಿ ಅವರಲ್ಲಿ ಹೊಂಚುಹಾಕುವವರನ್ನು ಇರಿಸಿದ್ದರು. ಅಮ್ಮೋನಿಯರೂ ಮೋವಾಬ್ಯರೂ ಸೇಯೀರ್‍ಪರ್ವತದವರ ಮೇಲೆ ಬಿದ್ದು ಅವರನ್ನು ಪೂರ್ಣವಾಗಿ ಸಂಹರಿಸಿಬಿಟ್ಟರು.",
			"23": "<sup>23</sup> ಅವರನ್ನು ಮುಗಿಸಿಬಿಟ್ಟ ಮೇಲೆ ತಾವೇ ಒಬ್ಬರನ್ನೊಬ್ಬರು ಕೊಲ್ಲುವುದಕ್ಕೆ ಪ್ರಾರಂಭಿಸಿದರು.",
			"24": "<sup>24</sup> ಯೆಹೂದ್ಯರು ಮರುಭೂಮಿಯ ಬುರುಜಿಗೆ ಬಂದು ಆ ಸೈನ್ಯಸಮೂಹವಿದ್ದ ಕಡೆಗೆ ನೋಡಿದಾಗ ನೆಲದ ಮೇಲೆ ಬಿದ್ದಿರುವ ಹೆಣಗಳು ಹೊರತಾಗಿ ಜೀವದಿಂದುಳಿದವರು ಯಾರೂ ಕಾಣಿಸಲಿಲ್ಲ.",
			"25": "<sup>25</sup> ಯೆಹೋಷಾಫಾಟನೂ ಅವನ ಜನರೂ ಸುಲಿಗೆಮಾಡುವುದಕ್ಕಾಗಿ ಅಲ್ಲಿಗೆ ಹೋದರು. ಅವರಿಗೆ ದ್ರವ್ಯ, ವಸ್ತ್ರ, ಶ್ರೇಷ್ಠಾಯುಧ, ಇವುಗಳು ರಾಶಿರಾಶಿಯಾಗಿ ಸಿಕ್ಕಿದವು. ಅವರು ಹೊರಲಾರದಷ್ಟನ್ನೂ ಸುಲಿದುಕೊಂಡರು. ಕೊಳ್ಳೆಯ ಮೊತ್ತ ಹೆಚ್ಚಾಗಿದ್ದುದರಿಂದ ಅವರು ಮೂರು ದಿನಗಳವರೆಗೂ ಕೂಡಿಸುತ್ತಿದ್ದರು.",
			"26": "<sup>26</sup> ನಾಲ್ಕನೆಯ ದಿನದಲ್ಲಿ ಬೆರಾಕ ಕಣಿವೆಗೆ ಕೂಡಿಬಂದರು. ಅಲ್ಲಿ ಅವರು ಸರ್ವೇಶ್ವರನನ್ನು ಸ್ತುತಿಸಿದ್ದರಿಂದ ಆ ಸ್ಥಳಕ್ಕೆ ಇಂದಿನವರೆಗೂ ‘ಬೆರಾಕ ಕಣಿವೆ’ ಎಂಬ ಹೆಸರಿದೆ.",
			"27": "<sup>27</sup> ಆಮೇಲೆ ಯೆಹೂದ್ಯರು, ಜೆರುಸಲೇಮಿನವರು, ಅವರ ಮುಂಗಡೆಯಲ್ಲಿ ಯೆಹೋಷಾಫಾಟನು, ಸರ್ವೇಶ್ವರ ತಮಗೆ ಶತ್ರುಗಳ ಮೇಲೆ ಜಯವನ್ನು ಅನುಗ್ರಹಿಸಿದ್ದಾರೆಂದು ಜಯಘೋಷ ಮಾಡುತ್ತಾ ಜೆರುಸಲೇಮಿಗೆ ಹಿಂದಿರುಗಿದರು.",
			"28": "<sup>28</sup> ಸ್ವರಮಂಡಲ, ಕಿನ್ನರಿ, ತುತೂರಿ ಇವುಗಳೊಡನೆ ಜೆರುಸಲೇಮಿನಲ್ಲಿರುವ ಸರ್ವೇಶ್ವರನಾಲಯಕ್ಕೆ ಬಂದರು.",
			"29": "<sup>29</sup> ಸರ್ವೇಶ್ವರಾ ಸ್ವಾಮಿಯೇ ಇಸ್ರಯೇಲರ ಪರವಾಗಿ ಶತ್ರಗಳೊಡನೆ ಯುದ್ಧಮಾಡಿದರೆಂಬ ಸುದ್ದಿ ಅನ್ಯದೇಶಗಳ ರಾಜ್ಯಗಳವರೆಗೆ ಮುಟ್ಟಿದಾಗ, ಅವರೆಲ್ಲರು ಬಹಳ ಭಯಭೀತರಾದರು.",
			"30": "<sup>30</sup> ಹೀಗೆ ದೇವ ಅನುಗ್ರಹದಿಂದ ಸುತ್ತಮುತ್ತಲಿನ ವೈರಿಗಳ ಭಯ ತಪ್ಪಿ, ಯೆಹೋಷಾಫಾಟನ ರಾಜ್ಯಕ್ಕೆ ಸಮಾಧಾನ ಉಂಟಾಯಿತು.",
			"31": "<sup>31</sup> ಯೆಹೂದ್ಯರ ಅರಸ ಯೆಹೋಷಾಫಾಟನು ಪಟ್ಟಕ್ಕೆ ಬಂದಾಗ ಮೂವತ್ತೈದು ವರ್ಷದವನಾಗಿದ್ದನು. ಇವನು ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಇಪ್ಪತ್ತೈದು ವರ್ಷ ಆಳಿದನು. ಶಿಲ್ಹಿಯ ಮಗಳಾದ ಅಜೂಬಳೆಂಬಾಕೆ ಇವನ ತಾಯಿ.",
			"32": "<sup>32</sup> ತಂದೆ ಆಸನ ಮಾರ್ಗದಲ್ಲಿ ಇವನು ತಪ್ಪದೆ ನಡೆಯುತ್ತಾ ಸರ್ವೇಶ್ವರನ ದೃಷ್ಟಿಯಲ್ಲಿ ಒಳ್ಳೆಯವನಾಗಿ ಇದ್ದನು.",
			"33": "<sup>33</sup> ಆದರೂ ಪೂಜಾಸ್ಥಳಗಳನ್ನು ಹಾಳುಮಾಡಲಿಲ್ಲ. ಜನರ ಮನಸ್ಸು ಇನ್ನೂ ತಮ್ಮ ಪಿತೃಗಳ ದೇವರ ಮೇಲೆ ನಾಟಿರಲಿಲ್ಲ.",
			"34": "<sup>34</sup> ಯೆಹೋಷಾಫಾಟನ ಉಳಿದ ಪೂರ್ವೋತ್ತರ ಚರಿತ್ರೆ ಇಸ್ರಯೇಲ್ ರಾಜರ ಗ್ರಂಥದಲ್ಲಿ ಹನಾನೀಯನ ಮಗ ಯೇಹುವಿನ ವೃತ್ತಾಂತದಲ್ಲಿ ದಾಖಲಾಗಿದೆ.",
			"35": "<sup>35</sup> ಒಮ್ಮೆ ಯೆಹೂದ್ಯರ ಅರಸ ಯೆಹೋಷಾಫಾಟನು, ಬಹು ದುರಾಚಾರಿಯಾದ ಇಸ್ರಯೇಲರ ಅರಸ ಅಹಜ್ಯನೊಡನೆ ಒಂದು ಒಪ್ಪಂದ ಮಾಡಿಕೊಂಡನು.",
			"36": "<sup>36</sup> ಅವರು ತಾರ್ಷೀಷಿಗೆ ಹೋಗುವುದಕ್ಕಾಗಿ ಹಡಗುಗಳನ್ನು ಕಟ್ಟಬೇಕೆಂದು ಒಪ್ಪಂದ ಮಾಡಿಕೊಂಡು ಅವುಗಳನ್ನು ಎಚ್ಯೋನ್ಗೆಬೆರಿನಲ್ಲಿ ಕಟ್ಟಿಸಿದರು.",
			"37": "<sup>37</sup> ಆಗ ಮಾರೇಷಾ ಊರಿನ ದೋದಾವಾಹುವಿನ ಮಗ ಎಲೀಯೆಜರನು ಯೆಹೋಷಾಫಾಟನಿಗೆ ವಿರೋಧವಾಗಿ, “ನೀನು ಅಹಜ್ಯನೊಡನೆ ಒಪ್ಪಂದಮಾಡಿಕೊಂಡಿದ್ದರಿಂದ ಸರ್ವೇಶ್ವರ ನಿನ್ನ ಕೆಲಸವನ್ನು ಹಾಳುಮಾಡುವರು,” ಎಂದು ಪ್ರವಾದಿಸಿದನು. ಆ ಹಡಗುಗಳು ಒಡೆದುಹೋದುದರಿಂದ ತಾರ್ಷೀಷಿಗೆ ಹೊರಡಲೇ ಇಲ್ಲ."
		},
		"21": {
			"1": "<sup>1</sup> ಯೆಹೋಷಾಫಾಟನು ಮೃತನಾಗಿ ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಿ ದಾವೀದನಗರದಲ್ಲಿದ್ದ ಪೂರ್ವಿಕರ ಸ್ಮಶಾನಭೂಮಿಯಲ್ಲಿ ಸಮಾಧಿಹೊಂದಿದ ಮೇಲೆ, ಅವನ ಸ್ಥಾನದಲ್ಲಿ ಅವನ ಮಗ ಯೆಹೋರಾಮನು ಅರಸನಾದನು.",
			"2": "<sup>2</sup> ಇವನಿಗೆ ಅಜರ್ಯ, ಯೆಹೀಯೇಲ್, ಜೆಕರ್ಯ, ಅಜರ್ಯ, ಮೀಕಾಯೇಲ್, ಶೆಫಟ್ಯ ಎಂಬ ತಮ್ಮಂದಿರಿದ್ದರು. ಇವರೆಲ್ಲರು ಯೆಹೂದ್ಯರ ಅರಸ ಯೆಹೋಷಾಫಾಟನ ಮಕ್ಕಳು.",
			"3": "<sup>3</sup> ಇವರ ತಂದೆ ಇವರಿಗೆ ಜುದೇಯದ ಕೋಟೆಕೊತ್ತಲಗಳುಳ್ಳ ಪಟ್ಟಣಗಳನ್ನು ಅಲ್ಲದೆ, ಬೆಳ್ಳಿಬಂಗಾರ, ಅಮೂಲ್ಯ ವಸ್ತು ಇತ್ಯಾದಿ ವಿಶೇಷ ದಾನಗಳನ್ನು ಕೊಟ್ಟನು. ಆದರೆ ಯೆಹೋರಾಮನು ಹಿರಿಯ ಮಗನಾಗಿದ್ದುದರಿಂದ ಅವನಿಗೆ ರಾಜ್ಯವನ್ನು ಕೊಟ್ಟನು.",
			"4": "<sup>4</sup> ಯೆಹೋರಾಮನು ತನ್ನ ತಂದೆಯ ರಾಜ್ಯವನ್ನು ಪಡೆದುಕೊಂಡು, ತನ್ನ ಆಳ್ವಿಕೆಯನ್ನು ಸ್ಥಿರಗೊಳಿಸಿಕೊಂಡ ಮೇಲೆ, ತನ್ನ ಎಲ್ಲ ಸಹೋದರರನ್ನೂ ಕೆಲವು ಮಂದಿ ಇಸ್ರಯೇಲ್ ಪ್ರಮುಖರನ್ನೂ ಕತ್ತಿಯಿಂದ ಸಂಹರಿಸಿದನು.",
			"5": "<sup>5</sup> ಯೆಹೋರಾಮನು ಪಟ್ಟಕ್ಕೆ ಬಂದಾಗ ಇವನಿಗೆ ಮೂವತ್ತೆರಡು ವರ್ಷ ವಯಸ್ಸು. ಇವನು ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಎಂಟು ವರ್ಷ ಆಳಿದನು.",
			"6": "<sup>6</sup> ಅಹಾಬನ ಮಗಳನ್ನು ಇವನು ಮದುವೆಮಾಡಿಕೊಂಡದ್ದರಿಂದ ಅಹಾಬನ ಕುಟುಂಬದವರಾದ ಇಸ್ರಯೇಲ್ ಅರಸರ ಹೆಜ್ಜೆಯಲ್ಲೇ ನಡೆದು ಸರ್ವೇಶ್ವರನ ಚಿತ್ತಕ್ಕೆ ವಿರುದ್ಧವಾಗಿ ನಡೆದನು.",
			"7": "<sup>7</sup> ಆದರೂ ಸರ್ವೇಶ್ವರ ದಾವೀದನಿಗೆ, “ನಿನ್ನ ಮತ್ತು ನಿನ್ನ ಸಂತಾನದವರ ದೀಪವನ್ನು ಎಂದೂ ನಂದಿಸುವುದಿಲ್ಲ,” ಎಂದು ಪ್ರಮಾಣ ಮಾಡಿ ಹೇಳಿದ್ದರಿಂದ ಆ ದಾವೀದನ ಮನೆತನದವರನ್ನು ನಾಶಮಾಡುವುದಕ್ಕೆ ಇಷ್ಟಪಡಲಿಲ್ಲ.",
			"8": "<sup>8</sup> ಯೆಹೋರಾಮನ ಕಾಲದಲ್ಲಿ ಎದೋಮ್ಯರು ಯೆಹೂದ್ಯರಿಗೆ ವಿರುದ್ಧ ದಂಗೆಯೆದ್ದರು; ಸ್ವತಂತ್ರರಾಗಿ ತಾವೇ ತಮಗೊಬ್ಬ ಅರಸನನ್ನು ನೇಮಿಸಿಕೊಂಡರು.",
			"9": "<sup>9</sup> ಆಗ ಯೆಹೋರಾಮನು ತನ್ನ ಸರದಾರರನ್ನೂ ಎಲ್ಲ ರಥಬಲವನ್ನೂ ಕೂಡಿಸಿಕೊಂಡು ಜೋರ್ಡನ್ ನದಿಯನ್ನು ದಾಟಿದನು. ಎದೋಮ್ಯರು ಬಂದು ಅವನನ್ನು ಸುತ್ತಿಕೊಳ್ಳಲು ಅವನು ರಾತ್ರಿಯಲ್ಲೇ ದಂಡೆತ್ತಿಹೋಗಿ ಅವರನ್ನೂ ಅವರ ರಥಬಲದ ಅಧಿಪತಿಗಳನ್ನೂ ಸೋಲಿಸಿ ಪಾರಾದನು.",
			"10": "<sup>10</sup> ಯೆಹೂದ್ಯರಿಗೆ ವಿರುದ್ಧ ದಂಗೆಯೆದ್ದ ಎದೋಮ್ಯರು ಅಂದಿನಿಂದ ಇಂದಿನವರೆಗೂ ಸ್ವತಂತ್ರರಾಗಿದ್ದಾರೆ. ಆ ಕಾಲದಲ್ಲೇ ಲಿಬ್ನದವರೂ ಅವನ ಕೈಯಿಂದ ತಪ್ಪಿಸಿಕೊಂಡು ಸ್ವತಂತ್ರರಾದರು. ಯೆಹೋರಾಮನು ತನ್ನ ಪಿತೃಗಳ ದೇವರಾದ ಸರ್ವೇಶ್ವರನನ್ನು ಬಿಟ್ಟದ್ದೇ ಇದಕ್ಕೆ ಕಾರಣ.",
			"11": "<sup>11</sup> ಅವನು ಜುದೇಯದ ಗುಡ್ಡಗಳಲ್ಲಿ ಪೂಜಾಸ್ಥಳಗಳನ್ನು ಏರ್ಪಡಿಸಿ, ಜೆರುಸಲೇಮಿನವರು ದೇವದ್ರೋಹ ಮಾಡುವಂತೆ ಪ್ರೇರೇಪಿಸಿ, ಯೆಹೂದ್ಯರನ್ನು ಸನ್ಮಾರ್ಗದಿಂದ ತಪ್ಪಿಸಿದನು.",
			"12": "<sup>12</sup> ಹೀಗಿರಲು, ಪ್ರವಾದಿ ಎಲೀಯನು ಪತ್ರದ ಮೂಲಕ ಅವನಿಗೆ, “ನಿನ್ನ ಪೂರ್ವಜ ದಾವೀದನ ದೇವರಾಗಿರುವ ಸರ್ವೇಶ್ವರನ ಮಾತನ್ನು ಕೇಳು: ನೀನು ನಿನ್ನ ತಂದೆ ಯೆಹೋಷಾಫಾಟನ ಮಾರ್ಗದಲ್ಲಿ ಹಾಗು ಯೆಹೂದ್ಯರ ಅರಸ ಆಸನ ಮಾರ್ಗದಲ್ಲಿ ನಡೆಯಲಿಲ್ಲ;",
			"13": "<sup>13</sup> ಇಸ್ರಯೇಲ್ ಅರಸರ ಹೆಜ್ಜೆಯಲ್ಲೇ ನಡೆದು, ಅಹಾಬನ ಮನೆಯವರಲ್ಲಿ ಪ್ರಬಲವಾಗಿದ್ದ ದೇವದ್ರೋಹವನ್ನು ಮಾಡುವಂತೆ ಯೆಹೂದ್ಯರನ್ನೂ ಜೆರುಸಲೇಮಿನವರನ್ನೂ ಪ್ರೇರೇಪಿಸಿದೆ; ಮತ್ತು ನಿನ್ನ ತಂದೆಯ ಕುಟುಂಬದವರೂ ನಿನಗಿಂತ ಉತ್ತಮರೂ ನಿನ್ನ ಸಹೋದರರೂ ಆಗಿದ್ದವರನ್ನು ವಧಿಸಿದೆ;",
			"14": "<sup>14</sup> ಆದುದರಿಂದ ಸರ್ವೇಶ್ವರ ನಿನ್ನ ಪ್ರಜೆಯನ್ನೂ ಮಡದಿಮಕ್ಕಳನ್ನೂ ಸರ್ವಸ್ವವನ್ನೂ ಮಹಾ ಆಪತ್ತಿಗೆ ಗುರಿಮಾಡುವರು.",
			"15": "<sup>15</sup> ನಿನಗಾದರೋ ಕರುಳುಬೇನೆಯ ಕಠಿಣರೋಗ ಬರುವುದು. ಅದು ಬಹುದಿನಗಳವರೆಗೂ ವಾಸಿ ಆಗದು; ಕಡೆಯಲ್ಲಿ ನಿನ್ನ ಕರುಳುಗಳು ಹೊರಗೆ ಬೀಳುವುವು,” ಎಂದು ತಿಳಿಸಿದನು.",
			"16": "<sup>16</sup> ಸರ್ವೇಶ್ವರ ಫಿಲಿಷ್ಟಿಯರನ್ನೂ ಸುಡಾನಿನ ನೆರೆಯವರಾದ ಅರೇಬಿಯರನ್ನೂ ಯೆಹೋರಾಮನಿಗೆ ವಿರೋಧಿಗಳಾಗುವಂತೆ ಪ್ರಚೋದಿಸಿದರು.",
			"17": "<sup>17</sup> ಅವರು ಜುದೇಯದ ಮೇಲೆ ಯುದ್ಧಕ್ಕೆ ಬಂದು, ನಾಡಿನೊಳಗೆ ನುಗ್ಗಿ, ಅರಮನೆಯಲ್ಲಿ ಸಿಕ್ಕಿದ ಎಲ್ಲ ಸೊತ್ತನ್ನೂ ಅರಸನ ಮಡದಿ ಮಕ್ಕಳನ್ನೂ ಸೆರೆಗೆ ಒಯ್ದರು. ಅವನಿಗೆ ಕಿರಿಯಮಗ ಯೆಹೋವಾಹಾಜನ ಹೊರತು ಮಕ್ಕಳೇ ಉಳಿಯಲಿಲ್ಲ.",
			"18": "<sup>18</sup> ಇಷ್ಟೆಲ್ಲಾ ಆದ ಮೇಲೆ, ಸರ್ವೇಶ್ವರ ಅವನ ಕರುಳುಗಳಲ್ಲಿ ವಾಸಿಯಾಗಲಾರದ ರೋಗವನ್ನು ಉಂಟುಮಾಡಿದರು.",
			"19": "<sup>19</sup> ಕಾಲಾಂತರದಲ್ಲಿ ಅಂದರೆ, ಸುಮಾರು ಎರಡು ವರ್ಷಗಳಾದ ಮೇಲೆ, ಅದೇ ರೋಗದ ನಿಮಿತ್ತ ಕರುಳುಗಳು ಹೊರಬಿದ್ದವು. ಅವನು ಕಡುಬೇನೆಯಿಂದ ಸತ್ತನು. ಅವನ ಪೂರ್ವಿಕರಿಗೆ ಧೂಪಹಾಕಿದಂತೆ ಅವನ ಪ್ರಜೆಗಳು ಅವನಿಗೆ ಹಾಕಲಿಲ್ಲ.",
			"20": "<sup>20</sup> ಅವನು ಪಟ್ಟಕ್ಕೆ ಬಂದಾಗ ಮೂವತ್ತ ಎರಡು ವರ್ಷದವನಾಗಿದ್ದನು. ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಎಂಟುವರ್ಷ ಆಳಿದನು. ಅವನು ತೀರಿಹೋದಾಗ ಯಾರೂ ಶೋಕಿಸಲಿಲ್ಲ. ಅವನ ಶವವನ್ನು ದಾವೀದನಗರದಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು; ಆದರೆ ರಾಜಸ್ಮಶಾನದಲ್ಲಿ ಮಾಡಲಿಲ್ಲ."
		},
		"22": {
			"1": "<sup>1</sup> ಜೆರುಸಲೇಮಿನವರು ಯೆಹೋರಾಮನಿಗೆ ಬದಲಾಗಿ ಅವನ ಕಿರಿಯಮಗ ಅಹಜ್ಯನನ್ನು ಅರಸನನ್ನಾಗಿ ಮಾಡಿದರು. ಅರೇಬಿಯರೊಡನೆ ಯೆಹೋರಾಮನ ಪಾಳೆಯಕ್ಕೆ ಬಂದ ಕೊಳ್ಳೆಗಾರರು ಅವನ ಹಿರಿಯ ಮಕ್ಕಳನ್ನೆಲ್ಲಾ ಕೊಂದುಬಿಟ್ಟಿದ್ದರು. ಆದುದರಿಂದ ಜುದೇಯದ ಅರಸ ಯೆಹೋರಾಮನ ಮಗ ಅಹಜ್ಯನು ಅರಸನಾದನು.",
			"2": "<sup>2</sup> ಪಟ್ಟಕ್ಕೆ ಬಂದಾಗ ಅವನಿಗೆ ನಲ್ವತ್ತೆರಡು ವರ್ಷ ಪ್ರಾಯ. ಇವನು ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಒಂದು ವರ್ಷ ಆಳಿದನು; ಒಮ್ರಿಯ ಮೊಮ್ಮಗಳಾದ ಅತಲ್ಯ ಎಂಬಾಕೆ ಇವನ ತಾಯಿ;",
			"3": "<sup>3</sup> ತನ್ನ ತಾಯಿಯ ದುರ್ಬೋಧನೆಗೆ ಕಿವಿಗೊಟ್ಟು ಇವನೂ ದುರಾಚಾರಿಯಾಗಿ ಅಹಾಬನ ಮನೆಯವರ ಹೆಜ್ಜೆ ಜಾಡಿನಲ್ಲೇ ನಡೆದನು.",
			"4": "<sup>4</sup> ಇವನ ತಂದೆ ತೀರಿಹೋದ ಮೇಲೆ ಅಹಾಬನ ಕುಟುಂಬದವರೇ ಇವನಿಗೆ ಸಲಹೆಕೊಟ್ಟು ಇವನನ್ನು ಕೆಡಿಸುತ್ತಾ ಬಂದರು. ಈ ಕಾರಣ ಸರ್ವೇಶ್ವರನಿಗೆ ದ್ರೋಹಿಯಾಗಿ ನಡೆದನು.",
			"5": "<sup>5</sup> ಆ ಪ್ರೇರಣೆಯಿಂದಲೇ ಇಸ್ರಯೇಲರ ಅರಸನೂ ಅಹಾಬನ ಮಗನೂ ಆದ ಯೋರಾಮನ ಜೊತೆಯಲ್ಲಿ ಸಿರಿಯಾದ ಅರಸ ಹಜಾಯೇಲನಿಗೆ ವಿರುದ್ಧ ಯುದ್ಧಮಾಡುವುದಕ್ಕೆ ರಾಮೋತ್‍ಗಿಲ್ಯಾದಿಗೆ ಹೋದನು. ಸಿರಿಯಾದವರು ಯೋರಾಮನನ್ನು ಯುದ್ಧದಲ್ಲಿ ಗಾಯಪಡಿಸಿದರು.",
			"6": "<sup>6</sup> ಆ ಗಾಯಗಳನ್ನು ಮಾಯಿಸಿಕೊಳ್ಳುವುದಕ್ಕಾಗಿ ಅವನು ಜೆಸ್ರೀಲಿಗೆ ಬಂದನು. ಅವನು ಅಸ್ವಸ್ಥನಾಗಿದ್ದುದರಿಂದ ಯೆಹೂದ್ಯರ ಅರಸ ಯೆಹೋರಾಮನ ಮಗನಾದ ಅಹಜ್ಯನು ಅವನನ್ನು ನೋಡುವುದಕ್ಕೆ ಅಲ್ಲಿಗೆ ಹೋದನು.",
			"7": "<sup>7</sup> ಅಹಜ್ಯನು ಯೋರಾಮನ ಬಳಿಗೆ ಹೋಗಿ ನಾಶವಾದದ್ದು ದೈವಸಂಕಲ್ಪದಿಂದಲೇ. ಅವನು ಆ ಸ್ಥಳವನ್ನು ಮುಟ್ಟಿದ ಕೂಡಲೆ ಯೋರಾಮನ ಜೊತೆಯಲ್ಲಿ ನಿಂಷಿಯ ಮಗ ಯೇಹುವನ್ನು ಎದುರುಗೊಳ್ಳುವುದಕ್ಕೆ ಹೋದನು. ಸರ್ವೇಶ್ವರ ಅಹಾಬನ ಮನೆಯನ್ನು ನಿರ್ನಾಮ ಮಾಡುವುದಕ್ಕಾಗಿಯೇ ಯೇಹುವಿಗೆ ರಾಜಾಭಿಷೇಕ ಮಾಡಿಸಿ ಇದ್ದರು.",
			"8": "<sup>8</sup> ಯೇಹುವು, ಅಹಾಬನ ಮನೆಯವರ ಮೇಲೆ ಆ ನಿರ್ಣಯವನ್ನು ನೆರವೇರಿಸುತ್ತಿರುವಾಗ ತನಗೆ ಸಿಕ್ಕಿದ ಯೆಹೂದ್ಯ ಪ್ರಮುಖರನ್ನೂ ಅಹಜ್ಯನಿಗೆ ಸೇವೆಮಾಡುತ್ತಿದ್ದ ಅವನ ಅಣ್ಣತಮ್ಮಂದಿರ ಮಕ್ಕಳನ್ನೂ ಕೊಲ್ಲಿಸಿದನು;",
			"9": "<sup>9</sup> ಆಮೇಲೆ ಅಹಜ್ಯನನ್ನು ಹುಡುಕಿಸಿದನು; ಅವನು ಸಮಾರಿಯದಲ್ಲಿ ಅಡಗಿಕೊಂಡಿದ್ದನು. ಜನರು ಅವನನ್ನು ಯೇಹುವಿನ ಬಳಿಗೆ ಹಿಡಿದುತಂದು, ವಧಿಸಿದರು. “ಇವನು ಸರ್ವೇಶ್ವರನ ನೈಜಭಕ್ತನಾದ ಯೆಹೋಷಾಫಾಟನ ಮೊಮ್ಮಗನಲ್ಲವೇ?” ಎಂದುಕೊಂಡು ಅವನನ್ನು ಸಮಾಧಿಮಾಡಿದರು. ರಾಜ್ಯಾಧಿಕಾರವನ್ನು ವಹಿಸಿಕೊಳ್ಳಲು ಅಹಜ್ಯನ ಮನೆಯವರಲ್ಲಿ ಸಮರ್ಥರಾರೂ ಉಳಿಯಲಿಲ್ಲ.",
			"10": "<sup>10</sup> ಅಹಜ್ಯನು ಮರಣಹೊಂದಿದನೆಂಬುದನ್ನು ಅವನ ತಾಯಿ ಅತಲ್ಯಳು ಕೇಳಿದ ಕೂಡಲೆ ಜುದೇಯರ ರಾಜಸಂತಾನದವರನ್ನೆಲ್ಲಾ ಸಂಹರಿಸಿಬಿಟ್ಟಳು.",
			"11": "<sup>11</sup> ಆದರೆ ರಾಜಪುತ್ರಿಯಾದ ಯೆಹೋಷಬತ್ ಎಂಬಾಕೆ, ಹತರಾಗುವುದಕ್ಕಿದ್ದ ರಾಜಪುತ್ರರ ಮಧ್ಯೆಯಿಂದ ಅಹಜ್ಯನ ಮಗ ಯೆಹೋವಾಷನನ್ನು, ಯಾರಿಗೂ ತಿಳಿಯದಂತೆ ಅವನ ದಾದಿಯೊಡನೆ ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ಮಲಗುವ ಕೋಣೆಯಲ್ಲಿ ಅಡಗಿಸಿಟ್ಟಳು. ಅರಸ ಯೆಹೋರಾಮನ ಮಗಳೂ ಅಹಜ್ಯನ ಸಹೋದರಿಯೂ ಯಾಜಕ ಯೆಹೋಯಾದನ ಹೆಂಡತಿಯೂ ಆಗಿದ್ದ ಯೆಹೋಷಬತಳು ಹೀಗೆ ಅವನನ್ನು ಅತಲ್ಯಳಿಂದ ಹತನಾಗದಂತೆ ರಕ್ಷಿಸಿದಳು.",
			"12": "<sup>12</sup> ಅವನು ಆರು ವರ್ಷಗಳವರೆಗೂ ಅವರೊಡನೆ ಗುಪ್ತವಾಗಿ ದೇವಾಲಯದಲ್ಲಿ ಇದ್ದನು. ಆಗ ಅತಲ್ಯಳೇ ದೇಶವನ್ನಾಳುತ್ತಿದ್ದಳು."
		},
		"23": {
			"1": "<sup>1</sup> ಏಳನೆಯ ವರ್ಷದಲ್ಲಿ ಯೆಹೋಯಾದನು ಧೈರ್ಯತಂದುಕೊಂಡು ಯೆರೋಹಾಮನ ಮಗ ಅಜರ್ಯ, ಯೆಹೋಹಾನಾನನ ಮಗ ಇಷ್ಮಾಯೇಲ್, ಓಬೇದನ ಮಗ ಅಜರ್ಯ, ಅದಾಯನ ಮಗ ಮಾಸೇಯ, ಜಿಕ್ರಿಯ ಮಗ ಎಲೀಷಾಫಾಟ್ ಎಂಬ ಶತಾಧಿಪತಿಗಳೊಡನೆ ಒಂದು ಒಪ್ಪಂದ ಮಾಡಿಕೊಂಡನು.",
			"2": "<sup>2</sup> ಇವರು ಜುದೇಯದಲ್ಲೆಲ್ಲಾ ಸಂಚರಿಸಿ ಎಲ್ಲ ಪಟ್ಟಣಗಳಿಂದ ಲೇವಿಯರನ್ನೂ ಇಸ್ರಯೇಲರ ಗೋತ್ರಪ್ರಧಾನರನ್ನೂ ಕೂಡಿಸಿದರು. ಅವರೆಲ್ಲರು ಜೆರುಸಲೇಮಿಗೆ ಬಂದರು.",
			"3": "<sup>3</sup> ಹೀಗೆ ಕೂಡಿಬಂದವರೆಲ್ಲರು ದೇವಾಲಯದಲ್ಲಿ ಅರಸನೊಂದಿಗೆ ಒಪ್ಪಂದ ಮಾಡಿಕೊಂಡರು. ಯೆಹೋಯಾದನು ಅವರಿಗೆ, “ಇಗೋ, ರಾಜಪುತ್ರನು; ಸರ್ವೇಶ್ವರಸ್ವಾಮಿ ದಾವೀದನ ಸಂತಾನದವರಿಗೆ ಮಾಡಿದ ವಾಗ್ದಾನಕ್ಕನುಸಾರ ಅರಸನಾಗತಕ್ಕವನು ಇವನೇ.",
			"4": "<sup>4</sup> ಈಗ ನೀವು ಮಾಡತಕ್ಕದ್ದೇನೆಂದರೆ - ಸಬ್ಬತ್‍ದಿನ ದೇವಾಲಯದ ಸೇವೆಗೆ ಬರಬೇಕಾದ ಲೇವಿಯರಲ್ಲೂ ಯಾಜಕರಲ್ಲೂ ಮೂರರಲ್ಲೊಂದು ಭಾಗದವರು ದೇವಸ್ಥಾನದ ದ್ವಾರಪಾಲಕರಾಗಿ ನಿಲ್ಲಬೇಕು.",
			"5": "<sup>5</sup> ಇನ್ನೊಂದು ಭಾಗದವರು ಅರಮನೆಯನ್ನೂ ಮತ್ತೊಂದು ಭಾಗದವರು ಯೆಸೋದ್‍ ಬಾಗಿಲನ್ನೂ ಕಾಯಬೇಕು. ಇತರ ಜನರೆಲ್ಲರು ಸರ್ವೇಶ್ವರನ ಆಲಯದ ಪ್ರಾಕಾರದಲ್ಲೇ ಇರಬೇಕು.",
			"6": "<sup>6</sup> ಯಾಜಕರು ಹಾಗು ಸೇವೆಯಲ್ಲಿರುವ ಲೇವಿಯರು ಇವರನ್ನು ಬಿಟ್ಟು ಬೇರೆ ಯಾರೂ ಸರ್ವೇಶ್ವರನ ಆಲಯದೊಳಗೆ ಬರಬಾರದು. ಇವರು ಪರಿಶುದ್ಧರಾಗಿರುವುದರಿಂದ ಬರಬಹುದು. ಉಳಿದ ಜನರೆಲ್ಲರು ಸರ್ವೇಶ್ವರನ ಆಜ್ಞಾನುಸಾರ ಹೊರಗಿರಬೇಕು.",
			"7": "<sup>7</sup> ಲೇವಿಯರು ಕೈಯಲ್ಲಿ ಆಯುಧಗಳನ್ನು ಹಿಡಿದುಕೊಂಡು ಅರಸನ ಸುತ್ತಲೂ ನಿಂತು ಆಲಯದೊಳಕ್ಕೆ ನುಗ್ಗುವವರನ್ನು ಸಂಹರಿಸಬೇಕು. ಅರಸನು ಹೋಗಿ ಬರುವಾಗಲೆಲ್ಲಾ ಅವರು ಅವನ ಜೊತೆಯಲ್ಲೇ ಇರಬೇಕು,” ಎಂದು ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ಲೇವಿಯರೂ ಎಲ್ಲ ಯೆಹೂದ್ಯರೂ ಯಾಜಕ ಯೆಹೋಯಾದನ ಆಜ್ಞೆಯಂತೆ ನಡೆದುಕೊಂಡರು. ಅವನು ಸಬ್ಬತ್‍ದಿನದಲ್ಲಿ ಸೇವಾವಿಮುಕ್ತರಾದ ವರ್ಗದವರನ್ನು ಕಳುಹಿಸಿಬಿಡಲಿಲ್ಲ; ಆದುದರಿಂದ ಪ್ರತಿಯೊಬ್ಬನು ಸಬ್ಬತ್‍ದಿನ ಮನೆಗೆ ಹೋಗಬೇಕಾಗಿದ್ದ ಮತ್ತು ಬರಬೇಕಾಗಿದ್ದ ತನ್ನ ಕೈ ಕೆಳಗಿನವರನ್ನು ಕೂಡಿಸಿಕೊಂಡನು.",
			"9": "<sup>9</sup> ದೇವಾಲಯದಲ್ಲಿಟ್ಟಿದ್ದ ದಾವೀದ ಅರಸನ ಬರ್ಜಿ, ಗುರಾಣಿ, ಖೇಡ್ಯಗಳೇ ಮುಂತಾದ ಆಯುಧಗಳನ್ನು ಯಾಜಕ ಯೆಹೋಯಾದನು ಶತಾಧಿಪತಿಗಳಿಗೆ ಕೊಟ್ಟನು.",
			"10": "<sup>10</sup> ಆಯುಧಪಾಣಿಗಳಾದ ಎಲ್ಲ ಜನರನ್ನು ಅರಸನ ಸುತ್ತಲು ದೇವಾಲಯದ ದಕ್ಷಿಣದಿಕ್ಕಿನ ಮೂಲೆಯಿಂದ ಬಲಿಪೀಠದವರೆಗೂ ಅಲ್ಲಿಂದ ಉತ್ತರದಿಕ್ಕಿನ ಮೂಲೆಯವರೆಗೂ ಸಾಲಾಗಿ ನಿಲ್ಲಿಸಿದನು.",
			"11": "<sup>11</sup> ಆಮೇಲೆ ಅವನೂ ಅವನ ಮಕ್ಕಳೂ ರಾಜಪುತ್ರನನ್ನು ಹೊರಗೆ ಕರೆದುಕೊಂಡು ಬಂದು ಅವನ ತಲೆಯ ಮೇಲೆ ಕಿರೀಟವನ್ನಿಟ್ಟು, ಕೈಗೆ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಕೊಟ್ಟು, ಅವನಿಗೆ ರಾಜಾಭಿಷೇಕ ಮಾಡಿದರು. ಕೂಡಲೆ ಜನರು, “ಅರಸನು ಚಿರಂಜೀವಿಯಾಗಿರಲಿ!” ಎಂದು ಹರಸಿದರು.",
			"12": "<sup>12</sup> ಜನರು ಅತ್ತಿತ್ತ ಓಡಾಡುತ್ತಾ ಅರಸನನ್ನು ಹರಸುವ ಗದ್ದಲವನ್ನು ಅತಲ್ಯಳು ಕೇಳಿ ಅವರು ಕೂಡಿದ್ದ ಸರ್ವೇಶ್ವರನ ಆಲಯಕ್ಕೆ ಬಂದಳು.",
			"13": "<sup>13</sup> ಅಲ್ಲಿ ಅರಸನು ಬಾಗಿಲಿನ ಬಳಿಯಲ್ಲಿರುವ ರಾಜಸ್ತಂಭದ ಹತ್ತಿರ ನಿಂತಿದ್ದನು. ಅಧಿಪತಿಗಳು ಹಾಗೂ ತುತೂರಿಗಳನ್ನು ಊದುವವರು ಅರಸನ ಹತ್ತಿರವಿದ್ದರು; ಜನಸಾಮಾನ್ಯರೆಲ್ಲರೂ ಸಂತೋಷದಿಂದ ಕೊಂಬೂದುತ್ತಿದ್ದರು. ಗಾಯಕರು ವಾದ್ಯಗಳನ್ನು ಬಾರಿಸುತ್ತಾ ಜನಪದ ಕೀರ್ತನೆಗಳನ್ನು ನುಡಿಸುತ್ತಿದ್ದರು. ಇದನ್ನು ಕಂಡಕೂಡಲೆ ಆಕೆ ಸಿಟ್ಟಿನಿಂದ ಬಟ್ಟೆಗಳನ್ನು ಹರಿದುಕೊಂಡು, “ದ್ರೋಹ! ದ್ರೋಹ!” ಎಂದು ಕೂಗಿದಳು.",
			"14": "<sup>14</sup> ಆಗ ಯಾಜಕ ಯೆಹೋಯಾದನು ಸೇನಾನಿಗಳಾದ ಶತಾಧಿಪತಿಗಳನ್ನು ಹೊರಗೆ ಕರೆದುಕೊಂಡು ಹೋಗಿ ಅವರಿಗೆ, “ಈಕೆಯನ್ನು ಸರ್ವೇಶ್ವರನ ಆಲಯದಲ್ಲಿ ಕೊಲ್ಲಬೇಡಿ; ಎರಡು ಸಾಲು ಸಿಪಾಯಿಗಳು ಈಕೆಯನ್ನು ನಡುವೆ ಸಾಗಿಸಿಕೊಂಡು ಹೊರಗೆ ತಳ್ಳಲಿ; ಮತ್ತು ಈಕೆಯನ್ನು ಹಿಂಬಾಲಿಸುವವರನ್ನು ಕತ್ತಿಯಿಂದ ಕೊಲ್ಲಿರಿ,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"15": "<sup>15</sup> ಅವರು ಆಕೆಯನ್ನು ಹಿಡಿದು ಅರಮನೆಗೆ ಹೋಗುವ ಕುದುರೆಬಾಗಿಲಿನ ತನಕ ಒಯ್ದು, ಅಲ್ಲಿ ಕೊಂದರು.",
			"16": "<sup>16</sup> ಅನಂತರ ಯೆಹೋಯಾದನು ಎಲ್ಲ ಜನರನ್ನೂ ಅರಸನನ್ನೂ ಪ್ರೇರಿಸಿ ತಾವೆಲ್ಲರೂ ಸರ್ವೇಶ್ವರನ ಪ್ರಜೆಗಳಾಗಿರುವೆವೆಂದು ಅವರೊಡನೆ ಪ್ರಮಾಣಮಾಡಿದನು.",
			"17": "<sup>17</sup> ತರುವಾಯ ಜನರೆಲ್ಲರೂ ಬಾಳ್‍ದೇವಸ್ಥಾನಕ್ಕೆ ಹೋಗಿ ಪೂಜಾರಿಯಾದ ಮತ್ತಾನನನ್ನು ಬಲಿಪೀಠಗಳ ಮುಂದೆಯೇ ಕೊಂದರು; ಆ ದೇವಸ್ಥಾನವನ್ನೂ ಅದರಲ್ಲಿದ್ದ ಪೀಠ ಹಾಗೂ ವಿಗ್ರಹಗಳನ್ನೂ ಹಾಳುಮಾಡಿಬಿಟ್ಟರು.",
			"18": "<sup>18</sup> ಯೆಹೋಯಾದನು ಲೇವಿಯರಾದ ಯಾಜಕರ ಕೈಕೆಳಗೆ ಸರ್ವೇಶ್ವರನ ಆಲಯದ ಕಾವಲುಗಾರರನ್ನು ನೇಮಿಸಿದನು. ದಾವೀದನು ಆ ಯಾಜಕರನ್ನು ಸರ್ವೇಶ್ವರನ ಆಲಯದ ಸೇವೆಗಾಗಿ ವರ್ಗವರ್ಗಗಳಾಗಿ ವಿಭಾಗಿಸಿದ್ದನು. ದಾವೀದನು ನೇಮಿಸಿದ್ದ ಉತ್ಸಾಹ ಗಾಯನದೊಡನೆ, ಮೋಶೆಯ ಧರ್ಮಶಾಸ್ತ್ರಾನುಸಾರ ಸರ್ವೇಶ್ವರನಿಗೆ ದಹನಬಲಿ ಸಮರ್ಪಣೆ ಮಾಡುವುದು ಅವರ ಕರ್ತವ್ಯವಾಗಿ ಇತ್ತು.",
			"19": "<sup>19</sup> ಯಾವ ವಿಧದಲ್ಲಾದರೂ ಅಶುದ್ಧನಾದವನು ಸರ್ವೇಶ್ವರನ ಆಲಯವನ್ನು ಪ್ರವೇಶಿಸದಂತೆ ಯೆಹೋಯಾದನು ಆಯಾ ಬಾಗಿಲುಗಳಲ್ಲಿ ದ್ವಾರಪಾಲಕರನ್ನಿರಿಸಿದನು.",
			"20": "<sup>20</sup> ಆಮೇಲೆ ಅವನು ಶತಾಧಿಪತಿಗಳು, ಶ್ರೀಮಂತರು, ಜನನಾಯಕರು ಹಾಗು ಶ್ರೀಸಾಮಾನ್ಯರು ಇವರೊಡನೆ ಅರಸನನ್ನು ಸರ್ವೇಶ್ವರನ ಆಲಯದಿಂದ ಮೇಲಣ ಬಾಗಿಲಿನ ಮಾರ್ಗವಾಗಿ ಅರಮನೆಗೆ ಕರೆದುಕೊಂಡು ಹೋಗಿ ರಾಜಸಿಂಹಾಸನದ ಮೇಲೆ ಕುಳ್ಳಿರಿಸಿದನು.",
			"21": "<sup>21</sup> ನಾಡಿನವರೆಲ್ಲರೂ ಬಹಳವಾಗಿ ಸಂತೋಷಪಟ್ಟರು; ಪಟ್ಟಣವು ಶಾಂತವಾಯಿತು; ಅತಲ್ಯಳನ್ನು ಕತ್ತಿಯಿಂದ ಸಂಹರಿಸಿದರು."
		},
		"24": {
			"1": "<sup>1</sup> ಯೆಹೋವಾಷನು ಅರಸನಾದಾಗ ಅವನಿಗೆ ಏಳು ವರ್ಷ ವಯಸ್ಸು. ಅವನು ಜೆರುಸಲೇಮಿನಲ್ಲಿ ನಲವತ್ತು ವರ್ಷ ಆಳಿದನು. ಬೇರ್ಷೆಬದವಳಾದ ಚಿಬ್ಯಳು ಅವನ ತಾಯಿ.",
			"2": "<sup>2</sup> ಯಾಜಕ ಯೆಹೋಯಾದನ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ಅವನು ಸರ್ವೇಶ್ವರನ ಚಿತ್ತಾನುಸಾರ ನಡೆದುಕೊಳ್ಳುತ್ತಿದ್ದನು.",
			"3": "<sup>3</sup> ಯೆಹೋಯಾದನು ಅರಸನಿಗೆ ಇಬ್ಬರು ಕನ್ಯೆಯರನ್ನು ಮದುವೆಮಾಡಿ ಕೊಟ್ಟನು; ಅವರಿಂದ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳು ಜನಿಸಿದರು.",
			"4": "<sup>4</sup> ಕ್ರಮೇಣ ಯೆಹೋವಾಷನು ಸರ್ವೇಶ್ವರನ ಆಲಯವನ್ನು ದುರಸ್ತಿಮಾಡಿಸುವುದಕ್ಕೆ ಮನಸ್ಸುಮಾಡಿದನು.",
			"5": "<sup>5</sup> ಯಾಜಕರನ್ನೂ ಲೇವಿಯರನ್ನೂ ಕೂಡಿಸಿ ಅವರಿಗೆ, “ನೀವು ಜುದೇಯದ ಪಟ್ಟಣಗಳಿಗೆ ಹೋಗಿ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಆಲಯದ ವಾರ್ಷಿಕ ದುರಸ್ತಿಯ ಖರ್ಚಿಗಾಗಿ ಎಲ್ಲ ಇಸ್ರಯೇಲರಿಂದ ಹಣ ಕೂಡಿಸಿರಿ; ಈ ಕಾರ್ಯವನ್ನು ಶೀಘ್ರವಾಗಿ ನೆರವೇರಿಸಿರಿ,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"6": "<sup>6</sup> ಆದರೂ ಆ ಲೇವಿಯರು ಅವಸರಪಡಲಿಲ್ಲ. ಆದುದರಿಂದ ಅರಸನು ಅವರ ಮುಖ್ಯಸ್ಥನಾದ ಯೆಹೋಯಾದನನ್ನು ಕರೆಯಿಸಿ, “ಸರ್ವೇಶ್ವರನ ದಾಸನಾದ ಮೋಶೆಯ ವಿಧಿಗನುಸಾರ ಇಸ್ರಯೇಲ್ ಸಮಾಜದವರೆಲ್ಲರು ದೇವದರ್ಶನದ ಗುಡಾರಕ್ಕೆ ಕೊಡಬೇಕಾದ ಕಾಣಿಕೆಯನ್ನು ಈ ಲೇವಿಯರು ಯೆಹೂದ್ಯರಿಂದಲೂ ಜೆರುಸಲೇಮಿನ ಜನರಿಂದಲೂ ವಸೂಲಿ ಮಾಡುವಂತೆ ನೀನೇಕೆ ನೋಡಿಕೊಳ್ಳಲಿಲ್ಲ?",
			"7": "<sup>7</sup> ಅತಿದುಷ್ಟಳಾದ ಅತಲ್ಯಳ ಮನೆಯವರು ದೇವಾಲಯವನ್ನು ಹಾಳುಮಾಡಿ, ಸರ್ವೇಶ್ವರನ ಆಲಯದ ಪ್ರತಿಷ್ಠಿತ ವಸ್ತುಗಳನ್ನು ಬಾಳ್‍ದೇವತೆಗಳಿಗಾಗಿ ಕೊಟ್ಟುಬಿಟ್ಟಿದ್ದಾರಲ್ಲವೇ?,” ಎಂದು ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ಅನಂತರ ಅರಸನು ಒಂದು ಪೆಟ್ಟಿಗೆಯನ್ನು ಮಾಡಿಸಿ ಅದನ್ನು ಸರ್ವೇಶ್ವರನ ಆಲಯದ ಬಾಗಿಲಿನ ಹೊರಗೆ ಇರಿಸಿದನು.",
			"9": "<sup>9</sup> ದೇವರ ದಾಸ ಮೋಶೆಯು ಮರುಭೂಮಿಯಲ್ಲಿ ವಿಧಿಸಿದಂತೆ ಇಸ್ರಯೇಲರು ಸರ್ವೇಶ್ವರನಿಗೆ ಕೊಡಬೇಕಾದ ಕಾಣಿಕೆಯನ್ನು ತಂದುಕೊಡಬೇಕು ಎಂಬುದಾಗಿ ಜುದೇಯದಲ್ಲೂ ಜೆರುಸಲೇಮಿನಲ್ಲೂ ಪ್ರಕಟಿಸಿದನು.",
			"10": "<sup>10</sup> ಎಲ್ಲ ಮುಖ್ಯಸ್ಥರೂ ಜನಸಾಮಾನ್ಯರೂ ಸಂತೋಷದಿಂದ, ಬೇಕಾಗುವಷ್ಟು ಹಣವನ್ನು ತಂದು ಪೆಟ್ಟಿಗೆಯಲ್ಲಿ ಹಾಕಿದರು.",
			"11": "<sup>11</sup> ಪೆಟ್ಟಿಗೆ ತುಂಬಾ ಹಣವಿರುತ್ತದೆಂದು ಕಂಡುಬಂದಾಗಲೆಲ್ಲಾ ಲೇವಿಯರು ಪೆಟ್ಟಿಗೆಯನ್ನು ಆಸ್ಥಾನದ ಅಧಿಕಾರಿಗಳ ಬಳಿಗೆ ತರುತ್ತಿದ್ದರು. ಆಗ ಅರಸನ ಕಾರ್ಯದರ್ಶಿಯೂ ಮಹಾಯಾಜಕನ ಪ್ರತಿನಿಧಿಯೂ ಬಂದು ಅದನ್ನು ತೆರವು ಮಾಡಿ ಮತ್ತೆ ಅದರ ಸ್ಥಳದಲ್ಲಿಡುತ್ತಿದ್ದರು. ಅವರು ಪ್ರತಿದಿನವೂ ಈ ಪ್ರಕಾರ ಮಾಡುತ್ತಿದ್ದುದರಿಂದ ಬಹಳ ಹಣವನ್ನು ಸಂಗ್ರಹಿಸಲಾಯಿತು.",
			"12": "<sup>12</sup> ಅರಸನೂ ಯೆಹೋಯಾದನೂ ಅದನ್ನು ಸರ್ವೇಶ್ವರನ ಆಲಯದ ಕೆಲಸವನ್ನು ನಡೆಸುವವರಿಗೆ ಒಪ್ಪಿಸಿದರು. ಇವರು ಸರ್ವೇಶ್ವರನ ಆಲಯದ ದುರಸ್ತಿ ಕೆಲಸಕ್ಕಾಗಿ, ಶಿಲ್ಪಿಗಾರರನ್ನೂ ಬಡಗಿಯರನ್ನೂ ಅದನ್ನು ಭದ್ರಪಡಿಸುವುದಕ್ಕಾಗಿ ಕಮ್ಮಾರರನ್ನೂ ಕಂಚುಗಾರರನ್ನೂ ಹಚ್ಚಿದರು.",
			"13": "<sup>13</sup> ಕೆಲಸ ಮಾಡುವವರು ಬಹು ಶ್ರಮವಹಿಸಿದ್ದರಿಂದ ದುರಸ್ತಿ ಕೆಲಸ ಮುಂದುವರೆಯಿತು. ಅವರು ದೇವಾಲಯವನ್ನು ಭದ್ರಪಡಿಸಿ ಪೂರ್ವಸ್ಥಿತಿಗೆ ತಂದರು.",
			"14": "<sup>14</sup> ಕೆಲಸ ಮುಗಿದಾಗ ಅವರು ಉಳಿದ ಹಣವನ್ನು ಅರಸನಿಗೂ ಯೆಹೋಯಾದನಿಗೂ ಒಪ್ಪಿಸಿದರು. ಇವರು ಅದರಿಂದ ಸರ್ವೇಶ್ವರನ ಆಲಯದಲ್ಲಿ ನಡೆವ ಆರಾಧನೆಗೆ ಹಾಗು ಬಲಿಯರ್ಪಣೆಗೆ ಉಪಯೋಗವಾಗುವ ಧೂಪಾರತಿ, ಬೆಳ್ಳಿಬಂಗಾರದ ಪಾತ್ರೆ, ಮೊದಲಾದ ಸಾಮಾನುಗಳನ್ನು ಮಾಡಿಸಿದರು. ಯೆಹೋಯಾದನ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ಸರ್ವೇಶ್ವರನ ಆಲಯದಲ್ಲಿ ಪ್ರತಿನಿತ್ಯವೂ ದಹನಬಲಿ ಸಮರ್ಪಣೆ ಆಗುತ್ತಿತ್ತು.",
			"15": "<sup>15</sup> ಅವನು ಮುಪ್ಪಿನ ಮುದುಕನಾಗಿ ಮರಣಹೊಂದಿದನು. ಸಾಯುವಾಗ ಅವನಿಗೆ ನೂರಮೂವತ್ತು ವರ್ಷವಾಗಿತ್ತು.",
			"16": "<sup>16</sup> ಅವನು ಇಸ್ರಯೇಲರ ವಿಷಯದಲ್ಲೂ ದೇವರ ಮತ್ತು ದೇವಾಲಯದ ವಿಷಯದಲ್ಲೂ ಹಲವಾರು ಸತ್ಕಾರ್ಯಗಳನ್ನು ಮಾಡಿದ್ದರಿಂದ ಅವನ ಶವವನ್ನು ದಾವೀದ ನಗರದೊಳಗೆ ರಾಜಸ್ಮಶಾನದಲ್ಲಿ ಸಮಾಧಿ ಮಾಡಿದರು.",
			"17": "<sup>17</sup> ಯೆಹೋಯಾದನು ಮೃತನಾದ ಮೇಲೆ, ಜುದೇಯದ ಪದಾಧಿಕಾರಿಗಳು ಅರಸ ಯೆಹೋವಾಷನ ಬಳಿಗೆ ಬಂದು, ಅವನಿಗೆ ಅಡ್ಡಬಿದ್ದು ಅವನನ್ನು ಒಲಿಸಿಕೊಂಡರು.",
			"18": "<sup>18</sup> ಅಂದಿನಿಂದ ಅವರು ತಮ್ಮ ಪೂರ್ವಜರ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಆಲಯವನ್ನು ನಿರಾಕರಿಸಿ, ಅಶೇರ ಸ್ತಂಭಗಳನ್ನೂ ವಿಗ್ರಹಗಳನ್ನೂ ಪೂಜಿಸ ತೊಡಗಿದರು. ಅವರ ಈ ಅಪರಾಧದಿಂದ ಜುದೇಯದ ಮೇಲೂ ಜೆರುಸಲೇಮಿನ ಮೇಲೂ ದೈವಕೋಪ ಎರಗಿತು.",
			"19": "<sup>19</sup> ಸರ್ವೇಶ್ವರ ಅವರನ್ನು ತಮ್ಮ ಕಡೆಗೆ ತಿರುಗಿಸಿಕೊಳ್ಳುವುದಕ್ಕಾಗಿ ಅವರ ಬಳಿಗೆ ಪ್ರವಾದಿಗಳನ್ನು ಕಳುಹಿಸಿದರು. ಇವರ ಮುಖಾಂತರ ಎಷ್ಟು ಎಚ್ಚರಿಸಿದರೂ ಅವರು ಕಿವಿಗೊಡಲಿಲ್ಲ.",
			"20": "<sup>20</sup> ಆಗ ಯಾಜಕ ಯೆಹೋಯಾದನ ಮಗ ಜೆಕರ್ಯನುದೇವಾತ್ಮನಿಂದ ಆವೇಶ ಉಳ್ಳವನಾದನು. ಅವನು ಜನರ ಎದುರಿಗೆ ಉನ್ನತಸ್ಥಾನದಲ್ಲಿ ನಿಂತು, “ದೇವರ ಮಾತನ್ನು ಕೇಳಿರಿ; ನೀವೇಕೆ ಸರ್ವೇಶ್ವರನ ಆಜ್ಞೆಗಳನ್ನು ಮೀರಿ, ನಿಮ್ಮನ್ನೆ ನಾಶಮಾಡಿಕೊಳ್ಳುತ್ತೀರಿ? ನೀವು ಸರ್ವೇಶ್ವರಸ್ವಾಮಿಯನ್ನು ಬಿಟ್ಟದ್ದರಿಂದ ಅವರೂ ನಿಮ್ಮನ್ನು ಬಿಟ್ಟುಬಿಟ್ಟಿದ್ದಾರೆ,” ಎಂದನು.",
			"21": "<sup>21</sup> ಆಗ ಅವರು ಅವನಿಗೆ ವಿರೋಧವಾಗಿ ಒಳಸಂಚುಮಾಡಿ,ಸರ್ವೇಶ್ವರನ ಆಲಯದ ಪ್ರಾಕಾರದಲ್ಲಿ ಅವನನ್ನು ಕಲ್ಲೆಸೆದು ಕೊಂದರು. ಇದು ಅರಸ ಯೆಹೋವಾಷನ ಅಪ್ಪಣೆಯಿಂದಲೇ ಆಯಿತು.",
			"22": "<sup>22</sup> ಜೆಕರ್ಯನ ತಂದೆ ಯೆಹೋಯಾದನಿಂದ ತನಗಾದ ಉಪಕಾರವನ್ನು ನೆನಪುಮಾಡಿಕೊಳ್ಳದೆ ಈ ಯೆಹೋವಾಷನು ಅವನ ಮಗನನ್ನು ಕೊಲ್ಲಿಸಿದನು. ಜೆಕರೀಯನು ಸಾಯುವಾಗ, “ಸರ್ವೇಶ್ವರಸ್ವಾಮಿಯೇ ಇದನ್ನು ನೋಡಿ ತಕ್ಕ ಶಾಸ್ತಿಮಾಡಲಿ,” ಎಂದನು.",
			"23": "<sup>23</sup> ವರ್ಷಾಂತ್ಯದಲ್ಲಿ ಸಿರಿಯಾದ ಸೈನ್ಯ ಯೆಹೋವಾಷನ ಮೇಲೆ ದಾಳಿಮಾಡಲು ಹೊರಟಿತು. ಆ ಸೈನ್ಯದವರು ಜುದೇಯ ನಾಡಿನೊಳಗೆ ನುಗ್ಗಿ ಜೆರುಸಲೇಮಿಗೆ ಬಂದು, ಇಸ್ರಯೇಲರ ಎಲ್ಲ ಜನಾಧಿಪತಿಗಳನ್ನು ನಿರ್ನಾಮಮಾಡಿ ಅವರಲ್ಲಿ ಸಿಕ್ಕಿದ ಕೊಳ್ಳೆಯನ್ನೆಲ್ಲಾ ದಮಸ್ಕದ ಅರಸನಿಗೆ ಕಳುಹಿಸಿದರು.",
			"24": "<sup>24</sup> ಸಿರಿಯಾದ ಸೈನ್ಯದಿಂದ ಬಂದ ಗುಂಪು ಚಿಕ್ಕದಾಗಿದ್ದರೂ ಯೆಹೂದ್ಯರು ತಮ್ಮ ಪೂರ್ವಜರ ದೇವರಾದ ಸರ್ವೇಶ್ವರನನ್ನು ಬಿಟ್ಟುಬಿಟ್ಟದ್ದರಿಂದ ಶಿಕ್ಷೆಗೆ ಗುರಿಯಾದರು. ತಮ್ಮದು ಮಹಾಸೈನ್ಯವಾಗಿದ್ದರೂ ಸಿರಿಯಾದವರ ಕೈಯಲ್ಲಿ ಸೋಲನ್ನು ಅನುಭವಿಸಿದರು; ಯೆಹೋವಾಷನಿಗೆ ದಂಡನೆಯಾಯಿತು.",
			"25": "<sup>25</sup> ಕಠಿಣವಾಗಿ ಗಾಯಗೊಂಡಿದ್ದ ಅವನನ್ನು ಸಿರಿಯಾದವರು ಬಿಟ್ಟುಹೋದರು. ಅವನ ಸೇವಕರೇ, ಯಾಜಕ ಯೆಹೋಯಾದನ ಮಗನ ವಧೆಯ ನಿಮಿತ್ತ, ಅವನಿಗೆ ವಿರೋಧವಾಗಿ ಒಳಸಂಚುಮಾಡಿ ಅವನನ್ನು ಹಾಸಿಗೆಯಲ್ಲೇ ಕೊಂದುಹಾಕಿದರು. ಅವನ ಶವವನ್ನು ದಾವೀದನಗರದೊಳಗೆ ಸಮಾಧಿಮಾಡಿದರು; ಆದರೆ ರಾಜಸ್ಮಶಾನದಲ್ಲಿ ಮಾಡಲಿಲ್ಲ.",
			"26": "<sup>26</sup> ಅಮ್ಮೋನ್‍ದೇಶದ ಶಿಮ್ಯಾತೆಂಬಾಕೆಯ ಮಗ ಜಾಬಾದ್‍, ಮೋವಾಬ್‍ ದೇಶದ ಶಿಮ್ರೀತೆಂಬಾಕೆಯ ಮಗ ಯೆಹೋಜಾಬಾದ್ ಎಂಬವರೇ ಅವನಿಗೆ ವಿರೋಧವಾಗಿ ಒಳಸಂಚು ಮಾಡಿದವರು.",
			"27": "<sup>27</sup> ಅವನ ಮಕ್ಕಳ ವಿವರ, ಅವನಿಗೆ ವಿರುದ್ಧ ಹೇಳಲಾದ ಅನೇಕ ದೈವೋಕ್ತಿಗಳು ಹಾಗು ದೇವಾಲಯದ ದುರಸ್ತಿಯ ವೃತ್ತಾಂತ ಇವು ರಾಜರ ಗ್ರಂಥದ ವ್ಯಾಖ್ಯಾನದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗ ಅಮಚ್ಯನು ಅರಸನಾದನು."
		},
		"25": {
			"1": "<sup>1</sup> ಅಮಚ್ಯನು ಪಟ್ಟಕ್ಕೆ ಬಂದಾಗ ಇಪ್ಪತ್ತೈದು ವರ್ಷದವನಾಗಿದ್ದನು. ಅವನು ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಇಪ್ಪತ್ತೊಂಬತ್ತು ವರ್ಷ ಆಳಿದನು. ಜೆರುಸಲೇಮಿನವಳಾದ ಯೆಹೋವದ್ಧಾನ್ ಎಂಬಾಕೆ ಅವನ ತಾಯಿ.",
			"2": "<sup>2</sup> ಅವನು ಸರ್ವೇಶ್ವರನ ಚಿತ್ತಾನುಸಾರ ನಡೆದನು; ಆದರೆ ತದೇಕಚಿತ್ತದಿಂದಲ್ಲ.",
			"3": "<sup>3</sup> ಅವನು ರಾಜ್ಯವನ್ನು ಭದ್ರಪಡಿಸಿಕೊಂಡ ಕೂಡಲೆ, ತನ್ನ ತಂದೆಯನ್ನು ಕೊಂದ ಸೇವಕರನ್ನು ಕೊಲ್ಲಿಸಿದನು.",
			"4": "<sup>4</sup> ಆದರೆ ‘ಮಕ್ಕಳು ಮಾಡಿದ ಪಾಪಕ್ಕೆ ತಂದೆಗಾಗಲಿ, ತಂದೆ ಮಾಡಿದ ಪಾಪಕ್ಕೆ ಮಕ್ಕಳಿಗಾಗಲಿ ಮರಣ ಶಿಕ್ಷೆಯಾಗಬಾರದು; ಪ್ರತಿಯೊಬ್ಬನೂ ತನ್ನ ಪಾಪದ ಫಲವನ್ನು ತಾನೇ ಅನುಭವಿಸಬೇಕು’ ಎಂಬುದಾಗಿ ಮೋಶೆಯ ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿರುವ ಸರ್ವೇಶ್ವರನ ಅಪ್ಪಣೆಯನ್ನು ನೆನಪುಮಾಡಿಕೊಂಡು, ಅವರ ಮಕ್ಕಳನ್ನು ಕೊಲ್ಲಿಸಲಿಲ್ಲ.",
			"5": "<sup>5</sup> ಆಮೇಲೆ ಅಮಚ್ಯನು ಯೆಹೂದ್ಯರನ್ನು ಸಹಸ್ರಾಧಿಪತಿ ಹಾಗು ಶತಾಧಿಪತಿಗಳ ಕೈಕೆಳಗೆ ಒಟ್ಟುಗೂಡಿಸಿ, ಯೆಹೂದ ಮತ್ತು ಬೆನ್ಯಾಮೀನ್ ಕುಲಗಳ ಜನರನ್ನು ಆಯಾ ಗೋತ್ರಗಳ ಪ್ರಕಾರ ಅವರನ್ನು ನಿಲ್ಲಿಸಿ, ಇಪ್ಪತ್ತು ವರ್ಷದವರು ಮತ್ತು ಅದಕ್ಕೆ ಮೇಲ್ಪಟ್ಟ ವಯಸ್ಸುಳ್ಳವರನ್ನು ಲೆಕ್ಕಮಾಡಿಸಿದನು. ಅವರಲ್ಲಿ ಬರ್ಜಿ, ಗುರಾಣಿಗಳನ್ನು ಹಿಡಿದುಕೊಂಡು ಯುದ್ಧಕ್ಕೆ ಹೊರಡತಕ್ಕ ಶೂರರು ಮೂರು ಲಕ್ಷಮಂದಿ ಸಿಕ್ಕಿದರು.",
			"6": "<sup>6</sup> ಇದಲ್ಲದೆ 3400 ಕಿಲೋಗ್ರಾಂ ಬೆಳ್ಳಿಯನ್ನು ಕೊಟ್ಟು ಇಸ್ರಯೇಲರಿಂದಲೂ ಲಕ್ಷಮಂದಿ ಯೋಧರನ್ನು ತರಿಸಿದನು.",
			"7": "<sup>7</sup> ಆಗ ದೈವಪುರುಷನೊಬ್ಬನು ಅವನ ಬಳಿಗೆ ಬಂದು, “ಅರಸರೇ, ಇಸ್ರಯೇಲ್ ಸೈನ್ಯದವರು ನಿಮ್ಮ ಜೊತೆಯಲ್ಲಿ ಯುದ್ಧಕ್ಕೆ ಹೋಗಬಾರದು. ಸರ್ವೇಶ್ವರ ಎಫ್ರಯಿಮರಾದ ಇಸ್ರಯೇಲರನ್ನೆಲ್ಲಾ ಕೈಬಿಟ್ಟಿದ್ದಾರೆ.",
			"8": "<sup>8</sup> ನೀವೇ ಹೋಗಿ ಕಾರ್ಯವನ್ನು ನಿರ್ವಹಿಸಿರಿ. ಧೈರ್ಯದಿಂದ ಯುದ್ಧಮಾಡಿ. ಹಾಗೆ ಮಾಡದಿದ್ದರೆ ದೇವರು ಶತ್ರುಗಳಿಂದ ನಿಮ್ಮನ್ನು ಅಪಜಯಗೊಳಿಸುವರು. ಜಯಾಪಜಯಗಳನ್ನು ನೀಡಲು ಅವರು ಶಕ್ತರು ಆಗಿರುತ್ತಾರಲ್ಲವೆ?” ಎಂದನು.",
			"9": "<sup>9</sup> ಅಮಚ್ಯನು ಆ ದೈವಪುರುಷನನ್ನು, “ಹಾಗಾದರೆ ನಾನು ಇಸ್ರಯೇಲ್ ಸೈನ್ಯಕ್ಕಾಗಿ 3400 ಕಿಲೋಗ್ರಾಂ ಬೆಳ್ಳಿಯನ್ನು ಕೊಟ್ಟುಬಿಟ್ಟೆನಲ್ಲಾ, ಏನು ಮಾಡುವುದು?,” ಎಂದು ಕೇಳಿದನು. ಅವನು, “ಸರ್ವೇಶ್ವರ ಅದಕ್ಕಿಂತ ಎಷ್ಟೋ ಹೆಚ್ಚಾಗಿ ನಿಮಗೆ ಕೊಡಬಲ್ಲರು,” ಎಂದನು.",
			"10": "<sup>10</sup> ಆಗ ಅಮಚ್ಯನು ಎಫ್ರಯಿಮರಿಂದ ಬಂದ ಸೈನಿಕರನ್ನು ಬೇರೆಮಾಡಿ, ಅವರನ್ನು ಅವರ ನಾಡಿಗೆ ಕಳುಹಿಸಿದನು. ಅವರು ಯೆಹೂದ್ಯರ ಮೇಲೆ ಕಡುಕೋಪಗೊಂಡು, ಬಲುಸಿಟ್ಟಿನಿಂದ ತಮ್ಮ ನಾಡಿಗೆ ಹೋದರು.",
			"11": "<sup>11</sup> ಅಮಚ್ಯನಾದರೋ ಧೈರ್ಯದಿಂದ ತನ್ನ ಸೈನ್ಯವನ್ನು ಕರೆದುಕೊಂಡು, ಉಪ್ಪಿನ ಕಣಿವೆಗೆ ಹೋಗಿ, ಸೇಯೀರ್ಯರಲ್ಲಿ ಹತ್ತು ಸಾವಿರ ಮಂದಿಯನ್ನು ವಧಿಸಿದನು.",
			"12": "<sup>12</sup> ಯೆಹೂದ್ಯರು ಬೇರೆ ಹತ್ತು ಸಾವಿರಮಂದಿಯನ್ನು ಜೀವಸಹಿತವಾಗಿ ಹಿಡಿದು, ಕಡಿದಾದ ಬಂಡೆಯ ತುದಿಗೆ ಒಯ್ದು, ಅವರೆಲ್ಲರೂ ಚೂರುಚೂರಾಗುವ ಹಾಗೆ ಅವರನ್ನು ಕೆಳಕ್ಕೆ ದೊಬ್ಬಿದರು.",
			"13": "<sup>13</sup> ಅಷ್ಟರಲ್ಲಿ ಅಮಚ್ಯನು ತನ್ನ ಜೊತೆಯಲ್ಲಿ ಯುದ್ಧಕ್ಕೆ ಬರಬಾರದೆಂದು ಹಿಂದಕ್ಕೆ ಕಳುಹಿಸಿದ ಗುಂಪಿನವರು, ಸಮಾರಿಯಾಕ್ಕೂ ಬೇತ್ಹೋರೋನಿಗೂ ಮಧ್ಯದಲ್ಲಿರುವ ಜುದೇಯ ಪಟ್ಟಣಗಳ ಮೇಲೆ ದಾಳಿಮಾಡಿ, ಮೂರು ಸಾವಿರ ಜನರನ್ನು ಕೊಂದು, ದೊಡ್ಡ ಕೊಳ್ಳೆಯನ್ನು ಕೂಡಿಸಿಕೊಂಡು ಹೋದರು.",
			"14": "<sup>14</sup> ಅಮಚ್ಯನು ಎದೋಮ್ಯರನ್ನು ಸೋಲಿಸಿ ಮನೆಗೆ ಬಂದ ಮೇಲೆ, ತಾನು ತೆಗೆದುಕೊಂಡು ಬಂದಿದ್ದ ಸೇಯೀರಿನವರ ದೇವತಾಪ್ರತಿಮೆಗಳನ್ನು ತನ್ನ ದೇವರುಗಳೆಂದು ನಿಲ್ಲಿಸಿ, ಅವುಗಳಿಗೆ ಅಡ್ಡಬಿದ್ದು ಧೂಪಾರತಿ ಎತ್ತುತ್ತಿದ್ದನು.",
			"15": "<sup>15</sup> ಆಗ ಸರ್ವೇಶ್ವರ ಅಮಚ್ಯನ ಮೇಲೆ ಬಹಳವಾಗಿ ಕೋಪಗೊಂಡು, ಅವನ ಬಳಿಗೆ ಪ್ರವಾದಿಯನ್ನು ಕಳುಹಿಸಿ, “ನಿನ್ನ ಕೈಯಿಂದ ತಮ್ಮ ಜನರನ್ನು ತಪ್ಪಿಸಲಾರದೆಹೋದ ಅನ್ಯದೇವತೆಗಳಲ್ಲಿ ಏಕೆ ಇಷ್ಟು ಭಕ್ತಿ?” ಎಂದು ಹೇಳಿಸಿದರು.",
			"16": "<sup>16</sup> ಈ ಮಾತನ್ನು ಹೇಳಿದ ಪ್ರವಾದಿಗೆ ಅರಸನು, “ನಿನ್ನನ್ನು ನಾನು ಆಲೋಚನಾಮಂತ್ರಿಯನ್ನಾಗಿ ನೇಮಿಸಲಿಲ್ಲ; ಬಾಯಿಮುಚ್ಚುವಿಯೋ: ಅಥವಾ ಏಟು ತಿನ್ನುವಿಯೋ?’ ಎಂದನು. ಅದಕ್ಕೆ ಅವನು, “ನೀವು ನನ್ನ ಬುದ್ಧಿವಾದವನ್ನು ಆಲಿಸದೆ ಹೀಗೆ ಮಾಡುವುದರಿಂದ ದೇವರು ನಿಮ್ಮನ್ನು ನಾಶಮಾಡಬೇಕೆಂದು ನಿಶ್ಚಯಿಸಿಕೊಂಡಿದ್ದಾರೆಂದು ನಾನು ಬಲ್ಲೆ,” ಎಂದು ಹೇಳಿ ಸುಮ್ಮನಾದನು.",
			"17": "<sup>17</sup> ಅನಂತರ ಜುದೇಯದ ಅರಸ ಅಮಚ್ಯನು ಸಮಾಲೋಚನೆ ನಡೆಸಿ, ಯೇಹುವಿನ ಮೊಮ್ಮಗನೂ ಯೆಹೋವಾಹಾಜನ ಮಗನೂ ಇಸ್ರಯೇಲರ ಅರಸನೂ ಆದ ಯೋವಾಷನಿಗೆ, “ನಾವು ಒಬ್ಬರನ್ನೊಬ್ಬರು ಎದುರುಗೊಳ್ಳೋಣ,” ಎಂದು ದೂತರ ಮುಖಾಂತರ ಹೇಳಿಸಿದನು.",
			"18": "<sup>18</sup> ಆಗ ಇಸ್ರಯೇಲರ ಅರಸ ಯೋವಾಷನು ಯೆಹೂದ್ಯರ ಅರಸ ಅಮಚ್ಯನಿಗೆ, “ಲೆಬನೋನಿನ ಒಂದು ಮುಳ್ಳುಗಿಡ ಅಲ್ಲಿನ ದೇವದಾರು ಮರಕ್ಕೆ, ‘ನನ್ನ ಮಗನಿಗೆ ನಿನ್ನ ಮಗಳನ್ನು ಕೊಡು’ ಎಂದು ಹೇಳಿಕಳುಹಿಸಿತು. ತುಸುಹೊತ್ತಾದ ಮೇಲೆ, ಲೆಬನೋನಿನ ಒಂದು ಕಾಡುಮೃಗ ಆ ಮಾರ್ಗವಾಗಿ ಹಾದುಹೋಗುತ್ತಿರುವಾಗ, ಆ ಮುಳ್ಳುಗಿಡವನ್ನು ತುಳಿದುಬಿಟ್ಟಿತು.",
			"19": "<sup>19</sup> ಅಹಹ! ಎದೋಮ್ಯರನ್ನು ಸೋಲಿಸಿ ಬಹುಕೀರ್ತಿ ಪಡೆದುಕೊಂಡೆ ಎಂದು ನೀನು ಹೆಮ್ಮೆಗೊಂಡಿರುವೆ! ಸುಮ್ಮನೆ ಮನೆಯಲ್ಲಿ ಕುಳಿತುಕೊಳ್ಳಬಾರದೆ? ನನ್ನನ್ನು ಕೆಣಕಿ ನಿನಗೂ ನಿನ್ನ ರಾಜ್ಯಕ್ಕೂ ಏಕೆ ಕೇಡನ್ನು ತಂದುಕೊಳ್ಳುವೆ,” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"20": "<sup>20</sup> ಅಮಚ್ಯನು ಆ ಮಾತನ್ನು ಲಕ್ಷಿಸಲಿಲ್ಲ; ಹಾಗಾಗಬೇಕೆಂದೇ ದೇವರ ಸಂಕಲ್ಪವಾಗಿತ್ತು. ಯೆಹೂದ್ಯರೂ ಎದೋಮ್ಯರ ದೇವತೆಗಳಲ್ಲಿ ಭಕ್ತಿಯನ್ನಿಟ್ಟದ್ದರಿಂದ ಅವರನ್ನು ಪರಾಧೀನ ಮಾಡಬೇಕೆಂದು ದೇವರು ನಿರ್ಣಯಿಸಿದ್ದರು.",
			"21": "<sup>21</sup> ಇಸ್ರಯೇಲರ ಅರಸ ಯೋವಾಷನು ಯುದ್ಧಕ್ಕೆ ಹೊರಟನು; ಅವನೂ ಜುದೇಯದ ಅರಸ ಅಮಚ್ಯನೂ ಜುದೇಯ ಪ್ರಾಂತದ ಬೇತ್ಷೆಮೆಷಿನಲ್ಲಿ ಒಬ್ಬರನ್ನೊಬ್ಬರು ಎದುರುಗೊಂಡರು.",
			"22": "<sup>22</sup> ಯೆಹೂದ್ಯರು ಇಸ್ರಯೇಲರಿಗೆ ಸೋತು ತಮ್ಮ ತಮ್ಮ ನಿವಾಸಗಳಿಗೆ ಓಡಿಹೋದರು.",
			"23": "<sup>23</sup> ಇಸ್ರಯೇಲರ ಅರಸ ಯೋವಾಷನು, ಯೆಹೋವಹಾಜನ ಮೊಮ್ಮಗನೂ ಯೆಹೋವಾಷನ ಮಗನೂ ಜುದೇಯದ ಅರಸನೂ ಆದ ಅಮಚ್ಯನನ್ನು ಬೇತ್ಷೆಮೆಷಿನಲ್ಲಿ ಸೆರೆಹಿಡಿದನು. ಅವನನ್ನು ಜೆರುಸಲೇಮಿಗೆ ಕರೆದುಕೊಂಡು ಬಂದು, ಎಫ್ರಯಿಮ್ ಬಾಗಿಲಿಗೂ ಮೂಲೆಯ ಬಾಗಿಲಿಗೂ ಮಧ್ಯದಲ್ಲಿದ್ದ ಜೆರುಸಲೇಮಿನ ಗೋಡೆಯನ್ನು ಸುಮಾರು ಇನ್ನೂರು ಮೀಟರಿನಷ್ಟು ಕೆಡವಿಬಿಟ್ಟನು.",
			"24": "<sup>24</sup> ಇದಲ್ಲದೆ, ಅವನು ಓಬೇದೆದೋಮನ ವಶದಲ್ಲಿದ್ದ ದೇವಾಲಯದ ಎಲ್ಲ ಬೆಳ್ಳಿಬಂಗಾರವನ್ನೂ ಪಾತ್ರೆಗಳನ್ನೂ ಅರಮನೆಯ ಭಂಡಾರಗಳಲ್ಲಿದ್ದುದೆಲ್ಲವನ್ನೂ ತೆಗೆದುಕೊಂಡು, ಹೊಣೆಗಾರರನ್ನಾಗಿ ಕೆಲವರನ್ನು ಸೆರೆಹಿಡಿದು ಸಮಾರಿಯಕ್ಕೆ ಹಿಂದಿರುಗಿದನು.",
			"25": "<sup>25</sup> ಇಸ್ರಯೇಲರ ಅರಸನೂ ಯೆಹೋವಾಹಾಜನ ಮಗನೂ ಆದ ಯೋವಾಷನು ಸತ್ತ ನಂತರ, ಜುದೇಯದ ಅರಸ ಯೆಹೋವಾಷನ ಮಗ ಅಮಚ್ಯನು ಇನ್ನೂ ಹದಿನೈದು ವರ್ಷ ಬದುಕಿದ್ದನು.",
			"26": "<sup>26</sup> ಉಳಿದ ಅವನ ಪೂರ್ವೋತ್ತರ ಚರಿತ್ರೆಯು ಯೆಹೂದ್ಯರ ಮತ್ತು ಇಸ್ರಯೇಲರ ರಾಜರ ಗ್ರಂಥದಲ್ಲಿ ಲಿಖಿತವಾಗಿದೆ.",
			"27": "<sup>27</sup> ಅಮಚ್ಯನು ಸರ್ವೇಶ್ವರನ ಭಕ್ತಿಯನ್ನು ಬಿಟ್ಟಂದಿನಿಂದ ಜೆರುಸಲೇಮಿನವರು ಅವನಿಗೆ ವಿರುದ್ಧ ಒಳಸಂಚು ಮಾಡುತ್ತಿದ್ದರು. ಆದುದರಿಂದ ಅವನು ಲಾಕೀಷಿಗೆ ಓಡಿಹೋದನು. ಆದರೆ ಅವರು ಆಳುಗಳನ್ನು ಕಳುಹಿಸಿ ಅವನನ್ನು ಅಲ್ಲಿಯೇ ಕೊಲ್ಲಿಸಿದರು.",
			"28": "<sup>28</sup> ಅವನ ಜನರು ಆ ಶವವನ್ನು ಕುದುರೆಯ ಮೇಲೆ ಜೆರುಸಲೇಮಿಗೆ ತಂದು, ಜುದೇಯದ ರಾಜಧಾನಿಯಲ್ಲಿ, ಅವನ ಪಿತೃ ಸ್ಮಶಾನದಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು."
		},
		"26": {
			"1": "<sup>1</sup> ಅನಂತರ ಎಲ್ಲ ಯೆಹೂದ್ಯರೂ ಕೂಡಿ ಹದಿನಾರು ವರ್ಷದವನಾಗಿದ್ದ ಉಜ್ಜೀಯನನ್ನು ಅವನ ತಂದೆ ಅಮಚ್ಯನಿಗೆ ಬದಲಾಗಿ ಅರಸನನ್ನಾಗಿ ಮಾಡಿದರು.",
			"2": "<sup>2</sup> ಅರಸನು ಸತ್ತು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಿದ ಮೇಲೆ ಇವನು ಏಲೋತ್ ಪಟ್ಟಣವನ್ನು ಮತ್ತೆ ಜುದೇಯ ರಾಜ್ಯಕ್ಕೆ ಸೇರಿಸಿಕೊಂಡು ಭದ್ರಪಡಿಸಿದನು.",
			"3": "<sup>3</sup> ಉಜ್ಜೀಯನು ಪಟ್ಟಕ್ಕೆ ಬಂದಾಗ ಅವನಿಗೆ ಹದಿನಾರು ವರ್ಷ ವಯಸ್ಸು. ಅವನು ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಐವತ್ತ ಎರಡು ವರ್ಷ ಆಳಿದನು. ಜೆರುಸಲೇಮಿನವಳಾದ ಯೆಕೊಲ್ಯ ಎಂಬಾಕೆ ಅವನ ತಾಯಿ.",
			"4": "<sup>4</sup> ಅವನು ತನ್ನ ತಂದೆ ಅಮಚ್ಯನಂತೆ ಸರ್ವೇಶ್ವರನ ಚಿತ್ತಾನುಸಾರವಾಗಿ ನಡೆದನು.",
			"5": "<sup>5</sup> ದೇವಭಕ್ತಿಯನ್ನು ಬೋಧಿಸುತ್ತಿದ್ದ ಜೆಕರ್ಯನ ಜೀವಮಾನ ಕಾಲದಲ್ಲಿ ಅವನು ದೇವರನ್ನು ಅವಲಂಬಿಸಿದ್ದ ಕಾಲಾವಧಿಯಲ್ಲೇ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಅವನನ್ನು ಅಭಿವೃದ್ಧಿಗೆ ತಂದದ್ದು.",
			"6": "<sup>6</sup> ಉಜ್ಜೀಯನು ಫಿಲಿಷ್ಟಿಯರ ಮೇಲೆ ಯುದ್ಧಕ್ಕೆ ಹೊರಟು ಗತ್‍ಊರು, ಯಬ್ನೆ, ಅಷ್ಡೋದ್ ಎಂಬ ಪಟ್ಟಣಗಳ ಗೋಡೆಗಳನ್ನು ಕೆಡವಿಬಿಟ್ಟು ಅಷ್ಡೋದಿನ ಮತ್ತು ಫಿಲಿಷ್ಟಿಯರ ಪ್ರಾಂತಗಳಲ್ಲಿ ಪಟ್ಟಣಗಳನ್ನು ಕಟ್ಟಿಸಿದನು.",
			"7": "<sup>7</sup> ಫಿಲಿಷ್ಟಿಯರೊಡನೆ ಗೂರ್‍ಬಾಳಿನಲ್ಲಿರುವ ಅರೇಬಿಯರೊಡನೆ ಹಾಗು ಮೆಗೂನ್ಯರೊಡನೆ ಯುದ್ಧಮಾಡುವಾಗ ಅವನಿಗೆ ದೇವರ ಸಹಾಯ ದೊರೆಯಿತು.",
			"8": "<sup>8</sup> ಅಮ್ಮೋನಿಯರೂ ಉಜ್ಜೀಯನಿಗೆ ಕಪ್ಪವನ್ನು ಕೊಡುತ್ತಿದ್ದರು. ಅವನು ಅಧಿಕ ಬಲ ಉಳ್ಳವನಾಗಿ ಅವನ ಹೆಸರು ಈಜಿಪ್ಟಿನ ಗಡಿಗಳವರೆಗೂ ಹಬ್ಬಿತು.",
			"9": "<sup>9</sup> ಉಜ್ಜೀಯನು ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಮೂಲೆ ಬಾಗಿಲು, ತಗ್ಗಿನ ಬಾಗಿಲು, ಗೋಡೆ ತಿರುಗುವ ಭಾಗ, ಇವುಗಳ ಮೇಲೆ ಬುರುಜುಗಳನ್ನು ಕಟ್ಟಿಸಿ ಭದ್ರಗೊಳಿಸಿದನು.",
			"10": "<sup>10</sup> ಅವನಿಗೆ ಇಳಕಲಿನ ಪ್ರದೇಶದಲ್ಲೂ ತಪ್ಪಲ ಸೀಮೆಯಲ್ಲೂ ಅಲ್ಲದೆ ಅಡವಿಯಲ್ಲೂ ಪಶುಪ್ರಾಣಿಗಳ ದೊಡ್ಡಮಂದೆಗಳು ಇದ್ದವು. ಆದುದರಿಂದ ಆ ಅಡವಿಯಲ್ಲಿ ಬುರುಜುಗಳನ್ನು ಕಟ್ಟಿಸಿ ಹಲವು ಬಾವಿಗಳನ್ನು ತೋಡಿಸಿದನು. ವ್ಯವಸಾಯದಲ್ಲಿ ಅವನಿಗೆ ಅಭಿರುಚಿ ಇದ್ದುದರಿಂದ ಗುಡ್ಡ, ಫಲವತ್ತಾದ ಬಯಲು, ಇವುಗಳಲ್ಲಿ ಹೊಲ ಹಾಗು ದ್ರಾಕ್ಷಿತೋಟಗಳ ಕೆಲಸಗಾರರನ್ನು ನೇಮಿಸಿದನು.",
			"11": "<sup>11</sup> ಇದಲ್ಲದೆ, ಯುದ್ಧಕ್ಕೆ ಬೇಕಾದ ಸೈನ್ಯವೂ ಉಜ್ಜೀಯನಿಗಿತ್ತು. ಲೇಖಕನಾದ ಯೆಗೀಯೇಲ್, ಅಧಿಕಾರಿಯಾದ ಮಾಸೇಯ, ಇವರು ಅರಸನ ಸರದಾರರಲ್ಲಿ ಒಬ್ಬನಾದ ಹನನ್ಯನ ಆಜ್ಞಾನುಸಾರ, ಅವನ ಪಟ್ಟಿಯನ್ನು ಬರೆದಿದ್ದರು. ಆಯಾ ಗುಂಪುಗಳು ಪಟ್ಟಿಯ ಕ್ರಮಾನುಸಾರ ಯುದ್ಧಕ್ಕೆ ಹೊರಡುತ್ತಿದ್ದವು.",
			"12": "<sup>12</sup> ರಣವೀರರು ಆಗಿದ್ದ ಎಲ್ಲ ಗೋತ್ರಪ್ರಧಾನರ ಸಂಖ್ಯೆ ಎರಡು ಸಾವಿರದ ಆರುನೂರು.",
			"13": "<sup>13</sup> ಇವರ ಕೈಕೆಳಗಿದ್ದ ಯೋಧರ ಸಂಖ್ಯೆ, ಮೂರು ಲಕ್ಷದ ಏಳು ಸಾವಿರದ ಐನೂರು; ಇವರು ಅರಸನ ಸೇವೆಯಲ್ಲಿದ್ದು, ಅವನ ವೈರಿಗಳಿಗೆ ವಿರುದ್ಧ, ಪರಾಕ್ರಮದಿಂದ ಯುದ್ಧ ಮಾಡುತ್ತಿದ್ದರು.",
			"14": "<sup>14</sup> ಉಜ್ಜೀಯನು ಈ ಎಲ್ಲ ಸೈನ್ಯದವರಿಗೆ ಬೇಕಾದ ಗುರಾಣಿ, ಬರ್ಜಿ, ಶಿರಸ್ತ್ರಾಣ, ಕವಚ, ಬಿಲ್ಲು, ಕವಣೆಯಕಲ್ಲು ಇವುಗಳನ್ನು ಒದಗಿಸಿಕೊಟ್ಟನು.",
			"15": "<sup>15</sup> ಇದಲ್ಲದೆ, ಅವನು ಬಾಣಗಳನ್ನೂ ದೊಡ್ಡ ಕಲ್ಲುಗಳನ್ನೂ ಎಸೆಯುವುದಕ್ಕಾಗಿ, ತಜ್ಞರ ಕಲ್ಪನೆಯ ಮೇರೆಗೆ ಯಂತ್ರಗಳನ್ನು ಮಾಡಿಸಿ, ಅವುಗಳನ್ನು ಜೆರುಸಲೇಮಿನ ಬುರುಜುಗಳ ಮೇಲೆಯೂ ಆಳುವೇರಿಗಳ ಮೇಲೆಯೂ ಇಡಿಸಿದ್ದನು. ಬಲಿಷ್ಠನಾಗುವವರೆಗೂ ಅವನ ದೇವರ ಸಹಾಯವನ್ನು ಆಶ್ಚರ್ಯಕರವಾದ ರೀತಿಯಲ್ಲಿ ಪಡೆಯುತ್ತಿದ್ದನು; ಅವನ ಕೀರ್ತಿ ಬಲು ದೂರದವರೆಗೆ ಹಬ್ಬಿತ್ತು.",
			"16": "<sup>16</sup> ಆದರೆ ಅವನು ಬಲಿಷ್ಠನಾದ ಮೇಲೆ, ಗರ್ವಿಷ್ಠನೂ ಭ್ರಷ್ಠನೂ ಆದನು. ತನ್ನ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ದ್ರೋಹಿಯಾಗಿ, ಧೂಪವೇದಿಯ ಮೇಲೆ ತಾನೇ ಧೂಪಾರತಿ ಎತ್ತಬೇಕೆಂದು ಸರ್ವೇಶ್ವರನ ಆಲಯದ ಒಳಕ್ಕೆ ಹೋದನು.",
			"17": "<sup>17</sup> ಆಗ ಮಹಾಯಾಜಕ ಅಜರ್ಯನು ಹಾಗು ಧೈರ್ಯಶಾಲಿಗಳಾದ ಎಂಬತ್ತು ಮಂದಿ ಇತರ ಯಾಜಕರು, ಅರಸ ಉಜ್ಜೀಯನ ಹಿಂದೆಯೇ ಹೋಗಿ ಅವನೆದುರಿಗೆ ನಿಂತು,",
			"18": "<sup>18</sup> “ಉಜ್ಜೀಯರೇ, ಸರ್ವೇಶ್ವರನಿಗೆ ಧೂಪಾರತಿ ಎತ್ತುವುದು ನಿಮ್ಮ ಕೆಲಸವಲ್ಲ; ಅದಕ್ಕೆ ಆರೋನನ ಸಂತಾನದವರ ಯಾಜಕರು ಪ್ರತಿಷ್ಠಿತರಾಗಿದ್ದಾರೆ. ಪವಿತ್ರಾಲಯವನ್ನು ಬಿಟ್ಟು ಹೋಗಿ; ನೀವು ಮಾಡುತ್ತಿರುವುದು ದ್ರೋಹ. ಇದಕ್ಕೆ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಂದ ನಿಮಗೆ ಮರ್ಯಾದೆ ದೊರಕಲಾರದು,” ಎಂದರು.",
			"19": "<sup>19</sup> ಧೂಪಾರತಿ ಎತ್ತಬೇಕೆಂದು ಕೈಯಲ್ಲಿ ಧೂಪಕಳಸ ವನ್ನು ಹಿಡಿದಿದ್ದ ಉಜ್ಜೀಯನು ಕೋಪಗೊಂಡನು . ಸರ್ವೇಶ್ವರನ ಆಲಯದ ಧೂಪವೇದಿಯ ಬಳಿಯಲ್ಲಿ ನಿಂತು, ಯಾಜಕ ರೊಡನೆ ಅವನು ಸಿಟ್ಟಿನಿಂದ ಮಾತಾಡುತ್ತಿರುವಾಗಲೆ ಯಾಜಕರ ಸಮಕ್ಷಮದಲ್ಲೇ, ಅವನ ಹಣೆಯಲ್ಲಿ ಕುಷ್ಠ ಹುಟ್ಟಿತು.",
			"20": "<sup>20</sup> ಮಹಾಯಾಜಕನಾದ ಅಜರ್ಯನೂ ಎಲ್ಲ ಯಾಜಕರೂ ಅವನನ್ನು ದೃಷ್ಟಿಸಿ ನೋಡಿದಾಗ, ಅವನ ಹಣೆಯಲ್ಲಿ ಕುಷ್ಠ ಕಂಡುಬಂದ ಕಾರಣ, ಅವನನ್ನು ಶೀಘ್ರವಾಗಿ ಅಲ್ಲಿಂದ ಹೊರಗಟ್ಟಿದರು. ಸರ್ವೇಶ್ವರ, ತನ್ನನ್ನು ದಂಡಿಸಿದ್ದಾರೆಂದು ಅವನಿಗೆ ತಿಳಿಯಿತು. ಎಂದೇ ಅವನು ಶೀಘ್ರವಾಗಿ ಹೊರಗೆಹೋದನು.",
			"21": "<sup>21</sup> ಉಜ್ಜೀಯನು ಜೀವದಿಂದಿರುವವರೆಗೂ ಕುಷ್ಠರೊಗಿಯಾಗಿದ್ದು, ಸರ್ವೇಶ್ವರನ ಆಲಯಕ್ಕೆ ಬಾರದಂತೆ ಬಹಿಷ್ಕೃತನಾದನು. ಕುಷ್ಠದ ನಿಮಿತ್ತ ಅವನು ಪ್ರತ್ಯೇಕವಾದ ಮನೆಯಲ್ಲಿ ವಾಸಿಸಬೇಕಾಯಿತು. ರಾಜಗೃಹಾಧಿಪತ್ಯವನ್ನೂ ಪ್ರಜಾಪಾಲನೆಯನ್ನೂ ಅವನ ಮಗ ಯೋತಾಮನು ನೋಡಿಕೊಳ್ಳುತ್ತಿದ್ದನು.",
			"22": "<sup>22</sup> ಉಜ್ಜೀಯನ ಉಳಿದ ಪೂರ್ವೋತ್ತರ ಚರಿತ್ರೆಯನ್ನು ಆಮೋಚನ ಮಗ ಯೆಶಾಯನೆಂಬ ಪ್ರವಾದಿ ಬರೆದಿದ್ದಾನೆ.",
			"23": "<sup>23</sup> ಉಜ್ಜೀಯನು ಸತ್ತು ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಲು ಅವನ ಶವವನ್ನು ಕುಷ್ಠರೋಗಿಯದೆಂದು, ರಾಜಕುಟುಂಬ ಸ್ಮಶಾನವಿದ್ದ ಹೊಲದಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗ ಯೋತಾಮನು ಅರಸನಾದನು."
		},
		"27": {
			"1": "<sup>1</sup> ಯೋತಾಮನು ಪಟ್ಟಕ್ಕೆ ಬಂದಾಗ ಅವನಿಗೆ ಇಪ್ಪತ್ತೈದು ವರ್ಷ. ಅವನು ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಹದಿನಾರು ವರ್ಷ ಆಳಿದನು. ಚಾದೋಕನ ಮಗಳಾದ ಯೆರೂಷ ಎಂಬಾಕೆ ಇವನ ತಾಯಿ.",
			"2": "<sup>2</sup> ತನ್ನ ತಂದೆಯಾದ ಉಜ್ಜೀಯನಂತೆ ಇವನು ಸರ್ವೇಶ್ವರನ ಚಿತ್ತಾನುಸಾರ ನಡೆದನು; ಆದರೆ ಅವನಂತೆ ಸರ್ವೇಶ್ವರನ ಆಲಯವನ್ನು ಪ್ರವೇಶಿಸಲಿಲ್ಲ. ಪ್ರಜೆಗಳಾದರೋ ದುರಾಚಾರಿಗಳಾಗಿಯೇ ಇದ್ದರು.",
			"3": "<sup>3</sup> ಸರ್ವೇಶ್ವರನ ಆಲಯಕ್ಕೆ ಮೇಲಣ ಹೆಬ್ಬಾಗಿಲನ್ನು ಇಡಿಸಿ ಓಫೇಲ್ ಗೋಡೆಯ ಹೆಚ್ಚಿನ ಭಾಗವನ್ನು ಕಟ್ಟಿಸಿದವನು ಇವನೇ.",
			"4": "<sup>4</sup> ಇದಲ್ಲದೆ, ಇವನು ಜುದೇಯ ಪರ್ವತಪ್ರದೇಶದಲ್ಲಿ ಪಟ್ಟಣಗಳನ್ನೂ ಕಾಡುಗಳಲ್ಲಿ ದುರ್ಗಗಳನ್ನೂ ಬುರುಜುಗಳನ್ನೂ ಕಟ್ಟಿಸಿದನು.",
			"5": "<sup>5</sup> ಅಮ್ಮೋನಿಯರ ಅರಸನೊಡನೆ ಯುದ್ಧಮಾಡಿ ಅವರ ಮೇಲೆ ಜಯಗಳಿಸಿದನು; ಅಮ್ಮೋನಿಯರು ಆ ವರ್ಷದಲ್ಲಿ ಇವನಿಗೆ 3,400 ಕಿಲೋಗ್ರಾಂ ಬೆಳ್ಳಿ, 1000 ಮೆಟ್ರಿಕ್ ಟನ್ ಗೋದಿ, 100 ಮೆಟ್ರಿಕ್ ಟನ್ ಜವೆಗೋದಿ ಇವುಗಳನ್ನು ಕೊಡಬೇಕಾಯಿತು. ಅಮ್ಮೋನಿಯರು ಅವನಿಗೆ ಎರಡನೆಯ ಮತ್ತು ಮೂರನೆಯ ವರ್ಷಗಳಲ್ಲಿಯೂ ಹೀಗೆಯೇ ಕೊಟ್ಟರು.",
			"6": "<sup>6</sup> ಯೋತಾಮನು ತನ್ನ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಚಿತ್ತಾನುಸಾರ ನಡೆದುಕೊಂಡಿದ್ದರಿಂದ ಶಕ್ತಿಶಾಲಿ ಆದನು.",
			"7": "<sup>7</sup> ಯೋತಾಮನ ಉಳಿದ ಚರಿತ್ರೆ ಅವನ ಯುದ್ಧಗಳು ಹಾಗು ಪ್ರವರ್ತನೆಗಳು ಇಸ್ರಯೇಲರ ಮತ್ತು ಯೆಹೂದ್ಯರ ರಾಜಗ್ರಂಥದಲ್ಲಿ ದಾಖಲಾಗಿರುತ್ತವೆ.",
			"8": "<sup>8</sup> ಅವನು ತನ್ನ ಇಪ್ಪತ್ತೈದನೆಯ ವರ್ಷ ತುಂಬಿದ ಮೇಲೆ ಪಟ್ಟಕ್ಕೆ ಬಂದು ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಹದಿನಾರು ವರ್ಷ ಆಳಿದನು.",
			"9": "<sup>9</sup> ಮೃತನಾಗಿ ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಲು ಅವನ ಶವವನ್ನು ದಾವೀದನಗರದಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗ ಅಹಾಜನು ಅರಸನಾದನು."
		},
		"28": {
			"1": "<sup>1</sup> ಅಹಾಜನು ಪಟ್ಟಕ್ಕೆ ಬಂದಾಗ ಇಪ್ಪತ್ತು ವರ್ಷದವನಾಗಿದ್ದನು. ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಅವನು ಹದಿನಾರು ವರ್ಷ ಆಳಿದನು. ಸರ್ವೇಶ್ವರನ ಚಿತ್ತಾನುಸಾರ ಅವನು ನಡೆಯಲಿಲ್ಲ.",
			"2": "<sup>2</sup> ತನ್ನ ಪೂರ್ವಿಕನಾದ ದಾವೀದನ ಮಾರ್ಗವನ್ನು ಬಿಟ್ಟು, ಇಸ್ರಯೇಲ್ ರಾಜರ ಮಾರ್ಗದಲ್ಲಿ ನಡೆದು, ಅವರಂತೆ ಬಾಳ್‍ದೇವರುಗಳ ಎರಕದ ವಿಗ್ರಹಗಳನ್ನು ಮಾಡಿಸಿದನು.",
			"3": "<sup>3</sup> ಅವನು ಬೆನ್‍ಹಿನ್ನೋಮ್ ಎಂಬ ಕಣಿವೆಯಲ್ಲಿ ಧೂಪ ಹಾಕಿಸಿದ್ದಲ್ಲದೆ ಸರ್ವೇಶ್ವರಸ್ವಾಮಿ ಇಸ್ರಯೇಲರ ನಾಡಿನಿಂದ ಓಡಿಸಿಬಿಟ್ಟ ಜನಾಂಗಗಳ ಅಸಹ್ಯಕಾರ್ಯಗಳನ್ನು ಅನುಸರಿಸಿ ತನ್ನ ಮಕ್ಕಳನ್ನು ಬಲಿಯಗ್ನಿ ಪರೀಕ್ಷೆಗೆ ಗುರಿಪಡಿಸಿದ್ದನು.",
			"4": "<sup>4</sup> ಪೂಜಾಸ್ಥಳಗಳಲ್ಲೂ, ದಿನ್ನೆಗಳ ಮೇಲೂ ಹಾಗು ಎಲ್ಲ ಹಸಿರು ಮರಗಳ ಕೆಳಗೂ ಬಲಿಗಳನ್ನೂ ಧೂಪಗಳನ್ನೂ ಸಮರ್ಪಿಸುತ್ತಿದ್ದನು.",
			"5": "<sup>5</sup> ಈ ಕಾರಣ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಅಹಾಜನನ್ನು ಸಿರಿಯಾದ ಅರಸನ ಕೈಗೆ ಒಪ್ಪಿಸಿದನು. ಸಿರಿಯಾದವರು ಅವನನ್ನು ಸೋಲಿಸಿ ಅವನ ಜನರಲ್ಲಿ ದೊಡ್ಡ ಗುಂಪನ್ನು ಸೆರೆಹಿಡಿದು ದಮಸ್ಕಕ್ಕೆ ಒಯ್ದರು. ಇದಲ್ಲದೆ, ಅವನು ಇಸ್ರಯೇಲ್ ರಾಜನ ಕೈವಶವಾಗಿ ಅವನಿಂದಲೂ ಪೂರ್ಣವಾಗಿ ಅಪಜಯ ಹೊಂದಿದನು.",
			"6": "<sup>6</sup> ರೆಮಲ್ಯನ ಮಗ ಪೆಕಹ ಎಂಬವನು ಒಂದೇ ದಿವಸ ಯೆಹೂದ್ಯರಲ್ಲಿ ಲಕ್ಷದ ಇಪ್ಪತ್ತು ಸಾವಿರ ಮಂದಿಯನ್ನು ಕೊಲ್ಲಿಸಿದನು. ಇವರೆಲ್ಲರು ರಣವೀರರು. ಯೆಹೂದ್ಯರು ತಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಸರ್ವೇಶ್ವರನನ್ನು ಬಿಟ್ಟದ್ದೇ ಇದಕ್ಕೆ ಕಾರಣವಾಗಿತ್ತು.",
			"7": "<sup>7</sup> ರಾಜಪುತ್ರನಾದ ಮಾಸೇಯ, ರಾಜಗೃಹಾಧಿಪತಿಯಾದ ಅಜ್ರೀಕಾಮ್, ರಾಜಪ್ರತಿನಿಧಿಯಾದ ಎಲ್ಕಾನ ಎಂಬವರನ್ನು ಎಫ್ರಯಿಮ್ ನಾಡಿನ ಜಿಕ್ರಿಯೆಂಬ ಶೂರನು ಕೊಂದನು.",
			"8": "<sup>8</sup> ಇದಲ್ಲದೆ, ಇಸ್ರಯೇಲರು ತಮ್ಮ ಬಂಧುಗಳಾದ ಯೆಹೂದ್ಯರಲ್ಲಿ ಎರಡು ಲಕ್ಷ ಮಂದಿ ಹೆಂಗಸರನ್ನೂ ಗಂಡು ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನೂ ಸೆರೆಹಿಡಿದು, ದೊಡ್ಡ ಕೊಳ್ಳೆಯನ್ನೂ ಕೂಡಿಸಿಕೊಂಡು, ಸಮಾರಿಯಕ್ಕೆ ಒಯ್ದರು.",
			"9": "<sup>9</sup> ಸಮಾರಿಯದಲ್ಲಿ ಸರ್ವೇಶ್ವರನ ಒಬ್ಬ ಪ್ರವಾದಿ ಇದ್ದನು. ಅವನ ಹೆಸರು ಓದೇದ್. ಅವನು ಸಮಾರಿಯಕ್ಕೆ ಬರುತ್ತಿದ್ದ ಸೈನ್ಯದವರನ್ನು ಎದುರುಗೊಂಡು, “ನಿಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಸರ್ವೇಶ್ವರನು ಯೆಹೂದ್ಯರ ಮೇಲೆ ಕೋಪಗೊಂಡು ಅವರನ್ನು ನಿಮ್ಮ ಕೈಗೆ ಒಪ್ಪಿಸಿಕೊಟ್ಟರು. ಅವರನ್ನು ಸಂಹರಿಸುವುದರಲ್ಲಿ ನಿಮ್ಮ ರೌದ್ರ ಆಕಾಶವನ್ನು ಮುಟ್ಟಿತು.",
			"10": "<sup>10</sup> ಇದೂ ಸಾಲದೆಂದು ನೀವು ಯೆಹೂದ್ಯರನ್ನೂ ಜೆರುಸಲೇಮನ್ನೂ ಬಲಾತ್ಕರಿಸಿ ದಾಸದಾಸಿಯರನ್ನಾಗಿಯೂ ಮಾಡಿಕೊಳ್ಳಬೇಕೆಂದಿದ್ದೀರಿ. ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ವಿರುದ್ಧ ಅಪರಾಧಗಳನ್ನು ನೀವೂ ಮಾಡಿದ್ದೀರಲ್ಲವೆ?",
			"11": "<sup>11</sup> ಈಗ ನೀವು ನನ್ನ ಮಾತಿಗೆ ಕಿವಿಗೊಟ್ಟು ನಿಮ್ಮ ಸಹೋದರರಿಂದ ಸೆರೆಯಾಗಿ ತಂದವರನ್ನು ಹಿಂದಕ್ಕೆ ಕಳುಹಿಸಿರಿ; ಸರ್ವೇಶ್ವರನ ಕೋಪಾಗ್ನಿ ನಿಮ್ಮ ಮೇಲೆ ಉರಿಯುತ್ತಾ ಇದೆ!” ಎಂದನು.",
			"12": "<sup>12</sup> ಆಗ ಎಫ್ರಯಿಮರಲ್ಲಿ ಮುಖಂಡರಾಗಿದ್ದ ಯೆಹೋಹಾನಾನನ ಮಗ ಅಜರ್ಯ, ಮೆಷಿಲ್ಲೇಮೋತನ ಮಗ ಬೆರೆಕ್ಯ, ಶಲ್ಲೂಮನ ಮಗ ಹಿಜ್ಕೀಯ, ಹದ್ಲೈಯನ ಮಗ ಅಮಾಸ ಎಂಬವರು ಯುದ್ಧದಿಂದ ಬರುತ್ತಿದ್ದವರ ಮುಂದೆ ನಿಂತು,",
			"13": "<sup>13</sup> “ನೀವು ಸೆರೆಹಿಡಿದವರನ್ನು ಇಲ್ಲಿಗೆ ತರಬೇಡಿ; ನೀವು ನಮ್ಮ ಪಾಪಾಪರಾಧಗಳ ಜೊತೆಗೆ ಸರ್ವೇಶ್ವರನಿಗೆ ವಿರುದ್ಧ ಮತ್ತೊಂದು ಅಪರಾಧವನ್ನು ಸೇರಿಸಬೇಕೆಂದಿರುತ್ತೀರೋ? ನಮ್ಮ ಅಪರಾಧ ದೊಡ್ಡದು; ಇಸ್ರಯೇಲರ ಮೇಲಿರುವ ದೈವಕೋಪ ಉಗ್ರವಾಗಿದೆ!” ಎಂದು ಹೇಳಿದರು.",
			"14": "<sup>14</sup> ಯೋಧರು ಇದನ್ನು ಕೇಳಿ ಸೆರೆಯವರನ್ನೂ ಕೊಳ್ಳೆಯನ್ನೂ ಅಧಿಪತಿಗಳ ಮತ್ತು ಸಮೂಹದವರ ಮುಂದೆಯೇ ಬಿಟ್ಟುಬಿಟ್ಟರು.",
			"15": "<sup>15</sup> ಆಗ ಮೇಲೆ ಹೇಳಿದ ಪುರುಷರು ಎದ್ದುಬಂದು ಸೆರೆಯವರಲ್ಲಿ ಬೆತ್ತಲೆಯಾದವರೆಲ್ಲರಿಗೆ ಕೊಳ್ಳೆಯಿಂದ ಬಂದ ಬಟ್ಟೆಗಳನ್ನೂ, ಕಾಲಿಗೆ ಕೆರಗಳನ್ನೂ ಕೊಟ್ಟರು; ಅನ್ನಪಾನಗಳನ್ನಿತ್ತು, ತೈಲಹಚ್ಚಿದರು; ಬಳಲಿಹೋದವರನ್ನು ಕತ್ತೆಗಳ ಮೇಲೆ ಕುಳ್ಳಿರಿಸಿ, ಎಲ್ಲರನ್ನೂ ಜೆರಿಕೋ ಎಂಬ ಖರ್ಜೂರ ನಗರಕ್ಕೆ ಕರೆದುಕೊಂಡು ಹೋದರು; ಅಲ್ಲಿ ಅವರ ಬಂಧುಗಳ ಹತ್ತಿರ ಬಿಟ್ಟು, ಸಮಾರಿಯಕ್ಕೆ ಹಿಂದಿರುಗಿದರು.",
			"16": "<sup>16</sup> ಆ ಕಾಲದಲ್ಲಿ ಅರಸ ಅಹಾಜನು ತನಗೆ ಸಹಾಯ ಮಾಡಬೇಕೆಂದು ಅಸ್ಸೀರಿಯದ ರಾಜರನ್ನು ದೂತರ ಮುಖಾಂತರ ಬೇಡಿಕೊಂಡನು.",
			"17": "<sup>17</sup> ಏಕೆಂದರೆ ಎದೋಮ್ಯರು ಯೆಹೂದ್ಯರ ಮೇಲೆ ಮರಳಿ ಯುದ್ಧಕ್ಕೆ ಬಂದು ಅವರನ್ನು ಸೋಲಿಸಿ ಅನೇಕ ಜನರನ್ನು ಸೆರೆಗೊಯ್ದಿದ್ದರು.",
			"18": "<sup>18</sup> ಫಿಲಿಷ್ಟಿಯರು ಜುದೇಯದ ಇಳಕಲಿನ ಪ್ರದೇಶದ ಮತ್ತು ದಕ್ಷಿಣಪ್ರಾಂತದ ಪಟ್ಟಣಗಳ ಮೇಲೆ ದಾಳಿಮಾಡಿ ಬೇತ್ಷೆಮೆಷ್, ಅಯ್ಯಾಲೋನ್, ಗೆದೇರೋತ್ ಇವುಗಳನ್ನೂ ಸೋಕೋ, ತಿಮ್ನಾ, ಗಿಮ್ಜೋ ಇವುಗಳನ್ನೂ ಇವುಗಳಿಗೆ ಸೇರಿದ ಗ್ರಾಮಗಳನ್ನೂ ಸ್ವಾಧೀನಮಾಡಿಕೊಂಡು ಅವುಗಳಲ್ಲಿ ವಾಸವಾಗಿ ಇದ್ದರು.",
			"19": "<sup>19</sup> ಯೆಹೂದ್ಯರನ್ನು ಅಧರ್ಮಕ್ಕೆ ಪ್ರಚೋದಿಸಿ ಸರ್ವೇಶ್ವರನಿಗೆ ದ್ರೋಹಮಾಡಿದ ಇಸ್ರಯೇಲ ಅರಸ ಅಹಾಜನ ನಿಮಿತ್ತ ಸರ್ವೇಶ್ವರ ಯೆಹೂದ್ಯರನ್ನು ಈ ಪ್ರಕಾರ ತಗ್ಗಿಸಿದ್ದರು.",
			"20": "<sup>20</sup> ಅಸ್ಸೀರಿಯದ ಅರಸ ತಿಲ್ಗತ್ಪಿಲ್ನೆಸರನು ಬಂದು, ಅವನಿಗೆ ನೆರವಾಗುವುದಕ್ಕೆ ಬದಲಾಗಿ ಅವನು ತಲೆತಗ್ಗಿಸುವಂತೆ ಮಾಡಿದ್ದನು.",
			"21": "<sup>21</sup> ಅಹಾಜನು, ಸರ್ವೇಶ್ವರನ ಆಲಯದ ಹಾಗು ಅರಮನೆಯ ಅಧಿಪತಿಗಳ ಆಸ್ತಿಯನ್ನು ಸೂರೆಮಾಡಿಕೊಂಡು ಹೋಗಿ, ಅಸ್ಸೀರಿಯದ ಅರಸನಿಗೆ ಕಾಣಿಕೆಯನ್ನಾಗಿ ಕೊಟ್ಟರೂ ಅವನಿಂದ ಏನೂ ಸಹಾಯವಾಗಲಿಲ್ಲ.",
			"22": "<sup>22</sup> ಕಷ್ಟಬಂದಾಗ ಅರಸ ಅಹಾಜನು ಸರ್ವೇಶ್ವರನಿಗೆ ಮತ್ತಷ್ಟು ದ್ರೋಹಮಾಡಿದನು.",
			"23": "<sup>23</sup> ಹೇಗೆಂದರೆ, ‘ಸಿರಿಯಾ ರಾಜರ ದೇವತೆಗಳು ಅವರಿಗೆ ಜಯವನ್ನನುಗ್ರಹಿಸಿವೆ; ಅಂತೆಯೇ ನಾನೂ ಅವುಗಳಿಗೆ ಬಲಿಸಮರ್ಪಿಸಿದರೆ ನನಗೂ ಜಯವಾಗುವುದು’, ಎಂದುಕೊಂಡನು; ಅವನು ತನ್ನ ಅಪಜಯಕ್ಕೆ ಕಾರಣವಾಗಿದ್ದ ದಮಸ್ಕದ ದೇವತೆಗಳಿಗೆ ಬಲಿಕೊಟ್ಟನು. ಆದರೆ ಆ ದೇವತೆಗಳಿಂದ ಅವನಿಗೂ ಎಲ್ಲ ಇಸ್ರಯೇಲರಿಗೂ ಕೇಡು ಉಂಟಾಯಿತು.",
			"24": "<sup>24</sup> ಇದಲ್ಲದೆ, ಅಹಾಜನು ದೇವಾಲಯದ ಸಾಮಗ್ರಿಗಳನ್ನು ಕೂಡಿಸಿ, ಅವುಗಳನ್ನು ಚೂರುಚೂರು ಮಾಡಿಬಿಟ್ಟನು; ಸರ್ವೇಶ್ವರನ ಆಲಯದ ಬಾಗಿಲುಗಳನ್ನು ಮುಚ್ಚಿ ಜೆರುಸಲೇಮಿನ ಪ್ರತಿಯೊಂದು ಮೂಲೆಯಲ್ಲೂ ಯಜ್ಞವೇದಿಗಳನ್ನು ಕಟ್ಟಿಸಿದನು.",
			"25": "<sup>25</sup> ಅನ್ಯದೇವತೆಗಳಿಗೆ ಧೂಪಹಾಕುವುದಕ್ಕಾಗಿ ಜುದೇಯದ ಎಲ್ಲ ಪಟ್ಟಣಗಳಲ್ಲೂ ಪೂಜಾಸ್ಥಳಗಳನ್ನು ನಿರ್ಮಿಸಿದನು. ಹೀಗೆ ತನ್ನ ಪಿತೃಗಳ ದೇವರಾದ ಸರ್ವೇಶ್ವರನನ್ನು ರೇಗಿಸಿದನು.",
			"26": "<sup>26</sup> ಅಹಾಜನ ಉಳಿದ ಪೂರ್ವೋತ್ತರ ಚರಿತ್ರೆ ಹಾಗು ಅವನ ಎಲ್ಲ ಪ್ರವರ್ತನೆಗಳು ಯೆಹೂದ್ಯರ ಮತ್ತು ಇಸ್ರಯೇಲರ ರಾಜಗ್ರಂಥದಲ್ಲಿ ಲಿಖಿತವಾಗಿವೆ.",
			"27": "<sup>27</sup> ಅವನು ಸತ್ತು ತನ್ನ ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಲು ಅವನನ್ನು ಇಸ್ರಯೇಲ್ ರಾಜಸ್ಮಶಾನದಲ್ಲಿ ಹೂಣದೆ ಜೆರುಸಲೇಮ್ ಪಟ್ಟಣದೊಳಗಿನ ಒಂದು ಸ್ಥಳದಲ್ಲಿ ಹೂಣಿಟ್ಟರು. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗ ಹಿಜ್ಕೀಯನು ಅರಸನಾದನು."
		},
		"29": {
			"1": "<sup>1</sup> ಹಿಜ್ಕೀಯನು ಪಟ್ಟಕ್ಕೆ ಬಂದಾಗ ಅವನಿಗೆ ಇಪ್ಪತ್ತೈದು ವರ್ಷ ಪ್ರಾಯ. ಅವನು ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಇಪ್ಪತ್ತೊಂಬತ್ತು ವರ್ಷ ಆಳಿದನು. ಜೆಕರ್ಯನ ಮಗಳಾದ ಅಬೀಯ ಎಂಬಾಕೆ ಅವನ ತಾಯಿ.",
			"2": "<sup>2</sup> ಅವನು ಎಲ್ಲಾ ವಿಷಯಗಳಲ್ಲೂ ತನ್ನ ಪೂರ್ವಜ ದಾವೀದನಂತೆ ಸರ್ವೇಶ್ವರನ ಚಿತ್ತಾನುಸಾರ ನಡೆದನು.",
			"3": "<sup>3</sup> ಹಿಜ್ಕೀಯನು ತನ್ನ ಆಳ್ವಿಕೆಯ ಮೊದಲನೆಯ ವರ್ಷದ ಮೊದಲನೆಯ ತಿಂಗಳಲ್ಲಿ ಸರ್ವೇಶ್ವರನ ಆಲಯದ ಬಾಗಿಲುಗಳನ್ನು ತೆರೆಯಿಸಿ ಅವುಗಳನ್ನು ಭದ್ರಪಡಿಸಿದನು.",
			"4": "<sup>4</sup> ಆಮೇಲೆ ಯಾಜಕರನ್ನೂ ಲೇವಿಯರನ್ನೂ ದೇವಾಲಯದ ಪೂರ್ವದಿಕ್ಕಿನ ಬಯಲಿನಲ್ಲಿ ಕೂಡಿಸಿ ಅವರಿಗೆ,",
			"5": "<sup>5</sup> “ಲೇವಿಯರೇ, ನನ್ನ ಮಾತನ್ನು ಕೇಳಿರಿ; ಈಗ ನಿಮ್ಮನ್ನೇ ಶುದ್ಧಪಡಿಸಿಕೊಂಡು, ನಿಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಪವಿತ್ರಾಲಯದಿಂದ ಹೊಲಸನ್ನೆಲ್ಲಾ ಹೊರಗೆ ಹಾಕಿ, ಆಲಯವನ್ನು ಶುದ್ಧೀಕರಿಸಿರಿ.",
			"6": "<sup>6</sup> ನಿಮ್ಮ ಪೂರ್ವಜರು ನಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ದ್ರೋಹಮಾಡಿ ಅವರ ಚಿತ್ತಕ್ಕೆ ವಿರುದ್ಧ ನಡೆದು ಅವರನ್ನು ತೊರೆದುಬಿಟ್ಟರು; ಅವರ ನಿವಾಸ ಸ್ಥಳಕ್ಕೆ ಬೆನ್ನುಕೊಟ್ಟು ವಿಮುಖರಾದರು.",
			"7": "<sup>7</sup> ಅದರ ಮಂಟಪದ ಬಾಗಿಲುಗಳನ್ನು ಮುಚ್ಚಿ, ದೀಪಗಳನ್ನು ನಂದಿಸಿದರು; ಇಸ್ರಯೇಲ್‍ದೇವರ ಪವಿತ್ರಸ್ಥಾನದಲ್ಲಿ ಧೂಪಾರತಿ ಎತ್ತುವುದನ್ನೂ ದಹನಬಲಿ ಸಮರ್ಪಿಸುವುದನ್ನೂ ನಿಲ್ಲಿಸಿಬಿಟ್ಟರು.",
			"8": "<sup>8</sup> ಆದುದರಿಂದ ಸರ್ವೇಶ್ವರ, ಜುದೇಯ ನಾಡಿನ ಮೇಲೂ ಜೆರುಸಲೇಮಿನ ಮೇಲೂ ಕೋಪಗೊಂಡು ಅವುಗಳನ್ನು ಭಯಭೀತಿಗೂ ಹಾಸ್ಯಪರಿಹಾಸ್ಯಗಳಿಗೂ ಆಸ್ಪದವಾಗಿಸಿದ್ದಾರೆ. ಇದಕ್ಕೆ ನೀವೇ ಸಾಕ್ಷಿಗಳು.",
			"9": "<sup>9</sup> ಅದೇ ಕಾರಣದಿಂದ ನಮ್ಮ ಹಿರಿಯರು ಕತ್ತಿಗೆ ತುತ್ತಾಗಿದ್ದಾರೆ. ನಮ್ಮ ಗಂಡುಹೆಣ್ಣುಮಕ್ಕಳೂ ಹೆಂಡತಿಯರೂ ಸೆರೆಯಾಳುಗಳಾಗಿ ಹೋಗಬೇಕಾಯಿತು.",
			"10": "<sup>10</sup> ಈಗ ನಮ್ಮ ಮೇಲಿರುವ ದೈವಕೋಪಾಗ್ನಿ ತೊಲಗಿಹೋಗುವಂತೆ ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರನೊಡನೆ ಒಡಂಬಡಿಕೆ ಮಾಡಿಕೊಳ್ಳುವುದಕ್ಕೆ ತೀರ್ಮಾನಿಸಿದ್ದೇನೆ.",
			"11": "<sup>11</sup> ಹೀಗಿರಲಾಗಿ ನನ್ನ ಮಕ್ಕಳೇ, ಉದಾಸೀನರಾಗಿರಬೇಡಿ; ಸರ್ವೇಶ್ವರ ತಮ್ಮನ್ನು ಆರಾಧಿಸುವುದಕ್ಕೂ ತಮಗೆ ಧೂಪಾರತಿ ಎತ್ತುವುದಕ್ಕೂ ನಿಮ್ಮನ್ನು ತಮ್ಮ ಸಾನ್ನಿಧ್ಯಸೇವಕರನ್ನಾಗಿ ಆರಿಸಿಕೊಂಡರಲ್ಲವೇ?’ ಎಂದು ಹೇಳಿದನು.",
			"12": "<sup>12</sup> ಕೂಡಲೆ ಕೆಹಾತ್ಯರಲ್ಲಿ ಅಮಾಸೈಯ ಮಗ ಮೆಹತ್, ಅಜರ್ಯನ ಮಗ ಯೋವೇಲ್, ಮೆರಾರೀಯರಲ್ಲಿ ಅಬ್ದೀಯ ಮಗ ಕೀಷ್, ಯೆಹಲ್ಲೆಲೇಲನ ಮಗ ಅಜರ್ಯ, ಗೆರ್ಷೋನ್ಯರಲ್ಲಿ ಜಿಮ್ಮನ ಮಗ ಯೋವಾಹ,",
			"13": "<sup>13</sup> ಯೋವಾಹನ ಮಗ ಏದೆನ್, ಎಲೀಚಾಫಾನ್ಯರಲ್ಲಿ ಶಿಮ್ರೀ, ಯೆಗೀಯೇಲ್, ಅಸಾಫ್ಯರಲ್ಲಿ ಜೆಕರ್ಯ,",
			"14": "<sup>14</sup> ಮತ್ತನ್ಯ, ಹೇಮಾನ್ಯರಲ್ಲಿ ಯೆಹೀಯೇಲ್, ಶಿಮ್ಮೀ, ಯೆದೂತೂನ್ಯರಲ್ಲಿ ಶೆಮಾಯ ಹಾಗು ಉಜ್ಜೀಯೇಲ್",
			"15": "<sup>15</sup> ಎಂಬ ಲೇವಿಯರು ಮುಂದೆ ಬಂದು ತಮ್ಮ ಸಹೋದರರನ್ನು ಒಂದುಗೂಡಿಸಿದರು. ಅವರು ಅರಸನ ಆಜ್ಞಾಧಾರಕರಾಗಿ ಸರ್ವೇಶ್ವರನ ಧರ್ಮಶಾಸ್ತ್ರವಿಧಿಯ ಮೇರೆಗೆ ತಮ್ಮನ್ನು ಶುದ್ಧಪಡಿಸಿಕೊಂಡು ಸರ್ವೇಶ್ವರನ ಆಲಯವನ್ನು ಶುದ್ಧೀಕರಿಸುವುದಕ್ಕೆ ಸಿದ್ಧರಾದರು.",
			"16": "<sup>16</sup> ಯಾಜಕರು ಸರ್ವೇಶ್ವರನ ಆಲಯವನ್ನು‍ ಶುದ್ಧಮಾಡುವುದಕ್ಕೆ ಒಳಗೆ ಹೋಗಿ ಪವಿತ್ರಸ್ಥಾನದಲ್ಲಿದ್ದ ಹೊಲಸನ್ನೆಲ್ಲಾ ಹೊರಗೆ ತಂದು ಪ್ರಾಕಾರದಲ್ಲಿ ಹಾಕಿದರು. ಲೇವಿಯರು ಅದನ್ನು ಕೂಡಿಸಿ ಹೊರಗೆ ಒಯ್ದು ಕಿದ್ರೋನ್ ಹಳ್ಳದಲ್ಲಿ ಹಾಕಿದರು.",
			"17": "<sup>17</sup> ಮೊದಲನೆಯ ತಿಂಗಳಿನ ಮೊದಲನೆಯ ದಿನದಲ್ಲಿ ಈ ಶುದ್ಧೀಕರಣವನ್ನು ಪ್ರಾರಂಭಿಸಿದರು. ಎಂಟನೆಯ ದಿನದಲ್ಲಿ ಸರ್ವೇಶ್ವರನ ಆಲಯದ ಮಂಟಪಕ್ಕೆ ಕೈಹಾಕಿದರು; ಹೀಗೆ ಸರ್ವೇಶ್ವರನ ಆಲಯವನ್ನು ಶುದ್ಧಪಡಿಸುವುದರಲ್ಲಿ ಎಂಟು ದಿನಗಳು ಕಳೆದವು. ಮೊದಲನೆಯ ತಿಂಗಳಿನ ಹದಿನಾರನೆಯ ದಿನದಲ್ಲಿ ಕೆಲಸವೆಲ್ಲಾ ಮುಗಿಯಿತು.",
			"18": "<sup>18</sup> ಆಮೇಲೆ ಅವರು ಅರಮನೆಗೆ ಹೋಗಿ, ಅರಸ ಹಿಜ್ಕೀಯನಿಗೆ, “ನಾವು ಸರ್ವೇಶ್ವರನ ಆಲಯವನ್ನು ಪೂರ್ತಿಯಾಗಿ ಶುದ್ಧೀಕರಿಸಿದೆವು. ಬಲಿಪೀಠವನ್ನು, ಅದರ ಉಪಕರಣಗಳನ್ನು, ನೈವೇದ್ಯ ರೊಟ್ಟಿಗಳನ್ನಿಡತಕ್ಕ ಮೇಜನ್ನು ಹಾಗು ಅದರ ಸಾಮಗ್ರಿಗಳನ್ನೂ ಶುದ್ಧಮಾಡಿದೆವು.",
			"19": "<sup>19</sup> ಅರಸ ಅಹಾಜನು, ತನ್ನ ಆಳ್ವಿಕೆಯಲ್ಲಿ ದೇವದ್ರೋಹಿಯಾಗಿ ತಳ್ಳಿಬಿಟ್ಟಿದ್ದ ಪಾತ್ರೆಗಳನ್ನು ಮತ್ತೆ ತಂದು ಇಟ್ಟು ಪ್ರತಿಷ್ಠಿಸಿದ್ದೇವೆ; ಅವು ಈಗ ಸರ್ವೇಶ್ವರನ ಬಲಿಪೀಠದ ಮುಂದಿರುತ್ತವೆ,” ಎಂದು ವರದಿಮಾಡಿದರು.",
			"20": "<sup>20</sup> ಅರಸ ಹಿಜ್ಕೀಯನು ಮರುದಿನ ಬೆಳಗಿನ ಜಾವದಲ್ಲೆದ್ದು ಪಟ್ಟಣದ ಮುಖಂಡರನ್ನು ಕೂಡಿಸಿ ಅವರೊಡನೆ ಸರ್ವೇಶ್ವರನ ಆಲಯಕ್ಕೆ ಹೋದನು.",
			"21": "<sup>21</sup> ಅವರು ಸರಕಾರಿ, ದೇವಾಲಯ ಹಾಗು ಯೆಹೂದಪ್ರಜೆ ಇವುಗಳಿಗಾಗಿ ದೋಷಪರಿಹಾರಕ ಬಲಿಯರ್ಪಿಸುವುದಕ್ಕಾಗಿ ಹೋರಿ, ಟಗರು, ಕುರಿಮರಿ, ಹೋತ, ಇವುಗಳಲ್ಲಿ ಏಳೇಳನ್ನು ತಂದರು. ಅರಸನು ಯಾಜಕ ಆರೋನನ ಸಂತಾನದವರಿಗೆ ಸರ್ವೇಶ್ವರನ ಬಲಿಪೀಠದ ಮೇಲೆ ದಹನಬಲಿ ಮಾಡಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"22": "<sup>22</sup> ಹೋರಿಗಳನ್ನು ವಧಿಸಿದ ಮೇಲೆ ಯಾಜಕರು ರಕ್ತವನ್ನು ಕೂಡಿಸಿ ಬಲಿಪೀಠಕ್ಕೆ ಪ್ರೋಕ್ಷಿಸಿದರು. ಅನಂತರ ಟಗರುಗಳನ್ನು ವಧಿಸಿ ಅವುಗಳ ರಕ್ತವನ್ನು ಬಲಿಪೀಠಕ್ಕೆ ಪ್ರೋಕ್ಷಿಸಿದರು. ಆಮೇಲೆ ಕುರಿದನಗಳನ್ನು ವಧಿಸಿ ಅವುಗಳ ರಕ್ತವನ್ನೂ ಬಲಿಪೀಠಕ್ಕೆ ಪ್ರೋಕ್ಷಿಸಿದರು.",
			"23": "<sup>23</sup> ಕಡೆಯಲ್ಲಿ ದೋಷಪರಿಹಾರಕ ಬಲಿಗಾಗಿ ತಂದ ಹೋತಗಳನ್ನು ಅರಸನ ಮುಂದೆಯೂ ಸಮಾಜದವರ ಮುಂದೆಯೂ ನಿಲ್ಲಿಸಿದರು. ಅವರು ಅವುಗಳ ಮೇಲೆ ತಮ್ಮ ಕೈಗಳನ್ನು ಚಾಚಿದರು.",
			"24": "<sup>24</sup> ಅನಂತರ ಯಾಜಕರು ಅವುಗಳನ್ನು ವಧಿಸಿ ಅವುಗಳ ರಕ್ತವನ್ನು ಸಮಸ್ತ ಇಸ್ರಯೇಲರ ದೋಷಪರಿಹಾರಕ್ಕಾಗಿ ಬಲಿಪೀಠದ ಮೇಲೆ ಸುರಿದರು. ಆ ದಹನಬಲಿಗಳೂ ದೋಷಪರಿಹಾರಕ ಬಲಿಗಳೂ ಎಲ್ಲ ಇಸ್ರಯೇಲರ ಪರವಾಗಿ ಆಗಬೇಕೆಂದು ಅರಸನು ಆಜ್ಞಾಪಿಸಿದ್ದನು.",
			"25": "<sup>25</sup> ಇದಲ್ಲದೆ, ಅವನು ತಾಳ, ಸ್ವರಮಂಡಲ, ಕಿನ್ನರಿ ಇವುಗಳಿಂದ ಭಜಿಸುವುದಕ್ಕಾಗಿ ಲೇವಿಯರನ್ನು ನೇಮಿಸಿದನು. ದಾವೀದನ ರಾಜದರ್ಶಿಯಾದ ಗಾದ್ ಹಾಗು ಪ್ರವಾದಿ ನಾತಾನ್ ಇವರ ಆಜ್ಞಾನುಸಾರ ಸರ್ವೇಶ್ವರನ ಆಲಯದಲ್ಲಿ ಈ ಗಾಯಕರನ್ನು ಇರಿಸಿದ್ದನು. ಸರ್ವೇಶ್ವರಸ್ವಾಮಿಯೇ ಪ್ರವಾದಿಗಳ ಮುಖಾಂತರ ಹೀಗೆ ಆಜ್ಞಾಪಿಸಿದ್ದರು.",
			"26": "<sup>26</sup> ಆ ಲೇವಿಯರು ದಾವೀದನ ವಾದ್ಯಗಳನ್ನೂ ಯಾಜಕರು ತುತೂರಿಗಳನ್ನೂ ಹಿಡಿದು ನಿಂತಿರಲು ಹಿಜ್ಕೀಯನು ಬಲಿಪೀಠದ ಮೇಲೆ ದಹನಬಲಿಯನ್ನು ಸಮರ್ಪಿಸುವುದಕ್ಕೆ ಆಜ್ಞಾಪಿಸಿದನು.",
			"27": "<sup>27</sup> ಹೀಗೆ ದಹನಬಲಿ ಸಮರ್ಪಣೆ ಪ್ರಾರಂಭಿಸಿದೊಡನೆ ಇಸ್ರಯೇಲ್ ಅರಸ ದಾವೀದನ ವಾದ್ಯಗಳಿಂದ ಸರ್ವೇಶ್ವರನನ್ನು ಭಜಿಸುವುದೂ ತುತೂರಿ ಊದುವುದೂ ಆರಂಭವಾಯಿತು.",
			"28": "<sup>28</sup> ದಹನಬಲಿ ಸಮರ್ಪಣೆಯು ಮುಗಿಯುವವರೆಗೆ ಸಭೆಸೇರಿದವರು ಸಾಷ್ಟಾಂಗ ನಮಸ್ಕಾರ ಮಾಡುತ್ತಾ ಇದ್ದರು; ಗಾಯಕರು ಗಾನ ಮಾಡುತ್ತಾ ಇದ್ದರು; ತುತೂರಿ ಊದುವವರು ಊದುತ್ತಾ ಇದ್ದರು.",
			"29": "<sup>29</sup> ದಹನಬಲಿ ಸಮರ್ಪಣೆ ತೀರಿದ ಮೇಲೆ ಅರಸನೂ ಅವನ ಜೊತೆಯಲ್ಲಿದ್ದವರೆಲ್ಲರೂ ಸಾಷ್ಟಾಂಗ ನಮಸ್ಕಾರ ಮಾಡಿದರು.",
			"30": "<sup>30</sup> ಅರಸ ಹಿಜ್ಕೀಯನೂ ಪದಾಧಿಕಾರಿಗಳೂ ದಾವೀದನ ಮತ್ತು ದರ್ಶಿಯಾದ ಆಸಾಫನ ಕೀರ್ತನೆಗಳಿಂದ ಸರ್ವೇಶ್ವರನನ್ನು ಸ್ತುತಿಸಬೇಕೆಂದು ಲೇವಿಯರಿಗೆ ಆಜ್ಞಾಪಿಸಿದರು. ಅವರು ಉತ್ಸಾಹದಿಂದ ಸ್ತುತಿಸುತ್ತಾ ತಲೆಬಾಗಿ ನಮಸ್ಕರಿಸಿದರು.",
			"31": "<sup>31</sup> ಆಮೇಲೆ ಹಿಜ್ಕೀಯನು ಸಭೆ ಸೇರಿದವರಿಗೆ, “ನೀವು ಈಗ ಸರ್ವೇಶ್ವರನಿಗಾಗಿ ನಿಮ್ಮನ್ನೇ ಪ್ರತಿಷ್ಠಿಸಿಕೊಂಡಿದ್ದೀರಿ; ಹತ್ತಿರ ಬಂದು ಸರ್ವೇಶ್ವರನ ಆಲಯಕ್ಕೆ ಸಮಾಧಾನಬಲಿಗಳನ್ನೂ ಕೃತಜ್ಞತಾಬಲಿಗಳನ್ನೂ ಸಮರ್ಪಿಸಿರಿ,” ಎಂದನು. ಸಭೆ ಸೇರಿದ್ದವರು ಸಮಾಧಾನ ಬಲಿಗಳನ್ನೂ ಕೃತಜ್ಞತಾಬಲಿಗಳನ್ನೂ ಸಮರ್ಪಿಸಿದ್ದಲ್ಲದೆ, ಅನೇಕರು ಸ್ವಇಚ್ಛೆಯಿಂದ ದಹನಬಲಿಗಳನ್ನೂ ಸಮರ್ಪಿಸಿದರು.",
			"32": "<sup>32</sup> ಹೀಗೆ ಸಭಿಕರು ದಹನಬಲಿದಾನಕ್ಕಾಗಿ ತಂದೊಪ್ಪಿಸಿದ ಹೋರಿಗಳು ಎಪ್ಪತ್ತು, ಟಗರುಗಳು ನೂರು, ಕುರಿಮರಿಗಳು ಇನ್ನೂರು. ಇವೆಲ್ಲವು ಸರ್ವೇಶ್ವರನಿಗೆ ದಹನಬಲಿ ಸಮರ್ಪಣೆಗಾಗಿಯೇ ಕೊಡಲಾದುವು.",
			"33": "<sup>33</sup> ಹೀಗೆ ದೇವರಿಗೆ ಕಾಣಿಕೆಯಾಗಿ ತರಲಾದ ಹೋರಿಗಳು ಆರುನೂರು, ಕುರಿಗಳು ಮೂರು ಸಾವಿರ.",
			"34": "<sup>34</sup> ಈ ಎಲ್ಲಾ ಪ್ರಾಣಿಗಳನ್ನು ವಧಿಸುವ ಕೆಲಸಕ್ಕೆ ಯಾಜಕರು ಸಾಲದೆಹೋದುದರಿಂದ ಆ ಕಾರ್ಯ ಮುಗಿವ ತನಕ, ಅವರ ಬಂಧುಗಳಾದ ಲೇವಿಯರು ಅವರಿಗೆ ಸಹಾಯ ಮಾಡುತ್ತಿದ್ದರು; ಅಷ್ಟರಲ್ಲಿ ಮಿಕ್ಕ ಯಾಜಕರು ತಮ್ಮನ್ನೇ ಶುದ್ಧೀಪಡಿಸಿಕೊಳ್ಳುತ್ತಿದ್ದರು. ತಮ್ಮನ್ನು ಶುದ್ಧಪಡಿಸಿಕೊಳ್ಳುವುದರಲ್ಲಿ ಯಾಜಕರಿಗಿಂತ ಲೇವಿಯರೇ ಹೆಚ್ಚು ಶ್ರದ್ಧೆಯುಳ್ಳವರಾಗಿದ್ದರು.",
			"35": "<sup>35</sup> ಇದಲ್ಲದೆ, ಯಾಜಕರು ಸಮರ್ಪಿಸಬೇಕಾಗಿದ್ದ ದಹನಬಲಿ, ಶಾಂತಿಸಮಾಧಾನ ಬಲಿಗಳ ಕೊಬ್ಬು ಹಾಗು ದಹನಬಲಿ ಸಂಬಂಧವಾದ ಪಾನದ್ರವ್ಯ ಇವೂ ಅಪಾರವಾಗಿ ಇದ್ದವು.",
			"36": "<sup>36</sup> ಹೀಗೆ ಸರ್ವೇಶ್ವರನ ಆಲಯದ ಸೇವಾಕ್ರಮವು ಪುನಃ ಸ್ಥಾಪಿತವಾಯಿತು. ಯಾರೂ ಗಮನಿಸುವುದಕ್ಕೆ ಮುಂಚೆಯೇ, ದೇವರು ತಾವೇ ತಮ್ಮ ಪ್ರಜೆಗಳಿಗಾಗಿ ಈ ಕಾರ್ಯವನ್ನು ನೆರವೇರಿಸಿದ್ದರಿಂದ ಹಿಜ್ಕೀಯನೂ ಎಲ್ಲ ಜನರೂ ಸಂತೋಷಪಟ್ಟರು."
		},
		"30": {
			"1": "<sup>1</sup> ತರುವಾಯ ಹಿಜ್ಕೀಯನು, ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರಸ್ವಾಮಿಯ ಪಾಸ್ಕವನ್ನು ಆಚರಿಸುವುದಕ್ಕಾಗಿ ಜೆರುಸಲೇಮಿನಲ್ಲಿರುವ ಸರ್ವೇಶ್ವರನ ಮಹಾಲಯಕ್ಕೆ ಬರಬೇಕೆಂದು ಎಲ್ಲ ಇಸ್ರಯೇಲರಿಗೆ, ಯೆಹೂದ್ಯರಿಗೆ, ಎಫ್ರಯಿಮರಿಗೆ ಹಾಗು ಮನಸ್ಸೆಯವರಿಗೆ ದೂತರ ಮುಖಾಂತರ ಮತ್ತು ಪತ್ರಗಳ ಮೂಲಕ ತಿಳಿಯಪಡಿಸಿದನು.",
			"2": "<sup>2</sup> ಯಾಜಕರಲ್ಲಿ ತಕ್ಕಷ್ಟು ಮಂದಿ ತಮ್ಮನ್ನು ಶುದ್ಧಪಡಿಸಿಕೊಂಡಿರಲಿಲ್ಲ. ಜನರು ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಇನ್ನೂ ಕೂಡಿಬಂದಿರಲಿಲ್ಲವಾದುದರಿಂದ ಪಾಸ್ಕವನ್ನು ಕೂಡಲೆ ಆಚರಿಸಲಾಗದೆಂದು ಅರಸನು, ಅವನ ಪದಾಧಿಕಾರಿಗಳು ಹಾಗು ಜೆರುಸಲೇಮಿನ ಸರ್ವಸಂಘದವರು ತಿಳಿದು",
			"3": "<sup>3</sup> ಎರಡನೆಯ ತಿಂಗಳಿನಲ್ಲಿ ಅದನ್ನು ಆಚರಿಸಬೇಕೆಂದು ಅಭಿಪ್ರಾಯಪಟ್ಟರು.",
			"4": "<sup>4</sup> ಈ ಅಭಿಪ್ರಾಯ ಅರಸನಿಗೂ ಸಾರ್ವಜನಿಕರಿಗೂ ಸರಿಕಂಡಿತು.",
			"5": "<sup>5</sup> ಜನರು ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ಪಾಸ್ಕವನ್ನು ಆಚರಿಸುವುದಕ್ಕಾಗಿ ಜೆರುಸಲೇಮಿಗೆ ಬರಬೇಕೆಂಬುದಾಗಿ ಬೇರ್ಷೆಬದಿಂದ ದಾನಿನವರೆಗೂ ವಾಸಿಸುವ ಇಸ್ರಯೇಲರೊಳಗೆ ಡಂಗುರ ಹೊಡಿಸಬೇಕೆಂದು ನಿರ್ಣಯಿಸಿದರು. ಆವರೆಗೂ, ಜನರಲ್ಲಿ ಹೆಚ್ಚುಮಂದಿ ಧರ್ಮಶಾಸ್ತ್ರವಿಧಿಯ ಮೇರೆಗೆ, ಪಾಸ್ಕವನ್ನು ಆಚರಿಸಲಿಲ್ಲ.",
			"6": "<sup>6</sup> ಅರಸನ ಮತ್ತು ಅವನ ಪದಾಧಿಕಾರಿಗಳ ಪತ್ರವನ್ನು ತೆಗೆದುಕೊಂಡುಹೋದ ದೂತರು ಇಸ್ರಯೇಲ್ ಹಾಗು ಜುದೇಯ ನಾಡುಗಳಲ್ಲೆಲ್ಲಾ ಸಂಚರಿಸಿ ರಾಜಾಜ್ಞೆಯಂತೆ ಹೀಗೆಂದು ಪ್ರಕಟಿಸಿದರು: “ಇಸ್ರಯೇಲರೇ, ಅಬ್ರಹಾಮ್, ಇಸಾಕ್, ಇಸ್ರಯೇಲರ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ಅಭಿಮುಖರಾಗಿರಿ. ಆಗ ಅವರೂ ನಿಮ್ಮಲ್ಲಿ ಅಸ್ಸೀರಿಯಾದ ಅರಸನ ಕೈಗೆ ಸಿಕ್ಕದೆ ತಪ್ಪಿಸಿಕೊಂಡ ಅಳಿದುಳಿದವರಿಗೆ ಅಭಿಮುಖರಾಗುವರು.",
			"7": "<sup>7</sup> ನಿಮ್ಮ ಹೆತ್ತವರೂ ಸಹೋದರರೂ ನಡೆದಂತೆ ನೀವೂ ನಡೆಯಬೇಡಿ. ಅವರು ತಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ದ್ರೋಹಿಗಳಾದರು; ಈ ಕಾರಣ ನಾಶನಕ್ಕೆ ಗುರಿಯಾದರು. ಅದಕ್ಕೆ ನೀವೇ ಸಾಕ್ಷಿಗಳು.",
			"8": "<sup>8</sup> ಈಗ ನಿಮ್ಮ ಪೂರ್ವಜರಂತೆ ಆಜ್ಞೆಗೆ ಮಣಿಯದವರಾಗಬೇಡಿ. ಸರ್ವೇಶ್ವರನಿಗೆ ಅಧೀನರಾಗಿರಿ. ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಸದಾಕಾಲಕ್ಕೂ ಪ್ರತಿಷ್ಠಿಸಿಕೊಂಡ ಪವಿತ್ರಾಲಯಕ್ಕೆ ಬಂದು ಅವರನ್ನು ಅವಲಂಬಿಸಿರಿ. ಆಗ ನಿಮ್ಮ ಮೇಲಿರುವ ಅವರ ಉಗ್ರಕೋಪ ತೊಲಗಿಹೋಗುವುದು.",
			"9": "<sup>9</sup> ನೀವು ಸರ್ವೇಶ್ವರನಿಗೆ ಅಭಿಮುಖರಾದರೆ, ನಿಮ್ಮ ಸಹೋದರರೂ ಮಕ್ಕಳೂ ತಮ್ಮನ್ನು ಸೆರೆಯೊಯ್ದವರ ದೃಷ್ಟಿಯಲ್ಲಿ ದಯೆಗೆ ಪಾತ್ರರಾಗಿ, ಮರಳಿ ಸ್ವದೇಶಕ್ಕೆ ಬರುವರು. ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ದಯಾವಂತರು, ಕನಿಕರವುಳ್ಳವರು. ತಮಗೆ ಅಭಿಮುಖರಾಗುವ ನಿಮ್ಮನ್ನು ಕಟಾಕ್ಷಿಸದೆ ಇರುವುದಿಲ್ಲ.”",
			"10": "<sup>10</sup> ದೂತರು ಎಫ್ರಯಿಮ್, ಮನಸ್ಸೆ, ಜೆಬುಲೂನ್ ಪ್ರಾಂತಗಳಲ್ಲಿ ಪಟ್ಟಣದಿಂದ ಪಟ್ಟಣಕ್ಕೆ ಹೋದರು. ಆದರೆ ಜನರು ಅವರನ್ನು ಕಂಡು ನಕ್ಕು ಗೇಲಿಮಾಡಿದರು.",
			"11": "<sup>11</sup> ಕೆಲವು ಮಂದಿ ಆಶೇರ್ಯರು, ಮನಸ್ಸೆಯವರು ಹಾಗು ಜೆಬುಲೂನ್ಯರು ಮಾತ್ರ ದೀನಮನಸ್ಕರಾಗಿ ಜೆರುಸಲೇಮಿಗೆ ಬಂದರು.",
			"12": "<sup>12</sup> ಯೆಹೂದ್ಯರಾದರೋ ದೇವರ ಕೃಪಾಹಸ್ತದಿಂದ, ಅವರ ಪ್ರೇರಣೆಯಿಂದ, ಏಕಮನಸ್ಸುಳ್ಳವರಾಗಿ, ಸರ್ವೇಶ್ವರನ ಧರ್ಮಶಾಸ್ತ್ರಾನುಸಾರ, ಅರಸನಿಂದಲೂ ಅಧಿಕಾರಿಗಳಿಂದಲೂ ಹೊರಟ ಆಜ್ಞೆಯನ್ನು ಕೈಕೊಂಡರು.",
			"13": "<sup>13</sup> ಹೀಗೆ, ಅನೇಕಾನೇಕ ಜನರು ಎರಡನೆಯ ತಿಂಗಳಿನಲ್ಲಿ ಹುಳಿಯಿಲ್ಲದ ರೊಟ್ಟಿಗಳ ಹಬ್ಬವನ್ನು ಆಚರಿಸುವುದಕ್ಕಾಗಿ ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಕೂಡಿದರು; ಅದೊಂದು ಮಹಾಕೂಟವಾಗಿತ್ತು.",
			"14": "<sup>14</sup> ಅವರು ಜೆರುಸಲೇಮಿನಲ್ಲಿದ್ದ ಯಜ್ಞವೇದಿಗಳನ್ನೂ ಧೂಪವೇದಿಗಳನ್ನೂ ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ಕಿದ್ರೋನ್ ಹಳ್ಳದಲ್ಲಿ ಬಿಸಾಡಿಬಿಟ್ಟರು.",
			"15": "<sup>15</sup> ಆಮೇಲೆ ಎರಡನೆಯ ತಿಂಗಳಿನ ಹದಿನಾಲ್ಕನೆಯ ದಿನ ಪಾಸ್ಕಹಬ್ಬಕ್ಕಾಗಿ ಕುರಿಮರಿಗಳನ್ನು ವಧಿಸಿದರು. ಯಾಜಕರೂ ಲೇವಿಯರೂ, ಮೊದಲನೆಯ ಸಾರಿ ತಪ್ಪಿದ್ದಕ್ಕಾಗಿ ನಾಚಿಕೆಪಟ್ಟು, ತಮ್ಮನ್ನು ಶುದ್ಧಪಡಿಸಿಕೊಂಡರು. ಸರ್ವೇಶ್ವರನ ಆಲಯದಲ್ಲಿ ದಹನಬಲಿಗಳನ್ನು ಸಮರ್ಪಿಸಿದರು;",
			"16": "<sup>16</sup> ತಮ್ಮ ತಮ್ಮ ಸ್ಥಳಗಳಲ್ಲಿ ನಿಂತು, ದೈವಪುರುಷನಾದ ಮೋಶೆಯ ಧರ್ಮಶಾಸ್ತ್ರದಿಂದ ತಮಗೆ ನೇಮಕವಾದ, ಸೇವೆಯನ್ನು ಸಲ್ಲಿಸಿದರು. ಯಾಜಕರು ಲೇವಿಯರ ಕೈಯಿಂದ ರಕ್ತವನ್ನು ತೆಗೆದುಕೊಂಡು ಅದನ್ನು ಪ್ರೋಕ್ಷಿಸಿದರು.",
			"17": "<sup>17</sup> ಜನಸಮೂಹದಲ್ಲಿ ಅನೇಕರು ತಮ್ಮನ್ನು ಶುದ್ಧಪಡಿಸಿಕೊಳ್ಳದೆ ಇದ್ದುದರಿಂದ, ಅಶುದ್ಧರಾಗಿದ್ದವರೆಲ್ಲರ ಬಲಿಪಶುಗಳು ಸರ್ವೇಶ್ವರನಿಗೆ ಮೀಸಲಾಗುವಂತೆ ಅವುಗಳನ್ನು ಲೇವಿಯರೇ ವಧಿಸಿದರು.",
			"18": "<sup>18</sup> ಜನರಲ್ಲಿ ಅನೇಕಾನೇಕರು ಅಂದರೆ, ಹೆಚ್ಚಾಗಿ ಎಫ್ರಯಿಮ್, ಮನಸ್ಸೆ, ಇಸ್ಸಾಕಾರ್ ಹಾಗು ಜೆಬಲೂನ್ ಪ್ರಾಂತಗಳವರು, ತಮ್ಮನ್ನು ಶುದ್ಧಪಡಿಸಿಕೊಳ್ಳದೆ, ಧರ್ಮಶಾಸ್ತ್ರಕ್ಕೆ ವಿರುದ್ಧವಾದ ರೀತಿಯಿಂದ, ಪಾಸ್ಕಭೋಜನವನ್ನು ಮಾಡಿದರು. ಆದರೆ ಹಿಜ್ಕೀಯನು, “ಸರ್ವೇಶ್ವರಾ, ದಯಾಪರರಾದ ದೇವರೇ, ಈ ದೇವಾಲಯಕ್ಕೆ ಯಾತ್ರಿಕರಾಗಿ ಬಂದಿರುವವರಲ್ಲಿ ಇರಬೇಕಾದ ಪರಿಶುದ್ಧತೆ ಅನೇಕರಲ್ಲಿ ಇಲ್ಲ.",
			"19": "<sup>19</sup> ಆದರೂ ಪಿತೃಗಳ ದೇವರಾದ ನಿಮ್ಮನ್ನು ಅರಸುವ ಮನಸ್ಸಿನಿಂದ ಬಂದಿರುವವರಿಗೆಲ್ಲಾ ಕ್ಷಮೆಯನ್ನು ಅನುಗ್ರಹಿಸಿರಿ,” ಎಂದು ವಿಜ್ಞಾಪನೆ ಮಾಡಿದನು.",
			"20": "<sup>20</sup> ಅಂತೆಯೇ ಸರ್ವೇಶ್ವರ ಹಿಜ್ಕೀಯನ ಪ್ರಾರ್ಥನೆಯನ್ನು ಆಲಿಸಿದರು; ಜನರಿಗೆ ಕೇಡು ಮಾಡಲಿಲ್ಲ.",
			"21": "<sup>21</sup> ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಕೂಡಿಬಂದ ಇಸ್ರಯೇಲರು ಏಳು ದಿನಗಳವರೆಗೂ ಮಹಾಸಂತೋಷದಿಂದ ಹುಳಿಯಿಲ್ಲದ ರೊಟ್ಟಿಗಳ ಹಬ್ಬವನ್ನು ಆಚರಿಸಿದರು. ಲೇವಿಯರೂ ಯಾಜಕರೂ ಮಹಾವಾದ್ಯದೊಡನೆ ಸರ್ವೇಶ್ವರನನ್ನು ಪ್ರತಿದಿನವೂ ಕೀರ್ತಿಸುತ್ತಿದ್ದರು.",
			"22": "<sup>22</sup> ಹಿಜ್ಕೀಯನು ಸರ್ವೇಶ್ವರನ ಸೇವೆಯಲ್ಲಿ ನಿಪುಣರಾದ ಎಲ್ಲ ಲೇವಿಯರನ್ನು ಪುರಸ್ಕರಿಸಿದನು. ನೇಮಕವಾದ ಏಳು ದಿನಗಳವರೆಗೂ ಜನರು ಶಾಂತಿಸಮಾಧಾನ ಬಲಿಗಳನ್ನು ಅರ್ಪಿಸುತ್ತಾ ತಮ್ಮ ಪಿತೃಗಳ ದೇವರಾದ ಸರ್ವೇಶ್ವರನನ್ನು ತಮ್ಮ ದೇವರೆಂದು ಕೊಂಡಾಡುತ್ತಾ ಔತಣಮಾಡಿದರು.",
			"23": "<sup>23</sup> ಜುದೇಯದ ಅರಸ ಹಿಜ್ಕೀಯನು ಸಾವಿರ ಹೋರಿಗಳನ್ನು, ಏಳು ಸಾವಿರ ಕುರಿಗಳನ್ನು, ಪದಾಧಿಕಾರಿಗಳು ಸಾವಿರ ಹೋರಿಗಳನ್ನು ಹಾಗು ಹತ್ತು ಸಾವಿರ ಕುರಿಗಳನ್ನು ಜನರಿಗೆ ದಾನಮಾಡಿದರು; ಯಾಜಕರಲ್ಲಿ ಬಹುಮಂದಿ ತಮ್ಮನ್ನು ಶುದ್ಧಪಡಿಸಿಕೊಂಡರು.",
			"24": "<sup>24</sup> ಈ ಕಾರಣ, ಸಮೂಹದವರು ಇನ್ನೂ ಏಳು ದಿವಸ ಹಬ್ಬಮಾಡಬೇಕೆಂದು ತೀರ್ಮಾನಿಸಿಕೊಂಡರು. ಆ ಏಳು ದಿನಗಳಲ್ಲಿಯೂ ಬಹಳವಾಗಿ ಸಂತೋಷದಿಂದ ಹಬ್ಬವನ್ನು ಆಚರಿಸಿದರು.",
			"25": "<sup>25</sup> ಯೆಹೂದ್ಯರ ಸರ್ವಸಮೂಹದವರು, ಯಾಜಕರು, ಲೇವಿಯರು ಇಸ್ರಯೇಲ್ ಪ್ರಾಂತಗಳಿಂದ ನೆರೆದುಬಂದವರು ಎಲ್ಲರು ಹಾಗು ಇಸ್ರಯೇಲ್, ಜುದೇಯ ಪ್ರಾಂತ್ಯಗಳಲ್ಲಿದ್ದ ಪ್ರವಾಸಿಗಳು ಈ ಉತ್ಸವದಲ್ಲಿ ಪಾಲುಗಾರರಾಗಿದ್ದರು.",
			"26": "<sup>26</sup> ದಾವೀದನ ಮಗನೂ ಇಸ್ರಯೇಲರ ಅರಸನೂ ಆದ ಸೊಲೊಮೋನನ ಕಾಲದಿಂದ ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಅಂಥ ಉತ್ಸವ ನಡೆದಿರಲಿಲ್ಲ. ಆದುದರಿಂದ ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಹೇಳತೀರದ ಆನಂದವಿತ್ತು.",
			"27": "<sup>27</sup> ಯಾಜಕರು ಆಗಿದ್ದ ಲೇವಿಯರು ಆಮೇಲೆ ಎದ್ದು ನಿಂತು ಜನರನ್ನು ಆಶೀರ್ವದಿಸಿದರು. ಅವರ ಸ್ವರ ದೇವರಿಗೆ ಕೇಳಿಸಿತು; ಅವರ ಪ್ರಾರ್ಥನೆ ಪರಲೋಕದಲ್ಲಿರುವ ಆ ದೇವರ ಪರಿಶುದ್ಧ ನಿವಾಸಕ್ಕೆ ಮುಟ್ಟಿತು."
		},
		"31": {
			"1": "<sup>1</sup> ಇದಾದನಂತರ ನೆರೆದುಬಂದ ಇಸ್ರಯೇಲರೆಲ್ಲರು ಜುದೇಯ ನಾಡಿನ ಪಟ್ಟಣಗಳಿಗೆ ಹೋಗಿ ಕಲ್ಲುಕಂಬಗಳನ್ನು ಒಡೆದು, ಆಶೇರ ವಿಗ್ರಹಸ್ತಂಭಗಳನ್ನು ಕಡಿದುಹಾಕಿ, ಪೂಜಾಸ್ಥಳಗಳನ್ನೂ ಯಜ್ಞವೇದಿಗಳನ್ನೂ ಹಾಳುಮಾಡಿಬಿಟ್ಟರು. ಜುದೇಯ ಹಾಗು ಬೆನ್ಯಾಮೀನ್ ಪ್ರಾಂತಗಳಲ್ಲಿ ಮಾತ್ರವಲ್ಲ, ಎಫ್ರಯಿಮ್, ಮನಸ್ಸೆ ಪ್ರಾಂತಗಳಲ್ಲೂ ಯಾವುದೊಂದನ್ನೂ ಉಳಿಸಲಿಲ್ಲ. ಆಮೇಲೆ ಇಸ್ರಯೇಲರೆಲ್ಲರೂ ತಮ್ಮ ತಮ್ಮ ಆಸ್ತಿಪಾಸ್ತಿಯಿದ್ದ ಪಟ್ಟಣಗಳಿಗೆ ಹೋದರು.",
			"2": "<sup>2</sup> ಯಾಜಕರ ಮತ್ತು ಲೇವಿಯರ ಆಯಾ ವರ್ಗಗಳವರನ್ನು ಅವರವರಿಗೆ ನೇಮಕವಾದ ಸೇವೆಗೆ ಅಂದರೆ, ದಹನಬಲಿ, ಶಾಂತಿಸಮಾಧಾನ ಬಲಿ ಇವುಗಳನ್ನು ಸಮರ್ಪಿಸುವುದಕ್ಕೂ ಸರ್ವೇಶ್ವರನ ಪಾಳೆಯದ ದ್ವಾರಗಳಲ್ಲಿ ಸೇರಿ ಸರ್ವೇಶ್ವರನನ್ನು ಆರಾಧಿಸುತ್ತಾ ಕೀರ್ತಿಸುತ್ತಾ ಕೃತಜ್ಞತಾಸ್ತುತಿ ಮಾಡುತ್ತಾ ಇರುವುದಕ್ಕೂ ಹಿಜ್ಕೀಯನು ನೇಮಿಸಿದನು.",
			"3": "<sup>3</sup> ಇದಲ್ಲದೆ, ಅವನು ಸರ್ವೇಶ್ವರನ ಧರ್ಮಶಾಸ್ತ್ರ ವಿಧಿಗನುಸಾರ ಬೆಳಿಗ್ಗೆ, ಸಂಜೆ, ಸಬ್ಬತ್‍ದಿನ, ಅಮಾವಾಸ್ಯೆ ಹಾಗು ಜಾತ್ರೆಗಳಲ್ಲಿ ಸಮರ್ಪಣೆ ಆಗತಕ್ಕ ದಹನ ಬಲಿಗಳಿಗೆ ಬೇಕಾದುದನ್ನೆಲ್ಲಾ ತನ್ನ ಆಸ್ತಿಯಿಂದಲೇ ಸಲ್ಲಿಸಲಾಗುವುದೆಂದು ಸೂಚಿಸಿದನು.",
			"4": "<sup>4</sup> ಯಾಜಕರು ಮತ್ತು ಲೇವಿಯರು ಸರ್ವೇಶ್ವರನ ಧರ್ಮದಲ್ಲಿ ಆಸಕ್ತರಾಗಿರುವುದಕ್ಕಾಗಿ ಅವರಿಗೆ ಸಲ್ಲತಕ್ಕ ಜೀವನಾಂಶವನ್ನು, ಜೆರುಸಲೇಮಿನಲ್ಲಿ ವಾಸಿಸುವ ಜನರು ಕೊಡಬೇಕೆಂತಲೂ ಗೊತ್ತುಮಾಡಿದನು.",
			"5": "<sup>5</sup> ಈ ರಾಜಾಜ್ಞೆಯು ಪ್ರಕಟವಾದ ಕೂಡಲೆ ಇಸ್ರಯೇಲರು ಧಾನ್ಯ, ದ್ರಾಕ್ಷಾರಸ, ಎಣ್ಣೆ, ಜೇನು ಮುಂತಾದವೆಲ್ಲಾ ಹುಟ್ಟುವಳಿಯಿಂದ ಪ್ರಥಮಫಲವನ್ನು ರಾಶಿರಾಶಿಯಾಗಿ ತಂದುಕೊಟ್ಟರು; ಮತ್ತು ತಮ್ಮ ಎಲ್ಲಾ ಆದಾಯದ ದಶಮಾಂಶವನ್ನೂ ಉದಾರವಾಗಿ ಸಲ್ಲಿಸಿದರು.",
			"6": "<sup>6</sup> ಜುದೇಯದ ಪಟ್ಟಣಗಳಲ್ಲಿ ವಾಸಿಸುತ್ತಿದ್ದ ಇಸ್ರಯೇಲರೂ ಯೆಹೂದ್ಯರೂ ಕುರಿದನಗಳಲ್ಲಿ ದಶಮಾಂಶವನ್ನೂ ಕೊಟ್ಟದ್ದಲ್ಲದೆ, ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ಸಮರ್ಪಣೆಯಾಗತಕ್ಕ ಪರಿಶುದ್ಧ ದ್ರವ್ಯಗಳಲ್ಲಿಯೂ ದಶಮಾಂಶವನ್ನು ತಂದು ರಾಶಿಹಾಕಿದರು.",
			"7": "<sup>7</sup> ಹೀಗೆ ರಾಶಿಹಾಕುವುದು ಮೂರನೆಯ ತಿಂಗಳಲ್ಲಿ ಪ್ರಾರಂಭವಾಗಿ ಏಳನೆಯ ತಿಂಗಳಲ್ಲಿ ಮುಗಿಯಿತು.",
			"8": "<sup>8</sup> ಹಿಜ್ಕೀಯನೂ ಪದಾಧಿಕಾರಿಗಳೂ ಬಂದು ಆ ರಾಶಿಗಳನ್ನು ನೋಡಿದಾಗ ಸರ್ವೇಶ್ವರನನ್ನು ಕೊಂಡಾಡಿ ಅವರ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಯೇಲರನ್ನು ಹರಸಿದರು.",
			"9": "<sup>9</sup> ಹಿಜ್ಕೀಯನು ಯಾಜಕರ ಮತ್ತು ಲೇವಿಯರ ಬಳಿ ಆ ರಾಶಿಗಳ ಬಗ್ಗೆ ವಿಚಾರಿಸಿಧಾಗ,",
			"10": "<sup>10</sup> ಚಾದೋಕ್ ಸಂತಾನದ ಮಹಾಯಾಜಕ ಅಜರ್ಯನು, “ಜನರು ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ಪ್ರತ್ಯೇಕಿಸತಕ್ಕದ್ದನ್ನು ಅವರ ಆಲಯಕ್ಕೆ ತಂದಂದಿನಿಂದ ಸರ್ವೇಶ್ವರ ತಮ್ಮ ಪ್ರಜೆಗೆ ಸಮೃದ್ಧಿಯನ್ನು ಅನುಗ್ರಹಿಸಿದ್ದಾರೆ. ನಾವು ಉಂಡು ತೃಪ್ತರಾಗಿ, ಇನ್ನೂ ಇಷ್ಟು ದೊಡ್ಡ ರಾಶಿಯನ್ನು ಉಳಿಸಿಕೊಂಡಿರುತ್ತೇವೆ,” ಎಂದನು.",
			"11": "<sup>11</sup> ಬಳಿಕ ಹಿಜ್ಕೀಯನು ಸರ್ವೇಶ್ವರನ ಆಲಯದಲ್ಲಿ ಕೊಠಡಿಗಳನ್ನು ಸಿದ್ಧಮಾಡಲು ಆಜ್ಞಾಪಿಸಿದನು.",
			"12": "<sup>12</sup> ಅವು ಸಿದ್ಧವಾದ ಮೇಲೆ ಜನರು ಕಾಣಿಕೆಗಳನ್ನು, ತಮ್ಮ ಆದಾಯದ ದಶಮಾಂಶವನ್ನು ಹಾಗು ದೇವರಿಗೆ ಮುಡುಪಾಗಿ ಇಟ್ಟಿದ್ದನ್ನು ನಿರ್ವಂಚನೆಯಿಂದ ತಂದು ಅವುಗಳಲ್ಲಿ ಹಾಕುತ್ತಿದ್ದರು. ಈ ಎಲ್ಲಾ ಉಗ್ರಾಣಗಳ ಮೇಲ್ವಿಚಾರಣೆಯಲ್ಲಿ ಲೇವಿಯನಾದ ಕೋನನ್ಯನು ಮುಖ್ಯಸ್ಥನಾಗಿದ್ದನು; ಅವನ ತಮ್ಮ ಶಿಮ್ಮಿಯು ದ್ವಿತೀಯ ಸ್ಥಾನದವನಾಗಿದ್ದನು.",
			"13": "<sup>13</sup> ಅರಸ ಹಿಜ್ಕೀಯನ ಮತ್ತು ಸರ್ವೇಶ್ವರನ ಆಲಯದ ಅಧಿಪತಿಯಾದ ಅಜರ್ಯನ ನಿಯಮದಂತೆ ಯೆಹೀಯೇಲ್, ಅಜಜ್ಯ, ನಹತ್, ಅಸಾಹೇಲ್, ಯೆರೀಮೋತ್, ಯೋಜಾಬಾದ್, ಎಲೀಯೇಲ್, ಇಸ್ಮಕ್ಯ, ಮಹತ್, ಬೆನಾಯ ಇವರು ಕೋನನ್ಯನ ಮತ್ತು ಅವನ ತಮ್ಮನಾದ ಶಿಮ್ಮಿಯ ಕೈಕೆಳಗಿನ ಪಾರುಪತ್ಯಗಾರರಾಗಿದ್ದರು.",
			"14": "<sup>14</sup> ಪೂರ್ವದಿಕ್ಕಿನ ದ್ವಾರಪಾಲಕನಾಗಿದ್ದ ಇಮ್ಲನ ಮಗ ಕೋರೆ ಎಂಬ ಲೇವಿಯನು, ಜನರು ಸ್ವಂತ ಇಚ್ಛೆಯಿಂದ ದೇವರಿಗೆ ಕಾಣಿಕೆಯಾಗಿ ಸಮರ್ಪಿಸುತ್ತಿದ್ದ ವಸ್ತುಗಳ ಮೇಲ್ವಿಚಾರಕನಾಗಿದ್ದನು. ಅದು ಮಾತ್ರವಲ್ಲ, ಸರ್ವೇಶ್ವರನಿಗೆ ಪ್ರತ್ಯೇಕಿಸಲ್ಪಟ್ಟ ದ್ರವ್ಯ, ಮಹಾಪರಿಶುದ್ಧ ವಸ್ತು, ಇವುಗಳನ್ನು ಹಂಚಿಕೊಡುವವನೂ ಆಗಿದ್ದನು.",
			"15": "<sup>15</sup> ಇವನ ಕೈಕೆಳಗೆ ಏದೆನ್, ಮಿನ್ಯಾಮಿನ್, ಯೇಷೂವ, ಶೆಮಾಯ, ಅಮರ್ಯ, ಶೆಕನ್ಯ ಎಂಬವರಿದ್ದರು. ಇವರು ಯಾಜಕರ ಪಟ್ಟಣಗಳಲ್ಲಿದ್ದು, ತಮ್ಮ ಬಂಧುಗಳಿಗೆ ದೊಡ್ಡವರು ಚಿಕ್ಕವರು ಎಂಬ ವ್ಯತ್ಯಾಸಮಾಡದೆ, ಅವರವರ ವರ್ಗಗಳ ಪ್ರಕಾರ ಪಾಲುಕೊಡುತ್ತಿದ್ದರು.",
			"16": "<sup>16</sup> (ಆಯಾ ಉದ್ಯೋಗ ವರ್ಗಗಳಿಗನುಸಾರ ಅನುದಿನ ಸೇವೆಗಾಗಿ ಸರ್ವೇಶ್ವರನ ಆಲಯಕ್ಕೆ ಬರುತ್ತಿದ್ದ ಗಂಡಸರಲ್ಲಿ ಮೂವತ್ತು ವರ್ಷ ಮತ್ತು ಅದಕ್ಕೂ ಮೇಲ್ಪಟ್ಟ ವಯಸ್ಸು ಉಳ್ಳವರಿಗೆ ಪಟ್ಟಿಯಲ್ಲಿ ಬರೆದಿದ್ದ ಹಾಗೆ ಕೊಡುತ್ತಿದ್ದರು.",
			"17": "<sup>17</sup> ಆ ಪಟ್ಟಿಯಲ್ಲಿ ಗೋತ್ರಗಳಿಗನುಸಾರ ಹಾಗು ಆಯಾ ಉದ್ಯೋಗ ವರ್ಗಗಳಿಗನುಸಾರ ಇಪ್ಪತ್ತು ವರ್ಷ ಮತ್ತು ಅದಕ್ಕೆ ಮೇಲ್ಪಟ್ಟ ವಯಸ್ಸುಳ್ಳ ಯಾಜಕರು, ಲೇವಿಯರು,",
			"18": "<sup>18</sup> ಅವರವರ ಹೆಂಡತಿಯರು, ಗಂಡು-ಹೆಣ್ಣು ಮಕ್ಕಳು, ಅಂತು ತಮ್ಮ ಉದ್ಯೋಗದ ನಿಮಿತ್ತ ತಮ್ಮನ್ನೇ ದೇವರಿಗೆ ಪ್ರತಿಷ್ಠಿಸಿಕೊಂಡ ಸಮೂಹದವರೂ ಅವರಿಗೆ ಸೇರಿದವರೆಲ್ಲರೂ ಲಿಖಿತರಾಗಿದ್ದರು.)",
			"19": "<sup>19</sup> ಇದಲ್ಲದೆ ಆಯಾ ಪಟ್ಟಣಗಳಿಗೆ ಸೇರಿದ ಗೋಮಾಳಗಳಲ್ಲಿದ್ದ ಆರೋನನ ಸಂತಾನದ ಯಾಜಕರಿಗೆ ಪಾಲುಕೊಡುತ್ತಿದ್ದರು. ಯಾಜಕ ಸಂತಾನದ ಎಲ್ಲ ಗಂಡಸರಿಗೂ, ಪಟ್ಟಿಯಲ್ಲಿ ಲಿಖಿತರಾದ ಎಲ್ಲ ಲೇವಿಯರಿಗೂ ಸಿಕ್ಕತಕ್ಕ ಭಾಗಗಳನ್ನು ಹಂಚಿಕೊಡುವುದಕ್ಕೆ ಹೆಸರಿಸಲಾದ ಪುರುಷರಿದ್ದರು.",
			"20": "<sup>20</sup> ಹಿಜ್ಕೀಯನು ಜುದೇಯದಲ್ಲೆಲ್ಲಾ ಇದೇ ವ್ಯವಸ್ಥೆಯನ್ನು ಮಾಡಿದನು. ಅವನು ತನ್ನ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ಒಳ್ಳೆಯವನು, ನೀತಿವಂತನು ಹಾಗು ನಂಬಿಗಸ್ತನು ಆಗಿ ನಡೆದನು.",
			"21": "<sup>21</sup> ದೇವಾಲಯದ ಸೇವಾಸಂಬಂಧದಲ್ಲೂ ಧರ್ಮಶಾಸ್ತ್ರವಿಧಿಗಳ ಸಂಬಂಧದಲ್ಲೂ ತಾನು ಪ್ರಾರಂಭಿಸಿದ ಎಲ್ಲಾ ಕಾರ್ಯಗಳನ್ನು ಯಥಾರ್ಥಚಿತ್ತದಿಂದ ಮಾಡಿ ಮುಗಿಸಿ, ತನ್ನ ದೇವರ ಒಲುಮೆಯನ್ನು ಗಳಿಸಿಕೊಂಡನು."
		},
		"32": {
			"1": "<sup>1</sup> ಈ ಧಾರ್ಮಿಕ ಸುಧಾರಣೆಗಳಾದ ನಂತರ ಅಸ್ಸೀರಿಯಾದ ಅರಸನಾದ ಸನ್ಹೇರೀಬನು ಯುದ್ಧಕ್ಕೆ ಹೊರಟು ಜುದೇಯಕ್ಕೆ ನುಗ್ಗಿದನು. ಕೋಟೆಕೊತ್ತಲುಗಳುಳ್ಳ ಪಟ್ಟಣಗಳನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳಬೇಕೆಂದು ಮುತ್ತಿಗೆ ಹಾಕಿದನು.",
			"2": "<sup>2</sup> ನಾಡಿನೊಳಗೆ ನುಗ್ಗಿದ ಸನ್ಹೇರೀಬನು ಜೆರುಸಲೇಮಿನ ಮೇಲೂ ಯುದ್ಧಕ್ಕೆ ಬರಲು ನಿಶ್ಚಯಿಸಿದ್ದಾನೆಂದು ಹಿಜ್ಕೀಯನಿಗೆ ಗೊತ್ತಾಯಿತು.",
			"3": "<sup>3</sup> ಅವನು ಪಟ್ಟಣದ ಹೊರಗಿರುವ ಎಲ್ಲಾ ಬಾವಿಗಳನ್ನು ಮುಚ್ಚಿಸಬೇಕೆಂದು ಮನಸ್ಸುಮಾಡಿ ತನ್ನ ಪ್ರಮುಖರ ಮತ್ತು ಶೂರರ ಮುಂದೆ ಆ ಪ್ರಸ್ತಾಪವೆತ್ತಿ ಅವರ ಸಹಾಯವನ್ನು ಪಡೆದುಕೊಂಡನು.",
			"4": "<sup>4</sup> ಆಗ ಅನೇಕರು ಸೇರಿ, “ಇಲ್ಲಿಗೆ ಬರುವ ಅಸ್ಸೀರಿಯದ ರಾಜನಿಗೆ ಬೇಕಾದಷ್ಟು ನೀರು ಏಕೆ ಸಿಕ್ಕಬೇಕು?,” ಎಂದುಕೊಂಡು ಎಲ್ಲಾ ಬಾವಿಗಳನ್ನೂ ನಾಡಿನ ಮಧ್ಯದಲ್ಲಿ ಹರಿಯುವ ಹಳ್ಳವನ್ನೂ ಮುಚ್ಚಿಬಿಟ್ಟರು.",
			"5": "<sup>5</sup> ಇದರಿಂದ ಅರಸನು ಧೈರ್ಯಗೊಂಡು, ಅಲ್ಲಲ್ಲಿ ಬಿದ್ದುಹೋಗಿದ್ದ ಪೌಳಿಗೋಡೆಯನ್ನು ದುರಸ್ತಿಮಾಡಿಸಿ, ಅದರ ಮೇಲೆ ಬುರುಜುಗಳನ್ನೂ ಹೊರಗೆ ಇನ್ನೊಂದು ಗೋಡೆಯನ್ನೂ ಕಟ್ಟಿಸಿದನು. ದಾವೀದ ನಗರದ ಮಿಲ್ಲೋ ಕೋಟೆಯನ್ನು ಭದ್ರಪಡಿಸಿದನು. ಅನೇಕ ಆಯುಧಗಳನ್ನೂ ಗುರಾಣಿಗಳನ್ನೂ ಮಾಡಿಸಿದನು.",
			"6": "<sup>6</sup> ಅನಂತರ ಅವನು ಜನರ ಮೇಲೆ ಸೇನಾಪತಿಗಳನ್ನು ನೇಮಿಸಿ ಅವರನ್ನು ಊರುಬಾಗಿಲಿನ ಬಯಲಿನಲ್ಲಿ ಕೂಡಿಸಿದನು. ಅವರಿಗೆ, “ಶೂರರಾಗಿರಿ, ಧೈರ್ಯದಿಂದಿರಿ;",
			"7": "<sup>7</sup> ಅಸ್ಸೀರಿಯದ ಅರಸನಿಗೂ ಅವನೊಂದಿಗಿರುವ ಆ ದೊಡ್ಡ ಸೈನ್ಯಕ್ಕೂ ಅಂಜಬೇಡಿ, ಕಳವಳಪಡಬೇಡಿ. ಅವನಿಗಿರುವ ಸಹಾಯಕ್ಕಿಂತ ನಮಗಿರುವ ಸಹಾಯ ದೊಡ್ಡದು.",
			"8": "<sup>8</sup> ಅವನಿಗಿರುವ ಸಹಾಯ ಮೂಳೆ ಮಾಂಸದ ತಡಿಕೆಯ ಬಲ; ನಮಗಿದ್ದಾರೆ ನಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ. ಅವರೇ ನಮಗೆ ನೆರವಾಗಿ, ಯುದ್ಧಗಳಲ್ಲಿ ನಮ್ಮ ಪರವಾಗಿ ಕಾದಾಡುವರು,” ಎಂದು ಹೇಳಿ ಹುರಿದುಂಬಿಸಿದನು. ಜನರು ಜುದೇಯದ ಅರಸ ಹಿಜ್ಕೀಯನ ಮಾತುಗಳನ್ನು ಕೇಳಿ ಭರವಸೆಯುಳ್ಳವರಾದರು.",
			"9": "<sup>9</sup> ಇದಾದ ಮೇಲೆ, ಸರ್ವಸೇನೆಯೊಡನೆ ಲಾಕೀಷಿನ ಎದುರಿನಲ್ಲಿ ಪಾಳೆಯಮಾಡಿಕೊಂಡು ಇದ್ದ ಅಸ್ಸೀರಿಯದ ಅರಸ ಸನ್ಹೇರೀಬನು ತನ್ನ ಮುಖಾಂತರ ಜುದೇಯದ ಅರಸ ಹಿಜ್ಕೀಯನಿಗೂ ಜೆರುಸಲೇಮಿನಲ್ಲಿದ್ದ ಎಲ್ಲಾ ಯೆಹೂದ್ಯರಿಗೂ ಹೀಗೆಂದು ಹೇಳಿಸಿದನು:",
			"10": "<sup>10</sup> “ಅಸ್ಸೀರಿಯದ ಅರಸ ಸನ್ಹೇರೀಬನ ಮಾತಿಗೆ ಕಿವಿಗೊಡಿ; ನೀವು ಯಾವುದರ ಮೇಲೆ ಭರವಸೆಯಿಟ್ಟು ಮುತ್ತಿಗೆಗೆ ಗುರಿಯಾಗಲಿರುವ ಜೆರುಸಲೇಮಿನಲ್ಲಿ ನಿಂತಿದ್ದೀರಿ?",
			"11": "<sup>11</sup> ಹಿಜ್ಕೀಯನು, ‘ನಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರಸ್ವಾಮಿ ಅಸ್ಸೀರಿಯಾದ ಅರಸನ ಕೈಯಿಂದ ನಮ್ಮನ್ನು ರಕ್ಷಿಸುವನು,’ ಎಂಬ ನಂಬಿಕೆಯನ್ನು ನಿಮ್ಮಲ್ಲಿ ಹುಟ್ಟಿಸುವುದಕ್ಕೆ ಪ್ರಯತ್ನಿಸುತ್ತಿರುವುದು ನಿಮ್ಮನ್ನು ಹಸಿವೆ ನೀರಡಿಕೆಗಳಿಂದ ಸಾಯಿಸುವುದಕ್ಕೇ ಅಲ್ಲದೆ ಮತ್ತೇನಕ್ಕೂ ಅಲ್ಲ.",
			"12": "<sup>12</sup> ಹಿಜ್ಕೀಯನು ಒಂದೇ ಬಲಿಪೀಠದ ಮುಂದೆ ಆರಾಧನೆಮಾಡಿ ಅದರ ಮೇಲೆಯೇ ಧೂಪಹಾಕಬೇಕೆಂಬುದಾಗಿ ಯೆಹೂದ್ಯರಿಗೂ ಜೆರುಸಲೇಮಿಗೂ ಆಜ್ಞಾಪಿಸಿ, ದೇವರ ಬೇರೆ ಎಲ್ಲ ಪೂಜಾಸ್ಥಳಗಳನ್ನೂ ಯಜ್ಞವೇದಿಗಳನ್ನೂ ಹಾಳುಮಾಡಿದನು ಅಲ್ಲವೆ?",
			"13": "<sup>13</sup> ನಾನೂ ನನ್ನ ತಂದೆತಾತಂದಿರೂ ಎಲ್ಲಾ ದೇಶಗಳ ಜನಾಂಗಗಳಿಗೆ ಮಾಡಿದ್ದನ್ನು ನೀವು ಕೇಳಲಿಲ್ಲವೋ? ಆ ಜನಾಂಗಗಳ ದೇವತೆಗಳಿಗೆ ಅವರ ದೇಶವನ್ನು ನನ್ನ ಕೈಗೆ ಸಿಕ್ಕದಂತೆ ತಪ್ಪಿಸುವುದಕ್ಕೆ ಶಕ್ತಿಯಿತ್ತೋ?",
			"14": "<sup>14</sup> ನನ್ನ ತಂದೆತಾತಂದಿರು ನಾಶಮಾಡಿಬಿಟ್ಟ ಆ ರಾಜ್ಯಗಳ ದೇವರುಗಳಲ್ಲಿ ಯಾವನು ತಾನೆ ತನ್ನ ಪ್ರಜೆಯನ್ನು ನನ್ನ ಕೈಯಿಂದ ಬಿಡಿಸುವುದಕ್ಕೆ ಸಮರ್ಥನಾಗಿದ್ದನು? ಹೀಗಿರುವಲ್ಲಿ ನಿಮ್ಮ ದೇವರು ನಿಮ್ಮನ್ನು ನನ್ನ ಕೈಗೆ ಸಿಕ್ಕದಂತೆ ತಪ್ಪಿಸಿ ಕಾಪಾಡುವುದಕ್ಕೆ ಶಕ್ತನಾಗಿರುತ್ತಾನೋ?",
			"15": "<sup>15</sup> ನೀವು ಹಿಜ್ಕೀಯನಿಂದ ಮರುಳಾಗಿ ಮೋಸಹೋಗಬೇಡಿ; ಅವನನ್ನು ನಂಬಲೂಬೇಡಿ. ಯಾವ ರಾಜ್ಯಜನಾಂಗಗಳ ದೇವತೆಯೂ ತನ್ನ ಪ್ರಜೆಯನ್ನು ನನ್ನ ಮತ್ತು ನನ್ನ ಪೂರ್ವಿಕರ ಕೈಯಿಂದ ಬಿಡಿಸಲಾರದೆಹೋದರು. ಅಂದ ಮೇಲೆ, ನಿಮ್ಮ ದೇವರಂಥವನು ನಿಮ್ಮನ್ನು ನನ್ನ ಕೈಗೆ ಸಿಕ್ಕದಂತೆ ತಪ್ಪಿಸಬಲ್ಲನೇ?\".",
			"16": "<sup>16</sup> ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೂ ಅವರ ಸೇವಕನಾದ ಹಿಜ್ಕೀಯನಿಗೂ ವಿರೋಧವಾದ ಈ ಮೊದಲಾದ ಮಾತುಗಳನ್ನು ಸನ್ಹೇರೀಬನು ತನ್ನ ಸೇವಕರ ಮುಖಾಂತರ ಹೇಳಿಕಳುಹಿಸಿದ್ದನು;",
			"17": "<sup>17</sup> ಅಷ್ಟುಮಾತ್ರವಲ್ಲ, ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರನನ್ನು ನಿಂದಿಸಿ ದೂಷಿಸುವುದಕ್ಕಾಗಿ ‘ಅನ್ಯದೇಶಗಳವರ ದೇವರುಗಳು ತಮ್ಮ ಜನರನ್ನು ನನ್ನ ಕೈಯಿಂದ ಬಿಡಿಸಲಾರದೆಹೋದಂತೆ ಹಿಜ್ಕೀಯನ ದೇವರೂ ತನ್ನ ಪ್ರಜೆಯನ್ನು ನನ್ನ ಕೈಯಿಂದ ಬಿಡಿಸಲಾರನು’ ಎಂಬುದಾಗಿ ಪತ್ರದಲ್ಲಿ ಬರೆದು ಕಳುಹಿಸಿದ್ದನು.",
			"18": "<sup>18</sup> ಜೆರುಸಲೇಮಿಗೆ ಬಂದ ಆ ಅಸ್ಸೀರಿಯದವರು ಗೋಡೆಯ ಮೇಲಿದ್ದ ಜನರನ್ನು ಬೆದರಿಸಿ, ಅಂಜಿಸಿ ಪಟ್ಟಣವನ್ನು ಸ್ವಾಧೀನಮಾಡಿಕೊಳ್ಳಲು",
			"19": "<sup>19</sup> ಜೆರುಸಲೇಮಿನ ದೇವರು ಮನುಷ್ಯರ ಕೈಕೆಲಸವಾಗಿರುವ ಅನ್ಯದೇಶಗಳ ಜನರ ದೇವತೆಗಳಿಗೆ ಸಮಾನನಾಗಿರುತ್ತಾನೆ, ಎಂಬ ಅರ್ಥದಲ್ಲಿ ಅವರ ಬಗ್ಗೆ ಗಟ್ಟಿಯಾಗಿ ಕೂಗಿ ಹೇಳಿದರು; ಆ ಜನರೊಡನೆ ಹಿಬ್ರುಭಾಷೆಯಲ್ಲೆ ಮಾತಾಡಿದರು.",
			"20": "<sup>20</sup> ಈ ಕಾರಣ ಅರಸ ಹಿಜ್ಕೀಯನು ಹಾಗು ಆಮೋಚನ ಮಗ ಯೆಶಾಯನೆಂಬ ಪ್ರವಾದಿಯು ಪರಲೋಕಕ್ಕೆ ಮೊರೆಯಿಟ್ಟು ಪ್ರಾರ್ಥಿಸಿದರು. ಸರ್ವೇಶ್ವರಸ್ವಾಮಿ ದೂತನನ್ನು ಕಳುಹಿಸಿ",
			"21": "<sup>21</sup> ಅಸ್ಸೀರಿಯದ ಅರಸನ ದಂಡಿನಲ್ಲಿದ್ದ ಎಲ್ಲ ಶೂರರನ್ನೂ ನಾಯಕರನ್ನೂ ಅಧಿಪತಿಗಳನ್ನೂ ಸಂಹರಿಸಿದರು; ಆ ಅರಸನು ನಾಚಿಕೆಯಿಂದ ತನ್ನ ದೇಶಕ್ಕೆ ಹಿಂದಿರುಗಬೇಕಾಯಿತು. ಅಲ್ಲಿ ಅವನು ತನ್ನ ದೇವರ ಗುಡಿಗೆ ಹೋಗಿದ್ದಾಗ ಅವನ ಸ್ವಂತ ಮಕ್ಕಳೇ ಅವನನ್ನು ಕತ್ತಿಯಿಂದ ಕೊಂದರು.",
			"22": "<sup>22</sup> ಹೀಗೆ ಸರ್ವೇಶ್ವರ, ಹಿಜ್ಕೀಯನನ್ನೂ ಜೆರುಸಲೇಮಿನವರನ್ನೂ ಅಸ್ಸೀರಿಯದ ಅರಸ ಸನ್ಹೇರೀಬನ ಕೈಗೂ ಸುತ್ತಲಿನ ಎಲ್ಲ ಶತ್ರುಗಳ ಕೈಗೂ ಸಿಕ್ಕದಂತೆ ತಪ್ಪಿಸಿ ಕಾಪಾಡುತ್ತಿದ್ದರು.",
			"23": "<sup>23</sup> ಅನೇಕರು ಸರ್ವೇಶ್ವರನಿಗೆ ಕಾಣಿಕೆಗಳನ್ನೂ ಜುದೇಯದ ಅರಸ ಹಿಜ್ಕೀಯನಿಗೆ ಶ್ರೇಷ್ಠವಸ್ತುಗಳನ್ನೂ ತೆಗೆದುಕೊಂಡು ಬಂದರು. ಅಂದಿನಿಂದ ಎಲ್ಲ ರಾಷ್ಟ್ರಗಳವರು ಹಿಜ್ಕೀಯನನ್ನು ಮಹಾನ್‍ವ್ಯಕ್ತಿಯೆಂದು ಸನ್ಮಾನಿಸುತ್ತಿದ್ದರು.",
			"24": "<sup>24</sup> ಕ್ರಮೇಣ ಹಿಜ್ಕೀಯನು ಮರಣಕರ ರೋಗಕ್ಕೆ ಗುರಿಯಾದನು. ಆಗ ಅವನು ದೇವರನ್ನು ಪ್ರಾರ್ಥಿಸಲು ಅವರು ಸದುತ್ತರವನ್ನು ದಯಪಾಲಿಸಿ ಅವನಿಗೆ ಒಂದು ಗುರುತನ್ನು ಅನುಗ್ರಹಿಸಿದರು.",
			"25": "<sup>25</sup> ಆದರೆ ಹಿಜ್ಕೀಯನು ಈ ಉಪಕಾರಕ್ಕೆ ತಕ್ಕಂತೆ ನಡೆಯದೆ ಅಹಂಕಾರಿಯಾದನು. ಆದ್ದರಿಂದ ಜುದೇಯದ ಮೇಲೂ ಜೆರುಸಲೇಮಿನವರ ಮೇಲೂ ದೇವರ ಕೋಪವೆರಗಿತು.",
			"26": "<sup>26</sup> ಆಗ ಹಿಜ್ಕೀಯನ ಗರ್ವ ಇಳಿಯಿತು; ಜೆರುಸಲೇಮಿನವರೊಡನೆ ಅವನು ದೀನಮನಸ್ಕನಾದನು. ಈ ಕಾರಣ ಅವನ ಜೀವಮಾನದಲ್ಲಿ ಸರ್ವೇಶ್ವರನ ಕೋಪ ಅವರ ಮೇಲೆ ಬರಲಿಲ್ಲ.",
			"27": "<sup>27</sup> ಹಿಜ್ಕೀಯನಿಗೆ ಅತ್ಯಧಿಕವಾದ ಧನಘನತೆಗಳು ಒದಗಿದವು; ಅವನು ಬೆಳ್ಳಿಬಂಗಾರ, ರತ್ನ, ಪರಿಮಳ ದ್ರವ್ಯ, ಗುರಾಣಿ ಮುಂತಾದ ಶ್ರೇಷ್ಠಾಯುಧಗಳನ್ನು ಇಡುವುದಕ್ಕಾಗಿ ಭಂಡಾರಗಳನ್ನು ಏರ್ಪಡಿಸಿದನು. ಧಾನ್ಯ, ದ್ರಾಕ್ಷಾರಸ, ಎಣ್ಣೆ, ಇವುಗಳನ್ನು ಸಂಗ್ರಹಿಸುವುದಕ್ಕಾಗಿ ಉಗ್ರಾಣಗಳನ್ನು ಕಟ್ಟಿಸಿದನು;",
			"28": "<sup>28</sup> ಆಯಾಜಾತಿಯ ಪಶುಗಳಿಗಾಗಿ ಕೊಟ್ಟಿಗೆಯನ್ನೂ ಆಡುಕುರಿಗಳಿಗಾಗಿ ಹಟ್ಟಿಗಳನ್ನೂ ಸಿದ್ಧಮಾಡಿಸಿಕೊಂಡನು.",
			"29": "<sup>29</sup> ಇದಲ್ಲದೆ, ಪಟ್ಟಣಗಳನ್ನು ಕಟ್ಟಿಸಿ ದನಕುರಿಗಳ ದೊಡ್ಡ ಹಿಂಡುಗಳನ್ನು ಸಂಪಾದಿಸಿಕೊಂಡನು. ದೇವರು ಅವನಿಗೆ ಕೊಟ್ಟ ಸಂಪತ್ತು ಅಪರಿಮಿತವಾಗಿತ್ತು.",
			"30": "<sup>30</sup> ಮೇಲಣ ಗೀಹೋನ್ ಎಂಬ ಬುಗ್ಗೆಗೆ ಕಟ್ಟೆಕಟ್ಟಿಸಿ ಅದರ ನೀರು ನೆಟ್ಟಗೆ ದಾವೀದನಗರದ ಪೂರ್ವದಿಕ್ಕಿನ ಕಣಿವೆಗೆ ಹರಿಯುವಂತೆ ಮಾಡಿದವನು ಹಿಜ್ಕೀಯನೇ. ಅವನು ತನ್ನ ಎಲ್ಲ ಕಾರ್ಯಗಳಲ್ಲೂ ಯಶಸ್ವಿ ಆದನು.",
			"31": "<sup>31</sup> ಆದುದರಿಂದ ಅವನ ನಾಡಿನಲ್ಲಾದ ಅದ್ಭುತಕರವಾದ ಮಾರ್ಪಾಟುಗಳನ್ನು ಪರಿಶೀಲಿಸುವುದಕ್ಕಾಗಿ ಬಾಬಿಲೋನಿಯದ ಅಧಿಪತಿಗಳು ರಾಯಭಾರಿಗಳನ್ನು ಅವನ ಬಳಿಗೆ ಕಳುಹಿಸಿದರು. ಅವರು ಬಂದಾಗ, ಅವನನ್ನು ಪರೀಕ್ಷಿಸುವುದಕ್ಕಾಗಿ ಹಾಗು ಅವನ ಅಂತರಾಳವನ್ನು ತಿಳಿದುಕೊಳ್ಳುವುದಕ್ಕಾಗಿ ದೇವರು ಅವನನ್ನು ಕೈಬಿಟ್ಟರು.",
			"32": "<sup>32</sup> ಹಿಜ್ಕೀಯನ ಉಳಿದ ಚರಿತ್ರೆಯನ್ನೂ ಅವನ ಭಕ್ತಿಕಾರ್ಯಗಳನ್ನೂ ಆಮೋಚನ ಮಗ ಯೆಶಾಯನ ದರ್ಶನ ಗ್ರಂಥದಲ್ಲಿ ಹಾಗು ಯೆಹೂದ್ಯರ ಮತ್ತು ಇಸ್ರಯೇಲರ ರಾಜಗ್ರಂಥದಲ್ಲಿ ಬರೆಯಲಾಗಿದೆ.",
			"33": "<sup>33</sup> ಹಿಜ್ಕೀಯನು ಮೃತನಾಗಿ ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಲು, ಅವನ ಶವವನ್ನು ದಾವೀದವಂಶದವರ ಕುಟುಂಬದ ಸ್ಮಶಾನಭೂಮಿಯ ದಿಬ್ಬದ ಮೇಲೆ ಸಮಾಧಿಮಾಡಿದರು. ಅವನ ಶವಸಂಸ್ಕಾರ ನಡೆಯುವಾಗ ಎಲ್ಲಾ ಯೆಹೂದ್ಯರೂ ಜೆರುಸಲೇಮಿನವರೂ ಅವನನ್ನು ಬಹಳವಾಗಿ ಸನ್ಮಾನಿಸಿದರು. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗ ಮನಸ್ಸೆ ಅರಸನಾದನು."
		},
		"33": {
			"1": "<sup>1</sup> ಮನಸ್ಸೆ ಅರಸನಾದಾಗ ಹನ್ನೆರಡು ವರ್ಷದವನಾಗಿದ್ದನು. ಅವನು ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಐವತ್ತೈದು ವರ್ಷ ಆಳಿದನು.",
			"2": "<sup>2</sup> ಸರ್ವೇಶ್ವರ ಇಸ್ರಯೇಲರ ನಾಡಿನಿಂದ ಹೊರಡಿಸಿಬಿಟ್ಟ ಅನ್ಯಜನಾಂಗಗಳ ಅಸಹ್ಯಕಾರ್ಯಗಳನ್ನು ಅವನು ಅನುಸರಿಸಿ ಆ ಸರ್ವೇಶ್ವರನ ಚಿತ್ತಕ್ಕೆ ವಿರುದ್ಧವಾಗಿ ನಡೆದನು.",
			"3": "<sup>3</sup> ತನ್ನ ತಂದೆ ಹಿಜ್ಕೀಯನು ತೆಗೆದುಹಾಕಿದ್ದ ಪೂಜಾಸ್ಥಳಗಳನ್ನು ಮತ್ತೆ ಸ್ಥಾಪಿಸಿದನು. ಬಾಳ್‍ದೇವತೆಗಳಿಗಾಗಿ ಬಲಿಪೀಠಗಳನ್ನು ಕಟ್ಟಿಸಿದನು; ಅಶೇರ ವಿಗ್ರಹಸ್ತಂಭಗಳನ್ನು ನಿಲ್ಲಿಸಿ, ನಕ್ಷತ್ರಮಂಡಲಕ್ಕೆ ಕೈಮುಗಿದು ಆರಾಧಿಸಿದನು.",
			"4": "<sup>4</sup> ಸರ್ವೇಶ್ವರ ಜೆರುಸಲೇಮಿನಲ್ಲಿ ತನ್ನ ಹೆಸರನ್ನು ಸದಾಕಾಲ ಸ್ಥಾಪಿಸುವುದಕ್ಕಾಗಿ",
			"5": "<sup>5</sup> ಆರಿಸಿಕೊಂಡ ಆಲಯದ ಎರಡು ಪ್ರಾಕಾರಗಳಲ್ಲಿಯೂ ಸರ್ವ ನಕ್ಷತ್ರಮಂಡಲಕ್ಕಾಗಿ ಬಲಿಪೀಠಗಳನ್ನು ಕಟ್ಟಿಸಿದನು.",
			"6": "<sup>6</sup> ಇದಲ್ಲದೆ, ಅವನು ತನ್ನ ಮಕ್ಕಳನ್ನು ಬೆನ್‍ಹಿನ್ನೋಮ್ ಕಣಿವೆಯಲ್ಲಿ ಬಲಿಯಗ್ನಿ ಪರೀಕ್ಷೆಗೆ ಗುರಿಪಡಿಸಿದನು; ಕಣಿ ಹೇಳಿಸುವುದು, ಶಕುನ ನೋಡಿಸುವುದು, ತಂತ್ರಮಂತ್ರಗಳನ್ನು ಮಾಡಿಸುವುದು, ಭೂತಪ್ರೇತಗಳನ್ನು ವಿಚಾರಿಸುವವರನ್ನು ಸಂಪರ್ಕಿಸುವುದು ಇವೇ ಮೊದಲಾದ ದುಷ್ಕೃತ್ಯಗಳಿಂದ ಸರ್ವೇಶ್ವರನಿಗೆ ಕೋಪ ಬರಿಸಿದನು.",
			"7": "<sup>7</sup> ತಾನು ಮಾಡಿಸಿದ್ದ ವಿಗ್ರಹಸ್ತಂಭವನ್ನು ದೇವಾಲಯದಲ್ಲಿಡಿಸಿದನು; ಸರ್ವೇಶ್ವರ ಆ ಆಲಯದ ವಿಷಯದಲ್ಲಿ ದಾವೀದನಿಗೂ ಅವನ ಮಗ ಸೊಲೊಮೋನನಿಗೂ, “ಇಸ್ರಯೇಲರು, ತಮಗೆ ಮೋಶೆಯ ಮುಖಾಂತರ ಕೊಡಲಾದ, ನನ್ನ ಎಲ್ಲ ಧರ್ಮಶಾಸ್ತ್ರವಿಧಿನ್ಯಾಯಗಳನ್ನು ಅನುಸರಿಸಿ ನಡೆಯುವುದಾದರೆ, ನನ್ನ ನಾಮ ಮಹತ್ತು ಈ ದೇವಾಲಯದಲ್ಲಿ, ಇಸ್ರಯೇಲರ ಎಲ್ಲ ಊರುಗಳಲ್ಲಿ ಹಾಗು ನನಗೆ ಪ್ರಿಯವಾದ ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಸದಾಕಾಲ ಇರುವುದು;",
			"8": "<sup>8</sup> ಅದು ಮಾತ್ರವಲ್ಲ, ಇಸ್ರಯೇಲರು ಇನ್ನು ಮುಂದೆ ದೇಶಭ್ರಷ್ಟರಾಗಿರದೆ ತಮ್ಮ ಪಿತೃಗಳಿಗೆ ಕೊಡಲಾದ ನಾಡಿನಲ್ಲಿಯೇ ವಾಸವಾಗಿರುವಂತೆ ಮಾಡುವೆನು,” ಎಂದು ಹೇಳಿದ್ದರು.",
			"9": "<sup>9</sup> ಆದರೆ ಯೆಹೂದ್ಯರೂ ಜೆರುಸಲೇಮಿನವರೂ ಮನಸ್ಸೆಯಿಂದ ಪ್ರಚೋದಿತರಾಗಿ ಇಸ್ರಯೇಲರ ಮುಂದೆಯೇ ಸರ್ವೇಶ್ವರನಿಂದ ನಾಶಹೊಂದಿದ ಅನ್ಯಜನಾಂಗಗಳಿಗಿಂತಲೂ ದುಷ್ಟರಾದರು.",
			"10": "<sup>10</sup> ಸರ್ವೇಶ್ವರಸ್ವಾಮಿ ಮನಸ್ಸೆಯನ್ನೂ ಅವನ ಪ್ರಜೆಗಳನ್ನೂ ಎಚ್ಚರಿಸಿದರೂ ಅವರು ಲಕ್ಷಿಸಲಿಲ್ಲ.",
			"11": "<sup>11</sup> ಆದುದರಿಂದ ಅಸ್ಸೀರಿಯದ ಅರಸನ ಸೈನ್ಯಾಧಿಪತಿಗಳನ್ನು ಅವನ ಮೇಲೆ ಬರಮಾಡಿದರು. ಆ ಸೇನಾಧಿಪತಿಗಳು ಮನಸ್ಸೆಯನ್ನು ಹಿಡಿದು ಅವನನ್ನು ಬಂಧಿಸಿ, ಬೇಡಿಹಾಕಿ, ಬಾಬಿಲೋನಿಗೆ ಒಯ್ದರು.",
			"12": "<sup>12</sup> ಆದರೆ ಆ ಕಷ್ಟದಲ್ಲಿ ಮನಸ್ಸೆ ತನ್ನ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಪ್ರಸನ್ನತೆಯನ್ನು ಬೇಡಿಕೊಂಡನು; ತನ್ನ ಪಿತೃಗಳ ದೇವರ ಮುಂದೆ ತನ್ನನ್ನೇ ಬಹಳವಾಗಿ ತಗ್ಗಿಸಿಕೊಂಡು ಅವರನ್ನು ಪ್ರಾರ್ಥಿಸಿದನು.",
			"13": "<sup>13</sup> ಅವರು ಆಲಿಸಿ, ಅವನಿಗೆ ಸದುತ್ತರವನ್ನು ದಯಪಾಲಿಸಿದರು. ಅವನನ್ನು ಮರಳಿ ಜೆರುಸಲೇಮಿಗೆ ಬರಮಾಡಿ ಅರಸುತನವನ್ನು ಕೊಟ್ಟರು. ಆಗ ಸರ್ವೇಶ್ವರಸ್ವಾಮಿಯೇ ದೇವರು ಎಂಬುದು ಅವನಿಗೆ ಮನದಟ್ಟಾಯಿತು.",
			"14": "<sup>14</sup> ಇದಾದ ಮೇಲೆ, ಅವನು ಗೀಹೋನ್ ಬುಗ್ಗೆಯ ದಕ್ಷಿಣದ ಕಡೆಯಿಂದ ಮೀನುಬಾಗಿಲಿನ ಮಾರ್ಗದವರೆಗಿರುವ ಕಣಿವೆಯಲ್ಲಿ, ಓಫೆಲ್ ಗುಡ್ಡದ ಸುತ್ತಲೂ ಇದ್ದ ದಾವೀದನಗರದ ಹೊರಗಿನ ಗೋಡೆಯನ್ನು ಇನ್ನೂ ಎತ್ತರವಾಗಿ ಕಟ್ಟಿಸಿದನು. ಜುದೇಯ ನಾಡಿನ ಕೋಟೆಕೊತ್ತಲುಗಳುಳ್ಳ ಎಲ್ಲ ಪಟ್ಟಣಗಳಲ್ಲಿ ಸೇನಾಪತಿಗಳನ್ನು ಇರಿಸಿದನು.",
			"15": "<sup>15</sup> ಅನ್ಯದೇವತಾ ಪ್ರತಿಮೆಗಳನ್ನು ಹಾಗು ತಾನು ಮಾಡಿಸಿದ್ದ ಸ್ತಂಭವನ್ನು ಸರ್ವೇಶ್ವರನ ಆಲಯದಿಂದ ತೆಗಿಸಿದನು. ಸರ್ವೇಶ್ವರನ ಮಂದಿರವಿರುವ ಗುಡ್ಡದಲ್ಲೂ ಜೆರುಸಲೇಮಿನಲ್ಲೂ ಕಟ್ಟಿಸಿದ್ದ ಎಲ್ಲ ಯಜ್ಞವೇದಿಗಳನ್ನು ಕೆಡವಿ, ಅವುಗಳನ್ನು ಪಟ್ಟಣದ ಹೊರಗೆ ಹಾಕಿಸಿದನು.",
			"16": "<sup>16</sup> ಸರ್ವೇಶ್ವರನ ಬಲಿಪೀಠವನ್ನು ಪುನಃ ಕಟ್ಟಿಸಿ, ಅದರ ಮೇಲೆ ಶಾಂತಿಸಮಾಧಾನ ಬಲಿಗಳನ್ನೂ ಕೃತಜ್ಞತಾ ಬಲಿಗಳನ್ನೂ ಅರ್ಪಿಸಿದನು. ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರನನ್ನೇ ಅವಲಂಬಿಸಬೇಕೆಂದು ಯೆಹೂದ್ಯರಿಗೆ ಆಜ್ಞಾಪಿಸಿದನು.",
			"17": "<sup>17</sup> ಅವರು ಈ ಪ್ರಕಾರ ದೇವರಾದ ಸರ್ವೇಶ್ವರಸ್ವಾಮಿಯೊಬ್ಬರಿಗೇ ಬಲಿಯನ್ನು ಅರ್ಪಿಸುವವರಾದರು. ಅವರು ಆ ಬಲಿಗಳನ್ನು ಇನ್ನೂ ಪೂಜಾಸ್ಥಳಗಳಲ್ಲಿಯೇ ಸಮರ್ಪಿಸುತ್ತಿದ್ದರು.",
			"18": "<sup>18</sup> ಮನಸ್ಸೆಯ ಉಳಿದ ಚರಿತ್ರೆ, ಅವನು ತನ್ನ ದೇವರಿಗೆ ಮಾಡಿದ ಪ್ರಾರ್ಥನೆ, ಹಾಗು ದರ್ಶಿಗಳು ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಹೆಸರಿನಲ್ಲಿ ಅವನಿಗೆ ಹೇಳಿದ ಮಾತುಗಳು, ಇವು ಇಸ್ರಯೇಲ್ ರಾಜರ ಇತಿಹಾಸದಲ್ಲಿ ದಾಖಲಾಗಿ ಇವೆ.",
			"19": "<sup>19</sup> ಅವನು ದೇವರಿಗೆ ಮಾಡಿದ ಪ್ರಾರ್ಥನೆ, ಅದಕ್ಕೆ ದೊರೆತ ಸದುತ್ತರ ಹಾಗು ಅವನು ದೀನಮನಸ್ಕನಾಗುವುದಕ್ಕೆ ಮುಂಚೆ ಮಾಡಿದ ಅಪರಾಧ, ದ್ರೋಹಗಳು, ಹಾಗು ಅವನು ಅಲ್ಲಲ್ಲಿ ಪೂಜಾಸ್ಥಳಗಳನ್ನು ಏರ್ಪಡಿಸಿ, ಅಶೇರಸ್ತಂಭ ವಿಗ್ರಹ, ಇವುಗಳನ್ನು ನಿಲ್ಲಿಸಿದ್ದು, ಇವುಗಳು ದರ್ಶಿಗಳ ಚರಿತ್ರೆಯಲ್ಲಿ ಲಿಖಿತವಾಗಿವೆ.",
			"20": "<sup>20</sup> ಮನಸ್ಸೆ ಮೃತನಾಗಿ ಪಿತೃಗಳ ಬಳಿಗೆ ಸೇರಲು ಅವನ ಶವವನ್ನು ಅರಮನೆಯ ನಿವೇಶನದಲ್ಲಿ ಸಮಾಧಿಮಾಡಿದರು. ಅವನ ಸ್ಥಾನದಲ್ಲಿ ಅವನ ಮಗ ಆಮೋನನು ಅರಸನಾದನು.",
			"21": "<sup>21</sup> ಆಮೋನನು ಅರಸನಾದಾಗ ಅವನಿಗೆ ಇಪ್ಪತ್ತೆರಡು ವರ್ಷ ವಯಸ್ಸು. ಅವನು ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಎರಡು ವರ್ಷ ಆಳಿದನು.",
			"22": "<sup>22</sup> ತನ್ನ ತಂದೆ ಮನಸ್ಸೆಯ ಹಾಗೆಯೇ ಸರ್ವೇಶ್ವರನ ಚಿತ್ತಕ್ಕೆ ವಿರುದ್ಧವಾಗಿ ನಡೆದನು. ತನ್ನ ತಂದೆ ಮನಸ್ಸೆ ಮಾಡಿಸಿದ ಎಲ್ಲಾ ವಿಗ್ರಹಗಳಿಗೆ ಬಲಿ ಅರ್ಪಿಸಿ ಅವುಗಳನ್ನು ಪೂಜಿಸಿದನು.",
			"23": "<sup>23</sup> ಆಮೋನನ ತಂದೆ ಮನಸ್ಸೆ ಸರ್ವೇಶ್ವರನ ಮುಂದೆ ತನ್ನನ್ನೇ ತಗ್ಗಿಸಿಕೊಂಡಿದ್ದನು. ಆಮೋನನಾದರೋ ತನ್ನನ್ನೇ ತಗ್ಗಿಸಿಕೊಳ್ಳದೆ ಮಹಾಪರಾಧಿಯಾದನು.",
			"24": "<sup>24</sup> ಅವನ ಸೇವಕರು ಅವನಿಗೆ ವಿರುದ್ಧ ಒಳಸಂಚುಮಾಡಿ ಅವನನ್ನು ಅರಮನೆಯಲ್ಲಿಯೇ ಕೊಂದರು.",
			"25": "<sup>25</sup> ಆದರೆ ನಾಡಿನ ಜನರು ಅರಸ ಆಮೋನನಿಗೆ ವಿರುದ್ಧ ಒಳಸಂಚುಮಾಡಿದವರನ್ನೆಲ್ಲಾ ಕೊಂದುಹಾಕಿ, ಆಮೋನನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗ ಯೋಷೀಯನನ್ನು ಅರಸನನ್ನಾಗಿ ಮಾಡಿದರು."
		},
		"34": {
			"1": "<sup>1</sup> ಯೋಷೀಯನು ಅರಸನಾದಾಗ ಅವನಿಗೆ ಎಂಟು ವರ್ಷ ಪ್ರಾಯ. ಅವನು ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಮೂವತ್ತೊಂದು ವರ್ಷ ಆಳಿದನು.",
			"2": "<sup>2</sup> ಅವನು ಎಡಕ್ಕಾಗಲಿ ಬಲಕ್ಕಾಗಲಿ ಹೋಗದೆ ತನ್ನ ಪೂರ್ವಜ ದಾವೀದನ ಮಾರ್ಗದಲ್ಲೇ ನೇರವಾಗಿ ಸರ್ವೇಶ್ವರನ ಚಿತ್ತಾನುಸಾರ ನಡೆದನು.",
			"3": "<sup>3</sup> ಯೋಷೀಯನು ತನ್ನ ಆಳ್ವಿಕೆಯ ಎಂಟನೆಯ ವರ್ಷದಲ್ಲಿ, ಇನ್ನೂ ಯೌವನಸ್ಥನಾಗಿರುವಾಗಲೇ, ತನ್ನ ಪೂರ್ವಜನಾದ ದಾವೀದನ ದೇವರನ್ನು ಅವಲಂಬಿಸುವವನಾದನು; ಹನ್ನೆರಡನೆಯ ವರ್ಷ ಜುದೇಯದಲ್ಲೂ, ಜೆರುಸಲೇಮಿನಲ್ಲೂ ಇದ್ದ ಪೂಜಾಸ್ಥಳಗಳನ್ನು, ಆಶೇರಸ್ತಂಭಗಳನ್ನು ಹಾಗು ಕೆತ್ತಿದ ಮತ್ತು ಎರಕದ ವಿಗ್ರಹಗಳನ್ನು ತೆಗೆದುಹಾಕಿ ನಾಡನ್ನು ಶುದ್ಧಪಡಿಸಿದನು.",
			"4": "<sup>4</sup> ಇದಲ್ಲದೆ, ಅವನು ಬಾಳ್‍ದೇವತೆಗಳ ಬಲಿಪೀಠಗಳನ್ನು ತನ್ನೆದುರಿನಲ್ಲೇ ಹಾಳುಮಾಡಿಸಿ, ಅವುಗಳ ಮೇಲಿದ್ದ ಸೂರ್ಯಸ್ತಂಭಗಳನ್ನು ಕೆಡವಿಸಿ, ಅಶೇರಸ್ತಂಭ, ಕೆತ್ತಿದ ಮತ್ತು ಎರಕದ ವಿಗ್ರಹ, ಇವುಗಳನ್ನು ಒಡಿಸಿ, ಪುಡಿಪುಡಿಮಾಡಿಸಿ, ಅವುಗಳಿಗೆ ಬಲಿಯರ್ಪಿಸಿದವರ ಸಮಾಧಿಗಳ ಮೇಲೆ ಆ ಧೂಳನ್ನು ಚೆಲ್ಲಿಸಿದನು.",
			"5": "<sup>5</sup> ಅವುಗಳ ಪೂಜಾರಿಗಳ ಎಲುಬುಗಳನ್ನು ಆ ಬಲಿಪೀಠಗಳ ಮೇಲೆ ಸುಡಿಸಿ, ಜುದೇಯದ ಮತ್ತು ಜೆರುಸಲೇಮಿನ ಅಶುದ್ಧತ್ವವನ್ನೆಲ್ಲಾ ಪರಿಹರಿಸಿದನು.",
			"6": "<sup>6</sup> ಮನಸ್ಸೆ, ಎಫ್ರಯಿಮ್, ಸಿಮೆಯೋನ್, ನಫ್ತಾಲಿ ಪ್ರಾಂತ್ಯಗಳ ಊರುಗಳ ಸುತ್ತಲು ಹಾಳುಬೀಳುಗಳಲ್ಲಿದ್ದ ಬಲಿಪೀಠಗಳನ್ನು ಕೆಡವಿಸಿದನು;",
			"7": "<sup>7</sup> ಆಶೇರಸ್ತಂಭಗಳನ್ನೂ ವಿಗ್ರಹಗಳನ್ನೂ ಒಡೆಸಿದನು, ಇಸ್ರಯೇಲ್ ನಾಡಿನಲ್ಲಿದ್ದ ಸೂರ್ಯಸ್ತಂಭಗಳನ್ನು ಪುಡಿಪುಡಿಮಾಡಿಸಿ ಜೆರುಸಲೇಮಿಗೆ ಹಿಂದಿರುಗಿದನು.",
			"8": "<sup>8</sup> ಅವನು ತನ್ನ ಆಳ್ವಿಕೆಯ ಹದಿನೆಂಟನೆಯ ವರ್ಷದಲ್ಲಿ ನಾಡನ್ನೂ ದೇವಾಲಯವನ್ನೂ ಶುದ್ಧಿಪಡಿಸಿದ ಮೇಲೆ ತನ್ನ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಆಲಯವನ್ನು ದುರಸ್ತಿಮಾಡಿಸುವುದಕ್ಕಾಗಿ ಅಚಲ್ಯನ ಮಗ ಶಾಫಾನನನ್ನು ಪುರಾಧಿಕಾರಿಯಾದ ಮಾಸೇಯನನ್ನು ಮತ್ತು ಮುಖ್ಯಮಂತ್ರಿಯಾಗಿದ್ದ ಯೋವಾಹಾಜನ ಮಗ ಯೋವಾಹನನ್ನು ಕಳುಹಿಸಿದನು.",
			"9": "<sup>9</sup> ಇವರು ಮಹಾಯಾಜಕ ಹಿಲ್ಕೀಯನ ಬಳಿಗೆ ಹೋಗಿ, ದೇವಾಲಯಕ್ಕಾಗಿ ಸಂಗ್ರಹವಾದ ಹಣವನ್ನು, ಅಂದರೆ, ದ್ವಾರಪಾಲಕರಾದ ಲೇವಿಯರು ಮನಸ್ಸೆ, ಎಫ್ರಯಿಮ್ ಮುಂತಾದ ಇಸ್ರಯೇಲ್‍ಕುಲಗಳಲ್ಲಿ ಉಳಿದವರಿಂದ ಎಲ್ಲ ಯೆಹೂದ ಬೆನ್ಯಾಮೀನ್ಯರಿಂದ ಹಾಗು ಜೆರುಸಲೇಮಿನವರಿಂದ ಸಂಗ್ರಹಿಸಿದ್ದ ಹಣವನ್ನು",
			"10": "<sup>10</sup> ದೇವಾಲಯದ ಕೆಲಸಮಾಡಿಸುವವರಿಗೆ ಕೊಡಲೆಂದು, ಅವನಿಗೆ ಒಪ್ಪಿಸಿದರು. ದೇವಾಲಯದ ಕೆಲಸಮಾಡಿಸುತ್ತಿದ್ದವರು",
			"11": "<sup>11</sup> ಕೆತ್ತಿದ ಕಲ್ಲು, ಜೋಡಿಸತಕ್ಕ ತೊಲೆ ಇವುಗಳನ್ನು ಕೊಂಡುಕೊಳ್ಳಲು ಹಾಗು ಜುದೇಯ ಅರಸರ ಅಲಕ್ಷ್ಯದಿಂದ ಹಾಳಾಗಿದ್ದ ಕಟ್ಟಡಗಳಿಗೆ ಮಾಳಿಗೆ ಕಟ್ಟಲು, ಅಂತು ದೇವಾಲಯವನ್ನು ದುರಸ್ತಿಮಾಡಿ ಭದ್ರಪಡಿಸಲು ಬಡಗಿಗಳಿಗೂ ಶಿಲ್ಪಿಗಳಿಗೂ ಆ ಹಣವನ್ನು ಕೊಟ್ಟರು.",
			"12": "<sup>12</sup> ಕೆಲಸಗಾರರು ನಂಬಿಗಸ್ತರಾಗಿದ್ದರು; ಮೆರಾರಿಯರಲ್ಲಿ ಯಹತ್, ಓಬದ್ಯ ಎಂಬ ಲೇವಿಯರೂ, ಕೆಹಾತ್ಯರಲ್ಲಿ ಜೆಕರ್ಯ, ಮೆಷುಲ್ಲಾಮ್ ಎಂಬ ಲೇವಿಯರೂ ಅವರ ಮೇಲ್ವಿಚಾರಕರಾಗಿದ್ದರು.",
			"13": "<sup>13</sup> ಗಾಯನ ನಿಪುಣರಾದ ಲೇವಿಯರೆಲ್ಲರೂ ಹೊರೆಹೊರುವವರ ಮೇಲ್ವಿಚಾರಕರಾಗಿದ್ದು ಎಲ್ಲಾ ವಿಧವಾದ ಕೆಲಸಮಾಡುವವರನ್ನು ನೋಡಿಕೊಳ್ಳುತ್ತಿದ್ದರು; ಎಲ್ಲ ಲೇಖಕರು, ಅಧಿಕಾರಿಗಳು ಹಾಗು ದ್ವಾರಪಾಲಕರು ಲೇವಿಯರೇ ಆಗಿದ್ದರು.",
			"14": "<sup>14</sup> ಸರ್ವೇಶ್ವರನ ಆಲಯದಲ್ಲಿ ಸಂಗ್ರಹವಾಗಿದ್ದ ಹಣವನ್ನು ಹೊರಗೆ ತೆಗೆಯುವಾಗ ಮೋಶೆಯ ಮುಖಾಂತರ ಕೊಡಲಾಗಿದ್ದ ಸರ್ವೇಶ್ವರನ ಧರ್ಮೋಪದೇಶದ ಗ್ರಂಥವು ಯಾಜಕ ಹಿಲ್ಕೀಯನಿಗೆ ಸಿಕ್ಕಿತು.",
			"15": "<sup>15</sup> ಹಿಲ್ಕೀಯನು ಲೇಖಕನಾದ ಶಾಫಾನನಿಗೆ, “ಸರ್ವೇಶ್ವರನ ಆಲಯದಲ್ಲಿ ನನಗೆ ಧರ್ಮೋಪದೇಶಗ್ರಂಥವು ಸಿಕ್ಕಿದೆ,” ಎಂದು ಹೇಳಿ ಅದನ್ನು ಅವನ ಕೈಗೆ ಕೊಟ್ಟನು.",
			"16": "<sup>16</sup> ಶಾಫಾನನು ಆ ಗ್ರಂಥವನ್ನು ಅರಸನ ಬಳಿಗೆ ತೆಗೆದುಕೊಂಡು ಹೋಗಿ, “ನೀವು ಆಜ್ಞಾಪಿಸಿದ್ದೆಲ್ಲವನ್ನೂ ನಿಮ್ಮ ಸೇವಕರು ಮಾಡುತ್ತಿದ್ದಾರೆ.",
			"17": "<sup>17</sup> ದೇವಾಲಯದಲ್ಲಿ ಸಂಗ್ರಹವಾದ ಹಣವನ್ನು ತೆಗೆದು ಮೇಸ್ತ್ರಿಗಳಿಗೂ ಕೆಲಸಮಾಡುವವರಿಗೂ ಒಪ್ಪಿಸಿದ್ದಾರೆ,” ಎಂದು ತಿಳಿಸಿದನು.",
			"18": "<sup>18</sup> ಇದಲ್ಲದೆ, ಲೇಖಕ ಶಾಫಾನನು ಅರಸನಿಗೆ, “ಯಾಜಕ ಹಿಲ್ಕೀಯ ನನಗೊಂದು ಗ್ರಂಥವನ್ನು ಕೊಟ್ಟಿದ್ದಾರೆ,” ಎಂದು ಹೇಳಿ ಅವನ ಮುಂದೆ ಅದನ್ನು ಓದಿದನು.",
			"19": "<sup>19</sup> ಅರಸ ಯೋಷೀಯನು ಧರ್ಮಶಾಸ್ತ್ರದ ವಾಕ್ಯಗಳನ್ನು ಕೇಳಿದಾಗ,",
			"20": "<sup>20</sup> “ದುಃಖಾವೇಶದಿಂದ ಬಟ್ಟೆಗಳನ್ನು ಹರಿದುಕೊಂಡನು. ಹಿಲ್ಕೀಯ, ಶಾಫಾನನ ಮಗ ಅಹೀಕಾಮ್, ಮೀಕನ ಮಗ ಅಬ್ದೋನ್, ಲೇಖಕ ಶಾಫಾನ್, ಸಹಕಾರಿಯಾದ ಅಸಾಯ ಎಂಬುವರಿಗೆ,",
			"21": "<sup>21</sup> “ನಮ್ಮ ಪೂರ್ವಜರು ಈ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುವ ಸರ್ವೇಶ್ವರನ ಆಜ್ಞೆಗಳನ್ನೆಲ್ಲಾ ಕೈಗೊಳ್ಳದೆ ಹೋದುದರಿಂದಲೇ ಸರ್ವೇಶ್ವರ ತಮ್ಮ ಮಹಾರೌದ್ರವನ್ನು ನಮ್ಮ ಮೇಲೆ ಸುರಿದಿದ್ದಾರೆ. ಆದುದರಿಂದ ನೀವು ನನ್ನ ಪರವಾಗಿ, ಹಾಗು ಇಸ್ರಯೇಲರಲ್ಲಿ ಮತ್ತು ಯೆಹೂದ್ಯರಲ್ಲಿ ಉಳಿದಿರುವವರ ಪರವಾಗಿ ಸರ್ವೇಶ್ವರನ ಬಳಿಗೆ ಹೋಗಿ ನಮಗೆ ಸಿಕ್ಕಿರುವ ಈ ಗ್ರಂಥದ ವಾಕ್ಯಗಳ ಬಗ್ಗೆ ವಿಚಾರಿಸಿರಿ,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"22": "<sup>22</sup> ಆಗ ಹಿಲ್ಕೀಯನೂ ಅರಸನ ಜನರೂ ಹುಲ್ದ ಎಂಬ ಪ್ರವಾದಿನಿಯ ಬಳಿಗೆ ಹೋಗಿ, ಅಪ್ಪಣೆಯಾದಂತೆ ಆಕೆಯ ಬಳಿ ವಿಚಾರಿಸಿದರು. ಹಸ್ತನ ಮೊಮ್ಮಗನೂ ತೊಕ್ಹತನ ಮಗನೂ ರಾಜವಸ್ತ್ರಾಗಾರದ ಅಧಿಪತಿಯೂ ಆಗಿದ್ದ ಶಲ್ಲೂಮನು ಆಕೆಯ ಗಂಡ. ಆಕೆ ಜೆರುಸಲೇಮಿನ ಎರಡನೆಯ ಕೇರಿಯಲ್ಲಿ ವಾಸವಾಗಿದ್ದಳು.",
			"23": "<sup>23</sup> ಹುಲ್ದಳು ಅವರಿಗೆ, “ನಿಮ್ಮನ್ನು ಕಳುಹಿಸಿದವರಿಗೆ ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಈ ಮಾತುಗಳನ್ನು ತಿಳಿಸಿರಿ:",
			"24": "<sup>24</sup> ‘ಈ ನಾಡಿನ ಮೇಲೂ ಜನರ ಮೇಲೂ, ಜುದೇಯದ ಅರಸನ ಹಿಂದೆ ಪಾರಾಯಣವಾದ ಆ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುವ ಎಲ್ಲ ಶಾಪಗಳನ್ನು ಬರಮಾಡುವೆನು;",
			"25": "<sup>25</sup> ಅವರು ನನ್ನನ್ನು ಬಿಟ್ಟು ಅನ್ಯದೇವತೆಗಳಿಗೆ ಧೂಪಾರತಿ ಎತ್ತಿ ತಮ್ಮ ದುಷ್ಕೃತ್ಯಗಳಿಂದ ನನ್ನನ್ನು ರೇಗಿಸಿದ್ದಾರೆ; ನನ್ನ ಕೋಪಾಗ್ನಿ ಈ ನಾಡಿನ ಮೇಲೆ ಉರಿಯುತ್ತಿರುವುದು ಆರಿಹೋಗುವುದಿಲ್ಲ,” ಎನ್ನುತ್ತಾರೆ.",
			"26": "<sup>26</sup> ತಾನು ಕೇಳಿದ ವಾಕ್ಯಗಳ ಬಗ್ಗೆ, ಸರ್ವೇಶ್ವರನ ಬಳಿ ವಿಚಾರಿಸುವುದಕ್ಕೆ ನಿಮ್ಮನ್ನು ಕಳುಹಿಸಿದ ಜುದೇಯದ ಅರಸನಿಗೆ ನೀವು ತಿಳಿಸಬೇಕಾದ ಮಾತೇನೆಂದರೆ: ‘ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಇಂತೆನ್ನುತ್ತಾರೆ;",
			"27": "<sup>27</sup> ಈ ನಾಡಿನ ಮತ್ತು ಇದರ ನಿವಾಸಿಗಳ ಬಗ್ಗೆ ನಾನು ಹೇಳಿದ ಮಾತುಗಳನ್ನು ಕೇಳಿದಾಗ ನೀನು ದುಃಖಪಟ್ಟು ದೇವರಾದ ನನ್ನ ಮುಂದೆ ತಗ್ಗಿಸಿಕೊಂಡೆ; ದೀನಮನಸ್ಸಿನಿಂದ ಬಟ್ಟೆಗಳನ್ನು ಹರಿದುಕೊಂಡು ಕಣ್ಣೀರು ಸುರಿಸಿದೆ; ಆದ್ದರಿಂದ ನಿನ್ನನ್ನು ಲಕ್ಷಿಸಿದೆನು",
			"28": "<sup>28</sup> ನಾನು ನಾಡಿನ ಮೇಲೂ ಇದರ ನಿವಾಸಿಗಳ ಮೇಲೂ ಬರಮಾಡುವ ಶಿಕ್ಷೆಗಳಲ್ಲಿ ನೀನು ಒಂದನ್ನೂ ನೋಡದೆ ಸಮಾಧಾನದಿಂದ ಮೃತಿಹೊಂದಿ ಸಮಾಧಿ ಸೇರುವಂತೆ ಅನುಗ್ರಹಿಸುವೆನು,’ ಎಂಬುದಾಗಿ ತಿಳಿಸಿರಿ,” ಎಂದು ಹೇಳಿದಳು. ಅವರು ಹಿಂದಿರುಗಿ ಬಂದು ಅರಸನಿಗೆ ಆ ಮಾತುಗಳನ್ನು ತಿಳಿಸಿದರು.",
			"29": "<sup>29</sup> ಅನಂತರ ಅರಸನು ದೂತರ ಮುಖಾಂತರ ಜೆರುಸಲೇಮಿನ ಮತ್ತು ಜುದೇಯದ ಎಲ್ಲ ಹಿರಿಯರನ್ನು ಕೂಡಿಸಿದನು.",
			"30": "<sup>30</sup> ಜೆರುಸಲೇಮಿನವರನ್ನು, ಬೇರೆ ಎಲ್ಲ ಯೆಹೂದ್ಯರನ್ನು, ಯಾಜಕರನ್ನು ಹಾಗು ಲೇವಿಯರನ್ನು ಕರೆದುಕೊಂಡು ಸರ್ವೇಶ್ವರನ ಆಲಯಕ್ಕೆ ಹೋದನು. ದೊಡ್ಡವರೂ ಚಿಕ್ಕವರೂ ಎಲ್ಲರೂ ಅವನನ್ನು ಹಿಂಬಾಲಿಸಿದರು. ಅಲ್ಲಿ ಅವನು ಎಲ್ಲರಿಗೂ ಕೇಳಿಸುವಂತೆ ಸರ್ವೇಶ್ವರನ ಆಲಯದಲ್ಲಿ ಸಿಕ್ಕಿದ ನಿಬಂಧನ ಗ್ರಂಥವನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ಓದಿದನು.",
			"31": "<sup>31</sup> ತನ್ನ ಸ್ಥಳದಲ್ಲೇ ನಿಂತು, ತಾನು ಸರ್ವೇಶ್ವರನ ಮಾರ್ಗದಲ್ಲಿ ನಡೆಯುವುದಾಗಿ ಆತನ ಆಜ್ಞಾನಿಯಮ ವಿಧಿಗಳನ್ನು ಪೂರ್ಣಮನಸ್ಸಿನಿಂದಲೂ ಪೂರ್ಣಪ್ರಾಣದಿಂದಲೂ ಕೈಕೊಳ್ಳುವುದಾಗಿ ಮತ್ತು ನಿಬಂಧನ ಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುವ ಎಲ್ಲ ವಾಕ್ಯಗಳನ್ನು ನೆರವೇರಿಸುವುದಾಗಿ ಸರ್ವೇಶ್ವರನಿಗೆ ಪ್ರಮಾಣಮಾಡಿದನು.",
			"32": "<sup>32</sup> ಅಂತೆಯೇ ಎಲ್ಲ ಜೆರುಸಲೇಮಿನವರಿಂದಲೂ ಬೆನ್ಯಾಮೀನ್ಯರಿಂದಲೂ ಪ್ರಮಾಣಮಾಡಿಸಿದನು . ಜೆರುಸಲೇಮಿನವರು ಏಕದೇವರಾದ ತಮ್ಮ ಪೂರ್ವಜರ ದೇವರ ನಿಬಂಧನೆಯನ್ನು ಕೈಕೊಳ್ಳುವಂಥವರಾದರು.",
			"33": "<sup>33</sup> ಯೋಷೀಯನು ಇಸ್ರಯೇಲರಿಗೆ ಸೇರಿದ ಎಲ್ಲಾ ಪ್ರಾಂತಗಳಿಂದ ಅಸಹ್ಯಮೂರ್ತಿಗಳನ್ನೆಲ್ಲಾ ತೆಗೆದುಹಾಕಿಸಿ, ಇಸ್ರಯೇಲರಲ್ಲಿ ಉಳಿದಿರುವವರು ತಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನನ್ನು ಅವಲಂಬಿಸುವಂತೆ ಮಾಡಿದನು. ಅವನ ಜೀವಮಾನದಲ್ಲೆಲ್ಲಾ ಅವರು ತಮ್ಮ ಪೂರ್ವಜರ ದೇವರಾದ ಸರ್ವೇಶ್ವರನನ್ನು ಬಿಡದೆ ಹಿಂಬಾಲಿಸಿದರು."
		},
		"35": {
			"1": "<sup>1</sup> ಯೋಷೀಯನು ಸರ್ವೇಶ್ವರನಿಗೆ ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಪಾಸ್ಕಹಬ್ಬವನ್ನು ಆಚರಿಸುವಂತೆ ಮಾಡಿದನು. ಮೊದಲನೆಯ ತಿಂಗಳಿನ ಹದಿನಾಲ್ಕನೆಯ ದಿನದಲ್ಲಿ ಪಾಸ್ಕದ ಕುರಿಮರಿಗಳನ್ನು ವಧಿಸಿದರು.",
			"2": "<sup>2</sup> ಯೋಷೀಯನು ಯಾಜಕರನ್ನು ಅವರವರ ಕೆಲಸಕ್ಕೆ ನೇಮಿಸಿ, ಸರ್ವೇಶ್ವರನ ಸೇವೆಮಾಡುವಂತೆ ಪ್ರೋತ್ಸಾಹಿಸಿದನು.",
			"3": "<sup>3</sup> ಎಲ್ಲ ಇಸ್ರಯೇಲರಿಗೆ ಉಪದೇಶಿಸತಕ್ಕವರೂ ಸರ್ವೇಶ್ವರನಿಗೆ ಪ್ರತಿಷ್ಠರೂ ಆದ ಲೇವಿಯರಿಗೆ, “ದಾವೀದನ ಮಗನೂ ಇಸ್ರಯೇಲ್ ಅರಸನೂ ಆದ ಸೊಲೊಮೋನನು ಕಟ್ಟಿಸಿದ ಮಹಾದೇವಾಲಯದಲ್ಲಿ ಪವಿತ್ರ ಮಂಜೂಷವನ್ನು ಇಟ್ಟುಬಿಡಿ; ಅದನ್ನು ಹೆಗಲ ಮೇಲೆ ಹೊತ್ತುಕೊಳ್ಳುವುದು ಅವಶ್ಯವಿಲ್ಲ; ಇನ್ನು ಮುಂದೆ ನಿಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೂ ಅವರ ಪ್ರಜೆಗಳಾದ ಇಸ್ರಯೇಲರಿಗೂ ಸೇವೆಸಲ್ಲಿಸಿರಿ.",
			"4": "<sup>4</sup> ಇಸ್ರಯೇಲರ ಅರಸ ದಾವೀದನೂ ಅವನ ಮಗ ಸೊಲೊಮೋನನೂ ಲಿಖಿತವಾಗಿ ನೇಮಿಸಿದಂತೆ ನಿಮ್ಮ ನಿಮ್ಮ ಗೋತ್ರವರ್ಗಗಳ ಪ್ರಕಾರ ಸಿದ್ಧರಾಗಿದ್ದು,",
			"5": "<sup>5</sup> ನಿಮ್ಮ ಸಹೋದರರಾದ ಇಸ್ರಯೇಲರ ಒಂದೊಂದು ಗೋತ್ರ ಶಾಖೆಯ ಪರವಾಗಿ, ನಿಮ್ಮಲ್ಲಿ ಒಂದೊಂದು ಗುಂಪಿನವರು ಪವಿತ್ರಸ್ಥಾನದಲ್ಲಿ ನಿಂತು ಪಾಸ್ಕದ ಕುರಿಮರಿಯನ್ನು ವಧಿಸಲಿ.",
			"6": "<sup>6</sup> ಮೋಶೆಯಿಂದ ಪ್ರಕಟವಾದ ಸರ್ವೇಶ್ವರನ ವಿಧಿಗನುಸಾರ ನಿಮ್ಮ ಸಹೋದರರು ಪಾಸ್ಕಭೋಜನ ಮಾಡುವಂತೆ ನೀವು ನಿಮ್ಮನ್ನು ಶುದ್ಧಿಪಡಿಸಿಕೊಂಡು ಅವರಿಗಾಗಿ ಎಲ್ಲವನ್ನೂ ಸಿದ್ಧಮಾಡಿರಿ,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"7": "<sup>7</sup> ಯೋಷೀಯನು, ಹಬ್ಬಕ್ಕೆ ಬಂದ ಜನರಿಗೆ ಪಾಸ್ಕ ಬಲಿಗಾಗಿ ಆಸ್ಥಾನದ ಸೊತ್ತಿನಿಂದ ಮೂವತ್ತು ಸಾವಿರ ಆಡುಮರಿಗಳನ್ನೂ ಕುರಿಮರಿಗಳನ್ನೂ ಮೂರು ಸಾವಿರ ಹೋರಿಗಳನ್ನೂ ದಾನಮಾಡಿದನು.",
			"8": "<sup>8</sup> ಅವನ ಪದಾಧಿಕಾರಿಗಳು ಕೂಡ ಜನರಿಗೂ ಯಾಜಕರಿಗೂ ಲೇವಿಯರಿಗೂ ಬಲಿಪಶುಗಳನ್ನು ಸಂತೋಷವಾಗಿ ದಾನಕೊಟ್ಟರು. ದೇವಾಲಯದ ಪ್ರಧಾನರಾದ ಹಿಲ್ಕೀಯ, ಜೆಕರ್ಯ, ಯೆಹೀಯೇಲ್ ಎಂಬುವರು ಯಾಜಕರಿಗೆ ಎರಡು ಸಾವಿರದ ಆರುನೂರು ಪಾಸ್ಕದ ಕುರಿಮರಿಗಳನ್ನೂ ಮುನ್ನೂರು ಹೋರಿಗಳನ್ನೂ ಕೊಟ್ಟರು.",
			"9": "<sup>9</sup> ಲೇವಿಯರಲ್ಲಿ ಮುಖ್ಯಸ್ಥರಾದ ಕೋನನ್ಯ, ಸೆಮಾಯ, ನೆತನೇಲ್ ಎಂಬ ಅಣ್ಣತಮ್ಮಂದಿರೂ ಹಷಲ್ಯ, ಯೆಗೀಯೇಲ್, ಯೋಜಾಬಾದ್ ಎಂಬವರೂ ಲೇವಿಯರಿಗೆ ಐದು ಸಾವಿರ ಪಾಸ್ಕದ ಕುರಿಮರಿಗಳನ್ನೂ ಐನೂರು ಹೋರಿಗಳನ್ನೂ ದಾನಮಾಡಿದರು.",
			"10": "<sup>10</sup> ಸೇವೆಯ ಸಂಬಂಧವಾದುದ್ದೆಲ್ಲವನ್ನೂ ಏರ್ಪಡಿಸಿದನಂತರ ಯಾಜಕರು ತಮ್ಮ ತಮ್ಮ ಸ್ಥಳಗಳಲ್ಲಿಯೂ ಲೇವಿಯರು ಅರಸನ ಅಪ್ಪಣೆಯಂತೆ ವರ್ಗವರ್ಗಗಳಾಗಿಯೂ ನಿಂತರು;",
			"11": "<sup>11</sup> ಲೇವಿಯರು ಪಾಸ್ಕದ ಪಶುಗಳನ್ನು ವಧಿಸಿದರು. ಯಾಜಕರು ಇವರ ಕೈಯಿಂದ ರಕ್ತವನ್ನು ತೆಗೆದುಕೊಂಡು ಪ್ರೋಕ್ಷಿಸಿದರು.",
			"12": "<sup>12</sup> ಲೇವಿಯರು, ಚರ್ಮ ಸುಲಿದು ಹೋಮಕ್ಕಾಗಿ ತೆಗೆದಿಟ್ಟಿದ್ದನ್ನು ಪ್ರತ್ಯೇಕಿಸಿ, ಜನರಿಗೆ ಆಯಾ ಗೋತ್ರಶಾಖೆಗಳಿಗನುಸಾರ, ಕೊಟ್ಟರು. ಇವರು ಅದನ್ನು ಮೋಶೆಯ ಧರ್ಮಶಾಸ್ತ್ರವಿಧಿಯ ಮೇರೆಗೆ ಸರ್ವೇಶ್ವರನಿಗೆ ಸಮರ್ಪಿಸಬೇಕಾಯಿತು. ಹೋರಿಗಳ ವಿಷಯದಲ್ಲೂ ಅದೇ ಕ್ರಮವನ್ನು ಅನುಸರಿಸಿದರು.",
			"13": "<sup>13</sup> ಆಮೇಲೆ ಲೇವಿಯರು ಪಾಸ್ಕದ ಮಾಂಸವನ್ನು ನಿಯಮದ ಪ್ರಕಾರ ಬೆಂಕಿಯಲ್ಲಿ ಸುಟ್ಟರು; ದೇವರಿಗೆ ಕಾಣಿಕೆಯಾಗಿ ಸಮರ್ಪಿತವಾದ ಪಶುಗಳ ಮಾಂಸವನ್ನು ಕೊಪ್ಪರಿಗೆ, ಬಾನೆ, ಹಂಡೆಗಳಲ್ಲಿ ಬೇಯಿಸಿ, ಬೇಗನೆ ತಂದು ಜನರಿಗೆ ಬಡಿಸಿದರು.",
			"14": "<sup>14</sup> ತರುವಾಯ ತಮಗೆ ಹಾಗು ಯಾಜಕರಿಗೆ ಭೋಜನವನ್ನು ಸಿದ್ಧಮಾಡಿದರು. ಆರೋನನ ಮಕ್ಕಳಾದ ಯಾಜಕರು ರಾತ್ರಿಯವರೆಗೂ ದಹನಬಲಿಗಳನ್ನೂ ಕೊಬ್ಬನ್ನೂ ಸಮರ್ಪಿಸುತ್ತಾ ಇದ್ದುದರಿಂದ ಲೇವಿಯರು ತಮಗೆ ಹೇಗೋ ಹಾಗೆ ಆರೋನನ ಮಕ್ಕಳಾದ ಯಾಜಕರಿಗೂ ಭೋಜನವನ್ನು ಸಿದ್ಧಮಾಡಿದರು.",
			"15": "<sup>15</sup> ಆಸಾಫ್ಯರಾದ ಗಾಯಕರು, ಅರಸನಾದ ದಾವೀದನ ಮತ್ತು ಆಸಾಫ್, ಹೇಮಾನ್ ಹಾಗು ಅರಸನ ದರ್ಶಿಯಾದ ಯೆದುತೂನ್ ಇವರ ಆಜ್ಞಾನುಸಾರ ತಮ್ಮ ಸ್ಥಳದಲ್ಲಿ ನಿಂತಿದ್ದರು; ದ್ವಾರಪಾಲಕರು ಆಯಾ ಬಾಗಿಲುಗಳನ್ನು ಕಾಯುತ್ತಿದ್ದರು. ಅವರ ಸಹೋದರರಾದ ಲೇವಿಯರು ಅವರ ಭೋಜನಕ್ಕೆ ಸಿದ್ಧಮಾಡುತ್ತಿದ್ದುದರಿಂದ ಅವರು ತಮ್ಮ ತಮ್ಮ ಸೇವೆಯನ್ನು ಬಿಡುವುದಕ್ಕೆ ಕಾರಣ ಇರಲಿಲ್ಲ.",
			"16": "<sup>16</sup> ಈ ಪ್ರಕಾರ ಅರಸ ಯೋಷೀಯನ ಅಪ್ಪಣೆಯಂತೆ ಪಾಸ್ಕವನ್ನು ಆಚರಿಸುವುದಕ್ಕೂ ಸರ್ವೇಶ್ವರನ ಬಲಿಪೀಠದ ಮೇಲೆ ದಹನಬಲಿಗಳನ್ನು ಅರ್ಪಿಸುವುದಕ್ಕೂ ಸರ್ವೇಶ್ವರನ ಸೇವೆಗೆ ಸಂಬಂಧವಾದುದೆಲ್ಲವೂ ಆ ದಿವಸವೇ ಸಿದ್ಧವಾಯಿತು.",
			"17": "<sup>17</sup> ಕೂಡಿಬಂದಿದ್ದ ಇಸ್ರಯೇಲರು ಆ ಸಂದರ್ಭದಲ್ಲಿ ಪಾಸ್ಕಹಬ್ಬವನ್ನು ಹಾಗು ಹುಳಿಯಿಲ್ಲದ ರೊಟ್ಟಿಗಳ ಜಾತ್ರೆಯನ್ನು ಏಳು ದಿನಗಳವರೆಗೆ ಆಚರಿಸಿದರು.",
			"18": "<sup>18</sup> ಇಂಥಾ ಪಾಸ್ಕಹಬ್ಬ ಪ್ರವಾದಿ ಸಮುವೇಲನ ದಿವಸಗಳಿಂದ ಇಸ್ರಯೇಲರಲ್ಲಿ ನಡೆದಿರಲಿಲ್ಲ. ಯೋಷೀಯನು, ಯಾಜಕರು, ಲೇವಿಯರು, ಕೂಡಿಬಂದಿದ್ದ ಇಸ್ರಯೇಲರು, ಯೆಹೂದ್ಯರು, ಹಾಗು ಜೆರುಸಲೇಮಿನವರು ಈ ಪಾಸ್ಕಹಬ್ಬವನ್ನು ಆಚರಿಸಿದಂತೆ ಇಸ್ರಯೇಲ್ ಅರಸರಲ್ಲಿ ಒಬ್ಬನೂ ಆಚರಿಸಿರಲಿಲ್ಲ.",
			"19": "<sup>19</sup> ಈ ಪಾಸ್ಕಹಬ್ಬ ಯೋಷೀಯನ ಆಳ್ವಿಕೆಯ ಹದಿನೆಂಟನೆಯ ವರ್ಷದಲ್ಲಿ ನಡೆಯಿತು.",
			"20": "<sup>20</sup> ಯೋಷೀಯನು ಮಹಾದೇವಾಲಯದ ವಿಷಯದಲ್ಲಿ ಈ ಎಲ್ಲ ವ್ಯವಸ್ಥೆಯನ್ನು ಮಾಡಿದ ನಂತರ, ಈಜಿಪ್ಟಿನ ರಾಜ ನೆಕೋ ಎಂಬುವನು ಯುದ್ಧಮಾಡುವುದಕ್ಕಾಗಿ ಯೂಫ್ರೆಟಿಸ್ ನದಿಯ ತೀರದಲ್ಲಿದ್ದ ಕರ್ಕೆಮೀಷಿಗೆ ಬಂದನು.",
			"21": "<sup>21</sup> ಯೋಷೀಯನು ಅವನಿಗೆ ವಿರುದ್ಧ ಹೊರಟನು. ನೆಕೋವನು ಅವನ ಬಳಿಗೆ ದೂತರನ್ನು ಕಳುಹಿಸಿ, “ಜುದೇಯದ ಅರಸನೇ, ನನ್ನ ಗೊಡವೆ ನಿನಗೇಕೆ? ನಾನು ಈ ಸಾರಿ ಯುದ್ಧಕ್ಕೆ ಹೊರಟಿದ್ದು ನಿನಗೆ ವಿರೋಧವಾಗಿ ಅಲ್ಲ, ನನ್ನ ಶತ್ರುವಂಶಕ್ಕೆ ವಿರೋಧವಾಗಿಯೇ, ನಾನು ಮುನ್ನುಗ್ಗಬೇಕೆಂದು ದೇವರ ಅಪ್ಪಣೆ ಆಗಿದೆ. ನನ್ನೊಂದಿಗಿರುವ ದೇವರಿಗೆ ವಿರೋಧವಾಗಿ ನೀನು ಕೈಯೆತ್ತುವುದನ್ನು ಬಿಡು. ಇಲ್ಲವಾದರೆ, ಅವರು ನಿನ್ನನ್ನು ನಾಶಮಾಡುವರು,” ಎಂದು ಹೇಳಿಸಿದನು.",
			"22": "<sup>22</sup> ಆದರೆ ಯೋಷೀಯನು ಅವನನ್ನು ಬಿಟ್ಟು ಹಿಂದಿರುಗಲಿಲ್ಲ. ದೇವೋಕ್ತಿಯಾಗಿದ್ದ ನೆಕೋವಿನ ಮಾತಿಗೆ ಕಿವಿಗೊಡದೆ, ವೇಷಹಾಕಿಕೊಂಡು ಅವನೊಡನೆ ಕಾದಾಡುವುದಕ್ಕೆ ಮೆಗಿದ್ದೋ ಬಯಲಿಗೆ ಹೋದನು.",
			"23": "<sup>23</sup> ಅಲ್ಲಿ ಬಿಲ್ಲುಗಾರರು ಅರಸ ಯೋಷೀಯನಿಗೆ ಬಾಣವನ್ನೆಸೆದಾಗ ಅವನು ತನ್ನ ಸೇವಕರಿಗೆ, “ನನಗೆ ದೊಡ್ಡ ಗಾಯವಾಯಿತು. ನನ್ನನ್ನು ಆಚೆಗೆ ತೆಗೆದುಕೊಂಡು ಹೋಗಿ,” ಎಂದನು.",
			"24": "<sup>24</sup> ಸೇವಕರು ಅವನನ್ನು ಯುದ್ಧ ರಥದಿಂದಿಳಿಸಿ, ಅವನ ಎರಡನೆಯ ರಥದಲ್ಲಿ ಮಲಗಿಸಿ, ಜೆರುಸಲೇಮಿಗೆ ತೆಗೆದುಕೊಂಡು ಹೋದರು. ಅಲ್ಲಿ ಅವನು ಸತ್ತನು. ಅವನ ಶವವನ್ನು ಅವನ ಪೂರ್ವಿಕರ ಸ್ಮಶಾನದಲ್ಲಿ ಸಮಾಧಿ ಮಾಡಿದರು. ಎಲ್ಲ ಯೆಹೂದ್ಯರೂ ಜೆರುಸಲೇಮಿನವರೂ ಯೋಷೀಯನ ಮರಣಕ್ಕಾಗಿ ಶೋಕಿಸಿದರು.",
			"25": "<sup>25</sup> ಯೆರೆಮೀಯನು ಯೋಷೀಯನನ್ನು ಕುರಿತು ಶೋಕಗೀತೆಯನ್ನು ರಚಿಸಿದನು. ಎಲ್ಲ ಗಾಯಕರೂ ಗಾಯಕಿಯರೂ ಅವನನ್ನು ಇಂದಿನವರೆಗು ತಮ್ಮ ಶೋಕಗೀತೆಗಳಲ್ಲಿ ವರ್ಣಿಸುತ್ತಿರುತ್ತಾರೆ. ಆ ಗೀತೆಗಳನ್ನು ಹಾಡುವುದು ಇಸ್ರಯೇಲರಲ್ಲಿ ಒಂದು ಪದ್ಧತಿಯಾಗಿದೆ. ಅವು ಶೋಕಗೀತೆ ಗ್ರಂಥದಲ್ಲಿ ಲಿಖಿತವಾಗಿವೆ.",
			"26": "<sup>26</sup> ಯೋಷೀಯನ ಉಳಿದ ಚರಿತ್ರೆ, ಸರ್ವೇಶ್ವರನ ಧರ್ಮಶಾಸ್ತ್ರ ಅನುಸಾರವಾಗಿ ಅವನು ಸಾಧಿಸಿದ ಧಾರ್ಮಿಕ ಸುಧಾರಣೆ ಹಾಗು",
			"27": "<sup>27</sup> ಅವನ ಪೂರ್ವೋತ್ತರ ವೃತ್ತಾಂತ ಇವು, ಇಸ್ರಯೇಲರ ಮತ್ತು ಯೆಹೂದ್ಯರ ರಾಜಗ್ರಂಥದಲ್ಲಿ ದಾಖಲಾಗಿವೆ."
		},
		"36": {
			"1": "<sup>1</sup> ನಾಡಿನ ಜನರು ಯೋಷೀಯನ ಮಗ ಯೆಹೋವಾಹಾಜನನ್ನು ಅವನ ತಂದೆಗೆ ಬದಲಾಗಿ, ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಅರಸನನ್ನಾಗಿ ಮಾಡಿದರು.",
			"2": "<sup>2</sup> ಯೆಹೋವಾಹಾಜನು ಪಟ್ಟಕ್ಕೆ ಬಂದಾಗ ಇಪ್ಪತ್ತಮೂರು ವರ್ಷದವನಾಗಿದ್ದನು; ಅವನು ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಮೂರು ತಿಂಗಳು ಆಳಿದನು.",
			"3": "<sup>3</sup> ಈಜಿಪ್ಟಿನ ರಾಜನು ಜೆರುಸಲೇಮಿಗೆ ಬಂದು ಅವನನ್ನು ಪಟ್ಟದಿಂದ ತೆಗೆದುಹಾಕಿ, ಯೆಹೂದ್ಯರು ತನಗೆ 3400 ಕಿಲೋಗ್ರಾಂ ಬೆಳ್ಳಿಯನ್ನೂ 34 ಕಿಲೋಗ್ರಾಂ ಬಂಗಾರವನ್ನೂ ದಂಡ ತೆರುವಂತೆ ಮಾಡಿದನು.",
			"4": "<sup>4</sup> ಈಜಿಪ್ಟಿನ ರಾಜ ನೆಕೋವನು ಯೆಹೋವಾಹಾಜನ ಸಹೋದರನಾದ ಎಲ್ಯಾಕೀಮನನ್ನು ಜುದೇಯದ ಮತ್ತು ಜೆರುಸಲೇಮಿನ ಅರಸನನ್ನಾಗಿ ನೇಮಿಸಿ ಅವನಿಗೆ ಯೆಹೋಯಾಕೀಮನೆಂಬ ಹೆಸರಿಟ್ಟನು; ಮತ್ತು ಅವನ ಸಹೋದರನಾದ ಯೆಹೋವಾಹಾಜನನ್ನು ಈಜಿಪ್ಟ್ ದೇಶಕ್ಕೆ ಕರೆದುಕೊಂಡು ಹೋದನು.",
			"5": "<sup>5</sup> ಯೆಹೋಯಾಕೀಮನು ಅರಸನಾದಾಗ ಅವನಿಗೆ ಇಪ್ಪತ್ತೈದು ವರ್ಷ ವಯಸ್ಸು. ಅವನು ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಹನ್ನೊಂದು ವರ್ಷ ಆಳಿದನು. ಅವನು ತನ್ನ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಚಿತ್ತಕ್ಕೆ ವಿರುದ್ಧ ನಡೆದನು.",
			"6": "<sup>6</sup> ಬಾಬಿಲೋನಿನ ರಾಜ ನೆಬೂಕದ್ನೆಚ್ಚರನು ಅವನ ಮೇಲೆ ಯುದ್ಧಕ್ಕೆ ಬಂದು ಬಾಬಿಲೋನಿಗೆ ಕೊಂಡೊಯ್ಯುವುದಕ್ಕಾಗಿ ಅವನಿಗೆ ಬೇಡಿಹಾಕಿದನು.",
			"7": "<sup>7</sup> ಸರ್ವೇಶ್ವರನ ಆಲಯದ ಹಲವು ಸಾಮಾಗ್ರಿಗಳನ್ನು ಬಾಬಿಲೋನಿಗೆ ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ತನ್ನ ದೇವಾಲಯದಲ್ಲಿಟ್ಟನು.",
			"8": "<sup>8</sup> ಯೆಹೋಯಾಕೀಮನ ಉಳಿದ ಚರಿತ್ರೆ ಅವನು ನಡಿಸಿದ ಅಸಹ್ಯಕೃತ್ಯಗಳು ಹಾಗು ಅವನಲ್ಲಿ ಕಂಡುಬಂದದ್ದೆಲ್ಲವು ಇಸ್ರಯೇಲರ ಮತ್ತು ಯೆಹೂದ್ಯರ ರಾಜಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತವೆ. ಅವನಿಗೆ ಬದಲಾಗಿ ಅವನ ಮಗ ಯೆಹೋಯಾಕೀನನು ಅರಸನಾದನು.",
			"9": "<sup>9</sup> ಯೆಹೋಯಾಕೀನನು ಅರಸನಾದಾಗ ಹದಿನೆಂಟು ವರ್ಷದವನಾಗಿದ್ದನು. ಅವನು ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಮೂರು ತಿಂಗಳು ಹತ್ತು ದಿವಸ ಆಳಿದನು. ಅವನು ಸರ್ವೇಶ್ವರನ ಚಿತ್ತಕ್ಕೆ ವಿರುದ್ಧವಾಗಿ ನಡೆದನು.",
			"10": "<sup>10</sup> ನೆಬೂಕದ್ನೆಚ್ಚರನು ವರ್ಷಾರಂಭದಲ್ಲಿ ಅವನನ್ನೂ ದೇವಾಲಯದ ಮೌಲ್ಯವಸ್ತುಗಳನ್ನು ಬಾಬಿಲೋನಿಗೆ ತರಿಸಿಕೊಂಡು, ಅವನ ಸಹೋದರನಾದ ಚಿದ್ಕೀಯನನ್ನು ಜುದೇಯದ ಮತ್ತು ಜೆರುಸಲೇಮಿನ ಅರಸನನ್ನಾಗಿ ಮಾಡಿದನು.",
			"11": "<sup>11</sup> ಚಿದ್ಕೀಯನು ಅರಸನಾದಾಗ ಅವನಿಗೆ ಇಪ್ಪತ್ತೊಂದು ವರ್ಷ ವಯಸ್ಸು. ಅವನು ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಹನ್ನೊಂದು ವರ್ಷ ಆಳಿದನು.",
			"12": "<sup>12</sup> ಅವನು ತನ್ನ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಚಿತ್ತಕ್ಕೆ ವಿರುದ್ಧ ನಡೆದನು; ಸರ್ವೇಶ್ವರನ ನುಡಿಗಳನ್ನು ತನಗೆ ತಿಳಿಸುತ್ತಿದ್ದ ಪ್ರವಾದಿ ಯೆರೆಮೀಯನ ಮುಂದೆ ತನ್ನನ್ನು ತಗ್ಗಿಸಿಕೊಳ್ಳಲಿಲ್ಲ.",
			"13": "<sup>13</sup> ಇದಲ್ಲದೆ, ನೆಬೂಕದ್ನೆಚ್ಚರನಿಗೆ ಒಳಗಾಗುತ್ತೇನೆಂದು, ದೇವರ ಮೇಲೆ ಆಣೆಯಿಟ್ಟು ಪ್ರಮಾಣಮಾಡಿದ್ದರೂ, ಅವನಿಗೆ ವಿರುದ್ಧ ದಂಗೆ ಎದ್ದನು. ಇಸ್ರಯೇಲ್ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ಅಭಿಮುಖನಾಗಲೊಲ್ಲದೆ, ಹಟಹಿಡಿದು ಮನಸ್ಸನ್ನು ಕಠಿಣಮಾಡಿಕೊಂಡನು.",
			"14": "<sup>14</sup> ಪ್ರಧಾನ ಯಾಜಕರೂ ಪ್ರಜೆಗಳೂ ಕೂಡ ಮಹಾದ್ರೋಹಿಗಳಾಗಿ, ಅನ್ಯಜನಾಂಗಗಳ ಅಸಹ್ಯಕೃತ್ಯಗಳನ್ನು ಅನುಸರಿಸಿ, ಸರ್ವೇಶ್ವರ ತಮಗೆ ಪ್ರತಿಷ್ಠಿಸಿಕೊಂಡಿದ್ದ ಜೆರುಸಲೇಮಿನ ದೇವಾಲಯವನ್ನು ಹೊಲೆಮಾಡಿದರು.",
			"15": "<sup>15</sup> ಅವರ ಪಿತೃಗಳ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ತಮ್ಮ ಪ್ರಜೆಯನ್ನೂ ನಿವಾಸಸ್ಥಾನವನ್ನೂ ಕನಿಕರಿಸಿ, ಸಾವಕಾಶ ಮಾಡದೆ, ತಮ್ಮ ದೂತರ ಮುಖಾಂತರ ಅವರನ್ನು ಎಚ್ಚರಿಸುತ್ತಾ ಬಂದರು.",
			"16": "<sup>16</sup> ಆದರೂ ಅವರು ದೇವಸಂದೇಶಕರನ್ನು ಗೇಲಿಮಾಡಿದರು. ದೇವರ ಮಾತುಗಳನ್ನು ತುಚ್ಛೀಕರಿಸಿ ಅವರ ಪ್ರವಾದಿಗಳನ್ನು ಹೀಯಾಳಿಸಿದರು. ಆದ್ದರಿಂದ ದೇವಕೋಪಾಗ್ನಿ ಅವರ ಪ್ರಜೆಯ ಮೇಲೆ ಉರಿಯತೊಡಗಿತು. ಅದರ ತಾಪ ಆರಿಹೋಗಲೇ ಇಲ್ಲ.",
			"17": "<sup>17</sup> ಸರ್ವೇಶ್ವರಸ್ವಾಮಿ ಬಾಬಿಲೋನಿಯದ ಅರಸನನ್ನು ಅವರ ಮೇಲೆ ಬರಮಾಡಿ ಎಲ್ಲರನ್ನೂ ಅವನ ಕೈಗೊಪ್ಪಿಸಿದರು. ಅವನು ಅವರ ಯುವ ಯೋಧರನ್ನು ಅವರ ಪವಿತ್ರಾಲಯದಲ್ಲೇ ಕತ್ತಿಯಿಂದ ಸಂಹರಿಸಿದನು. ಯುವಕರನ್ನು, ಕನ್ಯೆಯರನ್ನು, ವೃದ್ಧರನ್ನು ಹಾಗು ಅತಿವೃದ್ಧರನ್ನು ಕನಿಕರಿಸದೆ ಎಲ್ಲರನ್ನು ಕೊಲ್ಲಿಸಿದನು.",
			"18": "<sup>18</sup> ದೇವಾಲಯದ ಎಲ್ಲ ಚಿಕ್ಕ ದೊಡ್ಡ ಸಾಮಗ್ರಿಗಳನ್ನು ಮಾತ್ರವಲ್ಲದೆ,ಸರ್ವೇಶ್ವರನ ಮಂದಿರಕ್ಕೆ ಸೇರಿದ ಭಂಡಾರದ ಹಾಗು ಅರಸನ ಮತ್ತು ಅವನ ಪದಾಧಿಕಾರಿಗಳ ಭಂಡಾರದ ದ್ರವ್ಯವನ್ನೂ ಬಾಬಿಲೋನಿಗೆ ಒಯ್ದನು.",
			"19": "<sup>19</sup> ಅವನ ಜನರು ಜೆರುಸಲೇಮಿನ ಪೌಳಿಗೋಡೆಗಳನ್ನು ಕೆಡವಿದರು. ಅದರ ಎಲ್ಲ ರಾಜಮಂದಿರಗಳನ್ನೂ ದೇವಾಲಯವನ್ನೂ ಸುಟ್ಟು ನೆಲಸಮ ಮಾಡಿದರು; ಅಮೂಲ್ಯವಾದ ವಸ್ತುಗಳನ್ನು ನಾಶಮಾಡಿದರು.",
			"20": "<sup>20</sup> ಕತ್ತಿಗೆ ತಪ್ಪಿಸಿಕೊಂಡವರನ್ನು ಅರಸನು ಬಾಬಿಲೋನಿಗೆ ಸೆರೆ ಒಯ್ದನು. ಪರ್ಷಿಯದ ಪ್ರಭುತ್ವ ಅಲ್ಲಿ ಸ್ಥಾಪಿತವಾಗುವವರೆಗೆ ಅವರು ಅವನಿಗೂ ಅವನ ಮಕ್ಕಳಿಗೂ ಗುಲಾಮರಾಗಿದ್ದರು.",
			"21": "<sup>21</sup> ಈ ಪ್ರಕಾರ ಸರ್ವೇಶ್ವರ ಯೆರೆಮೀಯನ ಮುಖಾಂತರ ಹೇಳಿಸಿದ ಮಾತು ನೆರವೇರಿತು; ನಾಡು ತನ್ನ ಸಬ್ಬತ್‍ಕಾಲವನ್ನು ಅನುಭವಿಸುವುದಕ್ಕೆ ಮಾರ್ಗವಾಯಿತು. ಅದು ಎಪ್ಪತ್ತು ವರ್ಷ ಹಾಳುಬಿದ್ದು ಆ ಕಾಲಾವಧಿಯಲ್ಲಿ ವಿಶ್ರಾಂತಿಯನ್ನು ಅನುಭವಿಸುತ್ತಿತ್ತು.",
			"22": "<sup>22</sup> ಪರ್ಷಿಯದ ಅರಸ ಸೈರಸನ ಮೊದಲನೆಯ ವರ್ಷದಲ್ಲಿ ಸರ್ವೇಶ್ವರಸ್ವಾಮಿ ತಾವು ಯೆರೆಮೀಯನ ಮುಖಾಂತರ ಹೇಳಿಸಿದ ವಾಕ್ಯವನ್ನು ಕಾರ್ಯಗತ ಮಾಡಿದರು: ಆ ಪರ್ಷಿಯದ ರಾಜ ಕೋರೆಷನ ಮನಸ್ಸನ್ನು ಪ್ರೇರಿಸಿದರು; ಅವನು ತನ್ನ ರಾಜ್ಯದಲ್ಲೆಲ್ಲಾ ಡಂಗುರದಿಂದಲೂ ಪತ್ರಗಳಿಂದಲೂ,",
			"23": "<sup>23</sup> \"ಪರ್ಷಿಯದ ರಾಜ ಸೈರಸ್ ಎಂಬ ನನ್ನ ಮಾತಿಗೆ ಕಿವಿಗೊಡಿ; ಪರಲೋಕ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ನನಗೆ ಭೂಲೋಕದ ಎಲ್ಲಾ ರಾಜ್ಯಗಳನ್ನು ಒಪ್ಪಿಸಿದ್ದಾರೆ; ತಮಗಾಗಿ ಜುದೇಯ ನಾಡಿನ ಜೆರುಸಲೇಮಿನಲ್ಲಿ ಆಲಯವನ್ನು ಕಟ್ಟಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿದ್ದಾರೆ; ನಿಮ್ಮಲ್ಲಿ ಯಾರು ಅವರ ಪ್ರಜೆಗಳಾಗಿರುತ್ತಾರೋ ಅವರು ಸ್ವಂತನಾಡಿಗೆ ಹೋಗಬಹುದು; ಅವರ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ಅವರೊಂದಿಗೆ ಇರುತ್ತಾರೆ!” ಎಂದು ಪ್ರಕಟಿಸಿದನು."
		}
	}
};
module.exports = book;