var book = {
	"name": "ಪ್ರವಾದಿ ದಾನಿಯೇಲನ ಗ್ರಂಥ",
	"numChapters": 12,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ಜುದೇಯದ ಅರಸ ಯೆಹೋಯಾಕೀಮನ ಆಳ್ವಿಕೆಯ ಮೂರನೆಯ ವರ್ಷದಲ್ಲಿ ಬಾಬಿಲೋನಿನ ರಾಜ ನೆಬೂಕದ್ನೆಚ್ಚರನು ಜೆರುಸಲೇಮಿಗೆ ಬಂದು ಮುತ್ತಿಗೆ ಹಾಕಿದನು.",
			"2": "<sup>2</sup> ಸರ್ವೇಶ್ವರಸ್ವಾಮಿ ಜುದೇಯದ ಅರಸ ಯೆಹೋಯಾಕೀಮನನ್ನು ಅವನ ಕೈವಶ ವಾಗುವಂತೆ ಮಾಡಿದರು . ಅಂತೆಯೇ ದೇವಾಲಯದ ಅನೇಕ ಪೂಜಾಪಾತ್ರೆಗಳು ಅವನ ಕೈವಶವಾದವು. ನೆಬೂಕದ್ನೆಚ್ಚರನು ಅವುಗಳನ್ನು ಶಿನಾರ್ ದೇಶಕ್ಕೆ ಸಾಗಿಸಿ, ತನ್ನ ದೇವರ ಮಂದಿರಕ್ಕೆ ತಂದು, ಆ ದೇವರ ಭಂಡಾರಕ್ಕೆ ಸೇರಿಸಿಕೊಂಡನು.",
			"3": "<sup>3</sup> ಅನಂತರ ಆ ರಾಜನು ತನ್ನ ಕಂಚುಕಿಯರಲ್ಲಿ ಮುಖ್ಯಸ್ಥನಾದ ಅಶ್ಪೆನಜನಿಗೆ, “ನೀನು ಇಸ್ರಯೇಲರಲ್ಲಿ, ಅಂದರೆ ರಾಜವಂಶೀಯರಲ್ಲಿ ಮತ್ತು ಪ್ರಧಾನರಲ್ಲಿ ಕೆಲವು ಯುವಕರನ್ನು ಇಲ್ಲಿಗೆ ಕರೆದು ಬಾ.",
			"4": "<sup>4</sup> ಅವರು ಅಂಗದೋಷವಿಲ್ಲದವರಾಗಿರಬೇಕು. ಸುಂದರರು, ಸಮಸ್ತ ಶಾಸ್ತ್ರಜ್ಞರು, ಪಂಡಿತರು, ವಿದ್ಯಾನಿಪುಣರು, ರಾಜಾಲಯದಲ್ಲಿ ಸನ್ನಿಧಿಸೇವೆಮಾಡಲು ಸಮರ್ಥರೂ ಆಗಿರಬೇಕು. ಅವರಿಗೆ ಬಾಬಿಲೋನ್ ಪಂಡಿತರ ಭಾಷೆಯನ್ನೂ ಶಾಸ್ತ್ರಗಳನ್ನೂ ಕಲಿಸಬೇಕು,” ಎಂದು ಅಪ್ಪಣೆಕೊಟ್ಟನು.",
			"5": "<sup>5</sup> ಇದಲ್ಲದೆ, “ಇನ್ನು ಮುಂದಕ್ಕೆ ಅವರು ಸನ್ನಿಧಿಸೇವಕರಾಗುವಂತೆ, ನನ್ನ ಭೋಜನಪದಾರ್ಥಗಳನ್ನು ಹಾಗೂ ನಾನು ಕುಡಿಯುವ ದ್ರಾಕ್ಷಾರಸವನ್ನು ಅವರಿಗೆ ಪ್ರತಿದಿನ ಬಡಿಸುವ ಏರ್ಪಾಡುಮಾಡಿ ಅವರನ್ನು ಮೂರು ವರ್ಷ ಪೋಷಿಸಬೇಕು,” ಎಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"6": "<sup>6</sup> ಹೀಗೆ ಆರಿಸಿಕೊಂಡ ಯುವಕರಲ್ಲಿ ದಾನಿಯೇಲನು, ಹನನ್ಯನು, ಮಿಶಾಯೇಲನು, ಅಜರ್ಯನು ಎಂಬ ಯೆಹೂದ್ಯರು ಸೇರಿದ್ದರು.",
			"7": "<sup>7</sup> ಕಂಚುಕಿಯರ ನಾಯಕನು ಇವರಿಗೆ ನಾಮಕರಣಮಾಡಿ ದಾನಿಯೇಲನಿಗೆ ‘ಬೇಲ್ತೆಶಚ್ಚರ್’, ಹನನ್ಯನಿಗೆ ‘ಶದ್ರಕ್’, ಮಿಶಾಯೇಲನಿಗೆ ‘ಮೇಶಕ್’, ಹಾಗೂ ಅಜರ್ಯನಿಗೆ ‘ಅಬೇದ್‍ನೆಗೋ’, ಎಂಬ ಹೆಸರಿಟ್ಟನು.",
			"8": "<sup>8</sup> ಆದರೆ ದಾನಿಯೇಲನು ತಾನು ರಾಜನ ಭೋಜನ ಪದಾರ್ಥಗಳನ್ನು ತಿಂದು ಹಾಗೂ ರಾಜನು ಕುಡಿಯುವ ದ್ರಾಕ್ಷಾರಸವನ್ನು ಕುಡಿದು ತನ್ನನ್ನೇ ಅಶುದ್ಧ ಮಾಡಿಕೊಳ್ಳಬಾರದೆಂದು ನಿಶ್ಚಯಿಸಿಕೊಂಡನು. ಕಂಚುಕಿಯರ ನಾಯಕನಿಗೆ, “ಕ್ಷಮಿಸು, ನಾನು ಅಶುದ್ಧನಾಗಲಾರೆ,” ಎಂದು ವಿನಂತಿಸಿದನು.",
			"9": "<sup>9</sup> ಕಂಚುಕಿಯರ ನಾಯಕನು ದಾನಿಯೇಲನ ಮೇಲೆ ಕನಿಕರ ಮತ್ತು ದಯೆತೋರುವಂತೆ ದೇವರು ಅನುಗ್ರಹಿಸಿದ್ದರು.",
			"10": "<sup>10</sup> ಆದುದರಿಂದ ಆ ನಾಯಕನು ದಾನಿಯೇಲನಿಗೆ, “ನಿಮಗೆ ಅನ್ನಪಾನಗಳನ್ನು ಏರ್ಪಡಿಸಿದ ನನ್ನೊಡೆಯನಾದ ರಾಜನು ನಿಮ್ಮಂತೆ ಆಯ್ಕೆಯಾದ ಯುವಕರ ಮುಖಕ್ಕಿಂತ ನಿಮ್ಮ ಮುಖ ಬಾಡಿರುವುದನ್ನು ನೋಡಿ ನನ್ನ ತಲೆ ತೆಗೆಸಬಹುದೆಂಬ ಭಯ ನನಗಿದೆ. ಇದಕ್ಕೆ ನೀವು ಕಾರಣರಾಗಬಹುದಲ್ಲವೆ?” ಎಂದು ಹೇಳಿದನು.",
			"11": "<sup>11</sup> ದಾನಿಯೇಲನು ತನ್ನನ್ನೂ, ಹನನ್ಯ, ಮಿಶಾಯೇಲ, ಅಜರ್ಯ ಇವರನ್ನೂ ನೋಡಿಕೊಳ್ಳಲು ಆ ನಾಯಕನು ನೇಮಿಸಿದ್ದ ವಿಚಾರಕನ ಬಳಿಗೆ ಬಂದು,",
			"12": "<sup>12</sup> “ಅಯ್ಯಾ, ಹತ್ತು ದಿನಗಳ ಮಟ್ಟಿಗೆ ನಮ್ಮನ್ನು ಪರೀಕ್ಷಿಸಿನೋಡು. ಸೇವಕರಾದ ನಮಗೆ ಭೋಜನಕ್ಕೆ ಬದಲಾಗಿ ಕಾಯಿಪಲ್ಯ, ಪಾನಕ್ಕೆ ಬದಲಾಗಿ ನೀರು ಒದಗಿಸಿದರೆ ಸಾಕು.",
			"13": "<sup>13</sup> ಆಮೇಲೆ ನಮ್ಮ ಮುಖಗಳನ್ನೂ ರಾಜಭೋಜನ ಉಣ್ಣುವ ಯುವಕರ ಮುಖಗಳನ್ನೂ ಹೋಲಿಸಿನೋಡು. ಅನಂತರ ನಿನಗೆ ತೋಚಿದ ಹಾಗೆ ನಮಗೆ ಮಾಡು,” ಎಂದು ಕೇಳಿಕೊಂಡನು.",
			"14": "<sup>14</sup> ವಿಚಾರಕನು ಅವರ ಬಿನ್ನಹಕ್ಕೆ ಒಪ್ಪಿ ಹತ್ತು ದಿನದವರೆಗೆ ಪರೀಕ್ಷಿಸಿದನು.",
			"15": "<sup>15</sup> ಹತ್ತು ದಿನಗಳಾದ ಮೇಲೆ ರಾಜಭೋಜನ ಉಣ್ಣುತ್ತಿದ್ದ ಎಲ್ಲ ಯುವಕರಿಗಿಂತ ಇವರು ಸುಂದರವಾಗಿಯೂ ಪುಷ್ಟರಾಗಿಯೂ ಕಾಣಿಸಿದರು.",
			"16": "<sup>16</sup> ಅಂದಿನಿಂದ ವಿಚಾರಕನು ಇವರಿಗೆ ನೇಮಕವಾದ ಭೋಜನಪದಾರ್ಥಗಳನ್ನೂ ಕುಡಿಯಬೇಕಾದ ದ್ರಾಕ್ಷಾರಸವನ್ನೂ ತೆಗೆದುಬಿಟ್ಟು ಕಾಯಿಪಲ್ಯಗಳನ್ನೇ ಕೊಡುತ್ತಾ ಬಂದನು.",
			"17": "<sup>17</sup> ಹೀಗಿರಲು ದೇವರು ಆ ನಾಲ್ಕುಮಂದಿ ಯುವಕರಿಗೆ ಸಕಲ ಶಾಸ್ತ್ರಗಳಲ್ಲೂ ವಿದ್ಯೆಗಳಲ್ಲೂ ಜ್ಞಾನ ವಿವೇಕಗಳನ್ನು ದಯಪಾಲಿಸಿದರು. ದಾನಿಯೇಲನು ಸಮಸ್ತ ಸ್ವಪ್ನಗಳನ್ನೂ ದಿವ್ಯದರ್ಶನಗಳನ್ನೂ ಅರ್ಥೈಸುವುದರಲ್ಲಿ ಪ್ರವೀಣನಾದನು.",
			"18": "<sup>18</sup> ರಾಜನು ನೇಮಿಸಿದ ಕಾಲವು ಕಳೆಯಿತು. ಆ ಯುವಕರನ್ನು ರಾಜಸನ್ನಿಧಿಗೆ ಕರೆದುತರುವ ಸಮಯವು ಬಂದಿತು. ಕಂಚುಕಿಯರ ನಾಯಕ ಅವರನ್ನು ನೆಬೂಕದ್ನೆಚ್ಚರನ ಸಮ್ಮುಖಕ್ಕೆ ಕರೆದುತಂದನು.",
			"19": "<sup>19</sup> ರಾಜನು ಅವರ ಸಂಗಡ ಮಾತಾಡುವಾಗ ಅಲ್ಲಿದ್ದ ಯುವಕರಲ್ಲೆಲ್ಲ ದಾನಿಯೇಲನಿಗೆ, ಹನನ್ಯನಿಗೆ, ಮೀಶಾಯೇಲನಿಗೆ, ಅಜರ್ಯನಿಗೆ, ಸಮಾನರು ಯಾರೂ ಕಂಡುಬರಲಿಲ್ಲ. ಆದಕಾರಣ ಆ ನಾಲ್ವರು ಸನ್ನಿಧಿಸೇವಕರಾದರು.",
			"20": "<sup>20</sup> ಬಳಿಕ ರಾಜನು ಶಾಸ್ತ್ರೀಯ ವಿದ್ಯೆಯ ಎಲ್ಲ ವಿಷಯಗಳಲ್ಲಿ ಅವರನ್ನು ವಿಚಾರಮಾಡಿದಾಗ ಸಮಸ್ತರಾಜ್ಯದಲ್ಲಿನ ಎಲ್ಲ ಜೋಯಿಸರಿಗಿಂತಲೂ ಮಂತ್ರವಾದಿಗಳಿಗಿಂತಲೂ ಹತ್ತರಷ್ಟು ನಿಪುಣರಾಗಿ ಕಂಡುಬಂದರು.",
			"21": "<sup>21</sup> ದಾನಿಯೇಲನು ರಾಜ ಸೈರೆಷನ ಆಳ್ವಿಕೆಯ ಮೊದಲನೆಯ ವರ್ಷದ ತನಕ ಸನ್ನಿಧಿಸೇವಕನಾಗಿಯೇ ಇದ್ದನು."
		},
		"2": {
			"1": "<sup>1</sup> ನೆಬೂಕದ್ನೆಚ್ಚರನು ತನ್ನ ಆಳ್ವಿಕೆಯ ಎರಡನೆಯ ವರ್ಷದಲ್ಲಿ ಒಂದು ಕನಸು ಕಂಡು ತತ್ತರಗೊಂಡನು. ಅವನಿಗೆ ನಿದ್ರೆ ತಪ್ಪಿತು.",
			"2": "<sup>2</sup> ಆಗ ಆ ರಾಜನು ತನ್ನ ಕನಸಿನ ಅರ್ಥ ತಿಳಿಸಬೇಕೆಂದು ಜೋಯಿಸರನ್ನೂ ಮಂತ್ರವಾದಿಗಳನ್ನೂ ಮಾಟಗಾರರನ್ನೂ ಪಂಡಿತರನ್ನೂ ಕರೆಯಿಸಿದನು. ಅವರು ಆಸ್ಥಾನಕ್ಕೆ ಬಂದು ರಾಜನ ಮುಂದೆ ನಿಂತುಕೊಂಡರು.",
			"3": "<sup>3</sup> ರಾಜನು ಅವರಿಗೆ, “ನಾನು ಒಂದು ಕನಸು ಕಂಡೆ. ಅದರ ಅರ್ಥವೇನೋ ಎಂದು ನನ್ನ ಮನಸ್ಸು ತತ್ತರಗೊಂಡಿದೆ,” ಎಂದನು.",
			"4": "<sup>4</sup> ಆ ಪಂಡಿತರು ಅರಮೀಯ ಭಾಷೆಯಲ್ಲಿ ರಾಜನಿಗೆ, “ಅರಸರೇ, ಚಿರಂಜೀವಿಯಾಗಿರಿ! ಆ ಕನಸನ್ನು ನಿಮ್ಮ ದಾಸರಾದ ನಮಗೆ ಹೇಳಿ. ಅದರ ತಾತ್ಪರ್ಯವನ್ನು ತಿಳಿಸುತ್ತೇವೆ,” ಎಂದು ಅರಿಕೆಮಾಡಿದರು.",
			"5": "<sup>5</sup> ಆಗ ರಾಜನು ಆ ಪಂಡಿತರಿಗೆ, “ನನ್ನ ಈ ಮಾತು ಖಂಡಿತ; ನೀವು ಈ ಕನಸನ್ನೂ ಅದರ ಉದ್ದೇಶವನ್ನೂ ನನಗೆ ತಿಳಿಸದಿದ್ದರೆ ನಿಮ್ಮನ್ನು ತುಂಡುತುಂಡಾಗಿ ಕತ್ತರಿಸುವೆನು; ನಿಮ್ಮ ಮನೆಗಳನ್ನು ತಿಪ್ಪೆಗುಂಡಿಯನ್ನಾಗಿಸುವೆನು.",
			"6": "<sup>6</sup> ನೀವು ಆ ಕನಸನ್ನೂ ಅದರ ಉದ್ದೇಶವನ್ನೂ ತಿಳಿಸಿದ್ದೇ ಆದರೆ ನನ್ನಿಂದ ನಿಮಗೆ ದಾನ ಬಹುಮಾನಗಳೂ ವಿಶೇಷ ಸನ್ಮಾನಗಳೂ ದೊರಕುವುವು. ಆದಕಾರಣ ಆ ಕನಸನ್ನೂ ಅದರ ಉದ್ದೇಶವನ್ನೂ ನನಗೆ ತಿಳಿಸಿ,” ಎಂದನು.",
			"7": "<sup>7</sup> ಪಂಡಿತರು, “ರಾಜರು ತಮ್ಮ ದಾಸರಿಗೆ ಕನಸನ್ನು ತಿಳಿಸೋಣವಾಗಲಿ, ನಾವು ಅದರ ತಾತ್ಪರ್ಯವನ್ನು ವಿವರಿಸುವೆವು,” ಎಂದು ಮತ್ತೊಮ್ಮೆ ಉತ್ತರಕೊಟ್ಟರು.",
			"8": "<sup>8</sup> ಅದಕ್ಕೆ ರಾಜನು, “ನೀವು ಆ ಕನಸನ್ನು ನನಗೆ ತಿಳಿಸದಿದ್ದರೆ ನಿಮಗೆ ಮರಣದಂಡನೆಯಾಗುವುದು ಖಚಿತವೆಂದು ತಿಳಿದೇ ಕಾಲಹರಣ ಮಾಡುತ್ತಿದ್ದೀರಿ. ಇದು ನನಗೆ ಚೆನ್ನಾಗಿ ಗೊತ್ತು.",
			"9": "<sup>9</sup> ಕಾಲ ಬದಲಾಗುತ್ತಿರುತ್ತದೆ ಎಂದುಕೊಂಡೇ ನನ್ನ ಮುಂದೆ ಇಲ್ಲಸಲ್ಲದ್ದನ್ನು ಆಡಲು ಒಪ್ಪಂದ ಮಾಡಿಕೊಂಡಿದ್ದೀರಿ. ಅಂತು ಆ ಕನಸನ್ನು ನೀವು ನನಗೆ ತಿಳಿಸಲೇಬೇಕು. ಆಗ ಅದರ ಉದ್ದೇಶವನ್ನು ವಿವರಿಸಬಲ್ಲಿರೆಂದು ನಾನು ತಿಳಿದುಕೊಳ್ಳುವೆನು,” ಎಂದು ಹೇಳಿದನು.",
			"10": "<sup>10</sup> ಆಗ ಆ ಪಂಡಿತರು ಸನ್ನಿಧಿಯ ಮುಂದೆ, “ರಾಜರು ಕೇಳುವ ಸಂಗತಿಯನ್ನು ತಿಳಿಸಬಲ್ಲವನು ಈ ಲೋಕದಲ್ಲಿ ಯಾವನೂ ಇಲ್ಲ. ಎಷ್ಟೆಷ್ಟೋ ಬಲಿಷ್ಠರಾದ , ಪ್ರಬಲರಾದ ಯಾವ ಅರಸರೂ ಜೋಯಿಸನನ್ನಾಗಲಿ, ಮಾಟಗಾರನನ್ನಾಗಲಿ, ಪಂಡಿತನನ್ನಾಗಲಿ, ಇಂಥ ಸಂಗತಿಯನ್ನು ಎಂದೂ ಕೇಳಿದ್ದಿಲ್ಲ.",
			"11": "<sup>11</sup> ರಾಜರು ಕೇಳುತ್ತಿರುವ ಸಂಗತಿ ಅತಿಕಷ್ಟವಾದುದು. ನರಮಾನವರ ನಡುವೆ ವಾಸಮಾಡದ ದೇವರುಗಳೇ ಹೊರತು ಇನ್ನಾರೂ ರಾಜರ ಸಮ್ಮುಖದಲ್ಲಿ ಇದನ್ನು ತಿಳಿಸಲಾರರು,” ಎಂದು ಉತ್ತರಕೊಟ್ಟರು.",
			"12": "<sup>12</sup> ಇದನ್ನು ಕೇಳಿ ರಾಜನು ಉಗ್ರಕೋಪಗೊಂಡನು. ಬಾಬಿಲೋನಿನ ಸಕಲ ವಿದ್ವಾಂಸರನ್ನು ಕೊಲ್ಲಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"13": "<sup>13</sup> ಕೂಡಲೆ ಆ ಆಜ್ಞೆ ಹೊರಪಟ್ಟಿತು. ವಿದ್ವಾಂಸರು ಪ್ರಾಣಾಪಾಯಕ್ಕೆ ಗುರಿಯಾದರು. ಅಂತೆಯೇ ದಾನಿಯೇಲನನ್ನೂ ಅವನ ಗೆಳೆಯರೆಲ್ಲರನ್ನೂ ಕೊಲ್ಲಲಿಕ್ಕೆ ಹುಡುಕಿದರು.",
			"14": "<sup>14</sup> ರಾಜನ ಮೈಗಾವಲಿನವರ ದಳವಾಯಿಯಾದ ಅರ್ಯೋಕನು ಬಾಬಿಲೋನಿನ ವಿದ್ವಾಂಸರ ಸಂಹಾರಕ್ಕಾಗಿ ಹೊರಟಾಗ ದಾನಿಯೇಲನು ಬುದ್ಧಿವಿವೇಕಗಳಿಂದ ಅವನ ಸಂಗಡ ಮಾತಾಡಿದನು.",
			"15": "<sup>15</sup> “ಈ ರಾಜಾಜ್ಞೆಯೇಕೆ ಇಷ್ಟು ತೀಕ್ಷ್ಣ?” ಎಂದು ಕೇಳಿದನು. ಅರ್ಯೋಕನು ನಡೆದ ಸಂಗತಿಯನ್ನು ದಾನಿಯೇಲನಿಗೆ ತಿಳಿಸಿದನು.",
			"16": "<sup>16</sup> ಆಮೇಲೆ ದಾನಿಯೇಲನು ಅರಮನೆಗೆ ಹೋಗಿ, “ನನಗೆ ಸಮಯಕೊಟ್ಟರೆ ಆ ಕನಸಿನ ಅರ್ಥವನ್ನು ರಾಜರಿಗೆ ನಾನು ವಿವರಿಸಬಲ್ಲೆ,” ಎಂದು ಅರಿಕೆ ಮಾಡಿದನು.",
			"17": "<sup>17</sup> ಬಳಿಕ ದಾನಿಯೇಲನು ತನ್ನ ಮನೆಗೆ ಹಿಂತಿರುಗಿ ಹನನ್ಯ, ಮೀಶಾಯೇಲ ಮತ್ತು ಅಜರ್ಯ ಎಂಬ ತನ್ನ ಗೆಳೆಯರಿಗೆ ಈ ಸಮಾಚಾರ ತಿಳಿಸಿದನು.",
			"18": "<sup>18</sup> “ನಾವು ಬಾಬಿಲೋನಿನ ಇತರ ವಿದ್ವಾಂಸರೊಂದಿಗೆ ನಾಶವಾಗದಂತೆ ಪರಲೋಕ ದೇವರು ಈ ರಹಸ್ಯದ ವಿಷಯವಾಗಿ ಕೃಪೆತೋರುವಂತೆ ಬೇಡಿಕೊಳ್ಳೋಣ,” ಎಂದು ಹೇಳಿದನು.",
			"19": "<sup>19</sup> ಆ ರಾತ್ರಿಯೇ ಸ್ವಪ್ನದಲ್ಲಿ ಕನಸಿನ ಗುಟ್ಟು ದಾನಿಯೇಲನಿಗೆ ವ್ಯಕ್ತವಾಯಿತು. ಕೂಡಲೆ ಆತನು ಪರಲೋಕ ದೇವರನ್ನು ಹೀಗೆಂದು ಸ್ತುತಿಸಿದನು:",
			"20": "<sup>20</sup> ಯುಗಯುಗಾಂತರಕ್ಕೂ ದೇವರ ನಾಮಕ್ಕೆ ಸ್ತುತಿಸ್ತೋತ್ರ! ಏಕೆಂದರೆ ಜ್ಞಾನವೂ ಶಕ್ತಿಯೂ ಆತನಿಗೆ ಸ್ವಂತ.",
			"21": "<sup>21</sup> ಕಾಲಗಳು ಋತುಗಳು ಆತನ ಕೈಯಲ್ಲಿವೆ ರಾಜರನ್ನು ಕೆಳಕ್ಕಿಳಿಸುವವನು, ಮೇಲೆ ನಿಲ್ಲಿಸುವವನು ಆತನೆ. ಜ್ಞಾನಿಗಳ ಜ್ಞಾನ, ವಿವೇಕಿಗಳ ವಿವೇಕ ಆತನ ಕೊಡುಗೆ.",
			"22": "<sup>22</sup> ಅಗಾಧ ವಿಷಯಗಳನ್ನೂ ನಿಗೂಢ ರಹಸ್ಯಗಳನ್ನೂ ತರುವನಾತ ಬಯಲಿಗೆ. ಕಗ್ಗತ್ತಲೆಯಲ್ಲಿ ಅಡಗಿರುವುದೂ ಆತನಿಗೆ ಗೋಚರ ಏಕೆಂದರೆ ಬೆಳಕು ಆತನಲ್ಲೇ ಸುಸ್ಥಿರ.",
			"23": "<sup>23</sup> ನನ್ನ ಪಿತೃಗಳ ದೇವಾ, ಮಾಡುವೆ ನಿನ್ನ ಗುಣಗಾನ, ನಿನಗೆ ನನ್ನ ಧನ್ಯವಾದ. ಏಕೆಂದರೆ ನೀನೇ ನನಗೆ ಜ್ಞಾನಶಕ್ತಿಗಳನ್ನು ದಯಪಾಲಿಸಿದಾತ. ನಾವು ಬೇಡಿದ್ದನ್ನು ನನಗೆ ತೋರ್ಪಡಿಸಿದಾತ ನೀನೇ ಹೌದು, ರಾಜ ಬಯಸಿದ ಗುಟ್ಟನ್ನು ನಮಗೆ ವ್ಯಕ್ತಪಡಿಸಿದಾತ ನೀನೇ.",
			"24": "<sup>24</sup> ತರುವಾಯ ದಾನಿಯೇಲನು ಬಾಬಿಲೋನಿನ ವಿದ್ವಾಂಸರನ್ನು ಕೊಲ್ಲುವುದಕ್ಕೆ ರಾಜನು ನೇಮಿಸಿದ್ದ ಅರ್ಯೋಕನ ಬಳಿಗೆ ಹೋದನು. ಅವನಿಗೆ, “ಬಾಬಿಲೋನಿನ ವಿದ್ವಾಂಸರನ್ನು ಕೊಲ್ಲಬೇಡ. ನನ್ನನ್ನು ರಾಜನ ಸನ್ನಿಧಿಗೆ ಸೇರಿಸಿಬಿಡು, ನಾನು ಆ ಕನಸಿನ ಉದ್ದೇಶವನ್ನು ರಾಜನಿಗೆ ತಿಳಿಸುವೆನು,” ಎಂದು ಕೇಳಿಕೊಂಡನು.",
			"25": "<sup>25</sup> ಕೂಡಲೆ ಅರ್ಯೋಕನು ದಾನಿಯೇಲನನ್ನು ರಾಜನ ಸನ್ನಿಧಿಗೆ ಬೇಗಬೇಗನೆ ಕರೆದುಕೊಂಡು ಬಂದನು. “ರಾಜರೇ, ಆ ಕನಸಿನ ಉದ್ದೇಶವನ್ನು ನಿಮಗೆ ತಿಳಿಸಬಲ್ಲ ಒಬ್ಬನು ನನಗೆ ಸಿಕ್ಕಿದ್ದಾನೆ. ಅವನು ಜುದೇಯದಿಂದ ಸೆರೆಯಾಳಾಗಿ ತಂದವರಲ್ಲಿ ಒಬ್ಬನಾಗಿದ್ದಾನೆ,” ಎಂದು ಅರಿಕೆಮಾಡಿದನು.",
			"26": "<sup>26</sup> ರಾಜನು ಬೇಲ್ತೆಶಚ್ಚರನೆಂದು ಹೆಸರು ಪಡೆದಿದ್ದ ದಾನಿಯೇಲನನ್ನು ನೋಡಿ, “ನಾನು ಕಂಡ ಕನಸನ್ನೂ ಅದರ ಅರ್ಥವನ್ನೂ ತಿಳಿಸಬಲ್ಲೆಯಾ?” ಎಂದು ವಿಚಾರಿಸಿದನು.",
			"27": "<sup>27</sup> ಅದಕ್ಕೆ ದಾನಿಯೇಲನು ಸನ್ನಿಧಿಯಲ್ಲಿ, “ರಾಜರು ಕೇಳುವ ರಹಸ್ಯವನ್ನು ವಿದ್ವಾಂಸರಾಗಲಿ, ಮಾಟಗಾರರಾಗಲಿ, ಜೋಯಿಸರಾಗಲಿ, ಶಕುನದವರಾಗಲಿ ಯಾರೂ ತಿಳಿಸಲಾರರು.",
			"28": "<sup>28</sup> ಆದರೆ ರಹಸ್ಯಗಳನ್ನು ವ್ಯಕ್ತಪಡಿಸಬಲ್ಲವರು ಒಬ್ಬರಿದ್ದಾರೆ. ಅವರೇ ಪರಲೋಕದಲ್ಲಿರುವ ದೇವರು. ಬರಲಿರುವ ಕಾಲದಲ್ಲಿ ನಡೆಯತಕ್ಕದ್ದನ್ನು ರಾಜ ನೆಬೂಕದ್ನೆಚ್ಚರರಾದ ನಿಮಗೆ ಅವರೇ ತಿಳಿಯಪಡಿಸಿದ್ದಾರೆ. ನೀವು ಕಂಡ ಕನಸು, ಹಾಸಿಗೆಯ ಮೇಲೆ ಮಲಗಿದ್ದಾಗ ನಿಮ್ಮ ಮನಸ್ಸಿಗೆ ತೋಚಿದ ಸ್ವಪ್ನಗಳು ಹೀಗಿವೆ:",
			"29": "<sup>29</sup> “ರಾಜರೇ, ನೀವು ಹಾಸಿಗೆಯ ಮೇಲೆ ಮಲಗಿರುವಾಗ ಮುಂದೆ ಏನು ಸಂಭವಿಸುವುದೋ ಎಂಬ ಯೋಚನೆ ನಿಮ್ಮಲ್ಲಿ ಹುಟ್ಟಿತ್ತು. ಗುಟ್ಟುಗಳನ್ನು ಬಟ್ಟಬಯಲಾಗಿಸುವಾತ ಮುಂದೆ ಸಂಭವಿಸುವುದನ್ನು ನಿಮಗೆ ಗೋಚರಪಡಿಸಿದ್ದಾರೆ.",
			"30": "<sup>30</sup> ಆ ಗುಟ್ಟನ್ನು ನನಗೂ ವ್ಯಕ್ತಪಡಿಸಿದ್ದಾರೆ. ನಾನು ಎಲ್ಲ ಜೀವಂತರಿಗಿಂತ ಹೆಚ್ಚು ಬುದ್ಧಿಯುಳ್ಳವನು ಎಂದೇನೂ ಅಲ್ಲ. ಆ ಕನಸಿನ ಉದ್ದೇಶ ರಾಜರಾದ ತಮಗೆ ಗೋಚರವಾಗಿ ನಿಮ್ಮ ಮನದಾಲೋಚನೆಗಳು ನಿಮಗೆ ಅರ್ಥವಾಗಲಿ ಎಂದು ನನಗೂ ವ್ಯಕ್ತಪಡಿಸಲಾಗಿದೆ.",
			"31": "<sup>31</sup> “ರಾಜರೇ ಕೇಳಿ, ತಾವು ಕಂಡದ್ದು ಒಂದು ಅದ್ಭುತ ಪ್ರತಿಮೆ. ಥಳಥಳನೆ ಹೊಳೆಯುವ ಆ ದೊಡ್ಡ ಪ್ರತಿಮೆ ನಿಮ್ಮೆದುರಿಗೆ ನಿಂತಿತ್ತು. ಭಯಂಕರವಾಗಿ ಕಾಣಿಸುತ್ತಿತ್ತು.",
			"32": "<sup>32</sup> ಆ ಪ್ರತಿಮೆಯ ತಲೆ ಅಪ್ಪಟ ಬಂಗಾರದ್ದು. ಎದೆ ತೋಳುಗಳು ಬೆಳ್ಳಿಯವು. ಹೊಟ್ಟೆ ಸೊಂಟಗಳು ಕಂಚಿನವು.",
			"33": "<sup>33</sup> ಕಾಲುಗಳು ಕಬ್ಬಿಣದವು. ಪಾದಗಳು ಕಬ್ಬಿಣ ಮತ್ತು ಮಣ್ಣಿನಿಂದ ಮಾಡಿದವು.",
			"34": "<sup>34</sup> ನೀವು ನೋಡುತ್ತಿದ್ದ ಹಾಗೆ ಬೆಟ್ಟದಿಂದ ಒಂದು ಗುಂಡು ಬಂಡೆ, ಯಾವ ಕೈಯೂ ಮುಟ್ಟದೆಯೇ, ಸಿಡಿದುಬಂದು ಆ ಪ್ರತಿಮೆಯ ಕಬ್ಬಿಣ ಮಣ್ಣಿನಿಂದಾದ ಆ ಪಾದಗಳಿಗೆ ಬಡಿದು ಚೂರುಚೂರು ಮಾಡಿಬಿಟ್ಟಿತು.",
			"35": "<sup>35</sup> ಆಗ ಕಬ್ಬಿಣ-ಮಣ್ಣು-ಕಂಚು-ಬೆಳ್ಳಿ-ಬಂಗಾರ ಇವುಗಳೆಲ್ಲವೂ ಪುಡಿಪುಡಿಯಾದವು; ಸುಗ್ಗಿಯ ಕಣಗಳ ಹೊಟ್ಟಿನಂತಾದವು. ಗಾಳಿ ಅವುಗಳನ್ನು ತೂರಿಕೊಂಡುಹೋಯಿತು. ಅವುಗಳಿಗೆ ನೆಲೆಯೇ ಇಲ್ಲವಾಯಿತು. ಪ್ರತಿಮೆಗೆ ಬಡಿದ ಆ ಬಂಡೆ ಮಹಾ ಪರ್ವತವಾಗಿ ಭೂಲೋಕದಲ್ಲೆಲ್ಲಾ ತುಂಬಿಕೊಂಡಿತು.",
			"36": "<sup>36</sup> “ಅರಸರೇ, ಇದೇ ನೀವು ಕಂಡ ಕನಸು. ಇದರ ಅರ್ಥವನ್ನೂ ತಮ್ಮ ಸನ್ನಿಧಿಯಲ್ಲಿ ಅರಿಕೆಮಾಡುತ್ತೇವೆ.",
			"37": "<sup>37</sup> ನೀವು ರಾಜಾಧಿರಾಜರು, ಪರಲೋಕ ದೇವರು ನಿಮಗೆ ರಾಜ್ಯಬಲ, ಪರಾಕ್ರಮ ಹಾಗು ವೈಭವಗಳನ್ನು ದಯಪಾಲಿಸಿದ್ದಾರೆ.",
			"38": "<sup>38</sup> ನರಮಾನವರು ವಾಸಿಸುವ ಸಕಲ ಪ್ರಾಂತ್ಯಗಳಲ್ಲಿ ಆಕಾಶಪಕ್ಷಿಗಳನ್ನೂ ಭೂಜಂತುಗಳನ್ನೂ ನಿಮ್ಮ ಕೈಗೆ ಒಪ್ಪಿಸಿದ್ದಾರೆ.",
			"39": "<sup>39</sup> ನಿಮ್ಮ ಕಾಲವಾದ ಮೇಲೆ ನಿಮಗಿಂತ ಬೀಳಾದ ಮತ್ತೊಂದು ರಾಜ್ಯ ತಲೆಯೆತ್ತಿಕೊಳ್ಳುವುದು. ಅನಂತರ ಬೇರೊಂದು ರಾಜ್ಯ ಕಂಚಿನದಾಗಿ ಕಾಣಿಸಿಕೊಂಡು ಭೂಮಂಡಲವನ್ನೆಲ್ಲ ಆಳುವುದು.",
			"40": "<sup>40</sup> ನಾಲ್ಕನೆಯ ರಾಜ್ಯ ಕಬ್ಬಿಣದಷ್ಟು ಗಟ್ಟಿಗೆ ಕಬ್ಬಿಣ ಎಲ್ಲ ವಸ್ತುಗಳನ್ನು ಚೂರುಚೂರಾಗಿ ಒಡೆದುಹಾಕುತ್ತದೆ. ಅಂತೆಯೇ ಅದು ಎಲ್ಲವನ್ನು ಚೂರುಚೂರಾಗಿಸಿ ಧ್ವಂಸ ಮಾಡುವುದು.",
			"41": "<sup>41</sup> ಪಾದಗಳಲ್ಲಿ ಹಾಗು ಪಾದಬೆರಳುಗಳಲ್ಲಿ ಒಂದು ಅಂಶ ಕಬ್ಬಿಣ ಮತ್ತೊಂದು ಅಂಶ ಮಣ್ಣು ಆಗಿದ್ದನ್ನು ನೀವು ನೋಡಿದಿರಿ. ಅಂತೆಯೇ ಆ ರಾಜ್ಯವು ಭಿನ್ನಭಿನ್ನವಾಗಿರುವುದು. ಜೇಡಿಮಣ್ಣಿನೊಂದಿಗೆ ಕಬ್ಬಿಣ ಮಿಶ್ರವಾಗಿದ್ದನ್ನು ನೀವು ನೋಡಿದಂತೆ ಆ ರಾಜ್ಯದಲ್ಲಿ ಕಬ್ಬಿಣದ ಬಲ ಸೇರಿರುವುದು.",
			"42": "<sup>42</sup> ಕಾಲ್ಬೆರಳುಗಳ ಒಂದಂಶ ಮಣ್ಣು ಆಗಿದ್ದ ಹಾಗೆ ಆ ರಾಜ್ಯದ ಒಂದಂಶ ಗಟ್ಟಿ ಇನ್ನೊಂದು ಅಂಶ ಬೆಂಡು.",
			"43": "<sup>43</sup> ಕಬ್ಬಿಣ ಜೇಡಿಮಣ್ಣಿನೊಂದಿಗೆ ಬೆರೆತಿರುವುದನ್ನು ನೀವು ನೋಡಿದ ಮೇರೆಗೆ ಆ ರಾಜ್ಯಾಂಶಗಳು ಮದುವೆ ಸಂಬಂಧದಿಂದ ಬೆರೆತುಕೊಳ್ಳುವುವು. ಆದರೆ ಕಬ್ಬಿಣ ಮಣ್ಣಿನೊಂದಿಗೆ ಹೇಗೆ ಕಲೆಯುವುದಿಲ್ಲವೋ ಹಾಗೆ ಅವು ಒಂದಕ್ಕೊಂದು ಅಂಟಿಕೊಳ್ಳುವುದಿಲ್ಲ.",
			"44": "<sup>44</sup> ಆ ರಾಜರ ಕಾಲದಲ್ಲಿ ಪರಲೋಕ ದೇವರು ಒಂದು ಸಾಮ್ರಾಜ್ಯವನ್ನು ಸ್ಥಾಪಿಸುವರು. ಅದು ಎಂದಿಗೂ ಅಳಿಯದು. ಅದರ ಪ್ರಾಬಲ್ಯವು ಬೇರೆ ರಾಷ್ಟ್ರಕ್ಕೆ ಜಾರಿಹೋಗದು. ರಾಷ್ಟ್ರಗಳನ್ನೆಲ್ಲಾ ಭಂಗಪಡಿಸಿ, ನಿರ್ನಾಮಮಾಡಿ ಆ ಸಾಮ್ರಾಜ್ಯ ಶಾಶ್ವತವಾಗಿ ನಿಲ್ಲುವುದು.",
			"45": "<sup>45</sup> ಬೆಟ್ಟದಿಂದ ಒಂದು ಗುಂಡುಬಂಡೆ, ಯಾವ ಕೈಯೂ ಮುಟ್ಟದೆಯೇ, ಸಿಡಿದುಬಂದು ಕಬ್ಬಿಣ-ಕಂಚು-ಮಣ್ಣು-ಬೆಳ್ಳಿ-ಬಂಗಾರ ಇವುಗಳನ್ನು ಚೂರುಚೂರು ಮಾಡಿದ್ದನ್ನು ನೀವು ನೋಡಿದಿರಿ. ಇದರಿಂದ ಪರಲೋಕ ದೇವರು ಮುಂದೆ ನಡೆಯಲಿರುವ ವಿಷಯಗಳನ್ನು ರಾಜರಾದ ತಮಗೆ ತಿಳಿಯಪಡಿಸಿದ್ದಾರೆ. ಆ ಕನಸು ನಿಜವಾದುದು. ಅದರ ಅರ್ಥವೂ ನಂಬತಕ್ಕದ್ದು,” ಎಂದು ವಿವರಿಸಿದನು.",
			"46": "<sup>46</sup> ಇದನ್ನು ಆಲಿಸಿದ ರಾಜ ನೆಬೂಕದ್ನೆಚ್ಚರನು ಎದ್ದು ಅಡ್ಡಬಿದ್ದನು. ದಾನಿಯೇಲನನ್ನು ಪೂಜಿಸಿ, ಅವನಿಗೆ ನೈವೇದ್ಯಮಾಡಿ, ಧೂಪಾರತಿ ಎತ್ತಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"47": "<sup>47</sup> ದಾನಿಯೇಲನ ವಿವರಕ್ಕೆ ಉತ್ತರವಾಗಿ, “ನೀನು ಈ ಗುಟ್ಟನ್ನು ಬಯಲಿಗೆ ತರಲು ಸಮರ್ಥನಾದ ಕಾರಣ ನಿಮ್ಮ ದೇವರು ದೇವಾಧಿದೇವರು, ರಾಜಾಧಿರಾಜರು, ಗುಟ್ಟನ್ನು ಬಟ್ಟಬಯಲಾಗಿಸುವವರು! ಇದು ಸತ್ಯ,” ಎಂದು ಹೇಳಿದನು.",
			"48": "<sup>48</sup> ಬಳಿಕ ರಾಜನು ದಾನಿಯೇಲನನ್ನು ದೊಡ್ಡ ಪದವಿಗೆ ಏರಿಸಿದನು. ಅಮೂಲ್ಯವಾದ ಬಹುಮಾನಗಳನ್ನು ಕೊಟ್ಟನು. ಬಾಬಿಲೋನಿನ ಸಂಸ್ಥಾನವನ್ನೆಲ್ಲ ಅವನಿಗೆ ಅಧೀನಪಡಿಸಿ ಬಾಬಿಲೋನಿನ ಎಲ್ಲಾ ವಿದ್ವಾಂಸರಿಗೂ ಅವನನ್ನು ಅಧ್ಯಕ್ಷನನ್ನಾಗಿ ನೇಮಿಸಿದನು.",
			"49": "<sup>49</sup> ದಾನಿಯೇಲನ ಕೋರಿಕೆಯ ಪ್ರಕಾರ ರಾಜನು ಶದ್ರಕ್, ಮೇಶಕ್ ಮತ್ತು ಅಬೇದ್‍ನೆಗೋ ಎಂಬುವರನ್ನು ಬಾಬಿಲೋನಿನ ಪ್ರಾಂತ್ಯಾಧಿಪತಿಗಳನ್ನಾಗಿ ನೇಮಿಸಿದನು. ದಾನಿಯೇಲನು ಮಾತ್ರ ಅರಮನೆಯಲ್ಲೇ ಕಾರ್ಯನಿರತನಾದನು."
		},
		"3": {
			"1": "<sup>1</sup> ರಾಜ ನೆಬೂಕದ್ನೆಚ್ಚರನು 27 ಮೀಟರ್ ಎತ್ತರದ 3 ಮೀಟರ್ ಅಗಲದ ಒಂದು ಬಂಗಾರದ ಪ್ರತಿಮೆಯನ್ನು ಮಾಡಿಸಿ ಬಾಬಿಲೋನ್ ಸಂಸ್ಥಾನದ ‘ದೂರಾ’ ಎಂಬ ಬಯಲಿನಲ್ಲಿ ನಿಲ್ಲಿಸಿದನು.",
			"2": "<sup>2</sup> ಬಳಿಕ ರಾಜ ನೆಬೂಕದ್ನೆಚ್ಚರನು ತಾನು ನಿಲ್ಲಿಸಿದ್ದ ಆ ಪ್ರತಿಮೆಯ ಪ್ರತಿಷ್ಠಾಪನಾ ಸಮಾರಂಭವನ್ನು ಏರ್ಪಡಿಸಿ, ಉಪರಾಜ, ನಾಯಕ, ದೇಶಾಧಿಪತಿ, ಮಂತ್ರಿ, ಕೋಶಾಧ್ಯಕ್ಷ, ನ್ಯಾಯಾಧಿಪತಿ, ವಿಚಾರಕ ಇವರನ್ನೂ, ಸಮಸ್ತ ಸಂಸ್ಥಾನಾಧಿಕಾರಿಗಳನ್ನೂ ಸಭೆ ಸೇರಿಸುವಂತೆ ದೂತರನ್ನು ಕಳುಹಿಸಿದನು.",
			"3": "<sup>3</sup> ಆ ಉಪರಾಜ, ನಾಯಕ, ದೇಶಾಧಿಪತಿ, ಮಂತ್ರಿ, ಕೋಶಾಧ್ಯಕ್ಷ, ನ್ಯಾಯಾಧಿಪತಿ, ವಿಚಾರಕ ಇವರೂ ಸಮಸ್ತ ಸಂಸ್ಥಾನಾಧಿಕಾರಿಗಳೂ ರಾಜ ನೆಬೂಕದ್ನೆಚ್ಚರನು ನಿಲ್ಲಿಸಿದ್ದ ಪ್ರತಿಮೆಯ ಪ್ರತಿಷ್ಠಾಪನಾ ಸಮಾರಂಭಕ್ಕೆ ನೆರೆದುಬಂದು ಆ ಪ್ರತಿಮೆಯ ಮುಂದೆ ನಿಂತುಕೊಂಡರು.",
			"4": "<sup>4</sup> ಆಗ, ಸಾರುವವರು ಗಟ್ಟಿಯಾಗಿ ಕೂಗಿ, “ವಿವಿಧ ಜನಾಂಗ-ಕುಲ-ಭಾಷೆಗಳವರೇ, ನಿಮಗೆ ಹೀಗೆಂದು ರಾಜಾಜ್ಞೆಯಾಗಿದೆ:",
			"5": "<sup>5</sup> ನೀವು ತುತೂರಿ, ಕೊಳಲು, ಕಿನ್ನರಿ, ತಂಬೂರಿ, ವೀಣೆ, ಓಲಗ ಮುಂತಾದ ಸಕಲ ವಾದ್ಯಗಳ ಧ್ವನಿಯನ್ನು ಕೇಳಿದ ಕೂಡಲೆ ಅಡ್ಡಬಿದ್ದು ರಾಜ ನೆಬೂಕದ್ನೆಚ್ಚರರು ನಿಲ್ಲಿಸಿರುವ ಈ ಬಂಗಾರದ ಪ್ರತಿಮೆಯನ್ನು ಆರಾಧಿಸಬೇಕು.",
			"6": "<sup>6</sup> ಯಾರು ಅಡ್ಡಬಿದ್ದು ಆರಾಧಿಸುವುದಿಲ್ಲವೋ ಅಂಥವರನ್ನು ತಕ್ಷಣವೇ ಧಗಧಗನೆ ಉರಿಯುವ ಆವಿಗೆಯೊಳಗೆ ಹಾಕಲಾಗುವುದು,” ಎಂದು ಸಾರಿದನು.",
			"7": "<sup>7</sup> ಅಂತೆಯೇ ಸಮಸ್ತ ಜನಾಂಗ-ಕುಲ-ಭಾಷೆಗಳವರು ತುತೂರಿ, ಕೊಳಲು, ಕಿನ್ನರಿ, ತಂಬೂರಿ, ವೀಣೆ ಮುಂತಾದ ಸಕಲ ವಾದ್ಯಗಳ ಧ್ವನಿಯನ್ನು ಕೇಳಿದ ಕೂಡಲೆ ಎಲ್ಲರು ಅದೇ ಸಮಯದಲ್ಲಿ ಅಡ್ಡಬಿದ್ದು ರಾಜ ನೆಬೂಕದ್ನೆಚ್ಚರನು ನಿಲ್ಲಿಸಿದ್ದ ಆ ಬಂಗಾರದ ಪ್ರತಿಮೆಯನ್ನು ಆರಾಧಿಸಿದರು.",
			"8": "<sup>8</sup> ಆಗ ಕೆಲವು ಮಂದಿ ಬಾಬಿಲೋನಿಯರು ಯೆಹೂದ್ಯರ ಮೇಲೆ ದೂರು ಹೊರಿಸಲು ರಾಜ ನೆಬೂಕದ್ನೆಚ್ಚರನ ಬಳಿಗೆ ಬಂದರು.",
			"9": "<sup>9</sup> “ಅರಸರೇ, ನೀವು ಚಿರಂಜೀವಿಯಾಗಿರಿ!”",
			"10": "<sup>10</sup> ಪ್ರಭುವೇ, ತುತೂರಿ, ಕೊಳಲು, ಕಿನ್ನರಿ, ತಂಬೂರಿ, ವೀಣೆ, ಓಲಗ ಮುಂತಾದ ಸಕಲ ವಾದ್ಯಗಳ ಧ್ವನಿ ಕೇಳಿದ ಪ್ರತಿಯೊಬ್ಬನೂ ಅಡ್ಡಬಿದ್ದು ಬಂಗಾರದ ಪ್ರತಿಮೆಯನ್ನು ಆರಾಧಿಸಲಿ;",
			"11": "<sup>11</sup> ಯಾವನು ಅಡ್ಡಬಿದ್ದು ಆರಾಧಿಸುವುದಿಲ್ಲವೋ ಅಂಥವನು ಧಗಧಗಿಸುವ ಆವಿಗೆಯೊಳಗೆ ಹಾಕಲ್ಪಡಲಿ ಎಂದು ತಾವು ಆಜ್ಞೆ ಮಾಡಿದಿರಲ್ಲವೆ?",
			"12": "<sup>12</sup> ತಾವು ಬಾಬಿಲೋನಿನ ಸಂಸ್ಥಾನಾಧಿಕಾರತ್ವವನ್ನು ವಹಿಸಿಕೊಟ್ಟ ಶದ್ರಕ್, ಮೇಶಕ್ ಹಾಗೂ ಅಬೇದ್‍ನೆಗೋ ಎಂಬ ಕೆಲವು ಮಂದಿ ಯೆಹೂದ್ಯರು ಇದ್ದಾರೆ. ರಾಜರೇ ಅವರು ನಿಮ್ಮನ್ನು ಲಕ್ಷಿಸುವುದಿಲ್ಲ. ನಿಮ್ಮ ದೇವರುಗಳಿಗೆ ಸೇವೆಮಾಡುವುದಿಲ್ಲ, ನೀವು ಸ್ಥಾಪಿಸಿದ ಬಂಗಾರದ ಪ್ರತಿಮೆಯನ್ನು ಆರಾಧಿಸುವುದಿಲ್ಲ,” ಎಂದು ಆಪಾದಿಸಿದರು.",
			"13": "<sup>13</sup> ನೆಬೂಕದ್ನೆಚ್ಚರನು ಉಗ್ರಕೋಪಗೊಂಡು ಶದ್ರಕ್, ಮೇಶಕ್, ಅಬೇದ್‍ನೆಗೋ ಎಂಬವರನ್ನು ಹಿಡಿದುತರಲು ಆಜ್ಞಾಪಿಸಿದನು.",
			"14": "<sup>14</sup> ಅದರಂತೆಯೇ ಅವರನ್ನು ಹಿಡಿದುತರಲಾಯಿತು. ನೆಬೂಕದ್ನೆಚ್ಚರನು ಅವರಿಗೆ, “ಶದ್ರಕ್, ಮೇಶಕ್, ಹಾಗು ಅಬೇದ್‍ನೆಗೋ ಎಂಬವರೇ, ನೀವು ಬೇಕುಬೇಕೆಂದು, ನನ್ನ ದೇವರಿಗೆ ಸೇವೆಮಾಡದೆ, ನಾನು ಸ್ಥಾಪಿಸಿದ ಬಂಗಾರದ ಪ್ರತಿಮೆಯನ್ನು ಆರಾಧಿಸದೆ ಇರುವಿರೋ?",
			"15": "<sup>15</sup> ಈಗಲಾದರು ನೀವು ಸಿದ್ಧರಾಗಿದ್ದು ತುತೂರಿ, ಕೊಳಲು, ಕಿನ್ನರಿ, ತಂಬೂರಿ, ವೀಣೆ, ಓಲಗ ಮುಂತಾದ ಸಕಲ ವಾದ್ಯಗಳ ಧ್ವನಿ ಕೇಳಿದ ಕೂಡಲೆ ಅಡ್ಡಬಿದ್ದು ನಾನು ಸ್ಥಾಪಿಸಿದ ಪ್ರತಿಮೆಯನ್ನು ಆರಾಧಿಸಿದರೆ ಸರಿ, ಇಲ್ಲವಾದರೆ ಈ ಗಳಿಗೆಯಲ್ಲೆ ನಿಮ್ಮನ್ನು ಧಗಧಗನೆ ಉರಿಯುವ ಆವಿಗೆಯೊಳಗೆ ಹಾಕಲಾಗುವುದು. ನಿಮ್ಮನ್ನು ನನ್ನ ಕೈಯಿಂದ ಬಿಡಿಸಬಲ್ಲ ದೇವರು ಯಾರಿದ್ದಾನೆ?” ಎಂದು ಹೇಳಿದನು.",
			"16": "<sup>16</sup> ಇದನ್ನು ಕೇಳಿದ ಶದ್ರಕ್, ಮೇಶಕ್ ಮತ್ತು ಅಬೇದ್‍ನೆಗೋ ಎಂಬುವರು ರಾಜನಿಗೆ: “ನೆಬೂಕದ್ನೆಚ್ಚರ ಅರಸರೇ, ಈ ವಿಷಯದಲ್ಲಿ ನಾವು ನಿಮಗೆ ಏನನ್ನೂ ಹೇಳಬೇಕಾಗಿಲ್ಲ.",
			"17": "<sup>17</sup> ನಾವು ಸೇವೆಮಾಡುವ ದೇವರಿಗೆ ಚಿತ್ತವಿದ್ದರೆ ಅವರೇ ಧಗಧಗನೆ ಉರಿಯುವ ಆವಿಗೆಯೊಳಗಿಂದಲೂ ನಮ್ಮನ್ನು ಕಾಪಾಡಬಲ್ಲರು. ಅವರೇ ನಮ್ಮನ್ನು ನಿಮ್ಮ ಕೈಯಿಂದಲೂ ಬಿಡಿಸಬಲ್ಲರು.",
			"18": "<sup>18</sup> ಒಂದು ವೇಳೆ ಬಿಡಿಸದಿದ್ದರೂ ರಾಜರೇ, ಇದು ನಿಮಗೆ ತಿಳಿದಿರಲಿ: ನಾವು ನಿಮ್ಮ ದೇವರುಗಳಿಗೆ ಸೇವೆಮಾಡುವುದಿಲ್ಲ. ನೀವು ಸ್ಥಾಪಿಸಿರುವ ಬಂಗಾರದ ಪ್ರತಿಮೆಯನ್ನು ಆರಾಧಿಸುವುದಿಲ್ಲ,” ಎಂದು ಉತ್ತರಕೊಟ್ಟರು.",
			"19": "<sup>19</sup> ಆಗ ನೆಬೂಕದ್ನೆಚ್ಚರನು ಕೋಪೋದ್ರೇಕಗೊಂಡನು. ಶದ್ರಕ್, ಮೇಶಕ್ ಮತ್ತು ಅಬೇದ್‍ನೆಗೋ ಅವರ ಬಗ್ಗೆ ಅವನ ಮುಖಚರ್ಯೆ ಬದಲಾಯಿತು. ಆವಿಗೆಯನ್ನು ವಾಡಿಕೆಗಿಂತ ಏಳರಷ್ಟು ಹೆಚ್ಚಾಗಿ ಉರಿಸಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿದನು.",
			"20": "<sup>20</sup> ಇದಲ್ಲದೆ ತನ್ನ ಸೈನ್ಯದ ಕೆಲವುಮಂದಿ ಶೂರರಿಗೆ ಶದ್ರಕ್, ಮೇಶಕ್, ಅಬೇದ್‍ನೆಗೋ ಅವರನ್ನು ಕಟ್ಟಿ ಧಗಧಗನೆ ಉರಿಯುವ ಆವಿಗೆಯೊಳಗೆ ಎತ್ತಿಹಾಕಬೇಕೆಂದು ಅಪ್ಪಣೆಕೊಟ್ಟನು.",
			"21": "<sup>21</sup> ಅದರಂತೆಯೇ ಉಡಿಗೆತೊಡಿಗೆ, ಪೇಟ ಮುಂತಾದ ಬಟ್ಟೆಬರೆಗಳನ್ನು ಧರಿಸಿಕೊಂಡಿದ್ದ ಹಾಗೆಯೇ ಅವರನ್ನು ಕಟ್ಟಿ ಧಗಧಗನೆ ಉರಿಯುವ ಆವಿಗೆಯೊಳಗೆ ಹಾಕಿಬಿಟ್ಟರು.",
			"22": "<sup>22</sup> ರಾಜಾಜ್ಞೆಯು ಖಡಾಖಂಡಿತವಾಗಿದ್ದರಿಂದಲೂ ಆವಿಗೆಯ ಕಾವು ಅತಿ ತೀಕ್ಷ್ಣವಾಗಿದ್ದರಿಂದಲೂ ಅಗ್ನಿಕುಂಡವು ಶದ್ರಕ್, ಮೇಶಕ್ ಮತ್ತು ಅಬೇದ್‍ನೆಗೋ ಎಂಬುವರನ್ನು ಎತ್ತಿ ತಂದವರನ್ನೇ ಭಸ್ಮಮಾಡಿಬಿಟ್ಟಿತು.",
			"23": "<sup>23</sup> ಅತ್ತ ಶದ್ರಕ್, ಮೇಶಕ್, ಅಬೇದ್‍ನೆಗೋ ಈ ಮೂವರು ಕಟ್ಟಿನೊಂದಿಗೆ ಧಗಧಗನೆ ಉರಿಯುವ ಆವಿಗೆಯ ನಡುವೆ ಬಿದ್ದರು.",
			"24": "<sup>24</sup> ಇತ್ತ ರಾಜ ನೆಬೂಕದ್ನೆಚ್ಚರನು ಬೆಚ್ಚಿಬಿದ್ದು, ತಟ್ಟನೆ ಎದ್ದು ತನ್ನ ಮಂತ್ರಿಗಳನ್ನು ಕರೆಸಿ, “ನಾವು ಮೂವರನ್ನು ಕಟ್ಟಿ ಬೆಂಕಿಯಲ್ಲಿ ಹಾಕಿಸಿದೆವಲ್ಲವೆ?” ಎಂದು ಕೇಳಿದನು. ಅವರು ರಾಜನಿಗೆ, “ಹೌದು ಪ್ರಭೂ, ಅದು ಸತ್ಯ,” ಎಂದು ಉತ್ತರಕೊಟ್ಟರು.",
			"25": "<sup>25</sup> ಅದಕ್ಕೆ ಅವರು, “ಆದರೆ, ಇಗೋ ಕಟ್ಟಿಲ್ಲದ ನಾಲ್ವರನ್ನು ನಾನು ನೋಡುತ್ತಿದ್ದೇನೆ. ಬೆಂಕಿಯಲ್ಲಿ ತಿರುಗಾಡುತ್ತಿದ್ದಾರೆ; ಅವರಿಗೆ ಯಾವ ಹಾನಿಯೂ ಆಗುತ್ತಿಲ್ಲ.",
			"26": "<sup>26</sup> ನಾಲ್ಕನೆಯವನ ರೂಪ ದೇವಪುತ್ರನ ರೂಪದಂತಿದೆ!” ಎಂದು ಹೇಳಿ ಧಗಧಗನೆ ಉರಿಯುತ್ತಿದ್ದ ಆವಿಗೆಯ ಬಾಯಿಯ ಬಳಿಗೆಹೋಗಿ, “ಪರಾತ್ಪರ ದೇವರ ದಾಸರಾದ ಶದ್ರಕ್, ಮೇಶಕ್ ಮತ್ತು ಅಬೇದ್‍ನೆಗೋ ಎಂಬುವರೇ, ಬನ್ನಿ, ಹೊರಗೆ ಬನ್ನಿ,” ಎಂದು ಕೂಗಿಕೊಂಡನು. ಅಂತೆಯೇ ಶದ್ರಕ್, ಮೇಶಕ್ ಹಾಗು ಅಬೇದ್‍ನೆಗೋ ಅವರು ಬೆಂಕಿಯಿಂದ ಹೊರಟುಬಂದರು.",
			"27": "<sup>27</sup> ಅವರನ್ನು ನೋಡಲು ಉಪರಾಜ, ನಾಯಕ, ದೇಶಾಧಿಪತಿ, ಮಂತ್ರಿ ಇವರೆಲ್ಲರು ಕೂಡಿಬಂದರು. ಅವರಿಗೆ ಬೆಂಕಿಯ ತಾಪವೂ ತಟ್ಟಿಲ್ಲ. ತಲೆಗೂದಲೂ ಸುಟ್ಟಿಲ್ಲ, ಅವರ ಬಟ್ಟೆಬರೆಗೆ ಏನೂ ಆಗಿಲ್ಲ, ಬೆಂಕಿಯ ವಾಸನೆಯೂ ಇಲ್ಲ, ಎಂದು ತಿಳಿದುಕೊಂಡರು.",
			"28": "<sup>28</sup> ಆಗ ನೆಬೂಕದ್ನೆಚ್ಚರನು, “ಶದ್ರಕ್, ಮೇಶಕ್ ಹಾಗು ಅಬೇದ್‍ನೆಗೋ ಎಂಬುವರ ದೇವರಿಗೆ ಸ್ತೋತ್ರವಾಗಲಿ! ಆತ ತನ್ನ ದೂತರನ್ನು ಕಳಿಸಿ ತನ್ನಲ್ಲಿ ವಿಶ್ವಾಸವಿಟ್ಟ ಇವರನ್ನು ರಕ್ಷಿಸಿದ್ದಾನೆ. ಇವರೋ ರಾಜಾಜ್ಞೆಯನ್ನೂ ಮೀರಿ ತಮ್ಮ ದೇವರನ್ನೇ ಹೊರತು ಇನ್ನಾವ ದೇವರನ್ನೂ ಪೂಜಿಸಿ ಆರಾಧಿಸಬಾರದೆಂದು ಸ್ವಂತ ದೇಹಗಳನ್ನೇ ಸಾವಿಗೊಪ್ಪಿಸಿದಂಥ ಭಕ್ತರು!",
			"29": "<sup>29</sup> ಸಕಲ ದೇಶ-ಕುಲ-ಭಾಷೆಗಳವರಲ್ಲಿ ಯಾರಾದರು ಶದ್ರಕ್, ಮೇಶಕ್ ಹಾಗು ಅಬೇದ್‍ನೆಗೋ ಎಂಬುವರ ದೇವರ ವಿಷಯದಲ್ಲಿ ಅಲ್ಲಸಲ್ಲದ ಮಾತನಾಡಿದರೆ ಅಂಥವರನ್ನು ತುಂಡುತುಂಡಾಗಿ ಕತ್ತರಿಸಿ ಅವರ ಮನೆಗಳನ್ನು ತಿಪ್ಪೆಗುಂಡಿಯನ್ನಾಗಿಸಬೇಕೆಂದು ಆಜ್ಞೆ ವಿಧಿಸುತ್ತೇನೆ. ಹೀಗೆ ರಕ್ಷಿಸಲು ಸಮರ್ಥನಾದ ಇನ್ನಾವ ದೇವರೂ ಇಲ್ಲವೇ ಇಲ್ಲ,” ಎಂದು ಹೇಳಿದನು.",
			"30": "<sup>30</sup> ಇದಾದ ಬಳಿಕ ರಾಜನು ಶದ್ರಕ್, ಮೇಶಕ್, ಅಬೇದ್‍ನೆಗೋ ಎಂಬುವರನ್ನು ಸಂಸ್ಥಾನದಲ್ಲಿ ಉನ್ನತಪದವಿಗೆ ಏರಿಸಿದನು."
		},
		"4": {
			"1": "<sup>1</sup> ಜಗದಲ್ಲಿ ವಾಸಿಸುವ ಎಲ್ಲಾ ಜನಾಂಗದವರಿಗೂ ಕುಲದವರಿಗೂ ಭಾಷೆಯವರಿಗೂ ರಾಜ ನೆಬೂಕದ್ನೆಚ್ಚರನು ಹೊರಡಿಸಿದ ಪ್ರಕಟಣೆ - “ನಿಮಗೆ ಹೆಚ್ಚೆಚ್ಚು ಸುಖಶಾಂತಿ ಲಭಿಸಲಿ!",
			"2": "<sup>2</sup> ಪರಾತ್ಪರ ದೇವರು ನನ್ನ ವಿಷಯದಲ್ಲಿ ನಡೆಸಿರುವ ಸೂಚಕಕಾರ್ಯಗಳನ್ನೂ ಅದ್ಭುತಗಳನ್ನೂ ಪ್ರಚುರಪಡಿಸುವುದು ನನಗೆ ವಿಹಿತವೆಂದು ತೋರಿಬಂದಿದೆ:",
			"3": "<sup>3</sup> ದೇವರ ಸೂಚಕಕಾರ್ಯಗಳು ಎಷ್ಟೋ ಅತಿಶಯ! ಅವರ ಅದ್ಭುತಗಳು ಎಷ್ಟೋ ಅಪಾರ! ಅವರ ರಾಜ್ಯ ಶಾಶ್ವತ ಸಾಮ್ರಾಜ್ಯ; ಅವರ ಆಳ್ವಿಕೆ ತಲತಲಾಂತರಕ್ಕೂ ಸ್ಥಿರ.",
			"4": "<sup>4</sup> “ನೆಬೂಕದ್ನೆಚ್ಚರನಾದ ನಾನು ನನ್ನ ಅರಮನೆಯಲ್ಲಿ ಸುಖವಾಗಿದ್ದೆ. ನನ್ನ ರಾಜಭವನದಲ್ಲಿ ನೆಮ್ಮದಿಯಿಂದ ಇದ್ದೆ.",
			"5": "<sup>5</sup> ಹೀಗಿರುವಲ್ಲಿ ಭಯಂಕರವಾದ ಕನಸೊಂದನ್ನು ಕಂಡೆ. ನಿದ್ರೆಯಲ್ಲಿ ನನಗೆ ತೋಚಿದ ಆಲೋಚನೆಗಳು ಹಾಗು ಮನಸ್ಸಿಗೆ ಬಿದ್ದ ಸ್ವಪ್ನಗಳು ನನ್ನನ್ನು ಕಳವಳಕ್ಕೆ ಈಡುಮಾಡಿದವು.",
			"6": "<sup>6</sup> ಆದ್ದರಿಂದ ಕನಸಿನ ಅರ್ಥವನ್ನು ನನಗೆ ತಿಳಿಸಬೇಕೆಂದು ಬಾಬಿಲೋನಿನ ವಿದ್ವಾಂಸರೆಲ್ಲರನ್ನು ಕರೆದುತರಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಿದೆ.",
			"7": "<sup>7</sup> ಜೋಯಿಸರು, ಮಂತ್ರವಾದಿಗಳು, ಪಂಡಿತರು, ಶಾಕುನಿಕರು ಇವರೆಲ್ಲರು ನನ್ನ ಸಮ್ಮುಖಕ್ಕೆ ಬಂದರು. ನಾನು ಕನಸನ್ನು ತಿಳಿಸಿದೆ. ಆದರೆ ಅದರ ಅರ್ಥವನ್ನು ಅವರಿಂದ ತಿಳಿಸಲಾಗಲಿಲ್ಲ.",
			"8": "<sup>8</sup> ನನ್ನ ದೇವರ ಹೆಸರು ಸೇರಿರುವ ‘ಬೇಲ್ತೆಶಚ್ಚರ’ ಎಂಬ ಅಡ್ಡಹೆಸರನ್ನು ಹೊಂದಿದ ದಾನಿಯೇಲನು ಕೊನೆಗೆ ನನ್ನ ಬಳಿಗೆ ಬಂದ. ಪರಿಶುದ್ಧ ದೇವರ ಆತ್ಮ ಅವನಲ್ಲಿತ್ತು. ಅವನಿಗೆ ನನ್ನ ಕನಸನ್ನು ಹೀಗೆ ತಿಳಿಸಿದೆ.",
			"9": "<sup>9</sup> ‘ಬೇಲ್ತೆಶಚ್ಚರನೇ, ಜೋಯಿಸರಲ್ಲಿ ಪ್ರಾಮುಖ್ಯನೇ, ಪರಿಶುದ್ಧ ದೇವರ ಆತ್ಮ ನಿನ್ನಲ್ಲಿ ನೆಲೆಯಾಗಿದೆಯೆಂದು ತಿಳಿದಿದ್ದೇನೆ. ಯಾವ ರಹಸ್ಯವೂ ನಿನಗೆ ಅಶೋಧ್ಯವಲ್ಲವೆಂದು ನನಗೆ ಗೊತ್ತು. ಆದುದರಿಂದ ನಾನು ಕಂಡ ಕನಸನ್ನೂ ಅದರ ಅರ್ಥವನ್ನೂ ನನಗೆ ತಿಳಿಸು.’",
			"10": "<sup>10</sup> ‘ನಾನು ಹಾಸಿಗೆ ಮೇಲೆ ಮಲಗಿದ್ದಾಗ ನನ್ನ ಮನಸ್ಸಿಗೆ ಬಿದ್ದ ಕನಸುಗಳು ಇವು: ಇಗೋ ಭೂಮಧ್ಯದಲ್ಲಿ ಬಹು ಎತ್ತರವಾದ ಒಂದು ಮರವನ್ನು ಕಂಡೆ.",
			"11": "<sup>11</sup> ಆ ಮರ ಬೆಳೆದು ಬಲಗೊಂಡಿತ್ತು. ಅದರ ತುದಿ ಆಕಾಶವನ್ನು ಮುಟ್ಟುತ್ತಿತ್ತು. ಅದು ಭೂಲೋಕದ ಕಟ್ಟಕಡೆಗೂ ಕಾಣಿಸುತ್ತಿತ್ತು.",
			"12": "<sup>12</sup> ಅದರ ಎಲೆಗಳು ಚೆಲುವಾಗಿದ್ದವು. ಅದು ಹಣ್ಣುಗಳಿಂದ ತುಂಬಿತ್ತು. ಅದರಿಂದ ಎಲ್ಲಕ್ಕೂ ಆಹಾರ, ವನ್ಯಮೃಗಗಳಿಗೆ ಅದರ ನೆರಳಿನಲ್ಲಿ ಆಶ್ರಯ, ಆಕಾಶದ ಪಕ್ಷಿಗಳಿಗೆ ಅದರ ಕೊಂಬೆಗಳಲ್ಲಿ ನೆಲೆ, ಎಲ್ಲಾ ಪ್ರಾಣಿಗಳಿಗೂ ಅದರಿಂದ ಪೋಷಣೆ ದೊರಕುತ್ತಿತ್ತು.",
			"13": "<sup>13</sup> ‘ನಾನು ಹಾಸಿಗೆ ಮೇಲೆ ಮಲಗಿದ್ದಾಗ ನನ್ನ ಮನಸ್ಸಿಗೆ ಬಿದ್ದ ಕನಸಿನಲ್ಲಿ, ಇಗೋ, ಕಾವಲುಗಾರನಾದ ದೇವದೂತನೊಬ್ಬನು ಆಕಾಶದಿಂದ ಇಳಿದು, ಗಟ್ಟಿಯಾಗಿ ಕೂಗಿ:",
			"14": "<sup>14</sup> ಆ ಮರವನ್ನು ಕಡಿಯಿರಿ, ಕೊಂಬೆಗಳನ್ನು ಕತ್ತರಿಸಿ, ಎಲೆಗಳನ್ನು ಉದುರಿಸಿ, ಹಣ್ಣುಗಳನ್ನು ಕಿತ್ತುಬಿಸಾಡಿ. ಮೃಗಗಳು ಅದರ ನೆರಳನ್ನು ಬಿಟ್ಟು ತೆರಳಲಿ, ಪಕ್ಷಿಗಳು ರೆಂಬೆಗಳನ್ನು ತೊರೆದುಹೋಗಲಿ.",
			"15": "<sup>15</sup> ಆದರೆ ಅದರ ಬುಡದ ಮೋಟನ್ನು ನೆಲದಲ್ಲಿ ಉಳಿಸಿ ಹಿತ್ತಾಳೆ ಕಬ್ಬಿಣಗಳ ಪಟ್ಟಿಯನ್ನು ಅದಕ್ಕೆ ಬಿಗಿಯಿರಿ. ಅಡವಿಯ ಹುಲ್ಲು ಅದರ ಸುತ್ತ ಬೆಳೆದಿರಲಿ.’ ‘ಆಕಾಶದ ಇಬ್ಬನಿ ಅದನ್ನು ತೋಯಿಸಲಿ, ಹುಲ್ಲುಗಿಡಗಳ ಹಾಗು ಕಾಡುಮೃಗಗಳ ನಡುವೆ ಅದಿರಲಿ.",
			"16": "<sup>16</sup> ಅದಕ್ಕೆ ಏಳುವರ್ಷ ಕಾಲ, ಮನುಷ್ಯ ಹೃದಯ ಹೋಗಿ ಮೃಗದ ಹೃದಯ ಬರಲಿ.",
			"17": "<sup>17</sup> ಇದು ಕಾವಲುಗಾರ ದೂತನ ತೀರ್ಮಾನ; ಹಾಗು ದೇವರ ತೀರ್ಪು. ಪರಾತ್ಪರ ದೇವರಿಗೆ ನರಮಾನವರ ರಾಜ್ಯದ ಮೇಲೂ ಅಧಿಕಾರವಿದೆ. ಅದರ ಆಳ್ವಿಕೆಯನ್ನು ಅವರು ತಮಗೆ ಬೇಕಾದವರಿಗೆ ಒಪ್ಪಿಸುತ್ತಾರೆ. ಕನಿಷ್ಠರನ್ನೂ ಆ ಪದವಿಗೆ ನೇಮಿಸುತ್ತಾರೆ. ಇದು ಎಲ್ಲಾ ಜನರಿಗೆ ತಿಳಿದಿರಬೇಕೆಂಬುದೇ ಈ ತೀರ್ಮಾನ,' ಎಂದು ಸಾರಿದನು.",
			"18": "<sup>18</sup> ‘ಇದೇ ನಾನು ಕಂಡ ಕನಸು. ಬೇಲ್ತೆಶಚ್ಚರನೇ, ನನ್ನ ರಾಜ್ಯದ ಸಕಲ ವಿದ್ವಾಂಸರಲ್ಲಿ ಯಾರಿಂದಲೂ ಇದರ ಅರ್ಥವನ್ನು ಹೇಳಲಾಗಲಿಲ್ಲ. ಅದನ್ನು ನೀನು ತಿಳಿಸು. ಪರಿಶುದ್ಧ ದೇವರ ಆತ್ಮ ನಿನ್ನಲ್ಲಿ ನೆಲೆಸಿರುವುದರಿಂದ ಇದು ನಿನ್ನಿಂದ ಸಾಧ್ಯ’,” ಎಂದನು ರಾಜ ನೆಬೂಕದ್ನೆಚ್ಚರನು.",
			"19": "<sup>19</sup> ಇದನ್ನು ಕೇಳಿದ ಬೇಲ್ತೆಶಚ್ಚರನೆಂದು ಹೆಸರುಗೊಂಡಿದ್ದ ದಾನಿಯೇಲನು ತುಸು ಹೊತ್ತು ಸ್ತಬ್ದನಾದ. ಅವನ ಬುದ್ಧಿಗೆ ಹೊಳೆದ ವಿಷಯ ಅವನಲ್ಲಿ ದಿಗಿಲನ್ನು ಉಂಟುಮಾಡಿತು. ರಾಜನು ಇದನ್ನು ಅರಿತು, “ಬೇಲ್ತೆಶಚ್ಚರನೇ, ನನ್ನ ಕನಸಾಗಲಿ, ಅದರ ಅರ್ಥವಾಗಲಿ ನಿನ್ನನ್ನು ಹೆದರಿಸದಿರಲಿ,” ಎಂದು ಧೈರ್ಯಹೇಳಿದ. ಆಗ ಬೇಲ್ತೆಶಚ್ಚರನು: \"ನನ್ನೊಡೆಯಾ, ಈ ಕನಸು ನಿನ್ನ ವೈರಿಗಳಿಗೆ ಬರಲಿ! ಅದರ ಅರ್ಥ ನಿನ್ನ ವಿರೋಧಿಗಳಿಗೆ ತಗಲಲಿ!",
			"20": "<sup>20</sup> ನೀವು ಕಂಡ ಮರವು ಬೆಳೆದು, ಬಲಗೊಂಡು ಆಕಾಶದ ತುದಿಯನ್ನು ಮುಟ್ಟುತ್ತಿತ್ತು.",
			"21": "<sup>21</sup> ಲೋಕದ ಜನರೆಲ್ಲರಿಗೆ ಕಾಣಿಸುತ್ತಿತ್ತು. ಅಂದವಾದ ಎಲೆಗಳನ್ನೂ ಹುಲುಸಾದ ಹಣ್ಣುಗಳನ್ನೂ ಬಿಡುತ್ತಿತ್ತು. ಎಲ್ಲಕ್ಕೂ ಆಹಾರ ಒದಗಿಸುತ್ತಿತ್ತು. ತನ್ನ ನೆರಳನ್ನು ವನ್ಯಮೃಗಗಳಿಗೆ ಆಶ್ರಯವಾಗಿಯೂ ತನ್ನ ಕೊಂಬೆಗಳನ್ನು ಆಕಾಶ ಪಕ್ಷಿಗಳಿಗೆ ನೆಲೆಯಾಗಿಯೂ ಮಾಡಿತ್ತು. ಕನಸಿನಲ್ಲಿ ನಿಮಗೆ ತೋರಿದ ಆ ಮರವು ನೀವೇ.",
			"22": "<sup>22</sup> “ರಾಜರೇ, ನೀವು ಬೆಳೆದು ಬಲಗೊಂಡಿದ್ದೀರಿ. ನಿಮ್ಮ ಕೀರ್ತಿ ವೃದ್ಧಿಯಾಗಿ ಗಗನಕ್ಕೇರಿದೆ. ನಿಮ್ಮ ಆಳ್ವಿಕೆ ಲೋಕದ ಕಟ್ಟಕಡೆಗೂ ವ್ಯಾಪಿಸಿದೆ.",
			"23": "<sup>23</sup> ಕಾವಲುಗಾರನಾದ ದೂತನು ಆಕಾಶದಿಂದ ಇಳಿದುಬಂದು: ‘ಈ ಮರವನ್ನು ಕಡಿದು ಹಾಳುಮಾಡಿ. ಅದರ ಬುಡದ ಮೋಟನ್ನು ನೆಲದಲ್ಲಿ ಉಳಿಸಿ ಹಿತ್ತಾಳೆ ಕಬ್ಬಿಣಗಳ ಪಟ್ಟಿಯನ್ನು ಅದಕ್ಕೆ ಬಿಗಿಯಿರಿ. ಅಡವಿಯ ಹುಲ್ಲು ಅದರ ಸುತ್ತಲು ಬೆಳೆದಿರಲಿ, ಆಕಾಶದ ಇಬ್ಬನಿ ಅದನ್ನು ತೋಯಿಸಲಿ. ಏಳು ವರ್ಷಕಾಲ ಕಳೆಯುವ ತನಕ ಕಾಡುಮೃಗಗಳ ಸಹವಾಸದ ಗತಿ ಬರಲಿ’ ಎಂದು ಸಾರುವುದನ್ನು ರಾಜರಾದ ನೀವೇ ನೋಡಿದ್ದೀರಿ.”",
			"24": "<sup>24</sup> “ರಾಜರೇ, ಕನಸಿನ ತಾತ್ಪರ್ಯ ಇದು: ನನ್ನೊಡೆಯರಾದ ಅರಸರಿಗೆ ಉಂಟಾಗಲಿರುವ ಪರಾತ್ಪರ ದೇವರ ತೀರ್ಪು ಹೀಗಿದೆ -",
			"25": "<sup>25</sup> ತಾವು ಮಾನವ ಸಮಾಜದಿಂದ ಬಹಿಷ್ಕೃತರಾಗುವಿರಿ. ಕಾಡುಮೃಗಗಳ ನಡುವೆ ವಾಸಮಾಡುವಿರಿ. ದನಕರುಗಳಂತೆ ಹುಲ್ಲು ಮೇಯುವ ಗತಿ ನಿಮ್ಮದಾಗುವುದು. ಆಕಾಶದ ಇಬ್ಬನಿ ನಿಮ್ಮನ್ನು ತೋಯಿಸುವುದು. ಪರಾತ್ಪರ ದೇವರಿಗೆ ಮಾನವರ ರಾಜ್ಯದ ಮೇಲೂ ಅಧಿಕಾರವಿದೆ. ಅದನ್ನು ತಮಗೆ ಬೇಕಾದವರಿಗೆ ಒಪ್ಪಿಸುತ್ತಾರೆ. ಇದು ತಮಗೆ ತಿಳಿದುಬರುವುದರೊಳಗೆ ಏಳು ವರ್ಷ ಕಳೆದಿರುತ್ತದೆ.",
			"26": "<sup>26</sup> ಆ ಮರದ ಬುಡದ ಮೋಟನ್ನು ಉಳಿಸಿರೆಂದು ಅಪ್ಪಣೆಯಾಗಿದೆ. ಇದರ ತಾತ್ಪರ್ಯವೇನೆಂದರೆ: ಪರಲೋಕದ ದೇವರಿಗೆ ಸರ್ವಾಧಿಕಾರ ಉಂಟೆಂಬುದನ್ನು ತಾವು ಅರಿತುಕೊಂಡ ನಂತರ ನಿಮ್ಮ ರಾಜ್ಯ ನಿಮಗೆ ಸ್ಥಿರವಾಗಿ ದೊರಕುವುದು.",
			"27": "<sup>27</sup> ಆದಕಾರಣ ಅರಸರೇ, ಈ ನನ್ನ ಬುದ್ಧಿಮಾತು ತಮಗೆ ಒಪ್ಪಿಗೆಯಾಗಲಿ: ಸದಾಚಾರದ ಮೂಲಕ ನಿಮ್ಮ ಪಾಪಗಳನ್ನು ಅಳಿಸಿಬಿಡಿ. ದೀನದಲಿತರಿಗೆ ದಯೆತೋರಿ. ನಿಮ್ಮ ಅಪರಾಧಗಳನ್ನು ನೀಗಿಸಿಕೊಳ್ಳಿ. ಇದರಿಂದ ಬಹುಶಃ ನಿಮ್ಮ ನೆಮ್ಮದಿಕಾಲ ಹೆಚ್ಚಾದೀತು,” ಎಂದು ಅರಿಕೆಮಾಡಿದನು.",
			"28": "<sup>28</sup> ಇದೆಲ್ಲವು ನೆಬೂಕದ್ನೆಚ್ಚರನೆಂಬ ರಾಜನಿಗೆ ಸಂಭವಿಸಿದವು.",
			"29": "<sup>29</sup> ಹನ್ನೆರಡು ತಿಂಗಳು ಕಳೆದ ನಂತರ ಅವನು ಒಂದು ದಿನ ಬಾಬಿಲೋನಿನ ಅರಮನೆಯ ಮೇಲ್ಗಡೆ ತಿರುಗಾಡುತ್ತಿದ್ದನು.",
			"30": "<sup>30</sup> ಆಗ ಅವನು, “ಹಾ, ಬಾಬಿಲೋನ್ ಎಂಥಾ ಮಹಾನಗರ! ಇಗೋ, ನನ್ನ ಶಕ್ತಿ ಸಾಮರ್ಥ್ಯದಿಂದ ನಾನು ಕಟ್ಟಿಸಿರುವ ರಾಜಭವನ! ನನ್ನ ಕೀರ್ತಿ ಪರಾಕ್ರಮವನ್ನು ಇದು ಪ್ರಕಟಿಸುತ್ತಿದೆ!” ಎಂದು ಕೊಚ್ಚಿಕೊಂಡನು.",
			"31": "<sup>31</sup> ಈ ಮಾತು ಅವನ ಬಾಯಿಂದ ಹೊರಡುತ್ತಿರುವಾಗಲೆ, “ಎಲೈ ರಾಜ ನೆಬೂಕದ್ನೆಚ್ಚರನೇ, ನಿನ್ನ ವಿಷಯವಾದ ಈ ದೈವೋಕ್ತಿಯನ್ನು ಕೇಳು -",
			"32": "<sup>32</sup> ‘ಈ ರಾಜ್ಯ ನಿನ್ನನ್ನು ಬಿಟ್ಟು ತೊಲಗಿದೆ. ನೀನು ಮಾನವ ಸಮಾಜದಿಂದ ಬಹುಷ್ಕೃತನಾಗುವೆ. ಕಾಡುಮೃಗಗಳ ನಡುವೆ ವಾಸಿಸುವೆ. ದನಗಳಂತೆ ಹುಲ್ಲು ಮೇಯುವ ಗತಿ ನಿನ್ನದಾಗುವುದು. ಪರಾತ್ಪರ ದೇವರಿಗೆ ನರಮಾನವರ ರಾಜ್ಯದ ಮೇಲೆ ಅಧಿಕಾರವಿದೆ. ಆ ರಾಜ್ಯವನ್ನು ತಮಗೆ ಬೇಕಾದವರಿಗೆ ಒಪ್ಪಿಸುತ್ತಾರೆ. ಇದನ್ನು ನೀನು ಗ್ರಹಿಸುವುದರೊಳಗೆ ಏಳು ವರ್ಷ ಕಳೆಯುವುದು,” ಎಂದು ದೈವವಾಣಿಯಾಯಿತು.”",
			"33": "<sup>33</sup> ಆ ನುಡಿ ಕೂಡಲೆ ನೆರವೇರಿತು. ನೆಬೂಕದ್ನೆಚ್ಚರನು ಸಮಾಜದಿಂದ ಬಹಿಷ್ಕೃತನಾದ. ದನಗಳಂತೆ ಹುಲ್ಲು ಮೇಯುತ್ತಾ ಆಕಾಶದ ಇಬ್ಬನಿಯಿಂದ ನೆನೆಯುತ್ತಾ ಇದ್ದ. ಅವನ ಕೂದಲು ಹದ್ದುಗಳ ರೆಕ್ಕೆಗಳಂತೆಯೂ, ಅವನ ಉಗುರು ಹಕ್ಕಿಗಳ ಉಗುರಿನಂತೆಯೂ ಬೆಳೆದವು.",
			"34": "<sup>34</sup> ಗುರುತಿಸಲಾಗಿದ್ದ ಕಾಲವು ಕಳೆಯಿತು. ನೆಬೂಕದ್ನೆಚ್ಚರನು, “ನಾನು ಪರಲೋಕದ ಕಡೆಗೆ ಕಣ್ಣೆತ್ತಿ ನೋಡಿದೆ. ನನ್ನ ಬುದ್ಧಿ ಮತ್ತೆ ನನಗೆ ಸ್ವಾಧೀನವಾಯಿತು. ಆಗ ನಾನು ಪರಾತ್ಪರ ದೇವರನ್ನು ಕೊಂಡಾಡಿದೆ. ಜೀವಸ್ವರೂಪರಾದ ಅವರಿಗೆ ಸ್ತುತಿಸ್ತೋತ್ರಗಳನ್ನು ಸಲ್ಲಿಸಿದೆ: ಆತನ ಆಳ್ವಿಕೆ ಶಾಶ್ವತ ಆತನ ರಾಜ್ಯ ತಲತಲಾಂತರಕ್ಕೂ ಸ್ಥಿರ.",
			"35": "<sup>35</sup> ಆತನ ದೃಷ್ಟಿಯಲ್ಲಿ ಭೂನಿವಾಸಿಗಳೆಲ್ಲರು ಶೂನ್ಯರು ಇಹದ ಜನರೂ ಪರದ ದೂತರೂ ಆತನಿಗೆ ಅಧೀನರು. ಆತನ ಶಕ್ತಿಯುತ ಹಸ್ತವನ್ನು ಯಾರಿಂದಲೂ ತಡೆಯಲಾಗದು. ‘ನೀನು ಮಾಡುತ್ತಿರುವುದೇನು?’ ಎಂದು ಯಾರೂ ಆತನನ್ನು ಪ್ರಶ್ನಿಸಲಾರರು.",
			"36": "<sup>36</sup> ಅದೇ ಸಮಯದಲ್ಲಿ ನನ್ನ ಬುದ್ಧಿ ನನಗೆ ಮತ್ತೆ ಸ್ವಾಧೀನವಾಯಿತು. ಘನತೆ ಗೌರವಗಳು ನನಗೆ ಮತ್ತೆ ಲಭಿಸಿದವು. ನನ್ನ ರಾಜ್ಯದ ಕೀರ್ತಿ ಬೆಳಗತೊಡಗಿತು. ನನ್ನ ಮಂತ್ರಿಗಳೂ ಪದಾಧಿಕಾರಿಗಳೂ ನನ್ನನ್ನು ಸ್ವಾಗತಿಸಲು ಬಂದರು. ರಾಜ್ಯದಲ್ಲಿ ನಾನು ನೆಲೆಗೊಂಡೆ. ಹಿಂದೆ ಇದ್ದುದಕ್ಕಿಂತಲೂ ಹೆಚ್ಚಿನ ಪ್ರತಿಭೆ ನನ್ನದಾಯಿತು.",
			"37": "<sup>37</sup> ನೆಬೂಕದ್ನೆಚ್ಚರನಾದ ನಾನು ಪರಲೋಕ ರಾಜನನ್ನು ಹೊಗಳಿ, ಕೊಂಡಾಡಿ, ಕೀರ್ತಿಸುತ್ತೇನೆ: ಆತನ ಕಾರ್ಯಗಳೆಲ್ಲವು ಸತ್ಯ, ಆತನ ಮಾರ್ಗಗಳೆಲ್ಲವು ನ್ಯಾಯ, ಗರ್ವಿಷ್ಠರನ್ನು ಆತ ತಗ್ಗಿಸಬಲ್ಲ."
		},
		"5": {
			"1": "<sup>1</sup> ಬೇಲ್ಶಚ್ಚರ ಎಂಬ ಅರಸನು ತನ್ನ ರಾಜ್ಯದ ಪ್ರಮುಖರಲ್ಲಿ ಸಾವಿರ ಮಂದಿಯನ್ನು ಸೇರಿಸಿ ಒಂದು ಔತಣವನ್ನು ಏರ್ಪಡಿಸಿದ. ಆ ಸಾವಿರ ಜನರ ಮುಂದೆ ಅವನೂ ದ್ರಾಕ್ಷಾರಸವನ್ನು ಕುಡಿದ.",
			"2": "<sup>2</sup> ಹಾಗೆ ಕುಡಿದುಕೊಂಡಿರುವಾಗ ತಾನೂ ತನ್ನ ಪ್ರಮುಖರೂ ಪತ್ನಿಯರೂ ಉಪಪತ್ನಿಯರೂ ಕುಡಿಯುವುದಕ್ಕಾಗಿ ತನ್ನ ತಂದೆ ನೆಬೂಕದ್ನೆಚ್ಚರನು ಜೆರುಸಲೇಮಿನ ದೇವಾಲಯದ ಗರ್ಭಗುಡಿಯಿಂದ ಸೂರೆಯಾಗಿ ತಂದ",
			"3": "<sup>3</sup> ಬಂಗಾರದ ಪೂಜಾ ಪಾತ್ರೆಗಳನ್ನು ತರಿಸಿದ. ರಾಜನೂ ಅವನ ಪ್ರಮುಖರೂ ಪತ್ನಿ - ಉಪಪತ್ನಿಯರೂ ಅವುಗಳಲ್ಲಿ ಕುಡಿದರು.",
			"4": "<sup>4</sup> ಕುಡಿದುಕೊಂಡೇ ಬಂಗಾರ, ಬೆಳ್ಳಿ, ಕಂಚು, ಕಬ್ಬಿಣ, ಮರ ಮತ್ತು ಕಲ್ಲಿನ ತಮ್ಮ ದೇವರುಗಳನ್ನು ಆರಾಧಿಸಿದರು.",
			"5": "<sup>5</sup> ಅದೇ ಸಮಯದಲ್ಲಿ ಒಬ್ಬ ಮನುಷ್ಯನ ಕೈಬೆರಳುಗಳು ಕಾಣಿಸಿಕೊಂಡು ದೀಪಸ್ತಂಭದ ಎದುರಿಗೆ ಅರಮನೆಯ ಸುಣ್ಣದ ಗೋಡೆಯ ಮೇಲೆ ಬರೆಯಲು ತೊಡಗಿದವು. ಬರೆಯುತ್ತಿದ್ದ ಆ ಕೈಯನ್ನು ರಾಜನು ನೋಡಿದನು.",
			"6": "<sup>6</sup> ಅದನ್ನು ಕಂಡದ್ದೆ ಅವನ ಮುಖ ಕಳೆಗುಂದಿತು, ಮನಸ್ಸು ಕಳವಳಗೊಂಡಿತು, ಸೊಂಟದ ಕೀಲು ಸಡಿಲಗೊಂಡಿತು, ಮೊಣಕಾಲುಗಳು ಒಂದಕ್ಕೊಂದು ಬಡಿದುಕೊಂಡವು.",
			"7": "<sup>7</sup> ಕೂಡಲೆ ರಾಜನು ಗಟ್ಟಿಯಾಗಿ ಕೂಗಿ ಮಂತ್ರವಾದಿ, ಪಂಡಿತ ಹಾಗು ಶಾಕುನಿಕರನ್ನು ಕರೆಯಿಸಿದನು. ಬಾಬಿಲೋನಿನ ಆ ವಿದ್ವಾಂಸರಿಗೆ, “ಯಾವನು ಈ ಬರಹವನ್ನು ಓದಿ ಇದರ ಅರ್ಥವನ್ನು ನನಗೆ ತಿಳಿಸುತ್ತಾನೋ ಅವನಿಗೆ ಕೆನ್ನೀಲಿ ರಾಜವಸ್ತ್ರವನ್ನು ತೊಡಿಸಿ, ಕೊರಳಿಗೆ ಚಿನ್ನದ ಹಾರವನ್ನು ಹಾಕಿಸಿ, ಅವನನ್ನು ರಾಜ್ಯದ ಮೂವರು ಮುಖ್ಯಾಧಿಕಾರಿಗಳಲ್ಲಿ ಒಬ್ಬನನ್ನಾಗಿ ನೇಮಿಸುವೆನು,” ಎಂದು ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ಆಸ್ಥಾನದ ವಿದ್ವಾಂಸರೆಲ್ಲರು ಬಂದು ಸೇರಿದರು. ಆದರೆ ಆ ಬರಹವನ್ನು ಓದುವುದಕ್ಕಾಗಲಿ, ಅದರ ಅರ್ಥವನ್ನು ರಾಜನಿಗೆ ತಿಳಿಸುವುದಕ್ಕಾಗಲಿ ಅವರಿಂದ ಆಗಲಿಲ್ಲ.",
			"9": "<sup>9</sup> ಆಗ ರಾಜ ಬೇಲ್ಶಚ್ಚರನು ಬಹಳ ಕಳವಳಗೊಂಡನು. ಅವನ ಮುಖ ಕಳೆಗುಂದಿತು. ಅವನ ಸಾಮಂತರು ದಿಕ್ಕುತೋಚದವರಾದರು.",
			"10": "<sup>10</sup> ರಾಜನೂ ಅವನ ಸಾಮಂತರೂ ಆಡಿದ ಮಾತು ರಾಣಿಗೆ ಮುಟ್ಟಿತು. ಆಕೆ ಔತಣ ಶಾಲೆಗೆ ಬಂದು, “ರಾಜರೇ, ಚಿರಂಜೀವಿಯಾಗಿರಿ! ನಿಮ್ಮ ಮನಸ್ಸು ಕಳವಳಗೊಳ್ಳದಿರಲಿ, ನಿಮ್ಮ ಮುಖ ಕಳೆಗುಂದದಿರಲಿ! ಪರಿಶುದ್ಧ ದೇವರ ಆತ್ಮ ನೆಲೆಸಿರುವ ಒಬ್ಬ ವ್ಯಕ್ತಿ ನಿಮ್ಮ ರಾಜ್ಯದಲ್ಲಿದ್ದಾನೆ.",
			"11": "<sup>11</sup> ನಿಮ್ಮ ತಂದೆಯ ಕಾಲದಲ್ಲಿ ಬುದ್ಧಿವಂತಿಕೆ, ತಿಳುವಳಿಕೆ, ದೇವರುಗಳಿಗೆ ಸಮಾನವಾದ ಜ್ಞಾನ ಅವನಲ್ಲಿ ಕಂಡುಬಂದುವು. ನಿಮ್ಮ ತಂದೆ ರಾಜ ನೆಬೂಕದ್ನೆಚ್ಚರನು, ಅವನನ್ನು ಜೋಯಿಸರಿಗೂ ಮಂತ್ರವಾದಿಗಳಿಗೂ ಪಂಡಿತರಿಗೂ ಶಾಕುನಿಕರಿಗೂ ಅಧ್ಯಕ್ಷನನ್ನಾಗಿ ನೇಮಿಸಿದ್ದರು.",
			"12": "<sup>12</sup> ಹೌದು, ‘ಬೇಲ್ತೆಶಚ್ಚರ’ನೆಂಬ ಹೆಸರನ್ನು ರಾಜನಿಂದ ಹೊಂದಿದ ಆ ದಾನಿಯೇಲನು ಪರಮ ಬುದ್ಧಿವಂತ, ಜ್ಞಾನಿ, ವಿವೇಕಿ, ಕನಸುಗಳ ಅರ್ಥವನ್ನು ತಿಳಿಸುವ ಜಾಣತನ, ಒಗಟು ಬಿಡಿಸುವ ಚಮತ್ಕಾರ, ಗುಂಜುಗಂಟು ಬಿಚ್ಚುವ ಚಾತುರ್ಯ ಅವನಲ್ಲಿ ತೋರಿಬಂದವು. ಆದ್ದರಿಂದಲೇ ಅವನನ್ನು ಅಧ್ಯಕ್ಷನನ್ನಾಗಿ ನೇಮಿಸಲಾಯಿತು. ದಾನಿಯೇಲನನ್ನು ಈಗಲೇ ಕರೆಯಿಸಿರಿ. ಆತ ಬರಹದ ಅರ್ಥವನ್ನು ವಿವರಿಸುವನು,” ಎಂದಳು.",
			"13": "<sup>13</sup> ಅಂತೆಯೇ, ದಾನಿಯೇಲನನ್ನು ರಾಜ ಸನ್ನಿಧಿಗೆ ಬರಮಾಡಲಾಯಿತು. ರಾಜನು ಅವನಿಗೆ: “ರಾಜನಾದ ನನ್ನ ತಂದೆ ಜುದೇಯದಿಂದ ಖೈದಿಯಾಗಿ ತಂದು ಸೆರೆಮಾಡಿದ ಯೆಹೂದ್ಯರಲ್ಲಿ ದಾನಿಯೇಲ್ ಎಂಬುವನು ನೀನೋ?",
			"14": "<sup>14</sup> ದೇವರ ಆತ್ಮ ನಿನ್ನಲ್ಲಿ ನೆಲೆಸಿದೆಯೆಂದು ನಾನು ಕೇಳಿದ್ದೇನೆ. ನಿನಗೆ ಪರಮಜ್ಞಾನ, ವಿವೇಕ, ಬುದ್ಧಿ, ತೇಜಸ್ಸು ಇದೆಯೆಂದು ನನಗೆ ತಿಳಿದುಬಂದಿದೆ.",
			"15": "<sup>15</sup> ಈಗ ಈ ಬರಹವನ್ನು ಓದಿ ಇದರ ಅರ್ಥವನ್ನು ನನಗೆ ತಿಳಿಸುವುದಕ್ಕಾಗಿ ವಿದ್ವಾಂಸರನ್ನೂ ಮಂತ್ರವಾದಿಗಳನ್ನೂ ಸಮ್ಮುಖಕ್ಕೆ ಬರಮಾಡಿದೆ. ಅವರು ಇದರ ಅರ್ಥವನ್ನು ವಿವರಿಸಲಾರದೆಹೋದರು.",
			"16": "<sup>16</sup> ನೀನು ಗೂಡಾರ್ಥಗಳನ್ನು ವಿವರಿಸಬಲ್ಲವನೂ ಗುಂಜುಗಂಟುಗಳನ್ನು ಬಿಚ್ಚಬಲ್ಲವನೂ ಎಂಬ ಸಮಾಚಾರ ನನಗೆ ಮುಟ್ಟಿದೆ. ಈ ಬರಹವನ್ನು ಓದಿ, ಇದರ ಅಭಿಪ್ರಾಯವನ್ನು ನನಗೆ ತಿಳಿಸಿದೆಯಾದರೆ ನಿನಗೆ ಕೆನ್ನೀಲಿ ರಾಜವಸ್ತ್ರವನ್ನು ಹೊದಿಸಿ, ಕೊರಳಿಗೆ ಚಿನ್ನದ ಹಾರವನ್ನು ಹಾಕಿಸಿ, ನಿನ್ನನ್ನು ರಾಜ್ಯದ ಮೂವರು ಮುಖ್ಯಾಧಿಕಾರಿಗಳಲ್ಲಿ ಒಬ್ಬನನ್ನಾಗಿ ನೇಮಿಸುವೆನು,” ಎಂದು ಹೇಳಿದನು.",
			"17": "<sup>17</sup> ಆಗ ದಾನಿಯೇಲನು ರಾಜನಿಗೆ, “ಅರಸರೇ, ನಿಮ್ಮ ದಾನಗಳು ನಿಮಗೇ ಇರಲಿ. ನಿಮ್ಮ ಬಹುಮಾನಗಳು ಮತ್ತೊಬ್ಬನಿಗೆ ದೊರಕಲಿ. ಆದರೆ ನಾನು ಈ ಬರಹವನ್ನು ಓದಿ ಅದರ ಅರ್ಥವನ್ನು ತಮಗೆ ತಿಳಿಸುವೆನು.",
			"18": "<sup>18</sup> ಪರಾತ್ಪರ ದೇವರು ನಿಮ್ಮ ತಂದೆಯಾದ ನೆಬೂಕದ್ನೆಚ್ಚರ್ ಅವರಿಗೆ ರಾಜ್ಯ ಮಹತ್ವವನ್ನು ಹಾಗೂ ಮಾನ ಸನ್ಮಾನಗಳನ್ನು ದಯಪಾಲಿಸಿದ್ದರು.",
			"19": "<sup>19</sup> ಇಂಥ ಮಹತ್ವ ವರದ ನಿಮಿತ್ತ ಸಕಲ ದೇಶ-ಕುಲ-ಭಾಷೆಗಳವರು ಅವರ ಮುಂದೆ ಭಯದಿಂದ ನಡುಗುತ್ತಿದ್ದರು. ತಮಗೆ ಇಷ್ಟಬಂದ ಹಾಗೆ ಒಬ್ಬನನ್ನು ಬದುಕಿಸಿದರು, ಇನ್ನೊಬ್ಬನನ್ನು ಕೊಲ್ಲಿಸಿದರು. ಮನಸ್ಸಿಗೆ ಬಂದಹಾಗೆ ಒಬ್ಬನನ್ನು ಮೇಲೇರಿಸಿದರು, ಇನ್ನೊಬ್ಬನನ್ನು ಕೆಳಕ್ಕಿಳಿಸಿದರು.",
			"20": "<sup>20</sup> ಹಾಗೆ ಅವರ ಎದೆ ಗರ್ವದಿಂದ ಉಬ್ಬಿಹೋಯಿತು. ಮನಸ್ಸಿಗೆ ಸೊಕ್ಕೇರಿಹೋಯಿತು. ಅವರನ್ನೂ ರಾಜಾಸ್ಥಾನದಿಂದ ತಳ್ಳಲಾಯಿತು. ಮಾನ ಕಳೆದುಹೋಯಿತು.",
			"21": "<sup>21</sup> ಸಮಾಜದಿಂದ ಬಹಿಷ್ಕೃತರಾಗಿ, ಮೃಗಬುದ್ಧಿಯುಳ್ಳವರಾಗಿ, ಕಾಡುಕತ್ತೆಗಳ ನಡುವೆ ವಾಸಮಾಡಬೇಕಾಯಿತು. ಪರಾತ್ಪರ ದೇವರಿಗೆ ನರಮಾನವರ ರಾಜ್ಯದ ಮೇಲೆ ಅಧಿಕಾರವಿದೆ. ಅದನ್ನು ತಮಗೆ ಇಷ್ಟಬಂದವರಿಗೆ ಒಪ್ಪಿಸುತ್ತಾರೆ ಎಂಬುದನ್ನು ಅವರು ಗ್ರಹಿಸುವ ತನಕ ದನಗಳಂತೆ ಹುಲ್ಲುಮೇಯುವ ಗತಿ ಅವರದಾಯಿತು. ಆಕಾಶದ ಇಬ್ಬನಿ ಅವರ ಮೈಯನ್ನು ತೋಯಿಸುತ್ತಿತ್ತು.”",
			"22": "<sup>22</sup> “ಬೇಲ್ಶಚ್ಚರರೇ, ಅವರ ಕುಮಾರರಾದ ತಾವು ಇದನ್ನೆಲ್ಲ ತಿಳಿದುಕೊಂಡಿದ್ದರೂ ದೀನ ಮನಸ್ಕರಾಗದೆ ಇದ್ದೀರಿ.",
			"23": "<sup>23</sup> ಪರಲೋಕದ ಒಡೆಯನಿಗೆ ವಿರುದ್ಧವಾಗಿ ನಿಮ್ಮನ್ನೆ ಹೆಚ್ಚಿಸಿಕೊಂಡಿದ್ದೀರಿ. ದೇವಾಲಯದಿಂದ ಪವಿತ್ರ ಪೂಜಾಪಾತ್ರೆಗಳನ್ನು ಇಲ್ಲಿಗೆ ತಂದಿದ್ದೀರಿ. ನೀವೂ ನಿಮ್ಮ ರಾಜ್ಯದ ಪ್ರಮುಖರೂ ಪತ್ನಿ-ಉಪಪತ್ನಿಯರೂ ಅವುಗಳಲ್ಲಿ ದ್ರಾಕ್ಷಾರಸವನ್ನೂ ಕುಡಿದಿದ್ದೀರಿ. ಬುದ್ಧಿ, ಕಣ್ಣು, ಕಿವಿ ಇಲ್ಲದೆ ಬೆಳ್ಳಿಬಂಗಾರಗಳ, ಕಂಚುಕಬ್ಬಿಣಗಳ, ಮರ ಕಲ್ಲುಗಳ ದೇವರುಗಳನ್ನೂ ಆರಾಧಿಸಿದ್ದೀರಿ. ನಿಮ್ಮ ಪ್ರಾಣ ಯಾರ ಕೈಯಲ್ಲಿದೆಯೋ ನಿಮ್ಮ ಸ್ಥಿತಿಗತಿಗಳು ಯಾರ ಅಧೀನದಲ್ಲಿದೆಯೋ ಆ ದೇವರನ್ನು ಮಾತ್ರ ಗೌರವಿಸದೆ ಇದ್ದೀರಿ.",
			"24": "<sup>24</sup> ಹೀಗಿರಲು, ಆ ಕೈಬೆರಳು ದೇವರ ಸನ್ನಿಧಿಯಿಂದ ಬಂದು ಬರೆದಿದೆ.”",
			"25": "<sup>25</sup> “ಬರೆದಿರುವ ಮಾತುಗಳು ಇವು: ಮೆನೇ, ಮೆನೇ, ತೆಕೇಲ್, ಉಫರ್ಸಿನ್.”",
			"26": "<sup>26</sup> ಇದರ ಅರ್ಥ ಹೀಗಿದೆ : “ಮೆನೇ” ಎಂದರೆ, ದೇವರು ನಿನ್ನ ಆಳ್ವಿಕೆಯ ಕಾಲಾವಧಿಯನ್ನು ಲೆಕ್ಕಿಸಿ ಕೊನೆಗಾಣಿಸಿದ್ದಾರೆ.",
			"27": "<sup>27</sup> 'ತೆಕೇಲ್’ ಎಂದರೆ ನಿನ್ನನ್ನು ತಕ್ಕಡಿಯಲ್ಲಿ ಹಾಕಿ ನೋಡಲು ನಿನ್ನ ತೂಕ ಕಡಿಮೆಯಾಗಿ ಕಂಡುಬಂದಿದೆ.",
			"28": "<sup>28</sup> ‘ಫರ್ಸಿನ್’ ಎಂದರೆ ನಿನ್ನ ರಾಜ್ಯವನ್ನು ವಿಭಾಗಮಾಡಿ ಮೇದ್ಯರಿಗೂ ಪರ್ಷಿಯದವರಿಗೂ ಕೊಡಲಾಗಿದೆ,” ಎಂದು ಅರಿಕೆಮಾಡಿದನು.",
			"29": "<sup>29</sup> ತಕ್ಷಣವೇ ಬೇಲ್ಶಚ್ಚರನ ಆಜ್ಞೆಯ ಮೇರೆಗೆ ದಾನಿಯೇಲನಿಗೆ ಕೆನ್ನೀಲಿ ರಾಜವಸ್ತ್ರವನ್ನು ಹೊದಿಸಿದರು. ಅವನ ಕೊರಳಿಗೆ ಚಿನ್ನದ ಹಾರವನ್ನು ಹಾಕಿದರು. ರಾಜ್ಯದ ಮೂವರು ಮುಖ್ಯಾಧಿಕಾರಿಗಳಲ್ಲಿ ದಾನಿಯೇಲನು ಒಬ್ಬನಾದನು ಎಂದು ಪ್ರಕಟಿಸಿದರು.",
			"30": "<sup>30</sup> ಅದೇ ರಾತ್ರಿ ಬಾಬಿಲೋನಿನ ರಾಜ ಬೇಲ್ಶಚ್ಚರನು ಕೊಲ್ಲಲ್ಪಟ್ಟನು.",
			"31": "<sup>31</sup> ಸುಮಾರು 62 ವಯಸ್ಸಿನ ಮೇದ್ಯನಾದ ದಾರ್ಯಾವೆಷನು ರಾಜ್ಯವನ್ನು ವಹಿಸಿಕೊಂಡನು."
		},
		"6": {
			"1": "<sup>1</sup> ರಾಜ್ಯಾಡಳಿತ ನಿರ್ವಹಣೆಗಾಗಿ ದಾರ್ಯಾವೆಷನು ನೂರಿಪ್ಪತ್ತು ಮಂದಿ ಪ್ರಾಂತ್ಯಾಧಿಪತಿಗಳನ್ನು ಆಯಾಯ ಪ್ರಾಂತ್ಯಗಳ ಮೇಲೆ ನೇಮಿಸಲು ತೀರ್ಮಾನಿಸಿದನು.",
			"2": "<sup>2</sup> ಇವರ ಮೇಲ್ವಿಚಾರಣೆಗಾಗಿಯೂ ತನ್ನ ಹಿತಾಸಕ್ತಿಯ ದೃಷ್ಟಿಯಿಂದಲೂ ಮೂವರು ಮುಖ್ಯಾಧಿಕಾರಿಗಳನ್ನು ನೇಮಿಸಲು ನಿಶ್ಚಯಿಸಿದನು. ಈ ಮೂವರು ಮುಖ್ಯಸ್ಥರಲ್ಲಿ ದಾನಿಯೇಲನು ಒಬ್ಬನು.",
			"3": "<sup>3</sup> ಈತ ಪರಮಜ್ಞಾನಿ. ಮಿಕ್ಕ ಮುಖ್ಯಾಧಿಕಾರಿಗಳಿಗಿಂತಲೂ ಅಧಿಪತಿಗಳಿಗಿಂತಲೂ ಹೆಚ್ಚು ಸಮರ್ಥನು. ಇವನನ್ನು ರಾಜನು ಸಮಸ್ತ ರಾಜ್ಯದ ಮೇಲ್ವಿಚಾರಕನನ್ನಾಗಿ ನೇಮಿಸಲು ಉದ್ದೇಶಿಸಿದ್ದನು.",
			"4": "<sup>4</sup> ಹೀಗಿರಲು ಮುಖ್ಯಾಧಿಕಾರಿಗಳು ಮತ್ತು ಪ್ರಾಂತ್ಯಾಧಿಪತಿಗಳು ರಾಜ್ಯಾಡಳಿತದ ವಿಷಯವಾಗಿ ದಾನಿಯೇಲನ ಮೇಲೆ ತಪ್ಪುಹೊರಿಸುವುದಕ್ಕೆ ಸಂದರ್ಭ ಹುಡುಕುತ್ತಿದ್ದರು. ಆದರೆ ತಪ್ಪುಹೊರಿಸುವ ಯಾವ ಸಂದರ್ಭವೂ ದೊರಕಲಿಲ್ಲ. ಅವನಲ್ಲಿ ಯಾವ ತಪ್ಪನ್ನೂ ಕಾಣಲಾರದೆ ಹೋದರು. ಅವನು ಅಷ್ಟು ನಂಬಿಕಸ್ತನಾಗಿದ್ದನು! ಅವನಲ್ಲಿ ಯಾವ ಅಕ್ರಮವಾಗಲಿ, ಆಲಸ್ಯವಾಗಲಿ ಕಾಣಸಿಗಲಿಲ್ಲ.",
			"5": "<sup>5</sup> ಆಗ ಆ ಅಧಿಕಾರಿಗಳು, “ಈ ದಾನಿಯೇಲನ ಮೇಲೆ ತಪ್ಪುಹೊರಿಸಬೇಕಾದರೆ ಇವನ ದೇವರ ಹಾಗೂ ಧರ್ಮದ ಮೂಲಕವೇ ಹೊರತು ಇನ್ನಾವುದರಲ್ಲೂ ನಮಗೆ ಅವಕಾಶ ದೊರಕದು,” ಎಂದುಕೊಂಡರು.",
			"6": "<sup>6</sup> ಬಳಿಕ ಆ ಮುಖ್ಯಾಧಿಕಾರಿಗಳೂ ಪ್ರಾಂತ್ಯಾಧಿಪತಿಗಳೂ ರಾಜನ ಸಮ್ಮುಖದಲ್ಲಿ ಕೂಡಿಬಂದು ಅವನಿಗೆ, “ದಾರ್ಯಾವೆಷ ರಾಜರೇ, ಚಿರಂಜೀವಿಯಾಗಿರಿ!",
			"7": "<sup>7</sup> ರಾಜ್ಯದ ಸಕಲ ಮುಖ್ಯಾಧಿಕಾರಿ, ನಾಯಕ, ಪ್ರಾಂತ್ಯಾಧಿಪತಿ, ಮಂತ್ರಿ, ಸಂಸ್ಥಾನಾಧ್ಯಕ್ಷರು ಇವರೆಲ್ಲರ ಅಭಿಪ್ರಾಯದ ಪ್ರಕಾರ ತಾವು ಒಂದು ಶಾಸನವನ್ನು ಹೊರಡಿಸಬೇಕು. ಯಾವನೂ ಮೂವತ್ತು ದಿನಗಳ ತನಕ ರಾಜರಾದ ತಮಗೆ ಹೊರತು ಬೇರೆ ಯಾವ ದೇವರಿಗಾಗಲಿ, ಮನುಷ್ಯನಿಗಾಗಲಿ ಪ್ರಾರ್ಥನೆ ಮಾಡಬಾರದು; ಮಾಡಿದರೆ ಸಿಂಹಗಳ ಗವಿಗೆ ಹಾಕಲಾಗುವುದು ಎಂದು ರಾಜಾಜ್ಞಾರೂಪವಾಗಿ ನಿಷೇಧಿಸುವುದು ಒಳ್ಳೆಯದು.",
			"8": "<sup>8</sup> ರಾಜರೇ, ಈ ಶಾಸನಕ್ಕೆ ನೀವು ಇದೀಗಲೇ ಹಸ್ತಾಕ್ಷರಗಳಿಂದ ರುಜುಹಾಕಬೇಕು. ಆಗ ಅದು ಮೇದ್ಯದ ಮತ್ತು ಪರ್ಷಿಯದ ಧರ್ಮವಿಧಿಗಳಂತೆ ಎಂದಿಗೂ ರದ್ದಾಗದೆ ಊರ್ಜಿತವಾಗುವುದು,” ಎಂದು ವಿನಂತಿಸಿದರು.",
			"9": "<sup>9</sup> ಅಂತೆಯೇ ರಾಜನಾದ ದಾರ್ಯಾವೆಷನು ರುಜುಹಾಕಿದನು.",
			"10": "<sup>10</sup> ಶಾಸನಕ್ಕೆ ರುಜುವಾದ ವಿಷಯ ದಾನಿಯೇಲನಿಗೆ ತಿಳಿಯಿತು. ಆತ ತನ್ನ ಮನೆಗೆ ಹೊರಟುಹೋದನು. ಅಲ್ಲಿ ತನ್ನ ಮಹಡಿಯ ಕೊಠಡಿಯಲ್ಲಿನ ಕಿಟಕಿಗಳು ಜೆರುಸಲೇಮಿನ ಕಡೆಗೆ ತೆರೆದಿದ್ದವು. ವಾಡಿಕೆಯ ಪ್ರಕಾರ ದಿನಕ್ಕೆ ಮೂರಾವರ್ತಿ ಅಲ್ಲಿ ತನ್ನ ದೇವರಿಗೆ ಮೊಣಕಾಲೂರಿ ಪ್ರಾರ್ಥಿಸಿ ಸ್ತೋತ್ರ ಸಲ್ಲಿಸುತ್ತಿದ್ದನು.",
			"11": "<sup>11</sup> ದಾನಿಯೇಲನ ಆ ವಿರೋಧಿಗಳು ಗುಂಪಾಗಿ ಕೂಡಿಬಂದರು. ಆತ ತನ್ನ ದೇವರಿಗೆ ಅಭಿಮುಖವಾಗಿ ಪ್ರಾರ್ಥಿಸಿ ವಿಜ್ಞಾಪಿಸುವುದನ್ನು ಕಂಡರು.",
			"12": "<sup>12</sup> ಕೂಡಲೆ ಆಸ್ಥಾನಕ್ಕೆ ಬಂದು ರಾಜಾಜ್ಞೆಯ ಪ್ರಸ್ತಾಪವನ್ನೆತ್ತಿ, “ರಾಜರೇ, ಯಾರೂ ಮೂವತ್ತು ದಿನಗಳ ತನಕ ತಮಗೆ ಹೊರತು ಬೇರೆ ಯಾವ ದೇವರಿಗಾಗಲಿ ಮನುಷ್ಯನಿಗಾಗಲಿ ಪ್ರಾರ್ಥನೆ ಮಾಡಕೂಡದು. ಮಾಡಿದರೆ ಅಂಥವನನ್ನು ಸಿಂಹಗಳ ಗವಿಯಲ್ಲಿ ಹಾಕಲಾಗುವುದು ಎಂಬ ಶಾಸನಕ್ಕೆ ನೀವು ರುಜುಹಾಕಿದಿರಲ್ಲವೆ?” ಎಂದು ಕೇಳಿದರು. ಅದಕ್ಕೆ ರಾಜನು, “ಹೌದು, ಮೇದ್ಯರ ಹಾಗು ಪರ್ಷಿಯರ ಧರ್ಮವಿಧಿಗಳಂತೆ ಅದು ಸ್ಥಿರವಾದ ನಿಬಂಧನೆ,” ಎಂದು ಉತ್ತರಕೊಟ್ಟನು.",
			"13": "<sup>13</sup> ಆಗ ಅವರು ರಾಜಸನ್ನಿಧಿಯಲ್ಲಿ, “ರಾಜರೇ, ಜುದೇಯದಿಂದ ಸೆರೆಯಾಳುಗಳಾಗಿ ತಂದವರಲ್ಲಿ ಒಬ್ಬನಾದ ಆ ದಾನಿಯೇಲನು ನಿಮ್ಮನ್ನಾಗಲಿ, ನಿಮ್ಮ ಹಸ್ತಾಕ್ಷರದ ರುಜುವಿರುವ ಶಾಸನವನ್ನಾಗಲಿ ಮಾನ್ಯಮಾಡುತ್ತಿಲ್ಲ.“ ಬದಲಿಗೆ ದಿನಕ್ಕೆ ಮೂರಾವರ್ತಿ ಪ್ರಾರ್ಥನೆ ಮಾಡುತ್ತಿದ್ದಾನೆ,” ಎಂದು ದೂರಿತ್ತರು.",
			"14": "<sup>14</sup> ಆದರೆ ರಾಜನು ಈ ಮಾತುಗಳನ್ನು ಕೇಳಿ ಬಹಳ ವ್ಯಸನಗೊಂಡನು. ದಾನಿಯೇಲನನ್ನು ಈ ಇಕ್ಕಟ್ಟಿನಿಂದ ತಪ್ಪಿಸಲು ಮನಸ್ಸು ಮಾಡಿಕೊಂಡನು. ಆದರೆ ಹೇಗೆ ರಕ್ಷಿಸುವುದೆಂದು ಸೂರ್ಯಾಸ್ತಮದವರೆಗೂ ಆಲೋಚಿಸುತ್ತಾ ಇದ್ದನು.",
			"15": "<sup>15</sup> ಇದನ್ನು ಅರಿತುಕೊಂಡ ಆ ಅಧಿಕಾರಿಗಳು ರಾಜಸನ್ನಿಧಿಗೆ ಮತ್ತೆ ಕೂಡಿಬಂದು, “ಪ್ರಭೂ, ರಾಜರು ವಿಧಿಸಿದ ಯಾವ ನಿಬಂಧನೆಯಾಗಲಿ, ಶಾಸನವಾಗಲಿ ರದ್ದಾಗಬಾರದು ಎಂಬ ಧರ್ಮಸೂತ್ರ ಮೇದ್ಯರಲ್ಲೂ ಪರ್ಷಿಯರಲ್ಲೂ ಉಂಟೆಂಬುದು ತಮಗೆ ತಿಳಿದಿರಬೇಕು,” ಎಂದರು.",
			"16": "<sup>16</sup> ಈ ಕಾರಣ ರಾಜನು ಅಪ್ಪಣೆ ಕೊಡಲೇಬೇಕಾಯಿತು. ಕೂಡಲೆ ಅವರು ದಾನಿಯೇಲನನ್ನು ತಂದು ಸಿಂಹಗಳ ಗವಿಯಲ್ಲಿ ಹಾಕಿಯೇಬಿಟ್ಟರು. ಆಗ ರಾಜನು ದಾನಿಯೇಲನಿಗೆ, “ನೀನು ದಿನನಿತ್ಯವೂ ಆರಾಧಿಸುವ ದೇವರು ನಿನ್ನನ್ನು ಉದ್ಧರಿಸಲಿ!” ಎಂದು ಹರಸಿದನು.",
			"17": "<sup>17</sup> ಆ ಅಧಿಕಾರಿಗಳಾದರೋ ಒಂದು ಬಂಡೆಯನ್ನು ತಂದು ಆ ಗವಿಯ ಬಾಯನ್ನು ಮುಚ್ಚಿದರು. ದಾನಿಯೇಲನ ಈ ಗತಿಯನ್ನು ಯಾರೂ ಬದಲಾಯಿಸಲಾಗದಂತೆ ರಾಜನ ಮತ್ತು ಅವನ ಮುಖಂಡರ ಮುದ್ರೆಯನ್ನು ಅದಕ್ಕೆ ಒತ್ತಿ ಮೊಹರು ಮಾಡಲಾಯಿತು.",
			"18": "<sup>18</sup> ಬಳಿಕ ರಾಜನು ಅರಮನೆಗೆ ಹಿಂದಿರುಗಿದನು. ಆ ರಾತ್ರಿಯೆಲ್ಲಾ ಉಪವಾಸವಿದ್ದನು. ವಾದ್ಯ ವಿನೋದಾವಳಿ ಯಾವುದನ್ನೂ ಹತ್ತಿರಬರಲು ಬಿಡಲಿಲ್ಲ. ನಿದ್ರೆ ಅವನ ಕಣ್ಣುಗಳಿಂದ ಮಾಯವಾಯಿತು.",
			"19": "<sup>19</sup> ಮಾರನೆಯ ದಿನ ಬೆಳಿಗ್ಗೆ ರಾಜನು ಉದಯದಲ್ಲೇ ಎದ್ದು ಸಿಂಹಗಳ ಗವಿಯ ಬಳಿಗೆ ವೇಗವಾಗಿ ಹೋದನು.",
			"20": "<sup>20</sup> ದಾನಿಯೇಲನಿದ್ದ ಗವಿಯ ಸಮೀಪಕ್ಕೆ ಬಂದು, ದುಃಖ ಧ್ವನಿಯಿಂದ ದಾನಿಯೇಲನನ್ನು ಕೂಗಿ, “ದಾನಿಯೇಲನೇ, ಜೀವಸ್ವರೂಪನಾದ ದೇವರ ದಾಸನೇ, ನೀನು ದಿನನಿತ್ಯವೂ ಆರಾಧಿಸುವ ನಿನ್ನ ದೇವರಿಗೆ ನಿನ್ನನ್ನು ಸಿಂಹಗಳಿಂದ ಉದ್ಧರಿಸಲು ಸಾಧ್ಯವಾಯಿತೋ?” ಎಂದು ಕೇಳಿದನು.",
			"21": "<sup>21</sup> ಆಗ ದಾನಿಯೇಲನು ರಾಜನಿಗೆ, “ಅರಸರೇ, ಚಿರಂಜೀವಿಯಾಗಿರಿ!",
			"22": "<sup>22</sup> ನನ್ನ ದೇವರು ತಮ್ಮ ದೂತನನ್ನು ಕಳಿಸಿ, ಸಿಂಹಗಳ ಬಾಯನ್ನು ಬಂಧಿಸಿದರು. ಅವುಗಳಿಂದ ನನಗೆ ಯಾವ ಹಾನಿಯೂ ಆಗಲಿಲ್ಲ. ಏಕೆಂದರೆ ಆ ದೇವರ ದೃಷ್ಟಿಯಲ್ಲಿ ನಾನು ನಿರ್ಮಲನು. ರಾಜರಾದ ತಮಗೂ ನಾನು ಯಾವ ದ್ರೋಹವನ್ನೂ ಮಾಡಲಿಲ್ಲ,” ಎಂದು ಹೇಳಿದನು.",
			"23": "<sup>23</sup> ರಾಜನು ಇದನ್ನು ಕೇಳಿ ಬಹಳ ಸಂತೋಷಪಟ್ಟನು. ದಾನಿಯೇಲನನ್ನು ಗವಿಯೊಳಗಿಂದ ಮೇಲಕ್ಕೆತ್ತಬೇಕೆಂದು ಅಪ್ಪಣೆಮಾಡಿದನು. ಅಂತೆಯೇ ಅವನನ್ನು ಮೇಲಕ್ಕೆತ್ತಲಾಯಿತು. ಆತನು ತನ್ನ ದೇವರಲ್ಲಿ ದೃಢವಾದ ವಿಶ್ವಾಸವಿಟ್ಟ ಕಾರಣ ಅವನಿಗೆ ಯಾವ ಹಾನಿಯೂ ಆಗಿರಲಿಲ್ಲ.",
			"24": "<sup>24</sup> ಅನಂತರ ರಾಜನು ಆಜ್ಞಾಪಿಸಲು ದಾನಿಯೇಲನ ಮೇಲೆ ದೂರು ಹೊರಿಸಿದವರನ್ನು ಮಡದಿಮಕ್ಕಳ ಸಮೇತ ತಂದು ಸಿಂಹಗಳ ಗವಿಯಲ್ಲಿ ಹಾಕಿಬಿಟ್ಟರು. ಅವರು ಗವಿಯ ತಳಮುಟ್ಟುವುದರೊಳಗೆ ಸಿಂಹಗಳು ಅವರ ಮೇಲೆ ಹಾರಿ ಅವರ ಎಲುಬುಗಳನ್ನೆಲ್ಲ ಚೂರುಚೂರು ಮಾಡಿದವು.",
			"25": "<sup>25</sup> ಇದಾದ ಮೇಲೆ ರಾಜ ದಾರ್ಯಾವೆಷನು ಜಗದಲ್ಲಿ ವಾಸಿಸುವ ಸಕಲ ದೇಶ-ಕುಲ-ಭಾಷೆಗಳವರಿಗೆ ಈ ಪ್ರಸಿದ್ಧ ಪತ್ರವನ್ನು ಬರೆಯಿಸಿದನು: ನಿಮಗೆ ಹೆಚ್ಚೆಚ್ಚು ಸುಖಶಾಂತಿ ಲಭಿಸಲಿ!”",
			"26": "<sup>26</sup> ನನ್ನ ರಾಜ್ಯದ ಪ್ರಜೆಗಳೆಲ್ಲರಿಗೆ ನಾನು ಆಜ್ಞಾಪಿಸುವುದೇನೆಂದರೆ: ನೀವೆಲ್ಲರು ದಾನಿಯೇಲನ ದೇವರ ಮುಂದೆ ಭಯಭಕ್ತಿಯಿಂದ ನಡೆದುಕೊಳ್ಳತಕ್ಕದ್ದು. ಏಕೆಂದರೆ ಆತನೇ ಜೀವಸ್ವರೂಪನಾದ ಸನಾತನ ದೇವರು! ಆತನ ರಾಜ್ಯ ಎಂದೆಂದಿಗೂ ಅಳಿಯದು ಆತನ ಆಳ್ವಿಕೆ ಶಾಶ್ವತವಾದುದು!",
			"27": "<sup>27</sup> ಆತ ಉದ್ಧರಿಸುವಂಥವನು, ರಕ್ಷಿಸುವಂಥವನು ಭೂಮ್ಯಾಕಾಶಗಳಲ್ಲಿ ಅದ್ಭುತ ಮಹತ್ವಗಳನ್ನು ನಡೆಸುವಂಥವನು! ಆತನೇ ದಾನಿಯೇಲನನ್ನು ಸಿಂಹಗಳ ಬಾಯಿಂದ ತಪ್ಪಿಸಿದವನು!",
			"28": "<sup>28</sup> ಹೀಗೆ ಈ ದಾನಿಯೇಲನು ದಾರ್ಯಾವೆಷನ ಆಳ್ವಿಕೆಯಲ್ಲೂ ಪರ್ಷಿಯನಾದ ಸೈರೆಷನ ಆಳ್ವಿಕೆಯಲ್ಲೂ ಪ್ರವರ್ಧಿಸುತ್ತಾ ಬಾಳಿದನು."
		},
		"7": {
			"1": "<sup>1</sup> ಬಾಬಿಲೋನಿನ ಅರಸನಾದ ಬೇಲ್ಶಚ್ಚರನ ಆಳ್ವಿಕೆಯ ಮೊದಲನೆಯ ವರ್ಷದಲ್ಲಿ ದಾನಿಯೇಲನು ಹಾಸಿಗೆಯ ಮೇಲೆ ಮಲಗಿದ್ದಾಗ ಒಂದು ಕನಸು ಕಂಡನು. ಅವನ ಮನಸ್ಸಿಗೆ ಒಂದು ದೃಶ್ಯ ಕಾಣಿಸಿತು. ಕೂಡಲೆ ಅವನು ಆ ಕನಸನ್ನೂ ಅದರ ಮುಖ್ಯಾಂಶಗಳನ್ನೂ ಬರೆದಿಟ್ಟನು.",
			"2": "<sup>2</sup> ಅದರ ಸಾರಾಂಶ ಇದು: ನಾನು ರಾತ್ರಿ ಕಂಡ ಕನಸಿನಲ್ಲಿ ನಾಲ್ಕು ದಿಕ್ಕುಗಳಿಂದಲೂ ಗಾಳಿ ಮಹಾಸಾಗರದ ಮೇಲೆ ರಭಸವಾಗಿ ಬೀಸಿ ಬಡಿಯುತ್ತಿತ್ತು.",
			"3": "<sup>3</sup> ವಿಲಕ್ಷಣವಾದ ನಾಲ್ಕು ದೊಡ್ಡ ಮೃಗಗಳು ಆ ಸಾಗರದಿಂದ ಹೊರಗೆ ಬಂದವು.",
			"4": "<sup>4</sup> ಮೊದಲು ಕಾಣಿಸಿದ ಮೃಗ ಸಿಂಹದ ಹಾಗಿತ್ತು. ಆದರೆ ಅದಕ್ಕೆ ಹದ್ದಿನಂಥ ರೆಕ್ಕೆಗಳಿದ್ದವು. ನಾನು ನೋಡುತ್ತಿರುವಾಗಲೆ ಆ ರೆಕ್ಕೆಗಳು ಕೀಳಲ್ಪಟ್ಟವು. ಆ ಮೃಗವನ್ನು ನೆಲದಿಂದ ಮೇಲಕ್ಕೆತ್ತಿ ಮನುಷ್ಯನ ಹಾಗೆ ಎರಡು ಕಾಲುಗಳ ಮೇಲೆ ನಿಲ್ಲಿಸಲಾಯಿತು. ಅದಕ್ಕೆ ಮನುಷ್ಯನ ಹೃದಯ ಕೊಡಲಾಯಿತು.",
			"5": "<sup>5</sup> ಬಳಿಕ ಎರಡನೆಯ ಇನ್ನೊಂದು ಮೃಗವನ್ನು ಕಂಡೆ. ಅದು ಕರಡಿಯ ಹಾಗಿತ್ತು. ಒಂದು ಹೆಗಲನ್ನು ಮೇಲಕ್ಕೆ ಎತ್ತಿಕೊಂಡಿತ್ತು. ತನ್ನ ಬಾಯಲ್ಲಿನ ಹಲ್ಲುಗಳ ನಡುವೆ ಮೂರು ಪಕ್ಕೆಲುಬುಗಳನ್ನು ಕಚ್ಚಿಕೊಂಡಿತ್ತು. “ನೀನೆದ್ದು ಬೇಕಾದಷ್ಟು ಮಾಂಸವನ್ನು ತಿನ್ನು,” ಎಂದು ಅದಕ್ಕೆ ಹೇಳಲಾಯಿತು.",
			"6": "<sup>6</sup> ಅನಂತರ ನಾನು ನೋಡುತ್ತಿರುವಾಗಲೆ ಚಿರತೆಯ ಹಾಗಿದ್ದ ಮತ್ತೊಂದು ಮೃಗ ಕಾಣಿಸಿತು. ಅದರ ಪಕ್ಕಗಳಲ್ಲಿ ಪಕ್ಷಿಯ ರೆಕ್ಕೆಗಳಂತಿರುವ ನಾಲ್ಕು ರೆಕ್ಕೆಗಳಿದ್ದವು. ಆ ಮೃಗಕ್ಕೆ ನಾಲ್ಕು ತಲೆಗಳಿದ್ದವು. ಅದಕ್ಕೆ ದೊರೆತನ ಕೊಡಲಾಯಿತು.",
			"7": "<sup>7</sup> ಇದಾದ ಬಳಿಕ ಆ ರಾತ್ರಿ ಕನಸಿನಲ್ಲಿ ಕಾಣಿಸಿಕೊಂಡ ನಾಲ್ಕನೇ ಮೃಗ ಭಯಂಕರವಾಗಿತ್ತು. ಹೆದರಿಸುವಂಥದಾಗಿತ್ತು. ಅಧಿಕ ಬಲವುಳ್ಳದ್ದಾಗಿತ್ತು. ಅದಕ್ಕೆ ಕಬ್ಬಿಣದ ದೊಡ್ಡ ಹಲ್ಲುಗಳಿದ್ದವು. ಅದು ತನ್ನ ಬಲಿಯನ್ನು ತುಂಡುತುಂಡಾಗಿಸಿ ಕಬಳಿಸುತ್ತಿತ್ತು. ಮಿಕ್ಕದ್ದನ್ನು ಕಾಲುಗಳಿಂದ ತುಳಿಯುತ್ತಾ ಇತ್ತು. ಅದು ಮುಂಚಿನ ಮೃಗಗಳಿಗಿಂತ ವಿಲಕ್ಷಣವಾಗಿತ್ತು. ಅದಕ್ಕೆ ಹತ್ತು ಕೊಂಬುಗಳಿದ್ದವು.",
			"8": "<sup>8</sup> ನಾನು ಆ ಕೊಂಬುಗಳನ್ನು ಗಮನಿಸುತ್ತಿರುವಾಗಲೆ ಅವುಗಳ ನಡುವೆ ಇಗೋ ಇನ್ನೊಂದು ಚಿಕ್ಕ ಕೊಂಬು ಮೊಳೆಯಿತು. ಅದಕ್ಕೆ ಎಡೆಮಾಡಿಕೊಡಲು ಮುಂಚಿನ ಕೊಂಬುಗಳಲ್ಲಿ ಮೂರನ್ನು ಬೇರುಸಹಿತ ಕೀಳಲಾಯಿತು. ಆಶ್ಚರ್ಯವೆಂದರೆ, ಆ ಕೊಂಬಿನಲ್ಲಿ ಮನುಷ್ಯನ ಕಣ್ಣುಗಳಂತಿರುವ ಕಣ್ಣುಗಳಿದ್ದವು. ಬಡಾಯಿ ಕೊಚ್ಚಿಕೊಳ್ಳುವ ಬಾಯೂ ಇತ್ತು.",
			"9": "<sup>9</sup> ನಾನು ನೋಡುತ್ತಿದ್ದ ಹಾಗೆ: ಸಿಂಹಾಸನಗಳನ್ನು ಹಾಕಲಾಯಿತು. ಮಹಾವೃದ್ಧನೊಬ್ಬನು ಆಸೀನನಾದನು. ಆತನ ಉಡುಪು ಬೆಳ್ಳಗಿತ್ತು ಹಿಮದಂತೆ. ಆತನ ತಲೆಗೂದಲಿತ್ತು ನಿರ್ಮಲವಾದ ಬಿಳಿಯ ಉಣ್ಣೆಯಂತೆ. ಆತನ ಸಿಂಹಾಸನ ಅಗ್ನಿಜ್ವಾಲೆಗಳು. ಧಗಧಗಿಸುವ ಬೆಂಕಿ ಅದರ ಚಕ್ರಗಳು.",
			"10": "<sup>10</sup> ಆ ಸಿಂಹಾಸನದ ಸಮ್ಮುಖದಿಂದ ಪ್ರಜ್ವಲ ಪ್ರವಾಹವೊಂದು ಉಕ್ಕಿ ಹರಿದುಬಂದಿತು. ಲಕ್ಷೋಪಲಕ್ಷ ದೂತರು ಆತನಿಗೆ ಸೇವೆಸಲ್ಲಿಸುತ್ತಿದ್ದರು. ಕೋಟ್ಯಾನುಕೋಟಿ ಕಿಂಕರರು ಆತನ ಮುಂದೆ ನಿಂತಿದ್ದರು. ನ್ಯಾಯಸಭೆಯವರು ತೀರ್ಪುನೀಡಲು ಕುಳಿತುಕೊಂಡರು. ಪಟ್ಟಿಪುಸ್ತಕಗಳನ್ನು ತೆರೆಯಲಾಯಿತು.",
			"11": "<sup>11</sup> ನಾನು ನೋಡುತ್ತಾ ಇದ್ದೆ: ಆ ಕಿರುಕೊಂಬು ಬಡಾಯಿಕೊಚ್ಚಿಕೊಂಡ ನಿಮಿತ್ತ ಆ ಕೊಂಬಿನ ಮೃಗವನ್ನು ಕೊಂದು ಅದರ ಹೆಣವನ್ನು ಉರಿಯುವ ಬೆಂಕಿಗೆ ಹಾಕಿ ನಾಶಮಾಡಿದರು.",
			"12": "<sup>12</sup> ಮಿಕ್ಕ ಮೃಗಗಳ ದೊರೆತನವನ್ನು ತೆಗೆದುಬಿಟ್ಟರು. ಆದರೆ ಕೆಲವು ಕಾಲದ ಮಟ್ಟಿಗೆ, ತಕ್ಕ ಸಮಯ ಬರುವ ತನಕ, ಅವುಗಳ ಜೀವಾವಧಿಯನ್ನು ಉಳಿಸಿದರು.",
			"13": "<sup>13</sup> ನಾನು ಕಂಡ ರಾತ್ರಿಯ ಕನಸಿನಲ್ಲಿ ನರಪುತ್ರನಂತಿರುವವನು ಆಕಾಶದ ಮೇಘಗಳೊಂದಿಗೆ ಬಂದು ಆ ಮಹಾವೃದ್ಧನನ್ನು ಸಮೀಪಿಸಿದನು. ಅವನನ್ನು ಆತನ ಸನ್ನಿಧಿಗೆ ತಂದರು.",
			"14": "<sup>14</sup> ಸಕಲ ರಾಷ್ಟ್ರ-ಕುಲ-ಭಾಷೆಗಳವರು ಅವನಿಗೆ ಸೇವೆ ಸಲ್ಲಿಸಲೆಂದು ಅವನಿಗೆ ದೊರೆತನ, ಘನತೆ, ರಾಜ್ಯತ್ವ ಇವುಗಳನ್ನು ಕೊಡಲಾಯಿತು. ಅವನ ಆಳ್ವಿಕೆಗೆ ಅಂತ್ಯವಿಲ್ಲ, ಅದು ಶಾಶ್ವತವಾದುದು, ಅವನ ರಾಜ್ಯ ಎಂದಿಗೂ ಅಳಿಯದು!",
			"15": "<sup>15</sup> ದಾನಿಯೇಲನಾದ ನನ್ನ ಮನ ಒಳಗೇ ವ್ಯಥೆಗೊಂಡಿತು. ನನ್ನ ಮನಸ್ಸಿನಲ್ಲಿ ತೋಚಿದ ದರ್ಶನಗಳು ನನ್ನನ್ನು ಕಳವಳಪಡಿಸಿದವು.",
			"16": "<sup>16</sup> ಹತ್ತಿರದಲ್ಲೆ ನಿಂತಿದ್ದವರಲ್ಲಿ ಒಬ್ಬನ ಬಳಿಗೆ ಹೋಗಿ ಇವುಗಳ ಸರಿಯಾದ ಅರ್ಥ ಏನೆಂದು ವಿಚಾರಿಸಿದೆ.",
			"17": "<sup>17</sup> ಅವನು ನನಗೆ: “ಆ ನಾಲ್ಕು ದೊಡ್ಡ ಮೃಗಗಳು ಲೋಕಸಾಗರಗಳಿಂದ ಏರತಕ್ಕ ನಾಲ್ಕು ರಾಜ್ಯಗಳು.",
			"18": "<sup>18</sup> ಆದರೆ ರಾಜ್ಯಾಧಿಕಾರ ಲಭಿಸುವುದು ಮಹೋನ್ನತರ ಪವಿತ್ರ ಪ್ರಜೆಗೆ. ಅದನ್ನು ತಲತಲಾಂತರಕ್ಕೂ ಶಾಶ್ವತವಾಗಿ ಅನುಭವಿಸುವವರು ಅವರೇ,” ಎಂದು ಆ ವಿಷಯಗಳ ತಾತ್ಪರ್ಯವನ್ನು ವಿವರಿಸಿ ತಿಳಿಸಿದನು.",
			"19": "<sup>19</sup> ಅದಾದ ಮೇಲೆ ಇತರ ಮೃಗಗಳಿಗಿಂತ ವಿಲಕ್ಷಣವಾಗಿಯೂ ಅತಿಭಯಂಕರವಾಗಿಯೂ ಕಬ್ಬಿಣದ ಹಲ್ಲು, ಕಂಚಿನ ಉಗುರು ಉಳ್ಳದ್ದಾಗಿಯೂ ತನ್ನ ಬಲಿಯನ್ನು ತುಂಡುತುಂಡಾಗಿಸಿ ಕಬಳಿಸುತ್ತಿದ್ದ ಹಾಗು ಮಿಕ್ಕದ್ದನ್ನು ಕಾಲಿನಿಂದ ತುಳಿಯುತ್ತಿದ್ದ ಆ ನಾಲ್ಕನೆಯ ಮೃಗದ ವಿಷಯವನ್ನು ತಿಳಿದುಕೊಳ್ಳಲು ಬಯಸಿದೆ.",
			"20": "<sup>20</sup> ಅದರ ತಲೆಯ ಮೇಲಿದ್ದ ಹತ್ತು ಕೊಂಬುಗಳ ವಿಷಯವಾಗಿಯೂ,",
			"21": "<sup>21</sup> ನಾನು ನೋಡುತ್ತಿದ್ದ ಹಾಗೆ ಯಾವ ಕೊಂಬು ಮೊಳೆತು ಮೂರು ಕೊಂಬುಗಳನ್ನು ಬೀಳಿಸಿ, ತನ್ನಲ್ಲೆ ಕಣ್ಣುಳ್ಳದ್ದಾಗಿ ಬಡಾಯಿ ಕೊಚ್ಚಿಕೊಳ್ಳುವ ಬಾಯುಳ್ಳದ್ದಾಗಿ, ಮಿಕ್ಕ ಕೊಂಬುಗಳಿಗಿಂತ ಭಯಂಕರವಾಗಿ ಕಾಣಿಸುತ್ತಾ ಇತ್ತೋ, ಆ ಕೊಂಬಿನ ವಿಷಯವಾಗಿ ಸತ್ಯಾರ್ಥವನ್ನೂ ತಿಳಿದುಕೊಳ್ಳಬೇಕೆಂದು ವಿಚಾರಿಸಿದೆ.",
			"22": "<sup>22</sup> ಆ ಕೊಂಬು ಪವಿತ್ರ ಪ್ರಜೆಯೊಂದಿಗೆ ಯುದ್ಧಮಾಡುತ್ತಿತ್ತು. ಮಹಾವೃದ್ಧನು ಬಂದು ಮಹೋನ್ನತರ ಆ ಪವಿತ್ರ ಪ್ರಜೆಗೆ ನ್ಯಾಯತೀರಿಸಿ ಅವರಿಗೆ ರಾಜ್ಯವನ್ನು ದೊರಕಿಸುವ ತನಕ ಅವರನ್ನು ಗೆಲ್ಲುತ್ತಾ ಬಂದಿತ್ತು.",
			"23": "<sup>23</sup> ಆಗ ಅವನು ನನಗೆ ಕೊಟ್ಟ ವಿವರ ಇದು: “ಆ ನಾಲ್ಕನೆಯ ಮೃಗ ಲೋಕದಲ್ಲಿ ತಲೆಯೆತ್ತುವ ನಾಲ್ಕನೆಯ ರಾಜ್ಯ. ಅದು ಮಿಕ್ಕ ರಾಜ್ಯಗಳಿಗಿಂತ ವಿಲಕ್ಷಣವಾದುದು. ಲೋಕವನ್ನೇ ಕಾಲಿನಿಂದ ತುಳಿದು ಚೂರುಚೂರು ಮಾಡಿ ಕಬಳಿಸುವುದು.",
			"24": "<sup>24</sup> ಆ ಹತ್ತು ಕೊಂಬುಗಳು ಅದೇ ರಾಜ್ಯವನ್ನು ಆಳುವ ಹತ್ತುಮಂದಿ ಅರಸರು. ಅವರ ತರುವಾಯ ಮತ್ತೊಬ್ಬನು ತಲೆದೋರುವನು. ಅವನು ಮುಂಚಿನ ಅರಸರಿಗಿಂತ ವಿಲಕ್ಷಣವಾಗಿರುವನು, ಆ ಮೂವರು ಅರಸರನ್ನು ಸದೆಬಡಿದುಬಿಡುವನು.",
			"25": "<sup>25</sup> ಅವನು ಮಹೋನ್ನತ ದೇವರಿಗೆ ವಿರುದ್ಧವಾಗಿ ಕೊಚ್ಚಿಕೊಳ್ಳುವನು. ಮಹೋನ್ನತರ ಪವಿತ್ರ ಪ್ರಜೆಯನ್ನು (ಸಂತರನ್ನು) ಶೋಷಣೆಗೆ ಗುರಿಮಾಡುವನು. ಆ ಪ್ರಜೆ ಅವನಿಗೆ ಮೂರುವರೆ ವರ್ಷ ಅಧೀನರಾಗಿರುವರು.",
			"26": "<sup>26</sup> ಆದರೆ (ದೇವರ) ನ್ಯಾಯಸಭೆ ತೀರ್ಪಿತ್ತು, ಅವನ ದೊರೆತನವನ್ನು ಕಿತ್ತು, ವಿನಾಶಮಾಡಿ, ಕೊನೆಗಾಣಿಸಿಬಿಡುವುದು.",
			"27": "<sup>27</sup> ರಾಜ್ಯಭಾರವೂ ದೊರೆತನವೂ ಸಮಸ್ತ ಲೋಕದಲ್ಲಿನ ರಾಜ್ಯಗಳ ಮಹಿಮೆಯೂ ಮಹೋನ್ನತರ ಭಕ್ತಜನರಿಗೆ ಕೊಡಲಾಗುವುದು. ಅವರ ರಾಜ್ಯ ಶಾಶ್ವತರಾಜ್ಯ. ಎಲ್ಲ ದೇಶಾಧಿಪತಿಗಳೂ ಅವರಿಗೆ ಅಧೀನರಾಗಿ ಸೇವೆಮಾಡುವರು,” ಎಂದು ಹೇಳಿದನು.",
			"28": "<sup>28</sup> ಇಲ್ಲಿಗೆ ಆ ಕನಸಿನ ವಿವರ ಮುಗಿಯಿತು. ದಾನಿಯೇಲನಾದ ನಾನು ನನ್ನ ಆಲೋಚನೆಗಳಿಂದ ಬಹಳ ಕಳವಳಗೊಂಡೆ. ನನ್ನ ಮುಖ ಬಾಡಿತು. ಆದರೂ ನಡೆದ ಸಂಗತಿಯನ್ನು ಮನಸ್ಸಿನಲ್ಲೇ ಇಟ್ಟುಕೊಂಡೆ."
		},
		"8": {
			"1": "<sup>1</sup> ಮೊದಲು ನನಗೆ ಕಾಣಿಸಿದ ಕನಸಲ್ಲದೆ, ಅರಸ ಬೇಲ್ಶಚ್ಚರನ ಆಳ್ವಿಕೆಯ ಮೂರನೇ ವರ್ಷದಲ್ಲಿ ದಾನಿಯೇಲನಾದ ನನಗೆ ಇನ್ನೊಂದು ಕನಸು ಕಾಣಿಸಿತು.",
			"2": "<sup>2</sup> ಈ ಕನಸಿನಲ್ಲಿ ಏಲಾಮ್ ಸಂಸ್ಥಾನದ ಶೂಷನ್ ಕೋಟೆಯಲ್ಲಿ ನಾನಿದ್ದೆ. ಊಲಾ ಕಾಲುವೆಯ ದಡದ ಮೇಲೆ ನಾನು ನಿಂತುಕೊಂಡಿದ್ದ ಹಾಗೆ ತೋರಿತು.",
			"3": "<sup>3</sup> ಕಣ್ಣೆತ್ತಿ ನೋಡಿದಾಗ ಎರಡು ಕೊಂಬಿನ ಒಂದು ಟಗರು ಕಾಲುವೆಯ ಹತ್ತಿರ ನಿಂತಿದ್ದು ಕಾಣಿಸಿತು. ಅದರ ಎರಡು ಕೊಂಬುಗಳು ಉದ್ದವಾಗಿದ್ದರೂ ಒಂದಕ್ಕಿಂತ ಒಂದು ದೊಡ್ಡದಾಗಿತ್ತು. ಆ ದೊಡ್ಡದು ಮೊಳೆತದ್ದು ಚಿಕ್ಕ ಕೊಂಬು ಮೊಳೆತ ಮೇಲೆ.",
			"4": "<sup>4</sup> ನಾನು ನೋಡುತ್ತಿದ್ದ ಹಾಗೆ ಆ ಟಗರು ಪಶ್ಚಿಮಕ್ಕೂ ಉತ್ತರಕ್ಕೂ ದಕ್ಷಿಣಕ್ಕೂ ಹಾದಾಡುತ್ತಿತ್ತು. ಯಾವ ಪ್ರಾಣಿಯೂ ಅದರ ಎದುರಿಗೆ ನಿಲ್ಲಲಾರದೆಹೋಯಿತು. ಅದರ ಕೈಯಿಂದ ಬಿಡಿಸಲು ಯಾವ ಮೃಗಕ್ಕೂ ಶಕ್ತಿಯಿರಲಿಲ್ಲ. ಅದು ಇಷ್ಟಬಂದಂತೆ ನಡೆದು ತನ್ನ ಶಕ್ತಿಯನ್ನು ಹೆಚ್ಚಿಸಿಕೊಂಡಿತ್ತು.",
			"5": "<sup>5</sup> ನಾನು ಅದನ್ನು ಗಮನಿಸುತ್ತಿರುವಾಗಲೇ ಹೋತವೊಂದು ಪಶ್ಚಿಮದಿಂದ ಹೊರಟುಬಂದಿತು. ಅದು ಜಗವೆಲ್ಲವನ್ನು ದಾಟಿಕೊಂಡು ನೆಲವನ್ನು ಸೋಕದೆ, ಅಲ್ಲಿಗೆ ಧಾವಿಸಿತು. ಅದರ ಕಣ್ಣುಗಳ ನಡುವೆ ವಿಶೇಷವಾದ ಒಂದು ಕೊಂಬಿತ್ತು.",
			"6": "<sup>6</sup> ನಾನು ಕಾಲುವೆಯ ಹತ್ತಿರ ಕಂಡ ಎರಡು ಕೊಂಬಿನ ಟಗರಿನ ಬಳಿಗೆ ಆ ಹೋತ ಓಡಿತು. ಪರಾಕ್ರಮದಿಂದ ಉಬ್ಬಿ, ರೋಷಗೊಂಡು, ಅದರ ಮೇಲೆ ಬೀಳಬೇಕೆಂದು ದೌಡಾಯಿಸಿತು.",
			"7": "<sup>7</sup> ಆ ಟಗರನ್ನು ಸಮೀಪಿಸುವುದನ್ನು ನಾನು ಗಮನಿಸಿದೆ. ಟಗರಿನ ವಿರುದ್ಧ ಮಹಾರೌದ್ರದಿಂದ ಉರಿಯುತ್ತಾ ಅದರ ಮೇಲೆ ಎರಗಿ ಅದರ ಎರಡು ಕೊಂಬುಗಳನ್ನು ಮುರಿದುಬಿಟ್ಟಿತು. ಅದನ್ನು ಎದುರಿಸುವ ಶಕ್ತಿ ಯಾವುದೂ ಆ ಟಗರಿಗೆ ಇರಲಿಲ್ಲ. ಹೋತ ಟಗರನ್ನು ನೆಲಕ್ಕೆ ಕೆಡವಿ ತುಳಿದುಹಾಕಿತು. ಅದರ ಕೈಯಿಂದ ಟಗರನ್ನು ಬಿಡಿಸುವ ಪ್ರಾಣಿ ಯಾವುದೂ ಇರಲಿಲ್ಲ.",
			"8": "<sup>8</sup> ಆ ಹೋತಕ್ಕೆ ಅತ್ಯಧಿಕ ಹುಮ್ಮಸ್ಸು ಬಂದಿತು. ಆದರೆ ಅದು ಪ್ರಾಬಲ್ಯಕ್ಕೆ ಬರುವಾಗಲೇ ಅದರ ದೊಡ್ಡ ಕೊಂಬು ಮುರಿದುಹೋಯಿತು. ಅದರ ಸ್ಥಾನದಲ್ಲಿ ನಾಲ್ಕು ಪ್ರಸಿದ್ಧ ಕೊಂಬುಗಳು ಮೊಳೆತು ಚತುರ್ದಿಕ್ಕುಗಳಿಗೂ ಚಾಚಿಕೊಂಡವು.",
			"9": "<sup>9</sup> ಅವುಗಳೊಳಗೆ ಒಂದರಲ್ಲಿ ಒಂದು ಚಿಕ್ಕ ಕೊಂಬು ಮೊಳೆತು, ಬಹುದೊಡ್ಡದಾಗಿ ಬೆಳೆದು, ದಕ್ಷಿಣದ ಕಡೆಗೂ ಪೂರ್ವದ ಕಡೆಗೂ ಚೆಲುವಿನ ನಾಡಾದ ಪಾಲೆಸ್ತೀನಿನ ಕಡೆಗೂ ಪ್ರಬಲಗೊಂಡಿತು.",
			"10": "<sup>10</sup> ಅದು ನಕ್ಷತ್ರ ಗಣದ ಮೇಲೆ ಕೈಮಾಡುವಷ್ಟು ಹೆಚ್ಚಿ ಆ ಗಣದ ಕೆಲವು ನಕ್ಷತ್ರಗಳನ್ನೂ ನೆಲಕ್ಕೆ ಕೆಡವಿ ತುಳಿದುಬಿಟ್ಟಿತು.",
			"11": "<sup>11</sup> ಅದು ಮಾತ್ರವಲ್ಲ, ಆ ನಕ್ಷತ್ರಗಣದ ಅಧಿಪತಿಯನ್ನೇ ಎದುರಿಸುವಷ್ಟು ಉಬ್ಬಿಹೋಗಿ ಅನುದಿನದ ಬಲಿಯರ್ಪಣೆ ಆತನಿಗೆ ಸಲ್ಲದಂತೆ ಮಾಡಿತು.",
			"12": "<sup>12</sup> ಆತನ ಪವಿತ್ರಾಲಯವನ್ನು ಕೆಡವಿತು. ಅನುದಿನದ ಬಲಿಯರ್ಪಣೆಯನ್ನು ಅಡಗಿಸುವುದಕ್ಕಾಗಿ ನೀಚತನದಿಂದ ಒಂದು ಸೈನ್ಯವನ್ನೆಬ್ಬಿಸಿತು. ಸತ್ಯಧರ್ಮವನ್ನು ನೆಲಕ್ಕೆ ದೊಬ್ಬಿತು. ತನ್ನ ಇಷ್ಟಾರ್ಥವನ್ನು ಈಡೇರಿಸಿಕೊಂಡು ವೃದ್ಧಿಗೆ ಬಂದಿತು.",
			"13": "<sup>13</sup> ಆಗ ಒಬ್ಬ ದೇವದೂತನು ಮಾತಾಡುವುದು ನನಗೆ ಕೇಳಿಸಿತು. ಮಾತಾಡುವವನನ್ನು ಮತ್ತೊಬ್ಬ ದೇವದೂತನು ಸಂಬೋಧಿಸಿ: “ಅನುದಿನದ ಬಲಿಯರ್ಪಣೆಯನ್ನು ನಿಲ್ಲಿಸುವುದು, ಭಯಂಕರವಾದ ದೇವದ್ರೋಹ ಮಾಡುವುದು, ಪವಿತ್ರಾಲಯವನ್ನೂ ದೇವಭಕ್ತಗಣವನ್ನೂ ತುಳಿಯುವುದೂ-ಕನಸಿನ ಇಂಥಾ ಕಾರ್ಯಗಳು ಎಷ್ಟುಕಾಲ ನಡೆಯುವುವು?” ಎಂದು ಪ್ರಶ್ನೆಮಾಡಿದನು.",
			"14": "<sup>14</sup> ಅವನು ಇವನಿಗೆ, “ಉದಯಾಸ್ತಮಾನಗಳ ಎರಡು ಸಾವಿರದ ಮುನ್ನೂರರವರೆಗೆ ನಡೆಯುವುವು. ಅನಂತರ ಪವಿತ್ರಾಲಯಕ್ಕೆ ಪುನಃ ನ್ಯಾಯಸ್ಥಾಪನೆ ಆಗುವುದು,” ಎಂದು ಹೇಳಿದನು.",
			"15": "<sup>15</sup> ದಾನಿಯೇಲನಾದ ನಾನು ಈ ಕನಸನ್ನು ಕಂಡು ಅದನ್ನು ಗ್ರಹಿಸಲು ಪ್ರಯತ್ನಿಸುತ್ತಿರುವಾಗ ತಟ್ಟನೆ ಮನುಷ್ಯ ರೂಪಹೊಂದಿದ ಒಬ್ಬನು ನನ್ನೆದುರಿಗೆ ನಿಂತನು.",
			"16": "<sup>16</sup> ಆಗ, “ಗಬ್ರಿಯೇಲನೇ, ಕನಸಿನ ಅರ್ಥವನ್ನು ಇವನಿಗೆ ತಿಳಿಯಪಡಿಸು,” ಎಂದು ಮನುಷ್ಯ ಧ್ವನಿಯಂಥ ಒಂದು ವಾಣಿ ಊಲಾ ಕಾಲುವೆಯ ನಡುವೆ ನನಗೆ ಕೇಳಿಸಿತು.",
			"17": "<sup>17</sup> ಅದರಂತೆ ಅವನು ನನ್ನ ಹತ್ತಿರಕ್ಕೆ ಬಂದನು. ನಾನು ಭಯಭ್ರಾಂತನಾಗಿ ಅಡ್ಡಬಿದ್ದೆ. ನನಗೆ ಅವನು, “ನರಪುತ್ರನೇ, ಇದು ಅಂತ್ಯಕಾಲದಲ್ಲಿ ನೆರವೇರುವ ಕನಸು ಎಂಬುದು ನಿನಗೆ ಮನದಟ್ಟಾಗಿರಲಿ;” ಎಂದು ಹೇಳಿದನು.",
			"18": "<sup>18</sup> ಅವನು ನನ್ನೊಡನೆ ಮಾತಾಡುತ್ತಿರುವಾಗ ನಾನು ಮೈಮರೆತು ಅಧೋಮುಖವಾಗಿ ಬಿದ್ದಿದ್ದೆ. ಆಗ ಅವನು ನನ್ನನ್ನು ಮುಟ್ಟಿ, ನಿಲ್ಲಿಸಿ ನನಗೆ:",
			"19": "<sup>19</sup> “ದೇವರು ತಮ್ಮ ಕೋಪವನ್ನು ತೀರಿಸುವ ಮುಂದಿನ ಕಾಲದಲ್ಲಿ ನಡೆಯತಕ್ಕದ್ದನ್ನು ನಿನಗೆ ತಿಳಿಸುತ್ತೇನೆ ಕೇಳು - ಅದು ನಿಯಮಿತ ಅಂತ್ಯಕಾಲಕ್ಕೆ ಸಂಬಂಧಪಟ್ಟದ್ದು.”",
			"20": "<sup>20</sup> “ನೀನು ನೋಡಿದ ಎರಡು ಕೊಂಬಿನ ಟಗರು ಮೇದ್ಯದ ಮತ್ತು ಪರ್ಷಿಯದ ರಾಜ್ಯ.",
			"21": "<sup>21</sup> ಆ ಹೋತ ಗ್ರೀಕ್ ರಾಜ್ಯ. ಅದರ ಕಣ್ಣುಗಳ ನಡುವಣ ದೊಡ್ಡ ಕೊಂಬು ಆ ರಾಜ್ಯದ ಮೊದಲನೆಯ ರಾಜ.",
			"22": "<sup>22</sup> ಆ ಕೊಂಬು ಮುರಿದುಹೋದ ಮೇಲೆ ಅದರ ಸ್ಥಾನದಲ್ಲಿ ನಾಲ್ಕು ಕೊಂಬುಗಳು ಎದ್ದಂತೆಯೆ ಆ ಜನಾಂಗದಿಂದ ನಾಲ್ಕು ರಾಜ್ಯಗಳು ಏಳುವುವು. ಆದರೆ ಮೊದಲನೇ ರಾಜನಿಗೆ ಇದ್ದಷ್ಟು ಶಕ್ತಿ ಅವುಗಳಿಗೆ ಇರುವುದಿಲ್ಲ.",
			"23": "<sup>23</sup> ಆ ರಾಜ್ಯಗಳ ಕಡೆಯಕಾಲ ಬಂದಾಗ, ಅಧರ್ಮಿಗಳ ಅಪರಾಧ ಭರ್ತಿಯಾದಾಗ, ಕ್ರೂರ ಮುಖನೂ ಕುತಂತ್ರ ವಚನ ನಿಪುಣನೂ ಆದ ಒಬ್ಬ ರಾಜನು ತಲೆದೋರುವನು.",
			"24": "<sup>24</sup> ಅವನು ಪ್ರಬಲನಾಗುವನು, ಆದರೆ ಸ್ವಬಲದಿಂದಲ್ಲ. ಅತ್ಯಧಿಕವಾಗಿ ಹಾಳುಮಾಡಿ, ಅಭಿವೃದ್ಧಿಯಾಗುವನು. ತನ್ನ ಇಷ್ಟಾರ್ಥವನ್ನು ತೀರಿಸಿಕೊಳ್ಳುವನು. ಬಲಿಷ್ಠರನ್ನೂ ದೇವಜನರನ್ನೂ ಧ್ವಂಸಮಾಡುವನು.",
			"25": "<sup>25</sup> ಯುಕ್ತಿಯಿಂದಲೆ ತನ್ನ ಕಪಟತನವನ್ನು ಸಿದ್ಧಿಗೆ ತರುವನು. ಮನದಲ್ಲೆ ಉಬ್ಬಿಕೊಂಡು ನೆಮ್ಮದಿಯಾಗಿರುವ ಬಹುಜನರನ್ನು ನಾಶಪಡಿಸುವನು. ರಾಜಾಧಿರಾಜನಿಗೂ ವಿರುದ್ಧವಾಗಿ ಏಳುವನು. ಆದರೂ ಯಾರ ಕೈಯೂ ಸೋಕದೆ ಹಾಳಾಗುವನು.",
			"26": "<sup>26</sup> ಕನಸಿನಲ್ಲಿ ತಿಳಿಸಲಾದ ಉದಯಾಸ್ತಮಾನಗಳ ವಿಷಯ ನಿಜವೇ ಸರಿ. ಆದರೆ ಆ ಕನಸು ಗುಟ್ಟಾಗಿರಲಿ. ಅದು ಬಹುದೂರದ ಕಾಲದ್ದು,” ಎಂದು ಹೇಳಿದನು.",
			"27": "<sup>27</sup> ದಾನಿಯೇಲನಾದ ನಾನು ಮೂರ್ಛೆಗೊಂಡೆ. ಕೆಲವು ದಿವಸ ಕಾಯಿಲೆ ಬಿದ್ದೆ. ಆಮೇಲೆ ಎದ್ದು ರಾಜಕಾರ್ಯವನ್ನು ಆರಂಭಿಸಿದೆ. ಆದರೂ ಆ ಕನಸಿಗೆ ಬೆಚ್ಚಿಬೆರಗಾದೆ. ಅದರ ಅರ್ಥವನ್ನು ಗ್ರಹಿಸಿಕೊಳ್ಳಲಾಗಲಿಲ್ಲ."
		},
		"9": {
			"1": "<sup>1</sup> ಅದು, ದಾರ್ಯಾವೆಷನ ಆಳ್ವಿಕೆಯ ಮೊದಲನೆಯ ವರ್ಷ. ಈತನು ಅಹಷ್ವೇರೋಷನ ಮಗ, ಮೇದ್ಯ ವಂಶದವನು ಹಾಗು ಬಾಬಿಲೋನಿನ ರಾಜ್ಯದ ದೊರೆ.",
			"2": "<sup>2</sup> ದಾನಿಯೇಲನಾದ ನಾನು ಪವಿತ್ರಗ್ರಂಥಗಳನ್ನು ಪರೀಕ್ಷಿಸಿ, ಸರ್ವೇಶ್ವರಸ್ವಾಮಿ ಪ್ರವಾದಿ ಯೆರೆಮೀಯನಿಗೆ ದಯಪಾಲಿಸಿದ ವಾಕ್ಯಾನುಸಾರ ಜೆರುಸಲೇಮ್ ಪಾಳುಬಿದ್ದಿರಬೇಕಾದ ಪೂರ್ಣಕಾಲಾವಧಿ ಎಪ್ಪತ್ತು ವರ್ಷಗಳೆಂದು ತಿಳಿದುಕೊಂಡೆ.",
			"3": "<sup>3</sup> ಉಪವಾಸವಿದ್ದು, ಗೋಣಿತಟ್ಟು ಸುತ್ತಿಕೊಂಡು, ಬೂದಿ ಬಳಿದುಕೊಂಡು, ಸರ್ವೇಶ್ವರನಾದ ದೇವರ ಕಡೆಗೆ ಮುಖವೆತ್ತಿ, ಪ್ರಾರ್ಥನೆ-ವಿಜ್ಞಾಪನೆಗಳಲ್ಲಿ ನಿತರನಾದೆ.",
			"4": "<sup>4</sup> ಪಾಪವನ್ನು ಅರಿಕೆಮಾಡಿ ನನ್ನ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ಹೀಗೆಂದು ಬಿನ್ನವಿಸಿದೆ: “ಹೇ ಸರ್ವೇಶ್ವರಾ, ಮಹೋನ್ನತರೂ ಭಯಭಕ್ತಿಗೆ ಪಾತ್ರರೂ ಆದ ದೇವರೇ, ನಿಮ್ಮನ್ನು ಪ್ರೀತಿಸಿ, ನಿಮ್ಮ ಆಜ್ಞೆಗಳನ್ನು ಅನುಸರಿಸುವವರಿಗೆ ನೀವು ಮಾಡಿದ ವಾಗ್ದಾನಗಳನ್ನು ಪ್ರೀತಿಯಿಂದ ನೆರವೇರಿಸುತ್ತೀರಿ.",
			"5": "<sup>5</sup> “ನಾವು ಪಾಪಾಪರಾಧಗಳನ್ನು ಮಾಡಿ, ಕೆಟ್ಟವರಾಗಿ ನಡೆದಿದ್ದೇವೆ; ನಿಮಗೆ ವಿರುದ್ಧ ತಿರುಗಿಬಿದ್ದು, ನಿಮ್ಮ ಆಜ್ಞಾವಿಧಿಗಳನ್ನು ತೊರೆದುಬಿಟ್ಟಿದ್ದೇವೆ.",
			"6": "<sup>6</sup> ಅರಸರಿಗೆ, ಒಡೆಯರಿಗೆ, ಹಿರಿಯರಿಗೆ ಹಾಗು ಜನಸಾಮಾನ್ಯರಿಗೆ ನಿಮ್ಮ ದಾಸರಾದ ಪ್ರವಾದಿಗಳು ನಿಮ್ಮ ಹೆಸರಿನಲ್ಲಿ ನುಡಿದ ಮಾತುಗಳಿಗೆ ನಾವು ಕಿವಿಗೊಡಲೇ ಇಲ್ಲ.",
			"7": "<sup>7</sup> ಸರ್ವೇಶ್ವರಾ, ನೀವು ಸತ್ಯಸ್ವರೂಪಿ. ನಾವೋ ಲಜ್ಜೆಗೆಟ್ಟವರು. ಹೌದು, ಯೆಹೂದ್ಯರು ಹಾಗು ಜೆರುಸಲೇಮಿನ ನಿವಾಸಿಗಳು ಈಗಾಗಲೆ ನಿಮಗೆ ವಿರುದ್ಧ ಮಾಡಿದ ದ್ರೋಹದ ನಿಮಿತ್ತ ನಿಮ್ಮಿಂದ ದೇಶವಿದೇಶಗಳಿಗೆ ತಳ್ಳಲ್ಪಟ್ಟಿದ್ದಾರೆ. ದೂರದ ದೇಶಗಳಿಗೂ ಹತ್ತಿರದ ನಾಡುಗಳಿಗೂ ಚದರಿಹೋಗಿರುವ ಎಲ್ಲ ಇಸ್ರಯೇಲರು ನಾಚಿಕೆಗೆ ಈಡಾಗಿದ್ದಾರೆ.",
			"8": "<sup>8</sup> ಸ್ವಾಮೀ, ನಿಮಗೆ ವಿರುದ್ಧ ಪಾಪಮಾಡಿದ್ದರಿಂದ ನಾವೂ ನಮ್ಮ ಅರಸರೂ ಒಡೆಯರೂ ಹಿರಿಯರೂ ನಾಚಿಕೆಗೆ ಗುರಿಯಾಗಿದ್ದೇವೆ.",
			"9": "<sup>9</sup> ನಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ, ನಮ್ಮನ್ನು ಕರುಣಿಸುವವರೂ ಕ್ಷಮಿಸುವವರೂ ಆಗಿದ್ದಾರೆ. ಆದರೆ ನಾವು ಅವರಿಗೆ ವಿರುದ್ಧ ತಿರುಗಿಬಿದ್ದೆವು.",
			"10": "<sup>10</sup> ನಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನ ಮಾತನ್ನು ಕೇಳಲಿಲ್ಲ. ಅವರ ದಾಸರಾದ ಪ್ರವಾದಿಗಳ ಮುಖಾಂತರ ನಮಗೆ ಗೊತ್ತುಮಾಡಿದ ಸನ್ಮಾರ್ಗದಲ್ಲಿ ನಾವು ನಡೆಯಲಿಲ್ಲ.",
			"11": "<sup>11</sup> ಇಸ್ರಯೇಲರೆಲ್ಲರು ನಿಮ್ಮ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಮೀರಿ ನಿಮ್ಮ ಮಾತನ್ನು ಕೇಳಲೊಲ್ಲದೆ ಓರೆಯಾಗಿ ಹೋದರು. ಆದಕಾರಣ ನಿಮ್ಮ ಶಾಪದ ಕೇಡುಗಳನ್ನೂ ದೇವರ ದಾಸನಾದ ಮೋಶೆಯ ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿ ನೀವು ಆಣೆಯಿಟ್ಟು ಪ್ರಕಟಿಸಿದ ವಿಪತ್ತುಗಳನ್ನೂ ನಮ್ಮ ಮೇಲೆ ಸುರಿಯಲಾಗಿದೆ. ನಾವು ದ್ರೋಹಿಗಳೇ ಸರಿ.",
			"12": "<sup>12</sup> ನಮ್ಮ ದೇವರು ನಮ್ಮ ಮೇಲೆ ದೊಡ್ಡ ಕೇಡನ್ನು ಬರಮಾಡಿದ್ದಾರೆ. ನಮ್ಮನ್ನೂ ನಮ್ಮನ್ನಾಳುವ ಅಧಿಪತಿಗಳನ್ನೂ ಖಂಡಿಸಿ ಹೇಳಿದ ಮಾತುಗಳನ್ನು ನೆರವೇರಿಸಿದ್ದಾರೆ. ಜೆರುಸಲೇಮಿಗೆ ಆದಂಥ ಕೇಡು ವಿಶ್ವದಲ್ಲಿ ಎಂದೂ ಆಗಲಿಲ್ಲ.",
			"13": "<sup>13</sup> ಮೋಶೆಯ ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿ ಬರೆದಿರುವಂತೆಯೇ ಈ ವಿಪತ್ತೆಲ್ಲಾ ನಮ್ಮ ಮೇಲೆ ಬಂದಿದೆ. ಆದರೂ ನಾವು ನಮ್ಮ ಧರ್ಮಮಾರ್ಗಗಳನ್ನು ತೊರೆದುಬಿಟ್ಟಿದ್ದೇವೆ. ನಿಮ್ಮ ಸತ್ಯೋಪದೇಶವನ್ನು ಗ್ರಹಿಸಿ ನಡೆಯುವಂತೆ ನಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರನೆಂಬ ನಿಮ್ಮ ದಯೆಯನ್ನು ಬೇಡಿಕೊಳ್ಳಲಿಲ್ಲ.",
			"14": "<sup>14</sup> ಆದಕಾರಣ ಸರ್ವೇಶ್ವರ ಈ ಕೇಡನ್ನು ಸಕಾಲಕ್ಕೆ ನಮ್ಮ ಮೇಲೆ ಬರಮಾಡಿದ್ದಾರೆ. ನಮ್ಮ ದೇವರಾದ ಸರ್ವೇಶ್ವರ ತಾವು ಮಾಡುವ ಸಕಲ ಕಾರ್ಯಗಳಲ್ಲೂ ನ್ಯಾಯಸ್ವರೂಪರು. ನಾವೋ ಅವರ ಮಾತನ್ನು ಕೇಳಲಿಲ್ಲ.",
			"15": "<sup>15</sup> “ಸರ್ವೇಶ್ವರಾ, ನಮ್ಮ ದೇವರೇ, ನೀವು ನಿಮ್ಮ ಭುಜಪರಾಕ್ರಮವನ್ನು ತೋರಿಸಿ, ನಿಮ್ಮ ಜನರನ್ನು ಈಜಿಪ್ಟಿನಿಂದ ಪಾರುಮಾಡಿ, ಇಂದಿನವರೆಗೂ ಸುಪ್ರಸಿದ್ಧವಾದ ಹೆಸರನ್ನು ಪಡೆದುಕೊಂಡವರಾಗಿದ್ದೀರಿ. ಈಗ ಚಿತ್ತೈಸಿ. ನಾವು ಪಾಪಮಾಡಿ ಕೆಟ್ಟವರಾಗಿ ನಡೆದಿದ್ದೇವೆ.",
			"16": "<sup>16</sup> ಸರ್ವೇಶ್ವರಾ, ನಿಮ್ಮ ಸಮಸ್ತ ಧರ್ಮಕಾರ್ಯಗಳಿಗೆ ಅನುಸಾರವಾಗಿ ನಿಮ್ಮ ನಗರವೂ ನಿಮ್ಮ ಪವಿತ್ರ ಪರ್ವತವೂ ಆದ ಜೆರುಸಲೇಮ್ ಮೇಲೆ ನಿಮಗಿರುವ ಕೋಪವನ್ನೂ ರೋಷಾಗ್ನಿಯನ್ನೂ ದಯಮಾಡಿ ತೊಲಗಿಸಿಬಿಡಿ. ನಮ್ಮ ಮತ್ತು ನಮ್ಮ ಪೂರ್ವಜರ ಪಾಪಾಪರಾಧಗಳ ನಿಮಿತ್ತ ಜೆರುಸಲೇಮ್ ಮತ್ತು ನಿಮ್ಮ ಜನತೆ ನೆರೆಹೊರೆಯವರೆಲ್ಲರ ನಿಂದೆಗೆ ಗುರಿಯಾಗಿವೆ.",
			"17": "<sup>17</sup> ಓ ನಮ್ಮ ದೇವರೇ, ನಿಮ್ಮ ದಾಸನ ಪ್ರಾರ್ಥನೆ ವಿಜ್ಞಾಪನೆಗಳನ್ನು ಈಗ ಆಲಿಸಿರಿ. ಹಾಳಾಗಿರುವ ನಿಮ್ಮ ಪವಿತ್ರಾಲಯವನ್ನು ಸರ್ವೇಶ್ವರ ಎಂಬ ನಿಮ್ಮ ಹೆಸರಿನ ನಿಮಿತ್ತವೇ ಪ್ರಸನ್ನ ಮುಖದಿಂದ ನೋಡಿ.",
			"18": "<sup>18</sup> ನನ್ನ ದೇವರೇ, ಕಿವಿಗೊಟ್ಟು ಕೇಳಿ, ಕಣ್ಣು ತೆರೆದು ನಮ್ಮ ಹಾಳು ಪ್ರದೇಶಗಳನ್ನೂ ನಿಮ್ಮ ಹೆಸರುಗೊಂಡಿರುವ ನಗರವನ್ನೂ ನೋಡಿ. ನಾವು ಸದ್ಧರ್ಮಿಗಳೇನೂ ಅಲ್ಲ. ನಿಮ್ಮ ಮಹಾಕೃಪೆಯನ್ನೇ ನಂಬಿಕೊಂಡು ಈ ಬಿನ್ನಹಗಳನ್ನು ನಿಮ್ಮ ಮುಂದೆ ಅರಿಕೆಮಾಡುತ್ತಿದ್ದೇವೆ.",
			"19": "<sup>19</sup> ಸ್ವಾಮೀ, ಕೇಳಿ; ಸ್ವಾಮೀ, ಕ್ಷಮಿಸಿ; ಸ್ವಾಮೀ, ಆಲಿಸಿ ಕಾರ್ಯವನ್ನು ಸಾಧಿಸಿ. ತಡಮಾಡಬೇಡಿ. ನನ್ನ ದೇವರೇ, ನಿಮ್ಮ ಜನತೆ ಹಾಗು ನಿಮ್ಮ ನಗರ ನಿಮ್ಮ ಹೆಸರಿನವುಗಳು. ಆದಕಾರಣ, ನಿಮ್ಮ ಹೆಸರನ್ನು ಕಾಪಾಡಿಕೊಳ್ಳಿ.”",
			"20": "<sup>20</sup> ನಾನು ಬಾಯಿಬಿಟ್ಟು ಬೇಡಿಕೊಂಡೆ. ನನ್ನ ಮತ್ತು ನನ್ನ ಜನರಾದ ಇಸ್ರಯೇಲರ ಪಾಪವನ್ನು ಅರಿಕೆಮಾಡುತ್ತಾ ನನ್ನ ದೇವರ ಪವಿತ್ರ ಪರ್ವತದ ವಿಷಯವಾಗಿ ದೇವರಾದ ಸರ್ವೇಶ್ವರನಿಗೆ ಬಿನ್ನವಿಸಿದೆ.",
			"21": "<sup>21</sup> ಹೀಗೆ ಪ್ರಾರ್ಥನೆ ಮಾಡುತ್ತಿರುವಾಗಲೇ, ಮೊದಲು ನನ್ನ ಕನಸಿನಲ್ಲಿ ಕಂಡ ಗಬ್ರಿಯೇಲೆಂಬ ವ್ಯಕ್ತಿ ಅಸುರುಸುರಾಗಿ ಹಾರಿಬಂದು, ಸಂಧ್ಯಾನೈವೇದ್ಯ ಸಮಯದಲ್ಲಿ ನನ್ನನ್ನು ಸೇರಿ, ನನ್ನೊಂದಿಗೆ ಮಾತಾಡಿ, ಹೀಗೆಂದು ಉಪದೇಶ ಮಾಡಿದನು:",
			"22": "<sup>22</sup> “ದಾನಿಯೇಲನೇ, ನಿನಗೆ ಜ್ಞಾನಬೋಧೆ ಮಾಡಲು ಈಗ ಬಂದಿದ್ದೇನೆ;",
			"23": "<sup>23</sup> ನಿನ್ನ ವಿಜ್ಞಾಪನೆಯ ಆರಂಭದಲ್ಲೇ ದೇವರ ಅಪ್ಪಣೆಯಾಯಿತು. ಅದನ್ನು ನಿನಗೆ ತಿಳಿಸಲು ಇಲ್ಲಿಗೆ ಬಂದಿದ್ದೇನೆ. ನೀನು ದೇವರಿಗೆ ಅತಿ ಪ್ರಿಯನು. ಈ ದೈವೋಕ್ತಿಯನ್ನು ಆಲೋಚಿಸು; ಈ ದರ್ಶನವನ್ನು ಗ್ರಹಿಸಿಕೊ:",
			"24": "<sup>24</sup> “ಅಧರ್ಮವನ್ನು ಕೊನೆಗಾಣಿಸುವುದಕ್ಕೆ ಪಾಪಗಳನ್ನು ತೀರಿಸುವುದಕ್ಕೆ, ಅಪರಾಧವನ್ನು ನಿವಾರಿಸುವುದಕ್ಕೆ, ಸನಾತನ ಧರ್ಮವನ್ನು ಸ್ಥಾಪಿಸುವುದಕ್ಕೆ, ದರ್ಶನವನ್ನೂ ಪ್ರವಾದಿಯ ನುಡಿಯನ್ನೂ ಮುದ್ರೆಹಾಕಿ ಯಥಾರ್ಥ ಮಾಡುವುದಕ್ಕೆ, ಅತಿಪರಿಶುದ್ಧವಾದುದನ್ನು ಅಭಿಷೇಕಿಸುವುದಕ್ಕೆ ನಿನ್ನ ಜನಕ್ಕೂ ನಿನ್ನ ಪವಿತ್ರನಗರಕ್ಕೂ ಏಳೆಪ್ಪತ್ತು ವರ್ಷಗಳು ಕಳೆಯಬೇಕೆಂದು ನಿಷ್ಕರ್ಷೆಯಾಗಿದೆ.",
			"25": "<sup>25</sup> ಇದನ್ನು ತಿಳಿದು ಮನದಟ್ಟು ಮಾಡಿಕೊ, ‘ಹಿಂದಿರುಗಿ ಜೆರುಸಲೇಮನ್ನು ಪುನರ್‍ ನಿರ್ಮಿಸಿ’ ಎಂಬ ದೈವೋಕ್ತಿ ಹೊರಡುವಂದಿನಿಂದ ಪ್ರಭುವಾಗಿ ಅಭಿಷಿಕ್ತನಾದವನು ಬರುವುದರೊಳಗೆ ಏಳು ಸಾರಿ ಏಳು ವರ್ಷಗಳು ಕಳೆಯಬೇಕು. ಅದು ಪುನಃ ಬೀದಿ ಚೌಕಗಳಿಂದಲೂ ಕೋಟೆಕೊತ್ತಲುಗಳಿಂದಲೂ ಕಟ್ಟಲ್ಪಟ್ಟು ಏಳು ಸಾರಿ ಅರವತ್ತೆರಡು ವರ್ಷಗಳು ಇರುವುದು. ಆ ಕಾಲವು ಬಹು ಕಷ್ಟಕರವಾದ ಕಾಲವಾಗಿರುವುದು.",
			"26": "<sup>26</sup> ಆ ಏಳುಸಾರಿ ಅರವತ್ತೆರಡು ವರ್ಷಗಳು ಮುಗಿದ ಮೇಲೆ ಅಭಿಷಿಕ್ತನನ್ನು ಅನ್ಯಾಯವಾಗಿ ಕೊಲ್ಲುವರು. ದಂಡೆತ್ತಿಬರುವ ರಾಜನ ಜನರು ನಗರವನ್ನೂ ಪವಿತ್ರಾಲಯವನ್ನೂ ಹಾಳುಮಾಡುವರು. ಪ್ರಳಯದಿಂದಲೋ ಎಂಬಂತೆ ನಗರವು ನಾಶವಾಗುವುದು. ಅಂತ್ಯದವರೆಗೆ ಯುದ್ಧವೂ ನಿಶ್ಚಿತ ವಿನಾಶವೂ ಸಂಭವಿಸುವುವು.",
			"27": "<sup>27</sup> ಆ ರಾಜನು ಏಳು ವರ್ಷ ಗಳ ಮಟ್ಟಿಗೆ ಬಹುಜನರೊಂದಿಗೆ ದೃಢವಾದ ಒಡಂಬಡಿಕೆಯನ್ನು ಮಾಡಿಕೊಳ್ಳುವನು. ಈ ಅವಧಿಯ ಅರ್ಧಭಾಗದಲ್ಲಿ ಬಲಿನೈವೇದ್ಯಗಳನ್ನು ನಿಲ್ಲಿಸಿಬಿಡುವನು. ದೇವಾಲಯದ ರೆಕ್ಕೆಯ ಮೇಲೆ ಅಸಹ್ಯವಾದುದನ್ನು ಸ್ಥಾಪಿಸಲಾಗುವುದು. ಅವನ್ನು ಅಲ್ಲಿ ಇಟ್ಟು ಅಶುದ್ಧಗೊಳಿಸಿದ ಘಾತುಕನು ನಿಶ್ಚಿತ ಪ್ರಳಯದಲ್ಲಿ ಮುಳುಗುವ ತನಕ ಅವು ಅಲ್ಲೇ ಇರುವುವು.”"
		},
		"10": {
			"1": "<sup>1</sup> ಪರ್ಷಿಯದ ರಾಜನಾದ ಸೈರೆಷನ ಆಳ್ವಿಕೆಯ ಮೂರನೇ ವರ್ಷದಲ್ಲಿ ಬೇಲ್ತೆಶಚ್ಚೆರನೆಂಬ ದಾನಿಯೇಲನಿಗೆ ಒಂದು ಸಂಗತಿ ಪ್ರಕಟವಾಯಿತು. ಅದು ಮಹಾ ಹೋರಾಟದ ಸಂಗತಿ, ಸತ್ಯವಾದ ಸಂಗತಿ. ಅದರ ಅರ್ಥವನ್ನು ತಾನು ಕಂಡ ದರ್ಶನದಿಂದ ಮನದಟ್ಟು ಮಾಡಿಕೊಂಡನು.",
			"2": "<sup>2</sup> ಆ ದಿನಗಳಲ್ಲಿ ದಾನಿಯೇಲನಾದ ನಾನು ಮೂರು ವಾರ ಒಂದು ವ್ರತವನ್ನು ಕೈಗೊಂಡಿದ್ದೆ.",
			"3": "<sup>3</sup> ಆ ಮೂರು ವಾರ ಮುಗಿಯುವ ತನಕ ನಾನು ಯಾವ ರುಚಿಪದಾರ್ಥವನ್ನೂ ತಿನ್ನಲಿಲ್ಲ. ಮಾಂಸವನ್ನಾಗಲಿ, ದ್ರಾಕ್ಷಾರಸವನ್ನಾಗಲಿ ಬಾಯಲ್ಲಿ ಇಡಲಿಲ್ಲ. ಎಣ್ಣೆಯನ್ನೂ ಹಚ್ಚಿಕೊಳ್ಳಲಿಲ್ಲ.",
			"4": "<sup>4</sup> ಮೊದಲನೆಯ ತಿಂಗಳಿನ ಇಪ್ಪತ್ನಾಲ್ಕನೆಯ ದಿನದಲ್ಲಿ ನಾನು ಟೈಗ್ರಿಸ್ ಎಂಬ ಮಹಾನದಿಯ ದಡದ ಮೇಲೆ ನಿಂತಿದ್ದೆ.",
			"5": "<sup>5</sup> ಕಣ್ಣೆತ್ತಿ ನೋಡಲು, ನಾರುಬಟ್ಟೆಯನ್ನು ಹೊದ್ದುಕೊಂಡಿದ್ದ, ಊಫಜಿನ ಬಂಗಾರದ ಪಟ್ಟಿಯನ್ನು ಸೊಂಟಕ್ಕೆ ಬಿಗಿದುಕೊಂಡಿದ್ದ, ಒಬ್ಬ ವ್ಯಕ್ತಿ ನನಗೆ ಕಾಣಿಸಿದ.",
			"6": "<sup>6</sup> ಅವನ ಶರೀರ ಸುವರ್ಣರತ್ನದ ಹಾಗೆ ಕಂಗೊಳಿಸಿತು. ಅವನ ಮುಖ ಮಿಂಚಿನಂತೆ ಹೊಳೆಯಿತು. ಅವನ ಕಣ್ಣುಗಳು ಉರಿಯುವ ಪಂಜುಗಳೋಪಾದಿಯಲ್ಲಿ ಫಳಫಳಿಸಿದವು. ಅವನ ಕೈಕಾಲುಗಳು ಬೆಳಗಿದ ಕಂಚಿನ ಹಾಗೆ ಥಳಥಳಿಸಿದವು. ಅವನ ಮಾತಿನ ಶಬ್ದ ಜನಸಂದಣಿಯ ಕೋಲಾಹಲದಂತಿತ್ತು.",
			"7": "<sup>7</sup> ಆ ದರ್ಶನವನ್ನು ಕಂಡವನು ದಾನಿಯೇಲನಾದ ನಾನೊಬ್ಬನೇ. ನನ್ನೊಂದಿಗೆ ಇದ್ದವರು ಅದನ್ನು ಕಾಣಲಿಲ್ಲ. ಅವರು ಭಯಭ್ರಾಂತರಾಗಿ ಓಡಿಹೋಗಿ ಅವಿತುಕೊಂಡರು.",
			"8": "<sup>8</sup> ನಾನು ಒಂಟಿಯಾಗಿ ಉಳಿದು ಆ ಅದ್ಭುತವನ್ನು ಕಂಡೆ. ನನ್ನ ಶಕ್ತಿಯೆಲ್ಲಾ ಕರಗಿಹೋಯಿತು. ನನ್ನ ಗಾಂಭೀರ್ಯ ಅಳಿಯಿತು. ನಾನು ನಿತ್ರಾಣನಾದೆ.",
			"9": "<sup>9</sup> ಆದರೂ ಅವನು ಮಾತಾಡುವ ಶಬ್ದ ಕೇಳಿಸಿತು. ಅದು ನನ್ನ ಕಿವಿಗೆ ಬಿದ್ದಾಗ ನಾನು ಮೈಮರೆತು ಅಧೋಮುಖವಾಗಿ ಅಡ್ಡಬಿದ್ದೆ.",
			"10": "<sup>10</sup> ಆಗ ಯಾರದೋ ಕೈ ನನ್ನನ್ನು ಮುಟ್ಟಿದಂತಾಯಿತು. ನಾನು ಗಡಗಡನೆ ನಡುಗುತ್ತಾ ಅಂಗೈಗಳನ್ನೂ ಮೊಣಕಾಲುಗಳನ್ನೂ ಊರಿ ನಿಲ್ಲುವಂತೆ ಮಾಡಿತು.",
			"11": "<sup>11</sup> ಆಗ ಅವನು ನನಗೆ, “ಅತಿಪ್ರಿಯ ದಾನಿಯೇಲನೇ, ನಾನು ನುಡಿಯುವ ಮಾತುಗಳನ್ನು ಗ್ರಹಿಸು. ಎದ್ದು ನಿಂತುಕೋ. ನಾನು ನಿನ್ನ ಬಳಿಗೆ ಕಳುಹಿಸಲ್ಪಟ್ಟವನು,” ಎಂದನು.",
			"12": "<sup>12</sup> ಆಮೇಲೆ ಅವನು ನನಗೆ, “ದಾನಿಯೇಲನೇ, ಭಯಪಡಬೇಡ! ಏಕೆಂದರೆ ನೀನು ನಿನ್ನ ದೇವರ ಮುಂದೆ ವಿನಮ್ರಪೂರ್ವಕವಾಗಿ (ದೈವಸಂಕಲ್ಪವನ್ನು) ಅರ್ಥಮಾಡಿಕೊಳ್ಳಲು ಪ್ರಯತ್ನಿಸಿದ ಮೊದಲನೆಯ ದಿನದಲ್ಲೇ ನಿನ್ನ ಪ್ರಾರ್ಥನೆ ದೇವರಿಗೆ ಮುಟ್ಟಿತು. ಆ ಪ್ರಾರ್ಥನೆಯ ನಿಮಿತ್ತವೇ ನಾನು ಬಂದಿದ್ದೇನೆ.",
			"13": "<sup>13</sup> ಪರ್ಷಿಯ ರಾಜ್ಯದ ಕಾವಲುದೂತನು ಇಪ್ಪತ್ತೊಂದು ದಿನ ನನ್ನನ್ನು ಎದುರಿಸಿ ನಿಂತನು. ಆದರೆ ಪ್ರಧಾನ ದೂತರಲ್ಲಿ ಒಬ್ಬನಾದ ಮಿಕಾಯೇಲನು ನನ್ನ ಸಹಾಯಕ್ಕೆ ಬಂದನು. ಅಲ್ಲಿನ ಪರ್ಷಿಯ ರಾಜರ ಸಂಗಡ ಹೋರಾಡಲು ಅವನನ್ನು ಬಿಟ್ಟು ಬಂದಿದ್ದೇನೆ.",
			"14": "<sup>14</sup> ಅಂತ್ಯಕಾಲದಲ್ಲಿ ನಿನ್ನ ಜನರಿಗೆ ಬರಲಿರುವ ಗತಿಯನ್ನು ನಿನಗೆ ತಿಳಿಸುವುದಕ್ಕೋಸ್ಕರ ಬಂದೆ. ಆ ಕಾಲದ ಸಂಗತಿಯನ್ನು ವ್ಯಕ್ತಪಡಿಸುವ ಇನ್ನೊಂದು ದರ್ಶನವಿದೆ,” ಎಂದು ಹೇಳಿದನು.",
			"15": "<sup>15</sup> ಅವನು ಈ ಮಾತನ್ನು ನನಗೆ ಹೇಳಿದ ಕೂಡಲೆ ನಾನು ಮುಖವನ್ನು ತಗ್ಗಿಸಿಕೊಂಡು ನಿಶ್ಶಬ್ದನಾದೆ.",
			"16": "<sup>16</sup> ಆಗ ನರರೂಪ ಹೊಂದಿದ್ದ ಒಬ್ಬನು ನನ್ನ ತುಟಿಗಳನ್ನು ಮುಟ್ಟಿದ. ನಾನು ಬಾಯಿ ತೆರೆದು ನನ್ನ ಮುಂದೆ ನಿಂತಿದ್ದವನಿಗೆ, “ಎನ್ನೊಡೆಯಾ, ಈ ದರ್ಶನದಿಂದ ವೇದನೆಗಳು ನನ್ನನ್ನು ಆಕ್ರಮಿಸಿವೆ, ನಾನು ನಿತ್ರಾಣನಾಗಿದ್ದೇನೆ.",
			"17": "<sup>17</sup> ನನ್ನೊಡೆಯನ ದಾಸನಾದ ನನ್ನಂಥವನು ಎನ್ನೊಡೆಯರಾದ ತಮ್ಮಂಥವರ ಸಂಗಡ ಹೇಗೆ ಮಾತಾಡಬಹುದು? ಈಗ ಶಕ್ತಿಯನ್ನೆಲ್ಲಾ ಕಳೆದುಕೊಂಡಿದ್ದೇನೆ, ನನ್ನಲ್ಲಿ ಉಸಿರೇ ಇಲ್ಲವಾಗಿದೆ,” ಎಂದು ಹೇಳಿದೆ.",
			"18": "<sup>18</sup> ಮನುಷ್ಯರೂಪ ಹೊಂದಿದ್ದ ಆ ವ್ಯಕ್ತಿ ಪುನಃ ನನ್ನನ್ನು ಮುಟ್ಟಿ ಬಲಪಡಿಸಿದ.",
			"19": "<sup>19</sup> ಬಳಿಕ ಆ ವ್ಯಕ್ತಿ ನನಗೆ, “ಅತಿಪ್ರಿಯನೇ, ಭಯಪಡಬೇಡ. ನಿನಗೆ ಶಾಂತಿಸಮಾಧಾನವಿರಲಿ! ಧೈರ್ಯತಂದುಕೊ! ಧೈರ್ಯತಂದುಕೊ!” ಎಂದು ಹೇಳಿದ. ಅವನ ಈ ಮಾತನ್ನು ಕೇಳಿದ ಕೂಡಲೆ ನಾನು ಧೈರ್ಯದಿಂದ, “ಎನ್ನೊಡೆಯಾ, ಮಾತಾಡು. ನನಗೆ ಧೈರ್ಯತುಂಬಿರುವೆ,” ಎಂದು ಅರಿಕೆಮಾಡಿದೆ.",
			"20": "<sup>20</sup> ಅವನು ನನಗೆ ಹೀಗೆಂದು ಹೇಳಿದ: “ನಾನು ನಿನ್ನ ಬಳಿಗೆ ಏಕೆ ಬಂದೆನೆಂಬುದು ನಿನಗೆ ಗೊತ್ತಲ್ಲವೇ? ಈಗ ನಾನು ಪರ್ಷಿಯ ರಾಜ್ಯದ ಕಾವಲುದೂತನೊಂದಿಗೆ ಹೋರಾಡಲು ಹಿಂದಿರುಗಬೇಕು. ನಾನು ಆ ಹೋರಾಟವನ್ನು ತೀರಿಸಿದ ಕೂಡಲೆ ಗ್ರೀಕ್ ರಾಜ್ಯದ ಕಾವಲುದೂತನು ಎದುರುಬೀಳುವನು.",
			"21": "<sup>21</sup> ಸತ್ಯಗ್ರಂಥದಲ್ಲಿ ಲಿಖಿತವಾದುದನ್ನು ಈಗ ನಿನಗೆ ತಿಳಿಸುತ್ತೇನೆ. ಇವರಿಬ್ಬರೊಂದಿಗೆ ಹೋರಾಡುವಲ್ಲಿ ನಿಮ್ಮ ಕಾವಲುದೂತನಾದ ಮಿಕಾಯೇಲನ ಹೊರತು ನನಗೆ ಬೆಂಬಲಿಗರಾಗತಕ್ಕವರು ಇನ್ನಾರೂ ಇಲ್ಲ."
		},
		"11": {
			"1": "<sup>1</sup> “ಮೇದ್ಯನಾದ ದಾರ್ಯಾವೆಷನ ಆಳ್ವಿಕೆಯ ಮೊದಲನೆಯ ವರ್ಷದಲ್ಲಿ ನಾನೇ ಮಿಕಾಯೇಲನಿಗೆ ಬೆಂಬಲಕೊಟ್ಟು ಆಶ್ರಯನಾಗಿ ನಿಂತೆ.",
			"2": "<sup>2</sup> “ಈಗ ಸತ್ಯಾಂಶವನ್ನು ತಿಳಿಸುತ್ತೇನೆ, ಕೇಳು: ಪರ್ಷಿಯ ದೇಶದಲ್ಲಿ ಇನ್ನೂ ಮೂವರು ರಾಜರು ಏಳುವರು. ನಾಲ್ಕನೆಯ ರಾಜನು ಎಲ್ಲರಿಗಿಂತಲೂ ಅಧಿಕ ಧನವಂತನಾಗಿರುವನು. ಅವನು ತನ್ನ ಧನದಿಂದ ಪ್ರಬಲನಾಗಿ ಗ್ರೀಕ್ ರಾಜ್ಯಕ್ಕೆ ವಿರುದ್ಧ ತನ್ನ ಬಲವನ್ನೆಲ್ಲ ಪ್ರಯೋಗಿಸುವನು.",
			"3": "<sup>3</sup> “ಅನಂತರ ಪರಾಕ್ರಮಶಾಲಿಯಾದ ಒಬ್ಬ ರಾಜನು ಎದ್ದು ಮಹಾಪ್ರಭುತ್ವದಿಂದ ಆಳುತ್ತಾ ತನ್ನ ಇಷ್ಟಾರ್ಥಗಳನ್ನು ಸಾಧಿಸಿಕೊಳ್ಳುವನು.",
			"4": "<sup>4</sup> ಆದರೆ ಅವನು ತಲೆಯೆತ್ತಿದ ಮೇಲೆ ಅವನ ರಾಜ್ಯ ಒಡೆದು ನಾಲ್ಕು ದಿಕ್ಕುಗಳಿಗೂ ಸೀಳಿಹೋಗುವುದು. ಅದು ಅವನ ಸಂತತಿಗೆ ಭಾಗವಾಗದು. ಅವನ ಆಳ್ವಿಕೆಯಲ್ಲಿ ಪ್ರಬಲವಾಗಿ ಇದ್ದಂತೆ ಅದು ಇನ್ನು ಪ್ರಬಲವಾಗದು.",
			"5": "<sup>5</sup> “ದಕ್ಷಿಣ ದಿಕ್ಕಿನ ರಾಜನು ಮತ್ತು ಅವನ ದಳವಾಯಿಗಳಲ್ಲಿ ಒಬ್ಬನು ಶಕ್ತಿಶಾಲಿಗಳಾಗುವರು. ದಳವಾಯಿ ರಾಜನಿಗಿಂತ ಶಕ್ತಿಶಾಲಿಯಾಗಿ ಪ್ರಭುತ್ವಕ್ಕೆ ಬರುವನು. ಅವನ ರಾಜ್ಯ ದೊಡ್ಡ ರಾಜ್ಯವಾಗುವುದು.",
			"6": "<sup>6</sup> ಕೆಲವು ವರ್ಷಗಳಾದ ಮೇಲೆ ಅವರು ಒಪ್ಪಂದ ಮಾಡಿಕೊಳ್ಳುವರು. ಇದನ್ನು ದೃಢಪಡಿಸಿಕೊಳ್ಳಲು ಆ ದಕ್ಷಿಣ ರಾಜನ ಕುಮಾರಿ ಉತ್ತರ ದಿಕ್ಕಿನ ರಾಜನನ್ನು ಸೇರುವಳು. ಆದರೂ ತನ್ನ ಸಂಬಂಧವನ್ನು ಉಳಿಸಿಕೊಳ್ಳನು. ಅವನೂ ಅವನ ಸಂತಾನವೂ ನಿಲ್ಲವು. ಅವಳು, ಅವಳನ್ನು ಕರೆತಂದವರು, ಪಡೆದವನು, ಕರೆದುಕೊಂಡವನು ಇವರೆಲ್ಲರೂ ಅಂದು ನಾಶಕ್ಕೀಡಾಗುವರು.",
			"7": "<sup>7</sup> ಅನಂತರ ಅವಳು ಹುಟ್ಟಿದ ಬುಡದಿಂದ ಒಡೆದ ಸಸಿಯೊಂದು ಅದರ ಸ್ಥಾನದಲ್ಲಿ ನಿಲ್ಲುವುದು. ಉತ್ತರರಾಜನ ಸೈನ್ಯಕ್ಕೆ ವಿರುದ್ಧವಾಗಿ ಹೊರಟು, ಅವನ ದುರ್ಗದೊಳಗೆ ನುಗ್ಗಿ, ಅಲ್ಲಿನವರಿಗೆ ಮಾಡುವಷ್ಟೂ ಮಾಡಿ ಗೆಲ್ಲುವನು.",
			"8": "<sup>8</sup> ಅವರ ದೇವರುಗಳನ್ನೂ ಎರಕದ ಬೊಂಬೆಗಳನ್ನೂ ಒಳ್ಳೊಳ್ಳೆ ಬೆಳ್ಳಿಬಂಗಾರದ ಪಾತ್ರೆಗಳನ್ನೂ ಸೂರೆಮಾಡಿಕೊಂಡು ಈಜಿಪ್ಟಿಗೆ ಹಿಂದಿರುಗುವನು. ಕೆಲವು ವರ್ಷಗಳ ತನಕ ಉತ್ತರರಾಜನ ಗೊಡವೆಗೆ ಹೋಗನು.",
			"9": "<sup>9</sup> ಬಳಿಕ ಉತ್ತರರಾಜನು ದಕ್ಷಿಣರಾಜನ ರಾಜ್ಯದ ಮೇಲೆ ದಾಳಿಮಾಡುವನು. ಆದರೆ ಅವನು ಸ್ವದೇಶಕ್ಕೆ ಹಿಂದಿರುಗಬೇಕಾಗುವುದು.",
			"10": "<sup>10</sup> ಆಮೇಲೆ ಉತ್ತರರಾಜನ ಮಕ್ಕಳು ಯುದ್ಧಸನ್ನಾಹ ಮಾಡಿ ಮಹಾವ್ಯೂಹವಾದ ದೊಡ್ಡ ಸೈನ್ಯವನ್ನು ಕೂಡಿಸುವರು. ಆ ಸೈನ್ಯ ಬೆಳೆದು ತುಂಬಿತುಳುಕುವ ಪ್ರವಾಹದಂತೆ ಹಬ್ಬಿಕೊಳ್ಳುವುದು. ಅವರು ಮತ್ತೆ ಯುದ್ಧಕ್ಕೆ ಹೊರಟು ದಕ್ಷಿಣರಾಜನ ದುರ್ಗದವರೆಗೆ ನುಗ್ಗುವರು.",
			"11": "<sup>11</sup> ಆಗ ದಕ್ಷಿಣರಾಜನು ಕ್ರೋಧದಿಂದುರಿಯುತ್ತಾ ಹೊರಟುಬಂದು ಉತ್ತರರಾಜನ ಸಂಗಡ ಯುದ್ಧಮಾಡುವನು. ಉತ್ತರರಾಜನು ಮಹಾವ್ಯೂಹವನ್ನು ಕಟ್ಟಿದರೂ ಅದೆಲ್ಲವು ದಕ್ಷಿಣರಾಜನ ಕೈವಶವಾಗುವುದು.",
			"12": "<sup>12</sup> ಇದರಿಂದಾಗಿ ಅವನು ಗರ್ವಿಷ್ಠನಾಗುವನು. ಲಕ್ಷಾಂತರ ಸೈನಿಕರನ್ನು ಸದೆಬಡಿದಿದ್ದರೂ ಪ್ರಾಬಲ್ಯಕ್ಕೆ ಬಾರನು.",
			"13": "<sup>13</sup> “ತರುವಾಯ ಉತ್ತರರಾಜನು ಹಿಂದಿನ ದಂಡಿಗಿಂತ ದೊಡ್ಡ ದಂಡನ್ನು ಮತ್ತೆ ಕೂಡಿಸಿಕೊಂಡು ಬಹಳ ವರ್ಷಗಳು ಕಳೆದನಂತರ ಮಹಾಸೈನ್ಯ ಸಮೇತ ಅಧಿಕ ಸನ್ನಾಹದಿಂದ ಬರುವನು.",
			"14": "<sup>14</sup> ಆ ಕಾಲದಲ್ಲಿ ಅನೇಕರು ದಕ್ಷಿಣರಾಜನಿಗೆ ಎದುರು ನಿಲ್ಲುವರು. ಇದಲ್ಲದೆ, ನಿನ್ನ ಸ್ವಂತಜನರಲ್ಲಿ ಹಿಂಸಾಪ್ರಿಯರು ಆ ದರ್ಶನವನ್ನು ನಿಜವಾಗಿಸಬೇಕೆಂದು ದಂಗೆ ಏಳುವರು. ಆದರೆ ಸೋತುಹೋಗುವರು.",
			"15": "<sup>15</sup> ಉತ್ತರರಾಜನು ಬಂದು, ಮುತ್ತಿಗೆಹಾಕಿ, ಕೋಟೆಕೊತ್ತಲದ ನಗರವನ್ನು ಹಿಡಿಯುವನು. ದಕ್ಷಿಣದ ಭುಜಬಲವು ಅವನನ್ನು ನಿಲ್ಲಿಸಲಾರದು. ಅಲ್ಲಿನ ಮಹಾವೀರರು ತಡೆಯಲಾರರು. ಎದುರಿಸುವ ಯಾವ ಶಕ್ತಿಯೂ ಇರದು.",
			"16": "<sup>16</sup> ದಕ್ಷಿಣದರಾಜನಿಗೆ ವಿರುದ್ಧ ಬರುವವನು ಇಚ್ಛಾನುಸಾರ ವರ್ತಿಸುವನು. ಅವನನ್ನು ಎದುರಿಸಬಲ್ಲವನು ಒಬ್ಬನೂ ಇರನು. ಆ ‘ಚೆಲುವಿನ ನಾಡನ್ನೂ’ ಗೆದ್ದು ಅದನ್ನು ಪೂರ್ತಿಯಾಗಿ ತನ್ನ ಕೈಹಿಡಿತದಲ್ಲಿ ಇಟ್ಟುಕೊಳ್ಳುವನು.",
			"17": "<sup>17</sup> “ಉತ್ತರದ ರಾಜನು ದಕ್ಷಿಣರಾಜನ ಮೇಲೆ ಬೀಳಬೇಕೆಂದು ನಿರ್ಧರಿಸಿ ತನ್ನ ರಾಜ್ಯದ ಸಮಸ್ತ ಶಕ್ತಿಸಮೇತನಾಗಿ ಹೊರಟು ಅವನ ಸಂಗಡ ಒಪ್ಪಂದ ಮಾಡಿಕೊಳ್ಳುವನು. ಅವನ ರಾಜ್ಯದ ಹಾನಿಗಾಗಿಯೆ ಹೆಣ್ಣನ್ನು ಕೊಡುವನು. ಆದರೆ ಈ ಉಪಾಯ ಫಲಿಸದು. ಅದರಿಂದ ತನಗೂ ಅನುಕೂಲವಾಗದು.",
			"18": "<sup>18</sup> ಆಮೇಲೆ ಅವನು ಕರಾವಳಿಯ ನಾಡುಗಳ ಕಡೆಗೆ ಕಣ್ಣಿಟ್ಟು ಅಲ್ಲಿಯ ಅನೇಕ ನಾಡುಗಳನ್ನು ಆಕ್ರಮಿಸುವನು. ಆದರೆ ದಳವಾಯಿ ಒಬ್ಬನು ಅವನ ಅಟ್ಟಹಾಸವನ್ನು ತಡೆಗಟ್ಟುವನು. ಅವನು ಮಾಡುವ ಹಾನಿ ಅವನಿಗೇ ತಗಲುವಂತೆ ಮಾಡುವನು.",
			"19": "<sup>19</sup> ಬಳಿಕ ರಾಜನು ಸ್ವದೇಶದ ದುರ್ಗಗಳ ಕಡೆಗೆ ಹಿಂದಿರುಗುವನು. ಆದರೆ ಎಡವಿಬಿದ್ದು ಅಳಿದುಹೋಗುವನು.",
			"20": "<sup>20</sup> “ಅವನ ಸ್ಥಾನದಲ್ಲಿ ಮತ್ತೊಬ್ಬನು ರಾಜ್ಯವಾಳುವನು. ಇವನು ರಾಜ್ಯದಲ್ಲಿನ ಶಿರೋಮಣಿಯಂಥ ನಾಡನ್ನೆಲ್ಲ ದೋಚಿಕೊಳ್ಳಬಲ್ಲ ಉದ್ಯೋಗಿಯನ್ನು ನೇಮಿಸುವನು. ಆದರೆ ಕೆಲವೇ ದಿನಗಳಲ್ಲಿ ಅವನು ಕದನದಿಂದ ಅಲ್ಲ, ಕೋಪದಿಂದ ಅಲ್ಲ, ಹಾಗೆಯೇ ಮಡಿದುಹೋಗುವನು.",
			"21": "<sup>21</sup> “ಅವನ ಸ್ಥಾನದಲ್ಲಿ ನೀಚನೊಬ್ಬನು ಏಳುವನು. ಅವನು ರಾಜಪದವಿಗೆ ಹಕ್ಕುದಾರನಲ್ಲ. ಆದರೂ ನೆಮ್ಮದಿಯ ಕಾಲದಲ್ಲಿ ನಯವಾದ ನುಡಿಗಳಿಂದ ರಾಜ್ಯವನ್ನು ಪಡೆದುಕೊಳ್ಳುವನು.",
			"22": "<sup>22</sup> ಅವನನ್ನು ಎದುರಿಸುವ ದೊಡ್ಡ ದೊಡ್ಡ ಸೈನ್ಯಗಳು ಹಾಗು ದೇವರ ಪ್ರಧಾನ ಯಾಜಕನು ಅವನ ಸೈನ್ಯ ಪ್ರವಾಹಕ್ಕೆ ಸಿಕ್ಕಿ ಅದರಲ್ಲಿ ಮುಳುಗಿ ನಾಶವಾಗುವರು.",
			"23": "<sup>23</sup> ಅವನ ಸಂಗಡ ಒಪ್ಪಂದ ಮಾಡಿಕೊಂಡವನಿಗೆ ಕೂಡಲೆ ಮೋಸಮಾಡುವನು. ಕೊಂಚ ಜನರ ಸಹಾಯದಿಂದಲೆ ಏಳಿಗೆಯಾಗಿ ಬಲಗೊಳ್ಳುವನು.",
			"24": "<sup>24</sup> ನೆಮ್ಮದಿಯ ಕಾಲದಲ್ಲಿ ಸಂಸ್ಥಾನದ ಫಲವತ್ತಾದ ಪ್ರದೇಶಗಳ ಮೇಲೆ ದಾಳಿಮಾಡುವನು. ತಂದೆತಾತಂದಿರು ಮಾಡದಂಥ ಕಾರ್ಯಗಳನ್ನು ಮಾಡುವನು. ಸೂರೆ-ಸುಲಿಗೆ-ಕೊಳ್ಳೆಹೊಡೆದವುಗಳನ್ನು ತನ್ನವರಿಗೆ ಚೆಲ್ಲಿಬಿಡುವನು. ಕೋಟೆಗಳನ್ನು ಹಿಡಿಯುವ ಕುತಂತ್ರಗಳನ್ನು ಕೊನೆಯವರೆಗೂ ಮುಂದುವರೆಸುವನು.",
			"25": "<sup>25</sup> “ಅವನು ಧೈರ್ಯತಂದುಕೊಂಡು, ದೊಡ್ಡ ದಂಡೆತ್ತಿ, ದಕ್ಷಿಣರಾಜನಿಗೆ ವಿರುದ್ಧ ತನ್ನ ರಾಜ್ಯದ ಶಕ್ತಿಯನ್ನೆಲ್ಲ ಕೂಡಿಸುವನು. ದಕ್ಷಿಣರಾಜನಾದರೋ ಅತ್ಯಧಿಕ ಬಲವುಳ್ಳ ಮಹಾಸೈನ್ಯ ಸಮೇತನಾಗಿ ಯುದ್ಧಕ್ಕೆ ಹೊರಡುವನು. ಆದರೆ ಜಯಗಳಿಸನು. ಕುಯುಕ್ತಿಗಳಿಗೆ ಗುರಿಯಾಗಿ ಸೋಲನ್ನು ಅಪ್ಪಬೇಕಾಗುವುದು.",
			"26": "<sup>26</sup> ಅವನ ಮೃಷ್ಟಾನ್ನ ತಿಂದವರೇ ಅವನಿಗೆ ಎರಡು ಬಗೆಯುವರು. ಅವನ ಸೈನ್ಯವು ಕೊಚ್ಚಿಕೊಂಡುಹೋಗುವುದು. ಬಹುಜನರು ಹತರಾಗಿ ಬೀಳುವರು.",
			"27": "<sup>27</sup> ಆ ಇಬ್ಬರು ರಾಜರು ಪರಸ್ಪರ ಕೇಡಿನ ಮನಸ್ಸು ಉಳ್ಳವರಾಗಿ ಸಹಪಂಕ್ತಿಯಲ್ಲಿ ಕುಳಿತಿದ್ದರೂ ಸುಳ್ಳುಪೊಳ್ಳು ಮಾತಾಡಿಕೊಳ್ಳುವರು. ಆದರೆ ನಿಶ್ಚಿತಕಾಲ ಬರುವ ತನಕ ಅವರಿಂದ ಏನೂ ಸಾಧಿಸಲಾಗದು.",
			"28": "<sup>28</sup> ಅನಂತರ ಉತ್ತರದ ಆ ನೀಚರಾಜನು ಬಹು ಆಸ್ತಿಯನ್ನು ಕೊಳ್ಳೆಹೊಡೆದು ಸ್ವದೇಶಕ್ಕೆ ಹಿಂತಿರುಗುವನು. ಅವನ ಮನಸ್ಸು ಪವಿತ್ರ ಒಡಂಬಡಿಕೆಗೆ ವಿರುದ್ಧವಾಗಿರುವುದು. ಇಷ್ಟಬಂದ ಹಾಗೆ ವರ್ತಿಸಿ ಮತ್ತೆ ಸ್ವದೇಶವನ್ನು ಸೇರುವನು.",
			"29": "<sup>29</sup> “ನಿಶ್ಚಿತ ಕಾಲದಲ್ಲಿ ಪುನಃ ದಕ್ಷಿಣದೇಶದ ಮೇಲೆ ದಾಳಿಮಾಡುವನು. ಆದರೆ ಮೊದಲು ಆದಂತೆಯೇ ಎರಡನೇಯ ಸಲ ಆಗದು.",
			"30": "<sup>30</sup> ಅವನಿಗೆ ವಿರುದ್ಧವಾಗಿ ರೋಮಿನ ಹಡಗುಗಳು ಬರಲು ಅವನು ಎದೆಗುಂದಿ ಹಿಂದಿರುಗುವನು. ಪವಿತ್ರ ಒಡಂಬಡಿಕೆಯ ವಿರುದ್ಧ ಮತ್ಸರಗೊಂಡು ಸಾಧ್ಯವಾದಷ್ಟೂ ಹಾನಿಮಾಡುವನು. ಸ್ವದೇಶಕ್ಕೆ ಹಿಂದಿರುಗಿ ಪವಿತ್ರ ಒಡಂಬಡಿಕೆಯನ್ನು ತೊರೆದವರಿಗೆ ಆದರ ತೋರುವನು.",
			"31": "<sup>31</sup> ಅವನು ಕೂಡಿಸುವ ಸೈನ್ಯವು ಆಶ್ರಯದುರ್ಗವಾದ ಪವಿತ್ರಾಲಯವನ್ನು ಹೊಲೆಗೆಡಿಸುವುದು. ಅನುದಿನದ ಬಲಿಯನ್ನು ನಿಲ್ಲಿಸುವುದು. ವಿನಾಶಕರ ವಿಕಟ ಮೂರ್ತಿಯನ್ನು ಪ್ರತಿಷ್ಠಾಪಿಸುವುದು.",
			"32": "<sup>32</sup> ಒಡಂಬಡಿಕೆಯ ದ್ರೋಹಿಗಳು ಅವನ ನಯನುಡಿಗೆ ಮಾರುಹೋಗುವರು. ತಮ್ಮ ದೇವರನ್ನು ಅರಿತವರೋ ದೃಢಚಿತ್ತರಾಗಿ ಕೃತಾರ್ಥರಾಗುವರು.",
			"33": "<sup>33</sup> ಜನರಲ್ಲಿನ ಬುದ್ಧಿವಂತ ನಾಯಕರು ಅನೇಕರಿಗೆ ಬುದ್ಧಿಹೇಳುವರು. ಕೆಲಕಾಲ ಅವರು ಕತ್ತಿ-ಬೆಂಕಿ-ಸೆರೆ-ಸೂರೆಗಳಿಗೆ ತುತ್ತಾಗುವರು.",
			"34": "<sup>34</sup> ಹೀಗಾಗುವಾಗ ಅವರಿಗೆ ಅಲ್ಪಸ್ವಲ್ಪ ಸಹಾಯ ದೊರೆಯುವುದು. ಆ ಬಳಿಕ ಬಹುಮಂದಿ ನಯನವಿರಾದ ಮಾತುಗಳನ್ನಾಡುತ್ತಾ ಅವರನ್ನು ಸೇರಿಕೊಳ್ಳುವರು.",
			"35": "<sup>35</sup> ಅಂತ್ಯಕಾಲದವರೆಗೆ ಬುದ್ಧಿವಂತ ನಾಯಕರಲ್ಲಿ ಕೆಲವರು ಸಾವಿಗೆ ತುತ್ತಾಗುವರು. ಇದರಿಂದ ಜನರು ಶೋಧಿಸಲ್ಪಟ್ಟು, ಶುದ್ಧಿಹೊಂದಿ ಶುಭ್ರರಾಗುವರು. ಅಂತ್ಯವು ಕ್ಲುಪ್ತಕಾಲದಲ್ಲೇ ಬರುವುದು.",
			"36": "<sup>36</sup> “(ಉತ್ತರದ) ರಾಜನು ಮನಸ್ಸು ಬಂದ ಹಾಗೆ ನಡೆದು ತಾನು ಎಲ್ಲ ದೇವರುಗಳಿಗಿಂತಲೂ ದೊಡ್ಡವನೆಂದು ತನ್ನನ್ನೇ ಹೆಚ್ಚಿಸಿಕೊಂಡು ಗರ್ವಿಷ್ಠನಾಗುವನು. ದೇವಾಧಿದೇವರನ್ನು ಅತಿಯಾಗಿ ದೂಷಿಸುವನು. ನಿಮ್ಮ ಮೇಲಿನ ದೈವಕೋಪ ತೀರುವ ತನಕ ಅವನು ವೃದ್ಧಿಯಾಗುವನು. ದೈವಸಂಕಲ್ಪ ನೆರವೇರಲೇಬೇಕು.",
			"37": "<sup>37</sup> ಅವನು ತನ್ನ ಪೂರ್ವಜರ ದೇವರನ್ನಾಗಲಿ, ಮಹಿಳೆಯರು ಮೋಹಿಸುವ ದೇವರನ್ನಾಗಲಿ, ಯಾವ ದೇವರನ್ನೇ ಆಗಲಿ, ಲಕ್ಷಿಸನು. ಎಲ್ಲ ದೇವರುಗಳಿಗಿಂತ ತನ್ನನ್ನೇ ಹೆಚ್ಚಿಸಿಕೊಳ್ಳುವನು.",
			"38": "<sup>38</sup> ಕುಲದೇವರಿಗೆ ಬದಲಾಗಿ ದುರ್ಗ ದೇವರನ್ನು ಘನಪಡಿಸುವನು. ಪೂರ್ವಜರಿಗೆ ತಿಳಿಯದ ದೇವರನ್ನು ಬೆಳ್ಳಿಬಂಗಾರದಿಂದಲೂ ರತ್ನಗಳಿಂದಲೂ ಅಮೂಲ್ಯ ಕೊಡುಗೆಗಳಿಂದಲೂ ಗೌರವಿಸುವನು.",
			"39": "<sup>39</sup> ಅನ್ಯದೇವರ ಸಹಾಯದಿಂದ ಬಲವಾದ ಕೋಟೆಗಳನ್ನು ಆಕ್ರಮಿಸುವನು. ತನಗೆ ಮೆಚ್ಚುಗೆಯಾದವರನ್ನು ಹೆಚ್ಚಾಗಿ ಸತ್ಕರಿಸುವನು. ಅವರನ್ನು ಬಹುಜನರ ಮೇಲೆ ಅಧಿಕಾರಿಗಳನ್ನಾಗಿ ನೇಮಿಸುವನು. ಹಣಕ್ಕಾಗಿ ದೇಶವನ್ನೇ ಹಂಚಿಬಿಡುವನು.",
			"40": "<sup>40</sup> “ಅಂತ್ಯಕಾಲದಲ್ಲಿ ದಕ್ಷಿಣರಾಜನು ಉತ್ತರರಾಜನ ಮೇಲೆ ಬೀಳುವನು. ಆದರೆ ಉತ್ತರರಾಜನು ರಥಾಶ್ವಬಲಗಳಿಂದಲೂ ಬಹು ನಾವೆಗಳಿಂದಲೂ ಕೂಡಿದವನಾಗಿ ದಕ್ಷಿಣರಾಜನ ಮೇಲೆ ಧಾಳಿಮಾಡಿ ನಾಡುನಾಡುಗಳನ್ನು ನುಗ್ಗಿ, ಪ್ರವಾಹದಂತೆ ಹರಡಿಕೊಳ್ಳುವನು.",
			"41": "<sup>41</sup> ಆ ‘ಚೆಲುವಿನ ನಾಡಿಗೂ’ ನುಗ್ಗುವನು. ಅನೇಕ ಸೀಮೆಗಳು ಅವನಿಂದ ನಾಶವಾಗುವುವು. ಆದರೆ ಎದೋಮ್ಯರು, ಮೋವಾಬ್ಯರು, ಅಮ್ಮೋನಿನ ಹೆಚ್ಚಿನ ಭಾಗದವರು ಅವನ ಕೈಯಿಂದ ತಪ್ಪಿಸಿಕೊಳ್ಳುವರು.",
			"42": "<sup>42</sup> ಬೇರೆ ದೇಶಗಳ ಮೇಲೂ ಕೈಮಾಡುವನು; ಈಜಿಪ್ಟ್ ದೇಶವು ಅವನ ಕೈಯಿಂದ ತಪ್ಪಿಸಿಕೊಳ್ಳದು.",
			"43": "<sup>43</sup> ಬೆಳ್ಳಿಬಂಗಾರಗಳ ನಿಧಿನಿಕ್ಷೇಪಗಳನ್ನು ಹಾಗು ಈಜಿಪ್ಟಿನ ಒಳ್ಳೊಳ್ಳೆಯ ವಸ್ತುಗಳೆಲ್ಲವನ್ನು ವಶಮಾಡಿಕೊಳ್ಳುವನು. ಲಿಬ್ಯ ಮತ್ತು ಸುಡಾನಿನವರು ಅವನನ್ನು ಹಿಂಬಾಲಿಸುವರು.",
			"44": "<sup>44</sup> ಹೀಗಿರಲು ಪೂರ್ವದಿಂದ ಮತ್ತು ಉತ್ತರದಿಂದ ಬರುವ ಸುದ್ದಿ ಅವನನ್ನು ಬಾಧಿಸುವುದು. ಅವನು ಅತಿರೋಷಗೊಂಡು ಬಹುಜನರನ್ನು ಧ್ವಂಸಿಸಿ ನಿರ್ನಾಮಮಾಡಲು ಹೊರಡುವನು.",
			"45": "<sup>45</sup> ಕಡಲಿಗೂ ಚೆಲುವಿನ ಪರಿಶುದ್ಧ ಪರ್ವತಕ್ಕೂ ನಡುವೆ ಅರಮನೆಯಂಥ ಗುಡಾರವನ್ನು ಹಾಕಿಸುವನು. ಆದರೂ ಸಹಾಯಕ್ಕೆ ಯಾರೂ ಇಲ್ಲದವನಾಗಿ ಸಾಯುವನು."
		},
		"12": {
			"1": "<sup>1</sup> “ಆ ಕಾಲದಲ್ಲಿ ನಿನ್ನ ಜನರ ಪಕ್ಷ ವಹಿಸುವ ಮಹಾದೂತನಾದ ಮಿಕಾಯೇಲನು ಕಾಣಿಸಿಕೊಳ್ಳುವನು. ಮೊತ್ತಮೊದಲು, ಮಾನವ ಜನಾಂಗ ಉಂಟಾದಂದಿನಿಂದ ಅಂದಿನವರೆಗೆ ಸಂಭವಿಸದ ಘೋರ ಸಂಕಟ ಉಂಟಾಗುವುದು. ಆಗ ನಿನ್ನ ಜನರಲ್ಲಿ ಯಾರ ಯಾರ ಹೆಸರುಗಳು ಜೀವಬಾಧ್ಯರ ಪಟ್ಟಿಯಲ್ಲಿ ಇರುವುದೋ ಅವರೆಲ್ಲರು ಜೀವೋದ್ಧಾರವನ್ನು ಪಡೆಯುವರು.",
			"2": "<sup>2</sup> ಸತ್ತು ಧೂಳಿ ಮಣ್ಣಿನಲ್ಲಿ ‘ದೀರ್ಘನಿದ್ರೆ’ಮಾಡುತ್ತಿರುವ ಅನೇಕರು ಏಳುವರು. ಎಚ್ಚೆತ್ತವರಲ್ಲಿ ಕೆಲವರು ನಿತ್ಯಜೀವವನ್ನು ಅನುಭವಿಸುವರು; ಕೆಲವರು ತಿರಸ್ಕೃತರಾಗಿ ನಿತ್ಯ ನಿಂದನೆಗೆ ಗುರಿಯಾಗುವರು.",
			"3": "<sup>3</sup> ಬುದ್ಧಿವಂತರು ತೇಜೋಮಯವಾದ ಆಕಾಶಮಂಡಲದಂತೆ ಪ್ರಕಾಶಿಸುವರು. ಜನರನ್ನು ಸದ್ಧರ್ಮಿಗಳನ್ನಾಗಿ ಮಾಡುವವರು ಯುಗಯುಗಾಂತರಕ್ಕೂ ನಕ್ಷತ್ರಗಳಂತೆ ಹೊಳೆಯುವರು.",
			"4": "<sup>4</sup> “ದಾನಿಯೇಲನೇ, ನೀನು ಈ ಮಾತುಗಳನ್ನು ಮುಚ್ಚಿಡು; ಇವುಗಳನ್ನು ಬರೆಯುವ ಗ್ರಂಥಕ್ಕೆ ಮುದ್ರೆಹಾಕು; ಅಂತ್ಯಕಾಲದವರೆಗೆ ಮರೆಯಾಗಿರಲಿ. ಬಹುಜನರು ಅತ್ತಿತ್ತ ಅಲೆದಾಡುವರು (ಸತ್ಯಕ್ಕಾಗಿ). ಅಧರ್ಮ ಹೆಚ್ಚುತ್ತಿರುವುದು.”",
			"5": "<sup>5</sup> ಕೂಡಲೆ ದಾನಿಯೇಲನಾದ ನಾನು ಮತ್ತೆ ಇಬ್ಬರು ವ್ಯಕ್ತಿಗಳನ್ನು ಕಂಡೆ. ಅವರಲ್ಲಿ ಒಬ್ಬನು ನದಿಯ ಈ ದಡದಲ್ಲಿ, ಇನ್ನೊಬ್ಬನು ಆ ದಡದಲ್ಲಿ ನಿಂತಿದ್ದರು.",
			"6": "<sup>6</sup> ಇವರಲ್ಲಿ ಒಬ್ಬನು, ನದಿಯ ನೀರಿನ ಮೇಲ್ಗಡೆ ನಾರಿನ ಹೊದಿಕೆಯನ್ನು ಹೊದ್ದು ನಿಂತಿದ್ದ ದೂತನನ್ನು ನೋಡಿ, “ಈ ಆಶ್ಚರ್ಯಕಾರ್ಯಗಳು ನೆರವೇರಲು ಎಷ್ಟುಕಾಲ ಹಿಡಿಯುವುದು?” ಎಂದು ಕೇಳಿದನು.",
			"7": "<sup>7</sup> ಆ ದೂತ ಎಡಬಲಗೈಗಳನ್ನು ಆಕಾಶದ ಕಡೆಗೆ ಎತ್ತಿಕೊಂಡು, “ಶಾಶ್ವತ ಜೀವಸ್ವರೂಪನಾಣೆ, ಒಂದು ಯುಗ, ಎರಡು ಯುಗ, ಮತ್ತು ಅರ್ಧ ಯುಗ ಕಳೆಯಬೇಕು. ದೇವಜನರಿಗಾಗುವ ಹಿಂಸೆಬಾಧೆಗಳು ಸಂಪೂರ್ಣವಾಗಿ ನಿಂತಮೇಲೆ ಈ ಕಾರ್ಯಗಳೆಲ್ಲ ಮುಕ್ತಾಯವಾಗುವುವು,” ಎಂಬುದಾಗಿ ನನ್ನ ಕಿವಿಗೆ ಬೀಳುವಂತೆ ಹೇಳಿದನು.",
			"8": "<sup>8</sup> ನನಗೆ ಅದು ಕೇಳಿಸಿತ್ತಾದರೂ ಅರ್ಥವಾಗಲಿಲ್ಲ. ಆಗ ನಾನು, “ಎನ್ನೊಡೆಯಾ, ಈ ಕಾರ್ಯಗಳ ಪರಿಣಾಮವೇನು?” ಎಂದು ಪ್ರಶ್ನೆಮಾಡಿದೆ.",
			"9": "<sup>9</sup> ಅವನು, “ದಾನಿಯೇಲನೇ ಹೋಗು; ಈ ಮಾತುಗಳು ಅಂತ್ಯಕಾಲದವರೆಗೂ ಮುಚ್ಚಲ್ಪಟ್ಟು ಮುದ್ರಿತವಾಗಿವೆ.",
			"10": "<sup>10</sup> ಅನೇಕರು ತಮ್ಮನ್ನು ಶುದ್ಧೀಕರಿಸಿ, ಶುಭ್ರಮಾಡಿಕೊಂಡು ಶೋಧಿತರಾಗುವರು. ದುಷ್ಟರು ದುಷ್ಟರಾಗಿಯೇ ನಡೆಯುವರು. ಅವರಲ್ಲಿ ಯಾರಿಗೂ ವಿವೇಕವಿರದು. ಬುದ್ಧಿವಂತರಿಗೆ ವಿವೇಕವಿರುವುದು.",
			"11": "<sup>11</sup> ಅನುದಿನದ ಬಲಿ ನಿಂತುಹೋಗುವುದು. ‘ವಿನಾಶಕರ ವಿಕಟ ಮೂರ್ತಿ’ ಪ್ರತಿಷ್ಠಿತವಾದ ಮೇಲೆ 1290 ದಿನಗಳು ಕಳೆಯಬೇಕು.",
			"12": "<sup>12</sup> 1335 ದಿನಗಳ ಮಟ್ಟಿಗೆ ವಿಶ್ವಾಸದಿಂದ ಕಾದಿರುವವರು ಧನ್ಯರು!",
			"13": "<sup>13</sup> ನೀನು ಹೋಗಿ ಅಂತ್ಯದವರೆಗೆ ವಿಶ್ವಾಸದಿಂದಿರು. ನೀನು ‘ದೀರ್ಘನಿದ್ರೆ’ಯನ್ನು ಹೊಂದಿ, ನಿನಗೆ ನಿರ್ಧರಿಸಲಾದ ಸ್ಥಾನವನ್ನು ಪಡೆಯಲು ಯುಗಸಮಾಪ್ತಿಯಲ್ಲಿ ಎದ್ದುಬರುವೆ,” ಎಂದು ಹೇಳಿದನು."
		}
	}
};
module.exports = book;