var book = {
	"name": "೧ ಕೊರಿಂಥಿಯರಿಗೆ",
	"numChapters": 16,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ಕ್ರಿಸ್ತಯೇಸುವಿನಲ್ಲಿ ಪುನೀತರಾಗಿ ದೇವಜನರಾಗಲು ಕರೆಹೊಂದಿರುವ ಕೊರಿಂಥದ ಶ್ರೀಸಭೆಯವರಿಗೂ ಹಾಗೂ ಪ್ರಭು ಯೇಸುಕ್ರಿಸ್ತರನ್ನು ಎಲ್ಲೆಡೆಯೂ ನಾಮಸ್ಮರಣೆ ಮಾಡುವ ಸರ್ವಜನರಿಗೂ - ದೇವರ‍ ಚಿತ್ತಾನುಸಾರ ಕ್ರಿಸ್ತಯೇಸುವಿನ ಪ್ರೇಷಿತನಾಗಲು ಕರೆಹೊಂದಿದ ಪೌಲನಾದ ನಾನು ಸಹೋದರ ಸೊಸ್ಥೆನನೊಂದಿಗೆ ಸೇರಿ ಬರೆಯುವ ಪತ್ರ.",
			"2": "<sup>2</sup> ಯೇಸುಕ್ರಿಸ್ತರು ನಮಗೂ ಸಮಸ್ತ ದೇವಜನರಿಗೂ ಪ್ರಭುವಾಗಿದ್ದಾರೆ.",
			"3": "<sup>3</sup> ನಮ್ಮ ತಂದೆಯಾದ ದೇವರಿಂದಲೂ ಪ್ರಭುವಾದ ಯೇಸುಕ್ರಿಸ್ತರಿಂದಲೂ ನಿಮಗೆ ಅನುಗ್ರಹ ಮತ್ತು ಶಾಂತಿಸಮಾಧಾನ ಲಭಿಸಲಿ!",
			"4": "<sup>4</sup> ಕ್ರಿಸ್ತಯೇಸುವಿನಲ್ಲಿ ನಿಮಗೆ ಕೊಡಲಾಗಿರುವ ದೇವಾನುಗ್ರಹದ ಸಲುವಾಗಿ ನಾನು ನಿಮಗೋಸ್ಕರ ನನ್ನ ದೇವರಿಗೆ ಸತತವಾಗಿ ಕೃತಜ್ಞತೆಯನ್ನು ಸಲ್ಲಿಸುತ್ತೇನೆ.",
			"5": "<sup>5</sup> ಏಕೆಂದರೆ, ಕ್ರಿಸ್ತಯೇಸುವಿನಲ್ಲಿ ನೀವು ಎಲ್ಲ ದೃಷ್ಟಿಯಿಂದಲೂ ಶ್ರೀಮಂತರು, ಜ್ಞಾನಸಂಪನ್ನರು ಮತ್ತು ವಾಕ್‍ಚತುರರು ಆಗಿದ್ದೀರಿ.",
			"6": "<sup>6</sup> ಇದಲ್ಲದೆ, ಕ್ರಿಸ್ತಯೇಸುವನ್ನು ಕುರಿತ ಸಾಕ್ಷ್ಯವು ನಿಮ್ಮಲ್ಲಿ ನೆಲೆಗೊಂಡಿದೆ.",
			"7": "<sup>7</sup> ಇದರಿಂದಾಗಿ ನಮ್ಮ ಪ್ರಭು ಯೇಸುಕ್ರಿಸ್ತರು ಪ್ರತ್ಯಕ್ಷವಾಗುವುದನ್ನೇ ಎದುರುನೋಡುತ್ತಿರುವ ನಿಮಗೆ ಯಾವ ಕೃಪಾಶೀರ್ವಾದಗಳ ಕೊರತೆಯು ಇಲ್ಲ.",
			"8": "<sup>8</sup> ನಮ್ಮ ಪ್ರಭು ಯೇಸುಕ್ರಿಸ್ತರ ದಿನದಂದು ನೀವು ನಿರ್ದೋಷಿಗಳಾಗಿರುವಂತೆ ಆ ದೇವರೇ, ನಿಮ್ಮನ್ನು ಕಡೆಯವರೆಗೂ ಸ್ಥಿರವಾಗಿ ಕಾಪಾಡುವರು.",
			"9": "<sup>9</sup> ತಮ್ಮ ಪುತ್ರನೂ ನಮ್ಮ ಪ್ರಭುವೂ ಆದ ಯೇಸುಕ್ರಿಸ್ತರ ಅನ್ಯೋನ್ಯತೆಗೆ ನಿಮ್ಮನ್ನು ಕರೆದಿರುವ ದೇವರು ನಿಜಕ್ಕೂ ವಿಶ್ವಾಸಪಾತ್ರರು.",
			"10": "<sup>10</sup> ಸಹೋದರರೇ, ನಮ್ಮ ಪ್ರಭು ಯೇಸುಕ್ರಿಸ್ತರ ಹೆಸರಿನಲ್ಲಿ ನಾನು ಕೇಳಿಕೊಳ್ಳುವುದೇನೆಂದರೆ: ವಾದವಿವಾದವಿಲ್ಲದೆ, ಭಿನ್ನಭೇದಗಳಿಲ್ಲದೆ ಒಂದೇ ಮನಸ್ಸಿನಿಂದಲೂ ಒಂದೇ ಉದ್ದೇಶದಿಂದಲೂ ನೀವು ಐಕಮತ್ಯದಿಂದ ಬಾಳಿರಿ.",
			"11": "<sup>11</sup> ಸಹೋದರರೇ, ನೀವು ಜಗಳವಾಡುತ್ತಿದ್ದೀರಿ ಎಂಬುದು ಖ್ಲೊಯೇಯನ ಮನೆಯವರಿಂದ ತಿಳಿದುಬಂದಿದೆ.",
			"12": "<sup>12</sup> ನಿಮ್ಮ ಪೈಕಿ, ತಾನು ಪೌಲನ ಕಡೆಯವನು ಎಂದು ಒಬ್ಬ, ತಾನು ಅಪೊಲೋಸನ ಕಡೆಯವನು ಎಂದು ಇನ್ನೊಬ್ಬ, ತಾನು ಕೇಫನ ಕಡೆಯವನು ಎಂದು ಮತ್ತೊಬ್ಬ, ತಾನು ಕ್ರಿಸ್ತನ ಕಡೆಯವನು ಎಂದು ಮಗದೊಬ್ಬ. ಹೀಗೆ ಒಬ್ಬೊಬ್ಬರು ಒಂದೊಂದು ತೆರನಾಗಿ ಹೇಳಿಕೊಳ್ಳುತ್ತಾರಂತೆ.",
			"13": "<sup>13</sup> ಕ್ರಿಸ್ತಯೇಸು ಭಾಗಭಾಗವಾಗಿ ವಿಂಗಡಿಸಿ ಹೋಗಿದ್ದಾರೆಯೇ? ನಿಮಗೋಸ್ಕರ ಶಿಲುಬೆಯ ಮೇಲೆ ಮಡಿದವನು ಪೌಲನೇ? ನೀವು ದೀಕ್ಷಾಸ್ನಾನ ಪಡೆದದ್ದು ಪೌಲನ ಹೆಸರಿನಲ್ಲೇ?",
			"14": "<sup>14</sup> ಸದ್ಯ! ಕ್ರಿಸ್ಪನು ಮತ್ತು ಗಾಯನು ಇವರಿಬ್ಬರನ್ನು ಬಿಟ್ಟರೆ ನಿಮ್ಮ ಪೈಕಿ ಯಾರಿಗೂ ನಾನು ದೀಕ್ಷಾಸ್ನಾನವನ್ನು ಕೊಟ್ಟಿಲ್ಲ.",
			"15": "<sup>15</sup> ಆದ್ದರಿಂದ ನಿಮ್ಮಲ್ಲಿ ಯಾರೂ ದೀಕ್ಷಾಸ್ನಾನವನ್ನು ನನ್ನ ಹೆಸರಿನಲ್ಲಿ ಪಡೆದಿರಿ ಎಂದು ಹೇಳಿಕೊಳ್ಳುವಂತಿಲ್ಲ.",
			"16": "<sup>16</sup> ಹೌದು, ಸ್ತೇಫನನ ಮನೆಯವರಿಗೂ ದೀಕ್ಷಾಸ್ನಾನವನ್ನು ಕೊಟ್ಟಿದ್ದೇನೆ. ಇವರಲ್ಲದೆ ಇನ್ನಾರಿಗೂ ದೀಕ್ಷಾಸ್ನಾನವನ್ನು ಕೊಟ್ಟ ನೆನಪು ನನಗಿಲ್ಲ.",
			"17": "<sup>17</sup> ಯೇಸುಕ್ರಿಸ್ತರು ನನ್ನನ್ನು ಕಳುಹಿಸಿರುವುದು ದೀಕ್ಷಾಸ್ನಾನವನ್ನು ಕೊಡುವುದಕ್ಕಲ್ಲ. ಶುಭಸಂದೇಶವನ್ನು ಸಾರುವುದಕ್ಕೆ ಅದೂ ವಾಕ್‍ಚಾತುರ್ಯದಿಂದಲ್ಲ. ಹಾಗೆ ಸಾರಿದ್ದೇ ಆದರೆ, ಕ್ರಿಸ್ತಯೇಸುವಿನ ಶಿಲುಬೆಯೇ ನಿರರ್ಥಕವಾದೀತು.",
			"18": "<sup>18</sup> ವಿನಾಶಮಾರ್ಗದಲ್ಲಿರುವವರಿಗೆ ಶಿಲುಬೆಯ ಸಂದೇಶ ಹುಚ್ಚು ಸಂಗತಿಯಾಗಿದೆ. ಮುಕ್ತಿಮಾರ್ಗದಲ್ಲಿರುವ ನಮಗಾದರೋ ಅದು ದೈವಶಕ್ತಿಯಾಗಿದೆ.",
			"19": "<sup>19</sup> “ಜ್ಞಾನಿಗಳ ಜ್ಞಾನವನ್ನು ನಾಶಮಾಡುವೆನು ಜಾಣರ ಜಾಣತನಕ್ಕೆ ಭಂಗತರುವೆನು” ಎಂದು ಪವಿತ್ರಗ್ರಂಥದಲ್ಲಿ ಇದೆಯಲ್ಲವೇ?",
			"20": "<sup>20</sup> ಹೀಗಿರುವಾಗ ಜ್ಞಾನಿಯೆಲ್ಲಿ? ಶಾಸ್ತ್ರಜ್ಞನೆಲ್ಲಿ? ಜಗತ್ತಿನ ತರ್ಕನಿಪುಣನೆಲ್ಲಿ? ಪ್ರಪಂಚದ ಜ್ಞಾನವನ್ನು ದೇವರು ಹುಚ್ಚುತನವಾಗಿಸಿಲ್ಲವೇ?",
			"21": "<sup>21</sup> ಲೋಕವು ತನ್ನ ಸ್ವಂತ ಜ್ಞಾನದಿಂದ ದೇವರನ್ನು ಅರಿಯಲಾರದೆ ಹೋಯಿತು. ಇದು ದೈವಸಂಕಲ್ಪವೇ ಸರಿ. ಆದ್ದರಿಂದಲೇ ನಾವು ಸಾರುವ ‘ಹುಚ್ಚುತನ’ವೆಂಬ ಸಂದೇಶದ ಮೂಲಕ ವಿಶ್ವಾಸವುಳ್ಳವರನ್ನು ಉದ್ಧರಿಸುವುದು ದೇವರಿಗೆ ಉಚಿತವೆನಿಸಿತು",
			"22": "<sup>22</sup> ಯೆಹೂದ್ಯರು ಸೂಚಕಕಾರ್ಯಗಳನ್ನು ಕೇಳುತ್ತಾರೆ. ಗ್ರೀಕರು ಜ್ಞಾನವನ್ನು ಅರಸುತ್ತಾರೆ.",
			"23": "<sup>23</sup> ನಾವಾದರೋ ಶಿಲುಬೆಗೇರಿಸಲಾದ ಕ್ರಿಸ್ತಯೇಸುವನ್ನು ಪ್ರಚುರಪಡಿಸುತ್ತೇವೆ. ಯೆಹೂದ್ಯರಿಗೆ ಇದು ಅಸಹ್ಯವಾಗಿದೆ; ಅನ್ಯಜನರಿಗೆ ಹಾಸ್ಯಾಸ್ಪದವಾಗಿದೆ.",
			"24": "<sup>24</sup> ಆದರೆ ಯೆಹೂದ್ಯರಾಗಿರಲಿ, ಗ್ರೀಕರಾಗಿರಲಿ, ದೇವರಿಂದ ಕರೆಹೊಂದಿದ್ದರೆ ಅಂಥವರಿಗೆ ಯೇಸುಕ್ರಿಸ್ತರು ದೇವರ ಶಕ್ತಿಯಾಗಿದ್ದಾರೆ ಹಾಗೂ ದೇವರ ಜ್ಞಾನವಾಗಿದ್ದಾರೆ.",
			"25": "<sup>25</sup> ‘ದೇವರ ಹುಚ್ಚುತನ’ ಎಂಬುದು ಮಾನವ ಜ್ಞಾನಕ್ಕಿಂತಲೂ ಶ್ರೇಷ್ಠವಾದುದು. ‘ದೇವರ ದೌರ್ಬಲ್ಯ’ ಎಂಬುದು ಮಾನವ ಶಕ್ತಿಗಿಂತಲೂ ಪ್ರಬಲವಾದುದು.",
			"26": "<sup>26</sup> ಸಹೋದರರೇ, ನಿಮ್ಮನ್ನು ದೇವರು ಕರೆದಾಗ ನೀವು ಹೇಗಿದ್ದಿರಿ ಎಂದು ಜ್ಞಾಪಿಸಿಕೊಳ್ಳಿ. ಲೋಕದ ಎಣಿಕೆಯಲ್ಲಿ ನಿಮ್ಮೊಳಗೆ ಜ್ಞಾನಿಗಳು ಅನೇಕರಿಲ್ಲ. ಪರಾಕ್ರಮಿಗಳು ಅನೇಕರಿಲ್ಲ, ಕುಲೀನರು ಅನೇಕರಿಲ್ಲ.",
			"27": "<sup>27</sup> ಜ್ಞಾನಿಗಳನ್ನು ನಾಚಿಕೆಗೀಡುಮಾಡಲು ದೇವರು ಲೋಕದ ದೃಷ್ಟಿಯಲ್ಲಿ ಮೂರ್ಖರನ್ನು ಆರಿಸಿಕೊಂಡರು; ಅಂತೆಯೇ ಬಲಿಷ್ಠರನ್ನು ಲಜ್ಜೆಗೀಡುಮಾಡಲು ಲೋಕದ ದೃಷ್ಟಿಯಲ್ಲಿ ಬಲಹೀನರನ್ನು ಆರಿಸಿಕೊಂಡರು.",
			"28": "<sup>28</sup> ಲೋಕದ ದೃಷ್ಟಿಯಲ್ಲಿ ಗಣ್ಯವಾದುದನ್ನು ನಿರ್ನಾಮಗೊಳಿಸಲು ಗಣನೆಗೆ ಬಾರದುದನ್ನು, ಕೀಳಾದುದನ್ನು, ಬೀಳಾದುದನ್ನು ಆರಿಸಿಕೊಂಡರು.",
			"29": "<sup>29</sup> ಯಾವ ಮಾನವನೂ ತಮ್ಮ ಸಮ್ಮುಖದಲ್ಲಿ ಅಹಂಕಾರಪಡದಂತೆ ದೇವರು ಹೀಗೆ ಮಾಡಿದರು.",
			"30": "<sup>30</sup> ಅವರ ಕೃಪೆಯಿಂದಲೇ ನೀವು ಕ್ರಿಸ್ತಯೇಸುವಿನಲ್ಲಿ ಬಾಳುತ್ತಾ ಇದ್ದೀರಿ; ಅವರ ಕೃಪೆಯಿಂದಲೇ ಕ್ರಿಸ್ತಯೇಸು ನಮಗೆ ಜ್ಞಾನ ಮೂಲವಾಗಿದ್ದಾರೆ. ದೇವರಿಂದ ನಮಗೆ ದೊರಕುವ ಸತ್ಸಂಬಂಧ, ಪಾವನತೆ ಹಾಗೂ ವಿಮೋಚನೆ ಆ ಕ್ರಿಸ್ತಯೇಸುವಿನಿಂದಲೇ.",
			"31": "<sup>31</sup> ಆದ್ದರಿಂದ ಪವಿತ್ರಗ್ರಂಥದಲ್ಲಿ ಹೇಳಿರುವಂತೆ: ‘ಹೆಚ್ಚಳಪಡುವವನು ಪ್ರಭುವಿನಲ್ಲಿ ಹೆಚ್ಚಳಪಡಲಿ.’"
		},
		"2": {
			"1": "<sup>1</sup> ಸಹೋದರರೇ, ದೇವರ ಸತ್ಯಾರ್ಥವನ್ನು ಸಾರಲು ನಿಮ್ಮಲ್ಲಿಗೆ ನಾನು ಬಂದಾಗ ವಾಕ್ಚಾತುರ್ಯವನ್ನಾಗಲಿ, ಜ್ಞಾನಾಡಂಬರವನ್ನಾಗಲಿ ಪ್ರದರ್ಶಿಸುತ್ತಾ ಬರಲಿಲ್ಲ.",
			"2": "<sup>2</sup> ನಾನು ನಿಮ್ಮೊಡನೆ ಇದ್ದಾಗ, ಯೇಸುಕ್ರಿಸ್ತರನ್ನು, ಅದೂ ಶಿಲುಬೆಗೇರಿಸಲಾದ ಯೇಸುವನ್ನು ಹೊರತು ಬೇರೆ ಯಾವುದನ್ನೂ ಅರಿಯಲು ಆಶಿಸಲಿಲ್ಲ.",
			"3": "<sup>3</sup> ಈ ಕಾರಣ, ನಾನು ನಿಮ್ಮ ನಡುವೆ ದುರ್ಬಲನೂ ಭಯಭೀತನೂ ಆಗಿದ್ದೆ.",
			"4": "<sup>4</sup> ನನ್ನ ಬೋಧನೆ ಹಾಗು ಮಾತುಕತೆ ಕೇವಲ ಬುದ್ಧಿವಂತಿಕೆಯಿಂದಾಗಲಿ, ಮಾತಿನ ಚಮತ್ಕಾರದಿಂದಾಗಲಿ ಕೂಡಿರಲಿಲ್ಲ; ಅದಕ್ಕೆ ಬದಲಾಗಿ ದೇವರ ಆತ್ಮವನ್ನು ಅವರ ಶಕ್ತಿಯನ್ನು ಸಮರ್ಥಿಸುತ್ತಿದ್ದವು.",
			"5": "<sup>5</sup> ಅಂತೆಯೇ, ನಿಮ್ಮ ವಿಶ್ವಾಸವು ಮನುಷ್ಯ ಜ್ಞಾನವನ್ನು ಆಧರಿಸದೆ, ದೇವರ ಶಕ್ತಿಯನ್ನು ಆಧರಿಸಬೇಕೆಂಬುದೇ ನನ್ನ ಉದ್ದೇಶವಾಗಿತ್ತು.",
			"6": "<sup>6</sup> ನಾನು ಪರಿಣಿತರೊಂದಿಗೆ ಜ್ಞಾನವನ್ನೇ ಕುರಿತು ಮಾತನಾಡುತ್ತೇನೆ. ಆದರೆ ಇದು ಇಹಲೋಕದ ಜ್ಞಾನವಲ್ಲ, ಅಳಿದುಹೋಗುವಂಥ ಅಧಿಪತಿಗಳ ಜ್ಞಾನವೂ ಅಲ್ಲ.",
			"7": "<sup>7</sup> ನಾನು ಸಾರುವ ಜ್ಞಾನ ದೇವರ ನಿಗೂಢ ಜ್ಞಾನ. ಮಾನವರಿಗೆ ಮುಚ್ಚಿಟ್ಟ ಜ್ಞಾನ. ನಮ್ಮನ್ನು ಮಹಿಮೆಗೊಳಿಸುವುದಕ್ಕಾಗಿ ಲೋಕದ ಉತ್ಪತ್ತಿಗೆ ಮೊದಲೇ ದೇವರು ನಿಯೋಜಿಸಿದ ಜ್ಞಾನ.",
			"8": "<sup>8</sup> ಲೋಕಾಧಿಪತಿಗಳಾರಿಗೂ ಇದರ ಅರಿವಿರಲಿಲ್ಲ. ಹಾಗೆ ಅರಿತಿದ್ದರೆ ಆ ಮಹಿಮಾನ್ವಿತ ಪ್ರಭುವನ್ನು ಅವರು ಶಿಲುಬೆಗೇರಿಸುತ್ತಿರಲಿಲ್ಲ.",
			"9": "<sup>9</sup> ಪವಿತ್ರಗ್ರಂಥದಲ್ಲಿ ಹೇಳಿರುವಂತೆ : “ಕಣ್ಣಾವುದೂ ಕಂಡಿಲ್ಲ, ಕಿವಿಯಾವುದೂ ಕೇಳಿಲ್ಲ. ಮನುಜಕಲ್ಪನೆಗೂ ಎಟುಕಲಿಲ್ಲ. ಅಂಥ ಅತಿಶಯಗಳನ್ನು ಸಜ್ಜುಗೊಳಿಸಿರುವನು, ತನ್ನನೊಲಿದವರಿಗೆ ಪರಮದೇವನು.”",
			"10": "<sup>10</sup> ನಮಗಾದರೋ ದೇವರು ಪವಿತ್ರಾತ್ಮ ಅವರ ಮುಖಾಂತರ ಇವುಗಳನ್ನು ಪ್ರಕಟಿಸಿದ್ದಾರೆ. ಸಕಲವನ್ನು ಹಾಗೂ ದೇವರ ಅಂತರಾಳವನ್ನು ಅವಲೋಕಿಸುವವರು ಈ ಪವಿತ್ರಾತ್ಮರೇ.",
			"11": "<sup>11</sup> ಮಾನವನ ಆಲೋಚನೆಗಳನ್ನು ಅವನ ಅಂತರಾತ್ಮವಲ್ಲದೆ ಮತ್ತಾರು ಬಲ್ಲರು? ಅಂತೆಯೇ, ದೇವರ ಆಲೋಚನೆಗಳನ್ನು ಪವಿತ್ರಾತ್ಮರಲ್ಲದೆ ಬೇರೆ ಯಾರೂ ಅರಿಯಲಾರರು.",
			"12": "<sup>12</sup> ನಾವಾದರೋ ಪ್ರಾಪಂಚಿಕ ಆತ್ಮವನ್ನು ಹೊಂದಿದವರಲ್ಲ. ದೇವರು ನಮಗೆ ದಯಪಾಲಿಸಿರುವ ವರಪ್ರಸಾದಗಳನ್ನು ಅರಿತುಕೊಳ್ಳುವಂತೆ ಮಾಡುವ ಪಾರಮಾರ್ಥಿಕ ಆತ್ಮವನ್ನು ಪಡೆದಿದ್ದೇವೆ.",
			"13": "<sup>13</sup> ನಾವು ಇವುಗಳನ್ನು ಮಾನವ ಜ್ಞಾನ ಪ್ರೇರೇಪಿಸಿದ ಮಾತುಗಳಿಂದ ಹೇಳುವುದಿಲ್ಲ. ದೇವರ ಆತ್ಮದಿಂದ ಪ್ರೇರಿತವಾದ ಮಾತುಗಳಿಂದ ಹೇಳುತ್ತೇವೆ. ಹೀಗೆ ಆಧ್ಯಾತ್ಮಿಕ ಸತ್ಯಗಳನ್ನು, ಆಧ್ಯಾತ್ಮಿಕ ವ್ಯಕ್ತಿಗಳಿಗೆ ವಿವರಿಸುತ್ತೇವೆ.",
			"14": "<sup>14</sup> ಭೌತಿಕ ಮನುಷ್ಯನು ದೇವರ ಆತ್ಮದ ವರಗಳನ್ನು ನಿರಾಕರಿಸುತ್ತಾನೆ. ಅವು ಅವನಿಗೆ ಹುಚ್ಚುತನವಾಗಿ ತೋರುತ್ತವೆ. ಅವುಗಳನ್ನು ಗ್ರಹಿಸಲು ಅವನಿಂದಾಗದು. ಏಕೆಂದರೆ, ಆಧ್ಯಾತ್ಮಿಕ ವಿವೇಚನೆಯಿಂದ ಮಾತ್ರ ಅವುಗಳನ್ನು ಅರಿಯಲು ಸಾಧ್ಯ.",
			"15": "<sup>15</sup> ದೇವರ ಆತ್ಮವನ್ನು ಹೊಂದಿದವನು ಎಲ್ಲವನ್ನೂ ವಿಚಾರಣೆಮಾಡಿ ತಿಳಿದುಕೊಳ್ಳುತ್ತಾನೆ. ಅವನನ್ನು ಯಾರೂ ವಿಚಾರಣೆಗೆ ಗುರಿಮಾಡಲಾಗದು.",
			"16": "<sup>16</sup> “ಪ್ರಭುವಿನ ಮನಸ್ಸನ್ನರಿತವರಾರು? ಪ್ರಭುವಿಗೆ ಉಪದೇಶಿಸುವವರಾರು?” ಎಂದು ಲಿಖಿತವಾಗಿದೆ. ನಾವಾದರೋ, ಕ್ರಿಸ್ತಯೇಸುವಿನ ಮನೋಭಾವನೆಯುಳ್ಳವರು."
		},
		"3": {
			"1": "<sup>1</sup> ಪ್ರಿಯ ಸಹೋದರರೇ, ದೇವರ ಆತ್ಮವನ್ನು ಪಡೆದವರೊಡನೆ ಮಾತನಾಡಿದಂತೆ ನಿಮ್ಮೊಡನೆ ನಾವು ಮಾತನಾಡಲಿಲ್ಲ. ಲೌಕಿಕರು ನೀವು; ಕ್ರಿಸ್ತಯೇಸುವಿನಲ್ಲಿ ಹಸುಗೂಸುಗಳು ನೀವು ಎಂದು ಭಾವಿಸಿ ನಿಮ್ಮೊಡನೆ ಮಾತನಾಡಬೇಕಾಯಿತು.",
			"2": "<sup>2</sup> ನಾನು ನಿಮಗೆ ಹಾಲೂಡಿಸಿದೆನು; ಗಟ್ಟಿ ಆಹಾರವನ್ನು ಕೊಡಲಿಲ್ಲ. ಏಕೆಂದರೆ, ಅದನ್ನು ಜೀರ್ಣಿಸಿಕೊಳ್ಳಲು ನಿಮಗೆ ಶಕ್ತಿ ಇರಲಿಲ್ಲ. ಇಂದಿಗೂ ನೀವು ಶಕ್ತರಲ್ಲ.",
			"3": "<sup>3</sup> ನೀವು ಇನ್ನೂ ಲೌಕಿಕರಂತೆ ಬಾಳುತ್ತಿದ್ದೀರಿ. ನಿಮ್ಮ ನಡುವೆ ದ್ವೇಷ-ಅಸೂಯೆ, ವಾದ-ವಿವಾದಗಳು ಪ್ರಬಲವಾಗಿದ್ದು ನೀವಿನ್ನೂ ಪ್ರಾಣಿಗಳಂತೆ, ಸಾಧಾರಣ ಮನುಷ್ಯರಂತೆ ವರ್ತಿಸುತ್ತಿದ್ದೀರಲ್ಲವೆ?",
			"4": "<sup>4</sup> ಒಬ್ಬನು, ನಾನು ಪೌಲನ ಕಡೆಯವನು; ಇನ್ನೊಬ್ಬನು, ನಾನು ಅಪೊಲೋಸನ ಕಡೆಯವನು, ಎಂದು ನಿಮ್ಮೊಳಗೆ ಕಿತ್ತಾಡುವ ನೀವು ಕೇವಲ ನರಪ್ರಾಣಿಗಳಲ್ಲದೆ ಮತ್ತೇನು?",
			"5": "<sup>5</sup> ಅಪೊಲೋಸನು ಯಾರು? ಪೌಲನು ಯಾರು? ನಿಮ್ಮನ್ನು ವಿಶ್ವಾಸಕ್ಕೆ ಕರೆತಂದ ದಾಸರು ನಾವಲ್ಲವೇ? ನಮ್ಮಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬನು ಪ್ರಭು ನಿಯಮಿಸುವ ಕಾರ್ಯವನ್ನು ನಿರ್ವಹಿಸುತ್ತೇವೆ.",
			"6": "<sup>6</sup> ನಾನು ಸಸಿಯನ್ನು ನೆಟ್ಟೆನು; ಅಪೊಲೋಸನು ನೀರೆರೆದನು; ಆದರೆ ಅದನ್ನು ಬೆಳೆಸಿದವರು ದೇವರು.",
			"7": "<sup>7</sup> ಆದ್ದರಿಂದ ನೆಡುವವನಾಗಲಿ, ನೀರೆರೆಯುವವನಾಗಲಿ ಪ್ರಮುಖನಲ್ಲ; ಬೆಳವಣಿಗೆ ನೀಡುವ ದೇವರೇ ದೊಡ್ಡವರು.",
			"8": "<sup>8</sup> ನೆಡುವವನೂ ನೀರೆರೆಯುವವನೂ ಇಬ್ಬರೂ ಅಗತ್ಯವಾಗಿ ಬೇಕಾದವರೇ. ಅವನವನ ದುಡಿಮೆಗೆ ತಕ್ಕ ಹಾಗೆ ಪ್ರತಿಯೊಬ್ಬನೂ ಸಂಭಾವನೆ ಪಡೆಯುತ್ತಾನೆ.",
			"9": "<sup>9</sup> ನಾವು ದೇವರ ಸಹಕಾರ್ಮಿಕರು. ನೀವು ದೇವರೆ ಸಾಗುವಳಿಮಾಡುವ ಹೊಲ; ಅವರೆ ನಿರ್ಮಿಸುತ್ತಿರುವ ಮಂದಿರ.",
			"10": "<sup>10</sup> ದೇವರು ನನಗಿತ್ತ ವರದಾನಗಳಿಗೆ ಅನುಸಾರವಾಗಿ ನಾನು ಚತುರಶಿಲ್ಪಿಯಂತೆ ಅಸ್ತಿವಾರ ಹಾಕಿದೆ. ಇನ್ನೊಬ್ಬನು ಅದರ ಮೇಲೆ ಕಟ್ಟುತ್ತಿದ್ದಾನೆ. ಆದರೆ ಕಟ್ಟುವ ಪ್ರತಿಯೊಬ್ಬನು ತಾನು ಹೇಗೆ ಕಟ್ಟುತ್ತಿದ್ದಾನೆ ಎಂಬುದರ ಬಗ್ಗೆ ಎಚ್ಚರದಿಂದಿರಲಿ.",
			"11": "<sup>11</sup> ಈಗಾಗಲೇ ಅಸ್ತಿವಾರ ಹಾಕಲಾಗಿದೆ. ಯೇಸುಕ್ರಿಸ್ತರೇ ಆ ಅಸ್ತಿವಾರ. ಇದಲ್ಲದೆ, ಬೇರೆ ಅಸ್ತಿವಾರವನ್ನು ಯಾರೂ ಹಾಕಲಾಗದು.",
			"12": "<sup>12</sup> ಈ ಅಸ್ತಿವಾರದ ಮೇಲೆ ಕೆಲವರು ಚಿನ್ನ, ಬೆಳ್ಳಿ, ರತ್ನ - ಇವುಗಳನ್ನು ಉಪಯೋಗಿಸ ಕಟ್ಟುತ್ತಾರೆ. ಮತ್ತೆ ಕೆಲವರು ಹುಲ್ಲು, ಕಟ್ಟಿಗೆ, ಜೊಂಡು - ಇವುಗಳನ್ನು ಬಳಸುತ್ತಾರೆ.",
			"13": "<sup>13</sup> ಅವನವನ ಕೆಲಸದ ಗುಣವು ಕಡೆಯ ದಿನ ಬಟ್ಟಬಯಲಾಗುವುದು. ಯೇಸುಕ್ರಿಸ್ತರ ಆ ದಿನವು ಬೆಂಕಿಯ ದಿನವಾಗಿ ಪ್ರಜ್ವಲಿಸುವುದು. ಪ್ರತಿಯೊಬ್ಬನ ಕೆಲಸವನ್ನು ಆ ಬೆಂಕಿ ಪರೀಕ್ಷಿಸುವುದು.",
			"14": "<sup>14</sup> ಕಟ್ಟಡವು ಸುಟ್ಟುಹೋಗದೆ ಅಸ್ತಿವಾರದ ಮೇಲೆ ನೆಲೆಯಾಗಿ ನಿಂತರೆ ಕಟ್ಟಿದವನಿಗೆ ಸಂಭಾವನೆ ದೊರಕುತ್ತದೆ.",
			"15": "<sup>15</sup> ಸುಟ್ಟುಹೋದರೆ ಅವನಿಗೇ ನಷ್ಟವಾಗುತ್ತದೆ. ಅವನಾದರೋ ಬೆಂಕಿಯಿಂದ ತಪ್ಪಿಸಿಕೊಂಡವನ ಹಾಗೆ ರಕ್ಷಣೆಯನ್ನು ಹೊಂದುತ್ತಾನೆ.",
			"16": "<sup>16</sup> ನೀವು ದೇವರ ಆಲಯವಾಗಿದ್ದೀರಿ. ದೇವರ ಆತ್ಮವು ನಿಮ್ಮಲ್ಲಿ ವಾಸಿಸುತ್ತದೆಂದು ನಿಮಗೆ ಚೆನ್ನಾಗಿ ತಿಳಿದಿದೆ.",
			"17": "<sup>17</sup> ಯಾವನಾದರೂ ದೇವಾಲಯವನ್ನು ನಾಶಮಾಡಿದರೆ ದೇವರು ಅವನನ್ನು ನಾಶಮಾಡುತ್ತಾರೆ. ದೇವರ ಆಲಯವು ಪವಿತ್ರವಾದುದು. ನೀವೇ ಆ ಆಲಯ.",
			"18": "<sup>18</sup> ಯಾರೂ ತನ್ನನ್ನು ತಾನೇ ವಂಚಿಸಿಕೊಳ್ಳದಿರಲಿ. ನಿಮ್ಮಲ್ಲಿ ಯಾರಾದರೂ ಈ ಲೋಕದ ದೃಷ್ಟಿಯಲ್ಲಿ ತಾನು ಜಾಣನೆಂದು ಭಾವಿಸುವುದಾದರೆ ಅಂಥವನು ಮೊದಲು ಹುಚ್ಚನಂತಿರಲು ಕಲಿಯಲಿ. ಆಗ ಅವನು ನಿಜವಾಗಿಯೂ ಜಾಣನಾಗುತ್ತಾನೆ.",
			"19": "<sup>19</sup> ಇಹಲೋಕಪ ಜಾಣತನವು ದೇವರ ದೃಷ್ಟಿಯಲ್ಲಿ ಹುಚ್ಚುತನವೇ ಸರಿ. “ದೇವರು ಜ್ಞಾನಿಗಳನ್ನು ಅವರ ಜಾಲದಲ್ಲಿಯೇ ಸಿಕ್ಕಿಸುವರು” ಎಂದೂ",
			"20": "<sup>20</sup> “ಜ್ಞಾನಿಗಳ ಯೋಜನೆಗಳು ನಿಷ್ಫಲವೆಂಬುದು ಪ್ರಭುವಿಗೆ ತಿಳಿದಿದೆ” ಎಂದೂ ಲಿಖಿತವಾಗಿದೆಯಲ್ಲವೆ?",
			"21": "<sup>21</sup> ಆದ್ದರಿಂದ ಕೇವಲ ನರಮಾನವರನ್ನೇ ನೆಚ್ಚಿಕೊಂಡು ಯಾರೂ ಹೆಚ್ಚಳಪಡದಿರಲಿ.",
			"22": "<sup>22</sup> ವಾಸ್ತವವಾಗಿ ಸಮಸ್ತವೂ ನಿಮ್ಮದೇ. ಪೌಲ, ಅಪೊಲೋಸ್, ಕೇಫ - ಇವರಾಗಲಿ; ಜಗತ್ತು, ಜೀವ, ಮರಣ - ಇವುಗಳಾಗಲಿ, ಅಥವಾ ವರ್ತಮಾನ, ಭವಿಷ್ಯತ್ಕಾಲಗಳಾಗಲಿ, ಇವೆಲ್ಲವೂ ನಿಮ್ಮವೇ.",
			"23": "<sup>23</sup> ಆದರೆ ನೀವು ಕ್ರಿಸ್ತಯೇಸುವಿಗೆ ಸೇರಿದವರು; ಕ್ರಿಸ್ತಯೇಸು ದೇವರಿಗೆ ಸೇರಿದವರು."
		},
		"4": {
			"1": "<sup>1</sup> ನಾವು ಕ್ರಿಸ್ತಯೇಸುವಿನ ದಾಸರೆಂದೂ ದೇವರ ಸತ್ಯಾರ್ಥಗಳ ನಂಬಿಗಸ್ಥ ನಿರ್ವಾಹಕರೆಂದೂ ಜನರು ನಮ್ಮನ್ನು ಪರಿಗಣಿಸಲಿ.",
			"2": "<sup>2</sup> ನಿರ್ವಾಹಕರು ಪ್ರಾಮಾಣಿಕರಾಗಿರಬೇಕಾದುದು ಅತ್ಯವಶ್ಯ.",
			"3": "<sup>3</sup> ನನ್ನ ವಿಷಯದಲ್ಲಿ ಹೇಳುವುದಾದರೆ, ನಿಮ್ಮಿಂದಾಗಲಿ, ಮಾನವ ನಿಯಮಿತ ನ್ಯಾಯಾಲಯದಿಂದಾಗಲಿ ನನ್ನ ವಿಚಾರಣೆಯಾಗುವುದರ ಬಗ್ಗೆ ನನಗೆ ಕಿಂಚಿತ್ತೂ ಚಿಂತೆಯಿಲ್ಲ. ನನಗೆ ನಾನೇ ನ್ಯಾಯತೀರ್ಪು ಮಾಡಿಕೊಳ್ಳುವುದೂ ಇಲ್ಲ.",
			"4": "<sup>4</sup> ನನ್ನಲ್ಲಿ ದೋಷವಿದೆಯೆಂದು ನನ್ನ ಮನಸ್ಸಾಕ್ಷಿಗೆ ತೋರುವುದಿಲ್ಲ. ಆದರೂ ನಾನು ನಿರ್ದೋಷಿಯೆಂದು ಹೇಳುವಂತಿಲ್ಲ. ನನ್ನ ನ್ಯಾಯನಿರ್ಣಯ ಮಾಡುವವರು ಪ್ರಭುವೇ.",
			"5": "<sup>5</sup> ಅದೇ ಮೇರೆಗೆ, ನೀವು ಸೂಕ್ತಕಾಲಕ್ಕೆ ಮುಂಚೆ ತೀರ್ಪುಮಾಡಬೇಡಿ; ಪ್ರಭುವಿನ ಪುನರಾಗಮನದವರೆಗೂ ಕಾದುಕೊಂಡಿರಿ. ಕತ್ತಲಲ್ಲಿ ಗುಪ್ತವಾಗಿರುವುಗಳನ್ನು ಪ್ರಭುವು ಬೆಳಕಿಗೆ ತರುವರು; ಅಂತರಂಗದ ಯೋಜನೆಗಳನ್ನು ಬಹಿರಂಗಪಡಿಸುವರು. ಆಗ ಪ್ರತಿಯೊಬ್ಬನಿಗೂ ತಕ್ಕ ಪ್ರಶಂಸೆ ದೇವರಿಂದಲೇ ದೊರಕುವುದು.",
			"6": "<sup>6</sup> ಸಹೋದರರೇ, ನಿಮ್ಮ ಪ್ರಯೋಜನಕ್ಕಾಗಿ ಇದೆಲ್ಲವನ್ನು ನನಗೂ ಅಪೊಲೋಸನಿಗೂ ಅನ್ವಯಿಸಿ ಹೇಳಿದ್ದೇನೆ. ನೀವು ನಮ್ಮ ಆದರ್ಶವನ್ನು ಅನುಸರಿಸಬೇಕು. ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿ ಬರೆದಿರುವುದನ್ನು ಮೀರಬಾರದು. ನಿಮ್ಮಲ್ಲಿ ಯಾರೂ ಒಬ್ಬನ ಪಕ್ಷವಹಿಸಿ ಜಂಬ ಕೊಚ್ಚಿಕೊಂಡು ಮತ್ತೊಬ್ಬನನ್ನು ಕಡೆಗಣಿಸಬಾರದು.",
			"7": "<sup>7</sup> ಇತರರಿಗಿಂತಲೂ ನಿನ್ನನ್ನು ಶ್ರೇಷ್ಠನನ್ನಾಗಿಸಿದವರು ಯಾರು? ದೇವರಿಂದ ಪಡೆಯದೆ ಇರುವುದು ನಿನ್ನಲ್ಲಿ ಯಾವುದಾದರೂ ಇದೆಯೆ? ಹೀಗೆ ಎಲ್ಲವನ್ನೂ ದೇವರಿಂದ ಪಡೆದ ಮೇಲೆ, ಪಡೆಯದವನಂತೆ ಜಂಬ ಕೊಚ್ಚಿಕೊಳ್ಳುವುದೇಕೆ?",
			"8": "<sup>8</sup> ಈಗಾಗಲೇ ನೀವು ಸಂತೃಪ್ತರಾಗಿಬಿಟ್ಟಿರೋ? ಇಷ್ಟಕ್ಕೇ ಶ್ರೀಮಂತರಾಗಿಬಿಟ್ಟಿರೋ? ನಮ್ಮನ್ನು ಬಿಟ್ಟು ನೀವು ಸಿಂಹಾಸನವೇರಿಬಿಟ್ಟಿರೋ? ನೀವು ನಿಜವಾಗಿಯೂ ರಾಜರಾಗಿದ್ದರೆ ಚೆನ್ನಾಗಿರುತ್ತಿತ್ತು! ಆಗ ನಾವೂ ನಿಮ್ಮ ಜೊತೆ ರಾಜ್ಯ ಆಳಬಹುದಿತ್ತು!",
			"9": "<sup>9</sup> ಬದಲಿಗೆ, ಪ್ರೇಷಿತರಾದ ನಮ್ಮನ್ನು ದೇವರು ಕಟ್ಟಕಡೆಯವರನ್ನಾಗಿಸಿದ್ದಾರೆಂದು ತೋರುತ್ತದೆ. ಎಲ್ಲರ ಮುಂದೆ ಮರಣದಂಡನೆಗೆ ಎಳೆದೊಯ್ಯಲಾಗುವವರಂತೆ ನಾವು ದೂತರಿಗೂ ಮಾನವರಿಗೂ ಇಡೀ ಜಗತ್ತಿಗೂ ಹಾಸ್ಯಾಸ್ಪದವಾದ ನೋಟವಾಗಿದ್ದೇವೆ.",
			"10": "<sup>10</sup> ಕ್ರಿಸ್ತಯೇಸುವಿನ ನಿಮಿತ್ತ ನಾವಂತೂ ಹುಚ್ಚರು, ನೀವಾದರೋ ಕ್ರಿಸ್ತಯೇಸುವಿನಲ್ಲಿ ಬುದ್ಧಿವಂತರು; ನಾವು ಬಲಹೀನರು, ನೀವು ಬಲಾಢ್ಯರು; ನಾವು ಅವಮಾನಿತರು, ನೀವು ಸನ್ಮಾನಿತರು!",
			"11": "<sup>11</sup> ಈ ಗಳಿಗೆಯವರೆಗೂ ನಾವು ಹಸಿದು ಬಾಯಾರಿದವರು, ಬಟ್ಟೆಬರೆ ಇಲ್ಲದವರು, ಏಟುಪೆಟ್ಟು ತಿನ್ನುವವರು ಮತ್ತು ಮನೆಮಠವಿಲ್ಲದೆ ಅಲೆಯುವವರು ಆಗಿದ್ದೇವೆ.",
			"12": "<sup>12</sup> ಸ್ವಂತ ದುಡಿಮೆಯಿಂದ ಜೀವಿಸುತ್ತೇವೆ; ನಮ್ಮನ್ನು ಶಪಿಸುವವರನ್ನು ಆಶೀರ್ವದಿಸುತ್ತೇವೆ; ಹಿಂಸೆಗೊಳಗಾದಾಗ ಸಹಿಸಿಕೊಳ್ಳುತ್ತೇವೆ.",
			"13": "<sup>13</sup> ಅಪವಾದ ಹೊರಿಸಿದವರೊಡನೆ ವಿನಯದಿಂದ ವರ್ತಿಸುತ್ತೇವೆ; ಪ್ರಪಂಚದ ಪಾಲಿಗೆ ನಾವೀಗ ಕಸಕ್ಕಿಂತಲೂ ಕಡೆ; ವಿಶ್ವಕ್ಕೇ ಹೊಲಸು!",
			"14": "<sup>14</sup> ನಿಮ್ಮನ್ನು ನಾಚಿಕೆಪಡಿಸುವುದಕ್ಕೆ ಇದನ್ನು ಬರೆಯಲಿಲ್ಲ. ನಮ್ಮ ನೆಚ್ಚಿನ ಮಕ್ಕಳಾದ ನಿಮ್ಮನ್ನು ಎಚ್ಚರಿಸಲೆಂದು ಬರೆದೆನು.",
			"15": "<sup>15</sup> ನಿಮಗೆ ಕ್ರಿಸ್ತಯೇಸುವಿನಲ್ಲಿ ಸಾವಿರಾರು ಮಂದಿ ಶಿಕ್ಷಕರು ಇರಬಹುದು. ಆದರೆ ನಿಮಗೆ ನಾನೊಬ್ಬನೇ ತಂದೆ. ಶುಭಸಂದೇಶದ ಮೂಲಕ ನಾನೇ ನಿಮ್ಮನ್ನು ಕ್ರಿಸ್ತಯೇಸುವಿನಲ್ಲಿ ಪಡೆದ ತಂದೆ.",
			"16": "<sup>16</sup> ಆದ್ದರಿಂದ ನನ್ನನ್ನು ಅನುಸರಿಸಿ ಬಾಳಬೇಕೆಂದು ನಿಮ್ಮನ್ನು ಒತ್ತಾಯಿಸುತ್ತೇನೆ.",
			"17": "<sup>17</sup> ಇದಕ್ಕಾಗಿಯೇ ಪ್ರಭುವಿನಲ್ಲಿ ನನ್ನ ನೆಚ್ಚಿನ ಹಾಗೂ ಪ್ರಾಮಾಣಿಕ ಮಗನಾದ ತಿಮೊಥೇಯನನ್ನು ನಿಮ್ಮ ಬಳಿಗೆ ಕಳಿಸಿದ್ದೇನೆ. ಆತನು ಕ್ರಿಸ್ತಯೇಸುವಿನಲ್ಲಿ ನನ್ನ ಬಾಳುವೆಯನ್ನು ಕುರಿತು ತಿಳಿಸುವನು; ನಾನು ಎಲ್ಲೆಡೆಯಲ್ಲೂ ಎಲ್ಲಾ ಧರ್ಮಸಭೆಗಳಲ್ಲೂ ಬೋಧಿಸುತ್ತಿರುವುದನ್ನು ನಿಮ್ಮ ನೆನಪಿಗೆ ತರುವನು.",
			"18": "<sup>18</sup> ಇನ್ನು ನಾನು ನಿಮ್ಮ ಬಳಿಗೆ ಬರುವುದಿಲ್ಲ ಎಂದು ಭಾವಿಸಿ ಕೆಲವರು ಅಹಂಕಾರಿಗಳಾಗಿ ಮೆರೆಯುತ್ತಿದ್ದಾರೆಂದು ತಿಳಿದುಬಂದಿದೆ.",
			"19": "<sup>19</sup> ಪ್ರಭುವಿನ ಚಿತ್ತವಾದರೆ ನಾನು ಬೇಗನೆ ಬರುತ್ತೇನೆ. ಬಂದು, ಆ ಅಹಂಕಾರಿಗಳು ಕೇವಲ ಮಾತಿನ ಮಲ್ಲರೋ ಅಥವಾ ಶಕ್ತಿವಂತರೋ ಎಂದು ಕಂಡುಹಿಡಿಯುತ್ತೇನೆ.",
			"20": "<sup>20</sup> ದೇವರ ಸಾಮ್ರಾಜ್ಯ ಮರುಳು ಮಾತಲ್ಲ, ಶಕ್ತಿಯ ಸಾಧನ,",
			"21": "<sup>21</sup> ಬೆತ್ತವನ್ನು ಹಿಡಿದು ಬಿಗಿಯಲು ಬರಲೋ? ಅಥವಾ ಪ್ರೀತಿ ಸಹಾನುಭೂತಿಯಿಂದ ತುಂಬಿದವನಾಗಿ ಬರಲೋ? ನಿಮಗೆ ಯಾವುದು ಇಷ್ಟ?"
		},
		"5": {
			"1": "<sup>1</sup> ಅನ್ಯಜನರಲ್ಲಿ ಕೂಡ ಇಲ್ಲದಂಥ ದುರ್ನಡತೆ ನಿಮ್ಮಲ್ಲಿದೆಯೆಂಬುದಾಗಿ ವರದಿ ಬಂದಿದೆ. ನಿಮ್ಮಲ್ಲಿ ಒಬ್ಬನು ತನ್ನ ತಂದೆಯ ಪತ್ನಿಯನ್ನೇ ಇಟ್ಟುಕೊಂಡಿದ್ದಾನಂತೆ.",
			"2": "<sup>2</sup> ಹೀಗಿರಲು, ನೀವು ಸೊಕ್ಕಿನಿಂದ ಮೆರೆಯಲು ಕಾರಣವಾದರೂ ಎಲ್ಲಿದೆ? ಬದಲಿಗೆ, ನೀವು ವ್ಯಸನಪಟ್ಟು ಅವನನ್ನು ನಿಮ್ಮ ಸಭಾಕೂಟದಿಂದ ಬಹಿಷ್ಕರಿಸಬೇಕಾಗಿತ್ತಲ್ಲವೆ?",
			"3": "<sup>3</sup> ನಾನು ಶಾರೀರಿಕವಾಗಿ ನಿಮ್ಮಿಂದ ದೂರವಿದ್ದರೂ ಆತ್ಮಿಕವಾಗಿ ನಿಮ್ಮ ಹತ್ತಿರವಿದ್ದೇನೆ. ನಿಮ್ಮ ಸಂಗಡವೇ ಇರುವವನಂತೆ ಅವನಿಗೆ ಏನುಮಾಡಬೇಕೆಂದು ಈಗಾಗಲೇ ತೀರ್ಮಾನಿಸಿದ್ದೇನೆ.",
			"4": "<sup>4</sup> ಅದೇನೆಂದರೆ: ನೀವು ಸಭೆಸೇರಿ, ಪ್ರಭು ಯೇಸುವಿನ ಶಕ್ತಿಯ ಆಧಾರದ ಮೇಲೆ ಅವನನ್ನು ಸೈತಾನನಿಗೆ ಒಪ್ಪಿಸಿಬಿಡಬೇಕು. ನಾನು ಆತ್ಮಿಕವಾಗಿ ನಿಮ್ಮ ಮಧ್ಯೆಯಿರುತ್ತೇನೆ.",
			"5": "<sup>5</sup> ಅವನ ದೇಹವು ದಂಡನೆಗೊಳಗಾದರೂ ಅವನ ಆತ್ಮ ಪ್ರಭು ಯೇಸುವಿನ ದಿನದಂದು ಉದ್ಧಾರವಾಗುವಂತೆ ಹೀಗೆ ಮಾಡಬೇಕು.",
			"6": "<sup>6</sup> ನೀವು ಜಂಬ ಕೊಚ್ಚಿಕೊಳ್ಳುವುದು ತರವಲ್ಲ. ಒಂದಿಷ್ಟು ಹುಳಿ, ಹಿಟ್ಟನ್ನೆಲ್ಲಾ ಹುದುಗೆಬ್ಬಿಸುತ್ತದೆಂಬುದನ್ನು ನೀವು ಚೆನ್ನಾಗಿ ಬಲ್ಲಿರಿ.",
			"7": "<sup>7</sup> ನೀವು ಹುಳಿಯಿಲ್ಲದ ಕಣಕವಾಗಿದ್ದೀರಿ. ಅಪ್ಪಟ ರೊಟ್ಟಿಯಾಗುವಂತೆ ಹುಳಿಯನ್ನು ತೆಗೆದುಹಾಕಿರಿ. ಏಕೆಂದರೆ, ಪಾಸ್ಕ ಯಜ್ಞದ ಕುರಿಮರಿಯಾದ ಕ್ರಿಸ್ತಯೇಸು ಬಲಿಯಾಗಿದ್ದಾರೆ.",
			"8": "<sup>8</sup> ಆದಕಾರಣ ನಾವು ದುಷ್ಟತನ, ಕೆಡುಕುತನವೆಂಬ ಹುಳಿಹಿಟ್ಟನ್ನು ವರ್ಜಿಸೋಣ. ಪರಿಶುದ್ಧತೆ ಮತ್ತು ಸತ್ಯತೆ ಎಂಬ ಹುಳಿಯಿಲ್ಲದ ಹೊಸರೊಟ್ಟಿಯನ್ನು ತಿಂದು ಪಾಸ್ಕಹಬ್ಬವನ್ನು ಆಚರಿಸೋಣ.",
			"9": "<sup>9</sup> ದುರಾಚಾರಿಗಳ ಸಹವಾಸ ನಿಮಗೆ ಸಲ್ಲದು, ಎಂದು ನಾನು ಬರೆದಿದ್ದೆ.",
			"10": "<sup>10</sup> ಅಂದರೆ, ಈ ಲೋಕದಲ್ಲಿರುವ ದುರಾಚಾರಿಗಳು, ಲೋಭಿಗಳು, ಸುಲಿಗೆಗಾರರು, ವಿಗ್ರಹಾರಾಧಕರು - ಇಂಥವರ ಸಹವಾಸವನ್ನು ಬಿಟ್ಟುಬಿಡಬೇಕೆಂಬುದು ನನ್ನ ಮಾತಿನ ಅರ್ಥವಲ್ಲ. ಹಾಗೆ ಮಾಡಿದರೆ ನೀವು ಈ ಲೋಕವನ್ನೇ ಬಿಟ್ಟುಹೋಗಬೇಕಾದೀತು.",
			"11": "<sup>11</sup> ಆದರೆ ತಾನು ಕ್ರೈಸ್ತ ಸಹೋದರ ಎನಿಸಿಕೊಂಡು, ದುರಾಚಾರಿಯಾಗಿಯೋ ಲೋಭೀಯಾಗಿಯೋ ವಿಗ್ರಹಾರಾಧಕನಾಗಿಯೋ ಪರನಿಂದಕನಾಗಿಯೋ ಕುಡುಕನಾಗಿಯೋ ಸುಲಿಗೆಗಾರನಾಗಿಯೋ ಯಾರಾದರೂ ಇದ್ದರೆ, ಅಂಥವನ ಸಹವಾಸ ನಿಮಗೆ ಸಲ್ಲದು. ಇದು ನನ್ನ ಪತ್ರದ ಉದ್ದೇಶ.",
			"12": "<sup>12</sup> ಧರ್ಮಸಭೆಗೆ ಸೇರದೆ ಇರುವವರ ಬಗ್ಗೆ ತೀರ್ಪುಮಾಡುವುದು ನನ್ನ ಕೆಲಸವಲ್ಲ. ಅವರನ್ನು ಕುರಿತು ದೇವರೇ ತೀರ್ಪುನೀಡುವರು. ನೀವು ತೀರ್ಪುಮಾಡಬೇಕಾದುದು ಧರ್ಮಸಭೆಗೆ ಸೇರಿದವರನ್ನು ಕುರಿತು ದುರುಳನನ್ನು ನಿಮ್ಮಿಂದ ದೂರತಳ್ಳಿರಿ."
		},
		"6": {
			"1": "<sup>1</sup> ನಿಮ್ಮಲ್ಲಿ ಒಬ್ಬನಿಗೆ ಮತ್ತೊಬ್ಬನ ಮೇಲೆ ವ್ಯಾಜ್ಯವಿದ್ದರೆ ನ್ಯಾಯವಿಚಾರಣೆಗೆ ದೇವಜನರ ಮುಂದೆ ಹೋಗದೆ, ಅನ್ಯಜನರ ಮುಂದೆ ಹೋಗುವಷ್ಟು ಸೊಕ್ಕು ನಿಮಗೆಲ್ಲಿಂದ ಬಂದಿತು?",
			"2": "<sup>2</sup> ದೇವಜನರು ಈ ಲೋಕಕ್ಕೆ ನ್ಯಾಯತೀರ್ಪು ಮಾಡುತ್ತಾರೆಂದು ನಿಮಗೆ ತಿಳಿಯದೋ? ಲೋಕವೇ ನಿಮ್ಮಿಂದ ತೀರ್ಪು ಪಡೆಯಬೇಕಾಗಿರುವಲ್ಲಿ ಸಣ್ಣಪುಟ್ಟ ವಿಷಯಗಳನ್ನು ತೀರ್ಮಾನಿಸಿಕೊಳ್ಳಲು ನೀವು ಅಸಮರ್ಥರೋ?",
			"3": "<sup>3</sup> ದೇವದೂತರಿಗೂ ನಾವು ನ್ಯಾಯತೀರ್ಪು ಮಾಡುವೆವೆಂದು ನಿಮಗೆ ತಿಳಿಯದೋ? ಹೀಗಿರುವಲ್ಲಿ, ಈ ಜೀವನದ ಸಮಸ್ಯೆಗಳನ್ನು ಕುರಿತು ನ್ಯಾಯತೀರ್ಪು ಮಾಡುವುದು ಎಷ್ಟೋ ಸುಲಭವಲ್ಲವೇ?",
			"4": "<sup>4</sup> ಇಂಥ ವಿಷಯಗಳು ತಲೆದೋರಿದಾಗ ಧರ್ಮಸಭೆಯಿಂದ ಮಾನ್ಯತೆ ಪಡೆಯದವರನ್ನು ಏಕೆ ನ್ಯಾಯತೀರ್ಪುಗಾರರನ್ನಾಗಿ ಮಾಡುತ್ತೀರಿ?",
			"5": "<sup>5</sup> ಇದು ನಿಮಗೆ ನಾಚಿಕೆಗೇಡು! ಸಹೋದರರ ವ್ಯಾಜ್ಯವನ್ನು ತೀರ್ಮಾನಿಸಬಲ್ಲ ಜಾಣನು ನಿಮ್ಮಲ್ಲಿ ಒಬ್ಬನೂ ಇಲ್ಲವೇ?",
			"6": "<sup>6</sup> ಸಹೋದರನು ಸಹೋದರನ ವಿರುದ್ಧ ನ್ಯಾಯಾಲಯಕ್ಕೆ ಹೋಗುವುದು, ಅದೂ ಅವಿಶ್ವಾಸಿಗಳ ಮುಂದೆ ಹೋಗುವುದು ಸರಿಯೇ?",
			"7": "<sup>7</sup> ನಿಮ್ಮ ನಿಮ್ಮಲ್ಲಿರುವ ವ್ಯಾಜ್ಯಗಳೇ ನಿಮ್ಮ ಸೋಲಿನ ಸೂಚನೆಗಳು. ಅದಕ್ಕಿಂತ ಅನ್ಯಾಯವನ್ನು ಸಹಿಸಿಕೊಳ್ಳುವುದು ಒಳ್ಳೆಯದಲ್ಲವೇ? ಸುಲಿಗೆಗೆ ತುತ್ತಾದರೂ ಸಾವಧಾನದಿಂದ ಇರುವುದು ಒಳಿತಲ್ಲವೇ?",
			"8": "<sup>8</sup> ಅದಕ್ಕೆ ಬದಲು ನೀವೇ ಅನ್ಯಾಯಮಾಡುತ್ತೀರಿ; ನೀವೇ ಸುಲಿಗೆಮಾಡುತ್ತೀರಿ; ಅದೂ ಸಹೋದರರಿಗೆ ಹೀಗೆ ಮಾಡುತ್ತೀರಿ;",
			"9": "<sup>9</sup> ಅಧರ್ಮಿಗಳು ದೇವರ ಸಾಮ್ರಾಜ್ಯಕ್ಕೆ ಬಾಧ್ಯರಲ್ಲವೆಂದು ನೀವು ಬಲ್ಲಿರಿ. ನಿಮ್ಮನ್ನು ನೀವೇ ವಂಚಿಸಿಕೊಳ್ಳಬೇಡಿ. ದುರಾಚಾರಿಗಳು, ವಿಗ್ರಹಾರಾಧಕರು, ವ್ಯಭಿಚಾರಿಗಳು, ಸಲಿಂಗಕಾಮಿಗಳು,",
			"10": "<sup>10</sup> ಕಳ್ಳರು, ಲೋಭಿಗಳು, ಕುಡುಕರು, ಪರನಿಂದಕರು, ಸುಲಿಗೆಗಾರರು - ಇವರಾರೂ ದೇವರ ಸಾಮ್ರಾಜ್ಯಕ್ಕೆ ಬಾಧ್ಯರಲ್ಲ.",
			"11": "<sup>11</sup> ಹಿಂದೆ ನಿಮ್ಮಲ್ಲಿ ಕೆಲವರು ಅಂಥವರಾಗಿದ್ದಿರಿ. ಆದರೆ ಈಗ ಪ್ರಭು ಯೇಸುಕ್ರಿಸ್ತರ ನಾಮದಲ್ಲಿ ಮತ್ತು ದೇವರಾತ್ಮನಲ್ಲಿ ನೀವು ಶುದ್ಧರಾಗಿದ್ದೀರಿ, ಪುನೀತರಾಗಿದ್ದೀರಿ ಹಾಗೂ ದೇವರೊಡನೆ ಸತ್ಸಂಬಂಧ ಹೊಂದಿದ್ದೀರಿ.",
			"12": "<sup>12</sup> “ಏನನ್ನು ಮಾಡಲೂ ನನಗೆ ಸ್ವಾತಂತ್ರ್ಯ ಇದೆ,” ಎಂದು ಹೇಳುವುದುಂಟು. ಆದರೆ ಎಲ್ಲವೂ ನನಗೆ ಪ್ರಯೋಜನಕರವಲ್ಲ. ಏನನ್ನು ಮಾಡಲೂ ನನಗೆ ಸ್ವಾತಂತ್ರ್ಯವಿದೆ. ಆದರೆ ನಾನು ಯಾವುದಕ್ಕೂ ಗುಲಾಮನಲ್ಲ.",
			"13": "<sup>13</sup> “ಊಟ ಹೊಟ್ಟೆಗಾಗಿ, ಹೊಟ್ಟೆ ಊಟಕ್ಕಾಗಿ,” ಎಂದು ಹೇಳುವುದುಂಟು. ದೇವರು ಎರಡನ್ನೂ ನಾಶಗೊಳಿಸುವರು. ಆದರೆ ದೇಹವಿರುವುದು ದುರಾಚಾರಕ್ಕಲ್ಲ; ಪ್ರಭುವಿನ ಸೇವೆಗಾಗಿ. ಪ್ರಭುವೇ ದೇಹದ ಪರಿಪಾಲಕ.",
			"14": "<sup>14</sup> ಪ್ರಭುವನ್ನು ಪುನರುತ್ಥಾನಗೊಳಿಸಿದ ದೇವರು ನಮ್ಮನ್ನೂ ತಮ್ಮ ಪರಾಕ್ರಮದಿಂದ ಎಬ್ಬಿಸುವರು.",
			"15": "<sup>15</sup> ನಿಮ್ಮ ದೇಹಗಳು ಕ್ರಿಸ್ತಯೇಸುವಿನ ಅಂಗಗಳೆಂದು ನಿಮಗೆ ತಿಳಿಯದೇ? ಹಾಗಾದರೆ ನಾನು ಕ್ರಿಸ್ತಯೇಸುವಿನ ಅಂಗಗಳನ್ನು ತೆಗೆದು ವೇಶ್ಯೆಯ ಅಂಗಗಳನ್ನಾಗಿ ಮಾಡಬಹುದೇ? ಎಂದಿಗೂ ಇಲ್ಲ.",
			"16": "<sup>16</sup> “ಇಬ್ಬರೂ ಒಂದೇ ಶರೀರವಾಗುತ್ತಾರೆ,” ಎಂದು ಬರೆಯಲಾಗಿದೆ. ವೇಶ್ಯೆಯ ಸಂಸರ್ಗ ಮಾಡುವವನು ಅವಳೊಂದಿಗೆ ಒಂದೇ ದೇಹವಾಗುತ್ತಾನೆ, ಎಂದು ನಿಮಗೆ ತಿಳಿಯದೇ?",
			"17": "<sup>17</sup> ಆದರೆ ಪ್ರಭುವಿನ ಅನ್ಯೋನ್ಯತೆಯಲ್ಲಿ ಇರುವವನು ಅವರೊಂದಿಗೆ ಆಧ್ಯಾತ್ಮಿಕವಾಗಿ ಒಂದಾಗಿರುತ್ತಾನೆ.",
			"18": "<sup>18</sup> ವ್ಯಭಿಚಾರದಿಂದ ದೂರ ಸರಿಯಿರಿ. ಮಾನವನು ಮಾಡುವ ಇತರ ಪಾಪಗಳು ದೇಹಕ್ಕೆ ಬಾಹಿರವಾದವುಗಳು. ವ್ಯಭಿಚಾರ ಮಾಡುವಾತನು ತನ್ನ ದೇಹಕ್ಕೇ ದ್ರೋಹ ಬಗೆಯುತ್ತಾನೆ.",
			"19": "<sup>19</sup> ದೇವರು ನಿಮಗೆ ಅನುಗ್ರಹಿಸಿರುವ ಪವಿತ್ರಾತ್ಮರಿಗೆ ನಿಮ್ಮ ದೇಹ ಗರ್ಭಗುಡಿಯಾಗಿದೆ; ಆ ಪವಿತ್ರಾತ್ಮ ನಿಮ್ಮಲ್ಲಿ ವಾಸಮಾಡುತ್ತಿದ್ದಾರೆ ಎಂಬುದು ನಿಮಗೆ ಗೊತ್ತಿಲ್ಲವೇ? ನೀವು ನಿಮ್ಮ ಸ್ವಂತ ಸೊತ್ತಲ್ಲ.",
			"20": "<sup>20</sup> ಏಕೆಂದರೆ, ನೀವು ಕ್ರಯಕ್ಕೆ ಕೊಳ್ಳಲಾದವರು. ಆದ್ದರಿಂದ ನಿಮ್ಮ ದೇಹದಲ್ಲಿ ಆ ದೇವರ ಮಹಿಮೆ ಬೆಳಗುವಂತೆ ಮಾಡಿರಿ."
		},
		"7": {
			"1": "<sup>1</sup> ನೀವು ಬರೆದು ಕೇಳಿರುವ ವಿಷಯಗಳ ಬಗ್ಗೆ ನಾನು ಹೇಳುವುದೇನೆಂದರೆ: ಒಬ್ಬ ವ್ಯಕ್ತಿ ಸ್ತ್ರೀಯೊಡನೆ ಲೈಂಗಿಕ ಸಂಪರ್ಕವನ್ನು ಇಟ್ಟುಕೊಳ್ಳದಿರುವುದು ಒಳ್ಳೆಯದು.",
			"2": "<sup>2</sup> ಲೈಂಗಿಕ ಪ್ರಲೋಭನೆಗಳು ಪ್ರಬಲವಾಗಿರುವುದರಿಂದ ಪ್ರತಿಯೊಬ್ಬನಿಗೂ ಸ್ವಂತ ಸತಿ ಇರಲಿ, ಪ್ರತಿಯೊಬ್ಬಳಿಗೂ ಸ್ವಂತ ಪತಿ ಇರಲಿ.",
			"3": "<sup>3</sup> ಪತಿಯಾದವನು ತನ್ನ ಸತಿಗೆ ಸಲ್ಲತಕ್ಕ ದಾಂಪತ್ಯ ಕರ್ತವ್ಯಗಳನ್ನು ನೆರವೇರಿಸಲಿ. ಅಂತೆಯೇ, ಸತಿಯಾದವಳು ತನ್ನ ಪತಿಗೆ ಸಲ್ಲತಕ್ಕ ದಾಂಪತ್ಯ ಕರ್ತವ್ಯಗಳನ್ನು ನೆರವೇರಿಸಲಿ.",
			"4": "<sup>4</sup> ಸತಿಗೆ ತನ್ನ ದೇಹದ ಮೇಲೆ ಅಧಿಕಾರವಿಲ್ಲ. ಆದರೆ ಪತಿಗೆ ಆ ಅಧಿಕಾರವಿದೆ. ಪತಿಗೆ ತನ್ನ ದೇಹದ ಮೇಲೆ ಅಧಿಕಾರವಿಲ್ಲ, ಆದರೆ ಸತಿಗೆ ಆ ಅಧಿಕಾರವಿದೆ.",
			"5": "<sup>5</sup> ಆದುದರಿಂದ ಸತಿಪತಿಯರು ಒಬ್ಬರಿಗೊಬ್ಬರು ದಾಂಪತ್ಯಕರ್ತವ್ಯಗಳನ್ನು ನೆರವೇರಿಸಲು ನಿರಾಕರಿಸಬಾರದು. ಧ್ಯಾನ, ಪ್ರಾರ್ಥನೆಗಳಲ್ಲಿ ನಿರತರಾಗಿರಲು, ಸ್ವಲ್ಪಕಾಲ ಒಬ್ಬರನ್ನೊಬ್ಬರು ಅಗಲಿರಬಹುದು. ಆದರೆ ಅದಕ್ಕೆ ಪರಸ್ಪರ ಸಮ್ಮತಿ ಇರಬೇಕು. ಅನಂತರ ಒಂದುಗೂಡಬೇಕು. ಇಲ್ಲದೆ ಹೋದರೆ, ನಿಮ್ಮಲ್ಲಿ ಸಂಯಮ ಇಲ್ಲದಿರುವುದನ್ನು ಕಂಡು ಸೈತಾನನು ಪಾಪಕ್ಕೆ ಪ್ರಚೋದಿಸಬಹುದು.",
			"6": "<sup>6</sup> ಇದು ನನ್ನ ಆಜ್ಞೆಯಲ್ಲ, ಅನುಮತಿಯಷ್ಟೆ.",
			"7": "<sup>7</sup> ಎಲ್ಲರೂ ನಾನಿರುವಂತೆ ಇರಬೇಕೆಂಬುದು ನನ್ನ ಅಭಿಲಾಷೆ. ಆದರೆ ದೇವರು ಪ್ರತಿಯೊಬ್ಬನಿಗೂ ವಿಶಿಷ್ಟ ವರವನ್ನು ದಯಪಾಲಿಸಿದ್ದಾರೆ. ಒಬ್ಬನಿಗೆ ಒಂದು ವರವಾದರೆ, ಮತ್ತೊಬ್ಬನಿಗೆ ಇನ್ನೊಂದು ವರವುಂಟು.",
			"8": "<sup>8</sup> ಅವಿವಾಹಿತರಿಗೂ ವಿಧವೆಯರಿಗೂ ನನ್ನ ಬುದ್ಧಿಮಾತು ಯಾವುದೆಂದರೆ; ನಾನಿರುವಂತೆಯೇ ಇರುವುದು ಅವರಿಗೆ ಒಳ್ಳೆಯದು.",
			"9": "<sup>9</sup> ಅವರಿಗೆ ಸಂಯಮ ಇಲ್ಲದಿದ್ದರೆ ಮದುವೆಮಾಡಿಕೊಳ್ಳಲಿ. ಕಾಮತಾಪದಿಂದ ಬೆಂದುಹೋಗುವುದಕ್ಕಿಂತ ಮದುವೆಮಾಡಿಕೊಳ್ಳುವುದೇ ಲೇಸು.",
			"10": "<sup>10</sup> ವಿವಾಹಿತರಿಗೆ ನನ್ನದೊಂದು ಆಜ್ಞೆ: ನಿಜಕ್ಕೂ ಇದು ನನ್ನದಲ್ಲ, ಪ್ರಭುವಿನ ಆಜ್ಞೆ. ಸತಿಯು ತನ್ನ ಪತಿಯನ್ನು ಬಿಟ್ಟಗಲದಿರಲಿ.",
			"11": "<sup>11</sup> ಒಂದು ವೇಳೆ ಅಗಲಿದರೆ ಮತ್ತೆ ಮದುವೆಯಾಗದಿರಲಿ. ಇಲ್ಲವೆ, ತನ್ನ ಪತಿಯೊಂದಿಗೆ ಸಂಧಾನಮಾಡಿಕೊಳ್ಳಲಿ. ಅಂತೆಯೇ ಪತಿಯಾದವನು ತನ್ನ ಸತಿಯನ್ನು ಬಿಟ್ಟುಬಿಡದಿರಲಿ.",
			"12": "<sup>12</sup> ಮಿಕ್ಕವರಿಗೆ ನಾನು ಹೇಳುವುದೇನೆಂದರೆ: ಇದು ನನ್ನ ಮಾತು, ಪ್ರಭುವಿನದಲ್ಲ: ಒಬ್ಬ ಸಹೋದರನಿಗೆ ಕ್ರೈಸ್ತವಿಶ್ವಾಸಿಯಲ್ಲದ ಸತಿ ಇದ್ದರೆ, ಆಕೆ ಅವನೊಡನೆ ದಾಂಪತ್ಯ ಜೀವನ ನಡೆಸಲು ಒಪ್ಪಿದರೆ, ಆಕೆಯನ್ನು ಬಿಟ್ಟುಬಿಡಬಾರದು.",
			"13": "<sup>13</sup> ಹಾಗೆಯೇ ಒಬ್ಬ ಕ್ರೈಸ್ತ ಸಹೋದರಿಗೆ ಕ್ರೈಸ್ತವಿಶ್ವಾಸಿಯಲ್ಲದ ಪತಿ ಇದ್ದು ಅವನು ಆಕೆಯೊಂದಿಗೆ ದಾಂಪತ್ಯಜೀವನ ನಡೆಸಲು ಒಪ್ಪಿದರೆ, ಆಕೆ ಅವನನ್ನು ಬಿಟ್ಟುಬಿಡಬಾರದು.",
			"14": "<sup>14</sup> ಏಕೆಂದರೆ, ಕ್ರೈಸ್ತವಿಶ್ವಾಸಿಯಾದ ಸತಿಯ ಮುಖಾಂತರ ಪತಿ ದೇವಜನರೊಂದಿಗೆ ಸಂಬಂಧಪಡೆಯುತ್ತಾನೆ. ಅಂತೆಯೇ ಕ್ರೈಸ್ತವಿಶ್ವಾಸಿಯಾದ ಪತಿಯ ಮುಖಾಂತರ ಸತಿ ದೇವಜನರೊಂದಿಗೆ ಸಂಬಂಧಪಡೆಯುತ್ತಾಳೆ. ಇಲ್ಲದಿದ್ದರೆ ನಿಮ್ಮ ಮಕ್ಕಳು ಅಪವಿತ್ರರಾಗುತ್ತಿದ್ದರು. ಈಗಲಾದರೋ ಅವರು ದೇವಜನರೊಂದಿಗೆ ಸಂಬಂಧ ಉಳ್ಳವರಾಗಿದ್ದಾರೆ.",
			"15": "<sup>15</sup> ಒಂದು ವೇಳೆ, ವಿಶ್ವಾಸವಿಲ್ಲದ ಸತಿಯಾಗಲಿ, ಪತಿಯಾಗಲಿ ಬೇರ್ಪಟ್ಟುಹೋಗಬೇಕೆಂದಿದ್ದರೆ ಹೋಗಲಿ. ಇಂಥ ಸಂದರ್ಭಗಳಲ್ಲಿ ಕ್ರೈಸ್ತ ಸಹೋದರನು, ಇಲ್ಲವೆ ಸಹೋದರಿಯು ವಿವಾಹ ಬಂಧನದಿಂದ ವಿಮುಕ್ತರಾಗುತ್ತಾರೆ. ಏಕೆಂದರೆ, ದೇವರು ನಿಮ್ಮನ್ನು ಶಾಂತಿಸಮಾಧಾನದಿಂದ ಬಾಳಲು ಕರೆದಿದ್ದಾರೆ.",
			"16": "<sup>16</sup> ಅದೂ ಅಲ್ಲದೆ ಸ್ತ್ರೀಯೇ, ಕ್ರೈಸ್ತವಿಶ್ವಾಸಿಯಲ್ಲದ ನಿನ್ನ ಪತಿಯನ್ನು ಉದ್ಧಾರಮಾಡುವೆಯೆಂದು ಹೇಗೆ ಹೇಳಬಲ್ಲೆ? ಪುರುಷನೇ, ಕ್ರೈಸ್ತವಿಶ್ವಾಸಿಯಲ್ಲದ ನಿನ್ನ ಸತಿಯನ್ನು ಉದ್ಧಾರಮಾಡುವೆಯೆಂದು ಹೇಗೆ ಹೇಳಬಲ್ಲೆ?",
			"17": "<sup>17</sup> ದೇವರ ಕರೆಗೆ ಮತ್ತು ಪ್ರಭು ನೀಡಿರುವ ವರದಾನಗಳಿಗೆ ಅನುಗುಣವಾಗಿ ಪ್ರತಿಯೊಬ್ಬನೂ ಬಾಳುವೆ ನಡೆಸಲಿ. ಎಲ್ಲ ಸಭೆಗಳಿಗೂ ನಾನಿದನ್ನೇ ವಿಧಿಸಿದ್ದೇನೆ.",
			"18": "<sup>18</sup> ಸುನ್ನತಿಮಾಡಿಸಿಕೊಂಡವನು ದೇವರ ಕರೆಹೊಂದಿದ್ದರೆ ಅವನು ಸುನ್ನತಿಮಾಡಿಸಿಕೊಂಡದ್ದಕ್ಕಾಗಿ ಚಿಂತಿಸಬೇಕಾಗಿಲ್ಲ. ಸುನ್ನತಿ ಮಾಡಿಸಿಕೊಳ್ಳದವನು ದೇವರ ಕರೆಹೊಂದಿದ್ದರೆ ಅವನು ಸುನ್ನತಿ ಮಾಡಿಸಿಕೊಳ್ಳುವ ಅವಶ್ಯಕತೆಯಿಲ್ಲ.",
			"19": "<sup>19</sup> ಸುನ್ನತಿ ಮಾಡಿಸಿಕೊಳ್ಳುವುದೋ ಅಥವಾ ಮಾಡಿಸಿಕೊಳ್ಳದಿರುವುದೋ ಮುಖ್ಯವಲ್ಲ, ದೇವರ ಆಜ್ಞೆಗಳಿಗೆ ವಿಧೇಯನಾಗಿರುವುದೇ ಮುಖ್ಯ.",
			"20": "<sup>20</sup> ದೇವರು ಕರೆದಾಗ ಒಬ್ಬನು ಯಾವ ಸ್ಥಿತಿಯಲ್ಲಿದ್ದನೋ ಅದೇ ಸ್ಥಿತಿಯಲ್ಲಿ ಮುಂದುವರಿಯಲಿ.",
			"21": "<sup>21</sup> ದೇವರು ಕರೆದಾಗ ನೀನು ದಾಸ್ಯದಲ್ಲಿದ್ದೆಯೋ? ಚಿಂತೆಯಿಲ್ಲ. ಆದರೆ ಸ್ವತಂತ್ರನಾಗುವ ಅವಕಾಶ ದೊರೆತರೆ ಅದನ್ನು ಬಿಡಬೇಡ.",
			"22": "<sup>22</sup> ಪ್ರಭುವಿನಿಂದ ಕರೆಹೊಂದಿದವನು ದಾಸನಾಗಿದ್ದರೂ ಪ್ರಭುವಿನಲ್ಲಿ ಅವನು ಸ್ವತಂತ್ರನೆ. ಅದೇ ಮೇರೆಗೆ ಸ್ವತಂತ್ರನಾದವನು ಕ್ರಿಸ್ತಯೇಸುವಿನಿಂದ ಕರೆಹೊಂದಿದ್ದರೆ ಅವನು ಕ್ರಿಸ್ತಯೇಸುವಿನ ದಾಸನೇ ಆಗುತ್ತಾನೆ.",
			"23": "<sup>23</sup> ನಿಮ್ಮನ್ನು ಕ್ರಯಕ್ಕೆ ಕೊಳ್ಳಲಾಗಿದೆ. ಆದ್ದರಿಂದ ಮಾನವರಿಗೆ ದಾಸರಾಗಬೇಡಿ.",
			"24": "<sup>24</sup> ಪ್ರಿಯ ಸಹೋದರರೇ, ದೇವರು ಕರೆದಾಗ ಒಬ್ಬನು ಯಾವ ಸ್ಥಿತಿಯಲ್ಲಿ ಇದ್ದನೋ ಅದೇ ಸ್ಥಿತಿಯಲ್ಲಿ ದೇವರೊಂದಿಗೆ ಮುನ್ನಡೆಯಲಿ.",
			"25": "<sup>25</sup> ಅವಿವಾಹಿತರ ಬಗ್ಗೆ ಪ್ರಭುವಿನಿಂದ ಬಂದ ಕಟ್ಟಳೆ ಯಾವುದೂ ನನ್ನಲ್ಲಿಲ್ಲ. ಪ್ರಭುವಿನ ಕೃಪೆಯಿಂದ ನಂಬಿಕೆಗೆ ಅರ್ಹನಾದ ವ್ಯಕ್ತಿಯಂತೆ ನನ್ನ ಅಭಿಪ್ರಾಯಗಳನ್ನು ನಿಮ್ಮ ಮುಂದೆ ಇಡುತ್ತೇನೆ.",
			"26": "<sup>26</sup> ಇಂದಿನ ಸಂದಿಗ್ಧ ಪರಿಸ್ಥಿತಿಯಲ್ಲಿ ಒಬ್ಬನು ತಾನು ಇದ್ದ ಸ್ಥಿತಿಯಲ್ಲೇ ಇರುವುದು ಉತ್ತಮವೆಂದು ಭಾವಿಸುತ್ತೇನೆ.",
			"27": "<sup>27</sup> ನಿನಗೆ ವಿವಾಹವಾಗಿದೆಯೋ? ವಿವಾಹ ವಿಚ್ಛೇದನಕ್ಕೆ ಯತ್ನಿಸಬೇಡ. ನೀನು ಅವಿವಾಹಿತನೋ? ವಿವಾಹವಾಗಲು ಅವಸರಪಡಬೇಡ.",
			"28": "<sup>28</sup> ಹಾಗೇನಾದರೂ ನೀನು ವಿವಾಹ ಮಾಡಿಕೊಂಡರೆ ಅದು ಪಾಪವಲ್ಲ. ಅಂತೆಯೇ, ಅವಿವಾಹಿತ ಸ್ತ್ರೀಯೊಬ್ಬಳು ವಿವಾಹ ಮಾಡಿಕೊಂಡರೆ ಅದು ಪಾಪವಲ್ಲ. ಆದರೆ ದಂಪತಿಗಳಿಗೆ ತಾಪತ್ರಯಗಳು ಅನೇಕ. ಇವುಗಳು ನಿಮ್ಮನ್ನು ಬಾಧಿಸದಿರಲೆಂಬುದೇ ನನ್ನ ಹಾರೈಕೆ.",
			"29": "<sup>29</sup> ಪ್ರಿಯ ಸಹೋದರರೇ, ನನ್ನ ಅಭಿಪ್ರಾಯ ಇದು: ಉಳಿದಿರುವ ಕಾಲವು ಕೊಂಚ ಮಾತ್ರ. ಆದ್ದರಿಂದ ಮದುವೆಯಾದವರು ಮದುವೆಯಾಗದವರೋ ಎಂಬಂತೆ ನಡೆದುಕೊಳ್ಳಲಿ.",
			"30": "<sup>30</sup> ದುಃಖಿತರು ದುಃಖವಿಲ್ಲದವರಂತೆ, ಸಂತೋಷಭರಿತರು ಸಂತೋಷರಹಿತರಂತೆ ಹಾಗು ಕೊಂಡುಕೊಳ್ಳುವವರು ಕೊಂಡದ್ದು ತಮ್ಮದಲ್ಲವೋ ಎಂಬಂತೆ ನಡೆದುಕೊಳ್ಳಲಿ.",
			"31": "<sup>31</sup> ಲೋಕದ ವ್ಯವಹಾರದಲ್ಲಿ ಇರುವವರು ಅದರಲ್ಲಿಯೇ ತಲ್ಲೀನರಾಗದಿರಲಿ. ಏಕೆಂದರೆ, ಲೋಕದ ರೂಪರೇಷೆಗಳು ಗತಿಸಿಹೋಗುತ್ತವೆ.",
			"32": "<sup>32</sup> ನೀವು ಚಿಂತೆಯಿಲ್ಲದೆ ಇರಬೇಕೆಂಬುದೇ ನನ್ನ ಇಷ್ಟ. ಮದುವೆಯಾಗದವನು ಪ್ರಭುವಿನ ಸೇವೆಯಲ್ಲಿ ತತ್ಪರನಾಗಿರುತ್ತಾನೆ; ಪ್ರಭುವನ್ನು ಮೆಚ್ಚಿಸಲು ಶ್ರಮಿಸುತ್ತಾನೆ.",
			"33": "<sup>33</sup> ಮದುವೆ ಆದವನು ಲೋಕವ್ಯವಹಾರಗಳಲ್ಲಿ ಮಗ್ನನಾಗುತ್ತಾನೆ. ತನ್ನ ಸತಿಯನ್ನು ಮೆಚ್ಚಿಸಲು ಪ್ರಯತ್ನಿಸುತ್ತಾನೆ.",
			"34": "<sup>34</sup> ಅಂಥವನು ಉಭಯ ಸಂಕಟಕ್ಕೆ ಸಿಕ್ಕಿಕೊಳ್ಳುತ್ತಾನೆ. ಮದುವೆಯಾಗದ ಮಹಿಳೆ ಅಥವಾ ಕನ್ನಿಕೆ ಪ್ರಭುವಿನ ಸೇವೆಯಲ್ಲಿ ನಿರತಳಾಗಿರುತ್ತಾಳೆ. ದೇಹಾತ್ಮಗಳೆರಡನ್ನೂ ಪ್ರಭುವಿಗೆ ಮೀಸಲಾಗಿಡುತ್ತಾಳೆ. ಆದರೆ ವಿವಾಹಿತ ಸ್ತ್ರೀ ಲೋಕವ್ಯವಹಾರಗಳಲ್ಲಿ ಮಗ್ನಳಾಗಿರುತ್ತಾಳೆ. ತನ್ನ ಪತಿಯನ್ನು ಮೆಚ್ಚಿಸಲು ಪ್ರಯತ್ನಿಸುತ್ತಾಳೆ.",
			"35": "<sup>35</sup> ನಿಮ್ಮನ್ನು ಬಂಧಿಸಲು ಅಲ್ಲ, ಬೆಂಬಲಿಸಲು ಇದನ್ನು ಹೇಳುತ್ತಿದ್ದೇನೆ. ನೀವು ಸಜ್ಜನರಾಗಿ ನಡೆದುಕೊಂಡು, ಭಿನ್ನಭಾವವಿಲ್ಲದೆ ಪ್ರಭುವಿನ ಪಾದಸೇವೆ ಮಾಡಬೇಕೆಂಬುದೇ ನನ್ನ ಆಶೆ.",
			"36": "<sup>36</sup> ಒಬ್ಬನು ತನಗೆ ನಿಶ್ಚಿತವಾದ ಕನ್ಯೆಯೊಡನೆ ಅನುಚಿತ ರೀತಿಯಲ್ಲಿ ವರ್ತಿಸುತ್ತಿರಬಹುದು; ಅವನಿಗೆ ಸಂಯಮ ತಪ್ಪುವಂತಿದ್ದರೆ, ತಾನು ಆಶಿಸುವಂತೆ ಅವನು ಮದುವೆಮಾಡಿಕೊಳ್ಳಲಿ, ಅದು ಪಾಪವಲ್ಲ.",
			"37": "<sup>37</sup> ಆದರೆ ಅವನು ಯಾರ ಒತ್ತಾಯಕ್ಕೂ ಮಣಿಯದೆ ದೃಢಚಿತ್ತನಾಗಿದ್ದು, ತನಗೆ ನಿಶ್ಚಿತಳಾದ ಕನ್ಯೆಯೊಡನೆ ಮದುವೆಯಾಗುವುದು ಬೇಡವೆಂದು ತೀರ್ಮಾನಿಸಿಕೊಂಡರೆ ಮತ್ತು ತನ್ನ ಸಂಯಮವನ್ನು ಹತೋಟಿಯಲ್ಲಿಟ್ಟುಕೊಂಡಿದ್ದರೆ ಅವನು ಅಂತೆಯೇ ಮಾಡಲಿ. ಹೀಗೆ, ಮದುವೆ ಆಗದೆ ಇರುವುದು ಒಳ್ಳೆಯದು.",
			"38": "<sup>38</sup> ಈ ರೀತಿ ನಿಶ್ಚಯವಾದ ಕನ್ಯೆಯನ್ನು ಮದುವೆಮಾಡಿಕೊಳ್ಳುವುದು ಒಳ್ಳೆಯದೇ, ಮದುವೆ ಮಾಡಿಕೊಳ್ಳದಿರುವುದು ಇನ್ನಷ್ಟು ಒಳ್ಳೆಯದೇ.",
			"39": "<sup>39</sup> ಪತಿ ಬದುಕಿರುವವರೆಗೂ ಸತಿ ಅವನಿಗೆ ಬಂಧಿತಳು. ಅವನು ಸತ್ತರೆ ತಾನು ಇಷ್ಟಪಟ್ಟವನನ್ನು ಮತ್ತೆ ಮದುವೆ ಆಗಲು ಸ್ವತಂತ್ರಳು. ಆದರೆ ಅವನು ಕ್ರೈಸ್ತವಿಶ್ವಾಸಿಯಾಗಿರಬೇಕು.",
			"40": "<sup>40</sup> ಆಕೆ ವಿವಾಹವಾಗದೆ ಹಾಗೇ ಉಳಿದರೆ ಹೆಚ್ಚು ಭಾಗ್ಯವಂತಳು. ಇದು ನನ್ನ ಅಭಿಪ್ರಾಯ. ಆದರೂ ದೇವರ ಆತ್ಮದ ಪ್ರೇರಣೆಯಿಂದ ಇದನ್ನು ಹೇಳುತ್ತಿದ್ದೇನೆ, ಎಂಬುದೇ ನನ್ನ ನಂಬಿಕೆ."
		},
		"8": {
			"1": "<sup>1</sup> ವಿಗ್ರಹಗಳಿಗೆ ನೈವೇದ್ಯಮಾಡಿದ ಆಹಾರಪದಾರ್ಥಗಳ ವಿಷಯವಾಗಿ ನಾನು ಹೇಳುವುದೇನೆಂದರೆ: “ನಮಗೆಲ್ಲರಿಗೂ ಜ್ಞಾನೋದಯವಾಗಿದೆ” ಎಂದು ಹೇಳಿಕೊಳ್ಳುತ್ತೀರಿ. ಜ್ಞಾನವು ನಮ್ಮನ್ನು ಅಹಂಕಾರಿಗಳನ್ನಾಗಿ ಮಾಡುತ್ತದೆ. ಪ್ರೀತಿಯಾದರೋ ಭಕ್ತಿಯನ್ನು ವೃದ್ಧಿಗೊಳಿಸುತ್ತದೆ.",
			"2": "<sup>2</sup> ಯಾರಾದರೂ ತಾನು ಬಲ್ಲವನೆಂದು ಕೊಚ್ಚಿಕೊಳ್ಳುವುದಾದರೆ ತಾನು ತಿಳಿಯಬೇಕಾದುದನ್ನು ಅವನು ಸರಿಯಾಗಿ ತಿಳಿದಿರುವುದಿಲ್ಲ.",
			"3": "<sup>3</sup> ಆದರೆ ದೇವರನ್ನು ಪ್ರೀತಿಸುವವನನ್ನು ದೇವರು ಗುರುತಿಸುತ್ತಾರೆ.",
			"4": "<sup>4</sup> ವಿಗ್ರಹಗಳಿಗೆ ನೈವೇದ್ಯಮಾಡಿದ ಪದಾರ್ಥಗಳನ್ನು ತಿನ್ನುವ ಬಗ್ಗೆ ನನ್ನ ಅಭಿಪ್ರಾಯ ಹೀಗಿದೆ: ವಿಗ್ರಹಗಳಿಗೆ ನಿಜವಾದ ಅಸ್ತಿತ್ವವಿಲ್ಲ. ದೇವರು ಒಬ್ಬರೇ ಹೊರತು ಬೇರೆ ದೇವರಿಲ್ಲ. ಇದು ನಮಗೆ ತಿಳಿದ ವಿಷಯ.",
			"5": "<sup>5</sup> “ದೇವರುಗಳು” ಎನಿಸಿಕೊಳ್ಳುವವರು ಇಹಪರಗಳಲ್ಲಿ ಇರಬಹುದು; ಅಂಥ ಹಲವಾರು “ದೇವರು\"ಗಳನ್ನೂ “ಪ್ರಭು\"ಗಳನ್ನೂ ಪೂಜಿಸುವುದುಂಟು.",
			"6": "<sup>6</sup> ಆದರೆ ನಮಗೆ ಒಬ್ಬರೇ ದೇವರು. ಅವರು ನಮ್ಮ ಪರಮ ಪಿತ; ಎಲ್ಲ ಸೃಷ್ಟಿಗೂ ಮೂಲಕರ್ತ, ನಾವಿರುವುದು ಅವರಿಗಾಗಿಯೇ. ನಮಗೆ ಒಬ್ಬರೇ ಪ್ರಭು; ಅವರೇ ಯೇಸುಕ್ರಿಸ್ತ. ಅವರ ಮುಖಾಂತರವೇ ಸಮಸ್ತವೂ ಉಂಟಾಯಿತು. ಅವರ ಮುಖಾಂತರವೇ ನಾವು ಜೀವಿಸುತ್ತೇವೆ.",
			"7": "<sup>7</sup> ಆದರೆ ಎಲ್ಲರಿಗೂ ಇದರ ಅರಿವಿಲ್ಲ. ಕೆಲವರು ಸಾಂಪ್ರದಾಯಕವಾಗಿ ಬಂದ ವಿಗ್ರಹಾರಾಧನೆಯಲ್ಲಿ ಭಾಗವಹಿಸಿ, ವಿಗ್ರಹಕ್ಕೆ ಅರ್ಪಿತವಾದ ನೈವೇದ್ಯಪದಾರ್ಥವನ್ನು ತಿನ್ನುತ್ತಾರೆ. ಆದರೆ ಅವರ ಮನಸ್ಸಾಕ್ಷಿ ದುರ್ಬಲವಾಗಿರುವುದರಿಂದ ತಾವು ಕಲುಷಿತರಾದೆವೆಂದು ಭಾವಿಸಿಕೊಳ್ಳುತ್ತಾರೆ.",
			"8": "<sup>8</sup> ನಾವು ತಿನ್ನುವ ಆಹಾರ ನಮ್ಮನ್ನು ದೇವರ ಸನ್ನಿಧಿಗೆ ಸೇರಿಸಲಾರದು. ಅದನ್ನು ತಿನ್ನದಿರುವುದರಿಂದ ನಮಗೆ ನಷ್ಟವಿಲ್ಲ, ತಿಂದರೆ ಲಾಭವಿಲ್ಲ.",
			"9": "<sup>9</sup> ಆದರೆ ನಿಮ್ಮ ಸ್ವಾತಂತ್ರ್ಯವನ್ನು ಉಪಯೋಗಿಸುವಾಗ ದುರ್ಬಲಮನಸ್ಸುಳ್ಳ ಸೋದರನ ವಿಶ್ವಾಸಕ್ಕೆ ಅಡ್ಡಿಯಾಗದಂತೆ ಎಚ್ಚರಿಕೆ ವಹಿಸಿರಿ.",
			"10": "<sup>10</sup> ನೈಜ ಅರಿವುಳ್ಳವನೆಂದು ಹೇಳಿಕೊಳ್ಳುವ ನೀನು ವಿಗ್ರಹದ ಗುಡಿಯಲ್ಲಿ ಊಟಕ್ಕೆ ಕುಳಿತಿರುವುದನ್ನು ದುರ್ಬಲ ಮನಸ್ಸಾಕ್ಷಿಯುಳ್ಳ ಸೋದರನು ಕಂಡರೆ, ಅವನು ಸಹ ನೈವೇದ್ಯಮಾಡಿದ ಪದಾರ್ಥವನ್ನು ತಿನ್ನಲು ಧೈರ್ಯಗೊಳ್ಳಲಾರನೇ?",
			"11": "<sup>11</sup> ಈ ನಿನ್ನ ಅರಿವಿನಿಂದ ದುರ್ಬಲ ಸೋದರನು ನಾಶಕ್ಕೆ ಗುರಿಯಾಗುತ್ತಾನೆ. ಯೇಸುಕ್ರಿಸ್ತರು ಅವನಿಗಾಗಿಯೂ ಪ್ರಾಣತ್ಯಾಗ ಮಾಡಲಿಲ್ಲವೇ?",
			"12": "<sup>12</sup> ಹೀಗೆ ನೀವು ನಿಮ್ಮ ಸಹೋದರರ ದುರ್ಬಲ ಮನಸ್ಸಾಕ್ಷಿಯನ್ನು ನೋಯಿಸಿ, ಅವರ ವಿರುದ್ಧ ಪಾಪ ಕಟ್ಟಿಕೊಳ್ಳುವುದರಿಂದ ಯೇಸುಕ್ರಿಸ್ತರ ವಿರುದ್ಧವೇ ಪಾಪಮಾಡುತ್ತೀರಿ.",
			"13": "<sup>13</sup> ನಾನು ತಿನ್ನುವ ಆಹಾರ ನನ್ನ ಸಹೋದರನ ವಿಶ್ವಾಸಕ್ಕೆ ಅಡ್ಡಿಯಾಗುವುದಾದರೆ ಎಂದಿಗೂ ನಾನದನ್ನು ತಿನ್ನಲಾರೆ, ಅವನ ವಿಶ್ವಾಸಕ್ಕೆ ಅಡ್ಡಿಯನ್ನು ತರಲಾರೆ."
		},
		"9": {
			"1": "<sup>1</sup> ನಾನು ಸ್ವತಂತ್ರನಲ್ಲವೇ? ಪ್ರೇಷಿತನಲ್ಲವೇ? ನಮ್ಮ ಪ್ರಭು ಯೇಸುವನ್ನು ಕಂಡವನಲ್ಲವೇ? ಪ್ರಭುವಿನಲ್ಲಿ ನಾನು ಮಾಡಿದ ಸೇವೆಯ ಪ್ರತಿಫಲ ನೀವಲ್ಲವೇ?",
			"2": "<sup>2</sup> ಇತರರಿಗೆ ನಾನು ಪ್ರೇಷಿತನಲ್ಲದಿದ್ದರೂ ನಿಮಗಂತೂ ನಾನು ಪ್ರೇಷಿತನೇ. ನಾನು ಪ್ರೇಷಿತನು ಎಂಬುದಕ್ಕೆ ಪ್ರಭುವಿಗೆ ಸೇರಿದವರಾದ ನೀವೇ ಜೀವಂತ ಸಾಕ್ಷಿಗಳು.",
			"3": "<sup>3</sup> ನನ್ನನ್ನು ಟೀಕಿಸುವವರಿಗೆ ನನ್ನ ಉತ್ತರ ಇದು:",
			"4": "<sup>4</sup> ನಿಮ್ಮಿಂದ ಅನ್ನಪಾನಗಳನ್ನು ಪಡೆಯಲು ನನಗೆ ಹಕ್ಕಿಲ್ಲವೇ?",
			"5": "<sup>5</sup> ಮಿಕ್ಕ ಪ್ರೇಷಿತರಂತೆ, ಪ್ರಭುವಿನ ಸಹೋದರರಂತೆ, ಕೇಫನಂತೆ ನಾನು ಸಹ ಕ್ರೈಸ್ತವಿಶ್ವಾಸಿಯೊಬ್ಬಳನ್ನು ಮದುವೆಮಾಡಿಕೊಂಡು ಆಕೆಯೊಡನೆ ಪ್ರಯಾಣ ಕೈಗೊಳ್ಳಲು ನನಗೆ ಹಕ್ಕಿಲ್ಲವೇ?",
			"6": "<sup>6</sup> ಜೀವನೋಪಾಯಕ್ಕಾಗಿ ಬಾರ್ನಬ ಮತ್ತು ನಾನು ಮಾತ್ರ ದುಡಿಯಬೇಕೇ?",
			"7": "<sup>7</sup> ಯಾವ ಸೈನಿಕ ಸ್ವಂತ ಖರ್ಚಿನಿಂದ ಸಮರಕ್ಕೆ ನಿಲ್ಲುತ್ತಾನೆ? ಯಾವ ತೋಟಗಾರ ತನ್ನ ಸ್ವಂತ ದ್ರಾಕ್ಷಿಯ ತೋಟದಿಂದ ದ್ರಾಕ್ಷಿಯ ಫಲವನ್ನು ತಿನ್ನದಿರುತ್ತಾನೆ? ಯಾವ ಗೌಳಿಗ ತಾನು ಸಾಕಿದ ಹಸುವಿನ ಹಾಲನ್ನು ಕುಡಿಯದಿರುತ್ತಾನೆ?",
			"8": "<sup>8</sup> ಇವು ಕೇವಲ ಲೋಕರೂಢಿಯ ಮಾತುಗಳಲ್ಲ. ಧರ್ಮಶಾಸ್ತ್ರವೂ ಇದನ್ನು ಅನುಮೋದಿಸುತ್ತದೆ.",
			"9": "<sup>9</sup> “ಕಣ ತುಳಿಯುವ ಎತ್ತಿನ ಬಾಯನ್ನು ಕಟ್ಟಬೇಡ,” ಎಂದು ಮೋಶೆಯ ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲೇ ಲಿಖಿತವಾಗಿದೆ. ಇಲ್ಲಿ ದೇವರಿಗೆ ಎತ್ತಿನ ಮೇಲೆ ಚಿಂತೆಯೇ? ಇಲ್ಲ.",
			"10": "<sup>10</sup> ಇದನ್ನು ನಮ್ಮ ಹಿತಚಿಂತನೆಯಿಂದ ಹೇಳಲಾಗಿದೆ; ಅದೇ ಉದ್ದೇಶದಿಂದ ಬರೆಯಲಾಗಿದೆ. ಹೌದು, ಉಳುವವನೂ ಒಕ್ಕುವವನೂ ಕಾಳುಬೆಳೆಯಲ್ಲಿ ಪಾಲುಪಡೆಯುವ ಉದ್ದೇಶದಿಂದಲೇ ದುಡಿಯುತ್ತಾರೆ.",
			"11": "<sup>11</sup> ನಿಮ್ಮಲ್ಲಿ ಆಧ್ಯಾತ್ಮಿಕ ಬೀಜವನ್ನು ಬಿತ್ತನೆಮಾಡಿದ ನಾವು ಪ್ರತಿಫಲವಾಗಿ ದೈನಂದಿನ ಅಗತ್ಯಗಳನ್ನು ಅಪೇಕ್ಷಿಸುವುದು ಅತಿಯಾಯಿತೇ?",
			"12": "<sup>12</sup> ಇತರರಿಗೆ ನಿಮ್ಮ ಮೇಲೆ ಇಂಥ ಹಕ್ಕಿದ್ದರೆ ಅದಕ್ಕಿಂತಲೂ ಹೆಚ್ಚು ಹಕ್ಕು ನಮಗಿರಬೇಕಲ್ಲವೇ? ಆದರೂ ನಾವು ಈ ಹಕ್ಕನ್ನು ಚಲಾಯಿಸಲೇ ಇಲ್ಲ. ಬದಲಿಗೆ, ಕ್ರಿಸ್ತಯೇಸುವಿನ ಶುಭಸಂದೇಶಕ್ಕೆ ಯಾವ ಅಡ್ಡಿಯೂ ಬಾರದಿರಲೆಂದು ಎಲ್ಲವನ್ನು ಸಹಿಸಿಕೊಂಡೆವು.",
			"13": "<sup>13</sup> ದೇವಾಲಯದ ಸೇವೆಯಲ್ಲಿರುವವರು ದೇವಾಲಯದ ಆದಾಯದಿಂದ ಜೀವಿಸುತ್ತಾರೆ. ಬಲಿಪೀಠದ ಬಳಿ ಸೇವೆಮಾಡುವವರು ಅರ್ಪಿತವಾದ ಬಲಿಯಿಂದ ಪಾಲನ್ನು ಪಡೆಯುತ್ತಾರೆ. ಇದು ನಿಮಗೆ ತಿಳಿದಿದೆ.",
			"14": "<sup>14</sup> ಅದೇ ಪ್ರಕಾರ ಶುಭಸಂದೇಶ ಪ್ರಚಾರಕರು ಶುಭಸಂದೇಶ ಪ್ರಚಾರದ ಆದಾಯದಿಂದಲೇ ಜೀವನ ನಡೆಸಬೇಕೆಂದು ಪ್ರಭುವೇ ಆಜ್ಞಾಪಿಸಿದ್ದಾರೆ.",
			"15": "<sup>15</sup> ನಾನಂತೂ ಈ ಹಕ್ಕುಗಳಲ್ಲಿ ಒಂದನ್ನೂ ಚಲಾಯಿಸಲಿಲ್ಲ. ಚಲಾಯಿಸಬೇಕೆಂಬ ಉದ್ದೇಶದಿಂದಲೂ ಇದನ್ನು ಬರೆಯುತ್ತಿಲ್ಲ. ಅದಕ್ಕಿಂತಲೂ ಸಾಯುವುದೇ ಲೇಸು. ಈ ನನ್ನ ಹೆಮ್ಮೆಯನ್ನು ಯಾರೂ ಅಪಹರಿಸಲಾರರು.",
			"16": "<sup>16</sup> ಶುಭಸಂದೇಶವನ್ನು ನಾನು ಸಾರುತ್ತಿದ್ದೇನೆಂದು ಕೊಚ್ಚಿಕೊಳ್ಳುವುದಕ್ಕೂ ನನಗೆ ಆಸ್ಪದವಿಲ್ಲ. ಏಕೆಂದರೆ, ಸಾರಲೇಬೇಕೆಂಬ ಕರ್ತವ್ಯಕ್ಕೆ ನಾನು ಬದ್ಧನಾಗಿದ್ದೇನೆ. ಸಾರದಿದ್ದರೆ ನನಗೆ ಧಿಕ್ಕಾರವಿರಲಿ!",
			"17": "<sup>17</sup> ನಾನು ಸ್ವಂತ ಇಷ್ಟದಿಂದ ಈ ಕಾರ್ಯವನ್ನು ಕೈಗೊಂಡಿದ್ದರೆ ತಕ್ಕ ಸಂಭಾವನೆ ಇರುತ್ತಿತ್ತು. ಆದರೆ ಈ ಕರ್ತವ್ಯವನ್ನು ನನಗೆ ವಹಿಸಲಾಗಿದೆ. ಆದ್ದರಿಂದ ಕರ್ತವ್ಯಬದ್ಧನಾಗಿ ಮಾಡುತ್ತಿದ್ದೇನೆ.",
			"18": "<sup>18</sup> ಹಾಗಾದರೆ ನನಗೆ ಸಿಗುವ ಸಂಭಾವನೆಯಾದರೂ ಏನು? ಶುಭಸಂದೇಶವನ್ನು ಸಾರುವಾಗ ಜೀವನೋಪಾಯಕ್ಕಾಗಿ ನನಗಿರುವ ಹಕ್ಕನ್ನು ಬಳಸಿಕೊಳ್ಳದೆ ಉಚಿತವಾಗಿ ಸಾರುವ ಸೌಭಾಗ್ಯವೇ ನನ್ನ ಸಂಭಾವನೆ.",
			"19": "<sup>19</sup> ನಾನು ಸ್ವತಂತ್ರನು, ಯಾರಿಗೂ ದಾಸನಲ್ಲ. ಆದರೂ ಆದಷ್ಟು ಜನರನ್ನು ಗಳಿಸಿಕೊಳ್ಳಲೆಂದು ಎಲ್ಲರಿಗೂ ದಾಸನಾದೆ.",
			"20": "<sup>20</sup> ಯೆಹೂದ್ಯರನ್ನು ಗಳಿಸಿಕೊಳ್ಳಲು ಯೆಹೂದ್ಯರಿಗೆ ಯೆಹೂದ್ಯನಂತಾದೆ. ಧರ್ಮಶಾಸ್ತ್ರಕ್ಕೆ ಅಧೀನರಾದವರನ್ನು ಗಳಿಸಿಕೊಳ್ಳಲು ನಾನು ಆ ಶಾಸ್ತ್ರಕ್ಕೆ ಅಧೀನನಲ್ಲದಿದ್ದರೂ ಅವರಿಗೋಸ್ಕರ ಅಧೀನನಂತಾದೆ.",
			"21": "<sup>21</sup> ಧರ್ಮಶಾಸ್ತ್ರಕ್ಕೆ ಬಾಹಿರರಾದವರನ್ನು ಗಳಿಸಿಕೊಳ್ಳಲು ನಾನು ಧರ್ಮಶಾಸ್ತ್ರಕ್ಕೆ ಬಾಹಿರನಂತೆ ಆದೆ. ಯೇಸುಕ್ರಿಸ್ತರ ನಿಯಮಕ್ಕೆ ನಾನು ವಿಧೇಯನಾದುದರಿಂದ ದೇವರ ನಿಯಮಕ್ಕೆ ನಾನು ಬಾಹಿರನೇನೂ ಅಲ್ಲ.",
			"22": "<sup>22</sup> ವಿಶ್ವಾಸದಲ್ಲಿ ದುರ್ಬಲರನ್ನು ಗಳಿಸಿಕೊಳ್ಳಲು ನಾನೂ ದುರ್ಬಲನಂತಾದೆ. ಹೇಗಾದರೂ ಸರಿ, ಕೆಲವರನ್ನಾದರೂ ಉದ್ಧರಿಸಲು ಎಲ್ಲರಿಗೂ ಎಲ್ಲವೂ ಆದೆ.",
			"23": "<sup>23</sup> ಶುಭಸಂದೇಶದ ಸೌಭಾಗ್ಯದಲ್ಲಿ ಪಾಲುಗೊಳ್ಳಲೆಂದು ಇದೆಲ್ಲವನ್ನು ಶುಭಸಂದೇಶಕ್ಕಾಗಿಯೇ ಮಾಡುತ್ತೇನೆ,",
			"24": "<sup>24</sup> ನಿಮಗೆ ತಿಳಿದಿರುವಂತೆ ಕ್ರೀಡಾಂಗಣದಲ್ಲಿ ಅನೇಕರು ಓಟದ ಸ್ಪರ್ಧೆಯಲ್ಲಿ ಭಾಗವಹಿಸುತ್ತಾರೆ. ಆದರೆ ಪ್ರಥಮ ಬಹುಮಾನ ದೊರಕುವುದು ಒಬ್ಬನಿಗೆ ಮಾತ್ರ. ಅಂತೆಯೇ ಬಹುಮಾನವನ್ನು ಪಡೆದುಕೊಳ್ಳಲು ನೀವೂ ಓಡಿ.",
			"25": "<sup>25</sup> ಕ್ರೀಡಾಪಟುಗಳೆಲ್ಲರೂ ತಮ್ಮನ್ನು ಹತೋಟಿಯಲ್ಲಿಟ್ಟುಕೊಳ್ಳುತ್ತಾರೆ. ಅಳಿದುಹೋಗುವ ಪದಕದ ಗಳಿಕೆಗಾಗಿ ಪ್ರಯತ್ನಮಾಡುತ್ತಾರೆ. ನಾವಾದರೋ ಅಮರ ಪದಕದ ಗಳಿಕೆಗಾಗಿ ಶ್ರಮಿಸುತ್ತೇವೆ.",
			"26": "<sup>26</sup> ನಾನಾದರೋ ಗೊತ್ತು ಗುರಿಯಿಲ್ಲದವನಂತೆ ಓಡುವುದಿಲ್ಲ, ಗಾಳಿಯೊಡನೆ ಗುದ್ದಾಡುವವನಂತೆ ಹೋರಾಡುವುದಿಲ್ಲ.",
			"27": "<sup>27</sup> ಇತರರಿಗೆ ಕರೆಕೊಟ್ಟ ಮೇಲೆ ನಾನೇ ಅಯೋಗ್ಯನಾಗದಂತೆ, ನನ್ನ ದೇಹವನ್ನು ದಂಡಿಸಿ, ನನ್ನನ್ನೇ ನಾನು ಹತೋಟಿಯಲ್ಲಿಟ್ಟುಕೊಳ್ಳುತ್ತೇನೆ."
		},
		"10": {
			"1": "<sup>1</sup> ಪ್ರಿಯ ಸಹೋದರರೇ, ನಮ್ಮ ಪೂರ್ವಜರಿಗೆ ಸಂಭವಿಸಿದವುಗಳತ್ತ ನಿಮ್ಮ ಗಮನವನ್ನು ಸೆಳೆಯಬೇಕೆಂದಿದ್ದೇನೆ. ಅವರೆಲ್ಲರೂ ಮೋಡದ ನೆರಳಿನಲ್ಲಿಯೇ ನಡೆದರು. ಎಲ್ಲರೂ ಸಮುದ್ರವನ್ನು ಸುರಕ್ಷಿತವಾಗಿ ದಾಟಿದರು.",
			"2": "<sup>2</sup> ಹೀಗೆ ಅವರು ಮೋಡದಲ್ಲಿಯೂ ಸಮುದ್ರದಲ್ಲಿಯೂ ಸ್ನಾನದೀಕ್ಷೆಯನ್ನು ಪಡೆದು ಮೋಶೆಗೆ ಶಿಷ್ಯರಾದರು.",
			"3": "<sup>3</sup> ಎಲ್ಲರೂ ಒಂದೇ ದೈವಿಕ ಪಾನೀಯವನ್ನು ಕುಡಿದರು;",
			"4": "<sup>4</sup> ಅದೂ ತಮ್ಮೊಡನೆ ಬರುತ್ತಿದ್ದ ದೈವಿಕ ಬಂಡೆಯಿಂದ ಕುಡಿದರು. ಕ್ರಿಸ್ತಯೇಸುವೇ ಆ ಬಂಡೆ.",
			"5": "<sup>5</sup> ಆದರೂ ಅವರಲ್ಲಿ ಬಹುಮಂದಿ ದೇವರ ಮೆಚ್ಚುಗೆಗೆ ಪಾತ್ರರಾಗಲಿಲ್ಲ. ಆದ್ದರಿಂದಲೇ ಮರುಭೂಮಿಯಲ್ಲೆ ಮಡಿದು ಮಣ್ಣುಪಾಲಾದರು.",
			"6": "<sup>6</sup> ಈ ಘಟನೆಗಳು ನಮಗೆ ಎಚ್ಚರಿಕೆ ನೀಡುವ ನಿದರ್ಶನಗಳಾಗಿವೆ. ಅವರಂತೆ ನಾವು ಕೆಟ್ಟದಾದವುಗಳನ್ನು ಆಶಿಸಬಾರದು.",
			"7": "<sup>7</sup> ಅವರಲ್ಲಿ ಕೆಲವರು ವಿಗ್ರಹಾರಾಧಕರಾದಂತೆ ನಾವು ಆಗಬಾರದು. ಇವರನ್ನು ಕುರಿತೇ : “ತಿನ್ನಲೆಂದು ಕುಡಿಯಲೆಂದು ಕುಳಿತರು ಕುಣಿಯಲೆಂದು ಕಾಮವೆದ್ದು ನಿಂತರು ಈ ಜನರು” - ಎಂದು ಬರೆದಿದೆ.",
			"8": "<sup>8</sup> ಅವರಲ್ಲಿ ಕೆಲವರು ಹಾದರಮಾಡಿದ್ದರಿಂದ ಒಂದೇ ದಿನದಲ್ಲಿ ಇಪ್ಪತ್ತಮೂರು ಸಾವಿರಮಂದಿ ಸತ್ತರು. ನಾವು ಹಾದರದಿಂದ ದೂರವಿರಬೇಕು.",
			"9": "<sup>9</sup> ಅವರ ಪೈಕಿ ಕೆಲವರು ಪ್ರಭುವನ್ನು ಶೋಧಿಸಿದರು. ಪರಿಣಾಮವಾಗಿ ಸರ್ಪಗಳಿಂದ ನಾಶವಾದರು. ಅವರಂತೆ ಪ್ರಭುವನ್ನು ಶೋಧನೆಮಾಡುವುದು ನಮಗೆ ಬೇಡ.",
			"10": "<sup>10</sup> ಮತ್ತೆ ಕೆಲವರು ಗೊಣಗುಟ್ಟಿದರು; ಸಂಹಾರಕ ದೂತನಿಂದ ಸಂಹರಿಸಲ್ಪಟ್ಟರು. ನೀವೂ ಅವರಂತೆ ಗೊಣಗುಟ್ಟುವವರಾಗಬೇಡಿ.",
			"11": "<sup>11</sup> ಇದೆಲ್ಲ ಅವರಿಗೆ ಸಂಭವಿಸಿದುದು ಇತರರಿಗೆ ನಿದರ್ಶನವಾಗಿರಲೆಂದು. ಇವುಗಳನ್ನು ಬರೆದಿಟ್ಟಿರುವುದು, ಯುಗಾಂತ್ಯಕ್ಕೆ ಬಂದಿರುವ ನಮಗೆ ಮುನ್ನೆಚ್ಚರಿಕೆಯಾಗಿರಲೆಂದು.",
			"12": "<sup>12</sup> ಆದಕಾರಣ ಸ್ಥಿರವಾಗಿ ನಿಂತಿರುವೆನೆಂದು ಕೊಚ್ಚಿಕೊಳ್ಳುವವನೇ, ಕುಸಿದುಬೀಳದಂತೆ ಎಚ್ಚರಿಕೆಯಿಂದಿರು.",
			"13": "<sup>13</sup> ಮಾನವರಿಂದ ಜಯಿಸಲಾಗದ ಶೋಧನೆಗಳೇನೂ ನಿಮಗೆ ಬಂದಿಲ್ಲ. ದೇವರು ಕೊಟ್ಟಮಾತಿಗೆ ತಪ್ಪಲಾರರು. ಗೆಲ್ಲಲಾಗದ ಶೋಧನೆಗಳಿಗೆ ನಿಮ್ಮನ್ನೆಂದೂ ಗುರಿಪಡಿಸಲಾರರು. ಶೋಧನೆಗಳು ಬಂದಾಗ ಅವುಗಳನ್ನು ಸಹಿಸಿಕೊಳ್ಳುವ ಶಕ್ತಿಯನ್ನೂ ತಪ್ಪಿಸಿಕೊಳ್ಳುವ ಮಾರ್ಗವನ್ನೂ ಅವರೇ ಒದಗಿಸುತ್ತಾರೆ.",
			"14": "<sup>14</sup> ಆದ್ದರಿಂದ ಪ್ರಿಯರೇ, ವಿಗ್ರಹಾರಾಧನೆಯನ್ನು ತೊರೆದುಬಿಡಿ.",
			"15": "<sup>15</sup> ನೀವು ಅರಿತವರೆಂದು ತಿಳಿದೇ ಇದನ್ನು ಹೇಳುತ್ತಿದ್ಧೇನೆ. ನನ್ನ ಮಾತುಗಳನ್ನು ನೀವೇ ಪರಿಶೀಲಿಸಿನೋಡಿ.",
			"16": "<sup>16</sup> ನಾವು ದೇವರಿಗೆ ಕೃತಜ್ಞತಾಸ್ತೋತ್ರವನ್ನು ಸಲ್ಲಿಸಿ ಪಾನಪಾತ್ರೆಯಿಂದ ಕುಡಿಯುವಾಗ ಕ್ರಿಸ್ತಯೇಸುವಿನ ರಕ್ತದಲ್ಲಿ ಪಾಲುಗೊಳ್ಳುತ್ತೇವಲ್ಲವೇ? ರೊಟ್ಟಿಯನ್ನು ಮುರಿದು ತಿನ್ನುವಾಗ ಕ್ರಿಸ್ತಯೇಸುವಿನ ಶರೀರದಲ್ಲಿ ಪಾಲುಗೊಳ್ಳುತ್ತೇವಲ್ಲವೇ?",
			"17": "<sup>17</sup> ರೊಟ್ಟಿ ಒಂದೇ; ಆದ್ದರಿಂದ ನಾವು ಅನೇಕರಿದ್ದರೂ ಒಂದೇ ಶರೀರವಾಗುತ್ತೇವೆ. ಏಕೆಂದರೆ ನಾವೆಲ್ಲರೂ ಆ ಒಂದೇ ರೊಟ್ಟಿಯಲ್ಲಿ ಭಾಗಿಗಳಾಗುತ್ತೇವೆ.",
			"18": "<sup>18</sup> ಇಸ್ರಯೇಲರ ಪದ್ಧತಿಯನ್ನು ಗಮನಿಸಿರಿ: ಬಲಿಯಾಗಿ ಅರ್ಪಿತವಾದುದನ್ನು ಭುಜಿಸುವವರು ಬಲಿಪೀಠದ ಸೇವೆಯಲ್ಲಿ ಭಾಗಿಯಾಗುತ್ತಾರಷ್ಟೆ.",
			"19": "<sup>19</sup> ಹೀಗೆ ನಾನು ಹೇಳುವಾಗ ವಿಗ್ರಹಕ್ಕೆ ನೈವೇದ್ಯಮಾಡಿದ ಪದಾರ್ಥವು ವಾಸ್ತವವಾದುದು ಅಥವಾ ವಿಗ್ರಹವೇ ವಾಸ್ತವವಾದುದು ಎಂದು ಅರ್ಥವೇ?",
			"20": "<sup>20</sup> ಯಾವುದೂ ಅಲ್ಲ. ನಾನು ಹೇಳುವುದೇನೆಂದರೆ : ಅನ್ಯಜನರು ಬಲಿಯನ್ನು ದೆವ್ವಗಳಿಗೆ ಅರ್ಪಿಸುತ್ತಾರೆ ಹೊರತು ದೇವರಿಗಲ್ಲ. ಹೀಗೆ ನೀವು ದೆವ್ವಗಳೊಂದಿಗೆ ಭಾಗಿಯಾಗುವುದನ್ನು ನಾನೆಂದಿಗೂ ಇಷ್ಟಪಡುವುದಿಲ್ಲ.",
			"21": "<sup>21</sup> ನೀವು ಪ್ರಭುವಿನ ಪಾತ್ರೆಯಿಂದ ಹಾಗೂ ದೆವ್ವಗಳ ಪಾತ್ರೆಯಿಂದ ಕುಡಿಯಲಾಗದು. ಪ್ರಭುವಿನ ಪಂಕ್ತಿಯಲ್ಲೂ ಅಂತೆಯೇ ದೆವ್ವಗಳ ಪಂಕ್ತಿಯಲ್ಲೂ ಊಟಮಾಡಲಾಗದು.",
			"22": "<sup>22</sup> ನಾವು ಪ್ರಭುವನ್ನು ಅಸೂಯೆಗೆಬ್ಬಿಸಬಹುದೇ? ಅವರಿಗಿಂತ ನಾವು ಬಲಾಢ್ಯರೇ?",
			"23": "<sup>23</sup> “ಏನನ್ನು ಮಾಡಲೂ ಸ್ವಾತಂತ್ರ್ಯವಿದೆ,” ಎಂದು ಹೇಳುವುದುಂಟು. ಆದರೆ ಎಲ್ಲವೂ ಪ್ರಯೋಜನಕರವಲ್ಲ. ಏನನ್ನು ಮಾಡಲೂ ಸ್ವಾತಂತ್ರ್ಯವಿದೆ. ಆದರೆ ಎಲ್ಲವೂ ಅಭಿವೃದ್ಧಿಕರವಲ್ಲ.",
			"24": "<sup>24</sup> ಪ್ರತಿಯೊಬ್ಬನೂ ತನ್ನ ಹಿತವನ್ನು ಮಾತ್ರ ಬಯಸದೆ ಪರರ ಹಿತವನ್ನೂ ಬಯಸಬೇಕು.",
			"25": "<sup>25</sup> ಮನಸ್ಸಾಕ್ಷಿಯನ್ನು ಕೆದಕುವ ಪ್ರಶ್ನೆಗಳಿಗೆ ಎಡೆಕೊಡದೆ ಮಾಂಸದ ಮಾರುಕಟ್ಟೆಯಲ್ಲಿ ಮಾರುವುದೆಲ್ಲವನ್ನೂ ನೀವು ತಂದು ತಿನ್ನಬಹುದು.",
			"26": "<sup>26</sup> ಏಕೆಂದರೆ, “ಭೂಮಂಡಲವೂ ಅದರಲ್ಲಿರುವುದೆಲ್ಲವೂ ಪ್ರಭುವಿನದೆ.”",
			"27": "<sup>27</sup> ಕ್ರೈಸ್ತವಿಶ್ವಾಸಿ ಅಲ್ಲದವನು, ನಿಮ್ಮನ್ನು ಊಟಕ್ಕೆ ಕರೆದಾಗ, ನಿಮಗಿಷ್ಟವಿದ್ದರೆ ಹೋಗಿರಿ; ಮನಸ್ಸಾಕ್ಷಿಯನ್ನು ಕೆದಕುವ ಪ್ರಶ್ನೆಗಳಿಗೆ ಎಡೆಕೊಡದೆ ಅವನು ಬಡಿಸಿದ್ದನ್ನು ಊಟಮಾಡಿರಿ.",
			"28": "<sup>28</sup> ಆಗ ಯಾರಾದರೂ, “ಇದು ವಿಗ್ರಹಗಳಿಗೆ ಬಲಿಕೊಟ್ಟದ್ದು,” ಎಂದು ಹೇಳಿದರೆ, ಹಾಗೆ ತಿಳಿಸಿದವನ ಸಲುವಾಗಿ ಹಾಗೂ ಮನಸ್ಸಾಕ್ಷಿಯ ಸಲುವಾಗಿ ಅದನ್ನು ಊಟಮಾಡಬೇಡಿ.",
			"29": "<sup>29</sup> ಅಂದರೆ, ನಿನ್ನ ಸ್ವಂತ ಮನಸ್ಸಾಕ್ಷಿಯ ಸಲುವಾಗಿ ಅಲ್ಲ, ನಿನಗೆ ತಿಳಿಸಿದವನ ಮನಸ್ಸಾಕ್ಷಿಯ ಸಲುವಾಗಿ ಊಟಮಾಡಬೇಡ. ನಿಮ್ಮಲ್ಲಿ ಒಬ್ಬನು, “ನನ್ನ ಸ್ಯಾತಂತ್ರ್ಯಕ್ಕೆ ಇನ್ನೊಬ್ಬನ ಮನಸ್ಸಾಕ್ಷಿ ಏಕೆ ಅಡ್ಡಿಯಾಗಬೇಕು?” ಎಂದು ಕೇಳಬಹುದು.",
			"30": "<sup>30</sup> ಹಾಗೂ “ನಾನು ದೇವರಿಗೆ ಕೃತಜ್ಞತಾಸ್ತೋತ್ರ ಮಾಡಿ ಊಟಮಾಡಿದೆನಾದರೆ ಹಗೆ ಸ್ತೋತ್ರಸಲ್ಲಿಕೆಯೊಂದಿಗೆ ಮಾಡಿದ ಊಟಕ್ಕಾಗಿ ನನ್ನ ಮೇಲೆ ದೂಷಣೆ ಏಕೆ?” ಎಂದು ಕೇಳಬಹುದು.",
			"31": "<sup>31</sup> ಅದಕ್ಕೆ ನನ್ನ ಉತ್ತರ ಇದು: ನೀವು ಉಂಡರೂ ಕುಡಿದರೂ ಏನು ಮಾಡಿದರೂ ಎಲ್ಲವನ್ನೂ ದೇವರ ಮಹಿಮೆಗಾಗಿ ಮಾಡಿ.",
			"32": "<sup>32</sup> ಯೆಹೂದ್ಯರಿಗಾಗಲಿ, ಗ್ರೀಕರಿಗಾಗಲಿ, ದೇವರ ಧರ್ಮಸಭೆಗಾಗಲಿ ಬಾಧಕರಾಗಿರಬೇಡಿ.",
			"33": "<sup>33</sup> ನಾನಂತೂ ನನ್ನ ಸ್ವಂತ ಹಿತವನ್ನು ಲೆಕ್ಕಿಸದೆ ಸರ್ವರ ಉದ್ಧಾರಕ್ಕಾಗಿ ಅವರ ಹಿತವನ್ನು ಅಪೇಕ್ಷಿಸಿ, ಎಲ್ಲರನ್ನೂ ಎಲ್ಲದರಲ್ಲೂ ಮೆಚ್ಚಿಸಲು ಪ್ರಯತ್ನಿಸುತ್ತೇನೆ."
		},
		"11": {
			"1": "<sup>1</sup> ನಾನು ಕ್ರಿಸ್ತಯೇಸುವನ್ನು ಅನುಸರಿಸುವಂತೆ, ನೀವೂ ನನ್ನನ್ನೂ ಅನುಸರಿಸಿರಿ.",
			"2": "<sup>2</sup> ನೀವು ಸದಾ ನನ್ನನ್ನು ಸ್ಮರಿಸಿಕೊಂಡು ನಾನು ನಿಮಗೆ ತಿಳಿಸಿಕೊಟ್ಟ ಸಾಂಪ್ರದಾಯಿಕ ರೀತಿನೀತಿಗಳನ್ನು ಸರಿಯಾಗಿ ಪಾಲಿಸಿಕೊಂಡು ಬರುತ್ತಿದ್ದೀರಿ; ಇದಕ್ಕಾಗಿ ನಿಮ್ಮನ್ನು ಮೆಚ್ಚುತ್ತೇನೆ.",
			"3": "<sup>3</sup> ಒಂದು ವಿಷಯ ನಿಮಗೆ ತಿಳಿದಿರಲಿ. ಪ್ರತಿಯೊಬ್ಬ ವ್ಯಕ್ತಿಗೂ ಕ್ರಿಸ್ತಯೇಸುವೇ ಶಿರಸ್ಸು. ಪ್ರತಿಯೊಬ್ಬ ಸ್ತ್ರೀಗೂ ಪುರುಷನೇ ಶಿರಸ್ಸು. ಕ್ರಿಸ್ತೇಸುವಿಗೆ ದೇವರೇ ಶಿರಸ್ಸು.",
			"4": "<sup>4</sup> ಪುರುಷನು ತಲೆಯ ಮೇಲೆ ಮುಸುಕು ಹಾಕಿಕೊಂಡು ಪ್ರಾರ್ಥಿಸುವುದಾದರೆ ಅಥವಾ ದೇವರ ವಾಕ್ಯವನ್ನು ಪ್ರವಚಿಸುವುದಾದರೆ ತನ್ನ ಶಿರಸ್ಸಾದ ಕ್ರಿಸ್ತಯೇಸುವಿಗೆ ಅವಮಾನ ತರುತ್ತಾನೆ.",
			"5": "<sup>5</sup> ಸ್ತ್ರೀಯು ತಲೆಯ ಮೇಲೆ ಮುಸುಕು ಹಾಕದೆ ಪ್ರಾರ್ಥಿಸುವುದಾದರೆ ಅಥವಾ ದೇವರ ವಾಕ್ಯವನ್ನು ಪ್ರವಚಿಸುವುದಾದರೆ ತನ್ನ ಶಿರಸ್ಸಾದ ಪತಿಗೆ ಅವಮಾನ ತರುತ್ತಾಳೆ. ಮುಸುಕು ಹಾಕದ ಸ್ತ್ರೀ ತಲೆಯನ್ನು ಮುಂಡನ ಮಾಡಿಸಿಕೊಂಡಂತೆಯೇ.",
			"6": "<sup>6</sup> ಹಾಗೆ ಮುಸುಕು ಹಾಕದ ಸ್ತ್ರೀ ತನ್ನ ತಲೆಗೂದಲನ್ನು ತೆಗೆದುಬಿಡಲಿ. ಸ್ತ್ರೀಗೆ ತಲೆಗೂದಲನ್ನು ಕತ್ತರಿಸುವುದಾಗಲಿ, ಬೋಳಿಸುವುದಾಗಲಿ ಲಜ್ಜಾಸ್ಪದವಾಗಿದ್ದರೆ, ಆಕೆ ತಲೆಗೆ ಮುಸುಕು ಹಾಕಿಕೊಳ್ಳಲಿ.",
			"7": "<sup>7</sup> ಪುರುಷನು ತಲೆಯ ಮೇಲೆ ಮುಸುಕುಹಾಕಬೇಕಾಗಿಲ್ಲ. ಏಕೆಂದರೆ, ಅವನು ದೇವರ ರೂಪ ಹಾಗೂ ಅವರ ಮಹಿಮೆಯ ಪ್ರತಿಬಿಂಬ. ಸ್ತ್ರೀಯಾದರೋ, ಪುರುಷನ ಮಹಿಮೆಯ ಪ್ರತಿಬಿಂಬ.",
			"8": "<sup>8</sup> ಸ್ತ್ರೀಯಿಂದ ಪುರುಷನಲ್ಲ; ಪುರುಷನಿಂದ ಸ್ತ್ರೀ ಉತ್ಪತ್ತಿ ಆದಳು.",
			"9": "<sup>9</sup> ಅಂತೆಯೇ ಸ್ತ್ರೀಗಾಗಿ ಪುರುಷನಲ್ಲ; ಪುರುಷನಿಗಾಗಿ ಸ್ತ್ರೀ ಸೃಷ್ಟಿಯಾದಳು.",
			"10": "<sup>10</sup> ಹೀಗೆ ದೇವದೂತರ ಸಲುವಾಗಿಯಾದರೂ ಸ್ತ್ರೀಯು ಗೌರವ ಸೂಚಿಸಲು ಮುಸುಕನ್ನು ಹಾಕಬೇಕು.",
			"11": "<sup>11</sup> ಆದರೂ ಪ್ರಭುವಿನ ಮಟ್ಟಿಗೆ ಪುರುಷನಿಲ್ಲದೆ ಸ್ತ್ರೀಯು ಇಲ್ಲ; ಸ್ತ್ರೀ ಇಲ್ಲದೆ ಪುರುಷನೂ ಇಲ್ಲ.",
			"12": "<sup>12</sup> ಏಕೆಂದರೆ ಸ್ತ್ರೀ ಪುರುಷನಿಂದ ಉತ್ಪತ್ತಿಯಾದಂತೆ ಪುರುಷನು ಸ್ತ್ರೀಯಿಂದ ಜನಿಸುತ್ತಾನೆ. ಸಮಸ್ತವೂ ದೇವರಿಂದ ಹುಟ್ಟುತ್ತದೆ.",
			"13": "<sup>13</sup> ಸ್ತ್ರೀ ಮುಸುಕಿಲ್ಲದೆ ದೇವರನ್ನು ಪ್ರಾರ್ಥಿಸುವುದು ಲಕ್ಷಣವೇ? ನೀವೇ ತೀರ್ಮಾನಿಸಿರಿ.",
			"14": "<sup>14</sup> ಉದ್ದ ಕೂದಲು ಪುರುಷನಿಗೆ ಲಜ್ಜಾಸ್ಪದವೆಂಬುದೂ ನೀಳಕೂದಲು ಸ್ತ್ರೀಗೆ ಭೂಷಣವೆಂಬುದೂ ಸ್ವಭಾವಸಿದ್ಧವಲ್ಲವೇ? ಕೇಶರಾಶಿ ಸ್ತ್ರೀಗೆ ರಕ್ಷಾವರಣವಲ್ಲವೇ?",
			"16": "<sup>16</sup> ಇದರ ಬಗ್ಗೆ ಯಾರಾದರೂ ತರ್ಕಿಸಿದರೆ ಅಂಥವನಿಗೆ ಇದು ತಿಳಿದಿರಲಿ: ಇಂಥ ಪದ್ಧತಿ ನಮ್ಮಲ್ಲಿ ಇಲ್ಲ: ಇನ್ನಾವ ಧರ್ಮಸಭೆಗಳಲ್ಲೂ ಇಲ್ಲ.",
			"17": "<sup>17</sup> ಈಗ ನಾನು ಹೇಳಲಿರುವ ವಿಷಯದಲ್ಲಿ ನಿಮ್ಮನ್ನು ಮೆಚ್ಚಲಾರೆ. ನೀವು ಸಭೆ ಸೇರುವುದು ಒಳಿತಿಗಿಂತಲೂ ಹೆಚ್ಚಾಗಿ ಕೇಡನ್ನೆ ಉಂಟುಮಾಡುತ್ತಿದೆ.",
			"18": "<sup>18</sup> ಮೊದಲನೆಯದಾಗಿ, ನೀವು ಸೇರುವ ಸಭೆಯಲ್ಲಿ ಪಂಗಡಗಳಿವೆಯಂತೆ. ಸ್ವಲ್ಪಮಟ್ಟಿಗಾದರೂ ಈ ಸಮಾಚಾರ ನಿಜವೆಂದು ನನಗನ್ನಿಸುತ್ತದೆ.",
			"19": "<sup>19</sup> ನಿಮ್ಮಲ್ಲಿ ಭಿನ್ನಾಭಿಪ್ರಾಯಗಳು ಇರುವುದು ಸಹಜ. ಹಾಗಿರುವಾಗಲೇ ಪ್ರಾಮಾಣಿಕರು ಯಾರೆಂದು ಕಂಡುಕೊಳ್ಳಲು ಸಾಧ್ಯ.",
			"20": "<sup>20</sup> ಆದರೂ ನೀವೆಲ್ಲಾ ಒಂದಾಗಿ ಸೇರುವಾಗ ನೀವು ಮಾಡುವ ಭೋಜನ ಪ್ರಭುವಿನ ಭೋಜನವಲ್ಲ.",
			"21": "<sup>21</sup> ಏಕೆಂದರೆ, ಭೋಜನ ಸಮಯದಲ್ಲಿ ಒಬ್ಬೊಬ್ಬನೂ ತಾನು ತಂದದ್ದನ್ನು ತಿನ್ನಲು ಇತರರಿಗಿಂತ ಮುಂಚಿತವಾಗಿಯೇ ಕುಳಿತುಕೊಳ್ಳುತ್ತಾನೆ. ಒಬ್ಬನು ಹಸಿದಿರುತ್ತಾನೆ; ಇನ್ನೊಬ್ಬನು ಕುಡಿದು ಮತ್ತನಾಗುತ್ತಾನೆ.",
			"22": "<sup>22</sup> ತಿನ್ನಲು, ಕುಡಿಯಲು ನಿಮಗೆ ಸ್ವಂತ ಮನೆಗಳಿಲ್ಲವೇ? ನಿರ್ಗತಿಕರನ್ನು ನಾಚಿಕೆಗೀಡುಮಾಡಿ, ದೇವರ ಸಭೆಯನ್ನು ಉಪೇಕ್ಷಿಸಬೇಕೆಂದಿರುವಿರೋ? ಇದರ ಬಗ್ಗೆ ನಿಮಗೆ ನಾನು ಏನು ಹೇಳಲೀ? ನಿಮ್ಮನ್ನು ಹೊಗಳುವುದೋ, ತೆಗಳುವುದೋ? ಖಂಡಿತವಾಗಿ ನಿಮ್ಮನ್ನು ಹೊಗಳಲಾರೆ.",
			"23": "<sup>23</sup> ನಿಮಗೆ ಮಾಡಿದ ಉಪದೇಶವನ್ನು ನಾನು ಪ್ರಭುವಿನಿಂದಲೇ ಪಡೆದೆನು. ಅದೇನೆಂದರೆ: ಪ್ರಭುಯೇಸು, ತಮ್ಮನ್ನು ಹಿಡಿದುಕೊಡಲಾದ ಆ ರಾತ್ರಿ, ರೊಟ್ಟಿಯನ್ನು ತೆಗೆದುಕೊಂಡು",
			"24": "<sup>24</sup> ದೇವರಿಗೆ ಕೃತಜ್ಞತಾಸ್ತೋತ್ರವನ್ನು ಸಲ್ಲಿಸಿ ಅದನ್ನು ಮುರಿದು, “ಇದು ನಿಮಗಾಗಿ ಒಪ್ಪಿಸಲಾಗುವ ನನ್ನ ಶರೀರ, ಇದನ್ನು ನನ್ನ ಸ್ಮರಣೆಗಾಗಿ ಮಾಡಿ,” ಎಂದರು.",
			"25": "<sup>25</sup> ಅಂತೆಯೇ ಭೋಜನದ ಕೊನೆಯಲ್ಲಿ ಪಾನಪಾತ್ರೆಯನ್ನು ತೆಗೆದುಕೊಂಡು, “ಈ ಪಾತ್ರೆ ನನ್ನ ರಕ್ತದಿಂದ ಮುದ್ರಿತವಾದ ಹೊಸ ಒಡಂಬಡಿಕೆ; ಇದನ್ನು ನೀವು ಪಾನಮಾಡುವಾಗಲೆಲ್ಲಾ ನನ್ನ ಸ್ಮರಣೆಗಾಗಿ ಮಾಡಿರಿ,” ಎಂದರು.",
			"26": "<sup>26</sup> ಎಂದೇ, ನೀವು ಈ ರೊಟ್ಟಿಯನ್ನು ಭುಜಿಸಿ ಈ ಪಾತ್ರೆಯಿಂದ ಪಾನಮಾಡುವಾಗಲೆಲ್ಲಾ ಪ್ರಭು ಬರುವ ತನಕ ಅವರ ಮರಣವನ್ನು ಸಾರುತ್ತೀರಿ.",
			"27": "<sup>27</sup> ಯಾರಾದರೂ ಅಯೋಗ್ಯವಾಗಿ ಈ ರೊಟ್ಟಿಯನ್ನು ಭುಜಿಸಿದರೆ, ಅಥವಾ ಪ್ರಭುವಿನ ಪಾತ್ರೆಯಿಂದ ಪಾನಮಾಡಿದರೆ ಅವನು ಪ್ರಭುವಿನ ಶರೀರಕ್ಕೂ ರಕ್ತಕ್ಕೂ ದ್ರೋಹವೆಸಗುತ್ತಾನೆ.",
			"28": "<sup>28</sup> ಪ್ರತಿಯೊಬ್ಬನೂ ಮೊಟ್ಟಮೊದಲು ತನ್ನನ್ನು ಪರಿಶೋಧಿಸಿಕೊಂಡು ಅನಂತರ ಆ ರೊಟ್ಟಿಯನ್ನು ಭುಜಿಸಲಿ; ಮತ್ತು ಆ ಪಾತ್ರೆಯಿಂದ ಪಾನಮಾಡಲಿ.",
			"29": "<sup>29</sup> ಏಕೆಂದರೆ, ಅದು ಪ್ರಭುವಿನ ಶರೀರವೆಂದು ಪರಿಗ್ರಹಿಸದೆ, ಭುಜಿಸಿ ಪಾನಮಾಡುವಾತನು ನ್ಯಾಯತೀರ್ಪನ್ನು ತನ್ನ ಮೇಲೆ ಬರಮಾಡಿಕೊಳ್ಳುವುದಕ್ಕಾಗಿಯೇ ತಿನ್ನುತ್ತಾನೆ, ಕುಡಿಯುತ್ತಾನೆ.",
			"30": "<sup>30</sup> ನಿಮ್ಮಲ್ಲಿ ಅನೇಕರು ಈ ಕಾರಣದಿಂದಲೇ ದುರ್ಬಲರು, ಅಸ್ವಸ್ಥರು ಆಗಿದ್ದೀರಿ; ಮತ್ತು ಎಷ್ಟೋಮಂದಿ ಸತ್ತಿದ್ದಾರೆ.",
			"31": "<sup>31</sup> ನಮ್ಮನ್ನು ನಾವೇ ಪರಿಶೋಧಿಸಿಕೊಂಡರೆ ನಾವು ನ್ಯಾಯತೀರ್ಪಿಗೆ ಒಳಗಾಗುವುದಿಲ್ಲ.",
			"32": "<sup>32</sup> ಹಾಗೇನಾದರೂ ಪ್ರಭುವಿನ ವಿಚಾರಣೆಗೆ ನಾವು ಒಳಪಟ್ಟರೆ ಅವರು ನಮ್ಮನ್ನು ದಂಡಿಸಿ ತಿದ್ದುತ್ತಾರೆ. ಪ್ರಪಂಚದೊಂದಿಗೆ ನಾವು ತಪ್ಪಿತಸ್ಥರಾಗದಂತೆ ಮಾಡುತ್ತಾರೆ.",
			"33": "<sup>33</sup> ಆದ್ದರಿಂದ ಪ್ರಿಯ ಸಹೋದರರೇ, ಪ್ರಭುವಿನ ಭೋಜನಕ್ಕಾಗಿ ನೀವು ಒಂದಾಗಿ ಸೇರುವಾಗ ಒಬ್ಬರಿಗೊಬ್ಬರು ಕಾದುಕೊಂಡಿರಿ.",
			"34": "<sup>34</sup> ನೀವು ಹಸಿವು ನೀಗಿಸಿಕೊಳ್ಳುವುದಕ್ಕಾಗಿ ಸಭೆಗೆ ಬರುವುದಾದರೆ ಮನೆಯಲ್ಲೇ ಊಟಮಾಡಿ. ಆಗ ಸಭೆಸೇರಿ ನೀವು ನ್ಯಾಯವಿಚಾರಣೆಗೆ ಗುರಿಯಾಗುವುದಿಲ್ಲ. ಇನ್ನುಳಿದ ವಿಷಯಗಳನ್ನು ನಾನು ಬಂದು ಇತ್ಯರ್ಥ ಮಾಡುತ್ತೇನೆ."
		},
		"12": {
			"1": "<sup>1</sup> ಸಹೋದರರೇ, ಪವಿತ್ರಾತ್ಮ ಅವರ ವರದಾನಗಳ ಬಗ್ಗೆ ನೀವು ಸರಿಯಾದ ಅರಿವುಳ್ಳವರಾಗಿರಬೇಕು.",
			"2": "<sup>2</sup> ನೀವು ಅನ್ಯಮತಸ್ಥರಾಗಿದ್ದಾಗ, ನಿಮಗೆ ಮನಸ್ಸು ಬಂದಂತೆಲ್ಲಾ ಮೂಕ ವಿಗ್ರಹಗಳ ಬಳಿ ಹೋಗಿ ಬರುತ್ತಿದ್ದಿರಿ; ಇದನ್ನು ನೀವೇ ಬಲ್ಲಿರಿ.",
			"3": "<sup>3</sup> ಇಷ್ಟುಮಾತ್ರ ನಿಮಗೆ ಸ್ಪಷ್ಟವಾಗಿ ತಿಳಿದಿರಲಿ; ಪವಿತ್ರಾತ್ಮ ಪ್ರೇರಣೆಯಿಂದ ಮಾತನಾಡುವ ಯಾವಾತನೂ, “ಯೇಸುವಿಗೆ ಧಿಕ್ಕಾರ!” ಎನ್ನಲಾರನು. ಅಂತೆಯೇ, ಪವಿತ್ರಾತ್ಮ ಪ್ರೇರಣೆಯಿಂದಲ್ಲದೆ ಯಾವಾತನೂ, “ಯೇಸುವೇ ಪ್ರಭು,” ಎಂದು ಒಪ್ಪಿಕೊಳ್ಳಲಾರನು.",
			"4": "<sup>4</sup> ವರದಾನಗಳು ವಿಧವಿಧವಾಗಿವೆ; ಅವುಗಳನ್ನು ಕೊಡುವ ಪವಿತ್ರಾತ್ಮ ಒಬ್ಬರೇ.",
			"5": "<sup>5</sup> ಸೇವೆಗಳು ವಿಧವಿಧವಾಗಿವೆ; ಅವುಗಳನ್ನು ಸ್ವೀಕರಿಸುವ ಪ್ರಭುವು ಒಬ್ಬರೇ.",
			"6": "<sup>6</sup> ಶಕ್ತಿಸಾಮರ್ಥ್ಯವು ನಾನಾ ವಿಧ; ಅವುಗಳನ್ನು ಎಲ್ಲರಲ್ಲೂ ಸಾಧಿಸುವ ದೇವರು ಒಬ್ಬರೇ.",
			"7": "<sup>7</sup> ಪ್ರತಿಯೊಬ್ಬನಲ್ಲಿ ಕಂಡುಬರುವ ಪವಿತ್ರಾತ್ಮರ ವರದಾನಗಳನ್ನು ಸರ್ವರ ಒಳಿತಿಗಾಗಿಯೇ ಕೊಡಲಾಗಿದೆ.",
			"8": "<sup>8</sup> ಪವಿತ್ರಾತ್ಮ ಮುಖಾಂತರ ಒಬ್ಬನಿಗೆ ಜ್ಞಾನೋಕ್ತಿಗಳು, ಇನ್ನೊಬ್ಬನಿಗೆ ಅವರ ಮುಖಾಂತರವೇ ವಿದ್ಯೋಕ್ತಿಗಳು ಲಭಿಸುತ್ತವೆ;",
			"9": "<sup>9</sup> ಅದೇ ಪವಿತ್ರಾತ್ಮರಿಂದ ಒಬ್ಬನಿಗೆ ಅಗಾಧ ವಿಶ್ವಾಸವೂ ಇನ್ನೊಬ್ಬನಿಗೆ ಸ್ವಸ್ಥತೆಯನ್ನೀಯುವ ಶಕ್ತಿಯೂ ದೊರಕುತ್ತವೆ.",
			"10": "<sup>10</sup> ಒಬ್ಬನಿಗೆ ಪವಾಡಗಳನ್ನು ಎಸಗುವ ಪರಾಕ್ರಮವನ್ನು, ಇನ್ನೊಬ್ಬನಿಗೆ ಪ್ರವಾದನೆಮಾಡುವ ಪ್ರತಿಭೆಯನ್ನು, ಮತ್ತೊಬ್ಬನಿಗೆ ಸತ್ಯಾಸತ್ಯವನ್ನು ವಿವೇಚಿಸುವ ಜಾಣ್ಮೆಯನ್ನು, ಮಗದೊಬ್ಬನಿಗೆ ವಿವಿಧ ಭಾಷೆಗಳನ್ನಾಡುವ ಸಾಮರ್ಥ್ಯವನ್ನು, ಮತ್ತೂ ಒಬ್ಬನಿಗೆ ಅವುಗಳನ್ನು ಅರ್ಥೈಸುವ ಅರಿವನ್ನು ಆ ಪವಿತ್ರಾತ್ಮರಿಂದಲೇ ಕೊಡಲಾಗುತ್ತದೆ.",
			"11": "<sup>11</sup> ಈ ವರಗಳನ್ನೆಲ್ಲ ಆ ಪವಿತ್ರಾತ್ಮ ಒಬ್ಬರೇ ತಮ್ಮ ಇಷ್ಟಾನುಸಾರ ಪ್ರತಿಯೊಬ್ಬನಿಗೂ ಹಂಚುತ್ತಾರೆ.",
			"12": "<sup>12</sup> ದೇಹ ಒಂದೇ; ಅಂಗಗಳು ಹಲವು. ಆ ಅಂಗಗಳು ಅನೇಕವಿದ್ದರೂ ಅವು ಸೇರಿ ಒಂದೇ ದೇಹವಾಗುತ್ತದೆ. ಅಂತೆಯೇ ಕ್ರಿಸ್ತಯೇಸು.",
			"13": "<sup>13</sup> ಯೆಹೂದ್ಯರಾಗಿರಲಿ, ಗ್ರೀಕರಾಗಿರಲಿ, ಪರತಂತ್ರರಾಗಿರಲಿ, ಸ್ವತಂತ್ರರಾಗಿರಲಿ-ನಾವೆಲ್ಲರೂ ಒಂದೇ ದೇಹವಾಗುವಂತೆ ಒಂದೇ ಆತ್ಮದಿಂದ ದೀಕ್ಷಾಸ್ನಾನ ಹೊಂದಿದ್ದೇವೆ. ಒಂದೇ ಆತ್ಮವನ್ನು ನಮ್ಮೆಲ್ಲರಿಗೂ ಪಾನವಾಗಿ ಕೊಡಲಾಗಿದೆ.",
			"14": "<sup>14</sup> ದೇಹವು ಏಕ ಅಂಗದಿಂದ ಅಲ್ಲ, ಅನೇಕ ಅಂಗಗಳಿಂದ ಕೂಡಿದೆ.",
			"15": "<sup>15</sup> ಎಂದಾದರೂ ಕಾಲು, “ನಾನು ಕೈ ಅಲ್ಲ, ಈ ಕಾರಣ ನಾನು ದೇಹಕ್ಕೆ ಸೇರಿಲ್ಲ,” ಎಂದು ಹೇಳಿದ್ದಾದರೆ ಅದು ದೇಹದ ಅಂಗವಾಗಿರುವುದು ತಪ್ಪೀತೆ?",
			"16": "<sup>16</sup> ಅಥವಾ ಕಿವಿ, “ನಾನು ಕಣ್ಣಲ್ಲ, ಈ ಕಾರಣ ನಾನು ದೇಹಕ್ಕೆ ಸೇರಿಲ್ಲ,” ಎಂದು ಹೇಳಿದ್ದಾದರೆ ಅದು ದೇಹದ ಅಂಗವಾಗಿರುವುದು ತಪ್ಪೀತೆ?",
			"17": "<sup>17</sup> ದೇಹವಿಡೀ ಕಣ್ಣೇ ಆದರೆ ಅದು ಕೇಳುವ ಬಗೆ ಹೇಗೆ? ಅಥವಾ ಅದು ಇಡೀ ಕಿವಿಯೇ ಆದರೆ, ಮೂಸಲು ಮೂಗು ಎಲ್ಲಿ?",
			"18": "<sup>18</sup> ಸದ್ಯ, ದೇವರು ತಮ್ಮ ಚಿತ್ತಾನುಸಾರ, ಪ್ರತಿಯೊಂದು ಅಂಗವನ್ನು ಸರಿಯಾಗಿ ಜೋಡಿಸಿದ್ದಾರೆ.",
			"19": "<sup>19</sup> ಅವೆಲ್ಲವೂ ಒಂದೇ ಅಂಗವಾಗಿದ್ದರೆ ಆಗ ದೇಹವೆಲ್ಲಿ?",
			"20": "<sup>20</sup> ಹೀಗೆ ಅಂಗಗಳು ಅನೇಕವಾಗಿದ್ದರೂ ದೇಹ ಮಾತ್ರ ಏಕ.",
			"21": "<sup>21</sup> ಹೀಗಿರಲಾಗಿ, “ನಿನ್ನ ಅವಶ್ಯಕತೆ ನನಗಿಲ್ಲ,” ಎಂದು ಕೈಗೆ, ಕಣ್ಣು ಹೇಳಲಾಗದು. ಅಂತೆಯೇ, “ನಿನ್ನ ಅವಶ್ಯಕತೆ ನನಗಿಲ್ಲ,” ಎಂದು ಕಾಲುಗಳಿಗೆ ತಲೆ ಹೇಳಲಾಗದು.",
			"22": "<sup>22</sup> ಅಷ್ಟುಮಾತ್ರವಲ್ಲ, ಬಲಹೀನವಾಗಿ ತೋರುವ ಅಂಗಗಳೂ ನಮಗೆ ಬೇಕೇಬೇಕು.",
			"23": "<sup>23</sup> ಅಲ್ಪವೆಂದೆಣಿಸುವ ಅಂಗಗಳನ್ನು ಅಧಿಕವಾಗಿ ಮಾನ್ಯಮಾಡುತ್ತೇವೆ. ಗೋಪ್ಯವಾಗಿಡತಕ್ಕವುಗಳನ್ನು ಶೀಲದಿಂದ ಸಂರಕ್ಷಿಸುತ್ತೇವೆ.",
			"24": "<sup>24</sup> ಬಾಹ್ಯ ಅಂಗಗಳಿಗೆ ಇಂಥ ಸಂರಕ್ಷಣೆ ಅಗತ್ಯವಿರುವುದಿಲ್ಲ. ದೇಹವನ್ನು ನಿರ್ಮಿಸಿದ ದೇವರು ಗೌಣವೆಂದೆಣಿಸಲಾಗುವ ಅಂಗಗಳಿಗೂ ವಿಶೇಷ ಘನತೆಯನ್ನು ಇತ್ತಿದ್ದಾರೆ.",
			"25": "<sup>25</sup> ದೇಹದಲ್ಲಿ ಭಿನ್ನಭೇದವಿಲ್ಲದೆ ಅಂಗಗಳು ಒಂದಕ್ಕೊಂದರ ಹಿತವನ್ನು ಗಮನಿಸುವಂತೆ ಮಾಡಿದ್ದಾರೆ.",
			"26": "<sup>26</sup> ಒಂದು ಅಂಗಕ್ಕೆ ನೋವಾದರೆ, ಎಲ್ಲಾ ಅಂಗಗಳೂ ಅದರೊಂದಿಗೆ ನೋವನ್ನು ಅನುಭವಿಸುತ್ತವೆ. ಒಂದು ಅಂಗಕ್ಕೆ ನಲಿವಾದರೆ, ಮಿಕ್ಕೆಲ್ಲಾ ಅಂಗಗಳು ಅದರೊಡನೆ ಸೇರಿ ನಲಿದಾಡುತ್ತವೆ.",
			"27": "<sup>27</sup> ನೀವೆಲ್ಲರೂ ಕ್ರಿಸ್ತಯೇಸುವಿನ ದೇಹ ಆಗಿದ್ದೀರಿ; ಪ್ರತಿಯೊಬ್ಬನೂ ಈ ದೇಹದ ಅಂಗವಾಗಿದ್ದಾನೆ.",
			"28": "<sup>28</sup> ದೇವರು ತಮ್ಮ ಸಭೆಯಲ್ಲಿ, ಮೊದಲನೆಯದಾಗಿ ಪ್ರೇಷಿತರನ್ನು, ಎರಡನೆಯದಾಗಿ ಪ್ರವಾದಿಗಳನ್ನು, ಮೂರನೆಯದಾಗಿ ಬೋಧಕರನ್ನು ನೇಮಿಸಿದ್ದಾರೆ. ಅನಂತರ ಪವಾಡಗಳನ್ನು ಎಸಗುವವರನ್ನು, ರೋಗಗಳನ್ನು ಗುಣಪಡಿಸುವವರನ್ನು, ಪರೋಪಕಾರಿಗಳನ್ನು, ಪರಿಪಾಲಕರನ್ನು, ಬಹುಭಾಷಾಪಂಡಿತರನ್ನು ನೇಮಿಸಿದ್ದಾರೆ.",
			"29": "<sup>29</sup> ಎಲ್ಲರೂ ಪ್ರೇಷಿತರಲ್ಲ, ಎಲ್ಲರೂ ಪ್ರವಾದಿಗಳಲ್ಲ, ಎಲ್ಲರೂ ಬೋಧಕರಲ್ಲ, ಎಲ್ಲರೂ ಪವಾಡಪುರುಷರಲ್ಲ,",
			"30": "<sup>30</sup> ಎಲ್ಲರೂ ರೋಗಗಳನ್ನು ಗುಣಪಡಿಸುವವರಲ್ಲ, ಎಲ್ಲರೂ ಪರೋಪಕಾರಿಗಳಲ್ಲ, ಎಲ್ಲರೂ ಪರಿಪಾಲಕರಲ್ಲ, ಎಲ್ಲರೂ ಬಹುಭಾಷಾಪಂಡಿತರಲ್ಲ, ಅಥವಾ ಎಲ್ಲರೂ ಆ ಭಾಷೆಗಳನ್ನು ವಿವರಿಸಿ ಹೇಳುವವರಲ್ಲ.",
			"31": "<sup>31</sup> ಶ್ರೇಷ್ಠವಾದ ವರಗಳನ್ನು ನೀವು ಶ್ರದ್ಧಾಪೂರ್ವಕವಾಗಿ ಅಪೇಕ್ಷಿಸಿರಿ. ನಾನು ನಿಮಗೆ ಇನ್ನೂ ಸರ್ವೋತ್ತಮವಾದ ಮಾರ್ಗವನ್ನು ತೋರಿಸುತ್ತೇನೆ:"
		},
		"13": {
			"1": "<sup>1</sup> ಸುರನರರ ನುಡಿಗಳ ನಾನಾಡಬಲ್ಲೆನಾದರೂ ಪರಮ ಪ್ರೀತಿಯೊಂದೆನಗಿಲ್ಲದಿರಲು ನಾ ಕೇವಲ ಗಣಗಣಿಸುವ ಘಂಟೆ, ಝಣಝಣಿಸುವ ಜಾಗಟೆ.",
			"2": "<sup>2</sup> ಪ್ರವಾದನೆಯ ವರವೆನಗಿರಬಹುದು ಇರಬಹುದು ನಿಗೂಢ ರಹಸ್ಯಗಳರಿವು ಎಲ್ಲದರ ಪರಿಜ್ಞಾನ, ಪರ್ವತವನೇ ಕದಲಿಪ ವಿಶ್ವಾಸ ಪ್ರೀತಿಯೊಂದಿಲ್ಲದಿರೆ ನಾ ಶೂನ್ಯಸಮಾನ.",
			"3": "<sup>3</sup> ನನಗಿರುವುದೆಲ್ಲವನು ನಾ ದಾನಮಾಡೆ ದೇಹವನೆ ಸಜೀವ ದಹಿಸಲು ನೀಡೆ ನಾನಾಗಿರೆ ಪರಮ ಪ್ರೀತಿ ವಿಹೀನ ಏನದು ಜೀವನ, ನನಗೇನದು ಪ್ರಯೋಜನ?",
			"4": "<sup>4</sup> ಸಹನೆ ಸೈರಣೆ, ದಯೆದಾಕ್ಷಿಣ್ಯ ಪ್ರೀತಿಯಲ್ಲಿವೆ. ಎಡೆಯಿಲ್ಲ ಅದರಲಿ ಗರ್ವಕೆ, ಮರ್ಮಕೆ, ಮೆರೆತಕೆ, ಮತ್ಸರಕೆ, ಸಿಡುಕಿಗೆ, ಸೊಕ್ಕಿಗೆ, ಸ್ವಾರ್ಥಕೆ, ಸೇಡುಗಳೆಣಿಕೆಗೆ. ನಲಿಯದು ಪ್ರೀತಿ ಅನೀತಿಯಲಿ ನಲಿಯದಿರದದು ಸತ್ಯದ ಜಯದಲಿ.",
			"7": "<sup>7</sup> ನಂಬುವುದೆಲ್ಲವನು, ನಿರೀಕ್ಷಿಸುವುದೆಲ್ಲವನು, ಸಹಿಸಿಕೊಳ್ಳುವುದು ಸಮಸ್ತವನು ಪ್ರೀತಿಯದೆಂದೂ ಅರಿಯದು ಸೋಲನು.",
			"8": "<sup>8</sup> ಅಳಿದುಹೋಗುವುವು ಭವಿಷ್ಯವಾಣಿ ಗತಿಸಿಹೋಗುವುದು ಬಹುಭಾಷಾ‍ ಶಕ್ತಿ ಹೋಗುವುವು ನಶಿಸಿ ಜ್ಞಾನ, ಬುದ್ಧಿ. ಆದರೆ ಅಮರವಾದುದು ಪರಮ ಪ್ರೀತಿ.",
			"10": "<sup>10</sup> ಅಪೂರ್ವವಾದುದು ನಮ್ಮಾ ಅರಿವೆಲ್ಲ ಪೂರ್ಣವಾದುದಲ್ಲ ಪ್ರವಾದನವದೆಲ್ಲ; ಪರಿಪೂರ್ಣತೆ ಪ್ರಾಪ್ತವಾಗಲು ಇಲ್ಲವಾಗುವುದು ಅಪೂರ್ಣತೆಯೆಲ್ಲ.",
			"11": "<sup>11</sup> ಬಾಲಕ ನಾನಾಗಿರೆ ಆಡಿದೆ, ಮಾತಾಡಿದೆ, ಚಿಂತಿಸಿದೆ, ಸುಖದುಃಖಗಳ ಸವಿದೆ ಬಾಲಕನಂತೆ. ಬಲಿತು ಬೆಳೆದು ಮನುಜನಾದುದೆ ಬಾಲಿಶವಾದುದೆಲ್ಲವನು ಬದಿಗೊತ್ತಿದೆ.",
			"12": "<sup>12</sup> ನಾವೀಗ ಕಾಂಬುದು ದರ್ಪಣದ ಬಿಂಬವನು ಮುಸುಕಾಗಿ ತರುವಾಯ ಕಾಂಬೆವು ದೇವರನು ಮುಖಾಮುಖಿಯಾಗಿ. ಈಗಿರುವುದೆನ್ನ ಅರಿವು ತುಂಡುತುಂಡಾಗಿ ನಂತರ ದೇವನೆನ್ನ ಅರಿತಂತೆ ಈ ಮೂರಲಿ ನಾನರಿವೆನು ಅಖಂಡವಾಗಿ.",
			"13": "<sup>13</sup> ನಿಲ್ಲುವುವು ನಂಬಿಕೆ, ನಿರೀಕ್ಷೆ, ಪ್ರೀತಿ ನೆಲೆಯಾಗಿ; ಈ ಮೂರಲಿ ಪ್ರೀತಿಯೇ ಪರಮೋನ್ನತವೆಂಬುದ ನೀನರಿ."
		},
		"14": {
			"1": "<sup>1</sup> ಪ್ರೀತಿಯನ್ನು ಅರಸಿರಿ. ಪವಿತ್ರಾತ್ಮರ ವರಗಳನ್ನು ಬಯಸಿರಿ. ದೇವರ ಸಂದೇಶವನ್ನು ಸಾರಲು ಹಂಬಲಿಸಿರಿ.",
			"2": "<sup>2</sup> ಪರವಶಾಭಾಷೆಯನ್ನು ಆಡುವವನು ಮಾನವನೊಡನಲ್ಲ, ದೇವರೊಡನೆ ಮಾತನಾಡುತ್ತಾನೆ. ಪವಿತ್ರಾತ್ಮರ ಪ್ರೇರಣೆಯಿಂದ ನಿಗೂಢ ವಿಷಯಗಳ ಬಗ್ಗೆ ಮಾತನಾಡುವುದರಿಂದ ಯಾರೂ ಆತನ ಮಾತನ್ನು ಅರ್ಥಮಾಡಿಕೊಳ್ಳಲಾರರು.",
			"3": "<sup>3</sup> ಅವನ ಮಾತು ಭಕ್ತಿಯನ್ನು ವೃದ್ಧಿಸುತ್ತದೆ. ಉತ್ಸಾಹವನ್ನುಂಟುಮಾಡುತ್ತದೆ, ಉಪಶಮನವನ್ನು ತರುತ್ತದೆ.",
			"4": "<sup>4</sup> ಪರವಶಾಭಾಷೆಗಳನ್ನು ಆಡುವವನು ತನ್ನನ್ನೇ ಅಭಿವೃದ್ಧಿಗೊಳಿಸಿಕೊಳ್ಳುತ್ತಾನೆ. ಆದರೆ ದೇವರ ವಾಕ್ಯವನ್ನು ಸಾರುವವನು ಇಡೀ ಧರ್ಮಸಭೆಯನ್ನೇ ಅಭಿವೃದ್ಧಿಗೊಳಿಸುತ್ತಾನೆ.",
			"5": "<sup>5</sup> ನೀವೆಲ್ಲರೂ ಪರವಶಾಭಾಷೆಗಳನ್ನು ಆಡಬಹುದಾದರೂ ದೇವರ ವಾಕ್ಯವನ್ನು ಸಾರುವವರಾಗಬೇಕೆಂಬುದೇ ನನ್ನ ಅಭಿಲಾಷೆ. ಪರವಶಾಭಾಷೆಗಳನ್ನು ಆಡುವಾತನು ಧರ್ಮಸಭೆಯ ಅಭಿವೃದ್ಧಿಗಾಗಿ ತನ್ನ ಮಾತಿನ ಅರ್ಥವನ್ನು ಹೇಳಲಾರನಾದರೆ , ಅವನಿಗಿಂತಲೂ ದೇವರ ವಾಕ್ಯವನ್ನು ಸಾರುವವನೇ ಶ್ರೇಷ್ಠನು.",
			"6": "<sup>6</sup> ಸಹೋದರರೇ, ನಾನು ನಿಮ್ಮ ಬಳಿಗೆ ಬಂದು ದೇವರ ಶ್ರುತಿಯನ್ನು, ನೈಜ ಜ್ಞಾನವನ್ನು, ದೇವರ ವಾಕ್ಯವನ್ನು, ದಿವ್ಯಬೋಧೆಯನ್ನು ನೀಡದೆ ಪರವಶಾಭಾಷೆಗಳಲ್ಲಿಯೇ ಮಾತನಾಡಿದರೆ ನಿಮಗೆ ಸಿಗುವ ಲಾಭವಾದರೂ ಏನು?",
			"7": "<sup>7</sup> ಕೊಳಲು, ವೀಣೆ ಮೊದಲಾದ ನಿರ್ಜೀವವಾದ್ಯಗಳು ನಾದವನ್ನು ಹೊಮ್ಮಿಸುತ್ತವೆ; ಅವುಗಳ ಸ್ವರಗಳಲ್ಲಿ ಯಾವ ವ್ಯತ್ಯಾಸವೂ ಕಂಡುಬರದಿದ್ದರೆ, ನುಡಿಸಿದ್ದು, ಬಾಜಿಸಿದ್ದು ಇಂತಹುದೇ ರಾಗವೆಂದು ತಿಳಿಯುವುದು ಹೇಗೆ?",
			"8": "<sup>8</sup> ರಣಕಹಳೆಯು ನಿರ್ದಿಷ್ಟವಾದ ಧ್ವನಿಯನ್ನು ಮೊಳಗಿಸದಿದ್ದರೆ ಯಾರು ತಾನೇ ಯುದ್ಧಸನ್ನದ್ಧರಾಗುತ್ತಾರೆ?",
			"9": "<sup>9</sup> ಅಂತೆಯೇ, ನೀವು ಸಹ ಪರವಶಾಭಾಷೆಯನ್ನು ಆಡುವಾಗ, ಇತರರಿಗೆ ಅದು ಅರ್ಥವಾಗದಿದ್ದರೆ, ಅವರು ನಿಮ್ಮ ಮಾತನ್ನು ತಿಳಿದುಕೊಳ್ಳುವುದಾದರೂ ಹೇಗೆ? ನಿಮ್ಮ ಮಾತು ಗಾಳಿ ಪಾಲಾಗುತ್ತದಷ್ಟೆ.",
			"10": "<sup>10</sup> ಜಗತ್ತಿನಲ್ಲಿ ಎಷ್ಟೋ ಭಾಷೆಗಳಿವೆ. ಅವುಗಳಲ್ಲಿ ಒಂದಾದರೂ ಅರ್ಥರಹಿತವಾದುದಲ್ಲ.",
			"11": "<sup>11</sup> ಭಾಷೆಯ ಅರ್ಥವು ನನಗಾಗದಿದ್ದರೆ ಮಾತನಾಡುವವನಿಗೆ ನಾನು ಪರಕೀಯನಂತಾಗುತ್ತೇನೆ; ನನಗೆ ಅವನು ಪರಕೀಯನಂತಾಗುತ್ತಾನೆ.",
			"12": "<sup>12</sup> ಆದ್ದರಿಂದ ಪವಿತ್ರಾತ್ಮ ವರಗಳ ಮೇಲೆ ಆಸಕ್ತಿಯುಳ್ಳ ನೀವು, ಧರ್ಮಸಭೆಯನ್ನು ಅಭಿವೃದ್ಧಿಗೊಳಿಸಬಲ್ಲ ವರಗಳ ಮೇಲೆ ಆಸಕ್ತರಾಗಿರಿ.",
			"13": "<sup>13</sup> ಪರವಶಾಭಾಷೆಗಳಲ್ಲಿ ಮಾತನಾಡುವವನು ತಾನಾಡುವ ಮಾತಿನ ಅರ್ಥವನ್ನು ಹೇಳುವ ಸಾಮರ್ಥ್ಯಕ್ಕಾಗಿ ದೇವರನ್ನು ಪ್ರಾರ್ಥಿಸಲಿ.",
			"14": "<sup>14</sup> ಏಕೆಂದರೆ, ನಾನು ಪರವಶಾಭಾಷೆಯಲ್ಲಿ ಪ್ರಾರ್ಥಿಸಿದರೆ ನನ್ನ ಆತ್ಮವೇನೋ ಪ್ರಾರ್ಥಿಸಿದಂತೆ ಆಗುತ್ತದೆ; ಆದರೆ ಮನಸ್ಸು ಮಾತ್ರ ಅದನ್ನು ಗ್ರಹಿಸುವುದಿಲ್ಲ.",
			"15": "<sup>15</sup> ಹಾಗಾದರೆ ಈಗ ನಾನೇನು ಮಾಡಬೇಕು? ಆತ್ಮದಿಂದಲೂ ಪ್ರಾರ್ಥಿಸಬೇಕು, ಮನಸ್ಸಿನಿಂದಲೂ ಪ್ರಾರ್ಥಿಸಬೇಕು, ಆತ್ಮದಿಂದಲೂ",
			"16": "<sup>16</sup> ಸಭೆಯಲ್ಲಿ ನೀನು ಆತ್ಮದಿಂದ ಮಾತ್ರ ದೇವರ ಸ್ತುತಿಮಾಡಿದರೆ ನಿನ್ನ ಹಾಗೆ ಪರವಶನಲ್ಲದವನು ನಿನ್ನ ಕೃತಜ್ಞತಾ ಸ್ತುತಿಗೆ ‘ಆಮೆನ್’ ಎಂದು ಹೇಗೆ ತಾನೇ ಹೇಳಬಲ್ಲನು? ನೀನು ಹೇಳುವುದೇ ಅವನಿಗೆ ತಿಳಿಯುವುದಿಲ್ಲವಲ್ಲಾ!",
			"17": "<sup>17</sup> ನೀನು ಮಾಡುವ ಕೃತಜ್ಞತಾಸ್ತುತಿ ಎಷ್ಟು ಚೆನ್ನಾಗಿದ್ದರೂ ಅವನ ಮಟ್ಟಿಗೆ ಅದು ನಿಷ್ಪ್ರಯೋಜಕವೇ.",
			"18": "<sup>18</sup> ನಾನಾದರೋ ದೇವರ ದಯೆಯಿಂದ ನಿಮಗಿಂತಲೂ ಮಿಗಿಲಾಗಿ ಪರವಶಾಭಾಷೆಗಳನ್ನು ಆಡಬಲ್ಲೆ.",
			"19": "<sup>19</sup> ಆದರೂ ಸಭೆಯಲ್ಲಿ ಪರವಶನಾಗಿ ಹತ್ತುಸಾವಿರ ನುಡಿಗಳನ್ನಾಡುವ ಬದಲು ಮನಸ್ಸಾರೆ ಐದೇ ಮಾತುಗಳನ್ನಾಡಿ ಉಪದೇಶಿಸುವುದು ಉತ್ತಮವೆಂದು ನನಗೆ ತೋರುತ್ತದೆ.",
			"20": "<sup>20</sup> ಸಹೋದರರೇ, ಬುದ್ಧಿಯ ವಿಷಯದಲ್ಲಿ ಬಾಲಕರಾಗಿರಬೇಡಿ. ದುಷ್ಟ ವಿಷಯಗಳಲ್ಲಿ ಶಿಶುಗಳಂತೆ ಮುಗ್ಧರಾಗಿರಿ; ಬುದ್ಧಿಯ ವಿಷಯದಲ್ಲಿ ಪ್ರಾಯಸ್ಥರಂತೆ ಪ್ರೌಢರಾಗಿರಿ.",
			"21": "<sup>21</sup> ಪವಿತ್ರಗ್ರಂಥದಲ್ಲಿ ಇಂತಿದೆ: ಅನ್ಯಭಾಷೆಯವರ ಮುಖಾಂತರ ಅನ್ಯದೇಶೀಯರ ಬಾಯ ಮುಖಾಂತರ ನಾನೆನ್ನ ಜನರೊಡನೆ ಮಾತಾಡಿದಾಗಲೂ ಎನ್ನ ಮಾತಿಗೆ ಕಿವಿಗೊಡರವರು, ಎಂದರು ಪ್ರಭು.”",
			"22": "<sup>22</sup> ಪರವಶಾಭಾಷೆಗಳನ್ನು ಆಡುವುದರ ಸಾಕ್ಷ್ಯ ವಿಶ್ವಾಸಿಗಳಿಗಲ್ಲ, ಅವಿಶ್ವಾಸಿಗಳಿಗೆ. ದೇವರ ವಾಕ್ಯವನ್ನು ಪ್ರಬೋಧಿಸುವುದರ ಸಾಕ್ಷ್ಯವು ಅವಿಶ್ವಾಸಿಗಳಿಗಲ್ಲ, ವಿಶ್ವಾಸಿಗಳಿಗೆ.",
			"23": "<sup>23</sup> ಒಂದು ಕಡೆ ಎಲ್ಲರೂ ಸಭೆ ಸೇರಿದಾಗ ಪ್ರತಿಯೊಬ್ಬನೂ ಪರವಶಾಭಾಷೆಗಳನ್ನು ಆಡಲು ಆರಂಭಿಸಿದರೆ ಅಲ್ಲಿಗೆ ಬರುವ ಅಪರಿಚಿತರು, ಅವಿಶ್ವಾಸಿಗಳು ನಿಮ್ಮನ್ನು ಹುಚ್ಚರೆಂದು ಕರೆಯಲಾರರೇ?",
			"24": "<sup>24</sup> ಬದಲಾಗಿ, ಬರುವ ಅಪರಿಚಿತರ ಹಾಗೂ ಅವಿಶ್ವಾಸಿಗಳ ಮುಂದೆ ಎಲ್ಲರೂ ದೇವರ ವಾಕ್ಯವನ್ನು ಪ್ರಬೋಧಿಸಿದರೆ ಅವರಲ್ಲಿ ಒಬ್ಬನಾದರೂ ತಾನು ಕೇಳಿದ ಮಾತಿನಿಂದ ತನ್ನ ಪಾಪವನ್ನು ಒಪ್ಪಿಕೊಳ್ಳುತ್ತಾನೆ; ಅವನು ಎಲ್ಲರ ಮಾತಿನಿಂದ ತನ್ನನ್ನು ತಾನೇ ಪರೀಕ್ಷಿಸಿಕೊಳ್ಳುತ್ತಾನೆ.",
			"25": "<sup>25</sup> ಅವನ ಅಂತರಾಳದ ವಿಚಾರಗಳು ಬಯಲಾಗುತ್ತವೆ. ಅವನು ಅಡ್ಡಬಿದ್ದು ದೇವರನ್ನು ಆರಾಧಿಸುತ್ತಾನೆ. “ದೇವರು ನಿಜವಾಗಿಯೂ ನಿಮ್ಮ ಮಧ್ಯೆ ಇದ್ದಾರೆ,” ಎಂದು ಪ್ರಚುರಪಡಿಸುತ್ತಾನೆ.",
			"26": "<sup>26</sup> ಹಾಗಾದರೆ ಪ್ರಿಯ ಸಹೋದರರೇ, ನಾವು ನಡೆದುಕೊಳ್ಳಬೇಕಾದ ಬಗೆ ಹೇಗೆ? ನೀವು ಸಭೆ ಸೇರಿದಾಗ ನಿಮ್ಮಲ್ಲಿ ಒಬ್ಬನು ಹಾಡುತ್ತಾನೆ; ಇನ್ನೊಬ್ಬನು ಉಪದೇಶ ಮಾಡುತ್ತಾನೆ; ಮತ್ತೊಬ್ಬನು ದೇವರು ತನಗೆ ಶ್ರುತಪಡಿಸಿದ್ದನ್ನು ತಿಳಿಸುತ್ತಾನೆ; ಇನ್ನೂ ಒಬ್ಬನು ಪರವಶಾಭಾಷೆಯನ್ನಾಡುತ್ತಾನೆ; ಮಗದೊಬ್ಬನು ಅದಕ್ಕೆ ಅರ್ಥ ಹೇಳುತ್ತಾನೆ ಎಂದಿಟ್ಟುಕೊಳ್ಳೋಣ. ನೀವು ಏನು ಮಾಡಿದರೂ ಧರ್ಮಸಭೆಯ ಅಭಿವೃದ್ಧಿಗಾಗಿಯೇ ಮಾಡಬೇಕು.",
			"27": "<sup>27</sup> ಪರವಶಾಭಾಷೆಯನ್ನು ಆಡುವುದಾದರೆ ಇಬ್ಬರು, ಹೆಚ್ಚೆಂದರೆ ಮೂವರು ಮಾತನಾಡಲಿ. ಆದರೆ ಒಬ್ಬರ ನಂತರ ಇನ್ನೊಬ್ಬರು ಮಾತನಾಡಲಿ. ಬೇರೊಬ್ಬರು ವಿವರಿಸಲಿ.",
			"28": "<sup>28</sup> ವಿವರಿಸಬಲ್ಲವರು ಯಾರೂ ಇಲ್ಲವಾದರೆ, ಪರವಶಾಭಾಷೆಗಳನ್ನು ಆಡುವವನು ಸಭೆಯಲ್ಲಿ ಮೌನದಿಂದಿರಲಿ. ತನ್ನೊಳಗೆ ದೇವರೊಡನೆ ಮಾತನಾಡಿಕೊಳ್ಳಲಿ.",
			"29": "<sup>29</sup> ದೇವರ ವಾಕ್ಯವನ್ನು ಬೋಧಿಸುವವರು ಇಬ್ಬರು ಇಲ್ಲವೆ ಮೂವರು ಮಾತ್ರ ಮಾತನಾಡಲಿ. ಉಳಿದವರು ಅವರ ಮಾತನ್ನು ವಿವೇಚಿಸಲಿ.",
			"30": "<sup>30</sup> ಸಭೆಯಲ್ಲಿ ಕುಳಿತವರಲ್ಲಿ ಒಬ್ಬನಿಗೆ ದೇವರ ಶ್ರುತಿಲಭಿಸಿತೆಂದರೆ ಮೊದಲು ಮಾತನಾಡುತ್ತಿದ್ದವನು ಮೌನ ತಳೆಯಲಿ.",
			"31": "<sup>31</sup> ನೀವು, ಒಬ್ಬರಾದ ಮೇಲೆ ಒಬ್ಬರು ದೇವರ ವಾಕ್ಯವನ್ನು ಬೋಧಿಸಬಹುದು. ಆಗ ಎಲ್ಲರೂ ಕಲಿತುಕೊಂಡು ಪ್ರೋತ್ಸಾಹಪಡೆಯಲು ಸಾಧ್ಯವಾಗುತ್ತದೆ.",
			"32": "<sup>32</sup> ದೇವರ ವಾಕ್ಯವನ್ನು ಬೋಧಿಸುವವರು ತಮ್ಮ ವಾಕ್ಚಾತುರ್ಯವನ್ನು ಸ್ವಾಧೀನದಲ್ಲಿಟ್ಟುಕೊಳ್ಳಬೇಕು.",
			"33": "<sup>33</sup> ದೇವರು ಗಲಿಬಿಲಿಯನ್ನು ಬಯಸುವವರಲ್ಲ; ಅವರು ಶಾಂತಿ ಮತ್ತು ಸುವ್ಯವಸ್ಥೆಯ ದೇವರು.",
			"34": "<sup>34</sup> ಎಲ್ಲ ಕ್ರೈಸ್ತಸಭೆಗಳಲ್ಲಿ ರೂಢಿಯಲ್ಲಿರುವಂತೆ ಮಹಿಳೆಯರು ಮೌನವಾಗಿರಲಿ; ಮಾತನಾಡಲು ಅವರಿಗೆ ಅನುಮತಿಯಿಲ್ಲ. ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿ ಹೇಳಿರುವಂತೆ ಅವರು ಅಧೀನರಾಗಿರಬೇಕು.",
			"35": "<sup>35</sup> ಅವರು ಏನನ್ನಾದರೂ ತಿಳಿಯಬಯಸಿದರೆ, ಮನೆಯಲ್ಲಿ ತಮ್ಮ ತಮ್ಮ ಗಂಡಂದಿರನ್ನು ಕೇಳಿ ತಿಳಿದುಕೊಳ್ಳಲಿ. ಮಹಿಳೆಯರು ಸಭಾಕೂಟದಲ್ಲಿ ಮಾತನಾಡುವುದು ಲಕ್ಷಣವಲ್ಲ.",
			"36": "<sup>36</sup> ದೇವರ ವಾಕ್ಯ ನಿಮ್ಮಿಂದ ಹೊರಟಿತೋ? ಅಥವಾ ನಿಮಗೆ ಮಾತ್ರ ತಲುಪಿತೋ?",
			"37": "<sup>37</sup> ನಿಮ್ಮಲ್ಲಿ ಯಾರಾದರೊಬ್ಬನು ತಾನು ದೇವರ ವಾಕ್ಯವನ್ನು ಬೋಧಿಸುವವನು ಅಥವಾ ಪವಿತ್ರಾತ್ಮ ಪ್ರೇರಣೆಯನ್ನು ಹೊಂದಿದವನು ಎಂದು ಭಾವಿಸುವುದಾದರೆ, ನಾನು ನಿಮಗೆ ಬರೆದಿರುವುದೆಲ್ಲ ಪ್ರಭುವಿನ ಆಜ್ಞೆಯೆಂದು ಚೆನ್ನಾಗಿ ತಿಳಿದುಕೊಳ್ಳಲಿ.",
			"38": "<sup>38</sup> ಯಾರಾದರು ಇದನ್ನು ಒಪ್ಪಿಕೊಳ್ಳದಿದ್ದರೆ, ನೀವು ಅವರನ್ನು ಒಪ್ಪಿಕೊಳ್ಳಬೇಕಾಗಿಲ್ಲ.",
			"39": "<sup>39</sup> ಸಹೋದರರೇ, ದೇವರ ವಾಕ್ಯವನ್ನು ಸಾರುವುದರಲ್ಲಿ ಆಸಕ್ತರಾಗಿರಿ. ಪರವಶಾಭಾಷೆಗಳನ್ನು ಆಡುವವರನ್ನು ತಡೆಯಬೇಡಿ.",
			"40": "<sup>40</sup> ಆದರೆ ಎಲ್ಲವನ್ನು ಒಪ್ಪವಾಗಿ, ಓರಣವಾಗಿ ಮಾಡಿರಿ."
		},
		"15": {
			"1": "<sup>1</sup> ಪ್ರಿಯ ಸಹೋದರರೇ, ನಾನು ನಿಮಗೆ ಬೋಧಿಸಿದ ಶುಭಸಂದೇಶವನ್ನು ನೆನಪಿಗೆ ತಂದುಕೊಳ್ಳಿರಿ. ನೀವು ಅದನ್ನು ಸ್ವೀಕರಿಸಿದ್ದೀರಿ. ಅದರಲ್ಲಿ ನೆಲೆಯಾಗಿ ನಿಂತಿದ್ದೀರಿ.",
			"2": "<sup>2</sup> ನಾನು ನಿಮಗೆ ಶುಭಸಂದೇಶವೆಂದು ತಿಳಿಸಿದ ಸಂಗತಿಯನ್ನು ದೃಢವಾಗಿ ವಿಶ್ವಾಸಿಸಿದರೆ ನೀವು ಜೀವೋದ್ಧಾರವನ್ನು ಪಡೆಯುವಿರಿ. ಇಲ್ಲವಾದರೆ ನಿಮ್ಮ ವಿಶ್ವಾಸ ನಿರರ್ಥಕವಾದೀತು.",
			"3": "<sup>3</sup> ನಾನು ಪಡೆದುಕೊಂಡದ್ದನ್ನು ಮುಖ್ಯವಾದುದು ಎಂದೆಣಿಸಿ ನಿಮಗೆ ಒಪ್ಪಿಸಿದ್ದೇನೆ. ಅದು ಯಾವುದೆಂದರೆ, ಪವಿತ್ರಗ್ರಂಥದಲ್ಲಿ ಹೇಳಿರುವ ಪ್ರಕಾರ ಕ್ರಿಸ್ತಯೇಸು ನಮ್ಮ ಪಾಪಗಳ ಪರಿಹಾರಕ್ಕಾಗಿ ಪ್ರಾಣತ್ಯಾಗ ಮಾಡಿದರು.",
			"4": "<sup>4</sup> ಅವರನ್ನು ಭೂಸ್ಥಾಪನೆ ಮಾಡಲಾಯಿತು. ಅದೇ ಗ್ರಂಥದ ಪ್ರಕಾರ ಮೂರನೆಯ ದಿನ ಅವರು ಪುನರುತ್ಥಾನ ಹೊಂದಿದರು.",
			"5": "<sup>5</sup> ಅನಂತರ ಕೇಫನಿಗೂ ಹನ್ನೆರಡು ಮಂದಿಗೂ ಕಾಣಿಸಿಕೊಂಡರು.",
			"6": "<sup>6</sup> ತರುವಾಯ ಒಂದೇ ಸಮಯದಲ್ಲಿ ಐನೂರಕ್ಕೂ ಹೆಚ್ಚಿನ ಮಂದಿ ಸಹೋದರರಿಗೆ ಪ್ರತ್ಯಕ್ಷರಾದರು. ಅವರಲ್ಲಿ ಕೆಲವರು ಸತ್ತುಹೋಗಿದ್ದರೂ ಬಹುಮಂದಿ ಇಂದಿಗೂ ಬದುಕಿದ್ದಾರೆ.",
			"7": "<sup>7</sup> ಅದಾದ ಮೇಲೆ ಯಕೋಬನಿಗೂ ಅನಂತರ ಪ್ರೇಷಿತರೆಲ್ಲರಿಗೂ ಕಾಣಿಸಿಕೊಂಡರು.",
			"8": "<sup>8</sup> ಕಟ್ಟಕಡೆಗೆ ದಿನತುಂಬುವ ಮೊದಲೇ ಹುಟ್ಟಿದವನಂತಿರುವ ನನಗೂ ಕಾಣಿಸಿಕೊಂಡರು.",
			"9": "<sup>9</sup> ನಾನಾದರೋ ಪ್ರೇಷಿತರಲ್ಲಿ ಕನಿಷ್ಠನು, ಪ್ರೇಷಿತನೆಂಬ ಹೆಸರಿಗೂ ಅಪಾತ್ರನು. ಏಕೆಂದರೆ, ನಾನು ದೇವರ ಸಭೆಯನ್ನು ಹಿಂಸಿಸಿದೆನು.",
			"10": "<sup>10</sup> ನಾನು ಈಗ ಈ ಸ್ಥಿತಿಯಲ್ಲಿ ಇರುವುದು ದೇವರ ಅನುಗ್ರಹದಿಂದಲೇ. ಅವರ ಅನುಗ್ರಹ ನನ್ನಲ್ಲಿ ವ್ಯರ್ಥವಾಗಲಿಲ್ಲ. ನಾನು ಅವರೆಲ್ಲರಿಗಿಂತಲೂ ಹೆಚ್ಚು ಶ್ರಮಪಟ್ಟು ಸೇವೆಮಾಡಿದ್ದೇನೆ. ಅದನ್ನು ಮಾಡಿದವನು ನಾನಲ್ಲ, ನನ್ನಲ್ಲಿರುವ ದೇವರ ಅನುಗ್ರಹವೇ.",
			"11": "<sup>11</sup> ನಾನಾದರೇನು, ಅವರಾದರೇನು? ನಾವು ಸಾರುವ ಸಂದೇಶ ಒಂದೇ ಅದನ್ನು ನೀವು ನಂಬಿದ್ದೀರಿ.",
			"12": "<sup>12</sup> ಕ್ರಿಸ್ತಯೇಸು ಮರಣದಿಂದ ಪುನರುತ್ಥಾನ ಹೊಂದಿದರು ಎಂದು ನಾವು ಸಾರುತ್ತಿರುವಲ್ಲಿ, ಸತ್ತವರು ಪುನರುತ್ಥಾನರಾಗುವುದಿಲ್ಲ ಎಂದು ನಿಮ್ಮಲ್ಲಿ ಕೆಲವರು ವಾದಿಸುವುದು ಹೇಗೆ?",
			"13": "<sup>13</sup> ಸತ್ತವರಿಗೆ ಪುನರುತ್ಥಾನ ಇಲ್ಲವೆಂಬುದು ನಿಜವಾದರೆ ಕ್ರಿಸ್ತಯೇಸು ಪುನರುತ್ಥಾನ ಹೊಂದಲಿಲ್ಲ ಎಂದ ಹಾಗಾಯಿತು.",
			"14": "<sup>14</sup> ಕ್ರಿಸ್ತಯೇಸು ಪುನರುತ್ಥಾನರಾಗಲಿಲ್ಲ ಎಂದರೆ ನಮ್ಮ ಬೋಧನೆ ಬರಿದು; ನಿಮ್ಮ ವಿಶ್ವಾಸವೂ ಬರಿದು.",
			"15": "<sup>15</sup> ಸತ್ತವರು ಪುನರುತ್ಥಾನ ಹೊಂದುವುದಿಲ್ಲ ಎಂಬುದು ನಿಜವಾದರೆ ದೇವರು ಕ್ರಿಸ್ತಯೇಸುವನ್ನು ಪುನರುತ್ಥಾನಗೊಳಿಸಿಲ್ಲ ಎಂದಂತಾಯಿತು. ದೇವರು ಕ್ರಿಸ್ತಯೇಸುವನ್ನು ಸಾವಿನಿಂದ ಪುನರುತ್ಥಾನಗೊಳಿಸಿದ್ದಾರೆ ಎಂದು ಸಾಕ್ಷಿ ಹೇಳಿದ ನಾವು ಆಗ ದೇವರ ಬಗ್ಗೆಯೇ ಸುಳ್ಳುಸಾಕ್ಷಿ ಹೇಳಿದಂತಾಯಿತು.",
			"16": "<sup>16</sup> ಸತ್ತವರು ಪುನರುತ್ಥಾನ ಹೊಂದುವುದಿಲ್ಲ ಎಂದರೆ ಕ್ರಿಸ್ತಯೇಸುವು ಪುನರುತ್ಥಾನ ಹೊಂದಲಿಲ್ಲ ಎಂದಂತಾಯಿತು.",
			"17": "<sup>17</sup> ಕ್ರಿಸ್ತಯೇಸುವೇ ಪುನರುತ್ಥಾನ ಹೊಂದಿಲ್ಲ ಎಂದಮೇಲೆ ನಿಮ್ಮ ವಿಶ್ವಾಸವೇ ನಿರರ್ಥಕ; ಮತ್ತು ನೀವಿನ್ನೂ ನಿಮ್ಮ ಪಾಪಗಳಲ್ಲಿಯೇ ಮುಳುಗಿದ್ದೀರಿ;",
			"18": "<sup>18</sup> ಅಷ್ಟೇ ಅಲ್ಲದೆ, ಕ್ರಿಸ್ತಯೇಸುವಿನಲ್ಲಿ ಮೃತರಾದವರೆಲ್ಲರೂ ವಿನಾಶವಾದರು.",
			"19": "<sup>19</sup> ಕ್ರಿಸ್ತಯೇಸುವಿನಲ್ಲಿ ನಮಗಿರುವ ನಂಬಿಕೆ ಕೇವಲ ಬದುಕಿಗೆ ಸೀಮಿತವಾಗಿದ್ದರೆ ಜಗತ್ತಿನಲ್ಲಿ ನಮಗಿಂತ ನಿರ್ಭಾಗ್ಯರು ಬೇರೆ ಯಾರೂ ಇಲ್ಲ.",
			"20": "<sup>20</sup> ಕ್ರಿಸ್ತಯೇಸು ಪುನರುತ್ಥಾನಹೊಂದಿದ್ದೇನೋ ಸತ್ಯಸ್ಯ ಸತ್ಯ. ಅವರ ಪುನರುತ್ಥಾನವು, ಸತ್ತವರು ಪುನರುತ್ಥಾನ ಹೊಂದುತ್ತಾರೆ ಎನ್ನುವುದಕ್ಕೆ ಪ್ರಮಾಣ.",
			"21": "<sup>21</sup> ಒಬ್ಬ ಮನುಷ್ಯನಿಂದ ಸಾವು ಸಂಭವಿಸಿದಂತೆಯೇ ಒಬ್ಬ ಮನುಷ್ಯನಿಂದಲೇ ಸತ್ತವರಿಗೆ ಪುನರುತ್ಥಾನವು ಪರಿಣಮಿಸಿತು.",
			"22": "<sup>22</sup> ಆದಾಮನ ಸಂಬಂಧದಿಂದ ಎಲ್ಲರೂ ಸಾವಿಗೀಡಾದಂತೆ ಕ್ರಿಸ್ತಯೇಸುವಿನ ಸಂಬಂಧದಿಂದ ಎಲ್ಲರೂ ಜೀವಂತರಾಗಿ ಏಳುತ್ತಾರೆ.",
			"23": "<sup>23</sup> ಈ ಭಾಗ್ಯವನ್ನು ಒಬ್ಬೊಬ್ಬನೂ ಕ್ರಮಬದ್ಧ ರೀತಿಯಲ್ಲಿ ಪಡೆಯತ್ತಾನೆ. ಪ್ರಪ್ರಥಮ ಫಲವಾಗಿ ಕ್ರಿಸ್ತಯೇಸುವೇ ಜೀವಂತರಾದರು. ಅನಂತರ, ಕ್ರಿಸ್ತಯೇಸುವಿಗೆ ಸೇರಿದವರು ಯೇಸು ಪುನರಾಗಮಿಸುವಾಗ ಜೀವಂತರಾಗುತ್ತಾರೆ.",
			"24": "<sup>24</sup> ಅಂತ್ಯ ಬರುವುದು ಅನಂತರವೇ. ಆಗ ಯೇಸು ಎಲ್ಲಾ ಆಧಿಪತ್ಯವನ್ನೂ ಅಧಿಕಾರವನ್ನೂ ಶಕ್ತಿಯನ್ನೂ ನಿರ್ಮೂಲಮಾಡಿ ತಂದೆಯಾದ ದೇವರಿಗೆ ಸಾಮ್ರಾಜ್ಯವನ್ನು ಒಪ್ಪಿಸಿಕೊಡುವರು.",
			"25": "<sup>25</sup> ಶತ್ರುಗಳೆಲ್ಲರನ್ನು ತಮ್ಮ ಪಾದಪೀಠವಾಗಿಸಿಕೊಳ್ಳುವ ತನಕ ಅವರು ರಾಜ್ಯವಾಳಬೇಕಾಗಿದೆ.",
			"26": "<sup>26</sup> ಕಟ್ಟಕಡೆಗೆ ನಿರ್ಮೂಲವಾಗುವ ಶತ್ರುವೆಂದರೆ ಮೃತ್ಯುವೇ.",
			"27": "<sup>27</sup> “ದೇವರು ಎಲ್ಲವನ್ನು ಅಧೀನಪಡಿಸಿ ಆತನ ಪಾದಪೀಠವನ್ನಾಗಿಸಿದ್ದಾರೆ,” ಎಂದು ಲಿಖಿತವಾಗಿದೆ. “ಎಲ್ಲವನ್ನೂ ಅಧೀನಪಡಿಸಲಾಗಿದೆ,” ಎಂದು ಹೇಳುವಾಗ, ಹಾಗೆ ಅಧೀನಪಡಿಸಿದ ದೇವರು ಅವರಲ್ಲಿ ಸೇರಲಿಲ್ಲ ಎಂಬುದು ಸುಸ್ಪಷ್ಟವಾಗಿದೆ.",
			"28": "<sup>28</sup> ಎಲ್ಲವೂ ಕ್ರಿಸ್ತಯೇಸುವಿಗೆ ಅಧೀನವಾದಾಗ ಪುತ್ರನಾದ ಅವರೇ, ಎಲ್ಲವನ್ನು ತನಗೆ ಅಧೀನಪಡಿಸಿದ ದೇವರಿಗೆ ಅಧೀನರಾಗುತ್ತಾರೆ. ಆಗ ದೇವರು ಸರ್ವರಿಗೆ ಸರ್ವಸ್ವವೂ ಆಗುತ್ತಾರೆ.",
			"29": "<sup>29</sup> ಸತ್ತವರಿಗೆ ಪುನರುತ್ಥಾನವಿಲ್ಲದಿದ್ದರೆ, ಸತ್ತವರಿಗೋಸ್ಕರ ದೀಕ್ಷಾಸ್ನಾನ ಮಾಡಿಸಿಕೊಳ್ಳುತ್ತಾರಲ್ಲಾ ಅದರ ಉದ್ದೇಶವೇನು? ಸತ್ತವರಿಗೆ ಪುನರುತ್ಥಾನವೇ ಇಲ್ಲವಾದರೆ ಅವರಿಗೋಸ್ಕರ ದೀಕ್ಷಾಸ್ನಾನ ಪಡೆಯುವುದರ ಪ್ರಯೋಜನ ಏನು?",
			"30": "<sup>30</sup> ನಾವು ಕೂಡ ಏಕೆ ಪ್ರತಿಕ್ಷಣದಲ್ಲಿಯೂ ಆಪತ್ತು ವಿಪತ್ತುಗಳನ್ನು ಎದುರಿಸುತ್ತಿದ್ದೇವೆ?",
			"31": "<sup>31</sup> ಹೌದು ಸಹೋದರರೇ, ಪ್ರತಿದಿನವೂ ನಾನು ಸಾವಿನ ದವಡೆಯಲ್ಲಿದ್ದೇನೆ, ನಮ್ಮ ಪ್ರಭು ಯೇಸುಕ್ರಿಸ್ತರಲ್ಲಿ ನಿಮ್ಮ ಬಗ್ಗೆ ನನಗಿರುವ ಹೆಮ್ಮೆಯಿಂದ ಇದನ್ನು ಪ್ರಮಾಣವಾಗಿ ಹೇಳುತ್ತಿದ್ದೇನೆ.",
			"32": "<sup>32</sup> ಲೋಕ ಪ್ರಶಂಸೆಗಾಗಿ ನಾನು ಈ ಎಫೆಸದ ‘ಕಾಡುಮೃಗ’ಗಳೊಡನೆ ಹೋರಾಡಿದೆನಾದರೆ, ಅದರಿಂದ ಬರುವ ಲಾಭವಾದರೂ ಏನು? ಸತ್ತವರಿಗೆ ಪುನರುತ್ಥಾನವಿಲ್ಲವಾದರೆ, “ಬನ್ನಿರಿ, ತಿನ್ನೋಣ, ಕುಡಿಯೋಣ, ಕಾದಿದೆಯಲ್ಲ ನಾಳೆ ಮರಣ,” ಎಂಬ ನಾಣ್ನುಡಿಯಂತೆ ನಡೆಯಬಹುದಲ್ಲಾ.",
			"33": "<sup>33</sup> ಮೋಸಹೋಗದಿರಿ. ಏಕೆಂದರೆ, “ದುರ್ಜನರ ಸಂಗ ಸದಾಚಾರದ ಭಂಗ,”",
			"34": "<sup>34</sup> ಜಾಗೃತರಾಗಿರಿ, ದುರ್ಮಾರ್ಗವನ್ನು ಬಿಟ್ಟುಬಿಡಿ, ಸನ್ಮತಿಯುಳ್ಳವರಾಗಿರಿ. ದೇವರನ್ನು ಅರಿಯದ ಮೂಢರು ನಿಮ್ಮಲ್ಲಿ ಕೆಲವರು ಇದ್ದಾರೆ. ನಿಮಗೆ ನಾಚಿಕೆ ಆಗಲೆಂದೇ ಇದನ್ನು ಹೇಳುತ್ತಿದ್ದೇನೆ.",
			"35": "<sup>35</sup> ಸತ್ತವರನ್ನು ಮರಳಿ ಜೀವಂತರಾಗಿಸುವುದು ಹೇಗೆ ಸಾಧ್ಯ? ಅವರ ದೇಹಾಕೃತಿ ಹೇಗಿರುತ್ತದೆ? ಎಂದು ಯಾರಾದರೂ ಕೇಳಿಯಾರು.",
			"36": "<sup>36</sup> ಮತಿಹೀನನೇ, ನೀನು ಬಿತ್ತಿದ ಬೀಜ ಸಾಯದಿದ್ದರೆ ಅದು ಜೀವ ತಾಳುವುದುಂಟೆ? ಇಲ್ಲ.",
			"37": "<sup>37</sup> ನೀನು ಬಿತ್ತುವುದು ಗೋದಿಯನ್ನು ಅಥವಾ ಇನ್ನಾವುದೋ ಕಾಳನ್ನು; ಮುಂದಕ್ಕೆ ಬೆಳೆಯಲಿರುವ ಇಡೀ ಗಿಡವನ್ನಲ್ಲ.",
			"38": "<sup>38</sup> ದೇವರು ತಮ್ಮ ಚಿತ್ತಾನುಸಾರ ಅದಕ್ಕೆ ದೇಹಾಕೃತಿಯನ್ನು ಕೊಡುತ್ತಾರೆ. ಒಂದೊಂದು ಬೀಜಕ್ಕೂ ಅದರದರ ದೇಹಾಕೃತಿಯನ್ನು ಕೊಡುತ್ತಾರೆ.",
			"39": "<sup>39</sup> ಎಲ್ಲಾ ಶರೀರಗಳು ಒಂದೇ ವಿಧವಾಗಿಲ್ಲ; ಮನುಷ್ಯನ ಶರೀರ ಬೇರೆ, ಪಶುಗಳ ಶರೀರ ಬೇರೆ, ಪಕ್ಷಿಗಳ ಶರೀರ ಬೇರೆ, ಮೀನುಗಳ ಶರೀರ ಬೇರೆ.",
			"40": "<sup>40</sup> ಸ್ವರ್ಗೀಯ ಶರೀರಗಳೂ ಇವೆ. ಪೃಥ್ವಿಯ ಶರೀರಗಳೂ ಇವೆ. ಆದರೆ ಒಂದರ ಸ್ವರೂಪ ಮತ್ತೊಂದರಂತೆ ಇಲ್ಲ.",
			"41": "<sup>41</sup> ಸೂರ್ಯನ ಪ್ರಕಾಶ ಒಂದಾದರೆ, ಚಂದ್ರನ ಪ್ರಕಾಶವೇ ಇನ್ನೊಂದು; ನಕ್ಷತ್ರದ ಪ್ರಕಾಶವೇ ಬೇರೊಂದು. ನಕ್ಷತ್ರ ನಕ್ಷತ್ರಗಳ ಪ್ರಕಾಶವು ಸಹ ವೈವಿಧ್ಯವಾದುದು.",
			"42": "<sup>42</sup> ಸತ್ತವರ ಪುನರುತ್ಥಾನವೂ ಹಾಗೆಯೇ. ಬಿತ್ತುವಂಥದ್ದು ಅಳಿದುಹೋಗುವಂಥದ್ದು. ಪುನರುತ್ಥಾನವಾಗುವಂಥದ್ದು ಅಮರವಾದುದು. ಬಿತ್ತಿದ್ದು ಬಲಹೀನವಾದುದು; ಪುನರುತ್ಥಾನಹೊಂದುವಂಥದ್ದು, ಬಲಿಷ್ಠವಾದುದು.",
			"43": "<sup>43</sup> ಬಿತ್ತಿದ್ದು ಕುರೂಪಿಯಾದುದು; ಪುನರುತ್ಥಾನಹೊಂದುವುದು ಕೋಮಲವಾದುದು.",
			"44": "<sup>44</sup> ಬಿತ್ತಿದ್ದು ಭೌತಿಕ ದೇಹ; ಪುನರುತ್ಥಾನ ಹೊಂದುವಂಥದ್ದು ಆಧ್ಯಾತ್ಮಿಕ ದೇಹ. ಭೌತಿಕ ದೇಹ ಇರುವುದು ಸತ್ಯವಾದರೆ ಆಧ್ಯಾತ್ಮಿಕ ದೇಹ ಇರುವುದೂ ಸತ್ಯ.",
			"45": "<sup>45</sup> ಪವಿತ್ರಗ್ರಂಥದಲ್ಲಿ ಲಿಖಿತವಾಗಿರುವಂತೆ ಮೊದಲ ಮಾನವನಾದ ಆದಾಮನು ಜೀವ ಪಡೆದ ವ್ಯಕ್ತಿ; ಕಡೆಯ ಆದಾಮನಾದರೋ ಜೀವ ಕೊಡುವ ಆತ್ಮ.",
			"46": "<sup>46</sup> ಮೊದಲು ಆದುದು ಆಧ್ಯಾತ್ಮಿಕವಾದುದಲ್ಲ, ಪ್ರಾಕೃತವಾದುದು. ಅನಂತರ ಆದುದು ಆಧ್ಯಾತ್ಮಿಕವಾದುದು.",
			"47": "<sup>47</sup> ಮೊದಲನೆಯ ಮಾನವನು ಮಣ್ಣಿನಿಂದಾದವನು; ಮಣ್ಣಿಗೆ ಸಂಬಂಧಪಟ್ಟವನು. ಎರಡನೆಯ ಮಾನವನಾದರೋ ಸ್ವರ್ಗದಿಂದ ಬಂದವನು.",
			"48": "<sup>48</sup> ಮಣ್ಣಿಗೆ ಸಂಬಂಧಪಟ್ಟವರು ಮಣ್ಣಿನಿಂದ ಆದವನಂತೆಯೇ ಇರುತ್ತಾರೆ. ಸ್ವರ್ಗಕ್ಕೆ ಸಂಬಂಧಪಟ್ಟವರು ಸ್ವರ್ಗದಿಂದ ಬಂದವನಂತೆಯೇ ಇರುತ್ತಾರೆ.",
			"49": "<sup>49</sup> ಮಣ್ಣಿನಿಂದ ಆದವನ ರೂಪವನ್ನು ನಾವು ಧರಿಸಿರುವಂತೆಯೇ ಸ್ವರ್ಗದಿಂದ ಬಂದಾತನ ರೂಪವನ್ನು ಧರಿಸುತ್ತೇವೆ.",
			"50": "<sup>50</sup> ಸಹೋದರರೇ, ನಾನು ನಿಮಗೆ ಹೇಳುವುದೇನೆಂದರೆ: ರಕ್ತಮಾಂಸವು ದೇವರ ಸಾಮ್ರಾಜ್ಯಕ್ಕೆ ಬಾಧ್ಯವಾಗಲು ಸಾಧ್ಯವಿಲ್ಲ. ಅಂತೆಯೇ, ಅಳಿದುಹೋಗುವಂಥದ್ದು ಅಮರತ್ವಕ್ಕೆ ಬಾಧ್ಯವಾಗಲು ಸಾಧ್ಯವಿಲ್ಲ.",
			"51": "<sup>51</sup> ಇಗೋ, ಕೇಳೀ! ಇದುವರೆಗೂ ನಿಗೂಢವಾಗಿದ್ದ ರಹಸ್ಯವನ್ನು ನಾನು ನಿಮಗೆ ತಿಳಿಸುತ್ತೇನೆ. ನಾವೆಲ್ಲರೂ ಸಾಯುವುದಿಲ್ಲ. ಅಂತಿಮ ಕಹಳೆಯು ಮೊಳಗಿದಾಗ, ಕ್ಷಣಮಾತ್ರದಲ್ಲಿ, ರೆಪ್ಪೆಬಡಿಯುವಷ್ಟರಲ್ಲಿ ನಾವೆಲ್ಲರೂ ಮಾರ್ಪಡುವೆವು. ಹೌದು, ಕಹಳೆ ಮೊಳಗುವುದು. ಸತ್ತವರು ಪುನರುತ್ಥಾನಹೊಂದಿ ಅಮರರಾಗುವರು ಮತ್ತು ನಾವು ಮಾರ್ಪಡುವೆವು.",
			"53": "<sup>53</sup> ಏಕೆಂದರೆ, ಅಳಿದುಹೋಗುವ ಶರೀರ ಅಮರತ್ವವನ್ನು ಧರಿಸಲೇಬೇಕು. ಮರ್ತ್ಯವಾದ ಶರೀರ ಅಮರತ್ವವನ್ನು ಧರಿಸಲೇಬೇಕು.",
			"54": "<sup>54</sup> ಅಳಿದುಹೋಗುವಂಥದ್ದು ಅಮರತ್ವವನ್ನೂ ಮರ್ತ್ಯವಾದುದು ಅಮರ್ತ್ಯವನ್ನೂ ಧರಿಸಿಕೊಂಡಾಗ ಪವಿತ್ರಗ್ರಂಥದ ಈ ವಾಕ್ಯ ನೆರವೇರುವುದು:",
			"55": "<sup>55</sup> ಸಾವಿಗೆ ಸೋಲಾಯಿತು, ಜಯವು ಸಂಪೂರ್ಣವಾಯಿತು. ಎಲೈ ಸಾವೇ, ನಿನ್ನ ಜಯವೆಲ್ಲಿ? ಎಲೈ ಸಾವೇ, ನಿನ್ನ ವಿಷಕೊಂಡಿಯೆಲ್ಲಿ?",
			"56": "<sup>56</sup> ಪಾಪವೇ ಸಾವಿನ ವಿಷಕೊಂಡಿ. ಪಾಪಕ್ಕೆ ಶಕ್ತ್ಯಾಧಾರ ಶಾಸ್ತ್ರವಿಧಿಗಳೇ.",
			"57": "<sup>57</sup> ಆದರೆ ನಮ್ಮ ಪ್ರಭುವಾದ ಯೇಸುಕ್ರಿಸ್ತರ ಮುಖಾಂತರ ನಮಗೆ ಜಯವನ್ನು ದೊರಕಿಸಿಕೊಟ್ಟಿರುವ ದೇವರಿಗೆ ಸ್ತೋತ್ರ ಸಲ್ಲಲಿ!",
			"58": "<sup>58</sup> ನನ್ನ ಪ್ರಿಯ ಸಹೋದರರೇ, ಸ್ಥಿರಚಿತ್ತರಾಗಿರಿ; ನಿಶ್ಚಲರಾಗಿರಿ. ಪ್ರಭುವಿನ ಸೇವೆಯಲ್ಲಿ ನೀವು ಪಡುವ ಪ್ರಯಾಸ ಎಂದಿಗೂ ನಿಷ್ಫಲವಾಗದು. ಆದ್ದರಿಂದ ಅವರ ಸೇವೆಯಲ್ಲಿ ನಿರಂತರ ಶ್ರದ್ಧೆಯುಳ್ಳವರಾಗಿರಿ."
		},
		"16": {
			"1": "<sup>1</sup> ದೇವಜನರಿಗೋಸ್ಕರ ಹಣ ಸಂಗ್ರಹಿಸುವುದರ ವಿಷಯವಾಗಿ ಹೇಳುವುದಾದರೆ: ಗಲಾತ್ಯದ ಸಭೆಗಳಿಗೆ ನಾನು ಕೊಟ್ಟ ನಿಯಮವನ್ನೇ ನೀವೂ ಅನುಸರಿಸಿರಿ. ನಾನು ಬಂದಾಗ ಹಣ ಸಂಗ್ರಹಿಸುವ ಅವಶ್ಯಕತೆ ಇರಬಾರದು.",
			"2": "<sup>2</sup> ಈ ಕಾರಣ, ನಿಮ್ಮಲ್ಲಿ ಪ್ರತಿ ಒಬ್ಬನೂ ತನ್ನ ಸಂಪಾದನೆಗೆ ತಕ್ಕಂತೆ ಸ್ವಲ್ಪ ಹಣವನ್ನು ಪ್ರತಿ ವಾರದ ಮೊದಲನೆಯ ದಿನವೇ ಪ್ರತ್ಯೇಕಿಸಿ ಇಡಲಿ.",
			"3": "<sup>3</sup> ನಾನು ಬಂದ ನಂತರ ನಿಮ್ಮ ಕೊಡುಗೆಯನ್ನು ಪರಿಚಯ ಪತ್ರದ ಸಮೇತ ನೀವು ಆರಿಸಿದವರ ಕೈಯಲ್ಲಿ ಜೆರುಸಲೇಮಿಗೆ ಕಳುಹಿಸುತ್ತೇನೆ.",
			"4": "<sup>4</sup> ನಾನೇ ಹೋಗುವುದು ಉಚಿತವೆನಿಸಿದರೆ, ಅವರೂ ನನ್ನ ಜೊತೆಗೆ ಬರಲಿ.",
			"5": "<sup>5</sup> ನಾನು ಮೊದಲು ಮಕೆದೋನಿಯದ ಪ್ರವಾಸವನ್ನು ಮುಗಿಸಿಕೊಂಡು ಅನಂತರ ನಿಮ್ಮ ಬಳಿಗೆ ಬರುತ್ತೇನೆ.",
			"6": "<sup>6</sup> ಬಹುಶಃ ನಾನು ನಿಮ್ಮೊಡನೆ ತಂಗಬಹುದು. ಚಳಿಗಾಲವನ್ನು ಪೂರ್ತಿಯಾಗಿ ನಿಮ್ಮಲ್ಲಿ ಕಳೆಯಬೇಕಾಗಬಹುದು. ಆಮೇಲೆ ನಾನು ಹೋಗಬೇಕಾದ ಸ್ಥಳಕ್ಕೆ ನೀವು ನನ್ನನ್ನು ಕಳುಹಿಸಿಕೊಡಬೇಕು.",
			"7": "<sup>7</sup> ಪ್ರಯಾಣದ ನಡುವೆ ನಿಮ್ಮನ್ನು ನೋಡಿ ಹೋಗಲು ನನಗೆ ಇಷ್ಟವಿಲ್ಲ. ಪ್ರಭುವಿನ ಚಿತ್ತವಾದರೆ, ಕೊಂಚಕಾಲವಾದರೂ ನಿಮ್ಮೊಡನೆ ಕಾಲಕಳೆಯಬೇಕೆಂದಿದ್ದೇನೆ.",
			"8": "<sup>8</sup> ಪಂಚಾಶತ್ತಮ ಹಬ್ಬದ ತನಕ ನಾನು ಎಫೆಸದಲ್ಲಿಯೇ ಇರುತ್ತೇನೆ.",
			"9": "<sup>9</sup> ಇಲ್ಲಿ ಅನೇಕ ವಿರೋಧಿಗಳಿದ್ದರೂ ಶುಭಸಂದೇಶದ ಕಾರ್ಯವನ್ನು ಕೈಗೊಳ್ಳಲು ಮಹಾದ್ವಾರಗಳು ನನಗೆ ತೆರೆದಿವೆ.",
			"10": "<sup>10</sup> ತಿಮೊಥೇಯನು ಬಂದರೆ ಆತನಿಗೆ ಯಾವ ಚಿಂತೆಯೂ ಇರದಂತೆ ನೋಡಿಕೊಳ್ಳಿ. ಆತನು ನನ್ನ ಹಾಗೆ ಪ್ರಭುವಿನ ಸೇವೆಯಲ್ಲಿಯೇ ನಿರತನಾಗಿರುತ್ತಾನೆ.",
			"11": "<sup>11</sup> ಯಾರೂ ಆತನನ್ನು ತಾತ್ಸಾರ ಮಾಡದಿರಲಿ. ಸಮಾಧಾನದಿಂದ ಆತನು ನನ್ನ ಬಳಿಗೆ ಬರುವಂತೆ ಕಳುಹಿಸಿಕೊಡಿ. ಆತನ ಆಗಮನವನ್ನು ನಾನು ಇತರ ಸಹೋದರರೊಡನೆ ಎದುರುನೋಡುತ್ತಿದ್ದೇನೆ.",
			"12": "<sup>12</sup> ಸಹೋದರರೇ, ಅಪೊಲೋಸನ ವಿಷಯವಾಗಿ ಹೇಳುವುದಾದರೆ, ಆತನು ಇತರ ಸಹೋದರರೊಡನೆ ನಿಮ್ಮ ಬಳಿಗೆ ಬರಬೇಕೆಂದು ನಾನು ಬಹಳವಾಗಿ ಪ್ರೋತ್ಸಾಹಿಸಿದ್ದೇನೆ. ತಕ್ಷಣವೇ ಬರಲು ಆತನಿಗೆ ಮನಸ್ಸಿಲ್ಲ; ಸದವಕಾಶ ದೊರಕಿದಾಗ ಬರುತ್ತಾನೆ.",
			"13": "<sup>13</sup> ಜಾಗರೂಕರಾಗಿರಿ, ವಿಶ್ವಾಸದಲ್ಲಿ ದೃಢರಾಗಿರಿ, ಧೈರ್ಯಶಾಲಿಗಳಾಗಿರಿ, ಸ್ಥೈರ್ಯದಿಂದ ಬಾಳಿರಿ.",
			"14": "<sup>14</sup> ನೀವು ಮಾಡುವುದನ್ನೆಲ್ಲಾ ಪ್ರೀತಿಯಿಂದ ಮಾಡಿರಿ.",
			"15": "<sup>15</sup> ಸ್ತೇಫನನನ್ನು ಮತ್ತು ಆತನ ಕುಟುಂಬದವರನ್ನು ನೀವು ಬಲ್ಲಿರಿ. ಅವರು ಅಖಾಯದ ಪ್ರಥಮ ಕ್ರೈಸ್ತವಿಶ್ವಾಸಿಗಳು. ದೇವಜನರ ಸೇವೆಗೆ ತಮ್ಮನ್ನೇ ಸಮರ್ಪಿಸಿಕೊಂಡವರು. ನಾನು ನಿಮ್ಮಲ್ಲಿ ವಿನಂತಿಸುವುದೇನೆಂದರೆ:",
			"16": "<sup>16</sup> ಇಂಥವರನ್ನು ಮತ್ತು ಅವರೊಡನೆ ದುಡಿದು ಸೇವೆಮಾಡುವವರನ್ನು ಮುಂದಾಳುಗಳೆಂದು ಸನ್ಮಾನಿಸಿರಿ.",
			"17": "<sup>17</sup> ಸ್ತೇಫನನು, ಪೋರ್ತುನಾತನು ಮತ್ತು ಅಖಾಯಿಕನು ಬಂದುದರಿಂದ ನನಗೆ ಸಂತೋಷವುಂಟಾಯಿತು. ನೀವು ಇಲ್ಲದ್ದರಿಂದ ನನಗುಂಟಾದ ಕೊರತೆಯನ್ನು ಅವರು ನೀಗಿಸಿದ್ದಾರೆ.",
			"18": "<sup>18</sup> ಅವರು ನಿಮ್ಮನ್ನು ಸಂತೈಸಿದಂತೆ ನನ್ನನ್ನೂ ಸಂತೈಸಿದ್ದಾರೆ, ಇಂಥವರನ್ನು ಗೌರವಿಸಬೇಕು.",
			"19": "<sup>19</sup> ಏಷ್ಯ ಸೀಮೆಯಲ್ಲಿರುವ ಧರ್ಮಸಭೆಗಳ ಸಹೋದರರು ನಿಮಗೆ ವಂದನೆಗಳನ್ನು ತಿಳಿಸಿರುತ್ತಾರೆ. ಅಕ್ವಿಲನು ಮತ್ತು ಪ್ರಿಸಿಲ್ಲಳು ಹಾಗೂ ಅವರ ಮನೆಯಲ್ಲಿ ಸಭೆ ಸೇರುವ ಎಲ್ಲರೂ ಪ್ರಭುವಿನ ಹೆಸರಿನಲ್ಲಿ ನಿಮಗೆ ಹಾರ್ದಿಕ ಶುಭಾಶಯಗಳನ್ನು ಕೋರಿದ್ದಾರೆ.",
			"20": "<sup>20</sup> ಇಲ್ಲಿಯ ಸಹೋದರರೆಲ್ಲರೂ ನಿಮಗೆ ನಮಸ್ಕಾರ ಹೇಳಿದ್ದಾರೆ. ನೀವು ಸಹ ಪವಿತ್ರವಾದ ಮುದ್ದಿಟ್ಟು ಒಬ್ಬರನ್ನೊಬ್ಬರು ವಂದಿಸಿರಿ.",
			"21": "<sup>21</sup> ಪೌಲನಾದ ನಾನು ನನ್ನ ಸ್ವಂತ ಕೈಬರಹದಿಂದ ನಿಮಗೆ ವಂದನೆಯನ್ನು ಬರೆಯುತ್ತಿದ್ದೇನೆ.",
			"22": "<sup>22</sup> ಯಾರಾದರೂ ಪ್ರಭುವನ್ನು ಪ್ರೀತಿಸದೆಹೋದರೆ ಅವರಿಗೆ ಧಿಕ್ಕಾರ! “ಮಾರನಥ\" - ಪ್ರಭುವೇ ಬನ್ನಿ.",
			"23": "<sup>23</sup> ನಮ್ಮ ಪ್ರಭು ಯೇಸುಕ್ರಿಸ್ತರ ಅನುಗ್ರಹವು ನಿಮ್ಮೆಲ್ಲರಲ್ಲಿರಲಿ.",
			"24": "<sup>24</sup> ಕ್ರಿಸ್ತಯೇಸುವಿನಲ್ಲಿ ನಿಮ್ಮೆಲ್ಲರಿಗೂ ನನ್ನ ಪ್ರೀತಿವಾತ್ಸಲ್ಯಗಳು!"
		}
	}
};
module.exports = book;