var book = {
	"name": "೨ ಕೊರಿಂಥಿಯರಿಗೆ",
	"numChapters": 13,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ಕೊರಿಂಥದಲ್ಲಿರುವ ದೇವರ ಸಭೆಗೂ ಅಖಾಯ ಸೀಮೆಯಲ್ಲಿರುವ ಎಲ್ಲಾ ದೇವಜನರಿಗೂ - ದೇವರ ಚಿತ್ತಾನುಸಾರ ಕ್ರಿಸ್ತಯೇಸುವಿನ ಪ್ರೇಷಿತನಾದ ಪೌಲನು ಸಹೋದರ ತಿಮೊಥೇಯನೊಡನೆ ಸೇರಿ ಬರೆಯುವ ಪತ್ರ.",
			"2": "<sup>2</sup> ನಮ್ಮ ತಂದೆಯಾದ ದೇವರಿಂದಲೂ ಪ್ರಭುವಾದ ಯೇಸುಕ್ರಿಸ್ತರಿಂದಲೂ ನಿಮಗೆ ಆಶೀರ್ವಾದವೂ ಶಾಂತಿಸಮಾಧಾನವೂ ಲಭಿಸಲಿ!",
			"3": "<sup>3</sup> ನಮ್ಮ ಪ್ರಭು ಯೇಸುಕ್ರಿಸ್ತರ ತಂದೆಯಾದ ದೇವರಿಗೆ ಸ್ತೋತ್ರವಾಗಲಿ. ಅವರು ಕರುಣಾಭರಿತ ತಂದೆ; ಸಕಲ ಸಾಂತ್ವನವನ್ನೀಯುವ ದೇವರು.",
			"4": "<sup>4</sup> ನಮಗೆ ಒದಗುವ ಎಲ್ಲಾ ಸಂಕಷ್ಟಗಳಲ್ಲಿ ಅವರು ನಮ್ಮನ್ನು ಸಂತೈಸುತ್ತಾರೆ. ಹೀಗೆ ದೇವರಿಂದ ದೊರೆತ ಆದರಣೆಯಿಂದ ವಿವಿಧ ಸಂಕಷ್ಟಗಳಲ್ಲಿ ನರಳುತ್ತಿರುವವರನ್ನು ಸಂತೈಸುವುದಕ್ಕೆ ನಾವು ಶಕ್ತರಾಗುತ್ತೇವೆ.",
			"5": "<sup>5</sup> ಕ್ರಿಸ್ತಯೇಸುವಿನ ಯಾತನೆಯು ನಮ್ಮ ಬಾಳಿನಲ್ಲಿ ತುಂಬಿರುವಂತೆ ಅವರ ಮುಖಾಂತರ ಲಭಿಸುವ ಸಾಂತ್ವನವೂ ನಮ್ಮಲ್ಲಿ ತುಂಬಿರುತ್ತದೆ.",
			"6": "<sup>6</sup> ನಾವು ಕಷ್ಟಸಂಕಟಗಳನ್ನು ಸಹಿಸಬೇಕಾಗುವುದು ಸಹ ನಿಮ್ಮ ಉಪಶಮನ ಹಾಗೂ ಸಾಂತ್ವನಕ್ಕಾಗಿಯೇ. ನಮ್ಮ ಕಷ್ಟಸಂಕಟಗಳು ನಿವಾರಣೆಯಾದರೆ ಅದೂ ನಿಮ್ಮ ಆದರಣೆಗಾಗಿಯೇ. ನಾವು ಪಡುವ ಯಾತನೆಯನ್ನು ನೀವು ಸಹನೆಯಿಂದ ಅನುಭವಿಸುವಂತೆ ಅದು ಹುರಿದುಂಬಿಸುತ್ತದೆ.",
			"7": "<sup>7</sup> ನಿಮ್ಮಲ್ಲಿ ನಮಗೆ ಅಚಲವಾದ ಭರವಸೆಯಿದೆ. ಏಕೆಂದರೆ, ನಮ್ಮ ಯಾತನೆಗಳಲ್ಲಿ ನೀವು ಪಾಲುಗೊಳ್ಳುವಂತೆ ನಾವು ಪಡೆಯುವ ಸಾಂತ್ವನದಲ್ಲಿಯೂ ನೀವು ಸಹಭಾಗಿಗಳಾಗಿದ್ದೀರಿ, ಎಂಬುದು ನಮಗೆ ಗೊತ್ತಿದೆ.",
			"8": "<sup>8</sup> ಸಹೋದರರೇ, ಏಷ್ಯಾ ಪ್ರಾಂತ್ಯದಲ್ಲಿ ನಮಗೊದಗಿದ ಹಿಂಸೆಬಾಧೆಗಳು ನಿಮಗೆ ತಿಳಿದಿವೆಯಷ್ಟೆ. ಅಲ್ಲಿ ಸಹಿಸಲಸಾಧ್ಯವಾದಷ್ಟು ಸಂಕಟವನ್ನು ಅನುಭವಿಸಬೇಕಾಯಿತು. ನಾವು ಬದುಕುವ ಆಶೆಯನ್ನೇ ಬಿಡಬೇಕಾಯಿತು.",
			"9": "<sup>9</sup> ನಿಜವಾಗಿಯೂ ನಮಗೆ ಮರಣದಂಡನೆ ವಿಧಿಸಲಾಗಿದೆಯೆಂದು ಭಾವಿಸಿದೆವು. ಇದರಿಂದ ನಾವು ನಮ್ಮ ಸ್ವಂತ ಶಕ್ತಿಯನ್ನೇ ನಂಬಿಕೊಳ್ಳದೆ, ಸತ್ತವರನ್ನು ಪುನರುತ್ಥಾನಗೊಳಿಸುವ ದೇವರಲ್ಲಿ ಭರವಸೆಯಿಡುವಂತೆ ಹೀಗಾಯಿತು.",
			"10": "<sup>10</sup> ದೇವರೇ ನಮ್ಮನ್ನು ಈ ಭಯಂಕರ ಮರಣದಿಂದ ಪಾರುಮಾಡಿದರು. ಇನ್ನು ಮುಂದಕ್ಕೂ ಪಾರುಮಾಡುವರು. ಹೌದು, ನೀವು ಸಹ ನಮಗಾಗಿ ಪ್ರಾರ್ಥಿಸುತ್ತಾ ನಮ್ಮೊಡನೆ ಸಹಕರಿಸಿದರೆ, ಇನ್ನು ಮುಂದಕ್ಕೂ ನಮ್ಮನ್ನು ಪಾರುಮಾಡುವರು ಎಂಬ ಭರವಸೆಯಿಂದ ಇದ್ದೇವೆ. ಇದರ ಫಲವಾಗಿ ನಮಗೆ ದೊರೆಯುವ ವರದಾನಗಳಿಗಾಗಿ ಅನೇಕರು ನಮ್ಮ ಪರವಾಗಿ ದೇವರಿಗೆ ಕೃತಜ್ಞತಾಸ್ತೋತ್ರವನ್ನು ಸಲ್ಲಿಸುವರು.",
			"12": "<sup>12</sup> ಲೋಕದ ಜನರೊಡನೆ, ವಿಶೇಷವಾಗಿ ನಿಮ್ಮೊಡನೆ ವ್ಯವಹರಿಸುವಾಗ ನಾವು ಕೇವಲ ಮಾನವ ಜ್ಞಾನವನ್ನಾಶ್ರಯಿಸದೆ ದೇವರ ಅನುಗ್ರಹವನ್ನೇ ಆಶ್ರಯಿಸಿ ನಡೆದುಕೊಂಡೆವು. ದೇವದತ್ತವಾದ ನಿಷ್ಕಪಟ ಮನಸ್ಸಿನಿಂದಲೂ ಪರಿಶುದ್ಧತೆಯಿಂದಲೂ ವರ್ತಿಸಿದೆವು. ಇದಕ್ಕೆ ನಮ್ಮ ಮನಸ್ಸೇ ಸಾಕ್ಷಿ. ಇದಕ್ಕಾಗಿ ನಾವು ಹೆಮ್ಮೆಪಡುತ್ತೇವೆ.",
			"13": "<sup>13</sup> ನೀವು ಓದಿ ಗ್ರಹಿಸಿಕೊಳ್ಳಬಲ್ಲ ವಿಷಯಗಳನ್ನು ಮಾತ್ರವೇ ನಾವು ನಿಮಗೆ ಬರೆದಿದ್ದೇವೆ. ನಮ್ಮನ್ನು ನೀವಿನ್ನೂ ಭಾಗಶಃ ಅರಿತುಕೊಂಡಿದ್ದೀರಿ. ಮುಂದಕ್ಕಾದರೂ ನೀವು ಸಂಪೂರ್ಣವಾಗಿ ಅರ್ಥಮಾಡಿಕೊಳ್ಳುವಿರೆಂಬ ಭರವಸೆ ನನಗಿದೆ. ಹಾಗೆಯೇ ‘ಪ್ರಭುವಿನ ದಿನದಂದು ನಾವು ನಿಮ್ಮನ್ನು ಕುರಿತು ಹೆಮ್ಮೆಪಡುವಂತೆ ನೀವೂ ನಮ್ಮನ್ನು ಕುರಿತು ಹೆಮ್ಮೆಪಡುವಿರಿ.",
			"15": "<sup>15</sup> ಈ ನಂಬಿಕೆಯಿಂದಲೇ ನಿಮಗೆ ಇಮ್ಮಡಿ ಸಂತೋಷ ಲಭಿಸಬೇಕೆಂದು ನಾನು ನಿಮ್ಮ ಬಳಿಗೆ ಈ ಮುಂಚಿತವಾಗಿಯೇ ಬರಬೇಕೆಂದಿದ್ದೆ.",
			"16": "<sup>16</sup> ನಾನು ಮಕೆದೋನಿಯಕ್ಕೆ ಹೋಗುವ ದಾರಿಯಲ್ಲಿ ಒಮ್ಮೆ ನಿಮ್ಮನ್ನು ಭೇಟಿಮಾಡಬೇಕು ಎಂದಿದ್ದೆ; ಮಕೆದೋನಿಯದಿಂದ ಹಿಂದಿರುಗುವಾಗ ಇನ್ನೊಮ್ಮೆ ಭೇಟಿಯಿತ್ತು ನಿಮ್ಮ ನೆರವಿನಿಂದ ಜುದೇಯ ಪ್ರವಾಸವನ್ನು ಕೈಗೊಳ್ಳಬೇಕೆಂದು ಆಲೋಚಿಸಿದೆ.",
			"17": "<sup>17</sup> ಹೀಗೆ ಆಲೋಚಿಸುತ್ತಿದ್ದಾಗ ನಾನು ಚಂಚಲಚಿತ್ತನಾಗಿದ್ದೆನೋ? ಕೇವಲ ಪ್ರಾಪಂಚಿಕನಂತೆ ಈ ಕ್ಷಣ ಹೌದು, ಮರುಕ್ಷಣ ಇಲ್ಲ ಎಂದು ಹೇಳುವವನಂತಿದ್ದೆನೋ?",
			"18": "<sup>18</sup> ಎಂದಿಗೂ ಇಲ್ಲ, ನಾನು ನಿಮಗೆ ಕೊಟ್ಟ ಮಾತು ದ್ವಂದ್ವಾರ್ಥವುಳ್ಳದ್ದಾಗಿರಲಿಲ್ಲ. ಇದಕ್ಕೆ ದೇವರೇ ಸಾಕ್ಷಿ.",
			"19": "<sup>19</sup> ನಾವು, ಅಂದರೆ ಸಿಲ್ವಾನ, ತಿಮೊಥೆಯ ಮತ್ತು ನಾನು, ಪ್ರಚಾರಮಾಡಿದ ದೇವರ ಪುತ್ರರಾದ ಯೇಸುಕ್ರಿಸ್ತರು, ಈ ಕ್ಷಣ ಹೌದು, ಮರುಕ್ಷಣ ಇಲ್ಲ ಎಂದು ಹೇಳುವವರಲ್ಲ, ಅವರು ದೇವರ ತಥಾಸ್ತು ಆಗಿದ್ದಾರೆ.",
			"20": "<sup>20</sup> ಏಕೆಂದರೆ, ದೇವರು ಮಾಡಿದ ಸಮಸ್ತ ವಾಗ್ದಾನಗಳಿಗೂ ‘ಹೌದು’ ಎಂಬ ಉತ್ತರ ಸಾಕ್ಷಾತ್ ಅವರೇ. ಈ ಕಾರಣದಿಂದಲೇ ನಾವು ದೇವರ ಮಹಿಮೆಯನ್ನು ಸಾರುವಾಗ ಯೇಸುಕ್ರಿಸ್ತರ ಮುಖಾಂತರವೇ ‘ಆಮೆನ್’ ಎನ್ನುತ್ತೇವೆ.",
			"21": "<sup>21</sup> ನಿಮ್ಮನ್ನೂ ನಮ್ಮನ್ನೂ ಕ್ರಿಸ್ತಯೇಸುವಿನಲ್ಲಿ ಒಂದಾಗಿಸಿ ಸ್ಥಿರಪಡಿಸಿದವರು ದೇವರೇ. ನಮ್ಮನ್ನು ಅಭಿಷೇಕಿಸಿದವರೂ ಮೀಸಲಾಗಿಟ್ಟವರೂ ದೇವರೇ.",
			"22": "<sup>22</sup> ಅವರೇ ನಮ್ಮ ಮೇಲೆ ತಮ್ಮ ಮುದ್ರೆಯನ್ನೊತ್ತಿ ನಮ್ಮ ಅಂತರಂಗದಲ್ಲಿ ಪವಿತ್ರಾತ್ಮ ಅವರನ್ನು ಖಾತರಿಯಾಗಿ ಇರಿಸಿದ್ದಾರೆ.",
			"23": "<sup>23</sup> ನಿಮಗೆ ತೊಂದರೆ ಆಗಬಾರದೆಂದು ನಾನು ಕೊರಿಂಥಕ್ಕೆ ಬರಲಿಲ್ಲ. ಇದನ್ನು ಹೃದಯಪೂರ್ವಕವಾಗಿ ಹೇಳುತ್ತಿದ್ದೇನೆ. ಇದಕ್ಕೆ ದೇವರೇ ಸಾಕ್ಷಿ.",
			"24": "<sup>24</sup> ನಿಮ್ಮ ವಿಶ್ವಾಸದ ಬಗ್ಗೆ ನಾವು ದಬ್ಬಾಳಿಕೆ ನಡೆಸುತ್ತಿದ್ದೇವೆಂದು ತಿಳಿಯಬೇಡಿ; ವಿಶ್ವಾಸದಲ್ಲಿ ನೀವು ದೃಢರಾಗಿಯೇ ಇದ್ದೀರಿ. ನಿಮ್ಮ ಸಂತೋಷಕ್ಕಾಗಿ ನಾವು ನಿಮ್ಮೊಡನೆ ಸಹಕರಿಸುತ್ತೇವೆ."
		},
		"2": {
			"1": "<sup>1</sup> ಇನ್ನೊಮ್ಮೆ ನಿಮ್ಮ ಬಳಿಗೆ ಬಂದು, ನಿಮ್ಮನ್ನು ದುಃಖಕ್ಕೀಡುಮಾಡಬಾರದೆಂದು ನಾನು ನಿರ್ಧರಿಸಿಕೊಂಡೆ.",
			"2": "<sup>2</sup> ನಾನೇ ನಿಮ್ಮನ್ನು ದುಃಖಪಡಿಸಿದೆನಾದರೆ, ನನ್ನನ್ನು ಸಂತೋಷಪಡಿಸುವವರು ಯಾರು? ನನ್ನಿಂದ ದುಃಖಕ್ಕೆ ಒಳಗಾದ ನೀವೇ ಅಲ್ಲವೇ?",
			"3": "<sup>3</sup> ಇದಕ್ಕಾಗಿಯೇ ನಾನು ಆ ಪತ್ರವನ್ನು ಬರೆದದ್ದು; ನನ್ನನ್ನು ಸಂತೋಷಪಡಿಸಬೇಕಾದವರೇ ನನ್ನನ್ನು ದುಃಖಕ್ಕೆ ಈಡುಮಾಡಬಾರದೆಂದು ನಾನು ಬರಲಿಲ್ಲ. ನನ್ನ ಸಂತೋಷವೇ ನಿಮ್ಮ ಸಂತೋಷವೆಂದು ನೀವು ಭಾವಿಸುತ್ತೀರೆಂದು ಬಲ್ಲೆ.",
			"4": "<sup>4</sup> ಮನೋವ್ಯಥೆಯಿಂದಲೂ ಹೃದಯವೇದನೆಯಿಂದಲೂ ಕಣ್ಣೀರಿಡುತ್ತಾ ನಾನು ನಿಮಗೆ ಪತ್ರ ಬರೆದೆ. ನಿಮ್ಮನ್ನು ದುಃಖಕ್ಕೀಡುಮಾಡಬೇಕೆಂದಲ್ಲ, ನಿಮ್ಮನ್ನು ಎಷ್ಟರಮಟ್ಟಿಗೆ ಪ್ರೀತಿಸುತ್ತಿರುವೆನೆಂದು ನೀವು ತಿಳಿದುಕೊಳ್ಳಬೇಕೆಂದು ಬರೆದೆ.",
			"5": "<sup>5</sup> ಒಬ್ಬನು ದುಃಖವನ್ನುಂಟುಮಾಡಿದ್ದಾನಾದರೆ, ಅವನು ಹಾಗೆ ಮಾಡಿದ್ದು ನನಗೊಬ್ಬನಿಗೇ ಅಲ್ಲ. ಒಂದು ವಿಧದಲ್ಲಿ ನಿಮಗೆಲ್ಲರಿಗೂ ಸಹ ದುಃಖವನ್ನುಂಟುಮಾಡಿದ್ದಾನೆ. ಅವನ ಮೇಲೆ ಹೆಚ್ಚು ಕಠಿಣನಾಗಿರಲು ನನಗೆ ಇಷ್ಟವಿಲ್ಲ.",
			"6": "<sup>6</sup> ನಿಮ್ಮಲ್ಲಿ ಬಹುಮಂದಿ ಅವನಿಗೆ ವಿಧಿಸಿರುವ ಶಿಕ್ಷೆಯೇ ಸಾಕಾಗಿದೆ.",
			"7": "<sup>7</sup> ಈಗ ನೀವು ಅವನನ್ನು ಕ್ಷಮಿಸಿ ಸಂತೈಸಿರಿ. ಇಲ್ಲವಾದರೆ ಅವನು ಅತೀವ ದುಃಖದಲ್ಲಿಯೇ ಮುಳುಗಿಹೋದಾನು.",
			"8": "<sup>8</sup> ನಿಮ್ಮ ಪ್ರೀತಿ ಅವನಿಗೆ ಮನದಟ್ಟಾಗುವಂತೆ ನಡೆದುಕೊಳ್ಳಬೇಕೆಂದು ನಿಮ್ಮನ್ನು ಕೇಳಿಕೊಳ್ಳುತ್ತೇನೆ.",
			"9": "<sup>9</sup> ನೀವು ಎಲ್ಲ ವಿಷಯಗಳಲ್ಲೂ ನನಗೆ ವಿಧೇಯರಾಗಿದ್ದೀರೋ ಇಲ್ಲವೋ ಎಂದು ಪರೀಕ್ಷಿಸಲು ನಾನು ಬರೆದದ್ದು.",
			"10": "<sup>10</sup> ನೀವು ಯಾರನ್ನು ಕ್ಷಮಿಸುತ್ತೀರೋ ಅವನನ್ನು ನಾನೂ ಕ್ಷಮಿಸುತ್ತೇನೆ. ನಾನು ಯಾವುದನ್ನಾದರೂ ಕ್ಷಮಿಸಿದ್ದರೆ ಅಥವಾ ಕ್ಷಮಿಸಬೇಕಾಗಿದ್ದರೆ ಅದನ್ನು ಕ್ರಿಸ್ತಯೇಸುವಿನ ಸಮ್ಮುಖದಲ್ಲಿ ನಿಮಗೋಸ್ಕರ ಕ್ಷಮಿಸುತ್ತೇನೆ.",
			"11": "<sup>11</sup> ಸೈತಾನನ ಕುತಂತ್ರಗಳನ್ನು ನಾವು ಅರಿಯದವರಲ್ಲ; ಅವನ ಕೈ ಮೇಲಾಗದಂತೆ ನಾವು ನೋಡಿಕೊಳ್ಳಬೇಕು.",
			"12": "<sup>12</sup> ಯೇಸುಕ್ರಿಸ್ತರ ಶುಭಸಂದೇಶವನ್ನು ಸಾರುವುದಕ್ಕೆ ನಾನು ತ್ರೋವಕ್ಕೆ ಬಂದಾಗ, ಪ್ರಭುವಿನ ಕೃಪೆಯಿಂದ ನನಗೆ ಸದವಕಾಶದ ಬಾಗಿಲು ತೆರೆದಿತ್ತು.",
			"13": "<sup>13</sup> ಆದರೆ ನನ್ನ ಸಹೋದರ ತೀತನನ್ನು ಅಲ್ಲಿ ಕಾಣದೆಹೋದುದರಿಂದ ನನ್ನ ಮನಸ್ಸಿಗೆ ನೆಮ್ಮದಿ ಇಲ್ಲವಾಯಿತು. ಕೂಡಲೇ ಅಲ್ಲಿಯ ಜನರಿಂದ ಬೀಳ್ಕೊಂಡು ಮಕೆದೋನಿಯಕ್ಕೆ ಹೊರಟೆ.",
			"14": "<sup>14</sup> ಯೇಸುಕ್ರಿಸ್ತರ ಅನ್ಯೋನ್ಯತೆಯಲ್ಲಿ ನಮ್ಮನ್ನು ಸದಾ ವಿಜಯೋತ್ಸವದತ್ತ ನಡೆಸುವ ಹಾಗು ಕ್ರಿಸ್ತಜ್ಞಾನವೆಂಬ ಪರಿಮಳವನ್ನು ಎಲ್ಲೆಡೆಯಲ್ಲೂ ನಮ್ಮ ಮೂಲಕ ಪಸರಿಸುವ ದೇವರಿಗೆ ವಂದನೆಗಳು.",
			"15": "<sup>15</sup> ಹೌದು, ಉದ್ಧಾರದ ಮಾರ್ಗದಲ್ಲಿರುವವರಿಗೂ ವಿನಾಶಮಾರ್ಗದಲ್ಲಿರುವವರಿಗೂ ಕ್ರಿಸ್ತಯೇಸುವೇ ದೇವರಿಗರ್ಪಿಸಿದ ಪರಿಮಳದಂತೆ ನಾವು ಇದ್ದೇವೆ.",
			"16": "<sup>16</sup> ವಿನಾಶ ಮಾರ್ಗದಲ್ಲಿರುವವರಿಗೆ ಇದು ಮೃತ್ಯುಕಾರಕ ಗಂಧ; ಉದ್ಧಾರ ಮಾರ್ಗದಲ್ಲಿರುವವರಿಗೆ ಇದು ಸಜ್ಜೀವದಾಯಕ ಸುಗಂಧ. ಇಂಥ ಕಾರ್ಯಕ್ಕೆ ಸಮರ್ಥರು ಯಾರು?",
			"17": "<sup>17</sup> ಎಷ್ಟೋ ಮಂದಿ ದೇವರ ವಾಕ್ಯವನ್ನು ಕಲಬೆರಕೆ ಮಾಡಿ ವ್ಯಾಪಾರಮಾಡುತ್ತಾರೆ. ನಾವು ಹಾಗಲ್ಲ; ದೇವರಿಂದಲೇ ನಿಯೋಜಿತರಾಗಿ, ದೇವರ ಸಮಕ್ಷಮದಲ್ಲಿ ಕ್ರಿಸ್ತಯೇಸುವಿನ ಅನ್ಯೋನ್ಯತೆಯಲ್ಲಿ ನಾವು ಯಥಾರ್ಥವಾದುದನ್ನೇ ಉಪದೇಶಿಸುವವರು."
		},
		"3": {
			"1": "<sup>1</sup> ನಾವೀಗ ಆತ್ಮಸ್ತುತಿಯನ್ನು ಮಾಡಲು ಆರಂಭಿಸಿದಂತೆ ತೋರುತ್ತದೆಯೇ? ಅಥವಾ ನಿಮಗೆ ತೋರಿಸಲು ಯೋಗ್ಯತಾಪತ್ರ ಇತರರಿಗೆ ಬೇಕಾಗಿರುವಂತೆ ನಮಗೂ ಬೇಕಾಗಿದೆಯೇ? ಇಲ್ಲವೆ, ಯೋಗ್ಯತಾಪತ್ರವನ್ನು ನಿಮ್ಮಿಂದ ಪಡೆಯುವ ಅವಶ್ಯಕತೆ ನಮಗೂ ಇದೆಯೇ?",
			"2": "<sup>2</sup> ನೀವೇ ನಮ್ಮ ಯೋಗ್ಯತಾಪತ್ರ, ನಮ್ಮ ಹೃದಯ ಪಟಲದ ಮೇಲೆ ಬರೆಯಲಾದ ಪತ್ರ. ಅದನ್ನು ಯಾರು ಬೇಕಾದರೂ ಗುರುತಿಸಬಹುದು. ಓದಿ ತಿಳಿದುಕೊಳ್ಳಬಹುದು.",
			"3": "<sup>3</sup> ಕ್ರಿಸ್ತಯೇಸುವೇ ನಮ್ಮಿಂದ ಬರೆಸಿದ ಪತ್ರ ನೀವು; ಇದು ಸ್ಪಷ್ಟ. ಬರೆದಿರುವುದು ಶಾಯಿಯಿಂದಲ್ಲ, ಜೀವಂತ ದೇವರ ಪವಿತ್ರಾತ್ಮರಿಂದ. ಕೊರೆದದ್ದೂ ಕಲ್ಲಿನ ಮೇಲೆ ಅಲ್ಲ, ಮಾನವ ಹೃದಯದ ಮೇಲೆ.",
			"4": "<sup>4</sup> ಕ್ರಿಸ್ತಯೇಸುವಿನ ಮುಖಾಂತರ ನಮಗೆ ದೇವರಲ್ಲಿ ಇಂಥ ಭರವಸೆ ಇರುವುದರಿಂದಲೇ ಇದನ್ನು ಹೇಳುತ್ತಿದ್ದೇವೆ.",
			"5": "<sup>5</sup> ನಮ್ಮ ಸ್ವಂತ ಶಕ್ತಿಯಿಂದಲೇ ಏನನ್ನೋ ಸಾಧಿಸಿಬಿಟ್ಟಿದ್ದೇವೆ ಎಂದು ಹೇಳಿಕೊಳ್ಳುವ ಸಾಮರ್ಥ್ಯ ನಮಗಿಲ್ಲ. ನಮ್ಮ ಸಾಮರ್ಥ್ಯವೆಲ್ಲ ದೇವರಿಂದಲೇ ಬಂದುದು.",
			"6": "<sup>6</sup> ನಮಗೆ ಹೊಸ ಒಡಂಬಡಿಕೆಯ ಸೇವಕರಾಗುವಂಥ ಸಾಮರ್ಥ್ಯವನ್ನು ನೀಡಿದವರು ದೇವರೇ. ಈ ಒಡಂಬಡಿಕೆ ಲಿಖಿತ ಶಾಸನಕ್ಕೆ ಸಂಬಂಧಿಸಿದ್ದಲ್ಲ, ಪವಿತ್ರಾತ್ಮರಿಗೆ ಸಂಬಂಧಿಸಿದ್ದು. ಲಿಖಿತವಾದುದು ಮೃತ್ಯುಕಾರಕವಾದುದು; ಪವಿತ್ರಾತ್ಮ ಸಂಬಂಧವಾದುದು ಸಜ್ಜೀವದಾಯಕವಾದುದು.",
			"7": "<sup>7</sup> ಕಲ್ಲಿನ ಮೇಲೆ ಕೊರೆಯಲಾದ ಶಾಸನ ಪ್ರಕಟವಾದಾಗ ದೇವರ ಮಹಿಮೆ ಕಾಣಿಸಿಕೊಂಡಿತು. ಆಗ ಮೋಶೆಯ ಮುಖವನ್ನು ಆವರಿಸಿದ್ದ ಪ್ರಭೆ ಕುಂದಿಹೋಗುವಂಥದ್ದಾಗಿದ್ದರೂ ಆ ಪ್ರಭೆಯಿಂದಾಗಿ ಆತನ ಮುಖವನ್ನು ದಿಟ್ಟಿಸಿನೋಡಲು ಇಸ್ರಯೇಲರಿಗೆ ಆಗಲಿಲ್ಲ.",
			"8": "<sup>8</sup> ಮೃತ್ಯುಕಾರಕವಾದ ಈ ಶಾಸನ ಅಷ್ಟು ಮಹಿಮೆಯಿಂದ ಕೂಡಿತ್ತಾದರೆ ಪವಿತ್ರಾತ್ಮ ಚೈತನ್ಯದಿಂದ ಕೂಡಿದ ಸೇವೆಯು ಇನ್ನೆಷ್ಟು ಮಹಿಮೆಯಿಂದ ಕೂಡಿರಬೇಕು?",
			"9": "<sup>9</sup> ದಂಡನೆಗೆ ಗುರಿಮಾಡುವ ಈ ಶಾಸನ ಸೇವೆ ಇಷ್ಟು ಮಹಿಮೆಯಿಂದ ಕೂಡಿದ್ದಾದರೆ, ಸದ್ಧರ್ಮಿಗಳೆಂದು ನಿರ್ಣಯಿಸುವ ಸೇವೆ ಇನ್ನೆಷ್ಟು ಮಹಿಮೆಯಿಂದ ಕೂಡಿರಬೇಕು?",
			"10": "<sup>10</sup> ಈ ಸೇವೆಯ ಅಪರಿಮಿತ ಮಹಿಮೆಯ ಮುಂದೆ ಹಿಂದಿನ ಶಾಸನದ ಮಹಿಮೆ ಇಲ್ಲದಂತಾಗಿದೆ.",
			"11": "<sup>11</sup> ಅಳಿದು ಹೋಗುವಂಥದ್ದೇ, ಅಷ್ಟು ಮಹಿಮೆಯಿಂದ ಕೂಡಿತ್ತಾದರೆ, ಶಾಶ್ವತವಾಗಿ ಉಳಿಯುವಂಥದ್ದು ಮತ್ತೆಷ್ಟು ಮಹಿಮೆಯಿಂದ ಕೂಡಿರಬೇಕು?",
			"12": "<sup>12</sup> ನಮಗೆ ಇಂಥ ಭರವಸೆ ಇರುವುದರಿಂದಲೇ ನಾವು ಇಷ್ಟು ಧೈರ್ಯದಿಂದ ಇದ್ದೇವೆ.",
			"13": "<sup>13</sup> ಕುಂದಿಹೋಗುವಂಥ ಮಹಿಮೆಯು ನಂದಿಹೋಗುವುದನ್ನು ಇಸ್ರಯೇಲರು ಕಾಣದಂತೆ ಮೋಶೆಯು ತನ್ನ ಮುಖಕ್ಕೆ ಮುಸುಕುಹಾಕಿಕೊಳ್ಳುತ್ತಿದ್ದನು. ನಾವು ಆತನಂತೆ ಅಲ್ಲ.",
			"14": "<sup>14</sup> ಆ ಜನರ ಬುದ್ಧಿಗೆ ಮಂಕು ಕವಿದಿತ್ತು. ಇಂದಿನವರೆಗೂ ಹಳೆಯ ಒಡಂಬಡಿಕೆಯನ್ನು ಓದುವಾಗಲೆಲ್ಲಾ ಅದೇ ಮಂಕಿನ ಮುಸುಕು ಕವಿದಿದೆ. ಅದನ್ನು ಕ್ರಿಸ್ತಯೇಸುವಿನ ಮುಖಾಂತರವೇ ತೆಗೆಯಲು ಸಾಧ್ಯ.",
			"15": "<sup>15</sup> ಹೌದು, ಮೋಶೆ ಬರೆದುದನ್ನು ಓದುವಾಗಲೆಲ್ಲಾ ಅವರ ಮನಸ್ಸು ಇಂದಿಗೂ ಅದೇ ಮುಸುಕಿನಿಂದ ಮುಚ್ಚಿದೆ.",
			"16": "<sup>16</sup> ‘ಪ್ರಭುವಿಗೆ ಅಭಿಮುಖರಾದಾಗ ಮಾತ್ರ ಆ ಮುಸುಕನ್ನು ತೆಗೆಯಲಾಗುತ್ತದೆ.’",
			"17": "<sup>17</sup> ಈ ವಾಕ್ಯದಲ್ಲಿ, ‘ಪ್ರಭು’ ಎಂದರೆ ದೇವರ ಆತ್ಮವೇ. ಪ್ರಭುವಿನ ಆತ್ಮ ಎಲ್ಲಿದೆಯೋ ಅಲ್ಲಿ ವಿಮೋಚನೆಯೂ ಇದೆ.",
			"18": "<sup>18</sup> ಮುಸುಕು ತೆರೆದ ಮುಖವುಳ್ಳ ನಾವೆಲ್ಲರೂ ಪ್ರಭುವಿನ ಮಹಿಮೆಯನ್ನು ಪ್ರತಿಬಿಂಬಿಸುವ ಕನ್ನಡಿಯಂತೆ ಇದ್ದೇವೆ. ಆ ಪ್ರಭುವಿನಿಂದ ಹೊರಹೊಮ್ಮುವ ಮಹಿಮೆ ನಮ್ಮನ್ನು ಅಧಿಕಾಧಿಕವಾಗಿ ಮಾರ್ಪಡಿಸಿ ಅವರನ್ನೇ ಹೋಲುವಂತೆ ಮಾಡುತ್ತದೆ. ಇದೆಲ್ಲಾ ದೇವರಾತ್ಮವಾಗಿರುವ ಪ್ರಭುವಿನ ಕಾರ್ಯವೇ ಸರಿ."
		},
		"4": {
			"1": "<sup>1</sup> ದೇವರ ಕರುಣೆಯಿಂದ ಈ ಸೇವೆಯನ್ನು ಕೈಗೊಂಡಿರುವ ನಾವು ಧೈರ್ಯಗೆಟ್ಟು ಹಿಂಜರಿಯುವುದಿಲ್ಲ.",
			"2": "<sup>2</sup> ಲಜ್ಜೆಗೆಟ್ಟ ಗುಪ್ತಕಾರ್ಯಗಳಿಂದ ನಾವು ದೂರವಿದ್ದೇವೆ. ನಮ್ಮ ನಡತೆಯಲ್ಲಿ ಮೋಸ, ವಂಚನೆ ಎಂಬುದಿಲ್ಲ. ದೇವರ ವಾಕ್ಯವನ್ನು ನಾವು ಅಪಾರ್ಥಗೊಳಿಸುವುದಿಲ್ಲ. ಬದಲಿಗೆ ಸತ್ಯವನ್ನು ಪ್ರಾಮಾಣಿಕವಾಗಿ ಸಾರುತ್ತೇವೆ. ಹೀಗೆ ದೇವರ ಸಮ್ಮುಖದಲ್ಲಿ ಸಜ್ಜನರಾದವರಿಗೆ ಸಾಕ್ಷಿಗಳಾಗಿದ್ದೇವೆ.",
			"3": "<sup>3</sup> ನಾವು ಪ್ರಚಾರಮಾಡುವ ಶುಭಸಂದೇಶ ಕೆಲವರಿಗೆ ಇನ್ನೂ ಮುಸುಕಿನ ಮರೆಯಾಗಿದ್ದರೆ, ಹಾಗಿರುವುದು ವಿನಾಶ ಮಾರ್ಗದಲ್ಲಿರುವವರಿಗೆ ಮಾತ್ರ.",
			"4": "<sup>4</sup> ಇವರು ವಿಶ್ವಾಸಿಸುವುದಿಲ್ಲ. ಏಕೆಂದರೆ, ಪೃಥ್ವಿಯ ಮಿಥ್ಯ ದೈವವು ಇವರ ಮನಸ್ಸನ್ನು ಮಂಕಾಗಿಸಿದೆ; ದೇವರ ಪ್ರತಿರೂಪವಾಗಿರುವ ಕ್ರಿಸ್ತಯೇಸುವಿನ ಮಹಿಮೆಯನ್ನು ಸಾರುವ ಶುಭಸಂದೇಶದ ಬೆಳಕನ್ನು ಕಾಣದಂತೆ ಇವರನ್ನು ಕುರುಡಾಗಿಸಿದೆ.",
			"5": "<sup>5</sup> ನಾವು ನಮ್ಮನ್ನೇ ಕುರಿತು ಪ್ರಚಾರಮಾಡುತ್ತಿಲ್ಲ. ಆದರೆ ಕ್ರಿಸ್ತಯೇಸುವೇ ಪ್ರಭುವೆಂದೂ ಅವರಿಗೋಸ್ಕರ ನಾವು ನಿಮ್ಮ ದಾಸರೆಂದೂ ಪ್ರಚುರಪಡಿಸುತ್ತಿದ್ದೇವೆ.",
			"6": "<sup>6</sup> “ಕತ್ತಲೆಯಿಂದ ಬೆಳಕು ಹೊಳೆಯಲಿ,” ಎಂದ ದೇವರೇ, ತಮ್ಮ ಜ್ಯೋತಿಯಿಂದ ನಮ್ಮ ಅಂತರಂಗವನ್ನು ಬೆಳಗಿಸಿದ್ದಾರೆ. ಇದರ ಪರಿಣಾಮವಾಗಿ ಕ್ರಿಸ್ತಯೇಸುವಿನ ಮುಖಮಂಡಲದಲ್ಲಿ ಪ್ರಜ್ವಲಿಸುತ್ತಿರುವ ದೇವರ ಮಹಿಮೆಯ ದಿವ್ಯಜ್ಞಾನವು ನಮ್ಮಲ್ಲಿ ಉದಯಿಸುವಂತಾಗಿದೆ.",
			"7": "<sup>7</sup> ನಾವು ಇಂಥ ಆಧ್ಯಾತ್ಮಿಕ ಮಾಣಿಕ್ಯವನ್ನು ಪಡೆದಿದ್ದರೂ ಮಣ್ಣಿನ ಮಡಕೆಗಳಂತೆ ಇದ್ದೇವೆ. ಹೀಗೆ, ಈ ಪರಮೋನ್ನತ ಶಕ್ತಿ ನಮಗೆ ಸೇರಿದ್ದಲ್ಲ. ದೇವರಿಗೆ ಸೇರಿದ್ದೆಂದು ವ್ಯಕ್ತವಾಗುತ್ತದೆ.",
			"8": "<sup>8</sup> ನಾವು ಇಕ್ಕಟ್ಟುಬಿಕ್ಕಟ್ಟುಗಳಿಗೆ ಒಳಗಾಗಿದ್ದರೂ ನಜ್ಜುಗುಜ್ಜಾಗಲಿಲ್ಲ; ಸಂಶಯ ಸಂದೇಹಗಳಿಗೊಳಗಾದರೂ ನಿರಾಶೆಗೊಳ್ಳಲಿಲ್ಲ.",
			"9": "<sup>9</sup> ಶತ್ರುಗಳು ಅನೇಕರಿದ್ದರೂ ಮಿತ್ರನೊಬ್ಬನು ಇಲ್ಲದಿರಲಿಲ್ಲ. ಪ್ರಾಣ ಹಿಂಸೆಗಳಿಗೀಡಾಗಿದ್ದರೂ ಪ್ರಾಣನಷ್ಟವನ್ನು ಅನುಭವಿಸಲಿಲ್ಲ.",
			"10": "<sup>10</sup> ಯೇಸುವಿನ ದಿವ್ಯಜೀವವು ನಮ್ಮ ದೇಹದಲ್ಲಿ ಗೋಚರವಾಗುವಂತೆ ಅವರ ಮರಣಯಾತನೆಯನ್ನು ನಿರಂತರವಾಗಿ ಅನುಭವಿಸುತ್ತಾ ಬಾಳುತ್ತಿದ್ದೇವೆ.",
			"11": "<sup>11</sup> ಯೇಸುವಿನ ಅಮರ ಜೀವವು ನಮ್ಮ ನಶ್ವರ ಶರೀರದಲ್ಲಿ ಗೋಚರವಾಗುವಂತೆ ಬದುಕಿರುವಾಗಲೇ ನಾವು ಯೇಸುವಿಗೋಸ್ಕರ ಸತತ ಸಾವಿಗೆ ಈಡಾಗಿದ್ದೇವೆ.",
			"12": "<sup>12</sup> ಹೀಗೆ ನಮ್ಮಲ್ಲಿ ಸಾವು ಕಾರ್ಯಗತವಾಗುತ್ತಿದ್ದರೆ ನಿಮ್ಮಲ್ಲಿ ಜೀವ ಕಾರ್ಯಗತವಾಗುತ್ತಿದೆ.",
			"13": "<sup>13</sup> “ನಾನು ವಿಶ್ವಾಸವಿಟ್ಟೆನು. ಆದ್ದರಿಂದ ಮಾತನಾಡಿದೆನು,” ಎಂದು ಪವಿತ್ರಗ್ರಂಥದಲ್ಲಿ ಬರೆಯಲಾಗಿದೆ. ಇದೇ ಮನೋಭಾವನೆಯಿಂದ ಕೂಡಿರುವ ನಾವು ಮಾತನಾಡಲೇಬೇಕು. ಏಕೆಂದರೆ ನಮಗೆ ವಿಶ್ವಾಸವಿದೆ.",
			"14": "<sup>14</sup> ಪ್ರಭು ಯೇಸುವನ್ನು ಪುನರುತ್ಥಾನಗೊಳಿಸಿದ ದೇವರು ನಮ್ಮನ್ನು ಯೇಸುಕ್ರಿಸ್ತರೊಡನೆ ಪುನರುತ್ಥಾನಗೊಳಿಸಿ ತಮ್ಮ ಸಾನ್ನಿಧ್ಯಕ್ಕೆ ಬರಮಾಡಿಕೊಳ್ಳುವರು ಎಂಬುದನ್ನು ನಾವು ಬಲ್ಲೆವು. ಅಂತೆಯೇ ನಿಮ್ಮನ್ನೂ ಬರಮಾಡಿಕೊಳ್ಳುವರು.",
			"15": "<sup>15</sup> ಇದೆಲ್ಲವೂ ನಿಮ್ಮ ಒಳಿತಿಗಾಗಿಯೇ. ಹೀಗೆ ಅನೇಕರಲ್ಲಿ ದೇವರ ವರಪ್ರಸಾದವು ಉಕ್ಕಿ ಹರಿಯುವುದು; ಅವರಲ್ಲಿ ಕೃತಜ್ಞತಾಭಾವನೆಯನ್ನು ಹೆಚ್ಚಿಸುವುದು; ಇದರಿಂದಾಗಿ ದೇವರ ಮಹಿಮೆ ಬೆಳಗುವುದು.",
			"16": "<sup>16</sup> ಹೀಗಿರಲು, ನಾವು ಎದೆಗುಂದುವುದಿಲ್ಲ. ನಮ್ಮ ಭೌತಿಕ ದೇಹವು ನಶಿಸುತ್ತಿದ್ದರೂ ನಮ್ಮ ಅಂತರಂಗವು ದಿನೇದಿನೇ ನೂತನವಾಗುತ್ತದೆ.",
			"17": "<sup>17</sup> ನಾವು ಅನುಭವಿಸುತ್ತಿರುವ ಕಷ್ಟಸಂಕಟಗಳು ಅಲ್ಪವಾದುವು, ಕ್ಷಣಿಕವಾದುವು. ಅವುಗಳಿಂದ ಲಭಿಸುವ ಮಹಿಮೆಯಾದರೋ ಅಪಾರವಾದುದು, ಅನಂತವಾದುದು.",
			"18": "<sup>18</sup> ನಮ್ಮ ಗಮನವು ಕೇಂದ್ರೀಕೃತವಾಗಿರುವುದು ಗೋಚರವಾದುವುಗಳ ಮೇಲಲ್ಲ, ಅಗೋಚರವಾದುವುಗಳ ಮೇಲೆ. ಗೋಚರವಾದುವುಗಳು ತಾತ್ಕಾಲಿಕ; ಅಗೋಚರವಾದುವುಗಳು ಶಾಶ್ವತ."
		},
		"5": {
			"1": "<sup>1</sup> ಭೂಮಿಯ ಮೇಲಿನ ನಮ್ಮ ಈ ದೇಹ ಎಂಬ ಗುಡಾರವು ನಾಶವಾಗಿಹೋದರೂ ಸ್ವರ್ಗದಲ್ಲಿ ಶಾಶ್ವತವಾದ ಗೃಹವೊಂದು ನಮಗೆ ದೊರಕುವುದು. ಅದು ಮಾನವರಿಂದ ನಿರ್ಮಿತ ಆದುದಲ್ಲ, ದೇವರಿಂದಲೇ ನಿರ್ಮಿತವಾದುದು. ಇದು ನಮಗೆ ತಿಳಿದ ವಿಷಯ.",
			"2": "<sup>2</sup> ಈ ಗುಡಾರದಲ್ಲಿರುವವರೆಗೆ ನಾವು ನರಳುತ್ತೇವೆ; ಸ್ವರ್ಗೀಯ ನಿವಾಸವನ್ನು ನಮ್ಮ ದೇಹದ ಮೇಲೆ ಎಂದಿಗೆ ನಾವು ಧರಿಸಿಕೊಳ್ಳುತ್ತೇವೋ ಎಂದು ಹಾತೊರೆಯುತ್ತೇವೆ.",
			"3": "<sup>3</sup> ಅದನ್ನು ಧರಿಸಿಕೊಂಡಾಗ ನಾವು ಬೆತ್ತಲೆಯಾಗಿ ಇರಲಾರೆವು.",
			"4": "<sup>4</sup> ನಾವು ಈ ದೇಹವೆಂಬ ಗುಡಾರದಲ್ಲಿರುವ ತನಕ ನರಳುತ್ತೇವೆ, ಭಾರದಿಂದ ಬಳಲುತ್ತೇವೆ. ಈ ದೇಹವು ಕಳಚಿಹೋಗಬೇಕೆಂಬುದು ನಮ್ಮ ಬಯಕೆ ಅಲ್ಲ. ನಶ್ವರವಾದುದು ನುಂಗಿಹೋಗಿ ಅಮರವಾದುದು ಉಳಿಯಬೇಕೆಂದೇ ಈ ಗು಼ಡಾರದ ಮೇಲೆ ಆ ನಿವಾಸವನ್ನು ಧರಿಸಿಕೊಳ್ಳಲು ಅಪೇಕ್ಷಿಸುತ್ತೇವೆ.",
			"5": "<sup>5</sup> ಈ ಸುಸ್ಥಿತಿಗಾಗಿಯೇ ದೇವರು ನಮ್ಮನ್ನು ಸಿದ್ಧಗೊಳಿಸಿದ್ದಾರೆ. ಇದಕ್ಕೆಲ್ಲಾ ಖಾತರಿಯಾಗಿ ಪವಿತ್ರಾತ್ಮರನ್ನೇ ನಮಗೆ ದಯಪಾಲಿಸಿದ್ದಾರೆ.",
			"6": "<sup>6</sup> ಈ ಕಾರಣದಿಂದಲೇ ನಾವು ಸದಾ ಸ್ಥಿರಚಿತ್ತರಾಗಿದ್ದೇವೆ. ಈ ದೇಹವನ್ನೇ ನೆಚ್ಚಿ ಇದರಲ್ಲೇ ನೆಲೆಯಾಗಿರುವವರೆಗೂ ನಾವು ಪ್ರಭುವಿನಿಂದ ದೂರವಿರುವ ಪ್ರವಾಸಿಗಳಂತೆ ಇದ್ದೇವೆ.",
			"7": "<sup>7</sup> ನಾವು ನೋಡಿ ನಡೆಯುವವರಲ್ಲ, ನಂಬಿ ನಡೆಯುವವರು.",
			"8": "<sup>8</sup> ಆದ್ದರಿಂದ ಈ ದೇಹವನ್ನು ತ್ಯಜಿಸಿ, ಪ್ರಭುವನ್ನೇ ನೆಚ್ಚಿ ನೆಲಸುವುದು ಲೇಸೆಂದು ಧೈರ್ಯದಿಂದ ಹೇಳುತ್ತೇವೆ.",
			"9": "<sup>9</sup> ಈ ಕಾರಣ, ಇಲ್ಲಾದರೂ ಸರಿ, ಅಲ್ಲಾದರೂ ಸರಿ, ಪ್ರಭು ಮೆಚ್ಚುವಂತೆ ಬಾಳುವುದೊಂದೇ ನಮ್ಮ ಗುರಿ.",
			"10": "<sup>10</sup> ನಾವು ಎಲ್ಲರೂ ನ್ಯಾಯವಿಚಾರಣೆಗಾಗಿ ಕ್ರಿಸ್ತಯೇಸುವಿನ ಮುಂದೆ ನಿಲ್ಲಲೇಬೇಕು. ಪ್ರತಿಯೊಬ್ಬನೂ ತನ್ನ ದೈಹಿಕ ಜೀವನದಲ್ಲಿ ಮಾಡಿದ ಪಾಪ ಪುಣ್ಯಕಾರ್ಯಗಳಿಗೆ ತಕ್ಕ ಪ್ರತಿಫಲವನ್ನು ಪಡೆಯಲೇಬೇಕು.",
			"11": "<sup>11</sup> ಪ್ರಭುವಿನ ಭಯಭಕ್ತಿ ನಮಗಿರುವುದರಿಂದ ನಾವು ಮಾನವರ ಮನವೊಲಿಸಲು ಪ್ರಯತ್ನಿಸುತ್ತೇವೆ. ನಾವು ಎಂಥವರೆಂದು ದೇವರು ಚೆನ್ನಾಗಿ ಬಲ್ಲರು. ಅದು ನಿಮ್ಮ ಮನಸ್ಸಾಕ್ಷಿಗೂ ಅರಿವಾಗಿದೆಯೆಂದು ನಂಬುತ್ತೇವೆ.",
			"12": "<sup>12</sup> ಮತ್ತೊಮ್ಮೆ ನಮ್ಮನ್ನು ನಾವೇ ಹೊಗಳಿಕೊಳ್ಳುತ್ತೇವೆಂದು ನೀವು ಭಾವಿಸಬಾರದು. ನಮ್ಮನ್ನು ಕುರಿತು ನೀವೇ ಹೆಚ್ಚಳಪಡುವಂತೆ ಒಂದು ಅವಕಾಶವನ್ನು ಒದಗಿಸುತ್ತಿದ್ದೇವೆ, ಅಷ್ಟೆ. ಹೀಗೆ, ಮನುಷ್ಯನ ಅಂತರಂಗವನ್ನು ಅರಿಯದೆ ಅವನ ಹೊರ ತೋರಿಕೆಗಳನ್ನೇ ನೆಚ್ಚಿ ನಲಿಯುವವರಿಗೆ ಸೂಕ್ತ ಪ್ರತ್ಯುತ್ತರವನ್ನು ಕೊಡಲು ನೀವು ಶಕ್ತರಾಗುತ್ತೀರಿ.",
			"13": "<sup>13</sup> ನಮಗೆ ಬುದ್ಧಿಭ್ರಮಣೆಯಾಗಿದೆಯೆಂದು ಭಾವಿಸುತ್ತೀರೋ? ಹಾಗಿದ್ದರೆ ಅದು ದೇವರ ಮಹಿಮೆಗಾಗಿ ನಾವು ಸ್ವಸ್ಥಬುದ್ಧಿಯುಳ್ಳವರೆಂದು ನೆನೆಸುತ್ತೀರೋ? ಹಾಗಿದ್ದರೆ ಅದು ನಿಮ್ಮ ಹಿತಕ್ಕಾಗಿ.",
			"14": "<sup>14</sup> ಯೇಸುಕ್ರಿಸ್ತರ ಪ್ರೀತಿಯ ಪಾಲನೆಗೆ ನಾವು ಒಳಗಾಗಿದ್ದೇವೆ. ಎಲ್ಲಾ ಮಾನವರಿಗೋಸ್ಕರ ಒಬ್ಬನು ಮರಣಹೊಂದಿದನು. ಆದ್ದರಿಂದ ನಾವೆಲ್ಲರೂ ಆ ಮರಣದಲ್ಲಿ ಪಾಲುಗಾರರು.",
			"15": "<sup>15</sup> ಸರ್ವರಿಗಾಗಿ ಯೇಸು ಪ್ರಾಣತ್ಯಾಗ ಮಾಡಿದರು. ಪರಿಣಾಮವಾಗಿ, ಇನ್ನು ಮುಂದೆ ಜೀವಿಸುವವರು ತಮಗಾಗಿ ಜೀವಿಸದೆ, ಸತ್ತು ಪುನರುತ್ಥಾನರಾದ ಯೇಸುವಿಗಾಗಿ ಜೀವಿಸಬೇಕು.",
			"16": "<sup>16</sup> ಇನ್ನು ಮುಂದೆ ನಾವು ಯಾರನ್ನೂ ಕೇವಲ ಮಾನವ ದೃಷ್ಟಿಯಿಂದ ಪರಿಗಣಿಸುವುದಿಲ್ಲ. ಒಂದು ಕಾಲದಲ್ಲಿ, ಕ್ರಿಸ್ತಯೇಸುವನ್ನು ನಾವು ಹಾಗೆ ಪರಿಗಣಿಸಿದ್ದುಂಟು. ಆದರೆ ಇನ್ನು ಮುಂದೆ ಹಾಗೆ ಮಾಡುವುದಿಲ್ಲ.",
			"17": "<sup>17</sup> ಯಾರಾದರೂ ಯೇಸುಕ್ರಿಸ್ತರೊಡನೆ ಒಂದಾದರೆ ಅವನು ನೂತನ ಸೃಷ್ಟಿಯಾಗುತ್ತಾನೆ. ಹಳೆಯದೆಲ್ಲಾ ಅಳಿದುಹೋಗುತ್ತದೆ. ಹೊಸದಿದೋ, ಜನ್ಮತಳೆದಿದೆ.",
			"18": "<sup>18</sup> ಇದೆಲ್ಲಾ ಆಗುವುದು ದೇವರಿಂದ. ದೇವರು ಕ್ರಿಸ್ತಯೇಸುವಿನ ಮುಖಾಂತರ ನಮ್ಮನ್ನು ತಮ್ಮೊಡನೆ ಸಂಧಾನಪಡಿಸಿಕೊಂಡಿದ್ದಾರೆ.ಇದಲ್ಲದೆ, ಇತರರನ್ನು ಸಂಧಾನಕ್ಕೆ ತರುವ ಕಾರ್ಯವನ್ನು ನಮಗೆ ವಹಿಸಿದ್ದಾರೆ.",
			"19": "<sup>19</sup> ಹೌದು, ದೇವರು ಮಾನವರ ಅಪರಾಧಗಳನ್ನು ಲೆಕ್ಕಿಸದೆ, ಕ್ರಿಸ್ತಯೇಸುವಿನಲ್ಲಿ ಇಡೀ ಜಗತ್ತನ್ನೇ ತಮ್ಮೊಡನೆ ಸಂಧಾನಗೊಳಿಸುತ್ತಿದ್ದಾರೆ. ಈ ಸಂಧಾನದ ಸಂದೇಶವನ್ನು ಸಾರುವ ಸೌಭಾಗ್ಯವನ್ನು ಅವರೇ ನಮಗೆ ಕೊಟ್ಟಿದ್ದಾರೆ.",
			"20": "<sup>20</sup> ಆದ್ದರಿಂದಲೇ ನಾವು ಕ್ರಿಸ್ತಯೇಸುವಿನ ರಾಯಭಾರಿಗಳು. ದೇವರೇ ನಮ್ಮ ಮುಖಾಂತರ ಕರೆ ನೀಡುತ್ತಿದ್ದಾರೆ. ಅವರೊಡನೆ ಸಂಧಾನಮಾಡಿಕೊಳ್ಳಿರೆಂದು ಕ್ರಿಸ್ತಯೇಸುವಿನ ಹೆಸರಿನಲ್ಲಿ ನಾವು ನಿಮ್ಮನ್ನು ವಿನಂತಿಸುತ್ತೇವೆ.",
			"21": "<sup>21</sup> ಪಾಪವನ್ನೇ ಅರಿಯದ ಕ್ರಿಸ್ತಯೇಸುವನ್ನು ದೇವರು ನಮಗೋಸ್ಕರ ಪಾಪಸ್ವರೂಪಿಯನ್ನಾಗಿಸಿದರು. ಕ್ರಿಸ್ತಯೇಸುವಿನಲ್ಲಿ ನಾವು ದೇವರೊಡನೆ ಸತ್ಸಂಬಂಧವನ್ನು ಪಡೆಯಲೆಂದೇ ಹೀಗೆ ಮಾಡಿದರು."
		},
		"6": {
			"1": "<sup>1</sup> ದೇವರೊಡನೆ ದುಡಿಯುತ್ತಿರುವ ನಾವು ನಿಮ್ಮಲ್ಲಿ ವಿಜ್ಞಾಪಿಸುವುದೇನೆಂದರೆ: ದೇವರಿಂದ ನೀವು ಪಡೆದ ವರಪ್ರಸಾದಗಳನ್ನು ವ್ಯರ್ಥಮಾಡಬೇಡಿ.",
			"2": "<sup>2</sup> :ಸುಪ್ರಸನ್ನತೆಯ ಕಾಲದಲ್ಲಿ ನಿನ್ನನ್ನಾಲಿಸಿದೆನು; ಉದ್ಧಾರ ದಿನದಂದು ನಿನಗೆ ನೆರವಾದೆನು,” ಎಂದಿದ್ದಾರೆ ದೇವರು. ಇದೇ ಆ ಸುಪ್ರಸನ್ನತೆಯ ಕಾಲ. ಇದೇ ಆ ಉದ್ಧಾರದ ಸುದಿನ.",
			"3": "<sup>3</sup> ನಮ್ಮ ಸೇವಾಕಾರ್ಯವು ಅವಹೇಳನಕ್ಕೆ ಗುರಿಯಾಗದಂತೆ, ನಾವು ಯಾರಿಗೂ ಯಾವ ತೆರನಾದ ಅಡ್ಡಿಯನ್ನೂ ಒಡ್ಡಲಿಲ್ಲ.",
			"4": "<sup>4</sup> ಬದಲಿಗೆ, ಎಲ್ಲ ವಿಷಯಗಳಲ್ಲೂ ದೇವರ ದಾಸರೆಂದು ತೋರಿಸಿಕೊಳ್ಳುತ್ತೇವೆ. ಕಷ್ಟಸಂಕಟಗಳಲ್ಲೂ ದುಃಖದುರಿತಗಳಲ್ಲೂ ತಾಳ್ಮೆಯಿಂದ ವರ್ತಿಸಿದ್ದೇವೆ.",
			"5": "<sup>5</sup> ಏಟುಪೆಟ್ಟುಗಳನ್ನು ತಿಂದಿದ್ದೇವೆ, ಸೆರೆಮನೆವಾಸ ಅನುಭವಿಸಿದ್ದೇವೆ, ಕೋಪಕ್ರಾಂತಿಗಳಿಗೆ ಗುರಿಯಾಗಿದ್ದೇವೆ, ಮೈಮುರಿಯೆ ದುಡಿದಿದ್ದೇವೆ, ನಿದ್ದೆಗೆಟ್ಟು ಬಳಲಿದ್ದೇವೆ, ಊಟವಿಲ್ಲದೆ ಸೊರಗಿದ್ದೇವೆ.",
			"6": "<sup>6</sup> ನಿರ್ಮಲ ಮನಸ್ಸು, ಸನ್ಮತಿ, ಸಹನೆ, ಸದಯತೆಯಿಂದಲೂ, ಪವಿತ್ರಾತ್ಮ, ನಿಷ್ಕಪಟ ಪ್ರೇಮ,",
			"7": "<sup>7</sup> ಸತ್ಯೋಕ್ತಿ, ದೈವಶಕ್ತಿ, ಇವುಗಳಿಂದಲೂ ನಾವು ದೇವರ ದಾಸರೆಂದು ತೋರ್ಪಡಿಸುತ್ತೇವೆ. ರಕ್ಷಿಸಲೂ ಎದುರಿಸಲೂ ಧರ್ಮವೆಂಬ ಆಯುಧವನ್ನು ಕೈಯಲ್ಲಿ ಹಿಡಿದಿದ್ದೇವೆ.",
			"8": "<sup>8</sup> ಮಾನ-ಅವಮಾನ, ಕೀರ್ತಿ-ಅಪಕೀರ್ತಿ ಎರಡನ್ನೂ ಅನುಭವಿಸುತ್ತಿದ್ದೇವೆ. ಸತ್ಯವಾದಿಗಳು ನಾವಾದರೂ ಮಿಥ್ಯವಾದಿಗಳು ಎಂದೆನಿಸಿಕೊಂಡಿದ್ದೇವೆ.",
			"9": "<sup>9</sup> ಪ್ರಖ್ಯಾತರಾಗಿದ್ದರೂ ಖ್ಯಾತರಹಿತರಾಗಿದ್ದೇವೆ. ಬದುಕಿದ್ದರೂ ಸತ್ತವರೆನಿಸಿಕೊಂಡಿದ್ದೇವೆ. ಶಿಕ್ಷೆಗೆ ಗುರಿಯಾಗಿದ್ದರೂ ಇನ್ನೂ ಕೊಲೆಗೆ ಈಡಾಗದೆ ಇದ್ದೇವೆ.",
			"10": "<sup>10</sup> ದುಃಖಪಡುತ್ತಿದ್ದರೂ ಸದಾ ಸಂತೋಷದಿಂದ ಇದ್ದೇವೆ. ದರಿದ್ರರಾಗಿದ್ದರೂ ಅನೇಕರನ್ನು ಧನವಂತರನ್ನಾಗಿಸುತ್ತಿದ್ದೇವೆ; ನಾವು ಏನೂ ಇಲ್ಲದವರಾಗಿದ್ದರೂ ಎಲ್ಲವನ್ನೂ ಪಡೆದವರಂತೆ ಇದ್ದೇವೆ.",
			"11": "<sup>11</sup> ಕೊರಿಂಥದ ನಿವಾಸಿಗಳೇ, ಬಿಚ್ಚುಮನದಿಂದಲೂ ತೆರೆದ ಹೃದಯದಿಂದಲೂ ನಾವು ನಿಮ್ಮೊಡ಼ನೆ ಮಾತನಾಡಿದ್ದೇವೆ.",
			"12": "<sup>12</sup> ನಮ್ಮಲ್ಲಿ ಸಂಕುಚಿತ ಭಾವನೆಯೇನೂ ಇಲ್ಲ. ಇದು ಇರುವುದಾದರೆ ನಿಮ್ಮಲ್ಲೇ.",
			"13": "<sup>13</sup> ಆದ್ದರಿಂದ ಮಕ್ಕಳಿಗೆ ಹೇಳುವಂತೆ ಹೇಳುತ್ತಿದ್ದೇವೆ: ನಾವು ನಿಮ್ಮನ್ನು ಪೂರ್ಣ ಹೃದಯದಿಂದ ಪ್ರೀತಿಸಿದಂತೆ, ನೀವೂ ನಮ್ಮನ್ನು ಪೂರ್ಣ ಹೃದಯದಿಂದ ಪ್ರೀತಿಸಿರಿ.",
			"14": "<sup>14</sup> ಅವಿಶ್ವಾಸಿಗಳೊಡನೆ ಒಂದಾಗಬೇಡಿ. ಧರ್ಮಕ್ಕೂ ಅಧರ್ಮಕ್ಕೂ ಸಾಟಿಯೆಲ್ಲಿ? ಬೆಳಕಿಗೂ ಕತ್ತಲೆಗೂ ಎಲ್ಲಿಯ ಸಂಬಂಧ?",
			"15": "<sup>15</sup> ಕ್ರಿಸ್ತಯೇಸುವಿಗೂ ಸೈತಾನನಿಗೂ ಎಲ್ಲಿಯ ನಂಟು? ವಿಶ್ವಾಸಿಗೂ ಅವಿಶ್ವಾಸಿಗೂ ಎಲ್ಲಿಯ ಸಹವಾಸ?",
			"16": "<sup>16</sup> ದೇವರ ಆಲಯಕ್ಕೂ ಕಲ್ಲಿನ ವಿಗ್ರಹಕ್ಕೂ ಎಲ್ಲಿಯ ಸಾಮ್ಯ? ನಾವಾದರೋ ಜೀವಂತ ದೇವರ ಮಂದಿರವಾಗಿದ್ದೇವೆ. ಇದನ್ನು ದೇವರೇ ಹೇಳಿದ್ದಾರೆ: “ನನ್ನ ಜನರಲ್ಲೇ ನಾ ಮನೆಮಾಡುವೆನು ಅವರ ನಡುವೆಯೇ ನಾ ತಿರುಗಾಡುವೆನು ಅವರಿಗೆ ನಾನೇ ದೇವರಾಗಿರುವೆನು ಅವರೆನಗೆ ಪ್ರಜೆಯಾಗಿರುವರು.”",
			"17": "<sup>17</sup> ಇದಲ್ಲದೆ: ಅನ್ಯಜನರನ್ನು ಬಿಟ್ಟು ಹೊರಬನ್ನಿ ಅವರಿಂದ ಬೇರ್ಪಟ್ಟು ಬಾಳಿರಿ ಮಲಿನವಾದುದನ್ನು ಮುಟ್ಟದಿರಿ.",
			"18": "<sup>18</sup> ಅಕ್ಕರೆಯಿಂದ ನಾನಾಗ ನಿಮ್ಮನ್ನು ಸ್ವಾಗತಿಸುವೆನು ತಂದೆಯಾಗಿರುವೆನು ನಾನು ನಿಮಗೆ ಪುತ್ರಪುತ್ರಿಯರಾಗಿರುವಿರಿ ನೀವು ನನಗೆ.” - ಎಂದು ನುಡಿದಿಹರು ಸರ್ವಶಕ್ತ ಪ್ರಭು."
		},
		"7": {
			"1": "<sup>1</sup> ಪ್ರಿಯ ಸಹೋದರರೇ, ಇಂಥ ವಾಗ್ದಾನಗಳನ್ನು ಪಡೆದಿರುವ ನಾವು ಎಲ್ಲಾ ವಿಧವಾದ ಮಲಿನತೆಯಿಂದ ದೂರವಿದ್ದು ದೇಹಾತ್ಮಗಳಲ್ಲಿ ಶುದ್ಧರಾಗಿರೋಣ. ದೇವರಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರಾಗಿ ಪರಿಶುದ್ಧತೆಯಲ್ಲಿ ಪರಿಣತಿಯನ್ನು ಪಡೆಯೋಣ.",
			"2": "<sup>2</sup> ನಿಮ್ಮ ಹೃದಯಗಳಲ್ಲಿ ನಮಗೆ ಸ್ಥಳವಿರಲಿ. ನಾವು ಯಾರಿಗೂ ಅನ್ಯಾಯಮಾಡಿಲ್ಲ, ಯಾರಿಗೂ ಕೇಡನ್ನು ಬಗೆದಿಲ್ಲ, ಯಾರನ್ನೂ ವಂಚಿಸಲಿಲ್ಲ.",
			"3": "<sup>3</sup> ನಿಮ್ಮನ್ನು ನಿಂದಿಸುವುದಕ್ಕಾಗಿ ಇದನ್ನು ನಾನು ಹೇಳುತ್ತಿಲ್ಲ; ಈ ಮೊದಲೇ ನಿಮಗೆ ತಿಳಿಸಿರುವಂತೆ, ‘ಸತ್ತರೂ ಒಂದಾಗಿ ಸಾಯುತ್ತೇವೆ, ಬದುಕಿದರೂ ಒಂದಾಗಿ ಬದುಕುತ್ತೇವೆ,” ಎನ್ನುವಷ್ಟರ ಮಟ್ಟಿಗೆ ನೀವು ನನ್ನ ಹೃದಯದಲ್ಲಿದ್ದೀರಿ.",
			"4": "<sup>4</sup> ನಿಮ್ಮಲ್ಲಿ ನನಗೆ ದೃಢವಾದ ಭರವಸೆಯುಂಟು. ಗಾಢವಾದ ಅಭಿಮಾನವುಂಟು. ನಮ್ಮೆಲ್ಲಾ ಸಂಕಟಗಳಲ್ಲೂ ನಾನು ಎದೆಗುಂದದೆ ಆನಂದಭರಿತನಾಗಿದ್ದೇನೆ.",
			"5": "<sup>5</sup> ನಾವು ಮಕೆದೋನಿಯವನ್ನು ತಲುಪಿದ ಮೇಲೂ ದೇಹಕ್ಕೆ ಕಿಂಚಿತ್ತಾದರೂ ವಿಶ್ರಾಂತಿ ಸಿಗಲಿಲ್ಲ. ಎಲ್ಲಾ ಕಡೆಗಳಿಂದಲೂ ನಮಗೆ ತೊಂದರೆ ತಾಪತ್ರಯಗಳು ಬಂದುವು. ಹೊರಗೆ ಕಲಹ, ಒಳಗೆ ತಳಮಳ;",
			"6": "<sup>6</sup> ಆದರೂ ಎದೆಗುಂದಿದವರನ್ನು ಸಂತೈಸುವ ದೇವರು ತೀತನನ್ನು ಕಳುಹಿಸಿ ನಮ್ಮನ್ನು ಸಂತೈಸಿದರು.",
			"7": "<sup>7</sup> ತೀತನ ಆಗಮನದಿಂದ ಮಾತ್ರವಲ್ಲ, ನೀವು ಆತನಿಗೆ ತೋರಿದ ಪ್ರೀತ್ಯಾದರದ ವರದಿಯಿಂದಲೂ ನಾವು ಪುನಃ ಚೇತನಗೊಂಡೆವು. ನನ್ನನ್ನು ನೋಡಲು ನಿಮಗಿರುವ ಹಂಬಲ, ನನ್ನ ಮೇಲೆ ನಿಮಗಿರುವ ಅನುಕಂಪ, ನನ್ನ ಬಗ್ಗೆ ನಿಮಗಿರುವ ಹಿತಚಿಂತನೆ, ಇವುಗಳನ್ನು ಕೇಳಿ ನಮಗೆ ಮತ್ತಷ್ಟು ಆನಂದವಾಯಿತು.",
			"8": "<sup>8</sup> ನಾನು ಬರೆದ ಪತ್ರದಿಂದ ನಿಮ್ಮ ಮನಸ್ಸಿಗೆ ನೋವಾಗಿರಬಹುದು. ಆದರೂ ಚಿಂತೆಯಿಲ್ಲ. ಆ ಪತ್ರವು ನಿಮ್ಮನ್ನು ಸ್ವಲ್ಪಕಾಲ ದುಃಖಕ್ಕೀಡುಮಾಡಿತೆಂದು ನಾನು ಮೊದಮೊದಲು ನೊಂದುಕೊಂಡೆನಾದರೂ ಈಗ ಸಂತೋಷಪಡುತ್ತೇನೆ.",
			"9": "<sup>9</sup> ನಿಮಗೆ ದುಃಖ ಉಂಟಾಯಿತೆಂಬ ಕಾರಣದಿಂದ ಅಲ್ಲ, ಆ ದುಃಖದಿಂದ ನಿಮಗೆ ಮನಪರಿವರ್ತನೆ ಆಯಿತೆಂಬ ಕಾರಣದಿಂದ ಸಂತೋಷಪಡುತ್ತೇನೆ. ನಿಮಗೆ ದುಃಖ ಉಂಟಾದುದು ದೇವರ ಚಿತ್ತವೇ ಸರಿ. ಆದರೆ ನನ್ನಿಂದ ನಿಮಗೆ ಅನ್ಯಾಯವೇನೂ ಆಗಲಿಲ್ಲ.",
			"10": "<sup>10</sup> ದೇವರ ಚಿತ್ತಾನುಸಾರ ಬಂದೊದಗುವ ದುಃಖವು ಹೃದಯ ಪರಿವರ್ತನೆಗೆ ಕಾರಣವಾಗುತ್ತದೆ; ಜೀವೋದ್ಧಾರಕ್ಕೆ ಎಡೆಮಾಡುತ್ತದೆ. ಕೇವಲ ಪ್ರಾಪಂಚಿಕವಾದ ದುಃಖವು ಸಾವಿಗೆ ಒಯ್ಯುತ್ತದೆ;",
			"11": "<sup>11</sup> ದೇವರ ಚಿತ್ತಾನುಸಾರವಾದ ನಿಮ್ಮ ದುಃಖದಿಂದ ಎಂಥಾ ಶ್ರದ್ಧೆ ಉಂಟಾಗಿದೆ ಎಂಬುದನ್ನು ಗಮನಿಸಿನೋಡಿರಿ; ನಿಮ್ಮಲ್ಲಿ ಎಂಥ ಉತ್ಸಾಹ, ದೋಷವಿಮುಕ್ತರಾಗಲು ಎಂಥ ಪ್ರಯಾಸ, ಎಷ್ಟು ರೋಷ, ಎಷ್ಟು ಆವೇಶ, ಎಷ್ಟು ಎಚ್ಚರಿಕೆ, ಎಷ್ಟು ಹಂಬಲ, ತಪ್ಪಿತಸ್ಥರನ್ನು ಶಿಕ್ಷಿಸಲು ಎಷ್ಟು ಕಟ್ಟುನಿಟ್ಟು! ನೀವು ನಿರ್ದೋಷಿಗಳೆಂಬುದಕ್ಕೆ ಇವೆಲ್ಲವೂ ಸಾದೃಶ್ಯಗಳಾಗಿವೆ.",
			"12": "<sup>12</sup> ನಾನು ನಿಮಗೆ ಆ ಪತ್ರವನ್ನು ಬರೆದದ್ದು ತಪ್ಪುಮಾಡಿದವನಿಗೆ ದಂಡನೆಯಾಗಲಿ ಎಂದಲ್ಲ, ಆ ತಪ್ಪಿನಿಂದ ನೊಂದವನಿಗೆ ನ್ಯಾಯ ದೊರಕಲೆಂದೂ ಅಲ್ಲ; ನಮ್ಮ ಬಗ್ಗೆ ನಿಮಗಿರುವ ಅಕ್ಕರೆ-ಆಸಕ್ತಿಗಳು ದೇವರ ಸನ್ನಿಧಿಯಲ್ಲಿ ನಿಮಗೆ ವ್ಯಕ್ತವಾಗಲೆಂದೇ ಬರೆದೆನು. ಆದ್ದರಿಂದಲೇ ನಮ್ಮ ಮನಸ್ಸಿಗೆ ನೆಮ್ಮದಿ ಉಂಟಾಗಿದೆ.",
			"13": "<sup>13</sup> ಅಷ್ಟೇ ಅಲ್ಲ, ನೀವೆಲ್ಲರೂ ತೀತನ ಮನಸ್ಸನ್ನು ತಣಿಸಿದ್ದರಿಂದ ಆತನ ಸಂತೋಷವನ್ನು ಕಂಡು ನಾವು ಮತ್ತಷ್ಟು ತೃಪ್ತರಾಗಿದ್ದೇವೆ.",
			"14": "<sup>14</sup> ತೀತನ ಮುಂದೆ ನಾನು ನಿಮ್ಮನ್ನು ಹೊಗಳಿದ್ದು ನಿಜ; ಹಾಗೆ ಹೊಗಳಿದ್ದಕ್ಕಾಗಿ ನಾನು ನಾಚಿಕೆಪಡಬೇಕಾಗಿಲ್ಲ. ನಾವು ನಿಮಗೆ ಹೇಳಿದ್ದೆಲ್ಲವೂ ಹೇಗೆ ಸತ್ಯವಾಗಿತ್ತೋ, ಹಾಗೆಯೇ ತೀತನ ಮುಂದೆ ನಾವು ಆಡಿದ ಹೊಗಳಿಕೆಯ ಮಾತುಗಳೆಲ್ಲವೂ ಸತ್ಯವಾಗಿವೆ.",
			"15": "<sup>15</sup> ನಿಮ್ಮೆಲ್ಲರ ವಿನಯ-ವಿಧೇಯತೆಯನ್ನು, ಭಯಭಕ್ತಿಯಿಂದ ನೀವು ನೀಡಿದ ಸ್ವಾಗತವನ್ನು ಸ್ಮರಿಸಿಕೊಳ್ಳುವಾಗಲೆಲ್ಲ ನಿಮ್ಮ ಮೇಲೆ ಆತನಿಗಿರುವ ಪ್ರೀತಿ ಉಕ್ಕಿ ಹರಿಯುತ್ತದೆ.",
			"16": "<sup>16</sup> ನಿಮ್ಮ ಮೇಲೆ ನನಗೆ ಪೂರ್ಣಭರವಸೆ ಇದೆ. ಇದೇ ನನ್ನ ಸಂತೋಷ."
		},
		"8": {
			"1": "<sup>1</sup> ಸಹೋದರರೇ, ಮಕೆದೋನಿಯ ಸಭೆಗಳಿಗೆ ದೇವರು ದಯಪಾಲಿಸಿದ ಕೃಪಾತಿಶಯಗಳನ್ನು ನಾನು ನಿಮಗೆ ತಿಳಿಸ ಬಯಸುತ್ತೇನೆ.",
			"2": "<sup>2</sup> ಈ ಸಭೆಗಳವರು ಕಷ್ಟಸಂಕಟಗಳ ಕುಲುಮೆಯಲ್ಲಿ ಬೆಂದಿದ್ದರೂ ಹರ್ಷಭರಿತರಾಗಿದ್ದಾರೆ. ಕಡುಬಡತನದಲ್ಲಿ ನರಳುತ್ತಿದ್ದರೂ ಅಪಾರ ಔದಾರ್ಯದಿಂದ ನಡೆದುಕೊಳ್ಳುತ್ತಿದ್ದಾರೆ.",
			"3": "<sup>3</sup> ತಮ್ಮ ಶಕ್ತಿಗನುಸಾರವಾಗಿ ಮಾತ್ರವಲ್ಲ, ಕೆಲವೊಮ್ಮೆ ಶಕ್ತಿಮೀರಿಯೂ ನೀಡಿದ್ದಾರೆಂದು ನಾನು ಖಂಡಿತವಾಗಿ ಹೇಳುತ್ತೇನೆ.",
			"4": "<sup>4</sup> ದೇವಜನರಿಗೆ ನೆರವಾಗುವ ಸೌಭಾಗ್ಯದಲ್ಲಿ ತಾವೂ ಭಾಗಿಗಳಾಗಬೇಕಂದು, ಸ್ವಂತ ಇಷ್ಟದಿಂದ ನಮ್ಮನ್ನು ಅವರು ಬಹುವಾಗಿ ಬೇಡಿಕೊಂಡರು.",
			"5": "<sup>5</sup> ಇದನ್ನು, ನಾವು ನಿರೀಕ್ಷಿಸಲೇ ಇಲ್ಲ! ಮೊಟ್ಟಮೊದಲು ಅವರು ತಮ್ಮನ್ನೇ ಪ್ರಭುವಿಗೆ ಸಮರ್ಪಿಸಿದರು. ಅನಂತರ ದೈವಚಿತ್ತಕ್ಕೆ ಅನುಗುಣವಾಗಿ ನಮಗೂ ತಮ್ಮನ್ನು ಒಪ್ಪಿಸಿಕೊಟ್ಟರು.",
			"6": "<sup>6</sup> ಇಂಥ ಪ್ರೀತಿಮಯ ಸೇವಾಕಾರ್ಯವನ್ನು ನಿಮ್ಮಲ್ಲಿ ಪ್ರಾರಂಭಿಸಿದ ತೀತನು ಅದನ್ನು ಮುಂದುವರಿಸಿ, ಪೂರ್ಣಗೊಳಿಸಬೇಕೆಂದು ನಾವು ಆತನನ್ನು ಕೇಳಿಕೊಂಡಿದ್ದೇವೆ.",
			"7": "<sup>7</sup> ವಿಶ್ವಾಸ, ವಾಕ್ಚಾತುರ್ಯ, ಜ್ಞಾನ, ಶ್ರದ್ಧೆ, ನಮ್ಮ ಬಗ್ಗೆ ನಿಮಗಿರುವ ಪ್ರೀತಿ - ಈ ಎಲ್ಲಾ ವಿಷಯಗಳಲ್ಲೂ ನೀವು ಸಮೃದ್ಧರಾಗಿದ್ದೀರಿ. ಹಾಗೆಯೇ ಈ ಸೇವಾಕಾರ್ಯದಲ್ಲೂ ಸಮೃದ್ಧರಾಗಿರಿ.",
			"8": "<sup>8</sup> ಇದನ್ನು ನಾನು ಆಜ್ಞಾರೂಪವಾಗಿ ಹೇಳುತ್ತಿಲ್ಲ. ಬೇರೆಯವರಿಗೆ ಈ ಸೇವಾಕಾರ್ಯದಲ್ಲಿ ಇರುವ ಶ್ರದ್ಧೆಯನ್ನು ಆದರ್ಶವಾಗಿ ಹಿಡಿದು ನಿಮ್ಮ ಪ್ರೀತಿ ಎಷ್ಟು ಯಥಾರ್ಥವಾದುದೆಂದು ಕಂಡುಕೊಳ್ಳುವುದಕ್ಕಾಗಿ ಇದನ್ನು ಹೇಳುತ್ತಿದ್ದೇನೆ.",
			"9": "<sup>9</sup> ಪ್ರಭು ಯೇಸುಕ್ರಿಸ್ತರ ಕೃಪಾಶಕ್ತಿಯನ್ನು ನೀವು ಬಲ್ಲಿರಿ. ಅವರು ತಮ್ಮ ಬಡತನದಿಂದ ನಿಮ್ಮನ್ನು ಶ್ರೀಮಂತವಾಗಿಸಲೆಂದು, ತಾವು ಶ್ರೀಮಂತರಾಗಿದ್ದರೂ ನಿಮಗೋಸ್ಕರ ಬಡವರಾದರು.",
			"10": "<sup>10</sup> ಈ ವಿಷಯದಲ್ಲಿ ನನ್ನ ಸಲಹೆ ಏನೆಂದರೆ : ಒಂದು ವರ್ಷಕ್ಕೆ ಹಿಂದೆ ನೀವು ಪ್ರಾರಂಭಿಸಿದ ಈ ಸೇವಾಕಾರ್ಯವನ್ನು ಪೂರೈಸುವುದು ಲೇಸು. ಈ ಕಾರ್ಯವನ್ನು ಮೊದಲು ಆರಂಭಿಸಿದವರು ನೀವೇ. ಆರಂಭಿಸಬೇಕೆಂದು ಸೂಚಿಸಿದವರೂ ನೀವೇ.",
			"11": "<sup>11</sup> ಆದ್ದರಿಂದ ಆರಂಭಿಸುವುದರಲ್ಲಿ ಎಷ್ಟು ಆಸಕ್ತರಾಗಿದ್ದಿರೋ, ಅಷ್ಟೇ ಆಸಕ್ತಿಯಿಂದ ನಿಮ್ಮ ನಿಮ್ಮ ಶಕ್ತಿಗನುಸಾರವಾಗಿ ನೀಡಿ ಈ ಯೋಜನೆಯನ್ನು ಪೂರೈಸಿರಿ.",
			"12": "<sup>12</sup> ಒಬ್ಬನು ಕೊಡುವುದಕ್ಕೆ ಮನಸ್ಸುಳ್ಳವನಾಗಿದ್ದರೆ, ಅವನು ತನ್ನಲ್ಲಿ ಇರುವುದಕ್ಕೆ ತಕ್ಕಂತೆ ಕೊಟ್ಟರೆ ಸಾಕು. ನಿಮ್ಮಲ್ಲಿ ಇಲ್ಲದ್ದನ್ನು ದೇವರು ಬಯಸುವುದಿಲ್ಲ.",
			"13": "<sup>13</sup> ನಿಮ್ಮ ಕೊಡುಗೆಯಿಂದ ಇತರರ ಕಷ್ಟ ಪರಿಹಾರವಾಗಬೇಕು; ಅದರಿಂದ ನಿಮಗೆ ನಷ್ಟವುಂಟಾಗಬೇಕು ಎಂದು ಸಮಾನತೆ ಇರಬೇಕು.",
			"14": "<sup>14</sup> ಈ ಕಾಲದಲ್ಲಿ ನಿಮಗೆ ಸಮೃದ್ಧಿಯಾಗಿರುವುದರಿಂದ ಅವರ ಕೊರತೆಯನ್ನು ನೀವು ನೀಗಿಸಬೇಕು. ಮುಂದಕ್ಕೆ ಅವರು ಸಮೃದ್ಧಿಯಿಂದಿರುವಾಗ, ನಿಮ್ಮ ಕೊರತೆಯನ್ನು ಅವರು ನೀಗಿಸುವರು. ಹೀಗೆ ನಿಮ್ಮಲ್ಲಿ ಸಮಾನತೆ ಉಂಟಾಗುವುದು.",
			"15": "<sup>15</sup> :ಅತಿಯಾಗಿ ಶೇಖರಿಸಿದವನಿಗೆ ಅಧಿಕವಾಗಲಿಲ್ಲ ಮಿತಿಯಾಗಿ ಶೇಖರಿಸಿದವನಿಗೆ ಕೊರತೆಯಾಗಲಿಲ್ಲ.” ಎಂದು ಪವಿತ್ರಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿದೆ.",
			"16": "<sup>16</sup> ನಿಮ್ಮ ಮೇಲೆ ನನಗಿರುವ ಅದೇ ಅಕ್ಕರೆಯನ್ನು ತೀತನಲ್ಲೂ ಮೂಡಿಸಿದ ದೇವರಿಗೆ ಕೃತಜ್ಞತೆ ಸಲ್ಲಬೇಕು.",
			"17": "<sup>17</sup> ನಿಮ್ಮ ಬಳಿಗೆ ಹೋಗಬೇಕೆಂದು ಕೇಳಿಕೊಂಡಾಗ ಆತನು ಕೂಡಲೇ ಸಮ್ಮತಿಸಿದನು. ಅಷ್ಟೇ ಅಲ್ಲ, ನಿಮಗೆ ನೆರವಾಗಲು, ನಿಮ್ಮ ಬಳಿಗೆ ಬರಲು ಆತನೇ ಸ್ವಂತ ಇಷ್ಟದಿಂದ ಹೊರಟಿದ್ದಾನೆ.",
			"18": "<sup>18</sup> ಆತನ ಸಂಗಡ ಇನ್ನೊಬ್ಬ ಸಹೋದರನನ್ನು ಕಳುಹಿಸುತ್ತಿದ್ದೇವೆ. ಈತನು ಶುಭಸಂದೇಶವನ್ನು ಸಾರುವುದರಲ್ಲಿ ಎಲ್ಲ ಸಭೆಗಳಿಂದ ಪ್ರಶಂಸೆ ಪಡೆದಿದ್ದಾನೆ.",
			"19": "<sup>19</sup> ಇದಷ್ಟೇ ಅಲ್ಲದೆ, ಪ್ರಭುವಿನ ಮಹಿಮೆ ಬೆಳಗುವಂತೆಯೂ ಇತರರಿಗೆ ನೆರವಾಗಬೇಕೆಂಬ ಸದುದ್ದೇಶದಿಂದಲೂ ನಾವು ಕೈಗೊಂಡಿರುವ ಸೇವಾಕಾರ್ಯದಲ್ಲಿ ನಮ್ಮ ಸಂಗಡ ಪ್ರಯಾಣಮಾಡಲೆಂದು ಸಭೆಯವರು ಈತನನ್ನು ನೇಮಕಮಾಡಿದ್ದಾರೆ.",
			"20": "<sup>20</sup> ನಮ್ಮ ವಶಕ್ಕೆ ಒಪ್ಪಿಸಲಾಗಿರುವ ಈ ಉದಾರ ಕೊಡುಗೆಯ ವಿತರಣೆಯಲ್ಲಿ ಯಾರೂ ನಮ್ಮ ಮೇಲೆ ತಪ್ಪುಹೊರಿಸಲು ಆಸ್ಪದವಿರಕೂಡದು.",
			"21": "<sup>21</sup> ಪ್ರಭುವಿನ ದೃಷ್ಟಿಯಲ್ಲಿ ಮಾತ್ರವಲ್ಲ.ಮಾನವರ ದೃಷ್ಟಿಯಲ್ಲೂ ಯೋಗ್ಯವಾದುದನ್ನು ಮಾಡುವುದೇ ನಮ್ಮ ಉದ್ದೇಶ.",
			"22": "<sup>22</sup> ಇವರಿಬ್ಬರ ಸಂಗಡ ಮತ್ತೊಬ್ಬ ಸಹೋದರನನ್ನೂ ಕಳುಹಿಸುತ್ತಿದ್ದೇವೆ. ಈತನು ಆಸಕ್ತಿ ಉಳ್ಳವನೆಂದು ಅನುಭವದಿಂದ ಕಂಡುಕೊಂಡಿದ್ದೇವೆ. ನಿಮ್ಮಲ್ಲಿ ಪೂರ್ಣ ಭರವಸೆಯಿಟ್ಟು ನಿಮಗೆ ನೆರವಾಗಲು ಆತನು ಹೆಚ್ಚು ಉತ್ಸುಕನಾಗಿದ್ದಾನೆ.",
			"23": "<sup>23</sup> ತೀತನ ವಿಷಯವಾಗಿ ಹೇಳುವುದಾದರೆ, ಆತನು ನನ್ನ ಸಹೋದ್ಯೋಗಿ; ನಿಮ್ಮ ಸೇವೆಯಲ್ಲಿ ನನ್ನ ಸಹಕಾರಿ, ಆತನ ಸಂಗಡ ಬರುತ್ತಿರುವ ಇತರ ಸಹೋದರರು ಸಭೆಗಳ ಪ್ರತಿನಿಧಿಗಳು. ಕ್ರಿಸ್ತಯೇಸುವಿನ ಮಹಿಮೆಯನ್ನು ಬೆಳಗುವಂಥವರು ಅವರು.",
			"24": "<sup>24</sup> ಆದ್ದರಿಂದ ಇವರನ್ನು ಪ್ರೀತಿಯಿಂದ ನೋಡಿಕೊಳ್ಳಿ. ನಾವು ನಿಮ್ಮನ್ನು ಹೊಗಳಿದ್ದಕ್ಕೆ ತಕ್ಕಂತೆ ನಡೆದುಕೊಂಡು ಸಭೆಗಳಿಗೆ ನಿಮ್ಮ ಯೋಗ್ಯತೆಯನ್ನು ಮನದಟ್ಟುಮಾಡಿಕೊಡಿರಿ."
		},
		"9": {
			"1": "<sup>1</sup> ದೇವಜನರಿಗೆ ಸಲ್ಲಿಸಬೇಕಾದ ಈ ಸೇವಾಕಾರ್ಯವನ್ನು ಕುರಿತು ನಾನು ನಿಮಗೆ ಬರೆಯುವ ಅವಶ್ಯಕತೆಯಿಲ್ಲ.",
			"2": "<sup>2</sup> ನೆರವು ನೀಡಲು ಸಿದ್ಧರಿದ್ದೀರೆಂದು ನನಗೆ ಚೆನ್ನಾಗಿ ಗೊತ್ತಿದೆ. “ಅಖಾಯದ ಸಹೋದರರು ಕಳೆದ ವರ್ಷದಿಂದಲೂ ನೆರವು ನೀಡಲು ಏರ್ಪಾಡುಮಾಡುತ್ತಿರುವರು,” ಎಂದು ಮಕೆದೋನಿಯರ ಮುಂದೆ ನಾನು ನಿಮ್ಮನ್ನು ಹೊಗಳಿದ್ದೇನೆ. ನಿಮ್ಮ ಉತ್ಸಾಹ ಅವರಲ್ಲಿ ಅನೇಕರನ್ನು ಹುರಿದುಂಬಿಸಿದೆ.",
			"3": "<sup>3</sup> ಆದರೂ ನಮ್ಮ ಹೊಗಳಿಕೆ ಸುಳ್ಳಾಗಬಾರದೆಂದು ನಾನು ಈ ಸಹೋದರರನ್ನು ಮುಂಚಿತವಾಗಿ ಕಳುಹಿಸುತ್ತಿದ್ದೇನೆ.",
			"4": "<sup>4</sup> ನಾನು ನಿಮಗೆ ಹೇಳಿರುವಂತೆ ನಿಮ್ಮ ಏರ್ಪಾಡೆಲ್ಲಾ ಮುಗಿದಿರಬೇಕು. ಇಲ್ಲದಿದ್ದರೆ ನಾನು ಬರುವಾಗ, ನನ್ನ ಜೊತೆಯಲ್ಲಿಯೇ ಮಕೆದೋನಿಯದ ಸಹೋದರರೂ ಬಂದು, ನೀವು ಸಿದ್ಧರಿಲ್ಲದೆ ಇರುವುದನ್ನು ಕಂಡರೆ ನೀವು ತಲೆತಗ್ಗಿಸಬೇಕಾಗುತ್ತದೆ. ನಿಮ್ಮಲ್ಲಿ ನಂಬಿಕೆ ಇಟ್ಟದ್ದಕ್ಕಾಗಿ ನಾನೂ ತಲೆ ತಗ್ಗಿಸಬೇಕಾಗುತ್ತದೆ.",
			"5": "<sup>5</sup> ಆದ್ದರಿಂದಲೇ, ನೀವು ವಾಗ್ದಾನಮಾಡಿದ ಕೊಡುಗೆಯನ್ನು ಸಂಗ್ರಹಿಸುವ ಏರ್ಪಾಡನ್ನು ಮಾಡುವಂತೆ ಈ ಸಹೋದರರನ್ನು ನಮಗೆ ಮುಂಚಿತವಾಗಿಯೇ ನಿಮ್ಮ ಬಳಿಗೆ ಕಳುಹಿಸುವುದು ಉಚಿತವೆಂದು ನನಗೆ ತೋಚಿತು. ಹೀಗೆ ಈ ಕೊಡುಗೆ ಬಲಾತ್ಕಾರವಾದ ವಸೂಲಿ ಆಗಿರದೆ ಮನಃಪೂರ್ವಕವಾಗಿ ಕೊಟ್ಟ ನಿಧಿ ಆಗಿರುತ್ತದೆ.",
			"6": "<sup>6</sup> ವಿರಳವಾಗಿ ಬಿತ್ತುವವನು ವಿರಳವಾಗಿ ಕೊಯ್ಯುತ್ತಾನೆ ಹೇರಳವಾಗಿ ಬಿತ್ತುವವನು ಹೇರಳವಾಗಿ ಕೊಯ್ಯುತ್ತಾನೆ. ಇದು ನಿಮಗೆ ತಿಳಿದಿರಲಿ",
			"7": "<sup>7</sup> ಪ್ರತಿಯೊಬ್ಬನೂ ತನ್ನ ಮನಸ್ಸಿನಲ್ಲಿ ನಿಶ್ಚಯಿಸಿಕೊಂಡಷ್ಟು ನೀಡಲಿ. ಒಲ್ಲದ ಮನಸ್ಸಿನಿಂದಾಗಲಿ ಬಲಾತ್ಕಾರದಿಂದಾಗಲಿ ಕೊಡುವುದು ಬೇಡ. ನಗುನಗುತ್ತಾ ನೀಡುವಾತನನ್ನು ದೇವರು ಪ್ರೀತಿಸುತ್ತಾರೆ.",
			"8": "<sup>8</sup> ಸಕಲ ವಿಧವಾದ ವರದಾನಗಳನ್ನು ನಿಮಗೆ ಯಥೇಚ್ಛವಾಗಿ ನೀಡಬಲ್ಲ ಶಕ್ತಿ ದೇವರಿಗಿದೆ. ನೀವು ಸದಾ ಸಮೃದ್ಧಿಯಿಂದಿರುವಿರಿ. ಹೀಗೆ ಸಕಲ ಸತ್ಕಾರ್ಯಗಳಿಗೆ ಹೃತ್ಪೂರ್ವಕವಾಗಿ ನೆರವಾಗುವಿರಿ.",
			"9": "<sup>9</sup> “ದೀನದಲಿತರಿಗೆ ಧಾರಾಳವಾಗಿ ನೀಡುವನು; ಅನವರತ ಇರುವುದು ಅವನ ದಾನಧರ್ಮದ ಫಲವು,” ಎಂದು ಪವಿತ್ರಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿದೆ.",
			"10": "<sup>10</sup> ಬಿತ್ತುವವನಿಗೆ ಬೀಜವನ್ನೂ ಉಣ್ಣುವವನಿಗೆ ಊಟವನ್ನೂ ಒದಗಿಸುವ ದೇವರು, ಬಿತ್ತನೆಗೆ ಬೇಕಾದ ಬೀಜವನ್ನು ನಿಮಗೂ ಕೊಟ್ಟು ಅದನ್ನು ಬೆಳೆಸುವರು. ನಿಮ್ಮ ದಾನಧರ್ಮದ ಫಲಗಳು ಸಮೃದ್ಧಿಗೊಳ್ಳುವಂತೆ ಮಾಡುವರು.",
			"11": "<sup>11</sup> ನೀವು ಸದಾ ಉದಾರಿಗಳಾಗಿರುವಂತೆ ನಿಮ್ಮನ್ನು ಎಲ್ಲಾ ವಿಧದಲ್ಲೂ ಸಿರಿವಂತರನ್ನಾಗಿ ಮಾಡುವರು. ನಿಮ್ಮ ಕೊಡುಗೆ ನಮ್ಮ ಸೇವೆಯ ಮೂಲಕ ಬೇರೆಯವರಿಗೆ ತಲುಪಿ, ಅವರು ದೇವರನ್ನು ಕೃತಜ್ಞತೆಯಿಂದ ಸ್ಮರಿಸುವಂತೆ ಮಾಡುವುದು.",
			"12": "<sup>12</sup> ಹೀಗೆ ನಿಮ್ಮ ಕೊಡುಗೆ ದೇವಜನರ ಕೊರತೆಗಳನ್ನು ನೀಗಿಸುವುದಲ್ಲದೆ ಅನೇಕರು ದೇವರಿಗೆ ಸ್ತೋತ್ರ ಸಲ್ಲಿಸುವಂತೆ ಮಾಡುವುದು.",
			"13": "<sup>13</sup> ಕ್ರಿಸ್ತಯೇಸುವಿನ ಶುಭಸಂದೇಶದ ಅಂಗೀಕಾರದಿಂದ ನೀವು ಎಷ್ಟು ನಿಷ್ಠಾವಂತ ಸೇವಾಸಕ್ತರಾಗಿದ್ದೀರಿ, ತಮಗೂ ಇತರರಿಗೂ ಎಷ್ಟು ಉದಾರಿಗಳಾಗಿದ್ದೀರಿ ಎಂದು ತಿಳಿದು ಅನೇಕರು ದೇವರನ್ನು ಕೊಂಡಾಡುವರು.",
			"14": "<sup>14</sup> ದೇವರು ನಿಮಗೆ ಅನುಗ್ರಹಿಸಿರುವ ಅತಿಶಯವಾದ ವರಕ್ಕಾಗಿ ಅವರು ಪ್ರೀತಿವಾತ್ಸಲ್ಯದಿಂದ ನಿಮಗೋಸ್ಕರ ಪ್ರಾರ್ಥಿಸುವರು.",
			"15": "<sup>15</sup> ವರ್ಣಿಸಲು ಅಸಾಧ್ಯವಾದ ದೇವರ ಕೃಪಾವರಕ್ಕಾಗಿ ಅವರಿಗೆ ಸ್ತುತಿಸ್ತೋತ್ರ ಸಲ್ಲಲಿ!"
		},
		"10": {
			"1": "<sup>1</sup> “ಪೌಲನು ನಮ್ಮ ಹತ್ತಿರವಿರುವಾಗ ಮೆದುವಾಗಿಯೂ ದೂರವಿರುವಾಗ ಕಠಿಣನಾಗಿಯೂ ವರ್ತಿಸುತ್ತಾನೆ,” ಎಂದು ನಿಮ್ಮಲ್ಲಿ ಕೆಲವರು ಮಾತನಾಡಿಕೊಳ್ಳುವುದು ನನಗೆ ತಿಳಿದುಬಂದಿದೆ. ವಿನಯಶೀಲರೂ ದೀನದಯಾಳುವೂ ಆದ ಕ್ರಿಸ್ತಯೇಸುವಿನ ಹೆಸರಿನಲ್ಲಿ ನಾನು ಕೇಳಿಕೊಳ್ಳುವುದೇನೆಂದರೆ:",
			"2": "<sup>2</sup> ನಾನು ನಿಮ್ಮ ಬಳಿಗೆ ಬಂದಾಗ, ಕಠಿಣನಾಗಿ ವರ್ತಿಸಲು ಅವಕಾಶ ಕೊಡಬೇಡಿ. ನಾವು ಪ್ರಾಪಂಚಿಕ ರೀತಿನೀತಿಗಳಿಗೆ ಅನುಗುಣವಾಗಿ ನಡೆಯುತ್ತಿದ್ದೇವೆಂದು ದೂಷಿಸುವವರ ವಿರುದ್ಧ ಖಂಡಿತವಾಗಿಯೂ ಕಠಿಣನಾಗಿಯೇ ವರ್ತಿಸಬೇಕೆಂದಿದ್ದೇನೆ.",
			"3": "<sup>3</sup> ನಾವು ಪ್ರಪಂಚದಲ್ಲಿ ಬಾಳುತ್ತಿದ್ದರೂ ನಮ್ಮ ಹೋರಾಟ ಪ್ರಾಪಂಚಿಕ ಉದ್ದೇಶದಿಂದ ಕೂಡಿದ್ದಲ್ಲ.",
			"4": "<sup>4</sup> ನಾವು ಬಳಸುವ ಆಯುಧಗಳು ಮಾನವ ನಿರ್ಮಿತವಾದುವಲ್ಲ, ದೇವರ ಶಕ್ತಿಯಿಂದ ಕೂಡಿದುವು; ಕೋಟೆಕೊತ್ತಲಗಳನ್ನು ಕೆಡವಬಲ್ಲವು. ಅವುಗಳಿಂದ ನಾವು ಎಲ್ಲ ಕುತರ್ಕಗಳನ್ನು ನಿರ್ಮೂಲಮಾಡಬಲ್ಲೆವು.",
			"5": "<sup>5</sup> ದೈವಜ್ಞಾನಕ್ಕೆ ವಿರುದ್ಧ ತಲೆ ಎತ್ತುವ ದುರ್ಗಗಳನ್ನು ಧ್ವಂಸಮಾಡಬಲ್ಲೆವು. ಮಾನವನ ಪ್ರತಿಯೊಂದು ಆಲೋಚನೆಗಳನ್ನೂ ಬಂಧಿಸಬಲ್ಲೆವು.",
			"6": "<sup>6</sup> ನೀವು ಸಂಪೂರ್ಣರಾಗಿ ವಿಧೇಯರಾದ ಮೇಲೆ ಪ್ರತಿಯೊಂದು ಅವಿಧೇಯ ಕೃತ್ಯವನ್ನು ಶಿಕ್ಷಿಸಲು ನಾವು ಹಿಂಜರಿಯೆವು.",
			"7": "<sup>7</sup> ನೀವು ಹೊರಗಿನ ತೋರಿಕೆಗಳನ್ನು ಗಮನಿಸುವಂತವರು. ಯಾರಾದರೂ ತಾನು ಕ್ರಿಸ್ತಯೇಸುವಿಗೆ ಸೇರಿದವನು ಎಂದು ದೃಢವಾಗಿ ನಂಬುವುದಾದರೆ, ಅವನು ಮತ್ತೆ ತನ್ನನ್ನೇ ಪರೀಕ್ಷಿಸಿ ನೋಡಲಿ. ಏಕೆಂದರೆ, ಅವನಂತೆಯೇ ನಾವೂ ಕ್ರಿಸ್ತಯೇಸುವಿಗೆ ಸೇರಿದವರು.",
			"8": "<sup>8</sup> ಪ್ರಭು ನಮಗೆ ಅಧಿಕಾರವನ್ನು ಕೊಟ್ಟಿರುವುದು ನಿಮ್ಮನ್ನು ಕೆಡವಿ ಹಾಳುಮಾಡುವುದಕ್ಕಲ್ಲ, ಕಟ್ಟಿ ಎಬ್ಬಿಸುವುದಕ್ಕಾಗಿ. ಈ ಅಧಿಕಾರವನ್ನು ಕುರಿತು ಒಂದು ವೇಳೆ, ನಾವು ಕೊಂಚ ಹೆಚ್ಚಾಗಿ ಹೊಗಳಿಕೊಂಡಿದ್ದರೂ ಅದಕ್ಕಾಗಿ ಸಂಕೋಚಪಡುವುದಿಲ್ಲ.",
			"9": "<sup>9</sup> ನಾನು ನನ್ನ ಪತ್ರಗಳ ಮೂಲಕ ನಿಮ್ಮನ್ನು ಬೆದರಿಸುತ್ತಿದ್ದೇನೆ ಎಂದು ಯಾರೂ ಭಾವಿಸಬಾರದು.",
			"10": "<sup>10</sup> “ಪೌಲನ ಪತ್ರಗಳು ಪ್ರಬಲವಾದುವು, ತೀಕ್ಷ್ಣವಾದುವು; ಆದರೆ ಆತನು ಸ್ವತಃ ನಮ್ಮಲ್ಲಿಗೆ ಬಂದಾಗ ಆತನು ದುರ್ಬಲನು ಹಾಗೂ ಆತನ ಮಾತು ಸಹ ಕೆಲಸಕ್ಕೆ ಬಾರದವು,” ಎಂದು ಕೆಲವರು ಹೇಳುತ್ತಿದ್ದಾರಂತೆ.",
			"11": "<sup>11</sup> ದೂರದಲ್ಲಿದ್ದಾಗ ಪತ್ರದ ಮೂಲಕ ನಾವು ಹೇಗೆ ಕಾಣಿಸಿಕೊಳ್ಳುತ್ತೇವೋ ಹಾಗೆಯೇ ಹತ್ತಿರ ಬಂದಾಗಲೂ ನಡೆದುಕೊಳ್ಳುತ್ತೇವೆ ಎಂಬುದನ್ನು ಆ ವ್ಯಕ್ತಿಗಳು ತಿಳಿದುಕೊಳ್ಳಲಿ.",
			"12": "<sup>12</sup> ಕೆಲವರು ತಮ್ಮನ್ನು ತಾವೇ ಹೊಗಳಿಕೊಳ್ಳುತ್ತಾರೆ. ಅವರಂತೆ ಕೊಚ್ಚಿಕೊಳ್ಳುವುದಕ್ಕೂ ಅವರೊಡನೆ ಹೋಲಿಸಿಕೊಳ್ಳುವುದಕ್ಕೂ ನಾವು ಯತ್ನಿಸುವುದಿಲ್ಲ. ಅವರಿಗೆ ತಾವು ಹಾಕಿಕೊಂಡದ್ದೇ ಅಳತೆಗೋಲು. ತಮಗೆ ತಾವೇ ತಾಳೆಹಾಕಿಕೊಳ್ಳುವಂಥ ಅವಿವೇಕಿಗಳು.",
			"13": "<sup>13</sup> ನಾವಾದರೋ ಹಾಗೆ ಮಿತಿಮೀರಿ ಹೊಗಳಿಕೊಳ್ಳುವುದಿಲ್ಲ. ನಮಗೆ ದೇವರು ತಾವೇ ನಿಯಮಿಸಿದ ಕಾರ್ಯಕ್ಷೇತ್ರದ ಸರಹದ್ದನ್ನು ನಾವು ಮೀರುವುದಿಲ್ಲ. ಈ ಸರಹದ್ದಿಗೆ ನೀವೂ ಒಳಪಟ್ಟಿದ್ದೀರಿ.",
			"14": "<sup>14</sup> ಇತರರಿಗಿಂತಲೂ ಮೊದಲೇ ನಿಮಗೆ ಯೇಸುಕ್ರಿಸ್ತರ ಶುಭಸಂದೇಶವನ್ನು ತಂದವರು ನಾವು. ಹೀಗಿರುವಲ್ಲಿ ನಿಮ್ಮ ವಿಷಯದಲ್ಲಿ ನಾವು ಈಗ ಹೊಗಳಿಕೊಳ್ಳುವಾಗ ನಮ್ಮ ಸರಹದ್ದನ್ನು ಉಲ್ಲಂಘಿಸುತ್ತಿಲ್ಲ.",
			"15": "<sup>15</sup> ಇತರರ ಪ್ರಯಾಸದ ಫಲಗಳನ್ನು ನಮ್ಮದೆಂದುಕೊಂಡು ಎಲ್ಲೆಮೀರಿ ನಾವು ಹೊಗಳಿಕೊಳ್ಳುತ್ತಿಲ್ಲ. ಆದರೆ ನಿಮ್ಮ ವಿಶ್ವಾಸ ವೃದ್ಧಿಯಾದಂತೆಲ್ಲಾ ನಮ್ಮ ಕಾರ್ಯಕ್ಷೇತ್ರ ಇನ್ನೂ ವಿಸ್ತಾರವಾಗಬೇಕು.",
			"16": "<sup>16</sup> ಮತ್ತೊಬ್ಬರ ಕ್ಷೇತ್ರದ ಸೇವಾಫಲಗಳನ್ನು ನಮ್ಮದೆಂದು ಹೊಗಳಿಕೊಳ್ಳದೆ, ನಿಮ್ಮ ಗಡಿಯಾಚೆಯ ನಾಡುಗಳಲ್ಲೂ ಶುಭಸಂದೇಶವನ್ನು ಸಾರಬೇಕೆಂಬುದೇ ನಮ್ಮ ಆಶಯ.",
			"17": "<sup>17</sup> “ಹೆಚ್ಚಳಪಡುವವನು ಪ್ರಭುವಿನಲ್ಲೇ ಹೆಚ್ಚಳಪಡಲಿ,” ಎಂದು ಪವಿತ್ರಗ್ರಂಥದಲ್ಲಿ ಲಿಖಿತವಾಗಿದೆ.",
			"18": "<sup>18</sup> ಅಂತೆಯೇ, ತನ್ನನ್ನು ತಾನೇ ಹೊಗಳಿಕೊಳ್ಳುವವನು ಅಲ್ಲ, ಪ್ರಭು ಯಾರನ್ನು ಹೊಗಳುತ್ತಾರೋ, ಅವನೇ ಧನ್ಯನು."
		},
		"11": {
			"1": "<sup>1</sup> ನನ್ನನ್ನು ನಾನೇ ಹೊಗಳಿಕೊಳ್ಳುವ ಹುಚ್ಚುತನವನ್ನು ನೀವು ಸ್ವಲ್ಪ ಸಹಿಸಿಕೊಳ್ಳಬೇಕೆಂದು ಕೇಳಿಕೊಳ್ಳುತ್ತೇನೆ.",
			"2": "<sup>2</sup> ನಿಮ್ಮ ಬಗ್ಗೆ ದೇವರಿಗಿರುವ ಪ್ರೇಮಾಸೂಯೆ ನನಗೂ ಇದೆ. ನಿಷ್ಕಳಂಕ ಕನ್ನಿಕೆಯೊಬ್ಬಳನ್ನು ಅವಳ ಏಕೈಕ ವರನಿಗೆ ನಿಶ್ಚಯಿಸುವ ರೀತಿಯಲ್ಲಿ ನಾನು ಕ್ರಿಸ್ತಯೇಸುವೆಂಬ ಏಕೈಕ ಪುರುಷನಿಗೆ ನಿಮ್ಮನ್ನು ನಿಶ್ಚಯಮಾಡಿದ್ದೇನೆ.",
			"3": "<sup>3</sup> ಆದರೂ ಹವ್ವಳು ಸರ್ಪದ ಕುಯುಕ್ತಿಗೆ ಸಿಕ್ಕಿ ಮೋಸಹೋದಂತೆ ನೀವೂ ಕೂಡ ಕ್ರಿಸ್ತಯೇಸುವಿನ ವಿಷಯದಲ್ಲಿ ಇರಬೇಕಾದ ಶುದ್ಧ ಪಾತಿವ್ರತ್ಯವನ್ನು ಕಳೆದುಕೊಂಡು ಕೆಟ್ಟುಹೋದೀರಿ ಎಂಬ ಭಯ ನನಗುಂಟು.",
			"4": "<sup>4</sup> ಏಕೆಂದರೆ, ಯಾರಾದರೂ ಬಂದು, ನಾವು ಬೋಧಿಸದೆ ಇರುವ ಬೇರೊಬ್ಬ ಯೇಸುವನ್ನು ಬೋಧಿಸಿದರೆ ನೀವು ಸ್ವೀಕರಿಸುತ್ತೀರಿ. ನೀವು ಹೊಂದಿರುವ ಪವಿತ್ರಾತ್ಮರಿಗೆ ಬದಲಾಗಿ ಬೇರೊಬ್ಬ ಆತ್ಮವನ್ನು ಪಡೆಯುವಂತೆ ಮಾಡಿದರೆ, ನೀವು ಸ್ವಾಗತಿಸುತ್ತೀರಿ. ನಿಮಗೆ ಈಗಾಗಲೇ ದೊರಕಿರುವ ಶುಭಸಂದೇಶವಲ್ಲದೆ ಬೇರೊಂದು ಸಂದೇಶವನ್ನು ನಿಮಗೆ ಸಾರಿದರೆ, ನೀವು ಒಮ್ಮೆಗೇ ಒಪ್ಪಿಕೊಳ್ಳುತ್ತೀರಿ. ಇದು ನಿಜಕ್ಕೂ ಆಶ್ಚರ್ಯವೇ ಸರಿ.",
			"5": "<sup>5</sup> “ಮಹಾ ಪ್ರೇಷಿತರು” ಎನಿಸಿಕೊಳ್ಳುವ ಆ ಜನರಿಗಿಂತ ನಾನು ಯಾವುದರಲ್ಲೂ ಕಡಿಮೆಯಿಲ್ಲ.",
			"6": "<sup>6</sup> ಮಾತಿನಲ್ಲಿ ಚತುರನಲ್ಲದಿದ್ದರೂ ಜ್ಞಾನದಲ್ಲಿ ಅಲ್ಪನಲ್ಲ. ಇದನ್ನು ನಿಮಗೆ ಎಲ್ಲಾ ಸಮಯಸಂದರ್ಭಗಳಲ್ಲೂ ಸ್ಪಷ್ಟಪಡಿಸಿದ್ದೇನೆ.",
			"7": "<sup>7</sup> ನಿಮಗೆ ದೇವರ ಶುಭಸಂದೇಶವನ್ನು ಉಚಿತವಾಗಿಯೇ ಬೋಧಿಸಿದೆನು. ನಿಮ್ಮನ್ನು ಮೇಲಕ್ಕೇರಿಸಲು ನನ್ನನ್ನೇ ತಗ್ಗಸಿಕೊಂಡೆನು. ಹೀಗೆ ಮಾಡಿದ್ದು ತಪ್ಪಾಯಿತೇ?",
			"8": "<sup>8</sup> ನಿಮಗೆ ಸೇವೆಮಾಡುವುದಕ್ಕೋಸ್ಕರ ನಾನು ಬೇರೆ ಸಭೆಗಳಿಂದ ನೆರವುಪಡೆಯುತ್ತಿದ್ದೆ, ನಿಮಗೋಸ್ಕರ ಇತರ ಸಭೆಗಳಿಂದ ಹಣ ವಸೂಲಿಮಾಡುತ್ತಿದ್ದೆ.",
			"9": "<sup>9</sup> ನಾನು ನಿಮ್ಮೊಂದಿಗಿದ್ದಾಗ ನನಗೆ ಹಣದ ಕೊರತೆ ಇದ್ದರೂ ನಿಮ್ಮಲ್ಲಿ ಯಾರಿಗೂ ಹೊರೆಯಾಗಲಿಲ್ಲ. ಮಕೆದೋನಿಯದಿಂದ ಬಂದ ಸಹೋದರರು ನನ್ನ ಕೊರತೆಯನ್ನೆಲ್ಲಾ ನೀಗಿಸಿದರು. ನಿಮಗೆ ಯಾವುದರಲ್ಲೂ ಹೊರೆಯಾಗಬಾರದೆಂದು ಎಚ್ಚರಿಕೆ ವಹಿಸಿದ್ದೆ; ಇನ್ನು ಮುಂದಕ್ಕೂ ಎಚ್ಚರಿಕೆಯಿಂದ ಇರುತ್ತೇನೆ.",
			"10": "<sup>10</sup> ಈ ನನ್ನ ಹೊಗಳಿಕೆಯನ್ನು ಅಖಾಯ ಪ್ರಾಂತ್ಯದ ಯಾರೂ ಅಡಗಿಸುವಂತಿಲ್ಲವೆಂದು ಕ್ರಿಸ್ತಯೇಸುವಿನ ಮುಂದೆ ಸತ್ಯವಾಗಿ ನುಡಿಯುತ್ತೇನೆ.",
			"11": "<sup>11</sup> ನಿಮ್ಮಿಂದ ನಾನು ಏನನ್ನೂ ತೆಗೆದುಕೊಳ್ಳುತ್ತಿಲ್ಲವೇಕೆ? ನಿಮ್ಮ ಮೇಲೆ ಪ್ರೀತಿ ಇಲ್ಲದ್ದರಿಂದಲೋ? ಇಲ್ಲ, ನಿಮ್ಮ ಮೇಲೆ ನಮಗಿರುವ ಪ್ರೀತಿ ಎಷ್ಟೆಂದು ದೇವರೇ ಬಲ್ಲರು.",
			"12": "<sup>12</sup> ಕೆಲವರು ತಾವು ಮಾಡುತ್ತಿರುವ ಕಾರ್ಯ, ನಾವು ಮಾಡುತ್ತಿರುವ ಪ್ರೇಷಿತರ ಕಾರ್ಯಕ್ಕೆ ಸರಿಸಮವೆಂದು ವಾದಿಸಲು ಅವಕಾಶವನ್ನು ಹುಡುಕುತ್ತಿದ್ದಾರೆ. ಅವರಿಗೆ ಅಂಥ ಆಸ್ಪದವನ್ನು ಕೊಡದಂತೆ ನಾನೀಗ ಮಾಡುತ್ತಿರುವುದನ್ನೇ ಮುಂದುವರಿಸಿಕೊಂಡು ಹೋಗುತ್ತೇನೆ.",
			"13": "<sup>13</sup> ಅವರು ಕಪಟ ಪ್ರೇಷಿತರು, ಕಳ್ಳ ಕೆಲಸಗಾರರು, ಕ್ರಿಸ್ತಯೇಸುವಿನ ಪ್ರೇಷಿತರಂತೆ ನಟಿಸುವವರು.",
			"14": "<sup>14</sup> ಇದರಲ್ಲಿ ಆಶ್ಚರ್ಯವೇನೂ ಇಲ್ಲ. ಸೈತಾನನೂ ಸಹ ಜ್ಯೋತಿರ್ಮಯ ದೇವದೂತನಂತೆ ಕಾಣಿಸಿಕೊಳ್ಳಬಲ್ಲನು.",
			"15": "<sup>15</sup> ಹೀಗಿರುವಲ್ಲಿ, ಅವನ ಸೇವಕರೂ ಸಹ ಸತ್ಯಸೇವಕರಂತೆ ವೇಷಧಾರಿಗಳಾದರೆ ಅದರಲ್ಲೇನು ಸೋಜಿಗ? ಅವರ ಕೃತ್ಯಗಳಂತೆಯೇ ಅವರ ಅಂತ್ಯವೂ ಇರುತ್ತದೆ.",
			"16": "<sup>16</sup> ನನ್ನನ್ನು ಯಾರೂ ಹುಚ್ಚನೆಂದು ಭಾವಿಸದಿರಲಿ, ಎಂದು ಮತ್ತೆ ಹೇಳುತ್ತೇನೆ. ಒಂದು ವೇಳೆ ಹಾಗೆ ಭಾವಿಸಿದರೂ ಚಿಂತೆಯಿಲ್ಲ. ನನ್ನನ್ನು ಹುಚ್ಚನಾಗಿಯೇ ಸ್ವೀಕರಿಸಿ. ಆಗ ನಾನು ಆತ್ಮಪ್ರಶಂಸೆ ಮಾಡಿಕೊಳ್ಳಲು ಅಲ್ಪಸ್ವಲ್ಪವಾದರೂ ಆಸ್ಪದವಾಗುತ್ತದೆ.",
			"17": "<sup>17</sup> ನಾನೀಗ ಹೇಳುತ್ತಿರುವುದು ಪ್ರಭುವಿನ ಪ್ರೇರಣೆಯಿಂದಲ್ಲ, ಧೈರ್ಯದಿಂದ ತನ್ನನ್ನೇ ಕೊಚ್ಚಿಕೊಳ್ಳುವ ಒಬ್ಬ ಹುಚ್ಚನಂತೆ ಹೇಳುತ್ತಿದ್ದೇನೆ:",
			"18": "<sup>18</sup> ಅನೇಕರು ಕೇವಲ ಪ್ರಾಪಂಚಿಕ ವಿಷಯಗಳನ್ನು ಕುರಿತು ತಮ್ಮನ್ನೇ ಹೊಗಳಿಕೊಳ್ಳುತ್ತಾರೆ. ಹಾಗೆಯೇ ನಾನೂ ಹೊಗಳಿಕೊಳ್ಳುತ್ತೇನೆ.",
			"19": "<sup>19</sup> ನೀವಾದರೋ ಮಹಾ ಬುದ್ಧಿವಂತರು! ಬುದ್ಧಿಹೀನರನ್ನು ಬಹಳವಾಗಿ ಸಹಿಸಿಕೊಳ್ಳುತ್ತೀರಿ.",
			"20": "<sup>20</sup> ಯಾರಾದರೂ ನಿಮ್ಮನ್ನು ಅಧೀನಪಡಿಸಿಕೊಂಡರೂ ಕಬಳಿಸಿದರೂ ಮರುಳುಗೊಳಿಸಿದರೂ ತುಚ್ಛೀಕರಿಸಿದರೂ ಕೆನ್ನೆಗೆ ಬಿಗಿದರೂ ಎಲ್ಲವನ್ನೂ ಸಹಿಸಿಕೊಳ್ಳುತ್ತೀರಿ.",
			"21": "<sup>21</sup> ಇದನ್ನೆಲ್ಲಾ ಮಾಡುವ ಸಾಹಸ ನಮಗಿಲ್ಲವೆಂದು ಹೇಳಿಕೊಳ್ಳಲು ನನಗೆ ನಾಚಿಕೆಯಾಗುತ್ತದೆ. ಯಾರಿಗಾದರೂ ಯಾರ ವಿಷಯದಲ್ಲಾದರೂ ಹೊಗಳಿಕೊಳ್ಳುವ ಧೈರ್ಯವಿದ್ದರೆ ಅದಕ್ಕಿಂತ ಹೆಚ್ಚಿನ ಧೈರ್ಯ ನನಗಿದೆ. ಇದನ್ನು ನಾನು ಹುಚ್ಚನಂತೆಯೇ ಹೇಳುತ್ತಿದ್ದೇನೆ ಎಂದು ಭಾವಿಸಿಕೊಳ್ಳಿ.",
			"22": "<sup>22</sup> ಅವರು ಹಿಬ್ರಿಯರೋ? ನಾನೂ ಹಿಬ್ರಿಯನೇ. ಅವರು ಇಸ್ರಯೇಲರೋ? ನಾನೂ ಇಸ್ರಯೇಲನೇ. ಅವರು ಅಬ್ರಹಾಮನ ವಂಶದವರೋ? ನಾನೂ ಅದೆ ವಂಶದವನೇ.",
			"23": "<sup>23</sup> ಅವರು ಕ್ರಿಸ್ತಯೇಸುವಿನ ದಾಸರೋ? (ನಾನು ಬುದ್ಧಿ ಕೆಟ್ಟವನಂತೆಯೇ ಮಾತನಾಡುತ್ತಿದ್ದೇನೆ) ಅವರಿಗಿಂತ ನಾನು ದಾಸಾನುದಾಸನು. ಅವರಿಗಿಂತ ಹೆಚ್ಚಾಗಿ ಸೇವೆಮಾಡಿದ್ದೇನೆ; ಅವರಿಗಿಂತ ಹೆಚ್ಚಾಗಿ ಸೆರೆಮನೆಗಳ ವಾಸವನ್ನು ಅನುಭವಿಸಿದ್ದೇನೆ; ಮಿತಿಮೀರಿ ಏಟುಪೆಟ್ಟುಗಳನ್ನು ತಿಂದಿದ್ದೇನೆ; ಅನೇಕ ಸಲ ಸಾವಿನ ದವಡೆಗೆ ಸಿಲುಕಿಕೊಂಡಿದ್ದೇನೆ.",
			"24": "<sup>24</sup> ಐದು ಸಾರಿ ಯೆಹೂದ್ಯರಿಂದ ನನಗೆ ನಲವತ್ತಕ್ಕೆ ಒಂದು ಕಡಿಮೆ ಚಾಟಿ ಏಟುಗಳು ಬಿದ್ದವು.",
			"25": "<sup>25</sup> ಮೂರು ಸಾರಿ ರೋಮನರು ನನ್ನನ್ನು ಛಡಿಗಳಿಂದ ಹೊಡೆಸಿದರು. ಒಮ್ಮೆ ಜನರು ನನ್ನನ್ನು ಕೊಲ್ಲಲೆಂದು ಕಲ್ಲು ತೂರಿದರು. ಮೂರು ಸಲ ನಾನಿದ್ದ ಹಡಗು ಒಡೆದು ನೀರುಪಾಲಾಯಿತು. ಒಂದು ರಾತ್ರಿ, ಒಂದು ಹಗಲು ನಡುಗಡಲಲ್ಲಿ ತೇಲಾಡಿದೆ.",
			"26": "<sup>26</sup> ಪ್ರಭುವಿನ ಸೇವೆಯಲ್ಲಿ ನಾನು ಕೈಗೊಂಡ ಪ್ರಯಾಣಗಳು ಅನೇಕ; ನನಗೆ ಬಂದೊದಗಿದ ಅಪಾಯಗಳೂ ಅನೇಕ; ಪ್ರವಾಹಗಳ ಅಪಾಯ, ಕಳ್ಳಕಾಕರ ಅಪಾಯ, ಸ್ವಜನ ಹಾಗೂ ಅನ್ಯಜನರಿಂದ ಅಪಾಯ, ನಗರಗಳ ಹಾಗೂ ನಿರ್ಜನ‍ಪ್ರದೇಶಗಳ ಅಪಾಯ, ಕಡಲುಗಳ ಅಪಾಯ, ಕಪಟ ಸಹೋದರರಿಂದ ಅಪಾಯ - ಇವೆಲ್ಲಕ್ಕೂ ತುತ್ತಾದೆ.",
			"27": "<sup>27</sup> ಶ್ರಮಪಟ್ಟು ದುಡಿದಿದ್ದೇನೆ, ಎಷ್ಟೋ ಸಾರಿ ನಿದ್ದೆಗೆಟ್ಟಿದ್ದೇನೆ; ಹಸಿವು ನೀರಡಿಕೆಗಳಿಂದ ಬಳಲಿದ್ದೇನೆ; ಅನೇಕ ಸಾರಿ ಊಟಬಟ್ಟೆಯಿಲ್ಲದೆ ಅಲೆದಿದ್ದೇನೆ; ಚಳಿಗಾಳಿಯಲ್ಲಿ ನಡುಗಿದ್ದೇನೆ.",
			"28": "<sup>28</sup> ಇಂಥ ಇನ್ನಿತರ ಕಷ್ಟಸಂಕಟಗಳನ್ನು ಅನುಭವಿಸಿದ್ದೇನೆ. ಅಷ್ಟೇ ಅಲ್ಲದೆ, ಎಲ್ಲ ಸಭೆಗಳ ಹಿತಚಿಂತನೆಯೂ ನನ್ನನ್ನು ಅನುದಿನವೂ ಕಾಡಿಸಿದೆ.",
			"29": "<sup>29</sup> ಒಬ್ಬನು ಬಲಹೀನನಾಗಿದ್ದರೆ, ಅವನೊಡನೆ ನಾನೂ ಬಲಹೀನನೇ. ಒಬ್ಬನು ಪಾಪಕ್ಕೆ ಬಲಿಯಾದರೆ ನಾನೂ ತಾಪದಿಂದ ಕುದಿಯುತ್ತೇನೆ.",
			"30": "<sup>30</sup> ನಾನು ನನ್ನನ್ನೇ ಹೊಗಳಿಕೊಳ್ಳಬೇಕಾಗಿ ಬಂದರೆ, ನನ್ನ ದೌರ್ಬಲ್ಯವನ್ನು ಬಹಿರಂಗಪಡಿಸುವ ವಿಷಯಗಳ ಬಗ್ಗೆ ಹೊಗಳಿಕೊಳ್ಳುತ್ತೇನೆ.",
			"31": "<sup>31</sup> ನಾನು ಹೇಳುತ್ತಿರುವುದು ಸುಳ್ಳಲ್ಲ, ನಮ್ಮ ಪ್ರಭು ಯೇಸುಕ್ರಿಸ್ತರ ತಂದೆಯಾದ ದೇವರೇ ಇದನ್ನು ಬಲ್ಲರು. ಅವರಿಗೆ ಯುಗಯುಗಾಂತರಕ್ಕೂ ಸ್ತುತಿ ಸಲ್ಲಲಿ!",
			"32": "<sup>32</sup> ನಾನು ದಮಸ್ಕಸಿನಲ್ಲಿದ್ದಾಗ ರಾಜ ಅರೇತನ ಕೈಕೆಳಗಿದ್ದ ರಾಜ್ಯಪಾಲನು ನನ್ನನ್ನು ಸೆರೆಹಿಡಿಯುವ ಸಲುವಾಗಿ ಪಟ್ಟಣದ ದ್ವಾರಗಳಿಗೆ ಕಾವಲಿರಿಸಿದ್ದನು.",
			"33": "<sup>33</sup> ಆದರೆ, ಕೆಲವರು ನನ್ನನ್ನು ಗೂಡೆಯಲ್ಲಿ ಕೂರಿಸಿ ಗೋಡೆಯ ಕಿಂಡಿಯೊಂದರ ಮೂಲಕ ಕೆಳಗಿಳಿಸಿದರು. ಹೀಗೆ ನಾನು ಅವನ ಕೈಯಿಂದ ಪಾರಾದೆ."
		},
		"12": {
			"1": "<sup>1</sup> ಹೊಗಳಿಕೊಳ್ಳುವುದು ಹಿತಕರವಲ್ಲ. ಆದರೂ ಹೊಗಳಿಕೊಳ್ಳಬೇಕಾದ್ದು ಅನಿವಾರ್ಯವಾಗಿದೆ. ಆದುದರಿಂದ ಪ್ರಭು ನನಗಿತ್ತ ದರ್ಶನಗಳನ್ನು ಮತ್ತು ಶ್ರುತಪಡಿಸಿದ ಸತ್ಯಗಳನ್ನು ಕುರಿತು ಹೇಳುತ್ತೇನೆ:",
			"2": "<sup>2</sup> ಕ್ರೈಸ್ತನಾದ ಒಬ್ಬ ವ್ಯಕ್ತಿಯನ್ನು ನಾನು ಬಲ್ಲೆ. ಅವನು ಹದಿನಾಲ್ಕು ವರ್ಷಗಳಿಗೆ ಹಿಂದೆ ಮಹೋನ್ನತ ಸ್ವರ್ಗಕ್ಕೆ ಒಯ್ಯಲ್ಪಟ್ಟನು. ದೇಹಸಹಿತನಾಗಿಯೋ ದೇಹರಹಿತನಾಗಿಯೋ ಅದನ್ನು ನಾನರಿಯೆ. ದೇವರೊಬ್ಬರೇ ಬಲ್ಲರು.",
			"3": "<sup>3</sup> ಅವನು ಪರಂಧಾಮಕ್ಕೆ ಒಯ್ಯಲ್ಪಟ್ಟನೆಂಬುದು ನಿಶ್ಚಯ. ದೇಹಸಹಿತವಾಗಿಯೋ ದೇಹರಹಿತವಾಗಿಯೋ ಅದನ್ನು ನಾನರಿಯೆ. ದೇವರೊಬ್ಬರೇ ಬಲ್ಲರು.",
			"4": "<sup>4</sup> ಅಲ್ಲಿ ಅವನಿಗೆ ಕೇಳಿಬಂದ ವಿಷಯಗಳು ಮಾನವನ ವರ್ಣನೆಗೆ ಎಟುಕದವು. ಮಾನವನ ನುಡಿಗೆ ನಿಲುಕದವು.",
			"5": "<sup>5</sup> ಅವನನ್ನು ಕುರಿತು ನಾನು ಹೆಮ್ಮೆಪಡುತ್ತೇನೆ. ಆದರೆ ನನ್ನನ್ನು ಕುರಿತು ನಾನು ಹೊಗಳಿಕೊಳ್ಳುವುದಿಲ್ಲ. ನನ್ನ ದೌರ್ಬಲ್ಯವೇ ನನ್ನ ಹೊಗಳಿಕೆ.",
			"6": "<sup>6</sup> ಹಾಗೇನಾದರೂ ನಾನು ಹೊಗಳಿಕೊಳ್ಳಲು ಆಶಿಸಿದರೆ ಅದೇನು ಹುಚ್ಚುತನವಲ್ಲ. ಏಕೆಂದರೆ, ನಾನು ನುಡಿಯುತ್ತಿರುವುದು ಸತ್ಯವನ್ನೇ. ಆದರೂ ನಾನು ಹಾಗೆ ಹೊಗಳಿಕೊಳ್ಳುವುದಿಲ್ಲ. ಕಾರಣ, ಯಾರೂ ನನ್ನಲ್ಲಿ ಕಾಣುವುದಕ್ಕಿಂತಲೂ ನನ್ನಿಂದ ಕೇಳುವುದಕ್ಕಿಂತಲೂ ಹೆಚ್ಚಾಗಿ ನನ್ನನ್ನು ಪರಿಗಣಿಸಬಾರದು.",
			"7": "<sup>7</sup> ನನಗೆ ತಿಳಿಸಲಾದ ಗಹನ ಸತ್ಯಗಳಿಂದ ನಾನು ಉಬ್ಬಿಹೋಗದ ಹಾಗೆ ನನ್ನ ದೇಹದಲ್ಲಿ ಶೂಲದಂತೆ ನಾಟಿರುವ ಬೇನೆಯೊಂದನ್ನು ನನಗೆ ಕೊಡಲಾಯಿತು. ನಾನು ಅಹಂಕಾರಪಡದ ಹಾಗೆ ಇದು ನನ್ನನ್ನು ತಿವಿತಿವಿದು ಸೈತಾನನ ದೂತನಂತೆ ಕಾಡಿಸುತ್ತಿತ್ತು.",
			"8": "<sup>8</sup> ಈ ಪೀಡೆ ನನ್ನನ್ನು ಬಿಟ್ಟು ತೊಲಗಲೆಂದು ನಾನು ಮೂರು ಸಾರಿ ಪ್ರಭುವಿನಲ್ಲಿ ಬೇಡಿಕೊಂಡೆ.",
			"9": "<sup>9</sup> ಅದಕ್ಕವರು, “ನನ್ನ ಅನುಗ್ರಹವೇ ನಿನಗೆ ಸಾಕು. ನನ್ನ ಶಕ್ತಿ ಪರಿಪಕ್ವವಾಗುವುದು ನಿಶ್ಯಕ್ತಿಯಲ್ಲಿಯೇ,” ಎಂದು ಹೇಳಿದರು. ಕ್ರಿಸ್ತಯೇಸುವಿನ ಶಕ್ತಿ ನನ್ನಲ್ಲಿ ನೆಲಸುವಂತೆ ನನ್ನ ನಿಶ್ಯಕ್ತಿಯನ್ನು ಕುರಿತು ಮತ್ತಷ್ಟು ಹೆಮ್ಮೆಪಡುತ್ತೇನೆ.",
			"10": "<sup>10</sup> ದುರ್ಬಲತೆಗಳಲ್ಲೂ ದುರವಸ್ಥೆಗಳಲ್ಲೂ ಮಾನನಷ್ಟದಲ್ಲೂ ಕಷ್ಟಹಿಂಸೆಗಳಲ್ಲೂ, ಆಪತ್ತುವಿಪತ್ತುಗಳಲ್ಲೂ ನಾನು ಕ್ರಿಸ್ತಯೇಸುವಿನ ನಿಮಿತ್ತ ಸಂತುಷ್ಟನಾಗಿದ್ದೇನೆ. ನಾನು ಬಲಶಾಲಿಯಾಗಿರುವುದು ಬಲಹೀನನಾಗಿರುವಾಗಲೇ.",
			"11": "<sup>11</sup> ಹೀಗೆ ಹೊಗಳಿಕೊಳ್ಳುವ ನಾನು ಹುಚ್ಚನೇ ಸರಿ. ಆದರೆ ಇದಕ್ಕೆ ಒತ್ತಾಯಪಡಿಸಿದವರು ನೀವೇ. ನೀವೇ ನನ್ನನ್ನು ಹೊಗಳಬೇಕಾಗಿತ್ತು. ಏಕೆಂದರೆ, ನಾನು ಶೂನ್ಯಸಮಾನನಾದರೂ ಆ “ಮಹಾಪ್ರೇಷಿತರು” ಎಂದು ಹೇಳಿಕೊಳ್ಳುವವರಿಗಿಂತ ಕೀಳಾದವನಲ್ಲ.",
			"12": "<sup>12</sup> ನಿಜವಾದ ಪ್ರೇಷಿತನ ಲಕ್ಷಣಗಳಾದ ಕಷ್ಟಸಹಿಷ್ಣುತೆ, ಸೂಚಕಕಾರ್ಯ, ಅದ್ಭುತಕಾರ್ಯ, ಮಹತ್ಕಾರ್ಯ ಇವುಗಳನ್ನೆಲ್ಲಾ ನಿಮ್ಮ ಮುಂದೆ ಪ್ರದರ್ಶಿಸಲಾಗಿದೆ.",
			"13": "<sup>13</sup> ನನ್ನ ಜೀವನಾಂಶದ ಹೊಣೆಯನ್ನು ನಾನು ನಿಮ್ಮ ಮೇಲೆ ಹೊರಿಸಲಿಲ್ಲ. ಇದೊಂದನ್ನು ಬಿಟ್ಟರೆ ಯಾವ ವಿಷಯದಲ್ಲೂ ನಿಮ್ಮನ್ನು ಮಿಕ್ಕ ಸಭೆಗಳಿಗಿಂತ ಕೀಳಾಗಿ ಕಾಣಲಿಲ್ಲ. ಈ ಒಂದು ಅನ್ಯಾಯಕ್ಕಾಗಿ ನನ್ನನ್ನು ಕ್ಷಮಿಸಿರಿ.",
			"14": "<sup>14</sup> ಇಗೋ ಮೂರನೆಯ ಬಾರಿಗೆ ನಾನು ನಿಮ್ಮ ಬಳಿಗೆ ಬರಲು ಸಿದ್ಧನಿದ್ದೇನೆ. ಈ ಸಾರಿಯೂ ನಾನು ನಿಮಗೆ ಹೊರೆಯಾಗಿರಲಾರೆ. ನನಗೆ ಬೇಕಾದವರು ನೀವೇ ಹೊರತು ನಿಮ್ಮ ಹಣಕಾಸು ಅಲ್ಲ. ಮಕ್ಕಳು ಹೆತ್ತವರಿಗಾಗಿ ಅಲ್ಲ, ಹೆತ್ತವರು ಮಕ್ಕಳಿಗಾಗಿ ಆಸ್ತಿಪಾಸ್ತಿಯನ್ನು ಕೂಡಿಡುವುದು ನ್ಯಾಯ.",
			"15": "<sup>15</sup> ಅಂತೆಯೇ ನಿಮ್ಮ ಆತ್ಮಕ್ಷೇಮಕ್ಕಾಗಿ ನನಗಿರುವುದೆಲ್ಲವನ್ನೂ ಸಂತೋಷದಿಂದ ವ್ಯಯಮಾಡುತ್ತೇನೆ. ನನ್ನನ್ನೇ ಸವೆಸಲು ಸಿದ್ಧನಾಗಿದ್ದೇನೆ. ಇಷ್ಟರಮಟ್ಟಿಗೆ ನಿಮ್ಮನ್ನು ನಾನು ಪ್ರೀತಿಸುವಾಗ ನಿಮ್ಮ ಪ್ರೀತಿ ಕಡಿಮೆಯಾಗುವುದು ಸರಿಯೇ?",
			"16": "<sup>16</sup> ನಾನೇನೂ ನಿಮಗೆ ಹೊರೆಯಾಗಿರಲಿಲ್ಲ ಎಂಬುದನ್ನು ನೀವು ಒಪ್ಪಿಕೊಂಡರೂ ಯುಕ್ತಿಯಿಂದ ನಿಮ್ಮನ್ನು ನಾನು ದುರುಪಯೋಗಪಡಿಸಿಕೊಂಡೆ ಎಂದು ನಿಮ್ಮಲ್ಲಿ ಯಾರಾದರೂ ಹೇಳಬಹುದು.",
			"17": "<sup>17</sup> ನಾನು ನಿಮ್ಮ ಬಳಿಗೆ ಕಳುಹಿಸಿದವರಲ್ಲಿ ಯಾರ ಮೂಲಕವಾದರೂ ನಿಮ್ಮನ್ನು ಹಾಗೇನಾದರೂ ವಂಚಿಸಿದ್ದುಂಟೇ?",
			"18": "<sup>18</sup> ನನ್ನ ಕೋರಿಕೆಯಂತೆಯೇ ತೀತನು ನಿಮ್ಮ ಬಳಿಗೆ ಬಂದದ್ದು; ಆತನ ಸಂಗಡ ಒಬ್ಬ ಸಹೋದರನನ್ನೂ ಕಳುಹಿಸಿದೆ. ತೀತನು ನಿಮ್ಮನ್ನು ವಂಚಿಸಿ ಏನಾದರೂ ಪ್ರಯೋಜನ ಪಡೆದದ್ದುಂಟೇ? ಎಂದಿಗೂ ಇಲ್ಲ. ನಾವಿಬ್ಬರು ಒಂದೇ ಮನೋಭಾವನೆಯಿಂದ ವರ್ತಿಸಲಿಲ್ಲವೇ? ಒಂದೇ ರೀತಿಯಲ್ಲಿ ನಡೆದುಕೊಳ್ಳಲಿಲ್ಲವೇ?",
			"19": "<sup>19</sup> ನಾವು ತಪ್ಪಿತಸ್ಥರಲ್ಲವೆಂದು ನಿಮ್ಮ ಮುಂದೆ ಸಮರ್ಥಿಸಿಕೊಳ್ಳುವುದಕ್ಕಾಗಿ ಇದನ್ನು ಹೇಳುತ್ತಿದ್ದೇವೆಂದು ನೆನಸಬೇಡಿ. ದೇವರ ಸನ್ನಿಧಿಯಲ್ಲಿ, ಕ್ರಿಸ್ತಯೇಸುವಿನ ಹೆಸರಿನಲ್ಲಿ ನಾನು ನಿಮಗೆ ಹೇಳುತ್ತೇನೆ: ಪ್ರಿಯ ಸಹೋದರರೇ, ನಾವು ಮಾಡುತ್ತಿರುವುದೆಲ್ಲ ನಿಮ್ಮ ಆಧ್ಯಾತ್ಮಿಕ ಅಭಿವೃದ್ಧಿಗಾಗಿಯೇ.",
			"20": "<sup>20</sup> ನಾನು ನಿಮ್ಮಲ್ಲಿ ಬಂದಾಗ ಒಂದು ವೇಳೆ ನೀವು ನನ್ನ ನಿರೀಕ್ಷೆಗೆ ತಕ್ಕಂತೆ ಇರುವುದಿಲ್ಲವೇನೋ ಮತ್ತು ನಿಮ್ಮ ನಿರೀಕ್ಷೆಗೆ ತಕ್ಕಂತೆ ನಾನು ಕಾಣಿಸುವುದಿಲ್ಲವೇನೋ ಎಂಬ ಶಂಕೆ ನನಗಿದೆ. ನಿಮ್ಮಲ್ಲಿ ಜಗಳ, ದ್ವೇಷ, ಅಸೂಯೆ, ಸ್ವಾರ್ಥ, ಚಾಡಿಮಾತು, ಹರಟೆ, ಅಹಂಕಾರ, ಅನೀತಿ - ಇವುಗಳು ಇರಬಹುದೇನೋ ಎಂಬ ದಿಗಿಲೂ ನನಗಿದೆ.",
			"21": "<sup>21</sup> ಹಿಂದೆ ಪಾಪಮಾಡಿದವರಲ್ಲಿ ಹಲವರು ತಮ್ಮ ಅಶುದ್ಧ, ಅನೈತಿಕ, ಕಾಮುಕ ನಡತೆಗೆ ಪಶ್ಚಾತ್ತಾಪಪಡದೆ ಇದ್ದಾರೋ ಏನೋ, ನಾನು ಅವರಿಗಾಗಿ ಪರಿತಪಿಸಬೇಕಾದೀತೋ ಏನೋ, ನಿಮ್ಮ ನಿಮಿತ್ತ ನಾನು ತಲೆತಗ್ಗಿಸುವಂತೆ ದೇವರು ಮಾಡುತ್ತಾರೋ ಏನೋ, ಎಂಬ ಭಯವೂ ನನಗಿದೆ."
		},
		"13": {
			"1": "<sup>1</sup> ನಾನು ನಿಮ್ಮ ಬಳಿಗೆ ಬರುತ್ತಿರುವುದು ಇದು ಮೂರನೆಯ ಬಾರಿ. “ಯಾವ ದೂರಿದ್ದರೂ ಅದಕ್ಕೆ ಇಬ್ಬರು ಮೂವರು ಸಾಕ್ಷಿಗಳ ಆಧಾರವಿರಬೇಕು,” ಎಂದು ಲಿಖಿತವಾಗಿದೆ.",
			"2": "<sup>2</sup> ನಾನು ಎರಡನೆಯ ಸಾರಿ ನಿಮ್ಮ ಬಳಿಗೆ ಬಂದಿದ್ದಾಗ, ಪಾಪಮಾಡಿದ್ದವರನ್ನೂ ಇತರರನ್ನೂ ಎಚ್ಚರಿಸಿದ್ದೆನು. ಈಗ ದೂರವಿದ್ದರೂ ಮತ್ತೆ ಎಚ್ಚರಿಸುತ್ತಿದ್ದೇನೆ. ಪುನಃ ನಾನು ಬರುವಾಗ ಅವರಾರನ್ನೂ ಶಿಕ್ಷಿಸದೆ ಬಿಡುವುದಿಲ್ಲ.",
			"3": "<sup>3</sup> ಕ್ರಿಸ್ತಯೇಸುವೇ ನನ್ನ ಮುಖಾಂತರ ಮಾತನಾಡುತ್ತಾರೆ ಎಂಬುದಕ್ಕೆ ಆಧಾರ ಬೇಕೆನ್ನುತ್ತೀರಲ್ಲವೇ? ಯೇಸುಕ್ರಿಸ್ತರು ನಿಮ್ಮ ವಿಷಯದಲ್ಲಿ ದುರ್ಬಲರಾಗಿ ವರ್ತಿಸಲಿಲ್ಲ. ಪ್ರಬಲರಾಗಿ ನಿಮ್ಮಲ್ಲಿಯೇ ಇದ್ದಾರೆ ಎಂಬುದೇ ಇದಕ್ಕೆ ಆಧಾರ.",
			"4": "<sup>4</sup> ಬಲಹೀನಾವಸ್ಥೆಯಲ್ಲಿ ಅವರನ್ನು ಶಿಲುಬೆಗೇರಿಸಲಾಯಿತು ಎಂಬುದೇನೋ ನಿಜ. ಆದರೆ, ದೇವರ ಶಕ್ತಿಯಿಂದ ಅವರು ಜೀವಂತರಾಗಿದ್ದಾರೆ. ಕ್ರಿಸ್ತಯೇಸುವಿನಲ್ಲಿ ನಾವೂ ಬಲಹೀನರೇ. ಆದರೆ ನಿಮ್ಮ ವಿಷಯದಲ್ಲಿ ವರ್ತಿಸುವಾಗ ದೇವರ ಶಕ್ತಿಯಿಂದ ಯೇಸುಸ್ವಾಮಿಯೊಂದಿಗೆ ಜೀವಂತರಾಗಿದ್ದೇವೆ.",
			"5": "<sup>5</sup> ನಿಮ್ಮ ಜೀವನ ವಿಶ್ವಾಸದಲ್ಲಿ ಬೇರೂರಿದೆಯೇ ಎಂಬುದನ್ನು ನೀವೇ ಪರೀಕ್ಷಿಸಿನೋಡಿ. ನಿಮ್ಮ ಅಂತರಂಗವನ್ನು ಪರಿಶೋಧಿಸಿ ನೋಡಿ. ಕ್ರಿಸ್ತಯೇಸು ನಿಮ್ಮಲ್ಲಿ ಇದ್ದಾರೆ ಎಂಬುದನ್ನು ಅರಿತಿದ್ದೀರಿ ಅಲ್ಲವೇ? ಅವರು ನಿಮ್ಮಲ್ಲಿ ಇಲ್ಲವಾದರೆ ನೀವು ಅಯೋಗ್ಯರೇ ಸರಿ.",
			"6": "<sup>6</sup> ನಾವಂತೂ ಅಯೋಗ್ಯರಲ್ಲ ಎಂಬ ಅರಿವು ನಿಮಗಾಗಿದೆ ಎಂದು ನಂಬುತ್ತೇನೆ.",
			"7": "<sup>7</sup> ನೀವು ಯಾವ ಕೇಡನ್ನೂ ಮಾಡಬಾರದೆಂದು ದೇವರಲ್ಲಿ ನಾವು ಪ್ರಾರ್ಥಿಸುತ್ತೇವೆ. ಇದರಲ್ಲಿ ನಾವು ಮಾತ್ರ ಯೋಗ್ಯರೆಂದು ತೋರಿಸಿಕೊಳ್ಳುವ ಉದ್ದೇಶ ನಮಗಿಲ್ಲ. ನಾವು ಅಯೋಗ್ಯರೆನಿಸಿಕೊಂಡಿದ್ದರೂ ನೀವು ಒಳಿತನ್ನೇ ಮಾಡಬೇಕೆಂಬುದು ನಮ್ಮ ಉದ್ದೇಶ.",
			"8": "<sup>8</sup> ಸತ್ಯದ ಪರವಾಗಿ ಅಲ್ಲದೆ ಅದಕ್ಕೆ ವಿರುದ್ಧವಾಗಿ ನಾವೇನನ್ನೂ ಮಾಡಲಾರೆವು.",
			"9": "<sup>9</sup> ನೀವು ನಿಜವಾಗಿ ಬಲಾಢ್ಯರಾಗಿದ್ದರೆ ನಾವು ಬಲಹೀನರಾಗಿದ್ದರೂ ನಮಗೆ ಸಂತೋಷವೇ. ನೀವು ಕ್ರೈಸ್ತವಿಶ್ವಾಸದಲ್ಲಿ ಪರಿಪೂರ್ಣರಾಗಬೇಕೆಂಬುದೇ ನಮ್ಮ ಪ್ರಾರ್ಥನೆಯ ಉದ್ದೇಶ.",
			"10": "<sup>10</sup> ಆದ್ದರಿಂದಲೇ ನಿಮ್ಮ ಬಳಿಗೆ ಬರುವುದಕ್ಕೆ ಮುಂಚೆಯೇ ಇದನ್ನೆಲ್ಲಾ ಬರೆಯುತ್ತಿದ್ದೇನೆ. ನಿಮ್ಮನ್ನು ಕೆಡವಿ ಹಾಳುಮಾಡುವುದಕ್ಕಲ್ಲ, ಕಟ್ಟಿ ಎಬ್ಬಿಸುವುದಕ್ಕಾಗಿಯೇ ಪ್ರಭು ನನಗೆ ಅಧಿಕಾರವನ್ನು ಕೊಟ್ಟಿರುವುದು. ಎಂದೇ, ನಿಮ್ಮ ಮಧ್ಯೆ ಬಂದಾಗ ಅಧಿಕಾರ ಪ್ರಯೋಗಿಸಿ ಕಠಿಣವಾಗಿ ವರ್ತಿಸಲು ಅವಕಾಶವಿರಬಾರದೆಂಬುದೇ ನನ್ನ ಅಪೇಕ್ಷೆ.",
			"11": "<sup>11</sup> ಕೊನೆಯದಾಗಿ ಪ್ರಿಯ ಸಹೋದರರೇ, ಸಂತೋಷದಿಂದಿರಿ, ಪರಿಪೂರ್ಣರಾಗಲು ಪ್ರಯತ್ನಿಸಿರಿ. ನನ್ನ ಬುದ್ಧಿಮಾತುಗಳಿಗೆ ಕಿವಿಗೊಡಿ; ಒಮ್ಮನಸ್ಸಿನಿಂದ ಬಾಳಿರಿ; ಸಮಾಧಾನದಿಂದ ಜೀವಿಸಿರಿ; ಆಗ ಪ್ರೀತಿ ಮತ್ತು ಶಾಂತಿ ಸ್ವರೂಪರಾದ ದೇವರು ನಿಮ್ಮೊಡನೆ ಇರುತ್ತಾರೆ.",
			"12": "<sup>12</sup> ಪವಿತ್ರವಾದ ಮುದ್ದಿಟ್ಟು ಒಬ್ಬರನ್ನು ಒಬ್ಬರು ವಂದಿಸಿರಿ. ದೇವಜನರೆಲ್ಲರೂ ನಿಮಗೆ ಶುಭಾಶಯಗಳನ್ನು ಕೋರಿದ್ದಾರೆ.",
			"13": "<sup>13</sup> ನಮ್ಮ ಪ್ರಭು ಯೇಸುಕ್ರಿಸ್ತರ ವರಪ್ರಸಾದವೂ ದೇವರ ಪ್ರೀತಿಯೂ ಪವಿತ್ರಾತ್ಮರ ಅನ್ಯೋನ್ಯತೆಯೂ ನಿಮ್ಮೆಲ್ಲರೊಡನೆ ಇರಲಿ!"
		}
	}
};
module.exports = book;