var book = {
	"name": "ಗಲಾತ್ಯರಿಗೆ",
	"numChapters": 6,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ಗಲಾತ್ಯದಲ್ಲಿರುವ ಸಭೆಗಳಿಗೆ, ಪ್ರೇಷಿತನಾದ ಪೌಲನು ಎಲ್ಲಾ ಸಹೋದರರೊಡನೆ ಸೇರಿ ಬರೆಯುವ ಪತ್ರ:",
			"2": "<sup>2</sup> ಮಾನವರಿಂದಾಗಲಿ ಮಾನವ ಅಧಿಕಾರದಿಂದಾಗಲಿ ನಾನು ಪ್ರೇಷಿತನಾಗಿರದೆ ಯೇಸುಕ್ರಿಸ್ತರಿಂದಲೂ ಮತ್ತು ಅವರನ್ನು ಮರಣದಿಂದ ಎಬ್ಬಿಸಿದ ತಂದೆಯಾದ ದೇವರಿಂದಲೂ ಪ್ರೇಷಿತನಾಗಿರುತ್ತೇನೆ.",
			"3": "<sup>3</sup> ನಮ್ಮ ತಂದೆಯಾದ ದೇವರಿಂದಲೂ ಪ್ರಭು ಯೇಸುಕ್ರಿಸ್ತರಿಂದಲೂ ನಿಮಗೆ ಕೃಪಾಶೀರ್ವಾದವೂ ಶಾಂತಿಸಮಾಧಾನವೂ ಲಭಿಸಲಿ!",
			"4": "<sup>4</sup> ಯೇಸುಕ್ರಿಸ್ತರು, ನಮ್ಮ ತಂದೆಯಾದ ದೇವರ ಚಿತ್ತಕ್ಕನುಸಾರವಾಗಿ, ನಮ್ಮನ್ನು ಸದ್ಯದ ದುಷ್ಟಕಾಲದಿಂದ ಬಿಡುಗಡೆ ಮಾಡುವುದಕ್ಕಾಗಿ ಹಾಗು ನಮ್ಮ ಪಾಪಗಳ ಪರಿಹಾರಕ್ಕಾಗಿ ತಮ್ಮನ್ನೇ ಬಲಿಯಾಗಿ ಅರ್ಪಿಸಿದರು.",
			"5": "<sup>5</sup> ದೇವರಿಗೆ ಯುಗಯುಗಾಂತರಕ್ಕೂ ಸ್ತುತಿಸ್ತೋತ್ರ ಸಲ್ಲಲಿ! ಆಮೆನ್.",
			"6": "<sup>6</sup> ಕ್ರಿಸ್ತಯೇಸುವಿನ ಅನುಗ್ರಹದಿಂದ ನಿಮ್ಮನ್ನು ಕರೆದ ದೇವರನ್ನು ನೀವಿಷ್ಟು ಬೇಗನೆ ತ್ಯಜಿಸಿ, ಬೇರೊಂದು ‘ಶುಭಸಂದೇಶ’ವನ್ನು ಅಂಗೀಕರಿಸುತ್ತಿರುವಿರೆಂದು ಕೇಳಿ ನನಗೆ ಆಶ್ಚರ್ಯವಾಗುತ್ತಿದೆ.",
			"7": "<sup>7</sup> ವಾಸ್ತವವಾಗಿ, ಬೇರೆ ಯಾವ ಶುಭಸಂದೇಶವೂ ಇಲ್ಲ. ಆದರೆ ಕೆಲವರು ನಿಮ್ಮಲ್ಲಿ ಗೊಂದಲವೆಬ್ಬಿಸಿ, ಕ್ರಿಸ್ತಯೇಸುವಿನ ಶುಭಸಂದೇಶವನ್ನು ಮಾರ್ಪಡಿಸಲು ಯತ್ನಿಸುತ್ತಾ ಇದ್ದಾರೆ.",
			"8": "<sup>8</sup> ನಾವು ನಿಮಗೆ ಬೋಧಿಸಿದ ಶುಭಸಂದೇಶಕ್ಕೆ ವಿರುದ್ಧವಾದ ಸಂದೇಶವನ್ನು ನಾವೇ ಆಗಲಿ, ಸ್ವರ್ಗದಿಂದ ಇಳಿದ ದೇವದೂತನೇ ಆಗಲಿ, ಯಾರೇ ಆಗಲಿ ಬೋಧಿಸಿದರೆ ಅವನು ಶಾಪಗ್ರಸ್ತನಾಗಲಿ!",
			"9": "<sup>9</sup> ನೀವು ಅಂಗೀಕರಿಸಿದ ಶುಭಸಂದೇಶಕ್ಕೆ ವಿರುದ್ಧವಾದ ಸಂದೇಶವನ್ನು ಸಾರುವವನು ಶಾಪಗ್ರಸ್ತನಾಗಲಿ ಎಂದು ನಾನು ಮೊದಲೇ ಹೇಳಿದಂತೆ, ಮತ್ತೊಮ್ಮೆ ಒತ್ತಿ ಹೇಳುತ್ತೇನೆ.",
			"10": "<sup>10</sup> ಇದರಿಂದ ನಾನು ಯಾರನ್ನು ಒಲಿಸಿಕೊಳ್ಳಲು ಯತ್ನಿಸುತ್ತಿದ್ದೇನೆ? ಮಾನವರನ್ನೋ? ದೇವರನ್ನೋ? ನಾನು ಮಾನವರ ಮೆಚ್ಚುಗೆಯನ್ನು ಗಳಿಸಿಕೊಳ್ಳಲು ಯತ್ನಿಸುತ್ತಿಲ್ಲ; ನಾನಿನ್ನೂ ಜನರ ಮೆಚ್ಚುಗೆಯನ್ನು ಗಳಿಸುವವನೇ ಆಗಿದ್ದರೆ ಕ್ರಿಸ್ತಯೇಸುವಿನ ದಾಸನಾಗಿರಲು ಸಾಧ್ಯವೇ ಇಲ್ಲ.",
			"11": "<sup>11</sup> ಪ್ರಿಯ ಸಹೋದರರೇ, ನಾನು ಬೋಧಿಸಿದ ಶುಭಸಂದೇಶವು ಮಾನವ ಕಲ್ಪಿತ ಸಂದೇಶ ಅಲ್ಲವೆಂದು ನಿಮಗೆ ಮನದಟ್ಟು ಮಾಡಲು ಬಯಸುತ್ತೇನೆ.",
			"12": "<sup>12</sup> ಈ ಶುಭಸಂದೇಶವನ್ನು ನಾನು ಮನುಷ್ಯರಿಂದ ಪಡೆಯಲಿಲ್ಲ; ಅದನ್ನು ಯಾರಿಂದಲೂ ಕಲಿತುಕೊಳ್ಳಲಿಲ್ಲ. ಯೇಸುಕ್ರಿಸ್ತರೇ ನನಗದನ್ನು ಶ್ರುತಪಡಿಸಿದರು.",
			"13": "<sup>13</sup> ಹಿಂದೊಮ್ಮೆ ನಾನು ಯೆಹೂದ್ಯ ಮತಸ್ಥನಾಗಿದ್ದಾಗ ನನ್ನ ನಡತೆ ಹೇಗಿತ್ತೆಂದು ನೀವು ಚೆನ್ನಾಗಿ ಬಲ್ಲಿರಿ. ಆಗ ನಾನು ದೇವರ ಸಭೆಯನ್ನು ಕ್ರೂರವಾಗಿ ಹಿಂಸಿಸಿದೆ; ಅದನ್ನು ಧ್ವಂಸಮಾಡಲು ಪ್ರಯತ್ನಿಸಿದೆ.",
			"14": "<sup>14</sup> ಇದಲ್ಲದೆ, ನಾನು ನನ್ನ ಪೂರ್ವಜರ ಮತಸಂಪ್ರದಾಯಗಳಲ್ಲಿ ಅತ್ಯಧಿಕ ನಿಷ್ಠೆಯುಳ್ಳವನಾಗಿದ್ದೆ. ಯೆಹೂದ್ಯ ಮತಾಚರಣೆಯಲ್ಲಿ ಅನೇಕ ಸಮವಯಸ್ಕರಿಗಿಂತ ಎಷ್ಟೋ ಮುಂದಾಗಿದ್ದೆ.",
			"15": "<sup>15</sup> ಆದರೆ, ನಾನಿನ್ನೂ ತಾಯಿಯ ಗರ್ಭದಲ್ಲಿ ಇದ್ದಾಗಲೇ ದೇವರು ನನ್ನನ್ನು ತಮ್ಮ ಅನುಗ್ರಹದಿಂದ ಆರಿಸಿಕೊಂಡು ತಮ್ಮ ಸೇವೆಗೆ ಕರೆದರು.",
			"16": "<sup>16</sup> ನಾನು ಯೆಹೂದ್ಯರಲ್ಲದವರಿಗೆ ಶುಭಸಂದೇಶವನ್ನು ಸಾರಬೇಕೆಂದು ದೇವರು ತಮ್ಮ ಮಗನನ್ನು ನನಗೆ ಶ್ರುತಪಡಿಸಿದರು. ಆಗ ಸಲಹೆಗಾಗಿ ನಾನು ಯಾರ ಬಳಿಗೂ ಹೋಗಲಿಲ್ಲ.",
			"17": "<sup>17</sup> ನನಗಿಂತ ಮುಂಚಿತವಾಗಿ ಪ್ರೇಷಿತರಾದವರನ್ನು ಕಾಣಲೆಂದು ನಾನು ಜೆರುಸಲೇಮಿಗೂ ಹೋಗಲಿಲ್ಲ. ಬದಲಿಗೆ, ನೇರವಾಗಿ ಅರೇಬಿಯಾಕ್ಕೆ ಹೋದೆ. ಅಲ್ಲಿಂದ ದಮಸ್ಕಸ್ ನಗರಕ್ಕೆ ಹಿಂದಿರುಗಿದೆ.",
			"18": "<sup>18</sup> ಮೂರು ವರ್ಷಗಳ ನಂತರವೇ ಕೇಫನನ್ನು ಕಂಡು ಮಾತನಾಡಲು ಜೆರುಸಲೇಮಿಗೆ ಹೋದೆ; ಹದಿನೈದು ದಿನ ಆತನೊಂದಿಗೆ ತಂಗಿದ್ದೆ.",
			"19": "<sup>19</sup> ಆಗ, ಉಳಿದ ಪ್ರೇಷಿತರಲ್ಲಿ ಪ್ರಭುವಿನ ಸಹೋದರ ಯಕೋಬನಲ್ಲದೆ ಬೇರೆ ಯಾವ ಪ್ರೇಷಿತರನ್ನೂ ನಾನು ಕಾಣಲಿಲ್ಲ.",
			"20": "<sup>20</sup> ನಾನು ನಿಮಗೆ ಬರೆಯುವ ಸಂಗತಿಗಳು ಸುಳ್ಳಲ್ಲ.ಎಂಬುದಕ್ಕೆ ದೇವರೇ ಸಾಕ್ಷಿ.",
			"21": "<sup>21</sup> ಅನಂತರ ನಾನು ಸಿರಿಯ ಮತ್ತು ಸಿಲಿಸಿಯ ಪ್ರಾಂತ್ಯಗಳಿಗೆ ಹೋದೆ.",
			"22": "<sup>22</sup> ಆಗ ಜುದೇಯ ಪ್ರಾಂತ್ಯದಲ್ಲಿದ್ದ ಕ್ರೈಸ್ತ ಸಭೆಗಳಿಗೆ ನನ್ನ ಪರಿಚಯವಾಗಿರಲಿಲ್ಲ.",
			"23": "<sup>23</sup> ‘ಹಿಂದೊಮ್ಮೆ ನಮ್ಮನ್ನು ಹಿಂಸಿಸುತ್ತಿದ್ದವನು, ತಾನು ನಾಶಮಾಡಲೆತ್ನಿಸಿದ ವಿಶ್ವಾಸವನ್ನೇ ಈಗ ಪ್ರಚಾರಮಾಡುತ್ತಿದ್ದಾನೆ’, ಎಂಬ ಸುದ್ದಿಯನ್ನು ಮಾತ್ರ ಕೇಳಿದ್ದರು.",
			"24": "<sup>24</sup> ಈ ಕಾರಣ ನನ್ನನ್ನು ಕುರಿತು ಅವರು ದೇವರನ್ನು ಕೊಂಡಾಡುತ್ತಿದ್ದರು."
		},
		"2": {
			"1": "<sup>1</sup> ಹದಿನಾಲ್ಕು ವರ್ಷಗಳು ಕಳೆದ ಮೇಲೆ ತೀತನನ್ನು ಕರೆದುಕೊಂಡು ಬಾರ್ನಬನ ಜೊತೆಯಲ್ಲಿ ಪುನಃ ಜೆರುಸಲೇಮಿಗೆ ಹೋದೆ.",
			"2": "<sup>2</sup> ದೇವಪ್ರೇರಣೆಗೆ ಅನುಸಾರವಾಗಿಯೇ ಅಲ್ಲಿಗೆ ಹೋದೆ. ಅನ್ಯಧರ್ಮೀಯರಿಗೆ ನಾನು ಸಾರುತ್ತಿದ್ದ ಸಂದೇಶ ಏನೆಂಬುದನ್ನು ಅಲ್ಲಿದ್ದ ಕೆಲವು ಗಣ್ಯವ್ಯಕ್ತಿಗಳಿಗೆ ಪ್ರತ್ಯೇಕವಾಗಿ ತಿಳಿಸಿದೆ. ಅದುವರೆಗೆ ನಾನು ಪಟ್ಟ ಶ್ರಮವಾಗಲಿ ಈಗ ಪಡುತ್ತಿರುವ ಶ್ರಮವಾಗಲಿ ವ್ಯರ್ಥವಾಗಬಾರದೆಂಬ ಉದ್ದೇಶದಿಂದ ಹೀಗೆ ಮಾಡಿದೆ.",
			"3": "<sup>3</sup> ಆಗ ನನ್ನ ಸಂಗಡವಿದ್ದ ತೀತನು ಗ್ರೀಕನಾಗಿದ್ದರೂ ಅವನಿಗೆ ಸುನ್ನತಿ ಆಗಬೇಕೆಂದು ಯಾರೂ ಒತ್ತಾಯಪಡಿಸಲಿಲ್ಲ.",
			"4": "<sup>4</sup> ಗೂಢಾಚಾರರಾಗಿ ಬಂದಿದ್ದ ಕೆಲವು ಸಹೋದರರು ಅಲ್ಲಿದ್ದುದರಿಂದ ಒತ್ತಾಯ ನಡೆದೀತೆಂಬ ಶಂಕೆ ಇತ್ತು. ಇವರು ಕ್ರಿಸ್ತಯೇಸುವಿನಲ್ಲಿ ನಮಗಿರುವ ಮುಕ್ತ ಸ್ವಾತಂತ್ರ್ಯದ ಬಗ್ಗೆ ಗುಟ್ಟಾಗಿ ವಿಚಾರಿಸಲು ಬಂದಿದ್ದರು. ನಮ್ಮನ್ನು ಪುನಃ ದಾಸತ್ವದಲ್ಲಿ ಸಿಕ್ಕಿಸಬೇಕೆಂಬುದೇ ಅವರ ಉದ್ದೇಶವಾಗಿತ್ತು.",
			"5": "<sup>5</sup> ಆದರೆ ನಾವು ಅವರಿಗೆ ಕ್ಷಣಮಾತ್ರವೂ ಬಿಟ್ಟುಕೊಡಲಿಲ್ಲ. ಹೀಗೆ ನಿಮಗೋಸ್ಕರ ಶುಭಸಂದೇಶದ ಸತ್ಯಾರ್ಥವನ್ನು ಸುಭದ್ರವಾಗಿ ಇರಿಸಿದೆವು.",
			"6": "<sup>6</sup> ಅಲ್ಲಿದ್ದ ಗಣ್ಯವ್ಯಕ್ತಿಗಳು ಕೂಡ (ಅವರು ಹಿಂದೆ ಎಂಥವರಾಗಿದ್ದರು ಎಂಬುದು ನನಗೆ ಮುಖ್ಯವಲ್ಲ; ದೇವರು ಮುಖನೋಡಿ ಮಣೆ ಹಾಕುವವರಲ್ಲ) ನನಗೆ ಯಾವ ಹೊಸ ಸಲಹೆ ನೀಡಲಿಲ್ಲ.",
			"7": "<sup>7</sup> ಬದಲಾಗಿ, ಯೆಹೂದ್ಯರಿಗೆ ಶುಭಸಂದೇಶವನ್ನು ಸಾರುವ ಸೇವೆಯನ್ನು ಪೇತ್ರನಿಗೆ ವಹಿಸಿಕೊಟ್ಟಂತೆಯೇ ಯೆಹೂದ್ಯರಲ್ಲದವರಿಗೆ ಶುಭಸಂದೇಶವನ್ನು ಸಾರುವ ಸೇವಾಕಾರ್ಯವನ್ನು ದೇವರು ನನಗೆ ವಹಿಸಿಕೊಟ್ಟಿದ್ದಾರೆ ಎಂಬುದನ್ನು ಆ ಗಣ್ಯವ್ಯಕ್ತಿಗಳು ಒಪ್ಪಿಕೊಂಡರು.",
			"8": "<sup>8</sup> ಹೌದು, ಯೆಹೂದ್ಯರಿಗೆ ಪ್ರೇಷಿತನಾಗುವಂತೆ ಪೇತ್ರನನ್ನು ಪ್ರೇರೇಪಿಸಿದ ದೇವರು, ಯೆಹೂದ್ಯರಲ್ಲದವರಿಗೆ ಪ್ರೇಷಿತನಾಗಲು ನನ್ನನ್ನು ಪ್ರೇರೇಪಿಸಿದ್ದಾರೆ.",
			"9": "<sup>9</sup> ನನಗೆ ಕೊಡಲಾಗಿದ್ದ ಈ ವಿಶೇಷ ವರವನ್ನು ಮನಗಂಡು ಸಭಾಸ್ತಂಭಗಳೆಂದು ಪರಿಗಣಿತರಾದ ಯಕೋಬ, ಕೇಫ ಮತ್ತು ಯೊವಾನ್ನರು ನನಗೂ ಬಾರ್ನಬನಿಗೂ ಕೈಯಲ್ಲಿ ಕೈಯನ್ನಿಟ್ಟು ಅನ್ಯೋನ್ಯತೆಯನ್ನು ಸೂಚಿಸಿದರು. ಅಲ್ಲದೆ, “ನೀವು ಯೆಹೂದ್ಯರಲ್ಲದವರ ಬಳಿಗೆ ಹೋಗಿರಿ, ನಾವು ಯೆಹೂದ್ಯರ ಬಳಿಗೆ ಹೋಗುತ್ತೇವೆ,” ಎಂದೂ ಹೇಳಿದರು.",
			"10": "<sup>10</sup> ಅವರಲ್ಲಿದ್ದ ಬಡಬಗ್ಗರಿಗೆ ಮರೆಯದೆ ನೆರವಾಗಬೇಕೆಂಬ ಒಂದು ವಿಷಯವನ್ನು ಮಾತ್ರ ಕೇಳಿಕೊಂಡರು. ನಾನು ಮಾಡಬೇಕೆಂದು ಎಣಿಸಿದುದು ಅದೇ ಆಗಿತ್ತು.",
			"11": "<sup>11</sup> ಆದರೆ ಕೇಫನು ಅಂತಿಯೋಕ್ಯಕ್ಕೆ ಬಂದಾಗ ಆತನು ತಪ್ಪಿತಸ್ಥನೆಂಬುದು ಸಿದ್ಧವಾಗಿದ್ದುದರಿಂದ ನಾನು ಆತನನ್ನು ಮುಖಾಮುಖಿಯಾಗಿ ಖಂಡಿಸಿದೆ.",
			"12": "<sup>12</sup> ಯಕೋಬನ ಕಡೆಯಿಂದ ಕೆಲವು ಸಹೋದರರು ಬರುವುದಕ್ಕೆ ಮುಂಚೆ ಆತನು ಅನ್ಯಧರ್ಮೀಯರೊಡನೆ ಊಟಮಾಡುತ್ತಿದ್ದನು. ಆದರೆ ಅವರು ಬಂದ ಮೇಲೆ ಸುನ್ನತಿ ಪರವಾದಿಗಳಿಗೆ ಅಂಜಿ ಅನ್ಯಧರ್ಮೀಯರಿಂದ ದೂರವಾದನು.",
			"13": "<sup>13</sup> ಅಂತೆಯೇ, ಕೇಫನ ಸಂಗಡ ಇದ್ದ ಮಿಕ್ಕ ಯೆಹೂದ್ಯರು ಕಪಟತನದಿಂದ ವರ್ತಿಸಿದರು. ಇವರ ವರ್ತನೆಗೆ ಬಾರ್ನಬನೂ ಸಹ ಮಾರುಹೋದನು.",
			"14": "<sup>14</sup> ಅವರ ಈ ನಡತೆ, ಶುಭಸಂದೇಶದ ಸತ್ಯಕ್ಕೆ ಅನುಗುಣವಾದುದಲ್ಲ ಎಂದು ನನಗೆ ಮನದಟ್ಟಾಯಿತು. ಆಗ ನಾನು ಅವರೆಲ್ಲರ ಸಮ್ಮುಖದಲ್ಲೇ ಕೇಫನಿಗೆ: “ನೀನು ಯೆಹೂದ್ಯನಾಗಿದ್ದೂ ಯೆಹೂದ್ಯರಂತೆ ನಡೆಯದೆ ಅನ್ಯಧರ್ಮೀಯರಂತೆ ನಡೆದುಕೊಳ್ಳುತ್ತ ಇದ್ದಿಯಲ್ಲವೇ? ಹೀಗಿರುವಲ್ಲಿ ಅನ್ಯಧರ್ಮೀಯರು ಯೆಹೂದ್ಯರಂತೆ ನಡೆದುಕೊಳ್ಳಬೇಕೆಂದು ನೀನು ಒತ್ತಾಯಪಡಿಸುವುದಾದರೂ ಹೇಗೆ?” ಎಂದು ಕೇಳಿದೆ.",
			"15": "<sup>15</sup> ನಾವಂತೂ ಹುಟ್ಟು ಯೆಹೂದ್ಯರು. ಪಾಪಿಗಳೆಂದು ಕರೆಯಲಾಗುವ ಅನ್ಯಧರ್ಮೀಯರಲ್ಲ.",
			"16": "<sup>16</sup> ಆದರೂ ಧರ್ಮಶಾಸ್ತ್ರದ ವಿಧಿನಿಯಮಗಳನ್ನು ಅನುಸರಿಸುವುದರಿಂದಲ್ಲ, ಕ್ರಿಸ್ತಯೇಸುವಿನಲ್ಲಿ ವಿಶ್ವಾಸವಿಡುವುದರಿಂದ ಮಾತ್ರ ಮಾನವನು ದೇವರೊಡನೆ ಸತ್ಸಂಬಂಧವನ್ನು ಪಡೆಯುತ್ತಾನೆಂದು ಬಲ್ಲೆವು. ದೇವರೊಡನೆ ಸತ್ಸಂಬಂಧವನ್ನು ಪಡೆಯುವ ಸಲುವಾಗಿ ನಾವೂ ಧರ್ಮಶಾಸ್ತ್ರದ ವಿಧಿನಿಯಮಗಳನ್ನು ಬಿಟ್ಟು, ಕ್ರಿಸ್ತಯೇಸುವಿನಲ್ಲಿ ವಿಶ್ವಾಸವಿಟ್ಟಿದ್ದೇವೆ. ಧರ್ಮಶಾಸ್ತ್ರದ ವಿಧಿನಿಯಮಗಳನ್ನು ಅನುಸರಿಸುವುದರಿಂದಲೆ ಯಾರೂ ದೇವರೊಡನೆ ಸತ್ಸಂಬಂಧಿಕರಾಗಿ ಕಂಡುಬರುವುದಿಲ್ಲ.",
			"17": "<sup>17</sup> ಕ್ರಿಸ್ತಯೇಸುವಿನ ಮುಖಾಂತರ ದೇವರೊಡನೆ ಸತ್ಸಂಬಂಧವನ್ನು ಪಡೆಯಲು ಯತ್ನಿಸುತ್ತಿರುವ ನಾವು ಅನ್ಯಧರ್ಮೀಯರಂತೆ ಕಂಡುಬರುವುದಾದರೆ, ಕ್ರಿಸ್ತಯೇಸುವೇ ಪಾಪಕ್ಕೆ ಕಾರಣರೆಂದು ಹೇಳಿದಂತಾಗುತ್ತದೆ. ಆದರೆ ಅದೆಂದಿಗೂ ಆಗದು.",
			"18": "<sup>18</sup> ನಾನು ಕೆಡವಿದ್ದನ್ನೇ ಪುನಃ ಕಟ್ಟಿದರೆ ನನ್ನನ್ನು ನಾನೇ ಅಪರಾಧಿ ಎಂದು ರುಜುವಾತು ಪಡಿಸಿದಂತಾಗುವುದಿಲ್ಲವೇ?",
			"19": "<sup>19</sup> ನಾನಾದರೋ ದೇವರಿಗಾಗಿ ಜೀವಿಸುವುದಕ್ಕೋಸ್ಕರ, ಧರ್ಮಶಾಸ್ತ್ರದ ಮೂಲಕ, ಧರ್ಮಶಾಸ್ತ್ರದ ಪಾಲಿಗೆ ಸತ್ತವನಾಗಿದ್ದೇನೆ.",
			"20": "<sup>20</sup> ಕ್ರಿಸ್ತಯೇಸುವಿನೊಂದಿಗೆ ನಾನೂ ಶಿಲುಬೆಗೇರಿಸಲಾದವನು. ಈಗ ಜೀವಿಸುವವನು ನಾನಲ್ಲ. ಕ್ರಿಸ್ತಯೇಸು ನನ್ನಲ್ಲಿ ಜೀವಿಸುತ್ತಾರೆ, ನನ್ನನ್ನು ಪ್ರೀತಿಸಿ ನನಗಾಗಿ ಪ್ರಾಣಾರ್ಪಣೆ ಮಾಡಿದ ದೇವರ ಪುತ್ರನಲ್ಲಿ ನಾನು ಇರಿಸಿರುವ ವಿಶ್ವಾಸದಿಂದಲೇ ನಾನೀಗ ಈ ದೇಹದಲ್ಲಿ ಜೀವಿಸುತ್ತಿದ್ದೇನೆ.",
			"21": "<sup>21</sup> ದೇವರ ಅನುಗ್ರಹವನ್ನು ನಿರಾಕರಿಸುವಂಥವನು ನಾನಲ್ಲ. ಧರ್ಮಶಾಸ್ತ್ರದ ಮೂಲಕ ದೇವರೊಡನೆ ಸತ್ಸಂಬಂಧವು ದೊರಕುವುದಾದರೆ ಕ್ರಿಸ್ತಯೇಸು ಮರಣಕ್ಕೀಡಾದುದು ನಿರರ್ಥಕವೇ ಸರಿ."
		},
		"3": {
			"1": "<sup>1</sup> ಎಲೈ ಮತಿಗೆಟ್ಟ ಗಲಾತಿಯರೇ, ನಿಮ್ಮನ್ನು ಮರುಳುಗೊಳಿಸಿದವರು ಯಾರು? ಯೇಸುಕ್ರಿಸ್ತರು ಶಿಲುಬೆಯ ಮೇಲೆ ಪ್ರಾಣಾರ್ಪಣೆ ಮಾಡಿದ್ದನ್ನು ನಿಮ್ಮ ಕಣ್ಣೆದುರಿಗೇ ಚಿತ್ರಿತಗೊಳಿಸಲಿಲ್ಲವೆ?",
			"2": "<sup>2</sup> ನಿಮ್ಮ ಬಳಿ ಒಂದು ವಿಷಯವನ್ನು ಕೇಳಲಿಚ್ಛಿಸುತ್ತೇನೆ; ನೀವು ಪವಿತ್ರಾತ್ಮರನ್ನು ಪಡೆದುದು ಹೇಗೆ? ಧರ್ಮಶಾಸ್ತ್ರದ ವಿಧಿನಿಯಮಗಳನ್ನು ಅನುಸರಿಸಿದ್ದರಿಂದಲೋ? ಇಲ್ಲವೆ ಶುಭಸಂದೇಶವನ್ನು ಕೇಳಿ ವಿಶ್ವಾಸಿಸಿದ್ದರಿಂದಲೋ?",
			"3": "<sup>3</sup> ನೀವು ಪವಿತ್ರಾತ್ಮ ಸಂಬಂಧವಾದ ಆಧ್ಯಾತ್ಮಿಕ ಜೀವನವನ್ನು ಪ್ರಾರಂಭಿಸಿಯೂ ಈಗ ಅದನ್ನು ಕೇವಲ ದೇಹಸಂಬಂಧವಾದ ವಿಧಿಯಿಂದ ಪೂರೈಸಬೇಕೆಂದಿದ್ದೀರೋ? ನೀವು ಅಷ್ಟು ಮತಿಗೆಟ್ಟವರೋ?",
			"4": "<sup>4</sup> ನಿಮ್ಮ ಅನುಭವಗಳೆಲ್ಲವೂ ವ್ಯರ್ಥವಾದುವು ಎಂದು ಹೇಳೋಣವೇ?",
			"5": "<sup>5</sup> ದೇವರು ನಿಮಗೆ ಪವಿತ್ರಾತ್ಮರನ್ನು ಪ್ರಧಾನಮಾಡಿ ಮಹತ್ಕಾರ್ಯಗಳನ್ನು ಎಸಗುತ್ತಾ ಬಂದಿದ್ದಾರೆ. ಹಾಗೆ ಮಾಡುತ್ತಿರುವುದು, ನೀವು ಧರ್ಮಶಾಸ್ತ್ರದ ವಿಧಿನಿಯಮಗಳನ್ನು ಅನುಸರಿಸಿದ್ದರಿಂದಲ್ಲ, ಶುಭಸಂದೇಶವನ್ನು ಕೇಳಿ, ವಿಶ್ವಾಸ ಇಟ್ಟಿರುವುದರಿಂದ.",
			"6": "<sup>6</sup> ಪವಿತ್ರಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುವಂತೆ: “ಅಬ್ರಹಾಮನು ದೇವರಲ್ಲಿ ವಿಶ್ವಾಸವಿಟ್ಟನು; ಈ ಕಾರಣ ದೇವರು ಆತನನ್ನು ತಮ್ಮ ಸತ್ಸಂಬಂಧದಲ್ಲಿ ಇರುವುದಾಗಿ ಪರಿಗಣಿಸಿದರು.”",
			"7": "<sup>7</sup> ಆದ್ದರಿಂದ ಯಾರು ವಿಶ್ವಾಸದಿಂದ ಜೀವಿಸುತ್ತಾರೋ ಅವರೇ ಅಬ್ರಹಾಮನ ಮಕ್ಕಳೆಂದು ತಿಳಿದುಕೊಳ್ಳಿ.",
			"8": "<sup>8</sup> ಈ ವಿಶ್ವಾಸದ ಮೂಲಕವೇ ಅನ್ಯಧರ್ಮೀಯರನ್ನು ಕೂಡ ದೇವರು ತಮ್ಮ ಸತ್ಸಂಬಂಧದಲ್ಲಿ ಇರಿಸಿಕೊಳ್ಳುತ್ತಾರೆ, ಎಂದು ಪವಿತ್ರಗ್ರಂಥದಲ್ಲಿ ಮೊದಲೇ ಬರೆಯಲಾಗಿತ್ತು. ಈ ಕಾರಣದಿಂದಲೇ ಅಬ್ರಹಾಮನಿಗೆ, “ನಿನ್ನ ಮುಖಾಂತರವೇ ಎಲ್ಲಾ ಜನಾಂಗಗಳು ಆಶೀರ್ವಾದವನ್ನು ಪಡೆಯುವುವು,” ಎಂಬ ಶುಭವರ್ತಮಾನವನ್ನು ಪಡೆಯುವುವು,” ಎಂಬ ಶುಭವರ್ತಮಾನವನ್ನು ಮುಂಚಿತವಾಗಿ ತಿಳಿಸಲಾಯಿತು.",
			"9": "<sup>9</sup> ಹೀಗಿರಲಾಗಿ, ವಿಶ್ವಾಸದಿಂದ ಬಾಳುವವರು ವಿಶ್ವಾಸಿಯಾದ ಅಬ್ರಹಾಮನು ಪಡೆದ ಸೌಭಾಗ್ಯವನ್ನೇ ಪಡೆಯುವರು.",
			"10": "<sup>10</sup> ಧರ್ಮಶಾಸ್ತ್ರದ ವಿಧಿನಿಯಮಗಳನ್ನೇ ಆಧಾರವಾಗಿಟ್ಟುಕೊಂಡು ಬಾಳುವವರು ಶಾಪಗ್ರಸ್ತರು. ಏಕೆಂದರೆ, “ಧರ್ಮಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುವುದನ್ನೆಲ್ಲಾ ಅನುದಿನವೂ ಕೈಗೊಂಡು ನಡೆಯದ ಪ್ರತಿಯೊಬ್ಬನೂ ಶಾಪಗ್ರಸ್ತನು,” ಎಂದು ಲಿಖಿತವಾಗಿದೆ.",
			"11": "<sup>11</sup> ಇದಲ್ಲದೆ, ಧರ್ಮಶಾಸ್ತ್ರ ವಿಧಿಸುವುದನ್ನು ಪೂರೈಸುವುದರಿಂದ ಯಾರೂ ದೇವರೊಡನೆ ಸತ್ಸಂಬಂಧ ಪಡೆಯಲಾರರು ಎಂಬುದು ಸಹ ಸ್ಪಷ್ಟವಾಗಿದೆ. ಏಕೆಂದರೆ, “ಯಾರು ದೇವರೊಡನೆ ಸತ್ಸಂಬಂಧವನ್ನು ಹೊಂದಿರುತ್ತಾರೋ ಅವರು ವಿಶ್ವಾಸದಿಂದಲೇ ಸಜ್ಜೀವವನ್ನು ಪಡೆಯುತ್ತಾರೆ” ಎಂದು ಪವಿತ್ರಗ್ರಂಥದಲ್ಲೇ ಲಿಖಿತವಾಗಿದೆ.",
			"12": "<sup>12</sup> ಧರ್ಮಶಾಸ್ತ್ರ ವಿಶ್ವಾಸವನ್ನು ಆಧರಿಸಿಲ್ಲ, ಬದಲಿಗೆ, “ಅದರ ವಿಧಿನಿಯಮಗಳನ್ನು ಪಾಲಿಸುವವನು ಅವುಗಳ ಮೂಲಕವಾಗಿ ಜೀವಿಸುತ್ತಾನೆ,” ಎಂದು ಬರೆಯಲಾಗಿದೆ.",
			"13": "<sup>13</sup> ಮರಕ್ಕೆ ತೂಗುಹಾಕಲಾದ ಪ್ರತಿ ಒಬ್ಬನೂ ಶಾಪಗ್ರಸ್ತನು,” ಎಂದು ಬರೆದಿರುವಂತೆ, ನಮಗೋಸ್ಕರ ಕ್ರಿಸ್ತಯೇಸು ಶಾಪಸ್ವರೂಪಿಯಾಗಿ ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿ ಹೇಳಲಾಗಿರುವ ಶಾಪದಿಂದ ನಮ್ಮನ್ನು ಪಾರುಮಾಡಿದರು.",
			"14": "<sup>14</sup> ಇದರಿಂದಾಗಿ ಅಬ್ರಹಾಮನಿಗೆ ದೊರಕಿದ ಸೌಭಾಗ್ಯ, ಕ್ರಿಸ್ತಯೇಸುವಿನ ಮುಖಾಂತರ ಅನ್ಯಧರ್ಮೀಯರಿಗೂ ದೊರಕುವಂತಾಯಿತು; ವಿಶ್ವಾಸದ ಮೂಲಕವಾಗಿ, ದೇವರು ವಾಗ್ದಾನ ಮಾಡಿದ ಪವಿತ್ರಾತ್ಮ ನಮಗೂ ದೊರಕುವಂತಾಯಿತು.",
			"15": "<sup>15</sup> ಪ್ರಿಯ ಸಹೋದರರೇ, ದೈನಂದಿನ ಜೀವನದಿಂದ ಒಂದು ಉದಾಹರಣೆಯನ್ನೇ ತೆಗೆದುಕೊಳ್ಳೋಣ: ಒಬ್ಬನು ಮತ್ತೊಬ್ಬನೊಡನೆ ಒಂದು ಒಪ್ಪಂದ ಮಾಡಿಕೊಳ್ಳುತ್ತಾನೆ. ಕ್ರಮಬದ್ಧವಾಗಿ ಅದನ್ನು ಮಾಡಿ ಮುಗಿಸಿದ ಮೇಲೆ ಯಾರೂ ಅದನ್ನು ರದ್ದುಮಾಡುವಂತಿಲ್ಲ, ಬದಲಾಯಿಸುವಂತಿಲ್ಲ.",
			"16": "<sup>16</sup> ಹಾಗೆಯೇ, ದೇವರು ಅಬ್ರಹಾಮನಿಗೂ ಆತನ ಸಂತತಿಗೂ ವಾಗ್ದಾನಗಳನ್ನು ಮಾಡಿದರು. \"ನಿನ್ನ ಸಂತತಿಗಳಿಗೆ\" ಎಂದು ಹೇಳಿ ಅನೇಕರನ್ನು ಸೂಚಿಸದೆ “ನಿನ್ನ ಸಂತತಿಗೆ” ಎಂದು ಹೇಳಿ ಒಬ್ಬ ವ್ಯಕ್ತಿಯನ್ನೇ ಆ ವಾಗ್ದಾನದಲ್ಲಿ ಸೂಚಿಸಿದರು. ಆ ವ್ಯಕ್ತಿಯೇ ಕ್ರಿಸ್ತಯೇಸು.",
			"17": "<sup>17</sup> ದೇವರು ಅಬ್ರಹಾಮನೊಂದಿಗೆ ಮಾಡಿಮುಗಿಸಿದ ಒಪ್ಪಂದವನ್ನು ನಾನೂರ ಮೂವತ್ತು ವರ್ಷಗಳಾದ ಮೇಲೆ ಬಂದ ಧರ್ಮಶಾಸ್ತ್ರದಿಂದ ರದ್ದುಪಡಿಸಲಾಗದು; ಆ ವಾಗ್ದಾನವನ್ನು ವ್ಯರ್ಥಪಡಿಸಲಾಗದು.",
			"18": "<sup>18</sup> ದೇವರು ನಮಗೆ ಹಕ್ಕುಬಾಧ್ಯತೆಯಾಗಿ ಕೊಡುವುದು ಧರ್ಮಶಾಸ್ತ್ರದಿಂದ ಲಭಿಸುವುದಾದರೆ ಅದು ಇನ್ನು ದೇವವಾಗ್ದಾನದಿಂದ ಲಭಿಸದು ಎಂದಂತಾಯಿತು. ಆದರೆ ದೇವರು ಅದನ್ನು ಅಬ್ರಹಾಮನಿಗೆ ದಯಪಾಲಿಸಿದ್ದು ವಾಗ್ದಾನದ ಮೂಲಕವೇ.",
			"19": "<sup>19</sup> ಹಾಗಾದರೆ ಧರ್ಮಶಾಸ್ತ್ರದ ಉದ್ದೇಶವಾದರೂ ಏನು? ಅಪರಾಧದ ಅರಿವನ್ನು ಮೂಡಿಸುವುದಕ್ಕಾಗಿ ಅದನ್ನು ಕಾಲಕ್ರಮೇಣ ಸೇರಿಸಲಾಯಿತು. ದೇವವಾಗ್ದಾನವನ್ನು ಪಡೆದ ಆ ಸಂತತಿ ಬರುವ ತನಕ ಮಾತ್ರ ಅದು ಇರಲೆಂದು ಕೊಡಲಾಯಿತು. ಅಲ್ಲದೆ, ಕೇವಲ ದೇವದೂತರ ಮುಖಾಂತರ, ಒಬ್ಬ ಮಾನವ ಮಧ್ಯಸ್ಥನ ಮೂಲಕ ಮಾತ್ರ ಅದನ್ನು ನೀಡಲಾಯಿತು.",
			"20": "<sup>20</sup> ಒಂದೇ ಪಕ್ಷ ಇರುವಾಗ ಮಧ್ಯಸ್ಥನು ಬೇಕಿಲ್ಲ, ದೇವರಾದರೋ ಒಬ್ಬರೇ.",
			"21": "<sup>21</sup> ಹಾಗಾದರೆ, ಧರ್ಮಶಾಸ್ತ್ರ ದೇವರ ವಾಗ್ದಾನಕ್ಕೆ ವಿರುದ್ಧವಾಗಿದೆಯೆಂದು ಹೇಳೋಣವೇ? ಎಂದಿಗೂ ಇಲ್ಲ. ಸಜ್ಜೀವಗೊಳಿಸುವ ಶಕ್ತಿಯನ್ನು ಧರ್ಮಶಾಸ್ತ್ರ ಹೊಂದಿದ್ದರೆ ಅದನ್ನು ಅನುಸರಿಸುವುದರಿಂದ ನಿಸ್ಸಂದೇಹವಾಗಿ ದೇವರೊಡನೆ ಸತ್ಸಂಬಂಧ ದೊರಕುತ್ತಿತ್ತು.",
			"22": "<sup>22</sup> ಅದಕ್ಕೆ ಬದಲಾಗಿ ಸಮಸ್ತ ಲೋಕವೂ ಪಾಪಕ್ಕೆ ಒಳಗಾಯಿತು ಎಂದು ಪವಿತ್ರಗ್ರಂಥ ಸ್ಪಷ್ಟೀಕರಿಸುತ್ತದೆ. ಹೀಗೆ, ವಾಗ್ದಾನ ಮಾಡಿದ ಸೌಭಾಗ್ಯವನ್ನು ಯೇಸುಕ್ರಿಸ್ತರಲ್ಲಿ ವಿಶ್ವಾಸವಿಟ್ಟವರಿಗೆ ಅವರ ನಂಬಿಕೆಯ ಆಧಾರದ ಮೇಲೆ ಕೊಡಲಾಗುತ್ತದೆ.",
			"23": "<sup>23</sup> ಕ್ರೈಸ್ತವಿಶ್ವಾಸದ ಕಾಲವು ಬರುವುದಕ್ಕೆ ಮೊದಲು ನಾವು ಧರ್ಮಶಾಸ್ತ್ರದ ನಿರ್ಬಂಧಕ್ಕೆ ಒಳಗಾಗಿ ಕೈದಿಗಳಂತೆ ಇದ್ದೆವು. ಈ ವಿಶ್ವಾಸವು ಪ್ರಕಟವಾಗುವವರೆಗೂ ಇದೇ ಪರಿಸ್ಥಿತಿ ಇತ್ತು.",
			"24": "<sup>24</sup> ಅಂತೆಯೇ, ವಿಶ್ವಾಸದ ಮೂಲಕ ನಾವು ದೇವರೊಡನೆ ಸತ್ಸಂಬಂಧ ಹೊಂದುವಂತೆ ಕ್ರಿಸ್ತಯೇಸು ಬರುವತನಕ ಅದು ನಮಗೆ ಕಾವಲಾಗಿತ್ತು.",
			"25": "<sup>25</sup> ಈಗ ಕ್ರೈಸ್ತವಿಶ್ವಾಸವು ಆಗಮಿಸಿರುವುದರಿಂದ ಇನ್ನು ನಾವು ಧರ್ಮಶಾಸ್ತ್ರದ ಅಧೀನದಲ್ಲಿ ಇಲ್ಲ.",
			"26": "<sup>26</sup> ಕ್ರಿಸ್ತಯೇಸುವಿನಲ್ಲಿ ಇಟ್ಟಿರುವ ವಿಶ್ವಾಸದ ಮೂಲಕ ನೀವೆಲ್ಲರೂ ದೇವರ ಮಕ್ಕಳಾಗಿದ್ದೀರಿ.",
			"27": "<sup>27</sup> ಹೇಗೆಂದರೆ, ಕ್ರಿಸ್ತಯೇಸುವಿನಲ್ಲಿ ಒಂದಾಗುವುದಕ್ಕೆ ದೀಕ್ಷಾಸ್ನಾನ ಮಾಡಿಸಿಕೊಂಡಿರುವ ನೀವೆಲ್ಲರೂ ಕ್ರಿಸ್ತಂಬರರಾಗಿದ್ದೀರಿ.",
			"28": "<sup>28</sup> ನೀವೆಲ್ಲರೂ ಕ್ರಿಸ್ತಯೇಸುವಿನಲ್ಲಿ ಒಂದಾಗಿದ್ದೀರಿ. ಎಂದೇ, ಇನ್ನು ಮೇಲೆ ಯೆಹೂದ್ಯ-ಯೆಹೂದ್ಯನಲ್ಲದವ, ದಾಸ-ದಣಿ, ಗಂಡು-ಹೆಣ್ಣು, ಎಂಬ ಭೇದವಿಲ್ಲ.",
			"29": "<sup>29</sup> ನೀವು ಕ್ರಿಸ್ತಯೇಸುವಿಗೆ ಸೇರಿದವರಾಗಿದ್ದರೆ ಅಬ್ರಹಾಮನ ಸಂತತಿಯೂ ಆಗಿದ್ದೀರಿ; ದೈವವಾಗ್ದಾನದ ಪ್ರಕಾರ ವಾರಸುದಾರರೂ ಆಗಿದ್ದೀರಿ."
		},
		"4": {
			"1": "<sup>1</sup> ನಾನು ಮತ್ತೆ ಹೇಳುವುದೇನೆಂದರೆ; ವಾರಸುದಾರನು ತಂದೆಯ ಆಸ್ತಿಗೆಲ್ಲಾ ಹಕ್ಕುದಾರನಾಗಿದ್ದರೂ, ಅವನು ಬಾಲಕನಾಗಿರುವ ತನಕ ಅವನಿಗೂ ಆಳಿಗೂ ವ್ಯತ್ಯಾಸವಿರುವುದಿಲ್ಲ.",
			"2": "<sup>2</sup> ತಂದೆ ಗೊತ್ತುಮಾಡಿದ ದಿನದವರೆಗೂ ಅವನು ಪೋಷಕರ ಹಾಗೂ ಕಾರ್ಯನಿರ್ವಾಹಕರ ಕೈಕೆಳಗೆ ಇರುತ್ತಾನೆ.",
			"3": "<sup>3</sup> ನಮ್ಮ ಸ್ಥಿತಿಯೂ ಹಾಗೆಯೇ ಇತ್ತು. ನಾವು ಬಾಲಕರಂತೆ ವಿಶ್ವದ ಮೂಲಭೂತ ಶಕ್ತಿಗಳಿಗೆ ಅಧೀನರಾಗಿದ್ದೆವು.",
			"4": "<sup>4</sup> ಕಾಲವು ಪರಿಪಕ್ವವಾದಾಗ ದೇವರು ತಮ್ಮ ಪುತ್ರನನ್ನೇ ಕಳುಹಿಸಿಕೊಟ್ಟರು. ಇವರು ಧರ್ಮಶಾಸ್ತ್ರಕ್ಕೆ ಅಧೀನರಾದವರನ್ನು ಬಿಡುಗಡೆ ಮಾಡುವುದಕ್ಕಾಗಿಯೂ ದೇವಪುತ್ರರ ಪದವಿಯನ್ನು ನಮಗೆ ಒದಗಿಸಿಕೊಡುವುದಕ್ಕಾಗಿಯೂ ಒಬ್ಬ ಸ್ತ್ರೀಯಲ್ಲಿ ಜನಿಸಿದರು. ಧರ್ಮಶಾಸ್ತ್ರಕ್ಕೆ ಅಧೀನರಾಗಿಯೇ ಹುಟ್ಟಿದರು.",
			"6": "<sup>6</sup> ನೀವು ದೇವರ ಮಕ್ಕಳಾಗಿರುವುದರಿಂದಲೇ, “ಅಪ್ಪಾ, ತಂದೆಯೇ,” ಎಂದು ಕರೆಯುವ ತಮ್ಮ ಪುತ್ರನ ಆತ್ಮವನ್ನು ದೇವರು ನಮ್ಮ ಹೃದಯಗಳಿಗೆ ಕಳುಹಿಸಿದ್ದಾರೆ.",
			"7": "<sup>7</sup> ಆದ್ದರಿಂದ, ದೇವರ ಅನುಗ್ರಹದಿಂದಲೇ ನೀನು ಇನ್ನು ಮನೆಯಾಳಲ್ಲ, ಮಗನಾಗಿದ್ದೀ. ಮಗನೆಂದ ಮೇಲೆ ನೀನು ವಾರಸುದಾರನೂ ಹೌದು.",
			"8": "<sup>8</sup> ಹಿಂದೆ ನೀವು ದೇವರನ್ನು ಅರಿಯದೆ ಇದ್ದಿರಿ. ದೇವರಲ್ಲದವುಗಳಿಗೆ ಅಧೀನರಾಗಿದ್ದೀರಿ.",
			"9": "<sup>9</sup> ಈಗಲಾದರೋ ನಿಜದೇವರನ್ನು ಅರಿತುಕೊಂಡಿದ್ದೀರಿ; ಸರಿಯಾಗಿ ಹೇಳಬೇಕಾದರೆ ದೇವರು ನಿಮ್ಮನ್ನು ಅರಿತುಕೊಂಡಿದ್ದಾರೆ. ಹೀಗಿರಲಾಗಿ, ದುರ್ಬಲ ಹಾಗೂ ದರಿದ್ರವಾದ ಮೂಲಭೂತ ಶಕ್ತಿಗಳಿಗೆ ನೀವು ಮರಳುವುದೇಕೆ? ಪುನಃ ಅವುಗಳಿಗೆ ಗುಲಾಮರಾಗಲು ಇಚ್ಛಿಸುವುದೇಕೆ?",
			"10": "<sup>10</sup> ನೀವು ಶುಭಾಶುಭ ದಿನಗಳನ್ನು, ಮಾಸಗಳನ್ನು ಮತ್ತು ಸಂವತ್ಸರಗಳನ್ನು ಜಾಗರೂಕತೆಯಿಂದ ನೋಡುತ್ತೀರಿ.",
			"11": "<sup>11</sup> ನಿಮಗಾಗಿ ನಾನು ಪಟ್ಟ ಪ್ರಯಾಸವೆಲ್ಲ ವ್ಯರ್ಥವಾಯಿತೇನೋ ಎಂಬ ಶಂಕೆ ನನ್ನನ್ನು ಕಾಡುತ್ತಿದೆ.",
			"12": "<sup>12</sup> ಪ್ರಿಯ ಸಹೋದರರೇ, ನಾನು ನಿಮ್ಮಂತೆ ಆದಹಾಗೆ, ನೀವೂ ನನ್ನಂತೆ ಆಗಬೇಕೆಂದು ಕಳಕಳಿಯಿಂದ ಕೇಳಿಕೊಳ್ಳುತ್ತೇನೆ. ನೀವು ನನಗೆ ಯಾವ ಅನ್ಯಾಯವನ್ನೂ ಮಾಡಿಲ್ಲ.",
			"13": "<sup>13</sup> ನಾನು ಅಸ್ವಸ್ಥನಾಗಿದ್ದುದರಿಂದ ನಿಮ್ಮಲ್ಲೇ ಇದ್ದು ಮೊತ್ತಮೊದಲ ಬಾರಿಗೆ ಶುಭಸಂದೇಶವನ್ನು ನಿಮಗೆ ಸಾರಲು ಆಸ್ಪದವಾಯಿತು. ಇದನ್ನು ನೀವು ಬಲ್ಲಿರಿ.",
			"14": "<sup>14</sup> ನನ್ನ ದೇಹಸ್ಥಿತಿ ನಿಮಗೆ ಬೇಸರವನ್ನುಂಟುಮಾಡುವಂಥದ್ದಾಗಿದ್ದರೂ ನೀವು ನನ್ನನ್ನು ತಿರಸ್ಕರಿಸಲಿಲ್ಲ; ಅಸಹ್ಯಪಡಲಿಲ್ಲ. ನನ್ನನ್ನು ದೇವದೂತನಂತೆ ಏಕೆ, ಕ್ರಿಸ್ತಯೇಸುವಿನಂತೆಯೇ ಭಾವಿಸಿ ಉಪಚರಿಸಿದಿರಿ.",
			"15": "<sup>15</sup> ಅದೊಂದು ಭಾಗ್ಯವೆಂದೇ ತಿಳಿದಿರಿ. ಆ ಭಾವನೆ ಈಗೆಲ್ಲಿದೆ? ಆಗ, ಬೇಕಾಗಿದ್ದಿದ್ದರೆ ನಿಮ್ಮ ಕಣ್ಣುಗಳನ್ನೇ ಕಿತ್ತು ನನಗೆ ಕೊಡುತ್ತಿದ್ದಿರಿ ಎಂದು ನಾನು ಹೇಳಬಲ್ಲೆ!",
			"16": "<sup>16</sup> ಹೀಗಿರಲಾಗಿ ಸತ್ಯವನ್ನು ನುಡಿಯುವುದರಿಂದ ನಾನು ನಿಮಗೆ ಶತ್ರುವಾಗಿಬಿಟ್ಟೆನೇ?",
			"17": "<sup>17</sup> ಆ ನನ್ನ ವಿರೋಧಿಗಳು ನಿಮ್ಮನ್ನು ಮೆಚ್ಚಿಸಲು ತುಂಬ ಆಸಕ್ತಿಯನ್ನು ತೋರಿಸುತ್ತಿದ್ದಾರೆ. ಆದರೆ ಅವರ ಉದ್ದೇಶ ಒಳ್ಳೆಯದಲ್ಲ. ನೀವು ಸಹ ಅವರನ್ನು ಅಷ್ಟೇ ಆಸಕ್ತಿಯಿಂದ ಮೆಚ್ಚಿಸಬೇಕೆಂಬುದೇ ಅವರ ಉದ್ದೇಶ. ಆದ್ದರಿಂದಲೇ ನಿಮ್ಮನ್ನು ನನ್ನಿಂದ ಬೇರ್ಪಡಿಸಲು ಯತ್ನಿಸುತ್ತಿದ್ದಾರೆ.",
			"18": "<sup>18</sup> ನಾನು ನಿಮ್ಮ ಬಳಿ ಇರಲಿ, ಇಲ್ಲದಿರಲಿ, ಬೇರೆಯವರು ನಿಮ್ಮಲ್ಲಿ ಅಷ್ಟು ಆಸಕ್ತರಾಗಿರುವುದು ಒಳ್ಳೆಯದೇ. ಆದರೆ ಅಂಥ ಆಸಕ್ತಿ ಸದುದ್ದೇಶದಿಂದ ಕೂಡಿರಬೇಕು.",
			"19": "<sup>19</sup> ನನ್ನ ಪ್ರಿಯ ಮಕ್ಕಳೇ, ತಾಯಿ ತನ್ನ ಮಗುವಿಗಾಗಿ ಪ್ರಸವವೇದನೆಪಡುವ ಪ್ರಕಾರ ಕ್ರಿಸ್ತಯೇಸು ನಿಮ್ಮಲ್ಲಿ ರೂಪುಗೊಳ್ಳುವ ತನಕ ನಿಮಗಾಗಿ ನಾನು ಮತ್ತೆ ಅಂಥ ವೇದನೆಯನ್ನು ಪಡುತ್ತಿದ್ದೇನೆ.",
			"20": "<sup>20</sup> ನಿಮ್ಮ ವಿಷಯದಲ್ಲಿ ಏನು ಮಾಡಬೇಕೋ ನನಗೆ ತೋಚುತ್ತಿಲ್ಲ. ಬಹುಶಃ ನೇರವಾಗಿ ನಿಮ್ಮ ಬಳಿಗೆ ಬಂದು, ನಿಮ್ಮ ಮಧ್ಯದಲ್ಲೇ ಇದ್ದು ನನ್ನ ಮಾತಿನ ವೈಖರಿಯನ್ನು ಬದಲಾಯಿಸಿದ್ದರೆ ಚೆನ್ನಾಗಿರುತ್ತಿತ್ತೇನೋ!",
			"21": "<sup>21</sup> ಧರ್ಮಶಾಸ್ತ್ರಕ್ಕೆ ಅಧೀನರಾಗಲು ಆಶಿಸುವವರೇ, ನನಗೆ ಉತ್ತರ ಕೊಡಿ: ಅದೇ ಧರ್ಮಶಾಸ್ತ್ರ ಹೇಳುವುದನ್ನು ನೀವು ಕೇಳಿಲ್ಲವೇ?",
			"22": "<sup>22</sup> ಅಬ್ರಹಾಮನಿಗೆ ಇಬ್ಬರು ಮಕ್ಕಳಿದ್ದರು: ಒಬ್ಬನು ದಾಸಿಯಿಂದ ಹುಟ್ಟಿದವನು; ಇನ್ನೊಬ್ಬನು ಧರ್ಮಪತ್ನಿಯಿಂದ ಹುಟ್ಟಿದವನು.",
			"23": "<sup>23</sup> ದಾಸಿಯ ಮಗ ಪ್ರಕೃತಿ ಸಹಜವಾಗಿ ಹುಟ್ಟಿದವನು; ಧರ್ಮಪತ್ನಿಯ ಮಗನಾದರೋ ವಾಗ್ದಾನದ ಫಲವಾಗಿ ಹುಟ್ಟಿದವನು.",
			"24": "<sup>24</sup> ಇದೊಂದು ಅಲಂಕಾರ ರೂಪ. ಆ ಇಬ್ಬರು ಸ್ತ್ರೀಯರು ಎರಡು ಒಡಂಬಡಿಕೆಗಳಿಗೆ ಸದೃಶರಾಗಿದ್ದಾರೆ. ಒಂದು, ಸೀನಾಯ್ ಪರ್ವತದಲ್ಲಿ ಮಾಡಲಾದ ಒಡಂಬಡಿಕೆ; ಅದು ದಾಸತ್ವಕ್ಕೆ ಮಕ್ಕಳನ್ನು ಹೆರುತ್ತದೆ. ಅದನ್ನು ಸೂಚಿಸುವವಳೇ ದಾಸಿ ಹಾಗರಳು.",
			"25": "<sup>25</sup> ಅರೇಬಿಯಾದಲ್ಲಿರುವ ಸೀನಾಯ್ ಪರ್ವತವನ್ನು ಪ್ರತಿನಿಧಿಸುವ ಈ ಹಾಗರಳು ಈಗಿನ ಜೆರುಸಲೇಮ್ ನಗರವನ್ನು ಹೋಲುತ್ತಾಳೆ. ಹೇಗೆಂದರೆ, ಜೆರುಸಲೇಮ್ ತನ್ನ ಮಕ್ಕಳ ಸಮೇತವಾಗಿ ದಾಸತ್ವದಲ್ಲಿ ಇದೆ.",
			"26": "<sup>26</sup> ಆದರೆ ಸ್ವರ್ಗೀಯ ಜೆರುಸಲೇಮ್ ದಾಸಿ ಅಲ್ಲ, ಸ್ವತಂತ್ರಳು, ಈಕೆಯೇ ನಮ್ಮ ತಾಯಿ.",
			"27": "<sup>27</sup> “ಸಂತಾನವಿಲ್ಲದ ಸ್ತ್ರೀಯೇ, ಸಂತೋಷಿಸು ಪ್ರಸವವೇದನೆಯನರಿಯದವಳೇ, ಹರ್ಷೋದ್ಗಾರಮಾಡು ಗಂಡನುಳ್ಳವಳಿಗಿಂತ ಕೈಬಿಟ್ಟವಳ ಸಂತಾನ ಹೆಚ್ಚು.” ಎಂದು ಪವಿತ್ರಗ್ರಂಥದಲ್ಲೇ ಲಿಖಿತವಾಗಿದೆ.",
			"28": "<sup>28</sup> ಆದ್ದರಿಂದ ಪ್ರಿಯ ಸಹೋದರರೇ, ನೀವು ಇಸಾಕನಂತೆ ವಾಗ್ದಾನದ ಫಲವಾಗಿ ಹುಟ್ಟಿದ ಮಕ್ಕಳು.",
			"29": "<sup>29</sup> ಅಂದು ಪ್ರಕೃತಿಸಹಜವಾಗಿ ಹುಟ್ಟಿದವನು ದೇವರಾತ್ಮದಿಂದ ಹುಟ್ಟಿದವನನ್ನು ಹಿಂಸೆಪಡಿಸಿದಂತೆ ಇಂದೂ ನಡೆಯುತ್ತದೆ.",
			"30": "<sup>30</sup> ಪವಿತ್ರಗ್ರಂಥ ಹೇಳುವುದಾದರೂ ಏನು? “ದಾಸಿಯನ್ನೂ ಅವಳ ಮಗನನ್ನೂ ಹೊರಕ್ಕೆ ದೂಡು; ದಾಸಿಯ ಮಗನು ಧರ್ಮಪತ್ನಿಯ ಮಗನೊಂದಿಗೆ ಪಾಲುಗಾರನಾಗಕೂಡದು.” ಎಂದಲ್ಲವೇ?",
			"31": "<sup>31</sup> ಹೌದು, ಪ್ರಿಯ ಸಹೋದರರೇ, ನಾವು ದಾಸಿಯ ಮಕ್ಕಳಲ್ಲ, ಧರ್ಮಪತ್ನಿಯ ಮಕ್ಕಳು."
		},
		"5": {
			"1": "<sup>1</sup> ನಾವು ಮುಕ್ತ ಸ್ವಾತಂತ್ರ್ಯದಿಂದ ಬಾಳಬೇಕೆಂದು ಕ್ರಿಸ್ತಯೇಸು ನಮ್ಮನ್ನು ಬಿಡುಗಡೆಮಾಡಿದರು. ಆದ್ದರಿಂದ ದೃಢವಾಗಿರಿ. ಗುಲಾಮಗಿರಿಯ ನೊಗಕ್ಕೆ ಪುನಃ ಕೊರಳನ್ನು ಒಡ್ಡದಿರಿ.",
			"2": "<sup>2</sup> ಪೌಲನಾದ ನಾನು ಹೇಳುವುದನ್ನು ಕೇಳಿ: ನೀವು ಸುನ್ನತಿ ಮಾಡಿಸಿಕೊಂಡರೆ ಕ್ರಿಸ್ತಯೇಸುವಿನಿಂದ ನಿಮಗೆ ಯಾವ ಪ್ರಯೋಜನವೂ ಆಗುವುದಿಲ್ಲ.",
			"3": "<sup>3</sup> ಸುನ್ನತಿಮಾಡಿಸಿಕೊಳ್ಳುವ ಪ್ರತಿಯೊಬ್ಬನೂ ಇಡೀ ಧರ್ಮಶಾಸ್ತ್ರದ ವಿಧಿನಿಯಮಗಳನ್ನು ಅನುಸರಿಸಲು ಬದ್ಧನಾಗಿದ್ದಾನೆ ಎಂದು ಪುನಃ ನಿಮಗೆ ನಾನು ಒತ್ತಿ ಹೇಳುತ್ತೇನೆ.",
			"4": "<sup>4</sup> ಧರ್ಮಶಾಸ್ತ್ರದ ವಿಧಿನಿಯಮಗಳನ್ನು ಅನುಸರಿಸುವುದರ ಮೂಲಕವೇ ದೇವರೊಡನೆ ಸತ್ಸಂಬಂಧ ಪಡೆಯಬೇಕೆಂದಿರುವ ನೀವು ಕ್ರಿಸ್ತಯೇಸುವಿನಿಂದ ಬೇರ್ಪಟ್ಟಿದ್ದೀರಿ. ದೈವಾನುಗ್ರಹದಿಂದ ದೂರವಾಗಿದ್ದೀರಿ.",
			"5": "<sup>5</sup> ನಾವಾದರೋ ಪವಿತ್ರಾತ್ಮರ ಮುಖಾಂತರ ವಿಶ್ವಾಸದಿಂದ ದೇವರೊಂದಿಗೆ ಸತ್ಸಂಬಂಧವನ್ನು ಹೊಂದುತ್ತೇವೆಂಬ ನಿರೀಕ್ಷೆ ಉಳ್ಳವರಾಗಿದ್ದೇವೆ; ಈ ನಿರೀಕ್ಷೆ ಸಫಲವಾಗುವುದನ್ನು ಎದುರುನೋಡುತ್ತಿದ್ದೇವೆ.",
			"6": "<sup>6</sup> ಕ್ರಿಸ್ತಯೇಸುವಿನಲ್ಲಿ ಬಾಳುವವರಿಗೆ ಸುನ್ನತಿ ಮಾಡಿಸಿಕೊಳ್ಳುವುದೋ ಅಥವಾ ಮಾಡಿಸಿಕೊಳ್ಳದಿರುವುದೋ ಮುಖ್ಯವಲ್ಲ. ಪ್ರೀತಿಯಿಂದ ಕಾರ್ಯ ಎಸಗುವ ವಿಶ್ವಾಸವೇ ಪ್ರಮುಖವಾದುದು.",
			"7": "<sup>7</sup> ಇದುವರೆಗೂ ನಿಮ್ಮ ಬದುಕಿನ ಓಟ ಚೆನ್ನಾಗಿ ಸಾಗುತ್ತಿತ್ತು. ಸತ್ಯಕ್ಕೆ ಶರಣರಾಗದಂತೆ ಈಗ ನಿಮ್ಮನ್ನು ತಡೆದವರು ಯಾರು?",
			"8": "<sup>8</sup> ಹಾಗೆ ಅಡ್ಡಿಮಾಡಿದವರು, ನಿಮ್ಮನ್ನು ಕರೆದ ದೇವರೇನೂ ಅಲ್ಲ.",
			"9": "<sup>9</sup> ಒಂದಿಷ್ಟು ಹುಳಿ ಸಾಕು, ಹಿಟ್ಟನ್ನೆಲ್ಲಾ ಹುದುಗೆಬ್ಬಿಸಲು!",
			"10": "<sup>10</sup> ಭಿನ್ನಾಭಿಪ್ರಾಯಗಳಿಗೆ ನೀವು ಮಣಿಯುವುದಿಲ್ಲವೆಂದು ನಿಮ್ಮನ್ನು ಕುರಿತು ಪ್ರಭುವಿನಲ್ಲಿ ನನಗೆ ದೃಢವಾದ ನಂಬಿಕೆ ಇದೆ. ನಿಮ್ಮಲ್ಲಿ ಗೊಂದಲ ಎಬ್ಬಿಸುವವನು ಯಾರೇ ಆಗಿರಲಿ, ಅವನು ದೈವದಂಡನೆಗೆ ಗುರಿಯಾಗುತ್ತಾನೆ.",
			"11": "<sup>11</sup> ಸಹೋದರರೇ, ಸುನ್ನತಿಮಾಡಿಸಿಕೊಳ್ಳುವುದು ಅವಶ್ಯಕವೆಂದು ನಾನು ಇನ್ನೂ ಬೋಧಿಸುತ್ತಿರುವುದಾಗಿ ಕೆಲವರು ಹೇಳುತ್ತಿದ್ದಾರಲ್ಲವೇ? ಹಾಗೆ ಬೋಧಿಸುತ್ತಿರುವುದಾದರೆ ನಾನೇಕೆ ಹೀಗೆ ಹಿಂಸೆಬಾಧೆಗಳನ್ನು ಅನುಭವಿಸಬೇಕಾಗುತ್ತಿತ್ತು? ನಾನು ಹಾಗೇನಾದರೂ ಬೋಧಿಸಿದ್ದರೆ, ಶಿಲುಬೆಯ ಸಂದೇಶದಿಂದ ಯಾವ ಅಡ್ಡಿ ಆತಂಕವೂ ಉಂಟಾಗುತ್ತಿರಲಿಲ್ಲ.",
			"12": "<sup>12</sup> ನಿಮಗೆ ಕಿರುಕುಳ ಕೊಡುತ್ತಿರುವವರು ತಮ್ಮ ಅಂಗಚ್ಛೇದವನ್ನಾದರೂ ಮಾಡಿಕೊಂಡರೆ ಲೇಸು.",
			"13": "<sup>13</sup> ಸಹೋದರರೇ, ನೀವು ಮುಕ್ತ ಸ್ವತಂತ್ರ ಜೀವನವನ್ನು ನಡೆಸಬೇಕೆಂದು ದೇವರು ನಿಮ್ಮನ್ನು ಕರೆದಿದ್ದಾರೆ. ಆದರೆ, ಆ ಸ್ವಾತಂತ್ರ್ಯವನ್ನು ದೈಹಿಕ ಬಯಕೆಗಳಿಗೆ ಸಾಧನವಾಗಿ ಮಾಡಿಕೊಳ್ಳಬೇಡಿ. ಬದಲಾಗಿ, ನೀವು ಒಬ್ಬರಿಗೊಬ್ಬರು ಪ್ರೀತಿಯಿಂದ ಸೇವೆಮಾಡಿರಿ.",
			"14": "<sup>14</sup> “ನೀನು ನಿನ್ನನ್ನೇ ಪ್ರೀತಿಸಿಕೊಳ್ಳುವಂತೆ ನಿನ್ನ ನೆರೆಯವರನ್ನು ಪ್ರೀತಿಸು,” ಎಂಬ ಒಂದೇ ವಚನದಲ್ಲಿ ಇಡೀ ಧರ್ಮಶಾಸ್ತ್ರವು ಅಡಗಿದೆ.",
			"15": "<sup>15</sup> ಆದರೆ ನೀವು ನಿಮ್ಮ ಕಚ್ಚಾಟ, ಕಿತ್ತಾಟ, ನುಂಗಾಟ - ಇವುಗಳನ್ನು ನಿಲ್ಲಿಸದಿದ್ದರೆ ಒಬ್ಬರಿಂದೊಬ್ಬರು ವಿನಾಶವಾದೀರಿ ಎಚ್ಚರಿಕೆ!",
			"16": "<sup>16</sup> ನಾನು ಹೇಳುವುದೇನೆಂದರೆ: ನೀವು ಪವಿತ್ರಾತ್ಮರ ಪ್ರೇರಣೆಗನುಸಾರವಾಗಿ ನಡೆದುಕೊಳ್ಳಬೇಕು. ಆಗ, ದೈಹಿಕ ವ್ಯಾಮೋಹಕ್ಕೆ ನೀವು ಈಡಾಗಲಾರಿರಿ.",
			"17": "<sup>17</sup> ಏಕೆಂದರೆ, ದೈಹಿಕ ವ್ಯಾಮೋಹವು ಪವಿತ್ರಾತ್ಮರಿಗೆ ವಿರುದ್ಧವಾಗಿದೆ. ಪವಿತ್ರಾತ್ಮ ಆ ವ್ಯಾಮೋಹಕ್ಕೆ ವಿರುದ್ಧವಾಗಿದ್ದಾರೆ. ಇವೆರಡೂ ಒಂದಕ್ಕೊಂದು ಕಾದಾಡುವುದರಿಂದ ನಿಮ್ಮ ಬಯಕೆ ಕೈಗೂಡುವುದಿಲ್ಲ.",
			"18": "<sup>18</sup> ನೀವು ಪವಿತ್ರಾತ್ಮ ನಡೆಸುವ ಮಾರ್ಗದಲ್ಲಿ ನಡೆಯುವವರಾದರೆ, ಧರ್ಮಶಾಸ್ತ್ರದ ವಿಧಿನಿಯಮಗಳಿಗೆ ನೀವು ಅಧೀನರಲ್ಲ.",
			"19": "<sup>19</sup> ದೈಹಿಕ ವ್ಯಾಮೋಹದ ದುಷ್ಪರಿಣಾಮಗಳು ತಿಳಿದೇ ಇವೆ: ಹಾದರ, ಅಶುದ್ಧತೆ, ಕಾಮುಕತನ,",
			"20": "<sup>20</sup> ವಿಗ್ರಹಾರಾಧನೆ, ಮಾಟಮಂತ್ರ, ಹಗೆತನ, ಜಗಳ, ಹೊಟ್ಟೆಕಿಚ್ಚು, ಕ್ರೋಧ, ಸ್ವಾರ್ಥಭೇದ, ಪಕ್ಷಪಾತ, ಮತ್ಸರ, ಕುಡಿಕತನ, ದುಂದೌತಣ - ಇತ್ಯಾದಿಗಳು.",
			"21": "<sup>21</sup> ಇಂಥ ಕೃತ್ಯಗಳನ್ನು ಮಾಡುವವರು ದೇವರ ಸಾಮ್ರಾಜ್ಯಕ್ಕೆ ಬಾಧ್ಯರಲ್ಲ ಎಂದು ಈಗಾಗಲೇ ನಿಮ್ಮನ್ನು ಎಚ್ಚರಿಸಿದ್ದೇನೆ; ಈಗಲೂ ಎಚ್ಚರಿಸುತ್ತೇನೆ.",
			"22": "<sup>22</sup> ಪವಿತ್ರಾತ್ಮದತ್ತವಾದ ಸತ್ಫಲಗಳು ಯಾವುವೆಂದರೆ: ಪ್ರೀತಿ, ಆನಂದ, ಶಾಂತಿಸಮಾಧಾನ, ಸಹನೆ, ದಯೆ, ಸದ್ಗುಣ, ಪ್ರಾಮಾಣಿಕತೆ, ಸೌಭಾಗ್ಯ, ಸಂಯಮ - ಇಂಥವುಗಳೇ.",
			"23": "<sup>23</sup> ಇವುಗಳನ್ನು ಯಾವ ಧರ್ಮಶಾಸ್ತ್ರವೂ ಆಕ್ಷೇಪಿಸುವುದಿಲ್ಲ.",
			"24": "<sup>24</sup> ಕ್ರಿಸ್ತಯೇಸುವಿಗೆ ಸೇರಿದ ಎಲ್ಲರೂ ತಮ್ಮ ದೈಹಿಕ ವ್ಯಾಮೋಹವನ್ನು ಅದರ ಆಶಾಪಾಶಗಳ ಹಾಗೂ ದುರಿಚ್ಛೆಗಳ ಸಮೇತ ಶಿಲುಬೆಗೆ ಜಡಿದುಬಿಟ್ಟಿದ್ದಾರೆ.",
			"25": "<sup>25</sup> ಪವಿತ್ರಾತ್ಮ ನಮ್ಮ ಜೀವಾಳವಾಗಿದ್ದರೆ, ಅವರೇ ನಮ್ಮ ಜೀವನದ ಮಾರ್ಗದರ್ಶಿಯೂ ಆಗಿರಬೇಕು.",
			"26": "<sup>26</sup> ನಾವು ಅಹಂಕಾರಿಗಳಾಗಿರಬಾರದು; ಒಬ್ಬರನ್ನೊಬ್ಬರು ಕೆಣಕಬಾರದು; ಒಬ್ಬರ ಮೇಲೊಬ್ಬರು ಹೊಟ್ಟೆಕಿಚ್ಚು ಪಡಬಾರದು."
		},
		"6": {
			"1": "<sup>1</sup> ಸಹೋದರರೇ, ನಿಮ್ಮಲ್ಲಿ ಯಾರಾದರೂ ತಪ್ಪಿತಸ್ಥರಾಗಿದ್ದರೆ, ಆಧ್ಯಾತ್ಮಿಕ ಜೀವಿಗಳಾದ ನೀವು ಅಂಥವನನ್ನು ನಯವಿನಯದಿಂದ ತಿದ್ದಿ ಸರಿಯಾದ ದಾರಿಗೆ ತರಬೇಕು. ಆದರೆ ನೀನೂ ಅವನಂತೆ ಪಾಪಪ್ರಚೋದನೆಗೆ ಒಳಗಾಗದಂತೆ ಎಚ್ಚರಿಕೆಯಿಂದಿರಬೇಕು.",
			"2": "<sup>2</sup> ಒಬ್ಬರ ಭಾರವನ್ನು ಒಬ್ಬರು ಹೊತ್ತುಕೊಳ್ಳಿ. ಹೀಗೆ ಕ್ರಿಸ್ತಯೇಸುವಿನ ನಿಯಮವನ್ನು ನೆರವೇರಿಸುವಿರಿ.",
			"3": "<sup>3</sup> ಅಲ್ಪನು ತಾನು ಶ್ರೇಷ್ಠನೆಂದು ಭಾವಿಸಿಕೊಂಡರೆ ತನ್ನನ್ನು ತಾನೇ ವಂಚಿಸಿಕೊಳ್ಳುತ್ತಾನೆ, ಅಷ್ಟೆ.",
			"4": "<sup>4</sup> ಪ್ರತಿಯೊಬ್ಬನೂ ತನ್ನ ನಡತೆಯನ್ನು ತನ್ನಷ್ಟಕ್ಕೆ ತಾನೇ ಪರಿಶೋಧಿಸಿಕೊಳ್ಳಲಿ. ತನ್ನ ನಡತೆ ಒಳಿತಾಗಿದ್ದರೆ ಹೆಮ್ಮೆಪಡಲಿ. ಆದರೆ ಇತರರೊಂದಿಗೆ ಹೋಲಿಸಿ ಹೆಮ್ಮೆಪಡಬೇಕಾಗಿಲ್ಲ.",
			"5": "<sup>5</sup> ಪ್ರತಿಯೊಬ್ಬನೂ ತನ್ನ ಹೊರೆಯನ್ನು ತಾನೇ ಹೊತ್ತುಕೊಳ್ಳಬೇಕು.",
			"6": "<sup>6</sup> ದೇವರ ವಾಕ್ಯದ ಉಪದೇಶ ಪಡೆಯುವವನು ಉಪದೇಶ ಮಾಡುವವನೊಂದಿಗೆ ತನಗೆ ಇರುವ ಒಳ್ಳೆಯದನ್ನೆಲ್ಲಾ ಹಂಚಿಕೊಳ್ಳಲಿ.",
			"7": "<sup>7</sup> ಮೋಸಹೋಗದಿರಿ, ದೇವರನ್ನು ವಂಚಿಸಬಹುದೆಂದು ಭಾವಿಸದಿರಿ; ಬಿತ್ತುವುದನ್ನೇ ಕೊಯ್ಯುತ್ತೇವೆ ಎಂಬುದನ್ನು ನೆನಪಿನಲ್ಲಿಡಿ.",
			"8": "<sup>8</sup> ದೈಹಿಕ ವ್ಯಾಮೋಹವೆಂಬ ಸ್ವಭಾವದಲ್ಲಿ ಬಿತ್ತುವವನು ನಾಶವೆಂಬ ಫಲವನ್ನು ಕೊಯ್ಯುತ್ತಾನೆ. ಪವಿತ್ರಾತ್ಮ ಸ್ವಭಾವದಲ್ಲಿ ಬಿತ್ತುವವನು ಆತ್ಮದಿಂದ ನಿತ್ಯಜೀವವೆಂಬ ಫಲವನ್ನು ಕೊಯ್ಯುತ್ತಾನೆ.",
			"9": "<sup>9</sup> ಸತ್ಕಾರ್ಯಗಳನ್ನು ಮಾಡುವುದರಲ್ಲಿ ಬೇಸರಪಡದಿರೋಣ, ಎದೆಗುಂದದಿರೋಣ; ಆಗ ಸೂಕ್ತಕಾಲದಲ್ಲಿ ಸತ್ಫಲವನ್ನು ಕೊಯ್ಯುವೆವು.",
			"10": "<sup>10</sup> ಎಂದೇ ಸಮಯಸಂದರ್ಭಗಳು ಇರುವಾಗಲೇ ಸರ್ವರಿಗೂ ಉಪಕಾರಮಾಡೋಣ. ಅದರಲ್ಲೂ ಮುಖ್ಯವಾಗಿ, ಒಂದೇ ಕುಟುಂಬದವರಂತೆ ಇರುವ ಕ್ರೈಸ್ತವಿಶ್ವಾಸಿಗಳಿಗೆ ಉಪಕಾರ ಮಾಡೋಣ.",
			"11": "<sup>11</sup> ಈ ದೊಡ್ಡದೊಡ್ಡ ಅಕ್ಷರಗಳನ್ನು ನೋಡಿರಿ; ಇವನ್ನು ನನ್ನ ಸ್ವಂತ ಕೈಯಿಂದಲೇ ಬರೆಯುತ್ತಿದ್ದೇನೆ.",
			"12": "<sup>12</sup> ಬಾಹ್ಯಾಚಾರಗಳಿಂದ ತಾವು ಒಳ್ಳೆಯವರು ಎನಿಸಿಕೊಳ್ಳಬೇಕೆನ್ನುವವರು ನೀವು ಸುನ್ನತಿಮಾಡಿಸಿಕೊಳ್ಳಬೇಕೆಂದು ಬಲಾತ್ಕಾರ ಮಾಡುತ್ತಿದ್ದಾರೆ. ಕ್ರಿಸ್ತಯೇಸುವಿನ ಶಿಲುಬೆಯ ನಿಮಿತ್ತ ತಮಗೆ ಹಿಂಸೆಬಾಧೆಗಳು ಉಂಟಾಗಬಾರದು ಎಂಬುದೇ ಅವರ ಉದ್ದೇಶವಾಗಿದೆ.",
			"13": "<sup>13</sup> ಸುನ್ನತಿಮಾಡಿಸಿಕೊಳ್ಳುವ ತಾವಾದರೂ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಅನುಸರಿಸಿ ನಡೆಯುವುದಿಲ್ಲ. ಆದರೆ ನಿಮ್ಮ ದೈಹಿಕ ಸುನ್ನತಿಯಿಂದ, ಅವರು ಹೆಚ್ಚಳಿಕೆ ಗಳಿಸುವುದಕ್ಕಾಗಿ ನಿಮಗೆ ಸುನ್ನತಿಯಾಗಬೇಕೆಂದು ಬಯಸುತ್ತಾರೆ.",
			"14": "<sup>14</sup> ನನಗಾದರೋ ನಮ್ಮ ಪ್ರಭು ಯೇಸುಕ್ರಿಸ್ತರ ಶಿಲುಬೆಯನ್ನು ಬಿಟ್ಟು ಬೇರಾವುದರಲ್ಲೂ ಹೆಚ್ಚಳಪಡುವುದು ಬೇಡವೇ ಬೇಡ. ಆ ಶಿಲುಬೆಯ ಮೂಲಕ. ನನ್ನ ಪಾಲಿಗೆ ಲೋಕವೇ ಶಿಲುಬೆಗೆ ಜಡಿಸಿಕೊಂಡು ಸತ್ತಿದೆ; ನಾನೂ ಸಹ ಲೋಕದ ಪಾಲಿಗೆ ಶಿಲುಬೆಗೆ ಜಡಿಸಿಕೊಂಡು ಸತ್ತಿದ್ದೇನೆ.",
			"15": "<sup>15</sup> ಸುನ್ನತಿ ಮಾಡಿಸಿಕೊಳ್ಳುವುದೋ, ಮಾಡಿಸಿಕೊಳ್ಳದಿರುವುದೋ ಮಹತ್ತಾದುದಲ್ಲ. ಹೊಸ ಸೃಷ್ಟಿಯಾಗುವುದೇ ಮಹತ್ತರವಾದುದು.",
			"16": "<sup>16</sup> ಈ ನಿಯಮವನ್ನು ಅನುಸರಿಸುವ ಎಲ್ಲರಿಗೂ, ಅಂದರೆ ನಿಜ ಇಸ್ರಯೇಲರಾದ ದೇವಜನರೆಲ್ಲರಿಗೂ ಶಾಂತಿಸಮಾಧಾನವೂ ಕೃಪಾಶೀರ್ವಾದವೂ ಲಭಿಸಲಿ!",
			"17": "<sup>17</sup> ಇನ್ನು ಮೇಲೆ ಯಾರೂ ನನಗೆ ತೊಂದರೆ ಕೊಡದಿರಲಿ. ಯೇಸುವಿನ ದಾಸನೆಂದು ಸೂಚಿಸುವ ಕೆಂಗುರುತುಗಳು ನನ್ನ ದೇಹದಲ್ಲಿ ಮುದ್ರಿತವಾಗಿವೆ.",
			"18": "<sup>18</sup> ಸಹೋದರರೇ, ನಮ್ಮ ಪ್ರಭು ಯೇಸುಕ್ರಿಸ್ತರ ಅನುಗ್ರಹ ನಿಮ್ಮೊಡನೆ ಇರಲಿ! ಆಮೆನ್."
		}
	}
};
module.exports = book;