var book = {
	"name": "ಫಿಲಿಪ್ಪಿಯರಿಗೆ",
	"numChapters": 4,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ಕ್ರಿಸ್ತಯೇಸುವಿನಲ್ಲಿ ಜೀವಿಸುವ ಫಿಲಿಪ್ಪಿಯ ದೇವಜನರಿಗೆ, ಧರ್ಮಾಧಿಕಾರಿಗಳಿಗೆ ಹಾಗೂ ಧರ್ಮಸೇವಕರಿಗೆ - ಕ್ರಿಸ್ತಯೇಸುವಿನ ದಾಸರಾದ ಪೌಲ ಮತ್ತು ತಿಮೊಥೇಯರು ಜೊತೆಗೂಡಿ ಬರೆಯುವ ಪತ್ರ.",
			"2": "<sup>2</sup> ನಮ್ಮೆಲ್ಲರ ಪಿತನಾದ ದೇವರ ಮತ್ತು ಪ್ರಭುವಾದ ಯೇಸುಕ್ರಿಸ್ತರ ಆಶೀರ್ವಾದವೂ ಶಾಂತಿಸಮಾಧಾನವೂ ನಿಮಗೆ ಲಭಿಸಲಿ!",
			"3": "<sup>3</sup> ನಿಮ್ಮನ್ನು ಜ್ಞಾಪಿಸಿಕೊಳ್ಳುವಾಗಲೆಲ್ಲ ನಾನು ದೇವರಿಗೆ ಕೃತಜ್ಞತೆಯನ್ನು ಸಲ್ಲಿಸುತ್ತೇನೆ.",
			"4": "<sup>4</sup> ನಿಮಗೋಸ್ಕರ ಯಾವಾಗಲೂ ತುಂಬುಹೃದಯದಿಂದ ಪ್ರಾರ್ಥಿಸುತ್ತೇನೆ.",
			"5": "<sup>5</sup> ಏಕೆಂದರೆ, ನಾನು ನಿಮ್ಮನ್ನು ಸಂಧಿಸಿದ ಮೊದಲ ದಿನದಿಂದ ಇಂದಿನವರೆಗೂ ನೀವು ನನ್ನೊಡನೆ ಶುಭಸಂದೇಶದ ಪ್ರಚಾರಕಾರ್ಯದಲ್ಲಿ ಸಹಕರಿಸುತ್ತಿರುವಿರಿ.",
			"6": "<sup>6</sup> ಈ ಸತ್ಕಾರ್ಯವನ್ನು ನಿಮ್ಮಲ್ಲಿ ಪ್ರಾರಂಭಿಸಿದ ದೇವರು ಅದನ್ನು ಮುಂದುವರೆಸುತ್ತಾ, ಯೇಸುಕ್ರಿಸ್ತರು ಪುನರಾಗಮಿಸುವಷ್ಟರಲ್ಲಿ ಪೂರೈಸುವರು ಎಂಬುದೇ ನನ್ನ ದೃಢ ನಂಬಿಕೆ.",
			"7": "<sup>7</sup> ನೀವು ನನಗೆ ಆತ್ಮೀಯರು. ಆದಕಾರಣ, ನಿಮ್ಮೆಲ್ಲರನ್ನು ಕುರಿತು ನನಗೆ ಹೀಗನ್ನಿಸುವುದು ಸೂಕ್ತವೇ ಸರಿ. ನಾನು ಸೆರೆಯಲ್ಲಿರುವಾಗಲೂ ಶುಭಸಂದೇಶಕ್ಕಾಗಿ ಹೋರಾಡಿ ಆದನ್ನು ಸ್ಥಿರಗೊಳಿಸುವಾಗಲೂ ದೇವರ ಅನುಗ್ರಹದಲ್ಲಿ ನೀವು ನನ್ನೊಂದಿಗೆ ಸಹಭಾಗಿಗಳಾಗಿದ್ದಿರಿ.",
			"8": "<sup>8</sup> ಕ್ರಿಸ್ತಯೇಸುವಿನಲ್ಲಿರುವ ಉತ್ಕಟ ಪ್ರೀತಿಯಿಂದಲೇ ನಾನು ನಿಮ್ಮೆಲ್ಲರಿಗಾಗಿ ಹಂಬಲಿಸುತ್ತಿದ್ದೇನೆ; ಇದಕ್ಕೆ ದೇವರೇ ಸಾಕ್ಷಿ.",
			"9": "<sup>9</sup> ನೀವು ಪ್ರೀತಿಯಲ್ಲಿ ಪ್ರವರ್ಧಿಸುತ್ತಾ, ಪೂರ್ಣ ಜ್ಞಾನ ಹಾಗೂ ವಿವೇಕದಿಂದ ಕೂಡಿದವರಾಗಬೇಕೆಂಬುದೇ ನನ್ನ ಪ್ರಾರ್ಥನೆ.",
			"10": "<sup>10</sup> ಹೀಗೆ ನೀವು ಉತ್ತಮೋತ್ತಮವಾದುವುಗಳನ್ನೇ ಆರಿಸಿಕೊಂಡು, ಪ್ರಭು ಕ್ರಿಸ್ತರ ದಿನದಂದು ನಿರ್ದೋಷಿಗಳೂ ನಿಷ್ಕಳಂಕರೂ ಆಗಿ ಕಾಣಿಸಿಕೊಳ್ಳುವಿರಿ.",
			"11": "<sup>11</sup> ಯೇಸುಕ್ರಿಸ್ತರ ಮುಖಾಂತರ ಲಭಿಸುವ ಸತ್ಸಂಬಂಧದ ಫಲಗಳಿಂದ ತುಂಬಿದವರಾಗಿ ದೇವರಿಗೆ ಸ್ತುತಿಸಲ್ಲಿಸಿ, ಅವರ ಮಹಿಮೆ ಬೆಳಗುವಂತೆ ಮಾಡುವಿರಿ.",
			"12": "<sup>12</sup> ಸಹೋದರರೇ, ನನಗೆ ಬಂದೊದಗಿರುವುದೆಲ್ಲವೂ ಶುಭಸಂದೇಶದ ಬೆಳವಣಿಗೆಗೆ ಹಿತಕರವಾಗಿ ಪರಿಣಮಿಸಿದೆ. ಇದು ನಿಮಗೆ ತಿಳಿದಿರಬೇಕೆಂಬುದೇ ನನ್ನ ಆಶಯ.",
			"13": "<sup>13</sup> ಯೇಸುಕ್ರಿಸ್ತರ ಸಲುವಾಗಿಯೇ ನಾನು ಸೆರೆಯಲ್ಲಿರುವುದೆಂದು ಇಲ್ಲಿಯ ಅರಮನೆಯ ಕಾವಲಾಳುಗಳಿಗೂ ಇತರರೆಲ್ಲರಿಗೂ ಚೆನ್ನಾಗಿ ತಿಳಿದಿದೆ.",
			"14": "<sup>14</sup> ನನ್ನ ಬಂಧನದಿಂದಾಗಿ ನಮ್ಮ ಸಹೋದರರಲ್ಲಿ ಅನೇಕರು ದೇವರಲ್ಲಿ ಭರವಸೆಯಿಟ್ಟಿರುವರು. ಅಳುಕಿಲ್ಲದೆ, ಅಂಜಿಕೆಯಿಲ್ಲದೆ ದಿಟ್ಟತನದಿಂದ ಶುಭಸಂದೇಶವನ್ನು ಅವರು ಸಾರುತ್ತಿರುವರು.",
			"15": "<sup>15</sup> ಕೆಲವರೇನೋ ಅಸೂಯೆಯಿಂದಲೂ ವೈಮನಸ್ಸಿನಿಂದಲೂ ಕ್ರಿಸ್ತಯೇಸುವನ್ನು ಸಾರುತ್ತಿರುವುದು ನಿಜ; ಆದರೆ ಮಿಕ್ಕವರು ಸದುದ್ದೇಶದಿಂದ ಸಾರುತ್ತಿದ್ದಾರೆ;",
			"16": "<sup>16</sup> ಒಲವಿನ ಒತ್ತಾಯದಿಂದ ಸಾರುತ್ತಿದ್ದಾರೆ; ಶುಭಸಂದೇಶದ ಸತ್ಯವನ್ನು ಸಮರ್ಥಿಸುವುದಕ್ಕಾಗಿ ನಾನು ಬಂಧಿತನೆಂದು ಇವರು ಬಲ್ಲರು.",
			"17": "<sup>17</sup> ಆ ಕೆಲವರಾದರೋ ಸ್ವಾರ್ಥಸಾಧನೆಗಾಗಿಯೇ ಹೊರತು ಪ್ರಾಮಾಣಿಕರಾಗಿ ಕ್ರಿಸ್ತಯೇಸುವನ್ನು ಸಾರುತ್ತಿಲ್ಲ. ಸೆರೆಯಲ್ಲಿರುವ ನನ್ನನ್ನು ಇನ್ನೂ ಹೆಚ್ಚಿನ ಕಷ್ಟಕ್ಕೆ ಗುರಿಮಾಡಬೇಕೆಂಬುದೇ ಅವರ ಉದ್ದೇಶ.",
			"18": "<sup>18</sup> ಅವರ ಉದ್ದೇಶ ಏನೇ ಆಗಿರಲಿ, ನನಗದು ಮುಖ್ಯವಲ್ಲ. ಕ್ರಿಸ್ತಯೇಸುವನ್ನು ಅವರು ಸಾರುತ್ತಿರುವುದೇ ನನಗೆ ಸಂತೋಷ.",
			"19": "<sup>19</sup> ಹೌದು, ನನಗದು ತುಂಬಾ ಸಂತೋಷದ ವಿಷಯ. ನಿಮ್ಮ ಪ್ರಾರ್ಥನಾ ಫಲದಿಂದಲೂ ಯೇಸುಕ್ರಿಸ್ತರು ಕೊಡುವ ಆತ್ಮದ ನೆರವಿನಿಂದಲೂ ನನಗೆ ಬಿಡುಗಡೆ ಖಚಿತವೆಂದು ಬಲ್ಲೆ.",
			"20": "<sup>20</sup> ನನ್ನ ಕರ್ತವ್ಯಪಾಲನೆಯಲ್ಲಿ ನಾಚುವಂಥ ಸಂದರ್ಭವು ನನಗೆಂದಿಗೂ ಬರದೆಂದು ಬಲ್ಲೆ; ನಾನು ಬದುಕಿದರೂ ಸರಿ, ಸತ್ತರೂ ಸರಿ; ನನ್ನ ದೇಹದ ಮೂಲಕ ಕ್ರಿಸ್ತಯೇಸುವಿಗೆ ಎಂದಿನಂತೆ ಈಗಲೂ ಮಹಿಮೆ ಉಂಟಾಗಬೇಕೆಂಬುದೇ ನನ್ನ ಉತ್ಕಟ ಆಕಾಂಕ್ಷೆ ಹಾಗೂ ನಿರೀಕ್ಷೆ.",
			"21": "<sup>21</sup> ನನಗಂತೂ ಬದುಕುವುದೆಂದರೆ ಕ್ರಿಸ್ತಯೇಸುವೇ; ಸಾಯುವುದು ಲಾಭವೇ.",
			"22": "<sup>22</sup> ದೈಹಿಕವಾಗಿ ಬದುಕುವುದು ಫಲಪ್ರದವಾಗಿದ್ದರೆ, ನಾನು ಬದುಕುವುದು ಉತ್ತಮವೋ ಅಥವಾ ಸಾಯುವುದು ಸೂಕ್ತವೋ ನನಗೆ ತಿಳಿಯದು.",
			"23": "<sup>23</sup> ಉಭಯ ಸಂಕಟಕ್ಕೆ ಸಿಲುಕಿದ್ದೇನೆ. ಒಂದು ಕಡೆ, ಈ ಬದುಕನ್ನು ತೊರೆದು ಕ್ರಿಸ್ತಯೇಸುವಿನೊಡನೆ ಒಂದಾಗಿರಬೇಕೆಂಬುದೇ ನನ್ನ ಬಯಕೆ. ಇದು ಎಷ್ಟೋ ಮೇಲಾದುದು.",
			"24": "<sup>24</sup> ಮತ್ತೊಂದು ಕಡೆ, ನಿಮಗೋಸ್ಕರ ಇಹದಲ್ಲಿದ್ದು ಜೀವಿಸುವುದು ಅತ್ಯವಶ್ಯಕವಾಗಿದೆ.",
			"25": "<sup>25</sup> ಇದರಲ್ಲಿ ನನಗೆ ಎಳ್ಳಷ್ಟೂ ಸಂದೇಹವಿಲ್ಲ. ಈ ಕಾರಣ, ನಾನು ಇಹದಲ್ಲಿ ಉಳಿಯುವೆನೆಂದು ಬಲ್ಲೆ. ನಿಮ್ಮೆಲ್ಲರೊಡನಿದ್ದು, ಕ್ರೈಸ್ತವಿಶ್ವಾಸದಲ್ಲಿ ನೀವು ವೃದ್ಧಿಯಾಗಿ ಆನಂದಿಸುವಂತೆ ನಿಮಗೆ ನೆರವಾಗುತ್ತೇನೆ.",
			"26": "<sup>26</sup> ನಿಮ್ಮಲ್ಲಿಗೆ ನಾನು ಮರಳಿಬಂದಾಗ ಕ್ರಿಸ್ತಯೇಸುವಿನಲ್ಲಿ ನೀವು ಹಿರಿಹಿಗ್ಗುವುದಕ್ಕೆ ಮಹದವಕಾಶ ದೊರಕುವುದು.",
			"27": "<sup>27</sup> ಅದೇನೇ ಇರಲಿ, ನೀವು ಮಾತ್ರ ಕ್ರಿಸ್ತಯೇಸುವಿನ ಶುಭಸಂದೇಶಕ್ಕೆ ಯೋಗ್ಯರಾಗಿ ನಡೆದುಕೊಳ್ಳಿರಿ. ನಾನು ನಿಮ್ಮಲ್ಲಿಗೆ ಬಂದರೂ ಸರಿ, ಬಾರದಿದ್ದರೂ ಸರಿ, ಇದು ಮುಖ್ಯವಲ್ಲ. ಶುಭಸಂದೇಶವು ನೀಡುವ ವಿಶ್ವಾಸಕ್ಕಾಗಿ ನೀವು ಒಮ್ಮನಸ್ಸಿನಿಂದ, ಕೆಚ್ಚಿನಿಂದ ಹೋರಾಡುವುದೇ ಮುಖ್ಯ. ಇದು ನನಗೆ ತಿಳಿದುಬಂದರೆ ಅಷ್ಟೇ ಸಾಕು.",
			"28": "<sup>28</sup> ನಿಮ್ಮ ವಿರೋಧಿಗಳಿಗೆ ಯಾವ ವಿಷಯದಲ್ಲೂ ಅಂಜದೆ, ಅಳುಕದೆ ಧೈರ್ಯದಿಂದಿರಿ. ಇದೇ ಅವರಿಗೆ ಅವರ ವಿನಾಶದ ಸ್ಪಷ್ಟ ಮುನ್ಸೂಚನೆ! ನಿಮಗೆ ದೇವರಿಂದ ಬರುವ ಸಂರಕ್ಷಣೆಯ ಮುನ್ಸೂಚನೆ.",
			"29": "<sup>29</sup> ಕ್ರಿಸ್ತಯೇಸುವನ್ನು ನೀವು ವಿಶ್ವಾಸಿಸುವುದು ಮಾತ್ರವಲ್ಲ, ಅವರಿಗೋಸ್ಕರ ಸಂಕಷ್ಟಗಳನ್ನು ಅನುಭವಿಸುವ ಸೌಭಾಗ್ಯವೂ ನಿಮ್ಮದಾಗಿದೆ.",
			"30": "<sup>30</sup> ನಿಮ್ಮ ಹೋರಾಟವೂ ನಮ್ಮ ಹೋರಾಟವೂ ಒಂದೇ ಆಗಿದೆ. ನಾನು ಕ್ರಿಸ್ತಯೇಸುವಿಗಾಗಿ ಪಟ್ಟ ಕಷ್ಟವನ್ನು ನೀವು ಕಂಡಿದ್ದೀರಿ. ಈಗಲೂ ಅನುಭವಿಸುತ್ತಿದ್ದೇನೆಂದು ಅರಿತಿದ್ದೀರಿ."
		},
		"2": {
			"1": "<sup>1</sup> ಕ್ರಿಸ್ತಯೇಸುವಿನಲ್ಲಿ ನಿಮಗೆ ಉತ್ಸಾಹ, ಉತ್ತೇಜನ, ಪ್ರೀತಿ, ಪ್ರೇರಣೆ, ಪವಿತ್ರಾತ್ಮ ಅವರ ಅನ್ಯೋನ್ಯತೆ, ದೀನದಯಾಳತೆ ಇರುವುದಾದರೆ ಐಕಮತ್ಯದಿಂದ ಬಾಳಿರಿ.",
			"2": "<sup>2</sup> ನಿಮ್ಮೆಲ್ಲರಲ್ಲಿ ಒಂದೇ ಮನಸ್ಸು, ಒಂದೇ ಪ್ರೀತಿ ಇರಲಿ. ನಿಮ್ಮ ಗುರಿ ಧ್ಯೇಯಗಳು ಒಂದೇ ಆಗಿರಲಿ. ಆಗ ನನ್ನ ಸಂತೋಷವು ಸಂಪೂರ್ಣಗೊಳ್ಳುವುದು.",
			"3": "<sup>3</sup> ಸ್ವಾರ್ಥಸಾಧನೆಗಾಗಲಿ, ಡಂಭಾಚಾರಕ್ಕಾಗಲಿ ಏನನ್ನೂ ಮಾಡಬೇಡಿ. ಪರಸ್ಪರ ನಮ್ರತೆಯಿಂದ ನಡೆದುಕೊಳ್ಳಿ; ಇತರರು ನಿಮಗಿಂತಲೂ ಶ್ರೇಷ್ಠರೆಂದು ಪರಿಗಣಿಸಿರಿ.",
			"4": "<sup>4</sup> ಸ್ವಹಿತವನ್ನೇ ಗಮನಿಸದೆ ಪರರ ಹಿತವನ್ನೂ ಬಯಸಿರಿ.",
			"5": "<sup>5</sup> ಕ್ರಿಸ್ತಯೇಸುವಿನಲ್ಲಿರುವ ಮನೋಭಾವ ನಿಮ್ಮಲ್ಲೂ ನೆಲಸಿರಲಿ:",
			"6": "<sup>6</sup> ದೇವಸ್ವರೂಪಿ ತಾನಾಗಿದ್ದರೂ ನಿರುತ ದೇವರಿಗೆ ಸಮನಾದ ಆ ಸಿರಿ ಪದವಿಯನಾತ ಹಿಡಿದಿಟ್ಟುಕೊಳ್ಳಲಿಲ್ಲ ಬಿಡಲೊಲ್ಲೆನೆನುತ.",
			"7": "<sup>7</sup> ತನ್ನನ್ನೇ ಬರಿದುಮಾಡಿಕೊಂಡು ದಾಸನ ರೂಪವನು ಧರಿಸಿಕೊಂಡು ಮನುಜನಾಕಾರದಲಿ ಕಾಣಿಸಿಕೊಂಡು ನರಮಾನವರಿಗೆ ಸರಿಸಮನಾದ.",
			"8": "<sup>8</sup> ತನ್ನನ್ನೇ ಆತ ತಗ್ಗಿಸಿಕೊಂಡು ವಿಧೇಯನಾಗಿ ನಡೆದುಕೊಂಡು ಮರಣಪರಿಯಂತ ಹೌದೌದು, ಶಿಲುಬೆಯ ಮರಣಪರಿಯಂತ ವಿಧೇಯನಾದ.",
			"9": "<sup>9</sup> ಎಂತಲೇ ಪರಮೋನ್ನತ ಸ್ಥಾನಕ್ಕೇರಿಸಿದನಾತನನು ಇತ್ತನು ಹೆಸರುಗಳೊಳುತ್ತಮ ಹೆಸರನು ದೇವಪರಮನು.",
			"10": "<sup>10</sup> ಯೇಸುವಿನ ಹೆಸರಿಗೆಂದೇ ಮೊಣಕಾಲೂರಿ ಮಣಿವರು ಸ್ವರ್ಗವಾಸಿಗಳು, ಭೂನಿವಾಸಿಗಳು, ಪಾತಾಳ ಜೀವರಾಶಿಗಳು.",
			"11": "<sup>11</sup> ‘ಕ್ರಿಸ್ತಯೇಸುವೇ ಪ್ರಭು' ಎಂದೆಲ್ಲರಿಗೆ ಅರಿಕೆ ಮಾಡುವರು ಪಿತದೇವನ ಮಹಿಮೆಯನು ಈ ಪರಿ ಹೊಗಳಿ ಸಾರುವರವರು.",
			"12": "<sup>12</sup> ಪ್ರಿಯ ಸಹೋದರರೇ, ನಾನು ನಿಮ್ಮಲ್ಲಿದ್ದಾಗ ನೀವು ಯಾವಾಗಲೂ ನನ್ನ ಮಾತಿನಂತೆ ನಡೆದುಕೊಂಡಿರಿ. ಈಗ, ನಾನು ದೂರವಿರುವಾಗ ಇನ್ನೂ ಹೆಚ್ಚಿನ ಆಸಕ್ತಿಯಿಂದ ನಡೆದುಕೊಳ್ಳಿ. ನಿಮ್ಮ ಜೀವೋದ್ಧಾರಕ್ಕಾಗಿ ಭಯಭಕ್ತಿಯಿಂದ ಶ್ರಮಿಸಿರಿ.",
			"13": "<sup>13</sup> ಏಕೆಂದರೆ ದೈವಚಿತ್ತವನ್ನು ನೀವು ನೆರವೇರಿಸುವಂತೆ ಅವರೇ ನಿಮ್ಮಲ್ಲಿ ಸತ್ಪ್ರೇರಣೆಯನ್ನೂ ಸತ್ಫಲವನ್ನೂ ನೀಡುತ್ತಾರೆ.",
			"14": "<sup>14</sup> ನೀವು ಮಾಡುವ ಎಲ್ಲಾ ಕಾರ್ಯಗಳನ್ನು ಗೊಣಗುಟ್ಟದೆ, ವಿವಾದವಿಲ್ಲದೆ, ಏಕಮನಸ್ಸಿನಿಂದ ಮಾಡಿ.",
			"15": "<sup>15</sup> ನಿರ್ದೋಷಿಗಳೂ ನಿಷ್ಕಳಂಕರೂ ಆಗಿರಿ. ವಕ್ರಬುದ್ಧಿಯ ದುಷ್ಟಜನರ ನಡುವೆ ದೇವರ ಪರಿಶುದ್ಧ ಮಕ್ಕಳಂತೆ ಬಾಳಿರಿ. ಜೀವದಾಯಕ ಸಂದೇಶವನ್ನು ಎತ್ತಿಹಿಡಿಯಿರಿ; ಆಗ ನಕ್ಷತ್ರಗಳು ಆಕಾಶವನ್ನು ಬೆಳಗಿಸುವಂತೆ ಪ್ರಪಂಚದಲ್ಲಿ ನೀವು ಕಂಗೊಳಿಸುವಿರಿ.",
			"16": "<sup>16</sup> ಹೀಗೆ ನೀವು ನಡೆದುಕೊಂಡರೆ ನನ್ನ ಸೇವೆ ವ್ಯರ್ಥವಾಗದೆ, ಶ್ರಮೆ ನಿಷ್ಫಲವಾಗದೆ, ಫಲಪ್ರದವಾಯಿತೆಂದು ಕ್ರಿಸ್ತರ ದಿನದಂದು ನಾನು ಹೆಮ್ಮೆಪಡಬಹುದು.",
			"17": "<sup>17</sup> ನಿಮ್ಮ ವಿಶ್ವಾಸವೆಂಬ ಬಲಿಕಾಣಿಕೆಯ ಮೇಲೆ ನನ್ನ ರಕ್ತವನ್ನು ಧಾರೆಯಾಗಿ ಹರಿಸಬೇಕಾಗಿಬಂದರೂ ನನಗೆ ಸಂತೋಷವೇ; ನಿಮ್ಮೆಲ್ಲರೊಡನೆ ಸೇರಿ ಸಂತೋಷಿಸುತ್ತೇನೆ.",
			"18": "<sup>18</sup> ಅದೇ ರೀತಿ ನೀವು ಸಹ ನನ್ನೊಡನೆ ಸಂತೋಷದಿಂದ ಸಂಭ್ರಮಿಸಿರಿ.",
			"19": "<sup>19</sup> ಪ್ರಭು ಯೇಸುವಿನ ಚಿತ್ತವಾದಲ್ಲಿ ತಿಮೊಥೇಯನನ್ನು ನಿಮ್ಮ ಬಳಿಗೆ ಬೇಗನೆ ಕಳುಹಿಸಬೇಕೆಂದಿದ್ದೇನೆ. ಆತನಿಂದ ನಿಮ್ಮ ಸಮಾಚಾರವನ್ನು ತಿಳಿದು ನಾನು ಹರ್ಷಗೊಳ್ಳುತ್ತೇನೆ.",
			"20": "<sup>20</sup> ಆತನಂತೆ ನಿಷ್ಕಪಟವಾಗಿ ನಿಮ್ಮ ಯೋಗಕ್ಷೇಮವನ್ನು ಬಯಸುವವರು ಬೇರೆ ಯಾರೂ ಇಲ್ಲಿಲ್ಲ.",
			"21": "<sup>21</sup> ಇತರರೆಲ್ಲರೂ ಸ್ವಕಾರ್ಯದಲ್ಲಿ ಮಗ್ನರಾಗಿರುವವರೇ ಹೊರತು ಯೇಸುಸ್ವಾಮಿಯ ಕಾರ್ಯದಲ್ಲಿ ನಿರತರಾಗಿಲ್ಲ.",
			"22": "<sup>22</sup> ತಿಮೊಥೇಯನ ಯೋಗ್ಯತೆಯನ್ನು ನೀವು ಬಲ್ಲಿರಿ. ತಂದೆಯೊಂದಿಗೆ ಮಗನು ದುಡಿಯುವಂತೆ, ಶುಭಸಂದೇಶದ ಸೇವೆಯಲ್ಲಿ ನನ್ನೊಂದಿಗೆ ಆತನು ದುಡಿದಿದ್ದಾನೆ.",
			"23": "<sup>23</sup> ನನ್ನ ಬಗ್ಗೆ ನಡೆಯುತ್ತಿರುವ ವಿಚಾರಣೆಯ ಫಲಿತಾಂಶ ತಿಳಿದ ಕೂಡಲೇ ಆತನನ್ನು ನಿಮ್ಮ ಬಳಿಗೆ ಕಳುಹಿಸುತ್ತೇನೆ.",
			"24": "<sup>24</sup> ಅಲ್ಲದೆ, ದೇವರ ಚಿತ್ತವಿದ್ದರೆ, ನಾನೇ ನಿಮ್ಮಲ್ಲಿಗೆ ಬೇಗನೆ ಬರುವೆನೆಂಬ ದೃಢನಂಬಿಕೆಯೂ ನನಗಿದೆ.",
			"25": "<sup>25</sup> ನನಗೆ ನೆರವಾಗಲು ನೀವು ಕಳುಹಿಸಿದ ಸಹೋದರ ಎಪಫ್ರೋದಿತನು ನಿಮ್ಮಲ್ಲಿಗೆ ಹಿಂದಿರುಗುವುದು ಅವಶ್ಯವೆಂದು ಭಾವಿಸುತ್ತೇನೆ. ಅವನು ನನ್ನೊಂದಿಗೆ ದುಡಿಯುತ್ತಾ, ಹೋರಾಡುತ್ತಾ ಬಂದಿದ್ದಾನೆ.",
			"26": "<sup>26</sup> ನಿಮ್ಮನ್ನೆಲ್ಲಾ ನೋಡಲು ಅವನು ಹಂಬಲಿಸುತ್ತಿದ್ದಾನೆ. ತಾನು ಅಸ್ವಸ್ಥನಾದ ಸುದ್ದಿಯು ನಿಮ್ಮ ಕಿವಿಗೆ ಮುಟ್ಟಿತೆಂದು ತಿಳಿದು ಬಹಳ ವ್ಯಸನಪಡುತ್ತಿದ್ದಾನೆ.",
			"27": "<sup>27</sup> ಅವನು ಅಸ್ವಸ್ಥನಾದದ್ದು ನಿಜ; ಸಾವಿನ ದವಡೆಯಿಂದ ಪಾರಾದದ್ದೂ ನಿಜ. ದೇವರ ಕರುಣೆ ಅವನ ಮೇಲೆ ಮಾತ್ರವಲ್ಲದೆ ನನ್ನ ಮೇಲೂ ಇದ್ದುದರಿಂದ ನನಗೆ ದುಃಖದ ಮೇಲೆ ದುಃಖ ಬರದಂತಾಯಿತು.",
			"28": "<sup>28</sup> ಎಪಫ್ರೋದಿತನನ್ನು ನಿಮ್ಮ ಬಳಿಗೆ ಕಳುಹಿಸಲು ತವಕದಿಂದಿದ್ದೇನೆ. ಅವನನ್ನು ಕಂಡು ನಿಮಗೆ ಆನಂದವಾಗುವುದು, ನನ್ನ ದುಃಖವೂ ಶಮನವಾಗುವುದು.",
			"29": "<sup>29</sup> ಪ್ರಭುವಿನ ಹೆಸರಿನಲ್ಲಿ ನೀವು ಅವನನ್ನು ತುಂಬು ಹೃದಯದಿಂದ ಸ್ವಾಗತಿಸಿರಿ. ಅವನಂಥವರಿಗೆ ಸೂಕ್ತ ಗೌರವ ಸಲ್ಲಿಸಿರಿ.",
			"30": "<sup>30</sup> ಕ್ರಿಸ್ತಯೇಸುವಿನ ಸೇವೆಯಲ್ಲಿ ಅವನು ಸತ್ತು ಬದುಕಿದ್ದಾನೆ. ನೀವು ಕೊಡಲು ಸಾಧ್ಯವಾಗದೆ ಹೋದ ನೆರವನ್ನು ಅವನು ನನಗೆ ಕೊಟ್ಟು ಸೇವೆಯನ್ನು ಪೂರ್ಣಗೊಳಿಸಿದ್ದಾನೆ."
		},
		"3": {
			"1": "<sup>1</sup> ಕೊನೆಯದಾಗಿ ಸಹೋದರರೇ, ಪ್ರಭುವಿನಲ್ಲಿ ಆನಂದಿಸಿರಿ. ನಾನು ನಿಮಗೆ ಮೊದಲೇ ತಿಳಿಸಿದಂತೆ ಇದನ್ನು ಮತ್ತೆ ಮತ್ತೆ ಬರೆಯುವುದರಲ್ಲಿ ನನಗೆ ಬೇಸರವಿಲ್ಲ; ಇದು ನಿಮ್ಮ ಹಿತಕ್ಕಾಗಿಯೇ.",
			"2": "<sup>2</sup> ನಿಮಗೆ ಸುನ್ನತಿಯಾಗಬೇಕೆಂದು ಬೋಧಿಸುವವರ ವಿಷಯದಲ್ಲಿ ಜಾಗರೂಕರಾಗಿರಿ. ಅವರು ದುಷ್ಕರ್ಮಿಗಳು, ಶ್ವಾನಗಳಿಗೆ ಸಮಾನರು.",
			"3": "<sup>3</sup> ನಿಜವಾದ ಸುನ್ನತಿ ಪಡೆದವರು ನಾವು, ಏಕೆಂದರೆ, ನಾವು ಪವಿತ್ರಾತ್ಮ ಅವರ ಪ್ರೇರಣೆಯಿಂದ ದೇವರನ್ನು ಆರಾಧಿಸುತ್ತೇವೆ. ಕ್ರಿಸ್ತಯೇಸುವಿನ ಅನ್ಯೋನ್ಯತೆಯಲ್ಲಿ ಹರ್ಷಿಸುತ್ತೇವೆ. ಬಾಹ್ಯಾಚರಣೆಗಳಲ್ಲೇ ನಂಬಿಕೆಯಿಡದೆ ಬಾಳುತ್ತೇವೆ.",
			"4": "<sup>4</sup> ಇಂಥ ಬಾಹ್ಯಾಚರಣೆಯಲ್ಲಿ ನಂಬಿಕೆ ಇಡಲು ತಮಗೆ ಹಕ್ಕು ಇದೆಯೆಂದು ಯಾರಾದರೂ ಭಾವಿಸಿದರೆ, ನನ್ನ ಹಕ್ಕು ಅದಕ್ಕೂ ಮಿಗಿಲಾದುದು ಎಂದೇ ಹೇಳುತ್ತೇನೆ.",
			"5": "<sup>5</sup> ಹುಟ್ಟಿದ ಎಂಟನೆಯ ದಿನ ನನಗೆ ಸುನ್ನತಿಯಾಯಿತು; ಹುಟ್ಟಿನಿಂದ ನಾನು ಇಸ್ರಯೇಲನು, ಬೆನ್ಯಮೀನ ಕುಲದವನು; ಅಪ್ಪಟ ಹಿಬ್ರಿಯನು,",
			"6": "<sup>6</sup> ಧರ್ಮಶಾಸ್ತ್ರದ ದೃಷ್ಟಿಯಲ್ಲಿ ನಿಷ್ಠಾವಂತ ಫರಿಸಾಯನು; ಮತಾಶಕ್ತಿಯ ಹಿತದೃಷ್ಟಿಯಿಂದ ಧರ್ಮಸಭೆಯ ಹಿಂಸಕನು; ಧರ್ಮಶಾಸ್ತ್ರ ವಿಧಿನಿಯಮಗಳ ಪಾಲನೆಯಲ್ಲಿ ನಿಂದಾರಹಿತನು.",
			"7": "<sup>7</sup> ಇವನ್ನೆಲ್ಲಾ ಲಾಭವೆಂದು ಪರಿಗಣಿಸಿದ್ದ ನಾನು ಈಗ ಕ್ರಿಸ್ತಯೇಸುವಿನ ಸಲುವಾಗಿ ನಷ್ಟವೆಂದೇ ಎಣಿಸುತ್ತೇನೆ.",
			"8": "<sup>8</sup> ನಿಶ್ಚಯವಾಗಿಯೂ ನನ್ನ ಪ್ರಭು ಯೇಸುಕ್ರಿಸ್ತರನ್ನು ಅರಿತುಕೊಳ್ಳುವುದೇ ಉತ್ಕೃಷ್ಟವಾದುದೆಂದು ಮನದಟ್ಟಾಗಿರುವುದರಿಂದ ಸಮಸ್ತವನ್ನೂ ವ್ಯರ್ಥವೆಂದೇ ಎಣಿಸುತ್ತೇನೆ. ಅವರನ್ನು ಲಭ್ಯವಾಗಿಸಿಕೊಳ್ಳುವ ಸಲುವಾಗಿ ಎಲ್ಲವನ್ನೂ ತೊರೆದುಬಿಟ್ಟಿದ್ದೇನೆ, ಎಲ್ಲವನ್ನೂ ಕಸವೆಂದೇ ಪರಿಗಣಿಸುತ್ತೇನೆ.",
			"9": "<sup>9</sup> ಧರ್ಮಶಾಸ್ತ್ರದ ಪಾಲನೆಯಿಂದ ದೊರಕುವ, ನನ್ನದೇ ಎಂದು ಹೇಳಿಕೊಳ್ಳಬಹುದಾದ ಸತ್ಸಂಬಂಧ ಯಾವುದೂ ನನಗಿಲ್ಲ. ಪ್ರತಿಯಾಗಿ, ನಾನು ಕ್ರಿಸ್ತಯೇಸುವಿನಲ್ಲಿಟ್ಟಿರುವ ವಿಶ್ವಾಸದ ಪ್ರಯುಕ್ತ ದೇವರೊಂದಿಗೆ ಸರಿಯಾದ ಸತ್ಸಂಬಂಧವನ್ನು ಹೊಂದಿದ್ದೇನೆ. ಈ ಸಂಬಂಧವು ದೇವರು ನನಗೆ ದಯಪಾಲಿಸಿರುವ ಅನುಗ್ರಹ. ನನ್ನ ವಿಶ್ವಾಸದ ಆಧಾರದ ಮೇಲೆ ಅವರೇ ನೀಡಿರುವ ಕೃಪಾವರ.",
			"10": "<sup>10</sup> ಯೇಸುಸ್ವಾಮಿಯನ್ನು ಅರಿಯಬೇಕು; ಅವರ ಪುನರುತ್ಥಾನದ ಪ್ರಭಾವವನ್ನು ಅನುಭವಿಸಬೇಕು; ಅವರ ಯಾತನೆಗಳಲ್ಲಿ ಪಾಲುಗೊಳ್ಳಬೇಕು; ಅವರ ಮರಣದಲ್ಲಿ ಅವರಂತೆಯೇ ಆಗಬೇಕು - ಇದೇ ನನ್ನ ಹೆಬ್ಬಯಕೆ.",
			"11": "<sup>11</sup> ಹೀಗೆ ನಾನೂ ಸಹ ಮೃತ್ಯುಂಜಯನಾಗುತ್ತೇನೆ ಎಂಬುದೇ ನನ್ನ ನಂಬಿಕೆ-ನಿರೀಕ್ಷೆ.",
			"12": "<sup>12</sup> ಈಗಾಗಲೇ ನಾನಿದೆಲ್ಲವನ್ನೂ ಸಾಧಿಸಿದ್ದೇನೆ, ಪರಿಪೂರ್ಣತೆಯನ್ನು ಗಳಿಸಿದ್ದೇನೆ ಎಂದು ನೀವು ಭಾವಿಸಬಾರದು. ಕ್ರಿಸ್ತಯೇಸು ನನ್ನನ್ನು ಯಾವುದಕ್ಕಾಗಿ ತಮ್ಮವನನ್ನಾಗಿ ಮಾಡಿಕೊಂಡರೋ, ಅದನ್ನು ನನ್ನದಾಗಿಸಿಕೊಳ್ಳಲು ನಾನು ಪ್ರಯತ್ನಿಸುತ್ತಿದ್ದೇನೆ.",
			"13": "<sup>13</sup> ಸಹೋದರರೇ, ಆ ಗುರಿಯನ್ನು ತಲುಪಿದ್ದೇನೆಂದು ನಾನೆಂದೂ ಹೇಳಿಕೊಂಡಿಲ್ಲ. ಆದರೆ ಇದು ನಿಜ. ಹಿಂದಿನ ಸಂಗತಿಗಳನ್ನು ಮರೆತುಬಿಟ್ಟು, ಮುಂದಿನವುಗಳ ಮೇಲೆ ಕಣ್ಣಿಟ್ಟು ನಾನು ಓಡುತ್ತಿದ್ದೇನೆ.",
			"14": "<sup>14</sup> ಮೇಲಣ ಬಹುಮಾನವನ್ನು ಪಡೆಯಲೆಂದು ದೇವರು ನನಗೆ ಕ್ರಿಸ್ತಯೇಸುವಿನ ಮುಖಾಂತರ ಕರೆ ನೀಡಿದ್ದಾರೆ. ಆ ಗುರಿಯನ್ನು ತಲುಪಲೆಂದೇ ನಾನು ಮುಂದೋಡುತ್ತಲಿದ್ದೇನೆ.",
			"15": "<sup>15</sup> ನಮ್ಮಲ್ಲಿ ಪರಿಣತರಾದವರು ಈ ವಿಷಯದಲ್ಲಿ ಇದೇ ಮನೋಭಾವನೆಯಿಂದ ಇರಬೇಕು. ಯಾವುದಾದರೂ ಒಂದು ವಿಷಯದಲ್ಲಿ ನೀವು ಭಿನ್ನಾಭಿಪ್ರಾಯ ಉಳ್ಳವರಾಗಿದ್ದರೆ ದೇವರು ನಿಜಾಂಶವನ್ನು ಸ್ಪಷ್ಟೀಕರಿಸುವರು.",
			"16": "<sup>16</sup> ಅಂತೂ, ಇದುವರೆಗೆ ನಾವು ಎಷ್ಟೇ ಮುಂದುವರೆದಿದ್ದರೂ ಅದೇ ಮಾರ್ಗದಲ್ಲಿ ಸಾಗೋಣ.",
			"17": "<sup>17</sup> ಸಹೋದರರೇ, ನೀವು ನನ್ನನ್ನು ಅನುಸರಿಸಿರಿ. ನಮ್ಮಂತೆ ಆದರ್ಶಜೀವನ ನಡೆಸುವವರನ್ನು ಅನುಸರಿಸಿ ಬಾಳಿರಿ.",
			"18": "<sup>18</sup> ಕ್ರಿಸ್ತಯೇಸುವಿನ ಶಿಲುಬೆಗೆ ವಿರೋಧವಾಗಿ ನಡೆಯುವವರು ಅನೇಕರಿದ್ದಾರೆ. ಇದನ್ನು ನಾನು ಹಲವಾರು ಸಾರಿ ತಿಳಿಸಿರುವಂತೆ ಈಗಲೂ ಅತೀವ ದುಃಖದಿಂದ ಹೇಳುತ್ತಿದ್ದೇನೆ.",
			"19": "<sup>19</sup> ಹೊಟ್ಟೆಯೇ ಅವರ ದೇವರು, ನಾಚಿಕೆಪಡಿಸುವ ಕಾರ್ಯಗಳಲ್ಲಿಯೇ ಅವರಿಗೆ ಹೆಮ್ಮೆ, ನಶ್ವರವಾದ ವಿಷಯಗಳಲ್ಲಿಯೇ ಅವರಿಗೆ ವ್ಯಾಮೋಹ. ಹೀಗಾಗಿ, ವಿನಾಶವೇ ಅವರ ಅಂತ್ಯ.",
			"20": "<sup>20</sup> ನಾವಾದರೋ ಸ್ವರ್ಗಸಾಮ್ರಾಜ್ಯದ ಪ್ರಜೆಗಳು, ಉದ್ಧಾರಕರಾದ ಪ್ರಭು ಯೇಸುಕ್ರಿಸ್ತರು ಪುನರಾಗಮಿಸುವುದು ಅಲ್ಲಿಂದಲೇ ಎಂದು ಎದುರುನೋಡುತ್ತಿದ್ದೇವೆ.",
			"21": "<sup>21</sup> ಅವರು ಸಮಸ್ತವನ್ನು ಸ್ವಾಧೀನಪಡಿಸಿಕೊಳ್ಳುವರು; ನಶ್ವರವಾದ ನಮ್ಮ ದೀನದೇಹಗಳನ್ನು ತಮ್ಮ ಶಕ್ತಿಯಿಂದ ರೂಪಾಂತರಗೊಳಿಸಿ ತಮ್ಮ ತೇಜೋಮಯ ಶರೀರದಂತೆ ಮಾಡುವರು."
		},
		"4": {
			"1": "<sup>1</sup> ಹೀಗಿರಲಾಗಿ ಒಲವಿನ ಸಹೋದರರೇ, ನನ್ನ ಅತಿ ಪ್ರಿಯರೇ, ಆಪ್ತರೇ, ನನ್ನ ಮುದವೂ ಮುಕುಟವೂ ಆದವರೇ, ಪ್ರಭುವಿನಲ್ಲಿ ದೃಢ ವಿಶ್ವಾಸಿಗಳಾಗಿ ಬಾಳಿರಿ.",
			"2": "<sup>2</sup> ಯುವೋದ್ಯಳು ಮತ್ತು ಸಂತುಕೆ ಇವರಲ್ಲಿ ನನ್ನದೊಂದು ಬಿನ್ನಹ: ನಿಮ್ಮ ಭಿನ್ನಾಭಿಪ್ರಾಯಗಳನ್ನು ತೊರೆದು ಪ್ರಭುವಿನಲ್ಲಿ ಒಂದಾಗಿ ಬಾಳಿರಿ.",
			"3": "<sup>3</sup> ನನ್ನ ಜೊತೆ ಸೇವಕನಾದ ನೀನು ಈ ಮಹಿಳೆಯರಿಗೆ ನೆರವಾಗಬೇಕು. ಇವರು ಸಹ ಕ್ಲೇಮಂತನು ಮತ್ತು ಇತರ ಸಹಸೇವಕರೊಡನೆ ನನ್ನೊಂದಿಗೆ ಶುಭಸಂದೇಶದ ಪ್ರಚಾರಕಾರ್ಯದಲ್ಲಿ ಸಹಕರಿಸಿರುತ್ತಾರೆ. ಇವರೆಲ್ಲರ ಹೆಸರುಗಳು ನಿತ್ಯಜೀವಬಾಧ್ಯರ ಪಟ್ಟಿಯಲ್ಲಿವೆ.",
			"4": "<sup>4</sup> ಪ್ರಭುವಿನಲ್ಲಿ ನೀವು ಸತತವೂ ಆನಂದಿಸಿರಿ. ಆನಂದಿಸಬೇಕೆಂದು ಮತ್ತೆ ಒತ್ತಿ ಹೇಳುತ್ತೇನೆ.",
			"5": "<sup>5</sup> ನಿಮ್ಮ ಸಹನೆ, ಸಹಾನುಭೂತಿ ಸರ್ವರಿಗೂ ತಿಳಿದಿರಲಿ. ಪ್ರಭುವಿನ ಪುನರಾಗಮನ ಸಮೀಪಿಸಿದೆ.",
			"6": "<sup>6</sup> ಯಾವ ವಿಷಯದಲ್ಲೂ ಚಿಂತಿಸದೆ, ನಿಮ್ಮ ಅಗತ್ಯಗಳಿಗಾಗಿ ದೇವರಲ್ಲಿ ಪ್ರಾರ್ಥಿಸಿರಿ. ಪ್ರಾರ್ಥಿಸುವಾಗಲೆಲ್ಲಾ ನಿಮ್ಮಲ್ಲಿ ಕೃತಜ್ಞತಾಭಾವ ಇರಲಿ.",
			"7": "<sup>7</sup> ಆಗ ಮನುಷ್ಯಗ್ರಹಿಕೆಗೂ ಮೀರಿದ ದೈವಶಾಂತಿಯು ನಿಮ್ಮ ಹೃನ್ಮನಗಳನ್ನು ಕ್ರಿಸ್ತೇಸುವಿನ ಅನ್ಯೋನ್ಯತೆಯಲ್ಲಿ ಸುರಕ್ಷಿತವಾಗಿ ಕಾಪಾಡುವುದು.",
			"8": "<sup>8</sup> ಕಡೆಯದಾಗಿ ಸಹೋದರರೇ, ಯಾವುದು ಸತ್ಯವು-ಮಾನ್ಯವು, ನ್ಯಾಯವು-ಶುದ್ಧವು, ಪ್ರೀತಿಕರವು-ಮನೋಹರವು ಆಗಿದೆಯೋ ಯಾವುದು ಸದ್ಗುಣವು-ಸ್ತುತ್ಯಾರ್ಹವು ಆಗಿದೆಯೋ ಅಂಥವುಗಳಲ್ಲಿ ಮಗ್ನರಾಗಿರಿ.",
			"9": "<sup>9</sup> ನೀವು ನನ್ನಿಂದ ಕಲಿತವು ಹಾಗೂ ಅರಿತವುಗಳನ್ನು, ಕೇಳಿದವು ಹಾಗೂ ಕಂಡವುಗಳನ್ನು ನಿಮ್ಮ ಜೀವಿತದಲ್ಲಿ ಕಾರ್ಯರೂಪಕ್ಕೆ ತನ್ನಿರಿ. ಆಗ ಶಾಂತಿದಾಯಕ ದೇವರು ನಿಮ್ಮ ಸಂಗಡ ಇರುವರು.",
			"10": "<sup>10</sup> ನನ್ನ ಬಗ್ಗೆ ನಿಮಗಿರುವ ಮಮತೆಯು ಇಷ್ಟು ದಿನಗಳಾದ ಮೇಲೆ ಪುನಃ ಅರಳಿದ್ದಕ್ಕೆ ಪ್ರಭುವಿನಲ್ಲಿ ನಾನು ಬಹಳ ಸಂತೋಷಪಡುತ್ತೇನೆ. ಇಂಥ ಮಮತೆ ನಿಮಗೆ ಮೊದಲಿನಿಂದ ಇತ್ತಾದರೂ ಅದನ್ನು ವ್ಯಕ್ತಪಡಿಲು ನಿಮಗೆ ಸೂಕ್ತ ಸಂದರ್ಭ ಒದಗಿರಲಿಲ್ಲ.",
			"11": "<sup>11</sup> ನಾನು ಯಾವುದೋ ಕೊರತೆಯಲ್ಲಿರುವೆನೆಂದು ಸೂಚಿಸಲು ಇದನ್ನು ಹೇಳುತ್ತಿಲ್ಲ. ನಾನು ಯಾವ ಸ್ಥಿತಿಯಲ್ಲಿದ್ದರೂ ತೃಪ್ತಿಯಿಂದಿರಲು ಕಲಿತುಕೊಂಡಿದ್ದೇನೆ.",
			"12": "<sup>12</sup> ಬಡತನ ಇರಲಿ, ಸಿರಿತನವಿರಲಿ, ಕೊರತೆಯಿರಲಿ, ಸಮೃದ್ಧಿ ಇರಲಿ, ಹಸಿದಿರಲಿ, ಹೊಟ್ಟೆ ತುಂಬಿರಲಿ - ಯಾವ ಸ್ಥಿತಿಯಲ್ಲಿದ್ದರೂ, ಎಲ್ಲಾ ವೇಳೆಯಲ್ಲಿಯೂ ತೃಪ್ತನಾಗಿರುವ ರಹಸ್ಯವನ್ನು ತಿಳಿದಿದ್ದೇನೆ.",
			"13": "<sup>13</sup> ಸರ್ವೇಶನ ಶಕ್ತಿಯಿಂದ ಸರ್ವವನ್ನೂ ಸಾಧಿಸುವ ಸಾಮರ್ಥ್ಯ ನನಗಿದೆ.",
			"14": "<sup>14</sup> ಆದರೂ ನನ್ನ ಸಂಕಷ್ಟಗಳಲ್ಲಿ ಸಹಾಯ ಮಾಡಿದುದು ನಿಮ್ಮ ಸೌಜನ್ಯವೇ ಸರಿ.",
			"15": "<sup>15</sup> ಫಿಲಿಪ್ಪಿಯ ಸಹೋದರರೇ, ನಾನು ಶುಭಸಂದೇಶವನ್ನು ಸಾರಲು ಹೊರಟ ಪ್ರಾರಂಭದ ದಿನಗಳಲ್ಲಿ ಮಕೆದೋನಿಯವನ್ನು ಬಿಟ್ಟುಹೋದಾಗ ನೀವಲ್ಲದೆ ಬೇರೆ ಯಾವ ಸಭೆಯೂ ನನ್ನ ಆಯ-ವ್ಯಯಗಳಲ್ಲಿ ನೆರವಾಗಿರಲಿಲ್ಲ. ಇದು ನಿಮಗೆ ತಿಳಿದ ವಿಷಯ.",
			"16": "<sup>16</sup> ನಾನು ಥೆಸಲೋನಿಕದಲ್ಲಿದ್ದಾಗಲೂ ನನ್ನ ಕೊರತೆಯನ್ನು ನೀಗಿಸಲು ಒಂದೆರಡು ಬಾರಿ ಧನಸಹಾಯವನ್ನು ಕಳುಹಿಸಿದಿರಿ.",
			"17": "<sup>17</sup> ನಿಮ್ಮಿಂದ ಸಹಾಯ ಪಡೆಯಬೇಕೆಂಬುದು ನನ್ನ ಉದ್ದೇಶವಲ್ಲ. ಆದರೆ ಈ ಸಹಾಯದಿಂದ ನಿಮಗೆ ಮುಂದೆ ದೊರಕುವ ಪ್ರತಿಫಲವು ಸಮೃದ್ಧಿಯಾಗಲೆಂದೇ ನನ್ನ ಇಚ್ಛೆ.",
			"18": "<sup>18</sup> ನೀವು ಕಳುಹಿಸಿಕೊಟ್ಟಿದ್ದೆಲ್ಲವೂ ನನಗೆ ಸಂದಾಯವಾಗಿದೆ. ಈಗ ಅಗತ್ಯಕ್ಕಿಂತಲೂ ಅಧಿಕವಾಗಿದೆ, ಯಥೇಚ್ಛವಾಗಿದೆ. ಎಪಫ್ರೋದಿತನ ಮೂಲಕ ನೀವು ಕಳುಹಿಸಿದ್ದೆಲ್ಲವೂ ನನಗೆ ತಲುಪಿದೆ. ನಿಮ್ಮ ಕೊಡುಗೆ ಸುಗಂಧ ಕಾಣಿಕೆಯಾಗಿದೆ, ದೇವರಿಗೆ ಮೆಚ್ಚಿಗೆಯಾದ ಇಷ್ಟಾರ್ಥ ಬಲಿಯಾಗಿದೆ.",
			"19": "<sup>19</sup> ನನ್ನ ದೇವರು ತಮ್ಮ ಮಹದೈಶ್ವರ್ಯದಿಂದ ನಿಮ್ಮ ಅಗತ್ಯಗಳನ್ನೆಲ್ಲಾ ಕ್ರಿಸ್ತಯೇಸುವಿನ ಮುಖಾಂತರ ಪೂರೈಸುವರು.",
			"20": "<sup>20</sup> ನಮ್ಮ ತಂದೆಯಾದ ದೇವರಿಗೆ ನಿರಂತವೂ ಮಹಿಮೆ ಸಲ್ಲಲಿ!",
			"21": "<sup>21</sup> ಕ್ರಿಸ್ತಯೇಸುವಿನಲ್ಲಿ ಎಲ್ಲ ದೇವಜನರಿಗೂ ನನ್ನ ವಂದನೆಗಳನ್ನು ತಿಳಿಸಿರಿ. ನನ್ನೊಡನೆ ಇರುವ ಸಹೋದರರು ನಿಮಗೆ ತಮ್ಮ ಶುಭಾಶಯಗಳನ್ನು ಕಳುಹಿಸಿದ್ದಾರೆ.",
			"22": "<sup>22</sup> ಅಲ್ಲದೆ, ಇಲ್ಲಿರುವ ದೇವಜನರೆಲ್ಲರೂ ವಿಶೇಷವಾಗಿ ಚಕ್ರವರ್ತಿಯ ಅರಮನೆಯಲ್ಲಿರುವ ಕ್ರೈಸ್ತವಿಶ್ವಾಸಿಗಳು ತಮ್ಮ ಪ್ರಣಾಮಗಳನ್ನು ಸಲ್ಲಿಸಿದ್ದಾರೆ.",
			"23": "<sup>23</sup> ಪ್ರಭು ಯೇಸುಕ್ರಿಸ್ತರ ಅನುಗ್ರಹ ನಿಮ್ಮೆಲ್ಲರಲ್ಲಿ ಇರಲಿ!"
		}
	}
};
module.exports = book;