var book = {
	"name": "ಕೊಲೊಸ್ಸೆಯರಿಗೆ",
	"numChapters": 4,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ಕೊಲೊಸ್ಸೆಯಲ್ಲಿರುವ ದೇವಜನರಿಗೆ ಹಾಗೂ ಕ್ರಿಸ್ತಯೇಸುವಿನಲ್ಲಿ ವಿಶ್ವಾಸವಿಟ್ಟಿರುವ ಸಹೋದರರಿಗೆ -",
			"2": "<sup>2</sup> ದೇವರ ಚಿತ್ತಾನುಸಾರ ಕ್ರಿಸ್ತಯೇಸುವಿನ ಪ್ರೇಷಿತನಾದ ಪೌಲನು ಮತ್ತು ಸಹೋದರ ತಿಮೊಥೇಯನು ಬರೆಯುವ ಪತ್ರ. ನಮ್ಮ ತಂದೆಯಾದ ದೇವರ ಅನುಗ್ರಹವೂ ಶಾಂತಿ ಸಮಾಧಾನವೂ ನಿಮ್ಮಲ್ಲಿರಲಿ!",
			"3": "<sup>3</sup> ನೀವು ಪ್ರಭು ಯೇಸುಕ್ರಿಸ್ತರಲ್ಲಿ ಇಟ್ಟಿರುವ ವಿಶ್ವಾಸ ಹಾಗೂ ದೇವಜನರಲ್ಲಿ ನೀವು ಇರಿಸಿರುವ ಪ್ರೀತಿ ಇವುಗಳ ಬಗ್ಗೆ ನಾವು ಕೇಳಿದ್ದೇವೆ. ಇದಕ್ಕಾಗಿ ಪ್ರಭು ಯೇಸುಕ್ರಿಸ್ತರ ತಂದೆಯಾದ ದೇವರಲ್ಲಿ ಪ್ರಾರ್ಥಿಸುವಾಗಲೆಲ್ಲ ನಿಮಗಾಗಿ ಕೃತಜ್ಞತೆಯನ್ನು ಸಲ್ಲಿಸುತ್ತೇವೆ.",
			"5": "<sup>5</sup> ಸತ್ಯ ವಾಕ್ಯದ, ಅಂದರೆ ಶುಭಸಂದೇಶದ ಮೂಲಕ ನೀವು ಕೇಳಿ ತಿಳಿದಂಥ ಹಾಗೂ ನಿರೀಕ್ಷಿಸುವಂಥ ಸೌಭಾಗ್ಯವನ್ನು ಸ್ವರ್ಗಲೋಕದಲ್ಲಿ ನಿಮಗಾಗಿ ಕಾದಿರಿಸಲಾಗಿದೆ.",
			"6": "<sup>6</sup> ದೇವಾನುಗ್ರಹದ ಸತ್ಯಾರ್ಥವನ್ನು ನೀವು ತಿಳಿದ ದಿನದಿಂದಲೂ ನಿಮ್ಮಲ್ಲಿ ಶುಭಸಂದೇಶವು ಹೇಗೆ ಫಲಭರಿತವಾಗುತ್ತಿದೆಯೋ ಹಾಗೆ ಈ ಶುಭಸಂದೇಶವು ಜಗತ್ತಿನ ಎಲ್ಲೆಡೆಯೂ ಹಬ್ಬಿಹರಡುತ್ತಲಿದೆ.",
			"7": "<sup>7</sup> ಈ ಶುಭಸಂದೇಶವನ್ನು ಕ್ರಿಸ್ತಯೇಸುವಿನ ನಂಬಿಕಸ್ಥ ದಾಸನೂ ನಮ್ಮ ಜೊತೆಯ ಸೇವಕನೂ ಆದ ಎಪಫ್ರನಿಂದ ಕಲಿತುಕೊಂಡಿದ್ದೀರಿ.",
			"8": "<sup>8</sup> ಪವಿತ್ರಾತ್ಮ ಅವರು ನಿಮಗೆ ಅನುಗ್ರಹಿಸಿದ ಪ್ರೀತಿಯ ಬಗ್ಗೆ ನಮಗೆ ತಿಳಿಸಿದವನು ಸಹ ಆತನೇ.",
			"9": "<sup>9</sup> ಹೀಗಿರುವುದರಿಂದ ಈ ವಿಷಯವನ್ನು ನಾವು ಕೇಳಿದ ದಿನದಿಂದಲೂ ನಿಮಗಾಗಿ ಸದಾ ಪ್ರಾರ್ಥಿಸುತ್ತಲೇ ಇದ್ದೇವೆ. ನೀವು ಪರಿಪೂರ್ಣ ವಿವೇಕದಿಂದಲೂ ಆಧ್ಯಾತ್ಮಿಕ ಜ್ಞಾನದಿಂದಲೂ ದೇವರ ಚಿತ್ತವನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ಅರಿಯಬೇಕೆಂಬುದೇ ನಮ್ಮ ಕೋರಿಕೆ.",
			"10": "<sup>10</sup> ಆಗ ಎಲ್ಲ ವಿಷಯಗಳಲ್ಲಿಯೂ ಪ್ರಭುಯೇಸು ಮೆಚ್ಚುವ ರೀತಿಯಲ್ಲಿ ನೀವು ಜೀವಿಸುವಿರಿ. ನಾನಾ ತರಹದ ಸತ್ಕಾರ್ಯಗಳನ್ನು ಕೈಗೊಳ್ಳುವಿರಿ. ಸತ್ಫಲವನ್ನೀಯುವ ದೈವಜ್ಞಾನದಲ್ಲಿ ವೃದ್ಧಿಹೊಂದುವಿರಿ.",
			"11": "<sup>11</sup> ದೇವರ ಮಹಿಮಾಶಕ್ತಿಯಿಂದ ನೀವು ಬಲಗೊಂಡು ಎಲ್ಲವನ್ನೂ ತಾಳ್ಮೆಯಿಂದಲೂ ಸಮಾಧಾನದಿಂದಲೂ ಸಹಿಸಿಕೊಳ್ಳುವಿರಿ.",
			"12": "<sup>12</sup> ಪಿತನಿಗೆ ಹರ್ಷದಿಂದ ಕೃತಜ್ಞತಾಸ್ತುತಿ ಸಲ್ಲಿಸುವಿರಿ. ಏಕೆಂದರೆ, ಬೆಳಕಿನಲ್ಲಿರುವ ದೇವಜನರಿಗೆ ಕಾಯ್ದಿರಿಸಿರುವ ಸ್ವಾಸ್ತ್ಯದಲ್ಲಿ ಪಾಲುಗಾರರಾಗಲು ನಿಮ್ಮನ್ನು ಯೋಗ್ಯರನ್ನಾಗಿ ಅವರು ಮಾಡಿದ್ದಾರೆ.",
			"13": "<sup>13</sup> ದೇವರು ನಮ್ಮನ್ನು ಅಂಧಕಾರದ ಆಡಳಿತದಿಂದ ಬಿಡುಗಡೆಮಾಡಿ ತಮ್ಮ ಪುತ್ರನ ಸಾಮ್ರಾಜ್ಯಕ್ಕೆ ಸುರಕ್ಷಿತವಾಗಿ ತಂದಿದ್ದಾರೆ.",
			"14": "<sup>14</sup> ಈ ಪುತ್ರನಲ್ಲಿ ನಮಗೆ ಪಾಪಕ್ಷಮೆ ದೊರಕಿದೆ, ವಿಮೋಚನೆ ಲಭಿಸಿದೆ.",
			"15": "<sup>15</sup> ಅದೃಶ್ಯ ದೇವನ ಸದೃಶ್ಯರೂಪನು ಕ್ರಿಸ್ತ, ಸೃಷ್ಟಿಗೆಲ್ಲ ಜೇಷ್ಠಪುತ್ರನಾತ,",
			"16": "<sup>16</sup> ಇರಲಿ ಇಹದಲಿ, ಇರಲಿ ಪರದಲಿ, ಅಶರೀರ ಒಡೆಯರಾಗಲಿ, ಒಡೆತನವಾಗಲಿ, ಅಧಿಕಾರಿಗಳಾಗಲಿ, ಆಧಿಪತ್ಯವಾಗಿರಲಿ, ಆದುದೆಲ್ಲವೂ ಆತನಲಿ, ಆತನಿಂದ, ಆತನಿಗಾಗಿ.",
			"17": "<sup>17</sup> ಎಲ್ಲಕ್ಕೂ ಮೊದಲೇ ಇರುವನಾತ, ಸಮಸ್ತಕ್ಕೂ ಆಧಾರಭೂತ.",
			"18": "<sup>18</sup> ಸಭೆಯೆಂಬ ಶರೀರಕ್ಕೆ ಶಿರಸ್ಸಾತ, ಆದಿಸಂಭೂತ, ಆಗಲೆಲ್ಲದರಲೂ ಅಗ್ರಸ್ಥ, ಸತ್ತವರಿಂದ ಮೊದಲೆದ್ದು ಬಂದನಾತ.",
			"19": "<sup>19</sup> ಆ ಸುತನಲ್ಲೇ ಇರಿಸಲು ನಿರ್ಧರಿಸಿದನು ಪಿತದೇವ ತನ್ನ ಸರ್ವಸಂಪೂರ್ಣತೆಯನು.",
			"20": "<sup>20</sup> ಶಿಲುಬೆಯಿಂದಾತ ಹರಿಸಿದ ರಕುತದಿಂದ ಆಗುತಲಿದೆ ಶಾಂತಿಸಮಾಧಾನ, ನಡೆದಿದೆ ದೇವರೊಡನೆ ಸಂಧಾನ, ಇಹಪರಗಳೆಲ್ಲಕ್ಕೂ ಆತನ ಮುಖೇನ.",
			"21": "<sup>21</sup> ಹಿಂದೆ, ನೀವು ಆಚಾರ-ವಿಚಾರಗಳಲ್ಲಿ ಕೆಟ್ಟವರಾಗಿದ್ದರಿಂದ ದೇವರ ವಿರೋಧಿಗಳಾಗಿದ್ದು ಅವರಿಂದ ದೂರವಿದ್ದಿರಿ.",
			"22": "<sup>22</sup> ಈಗಲಾದರೋ ಯೇಸುಕ್ರಿಸ್ತರ ದೈವಿಕ ಮರಣದ ಫಲವಾಗಿ ನಿಮಗೂ ದೇವರಿಗೂ ಸಂಧಾನ ನಡೆದಿದೆ. ಹೀಗೆ ದೇವರು ನಿಮ್ಮನ್ನು ತಮ್ಮ ಸನ್ನಿಧಿಗೆ ಸೇರಿಸಿಕೊಳ್ಳಲು ನೀವು ಪವಿತ್ರರೂ ನಿಷ್ಕಳಂಕರೂ ದೋಷರಹಿತರೂ ಆಗಿರುವಂತೆ ಮಾಡಿದ್ದಾರೆ.",
			"23": "<sup>23</sup> ಇನ್ನು ನೀವು ವಿಶ್ವಾಸದಲ್ಲಿ ದೃಢವಾಗಿ ಮುನ್ನಡೆಯಬೇಕು. ಶುಭಸಂದೇಶವನ್ನು ಕೇಳಿದಾಗ ನೀವು ಹೊಂದಿದ ಭರವಸೆಯನ್ನು ಕಳೆದುಕೊಳ್ಳದಂತೆ ಜಾಗರೂಕರಾಗಿರಬೇಕು. ಪೌಲನಾದ ನಾನು ಇದೇ ಶುಭಸಂದೇಶದ ಪ್ರಚಾರಕ. ಈ ಶುಭಸಂದೇಶವನ್ನು ಜಗತ್ತಿನಲ್ಲಿರುವ ಸರ್ವಸೃಷ್ಟಿಗೂ ಸಾರಲಾಗುತ್ತಿದೆ.",
			"24": "<sup>24</sup> ಈಗ ನಿಮಗೋಸ್ಕರ ಸಂಕಟಪಡುವುದರಲ್ಲಿ ನನಗೆ ಸಂತೋಷವಿದೆ. ಕ್ರಿಸ್ತಯೇಸು ತಮ್ಮ ಶರೀರವಾದ ಧರ್ಮಸಭೆಗೋಸ್ಕರ ಅನುಭವಿಸಬೇಕಾದ ಯಾತನೆಗಳಲ್ಲಿ ಉಳಿದದ್ದನ್ನು ನಾನು ನನ್ನ ದೇಹದಲ್ಲಿ ಅನುಭವಿಸಿ ಪೂರ್ಣಗೊಳಿಸುತ್ತಿದ್ದೇನೆ.",
			"25": "<sup>25</sup> ದೇವರು ನನ್ನನ್ನು ಧರ್ಮಸಭೆಯ ದಾಸನನ್ನಾಗಿ ನೇಮಿಸಿದ್ದಾರೆ. ನಿಮ್ಮ ಹಿತಕ್ಕಾಗಿ ಒಂದು ಮಹತ್ವದ ಕಾರ್ಯವನ್ನು ನನಗೆ ವಹಿಸಿರುತ್ತಾರೆ.",
			"26": "<sup>26</sup> ಯುಗಯುಗಾಂತರಗಳಿಂದಲೂ ತಲತಲಾಂತರಗಳಿಂದಲೂ ನಿಗೂಢವಾಗಿದ್ದ ರಹಸ್ಯಗಳನ್ನು, ಅಂದರೆ ತಮ್ಮ ವಾಕ್ಯವನ್ನು, ಈಗ ತಮ್ಮ ಜನರಿಗೆ ದೇವರು ಪ್ರಕಟಿಸಿದ್ದಾರೆ.",
			"27": "<sup>27</sup> ಈ ರಹಸ್ಯ ಎಷ್ಟು ಶ್ರೀಮಂತವಾದುದು, ಎಷ್ಟು ಮಹಿಮಾನ್ವಿತವಾದುದು ಎಂಬುದನ್ನು ಎಲ್ಲಾ ಜನಾಂಗಗಳಿಗೂ ತಿಳಿಸಲು ದೇವರು ಇಚ್ಛಿಸಿದರು. ಕ್ರಿಸ್ತಯೇಸು ನಿಮ್ಮಲ್ಲಿದ್ದು ಮುಂದಿನ ಮಹಿಮೆಯ ನಿರೀಕ್ಷೆಗೆ ಆಧಾರವಾಗಿದ್ದಾರೆ ಎಂಬುದೇ ಈ ರಹಸ್ಯ.",
			"28": "<sup>28</sup> ನಾವು ಸಾರುತ್ತಲಿರುವುದೂ ಯೇಸುಕ್ರಿಸ್ತರನ್ನೇ. ಎಲ್ಲರಿಗೂ ಬುದ್ಧಿಹೇಳುತ್ತಾ ಎಲ್ಲರಿಗೂ ಪೂರ್ಣಜ್ಞಾನವನ್ನು ಉಪದೇಶಿಸುತ್ತಾ ದೇವರ ಮುಂದೆ ಎಲ್ಲರನ್ನು ಕ್ರಿಸ್ತಯೇಸುವಿನಲ್ಲಿ ಪರಿಣತರನ್ನಾಗಿ ಊರ್ಜಿತಗೊಳಿಸಲು ಪ್ರಯತ್ನಿಸುತ್ತಿದ್ದೇವೆ.",
			"29": "<sup>29</sup> ನನ್ನನ್ನು ಚೇತನಗೊಳಿಸುತ್ತಿರುವ ಯೇಸುಕ್ರಿಸ್ತರ ಶಕ್ತಿಯಿಂದ ಈ ಗುರಿಯನ್ನು ಸಾಧಿಸಲು ಶ್ರಮವಹಿಸಿ ಹೋರಾಡುತ್ತಿದ್ದೇನೆ."
		},
		"2": {
			"1": "<sup>1</sup> ನಿಮಗಾಗಿಯೂ ಲವೊದಿಕೀಯದವರಿಗಾಗಿಯೂ ಹಾಗೂ ನನ್ನ ನೇರ ಪರಿಚಯ ಇಲ್ಲದವರಿಗಾಗಿಯೂ ನಾನು ಎಷ್ಟು ಶ್ರಮಿಸುತ್ತಿದ್ದೇನೆಂಬುದನ್ನು ತಿಳಿಸಬಯಸುತ್ತೇನೆ.",
			"2": "<sup>2</sup> ಇದರಿಂದ ಅವರು ಅಂತರಂಗದಲ್ಲಿ ಉತ್ತೇಜನಗೊಂಡು ಪ್ರೀತಿಯಲ್ಲಿ ಒಂದಾಗಬೇಕು; ನೈಜ ಅರಿವಿನಿಂದ ಅವರಿಗೆ ಪೂರ್ಣಜ್ಞಾನ ಲಭಿಸಬೇಕು ಎಂಬುದೇ ನನ್ನ ಆಶಯ. ಆಗ ಅವರು ದೇವರ ರಹಸ್ಯವನ್ನು, ಅಂದರೆ ಕ್ರಿಸ್ತಯೇಸುವನ್ನು ಅರಿತುಕೊಳ್ಳಲು ಸಾಧ್ಯ.",
			"3": "<sup>3</sup> ಕ್ರಿಸ್ತಯೇಸುವಿನಲ್ಲಿಯೇ ಜ್ಞಾನ, ವಿವೇಕ ಎಂಬ ಸಿರಿಸಂಪತ್ತು ಅಡಗಿದೆ.",
			"4": "<sup>4</sup> ಯಾರೂ ತಮ್ಮ ಕುತರ್ಕಗಳಿಂದ ನಿಮ್ಮನ್ನು ಮೋಸಗೊಳಿಸದಿರಲಿ.",
			"5": "<sup>5</sup> ನಾನು ಶಾರೀರಿಕವಾಗಿ ನಿಮ್ಮಿಂದ ದೂರವಿದ್ದರೂ ಆತ್ಮಿಕವಾಗಿ ನಿಮ್ಮೊಂದಿಗಿದ್ದೇನೆ. ನಿಮ್ಮ ಕ್ರಮಬದ್ಧ ನಡತೆಯನ್ನೂ ಕ್ರಿಸ್ತಯೇಸುವಿನಲ್ಲಿ ನಿಮಗಿರುವ ಸ್ಥಿರವಿಶ್ವಾಸವನ್ನೂ ನೋಡುವಾಗ ನನಗೆ ಅಮಿತಾನಂದವಾಗುತ್ತದೆ.",
			"6": "<sup>6</sup> ನೀವು ಕ್ರಿಸ್ತಯೇಸುವನ್ನು ಪ್ರಭುವಾಗಿ ಸ್ವೀಕರಿಸಿರುವುದರಿಂದ, ಅವರೊಡನೆ ಐಕ್ಯತೆಯಿಂದ ಬಾಳಿರಿ.",
			"7": "<sup>7</sup> ಅವರಲ್ಲಿ ಸ್ಥಿರವಾಗಿರಿ. ಅವರನ್ನೇ ಅವಲಂಬಿಸಿ ನಿಮ್ಮ ಬಾಳೆಂಬ ಸೌಧವನ್ನು ನಿರ್ಮಿಸಿರಿ. ನೀವು ಕಲಿತುಕೊಂಡಿರುವಂತೆ ನಿಮ್ಮ ವಿಶ್ವಾಸ ಅವರಲ್ಲಿ ದೃಢವಾಗಿರಲಿ. ಕೃತಜ್ಞತೆಯು ನಿಮ್ಮಲ್ಲಿ ಉಕ್ಕೇರಲಿ.",
			"8": "<sup>8</sup> ಪ್ರಪಂಚದ ಪ್ರಬಲ ಶಕ್ತಿಗಳಿಂದಲೂ ಮಾನವ ವಿವೇಚನೆಯ ಶುಷ್ಕತರ್ಕ ಸಿದ್ಧಾಂತಗಳಿಂದಲೂ ಯಾರೂ ನಿಮ್ಮನ್ನು ಮೋಸಗೊಳಿಸಿ ವಶಪಡಿಸಿಕೊಳ್ಳದಂತೆ ಎಚ್ಚರಿಕೆಯಿಂದಿರಿ. ಇವುಗಳು ಪ್ರಾಪಂಚಿಕ ಪಾರಂಪರ್ಯಕ್ಕೆ ಮತ್ತು ವಿಶ್ವದ ಮೂಲಭೂತ ಶಕ್ತಿಗಳಿಗೆ ಸಂಬಂಧಿಸಿದವುಗಳೇ ಹೊರತು ಯೇಸುಕ್ರಿಸ್ತರಿಗಲ್ಲ.",
			"9": "<sup>9</sup> ದೈವತ್ವದ ಸಂಪೂರ್ಣತೆ ಸಶರೀರವಾಗಿ ಯೇಸುಕ್ರಿಸ್ತರಲ್ಲಿ ನೆಲೆಗೊಂಡಿದೆ.",
			"10": "<sup>10</sup> ಸಕಲ ಆಧಿಪತ್ಯಕ್ಕೂ ಅಧಿಕಾರಕ್ಕೂ ಶಿರಸ್ಸು ಅವರೇ. ಅವರಲ್ಲಿ ಮಾತ್ರ ನೀವು ಪರಿಪೂರ್ಣತೆಯನ್ನು ಹೊಂದಲು ಸಾಧ್ಯ.",
			"11": "<sup>11</sup> ಕ್ರಿಸ್ತಯೇಸುವಿನಲ್ಲಿಯೇ ನೀವು ಸುನ್ನತಿಯನ್ನು ಪಡೆದಿದ್ದೀರಿ. ಇದು ಶಾರೀರಿಕ ಸುನ್ನತಿಯಲ್ಲ, ಪಾಪಮಯ ಸ್ವಭಾವವನ್ನು ಕಿತ್ತೊಗೆಯುವ ಸುನ್ನತಿ. ಸ್ವಯಂ ಕ್ರಿಸ್ತಯೇಸುವೆ ಯೋಜಿಸಿದ ಸುನ್ನತಿ.",
			"12": "<sup>12</sup> ದೀಕ್ಷಾಸ್ನಾನದಲ್ಲಿ ನೀವು ಅವರೊಂದಿಗೆ ಸಮಾಧಿಯಾದಿರಿ. ಕ್ರಿಸ್ತಯೇಸುವನ್ನು ಮೃತರ ಮಧ್ಯದಿಂದ ದೇವರು ತಾವೇ ಎಬ್ಬಿಸಿದರು. ಈ ದೇವರ ಶಕ್ತಿಯಲ್ಲಿ ನೀವು ವಿಶ್ವಾಸವಿಟ್ಟಿರುವುದರಿಂದ ದೀಕ್ಷಾಸ್ನಾನದಲ್ಲಿಯೇ ನಿಮ್ಮನ್ನು ಕ್ರಿಸ್ತಯೇಸುವಿನೊಂದಿಗೆ ಎಬ್ಬಿಸಲಾಯಿತು.",
			"13": "<sup>13</sup> ನಿಮ್ಮ ಪಾಪಮಯ ಜೀವನದಿಂದಲೂ ಸುನ್ನತಿ ರಹಿತವಾದ ಸ್ವಭಾವದಿಂದಲೂ ಒಮ್ಮೆ ನೀವು ಮೃತರಾಗಿದ್ದೀರಿ. ಆದರೆ ಈಗ ದೇವರು ಕ್ರಿಸ್ತಯೇಸುವಿನೊಂದಿಗೆ ನಿಮ್ಮನ್ನು ಜೀವಂತಗೊಳಿಸಿದ್ದಾರೆ.",
			"14": "<sup>14</sup> ಅವರು ನಮ್ಮ ಎಲ್ಲಾ ಪಾಪಗಳನ್ನು ಕ್ಷಮಿಸಿದ್ದಾರೆ. ನಮ್ಮನ್ನು ಬಂಧಿಸಿದ್ದ ಧರ್ಮಶಾಸ್ತ್ರದ ವಿಧಿನಿಯಮಗಳನ್ನು ಕ್ರಿಸ್ತಯೇಸು ತೊಡೆದುಹಾಕಿದ್ದಲ್ಲದೆ ಅವುಗಳನ್ನು ಶಿಲುಬೆಗೆ ಜಡಿದು ನಮ್ಮನ್ನು ಬಿಡುಗಡೆ ಮಾಡಿದ್ದಾರೆ.",
			"15": "<sup>15</sup> ಬಲಾಢ್ಯ ಒಡೆಯರನ್ನೂ ಅಧಿಕಾರಿಗಳನ್ನೂ ನಿರಾಯುಧರನ್ನಾಗಿ ಮಾಡಿ, ತಾವು ಗಳಿಸಿದ ಜಯದ ನಿಮಿತ್ತ ಜನರೆಲ್ಲರ ಮುಂದೆ ಶಿಲುಬೆಯ ವಿಜಯೋತ್ಸವದಲ್ಲಿ ಅವರನ್ನು ಸೆರೆಯಾಳುಗಳನ್ನಾಗಿ ಪ್ರದರ್ಶಿಸಿದ್ದಾರೆ.",
			"16": "<sup>16</sup> ಹೀಗಿರಲಾಗಿ, ತಿಂಡಿತೀರ್ಥದ ವಿಷಯದಲ್ಲಾಗಲಿ, ಹಬ್ಬಹುಣ್ಣಿಮೆಗಳ ವಿಚಾರದಲ್ಲಾಗಲಿ, ಸಬ್ಬತ್‍ದಿನವನ್ನು ಆಚರಿಸುವ ರೀತಿಯಲ್ಲಾಗಲಿ ನಿಮ್ಮನ್ನು ದೋಷಿಗಳೆಂದು ಯಾರೂ ದೂರದಿರಲಿ.",
			"17": "<sup>17</sup> ಇವುಗಳೆಲ್ಲವೂ ಬರಲಿದ್ದುದರ ಛಾಯೆ ಮಾತ್ರ; ಕ್ರಿಸ್ತಯೇಸುವೇ ಅವುಗಳ ಸತ್ಯಸ್ವರೂಪ.",
			"18": "<sup>18</sup> ನಿಮ್ಮಲ್ಲಿ ಕೆಲವರು ದೇವದೂತರ ಆರಾಧನೆಯಲ್ಲಿ ಆಸಕ್ತರಾಗಿದ್ದಾರೆ. ಲೌಕಿಕ ಜ್ಞಾನದಿಂದ ನಿರರ್ಥಕವಾಗಿ ಉಬ್ಬಿಕೊಂಡಿದ್ದಾರೆ. ತಾವು ಬಹಳ ವಿನಯಶಾಲಿಗಳೆಂದು ಹೆಚ್ಚಳಪಡುತ್ತಾರೆ. ಇಂಥವರು ನಿಮ್ಮನ್ನು ಕಡೆಗಾಣಿಸದಂತೆ ಎಚ್ಚರದಿಂದಿರಿ. ಇವರು ಯೇಸುಕ್ರಿಸ್ತರೆಂಬ ಶಿರಸ್ಸಿನಿಂದ ಬೇರ್ಪಟ್ಟಿದ್ದಾರೆ.",
			"19": "<sup>19</sup> ಈ ಶಿರಸ್ಸಿನಿಂದಲೇ ಇಡೀ ದೇಹವು, ಕೀಲು-ನರಗಳ ಮೂಲಕ ಒಂದಾಗಿ ಸೇರಿ, ಪೋಷಣೆ ಪಡೆದು, ದೇವರು ನಿಯೋಜಿಸಿರುವ ಪ್ರಕಾರ ಬೆಳೆಯುತ್ತಲಿದೆ.",
			"20": "<sup>20</sup> ನೀವು ಕ್ರಿಸ್ತಯೇಸುವಿನೊಡನೆ ಮೃತರಾಗಿರುವ ಕಾರಣ, ಪ್ರಾಕೃತ ಶಕ್ತಿಗಳ ಬಂಧನದಿಂದ ಬಿಡುಗಡೆ ಹೊಂದಿದ್ದೀರಿ. ಹೀಗಿರುವಲ್ಲಿ, ಪ್ರಾಪಂಚಿಕ ವಿಧಿನಿಯಮಗಳಿಗೆ ಒಳಪಟ್ಟು ಬಾಳುತ್ತಿರುವುದೇಕೆ?",
			"21": "<sup>21</sup> ‘ಇದನ್ನು ಮುಟ್ಟಬೇಡ,’ ‘ಅದನ್ನು ಸವಿಯಬೇಡ,’ ‘ಇನ್ನೊಂದನ್ನು ಬಳಸಬೇಡ', ಎಂಬ ಕಟ್ಟಳೆಗಳಿಗೇಕೆ ಒಳಗಾಗುತ್ತೀರಿ?",
			"22": "<sup>22</sup> ಇವುಗಳೆಲ್ಲವೂ ಬಳಕೆಯಿಂದ ಅಳಿದುಹೋಗುತ್ತವೆ. ಅಲ್ಲದೆ, ಅವೆಲ್ಲವೂ ಮಾನವ ವ್ಯವಸ್ಥಿತ ಬೋಧನೆ ಹಾಗೂ ನಿಬಂಧನೆಗಳು.",
			"23": "<sup>23</sup> ಸ್ವಕಲ್ಪಿತ ಆಚಾರವಿಧಿಗಳಿಂದಲೂ ಕಪಟ ವಿನಯದಿಂದಲೂ ಕಠಿಣ ದೇಹದಂಡನೆಯಿಂದಲೂ ಕೂಡಿರುವ ಈ ವಿಧಿನಿಯಮಗಳು ಹೊರನೋಟಕ್ಕೆ ಜಾಣ್ಮೆಯುಳ್ಳದಾಗಿ ಕಾಣಬಹುದು. ಆದರೂ ಶಾರೀರಿಕ ಇಚ್ಛೆಗಳನ್ನು ನಿಯಂತ್ರಿಸಲು ಇವುಗಳು ಪ್ರಯೋಜನಕ್ಕೆ ಬರುವಂಥವುಗಳಲ್ಲ."
		},
		"3": {
			"1": "<sup>1</sup> ನೀವು ಯೇಸುಕ್ರಿಸ್ತರೊಂದಿಗೆ ಪುನರುತ್ಥಾನ ಹೊಂದಿರುವುದಾದರೆ ಸ್ವರ್ಗೀಯ ವಿಷಯಗಳ ಕಡೆಗೆ ಗಮನಕೊಡಿ. ಕ್ರಿಸ್ತಯೇಸು ಸ್ವರ್ಗದಲ್ಲಿ ದೇವರ ಬಲಗಡೆಯಲ್ಲಿ ಆಸೀನರಾಗಿದ್ದಾರೆ.",
			"2": "<sup>2</sup> ನೀವು ಕ್ರಿಸ್ತಯೇಸುವಿನೊಂದಿಗೆ ಮರಣ ಹೊಂದಿರುವುದರಿಂದ ಅವರೊಂದಿಗೆ ನಿಮ್ಮ ಜೀವ ದೇವರಲ್ಲಿ ಮರೆಯಾಗಿದೆ. ನಿಮ್ಮ ಮನಸ್ಸು ಪ್ರಾಪಂಚಿಕ ವಿಷಯಗಳ ಮೇಲೆ ಅಲ್ಲ, ಸ್ವರ್ಗೀಯ ವಿಷಯಗಳ ಮೇಲೆ ಕೇಂದ್ರೀಕೃತವಾಗಿರಲಿ.",
			"4": "<sup>4</sup> ಕ್ರಿಸ್ತಯೇಸುವೇ ನಿಮ್ಮ ನೈಜ ಜೀವವಾಗಿದ್ದಾರೆ. ಅವರು ಪುನರಾಗಮಿಸುವಾಗ ನೀವೂ ಸಹ ಅವರ ಮಹಿಮೆಯಲ್ಲಿ ಪಾಲುಗಾರರಾಗಿ ಅವರೊಂದಿಗೆ ಪ್ರತ್ಯಕ್ಷರಾಗುವಿರಿ.",
			"5": "<sup>5</sup> ನಿಮ್ಮಲ್ಲಿರುವ ಪ್ರಾಪಂಚಿಕ ಆಶೆ ಆಕಾಂಕ್ಷೆಗಳನ್ನು ತ್ಯಜಿಸಿರಿ. ಹಾದರ, ಅನೈತಿಕತೆ, ಕಾಮಾಭಿಲಾಷೆ, ದುರಾಲೋಚನೆ, ವಿಗ್ರಹಾರಾಧನೆಗೆ ಸಮವಾಗಿರುವ ಲೋಭ - ಇವುಗಳನ್ನು ದಮನಮಾಡಿರಿ.",
			"6": "<sup>6</sup> ಇಂಥವುಗಳಿಗೆ ಈಡಾಗುವ ದುಷ್ಕರ್ಮಿಗಳು ದೇವರ ಕೋಪಾಗ್ನಿಗೆ ಗುರಿಯಾಗುತ್ತಾರೆ.",
			"7": "<sup>7</sup> ಒಮ್ಮೆ ಇವೆಲ್ಲ ನಿಮ್ಮ ಜೀವನದ ರೀತಿನೀತಿಗಳಾಗಿದ್ದು ಇವುಗಳಲ್ಲೇ ಮಗ್ನರಾಗಿದ್ದೀರಿ.",
			"8": "<sup>8</sup> ಈಗಲಾದರೋ ಕೋಪ, ರೋಷ, ಮತ್ಸರ - ಇವುಗಳನ್ನು ತ್ಯಜಿಸಿರಿ; ದೂಷಣೆ ಮತ್ತು ಹೊಲಸು ಮಾತುಗಳು ನಿಮ್ಮ ಬಾಯಿಂದ ಬರಕೂಡದು.",
			"9": "<sup>9</sup> ಒಬ್ಬರಿಗೊಬ್ಬರು ಸುಳ್ಳು ಹೇಳಬೇಡಿ. ಏಕೆಂದರೆ, ನಿಮ್ಮಲ್ಲಿದ್ದ ಹಳೆಯ ಸ್ವಭಾವವನ್ನೂ ಅದಕ್ಕೆ ಸಂಬಂಧಿಸಿದ ದುರಭ್ಯಾಸಗಳನ್ನೂ ತೊರೆದುಬಿಟ್ಟಿದ್ದೀರಿ.",
			"10": "<sup>10</sup> ಮಾತ್ರವಲ್ಲ, ನೂತನ ಸ್ವಭಾವವನ್ನು ಧರಿಸಿಕೊಂಡಿದ್ದೀರಿ. ನೀವು ಸೃಷ್ಟಿಕರ್ತನನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ಅರಿತುಕೊಳ್ಳಬೇಕು ಎಂಬ ಉದ್ದೇಶದಿಂದ ಈ ಸ್ವಭಾವವನ್ನು ಅನುದಿನವೂ ನವೀಕರಿಸಲಾಗುತ್ತಿದೆ.",
			"11": "<sup>11</sup> ಇಂಥ ಹೊಸ ಜೀವನದಲ್ಲಿ ಯೆಹೂದ್ಯ-ಗ್ರೀಕ, ಸುನ್ನತಿ ಹೊಂದಿದವ-ಸುನ್ನತಿ ಇಲ್ಲದವ, ನಾಗರಿಕ-ಅನಾಗರಿಕ, ಯಜಮಾನ-ಗುಲಾಮ ಎಂಬ ಭಿನ್ನಭೇದಗಳಿಲ್ಲ, ಕ್ರಿಸ್ತಯೇಸುವೇ ಸಮಸ್ತದಲ್ಲಿ ಸಮಸ್ತವೂ ಆಗಿದ್ದಾರೆ.",
			"12": "<sup>12</sup> ನೀವು ದೇವರಿಂದ ಆಯ್ಕೆಯಾದವರು. ದೇವರಿಗೆ ಪ್ರಿಯವಾದವರು. ದೇವರ ಸ್ವಂತಜನರು. ಹೀಗಿರಲಾಗಿ ಕನಿಕರ, ದಯೆ, ದೀನತೆ, ವಿನಯಶೀಲತೆ, ಶಾಂತಿ, ಸಹನೆ ಎಂಬ ಸದ್ಗುಣಗಳೇ ನಿಮ್ಮ ಆಭರಣಗಳಾಗಿರಲಿ.",
			"13": "<sup>13</sup> ಒಬ್ಬರನ್ನೊಬ್ಬರು ಸೈರಿಸಿಕೊಳ್ಳಿ. ಒಬ್ಬನ ಮೇಲೆ ತಪ್ಪುಹೊರಿಸಲು ಕಾರಣವಿದ್ದರೂ ಕ್ಷಮಿಸಿಬಿಡಿ. ಪ್ರಭು ಯೇಸು ನಿಮ್ಮನ್ನು ಕ್ಷಮಿಸಿದಂತೆ ನೀವೂ ಇತರರನ್ನು ಕ್ಷಮಿಸಿರಿ.",
			"14": "<sup>14</sup> ಎಲ್ಲಕ್ಕಿಂತ ಮಿಗಿಲಾಗಿ ನಿಮ್ಮಲ್ಲಿ ಪ್ರೀತಿಯಿರಲಿ. ಪ್ರೀತಿಯೇ ಸಮಸ್ತವನ್ನು ಸಂಪೂರ್ಣಗೊಳಿಸುವ ಬಂಧನ.",
			"15": "<sup>15</sup> ನಿಮ್ಮ ಹೃನ್ಮನಗಳು ಕ್ರಿಸ್ತಯೇಸುವಿನ ಶಾಂತಿಸಮಾಧಾನದಿಂದ ತುಂಬಿರಲಿ. ನೀವು ಒಂದೇ‍ ಶರೀರವಾಗಿ ಬಾಳಲು ಕರೆಯಲ್ಪಟ್ಟಿದ್ದೀರಿ; ಕೃತಜ್ಞತೆ ಉಳ್ಳವರಾಗಿ ಜೀವಿಸಿರಿ.",
			"16": "<sup>16</sup> ಕ್ರಿಸ್ತಯೇಸುವಿನ ವಾಕ್ಯ ನಿಮ್ಮಲ್ಲಿ ನೆಲೆಸಿ ಸಮೃದ್ಧಿಯಾಗಿ ಬೆಳೆಯಲಿ. ಜ್ಞಾನಸಂಪನ್ನರಾಗಿ ಒಬ್ಬರಿಗೊಬ್ಬರು ಉಪದೇಶಮಾಡಿರಿ ಹಾಗೂ ಬುದ್ಧಿ ಹೇಳಿಕೊಳ್ಳಿರಿ. ಕೃತಜ್ಞತೆಯುಳ್ಳವರಾಗಿ ಕೀರ್ತನೆಗಳಿಂದಲೂ ಸಂಗೀತಗಳಿಂದಲೂ ಭಕ್ತಿಗೀತೆಗಳಿಂದಲೂ ಹೃದಯಾಂತರಾಳದಿಂದ ದೇವರಿಗೆ ಹಾಡಿರಿ.",
			"17": "<sup>17</sup> ನುಡಿಯಲ್ಲಾಗಲೀ ನಡೆಯಲ್ಲಾಗಲೀ ನೀವು ಏನು ಮಾಡಿದರೂ ಯೇಸುಸ್ವಾಮಿಯ ಹೆಸರಿನಲ್ಲಿಯೇ ಮಾಡಿರಿ. ಅವರ ಮುಖಾಂತರವೇ ಪಿತನಾದ ದೇವರಿಗೆ ಕೃತಜ್ಞತೆಯನ್ನು ಸಲ್ಲಿಸಿರಿ.",
			"18": "<sup>18</sup> ಮಹಿಳೆಯರೇ, ನಿಮ್ಮ ನಿಮ್ಮ ಪತಿಯರಿಗೆ ಪ್ರಭು ಮೆಚ್ಚುವಂತೆ ತಗ್ಗಿ ನಡೆದುಕೊಳ್ಳಿರಿ.",
			"19": "<sup>19</sup> ಪುರುಷರೇ, ನಿಮ್ಮ ನಿಮ್ಮ ಪತ್ನಿಯರನ್ನು ಪ್ರೀತಿಸಿರಿ. ಅವರೊಡನೆ ಕಠಿಣವಾಗಿ ವರ್ತಿಸದಿರಿ.",
			"20": "<sup>20</sup> ಮಕ್ಕಳೇ, ಎಲ್ಲದರಲ್ಲೂ ನಿಮ್ಮ ತಂದೆತಾಯಿಗಳ ಮಾತು ಕೇಳಿರಿ. ಹೀಗೆ ಮಾಡಿದರೆ ಪ್ರಭುವಿಗೆ ಮೆಚ್ಚುಗೆಯಾಗುತ್ತದೆ.",
			"21": "<sup>21</sup> ತಂದೆತಾಯಿಯರೇ, ನಿಮ್ಮ ಮಕ್ಕಳನ್ನು ಕೆರಳಿಸಿ ಮನಕುಗ್ಗಿಸದಿರಿ.",
			"22": "<sup>22</sup> ದಾಸ್ಯದಲ್ಲಿರುವವರೇ, ಎಲ್ಲಾ ವಿಷಯಗಳಲ್ಲೂ ನಿಮ್ಮ ನಿಮ್ಮ ಧಣಿಗಳಿಗೆ ವಿಧೇಯರಾಗಿರಿ. ಧಣಿಗಳನ್ನು ಮೆಚ್ಚಿಸುವ ಸಲುವಾಗಿ ಮುಖದಾಕ್ಷಿಣ್ಯದ ಸೇವೆಮಾಡಬೇಡಿ. ಪ್ರಭುವಿಗೆ ಭಯಪಟ್ಟು ಪ್ರಾಮಾಣಿಕತೆಯಿಂದ ದುಡಿಯಿರಿ.",
			"23": "<sup>23</sup> ನೀವು ಏನನ್ನು ಮಾಡಿದರೂ ಪೂರ್ಣಮನಸ್ಸಿನಿಂದ ಮಾಡಿ; ಮನುಷ್ಯರನ್ನು ಮೆಚ್ಚಿಸುವುದಕ್ಕಾಗಿ ಅಲ್ಲ, ಪ್ರಭುವನ್ನು ಮೆಚ್ಚಿಸುವುದಕ್ಕಾಗಿ ಮಾಡಿ.",
			"24": "<sup>24</sup> ಅದಕ್ಕೆ ಪ್ರತಿಫಲವಾಗಿ ಪ್ರಭುವಿನಿಂದ ನೀವು ಸ್ವರ್ಗೀಯ ಬಾಧ್ಯತೆಯನ್ನು ಪಡೆಯುವಿರೆಂದು ನಿಮಗೆ ತಿಳಿದೇ ಇದೆ.",
			"25": "<sup>25</sup> ಅನ್ಯಾಯ ಮಾಡುವವನು ತಾನು ಮಾಡಿದ ಅನ್ಯಾಯಕ್ಕೆ ತಕ್ಕ ಶಿಕ್ಷೆಯನ್ನು ಪಡೆಯುವನು. ದೇವರು ಪಕ್ಷಪಾತಿಯಲ್ಲ."
		},
		"4": {
			"1": "<sup>1</sup> ಯಜಮಾನರೇ, ಸ್ವರ್ಗಲೋಕದಲ್ಲಿ ನಿಮಗೂ ಒಬ್ಬ ಒಡೆಯನಿದ್ದಾನೆಂಬುದನ್ನು ತಿಳಿದುಕೊಂಡು ನಿಮ್ಮ ಮನೆಯಾಳುಗಳನ್ನು ನ್ಯಾಯಬದ್ಧ ರೀತಿಯಲ್ಲಿ ನೋಡಿಕೊಳ್ಳಿ.",
			"2": "<sup>2</sup> ಪ್ರಾರ್ಥನಾ ಜೀವನ ನಡೆಸಿರಿ. ಪ್ರಾರ್ಥಿಸುವಾಗಲೆಲ್ಲಾ ಎಚ್ಚರವಾಗಿದ್ದು ಕೃತಜ್ಞತಾಸ್ತುತಿಯನ್ನು ಸಲ್ಲಿಸಿರಿ.",
			"3": "<sup>3</sup> ಕ್ರಿಸ್ತಯೇಸುವಿನ ರಹಸ್ಯವನ್ನು ಪ್ರಚುರಪಡಿಸಲು ನಮಗೆ ಅವಕಾಶ ದೊರಕುವಂತೆ ಪ್ರಾರ್ಥಿಸಿರಿ. ಈ ಶುಭಸಂದೇಶದ ನಿಮಿತ್ತವೇ ನಾನು ಸೆರೆಯಲ್ಲಿ ಇದ್ದೇನೆಂಬುದು ನಿಮಗೆ ತಿಳಿದಿದೆ. ನಾನು ಶುಭಸಂದೇಶವನ್ನು ಸ್ಪಷ್ಟವಾಗಿಯೂ ಸರಳವಾಗಿಯೂ ಸಾರುವಂತೆ ಪ್ರಾರ್ಥಿಸಿರಿ.",
			"5": "<sup>5</sup> ನೆರೆಯವರ ವಿಷಯದಲ್ಲಿ ಜಾಣತನದಿಂದ ವರ್ತಿಸಿರಿ. ನಿಮಗಿರುವ ಸಮಯ ಸಂದರ್ಭವನ್ನು ಸದುಪಯೋಗಪಡಿಸಿಕೊಳ್ಳಿರಿ.",
			"6": "<sup>6</sup> ನಿಮ್ಮ ಸಂಭಾಷಣೆ ಯಾವಾಗಲೂ ಹಿತಕರವಾಗಿದ್ದು ಇತರರನ್ನು ಆಕರ್ಷಿಸುವಂತಿರಲಿ. ನೀವು ಯಾರು ಯಾರಿಗೆ, ಹೇಗೆ ಉತ್ತರಿಸಬೇಕು ಎಂಬುದನ್ನು ಕಲಿತುಕೊಳ್ಳಿರಿ.",
			"7": "<sup>7</sup> ನಮ್ಮ ಪ್ರಿಯ ಸಹೋದರ ತುಖಿಕನು ನನ್ನ ವಿಷಯದಲ್ಲಿ ನಿಮಗೆ ಎಲ್ಲಾ ವರ್ತಮಾನವನ್ನು ಕೊಡುವನು. ಆತನು ಪ್ರಾಮಾಣಿಕ ಸೇವಕನೂ ಪ್ರಭುವಿನ ಸೇವೆಯಲ್ಲಿ ನನ್ನ ಸಂಗಡಿಗನೂ ಆಗಿದ್ದಾನೆ.",
			"8": "<sup>8</sup> ನನ್ನ ವಿಷಯದಲ್ಲಿ ನಿಮಗೆ ಎಲ್ಲವನ್ನೂ ತಿಳಿಸಿ, ನಿಮ್ಮನ್ನು ಹುರಿದುಂಬಿಸಿ, ಪ್ರೋತ್ಸಾಹಿಸುವುದಕ್ಕಾಗಿ ಆತನನ್ನು ನಿಮ್ಮ ಬಳಿಗೆ ಕಳುಹಿಸಿದ್ದೇನೆ.",
			"9": "<sup>9</sup> ಆತನ ಜೊತೆಯಲ್ಲಿ ನಂಬಿಕಸ್ಥನಾದ ಪ್ರಿಯ ಸಹೋದರ ಒನೇಸಿಮನನ್ನು ಕಳುಹಿಸುತ್ತಿದ್ದೇನೆ. ಇವನು ನಿಮ್ಮ ಊರಿನ ಸಭೆಗೆ ಸೇರಿದವನು. ಇಲ್ಲಿ ನಡೆಯುವುದನ್ನೆಲ್ಲಾ ಇವರು ನಿಮಗೆ ತಿಳಿಸುವರು.",
			"10": "<sup>10</sup> ನನ್ನೊಡನೆ ಸೆರೆಯಾಳಾಗಿರುವ ಅರಿಸ್ತಾರ್ಕನೂ ಬಾರ್ನಬನಿಗೆ ಹತ್ತಿರದ ನೆಂಟನಾದ ಮಾರ್ಕನೂ ನಿಮಗೆ ಶುಭಾಶಯಗಳನ್ನು ಕಳುಹಿಸಿದ್ದಾರೆ. ಮಾರ್ಕನು ನಿಮ್ಮ ಬಳಿಗೆ ಬಂದಾಗ ಅವನನ್ನು ಸ್ವಾಗತಿಸಿರಿ. ಅವನ ವಿಷಯದಲ್ಲಿ ನಿಮಗೆ ಸಲಹೆಗಳನ್ನು ಕೊಡಲಾಗಿದೆ.",
			"11": "<sup>11</sup> ಯುಸ್ತನೆನಿಸಿಕೊಳ್ಳುವ ಯೇಸು ಕೂಡ ನಿಮಗೆ ವಂದನೆಗಳನ್ನು ತಿಳಿಸಿದ್ದಾನೆ. ಕ್ರೈಸ್ತರಾದ ಯೆಹೂದ್ಯರಲ್ಲಿ ಇವರೇ ನನ್ನೊಡನೆ ದೇವರ ಸಾಮ್ರಾಜ್ಯದ ಏಳಿಗೆಗಾಗಿ ದುಡಿಯುತ್ತಿರುವವರು. ಇವರಿಂದ ನನಗೆ ತುಂಬಾ ಆದರಣೆ ದೊರಕಿದೆ.",
			"12": "<sup>12</sup> ಕ್ರಿಸ್ತಯೇಸುವಿನ ಸೇವಕನೂ ನಿಮ್ಮ ಸಭೆಗೆ ಸೇರಿದವನೂ ಆದ ಎಪಫ್ರನಿಂದಲೂ ನಿಮಗೆ ವಂದನೆಗಳು. ನೀವು ಮಾಡುವ ಎಲ್ಲಾ ಕಾರ್ಯಗಳಲ್ಲೂ ದೇವರ ಚಿತ್ತಕ್ಕೆ ವಿಧೇಯರಾಗಿ ಸ್ಥಿರ ಹಾಗೂ ಸಿದ್ಧ ಕ್ರೈಸ್ತರಾಗಿ ಬಾಳಬೇಕೆಂದು ಅವನು ನಿಮಗಾಗಿ ಸದಾ ಆಸಕ್ತಿಯಿಂದ ಪ್ರಾರ್ಥಿಸುತ್ತಿದ್ದಾನೆ.",
			"13": "<sup>13</sup> ನಿಮಗಾಗಿಯೂ ಲವೊದಿಕೀಯ ಹಾಗೂ ಹಿರೆಯಾಪೊಲಿಯದ ಜನರಿಗಾಗಿಯೂ ಆತನು ಶ್ರಮವಹಿಸಿ ದುಡಿಯುತ್ತಿದ್ದಾನೆಂಬುದಕ್ಕೆ ನಾನೇ ಸಾಕ್ಷಿ.",
			"14": "<sup>14</sup> ನಮ್ಮ ಪ್ರಿಯ ವೈದ್ಯನಾದ ಲೂಕನೂ ದೇಮಾಸನೂ ನಿಮಗೆ ವಂದನೆಗಳನ್ನು ತಿಳಿಸಿದ್ದಾರೆ.",
			"15": "<sup>15</sup> ಲವೊದಿಕೀಯದಲ್ಲಿರುವ ನಮ್ಮ ಸಹೋದರರು ನಿಂಫಳು ಮತ್ತು ಆಕೆಯ ಮನೆಯಲ್ಲಿ ಸೇರುವ ಸಭೆಯವರು ಇವರೆಲ್ಲರಿಗೂ ನಮ್ಮ ಅಭಿನಂದನೆಗಳು.",
			"16": "<sup>16</sup> ಈ ಪತ್ರವನ್ನು ನೀವು ಓದಿದ ಬಳಿಕ ಲವೊದಿಕೀಯದ ಸಭೆಯವರಿಗೆ ಓದಿ ಹೇಳುವ ವ್ಯವಸ್ಥೆಮಾಡಿರಿ. ಅಂತೆಯೇ, ಲವೊದಿಕೀಯದಿಂದ ಕಳಿಸಲಾಗುವ ಪತ್ರವನ್ನು ತರಿಸಿಕೊಂಡು ಓದಿರಿ.",
			"17": "<sup>17</sup> ತನಗೆ ಒಪ್ಪಿಸಿದ ಕೆಲಸವನ್ನು ಜವಾಬ್ದಾರಿಯಿಂದ ನಿರ್ವಹಿಸಬೇಕೆಂಬುದಾಗಿ ಅರ್ಖಿಪ್ಪನಿಗೆ ತಿಳಿಸಿರಿ.",
			"18": "<sup>18</sup> ಇದನ್ನು ಪೌಲನಾದ ನಾನೇ ಸ್ವತಃ ಬರೆಯುತ್ತಿದ್ದೇನೆ. ನಾನು ಸೆರೆಯಲ್ಲಿದ್ದೇನೆ ಎಂಬುದು ನಿಮಗೆ ನೆನಪಿರಲಿ. ಶುಭಾಶಯಗಳು! ದೈವಾನುಗ್ರಹ ನಿಮ್ಮಲ್ಲಿರಲಿ!"
		}
	}
};
