var book = {
	"name": "೧ ತಿಮೊಥೇಯನಿಗೆ",
	"numChapters": 6,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ಕ್ರೈಸ್ತವಿಶ್ವಾಸದಲ್ಲಿ ಸತ್‍ಪುತ್ರನಾದ ತಿಮೊಥೇಯನಿಗೆ-ಪೌಲನು ಬರೆಯುವ ಪತ್ರ.",
			"2": "<sup>2</sup> ನಮ್ಮ ಉದ್ಧಾರಕರಾದ ದೇವರ ಮತ್ತು ನಮ್ಮ ನಿರೀಕ್ಷೆಯಾಗಿರುವ ಕ್ರಿಸ್ತಯೇಸುವಿನ ಆಜ್ಞಾನುಸಾರ, ನಾನು ಕ್ರಿಸ್ತಯೇಸುವಿನ ಪ್ರೇಷಿತನಾಗಿದ್ದೇನೆ. ಪಿತನಾದ ದೇವರೂ ನಮ್ಮ ಪ್ರಭುವಾದ ಕ್ರಿಸ್ತಯೇಸುವೂ ನಿನಗೆ ಕೃಪೆಯನ್ನೂ ಕರುಣೆಯನ್ನೂ ಶಾಂತಿಯನ್ನೂ ಅನುಗ್ರಹಿಸಲಿ!",
			"3": "<sup>3</sup> ನಾನು ಮಕೆದೋನಿಯಕ್ಕೆ ಹೋಗುವಾಗ ನಿನಗೆ ಹೇಳಿದಂತೆ, ನೀನು ಎಫೆಸದಲ್ಲಿಯೇ ಇರಬೇಕು. ಅಲ್ಲಿ ಕೆಲವರು ತಪ್ಪುಬೋಧನೆಯನ್ನು ಮಾಡುತ್ತಿದ್ದಾರೆ; ಅದನ್ನು ನೀನು ನಿಲ್ಲಿಸಬೇಕು.",
			"4": "<sup>4</sup> ಕಟ್ಟುಕತೆಗಳಲ್ಲೂ ಕೊನೆಮೊದಲಿಲ್ಲದ ವಂಶಾವಳಿಗಳ ನಿರೂಪಣೆಗಳಲ್ಲೂ ಕಾಲಹರಣ ಮಾಡಬಾರದೆಂದು ಅವರಿಗೆ ಆಜ್ಞಾಪಿಸಬೇಕು. ಅಂಥ ಕತೆಗಳು, ವಂಶಾವಳಿಗಳು ಅನಗತ್ಯವಾದ ವಾದವಿವಾದಗಳಿಗೆ ಎಡೆಮಾಡುತ್ತವೆಯೇ ಹೊರತು ದೇವರ ಯೋಜನೆ ಕೈಗೂಡಲು ಅವು ಅನುಕೂಲವಾಗಿಲ್ಲ; ವಿಶ್ವಾಸದಿಂದ ಮಾತ್ರ ಅದು ಸಾಧ್ಯ.",
			"5": "<sup>5</sup> ನಿರ್ಮಲ ಹೃದಯ, ಶುದ್ಧ ಮನಸ್ಸಾಕ್ಷಿ ಹಾಗೂ ನಿಷ್ಕಪಟ ವಿಶ್ವಾಸದಿಂದ ಹುಟ್ಟುವ ಪ್ರೀತಿಯು ವೃದ್ಧಿಯಾಗಬೇಕೆಂಬುದೇ ವಾಕ್ಯೋಪದೇಶದ ಉದ್ದೇಶ.",
			"6": "<sup>6</sup> ಕೆಲವರಾದರೋ ಈ ಮಾರ್ಗವನ್ನು ಬಿಟ್ಟು ವ್ಯರ್ಥವಾದ ವಾದವಿವಾದಗಳಲ್ಲಿಯೇ ಕಾಲಹರಣ ಮಾಡುತ್ತಿದ್ದಾರೆ;",
			"7": "<sup>7</sup> ತಮ್ಮ ಮಾತಿನ ಅರ್ಥವಾಗಲಿ, ತಮ್ಮ ವಾದದ ತಿರುಳಾಗಲಿ ಅವರಿಗೆ ತಿಳಿಯದಿದ್ದರೂ ಅವರು ಧರ್ಮೋಪದೇಶಕರಾಗಲು ಹವಣಿಸುತ್ತಾರೆ.",
			"8": "<sup>8</sup> ನಮಗೆಲ್ಲರಿಗೂ ತಿಳಿದಿರುವಂತೆ ಧರ್ಮಶಾಸ್ತ್ರವೇನೋ ಒಳ್ಳೆಯದು. ಆದರೆ ಅದನ್ನು ನಿಷ್ಠೆಯಿಂದ ಕಾರ್ಯಗತ ಮಾಡಬೇಕು.",
			"9": "<sup>9</sup> ಧರ್ಮಶಾಸ್ತ್ರದ ನಿರ್ಬಂಧನೆಗಳು ಸಜ್ಜನರಿಗಲ್ಲ; ಬದಲಿಗೆ ಅಕ್ರಮಿಗಳಿಗೆ, ಅವಿಧೇಯರಿಗೆ, ಅಧರ್ಮಿಗಳಿಗೆ, ಭಕ್ತಿಹೀನರಿಗೆ, ಪಾಪಿಗಳಿಗೆ, ಅಶುದ್ಧರಿಗೆ, ಪ್ರಾಪಂಚಿಕರಿಗೆ, ಹೆತ್ತವರನ್ನು ಹತ್ಯೆಮಾಡುವವರಿಗೆ ಮತ್ತು ಕೊಲೆಗಾರರಿಗೆ.",
			"10": "<sup>10</sup> ಅಂತೆಯೇ, ಕಾಮುಕರಿಗೆ, ಸಲಿಂಗ ಪ್ರೇಮಿಗಳಿಗೆ, ನರಚೋರರಿಗೆ, ಸುಳ್ಳುಸಾಕ್ಷಿಗಳಿಗೆ ಸುಳ್ಳಾಣೆ ಇಡುವವರಿಗೆ ಮತ್ತು ಸದ್ಧರ್ಮ ವಿರೋಧಿಗಳಿಗೆ ಮುಂತಾದವರಿಗೆ ಈ ನಿರ್ಬಂಧನೆಗಳು ನೇಮಕವಾಗಿವೆ.",
			"11": "<sup>11</sup> ಸ್ತುತ್ಯಾರ್ಹರೂ ಮಹಿಮಾಭರಿತರೂ ಆದ ದೇವರು ನನಗೆ ವಹಿಸಿರುವ ಶುಭಸಂದೇಶಕ್ಕೆ ಈ ಬೋಧನೆ ಅನುಗುಣವಾಗಿದೆ.",
			"12": "<sup>12</sup> ಶುಭಸಂದೇಶದ ಸೇವೆಯನ್ನು ಕೈಗೊಳ್ಳಲು ಯೋಗ್ಯನೆಂದು ಎಣಿಸಿ ನನಗೆ ಬೇಕಾದ ಶಕ್ತಿಸಾಮರ್ಥ್ಯವನ್ನು ದಯಪಾಲಿಸಿದ ನಮ್ಮ ಪ್ರಭು ಯೇಸುಕ್ರಿಸ್ತರಿಗೆ ನಾನು ಕೃತಜ್ಞತೆಯನ್ನು ಸಲ್ಲಿಸುತ್ತೇನೆ.",
			"13": "<sup>13</sup> ಹಿಂದೆ ನಾನು ಅವರನ್ನು ದೂಷಿಸಿದೆ, ಹಿಂಸಿಸಿದೆ, ಅವಮಾನಪಡಿಸಿದೆ. ಆದರೆ ದೇವರು ದಯಾಮಯಿ; ಆಗ ನಾನು ತಿಳಿಯದೆ ಅವಿಶ್ವಾಸಿಯಾಗಿ ಹಾಗೆ ಮಾಡಿದ್ದರಿಂದ ದೇವರು ನನಗೆ ಕರುಣೆತೋರಿದರು.",
			"14": "<sup>14</sup> ಕ್ರಿಸ್ತಯೇಸುವಿನ ಅನ್ಯೋನ್ಯತೆಯಿಂದ ಲಭಿಸುವ ಪ್ರೀತಿ ವಿಶ್ವಾಸದಲ್ಲಿ ಭಾಗಿಯಾಗುವಂತೆ ಅವರು ನನ್ನ ಮೇಲೆ ತಮ್ಮ ಕೃಪಾವರಗಳನ್ನು ಹೇರಳವಾಗಿ ಸುರಿಸಿದರು.",
			"15": "<sup>15</sup> ಕ್ರಿಸ್ತಯೇಸು, ಪಾಪಿಗಳ ಉದ್ಧಾರಕ್ಕಾಗಿ ಈ ಲೋಕಕ್ಕೆ ಬಂದರು ಎನ್ನುವ ಮಾತು ಸತ್ಯವಾದುದು, ನಂಬಲರ್ಹವಾದುದು ಹಾಗೂ ಎಲ್ಲರ ಅಂಗೀಕಾರಕ್ಕೆ ಯೋಗ್ಯವಾದುದು. ಅಂಥ ಪಾಪಿಗಳಲ್ಲಿ ನಾನೇ ಪ್ರಮುಖನು.",
			"16": "<sup>16</sup> ಇದರಿಂದಾಗಿ, ಮುಂದೆ ವಿಶ್ವಾಸಿಗಳಾಗಿ ನಿತ್ಯಜೀವ ಪಡೆಯುವವರಿಗೆ ಆದರ್ಶ ದೊರಕುವಂತೆ ಕ್ರಿಸ್ತಯೇಸು ನನಗೆ ಪೂರ್ಣ ಸಹನೆ ತೋರಿದರು. ಇದು ದೇವರ ಕರುಣೆಯೇ ಸರಿ.",
			"17": "<sup>17</sup> ಸರ್ವಯುಗಗಳ ಅರಸರೂ ಅಮರರೂ ಅಗೋಚರರೂ ಆಗಿರುವ ಏಕೈಕ ದೇವರಿಗೆ ಯುಗಯುಗಾಂತರಕ್ಕೆ ಗೌರವವೂ ಮಹಿಮೆಯೂ ಸಲ್ಲಲಿ! ಆಮೆನ್.",
			"18": "<sup>18</sup> ತಿಮೊಥೇಯನೇ, ನನ್ನ ಕುಮಾರನೇ, ಹಿಂದೆ ನಿನ್ನ ಕುರಿತು ಮಾಡಲಾಗಿರುವ ಪ್ರವಾದನೆಗಳಿಗೆ ಅನುಗುಣವಾಗಿ ನಾನು ನಿನಗೆ ಕೊಡುವ ಆಜ್ಞೆ ಇದು: ಪ್ರವಾದನೆಗಳಿಂದ ಪ್ರೇರಣೆ ಪಡೆದು ದಿಟ್ಟಹೋರಾಟವನ್ನು ಮಾಡು.",
			"19": "<sup>19</sup> ವಿಶ್ವಾಸವನ್ನೂ ಒಳ್ಳೆಯ ಮನಸ್ಸಾಕ್ಷಿಯನ್ನೂ ಸುರಕ್ಷಿತವಾಗಿ ಇಟ್ಟುಕೋ. ಕೆಲವರು ತಮ್ಮ ಮನಸ್ಸಾಕ್ಷಿಗೆ ವ್ಯತಿರಿಕ್ತವಾಗಿ ನಡೆದು ವಿಶ್ವಾಸವನ್ನೇ ಕಳೆದುಕೊಂಡಿದ್ದಾರೆ.",
			"20": "<sup>20</sup> ಹುಮೆನಾಯನೂ ಅಲೆಗ್ಸಾಂಡರನೂ ಇದಕ್ಕೆ ನಿದರ್ಶನವಾಗಿದ್ದಾರೆ. ದೇವದೂಷಣೆ ಮಾಡಬಾರದೆಂಬುದನ್ನು ಕಲಿತುಕೊಳ್ಳುವಂತೆ ಇವರನ್ನು ನಾನು ಸೈತಾನನ ವಶಕ್ಕೆ ಒಪ್ಪಿಸಿಬಿಟ್ಟಿದ್ದೇನೆ."
		},
		"2": {
			"1": "<sup>1</sup> ಮೊತ್ತಮೊದಲು ಮಾನವರೆಲ್ಲರಿಗೋಸ್ಕರ ದೇವರಿಗೆ ವಿಜ್ಞಾಪನೆ, ಪ್ರಾರ್ಥನೆ, ಬಿನ್ನಹ ಹಾಗೂ ಕೃತಜ್ಞತಾಸ್ತುತಿಗಳನ್ನು ಸಲ್ಲಿಸಬೇಕೆಂದು ಕೇಳಿಕೊಳ್ಳುತ್ತೇನೆ.",
			"2": "<sup>2</sup> ನಾವು ದೈವಭಕ್ತಿಯುಳ್ಳವರಾಗಿ ಮತ್ತು ಗೌರವಯುತರಾಗಿ ಶಾಂತಿಸಮಾಧಾನದಿಂದಲೂ ನೆಮ್ಮದಿಯಿಂದಲೂ ಜೀವಿಸಲು ಅನುಕೂಲವಾಗುವಂತೆ ಅರಸರಿಗಾಗಿಯೂ ಎಲ್ಲಾ ಅಧಿಕಾರಿಗಳಿಗಾಗಿಯೂ ಪ್ರಾರ್ಥಿಸಬೇಕು.",
			"3": "<sup>3</sup> ಹೀಗೆ ಮಾಡುವುದು ಒಳ್ಳೆಯದು, ನಮ್ಮ ಉದ್ಧಾರಕರಾದ ದೇವರಿಗೆ ಮೆಚ್ಚುಗೆಯಾದುದು.",
			"4": "<sup>4</sup> ಎಲ್ಲಾ ಮಾನವರೂ ಸತ್ಯವನ್ನರಿತು ಜೀವೋದ್ಧಾರ ಪಡೆಯಬೇಕೆಂಬುದೇ ದೈವೇಚ್ಛೆ.",
			"5": "<sup>5</sup> ದೇವರು ಒಬ್ಬರೇ; ದೇವರನ್ನೂ ಮಾನವರನ್ನೂ ಒಂದುಗೂಡಿಸುವ ಮಧ್ಯಸ್ಥರೂ ಒಬ್ಬರೇ; ಅವರೇ ಮಾನವರಾಗಿರುವ ಕ್ರಿಸ್ತಯೇಸು.",
			"6": "<sup>6</sup> ಇವರು ಎಲ್ಲರ ಉದ್ಧಾರಕ್ಕಾಗಿ ತಮ್ಮ ಪ್ರಾಣವನ್ನೇ ಅರ್ಪಿಸಿದರು. ಸರ್ವರೂ ಉದ್ಧಾರವಾಗಬೇಕೆಂಬ ದೈವೇಚ್ಛೆಗೆ ಸೂಕ್ತ ಸಮಯದಲ್ಲಿ ಸಾಕ್ಷಿ ನೀಡಿದರು.",
			"7": "<sup>7</sup> ವಿಶ್ವಾಸ ಮತ್ತು ಸತ್ಯದ ಸಂದೇಶವನ್ನು ಸಾರುವುದಕ್ಕಾಗಿಯೇ ದೇವರು ನನ್ನನ್ನು ಪ್ರೇಷಿತನನ್ನಾಗಿಯೂ ಯೆಹೂದ್ಯೇತರರಿಗೆ ಬೋಧಕನನ್ನಾಗಿಯೂ ನೇಮಕಮಾಡಿದರು. ನಾನು ಹೇಳುತ್ತಿರುವುದು ಸುಳ್ಳಲ್ಲ, ಸತ್ಯಸ್ಯ ಸತ್ಯ.",
			"8": "<sup>8</sup> ಪ್ರಾರ್ಥನೆ ಮಾಡುವ ಸ್ಥಳಗಳಲ್ಲೆಲ್ಲಾ ಪುರುಷರು ಕೋಪತಾಪವಿಲ್ಲದೆ, ಕೋಲಾಹಲವಿಲ್ಲದೆ ಕರಗಳನ್ನೆತ್ತಿ ಭಕ್ತಿಪೂರ್ವಕವಾಗಿ ಪ್ರಾರ್ಥಿಸಬೇಕು ಎಂಬುದೇ ನನ್ನ ಅಪೇಕ್ಷೆ.",
			"9": "<sup>9</sup> ಅಂತೆಯೇ, ಮಹಿಳೆಯರು, ಸಭ್ಯತೆಯನ್ನು ಮೀರದೆ ಘನತೆ, ಗೌರವಕ್ಕೆ ಒಪ್ಪುವಂತಹ ಉಡುಪನ್ನು ಧರಿಸಿಕೊಳ್ಳಬೇಕು.",
			"10": "<sup>10</sup> ಅವರು ಮಿತಿಮೀರಿದ ಕೇಶಾಲಂಕಾರ, ಆಭರಣಾಲಂಕಾರ, ವಸ್ತ್ರಾಲಂಕಾರಗಳಿಂದ ತಮ್ಮನ್ನೇ ಶೃಂಗರಿಸಿಕೊಳ್ಳುವುದರ ಬದಲು ದೈವಭಕ್ತೆಯರಾದ ಸ್ತ್ರೀಯರಿಗೆ ಲಕ್ಷಣವಾದ ಸತ್ಕಾರ್ಯಗಳಿಂದ ಅಲಂಕರಿಸಿಕೊಳ್ಳಲಿ.",
			"11": "<sup>11</sup> ಮಹಿಳೆಯರು ಮೌನದಿಂದಲೂ ನಮ್ರತೆಯಿಂದಲೂ ಧರ್ಮೋಪದೇಶವನ್ನು ಕಲಿಯಲಿ.",
			"12": "<sup>12</sup> ಅವರು ಬೋಧನೆಮಾಡುವುದಕ್ಕಾಗಲಿ, ಪುರುಷರ ಮೇಲೆ ಅಧಿಕಾರ ಚಲಾಯಿಸುವುದಕ್ಕಾಗಲಿ ನಾನು ಅನುಮತಿಸೆನು. ಅವರು ಮೌನ ತಾಳಬೇಕು.",
			"13": "<sup>13</sup> ಏಕೆಂದರೆ, ದೇವರು ಮೊದಲು ಆದಾಮನನ್ನು, ಅನಂತರ ಹವ್ವಳನ್ನು ಸೃಷ್ಟಿಸಿದರು.",
			"14": "<sup>14</sup> ಅಲ್ಲದೆ, ಮೊದಲು ವಂಚನೆಗೊಳಗಾಗಿ ದೇವರಿಗೆ ವಿರುದ್ಧ ಪಾಪಮಾಡಿದವಳು ಹವ್ವಳೇ ಹೊರತು ಆದಾಮನಲ್ಲ.",
			"15": "<sup>15</sup> ಮಹಿಳೆ ಸಚ್ಚರಿತಳಾಗಿದ್ದು ವಿಶ್ವಾಸ, ಪ್ರೀತಿ ಮತ್ತು ಪವಿತ್ರತೆಯಲ್ಲಿ ಪ್ರವರ್ಧಿಸಿದರೆ, ತನ್ನ ತಾಯ್ತನದ ಮೂಲಕ ಸಂರಕ್ಷಣೆಯನ್ನು ಪಡೆಯುತ್ತಾಳೆ."
		},
		"3": {
			"1": "<sup>1</sup> ಯಾರಾದರೂ ಧರ್ಮಾಧ್ಯಕ್ಷನಾಗಲು ಅಪೇಕ್ಷಿಸಿದರೆ, ಅಂಥವನು ಉತ್ತಮ ಸೇವೆಯನ್ನೇ ಬಯಸುತ್ತಾನೆಂಬುದು ನಿಜ.",
			"2": "<sup>2</sup> ಅಂಥವನು ನಿಂದಾರಹಿತನಾಗಿರಬೇಕು. ಏಕಪತ್ನಿ ಉಳ್ಳವನು, ಸ್ವಸ್ಥಬುದ್ಧಿಯುಳ್ಳವನು, ಜಿತೇಂದ್ರಿಯನು, ಗೌರವಸ್ಥನು ಮತ್ತು ಅತಿಥಿ ಸತ್ಕಾರ ಮಾಡುವವನು ಆಗಿರಬೇಕು. ಬೋಧಿಸುವುದರಲ್ಲಿ ಪ್ರವೀಣನಾಗಿರಬೇಕು.",
			"3": "<sup>3</sup> ಅವನು ಕುಡುಕನು, ಕಲಹಪ್ರಿಯನು ಮತ್ತು ಹೊಡೆದಾಡುವವನು ಆಗಿರಬಾರದು. ಶಾಂತನೂ ದಯಾವಂತನೂ ಹಣದ ವ್ಯಾಮೋಹ ಇಲ್ಲದವನೂ ಆಗಿರಬೇಕು.",
			"4": "<sup>4</sup> ತನ್ನ ಕುಟುಂಬವನ್ನು ಜವಾಬ್ದಾರಿಯುತವಾಗಿ ನಡೆಸುವವನೂ ಮಕ್ಕಳನ್ನು ಶಿಸ್ತಿನಲ್ಲಿ ಬೆಳೆಸಿ ಅವರಿಂದ ಗೌರವವನ್ನು ಪಡೆದುಕೊಳ್ಳುವವನೂ ಆಗಿರಬೇಕು.",
			"5": "<sup>5</sup> ತನ್ನ ಕುಟುಂಬವನ್ನೇ ಜವಾಬ್ದಾರಿಯುತವಾಗಿ ನಡೆಸಲು ತಿಳಿಯದವನು ದೇವರ ಸಭೆಯನ್ನು ಹೇಗೆ ತಾನೇ ಪರಿಶೀಲಿಸಿಯಾನು?",
			"6": "<sup>6</sup> ಆತನು ಹೊಸದಾಗಿ ಕ್ರೈಸ್ತಧರ್ಮವನ್ನು ಅಂಗೀಕರಿಸಿದವನಾಗಿರಬಾರದು; ಇಲ್ಲದಿದ್ದರೆ ಅಹಂಕಾರದಿಂದ ಉಬ್ಬಿಕೊಂಡು ಸೈತಾನನಂತೆಯೇ ದಂಡನೆಗೆ ಗುರಿಯಾದಾನು.",
			"7": "<sup>7</sup> ಕ್ರೈಸ್ತರಲ್ಲದವರಿಂದಲೂ ಆತನು ಒಳ್ಳೆಯವನೆನಿಸಿಕೊಂಡಿರಬೇಕು. ಹೀಗಿದ್ದರೆ ಮಾತ್ರ ನಿಂದೆಗೊಳಗಾಗದೆ ಸೈತಾನನ ಕುತಂತ್ರಗಳಿಂದ ದೂರವಿರಬಲ್ಲನು.",
			"8": "<sup>8</sup> ಸಭಾಸೇವಕರು ಸಜ್ಜನರಾಗಿರಬೇಕು. ಎರಡು ನಾಲಿಗೆಯುಳ್ಳವರೂ ಕುಡುಕರೂ ಲಾಭಕೋರರೂ ಆಗಿರಬಾರದು.",
			"9": "<sup>9</sup> ವಿಶ್ವಾಸದ ನಿಗೂಢರಹಸ್ಯಗಳನ್ನು ಶುದ್ಧ ಮನಸ್ಸಾಕ್ಷಿಯಿಂದ ಕಾಪಾಡಿಕೊಂಡು ಬರುವವರಾಗಿರಬೇಕು.",
			"10": "<sup>10</sup> ಇವರನ್ನು ಮೊದಲು ಪರೀಕ್ಷೆಗೊಳಪಡಿಸಬೇಕು. ಯಾವುದೇ ಕುಂದುಕೊರತೆ ಕಂಡುಬರದಿದ್ದಲ್ಲಿ, ಇವರನ್ನು ಸಭಾಸೇವಕರನ್ನಾಗಿ ನೇಮಿಸಬಹುದು.",
			"11": "<sup>11</sup> ಅಂತೆಯೇ, ಸಭಾಸೇವಕಿಯರು ಸಜ್ಜನೆಯರಾಗಿರಬೇಕು. ಚಾಡಿಮಾತುಗಳನ್ನು ಆಡುವವರೂ ಮದ್ಯಾಸಕ್ತರೂ ಆಗಿರದೆ ಎಲ್ಲ ವಿಷಯಗಳಲ್ಲಿ ನಂಬಿಕಸ್ಥರಾಗಿರಬೇಕು.",
			"12": "<sup>12</sup> ಸಭಾಸೇವಕರು ಏಕಪತ್ನಿಯುಳ್ಳವರಾಗಿದ್ದು ತಮ್ಮ ಮಕ್ಕಳನ್ನೂ ಕುಟುಂಬವನ್ನೂ ಜವಾಬ್ದಾರಿಯುತವಾಗಿ ನಡೆಸಿಕೊಂಡು ಹೋಗುವವರಾಗಿರಬೇಕು.",
			"13": "<sup>13</sup> ನಿಷ್ಠೆಯಿಂದ ಸೇವೆಸಲ್ಲಿಸುವ ಸಭಾಸೇವಕರು ಒಳ್ಳೆಯ ಸ್ಥಾನಮಾನಗಳನ್ನು ಪಡೆಯುತ್ತಾರೆ: ಕ್ರಿಸ್ತಯೇಸುವಿನಲ್ಲಿ ಅವರಿಗಿರುವ ವಿಶ್ವಾಸವನ್ನು ಸಾರಲು ಧೈರ್ಯಸ್ಥೈರ್ಯವನ್ನು ಪಡೆಯುತ್ತಾರೆ.",
			"14": "<sup>14</sup> ನಾನು ನಿನ್ನ ಬಳಿಗೆ ಬೇಗನೆ ಬರುವೆನೆಂಬ ನಿರೀಕ್ಷೆಯಿಂದಲೇ ಈ ಪತ್ರವನ್ನು ಬರೆಯುತ್ತಿದ್ದೇನೆ.",
			"15": "<sup>15</sup> ಒಂದು ವೇಳೆ ನಾನು ಬರುವುದು ತಡವಾದರೆ, ದೇವರ ಮಂದಿರದಲ್ಲಿ ಅಂದರೆ, ಜೀವಸ್ವರೂಪಿಯಾದ ದೇವರ ಸಭೆಯಲ್ಲಿ, ಹೇಗೆ ನಡೆದುಕೊಳ್ಳಬೇಕು ಎಂಬುದು ನಿನಗೆ ತಿಳಿಯಲೆಂದು ಇದನ್ನು ಬರೆದಿದ್ದೇನೆ. ಸಭೆಯು ಸತ್ಯಕ್ಕೆ ಸ್ತಂಭವೂ ಅಸ್ತಿವಾರವೂ ಆಗಿದೆ.",
			"16": "<sup>16</sup> ನಿಜವಾಗಿಯೂ ನಮ್ಮ ಧರ್ಮದ ನಿಗೂಢಾರ್ಥ ಶ್ರೇಷ್ಠವಾದದ್ದು ಎಂಬುದು ನಿಸ್ಸಂದೇಹವಾದ ವಿಷಯ. “ನರಮಾನವ ರೂಪದಲಿ ಪ್ರತ್ಯಕ್ಷನಾಗಿ ದೇವರಿಗೆ ಪ್ರಿಯನೆಂದು ಪವಿತ್ರಾತ್ಮನಿಂದ ಪ್ರಕಟಿತನಾಗಿ ದೇವದೂತರಿಗೆ ಪ್ರದರ್ಶಿತವಾಗಿ ಅನ್ಯಜನರಿಗೆ ಪ್ರಬೋಧಿತನಾಗಿ ಜಗದಲ್ಲೆಲ್ಲೂ ವಿಶ್ವಾಸಪಡೆದವನಾಗಿ ಸ್ವರ್ಗಕ್ಕೇರಿದಾತ ಮಹಿಮಾನ್ವಿತ ಯೇಸುಕ್ರಿಸ್ತ."
		},
		"4": {
			"1": "<sup>1</sup> ಮುಂಬರುವ ದಿನಗಳಲ್ಲಿ ಕೆಲವರು ವಂಚಿಸುವ ಆತ್ಮಗಳಿಗೆ ಬಲಿಬಿದ್ದು, ದುರಾತ್ಮಗಳ ದುರುಪದೇಶಗಳಿಗೆ ಕಿವಿಗೊಟ್ಟು ವಿಶ್ವಾಸಭ್ರಷ್ಟರಾಗುವರೆಂದು ಪವಿತ್ರಾತ್ಮ ಸ್ಪಷ್ಟವಾಗಿ ತಿಳಿಸಿದ್ದಾರೆ.",
			"2": "<sup>2</sup> ಕಪಟಿಗಳು ದುರ್ಬೋಧನೆಗಳನ್ನು ಪ್ರಸರಿಸುತ್ತಾರೆ. ಬರೆಹಾಕಿದ ಚರ್ಮದಂತೆ ಅವರ ಮನಸ್ಸಾಕ್ಷಿಯು ಸುಟ್ಟುಹೋಗಿದೆ.",
			"3": "<sup>3</sup> ಮದುವೆಯಾಗಬಾರದು, ಇಂತಿಂಥ ಆಹಾರವನ್ನು ಸೇವಿಸಬಾರದು ಎಂದು ಬೋಧಿಸುತ್ತಾರೆ. ಆದರೆ ಸತ್ಯವನ್ನು ಅರಿತು ಯಾರು ವಿಶ್ವಾಸಿಗಳಾಗಿದ್ದಾರೋ ಅಂಥವರು ಎಲ್ಲ ಆಹಾರಪದಾರ್ಥಗಳನ್ನು ಕೃತಜ್ಞತಾಸ್ತುತಿಯೊಡನೆ ಸೇವಿಸಲೆಂದೇ ದೇವರು ಅವುಗಳನ್ನು ಸೃಷ್ಟಿಸಿದ್ದಾರೆ.",
			"4": "<sup>4</sup> ದೇವರು ಸೃಷ್ಟಿಸಿದ್ದೆಲ್ಲವೂ ಒಳ್ಳೆಯದೇ. ಅವರಿಗೆ ಸ್ತುತಿಸಲ್ಲಿಸಿ ಸ್ವೀಕರಿಸಿದ ಯಾವುದನ್ನೂ ತಿರಸ್ಕರಿಸಬೇಕಾಗಿಲ್ಲ.",
			"5": "<sup>5</sup> ಏಕೆಂದರೆ, ದೇವರ ವಾಕ್ಯದಿಂದಲೂ ನಮ್ಮ ಪ್ರಾರ್ಥನೆಯಿಂದಲೂ ಅದು ಪಾವನಗೊಳ್ಳುತ್ತದೆ.",
			"6": "<sup>6</sup> ಈ ವಿಷಯಗಳನ್ನೆಲ್ಲಾ ಇತರ ಸಹೋದರರಿಗೆ ನೀನು ವಿವರಿಸುವವನಾದರೆ, ನೀನು ಕ್ರಿಸ್ತಯೇಸುವಿನ ಯೋಗ್ಯ ದಾಸನಾಗುವೆ, ನೀನು ಅನುಸರಿಸುತ್ತಾ ಬಂದಿರುವ ಸದ್ಬೋಧನೆಯಿಂದಲೂ ವಿಶ್ವಾಸದಿಂದಲೂ ಆಧ್ಯಾತ್ಮಿಕ ಪೋಷಣೆಯನ್ನು ಪಡೆಯುವೆ.",
			"7": "<sup>7</sup> ಪ್ರಾಪಂಚಿಕ ಹಾಗೂ ತಿರುಳಿಲ್ಲದ ಕಟ್ಟುಕತೆಗಳನ್ನು ತಳ್ಳಿಬಿಟ್ಟು, ನಿನ್ನ ಭಕ್ತಿಯನ್ನು ವೃದ್ಧಿಮಾಡಿಕೋ.",
			"8": "<sup>8</sup> ಅಂಗಸಾಧನೆಯು ಸ್ವಲ್ಪಮಟ್ಟಿಗೆ ಪ್ರಯೋಜನಕರವಾದುದು; ಭಕ್ತಿಸಾಧನೆಯಾದರೋ ಎಲ್ಲಾ ದೃಷ್ಟಿಯಿಂದಲೂ ಪ್ರಯೋಜನಕರವಾದುದು. ಭಕ್ತಿಸಾಧನೆಯಿಂದ ಇಹಪರಗಳೆರಡರಲ್ಲೂ ನಿತ್ಯಜೀವವನ್ನು ಪಡೆಯುವ ಭರವಸೆ ನಮಗಿದೆ.",
			"9": "<sup>9</sup> ಈ ಉಪದೇಶ ಸತ್ಯವಾದುದು ಹಾಗೂ ನಂಬಲರ್ಹವಾದುದು; ಎಲ್ಲರ ಅಂಗೀಕಾರಕ್ಕೂ ಯೋಗ್ಯವಾದುದು.",
			"10": "<sup>10</sup> ಸಕಲ ಮಾನವರ, ವಿಶೇಷವಾಗಿ ಕ್ರೈಸ್ತವಿಶ್ವಾಸಿಗಳ, ಉದ್ಧಾರಕರಾದ ಜೀವಸ್ವರೂಪ ದೇವರಲ್ಲಿಯೇ ನಾವು ಭರವಸೆಯಿಟ್ಟಿದ್ದೇವೆ; ಅದಕ್ಕಾಗಿಯೇ ನಾವು ಹೋರಾಡುತ್ತೇವೆ ಹಾಗೂ ಶ್ರಮಿಸುತ್ತೇವೆ.",
			"11": "<sup>11</sup> ಈ ಅಂಶಗಳನ್ನು ಆಜ್ಞಾಪಿಸಿ ಬೋಧಿಸು.",
			"12": "<sup>12</sup> ನೀನಿನ್ನೂ ಯುವಕನೆಂದು ನಿನ್ನನ್ನು ತಾತ್ಸಾರ ಮಾಡುವುದಕ್ಕೆ ಯಾರಿಗೂ ಅವಕಾಶ ಕೊಡಬೇಡ. ನಿನ್ನ ನಡೆನುಡಿ, ಪ್ರೀತಿವಿಶ್ವಾಸ ಹಾಗೂ ಪರಿಶುದ್ಧತೆಯ ವಿಷಯದಲ್ಲಿ ವಿಶ್ವಾಸಿಗಳಿಗೆಲ್ಲರಿಗೂ ನೀನೇ ಆದರ್ಶಪ್ರಾಯನಾಗಿರು.",
			"13": "<sup>13</sup> ನಾನು ಬರುವವರೆಗೂ ಪವಿತ್ರಗ್ರಂಥವನ್ನು ಪಠಿಸುವುದರಲ್ಲೂ ಪ್ರಬೋಧಿಸುವುದರಲ್ಲೂ ಉಪದೇಶಿಸುವುದರಲ್ಲೂ ಆಸಕ್ತನಾಗಿರು.",
			"14": "<sup>14</sup> ನಿನ್ನಲ್ಲಿರುವ ವರದಾನವನ್ನು ಅಲಕ್ಷಿಸಬೇಡ. ಸಭೆಯ ಹಿರಿಯರು ನಿನ್ನ ಮೇಲೆ ದೈವವಾಕ್ಯದ ಉಚ್ಚಾರದೊಂದಿಗೆ ಹಸ್ತನಿಕ್ಷೇಪಮಾಡಿದಾಗ ಈ ವರವು ನಿನಗೆ ಲಭಿಸಿತಲ್ಲವೇ?",
			"15": "<sup>15</sup> ನಿನ್ನ ಆಧ್ಯಾತ್ಮಿಕ ಪ್ರಗತಿ ಎಲ್ಲರಿಗೂ ಗೋಚರವಾಗುವಂತೆ ಈ ಕಾರ್ಯಸಾಧನೆಗಳಲ್ಲಿ ನಿರತನಾಗಿರು;",
			"16": "<sup>16</sup> ಇವುಗಳಲ್ಲೇ ಮಗ್ನನಾಗಿರು. ಇದರಿಂದ ನಿನ್ನ ಮತ್ತು ನಿನ್ನ ಉಪದೇಶವನ್ನು ಕೇಳುವವರ ಜೀವೋದ್ಧಾರವನ್ನು ಸಾಧಿಸುವೆ."
		},
		"5": {
			"1": "<sup>1</sup> ಹಿರಿಯರನ್ನು ಗದರಿಸಬೇಡ; ಅವರನ್ನು ತಂದೆಯಂತೆ ಭಾವಿಸಿ ಗೌರವದಿಂದ ತಿಳುವಳಿಕೆ ನೀಡು. ಯುವಕರೊಡನೆ ಸೋದರಭಾವನೆಯಿಂದ ವರ್ತಿಸು.",
			"2": "<sup>2</sup> ವೃದ್ಧೆಯರೊಡನೆ ಮಾತೃಭಾವನೆಯಿಂದ ನಡೆದುಕೋ. ಯುವತಿಯರನ್ನು ಅಕ್ಕತಂಗಿಯರೆಂದು ಪರಿಗಣಿಸಿ, ಪರಿಶುದ್ಧ ಭಾವನೆಯಿಂದ ನಡೆದುಕೋ.",
			"3": "<sup>3</sup> ದಿಕ್ಕಿಲ್ಲದ ವಿಧವೆಯರನ್ನು ಸಂರಕ್ಷಿಸು.",
			"4": "<sup>4</sup> ವಿಧವೆಯಾದವಳಿಗೆ ಮಕ್ಕಳಾಗಲಿ ಮೊಮ್ಮಕ್ಕಳಾಗಲಿ ಇದ್ದರೆ, ಆ ಮಕ್ಕಳು ಮೊತ್ತಮೊದಲು ತಮ್ಮ ಸ್ವಂತ ಕುಟುಂಬಕ್ಕೆ ಸಹಾಯಮಾಡಬೇಕಾದುದು ಅವರ ಧಾರ್ಮಿಕ ಕರ್ತವ್ಯವೆಂಬುದು ಅವರಿಗೆ ತಿಳಿದಿರಲಿ. ಹೀಗೆ ಅವರು ತಮ್ಮ ತಂದೆತಾಯಿಯರಿಗೆ ಪ್ರತ್ಯುಪಕಾರಮಾಡಿದಂತಾಗುತ್ತದೆ. ಅಲ್ಲದೆ, ಇದು ದೇವರ ದೃಷ್ಟಿಯಲ್ಲಿ ಮೆಚ್ಚುಗೆ ಪಡೆದುದು.",
			"5": "<sup>5</sup> ದಿಕ್ಕಿಲ್ಲದೆ ಒಬ್ಬಂಟಿಗಳಾಗಿರುವ ವಿಧವೆ ದೇವರ ಮೇಲೆ ಭರವಸೆಯಿಟ್ಟು ಹಗಲಿರುಳು ಪ್ರಾರ್ಥನೆಗಳಲ್ಲೂ ವಿಜ್ಞಾಪನೆಗಳಲ್ಲೂ ನಿರತಳಾಗಿರುತ್ತಾಳೆ.",
			"6": "<sup>6</sup> ವಿಲಾಸಿಯಾದ ವಿಧವೆಯಾದರೋ ಬದುಕಿದ್ದರೂ ಸತ್ತಂತೆಯೇ.",
			"7": "<sup>7</sup> ವಿಧವೆಯರು ಯಾವ ದೋಷಾರೋಪಣೆಗೂ ಗುರಿಯಾಗದಂತೆ, ಅವರಿಗೆ ಈ ಆಜ್ಞೆಗಳನ್ನು ಕೊಡು.",
			"8": "<sup>8</sup> ಯಾರಾದರೂ ತನ್ನ ಸಂಬಂಧಿಕರನ್ನು, ವಿಶೇಷವಾಗಿ ಸ್ವಂತ ಕುಟುಂಬದವರನ್ನು ಸಂರಕ್ಷಿಸದೆಹೋದರೆ, ಅಂಥವನು ವಿಶ್ವಾಸಭ್ರಷ್ಟನೂ ಅವಿಶ್ವಾಸಿಗಳಿಗಿಂತ ತುಚ್ಛನಾದವನೂ ಆಗಿದ್ದಾನೆ.",
			"9": "<sup>9</sup> ಅರವತ್ತು ವರ್ಷಕ್ಕೆ ಕಡಿಮೆಯಿಲ್ಲದ ವಿಧವೆಯನ್ನು, ವಿಧವೆಯರ ಪಟ್ಟಿಗೆ ಸೇರಿಸಿಕೊಳ್ಳಬಹುದು. ಅಂಥವಳು ಒಂದೇ ಸಾರಿ ಮದುವೆ ಆದವಳಾಗಿರಬೇಕು.",
			"10": "<sup>10</sup> ಆಕೆ ಮಕ್ಕಳನ್ನು ಯೋಗ್ಯವಾಗಿ ಸಾಕಿಸಲಹಿದವಳೂ ಅತಿಥಿಸತ್ಕಾರ ಮಾಡಿದವಳೂ ಆಗಿರಬೇಕು. ಅಲ್ಲದೆ, ದೇವಜನರ ಪಾದಸೇವೆ ಮಾಡಿದವಳೂ ಸಂಕಷ್ಟದಲ್ಲಿದ್ದವರಿಗೆ ಸಹಾಯ ಮಾಡಿದವಳೂ ಆಗಿರಬೇಕು. ಇಂಥಾ ಪುಣ್ಯಕಾರ್ಯಗಳನ್ನು ಮಾಡಿ ಸತ್ಕ್ರಿಯೆಗಳಿಗೆ ಹೆಸರಾದವಳೂ ಆಗಿರಬೇಕು.",
			"11": "<sup>11</sup> ಯೌವನಸ್ಥ ವಿಧವೆಯರನ್ನು ಆ ಪಟ್ಟಿಯಲ್ಲಿ ಸೇರಿಸಬೇಡ. ಏಕೆಂದರೆ, ಯೇಸುಕ್ರಿಸ್ತರಿಂದ ವಿಮುಖರನ್ನಾಗಿಸುವ ಕಾಮನೆಗಳು ಇವರನ್ನು ಮರುಮದುವೆಗೆ ಹುರಿದುಂಬಿಸಬಹುದು.",
			"12": "<sup>12</sup> ಹೀಗೆ, ಅವರು ಮೊದಲು ಮಾಡಿದ ವಾಗ್ದಾನವನ್ನು ಉಲ್ಲಂಘಿಸಿ ತೀರ್ಪಿಗೆ ಗುರಿಯಾಗುತ್ತಾರೆ.",
			"13": "<sup>13</sup> ಇದಲ್ಲದೆ ಅವರು ಮನೆಮನೆಗಳನ್ನು ಸುತ್ತುತ್ತಾ ಅಲೆದಾಡುತ್ತಾ ಹರಟೆಮಲ್ಲರೂ ಸೋಮಾರಿಗಳೂ ಆಗಬಹುದು. ಇತರರ ವಿಷಯಗಳಲ್ಲಿ ತಲೆಹಾಕಬಹುದು.",
			"14": "<sup>14</sup> ಆದ್ದರಿಂದ, ತರುಣಿ ವಿಧವೆಯರು ವಿವಾಹಮಾಡಿಕೊಂಡು, ಮಕ್ಕಳನ್ನು ಹೆತ್ತು ಸಂಸಾರವನ್ನು ನಡೆಸುವುದೇ ಒಳ್ಳೆಯದೆಂದು ನನ್ನ ಭಾವನೆ. ಆಗ ನಮ್ಮನ್ನು ನಿಂದಿಸಲು ವಿರೋಧಿಗಳಿಗೆ ಆಸ್ಪದವಿರುವುದಿಲ್ಲ.",
			"15": "<sup>15</sup> ಏಕೆಂದರೆ, ಈಗಾಗಲೇ ಕೆಲವು ವಿಧವೆಯರು ದಾರಿತಪ್ಪಿ ಸೈತಾನನನ್ನು ಹಿಂಬಾಲಿಸಿದ್ದಾರೆ.",
			"16": "<sup>16</sup> ಕ್ರೈಸ್ತವಿಶ್ವಾಸಿಯಾದ ಒಬ್ಬ ಮಹಿಳೆಯ ಮನೆಯಲ್ಲಿ ವಿಧವೆಯಿದ್ದರೆ, ಆಕೆಯೇ ಅವರನ್ನು ಸಂರಕ್ಷಿಸಲಿ. ಅವರ ಪೋಷಣೆಯ ಹೊಣೆಯನ್ನು ಧರ್ಮಸಭೆ ವಹಿಸಿಕೊಳ್ಳುವುದು ತಪ್ಪುತ್ತದೆ. ಆಗ ನಿಜವಾಗಿಯೂ ದಿಕ್ಕಿಲ್ಲದ ವಿಧವೆಯರಿಗೆ ಸಭೆ ನೆರವು ನೀಡಲು ಸಾಧ್ಯವಾಗುತ್ತದೆ.",
			"17": "<sup>17</sup> ಸರಿಯಾಗಿ ಆಡಳಿತವನ್ನು ನಿರ್ವಹಿಸುತ್ತಿರುವ ಸಭಾಹಿರಿಯರು, ವಿಶೇಷವಾಗಿ ಬೋಧನೆಯಲ್ಲೂ ಉಪದೇಶದಲ್ಲೂ ಶ್ರಮಿಸುತ್ತಿರುವವರು, ಇಮ್ಮಡಿ ಸಂಭಾವನೆಗೆ ಯೋಗ್ಯರು.",
			"18": "<sup>18</sup> ‘ಕಣ ತುಳಿಯುವ ಎತ್ತಿನ ಬಾಯನ್ನು ಕಟ್ಟಬೇಡ’ ಎಂದೂ, ‘ಕಾರ್ಮಿಕನು ಕೂಲಿಗೆ ಅರ್ಹನು’ ಎಂದೂ ಧರ್ಮಶಾಸ್ತ್ರ ಹೇಳುತ್ತದೆಯಲ್ಲವೇ?",
			"19": "<sup>19</sup> ಸಭಾಹಿರಿಯರ ವಿರುದ್ಧ ಯಾರಾದರೂ ದೂರು ತಂದರೆ, ಇಬ್ಬರ ಅಥವಾ ಮೂವರ ಸಾಕ್ಷಿ ಇಲ್ಲದೆ ಅದನ್ನು ಲಕ್ಷ್ಯಕ್ಕೆ ತಂದುಕೊಳ್ಳಬೇಡ.",
			"20": "<sup>20</sup> ಯಾರಾದರೂ ಪಾಪದಲ್ಲಿಯೇ ಮುಂದುವರಿಯುತ್ತಾ ಇದ್ದರೆ, ಅಂಥವರನ್ನು ಬಹಿರಂಗವಾಗಿಯೇ ಖಂಡಿಸು. ಇದು ಇತರರಿಗೂ ಎಚ್ಚರಿಕೆಯಾಗಿರಲಿ.",
			"21": "<sup>21</sup> ದೇವರ ಸನ್ನಿಧಿಯಲ್ಲಿಯೂ ಕ್ರಿಸ್ತಯೇಸುವಿನ ಪ್ರಸನ್ನತೆಯಲ್ಲಿಯೂ ಹಾಗೂ ಆಯ್ಕೆಯಾದ ದೇವದೂತರ ಮುಂದೆಯೂ ನಾನು ನಿನಗೆ ಆಜ್ಞಾಪಿಸುವುದಿದು: ಪೂರ್ವಾಗ್ರಹದಿಂದಾಗಲಿ, ಪಕ್ಷಪಾತದಿಂದಾಗಲಿ ಏನೂ ಮಾಡಬೇಡ.",
			"22": "<sup>22</sup> ದೇವರ ಸೇವೆಗೆಂದು ಅವಸರದಿಂದ ಯಾರ ಮೇಲೂ ಹಸ್ತನಿಕ್ಷೇಪ ಮಾಡಿ ದೀಕ್ಷೆ ನೀಡಬೇಡ. ಹಾಗೆ ಮಾಡಿದರೆ, ಅವರ ಪಾಪಗಳಲ್ಲಿ ನೀನೂ ಭಾಗಿಯಾಗುವೆ. ನೀನು ಪರಿಶುದ್ಧನಾಗಿರುವ ಹಾಗೆ ನೋಡಿಕೋ.",
			"23": "<sup>23</sup> ಆಗಾಗ್ಗೆ ನೀನು ಅಸ್ವಸ್ಥನಾಗುವುದುಂಟು. ಆದ್ದರಿಂದ ಬರೀ ನೀರನ್ನು ಕುಡಿದರೆ ಸಾಲದು. ನಿನ್ನ ಜೀರ್ಣಶಕ್ತಿಯ ಸುಧಾರಣೆಗಾಗಿ ಸ್ವಲ್ಪ ದ್ರಾಕ್ಷಾರಸವನ್ನೂ ಸೇವಿಸು.",
			"24": "<sup>24</sup> ಕೆಲವರ ಪಾಪಕೃತ್ಯಗಳು ತೀರ್ಪಿಗೆ ಮುಂಚೆಯೇ ಬಟ್ಟಬಯಲಾಗುತ್ತವೆ; ಇನ್ನು ಕೆಲವರ ಪಾಪಗಳು ಮರೆಯಾಗಿದ್ದು ಕ್ರಮೇಣ ಬೆಳಕಿಗೆ ಬರುತ್ತವೆ.",
			"25": "<sup>25</sup> ಅಂತೆಯೇ, ಕೆಲವರ ಸತ್ಕಾರ್ಯಗಳು ಎಲ್ಲರಿಗೂ ಬೇಗನೆ ಗೋಚರವಾಗುತ್ತವೆ. ಇನ್ನು ಕೆಲವರ ಸತ್ಕಾರ್ಯಗಳು ಅಗೋಚರವಾಗಿದ್ದರೂ ಸದಾ ಗುಟ್ಟಾಗಿರಲಾರವು."
		},
		"6": {
			"1": "<sup>1</sup> ದಾಸ್ಯದಲ್ಲಿರುವವರು ತಮ್ಮ ಯಜಮಾನರನ್ನು ಎಲ್ಲಾ ವಿಧದಲ್ಲೂ ಗೌರವಿಸಬೇಕು. ಇಲ್ಲದಿದ್ದರೆ, ದೇವರ ಹೆಸರಿಗೂ ನಮ್ಮ ಬೋಧನೆಗೂ ಕಳಂಕವುಂಟಾಗುತ್ತದೆ.",
			"2": "<sup>2</sup> ಒಂದು ವೇಳೆ ಯಜಮಾನರು ಕ್ರೈಸ್ತವಿಶ್ವಾಸಿಗಳಾಗಿದ್ದರೆ ತಮ್ಮ ಯಜಮಾನರು ಸಹೋದರ ವರ್ಗದವರು ಎಂದು ಉದಾಸೀನ ಮಾಡಬಾರದು. ಬದಲಿಗೆ ಹೆಚ್ಚಿನ ಉತ್ಸಾಹದಿಂದ ಸೇವೆಸಲ್ಲಿಸಬೇಕು. ಏಕೆಂದರೆ, ಅವರ ಸೇವೆಯ ಪ್ರಯೋಜನವನ್ನು ಪಡೆಯುತ್ತಿರುವ ಯಜಮಾನರು, ಕ್ರೈಸ್ತವಿಶ್ವಾಸಿಗಳೂ ಪ್ರೀತಿಗೆ ಪಾತ್ರರಾದವರು. ಈ ವಿಷಯಗಳ ಬಗ್ಗೆ ಆಜ್ಞಾಪೂರ್ವಕವಾಗಿ ಬೋಧಿಸು.",
			"3": "<sup>3</sup> ಯಾರಾದರೂ ನಮ್ಮ ಧಾರ್ಮಿಕ ಉಪದೇಶವನ್ನು ಅನುಸರಿಸದೆ, ಪ್ರಭು ಯೇಸುಕ್ರಿಸ್ತರ ನೈಜ ಬೋಧನೆಯನ್ನು ಒಪ್ಪಿಕೊಳ್ಳದೆ ಭಿನ್ನ ಬೋಧನೆಯನ್ನು ಮಾಡುವುದಾದರೆ, ಅಂಥವನು ಅಹಂಭಾವಿ ಹಾಗೂ ಅಜ್ಞಾನಿಯೇ ಸರಿ.",
			"4": "<sup>4</sup> ಅಂಥವನಿಗೆ ಒಣ ವಾಗ್ವಾದಗಳಲ್ಲಿ ಮತ್ತು ಬರಡು ಮಾತುಗಳಲ್ಲಿ ಬಲು ಹಂಬಲ. ಇವುಗಳು ಅಸೂಯೆ, ಕಲಹ, ದೂಷಣೆ, ಅನುಮಾನ ಮತ್ತು ಕಚ್ಚಾಟಗಳಿಗೆ ಎಡೆಕೊಡುತ್ತವೆ.",
			"5": "<sup>5</sup> ಮತಿಗೆಟ್ಟ ಅಂಥವರಲ್ಲಿ ಸತ್ಯವೆಂಬುದು ಇರದು. ಧಾರ್ಮಿಕ ಸೇವೆಯು ಧನಗಳಿಕೆಯ ಸಾಧನವೆಂದೇ ಇವರ ನಂಬಿಕೆ.",
			"6": "<sup>6</sup> ಇರುವುದರಲ್ಲೇ ಸಂತೃಪ್ತನಾಗಿರುವವನು ಭಕ್ತಿಯಲ್ಲಿ ನಿಜಕ್ಕೂ ಶ್ರೀಮಂತನಾಗಿರುತ್ತಾನೆ.",
			"7": "<sup>7</sup> ಹುಟ್ಟಿದಾಗ ನಾವು ಈ ಲೋಕಕ್ಕೆ ಏನನ್ನೂ ತರಲಿಲ್ಲ; ಸಾಯುವಾಗ ಏನನ್ನೂ ಕೊಂಡು ಒಯ್ಯುವುದಿಲ್ಲ.",
			"8": "<sup>8</sup> ನಮಗೆ ಊಟ, ಬಟ್ಟೆ ಇದ್ದರೆ ಅಷ್ಟೇ ಸಾಕು.",
			"9": "<sup>9</sup> ಐಶ್ವರ್ಯವಂತರಾಗಬೇಕೆಂದು ಆಶಿಸುವವರು ಅನೇಕ ಪ್ರಲೋಭನೆಗಳಿಗೆ ಒಳಗಾಗುತ್ತಾರೆ. ನಿರರ್ಥಕವೂ ಹಾನಿಕರವೂ ಆದ ಆಶಾಪಾಶಗಳಲ್ಲಿ ಸಿಕ್ಕಿಬೀಳುತ್ತಾರೆ. ಇಂಥ ದುರಾಶೆಗಳು ಮನುಷ್ಯರನ್ನು ಕೇಡಿಗೂ ವಿನಾಶಕ್ಕೂ ಒಯ್ಯುತ್ತವೆ.",
			"10": "<sup>10</sup> ಹಣದ ವ್ಯಾಮೋಹವೇ ಎಲ್ಲಾ ಕೇಡುಗಳಿಗೂ ಮೂಲ. ಹಣದ ವ್ಯಾಮೋಹದಿಂದಲೇ ಹಲವರು ವಿಶ್ವಾಸದಿಂದ ದೂರ ಸರಿದು, ತಮ್ಮ ಹೃದಯಗಳನ್ನು ಹಲತರದ ತಿವಿತಗಳಿಗೆ ಗುರಿಮಾಡುತ್ತಾರೆ.",
			"11": "<sup>11</sup> ದೇವರ ಭಕ್ತನಾದ ನೀನು ಈ ಎಲ್ಲಾ ಕೇಡುಗಳಿಂದ ದೂರವಿರು. ದೇವರೊಡನೆ ಸತ್ಸಂಬಂಧ, ಭಕ್ತಿ, ವಿಶ್ವಾಸ, ಪ್ರೀತಿ, ಸ್ಥಿರಚಿತ್ತ, ಸಾತ್ವಿಕತೆ - ಇವೇ ಮೊದಲಾದ ಸದ್ಗುಣಗಳನ್ನು ಸಂಪಾದಿಸಲು ಪ್ರಯತ್ನಿಸು.",
			"12": "<sup>12</sup> ನಿತ್ಯಜೀವವೆಂಬ ಬಹುಮಾನವನ್ನು ಗಳಿಸಲು ವಿಶ್ವಾಸವೆಂಬ ಪಂದ್ಯದಲ್ಲಿ ಉತ್ತಮ ಓಟಗಾರನಾಗಿ ಓಡು. ಇದಕ್ಕಾಗಿಯೇ ದೇವರು ನಿನ್ನನ್ನು ಆರಿಸಿಕೊಂಡರೆಂಬುದನ್ನು ಮರೆಯಬೇಡ. ಈ ಗುರಿಯನ್ನು ಮುಂದಿಟ್ಟುಕೊಂಡೇ ನೀನು ಅನೇಕರ ಮುಂದೆ ವಿಶ್ವಾಸಪ್ರಮಾಣ ಮಾಡಿರುವೆ.",
			"13": "<sup>13</sup> ಸಕಲ ಸೃಷ್ಟಿಗೂ ಜೀವದಾತರಾದ ದೇವರ ಸನ್ನಿಧಿಯಲ್ಲಿ ಪೊಂತಿಯ ಪಿಲಾತನ ಮುಂದೆ ಸೂಕ್ತ ಸಾಕ್ಷಿ ನೀಡಿದ ಪ್ರಭು ಯೇಸುವಿನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನಾನು ಆಜ್ಞಾಪಿಸುವುದೇನೆಂದರೆ:",
			"14": "<sup>14</sup> ನಮ್ಮ ಪ್ರಭು ಯೇಸುಕ್ರಿಸ್ತರು ಪ್ರತ್ಯಕ್ಷರಾಗುವ ತನಕ ಯಾವ ದೋಷಾರೋಪಣೆಗೂ ಎಡೆಗೊಡದೆ ಈ ಕಟ್ಟಳೆಗಳೆಲ್ಲವನ್ನೂ ಪ್ರಾಮಾಣಿಕತೆಯಿಂದ ಅನುಸರಿಸು:",
			"15": "<sup>15</sup> ಸೂಕ್ತಕಾಲದಲ್ಲಿ ಕ್ರಿಸ್ತಯೇಸುವನು ಪ್ರತ್ಯಕ್ಷಪಡಿಸುವನು ಪರಮ ದೇವನು. ಆ ದೇವರು ಪರಮಾನಂದನು ಏಕಾಧಿಪತಿ, ರಾಜಾಧಿರಾಜನು ಒಡೆಯರಿಗೆಲ್ಲಾ ಒಡೆಯನು, ಅಮರನು ಅಗಮ್ಯ ಜ್ಯೋತಿಯಲಿ ವಾಸಿಸುವವನು. ಆತನನು ಕಂಡವರಾರೂ ಇಲ್ಲ ಕಾಣಬಲ್ಲವರಾರೂ ಇಲ್ಲ. ಆತನೊಬ್ಬನಿಗೆ ಮಹಿಮೆಯೂ ಗೌರವವೂ ಪ್ರಭುತ್ವವೂ ಸಲ್ಲಲಿ ಎಂದೆಂದಿಗೂ, ಆಮೆನ್.",
			"17": "<sup>17</sup> ಈ ಲೋಕದ ಐಶ್ವರ್ಯವಂತರು ಅಹಂಕಾರಿಗಳಾಗಬಾರದೆಂದು ಎಚ್ಚರಿಸು. ಅವರು ಅಳಿದುಹೋಗುವ ಆಸ್ತಿಯ ಮೇಲೆ ಭರವಸೆ ಇಡದೆ, ನಮ್ಮ ಸಂತೋಷಕ್ಕಾಗಿ ಸಮಸ್ತವನ್ನೂ ಧಾರಾಳವಾಗಿ ದಯಪಾಲಿಸುವ ದೇವರಲ್ಲೇ ಭರವಸೆಯಿಡುವಂತೆ ಅವರಿಗೆ ಆಜ್ಞಾಪಿಸು.",
			"18": "<sup>18</sup> ಅವರು ಸದ್ಗುಣಶೀಲರಾಗಬೇಕು, ಸತ್ಕಾರ್ಯಗಳನ್ನು ಮಾಡುವುದರಲ್ಲಿ ಶ್ರೀಮಂತರಾಗಿರಬೇಕು. ದಾನಶೀಲರೂ ಇತರರೊಂದಿಗೆ ಪಾಲು ಹಂಚಿಕೊಳ್ಳುವವರೂ ಪರೋಪಕಾರಿಗಳೂ ಆಗಿರಬೇಕು.",
			"19": "<sup>19</sup> ಹೀಗೆ, ಅವರು ತಮ್ಮ ಭವಿಷ್ಯಜೀವನಕ್ಕೆ ಭದ್ರವಾದ ಬುನಾದಿಯಾಗಬಲ್ಲ ನಿಧಿಯನ್ನು ಕೂಡಿಸಿಕೊಳ್ಳಬೇಕೆಂದು ವಿಧಿಸು. ಆಗ ಅವರು ಶಾಶ್ವತ ಜೀವವನ್ನು ಪಡೆಯುವರು.",
			"20": "<sup>20</sup> ಪ್ರಿಯ ತಿಮೊಥೇಯನೇ, ನಿನ್ನ ವಶಕ್ಕೆ ಕೊಡಲಾಗಿರುವ ಸದ್ಬೋಧನೆಯನ್ನು ಸುರಕ್ಷಿತವಾಗಿ ಕಾಪಾಡಿಕೋ. ಪ್ರಾಪಂಚಿಕ ವ್ಯರ್ಥ ಸಂಭಾಷಣೆಗಳಿಂದಲೂ ‘ಜ್ಞಾನಿ'ಗಳೆಂದು ತಪ್ಪಾಗಿ ಕೊಚ್ಚಿಕೊಳ್ಳುವವರ ವಾಗ್ವಾದಗಳಿಂದಲೂ ದೂರವಿರು.",
			"21": "<sup>21</sup> ಕೆಲವರು ಇಂಥ ಕಪಟಜ್ಞಾನವನ್ನು ಅವಲಂಬಿಸಿ ವಿಶ್ವಾಸವನ್ನೇ ಕಳೆದುಕೊಂಡಿದ್ದಾರೆ. ದೇವರ ಅನುಗ್ರಹ ನಿಮ್ಮಲ್ಲಿರಲಿ!"
		}
	}
};
module.exports = book;