var book = {
	"name": "ಯಾಕೋಬನು",
	"numChapters": 5,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ಜಗದಾದ್ಯಂತ ಚದರಿರುವ ಇಸ್ರಯೇಲಿನ ಹನ್ನೆರಡು ಕುಲದವರಿಗೆ - ದೇವರ ಹಾಗೂ ಪ್ರಭು ಯೇಸುಕ್ರಿಸ್ತರ ದಾಸನಾದ ಯಕೋಬನ ಶುಭಾಶಯಗಳು.",
			"2": "<sup>2</sup> ನನ್ನ ಪ್ರಿಯ ಸಹೋದರರೇ, ನಿಮಗೆ ವಿವಿಧ ಸಂಕಟ ಶೋಧನೆಗಳು ಬಂದೊದಗಿದಾಗ ಅವುಗಳನ್ನು ಸಂತೋಷದಿಂದ ಸಹಿಸಿಕೊಳ್ಳಿ.",
			"3": "<sup>3</sup> ಇವು ನಿಮ್ಮ ವಿಶ್ವಾಸವನ್ನು ಒರೆಗೆ ಹಚ್ಚಿ ನಿಮ್ಮನ್ನು ದೃಢಪಡಿಸುತ್ತವೆ; ನಿಮ್ಮನ್ನು ಸಹನಶೀಲರನ್ನಾಗಿ ಮಾಡುತ್ತವೆ.",
			"4": "<sup>4</sup> ಈ ಸಹನೆ ಸಿದ್ಧಿಗೆ ಬಂದಾಗ ನೀವು ಯಾವ ಕುಂದುಕೊರತೆ ಇಲ್ಲದೆ, ಪರಿಪೂರ್ಣರೂ ಸರ್ವಗುಣ ಸಂಪನ್ನರೂ ಆಗುತ್ತೀರಿ.",
			"5": "<sup>5</sup> ನಿಮ್ಮಲ್ಲಿ ಯಾರಿಗಾದರೂ ಜ್ಞಾನದ ಕೊರತೆಯಿದ್ದರೆ ಅಂಥವನು ದೇವರಲ್ಲಿ ಬೇಡಿಕೊಳ್ಳಲಿ. ಯಾರನ್ನೂ ತಿರಸ್ಕರಿಸದೆ ಎಲ್ಲರಿಗೂ ಯಥೇಚ್ಛವಾಗಿ ನೀಡುವ ದೇವರು, ಅವನಿಗೆ ಜ್ಞಾನವನ್ನು ದಯಪಾಲಿಸುತ್ತಾರೆ.",
			"6": "<sup>6</sup> ಆದರೆ ಬೇಡುವವನು ವಿಶ್ವಾಸದಿಂದ ಬೇಡಲಿ. ಸಂಶಯಕ್ಕೆ ಎಡೆಕೊಡದಿರಲಿ. ಸಂಶಯಕ್ಕೀಡಾಗುವವನ ಪರಿಸ್ಥಿತಿಯಾದರೋ ಬಿರುಗಾಳಿಯ ಬಡಿತಕ್ಕೆ ಸಿಕ್ಕಿದ ಸಮುದ್ರದ ಅಲೆಯಂತೆ ತುಯ್ದಾಡುತ್ತಿರುತ್ತದೆ.",
			"7": "<sup>7</sup> ಅಂಥವನು ಚಂಚಲಚಿತ್ತನು. ಆಚಾರವಿಚಾರಗಳಲ್ಲಿ ಅಸ್ಥಿರ ಮನಸ್ಸುಳ್ಳವನು. ಆದ್ದರಿಂದ ಅವನು ಪ್ರಭುವಿನಿಂದ ಏನನ್ನಾದರೂ ಪಡೆಯುತ್ತೇನೆಂದು ಭಾವಿಸದಿರಲಿ.",
			"9": "<sup>9</sup> ದೀನದಲಿತನಾದ ಸಹೋದರನು, ದೇವರು ತನ್ನನ್ನು ಉನ್ನತ ಸ್ಥಿತಿಗೆ ಏರಿಸಿದಾಗ ಆನಂದಿಸಲಿ.",
			"10": "<sup>10</sup> ಅಂತೆಯೇ ಧನಿಕನಾದ ಸಹೋದರನು, ದೇವರು ತನ್ನನ್ನು ದೀನಸ್ಥಿತಿಗೆ ಇಳಿಸಿದಾಗ ಆನಂದಿಸಲಿ.",
			"11": "<sup>11</sup> ಏಕೆಂದರೆ, ಧನಿಕರು ಹುಲ್ಲಿನ ಹೂವಿನಂತೆ ಗತಿಸಿಹೋಗುತ್ತಾರೆ. ಸೂರ್ಯನ ಸುಡುಬಿಸಿಲಿಗೆ ಹುಲ್ಲು ಒಣಗಿಹೋಗುತ್ತದೆ. ಅದರ ಹೂವು ಉದುರಿಹೋಗುವುದು, ಅದರ ಅಂದಚೆಂದವೂ ಹಾಳಾಗಿ ಹೋಗುತ್ತದೆ. ಅಂತೆಯೇ ಧನಿಕನು ತನ್ನ ವ್ಯವಹಾರಗಳಲ್ಲಿಯೇ ಕುಂದಿಹೋಗುತ್ತಾನೆ.",
			"12": "<sup>12</sup> ಸಂಕಟ ಶೋಧನೆಗಳನ್ನು ತಾಳ್ಮೆಯಿಂದ ಸಹಿಸಿಕೊಳ್ಳುವವನೇ ಧನ್ಯನು. ಅವನು ಪರಿಶೋಧನೆಯಲ್ಲಿ ಯಶಸ್ವಿಯಾದ ಮೇಲೆ ಸಜ್ಜೀವವೆಂಬ ಜಯಮಾಲೆಯನ್ನು ಪಡೆಯುತ್ತಾನೆ. ದೇವರು ತಮ್ಮನ್ನು ಪ್ರೀತಿಸುವವರಿಗೆ ಇದನ್ನು ಕಾದಿರಿಸಿರುತ್ತಾರೆ.",
			"13": "<sup>13</sup> ಪಾಪಪ್ರಲೋಭನೆಗೆ ಒಳಗಾದಾಗ, “ಈ ಪ್ರಲೋಭನೆಗಳು ದೇವರಿಂದಲೇ ಬಂದವು,” ಎಂದು ಯಾರೂ ಹೇಳದಿರಲಿ. ಏಕೆಂದರೆ, ದೇವರು ಸ್ವತಃ ಪಾಪಪ್ರಲೋಭನೆಗೆ ಗುರಿಯಾಗುವುದಿಲ್ಲ ಮತ್ತು ಯಾರನ್ನೂ ಅಂಥ ಪ್ರಲೋಭನೆಗೆ ಗುರಿಪಡಿಸುವುದಿಲ್ಲ.",
			"14": "<sup>14</sup> ಮಾನವನು ಪ್ರಲೋಭನೆಗೆ ಒಳಗಾಗುವುದು ತನ್ನ ದುರಿಚ್ಛೆಯಿಂದಲೇ ಅದು ಅವನನ್ನು ಆಕರ್ಷಿಸಿ ಮರುಳುಗೊಳಿಸುತ್ತದೆ.",
			"15": "<sup>15</sup> ದುರಿಚ್ಛೆ ಗರ್ಭಧರಿಸಿ ಪಾಪಕ್ಕೆ ಜನ್ಮವೀಯುತ್ತದೆ; ಪಾಪವು ಪೂರ್ತಿಯಾಗಿ ಬೆಳೆದು ಮರಣವನ್ನು ಹಡೆಯುತ್ತದೆ.",
			"16": "<sup>16</sup> ಪ್ರಿಯ ಸಹೋದರರೇ, ಮೋಸಹೋಗಬೇಡಿ.",
			"17": "<sup>17</sup> ಎಲ್ಲಾ ಯೋಗ್ಯ ವರಗಳೂ ಉತ್ತಮ ಕೊಡುಗೆಗಳೂ ಬರುವುದು ಮೇಲಿನಿಂದಲೇ. ಜ್ಯೋತಿರ್ಮಂಡಲವನ್ನು ಉಂಟುಮಾಡಿದ ದೇವರೇ ಅವುಗಳ ಮೂಲದಾತರು. ಅವರಲ್ಲಿ ಚಂಚಲತೆ ಇಲ್ಲ, ಮಬ್ಬು ಮುಸುಕಿನ ಛಾಯೆಯೂ ಇಲ್ಲ.",
			"18": "<sup>18</sup> ಸೃಷ್ಟಿಗಳಲ್ಲೆಲ್ಲಾ ನಾವು ಪ್ರಥಮ ಫಲವಾಗುವಂತೆ ದೇವರು ತಮ್ಮ ಸುಚಿತ್ತದ ಪ್ರಕಾರ ಸತ್ಯವಾಕ್ಯದ ಮೂಲಕ ನಮಗೆ ಜೀವವಿತ್ತರು.",
			"19": "<sup>19</sup> ಸಹೋದರರೇ, ಇದನ್ನು ನೆನಪಿನಲ್ಲಿಡಿ; ನಿಮ್ಮಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬನು ಆಲಿಸುವುದರಲ್ಲಿ ಚುರುಕಾಗಿ, ಮಾತನಾಡುವುದರಲ್ಲಿ ದುಡುಕದೆ, ಸಿಟ್ಟುಗೊಳ್ಳುವುದರಲ್ಲಿ ಸಾವಧಾನವಾಗಿ ಇರಲಿ.",
			"20": "<sup>20</sup> ಮಾನವನ ಕೋಪ ದೇವನೀತಿಗೆ ಧಕ್ಕೆ! ಆದುದರಿಂದ ನಿಮ್ಮಲ್ಲಿರುವ ಎಲ್ಲ ನೀಚತನವನ್ನು ಮತ್ತು ಬೆಳೆಯುತ್ತಿರುವ ದುಷ್ಟತನವನ್ನು ಕಿತ್ತೊಗೆಯಿರಿ. ನಿಮ್ಮ ಹೃದಯದಲ್ಲಿ ದೇವರು ನೆಟ್ಟಿರುವ ವಾಕ್ಯವನ್ನು ನಮ್ರತೆಯಿಂದ ಪರಿಗ್ರಹಿಸಿರಿ. ಇದು ನಿಮ್ಮ ಜೀವೋದ್ಧಾರಮಾಡಬಲ್ಲದು.",
			"22": "<sup>22</sup> ದೇವರ ವಾಕ್ಯವನ್ನು ಕಿವಿಯಿಂದ ಕೇಳಿದರೆ ಸಾಕೆಂದು ತಿಳಿದು ಮರುಳಾಗದಿರಿ. ಆ ವಾಕ್ಯವನ್ನು ಅನುಸರಿಸಿ ನಡೆಯುವವರಾಗಿರಿ.",
			"23": "<sup>23</sup> ದೇವರ ವಾಕ್ಯವನ್ನು ಕೇಳಿ ಅದರಂತೆ ನಡೆಯದವನು ಕನ್ನಡಿಯಲ್ಲಿ ತನ್ನ ಸಹಜ ಮುಖವನ್ನು ನೋಡಿ,",
			"24": "<sup>24</sup> ಮರುಕ್ಷಣವೇ ತಾನು ಹೇಗಿದ್ದೇನೆಂದು ಮರೆತುಬಿಡುವವನಿಗೆ ಸಮಾನನು.",
			"25": "<sup>25</sup> ಆದರೆ ನಮಗೆ ವಿಮೋಚನೆಯನ್ನು ತರುವ ಸರ್ವೋತ್ತಮ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನು ಲಕ್ಷ್ಯವಿಟ್ಟು ನೋಡಿ ಅದರಲ್ಲೇ ಧ್ಯಾನಾಸಕ್ತನಾಗಿರುವವನು ಅದನ್ನು ಮರೆಯುವುದಿಲ್ಲ. ಅವನು ಕೇಳುವವನು ಮಾತ್ರ ಆಗಿರದೆ, ವಾಕ್ಯದ ಪ್ರಕಾರ ನಡೆಯುವವನೂ ಆಗಿರುತ್ತಾನೆ. ಇಂಥವನು ಭಾಗ್ಯವಂತನು!",
			"26": "<sup>26</sup> ತಾನು ಸದ್ಭಕ್ತನೆಂದು ಭಾವಿಸಿಕೊಳ್ಳುವ ಯಾವನಾದರೂ ತನ್ನ ನಾಲಿಗೆಗೆ ಕಡಿವಾಣ ಹಾಕದಿದ್ದರೆ, ಅಂಥವನ ಭಕ್ತಿ ವ್ಯರ್ಥ. ಅವನು ತನ್ನನ್ನೇ ವಂಚಿಸಿಕೊಳ್ಳುತ್ತಾನಷ್ಟೆ.",
			"27": "<sup>27</sup> ಕಷ್ಟಸಂಕಟಗಳಲ್ಲಿರುವ ಅನಾಥರಿಗೂ ವಿಧವೆಯರಿಗೂ ನೆರವಾಗುವುದು ಹಾಗೂ ಪ್ರಾಪಂಚಿಕ ಮಲಿನತೆಯಿಂದ ದೂರವಿರುವುದು - ನಮ್ಮ ತಂದೆಯಾದ ದೇವರ ಮುಂದೆ ನಿಷ್ಕಳಂಕವಾದ ಮತ್ತು ನಿರ್ಮಲವಾದ ಧರ್ಮವೆನಿಸುತ್ತದೆ."
		},
		"2": {
			"1": "<sup>1</sup> ನನ್ನ ಸಹೋದರರೇ, ಮಹಿಮಾನ್ವಿತ ಪ್ರಭು ಯೇಸುಕ್ರಿಸ್ತರಲ್ಲಿ ವಿಶ್ವಾಸವಿಟ್ಟಿರುವ ನೀವು ಪಕ್ಷಪಾತ ಮಾಡಲೇಬಾರದು.",
			"2": "<sup>2</sup> ಉದಾಹರಣೆಗೆ: ಇಬ್ಬರು ನಿಮ್ಮ ಪ್ರಾರ್ಥನಾಲಯಕ್ಕೆ ಬಂದರೆಂದು ಇಟ್ಟುಕೊಳ್ಳಿ. ಅವರಲ್ಲಿ ಒಬ್ಬನು ಚಿನ್ನದುಂಗುರವನ್ನು ತೊಟ್ಟು, ಸೊಗಸಾದ ಉಡುಪನ್ನು ಧರಿಸಿದವನು. ಮತ್ತೊಬ್ಬನು ಹರಕುಬಟ್ಟೆಯನ್ನು ಉಟ್ಟ ಬಡವನು.",
			"3": "<sup>3</sup> ಆಗ, ನೀವು ಸೊಗಸಾದ ಉಡುಪನ್ನು ಧರಿಸಿದವನಿಗೆ ಗೌರವದಿಂದ, “ಬನ್ನಿ ಸ್ವಾಮೀ ಬನ್ನಿ, ದಯವಿಟ್ಟು ಈ ಪೀಠವನ್ನು ಅಲಂಕರಿಸಿ,” ಎನ್ನುತ್ತೀರಿ. ಬಡವನಿಗೆ, “ನೀನು ಅಲ್ಲಿಯೇ ನಿಂತುಕೋ, ಇಲ್ಲವೇ ಕೆಳಗೆ, ನನ್ನ ಕಾಲ ಬಳಿ ಕುಳಿತುಕೋ,” ಎನ್ನಿತ್ತೀರಿ. ಹೀಗಿರಲು,",
			"4": "<sup>4</sup> ನೀವು ನಿಮ್ಮಲ್ಲೇ ಭೇದಭಾವ ಮಾಡುತ್ತೀರಿ; ಅಲ್ಲದೆ ನೀವು ಮಾಡುವ ಈ ತೀರ್ಪು ದುರುದ್ದೇಶದಿಂದ ಕೂಡಿರುತ್ತದೆ.",
			"5": "<sup>5</sup> ನನ್ನ ಪ್ರಿಯ ಸಹೋದರರೇ, ಪ್ರಪಂಚದ ದೃಷ್ಟಿಗೆ ಬಡವರಾಗಿ ಕಾಣುವವರನ್ನು ವಿಶ್ವಾಸದಲ್ಲಿ ಸಿರಿವಂತರನ್ನಾಗಿಸಲು ದೇವರು ಆರಿಸಿಕೊಳ್ಳಲಿಲ್ಲವೇ? ತಮ್ಮನ್ನು‍ ಪ್ರೀತಿಸುವವರು ಸ್ವರ್ಗಸಾಮ್ರಾಜ್ಯಕ್ಕೆ ಬಾಧ್ಯಸ್ಥರೆಂದು ದೇವರೇ ವಾಗ್ದಾನ ಮಾಡಿಲ್ಲವೇ?",
			"6": "<sup>6</sup> ಹೀಗಿದ್ದರೂ ನೀವು ಬಡವನನ್ನು ಅವಮಾನಕ್ಕೆ ಈಡುಮಾಡಿದಿರಿ. ನಿಮ್ಮನ್ನು ತುಳಿದು ಹಿಂಸಿಸುವವರು ಮತ್ತು ನಿಮ್ಮನ್ನು ನ್ಯಾಯಾಲಯಕ್ಕೆ ಎಳೆಯುವವರು ಸಿರಿವಂತರೇ ಅಲ್ಲವೇ?",
			"7": "<sup>7</sup> ನೀವು ಯಾರಿಗೆ ಶರಣರಾಗಿದ್ದೀರೋ ಅವರ ಶ್ರೇಷ್ಠ ನಾಮವನ್ನು ದೂಷಿಸುವವರೂ ಸಿರಿವಂತರೇ ಅಲ್ಲವೇ?",
			"8": "<sup>8</sup> ‘ನಿನ್ನನ್ನು ನೀನು ಪ್ರೀತಿಸಿಕೊಳ್ಳುವಂತೆ ನಿನ್ನ ನೆರೆಯವನನ್ನು ಪ್ರೀತಿಸು’ ಎಂದು ಪವಿತ್ರಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿದೆ. ಈ ರಾಜಾಜ್ಞೆಯನ್ನು ಕೈಗೊಂಡು ನಡೆಯುತ್ತಿದ್ದರೆ ಅದು ಒಳ್ಳೆಯದು.",
			"9": "<sup>9</sup> ಅದನ್ನು ಬಿಟ್ಟು ಪಕ್ಷಪಾತ ಮಾಡಿದರೆ ನೀವು ಪಾಪಮಾಡುತ್ತೀರಿ ಮತ್ತು ಧರ್ಮಶಾಸ್ತ್ರ ನಿಮ್ಮನ್ನು ಅಪರಾಧಿಗಳೆಂದು ನಿರ್ಣಯಿಸುತ್ತದೆ.",
			"10": "<sup>10</sup> ಒಬ್ಬನು ಇಡೀ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನೇ ಕೈಗೊಂಡು ಒಂದೇ ಒಂದು ನಿಯಮವನ್ನು ಮೀರಿ ನಡೆದರೂ ಅವನು ಇಡೀ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನೇ ಮೀರಿ ನಡೆದಂತಾಗುತ್ತದೆ.",
			"11": "<sup>11</sup> ಏಕೆಂದರೆ, ‘ವ್ಯಭಿಚಾರ ಮಾಡಬೇಡ’ ಎಂದು ಹೇಳಿದ ದೇವರೇ, ‘ಕೊಲ್ಲಬೇಡ’ ಎಂದೂ ಹೇಳಿದ್ದಾರೆ. ನೀನು ವ್ಯಭಿಚಾರ ಮಾಡದಿದ್ದರೂ ಕೊಲೆ ಮಾಡುವವನಾದರೆ ಧರ್ಮಶಾಸ್ತ್ರವನ್ನೇ ಮೀರಿದಂತಾಗುತ್ತದೆ.",
			"12": "<sup>12</sup> ವಿಮೋಚನೆಯನ್ನೀಯುವ ಧರ್ಮಶಾಸ್ತ್ರದ ನ್ಯಾಯತೀರ್ಪಿಗೆ ಗುರಿಯಾಗುವಿರಿ ಎಂದು ತಿಳಿದು ನೀವು ನಡೆಯಲ್ಲೂ ನುಡಿಯಲ್ಲೂ ಯೋಗ್ಯರಾಗಿ ಬಾಳಿ.",
			"13": "<sup>13</sup> ದಯೆತೋರದವನಿಗೆ, ದಯೆದಾಕ್ಷಿಣ್ಯವಿಲ್ಲದ ನ್ಯಾಯತೀರ್ಪು ಕಾದಿರುತ್ತದೆ. ನ್ಯಾಯಕ್ಕೂ ಮಿಗಿಲಾಗಿ ವಿಜೃಂಭಿಸುವುದು ದಯೆಯೇ.",
			"14": "<sup>14</sup> ಸಹೋದರರೇ, ಒಬ್ಬನು ತನಗೆ ವಿಶ್ವಾಸ ಇದೆಯೆಂದು ಕೊಚ್ಚಿಕೊಂಡು ಅದನ್ನು ಕಾರ್ಯತಃ ವ್ಯಕ್ತಪಡಿಸದಿದ್ದರೆ ಅದರಿಂದ ಪ್ರಯೋಜನ ಏನು? ಅಂಥ ವಿಶ್ವಾಸ ಅವನನ್ನು ಉದ್ಧಾರಮಾಡಬಲ್ಲುದೇ?",
			"15": "<sup>15</sup> ಒಂದು ವೇಳೆ ಒಬ್ಬ ಸಹೋದರನಾಗಲಿ, ಸಹೋದರಿಯಾಗಲಿ ದಿನನಿತ್ಯ ಊಟಬಟ್ಟೆಗಿಲ್ಲದೆ ಇರುವಾಗ, ನಿಮ್ಮಲ್ಲಿ ಯಾರಾದರು ಅವರಿಗೆ ಅಗತ್ಯವಾದುದನ್ನು ನೀಡದೆ, “ಹೋಗು, ಸುಖವಾಗಿ ಬಾಳು, ಚಳಿ ಕಾಯಿಸಿಕೊ, ಹೊಟ್ಟೆ ತುಂಬಾ ತಿನ್ನು,” ಎಂದು ಬರೀ ಬಾಯಿಮಾತಿನಲ್ಲಿ ಉಪಚರಿಸಿದರೆ ಅದರಿಂದಾದ ಪ್ರಯೋಜನವೇನು?",
			"17": "<sup>17</sup> ಸತ್ಕ್ರಿಯೆಗಳಿಲ್ಲದ ವಿಶ್ವಾಸ ಸತ್ತವಿಶ್ವಾಸವೇ ಸರಿ.",
			"18": "<sup>18</sup> ಒಬ್ಬನು, “ನಿನ್ನಲ್ಲಿ ವಿಶ್ವಾಸವಿದೆ, ನನ್ನಲ್ಲಿ ಸತ್ಕ್ರಿಯೆಗಳಿವೆ,” ಎನ್ನಬಹುದು. ಅಂಥವನಿಗೆ, “ಸತ್ಕ್ರಿಯೆಗಳಿಲ್ಲದ ನಿನ್ನ ವಿಶ್ವಾಸವನ್ನು ನನಗೆ ತೋರಿಸು; ನನ್ನ ಸತ್ಕಾರ್ಯಗಳ ಮೂಲಕ ನನ್ನ ವಿಶ್ವಾಸವನ್ನು ನಿನಗೆ ವ್ಯಕ್ತಪಡಿಸುತ್ತೇನೆ,” ಎಂದು ಉತ್ತರಿಸಬಹುದು.",
			"19": "<sup>19</sup> ದೇವರು ಒಬ್ಬರೇ ಎಂದು ನೀವು ನಂಬುತ್ತೀರಿ. ಈ ನಂಬಿಕೆ ಒಳ್ಳೆಯದೇ. ಆದರೆ ದೆವ್ವಗಳೂ ನಂಬುತ್ತವೆ; ಹೆದರಿ ನಡುಗುತ್ತವೆ.",
			"20": "<sup>20</sup> ಮತಿಹೀನನೇ, ಸತ್ಕ್ರಿಯೆಗಳಿಲ್ಲದ ವಿಶ್ವಾಸ ಗೊಡ್ಡು ವಿಶ್ವಾಸ ಎಂಬುದಕ್ಕೆ ದೃಷ್ಟಾಂತ ಬೇಕೇ?",
			"21": "<sup>21</sup> ನಮ್ಮ ಪಿತಾಮಹ ಅಬ್ರಹಾಮನು ದೇವರೊಂದಿಗೆ ಸತ್ಸಂಬಂಧವನ್ನು ಪಡೆದುದ್ದಾದರೂ ಹೇಗೆ? ತನ್ನ ಮಗ ಇಸಾಕನನ್ನು ದೇವರಿಗೆ ಬಲಿಯಾಗಿ ಸಮರ್ಪಿಸಿದ ಸತ್ಕ್ರಿಯೆಯಿಂದಲ್ಲವೇ? ವಿಶ್ವಾಸವು ಸತ್ಕ್ರಿಯೆಗಳಿಂದ ಸಚೇತನಗೊಂಡಿತು.",
			"22": "<sup>22</sup> ಆತನ ಸತ್ಕ್ರಿಯೆಗಳಿಂದಲೇ ವಿಶ್ವಾಸವು ಸಿದ್ಧಿಗೆ ಬಂದಿತು ಎಂಬುದನ್ನು ನಾವು ನೋಡುತ್ತೇವೆ.",
			"23": "<sup>23</sup> ಆದುದರಿಂದಲೇ, “ಅಬ್ರಹಾಮನು ದೇವರಲ್ಲಿ ವಿಶ್ವಾಸ ಇಟ್ಟನು. ದೇವರು ಆತನನ್ನು ತಮ್ಮ ಸತ್ಸಂಬಂಧದಲ್ಲಿ ಇರುವುದಾಗಿ ಪರಿಗಣಿಸಿದರು,” ಎಂದು ಪವಿತ್ರಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುವ ಮಾತು ನೆರವೇರಿತು. ಅಬ್ರಹಾಮನು ದೇವರ ಮಿತ್ರನೆನಿಸಿಕೊಂಡನು.",
			"24": "<sup>24</sup> ಮಾನವನು ದೇವರೊಡನೆ ಸತ್ಸಂಬಂಧವನ್ನು ಪಡೆಯುವುದು ವಿಶ್ವಾಸದಿಂದ ಮಾತ್ರವಲ್ಲ, ವಿಶ್ವಾಸವು ಸತ್ಕ್ರಿಯೆಗಳೊಡನೆ ಬೆರೆತಾಗ ಮಾತ್ರ ಎಂಬುದು ನಿಮಗೆ ತಿಳಿದಿರಲಿ.",
			"25": "<sup>25</sup> ಇದೇ ರೀತಿಯಾಗಿ ರಹಾಬಳೆಂಬ ಜಾರಿಣಿ ಗೂಢಚಾರರನ್ನು ತನ್ನ ಮನೆಗೆ ಸೇರಿಸಿಕೊಂಡು ಬೇರೆ ದಾರಿಯಿಂದ ಅವರನ್ನು ಸಾಗಕಳುಹಿಸಿದಳು. ಈ ಸತ್ಕ್ರಿಯೆಯಿಂದಲೇ ದೇವರೊಡನೆ ನೀತಿಯುತ ಸಂಬಂಧವನ್ನು ಪಡೆದಳು.",
			"26": "<sup>26</sup> ಆತ್ಮವಿಲ್ಲದ ದೇಹವು ಸತ್ತದ್ದು; ಸತ್ಕ್ರಿಯೆಗಳಿಲ್ಲದ ವಿಶ್ವಾಸವೂ ಸತ್ತದ್ದೇ ಸರಿ."
		},
		"3": {
			"1": "<sup>1</sup> ಸಹೋದರರೇ, ಎಲ್ಲರೂ ಬೋಧಕರಾಗಲು ಆತುರಪಡಬೇಡಿ. ಬೋಧಕರಾದ ನಾವು ಇತರರಿಗಿಂತಲೂ ತೀವ್ರವಾದ ನ್ಯಾಯವಿಚಾರಣೆಗೆ ಗುರಿಯಾಗುತ್ತೇವೆ.",
			"2": "<sup>2</sup> ನಾವೆಲ್ಲರೂ ಎಷ್ಟೋ ವಿಷಯಗಳಲ್ಲಿ ತಪ್ಪುಮಾಡುವುದುಂಟು. ತನ್ನ ಮಾತಿನಲ್ಲಿ ತಪ್ಪುಮಾಡದೆ ಪೂರ್ತಿಯಾಗಿ ತನ್ನನ್ನೇ ಸ್ವಾಧೀನದಲ್ಲಿ ಇಟ್ಟುಕೊಳ್ಳುವ ವ್ಯಕ್ತಿ ಸಿದ್ಧಪುರುಷನೇ ಹೌದು.",
			"3": "<sup>3</sup> ಕುದುರೆಗಳನ್ನು ಗಮನಿಸಿರಿ, ಅವುಗಳ ಬಾಯಿಗೆ ಹಾಕುವ ಚಿಕ್ಕ ಕಡಿವಾಣದಿಂದ ನಮಗೆ ಇಷ್ಟಬಂದ ಕಡೆಗೆ ಅವುಗಳನ್ನು ನಡೆಸುತ್ತೇವೆ.",
			"4": "<sup>4</sup> ಅಂತೆಯೇ, ಹಡಗು ಎಷ್ಟೇ ದೊಡ್ಡದಿರಲಿ, ಎಂತಹ ಬಿರುಗಾಳಿಯ ಬಡಿತಕ್ಕೇ ಸಿಕ್ಕಿರಲಿ, ಕೇವಲ ಒಂದು ಚಿಕ್ಕ ಚುಕ್ಕಾಣಿಯಿಂದ ನಾವಿಕನು ತನಗೆ ಇಷ್ಟಬಂದ ಕಡೆಗೆ ಅದನ್ನು ತಿರುಗಿಸುತ್ತಾನೆ.",
			"5": "<sup>5</sup> ನಾಲಿಗೆಯೂ ಹಾಗೆಯೇ. ಅದು ಶರೀರದ ಸಣ್ಣ ಅಂಗವಾಗಿದ್ದರೂ ಬಡಾಯಿ ಕೊಚ್ಚುವುದು ಬಹಳ. ಒಂದು ಸಣ್ಣ ಕಿಡಿಯು ದೊಡ್ಡ ಕಾಡನ್ನೇ ಸುಡಬಲ್ಲದು.",
			"6": "<sup>6</sup> ನಾಲಿಗೆಯು ಆ ಬೆಂಕಿಯ ಕಿಡಿಯಂತೆ. ಅದು ಅಧರ್ಮಲೋಕದ ಪ್ರತೀಕ. ಅಂಗಾಂಗಗಳ ನಡುವೆ ಇದ್ದು ಇಡೀ ಶರೀರವನ್ನು ಮಲಿನಗೊಳಿಸುತ್ತದೆ. ನರಕಾಗ್ನಿಯಿಂದ ಹೊತ್ತಿಕೊಂಡು ಅದು ಬಾಳಿನ ಚಕ್ರಕ್ಕೆ ಬೆಂಕಿಯನ್ನು ಹಚ್ಚುತ್ತದೆ.",
			"7": "<sup>7</sup> ಸಕಲ ಜಾತಿಯ ಪ್ರಾಣಿಪಕ್ಷಿಗಳನ್ನೂ ಹರಿದಾಡುವ ಜಂತುಗಳನ್ನೂ ಜಲಜೀವಿಗಳನ್ನೂ ಮಾನವ ಪಳಗಿಸಬಲ್ಲನು; ಪಳಗಿಸಿಯೂ ಇದ್ದಾನೆ.",
			"8": "<sup>8</sup> ಆದರೆ ನಾಲಿಗೆಯನ್ನಾದರೋ ಹತೋಟಿಗೆ ತರಲು ಯಾರಿಗೂ ಸಾಧ್ಯವಿಲ್ಲ. ಅದು ಸತತ ಕೇಡನ್ನುಂಟುಮಾಡುವ ಕಿಡಿ; ಮೃತ್ಯುಮಾರಕ ವಿಷಕೊಂಡಿ.",
			"9": "<sup>9</sup> ಪ್ರಭುವನ್ನೂ ಪರಮ ಪಿತನನ್ನೂ ಸ್ತುತಿಸುವುದು ಅದೇ ನಾಲಿಗೆಯೇ; ದೇವಸ್ವರೂಪದಲ್ಲಿ ಸೃಷ್ಟಿಸಲಾದ ಮಾನವನನ್ನು ಶಪಿಸುವುದೂ ಅದೇ ನಾಲಿಗೆಯೇ.",
			"10": "<sup>10</sup> ಸ್ತುತಿ ಶಾಪಗಳೆರಡೂ ಒಂದೇ ಬಾಯಿಂದ ಹೊರಡುತ್ತವೆ. ಪ್ರಿಯ ಸಹೋದರರೇ, ಇದು ತರವಲ್ಲ.",
			"11": "<sup>11</sup> ಒಂದೇ ಬುಗ್ಗೆಯಿಂದ ಸಿಹಿ ನೀರು, ಸೌಳು ನೀರು - ಎರಡೂ ಬರುವುದುಂಟೇ?",
			"12": "<sup>12</sup> ಅಂಜೂರದ ಮರ ಓಲಿವ್ ಹಣ್ಣನ್ನು ಕೊಡುವುದುಂಟೇ? ದ್ರಾಕ್ಷಿಯ ಬಳ್ಳಿಯಲ್ಲಿ ಅಂಜೂರ ಸಿಕ್ಕೀತೇ? ಎಂದಿಗೂ ಇಲ್ಲ. ಅಂತೆಯೇ ಸೌಳು ನೀರಿನ ಒರತೆಯಿಂದ ಸಿಹಿ ನೀರು ಬರುವುದಿಲ್ಲ.",
			"13": "<sup>13</sup> ನಿಮ್ಮಲ್ಲಿ ಯಾರಾದರೂ ಜ್ಞಾನಿಯೂ ವಿವೇಕಿಯೂ ಆದವನು ಇದ್ದಾನೋ? ಅಂಥವನು ಜ್ಞಾನದ ಲಕ್ಷಣವಾಗಿರುವ ವಿನಯಶೀಲತೆಯನ್ನು ತನ್ನ ನಡೆನುಡಿಯಲ್ಲಿ ತೋರ್ಪಡಿಸಲಿ.",
			"14": "<sup>14</sup> ನಿಮ್ಮ ಹೃದಯದಲ್ಲಿ ಮರ್ಮಮತ್ಸರವೂ ಸ್ವಾರ್ಥಾಭಿಲಾಶೆಯೂ ತುಂಬಿರುವಾಗ ಜ್ಞಾನಿಗಳೆಂದು ಕೊಚ್ಚಿಕೊಳ್ಳಬೇಡಿ; ಸತ್ಯಕ್ಕೆ ವಿರುದ್ಧವಾಗಿ ಸುಳ್ಳಾಡಬೇಡಿ.",
			"15": "<sup>15</sup> ಅಂಥ ಜ್ಞಾನ ದೇವರಿಂದ ಬಂದ ಜ್ಞಾನವಲ್ಲ; ಅದು ಪ್ರಾಪಂಚಿಕವಾದುದು, ಪ್ರಾಕೃತವಾದುದು, ಪೈಶಾಚಿಕವಾದುದು.",
			"16": "<sup>16</sup> ಮರ್ಮಮತ್ಸರವೂ ಸ್ವಾರ್ಥಾಭಿಲಾಶೆಯೂ ಇರುವ ಕಡೆಗಳಲ್ಲೆಲ್ಲಾ ನಾನಾವಿಧವಾದ ಅಕ್ರಮಗಳೂ ದುಶ್ಚಟಗಳು ಇರುತ್ತವೆ.",
			"17": "<sup>17</sup> ದೇವರಿಂದ ಬರುವ ಜ್ಞಾನವಾದರೋ ಮೊಟ್ಟಮೊದಲನೆಯದಾಗಿ ಪವಿತ್ರವಾದುದು. ಅದು ಶಾಂತಿಸಮಾಧಾನ ಉಳ್ಳದ್ದು. ಸಹನೆ ಸಂಯಮವುಳ್ಳದ್ದು, ನ್ಯಾಯಸಮ್ಮತವಾದದ್ದು, ದಯೆದಾಕ್ಷಿಣ್ಯಗಳಿಂದಲೂ ಸತ್ಕಾರ್ಯಗಳಿಂದಲೂ ಫಲಭರಿತವಾದದ್ದು. ವಂಚನೆಯಾಗಲಿ, ಚಂಚಲತೆಯಾಗಲಿ ಅದರಲ್ಲಿ ಇರುವುದಿಲ್ಲ.",
			"18": "<sup>18</sup> ಸಮಾಧಾನಪಡಿಸುವವರು ಸಮಾಧಾನವೆಂಬ ಬೀಜವನ್ನು ಬಿತ್ತಿ ದೇವರೊಡನೆ ಸತ್ಸಂಬಂಧವೆಂಬ ಫಲವನ್ನು ಕೊಯ್ಯುತ್ತಾರೆ."
		},
		"4": {
			"1": "<sup>1</sup> ನಿಮ್ಮಲ್ಲಿ ಕಲಹ ಕಾದಾಟಗಳು ಉಂಟಾಗುವುದು ಹೇಗೆ? ನಿಮ್ಮ ಇಂದ್ರಿಯಗಳಲ್ಲಿ ಗೊಂದಲವೆಬ್ಬಿಸುವ ದುರಿಚ್ಛೆಗಳಿಂದಲ್ಲವೇ?",
			"2": "<sup>2</sup> ಪರರ ಆಸ್ತಿಪಾಸ್ತಿಗಳನ್ನು ಬಯಸುತ್ತೀರಿ; ಅವುಗಳು ಸಿಗದೆ ಇದ್ದಾಗ, ನೀವು ಕೊಲೆಮಾಡಲೂ ಹಿಂಜರಿಯುವುದಿಲ್ಲ. ದುರಾಶೆಗಳಿಗೆ ಬಲಿಯಾಗುತ್ತೀರಿ; ಅವುಗಳು ಈಡೇರದಾಗ ಜಗಳವಾಡುತ್ತೀರಿ, ಕಾದಾಡುತ್ತೀರಿ. ನಿಮಗೆ ಬೇಕಾದುದು ನಿಮ್ಮಲ್ಲಿಲ್ಲ. ಏಕೆಂದರೆ, ನೀವು ಅದಕ್ಕಾಗಿ ಬೇಡಿಕೊಳ್ಳಲಿಲ್ಲ.",
			"3": "<sup>3</sup> ಬೇಡಿಕೊಂಡರೂ ನಿಮಗದು ದೊರಕುವುದಿಲ್ಲ. ಏಕೆಂದರೆ, ನಿಮ್ಮ ಬೇಡಿಕೆ ದುರುದ್ದೇಶದಿಂದ ಕೂಡಿರುತ್ತದೆ. ಭೋಗಾಭಿಲಾಶೆಗಳ ಈಡೇರಿಕೆ ನಿಮ್ಮ ಗುರಿಯಾಗಿರುತ್ತದೆ.",
			"4": "<sup>4</sup> ವಿಶ್ವಾಸದಲ್ಲಿ ವ್ಯಭಿಚಾರಿಗಳಂತೆ ಬಾಳುವವರೇ, ಲೋಕದೊಡನೆ ಗೆಳೆತನವೆಂದರೆ ದೇವರೊಡನೆ ಹಗೆತನವೆಂಬುದು ನಿಮಗೆ ತಿಳಿಯದೇ? ಲೋಕದೊಡನೆ ಗೆಳೆತನವನ್ನು ಬಯಸುವವನು ದೇವರೊಡನೆ ಹಗೆತನವನ್ನು ಬೆಳೆಸುತ್ತಾನೆ.",
			"5": "<sup>5</sup> “ದೇವರು ನಮ್ಮಲ್ಲಿ ಇರುವ ಆತ್ಮವನ್ನು ಅತ್ಯಾಸಕ್ತಿಯಿಂದ ಅಪೇಕ್ಷಿಸುತ್ತಾರೆ,” ಎಂಬ ಪವಿತ್ರಗ್ರಂಥದ ವಾಕ್ಯವು ಹುರುಳಿಲ್ಲದ್ದೆಂದು ಭಾವಿಸುವಿರಾ?",
			"6": "<sup>6</sup> ದೇವರು ನಮಗೆ ದಯಪಾಲಿಸುವ ವರಪ್ರಸಾದ ಅತ್ಯಧಿಕವಾದುದು. ಎಂತಲೇ, “ದೇವರು ಗರ್ವಿಷ್ಠರನ್ನು ವಿರೋಧಿಸುತ್ತಾರೆ. ದೀನದಲಿತರಿಗೆ ಕೃಪಾವರವನ್ನು ಅನುಗ್ರಹಿಸುತ್ತಾರೆ,” ಎಂದು ಲಿಖಿತವಾಗಿದೆ.",
			"7": "<sup>7</sup> ಆದ್ದರಿಂದ ದೇವರಿಗೆ ಶರಣರಾಗಿ ಬಾಳಿ, ಸೈತಾನನನ್ನು ಎದುರಿಸಿ ನಿಲ್ಲಿ. ಅವನು ನಿಮ್ಮಿಂದ ಪಲಾಯನಗೈಯುವನು.",
			"8": "<sup>8</sup> ದೇವರ ಸಮೀಪಕ್ಕೆ ಬನ್ನಿ, ಆಗ ಅವರು ನಿಮ್ಮ ಸಮೀಪಕ್ಕೆ ಬರುವರು. ಪಾಪಾತ್ಮರೇ, ನಿಮ್ಮ ಕೈ ಶುದ್ಧವಾಗಿರಲಿ. ಎರಡು ಮನಸ್ಸಿನವರೇ, ನಿಮ್ಮ ಹೃದಯ ನಿರ್ಮಲವಾಗಿರಲಿ.",
			"9": "<sup>9</sup> ನಿಮ್ಮ ಹೀನಸ್ಥಿತಿಗಾಗಿ ವ್ಯಥೆಪಡಿರಿ. ಕಣ್ಣೀರಿಟ್ಟು ಗೋಳಾಡಿರಿ. ನಗುವುದನ್ನು ಬಿಟ್ಟು ದುಃಖಿಸಿರಿ; ಸಂತೋಷವನ್ನು ಬಿಟ್ಟು ಶೋಕಿಸಿರಿ.",
			"10": "<sup>10</sup> ಪ್ರಭುವಿನ ಮುಂದೆ ನಮ್ರರಾಗಿರಿ. ಆಗ ಅವರು ನಿಮ್ಮನ್ನು ಉದ್ಧರಿಸುವರು.",
			"11": "<sup>11</sup> ಸಹೋದರರೇ, ಒಬ್ಬರನ್ನೊಬ್ಬರು ನಿಂದಿಸಬೇಡಿ. ಯಾರಾದರೂ ತನ್ನ ಸಹೋದರನನ್ನು ನಿಂದಿಸಿದರೆ ಅಥವಾ ಖಂಡಿಸಿದರೆ, ಅಂಥವನು ಧರ್ಮಶಾಸ್ತ್ರವನ್ನೇ ನಿಂದಿಸಿ ಖಂಡಿಸುತ್ತಾನೆ. ನೀನು ಧರ್ಮಶಾಸ್ತ್ರವನ್ನುಖಂಡಿಸಿದರೆ, ನ್ಯಾಯಾಧಿಪತಿ ಎನಿಸಿಕೊಳ್ಳುವೆಯೇ ಹೊರತು ನೀನು ಅದನ್ನು ಅನುಸರಿಸಿ ನಡೆಯುವವನಾಗುವುದಿಲ್ಲ.",
			"12": "<sup>12</sup> ನ್ಯಾಯವಿಧಿಗಳನ್ನು ಕೊಟ್ಟವರೂ ನ್ಯಾಯಾಧಿಪತಿಯೂ ಒಬ್ಬರೇ; ಅವರೇ ಉದ್ಧಾರಮಾಡುವುದಕ್ಕೂ ವಿನಾಶಗೊಳಿಸುವುದಕ್ಕೂ ಶಕ್ತರು. ಹೀಗಿರುವಲ್ಲಿ, ನಿನ್ನ ನೆರೆಯವನಿಗೆ ತೀರ್ಪುಕೊಡಲು ನೀನು ಯಾರು?",
			"13": "<sup>13</sup> “ಇಂಥಿಂಥ ದಿನ ಇಂಥಿಂಥ ಪಟ್ಟಣಕ್ಕೆ ಹೋಗೋಣ, ಅಲ್ಲಿ ಒಂದು ವರ್ಷ ಇದ್ದು ವ್ಯಾಪಾರ ಮಾಡೋಣ; ಅತಿಯಾಗಿ ಲಾಭ ಸಂಪಾದಿಸೋಣ,” ಎಂದೆಲ್ಲಾ ಆಲೋಚಿಸುವವರೇ, ಈಗ ಕೇಳಿ: ನೀವು ಇಷ್ಟೆಲ್ಲಾ ಆಲೋಚಿಸಿದರೂ ನಾಳೆ ಏನಾಗುವುದೋ ನಿಮಗೇ ತಿಳಿಯದು.",
			"14": "<sup>14</sup> ನಿಮ್ಮ ಜೀವಮಾನ ಎಷ್ಟುಮಾತ್ರದ್ದು? ಈಗ ಕಾಣಿಸಿಕೊಂಡು ಆಮೇಲೆ ಕಾಣದೆಹೋಗುವ ಹೊಗೆಯಂತೆ ಅದು.",
			"15": "<sup>15</sup> ಆದ್ದರಿಂದ ನೀವು ಅಂಥ ಮಾತನ್ನು ಬಿಟ್ಟು, “ಪ್ರಭುವಿನ ಚಿತ್ತವಿದ್ದರೆ ನಾವು ಬದುಕಿರುತ್ತೇವೆ, ಇಂಥಿಂಥದ್ದನ್ನು ಮಾಡುತ್ತೇವೆ,” ಎಂದು ನೀವು ಹೇಳುವುದೇ ಸರಿ.",
			"16": "<sup>16</sup> ಆದರೆ ನೀವು ಅಹಂಭಾವದಿಂದ ಕೊಚ್ಚಿಕೊಳ್ಳುತ್ತೀರಿ. ಹಾಗೆ ಕೊಚ್ಚಿಕೊಳ್ಳುವುದು ಸರಿಯಲ್ಲ.",
			"17": "<sup>17</sup> ಒಬ್ಬನು ಒಳ್ಳೆಯದನ್ನು ಮಾಡಬೇಕೆಂದು ತಿಳಿದಿದ್ದೂ ಅದನ್ನು ಮಾಡದಿದ್ದರೆ ಅದು ಅವನಿಗೆ ಪಾಪವಾಗಿರುತ್ತದೆ."
		},
		"5": {
			"1": "<sup>1</sup> ಐಶ್ವರ್ಯವಂತರೇ, ಕೇಳಿ: ನಿಮಗೆ ಬರಲಿರುವ ಮಹಾಕಷ್ಟಗಳಿಗಾಗಿ ಕಣ್ಣೀರಿಡಿ, ಗೋಳಾಡಿರಿ.",
			"2": "<sup>2</sup> ನಿಮ್ಮ ಐಶ್ವರ್ಯ ನಾಶವಾಗಿದೆ. ನಿಮ್ಮ ಬೆಲೆಬಾಳುವ ಬಟ್ಟೆಗಳಿಗೆ ನುಸಿಹತ್ತಿದೆ.",
			"3": "<sup>3</sup> ನಿಮ್ಮ ಬೆಳ್ಳಿಬಂಗಾರಕ್ಕೆ ತುಕ್ಕುಹಿಡಿದಿದೆ. ಆ ತುಕ್ಕೇ ನಿಮಗೆ ವಿರುದ್ಧ ಸಾಕ್ಷಿಯಾಗಿ ನಿಮ್ಮನ್ನು ಬೆಂಕಿಯಂತೆ ದಹಿಸಿಬಿಡುತ್ತದೆ. ಅಂತ್ಯಕಾಲಕ್ಕಾಗಿ ನೀವು ಬೆಂಕಿಯನ್ನೇ ಶೇಖರಿಸಿಟ್ಟುಕೊಂಡಿದ್ದೀರಿ.",
			"4": "<sup>4</sup> ನಿಮ್ಮ ಹೊಲಗಳಲ್ಲಿ ದುಡಿದ ಆಳುಗಳ ಕೂಲಿಯನ್ನು ಮೋಸದಿಂದ ಹಿಡಿದಿಟ್ಟುಕೊಂಡಿದ್ದೀರಿ. ನೀವು ಕೊಡದ ಕೂಲಿಯೇ ನಿಮಗೆ ವಿರುದ್ಧವಾಗಿ ಕೂಗಿಕೊಳ್ಳುತ್ತಿದೆ. ಕೂಲಿಯಾಳುಗಳ ಗೋಳಾಟ ಸ್ವರ್ಗಸೇನಾಧೀಶ್ವರನಾದ ಪ್ರಭುವಿನ ಕಿವಿಗೂ ಬಿದ್ದಿದೆ.",
			"5": "<sup>5</sup> ಲೋಕದಲ್ಲಿ ನೀವು ಸುಖಭೋಗಿಗಳಾಗಿ ವಿಲಾಸ ಜೀವನ ನಡೆಸಿದ್ದೀರಿ. ವಧೆಯ ದಿನಕ್ಕಾಗಿ ಪಶುಗಳಂತೆ ನಿಮ್ಮನ್ನೇ ಕೊಬ್ಬಿಸಿಕೊಂಡಿದ್ದೀರಿ.",
			"6": "<sup>6</sup> ನಿಮ್ಮನ್ನು ವಿರೋಧಿಸಿದ ಸಜ್ಜನನನ್ನು ಖಂಡಿಸಿ ಕೊಲೆಮಾಡಿಸಿದ್ದೀರಿ.",
			"7": "<sup>7</sup> ಆದ್ದರಿಂದ ಸಹೋದರರೇ, ಪ್ರಭು ಪುನರಾಗಮಿಸುವವರೆಗೆ ತಾಳ್ಮೆಯಿಂದ ಇರಿ. ರೈತನನ್ನು ಗಮನಿಸಿರಿ: ತನ್ನ ಹೊಲದಲ್ಲಿ ಉತ್ತಮ ಬೆಳೆಯನ್ನು ನಿರೀಕ್ಷಿಸುವ ಅವನು ಮುಂಗಾರು ಹಿಂಗಾರು ಮಳೆಗಾಗಿ ತಾಳ್ಮೆಯಿಂದ ಕಾದಿರುತ್ತಾನೆ.",
			"8": "<sup>8</sup> ಹಾಗೆಯೇ, ನೀವೂ ಸಹ ತಾಳ್ಮೆಯಿಂದಿರಿ, ದೃಢಚಿತ್ತರಾಗಿರಿ. ಪ್ರಭುವಿನ ಪುನರಾಗಮನ ಸನ್ನಿಹಿತವಾಗಿದೆ.",
			"9": "<sup>9</sup> ಸಹೋದರರೇ, ದಂಡನಾತೀರ್ಪಿಗೆ ಗುರಿ ಆಗದಂತೆ ಒಬ್ಬರ ಮೇಲೊಬ್ಬರು ಗೊಣಗುಟ್ಟಬೇಡಿ. ಇಗೋ, ನ್ಯಾಯಾಧೀಶನು ಬಾಗಿಲ ಬಳಿಯಲ್ಲೇ ನಿಂತಿದ್ದಾನೆ.",
			"10": "<sup>10</sup> ಸಹೋದರರೇ, ಸಂಕಷ್ಟಗಳನ್ನು ತಾಳ್ಮೆಯಿಂದ ಸಹಿಸಿದ್ದಕ್ಕೆ ನಿದರ್ಶನ ಬೇಕೇ? ಪ್ರಭುವಿನ ಹೆಸರಿನಲ್ಲಿ ಬೋಧಿಸಿದ ಪ್ರವಾದಿಗಳನ್ನು ಸ್ಮರಿಸಿಕೊಳ್ಳಿ.",
			"11": "<sup>11</sup> ತಾಳಿದವನು ಬಾಳಿಯಾನು ಎಂದು ಹೇಳುತ್ತೇವಲ್ಲವೇ? ಯೋಬನ ತಾಳ್ಮೆಯನ್ನು ಕುರಿತು ನೀವು ಕೇಳಿದ್ದೀರಿ. ಅಂತ್ಯದಲ್ಲಿ ಆತನಿಗೆ ಸರ್ವೇಶ್ವರ ದಯಪಾಲಿಸಿದ್ದನ್ನು ಬಲ್ಲಿರಿ. ದೇವರ ದಯೆ ಅನಂತ, ಅವರ ಅನುಕಂಪ ಅಪಾರ.",
			"12": "<sup>12</sup> ಮುಖ್ಯವಾಗಿ ಸಹೋದರರೇ, ಆಣೆಯಿಡಬೇಡಿ. ಪರಲೋಕದ ಮೇಲಾಗಲಿ, ಭೂಲೋಕದ ಮೇಲಾಗಲಿ, ಇನ್ನಾವುದರ ಮೇಲಾಗಲಿ ನೀವು ಆಣೆಯಿಡಬಾರದು. ಹೌದಾದರೆ ಹೌದು, ಇಲ್ಲವಾದರೆ ಇಲ್ಲ ಎನ್ನಿ. ಆಗ ನೀವು ದಂಡನಾತೀರ್ಪಿಗೆ ಗುರಿಯಾಗುವುದಿಲ್ಲ.",
			"13": "<sup>13</sup> ನಿಮ್ಮಲ್ಲಿ ಯಾರಾದರೂ ಸಂಕಟದಲ್ಲಿದ್ದರೆ ಅಂಥವನು ದೇವರಲ್ಲಿ ಪ್ರಾರ್ಥಿಸಲಿ. ಸಂತೋಷದಲ್ಲಿದ್ದರೆ ದೇವರಿಗೆ ಸ್ತುತಿಗಾನ ಹಾಡಲಿ.",
			"14": "<sup>14</sup> ನಿಮ್ಮಲ್ಲಿ ಯಾರಾದರೂ ಅಸ್ವಸ್ಥರಾಗಿದ್ದರೆ, ಅವನು ಸಭಾಪಾಲಕರನ್ನು ಕರೆಸಲಿ. ಅವರು ಅವನಿಗಾಗಿ ದೇವರನ್ನು ಪ್ರಾರ್ಥಿಸಿ ಪ್ರಭುವಿನ ಹೆಸರಿನಲ್ಲಿ ಅವನನ್ನು ಅಭ್ಯಂಗಿಸಲಿ.",
			"15": "<sup>15</sup> ಆಗ ವಿಶ್ವಾಸಭರಿತ ಪ್ರಾರ್ಥನೆ ರೋಗಿಯನ್ನು ಗುಣಪಡಿಸುತ್ತದೆ. ಹಾಸಿಗೆ ಹಿಡಿದ ರೋಗಿಯನ್ನು ಪ್ರಭು ಎಬ್ಬಿಸುತ್ತಾರೆ; ಅವನು ಪಾಪಮಾಡಿದವನಾಗಿದ್ದರೆ ಕ್ಷಮೆಯನ್ನು ಪಡೆಯುತ್ತಾನೆ.",
			"16": "<sup>16</sup> ಆದ್ದರಿಂದ ಒಬ್ಬರಿಗೊಬ್ಬರು ಪಾಪಗಳನ್ನು ಒಪ್ಪಿಕೊಳ್ಳಿರಿ. ಒಬ್ಬರಿಗೊಬ್ಬರು ಪ್ರಾರ್ಥಿಸಿರಿ. ಆಗ ನೀವು ಸ್ವಸ್ಥರಾಗುತ್ತೀರಿ. ಸತ್ಪುರುಷನ ಪ್ರಾರ್ಥನೆ ಶಕ್ತಿಯುತವಾದುದು ಹಾಗೂ ಫಲದಾಯಕವಾದುದು.",
			"17": "<sup>17</sup> ಎಲೀಯನು ನಮ್ಮಂಥ ಸಾಧಾರಣ ಮನುಷ್ಯ. ಆದರೂ ಅವನು ಮಳೆ ಬಾರದಿರಲೆಂದು ಭಕ್ತಿಯಿಂದ ಪ್ರಾರ್ಥಿಸಿದ್ದರಿಂದ ಮೂರು ವರ್ಷ ಆರು ತಿಂಗಳವರೆಗೂ ಮಳೆ ಬೀಳಲಿಲ್ಲ.",
			"18": "<sup>18</sup> ಅನಂತರ, ಅವನು ಮತ್ತೆ ಮಳೆಗಾಗಿ ಪ್ರಾರ್ಥಿಸಿದಾಗ ಆಕಾಶದಿಂದ ಮಳೆ ಸುರಿಯಿತು. ಭೂಮಿ ಫಲಿಸಿತು.",
			"19": "<sup>19</sup> ಸಹೋದರರೇ, ನಿಮ್ಮಲ್ಲಿ ಒಬ್ಬನು ಸತ್ಯದ ಮಾರ್ಗವನ್ನು ಬಿಟ್ಟು ತಪ್ಪಿಹೋದಾಗ ಇನ್ನೊಬ್ಬನು ಅವನನ್ನು ಸನ್ಮಾರ್ಗಕ್ಕೆ ತಂದರೆ,",
			"20": "<sup>20</sup> ಇದನ್ನು ನೆನಪಿಗೆ ತಂದುಕೊಳ್ಳಿರಿ: ಪಾಪಿಯನ್ನು ದುರ್ಮಾರ್ಗದಿಂದ ಮರಳಿ ಸನ್ಮಾರ್ಗಕ್ಕೆ ತಂದವನು ತನ್ನ ಆತ್ಮವನ್ನು ನಿತ್ಯ ಮರಣದಿಂದ ತಪ್ಪಿಸಿ ತನ್ನ ಲೆಕ್ಕವಿಲ್ಲದ ಪಾಪಗಳಿಗೆ ಕ್ಷಮೆಯನ್ನು ಪಡೆಯುತ್ತಾನೆ."
		}
	}
};
module.exports = book;