var book = {
	"name": "1 Chronicles",
	"numChapters": 29,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> Adama, Setha, Enosa,",
			"2": "<sup>2</sup> Kenana, Mahahlalela, Jareda,",
			"3": "<sup>3</sup> Enoka, Methusela, Lameka,",
			"4": "<sup>4</sup> Nova, Sema, hama, Japhetha te.",
			"5": "<sup>5</sup> Japhetha fapate chu Gomera te, Magoga te, Madaia te, Javana te, Tubala te, Meseka te, Tirasa te an ni.",
			"6": "<sup>6</sup> Tin, Gomera fapate chu Askenaza te, Diphatha te, Togarma te an ni.",
			"7": "<sup>7</sup> Tin, Javana fapate chu Elisa te, Tarsisa te, Kitima te, Rodanima te an ni.",
			"8": "<sup>8</sup> Hama fapate chu Kusa te, Mizraima te, Puta te, Kanaana te an ni.",
			"9": "<sup>9</sup> Tin, Kusa fapate chu Seba te, Havila te, Sabta te, Raama te, Sabteka te an ni. Tin, Raama fapate chu Seba te, Dedana te an ni.",
			"10": "<sup>10</sup> Tin, Kusan Nimroda a hring a; a ni chu leia mi chak huaisen lo awm tanna a ni.",
			"11": "<sup>11</sup> Tin, Mizraiman Ludima te, Anamima te, Lehabima te, Baphtuhima te,",
			"12": "<sup>12</sup> Pathrusima te, Kasluhima te, (chu mi lakah chuan Philistia-ho hi an lo chhuak a ni) Kaphtorima te a hring a.",
			"13": "<sup>13</sup> Tin, Kanaana chuan a fa tirah Zidona a hring a, tin, Hetha;",
			"14": "<sup>14</sup> tin, Jebus-ho te, Amor-ho te, Girgas-ho te;",
			"15": "<sup>15</sup> Hiv-ho te, Ark-ho te, Sin-ho te,",
			"16": "<sup>16</sup> Arvad-ho te, Zemar-ho te, Hamath-ho te.",
			"17": "<sup>17</sup> Sema fapate chu Elama te, Asura te, Arpaksada te, Luda te, Arama te, Uza te, Hula te, ethera te, Meseka te an ni.",
			"18": "<sup>18</sup> Tin, Arpaksada chuan Sela a hring a, tin, Sela chuan Ebera a hring a.",
			"19": "<sup>19</sup> Tin, Ebera lakah chuan fapa pahnih an piang a: pakhata hming chu Pelega a ni a; a dam laiin lei hi thena a awm avangin; a nau hming chu Joktana a ni a.",
			"20": "<sup>20</sup> Tin, Joktana chuan Almodada te, Selepha te, Hazarmavetha te, Jera te leh",
			"21": "<sup>21</sup> Hadorama te, Uzala te, Dikla te leh",
			"22": "<sup>22</sup> Ebala te, Abimaela te, Seba te leh",
			"23": "<sup>23</sup> Ophira te, Havila te, Jobaba te a hring a. Chung zawng zawng chu Joktana thlahte an ni.",
			"24": "<sup>24</sup> Sema te, Arpaksada te, Sela te;",
			"25": "<sup>25</sup> Ebera te, Pelega te, Reuva te;",
			"26": "<sup>26</sup> Seruga te, Nahora te, Tera te;",
			"27": "<sup>27</sup> Abrama te (chu chu Abrahama a ni).",
			"28": "<sup>28</sup> Abrahama fapate chu Isaaka te, Ismaela te an ni.",
			"29": "<sup>29</sup> Henge hi an thlahte chu an ni: Ismaela fa tir chu Nebaiotha; tin, Kedara te, Adbeela te, Mibsama te,",
			"30": "<sup>30</sup> Misma te, Duma te, Masa te, Hadada te, Tema te,",
			"31": "<sup>31</sup> Jetura te, Naphisa te, Kedema te, Chungte chu Ismaela thlahte an ni.",
			"32": "<sup>32</sup> Joksana te, Medana te, Midiana te, Isbaka te, Sua te an ni. Tin, Joksana fapate chu Seba leh Dedana an ni.",
			"33": "<sup>33</sup> Tin, Midiana fapate chu Epha te, Ephera te, Hanoka te, Abida te, Elda te an ni. Chung zawng zawng chu Keturi thlahte an ni.",
			"34": "<sup>34</sup> Abrahama'n Isaaka a hring a. Isaaka fapate chu Esauva leh Israela an ni.",
			"35": "<sup>35</sup> Esauva fapate chu Elophaza te, Reuela te, Jeusa te, Jalama te, Kora te an ni.",
			"36": "<sup>36</sup> Eliphaza fapate chu Temana te, Omara te, Zephia te, Gatama te, Kenaza te, Timna te, Amaleka te an ni.",
			"37": "<sup>37</sup> Reuela fapate chu Nahatha te, Zera te, Sama te, Miza te an ni.",
			"38": "<sup>38</sup> Tin, Seira fapate chu Lotana te, Sobala te, Zibeon te, Ana te, Disona te, Ezera te, Disana te an ni.",
			"39": "<sup>39</sup> Tin, Lotana fapate chu Horia leh Homama an ni a: tin, Lotana chuan farnu Timni a nei a.",
			"40": "<sup>40</sup> Sobala fapate chu Aliana te, Mahanatha te, Ebala te, Sephia te, Onama te an ni. Tin, Zibeona fapate chu Aia leh Ana an ni.",
			"41": "<sup>41</sup> Ana fapa chu Disona a ni. Tin, Disona fapate chu Hamrana te, Esbana te, Ithrana te, Kerana te an ni.",
			"42": "<sup>42</sup> Ezera fapate chu Bilhana te, Zaavana te, Jaakana te an ni. Disana fapate chu Uza leh Arana an ni.",
			"43": "<sup>43</sup> Israela thlahte chunga lal an la awm hmaa Edom ram lalte chu henge hi an ni: Beora fapa Bela a ni; a khawpui hming chu Dinhaba a ni.",
			"44": "<sup>44</sup> Tin, Bela chu a thi a, Bozra khuaa mi Zera fapa Jobaba a aiah a lal ta a.",
			"45": "<sup>45</sup> Tin, Jobaba chu a thi a, Teman-ho rama mi Husama a aiah a lal ta a.",
			"46": "<sup>46</sup> Tin, Husama chu a thi a, Bedada fapa Hadada, Moab phula Midian-ho thattu kha a aiah a lal ta a: a khawpui hming chu Avith a ni.",
			"47": "<sup>47</sup> Tin, Hadada chu a thi a, Masreka mi Samla a aiah a lal ta a.",
			"48": "<sup>48</sup> Tin, Samala chu at hi a, Luipui kama Rehoboth khua a mi Saula a aiah a lal ta a.",
			"49": "<sup>49</sup> Tin, Saula chu a thi a, Akbora fapa Baalhanana a aiah a lal ta a.",
			"50": "<sup>50</sup> Tin, Baal-hanana chu a thi a, Hadada a aiah a lal ta a; a khawpui hming chu Pai a ni: a nupui hming chu Mezahaba fa Matredi fanu Mehetabeli a ni.",
			"51": "<sup>51</sup> Tin, Hadada chu a thi a. Tin, Edom-ho hotute chu hotu Timana te, hotu Alia te, hotu Jethetha te;",
			"52": "<sup>52</sup> hotu Oholibama te, hotu Ela te, hotu Pinona te;",
			"53": "<sup>53</sup> hotu Kenaza te, hotu Temana te, hotu Mibzara te;hotu Magdiela te, hotu Irama te an ni. Chung chu Edom-ho hotute an ni.",
			"54": "<sup>54</sup> hotu Magdiela te, hotu Irama te an ni. Chung chu Edom-ho hotute an ni."
		},
		"2": {
			"1": "<sup>1</sup> Hengte hi Israela fapate chu an ni; Reubena, Simeona, Levia, Juda, Isakara, Zebuluna,",
			"2": "<sup>2</sup> Dana, Josefa, Benjamina, naphtalia, Gada, Asera.",
			"3": "<sup>3</sup> Juda fapate chu Era, Onana, Sela: chu mite pathum chu Kanaan mi Bathsuii laka piang an ni. Tin, Juda fa tir Era chu LALPA mithmuhin mi sual a ni a; tichuan a tihlum ta a.",
			"4": "<sup>4</sup> Tin, a monu Tamari chuan Pereza leh Zera a hrinsak a. Juda fapate zawng zawng chu panga an ni.",
			"5": "<sup>5</sup> Pereza fapate chu Jezrona leh Hamula an ni.",
			"6": "<sup>6</sup> Tin, Zera fapate chu Zimria te, Ethana te, Hemana te, Kalkola te, Dara te an ni: an vaiin panga an ni.",
			"7": "<sup>7</sup> Tin, Karmia fapa chu Akara, Israelte tibuaitu, thilhlan bawhchhetupa kha a ni.",
			"8": "<sup>8</sup> Tin, Ethana fapa chu Azaria a ni.",
			"9": "<sup>9</sup> Hezrona fapa te, a laka piangte chu Jerameela te, Rama te, Kelubaia te an ni.",
			"10": "<sup>10</sup> Tin, Raman Aminadaba ahring a; tin, Aminadaban Juda thlah mi lian Nasona a hring a;",
			"11": "<sup>11</sup> tin, Nasonan Salma a hring a, tin, Salman Boaza a hring a;",
			"12": "<sup>12</sup> tin, Boazan Obeda a hring a, tin, Obedan Jesaia a hring a;",
			"13": "<sup>13</sup> tin, Jesaian a fa tirah Eliaba a hring a, a apahnihna chu Abinadaba, a pathumna chu Simea,",
			"14": "<sup>14</sup> a palina chu Nethanela, a pangana chu Radaia,",
			"15": "<sup>15</sup> a parukna chu Ozema, a pasarihna chu Davida a ni:",
			"16": "<sup>16</sup> tin, an farnute chu Zeruii leh Abigaili an ni. Tin, Zeruii fapate chu Abisaia te, Joaba te, Asahela te an ni a, pathum an ni.",
			"17": "<sup>17</sup> Tin, Abigailin Amasa a hring a: tin, Amasa pa chu Ismael mi Jethera a ni.",
			"18": "<sup>18</sup> Tin, Hezrona fapa Kaleban a nupui Azubi, Jeriotha fanu lakah chuan fate a hring a: tin, hengte hi a fapate chu an ni: Jesera, Sobaba, Ardona,",
			"19": "<sup>19</sup> Tin, Azubi chu a thi a, Kaleba chuan Ephrathi a nei a, ani chuan Hura a hrinsak a.",
			"20": "<sup>20</sup> Tin, Hura chuan Uria a hring a, Urian Bezalela a hring a.",
			"21": "<sup>21</sup> Tin, chu mi hnuah chuan Hezrona chuan Gilead pa Makira fanu a pawl a; kum sawmruk a nihin chu mi chu a nei a; ani chuan Seguba a hrinsak a.",
			"22": "<sup>22</sup> Tin, Seguba chuan Jaira a hring a, ani chuan Gilead ramah khawpui sawmhnih leh pathum a nei a.",
			"23": "<sup>23</sup> Tin, Gesura leh Arama chuan jaira khawpui te, Kenath khua leh a khawperte nen, khaw sawmruk alaksak a. Chung zawng zawng chu Gileada pa Makira fapate chu an ni.",
			"24": "<sup>24</sup> Tin, Keleb-ephrath-a khua a Jezrona a thih hnu chuan Hezrona nupui Aibiji chuan Tekoa pa Asura a hrinsak a.",
			"25": "<sup>25</sup> Tin, Hezrona fa tir Jerameela fapa upa ber chu Rama ani a, tin, Buna te, Orena te, Ozema te, Ahija te an ni.",
			"26": "<sup>26</sup> Tin, Jerameela chuan nupui dang a nei a, a hming chu Atari a ni a, Onama nu a ni.",
			"27": "<sup>27</sup> Jerameela fapa upa ber Rama fapate chu maaza te, Jamina te, Ekera-te an ni.",
			"28": "<sup>28</sup> Tin, Onama fapate chu Samaia leh Jada an ni a; tin, Samaia fapa te chu nadaba leh Abisura an ni.",
			"29": "<sup>29</sup> Tin, Abisura nupuia hming chu Abihaili a ni; tin, ani chuan Abana leh Molida a hrinsak a.",
			"30": "<sup>30</sup> Tin, Nadaba fapate chu Seleda leh Apaima an ni; Seleda erawh chu fa nei loin a thi a.",
			"31": "<sup>31</sup> Tin, Apaima fapa chu Isia a ni a. Tin, Isia fapa chu Sesana a ni a. Tin, Sesana fapa chu Alaia a ni.",
			"32": "<sup>32</sup> Tin, Samaia unaupa Jada fapate chu Jethera leh Jonathana an ni: tin, Jethera chu fa nei loin a thi a.",
			"33": "<sup>33</sup> Tin, Jonathana fapate chu Peletha leh Zaza an ni. Chungte chu Jerameela thlahte an ni.",
			"34": "<sup>34</sup> Sesana chuan fanute tih loh chu fa a nei lo a. Tin, Sesana chuan Aigupta mi chhiahhlawh anei a, a hming chu Jara a ni.",
			"35": "<sup>35</sup> Tin, Sesana chuan a fanu chu a chhiahhlawh Jara chu nupuiah a neihtir a; tin, Ataia a hrinsak a.",
			"36": "<sup>36</sup> Tin, Ataia chuan nathana a hring a, tin, Nathana chuan Zabada a hring a,",
			"37": "<sup>37</sup> tin, Zabada chuan Ephlala a hring a, tin, Ephlala chuan Obeda ahring a,",
			"38": "<sup>38</sup> tin, Obeda chuan Jehua a hring a, tin, Jehua chuan Azaria ahing a.",
			"39": "<sup>39</sup> Tin, Azaria chuan Heleza a hring a, tin, Heleza chuan Eleasa a hring a,",
			"40": "<sup>40</sup> tin, Eleasa chuan Sismaia a hring a, tin, Sismaia chuan Saluma a hring a;",
			"41": "<sup>41</sup> tin, Saluma chuan Jekamia a hring a, tin, Jekamia chuan Elisama a hring a.",
			"42": "<sup>42</sup> Tin, Jerameela unaupa Keleba fa tir chu Zip-ho thlahtu Mesaka a ni:",
			"43": "<sup>43</sup> Tin, Hebrona fapate chu Kora te, Tapua te, Rekema te, Sema te an ni.",
			"44": "<sup>44</sup> Tin, Seman Jorkeama pa Rahama a hring a; tin, Rekeman Samaia a hring a.",
			"45": "<sup>45</sup> Tin, Samaia fapa chu Maona a ni a; tin, Maona chu Beth-zura pa a ni.",
			"46": "<sup>46</sup> Tin, Kaleba hmei Ephi chuan Harana te, Moza te, Gazeza te a hring a tin, harana chuan Gazeza a hring a.",
			"47": "<sup>47</sup> Tin, Judaii fapate chu Regema te, Jethama te, Gesana te, Peleta te, Epha te, Saapha te an ni.",
			"48": "<sup>48</sup> Kaleba hmei Maakin Sebera leh Tirhana a hring a.",
			"49": "<sup>49</sup> Madmana pa Saapha leh Makbena leh Gibea pa Seva a hring bawk a; tin, Kaleba fanu chu Aksi a ni.",
			"50": "<sup>50</sup> Hengte hi Kaleba thlahte an ni: Hura fa, Kiriath-jeraim-ho thlahtu, Ephrathi fa tir Sobala,",
			"51": "<sup>51</sup> tin, Beth-lehem-ho thlahtu Salma, tin, Beth-gader-ho thlahtu Harepha te hi.",
			"52": "<sup>52</sup> Tin, Kiriath-jearim-ho thlahtu Sobala chuan Horoe-ho leh Menuhot-ho zatve a thlah a.",
			"53": "<sup>53</sup> Tin, Kiriath-jearim hnamte chu Ithra-ho te, Put-ho te, Sumat-ho te, Misra-ho te an ni; chungho atang chuan Zorat-ho te leh Estaol-ho te an lo chhuak a.",
			"54": "<sup>54</sup> Salma thlahte chu Beth-lehem-ho te, Netophat-ho te, Atrot-beth-joab-ho te, Manahat-ho zatve te, Zor-ho te an ni.Tin, Jabez-a awm lehkhaziaktu hnamte chu Tirat-ho te, Simeat-ho te, Sukat-ho te an ni. Chungte chu Rekaba thlahte zinga mi Hamatha thlah Ken-hote chu an ni.",
			"55": "<sup>55</sup> Tin, Jabez-a awm lehkhaziaktu hnamte chu Tirat-ho te, Simeat-ho te, Sukat-ho te an ni. Chungte chu Rekaba thlahte zinga mi Hamatha thlah Ken-hote chu an ni."
		},
		"3": {
			"1": "<sup>1</sup> Tichuan hengte hi Davida fapa, Hebron khuaa a hrinte chu an ni: a fa tir chu Jezreel khua Ahinoami laka mi Amnona a ni a; a dawttu chu Karmel khua Abigaili laka mi Daniela a ni a;",
			"2": "<sup>2</sup> a pathumna chu Gesur lal Talmaia fanu Maaki fapa Absaloma a ni a; a palina chu Hagithi fapa Adonija a ni a;",
			"3": "<sup>3</sup> a pangana chu Abitali laka mi Sepahtia a ni a; a parukna chu a nupui Egli laka mi Ithreama a ni a.",
			"4": "<sup>4</sup> Hebron khuaah chuan paruk ahring a; chutah chuan kum sarih leh thla ruk a lal a: tin, Jerusalem khuaah kum sawmthum leh kum thum a lal a.",
			"5": "<sup>5</sup> Tin, Jerusalem khuaa a hrinte chu Simea te, Sobaba te, Nathana te, Solomona te an ni a; an pali chuan Amiela fanu Bath-sui laka mi an ni.",
			"6": "<sup>6</sup> Tin, Ibhara te, Elisama te, Elipheleta te,",
			"7": "<sup>7</sup> Noga te, Nephega te, Japhia te,",
			"8": "<sup>8</sup> Elisama te, Eliada te, Elipheleta te, pakua an ni.",
			"9": "<sup>9</sup> Chung zawng zawngte chu Davida fapate anni, hmei fate tiam loin; tin, Tamari chu an farnu a ni.",
			"10": "<sup>10</sup> Tin, Solomona fapa chu Rehoboama a ni a, chu mi fapa chu Abija a ni a, chu mi fapa chu Asa ani a, chu mi fapa chu Jehosaphata a ni a;",
			"11": "<sup>11</sup> chu mi fapa chu Jorama a ni a, chu mi fapa chu Ahazia a ni a, chu mi fapa chu Joasa ani a;",
			"12": "<sup>12</sup> chu mi fapa chu Amazia a ni a, chu mi fapa chu Azaria a ni a, chu mi fapa chu Jothama ani a;",
			"13": "<sup>13</sup> chu mi fapa chu Ahaza a ni a, chu mi fapa chu Hezekia a ni a, chu mi fapa chu Manasea a ni a;",
			"14": "<sup>14</sup> chu mi fapa chu Amona a ni a, chu mi fapa chu Josia a ni a.",
			"15": "<sup>15</sup> Tin, Josia fapate chu a fa tir chu Johanana a ni a, a dawttu chu Jehoiakima a ni a, a pathumna chu Zedekia ani a, a palina chu Saluma a ni.",
			"16": "<sup>16</sup> Jehoiakima fapa Jekonia chu Zedekia pa a ni.",
			"17": "<sup>17</sup> Tin, sal tang Jekonia fapate chu Sealtiela te,",
			"18": "<sup>18</sup> malkirama te, Pedaia te, Senazara te, Jekamia te, Hosama te, Nedabia te an ni.",
			"19": "<sup>19</sup> Tin, Pedaia fapate chu Zerubabela leh Simeia an ni; tin, Zerubabela fapate chu Mesulama leh Hanania an ni a; tin, Selomithi chu an farnu a ni;",
			"20": "<sup>20</sup> tin, Hasuba te, Ohela te, Berekia te, Hasadia te, Jusab-heseda te, panga an ni.",
			"21": "<sup>21</sup> Tin, Hanania fapa chu Pelatia a ni a, chu mi fapa chu Jesaia a ni a, chu mi fapa chu Rephaia a ni a, chu mi fapa chu Arnana ani a, chu mi fapa chu Obadia a ni a, chu mi fapa chu Sekania a ni.",
			"22": "<sup>22</sup> Tin, Sekania fapa chu Semaia a ni; tin, Semaia fapate chu Hatusa te, Igala te, Baria te, Nearia te, Saphata te an ni a, paruk an ni.",
			"23": "<sup>23</sup> Tin, Nearia fapate chu Elioenaia te, Hizkia te, Azrikama te an ni a, pathum an ni.Tin, Elioenaia fapate chu Hodavia te, Eliasiba te, Pelaia te, Akuba te, Johanana te, Delaia te, Anania te an ni a, pasarih an ni.",
			"24": "<sup>24</sup> Tin, Elioenaia fapate chu Hodavia te, Eliasiba te, Pelaia te, Akuba te, Johanana te, Delaia te, Anania te an ni a, pasarih an ni."
		},
		"4": {
			"1": "<sup>1</sup> Juda fapate chu Pereza te, Hezrona te, Karmia te, Hura te, Sobala te an ni.",
			"2": "<sup>2</sup> Tin, Sobala fapa Reaian Jahatha a hring a; tin, Jahathan Ahumaia leh Lahada a hring a, chung chu Zorat-ho hnamte an ni.",
			"3": "<sup>3</sup> Tin, Etama pa fapate chu Jezreela te, Isma te, Idbasa te an ni; tin, an farnu hming chu Hazelelponi a ni;",
			"4": "<sup>4</sup> tin, Gedora pa Penuela leh Husa pa Ezera an ni. Chungte chu Bethlehema pa, Ephrathi fatir, Hura thlahte an ni.",
			"5": "<sup>5</sup> Tin, Tekoa pa Asuran nupui pahnih Heli leh Nari a nei a.",
			"6": "<sup>6</sup> Tin, Nari chuan Ahuzama te, Hephera te, Temenia te, Hahastaria te a hrinsak a. Chungte chu Nari fapate an ni.",
			"7": "<sup>7</sup> Tin, Heli fapate chu Zeretha te, Izhara te, Ethnana te an ni.",
			"8": "<sup>8</sup> Tin, Hakozan Anuba te, Zobeba te, Haruma fapa Aharhel-ho thlahte a hring a.",
			"9": "<sup>9</sup> Tin, Jabeza chu a unaute aia mi zahawm a ni. A nu chuan, \"Lungngai chunga ka hrin a ni a,\" tiin a hmingah Jabeza a ti a ni.",
			"10": "<sup>10</sup> Tin, Jabeza chuan, \"Aw, mal mi sawm ngei ngei la, ka ramri hi mi zauh la, i kut chuan mi awmpuiin lungngaihna ka chungah a thlen lohna'n sual lak ata mi thiar fihlim ang che,\" tiin Israelte Pathian chu a au a. Tichuan Pathianin a thil dil chu a hlawhtlintir a.",
			"11": "<sup>11</sup> Tin, Suha unaupa Keluban Mehira a hring a, chu mi chu Estona pa a ni.",
			"12": "<sup>12</sup> Tin, Estona chuan Beth-rapha te, Pasea te, Ir-nahasa pa Tehina te a hring a. Chungte chu Reka-hote an ni.",
			"13": "<sup>13</sup> Tin, Kenaza fapate chu Othniela leh Seraia an ni a: tin, Othniela fapa chu Hathatha a ni.",
			"14": "<sup>14</sup> Tin, Meonothaian Ophra a hring a; tin, Seraian Geharasim-ho thlahtu Joaba a hring a: chu mite chu kut themthiam mi an ni.",
			"15": "<sup>15</sup> Tin, Jephunea fapa Kaleba fapate chu Irua te, Ela te, Naama te an ni a: tin, Ela fapa chu Kenaza a ni.",
			"16": "<sup>16</sup> Tin, Jehalela fapate chu Zipa te, Zipha te, Tiria te, Asarela te an ni.",
			"17": "<sup>17</sup> Tin, Ezra fapate chu Jethera te, Mereda te, Ephera te, Jalona te an ni; tin, Pharaoa fanu Bithiaii Mereda nupui fapate chu Miriama te, Samaia te, Estemoa pa Isba te an ni.",
			"18": "<sup>18</sup> Tin, a nupui Juda hmeichhiain Gedora pa Jereda te, Sokoa pa Hebera te, Zanoa pa Jekuthiela te a hring a.",
			"19": "<sup>19</sup> Tin, Nahama farnu Hodia nupuia fapate chu Garm-ho thlahtu Keila pa leh Maakat-ho thlahtu Estemoa an ni.",
			"20": "<sup>20</sup> Tin, Simona fapate chu Amnona te, Rina te, Benhanana te, Tilona te an ni. Tin, Isia fapate chu Zohetha leh Ben-zohetha an ni.",
			"21": "<sup>21</sup> Juda fapa Sela fapate chu Leka pa Era te, Maresa pa Laada te, Asbea-ho puan zai sin siamtuhote an ni a;",
			"22": "<sup>22</sup> tin, Jokima te, Kozeba-ho te, Joasa te, Sarapha, Moab rama rorela awma, chu mi hnua Jasubi-lehem-a awm taa te kha an ni.",
			"23": "<sup>23</sup> Chung thu ziak chu hmasanga mi a ni. Chungho chu bel vawtu, Netaim leh Gedera khuaa awm an ni a: lal hnenah a hna thawkin an awm a ni.",
			"24": "<sup>24</sup> Simeona fapate chu Nemuela te, Jamina te, Jariba te, Zera te, Saula te an ni;",
			"25": "<sup>25</sup> Saula fapa chu Saluma a ni a, chu mi fapa chu Mibsama a ni a, chu mi fapa chu Misma a ni.",
			"26": "<sup>26</sup> Tin, Misma fapa chu Hamuela a ni a; chu mi fapa chu Zakura a ni a, chu mi fapa chu Simeia a ni.",
			"27": "<sup>27</sup> Tin, Simeia chuan fapa sawm leh paruk leh fanu paruk a nei a; a unaute erawh chuan fa an ngah ve lo a, Juda thlahte angin an thlahte pawh an pung ve lo.",
			"28": "<sup>28</sup> Beer-seba-ah te, Molada-ah te, Hazar-sual-ah te an awm a,",
			"29": "<sup>29</sup> Bilha-ah te, Ezem-ah te, Tolad-ah te,",
			"30": "<sup>30</sup> Bethuel-ah te, Horma-ah te, Ziklag-ah te,",
			"31": "<sup>31</sup> Bethmarkaboth-ah te, Hazar-susim-ah te, Beth-biri-ah te, Saraim-ah te pawh. Chungte chu Davida lal thlenga an khawpuite chu a ni.",
			"32": "<sup>32</sup> Tin, an khua chu khaw nga, Etam te, Ain te, Rimon te, Token te, Asan te a ni:",
			"33": "<sup>33</sup> tin, chu mi vela khaw te zawng zawng baal thlengin a ni. Chungte chu an chenna a ni a, an thlahtute thu ziakna an kawl bawk.",
			"34": "<sup>34</sup> Tin, Mesobaba te, Jamleka te, Amazia fapa Josa te,",
			"35": "<sup>35</sup> Joela te, Josibia fapa lohua te, (chu mite pa chu Adiela fapa Seraia a ni a)",
			"36": "<sup>36</sup> Elioenaia te, Jakoba te, Jesohaia te, Asaia te, Adiela te, Jesi-miela te, Benaia te,",
			"37": "<sup>37</sup> Siphia fapa Ziza te, (Chu mite pa chu Allona a ni a, chu mi chu Jedaia fapa ani a, chu mi chu Simria fapa a ni a, chu mi chu Semaia fapa a ni a.)",
			"38": "<sup>38</sup> Chung an hming an lamte chu an chhungkaw puipate an ni a; an chhungkua chu nasa takin an lo pung ta a.",
			"39": "<sup>39</sup> Tin, Gedor luhnaah, ruam chhak lampang thlengin an ran rualte tlatna tur zawngin an kal a.",
			"40": "<sup>40</sup> Tin, ran tlatna hnim tui tak tak leh tha tak tak an hmu a, a ram chu a zauin a reh a, ralmuanawm tak a ni; a hmaa lo awmte chu Hama thalhte an ni a.",
			"41": "<sup>41</sup> Tichuan chung an hming an ziakate chu Judai ram lal Hezekia lal lai khan an lut a, an puan inte leh chutih laia Meunim-ho lo awm te chu an tiboral vek a, tun thlengin; an hmun chu an luahsak a; an ran rual tan tlatna a awm avangin.",
			"42": "<sup>42</sup> Tin, thenkhat Simeona fapateho, mi zanga lai Seir tlangah an kal a, an hotu atan Isia fapa Pelatia te, Nearia te, Rephaia te, Uziela te an hmang a.Tichuan Amalek-ho hnam tlan bo la awmte kha an hneh a, tun thlengin chutah chuan an awm ta a.",
			"43": "<sup>43</sup> Tichuan Amalek-ho hnam tlan bo la awmte kha an hneh a, tun thlengin chutah chuan an awm ta a."
		},
		"5": {
			"1": "<sup>1</sup> Israela fa tir Reubena fapate chu, (ani chu fa tir zawng a ni a; amaherawhchu a pa khum a tihbawlhlawh avangin a fa tir nihna chu Israela fapa Josefa fapate pek a ni a; tichuan thlahtute chhuina chu fa tir nihnaa chhui tur a ni ta lo.",
			"2": "<sup>2</sup> Juda chuan a unaute a khum ta si a, amahah chuan mi lian chu a lo chhuak a; fa tir nihna erawh chu Josefa chan a ni)",
			"3": "<sup>3</sup> Israela fa tir Reubena fapate chu Hanoka te, Palua te, Hezrona te, Karmia te an ni.",
			"4": "<sup>4</sup> Joela fapa chu Semaia a ni a, chu mi fapa chu Goga a ni a, chu mi fapa chu Simeia a ni a,",
			"5": "<sup>5</sup> chu mi fapa chu Mika ni a, chu mi fapa chu Reaia a ni a, chu mi fapa chu Baala a ni,",
			"6": "<sup>6</sup> chu mi fapa chu Beera, Assuria lal Tilgathpileseran sala a hruai chu a ni: ani chu Reuben-ho mi lian a ni.",
			"7": "<sup>7</sup> ,8 An thlahtute hnam chhui dana unaute hnam hotute chu Nebo leh Baalmeon thlenga awm Jeiela te, Zekaria te, Bela te an ni; chu mi pa chu Azaza a ni a, chu mi pa chu Sema ani a, chu mi pa chu Aroera a ni a:",
			"8": "<sup>8</sup> ,7 An thlahtute hnam chhui dana unaute hnam hotute chu Nebo leh Baalmeon thlenga awm Jeiela te, Zekaria te, Bela te an ni; chu mi pa chu Azaza a ni a, chu mi pa chu Sema ani a, chu mi pa chu Aroera a ni a:",
			"9": "<sup>9</sup> ani chuan chhak laman Euphrates lui atanga thlaler luhna thlengin a luah a ni: Gilead ramah chuan an ran a lo pun tak avangin",
			"10": "<sup>10</sup> Tin, Saula dam lai chuan Hagarho an run a, chungho chu an kutah an lo tlawm ta a: tichuan Gilead chhak lam ram tluanin an puan inte an zar a.",
			"11": "<sup>11</sup> Tin, Gada fapate chu an ep Basan raman Saleka thlengin an awm a:",
			"12": "<sup>12</sup> hotu Joela te, a dawttu Saphama te, Janaia te, Basan-a mi Saphata te,",
			"13": "<sup>13</sup> an chhung te, an unau Mikaila te, Mesulama te, Seba te, Joraia te, Jakana te, Zia te, Ebera te nen pasarih an ni.",
			"14": "<sup>14</sup> Hengte hi Huria fapa Abihaila fapate an ni, chu mi chu Jaroa fapa a ni a, chu mi chu Gileada fapa ani a, chu mi chu Mikaila fapa a ni a, chu mi chu Jesisaia fapa a ni a, chu mi chu Jadoa fapa a ni a, chu mi chu Buza fapa a ni a;",
			"15": "<sup>15</sup> Ahia chu Abdiela fapa a ni a, chu mi chu Gunia fapa a ni a, an chhung hotu a ni.",
			"16": "<sup>16</sup> Tichuan Gilead rama Basan-ah leh a khawpuiahte leh Saron daivel zawng zawng an ram chin thlengin an luah a.",
			"17": "<sup>17</sup> Chung zawng zawngte chu Judai ram lal Jothama lal lai leh Israel ram lal Jeroboama lal laia thlahtute chhuinaah ziak an ni.",
			"18": "<sup>18</sup> Reuben-ho te, Gad-ho te, Manase hnam zatvete zinga huaisen tha thiau, phaw leh kawlhnam keng thei, thal kah thiam, indo thiamte chu 44,760 an ni.",
			"19": "<sup>19</sup> Tin, Hagar-hote, Jetur-hote, Nephis-ho te, Nodab-hote chu an do a.",
			"20": "<sup>20</sup> Tin, tanpuiin an awm a, tichuan Hagar-ho leh an hnena awm zawng zawngte chu an kutah pek an ni ta a: indo lai chuan Pathian an au a, anni chu a lo chhang a; amahah chuan rinna an nghah avangin.",
			"21": "<sup>21</sup> Tin, an rante an mansak a; an sanghawngsei singnga leh beram nuaihnih leh singnga leh sabengtung sanghnih leh mihring nuaikhat nen.",
			"22": "<sup>22</sup> Chu indo chu Pathian remtihpui a nih avangin mi tam tak an that a ni. Sal tan thlengin an awmna ram chu an luahsak ta reng a.",
			"23": "<sup>23</sup> Tin, Manasea thlah hnam zatvete chu, chu mi ramah chuan an awm a: Basan atana Baal-hermon te, Senir te, Hermon tlang thlengin an sawn zau zel a.",
			"24": "<sup>24</sup> Tin, hengte hi an chhungkaw puipate chu an ni; Hephera te, Isia te, Eliela te, Azriela te, Jeremia te, Hodavia te, Jadiela te, mi chak huaisen, mi hmingthang, an pute chhungkaw puipate chu.",
			"25": "<sup>25</sup> Tin, an pi leh pute Pathian chungah thil an tisual a, an hmaa Pathianin a lo tihboral tak, chu rama mi pathian te lakah chuan an uire ta a.Tin, Israelte Pathian chuan Assuria lal Pula rilru leh Assuria lal Tilgath-pilnesera rilru chu a chawk tho a, ani chuan Reuben-ho te, Gad-ho te, Manase hnam zatvete chu a man a, Hala-ah te, Habor-ah te, Haraah te, Gozan luiah te a hruai a, tun thleng hian an la awm.",
			"26": "<sup>26</sup> Tin, Israelte Pathian chuan Assuria lal Pula rilru leh Assuria lal Tilgath-pilnesera rilru chu a chawk tho a, ani chuan Reuben-ho te, Gad-ho te, Manase hnam zatvete chu a man a, Hala-ah te, Habor-ah te, Haraah te, Gozan luiah te a hruai a, tun thleng hian an la awm."
		},
		"6": {
			"1": "<sup>1</sup> Levia fapate chu Gersona te, Lohatha te, Meraria te an ni.",
			"2": "<sup>2</sup> Tin, Kohatha fapate chu Amrama te, Izhara te, Hebrona te, Uziela te an ni.",
			"3": "<sup>3</sup> Tin, Amrama thlahte chu Arona te, Mosia te, Miriami te an ni a. Tin, Arona fapate chu Nadaba te, Abihua te, Eleazara te, Ithamara te an ni.",
			"4": "<sup>4</sup> Elazara chuan Phinehasa a hring a, Phinehasan Abisua a hring a;",
			"5": "<sup>5</sup> tin, Abisuan Bukia a hring a, tin, Bukian Uzia a hring a;",
			"6": "<sup>6</sup> tin, Uzian Zerahia a hring a, tin, Zerahian Meraotha a hring a;",
			"7": "<sup>7</sup> Meraiothan Amarai a hring a, tin, Amarian Ahituba a hring a;",
			"8": "<sup>8</sup> tin, Ahituban Zadoka a hring a, tin, Zadokan Ahimaaza a hring a;",
			"9": "<sup>9</sup> tin, Ahimaazan Azaria a hring a, tin, Azarian Johanana ah ring a,",
			"10": "<sup>10</sup> tin, Johananan Azaria a hring a, (chu mi chuan a ni, Jerusalema Solomona sak biak ina puithiam hna thawk ni:)",
			"11": "<sup>11</sup> tin, Azarian Amaria a hring a, tin, Amarian Ahituba a hring a,",
			"12": "<sup>12</sup> tin, Ahituban Zadoka a hring a, tin, Zadokan Saluma a hring a;",
			"13": "<sup>13</sup> tin, Saluman Hilkia a hring a, tin, Hilkian Azaria a hring a;",
			"14": "<sup>14</sup> tin, Azarian Seraia a hring a, tin, Seraian Jehozadaka a hring a;",
			"15": "<sup>15</sup> tin, LALPA'n Judai ram leh Jerusalem khua Nebukdanezzara kuta a pek lai khan Jehozadaka chu salah a tang ve a.",
			"16": "<sup>16</sup> Levia fapate chu Gersoma te, Kohatha te, Meraria te an ni.",
			"17": "<sup>17</sup> Tin, he'ng hi Gersoma fapate hming chu a ni: Libnia leh Simeia.",
			"18": "<sup>18</sup> Tin, Kohatha fapate chu Amrama te, Izhara te, Hebrona te, Uziela te an ni.",
			"19": "<sup>19</sup> Meraria fapate chu Mahlia leh Musia an ni. Chunge chu an chhungkaw kal dan ang zela Levi-ho hnamte chu an ni.",
			"20": "<sup>20</sup> Gersoma fapa chu Libnia a ni a, chu mi fapa chu Jahatha a ni a, chu mi fapa chu Zima a ni a;",
			"21": "<sup>21</sup> chu mi fapa chu Joa a ni a, chu mi fapa chu Idova a ni a, chu mi fapa chu Zera a ni a, chu mi fapa chu Jeatheraia a ni.",
			"22": "<sup>22</sup> Kohatha fapae chu Aminadaba a ni a, chu mi fapa chu Kora a ni a, chu mi fapa chu Asira a ni a;",
			"23": "<sup>23</sup> chu mi fapa chu Elkana a ni a, chu mi fapa chu Ebiasapha a ni a, chu mi fapa chu Asira a ni a;",
			"24": "<sup>24</sup> chu mi fapa chu Tahatha a ni a, chu mi fapa chu Uriela a ni a, chu mi fapa chu Uzia ani a, tin, chu mi fapa chu Saula a ni.",
			"25": "<sup>25</sup> Tin, Elkana fapate chu Amasaia leh Ahimotha an ni.",
			"26": "<sup>26</sup> Elkana thlahte chu: Elkana fapa chu Zophaia a ni a, chu mi fapa chu Nahatha a ni a;",
			"27": "<sup>27</sup> chu mi fapa chu Eliaba a ni a, chu mi fapa chu Jerohama a ni a, chu mi fapa chu Elkana a ni.",
			"28": "<sup>28</sup> Tin, Samuela fapate chu a upa zawk chu Joela a nia , a dawttu chu Abija a ni.",
			"29": "<sup>29</sup> Meraria fapa chu Mahlia a ni a, chu mi fapa chu Libnia a ni a, chu mi fapa chu Simeia a ni a, chu mi fapa chu Uza a ni a;",
			"30": "<sup>30</sup> chu mi fapa chu Simea a ni a, chu mi fapa chu Jagia a ni a, chu mi fapa chu Asaia a ni.",
			"31": "<sup>31</sup> Chungte chu a ni, bawm dah hnua LALPA ina hla hruaitu atan Davida'n a ruatte chu.",
			"32": "<sup>32</sup> Tichuan Solomonan Jerusalem-a LALPA in a sak hma loh chu, inbiakna puan inah chuan hlain rawng an bawl thin a; a pawl pawlin rawngbawl hna an inchhawk kual thin a.",
			"33": "<sup>33</sup> Tin, henge hi chung hnathawktute chu a ni, an fapate nen, Kohath-ho thlahte chu hla satu Hemana a ni a, chu mi pa chu Joela a ni a, chu mi pa chu Samuela a ni a;",
			"34": "<sup>34</sup> chu mi pa chu Elkana a ni a, chu mi pa chu Jerohama a ni a, chu mi pa chu Eliela a ni a, chu mi pa chu Tova a ni a;",
			"35": "<sup>35</sup> chu mi pa chu Zupha a ni a, chu mi pa chu Elkana a ni a, chu mi pa chu Mahatha a ni a, chu mi pa chu Amasaia a ni a,",
			"36": "<sup>36</sup> chumi pa chu Elkana a ni a, chu mi pa chu Joela a ni a, chu mi pa chu Azaria a ni a, chu mi pa chu Zephani a ani a,",
			"37": "<sup>37</sup> chu mi pa chu Tahatha a ni a, chu mi pa chu Asira a ni a, chu mi pa chu Asira a ni a, chu mi pa chu Ebiasapha a ni a, chu mi pa chu Kora a ni a;",
			"38": "<sup>38</sup> chu mi pa chu Izhara a ni a, chu mi pa chu Kohatha a ni a, chu mi pa chu Levia a ni a, chu mi pa chu Israela a ni a.",
			"39": "<sup>39</sup> Tin, a unaupa Asapha, a ding lama che thin a ni a, Asapha pa chu Berekia a ni a, chu mi pa chu Simea a ni a;",
			"40": "<sup>40</sup> chu mi pa chu Mikaela a ni a, chu mi pa chu Baaseia a ni a, chu mi pa chu Malkija a ni a;",
			"41": "<sup>41</sup> chu mi pa chu Ethnia a ni a, chu mi pa chu Zera a ni a, chu mi pa chu Adaia a ni a;",
			"42": "<sup>42</sup> chu mi pa chu Ethana a ni a, chu mi pa chu Zima a ni a, chu mi pa chu Simeia a ni a;",
			"43": "<sup>43</sup> chu mi pa chu Jahatha a ni a, chu mi pa chu Gersoma a ni a, chu mi pa chu Levia a ni a.",
			"44": "<sup>44</sup> Tin, vei lama che thin an unau, Meraria fapa Ethana a ni a, chu mi pa chu Kisia a ni a, chu mi pa chu Abdia a ni a, chu mi pa chu Maluka a ni a;",
			"45": "<sup>45</sup> chu mi pa chu Hasabia a ni a, chu mi pa chu Amazia a ni a, chu mi pa chu Hilkia a ni a;",
			"46": "<sup>46</sup> chu mi pa chu Amzia a ni a, chu mi pa chu Bania a ni a, chu mi pa chu Semera a ni a;",
			"47": "<sup>47</sup> chu mi pa chu Mahlia a ni a, chu mi pa chu Musia a ni a, chu mi pa chu Meraria a ni a, chu mi pa chu Levia a ni a.",
			"48": "<sup>48</sup> Tin, an unau Levi-hote chu Pathian biakbuka rawngbawl hna zawng zawng thawk tura ruat an ni.",
			"49": "<sup>49</sup> Arona leh a thlahte erawh chuan Pathian chhiahhlawh Mosia'n thu a lo pek tawh zawng zawng ang khan, hmun thianghlim ber hna zawng zawng chu changin, Israelte tana remna siamna'n halral thilhlanna maichamah leh rimtui hlanna maichamah an hlan thin a.",
			"50": "<sup>50</sup> Tin, hengte hi Arona fapate chu an ni: Eleazara chu a fapa a ni a, chu mi fapa chu Phinehasa a ni a, chu mi fapa chu Abisua a ni a;",
			"51": "<sup>51</sup> chu mi fapa chu Bukia a ni a, chu mi fapa chu Uzia ani a, chu mi fapa chu Zerahia a ni a;",
			"52": "<sup>52</sup> chu mi fapa chu Meraiotha a ni a, chu mi fapa chu Amaria a ni a,chu mi fapa chu Ahituba a ni a;",
			"53": "<sup>53</sup> chu mi fapa chu Zadoka a ni a, chu mi fapa chu Ahimaaza a ni.",
			"54": "<sup>54</sup> Tin, hengte hi an ram chhunga an awmhmun khuar dan ang zela na awmhmunte chu a ni: Arona fa, Kohath mi hnamte chu (thumvawr chu an chunga tlu hmasa ber a ni reng a).",
			"55": "<sup>55</sup> Judai ram a Hebron leh a daivelte chu an pe a,",
			"56": "<sup>56</sup> khawpui daiv0elte leh a khawperte erawh chu Jephunea fapa Kaleba an pe a.",
			"57": "<sup>57</sup> Tin, Arona fapate chu inhumhimna khawpui Hebron te, Libna khua a daivelte nen, Jatir leh Estomoa khua a daivelte nen;",
			"58": "<sup>58</sup> tin, Hilen khua a daivelte nen, Debir khua a daivelte nen;",
			"59": "<sup>59</sup> tin, Asan khua a daivelte nen, tin, Beth-semes khua a daivelte nen;",
			"60": "<sup>60</sup> tin, Benjamin hmate zing ata Geba khua a daivelte nen, Anathoth khua a daivelte nen an pe a. An hnam zinga khawpui pek zawng zawng chu khaw sawm leh khaw thum a ni.",
			"61": "<sup>61</sup> Tin, Kohatha fapa dangte chu hnam zatve, Manasea thlahte hnam zatve chanpuala mite khawpui sawm, thumvawrin an pe a.",
			"62": "<sup>62</sup> Tin, Gersoma fapate chu an hnam kal dan ang zelin, Isakar hnam chanpuala mi te, Aser hnam chanpuala mi te, Naphtali hnam chanpuala mi te, Basan rama Manase hnam chanpuala mi te, khawpui sawm leh pathum an pe a.",
			"63": "<sup>63</sup> Meraria fapate chu an hnam kal dan ang zelin Reuben hnam chanpuala mi te, Gad hnam chanpuala mi te, Zebulun hnam chanpuala mi te, thumvawrin khaw sawm leh pahnih an pe a.",
			"64": "<sup>64</sup> Tin, Israela thlahte chuan Levi-ho chite chu khawpuite leh a daivelte an pe a.",
			"65": "<sup>65</sup> Tichuan Juda hnam chanpuala mi te, Simeon hnam chanpuala mi te, Benjamin hnam chanpuala mi te khawpui hming sawi takte chu thumvawrin an pe a ni.",
			"66": "<sup>66</sup> Tin, Kohatha thlahte hnam thenkhat chuan Ephraim-hote chanpuala mi khawpuite an chang a.",
			"67": "<sup>67</sup> Tin, Ephraim tlang rama inhumhimna khawpui Sekem chu a daivelte nen an pe a;",
			"68": "<sup>68</sup> Gezer a daivelte nen; Jokmeam a daivelte nen Beth-horon a daivelte nen;",
			"69": "<sup>69</sup> Tin, Aijalon a daivelte nen, Gath-rimon a daivelte nen;",
			"70": "<sup>70</sup> Tin, Manase hnam zatvete chanpuala mi chu - Kohatha thlahte hnam la awmte tan chuan - Aner a daivelte nen, Bileam a daivelte nen an pe a.",
			"71": "<sup>71</sup> Manase hnam zatvete chanpuala mi Gersoma fapate an pek chu Basan rama Golan a daivelte nen, Astaroth a daivelte nen a ni,",
			"72": "<sup>72</sup> Isakar hnam chanpuala mi Kedes a daivelte nen, Daberoth a daivelte nen,",
			"73": "<sup>73</sup> Ramoth a daivelte nen, Anem a daivelte nen,",
			"74": "<sup>74</sup> Aser hnam chanpuala mi Masal a daivelte nen, Abdon a daivelte nen,",
			"75": "<sup>75</sup> Hukok a daivelte nen, Rehob a daivelte nen,",
			"76": "<sup>76</sup> Naphtali hnam chanpuala mi Galili rama Kedes a daivelte nen, Hamon a daivelte nen, Kiriathaim a daivelte nen a ni.",
			"77": "<sup>77</sup> Tin, Meraria thlah Levi-ho dangte an pek chu Zebulun hnam chanpuala mi Rimono khua a daivelte nen, Tabor khua a daivelte nen,",
			"78": "<sup>78</sup> Jeriko zawn Jordan ral, Jordan chhak lampangah Reuben hnam chanpuala mi thlalera Bezer khua a daivelte nen, Jaza khua a daivelte nen,",
			"79": "<sup>79</sup> Kedemoth khua a daivelte nen, Mephaath khua a daivelte nen,",
			"80": "<sup>80</sup> Gad hnam chanpuala mi Gilead rama Ramoth khua a daivelte nen, Mahanaim khua a daivelte nen,Hesbon khua a daivelte nen,Jazer khua a daivelte nen a ni.",
			"81": "<sup>81</sup> Hesbon khua a daivelte nen,Jazer khua a daivelte nen a ni."
		},
		"7": {
			"1": "<sup>1</sup> Isakara fapate chu Tola te, Pua te, Jasuba te, Simrona te an ni a, pali an ni.",
			"2": "<sup>2</sup> Tin, Tola fapate chu Uzia te, Rephaia te, Jeriela te, Jamaia te, Ibsama te, Samuela te an ni a, Tola thlahte chhungkaw puipa an ni: anmahni chhuana mi chak huaisen an ni a; Davida dam lai chuan singhnih leh sanghnih leh zaruk an ni.",
			"3": "<sup>3</sup> Tin, Uzia fapa chu Izrahia a ni a; tin, Izrahia fapate chu Mikaela te, Obadia te, Joela te, Isia te an ni a, panga an ni, an vaiin Puipa an ni.",
			"4": "<sup>4</sup> Tin, an chhungkaw chhiarnaa chhiar telte sipai 36,000 an awm bawk; nupui leh fapa tam tak nei an ni a.",
			"5": "<sup>5</sup> Tin, Isakar hnam zawng zawng chhiarnaa chhiar tel an unaute chu mi chak huaisen 87,000 an ni.",
			"6": "<sup>6</sup> Benjamina fapate chu Bela te, Bekera te, Jediaela te an ni a, pathum an ni.",
			"7": "<sup>7</sup> Tin, Bela fapate chu Ezbona te, Uzia te, Uziela te, Jerimotha te, Iria te an ni a, panga an ni; chhungkaw puipa, mi chak huaisen an ni; thlahte chhiarnaa chhiarte chu 22,034 an ni.",
			"8": "<sup>8</sup> Tin, Bekera fapate chu Zemira te, Joasa te, Eliezera te, Elioenaia te, Omria te, Jeremotha te, Abija te, Anathotha te, Alemetha te an ni. Chung zawng zawng chu Bekera fapate an ni.",
			"9": "<sup>9</sup> Tin, anni chu thlahte chhiarnaa ziak dan chuan chhungkaw puipa, mi chak huaisen 20,200 an ni.",
			"10": "<sup>10</sup> Tin, Jediaela fapa chu Bilhana a ni a; tin, Bilhana fapate chu Jeusa te, Benjamina te, Ehuda te, Kenaana te, Zethana te, Tarsisa te, Ahisahara te an ni.",
			"11": "<sup>11</sup> Jediaela thlah zawng zawngte chu chhungkaw puipa mi chak huaisen, 17,200 an ni.",
			"12": "<sup>12</sup> Ira fapa Supima leh Hupima te, Ahera fapa Husima-te nen.",
			"13": "<sup>13</sup> Naphtalia fapate chu Jaziela te, Gunia te, Jezera te, Saluma te an ni a, Bilhai fate an ni.",
			"14": "<sup>14</sup> Manasea fapate chu, a nupuiin Asriela a hring a, (a hmeinu Aram miin Gileada pa Makira a hring a;",
			"15": "<sup>15</sup> tin, Makira chuan Hupim leh Supim mi nupuiah anei a; tin, a farnu hming chu Maaki a ni:) a dawttu hming chu Zelophehada a ni; tin, Zelophehada chuan fanute a nei a.",
			"16": "<sup>16</sup> Tin, Makira nupui Maaki chuan fapa a hring a, a hmingah Peresa a sa a, tin, a unaupa hming chu Seresa a ni a; a fapate chu Ulama leh Rakema an ni.",
			"17": "<sup>17</sup> Tin, Ulama fapa chu Bedana a ni. Chungte chu Manasea thlah Makira fapa Gileada fapate chu an ni.",
			"18": "<sup>18</sup> Tin, a farnu Hamoleketin Isoda te, Abiezera te, mala te a hring a.",
			"19": "<sup>19</sup> Tin, Semida fapate chu Ahiana te, Sekema te, Likhia te, Aniama te an ni.",
			"20": "<sup>20</sup> Tin, Ephraima fapa chu Suthela a ni a, chu mi fapa chu Bereda a ni a, chu mi fapa chu Tahatha a ni a, chu mi fapa chu Eleada a ni a, chu mi fapa chu Tahatha a ni a,",
			"21": "<sup>21</sup> chu mi fapa chu Zabada a ni a, chu mi fapa chu Suthela a ni a, Ezera leh Eleada, chu mi rama cheng Gath khuaa mite ran rual zuk lak an tum avanga an lo thah takte nen.",
			"22": "<sup>22</sup> An pa Ephraima chuan ni rei tak a sun a, tin, a unaute chu amah hnem turin an va kal a.",
			"23": "<sup>23</sup> Tin, a nupui chu a pawl a, tin, a rai a, fapa a hring a, an in chhung vanduainain a tlakbuak avangin a hmingah chuan Beria a sa a.",
			"24": "<sup>24</sup> Tin, a fanu chu Seeri a ni a, chu mi chuan Beth-horon thlangta zawk leh chhakta zawk leh Uzenseer a siam a.",
			"25": "<sup>25</sup> A fapate chu Repha a ni a, Resepha nen, chu mi fapa chu Tela a ni a, chu mi fapa chu Tahana a ni a,",
			"26": "<sup>26</sup> chu mi fapa chu Ladana a ni a, chu mi fapa chu Amihuda a ni a, chu mi fapa chu Elisama a ni a;",
			"27": "<sup>27</sup> Nuna a ni a, chu mi fapa chu Josua a ni.",
			"28": "<sup>28</sup> Tin, an chanpual leh an chennate chu Bethel leh a khawperte, chhak lamah Naaran, tin, thlanglamah Gezer a khawperte nen; Sekem pawh a khawperte nen, Azar a khawperte thlengin;",
			"29": "<sup>29</sup> Tin, Manasea thlahte ramri lamah chuan Betsian leh a khaw per te, taanak leh a khaw perte, megido leh a khawperte, dor leh a khawperte nen ani. Chungah chuan Israel a fa Josefa thlahte chu an awm ani.",
			"30": "<sup>30</sup> Asera fate chu Imna te, Isfa te, Isvia te, Beria te, an farnu Seraiite an ni.",
			"31": "<sup>31</sup> Tin, Beria fapate chu Hebera leh Berozaitha pa Malkiana an ni.",
			"32": "<sup>32</sup> Tin, Hebera chuan Jafleta te, Somera te, Hothama te, an farnu Suai te an hring a.",
			"33": "<sup>33</sup> Tin, Joleta fapate chu Pasaka te, Bimhala te, Asvatha te an ni.",
			"34": "<sup>34</sup> Tin, Semera fapate chu Ahaia te, Roga te, Jehuba te, Arama te an ni.",
			"35": "<sup>35</sup> Tin, a unaupa Helema fapa te chu Jophate, Imnate, Selesa te, Amala te an ni.7",
			"36": "<sup>36</sup> Tin, Jopha fapate chu Sua te, Harnephera te, Suala te, Beria te, Imra te,",
			"37": "<sup>37</sup> Gezera te, Hoda te, Sama te, Silsa te, Itharana te, Beera te an ni.",
			"38": "<sup>38</sup> Tin, Jethera fapate chu Jephunia te, Pista te, Ara te an ni.",
			"39": "<sup>39</sup> Tin, Ula fapate chu Ara te, Haniela te, Rizia te an ni.Chung zawng zawngte chu Asera thlah, chhungkhaw puipa, mi chak huaisen, hotuber te chu an ni. Tichuan thlahte chhiarnaah ralkaia chhiarte chu 26,000 an ni.",
			"40": "<sup>40</sup> Chung zawng zawngte chu Asera thlah, chhungkhaw puipa, mi chak huaisen, hotuber te chu an ni. Tichuan thlahte chhiarnaah ralkaia chhiarte chu 26,000 an ni."
		},
		"8": {
			"1": "<sup>1</sup> Tin, Benjaminan a fa tirah Bela a hring a, a dawtah Asbela, a dawt lehah Ahara;",
			"2": "<sup>2</sup> a dawt lehah Noh a, tin, a dawt leh pekah Rapha.",
			"3": "<sup>3</sup> Tin, Bela fapate chu Adara te, Gera te, Abihuda te,",
			"4": "<sup>4</sup> Abisua te, Naamana te, Ahova te,",
			"5": "<sup>5</sup> Gera te, Sephuphana te, Hurama te an ni.",
			"6": "<sup>6</sup> Tin, Ehuda fapate chu hengte hi an ni: hengte hi Geba-a chengho chhungkaw puipate an ni a, manahath-ah salah an hruai a:",
			"7": "<sup>7</sup> chungte chu Naamana te, Ahija te, Gera te an ni a, salah a hruai a, tin, ani chuan Uza leh Ahihuda a hring a.",
			"8": "<sup>8</sup> Tin, Saharaima chuan anmahni a kaltir hnuin Moab ramah fate a hring a; a nupuite chu Husimi leh Baari an ni.",
			"9": "<sup>9</sup> Tin, ani chuan a nupui Hodesi lakah Jobaba te, Zibia te, Mesa te, Malkama te,",
			"10": "<sup>10</sup> Jeuza te, Sakia te, Mirma te a nei a. Chungte chu a fapa, chhungkaw puipate chu an ni.",
			"11": "<sup>11</sup> Tin, Husimi lakah Abituba leh Elpaala a hring a.",
			"12": "<sup>12</sup> Tin, Elpaala fapate chu Ebera te, Misama te, Semeda te, (chu mi chuan Ono leh Lod khua leh a khawperte a siam a;)",
			"13": "<sup>13</sup> Gath-a awmho hanwt chhuaktu, Aijalon-a awmho chhungkaw puipa, Beria leh Sema te nen;",
			"14": "<sup>14</sup> Ahiova te, Sasaka te, Jeremotha te;",
			"15": "<sup>15</sup> Zebadia te, Arada te, Edera te;",
			"16": "<sup>16</sup> Beria fapa Mikaela te, Ispa te, Joha te;",
			"17": "<sup>17</sup> Zebadia te, Mesulama te, Hizkia te, Hebera te;",
			"18": "<sup>18</sup> Elpaala fapa Ismeraia te, Islia te, Jobaba te;",
			"19": "<sup>19</sup> jakima te, Zikria te, Zabdia te;",
			"20": "<sup>20</sup> Elienaia te, Zilethaia te, Eliela te;",
			"21": "<sup>21</sup> Simeia fapa Adaia te, Beraia te, Simratha te;",
			"22": "<sup>22</sup> ispana te, Ebera te, Eliela te;",
			"23": "<sup>23</sup> Abdona te, Zikria te, Hanana te;",
			"24": "<sup>24</sup> hanania te, Elama te, Anthothija te;",
			"25": "<sup>25</sup> Sasaka fapa Iphdeia te, Penuela te;",
			"26": "<sup>26</sup> Samseraia te, Seharia te, Athalia te,",
			"27": "<sup>27</sup> Jerohama fapa Jaaresia te, Elija te, Zikria te nen.",
			"28": "<sup>28</sup> Chungte chu anmahni chhuanah chuan chhungkaw puipa, hotute an ni: chungte chu Jerusalem-ah an awm.",
			"29": "<sup>29</sup> Tin, Gibeon-ah chuan Gibeona pa Jeiela a awm a, a nupuia hming chu Maaki a ni;",
			"30": "<sup>30</sup> tin, a fapa upa ber chu Abdona a ni a, tin, Zura te, Kisa te, Baala te, Nadaba te;",
			"31": "<sup>31</sup> Gedora te, Ahiova te, Zekera te an ni.",
			"32": "<sup>32</sup> Tin, Miklothan Simea a hring a. Tin, anni pawh chu Jerusalem-ah an unaute zingah, an unaute depah an awm a.",
			"33": "<sup>33</sup> Tin, Neran Kisa a hring a; tin, Kisan Saula a hring a; tin, Saula'n Jonathana te, Malkisua te, Abinadaba te, Esbaala te a hring a.",
			"34": "<sup>34</sup> Tin, Jonathana fapa chu Meribaala a ni a: tin, Meribaalan Mika a hring a.",
			"35": "<sup>35</sup> Tin, Mika fapate chu Pithona te, Meleka te, Tarea te, Ahaza te an ni.",
			"36": "<sup>36</sup> Tin, Ahazan Jehoada a hring a; tin, Jehoadan Alemetha leh Azmavetha leh Zimria ahring a; tin, Zimrian Moza a hring a;",
			"37": "<sup>37</sup> tin, Mozan Binea a hring a, chu mi fapa chu Rapha a ni a, chu mi fapa chu Eleasa a ni a, chu mi fapa chu Azela a ni;",
			"38": "<sup>38</sup> tin, Azela chuan fapa paruk a nei a, an hming chu hengte hi a ni: Azrikama te, Bokerua te, Ismaela te, Searia te, Obadia te, Hanana te. Chung zawng zawngte chu Azela fapate an ni.",
			"39": "<sup>39</sup> Tin, a unaupa Eseka fapate chu Ulama a fa tir a ni a, a dawttu chu Jeusa a ni a, a dawt lehtu chu Elipheleta a ni.",
			"40": "<sup>40</sup> Tin, Ulama fapate chu mi chak huaisen, thal kap mi an ni a, fapa te, tupate tam tak an nei a, za leh sawmnga an ni.Chung zawng zawng chu Benjamina thlahte an ni."
		},
		"9": {
			"1": "<sup>1</sup> Chutichuan Israel zawng zawngte chu thlahte chhiarnaa chhiar an ni a; tichuan ngai teh, Israel lalte lehkhabuah a chuang a ni; tin, Judate chu an bawhchhiatna avangin babulon-ah sala hruaiin an awm a.",
			"2": "<sup>2</sup> Tin, an chanpual khua luah tantute chu Israel-ho te, puithiam ho te, Levi-ho te, Nethin-hote an ni.",
			"3": "<sup>3</sup> Tin, Jerusalem-ah chuan Juda thlah te, Benjamina thlah te, Ephraima leh Manasea thlahte an awm a;",
			"4": "<sup>4</sup> Uthaia chu Amihuda fapa ani a, chu mi chu Omria fapa a ni a, chu mi chu Imria fapa a ni a, chu mi chuBania fapa a ni a, chungte chu Juda fapa Pereza thlahte an ni.",
			"5": "<sup>5</sup> Tin, Silon-ho thlahte chu Asaia chu an zinga upa ber a ni a, a fate nen.",
			"6": "<sup>6</sup> Tin, Zera thlahte chu Jeuela, a unaute nen, 690 an ni.",
			"7": "<sup>7</sup> Tin, Benjamina thlahte chu, Salua chu Mesulama fapa a ni a, chu mi ch Hodavia fapa a ni a, chu mi chu Haenua fapa a ni a;",
			"8": "<sup>8</sup> tin, Ibneia chu Jerohama fapa a ni a, Ela chu Uzia fapa a ni a, chu mi chu Mikria fapa a ni a, Mesulama chu Sephatia fapa ani a, chu mi chu reuela fapa a ni a, chu mi chu Ibnija fapa ni;",
			"9": "<sup>9</sup> tin, anmahniho chhuanah chuan unau 956 an ni. Chung zawng zawngte chu an hnam zinga an chhungkaw puipate an ni.",
			"10": "<sup>10</sup> Tin, puithiamte chu Jedaia te, Jehoiariba te, Jakina te,",
			"11": "<sup>11</sup> Hilkia fapa Azaria te an ni a; chu mi chu Mesulama fapa a ni a, chu mi chu Meraiotha fapa a ni a, chu mi chu Pathian ina hotu Ahituba fapa a ni a;",
			"12": "<sup>12</sup> Jerohama fapa Adaia te nen, chu mi chu Pasura fapa ani a, chu mi chu Malkija fapa a ni a, Adiela fapa maasaia te nen, chu mi chu Jazera fapa a ni a, chu mi chu Mesulama fapa a ni a, chu mi chu Mesilemitha fapa a ni a, chu mi chu Imera fapa a ni a;",
			"13": "<sup>13</sup> an unaute chu chhungkaw puipa, 1,760 an ni a; Pathian ina rawngbawl hna thawk mi tak an ni.",
			"14": "<sup>14</sup> Tin, Levi-hote chu, Hasuba fapa Semaia a ni a, chu mi chu Azrikama fapa ani a, chu mi chu Hasabia fapa a ni a, chu mite chu Meraria fapate an ni;",
			"15": "<sup>15</sup> tin, bakbakara te, Heresa te, Galala te, Matania te an ni a, chu mi chu Mika fapa ani a, chu mi chu Zikria fapa a ni a, chu mi chu Asapha fapa a ni.",
			"16": "<sup>16</sup> Tin, Semaia fapa Obadia, chu mi chu Galala fapa a ni a, chu mi chu Jeduthuna fapa a ni; tin, Asa fapa Berekia, chu mi chu Netophat-ho khuaa mi Elkana fapa a ni a.",
			"17": "<sup>17</sup> Tin, kawngkhar vengtute chu Saluma te, Akuba te, Talmona te, Ahimana te, a unaute nen an ni, Saluma chu hotu ber a ni;",
			"18": "<sup>18</sup> ani chu tun hmain lal kulh kawngka chhakta vengtu ani thin a: anni chu Levia thlahte riahhmun kawngkhar vengtu an ni.",
			"19": "<sup>19</sup> Tin, Korea fapa chu Saluma a ni a, chu mi chu Ebiasapha fapa ani a, chu mi chu Kora ani ,chungte chu an unau an chhungte Kora-hote nen rawngbawltu, biakbuk kawngkharte vengtu an ni a; tin, an pute chu LALPA riahbuk rawngbawltu, a luhnate vengtu an ni thin;",
			"20": "<sup>20</sup> tun hma chuan Elazara fapa Phinehasa chu an hotu ani a, LALPA chu a hnenah a awm a.",
			"21": "<sup>21</sup> Meselemia fapa Zekaria chu inbiakna puan in kawngkhar vengtu a ni a.",
			"22": "<sup>22</sup> Chung kawngkhar vengtu atana thlan zawng zawngte chu zahnih leh sawm leh pahnih an ni.Davida leh zawlnei Samuelan an thawh tur an lo ruatte chu mahni khua theuha thlahte chhiarnaa ziak an ni.",
			"23": "<sup>23</sup> Chutichuan anmahni leh an fate chuan LALPA in kawngkhar, biakbuk in kawngkharte chu enkawlin, vengtu chan an chang thin a ni.",
			"24": "<sup>24</sup> A kil pali- chhak lampangah te, thlang lampangah te, hmar lampangah te, chhim lampangahte chuan - kawng khar vengtute an awm thin a.",
			"25": "<sup>25</sup> Tin, khuaa mi an unaute chu, ni sarih apiangin hunbi nei tein an hnena awm turin an kal ve thin tur a ni a:",
			"26": "<sup>26</sup> kawngkhar vengtu hotu palite chu Levi hnamte an ni a, Pathian ina sum dahna leh pindan enkawltu an ni si a.",
			"27": "<sup>27</sup> Tichuan enkawl hna chu an hna a nih avang leh, tuk tina kawng hawn chu an hna a nih avangin Pathian in velahte chuan an riak thin a ni.",
			"28": "<sup>28</sup> Thenkhat chuan rawngbawlna bungbel an enkawl a; an dah luh dawn leh lak chhuah dawnin an chhiar zel thin a ni.",
			"29": "<sup>29</sup> Thenkhatte chu bungrua te, hmun thianghlma bungbel zawng zawng te, chhangphut dip tak te, uain te, hriak te, beraw leh rimtuite enkawl tura ruat an ni a.",
			"30": "<sup>30</sup> Tin, puithiam fapa thenkhatte chuan rimtui an siam thin a.",
			"31": "<sup>31</sup> Tin, kora hnam, Saluma fapa upa ber, Levi mi matthithia chu bela thil kan lam ngaihtuahtu a ni.",
			"32": "<sup>32</sup> Tin, Kohath-ho thlahte zinga mi an unau thenkhatte chu chhang dah, chawlhni apianga buatsaiha chhang dahtute an ni.",
			"33": "<sup>33</sup> Chungte chu hla satuho, Levi-ho chhungkaw puipa, bhiak in pindan nghaktu, rawngbawlna danga awlte an ni: chhung leh zana thawk reng an ni a.",
			"34": "<sup>34</sup> Chungte chu anmahni chhuan zawng zawnga htou, Levi-ho chhungkaw puipate an ni a, Jerusalem-a awm an ni.",
			"35": "<sup>35</sup> Tin, Gibeon-ah chuan Gibeon-ho thlahtu Jeiela a awm a, (A nupuia hming chu Maaki a ni)",
			"36": "<sup>36</sup> a fa tir chu Abdona a ni a, Zura te, Kisa te, Baala te, Nera te, Nadaba te;",
			"37": "<sup>37</sup> Gedora te, Ahiova e, Zekaria te, Miklotha te an ni leh a.",
			"38": "<sup>38</sup> Tin, Miklothan Simeama a hring a. Anniho pawh chu Jerusalem-ah an unauzain an unaute depah an awm a.",
			"39": "<sup>39</sup> Tin, Neran Kisa a hring a; tin, Kisan Saula a hring a, tin, Saula'n Jonathana te, Malki-sua te, Abinadaba te, Esbaala te a hring a.",
			"40": "<sup>40</sup> Tin, Jonathana fapa chu Merib-baala a ni a: tin, Merib-baala chuan Mika a hring a.",
			"41": "<sup>41</sup> Tin, Mika fapate chu Pithona te, Meleka te, Tahrea te, Ahaza-te an ni.",
			"42": "<sup>42</sup> Tin, Ahazan Jara a hring a; tin, Jaran Alemetha te, Azmavetha te, Zimria te a hring a; tin, Zimrian Moza a hring a:",
			"43": "<sup>43</sup> tin, Mozan Binea a hring a; chu mi fapa chu Rephaia ani a, chu mi fapa chu Elasa a ni a, chu mi fapa chu Azela a ni:tin, Azela chuan fapa paruk a nei a, anhmingte chu Azrikama te, Bokerua te, Ismaela te, Searia te, Obadia te, Hahana te an ni: chungte chu Azela fapate an ni.",
			"44": "<sup>44</sup> tin, Azela chuan fapa paruk a nei a, anhmingte chu Azrikama te, Bokerua te, Ismaela te, Searia te, Obadia te, Hahana te an ni: chungte chu Azela fapate an ni."
		},
		"10": {
			"1": "<sup>1</sup> Chutichuan Philistia mite chuan Israel hote chu an bei a; tin, Israel mite chu Philistia mite hmaah chuan a tlan chhia a, Gilboa tlangah chuan thahin an awm ta zul mai a.",
			"2": "<sup>2</sup> Tin, Philistia mite chuan Saula leh a fapate chu an um ta ngat ngat a; tin, Philista mite chuan Saula fapa Jonathana te, Abinadaba te, Malki-sua te chu an that a.",
			"3": "<sup>3</sup> Tin, Saula chu a chau ta em em mai a, thal kap mite chuan an umpha a, an hliam ta a.",
			"4": "<sup>4</sup> Chutichuan Saula chuan a ralthuam kengtu hnenah chuan, \"I khandaih kha phawi la, mi han chhun tlang rawh; he'ng serhtan lohte hian mi uankhum dah ang e,\" a ti a. Nimahsela a ralthuam kengtu chuan a ti duh lo a; a hlau em em si a. Chutichuan Saula chuan akhandaih chu a la a, a bawh ta a.",
			"5": "<sup>5</sup> Tin, a ralthuam kengtu chuan Saula a thi tih a hriat chuan, ani pawh chuan akhandaih chu a bawh ve a, athi ve ta a.",
			"6": "<sup>6</sup> chutichuan Saula leh a fapate pathum chu an thi ta a; an pafaza chuan an thi ta a ni.",
			"7": "<sup>7</sup> Tichuan Israel ruama awm mi zawng zawngte chuan an tlanchhia tih leh Saula leh afapate chu an thi ta tih an hriat chuan, an khuate chu an chhuahsan a, an tlan bo ta a; tin, Philistia mite chuan an va luah ta a.",
			"8": "<sup>8</sup> Tin, heti hi a ni a, a tukah chuan Philistia-hoten an ral thahte thuam hlihsak tura an va kal chuan Gilboa tlanga Saula leh a fapate lo thi chu an va hmu a.",
			"9": "<sup>9</sup> Tin, a thuam an hlip a, a lu leh a ralthuam chu an la a, an pathian lemte leh mite hnena chanchin puan turin Philistia ramah chuan an thawn vel a.",
			"10": "<sup>10</sup> Tin, a ralthuam chu an pathiante inah an dah a, a lu chu Dagona inah an tar a.",
			"11": "<sup>11</sup> Tin, Jabes-gilead mi zawng zawngten Philistia mitean Saula an tih dan zawng zawng thu an lo hriat chuan,",
			"12": "<sup>12</sup> mi huaisenho zawng zawngte chu an thawk chhuak a, Saula ruang leh a fapate ruang chu an la a, Jabes khuaah an kalpui a, a ruhte chu Jabes khuaa sasaw hnuaiah an phum a, ni sarih chaw an nghei a.Chutichuan Saula chuan LALPA thu a pawm duh lo a, LALPA chunga a bawhchhiat avang leh LALPA chu rawn loa ramhuai zawl aienthiam aientira a rawn avangin a thi a ni: chuvang chuan , a tihhlum a, ram pawh chu Jesaia fapa Davida chu a pek ta ni.",
			"13": "<sup>13</sup> Chutichuan Saula chuan LALPA thu a pawm duh lo a, LALPA chunga a bawhchhiat avang leh LALPA chu rawn loa ramhuai zawl aienthiam aientira a rawn avangin a thi a ni: chuvang chuan , a tihhlum a, ram pawh chu Jesaia fapa Davida chu a pek ta ni."
		},
		"11": {
			"1": "<sup>1</sup> Chutichuan Israel-ho zawng zawng chu Hebron-a Davida hnenah an kal khawm a, \"Ngai teh, i thisen zawmpui kan ni a.,",
			"2": "<sup>2</sup> Tun hmaa Saula a lal lai khan Israelte hruai chhuak leh hruai luttu i ni thin a; tin, LALPA i Pathianin i hnenah, 'Ka mi Israelte chu i chawm ang a, ka mi Israelte chungah hotuah i awm ang,' a ti chia,\" an ti a.",
			"3": "<sup>3</sup> Chutichuan Israel-ho upa zawng zawng chu Hebron-ah lal hnenah chuan an kal a; tin, Davida'n Hebron-ah chuan LALPA hmaah an hnenah thu a thlung a, tin, LALPA'n Samuela hmanga a sawi ang khan Davida chu Israelte lal atan hriak an thih ta a.",
			"4": "<sup>4</sup> Tin, Davida leh Israel-ho zawng zawng chu Jerusalem-ah an kal a (chu chu Jebus a ni);tin, chutah chuan chu mi rama mi, Jebus-hote chu an awm a.",
			"5": "<sup>5</sup> Tin, Jebus-a awmhote chuan Davida hnenah, \"Hetah hian i lo lut tawp lo ang,\" an ti a. Nimahsela Davida chuan Zion kulh chu a la ta reng reng a: chu chu Davida khua tih a ni ta a.",
			"6": "<sup>6</sup> Tin, Davida'n, \"Tu pawh Jebus-ho han bei hmasa apiang chu sipai hotu berah a awm ang,\" a ti a. Tichuan Zeruia fapa Joaba chu a han chho hmasa a, hotu berah an siam ta a.",
			"7": "<sup>7</sup> Tin, Davida chu kulhah a awm ta a; chuvangin Davida khawpui an ti a ni.",
			"8": "<sup>8</sup> Tin, Milo atangin khawpui vel kulh chu a siam tha a; tin, Joaban khawpui chhung a siam tha a.",
			"9": "<sup>9</sup> Tichuan Davida chu a lo ropui ta deuh deuh a; sipaihote LALPA chu a hnenah a awm avangin.",
			"10": "<sup>10</sup> Hengte hi Davida sipai huaisen, Israel zawng zawngte nena tanga, Israelte chungchang thua LALPA'n a sawi anga lala siam tura a ram dinpuitu huaisen tak takte chu an ni.",
			"11": "<sup>11</sup> Hengte hi sipai huaisen, Davida hnena awmte an ni: Hakmon mi fapa Jasobeama, sawmthum pawl hotu, zathum chungah a fei leka tum khata thattu chu.",
			"12": "<sup>12</sup> Tin, a dawttu chu Aho mi Dodova fapa Eleazara, sipai huaisen pathumte zinga pakhat kha.",
			"13": "<sup>13</sup> Ani chu barli awmna chuk, Philistia mite indo tura infawk khawmna hmun Pas-dammim-ah khan Davida hnenah a awm a; tin, mipuite chu Philistia mite hmaah chuan an tlanchhe ta a.",
			"14": "<sup>14</sup> Tin, anni chu chu mi hmunah chuan an ding a, an lo tang tlat a, Philistia mite chu an that a, tichuan LALPA'n ropui taka ngamsakin a chhandam a.",
			"15": "<sup>15</sup> Tin, sipai sawmthum zinga huaisen pathumte chu lungpuiah Adulam pukan Davida hnenah an chhuk a; Philistia sipaiho chuan Rephaim rumah tanhmun an khuar a.",
			"16": "<sup>16</sup> Chutih lai chuan Davida chu kulhbingah a awm a, tin, Philistia sipaiho chu Bethlehem-ah an awm a.",
			"17": "<sup>17</sup> TiN Davida chuan, \"Beth-lehem-a tuichhunchhuah tui, kulh kawngkhar kianga mi kha in tura mi pe han awm teh se la aw!\" a ti a, a ngai em em a.",
			"18": "<sup>18</sup> Tin, pathumte chuan Philistia sipaiho chu an tawn tlang a, kulh kawngkhar kianga mi Bethlehem-a tui chhuunchhuah tui chu an va chawi a, Davida hnenah an rawn la a; nimahsela Davida chuan a lo in duh lo a, LALPA tan a bun chhuak a,",
			"19": "<sup>19</sup> Hei hi ka in loh turzia mai chu: he'ng thih huama inpehote thisen chu ka in dawn em ni? Thih huama inpein an rawn la ani si a,\" a ti a. Chutichuan a in duh ta lo a. Mi huaisen pathumte chuan chutiang chuan an ti a.",
			"20": "<sup>20</sup> Tin, Joaba unau Abisaia chu pathumte zinga hotu ber a ni a; mi zathumte chungah a fei a lek a, anmahni chu a thah avangin pathumte zingah chuan hming that a hlawh a.",
			"21": "<sup>21</sup> pathumte zingah chuan pahnihte ai chuan amah chu chawimawi tlak a ni a, an hotuah an siam a, pathum hamsate zingah erawh chuan a tel lo.",
			"22": "<sup>22</sup> Tin, Kabzeel mi huai, thil nasa tak titu Jehoiada fapa Benaia chuan Moab mi Ariela fapate pahnih chu a that a; tin, a zuk chhuk a, vur tlak hun laiin khuarkhurumah sakeibaknei a zuk that bawk a.",
			"23": "<sup>23</sup> Tin, Aigupta mi, mi awmkhauh pui, tawng ngaa sang a that a; chu Aigupta mi chuan fei lian pui, paun tahtu themkawl ang hi a keng a. Tin, ani chuan tiang nen chuan a zuk pan a, Aigupta mi fei chu a pawhthlaksak a, ama feiin a chhunhlum ta a.",
			"24": "<sup>24</sup> Jehoiada fapa Benaia chuan chutiang chuan a ti a, sipai huaisen pathumte zingah hming that a hlawh a.",
			"25": "<sup>25</sup> Ngai teh u, mi sawmthumte ai chuan chawimawi tlak a ni a, pathum hmasate zingah erawh chuan a tel lo; tin, Davida chuan a vengtu pawl hotuah a dah a.",
			"26": "<sup>26</sup> Sipaite zinga mi huaisen dangte chu hengte hi an ni. Joaba unau Asahela te, Bethlehem khuaa mi Dodova fapa Elhanana te,",
			"27": "<sup>27</sup> Haror mi Samotha te, Pelon mi Heleza te,",
			"28": "<sup>28</sup> Teko mi Ikesa fapa Ira te, Anathot mi Abiezera te,",
			"29": "<sup>29</sup> Hushat mi Sibekaia te, Aho mi Ilaia te,",
			"30": "<sup>30</sup> Netophat mi Maharaia te, Netophat mi Baana fapa Heleda te,",
			"31": "<sup>31</sup> Benjamina thlah Gibea khuaa mi Ribaia fapa Ithaia te, Pirathon mi Benaia te;",
			"32": "<sup>32</sup> Gass luitea mi Huraia te, Arbat mi Abiela te;",
			"33": "<sup>33</sup> Baharum mi Azmavetha te, Saalbon mi Eliaba te;",
			"34": "<sup>34</sup> Gizon mi Hasema fapa te, Harar mi Sagea fapa Jonathana te;",
			"35": "<sup>35</sup> Harar mi Sakara fapa Ahiama te, Ura fapa Eliphala te;",
			"36": "<sup>36</sup> Mekerat mi Hephera te, Pelon mi Ahija te;",
			"37": "<sup>37</sup> Karmel mi Hezroa te, Ezbaia fapa Naaraia te;",
			"38": "<sup>38</sup> Nathana unaupa Joela te, Hagria fapa Mibaha te;",
			"39": "<sup>39</sup> Amon mi Zeleka te, Zereuia fapa Joaba ralthuam kengtu Berot mi Naharaia te;",
			"40": "<sup>40</sup> Ithra mi Ira te, Ithra mi Gareba te;",
			"41": "<sup>41</sup> Hit mi Uria te, Ahlaia fapa Zabada e;",
			"42": "<sup>42</sup> Reuben m Siza fapa Adina, Reuben mite zinga hotu, a hnena mi sawmthumte nen;",
			"43": "<sup>43</sup> Maaka fapa Hanana te, Mithna mi Josaphata te;",
			"44": "<sup>44</sup> Asterat mi Uzia te, Aroer mi Hothama fapa Sama leh Jeiela te;",
			"45": "<sup>45</sup> Simria fapa Jediaela te, a unaupa Tiz mi Joha te;",
			"46": "<sup>46</sup> Mahav mi Eliela te, Elnaama fapa Jeribaia leh Josavia te, Moab mi Ithma te,Mezoba mi Eliela te, Obeda te, Jaasiela te pawh.",
			"47": "<sup>47</sup> Mezoba mi Eliela te, Obeda te, Jaasiela te pawh."
		},
		"12": {
			"1": "<sup>1</sup> Tichuan Kisa fapa Saula avanga Davida chu Ziklag-a a inkhung tlat laia a hnena lo kalte chu hengte hi an ni: anni chu indona amah puitu mi huaisente zinga mi an ni.",
			"2": "<sup>2</sup> Thalngul keng mi, ding lam leh vei lama virvawma lung vawm leh thlangula thal kah thian an ni a; Benjamin mi Saula chite an ni.",
			"3": "<sup>3</sup> A puipa ber chu Ahiezera a ni a, tin, Gibeat mi Semaa fapa Joasa; tin, Azmavetha fapa Jeziela leh Peleta te; Beraka te, Anathot mi Jehua te;",
			"4": "<sup>4</sup> sawmthum zinga mi huaisen, sawmthum hotu Gibeon mi Ismaia te; Jeremia te, Jahaziela te, Johanana te, Gederat mi Jozabada te,",
			"5": "<sup>5</sup> Eluzaia te, Jerimotha te, Bealia te, Semaria te, Harup mi Sephatia te;",
			"6": "<sup>6</sup> Kjora mi Elkana te, Isia te, Azarela te, Joezera te, Jasobeama te;",
			"7": "<sup>7</sup> Gedor mi Jerohama fapa Joela leh Zebadia te an ni.",
			"8": "<sup>8</sup> Tin, Gad mite zinga mi mi chak huaisen, indo thiam, phaw leh fei hman thiam thenkhatte chu thlalera kulhah Davida hnenah khan an pakai a; an hmemate chu sakeibaknei hmel ang a ni a, tlanga sakhi la anga zuanzang an ni;",
			"9": "<sup>9</sup> a pakhatna chu Ezera a ni a, a dawttu chu Obadia, a dawt lehtu chu Eliaba,",
			"10": "<sup>10</sup> a dawt lehtu chu Mismana, a dawt lehtu chu Jeremia;",
			"11": "<sup>11</sup> a dawt lehtu chu Ataia, dawtlehtu chu Eliela;",
			"12": "<sup>12</sup> a dawt lehtu chu Johanana, a dawt lehtu chu Elzabada;",
			"13": "<sup>13</sup> a dawt lehtu chu Jeremia, a dawt lehtu chu Makbanaia.",
			"14": "<sup>14</sup> Chung Gad mite chu sipai hotute an ni; an zinga chak lo ber pawh chu za hen a ni a, tin, a chak ber chu sangkhat hen a ni.",
			"15": "<sup>15</sup> Chungho chu an ni, thla khatnaa Jrodan luia kuang zawng zawng a liam laia va kal kai a, ruama chengho zawng zawng chhak lam leh thlang lama tlanchhiattirtute kha,",
			"16": "<sup>16</sup> Tin, Benjamin-ho leh Juda-ho zinga mi thenkhat chu kulhah Davida hnenah an kal a.",
			"17": "<sup>17</sup> Tichuan Davida chu anmahni hmuaki turin a kal a, \"Tha lam thua mi pui tura ka hnena lo kal in nih chuan, ka thinlungin a zawm tlat ang che u a; amaherawhchu ka kutin pawi a khawih lo tih hre reng chung sia ka hmelmate mi mantir tuma lo kal in ni chuan, kan pi leh pute Pathian chuan chhinchhiahin, zilhhau che u rawh se,\" a va ti a.",
			"18": "<sup>18</sup> Chutichuan sawmthum chunga hotu Amasaia chungah chuan thlarau a lo thleng a, \"David, i mite kan ni e, Jesaia fapa, i lama tang kan ni e, i chungah thlamuanna awm rawh se, nangmah puitute chungah apwh thlamuanna awm rawh se; i Pathian chuan a tanpui thin si chia,\" a ti a. Tichan Davida chuan anmahni chu a la a, siapi pawl hotuahte a siam ta a.",
			"19": "<sup>19</sup> Tin, Philistian-hoten Saula do tura an thawh lai khan Davida pawh a tel ve a, Manase-ho zinga thenkhatte pawh Davida lamah an lo pakai a; amaherawhchu Philisita hotute a pi ta chuang lo; \"A pu Saula lamah a pakai ang a, kan lu kan hloh dah ang e,\" tiin an inrawn a, an hnarkir leh ta a ni.",
			"20": "<sup>20</sup> Tin, Ziglag khua a thlen chuan Manase-ho zinga sipai sang hotu, Adna te, Jozabada te, Elihua te, Zilethaia te chu a lamah an pakai a.",
			"21": "<sup>21</sup> Davida'n rawlral pawlho abei chu an beihpui ve a; an vai chuan mi chak huaisen, sipai hotu an ni a.",
			"22": "<sup>22</sup> Ni tin, Davida lama tang turin an lo kal an lo kal a, Pathian sipaiho ang maia sipai pawl ropui tak an lo ni ta a.",
			"23": "<sup>23</sup> LALPA thu anga Saula ram Davida luahtir tura Hebron khuaa Davida hnena indona ralthuama inthuam kal zat chu hetiang a ni.",
			"24": "<sup>24</sup> Juda thlah, indona ralthuama inthuam, pawh leh fei keng mi chu 6800 an ni.",
			"25": "<sup>25</sup> Simeona thlah, indonaa mi chak huaisen chu 7,100.",
			"26": "<sup>26</sup> Levia thlah 4,600.",
			"27": "<sup>27</sup> Tin, Jehoiada chu Arona thlah hruaitu a ni a, a hnenah 3,700 an tel ve a;",
			"28": "<sup>28</sup> tin, Zadoka, tlangval chak huaisen, a chhungkaw inga mi, sipai hotu 22 nen.",
			"29": "<sup>29</sup> Tin, Benjamina thlahte zinga mi, Saual chi te, 3,000 tun hma chuan a tam zawkte chu Saula ho lama tang an ni thin a.",
			"30": "<sup>30</sup> Tin, Ephraima thlahte zinga mi, mi chak huaisen, an chhungkaw zinga mi hmingthang 20,800.",
			"31": "<sup>31</sup> Tin, Manse hnam zatvete zinga mi, Davida lala siam tura thlan chauh lo kalte 18,000.",
			"32": "<sup>32</sup> Tin, Isakara thlahte zinga mi, chun glai hn thil chingchang hre mi, Israeltena an thiltih tur tul hre mi, hotu deuh deuh 200; an unau zawng zawng chu an thu hnuaia awm an ni a.",
			"33": "<sup>33</sup> Zebulun-ho zinga mi, ral bei talk thiau, indona hmanraw tinreng nena tan khawh thiam, 50,000; rilru nghet tak pu mi an ni a.",
			"34": "<sup>34</sup> Naphtali-ho zinga mi sipai hotu 1,000, phaw leh fei keng mi 37,000 nen.",
			"35": "<sup>35</sup> Dan-ho zinga mi, ralthuama intuamsa 28,600.",
			"36": "<sup>36</sup> Aser-ho zinga mi , ral bei tlak thiau tan khawh thiam 40,000.",
			"37": "<sup>37</sup> Tin, Jordan rala Reuben-ho zinga mi te, Gad-ho zinga mi te, manase hnam zatve zinga mi te, indona hmanrua tinrengte nena thawk 120,000.",
			"38": "<sup>38</sup> Chung zawng zawng chu indo mi, tan khawh thiam mi an ni a, Davida chu Israel-ho zawng zawng chunga lala siam turin rilru hmun khat puin Hebron-ah an kal a; tin, Israel dang zawng zawngte pawh in Davida chu lala siam turin rilru hmun khat an pu theuh a.",
			"39": "<sup>39</sup> Tin, Davida hnenah chuan ni thum ei leh barin an awm a; an unauten an buatsaihsak vek a ni.Chu chang pawh ni loin, an laichin unau, Isakar ram atang te, Zebulun ram atang te, Naphtali ram atangte lamin sabengtung te, sanghawngsei te, sakawr te, sebawngpatein ei tur lam chhang te, theipui hlawm te, grep rah pho ro bawr te, uain te, hraik te an phurhtir a, bawngpa te, beramte tam tak an rawn keng bawk a; Israel ram chu lawmnain a khat a.",
			"40": "<sup>40</sup> Chu chang pawh ni loin, an laichin unau, Isakar ram atang te, Zebulun ram atang te, Naphtali ram atangte lamin sabengtung te, sanghawngsei te, sakawr te, sebawngpatein ei tur lam chhang te, theipui hlawm te, grep rah pho ro bawr te, uain te, hraik te an phurhtir a, bawngpa te, beramte tam tak an rawn keng bawk a; Israel ram chu lawmnain a khat a."
		},
		"13": {
			"1": "<sup>1</sup> Tin, Davida chuan htou zawng zawng, sang hotute leh za hotute chu a rawn hlawm a.",
			"2": "<sup>2</sup> Tin, Davida'n Israel pung khawmho zawng zawng hnenah chuan, \"Tha in tihzawng a nih a, LALPA kan Pathian remtihzawng ani bawk chuan, Israel ram zawng zawng akan unau la awmate chu, dai vel nei khuaa awm puithiamte leh Levi-hote nen kan hnena rawn inkhawm ve tur hian i sawm vel ang u hmiang.",
			"3": "<sup>3</sup> Tin, kan Pathian bawm hi i la leh teh ang u Sauala lal lai chan kan ngaihven hau lo mai a,\" a ti a.",
			"4": "<sup>4</sup> Tichuan pungkhawmho zawng zawng chuan, chutianga tih chu an duh a, mi zawng zawng ngaiha thil dik a ni reng a.",
			"5": "<sup>5</sup> Chutichan Davida'n Aigupta lui Sihor atanga Hamath khaw luhna thlenga Israel mi zawng zawngte chu la turin a khawm vek a.",
			"6": "<sup>6</sup> Chutichuan Davida leh mi zawng zawng te chuJudai rama Baala Kiriath-jearim an tihah chuan Cherub-ho chunga thu LALPA hmiong chuanna thuthlung bawm chu la turin an han chho a.",
			"7": "<sup>7</sup> Tin, Pathian bawm chu Abinadaba in ata tawlailir tharin an kalpui a, Uza leh Ahiovan tawlailir chu an khal a.",
			"8": "<sup>8</sup> Tin, Davida leh Israel mi zawng zawngte chuan hal sain, tingtang te, perhkhuang te, khuang te, darbenthek te, tawtawrawt te nen, pathian hmaah an lam bung bung a.",
			"9": "<sup>9</sup> Tin, Kidron hruih an han thlen chuan, Uzan bawm chu chelh tumin a han ban a, bawngte chu an pai an pai avangin.",
			"10": "<sup>10</sup> Tin, LALPA thin chu Uza chngah a thawk ta thut mai a, bawm a chelh avang chuan a tihlum ta a; chutah chuan Pathian hmaah a thi ta a.",
			"11": "<sup>11</sup> Tin, LALPA chuan Uza chunga a kut a thlak avangin, Davida chu a thinur a; chu mi hmun chu Perez-uza a vuah a; tun thleng hian a la ni ta reng a.",
			"12": "<sup>12</sup> Tin, Davida chuan chu mi ni chuan Pathian chu a hlau a, Engtin nge Pathian bawm chu ka hawn theih tak ang?\" a tia.",
			"13": "<sup>13</sup> Chutichuan Davida chuan bawm chu Davida khuaah ama hnenah chuan a suan ta lo a, Git mi Obed-edoma inah a da ta zawk a.Tin, Pathian bawm chu Git mi Obed-edoma inah, a chhungte hnenah chuan thla thum a awm a; tichuan Obed-edoma chhungte leh aneih zawng zawngte chu LALPA'n mal a sawm a.",
			"14": "<sup>14</sup> Tin, Pathian bawm chu Git mi Obed-edoma inah, a chhungte hnenah chuan thla thum a awm a; tichuan Obed-edoma chhungte leh aneih zawng zawngte chu LALPA'n mal a sawm a."
		},
		"14": {
			"1": "<sup>1</sup> Tin, tura lal Hiraman Davida hnenah mi a tir a, Sidar thingte a thawn a, lung chhertute, themthiamte a in sa turin a tir bawk a.",
			"2": "<sup>2</sup> Tin, Davida chuan LALPA'n Israel mite chunga lalah amah chu a tinghetin a mi Israelte avangin a ram chu a chawimawi a ni tih a hre ta a.",
			"3": "<sup>3</sup> Tin, Davida chuan Jerusalem-ah nupuite a nei belhchhah leh a, tin, Davida chuan fanu leh fapate a nei leh a.",
			"4": "<sup>4</sup> Jerusalem-a a fa neihte chu hengte hi an ni: Samua te, Sobaba te, Nathana te, Solomona te;",
			"5": "<sup>5</sup> Ibhara te, Elisua te, Elpeleta te;",
			"6": "<sup>6</sup> Noga te, Nephega te, Japhia te;",
			"7": "<sup>7</sup> Elisama te, Beeliada te, Elipheletea te.",
			"8": "<sup>8</sup> Tin, Philistia miten Davida chu Israel mi zawng zawngte lal atan hriak an thih tih an hriatin, Philistia mi zawng zawng chu Davida zawng turin an chho ta a; tin, Davida chan a lo hria a, anamahn bei turin a kal ta a.",
			"9": "<sup>9</sup> Tin, Philistia mite chu rephaim ruamah an inzar a.",
			"10": "<sup>10</sup> Tin, Davida chuan, \"Philistia mite bei turin ka han chho dawn em? Ka kuta hian anniho chu i pe dawn em?\" tiin LALPA a rawn a. Tin, LALPA chuan, \"Han chho rawh; anniho chu i kutah ka pe dawn a ni,\" a ti a.",
			"11": "<sup>11</sup> Tichuan baal-perazim-ah an chho a, chuta chuan Davida'n anniho chu a hneh a, \"LALPA'n ka hmelmate chu ka kut hmangin tuikhuah sah ang maiin min tihdarhsak ta,\" a ti a.Chutichuan chu mi hmun hmingah chuan Baal-perazim an vuah a.",
			"12": "<sup>12</sup> Tin, chutah chuan an milemte an kalsan a, tin, Davida'n thu a pe a, an hal ta a.",
			"13": "<sup>13</sup> Tin, Philisita-hote ch ruamah an inzar leh a.",
			"14": "<sup>14</sup> Tin, Davida'n Pathian a rawn leh a; tin, Pathianin a hnenah, \"Um loin kiansan la, thingtheihmukung zawnlamah han bei zawk rawh.",
			"15": "<sup>15</sup> Tin, ehti hi ani ang a, thingtheihmu lera kal thawm ang ri thup thup i hriat chuan che rawh, Philsitai sipaite bei turin i hmaah Pathian a chhuak ta tihna ani ang,\" a ti a.",
			"16": "<sup>16</sup> Tin, Pathianin thu apek angin Davida chuan a ti a; Philistia sipaite chu Gibeon atanga Gezer thalngin an bei a.Tichuan ram tinah Davida hming chu a lo thang ta a; LALPA'n hnam tin, mah a tihtir ta a.",
			"17": "<sup>17</sup> Tichuan ram tinah Davida hming chu a lo thang ta a; LALPA'n hnam tin, mah a tihtir ta a."
		},
		"15": {
			"1": "<sup>1</sup> Tin, Davida chuan Davida khawpiah chuan inte a sa a; tin, pathian bawm awmna hmun a buatsaih a, puan in a zarsak a.",
			"2": "<sup>2</sup> Tichuan Davida'n, \"Pathian bawm hi Levi-ho chauh lo chuan tuma'n an zawn tur ani lo e; pathian bawm hi kumkhuaa zawntu leh a rawngbawltu atana LALPA thlan an ni e,\" a ti a",
			"3": "<sup>3</sup> Tin, Davida'n LALPA bawm a dahna tur hmun a buatsaihnaa zawn chho turin Jerusalem-ah Israel-ho zawng zawng chu a khawm a.",
			"4": "<sup>4</sup> Tin, Davida'n Arona fate leh Levi-ho a khawm a;",
			"5": "<sup>5</sup> Kohatha thlahte zinga mi, puipa ber Uriela, a unaute nen za leh sawm hnih;",
			"6": "<sup>6</sup> Meraria thlahte zinga mi, puipa ber Asaia, a unaute nen zahnih leh sawmhnih;",
			"7": "<sup>7</sup> Gersoma thlahte zinga mi, puipa ber Joela, a unaute nen za leh sawmthum;",
			"8": "<sup>8</sup> Elizaphana thlahte zinga mi, puipa ber Semaia, a unaute nen zahnih,",
			"9": "<sup>9</sup> Hebrona thlahte zinga mi, puipa ber Eliela, a unaute nen sawmriat;",
			"10": "<sup>10</sup> Uziela thlahte zinga mi, puipa ber Aminadaba, a unaute nen za leh sawm leh pahnih.",
			"11": "<sup>11</sup> Tin, Davida'n puithiam Zadoka leh Abiathara leh Levi-ho, Uriela te, Asaia te, Joela te, Semaia te, Eliela te, Aminadaba te a ko a,",
			"12": "<sup>12</sup> an hnenah, \"Nangni hi Levi-ho puipa berte in ni a; Israelte Pathian , LALPA bawm chu a hmun ka buatsaiha in zawn choh theihna'n nangni hi in unaute nen intithianghlim rawh u.",
			"13": "<sup>13</sup> Atirah pawh kahn nangnin in zawn loh avangin LALPA kan Pathianc ukan chungah a tuan anih kha, thupek angin kan lo ti ta lo va ni,\" a ti a.",
			"14": "<sup>14</sup> Chutichan Israelte Pathian LALPA bawm chu zawn chho tur chuan pithaimte leh Levi-hote chu an intithianghlim a.",
			"15": "<sup>15</sup> Tin, LALPA thupek Mosia'n a sawi ang khan Levia thalhte chuan bawm chu a zawnfung chuan an zawn ta a.",
			"16": "<sup>16</sup> Tin, Davida'n Levi-ho pipate chu an unaute zinga mi hla sa mite, til tum rik mi perhkhuang te, tingtang te, darbenthekte nena ring taka leh hlim taka hla sa turin a ruattir a.",
			"17": "<sup>17</sup> Chutichuan Levi-ho chuan Joela fapa Hemana te, a unaute zinga mi Berekia fapa Asapha te, an unau Meraria fapate zinga mi Kusaia fapa Ethana tean ruat a.",
			"18": "<sup>18</sup> Tin, an unaute zinga mi Zekaria te, Bena te, Jaazeiela te, Semiramotha te, Jehiela te, Unia te, Eliaba te, Benaia te, Maaseia te; Matithia te, Eliphelehua te, Mikneia te, kawngkhar vengtu Obed-edoma leh Jeiela te nen an ruat bawk a.",
			"19": "<sup>19</sup> Chutichuan hla satuho Hemana te, Asapha te, Ethana te, chu darbenthek ring taka beng tura ruat an ni a;",
			"20": "<sup>20</sup> tin, Zekaria te, Aziela te, Semiramotha te, Jehiela te, Unia te, Eliaba te, Maaseia te, Benaia te chu perhkhuangte nena Alamoth sa tura ruat an ni a;",
			"21": "<sup>21</sup> tin, Matithia te, Eliphelehua te, Mikenia te, Obed-edemoan te Jeiela te, Azazia te chu tingtangte nena Seminit saa hma hruai tura ruat an ni.",
			"22": "<sup>22</sup> Tin, Levi-hi puipa Kenania chu hla sa hotu a ni a, a thiam avangin hla hruaitu ber a ni.",
			"23": "<sup>23</sup> Tin, Berekia leh Elkana chu bawm veng veltu an ni.",
			"24": "<sup>24</sup> Tin, puithiamho Sebania te, Josaphata te, Nethanela te, Amasaia te, Zekaria te, Benaia te, Eliezera ten Pathian bawm hmaah chuan tawtawrawt an tum a; tin, Obed-edoma leh Jehia chu bawm veng veltu an ni bawk a.",
			"25": "<sup>25</sup> Chutichuan Davida leh Israel upaho leh sipai sang otute Obed-edoma ina mi LALPA thuthlunan bawm chu zuk la chho turin hlim takin an kal ta a;",
			"26": "<sup>26</sup> tin, heti hi a ni a, LALPA thuthlunna bawm zawntu Levi-hote chu Pathianin a tanpi a, sebawngpa pasarih leh berampa pasarihin an inthawi a.",
			"27": "<sup>27</sup> Tin, Davida leh bawm zawntu Levi-ho zawng zawng leh, hla satute leh hla sa hotu Kenania te chu puan zai sinin an inthuam a, Davida chuanpuan zai sin puithiam kawr a ha bawk a.",
			"28": "<sup>28</sup> Chutichuan Israel mi zawng zawngte chuan au dur dur chung leh tawtawrawt i nei leh tawtawrawt ham ruai ruai leh darbenthek te, perhkhuang te, tingtangte nena ri bung bungin LALPA thuthluna bawm chu an la chho ta a.Tin, heti hi a ni a, LALPA thuthlunna bawm Davida khuaa a lu lai chuan, Saual fanu Mikali chuan tukverh atangin a lo thlir a, lal Davida lama zuang chua han hmu a, a hmusit rilru ta a.",
			"29": "<sup>29</sup> Tin, heti hi a ni a, LALPA thuthlunna bawm Davida khuaa a lu lai chuan, Saual fanu Mikali chuan tukverh atangin a lo thlir a, lal Davida lama zuang chua han hmu a, a hmusit rilru ta a."
		},
		"16": {
			"1": "<sup>1</sup> Tin, Pathian bawm chu an la lut a, a dahna tura Davida'n a zar, puanin chungah chuan an dah ta a; tin, halral thilhlante leh remna thilhlante Pathian hmaah chan an hlan a.",
			"2": "<sup>2</sup> Tin, Davida'n halral thilhlan leh remna thilhlante a hlan zawh chuan, LALPA hmingin mipite chu mal as awm a.",
			"3": "<sup>3</sup> Tin, Israel mi zawng zawngte chu ahmei apain chhang hlawm khat leh sa hrang khat leh grep rah pho ro bawr khat a pe theuh a.",
			"4": "<sup>4</sup> Tin, lalpabawm hmaa rawngbawl tur leh LALPA, Israelte Pathian chu hre renga amah fak tur leh a hnena lawmthu sawi tur Levi-ho thenkhatte a ruat a;",
			"5": "<sup>5</sup> a hotu bera chuan Asapha, tin, a dawttu perhkhuang leh tingtang tum mi Zekaria te, Jeiela te, Semiramotha te, Jehiela te, Matithia te, Eliaba te, Benaia te, Obed-edoma te, Jeiela te nen; Asapha chuan darbenthek ringakin a beng a;",
			"6": "<sup>6</sup> tin, puithiam Benaia leh Jahaziela chuan Pathian thuthlunna bawm hmaah tawtawrawtte an ham reng a.",
			"7": "<sup>7</sup> Tichuan chu mi ni chuan Davida'n LALPA hnena lawm thu hril turin Asapha leh a unaute chu a hmang tan ta a.",
			"8": "<sup>8</sup> Aw, LALPA nenah lawm thu hril u la, A hming lam rawh u; Hnamte zingah chuan a thiltihte chu hriattir rawh u.",
			"9": "<sup>9</sup> Amah chawimawiin hla sa u la, amah fakin zai rawh u; A thilmak tih zawng zawng thu chu chui rawh u.",
			"10": "<sup>10</sup> A hming thianghlim chu chhuang rawh u; LALPA zawngtute thinlung chu hlim rawh se.",
			"11": "<sup>11</sup> LALPA leh a chakna chu zawng u la; A hmel chu zawng fo rawh u.",
			"12": "<sup>12</sup> A thil ropui tak tihte hre reng u la; A thiltihte leh a kaa a rorelte chu;",
			"13": "<sup>13</sup> Aw, a chhiahhlawh Israela thlahho, Jakoba thlah, a thlanate u.",
			"14": "<sup>14</sup> Ani chu LALPA kan Pathianc hu a ni a; A thupekte chu khawvel pum huap a ni.",
			"15": "<sup>15</sup> A thuthlun chu kumkhuain hre reng u la, Chhuan sang thlenga a thu lo pek,",
			"16": "<sup>16</sup> Abramhama hnena a thuthlun leh, Isaaka hnena a chhechham chu;",
			"17": "<sup>17</sup> Chu vek chu Jakoba hnenah thuruatah, Israela hnena kumkhaw thuthlung atan a tinghet a,",
			"18": "<sup>18</sup> \"Kanaan ram hi ka pe ang che, I ro chanpual tur ram chu,\" tiin;",
			"19": "<sup>19</sup> Mi tlemte chauh in nih a, A ni, tlem te, khuazin ang mai in nih lai kha;",
			"20": "<sup>20</sup> Hnam hrang hrang hnenah an kal lawr a, Ram hrang hrang atanginmi hrang hrangte hnenah.",
			"21": "<sup>21</sup> Tuma hnehchiah a phal lo a; A ni, anmahni avangin lalte pawh a zilhhau val",
			"22": "<sup>22</sup> \"Ka hriakthihate chu tawk suh u la, ka zawlneite pawi reng reng khawih suh u,\" tiin.",
			"23": "<sup>23</sup> Leilung luah zawng zawngte u, LALPA chawimawiin hla sa u la, Ni tin, a chhandamna thu chu hriatir rawh u.",
			"24": "<sup>24</sup> Hnam tin, zingah a ropuizia hril u la, Mi tinrengte zingah a thilmak tihte chu hril rawh u.",
			"25": "<sup>25</sup> LALPA chu a ropui a, nasa taka fak tur a ni si a. Pathian zawng zawng aia tih tur a ni.",
			"26": "<sup>26</sup> Hnamte pathian chu milem a ni si a. LALPA erawh chuan vante khi a siam a ni.",
			"27": "<sup>27</sup> Chawimawina leh ropuina a hmaah a awm a; Chakna leh mawina a hmun thianghlima a awm.",
			"28": "<sup>28</sup> Mi tina chi tinrengte u, LALPA chu bel u la, LALPA chu ropina leh chakna bel rawh u.",
			"29": "<sup>29</sup> LALPA chu a hming ropuina bel u la, Thlhlan tur keng u la, a hmaah lo kal rawh u; Thianghlim mawina inbelin LALPA chu chibai buk rawh u.",
			"30": "<sup>30</sup> Leilung zawng zawngte u, a hmaah khur rawh u; Khawvel hi a nghin thieh lohna turin tihnghehin a awm a.",
			"31": "<sup>31</sup> Vante khi lawm takin awm se la, lei pawh hi hlim takin awm rawh se; Hnam tin, zingah chuan, \"LALPA chuan ro a rel,\" ti rawh se.",
			"32": "<sup>32</sup> Tuifinriatleh a chhunga thil awm zawng zawng hi ri hum hum se la; Lo leh a chhunga awm zawng zawng hi lawm takin awm rawh se;",
			"33": "<sup>33</sup> Chutih hunah chuan ramhnuaia thing zawng zawng hi LALPA hmaah chuan hlimin an zai ang,Lei ro rel turin a lo kal dawn si a.",
			"34": "<sup>34</sup> Aw, LALPA hnenah lawm thu hril rawh u, ani chu a tha si a; A ngilneihna pawh kumkhuain a awm dawn si a,",
			"35": "<sup>35</sup> \"Aw kan chandamna Pathian, min chhandam la, I hming thianglim fak tur leh, I fakawmna chhuang turin, Hnam tin, zing ata min hruai khawm la, min chhanhim ang che,\" ti rawh u.",
			"36": "<sup>36</sup> Israelte Pathian LALPA chu fakin awm rawh se, Chatuan ata chatuanthleng pawhin. Tin, mipi zawng zawng chuan, \"Amen,\" an ti a. LALPA chu an fak a.",
			"37": "<sup>37</sup> Chutichuan chuta LALPA thuthlunna bawm hmaa ni tin, a tul dan ang anga rawngbawl turin Asapha leha unaute a dah ta a;",
			"38": "<sup>38</sup> Obed-edoma leh Hosa, an unau sawmruk leh pariatten an pui tur a ni; Jeduthuna fapa Obed-edoma leh Hosa te chu kawngkhar vengtu tur an ni;",
			"39": "<sup>39</sup> LALPA'n Israel-hote hu a pek, LALPA dana ziak zawng anwg anga halral thilhlan maicham chunga tuk leh zana Gibeon hmun sanga LALPA biak buk hmaa halral thilhlante hlan reng tur atan erawh chuan puithiam Zadoka leh",
			"40": "<sup>40</sup> a unau puithiamhote ruat an ni a;",
			"41": "<sup>41</sup> an zinga tel a, a khawngaihna chu kumkhua atan a i avangin, LALPA hnena lawm thu sawi tur Hemana leh Jeduthuna leh mi dang an hming lama thlan chhuahte chu ruat an ni a.",
			"42": "<sup>42</sup> Hemana leh Jeduthuna te chu tawtawrawt te, darbenthek te leh thil tum chi dangte ruala tum tur kawltu an ni a. Jeduthuan leh a fate chu kawngkhar vengtu tur an ni.Tichuan mipuiz awng zawng chu mahni in lam theuvah an haw ta a; tin, Davida pawh chu a chhungte malsawm turin a haw ve ta a.",
			"43": "<sup>43</sup> Tichuan mipuiz awng zawng chu mahni in lam theuvah an haw ta a; tin, Davida pawh chu a chhungte malsawm turin a haw ve ta a."
		},
		"17": {
			"1": "<sup>1</sup> Tin, heti hi a ni a, Davida chu a ina a awm laiin zawlnei Nathana hnenah Davida chuan, \"Hei, kei chu sidar thing inah ka awm a, nimahsela LALPA thuthlunna bawm chu puan zar hnuai maiah a awm a,\" a ti a.",
			"2": "<sup>2</sup> Tin, Nathana chuan Davida hnenah, \"I rulrua i vei ang tak chuan ti ta che: Pathian i hnenah a awm si a;\" a ti a.",
			"3": "<sup>3</sup> Tin, chu mi zan vek chuan heti hi a ni a, Nathana hnenah Pathian thu a lo thleng a,",
			"4": "<sup>4</sup> \"Ka chiahhlawh Davida chu va hrilh rawh, 'LALPA chuan heti hian a ti, Ka awmna turin nangin in mi saksak tur ani lo;",
			"5": "<sup>5</sup> Israela thlahte Aivupta rama ata ka hruai chua atangin tun thlengininah ka awm lo a; puanin leh biakbukah ka awm zel zawk thin a.",
			"6": "<sup>6</sup> Israel-ho zawng zawngte nena kan kalna reng rengah pawh ka mite chawm tura thu ka pek Israel-ho roreltute hnenah, Engati nge sidar thingin in mi sak loh: Ka ti ngai em ni?'",
			"7": "<sup>7</sup> Chuvangin ka chhiahhlawh Davida hnenah heti hian sawi rawh, 'Sipaiote LALPA chuan heti hian a ti a ni, beram tlatna hmun ata i beram veng lai chu kami Israelte chunga lal turin ka la chia;",
			"8": "<sup>8</sup> tin, i kalna apiangah i hnenah ka awm zel a, i hmelmate ka tihbosak zel chia; leia mi ropui takte angin hmin gropi tak ka siamsak dawn che a ni.",
			"9": "<sup>9</sup> ,10 Tin, ka mi Israelte tan hmun ka ruat ang a, insuan tawh loa anmahnipual hmuna an awm reng theihna'n anni chu ka phun ang a; tun hmaa ka mi Israelte chunga roreltute ka ruat laia suahsuallna faten an ti thin ang khan an tirethei tawh hek lo ang a; i hmelma zawng zawngte pawh ka titlawm bawk ang. Tin chu lo pawh chu lalpa chuan chhungkua a dinsak ang che ith ka hrilh che a ni.",
			"10": "<sup>10</sup> ,9 Tin, ka mi Israelte tan hmun ka ruat ang a, insuan tawh loa anmahnipual hmuna an awm reng theihna'n anni chu ka phun ang a; tun hmaa ka mi Israelte chunga roreltute ka ruat laia suahsuallna faten an ti thin ang khan an tirethei tawh hek lo ang a; i hmelma zawng zawngte pawh ka titlawm bawk ang. Tin chu lo pawh chu lalpa chuan chhungkua a dinsak ang che ith ka hrilh che a ni.",
			"11": "<sup>11</sup> Tin, i ni a kin a, i pi leh pute hnena i kal hunah chuan, heti hi ani ang a, i hnua i thlah, i fate zinga mi chu ka tungding ang a; tin, a ram chu ka tinghet ang.",
			"12": "<sup>12</sup> Ani chuan in mi sak sak ang a; tin, a lalthutthleng hchu kumkhuainka tinghet ang.",
			"13": "<sup>13</sup> Tichuan a pa ka ni ang a, ani chu ka fapa ani ang a; ka zahngaihna i hmaa mi ka lak bosak ang khan, ani zawng ka lak bosak lo ang a;",
			"14": "<sup>14</sup> ka inah leh ka ramah ani chu kumkhuain ka din nghet ang a; tin, a lalthutthleng hchu kumkhuaa tihngheh a ni ang,'\" tiin.",
			"15": "<sup>15</sup> Chung thu zawng zawng lehchu inlarna ang chiahin Nathana chuan Davida hnenah a sawi a.",
			"16": "<sup>16</sup> Chutichuan lal Davida chu a lut a, LALPA hmaa chuan a thu a; \"Aw, LALPA Pathian, tu nge maw ka nih a, ka chhungte pawh tu nge maw an nih a, heti chen chen mi hruai?\"",
			"17": "<sup>17</sup> Aw Pathian, chung pawh chu nanga mithmuhah chuan thil te tham zawng ani a nimahsela aw LALPA Pathian, ichhiahhlawh chhungkaw lo awm dan tur thu chu kum tam tak chhung atan i lo sawi tawh a, mi lian angah mi ngai bawk a, aw LALPA Pathian.",
			"18": "<sup>18</sup> I chiahhlawh chawimawia awm chungchang thuah hian Davida'n i hnenah eng nge a sawi leh cheu vang? I hnenah eng a sawi leh cheu vang? I chiahhlawh hi i hre chiang hle si a.",
			"19": "<sup>19</sup> Aw LALPA, i chhiahhlawh avangin nangma rilrua chuak ang zelin he'ng thil roput ak hi hriattir turin thil ropui tak takte i ti ta a.",
			"20": "<sup>20</sup> Aw LALPA kan hriatah chuan nangmah ang reng reng an awm lo a, nangmah lo chu pathian reng reng an awm hek lo.",
			"21": "<sup>21</sup> Aigupta ram ata i tlan chauha i mite hmaa hnamdange titlawm a, thil ropui tak leh hlauhawm ak tia hming ropui ak siamsak tur chea Pathianin ama mite atana a rawn tlan chhuah i mi Israel ang reng reng hi khawvela hnam dang tu nge awm?",
			"22": "<sup>22</sup> I mi Israelte chu kumkhuaa i mite atan i lo siam ta si a; nang LALPA chu an Pathian i lo ni ta a.",
			"23": "<sup>23</sup> Tichuan aw LALPA, i chhiahhlawh chunghang htu leh a chhungte chungchang thu i sawi chu kumkhua atan tinghet la, i sawi tawh ang khan ti ang che.",
			"24": "<sup>24</sup> Tin, 'Sipaihote Pathian chu Israelte Pathian, Israelte tan ngeia Pathian a ni, tiin i hming chu kumkhuaa tihngheha, chawimawiin awm se la; i chhiahhlawh Davida chhungte chu i hmaa tihnghehin awm rawh se.",
			"25": "<sup>25</sup> Aw nang ka Pathian, i chhiahhlawh hnenah chhungkua i dinsak ang tih i lo puang tawh si a; chuvangin i chhiahhlawh hian i hnenah ka la tawngtai ngam ta a ni.",
			"26": "<sup>26</sup> Aw LALPA, nang hchu Pathian i ni a, he thil tha hi i chhiahhlawh chu i lo tiam tawh a,i chhiahhlawh chhungte chu i hmaa kumkhuaa an awm reng theihna'n mal i lo sawm ta a; aw LALPA, nangin mal i sawm chu kumkhaw malsawmna a ni,\" a ti a.",
			"27": "<sup>27</sup> i chhiahhlawh chhungte chu i hmaa kumkhuaa an awm reng theihna'n mal i lo sawm ta a; aw LALPA, nangin mal i sawm chu kumkhaw malsawmna a ni,\" a ti a."
		},
		"18": {
			"1": "<sup>1</sup> Tin, chu mi hnua chuan heti hi a ni a, Davida'n Philistia mite chu abei a, a hneh a, Gath khua leh a khawperte chu Philistia mite a chuhsak a.",
			"2": "<sup>2</sup> Tin, Moa-ho abei leh a. Moab mite ch Davida chiah khua an lo ni ta a. chhiahte an pe thin a.",
			"3": "<sup>3</sup> Tin, Davida chuan Euphrates luia a ram din nghet tura a kal pahin Zoba lal Hadarezera pawh Hamat bulah a bei leh a.",
			"4": "<sup>4</sup> Tichuan Davida chuan tawlailir sangkhat leh sakawr chun ghchuang mi sangsarih leh sipai singhnih a mansak a; tin, Davida chuan tawlailir hnuk mi sakawr zawng zawng chu a tha a chah sak a, tawlailir za hnuk tur erawh chu a zuah a.",
			"5": "<sup>5</sup> Tin, Damaska-a Suria mite Zoba lal Hadarezera chan turin an lo kal a, tin, Davida'n Suria mi singhni leh sangnih a that a;",
			"6": "<sup>6</sup> chutichuan Davida chuan Damaska-a Suria mite zingah chuan sipai a dah a; tichuan Suria mite chu Davida chiah khua an lo ni a, chhiahte an pe thin a. Tin, LALPA chan Davida chu a kalna apiangah a hnehtir zel a.",
			"7": "<sup>7</sup> Tin, Davida chuan Hadarezera-hote rangkachak phawte a laksak a, Jerusalemah a hawn a.",
			"8": "<sup>8</sup> Tin, Davida chuan Hadarezera khua Tibhat leh Kun-a mi Davida chuan dar tam tak a hawn a, chung chuan Solomona dar tuizem, a hunna leh a suahna dar note a siam a.",
			"9": "<sup>9</sup> Tin, Hamat lal Toua chuan Zoba lal Hadarezera sipai zawng zawng Davida'n a hneh ti alo hria a,",
			"10": "<sup>10</sup> Hadarezerea chu a run a, a thah avanga chibai buk tur leh lawmpui turain a fapa Hadoramaa chu Davida hnenah a tir a; Hadarezera leh Toua hi lo indo thin an ni si a; rangkachak leh tangkarua leh dar bungbel tinreng a kensak bawk a.",
			"11": "<sup>11</sup> Chungte pawh chu ram dang a ngam zawng zawnga mi tangkarua leh rangkachak alakte nen chuan Davida'n LALPA hnenah ahlan a; Edomho laka mi te, Moab-ho laka mi te, Amona thlahte laka mi te, Philistia-ho laka mi te, Ameklek-ho laka mi te,",
			"12": "<sup>12</sup> Chu lo pawh, Zeruii fapa Abisaian Edom-ho singkhat leh sangriat Chi ruamah a that a.",
			"13": "<sup>13</sup> Tin, Edom rama chuan sipaite a dah a; tichuan Edom mi zawng zawng chu Davida chhiah khua an lo ni ta a. Tin, LALPA'n Davida chu akalna pianga ahnehtir zel a.",
			"14": "<sup>14</sup> Chutichuan Davida chuan Israel mi zawng zawng chungah ro a rel a; a mi zawng zawng chungah chuan fel takin ro a rel a.Tin, Zeruii fapa Joaba chu sipai hotu ber a ni a; tin, Ahiluda fapa Jehosaphata chu chanchin ziaktu a ni. Tin, Ahitua fapa Zadoka leh Abiathara fapa Abimeleka chu puithiam an ni a, tin, Savsa chu ziaktu a ni a; tin, Jehoiada fapa Benaia chu Keret mi leh Pelet mite hotu ani a, tin Davida fapa te chu lal thu zaisatu ber an ni.",
			"15": "<sup>15</sup> Tin, Zeruii fapa Joaba chu sipai hotu ber a ni a; tin, Ahiluda fapa Jehosaphata chu chanchin ziaktu a ni. Tin, Ahitua fapa Zadoka leh Abiathara fapa Abimeleka chu puithiam an ni a, tin, Savsa chu ziaktu a ni a; tin, Jehoiada fapa Benaia chu Keret mi leh Pelet mite hotu ani a, tin Davida fapa te chu lal thu zaisatu ber an ni."
		},
		"19": {
			"1": "<sup>1</sup> Tin, chung hnua chuan heti hi a ni a, Amona thlahte lal Nahasa chu a thi a, a fapa chu a aiawhin a lal a.",
			"2": "<sup>2</sup> Tin, Davida chuan, \"A pa chuan mi lo khawngaih thinavangin kei pawhin Nahasa fapa Hanuna chu ka khawngaih ve ang,\" a ti a. Chutichan Davida chuan a pa chungchang thua thlamuan turin mite a tir a. Tin, Davida mite chu Amona thlahte ramah Hanuna hnen chu amah thlamuan turin an va thleng a.",
			"3": "<sup>3</sup> Nimahsela Amona thlah puipahote chuan Hanuna hnenah, \"Davida hian i pa a zah avangin thlamuantu tur i hnenah a rawn tir niin i ring em ni? A mite hi ram tichhe tura thlithlai leh enthla tura i hnena lo kal an ni lawng maw?\" an ti a.",
			"4": "<sup>4</sup> Chutichuan Hanuna chuan Davida mite chu a man a, an khabe hmul a meh kawnsak a, tin, an silhfente chu a lai takah an mawngtam khuh pha chauin a tansak a, a hawntir ta a.",
			"5": "<sup>5</sup> Tichuan tuten emaw a mite a lo tih dan chu Davida an va hrilh a. Tin, a hmuak turin mi a ti ra; anni lah chu n zak hle mai si a. Tin, lal chuan, \"In khabe hmul a hawr sei hma chu Jeriko-ah awm rih u la, chu mi hnuah chuan in lo haw ani ang chu,\" a ti a.",
			"6": "<sup>6</sup> Tin, Amona thlahten Davida huatzawng an lo ni ta tih an lo inhriat chuan, Hanuna leh Amona thlahte chuan Mesopotamia rama mi te, tawlailir leh sakawr chung chuang mite rawihna'n tangkaraw talent sangkhat an thawn a.",
			"7": "<sup>7</sup> Tin, Benjamina thlahte chuan chutiang chuan an ti a, lamho zinga mi anmahni zat theuh nupuiah an man a, an kal bopui ta a: tin, an ram lamah chuan an hawng a, khawpui chu an chei leh a, chutah chuan an awm ta a.",
			"8": "<sup>8</sup> Tin, Davida'n chu Chu a hriatin, Joaba leh sipaihuaisenho zawng zawngte chu a tir chhuak a.",
			"9": "<sup>9</sup> Tin, Amona thlahte chu an lo thawk a, khawpui kawngkhar bulah an inzar a,tin, lalho lo tel vete chu ahrangin phulah an awm a.",
			"10": "<sup>10</sup> Chutichan ahma lam leh hnung lamah an rawn bei dawn tih Joaban ahriatin, Israel mite zinga mi atha tha a thalng chhuak a, Suria mite bei turin an lo inzar a.",
			"11": "<sup>11</sup> Tin, mi dang zawng zawng chu a unaupa Abisaia kutah a dah vek a, Amona thlahte bei turin a iznar bawk a.",
			"12": "<sup>12</sup> Tin, ani chua, \"Suria mite hi ka chelh dawn loh chuan, nangin mi pi ang a; tin, Amona thlahte i chelh dawn loh chuan, ka rawn pui thung ang che.",
			"13": "<sup>13</sup> Huai takin awm u la, kan mite leh kan Pathian khuate chhanin i intipachang ang u; tin, LALPA'n tha a tih ang zelin ti rawh se,\" a ti a.",
			"14": "<sup>14</sup> ChutichuanJoaba leh ahnena mite chuan indona hmun alam Suria mite chu an va hnaih a; tin, a hmaah chuan an tlanche ta a.",
			"15": "<sup>15</sup> Tin, Amona thlahten Suria mite an thlanchhia tih an han hriat chuan,anni pawh chu a unaupa Abisaia hmaah chuan an tlanchhe ve ta a, khuaah an lut a. Tichuan Joaba chu Jerusalem-ah a haw ta a.",
			"16": "<sup>16</sup> Tin, Suria miten Israelte hmaa an chan a lo chhe ta tih an lo hriat chuan, mi an tir a, Lui piah lama Suria-hote chu an ko chhuak a, tin, Hadarezera sipai hotu Sophaka chu aotu ber a ni.",
			"17": "<sup>17</sup> Tin, chu chu Davida an hrilh a; tin, ani chuan Israel mi zawng zawng a ko khawm a, Jordan li an kai a, an va hmu a, bei turin an inzar a. Chutichuan Davida hote chu an haninzar zawh chuan Suria-hote chu an han bei ta a.",
			"18": "<sup>18</sup> Tin, Suria mite chu Israelte hmaah chuan an tlanchhe ta a; tin, Davida chuan Suria mi, tawlailir sangsarih chunga chuang leh sipai singli a that a, tin, sipai hotu Sophaka pawh a that a.Tichuan Hadarezera mite chuan Israelte hmaah an chan a che ta tih an lo hriat chuan, Davida nena inremna an siam a, chhiah khuaah an awm ta a; Suria mite pawh chuan Amona thlahte chu an pui leh duh ta lo.",
			"19": "<sup>19</sup> Tichuan Hadarezera mite chuan Israelte hmaah an chan a che ta tih an lo hriat chuan, Davida nena inremna an siam a, chhiah khuaah an awm ta a; Suria mite pawh chuan Amona thlahte chu an pui leh duh ta lo."
		},
		"20": {
			"1": "<sup>1</sup> Tin, heti hi a ni a. kum a lo vei a, lalte indo tura an chua thin lai hun chuan, Joaban sipai run pui a hruai chuak a, Amona thlahte ram chu a chu chhia a, Raba khua an hual a, Davida erawh chu Jerusalema-ah a awm a. Tin, Joaba chuan Raba khua chu a bei a, a la ta a.",
			"2": "<sup>2</sup> Tin, Davida'n an lal lallukhum khum lai chu alaksak a, a rih lam chu rangkachak talent khat a ni a, lunghlute pawh a tel a; tin, Davida an khumtir a; tin, khawpia mi rallak sum tam tak chu a la a.",
			"3": "<sup>3</sup> Tin, chuta mipuite chu a kawi chuak a, thingzaina te, lei lehna thir zum te, hreipuitein hna a tahwhtir a. Chuting chuan Amona thlah khawpui zawng zawngte chu Davida'n a ti a. Tin, Davida leh mi zawng zawng hu Jerusalem-ah an haw leh ta a.",
			"4": "<sup>4</sup> Tin, heti hi a ni a, chu mi hnua chuan Philistia mite nen Gezer-ah indona a awm a; tichuan Husat mi Seibekaian mi lian thlahte zinga mi sipaia a that a: tin, an tlawm ta a.",
			"5": "<sup>5</sup> Tin, Philisita mite nena indona a awm leh a; tin, Jaira fapa Elhanana Git mi Goliatha unaupa Lahmia a that a, a feingul chu lian tak a ni.",
			"6": "<sup>6</sup> Tin, Gath khuaa indona a awm leh a, chutah chuan mi sang tak mai, a kut lehlam lehlama zungtang paruk, a ke lehlam lehlama ke zungtang paruk, a vaia sawmnih leh pali nei hi a awm a; ani pawh mi lian laka piang a ni.",
			"7": "<sup>7</sup> Tin, ani chuan Israel mite a va cho a, Davida unau Simea fapa Jonathan chuan a lo that a.Chungte chu Gath khuaa mi lian laka piang an ni a; chung chu Davida leh amite kutah an thi a .",
			"8": "<sup>8</sup> Chungte chu Gath khuaa mi lian laka piang an ni a; chung chu Davida leh amite kutah an thi a ."
		},
		"21": {
			"1": "<sup>1</sup> Tin, Setanan Israelte chu a bei ta a, Davida chu Israelte chiar duhna a neihtir a.",
			"2": "<sup>2</sup> Tin, Davida chuan Joaba tle an mi liante hnenah, \"Kal u la, Beer-seba atanga Dan thlengin Israel mite chhiar u la, a zat ka hrait theihnan mi rawnhrilh leh ang che u, a ti a.",
			"3": "<sup>3</sup> Tin, Joaban lal hnenah, \"LALPA'n a mite hi a lehza pawhin tipung se la, lalpa ka pu, ka pu mite vek an ni lo'm ni? Ka pu in chu chu engah nge maw hriat a chak le? Enga tan nge Israelte thiam loh chan phahna anih ang le?\" a ti a.",
			"4": "<sup>4</sup> Nimahsela lal thu chuan Joaba chu a hneh ta a. Chutichuan Joaba chu a chhuak a, Israel ram zawng zawngah chuan a kal a, Jerusalem-ah a haw leh a.",
			"5": "<sup>5</sup> Tin, Joaba chuan mirhng awm zat chu Davida a hrilh a. Israel rama khandaih keng mi zawng anwg chu matkaduai khat leh nuai khat an ni a; tin, Judai rama khandaih keng mi chu nuai li leh singsarih an ni.",
			"6": "<sup>6</sup> Levi-ho leh Benjamin-ho eraw chu a chhiar tel velo; lal thu chu Joaba huatzawng tak ani si a.",
			"7": "<sup>7</sup> Tin, chu thila chuan Pathian a lawm ta lo a; chutichuan Israel-ho ch a hrem ta a.",
			"8": "<sup>8</sup> Tin, Davida'n Pathian hnenah, \"He ka thil lo tihah hian nasa takin ka lo sual ta a ni; nimahsela i chhiahhlawh khawlohna hi khawngaih takin ngaidam ang che, atthlak takin ka lo ti ta ni e,\" a ti a.",
			"9": "<sup>9</sup> Tin, LALPA'n Davida zawlnei Gada hnenah,",
			"10": "<sup>10</sup> \"Kal la, Davida hnenah, 'LALPA'n heti hian a ti, Thil kawng thum ka ruatsak a che: i chunga ka tih turin a pakhat chu thlang rawh, a ti e,' ti rawh,\" tiin a sawi a.",
			"11": "<sup>11</sup> Chutichuan Gada chu Davida hnenah a kal a, a hnenah, \"LALPA'n heti hian a ti, 'A eng nge i duh thlang rawh;",
			"12": "<sup>12</sup> kum thum tam tlak nge, i hmelmate khandaiha um phaka awma tla thum i hmelmate tihtlawm nge, ni thum chhung hri, LALPA khandaiah LALPA vantirhkohin Israel ram zawng zawng mi tiboral zel?\" tiin. Chutichuan mi tirtu chu engtin nge ka fachan dawn, inngaihtuah la, mi hrilh rawh khai,\" a ti a.",
			"13": "<sup>13</sup> Tin, Davida huan Fada hnenah, \"Ka mang a ang hle mai a; LALPA kuta tlu lut phawt mai i la; a zahngaihna hi a nasa hle si a; mihring kutah chuan min tluk luhtir lul suh ang che,\" a ti a.",
			"14": "<sup>14</sup> Chutichan LALPA'n Israelte zingah chuan hri alentir ta a; Israel-hote chu singsarih an thi a.",
			"15": "<sup>15</sup> Tin, Jerusalem khua tiche turin pathianin vantirhkoh a tir a; tin, a han tichhiat dawn lai tak chuan, LALPA chuan ahan en a, thil tha lo tih a tum chu a lamlet a, mite tiboraltu vantirhkoh hnenah chuan, \"A tawk tawh e; i kut lek tawh suh,\" a ti a. Tin, lalpa vantirhkoh chu Jebus mi Ornana hruih bulah chuan a ding a.",
			"16": "<sup>16</sup> Tin, Davida chua han dak a, LALPA vantirhkoh, kahndaih pawhi saa Jerusalem lam tina, lei leh van inkara ding hamu a. Chutichuan Davida leh upaho chuan saiip puan sinin leiah an bawkkhup ta a.",
			"17": "<sup>17</sup> Tin, Davida'n Pathian hnenah, \"Keimah hi a ni lawm ni, mipite chiat tur tih thu petu chu?Keimah hi a ni, thil lo tisual a, suahsualthlak taka thil lo ti ta chu: he'ng beramte hian eng thil nge an tih ve? Aw LALPA ka Pathian, keimah leh ka chhungte chungah i kut thlak zawk la, i mite hian hri hi tuar tawh lo se,\" a ti a.",
			"18": "<sup>18</sup> Chutichuan LALPA vantirhkohin Gada hnenah Davida chu han chho a, Jebus mi Ornana hruia LALPA tan amaicham han siam tura Davida hrilh turin thu a pe a.",
			"19": "<sup>19</sup> Tin, Gadan LALPA hminga a sawi ang chuan Davida chu a chho ta a.",
			"20": "<sup>20</sup> Tin, Ornana chua han lehhawi a, vantirhkoh chu a hmu a; tin, a fapa pali a hnena awm vete chu an boru ta a. Ornana chuan buh a lo chil a ni a.",
			"21": "<sup>21</sup> Tin, Davida'n Ornana hnena a va thlen chuan, Ornana chua han hawi a, Davida chu ahmu ta a, tin, hruih ata chu a chuak a, a hmaia lei siin Davida hmaah chuan abawkkhup a.",
			"22": "<sup>22</sup> Tichuan Davida'n Ornana hnenah, \"He hruih hi LALPA tana maicham ka siamna'n mi pe rawh khai; aman kim chiah telin mi pe rawh, mipuite laka hri a kian theihna tur hian,\" a tia .",
			"23": "<sup>23</sup> Tin, Ornanan Davida hennah chuan, \"Nei mai rawh, lalpa ka pu chuan awm a tih ang angin ti rawh se; en teh, halrala hlan atan bawngpa te, thing atan hrui hmanrua te, changphut thilhlan atan buhte ka pe ang che; ka tum vek ang che,\" a ti a.",
			"24": "<sup>24</sup> Tin, lal Davida'n Ornana hnenah chuan, \"Ni lo e a; a man kim chiahin ka lei zawk ang e, man loin i ta chu LALPA tan ka la lo ang,\" a tia .",
			"25": "<sup>25</sup> Chutichuan Davida'n chu mi hmun leina chuan Ornana chu rangkachak sekel zaruk a pe a.",
			"26": "<sup>26</sup> Tin, Davida'n chutah chuan LALPA tan maicham a siam ta a, halral thilhalte leh remna thilhlante a hlan a, LALPA chu a au a; tin, ani chuan maicham chunga meiin van atangin a rawn chhang ta a.",
			"27": "<sup>27</sup> Tin, LALPA'n vantirhkoh chu thu a pe a, a khandaih chu apaiah a khung leh ta a.",
			"28": "<sup>28</sup> Tin Davida'n Jebus mi Ornana hruihah LALPA'n a rawn chhang tih a hriatin chutah chuan a inthawi ta a.",
			"29": "<sup>29</sup> Chutih lai chuan Mosia'n thlalera LALPA biakbuk leh halral thilhlanna maicham a siam kha Gibeon khuaa hmun sangah a awm a ni.Nimahsela Davida chu LALPA vantirhkoh khandaih a hlauh avangin Pathian rawn turin chuta chuan a kal thei lo a ni.",
			"30": "<sup>30</sup> Nimahsela Davida chu LALPA vantirhkoh khandaih a hlauh avangin Pathian rawn turin chuta chuan a kal thei lo a ni."
		},
		"22": {
			"1": "<sup>1</sup> Tichuan Davida'n, \"Hei hi LALPA Pathian in a lo ni a, hei hi Israelte halral thilhlanna maicham a lo nih hi le,\" a ti a.",
			"2": "<sup>2</sup> Chutichuan Davida'n Israel rama hnam dang awmte chu koh khawm thu a pe a; tin, pathian in sakna turin lungchertute chu lung a chhertir a.",
			"3": "<sup>3</sup> Tin, Davida'n kawt hun gkawngkhar leh a vuahna'n a kilhna tur thir tam tak a lo siam khawl a; dar tam tak buk sen loh nen;",
			"4": "<sup>4</sup> sidar thin gchiar sen loh nen; Zidon mi leh tura miten Davida hnenah chuan sidar thing tam tak an rawn la a ni.",
			"5": "<sup>5</sup> Tin, Davida'n, \"Ka fapa Solomona hi a la naupangin, engmah, a la hre rih lo a, tin, LALPA in sak tur chu ropui tak, a hming leh a ropuina chu ram tinah thang chhuak tur a ni a; chuvangin ka lo buatsaih lawk dawn e,\" a tia . Chutichuan Davida'n a thih hmain a lo buatsaih lawk nasa hle a.",
			"6": "<sup>6</sup> Tichuan a fapa Solomona a ko a, LALPA Israelte Pathian in sa tur chuan thu a pe a.",
			"7": "<sup>7</sup> Tin, Davida'n a fapa Solomona hnenah chuan, \"LALPA ka Pathian hming atan hian in sak ka lo rilruk thin a.",
			"8": "<sup>8</sup> Nimahsela, 'Nang zawngin thisen i chhuah nasa em a, indote i uar em em a; lei chungah hian ka mithmuha thisen i chhuah nasat avangin, ka hming atan in i sa lo vang;",
			"9": "<sup>9</sup> ngai teh; fapa i la nei ang a, ani chu remna duh mi a ni ang a, a vel rama a hmelmate zawng zawng lakah chuan ralmuanna ka pe ang; a hming chu Solomona ani ang a, ama chhanah chuan Israel-hote chu ralmuang leh tlamuang takin ka awmtir ang;",
			"10": "<sup>10</sup> \"Ahaba ka hmaa a inngaihtlawmzia hi i hmu em? Ka hmaa a inngaihtlawm avang hian ama chanah hi chuan thil tha lo ka thlentir lo ang a, a fapa chhanah erawh chuan a chhungte chungah thil tha lo ka thlentir ang,\" tiin.",
			"11": "<sup>11</sup> Chutichuan ka fapa, LALPA chu i hennah awm se la, hmuingil zel la, i chungchang thu a lo sawi lawk tawh ang kha, LALPA Pathian in chu sa ang che.",
			"12": "<sup>12</sup> LALPA'n finna leh hriatna te neihtir pahwt che se la, Israelte chunga thuneihna pe che rawh se; tichuan LALPA i Pathian dan chu i zawm thei dawn nia.",
			"13": "<sup>13</sup> Tichuan Israelte chungchang thua thuruatte leh rorelnate LALPA'n Mosia a pek kha i zawm chuan, i hmuingil zela ng; chak takin awm la, huai takin awm rawh; hlau suh la, zam hek suh.",
			"14": "<sup>14</sup> Ngai teh, ka mangan laiin LALPA in atan rangkachek talent nuaikhat leh, tangkaraw talent maktaduai khat ka lo buatsaih tawh a; tin, dar leh thirte pawh buk sen loh, tam tak a ni; thing leh lung pawh ka buatsaih lawk tawh a; chung chu i chnzawm thei tawh ang chu.",
			"15": "<sup>15</sup> Tin, Jehosaphata hnena chuan, \"Ramoth-gilead khua mi han beipui ve duh ang emaw chu?\" a ti a. Tin, Jehosaphatan Israel lal hnena chuan, \"Kei leh nang, ka mite leh mite chu pumkhat kan ni a, ka sakawrte leh i sakawrte chu pumkhat an ni alawm,\" a ti a.",
			"16": "<sup>16</sup> chhiar sen rual loh rangkachak te, tangka te, dar te, thirte pawh; bei nghal mai rawh khai, LALPA chu i hnenah awm rawh se,\" a ti a.",
			"17": "<sup>17</sup> Chutichuan Israel lal chuan zawlneiho mi zali lai a ko khawm a, an hnenah, \"Ramoth-gilead kua bei turin ka han tawk ang nge thawk lo ang?\" a ti a. Tin, anni chuan, \"Han thawk rawh, Lalpa'n lal kutah a pe dawn a ni,\" an ti a.",
			"18": "<sup>18</sup> \"LALPA in Pathian chu in hnenah a awm lo'm ni? Kil tin, kil tanga ralmuanna a pe lo che u em ni? He rama mite hi ka kutah a rawn hlan ta a, ram chu LALPA hmaah leh a mite hmaah lak ani ta a.Tichuan in thinlung leh in rilruin LALPA in Pathian lam hawi u la; bei ngal u la, LALPA thuthlung bawm leh Pathian bungbel thianghlimte chu LALPA hming in sak tura lak luhna'na LALPA Pathian hmun thianghlim chu siam rawh u,\" tiin.",
			"19": "<sup>19</sup> Tichuan in thinlung leh in rilruin LALPA in Pathian lam hawi u la; bei ngal u la, LALPA thuthlung bawm leh Pathian bungbel thianghlimte chu LALPA hming in sak tura lak luhna'na LALPA Pathian hmun thianghlim chu siam rawh u,\" tiin."
		},
		"23": {
			"1": "<sup>1</sup> Tin, Davida chu a dam reiin a lo upa ta hle a; tichuan a fapa Solomona chu Israelte lalah a siam a.",
			"2": "<sup>2</sup> Tin, Israel-ho puipa zawng zawngte chu puithiamte leh Levi-hote nen a ko khawm a.",
			"3": "<sup>3</sup> Tin, Levi-hote chu kum sawmthum achin chng lam chiar an ni a; an tam lam chu patling singthum leh sangriat an ni.",
			"4": "<sup>4</sup> Chung zinga sing hnih leh sangli chu LALPA in satuho hotu tur an ni a; tin, sangrukte chu hotute leh roreltu turte an ni a;",
			"5": "<sup>5</sup> tin, sangli te chu kawngkhar vengtu turte an ni a, tin, sanglite chu, \"Fakna tura thil tumrik chi ka siam,\" Davida'n a tihte chu hmanga LALPA faktu turte an ni.",
			"6": "<sup>6</sup> Tin, Levia fa Gersona te, Kohatha te, Meraria te zatin Davida'n pawl a siam a,",
			"7": "<sup>7</sup> Gerson mite chu Ladana leh Simeia an ni.",
			"8": "<sup>8</sup> Laldana fate chu puipa ber Jehiela te, Zethama te, Joela te, pathuam an ni.",
			"9": "<sup>9</sup> Simeia fate chu Selomotha te, Haziela te Harana te, pathum an ni. Hengte hi Ladana thlah chhungkaw puipate chu an ni.",
			"10": "<sup>10</sup> Tin, Simeia fate chu Jahatha te, Zina te, Jeusa te, Beria te an ni . Heng palite hi Simeia fate chu an ni.",
			"11": "<sup>11</sup> Tin, Jahatha chu a upa ber a ni a, Ziza chu a chhang a ni; Jeusa leh Beria erawh chuan fa an ngah lo a; chuvangin chhung khat anga chhiar an ni.",
			"12": "<sup>12</sup> Kohatha fate chu Amrama te, Izhara te, Hebrona te, Uziela te, pali an ni.",
			"13": "<sup>13</sup> Amrama fate chu Arona leh Mosia an ni; Arona chu amah leh athlahte chu kumkhua atana thil thianghlim takte tithianghlim a, kumkhuaa LALPA hmaa rimtui hal a, a rawng bawl a, a hming malsawmtu tura tihhran an ni.",
			"14": "<sup>14</sup> Pathian mi Mosia erawh chu , a fate chu Levi hnam zinga chhiar an ni.",
			"15": "<sup>15</sup> Mosia fate chu Gersoma leh Eliezera an ni.",
			"16": "<sup>16</sup> Gersoma fatea upa ber chu Sebuela a ni.",
			"17": "<sup>17</sup> Tin, Eliezera fatea upa ber chu Rehabia a ni. Tin, Eliezera chua nfapa dang reng reng anei lo ; Rehabia erawh chuan fapa tam tak anei.",
			"18": "<sup>18</sup> Izhara fatea upa ber chu Selomitha a ni.",
			"19": "<sup>19</sup> Hebrona fa tea upa ber chu Jeria a ni a, a chhang chu Amaria, a chhang leh chu Jahaziela, a chhang leh chu Jekameama a ni.",
			"20": "<sup>20</sup> Uziela fatea pa zawk chu Mika a ni a, a chhang chu Isia a ni.",
			"21": "<sup>21</sup> Meraria fate chu Mahlia leh Musia an ni. Mahlia fate chu Elazara leh Kisa an ni.",
			"22": "<sup>22</sup> Tin, Elazara chu a thi a, fapate nei loin fanute chau a nei a; tin, Kisa fapa an unaute nupiah an nei a.",
			"23": "<sup>23</sup> Musia fate chu Mahlia te, Edera te, Jeremotha te, pathuam an ni.",
			"24": "<sup>24</sup> Chungte chu Levia thlah, an chhungkua an gzela hming ziaka an chhiar chhngkaw puipa, LALPA in saka tawhk, kums awmhnih achin chung lama chhiar zawngte chu an ni.",
			"25": "<sup>25</sup> Davida'n, \"LALPA, Israelte Pathianin a mite chu ralmuan takin a awmtir ta a, Jerusalem -ah hian a awm ta reng a,",
			"26": "<sup>26</sup> Levi-hote pawh hian biak buk leh a rawngbawlna'n bungbel zawng zawngte hi an zawn a ngai tawh lo e,\" a ti a ni.",
			"27": "<sup>27</sup> Davida thupek hnuhnugn avangin Levi-hote hchu kum sawm hnih achin chunglam thlengin chhiar an ni si a.",
			"28": "<sup>28</sup> A hnathawh chu Pathian ina rawngbawla Arona fapate tanpuia kawt hung hchung leh pindan chhunga thil thianghlim zawng zawng tihthianghlim a, LALPA ina rawngbawl a ni si a;",
			"29": "<sup>29</sup> chhang chiah tur te, changphut thilhlan atana changphut dip ta, dawi dim telh loh chhangper pan emaw, bela ur emaw, kan emaw, a teh leh a lenzia ang zawng anwg tur buatsaih velte nen;",
			"30": "<sup>30</sup> tin, tuk tina LALPA hnena lawm thu sawileh amah fakte nen, tlaiah pawh chuti bawk;",
			"31": "<sup>31</sup> tin, chawlhniah te, thla tharah te, kut bi neiah te, a tih tura ruat ang zela LALPA hnena halral thilhlan zawng zawng LALPA hmaa hlan rengte nen;tin, inbiakna puan in enkawl te, hmun thianghlim enkawl te, LALPA in arawngbawl a an unau Arona fapaten tih tur an ruat apiang tihte nen.",
			"32": "<sup>32</sup> tin, inbiakna puan in enkawl te, hmun thianghlim enkawl te, LALPA in arawngbawl a an unau Arona fapaten tih tur an ruat apiang tihte nen."
		},
		"24": {
			"1": "<sup>1</sup> Tin, Arona fapate pawl then dan chu hetiang hi a ni. Arona fapate chu Nadaba te, Abihua te, Eleazara te, Ithamara te an ni.",
			"2": "<sup>2</sup> Nadaba leh Abihau erawh chu an pa aiin an thi hmasa a, fa an nei lo; chuvangin Elazara leh Ithamara chuan puithain hna an thawk a ni.",
			"3": "<sup>3</sup> Tin, Davida'n Elazara fapa Zadoka leh Ithamara fapa Ahimeleka te nen an rawnbawl dan tur a semsak a.",
			"4": "<sup>4</sup> Tin, Ithamara thlahte ai chuan Elazara thlah puipaa tang an tam zawk a; hetiang hian an then a: Eleazara thlaha mi, chhungkaw puipa sawm leh paruk an ni a; tin, Ithamara thlaha mi, chhungkaw puipa pariat an ni.",
			"5": "<sup>5</sup> Chutiang chuan thumvawrin, ai khata luangin an then a; Eleazara thlaha mi leh Ithamara thlaha mite zingah, hmun thianghlima mi liante leh Pathian mi liante an awm ve ve si a.",
			"6": "<sup>6</sup> Tin, Levi mi, lehkhaziaktu Nethanela fapa Semaian lal te, mi lianho te, puithiam Zadoka te, Abiathara fapa Ahimeleka te, puithiamho leh Levi-ho chhungkaw puipate mithmuhah chuan a ziak a; chhungkaw puipa pakhat chan chu Eleazara chungah a tlu a, pakhat chu Ithamara chungah a tlu a.",
			"7": "<sup>7</sup> Tichuan thumvawr vawi khatna chu Jehoiariba chungah a tlu a, vawi hnihna chu Jedaia chungah;",
			"8": "<sup>8</sup> vawi thumna chu Harima chungah, vawi lina chu Seorima chungah,",
			"9": "<sup>9</sup> vawi ngana chu Malkija chungah, vawi rukna chu Kihamina chungah;",
			"10": "<sup>10</sup> vawi sarihna chu Hakoa chungah vawi riatna chu Abija chungah;",
			"11": "<sup>11</sup> vawi kuana chu Jesua chungah, vawi sawmna chu Sekania chungah;",
			"12": "<sup>12</sup> vawi sawm leh vawi khatna chu Eliasiba chungah, vawi sawm leh vawi hnihna chu Jakima chungah;",
			"13": "<sup>13</sup> vawi samw leh vawi thumna chu Hupa chungah, vawi sawm leh vawi lina chu Jesebeaba chungah;",
			"14": "<sup>14</sup> vawi sawm leh vawi ngana chu Bilga chungah, vawi sawm leh vawi rukna chu Imera chungah;",
			"15": "<sup>15</sup> vawi sawm leh vawi sarihna chu Jezira chungah, vawi sawm leh vawi riatna chu Hapizeza chungah;",
			"16": "<sup>16</sup> vawi sawm leh vawi kuana chu Pethahia chungah, vawi sawm hnihna chu Jehezkela chungah;",
			"17": "<sup>17</sup> vawi sawmhnih leh vawi khatna chu Jakina chungah,v awi sawmhnih leh vawi hnihna chu Gamula chungah;",
			"18": "<sup>18</sup> vawi sawmhnih leh vawi thumna chu Delaia chungah, vawi sawmhnih leh vawi lina chu Maazia chungah,",
			"19": "<sup>19</sup> Chu chu LALPA, Israelte Pathianin an pu Arona thu a lo pek, Pathianin an pu Arona thu a lo pek, LALPA in chhunga rawng an bawl dan tur a lo ruat dan chu a ni.",
			"20": "<sup>20</sup> Tin, Levia thlah dangte zinga mi chu: Amrama fate zingah chuan Subaela a ni; Subaela fate zingah Jedeia.",
			"21": "<sup>21</sup> Rehabia lama chuan: Rehabia-hote zingah puipa Isia.",
			"22": "<sup>22</sup> Izhar-ho zingah Selomotha; Selomotha fate zingah Jahatha.",
			"23": "<sup>23</sup> Tin, Hebrrona fate chu a upa ber Jeria a ni a, a chhang Amaria, a chhang leh Jahaziela, a chahgn leh Jekameama a ni.",
			"24": "<sup>24</sup> Uziela fate zinga mi Mika; Mika fate zinga mi Samira a ni.",
			"25": "<sup>25</sup> Mika unaupa chu Isia a ni a; Isia fate zinga mi Zekaria,",
			"26": "<sup>26</sup> Meraria fate chu Mahlia leh Musia an ni: Jaaiza fate zinga mi Benoa a ni.",
			"27": "<sup>27</sup> Meraria thlahte chu Jaazia lakah Benoa te, Sohama te, Zakura te, Ibria te an ni.",
			"28": "<sup>28</sup> Mahlia lakah, Elazara a ni a, ani chuan fapa a nei lo.",
			"29": "<sup>29</sup> Kisa lakah chuan Kisa fapa Jarameela a ni.",
			"30": "<sup>30</sup> Tin, Musia thlahte chu Mahlia te, Edera te, Jerimotha te an ni. Chungte chu Levi ho thlahte zinga an chhungkaw puipate chu an ni.Anni pawh chuan Arona fa a n unaute ang bawkin, lal Davida te, Zadoka te, Ahimeleka te, puithiamho leh Levi-ho chhungkaw pipate mithmuhah thum an vawr ve bawk a; chhungkaw puipa ber an unaute pawh ai khata luangin.",
			"31": "<sup>31</sup> Anni pawh chuan Arona fa a n unaute ang bawkin, lal Davida te, Zadoka te, Ahimeleka te, puithiamho leh Levi-ho chhungkaw pipate mithmuhah thum an vawr ve bawk a; chhungkaw puipa ber an unaute pawh ai khata luangin."
		},
		"25": {
			"1": "<sup>1</sup> Tin, Asapha te, Hemana te, Jeuthuna teho fapa thenkhat chu tingtang te, perhkhuang te, darbenthekte nena fak dual duala rawngbawltu atan Davida leh sipai hotuten an tihrang bawk a; tin, chutianga thawka rawngbawltute tam lam chu:",
			"2": "<sup>2</sup> Asapha fapate zinga mi chu Zakura te, Josepha te, Nethania te, Asarela te an ni a, anni chu Asapha fate, lal thupek anga fak dual dual thin. Asapha thu thua awm an ni.",
			"3": "<sup>3</sup> Jeduthuna lakah chuan: Jeduthuna fa Gedalia te, Zeria te, Jesaia te, Hasabia te, Matithia te, paruk an ni: Tingtang nena LALPA hnena lawmt u sawia fak dual duala an pa Jeduthuan thu thua awm an ni.",
			"4": "<sup>4</sup> Hemana lakah chuanL Hemana fa Bukia te, Matania te, Uziela te, Sebuela te, Jeribnotha te, Hanania te, Hanan te, Eliatha te, Gidaltia te leh Romamtiezera te, Josbekasa te, Malothian te, Hothira te, Mahoziaotha te an ni a;",
			"5": "<sup>5</sup> chung zawng zawng chu Lal tana zawlnei Hemana fa, englo ki tawtawrawt hamtu turte an ni. Pathianin Hemana chu fapa sawm leh pali leh fanu pathum a pe a.",
			"6": "<sup>6</sup> Chung zawng zawngte chu LALPA inah darbenthek nen, perhkhuang nen, tingtang nen, Pathian ina rawngbawlna'na hla nena remtu, an pa thu thua awm an ni; Asapha te, Jeduthuan te, Hemana te chu lal thupeka awm an ni ve thung a.",
			"7": "<sup>7</sup> Tin, an tam lam chu, an unau, LALPA tana hla sak zir, thiamsa tawh zawng zawngte nen chuan zahnih leh sawmriat leh pariat an ni.",
			"8": "<sup>8</sup> Tin, a lian a te, zirtirtu leh zirtuin an chan tur angkhat vekin thum an vawr a.",
			"9": "<sup>9</sup> Tichuan thumvawr vawi khatna chu Asapha fapa Josefa chungah a tlu a; vawi hnihna chu Gedalia chungah; amah leh a unaute leh a fapate chu sawm leh pahnih an ni;",
			"10": "<sup>10</sup> vawi thumna chu Zakura chungah, a fapate leh a unaute chu sawm leh pahnih;",
			"11": "<sup>11</sup> vawi lina chu Izria chungah, a fapate leh a unaute chu sawm leh pahnih;",
			"12": "<sup>12</sup> vawi ngana chu Nethania chungah, a fapate leh a unaute chu sawm leh pahnih;",
			"13": "<sup>13</sup> vawi rukna chu Bukia chungah, a fapate leh a unaute chu sawm leh pahnih;",
			"14": "<sup>14</sup> vawi sarihna chu Jasarela chungah, a fapate leh a unaute chu sawm leh pahnih;",
			"15": "<sup>15</sup> vawi riatna chu Jesaia chungah, a fapate leh a unaute chu sawm leh pahnih;",
			"16": "<sup>16</sup> vawi kuana chu Matania chungah, a fapate leh a unaute chu sawm leh pahnih;",
			"17": "<sup>17</sup> vawi sawmna chu Simeia chungah, a fapate leh a unaute chu sawm leh pahnih;",
			"18": "<sup>18</sup> vawi sawm leh vawi khatna chu Azarela chungah, a fapate leh a unaute chu sawm leh pahnih;",
			"19": "<sup>19</sup> vawi sawm leh vawi hnihna chu Hasabia chungah, a fapate leh a unaute chu sawm leh pahnih;",
			"20": "<sup>20</sup> vawi sawm leh vawi thumna chu Subaela chungah, a fapate leh a unaute chu sawm leh pahnih;",
			"21": "<sup>21</sup> vawi sawm leh vawi lina chu Matithia chungah, a fapate leh a unaute chu sawm leh pahnih;",
			"22": "<sup>22</sup> vawi sawm leh vawi ngana chu Jeremotha chungah, a fapate leh a unaute chu sawm leh pahnih;",
			"23": "<sup>23</sup> vawi sawm leh vawi rukna chu Hanania chungah, a fapate leh a unaute chu sawm leh pahnih;",
			"24": "<sup>24</sup> vawi sawm leh vawi sarihna chu Josbekasa chungah, a fapate leh a unaute chu sawm leh pahnih;",
			"25": "<sup>25</sup> vawi sawm leh vawi riatna chu Hanana chungah, a fapate leh a unaute chu sawm leh pahnih;",
			"26": "<sup>26</sup> vawi sawm leh vawi kuana chu Malothia chungah, a fapate leh a unaute chu sawm leh pahnih;",
			"27": "<sup>27</sup> vawi sawmhniha chu Eliatha chungah, a fapate leh a unaute chu sawm leh pahnih;",
			"28": "<sup>28</sup> vawi sawmhnih leh vawi khatna chu Hothira chungah, a fapate leh a unaute chu sawm leh pahnih;",
			"29": "<sup>29</sup> vawi sawmhnih leh vawi hnihna chu Gidaltia chungah, a fapate leh a unaute chu sawm leh pahnih;",
			"30": "<sup>30</sup> vawi sawmhnih leh vawi thumna chu Mahaziotha chungah, a fapate leh a unaute chu sawm leh pahnih;vawi sawmhnih leh vawi lina chu Romamti-ezera chungah, a fapate leh a unaute chu sawm leh pahnih;",
			"31": "<sup>31</sup> vawi sawmhnih leh vawi lina chu Romamti-ezera chungah, a fapate leh a unaute chu sawm leh pahnih;"
		},
		"26": {
			"1": "<sup>1</sup> Kawngkhar vengtu pawl then dan chu: Kora mi chu Asapha fapate zinga Korea fapa Meselmia chuan fapate a nei a; a fa tir chu Zekaria a ni a, a dawttu chu Jediaela, a dawt lehtu chu Zebadia, a dawt lehtu chu Jathniela;",
			"2": "<sup>2</sup> a dawt lehtu chu Elama, a dawt lehtu chu Jehohanana, a dawt lehtu chu Eliehoenaia a ni.",
			"3": "<sup>3</sup> Tin, Obed-edoman fapa tea nei a; a fa tir chu Semaia a ni a, a dawttu chu Jehozabada, a dawt lehtu chu Joa, a dawt lehtu chu Sakara, tin, a a dawt lehtu chu Nethanela;",
			"4": "<sup>4</sup> a dawt lehtu chu Amiela, a dawt lehtu chu Isakara, a dawt lehtu chu Peulethaia a ni a, ani chu Pathianin mal a sawma ni.",
			"5": "<sup>5</sup> A fapa Semaia lakah chuan fapa an chhungkaw puipate an piang bawk a; mi chak huaisen an ni hlawm a ni.",
			"6": "<sup>6</sup> Semaia fapate chu Othnia te, Rephaela te, Obeda te, Elzabada te a ni a, an unau Elihua leh Semakia chu mi chak tak an ni.",
			"7": "<sup>7</sup> Chung zawng zawng chu Oed-edoma thlahte an ni: anmahni leh an fapate leh a unaute chu rawngbawlna kawnga mi pitling an ni a; Oed-edoma laka mite chu sawmruk leh pahnih an ni.",
			"8": "<sup>8</sup> Meselemia chuan fapa lehunau mi chak tak tak sawm leh pariat anei.",
			"9": "<sup>9</sup> Tin, Meraria thlahte zinga Hosa chuan fapate a nei bawk a; Simria chu puipa ber a ni a, (fa tir a ni lo na a, a pain puipa berah a siam a ni);",
			"10": "<sup>10</sup> a dawttu chu Hilkia a ni a, a dawt lehtu chu Tebalia, a dawt lehtu chu Zekaria a ni. Hosa fapate leh a unaute chu an zain sawm leh pathum an ni.",
			"11": "<sup>11</sup> Chung puipaho zinga mi chu an unaute anga LALPA ina rawngbawl hmaa chanvo neia kawngkhar vengtu pawla an thente chu an ni.",
			"12": "<sup>12</sup> Alian atein, an chhungkaw zir ang zelin kawngka tina awm tur atan thum an vawr a.",
			"13": "<sup>13</sup> Tin, chhak lam atana thumvawr chu Selemia chungah a tlu a. Tin, roreltu fing tak, a fapa Zekaria tan thum an vawr a, hmar lam chu a pualah a tlu a.",
			"14": "<sup>14</sup> Chhim lam chu Obed-edoma pualah; tin, thil dahkhawmna in chu a fapate pualah.",
			"15": "<sup>15</sup> Thlang lam chu a chhohan kawnga Saleket kawngkhar vena nen, Supima leh Hosa pualah.",
			"16": "<sup>16</sup> Chak lamah Levi-ho paruk an awm a, ni tin, hmar lamah pali, ni tin, chhim lama pali, tin, thil dah khawmna inah pahnih ve ve.",
			"17": "<sup>17</sup> Thlang lam in hualchhung atan chuan a luhnaah pali; in hualchungah chuan pahnih.",
			"18": "<sup>18</sup> Chungte chu an ni, Kora mi fapate leh Merari mi fapate kawngkhar vengtu pawla an thente chu.",
			"19": "<sup>19</sup> ,21 Tin, an unau Levi-ho Pathian sum dah khawmna in leh thil thianghlim dah khawmna in vawngtute chu Ladana chhungkaw pipa, Ladana laka Gerson mi thlah,",
			"20": "<sup>20</sup> ,20 Tin, an unau Levi-ho Pathian sum dah khawmna in leh thil thianghlim dah khawmna in vawngtute chu Ladana chhungkaw pipa, Ladana laka Gerson mi thlah,",
			"21": "<sup>21</sup> Jehiela leh a unau Zethama leh Joela te an ni; chungte chu LALPA sum dah khawmna in vawngtute an ni.",
			"22": "<sup>22</sup> Amram-ho te, Izhar-ho te, Hebron-ho te, Uziel-ho te zinga mi.",
			"23": "<sup>23</sup> Mosia fa Gersoma fapa Sebuela hu Sum dah khawmna in vawngtu a ni.",
			"24": "<sup>24</sup> Tin, a unaute chu Eliezera fapa Rehabia te, Jesaia te, Jorama te, Zikria te, Selomotha te an ni.",
			"25": "<sup>25</sup> Selemotha leh a unaute chu lal Davida leh an chhungkaw puipa sipai sang otute le za hotute leh sipai pawl hotuten an tihthianghlimt awh, thil thianghlim dah khawmna in zawng zawng vawngtu an ni.",
			"26": "<sup>26</sup> Indonaa trallak thilte chu LALPA in siam thatna'n an dah a ni.",
			"27": "<sup>27</sup> Zawlnei Samuela te, Kisa fapa Saula e, Nere fapa Abnera te, Zeruia fapa Joaba tena an tihthianghlim zawng zawng, tupawhinthil an tihthianghlimt awh phawt chu Selomotha leh a unaute vawn vek a ni.",
			"28": "<sup>28</sup> Izhar mi Kenania leh a fapate ch Israel ram pawn lam chungchang thua hottue leh roreltute an ni.",
			"29": "<sup>29</sup> Hebron mi Hasabia le a unau mi huaisen sangkhat leh zasarih te chu Jordan ral thalng lama Israel-ho awptu an ni; LALPA hna zawng zawng leh lal rawngbawlna lam hnaat.",
			"30": "<sup>30</sup> Hebron thlahte hruaitu ber chu Jeria a ni a. Davida lal kum sawmpali kum khan Hebron thlahte chhuichhuahna a awm a, Gilead rama Jazer-ah chuanan zingah mi chakhuaisen an lo awm tuh mai a,Davida chuan Jerija chhungte zinga mi huaisente chu mi 2,700 a thlang chhuak a, chung mite chu Jordan lui khawchhak lam Reuben-ho te, Gad hote leh chhak lam Manasea hote rama Israel-te sakhaw thu leh tualchhung thu rel turin a ruat a.",
			"31": "<sup>31</sup> Davida chuan Jerija chhungte zinga mi huaisente chu mi 2,700 a thlang chhuak a, chung mite chu Jordan lui khawchhak lam Reuben-ho te, Gad hote leh chhak lam Manasea hote rama Israel-te sakhaw thu leh tualchhung thu rel turin a ruat a."
		},
		"27": {
			"1": "<sup>1</sup> Israel-ho ant am chin chu- chhungkaw puipa te, sipai sang hotu te, za hotute leh lal rawngbawltu zawng zawng, kum khat chhunga thla tina inthlak zel thin, pawl tina mi chu singhnih leh sang li an ni.",
			"2": "<sup>2</sup> Pawl khatah thla khatnaa hotu ch Jebdiela fapa Jasobeama a ni a, a pawlah chuan singhnih leh sangli an awm.",
			"3": "<sup>3</sup> Ani chuan Pereza thlaha mi, thlakhatnaa sipai hotu zawng zawng hotu ber a ni.",
			"4": "<sup>4</sup> Tin, thla nihnaa pawl hotu chu Aho mi Dodaia a ni a, a pawlah chuan singhnih leh sangli an awm.",
			"5": "<sup>5</sup> Thla linaa sipai hotu chu Joaba unaupa Asahela a ni a, a fpa Zebadian a ai a awh a ni; tin, a pawlah chuan singhnih leh sangli an awm.",
			"6": "<sup>6</sup> Thla nganaa sipai hotu chu Izra mi Samhutha a ni a; tin, apawlah chuan singhnih leh sangli an awm.",
			"7": "<sup>7</sup> Thla ruknaa sipai hotu chu Teko mi,Ikesa fapa Ira a ni a; tin, a pawlah chuan singhnih leh sangli an awm.",
			"8": "<sup>8</sup> Thla sarihnaa sipai hotu chu Ephraima thlah Pelon mi Heleza a ni a, a pawla chuan singhnih leh sangli an awm.",
			"9": "<sup>9</sup> Thla riatnaa sipai hotu chu Zera thlah, Husat mi Sibekaia a ni a; tin, a pawlah chuan singhnih leh sangli an awm..",
			"10": "<sup>10</sup> Thla sarihnaa sipai hotu chu Ephraima thla Pelon mi Heleza a ni a, a pawlah chuan singhnih leh sangli an awm.",
			"11": "<sup>11</sup> Thla riatnaa sipai hotu chu Zera thlah, Husat mi Sibekaia a ni a, a pawlah chuan singhnih leh sangli an awm.",
			"12": "<sup>12</sup> Thla kuanaa sipai hotu chu Benjamina thlah Anathot mi Abiezera a ni a; a pawlah chuan singhnih leh sangli an awm.",
			"13": "<sup>13</sup> Thla sawmnaa sipai hotu chu Zera thlah Netophat mi Maharaia a ni a; tin, a pawlah chuan singhnih leh sangli an awm.",
			"14": "<sup>14</sup> Thla sawm leh thla khatnaa sipai hotu chu Ephraima thlah Pirathon mi Benaia a ni a; tin, a pawlah chuan singhnih leh sangli an awm.",
			"15": "<sup>15</sup> Thla sawm leh thla hnihnaa sipai hotu chu Othniela thlah Netophat mi Heldaia a ni a; tin, a pawlah chuan singhnih leh sangli an awm.",
			"16": "<sup>16</sup> Chung lo pawhchu, Israel hnamo chunga Reuben-hote zingah chuan Zikria fapa Eliezera roreltu a ni a; Simeon-ho zingah Maaka fapa Sephatia;",
			"17": "<sup>17</sup> Levi-ho zingah Kemuela fapa Hasabia; Arnon-ho zingah Zadoka;",
			"18": "<sup>18</sup> Juda-ho zingah Davida unaute zinga mi Elihua; Isakar ho zingah Mikaela fapa Omria;",
			"19": "<sup>19</sup> Zebulun-ho zingah Obadia fapaIsmaia; Naphtali-ho zingah Ariela fapa Jeremotha;",
			"20": "<sup>20</sup> Ephraima thlahte zingah Azazaia fapa Hosea; Manasea hnam zatvete zingah Pedaia fapa Joela;",
			"21": "<sup>21</sup> Gilead rama Manasea hnam zatvete zingah Zekaria fapa Idova; Benjamin-ho zingah Abnera fapa Jaasiela;",
			"22": "<sup>22</sup> Dan-ho zingah Jerohama fapa Azarela. Chungte chu Israel hnam hotute an ni.",
			"23": "<sup>23</sup> Davida chuan kum sawmhnih achin hnuai lam chu a chhiar lo a; Israel-o te chu vana arsi zat khiang a la tipung ang ti LALPA'n a lo sawi tawh avangin.",
			"24": "<sup>24</sup> Zeruii fapa Joaba chuan a han chhiar tan a, nimahsela a chiar zo lo a, Israelte chungah pawh thinurnaa lo thleng ta a; lal Davida lal lai chanchin ziakna buah a zat ziak a ni bawk hek lo.",
			"25": "<sup>25</sup> Tin, lal sum enkawltu chu Adiela fapa Azmavetha a ni a; tin, lo lamah te, khawpiah te, khawperah te, in sangtea sum enkawltu chu Uzia fapa Jonathana a ni a;",
			"26": "<sup>26</sup> tin, loa leleh hnathawk ho enkawltu chu Keluba fapa Ezria a ni a;",
			"27": "<sup>27</sup> tin, grep huan enkawtu chu Ramat mi Simeia a ni a; tin, grep huan a mi uain sawr da khawmna enkawltu chu Siphmot mi Zabdia a ni a;",
			"28": "<sup>28</sup> tin, phai ruama Olive thing leh theipui kiungte enkalwtu c Geder mi baal-hanana a ni a; tin, hriak da khawmna enkawltu chu Joasa a ni a;",
			"29": "<sup>29</sup> tin, Saron-a tla ran rual enklawtu chu Saron mi Straia a ni a; tin, ruama ran rual enkawltu chu laia fapa Saphata a ni a;",
			"30": "<sup>30</sup> tin, sanghawngsei enkawltu chu Ismael mi Obila a ni a; tin, sabengtun genkawltu chu Meronot mi Jehdeia a ni a;",
			"31": "<sup>31</sup> tin, beram rual enkawltu chu Hagar mi Jaziza a ni a. Chung zawng zawngte chu lal Davida sum enkawltute an ni.",
			"32": "<sup>32</sup> Tin, Davida pami, mi fing, lehkha thiam tak Jonathana chu remruattu a ni a; tin, Hakmonia fapa Jehiela chu lal fapate zirtirtu a ni a;",
			"33": "<sup>33</sup> tin, Ahithophela chu lal remruatpuitu a ni a; tin, Ark mi Husaia chu lal thian a ni a;tin, Ahithophela dawtah chuan Benaia fapa Jehoiada leh Abiathara an ni a; tin, lal sipaio hotu ch Joaba a ni.",
			"34": "<sup>34</sup> tin, Ahithophela dawtah chuan Benaia fapa Jehoiada leh Abiathara an ni a; tin, lal sipaio hotu ch Joaba a ni."
		},
		"28": {
			"1": "<sup>1</sup> Tin, Davida'n Israel hotu zawng zawng - hnam puipa te, lal rawngbawla intlak thin pawl hotu te, sipai sang hotu te, za hotu te, lal leh a fapate sum le thil neih zawng zawng chunga hotu te, mi lian leh mi chak, mi chak huaisen zawng zawngte nen- Jerusalemah a ko khawm a.",
			"2": "<sup>2</sup> Tichuan lal Davida chu a ding a, \"Ka unaute leh ka mite u, ka thu hi lo ngaithla teh u: kei chuan LALPA thuthlunna bawm dahna leh kan Pathian ke nghahchhan tur in sa i la tih ka lo rilru thin a; tin, sakna tur pawh ka ngah tawh diam a.",
			"3": "<sup>3</sup> Nimahsela Pathianin ka hnenah, 'Nang zawng indo mi, thisen chhuah mi i niha vangin ka hming atan i sa lo ang,' a ti a.",
			"4": "<sup>4</sup> Amaherawhchu LALPA, Israelte Pathianin ka pa chhungte zawng zawng zing ata Israelte lala kumkhuaa awm turin mi lo thlang ta a; Juda hnam chu puipa atan a lo thleng ta a; tin, Juda hnamte zingah ka pa chhung te; tin, ka pa fapate zing ata Israel zawng zawng lalah mi lo thlang chhuak ta bik a;",
			"5": "<sup>5</sup> tin, ka fapa zawng zawngte zingah chuan (LALPA'n fapa tam tak mi pe a) Israaelte lal atan LALPA ram lalthutthlenga thu turin ka fapa Solomona hi a lo thlang ta a.",
			"6": "<sup>6</sup> Tichuan ka hnenah, 'I fapa Solomonan ka in lehka kawtlaite chu a din ang; ka fapa atan ka thlang tawh a, tichuan a pa ka ni ang.",
			"7": "<sup>7</sup> ka thupekte leh ka rorelte a pawm reng chuan - tunah in tih reng ang hian - a ram chu kumkhua atan ka tinghet ang,' a ti a.",
			"8": "<sup>8</sup> chutichuan LALPA mipui, Israel-ho zawng zawng mithmuhah leh ka Pathian hriata hian LALPA in Pathian thupek zawng zawngte hi pawm tur leh ngaihven turin ka fuih a che u; tichuan he ram tha tak hi in lua ang a, in hnua in thlahte kum khua atan in rochun chhawntir zel ang.",
			"9": "<sup>9</sup> Tin, nang ka fapa Solomon, i pa Pathian hi hria la, thinlung tha famkim leh rilru phur takin a rawng bawl ang che; LALPA chuan thinlung a en thin a, rilrua suangtuah zawng zawngte hi a hre vek a ni; amah i zawn chuan i hmu ang a, i hawisan erawh chuan kumkhuain a thlauhthla ang che.",
			"10": "<sup>10</sup> Fimkhur rawh khai; hmun thianghlim atana in sa turin LALPA'n a lo ruat tak che hi; hak takin awm la, bei rawh, a ti a.",
			"11": "<sup>11</sup> Chutichuan Davida chuan a fapa Solomona ch biak in hulhliap lem te, a in lem te, sum dah khawmna in lem te, a pindan chungnung lem te, a chhung pindam lem te, zahngaihna thutphah lem te,",
			"12": "<sup>12</sup> a rilrua a suangtua ang zawng zawng, LALPA in hungchhung kawtlai lem te, a vel pindan zawng zawng lem te, Pathian ina sum dah khawmna in lem te, thil serh dah khawmna in lem te chu a pe a;",
			"13": "<sup>13</sup> puithiamte le Levi-hote a pawl pawla an awmna tur lem te, LALPA ina rawngbawlna lam tur zawng zawng lem te, LALPA ina rawngbawlna tur bungbel zawng zawng lem te pawh;",
			"14": "<sup>14</sup> rawngbawlna lam atana bungbel chi tin, rangkachak bungbel tur, rangkachak hman zat tur te, rawngbawlna lam atana tangkaraw bungbel tur tangkaraw hman zat tur te,",
			"15": "<sup>15</sup> Rangkachak khawnvar dahna leh a khawnvar tur rangkachak hman zat tur te, khawnvar dahna leh a khawnvar tur zel atana hman zat tur te, khawnvar dahna hman tulna theu ang zela tangkaraw khawnvvar dahna tur zel atana hman zat turte nen;",
			"16": "<sup>16</sup> chhang chiahna dawhkan tur, pakhat zela tana rangkachak hmanzat tur te, tangkaraw dawhkan atan tangkaraw hman zat tur te.",
			"17": "<sup>17</sup> Rangkachak hlir hlak sa thurna thir kawm te, maihun te, note nen; rangkachak berhbu tur, berhbu pakhat zel atana hman zat tur te, tangkaraw berhbu tur, berhbu pakhat zel atana hman zat tur te;",
			"18": "<sup>18</sup> Rangkachak thianghlima siam, rimtui halna maichamtur te; cherub-te an thla zara LALPA thuthlunna bawm tuam chuanna lem ang siamna tur rangkachak te nen.",
			"19": "<sup>19</sup> Davida chuan, \"Hengte hi tih dan tur rem a ruat sa tichiang tura LALPA'n ama kut ngeia a ziak ang kha a ni,\" a ti a.",
			"20": "<sup>20</sup> Tin, Davida'n a fapa Solomona hnenah chuan, \"Chak tak leh huai takin awm la, bei rawh; hlau suh la, zam hek suh; LALPA Pathian, ka Pathian hi i hnenah a awm e; LALPA in lam hna zawng zawng hi peih vek a nih hma loh chuan a kalsanin a thlahthlam lo ang che.Tin, ngai teh, Pathian in hna tinreng thawktu pawl puithiamte leh Levi-hote na awm a, eng hna pawh kawng tinrenga hna thiam, chak tak theuha tanpuitu tur che i hnenah an awm zel ang; a hotute leh mipui zawng zawng pawhin i thupek apiang chu an zawm zel bawk ang,\" a ti a.",
			"21": "<sup>21</sup> Tin, ngai teh, Pathian in hna tinreng thawktu pawl puithiamte leh Levi-hote na awm a, eng hna pawh kawng tinrenga hna thiam, chak tak theuha tanpuitu tur che i hnenah an awm zel ang; a hotute leh mipui zawng zawng pawhin i thupek apiang chu an zawm zel bawk ang,\" a ti a."
		},
		"29": {
			"1": "<sup>1</sup> Tin, lal Davida'n mipui zawng anwg hnenah chuan, \"Pathian thlan chhuah ka fapa Solomona hi a la naupangin engmah a la hre rih lo a, a thawh tur lah chu a ropui si a; biak in chu mihring tan ani lo a, LALPA Pathian tan ani zawk si a.",
			"2": "<sup>2</sup> Tichuan ka Pathiain atan chuan rangkachak te, tangkaraw lam thil atan tangkarua te, dar lam thil atan dar te, thir lam thil atan thir te, thing lam thil atan thingte chu theihtawpin ka lo buatsaiht awh a; a zeh tur onuk lung te, lungmawi te, a bel tur lungmawi chi hrang hrang leh lunghlu chi tinreng leh sailungvar lung tam takte nen.",
			"3": "<sup>3</sup> chu chauh lo pawh chu ka Pathian in hi ka duhsak em em mai a, keima sum rangkachak leh tangkaruate pawh, in thianghlim atana ka lo buatsaih zawng zawng tiam lo hian, ka Pathian in atan ke pe a ni:",
			"4": "<sup>4</sup> chu chu bangte luanna'n Ophir rangkachak, rangkachak talent sangthum leh tangkaraw thianghlim talen sangsarih;",
			"5": "<sup>5</sup> hmanna kawn ghrang hranga mi thiamte hman turin rangkachak hmanna tura rangkachak te, tangkaraw hmanna tura tangkarua te a ni. Vawiin hian LALPA tana lawm taka pe phal tu nge awm le?' a ti a.",
			"6": "<sup>6</sup> Tichuan chhungkaw puipa te, Israel hnam hotu te, sang hotute leh za hotu te, lal hnathawkho hotute nen lawm takin an pe a;",
			"7": "<sup>7</sup> tin, Pathian in sakna'n rangkachak talent sangnga leh darik sing khat, tangkarua talent singkhat leh dar talent singkhat leh sangriat, thir talent nuai khat an pe a.",
			"8": "<sup>8</sup> Tin, lunghlu neite chuan Gerson mi Jehielavawn LALPA sumah an dah a.",
			"9": "<sup>9</sup> Tichuan mipuite chu an hlim em me mai a, rilru hmun khat pua lawm taka LALPA an peka vangin; lal Davida pawh hlim namen lohin a hlim a.",
			"10": "<sup>10</sup> Chutichuan Davida'n mipi zawng zawng, hmaah chuan LALPA hnenah lawm thu a sawi a; \"Aw LALPA, kan pu Israela Pathian, chatuana fakin awm rawh.",
			"11": "<sup>11</sup> Aw LALPA, ropuina te, thiltihtheihna te, chawimawina te,hnehana te, lalna te hi i ta a ni a; lei leh vana awm zawng zawng hi i ta a ni si a; aw LALPA, ram chu i ta a ni a, mi zawng zawnga chungnung ber i ni.",
			"12": "<sup>12</sup> Hausakna leh chawimawina e hi i hnena mi a ni a, engkim chungah thu i nei a; tin, thiltihthiehna ikutah a awm a; i kutah hian mi zawng zawng tihropuina leh chaknana pek theihnaa wm.",
			"13": "<sup>13</sup> Chutichuan kan Pathian, lawmthu kan hril chia, i hming ropui hi kan fak e.",
			"14": "<sup>14</sup> Nimahsela tu nge maw ka nih,a ka mite phaw hi eng nge maw an nih a, hetiangte lawm taka kan lo hlan theih ni le? Thil zawng zawng hi i hnen atanga chhuak a ni a, i ta chu kan rawn pe leh mai che a ni si a.",
			"15": "<sup>15</sup> Kan pi leh pute ang khan keini zawng i hmaahhianmikhual, mi hnena khawsa ve mai ang kan ni a; leilunga dam chhung nite chu hlim ang chauh a ni a, beiseitur reng a awm lo.",
			"16": "<sup>16</sup> Aw LALPA kan Pathian, i hming thianghlim atana in ka saksakna tur chea he'ng thil kan lo buatsaihna zawng zawngte hi nangma kutchhuak a ni a, i ta vek a ni e.",
			"17": "<sup>17</sup> Ka Pathian, nangin thinlungte i fiah thin a, felna te i lawm thin tih ka hre bawk. Kei zawngin rilru tak takin hengthil zawng zawng hi lawm takin ka rawn hlan a, tin, heta i mite pawh lawm taka i hnena rawn hlantu ka hmuh hian ka lawm em em a ni.",
			"18": "<sup>18</sup> Aw LALPA, kan pu Abrahama te, Isaka te, Israela-te Pathian, i miten hetiang rilru an put reng theina turin an rilrute hi i tan tinghet la;",
			"19": "<sup>19</sup> tin, ka fapa Solomona hi i thupek te, i thuhriattir te, i dan te zawm a, chung zawng zawng ti rute leh ka lo buatsaih lawk in sa tur hian rilru famkim han puttir ang che,\" Davida'n a ti a.",
			"20": "<sup>20</sup> Tin, Davida'n mipui zawng zawng hnenah chuan, \"khai le, LALPA in Pathian chu fak rawh u,\" a ti a. Tichuan mipui zawng zawng chuan LALPA, an pi leh pute Pathian chu an fak a, an kun a, LALPA leh lal chu chibai an buk a.",
			"21": "<sup>21</sup> Tin, a tukah chuan LALPA hnenah inthawina'n leh LALPA hnena halral thilhlan atan bawngpa sangkhat leh berampa sangkhat leh beram no sangkhat, an in tur thilhlante nen Israel-ho zawng zawng atan inthawina tam tak an hlan a;",
			"22": "<sup>22</sup> tichuan chu mi ni chuan LALPA hmaah hlim takin an ei a, an in hlawm a. Tin, Davida'n fapa Solomona chu lalah an siam nawn leh a, LALPA mi lian atan hriak an thih a, tin, Zadoka chu puithiam atan.",
			"23": "<sup>23</sup> Chutichuan Soilomona chu LALPA lalthutthlenga chuan a pa Davida aiah lalan a thu ta a, hmuingil takin a awm a; tin, Israel-ho zawng zawngin a thu an zawm a.",
			"24": "<sup>24</sup> Tin, hotu zawng zawng leh mi chak huaisente leh lal Davida fapa dang zawng zawngte chu lal Solomona lakah an intulut a.",
			"25": "<sup>25</sup> Tin, LALPA'n Solomona chu Israel-hoz awng zang mithmuhah nasa takin a tiropui a, Israel rama a hmaa lalte zinga tuma la chan ngai loh, lal nihna ropi tak a chantir a.",
			"26": "<sup>26</sup> Jesaia fapa Davida chu Israel-o zawng zawng chunga a lal a.",
			"27": "<sup>27</sup> Israelho chunga a lal chhung chu kum sawm li a ni; Hebraon khuaah kum sarih a lal a, Jerusalem-ah kum sawmthum leh kum thum a lal a.",
			"28": "<sup>28</sup> Tin, upa tak, tarkuna damin, hausa tak leh chawimawi hlawhin a thi a; tin, afapa Solomona chu a aiawhin a lal ta a.",
			"29": "<sup>29</sup> Ngai teh, lal Davida chanchin chu a tir atanga a tawp thlengin Zawlnei Samuela lehkhabuah te, zawlnei Nathana lehkhabuah te, Zawlnei Gada lehkhabuah te a chuang a,a lal thu te, a thiltihtheihzia zawng zawng te, amah leh Israel-ho chung leh ram zawng zawng chunga thil lo awm dante nen.",
			"30": "<sup>30</sup> a lal thu te, a thiltihtheihzia zawng zawng te, amah leh Israel-ho chung leh ram zawng zawng chunga thil lo awm dante nen."
		}
	}
};
