var book = {
	"name": "1TANTARA",
	"numChapters": 29,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ADAMA, Seta, Enosy,",
			"2": "<sup>2</sup> Kenana, Mahalalila, Jareda,",
			"3": "<sup>3</sup> Enoka, Metosela, Lameka,",
			"4": "<sup>4</sup> Noa, Sema, Hama ary Jafeta.",
			"5": "<sup>5</sup> Ny zanakalahin’i Jafeta dia Gomera sy Magoga sy Maday sy Javana sy Tobala sy Maseka ary Tirasa.",
			"6": "<sup>6</sup> Ary ny zanakalahin’i Gomera dia Askenaza sy Rifata ary Togarma.",
			"7": "<sup>7</sup> Ary ny zanakalahin’i Javana dia Elisaha sy Tarsisy sy ny Kitima ary ny Dodanita.",
			"8": "<sup>8</sup> Ny zanakalahin’i Hama dia Kosy sy Mizraima sy Pota ary Kanana.",
			"9": "<sup>9</sup> Ary ny zanakalahin’i Kosy dia Seba sy Havila sy Sabta sy Ragama ary Sabteka. Ary ny zanakalahin’i Ragama dia Seba sy Dedana.",
			"10": "<sup>10</sup> Ary Kosy niteraka an’i Nimroda; izy no voalohany izay tonga olona mahery teo ambonin’ny tany.",
			"11": "<sup>11</sup> Ary Mizraima niteraka ny Lodita sy ny Anamita sy ny Lehabita sy ny Naftohita",
			"12": "<sup>12</sup> sy ny Patrosita sy ny Kaslohita (izay nihavian’ny Filistina) ary ny Kaftorita.",
			"13": "<sup>13</sup> Ary Kanana niteraka an’i Sidona, lahimatoany, sy Heta",
			"14": "<sup>14</sup> ary ny Jebosita sy ny Amorita sy ny Girgasita",
			"15": "<sup>15</sup> sy ny Hivita sy ny Arkita sy ny Sinita",
			"16": "<sup>16</sup> sy ny Arvadita sy ny Zemarita ary ny Hamatita.",
			"17": "<sup>17</sup> Ny zanakalahin’i Sema dia Elama sy Asyra sy Arpaksada sy Loda sy Arama sy Oza sy Hola sy Gatera ary Maseka.",
			"18": "<sup>18</sup> Ary Arpaksada niteraka an’i Sela; ary Sela niteraka an’i Ebera.",
			"19": "<sup>19</sup> Ary Ebera niteraka roa lahy: ny anaran’ny iray natao hoe Palega, satria tamin’ny androny no nizarana ny tany; ary Joktana no anaran’ny rahalahiny.",
			"20": "<sup>20</sup> Ary Joktana niteraka an’i Almodada sy Salefa sy Hazarmaveta sy Jera",
			"21": "<sup>21</sup> sy Hadorama sy Ozala sy Dikla",
			"22": "<sup>22</sup> sy Ebala sy Abimaela sy Sheba",
			"23": "<sup>23</sup> sy Ofira sy Havila ary Jobaba; ireo rehetra ireo no zanakalahin’i Joktana.",
			"24": "<sup>24</sup> Sema, Arpaksada, Sela,",
			"25": "<sup>25</sup> Ebera, Palega, Reo,",
			"26": "<sup>26</sup> Seroga, Nahora, Tera,",
			"27": "<sup>27</sup> Abrama (dia Abrahama izany).",
			"28": "<sup>28</sup> NY zanakalahin’i Abrahama dia Isaka sy Isimaela.",
			"29": "<sup>29</sup> Izao no taranany: Nebaiota, lahimatoan’Isimaela, dia Kedara sy Abdela sy Mibsama",
			"30": "<sup>30</sup> sy Misma sy Doma sy Masa sy Hadada sy Tema",
			"31": "<sup>31</sup> sy Jetora sy Nafisy ary Kedma. Ireo no zanakalahin’Isimaela.",
			"32": "<sup>32</sup> Ary ny zanakalahin’i Ketora, vaditsindranon’i Abrahama: izy no niteraka an’i Zimrama sy Joksana sy Medana sy Midiana sy Jisbaka ary Soaha. Ary ny zanakalahin’i Joksana dia Sheba sy Dedana.",
			"33": "<sup>33</sup> Ary ny zanakalahin’i Midiana dia Efaha sy Efera sy Hanoka sy Abida ary Eldaha. Ireo rehetra ireo no zanakalahin’i Ketora.",
			"34": "<sup>34</sup> Ary Abrahama niteraka an’Isaka. Ny zanakalahin’i Isaka dia Esao sy Isiraely.",
			"35": "<sup>35</sup> Ny zanakalahin’i Esao dia Elifaza sy Regoela sy Jeosy sy Jalama ary Kora.",
			"36": "<sup>36</sup> Ny zanak’i Elifaza dia Temana sy Omara sy Zefo sy Gatama sy Kenaza sy Timna ary Amaleka.",
			"37": "<sup>37</sup> Ny zanakalahin’i Regoela dia Nahata sy Zera sy Sama ary Miza.",
			"38": "<sup>38</sup> Ary ny zanakalahin’i Seïra dia Lotana sy Sobala sy Zibona sy Ana sy Disona sy Ezera ary Disana.",
			"39": "<sup>39</sup> Ary ny zanakalahin’i Lotana dia Hory sy Homama; ary Timna no anabavin’i Lotana.",
			"40": "<sup>40</sup> Ary ny zanakalahin’i Sobala dia Aliana sy Manahata sy Ebala sy Sefy ary Onama. Ary ny zanakalahin’i Zibona dia Aia sy Ana.",
			"41": "<sup>41</sup> Ary ny zanakalahin’i Ana dia Disona. Ary ny zanakalahin’i Disona dia Hamrana sy Esbana sy Jitrana ary Kerana.",
			"42": "<sup>42</sup> Ary ny zanakalahin i Ezera dia Bilana sy Zavana ary Jakana. Ny zanakalahin’i Disana dia Oza sy Arana.",
			"43": "<sup>43</sup> Ary izao no mpanjaka izay nanjaka tany amin’ny tany Edoma, fony tsy mbola nisy mpanjaka nanjaka tamin’ny Zanak’Isiraely: Bela, zanak’i Beora; ary Dinaba no anaran’ny tanànany.",
			"44": "<sup>44</sup> Ary maty Bela, dia nanjaka nandimby azy Jobaba, zanak’i Zera, avy any Bozra.",
			"45": "<sup>45</sup> Ary maty Jobaba, dia nanjaka nandimby azy Hosama, avy any amin’ny tanin’ny Temanita.",
			"46": "<sup>46</sup> Ary maty Hosama, dia nanjaka nandimby azy Hadada, zanak’i Bedada, izay namely ny Midianita tany amin’ny tany Moaba; ary Avita no anaran’ny tanànany.",
			"47": "<sup>47</sup> Ary maty Hadada, dia nanjaka nandimby azy Samla avy any Masreka.",
			"48": "<sup>48</sup> Ary maty Samla, dia nanjaka nandimby azy Saoly avy any Rehobota eo amoron’ny Ony.",
			"49": "<sup>49</sup> Ary maty Saoly, dia nanjaka nandimby azy Bala-hanana, zanak’i Akbora.",
			"50": "<sup>50</sup> Ary maty Bala-hanana, dia nanjaka nandimby azy Hadada; ary Pao no anaran’ny tanànany; ary ny anaran’ny vadiny dia Mehetabela, zanakavavin’i Matreda, zanakavavin’i Me-zahaba.",
			"51": "<sup>51</sup> Dia maty koa Hadada. Ary ny loham-pirenen’i Edoma dia Timna loham-pireneny, Alva loham-pireneny, Jetata loham-pireneny,",
			"52": "<sup>52</sup> Oholibama loham-pireneny, Elaha loham-pireneny, Pinona loham-pireneny,",
			"53": "<sup>53</sup> Kenaza loham-pireneny, Temana loham-pireneny, Mibzara loham-pireneny,",
			"54": "<sup>54</sup> Magdiela loham-pireneny, Irama loham-pireneny. Ireo no loham-pirenenen’i Edoma."
		},
		"2": {
			"1": "<sup>1</sup> IZAO no zanakalahin’Isiraely: Robena sy Simeona sy Levy sy Joda sy Isakara sy Zebolona",
			"2": "<sup>2</sup> sy Dana sy Josefa sy Benjamina sy Naftaly sy Gada ary Asera.",
			"3": "<sup>3</sup> Ny zanakalahin’i Joda dia Era sy Onana ary Sela; ireo telo ireo no naterak’i Batsoa Kananita taminy. Ary ratsy fanahy teo imason’i Jehovah Era, lahimatoan’i Joda, dia nahafaty azy Jehovah.",
			"4": "<sup>4</sup> Ary Tamara vinantoni-vavy niteraka an’i Fareza sy Zera taminy. Dimy no zanakalahin’i Joda rehetra.",
			"5": "<sup>5</sup> Ny zanakalahin’i Fareza dia Hezrona sy Hamola.",
			"6": "<sup>6</sup> Ary ny zanakalahin’i Zera dia Zimry sy Etana sy Hemana sy Kalkola ary Dara; dimy lahy no isany.",
			"7": "<sup>7</sup> Ary ny zanakalahin’i Karmy dia Akara, izay nampidi-doza tamin’ny Isiraely, dia ilay nandika ny didy ny amin’ny zavatra voaozona.",
			"8": "<sup>8</sup> Ary ny zanakalahin’i Etana dia Azaria.",
			"9": "<sup>9</sup> Ary ny zanakalahin’i Hezrona izay naterany dia Jeramela sy Rama ary Kelobay;",
			"10": "<sup>10</sup> ary Rama niteraka an’i Aminadaba; ary Aminadaba niteraka an’i Nasona, lohan’ny taranak’i Joda;",
			"11": "<sup>11</sup> ary Nasona niteraka an’i Salma; ary Salma niteraka an’i Boaza;",
			"12": "<sup>12</sup> ary Boaza niteraka an’i Obeda; ary Obeda niteraka an’i Jese;",
			"13": "<sup>13</sup> ary Jese niteraka an’i Eliaba, lahimatoa, sy Abinadaba, lahiaivo, sy Simea, fahatelo,",
			"14": "<sup>14</sup> sy Netanela, fahefatra, sy Raday, fahadimy,",
			"15": "<sup>15</sup> sy Ozema, fahenina, ary Davida, faralahy;",
			"16": "<sup>16</sup> Zeroia sy Abigaila no anabaviny; ary telo ny zanakalahin’i Zeroia, dia Abisay sy Joaba ary Asahela;",
			"17": "<sup>17</sup> ary Abigaila niteraka an’i Amasa; ary Jitra Isimaelita no rain’i Amasa.",
			"18": "<sup>18</sup> Ary Kaleba, zanakalahin’i Hezrona, niteraka tamin’i Azoba vadiny sy tamin’i Jeriota. Izao no zanakalahin’i Azoba: Jasera sy Sobaba ary Ardona.",
			"19": "<sup>19</sup> Ary nony maty Azoba, dia Efrata no nalain’i Kaleba ho vadiny, ary izy niteraka an’i Hora taminy;",
			"20": "<sup>20</sup> ary Hora niteraka an’i Ory; ary Ory niteraka an’i Bezalila.",
			"21": "<sup>21</sup> Ary nony afaka izany, Hezrona niray tamin’ny zanakavavin’i Makira, rain’i Gileada, izay novadiny rehefa enim-polo taona izy, dia niteraka an’i Segoba taminy ravehivavy;",
			"22": "<sup>22</sup> ary Segoba niteraka an’i Jaira, izay nanana tanàna madinika telo amby roa-polo tany amin’ny tany Gileada.",
			"23": "<sup>23</sup> Ary nalain’i Gesora sy Arama tamin’ny Jaïrita ny tanàna madinika an’i Jaïra mbamin’i Kenata sy ny zana-bohiny, dia tanàna enim-polo izany. Ireo rehetra ireo no an’ny zanak’i Makira, rain’i Gileada.",
			"24": "<sup>24</sup> Ary nony maty Hezrona tany Kaleba-efrata, Abia, vadin’i Hezrona, dia niteraka an’i Asora, razamben’ny any Tekoa, taminy.",
			"25": "<sup>25</sup> Ary ny zanakalahin’i Jeramela, lahimatoan’i Hezrona, dia Rama, lahimatoa, sy Bona sy Orena sy Ozema ary Ahia.",
			"26": "<sup>26</sup> Jeramela nanana vady hafa koa atao hoe Atara; izy no renin’i Onama.",
			"27": "<sup>27</sup> Ary ny zanakalahin’i Rama, lahimatoan’i Jeramela, dia Maza sy Jamina ary Ekera.",
			"28": "<sup>28</sup> Ary ny zanakalahin’i Onama dia Samay sy Jada. Ary ny zanakalahin’i Samay dia Nadaba sy Abisora.",
			"29": "<sup>29</sup> Ary ny anaran’ny vadin’i Abisora dia Abihaila; ary izy niteraka an’i Abana sy Molida taminy.",
			"30": "<sup>30</sup> Ary ny zanakalahin’i Nadaba dia Saleda sy Apaima; fa maty momba Saleda.",
			"31": "<sup>31</sup> Ary ny zanakalahin’i Apaima dia Jisy. Ary ny zanakalahin’i Jisy dia Sesana. Ary ny zanakalahin’i Sesana dia Ahelay.",
			"32": "<sup>32</sup> Ary ny zanakalahin’i Jada, rahalahin’i Samay, dia Jatera sy Jonatana; nefa maty momba Jatera.",
			"33": "<sup>33</sup> Ary ny zanakalahin’i Jonatana dia Paleta sy Zaza. Ireo no zanakalahin’i Jeramela.",
			"34": "<sup>34</sup> Ary Sesana tsy nanana zanakalahy, fa vavy avokoa. Ary Sesana nanana mpanompo Egyptiana, Jara no anarany.",
			"35": "<sup>35</sup> Ary ny zanani-vavy nomen’i Sesana ho vadin’i Jara mpanompony; ary izy niteraka an’i Atahy taminy.",
			"36": "<sup>36</sup> Ary Atahy niteraka an’i Natana; ary Natana niteraka an’i Zabada;",
			"37": "<sup>37</sup> ary Zabada niteraka an’i Efiala; ary Efiala niteraka an’i Obeda;",
			"38": "<sup>38</sup> ary Obeda niteraka an’i Jeho; ary Jeho niteraka an’i Azaria;",
			"39": "<sup>39</sup> ary Azaria niteraka an’i Haleza; ary Haleza niteraka an’i Elasa;",
			"40": "<sup>40</sup> ary Elasa niteraka an’i Sismay; ary Sismay niteraka an’i Saloma;",
			"41": "<sup>41</sup> ary Saloma niteraka an’i Jekamia; ary Jekamia niteraka an’i Elisama.",
			"42": "<sup>42</sup> Ary ny zanakalahin’i Kaleba, rahalahin’i Jeramela, dia Mesa, lahimatoa, razamben’ny any Zifa, sy ny zanakalahin’i Maresa, razamben’ny any Hebrona.",
			"43": "<sup>43</sup> Ary ny zanakalahin’i Hebrona dia Kora sy Tapoa sy Rakema ary Sema.",
			"44": "<sup>44</sup> Ary Sema niteraka an’i Rahama, rain’i Jorkeama, ary Rakema niteraka an’i Samay;",
			"45": "<sup>45</sup> ary ny zanakalahin’i Samay dia Maona, ary Maona no razamben’ny any Beti-zora;",
			"46": "<sup>46</sup> ary Efaha, vaditsindranon’i Kaleba, niteraka an’i Harana sy Moza sy Gazeza; ary Harana niteraka an’i Gazeza.",
			"47": "<sup>47</sup> Ary ny zanakalahin’i Jeday dia Ragema sy Jotama sy Gesana sy Paleta sy Efaha ary Safa.",
			"48": "<sup>48</sup> Maka, vaditsindranon’i Kaleba, niteraka an’i Sabera sy Tirana.",
			"49": "<sup>49</sup> Izy koa niteraka an’i Safa, razamben’ny any Madmana, sy Seva, razamben’ny any Makbena sady razamben’ny any Gibea; ary ny zanakavavin’i Kaleba dia Aksa.",
			"50": "<sup>50</sup> Izao no zanakalahin’i Kaleba, zanak’i Hora, lahimatoan’i Efrata: Sobala, razamben’ny any Kiriata-jearima,",
			"51": "<sup>51</sup> Salma, razemben’ny any Betlehema, Harefa, razamben’ny any Beti-gadera.",
			"52": "<sup>52</sup> Ary Sobala, razamben’ny any Kiriata-jearima, nanan-janakalahy, dia Haroe sy Hatsi-hamenokota",
			"53": "<sup>53</sup> ary ny fokon’i Kiriata-jearima koa, dia ny Jitrita sy ny Potita sy ny Somatita ary ny Misraita; avy tamin’ireo no nihavian’ny Zoraïta sy ny Estaolita.",
			"54": "<sup>54</sup> Ny zanakalahin’i Salma dia Betlehema sy ny Netofatita sy Atrota-beti-joaba ary Hatsi-hamanatita sy ny Zoraïta",
			"55": "<sup>55</sup> ary ny fokon’ny mpanoratra izay nonina tany Jabeza, dia ny Tiratita sy ny Simeatita ary ny Sokatita. Ireo no Kenita avy tamin’i Hamata, rain’ny taranak’i Rekaba."
		},
		"3": {
			"1": "<sup>1</sup> ARY izao no zanakalahin’i Davida, izay naterany tao Hebrona: Amnona, zanak’i Ahinoama Jezirelita, no lahimatoa; ary Daniela, zanak’i Abigaila Karmelita, no lahiaivo;",
			"2": "<sup>2</sup> Absaloma, zanak’i Maka, zanakavavin’i Talmay, mpanjakan’i Gesora, no fahatelo; Adonia, zanak’i Hagita, no fahefatra;",
			"3": "<sup>3</sup> Sefatia, zanak’i Abitala, no fahadimy; Jitreama, zanak’i Egla vadiny, no fahenina.",
			"4": "<sup>4</sup> Ireo enina ireo no naterany tao Hebrona; fa tao no nanjakany fito taona sy enim-bolana; ary tany Jerosalema kosa no nanjakany telo amby telo-polo taona.",
			"5": "<sup>5</sup> Ary izao no naterany tany Jerosalema: Simea sy Sobaba sy Natana ary Solomona (ireo efa-dahy ireo no naterak’i Batsoa, zanakavavin’i Amiela)",
			"6": "<sup>6</sup> sy Jibara sy Elisama sy Elifeleta",
			"7": "<sup>7</sup> sy Noga sy Nafega sy Jafia",
			"8": "<sup>8</sup> sy Elisama sy Eliada ary Elifeleta; dia sivy kosa ireo.",
			"9": "<sup>9</sup> Ireo rehetra ireo no zanakalahin’i Davida, afa-tsy ny zanaky ny vaditsindranony sy Tamara, anabavin’ireo.",
			"10": "<sup>10</sup> Ary ny zanakalahin’i Solomona dia Rehoboama, Abia no zanakalahin-dRehoboama, Asa no zanakalahin’i Abia, Josafata no zanakalahin’i Asa,",
			"11": "<sup>11</sup> Jehorama no zanakalahin’i Josafata, Ahazia no zanakalahin’i Jehorama, Joasy no zanakalahin’i Ahazia,",
			"12": "<sup>12</sup> Amazia no zanakalahin’i Joasy, Azaria no zanakalahin’Amazia, Jotama no zanakalahin’i Azaria,",
			"13": "<sup>13</sup> Ahaza no zanakalahin’i Jotama, Hezekia no zanakalahin’i Ahaza, Manase no zanakalahin’i Hezekia,",
			"14": "<sup>14</sup> Amona no zanakalahin’i Manase, Josia no zanakalahin’i Amona.",
			"15": "<sup>15</sup> Ary izao no zanakalahin’i Josia: Johanana, lahimatoa, Joiakima, lahiaivo, Zedekia, fahatelo, Saloma, faralahy.",
			"16": "<sup>16</sup> Ary ny zanakalahin’i Joiakima dia Jekonia, ary Zedekia no zanak’i Jekonia.",
			"17": "<sup>17</sup> Ary ny zanakalahin’Jekonia dia Asira, Sealtiela no zanak’i Asira,",
			"18": "<sup>18</sup> ary Malkirama koa sy Pedaia sy Senazara sy Jekamia sy Hosama ary Nedabia.",
			"19": "<sup>19</sup> Ary ny zanakalahin’i Pedaia dia Zerobabela sy Simey. Ary ny zanakalahin’i Zerobabela dia Mesolama sy Hanania ary Selomita, anabaviny,",
			"20": "<sup>20</sup> ary Hasoba sy Ohela sy Berekia sy Hasadia ary Josaba-heseda; dimy izy.",
			"21": "<sup>21</sup> Ary ny zanakalahin’i Hanania dia Pelatia sy Jisaia sy ny zanakalahin’i Rafaia sy ny zanakalahin’i Arnana sy ny zanakalahin’i Obadia ary ny zanakalahin’i Sekania.",
			"22": "<sup>22</sup> Ary ny zanakalahin’i Sekania dia Semaia. Ary ny zanakalahin’i Semaia dia Hatosy sy Jigala sy Baria sy Nearia ary Safata; enina izy.",
			"23": "<sup>23</sup> Ary ny zanakalahin’i Nearia dia Elioenay sy Hezekia ary Azrikama; telo izy.",
			"24": "<sup>24</sup> Ary ny zanakalahin’i Elioenay dia Hodaia sy Eliasiba sy Pelaia sy Akoba sy Johanana sy Delaia ary Anany; fito izy."
		},
		"4": {
			"1": "<sup>1</sup> NY zanakalahin’i Joda dia Fareza sy Hezrona sy Karmy sy Hora ary Sobala.",
			"2": "<sup>2</sup> Ary Reaia, zanakalahin’i Sobala, niteraka an’i Jahata; ary Jahata niteraka an’i Ahomay sy Lahada. Ireo no fokon’ny Zoraïta.",
			"3": "<sup>3</sup> Ary izao no avy amin’ny rain’i Etama: Jezirela sy Jisma ary Jidbasy, ary Hazelelpony no anaran’ny anabaviny,",
			"4": "<sup>4</sup> ary Penoela, rain’i Gedora, sy Ezera, rain’i Hosa. Ireo no zanak’i Hora, lahimatoan’i Efrata, razamben’ny any Betlehema.",
			"5": "<sup>5</sup> Ary Asora, razamben’ny any Tekoa, namporafy, dia Hela sy Nara.",
			"6": "<sup>6</sup> Ary Nara niteraka an’i Ahozama sy Hafera sy Temeny ary Hahastary. Ireo no zanakalahin’i Nara.",
			"7": "<sup>7</sup> Ary ny zanakalahin’i Hela dia Zareta sy Jizara ary Etnana.",
			"8": "<sup>8</sup> Ary Hakoza niteraka an’i Anoba sy Zobeba ary ny fokon’i Aharela, zanakalahin’i Haroma.",
			"9": "<sup>9</sup> Ary Jabeza dia nalaza noho ny rahalahiny; ary ny reniny nanao ny anarany hoe Jabeza, fa hoy izy: Ory aho, raha niteraka azy.",
			"10": "<sup>10</sup> Ary Jabeza niantso an’Andriamanitry ny Isiraely hoe: Enga anie ka hotahinao tokoa aho, ka hohalalahinao ny fari-taniko, ary ho amiko ny tànanao, ka harovanao amin’ny ratsy aho, mba tsy hampahory ahy izany. Ary dia nomen’Andriamanitra azy ny nangatahiny.",
			"11": "<sup>11</sup> Ary Keloba, rahalahin’i Soha, niteraka an’i Mehira, rain’i Estona;",
			"12": "<sup>12</sup> ary Estona niteraka an’i Betirafa sy Pasea ary Tehina, rain’Ir-nahasa. Ireo dia mponina tao Reka.",
			"13": "<sup>13</sup> Ary ny zanakalahin’i Kenaza dia Otniela sy Seraia; ary ny zanakalahin’i Otniela dia Hatata.",
			"14": "<sup>14</sup> Ary Meonotahy niteraka an’i Ofra; ary Seraia niteraka an’i Joaba, razamben’ny ao an-dohasahan’ny mpiasa; fa mpiasa ireo.",
			"15": "<sup>15</sup> Ary ny zanakalahin’i Kaleba, zanakalahin’i Jefone, dia Iro sy Elaha sy Nama; ary ny zanakalahin’i Elaha dia Kenaza.",
			"16": "<sup>16</sup> Ary ny zanakalahin’i Jehalalila dia Zifa sy Zifaha sy Tiria ary Asarela.",
			"17": "<sup>17</sup> Ary ny zanakalahin’i Ezra dia Jatera sy Mareda sy Efera ary Jalona; ary izy niteraka an’i Miriama sy Samay ary Jisba, razamben’ny any Estemoa.",
			"18": "<sup>18</sup> Ary Jehodia vadiny niteraka an’i Jareda, razamben’ny any Gedora, sy Hebera, razamben’ny any Soko, ary Jakotiela, razamben’ny any Zanoa. Ireo no zanakalahin’i Bitia, zanakavavin’i Farao, izay novadin’i Mareda.",
			"19": "<sup>19</sup> Ary ny zanakalahin’i Hodia vadiny, izay anabavin’i Nahama, dia ny razamben’ny an’i Keila Garmita sy Estemoa Makatita.",
			"20": "<sup>20</sup> Ary ny zanakalahin’i Simona dia Amnona sy Rina sy Ben-hanana ary Tilona. Ary ny zanakalahin’i Jisy dia Zoheta sy Ben-zoheta.",
			"21": "<sup>21</sup> Ary ny zanakalahin’i Sela, zanak’i Joda, dia Era, rain’i Leka, sy Lada, rain’i Maresa, sy ny fokon’ny mpanao rongony fotsy madinika amin’ny ankohonan’i Asbea;",
			"22": "<sup>22</sup> ary Jokima sy ny mponina tao Kozeba ary Joasy sy Sarafa, izay samy nanapaka tany Moaba, ary Jasobi-lehema. Ary efa ela izany zavatra izany.",
			"23": "<sup>23</sup> Ireo no mpanefy vilany sy mponina tany Netaima sy Gadera; tany no nitoerany tamin’ny mpanjaka hanao ny asany.",
			"24": "<sup>24</sup> NY zanakalahin’i Simeona dia Nemoela sy Jamina sy Jariba sy Zera ary Saoly,",
			"25": "<sup>25</sup> dia Saloma no zanakalahin’i Saoly, Mibsama no zanakalahin’i Saloma, Misma no zanakalahin’i Mibsama.",
			"26": "<sup>26</sup> Ary ny zanakalahin’i Misma dia Hamoela, ary Zakora no zanakalahin’i Hamoela, Simey no zanakalahin’i Zakora.",
			"27": "<sup>27</sup> Ary Simey nanan-janaka enina ambin’ny folo lahy sy enim-bavy; fa ny rahalahiny tsy nanan-janaka maro, sady tsy nihamaro tahaka ny taranak’i Joda ny fokony rehetra.",
			"28": "<sup>28</sup> Ary izy nonina tao Beri-sheba sy Molada sy Hazara-soala",
			"29": "<sup>29</sup> sy Bila sy Ezema sy Tolada",
			"30": "<sup>30</sup> sy Betoela sy Horma sy Ziklaga",
			"31": "<sup>31</sup> sy Beti-markabota sy Hazora-sosima sy Beti-biry ary Saraima. Ireo no tanàna nonenany mandra-panjakan’i Davida.",
			"32": "<sup>32</sup> Ary ny zana-bohiny dia Etama sy Aina sy Rimona sy Tokena ary Asana, tanàna dimy ireo,",
			"33": "<sup>33</sup> ary ny zana-bohiny rehetra koa izay manodidina izany tanàna izany hatrany Bala. Ireo no fonenany sady nanana fitantaram-pirazanana manokana izy.",
			"34": "<sup>34</sup> Ary Mesobaba sy Jamleka sy Josa, zanak’i Amazia,",
			"35": "<sup>35</sup> ary Joela sy Jeho, zanak’i Josibia, zanak’i Seraia, zanak’i Asiela,",
			"36": "<sup>36</sup> ary Elioenay sy Jakoba sy Jesohaia sy Asaia sy Adiela sy Jesimiela sy Benaia",
			"37": "<sup>37</sup> sy Ziza, zanak’i Sify, zanak’i Alona, zanak’i Jedaia, zanak’i Simry, zanak’i Semaia;",
			"38": "<sup>38</sup> ireo voalaza anarana ireo dia samy lohany tamin’ny fokony avy; ary efa nihamaro indrindra ny fianakaviany.",
			"39": "<sup>39</sup> Ary nankany amin’ny fidirana ho any Gedora izy, dia hatramin’ny ilany atsinanana amin’ny lohasaha, hitady ahitra ho an’ny ondriny aman-osiny.",
			"40": "<sup>40</sup> Ary nahita ahitra maitso sady tasra izy, ary malalaka ny tany sady nandry fahizay; nefa ny taranak’i Hama no nonina tany taloha.",
			"41": "<sup>41</sup> Ary ireo voasoratra anarana ireo dia tonga tao tamin’ny andron’i Hezekia, mpanjakan’ny Joda, ka namely ny lay sy ny fonenana hitany tao sy nandringana azy rehetra mandraka ankehitriny, dia nonina nisolo azy; fa nisy ahitra ho an’ny ondriny aman-osiny tany.",
			"42": "<sup>42</sup> Ary nisy lehilahy dimam-jato tamin’ny taranak’i Simeona nankany amin’ny tendrombohitra Seïra ka nanana an’i Peletia sy Nearia sy Refaia ary Oziela, zanakalahin’i Jisy, ho mpifehy azy.",
			"43": "<sup>43</sup> Ary namely ny sisa afaka tamin’ny Amalekita izy, ka dia monina ao mandraka andro-any."
		},
		"5": {
			"1": "<sup>1</sup> ARY ny zanakalahin’i Robena, lahimatoan’Isiraely (fa izy no lahimatoa; saingy ny nandotoany ny farafaran-drainy no nanomezana ny fizokiany ho an’ny zanak’i Josefa, zanak’Isiraely; nefa tsy voasoratra ho zokiny amin’ny fitantaram-pirazanana ireo;",
			"2": "<sup>2</sup> fa Joda nisandratra noho ny rahalahiny, ary izy no nihavian’ny mpanapaka; nefa ny fizokiana kosa dia an’i Josefa ihany),–",
			"3": "<sup>3</sup> ny zanakalahin’i Robena, lahimatoan’Isiraely, dia Hanoka sy Palo sy Hezrona ary Karmy.",
			"4": "<sup>4</sup> Ny zanakalahin’i Joela dia Semaia, ary Goga no zanakalahin’i Semaia, Simey no zanakalahin’i Goga,",
			"5": "<sup>5</sup> Mika no zanakalahin’i Simey, Reaia no zanakalahin’i Mika, Bala no zanakalahin’i Reaia,",
			"6": "<sup>6</sup> Bera no zanakalahin’i Bala, izay nobaboin’i Tiglato-pilesera, mpanjakan’i Asyria; izy no loham-pirenen’ny Robenita.",
			"7": "<sup>7</sup> Ary ny rahalahiny araka ny fokony avy, raha nisaina ny firazanan’ny fireneny, dia Jeiela lohany sy Zakaria",
			"8": "<sup>8</sup> ary Bela, zanak’i Azaza, zanak’i Sema, zanak’i Joela, izay nonina tao Aroera hatrany Nebo sy Bala-meona.",
			"9": "<sup>9</sup> Ary nonina tany atsinanana hatrany akaikin’ny efitra ka hatramin’ny ony Eofrata izy; fa ny ombiny aman-ondriny nihamaro teny amin’ny tany Gileada.",
			"10": "<sup>10</sup> Ary tamin’ny andron’i Saoly dia niady tamin’ny Hagarita izy ka naharesy azy; ary nonina an-day eran’ny ilany atsinanana rehetra amin’i Gileada izy.",
			"11": "<sup>11</sup> Ary ny taranak’i Gada nonina tandrifiny, teny amin’ny tany Basana hatrany Saleka;",
			"12": "<sup>12</sup> Joela no lohany, dia Safama no manarakaraka, dia Janay sy Safata tany Basana.",
			"13": "<sup>13</sup> Ary ny rahalahiny araka ny fianakaviany avy dia Mikaela sy Mesolama sy Seba sy Joray sy Jakana sy Zia ary Ebera; fito izy.",
			"14": "<sup>14</sup> Ireo no zanakalahin’i Abihaila, zanak’i Hory, zanak’i Jaroa, zanak’i Gileada, zanak’i Mikaela, zanak’i Jesisay, zanak’i Jado, zanak’i Boza;",
			"15": "<sup>15</sup> Ahia, zanak’i Abdiela, zanak’i Gony, no lohan’ny fianakaviany.",
			"16": "<sup>16</sup> Ary nonina tany Gileada sy tany Basana sy tamin’ny zana-bohiny ary tamin’ny tany rehetra an-tsisin’i Sarôna manodidina izy.",
			"17": "<sup>17</sup> Ireo rehetra ireo dia nisaina araka ny firazanany tamin’ny andron’i Jotama, mpanjakan’ny Joda, sy tamin’ny andron’i Jeroboama, mpanjakan’ny Isiraely.",
			"18": "<sup>18</sup> Ny taranak’i Robena sy ny Gadita ary ny antsasaky ny firenen’i Manase, izay isan’ny lehilahy mahery, ka lehilahy mahatana ampinga sy sabatra ary mahahenjana tsipìka sady zatra ny miady, dia enim-polo amby fiton-jato amby efatra arivo sy efatra alina no isan’ny azo nalefa hanafika.",
			"19": "<sup>19</sup> Ary niady tamin’ny Hagarita sy tamin’i Jetora sy Nafisy ary Nodaba izy.",
			"20": "<sup>20</sup> Ary nombana hamely ireo izy, ka dia voatolotra teo an-tànany ny Hagarita mbamin’izay rehetra nomba azy, satria nitaraina tamin’Andriamanitra teo amin’ny ady izy, ary nekeny ny fangatahany noho ny fahatokiany Azy.",
			"21": "<sup>21</sup> Ary nobaboiny ny bibiny, dia rameva dimy alina sy ondry aman-osy dimy alina amby roa hetsy sy boriky roa arivo ary olona iray hetsy;",
			"22": "<sup>22</sup> fa maro ny faty niampatrampatra, satria avy tamin’Andriamanitra ny ady. Ary nonina nandimby ireo izy mandra-pahatongan’ny namaboana azy kosa.",
			"23": "<sup>23</sup> Ary ny taranaky ny antsasaky ny firenen’i Manase dia nonina tamin’ny tany; ary nihamaro izy hatrany Basana ka hatrany Bala-hermona sy Senira ary hatramin’ny tendrombohitra Hermona.",
			"24": "<sup>24</sup> Ary izao no lohan’ny fianakaviany: Efera sy Jisy sy Eliala sy Azriela sy Jeremia sy Hodavia ary Jadiela; lehilahy mahery ireo ary malaza sady samy lohan’ny fianakaviany avy.",
			"25": "<sup>25</sup> Ary nivadika tamin’Andriamanitry ny razany ireo, fa nijangajanga nanaraka ny andriamanitry ny tompon-tany, izay naringan’Andriamanitra tsy ho eo anoloany.",
			"26": "<sup>26</sup> Ary Andriamanitry ny Isiraely namporisika an’i Pola, mpanjakan’i Asyria, sy Tiglato-pilesera, mpanjakan’i Asyria, ka dia nobaboin’ireo ny Robenita sy ny Gadita sy ny antsasaky ny firenen’i Manase ka nentiny tany Hala sy Habora sy Hara ary tany amoron’ny ony Gozana ambaraka andro-any.",
			"27": "<sup>27</sup> NY zanakalahin’i Levy dia Gersona sy Kehata ary Merary.",
			"28": "<sup>28</sup> Ary ny zanakalahin’i Kehata dia Amrama sy Jizara sy Hebrona ary Oziela.",
			"29": "<sup>29</sup> Ary ny zanak’i Amrama dia Arona sy Mosesy ary Miriama. Ny zanakalahin’i Arona dia Nadaba sy Abiho sy Eleazara ary Itamara.",
			"30": "<sup>30</sup> Eleazara niteraka an’i Finehasa; ary Finehasa niteraka an’i Abisoa;",
			"31": "<sup>31</sup> ary Abisoa niteraka an’i Boky; ary Boky niteraka an’i Ozy;",
			"32": "<sup>32</sup> ary Ozy niteraka an’i Zerahia; ary Zerahia niteraka an’i Meraiota;",
			"33": "<sup>33</sup> ary Meraiota niteraka an’i Amaria; ary Amaria niteraka an’i Ahitoba;",
			"34": "<sup>34</sup> ary Ahitoba niteraka an’i Zadoka; ary Zadoka niteraka an’i Ahimaza;",
			"35": "<sup>35</sup> ary Ahimaza niteraka an’i Azaria; ary Azaria niteraka an’i Johanana;",
			"36": "<sup>36</sup> ary Johanana niteraka an’i Azaria (ilay nanao fisoronana tao amin’ny trano nataon’i Solomona tany Jerosalema);",
			"37": "<sup>37</sup> ary Azaria niteraka an’i Amaria; ary Amaria niteraka an’i Ahitoba;",
			"38": "<sup>38</sup> ary Ahitoba niteraka an’i Zadoka; ary Zadoka niteraka an’i Saloma;",
			"39": "<sup>39</sup> ary Saloma niteraka an’i Hilkia; ary Hilkia niteraka an’i Azaria;",
			"40": "<sup>40</sup> ary Azaria niteraka an’i Seraia; ary Seraia niteraka an’i Jozadaka;",
			"41": "<sup>41</sup> ary dia lasan-ko babo Jozadaka, nony voatolotr’i Jehovah ho babon’i Nebokadnezara ny Joda sy Jerosalema."
		},
		"6": {
			"1": "<sup>1</sup> Ny zanakalahin’i Levy dia Gersona sy Kehata ary Merary.",
			"2": "<sup>2</sup> Ary izao no anaran’ny zanakalahin’i Gersona: Libny sy Semey.",
			"3": "<sup>3</sup> Ary ny zanakalahin’i Kehata dia Amrama sy Jizara sy Hebrona ary Oziela.",
			"4": "<sup>4</sup> Ny zanakalahin’i Merary dia Maly sy Mosy. Ary izao no fokon’ny Levita araka ny rainy:",
			"5": "<sup>5</sup> Avy amin’i Gersona: Libny no zanak’i Gersona, Jahata no zanak’i Libny, Zima no zanak’i Jahata,",
			"6": "<sup>6</sup> Joa no zanak’i Zima, Ido no zanak’i Joa, Zera no zanak’Ido, Jeateray no zanak’i Zera.",
			"7": "<sup>7</sup> Ny zanakalahin’i Kehata dia Aminadaba, Kora no zanakalahin’i Aminadaba, Asira no zanakalahin’i Kora,",
			"8": "<sup>8</sup> Elkana no zanakalahin’i Asira, Ebiasafa no zanakalahin’i Elkana, Asira no zanakalahin’i Ebiasafa,",
			"9": "<sup>9</sup> Tahata no zanakalahin’i Asira, Oriela no zanakalahin’i Tahata, Ozia no zanakalahin’i Oriela, ary Saoly no zanakalahin’i Ozia.",
			"10": "<sup>10</sup> Ary ny zanakalahin’i Elkana dia Amasay sy Ahimota.",
			"11": "<sup>11</sup> Ny amin’i Elkana, ny zanani-lahy dia Zofay, ary Nahata no zanakalahin’i Zofay,",
			"12": "<sup>12</sup> Eliaba no zanakalahin’i Nahata, Jerohama no zanakalahin’i Eliaba, Elkana no zanakalahin’i Jerohama.",
			"13": "<sup>13</sup> Ary ny zanakalahin’i Samoela dia Vasny lahimatoa sy Abia.",
			"14": "<sup>14</sup> Ny zanakalahin’i Merary dia Maly, Libny no zanakalahin’i Maly, Semey no zanakalahin’i Libny, Oza no zanakalahin’i Semey,",
			"15": "<sup>15</sup> Simea no zanakalahin’i Oza, Hagia no zanakalahin’i Simea, Asaia no zanakalahin’i Hagia.",
			"16": "<sup>16</sup> Ary izao no notendren’i Davida ho mpiventy hira tao amin’ny tranon’i Jehovah, rehefa voapetraka tao ny fiara,",
			"17": "<sup>17</sup> ka nanao hira fanompoam-pivavahana izy tao amin’ny tabernakely, dia ny trano-lay fihaonana, ambara-panaon’i Solomona ny tranon’i Jehovah tany Jerosalema; koa raha nanao ny raharahany izy, dia nitsangana araka ny filaharany avy,–",
			"18": "<sup>18</sup> dia izao no nanompo mbamin’ny zanany: Tamin’ny zanakalahin’ny Kehatita dia Hemana mpihira, zanakalahin’i Joela, zanakalahin’i Samoela,",
			"19": "<sup>19</sup> zanakalahin’i Elkana, zanakalahin’i Jerohama, zanakalahin’i Eliala, zanakalahin’i Toa.",
			"20": "<sup>20</sup> zanakalahin’i Zofa, zanakalahin’i Elkana, zanakalahin’i Mahata, zanakalahin’i Amasay,",
			"21": "<sup>21</sup> zanakalahin’i Elkana, zanakalahin’i Joela, zanakalahin’i Azaria, zanakalahin’i Zefania,",
			"22": "<sup>22</sup> zanakalahin’i Tahata, zanakalahin’i Asira, zanakalahin’i Ebiasafa, zanakalahin’i Kora,",
			"23": "<sup>23</sup> zanakalahin’i Jizara, zanakalahin’i Kehata, zanakalahin’i Levy, zanak’Isiraely.",
			"24": "<sup>24</sup> Ary Asafa rahalahiny, izay nitsangana teo amin’ny ankavanany, dia Asafa, zanakalahin’i Berekia, zanakalahin’i Simea,",
			"25": "<sup>25</sup> zanakalahin’i Mikaela, zanakalahin’i Beseia, zanakalahin’i Malkia,",
			"26": "<sup>26</sup> zanakalahin’i Etny, zanakalahin’i Zera, zanakalahin’i Adaia,",
			"27": "<sup>27</sup> zanakalahin’i Etana, zanakalahin’i Zima, zanakalahin’i Simey,",
			"28": "<sup>28</sup> zanakalahin’i Jahata, zanakalahin’i Gersona, zanakalahin’i Levy.",
			"29": "<sup>29</sup> Ary ny rahalahin’ireo kosa, dia ny zanakalahin’i Merary, nitsangana teo amin’ny ankaviany: Etana, zanakalahin’i Kisy, zanakalahin’i Abdia, zanakalahin’i Maloka,",
			"30": "<sup>30</sup> zanakalahin’i Hasabia, zanakalahin’i Amazia, zanakalahin’i Hilkia,",
			"31": "<sup>31</sup> zanakalahin’i Amzy, zanakalahin’i Bany, zanakalahin’i Samera,",
			"32": "<sup>32</sup> zanakalahin’i Maly, zanakalahin’i Mosy, zanakalahin’i Merary, zanakalahin’i Levy.",
			"33": "<sup>33</sup> Ary ny Levita rahalahin’ireo koa dia efa nomena hanao ny fanompoana rehetra ao amin’ny tabernakelin’ny tranon’Andriamanitra.",
			"34": "<sup>34</sup> Fa Arona sy ny zanani-lahy no nanatitra teo ambonin’ny alitara fandoroana ny fanatitra dorana sy teo ambonin’ny alitara fandoroana ditin-kazo manitra, mba hanao ny raharaha rehetra ao amin’ny fitoerana masina indrindra sy hanao fanavotana ho an’ny Isiraely, araka izay rehetra nandidian’i Mosesy, mpanompon’Andriamanitra.",
			"35": "<sup>35</sup> Ary ny zanakalahin’i Arona dia Eleazara, ary Finehasa no zanakalahin’i Eleazara, Abisoa no zanakalahin’i Finehasa,",
			"36": "<sup>36</sup> Boky no zanakalahin’i Abisoa, Ozy no zanakalahin’i Boky, Zerahia no zanakalahin’i Ozy,",
			"37": "<sup>37</sup> Meraiota no zanakalahin’i Zerahia, Amaria no zanakalahin’i Meraiota, Ahitoba no zanakalahin’i Amaria,",
			"38": "<sup>38</sup> Zadoka no zanakalahin’i Ahitoba, Ahimaza no zanakalahin’i Zadoka.",
			"39": "<sup>39</sup> Ary izao no fonenany araka ny vohiny tao amin’ny zara-taniny avy, dia ny an’ny taranak’i Arona, avy amin’ny fokon’ny Kehatita; fa azy ny loka:",
			"40": "<sup>40</sup> Nomeny azy Hebrona tao amin’ny tanin’ny Joda sy ny tany manodidina azy.",
			"41": "<sup>41</sup> Fa ny saha momba ny tanàna sy ny vohiny kosa nomeny an’i Kaleba, zanak’i Jefone.",
			"42": "<sup>42</sup> Ary ny taranak’i Arona nomeny an’i Hebrona, tanàna fiarovana, ary Libna sy ny tany manodidina azy ary Jatira sy Estemoa sy ny tany manodidina azy",
			"43": "<sup>43</sup> ary Hilena sy ny tany manodidina azy ary Debira sy ny tany manodidina azy",
			"44": "<sup>44</sup> ary Asana sy ny tany manodidina azy ary Beti-semesy sy ny tany manodidina azy;",
			"45": "<sup>45</sup> ary avy tamin’ny firenen’i Benjamina dia Geba sy ny tany manodidina azy ary Alemeta sy ny tany manodidina azy ary Anotata sy ny tany manodidina azy. Dia telo ambin’ny folo no isan’ny tanànan’izy mianakavy rehetra.",
			"46": "<sup>46</sup> Ary ny an’ny taranak’i Kehata sisa tamin’ny fokon’izany firenena izany dia tanàna folo araka ny filokana tamin’ny antsasaky ny firenen’i Manase.",
			"47": "<sup>47</sup> Ary ny an’ny taranak’i Gersona araka ny fokony dia tanàna telo ambin’ny folo tamin’ny firenen’Isakara sy ny firenen’i Asera sy ny firenen’i Naftaly ary ny firenen’i Manase any Basana.",
			"48": "<sup>48</sup> Ary ny loka azon’ny taranak’i Merary araka ny fokony dia tanàna roa ambin’ny folo avy tamin’ny firenen’i Robena sy ny firenen’i Gada ary ny firenen’i Zebolona.",
			"49": "<sup>49</sup> Dia nomen’ny Zanak’i Isiraely ho an’ny Levita ireo tanàna ireo sy ny tany manodidina azy.",
			"50": "<sup>50</sup> Ary nomeny tamin’ny filokana ireto tanàna tononina ireto avy tamin’ny firenena taranak’i Joda sy ny firenena taranak’i Simeona ary ny firenena taranak’i Benjamina.",
			"51": "<sup>51</sup> Fa ny sasany amin’ny fokon’ny taranak’i Kehata dia nahazo tanàna avy tamin’ny firenen’i Efraima.",
			"52": "<sup>52</sup> Ary nomeny azy Sekema, tanàna fiarovana, tao amin’ny tany havoan’i Efraima sy ny tany manodidina azy ary Gazera sy ny tany manodidina azy",
			"53": "<sup>53</sup> ary Jokmeama sy ny tany manodidina azy ary Beti-horona sy ny tany manodidina azy",
			"54": "<sup>54</sup> ary Aialona sy ny tany manodidina azy ary Gata-rimona sy ny tany manodidina azy;",
			"55": "<sup>55</sup> ary ny avy tamin’ny antsasaky ny firenen’i Manase dia Amera sy ny tany manodidina azy ary Bileama sy ny tany manodidina azy, dia ho an’ny fokon’ny taranak’i Kehata sisa.",
			"56": "<sup>56</sup> Ary ny an’ny taranak’i Gersona dia Golana any Basana sy ny tany manodidina azy ary Astarta, sy ny tany manodidina azy avy tamin’ny antsasaky ny firenen’i Manase;",
			"57": "<sup>57</sup> ary ny avy tamin’ny firenen’Isakara dia Kadesy sy ny tany manodidina azy ary Daberata sy ny tany manodidina azy",
			"58": "<sup>58</sup> ary Ramota sy ny tany manodidina azy ary Anema sy ny tany manodidina azy;",
			"59": "<sup>59</sup> ary ny avy tamin’ny firenen’i Asera dia Masala sy ny tany manodidina azy ary Abdona sy ny tany manodidina azy",
			"60": "<sup>60</sup> ary Hokoka sy ny tany manodidina azy ary Rehoba sy ny tany manodidina azy;",
			"61": "<sup>61</sup> ary ny avy tamin’ny firenen’i Naftaly dia Kadesy any Galilia sy ny tany manodidina azy ary Hamona sy ny tany manodidina azy ary Kiriataima sy ny tany manodidina azy.",
			"62": "<sup>62</sup> Ary ny an’ny sisa, taranak’i Merary, dia avy tamin’ny firenen’i Zebolona, dia Rimono sy ny tany manodidina azy ary Tabara sy ny tany manodidina azy;",
			"63": "<sup>63</sup> ary any an-dafin’i Jordana, tandrifin’i Jeriko, atsinanan’i Jordana, koa dia avy tamin’ny firenen’i Robena, dia Bazera any an-efitra sy ny tany manodidina azy ary Jaza sy ny tany manodidina azy",
			"64": "<sup>64</sup> ary Kedemota sy ny tany manodidina azy ary Mefata sy ny tany manodidina azy;",
			"65": "<sup>65</sup> ary avy tamin’ny firenen’i Gada dia Ramota-gileada sy ny tany manodidina azy ary Mahanaima sy ny tany manodidina azy",
			"66": "<sup>66</sup> ary Hesbona sy ny tany manodidina azy ary Jazera sy ny tany manodidina azy."
		},
		"7": {
			"1": "<sup>1</sup> ARY ny zanakalahin’Isakara dia Tola sy Poa sy Jasoba ary Simrona; efatra izy.",
			"2": "<sup>2</sup> Ary ny zanakalahin’i Tola dia Ozy sy Refaia sy Jeriela sy Jamay sy Jibsama ary Samoela, samy lohan’ny fianakaviany avy, dia zanak’i Tola sady samy lehilahy mahery tamin’ny fokony avy ireo; enin-jato amby roa arivo sy roa alina no isany tamin’ny andron’i Davida.",
			"3": "<sup>3</sup> Ary ny zanakalahin’i Ozy dia Jizrahia. Ary ny zanakalahin’i Jizrahia dia Mikaela sy Obadia sy Joela ary Jisia; dimy izy; loholona avokoa izy rehetra ireo.",
			"4": "<sup>4</sup> Ary nanana miaramila antokotokony izy araka ny fireneny sy ny fianakaviany avy, dia enina arivo amby telo alina; fa maro ny vady aman-janak’ireo.",
			"5": "<sup>5</sup> Ary ny rahalahiny ao amin’ny fokon’ny Isakara rehetra, izay samy lehilahy mahery, ny isan’izy rehetra araka ny firazanany avy dia fito arivo amby valo alina.",
			"6": "<sup>6</sup> Ny zanakalahin’ i Benjamina dia Bela sy Bakera ary Jediaela; telo izy.",
			"7": "<sup>7</sup> Ary ny zanakalahin’i Bela dia Ezbona sy Ozy sy Oziela sy Jerimota ary Iry; dimy izy, ary samy lohan’ny fianakaviany avy sady lehilahy mahery avokoa; ary ny isany araka ny firazanany dia efatra amby telo-polo amby roa arivo sy roa alina.",
			"8": "<sup>8</sup> Ary ny zanakalahin’i Bakera dia Zemira sy Joasy sy Eliezera sy Elioenay sy Omry sy Jerimota sy Abia sy Anatota ary Alameta. Ireo rehetra ireo no zanakalahin’i Bakera.",
			"9": "<sup>9</sup> Ary ny isany araka ny firazanany amin’ny fireneny, izay samy lohan’ny fianakaviany avy sady lehilahy mahery, dia roan-jato sy roa alina.",
			"10": "<sup>10</sup> Ny zanakalahin’i Jediaela dia Bilana; ary ny zanakalahin’i Bilana dia Jeosy sy Benjamina sy Ehoda sy Kenana sy Zetana sy Tarsisy ary Ahisahara.",
			"11": "<sup>11</sup> Ireo zanakalahin’i Jediaela rehetra ireo, araka ny lohan’ny fianakaviany, dia roan-jato amby fito arivo sy iray alina no isan’ny lehilahy mahery azo nalefa hanafika.",
			"12": "<sup>12</sup> Sopima koa sy Hopima, zanakalahin’Iry, ary Hosima, zanakalahin’i Ahera.",
			"13": "<sup>13</sup> Ny zanakalahin’i Naftaly dia Jaziela sy Gony sy Jezera sy Saloma, samy zanakalahin’i Bila ireo.",
			"14": "<sup>14</sup> Ny zanakalahin’i Manase dia Asriela, izay naterany; fa ilay Syriana vaditsindranony kosa no niteraka an’i Makira, rain’i Gileada.",
			"15": "<sup>15</sup> Ary Makira naka vady ho an’i Hopima sy Sopima; ny anaran’ny rahavaviny dia Imaka; ary ny anaran’ny manarakaraka dia Zelofada; ary Zelofada nanan-janakavavy.",
			"16": "<sup>16</sup> Ary Imaka, vadin’i Makira, niteraka zazalahy, ka Paresy no nataony anarany; ary Saresy no anaran’ny rahalahiny; ary ny anaran’ny zanani-lahy dia Olama sy Rakema.",
			"17": "<sup>17</sup> Ary ny zanakalahin’i Olama dia Bedana. Ireo no zanakalahin’i Gileada, zanakalahin’i Makira, zanakalahin’i Manase.",
			"18": "<sup>18</sup> Ary Hamoleketa anabaviny niteraka an’Isehoda sy Abiezera ary Mahala.",
			"19": "<sup>19</sup> Ary ny zanakalahin’i Semida dia Ahiana sy Sekema sy Liky ary Aniama.",
			"20": "<sup>20</sup> Ary ny zanakalahin’i Efraima dia Sotela (Bareda no zanakalahin’i Sotela, Tahata no zanakalahin’i Bareda, Elada no zanakalahin’i Tahata, Tahata no zanakalahin’i Elada,",
			"21": "<sup>21</sup> Zabada no zanakalahin’i Tahata, Sotela no zanakalahin’i Zabada) ary Ezera sy Elada, izay novonoin’ny tompon-tany tao Gata, satria nidina hangalatra ny ombiny aman-ondriny izy.",
			"22": "<sup>22</sup> Ary Efraima, rainy nisaona azy ela, dia avy ny rahalahiny hampionona azy.",
			"23": "<sup>23</sup> Ary nony niray tamin’ny vadiny izy, dia nanan-anaka ravehivavy ka niteraka zazalahy, ary ny anarany nataony hoe Beria, satria nisy fahoriana tao an-tranony tamin’izany. (",
			"24": "<sup>24</sup> Ary ny zanani-vavy dia Sera, izay nanorina an’i Beti-horona ambany sy ambony sy Ozena-sera).",
			"25": "<sup>25</sup> Ary Refa sy Rasefa no zanakalahin’i Beria, Tela no zanakalahin’i Rasefa, Tahana no zanakalahin’i Tela,",
			"26": "<sup>26</sup> Ladana no zanakalahin’i Tahana, Amihoda no zanakalahin’i Ladana, Elisama no zanakalahin’i Amihoda.",
			"27": "<sup>27</sup> Nona no zanakalahin’i Elisama, Josoa no zanakalahin’i Nona.",
			"28": "<sup>28</sup> Ary ny taniny sy ny fonenany dia Betela sy ny zana-bohiny, ary any atsinanana dia Narana, ary any andrefana dia Gazera sy ny zana-bohiny ary Sekema sy ny zana-bohiny hatrany Gaza sy ny zana-bohiny;",
			"29": "<sup>29</sup> ary teo amin’ny sisin-tanin’ny taranak’i Manase dia Beti-sana sy ny zana-bohiny, Tanaka sy ny zana-bohiny, Megido sy ny zana-bohiny, Dora sy ny zana-bohiny Tamin’ireo no nonenan’ny taranak’i Josefa, zanak’Isiraely.",
			"30": "<sup>30</sup> Ny zanakalahin’i Asera dia Jimna sy Jisva sy Jisvy sy Beria; ary Sera no anabavin’ireo.",
			"31": "<sup>31</sup> Ary ny zanakalahin’i Beria dia Hebera sy Malkiela, rain’i Birzavita.",
			"32": "<sup>32</sup> Ary Hebera niteraka an’i Jafieta sy Somera sy Hotama; ary Soha no anabavin’ireo.",
			"33": "<sup>33</sup> Ary ny zanakalahin’i Jafieta dia Pasaka sy Bimala ary Asvata. Ireo no zanakalahin’i Jafieta.",
			"34": "<sup>34</sup> Ary ny zanakalahin’i Samera dia Ahaia sy Rohaga sy Hoba ary Arama.",
			"35": "<sup>35</sup> Ary ny zanakalahin’i Helema rahalahiny dia Zofa sy Jimna sy Selesy ary Amala.",
			"36": "<sup>36</sup> Ny zanakalahin’i Zofa dia Soha sy Harnefera sy Soala sy Bery sy Jimra",
			"37": "<sup>37</sup> sy Bazera sy Hoda sy Sama sy Silsa sy Jitrana ary Bera.",
			"38": "<sup>38</sup> Ary ny zanakalahin’i Jatera dia Jefone sy Pispa ary Ara.",
			"39": "<sup>39</sup> Ary ny zanakalahin’i Ola dia Ara sy Haniela ary Rizia.",
			"40": "<sup>40</sup> Ireo zanakalahin’i Asera rehetra ireo dia samy lohan’ny fianakaviany, olom-boafidy sady mahery ary lehiben’ny loham-pireneny avy. Ary ny nanisana azy, araka ny firazanany, dia enina arivo amby roa alina no isan’ny azo nalefa hanafika."
		},
		"8": {
			"1": "<sup>1</sup> ARY Benjamina niteraka an’i Bela, lahimatoa, sy Asbela, lahiaivo, sy Ahara, fahatelo,",
			"2": "<sup>2</sup> sy Noha, fahefatra, ary Rafa, faralahy.",
			"3": "<sup>3</sup> Ary ny zanakalahin’i Bela dia Adara sy Gera sy Abihoda",
			"4": "<sup>4</sup> sy Abisoa sy Namana sy Ahoa",
			"5": "<sup>5</sup> sy Gera sy Sefofana ary Horama.",
			"6": "<sup>6</sup> Ary izao no zanakalahin’i Ehoda (izao no lohan’ny fianakavian’ny mponina any Geba; ary nitondra azy ho babo tany Manahata,",
			"7": "<sup>7</sup> Namana sy Ahia ary Gera; izy no nitondra azy ho babo),– niteraka an’i Oza sy Ahihoda izy.",
			"8": "<sup>8</sup> Ary Saharaima niteraka tao amin’ny tany Moaba taorian’ny nisaorany an’i Hosima sy Bara vadiny.",
			"9": "<sup>9</sup> Ary Hodesy vadiny no niterahany an’i Jobaba sy Ziba sy Mesa sy Malkama",
			"10": "<sup>10</sup> sy Jeoza sy Sakia ary Mirma. Ireo no zanani-lahy, samy lohan’ny fianakaviany avy.",
			"11": "<sup>11</sup> Ary Hosima no niterahany an’i Abitoba sy Elpala.",
			"12": "<sup>12</sup> Ary ny zanakalahin’i Elpala dia Ebera sy Misama ary Samera, izay nanorina an’i Ono sy Loda sy ny zana-bohiny;",
			"13": "<sup>13</sup> Beria koa sy Sema, izay lohan’ny fianakavian’ny mponina ao Aialona sady nandroaka ny mponina tao Gata;",
			"14": "<sup>14</sup> ary Ahio sy Sasaka sy Jeremota",
			"15": "<sup>15</sup> sy Zebadia sy Arada sy Adera",
			"16": "<sup>16</sup> sy Mikaela sy Jispa sy Joha (zanakalahin’i Beria ireo),",
			"17": "<sup>17</sup> ary Zebadia sy Mesolama sy Hizky sy Hebera",
			"18": "<sup>18</sup> sy Jismeray sy Jizlia sy Jobaba (zanakalahin’i Elpala ireo),",
			"19": "<sup>19</sup> ary Jakima sy Zikry sy Zabdy",
			"20": "<sup>20</sup> sy Elienay sy Ziletahy sy Eliala",
			"21": "<sup>21</sup> sy Adaia sy Beraia sy Simrata (zanakalahin’i Simey ireo),",
			"22": "<sup>22</sup> ary Jispana sy Hebera sy Eliala",
			"23": "<sup>23</sup> sy Abdona sy Zikry sy Hanana",
			"24": "<sup>24</sup> sy Hanania sy Elama sy Antotia",
			"25": "<sup>25</sup> sy Jifdia sy Penoela (zanakalahin’i Sasaka ireo),",
			"26": "<sup>26</sup> ary Samseray sy Seharia sy Atalia",
			"27": "<sup>27</sup> sy Jaresia sy Elia sy Zikry (zanakalahin’i Jerohama ireo).",
			"28": "<sup>28</sup> Ireo no lohan’ny fianakaviany araka ny firazanany, dia samy loholona. Ireo no nonina tany Jerosalema.",
			"29": "<sup>29</sup> Ary tao Gibeona no nonenan’ny razamben’ny an’i Gibeona, ary ny anaran’ny vadiny dia Imaka,",
			"30": "<sup>30</sup> ary Abdona no lahimatoany, dia Zora, dia Kisy, dia Bala, dia Nadaba,",
			"31": "<sup>31</sup> dia Gedora, dia Ahio, dia Zakera.",
			"32": "<sup>32</sup> Ary Miklota niteraka an’i Simea. Ireo koa dia niara-nonina tamin’ny rahalahiny tany Jerosalema nifanandrify fonenana taminy.",
			"33": "<sup>33</sup> Ary Nera niteraka an’i Kisy; ary Kisy niteraka an’i Saoly; ary Saoly niteraka an’i Jonatana sy Malkisoa sy Abinadaba ary Esbala.",
			"34": "<sup>34</sup> Ary ny zanakalahin’i Jonatana dia Meribala; ary Meribala niteraka an’i Mika.",
			"35": "<sup>35</sup> Ary ny zanakalahin’i Mika dia Pitona sy Maleka sy Tarea ary Ahaza.",
			"36": "<sup>36</sup> Ary Ahaza niteraka an’i Joada; ary Joada niteraka an’i Alemeta sy Azmaveta ary Zimry; ary Zimry niteraka an’i Moza;",
			"37": "<sup>37</sup> ary Moza niteraka an’i Binea; Rafa no zanakalahin’i Binea, Elasa no zanakalahin’i Rafa, Azela no zanakalahin’i Elasa",
			"38": "<sup>38</sup> Ary Azela nanana zanaka enina mirahalahy, ka izao no anarany: Azrikama sy Bokero sy Isimaela sy Searia sy Obadia ary Hanana. Ireo rehetra ireo no zanakalahin’i Azela.",
			"39": "<sup>39</sup> Ary ny zanakalahin’i Eseka rahalahiny dia Olama, lahimatoa, sy Jeosy, lahiaivo, ary Elifeleta, faralahy.",
			"40": "<sup>40</sup> Ary ny zanakalahin’i Olama dia lehilahy mahery sady mpandefa zana-tsipìka, ary nanana zanakalahy sy zafy maro izy, dia dimam-polo amby zato. Ireo rehetra ireo no taranak’i Benjamina."
		},
		"9": {
			"1": "<sup>1</sup> DIA nisaina araka ny firazanany avy ny Isiraely rehetra; ary, indro, efa voasoratra ao amin’ny bokin’ny mpanjakan’ny Isiraely izany. Ary ny Joda dia nentina ho babo tany Babylona noho ny fahotany.",
			"2": "<sup>2</sup> Ary ny mponina taloha tamin’ny taniny sy ny tanànany avy dia ny Isiraely sy ny mpisorona sy ny Levita ary ny Netinima.",
			"3": "<sup>3</sup> Ary tany Jerosalema no nonenan’ny sasany amin’ny taranak’i Joda sy Benjamina sy Efraima sy Manase:",
			"4": "<sup>4</sup> dia Otahy, zanak’i Amihoda, zanak’i Omry, zanak’Imry, zanak’i Bany, avy amin’ny taranak’i Fareza, zanak’i Joda.",
			"5": "<sup>5</sup> Ary tamin’ny Silonita dia Asay, lahimatoa, sy ny zanany.",
			"6": "<sup>6</sup> Ary tamin’ny taranak’i Zera dia Jeoela sy ny rahalahiny, dia sivi-folo amby enin-jato.",
			"7": "<sup>7</sup> Ary tamin’ny taranak’i Benjamina dia Salo, zanak’i Mesolama, zanak’i Hodavia, zanak’i Hasenoa,",
			"8": "<sup>8</sup> sy Jibnia, zanak’i Jerohama, sy Elaha, zanak’i Ozy, zanak’i Mikry, sy Mesolama, zanak’i Sefatia, zanak’i Regoela, zanak’i Jibnia;",
			"9": "<sup>9</sup> ary ny isan’ny rahalahiny araka ny firazanany dia enina amby dimam-polo sy sivin-jato. Ireo lehilahy rehetra ireo dia samy lohan’ny fianakaviany avy.",
			"10": "<sup>10</sup> Ary tamin’ny mpisorona dia Jedaia sy Joiariba sy Jakina",
			"11": "<sup>11</sup> sy Azaria, zanak’i Hilkia, zanak’i Mesolama, zanak’i Zadoka, zanak’i Meraiota, zanak’i Ahitoba, mpanapaka ny tranon’Andriamanitra,",
			"12": "<sup>12</sup> ary Adaia, zanak’i Jerohama, zanak’i Pasora, zanak’i Malkia, ary Masay, zanak’i Adiela, zanak’i Jazera, zanak’i Mesolama, zanak’i Mesilemita, zanak’Imera.",
			"13": "<sup>13</sup> Ary ny rahalahin’ireo, izay samy lohan’ny fianakaviany avy, dia enim-polo amby fiton-jato amby arivo; lehilahy mahery amin’ny fanaovana ny fanompoana ao an-tranon’Andriamanitra ireo.",
			"14": "<sup>14</sup> Ary tamin’ny Levita dia Semaia, zanak’i Hasoba, zanak’i Azrikama, zanak’i Hasabia, tamin’ny taranak’i Merary,",
			"15": "<sup>15</sup> ary Bakbakara sy Haresy sy Galala sy Matania, zanak’i Mika, zanak’i Zikry, zanak’i Asafa,",
			"16": "<sup>16</sup> ary Obadia, zanak’i Semaia, zanak’i Galala, zanak’i Jedotona, ary Berekia, zanak’i Asa, zanak’i Elkana, izay nonina tamin’ny vohitry ny Netofatita.",
			"17": "<sup>17</sup> Ary ny mpiandry varavarana dia Saloma sy Akoba sy Talmona sy Ahimana ary ny rahalahiny; fa Saloma no lohany;",
			"18": "<sup>18</sup> ambaraka ankehitriny dia ao amin’ny vavahadin’ny mpanjaka atsinanana izy; ireo no mpiandry varavarana tamin’ny tobin’ny taranak’i Levy.",
			"19": "<sup>19</sup> Ary Saloma, zanak’i Kore, zanak’i Abiasafa, zanak’i Kora, sy ny Koraïta rahalahiny araka ny fianakaviany avy no mpanao ny fanompoana amin’ny fiandrasana ny varavaran’ny lay; ary ny razany koa dia mpiambina ny vavahady tao amin’ny tobin’i Jehovah.",
			"20": "<sup>20</sup> Ary Finehasa, zanak’i Eleazara, no mpanapaka ireo taloha, ary Jehovah nomba azy.",
			"21": "<sup>21</sup> Ary Zakaria, zanak’i Meselemia, no mpiandry ny varavaran’ny trano-lay fihaonana.",
			"22": "<sup>22</sup> Ary ny isan’ireo voafidy ho mpiandry varavarana ireo dia roa ambin’ny folo amby roan-jato. Ireo dia voasoratra araka ny firazanany tany amin’ny vohiny avy; ary notendren’i Davida sy Samoela mpahita izy noho ny fahamarinany.",
			"23": "<sup>23</sup> Ka dia ireo sy ny taranany no niandry varavarana tao amin’ny tranon’i Jehovah, dia ny trano-lay.",
			"24": "<sup>24</sup> Teo amin’ny lafiny efatra no nitoeran’ny mpiandry varavarana, dia teo atsinanana sy andrefana sy avaratra ary atsimo.",
			"25": "<sup>25</sup> Ary ny rahalahiny izay nonina teo amin’ny vohiny dia nasaina ho avy isan-kafitoana ho eo aminy.",
			"26": "<sup>26</sup> Fa nanao tamin’ny fahamarinany ireo Levita efa-dahy, lohan’ny mpiandry varavarana, ireo, ary tonian’ny efi-trano fitehirizana sy ny rakitry ny tranon’Andriamanitra koa izy ireo.",
			"27": "<sup>27</sup> Ary manodidina ny tranon’Andriamanitra no nandriany; fa izy ireo no mpiambina azy sady adidiny ny famohana ny trano isa-maraina.",
			"28": "<sup>28</sup> Ary ny sasany aminy nitandrina ny fanaka momba ny fanompoana; fa an-isany no nitondrany azy miditra sy mivoaka.",
			"29": "<sup>29</sup> Ary ny sasany aminy koa notendrena hitandrina ny fanaka, dia ny fanaka masina rehetra, sy ny koba tsara toto sy ny divay sy ny diloilo sy ny ditin-kazo manitra ary ny zava-manitra.",
			"30": "<sup>30</sup> Ary ny sasany tamin’ny zanaky ny mpisorona nangaroharo ny zava-manitra fanosorana.",
			"31": "<sup>31</sup> Ary Matatia, anankiray tamin’ny Levita, lahimatoan’i Saloma Koraïta, no tonian’ny zavatra izay natao tamin’ny fanendasana.",
			"32": "<sup>32</sup> Ary ny sasany tamin’ny rahalahiny, tamin’ny taranaky ny Kehatita, no tonian’ny mofo aseho hamboatra azy isan-tSabata.",
			"33": "<sup>33</sup> Ary ireo ihany koa no mpihira, lohan’ny fianakaviana amin’ny Levita, izay mitoetra tsy manan-draharaha hafa ao amin’ireo efi-trano; fa tsy maintsy ho amin’izany raharaha izany andro aman-alina izy.",
			"34": "<sup>34</sup> Ireo dia lohan’ny fianakaviana tamin’ny Levita araka ny firazanany avy, dia samy loholona. Ireo no nonina tany Jerosalema.",
			"35": "<sup>35</sup> ARY tao Gibeona no nonenan’i Jeiela, razamben’ny an’i Gibeona, ary ny anaran’ny vadiny dia Imaka;",
			"36": "<sup>36</sup> ary ny zanany lahimatoa dia Abdona, dia Zora, dia Kisy, dia Bala, dia Nera, dia Nadaba,",
			"37": "<sup>37</sup> dia Gedora, dia Ahio, dia Zakaria, dia Miklota.",
			"38": "<sup>38</sup> Ary Miklota niteraka an’i Simeama. Ary ireo koa niara-nonina tamin’ny rahalahiny tany Jerosalema nifanandrify fonenana taminy.",
			"39": "<sup>39</sup> Ary Nera niteraka an’i Kisy; ary Kisy niteraka an’i Saoly; ary Saoly niteraka an’i Jonatana sy Malkisoa sy Abinadaba ary Esbala.",
			"40": "<sup>40</sup> Ary ny zanakalahin’i Jonatana dia Meribala; ary Meribala niteraka an’i Mika.",
			"41": "<sup>41</sup> Ary ny zanakalahin i Mika dia Pitona sy Maleka sy Tarea ary Ahaza.",
			"42": "<sup>42</sup> Ary Ahaza niteraka an’i Jara; ary Jara niteraka an’i Alemeta sy Azmaveta ary Zimry; ary Zimry niteraka an’i Moza;",
			"43": "<sup>43</sup> ary Moza niteraka an’i Binea; ary Refaia no zanakalahin’i Binea, Elasa no zanakalahin’i Refaia, Azela no zanakalahin’i Elasa.",
			"44": "<sup>44</sup> Ary Azela nanana zanaka enina mirahalahy, ka izao no anarany avy: Azrikama sy Bokero sy Isimaela sy Searia sy Obadia ary Hanana; ireo no zanakalahin’i Azela."
		},
		"10": {
			"1": "<sup>1</sup> ARY ny Filistina niady tamin’ny Isiraely, ary nandositra teo anoloan’ny Filistina ny lehilahy amin’ny Isiraely, ka niampatrampatra teny an-tendrombohitra Gilboa ny faty.",
			"2": "<sup>2</sup> Ary ny Filistina nanenjika fatratra an’i Saoly sy ny zanany, ka matin’ny Filistina Jonatana sy Abinadaba sy Malkisoa, zanakalahin’i Saoly.",
			"3": "<sup>3</sup> Ary mafy ny ady tamin’i Saoly, dia nifanehatra tamin’ny mpandefa tsipìka izy; ary raiki-tahotra izy noho ny mpandefa tsipìka.",
			"4": "<sup>4</sup> Ka hoy Saoly tamin’ny mpitondra ny fiadiany: Tsoahy ny sabatrao, ka tsatohy miboroaka aho; fandrao avy irerỳ tsy mifora irerỳ ka hanao ahy ho fihomehezana. Nefa tsy nety ny mpitondra ny fiadiany, fa natahotra loatra izy. Dia nalain’i Saoly ny sabatra ka nianjerany.",
			"5": "<sup>5</sup> Ary rehefa hitan’ny mpitondra ny fiadiana fa maty Saoly, dia mba nianjera tamin’ny sabany koa izy ka maty.",
			"6": "<sup>6</sup> Ka dia niara-maty Saoly sy ny zanany telo lahy ary ny ankohonany rehetra.",
			"7": "<sup>7</sup> Ary nony hitan’ny lehilahy rehetra amin’ny Isiraely teny amin’ny lohasaha fa nandositra ny olona, ary maty Saoly sy ny zanany, dia niala tamin’ny tanànany izy ka nandositra; ary dia tonga ny Filistina ka nonina tao.",
			"8": "<sup>8</sup> Ary nony ampitso, raha tonga haka ny zavatra teo amin’ny faty ny Filistina, dia nahita an’i Saoly sy ny zanani-lahy niampatrampatra teo an-tendrombohitra Gilboa izy.",
			"9": "<sup>9</sup> Dia nesoriny ny akanjony, ka nentiny ny lohany sy ny fiadiany, ary dia naniraka tany amin’ny tanin’ny Filistina manodidina izy hanambara izany zavatra mahafaly izany tamin’ny sampiny sy ny olona.",
			"10": "<sup>10</sup> Dia napetrany tao an-tranon’ny andriamaniny ny fiadian’i Saoly, ary nohomboany teo amin’ny tempolin’i Dagona ny lohany.",
			"11": "<sup>11</sup> Ary nony ren’ny mponina rehetra tao Jabesi-gileada izay rehetra nataon’ny Filistina tamin’i Saoly,",
			"12": "<sup>12</sup> dia niainga ny lehilahy mahery rehetra naka ny fatin’i Saoly sy ny fatin’ny zanany ka nitondra azy ho any Jabesy, ary nandevina ny taolany teo ambanin’ilay hazo terebinta tany Jabesy izy, ary dia nifady hanina hafitoana.",
			"13": "<sup>13</sup> Eny, maty Saoly noho ny nivadihany tamin’i Jehovah, dia noho ny tsy nitandremany ny tenin’i Jehovah sy noho ny nakany saina tamin’izay nanao azy ho tsindrian-javatra",
			"14": "<sup>14</sup> ary noho ny tsy nanontaniany tamin’i Jehovah; ka dia novonoiny izy, ary nafindrany ho an’i Davida, zanak’i Jese, ny fanjakana."
		},
		"11": {
			"1": "<sup>1</sup> ARY dia nivory teo amin’i Davida tao Hebrona ny Isiraely rehetra ka nanao hoe: Indreto izahay, fa taolanao sy nofonao.",
			"2": "<sup>2</sup> Hatry ny fahiny ihany koa, fony Saoly mbola nanjaka aza, dia hianao ihany no mpitarika ny Isiraely nivoaka sy niditra; ary hoy Jehovah Andriamanitrao taminao; Hianao no ho mpiandry ny Isiraely oloko, ary hianao no ho mpanapaka azy.",
			"3": "<sup>3</sup> Eny, tonga tao amin’ny mpanjaka tao Hebrona ny loholon’ny Isiraely rehetra; ary Davida nanao fanekena taminy tao Hebrona teo anatrehan’i Jehovah; ary dia nanosotra an’i Davida ho mpanjakan’ny Isiraely izy araka ny tenin’i Jehovah izay nampilazainy an’i Samoela.",
			"4": "<sup>4</sup> Ary Davida sy ny Isiraely rehetra nankany Jerosalema (Jebosa izany); ary tao ny Jebosita tompon-tany.",
			"5": "<sup>5</sup> Ary hoy ny mponina tao Jebosa tamin’i Davida: Tsy ho tafiditra eto hianao. Kanjo afak’i Davida ihany ny batery fiarovana, dia Ziona (Tanànan’i Davida izany).",
			"6": "<sup>6</sup> Ary hoy Davida: Na iza na iza no hamely voalohany ny Jebosita, dia izy no ho lohany sy ho komandy. Ary Joaba, zanak’i Zeroia, no niakatra voalohany, ka dia izy no lohany.",
			"7": "<sup>7</sup> Dia nitoetra tao anatin’ny batery fiarovana Davida, ka izany no nanaovana ny anarany hoe Tanànan’i Davida.",
			"8": "<sup>8</sup> Ary nandrafitra ny manodidina ny tanàna hatrany Milo",
			"9": "<sup>9</sup> Koa dia nandroso nihalehibe Davida; fa Jehovah, Tompon’ny maro, no nomba azy.",
			"10": "<sup>10</sup> ARY izao no lohan’ny lehilahy maherin’i Davida izay nifikitra mafy taminy tamin’ny fanjakany, mbamin’ny Isiraely rehetra, hampanjaka azy araka ny tenin’i Jehovah ny amin’ny Isiraely.",
			"11": "<sup>11</sup> Dia izao no isan’ny lehilahy maherin’i Davida: Jasobeama, zanak’i Hakmony, lehiben’ny mpanafika malaza; izy no namely telon-jato lahy tamin’ny lefona ka naharingana azy tamin’ny ady indray mandeha monja.",
			"12": "<sup>12</sup> Ary ny manarakaraka azy dia Eleazara, zanak’i Dodo Ahohita, anankiray amin’izy telo lahy mahery.",
			"13": "<sup>13</sup> Izy dia teo amin’i Davida tao Efesa-damima, raha vory teo ny Filistina hiady, ary ny tany nisy vary hordea maniry, ary ny olona nandositra ny Filistina.",
			"14": "<sup>14</sup> Fa nijanona teo afovoan’io tany io kosa Eleazara ka nahazo io ary namono ny Filistina, ka dia nanao famonjena lehibe Jehovah.",
			"15": "<sup>15</sup> Ary nisy telo lahy malaza tamin’ny telo-polo nidina nankeo amin’ny vatolampy ho any amin’i Davida tamin’ny zohin’i Adolama; ary ny miaramilan’ny Filistina nitoby tao amin’ny Lohasahan’ny Refaïta.",
			"16": "<sup>16</sup> Ary Davida dia tao amin’ny batery fiarovana tamin’izay, ary ny miaramilan’ny Filistina kosa dia tao Betlehema.",
			"17": "<sup>17</sup> Ary Davida naniry ka nanao hoe: Enga anie ka hisy hampisotro rano ahy avy amin’ny fantsakana ao Betlehema, ilay ao akaikin’ny vavahady.",
			"18": "<sup>18</sup> Ary izy telo lahy ireo namaky teo amin’ny miaramilan’ny Filistina ka nanovo rano tao amin’ny fantsakana tao Betlehema, ilay tao akaikin’ny vavahady, dia nentiny ho any amin’i Davida. Kanjo tsy nety nisotro izy, fa naidiny ho an’i Jehovah.",
			"19": "<sup>19</sup> Fa hoy izy: Sanatria amiko eo anatrehan’Andriamanitra raha hanao izany; hisotro ny ran’ireto olona nanao vi-very ny ainy ireto va aho? Fa nanao vi-very ny ainy izy ireo tamin’ny nakany ity. Ka dia tsy nety nisotro izy. Izany zavatra izany no nataon’ireo telo lahy mahery ireo.",
			"20": "<sup>20</sup> Ary Abisay, rahalahin’i Joaba, no lehibe tamin’izy telo lahy; fa izy koa namely telon-jato lahy tamin’ny lefona ka nahafaty azy, ka dia izy no nalaza tamin’izy telo lahy.",
			"21": "<sup>21</sup> Eny, tamin’izy telo lahy dia izy no nanana voninahitra mihoatra noho ny azy roa lahy, fa izy no lehibeny; nefa tsy nahomby azy telo lahy teo izy.",
			"22": "<sup>22</sup> Ary Benaia, zanak’i Joiada, zanaky ny lehilahy mahay tany Kabzela, izay nahavita asa be, izy no nahafaty ny roa lahy mahery tany Moaba; ary izy koa no nidina namono ny liona tao anatin’ny lavaka fantsakana tamin’ilay andro nisy orampanala iny.",
			"23": "<sup>23</sup> Ary izy koa no namono ilay Egyptiana makadiry, izay dimy hakiho ny halavany, nefa teny an-tànan’ilay Egyptiana nisy lefona tahaka ny vodi-tenon’ny mpanenona; nefa namoro-tsahan’i Benaia tamin’ny tehiny ihany izy, dia nosarihany ny lefona teny an-tànan’ilay Egyptiana, ka iny lefony iny ihany no namonoany azy.",
			"24": "<sup>24</sup> Izany zavatra izany no nataon’i Benaia, zanak’i Joiada, ary tamin’izy telo lahy mahery dia izy no nalaza.",
			"25": "<sup>25</sup> Indro fa be voninahitra tamin’ny telo-polo lahy izy, nefa tsy nahomby azy telo lahy teo; ary Davida nanao azy ho isan’ny mpanolo-tsaina azy.",
			"26": "<sup>26</sup> Ary ny lehilahy mahery dia Asahela, rahalahin’i Joaba, sy Elanana, zanak’i Dodo, avy any Betlehema,",
			"27": "<sup>27</sup> sy Samota Harorita sy Haleza Pelonita",
			"28": "<sup>28</sup> sy Ira, zanak’Ikesy Tekoita, sy Abiezera Anatotita",
			"29": "<sup>29</sup> sy Sibekay Hosatita sy Ely Ahohita",
			"30": "<sup>30</sup> sy Maharay Netofatita sy Heleda, zanak’i Bana Netofatita,",
			"31": "<sup>31</sup> sy Itahy, zanak’i Ribay avy any Gibean’ny taranak’i Benjamina, sy Benaia Piratonita",
			"32": "<sup>32</sup> sy Horay, any amin’ny lohasahan-driak’i Gasy, sy Abiela Arbatita",
			"33": "<sup>33</sup> sy Azmaveta Baharomita sy Eliaba Salbonita",
			"34": "<sup>34</sup> sy ny zanak’i Hasema Gizonita sy Jonatana, zanak’i Sage Hararita,",
			"35": "<sup>35</sup> sy Ahiama, zanak’i Sakara Hararita, sy Elifala, zanak’i Ora,",
			"36": "<sup>36</sup> sy Hefera Mekeratita sy Ahia Pelonita",
			"37": "<sup>37</sup> sy Hezro Karmelita sy Naray, zanak’i Ezbay,",
			"38": "<sup>38</sup> sy Joela, rahalahin’i Natana, sy Mibara, zanak’i Hagry.",
			"39": "<sup>39</sup> sy Zaleka Amonita sy Naharay Berotita, mpitondra ny fiadian’i Joaba, zanak’i Zeroia,",
			"40": "<sup>40</sup> sy Ira Jitrita sy Gareba Jitrita",
			"41": "<sup>41</sup> sy Oria Hetita sy Zabada, zanak’i Ahelay,",
			"42": "<sup>42</sup> sy Adina, zanak’i Siza Robenita, mpifehy ny Robenita, ary ny namany telo-polo lahy koa,",
			"43": "<sup>43</sup> sy Hanana, zanak’i Maka, sy Josafata Mitnita",
			"44": "<sup>44</sup> sy Ozia Astartita ary Sama sy Jeiela, zanak’i Hotama Aroerita,",
			"45": "<sup>45</sup> sy Jediaela, zanak’i Simry, sy Joha Tizita rahalahiny",
			"46": "<sup>46</sup> sy Eliala Mahavita sy Jeribay sy Josavia, zanak’i Elnama, sy Jitma Moabita",
			"47": "<sup>47</sup> sy Eliala sy Obeda ary Jasiela Mezobaita."
		},
		"12": {
			"1": "<sup>1</sup> ARY izao no nankany amin’i Davida tany Ziklaga, fony izy mbola niery an’i Saoly, zanak’i Kisy; ary isan’ny lehilahy mahery mpanampy amin’ny ady ireo",
			"2": "<sup>2</sup> sady mpitondra tsipìka ka nahay nanavia sy nanavanana raha nitora-bato na nandefa tsipìka. Isan’ny Benjamita, rahalahin’i Saoly, ireo.",
			"3": "<sup>3</sup> Ny lohany dia Ahiezera sy Joasy, zanak’i Sema Gibeatita, sy Jeziela sy Peleta zanak’i Azmaveta, ary Beraka sy Jeho Anatotita",
			"4": "<sup>4</sup> ary Jismaia Gibeonita, lehilahy mahery isan’ny telo-polo lahy sady mpifehy ny telo-polo lahy, ary Jeremia sy Jahaziela sy Johanana sy Jozababa Gaderatita",
			"5": "<sup>5</sup> sy Elozay sy Jerimota ary Bealia sy Semaria sy Sefatia Harofita",
			"6": "<sup>6</sup> sy Elkana sy Jisia sy Azarela sy Joezera ary Jasobeama Koraïta",
			"7": "<sup>7</sup> ary Joela sy Zebadia, zanak’i Jerohama avy any Gedora.",
			"8": "<sup>8</sup> Ary tamin’ny Gadita nisy lehilahy mahery niendaka hanatona an’i Davida ho amin’ny fiarovana tany an-efitra, dia ny miaramila nahay niady ka nahatana ampinga lehibe sy lefona; ny tarehiny dia tahaka ny tarehin’ny liona, ary faingam-pandeha tahaka ny gazela eny an-tendrombohitra izy.",
			"9": "<sup>9</sup> Ezera no voalohany, Obadia no faharoa, Eliaba no fahatelo,",
			"10": "<sup>10</sup> Mismana no fahefatra, Jeremia no fahadimy,",
			"11": "<sup>11</sup> Atahy no fahenina, Eliala no fahafito,",
			"12": "<sup>12</sup> Johanana no fahavalo, Elizabada no fahasivy,",
			"13": "<sup>13</sup> Jeremia no fahafolo, Makbanay no fahiraika ambin’ny folo.",
			"14": "<sup>14</sup> Ireo no isan’ny taranak’i Gada sady samy mpifehy miaramila; ny kely indrindra naharesy olona zato, ary ny lehibe indrindra naharesy arivo.",
			"15": "<sup>15</sup> Ireo no nita an’i Jordana tamin’ny volana voalohany, fony iny nihoatra ny morony rehetra; ary nampandositra izay rehetra teny amin’ny lohasaha na atsinanana na andrefana ireo.",
			"16": "<sup>16</sup> Ary nisy tamin’ny taranak’i Benjamina sy Joda nankany amin’i Davida tany amin’ny fiarovana.",
			"17": "<sup>17</sup> Ary Davida nivoaka hitsena azy ka niteny taminy hoe: Raha fihavanana no ihavianareo atỳ amiko hanampy ahy, dia hikambana isika; fa raha avy hamadika ahy kosa hianareo ho azon’ny fahavaloko, nefa tsy mba nanao an-keriny ny tànako, dia Andriamanitry ny razantsika no hijery sy hamaly izany.",
			"18": "<sup>18</sup> Ary Amasay, lehiben’ny mpanafika malaza, dia nampitenenin’ny Fanahy hoe: Anao izahay, ry Davida ô, fa miandany aminao, ry zanak’i Jese; fiadanana, eny, fiadanana anie ho anao, ary fiadanana anie ho an’ny manampy anao; fa Andriamanitrao no manampy anao. Dia noraisin’i Davida ireo ka notendreny ho mpifehy ny miaramila.",
			"19": "<sup>19</sup> Ary nisy sasany tamin’ny Manase niala hanatona an’i Davida, raha nomba ny Filistina hiady amin’i Saoly izy, nefa tsy nanampy azy izy; fa ireo andrianan’ny Filistina nifampisaina, dia nampandeha azy ka nanao hoe: Ny lohantsika no ho entiny mila sitraka hiverenany amin’i Saoly tompony.",
			"20": "<sup>20</sup> Raha nankany Ziklaga izy, nisy tamin’ny Manase nanatona azy, dia Adna sy Jozabada sy Jediaela sy Mikaela sy Jozabada sy Eliho ary Ziletahy, mpifehy arivo tamin’ny Manase.",
			"21": "<sup>21</sup> Ary ireo dia nanampy an’i Davida hamely ny antokon’ny jirika; fa lehilahy mahery avokoa izy sady komandy amin’ny miaramila.",
			"22": "<sup>22</sup> Fa tamin’izany nisy nankany amin’i Davida isan-andro isan-andro hanampy azy mandra-pahatongany ho toby lehibe toy ny tobin’Andriamanitra.",
			"23": "<sup>23</sup> ARY izao no isan’ny miaramila efa voaomana hiady, izay tonga tao amin’i Davida tao Hebrona hamindra ny fanjakan’i Saoly ho azy araka ny tenin’i Jehovah:–",
			"24": "<sup>24</sup> Tamin’ny taranak’i Joda, valon-jato amby enina arivo, mpitondra ampinga lehibe sy lefona sady efa voaomana hiady.",
			"25": "<sup>25</sup> Tamin’ny taranak’i Simeona, zato amby fito arivo, lehilahy mahery sady matanjaka amin’ny ady.",
			"26": "<sup>26</sup> Tamin’ny taranak’i Levy, enin-jato amby efatra arivo.",
			"27": "<sup>27</sup> Ary Joiada dia lehiben’ny Aronita, ary nisy fiton-jato amby telo arivo teo aminy;",
			"28": "<sup>28</sup> ary Zadoka, zatovo mahery, sy ny fianakaviany, dia roa amby roa-polo lahy mpifehy.",
			"29": "<sup>29</sup> Ary tamin’ny taranak’i Benjamina, havan’i Saoly, telo arivo; fa hatramin’izany ny ankamaroany mbola nomba ny mpianakavin’i Saoly ihany.",
			"30": "<sup>30</sup> Ary tamin’ny taranak’i Efraima dia valon-jato amby roa alina, lehilahy mahery avokoa sady samy nalaza tamin’ny fianakaviany avy.",
			"31": "<sup>31</sup> Ary tamin’ny antsasaky ny firenen’i Manase dia valo arivo amby iray alina, izay notononina anarana ho avy hampanjaka an’i Davida.",
			"32": "<sup>32</sup> Ary tamin’ny taranak’Isakara dia roan-jato no lohany, samy nahay namantatra tsara izay fotoana mety ka nahalala izay tokony hataon’ny Isiraely; ary ny rahalahiny rehetra nanaraka ny teniny.",
			"33": "<sup>33</sup> Tamin’ny Zebolona, izay azo nalefa hanafika ka nahay niady tamin’ny fiadiana samy hafa rehetra dia dimy alina, izay nahay nilahatra tsara tamin’ny firaisan-kina.",
			"34": "<sup>34</sup> Ary tamin’ny Naftaly dia nisy mpifehy arivo, ary manaraka azy dia nisy fito arivo amby telo alina amin’ampinga lehibe sy lefona.",
			"35": "<sup>35</sup> Ary tamin’ny Danita dia enin-jato amby valo arivo sy roa alina izay nahay niady.",
			"36": "<sup>36</sup> Ary tamin’ny Asera, efatra alina izay azo nalefa hanafika sady nahay niady.",
			"37": "<sup>37</sup> Ary avy tany an-dafin’i Jordana, tamin’ny Robenita sy ny Gadita ary ny antsasaky ny firenen’i Manase, nisy roa alina amby iray hetsy nitana fiadiana samy hafa rehetra.",
			"38": "<sup>38</sup> Ireo miaramila rehetra ireo, izay nahay nilahatra tsara, dia niray fo nankany Hebrona hampanjaka an’i Davida amin’ny Isiraely rehetra; ary ny sisa rehetra tamin’ny Isiraely koa niray fo hampanjaka an’i Davida.",
			"39": "<sup>39</sup> Ary tao amin’i Davida hateloana izy sady nihinana sy nisotro; fa efa nanao nahandro ho azy ny rahalahiny.",
			"40": "<sup>40</sup> Ary izay teo akaikiny koa hatramin’ny Isakara sy ny Zebolona ary ny Naftaly dia samy nampitondra hanina samy hafa nentin’ny boriky sy rameva sy ampondra ary omby, dia koba sy ampempan’aviavy sy voaloboka maina sy divay sy diloilo, ary omby sy ondry aman-osy betsaka koa; fa nisy fifaliana tamin’ny Isiraely."
		},
		"13": {
			"1": "<sup>1</sup> ARY Davida naka saina tamin’ny mpifehy arivo sy ny mpifehy zato, dia ny mpifehy rehetra,",
			"2": "<sup>2</sup> ka hoy Davida tamin’ny Isiraely rehetra izay tafangona: Raha ataonareo ho mety, sady avy amin’i Jehovah Andriamanitsika izany, dia andeha isika haniraka faingana any amin’ny rahalahintsika rehetra izay sisa amin’ny tanin’ny Isiraely rehetra ary amin’ny mpisorona sy ny Levita koa, izay any amin’ny tanànany sy ny tany manodidina azy, mba hivory atỳ amintsika;",
			"3": "<sup>3</sup> ary aoka ho entintsika atỳ amintsika indray ny fiaran’Andriamanitsika; fa tsy nahoantsika tamin’ny andron’i Saoly izany.",
			"4": "<sup>4</sup> Ary ny olona rehetra izay tafangona dia nanaiky hanao izany, satria marina teo imason’ny olona rehetra izany.",
			"5": "<sup>5</sup> Dia novorin’i Davida ny Isiraely rehetra hatrany Sihora any Egypta ka hatrany akaikin’i Hamata haka ny fiaran’Andriamanitra avy any Kiriata-jearima.",
			"6": "<sup>6</sup> Dia niakatra Davida sy ny Isiraely rehetra hankany Bala any Joda (Kiriata-jearima izany) hampakatra ny fiaran’i Jehovah Andriamanitra, Izay mipetraka amin’ny kerobima, dia ilay niantsoany ny anarany.",
			"7": "<sup>7</sup> Ary nentiny tamin’ny sariety vaovao niala tao an-tranon’i Abinadaba ny fiaran’Andriamanitra, ary Oza sy Ahio no nampandeha ny sariety.",
			"8": "<sup>8</sup> Ary Davida sy ny Isiraely rehetra dia nitsinjaka fatratra teo anatrehan’Andriamanitra tamin’ny hira sy ny lokanga sy ny valiha sy ny ampongatapaka sy ny kipantsona ary ny trompetra.",
			"9": "<sup>9</sup> Ary nony tonga teo amin’ny famoloan’i Kidona izy, dia naninjitra ny tànany hihazona ny fiara Oza, satria nazeran’ny omby izany.",
			"10": "<sup>10</sup> Dia nirehitra tamin’i Oza ny fahatezeran’i Jehovah, ka namely azy Izy noho ny naninjirany ny tànany tamin’ny fiara, dia maty teo anatrehan’Andriamanitra izy.",
			"11": "<sup>11</sup> Ary dia tezitra Davida noho ny namelezan’i Jehovah an’i Oza, ka dia nataony hoe Perez-oza no anaran’izany tany izany mandraka andro-any.",
			"12": "<sup>12</sup> Ary Davida natahotra an’Andriamanitra tamin’izany andro izany ka nanao hoe: Nahoana aho no hitondra ny fiaran’Andriamanitra ho atỳ amiko?",
			"13": "<sup>13</sup> Dia tsy nafindran’i Davida ho ao aminy ao an-Tanànan’i Davida ny fiara, fa naviliny ho ao an-tranon’i Obed-edoma Gatita.",
			"14": "<sup>14</sup> Ary nipetraka telo volana tao amin’ny ankohonan’i Obed-edoma tao an-tranony ny fiaran’Andriamanitra; ka dia notahin’i Jehovah ny ankohonan’i Obed-edoma sy izay rehetra nananany."
		},
		"14": {
			"1": "<sup>1</sup> ARY Hirama, mpanjakan’i Tyro, naniraka olona tany amin’i Davida sady nampanatitra hazo sedera ary nandefa mpanao trano vato sy mpandrafitra hanao trano ho azy.",
			"2": "<sup>2</sup> Dia fantatr’i Davida fa Jehovah efa nampitoetra azy ho mpanjakan’ny Isiraely, ka voasandratra ny fanjakany noho ny amin’ny Isiraely olony.",
			"3": "<sup>3</sup> Ary Davida nampaka-bady koa tany Jerosalema ka niteraka zazalahy sy zazavavy.",
			"4": "<sup>4</sup> Ary izao no anaran’ny zanany naterany tany Jerosalema: Samoa sy Sobaba sy Natana sy Solomona",
			"5": "<sup>5</sup> sy Jibara sy Elisoa sy Elipeleta",
			"6": "<sup>6</sup> sy Noga sy Nafega sy Jafia",
			"7": "<sup>7</sup> sy Elisama sy Beliada ary Elifeleta.",
			"8": "<sup>8</sup> KOA nony ren’ny Filistina fa efa nohosorana ho mpanjakan’ny Isiraely rehetra Davida, dia niakatra hitady azy ny Filistina rehetra. Koa raha ren’i Davida izany, dia nivoaka hiady taminy izy.",
			"9": "<sup>9</sup> Ary dia tonga ny Filistina ka nihahakahaka teny amin’ny Lohasahan’ny Refaïta.",
			"10": "<sup>10</sup> Ary Davida nanontany tamin’Andriamanitra hoe: Hiakatra hamely ny Filistina va aho? Hatolotrao eo an-tànako va izy? Dia hoy Jehovah taminy: Miakara, fa hatolotro eo an-tànanao izy.",
			"11": "<sup>11</sup> Dia niakatra nankao Bala-perazima Davida ka namely azy teo; dia hoy izy: Ny tànako no namakian’Andriamanitra ny fahavaloko hipariaka toy ny rano vaky; koa izany no nanaovany ny anaran’izany tany izany hoe Bala-perazima.",
			"12": "<sup>12</sup> Dia nilaozan’ireny teo ireo andriamaniny, ka dia nasain’i Davida nodorana tamin’ny afo.",
			"13": "<sup>13</sup> Ary tonga indray ny Filistina ka nihahakahaka teny an-dohasaha indray.",
			"14": "<sup>14</sup> Dia nanontany tamin’Andriamanitra indray Davida ka novaliany hoe: Aza mandeha hanenjika azy; fa mivilia azy, dia mandehana, fa ho tonga eo aminy hianao eo amin’ny tandrifin’ny hazo balsama.",
			"15": "<sup>15</sup> Ary raha toa mahare fingadongadon’olona eny amin’ny tendron’ny hazo balsama hianao, dia miaingà amin’izay hiady; fa amin’izay no efa miainga mialoha anao Andriamanitra hamely ny miaramilan’ny Filistina.",
			"16": "<sup>16</sup> Ary dia nanao araka izay nandidian’Andriamanitra azy Davida, ka dia namely ny miaramilan’ny Filistina hatrany Gibeona ka hatrany Gazera izy.",
			"17": "<sup>17</sup> Ary ny lazan’i Davida niely teny amin’ny tany rehetra; ary nataon’i Jehovah nahazo ny firenena rehetra ny tahotra an’i Davida."
		},
		"15": {
			"1": "<sup>1</sup> ARY Davida nanao trano ho azy tao an-Tanànan’i Davida sady namboatra fitoerana sy nanorin-day ho an’ny fiaran’Andriamanitra.",
			"2": "<sup>2</sup> Ary tamin’izay dia hoy Davida: Tsy misy tokony hitondra ny fiaran’Andriamanitra afa-tsy ny Levita ihany, satria izy no efa nofidin’i Jehovah hitondra ny fiaran’Andriamanitra sy hanao fanompoam-pivavahana ho Azy mandrakizay.",
			"3": "<sup>3</sup> Dia novorin’i Davida tany Jerosalema ny Isiraely rehetra hampakatra ny fiaran’i Jehovah ho any amin’ny fitoerana izay namboariny ho azy.",
			"4": "<sup>4</sup> Ary novorin’i Davida ny taranak’i Arona sy ny Levita:",
			"5": "<sup>5</sup> tamin’ny taranak’i Kehata dia Oriela lehibeny sy ny rahalahiny roa-polo amby zato;",
			"6": "<sup>6</sup> tamin’ny taranak’i Merary dia Asaia lehibeny sy ny rahalahiny roa-polo amby roan-jato;",
			"7": "<sup>7</sup> tamin’ny taranak’i Gersona dia Joela lehibeny sy ny rahalahiny telo-polo amby zato;",
			"8": "<sup>8</sup> tamin’ny taranak’i Elizafana dia Semaia lehibeny sy ny rahalahiny roan-jato;",
			"9": "<sup>9</sup> tamin’ny taranak’i Hebrona dia Eliala lehibeny sy ny rahalahiny valo-polo;",
			"10": "<sup>10</sup> tamin’ny taranak’i Oziela dia Aminadaba lehibeny sy ny rahalahiny roa ambin’ny folo amby zato.",
			"11": "<sup>11</sup> Ary Davida niantso an’i Zadoka sy Abiatara mpisorona sy ny Levita, dia Oriela sy Asaia sy Joela sy Semaia sy Eliala ary Aminadaba,",
			"12": "<sup>12</sup> ka nanao taminy hoe: Hianareo lohan’ny fianakavian’ny Levita, hamasino ny tenanareo, dia hianareo sy ny rahalahinareo, mba hampakaranareo ny fiaran’i Jehovah, Andriamanitry ny Isiraely, ho ao amin’ilay namboariko ho azy.",
			"13": "<sup>13</sup> Satria tsy hianareo no tamin’ilay teo, dia nasian’i Jehovah Andriamanitsika isika, fa tsy nitady Azy araka ny voadidy.",
			"14": "<sup>14</sup> Dia nanamasina ny tenany ny mpisorona sy ny Levita hampakatra ny fiaran’i Jehovah, Andriamanitry ny Isiraely.",
			"15": "<sup>15</sup> Ary ny Levita nilanja ny fiaran’Andriamanitra tamin’ny hazo, tahaka ny nandidian’i Mosesy araka ny tenin’i Jehovah.",
			"16": "<sup>16</sup> Ary ireo lehiben’ny Levita dia nasain’i Davida nanendry ny mpihira rahalahiny hitondra ny zava-maneno, dia ny valiha sy ny lokanga ary hampaneno ny kipantsona, mba hanandra-peo amin’ny fifaliana.",
			"17": "<sup>17</sup> Ka dia notendren’ny Levita Hemana, zanak’i Joela; ary tamin’ny rahalahiny dia Asafa, zanak’i Berekia; ary tamin’ny taranak’i Merary rahalahiny dia Etana, zanak’i Kosaia;",
			"18": "<sup>18</sup> ary naman’ireo ihany koa ny rahalahiny manarakaraka, dia Zakaria sy Bena sy Jaziela sy Semiramota sy Jehiela sy Ony sy Eliaba sy Benaia sy Mahaseia sy Matitia sy Elifeleho sy Mikneia sy Obed-edoma ary Jeiela, mpiandry varavarana.",
			"19": "<sup>19</sup> Ary Hemana sy Asafa ary Etana mpihira dia nasaina nitondra kipantsona varahina hampanenoina.",
			"20": "<sup>20</sup> Ary Zakaria sy Aziela sy Semiramota sy Jehiela sy Ony sy Eliaba sy Mahaseia sy Benaia no nasaina nitendry valiha tamin’ny Alamota.",
			"21": "<sup>21</sup> Ary Matitia sy Elifeleho sy Mikneia sy Obed-edoma sy Jeiela ary Azazia no nasaina nitendry ny lokanga tamin’ny Seminita ho mpitarika.",
			"22": "<sup>22</sup> Ary Kenania, lehiben’ny Levita, no tonian’ny fitondrana; ary nampianatra ny fitondrana izy, fa nahay.",
			"23": "<sup>23</sup> Ary Berekia sy Elikana no mpiandry varavarana amin’ny fiara.",
			"24": "<sup>24</sup> Ary Sebania sy Josafata sy Netanela sy Amasay sy Zakaria sy Benaia ary Eliezera, izay samy mpisorona, no nitsoka ny trompetra teo alohan’ny fiaran’Andriamanitra; ary Obed-edoma sy Jehia koa no mpiandry varavarana amin’ny fiara.",
			"25": "<sup>25</sup> Ary Davida sy ny loholon’ny Isiraely mbamin’ny mpifehy arivo no nandeha hampakatra ny fiaran’ny faneken’i Jehovah avy tao an-tranon’i Obed-edoma tamin’ny fifaliana.",
			"26": "<sup>26</sup> Ary rehefa nomban’Andriamanitra ny Levita izay nilanja ny fiaran’ny faneken’i Jehovah, dia namono vantotr’ombilahy fito sy ondrilahy fito hatao fanatitra izy.",
			"27": "<sup>27</sup> Ary niakanjo akanjo rongony fotsy madinika Davida, ary niakanjo toy izany koa ny Levita rehetra izay nitondra ny fiara ary ny mpihira sy Kenania, tonian’ny fitondrana, mbamin’ny mpihira; ary Davida nisalotra efoda rongony fotsy koa.",
			"28": "<sup>28</sup> Toy izany no nampakaran’ny Isiraely rehetra ny fiaran’ny faneken’i Jehovah tamin’ny fihobiana sy ny feon’ny anjomara sy ny trompetra ary ny kipantsona sy tamin’ny fitendrena ny valiha sy ny lokanga.",
			"29": "<sup>29</sup> Ary nony tonga tao an-Tanànan’i Davida ny fiaran’ny faneken’i Jehovah, dia nitsirika teo am-baravarankely Mikala, zanakavavin’i Saoly, ka nahita an’i Davida mpanjaka nibitaka sy nitsinjaka; ka dia nikizaka azy tao am-pony izy."
		},
		"16": {
			"1": "<sup>1</sup> Ary nampiditra ny fiaran’Andriamanitra ny olona ka nametraka azy tao afovoan’ny lay izay naorin’i Davida ho azy; ary nanatitra fanatitra dorana sy fanati-pihavanana teo anatrehan’Andriamanitra izy.",
			"2": "<sup>2</sup> Ary nony voatitr’i Davida ny fanatitra odorana sy ny fanati-pihavanana, dia nitso-drano ny olona tamin’ny anaran’i Jehovah izy.",
			"3": "<sup>3</sup> Dia nizara mofo iray avy sy hena iray didy ary ampempam-boaloboka ho an’ny Isiraely rehetra na lahy na vavy izy.",
			"4": "<sup>4</sup> ARY ny Levita sasany notendren’i Davida ho mpanao fanompoam-pivavahana eo anatrehan’ny fiaran’i Jehovah mba hankalaza sy hisaotra ary hidera an’i Jehovah, Andriamanitry ny Isiraely:",
			"5": "<sup>5</sup> Asafa no lohany, ary Zakaria no nanarakaraka, dia Jeiela sy Semiramota sy Jehiela sy Matitia sy Eliaba sy Benaia sy Obed-edoma ary Jeiela no nitendry ny valiha sy ny lokanga; fa Asafa no nampaneno ny kipantsona;",
			"6": "<sup>6</sup> Benaia sy Jahaziela mpisorona kosa no mpitsoka ny trompetra mandrakariva eo anoloan’ny fiaran’ny faneken’Andriamanitra.",
			"7": "<sup>7</sup> Tamin’izay andro izay no voalohan’ny nanendren’i Davida an’i Asafa sy ny rahalahiny hidera an’i Jehovah.",
			"8": "<sup>8</sup> Miderà an’i Jehovah, miantsoa ny anarany; Ampahafantaro ny firenena ny asany.",
			"9": "<sup>9</sup> Mihirà ho Azy, mankalazà Azy; Saintsaino ny fahagagana rehetra ataony.",
			"10": "<sup>10</sup> Ataovy ho reharehanareo ny anarany masina; Aoka hifaly ny fon’izay mitady an’i Jehovah.",
			"11": "<sup>11</sup> Mitadiava an’i Jehovah sy ny heriny, Katsaho mandrakariva ny tavany.",
			"12": "<sup>12</sup> Mahatsiarova ny asa mahagaga izay nataony, Dia ny fahagagany sy ny fitsaran’ny vavany,",
			"13": "<sup>13</sup> Hianareo taranak’Isiraely mpanompony, Hianareo olom-boafidiny, taranak’i Jakoba.",
			"14": "<sup>14</sup> Jehovah, Izy no Andriamanitsika, Manerana ny tany rehetra ny fitsarany.",
			"15": "<sup>15</sup> Tsarovy mandrakizay ny fanekeny. Dia ilay teny voadidiny ho an’ny taranaka arivo mandimby,",
			"16": "<sup>16</sup> Eny, dia ilay nataony tamin’i Abrahama Sy ny fianianany tamin’Isaka,",
			"17": "<sup>17</sup> Izay naoriny ho lalàna ho an’i Jakoba Sy ho fanekena mandrakizay ho an’Isiraely,",
			"18": "<sup>18</sup> Ka nataony hoe: Homeko anao ny tany Kanana Ho lovanareo voazara amin’ny famolaina;",
			"19": "<sup>19</sup> Fony mbola olom-bitsy hianareo, Eny, vitsy sady vahiny tao,",
			"20": "<sup>20</sup> Ary nifindrafindra tany amin’ny firenena, Sy nifindrafindra fanjakana ho amin’ny firenen-kafa,",
			"21": "<sup>21</sup> Tsy nisy navelany hampahory azy; Ary nananatra mpanjaka Izy noho ny amin’ireo hoe:",
			"22": "<sup>22</sup> Aza manendry ireo voahosotro, Ary aza manisy ratsy ireo mpaminaniko.",
			"23": "<sup>23</sup> Mihirà ho an’i Jehovah, ry tany rehetra; Lazao isan-andro isan-andro ny famonjeny mahafaly.",
			"24": "<sup>24</sup> Ambarao any amin’ny jentilisa ny voninahiny, Ary any amin’ny firenena rehetra ny fahagagana ataony.",
			"25": "<sup>25</sup> Fa lehibe Jehovah ka mendrika hoderaina indrindra, Sady mahatahotra, fa ambonin’ny andriamanitra rehetra.",
			"26": "<sup>26</sup> Fa ny andriamanitry ny firenena rehetra dia tsinontsinona, Fa Jehovah no nanao ny lanitra.",
			"27": "<sup>27</sup> Famirapiratana sy voninahitra no eo anatrehany, Hery sy hafaliana no ao amin’ny fitoerany.",
			"28": "<sup>28</sup> Ry firenena, manomeza an’i Jehovah, Eny, manomeza voninahitra sy hery ho an’i Jehovah.",
			"29": "<sup>29</sup> Manomeza an’i Jehovah ny voninahitry ny anarany; Mitondrà fanatitra, ka mankanesa eo anatrehany; Miankohofa eo anatrehan’i Jehovah amin’ny fihaingoana masina",
			"30": "<sup>30</sup> Mangovita eo anatrehany, ry tany rehetra; Izao rehetra izao voaorina ka tsy hihetsika.",
			"31": "<sup>31</sup> Aoka ho faly ny lanitra ary ho ravo ny tany; Ary aoka holazaina any amin’ny firenena hoe: Jehovah no Mpanjaka.",
			"32": "<sup>32</sup> Aoka hirohondrohona ny ranomasina sy izay rehetra ao aminy; Aoka ho faly ny saha sy izay rehetra ao aminy.",
			"33": "<sup>33</sup> Dia hihoby eo anatrehan’i Jehovah amin’izay ny hazo an’ala; Fa avy hitsara ny tany Izy.",
			"34": "<sup>34</sup> Miderà an’i Jehovah, fa tsara Izy: Fa mandrakizay ny famindrampony.",
			"35": "<sup>35</sup> Ary manaova hoe: Vonjeo izahay, ry Andriamanitry ny famonjena anay ô, Ary angony izahay, ka vonjeo tsy ho azon’ny jentilisa, Mba hisaoranay ny anaranao masina, Ka ho reharehanay ny fiderana Anao.",
			"36": "<sup>36</sup> Isaorana anie Jehovah, Andriamanitry ny Isiraely, Hatramin’ny taloha indrindra ka ho mandrakizay. Ary ny olona rehetra nanao hoe: Amena, sady nidera an’i Jehovah.",
			"37": "<sup>37</sup> Ary Asafa sy ny rahalahiny dia navelan’i Davida teo anoloan’ny fiaran’ny faneken’i Jehovah hanao fanompoam-pivavahana ao mandrakariva araka ny raharaha tokony hatao isan-andro,–",
			"38": "<sup>38</sup> ary Obed-edoma sy ny rahalahiny valo amby enim-polo sy Obed-edoma, zanak’i Jedotona, ary Hosa ho mpiandry varavarana kosa.",
			"39": "<sup>39</sup> Ary Zadoka mpisorona sy ny mpisorona rahalahiny dia navelany teo anoloan’ny tabernakelin’i Jehovah teo amin’ny fitoerana avo any Gibeona,",
			"40": "<sup>40</sup> hanatitra fanatitra dorana ho an’i Jehvoah marain-tsy hariva eo amin’ny alitara fandoroana ny fanatitra dorana mandrakariva, ary hanao araka izay rehetra voasoratra ao amin’ny lalàn’i Jehovah, izay nandidiany ny Isiraely,–",
			"41": "<sup>41</sup> ary naman’ireo koa Hemana sy Jedotona ary ny sisa amin’ny olona voafidy izay voatonona anarana hidera an’i Jehovah, fa mandrakizay ny famindrampony.",
			"42": "<sup>42</sup> Ary tao amin’i Hemana sy Jedotona nisy trompetra sy kipantsona ho an’izay mampaneno ary ny fomban’ny zava-manenon’Andriamanitra. Ary ny zanak’i Jedotona no mpiandry varavarana.",
			"43": "<sup>43</sup> Ary dia samy nody tany an-tranony avy ny olona rehetra, ary Davida koa lasa nody mba hitso-drano ny ankohonany."
		},
		"17": {
			"1": "<sup>1</sup> ARY Davida, raha nipetraka tao an-tranony, dia nanao tamin’i Natana mpaminany hoe: Akory izato izaho mitoetra amin’ny trano sedera, nefa ny fiaran’ny faneken’i Jehovah kosa mitoetra ao anaty lay!",
			"2": "<sup>2</sup> Ary hoy Natana tamin’i Davida: Ataovy izay rehetra ao am-ponao, fa Andriamanitra no momba anao.",
			"3": "<sup>3</sup> Nefa tamin’ny alin’iny ihany dia tonga tamin’i Natana ny tenin’Andriamanitra nanao hoe:",
			"4": "<sup>4</sup> Andeha, lazao amin’i Davida mpanompoko hoe: Izao no lazain’i Jehovah: Tsy hianao no hanao trano hitoerako;",
			"5": "<sup>5</sup> fa tsy mbola nitoetra an-trano Aho hatramin’ny nitondrako ny Isiraely niakatra ka mandraka andro-any, fa nifindrafindra toby sy nifindrafindra toerana.",
			"6": "<sup>6</sup> Ary tamin’izay rehetra nifindrafindrako niaraka tamin’ny Isiraely moa nisy nitenenako akory va ny mpitsaran’ny Isiraely, izay notendreko ho mpiandry ny oloko, ka nanaovako hoe: Nahoana no tsy nanao trano sedera ho Ahy hianareo?",
			"7": "<sup>7</sup> Koa lazao amin’i Davida mpanompoko hoe: Izao no lazain’i Jehovah, Tompon’ny maro: Izaho no naka anao tany an-tsaha, dia avy tamin’ny niandrasanao ondry, mba ho mpanapaka ny Isiraely oloko.",
			"8": "<sup>8</sup> Ary nomba anao tamin’izay rehetra nalehanao Aho, ka naringako tsy ho eo anoloanao ny fahavalonao rehetra, ary homeko anarana tahaka ny anaran’izay lehibe ambonin’ny tany hianao.",
			"9": "<sup>9</sup> Ary hifidy tany ho an’ny Isiraely oloko Aho sady hamponina azy, ka dia handry fahizay amin’ny fonenany izy ka tsy hanana ahiahy intsony, ary ny zanaky ny faharatsiana tsy hampahory azy intsony tahaka ny tany aloha",
			"10": "<sup>10</sup> sy tahaka ny hatramin’ny andro nanendreko mpitsara ho mpifehy ny Isiraely oloko. Ary haetriko avokoa ny fahavalonao rehetra. Ary ambarako aminao koa fa Jehovah hanome taranaka anao.",
			"11": "<sup>11</sup> Ary rehefa tapitra ny andronao, ka lasa any amin’ny razanao hianao, dia hatsangako ny zanakao anankiray mandimby anao, izay isan’ny terakao; ary hampitoeriko ny fanjakany;",
			"12": "<sup>12</sup> dia io no hanao trano ho Ahy, ary Izaho kosa hampitoetra ny seza fiandrianany ho mandrakizay.",
			"13": "<sup>13</sup> Izaho ho rainy, ary izy ho zanako; ary tsy hanaisotra ny famindrampoko aminy Aho, tahaka ny nanesorako izany tamin’ilay nodiasanao;",
			"14": "<sup>14</sup> fa hampitoeriko ao an-tranoko sy ao amin ny fanjakako mandrakizay izy; ary hampitoerina mandrakizay ny seza fiandrianany.",
			"15": "<sup>15</sup> Araka izany teny rehetra izany sy araka izao fahitana izao no nolazain’i Natana tamin’i Davida.",
			"16": "<sup>16</sup> Ary Davida mpanjaka niditra ka nipetraka teo anatrehan’i Jehovah, dia nanao hoe: Zinona moa aho, Jehovah Andriamanitra ô? Ary zinona moa ny mpianakaviko, no nentinao ambaraka ankehitrny aho?",
			"17": "<sup>17</sup> Ary mbola nataonao ho zavatra kely ihany aza izany, Andriamanitra ô; fa, indro, efa voalazanao koa ny amin’ny taranaky ny mpanomponao hatramin’ny mbola any aoriana lavitra aza, ary nitsinjo ahy araka ny fanaon’ny olona Hianao, Jehovah Andriamanitra ô, ka nanandratra ahy.",
			"18": "<sup>18</sup> Inona intsony no holazain i Davida aminao ny amin’ny voninahitra omena ny mpanomponao? fa Hianao no mahalala ny mpanomponao.",
			"19": "<sup>19</sup> Jehovah ô, noho ny amin’ny mpanomponao sy araka ny sitraponao no nanaovanao io zavatra lehibe io, mba hampiharihariana ireo zavatra lehibe rehetra ireo.",
			"20": "<sup>20</sup> Jehovah ô, tsy misy tahaka Anao, ary tsy misy Andriamanitra afa-tsy Hianao, araka izay rehetra ren’ny sofinay.",
			"21": "<sup>21</sup> Ary aiza moa no misy firenena etỳ an-tany tahaka ny Isiraely olonao, izay nalehan’Andriamanitra havotana ho olony, hanaovanao anarana lehibe sy mahatahotra ho Anao, dia tamin’ny nandroahanao firenena tsy ho eo anoloan’ny olonao, izay navotanao avy tany Egypta?",
			"22": "<sup>22</sup> Fa ny Isiraely olonao nofidinao ho olonao mandrakizay, ary Hianao, Jehovah ô, no efa tonga Andriamaniny.",
			"23": "<sup>23</sup> Ary ankehitriny, Jehovah ô, ilay teny nolazainao ny amin’ny mpanomponao sy ny taranany dia ampitoero ho mandrakizay, ary ataovy araka izay nolazainao.",
			"24": "<sup>24</sup> Eny, aoka hampitoerina izany, ary aoka hohalehibiazina mandrakizay ny anaranao ka hatao hoe: Jehovah, Tompon’ny maro, Andriamanitry ny Isiraely, no Andriamanitry ny Isiraely; ka dia hampitoerina eo anatrehanao ny taranak’i Davida mpanomponao.",
			"25": "<sup>25</sup> Fa Hianao, Andriamanitro ô, efa nanambara tamin’ny mpanomponao fa homenao taranaka izy; koa izany no nampahasahy ny mpanomponao hivavaka eto anatrehanao.",
			"26": "<sup>26</sup> Ary noho izany, Jehovah ô, satria Hianao no Andriamanitra, ary Hianao efa nilaza izany zava-tsoa izany ny amin’ny mpanomponao,",
			"27": "<sup>27</sup> dia ho sitrakao ny hitahy ny taranaky ny mpanomponao haharitra mandrakizay eo anatrehanao; fa izay tahinao, Jehovah ô, dia voatahy mandrakizay."
		},
		"18": {
			"1": "<sup>1</sup> ARY nony afaka izany, dia nandresy ny Filistina Davida ka nampietry azy, ary Gata sy ny zana-bohiny dia nesoriny tamin’ny tànan’ny Filistina.",
			"2": "<sup>2</sup> Ary nandresy ny Moabita koa izy, ka dia tonga mpanompon’i Davida nandoa hetra ireo.",
			"3": "<sup>3</sup> Dia resin’i Davida koa Hadarezera, mpanjakan’i Zoba akaikin’i Hamata, tamin izy lasa nampiorina ny fanjakany teo amoron’ny ony Eofrata.",
			"4": "<sup>4</sup> Ary Davida nahazo babo kalesy arivo taminy sy mpitaingin-tsoavaly fito arivo ary miaramila an-tongotra roa alina; ary notraingotraingoin’i Davida avokoa ny soavalin-kalesy rehetra, fa ny tokony ho amin’ny kalesy zato ihany no navelany.",
			"5": "<sup>5</sup> Ary nony tonga ny Syriana avy any Damaskosy hanampy an’i Hadarezera, mpanjakan’i Zoba, dia nahafatesan’i Davida olona roa arivo sy roa alina ny Syriana.",
			"6": "<sup>6</sup> Ary nisy namponenin’i Davida tany Syria any Damaskosy; dia tonga mpanompon’i Davida ny Syriana ka nandoa hetra. Fa Jehovah nampahery an’i Davida teny amin’izay rehetra nalehany.",
			"7": "<sup>7</sup> Ary nalain’i Davida ny ampinga volamena teny amin’ny mpanompon’i Hadarezera ka nentiny nankany Jerosalema.",
			"8": "<sup>8</sup> Ary tao Tibata sy Kona, tanànan’i Hadarezera, no nahazoan’i Davida babo varahina be dia be, ary izany no nanaovan’i Solomona ilay tavin-drano varahina lehibe sy ny andry ary ny fanaka varahina.",
			"9": "<sup>9</sup> Ary nony ren’i Toy, mpanjakan’i Hamata, fa Davida efa naharesy ny miaramila rehetra an’i Hadarezera, mpanjakan’i Zoba,",
			"10": "<sup>10</sup> dia naniraka an’i Hadorama zanany hankany amin’i Davida mpanjaka izy hiarahaba sy hisaotra azy noho ny niadiany tamin’i Hadarezera sy ny nahareseny azy (fa Hadarezera dia fahavalon’i Toy), sady nampitondrainy fanaka volamena sy volafotsy ary varahina izay samy maro karazana izy.",
			"11": "<sup>11</sup> Dia nohamasinin’i Davida mpanjaka ho an’i Jehovah koa izany mbamin’ny volafotsy sy ny volamena nentiny avy tamin’ny firenena rehetra, dia tany Edoma sy Moaba sy ny taranak’i Amona sy ny Filistina ary ny Amalekita.",
			"12": "<sup>12</sup> Ary Abisay, zanak’i Zeroia, naharesy ny Edomita teo amin’ny Lohasahan-tsira, dia lehilahy valo arivo sy iray alina.",
			"13": "<sup>13</sup> Ary nametraka miaramila tany Edoma izy; dia tonga mpanompon’i Davida ny Edomita rehetra. Fa Jehovah nampahery an’i Davida teny amin’izay rehetra nalehany.",
			"14": "<sup>14</sup> ARY Davida nanjaka tamin’ny Isiraely rehetra ka nitsara sy nitondra ny vahoakany rehetra tamin’ny fahamarinana.",
			"15": "<sup>15</sup> Ary Joaba, zanak’i Zeroia, no komandin’ny miaramila; ary Josafata, zanak’i Ahiloda, no mpitadidy ny raharaham-panjakana.",
			"16": "<sup>16</sup> Ary Zadoka, zanak’i Ahitoba, sy Abimeleka, zanak’i Abiatara, no mpisorona; ary Saosa no mpanoratra.",
			"17": "<sup>17</sup> Ary Benaia, zanak’i Joiada, no mpifehy ny miaramila mpiambina andriana; ary ny zanak’i Davida no lehibe tamin’izay nanolotra ny mpanjaka."
		},
		"19": {
			"1": "<sup>1</sup> ARY rehefa afaka izany, dia maty Nahasy, mpanjakan’ny taranak’i Amona, ary ny zanany no nanjaka nandimby azy.",
			"2": "<sup>2</sup> Ary hoy Davida: Hanisy soa an’i Hanona, zanak’i Nahasy, aho, fa soa no nataon’ny rainy tamiko. Ary Davida naniraka olona hitsapa alahelo azy noho ny amin-drainy. Ary tonga tany amin’i Hanona tany amin’ny tanin’ny Amonita ny mpanompon’i Davida hitsapa alahelo azy.",
			"3": "<sup>3</sup> Ary hoy ireo mpanapaka ny taranak’i Amona tamin’i Hanona: Moa ataonao ho fanajana ny rainao no anirahan’i Davida hitsapa alahelo anao? Fa tsy hizaha sy handrava ary hisafo ny tany va no nalehan’ireo mpanompony ireo atỳ aminao?",
			"4": "<sup>4</sup> Dia nosamborin’i Hanona ny mpanompon’i Davida ka noharatany, sady nofolonany hatreo amin’ny foto-peny ny akanjony, ka dia nalefany hody izy.",
			"5": "<sup>5</sup> Ary dia nisy lasa nanambara tamin’i Davida izay natao tamin’ireo lehilahy ireo; ary izy naniraka hitsena azy, fa afa-baraka loatra izy, ka dia hoy ny mpanjaka: Mitoera any Jeriko aloha ambara-panirin’ny somotrareo, vao mody hianareo.",
			"6": "<sup>6</sup> Ary nony hitan’ny taranak’i Amona fa efa nanao izay nampangidy hoditra azy tamin’i Davida izy, dia naniraka nampanatitra talenta volafotsy arivo hakaramany kalesy sy mpitaingin-tsoavaly any Mesopotamia sy Syriamaka ary Zoba Hanona sy ny taranak’i Amona.",
			"7": "<sup>7</sup> Dia nanakarama kalesy roa arivo amby telo alina mbamin’ny mpanjakan’i Maka sy ny olony izy; ary tonga ireo ka nitoby tandrifin’i Medeba. Ary nivory ny taranak’ Amona niala tany amin’ny tanànany avy ka tamy hiady.",
			"8": "<sup>8</sup> Ary nony ren’i Davida izany, dia naniraka an’i Joaba sy ny miaramila rehetra izy, dia ny lehilahy mahery.",
			"9": "<sup>9</sup> Ary ny taranak’i Amona nivoaka ka nilahatra hiady teo anoloan’ny vavahadin’ny tanàna; fa ireo mpanjaka tonga ireo kosa dia nitokana teny an-tsaha.",
			"10": "<sup>10</sup> Ary rehefa hitan’i Joaba fa sady teo anoloany no teo ivohony ny fahavalo, dia nifidy olona tamin’ireo voafantina tamin’ny Isiraely rehetra izy, ka nalahany hiady tamin’ny Syriana.",
			"11": "<sup>11</sup> Ary ny olona sisa kosa nomeny ho entin’i Abisay rahalahiny, ka dia nilahatra hiady amin’ny taranak’i Amona ireo.",
			"12": "<sup>12</sup> Ary hoy Joaba: Raha tsy tohako ny Syriana, dia vonjeo aho; fa raha tsy tohanao kosa ny taranak’i Amona, dia hovonjeko hianao;",
			"13": "<sup>13</sup> mahereza, ary aoka hitombandahy isika ho an’ny firenentsika sy ireo tanànan’Andriamanitsika; ary aoka Jehovah hanao izay sitrapony.",
			"14": "<sup>14</sup> Ary nandroso hiady tamin’ny Syriana Joaba sy ny olona nanaraka azy; ka dia nandositra niala teo anoloany ireo.",
			"15": "<sup>15</sup> Ary nony hitan’ny taranak’i Amona fa nandositra ny Syriana, dia mba nandositra niala teo anoloan’i Abisay, rahalahin’i Joaba, koa izy ka niditra tao an-tanàna. Ary dia tonga tany Jerosalema Joaba.",
			"16": "<sup>16</sup> Ary nony hitan’ny Syriana fa resy teo anoloan’ny Isiraely izy, dia naniraka olona izy haka ny Syriana izay tany an-dafin’ny Ony; ary Sobaka, komandin’ny miaramilan’i Hadarezera, no nitarika azy.",
			"17": "<sup>17</sup> Ary nony nambara tamin’i Davida izany, dia namory ny Isiraely rehetra izy ka lasa nita an’i Jordana, dia tonga teo aminy ka nilahatra hiady aminy. Ary nony nilahatra hiady amin ny Syriana Davida, dia niady taminy ireo.",
			"18": "<sup>18</sup> Fa vaky nandositra teo anoloan’ny Isiraely ny Syriana, ka nahafaty ny teo amin’ny kalesy fito arivo sy miaramila an-tongotra efatra alina tamin’ny Syriana Davida; ary matiny koa Sobaka, komandin’ny miaramila.",
			"19": "<sup>19</sup> Ary nony hitan’ireo nanoa an’i Hadarezera fa resy teo anoloan’ny Isiraely izy, dia nanao fihavanana tamin’i Davida izy ka nanompo azy; ary tsy nety nanampy ny taranak’i Amona intsony ny Syriana."
		},
		"20": {
			"1": "<sup>1</sup> Ary rehefa niherina ny taona, ka mby amin’ny andro fandehanan’ny mpanjaka hiady, dia nitondra ny miaramila rehetra Joaba ka nandrava ny tanin’ny taranak’i Amona, dia tonga izy ka nanao fahirano an’i Raba; fa Davida kosa nijanona tany Jerosalema ihany. Ary Joaba nahafaka an’i Raba, ka dia nandrava azy.",
			"2": "<sup>2</sup> Dia nesorin’i Davida ny satro-boninahitr’ilay mpanjaka teo an-dohany, ka hitany fa talenta volamena iray no lanjany, sady nisy vato soa, ary dia nisatrohan’i Davida. Ary navoakany ny babo azo tao an-tanàna ka be dia be.",
			"3": "<sup>3</sup> Ary dia navoakany koa ny olona izay tao, ka nasiany tamin’ny tsofa sy tamin’ny fangao-by lehibe sy ny famaky; toy izany no nataon’i Davida tamin’ny tanànan’ny taranak’i Amona rehetra. Ary Davida sy ny vahoaka rehetra dia niverina nankany Jerosalema.",
			"4": "<sup>4</sup> ARY nony afaka izany, dia nisy ady tamin’ny Filistina tao Gazera; tamin’izany no namonoan’i Sibekay Hosatita an’i Sipay, izay isan’ny taranaky ny Refaïta, ka dia naetriny ny Filistina.",
			"5": "<sup>5</sup> Ary dia nisy ady tamin’ny Filistina indray ary Elanana, zanak’i Jaïra, namono an’i Lamy, rahalahin’i Goliata Gatita, izay nanan-jaran-defona tahaka ny vodi-tenon’ny mpanenona.",
			"6": "<sup>6</sup> Ary nisy ady indray tao Gata; ary tao nisy lehilahy vaventibe izay enin-drantsana, ka dia efatra amby roa-polo no isan’ny rantsan-tànany sy ny rantsan-tongony; ary izy koa mba isan’ny taranaky ny Refaïta.",
			"7": "<sup>7</sup> Ary nony nihaika ny Isiraely izy, dia nahafaty azy Jonatana, zanak’i Simea, rahalahin’i Davida.",
			"8": "<sup>8</sup> Ireo no taranaky ny Refaïta tao Gata, ka matin’ny tànan’i Davida sy ny tànan’ny mpanompony ireo."
		},
		"21": {
			"1": "<sup>1</sup> ARY Satana nitsangana hampidi-doza tamin’ny Isiraely ka nanome saina an’i Davida hanisa ny Isiraely.",
			"2": "<sup>2</sup> Dia hoy Davida tamin’i Joaba sy tamin’ireo lehiben’ny vahoaka: Mandehana, isao ny Isiraely hatrany Beri-sheba ka hatrany Dana; ary ento atỳ amiko ny isany mba ho fantatro.",
			"3": "<sup>3</sup> Fa hoy Joaba: Jehovah anie hampitombo ny olony ho injato toy izao na firy na firy izy; fa moa tsy mpanompon’ny tompoko izy rehetra, ry mpanjaka tompoko? Koa inona indray no itadiavan’ny tompoko izany zavatra izany? Nahoana no dia hampahadiso ny Isiraely?",
			"4": "<sup>4</sup> Nefa tsy azon’i Joaba nolavina ny tenin’ny mpanjaka; ka dia niainga izy ka nitety ny Isiraely rehetra; dia tonga tany Jerosalema indray izy.",
			"5": "<sup>5</sup> Ary natolotr’i Joaba an’i Davida ny isan’ny olona voalamina, ka nisy iray hetsy sy iray tapitrisa ny lehilahy mahatan-tsabatra teo amin’ny Isiraely; ary fito alina sy efatra hetsy ny lehilahy mahatan-tsabatra teo amin’ny Joda.",
			"6": "<sup>6</sup> Fa ny Levy sy ny Benjamina tsy mba nalaminy, satria fahavetavetana tamin’i Joaba ny tenin’ny mpanjaka.",
			"7": "<sup>7</sup> Ary tsy sitrak’Andriamanitra izany zavatra izany; ka dia namely ny Isiraely Izy.",
			"8": "<sup>8</sup> Ary hoy Davida tamin’Andriamanitra; Efa nanota indrindra aho tamin’izao nataoko izao; koa ankehitriny, mifona aminao aho, esory ny heloky ny mpanomponao, fa efa nanao fahadalana lehibe tokoa aho.",
			"9": "<sup>9</sup> Ary hoy Jehovah tamin’i Gada mpahitan’i Davida:",
			"10": "<sup>10</sup> Mandehana, ka lazao amin’i Davida hoe: Izao no lazain’i Jehovah: Zavatra telo loha no apetrako eto anoloanao, koa fidio izay iray hataoko aminao.",
			"11": "<sup>11</sup> Ary Gada nankany amin’i Davida ka nanao taminy hoe: Izao no lazain’i Jehovah: Mifidia:",
			"12": "<sup>12</sup> Mosary telo taona va? Sa fandringanana eo alohan’ny fahavalonao telo volana, raha tratry ny sabatry ny fahavalonao hianao? Sa ny sabatr’i Jehovah, dia areti-mandringana, no hamely ny tany hateloana, ka Ilay Anjelin’i Jehovah handringana eran’ny tanin’ny Isiraely rehetra? Koa hevero tsara izay valiny ho entiko miverina any amin’Izay naniraka ahy.",
			"13": "<sup>13</sup> Ary hoy Davida tamin’i Gada: Indrisy! poritra loatra aho! nefa aleoko ho azon’ny tànan’i Jehovah,–fa lehibe ny famindrampony–toy izay ho azon’izay tànan’olona.",
			"14": "<sup>14</sup> Dia nasian’i Jehovah areti-mandringana ny Isiraely ka nahafatesana fito alina izy.",
			"15": "<sup>15</sup> Ary Andriamanitra naniraka Anjely hankany Jerosalema handrava azy; ary raha ilay mbola nandrava iny Izy, dia nijery Jehovah ka nanenina ny amin’ny loza ka nanao tamin’Ilay Anjely nandrava hoe: Aoka izay! atsaharo ny tànanao ankehitriny. Ary Ilay Anjelin’i Jehovah nitsangana teo anilan’ny famoloan’i Araona Jebosita.",
			"16": "<sup>16</sup> Ary Davida nanopy ny masony ka nahita Ilay Anjelin’i Jehovah nitsangana teo anelanelan’ny tany sy ny lanitra sady nitana sabatra voatsoaka teny an-tànany, izay natondrony manandrify an’i Jerosalema. Dia niankohoka Davida sy ny loholon’ny Isiraely, izay efa samy nitafy lamba fisaonana.",
			"17": "<sup>17</sup> Ary hoy Davida tamin’Andriamanitra: Tsy izaho va no nandidy hanisa ny vahoaka? Izaho no nanota sy nanao ratsy tokoa; fa ireo ondry ireo kosa, inona moa no mba nataony? Eny, Jehovah Andriamanitro ô, mifona aminao re aho, aoka ny tànanao hamely ahy sy ny mpianakavin’ny raiko, fa aza ny olonao no asianao amin’ny areti-mandringana.",
			"18": "<sup>18</sup> Ary Gada nasain’Ilay Anjelin’i Jehovah hilaza amin’i Davida mba hiakatra hanangana alitara ho an’i Jehovah ao amin’ny famoloan’i Araona Jebosita.",
			"19": "<sup>19</sup> Koa dia niakatra Davida araka ny tenin’i Gada, izay nolazainy tamin’ny anaran’i Jehovah.",
			"20": "<sup>20</sup> Ary Araona niherika ka nahita Ilay Anjely; ary ny zanany efa-dahy izay teo aminy dia niery. Ary Araona nively vary tao tamin’izay.",
			"21": "<sup>21</sup> Ary nony tonga teo amin’i Araona Davida, dia nijery Araona ka nahita an’i Davida, ary dia niala avy teo amin’ny famoloana izy ka niankohoka tamin’ny tany teo anatrehan’i Davida.",
			"22": "<sup>22</sup> Ary hoy Davida tamin’i Araona: Mba omeo ahy amin’izay tokom-bidiny ity famoloana ity hanorenako alitara ho an’i Jehovah; omeo ahy izy, mba hitsaharan’ny areti-mandringana tsy hamely ny vahoaka.",
			"23": "<sup>23</sup> Fa hoy Araona tamin’i Davida: Ento ary ho anao, ary aoka ny mpanjaka tompoko hanao izay sitrany; indro, omeko koa ny omby ho fanatitra dorana ary ny fivelezam-bary ho kitay ary ny vary tritika ho fanatitra hohanina; foiko avokoa izy rehetra.",
			"24": "<sup>24</sup> Fa hoy Davida mpanjaka tamin’i Araona: Tsia, fa hovidiko mihitsy araka izay tokom-bidiny; fa tsy haka izay anao ho an’i Jehovah aho ary tsy hanatitra fanatitra dorana amin’izay azoko fotsiny.",
			"25": "<sup>25</sup> Dia nomen’i Davida sekely volamena lanjan’enin-jato Araona ho vidin’ny tany.",
			"26": "<sup>26</sup> Ary Davida nanorina alitara teo ho an’i Jehovah ka nanatitra fanatitra dorana sy fanati-pihavanana; dia niantso an’i Jehovah izy ka novaliany tamin’ny afo avy tany an-danitra, izay latsaka teo amin’ny alitara fandoroana ny fanatitra dorana.",
			"27": "<sup>27</sup> Ary Jehovah nandidy Ilay Anjely, ka dia nampidiriny tamin’ny tranony ny sabany.",
			"28": "<sup>28</sup> Tamin’izany andro izany, nony hitan’i Davida fa novalian’i Jehovah teo amin’ny famoloan’i Araona Jebosita izy, dia namono zavatra hatao fanatitra teo izy.",
			"29": "<sup>29</sup> Fa ny tabernakelin’i Jehvoah izay nataon’i Mosesy tany an-efitra sy ny alitara fandoroana ny fanatitra dorana kosa dia teo amin’ny fitoerana avo tao Gibeona fahizay;",
			"30": "<sup>30</sup> nefa Davida tsy nahazo nankeo anoloan’izany hanontany amin’Andriamanitra; fa raiki-tahotra izy noho ny sabatr’Ilay Anjelin’i Jehovah."
		},
		"22": {
			"1": "<sup>1</sup> Dia hoy Davida: Eto no ho tranon’i Jehovah Andriamanitra, ary eto no ho alitara fandoroana ny fanatitra dorana ho an’ny Isiraely.",
			"2": "<sup>2</sup> ARY nasain’i Davida novorina ny olona hafa firenena izay tany amin’ny tanin’ny Isiraely, ka nanendreny tambato hipai-bato hanaovana ny tranon’Andriamanitra.",
			"3": "<sup>3</sup> Ary Davida nahavonona vy betsaka hatao hombon’ny lela-vavahady sy ny fikambanany, ary varahina betsaka tsy hita lanja",
			"4": "<sup>4</sup> sy hazo sedera tsy hita isa; fa ny mponina tany Sidona sy Tyro nitondra hazo sedera betsaka ho any amin’i Davida.",
			"5": "<sup>5</sup> Fa hoy Davida: Solomona zanako mbola zaza sady tanora fanahy, ary ny trano hatao ho an’i Jehovah dia tsy maintsy ho lehibe indrindra, mba hiely any amin’ny tany rehetra ny lazany sy ny voninahiny, koa hovononiko rahateo ny zavatra ho enti-manao azy. Ka dia nahavonona betsaka Davida, raha mbola velona izy.",
			"6": "<sup>6</sup> Ary niantso an’i Solomona zanany izy ka nandidy azy hanao trano ho an’i Jehovah, Andriamanitry ny Isiraely,",
			"7": "<sup>7</sup> ary nanao taminy hoe: Anaka, efa mby tato am-poko ny hanao trano ho an’ny anaran’i Jehovah Andriamanitro;",
			"8": "<sup>8</sup> saingy nisy tenin’i Jehovah tonga tamiko nanao hoe: Be ny ra efa nalatsakao, ary ady lehibe no efa nataonao; koa tsy mahazo manao trano ho an’ny anarako hianao, satria be ny ra efa nalatsakao tamin’ny tany teo imasoko.",
			"9": "<sup>9</sup> Fa, indro, hianao hiteraka zazalahy, izay ho lehilahy miadana, ary hampiadaniko tsy hisy hahazo azy ny fahavalony rehetra manodidina; koa ny anarany dia hatao hoe Solomona; ary hampiadaniko sy hampandriko fahizay ny Isiraely amin’ny androny.",
			"10": "<sup>10</sup> Izy no hanao trano ho an’ny anarako; ary izy ho zanako, ary Izaho ho rainy, ary Izaho hampitoetra ny seza fiandrianan’ny fanjakany amin’ny Isiraely mandrakizay.",
			"11": "<sup>11</sup> Koa ankehitriny, anaka, ho aminao anie Jehovah, ka hambinina anie hianao, fa ataovy ny tranon’i Jehovah Andriamanitrao araka izay nilazany anao.",
			"12": "<sup>12</sup> Ary homen’i Jehovah saina sy fahendrena anie hianao ka hatsangany ho mpanapaka ny Isiraely, mba hitandremanao ny làlan’i Jehovah Andriamanitrao.",
			"13": "<sup>13</sup> Ary amin’izany dia hambinina hianao, raha mitandrina hankatò ny lalàna sy ny fitsipika izay nandidian’i Jehovah an’i Mosesy ho an’ny Isiraely; mahereza sy matanjaha; aza matahotra na mivadi-po.",
			"14": "<sup>14</sup> Ary, he! fa na dia be fahoriana aza aho, dia nahavory talenta volamena iray hetsy sy talenta volafotsy iray tapitrisa ary varahina sy vy betsaka tsy hita lanja ho enti-manao ny tranon’i Jehovah; ary efa nahavonona hazo sy vato koa aho; ka dia ampionao kosa ireo.",
			"15": "<sup>15</sup> Ary koa, efa manana mpiasa betsaka hianao, dia mpikapa hazo sy tambato sy mpandrafitra ary olona mahay ny asa samy hafa rehetra.",
			"16": "<sup>16</sup> Ny volamena sy ny volafotsy ary ny varahina sy ny vy dia tsy hita isa. Koa mitsangàna hianao, ka manaova, ary homba anao anie Jehovah.",
			"17": "<sup>17</sup> Ary Davida nandidy ny lehibe rehetra tamin’ny Isiraely hanampy an’i Solomona zanany hoe:",
			"18": "<sup>18</sup> Tsy momba anareo va Jehovah Andriamanitrareo ka efa nampiadana anareo amin’ny manodidina? Fa efa natolony teo an-tànako ny mponina amin’ny tany; ary ny tany efa resy teo anoloan’i Jehovah sy teo anoloan’ny olony.",
			"19": "<sup>19</sup> Koa ankehitriny ampitadiavo an’i Jehovah Andriamanitrareo ny fonareo sy ny fanahinareo, ka mitsangàna, ary manaova ny fitoerana masina ho an’i Jehovah Andriamanitra, hampidirana ny fiaran’ny faneken’i Jehovah sy ny fanaka masin’Andriamanitra ho ao amin’ny trano izay hatao ho an’ny anaran’i Jehovah."
		},
		"23": {
			"1": "<sup>1</sup> ARY nony efa ela niainana sy tratrantitra tsara Davida, dia Solomona zanany no nataony mpanjaka tamin’ny Isiraely.",
			"2": "<sup>2</sup> Ary novoriny ny lehiben’ny Isiraely rehetra mbamin’ny mpisorona sy ny Levita.",
			"3": "<sup>3</sup> Ary nisaina ny Levita hatramin’ny telo-polo taona no ho miakatra, ka valo arivo amby telo alina no isan’ny lehilahy.",
			"4": "<sup>4</sup> Ary hoy Davida: Ny efatra arivo amby roa alina amin’ireo dia ataoko ho tonian’ny asa ao an-tranon’i Jehovah, ary ny enina arivo ataoko mpifehy sy mpitsara,",
			"5": "<sup>5</sup> ary ny efatra arivo ho mpiandry varavarana, ary ny efatra arivo ho mpidera an’i Jehovah amin’ny zavatra namboariko hiderana Azy.",
			"6": "<sup>6</sup> Ary nozarazarain’i Davida ho antokony ireo araka ny zanakalahin’i Levy, dia Gersona sy Kehata sy Merary:–",
			"7": "<sup>7</sup> Tamin’ny Gersonita dia Ladana sy Simey.",
			"8": "<sup>8</sup> Ny zanakalahin’i Ladana dia Jehiela lohany sy Zetama ary Joela; telo izy.",
			"9": "<sup>9</sup> Ny zanakalahin’i Simey dia Selomota sy Haziela ary Harana; telo izy. Ireo no lohany tamin’ny fianakavian’i Ladana.",
			"10": "<sup>10</sup> Ary ny zanakalahin’i Simey dia Jahata sy Zina sy Jeosy ary Beria; izy efa-dahy ireo no zanakalahin’i Simey.",
			"11": "<sup>11</sup> Ary Jahata no lohany, ary Ziza no faharoany; fa Jeosy sy Beria tsy nanana zanakalahy maro, ka dia nisaina ho fianakaviana iray sy antokony iray izy.",
			"12": "<sup>12</sup> Ny zanakalahin’i Kehata dia Amrama sy Jizara sy Hebrona ary Oziela; efatra izy.",
			"13": "<sup>13</sup> Ny zanakalahin’i Amrama dia Arona sy Mosesy. Ary Arona natokana ka nohamasinina ho masina indrindra, dia izy sy ny taranany mandrakizay, handoro ditin-kazo manitra eo anatrehan’i Jehovah sy hanao fanompoam-pivavahana ho Azy ary hitso-drano amin’ny anarany mandrakizay.",
			"14": "<sup>14</sup> Nefa ny amin’i Mosesy, lehilahin’Andriamanitra, dia nisaina tamin’ny firenen’i Levy ihany ny zanany.",
			"15": "<sup>15</sup> Ny zanakalahin’i Mosesy dia Gersona sy Eliezera.",
			"16": "<sup>16</sup> Tamin’ny zanakalahin’i Gersona dia Seboela no lohany.",
			"17": "<sup>17</sup> Ary tamin’ny zanakalahin’i Eliezera dia Rehabia no lohany (Eliezera anefa tsy nanan-janaka hafa); fa ny zanak’i Rehabia dia maro indrindra.",
			"18": "<sup>18</sup> Tamin’ny zanakalahin’i Jizara dia Selomota no lohany.",
			"19": "<sup>19</sup> Ny zanakalahin’i Hebrona, dia Jeria no lahimatoa, Amaria no lahiaivo, Jahaziela no fahatelo, ary Jekameama no faralahy.",
			"20": "<sup>20</sup> Ny zanakalahin’i Oziela, dia Mika no lahimatoa, ary Jisia no faralahy.",
			"21": "<sup>21</sup> Ny zanakalahin’i Merary dia Maly sy Mosy. Ny zanakalahin’i Maly dia Eleazara sy Kisy.",
			"22": "<sup>22</sup> Ary maty Eleazara ka tsy nanan-janakalahy, fa vavy avokoa; ary ireny zanakalahin’i Kisy havany no nanambady ireo.",
			"23": "<sup>23</sup> Ny zanakalahin’i Mosy dia Maly sy Edera ary Jeremota; telo izy.",
			"24": "<sup>24</sup> Ireo no zanakalahin’i Levy araka ny fianakaviany, dia ny lohan’ny fianakaviana tamin’ny antokony avy araka ny nanisana ny anarany tsirairay, dia izay nanao ny fanompoana tao an-tranon’i Jehovah, hatramin’ny roa-polo taona no ho miakatra.",
			"25": "<sup>25</sup> Fa hoy Davida: Jehovah, Andriamanitry ny Isiraely, efa nanome fitsaharana ho an’ny olony sady mitoetra eto Jerosalema mandrakizay.",
			"26": "<sup>26</sup> Ary ny Levita kosa dia tsy hitondra ny tabernakely na ny fanaka enti-manompo ao aminy intsony.",
			"27": "<sup>27</sup> Fa araka ny fara-tenin’i Davida dia nisaina hatramin’ny roa-polo taona no ho miakatra ny Levita.",
			"28": "<sup>28</sup> Ary ny raharahany dia ny hanampy ny taranak’i Arona amin’ny fanompoana ao an-tranon’i Jehovah amin’ireo kianja sy efi-trano tao ary amin’ny fanadiovana ny zavatra masina rehetra sy ny fanompoana ao an-tranon’Andriamanitra,",
			"29": "<sup>29</sup> ary amin’ny mofo aseho sy ny koba tsara toto ho fanatitra hohanina sy amin’ny mofo manify tsy misy masirasira sy amin’izay endasina amin’ny lapoely sy amin’izay vonton-diloilo ary amin’ny famarana sy ny fandrefesana rehetra,",
			"30": "<sup>30</sup> ary ny hitsangana isa-maraina hisaotra sy hidera an’i Jehovah, ary toy izany koa amin’ny hariva,",
			"31": "<sup>31</sup> ary ny hanatitra ny fanatitra dorana rehetra ho an’i Jehovah–na amin’ny Sabata, na amin’ny voaloham-bolana, na amin’ny fotoam-pivavahana hafa–dia araka ny isany avy, sy araka ny fanao voatendry hanaovana azy mandrakariva eo anatrehan’i Jehovah.",
			"32": "<sup>32</sup> Aoka ho toy izany no hitandremany ny anjara-raharahany ny amin’ny trano-lay fihaonana sy ny anjara-raharahany ny amin’ny fitoerana masina ary ny anjara-raharahany ny amin’ny taranak’i Arona rahalahiny ho fanompoana ao an-tranon’i Jehovah."
		},
		"24": {
			"1": "<sup>1</sup> ARY ny amin’ny taranak’i Arona dia izao no nizarana azy ho antokony: Ny zanakalahin’i Arona dia Nadaba sy Abiho sy Eleazara ary Itamara;",
			"2": "<sup>2</sup> fa Nadaba sy Abiho dia maty talohan-drainy sady samy tsy nanan-janaka, koa dia Eleazara sy Itamara no nanao fisoronana.",
			"3": "<sup>3</sup> Ary Davida sy Zadoka, isan’ny taranak’i Eleazara, sy Ahimeleka, isan’ny taranak’Itamara, dia nizara azy araka ny antokony sy ny fanompoany avy.",
			"4": "<sup>4</sup> Ary ny lohany tamin’ireo, dia tamin’ny taranak’i Eleazara, no maromaro kokoa noho ny tamin’ny taranak’Itamara, ka dia nozarainy toy izao izy: Tamin’ny taranak’i Eleazara dia enina ambin’ny folo no lohan’ny fianakaviana; ary tamin’ny taranak’Itamara dia valo kosa no lohan’ny fianakaviana.",
			"5": "<sup>5</sup> Ary samy nozarazaraina tamin’ny filokana avokoa izy rehetra; fa samy mpifehy masina, dia mpifehy ho an’Andriamanitra avokoa, na ny avy tamin’ny taranak’i Eleazara, na ny avy tamin’ny taranak’Itamara.",
			"6": "<sup>6</sup> Ary Semaia, zanak’i Netanela, mpanoratra anankiray tamin’ny Levita, no nanoratra azy teo anatrehan’ny mpanjaka sy ny mpanapaka sy Zadoka mpisorona sy Ahimeleka, zanak’i Abiatara, sy teo anatrehan’ny lohan’ny fianakaviana tamin’ny mpisorona sy ny Levita: fianakaviana iray no nalaina ho an’i Eleazara, ary iray kosa ho an’Itamara.",
			"7": "<sup>7</sup> Ny loka voalohany dia azon’i Joariba, ny faharoa an’i Jedaia,",
			"8": "<sup>8</sup> ny fahatelo an’i Harima, ny fahefatra an’i Seorima,",
			"9": "<sup>9</sup> ny fahadimy an’i Malkia, ny fahenina an’i Miamina,",
			"10": "<sup>10</sup> ny fahafito an’i Hakoza, ny fahavalo an’i Abia,",
			"11": "<sup>11</sup> ny fahasivy an’i Jesoa, ny fahafolo an’i Sekania.",
			"12": "<sup>12</sup> ny fahiraika ambin’ny folo an’i Eliasiba, ny faharoa ambin’ny folo an’i Jakima,",
			"13": "<sup>13</sup> ny fahatelo ambin’ny folo an’i Hopa, ny fahefatra ambin’ny folo an’i Jesebaba,",
			"14": "<sup>14</sup> ny fahadimy ambin’ny folo an’i Bilga, ny fahenina ambin’ny folo an’i Imera,",
			"15": "<sup>15</sup> ny fahafito ambin’ny folo an’i Hezira, ny fahavalo ambin’ny folo an’i Hafizeza,",
			"16": "<sup>16</sup> ny fahasivy ambin’ny folo an’i Petahia, ny faharoa-polo an’i Jehezekela,",
			"17": "<sup>17</sup> ny fahiraika amby roa-polo an’i Jakina, ny faharoa amby roa-polo an’i Gamola,",
			"18": "<sup>18</sup> ny fahatelo amby roa-polo an’i Delaia, ny fahefatra amby roa-polo an’i Mazia.",
			"19": "<sup>19</sup> Ireo avy no antokony amin’ny fanompoany izay idirany ao an-tranon’i Jehovah araka ny fitsipika voatendrin’i Arona rainy, araka ny nandidian’i Jehovah, Andriamanitry ny Isiraely, azy.",
			"20": "<sup>20</sup> Ary tamin’ny taranak’i Levy sisa dia izao; Tamin’ny taranak’i Amrama dia Sobaela; tamin’ny zanak’i Sobaela dia Jedaia.",
			"21": "<sup>21</sup> Ny amin’i Rehabia: tamin’ny taranak’i Rehabia dia Jisia no voalohany.",
			"22": "<sup>22</sup> Tamin’ny Jizarita dia Selomota; tamin’ny taranak’i Selomota dia Jahata.",
			"23": "<sup>23</sup> Ary ny amin’ny taranak’i Hebrona dia Jeria no voalohany, Amaria no faharoa, Jahaziela no fahatelo, Jekameama no fahefatra.",
			"24": "<sup>24</sup> Ny zanakalahin’i Oziela dia Mika; tamin’ny taranak’i Mika dia Samira;",
			"25": "<sup>25</sup> ny rahalahin’i Mika dia Jisia; tamin’ny taranak’i Jisia dia Zakaria.",
			"26": "<sup>26</sup> Ny zanakalahin’i Merary dia Maly sy Mosy; ny zanakalahin’i Jazia dia Beno.",
			"27": "<sup>27</sup> Ny zanakalahin’i Merary tamin’i Jazia dia Beno sy Sohama sy Zakora ary Ibry.",
			"28": "<sup>28</sup> Avy tamin’i Maly no nihavian’i Eleazara, izay tsy nanan-janakalahy.",
			"29": "<sup>29</sup> Ny amin’i Kisy, ny zanani-lahy dia Jeramela.",
			"30": "<sup>30</sup> Ary ny zanakalahin’i Mosy dia Maly sy Edera ary Jerimota. Ireo no taranaky ny Levita araka ny fianakaviany avy.",
			"31": "<sup>31</sup> Ary ireo dia mba niara-niloka tamin’ny rahalahiny, taranak’i Arona, teo anatrehan’i Davida mpanjaka sy Zadoka sy Ahimeleka sy ny lohan’ny fianakavian’ny mpisorona sy ny Levita: niara-niloka avokoa na ny zokiny na ny zandriny."
		},
		"25": {
			"1": "<sup>1</sup> ARY ny taranak’i Asafa sy Hemana ary Jedotona dia natokan’i Davida sy ireo komandin’ny miaramila ho amin’ny fanompoana mba haminany amin’ny lokanga sy ny valiha ary ny kipantsona; ary izao no isan’ny mpanao araka ny fanompoany avy:–",
			"2": "<sup>2</sup> Tamin’ny zanakalahin’i Asafa dia Zakora sy Josefa sy Netania ary Asarela, zanak’i Asafa, nofehezin’i Asafa, izay naminany araka izay nifehezan’ny mpanjaka azy.",
			"3": "<sup>3</sup> Tamin’i Jedotona, ny zanany dia Gedalia sy Zery sy Jesaia sy Hasabia ary Matitia, enin-dahy ireo ka nofehezin’i Jedotona rainy, izay naminany tamin’ny lokanga hidera sy hisaotra an’i Jehovah.",
			"4": "<sup>4</sup> Tamin’i Hemana, ny zanany dia Bokia, Matania, Oziela, Seboela sy Jerimota, Hanania, Hanany, Eliata, Gidalty sy Romamti-ezera sy Josbekasa sy Maloty sy Hotira ary Mahaziota.",
			"5": "<sup>5</sup> Ireo rehetra ireo no zanak’i Hemana, mpahitan’ny mpanjaka ny amin’ny tenin’Andriamanitra, hanandratra ny tandroka. Ary Hemana dia efa nomen’Andriamanitra zazalahy efatra ambin’ny folo sy zazavavy telo.",
			"6": "<sup>6</sup> Ireo rehetra ireo no fehin’ny rainy tamin’ny hira natao tao an-tranon’i Jehovah tamin’ny kipantsona sy ny valiha sy ny lokanga hanaovana ny fanompoana ao an-tranon’Andriamanitra; Asafa sy Jedotona ary Hemana dia nofezehin’ny mpanjaka.",
			"7": "<sup>7</sup> Ary ny isany mbamin’ny rahalahiny, izay efa nianatra ny hira ho an’i Jehovah–izay rehetra nahay–dia valo amby valo-polo amby roan-jato.",
			"8": "<sup>8</sup> Ary nanao filokana ny amin’ny anjara-raharahany avy izy, na ny kely na ny lehibe, na ny mpampianatra na ny mpianatra.",
			"9": "<sup>9</sup> Ary ny loka voalohany dia azon’i Josefa avy amin’ny taranak’i Asafa; ny faharoa an’i Gedalia, izy mbamin’ny rahalahiny sy ny zanany, dia roa ambin’ny folo;",
			"10": "<sup>10</sup> ny fahatelo an’i Zakora, izy mbamin’ny zanany sy ny rahalahiny, dia roa ambin’ny folo;",
			"11": "<sup>11</sup> ny fahefatra an’i Jizry, izy mbamin’ny zanany sy ny rahalahiny, dia roa ambin’ny folo;",
			"12": "<sup>12</sup> ny fahadimy an’i Netania, izy mbamin’ny zanany sy ny rahalahiny, dia roa ambin’ny folo;",
			"13": "<sup>13</sup> ny fahenina an’i Bokia, izy mbamin’ny zanany sy ny rahalahiny, dia roa ambin’ny folo;",
			"14": "<sup>14</sup> ny fahafito an’i Jesarela, izy mbamin’ny zanany sy ny rahalahiny, dia roa ambin’ny folo;",
			"15": "<sup>15</sup> ny fahavalo an’i Jesaia, izy mbamin’ny zanany sy ny rahalahiny, dia roa ambin’ny folo;",
			"16": "<sup>16</sup> ny fahasivy an’i Matania, izy mbamin’ny zanany sy ny rahalahiny, dia roa ambin’ny folo;",
			"17": "<sup>17</sup> ny fahafolo an’i Simey, izy mbamin’ny zanany sy ny rahalahiny, dia roa ambin’ny folo;",
			"18": "<sup>18</sup> ny fahiraika ambin’ny folo an’i Azarela, izy mabmin’ny zanany sy ny rahalahiny, dia roa ambin’ny folo;",
			"19": "<sup>19</sup> ny faharoa ambin’ny folo an’i Hasabia, izy mbamin’ny zanany sy ny rahalahiny, dia roa ambin’ny folo;",
			"20": "<sup>20</sup> ny fahatelo ambin’ny folo an’i Sobaela, izy mbamin’ny zanany sy ny rahalahiny, dia roa ambin’ny folo;",
			"21": "<sup>21</sup> ny fahefatra ambin’ny folo an’i Matitia, izy mbamin’ny zanany sy ny rahalahiny dia roa ambin’ny folo;",
			"22": "<sup>22</sup> ny fahadimy ambin’ny folo an’i Jeremota, izy mbamin’ny zanany sy ny rahalahiny dia roa ambin’ny folo;",
			"23": "<sup>23</sup> ny fahenina ambin’ny folo an’i Hanania, izy mbamin’ny zanany sy ny rahalahiny, dia roa ambin’ny folo;",
			"24": "<sup>24</sup> ny fahafito ambin’ny folo an’i Josbekasa, izy mbamin’ny zanany sy ny rahalahiny, dia roa ambin’ny folo;",
			"25": "<sup>25</sup> ny fahavalo ambin’ny folo an’i Hanany, izy mbamin’ny zanany sy ny rahalahiny, dia roa ambin’ny folo;",
			"26": "<sup>26</sup> ny fahasivy ambin’ny folo an’i Maloty, izy mbamin’ny zanany sy ny rahalahiny, dia roa ambin’ny folo;",
			"27": "<sup>27</sup> ny faharoa-polo an’i Eliata, izy mbamin’ny zanany sy ny rahalahiny, dia roa ambin’ny folo;",
			"28": "<sup>28</sup> ny fahiraika amby roa-polo an’i Hotira, izy mbamin’ny zanany sy ny rahalahiny, dia roa ambin’ny folo;",
			"29": "<sup>29</sup> ny faharoa amby roa-polo an’i Gidalty, izy mbamin’ny zanany sy ny rahalahiny, dia roa ambin’ny folo;",
			"30": "<sup>30</sup> ny fahatelo amby roa-polo an’i Mahaziota, izy mbamin’ny zanany sy ny rahalahiny, dia roa ambin’ny folo;",
			"31": "<sup>31</sup> ny fahefatra amby roa-polo an’i Romamti-ezera, izy mbamin’ny zanany sy ny rahalahiny, dia roa ambin’ny folo."
		},
		"26": {
			"1": "<sup>1</sup> ARY ny amin’ny antokon’ny mpiandry varavarana:– Tamin’ny Koraïta dia Meselemia, zanak’i Kore, avy tamin’ny zanak’i Asafa.",
			"2": "<sup>2</sup> Ary ny zanakalahin’i Meselemia dia Zakaria, lahimatoa, sy Jediaela, lahiaivo, sy Zebadia, fahatelo, sy Jatniela, fahefatra,",
			"3": "<sup>3</sup> sy Elama, fahadimy, sy Johanana, fahenina, sy Elihoenay, faralahy.",
			"4": "<sup>4</sup> Ary ny zanakalahin’i Obed-edoma dia Semaia, lahimatoa, sy Jozabada, lahiaivo, sy Joa, fahatelo, sy Sakara, fahefatra, sy Netanela, fahadimy,",
			"5": "<sup>5</sup> sy Amiela, fahenina, sy Isakara, fahafito, sy Peoletahy, faralahy; fa Andriamanitra efa nitahy azy.",
			"6": "<sup>6</sup> Ary Semaia zanany niteraka zazalahy maromaro izay samy tonga mpanapaka tamin’ny fianakavian-drainy, satria samy lehilahy mahay izy.",
			"7": "<sup>7</sup> Ny zanakalahin’i Semaia dia Otny sy Refaela sy Obeda ary Elzabada mbamin’ireo rahalahiny, lehilahy mahay, dia Eliho sy Semakia.",
			"8": "<sup>8</sup> Ireo rehetra ireo no avy tamin’ny zanak’i Obed-edoma, dia izy sy ny zanany sy ny rahalahiny, izay samy lehilahy mahery sady matanjaka amin’ny fanompoany; roa amby enim-polo no an’i Obed-edoma.",
			"9": "<sup>9</sup> Ary Meselemia nanan-janaka sy rahalahy valo ambin’ny folo, samy lehilahy mahay avokoa.",
			"10": "<sup>10</sup> Ary Hosa, tamin’ny taranak’i Merary, nanan-janakalahy, dia Simry, lohany (fa na dia tsy lahimatoa aza izy, dia izy no nataon-drainy ho lohany),",
			"11": "<sup>11</sup> sy Hilkia, lahiaivo, sy Tebalia, fahatelo, ary Zakaria, fahefatra; ny zanakalahin’i Hosa sy ny rahalahiny rehetra dia telo ambin’ny folo.",
			"12": "<sup>12</sup> Ireo lohan’ny tonian’ny mpiandry varavarana ireo dia nomena anjara-raharaha niaraka tamin’ny rahalahiny hanao fanompoam-pivavahana ao an-tranon’i Jehovah.",
			"13": "<sup>13</sup> Ary izy ireo, na ny kely na ny lehibe, dia nanao filokana avokoa araka ny fianakaviany ny ho amin’ny isam-bavahady.",
			"14": "<sup>14</sup> Koa ny loka tamin’ny atsinanana dia azon’i Selemia. Ary nanaovana filokana Zakaria zanany, mpanolo-tsaina hendry, ka ny loka tamin’ny avaratra no azony.",
			"15": "<sup>15</sup> An’i Obed-edoma ny atsimo, ary an’ny zanany ny trano firaketana.",
			"16": "<sup>16</sup> An’i Sopima sy Hosa ny andrefana mbamin’ny vavahady Saleketa amin’ny làlana miakatra; ary dia feno avokoa ny fiambenana.",
			"17": "<sup>17</sup> Ny atsinanana nisy Levita enina, ny avaratra nisy efatra isan-andro, ny atsimo nisy efatra isan-andro, ary ny trano firaketana nisy efa-dahy nizara ho roa avy.",
			"18": "<sup>18</sup> Tany Parbara andrefana nisy efatra amin’ny làlana, ary roa tao Parbara.",
			"19": "<sup>19</sup> Dia ireo no antokon’ny mpiandry varavarana tamin’ny taranak’i Kora sy ny taranak’i Merary.",
			"20": "<sup>20</sup> ARY tamin’ny Levita dia Ahia no tonian’ny rakitry ny tranon’Andriamanitra sy ny zavatra nohamasinina ho rakitra.",
			"21": "<sup>21</sup> Ny taranak’i Ladana, ilay Ladana Gersonita, ireo lohan’ny fianakavian’i Ladana Gersonita, dia ny Jehielita.",
			"22": "<sup>22</sup> Ary ny taranaky ny Jehielita, dia Zetama sy Joela rahalahiny, no tonian’ny rakitry ny tranon’i Jehovah.",
			"23": "<sup>23</sup> Tamin’ny Amramita sy ny Jizarita sy ny Hebronita ary ny Ozielita",
			"24": "<sup>24</sup> dia Seboela, avy tamin’ny taranak’i Gersona, zanak’i Mosesy, no tonian’ny rakitra.",
			"25": "<sup>25</sup> Ary ny rahalahiny avy tamin’i Eliezera dia Rehabia, zanak’i Eliezera, sy Jesaia, zanak’i Rehabia, sy Jorama, zanak’i Jesaia, sy Zikry, zanak’i Jorama, ary Selomota, zanak’i Zikry.",
			"26": "<sup>26</sup> Io Selomota io sy ny rahalahiny no tonian’ny zavatra nohamasinina ho rakitra, dia izay nohamasinin’i Davida mpanjaka sy ny lohan’ny fianakaviana ary ny mpifehy arivo sy ny mpifehy zato ary ny komandin’ny miaramila.",
			"27": "<sup>27</sup> Tamin’ny babo izay azony tamin’ny ady nisy nohamasininy ho enti-manamboatra ny tranon’i Jehovah.",
			"28": "<sup>28</sup> Ary izay rehetra nohamasinin’i Samoela mpahita sy Saoly, zanak’i Kisy, sy Abnera, zanak’i Nera, sy Joaba, zanak’i Zeroia, dia izay rehetra nohamasininy, dia Selomota sy ny rahalahiny no nitahiry izany.",
			"29": "<sup>29</sup> Tamin’ny Jizarita dia Kenania sy ny zanany no voatendry ho amin’ny raharaha ivelany amin’ny Isiraely, dia ho mpifehy sy mpitsara.",
			"30": "<sup>30</sup> Ary tamin’ny Hebronita dia Hasabia sy ny rahalahiny, lehilahy mahay fiton-jato amby arivo, no mpifehy tamin’ny Isiraely tany an-dafy andrefan’i Jordana ny amin’ny raharaha rehetra ho an’i Jehovah sy ny fanompoana ny mpanjaka.",
			"31": "<sup>31</sup> Ny Hebronita dia Jeria lohany (tamin’ny taona fahefa-polo nanjakan’i Davida no nitadiavana ny Hebronita araka ny fianakaviany avy, ka nisy lehilahy mahay hita tao aminy tao Jazera any Gileada)",
			"32": "<sup>32</sup> sy ny rahalahiny fiton-jato amby roa arivo, samy lehilahy mahay, lohan’ny fianakaviany avy. Ary Davida nanendry ireo ho mpifehy ny Robenita sy ny Gadita ary ny antsasaky ny firenen’i Manase, ny amin’ny raharahan’Andriamanitra sy ny mpanjaka."
		},
		"27": {
			"1": "<sup>1</sup> ARY ny amin’ny Zanak’Isiraely araka ny isany, dia ny lohan’ny fianakaviany sy ny mpifehy arivo sy ny mpifehy zato ary ny mpifehy azy, izay nanompo ny mpanjaka tamin’izay raharahany rehetra isan-tokony, izay nifandimby isam-bolana mandritra ny herintaona, dia efatra arivo amby roa alina avy no isan’ny isan-tokony.",
			"2": "<sup>2</sup> Ny nifehy ny antokony voalohany, ho amin’ny volana voalohany, dia Jasobeama, zanak’i Zabdiela, ary ny antokony dia efatra arivo amby roa alina;",
			"3": "<sup>3</sup> isan’ny taranak’i Fareza izy sady lohan’ny mpifehy rehetra tamin’ny miaramila amin’ny volana voalohany.",
			"4": "<sup>4</sup> Ary ny nifehy ny antokony ho amin’ny volana faharoa dia Dodo Ahohita; Miklota andriandahy no isan’ny antokony; ary ny antokon’ny Ahohita dia efatra arivo amby roa alina.",
			"5": "<sup>5</sup> Ny komandin’ny antokony fahatelo tamin’ny miaramila, ho amin’ny volana fahatelo, dia Benaia lohany, zanak’i Joiada mpisorona; ary ny antokony dia efatra arivo amby roa alina.",
			"6": "<sup>6</sup> Io no ilay Benaia, izay nahery sady ambony tamin’izy telo-polo lahy; Amizabada zanany no isan’ny antokony.",
			"7": "<sup>7</sup> Ny fahefatra, ho amin’ny volana fahefatra, dia Asahela, rahalahin’i Joaba, ary Zebadia zanany no nanarakaraka azy; ary ny antokony dia efatra arivo amby roa alina.",
			"8": "<sup>8</sup> Ny fahadimy, ho amin’ny volana fahadimy, dia Samihota Jizarita; ary ny antokony dia efatra arivo amby roa alina.",
			"9": "<sup>9</sup> Ny fahenina, ho amin’ny volana fahenina, dia Ira, zanak’Ikesy Tekoita; ary ny antokony dia efatra arivo amby roa alina.",
			"10": "<sup>10</sup> Ny fahafito, ho amin’ny volana fahafito, dia Heleza Pelonita, isan’ny taranak’i Efraima; ary ny antokony dia efatra arivo amby roa alina.",
			"11": "<sup>11</sup> Ny fahavalo, ho amin’ny volana fahavalo, dia Sibekay Hosatita, isan’ny Zerita; ary ny antokony dia efatra arivo amby roa alina.",
			"12": "<sup>12</sup> Ny fahasivy, ho amin’ny volana fahasivy, dia Abiezera Anatotita, isan’ny Benjamita; ary ny antokony dia efatra arivo amby roa alina.",
			"13": "<sup>13</sup> Ny fahafolo, ho amin’ny volana fahafolo, dia Maharay Netofatita, isan’ny Zerita; ary ny antokony dia efatra arivo amby roa alina.",
			"14": "<sup>14</sup> Ny fahiraika ambin’ny folo, ho amin’ny volana fahiraika ambin’ny folo, dia Benaia Piratonita, isan’ny taranak’i Efraima; ary ny antokony dia efatra arivo amby roa alina.",
			"15": "<sup>15</sup> Ny faharoa ambin’ny folo, ho amin’ny volana faharoa ambin’ny folo, dia Helday Netofatita, isan’ny taranak’i Otniela; ary ny antokony dia efatra arivo amby roa alina.",
			"16": "<sup>16</sup> ARY izao no lohan’ny firenen’Isiraely: Tamin’ny Robenita dia Eliezera, zanakalahin’i Zikry; tamin’ny Simeonita dia Sefatia, zanakalahin’i Maka;",
			"17": "<sup>17</sup> tamin’ny Levita dia Hasabia, zanakalahin’i Kemoela; tamin’ny Aronita dia Zadoka;",
			"18": "<sup>18</sup> tamin’ny Joda dia Eliho, izay isan’ny rahalahin’i Davida; tamin’ny Isakara dia Omry, zanakalahin’i Mikaela;",
			"19": "<sup>19</sup> tamin’ny Zebolona dia Jismaia, zanakalahin’i Obadia; tamin’ny Naftaly dia Jerimota, zanakalahin’i Azriela;",
			"20": "<sup>20</sup> tamin’ny taranak’i Efraima dia Hosea, zanakalahin’i Azazia; tamin’ny antsasaky ny firenen’i Manase dia Joela, zanakalahin’i Pedaia;",
			"21": "<sup>21</sup> tamin’ny antsasaky ny firenen’i Manase tao Gileada dia Ido, zanakalahin’i Zakaria; tamin’ny Benjamina dia Jasiela, zanakalahin’i Abnera;",
			"22": "<sup>22</sup> tamin’ny Dana dia Azarela, zanakalahin’i Jerohama. Ireo no lohan ny firenen’Isiraely.",
			"23": "<sup>23</sup> Nefa tsy nanisa ny hatramin’ny roa-polo taona no ho midina Davida; fa Jehovah efa nilaza fa hampitombo ny Isiraely ho tahaka ny kintana eny amin’ny lanitra Izy.",
			"24": "<sup>24</sup> Joaba, zanak’i Zeroia, nanomboka nanisa, fa tsy nahefa, satria nisy fahatezerana nihatra tamin’ny Isiraely tamin’izany, ka dia tsy voasoratra ao amin’ny bokin’ny tantaran’i Davida mpanjaka ny isany.",
			"25": "<sup>25</sup> ARY ny tonian’ny rakitry ny mpanjaka dia Azmaveta, zanak’i Adiela; ary ny tonian ny trano firaketana tany ambany vohitra, na tamin’ny tanàna, na tamin’ny vohitra, na tamin’ny tilikambo, dia Jonatana, zanak’i Ozia;",
			"26": "<sup>26</sup> ary ny mpifehy ny mpiasa tany an-tsaha hiasa ny tany dia Ezry, zanak’i Keloba;",
			"27": "<sup>27</sup> ary ny tonian’ny tanimboaloboka dia Simey Ramatita; ary ny tonian’ny divay tehirizina any amin’ny tanimboaloboka dia Zebdy Sifmita;",
			"28": "<sup>28</sup> ary ny tonian’ny hazo oliva sy ny aviavy eny amin’ny tany lemaka amoron-tsiraka dia Bala-hanana Gaderita; ary ny tonian’ny diloilo tehirizina dia Joasy;",
			"29": "<sup>29</sup> ary ny tonian’ny omby nandrasana tany Sarôna dia Sitray Sarônita; ary ny tonian’ny omby tany an-dohasaha dia Safata, zanak’i Adlay;",
			"30": "<sup>30</sup> ary ny tonian’ny rameva dia Obila Ismaelita; ary ny tonian’ny borikivavy dia Jedeia Meronotita;",
			"31": "<sup>31</sup> ary ny tonian’ny ondry aman-osy dia Jaziza Hagarita. Ireo rehetra ireo no tonian’ny fananan’i Davida mpanjaka.",
			"32": "<sup>32</sup> Ary Jonatana, rahalahin-drain’i Davida, dia mpanolo-tsaina,–lehilahy hendry sady be fahalalana izy; ary Jehiela, zanak’i Hakmony, dia teo amin’ny zanaky ny mpanjaka;",
			"33": "<sup>33</sup> ary Ahitofela dia mpanolo-tsaina ny mpanjaka; ary Hosay Arkita no sakaizan’ny mpanjaka; ary ny manarakaraka an’i Ahitofela dia Joiada, zanak’i Benaia, sy Abiatara; ary ny komandin’ny miaramilan’ny mpanjaka dia Joaba."
		},
		"28": {
			"1": "<sup>1</sup> ARY novorin’i Davida tany Jerosalema ny lehibe rehetra tamin’ny Isiraely, dia ny lohan’ny firenena sy ny komandin’ny antokony izay nanompo ny andriamanjaka, ary ny mpifehy arivo sy ny mpifehy zato sy ny mpitandrina ny harena mbamin’ny fananana rehetra an’ny mpanjaka sy ny zanany mbamin’ny tandapa sy ny lehilahy mahery, dia ny lehibe rehetra.",
			"2": "<sup>2</sup> Ary Davida mpanjaka nitsangana ka nanao hoe: Mihainoa ahy, ry rahalahiko sy ry vahoakako! Raha izaho, dia efa mby tato am-poko ny hanao trano fitsaharana ho an’ny fiaran’ny faneken’i Jehovah sy ho an’ny fitoeran-tongotr’Andriamanitsika, ary efa novononiko izay ho enti-manao azy;",
			"3": "<sup>3</sup> nefa hoy Andriamanitra tamiko: Tsy hanao trano ho an’ny anarako hianao, satria lehilahy mpiady sy mpandatsa-dra.",
			"4": "<sup>4</sup> Nefa kosa Jehovah, Andriamanitry ny Isiraely, efa nifidy ahy tamin’ny ankohonan’ny raiko rehetra ho mpanjakan’ny Isiraely mandrakizay; fa Joda no nofidiny ho mpanapaka, ary tamin’ny taranak’i Joda indray dia ny ankohonan’ny raiko no nofidiny; ary tamin’ny ankohonan’ny raiko dia izaho kosa no nankasitrahany ka nataony mpanjaka amin’ny Isiraely rehetra.",
			"5": "<sup>5</sup> Ary tamin’ny zanako rehetra (fa zanaka maro no nomen’i Jehovah ahy) dia Solomona zanako no nofidiny hipetraka amin’ny seza fiandrianan’ny fanjakan’i Jehovah hanjaka amin’ny Isiraely.",
			"6": "<sup>6</sup> Ary hoy Izy tamiko: Solomona zanakao no hanao ny tranoko sy ny kianjako, fa efa nifidy azy ho zanako Aho, ary Izaho ho rainy.",
			"7": "<sup>7</sup> Ary hampitoetra ny fanjakany ho mandrakizay Aho, raha maharitra mitandrina ny didiko sy ny fitsipiko mandra-kariva izy tahaka ny amin’izao anio izao.",
			"8": "<sup>8</sup> Ary ankehitriny, eto anatrehan’ny Isiraely rehetra, fiangonan’i Jehovah, sy eto anatrehan’Andriamanitsika, tandremo sy diniho ny didin’i Jehovah Andriamanitrareo rehetra, mba hahatananareo ity tany soa ity, sady havelanareo ho lovan’ny zanakareo mandimby anareo mandrakizay izy.",
			"9": "<sup>9</sup> Ary hianao, ry Solomona zanako, aoka ho fantatrao Andriamanitry ny rainao, ka manompoa Azy amin’ny fo rehetra sy ny fanahy mazoto; fa Jehovah, dia mpandinika ny fo rehetra, ary ny fieritreretana rehetra dia fantany avokoa. Raha mitady Azy hianao, dia hahita Azy; fa raha mahafoy Azy kosa hianao, dia hariany mandrakizay.",
			"10": "<sup>10</sup> Koa mitandrema; fa Jehovah efa nifidy anao hanao trano ho fitoerana masina; dia mahereza ka mahefà.",
			"11": "<sup>11</sup> Ary Davida nanome an’i Solomona zanany ny sarin’ny lavarangana fidirana sy ny tranony sy ny trano firaketany sy ny efi-trano ambony sy ny efi-trano anatiny ary ny trano misy ny rakotra fanaovam-panavotana,",
			"12": "<sup>12</sup> ary ny sarin’izay rehetra efa niseho tamin’ny fanahiny; dia ny amin’ny kianjan’ny tranon’i Jehovah sy ny efi-trano rehetra manodidina azy ho an’ny rakitry ny tranon’i Jehovah sy ny zavatra nohamasinina ho rakitra,",
			"13": "<sup>13</sup> sy ho an’ny antokon’ny mpisorona sy ny Levita koa, ary ho an’ny fanompoana rehetra ao an-tranon’i Jehovah sy ho an’ny fanaka rehetra ho enti-manompo ao an-tranon’i Jehovah,",
			"14": "<sup>14</sup> ary ny lanjan’ny volamena hanaovana ny fanaka volamena samy hafa rehetra, ary ny lanjan’ny volafotsy koa hanaovana ny fanaka volafotsy samy hafa rehetra,",
			"15": "<sup>15</sup> ary ny lanjan’ny volamena hanaovana ny fanaovan-jiro volamena sy ny lela fanaovan-jirony araka ny lanjany avy, ary ny lanjan’ny volafotsy hanaovana ny fanaovan-jiro volafotsy sy ny lela fanaovan-jirony araka ny fombany avy,",
			"16": "<sup>16</sup> ary ny lanjan’ny volamena hanaovana ireo latabatra hametrahana ny mofo aseho, ary ny amin’ny volafotsy ho amin’ny latabatra volafotsy,",
			"17": "<sup>17</sup> ary ny amin’ny fitrebika sy ny lovia famafazana ary ny fitoeran-divay, izay samy volamena tsara, ary ny amin’ny kapoaka misarona, na ny volamena, na ny volafotsy, araka ny lanjany avy,",
			"18": "<sup>18</sup> ary ny lanjan’ny volamena voadio hanaovana ny alitara fandoroana ditin-kazo manitra; ary nomeny koa ny sarin’ny kalesy, dia ny kerobima volamena, izay manana elatra mivelatra ka manaloka ny fiaran’ny faneken’i Jehovah.",
			"19": "<sup>19</sup> Izany asa rehetra araka ny sary izany, hoy Davida, no nampahafantarin’i Jehovah ahy tamin’ny soratra avy tamin’ny tànany, izay atỳ amiko.",
			"20": "<sup>20</sup> Ary hoy koa Davida tamin’i Solomona zanany: Mahereza sy matanjaha ka mahefà; aza matahotra na mivadi-po; fa Jehovah Andriamanitra, dia Andriamanitro, no momba anao; tsy handao na hahafoy anao Izy ambara-pahavitanao ny asa rehetra hanaovana ny tranon’i Jehovah.",
			"21": "<sup>21</sup> Ary indreo ny antokon’ny mpisorona sy ny Levita hanao ny fanompoana rehetra ao an-tranon’Andriamanitra; ary ao aminao hanampy amin’ny asa samy hafa rehetra ny olon-kendry izay manolo-tena mba hahavita ny fanompoana rehetra sy ny mpifehy ary ny vahoaka rehetra hankatò ny didinao."
		},
		"29": {
			"1": "<sup>1</sup> ARY hoy Davida mpanjaka tamin’ny fiangonana rehetra: Solomona zanako, ilay nofidin’Andriamanitra, dia mbola zaza sy tanora fanahy; nefa lehibe ny asa, fa tsy lapa ho an’olona, fa ho an’i Jehovah Andriamanitra.",
			"2": "<sup>2</sup> Ary tamin’ny heriko rehetra no namoriako izay hanaovana ny tranon’Andriamanitra, dia ny volamena hanaovana ny fanaka volamena, ary ny volafotsy hanaovana ny fanaka volafotsy, ary ny varahina hanaovana ny fanaka varahina, ary ny vy hanaovana ny fanaka vy, ary ny hazo hanaovana ny fanaka hazo, ary ny vato beryla sy ny vato halatsaka an-tranontranony ary ny vato alatsaka asiana antimony sy ny vato misoratsoratra ary ny vato soa samy hafa rehetra ary ny vatosanga betsaka.",
			"3": "<sup>3</sup> Ary koa, noho ny fitiavako ny tranon’Andriamanitro dia omeko ho an’ny tranon’Andriamanitra, ho fanampin’ny zavatra rehetra novoriko hanaovana ny tempoly masina, izay volamena sy volafotsy ananako,",
			"4": "<sup>4</sup> dia talenta volamena telo arivo, volamena avy any Ofira, ary talenta volafotsy voadio fito arivo hapetaka amin’ny rindrin’ny isan-efi-trano,",
			"5": "<sup>5</sup> ny volamena hanaovana ny fanaka volamena ary ny volafotsy hanaovana ny fanaka volafotsy sy hanaovana ny fanaka volafotsy sy hanaovana ny asa samy hafa rehetra izay hataon’ny tanan’ny mpiasa mahay tao-zavatra. Iza ary no mazoto hitondra fanatitra ho an’i Jehovah anio?",
			"6": "<sup>6</sup> Dia samy nazoto nanatitra zavatra ny lohan’ny fianakaviana sy ny lehiben’ny firenen’Isiraely ary ny mpifehy arivo sy ny mpifehy zato mbamin’ny mpitandrina ny raharahan’ny mpanjaka",
			"7": "<sup>7</sup> ka nanome izay hanaovana ny fanompoana amin’ny tranon’Andriamanitra, dia talenta volamena dimy arivo sy darika iray alina sy talenta volafotsy iray alina sy talenta varahina valo arivo amby iray alina ary talenta vy iray hetsy.",
			"8": "<sup>8</sup> Ary izay nanana vato soa dia nanome azy ho amin’ny rakitry ny tranon’i Jehovah, hotehirizin’i Jehiela Gersonita.",
			"9": "<sup>9</sup> Dia faly ny vahoaka noho ny fahazotoan’ireo, fa tamin’ny fony rehetra no nahazotoany nanatitra ho an’i Jehovah; ary Davida mpanjaka koa faly dia faly.",
			"10": "<sup>10</sup> KOA dia nisaotra an’i Jehovah teo anatrehan’ny fiangonana rehetra Davida ka nanao hoe: Isaorana anie Hianao, Jehovah ô, Andriamanitr’Isiraely razanay, hatramin’ny taloha indrindra ka ho mandrakizay.",
			"11": "<sup>11</sup> Anao, Jehovah ô, ny fahalehibiazana sy ny hery sy ny voninahitra sy ny famirapiratana ary ny fiandrianana, dia izay rehetra any an-danitra sy etỳ an-tany; Anao ny fanjakana, Jehovah ô, ary Anao ny fisandratana ho Lohany ambonin’izy rehetra.",
			"12": "<sup>12</sup> Avy aminao ny harena sy ny voninahitra, ary Hianao no manjaka amin’izao rehetra izao; eo an-tànanao ny hery sy ny tanjaka, ka eo an-tànanao koa ny mampahalehibe sy ny mampahatanjaka izao rehetra izao.",
			"13": "<sup>13</sup> Koa ankehitriny misaotra Anao izahay, ry Andriamanitray ô, sy midera ny anaranao be voninahitra.",
			"14": "<sup>14</sup> Fa zinona moa aho, ary zinona moa ny oloko, no ahazoanay hery hanatitra toy izao? Fa avy aminao ny zavatra rehetra, ary avy tamin’ny Anao ihany no nanomezanay Anao.",
			"15": "<sup>15</sup> Fa vahiny sy mpivahiny eo anatrehanao izahay tahaka ny razanay rehetra; tahaka ny aloka ny andronay etỳ an-tany, ka tsy misy fanantenana.",
			"16": "<sup>16</sup> Ry Jehovah Andriamanitray ô, ireto zavatra rehetra novorinay hanaovana ny trano ho an’ny Anaranao masina ireto dia avy tamin’ny tànanao, ary Anao rahateo izy rehetra.",
			"17": "<sup>17</sup> Ary fantatro, ry Andriamanitro ô, fa Hianao no mamantatra ny fo ary mankasitraka izay mahitsy. Ary ny amiko, dia amin’ny hitsim-poko no nahazotoako nanatitra ireto zavatra rehetra ireto; ary ankehitriny efa hitako tamin’ny fifaliana ny olonao, izay vory eto, mazoto mitondra fanatitra ho Anao.",
			"18": "<sup>18</sup> Jehovah ô, Andriamanitry ny razanay, dia Abrahama sy Isaka ary Isiraely, mba ampahareto ao am-pon’ny olonao mandrakizay izany fikasan’ny sainy izany, ka ampiomany ho Anao ny fony;",
			"19": "<sup>19</sup> ary omeo fo marina Solomona zanako hitandrina ny didinao sy ny teni-vavolombelonao sy ny lalànao ka hanao izany rehetra izany sady hahavita ny lapa izay efa namoriako ny hanaovana azy.",
			"20": "<sup>20</sup> Ary hoy Davida tamin’ny fiangonana rehetra: Masìna hianareo, misaora an’i Jehovah Andriamanitrareo. Ary ny fiangonana rehetra dia nisaotra an’i Jehovah, Andriamanitry ny razany, sady niondrika ka niankohoka teo anatrehan’i Jehovah sy ny mpanjaka.",
			"21": "<sup>21</sup> Ary namono zavatra hatao fanatitra ho an’i Jehovah izy sady nanatitra fanatitra dorana ho an’i Jehovah nony ampitson’iny, dia vantotr’ombilahy arivo sy ondrilahy arivo ary zanak’ondry arivo mbamin’ny fanatitra aidina momba ireo ary fanatitra hafa be dia be ho an’ny Isiraely rehetra.",
			"22": "<sup>22</sup> Ary nihinana sy nisotro tamin’ny fifaliana be teo anatrehan’i Jehovah ny olona tamin’izany andro izany. Ary nampanjakainy fanindroany Solomona, zanak’i Davida, sady nohosorany ho an’i Jehovah ho mpanapaka, ary Zadoka ho mpisorona.",
			"23": "<sup>23</sup> Ary Solomona dia nipetraka tamin’ny seza fiandrianan’i Jehovah ho mpanjaka handimby an’i Davida rainy, sady nambinina izy, ka nanaiky azy ny Isiraely rehetra.",
			"24": "<sup>24</sup> Ary ny mpifehy rehetra sy ny lehilahy mahery ary ny zanak’i Davida rehetra koa dia samy nanaiky an’i Solomona mpanjaka.",
			"25": "<sup>25</sup> Ary Jehovah nanandratra an’i Solomona indrindra teo imason’ny Isiraely rehetra sady nanome azy voninahitry ny fiandrianana mihoatra noho ny mpanjaka rehetra talohany teo amin’ny Isiraely.",
			"26": "<sup>26</sup> Toy izany no nanjakan’i Davida, zanak’i Jese, tamin’ny Isiraely rehetra.",
			"27": "<sup>27</sup> Ary ny andro nanjakany tamin’ny Isiraely dia efa-polo taona; tao Hebrona no nanjakany fito taona, ary tany Jerosalema no nanjakany telo amby telo-polo taona.",
			"28": "<sup>28</sup> Ary maty izy rehefa tratrantitra tsara sady lava andro ary be harena sy voninahitra; ary Solomona zanany no nanjaka nandimby azy.",
			"29": "<sup>29</sup> Ary ny tnataran’i Davida mpanjaka, na ny voalohany na ny farany, indro fa efa voasoratra ao amin’ny bokin’i Samoela mpahita sy ao amin’ny bokin’i Natana mpaminany ary ao amin’ny bokin’i Gada mpahita izany,",
			"30": "<sup>30</sup> mbamin’ny nanjakany rehetra sy ny heriny, ary izay niseho tamin’ny androny ary tamin’ny Isiraely sy tamin’ny fanjakana rehetra tamin’ny tany."
		}
	}
};
module.exports = book;