var book = {
	"name": "Salamo",
	"numChapters": 150,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> SAMBATRA ny olona Izay tsy mandeha eo amin’ny fisainan’ny ratsy fanahy, Ary tsy mijanona eo amin’ny làlana falehan’ny mpanota, Ary tsy mipetraka eo amin’ny fipetrahan’ny mpaniratsira;",
			"2": "<sup>2</sup> Fa ny lalàn’i Jehovah no sitrany; Eny, ny lalàny no saintsaininy andro aman-alina.",
			"3": "<sup>3</sup> Dia tahaka ny hazo ambolena eo amoron’ny rano velona izy, Izay mamoa amin’ny fotoany, Ny raviny koa tsy mba malazo; Ary ny asany rehetra dia ataony lavorary avokoa.",
			"4": "<sup>4</sup> Fa tsy mba toy izany ny ratsy fanahy, Fa toy ny akofa aelin’ny rivotra izy.",
			"5": "<sup>5</sup> Ary noho izany ny ratsy fanahy dia tsy hahajanona eo amin’ny fitsarana, Na ny mpanota eo amin’ny fiangonan’ny marina.",
			"6": "<sup>6</sup> Fa fantatr’i Jehovah ny làlan’ny marina, Fa ny làlan’ny ratsy fanahy kosa dia mankany amin’ny fahaverezana."
		},
		"2": {
			"1": "<sup>1</sup> NAHOANA no mitabataba ny jentilisa, Ary misaina anoano foana ny firenena?",
			"2": "<sup>2</sup> Ireo mpanjakan’ny tany mitsangana, Ary ny mpanapaka miara-mioko Hanohitra an’i Jehovah sy ny Mesiany ka manao hoe:",
			"3": "<sup>3</sup> ,,Aoka hotosantsika ny famatorany, Ka hariantsika hiala amintsika ny famehezany.’’",
			"4": "<sup>4</sup> Izay mipetraka any an-danitra mihomehy, Ny Tompo maneso ireo.",
			"5": "<sup>5</sup> Dia hiteny azy amin’ny fahavinirany Izy Ka hampangorohoro azy amin’ny fahatezerany hoe:",
			"6": "<sup>6</sup> ,,Izaho kosa efa nanendry ny Mpanjakako Tambonin’i Ziona, tendrombohitro masina.’’",
			"7": "<sup>7</sup> ,,Hilaza zava-boatendry Aho; Jehovah nilaza tamiko hoe: Zanako Hianao, Izaho niteraka Anao andro-any.",
			"8": "<sup>8</sup> Mangataha amiko, dia homeko ho lova nao ny jentilisa Ary ny faran’ny tany ho fanananao.",
			"9": "<sup>9</sup> Hotorotoroinao amin’ny tehimby izy: Toy ny famaky vilany tany no hanamontsamontsananao azy.’’",
			"10": "<sup>10</sup> Ary amin’izany dia hendre hianareo, ry mpanjaka; Minoa anatra hianareo, ry mpitsaran’ny tany.",
			"11": "<sup>11</sup> Manompoa an’i Jehovah amin’ny fahatahorana, Ary mifalia amin’ny fangovitana.",
			"12": "<sup>12</sup> Manoroha ny Zanaka, fandrao ho tezitra Izy, ka ho very eny an-dàlana hianareo; Fa vetivety dia hirehitra ny fahatezerany. Sambatra izay rehetra mialoka aminy."
		},
		"3": {
			"1": "<sup>1</sup> Salamo nataon’i Davida, fony izy nandositra an’i Absaloma zanany. JEHOVAH ô, akory izato hamaroan’ny mampahory ahy! Maro no mitsangana hanohitra ahy.",
			"2": "<sup>2</sup> Maro no milaza ny fanahiko hoe: Tsy manam-pamonjena amin’Andriamanitra izy. [Sela.]",
			"3": "<sup>3</sup> Kanjo Hianao, Jehovah ô, no ampinga manodidina ahy, Voninahitro sy mpanandratra ny lohako.",
			"4": "<sup>4</sup> Amin’ny feoko no iantsoako an’i Jehovah, Ary any an-tendrombohiny masina no mamaly ahy Izy. [Sela.]",
			"5": "<sup>5</sup> Izaho nandry ka natory; Nifoha aho, satria Jehovah no manohana ahy.",
			"6": "<sup>6</sup> Tsy hatahotra olona alinalina aho, Izay milahatra manodidina hamely ahy.",
			"7": "<sup>7</sup> Mitsangàna, Jehovah ô; vonjeo aho, Andriamanitro ô! Fa efa namely ny takolaky ny fahavaloko rehetra Hianao; Ny nifin’ny ratsy fanahy novakivakinao.",
			"8": "<sup>8</sup> n’i Jehovah ny famonjena; Ho amin’ny olonao anie ny fitahianao. [Sela.]"
		},
		"4": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Hampiarahina amin’ny valiha. Salamo nataon’i Davida. RAHA miantso aho, mamalia ahy, ry Andriamanitry ny fahamarinako ô; Raha tao an-katerena aho, dia nohalalahinao toerana; Mamindrà fo amiko, ka henoy ny fivavako.",
			"2": "<sup>2</sup> Ry lehibe, mandra-pahoviana no hataonareo ho henatra ny voninahitro, Ary ho tia zava-poana sy hamoron-dainga hianareo? [Sela.]",
			"3": "<sup>3</sup> Kanefa aoka ho fantatrareo fa efa navahan’i Jehovah ho Azy ny tsara fanahy; Jehovah hihaino, raha miantso Azy aho.",
			"4": "<sup>4</sup> Tezera, fa aza manota; Mieritrereta eo am-pandrianareo, ka mangìna. [Sela.]",
			"5": "<sup>5</sup> Manatera fanati-pahamarinana, Ary matokia an’i Jehovah.",
			"6": "<sup>6</sup> Maro no niteny hoe: Iza no haneho soa antsika? Asandrato aminay ny fahazavan’ny tavanao, Jehovah ô.",
			"7": "<sup>7</sup> Efa nasianao fifaliana ny foko Mihoatra noho ny tamin’ny taona nahabetsahan’ny variny sy ny ranom-boalobony.",
			"8": "<sup>8</sup> andry amin’ny fiadanana aho, ka dia hatory miaraka amin’izay, Satria Hianao, Jehovah ô, no mampitoetra ahy mitokana irery tsy manana ahiahy."
		},
		"5": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Hampiarahina amin’ny sodina. Salamo nataon’i Davida. JEHOVAH ô, henoy ny teniko; Fantaro ny eritreritro.",
			"2": "<sup>2</sup> Henoy ny feon’ny fitarainako, ry Mpanjakako sy Andriamanitro ô; Fa Hianao no ivavahako.",
			"3": "<sup>3</sup> Jehovah ô, maraina no handrenesanao ny feoko; Maraina no hivavahako aminao, ka hanandrandra aho.",
			"4": "<sup>4</sup> Fa tsy Andriamanitra mankasitraka izay ratsy Hianao; Tsy hitoetra aminao ny ratsy.",
			"5": "<sup>5</sup> Tsy hahazo mijanona eo imasonao ny mpirehareha; Halanao ny mpanao ratsy rehetra.",
			"6": "<sup>6</sup> Haringanao ny mpiteny lainga; Ny olona mpandatsa-dra sy mpamitaka dia ataon’i Jehovah ho zava-betaveta.",
			"7": "<sup>7</sup> Fa izaho kosa noho ny haben’ny famindramponao dia hiditra ao an-tranonao; Hiankohoka manatrika ny lapanao masina amin’ny fahatahorana Anao aho.",
			"8": "<sup>8</sup> Jehovah ô, tariho amin’ny fahamarinanao aho noho ny amin’ny mpampahory ahy; Ataovy marina eo anoloako ny làlanao.",
			"9": "<sup>9</sup> Fa tsy misy azo itokiana ny aloaky ny vavany; Ny ao anatiny dia tena faharatsiana; Fasana misokatra ny tendany; Mandambolambo ny lelany izy.",
			"10": "<sup>10</sup> Helohy izy, Andriamanitra ô; Aoka ho lavon’ny saina ataony izy; Roahy izy noho ny haben’ny fahotany, Satria efa niodina taminao.",
			"11": "<sup>11</sup> Dia hifaly izay rehetra mialoka aminao; Hihoby mandrakariva izy, koa miarova azy Hianao; Ka dia ho ravoravo aminao izay tia ny anaranao,",
			"12": "<sup>12</sup> a Hianao, Jehovah ô, mitahy ny marina; Fankasitrahana no ataonao manodidina azy toy ny ampinga lehibe."
		},
		"6": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Nampiarahina amin’ny valiha. Al-hasheminith. Salamo nataon’i Davida. JEHOVAH ô, aza ny fahatezeranao no ananaranao ahy, Ary aza ny fahaviniranao no amaizanao ahy.",
			"2": "<sup>2</sup> Mamindrà fo amiko, Jehovah ô, fa reraka aho; Sitrano aho, Jehovah ô, fa mihozohozo ny taolako.",
			"3": "<sup>3</sup> Ny fanahiko koa dia mangovitra loatra; Fa Hianao, Jehovah ô, mandra-pahoviana?",
			"4": "<sup>4</sup> Miverena, Jehovah ô, afaho ny fanahiko; Vonjeo aho noho ny famindramponao.",
			"5": "<sup>5</sup> Fa tsy misy fahatsiarovana Anao any amin’ny fahafatesana, Any amin’ny fiainan-tsi-hita iza no hidera Anao?",
			"6": "<sup>6</sup> Efa sasatry ny misento aho; Mampitsinkafona ny farafarako isan-alina aho Sady mahampo-dranomaso ny fandriako.",
			"7": "<sup>7</sup> Lanin’alahelo ny masoko; Efa pahina izy amin’ny ataon’ny mpampahory ahy rehetra.",
			"8": "<sup>8</sup> Mialà amiko, hianareo mpanao ratsy rehetra; Fa Jehovah efa nandre ny feon’ny fitomaniako.",
			"9": "<sup>9</sup> Jehovah efa nandre ny fangatahako; Jehovah no handray ny fivavahako.",
			"10": "<sup>10</sup> Ho menatra sy hangovitra indrindra ny fahavaloko rehetra; Hihodina izy ka ho menatra tampoka."
		},
		"7": {
			"1": "<sup>1</sup> Sigaiona nataon’i Davida, izay nohirainy ho an’i Jehovah noho ny tenin’i Kosy Benjamita. JEHOVAH Andriamanitro ô, Hianao no ialofako; Vonjeo aho amin’izay rehetra manenjika ahy, ka afaho;",
			"2": "<sup>2</sup> Fandrao ny fanahiko viravirainy tahaka ny fanaon’ny liona, Ary kepokepohiny, ka tsy misy mamonjy.",
			"3": "<sup>3</sup> Jehovah Andriamanitro ô, raha nanao izany aho; Raha misy heloka eto an-tànako;",
			"4": "<sup>4</sup> Raha namaly ratsy izay tsara fihavanana tamiko aho– Novonjeko aza izay nampahory ahy tsy ahoan-tsy ahoana–",
			"5": "<sup>5</sup> Dia aoka ny fahavalo hanenjika ny fanahiko ka hahatratra azy; Eny, aoka izy hanitsaka ny aiko amin’ny tany Ary hampandry ny voninahitro amin’ny vovoka. [Sela.]",
			"6": "<sup>6</sup> Jehovah ô, mitsangàna amin’ny fahatezeranao; Misandrata hisakana ny fisafoaky ny mpampahory ahy; Ary mifohaza hanampy ahy, fa didy marina no navoakanao;",
			"7": "<sup>7</sup> Aoka ny firenena maro hivory manodidina Anao; Ary miverena ho any amin’ny avo eo amboniny Hianao,",
			"8": "<sup>8</sup> Jehovah hitsara ny firenena; Jehovah ô, tsarao aho araka ny fahamarinako sy ny tsi-fananako tsiny.",
			"9": "<sup>9</sup> Aoka ho tapaka ny ratsy ataon’ny ratsy fanahy, fa ampitoero kosa ny marina; Fa Andriamanitra marina no mamantatra ny fo sy ny voa.",
			"10": "<sup>10</sup> Ny ampingako dia ao amin’Andriamanitra, Mpamonjy ny mahitsy fo.",
			"11": "<sup>11</sup> Mpitsara marina Andriamanitra, Eny, Andriamanitra Izay tezitra isan-andro.",
			"12": "<sup>12</sup> Raha tsy ibebahana Izy, dia hanasa ny sabany; Efa manenjana ny tsipìkany Izy ka manamboatra azy,",
			"13": "<sup>13</sup> Ary ireo dia kendreny amin’ny fiadiana mahafaty; Ny zana-tsipìkany ataony mirehitra.",
			"14": "<sup>14</sup> Indro, efa marary hiteraka zava-poana ireo; Eny, torontoronina fampahoriana izy ary mitera-doza;",
			"15": "<sup>15</sup> Mihady lavaka izy ka manalalaka izany, Dia latsaka ao anatin’ny longoa nataony ihany izy;",
			"16": "<sup>16</sup> Mitsingerina ho eo an-dohany ihany ny fampahoriany; Ary eo an-tampon-dohany ihany no ianjeran’ny loza nataony.",
			"17": "<sup>17</sup> Hidera an’i Jehovah araka ny fahamarinany aho Ary hankalaza ny anaran’i Jehovah Avo Indrindra."
		},
		"8": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Al-hagitith. Salamo nataon’i Davida. JEHOVAH, Tomponay ô, Endrey ny voninahitry ny anaranao amin’ny tany rehetra! Nataonao eny amin’ny lanitra ny voninahitrao.",
			"2": "<sup>2</sup> Avy amin’ny vavan’ny ankizy madinika sy ny minono no nanorenanao hery Noho ny mpandrafy Anao, Mba hampanginanao ny fahavalo sy izay ta-hamaly ratsy.",
			"3": "<sup>3</sup> Raha jereko ny lanitrao, izay asan’ny rantsan-tànanao, Sy ny volana aman-kintana, izay noforoninao,",
			"4": "<sup>4</sup> Inona moa ny olona, no ahatsiarovanao azy? Ary ny zanak’olombelona, no amangianao azy?",
			"5": "<sup>5</sup> Fa efa nataonao ambany kely noho Andriamanitra izy. Ary efa nosatrohanao voninahitra sy fiandrianana.",
			"6": "<sup>6</sup> Efa nampanjakainao amin’ny asan’ny tànanao izy; Ny zavatra rehetra dia efa nataonao eo ambanin’ny tongony,",
			"7": "<sup>7</sup> Dia ny ondry aman-osy sy ny omby rehetra, Ary ny bibi-dia koa",
			"8": "<sup>8</sup> Mbamin’ny voro-manidina sy ny hazandrano any an-dranomasina, Izay mandeha amin’ny làlan-dranomasina.",
			"9": "<sup>9</sup> Jehovah, Tomponay ô, Endrey ny voninahitry ny anaranao amin’ny tany rehetra!"
		},
		"9": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira, Almoth-laben. Salamo nataon’i Davida. HIDERA an’i Jehovah amin’ny foko rehetra aho; Hanambara ny fahagaganao rehetra aho.",
			"2": "<sup>2</sup> Ho faly sy ho ravoravo aminao aho; Hankalaza ny anaranao aho ry Avo Indrindra ô,",
			"3": "<sup>3</sup> Satria lasa nihodina ny fahavaloko, Nirepirepy izy ka ringana tsy ho eo anatrehanao.",
			"4": "<sup>4</sup> Fa efa nitsara ahy Hianao ka nanome ahy ny rariny; Mipetraka eo amin’ny seza fitsaranao Hianao ho Mpitsara marina.",
			"5": "<sup>5</sup> Efa nanarinao mafy ny jentilisa, efa naringanao ny ratsy fanahy; Ny anarany efa nokosehinao ho faty mandrakizay doria.",
			"6": "<sup>6</sup> Ny amin’ny fahavalo, dia tanteraka ho mandrakizay ny fandravana azy; Ary izay tanàna efa noravanao, dia very na dia ny fahatsiarovana azy aza.",
			"7": "<sup>7</sup> Kanefa Jehovah mitoetra mandrakizay, Efa nametraka ny sezany hitsarany Izy,",
			"8": "<sup>8</sup> Ary Izy hitsara izao tontolo izao amin’ny fahamarinana, Eny, hitsara ny firenena amin’ny fahitsiana Izy.",
			"9": "<sup>9</sup> Ka dia Jehovah no ho fiarovana avo ho an’ny mahantra, Ho fiarovana avo amin’ny andro fahoriana Izy.",
			"10": "<sup>10</sup> Ary hatoky Anao izay mahalala ny anaranao; Fa tsy foinao izay mitady Anao, Jehovah ô.",
			"11": "<sup>11</sup> Mankalazà an’i Jehovah, Izay mitoetra any Ziona; Ambarao any amin’ny firenena ny asany.",
			"12": "<sup>12</sup> Fa Izay manadin-dra dia mahatsiaro ny ory; Tsy manadino ny fitarainany Izy.",
			"13": "<sup>13</sup> Mamindrà fo amiko, Jehovah ô; Mitsinjova ny fahoriako avy amin’izay mankahala ahy, Ry Mpanandratra ahy ho afaka amin’ny vavahadin’ny fahafatesana,",
			"14": "<sup>14</sup> Mba holazaiko avokoa ny fiderana Anao rehetra; Eo am-bavahadin’i Ziona zanakavavy no hifaliako amin’ny famonjenao.",
			"15": "<sup>15</sup> Ny jentilisa efa latsaka tamin’ny lavaka izay nohadiny; Ny fandrika harato izay nafeniny efa nahazo ny tongony,",
			"16": "<sup>16</sup> Jehovah efa nanao izay mampiseho ny tenany ho Mpitsara; Namandrika ny ratsy fanahy tamin’ny asan’ny tànany Izy. [Higaiona. Sela.]",
			"17": "<sup>17</sup> Hiverina ho any amin’ny fiainan-tsi-hita ny ratsy fanahy, Dia ny jentilisa rehetra izay manadino an’Andriamanitra.",
			"18": "<sup>18</sup> Fa tsy ho mandrakizay no hanadinoana ny malahelo; Ary tsy ho very mandrakizay ny fanantenan’ny mahantra.",
			"19": "<sup>19</sup> Mitsangàna, Jehovah ô, aoka tsy hahery ny olona; Aoka hotsaraina eo anatrehanao ny jentilisa.",
			"20": "<sup>20</sup> Asio fampahatahorana izy, Jehovah ô; Aoka ho fantatry ny jentilisa fa olona ihany izy. [Sela.]"
		},
		"10": {
			"1": "<sup>1</sup> JEHOVAH ô, nahoana no mijanona eny lavitra eny Hianao Ka miery amin’ny andro fahoriana?",
			"2": "<sup>2</sup> Noho ny fireharehan’ny ratsy fanahy dia enjehina mafy ny malahelo; Azon’ny fihendrena nataony izy ireny.",
			"3": "<sup>3</sup> Ny ratsy fanahy mandoka izay irin’ny fony; Ary ny mpierina mahafoy sy manamavo an’i Jehovah.",
			"4": "<sup>4</sup> Ny ratsy fanahy mirehareha hoe: Tsy hanadina Izy; Eny, ,,Tsy misy Andriamanitra’’ no tontalin-keviny.",
			"5": "<sup>5</sup> Tanteraka mandrakariva ny alehany; Any amin’ny avo lavitra azy ny fitsaranao; Ny mpandrafy azy rehetra dia ataony hoe: Zinona ireny?",
			"6": "<sup>6</sup> Hoy izy am-pony: Tsy hangozohozo aho, Tsy ho azon-doza mandrakizay doria aho.",
			"7": "<sup>7</sup> Feno ozona sy fitaka ary fanaovana an-keriny ny vavany; Ao ambanin’ny lelany dia misy fampahoriana sy faharatsiana.",
			"8": "<sup>8</sup> Mamitsaka amin’ny fanotrehana ao an-tanàna izy, Ao an-katakonana no amonoany ny marina; Ny masony mikendry ny mahantra.",
			"9": "<sup>9</sup> Manotrika ao an-katakonana tahaka ny liona ao amin’ny famitsahany izy; Manotrika hisambotra ny malahelo izy; Fandrihany amin’ny haratony no fisambony ny malahelo.",
			"10": "<sup>10</sup> Torotoro sy mitanondrika ny mahantra Ka lavo amin’ny herin’ny ratsy fanahy.",
			"11": "<sup>11</sup> Hoy izy anakampony: Efa nanadino Andriamanitra; Efa nanafina ny tavany Izy ka tsy hahita mandrakizay.",
			"12": "<sup>12</sup> Mitsangàna, Jehovah ô; Andriamanitra ô, atsangano ny tànanao; Aza manadino ny ory.",
			"13": "<sup>13</sup> Nahoana no mamingavinga an’Andriamanitra ny ratsy fanahy Ka manao anakampo hoe: Tsy hanadina Hianao?",
			"14": "<sup>14</sup> Efa nahita ihany Hianao, satria mijery fahoriana sy alahelo, mba hovalian’ny tànanao. Hianao no iononan’ny mahantra; Hianao no Mpamonjy ny kamboty.",
			"15": "<sup>15</sup> Torotoroy ny sandrin’ny ratsy fanahy; Ary adino ny heloky ny mpanao ratsy mandra-paha-tsy hisy hitanao intsony.",
			"16": "<sup>16</sup> Jehovah no Mpanjaka mandrakizay doria; Voafongotra tsy ho amin’ny taniny intsony ny jentilisa.",
			"17": "<sup>17</sup> Efa nohenoinao, Jehovah ô, ny irin’ny mpandefitra; Mampiomana ny fony Hianao sady mampihaino ny sofinao,",
			"18": "<sup>18</sup> Hanomezanao rariny ny kamboty sy ny mahantra, Mba tsy handroso hampitahotra intsony ny zanak’olombelona etỳ an-tany."
		},
		"11": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Nataon’i Davida. JEHOVAH no ialofako; Ahoana no anaovanareo amin’ny fanahiko hoe: Mandosira ho any an-tendrombohitrareo toy ny voron-kely?",
			"2": "<sup>2</sup> Fa, indro, ny ratsy fanahy manenjana ny tsipìkany; Efa nanamboatra ny zana-tsipìkany eo amin’ny kofehy izy, Nikendry tao amin’ny maizina hitifitra ny mahitsy fo.",
			"3": "<sup>3</sup> Fa rava ny fanorenana, Ka inona indray no azon’ny marina atao?",
			"4": "<sup>4</sup> Jehovah dia ao amin’ny tempoliny masina, Ny seza fiandrianan’i Jehovah dia any an-danitra; Ny masony mijery, eny, ny hodi-masony mizaha toetra ny zanak’olombelona.",
			"5": "<sup>5</sup> Jehovah mizaha toetra ny marina; Fa ny ratsy fanahy sy ny mifaly hampidi-doza dia samy halan’ny fanahiny.",
			"6": "<sup>6</sup> Handatsaka fandrika amin’ny ratsy fanahy Izy tahaka ny fandatsaka ranonorana; Afo sy solifara ary rivo-mahamay no ho anjara kapoakany.",
			"7": "<sup>7</sup> Fa marina Jehovah, ka fahamarinana no tiany; Ny mahitsy no hahita ny tavany."
		},
		"12": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Al-hasheminith. Salamo nataon’i Davida. VONJEO, Jehovah ô, fa efa lany ny tsara fanahy; Eny, tsy misy mahatoky intsony eo amin’ny zanak’ olombelona.",
			"2": "<sup>2</sup> Samy milaza lainga amin’ny namany amin’ny molotra mandrobo izy; Fo mamorom-pitaka no itenenany.",
			"3": "<sup>3</sup> Holevonin’i Jehovah anie ny molotra mandrobo rehetra Sy ny lela miteny avonavona,",
			"4": "<sup>4</sup> Izay manao hoe: Hampahery anay ny lelanay; Miandany aminay ny molotray; iza no tomponay?",
			"5": "<sup>5</sup> Noho ny fampahoriana ny ory sy ny fisentoan’ny malahelo Dia hitsangana Aho ankehitriny, hoy Jehovah; Hovonjeko izay vingavingain’ny sasany.",
			"6": "<sup>6</sup> Ny tenin’i Jehovah dia teny madio, Tahaka ny volafotsy voarendrika tao anatin’ny memy ka mitsoriaka amin’ny tany–eny, voadio impito.",
			"7": "<sup>7</sup> Hianao, Jehovah ô, no hiaro azy, Hianao no hiambina azy mandrakizay tsy ho azon’ity taranaka ity.",
			"8": "<sup>8</sup> Raha avy izay isandratan’ny tsinontsinona eo amin’ny zanak’olombelona Dia mieboebo ombieniombieny ny ratsy fanahy."
		},
		"13": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Salamo nataon’i Davida. JEHOVAH ô, mandra-pahoviana no mbola hanadinoanao ahy ihany? Mandra-pahoviana no mbola hanafenanao ny tavanao amiko?",
			"2": "<sup>2</sup> Mandra-pahoviana no mbola hisaintsainako am-panahy Sy hahalahelovako am-po isan-andro? Mandra-pahoviana no mbola hisandratan’ny fahavaloko ho amboniko?",
			"3": "<sup>3</sup> Jereo aho ka valio, Jehovah Andriamanitro ô; Ampahazavao ny masoko, fandrao renoky ny torimaso fahafatesana aho;",
			"4": "<sup>4</sup> Fandrao ny fahavaloko hanao hoe: Naharesy azy aho, Fandrao hifaly ny rafilahiko, raha mangozohozo aho.",
			"5": "<sup>5</sup> Fa raha izaho kosa, dia ny famindramponao no itokiako; Ho faly amin’ny famonjenao ny foko.",
			"6": "<sup>6</sup> Hihira ho an’i Jehovah aho, satria nanisy soa ahy Izy."
		},
		"14": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Nataon’i Davida. NY adala manao anakampo hoe: Tsy misy Andriamanitra. Manao izay ratsy sy vetaveta izy; Tsy misy manao ny tsara.",
			"2": "<sup>2</sup> Eny an-danitra Jehovah miondrika mijery ny zanak’olombelona, Mba hizaha na misy hendry mitady an’Andriamanitra.",
			"3": "<sup>3</sup> Samy efa nivily izy rehetra ka tonga vetaveta avokoa; Tsy misy manao ny tsara, na dia iray akory aza.",
			"4": "<sup>4</sup> Tsy mahalala va ny mpanao ratsy rehetra, Izay mihinana ny oloko toy ny fihinan-kanina, Ary Jehovah tsy antsoiny?",
			"5": "<sup>5</sup> Indreo, matahotra dia matahotra izy, Satria Andriamanitra no eo amin’ny taranaka marina.",
			"6": "<sup>6</sup> Na dia foananareo aza ny fisainan’ny ory, Jehovah kosa no arony.",
			"7": "<sup>7</sup> Enga anie ka hivoaka avy ao Ziona ny ny famonjena ny Isiraely! Raha ampodin’i Jehovah avy amin’ny fahababoana ny olony, Dia ho faly Jakoba, ary ho ravoravo Isiraely. ."
		},
		"15": {
			"1": "<sup>1</sup> Salamo nataon’i Davida. JEHOVAH ô, iza no hitoetra ao amin’ny tabernakelinao? Iza no honina ao an-tendrombohitrao masina?",
			"2": "<sup>2</sup> Izay mandeha tsy misy tsiny ary manao izay mahitsy, Sady misaintsaina ny marina ny fony,",
			"3": "<sup>3</sup> Tsy manendrikendrika amin’ny lelany, Sady tsy manisy ratsy ny sakaizany, Ary tsy manao izay hahafa-baraka ny namany,",
			"4": "<sup>4</sup> Tsy manaja ny ratsy fanahy, Fa manome voninahitra ny matahotra an’i Jehovah, Tsy mivadika amin’ny fianianany, na dia maningotra ny tenany aza,",
			"5": "<sup>5</sup> Sady tsy mampihana ny volany, Na mandray kolikoly hanameloka ny marina,– Izay mitandrina izany dia tsy mba hangozohozo mandrakizay."
		},
		"16": {
			"1": "<sup>1</sup> Miktama nataon’i Davida. AROVY aho, Andriamanitra ô, fa mialoka aminao aho.",
			"2": "<sup>2</sup> Hoy izaho ny amin’i Jehovah: Tompoko Hianao; Tsy misy soa ananako azo oharina aminao;",
			"3": "<sup>3</sup> Ary ny amin’ny olona masina ambonin’ny tany, Ireo no olona tsara laza izay sitrako indrindra.",
			"4": "<sup>4</sup> Ho be ny fahorian’izay manova an’Andriamanitra hatakalony ny hafa. Tsy mba hanatitra ny fanati-dra aidiny aho, Ary tsy mba hotononin’ny molotro ny anarany.",
			"5": "<sup>5</sup> Jehovah no anjara lovako sy kapoakako; Hianao no mampaharitra ny anjarako.",
			"6": "<sup>6</sup> Ny voafaritra ho anjarako dia eo amin’ny tany mahafinaritra; Eny, manan-dova tsara aho.",
			"7": "<sup>7</sup> Hisaotra an’i Jehovah Izay manome saina ahy aho; Ary na amin’ny alina aza dia mananatra ahy ny foko.",
			"8": "<sup>8</sup> Ataoko eo anatrehako mandra-kariva Jehovah, Fa eo an-kavanako Izy, ka dia tsy hangozohozo aho.",
			"9": "<sup>9</sup> Izany no ifalian’ny foko sy iravoravoan’ny fanahiko; Eny, ny nofoko aza hitoetra tsy manana ahiahy.",
			"10": "<sup>10</sup> Fa tsy ho foinao ho any amin’ny fiainan-tsi-hita ny aiko; Tsy hamela ny Iray Masinao ho latsaka ao amin’ ny lavaka Hianao.",
			"11": "<sup>11</sup> Hampahafantatra ahy ny làlan’aina Hianao; Fahavokisam-pifaliana no eo anatrehanao, Ary fahafinaretana no eo an-tànanao ankavanana mandrakizay."
		},
		"17": {
			"1": "<sup>1</sup> Fivavak’i Davida. JEHOVAH ô, mihainoa ny rariny, mandrenesa ny fitarainako, Atongilano ny sofinao amin’ny fivavako avy amin’ny molotra tsy misy fitaka.",
			"2": "<sup>2</sup> Hivoaka avy eo anatrehanao ny fitsarana ahy; Ny masonao hijery marina.",
			"3": "<sup>3</sup> Nizaha toetra ny foko Hianao, namantatra ahy nony alina; Nizaha toetra ahy tamin’ny memy Hianao, fa tsy nahita na inona na inona; Ny vavako tsy hafa noho ny eritreritro.",
			"4": "<sup>4</sup> Ny amin’ny asan’ny olona, dia ny tenin’ny molotrao No nitandremako tsy ho amin’ny làlan’ny mpanao an-keriny.",
			"5": "<sup>5</sup> Ny diako maharitra eo amin’ny làlanao, Tsy solafaka ny tongotro.",
			"6": "<sup>6</sup> Izaho miantso Anao, ka mamaly ahy Hianao, Andriamanitra ô; Atongilano amiko ny sofinao, ka henoy ny teniko.",
			"7": "<sup>7</sup> Ataovy mahagaga ny famindramponao, ry Mpamonjy amin’ny tànanao ankavanana izay olona mialoka aminao, Mba tsy ho azon’ny fahavalo.",
			"8": "<sup>8</sup> Arovy tahaka ny anakandriamaso aho, Afeno ao ambanin’ny aloky ny elatrao,",
			"9": "<sup>9</sup> Mba tsy ho azon’ny ratsy fanahy izay mampahory ahy, Dia ny fahavaloko masiaka, izay manodidina ahy.",
			"10": "<sup>10</sup> Efa mihirim-belona izy; Teny miavonavona no aloaky ny vavany.",
			"11": "<sup>11</sup> Manodidina anay amin’ny alehanay ankehitriny izy, Vandrininy hapotrany amin’ny tany izahay;",
			"12": "<sup>12</sup> Ny fijery azy dia tahaka ny liona noana ta-hamiravira, Sy tahaka ny liona tanora mamitsaka ao am-pierena.",
			"13": "<sup>13</sup> Mitsangàna, Jehovah ô, sakano izy, aripaho izy; Vonjeo amin’ny sabatrao ny fanahiko tsy ho azon’ny ratsy fanahy;",
			"14": "<sup>14</sup> Vonjeo amin’ny tànanao aho, Jehovah ô, mba tsy ho azon’ny olon’izao tontolo izao, Izay manana ny anjarany amin’izao fiainana izao, sady fenoinao ny harenao ny kibony; Maro anaka izy, dia mamela ny fananany be ho an’ny zanany madinika.",
			"15": "<sup>15</sup> Fa izaho kosa, dia aoka hahita ny tavanao amin’ny fahamarinana; Aoka ny endrikao no hahafa-po ahy, raha mifoha. ."
		},
		"18": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Nataon’i Davida, mpanompon’i Jehovah, izay nilaza tamin’i Jehovah ny tenin’ity fihirana ity tamin’ny andro namonjen’i Jehovah azy tamin’ny tànan’ny fahavalony rehetra, indrindra fa tamin’ny tànan’i Saoly; dia hoy izy:– MAMIKO indrindra Hianao, Jehovah Heriko ô.",
			"2": "<sup>2</sup> Jehovah no harambatoko sy batery fiarovana ho ahy ary Mpamonjy ahy; Andriamanitro, vatolampy ialofako, Sy ampingako ary tandroka famonjena ahy sy fiarovana avo ho ahy.",
			"3": "<sup>3</sup> Miantso an’i Jehovah Izay mendrika hoderaina aho, Ka dia vonjena ho afaka amin’ny fahavaloko.",
			"4": "<sup>4</sup> Nihodidina tamiko ny famatoram-pahafatesana, Ary ny riaky ny faharatsiana nampahatahotra ahy.",
			"5": "<sup>5</sup> Ny famatoran’ny fianan-tsi-hita nihodidina tamiko; Nisakana ahy ny fandriky ny fahafatesana.",
			"6": "<sup>6</sup> Fony azom-pahoriana aho, dia niantso an’i Jehovah, Eny, Andriamanitro no nitarainako; Nihaino ny feoko tao an-tempoliny Izy, Ary ny fitarainako teo anatrehany dia efa tonga teo an-tsofiny.",
			"7": "<sup>7</sup> Dia nihozongozona sy nihorohoro ny tany, Ary ny fanambanin’ny tendrombohitra nihorohoro; Eny, nihozongozona ireo, satria tezitra Jehovah.",
			"8": "<sup>8</sup> Nisy setroka nisavoana teo am-bavorony, Ary afo avy teo am-bavany no nandevona Ka nisy vainafo nidedadeda teo aminy.",
			"9": "<sup>9</sup> Dia naondriny ny lanitra, ka nidina Izy; Ary aizim-pito no teo ambanin’ny tongony.",
			"10": "<sup>10</sup> Dia nitaingina kerobima Izy ka nanidina, Eny, nanidina faingana tamin’ny ela-drivotra Izy.",
			"11": "<sup>11</sup> Efa nanao ny maizina ho fiereny sy tranony manodidina Azy Izy, Dia rahona ngalingaly –rahona matevina.",
			"12": "<sup>12</sup> Ary teo amin’ny famirapiratana teo anatrehany dia nandalo ny rahony matevina– Havandra sy vainafo.",
			"13": "<sup>13</sup> Dia nampikotroka tany an-danitra Jehovah, Ny Avo Indrindra niloa-peo– Havandra sy vainafo.",
			"14": "<sup>14</sup> Dia nandefa ny zana-tsipìkany Izy ka nampiely ny fahavaloko, Ary nampandeha helatra be ka nampifanaritaka azy.",
			"15": "<sup>15</sup> Dia hita ny mason-drano, Ary niseho ny fanambanin’ny tany Noho ny teny mafy nataonao, Jehovah ô, Noho ny fifofofofon’ny fofonain’ny vavoronao.",
			"16": "<sup>16</sup> Naninjitra ny tànany avy tany ambony Izy ka nandray ahy; Nanintona ahy niala tamin’ny rano be Izy;",
			"17": "<sup>17</sup> Namonjy ahy tamin’ny fahavaloko mahery Izy Sy tamin’izay nankahala ahy, satria tsy leoko ireny.",
			"18": "<sup>18</sup> Nisakana ahy tamin’ny andro nahitako loza ireny, Nefa Jehovah no niseho ho Mpanohana ahy.",
			"19": "<sup>19</sup> Dia nitondra ahy ho any amin’ny malalaka Izy; Namonjy ahy Izy, satria tiany aho.",
			"20": "<sup>20</sup> Nanisy soa ahy araka ny fahamarinako Jehovah; Araka ny fahadiovan’ny tànako no namaliany ahy.",
			"21": "<sup>21</sup> Fa nitandrina ny làlan’i Jehovah aho, Ka tsy mba nanao ratsy hialana tamin’Andriamanitro;",
			"22": "<sup>22</sup> Ny fitsipiny rehetra dia teo anatrehako, Ary ny lalàny tsy mba nampialaiko tamiko;",
			"23": "<sup>23</sup> Tsy nanan-tsiny taminy aho Sady niaro tena tsy ho azon’ny fahotako;",
			"24": "<sup>24</sup> Koa namaly ahy araka ny fahamarinako Jehovah, Dia araka ny fahadiovan’ny tànako teo imasony.",
			"25": "<sup>25</sup> Ny olona tsara no isehoanao fa tsara; Ny tena olo-marina no isehoanao fa marina;",
			"26": "<sup>26</sup> Ny madio no isehoanao fa madio; Ary ny mpiolakolaka kosa no isehoanao fa lalin-tsaina.",
			"27": "<sup>27</sup> Fa Hianao mamonjy ny olona ory; Ary ny maso miandranandrana kosa aetrinao.",
			"28": "<sup>28</sup> Fa Hianao mampirehitra ny jiroko; Jehovah Andriamanitro no mampahazava ny fahamaizinako.",
			"29": "<sup>29</sup> Fa Hianao no ahazoako mitsarapaka hamely ny miaramila iray toko; Ary Andriamanitro no ahazoako mitsambikina mihoatra ny mànda.",
			"30": "<sup>30</sup> Raha ny amin’Andriamanitra, dia marina ny làlany; Ny tenin’i Jehovah dia voazaha toetra amin’ny memy; Jehovah no ampingan’izay rehetra mialoka aminy.",
			"31": "<sup>31</sup> Fa iza no Andriamanitra afa-tsy Jehovah? Ary iza no Vatolampy afa-tsy Andriamanitsika?",
			"32": "<sup>32</sup> Izany Andriamanitra izany no mampisikina hery ahy Sy manamarina ny alehako.",
			"33": "<sup>33</sup> Manao ny tongotro ho tahaka ny an’ny dieravavy Izy Ka mampitoetra ahy eo amin’ny fitoerako avo.",
			"34": "<sup>34</sup> Mampianatra ny tànako hiady Izy, Ka mahahenjana tsipìka varahina ny sandriko.",
			"35": "<sup>35</sup> Omenao ahy ny ampingan’ny famonjenao; Ny tànanao ankavanana no manohana ahy, Ary ny fahamoram-panahinao no maha-lehibe ahy.",
			"36": "<sup>36</sup> Halalahinao ny làlana hodiaviko, Ka tsy mangovitra ny hato-tongotro.",
			"37": "<sup>37</sup> Manenjika ny fahavaloko aho ka mahatratra azy; Ary tsy mba miverina aho mandra-pahalany ritrany.",
			"38": "<sup>38</sup> Mamely mafy azy aho, ka tsy mahaleo izy; Lavo eo ambanin’ny tongotro izy.",
			"39": "<sup>39</sup> Hianao mampisikina ahy hery hiady; Aripakao ho ambaniko izay mitsangana hamely ahy,",
			"40": "<sup>40</sup> Ary ataonao miamboho ahy ny fahavaloko; Ka aringako izay mankahala ahy.",
			"41": "<sup>41</sup> Mitaraina izy, fa tsy misy mpamonjy, Dia amin’i Jehovah, fa tsy mamaly azy Izy.",
			"42": "<sup>42</sup> Torotoroiko madinika tahaka ny vovoka entin’ny rivotra izy; Tahaka ny fanary fotaka eny an-dàlambe no fanariko azy.",
			"43": "<sup>43</sup> Mamonjy ahy amin’ny fifanoheran’ny olona Hianao Sy manandratra ahy ho lohan’ny firenen-tsamy hafa; Ny firenena tsy fantatro aza dia manompo ahy.",
			"44": "<sup>44</sup> Raha vao mahare ny lazako fotsiny ihany aza izy, dia manoa ahy; Ny hafa firenena mikoy ahy.",
			"45": "<sup>45</sup> Ny hafa firenena dia mihaketraka Ka miala amin-kovitra avy ao amin’ny fiarovany mafy.",
			"46": "<sup>46</sup> Velona Jehovah; isaorana anie ny Vatolampiko; Asandratra anie Andriamanitry ny famonjena ahy,",
			"47": "<sup>47</sup> Dia Andriamanitra, Izay manao famaliana ho ahy Ka mampanaiky ny firenena ahy.",
			"48": "<sup>48</sup> Mamonjy ahy amin’ny fahavaloko Izy; Eny, manandratra ahy ho ambonin’izay mitsangana hanohitra ahy Hianao, Ary mamonjy ahy amin’ny olon-dozabe.",
			"49": "<sup>49</sup> Koa izany no hiderako Anao eny amin’ny firenen-tsamy hafa, Jehovah ô, Sy hankalazako ny anaranao.",
			"50": "<sup>50</sup> Manao famonjen-dehibe ho an’ny mpanjaka voatendriny Izy Ary mamindrafo amin’ny voahosony, Dia amin’i Davida sy ny taranany mandrakizay."
		},
		"19": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Salamo nataon’i Davida. NY lanitra mitory ny voninahitr’Andriamanitra; Ary ny habakabaka manambara ny asan’ny tànany.",
			"2": "<sup>2</sup> Ny andro samy miteny amin’ny andro mandimby azy. Ary ny alina samy manolo-pahalalana amin’ny alina mandimby azy.",
			"3": "<sup>3</sup> Tsy misy teny na fiteny anefa; Tsy re izay feony.",
			"4": "<sup>4</sup> Efa nivoaka ho any amin’ny tany rehetra ny famolainany, Ary ny teniny dia mihatra amin’ny faran’izao rehetra izao. Trano-lay ho an’ny masoandro no nataony teo aminy;",
			"5": "<sup>5</sup> Ary ny masoandro dia tahaka ny mpampaka-bady mivoaka avy ao an-trano fandriany; Sady mifaly tahaka ny lehilahy matanjaka mihazakazaka amin’ny alehany izy.",
			"6": "<sup>6</sup> Any amin’ny faravodilanitra ny fiposahany, Ary ny alehany dia hatramin’ny farany koa, Ka tsy misy takona amin’ny hafanany.",
			"7": "<sup>7</sup> Tsy misy tsiny ny lalàn’i Jehovah, mamelombelona ny fanahy; Mahatoky ny teni-vavolombelon’i Jehovah, mahahendry ny kely saina.",
			"8": "<sup>8</sup> Mahitsy ny fandidian’i Jehovah, mahafaly ny fo; Madio ny didin’i Jehovah, mampahazava ny maso.",
			"9": "<sup>9</sup> Madio ny fahatahorana an’i Jehovah, maharitra mandrakizay; Marina ny fitsipik’i Jehovah, eny, mahitsy avokoa.",
			"10": "<sup>10</sup> Irina noho ny volamena ireny, Eny, noho ny tena volamena betsaka aza; Ary mamy noho ny tantely, Eny, noho ny tantely mitete avy amin’ny tohotra.",
			"11": "<sup>11</sup> Ary ny mpanomponao dia hazavain’ireny; Amin’ny fitandremana azy misy valiny lehibe.",
			"12": "<sup>12</sup> Raha ny fahatsontsana, dia iza re no mahalala izany? Ataovy tsy manan-tsiny aho ny amin’izay ota tsy fantatra.",
			"13": "<sup>13</sup> Arovy ny mpanomponao tsy ho azon’ny ota sahisahy; Aoka tsy hanapaka ahy izany; Dia ho marina tokoa aho ka tsy hanan-tsiny ny amin’izay fahadisoana lehibe.",
			"14": "<sup>14</sup> Aoka ny tenin’ny vavako sy ny fisainan’ny foko samy hankasitrahana eo imasonao, Jehovah ô, Vatolampiko sy Mpanavotra ahy."
		},
		"20": {
			"1": "<sup>1</sup> Ho an’ny mpiventry hira. Salamo nataon’i Davida. HIHAINO anao anie Jehovah amin’ny andro fahoriana; Hitondra anao ho any amin’ny avo anie ny anaran’Andriamanitr’i Jakoba.",
			"2": "<sup>2</sup> Hahatonga famonjena ho anao avy amin’ny fitoerana masina anie Izy Ary hanohana anao avy any Ziona.",
			"3": "<sup>3</sup> Hahatsiaro ny fanatitra hohaninao rehetra anie Izy Ary hankasitraka ny fanatitra doranao. [Sela.]",
			"4": "<sup>4</sup> Hanome anao araka ny fonao anie Izy Ary hahatanteraka ny fisainanao rehetra.",
			"5": "<sup>5</sup> Aoka hihoby noho ny famonjena azonao izahay, Ary amin’ny anaran’Andriamanitray no hanangananay faneva; Hotanterahin’i Jehovah anie ny angatahinao rehetra.",
			"6": "<sup>6</sup> Ankehitriny dia fantatro fa Jehovah mamonjy ny voahosony; Hamaly azy avy amin’ny lanitry ny fahamasinany Izy amin’ny herin’ny famonjen’ny tànany ankavanana.",
			"7": "<sup>7</sup> Ny sasany mitoky amin’ny kalesy, ary ny sasany amin’ny soavaly; Fa raha izahay kosa, dia ny anaran’i Jehovah Andriamanitray no hoderainay.",
			"8": "<sup>8</sup> Ireo dia reraka sady lavo; Fa izahay kosa efa mitsangana sy mijoro,",
			"9": "<sup>9</sup> Jehovah ô, vonjeo ny mpanjaka; Hamaly anay anie Izy amin’izay andro iantsoanay Azy."
		},
		"21": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Salamo nataon’i Davida. JEHOVAH ô, ny herinao no hifalian’ny mpanjaka; Ary endrey ny haben’ny hiravoravoany amin’ny famonjenao!",
			"2": "<sup>2</sup> Ny nirin’ny fony dia nomenao azy, Ary ny nangatahin’ny molony tsy mba nolavinao. [Sela.]",
			"3": "<sup>3</sup> Fa notsenainao fitahiana soa izy; Teo an-dohany nasianao satroboninahitra tena volamena.",
			"4": "<sup>4</sup> Aina no nangatahiny taminao, dia nomenao azy izany, Dia andro lava mandrakizay doria.",
			"5": "<sup>5</sup> Lehibe ny voninahiny amin’ny famonjenao; Laza sy voninahitra no nataonao taminy.",
			"6": "<sup>6</sup> Fa nataonao ho fitahiana mandrakizay izy; Ampifalinao indrindra amin’ny tavanao izy.",
			"7": "<sup>7</sup> Fa ny mpanjaka matoky an’i Jehovah, Ary noho ny famindrampon’ny Avo Indrindra dia tsy hangozohozo izy.",
			"8": "<sup>8</sup> Ho azon’ny tànanao ny fahavalonao rehetra; Eny, ho azon’ny tànanao ankavanana ny mankahala anao;",
			"9": "<sup>9</sup> Hataonao tahaka ny fatana misy afo mirehitra izy amin’ny andro hisehoanao– Jehovah handany azy amin’ny fahatezerany, ary handevona azy ny afo–",
			"10": "<sup>10</sup> Ny zanany haringanao tsy ho eo amin’ny tany, Ary ny taranany tsy ho eo amin’ny zanak’olombelona;",
			"11": "<sup>11</sup> Fa nikasa hanisy ratsy anao izy Ka namoron-kevi-dratsy, fa tsy nahefa.",
			"12": "<sup>12</sup> Fa hampiamboho azy hianao; Ny tsipìkanao no ikendrenao ny tavany.",
			"13": "<sup>13</sup> Misandrata amin’ny herinao, Jehovah ô, Hihira sy hankalaza ny herinao izahay."
		},
		"22": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Alaieleth-hasahara. Salamo nataon’i Davida. ANDRIAMANITRO ô, Andriamanitro ô, nahoana no dia mahafoy ahy Hianao? Lavitra ny famonjena ahy ny tenin’ny fitarainako.",
			"2": "<sup>2</sup> Andriamanitro ô, miantso aho nony andro, nefa tsy mamaly Hianao; Ary nony alina, nefa tsy mahita fiononana aho.",
			"3": "<sup>3</sup> Kanefa masina Hianao, Ry mipetraka eo ambonin’ny fideran’ny Isiraely.",
			"4": "<sup>4</sup> Hianao no nitokian’ny razanay; Natoky izy, ka namonjy azy Hianao.",
			"5": "<sup>5</sup> Hianao no nitarainany, ka afaka izy; Hianao no nitokiany, ka tsy menatra izy.",
			"6": "<sup>6</sup> Ary izaho dia olitra, fa tsy olona, Fandàtsan’olona sy fanevatevam-bahoaka.",
			"7": "<sup>7</sup> Izay rehetra mahita ahy dia manaraby ahy; Manondro molotra izy sady mihifikifi-doha ka manao hoe:",
			"8": "<sup>8</sup> Ankino amin’i Jehovah, dia aoka Izy hamonjy azy; Eny, aoka Izy hanafaka azy, raha mankasitraka azy Izy.",
			"9": "<sup>9</sup> Fa Hianao no nampivoaka ahy avy tao an-kibo; Nampandry ahy fahizay Hianao, fony aho mbola nampinonoin’ny reniko.",
			"10": "<sup>10</sup> Nankinina taminao hatrany am-bohoka aho; Hatrany an-kibon-dreniko dia Andriamanitro Hianao.",
			"11": "<sup>11</sup> Aza manalavitra ahy Hianao, fa akaiky ny fahoriana, Sady tsy misy mpamonjy.",
			"12": "<sup>12</sup> Mitangorona amiko ireo ombilahy be, Ny maherin’i Basana manemitra ahy.",
			"13": "<sup>13</sup> Misanasana vava amiko izy; Eny, liona mamiravira sy mierona izy.",
			"14": "<sup>14</sup> Efa rano aidina aho, ka mivaha ny taolako rehetra; Tahaka ny savoka ny foko: Miempo ato anatiko izy.",
			"15": "<sup>15</sup> Maina tahaka ny tavim-bilany ny heriko; Ary ny lelako miraikitra amin’ny lanilaniko; Ao amin’ny vovoky ny fahafatesana no ametrahanao ahy.",
			"16": "<sup>16</sup> Fa mitangorona amiko ny amboa, Ny fiangonan’ny rasty fanahy manemitra ahy. Manindrona ny tongotro aman-tànako izy –",
			"17": "<sup>17</sup> Mahisa ny taolako rehetra aho– Ireo dia mibanjina sady mangarika ahy.",
			"18": "<sup>18</sup> Mizara ny fitafiako ho azy izy, Ary ny akanjoko ilokany.",
			"19": "<sup>19</sup> Fa Hianao, Jehovah ô, aza manalavitra; Ry Heriko ô, faingàna hamonjy ahy.",
			"20": "<sup>20</sup> Vonjeo amin’ny sabatra ny fanahiko, Dia ny hany malalako amin’ny herin’ny amboa.",
			"21": "<sup>21</sup> Vonjeo amin’ny vavan’ny liona aho; Ary valio ho afaka amin’ny tandroky ny ombimanga.",
			"22": "<sup>22</sup> Hitory ny anaranao amin’ny rahalahiko aho; Eo amin’ny fiangonana no hiderako Anao.",
			"23": "<sup>23</sup> Hianareo izay matahotra an’i Jehovah, miderà Azy; Ry taranak’i Jakoba rehetra, manomeza voninahitra Azy; Ary matahora Azy, ry Zanak’Isiraely rehetra.",
			"24": "<sup>24</sup> Fa tsy manao tsinontsinona na marikorikon’ny fahorian’ny ory Izy, Ary tsy mba manafina ny tavany aminy; Fa raha mitaraina aminy ireny, dia mihaino Izy.",
			"25": "<sup>25</sup> Avy aminao no ahazoako manao fiderana eo amin’ny fiangonan-dehibe; Ny voadiko dia hefaiko eo anatrehan’izay matahotra an’i Jehovah.",
			"26": "<sup>26</sup> Hihinana ny mpandefitra ka ho voky; Hidera an’i Jehovah izay mitady Azy; Ho velona mandrakizay anie ny fonareo.",
			"27": "<sup>27</sup> Hahatsiaro ka hiverina ho amin’i Jehovah ny faran’ny tany rehetra; Ary hiankohoka eo anatrehanao ny fokon’ny firenen-tsamy hafa.",
			"28": "<sup>28</sup> Fa an’i Jehovah ny fanjakana; Ary manapaka amin’ny firenen-tsamy hafa Izy.",
			"29": "<sup>29</sup> Hihinana sy hiankohoka ny mpanefohefo rehetra ambonin’ny tany; Eo anatrehany no hiondrehan’izay rehetra efa hidina any amin’ny vovoka ka tsy mahavelona ny ainy.",
			"30": "<sup>30</sup> Hisy taranaka hanompo Azy; Ary ny Tompo dia holazaina amin’ny taranaka ho avy.",
			"31": "<sup>31</sup> Hisy ho avy ka hanambara ny fahamarinany Amin’ny olona izay mbola hateraka, fa Izy no nanao izany."
		},
		"23": {
			"1": "<sup>1</sup> Salamo nataon’i Davida. JEHOVAH no Mpiandry ahy, Tsy hanan-java-mahory aho,",
			"2": "<sup>2</sup> Mampandry ahy amin’ny ahi-maitso Izy; Mitondra ahy eo amoron’ny rano fialan-tsasatra Izy.",
			"3": "<sup>3</sup> Mamelombelona ny fanahiko Izy; Mitarika ahy amin’ny làlan’ny fahamarinana noho ny anarany Izy.",
			"4": "<sup>4</sup> Eny, na dia mandeha mamaky ny lohasaha aloky ny fahafatesana aza aho, Dia tsy hatahotra ny loza aho; Fa Hianao no amiko; Ny tsorakazonao sy ny tehinao, ireo no mahafa-tahotra ahy.",
			"5": "<sup>5</sup> Mamelatra latabatra eo anoloako eo imason’ny fahavaloko Hianao; Manosotra diloilo ny lohako Hianao; Ny kapoakako feno dia feno.",
			"6": "<sup>6</sup> Eny tokoa, fahasoavana sy famindrampo no hanaraka ahy amin’ny andro rehetra hiainako, Dia hitoetra ao an-tranon’i Jehovah andro lava aho."
		},
		"24": {
			"1": "<sup>1</sup> Salamo nataon’i Davida. AN’i Jehovah ny tany sy izay rehetra eo aminy. Izao rehetra izao sy ny mponina eo aminy.",
			"2": "<sup>2</sup> Fa Izy no nanorina azy tambonin’ny ranomasina. Tambonin’ny rano no nampitoerany azy.",
			"3": "<sup>3</sup> Iza no hiakatra any an-tendrombohitr’i Jehovah? Ary iza no hitoetra ao amin’ny fitoerany masina?",
			"4": "<sup>4</sup> Izay madio tànana sy mahitsy fo, Izay tsy manandratra ny fanahiny ho amin’ny lainga, na mianiana hamitaka.",
			"5": "<sup>5</sup> Handray fitahiana avy amin’i Jehovah izy. Ary fahamarinana avy amin’Andriamanitry ny famonjena azy.",
			"6": "<sup>6</sup> Izany no taranaka mitady Azy, Izay mitady ny tavanao,–dia Jakoba. [Sela.]",
			"7": "<sup>7</sup> Asandrato ny lohanareo, ry vavahady, Ary misandrata hianareo, ry varavarana fahagola, Mba hidiran’ny Mpanjakan’ny voninahitra.",
			"8": "<sup>8</sup> Iza izao Mpanjakan’ny voninahitra izao? Jehovah mahery tsi-toha, Jehovah mpiady mahery.",
			"9": "<sup>9</sup> Asandrato ny lohanareo, ry vavahady; Ary manandrata ny lohanareo, ry varavarana fahagola, Mba hidiran’ny Mpanjakan’ny voninahitra.",
			"10": "<sup>10</sup> Iza ary izao Mpanjakan’ny voninahitra izao? Jehovah, Tompon’ny maro, Izy no Mpanjakan’ny voninahitra. [Sela.]"
		},
		"25": {
			"1": "<sup>1</sup> Nataon’i Davida. AMINAO, Jehovah ô, no anandratako ny fanahiko.",
			"2": "<sup>2</sup> Andriamanitro ô, Hianao no itokiako, aoka tsy ho menatra aho; Aoka tsy hifalian’ny fahavaloko aho.",
			"3": "<sup>3</sup> Eny, tsy hisy ho menatra izay rehetra miandry Anao; Fa izay mivadika foana no ho menatra.",
			"4": "<sup>4</sup> Ampahafantaro ny làlanao aho, Jehovah ô; Ampianaro ny sitrakao aho.",
			"5": "<sup>5</sup> Tariho amin’ny fahamarinanao aho, ka ampianaro; Fa Hianao no Andriamanitry ny famonjena ahy; Hianao no andrasako mandritra ny andro.",
			"6": "<sup>6</sup> Tsarovy ny antranao sy ny indrafonao, Jehovah ô, Fa hatrizay hatrizay ireny.",
			"7": "<sup>7</sup> Aza ny fahotan’ny fahatanorako, na ny ratsy nataoko, no tsarovanao; Fa aoka ho araka ny famindramponao no hahatsiarovanao ahy, Noho ny fahasoavanao Jehovah ô.",
			"8": "<sup>8</sup> Tsara sy mahitsy Jehovah, Ka izany no ampianarany ny mpanota ny làlana.",
			"9": "<sup>9</sup> Mitarika ny mpandefitra amin’ny marina Izy Ary mampianatra ny mpandefitra ny làlany.",
			"10": "<sup>10</sup> Ny làlan’i Jehovah rehetra dia famindrampo sy fahamarinana Amin’izay mitandrina ny fanekeny sy ny teni-vavolombelony.",
			"11": "<sup>11</sup> Noho ny anaranao, Jehovah ô, dia mamelà ny heloko, Fa lehibe izany.",
			"12": "<sup>12</sup> Iza no lehilahy matahotra an’i Jehovah? Izy no hampianariny izay làlana tokony hofidiny.",
			"13": "<sup>13</sup> Ny fanahiny hitoetra amin’ny soa; Ary ny taranany handova ny tany.",
			"14": "<sup>14</sup> Ny fisainan’i Jehovah asehony amin’izay matahotra Azy, Ary ny fanekeny no hampahafantariny azy.",
			"15": "<sup>15</sup> Ny masoko mijery an’i Jehovah mandrakariva; Fa Izy no nanafaka ny tongotro tamin’ny fandrika harato.",
			"16": "<sup>16</sup> Todiho aho, ka amindrao fo, Fa irery aho ka mahantra.",
			"17": "<sup>17</sup> Ny fahorian’ny foko efa mihalehibe; Afaho amin’ny fahatereko aho.",
			"18": "<sup>18</sup> Jereo ny fahoriako sy ny mavesatra amiko, Ka mamelà ny fahotako rehetra.",
			"19": "<sup>19</sup> Jereo ny fahavaloko, fa maro izy; Ary loza ny fankahalany ahy.",
			"20": "<sup>20</sup> Arovy ny fanahiko, ka vonjeo aho; Aoka tsy ho menatra aho, fa mialoka aminao.",
			"21": "<sup>21</sup> Aoka ny tsi-fisian-tsiny sy ny fahitsiana no hiaro ahy; Fa miandry Anao aho.",
			"22": "<sup>22</sup> Andriamanitra ô, afaho ny Isiraely Amin’ny fahoriany rehetra."
		},
		"26": {
			"1": "<sup>1</sup> Nataon’i Davida. TSARAO aho, Jehovah ô, fa mandeha amin’ny fahamarinako aho; Ary Jehovah no itokiako, ka tsy miahanahana aho.",
			"2": "<sup>2</sup> Diniho aho, Jehovah ô, ka fantaro; Izahao toetra ny voako sy ny foko.",
			"3": "<sup>3</sup> Fa ny famindramponao no eo anoloan’ny masoko; Ary mandeha amin’ny fahamarinanao aho.",
			"4": "<sup>4</sup> Tsy mba miara-mipetraka amin’ny olon-dratsy aho, Na manan-draharaha amin’ny olom-petsy.",
			"5": "<sup>5</sup> Halako ny fiangonan’ny mpanao ratsy; Ary tsy miara-mipetraka amin’ny ratsy fanahy aho.",
			"6": "<sup>6</sup> Manasa ny tànako noho ny fahamarinako aho Ka te-hitoetra eo akaikin’ny alitaranao, Jehovah ô,",
			"7": "<sup>7</sup> Mba hanao feo fiderana Ka hilaza ny asanao mahagaga rehetra.",
			"8": "<sup>8</sup> Jehovah ô, tiako ny trano itoeranao, Dia ny fonenan’ny voninahitrao.",
			"9": "<sup>9</sup> Aza mitondra ny fanahiko hiara-maty amin’ny mpanota, Na ny aiko amin’ny mpandatsa-dra,",
			"10": "<sup>10</sup> Izay misy fitaka eny an-tànany, Ary feno kolikoly ny tànany ankavanana.",
			"11": "<sup>11</sup> Fa izaho kosa dia mandeha amin’ny fahamarinako; Avoty sy amindrao fo aho.",
			"12": "<sup>12</sup> Ny tongotro mijoro eo amin’ny tany marina; Eo am-piangonana no isaorako an’i Jehovah."
		},
		"27": {
			"1": "<sup>1</sup> Nataon’i Davida. JEHOVAH no fahazavako sy famonjena ahy; Iza no hatahorako? Jehovah no fiarovana mafy ho an’ny aiko; Iza no hangovitako?",
			"2": "<sup>2</sup> Raha manatona ahy hihinana ny nofoko ny ratsy fanahy, Izay fahavaloko sy rafilahiko, Dia izy ihany no tafintohina ka lavo.",
			"3": "<sup>3</sup> Na dia misy antokony mitoby hiady amiko aza, Tsy hatahotra ny foko; Na dia misy ady mihatra amiko aza, Dia hatoky ihany aho.",
			"4": "<sup>4</sup> Zavatra iray loha no nangatahiko tamin’i Jehovah, Izany no hotadiaviko, Dia ny hitoetra ao an-tranon’i Jehovah amin’ny andro rehetra hiainako, Mba ho faly mijery ny fahasoavan’i Jehovah sy mandinika ny tempoliny.",
			"5": "<sup>5</sup> Fa hanafina ahy ao an-trano fialofany amin’ny andro fahoriana Izy; Hampiery ahy amin’ny fierena ao an-dainy Izy; Hanandratra ahy ho eny ambony vatolampy Izy.",
			"6": "<sup>6</sup> Koa dia hisandratra ambonin’ny fahavaloko manodidina ahy ny lohako, Ka hanatitra fanatitra hihobiana ao an-dainy aho; Hihira sy hankalaza an’i Jehovah aho.",
			"7": "<sup>7</sup> Mihainoa, Jehovah ô, Fa mitaraina mafy aho; Ary mamindrà fo amiko, ka valio aho.",
			"8": "<sup>8</sup> Raha hoy Hianao: Tadiavonareo ny tavako, Dia hoy ny navalin’ny foko: Eny, ny tavanao no tadiaviko, Jehovah ô;",
			"9": "<sup>9</sup> Aza manafina ny tavanao amiko Aza mandroaka ny mpanomponao an-katezerena; Efa famonjena ahy Hianao; Aza manary na mahafoy ahy, ry Andriamanitry ny famonjena ahy.",
			"10": "<sup>10</sup> Fa ny raiko aman-dreniko efa nahafoy ahy; Fa Jehovah kosa no mandray ahy.",
			"11": "<sup>11</sup> Ampianaro ny làlanao aho, Jehovah ô; Ary tariho amin’ny làlana marina aho noho ny fahavaloko.",
			"12": "<sup>12</sup> Aza manolotra ahy ho amin’ny sitrapon’ny mpampahory ahy; Fa vavolombelona mandainga sy izay mifofofofo loza no mitsangana hiampanga ahy.",
			"13": "<sup>13</sup> Raha izay re aho no tsy nanantena hahita ny fahasoavan’i Jehovah amin’ny tanin’ny velona.",
			"14": "<sup>14</sup> Miandrasa an’i Jehovah hianao; Matokia, ary aoka hahery ny fonao; Eny, miandrasa an’i Jehovah."
		},
		"28": {
			"1": "<sup>1</sup> Nataon’i Davida. HIANAO no antsoiko, Jehovah ô, Ry Vatolampiko ô, aza manentsin-tadìny amiko; Fandrao, raha mangina amiko Hianao, Dia ho tahaka izay midina any an-davaka aho.",
			"2": "<sup>2</sup> Mihainoa ny feon’ny fifonako, raha mitaraina aminao aho, Raha manandratra ny tànako ho amin’ny fitoeranao masina indrindra aho.",
			"3": "<sup>3</sup> Aza atao indray manakipaka amin’ny ratsy fanahy sy ny mpanao meloka aho, Fa ireny dia milaza fihavanana amin’ny namany, Kanjo ratsy no ao am-pony.",
			"4": "<sup>4</sup> Manomeza azy araka ny asany sy ny faharatsian’ny ataony; Eny, manomeza azy araka ny vitan’ny tànany, Valio izy araka izay iendrehany.",
			"5": "<sup>5</sup> Fa tsy mihevitra ny asan’i Jehovah, na ny vitan’ny tànany, izy, Dia horavan’i Jehovah izy, fa tsy haoriny.",
			"6": "<sup>6</sup> Isaorana anie Jehovah, Fa efa nihaino ny feon’ny fifonako Izy.",
			"7": "<sup>7</sup> Jehovah no heriko sy ampingako; Izy no nitokian’ny foko, ka voavonjy aho; Dia mifaly ny foko, ka ny hirako no hiderako Azy.",
			"8": "<sup>8</sup> Jehovah no heriny, Ary Izy no fiarovana mafy hamonjena ny voahosony.",
			"9": "<sup>9</sup> Vonjeo ny olonao, Ary tahio ny lovanao; Andraso izy, ka tezao mandrakizay."
		},
		"29": {
			"1": "<sup>1</sup> Salamo nataon’i Davida. MANOMEZA an’i Jehovah, ry zanak’Andriamanitra, Eny, manomeza voninahitra sy hery ho an’i Jehovah.",
			"2": "<sup>2</sup> Omeo an’i Jehovah ny voninahitry ny anarany; Miankohofa eo anatrehan’i Jehovah amin’ny fihaingoana masina.",
			"3": "<sup>3</sup> Ny feon’i Jehovah no eny ambonin’ny rano be; Andriamanitry ny voninahitra no mampikotrokorana; Jehovah no eny ambonin’ny rano be.",
			"4": "<sup>4</sup> Ny feon’i Jehovah be hery; Ny feon’i Jehovah be voninahitra.",
			"5": "<sup>5</sup> Ny feon’i Jehovah manorotoro ny hazo sedera; Eny, Jehovah manorotoro ny hazo sedera any Libanona.",
			"6": "<sup>6</sup> Dia mampitsambikina azy tahaka ny zanak’omby Izy, Libanona sy Siriona dia tahaka ny zanaky ny ombimanga.",
			"7": "<sup>7</sup> Ny feon’i Jehovah mampisaraka ny lelafo;",
			"8": "<sup>8</sup> Ny feon’i Jehovah mampihorohoro ny efitra; Jehovah mampihorohoro ny efitr’i Kadesy.",
			"9": "<sup>9</sup> Ny feon’i Jehovah mampiteraka ny dieravavy Ary mivaofy ny ala be. Ary ny ao an-tempoliny samy manao hoe: Voninahitra e!",
			"10": "<sup>10</sup> Jehovah nipetraka teo ambonin’ny Safo-drano; Eny, Jehovah mipetraka ho Mpanjaka mandrakizay.",
			"11": "<sup>11</sup> Jehovah hanome hery ny olony; Jehovah hitahy ny olony amin’ny fiadanana."
		},
		"30": {
			"1": "<sup>1</sup> Salamo. Fihirana tamin’ny nitokanana ny trano. Nataon’i Davida. HANANDRATRA Anao aho, Jehovah ô; fa nanintona ahy niakatra Hianao, Ka tsy nataonao ho fifalian’ny fahavaloko aho.",
			"2": "<sup>2</sup> Jehovah Andriamanitro ô, nitaraina taminao aho, Dia nahasitrana ahy Hianao.",
			"3": "<sup>3</sup> Jehovah ô, efa nampakatra ny fanahiko avy tany amin’ny fiainan-tsi-hita Hianao; Efa nampody ny aiko Hianao, mba tsy ho isan’izay midina any an-davaka aho.",
			"4": "<sup>4</sup> Mankalazà an’i Jehovah, ry olony masina, Ka miderà ny anarany masina.",
			"5": "<sup>5</sup> Fa indray mipì-maso ihany ny fahatezerany; Nefa mandritra ny andro iainana kosa ny fankasitrahany; Amin’ny hariva dia misy fitomaniana tonga hivahiny miloaka alina; Fa nony maraina kosa dia misy fihobiana.",
			"6": "<sup>6</sup> Ary hoy izaho, raha mbola niadana: Tsy hangozohozo mandrakizay aho.",
			"7": "<sup>7</sup> Jehovah ô, noho ny fankasitrahanao dia nampitoerinao mafy ny tendrombohitro; Nanafina ny tavanao Hianao, dia ory aho.",
			"8": "<sup>8</sup> Hianao no nantsoiko, Jehovah ô; Ary Jehovah no nifonako hoe:",
			"9": "<sup>9</sup> Inona no soa azo amin’ny rako sy ny hidinako any an-davaka? Hidera Anao va ny vovoka? hanambara ny fahamarinanao va izany?",
			"10": "<sup>10</sup> Mihainoa, Jehovah ô, ka mamindrà fo amiko; Jehovah ô, aoka ho Mpamonjy ahy Hianao.",
			"11": "<sup>11</sup> Efa nampody ny fisaonako ho fandihizana Hianao; Efa nanaisotra ny lamba fisaonako Hianao ka nampisikina ahy fifaliana,",
			"12": "<sup>12</sup> Mba hankalazan’ny fanahiko Anao ka tsy hangina. Jehovah Andriamanitro ô, hidera Anao mandrakizay aho."
		},
		"31": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Salamo nataon’i Davida. JEHOVAH ô, Hianao no ialofako; Aoka tsy ho menatra akory mandrakizay aho; Vonjeo amin’ny fahamarinanao aho.",
			"2": "<sup>2</sup> Atongilano amiko ny sofinao; faingàna hamonjy ahy; Aoka ho vatolampiko fiarovana mafy Hianao Sy batery fiarovana hamonjena ahy.",
			"3": "<sup>3</sup> Fa harambatoko sy batery fiarovana ho ahy Hianao; Ary noho ny anaranao no hitondranao sy hitarihanao ahy.",
			"4": "<sup>4</sup> Hanafaka ahy amin’ny fandrika harato izay nafeniny hamandrihany ahy Hianao; Fa Hianao no fiarovana mafy ho ahy.",
			"5": "<sup>5</sup> Eo an-tànanao no atolotro ny fanahiko; Fa efa nanavotra ahy Hianao, Jehovah ô, Andriamanitry ny fahamarinana.",
			"6": "<sup>6</sup> Halako izay mivavaka amin’ny sampy; Fa Jehovah kosa no itokiako.",
			"7": "<sup>7</sup> Hifaly sy ho ravoravo amin’ny famindramponao aho; Fa nahita ny fahantrako Hianao Ka nahafantatra ny fahorian’ny fanahiko",
			"8": "<sup>8</sup> Ary tsy mba nangeja ahy teo an-tànan’ny fahavalo; Fa efa nampitoetra ny tongotro eo amin’ny malalaka Hianao.",
			"9": "<sup>9</sup> Mamindrà fo amiko, Jehovah ô, fa azom-pahoriana aho; Simban’alahelo ny masoko sy ny fanahiko ary ny tenako.",
			"10": "<sup>10</sup> Fa lanin’alahelo ny aiko, sady lanim-pisentoana ny taonako; Tsy manan-kery aho noho ny heloko, Ary efa lany ny taolako.",
			"11": "<sup>11</sup> Noho ny ataon’ny fahavaloko rehetra dia efa tonga fandatsa aho, Eny, na dia amin’ny namako aza, sady atahoran’ny olom-pantatro aho; Izay mahita ahy eny ivelany mandositra ahy.",
			"12": "<sup>12</sup> Efa hadino tahaka ny olona maty izay tsy tsaroana intsony aho; Tahaka ny vilany ariana aho.",
			"13": "<sup>13</sup> Fa mandre ny endrikendriky ny maro aho; Misy fampangorohoroana manodidina, Raha miara-mioko hamely ahy izy Ka miray tetika hahafaty ny aiko.",
			"14": "<sup>14</sup> Nefa izaho matoky Anao, Jehovah ô; Hoy izaho: Andriamanitro Hianao.",
			"15": "<sup>15</sup> Eo an-tànanao ny androko; Vonjeo aho tsy ho azon’ny tànan’ny fahavaloko sy izay manenjika ahy.",
			"16": "<sup>16</sup> Ampamirapirato amin’ny mpanomponao ny tavanao; Vonjeo amin’ny famindramponao aho.",
			"17": "<sup>17</sup> Aoka tsy ho menatra aho, Jehovah ô, satria miantso Anao; Fa aoka kosa ho menatra ny ratsy fanahy ka hangina any amin’ny fiainan-tsi-hita.",
			"18": "<sup>18</sup> Aoka hangina ny molotra mandainga, Izay miteny sahisahy hanohitra ny marina Amin’ny avonavona sy ny fanamavoana.",
			"19": "<sup>19</sup> Akory re ny haben’ny zava-tsoanao voarakitrao ho an’ny matahotra Anao! Dia izay nataonao teo anatrehan’ny zanak’olombelona ho an’izay mialoka aminao!",
			"20": "<sup>20</sup> Hanafina azy ao amin’ny fiafenana eo anatrehanao Hianao, mba tsy ho tratry ny teti-dratsy ataon’ny olona izy; Hampiery azy ao an-tranonao Hianao, mba tsy ho azon’ny lela mifanditra izy.",
			"21": "<sup>21</sup> Isaorana anie Jehovah; Fa nataony mahagaga ny famindrampony tamiko tao an-tanàna mimànda.",
			"22": "<sup>22</sup> Fa hoy izaho tamin’ny fahatairako; Voafongotra tsy ho eo imasonao aho; Kanjo nihaino ny feon’ny fifonako ihany Hianao, raha nitaraina taminao aho.",
			"23": "<sup>23</sup> Tiava an’i Jehovah, ry olony masina rehetra; Miaro ny marina Jehovah, Ary mamaly dia mamaly ny mpiavonavona Izy.",
			"24": "<sup>24</sup> Mahereza, ary aoka hatanjaka ny fonareo, Dia hianareo rehetra izay manantena an’i Jehovah."
		},
		"32": {
			"1": "<sup>1</sup> Nataon’i Davida. Maskila. SAMBATRA ny olona izay voavela ny helony sy voasarona ny fahotany.",
			"2": "<sup>2</sup> Sambatra ny olona izay tsy isain’i Jehovah heloka, Ary tsy misy fitaka ny fanahiny.",
			"3": "<sup>3</sup> Raha nangina aho, dia nihalany ny taolako rehetra Noho ny fitarainako mandrakariva.",
			"4": "<sup>4</sup> Fa efa navesatra tamiko andro aman-alina ny tànanao; Niova ny fahatanjahako toy ny azon’ny fahamainana amin’ny lohataona. [Sela.]",
			"5": "<sup>5</sup> Ny fahotako nambarako taminao, ary ny heloko tsy mba nafeniko. Hoy izaho: Hiaiky ny fahadisoako amin’i Jehovah aho; Ary Hianao dia namela ny heloky ny fahotako. [Sela.]",
			"6": "<sup>6</sup> Izany no aoka hivavahan’ny tsara fanahy rehetra aminao amin’izay andro hahitana Anao; Eny tokoa, na dia tondraka aza ny ranobe, Tsy hahatratra azy izany.",
			"7": "<sup>7</sup> Hianao no fiereko, miaro ahy tsy ho azon’ny fahoriana Hianao; Fihobiam-pamonjena no ahodidinao ahy. [Sela.]",
			"8": "<sup>8</sup> Hanome saina anao sy hampianatra anao izay làlan-kalehanao Aho; Hitsinjo anao ny masoko ka hanolo-tsaina anao Aho.",
			"9": "<sup>9</sup> Aza manahaka ny soavaly na ny ampondra tsy manan-tsaina; Vy sy lamboridy no haingo hihazonana azy; Fa tsy mety manakaiky anao izy.",
			"10": "<sup>10</sup> Be ny fanaintainan’ny ratsy fanahy; Fa izay matoky an’i Jehovah kosa dia hohodidinin’ny famindrampo.",
			"11": "<sup>11</sup> Mifalia amin’i Jehovah, ary ravoravoa, hianareo olo-marina; Ary mihobia, hianareo mahitsy fo rehetra."
		},
		"33": {
			"1": "<sup>1</sup> MIHOBIA ho an’i Jehovah hianareo, ry olo-marina; Fa mendrika hataon’ny olo-mahitsy ny fiderana.",
			"2": "<sup>2</sup> Miderà an’i Jehovah amin’ny lokanga; Mankalazà Azy amin’ny valiha tory folo.",
			"3": "<sup>3</sup> Mihirà fihiram-baovao ho Azy; Mitendre tsara amin’ny feo fifaliana.",
			"4": "<sup>4</sup> Fa mahitsy ny tenin’i Jehovah; Ary ny asany rehetra dia mahatoky avokoa.",
			"5": "<sup>5</sup> Tia fahamarinana sy rariny Izy; Henika ny famindrampon’i Jehovah ny tany.",
			"6": "<sup>6</sup> Ny tenin’i Jehovah no nanaovana ny lanitra; Ary ny fofonain’ny vavany no nanaovana ny hamaroany rehetra.",
			"7": "<sup>7</sup> Mampiavosa ny rano ao an-dranomasina ho toy ny antontan-javatra Izy; Mitahiry ny rano lalina ao an-trano fitehirizana Izy.",
			"8": "<sup>8</sup> Aoka hatahotra an’i Jehovah ny tany rehetra; Aoka hangovitra eo anatrehany ny mponina rehetra amin’izao tontolo izao.",
			"9": "<sup>9</sup> Fa Izy no niteny, dia ary izany; Izy no nandidy, dia nitoetra mafy izany.",
			"10": "<sup>10</sup> Jehovah mahafoana ny fisainan’ny firenen-tsamy hafa; Mahatsinontsinona ny hevitry ny olona Izy.",
			"11": "<sup>11</sup> Ny fisainan’i Jehovah maharitra mandrakizay, Ny hevitry ny fony mahatratra ny taranaka fara mandimby.",
			"12": "<sup>12</sup> Sambatra ny firenena izay manana an’i Jehovah ho Andriamaniny, Dia ny olona nofidiny ho lovany.",
			"13": "<sup>13</sup> Eny an-danitra no itsinjovan’i Jehovah; Mijery ny zanak’olombelona rehetra Izy;",
			"14": "<sup>14</sup> Eo amin’ny fonenany no ijereny Ny mponina rehetra ambonin’ny tany,–",
			"15": "<sup>15</sup> Dia Izy Mpamorona ny fon’izy rehetra Sy Mpamantatra ny asany rehetra.",
			"16": "<sup>16</sup> Tsy misy mpanjaka voavonjin’ny hamaroan’ny miaramilany; Ny olo-mahery tsy voavonjin’ny haben’ny heriny.",
			"17": "<sup>17</sup> Ny soavaly tsy azo itokiana ho famonjena, Ary tsy mahavonjy ny haben’ny heriny.",
			"18": "<sup>18</sup> Indro, ny mason’i Jehovah mitsinjo izay matahotra Azy, Dia izay manantena ny famindrampony,",
			"19": "<sup>19</sup> Mba hanafaka ny fanahiny amin’ny fahafatesana Sy hamelona azy, raha misy mosary.",
			"20": "<sup>20</sup> Ny fanahintsika miandry an’i Jehovah; Famonjena antsika sy ampingantsika Izy.",
			"21": "<sup>21</sup> Fa Izy no ifalian’ny fontsika, Satria ny anarany masina no itokiantsika.",
			"22": "<sup>22</sup> Jehovah ô, aoka ny famindramponao hitoetra aminay Araka ny anantenanay Anao."
		},
		"34": {
			"1": "<sup>1</sup> Nataon’i Davida tamin’izy nody adala teo anatrehan’i Abimeleka, dia noroahiny ka lasa nandeha. HISAOTRA an’i Jehovah lalandava aho; Ho eo am-bavako mandrakariva ny fiderana Azy.",
			"2": "<sup>2</sup> Jehovah no reharehan’ny fanahiko; Hahare izany ny mpandefitra ka ho faly.",
			"3": "<sup>3</sup> Miaraha mankalaza an’i Jehovah amiko; Ary aoka hiara-manandratra ny anarany isika.",
			"4": "<sup>4</sup> Mitady an’i Jehovah aho, dia mamaly ahy Izy Ka manafaka ahy amin’ny tahotro rehetra.",
			"5": "<sup>5</sup> Mijery Azy ireo ka miramirana, Sady tsy mangaihay ny tavany.",
			"6": "<sup>6</sup> Ity olo-mahantra ity niantso, dia nihaino Jehovah Ka namonjy azy tamin’ny fahoriany rehetra.",
			"7": "<sup>7</sup> Ny Anjelin’i Jehovah mitoby manodidina izay matahotra Azy Ka mamonjy azy.",
			"8": "<sup>8</sup> Andramo ka izahao fa tsara Jehovah; Sambatra izay olona mialoka aminy.",
			"9": "<sup>9</sup> Matahora an’i Jehovah, hianareo olony masina; Fa tsy hanan-java-mahory izay matahotra Azy.",
			"10": "<sup>10</sup> Na dia ny liona tanora aza tsy ampy hanina ka noana; Fa izay mitady an’i Jehovah kosa dia tsy ho orin-java-tsoa akory.",
			"11": "<sup>11</sup> Avia, anaka, mihainoa ahy; Ny fahatahorana an’i Jehovah no hampianariko anareo.",
			"12": "<sup>12</sup> Na iza na iza ianao no maniry fiainana Ka tia andro maro hahitana ny soa,",
			"13": "<sup>13</sup> Dia arovy ny lelanao amin’ny ratsy Ary ny molotrao mba tsy hiteny fitaka.",
			"14": "<sup>14</sup> Halaviro ny ratsy, ka manaova soa: Mitadiava fihavanana, ka araho izany.",
			"15": "<sup>15</sup> Ny mason’i Jehovah dia mitsinjo ny marina, Ary ny sofiny mihaino ny fitarainany.",
			"16": "<sup>16</sup> Ny tavan’i Jehovah tezitra amin’ny mpanao ratsy kosa, Mba hofongorany tsy ho eo amin’ny tany ny fahatsiarovana azy.",
			"17": "<sup>17</sup> Mitaraina ny marina, ary mihaino azy Jehovah Ka mamonjy azy amin’ny fahoriany rehetra.",
			"18": "<sup>18</sup> Akaikin’izay manana fo mangorakoraka Jehovah Ary mamonjy izay torotoro fanahy.",
			"19": "<sup>19</sup> Maro ny fahorian’ny marina; Nefa Jehovah manafaka azy amin’izany rehetra izany.",
			"20": "<sup>20</sup> Miaro ny taolany rehetra Izy; Tsy hisy ho tapaka ireny na dia iray akory aza.",
			"21": "<sup>21</sup> Hahafaty ny ratsy fanahy ny fahoriana; Ary ny mankahala ny olo-marina hohelohina.",
			"22": "<sup>22</sup> Fa Jehovah manavotra ny fanahin’ny mpanompony, Ka tsy mba hisy hohelohina izay rehetra mialoka aminy."
		},
		"35": {
			"1": "<sup>1</sup> Nataon’i Davida. JEHOVAH ô, mamalia izay manana ady amiko; Miadia amin’izay mamely ahy.",
			"2": "<sup>2</sup> Raiso ny ampinga kely sy ny ampinga lehibe, Ka mitsangàna hamonjy ahy.",
			"3": "<sup>3</sup> Ary raiso ny lefona, ka sakano izay manenjika ahy; Lazao amin’ny fanahiko hoe: Famonjena anao Aho.",
			"4": "<sup>4</sup> Aoka ho menatra sy ho afa-baraka izay mitady ny aiko; Aoka hiamboho sy hangaihay izay misaina hanisy ratsy ahy.",
			"5": "<sup>5</sup> Aoka ho tahaka ny akofa eo alohan’ny rivotra izy. Ary aoka ny Anjelin’i Jehovah handroaka azy.",
			"6": "<sup>6</sup> Aoka ho maizina sy malama ny làlany, Ary aoka ny Anjelin’i Jehovah hanenjika azy.",
			"7": "<sup>7</sup> Fa tsy ahoan-tsy ahoana no nanaovany fandri-totofana hahazoany ahy; Tsy ahoan-tsy ahoana no nandavahany hamandrihany ny aiko.",
			"8": "<sup>8</sup> Aoka ny fandringanana ho tonga tampoka aminy; Ary aoka hahazo azy ny fandrika harato nafeniny, Ka ho latsaka ao amin’izany fandringanana izany indrindra izy.",
			"9": "<sup>9</sup> Fa ny fanahiko kosa hifaly amin’i Jehovah Sy ho ravoravo amin’ny famonjeny.",
			"10": "<sup>10</sup> Ny taolako rehetra hanao hoe: Jehovah ô, iza no tahaka Anao, Izay manafaka ny ory amin’izay tsy tohany, Eny, ny ory sy ny malahelo amin’izay mamabo azy?",
			"11": "<sup>11</sup> Mitsangana ny vavolombelona mampidi-doza; Ka izay tsy fantatro no anadinany ahy.",
			"12": "<sup>12</sup> Mamaly ratsy ahy ho solon’ny soa izy, Ka mamoy malala ny fanahiko.",
			"13": "<sup>13</sup> Ary ny amiko, raha narary izy ireo, Dia lamba fisaonana no nitafiako; Nampahory ny tenako tamin’ny fifadian-kanina aho; Ary ny fivavako nody tao an-tratrako.",
			"14": "<sup>14</sup> Toy ny nisy nanjo ny sakaizako na ny rahalahiko no nandehanako; Nitanondrika tamin’alahelo toy ny fisaona reny aho.",
			"15": "<sup>15</sup> Nefa nony ho lavo kosa aho, dia faly izy ireo ka niangona; Olona faraidina no nitangorona hamely ahy, nefa tsy fantatro; Namiravira izy ka tsy mba nitsahatra;",
			"16": "<sup>16</sup> Naman’ny olon-dratsy manendrikendrika ho enti-mila sitraka izy Ka nihidy vazana tamiko.",
			"17": "<sup>17</sup> Ry Tompo ô, mandra-pahoviana no hijerenao? Avereno ny fanahiko ho afaka amin’ny fandringanany, Ny hany malalako ho afaka amin’ny liona tanora.",
			"18": "<sup>18</sup> Hisaotra Anao eo amin’ny fiangonana lehibe aho; Hidera Anao eo afovoan’ny olona maro aho.",
			"19": "<sup>19</sup> Aoka tsy hifalian’izay mandrafy ahy foana aho; Ary aoka tsy hamì-maso izay mankahala ahy tsy ahoan-tsy ahoana.",
			"20": "<sup>20</sup> Fa tsy fihavanana no lazainy; Fa mamorom-pitaka izy hamelezany izay mibonaika ambonin’ny tany.",
			"21": "<sup>21</sup> Dia mitanatana vava amiko izy Ka manao hoe: Hia! sakoa izay! efa hitan’ny masonay.",
			"22": "<sup>22</sup> Efa hitanao, Jehovah ô, ka aza manentsin-tadiny; Ry Tompo ô, aza manalavitra ahy.",
			"23": "<sup>23</sup> Mahatsiarova, ka mifohaza ho amin’ny fitsarana ahy, O Andriamanitro sy Tompoko! mifohaza ho amin’ny adiko.",
			"24": "<sup>24</sup> Tsarao aho, Jehovah Andriamanitro ô, araka ny fahamarinanao; Ary aoka tsy hifalian’ireo aho.",
			"25": "<sup>25</sup> Aoka tsy hanao anakampo hoe izy: Hia! faly izahay! Aoka tsy hanao hoe izy: Efa natelinay izy.",
			"26": "<sup>26</sup> Aoka ho menatra sy hangaihay avokoa izay mifaly amin’ny manjo ahy; Aoka hitafy henatra sy fahafaham-baraka izay mirehareha amiko.",
			"27": "<sup>27</sup> Aoka hihoby sy hifaly izay mankasitraka ny fahamarinako; Ary aoka izy hanao mandrakariva hoe: Hankalazaina anie Jehovah, Izay mankasitraka ny fiadanan’ny mpanompony.",
			"28": "<sup>28</sup> Ary ny lelako hilaza ny fahamarinanao; Ny fiderana Anao no hataoko mandrakariva."
		},
		"36": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Nataon’i Davida, mpanompon’i Jehovah. NY ota milaza ato am-poko ny amin’ny ratsy fanahy hoe: Tsy misy fahatahorana an’Andriamanitra ao aminy.",
			"2": "<sup>2</sup> Fa miarahaba tena izy Ny amin’ny tsy hahitana ny helony ka hankahalana azy.",
			"3": "<sup>3</sup> Ny tenin’ny vavany dia faharatsiana sy fitaka; Efa nihadala izy ka nitsahatra tsy hanao soa.",
			"4": "<sup>4</sup> Faharatsiana no saintsaininy eo am-pandriany; Mitsangana eo amin’ny làlana tsy mety izy; Ny ratsy tsy mba laviny akory.",
			"5": "<sup>5</sup> Jehovah ô, mipaka amin’ny lanitra ny famindramponao; Mihatra amin’ny rahona ny fahamarinanao.",
			"6": "<sup>6</sup> Ny fahamarinanao dia tahaka ny tendrombohitra avo dia avo; Ny fitsaranao dia toy ny lalina indrindra; Ny olona sy ny biby dia samy vonjenao, Jehovah ô,",
			"7": "<sup>7</sup> Endrey ny hatsaran’ny famindramponao, Andriamanitra ô! Ka mialoka eo amin’ny aloky ny elatrao ny zanak’olombelona.",
			"8": "<sup>8</sup> Voky ny matavy ao an-tranonao izy; Ary ny ony fampifalianao no ampisotroanao azy.",
			"9": "<sup>9</sup> Fa ao aminao no misy loharanon’aina; Ny fahazavanao no hahitanay fahazavana.",
			"10": "<sup>10</sup> Ampahareto amin’izay mahalala Anao ny famindramponao, Ary ny fahamarinanao amin’ny mahitsy fo.",
			"11": "<sup>11</sup> Aoka tsy hanitsaka ahy ny tongotry ny mpiavonavona, Na hampandositra ahy ny tànan’ny ratsy fanahy.",
			"12": "<sup>12</sup> Indreo! efa lavo ny mpanao ratsy; Naripaka izy ka tsy maharina."
		},
		"37": {
			"1": "<sup>1</sup> Nataon’i Davida. AOKA tsy hirehitra ny fahatezeranao noho ny amin’ny mpanao ratsy; Ary aza mialona ny mpanao meloka;",
			"2": "<sup>2</sup> Fa hojinjana faingana tahaka ny ahitra izy Ka halazo tahaka ny ahi-maitso.",
			"3": "<sup>3</sup> Matokia an’i Jehovah hianao, ka manaova soa, Dia honina amin’ny tany hianao ka ho faly amin’ny fahamarinana.",
			"4": "<sup>4</sup> Ary miravoravoa amin’i Jehovah hianao, Dia homeny anao izay irin’ny fonao.",
			"5": "<sup>5</sup> Ankino amin’i Jehovah ny làlanao, Ary matokia Azy, fa hataony tanteraka.",
			"6": "<sup>6</sup> Ary hampiseho ny fahamarinanao tahaka ny mazava Izy, Ary ny rariny ho anao tahaka ny mitataovovonana.",
			"7": "<sup>7</sup> Miantombena tsara miandry an’i Jehovah hianao ka manantenà Azy; Aza tezitra amin’izay ambinina amin’ny làlany, Dia amin’izay olona manao sain-dratsy.",
			"8": "<sup>8</sup> Mitsahara amin’ny fahatezerana, ary mahafoiza ny fahavinirana; Aoka tsy hirehitra ny fahatezeranao, fa ratsy no hiafaran’izany.",
			"9": "<sup>9</sup> Fa ny mpanao ratsy hofongorana; Ary izay miandry an’i Jehovah no handova ny tany.",
			"10": "<sup>10</sup> Fa rehefa afaka kelikely, dia tsy hisy ny ratsy fanahy; Handinika ny fonenany hianao, fa tsy ho hita izy.",
			"11": "<sup>11</sup> Fa ny mpandefitra kosa no handova ny tany Ka hiravoravo amin’ny haben’ny fiadanana.",
			"12": "<sup>12</sup> Ny ratsy fanahy manao hevi-dratsy hamelezany ny marina Ka mihidy vazana aminy.",
			"13": "<sup>13</sup> Ny Tompo mihomehy azy, Satria hitany fa ho tonga ny androny.",
			"14": "<sup>14</sup> Ny ratsy fanahy nampitsoaka ny sabany ka nanenjana ny tsipìkany Mba hampikarapoka ny ory sy ny malahelo Ary hamono ny mahitsy làlana.",
			"15": "<sup>15</sup> Ny sabany hitsatoka amin’ny fony ihany, Ary ny tsipìkany ho tapatapaka.",
			"16": "<sup>16</sup> Tsara lavitra ny kely ananan’ny olo-marina anankiray Noho ny be ananan’ny ratsy fanahy maro.",
			"17": "<sup>17</sup> Fa ny sandrin’ny ratsy fanahy ho tapatapaka; Fa Jehovah manohana ny marina.",
			"18": "<sup>18</sup> Fantatr’i Jehovah ny andron’ny mahitsy; Ary ny lovany ho mandrakizay.",
			"19": "<sup>19</sup> Tsy mba ho menatra amin’ny andro fahoriana ireny; Ary amin’ny taona mosarena dia ho voky izy.",
			"20": "<sup>20</sup> Fa ny ratsy fanahy haringana, Ary ny fahavalon’i Jehovah ho tahaka ny voninahitry ny saha; Ho levona izy, eny, ho levon-ko setroka izy.",
			"21": "<sup>21</sup> Misambotra ny ratsy fanahy, nefa tsy manonitra; Fa ny marina kosa mamindra fo ka manome.",
			"22": "<sup>22</sup> Fa izay tahìny no handova ny tany; Ary izay ozoniny kosa no hofongorana.",
			"23": "<sup>23</sup> Avy amin’i Jehovah no mahalavorary ny dian’ny olona, Ka Izy no mankasitraka ny làlany.",
			"24": "<sup>24</sup> Na dia lavo aza izy, tsy dia mikarapoka, Fa Jehovah no mitantana azy.",
			"25": "<sup>25</sup> Efa mba tanora ihany aho, ary efa antitra ankehitriny; Fa tsy mbola hitako izay marina nafoy, na ny zanany mangataka hanina.",
			"26": "<sup>26</sup> Miantra mandrakariva ireny ka mampisambotra; Ary ny taranany hotahina.",
			"27": "<sup>27</sup> Halaviro ny ratsy, ka manaova soa, Dia honina mandrakizay hianao.",
			"28": "<sup>28</sup> Fa Jehovah tia rariny sady tsy hahafoy ny olony masina; Harovana mandrakizay ireo; Fa ny taranaky ny ratsy fanahy hofongorana.",
			"29": "<sup>29</sup> Ny marina handova ny tany Ka honina eo aminy mandrakizay.",
			"30": "<sup>30</sup> Ny vavan’ny marina miresaka fahendrena, Ary ny lelany manambara ny rariny.",
			"31": "<sup>31</sup> Ny lalàn’Andriamaniny no ao am-pony; Tsy mba ho solafaka ny diany.",
			"32": "<sup>32</sup> Mitsikilo ny marina ny ratsy fanahy Ka mitady hahafaty azy.",
			"33": "<sup>33</sup> Jehovah tsy hahafoy azy ho eo an-tànany. Na hanameloka azy, raha tsaraina izy.",
			"34": "<sup>34</sup> Miandrasa an’i Jehovah, ka tandremo ny làlany, Dia hanandratra anao handova ny tany Izy; Ho faly hahita ny hamongorana ny ratsy fanahy hianao.",
			"35": "<sup>35</sup> Efa hitako ny ratsy fanahy, fa loza loatra izy Ary nandrahaka tahaka ny hazo maitso naniry ho azy;",
			"36": "<sup>36</sup> Kanjo nandalo izy ka, indro, tsy teo intsony; Ary nitady azy aho, fa tsy hita izy.",
			"37": "<sup>37</sup> Diniho ny mahitsy, ka jereo ny marina; Fa misy farany ho an’ny olona tia fihavanana.",
			"38": "<sup>38</sup> Fa ny mpanota haringana avokoa; Ny hiafaran’ny ratsy fanahy ho fongotra.",
			"39": "<sup>39</sup> Fa ny famonjena ny marina dia avy amin’i Jehovah; Fiarovana mafy ho azy amin’ny andro fahoriana Izy.",
			"40": "<sup>40</sup> Ary Jehovah mamonjy azy sy manafaka azy; Eny, manafaka azy amin’ny ratsy fanahy Izy ka mamonjy azy, Satria nialoka taminy izy."
		},
		"38": {
			"1": "<sup>1</sup> Salamo nataon’i Davida. Ho fampahatsiarovana. JEHOVAH ô, aza ny fahatezeranao no ananaranao ahy; Ary aza ny fahaviniranao no amaizanao ahy.",
			"2": "<sup>2</sup> Fa mitsatoka amiko ny zana-tsipìkanao, Ary manindry ahy mafy ny tànanao.",
			"3": "<sup>3</sup> Tsy misy fahasalamana ny nofoko noho ny fahatezeranao; Tsy misy tsy marary ny taolako noho ny fahotako.",
			"4": "<sup>4</sup> Fa ny heloko efa mihoatra ny lohako; Tahaka ny entana mavesatra izy ka tsy zakako.",
			"5": "<sup>5</sup> Maimbo sady mihady ny feriko Noho ny fahadalako.",
			"6": "<sup>6</sup> Mitanondrika sy mijoretra indrindra aho Ary misaona mandrakariva.",
			"7": "<sup>7</sup> Ary ny vaniako henika ny aretina mahamay, Ka tsy misy fahasalamana ny nofoko.",
			"8": "<sup>8</sup> Marikoditra aho sady torovana indrindra Midradradradra aho noho ny fitolokon’ny foko.",
			"9": "<sup>9</sup> Ry Tompo ô, eo anatrehanao ny faniriako rehetra; Ary ny fisentoako tsy mba miafina aminao.",
			"10": "<sup>10</sup> Miemponempona ny foko, lany ny heriko; Ary ny fahiratan’ny masoko aza efa nandao ahy.",
			"11": "<sup>11</sup> Ny olo-malalako sy ny sakaizako aza efa manalavitra ny fahoriako; Ary ny havako mijanona eny lavitra eny.",
			"12": "<sup>12</sup> Mamela-pandrika izay mitady ny aiko; Ary izay mitady hanimba ahy miteny fandozana Sy misaim-pitaka mandrakariva.",
			"13": "<sup>13</sup> Fa izaho dia tahaka ny marenina ka tsy mandre, Ary tahaka ny moana ka tsy mahaloa-bava;",
			"14": "<sup>14</sup> Eny, dia tahaka ny olona tsy mandre, Sady tsy misy teny havaly eo am-bavany.",
			"15": "<sup>15</sup> Fa Hianao no antenaiko, Jehovah ô; Hianao no hamaly, ry Tompo Andriamanitro ô.",
			"16": "<sup>16</sup> Fa hoy izaho: Sao dia hifalian’izy ireo anie aho; Ary raha mibolasitra ny tongotro, dia hirehareha amiko izy.",
			"17": "<sup>17</sup> Fa efa ho lavo aho, Ary ny fanaintainako dia tsaroako mandrakariva.",
			"18": "<sup>18</sup> Fa ny heloko no hambarako; Matahotra aho noho ny fahotako.",
			"19": "<sup>19</sup> Fa ny fahavaloko velona ihany sady mahery; Ary maro no mandrafy ahy tsy ahoan-tsy ahoana;",
			"20": "<sup>20</sup> Ary mamaly ratsy ny soa izy ireo Ka mandrafy ahy ho valin’ny fanarahako ny tsara.",
			"21": "<sup>21</sup> Aza mahafoy ahy, Jehovah ô; Ry Andriamanitro ô, aza manalavitra ahy.",
			"22": "<sup>22</sup> Faingàna hamonjy ahy, Ry Tompo, Mpamonjy ahy, ô."
		},
		"39": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Ho an’i Jedotona. Salamo nataon’i Davida. HOY izaho: Hitandrina ny làlako aho ka tsy hanota amin’ny lelako; Hasiako fehim-bava ny vavako, Raha mbola eo anatrehako ny ratsy fanahy.",
			"2": "<sup>2</sup> Nangina mihitsy aho, Namihim-bava aho ka tsy nilaza na dia ny tsara aza, Dia nihetsika mafy ny fanaintainan’ny aretiko.",
			"3": "<sup>3</sup> Nafana ny foko tato anatiko; Raha nieritreritra aho, dia nisy afo nirehitra, Ka niteny tamin’ny lelako aho.",
			"4": "<sup>4</sup> Jehovah ô, ampahafantaro ny farako aho Sy izay ohatry ny androko; Aoka ho fantatro fa olon-kandalo aho.",
			"5": "<sup>5</sup> Indro, efa nataonao vodivoam-pelatanana ny androko; Ary ny fiainako toy ny tsinontsinona eo anatrehanao; Zava-poana ihany tokoa ny olona rehetra, na dia ny matanjaka aza. [Sela.]",
			"6": "<sup>6</sup> Aloka tokoa no fandehandehan’ny olona; Zava-poana tokoa no itabatabany; Mihary harena izy, nefa tsy fantany izay ho tompon’izany.",
			"7": "<sup>7</sup> Ary ankehitriny, inona no andrasako, Tompo ô? Hianao no antenaiko.",
			"8": "<sup>8</sup> Manafaha ahy amin’ny fahadisoako rehetra; Aza manao ahy ho fanakòran’ny adala.",
			"9": "<sup>9</sup> Mangina aho ka tsy miloa-bava; Fa Hianao no nanao izao.",
			"10": "<sup>10</sup> Aoka hitsahatra ny fikapohanao ahy; Reraka aho azon’ny famelin’ny tànanao.",
			"11": "<sup>11</sup> Anatra noho ny fahadisoany no ananaranao ny olona, Ka manimba ny hatsaran-tarehiny tahaka ny kalalao Hianao; Zava-poana tokoa ny olona rehetra. [Sela.]",
			"12": "<sup>12</sup> Mandrenesa ny fivavako, Jehovah ô; Ary mihainoa ny fitarainako; Aza mangina amin’ny ranomasoko; Fa vahiny aminao aho Sy mpivahiny tahaka ny razako rehetra.",
			"13": "<sup>13</sup> Aza atrehinao aho aloha, dia ho miramirana indray, Dieny aho tsy mbola lasa ka tsy ho eto intsony."
		},
		"40": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Salamo nataon’i Davida. NIANDRY dia niandry an’i Jehovah aho; Ary nitodika nitsinjo ahy Izy ka nandre ny fitarainako.",
			"2": "<sup>2</sup> Dia nampakatra ahy avy tao amin’ny lavaka fandringanana Izy, eny, tamin’ny fotaka mandrevo; Ary nampitoetra ny tongotro teo ambony vatolampy Izy ka nampijoro ahy.",
			"3": "<sup>3</sup> Ary nanisy fihiram-baovao teo am-bavako Izy, dia fiderana an’Andriamanitsika; Maro no hahita sy hatahotra ary hatoky an’i Jehovah.",
			"4": "<sup>4</sup> Sambatra izay olona manao an’i Jehovah ho tokiny Ka tsy mivily ho naman’ny mpiavonavona na ny mpivadika ho amin’ny lainga.",
			"5": "<sup>5</sup> Nohamaroinao ny fahagaganao sy ny hevitrao izay nataonao ho anay, Jehovah Andriamanitro ô– Tsy misy azo ampitahaina aminao– Raha tiako hambara sy holazaina ireny, dia, indro, maro loatra izy ka tsy tambo isaina.",
			"6": "<sup>6</sup> Ny fanatitra alatsa-dra sy ny fanatitra hohanina tsy mba sitrakao– Ny sofiko nataonao malady– Ny fanatitra dorana sy ny fanatitra noho ny ota tsy mba nilainao;",
			"7": "<sup>7</sup> Dia hoy Izaho: Inty Aho tonga– Ao amin’ny horonam-boky no nanoratana Ahy–",
			"8": "<sup>8</sup> Ny hanao ny sitraponao, Andriamanitra ô, no sitrako; Ary ny lalànao no ato anatiko.",
			"9": "<sup>9</sup> Efa nilaza teny mahafaly ny amin’ny fahamarinana teo amin’ny fiangonana lehibe Aho; Indro, ny molotro tsy mba nakomboko; Jehovah ô, Hianao no mahalala.",
			"10": "<sup>10</sup> Ny fahamarinanao tsy mba nafeniko tato am-poko; Ny fahamarinanao sy ny famonjenao no nambarako; Tsy nanafina ny famindramponao sy ny fahamarinanao tamin’ny fiangonana lehibe Aho.",
			"11": "<sup>11</sup> Hianao, Jehovah ô, aza miaro ny antranao tsy ho amiko; Aoka ny famindramponao sy ny fahamarinanao hiaro ahy mandrakariva.",
			"12": "<sup>12</sup> Fa nanodidina ahy ny fahoriana tsy tambo isaina; Nahazo ahy ny heloko, ka tsy mahay mijery aho; Maro noho ny volon-dohako ireny, Ka reraka ny foko.",
			"13": "<sup>13</sup> Aoko ho sitraky ny fonao, Jehovah ô, ny hanafaka ahy, Jehovah ô, faingàna hamonjy ahy.",
			"14": "<sup>14</sup> Aoka ho indray menatra sy mangaihay izay mitady hanimba ny aiko; Aoka hiamboho sy ho menatra izay mankasitraka ahy ho ory.",
			"15": "<sup>15</sup> Aoka ho ankona noho ny fahamenarany Ireny manao amiko hoe: Hia! sakoa izay!",
			"16": "<sup>16</sup> Aoka ho faly sy ho ravoravo aminao izay rehetra mitady Anao; Aoka izay rehetra tia ny famonjenao hanao mandrakariva hoe: Hankalazaina anie Jehovah.",
			"17": "<sup>17</sup> Fa na dia ory sy malahelo aza aho, ny Tompo no mihevitra ahy; Mpamonjy sy Mpanafaka ahy Hianao; Aza ela, Andriamanitro ô."
		},
		"41": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Salamo nataon’i Davida. SAMBATRA izay mahatsiaro ny malahelo; Jehovah hamonjy azy amin’ny andro fahoriana.",
			"2": "<sup>2</sup> Jehovah hitandrina azy sy hiaro azy tsy ho faty; Hatao ho sambatra ambonin’ny tany izy, Ary tsy mba hatolotrao amin’ny sitrapon’ny fahavalony.",
			"3": "<sup>3</sup> Jehovah hanohana azy eo ambonin’ny farafara itsaboana azy; Ny fandriany rehetra eo am-paharariany dia hovanao.",
			"4": "<sup>4</sup> Hoy izaho: Jehovah ô, mamindrà fo amiko; Sitrano ny fanahiko, fa efa nanota taminao aho.",
			"5": "<sup>5</sup> Ny fahavaloko maniry ahy ho simba ka manao hoe: Rahoviana no ho faty izy, ka ho very ny anarany?",
			"6": "<sup>6</sup> Ary raha avy mamangy izy, dia miteny zava-poana; Ny fony mamory faharatsiana ho an’ny tenany; Mivoaka eny ivelany izy ka milazalaza.",
			"7": "<sup>7</sup> Mitakoritsika ahy izay rehetra mankahala ahy; Misaina hanisy ratsy ahy izy.",
			"8": "<sup>8</sup> Tena zava-dratsy, hoy izy, no aidina aminy; Ary raha mandry izy, dia tsy hitsangana intsony.",
			"9": "<sup>9</sup> Eny, ny sakaizako, izay nahatoky ahy sady nihinana ny haniko, No nanangana ny ombelahin-tongony hamely ahy.",
			"10": "<sup>10</sup> Fa Hianao kosa, Jehovah ô, mamindrà fo amiko, ka atsangano aho, Mba hamaliako azy.",
			"11": "<sup>11</sup> Izao no hahafantarako fa mankasitraka ahy Hianao, Dia ny tsy anakoran’ny fahavaloko ahy.",
			"12": "<sup>12</sup> Ary ny amiko, dia manohana ahy amin’ny fahitsiako Hianao Ka mampitoetra ahy eo anatrehanao mandrakizay.",
			"13": "<sup>13</sup> Isaorana anie Jehovah, Andriamanitry ny Isiraely, Hatramin’ny taloha indrindra ka mandrakizay. Amena dia Amena."
		},
		"42": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Maskila. Nataon’ny Koraïta. TAHAKA ny diera mihanahana maniry ny rano an-ony No anirian’ny fanahiko Anao, Andriamanitra ô.",
			"2": "<sup>2</sup> Mangetaheta an’Andriamanitra ny fanahiko, dia Andriamanitra velona; Rahoviana no ho tonga aho ka hiseho eo anatrehan’Andriamanitra?",
			"3": "<sup>3</sup> Ny ranomasoko no fiahiko andro aman-alina, Amin’ny anaovany ahy mandrakariva hoe: Aiza izay Andriamanitrao?",
			"4": "<sup>4</sup> Izao zavatra izao no hotsarovako, ka haidiko ato anatiko ny fanahiko, Dia ny nandrosoako niaraka tamin’ny maro, sy ny nitarihako azy ho any an-tranon’Andriamanitra, Nanao feo mihoby sy midera, dia olona maro izay nitandrina ny andro firavoravoana.",
			"5": "<sup>5</sup> Nahoana no mitanondrika hianao, ry fanahiko, ka mitoloko ato anatiko? Manantenà an’Andriamanitra hianao, fa mbola hidera Azy ihany aho Noho ny famonjen’ny tavany.",
			"6": "<sup>6</sup> Andriamanitro ô, mitanondrika ato anatiko ny fanahiko; Izany no ahatsiarovako Anao eo amin’ny tanin’i Jordana Sy amin’ireo Hermona ary amin’ny tendrombohitra Mizara.",
			"7": "<sup>7</sup> Ny lalina miantso ny lalina noho ny firohondrohon’ny riananao; Ny onjanao rehetra sy ny alon-dranonao efa nanafotra ahy.",
			"8": "<sup>8</sup> Jehovah handidy ny famindrampony nony andro, Ary ho amiko nony alina ny fihirana Azy, dia vavaka amin’Andriamanitry ny aiko.",
			"9": "<sup>9</sup> Hoy izaho amin’Andriamanitra Vatolampiko: Nahoana no nanadino ahy Hianao? Nahoana no mandeha mijoretra aho noho ny fampahorian’ny fahavaloko?",
			"10": "<sup>10</sup> Toy ny zavatra mahatorotoro ny taolako no fandàtsan’ny fahavaloko ahy, Ka hoy izy isan-andro: Aiza izay Andriamanitrao?",
			"11": "<sup>11</sup> Nahoana no mitanondrika hianao, ry fanahiko? ary nahoana no mitoloko ato anatiko hianao? Manantenà an’Andriamanitra, fa mbola hidera Azy ihany aho, Izy no famonjena ny tavako sady Andriamanitro."
		},
		"43": {
			"1": "<sup>1</sup> OMEO rariny aho, Andriamanitra ô, Ary alaharo ny teniko haharesy ny firenena tsy misy famindrampo; Vonjeo aho ho afaka amin’ny lehilahy mpamitaka sy mpanao meloka.",
			"2": "<sup>2</sup> Fa Hianao no Andriamanitry ny heriko; Nahoana no manary ahy Hianao? Nahoana no mandeha mijoretra aho noho ny fampahorian’ny fahavaloko?",
			"3": "<sup>3</sup> Ampandehano ny fahazavanao sy ny fahamarinanao; Aoka hitarika ahy ireo ka hitondra ahy ho any an-tendrombohitrao masina, dia ho any amin’ny tabernakelinao.",
			"4": "<sup>4</sup> Dia hankany amin’ny alitaran’Andriamanitra aho, Dia any amin’Andriamanitry ny fifaliako indrindra; Ary hidera Anao amin’ny lokanga aho, ry Andriamanitra ô, dia Andriamanitro.",
			"5": "<sup>5</sup> Nahoana no mitanondrika hianao, ry fanahiko? ary nahoana no mitoloko ato anatiko hianao? Manantenà an’Andriamanitra, fa mbola hidera Azy ihany aho. Izy no famonjena ny tavako sady Andriamanitro."
		},
		"44": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Nataon’ny Koraïta. Maskila. NY sofinay no nandrenesanay, Andriamanitra ô, Ny razanay nilaza taminay Ny asa nataonao tamin’ny androny, dia tamin’ny andro fahiny.",
			"2": "<sup>2</sup> Ny tànanao no nandroahanao ny firenen-tsamy hafa, fa izy kosa nampitoerinao; Efa nampahorinao ny firenena maro, fa izy kosa nataonao mahafeno tany.",
			"3": "<sup>3</sup> Fa tsy ny sabany no nahazoany ny tany, Ary tsy ny sandriny no namonjy azy; Fa ny tànanao ankavanana sy ny sandrinao ary ny fahazavan’ny tavanao, satria efa sitrakao izy.",
			"4": "<sup>4</sup> Hianao dia Hianao no Mpanjakako, Andriamanitra ô; Mandidia famonjena ho an’i Jakoba.",
			"5": "<sup>5</sup> Hianao no hanosehanay ny fahavalonay; Ny anaranao no hanitsahanay izay mitsangana hamely anay.",
			"6": "<sup>6</sup> Fa tsy ny tsipìkako no itokiako, Ary ny sabatro tsy hahavonjy ahy.",
			"7": "<sup>7</sup> Fa namonjy anay tamin’ny fahavalonay Hianao, Ary ny nankahala anay dia nataonao menatra.",
			"8": "<sup>8</sup> Andriamanitra no ankalazainay mandrakariva; Ary ny anaranao no hoderainay mandrakizay. [Sela.]",
			"9": "<sup>9</sup> Fa efa nanary anay Hianao ka nampahamenatra anay, Ary tsy mba mivoaka miaraka amin’ny antokon’ny miaramilanay Hianao.",
			"10": "<sup>10</sup> Ataonao miamboho ny fahavalonay izahay, Koa ny mankahala anay dia nanaram-po namabo.",
			"11": "<sup>11</sup> Atolotrao tahaka ny ondry hohanina izahay, Ary aelinao any amin’ny firenen-tsamy hafa.",
			"12": "<sup>12</sup> Mivarotra ny olonao tsy vola tsy harena Hianao, Ka tsy mahazo tombony amin’ny vidiny.",
			"13": "<sup>13</sup> Ataonao fandàtsan’ny olona miray monina aminay izahay, Dia fanakora sy faneson’izay manodidina anay.",
			"14": "<sup>14</sup> Ataonao ho ambentinteny amin’ny firenen-tsamy hafa izahay, Dia fihifikifihan-doha amin’ny olona maro.",
			"15": "<sup>15</sup> Tsaroako mandrakariva ny fahafaham-boninahitro, Ary efa manafotra ahy ny fangaihaizako,",
			"16": "<sup>16</sup> Noho ny feon’ny mpanaratsy sy ny mpanamavo, Noho ny fahavalo sy ny mpamaly ratsy.",
			"17": "<sup>17</sup> Izany rehetra izany no manjo anay, nefa tsy manadino Anao izahay Ary tsy mivadika ny fanekenao.",
			"18": "<sup>18</sup> Tsy mihemotra ny fonay, Ary tsy miala amin’ny làlanao ny dianay,",
			"19": "<sup>19</sup> Nefa Hianao nanorotoro anay teo amin’ny fitoeran’ny amboadia Sy nanarona anay tamin’ny aloky ny fahafatesana.",
			"20": "<sup>20</sup> Raha nanadino ny anaran’Andriamanitray izahay, Na nananty tànana tamin’ny andriamani-kafa,",
			"21": "<sup>21</sup> Tsy handinika izany va Andriamanitra? Fa Izy mahalala ny miafina ao am-po.",
			"22": "<sup>22</sup> Fa noho ny aminao no amonoana anay mandrakariva; Toy ny ondry hovonoina no fijery anay.",
			"23": "<sup>23</sup> Mifohaza, ry Tompo ô, nahoana no matory Hianao? Mitsangàna, aza manary mandrakizay.",
			"24": "<sup>24</sup> Nahoana no manafina ny tavanao Hianao Ka manadino ny fahantranay sy ny fahorianay?",
			"25": "<sup>25</sup> Fa miankohoka amin’ny vovoka ny fanahinay, Miraikitra amin’ny tany ny kibonay.",
			"26": "<sup>26</sup> Mitsangàna hamonjy anay, Ary avoty izahay araka ny famindramponao."
		},
		"45": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Al-shoshanim. Nataon’ny Koraïta. Maskila. Tonon-kiram-pitiavana. MAMPIBOIBOIKA teny tsara ny foko; Miteny aho, ka ny hilaza ny mpanjaka no teny ataoko; Fanoratan’ny faingan-tsoratra ny lelako.",
			"2": "<sup>2</sup> Tsara tarehy noho ny zanak’olombelona hianao; Fahasoavana no naidina tamin’ny molotrao; Izany no nitahian’Andriamanitra anao mandrakizay.",
			"3": "<sup>3</sup> Fehezo eo am-balahanao ny sabatrao, ry ilay mahery, Dia ny fiandriananao sy ny voninahitrao.",
			"4": "<sup>4</sup> Ary mitaingena handroso soa aman-tsara amin’ny voninahitrao, Noho ny teny marina sy ny fahalemem-panahim-pahamarinana; Ary hampianatra anao zava-mahatsiravina ny tànanao ankavanana.",
			"5": "<sup>5</sup> Ny zana-tsipìkanao dia voaranitra– Firenena maro no ringana ka hitsakitsahinao– Ka mitsatoka ao am-pon’ny fahavalon’ny mpanjaka.",
			"6": "<sup>6</sup> Ny seza fiandriananao, Andriamanitra ô, dia mandrakizay doria; Tehim-pahamarinana ny tehim-panjakanao.",
			"7": "<sup>7</sup> Tianao ny fahamarinana, fa halanao ny heloka; Koa Andriamanitra, dia Andriamanitrao, efa nanoso-diloilo fifaliana anao Mihoatra noho ny namanao.",
			"8": "<sup>8</sup> Miora sy hazo manitra ary kasia ny fitafianao rehetra, Zava-maneno avy ao amin’ny lapa ivory no mampifaly anao.",
			"9": "<sup>9</sup> Zanakavavin’andriamanjaka no isan’ny malalanao; Eo ankavananao no itoeran’ny vadin’ny mpanjaka Miravaka volamena avy any Ofira.",
			"10": "<sup>10</sup> Henoy, razazavavy, dia jereo, ka atongilano ny sofinao, Ary hadinoy ny firenenao sy ny tranon-drainao;",
			"11": "<sup>11</sup> Raha irin’ny mpanjaka ny hatsaran-tarehinao– Fa izy no tomponao–dia miankohofa eo anatrehany.",
			"12": "<sup>12</sup> Ary ny zanakavavin’i Tyro hitondra fanatitra; Eny, ny manan-karena amin’ny vahoaka hila sitraka aminao.",
			"13": "<sup>13</sup> Be voninahitra ny zanakavavin’ny mpanjaka ao anaty trano; Tenona misy volamena no fitafiany.",
			"14": "<sup>14</sup> Mitafy lamba misoratsoratra izy ka entina ho ao amin’ny mpanjaka; Ny zazavavy sakaizany izay manaraka azy dia entina ao aminao.",
			"15": "<sup>15</sup> Entina amin’ny fifaliana sy ny firavoravoana ireny; Hiditra ao an-dapan’ny mpanjaka izy.",
			"16": "<sup>16</sup> Ho solon-drainao ny zanakao, Hotendrenao ho mpanapaka eny amin’ny tany rehetra izy.",
			"17": "<sup>17</sup> Hampahatsiaro ny anaranao hatramin’ny taranaka fara mandimby aho. Izany rehetra izany no hideran’ny firenena anao mandrakizay doria."
		},
		"46": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Nataon’ny Koraïta. Al-alamoth. Tonon-kira. ANDRIAMANITRA no aro sy hery ho antsika; Mpamonjy mora azo indrindra amin’ny fahoriana Izy.",
			"2": "<sup>2</sup> Koa izany no tsy hatahorantsika, na dia ho levona aza ny tany, Na mifindra ho ao anatin’ny ranomasina aza ny tendrombohitra,",
			"3": "<sup>3</sup> Na mirohondrohona sy misamboaravoara aza ny onjany, Ka mihorohoro ny tendrombohitra noho ny fisafoakany. [Sela.]",
			"4": "<sup>4</sup> Misy ony, koa ny sampany dia mampifaly ny tanànan’Andriamanitra, Fonena-masin’ny Avo Indrindra.",
			"5": "<sup>5</sup> Andriamanitra no eo afovoany, ka tsy mba hihetsika izy; Andriamanitra no hamonjy azy, rehefa madiva ho maraina ny andro.",
			"6": "<sup>6</sup> Nitabataba ny firenen-tsamy hafa, nikotrankotrana ny fanjakana maro; Nanonona ny feony Izy, ka niempo ny tany.",
			"7": "<sup>7</sup> Jehovah, Tompon’ny maro, no momba antsika; Andriamanitr’i Jakoba no fiarovana avo ho antsika. [Sela.]",
			"8": "<sup>8</sup> Avia, jereo ny asan’i Jehovah, Izay mahatonga faharavana etỳ an-tany.",
			"9": "<sup>9</sup> Mampitsahatra ady hatramin’ny faran’ny tany Izy; Manapaka ny tsipìka sy manito ny lefona Izy Ary mandoro ny sariety amin’ny afo.",
			"10": "<sup>10</sup> Mitsahara, ka aoka ho fantatrareo fa Izaho no Andriamanitra; Hisandratra amin’ny firenen-tsamy hafa Aho, hisandratra amin’ny tany Aho.",
			"11": "<sup>11</sup> Jehovah, Tompon’ny maro, no momba antsika; Andriamanitr’i Jakoba no aro ho antsika. [Sela.]"
		},
		"47": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Nataon’ny Koraïta. Salamo. MITEHAFA tànana, ry firenena rehetra; Mihobia ho an’Andriamanitra.",
			"2": "<sup>2</sup> Fa Jehovah no avo indrindra ka tokony hatahorana; Mpanjaka lehibe ambonin’ny tany rehetra Izy.",
			"3": "<sup>3</sup> Mandresy firenena maro Izy ho ambanintsika Ary firenen-tsamy hafa ho ambanin’ny tongotsika.",
			"4": "<sup>4</sup> Mifidy ny lovantsika ho antsika Izy, Dia ny voninahitr’i Jakoba izay tiany. [Sela.]",
			"5": "<sup>5</sup> Efa niakatra tamin’ny fihobiana Andriamanitra, Jehovah niakatra tamin’ny fitsofana ny anjomara.",
			"6": "<sup>6</sup> Mankalazà an’Andriamanitra, eny, mankalazà; Mankalazà ny Mpanjakantsika, eny, mankalazà.",
			"7": "<sup>7</sup> Fa Mpanjaka amin’ny tany rehetra Andriamanitra; Mahaiza mankalaza tsara hianareo.",
			"8": "<sup>8</sup> Andriamanitra manjaka amin’ny firenen-tsamy hafa; Andriamanitra mipetraka eo ambonin’ny seza fiandrianany masina.",
			"9": "<sup>9</sup> Ny mpanapaka ny firenen-tsamy hafa miangona Ho olon’Andriamanitr’i Abrahama; Fa an’Andriamanitra ny ampingan’ny tany; Avo indrindra Izy."
		},
		"48": {
			"1": "<sup>1</sup> Tonon-kira. Salamo nataon’ny Koraïta. LEHIBE Jehovah ka mendrika hoderaina indrindra, Ao an-tanànan’Andriamanitsika, ao an-tendrombohiny masina.",
			"2": "<sup>2</sup> Avo mahafinaritra, fifalian’ny tany rehetra, ny tendrombohitra Ziona, Eo amin’ny ilany avaratra, Tanànan’ny Mpanjaka lehibe.",
			"3": "<sup>3</sup> Andriamanitra ao an-tranon’andriana ao Dia nanao izay hahafantarana Azy ho fiarovana avo.",
			"4": "<sup>4</sup> Fa he! niangona ny mpanjaka maro, Niara-nandroso avokoa izy;",
			"5": "<sup>5</sup> Nijery izy ka talanjona terỳ; Very hevitra izy ka nalaky nandositra;",
			"6": "<sup>6</sup> Hovitra no nahazo azy teo, Dia tahaka ny fanaintainan’ny vehivavy raha miteraka.",
			"7": "<sup>7</sup> Amin’ny rivotra avy any atsinanana No namakianao ny sambon’i Tarsisy.",
			"8": "<sup>8</sup> Araka izay efa rentsika no hitantsika Tao an-tanànan’i Jehovah, Tompon’ny maro, tao an-tanànan’Andriamanitsika. Andriamanitra mampitoetra azy mandrakizay. [Sela.]",
			"9": "<sup>9</sup> Efa nieritreritra ny famindramponao izahay, Andriamanitra ô, Tao anatin’ny tempolinao.",
			"10": "<sup>10</sup> Araka ny anaranao, Andriamanitra ô, no fiderana Anao Hatramin’ny faran’ny tany; Feno fahamarinana ny tànanao ankavanana.",
			"11": "<sup>11</sup> Aoka hifaly ny tendrombohitra Ziona, Aoka ho ravoravo ny zanakavavin’i Joda, Noho ny fitsaranao.",
			"12": "<sup>12</sup> Manodidìna an’i Ziona hianareo, eny, mandehana manodidina azy; Isao ny tilikambony,",
			"13": "<sup>13</sup> Hevero tsara ny màndany, Diniho ny tranon’andrianany, Mba hambaranareo amin’ny taranaka mandimby.",
			"14": "<sup>14</sup> Fa izany no Andriamanitra, dia Andriamanitsika mandrakizay doria; Izy no hitarika antsika na dia hatramin’ny fahafatesana aza."
		},
		"49": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Nataon’ny Koraïta. Salamo. MANDRENESA izao, ry firenena rehetra; Mihainoa, ry mponina rehetra amin’izao tontolo izao:",
			"2": "<sup>2</sup> Na ny ambany, na ny ambony, Na ny manan-karena, na ny malahelo.",
			"3": "<sup>3</sup> Ny vavako hiteny fahendrena; Ary ny fieritreretan’ny foko dia fahalalana.",
			"4": "<sup>4</sup> Hanongilana ny sofiko hihaino oha-teny aho, Ary hanonona teny saro-pantarina ampiarahiko amin-dokanga.",
			"5": "<sup>5</sup> Nahoana no hatahotra amin’ny andro fahoriana aho, Nony manodidina ahy ny haratsian’ny mpamingana ahy,",
			"6": "<sup>6</sup> Dia izay matoky ny hareny Sy mirehareha amin’ny haben’ny fananany?",
			"7": "<sup>7</sup> Tsy misy mahavotra ny rahalahiny akory, Na mahazo manome an’Andriamanitra izay avony",
			"8": "<sup>8</sup> (Fa sarotra avotra ny ainy, Ka tsy azo atao mandrakizay),",
			"9": "<sup>9</sup> Mba ho velona mandrakizay izy Ka tsy ho latsaka ao amin’ny lavaka.",
			"10": "<sup>10</sup> Fa hita fa maty ihany ny olon-kendry; Ary ny adala sy ny ketrina samy levona Ka mamela ny fananany ho an’ny hafa.",
			"11": "<sup>11</sup> Araka ny heviny, dia ataony fa ho mandrakizay ny tranony, Ary ny fonenany hahatratra ny taranaka fara mandimby; Araka ny anarany no anononany ny taniny.",
			"12": "<sup>12</sup> Nefa na dia manana voninahitra aza ny olona, dia tsy maharitra izy; Tahaka ny biby izay samy tsy maintsy ho faty izy.",
			"13": "<sup>13</sup> Izany no falehan’ny olona adala; Ary ny taranany mandimby azy mankasitraka ny teniny. [Sela.]",
			"14": "<sup>14</sup> Tahaka ny fanangona ondry no fanangona azy any amin’ny fiainan-tsi-hita; Ny fahafatesana no mpiandry azy; Ary hanapaka azy ny mahitsy nony maraina; Ary ny bikany ho levona any amin’ny fiainan-tsi-hita, ka tsy hisy fonenany.",
			"15": "<sup>15</sup> Fa Andriamanitra kosa hamonjy ny aiko amin’ny herin’ny fiainan-tsi-hita, Satria handray ahy Izy. [Sela.]",
			"16": "<sup>16</sup> Aza matahotra hianao, raha misy olona tonga manan-karena, Ka mitombo ny voninahitry ny tranony;",
			"17": "<sup>17</sup> Fa tsy hitondra na inona na inona izy, raha maty; Tsy mba hidina hanaraka azy ny voninahiny.",
			"18": "<sup>18</sup> Na dia miarahaba tena aza izy, raha mbola velona– Fa hidera anao ny olona, raha manisy soa ny tenanao hianao–",
			"19": "<sup>19</sup> Dia hankany amin’ny razany izy, Tsy hahita ny mazava intsony mandrakizay ireny.",
			"20": "<sup>20</sup> Izay olona manana voninahitra, nefa tsy manan-tsaina, Dia tahaka ny biby izay samy tsy maintsy ho faty."
		},
		"50": {
			"1": "<sup>1</sup> Salamo nataon’i Asafa. ANDRIAMANITRA Andriananahary, dia Jehovah, no miteny Ka miantso ny tany hatramin’ny fiposahan’ny masoandro ka hatramin’ny filentehany.",
			"2": "<sup>2</sup> Avy ao Ziona, izay tena fahatsaran-tarehy, no amirapiratan’Andriamanitra.",
			"3": "<sup>3</sup> Avy Andriamanitsika ka tsy hangina; Afo no mandevona eo alohany, Ary tafio-drivotra mahery no manodidina Azy.",
			"4": "<sup>4</sup> Miantso ny lanitra any ambony Izy, Sy ny tany koa, mba hitsarany ny olony:",
			"5": "<sup>5</sup> Angony etỳ amiko ny oloko masina Izay manao fanekena amiko amin’ny fanatitra.",
			"6": "<sup>6</sup> Hasehon’ny lanitra ny fahamarinany; Fa Andriamanitra no Mpitsara. [Sela.]",
			"7": "<sup>7</sup> Mihainoa, ry oloko, fa hiteny Aho; Ry Isiraely ô, fa hanambara aminao Aho; Izaho no Andriamanitra, dia Andriamanitrao.",
			"8": "<sup>8</sup> Tsy ny fanatitrao no ananarako anao, Ary efa eo anatrehako mandrakariva ny fanatitra doranao.",
			"9": "<sup>9</sup> Tsy maka vantotr’ombilahy amin’ny tranonao Aho, Na osilahy amin’ny valanao;",
			"10": "<sup>10</sup> fa Ahy ny biby rehetra any an-ala, Sy ny biby fiompy arivoarivo eny an-tendrombohitra.",
			"11": "<sup>11</sup> Fantatro ny vorona rehetra eny an-tendrombohitra; Ary eto amiko ny zava-mihetsiketsika eny an-tsaha.",
			"12": "<sup>12</sup> Raha mba noana Aho, dia tsy hilaza aminao; Fa Ahy izao tontolo izao sy izay rehetra eo aminy.",
			"13": "<sup>13</sup> Hihinana ny nofon’ombilahy va Aho. Na hisotro ny ran osilahy?",
			"14": "<sup>14</sup> Manatera fanati-pisaorana ho an’Andriamanitra; Ary efao amin’ny Avo Indrindra ny voadinao;",
			"15": "<sup>15</sup> Antsoy Aho amin’ny andro fahoriana; Dia hamonjy anao Aho, ka hanome voninahitra Ahy hianao.",
			"16": "<sup>16</sup> Fa amin’ny ratsy fanahy kosa dia hoy Andriamanitra; Ahoanao no itorianao ny lalàko Sy itondranao ny fanekeko eo am-bavanao?",
			"17": "<sup>17</sup> Fa hianao mankahala fananarana, Ary arianao eo ivohonao ny teniko.",
			"18": "<sup>18</sup> Raha mahita mpangalatra hianao, dia faly hikambana aminy, Ary miray anjara amin’ny mpijangajanga.",
			"19": "<sup>19</sup> Ny vavanao alefanao hiteny ratsy, Ary ny lelanao mamorom-pitaka.",
			"20": "<sup>20</sup> Mipetraka miteny ratsy ny rahalahinao hianao Sady manendrikendrika ny zanakalahin-dreninao.",
			"21": "<sup>21</sup> Izany no nataonao, nefa nangina ihany Aho, Ka dia nataonao fa mitovy aminao ihany Aho! Hananatra anao Aho ka handahatra izany eo imasonao.",
			"22": "<sup>22</sup> Mihevera izany ankehitriny, hianareo izay manadino an’Andriamanitra, Fandrao hamiravira Aho, ka tsy hisy hamonjy.",
			"23": "<sup>23</sup> Izay manatitra fanati-pisaorana no manome voninahitra Ahy; Ary izay mitandrina ny làlany no hanehoako ny famonjen’Andriamanitra."
		},
		"51": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Salamo nataon’i Davida, fony Natana mpaminany nankao aminy, rehefa naka an’i Batseba izy. MAMINDRÀ fo amiko, Andriamanitra ô, araka ny fahasoavanao; Araka ny haben’ny fiantranao vonoy ny fahadisoako.",
			"2": "<sup>2</sup> Sasao madio aho mba ho afa-keloka, Ary diovy aho mba ho afa-pahotana.",
			"3": "<sup>3</sup> Fa tsaroako ny fahadisoako, Ary eo anatrehako mandrakariva ny fahotako.",
			"4": "<sup>4</sup> Taminao dia taminao ihany no nanotako, Ary izay ratsy eo imasoano no efa nataoko,– Mba ho marina Hianao, raha miteny, Ary tsy hanan-tsiny Hianao, raha mitsara.",
			"5": "<sup>5</sup> Indro, tamin’ny heloka no niterahana ahy, Ary tamin’ny fahotana no torontoronina ahy ny reniko.",
			"6": "<sup>6</sup> Indro, fahamarinana ao am-po no sitrakao; Ary fahendrena ao amin’ny miafina no ampahafantarinao ahy.",
			"7": "<sup>7</sup> Diovy hysopa aho ho afa-pahotana, dia hadio; Sasao aho, dia ho fotsy mangatsakatsaka.",
			"8": "<sup>8</sup> Ampandreneso fifaliana sy faharavoravoana aho, Hifalian’ny taolana izay voavakivakinao.",
			"9": "<sup>9</sup> Afeno ny tavanao ny fahotako, Ary vonoy ny heloko rehetra.",
			"10": "<sup>10</sup> Amoròny fo madio aho, Andriamanitra ô; Ary havaozy hiorina marina ny fanahiko ato anatiko.",
			"11": "<sup>11</sup> Aza manary ahy hiala eo anatrehanao; Ary aza manaisotra ny Fanahinao Masina amiko.",
			"12": "<sup>12</sup> Ampodio amiko ny fifaliana amin’ny famonjenao; Ary tohàny fanahy mazoto aho.",
			"13": "<sup>13</sup> Dia hampianatra ny olon-diso ny làlanao aho; Ary ny mpanota hampiverenina aminao.",
			"14": "<sup>14</sup> Vonjeo ho afaka amin’ny valin-dra aho, Andriamanitra ô, Andriamanitry ny famonjena ahy; Hihoby ny fahamarinanao ny vavako.",
			"15": "<sup>15</sup> Tompo ô, sokafy ny molotro, Dia hanambara ny fiderana Anao ny vavako.",
			"16": "<sup>16</sup> Fa tsy fanatitra no sitrakao; raha izany, dia nomeko ihany; Ny fanatitra dorana tsy mahafaly Anao.",
			"17": "<sup>17</sup> Ny fanatitra ho an’Andriamanitra dia fanahy torotoro; Ny fo torotoro sy mangorakoraka, Andriamanitra ô, tsy mba ataonao tsinontsinona.",
			"18": "<sup>18</sup> Manaova soa amin’i Ziona araka ny fahasoavanao; Ataovy ny màndan’i Jerosalema.",
			"19": "<sup>19</sup> Dia ho sitrakao ny fanati-pahamarinana sy ny fanatitra dorana ary ny fanatitra levonina amin’ny afo; Dia hanatitra vantotr’ombilahy eo amin’ny alitaranao ny olona."
		},
		"52": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Maskila nataon’i Davida, fony tonga Doega Edomita ka nilaza tamin’i Saoly hoe: Davida dia efa tonga ao an-tranon’i Ahimeleka. NAHOANA no mandoka tena amin’ny haratsiana hianao, ry ilay mahery? Ny famindrampon’Andriamanitra maharitra mandrakariva.",
			"2": "<sup>2</sup> Loza no heverin’ny lelanao, Tahaka ny hareza maranitra, ry mpanao fitaka.",
			"3": "<sup>3</sup> Efa tia ny ratsy noho ny tsara hianao, Ary ny lainga noho ny miteny ny marina. [Sela.]",
			"4": "<sup>4</sup> Ny teny mandripaka rehetra no tianao, ry lela mamitaka.",
			"5": "<sup>5</sup> Andriamanitra kosa hanimba anao mandrakizay; Hisambotra anao sy hanongotra anao hiala ao an-day Izy Ary hamongotra anao tsy ho eo amin’ny tanin’ny velona. [Sela.]",
			"6": "<sup>6</sup> Ny marina hijery, dia hatahotra Ka hihomehy azy hoe:",
			"7": "<sup>7</sup> Indro, ny lehilahy izay tsy nanao an’Andriamanitra ho heriny; Fa natoky ny haben’ny hareny ka nikikitra tamin’ny faharatsiany.",
			"8": "<sup>8</sup> Fa izaho dia tahaka ny hazo oliva maitso ao an-tranon’Andriamanitra. Matoky ny famindrampon’Andriamanitra mandrakizay doria aho.",
			"9": "<sup>9</sup> Hidera Anao mandrakizay aho, fa efa nataonao izany; Ary hiandry ny anaranao eo anatrehan’ny olonao masina aho, fa tsara izany."
		},
		"53": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Al-mahalath. Maskila nataon’i Davida. NY adala manao anakampo hoe: Tsy misy Andriamanitra. Manao izay ratsy sy vetaveta izy, Tsy misy manao ny tsara.",
			"2": "<sup>2</sup> Eny an-danitra Andriamanitra miondrika mijery ny zanak’olombelona, Mba hizaha na misy hendry mitady an’Andriamanitra.",
			"3": "<sup>3</sup> Samy efa nivily izy rehetra ka tonga vetaveta avokoa; Tsy misy manao ny tsara, na dia iray akory aza.",
			"4": "<sup>4</sup> Tsy mahalala va ny mpanao ratsy, Izay mihinana ny oloko toy ny fihinan-kanina, Ary Andriamanitra tsy antsoiny?",
			"5": "<sup>5</sup> Indreo, matahotra dia matahotra ireny, nefa tsy nisy tokony hatahorany; Fa Andriamanitra efa nanely ny taolan’izay nitoby hamely anao; Nampahamenatra azy hianao, satria efa narian’Andriamanitra izy.",
			"6": "<sup>6</sup> Enga anie ka hivoaka avy ao Ziona ny famonjena ny Isiraely! Raha ampodin’Andriamanitra avy amin’ny fahababoana ny olony, Dia ho faly Jakoba, ary ho ravoravo Isiraely."
		},
		"54": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Hampiarahina amin’ny valiha. Maskila nataon’i Davida, fony tonga ny Zifita ka nanao tamin’i Saoly hoe: Tsy miery any aminay va Davida? VONJEO amin’ny anaranao aho, Andriamanitra ô, Ary omeo rariny aho araka ny herinao.",
			"2": "<sup>2</sup> Andriamanitra ô, henoy ny fivavako; Mihainoa ny tenin’ny vavako.",
			"3": "<sup>3</sup> Fa olona hafa firenena no mitsangana hamely ahy, Ary olon-dozabe no mitady ny aiko; Tsy mihevitra an’Andriamanitra ho eo anatrehany izy. [Sela.]",
			"4": "<sup>4</sup> Indro, Andriamanitra no Mpamonjy ahy; Ny Tompo no Mpanohana ny fanahiko.",
			"5": "<sup>5</sup> Hamaly ny ratsy ataon’ny mpampahory ahy Izy; Aoka ny fahamarinanao no handringananao azy.",
			"6": "<sup>6</sup> Sitrapoko ihany no hanaterako fanatitra ho Anao; Hidera ny anaranao aho, Jehovah ô, fa tsara izy.",
			"7": "<sup>7</sup> Fa efa namonjy ahy ho afaka tamin’ny fahoriana rehetra Izy; Ary ny masoko dia faly mijery ny amin’ny fahavaloko."
		},
		"55": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Hampiarahina amin’ny valiha. Maskila nataon’i Davida. MIHAINOA ny fivavako, Andriamanitra ô, Ary aza miery amin’ny fitarainako.",
			"2": "<sup>2</sup> Henoy aho, ka valio; Miriorio ny eritreritro, ka mitoloko aho",
			"3": "<sup>3</sup> Noho ny feon’ny fahavalo sy ny fampahorian’ny ratsy fanahy; Fa mampianjera loza amiko ireny, Ary amin’ny fahatezerana no ankahalany ahy.",
			"4": "<sup>4</sup> Mangorohoro ato anatiko ny foko; Ary mamely ahy ny tahotra ny fahafatesana.",
			"5": "<sup>5</sup> Tahotra sy hovitra no mahazo ahy, Ary manafotra ahy ny horohoro.",
			"6": "<sup>6</sup> Ka hoy izaho: Enga anie ka manana elatra tahaka ny voromailala aho, Dia handositra aho ka hahita izay hitoerako;",
			"7": "<sup>7</sup> Eny, handositra lavitra aho Ka hitoetra any an-efitra. [Sela.]",
			"8": "<sup>8</sup> Handeha faingana aho hamonjy fialofana Mba tsy ho azon’ny tafio-drivotra sy ny ranonoram-baratra.",
			"9": "<sup>9</sup> Aringano izy, ary akoroy ny fiteniny, Tompo ô. Fa mahita loza sy ady ao an-tanàna aho.",
			"10": "<sup>10</sup> Hodidininy andro aman-alina eny ambonin’ny màndany io; Ary heloka sy fampahoriana no ao aminy;",
			"11": "<sup>11</sup> Ratsy no ao anatiny; Ary ny fitaka sy ny fanangolena tsy mba miala amin’ny làlambeny.",
			"12": "<sup>12</sup> Fa tsy fahavalo no nanaratsy ahy,– Fa raha izany, dia niaritra ihany aho; Tsy olona nankahala ahy no nanandra-tena namely ahy,– Fa raha izany, dia niery azy aho;",
			"13": "<sup>13</sup> Fa hianao, lehilahy mitovy saranga amiko, Namako sy sakaizako indrindra,",
			"14": "<sup>14</sup> Dia isika izay nifankamamy resaka Ka niara-nandeha tamin’ny maro ho ao an-tranon’Andriamanitra.",
			"15": "<sup>15</sup> Aoka hanampoka azy ny fahafatesana. Aoka hidina velona any amin’ny fiainan-tsi-hita izy; Fa ratsy no ao amin’ny fonenany, dia ao afovoany.",
			"16": "<sup>16</sup> Raha izaho, dia Andriamanitra no hantsoiko; Ary Jehovah no hamonjy ahy.",
			"17": "<sup>17</sup> Hariva sy maraina ary mitataovovonana no hitarainako sy hitolokoako; Dia hihaino ny feoko Izy,",
			"18": "<sup>18</sup> Ka hanavotra ny fanahiko ho amin’ny fiadanana hahafahako amin’ny adin’ireny amiko, Fa maro no miady amiko.",
			"19": "<sup>19</sup> Hihaino Andriamanitra, Izay mipetraka hatramin’ny taloha, [Sela.] Ka hamaly ireo olona tsy miova toetra Sady tsy matahotra an’Andriamanitra.",
			"20": "<sup>20</sup> Efa naninjitra ny tànany hamely izay nihavana taminy izy; Namadika ny fanekeny izy.",
			"21": "<sup>21</sup> Malambo noho ny rononomandry ny vavany, Kanjo ady no ao am-pony; Malemy noho ny diloilo ny teniny, Kanjo sabatra voatsoaka ireny.",
			"22": "<sup>22</sup> Apetraho amin’i Jehovah ny entanao, Ary Izy no hanohana anao; Tsy havelany hangozohozo mandrakizay ny marina.",
			"23": "<sup>23</sup> Fa Hianao, Andriamanitra ô, no hampidina ireo any an-davaka fanimbana; Ny mpandatsa-dra sy mpamitaka tsy hahatratra ny antsasaky ny androny; Fa izaho kosa dia hatoky Anao."
		},
		"56": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Al-jonath-elem-rehokim. Miktama nataon’i Davida, fony nisambotra azy tany Gata ny Filistina. MAMINDRÀ fo amiko, Andriamanitra ô, fa misafoaka manenjika ahy ny olona; Miady amiko mandrakariva izy ka mampahory ahy.",
			"2": "<sup>2</sup> Misafoaka manenjika mandrakariva ny mpampahory ahy; Maro tokoa no mirehareha miady amiko.",
			"3": "<sup>3</sup> Amin’ny andro atahorako no itokiako Anao.",
			"4": "<sup>4</sup> Andriamanitra no iderako ny teniny; Andriamanitra no itokiako, ka tsy mba hatahotra aho; Inona no hataon’ny nofo amiko?",
			"5": "<sup>5</sup> Manimba ny raharahako mandrakariva ireny; Ny heviny rehetra dia ny hanisy ratsy ahy ihany.",
			"6": "<sup>6</sup> Miangona izy, miery izy, Mitsongo-dia ahy izy, satria mikendry ny aiko.",
			"7": "<sup>7</sup> Amin’izany faharatsiany izany moa hisy hahafahany va? Aripaho amin’ny fahatezeranao ny firenena, Andriamanitra ô.",
			"8": "<sup>8</sup> Manisa ny firenireneko Hianao; Ataovy ao an-tavoahanginao ny ranomasoko; Tsy eo amin’ny bokinao va ireny?",
			"9": "<sup>9</sup> Amin’izay dia hiamboho ny fahavaloko amin’ny andro hiantsoako Anao; Izao dia fantatro, fa momba ahy Andriamanitra.",
			"10": "<sup>10</sup> Andriamanitra no iderako ny teniny; Jehovah no iderako ny teniny.",
			"11": "<sup>11</sup> Andriamanitra no itokiako; Ka tsy mba hatahotra aho; Inona no hataon’ny olona amiko?",
			"12": "<sup>12</sup> Nivoady taminao aho, Andriamanitra ô, Dia hanatitra fanati-pisaorana ho Anao.",
			"13": "<sup>13</sup> Fa efa nanafaka ny fanahiko tamin’ny fahafatesana Hianao; Eny, nanafaka ny tongotro Hianao mba tsy ho solafaka. Handehanako eo anatrehan’Andriamanitra amin’ny fahazavan’ny velona."
		},
		"57": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Al-tasheth. Miktama nataon’i Davida tamin’ny nandosirany an’i Saoly tao anaty zohy. MAMINDRÀ fo amiko, Andriamanitra ô, mamindrà fo amiko; Fa Hianao no ialofan’ny fanahiko; Eny, ao amin’ny aloky ny elatrao no ialofako, Mandra-pahalasan’ny loza.",
			"2": "<sup>2</sup> Hitaraina amin’Andriamanitra Avo Indrindra aho, Dia amin’Andriamanitra Izay mahatanteraka ho ahy.",
			"3": "<sup>3</sup> Haniraka avy any an-danitra Izy ka hamonjy ahy, Raha miteny ratsy izay misafoaka manenjika ahy; [Sela.] Eny, Andriamanitra haniraka ny famindrampony sy ny fahamarinany.",
			"4": "<sup>4</sup> Ny fanahiko dia eo afovoan’ny liona; Handry eo amin’ny masiaka aho, Dia eo amin’ny zanak’olombelona,– Lefona sy zana-tsipìka ny nifiny, ary sabatra maranitra ny lelany.",
			"5": "<sup>5</sup> Misandrata any ambonin’ny lanitra Hianao, Andriamanitra ô! Aoka ho ambonin’ny tany rehetra ny voninahitrao.",
			"6": "<sup>6</sup> Efa namela-pandrika harato haningotra ny diako izy Ary nampitanondrika ny fanahiko; Nihady lavaka teo anoloako izy, Kanjo ny tenany ihany no latsaka ao anatiny. [Sela.]",
			"7": "<sup>7</sup> Tafatoetra ny foko, Andriamanitra ô, eny, tafatoetra ny foko; Hihira sy hankalaza aho.",
			"8": "<sup>8</sup> Mifohaza, ry voninahitro; Mifohaza, ry valiha sy lokanga; Hifoha maraina koa aho.",
			"9": "<sup>9</sup> Hidera Anao eny amin’ny vahoaka aho, Tompo ô, Hankalaza Anao eny amin’ny firenena aho.",
			"10": "<sup>10</sup> Fa lehibe mihatra amin’ny lanitra ny famindramponao Ary mihatra amin’ny rahona ny fahamarinanao.",
			"11": "<sup>11</sup> Misandrata any ambonin’ny lanitra Hianao, Andriamanitra ô; Aoka ho ambonin’ny tany rehetra ny voninahitrao."
		},
		"58": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Al-tasheth. Miktama nataon’i Davida. MOA ny tsi-fitenenana va ataonareo ho filazana ny marina? Mitsara mahitsy va hianareo, ry zanak’olombelona?",
			"2": "<sup>2</sup> Tsy izany, fa ny fonareo no amoronanareo ny ratsy; Mandanja ny fandozan’ny tànanareo etỳ an-tany hianareo.",
			"3": "<sup>3</sup> Mivily hatrany am-bohoka ny ratsy fanahy; Mania hatrany an-kibo izy ka miteny lainga.",
			"4": "<sup>4</sup> Misy poizina tahaka ny an’ny menarana aminy; Eny, tahaka ny vipera marenina izay manentsin-tadìny izy",
			"5": "<sup>5</sup> Ka tsy mihaino ny feon’ny mpanao ody, Na dia izay mpahay ody indrindra aza.",
			"6": "<sup>6</sup> Andriamanitra ô, torotoroy ny nifiny eo am-bavany; Omboty ny vazan’ny liona tanora, Jehovah ô.",
			"7": "<sup>7</sup> Aoka ho ritra tahaka ny rano-trambo izy; Raha atraniny ny zana-tsipìkany, dia aoka ho tapa-doha ireny;",
			"8": "<sup>8</sup> Aoka ho tahaka ny lelosy izay mihalevona eny am-pandehanana izy; Aoka ho tahaka ny zaza-vakimanta izay tsy mahita masoandro izy.",
			"9": "<sup>9</sup> Dieny tsy mbola mafanan’ny afon-tsilo ny vilaninareo, Aoka ho entin’ny tafio-drivotra ireny, na ny lena na ny maina.",
			"10": "<sup>10</sup> Hifaly ny marina, raha mahita ny famaliana; Hanasa ny tongony amin’ny ran’ny ratsy fanahy izy;",
			"11": "<sup>11</sup> Dia hiteny ny olona ka hanao hoe: Misy valiny tokoa ho an’ny marina; Misy tokoa Andriamanitra Izay mitsara etỳ ambonin’ny tany."
		},
		"59": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Al-tasheth. Miktama nataon’i Davida, fony Saoly naniraka, ka nisy niambina ny trano hamonoana azy. AFAHO amin’ny fahavaloko aho, Andriamanitro ô; Asondroty eo amin’ny avo aho tsy ho azon’izay miodina amiko.",
			"2": "<sup>2</sup> Afaho amin’ny mpanao ratsy aho, Ary vonjeo amin’ny mpandatsa-dra.",
			"3": "<sup>3</sup> Fa, indro, manotrika hahazo ny aiko izy; Miangona hanohitra ahy ny mahery, Nefa tsy fahadisoako ary tsy fahotako, Jehovah ô.",
			"4": "<sup>4</sup> Tsy heloko no ihazakazahany sy iomanany, Koa mifohaza hitsena ahy, ka jereo.",
			"5": "<sup>5</sup> Ary Hianao, Jehovah ô, Andriamanitry ny maro, Andriamanitry ny Isiraely, Mifohaza hitsara ny firenen-tsamy hafa; Aza mamindra fo amin’izay mpivadika ratsy fanahy. [Sela.]",
			"6": "<sup>6</sup> Miverina nony hariva ireny ka mierona tahaka ny alika Sady mandehandeha eran’ny tanàna.",
			"7": "<sup>7</sup> Indro, mirezadrezatra amin’ny vavany izy; Sabatra no eo amin’ny molony, Fa iza, hono, no handre?",
			"8": "<sup>8</sup> Kanjo Hianao, Jehovah ô, hihomehy azy; Hianao haneso ny firenena rehetra.",
			"9": "<sup>9</sup> Koa noho ny herin’ireny dia hiandry Anao aho; Fa Andriamanitra no fiarovana avo ho ahy.",
			"10": "<sup>10</sup> Andriamanitra, Izay mamindra fo amiko, no hialoha ahy; Hataon’Andriamanitra faly mijery ny amin’ny fahavaloko aho.",
			"11": "<sup>11</sup> Aza mahafaty azy Hianao, fandrao hanadino izany ny fireneko; Ampirenireneo amin’ny herinao izy, ka aripaho, ry Tompo ô, Ampinganay.",
			"12": "<sup>12</sup> Ny fahotan’ny vavany dia ny tenin’ny molony; Aoka hosamborina amin’ny fireharehany izy, Eny, noho ny ozona sy ny lainga ataony.",
			"13": "<sup>13</sup> Aringano an-katezerana izy, aringano mba tsy ho ao intsony; Ary aoka ho fantany fa Andriamanitra no manapaka eo amin’i Jakoba Ka hatramin’ny faran’ny tany. [Sela.]",
			"14": "<sup>14</sup> Ary miverina nony hariva ireny ka mierona tahaka ny alika Sady mandehandeha eran’ny tanàna.",
			"15": "<sup>15</sup> Miriorio mitady hanina izy; Raha tsy voky izy, dia any ihany mandritra ny alina.",
			"16": "<sup>16</sup> Fa izaho kosa dia hihira ny herinao, Ary hihoby ny famindramponao nony maraina, Satria fiarovana avo ho ahy Hianao. Ary fandosirako amin’ny andro fahoriana.",
			"17": "<sup>17</sup> Ry Heriko ô, Hianao no hankalazaiko; Fa Andriamanitra no fiarovana avo ho ahy, dia Andriamanitra Izay mamindra fo amiko."
		},
		"60": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Al-shoshan-edoth. Miktama nataon’i Davida hampianarana, fony izy nanafika an’i Mesopotamia sy Arama-zoba, tamin’ny niverenan’i Joaba ka namelezany ny Edomita roa arivo amby iray alin-dahy teo amin’ny Lohasahan-tsira. ANDRIAMANITRA ô, efa nanary sy nampihahaka anay Hianao; Efa tezitra Hianao, velombelomy izahay.",
			"2": "<sup>2</sup> Efa nampihorohoro ny tany Hianao ka namaky azy; Amboary izay rava aminy, fa mihozongozona izy.",
			"3": "<sup>3</sup> Efa nampahita ny olonao zava-tsarotra Hianao, Ary efa nampisotro anay divay mampiraikiraiky.",
			"4": "<sup>4</sup> Efa notoloranao faneva ny matahotra Anao, Hatsangany noho ny fahamarinana. [Sela.]",
			"5": "<sup>5</sup> Mba hahafahan’ny malalanao, Dia vonjeo amin’ny tànanao ankavanana aho, ka valio.",
			"6": "<sup>6</sup> Andriamanitra niteny tamin’ny fahamasinany hoe: Hifaly Aho ka hizara an’i Sekema; Ary ny lohasahan’i Sokota horefesiko.",
			"7": "<sup>7</sup> Ahy Gileada, Ahy koa Manase; Ary aron’ny lohako Efraima; Tehim-panjakako Joda;",
			"8": "<sup>8</sup> Tavin-dranoko fanasana Moaba; Edoma no anipazako ny kapako; Mihobia noho ny amiko, ry Filistia.",
			"9": "<sup>9</sup> Iza no hitondra ahy ho any an-tanàna mafy? Iza no hitarika ahy ho any Edoma?",
			"10": "<sup>10</sup> Tsy Hianao va, Izay efa nanary anay, Andriamanitra ô, Sady tsy nivoaka niaraka tamin’ny antokon’ny miaramilanay, Andriamanitra ô?",
			"11": "<sup>11</sup> Vonjeo amin’ny mpandrafy izahay; Fa zava-poana ny famonjena ataon’ny olona.",
			"12": "<sup>12</sup> Andriamanitra no haherezantsika; Fa Izy no hanitsakitsaka ny fahavalontsika."
		},
		"61": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Hampiarahina amin’ny valiha. Nataon’ Davida. HENOY ny fitarainako, Andriamanitra ô; Tandremo ny fivavako.",
			"2": "<sup>2</sup> Any am-paran’ny tany no hiantsoako Anao, raha reraka ny foko; Ento aho ho any amin’ny vatolampy izay avo ka tsy tratro;",
			"3": "<sup>3</sup> Fa aroko Hianao Sy tilikambo mafy handosirako ny fahavalo.",
			"4": "<sup>4</sup> Aoka hitoetra ao an-tranonao mandrakizay aho; Aoka hialoka ao ambanin’ny fanomban’ny elatrao aho,",
			"5": "<sup>5</sup> Fa Hianao, Andriamanitra ô, efa nandre ny voadiko; Efa nomenao ahy ny lovan’izay matahotra ny anaranao.",
			"6": "<sup>6</sup> Halavao ny andron’ny mpanjaka; Aoka ny taonany hahatratra ny taranaka maro.",
			"7": "<sup>7</sup> Aoka hitoetra eo anatrehan’Andriamanitra mandrakizay izy; Famindrampo sy fahamarinana no tendreo hiaro azy;",
			"8": "<sup>8</sup> Ka dia izany no hankalazako ny anaranao mandrakizay, Mba hanefako isan-andro ny voadiko."
		},
		"62": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Al-jedotona. Salamo nataon’i Davida. ANDRIAMANITRA tokoa no iantombenan’ny fanahiko; Avy aminy ny famonjena ahy.",
			"2": "<sup>2</sup> Izy tokoa no vatolampiko sy famonjena ahy. Dia fiarovana avo ho ahy, ka tsy hangozohozo loatra aho.",
			"3": "<sup>3</sup> Mandra-pahoviana no hirohotanareo hamely olona Sy handrodananareo rehetra azy toy ny mànda mirona sy ny rova mihozongozona;",
			"4": "<sup>4</sup> Misaina hanongana azy hiala amin’ny voninahiny ireny; Lainga no ifaliany, Misaotra amin’ny vavany izy, fa manozona amin’ny fony. [Sela.]",
			"5": "<sup>5</sup> Miantombena amin’Andriamanitra tokoa hianao, ry fanahiko; Fa Izy no antenaiko.",
			"6": "<sup>6</sup> Izy tokoa no vatolampiko sy famonjena ahy, Dia fiarovana avo ho ahy, ka tsy mba hangozohozo aho.",
			"7": "<sup>7</sup> Andriamanitra no iankinan’ny famonjena ahy sy ny voninahitro; Ny vatolampiko mafy sy ny aroko dia ao amin’Andriamanitra.",
			"8": "<sup>8</sup> Matokia Azy mandrakariva hianareo, ry olona; Loary eo anatrehany ny fonareo; Andriamanitra no aro ho antsika. [Sela.]",
			"9": "<sup>9</sup> Zava-poana tokoa ny ambany, ary lainga ny ambony; Raha hatao amin’ny mizana izy, dia maivana; zava-poana mihitsy izy.",
			"10": "<sup>10</sup> Aza matoky ny fanaovana an-keriny, ary aza miantehitra amin’ny halatra; Raha mitombo ny harena, dia aza anorenana ny fonareo.",
			"11": "<sup>11</sup> Indray mandeha no nitenenan’Andriamanitra; Indroa no nandrenesako hoe: An’Andriamanitra ny hery.",
			"12": "<sup>12</sup> Ary Anao koa, ry Tompo ô, ny famindrampo, Fa Hianao no mamaly ny olona rehetra araka ny asany avy."
		},
		"63": {
			"1": "<sup>1</sup> Salamo nataon’i Davida, fony tany an-efitr’i Joda izy. ANDRIAMANITRA ô, Andriamanitro Hianao; Mitady Anao fatratra aho; Mangetaheta Anao ny fanahiko, maniry Anao ny nofoko Eto amin’ny tany maina sy mangentana tsy misy rano;",
			"2": "<sup>2</sup> Toy izany no nijereko Anao tao amin’ny fitoerana masina, Hahitako ny herinao sy ny voninahitrao.",
			"3": "<sup>3</sup> Fa tsara noho ny aina ny famindramponao; Ny molotro hidera Anao.",
			"4": "<sup>4</sup> Toy izany no hisaorako Anao, raha mbola velona koa aho; Amin’ny anaranao no hanandratako ny tànako.",
			"5": "<sup>5</sup> Toy ny voky tsoka sy tavy no hahavokisan’ny fanahiko; Ary amin’ny molotra falifaly no hideran’ny vavako Anao.",
			"6": "<sup>6</sup> Raha mahatsiaro Anao eo am-pandriako aho, Dia mieritreritra Anao amin’ny fiambenan-alina.",
			"7": "<sup>7</sup> Fa efa namonjy ahy Hianao, Ary ao ambanin’ny aloky ny elatrao no hihobiako.",
			"8": "<sup>8</sup> Mifikitra aminao ny fanahiko; Manohana ahy ny tànanao ankavanana.",
			"9": "<sup>9</sup> Fa ireny dia mitady ny aiko ka mampidi-doza amin’ny tenany; Hiditra any ambanin’ny tany izy;",
			"10": "<sup>10</sup> Hatolotra ho amin’ny herin’ny sabatra izy; Ho anjaran’ny amboahaolo izy.",
			"11": "<sup>11</sup> Fa ny mpanjaka hifaly amin’Andriamanitra; Ho ravoravo izay rehetra mianiana aminy; Fa hakombona ny vavan’izay rehetra mandainga."
		},
		"64": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Salamo nataon’i Davida. HENOY ny feoko, raha mitaraina aho, Andriamanitra ô; Arovy ny aiko amin’ny tahotro ny fahavalo.",
			"2": "<sup>2</sup> Afeno aho tsy ho azon’ny firaisan-tetiky ny ratsy fanahy Sy ny fitabataban’ny mpanao ratsy,",
			"3": "<sup>3</sup> Izay mandranitra ny lelany ho tahaka ny sabatra, Ka mikendry handefa ny zana-tsipìkany, dia teny mangidy,",
			"4": "<sup>4</sup> Mba hitifitra mangingina ny mahitsy; Mitifitra azy tampoka izy ka tsy matahotra.",
			"5": "<sup>5</sup> Hamafiny ny hevi-dratsiny; Mifampitaona hanao fandri-totofana izy Ka manao hoe: Iza no hahita izany?",
			"6": "<sup>6</sup> Manao saim-petsy izy; Omaniny tsara ny sain-dalina voaheviny; Lalina ny ao anatin’ny olombelona, dia ny fony.",
			"7": "<sup>7</sup> Mitifitra azy Andriamanitra; Tampoka toy ny fiantefan’ny zana-tsipìka no fahavoazany.",
			"8": "<sup>8</sup> Apotrany izy ka voasingotry ny lelany ihany; Mandositra izay rehetra mahita azy.",
			"9": "<sup>9</sup> Dia matahotra ny olona rehetra ka manambara ny ataon’Andriamanitra Sady mandinika tsara ny asany.",
			"10": "<sup>10</sup> Mifaly amin’i Jehovah ny marina ka mialoka aminy; Eny, mifaly tena ny mahitsy fo rehetra."
		},
		"65": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Salamo nataon’i Davida. Tonon-kira. HO anao ny fanginana sy ny fiderana, Andriamanitra any Ziona ô, Ary aminao no anefana ny voady.",
			"2": "<sup>2</sup> Ry Mpihaino vavaka ô, Hianao no hohatonin’ny nofo rehetra.",
			"3": "<sup>3</sup> Ny zava-mahameloka ahy dia mihoatra noho izay zakako; Nefa Hianao no mamela ny fahadisoanay.",
			"4": "<sup>4</sup> Sambatra izay fidinao ka ampanatoninao hitoetra eo an-kianjanao; Te-ho voky ny zava-tsoa ao amin’ny tranonao izahay, dia ny zava-masina ao amin’ny tempolinao.",
			"5": "<sup>5</sup> Zava-mahatahotra no ataonao amin’ny fahamarinana ho valin’ny vavakay, Ry Andriamanitry ny famonjena anay ô, Ry tokin’ny faran’ny tany rehetra sy ny ranomasina eny lavitra eny.",
			"6": "<sup>6</sup> Mampiorina ny tendrombohitra amin’ny heriny Izy, Misikina fahatanjahana Izy.",
			"7": "<sup>7</sup> Mampitsahatra ny firohondrohon’ny onjan-dranomasina Izy Sy ny fitabataban’ny firenena.",
			"8": "<sup>8</sup> Dia matahotra noho ny famantaranao ny mponina any am-paran’ny tany; Ny any amin’ny fiposahan’ny maraina sy ny hariva no ampihobinao.",
			"9": "<sup>9</sup> Mamangy ny tany Hianao ka mahamando azy; Mampahavokatra azy betsaka Hianao– Ny onin’Andriamanitra dia tondra-drano– Mamboatra vary ho an’ny olona Hianao; Fa izany no famboatrao ny tany.",
			"10": "<sup>10</sup> Mahampoka ny tany voasa Hianao ka mampiravona azy; Ranonorana mivatravatra no amontosanao azy; Mitahy ny fitsimohany Hianao.",
			"11": "<sup>11</sup> Satrohanao voninahitra ny taona fitahianao; Ary ny dianao mitete menaka.",
			"12": "<sup>12</sup> Vonton-drano ny fiandrasana ondry any an-efitra, Ary misikina fifaliana ny havoana.",
			"13": "<sup>13</sup> Rakotra ondry ny saha; Ary voasarona vary ny lohasaha: Mihoby izy, eny, mihira koa."
		},
		"66": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Tonon-kira. Salamo. MIHOBIA ho an’Andriamanitra, ry tany rehetra;",
			"2": "<sup>2</sup> Mankalazà ny voninahitry ny anarany; Omeo voninahitra Izy ho fiderana Azy.",
			"3": "<sup>3</sup> Lazao amin’Andriamanitra hoe: Endrey, mahatahotra ny asanao! Ny fahalehibiazan’ny herinao no hikoizan’ny fahavalonao Anao.",
			"4": "<sup>4</sup> Ny tany rehetra hiankohoka eo anatrehanao ka hankalaza Anao; Eny, hankalaza ny anaranao izy. [Sela.]",
			"5": "<sup>5</sup> Avia, ka jereo ny asan’Andriamanitra, Mahatahotra ny zavatra ataony amin’ny zanak’olombelona.",
			"6": "<sup>6</sup> Efa nampody ny ranomasina ho tany maina Izy; Nandeha an-tongotra nita ny ony ny olona; Tao no nifaliantsika tamin’Andriamanitra.",
			"7": "<sup>7</sup> Manjaka mandrakizay amin’ny heriny Izy, Ary ny masony mandinika ny firenen-tsamy hafa; Aoka tsy hanandra-tena ny maditra. [Sela.]",
			"8": "<sup>8</sup> Misaora an’Andriamanitsika, ry firenena; Ary asandrato ny feo fiderana Azy,",
			"9": "<sup>9</sup> Izay mampitoetra ny fanahintsika amin’ny fiainana Ka tsy mamela ny tongotsika hangozohozo.",
			"10": "<sup>10</sup> Fa efa nizaha toetra anay Hianao, Andriamanitra ô; Efa nanadio anay Hianao, tahaka ny fanadio volafotsy amin’ny memy.",
			"11": "<sup>11</sup> Efa nampiditra anay tamin’ny fandrika Hianao; Efa nanisy enta-mavesatra teo am-balahanay Hianao.",
			"12": "<sup>12</sup> Efa nampandeha ny olona hanitsaka ny lohanay Hianao; Nandeha namaky ny afo sy ny rano izahay; Fa nitondra anay nivoaka ho amin’ny fitahiana mahatretrika Hianao.",
			"13": "<sup>13</sup> Hiditra ao an-tranonao mitondra fanatitra dorana aho; Hefaiko aminao ny voadiko,",
			"14": "<sup>14</sup> Izay notononin’ny molotro Sy naloaky ny vavako, fony aho azom-pahoriana.",
			"15": "<sup>15</sup> Hateriko aminao ho fanatitra dorana ny matavy Mbamin’ny setroky ny ondrilahy; Hanatitra omby sy osilahy aho. [Sela.]",
			"16": "<sup>16</sup> Avia, mihainoa hianareo rehetra izay matahotra an’Andriamanitra, Fa holazaiko izay nataony ho an’ny fanahiko.",
			"17": "<sup>17</sup> Izy no nantsoin’ny vavako; Ary ny fanandratana Azy no teo ambanin’ny lelako.",
			"18": "<sup>18</sup> Raha nankasitraka ota tao am-poko aho, Dia tsy mba hihaino ny Tompo;",
			"19": "<sup>19</sup> Kanefa efa nihaino tokoa Andriamanitra; Efa nihaino ny feon’ny fivavako Izy.",
			"20": "<sup>20</sup> Isaorana anie Andriamanitra, Izay tsy nandà ny fivavako, Na nanaisotra ny famindrampony tamiko."
		},
		"67": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Hampiarahina amin’ny valiha. Salamo. Tonon-kira. ANDRIAMANITRA anie hamindra fo aminay sy hitahy anay; [Sela.] Hampamirapiratra ny tavany aminay anie Izy;",
			"2": "<sup>2</sup> Mba ho fantatra etỳ ambonin’ny tany ny làlanao Sy ny famonjenao any amin’ny jentilisa rehetra.",
			"3": "<sup>3</sup> Hidera Anao ny firenena, Andriamanitra ô; Eny, hidera Anao ny firenena rehetra.",
			"4": "<sup>4</sup> Hifaly sy hihoby ny firenen-tsamy hafa, Satria mitsara marina ny firenena Hianao; Ny firenen-tsamy hafa ambonin’ny tany dia entinao. [Sela.]",
			"5": "<sup>5</sup> Hidera Anao ny firenena, Andriamanitra ô; Eny, hidera Anao ny firenena rehetra.",
			"6": "<sup>6</sup> Ny tany efa nahavokatra; Mitahy antsika Andriamanitra, dia Andriamanitsika.",
			"7": "<sup>7</sup> Mitahy antsika Andriamanitra; Ary hatahotra Azy ny vazan-tany rehetra."
		},
		"68": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Salamo nataon’i Davida. Tonon-kira. AOKA hitsangana Andriamanitra, ka hiely ny fahavalony; Ary aoka izay mankahala Azy handositra ny tavany.",
			"2": "<sup>2</sup> Tahaka ny fanely setroka no fanelinao azy; Tahaka ny fiempon’ny savoka eo anoloan’ny afo No aoka hahalevonan’ny ratsy fanahy eo anatrehan’Andriamanitra.",
			"3": "<sup>3</sup> Fa ny marina mifaly; Eny, ravoravo eo anatrehan’Andriamanitra izy ka faly tokoa.",
			"4": "<sup>4</sup> Mihirà ho an’Andriamanitra, mankalazà ny anarany; Manamboara làlana ho an’Izay mitaingina mamaky ny efitra– Jehovah no anarany–ary mifalia eo anatrehany.",
			"5": "<sup>5</sup> Rain’ny kamboty sy Mpanome rariny ny mpitondratena Andriamanitra ao amin’ny fonenany masina.",
			"6": "<sup>6</sup> Andriamanitra mampitoetra an-trano izay irery tena; Mamoaka ny mpifatotra ho amin’ny fanambinana Izy; Fa ny maditra hitoetra amin’ny tany karankaina.",
			"7": "<sup>7</sup> Andriamanitra ô, raha nandeha teo alohan’ny olonao Hianao Ka namaky ny efitra, [Sela.]",
			"8": "<sup>8</sup> Dia nihorohoro ny tany, Ary nitaitaika ny lanitra, tamin’ny fanatrehan’Andriamanitra, Na iry Sinay iry aza tamin’ny fanatrehan’Andriamanitra, Andriamanitry ny Isiraely.",
			"9": "<sup>9</sup> Ranonoram-pitahiana no nalatsakao tamin’ny lovanao, Andriamanitra ô; Ary raha kotra izy, dia novelombelominao.",
			"10": "<sup>10</sup> Ny olonao taman-toerana teo; Mamboatra ho an’ny ory araka ny fahasoavanao Hianao, Andriamanitra ô.",
			"11": "<sup>11</sup> Ny Tompo manome ny teny; Ary ny vehivavy mpilaza soa dia antokony lehibe.",
			"12": "<sup>12</sup> Ireo mpanjaka be miaramila dia vaky mandositra; Ary ny vehivavy izay mitoetra ao an-trano no mizara ny babo.",
			"13": "<sup>13</sup> Raha mandry eo amin’ny vala hianareo, Dia ho tahaka ny ela-boromailala miravaka volafotsy, Ary ny volony miravaka volamena tsara.",
			"14": "<sup>14</sup> Raha aelin’ny Tsitoha eo ny mpanjaka maro, Dia latsaka ny orampanala ao Zalmona.",
			"15": "<sup>15</sup> Tendrombohitr’Andriamanitra ny tendrombohitr’i Basana. Tendrombohitra maro tampona ny tendrombohitr’i Basana.",
			"16": "<sup>16</sup> Ry tendrombohitra maro tampona, nahoana hianareo no mihendry Ny tendrombohitra izay tian’Andriamanitra hitoerany? Eny, hitoetra ao mandrakizay Jehovah.",
			"17": "<sup>17</sup> Ny kalesin’Andriamanitra dia roa alina, eny, arivo arivo; Ny Tompo no eo aminy; Sinay dia eo amin’ny itoerana masina.",
			"18": "<sup>18</sup> Efa niakatra ho any amin’ny avo Hianao ka nitondra babo; Efa nandray fanomezana teo amin’ny olombelona Hianao– Eny, teo amin’ny maditra aza,–mba hitoeran’i Jehovah Andriamanitra any.",
			"19": "<sup>19</sup> Isaorana anie ny Tompo, Izay mitondra ny entantsika isan-andro isan-andro, Eny, Andriamanitra Izay famonjena antsika. [Sela.]",
			"20": "<sup>20</sup> Andriamanitra no Andriamanitry ny famonjena ho antsika; Ary an’i Jehovah Tompo ny fanafahana amin’ny fahafatesana.",
			"21": "<sup>21</sup> Fa Andriamanitra mamely mafy ny lohan’ny fahavalony, Dia ny tampon-doha lava volo an’izay mandroso amin’ny fahadisoany.",
			"22": "<sup>22</sup> Lazain’ny Tompo hoe: Hampody ny any Basana Aho; Hampody ny amin’ny ranomasina lalina Aho,–",
			"23": "<sup>23</sup> Mba hampihosenanao ra ny tongotrao, Ary ho anjaran’ny lelan’ny amboanao ny fahavalo.",
			"24": "<sup>24</sup> Efa hita ny fandehananao, Andriamanitra ô, Dia ny fandehanan’Andriamanitro, Mpanjakako, ao amin’ny fitoerana masina.",
			"25": "<sup>25</sup> Mialoha ny mpihira, ary manaraka ny mpitendry valiha, Eo afovoan’ny zatovovavy mively ampongatapaka.",
			"26": "<sup>26</sup> Misaora an’Andriamanitra eo amin’ny fiangonana, Eny, misaora ny Tompo, hianareo avy amin’ny loharanon’ny Isiraely.",
			"27": "<sup>27</sup> Indry Benjamina kely, mpanapaka azy, Ny antokon’ny mpanapaka ny Joda, Ny mpanapaka ny Zebolona sy ny mpanapaka ny Naftaly.",
			"28": "<sup>28</sup> Andriamanitrao no nampahery anao; Ry Andriamanitra Izay miasa ho anay ô, asehoy ny herinao.",
			"29": "<sup>29</sup> Noho ny tempolinao eo an-tampon’i Jerosalema No hitondran’ny mpanjaka maro fanatitra ho Anao.",
			"30": "<sup>30</sup> Teneno mafy ny biby eny anaty zozoro Sy ny fivorian’ny ombilahy mbamin’ireo vahoaka zanak’omby, Dia izay miankohoka manatitra anja-bolafotsy– Aelezo ny olona tia ady–",
			"31": "<sup>31</sup> Ho avy ny tremalahin’i Egypta; Etiopia halaky hananty tànana amin’Andriamanitra.",
			"32": "<sup>32</sup> Ry fanjakana ambonin’ny tany, mihirà ho an’Andriamanitra; Mankalazà ny Tompo, [Sela.]",
			"33": "<sup>33</sup> Ilay mitaingina ny lanitry ny lanitra izay hatry ny fahagola; Indro, mamoaka ny feony Izy, dia feo mahery.",
			"34": "<sup>34</sup> Ekeo ho an’Andriamanitra ny hery; Ambonin’ny Isiraely ny voninahiny, Ary ny heriny any amin’ny lanitra.",
			"35": "<sup>35</sup> Mahatahotra Hianao, Andriamanitra ô, ao amin’ny fitoeranao masina; Andriamanitry ny Isiraely no manome hery sy tanjaka ho an’ny olona. Isaorana anie Andriamanitra."
		},
		"69": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Al-shoshanim. Nataon’i Davida. VONJEO aho, Andriamanitra ô; Fa efa mihatra amin’ny aiko ny rano.",
			"2": "<sup>2</sup> Milentika amin’ny fotaka mandrevo aho, ka tsy misy hifaharana; Efa miditra amin’ny rano lalina aho, ka mandifotra ahy ny riaka.",
			"3": "<sup>3</sup> Sasatry ny mitaraina aho, maimay ny tendako; Pahina ny masoko, raha miandry an’Andriamanitro aho.",
			"4": "<sup>4</sup> Maro noho ny volon-dohako ny isan’izay mankahala ahy tsy ahoan-tsy ahoana; Mahery izay ta-hahafaty ahy, dia ny fahavaloko tsy ahoan-tsy ahoana; Mampanonitra ahy izay tsy nalaiko izy.",
			"5": "<sup>5</sup> Andriamanitra ô, Hianao mahalala ny hadalako; Ary ny heloko tsy mba miafina aminao.",
			"6": "<sup>6</sup> Aoka tsy ho menatra noho ny amiko izay miandry Anao, Jehovah, Tompon’ny maro ô; Aoka tsy ho very hevitra noho ny amiko izay mitady Anao, ry Andriamanitry ny Isiraely ô.",
			"7": "<sup>7</sup> Fa noho ny aminao no itondrako latsa; Safotry ny henatra ny tavako.",
			"8": "<sup>8</sup> Efa vahiny amin’ny rahalahiko aho; Eny, efa olon-ko azy amin’ny zanak’ineny aho.",
			"9": "<sup>9</sup> Fa ny firehetako ny amin’ny tranonao no naharìtra aina ahy; Ary ny latsa nataon’izay nandatsa Anao dia nihatra tamiko.",
			"10": "<sup>10</sup> Nitomany sady nifady hanina aho, Dia natao latsa ho ahy izany.",
			"11": "<sup>11</sup> Lamba fisaonana no nataoko fitafiako, Dia efa ambentinteny tamin’ireny aho.",
			"12": "<sup>12</sup> Miresaka ahy izay mipetraka eo am-bavahady; Ary ataon’ny mpisotro toaka an-kira aho.",
			"13": "<sup>13</sup> Fa izaho hivavaka aminao amin’ny andro fankasitrahana, Jehovah ô, Dia aminao, Andriamanitra ô, noho ny haben’ny famindramponao; Valio aho araka ny hamarinan’ny famonjenao.–",
			"14": "<sup>14</sup> Afaho amin’ny fotaka aho, ka aza avela hilentika; Vonjeo aho amin’izay mankahala ahy sy amin’ny rano lalina.",
			"15": "<sup>15</sup> Aza avela hanafotra ahy ny rano mandriaka, Na hitelina ahy ny lalina; Ary aza avela hikatom-bava amiko ny lavaka fantsakana.",
			"16": "<sup>16</sup> Valio aho, Jehovah ô, fa tsara ny famindramponao; Todiho aho araka ny haben’ny fiantranao.",
			"17": "<sup>17</sup> Ary aza manafina ny tavanao amin’ny mpanomponao; Fa azom-pahoriana aho, koa faingàna hamaly ahy Hianao.",
			"18": "<sup>18</sup> Hatòny ny fanahiko, avoty izy; Eny, vonjeo aho noho ny ataon’ny fahavaloko.",
			"19": "<sup>19</sup> Hianao no mahalala ny latsa mihatra amiko Ary ny fahamenarako sy ny fahafaham-boninahitro; Eo anatrehanao ny mpandrafy ahy rehetra.",
			"20": "<sup>20</sup> Ny latsa dia nanorotoro ny foko ka reraka indrindra aho; Ary niandry antra aho, fa tsy nisy, Sy mpanony, fa tsy nahita.",
			"21": "<sup>21</sup> Fa zava-mangidy no nomeny ho haniko; Ary vinaingitra no natolony ahy hosotroina, raha nangetaheta aho.",
			"22": "<sup>22</sup> Aoka ho fandrika ho azy ny latabany eo anoloany; Eny, ho tonta ho an’ny tsy manana ahiahy.",
			"23": "<sup>23</sup> Aoka ho jamba ny masony mba tsy hahita; Ary ampangozohozoy mandrakariva ny valahany.",
			"24": "<sup>24</sup> Aidino aminy ny fahatezeranao; Ary aoka hahatratra azy ny firehetan’ny fahaviniranao.",
			"25": "<sup>25</sup> Aoka ho foana ny tobiny; Aza avela hisy hitoetra ao an-dainy.",
			"26": "<sup>26</sup> Fa izay nasianao no nenjehiny; Ary ny fahararian’ny voatsindronao dia lazalazainy.",
			"27": "<sup>27</sup> Aoka hiampy ny helony; Ary aoka tsy hiditra amin’ny fahamarinanao izy.",
			"28": "<sup>28</sup> Aoka hovonoina tsy ho eo amin’ny bokin’ny velona ny anarany, Ary aoka tsy hosoratana eo amin’ny marina.",
			"29": "<sup>29</sup> Fa izaho dia ory sy mahantra, Andriamanitra ô; Aoka ny famonjenao no hanandratra ahy.",
			"30": "<sup>30</sup> Hankalaza ny anaran’Andriamanitra amin’ny fihirana aho, Ary hidera Azy amin’ny fisaorana.",
			"31": "<sup>31</sup> Fa ho sitrak’i Jehovah izany mihoatra noho ny vantotr’ombilahy, Izay manan-tandroka sady mivaky kitro.",
			"32": "<sup>32</sup> Hahita izany ny mpandefitra ka ho faly; Aoka ho velona ny fonareo, ry mpitady an’Andriamanitra.",
			"33": "<sup>33</sup> Fa mihaino ny malahelo Jehovah, Ary ny olony mifatotra tsy mba hamavoiny.",
			"34": "<sup>34</sup> Aoka hidera Azy ny lanitra sy ny tany, Ny ranomasina sy izay rehetra mihetsika ao aminy.",
			"35": "<sup>35</sup> Fa Andriamanitra hamonjy an’i Ziona sady hanangana ny tanànan’i Joda; Ary hitoetra ao ny olony ka ho tompony;",
			"36": "<sup>36</sup> Dia handova azy ny taranaky ny mpanompony; Ary hitoetra ao izay tia ny anarany."
		},
		"70": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Nataon’i Davida. Ho fampahatsiarovana. ANDRIAMANITRA ô, faingàna hanafaka ahy; Jehovah ô, faingàna hamonjy ahy.",
			"2": "<sup>2</sup> Aoka ho menatra sy hangaihay izay mitady ny aiko; Aoka hiamboho sy ho afa-baraka izay faly amin’ny manjo ahy;",
			"3": "<sup>3</sup> Aoka hiamboho noho ny fahamenarany Izay manao hoe: Hia! sakoa izay!",
			"4": "<sup>4</sup> Aoka hifaly sy ho ravoravo aminao izay rehetra mitady Anao; Ary aoka izay tia ny famonjenao hanao mandrakariva hoe: Hankalazaina anie Andriamanitra.",
			"5": "<sup>5</sup> Fa izaho dia ory sy malahelo; Faingàna hankatỳ amiko, Andriamanitra ô; Mpamonjy sy Mpanafaka ahy Hianao; Aza ela, Jehovah ô."
		},
		"71": {
			"1": "<sup>1</sup> HIANAO, Jehovah ô, no itokiako; Aoka tsy ho menatra mandrakizay aho.",
			"2": "<sup>2</sup> Vonjeo ny fahamarinanao aho, ka afaho; Atongilano amiko ny sofinao, ka vonjeo aho.",
			"3": "<sup>3</sup> Aoka ho vatolampy fonenana hohatoniko mandrakariva Hianao, Izay efa nandidy hamonjy ahy; Fa harambatoko sy batery fiarovana ho ahy Hianao.",
			"4": "<sup>4</sup> Andriamanitro ô, afaho amin’ny tànan’ny ratsy fanahy aho, Dia amin’ny tànan’ny mpanao meloka sy ny lozabe.",
			"5": "<sup>5</sup> Fa Hianao no fanantenako, Jehovah Tompo ô, Ry tokiko hatry ny fony aho mbola tanora.",
			"6": "<sup>6</sup> Hianao no nanankinana ahy hatrany am-bohoka; Hianao namoaka ahy avy tany an-kibon-dreniko; Hianao no deraiko mandrakariva.",
			"7": "<sup>7</sup> Tonga toa zava-mahagaga eo imason’ny maro aho; Nefa Hianao no fiarovana mafy ho ahy.",
			"8": "<sup>8</sup> Ny vavako ho feno ny fiderana Anao Sy ny fankalazana Anao mandrakariva.",
			"9": "<sup>9</sup> Aza manary ahy, rehefa antitra aho; Ary aza mahafoy ahy, rehefa ketraka ny heriko.",
			"10": "<sup>10</sup> Fa miresaka ahy ny fahavaloko; Ary miara-mioko izay manotrika hahazo ny aiko.",
			"11": "<sup>11</sup> Ka manao hoe: Andriamanitra efa nahafoy azy; Enjeho izy, ka sambory, fa tsy misy hamonjy.",
			"12": "<sup>12</sup> Andriamanitra ô, aza manalavitra ahy; Andriamanitro ô, faingàna hamonjy ahy.",
			"13": "<sup>13</sup> Aoka ho menatra sy ho levona ny mpanohitra ny fanahiko; Aoka hitafy henatra sy fahafaham-baraka izay mitady hanisy ratsy ahy.",
			"14": "<sup>14</sup> Fa izaho kosa dia hanantena mandrakariva, Ary hitombo lalandava ny fiderako Anao.",
			"15": "<sup>15</sup> Ny vavako hitory ny fahamarinanao Ny famonjenao mandrakariva, Fa tsy fantatro ny isany.",
			"16": "<sup>16</sup> Ho entiko ny asa lehibe nataon’i Jehovah Tompo; Hampahatsiaro ny fahamarinanao aho, dia ny Anao ihany.",
			"17": "<sup>17</sup> Andriamanitra ô, Hianao efa nampianatra ahy hatry ny fony aho mbola tanora; Ary mandraka ankehitriny dia nitory ny fahagaganao aho.",
			"18": "<sup>18</sup> Koa mandra-pahantitro sy fotsy volo, dia aza mahafoy ahy, Andriamanitra ô, Mandra-pilazako ny sandrinao amin’ny taranaka Sy ny asanao lehibe amin’izay rehetra mbola ho avy.",
			"19": "<sup>19</sup> Ary mihatra amin’izay avo indrindra ny fahamarinanao, Andriamanitra ô, Izay nanao zava-dehibe: Andriamanitra ô, iza no tahaka Anao?",
			"20": "<sup>20</sup> Hianao Izay nampahita ahy fahoriana be sady mafy No hamelona ahy ihdray Ka hampiakatra ahy indray hiala amin’ny fitoerana lalina amin’ny tany.",
			"21": "<sup>21</sup> Ampitomboy ny voninahitro, Ary aoka hampionona ahy indray Hianao.",
			"22": "<sup>22</sup> Izaho kosa hidera Anao amin’ny valiha, dia ny fahamarinanao, Andriamanitro ô; Hankalaza Anao amin’ny lokanga aho, ry Iray Masin’ny Isiraely ô.",
			"23": "<sup>23</sup> Hihoby ny molotro, raha mankalaza Anao; Ny fanahiko koa izay efa navotanao.",
			"24": "<sup>24</sup> Ary ny lelako hitory ny fahamarinanao mandrakariva; Fa efa menatra sy mangaihay izay mitady hanisy ratsy ahy."
		},
		"72": {
			"1": "<sup>1</sup> Nataon’i Solomona. ANDRIAMANITRA ô, omeo ho an’ny mpanjaka ny fitsaranao, Ary ho an’ny zanaky ny mpanjaka ny fahamarinanao.",
			"2": "<sup>2</sup> Hitsara ny olonao amin’ny fahamarinana izy, Ary ny malahelonao amin’ny fahitsiana.",
			"3": "<sup>3</sup> Ny tendrombohitra hahavokatra fiadanana ho an’ny olona, Eny, ny havoana koa amin’ny fahamarinana.",
			"4": "<sup>4</sup> Hitsara ny olona ory izy Sy hamonjy ny zanaky ny mahantra Ary hanorotoro ny mpampahory.",
			"5": "<sup>5</sup> Hatahotra anao ny olona, raha mbola maharitra koa ny masoandro Sy ny volana hatramin’ny taranaka fara mandimby.",
			"6": "<sup>6</sup> Hidina tahaka ny ranonorana amin’ny saha voajinja izy, Tahaka ny ranonorana mivatravatra amin’ny tany.",
			"7": "<sup>7</sup> Ny marina hitrebona amin’ny androny, Ary ho be ny fiadanana mandra-paha-tsy hisy volana intsony.",
			"8": "<sup>8</sup> Ary hanjaka hatramin’ny ranomasina ka hatramin’ny ranomasina izy, Ary hatramin’ny Ony ka hatramin’ny faran’ny tany.",
			"9": "<sup>9</sup> Ny mponina any an-efitra handohalika eo anatrehany; Ary ny fahavalony hilelaka ny vovoka.",
			"10": "<sup>10</sup> Ny mpanjakan’i Tarsisy sy ny nosy handoa hetra; Ny mpanjakan’i Sheba sy Seba hanatitra fanomezana.",
			"11": "<sup>11</sup> Ary ny mpanjaka rehetra hiankohoka eo anatrehany; Ny firenena rehetra hanompo azy.",
			"12": "<sup>12</sup> Fa hamonjy ny mahantra mitaraina izy Sy ny ory tsy manan-kamonjy;",
			"13": "<sup>13</sup> Hiantra ny reraka sy ny malahelo izy Ary hamonjy ny fanahin’ny malahelo.",
			"14": "<sup>14</sup> Hanavotra ny fanahiny amin’ny fampahoriana sy ny fanaovana an-keriny izy; Ary ho zava-dehibe eo imasony ny rany.",
			"15": "<sup>15</sup> Dia ho velona ireny ka hanome azy volamena avy any Sheba, Ary hanao fifonana ho azy mandrakariva, izy, ary isan-andro no hisaorany azy.",
			"16": "<sup>16</sup> Hahavoka-bary betsaka ny tany, na dia any an-tampon’ny tendrombohitra aza; Hiròndròna tahaka ny any Libanona ny fahavokarany; Ary ny ao an-tanàna hidokadoka tahaka ny ahitra amin’ny tany.",
			"17": "<sup>17</sup> Ho mandrakizay ny anarany; Raha mbola maharitra koa ny masoandro, dia hanorobona ny anarany; Ary izy ho fitahiana ny olona; Ny firenena rehetra hanao azy ho sambatra.",
			"18": "<sup>18</sup> Isaorana anie Jehovah Andriamanitra, Andriamanitry ny Isiraely; Izy irery ihany no manao fahagagana;",
			"19": "<sup>19</sup> Ary isaorana mandrakizay anie ny anarany malaza; Ary aoka ny tany rehetra ho henika ny voninahiny. Amena dia Amena.",
			"20": "<sup>20</sup> Tapitra ny fivavak’i Davida, zanak’i Jese."
		},
		"73": {
			"1": "<sup>1</sup> Salamo nataon’i Asafa. ANDRIAMANITRA mahasoa ny Isiraely tokoa, Dia ny madio fo.",
			"2": "<sup>2</sup> Fa izaho, dia saiky nibolasitra ny tongotro; Saiky solafaka ny diako,",
			"3": "<sup>3</sup> Satria nialona ny mpirehareha aho, Raha nahita ny fiadanan’ny ratsy fanahy.",
			"4": "<sup>4</sup> Fa tsy misy fanaintainana amin’ny fahafatesany, Ary fatratra ny heriny.",
			"5": "<sup>5</sup> Tsy mba misy manavesatra azy, tahaka ny olona sasany, Ary tsy mba azom-pahoriana tahaka ny olon-kafa izy.",
			"6": "<sup>6</sup> Dia izany no iambozonany rehareha toy ny rojo, Sy itafiany fahalozana tahaka ny lamba.",
			"7": "<sup>7</sup> Bongon-tavy ny masony; Miloatra ny hevitry ny fony.",
			"8": "<sup>8</sup> Manazimbazimba izy ka miresaka fampahoriana an-dolompo; Avo vava izy.",
			"9": "<sup>9</sup> Manao vava manakatra ny lanitra izy, Ary ny lelany mandeha mitety ny tany.",
			"10": "<sup>10</sup> Izany no itodihan’ny olony hankany aminy; Ary rano be no gohin’ireny;",
			"11": "<sup>11</sup> Ka hoy izy: Hataon’Andriamanitra ahoana no fahalala? Ary manam-pahalalana va ny Avo Indrindra?",
			"12": "<sup>12</sup> Indro, ireny dia olona ratsy fanahy; Miadana mandrakariva izy ka mitombo harena.",
			"13": "<sup>13</sup> Foana mihitsy ny nanadiovako ny foko Sy ny nanasako ny tànako tamin’ny fahamarinana.",
			"14": "<sup>14</sup> Azom-pahoriana mandritra ny andro aho, Ary faizana isa-maraina.",
			"15": "<sup>15</sup> Raha tàhiny aho nanao hoe: Hiteny izany aho, Dia ho namitaka ireo zanakao.",
			"16": "<sup>16</sup> Dia nieritreritra ta-hahalala izany zavatra izany aho, Kanjo sarotra teo imasoko izany,",
			"17": "<sup>17</sup> Mandra-piditro tao amin’ny fitoera-masin’Andriamanitra Ka nihevitra izay ho farany.",
			"18": "<sup>18</sup> Eo amin’izay mahasolafaka tokoa no ametrahanao azy, Ary aripakao ho ringana izy.",
			"19": "<sup>19</sup> Hià! rava indray mipì-maso izy! Levona izy, matin-java-mahatahotra izy!",
			"20": "<sup>20</sup> Tahaka ny anaovan’ny olona ny nofiny ho tsinontsinona, Dia tahaka izany, Tompo ô, no anaovanao ny endrik’ireny ho tsinontsinona, raha mifoha Hianao.",
			"21": "<sup>21</sup> Raha lotika ny foko, Ka voatsindrona ny voako,",
			"22": "<sup>22</sup> Dia ketrina aho ka tsy nahalala; Eny, biby teo anatrehanao aho.",
			"23": "<sup>23</sup> Nefa izaho dia eo aminao ihany mandrakariva, Mihazona ny tànako ankavanana Hianao.",
			"24": "<sup>24</sup> Ny saina omenao no hitondranao ahy, Ary rehefa afaka izany, dia horaisinao ho amin’ny voninahitra aho.",
			"25": "<sup>25</sup> Iza no ahy any an-danitra? Ary tsy misy etỳ ambonin’ny tany iriko tahaka Anao.",
			"26": "<sup>26</sup> Na dia levona aza ny nofoko sy ny foko, Dia mbola vatolampin’ny foko sy anjarako mandrakizay Andriamanitra.",
			"27": "<sup>27</sup> Fa, indro, very izay lavitra Anao; Aringanao izay rehetra mijangajanga mahafoy Anao.",
			"28": "<sup>28</sup> Fa, izaho, dia ny ho akaikin’Andriamanitra no mahatsara ahy; Hianao, Jehovah Tompo ô, no nataoko aroko, Mba hilaza ny asanao rehetra aho."
		},
		"74": {
			"1": "<sup>1</sup> Maskila nataon’i Asafa. ANDRIAMANITRA ô, nahoana no nanary anay mandrakizay Hianao, Sady manetroka amin’ny ondry fiandrinao ny fahatezeranao?",
			"2": "<sup>2</sup> Tsarovy ny fiangonanao izay efa azonao fahiny, Ilay navotanao ho firenena lovanao, dia ny tendrombohitra Ziona izay nonenanao.",
			"3": "<sup>3</sup> Aingao ny tongotrao, ka miakara ho amin’ny voarava mandrakizay; Ny zavatra rehetra tao amin’ny fitoerana masina dia simban’ny fahavalo avokoa.",
			"4": "<sup>4</sup> Nierona tao anatin’ny fiangonanao ny mpandrafy anao; Nanangana ny sainany ho famantarana izy.",
			"5": "<sup>5</sup> Ny fijery azy dia tahaka ny olona manainga Famaky hijinja ny ala mikitroka;",
			"6": "<sup>6</sup> Ary amin’izao ny rafitra misoratra rehetra ao Dia asiany ny famaky sy ny kanonta.",
			"7": "<sup>7</sup> Efa nandoro ny fitoeranao masina tamin’ny afo izy; Nolotoiny sy nazerany tamin’ny tany ny fonenan’ny anaranao.",
			"8": "<sup>8</sup> Hoy izy tam-pony: Aoka hampahorintsika avokoa izy. Efa nandoro ny tranon’Andriamanitra eran’ny tany rehetra izy.",
			"9": "<sup>9</sup> Tsy hitanay ny famantaranay–tsy misy mpaminany intsony– Ary tsy misy aminay mahalala izay mbola haharetan’izao.",
			"10": "<sup>10</sup> Mandra-pahoviana, Andriamanitra ô, no hanaratsian’ny mpandrafy? Hanamavo ny anaranao mandrakizay va ny fahavalo?",
			"11": "<sup>11</sup> Nahoana no mampihemotra ny tànanao, dia ny tànanao ankavanana, Hianao? Mampialà azy amin’ny tratranao, ka mandringàna.",
			"12": "<sup>12</sup> Fa Andriamanitra no Mpanjakako hatrizay hatrizay; Manao famonjena eny amin’ny tany Izy.",
			"13": "<sup>13</sup> Nampisaraka ny ranomasina tamin’ny herinao Hianao; Efa namakivaky ny lohan’ny dragona tao anaty rano Hianao.",
			"14": "<sup>14</sup> Hianao nanorotoro ny lohan’ny mamba Ka nanome azy hohanin’izay firenena any an-efitra.",
			"15": "<sup>15</sup> Hianao namaky ny vatolampy hamoaka loharano sy ony; Hianao nahatankina ny ony tsy mety ritra.",
			"16": "<sup>16</sup> Anao ny andro, Anao koa ny alina; Hianao namboatra ny fanazavana, indrindra fa ny masoandro.",
			"17": "<sup>17</sup> Hianao nametra ny faritry ny tany rehetra; Ny fahavaratra sy ny ririnina, dia Hianao no nanao azy.",
			"18": "<sup>18</sup> Tsarovy izao: Manaratsy an’i Jehovah ny fahavalo, Ary ny olona adala manamavo ny anaranao.",
			"19": "<sup>19</sup> Aza manolotra ny ain’ny voromailalanao ho an’ny bibi-dia; Ary aza manadino mandrakizay ny ain’ny olo-mahantranao.",
			"20": "<sup>20</sup> Hevero ny fanekena; Fa feno fonenan’ny fandozana ny fitoera-maizina amin’ny tany.",
			"21": "<sup>21</sup> Aoka tsy hiverina amin-kenatra ny mahantra; Aoka ny ory sy ny malahelo hidera ny anaranao.",
			"22": "<sup>22</sup> Mitsangàna, Andriamanitra ô, efao ny adinao; Tsarovy ny fanaratsian’ny adala Anao isan-andro.",
			"23": "<sup>23</sup> Aza manadino ny feon’ny rafinao, Dia ny fitabataban’ny mpanohitra Anao, izay miakatra mandrakariva."
		},
		"75": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Al-tasheth. Salamo nataon’i Asafa. Tonon-kira. HISAORANAY Hianao, Andriamanitra ô; eny, hisaoranay Hianao; Fa akaiky ny anaranao; Mitory ny fahagagana ataonao ny olona.",
			"2": "<sup>2</sup> Fa ho avy tokoa amin’ny fotoana Aho; Eny, Izaho no hitsara marina.",
			"3": "<sup>3</sup> Mihorohoro ny tany sy ny mponina rehetra eo aminy, Nefa Izaho no nanorina ny andriny. [Sela.]",
			"4": "<sup>4</sup> Hoy izaho tamin’ny mpirehareha: Aza mirehareha, Ary tamin’ny ratsy fanahy: Aza manandra-tandroka;",
			"5": "<sup>5</sup> Aza manandratra ny tandrokareo ho avo; Ary aza miteny sahisahy amin’ny fahamafian-katoka.",
			"6": "<sup>6</sup> Fa tsy avy any atsinanana, na avy any andrefana, Na avy any atsimo, ny fisandratana.",
			"7": "<sup>7</sup> Fa Andriamanitra no Mpitsara; Mampietry ny anankiray Izy ary manandratra ny anankiray kosa.",
			"8": "<sup>8</sup> Fa misy kapoaka eo an-tànan’i Jehovah, Feno divay mandroatra miharoharo zavatra iny, ka ampisotroany; Eny, ny ratsy fanahy rehetra amin’ny tany dia hitroka ny faikany.",
			"9": "<sup>9</sup> Fa izaho hanambara izany mandrakizay Ka hankalaza an’Andriamanitr’i Jakoba.",
			"10": "<sup>10</sup> Ary ny tandroky ny ratsy fanahy rehetra hotapahiko; Fa ny tandroky ny marina kosa no hasandratra."
		},
		"76": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Hampiarahina amin’ny valiha. Salamo nataon’i Asafa. Tonon-kira. ANDRIAMANITRA dia fantatra eo amin’ny Joda; Malaza eo amin’ny Isiraely ny anarany;",
			"2": "<sup>2</sup> Naoriny ao Salema ny trano-lainy, Ary any Ziona ny fonenany.",
			"3": "<sup>3</sup> Teo no nanapatapahany ny zana-tsipìka manelatra Sy ny ampinga sy ny sabatra ary ny fiadiana. [Sela.]",
			"4": "<sup>4</sup> Mamirapiratra Hianao sady be voninahitra miseho avy any amin’ ny tendrombohitra fitohana.",
			"5": "<sup>5</sup> Voababo ny mahery fo; Renoky ny torimasony izy; Ary samy ankona tsy nahahetsika ny tànany avokoa ny olo-mahery rehetra.",
			"6": "<sup>6</sup> Noho ny teny mafy nataonao, ry Andriamanitr’i Jakoba, Dia sondrian-tory ny kalesy sy ny soavaly.",
			"7": "<sup>7</sup> Hianao no mahatahotra; Ka iza no mahajanona eo anatrehanao, rehefa miseho ny fahatezeranao?",
			"8": "<sup>8</sup> Avy tany an-danitra no nampandrenesanao fitsarana. Raiki-tahotra ny tany ka sina,",
			"9": "<sup>9</sup> Raha nitsangana hitsara Andriamanitra Mba hamonjy ny mpandefitra rehetra ambonin’ny tany. [Sela.]",
			"10": "<sup>10</sup> Fa ny fahatezeran’ny olona hidera Anao tokoa; Ny fahatezerana sisa dia isikinanao.",
			"11": "<sup>11</sup> Mivoadia, ka efao ny voadinareo amin’i Jehovah Andriamanitrareo; Aoka izay rehetra manodidina Azy hitondra fanatitra ho an’Izay mahatahotra.",
			"12": "<sup>12</sup> Manapaka ny reharehan’ny lehibe Izy Sady tahotry ny mpanjakan’ny tany."
		},
		"77": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Al-jedotona. Salamo natoan’i Asafa. NY feoko dia mankany amin’Andriamanitra, ka mitaraina aho, Eny, ny feoko mankany amin’Andriamanitra, ka mihaino ahy Izy.",
			"2": "<sup>2</sup> Tamin’ny andro fahoriako no nitadiavako ny Tompo; Ny tànako nasandratra nony alina ka tsy vizana; Ny fanahiko tsy nety nampiononina.",
			"3": "<sup>3</sup> Ta-hahatsiaro an’Andriamanitra aho, ka dia nisento; Te-hieritreritra aho, ka dia reraka ny fanahiko. [Sela.]",
			"4": "<sup>4</sup> Mampiari-tory ny masoko Hianao; Mitebiteby aho ka tsy mahateny.",
			"5": "<sup>5</sup> Nihevitra ny andro fahiny aho, Dia ny taon-dasa ela.",
			"6": "<sup>6</sup> Ta-hahatsiaro ny nitendreko valiha tamin’ny alina aho; Te-hidinidinika amin’ny foko aho, Ary ny fanahiko nanontany mafy hoe:",
			"7": "<sup>7</sup> Hanary mandrakizay va ny Tompo? Tsy hankasitraka intsony va Izy?",
			"8": "<sup>8</sup> Efa nitsahatra mandrakizay va ny famindrampony? Efa tapitra mandrakizay va ny teniny?",
			"9": "<sup>9</sup> Efa nanadino hamindra fo va Andriamanitra? Efa nohidiany an-katezerana va ny fiantrany? [Sela.]",
			"10": "<sup>10</sup> Ary hoy izaho: Izao no mahararirary ny foko, Dia ny niovan’ ny tànana ankavanan’ny Avo Indrindra.",
			"11": "<sup>11</sup> Hampahatsiaro ny asan’i Jehovah aho, Fa ta-hahatsiaro ny fahagagana nataonao fony taloha;",
			"12": "<sup>12</sup> Eny, hisaina ny asanao rehetra aho Ary hihevitra ny nataonao.",
			"13": "<sup>13</sup> Andriamanitra ô, amin’ny fitoerana masina ny làlanao; Iza no andriamani-dehibe tahaka an’Andriamanitra?",
			"14": "<sup>14</sup> Hianao no Andriamanitra Izay manao fahagagana; Efa nampahalala ny herinao teny amin’ny firenena Hianao.",
			"15": "<sup>15</sup> Navotan’ny sandrinao ny olonao, Dia ny taranak’i Jakoba sy Josefa. [Sela.]",
			"16": "<sup>16</sup> Ny rano be nahita Anao, Andriamanitra ô; Ny rano be nahita Anao ka toran-kovitra; Eny, nanonjanonja ny lalina.",
			"17": "<sup>17</sup> Nandatsaka ranonorana ny rahona; Nikotrokotroka ny lanitra; Nifanaretsaka koa ny zana-tsipìkanao.",
			"18": "<sup>18</sup> Ny fikotrokotroky ny kotrokorana dia teo amin’ny tadio; Nahazava izao rehetra izao ny helatra; Nihovotrovotra sy nihorohoro ny tany.",
			"19": "<sup>19</sup> Tany amin’ny ranomasina ny làlanao, Ary ny nalehanao dia tany amin’ny rano mandriaka, Ka tsy fantatra ny dian-tongotrao.",
			"20": "<sup>20</sup> Ny tànan’i Mosesy sy Arona No nitondranao ny olonao toy ny ondry."
		},
		"78": {
			"1": "<sup>1</sup> Maskila nataon’i Asafa. HENOY ny lalàko, ry oloko; Atongilano ny sofinareo amin’ny tenin’ny vavako.",
			"2": "<sup>2</sup> Hiloa-bava hilaza fanoharana aho; Hanonona teny saro-pantarina fony taloha aho.",
			"3": "<sup>3</sup> Izay efa rentsika sy fantatsika Ary nambaran’ny razantsika tamintsika",
			"4": "<sup>4</sup> Dia tsy mba hafenintsika amin’ny zanany; Fa hambarantsika amin’ny taranaka ho avy ny fiderana an’i Jehovah Sy ny heriny ary ny fahagagana izay efa nataony.",
			"5": "<sup>5</sup> Fa nanorina teni-vavolombelona teo amin’i Jakoba Izy, Ary teo amin’Isiraely no nametrahany lalàna, Izay nandidiany ny razantsika, Hampahafantariny ny zanany;",
			"6": "<sup>6</sup> Mba hahafantatra izany ny taranaka ho avy–dia ny zaza mbola hateraka– Ka hitsangana hitory izany amin’ny zanany kosa,",
			"7": "<sup>7</sup> Mba hametraka ny fanantenany amin’Andriamanitra Ka tsy hanadino ny asany, Fa hitandrina ny didiny;",
			"8": "<sup>8</sup> Mba tsy ho tahaka ny razany, izay taranaka maditra sy miodina, izy, Dia taranaka izay tsy nampiomana ny fony, Sady tsy marina tamin’Andriamanitra ny fanahiny.",
			"9": "<sup>9</sup> Ny zanak’i Efraima, izay nitondra tsipìka hitifirany, Dia niamboho tamin’ny andro fiadiana.",
			"10": "<sup>10</sup> Tsy nitandrina ny fanekena amin’Andriamanitra izy, Ary tsy nety nandeha araka ny lalàny;",
			"11": "<sup>11</sup> Nanadino ny asany izy Sy ny fahagagana izay nampahitany azy.",
			"12": "<sup>12</sup> Teo anatrehan’ny razany no nanaovany fahagagana Tany amin’ny tany Egypta, dia tany amin’ny tany lemak’i Zoana.",
			"13": "<sup>13</sup> Nampisaraka ny ranomasina Izy ka nitondra azy nita, Dia nampijanona ny rano ho toy ny antontan-javatra.",
			"14": "<sup>14</sup> Ary ny rahona no nitondrany azy raha antoandro, Sy afo mahazava nony alina.",
			"15": "<sup>15</sup> Namaky vatolampy tany an-efitra Izy Ka nampisotro azy nanaram-po tahaka ny amin’ny rano lalina.",
			"16": "<sup>16</sup> Ary nampivoaka rano mandriaka avy tamin’ny haram-bato Izy Ka nampirotsaka rano ho tahaka ny ony.",
			"17": "<sup>17</sup> Kanjo mbola nandroso nanota taminy ihany izy ireo Ka nampahatezitra ny Avo Indrindra tany an-efitra.",
			"18": "<sup>18</sup> Dia naka fanahy an’Andriamanitra tam-pony izy Tamin’ny nilany hanina araka ny nahaliana azy.",
			"19": "<sup>19</sup> Dia niteny nanohitra an’Andriamanitra izy ka nanao hoe: Mahavelatra latabatra atỳ an-efitra va Andriamanitra?",
			"20": "<sup>20</sup> Indro, namely ny vatolampy Izy, Dia nigororoana ny rano ka nandriaka; Mahay manome mofo koa va Izy? Mahatonga hena ho an’ny olony va Izy?",
			"21": "<sup>21</sup> Koa nony ren’i Jehovah izany, dia tezitra Izy; Ary nisy afo nirehitra tamin’i Jakoba, Sady nisy fahatezerana namely ny Isiraely,",
			"22": "<sup>22</sup> Satria tsy nino an’Andriamanitra izy ireo Ka tsy natoky ny famonjeny.",
			"23": "<sup>23</sup> Kanefa nandidy ny rahona tany ambony Izy, Ary ny varavaran’ny lanitra no novohany,",
			"24": "<sup>24</sup> Dia nandatsaka mana teny aminy hohaniny Izy, Ary varin’ny lanitra no nomeny azy.",
			"25": "<sup>25</sup> Ny fihinan’ny mahery no nohanin’ny olona; Nanaterany hanina hahavoky azy izy.",
			"26": "<sup>26</sup> Nampandeha ny rivotra avy any atsinanana teny amin’ny lanitra Izy; Ary ny heriny no nitondrany ny rivotra avy any atsimo.",
			"27": "<sup>27</sup> Dia nampilatsahany hena betsaka hoatra ny vovoka izy Sy voro-manidina hoatra ny fasiky ny ranomasina,",
			"28": "<sup>28</sup> Ka nalatsany teny afovoan’ny tobiny, Manodidina ny fitoerany.",
			"29": "<sup>29</sup> Dia nihinana izy ireo ka voky tsara; Ary izay nilainy dia nomeny azy.",
			"30": "<sup>30</sup> Nefa tsy niala tamin’ny filany izy, Ary mbola teo am-bavany ihany ny haniny;",
			"31": "<sup>31</sup> Dia nirehitra taminy ny fahatezeran’Andriamanitra, Ka nahafatesany ny tremalahy teo aminy, Ary naripany ny zatovolahin’ny Isiraely.",
			"32": "<sup>32</sup> Fa na dia izany rehetra izany aza, dia mbola nanota ihany izy ireo Ka tsy nino ny fahagagana nataony.",
			"33": "<sup>33</sup> Dia nampahalevona ny androny toy ny fofonaina Izy, Ary ny taonany tamin’ny fampitahorana.",
			"34": "<sup>34</sup> Raha novonoiny izy ireo, dia nitady hahalala an’Andriamanitra izy Ary niverina nikatsaka Azy.",
			"35": "<sup>35</sup> Dia nahatsiaro izy fa Andriamanitra no Vatolampiny, Ary Andriamanitra Avo Indrindra no Mpanavotra azy.",
			"36": "<sup>36</sup> Kanefa nanao vava malefaka taminy ireo, Ary ny lelany no nandaingany taminy.",
			"37": "<sup>37</sup> Ary ny fony tsy niorina taminy; Eny, tsy nitana ny fanekeny izy.",
			"38": "<sup>38</sup> Nefa Andriamanitra kosa be famindrampo, Dia mamela heloka ka tsy mandringana; Eny, nahatsindry fo tokoa Izy, Ka tsy nofohaziny avokoa ny fahavinirany.",
			"39": "<sup>39</sup> Fa nahatsiaro Izy fa nofo ihany ireo, Dia rivotra izay mandalo ka tsy miverina intsony.",
			"40": "<sup>40</sup> Impiry akory izy ireo no efa niodina taminy tany an-efitra Ka nampalahelo Azy tany amin’ny tany foana!",
			"41": "<sup>41</sup> Naka fanahy an’Andriamanitra mandrakariva izy Ka nampalahelo ny Iray Masin’ny Isiraely.",
			"42": "<sup>42</sup> Tsy mba nahatsiaro ny tànany izy, Dia ny andro izay nanavotany azy tamin’ny fahavalo,",
			"43": "<sup>43</sup> Izay nanaovany famantarana tany Egypta Sy fahagagana tany an-tany lemak’i Zoana:",
			"44": "<sup>44</sup> Nampody ny oniny ho ra Izy, Ary ny reniranony tsy azony nisotroana.",
			"45": "<sup>45</sup> Nandefa lalitra betsaka hamely azy Izy, ka nandany azy ireny, Ary sahona, ka nanimba azy ireny.",
			"46": "<sup>46</sup> Dia nanome ny vokatry ny taniny ho an’ny sompanga Izy Ary ny asany ho an’ny valala.",
			"47": "<sup>47</sup> Namono ny voalobony tamin’ny havandra Izy Ary ny aviaviny tamin’ny havandra vaventy.",
			"48": "<sup>48</sup> Dia nanolotra ny biby fiompiny ho an’ny havandra Izy, Ary ny omby aman-ondriny ho an’ny varatra.",
			"49": "<sup>49</sup> Nandatsaka ny fahatezerany mirehitra taminy Izy: Eny, fahavinirana sy fahatezerana ary fampahoriana, Dia anjely andiany mitondra loza.",
			"50": "<sup>50</sup> Namboatra làlana ho an’ny fahatezerany Izy, Ary tsy niaro ny fanahin’ireo tamin’ny fahafatesana, Fa nanolotra ny ainy ho lanin’ny areti-mandringana;",
			"51": "<sup>51</sup> Ary namely ny lahimatoa rehetra tany Egypta Izy, Dia ny voalohan’ainy tany an-dain’i Hama;",
			"52": "<sup>52</sup> Fa namindra toerana ny olony toy ny ondry Izy, Ary nitondra azy tany an-efitra toy ny ondry andiany.",
			"53": "<sup>53</sup> Nentiny soa aman-tsara izy ireo ka tsy mba natahotra; Fa ny fahavalony kosa voasaron’ny ranomasina.",
			"54": "<sup>54</sup> Ary nitondra azy ho any amin’ny tany misy ny fitoerany masina Izy, Dia ny tendrombohitra izay azon’ny tànany ankavanana.",
			"55": "<sup>55</sup> Ary nandroaka ny jentilisa teo anoloany Izy Ka nanome ny tanin’ireny ho lova nozaraina tamin’ny famolaina, Dia nampitoetra ny firenen’Isiraely tao an-dainy.",
			"56": "<sup>56</sup> Nefa naka fanahy an’Andriamanitra Avo Indrindra izy ka niodina taminy, Ary ny teni-vavolombelony tsy notandremany.",
			"57": "<sup>57</sup> Ary nihemotra izy ka nivadika tahaka ny razany. Dia nania tahaka ny tsipìka manjiona.",
			"58": "<sup>58</sup> Ary nampahatezitra Azy tamin’ny fitoerana avony izy, Sady nahasaro-piaro Azy tamin’ny sarin-javatra voasokitra.",
			"59": "<sup>59</sup> Ary nandre izany Andriamanitra, dia tezitra Izy, Ka nolaviny mihitsy ny Isiraely,",
			"60": "<sup>60</sup> Sady nahafoy ny tabernakeliny tao Silo Izy, Dia ny lay izay naoriny teo amin’ny olona;",
			"61": "<sup>61</sup> Dia natolony ho babo ny heriny, Ary ny voninahiny ho eo an-tànan’ny fahavalo.",
			"62": "<sup>62</sup> Dia nanolotra ny olony ho an’ny sabatra Izy; Ary tezitra indrindra tamin’ny lovany Izy.",
			"63": "<sup>63</sup> Ny zatovolahiny dia lanin’ny afo; Ary ny zatovovaviny tsy mba nanaovana hiram-panambadiana.",
			"64": "<sup>64</sup> Ny mpisorony lavon-tsabatra; Nefa ny mpitondratenan’ireo tsy nisaona.",
			"65": "<sup>65</sup> Dia nifoha ny Tompo tahaka ny anankiray avy natory, Tahaka ny lehilahy mahery izay miantsoantso azon’ny divay.",
			"66": "<sup>66</sup> Dia namely ny fahavalony hiamboho Izy Ka nahafa-baraka azy mandrakizay.",
			"67": "<sup>67</sup> Dia nanary ny lain’i Josefa Izy, Ary ny firenen’i Efraima tsy nofidiny.",
			"68": "<sup>68</sup> Fa nifidy ny firenen’i Joda kosa Izy, Dia ny tendrombohitra Ziona izay tiany.",
			"69": "<sup>69</sup> Dia nanao ny fitoerany masina ho tahaka ny havoana Izy, Tahaka ny tany izay naoriny ho mandrakizay.",
			"70": "<sup>70</sup> Dia nifidy an’i Davida mpanompony Izy Ka naka azy avy tany am-balan’ondry;",
			"71": "<sup>71</sup> Tany amin’ny fiandrasany ny ondry ampianahana no nakany azy Mba hiandry an’i Jakoba olony Sy Isiraely lovany;",
			"72": "<sup>72</sup> Dia niandry ireo araka ny hitsin’ny fony izy Ka nitondra azy araka ny fahaizan’ny tànany."
		},
		"79": {
			"1": "<sup>1</sup> Salamo nataon’i Asafa. ANDRIAMANITRA ô, tonga eto amin’ny lovanao ny jentilisa Ka nandoto ny tempolinao masina Sady nanao an’i Jerosalema ho korontam-bato.",
			"2": "<sup>2</sup> Ny fatin’ny mpanomponao nomeny hehanin’ny voro-manidina, Ny nofon’ny olonao masina ho an’ny bidi-dia;",
			"3": "<sup>3</sup> Ny rany naidiny manodidina an’i Jerosalema tahaka ny rano, Ary tsy nisy handevina.",
			"4": "<sup>4</sup> Tonga fandàtsan’ny namanay izahay Sady faneso sy fanakòran’izay manodidina anay.",
			"5": "<sup>5</sup> Mandra-pahoviana, Jehovah ô, no hanontolo fo Hianao, Ka hirehitra tahaka ny afo ny fahasaro-piaronao?",
			"6": "<sup>6</sup> Aidino amin’ny jentilisa izay tsy mahalala Anao ny fahatezeranao Sy amin’ny fanjakana izay tsy miantso ny anaranao;",
			"7": "<sup>7</sup> Fa efa nandany an’i Jakoba izy ireo Sady nandrava ny fonenany.",
			"8": "<sup>8</sup> Aza tsarovana aminay ny heloky ny razanay; Aoka ho avy faingana hitsena anay ny fiantranao, Fa reraka indrindra izahay.",
			"9": "<sup>9</sup> Vonjeo izahay, ry Andriamanitry ny famonjena anay ô, noho ny voninahitry ny anaranao; Ary afaho izahay, ka mamelà ny helokay, noho ny anaranao.",
			"10": "<sup>10</sup> Nahoana ny jentilisa no hanao hoe: Aiza izay Andriamaniny? Aoka ho fantatra amin’ny jentilisa eo imasonay Ny famaliana ny ran’ny mpanomponao izay nalatsaka.",
			"11": "<sup>11</sup> Aoka ho tonga eo anatrehanao ny fisentoan’ny mpifatotra; Araka ny halehiben’ny sandrinao dia arovy tsy ho faty ny zanaky ny fahafatesana.",
			"12": "<sup>12</sup> Ary valio impito heny ho ao an-tratran’ny mifanila fonenana aminay Ny fanaratsiana izay nanaratsiany Anao, Tompo ô.",
			"13": "<sup>13</sup> Fa izahay, olonao sy ondry fiandrinao, Dia hisaotra Anao mandrakizay; Hatramin’ny taranaka fara mandimby no hilazanay ny fiderana Anao."
		},
		"80": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Al-shoshanim-edoth. Salamo nataon’i Asafa. RY Mpiandry ny Isiraely ô, mihainoa, Hianao Izay mitondra an’i Josefa toy ny ondry; Hianao, Izay mipetraka amin’ny kerobima, mamirapirata.",
			"2": "<sup>2</sup> Fohazy ny herinao Eo anoloan’i Efraima sy Benjamina ary Manase, Ka avia hamonjy anay.",
			"3": "<sup>3</sup> Andriamanitra ô, ampodio izahay, Ka ampamirapirato ny tavanao, ary aoka hovonjena izahay.",
			"4": "<sup>4</sup> Jehovah, Andriamanitry ny maro, ô, Mandra-pahoviana no hanetroka amin’ny fivavaky ny olonao ny fahatezeranao?",
			"5": "<sup>5</sup> Mampihinana azy mofo len-dranomaso Hianao, Ary mampisotro azy ranomaso tovozina.",
			"6": "<sup>6</sup> Ataonao ho fifandirana amin’ny mifanila fonenana aminay izahay; Ary ny fahavalonay mifaly handatsa.",
			"7": "<sup>7</sup> Andriamanitry ny maro ô, ampodio izahay, Ka ampamirapirato ny tavanao, ary aoka hovonjena izahay.",
			"8": "<sup>8</sup> Nisy voaloboka nafindranao avy tany Egypta; Nandroaka ny jentilisa Hianao ka namboly azy kosa.",
			"9": "<sup>9</sup> Nitevy teo alohany Hianao, Dia namaka lalina izy ka nameno ny tany.",
			"10": "<sup>10</sup> Nanaloka ny tendrombohitra ny alokalony, Ary ny sampany nanaloka ny sedera andrian-kazo.",
			"11": "<sup>11</sup> Nampisandrahaka ny rantsany hatramin’ny ranomasina izy, Ary ny sakeliny hatramin’ny Ony.",
			"12": "<sup>12</sup> Nahoana no noravanao ny fefiny, Ka anotazan’izay rehetra mandalo eny an-dàlana izy?",
			"13": "<sup>13</sup> Ny lambo avy any an-ala mitrongy azy. Ary izay bibi-dia any an-tsaha mandany azy.",
			"14": "<sup>14</sup> Ry Andriamanitry ny maro ô, miverena, ka tsinjovy any an-danitra, ary jereo, Ka vangio ity voaloboka ity.",
			"15": "<sup>15</sup> Arovy izay nambolen’ny tànanao ankavanana Sy ny zanaka izay nampaherezinao ho Anao.",
			"16": "<sup>16</sup> Voadotry ny afo izy sady voakapa; Fongotry ny fananaran’ny tavanao izy.",
			"17": "<sup>17</sup> Aoka ny tànanao ho amin’ny lehilahin’ny tànanao ankavanana, Dia amin’ny zanak’olombelona izay nampaherezinao ho Anao.",
			"18": "<sup>18</sup> Dia tsy hihemotra hiala aminao izahay; Velomy izahay, dia hiantso ny anaranao.",
			"19": "<sup>19</sup> Ry Jehovah, Andriamanitry ny maro, ô, ampodio izahay, Ampamirapirato ny tavanao, ary aoka hovonjena izahay."
		},
		"81": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Al-gitith. Nataon’i Asafa. MIHOBIA ho an’Andriamanitra herintsika; Manaova feo fifaliana ho an’Andriamanitr’i Jakoba.",
			"2": "<sup>2</sup> Asandrato ny hira, ka velezo ny ampongatapaka, Ary tendreo ny lokanga mahafinaritra mbamin’ny valiha.",
			"3": "<sup>3</sup> Tsofy ny anjomara amin’ny volana; Eny, amin’ny feno-volana, dia amin’ny andro firavoravoantsika.",
			"4": "<sup>4</sup> Fa lalàna ho an’ny Isiraely izany, Ary fitsipik’Andriamanitr’i Jakoba.",
			"5": "<sup>5</sup> Notendreny ho vavolombelona eo amin’i Josefa izany, Raha nandeha namely ny tany Egypta Izy; Nandre teny tsy fantatro aho.",
			"6": "<sup>6</sup> Nampiala ny sorony tamin’ny entana Aho; Nitsahatra tsy nitondra sobiky ny tànany.",
			"7": "<sup>7</sup> Fony azom-pahoriana hianao, dia niantso Ahy, ka namonjy anao Aho; Namaly anao tao amin’ny fiafenan’ny kotrokorana Aho Ary nizaha toetra anao tao amin’ny ranon’i Meriba. [Sela.]",
			"8": "<sup>8</sup> Mihainoa, ry oloko, dia hambarako aminao; Enga anie hihaino Ahy hianao, ry Isiraely ô;",
			"9": "<sup>9</sup> Aza asiana andriamani-kafa eo aminao; Ary aza mivavaka amin’ny andriamanitry ny firenena hafa.",
			"10": "<sup>10</sup> Izaho no Jehovah Andriamanitrao, Izay nitondra anao niakatra avy tany amin’ny tany Egypta; Atanatanao ny vavanao, dia hofenoiko.",
			"11": "<sup>11</sup> Fa tsy nihaino ny feoko ny oloko; Ary ny Isiraely tsy nankatò Ahy;",
			"12": "<sup>12</sup> Ka dia navelako hanaraka ny ditry ny fony izy, Handehanany manaraka ny fisainan’ny tenany.",
			"13": "<sup>13</sup> Enga anie ka hihaino Ahy ny oloko, Dia handeha amin’ny làlako ny Isiraely;",
			"14": "<sup>14</sup> Vetivety dia hampietry ny fahavalony Aho Ka hamerina ny tànako hamely ny rafiny!",
			"15": "<sup>15</sup> Izay mankahala an’i Jehovah dia hikoy aminy, Ka dia haharitra mandrakizay ny andron’ny oloko;",
			"16": "<sup>16</sup> Ary hampihinana azy vary tsara indrindra Izy; Eny, tantely avy amin’ny vatolampy no hamokisako anao."
		},
		"82": {
			"1": "<sup>1</sup> Salamo nataon’i Asafa. ANDRIAMANITRA dia eo amin’ny fiangonan’Andriamanitra; Ary eo amin’ireo andriamanitra no itsarany.",
			"2": "<sup>2</sup> Mandra-pahoviana no hitsaranareo tsy marina, Ka ihenaranareo maso ny ratsy fanahy? [Sela.]",
			"3": "<sup>3</sup> Alaharo ny tenin’ny mahantra sy ny kamboty; Omeo rariny ny ory sy ny malahelo.",
			"4": "<sup>4</sup> Vonjeo ny mahantra sy ny malahelo; Afaho amin’ny tànan’ny ratsy fanahy izy.",
			"5": "<sup>5</sup> Tsy mahalala na mahazo an-tsaina ireny; Ao amin’ny maizina no andehanany; mihorohoro ny fanambanin’ny tany rehetra.",
			"6": "<sup>6</sup> Hoy izaho: Andriamanitra hianareo, Ary zanaky ny Avo Indrindra hianareo rehetra;",
			"7": "<sup>7</sup> kanjo ho faty tahaka ny olona ihany hianareo, Ary samy ho lavo tahaka ny anankiray amin’ny lehibe.",
			"8": "<sup>8</sup> Mitsangàna, Andriamanitra ô, tsarao ny tany; Fa Hianao no handova ny firenena rehetra."
		},
		"83": {
			"1": "<sup>1</sup> Tonon-kira. Salamo nataon’i Asafa. ANDRIAMANITRA ô, aza mangina; Aza mihombom-bava Hianao, ary aza mamitsaka, Andriamanitra ô.",
			"2": "<sup>2</sup> Fa, indro, mitabataba ny fahavalonao; Ary izay mankahala Anao dia manandratra ny lohany.",
			"3": "<sup>3</sup> Ny olonao no iokoany amin’ny hafetsena; Ary miara-misaina hamely izay voàfinao izy.",
			"4": "<sup>4</sup> Hoy izy: Avia, ka andeha hofongorantsika tsy ho firenena izy, Mba tsy hotsarovana intsony ny anaran’Isiraely.",
			"5": "<sup>5</sup> Fa efa niray tetika tokoa ireo Ka nanao fanekena hanohitra Anao,–",
			"6": "<sup>6</sup> Dia ny lain’ny Edomita sy ny Isimaelita, Ny Moabita sy ny Hagarita,",
			"7": "<sup>7</sup> Ny Gebalita sy ny Amonita ary ny Amalekita, Ny Filistina mbamin’ny mponina any Tyro,",
			"8": "<sup>8</sup> Ny Asyriana koa nikambana taminy; Tonga sandrin’ny taranak’i Lota ireo. [Sela.]",
			"9": "<sup>9</sup> Ataovy tahaka ny Midiana izy, Ary tahaka an’i Sisera sy Jabina teo amin’ny ony Kisona;",
			"10": "<sup>10</sup> Maty tany En-dora ireo; Tonga zeziky ny tany izy.",
			"11": "<sup>11</sup> Ataovy tahaka an’i Oreba sy Zeba ny mpanapaka azy, Eny, ny andrianany rehetra dia ataovy tahaka an’i Zebaha sy Zalmona,",
			"12": "<sup>12</sup> Izay nanao hoe: Aoka halaintsika Ho antsika ny fonenan’Andriamanitra.",
			"13": "<sup>13</sup> Andriamanitro ô, ataovy tahaka ny tadio ireo, Dia tahaka ny akofa entin’ny rivotra,",
			"14": "<sup>14</sup> Tahaka ny afo mandoro ala Sy tahaka ny lelafo mampirehitra tendrombohitra;",
			"15": "<sup>15</sup> Aoka ho toy izany no anafotofotoanao azy amin’ny tadionao, Sy ampahatahoranao azy amin’ny tafio-drivotrao.",
			"16": "<sup>16</sup> Saròny henatra ny tavany, Mba hitadiavany ny anaranao, Jehovah ô.",
			"17": "<sup>17</sup> Aoka ho menatra sy ho azon-tahotra mandrakizay izy; Eny, aoka hangaihay sy haringana izy.",
			"18": "<sup>18</sup> Ary aoka ho fantany fa Hianao irery ihany, Izay atao hoe Jehovah, No Ilay Avo Indrindra ambonin’ny tany rehetra."
		},
		"84": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Al-hagitith. Salamo nataon’ny Koraïta. ENDREY ny hatsaran’ny tabernakelinao, Jehovah, Tompon’ny maro, ô!",
			"2": "<sup>2</sup> Manina sady vonton’alahelo ny kianjan’i Jehovah ny fanahiko; Ny foko sy ny nofoko mihoby an’Andriamanitra velona.",
			"3": "<sup>3</sup> Eny, ny tsikirity mahita trano, Ary ny sidintsidina mahita akany ho azy hametrahany ny zanany, Dia ny alitaranao, Jehovah, Tompon’ny maro, ô, Mpanjakako sy Andriamanitro.",
			"4": "<sup>4</sup> Sambatra izay mitoetra ao an-tranonao; Hidera Anao mandrakariva izy. [Sela.]",
			"5": "<sup>5</sup> Sambatra izay olona manan-kery avy aminao; Ao am-pony ny làlana.",
			"6": "<sup>6</sup> Raha mandeha mamaky ny lohasaha Baka izy, Dia ataony misy loharano izany, ary ny loha-orana manarom-pitahiana azy.",
			"7": "<sup>7</sup> Miampy hery eny am-pandehanana izy; Samy miseho eo anatrehan’Andriamanitra any Ziona izy rehetra.",
			"8": "<sup>8</sup> Jehovah, Andriamanitry ny maro, ô, henoy ny fivavako; Mandrenesa, ry Andriamanitr’i Jakoba ô. [Sela.]",
			"9": "<sup>9</sup> Andriamanitra, Ampinganay, ô, tsinjovy, Ka jereo ny tavan’ny voahosotrao.",
			"10": "<sup>10</sup> Fa ny indray andro eo an-kianjanao dia tsara noho ny arivo; Aleoko mipetraka eo amin’ny tokonam-baravaran’ny tranon’Andriamanitra, Toy izay mitoetra ao an-dain’ny ratsy fanahy.",
			"11": "<sup>11</sup> Fa Masoandro sy Ampinga Jehovah Andriamanitra; Fahasoavana sy voninahitra no homen’i Jehovah; Tsy hiaro zava-tsoa amin’izay mandeha amin’ny fahitsiana Izy.",
			"12": "<sup>12</sup> Jehovah, Tompon’ny maro, ô, Sambatra izay olona matoky Anao."
		},
		"85": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Salamo nataon’ny Koraïta. JEHOVAH ô, efa naneho fitia tamin’ny taninao Hianao, Efa nampodinao Jakoba avy tamin’ny fahababoany.",
			"2": "<sup>2</sup> Voaesotrao ny heloky ny olonao, Voasaronao ny fahotany rehetra. [Sela.]",
			"3": "<sup>3</sup> Efa nampijanona ny fahatezeranao rehetra Hianao Ary niala tamin’ny fahatezeranao mirehitra.",
			"4": "<sup>4</sup> Miverena aminay, ry Andriamanitry ny famonjena anay ô, Ary ampitsaharo ny fahatezeranao aminay.",
			"5": "<sup>5</sup> Ho tezitra aminay mandrakizay va Hianao? Hampaharetinao hahatratra ny taranaka fara mandimby va ny fahatezeranao?",
			"6": "<sup>6</sup> Tsy hovelominao indray va izahay, Mba hifalian’ny olonao aminao?",
			"7": "<sup>7</sup> Asehoy anay ny famindramponao, Jehovah ô, Ka omeo anay ny famonjenao.",
			"8": "<sup>8</sup> Hihaino izay holazain’i Jehovah Andriamanitra aho; Fa hilaza fiadanana amin’ny olony Izy, dia amin’ny olony masina; Koa aoka tsy hiverina ho ao amin’ny fahadalana ireo.",
			"9": "<sup>9</sup> Eny tokoa, akaikin’izay matahotra Azy ny famonjeny, Mba hitoeran’ny voninahitra eo amin’ny tanintsika.",
			"10": "<sup>10</sup> Mihaona ny famindrampo sy ny fahamarinana; Mifanoroka ny rariny sy ny fihavanana.",
			"11": "<sup>11</sup> Ny fahamarinana mitsimoka amin’ny tany; Ary ny rariny mitsinjo eny an-danitra.",
			"12": "<sup>12</sup> Eny, Jehovah hanome ny tsara; Ary ny tanintsika hahavoka-javatra.",
			"13": "<sup>13</sup> Ny rariny no hialoha Azy Sady hizotra hanaraka ny diany eny an-dàlana."
		},
		"86": {
			"1": "<sup>1</sup> Fivavak’i Davida. ATONGILANO ny sofinao, Jehovah ô, ka valio aho, Fa ory sy malahelo.",
			"2": "<sup>2</sup> Arovy ny fanahiko, fa masina aho; Andriamanitro ô, vonjeo ny mpanomponao, Izay matoky Anao.",
			"3": "<sup>3</sup> Tompo ô, mamindrà fo amiko; Fa Hianao no itarainako mandrakariva.",
			"4": "<sup>4</sup> Ampifalio ny fanahin’ny mpanomponao; Fa Hianao, Tompo ô, no ananganako ny fanahiko.",
			"5": "<sup>5</sup> Fa Hianao, Tompo ô, no tsara sady mamela heloka Ary be famindrampo amin’izay rehetra miantso Anao.",
			"6": "<sup>6</sup> Mandrenesa ny fivavako, Jehovah ô, Ary henoy ny feon’ny fifonako.",
			"7": "<sup>7</sup> Amin’ny andro fahoriako dia miantso Anao aho, Fa mamaly ahy Hianao.",
			"8": "<sup>8</sup> Tsy misy tahaka Anao, Tompo ô, amin’ireo andriamanitra; Ary tsy misy tahaka ny asanao.",
			"9": "<sup>9</sup> Ny firenena rehetra nataonao dia ho avy hiankohoka eo anatrehanao, Tompo ô, Ka hanome voninahitra ny anaranao.",
			"10": "<sup>10</sup> Fa lehibe Hianao ka manao zava-mahagaga; Hianao irery ihany no Andriamanitra.",
			"11": "<sup>11</sup> Ampianaro ny làlanao aho, Jehovah ô, Dia handeha amin’ny fahamarinanao aho; Ampiraiso ny foko hatahotra ny anaranao.",
			"12": "<sup>12</sup> Hidera Anao amin’ny foko rehetra aho, Andriamanitro Tompo ô; Hanome voninahitra ny anaranao mandrakizay aho.",
			"13": "<sup>13</sup> Fa lehibe ny famindramponao amiko; Ary efa novonjenao ny fanahiko tsy ho any amin’ny fiainan-tsi-hita ambany indrindra.",
			"14": "<sup>14</sup> Andriamanitra ô, ny mpirehareha mitsangana hanohitra ahy, Ary ny antokon’ny lozabe mitady ny aiko; Ary tsy mba heveriny fa eo anatrehany Hianao.",
			"15": "<sup>15</sup> Fa Hianao, Tompo ô, dia Andriamanitra mamindra fo sy miantra, Mahari-po sady be famindrampo sy fahamarinana.",
			"16": "<sup>16</sup> Todiho aho ka amindrao fo; Omeo ny herinao ny mpanomponao, Ary vonjeo ny zanaky ny ankizivavinao.",
			"17": "<sup>17</sup> Anehoy famantarana ny ho soa aho, Mba ho hitan’izay mankahala ahy, ka ho menatra izy; Fa Hianao, Jehovah ô, no namonjy sy nampionona ahy."
		},
		"87": {
			"1": "<sup>1</sup> Salamo nataon’ny Koraïta. Tonon-kira. NY naorin’i Jehovah dia ao an-tendrombohitra masina;",
			"2": "<sup>2</sup> Ny vavahadin’i Ziona dia tian’i Jehovah Mihoatra noho ny fonenana rehetra izay an’i Jakoba.",
			"3": "<sup>3</sup> Zava-malaza no anambarana anao, Ry tanànan’Andriamanitra. [Sela.]",
			"4": "<sup>4</sup> Hanonona an-dRahaba sy Babylona Aho ho isan’izay mahalala Ahy; Indro Filistia sy Tyro mbamin’i Etiopia,– Ity no efa teraka teo.",
			"5": "<sup>5</sup> Ary Ziona hilazana hoe: Ireto sy ireroa samy efa teraka teo; Ary ny Avo Indrindra no mampitoetra azy.",
			"6": "<sup>6</sup> Jehovah hanisa, raha manoratra ny firenena Izy: Ity no efa teraka teo. [Sela.]",
			"7": "<sup>7</sup> Hoy izay mihira sy mandihy: Ny loharanoko rehetra dia ao aminao."
		},
		"88": {
			"1": "<sup>1</sup> Tonon-kira. Salamo nataon’ny Koraïta. Ho an’ny mpiventy hira. Al-mahalath-leanoth. Maskila nataon’i Hemana Ezrahita. JEHOVAH ô, Andriamanitry ny famonjena ahy, Andro aman-alina no itarainako eo anatrehanao;",
			"2": "<sup>2</sup> Aoka ny fivavako ho tonga eo anatrehanao; Atongilano amin’ny fitarainako ny sofinao;",
			"3": "<sup>3</sup> Fa diboka ny fahoriana ny fanahiko; Ary ny aiko manakaiky ny fiainan-tsi-hita.",
			"4": "<sup>4</sup> Natao ho isan’izay midina any an-davaka aho Ary efa tahaka izay tsy manan-kery;",
			"5": "<sup>5</sup> Efa nafoy ho any amin’ny maty aho, Tahaka izay voavono, mandry ao am-pasana, Izay tsy tsarovanao intsony, Fa voafongotry ny tànanao.",
			"6": "<sup>6</sup> Efa nametraka ahy any an-davaka ambany indrindra Hianao, Ao amin’ny maizina, dia ao amin’ny lalina.",
			"7": "<sup>7</sup> Ny fahatezeranao manindry ahy mafy, Ary ny alon-dranonao rehetra azeranao amiko. [Sela.]",
			"8": "<sup>8</sup> Efa nampanalavirinao ahy ny olona nahazatra ahy; Efa nataonao fahavetavetana eo imasony aho; Voahidy aho ka tsy afa-mivoaka.",
			"9": "<sup>9</sup> Ny masoko efa pahina noho ny fahoriana; Jehovah ô, miantso Anao isan-andro aho, Mamefy tànana aminao aho.",
			"10": "<sup>10</sup> Haneho fahagagana amin’ny maty va Hianao? Ny matoatoa va no hitsangana hidera Anao? [Sela.]",
			"11": "<sup>11</sup> Hotorina any am-pasana va ny famindramponao, Ary ny fahamarinanao any amin’ny fandringanana?",
			"12": "<sup>12</sup> Ho fantatra ao amin’ny maizina va ny fahagagana ataonao, Ary ny fahamarinanao ao amin’ny tany fanadinoana?",
			"13": "<sup>13</sup> Fa raha izaho, dia Hianao no itarainako, Jehovah ô; Ary ny fivavako tonga eo anatrehanao nony maraina.",
			"14": "<sup>14</sup> Jehovah ô, nahoana no manary ny fanahiko Hianao Ka manafina ny tavanao amiko?",
			"15": "<sup>15</sup> Ory aho ka efa ho faty hatramin’ny fahazazako; Raha itambesaran’ny fampitahoranao aho, dia very hevitra;",
			"16": "<sup>16</sup> Manafotra ahy ny fahatezeranao; Matin’ny fampitahoranao aho;",
			"17": "<sup>17</sup> Mandifotra ahy mandrakariva tahaka ny rano ireny; Miara-manodidina ahy izy.",
			"18": "<sup>18</sup> Efa nampanalavirinao ahy ny olona tia ahy sy ny sakaizako; Ireo nahazatra ahy–dia fahamaizinana."
		},
		"89": {
			"1": "<sup>1</sup> Maskila nataon’i Etana Ezrahita. NY famindrampon’i Jehovah no hohiraiko mandrakizay; Hatramin’ny taranaka fara mandimby no hampahafantaran’ny vavako ny fahamarinanao.",
			"2": "<sup>2</sup> Fa hoy izaho: Hotovonana mandrakizay ny famindrampo; Ny fahamarinanao hampitoerinao any an-danitra.",
			"3": "<sup>3</sup> ,,Efa nanao fanekena tamin’ny voafidiko Aho; Eny, efa nianiana tamin’i Davida mpanompoko Aho hoe:",
			"4": "<sup>4</sup> Hampitoetra ny taranakao ho mandrakizay Aho Ka hanangana ny seza fiandriananao hatramin’ny taranaka fara mandimby’’ [Sela.]",
			"5": "<sup>5</sup> Ary ny lanitra hidera ny fahagagana ataonao, Jehovah ô, Ary ny fahamarinanao koa ao amin’ny fiangonan’ny olomasina.",
			"6": "<sup>6</sup> Fa iza any an-danitra no mitovy amin’i Jehovah? Ary iza amin’ny zanak’ireo andriamanitra no manahaka an’i Jehovah,",
			"7": "<sup>7</sup> Dia Andriamanitra mahatahotra indrindra eo amin’ny fiangonan’ny olo-masiny Ary hajain’izay rehetra manodidina Azy?",
			"8": "<sup>8</sup> Jehovah, Andriamanitry ny maro, ô, iza no mahery tahaka Anao, Jehovah? Ary manodidina Anao ny fahamarinanao.",
			"9": "<sup>9</sup> Hianao manapaka ny fisavoan’ny ranomasina; Raha manonja ny alony, dia mampitsahatra azy Hianao.",
			"10": "<sup>10</sup> Hianao nanorotoro an-dRahaba ho tahaka izay voavono; Ny sandrinao mahery no nampielezanao ny fahavalonao.",
			"11": "<sup>11</sup> Anao ny lanitra, Anao koa ny tany; Izao tontolo izao sy izay rehetra eo aminy, Hianao no nanorina azy.",
			"12": "<sup>12</sup> Ny avaratra sy ny atsimo, Hianao no nahary azy; Ny anaranao no ihobian’i Tabara sy Hermona.",
			"13": "<sup>13</sup> Manan-tsandry mahery Hianao; Mahery ny tananao, avo ny tànanao ankavanana.",
			"14": "<sup>14</sup> Rariny sy fitsarana no fanorenan’ny seza fiandriananao; Famindrampo sy fahamarinana no mialoha ny tavanao.",
			"15": "<sup>15</sup> Sambatra izay olona mahalala ny feo mahafaly; Jehovah ô, amin’ny fahazavan’ny tavanao no handehanany.",
			"16": "<sup>16</sup> Ny anaranao no hifaliany mandrakariva; Ary ny fahamarinanao no hisandratany.",
			"17": "<sup>17</sup> Fa voninahitry ny heriny Hianao; Ary amin’ny fankasitrahanao no hisandratan’ny tandrokay.",
			"18": "<sup>18</sup> Fa an’i Jehovah ny ampinganay; Ary an’ny Iray Masin’ny Isiraely ny mpanjakanay.",
			"19": "<sup>19</sup> Tamin’izany ny olonao masina dia nilazanao tamin’ny fahitana hoe: Nametraka famonjena tamin’izay mahery Aho Ary nanandratra ny anankiray voafidy tamin’ny olona.",
			"20": "<sup>20</sup> Efa nahita an’i Davida mpanompoko Aho; Ny diloiloko masina no nanosorako azy;",
			"21": "<sup>21</sup> Izy no homban’ny tànako; Eny, ny sandriko no hampahery azy.",
			"22": "<sup>22</sup> Tsy hamitaka azy ny fahavalo; Ary tsy hampahory azy ny ratsy fanahy.",
			"23": "<sup>23</sup> Fa handripaka ny fahavalony eo anoloan’ny tavany Aho Sady hamely izay mankahala azy.",
			"24": "<sup>24</sup> Ny fahamarinako sy ny famindrampoko homba azy; Ary ny anarako no hisandratan’ny tandrony.",
			"25": "<sup>25</sup> Dia hampitoetra ny tànany amin’ny ranomasina Aho Ary ny tànany ankavanana amin’ny ony.",
			"26": "<sup>26</sup> Izy hiantso Ahy hoe: Raiko Hianao, Andriamanitro sy Vatolampin’ny famonjena ahy.",
			"27": "<sup>27</sup> Ary Izaho kosa hanao azy ho lahimatoa, Ambony indrindra amin’ny mpanjakan’ny tany.",
			"28": "<sup>28</sup> Hitahiry ny famindrampoko ho azy mandrakizay Aho, Ary ny fanekeko hampaharetiko aminy.",
			"29": "<sup>29</sup> Hampaharitra ny taranany ho mandrakizay Aho, Ary ny seza fiandrianany ho tahaka ny faharetan’ny lanitra.",
			"30": "<sup>30</sup> Raha mahafoy ny lalàko ny zanany Ka tsy mandeha araka ny fitsipiko,–",
			"31": "<sup>31</sup> Raha ny teniko no ataony ho fahavetavetana, Ary ny didiko no tsy tandremany,",
			"32": "<sup>32</sup> Dia hovaliako tsorakazo ny fahadisoany, Ary kapoka ny helony.",
			"33": "<sup>33</sup> Nefa ny famindrampoko tsy hofoanako aminy, Ary ny fahamarinako tsy havadiko,",
			"34": "<sup>34</sup> Ny fanekeko tsy hotsoahako, Ary ny nolazain’ny molotro tsy hovako.",
			"35": "<sup>35</sup> Izao zavatra iray loha izao no nianianako tamin’ny fahamasinako, Tsy handainga amin’i Davida mihitsy Aho:",
			"36": "<sup>36</sup> Ny taranany haharitra mandrakizay, Ary ny seza fiandrianany ho tahaka ny masoandro eo anatrehako;",
			"37": "<sup>37</sup> Hampitoerina mandrakizay tahaka ny volana izy, Tahaka ny vavolombelona marina any an-danitra. [Sela.]",
			"38": "<sup>38</sup> Fa efa narianao sy nolavinao izy; Efa tezitra tamin’ny voahosotrao Hianao.",
			"39": "<sup>39</sup> Efa nahafoana ny fanekena tamin’ny mpanomponao Hianao; Efa nataonao ho fahavetavetana ny satro-boninahiny ka narianao teny an-tany.",
			"40": "<sup>40</sup> Efa noravanao ny fefiny rehetra; Efa nosimbanao ny fiarovany mafy.",
			"41": "<sup>41</sup> Izay rehetra mandalo eny an-dàlana dia manimba azy; Efa fandàtsan’ny mpiray monina aminy izy.",
			"42": "<sup>42</sup> Efa nampisandratra ny tànana ankavanan’ny rafilahiny Hianao Ary efa nampifaly ny fahavalony rehetra.",
			"43": "<sup>43</sup> Efa nampihemotra ny lelan’ny sabany Hianao. Ka tsy nampaharitra azy tamin’ny ady.",
			"44": "<sup>44</sup> Efa nampitsahatra ny voninahiny Hianao Ka nanongana ny seza fiandrianany ho amin’ny tany.",
			"45": "<sup>45</sup> Efa nahafohy ny andron’ny fahatanorany Hianao Ary efa nanaron-kenatra azy. [Sela.]",
			"46": "<sup>46</sup> Mandra-pahoviana, Jehovah ô? Hiery mandrakizay va Hianao? Hirehitra tahaka ny afo va ny fahatezeranao?",
			"47": "<sup>47</sup> Tsarovy ny hafohin’ny androko; Toa zava-poana mihitsy ny namoronanao ny zanak’olombelona rehetra!",
			"48": "<sup>48</sup> Iza no olona ho velona tsy hahita fahafatesana, Fa hamonjy ny fanahiny tsy ho azon’ny tànan’ny fiainan-tsi-hita? [Sela.]",
			"49": "<sup>49</sup> Tompo ô, aiza ny famindramponao fahiny, Izay nianiananao tamin’i Davida tamin’ny fahamarinanao?",
			"50": "<sup>50</sup> Tompo ô, tsarovy ny latsa manjo ny mpanomponao, Ny tratrako vesaran’ny firenena rehetra,",
			"51": "<sup>51</sup> Dia ny nandatsan’ny fahavalonao, Jehovah ô, Eny, ny nandatsany ny dian’ny voahosotrao.",
			"52": "<sup>52</sup> Isaorana mandrakizay anie Jehovah. Amena dia Amena."
		},
		"90": {
			"1": "<sup>1</sup> Fivavak’i Mosesy, lehilahin’Andriamanitra. TOMPO ô, efa fonenanay hatramin’ny taranaka rehetra Hianao.",
			"2": "<sup>2</sup> Fony tsy mbola ary ny tendrombohitra, Na voaforonao ny tany sy izao rehetra izao, Dia Andriamanitra Hianao hatramin’ny taloha indrindra ka mandrakizay.",
			"3": "<sup>3</sup> Mamerina ny olona ho amin’ny vovoka Hianao Ka manao hoe: Miverena, ry zanak’olombelona.",
			"4": "<sup>4</sup> Fa eo imasonao ny arivo taona dia toy ny fandalon’ny omaly, Ary tahaka ny fotoam-piambenana iray amin’ny alina.",
			"5": "<sup>5</sup> Manindao azy tahaka ny riaka Hianao; torimaso izy; Amin’ny maraina tahaka ny ahitra mora miova izy.",
			"6": "<sup>6</sup> Amin’ny maraina mamony izy, nefa mora miova; Amin’ny hariva voajinja izy ka maina.",
			"7": "<sup>7</sup> Fa lanin’ny fahatezeranao izahay, Ary raiki-tahotra noho ny fahaviniranao.",
			"8": "<sup>8</sup> Ny helokay efa nataonao teo anatrehanao, Ary ny fahotanay miafina teo amin’ny fahazavan’ny tavanao.",
			"9": "<sup>9</sup> Fa ny androanay rehetra efa lasa amin ny fahatezeranao; Efa laninay toy ny eritreritra ny taonanay.",
			"10": "<sup>10</sup> Fito-polo taona no andron’ny taonanay Ary raha tàhiny mateza aza, dia valo-polo taona, Nefa fahasasarana sy zava-poana ny voninahiny, Fa mihelina faingana izany, ka lasa manidina izahay.",
			"11": "<sup>11</sup> Iza no mahafantatra ny herin’ny fahatezeranao sy ny fahaviniranao Araka izay tokony ho fahatahorana Anao?",
			"12": "<sup>12</sup> Mampianara anay hanisa ny andronay, Hahazoanay fo hendry.",
			"13": "<sup>13</sup> Miverena, Jehovah ô! Mandra-pahoviana? Ary mba aneneno ny amin’ny mpanomponao.",
			"14": "<sup>14</sup> Vokiso ny famindramponao nony maraina izahay, Mba hihobianay sy hifalianay amin’ny andro rehetra hiainanay.",
			"15": "<sup>15</sup> Mampifalia anay araka ny andro nampahorianao anay Sy ny taona nahitanay loza.",
			"16": "<sup>16</sup> Aoka hiseho amin’ny mpanomponao ny asanao, Ary ny voninahitrao amin’ny zanany.",
			"17": "<sup>17</sup> Ary aoka ho aminay ny fahasoavan’ny Tompo Andriamanitray; Ary ampitoero aminay ny asan’ny tànanay; Eny, ny asan’ny tànanay, ampitoero izy."
		},
		"91": {
			"1": "<sup>1</sup> NY mitoetra ao amin’ny fierena izay an’ny Avo Indrindra Dia monina eo ambanin’ny aloky ny Tsitoha.",
			"2": "<sup>2</sup> Izaho hilaza an’i Jehovah hoe: Fialofako sy batery fiarovana ho ahy Izy Ary Andriamanitro, Izay hitokiako.",
			"3": "<sup>3</sup> Fa Izy no hanafaka anao amin’ny fandriky ny mpihaza sy ny areti-mandoza.",
			"4": "<sup>4</sup> Ny volon’elany no anaronany anao, Ary ao ambanin’ny elany no hialofanao; Ho ampinga kely sy ampinga lehibe ny fahamarinany.",
			"5": "<sup>5</sup> Tsy hatahotra ny horohoro nony alina hianao, Na ny zana-tsipìka manidina nony andro,",
			"6": "<sup>6</sup> Na ny areti-mandringana mandeha amin’ny maizina, Na ny fandringanana mandripaka amin’ny mitataovovonana.",
			"7": "<sup>7</sup> Na dia hisy arivo aza lavo eo anilanao sy iray alina eo an-kavananao, Dia tsy hihatra aminao izany.",
			"8": "<sup>8</sup> Hijery maso fotsiny hianao, Ka ho hitanao ny hamaliana ny ratsy fanahy.",
			"9": "<sup>9</sup> Satria hoy hianao: Jehovah ô, Hianao no aroko, Sady nataonao ho fonenanao ny Avo Indrindra,",
			"10": "<sup>10</sup> Dia tsy hisy loza hanjo anao, Na aretina hanakaiky ny lainao.",
			"11": "<sup>11</sup> Fa Izy handidy ny anjeliny ny aminao Mba hiaro anao eny amin’izay rehetra halehanao.",
			"12": "<sup>12</sup> Eny an-tànany no hitondran’ireo anao, Fandrao ho tafintohina amin’ny vato ny tongotrao.",
			"13": "<sup>13</sup> Ny liona masiaka sy ny vipera hodiavinao; Ny liona tanora sy ny menarana no hohitsahinao.",
			"14": "<sup>14</sup> Satria Izaho no niraiketan’ny fitiavany, Dia hamonjy azy Aho; Hanandratra azy ho any amin’ny avo Aho, satria mahalala ny anarako izy.",
			"15": "<sup>15</sup> Hiantso Ahy izy, dia hamaly azy Aho; Ho eo aminy Aho, raha ory izy; Hamonjy sy hanome voninahitra azy Aho.",
			"16": "<sup>16</sup> Hovokisako fahela-velona izy; Ary hasehoko azy ny famonjeko."
		},
		"92": {
			"1": "<sup>1</sup> Salamo. Tonon-kira hatao amin’ny andro Sabata. TSARA ny hidera an’i Jehovah Sy ny hankalaza ny anaranao, ry Avo Indrindra ô,",
			"2": "<sup>2</sup> Ary hanambara ny famindramponao nony maraina Sy ny fahamarinanao nony alina,",
			"3": "<sup>3</sup> Amin’ny valiha tory folo Sy amin’ny feon-dokanga.",
			"4": "<sup>4</sup> Fa ampifalinao amin’ny nataonao aho, Jehovah ô; Ny asan’ny tànanao no hihobiako.",
			"5": "<sup>5</sup> Jehovah ô, akory ny halehiben’ny asanao! Lalina indrindra ny fisainanao.",
			"6": "<sup>6</sup> Ny ketrina tsy mahafantatra izany, Ary ny adala koa tsy mba mahalala.",
			"7": "<sup>7</sup> Raha mitsiry tahaka ny ahitra ny ratsy fanahy, Ary mamony ny mpanao ratsy rehetra, Dia izany no ho fandringanana azy mandrakizay;",
			"8": "<sup>8</sup> Nefa Hianao, Jehovah ô, dia avo mandrakizay.",
			"9": "<sup>9</sup> Fa, indreo, ny fahavalonao, Jehovah ô, Fa, indreo, ringana ny fahavalonao; Mihahaka ny mpanao ratsy rehetra.",
			"10": "<sup>10</sup> Fa ny tandroko kosa asandratrao ho tahaka ny an’ny ombimanga; Mihosotra diloilo vaovao aho.",
			"11": "<sup>11</sup> Ary faly ny masoko mahita ny amin’ny fahavaloko, Ary finaritra ny sofiko mandre ny amin’ny ratsy fanahy izay efa mitsangana hamely ahy.",
			"12": "<sup>12</sup> Ny marina hamololona tahaka ny rofia; Hisandrahaka tahaka ny sedera any Libanona izy;",
			"13": "<sup>13</sup> Efa nambolena tao an-tranon’i Jehovah izy Ka maniry eo an-kianjan’Andriamanitsika;",
			"14": "<sup>14</sup> Mbola manorobona ihany izy, na dia efa antitra aza; Dofodofotra sy maitso izy,",
			"15": "<sup>15</sup> Mba hanambara fa mahitsy Jehovah, Vatolampiko Izay tsy misy tsiny."
		},
		"93": {
			"1": "<sup>1</sup> JEHOVAH no Mpanjaka, mitafy voninahitra Izy; Jehovah mitafy,–eny, misikina hery Izy; Ary izao rehetra izao nampitoerina ka tsy hihetsika.",
			"2": "<sup>2</sup> Ny seza fiandriananao nampitoerina hatramin’ny fahagola; Hatrizay hatrizay Hianao.",
			"3": "<sup>3</sup> Manandra-peo ny riaka, Jehovah ô, Eny, manandra-peo ny riaka; Mampirohondrohona ny feony ny riaka.",
			"4": "<sup>4</sup> Mihoatra noho ny rano sady be no mahery, Eny, mihoatra noho ny onjan-dranomasina lehibe, Ny voninahitr’i Jehovah any amin’ny avo.",
			"5": "<sup>5</sup> Ny teni-vavolombelonao dia marina indrindra; Fahamasinana no mendrika ny tranonao mandritra ny andro maro, Jehovah ô."
		},
		"94": {
			"1": "<sup>1</sup> JEHOVAH, Andriamanitra Mpamaly ny natao, ô, Andriamanitra, Mpamaly ny natao, ô, mamirapirata.",
			"2": "<sup>2</sup> Misandrata, ry Mpitsara ny tany, ô; Mamalia ny mpiavonavona.",
			"3": "<sup>3</sup> Mandra-pahoviana re ny ratsy fanahy, Jehovah ô, Mandra-pahoviana re no mbola hifalifaly ny ratsy fanahy?",
			"4": "<sup>4</sup> Mirezadrezatra sy miteny sahisahy izy; Mirehareha ny mpanao ratsy rehetra.",
			"5": "<sup>5</sup> Ny olonao, Jehovah ô, torotoroiny, Ary ny lovanao ampahoriny;",
			"6": "<sup>6</sup> Ny mpitondratena sy ny vahiny vonoiny, Ary ny kamboty dia matiny.",
			"7": "<sup>7</sup> Fa hoy izy: Tsy mijery Jehovah, Ary tsy mandinika Andriamanitr’i Jakoba.",
			"8": "<sup>8</sup> Mahalalà hianareo olona ketrina; Ary hianareo adala, rahoviana re no mba ho hendry hianareo?",
			"9": "<sup>9</sup> Ny Mpanao ny sofina va tsy handre? Ny Mpamorona ny maso va tsy hahita?",
			"10": "<sup>10</sup> Ny Mpampianatra ny jentilisa va tsy hananatra, Dia Izay mampianatra fahalalana ny olona?",
			"11": "<sup>11</sup> Jehovah no mahalala ny hevitry ny olona, Fa zava-poana izany.",
			"12": "<sup>12</sup> Sambatra izay olona anarinao, Jehovah ô, Sady ampianarinao avy amin’ny lalànao,",
			"13": "<sup>13</sup> Mba homenao fitsaharana amin’ny andro fahoriana izy, Mandra-pihady lavaka ho an’ny ratsy fanahy.",
			"14": "<sup>14</sup> Fa Jehovah tsy mba hanary ny olony, Ary ny lovany tsy hafoiny.",
			"15": "<sup>15</sup> Fa ny fitsarana hiverina ho amin’ny fahamarinana, Ary hanaraka izany ny mahitsy fo rehetra",
			"16": "<sup>16</sup> Iza no hitsangana ho ahy hisakana ny mpanao ratsy? Iza no hiseho ho ahy hisakana ny mpanao meloka?",
			"17": "<sup>17</sup> Raha tsy Jehovah no Mpamonjy ahy, Dia efa saiky nalaky nitoetra tao amin’ny mangingina ny fanahiko.",
			"18": "<sup>18</sup> Fony aho nanao hoe: Mangozohozo ny tongotro, Dia ny famindramponao, Jehovah ô, no nanohana ahy.",
			"19": "<sup>19</sup> Raha be ny ahiahy ato anatiko, Dia mampifaly ny fanahiko ny fampiononanao.",
			"20": "<sup>20</sup> Hikambana aminao va ny seza fiandrianan’ny ratsy, Izay misaina fampahoriana manohitra ny lalàna?",
			"21": "<sup>21</sup> Manao andiany hamely ny fanahin’ny marina ireo Ka manameloka ny ra marina.",
			"22": "<sup>22</sup> Fa tonga fiarovana avo ho ahy Jehovah; Ary vatolampy fialofako Andriamanitro.",
			"23": "<sup>23</sup> Dia ampanodiaviny azy ireo ny helony ka aringany noho ny faharatsiany izy; Eny, aringan’i Jehovah Andriamanitsika izy."
		},
		"95": {
			"1": "<sup>1</sup> AVIA, aoka isika hihoby ho an’i Jehovah; Aoka isika hanao feo fifaliana ho an’ny Vatolampy famonjena antsika.",
			"2": "<sup>2</sup> Aoka hankeo anatrehany amin’ny fiderana isika; Aoka hanao feo fifaliana ho Azy amin’ny fihirana isika.",
			"3": "<sup>3</sup> Fa Andriamanitra lehibe Jehovah, Ary Mpanjaka lehibe ambonin’ny andriamanitra rehetra Izy.",
			"4": "<sup>4</sup> Eo an-tànany ny fitoerana lalina amin’ny tany; Ary Azy ny tendrombohitra avo.",
			"5": "<sup>5</sup> Azy ny ranomasina, fa Izy no nanao azy; Ary ny tany maina dia noforonin’ny tànany.",
			"6": "<sup>6</sup> Avia, ka aoka hiondrika sy hiankohoka isika; Aoka isika handohalika eo anatrehan’i Jehovah, Mpanao antsika;",
			"7": "<sup>7</sup> Fa Izy no Andriamanitsika; Ary olona fiandriny sy ondry tandremany isika. Enga anie ka hohenoinareo anio ny feony manao hoe:",
			"8": "<sup>8</sup> Aza manamafy ny fonareo tahaka ny tao Meriba, Sy tahaka ny tamin’ny andro tao Masa, fony tany an-efitra,",
			"9": "<sup>9</sup> Izay nakan’ny razanareo fanahy Ahy, Sy nizahany toetra Ahy, na dia efa hitany aza ny asako.",
			"10": "<sup>10</sup> Efa-polo taona no nahamonamonaina Ahy tamin’izany taranaka izany, Ka hoy Izaho: Olona maniasia amin’ny fony izy Ka tsy mahalala ny làlako;",
			"11": "<sup>11</sup> Ka dia nianiana tamin’ny fahatezerako Aho hoe: Tsy hiditra amin’ny fitsaharako mihitsy izy."
		},
		"96": {
			"1": "<sup>1</sup> MIHIRÀ fihiram-baovao ho an’i Jehovah; Mihirà ho an’i Jehovah, ry tany rehetra.",
			"2": "<sup>2</sup> Mihirà ho an’i Jehovah, misaora ny anarany; Lazao isan-andro isan-andro ny famonjeny mahafaly.",
			"3": "<sup>3</sup> Ambarao any amin’ny jentilisa ny voninahiny, Ary any amin’ny firenena rehetra ny fahagagana ataony.",
			"4": "<sup>4</sup> Fa lehibe Jehovah ka mendrika hoderaina indrindra, Sady mahatahotra, fa ambonin’ny andriamanitra rehetra.",
			"5": "<sup>5</sup> Fa ny andriamanitry ny firenena dia tsinontsinona; Fa Jehovah no nanao ny lanitra.",
			"6": "<sup>6</sup> Famirapiratana sy voninahitra no eo anatrehany; Hery sy fahatsaran-tarehy no ao amin’ny fitoerany masina.",
			"7": "<sup>7</sup> Ry firenena, manomeza an’i Jehovah, Eny, manomeza voninahitra sy hery ho an’i Jehovah.",
			"8": "<sup>8</sup> Manomeza an’i Jehovah ny voninahitry ny anarany; Mitondrà fanatitra, ka mankanesa eo an-kianjany.",
			"9": "<sup>9</sup> Miankohofa eo anatrehan’i Jehovah amin’ny fihaingoana masina; Mangovita eo anatrehany, ry tany rehetra.",
			"10": "<sup>10</sup> Lazao any amin’ny jentilisa fa Jehovah no Mpanjaka; Ary izao rehetra izao voaorina ka tsy hihetsika; Hitsara ny olona amin’ny fahamarinana Izy.",
			"11": "<sup>11</sup> Aoka ho faly ny lanitra, ary ho ravoravo ny tany; Aoka hirohondrohona ny ranomasina sy izay rehetra ao aminy.",
			"12": "<sup>12</sup> Aoka ho faly ny saha sy izay rehetra eo aminy; Dia hihoby amin’izay ny hazo rehetra any an-ala",
			"13": "<sup>13</sup> Eo anatrehan’i Jehovah, fa avy Izy,– Eny, avy hitsara ny tany Izy; Hitsara izao tontolo izao araka ny rariny Izy Ary ny firenena amin’ny fahamarinany."
		},
		"97": {
			"1": "<sup>1</sup> JEHOVAH no Mpanjaka, aoka ho faly ny tany; Aoka ho ravoravo ny nosy maro.",
			"2": "<sup>2</sup> Rahona sy aizim-pito no manodidina Azy Fahamarinana sy fitsarana no fanorenan’ny seza fiandrianany.",
			"3": "<sup>3</sup> Misy afo mialoha Azy Ka mandevona ny fahavalony manodidina.",
			"4": "<sup>4</sup> Mahazava izao tontolo izao ny helany; Mahita izany ny tany ka mihorohoro.",
			"5": "<sup>5</sup> Ny tendrombohitra miempo tahaka ny savoka eo anatrehan’i Jehovah, Dia eo anatrehan’ny Tompon’ny tany rehetra.",
			"6": "<sup>6</sup> Ny lanitra maneho ny fahamarinany, Ary ny firenena rehetra mahita ny voninahiny.",
			"7": "<sup>7</sup> Aoka ho menatra izay rehetra manompo sarin-javatra voasokitra Sy miarahaba tena ny amin’ny andriamani-tsi-izy; Miankohofa eo anoloan’i Jehovah, hianareo andriamanitra rehetra.",
			"8": "<sup>8</sup> Ziona mandre ka faly; Ary ravoravo ny zanakavavin’i Joda Noho ny fitsaranao, Jehovah ô.",
			"9": "<sup>9</sup> Fa Hianao, Jehovah ô, no avo indrindra ambonin’ny tany rehetra; Efa avo ambonin’ny andriamanitra rehetra Hianao.–",
			"10": "<sup>10</sup> Hianareo izay tia an’i Jehovah, mankahalà ny ratsy; Miaro ny fanahin’ny olony masina Izy Ary mamonjy azy amin’ny tànan’ny ratsy fanahy.",
			"11": "<sup>11</sup> Ny fahazavana no voafafy ho an’ny marina, Ary ny fifaliana ho an’ny mahitsy fo.",
			"12": "<sup>12</sup> Mifalia amin’i Jehovah, ry olo-marina, Ary miderà ny anarany masina."
		},
		"98": {
			"1": "<sup>1</sup> Fihirana. MIHIRÀ fihiram-baovao ho an’i Jehovah; Fa efa nanao fahagagana Izy; Ny tànany ankavanana sy ny sandriny masina no nahavitany famonjena.",
			"2": "<sup>2</sup> Jehovah efa nampahafantatra ny famonjeny; Teo imason’ny jentilisa no nanehoany ny fahamarinany.",
			"3": "<sup>3</sup> Efa nahatsiaro ny famindrampony sy ny fahamarinany tamin’ny taranak’i Isiraely Izy; Efa nahita ny famonjen’Andriamanitsika ny vazan-tany rehetra.",
			"4": "<sup>4</sup> Mihobia ho an’i Jehovah, ry tany rehetra, Velomy ny hoby, ka mihirà.",
			"5": "<sup>5</sup> Mankalazà an’i Jehovah amin’ny lokanga, Dia amin’ny lokanga sy ny feo fihirana,",
			"6": "<sup>6</sup> Amin’ny trompetra sy ny fitsofana ny anjomara, Ka mihobia eo anatrehan’i Jehovah Mpanjaka.",
			"7": "<sup>7</sup> Aoka hirohondrohona ny ranomasina sy izay rehetra ao aminy, Izao rehetra izao sy ny mponina eo aminy.",
			"8": "<sup>8</sup> Aoka hiteha-tànana ny riaka; Aoka hiara-mihoby aminy koa ny tendrombohitra",
			"9": "<sup>9</sup> Eo anatrehan’i Jehovah, fa avy hitsara ny tany Izy; Hitsara izao rehetra izao amin’ny fahamarinana Izy Ary ny firenena amin’ny fahitsiana."
		},
		"99": {
			"1": "<sup>1</sup> JEHOVAH no Mpanjaka, aoka hangovitra ny firenena; Mipetraka amin’ny kerobima Izy, aoka hihorohoro ny tany.",
			"2": "<sup>2</sup> Lehibe any Ziona Jehovah; Ary avo ambonin’ny firenena rehetra Izy.",
			"3": "<sup>3</sup> Aoka hidera ny anaranao lehibe sy mahatahotra ireo; Fa masina Jehovah.",
			"4": "<sup>4</sup> Ny herin’ny mpanjaka dia tia rariny; Ampitoerinao ny fahitsiana; Fitsarana sy fahamarinana no ataonao eo amin’i Jakoba.",
			"5": "<sup>5</sup> Manandrata an’i Jehovah Andriamanitsika hianareo, Ka miankohofa eo amin’ny fitoeran-tongony; Fa masina Izy.",
			"6": "<sup>6</sup> Mosesy sy Arona tamin’ny mpisorony, Ary Samoela tamin’izay mpiantso ny anarany,– Niantso an’i Jehovah ireo, ary namaly azy Izy.",
			"7": "<sup>7</sup> Niteny taminy tao anatin’ny andri-rahona Izy; Nitandrina ny teni-vavolombelony izy ireo, Dia ny didy izay efa nomeny azy.",
			"8": "<sup>8</sup> Jehovah Andriamanitray ô, Hianao no namaly ny nataony; Andriamanitra, Mpamela ny helony Hianao, Na dia namaly azy noho ny nataony aza.",
			"9": "<sup>9</sup> Asandrato Jehovah Andriamanitsika, Ary miankohofa eo an-tendrombohiny masina; Fa masina Jehovah Andriamanitsika."
		},
		"100": {
			"1": "<sup>1</sup> Salamo ho fiderana. MIHOBIA ho an’i Jehovah, ry tany rehetra.",
			"2": "<sup>2</sup> Manompoa an’i Jehovah amin’ny fifaliana; Mankanesa eo anatrehany amin’ny fihobiana.",
			"3": "<sup>3</sup> Aoka ho fantatrareo fa Jehovah no Andriamanitra; Izy no nanao antsika, ary Azy isika, Dia olony sy ondry fiandriny.",
			"4": "<sup>4</sup> Midira eo amin’ny vavahadiny amin’ny fisaorana, Ary eo an-kianjany amin’ny fiderana; Misaora Azy, mankalazà ny anarany,",
			"5": "<sup>5</sup> Fa tsara Jehovah; Mandrakizay ny famindrampony, Ary mihatra amin’ny taranaka fara mandimby ny fahamarinany."
		},
		"101": {
			"1": "<sup>1</sup> Salamo nataon’i Davida. FAMINDRAMPO sy fitsarana no hankalazaiko; Hianao, Jehovah ô, no hoderaiko.",
			"2": "<sup>2</sup> Hitandrina ny làlana marina aho; Rahoviana no ho tonga atỳ amiko Hianao? Handeha amin’ny fahitsian’ny foko ao anatin’ny tranoko aho.",
			"3": "<sup>3</sup> Tsy hampitoetra zava-dratsy eo anoloan’ny masoko aho; Halako ny mivadika, Tsy hikambana amiko izany.",
			"4": "<sup>4</sup> Ny fo maditra hiala amiko; Tsy hankasitraka izay ratsy aho.",
			"5": "<sup>5</sup> Izay manendrikendrika ny namany mangingina dia hofongorako; Tsy tohako izay miandranandrana sy miavonavona am-po.",
			"6": "<sup>6</sup> Ny masoko hijery izay olo-mahatoky amin’ny tany mba hitoerany ato amiko; Izay mandeha amin’ny làlana marina no hanompo ahy.",
			"7": "<sup>7</sup> Tsy mba hitoetra ao anatin’ny tranoko izay manao fitaka; Tsy mba hijanona eo anoloan’ny masoko izay milaza lainga.",
			"8": "<sup>8</sup> Haringako isa-maraina ny ratsy fanahy rehetra amin’ny tany, Mba hofongorako ny mpanao ratsy rehetra tsy ho ao an-tanànan’i Jehovah."
		},
		"102": {
			"1": "<sup>1</sup> Fivavaky ny ory, raha reraka izy ka mandoatra ny alahelony eo anatrehan’i Jehovah. JEHOVAH ô, henoy ny fivavako, Ary aoka hankeo aminao ny fitarainako.",
			"2": "<sup>2</sup> Aza manafina ny tavanao amiko amin’ny andro fahoriako; Atongilano amiko ny sofinao; Amin’ny andro itarainako dia faingàna hamaly ahy.",
			"3": "<sup>3</sup> Fa levon-ko setroka ny androko, Ary ny taolako mahamay tahaka ny forohana.",
			"4": "<sup>4</sup> Taninina tahaka ny ahitra ny foko ka malazo; Fa hadinoko na dia ny fihinanan-kanina aza.",
			"5": "<sup>5</sup> Noho ny fisentoan’ny foko Ny taolako miraikitra amin’ny hoditro.",
			"6": "<sup>6</sup> Tahaka ny sama any an-efitra aho, Ary tahaka ny vorondolo any an-tanànaolo.",
			"7": "<sup>7</sup> Tsy mahita tory aho Sady tahaka ny tsikirity irery eo an-tampon-trano.",
			"8": "<sup>8</sup> Mandatsa ahy mandrakariva ny fahavaloko; Izay misafoaka amiko dia manao ahy ho fiozonana.",
			"9": "<sup>9</sup> Fa lavenona no nohaniko tahaka ny mofo; Ary ny zavatra nosotroiko noharoharoako ny ranomasoko,",
			"10": "<sup>10</sup> Noho ny fahaviniranao sy ny fahatezeranao; Fa naingainao aho, kanefa natontanao indray.",
			"11": "<sup>11</sup> Ny androko dia tahaka ny aloka efa mandroso; Ary efa malazo tahaka ny ahitra aho.",
			"12": "<sup>12</sup> Nefa Hianao, Jehovah ô, mipetraka manjaka mandrakizay; Ary ny fahatsiarovana Anao mihatra amin’ny taranaka fara mandimby.",
			"13": "<sup>13</sup> Hianao hitsangana ka hamindra fo amin’i Ziona; Fa efa andro hamindram-po aminy izao; Eny, tonga ny fotoana.",
			"14": "<sup>14</sup> Fa sitraky ny mpanomponao ny vatony, Sady malahelo ny vovo-taniny izy.",
			"15": "<sup>15</sup> Hatahotra ny anaran’i Jehovah ny jentilisa, Ary ny mpanjaka rehetra amin’ny tany hatahotra ny voninahitrao;",
			"16": "<sup>16</sup> Fa Jehovah nanangana an’i Ziona; Niseho tamin’ny voninahiny Izy;",
			"17": "<sup>17</sup> Nijery ny fivavaky ny mahantra Izy Ka tsy nanevateva ny fivavany.",
			"18": "<sup>18</sup> Hosoratana ho an’ny taranaka mandimby izany; Ary izay firenena mbola hoforonina no hidera an’i Jehovah;",
			"19": "<sup>19</sup> Fa Izy nitazana tany amin’ny fitoerany masina any amin’ny avo. Tany an-danitra no nitsinjovan’i Jehovah ny tany,",
			"20": "<sup>20</sup> Mba hihaino ny fisentoan’ny mpifatotra Sy hanafaka izay voatendry ho faty,",
			"21": "<sup>21</sup> Mba hotorina any Ziona ny anaran’i Jehovah, Ary ny fiderana Azy any Jerosalema,",
			"22": "<sup>22</sup> Rehefa tafangona ny firenena Sy ny fanjakana maro hanompo an’i Jehovah.",
			"23": "<sup>23</sup> Efa nahaketraka ny heriko tany an-dàlana Izy; Efa nahafohy ny androko Izy.",
			"24": "<sup>24</sup> Hoy izaho: Andriamanitro ô, aza maka ahy an-tenatenan’ny androko; Ny taonanao mihatra amin’ny taranaka fara mandimby.",
			"25": "<sup>25</sup> Tany aloha no nanorenanao ny tany; Ary asan’ny tànanao ny lanitra.",
			"26": "<sup>26</sup> Ireo dia ho levona, fa Hianao no maharitra; Izy rehetra ho rovitra toy ny fitafiana; Toy ny lamba no hanovanao azy, ka hovana izy;",
			"27": "<sup>27</sup> Fa Hianao no tsy miova, Ary ny taonanao no tsy ho tapitra.",
			"28": "<sup>28</sup> Ny zanaky ny mpanomponao handry fahizay, Ary ny taranany hampitoerina eo anatrehanao."
		},
		"103": {
			"1": "<sup>1</sup> Nataon’i Davida. MISAORA an’i Jehovah, ry fanahiko; Ary izay rehetra ato anatiko, misaora ny anarany masina.",
			"2": "<sup>2</sup> Misaora an’i Jehovah, ry fanahiko; Ary aza misy hadinoinao ny fitahiany rehetra,",
			"3": "<sup>3</sup> Izay mamela ny helokao rehetra, Izay manasitrana ny aretinao rehetra,",
			"4": "<sup>4</sup> Izay manavotra ny ainao tsy hidina any an-davaka, Izay manarona famindrampo sy fiantrana anao,",
			"5": "<sup>5</sup> Izay mahavoky soa ny vavanao; Ny fahatanoranao mody indray toy ny an’ny voromahery.",
			"6": "<sup>6</sup> Jehovah manao fahamarinana Sy fitsarana amin’izay rehetra ampahorina.",
			"7": "<sup>7</sup> Efa nampahafantatra an’i Mosesy ny làlan-kalehany Izy; Ny Zanak’Isiraely nampahafantariny ny asany.",
			"8": "<sup>8</sup> Mamindra fo sy miantra Jehovah, Mahari-po sady be famindrampo.",
			"9": "<sup>9</sup> Tsy mandaha-teny mandrakariva Izy, Na mitahiry fahatezerana mandrakizay.",
			"10": "<sup>10</sup> Tsy mba manao amintsika araka ny fahotantsika Izy, Na mamaly antsika araka ny helotsika.",
			"11": "<sup>11</sup> Fa tahaka ny hahavon’ny lanitra ambonin’ny tany No haben’ny famindrampony amin’izay matahotra Azy.",
			"12": "<sup>12</sup> Tahaka ny halavitry ny atsinanana amin’ny andrefana No halavitry ny anesorany ny fahotantsika amintsika.",
			"13": "<sup>13</sup> Tahaka ny fiantràn’ny ray ny zanany No fiantràn’i Jehovah izay matahotra Azy;",
			"14": "<sup>14</sup> Fa Izy mahalala ny toetsika Ka mahatsiaro fa vovoka isika.",
			"15": "<sup>15</sup> Tahaka ny ahitra ny andron’ny zanak’olombelona; Tahaka ny vonin-javatra any an-tsaha ny famoniny,",
			"16": "<sup>16</sup> Satria tsofin’ny rivotra izy ka lasa, Ary tsy mahalala azy intsony ny fitoerany.",
			"17": "<sup>17</sup> Fa ny famindrampon’i Jehovah dia hatramin’ny taloha indrindra ka ho mandrakizay amin’izay matahotra Azy Ary ny fahamarinany mihatra amin’ny taranaka.",
			"18": "<sup>18</sup> Dia amin’izay mitandrina ny fanekeny Sy mahatsiaro hankatò ny didiny.",
			"19": "<sup>19</sup> Jehovah efa nampitoetra ny seza fiandrianany any an-danitra; Ary ny fanjakany manapaka izao tontolo izao.",
			"20": "<sup>20</sup> Misaora an’i Jehovah, hianareo anjeliny, Dia hianareo izay mahery indrindra sady mankatò ny didiny Ary mihaino ny feon’ny teniny.",
			"21": "<sup>21</sup> Misaora an’i Jehovah, ny miaramilany rehetra, Dia ny mpanompony izay manao ny sitrapony.",
			"22": "<sup>22</sup> Misaora an’i Jehovah, ny asany rehetra eran’ny fanjakany; Misaora an’i Jehovah, ry fanahiko."
		},
		"104": {
			"1": "<sup>1</sup> MISAORA an’i Jehovah, ry fanahiko. Jehovah Andriamanitro ô, lehibe indrindra Hianao; Famirapiratana sy voninahitra no fitafinao,",
			"2": "<sup>2</sup> Mitafy ny mazava tahaka ny lamba Izy Ary mamelatra ny lanitra tahaka ny lay,",
			"3": "<sup>3</sup> Ary mametraka ny rairain-drihan’ny tranony amin’ny rano, Sady manao ny rahona matevina ho kalesiny Ka mandeha amin’ny elatry ny rivotra,",
			"4": "<sup>4</sup> Ary manao ny rivotra ho irany Sy ny lelafo ho mpanompony.",
			"5": "<sup>5</sup> Nanorina ny tany tambonin’ny fanorenany Izy, Mba tsy hihetsehany mandrakizay doria.",
			"6": "<sup>6</sup> Ny rano lalina no nasaronao azy tahaka ny lamba; Nandifotra ny tendrombohitra aza ireny.",
			"7": "<sup>7</sup> Noho ny teny mafy nataonao dia nandositra ny tendrombohitra, Noho ny feon’ny kotrokoranao dia nirifatra izy–",
			"8": "<sup>8</sup> Nisondrotra ny tendrombohitra, nietry ny lohasaha–ho any amin’izay namboarinao hitoerany.",
			"9": "<sup>9</sup> Efa nasianao fetra tsy hihoarany izy Mba tsy hiverina hanarona ny tany.",
			"10": "<sup>10</sup> Mampandeha loharano eny amin’ny lohasaha Izy; Eny anelanelan’ny tendrombohitra no alehan’ireny,",
			"11": "<sup>11</sup> Ka isotroan’ny bibi-dia rehetra, Sy analan’ny boriki-dia ny hetahetany;",
			"12": "<sup>12</sup> Eo amboniny no itoeran’ny voro-manidina; Ary eo amin’ny rantsan-kazo no anenoany.",
			"13": "<sup>13</sup> Manondra-drano ny tendrombohitra avy amin’ny tranony ambony Izy; Ny vokatry ny asanao no mahavoky ny tany.",
			"14": "<sup>14</sup> Mampaniry ahitra ho an’ny biby fiompy Izy Sy anana hahasoa ny olona, Ary mamoaka hanina avy amin’ny tany,",
			"15": "<sup>15</sup> Ary divay izay mampifaly ny olona, Ka mampinendonendo ny tavany mihoatra noho ny diloilo, Ary mofo hanohanana ny ain’ny olona.",
			"16": "<sup>16</sup> Voky ny hazon’i Jehovah, Dia ny sederan’i Libanona, izay namboleny;",
			"17": "<sup>17</sup> Izay anaovan’ny voron-kely ny akaniny; Ary ny vano, dia ao amin’ny kypreso no anaovany ny tranony.",
			"18": "<sup>18</sup> Ny tendrombohitra avo dia ho an’ny osi-dia; Ny harambato no fiarovana ho an’ny hyraka.",
			"19": "<sup>19</sup> Nanao ny volana ho fotoana Izy, Ary ny masoandro mahalala ny filentehany.",
			"20": "<sup>20</sup> Mahatonga aizina Hianao, dia tonga ny alina. Ka mivezivezy ny biby rehetra any an-ala;",
			"21": "<sup>21</sup> Ny liona tanora mierona maniry toha Ka mitady ny haniny avy amin’Andriamanitra.",
			"22": "<sup>22</sup> Miposaka ny masoandro, dia miverina ireny, Ary ao an-davany no amitsahany.",
			"23": "<sup>23</sup> Fa ny olona kosa mivoaka ho any amin’ny tao-zavany Sy ny asany mandra-paharivan’ny andro.",
			"24": "<sup>24</sup> Endrey ny hamaroan’ny asanao, Jehovah ô! Fahendrena no nanaovanao azy rehetra; Henika ny zavatra nataonao ny tany.",
			"25": "<sup>25</sup> Indro ny ranomasina sady lehibe no malalaka, Any no misy zava-mihetsiketsika tsy tambo isaina, Dia zava-miaina, na kely na lehibe.",
			"26": "<sup>26</sup> Any no alehan’ny sambo Sy ny trozona, izay noforoninao hilaolao any.",
			"27": "<sup>27</sup> Ireo rehetra ireo miandry Anao, Mba homenao ny haniny amin’ny fotoany.",
			"28": "<sup>28</sup> Manome Hianao, dia manangona izy; Manokatra ny tànanao Hianao, dia voky ny soa izy;",
			"29": "<sup>29</sup> Manafina ny tavanao Hianao, dia raiki-tahotra izy; Alainao indray ny ainy, dia maty izy Ka mody any amin’ny vovoka nanalana azy,",
			"30": "<sup>30</sup> Maniraka ny Fanahinao Hianao, dia ary izy, Ary manavao ny tarehin’ny tany Hianao.",
			"31": "<sup>31</sup> Ho mandrakizay anie ny voninahitr’i Jehovah; Aoka Jehovah hifaly amin’ny asany.",
			"32": "<sup>32</sup> Jereny ny tany, dia mihorohoro; Tendren’ny tànany ny tendrombohitra, dia midonaka.",
			"33": "<sup>33</sup> Hihira ho an’i Jehovah aho, raha mbola velona koa; Hankalaza an’Andriamanitro aho, raha mbola miaina.",
			"34": "<sup>34</sup> Ho maminy anie ny fisainako Azy; Izaho no hifaly amin’i Jehovah.",
			"35": "<sup>35</sup> Ho lany ringana tsy ho etỳ amin’ny tany ny mpanota, Ary ny ratsy fanahy tsy ho etỳ intsony. Misaora an’i Jehovah, ry fanahiko. Haleloia."
		},
		"105": {
			"1": "<sup>1</sup> MIDERÀ an’i Jehovah, miantsoa ny anarany; Ataovy fantatra any amin’ny firenena ny asany.",
			"2": "<sup>2</sup> Mihirà ho Azy, mankalazà Azy; Saintsaino ny fahagagana rehetra ataony.",
			"3": "<sup>3</sup> Ataovy ho reharehanareo ny anarany masina; Aoka hifaly ny fon’izay mitady an’i Jehovah.",
			"4": "<sup>4</sup> Mitadiava an’i Jehovah sy ny heriny; Katsaho mandrakariva ny tavany.",
			"5": "<sup>5</sup> Tsarovy ny asa mahagaga nataony, Dia ny fahagagany sy ny fitsaran’ny vavany,",
			"6": "<sup>6</sup> Ry taranak’i Abrahama mpanompony, Dia hianareo olom-boafidiny, taranak’i Jakoba.",
			"7": "<sup>7</sup> Jehovah, Izy no Andriamanitsika; Manerana ny tany rehetra ny fitsarany.",
			"8": "<sup>8</sup> Mahatsiaro ny fanekeny mandrakizay Izy, Dia ilay teny izay voadidiny ho an’ny taranaka arivo mandimby,",
			"9": "<sup>9</sup> Eny, ilay nataony tamin’i Abrahama, Sy ny fianianany tamin’Isaka;",
			"10": "<sup>10</sup> Izay naoriny ho lalàna ho an’i Jakoba Sy ho fanekena mandrakizay ho an’Isiraely,",
			"11": "<sup>11</sup> Ka nataony hoe: Homeko anao ny tany Kanana Ho lovanareo voazara amin’ny famolaina,",
			"12": "<sup>12</sup> Fony mbola olom-bitsy izy, Eny, vitsy sady vahiny tao,",
			"13": "<sup>13</sup> Ary nifindrafindra tany amin’ny firenena Sy nifindrafindra fanjakana ho amin’ny firenen-kafa.",
			"14": "<sup>14</sup> Tsy nisy navelany hampahory azy. Ary nananatra mpanjaka Izy noho ny amin’ireo hoe:",
			"15": "<sup>15</sup> Aza manendry ireo voahosotro, Ary aza manisy ratsy ireo mpaminaniko.",
			"16": "<sup>16</sup> Dia niantso ny mosary ho amin’ny tany Izy; Notapahiny avokoa ny mofo rehetra, izay tohan’aina;",
			"17": "<sup>17</sup> Naniraka lehilahy hialoha azy Izy; Josefa namidy ho andevo;",
			"18": "<sup>18</sup> Nampijaliany tamin’ny fatorana ny tongony; Nogadrany vy izy,",
			"19": "<sup>19</sup> Mandra-pahatongan’ny fotoana hahatanterahan’ny teniny; Nizaha toetra azy ny tenin’i Jehovah.",
			"20": "<sup>20</sup> Naniraka ny mpanjaka hamaha azy, Eny, ny mpanapaka ny vahoaka, mba handefa azy.",
			"21": "<sup>21</sup> Nanao azy ho tompon’ny tranony izy Sy ho mpanapaka ny fananany rehetra,",
			"22": "<sup>22</sup> Mba hamatotra ny mpanapaka araka ny sitrapony, Sy hampianatra fahendrena ny loholony.",
			"23": "<sup>23</sup> Ary Isiraely nankany Egypta; Eny, Jakoba nivahiny tany amin’ny tanin’i Hama.",
			"24": "<sup>24</sup> Dia nahamaro ny taranaky ny olony ho be indrindra Izy Ka nampahery azy noho ny fahavalony.",
			"25": "<sup>25</sup> Nampiova ny fon’ireny hankahala ny olony Izy Sy hihendry ny mpanompony.",
			"26": "<sup>26</sup> Naniraka an’i Mosesy mpanompony Sy Arona izay nofidiny Izy.",
			"27": "<sup>27</sup> Ary izy ireo nanao ny famantarany teo aminy Sy fahagagana tany amin’ny tanin’i Hama.",
			"28": "<sup>28</sup> Nahatonga aizina Izy ka nahamaizina; Ary tsy nandà ny teniny izy ireo.",
			"29": "<sup>29</sup> Nahatonga ny ranony ho ra Izy Ka nahafaty ny hazandranony.",
			"30": "<sup>30</sup> Nisy sahona betsaka ny taniny, Eny, na dia hatrao an-trano fandrian’ny mpanjakany aza.",
			"31": "<sup>31</sup> Niteny Izy, dia tonga ny lalitra betsaka Sy ny moka teny amin’ny faritaniny rehetra.",
			"32": "<sup>32</sup> Nahatonga havandra ho solon’ny ranonorana Izy Sy afo midedadeda teny amin’ny taniny.",
			"33": "<sup>33</sup> Namely ny voalobony sy ny aviaviny Izy Ka nanapatapaka ny hazo teo amin’ny taniny.",
			"34": "<sup>34</sup> Niteny Izy, dia tonga ny valala Sy ny sompanga tsy hita isa;",
			"35": "<sup>35</sup> Dia nihinana ny zava-maitso rehetra teny amin’ny taniny ireny Ka nandany ny vokatry ny taniny.",
			"36": "<sup>36</sup> Ary namely ny lahimatoa rehetra tany amin’ny taniny Izy, Dia ny voalohan’ainy rehetra.",
			"37": "<sup>37</sup> Ary dia nitondra ny olony nivoaka Izy sady nampahazo azy volafotsy sy volamena; Ary tsy nisy nangozohozo na dia iray akory aza tamin’ny fireneny.",
			"38": "<sup>38</sup> Faly Egypta tamin’ny nahalasanany; Fa efa nahazo azy ny fahatahorana ireo.",
			"39": "<sup>39</sup> Namelatra rahona ho fialokalofana Izy Ary afo ho fanazavana nony alina.",
			"40": "<sup>40</sup> Nangataka izy ireo, dia nahatonga papelika Izy, Ary mofon-danitra no namokisany azy.",
			"41": "<sup>41</sup> Nanokatra ny vatolampy Izy, dia nigororoana ny rano Ka nandriaka tamin’ny tany maina ho renirano.",
			"42": "<sup>42</sup> Fa nahatsiaro ny teniny masina Sy Abrahama mpanompony Izy,",
			"43": "<sup>43</sup> Dia nitondra ny olony nivoaka tamin’ny fifaliana Izy Ary ny voafidiny tamin’ny fihobiana;",
			"44": "<sup>44</sup> Ka dia nanome azy ny tanin’ny jentilisa Izy; Ary nandova ny vitan’ny firenena maro ireny,",
			"45": "<sup>45</sup> Mba hitandremany ny didiny, Sy hankatoavany ny lalàny. Haleloia."
		},
		"106": {
			"1": "<sup>1</sup> HALELOIA. Miderà an’i Jehovah, fa tsara Izy, Fa mandrakizay ny famindrampony.",
			"2": "<sup>2</sup> Iza no mahalaza ny asa lehibe ataon’i Jehovah, Na mahatonona ny fiderana Azy rehetra?",
			"3": "<sup>3</sup> Sambatra izay mitandrina ny mety Ka manao ny marina mandrakariva.",
			"4": "<sup>4</sup> Jehovah ô, tsarovy aho araka ny fankasitrahanao ny olonao; Vangio amin’ny famonjenao aho,",
			"5": "<sup>5</sup> Hahitako ny soa azon’ny voafidinao, Hifaliako amin’ny fifalian’ny firenenao, Hiravoravoako miaraka amin’ny lovanao.",
			"6": "<sup>6</sup> Efa nanota izahay mbamin’ny razanay, Efa nanao izay meloka sy ratsy izahay.",
			"7": "<sup>7</sup> Ny razanay tany Egypta tsy mba nandinika ny fahagagana nataonao; Tsy mba nahatsiaro ny habetsahan’ny famindramponao ireny, Fa niodina teo amoron-dranomasina, Dia teo amoron’ny Ranomasina Mena.",
			"8": "<sup>8</sup> Kanefa namonjy azy ihany Izy noho ny anarany, Mba hampahafantarany ny asany lehibe.",
			"9": "<sup>9</sup> Dia noteneniny mafy ny Ranomasina Mena, ka tonga maina iny; Ary nitondra azy nita ny rano lalina tahaka ny efitra Izy.",
			"10": "<sup>10</sup> Namonjy azy tamin’ny tànan’ny mpandrafy Izy Ary nanavotra azy tamin’ny tànan’ny fahavalo.",
			"11": "<sup>11</sup> Dia nosaronan’ny rano kosa ny rafiny, Ka tsy nisy niangana na dia iray akory aza.",
			"12": "<sup>12</sup> Dia nino ny teniny izy Ka nihira ny fiderana Azy.",
			"13": "<sup>13</sup> Nalaky nanadino ny asany izy Ka tsy niandry ny saina hatolony,",
			"14": "<sup>14</sup> Fa fatra-pila izy tany an-efitra, Sady naka fanahy an’Andriamanitra tany an-tany foana.",
			"15": "<sup>15</sup> Dia nomeny azy izay nilainy, Kanefa nampahahiaziny kosa izy.",
			"16": "<sup>16</sup> Dia nialona an’i Mosesy tany an-toby izy ireo Sady nialona an’i Arona, olo-masin’i Jehovah.",
			"17": "<sup>17</sup> Nisokatra ny tany, dia nitelina an’i Datana Ka nanarona ny antokon’ i Abìrama.",
			"18": "<sup>18</sup> Dia nisy afo nirehitra tamin’ny antokony; Lelafo no nandoro ny ratsy fanahy.",
			"19": "<sup>19</sup> Nanao ombilahy kely tany Horeba izy Ka niankohoka teo anoloan’ny sarin-javatra an-idina.",
			"20": "<sup>20</sup> Ary ny voninahiny dia natakalony Sarin’ny omby homana ahitra.",
			"21": "<sup>21</sup> Nohadinoiny Andriamanitra, Mpamonjy azy, Izay efa nanao zava-dehibe tany Egypta,",
			"22": "<sup>22</sup> Dia fahagagana tany amin’ny tanin’i Hama Sy zava-mahatahotra teo amin’ny Ranomasina Mena.",
			"23": "<sup>23</sup> Ary nikasa handringana azy Izy, Raha tsy Mosesy voafidiny No nandroso hijoro tamin’ny banga teo anatrehany Hampiala ny fahatezerany mba tsy handringanany azy.",
			"24": "<sup>24</sup> Ary nanamavo ny tany mahafinaritra ireo Sady tsy mba nino ny teniny,",
			"25": "<sup>25</sup> Fa nimonomonona tany an-dainy Ka tsy nihaino ny feon’i Jehovah.",
			"26": "<sup>26</sup> Dia nanandratra ny tànany taminy Izy, Fa hampiampatrampatra azy any an-efitra.",
			"27": "<sup>27</sup> Ary ny taranany kosa any amin’ny jentilisa, Ary hampihahakahaka azy any amin’ny tany maro Izy.",
			"28": "<sup>28</sup> Nikambana tamin’i Bala-peora ireo Ka nihinana ny fanatitra ho an’ny maty,",
			"29": "<sup>29</sup> Dia nampahatezitra an’i Jehovah tamin’ny nataony, Ka dia namely azy tampoka ny areti-mandringana.",
			"30": "<sup>30</sup> Fa nitsangana nandroso Finehasa ka nanao fitsarana; Dia nitsahatra ny areti-mandringana.",
			"31": "<sup>31</sup> Ary nisaina ho fahamarinany izany Hatramin’ny taranaka fara mandimby mandrakizay.",
			"32": "<sup>32</sup> Nampahatezitra an’i Jehovah teo anilan’ny ranon’i Meriba ireo, Ka nisy nanjo an’i Mosesy noho ny nataon’ireo;",
			"33": "<sup>33</sup> Fa niodina tamin’ny Fanahin’i Jehovah ireo, Ka dia nihoa-bava Mosesy.",
			"34": "<sup>34</sup> Tsy naringany ny firenena Izay nasain’i Jehovah naringany;",
			"35": "<sup>35</sup> Fa nifangaro tamin’ny jentilisa izy Ka nianatra ny fanaony,",
			"36": "<sup>36</sup> Dia nanompo ny sampiny izy, Ka dia nody fandrika ho azy izany.",
			"37": "<sup>37</sup> Namono ny zanani-lahy sy ny zanani-vavy ho fanatitra ho an’ny demonia ireo;",
			"38": "<sup>38</sup> Eny, nandatsaka ra marina izy, Dia ny ran’ny zanani-lahy sy ny zanani-vavy, Izay novonoiny ho fanatitra ho an’ny sampin’i Kanana; Ka dia nametaveta ny tany tamin’ny ra izy.",
			"39": "<sup>39</sup> Ary naloto tamin’ny asany izy Ary nahatonga azy ho mijangajanga ny nataony.",
			"40": "<sup>40</sup> Dia nirehitra tamin’ny olony ny fahatezeran’i Jehovah, Ka nataony ho zava-betaveta ny lovany.",
			"41": "<sup>41</sup> Dia nanolotra azy teo an-tànan’ny jentilisa Izy, Ka izay nankahala azy no nanapaka azy.",
			"42": "<sup>42</sup> Ary nampahory azy ny fahavalony, Ka naetry tambanin’ny tànany izy.",
			"43": "<sup>43</sup> Matetika Izy no nahafaka azy; Fa ireo kosa niodina taminy ihany araka ny fisainany Ka nihalevona noho ny helony.",
			"44": "<sup>44</sup> Kanefa nijery ny fahoriany ihany Izy, Raha nandre ny fitarainany;",
			"45": "<sup>45</sup> Dia nahatsiaro ny fanekeny Izy Ka nanenina araka ny haben’ny famindrampony.",
			"46": "<sup>46</sup> Dia nataony nahita famindrampo ireny Tamin’izay rehetra namabo azy.",
			"47": "<sup>47</sup> Vonjeo izahay, ry Jehovah Andriamanitray ô, Ka angony avy amin’ny jentilisa, Mba hisaoranay ny anaranao masina Sy hifalianay amin’ny fiderana Anao.",
			"48": "<sup>48</sup> Isaorana anie Jehovah, Andriamanitry ny Isiraely, hatramin’ny taloha indrindra ka mandrakizay; Ary aoka ny olona rehetra hanao hoe: Amena! Haleloia."
		},
		"107": {
			"1": "<sup>1</sup> MIDERÀ an’i Jehovah, fa tsara Izy Fa mandrakizay ny famindrampony.",
			"2": "<sup>2</sup> Aoka hanao izany ny navotan’i Jehovah, Dia izay navotany ho afaka tamin’ny tànan’ny fahavalo,",
			"3": "<sup>3</sup> Sy nangoniny avy tany amin’ny tany maro, Dia avy tany atsinanana sy andrefana ary tany avaratra sy tany amin’ny ranomasina.",
			"4": "<sup>4</sup> Nirenireny tany an-efitra ireny ka nanavatsava tany an-tany foana, Ary tsy nahita tanàna honenana.",
			"5": "<sup>5</sup> Noana sy nangetaheta izy, Sady reraka ny ainy tao anatiny.",
			"6": "<sup>6</sup> Dia nitaraina tamin’i Jehovah ireo, raha ory, Ka novonjeny tamin’ny fahatereny;",
			"7": "<sup>7</sup> Dia nitondra azy tamin’ny làlana mahitsy Izy, Mba hankanesany any amin’izay tanàna honenana.",
			"8": "<sup>8</sup> Aoka hidera an’i Jehovah ireo noho ny famindrampony Sy ny fahagagana ataony amin’ny zanak’olombelona!",
			"9": "<sup>9</sup> Fa mahafa-po ny fanahy mangetaheta Izy, Ary ny fanahy noana dia vokisany soa.",
			"10": "<sup>10</sup> Izay nipetraka tao amin’ny maizina sy ny aloky ny fahafatesana, Voafato-pahoriana sy vy,",
			"11": "<sup>11</sup> Satria nandà ny tenin’Andriamanitra Sady naniratsira ny anatry ny Avo Indrindra;",
			"12": "<sup>12</sup> Dia nampietreny tamin’ny fahoriana ny fony; Ka potraka izy, nefa tsy nisy hamonjy;",
			"13": "<sup>13</sup> Dia nitaraina tamin’i Jehovah ireo, raha ory, Ka novonjeny tamin’ny fahatereny.",
			"14": "<sup>14</sup> Nitondra azy nivoaka avy tao amin’ny maizina sy ny aloky ny fahafatesana Izy, Ary notosany ny fatorany.",
			"15": "<sup>15</sup> Aoka hidera an’i Jehovah ireo noho ny famindrampony Sy ny fahagagana ataony amin’ny zanak’olombelona!",
			"16": "<sup>16</sup> Fa nanorotoro ny varavarana varahina Izy, Ary ny hidy vy nohenteriny.",
			"17": "<sup>17</sup> Mitondra fahoriana ny adala noho ny fahadisoany sy ny helony:",
			"18": "<sup>18</sup> Nahamonamonaina azy ny hanina rehetra; Ary nanakaiky ny vavahadin’ny fahafatesana izy.",
			"19": "<sup>19</sup> Dia nitaraina tamin’i Jehovah ireo, raha ory, Ka novonjeny tamin’ny fahatereny.",
			"20": "<sup>20</sup> Nandefa ny teniny Izy mba hahasitrana azy Ary nanafaka azy tamin’ny longoa nianjerany.",
			"21": "<sup>21</sup> Aoka hidera an’i Jehovah ireo noho ny famindrampony Sy ny fahagagana ataony amin’ny zanak’olombelona!",
			"22": "<sup>22</sup> Ary aoka hanatitra fanati-pisaorana izy Sy hilaza ny asany amin’ny fihobiana.",
			"23": "<sup>23</sup> Izay midina any an-dranomasina ka mandeha an-tsambo Sy manao raharaha any amin’ny rano lehibe",
			"24": "<sup>24</sup> No mahita ny asan’i Jehovah Sy ny fahagagana ataony amin’ny rano lalina.",
			"25": "<sup>25</sup> Fa niteny Izy ka nanangana rivotra mahery, Izay nampisondrotra ny onjan-drano;",
			"26": "<sup>26</sup> Niakatra hatramin’ny lanitra ny olona, ary nidina hatramin’ny lalina; Ny fanahiny levona noho ny fahoriana.",
			"27": "<sup>27</sup> Nisangodingodina ireo sady nivembena toy ny mamo, Ka dia very ny sainy rehetra.",
			"28": "<sup>28</sup> Dia nitaraina tamin’i Jehovah ireo raha ory, Ka nampiala azy tamin’ny fahatereny Izy.",
			"29": "<sup>29</sup> Nanony ny rivotra mahery Izy, Ka nitsahatra ny onjan-drano.",
			"30": "<sup>30</sup> Dia faly ireo, satria nitsahatra izany; Ary nentiny ho amin’ny fitodiana niriny izy.",
			"31": "<sup>31</sup> Aoka hidera an’i Jehovah ireo noho ny famindrampony Sy ny fahagagana ataony amin’ny zanak’olombelona!",
			"32": "<sup>32</sup> Ary aoka hanandratra Azy eo amin’ny fiangonan’ny olona izy Ka hidera Azy eo amin’ny fipetrahan’ny loholona.",
			"33": "<sup>33</sup> Mampody ny ony ho efitra Izy Ary ny loharano ho tany mangentana,",
			"34": "<sup>34</sup> Ny tany mahavokatra ho tanin-tsira, Noho ny faharatsian’ny mponina eo.",
			"35": "<sup>35</sup> Mampody ny efitra ho rano monina Izy Sy ny tany maina ho loharano,",
			"36": "<sup>36</sup> Ary amponeniny eo izay noana, Mba hanamboatra tanàna honenana;",
			"37": "<sup>37</sup> Ary hamafy eny an-tsaha izy Sady hanao tanimboaloboka Ka hahazo vokatra be.",
			"38": "<sup>38</sup> Dia mitahy azy Izy mba hitomboany betsaka, Ka tsy avelany hihavitsy ny biby fiompiny.",
			"39": "<sup>39</sup> Fa mihakely sy mietry indray izy Noho ny fampahoriana sy ny loza ary ny alahelo.",
			"40": "<sup>40</sup> Mampidina fanamavoana amin’ny mpanapaka Jehovah Ka mampirenireny azy any an-efitra tsy misy làlana;",
			"41": "<sup>41</sup> Fa manandratra ny malahelo ho afa-pahoriana kosa izy, Ka mahamaro mianakavy azy toy ny ondry andiany.",
			"42": "<sup>42</sup> Hahita izany ny marina ka ho faly; Fa hihombom-bava kosa ny ratsy fanahy rehetra.",
			"43": "<sup>43</sup> Izay hendry dia aoka hahafantatra izany Ka hahalala ny famindrampon’i Jehovah."
		},
		"108": {
			"1": "<sup>1</sup> Tonon-kira. Salamo nataon’i Davida. ANDRIAMANITRA ô, tafatoetra ny foko; Ny fanahiko no hihira sy hankalaza.",
			"2": "<sup>2</sup> Mifohaza, ry valiha sy lokanga; hifoha maraina koa aho.",
			"3": "<sup>3</sup> Hidera Anao eny amin’ny firenena maro aho, Jehovah ô, Ary hankalaza Anao eny amin’ny jentilisa.",
			"4": "<sup>4</sup> Fa lehibe mihoatra ny lanitra ny famindramponao; Ary mipaka eny amin’ny rahona ny fahamarinanao.",
			"5": "<sup>5</sup> Misandrata any ambonin’ny lanitra Hianao, Andriamanitra ô; Ary aoka ho etỳ ambonin’ny tany rehetra ny voninahitrao.",
			"6": "<sup>6</sup> Mba hahafaka ny malalanao, Dia vonjeo amin’ny tànanao ankavanana aho, ka valio.",
			"7": "<sup>7</sup> Andriamanitra niteny tamin’ny fahamasinany hoe: Hifaly Aho ka hizara an’i Sekema, Ary ny lohasahan’i Sokota horefesiko;",
			"8": "<sup>8</sup> Ahy Gileada, Ahy Manase; Ary aron’ny lohako Efraima; Tehim-panjakako Joda;",
			"9": "<sup>9</sup> Tavindranoko fanasana Moaba; Edoma no hanipazako ny kapako; Noho ny amin’i Filistia no hihobiako.",
			"10": "<sup>10</sup> Iza no hitondra ahy ho any an-tanàna mafy? Iza no hitarika ahy ho any Edoma?",
			"11": "<sup>11</sup> Tsy Hianao va no efa nanary anay, Andriamanitra ô, Sady tsy mivoaka miaraka amin’ny miaramilanay, Andriamanitra ô?",
			"12": "<sup>12</sup> Vonjeo amin’ny fahavalo izahay; Fa zava-poana ny famonjena ataon’ny olona.",
			"13": "<sup>13</sup> Andriamanitra no haharezantsika; Fa Izy no hanitsakitsaka ny fahavalontsika."
		},
		"109": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Salamo nataon’i Davida. RY Andriamanitra fiderako ô, aza mangina Hianao,",
			"2": "<sup>2</sup> Fa nisoka-bava ny ratsy fanahy sy ny mpamitaka hamely ahy; Niteny ahy tamin’ny lela mandainga izy.",
			"3": "<sup>3</sup> Teny fankahalana no nanodidinany ahy; Ary niady tamiko tsy ahoan-tsy ahoana izy.",
			"4": "<sup>4</sup> Ny fitiavako dia valiany fandrafiana; Fa izaho kosa dia mivavaka.",
			"5": "<sup>5</sup> Ny soa nataoko novaliany ratsy, Ary ny fitiavako novaliany fankahalana.",
			"6": "<sup>6</sup> Anendreo olona ratsy fanahy hanapaka azy; Ary aoka hisy mpanohitra hitsangana eo an-kavanany.",
			"7": "<sup>7</sup> Raha hotsaraina izy, dia aoka ho meloka; Ary aoka ny fivavahany ho tonga fahotana.",
			"8": "<sup>8</sup> Aoka ho vitsy ny androny; Ary aoka ho lasan’olon-kafa ny anjara-raharahany.",
			"9": "<sup>9</sup> Aoka ho kamboty ny zanany; Ary aoka ho mpitondratena ny vadiny.",
			"10": "<sup>10</sup> Aoka hirenireny mangataka ny zanany, Ary aoka hiremby lavitra ny tranony efa rava izy.",
			"11": "<sup>11</sup> Aoka ny mpampanàna vola hamandrika izay rehetra ananany; Ary aoka ny olon-kafa hifaoka ny vokatry ny asany.",
			"12": "<sup>12</sup> Aoka tsy hisy haharitra hamindra fo aminy, Na hiantra ny zanany kamboty.",
			"13": "<sup>13</sup> Aoka ho fongotra ny taranany; Ary aoka hovonoina amin’ny taranaka mandimby ny anarany.",
			"14": "<sup>14</sup> Aoka hotsarovan’i Jehovah ny helo-drainy; Ary aoka tsy hovonoina ny helo-dreniny.",
			"15": "<sup>15</sup> Aoka ho eo anatrehan’i Jehovah mandrakariva izany, Ary hofoanany tsy ho eo ambonin’ny tany anie ny fahatsiarovana azy.",
			"16": "<sup>16</sup> Satria tsy mba nahatsiaro hamindra fo izy, Fa nanenjika ny ory sy ny malahelo Ary ny reraka am-po mba hahafaty azy;",
			"17": "<sup>17</sup> Tia ozona izy, ka nanody azy izany; Tsy tia indrafo izy, ka nanalavitra azy izany.",
			"18": "<sup>18</sup> Nitafy ozona tahaka ny lambany izy, Ka niditra tao an-kibony tahaka ny rano izany, Ary tao amin’ny taolany tahaka ny diloilo;",
			"19": "<sup>19</sup> Dia aoka ho tahaka ny lamba itafiany izany, Ary ho fisikinana isikinany mandrakariva.",
			"20": "<sup>20</sup> Izany no famaliana avy amin’i Jehovah ho an’ny mpanohitra ahy, Dia izay manendrikendrika ahy.",
			"21": "<sup>21</sup> Fa Hianao, Jehovah Tompo ô, miasa ho ahy noho ny anaranao; Vonjeo aho, fa tsara ny famindramponao.",
			"22": "<sup>22</sup> Fa mahantra sy malahelo aho, Ary ny foko dia voatsindrona ato anatiko.",
			"23": "<sup>23</sup> Lasa tahaka ny aloka mihelina aho; Roahina tahaka ny valala aho.",
			"24": "<sup>24</sup> Efa malemy ny lohaliko noho ny fifadian-kanina; Ary efa mihena ny hatavin’ny nofoko.",
			"25": "<sup>25</sup> Ary izaho dia tonga fandàtsan’ireny; Mijery ahy izy ka mihifikifi-doha.",
			"26": "<sup>26</sup> Ampio aho, Jehovah Andriamanitro ô; Vonjeo araka ny famindramponao aho;",
			"27": "<sup>27</sup> Ary aoka ho fantany fa tànanao izany; Hianao, Jehovah ô, no nanao izany.",
			"28": "<sup>28</sup> Manozona izy ireo, fa Hianao kosa mitahy; Mitsanga-menatra izy, fa mifaly kosa ny mpanomponao.",
			"29": "<sup>29</sup> Mitafy henatra ny fahavaloko Ka misaron-kenatra tahaka ny lamba.",
			"30": "<sup>30</sup> Hidera an’i Jehovah indrindra amin’ny vavako aho; Eny, eo amin’ny olona maro no hiderako Azy.",
			"31": "<sup>31</sup> Fa mitsangana eo an-kavanan’ny malahelo Izy Mba hamonjy azy amin’izay manameloka ny fanahiny."
		},
		"110": {
			"1": "<sup>1</sup> Salamo nataon’i Davida. IZAO no lazain’i Jehovah amin’ny Tompoko: Mipetraha eo an-tànako ankavanana Ambara-panaoko ny fahavalonao ho fitoeran-tongotrao.",
			"2": "<sup>2</sup> Ny tehin’ny herinao Havoak’i Jehovah avy ao Ziona; Manapaha eo amin’ny fahavalonao, Hianao.",
			"3": "<sup>3</sup> Ny olonao dia mazoto manolo-tena amin’ny andro anafihanao; Amin’ny fihaingoana masina, tahaka ny ando ateraky ny maraina ho Anao ny zatovonao.",
			"4": "<sup>4</sup> Efa nianiana Jehovah ka tsy hanenina: Hianao no Mpisorona mandrakizay Araka ny fanaon’i Melkizedeka.",
			"5": "<sup>5</sup> Ny Tompo eo an-kavananao Dia handripaka ny mpanjaka maro amin’ny andron’ny fahatezerany.",
			"6": "<sup>6</sup> Hitsara any amin’ny jentilisa Izy Ka hampiampatrampatra faty any; Handripaka loholona any amin’ny tany malalaka Izy.",
			"7": "<sup>7</sup> Hisotro amin’ny ony any an-dàlana Izy, Ka izany no hitrahan’ny lohany."
		},
		"111": {
			"1": "<sup>1</sup> HALELOIA. Hidera an’i Jehovah amin’ny foko rehetra aho, Eo amin’ny fivorian’ny marina sy ny fiangonan-dehibe.",
			"2": "<sup>2</sup> Lehibe ny asan’i Jehovah, Ka dinihin’izay rehetra mankasitraka azy.",
			"3": "<sup>3</sup> Madera sady malaza ny asany; Ary ny fahamarinany maharitra mandrakizay.",
			"4": "<sup>4</sup> Manao izay hahatsiarovana ny fahagagana ataony Izy; Miantra sy mamindra fo Jehovah.",
			"5": "<sup>5</sup> Nomeny hanina izay matahotra Azy; Tsarovany mandrakizay ny fanekeny.",
			"6": "<sup>6</sup> Ny herin’ny asany nasehony tamin’ny olony, Tamin’ny nanomezany azy ny lovan’ny jentilisa.",
			"7": "<sup>7</sup> Ny asan’ny tànany dia fahamarinana sy fitsarana; Mahatoky avokoa ny didiny rehetra;",
			"8": "<sup>8</sup> Mitoetra mafy mandrakizay doria ireny, Fa natao tamin’ny fahamarinana sy ny fahitsiana.",
			"9": "<sup>9</sup> Manatitra fanavotana ho an’ny olony Izy; Efa nanorina ny fanekeny ho mandrakizay Izy; Masina sy mahatahotra ny anarany.",
			"10": "<sup>10</sup> Ny fahatahorana an’i Jehovah no fiandoham-pahendrena; Fahalalana tsara no ananan’izay rehetra mankatò izany; Maharitra mandrakizay ny fiderana Azy."
		},
		"112": {
			"1": "<sup>1</sup> HALELOIA. Sambatra ny olona izay matahotra an’i Jehovah Sady mankasitraka ny didiny indrindra.",
			"2": "<sup>2</sup> Hahery ambonin’ny tany ny zanany; Hotahina ny taranaky ny marina.",
			"3": "<sup>3</sup> Fananana sy harena no ao an-tranony; Ary ny fahamarinany haharitra mandrakizay.",
			"4": "<sup>4</sup> Ao amin’ny maizina no iposahan’ny mazava ho an’ny mahitsy; Mamindra fo sy miantra ary marina ireny.",
			"5": "<sup>5</sup> Sambatra ny olona izay miantra ka mampisambotra; Tsy mba ho resy eo am-pitsarana izy.",
			"6": "<sup>6</sup> Fa tsy hangozohozo mandrakizay izy; Hotsarovana mandrakizay ny marina.",
			"7": "<sup>7</sup> Tsy mba manahinahy hahare loza izy; Tafatoetra matoky an’i Jehovah ny fony.",
			"8": "<sup>8</sup> Voatohana ny fony, ka tsy hatahotra izy, Mandra-pifaliny mahita ny amin’ny mpandrafy azy.",
			"9": "<sup>9</sup> Namafy izy, eny, nanome ho an’ny malahelo; Ny fahamarinany maharitra mandrakizay; Hisandratra amim-boninahitra ny tandrony.",
			"10": "<sup>10</sup> Ny ratsy fanahy hahita ka ho sosotra; Hihidy vazana izy ka ho levona; Ho foana ny fanirian’ny ratsy fanahy."
		},
		"113": {
			"1": "<sup>1</sup> HALELOIA. Miderà, hianareo mpanompon’i Jehovah, Miderà ny anaran’i Jehovah.",
			"2": "<sup>2</sup> Isaorana anie ny anaran’i Jehovah Hatramin’izao ka ho mandrakizay.",
			"3": "<sup>3</sup> Hatramin’ny fiposahan’ny masoandro ka hatramin’ny filentehany No hoderaina anie ny anaran’i Jehovah.",
			"4": "<sup>4</sup> Avo ambonin’ny jentilisa Jehovah; Ambonin’ny lanitra ny voninahiny.",
			"5": "<sup>5</sup> Iza no tahaka an’i Jehovah Andriamanitsika, Izay mipetraka any amin’ny avo,",
			"6": "<sup>6</sup> Nefa miondrika mijery Ny any an-danitra sy ny etỳ an-tany?",
			"7": "<sup>7</sup> Manangana ny malahelo hiala amin’ny vovoka Izy Ary manandratra ny mahantra ho afaka eo amin’ny zezika,",
			"8": "<sup>8</sup> Mba hametraka azy ho naman’ny mpanapaka ny olony.",
			"9": "<sup>9</sup> Mampitoetra tsara ny momba Izy, Fa mahatonga azy ho renin-jaza mifaly. Haleloia."
		},
		"114": {
			"1": "<sup>1</sup> FONY nivoaka avy tany Egypta ny Isiraely, Dia ny taranak’i Jakoba avy tany amin’ny firenena hafa fiteny,",
			"2": "<sup>2</sup> Joda no fitoerany masina, Isiraely no fanjakany.",
			"3": "<sup>3</sup> Ny ranomasina nahita ka nandositra; Ary niorika Jordana.",
			"4": "<sup>4</sup> Ny tendrombohitra dia nifalihavanja tahaka ny ondrilahy, Ary ny havoana tahaka ny zanak’ondry.",
			"5": "<sup>5</sup> Inona izato mahazo anao, ry ranomasina, no mandositra hianao? Ary hianao, ry Jordana, no miorika hianao?",
			"6": "<sup>6</sup> Ary hianareo tendrombohitra, no mifalihavanja tahaka ny ondrilahy; Ary hianareo havoana, tahaka ny zanak’ondry?",
			"7": "<sup>7</sup> Mihorohoroa eo anatrehan’ny Tompo, ry tany, Dia eo anatrehan’Andriamanitr’i Jakoba,",
			"8": "<sup>8</sup> Izay mampody ny vatolampy ho rano monina Ary ny afovato ho loharano."
		},
		"115": {
			"1": "<sup>1</sup> JEHOVAH ô, aza izahay, aza izahay no omena voninahitra, Fa ny anaranao ihany, Noho ny famindramponao sy ny fahamarinanao.",
			"2": "<sup>2</sup> Nahoana ny jentilisa no manao hoe: Aiza izay Andriamaniny?",
			"3": "<sup>3</sup> Nefa any an-danitra Andriamanitsika; Efa nanao izay sitrapony rehetra Izy.",
			"4": "<sup>4</sup> Volafotsy sy volamena ny sampin’ireny, Asan’ny tànan’olona:",
			"5": "<sup>5</sup> Manam-bava izy, fa tsy miteny; Mana-maso izy, fa tsy mahita;",
			"6": "<sup>6</sup> Manan-tsofina izy, fa tsy mandre; Manana orona izy, fa tsy manimbolo;",
			"7": "<sup>7</sup> Manan-tànana izy, fa tsy mandray; Manan-tongotra izy, fa tsy mandeha; Ary tsy mahaloa-peo ny tendany.",
			"8": "<sup>8</sup> Ho tahaka azy izay manao azy Sy izay rehetra matoky azy.",
			"9": "<sup>9</sup> Ry Isiraely ô, matokia an’i Jehovah; Heriny sy ampingany Izy.",
			"10": "<sup>10</sup> Ry taranak’i Arona ô, matokia an’i Jehovah; Heriny sy ampingany Izy.",
			"11": "<sup>11</sup> Hianareo izay matahotra an’i Jehovah, matokia an’i Jehovah; Heriny sy ampingany Izy.",
			"12": "<sup>12</sup> Jehovah mahatsiaro ka mitahy antsika; Hitahy ny taranak’i Isiarely Izy; Hitahy ny taranak’i Arona Izy;",
			"13": "<sup>13</sup> Hotahiny izay matahotra an’i Jehovah, Na ny kely na ny lehibe.",
			"14": "<sup>14</sup> Jehovah anie hampitombo anareo, Dia hianareo mbamin’ny zanakareo.",
			"15": "<sup>15</sup> Hitahy anareo anie Jehovah, Mpanao ny lanitra sy ny tany.",
			"16": "<sup>16</sup> Ny lanitra dia an’i Jehovah; Fa ny tany nomeny ho an’ny zanak’olombelona.",
			"17": "<sup>17</sup> Ny maty tsy mba hidera an’i Jehovah, Na izay midina any amin’ny mangingina;",
			"18": "<sup>18</sup> Fa izahay hisaotra an’i Jehovah Hatramin’izao ka ho mandrakizay. Haleloia."
		},
		"116": {
			"1": "<sup>1</sup> IZAHO tia an’i Jehovah, satria efa nandre Ny feon’ny fifonako Izy.",
			"2": "<sup>2</sup> Satria efa nanongilana ny sofiny tamiko izy, Dia hiantso aho, raha mbola velona koa.",
			"3": "<sup>3</sup> Nihodidina tamiko ny famatoran’ny fahafatesana, Ary nahazo ahy ny fahorian’ny fiainan-tsi-hita; Nahita fahoriana sy alahelo aho.",
			"4": "<sup>4</sup> Dia niantso ny anaran’i Jehovah hoe aho: Jehovah ô, mifona aminao aho, vonjeo ny fanahiko.",
			"5": "<sup>5</sup> Mamindra fo sy marina Jehovah; Eny, miantra Andriamanitsika.",
			"6": "<sup>6</sup> Miaro ny kely saina Jehovah; Reraka aho, dia namonjy ahy Izy.",
			"7": "<sup>7</sup> Miverena ho amin’ny fitsaharanao, ry fanahiko, Fa Jehovah efa nanisy soa anao,",
			"8": "<sup>8</sup> Dia namonjy ny fanahiko tamin’ny fahafatesana Hianao Sy ny masoko tamin’ny ranomaso Ary ny tongotro tamin’ny fahatafintohinana.",
			"9": "<sup>9</sup> Handeha eo anatrehan’i Jehovah Eo amin’ny tanin’ny velona aho.",
			"10": "<sup>10</sup> Nino aho; hiteny tokoa aho; Mahantra indrindra aho.",
			"11": "<sup>11</sup> Hoy izaho tamin’ny fahatairako; Mpandainga ny olona rehetra.",
			"12": "<sup>12</sup> Inona no havaliko an’i Jehovah Noho ny soa rehetra nataony tamiko?",
			"13": "<sup>13</sup> Handray ny kapoaky ny famonjena aho Ka hiantso ny anaran’i Jehovah.",
			"14": "<sup>14</sup> Hanefa ny voadiko amin’i Jehovah aho. Eny, eo anatrehan’ny olony rehetra.",
			"15": "<sup>15</sup> Zava-dehibe eo imason’i Jehovah Ny fahafatesan’ny olony masina.",
			"16": "<sup>16</sup> Indrisy! Jehovah ô, mpanomponao tokoa aho, Eny, mpanomponao sady zanaky ny ankizivavinao; Namaha ny fatorako Hianao.",
			"17": "<sup>17</sup> Hianao no haterako fanati-pisaorana, Ary ny anaran’i Jehovah no hantsoiko.",
			"18": "<sup>18</sup> Hanefa ny voadiko amin’i Jehovah aho, Eny, eo anatrehan’ny olony rehetra,",
			"19": "<sup>19</sup> Dia eo an-kianjan’ny tranon’i Jehovah, Eo afovoanao, ry Jerosalema ô. Haleloia."
		},
		"117": {
			"1": "<sup>1</sup> MIDERÀ an’i Jehovah ry firenena rehetra; Miderà Azy, ry olona rehetra.",
			"2": "<sup>2</sup> Fa lehibe ny famindrampony amintsika; Ary mandrakizay ny fahamarinan’i Jehovah. Haleloia."
		},
		"118": {
			"1": "<sup>1</sup> MIDERÀ an’i Jehovah, fa tsara Izy; Eny, mandrakizay ny famindrampony.",
			"2": "<sup>2</sup> Aoka ny Isiraely hanao hoe: Mandrakizay ny famindrampony.",
			"3": "<sup>3</sup> Aoka ny taranak’i Arona hanao hoe: Mandrakizay ny famindrampony.",
			"4": "<sup>4</sup> Aoka izay matahotra an’i Jehovah hanao hoe: Mandrakizay ny famindrampony.",
			"5": "<sup>5</sup> Tao an-katerena no niantsoako an’i Jehovah; Dia namaly ahy Jehovah ka nampahalalaka ahy.",
			"6": "<sup>6</sup> Jehovah no miandany amiko, ka tsy hatahotra aho. Inona no azon’ny olona atao amiko?",
			"7": "<sup>7</sup> Jehovah no miandany amiko, sady mamonjy ahy; Ary izaho ho faly hahita ny amin’izay mankahala ahy.",
			"8": "<sup>8</sup> Tsara ny mialoka amin’i Jehovah Noho ny mialoka amin’ny olona.",
			"9": "<sup>9</sup> Tsara ny matoky an’i Jehovah Noho ny matoky ny mpanapaka.",
			"10": "<sup>10</sup> Ny firenena rehetra nanodidina ahy; Fa ny anaran’i Jehovah no handringanako azy tokoa;",
			"11": "<sup>11</sup> Nitangorona tamiko ireny, eny, nitangorona tamiko; Fa ny anaran’i Jehovah no handringanako azy tokoa.",
			"12": "<sup>12</sup> Nanodidina ahy tahaka ny renitantely izy; Voavono tahaka ny afon-tsilo izy; Fa ny anaran’i Jehovah no handringanako azy tokoa.",
			"13": "<sup>13</sup> Nanosika ahy mafy hahalavo ahy ianao; Fa Jehovah no namonjy ahy.",
			"14": "<sup>14</sup> Jehovah no heriko sy fidèrako. Fa efa famonjena ahy Izy.",
			"15": "<sup>15</sup> Ao an-dain’ny marina ny feo mihoby noho ny famonjena; Ny tànana ankavanan’i Jehovah mampiseho hery;",
			"16": "<sup>16</sup> Ny tànana ankavanan’i Jehovah dia misandratra; Ny tànana ankavanan’i Jehovah mampiseho hery.",
			"17": "<sup>17</sup> Tsy ho faty aho, fa ho velona Ka hitory ny asan’i Jehovah.",
			"18": "<sup>18</sup> Nofaizan’i Jehovah mafy aho, Nefa tsy natolony ho amin’ny fahafatesana.",
			"19": "<sup>19</sup> Vohay ny vavahadin’ny fahamarinana aho; Hiditra eo aho ka hidera an’i Jehovah.",
			"20": "<sup>20</sup> Ity no vavahadin’i Jehovah; Ny marina no hiditra eo.",
			"21": "<sup>21</sup> Hidera Anao aho, fa efa namaly ahy Hianao Ka efa famonjena ahy.",
			"22": "<sup>22</sup> Ny vato izay nolavin’ny mpanao trano No efa tonga fehizoro indrindra.",
			"23": "<sup>23</sup> Avy tamin’i Jehovah izany Ka mahagaga eo imasontsika.",
			"24": "<sup>24</sup> Ity no andro nataon’i Jehovah; Hifaly sy ho ravoravo amin’izao isika.",
			"25": "<sup>25</sup> Mifona aminao aho, Jehovah ô, vonjeo è! Mifona aminao aho, Jehovah ô, ambino è!",
			"26": "<sup>26</sup> Hotahina anie izay avy amin’ny anaran’i Jehovah; Avy ao an-tranon’i Jehovah no itsofanay rano anareo.",
			"27": "<sup>27</sup> Andriamanitra Jehovah ka nampahazava antsika; Rohizo kofehy ny fanati-piravoravoana na dia hatreo amin’ny tandroky ny alitara aza.",
			"28": "<sup>28</sup> Andriamanitro Hianao, ka hidera Anao aho; Eny, Andriamanitro, ka hanandratra Anao aho.",
			"29": "<sup>29</sup> Miderà an’i Jehovah, fa tsara Izy; Eny, mandrakizay ny famindrampony."
		},
		"119": {
			"1": "<sup>1</sup> SAMBATRA ny mahitsy làlana, Izay mandeha araka ny lalàn’i Jehovah.",
			"2": "<sup>2</sup> Sambatra izay mitandrina ny teni-vavolombelony Ka mitady Azy amin’ny fo rehetra;",
			"3": "<sup>3</sup> Eny, izay tsy manao ratsy, Fa mandeha araka ny làlany.",
			"4": "<sup>4</sup> Hianao namoaka ny didinao Mba hotandremana tsara.",
			"5": "<sup>5</sup> Enga anie ka ho tafatoetra ny làlako Hitandremako ny didinao!",
			"6": "<sup>6</sup> Dia tsy ho menatra aho, Raha mandinika ny didinao rehetra.",
			"7": "<sup>7</sup> Hidera Anao amin’ny hitsim-po aho Amin’ny ianarako ny fitsipikao marina.",
			"8": "<sup>8</sup> Ny didinao no hotandremako; Aza dia mahafoy ahy Hianao",
			"9": "<sup>9</sup> Inona no hiarovan’ny zatovo ny làlan-kalehany tsy ho voaloto? Ny fitandremana araka ny teninao.",
			"10": "<sup>10</sup> Tamin’ny foko rehetra no nitadiavako Anao; Aza avela hivily hiala amin’ny didinao aho.",
			"11": "<sup>11</sup> Ato am-poko no iraketako ny teninao, Mba tsy hanotako aminao.",
			"12": "<sup>12</sup> Isaorana anie Hianao, Jehovah ô; Ampianaro ahy ny didinao.",
			"13": "<sup>13</sup> Ny molotro no nentiko nilaza Ny fitsipika rehetra naloaky ny vavanao.",
			"14": "<sup>14</sup> Ny làlan’ny teni-vavolombelonao no ifaliako, Tahaka ny ifaliako amin’ny harena rehetra.",
			"15": "<sup>15</sup> Ny didinao no hosaintsainiko, Ary hodinihiko ny làlanao.",
			"16": "<sup>16</sup> Ny didinao no hiravoravoako; Tsy hohadinoiko ny teninao.",
			"17": "<sup>17</sup> Asio soa aho mpanomponao, mba ho velona Ka hitandrina ny teninao.",
			"18": "<sup>18</sup> Ampahirato ny masoko, mba hahitako Zava-mahagaga ao amin’ny lalànao.",
			"19": "<sup>19</sup> Vahiny etỳ an-tany aho; Aza afeninao ahy ny didinao.",
			"20": "<sup>20</sup> Torotoro ny fanahiko noho ny faniriany Ny fitsipikao mandrakariva.",
			"21": "<sup>21</sup> Voateninao mafy ny mpirehareha, dia ny olom-boaozona, Izay mania miala amin’ny didinao.",
			"22": "<sup>22</sup> Esory amiko ny fahafaham-baraka sy ny fanamavoana; Fa efa nitandrina ny teni-vavolombelonao aho.",
			"23": "<sup>23</sup> Na dia ny mpanapaka aza dia mipetraka, ka iokoany aho; Izaho mpanomponao misaintsaina ny didinao.",
			"24": "<sup>24</sup> Ny teni-vavolombelonao koa no firavoravoako, Eny, mpanolo-tsaina ahy izany.",
			"25": "<sup>25</sup> Miraikitra amin’ny vovoka ny fanahiko; Velomy aho araka ny teninao.",
			"26": "<sup>26</sup> Ny làlako no nolazaiko, ary namaly ahy Hianao; Ampianaro ahy ny didinao.",
			"27": "<sup>27</sup> Ampahalalao ahy ny làlan’ny didinao; Dia hoheveriko ny fahagagana nataonao.",
			"28": "<sup>28</sup> Mipotrapotra-dranomaso aho noho ny alaheloko; Ampaherezo aho araka ny teninao.",
			"29": "<sup>29</sup> Ny làlan’ny lainga no esory amiko; Ary ny lalànao no anasoavy ahy.",
			"30": "<sup>30</sup> Ny làlan’ny fahamarinanao no fidiko; Apetrako eo anatrehako ny fitsipikao.",
			"31": "<sup>31</sup> Mifikitra amin’ny teni-vavolombelonao aho; Jehovah ô, aza avela ho azon-kenatra aho.",
			"32": "<sup>32</sup> Ny làlan’ny didinao no ihazakazahako, Fa ampahalalahinao ny foko.",
			"33": "<sup>33</sup> Jehovah ô, toroy ny làlan’ny didinao aho; Dia hotandremako hatramin’ny farany izany.",
			"34": "<sup>34</sup> Omeo fahalalana aho mba hitandrina ny lalànao; Eny, hitandrina azy amin’ny foko rehetra aho.",
			"35": "<sup>35</sup> Ampandehano amin’ny làlan’ny didinao aho; Fa izany no sitrako.",
			"36": "<sup>36</sup> Amporisiho ny foko ho amin’ny teni-vavolombelonao, Fa tsy ho amin’ny fieremana.",
			"37": "<sup>37</sup> Avilio ny masoko tsy hijery zava-poana; Velomy amin’ny làlanao aho.",
			"38": "<sup>38</sup> Tovy ny teninao amin’ny mpanomponao, Izay matahotra Anao indrindra.",
			"39": "<sup>39</sup> Esory ny fahafaham-baraka, izay atahorako; Fa tsara ny fitsipikao.",
			"40": "<sup>40</sup> Indro, manina ny didinao aho, Velomy amin’ny fahamarinanao.",
			"41": "<sup>41</sup> Aoka ho tonga amiko ny famindramponao, Jehovah ô, Dia ny famonjenao, araka ny teninao.",
			"42": "<sup>42</sup> Dia hamaly izay miteny ratsy ahy aho, Satria matoky ny teninao.",
			"43": "<sup>43</sup> Ary aza dia esorina amin’ny vavako ny teny marina; Fa miandry ny fitsaranao aho.",
			"44": "<sup>44</sup> Dia hitandrina ny lalànao mandrakariva aho, Dia mandrakizay doria.",
			"45": "<sup>45</sup> Ary handeha amin’ny malalaka aho; Fa ny didinao no tadiaviko.",
			"46": "<sup>46</sup> Hilaza ny teni-vavolombelonao eo anatrehan’ireo mpanjaka aho Ka tsy ho menatra.",
			"47": "<sup>47</sup> Ary hiravoravo amin’ny didinao aho, Satria tiako ireny.",
			"48": "<sup>48</sup> Hasandratro ny tànako ho amin’ny didinao, satria tiako ireny; Hosaintsainiko ny didinao.",
			"49": "<sup>49</sup> Tsarovy ny teny natao tamiko mpanomponao, Izay nampanantenanao ahy.",
			"50": "<sup>50</sup> Izao no mampionona ahy amin’ny fahoriako; Ny teninao no mamelona ahy.",
			"51": "<sup>51</sup> Ny mpirehareha maniratsira ahy; Tsy mba mivily miala amin’ny lalànao aho.",
			"52": "<sup>52</sup> Mahatsiaro ny fitsipikao izay hatramin’ny taloha aho, Jehovah ô, Dia mionona aho.",
			"53": "<sup>53</sup> Fahatezerana mirehitra no nahazo ahy Noho ny amin’ny ratsy fanahy izay mahafoy ny lalànao.",
			"54": "<sup>54</sup> Ny didinao no ataoko an-kira Ao amin’ny trano fivahiniako.",
			"55": "<sup>55</sup> Mahatsiaro ny anaranao nony alina aho, Jehovah ô, Ka mitandrina ny lalànao.",
			"56": "<sup>56</sup> Izao no ahy: Mitandrina ny didinao aho.",
			"57": "<sup>57</sup> Anjarako Jehovah; Minia mitandrina ny teninao aho.",
			"58": "<sup>58</sup> Mifona aminao amin’ny foko rehetra aho; Mamindrà fo amiko araka ny teninao.",
			"59": "<sup>59</sup> Mihevitra ny làlako aho Ka mamerina ny tongotro ho amin’ny teni-vavolombelonao.",
			"60": "<sup>60</sup> Mandeha faingana aho ka tsy mitaredretra, Amin’ny fitandremana ny didinao.",
			"61": "<sup>61</sup> Nihodidina tamiko ny famatoran’ny ratsy fanahy; Tsy mba nanadino ny lalànao aho.",
			"62": "<sup>62</sup> Mamatonalina no hifohazako hidera Anao Noho ny fitsipikao marina.",
			"63": "<sup>63</sup> Naman’izay rehetra matahotra Anao aho Sy naman’izay mitandrina ny didinao.",
			"64": "<sup>64</sup> Jehovah ô, henika ny famindramponao ny tany; Ampianaro ahy ny didinao.",
			"65": "<sup>65</sup> Jehovah ô, efa nasianao soa ny mpanomponao, Araka ny teninao.",
			"66": "<sup>66</sup> Ampianaro fahafantarana tsara sy fahalalana aho, Fa matoky ny didinao aho.",
			"67": "<sup>67</sup> Fony tsy mbola azom-pahoriana aho, dia naniasia; Fa ankehitriny dia mitandrina ny teninao aho.",
			"68": "<sup>68</sup> Sady tsara Hianao no Mpanao ny tsara; Ampianaro ahy ny didinao.",
			"69": "<sup>69</sup> Namoron-dainga hamelezana ahy ny mpirehareha; Amin’ny foko rehetra no itandremako ny didinao.",
			"70": "<sup>70</sup> Matavy tahaka ny menaka ny fony; Fa izaho kosa, dia ny lalànao no iravoravoako.",
			"71": "<sup>71</sup> Soa ihany aho nampahorina, Mba hianarako ny didinao.",
			"72": "<sup>72</sup> Ny lalàn’ny vavanao no mahatsara ahy Mihoatra noho ny volamena sy ny volafotsy tsy omby arivo.",
			"73": "<sup>73</sup> Ny tànanao nanao ahy sy namboatra ahy; Ampianaro aho, mba hianarako ny didinao.",
			"74": "<sup>74</sup> Izay matahotra Anao hahita ahy ka ho faly; Fa ny teninao no andrasako.",
			"75": "<sup>75</sup> Fantatro, Jehovah ô, fa marina ny fitsaranao. Ary ny fahamarinanao no nampahorianao ahy.",
			"76": "<sup>76</sup> Ny famindramponao anie no hampiononana ahy Araka ny teninao tamin’ny mpanomponao.",
			"77": "<sup>77</sup> Aoka ho tonga amiko ny fiantranao, mba ho velona aho; Fa ny lalànao no firavoravoako.",
			"78": "<sup>78</sup> Aoka ho menatra ny mpirehareha, fa nampahory ahy tsy ahoan-tsy ahoana izy; Izaho hisaintsaina ny didinao.",
			"79": "<sup>79</sup> Aoka hiverina amiko izay matahotra Anao Sy izay mahalala ny teni-vavolombelonao.",
			"80": "<sup>80</sup> Aoka ho marina tsara ny foko amin’ny fitanana ny didinao, Mba tsy ho menatra aho.",
			"81": "<sup>81</sup> Ritra ny aiko, satria manina ny famonjenao aho; Ny teninao no andrasako.",
			"82": "<sup>82</sup> Pahina ny masoko miandry ny teninao, Ka hoy izaho: Rahoviana no hampionona ahy Hianao?",
			"83": "<sup>83</sup> Fa tahaka ny siny hoditra ao amin’ny setroka aho, Nefa ny didinao tsy mba hadinoiko.",
			"84": "<sup>84</sup> Hoatrinona no andron’ny mpanomponao? Rahoviana no hanao fitsarana amin’izay manenjika ahy Hianao?",
			"85": "<sup>85</sup> Efa nanao longoa hianjerako ny mpirehareha, Dia izay tsy manaraka ny lalànao.",
			"86": "<sup>86</sup> Marina ny didinao rehetra; Manenjika ahy tsy ahoan-tsy ahoana ireny; vonjeo aho.",
			"87": "<sup>87</sup> Saiky laniny teo amin’ny tany aho, Kanefa tsy mba mahafoy ny didinao.",
			"88": "<sup>88</sup> Velomy aho araka ny famindramponao; Dia hitandrina ny teni-vavolombelona naloaky ny vavanao aho.",
			"89": "<sup>89</sup> Ho mandrakizay, Jehovah ô, No nanorenanao any an-danitra ny teninao.",
			"90": "<sup>90</sup> Hihatra amin’ny taranaka fara mandimby ny fahamarinanao; Efa nampitoetra ny tany Hianao, ka, indro, mitoetra izy.",
			"91": "<sup>91</sup> Hanefa ny fitsaranao no itoerany mandraka andro-any; Fa mpanomponao ny zavatra rehetra.",
			"92": "<sup>92</sup> Raha tsy ny lalànao no niravoravoako, Dia maty tamin’ny fahoriako aho.",
			"93": "<sup>93</sup> Tsy hohadinoiko mandrakizay ny didinao; Fa ireny no amelomanao ahy.",
			"94": "<sup>94</sup> Anao aho, vonjeo aho; Fa ny didinao no tadiaviko.",
			"95": "<sup>95</sup> Miandry ahy ny ratsy fanahy mba hahafaty ahy; Mandinika ny teni-vavolombelonao aho",
			"96": "<sup>96</sup> Efa hitako fa manam-pahataperana avo koa ny tanteraka rehetra; Malalaka indrindra ny didinao.",
			"97": "<sup>97</sup> Endrey ny fitiavako ny lalànao! Fisaintsainako mandrakariva izany.",
			"98": "<sup>98</sup> Ny didinao mampahahendry ahy mihoatra noho ny fahavaloko, Satria ato amiko mandrakizay ireny.",
			"99": "<sup>99</sup> Hendry noho ny mpampianatra ahy rehetra aho, Satria ny teni-vavolombelonao no fisaintsainako.",
			"100": "<sup>100</sup> Hendry noho ny anti-panahy aho, Satria ny didinao no notandremako.",
			"101": "<sup>101</sup> Efa narovako ny tongotro tsy ho amin’izay làlan-dratsy, Mba hitandremako ny teninao.",
			"102": "<sup>102</sup> Tsy mba miala amin’ny fitsipikao aho; Fa Hianao no mampianatra ahy.",
			"103": "<sup>103</sup> Endrey ny hamamin’ny teninao, raha andramako! Mihoatra noho ny tantely ato am-bavako aza!",
			"104": "<sup>104</sup> Ny didinao no nahazoako fahalalana; Izany no ankahalako ny làlam-pitaka rehetra.",
			"105": "<sup>105</sup> Fanilon’ny tongotro Sy fanazavana ny làlako ny teninao.",
			"106": "<sup>106</sup> Efa nianiana aho fa hitandrina ny fitsipikao marina, Ka nefaiko izany.",
			"107": "<sup>107</sup> Ampahorina indrindra aho; Jehovah ô, velomy aho araka ny teninao.",
			"108": "<sup>108</sup> Jehovah ô, aoka anie mba hankasitrahanao ny fanati-tsitrapo aloaky ny vavako, Ary ampianaro ahy ny fitsipikao.",
			"109": "<sup>109</sup> An-tenda ihany ny aiko mandrakariva; Kanefa tsy manadino ny lalànao aho.",
			"110": "<sup>110</sup> Ny ratsy fanahy mamela-pandrika haningotra ahy; Kanefa tsy mania miala amin’ny didinao aho.",
			"111": "<sup>111</sup> Efa noraisiko ho lovako mandrakizay ny teni-vavolombelonao, Satria fifalian’ny foko izany.",
			"112": "<sup>112</sup> Apetrako ny foko hankatò ny didinao Mandrakizay hatramin’ny farany.",
			"113": "<sup>113</sup> Halako ny mpiroa saina; Fa ny lalànao no tiako.",
			"114": "<sup>114</sup> Fiereko sy ampingako Hianao; Ny teninao no andrasako.",
			"115": "<sup>115</sup> Mialà amiko hianareo mpanao ratsy, Mba hitandremako ny didin’Andriamanitro.",
			"116": "<sup>116</sup> Tohàny aho araka ny teninao, mba ho velona, Ary aoka tsy ho menatra amin’ny fanantenako aho.",
			"117": "<sup>117</sup> Tohàny aho mba ho voavonjy, Ka ho faly hijery ny didinao mandrakariva.",
			"118": "<sup>118</sup> Ataonao tsinontsinona izay rehetra mania amin’ny didinao; Fa famitahan-tena ihany ny laingany.",
			"119": "<sup>119</sup> Efa nataonao fanary tain-drendrika ny ratsy fanahy rehetra amin’ny tany; Izany no itiavako ny teni-vavolombelonao.",
			"120": "<sup>120</sup> Mipararetra ny nofoko ny fahatahorana Anao, Ary matahotra ny fitsaranao aho.",
			"121": "<sup>121</sup> Manaraka ny fitsipika sy ny fahamarinana aho; Aza manolotra ahy ho amin’ny mpampahory ahy.",
			"122": "<sup>122</sup> Iantohy mba ho soa ny mpanomponao; Aza avela hampahory ahy ny mpirehareha.",
			"123": "<sup>123</sup> Pahina ny masoko miandry ny famonjenao Sy ny tenin’ny fahamarinanao.",
			"124": "<sup>124</sup> Ataovy amin’ny mpanomponao araka ny famindramponao; Ary ampianaro ahy ny didinao.",
			"125": "<sup>125</sup> Mpanomponao aho, ka ampianaro, Mba ho fantatro ny teni-vavolombelonao.",
			"126": "<sup>126</sup> Efa andro tokony hampisehoan’i Jehovah ny heriny izao; Fa nandika ny lalànao ny olona.",
			"127": "<sup>127</sup> Izany no itiavako ny didinao mihoatra noho ny volamena, Eny, noho ny tena volamena aza.",
			"128": "<sup>128</sup> Izany no iheverako ny didy rehetra ho marina avokoa; Halako ny làlam-pitaka rehetra.",
			"129": "<sup>129</sup> Mahagaga ny teni-vavolombelonao, Ka dia mitandrina azy ny fanahiko.",
			"130": "<sup>130</sup> Mahazava ny famoahana ny hevitry ny teninao Ka manome fahalalana ho an’ny kely saina.",
			"131": "<sup>131</sup> Mitanatana vava aho ka mihanahana, Fa ny didinao no iriko.",
			"132": "<sup>132</sup> Todiho aho, ka mamindrà fo amiko, Araka ny tokony ho anjaran’izay tia ny anaranao.",
			"133": "<sup>133</sup> Ampitoero eo amin’ny teninao ny diako; Ary aza avela hanapaka ahy izay mety ho faharatsiana.",
			"134": "<sup>134</sup> Vonjeo amin’ny fampahorian’ny olona aho, Dia hitandrina ny didinao.",
			"135": "<sup>135</sup> Ampamirapirato amin’ny mpanomponao ny tavanao; Ary ampianaro ahy ny didinao.",
			"136": "<sup>136</sup> Rano mandriaka no mijononoka amin’ny masoko Noho ny tsi-fitandreman’ny olona ny lalànao.",
			"137": "<sup>137</sup> Marina Hianao, Jehovah ô, Ary mahitsy ny fitsipikao.",
			"138": "<sup>138</sup> Efa voadidinao amin’ny fahamarinana Sy ny fahatokiana indrindra ny teni-vavolombelonao.",
			"139": "<sup>139</sup> Efa maharìtra aina ahy ny fahasaro-piaroko, Satria nanadino ny teninao ny mpandrafy ahy.",
			"140": "<sup>140</sup> Voazaha toetra tamin’ny memy ny teninao, Ka dia tian’ny mpanomponao.",
			"141": "<sup>141</sup> Kely aho ka natao ho tsinontsinona; Nefa tsy hadinoiko ny didinao.",
			"142": "<sup>142</sup> Ny fahamarinanao dia fahamarinana mandrakizay, Ary marina ny lalànao.",
			"143": "<sup>143</sup> Fahoriana sy fahaterena no nahazo ahy; Ny didinao no firavoravoako.",
			"144": "<sup>144</sup> Ny fahamarinan’ny teni-vavolombelonao dia mandrakizay; Ampianaro aho, mba ho velona.",
			"145": "<sup>145</sup> Ny foko rehetra no nentiko nitaraina hoe: Valio aho, Jehovah ô; Ny didinao no hotandremako;",
			"146": "<sup>146</sup> Nitaraina taminao aho nanao hoe: Vonjeo aho, Dia hitandrina ny didinao.",
			"147": "<sup>147</sup> Raha mbola tsy nazavaratsy aza ny andro, dia nitaraina aho; Ny teninao no andrasako.",
			"148": "<sup>148</sup> Nialoha ny fiambenana amin’ny alina ny masoko, Mba hisaintsainako ny teninao.",
			"149": "<sup>149</sup> Henoy ny feoko araka ny famindramponao, Jehovah ô, Velomy aho araka ny fitsipikao.",
			"150": "<sup>150</sup> Efa mby akaiky izay fatra-panaraka ny ratsy, Dia izay manalavitra ny lalànao.",
			"151": "<sup>151</sup> Akaiky Hianao, Jehovah ô; Ary marina ny didinao rehetra.",
			"152": "<sup>152</sup> Efa fantatro ela tamin’ny tenivavolombelonao Fa nampitoerinao ho mandrakizay ireny.",
			"153": "<sup>153</sup> Jereo ny fahoriako, ka vonjeo aho; Fa ny lalànao tsy mba hadinoiko.",
			"154": "<sup>154</sup> Soloy vava aho, ka vonjeo; Velomy aho araka ny teninao.",
			"155": "<sup>155</sup> Lavitra ny ratsy fanahy ny famonjena, Satria ny didinao tsy tadiaviny.",
			"156": "<sup>156</sup> Lehibe ny antranao, Jehovah ô; Velomy aho araka ny fitsipikao.",
			"157": "<sup>157</sup> Maro ny mpanenjika sy mpandrafy ahy; Tsy mba niala tamin’ny teni-vavolombelonao aho.",
			"158": "<sup>158</sup> Mijery ny mpivadika aho, ka maharikoriko ahy ireny, Dia izay tsy nitandrina ny teninao.",
			"159": "<sup>159</sup> He ny fitiavako ny didinao! Jehovah ô, velomy aho araka ny famindramponao.",
			"160": "<sup>160</sup> Fahamarinana no tontalin-kevitry ny teninao; Ary mandrakizay ny fitsipikao marina rehetra.",
			"161": "<sup>161</sup> Na dia ny mpanapaka aza dia nanenjika ahy tsy ahoan-tsy ahoana; Nefa ny teninao no atahoran’ny foko.",
			"162": "<sup>162</sup> Faly aho noho ny teninao Tahaka ny mahazo babo be.",
			"163": "<sup>163</sup> Halako ny lainga ka ataoko ho fahavetavetana; Ny lalànao no tiako.",
			"164": "<sup>164</sup> Impito isan-andro no iderako Anao Noho ny fitsipikao marina.",
			"165": "<sup>165</sup> Be fiadanana izay tia ny lalànao, Ary tsy misy mahatafintohina azy.",
			"166": "<sup>166</sup> Miandry ny famonjenao aho, Jehovah ô, Ka mankatô ny didinao.",
			"167": "<sup>167</sup> Mitandrina ny teni-vavolombelonao ny fanahiko, Ka tiako indrindra izy.",
			"168": "<sup>168</sup> Mitandrina ny didinao sy ny teni-vavolombelonao aho; Fa ny làlan-kalehako rehetra dia eo anatrehanao.",
			"169": "<sup>169</sup> Aoka anie ho tonga eo anatrehanao ny fitarainako, Jehovah ô; Ampianaro aho araka ny teninao.",
			"170": "<sup>170</sup> Aoka anie ho tonga eo anatrehanao ny fifonako; Vonjeo aho araka ny teninao.",
			"171": "<sup>171</sup> Aoka hiboiboika fiderana ny molotro, Fa ampianarinao ahy ny didinao.",
			"172": "<sup>172</sup> Hihira ny teninao ny lelako, Fa marina ny didinao rehetra.",
			"173": "<sup>173</sup> Aoka anie hamonjy ahy ny tànanao; Fa efa nifidy ny didinao aho.",
			"174": "<sup>174</sup> Efa manina ny famonjenao aho, Jehovah ô; Ary ny lalànao no firavoravoako.",
			"175": "<sup>175</sup> Aoka anie ho velona ny fanahiko ka hidera Anao; Ary aoka anie hamonjy ahy ny fitsaranao.",
			"176": "<sup>176</sup> Efa nania aho; tadiavo ny mpanomponao toy ny fitady ondry very; Fa ny didinao tsy mba hadinoiko."
		},
		"120": {
			"1": "<sup>1</sup> Fihirana fiakarana. JEHOVAH no nitarainako tamin’ny fahoriako dia namaly ahy Izy,",
			"2": "<sup>2</sup> Jehovah ô, vonjeo ny fanahiko amin’ny molotra mandainga, Eny, amin’ny lela mamitaka.",
			"3": "<sup>3</sup> Inona no homeny anao, ary inona koa no hataony aminao, Ry lela mamitaka?",
			"4": "<sup>4</sup> Zana-tsipìkan’ny mahery sady voaranitra, Mbamin’ny vain’afon’anjavidy.",
			"5": "<sup>5</sup> Lozako, fa mivahiny eto amin’ny Maseka aho Ary mitoetra ato an-dain’ny Kedarita!",
			"6": "<sup>6</sup> Ela loatra izay no nitoeran’ny fanahiko Teo amin’izay tsy tia fihavanana.",
			"7": "<sup>7</sup> Raha izaho, dia fihavanana; Kanjo nony miteny aho, dia mila ady kosa ireny."
		},
		"121": {
			"1": "<sup>1</sup> Fihirana fiakarana. MANOPY ny masoko ho amin’ny tendrombohitra aho; Avy aiza ny famonjena ahy?",
			"2": "<sup>2</sup> Ny famonjena ahy dia avy amin’i Jehovah, Mpanao ny lanitra sy ny tany.",
			"3": "<sup>3</sup> Tsy mba havelany hangozohozo anie ny tongotrao; Tsy ho rendrehana anie ny Mpiaro anao.",
			"4": "<sup>4</sup> Indro, tsy matory na rendrehana Ny Mpiaro ny Isiraely.",
			"5": "<sup>5</sup> Jehovah no Mpiaro anao; Jehovah no fialofanao eo amin’ny ankavananao.",
			"6": "<sup>6</sup> Ny masoandro tsy mba hamely anao nony andro, Na ny volana nony alina.",
			"7": "<sup>7</sup> Jehovah hiaro anao amin’ny ratsy rehetra; Eny, hiaro ny fanahinao Izy.",
			"8": "<sup>8</sup> Jehovah hiaro anao, na mivoaka na miditra, Hatramin’izao ka ho mandrakizay."
		},
		"122": {
			"1": "<sup>1</sup> Fihirana fiakarana. Nataon’i Davida. FALY aho, raha hoy izy ireo tamiko: Andeha isika ho any an-tranon’i Jehovah.",
			"2": "<sup>2</sup> Ny tongotray efa mijoro Eo anatin’ny vavahadinao, ry Jerosalema ô,",
			"3": "<sup>3</sup> Dia hianao, ry Jerosalema, izay efa vita Tahaka ny tanàna efa voalamina tsara;",
			"4": "<sup>4</sup> Any no iakaran’ny firenena, Dia ireo firenen’i Jehovah (Lalàna amin’ny Isiraely izany), Mba hisaotra ny anaran’i Jehovah.",
			"5": "<sup>5</sup> Fa any no misy seza fitsarana mipetraka, Dia ny seza fiandrianan’ny mpianakavin’i Davida.",
			"6": "<sup>6</sup> Mangataha fiadanana ho an’i Jerosalema; Hambinina anie izay tia anao;",
			"7": "<sup>7</sup> Fiadanana anie ho ao anatin’ny mandanao; Fanambinana anie ho ao an-dapanao.",
			"8": "<sup>8</sup> Noho ny rahalahiko sy ny namako No hanaovako hoe: Fiadanana anie ho ao anatinao.",
			"9": "<sup>9</sup> Noho ny tranon’i Jehovah Andriamanitsika Dia hitady izay hahatsara anao aho."
		},
		"123": {
			"1": "<sup>1</sup> Fihirana fiakarana AMINAO no anandratako ny masoko, Ry Mpitoetra any an-danitra ô.",
			"2": "<sup>2</sup> Indro, toy ny fijerin’ny ankizilahy Ny tànan’ny tompolahiny, Ary toy ny fijerin’ny ankizivavy Ny tànan’ny tompovaviny, No fijerintsika an’i Jehovah Andriamanitsika, Mandra-pamindra fony amintsika.",
			"3": "<sup>3</sup> Mamindrà fo aminay, Jehovah ô, mamindrà fo aminay; Fa tototry ny fanamavoana loatra izahay;",
			"4": "<sup>4</sup> Eny, tototry ny famingavingan’ny miadana ny fanahinay, Sy tototry ny fanamavoan’ny mpiavonavona."
		},
		"124": {
			"1": "<sup>1</sup> Fihirana fiakarana. Nataon’i Davida. RAHA tsy Jehovah no nomba antsika,– Eny, aoka hataon’ny Isiraely hoe:",
			"2": "<sup>2</sup> Raha tsy Jehovah no nomba antsika, Raha nitsangana hamely antsika ny olona.",
			"3": "<sup>3</sup> Dia nitelina antsika velona izy, Raha nirehitra tamintsika ny fahatezerany;",
			"4": "<sup>4</sup> Dia nandifotra antsika ny rano, Ary nanafotra ny fanahintsika ny riaka;",
			"5": "<sup>5</sup> Eny, nanafotra ny fanahintsika Ny rano misafoaka.",
			"6": "<sup>6</sup> Isaorana anie Jehovah, Izay tsy nanolotra antsika ho rembin’ny nifiny.",
			"7": "<sup>7</sup> Ny fanahintsika dia efa afaka tahaka ny vorona afaka tamin’ny fandriky ny mpihaza; Tapaka ny fandrika, ka afaka isika.",
			"8": "<sup>8</sup> Ny famonjena antsika dia amin’ny anaran’i Jehovah, Mpanao ny lanitra sy ny tany."
		},
		"125": {
			"1": "<sup>1</sup> Fihirana fiakarana. IZAY matoky an’i Jehovah dia tahaka ny tendrombohitra Ziona, Izay tsy mihetsika, fa mitoetra mandrakizay.",
			"2": "<sup>2</sup> Tahaka ny tendrombohitra manodidina an’i Jerosalema No anodidinan’i Jehovah ny olony Hatramin’izao ka ho mandrakizay.",
			"3": "<sup>3</sup> Fa tsy hitoetra eo amin’ny anjaran’ny marina ny tehim-panjakan’ny ratsy fanahy, Fandrao ny marina haninjitra ny tànany hanendry zava-dratsy.",
			"4": "<sup>4</sup> Jehovah ô, manaova soa amin’ny tsara Sy ny mahitsy fo.",
			"5": "<sup>5</sup> Fa izay mivily ho amin’ny sakeli-dàlana mivilivily Dia horoahin’i Jehovah miaraka amin’ny mpanao ratsy anie. Fiadanana anie ho amin’ny Isiraely."
		},
		"126": {
			"1": "<sup>1</sup> Fihirana fiakarana. FONY Jehovah nampody an’i Ziona avy tamin’ny fahababoany, Dia tahaka izay nanonofy isika.",
			"2": "<sup>2</sup> Fahizay dia feno hehy ny vavantsika Sy fihobiana ny lelantsika; Dia natao tany amin’ny jentilisa hoe: Zava-dehibe no nataon’i Jehovah ho an’ireny.",
			"3": "<sup>3</sup> Eny, zava-dehibe no nataon’i Jehovah ho antsika, Ka dia faly isika.",
			"4": "<sup>4</sup> Tanteraho, Jehovah ô, ny hampodiana anay avy amin’ny fahababoana, Tahaka ny fiverenan’ny riaka any amin’ny tany atsimo.",
			"5": "<sup>5</sup> Izay mamafy am-pitomaniana Dia hijinja am-pifaliana.",
			"6": "<sup>6</sup> Mandeha mitomany mitondra ny voa afafiny izy, Fa hiverina mitondra ny amboarany amin’ny fihobiana kosa."
		},
		"127": {
			"1": "<sup>1</sup> Fihirana fiakarana. Nataon’i Solomona. RAHA tsy Jehovah no manao ny trano, Dia miasa foana ny mpanao azy. Raha tsy Jehovah no miambina ny tanàna, Dia miari-tory foana ny mpiambina.",
			"2": "<sup>2</sup> Zava-poana ho anareo ny mifoha maraina Sy ny alim-pandry Ka homana ny hanina azo amin’ny fahasahiranana; Izany anefa dia omeny ihany ho an’ny malalany, na dia matory aza izy.",
			"3": "<sup>3</sup> Indro, lova avy amin’i Jehovah ny zanaka maro; Tamby ny ateraky ny kibo.",
			"4": "<sup>4</sup> Tahaka ny zana-tsipìka eo an-tànan’ny mahery Ny zanaky ny fahatanorana.",
			"5": "<sup>5</sup> Sambatra izay mameno ny tranon-jana-tispìkany amin’ireny; Tsy ho menatra ireny, raha mifamaly amin’ny fahavalo eo am-bavahady."
		},
		"128": {
			"1": "<sup>1</sup> Fihirana fiakarana. SAMBATRA izay rehetra matahotra an’i Jehovah, Dia izay mandeha amin’ny làlany.",
			"2": "<sup>2</sup> Fa hohaninao tokoa ny asan’ny tànanao; Sambatra sady ambinina hianao.",
			"3": "<sup>3</sup> Ny vadinao dia tahaka ny voaloboka mahavokatra Ao anatin’ny tranonao; Ny zanakao dia tahaka ny zanak’oliva Manodidina ny latabatrao.",
			"4": "<sup>4</sup> Indro, fa toy izany no hitahiana Izay lehilahy matahotra an’i Jehovah.",
			"5": "<sup>5</sup> Hitahy anao avy any Ziona anie Jehovah; Ary ho faly hahita ny fahasoavan’i Jerosalema anie hianao Amin’ny andro rehetra hiainanao.",
			"6": "<sup>6</sup> Eny, hahita ny zafinao anie hianao; Fiadanana anie ho amin’ny Isiraely."
		},
		"129": {
			"1": "<sup>1</sup> Fihirana fiakarana. MAFY no nampahoriany ahy hatry ny fony aho mbola tanora,– Eny, aoka hataon’ny Isiraely hoe:",
			"2": "<sup>2</sup> Mafy no nampahoriany ahy hatry ny fony aho mbola tanora; Nefa tsy naharesy ahy izy.",
			"3": "<sup>3</sup> Ny mpiasa dia niasa ny lamosiko; Efa nanalava ny vavasany izy.",
			"4": "<sup>4</sup> Marina Jehovah; Efa nanapaka ny famatorana nataon’ny ratsy fanahy Izy.",
			"5": "<sup>5</sup> Aoka ho menatra sy hiamboho Izay rehetra mandrafy an’i Ziona",
			"6": "<sup>6</sup> Ka ho tahaka ny ahitra eo an-tampon-trano, Izay malazo, na dia tsy misy manongotra azy aza;",
			"7": "<sup>7</sup> Tsy misy mpijinja mameno tànana Na mpamehy amboara mameno tratra amin’izany;",
			"8": "<sup>8</sup> Ka dia tsy hisy mpandalo hanao hoe: Hotahin’i Jehovah anie hianareo; Eny, ny anaran’i Jehovah no itsofanay rano anareo."
		},
		"130": {
			"1": "<sup>1</sup> Fihirana fiakarana. TAO amin’ny lalina no niantsoako Anao, Jehovah ô,",
			"2": "<sup>2</sup> Tompo ô, henoy ny feoko; Aoka ny sofinao hihaino tsara ny feon’ny fifonako.",
			"3": "<sup>3</sup> Raha mandinika heloka Hianao, Jehovah ô, Iza no hahajanona, Tompo ô?",
			"4": "<sup>4</sup> Fa eo aminao no misy ny famelan-keloka, Mba hatahorana Anao.",
			"5": "<sup>5</sup> Niandry an’i Jehovah aho; Eny, niandry Azy ny fanahiko, Ary ny teniny no nanantenako.",
			"6": "<sup>6</sup> Ny fanahiko miandry ny Tompo Mihoatra noho ny fiandrin’ny mpiambina ny maraina, Dia ny fiandrin’ny mpiambina ny maraina.",
			"7": "<sup>7</sup> Manantenà an’i Jehovah, ry Isiraely; Fa eo amin’i Jehovah no misy famindrampo sy fanavotana be;",
			"8": "<sup>8</sup> Ary Izy no hanavotra ny Isiraely Ho afaka amin’ny helony rehetra."
		},
		"131": {
			"1": "<sup>1</sup> Fihirana fiakarana. Nataon’i Davida. JEHOVAH ô, tsy nirehareha ny foko, ary tsy niandranandrana ny masoko; Ary tsy nisaintsaina izay zavatra ambony loatra aho, Na izay saro-pantarina tsy takatro.",
			"2": "<sup>2</sup> Fa nampionona sy nampangina ny fanahiko aho; Tahaka ny zaza efa maoty eo amin-dreniny, Dia tahaka izany ny fanahiko ato anatiko.",
			"3": "<sup>3</sup> Ry Isiraely ô, manantenà an’i Jehovah Hatramin’izao ka ho mandrakizay."
		},
		"132": {
			"1": "<sup>1</sup> Fihirana fiakarana. JEHOVAH ô, tsarovy Davida Noho ny fiahiany rehetra,",
			"2": "<sup>2</sup> Dia ilay nianiana tamin’i Jehovah Sy nivoady tamin’ny Maherin’i Jakoba hoe:",
			"3": "<sup>3</sup> Tsy mba hiditra ao an-daiko aho Na hiakatra amin’ny farafara fandriako,–",
			"4": "<sup>4</sup> Tsy mba havelako hatory ny masoko, Na ho rendrehana ny hodi-masoko,",
			"5": "<sup>5</sup> Mandra-pahitako fitoerana ho an’i Jehovah. Dia fonenana ho an’ny Maherin’i Jakoba.",
			"6": "<sup>6</sup> Indro, nandre azy tany Efrata isika; Nahita azy tao amin’ny saha anaty ala isika.",
			"7": "<sup>7</sup> Aoka hiditra any amin’ny fonenany isika Ka hivavaka eo amin’ny fitoeran-tongony.",
			"8": "<sup>8</sup> Mitsangàna, Jehovah ô, ho amin’ny fitsaharanao, Dia Hianao sy ny fiaran’ny herinao.",
			"9": "<sup>9</sup> Aoka ny mpisoronao hitafy fahamarinana; Ary aoka ny olonao masina hihoby.",
			"10": "<sup>10</sup> Noho ny amin’i Davida mpanomponao Dia aza mampiala maina ny tavan’ny voahosotrao.",
			"11": "<sup>11</sup> Jehovah efa nianiana tamin’i Davida, Ary marina izany ka tsy hitsoahany: Ny ateraky ny kibonao no hapetrako eo ambonin’ny seza fiandriananao.",
			"12": "<sup>12</sup> Raha hotandreman’ny zanakao ny fanekeko Sy ny teny vavolombeloko izay hampianariko azy, Dia hieptraka eo ambonin’ny seza fiandriananao mandrakizay doria kosa ny zanany.",
			"13": "<sup>13</sup> Fa Jehovah efa nifidy an’i Ziona. Ary naniry azy ho fonenany.",
			"14": "<sup>14</sup> Ity, hoy Izy, no fitsaharako mandrakizay doria; Eto no honenako, fa efa niriko ity.",
			"15": "<sup>15</sup> Hotahiko tokoa ny fihinana ao; Hovokisako hanina ny malahelo ao.",
			"16": "<sup>16</sup> Ny mpisorona ao dia hampitafiko famonjena; Ary hihoby tokoa ny olo-masina ao.",
			"17": "<sup>17</sup> Ao no hampitsimohako tandroka ho an’i Davida Sy hanamboarako jiro ho an’ny voahosotro.",
			"18": "<sup>18</sup> Ny fahavalony hampitafiko henatra; Fa eo aminy kosa no hamirapiratra ny satroboninahiny."
		},
		"133": {
			"1": "<sup>1</sup> Fihirana fiakarana. Nataon’i Davida. ENDREY, tsara sady mahafinaritra Raha ny mpirahalahy no miara-monina.",
			"2": "<sup>2</sup> Tahaka ny diloilo tsara amin’ny loha, Izay mitsororoka amin’ny somotra, dia ny somotr’i Arona, Izay mitsororoka amin’ny moron-tongotr’akanjony;",
			"3": "<sup>3</sup> Tahaka ny ando any Hermona, izay milatsaka amin’ny tendrombohitra Ziona; Fa voadidin’i Jehovah ho eo ny fitahiana, Dia fiainana ho mandrakizay."
		},
		"134": {
			"1": "<sup>1</sup> Fihirana fiakarana. ANDEHA, misaora an’i Jehovah, hianareo rehetra mpanompon’i Jehovah, Izay mitoetra alina ao an-tranon’i Jehovah.",
			"2": "<sup>2</sup> Manandrata ny tànanareo ho amin’ny fitoerana masina, Ka misaora an’i Jehovah.",
			"3": "<sup>3</sup> Hitahy anao avy ao Ziona anie Jehovah, Mpanao ny lanitra sy ny tany."
		},
		"135": {
			"1": "<sup>1</sup> HALELOIA. Miderà ny anaran’i Jehovah; Miderà, hianareo mpanompon’i Jehovah,–",
			"2": "<sup>2</sup> Hianareo izay mpitoetra ao an-tranon’i Jehovah, Eo anatin’ny kianjan’ny tranon’Andriamanitsika.",
			"3": "<sup>3</sup> Miderà an’i Jehovah, fa tsara Izy; Mankalazà ny anarany, fa mahafinaritra izany.",
			"4": "<sup>4</sup> Fa Jakoba nofidin’i Jehovah ho Azy, Ary Isiraely ho rakitra soa ho Azy.",
			"5": "<sup>5</sup> Fa fantatro fa lehibe Jehovah, Ary ambonin’ny andriamanitra rehetra ny Tompontsika.",
			"6": "<sup>6</sup> Manao izay sitrapony rehetra Jehovah, Na any amin’ny lanitra, na etỳ ambonin’ny tany, Na any amin’ny ranomasina sy ny rano lalina rehetra.",
			"7": "<sup>7</sup> Mampiakatra zavona avy amin’ny faran’ny tany Izy; Manao helatra arahin-dranonorana Izy; Mamoaka ny rivotra avy ao an-trano fitehirizany Izy,",
			"8": "<sup>8</sup> Dia Ilay namely ny voalohan-terak’i Egypta, Na olona, na biby.",
			"9": "<sup>9</sup> Nanatitra famantarana sy fahagagana teo aminao Izy, ry Egypta ô, Eny, tamin’i Farao sy ny mpanompony rehetra,",
			"10": "<sup>10</sup> Dia Ilay namely ny jentilisa maro Ka nahafaty mpanjaka mahery,",
			"11": "<sup>11</sup> Dia Sihona, mpanjakan’ny Amorita, Sy Oga, mpanjakan’i Basana, Ary ny fanjakan’i Kanana rehetra;",
			"12": "<sup>12</sup> Ary ny taniny nomeny ho lova, Eny, ho lovan’ny Isiraely olony.",
			"13": "<sup>13</sup> Jehovah ô, mandrakizay ny anaranao; Jehovah ô, hihatra amin’ny taranaka fara mandimby ny fahatsiarovana Anao.",
			"14": "<sup>14</sup> Fa hanome rariny ny olony Izy Ary hanenina ny amin’ny mpanompony.",
			"15": "<sup>15</sup> Ny sampin’ny jentilisa dia volafotsy sy volamena, Asan’ny tànan’olona;",
			"16": "<sup>16</sup> Manam-bava izy, fa tsy miteny; Mana-maso izy, fa tsy mahita;",
			"17": "<sup>17</sup> Manan-tsofina izy, fa tsy mandre; Ary tsy misy fofonaina ao am-bavany.",
			"18": "<sup>18</sup> Ho tahaka azy izay manao azy Sy izay rehetra matoky azy.",
			"19": "<sup>19</sup> Misaora an’i Jehovah, ry taranak’Isiraely; Misaora an’i Jehovah, ry taranak’i Arona;",
			"20": "<sup>20</sup> Misaora an’i Jehovah, ry taranak’i Levy; Hianareo izay matahotra an’i Jehovah, Misaora an’i Jehovah.",
			"21": "<sup>21</sup> Isaorana avy any Ziona anie Jehovah, Izay mitoetra any Jerosalema. Haleloia."
		},
		"136": {
			"1": "<sup>1</sup> MIDERÀ an’i Jehovah, fa tsara Izy; Fa mandrakizay ny famindrampony.",
			"2": "<sup>2</sup> Miderà an’Andriamanitra Avo Indrindra; Fa mandrakizay ny famindrampony.",
			"3": "<sup>3</sup> Miderà ny Tompon’ny tompo; Fa mandrakizay ny famindrampony;",
			"4": "<sup>4</sup> Izy irery no Mpanao fahagagan-dehibe (Fa mandrakizay ny famindrampony),",
			"5": "<sup>5</sup> Izay nanao ny lanitra tamin’ny fahendrena (Fa mandrakizay ny famindrampony),",
			"6": "<sup>6</sup> Izay namelatra ny tany ho ambonin’ny rano, (Fa mandrakizay ny famindrampony),",
			"7": "<sup>7</sup> Izay nanao ireo fanazavana lehibe (Fa mandrakizay ny famindrampony),",
			"8": "<sup>8</sup> Dia ny masoandro ho mpanapaka ny andro (Fa mandrakizay ny famindrampony),",
			"9": "<sup>9</sup> Ary ny volana sy ny kintana ho mpanapaka ny alina (Fa mandrakizay ny famindrampony),–",
			"10": "<sup>10</sup> Izay namely ny voalohan-terak’i Egypta, (Fa mandrakizay ny famindrampony)",
			"11": "<sup>11</sup> Ka nitondra ny Isiraely nivoaka avy teo aminy (Fa mandrakizay ny famindrampony),",
			"12": "<sup>12</sup> Tamin’ny tànana mahery sy ny sandry nahinjitra (Fa mandrakizay ny famindrampony),–",
			"13": "<sup>13</sup> Izay nampisaraka ny Ranomasina Mena (Fa mandrakizay ny famindrampony),",
			"14": "<sup>14</sup> Ka nampandeha ny Isiraely namaky teo afovoany (Fa mandrakizay ny famindrampony),",
			"15": "<sup>15</sup> Fa nanary an’i Farao sy ny miaramilany tao anatin’ny Ranomasina Mena (Fa mandrakizay ny famindrampony),–",
			"16": "<sup>16</sup> Izay nitondra ny olony nitety ny efitra (Fa mandrakizay ny famindrampony),",
			"17": "<sup>17</sup> Izay namely mpanjaka lehibe (Fa mandrakizay ny famindrampony),",
			"18": "<sup>18</sup> Ka nahafaty mpanjaka malaza (Fa mandrakizay ny famindrampony),",
			"19": "<sup>19</sup> Dia Sihona, mpanjakan’ny Amorita (Fa mandrakizay ny famindrampony),",
			"20": "<sup>20</sup> Sy Oga, mpanjakan’i Basana (Fa mandrakizay ny famindrampony),",
			"21": "<sup>21</sup> Ary ny taniny nomeny ho lova (Fa mandrakizay ny famindrampony),",
			"22": "<sup>22</sup> Dia ho lovan’ny Isiraely mpanompony (Fa mandrakizay ny famindrampony),–",
			"23": "<sup>23</sup> Izay nahatsiaro antsika fony isika ambany toetra (Fa mandrakizay ny famindrampony),",
			"24": "<sup>24</sup> Ka naneho hery namonjy antsika ho afaka tamin’ny fahavalontsika (Fa mandrakizay ny famindrampony),–",
			"25": "<sup>25</sup> Izay manome hanina ny nofo rehetra (Fa mandrakizay ny famindrampony);",
			"26": "<sup>26</sup> Miderà an’Andriamanitry ny lanitra; Fa mandrakizay ny famindrampony."
		},
		"137": {
			"1": "<sup>1</sup> TEO amoron’ny onin’i Babylona no nipetrahanay, Sady nitomanianay, raha nahatsiaro an’i Ziona.",
			"2": "<sup>2</sup> Teo amin’ny hazomalahelo teo afovoan’i Babylona No nanantonanay ny lokanganay.",
			"3": "<sup>3</sup> Fa teo dia nangataka anay hihira izay namabo anay; Ary izay nampahory anay nangataka anay hanao fifaliana ka nanao hoe: Manaova hiran’i Ziona eto anatrehanay.",
			"4": "<sup>4</sup> Nefa ho nataontsika ahoana no fihira ny fihiran’i Jehovah Tany an-tany nivahiniana?",
			"5": "<sup>5</sup> Raha manadino anao aho, ry Jerosalema ô, Aoka kosa ny tànako ankavanana mba hanadino.",
			"6": "<sup>6</sup> Raha tsy mahatsiaro anao aho, Aoka ny lelako hiraikitra amin’ny lanilaniko, Raha tsy Jerosalema no ataoko ambony indrindra amin’ny fifaliako rehetra.",
			"7": "<sup>7</sup> Jehovah ô, tsarovy amin’ny taranak’i Edoma Ny andron’i Jerosalema, Dia ireo izay nanao hoe: Ravao, ravao izy Hatramin’ny fanorenany.",
			"8": "<sup>8</sup> Ry Babylona zanakavavy izay efa resy, Sambatra izay mamaly anao Araka izay nataonao taminay.",
			"9": "<sup>9</sup> Sambatra izay maka ny zanakao madinika ka manjera azy ho torotoro, Amin’ny harambato."
		},
		"138": {
			"1": "<sup>1</sup> Nataon’i Davida. HIDERA Anao amin’ny foko rehetra aho; Eo anatrehan’ireo andriamanitra no hankalazako Anao.",
			"2": "<sup>2</sup> Hivavaka manandrify ny tempolinao masina aho Ka hidera ny anaranao noho ny famindramponao sy ny fahamarinanao; Fa efa nomenao voninahitra ny teninao ho ambonin’ny anaranao rehetra.",
			"3": "<sup>3</sup> Tamin’ny andro nitarainako dia namaly ahy Hianao; Nomenao fahasahiana aho, ka nisy hery ny fanahiko.",
			"4": "<sup>4</sup> Jehovah ô, hidera Anao ny mpanjaka rehetra amin’ny tany, Fa handre ny tenin’ny vavanao izy.",
			"5": "<sup>5</sup> Eny, hihira ny amin’ny làlan’i Jehovah izy; Fa lehibe ny voninahitr’i Jehovah.",
			"6": "<sup>6</sup> Fa na dia avo aza Jehovah, dia mijery ny ambany toetra, Ary ny mpiavonavona fantany eny lavitra eny.",
			"7": "<sup>7</sup> Na dia handeha eo amin’ny fahoriana aza aho, dia hamelona ahy Hianao; Haninjitra ny tànanao hanohitra ny fahatezeran’ny fahavaloko Hianao Ary hamonjy ahy ny tànanao ankavanana.",
			"8": "<sup>8</sup> Jehovah no hahatanteraka ny amiko; Jehovah ô, mandrakizay ny famindramponao; Aza mahafoy ny asan’ny tànanao."
		},
		"139": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Salamo nataon’i Davida. JEHOVAH ô, efa nandinika ahy Hianao ka mahalala ahy.",
			"2": "<sup>2</sup> Hianao no mahalala ny fipetrako sy ny fitsangako Sady mahafantatra ny hevitro eny lavitra eny.",
			"3": "<sup>3</sup> Voadinikao ny toetro, na mandeha aho, na mandry; Ary fantatrao tsara ny alehako rehetra.",
			"4": "<sup>4</sup> Fa, indro, tsy misy teny tsy fantatrao eo amin’ny lelako, Jehovah ô.",
			"5": "<sup>5</sup> Manodidina ahy eo ivohoko sy eo anatrehako Hianao, Ary apetrakao amiko ny tànanao.",
			"6": "<sup>6</sup> Fahalalana mahagaga ahy loatra izany, Eny, avo loatra ka tsy tratro.",
			"7": "<sup>7</sup> Aiza no hialako amin’ny Fanahinao? Aiza no handosirako tsy ho eo anatrehanao?",
			"8": "<sup>8</sup> Na dia miakatra any an-danitra aza aho, dia any Hianao; Ary na dia manao ny fiainan-tsi-hita ho fandriako aza aho, indro, any Hianao.",
			"9": "<sup>9</sup> Na dia maka ny elatry ny maraina aza aho, Mba hitoetra any am-paran’ny ranomasina,",
			"10": "<sup>10</sup> Dia mbola entin’ny tànanao ihany aho, na dia any aza, Sy hazonin’ny tànanao ankavanana.",
			"11": "<sup>11</sup> Ary na dia hoy izaho aza: Aoka ny maizina hanarona ahy, Ary aoka ny mazava manodidina ahy ho tonga alina,–",
			"12": "<sup>12</sup> Tsia, fa ny maizina aza tsy mahamaizina aminao; Fa ny alina dia mahazava toy ny andro ihany, Ka sahala ny mazava sy ny maizina.",
			"13": "<sup>13</sup> Fa Hianao no nahary ny voako; Namolavola ahy tao an-kibon-dreniko Hianao.",
			"14": "<sup>14</sup> Hidera Anao aho; fa mahatahotra sy mahatalanjona ny nanaovana ahy; Mahagaga ny asanao; Ary fantatry ny fanahiko marimarina izany.",
			"15": "<sup>15</sup> Ny tenako tsy niafina taminao, Fony natao tao amin’ny miafina aho, Ka noforoninao hisy endrika tany ambanin’ny tany.",
			"16": "<sup>16</sup> Ny masonao efa nahita ahy na dia fony tsy mbola ary fady aza aho; Voasoratra teo amin’ny bokinao avokoa ny andro voatendry, Fony tsy mbola nisy na dia iray akory aza.",
			"17": "<sup>17</sup> Endrey, zava-tsoa amiko indrindra ny hevitrao, Andriamanitra ô! Akory ny hamaron’ny isany!",
			"18": "<sup>18</sup> Raha hanisa azy aho, dia maro isa lavitra noho ny fasika izy; Raha mifoha aho, dia mbola aminao ihany.",
			"19": "<sup>19</sup> Haringanao mihitsy ny ratsy fanahy, Andriamanitra ô, Ary mialà amiko, hianareo mpandatsa-dra.",
			"20": "<sup>20</sup> Dia izay manonona Anao ho enti-mamitaka, Jehovah ô, Eny, ny fahavalonao izay manonona Anao foana.",
			"21": "<sup>21</sup> Tsy hankahala izay mankahala Anao va aho, Jehovah ô, Sy ho tezitra amin’izay mitsangana hanohitra Anao?",
			"22": "<sup>22</sup> Halako dia halako marina tokoa ireny; Efa fahavaloko ireny.",
			"23": "<sup>23</sup> Diniho aho, Andriamanitra ô, ary fantaro ny foko; Izahao toetra aho, ary fantaro ny eritreritro;",
			"24": "<sup>24</sup> Ary izahao na misy làlana mampahory ato anatiko, Dia tariho amin’ny làlana mandrakizay aho."
		},
		"140": {
			"1": "<sup>1</sup> Ho an’ny mpiventy hira. Salamo nataon’i Davida. JEHOVAH ô, vonjeo amin’ny olon-dratsy aho, Ary arovy amin’ny olon-dozabe,",
			"2": "<sup>2</sup> Izay mihevitra ratsy ao am-pony Ka manangana ady isan-andro.",
			"3": "<sup>3</sup> Mandranitra ny lelany tahaka ny bibilava izy, Ny poizin’ny menarana no ao ambanin’ny molony. [Sela.]",
			"4": "<sup>4</sup> Jehovah ô, vonjeo amin’ny tànan’ny ratsy fanahy aho, Ary arovy amin’ny olon-dozabe, Izay efa nihevitra handronjina ahy ho potraka.",
			"5": "<sup>5</sup> Ny mpiavonavona efa nanafina fandrika sy kofehy hamandrihany ahy; Efa namela-pandrika harato teo amoron-dàlana izy; Nametraka tadivavarana hahazoany ahy izy. [Sela.]",
			"6": "<sup>6</sup> Hoy izaho tamin’i Jehovah: Andriamanitro Hianao; Henoy ny feon’ny fifonako, Jehovah ô!",
			"7": "<sup>7</sup> Jehovah, Tompo ô, ry Herin’ny famonjena ahy, Manarona ny lohako amin’ny andro fiadiana Hianao.",
			"8": "<sup>8</sup> Jehovah ô, aza manome ny irin’ny ratsy fanahy; Ary aza avela ho tanteraka ny hevi-dratsiny, fandrao hisandratra izy. [Sela.]",
			"9": "<sup>9</sup> Ny lohan’izay manemitra ahy Dia aoka hosaronan’ny ratsy tononin’ny molony.",
			"10": "<sup>10</sup> Aoka hisy vain’afo halatsaka aminy; Aoka hariana any anaty afo izy, na any amin’ny rano lalina, ka tsy hiarina intsony.",
			"11": "<sup>11</sup> Tsy hampitoerina amin’ny tany ny olo-mahay vava; Henjehin-doza ny olon-dozabe ka ho lavo.",
			"12": "<sup>12</sup> Fantatro fa Jehovah no handahatra ny tenin’ny mahantra Sady hanome rariny ho an’ny malahelo.",
			"13": "<sup>13</sup> Eny tokoa, ny marina hisaotra ny anaranao; Hitoetra eo anatrehanao ny mahitsy."
		},
		"141": {
			"1": "<sup>1</sup> Salamo nataon’i Davida. JEHOVAH ô, mitaraina aminao aho, faingana hankatỳ amiko; Henoy ny feoko amin’ny fiantsoako Anao.",
			"2": "<sup>2</sup> Aoka hankeo anatrehanao mandrakariva toy ny ditin-kazo manitra ny fivavako, Ary ho toy ny fanatitra hariva ny fanandratako ny tànako.",
			"3": "<sup>3</sup> Jehovah ô, asio fiambenana eo anoloan’ny vavako; Hambeno ny varavaran’ny molotro.",
			"4": "<sup>4</sup> Aza avela hirona ho amin’ny zava-dratsy ny foko, Hiray raharaha amin’ny olona mpanao ratsy hanao izay asa tsy mety; Ary aoka tsy hihinana ny zava-piny aho.",
			"5": "<sup>5</sup> Aoka ny marina hamely ahy amin’ny fahalemen-panahy ka hananatra ahy, Fa diloilo ahosotra ny lohako izany, ary aoka tsy handa izany ny lohako; Fa ny hany heriko dia ny mbola hivavaka ihany noho ny faharatsian’ireny.",
			"6": "<sup>6</sup> Havarina amin’ny hantsam-bato ny mpitsara azy, Dia hankasitraka ny teniko ny olona.",
			"7": "<sup>7</sup> Toy ny fiasa sy ny fihevo ny tany No nanelezana ny taolanay teo am-bavan’ny fiainan-tsi-hita.",
			"8": "<sup>8</sup> Fa Hianao no andrandrain’ny masoko, Jehovah Tompo ô; Hianao no ialofako, aza mandatsaka ny aiko.",
			"9": "<sup>9</sup> Arovy aho amin’ny fandrika amandrihany ahy Sy amin’ny tadivavaran’ny mpanao ratsy.",
			"10": "<sup>10</sup> Aoka ny ratsy fanahy ho latsaka eo anatin’ny fandriny ihany, Fa izaho kosa handalo fotsiny."
		},
		"142": {
			"1": "<sup>1</sup> Maskila nataon’i Davida tamin’izy tao anaty lavaka. Fivavahana. NY feoko no itarainako amin’i Jehovah; Ny feoko no ifonako amin’i Jehovah.",
			"2": "<sup>2</sup> Loarako eo anatrehany ny fitarainako; Ambarako eo anatrehany ny fahoriako.",
			"3": "<sup>3</sup> Raha reraka ato anatiko ny fanahiko, dia fantatro ny alehako; Eo amin’ny làlana izorako No nanafenany fandrika hamandrihany ahy.",
			"4": "<sup>4</sup> Jereo eo an-kavanana, ka izahao, Fa tsy misy olona mahalala ahy; Foana avokoa ny fiarovana ahy; Tsy misy miahy ny fanahiko.",
			"5": "<sup>5</sup> Mitaraina aminao aho, Jehovah ô; Hoy izaho: Hianao no aroko Sy anjarako eo amin’ny tanin’ny velona.",
			"6": "<sup>6</sup> Henoy ny fitarainako, Fa reraka indrindra aho; Vonjeo aho amin’ny mpanenjika ahy, Fa tsy leoko ireny.",
			"7": "<sup>7</sup> Avoahy hiala amin’ny trano-maizina ny fanahiko, Mba hiderako ny anaranao; Hanodidina ahy ny marina, Satria manisy soa ahy Hianao."
		},
		"143": {
			"1": "<sup>1</sup> Salamo nataon’i Davida. JEHOVAH ô, henoy ny fivavako, ary atongilano amin’ny fifonako ny sofinao; Valio aho araka ny teninao marina sy ny fahamarinanao.",
			"2": "<sup>2</sup> Ary aza dia mifandahatra amin’ny mpanomponao eo am-pitsarana; Fa tsy misy olombelona ho marina eo anatrehanao.",
			"3": "<sup>3</sup> Fa nanenjika ny fanahiko ny fahavalo; Namely ny aiko ho lavo tamin’ny tany izy; Nampitoeriny tao amin’ny maizina aho, toy izay efa maty ela.",
			"4": "<sup>4</sup> Dia reraka ny fanahiko ato anatiko; Fadiranovana ny foko ato anatiko.",
			"5": "<sup>5</sup> Mahatsiaro ny andro fahiny aho Ary misaina ny asanao rehetra; Ny asan’ny tànanao no eritreretiko.",
			"6": "<sup>6</sup> Mananty tànana aminao aho; Ny fanahiko dia tahaka ny tany mangentana eo anatrehanao. [Sela.]",
			"7": "<sup>7</sup> Faingàna hamaly ahy, Jehovah ô, fa reraka ny fanahiko; Aza manafina ny tavanao amiko, Fandrao ho tahaka izay midina any am-pasana aho.",
			"8": "<sup>8</sup> Ampandreneso ny famindramponao aho nony maraina, Fa Hianao no itokiako; Ampahalalao ahy izay làlana tokony halehako, Fa aminao no ananganako ny fanahiko.",
			"9": "<sup>9</sup> Jehovah ô, vonjeo amin’ny fahavaloko aho; Ao aminao no iereko.",
			"10": "<sup>10</sup> Ampianaro hanao ny sitraponao aho; Fa Hianao no Andriamanitro; Aoka ny Fanahinao tsara no hitarihanao ahy amin’izay tany marina.",
			"11": "<sup>11</sup> Noho ny anaranao, Jehovah ô, velomy aho; Araka ny fahamarinanao avoahy ny fanahiko ho afaka amin’ny fahoriana.",
			"12": "<sup>12</sup> Ary araka ny famindramponao fongory ny fahavaloko, Ka aringano izay rehetra mampahory ny fanahiko; Fa mpanomponao aho."
		},
		"144": {
			"1": "<sup>1</sup> Nataon’i Davida. ISAORANA anie Jehovah Vatolampiko, Izay mampianatra ny tanako hamely Sy ny rantsan-tànako hiady,",
			"2": "<sup>2</sup> Izay Mpiantra sy batery fiarovana ho ahy sady fiarovana avo sy Mpanafaka ahy, Ary ampingako sy fialofako, Izay mahafolaka ny vahoakako hanoa ahy",
			"3": "<sup>3</sup> Jehovah ô, inona ny olona, no iahianao azy? Ary ny zanak’olombelona, no iheveranao azy?",
			"4": "<sup>4</sup> Ny olona dia tahaka ny zava-poana, Ny androny dia tahaka ny aloka mihelina.",
			"5": "<sup>5</sup> Jehovah ô, aondreho ny lanitrao, ka midìna; Tendreo ny tendrombohitra, dia hidonaka ireny.",
			"6": "<sup>6</sup> Ampitselatselaro ny helatra, ka ampielezo ireo; Alefao ny zana-tsipìkanao, ka ampifanaritaho ireo;",
			"7": "<sup>7</sup> Ahinjiro avy any ambony ny tànanao; Afaho aho, ka vonjeo amin’ny rano be, Dia amin’ny tànan’ny hafa firenena,",
			"8": "<sup>8</sup> Izay miteny zava-poana amin’ny vavany, Ary ny tànany ankavanana dia tànana ankavanana mamitaka.",
			"9": "<sup>9</sup> Andriamanitra ô, hihira fihiram-baovao ho Anao aho; Amin’ny valiha tory folo no hankalazako Anao,",
			"10": "<sup>10</sup> Izay manome famonjena ho an’ireo mpanjaka, Sady mamonjy an’i Davida mpanompony tsy ho voan’ny sabatra mandratra.",
			"11": "<sup>11</sup> Afaho aho, ka vonjeo amin’ny tànan’ny hafa firenena, Izay miteny zava-poana amin’ny vavany, Ary ny tànany ankavanana dia tànana ankavanana mamitaka;",
			"12": "<sup>12</sup> Mba ho tahaka ny zava-maniry maroroka ny zanakai-lahy, Ary ho tahaka ny vato fehizoro voasoratra tsara toy ny fomban’ny lapan’andriana ny zanakai-vavy.",
			"13": "<sup>13</sup> Ho feno ny trano fitehirizanay, ka hisy vokatra betsaka samy hafa karazana; Hiteraka arivoarivo sy alinalina ny ondrinay any an-tsahanay;",
			"14": "<sup>14</sup> Hahazaka be ny ombinay, Ary tsy hisy ho rava, na ho lasan-ko babo, Sady tsy hisy fitarainana any an-dàlambenay.",
			"15": "<sup>15</sup> Sambatra ny firenena izay tahaka izany; Eny, sambatra ny firenena izay manana an’i Jehovah ho Andriamaniny."
		},
		"145": {
			"1": "<sup>1</sup> Fideran’i Davida. HANANDRATRA Anao aho, ry Andriamanitro Mpanjaka ô, Sy hankalaza ny anaranao mandrakizay doria.",
			"2": "<sup>2</sup> Hankalaza Anao isan-andro aho Sy hidera ny anaranao mandrakizay doria.",
			"3": "<sup>3</sup> Lehibe Jehovah ka tokony hoderaina indrindra; Ary tsy takatry ny saina ny fahalehibiazany.",
			"4": "<sup>4</sup> Ny taranaka rehetra samy hidera ny asanao amin’izay mandimby azy Sy hanambara ny asanao lehibe.",
			"5": "<sup>5</sup> Ny lazan’ny voninahitry ny fiandriananao Sy ny asanao mahagaga no hosaintsainiko.",
			"6": "<sup>6</sup> Ny herin’ny asanao mahatahotra no hotenenin’ny olona; Ary ny fahalehibiazanao no holazaiko.",
			"7": "<sup>7</sup> Hanonona ny fahatsiarovana ny haben’ny fahatsaranao izy Sy hihoby ny fahamarinanao.",
			"8": "<sup>8</sup> Mamindra fo sy miantra Jehovah; Mahari-po sady be famindrampo Izy.",
			"9": "<sup>9</sup> Manisy soa izao tontolo izao Jehovah; Ary ny fiantràny dia eny amin’ny asany rehetra.",
			"10": "<sup>10</sup> Jehovah ô, midera Anao ny asanao rehetra; Ary ny olonao masina misaotra Anao.",
			"11": "<sup>11</sup> Milaza ny voninahitry ny fanjakanao izy Sady miresaka ny herinao,",
			"12": "<sup>12</sup> Mba hampahafantarina ny zanak’olombelona ny herinao Sy ny haben’ny voninahitry ny fanjakanao.",
			"13": "<sup>13</sup> Fanjakana mandrakizay ny fanjakanao, Ary hihatra amin’ny taranaka fara mandimby ny fanapahanao.",
			"14": "<sup>14</sup> Jehovah manohana izay rehetra efa ho lavo Sy mampitraka izay rehetra mitanondrika.",
			"15": "<sup>15</sup> Ny mason’izao rehetra izao miandrandra Anao, Ary Hianao manome azy ny haniny amin’ny fotoany.",
			"16": "<sup>16</sup> Manokatra ny tànanao Hianao Ka mahavoky soa ny zava-miaina rehetra.",
			"17": "<sup>17</sup> Marina Jehovah amin’ny làlany rehetra, Sady mamindra fo amin’ny asany rehetra.",
			"18": "<sup>18</sup> Jehovah dia eo akaikin’izay rehetra miantso Azy, Dia izay rehetra miantso Azy marina tokoa.",
			"19": "<sup>19</sup> Hanefa ny irin’izay matahotra Azy Izy Ary hihaino ny fitarainany ka hamonjy azy.",
			"20": "<sup>20</sup> Miaro izay rehetra tia Azy Jehovah; Fa ny ratsy fanahy rehetra kosa haringany.",
			"21": "<sup>21</sup> Ny fiderana an’i Jehovah no hotenenin’ny vavako; Ary aoka ny nofo rehetra hankalaza ny anarany masina mandrakizay doria."
		},
		"146": {
			"1": "<sup>1</sup> HALELOIA. Miderà an’i Jehovah, ry fanahiko.",
			"2": "<sup>2</sup> Hidera an’i Jehovah aho, raha mbola miaina koa; Eny, hankalaza an’Andriamanitro aho, Raha mbola velona koa.",
			"3": "<sup>3</sup> Aza matoky ny lehibe, Na ny zanak’olombelona izay tsy mahavonjy.",
			"4": "<sup>4</sup> Miala ny fofonainy, ka miverina ho amin’ny taniny izy; Ary amin’izay indrindra no hahafoanan’ny fikasany.",
			"5": "<sup>5</sup> Sambatra izay manana an’Andriamanitr’i Jakoba ho Mpanampy azy Sady manantena an’i Jehovah Andriamaniny,",
			"6": "<sup>6</sup> Izay nanao ny lanitra sy ny tany, Ny ranomasina sy izay rehetra ao anatiny, Izay mitandrina fahamarinana mandrakizay,",
			"7": "<sup>7</sup> Izay manome rariny ho an’ny ampahorina, Izay manome hanina ho an’ny noana. Jehovah mamaha ny mpifatotra;",
			"8": "<sup>8</sup> Jehovah mampahiratra ny jamba; Jehovah mampitraka ny mitanondrika; Jehovah tia ny marina;",
			"9": "<sup>9</sup> Jehovah miaro ny vahiny Ary manandratra ny kamboty sy ny mpitondratena, Fa mamadika ny làlan’ny ratsy fanahy kosa.",
			"10": "<sup>10</sup> Manjaka mandrakizay Jehovah, Hatramin’ny taranaka fara mandimby Andriamanitrao, ry Ziona. Haleloia."
		},
		"147": {
			"1": "<sup>1</sup> HALELOIA; Fa tsara ny mihira ho an’Andriamanitsika; Eny, mamy sady mendrika ny fiderana.",
			"2": "<sup>2</sup> Manangana an’i Jerosalema Jehovah; Mamory ny Isiraely voaroaka Izy.",
			"3": "<sup>3</sup> Mahasitrana ny torotoro fo Izy Sy mamehy ny feriny.",
			"4": "<sup>4</sup> Milaza ny isan’ny kintana Izy; Samy tononiny amin’ny anarany avy ireny rehetra ireny.",
			"5": "<sup>5</sup> Lehibe ny Tompontsika sady be hery; Tsy hita lany ny fahalalany.",
			"6": "<sup>6</sup> Manandratra ny mpandefitra Jehovah, Fa mampietry ny ratsy fanahy hatramin’ny tany.",
			"7": "<sup>7</sup> Mihirà fiderana ho an’i Jehovah; Mankalazà an’Andriamanitsika amin’ny lokanga,",
			"8": "<sup>8</sup> Izay manaron-drahona ny lanitra, Izay mamboatra ranonorana ho amin’ny tany, Izay mampaniry ahitra ny tendrombohitra,",
			"9": "<sup>9</sup> Manome hanina ho an’ny biby Izy Sy ho an’ny zana-goaika izay mitaraina.",
			"10": "<sup>10</sup> Tsy ny herin’ny soavaly no mahafinaritra Azy, Ary tsy ny tongotry ny olona no sitrany.",
			"11": "<sup>11</sup> Jehovah mankasitraka izay matahotra Azy, Dia izay manantena ny famindrampony.",
			"12": "<sup>12</sup> Mankalazà an’i Jehovah, ry Jerosalema ô; Miderà an’Andriamanitrao, ry Ziona ô.",
			"13": "<sup>13</sup> Fa manamafy ny hidim-bavahadinao Izy, Sady mitahy ny zanakao eo afovoanao.",
			"14": "<sup>14</sup> Mahatonga ny fari-taninao ho fiadanana Izy; Vokisany vary tsara indrindra hianao.",
			"15": "<sup>15</sup> Mampandeha ny didiny ho amin’ny tany Izy; Miely faingana dia faingana ny teniny.",
			"16": "<sup>16</sup> Mandatsaka orampanala tahaka ny landihazo Izy; Mampiely fanala tahaka ny lavenona Izy.",
			"17": "<sup>17</sup> Mandatsaka ny havandrany tahaka ny sombintsombin-javatra Izy; Iza no maharitra ny hatsiakany?",
			"18": "<sup>18</sup> Mamoaka ny teniny izy ka mampiempo ireny; Mampifofofofo ny rivony Izy, dia mandriaka ny rano.",
			"19": "<sup>19</sup> Milaza ny teniny amin’i Jakoba Izy Ary ny didiny sy ny fitsipiny amin’Isiraely.",
			"20": "<sup>20</sup> Tsy nanao toy izany tamin’izay firenena hafa Izy; Ary ny fitsipiny tsy mba fantatr’ireny. Haleloia."
		},
		"148": {
			"1": "<sup>1</sup> HALELOIA. Miderà an’i Jehovah, ry any an-danitra; Miderà Azy any ambony.",
			"2": "<sup>2</sup> Miderà Azy, ry anjeliny rehetra; Miderà Azy, ry miaramilany rehetra.",
			"3": "<sup>3</sup> Miderà Azy, ry masoandro amam-bolana; Miderà Azy, ry kintana mazava rehetra.",
			"4": "<sup>4</sup> Miderà Azy, ry lanitry ny lanitra, Ary hianareo rano ambonin’ny lanitra.",
			"5": "<sup>5</sup> Aoka hidera ny anaran’i Jehovah ireo; Fa Izy no nandidy, dia ary ireo.",
			"6": "<sup>6</sup> Nampitoetra ireo ho mandrakizay doria Izy; Nomen-dalàna ireo ka tsy mba mihoatra.",
			"7": "<sup>7</sup> Miderà an’i Jehovah etỳ an-tany. Hianareo dragona sy hianareo rano lalina rehetra,",
			"8": "<sup>8</sup> Ny afo sy ny havandra, ny orampanala sy ny zavona, Ny rivotra mahery izay mankatò ny teniny,",
			"9": "<sup>9</sup> Ny tendrombohitra sy ny havoana rehetra, Ny hazo fihinam-boa sy ny sedera rehetra,",
			"10": "<sup>10</sup> Ny bibi-dia sy ny biby fiompy rehetra, Ny biby mandady na mikisaka ary ny voro-manidina,",
			"11": "<sup>11</sup> Ny mpanjaka amin’ny tany sy ny vahoaka rehetra, Ny mpanapaka sy ny mpitsara rehetra amin’ny tany,",
			"12": "<sup>12</sup> Na ny zatovolahy, na ny zatovovavy, Na ny antitra, na ny tanora,–",
			"13": "<sup>13</sup> Samia midera ny anaran’i Jehovah avokoa, Fa ny anarany ihany no misandratra; Ambonin’ny tany sy ny lanitra ny voninahiny.",
			"14": "<sup>14</sup> Ary manandratra tandroka ho an’ny olony Izy, Dia fiderana ho an’ny olony masina rehetra, Eny, ho an’ny Zanak’Isiraely, firenena akaiky Azy. Haleloia."
		},
		"149": {
			"1": "<sup>1</sup> HALELOIA. Mihirà fihiram-baovao ho an’i Jehovah, Ny fiderana Azy eo amin’ny fiangonan’ny olona masina.",
			"2": "<sup>2</sup> Aoka ny Isiraely hifaly amin’ny Mpanao azy; Aoka ho finaritra amin’ny Mpanjakany ny zanak’i Ziona.",
			"3": "<sup>3</sup> Aoka hidera ny anarany amin’ny dihy izy Sy hankalaza Azy amin’ny ampongatapaka sy ny lokanga.",
			"4": "<sup>4</sup> Fa sitrak’i Jehovah ny olony; Ravahany famonjena ny mpandefitra.",
			"5": "<sup>5</sup> Aoka ny olona masina hifaly amin’ny voninahitra; Aoka hihoby tsara eo am-pandriany izy.",
			"6": "<sup>6</sup> Aoka hisy fanandratana an’Andriamanitra eo am-bavany Sy sabatra roa lela eny an-tànany,",
			"7": "<sup>7</sup> Hamaliany ny jentilisa Sy hamaizany ny firenena maro,",
			"8": "<sup>8</sup> Hamatorany ireo mpanjakany amin’ny gadra, Sy ny olo-malazany amin’ny gadra vy,",
			"9": "<sup>9</sup> Mba hanaovany ny fitsarana voasoratra: Voninahitry ny olony masina rehetra izany. Haleloia."
		},
		"150": {
			"1": "<sup>1</sup> HALELOIA. Miderà an’Andriamanitra eo amin’ny fitoerany masina. Miderà Azy eo amin’ny habakabaky ny heriny.",
			"2": "<sup>2</sup> Miderà Azy noho ny asany lehibe; Miderà Azy araka ny haben’ny voninahiny.",
			"3": "<sup>3</sup> Miderà Azy amin’ny fitsofana ny anjomara; Miderà Azy amin’ny valiha sy ny lokanga.",
			"4": "<sup>4</sup> Miderà Azy amin’ny ampongatapaka sy ny dihy; Miderà Azy amin’ny zava-maneno tendrena sy ny sodina.",
			"5": "<sup>5</sup> Miderà Azy amin’ny kipantsona maneno; Miderà Azy amin’ny kipantsona tsara feo.",
			"6": "<sup>6</sup> Aoka izay rehetra manam-pofonaina samy hidera an’i Jehovah. Haleloia."
		}
	}
};
module.exports = book;