var book = {
	"name": "Ohabolana",
	"numChapters": 31,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> NY Ohabolan’i Solomona, zanak’i Davida, mpanjakan’ny Isiraely:",
			"2": "<sup>2</sup> Hahalalana fahendrena sy fananarana Ary hahafantarana ny tenin’ny fahalalana;",
			"3": "<sup>3</sup> Hahazoana fananarana mampahahendry, Dia fahamarinana sy rariny ary fahitsiana;",
			"4": "<sup>4</sup> Hanomezam-pahendrena ho an’ny kely saina Ary fahalalana sy fisainana mazava ho an’ny zatovo.",
			"5": "<sup>5</sup> Aoka ny hendry hihaino ka hitombo saina; Ary aoka ny manam-panahy hahazo fitarihana tsara;",
			"6": "<sup>6</sup> Hahafantarana ohabolana sy fanoharana Ary ny tenin’ny hendry sy ny teny saro-pantarina ataony.",
			"7": "<sup>7</sup> Ny fahatahorana an’i Jehovah no fiandoham-pahalalana; Fa ny adala manamavo ny fahendrena sy ny famaizana.",
			"8": "<sup>8</sup> ANAKA, mihainoa ny ana-drainao, Ary aza mahafoy ny lalàn-dreninao;",
			"9": "<sup>9</sup> Fa fehiloha tsara tarehy amin’ny lohanao izany Ary rado amin’ny vozonao.",
			"10": "<sup>10</sup> Anaka, raha taomin’ny mpanota hianao, Aza manaiky.",
			"11": "<sup>11</sup> Raha hoy izy: Andeha hiaraka isika, Aoka isika hanotrika handatsa-dra; Andeha hamitsahantsika tsy ahoan-tsy ahoana ny tsy manan-tsiny;",
			"12": "<sup>12</sup> Aoka isika hitelina azy velona tahaka ny fitelin’ny fiainan-tsi-hita Ary hanao azy telimoka toy ny latsaka any an-davaka;",
			"13": "<sup>13</sup> Dia ho azontsika ny fananana saro-bidy rehetra, Ka hofenointsika babo ny tranontsika;",
			"14": "<sup>14</sup> Avia mba hiara-miloka eto aminay; Aoka ho iray ihany ny kitapom-bolantsika rehetra.",
			"15": "<sup>15</sup> Anaka, aza miara-dàlana aminy hianao; Arovy ny tongotrao tsy hankamin’ny alehany;",
			"16": "<sup>16</sup> Fa ny tongony mihazakazaka ho amin’ny ratsy Sady faingana handatsa-dra.",
			"17": "<sup>17</sup> Fa velarim-poana ny fandrika, Raha hitan’ny vorona.",
			"18": "<sup>18</sup> Fa ireny dia manotrika handatsaka ny ran’ny tenany ihany; Mamitsaka hahafaty ny tenany ihany izy.",
			"19": "<sup>19</sup> Toy izany no iafaran’izay rehetra fatra-pila harena; Fa hipaoka ny ain’izay fatra-pila azy izany.",
			"20": "<sup>20</sup> NY fahendrena miantso mafy eny ivelany; Manandratra ny feony eny an-kalalahana izy;",
			"21": "<sup>21</sup> Mitory ao amin’izay fivorian’ny maro be mireondreona izy; Eo anoloan’ny vavahady sy ao an-tanàna no ilazany ny teniny hoe:",
			"22": "<sup>22</sup> Ry kely saina, mandra-pahoviana no hitiavanareo ny fahadalana, Sy hifalian’ny mpaniratsira amin’ny faniratsirana, Ary hankahalan’ny adala ny fahalalana?",
			"23": "<sup>23</sup> Miverena hianareo hihaino ny fananarako; Indro, haidiko aminareo ny fanahiko, Ary hampahafantariko anareo ny teniko.",
			"24": "<sup>24</sup> Satria efa niantso Aho, fa nandà hianareo; Efa naninjitra ny tànako Aho, fa tsy nisy nitandrina;",
			"25": "<sup>25</sup> Fa nolavinareo ny saina rehetra natolotro, Sady tsy nety nanaiky ny anatro hianareo:",
			"26": "<sup>26</sup> Izaho kosa hihomehy, raha tonga ny fandringanana anareo; Handatsa Aho, raha tonga ny mahatahotra anareo.",
			"27": "<sup>27</sup> Raha tonga ny zava-mahatahotra anareo tahaka ny rivo-doza Ary ny fandringanana anareo toy ny tadio, Ka manjo anareo ny fahoriana sy ny fangirifiriana,",
			"28": "<sup>28</sup> Dia vao hiantso Ahy izy, fa tsy hamaly Aho; Dia vao hitady Ahy fatratra izy, fa tsy ho hitany Aho.",
			"29": "<sup>29</sup> Satria nankahala ny fahalalana izy Ary tsy nifidy ny fahatahorana an’i Jehovah,",
			"30": "<sup>30</sup> Tsy nekeny ny saina natolotro, Notsiratsirainy avokoa ny anatro rehetra.",
			"31": "<sup>31</sup> Dia hihinana ny vokatry ny alehany izy Ka ho voky ny fihendreny.",
			"32": "<sup>32</sup> Fa ny fihemoran’ny kely saina hahafaty azy, Ary ny fiadanan’ny adala handringana azy.",
			"33": "<sup>33</sup> Fa izay mihaino Ahy kosa hitoetra tsy manana ahiahy, Eny, handry fahizay izy ka tsy hatahotra ny ratsy."
		},
		"2": {
			"1": "<sup>1</sup> ANAKA, raha raisinao ny teniko, Ary raketinao ao am-ponao ny didiko,",
			"2": "<sup>2</sup> Ka atongilanao amin’ny fahendrena ny sofinao, Ary ampitandremanao fahalalana ny fonao;",
			"3": "<sup>3</sup> Eny, raha miantso ny fahalalana hianao Ary manandratra ny feonao hahazo saina,",
			"4": "<sup>4</sup> Raha mitady azy toy ny fitady volafotsy hianao Ary mikatsaka azy toy ny fikatsaka harena afenina:",
			"5": "<sup>5</sup> Dia ho fantatrao marina ny fahatahorana an’i Jehovah, Ary ho hitanao ny fahalalana an’Andriamanitra.",
			"6": "<sup>6</sup> Fa Jehovah no manome fahendrena; Avy amin’ny vavany no ihavian’ny fahalalana sy ny fahazavan-tsaina.",
			"7": "<sup>7</sup> Izy mitahiry fanambinana ho an’ny mahitsy Sady ampingan’izay mandeha tsy misy tsiny;",
			"8": "<sup>8</sup> Ka dia mitandrina ny làlan’ny fahamarinana Izy Ary miambina ny làlan’ny olony masina.",
			"9": "<sup>9</sup> Ary amin’izay dia ho fantatrao ny fahamarinana sy ny rariny Ary ny hitsiny, dia ny làlana tsara rehetra.",
			"10": "<sup>10</sup> Raha latsaka ao am-ponao ny fahendrena, Ary mamin’ny fanahinao ny fahalalana,",
			"11": "<sup>11</sup> Dia hiaro anao ny fisainana mazava, Ary hiambina anao ny fahalalana,",
			"12": "<sup>12</sup> Ka hamonjy anao amin’ny làlan-dratsy Sy amin’ny lehilahy izay miteny fitaka,",
			"13": "<sup>13</sup> Izay mahafoy ny làlan’ny fahamarinana Mba hizorany amin’ny làlan’ny fahamaizinana,",
			"14": "<sup>14</sup> Dia izay faly hanao ratsy Sy finaritra amin’ny fitaky ny ratsy,",
			"15": "<sup>15</sup> Dia izay melo-dàlana Sy maniasia amin’ny alehany;",
			"16": "<sup>16</sup> Ary hamonjy anao amin’ny vehivavy jejo koa izany, Dia amin’ny vahiny janga izay mandrobo amin’ny teniny,",
			"17": "<sup>17</sup> Izay mahafoy ny vadin’ ny fahatanorany Sy manadino ny fanekeny teo amin’Andriamaniny.",
			"18": "<sup>18</sup> Fa ny tranony milentika ho amin’ny fahafatesana, Ary ny alehany mankany amin’ny maty.",
			"19": "<sup>19</sup> Tsy misy afa-miverina izay mankany aminy, Na mihazo amin’ny làlan’aina, –",
			"20": "<sup>20</sup> Handehananao amin’ny làlan’ny tsara fanahy Sy hitandremanao ny alehan’ny marina.",
			"21": "<sup>21</sup> Fa ny olona mahitsy no honina amin’ny tany, Ary ny tsy manan-tsiny ihany no ho sisa mitoetra ao;",
			"22": "<sup>22</sup> Fa ny ratsy fanahy kosa hofongorana amin’ny tany, Ary ny mpivadika hongotana."
		},
		"3": {
			"1": "<sup>1</sup> ANAKA, aza manadino ny lalàko; Fa aoka ny fonao hitandrina ny didiko;",
			"2": "<sup>2</sup> Fa andro maro sy taona ela iainana Ary fiadanana no hanampiny ho anao.",
			"3": "<sup>3</sup> Aoka tsy handao anao ny famindrampo sy ny fahamarinana; Afehezo eo am-bozonao izany, Ary soraty ao amin’ny fonao tahaka ny amin’ny vato fisaka,",
			"4": "<sup>4</sup> Ka dia hahazo fitia sy fahalalana tsara Eo anatrehan’Andriamanitra sy ny olona hianao.",
			"5": "<sup>5</sup> Matokia an’i Jehovah amin’ny fonao rehetra, Fa aza miankina amin’ny fahalalanao;",
			"6": "<sup>6</sup> Maneke Azy amin’ny alehanao rehetra, Fa Izy handamina ny làlanao.",
			"7": "<sup>7</sup> Aza manao anao ho hendry; Matahora an’i Jehovah, ka mifadia ny ratsy.",
			"8": "<sup>8</sup> Dia ho famelombelomana ny nofonao izany Sy ho fanamandoana ny taolanao.",
			"9": "<sup>9</sup> Mankalazà an’i Jehovah amin’ny fanananao Sy amin’izay voaloham-bokatrao rehetra;",
			"10": "<sup>10</sup> Ka dia ho feno mitafotafo ny fitoeram-barinao, Ary hihoatra ny ranom-boaloboka eo amin’ny vata fanantazanao.",
			"11": "<sup>11</sup> Anaka, aza manamavo ny famaizan’i Jehovah, Ary aza tofoka amin’ny fananarany;",
			"12": "<sup>12</sup> Fa izay tian’i Jehovah no anariny, Dia tahaka ny ataon’ny ray amin’ny zanaka tiany.",
			"13": "<sup>13</sup> SAMBATRA izay olona mahita fahendrena Sy izay olona mahazo fahalalana.",
			"14": "<sup>14</sup> Fa ny fandrantoana azy dia tsara noho ny fandrantoana volafotsy, Ary ny tombom-barotra aminy dia mihoatra noho ny volamena tsara.",
			"15": "<sup>15</sup> Saro-bidy noho ny voahangy izy; Ary izay rehetra irinao tsy misy azo ampitahaina aminy.",
			"16": "<sup>16</sup> Fa andro maro no eo an-tànany ankavanana; Ary harena sy voninahitra no eo an-tànany ankavia.",
			"17": "<sup>17</sup> Làlana mahafinaritra ny làlany, Ary fiadanana ny alehany rehetra.",
			"18": "<sup>18</sup> Hazon’aina ho an’izay rehetra mifikitra aminy izy; Ary izay rehetra mitana azy no hatao hoe sambatra.",
			"19": "<sup>19</sup> Fahendrena no nanorenan’i Jehovah ny tany, Ary fahalalana no nampitoerany ny lanitra.",
			"20": "<sup>20</sup> Ny fahalalany no entiny mampiboiboika ny rano any amin’ny lalina Sy ampitetevany ando avy amin’ny rahona.",
			"21": "<sup>21</sup> Anaka, aoka tsy hiala amin’ny masonao izany; Raketo ny tena fahendrena sy ny fisainana mazava,",
			"22": "<sup>22</sup> Ka dia ho ain’ny fanahinao izany Ary ho firavaky ny vozonao,",
			"23": "<sup>23</sup> Dia handroso aman-toky hianao, Ka tsy ho tafintohina ny tongotrao.",
			"24": "<sup>24</sup> Tsy hatahotra hianao, raha mandry; Eny, handry hianao, ary ho mamy ny torimasonao.",
			"25": "<sup>25</sup> Aza manahy ny tahotra avy tampoka, Na ny rivo-doza mamely ny ratsy fanahy, raha avy izany;",
			"26": "<sup>26</sup> Fa Jehovah no ho tokinao Ka hiambina ny tongotrao tsy ho voafandrika.",
			"27": "<sup>27</sup> AZA mamihina ny soa amin’izay tokony homena azy, Raha azon’ny tananao atao ihany;",
			"28": "<sup>28</sup> Aza manao amin’ny namanao hoe: Mandehana, ka avia indray, fa rahampitso no homeko; Kanefa manana ihany hianao.",
			"29": "<sup>29</sup> Aza mamorona sain-dratsy ho enti-mamely ny namanao, Saingy mitoetra matoky eo aminao izy.",
			"30": "<sup>30</sup> Aza miady amin’olona foana, Raha tsy mbola nanisy ratsy anao izy.",
			"31": "<sup>31</sup> Aza mialona ny olon-dozabe, Na mifidy izay làlany akory,",
			"32": "<sup>32</sup> Satria fahavetavetana eo imason’i Jehovah ny maniasia, Fa ao amin’ny marina kosa ny fisakaizany.",
			"33": "<sup>33</sup> Ny ozon’i Jehovah dia ao an-tranon’ny ratsy fanahy; Fa ny fonenan’ny marina dia tahìny kosa.",
			"34": "<sup>34</sup> Na dia tsiratsirainy aza ny mpaniratsira, Dia omeny fahasoavana kosa ny mpandefitra.",
			"35": "<sup>35</sup> Ny hendry handova voninahitra; Fa henatra kosa no ho anjaran’ny adala."
		},
		"4": {
			"1": "<sup>1</sup> RY zanaka, henoy ny anatry ny ray, Ary tandremo mba hahazoanareo ny fahalalana tsara.",
			"2": "<sup>2</sup> Fa anatra tsara no omeko anareo; Aza mahafoy ny lalàko.",
			"3": "<sup>3</sup> Fa zanak’ikaky aho, Sady tiana no hany malala teo imason’ineny.",
			"4": "<sup>4</sup> Ary nampianatra ahy izy ka nanao tamiko hoe: Aoka ny fonao hitana ny teniko; Tandremo ny didiko, dia ho velona hianao.",
			"5": "<sup>5</sup> Mahazoa fahendrena, mahazoa fahalalana; Fa aza hadinoina na ihemorana ny teny aloaky ny vavako.",
			"6": "<sup>6</sup> Aza mahafoy azy, fa hiaro anao izy: Tiavo izy, dia hitandrina anao.",
			"7": "<sup>7</sup> Ny voaloham-pahendrena dia izao: Mahazoa fahendrena; Eny, lanio ny fanananao rehetra hamidy fahalalana tsara.",
			"8": "<sup>8</sup> Asandrato izy, dia hanandratra anao kosa. Eny, hanome voninahitra anao izy, raha tànanao.",
			"9": "<sup>9</sup> Dia hasiany fehiloha mahafinaritra ny lohanao; Eny, satroboninahitra tsara tarehy no homeny anao.",
			"10": "<sup>10</sup> Anaka, mihainoa, ka raiso ny teniko; Dia hihamaro ny taona hiainanao.",
			"11": "<sup>11</sup> Làlam-pahendrena no atoroko anao; Eny, mitarika anao amin’ny làlana mahitsy aho.",
			"12": "<sup>12</sup> Raha mandeha hianao, dia tsy ho ety ny làlanao; Ary raha mihazakazaka hianao, dia tsy ho tafintohina.",
			"13": "<sup>13</sup> Tano mafy ny fananarana, ka aza avela hiala; Hazony izy, fa ainao.",
			"14": "<sup>14</sup> Aza mankany amin’ny alehan’ny ratsy fanahy, Ary aza mizotra amin’ny làlan’ny ratsy.",
			"15": "<sup>15</sup> Manalavira azy; aza mandalo eo akory; Mivilia azy, ka mizora.",
			"16": "<sup>16</sup> Fa tsy matory ireny, raha tsy efa nanao ratsy; Ary tsy mahita torimaso izy, raha tsy efa nanafintohina ny sasany.",
			"17": "<sup>17</sup> Fa mihinana ny mofon’ny faharatsiana izy, Sy misotro ny divain’ny fahalozana.",
			"18": "<sup>18</sup> Fa ny làlan’ny marina dia toy ny fipoak’andro maraina, Izay mihamazava mandra-pitataovovonan’ny andro.",
			"19": "<sup>19</sup> Ny alehan’ny ratsy fanahy dia toy ny aizim-pito; Tsy fantany akory izay manafintohina azy.",
			"20": "<sup>20</sup> Anaka, tandremo ny teniko; Atongilano ny sofinao hihaino ny filazako.",
			"21": "<sup>21</sup> Aza avela hiala eo imasonao izy, Fa raketo ao am-ponao.",
			"22": "<sup>22</sup> Fa aina ho an’ny mahazo azy izy Ary fahasalamana ho an’ny nofony rehetra.",
			"23": "<sup>23</sup> Tandremo ny fonao mihoatra noho izay rehetra tokony hotandremana; Fa avy ao aminy no ihavian’ ny aina.",
			"24": "<sup>24</sup> Esory aminao ny vava mandainga, Ary ampanalaviro anao ny molotra mamitaka.",
			"25": "<sup>25</sup> Aoka ny masonao hijery mahitsy, Ary ny hodimasonao hibanjina eo anoloanao.",
			"26": "<sup>26</sup> Ataovy marina ny alehan’ny tongotrao, Dia ho voalamina ny alehanao rehetra;",
			"27": "<sup>27</sup> Aza mivily, na ho amin’ny ankavanana, na ho amin’ny ankavia; Esory amin’ny ratsy ny tongotrao."
		},
		"5": {
			"1": "<sup>1</sup> ANAKA, tandremo ny fahendreko, Ary atongilano ny sofinao ho amin’ny fahalalako,",
			"2": "<sup>2</sup> Hiheveranao tsara ny fisainana mazava Sy hitandreman’ny molotrao ny fahalalana.",
			"3": "<sup>3</sup> Fa ny molotry ny vehivavy jejo dia mitete toy ny tantely avy amin’ny tohotra, Ary ny vavany dia malama noho ny diloilo;",
			"4": "<sup>4</sup> Nefa ny farany mangidy tahaka ny hazo mahafaty Ary maranitra hoatra ny sabatra roa lela.",
			"5": "<sup>5</sup> Ny tongony midina ho any amin’ny fahafatesana, Ary ny diany mizotra ho any amin’ny fiainan-tsi-hita.",
			"6": "<sup>6</sup> Tsy mahazo mandia ny làlan’aina mihitsy izy, Fa manjenjena, ka tsy fantany akory izay halehany.",
			"7": "<sup>7</sup> Koa mihainoa ahy, ry zanaka, Ary aza miala amin’ny teny aloaky ny vavako.",
			"8": "<sup>8</sup> Ampanalaviro azy ny alehanao, Ary aza manakaiky ny varavaran’ny tranony;",
			"9": "<sup>9</sup> Fandrao omenao ho an’ny sasany ny voninahitrao, Ary ny taonanao ho an’izay lozabe;",
			"10": "<sup>10</sup> Fandrao hivokisan’ny vahiny ny fanananao; Ary izay nisasaranao ho tonga ao an-tranon’ny vahiny;",
			"11": "<sup>11</sup> Ka dia hitoloko hianao any am-parany, Raha mihalevona ny nofonao sy ny tenanao;",
			"12": "<sup>12</sup> Dia hanao hoe hianao: Nahoana re no nankahala famaizana aho? Ary nahoana no naniratsira fanaranana ny foko,",
			"13": "<sup>13</sup> Ka tsy nihaino ny tenin’ny mpanoro hevitra ahy, Ary tsy nanongilana ny sofiko hihaino ny mpampianatra ahy?",
			"14": "<sup>14</sup> Efa saiky azon’ny ratsy rehetra aho Tao amin’ny fiangonana sy ny fivoriana.",
			"15": "<sup>15</sup> Misotroa rano avy amin’ny lavaka famorian-dranonao ihany Sy rano miboiboika avy amin’ny fantsakanao.",
			"16": "<sup>16</sup> Hipasaka eny ivelany va ny rano avy amin’ny loharanonao, Ary eny an-dàlambe ny rano velonao?",
			"17": "<sup>17</sup> Aoka ho anao irery ihany izany, Fa aza iombonana amin’ny hafa.",
			"18": "<sup>18</sup> Aoka hotahina ny loharanonao, Ary mifalia amin’ny vadin’ny fahatanoranao.",
			"19": "<sup>19</sup> Fa dieravavy mahate-ho-tia sy osi-dia tsara bika izy, Dia aoka ny tratrany no hahafeno fifaliana anao mandrakariva, Ary aoka ho feno faharavoravoana làlandava amin’ny fitiavany hianao.",
			"20": "<sup>20</sup> Nahoana hianao, anaka, no ho renoka amin’ny fitiavana vehivavy jejo Ka hanohona ny tratran’ny vahiny janga?",
			"21": "<sup>21</sup> Fa eo imason’i Jehovah ny alehan’ny olona, Ary ny diany rehetra dia dinihiny avokoa.",
			"22": "<sup>22</sup> Ny heloky ny ratsy fanahy dia mahazo ny tenany ihany, Ary ny kofehin’ny fahotany no itanana azy.",
			"23": "<sup>23</sup> Maty noho ny tsi-fahazoany famaizana izy, Eny, mikorapaka noho ny haben’ny fahadalany."
		},
		"6": {
			"1": "<sup>1</sup> ANAKA, raha niantoka ny namanao hianao Ary nandray tànana hiantoka ny hafa,",
			"2": "<sup>2</sup> Ka voafandriky ny teny naloaky ny vavanao hianao Sady voasingotry ny teny naloaky ny vavanao,",
			"3": "<sup>3</sup> Dia izao no ataovy, anaka, ka afaho ny tenanao (Satria efa mby eo an-tànan’ny namanao hianao): Andeha mihohoka sy mifona mafy amin’ny namanao.",
			"4": "<sup>4</sup> Aza avela hatory akory ny masonao, Ary aza avela hilondolondo ny hodimasonao.",
			"5": "<sup>5</sup> Afaho ny tenanao toy ny gazela miala amin’izay mihazona azy, Ary toy ny voron-kely miala amin’ny tànan’ny mpamandrika.",
			"6": "<sup>6</sup> Mandehana any amin’ny vitsika, ry ilay malaina; Diniho ny fanaony, ka mba hendre;",
			"7": "<sup>7</sup> Tsy manana komandy ireny, Na mpifehy, na mpanapaka,",
			"8": "<sup>8</sup> Nefa mamory ny haniny raha fahavaratra izy, Ary manangona ny fihinany amin’ny fararano.",
			"9": "<sup>9</sup> Mandra-pahoviana no mbola handry ihany hianao, ry ilay malaina? Rahoviana no hifoha amin’ny torimasonao hianao?",
			"10": "<sup>10</sup> Mbola hatory kely ihany, mbola ho renoka kely ihany, Ary mbola hanohon-tànana kely hatory,",
			"11": "<sup>11</sup> Dia ho tonga toy ny lehilahy mimaona hamely ny alahelonao, Ary ny fahantranao ho toy ny jiolahy.",
			"12": "<sup>12</sup> Ny olona tena ratsy fanahy sy mpanao ratsy Dia ilay mandehandeha manao vava ratsy,",
			"13": "<sup>13</sup> Izay mamì-maso sy manao famantarana amin’ny tongony Ary manao baiko amin’ny rantsan-tànany;",
			"14": "<sup>14</sup> Fahafetsena no ao am-pony, Mihevi-dratsy mandrakariva izy Sady mamafy ady.",
			"15": "<sup>15</sup> Ary noho izany dia ho avy tampoka kosa ny loza hanjo azy; Ho torotoro tampoka izy, ka tsy hisy fahasitranana.",
			"16": "<sup>16</sup> Izao enina izao no halan’i Jehovah,– Eny, fito no ataony ho fahavetavetana:",
			"17": "<sup>17</sup> Ny maso miavonavona, ny lela fandainga, Ary ny tànana mandatsa-dra marina,",
			"18": "<sup>18</sup> Ny fo mamoron-tsain-dratsy, Ny tongotra malaky mihazakazaka hanao ratsy,",
			"19": "<sup>19</sup> Ny mamoaka lainga ho vavolombelona tsy marina, Ary ny mamafy ady ao amin’ny rahalahy.",
			"20": "<sup>20</sup> ANAKA, tandremo ny didin-drainao, Ary aza mahafoy ny lalàn-dreninao.",
			"21": "<sup>21</sup> Afatory amin’ny fonao mandrakariva ireo, Ary afehezo eo am-bozonao,",
			"22": "<sup>22</sup> Raha mandeha hianao, dia hitari-dàlana anao ireo; Raha mandry hianao, dia hiambina anao; Ary raha mifoha hianao, dia hamelona eritreritra anao.",
			"23": "<sup>23</sup> Fa jiro ny didy, ary fanazavana ny lalàna, Ary làlan’aina ny anatra famaizana,–",
			"24": "<sup>24</sup> Hiarovana anao amin’ny vehivavy ratsy fanahy, Ary amin’ny fandroboan’ny lelan’ny vahiny janga.",
			"25": "<sup>25</sup> Aoka ny fonao tsy haniry ny hatsaran-tarehiny; Ary aoka tsy hahazo anao amin’ny hodi-masony izy.",
			"26": "<sup>26</sup> Fa noho ny vehivavy mpijangajanga no mila tsy hananan’ny lehilahy sombi-mofo, Ary ny amin’ny vadin’ny olona no mihaza ny aina soa.",
			"27": "<sup>27</sup> Mahatondra afo ao an-tratran’akanjony va ny olona, Ka tsy ho may ny akanjony?",
			"28": "<sup>28</sup> Misy mahadia vain’afo va, Ka tsy ho may ny tongony?",
			"29": "<sup>29</sup> Dia toy izany izay mankamin’ny vadin’ny namany; Tsy maintsy hampijalina izay mikasika azy.",
			"30": "<sup>30</sup> Tsy avelan’ny olona ho afa-maina ny mpangalatra, Raha maka zavatra hahavoky ny tenany izy, na dia noana aza;",
			"31": "<sup>31</sup> Fa raha azo izy, dia hampanoneriny fito heny Ka hanome ny fananana rehetra ao an-tranony.",
			"32": "<sup>32</sup> Izay mijangajanga amin’ny vehivavy dia tsy ampy saina; Izay ta-hanimba tena no manao izany.",
			"33": "<sup>33</sup> Voakapoka sy afa-baraka izy Ary tsy ho afaka amin’ny tondro-molotra.",
			"34": "<sup>34</sup> Fa ny fahasaro-piaro dia mampirehitra ny fahatezeran-dehilahy, Koa tsy hamindra fo izy amin’ny andro hamaliany.",
			"35": "<sup>35</sup> Tsy handray vola ho enti-manalady akory izy, Ary tsy hety, na dia omenao fanambatambazana be aza."
		},
		"7": {
			"1": "<sup>1</sup> Anaka, tandremo ny teniko, Ary raketo ao aminao ny didiko.",
			"2": "<sup>2</sup> Tandremo ny didiko, dia ho velona hianao; Ary tandremo toy ny anakandriamasonao ny lalàko.",
			"3": "<sup>3</sup> Afehezo eo amin’ny rantsan-tànanao izy, Soraty ao amin’ny fonao tahaka ny amin’ny vato fisaka izy.",
			"4": "<sup>4</sup> Ny fahendrena dia ilazao hoe: Anabaviko hianao; Ary ny fahafantarana dia antsoy hoe: Sakaiza mahazatra hianao;",
			"5": "<sup>5</sup> Mba hiarovana anao amin’ny vehivavy jejo, Dia amin’ny vahiny janga izay manao teny mandrobo.",
			"6": "<sup>6</sup> Fa teo amin’ny varavarankelin’ny tranoko, Dia teo amin’ny makarakara, no nitsirihako,",
			"7": "<sup>7</sup> Ka nahita anankiray tamin’ny kely saina aho,– Eny, nandinika ny tovolahy aho ka nahita zazalahy anankiray tsy ampy saina,",
			"8": "<sup>8</sup> Izay nandeha tamin’ny làlana akaikin’ny zoron-tranon’ny vehivavy janga Sy nizotra nankany an-tranony,",
			"9": "<sup>9</sup> Nony takariva ilay somambisamby iny Sy tamin’ny alina efa maizina dia maizina.",
			"10": "<sup>10</sup> Dia, indro, nisy vehivavy nitsena azy Sady manao fitafin’ny janga no mifetsy am-po,",
			"11": "<sup>11</sup> Sady vavana no maditra, Tsy mahajanona ao an-tranony ny tongony;",
			"12": "<sup>12</sup> Fa vetivety mby eny an-dàlambe izy, ary vetivety mby eny an-tsena, Ary mamitsaka eny an-joron-trano rehetra eny izy.",
			"13": "<sup>13</sup> Ka dia nisavika ilay tovolahy izy ka nanoroka azy Sady nisolantsolana taminy ka nilaza hoe:",
			"14": "<sup>14</sup> Efa nivoady hanatitra fanati-pihavanana aho; Ary andro-any no nanefako ny voadiko.",
			"15": "<sup>15</sup> Koa dia nivoaka hitsena anao aho, Mba hitady fatratra ny tarehinao, ka dia nahita anao tokoa.",
			"16": "<sup>16</sup> Ny fandriako efa nasiako firakotra tsara, Dia lambam-pandriana misoratsoratra voafoly hatrany Egypta.",
			"17": "<sup>17</sup> Efa norarahako zava-manitra ny fandriako, Dia miora sy hazomanitra ary havozo manitra.",
			"18": "<sup>18</sup> Andeha, aoka isika hivoky fitia ambara-pahamarain’ny andro, Aoka ho feno fahafinaretana amin’ny fitiavana isika.",
			"19": "<sup>19</sup> Fa tsy ao an-trano ny lehilahy, Fa lasa nivahiny lavitra izy;",
			"20": "<sup>20</sup> Ny kitampom-bola dia nentiny any an-tànany, Ka tsy mbola hody izy mandra-pahafenon’ny volana.",
			"21": "<sup>21</sup> Tamin’ny teny maro no nampanekeny azy, Tamin’ny fandroboan’ny molony no nitaomany azy,",
			"22": "<sup>22</sup> Ka dia nandeha nanaraka azy niaraka tamin’izay ilay zatovo, Tahaka ny omby entina hovonoina, Ary tahaka ny adala hogadrana,",
			"23": "<sup>23</sup> Ambara-piboroaky ny lefompohy amin’ny atiny, Ary tahaka ny vorona mipaoka ny fandrika, Fa tsy fantany hahafaty ny ainy izany.",
			"24": "<sup>24</sup> Koa mihainoa ahy hianareo, anaka, Ary tandremo ny teny aloaky ny vavako.",
			"25": "<sup>25</sup> Aoka tsy hivily ho any amin’ny làlany ny fonareo, Ary aza mania ho amin’ny alehany.",
			"26": "<sup>26</sup> Fa betsaka no naripany ka maty; Ary maro be no efa matiny.",
			"27": "<sup>27</sup> Ny tranony dia làlana mankany amin’ny fiainan-tsi-hita, Eny, midina ho any amin’ny efitranon’ny fahafatesana."
		},
		"8": {
			"1": "<sup>1</sup> TSY miantso va ny fahendrena, Ary tsy manandra-peo va ny fahalalana?",
			"2": "<sup>2</sup> Eo an-tampon’ny fitoerana avo amoron-dàlana Sy eo an-tsampanan-dàlana no ijoroany.",
			"3": "<sup>3</sup> Eo anilan’ny vavahady, amin’ny fidirana ho ao an-tanàna, Sy eo anaty vavahady no iantsoany mafy hoe:",
			"4": "<sup>4</sup> Hianareo no antsoiko, ry olona; Ary ny feoko ho amin’ny zanak’olombelona.",
			"5": "<sup>5</sup> Fantaro ny fahendrena, ry kely saina; Ary fantaro ny fahalalana, ry adala.",
			"6": "<sup>6</sup> Henoy, fa hilaza zava-tsoa aho; Ary ny hilaza ny mahitsy no isokafan’ny molotro.",
			"7": "<sup>7</sup> Fa ny marina ihany no holazain’ny vavako; Ary ny ratsy dia fahavetavetana, raha amin’ny molotro.",
			"8": "<sup>8</sup> Marina ny teny rehetra aloaky ny vavako; Tsy misy fiolakolahana na fitaka ao aminy.",
			"9": "<sup>9</sup> Miharihary avokoa izy, raha amin’ny mazava saina, Ary mahitsy, raha amin’ny efa nahazo fahalalana.",
			"10": "<sup>10</sup> Aza ny volafotsy no raisina, fa ny anatro, Ary aleo ny fahalalana toy izay ny volamena voafantina.",
			"11": "<sup>11</sup> Fa ny fahendrena dia tsara noho ny voahangy; Ary ny zavatra irina rehetra tsy azo ampitahaina aminy.",
			"12": "<sup>12</sup> Izaho fahendrena dia miara-monina amin-tsaina, Ary hitan’ny fahalalako ny fisainana mazava.",
			"13": "<sup>13</sup> Ny fankahalana ny ratsy no fahatahorana an’i Jehovah. Ny fiavonavonana sy ny fireharehana sy ny làlana ratsy Ary ny vava fandainga dia samy halako avokoa.",
			"14": "<sup>14</sup> Ahy ny fisainana sy ny fanambinana; Izaho no fahalalana, izaho no manan-kery;",
			"15": "<sup>15</sup> Izaho no anjakan’ny mpanjaka Sy andidian’ny mpanapaka araka ny rariny;",
			"16": "<sup>16</sup> Izaho no anapahan’ny andriandahy sy ny lehibe, Dia ny mpitsara rehetra amin’ny tany.",
			"17": "<sup>17</sup> Tiako izay tia ahy; Ary izay fatra-pitady ahy dia hahita ahy.",
			"18": "<sup>18</sup> Ato amiko ny harena sy ny voninahitra, Eny, ny harena mateza sy ny fahamarinana.",
			"19": "<sup>19</sup> Ny vokatro dia tsara noho ny volamena, eny, noho ny tena volamena aza, Ary ny fitomboan’ny hareko mihoatra noho ny volafotsy voafantina.",
			"20": "<sup>20</sup> Amin’ny alehan’ny fahamarinana no izorako, Dia ao amin’ny làlan’ny fitsarana marina,",
			"21": "<sup>21</sup> Mba hampandovako harena izay tia ahy, Sy hamenoako ny firaketany.",
			"22": "<sup>22</sup> Jehovah nahary ahy ho fiandohan’ny alehany, Ho voalohany amin’ny asany hatramin’ny taloha.",
			"23": "<sup>23</sup> Izaho notendreny hatramin’ny fahagola, Eny, hatramin’ny voalohany indrindra, dia fony tsy mbola ary ny tany.",
			"24": "<sup>24</sup> Fony tsy mbola ary ny lalina, no efa teraka aho, Fony tsy mbola ary ny loharano miboiboika be,",
			"25": "<sup>25</sup> Fony tsy mbola naorina ny tendrombohitra, Fony tsy mbola nisy havoana, no efa teraka aho,",
			"26": "<sup>26</sup> Fony tsy mbola natao ny tany, na ny saha, Na ny voalohan’ny vovo-tany ambonin’izao rehetra izao.",
			"27": "<sup>27</sup> Raha namorona ny lanitra Izy, dia tany aho, Raha namaritra ny habakabaka tambonin’ny lalina Izy,",
			"28": "<sup>28</sup> Raha nampitoetra ny rahona eny ambony Izy, Raha niboiboika fatratra ny loharanon’ny lalina,",
			"29": "<sup>29</sup> Raha nanisy faritra ho an’ny ranomasina Izy, Mba tsy hahazoan’ny rano mihoatra ny fetrany, Ary nandidy ny amin’ny fanorenan’ny tany,–",
			"30": "<sup>30</sup> Dia mpità-marika teo anilany aho Sady ravoravo isan-andro isan-andro Ka nilalao mandrakariva teo anatrehany;",
			"31": "<sup>31</sup> Eny, nahafaly ahy ny tany nataony honenana, Ary naharavoravo ahy ny zanak’olombelona.",
			"32": "<sup>32</sup> Koa mihainoa ahy hianareo, anaka; Fa sambatra izay manaraka ny diako;",
			"33": "<sup>33</sup> Minoa anatra, ka hendre, Fa aza mandà.",
			"34": "<sup>34</sup> Sambatra izay olona mihaino ahy Ka miari-tory mandrakariva eo am-bavahadiko, Ary miandry eo amin’ny tolam-baravarako.",
			"35": "<sup>35</sup> Fa izay mahita ahy dia mahita fiainana Sady mahazo sitraka amin’i Jehovah;",
			"36": "<sup>36</sup> Fa izay diso ahy kosa mampidi-doza amin’ny fanahiny; Ary izay rehetra mankahala ahy tia fahafatesana."
		},
		"9": {
			"1": "<sup>1</sup> NY fahendrena efa nanao ny tranony; Efa nipaika ny andriny fito izy;",
			"2": "<sup>2</sup> Efa namono ny bibiny sy nangaroharo ny divainy izy Ary efa namelatra ny latabany koa;",
			"3": "<sup>3</sup> Ary efa naniraka ny ankizivaviny izy; Miantso eny an-tampon’ny fitoerana avo any an-tanàna izy ka manao hoe:",
			"4": "<sup>4</sup> Izay kely saina, dia aoka izy hankatỳ; Ary amin’izay tsy ampy saina dia hoy izy:",
			"5": "<sup>5</sup> Avia, hano ny mofoko, Ary sotroy ny divay voaharoharoko.",
			"6": "<sup>6</sup> Ilaozy ny kely saina, dia ho velona hianao, Ary mizora amin’ny làlan’ny fahalalana.",
			"7": "<sup>7</sup> Izay mananatra ny mpaniratsira dia afa-baraka; Ary izay mananatra ny ratsy fanahy dia hahazo tsiny.",
			"8": "<sup>8</sup> Aza mananatra ny mpaniratsira hianao, fandrao halany; Ny hendry no anaro, dia ho tiany hianao.",
			"9": "<sup>9</sup> Toroy ny hendry, dia hihahendry izy; Ampianaro ny marina, dia hitombo saina izy.",
			"10": "<sup>10</sup> Ny fahatahorana an’i Jehovah no fiandoham-pahendrena; Ary ny fahafantarana ny Iray Masina no fahazavan-tsaina.",
			"11": "<sup>11</sup> Fa izaho no hampitomboana ny andronao Sy hanamaroana ny taona hiainanao.",
			"12": "<sup>12</sup> Raha hendry hianao, dia hendry ho an’ny tenanao ihany; Fa raha maniratsira kosa hianao, dia hianao ihany no hiharan’izany.",
			"13": "<sup>13</sup> Ny fahadalana dia toy ny vehivavy vavana; Kely saina izy ka tsy mahalala na inona na inona.",
			"14": "<sup>14</sup> Fa mipetraka eo am-baravaran’ny tranony izy, Ary amin’ny seza eny amin’ny fitoerana avo eo an-tanàna,",
			"15": "<sup>15</sup> Mba hiantso ny mpandalo Izay mizotra amin’ny alehany, ka manao hoe:",
			"16": "<sup>16</sup> Aoka hivily atỳ izay kely saina; Ary amin’izay tsy ampy saina dia hoy izy:",
			"17": "<sup>17</sup> Mamy ny rano angalarina, Ary fy ny mofo hanina amin’ny fiafenana,",
			"18": "<sup>18</sup> Nefa tsy fantany fa ny maty no ao, Ary any amin’ny halalin’ny fiainan-tsi-hita ny olona nasainy."
		},
		"10": {
			"1": "<sup>1</sup> OHABOLAN’i Solomona. Ny zanaka hendry mahafaly ny rainy; Fa ny zanaka adala mampalahelo ny reniny.",
			"2": "<sup>2</sup> Ny harena azo amin’ny tsy marina dia tsy mahasoa na inona na inona; Fa ny fahamarinana no mamonjy amin’ny fahafatesana.",
			"3": "<sup>3</sup> Tsy avelan’i Jehovah ho mosarena ny fanahin’ny marina; Fa foanany ny fanirian’ny ratsy fanahy.",
			"4": "<sup>4</sup> Ho tonga malahelo ny miraviravy tànana amin’ny asany; Fa mampanan-karena ny tànan’ny mazoto.",
			"5": "<sup>5</sup> Izay manangona amin’ny fararano no zanaka hendry; Fa izay sondrian-tory amin’ny taom-pijinjana no zanaka mampahamenatra.",
			"6": "<sup>6</sup> Fitahiana no ho ao amin’ny lohan’ny marina; Fa loza no mandrakotra ny vavan’ny ratsy fanahy.",
			"7": "<sup>7</sup> Hankasitrahana ny fahatsiarovana ny marina; Fa ho lo ny anaran’ny ratsy fanahy.",
			"8": "<sup>8</sup> Izay hendry am-po no handray ny didy; Fa ho lavo ny adala maro vava.",
			"9": "<sup>9</sup> Izay mandeha tsy misy tsiny no mandeha matoky; Fa izay maniasia amin’ny alehany dia fantatra.",
			"10": "<sup>10</sup> Izay mamì-maso manome alahelo; Fa ho lavo ny adala maro vava.",
			"11": "<sup>11</sup> Loharanon’aina ny vavan’ny marina; Fa loza no mandrakotra ny vavan’ny ratsy fanahy.",
			"12": "<sup>12</sup> Ny fankahalana dia manetsika ady; Fa ny fitiavana manarona ny ota rehetra.",
			"13": "<sup>13</sup> Amin’ny molotry ny manan-tsaina no ahitam-pahendrena; Fa tsorakazo no ho amin’ny lamosin’ny tsy ampy saina.",
			"14": "<sup>14</sup> Ny hendry mirakitra fahalalana; Fa ny vavan’ny adala eo akaikin’ny fandringanana.",
			"15": "<sup>15</sup> Ny haren’ny manan-karena no vohiny mafy; Fa ny tsi-fananan’ny ory no fandringanana azy.",
			"16": "<sup>16</sup> Ho fiainana ny asan’ny marina; Fa ho ota ny fitomboan’ny haren’ny ratsy fanahy.",
			"17": "<sup>17</sup> Izay mino anatra dia ho tonga eo amin’ny làlan’ny fiainana; Fa mivily izay mandà anatra.",
			"18": "<sup>18</sup> Izay mitahiry lolompo dia manana molotra mandainga, Ary izay mampiely endrikendrika dia adala.",
			"19": "<sup>19</sup> Ny teny maro tsy ilaozan’izay ota; Fa izay mahatana ny molony no hendry.",
			"20": "<sup>20</sup> Volafotsy voafantina ny lelan’ny marina; Fa ny fon’ny ratsy fanahy tsy misy vidiny.",
			"21": "<sup>21</sup> Ny molotry ny marina mamelona ny maro; Fa maty ny adala noho ny tsi-fananany fahalalana.",
			"22": "<sup>22</sup> Ny fitahian’i Jehovah no mampanan-karena, Ary tsy azon’ny fisasarana ampiana izany.",
			"23": "<sup>23</sup> Toa laolaon’ny adala ny fanaovan-dratsy Fa raha ny manan-tsaina, dia fahendrena kosa.",
			"24": "<sup>24</sup> Ny tahotry ny ratsy fanahy hihatra aminy; Fa ny irin’ny marina no homena azy.",
			"25": "<sup>25</sup> Rehefa mipaoka ny tafio-drivotra, dia tsy ao intsony ny ratsy fanahy; Fa ny marina voaorina ho mandrakizay.",
			"26": "<sup>26</sup> Toy ny vinaingitra amin’ny nify sy ny setroka amin’ny maso. Dia mba toy izany koa ny malaina amin’izay maniraka azy.",
			"27": "<sup>27</sup> Ny fahatahorana an’i Jehovah no mahela velona; Fa ny taonan’ny ratsy fanahy hohafohezina.",
			"28": "<sup>28</sup> Ny fanantenan’ny marina dia fifaliana; Fa ny fanantenan’ny ratsy fanahy ho foana.",
			"29": "<sup>29</sup> Ny làlan’i Jehovah dia fiarovana mafy ho an’izay tsy misy tsiny, Fa fandringanana kosa ho an’ny mpanao ratsy.",
			"30": "<sup>30</sup> Ny marina tsy hangozohozo mandrakizay; Fa ny ratsy fanahy tsy honina amin’ny tany.",
			"31": "<sup>31</sup> Eo amin’ny vavan’ny marina no itsimohan’ny fahendrena; Fa ny lela fandainga hofongorana.",
			"32": "<sup>32</sup> Ny molotry ny marina mahalala izay ankasitrahana; Fa ny vavan’ny ratsy fanahy dia feno fitaka."
		},
		"11": {
			"1": "<sup>1</sup> Ny mizana mandainga dia fahavetavetana eo imason’i Jehovah; Fa ny vato marina no ankasitrahany.",
			"2": "<sup>2</sup> Raha tonga ny fiavonavonana, dia tonga koa ny fahafaham-baraka; Fa ao amin’ny manetry tena no misy fahendrena.",
			"3": "<sup>3</sup> Ny fahitsian’ny mahitsy no mitarika azy; Fa ny faharatsian’ny mpivadika mahatonga fandringanana amin’ny tenany.",
			"4": "<sup>4</sup> Ny harena dia tsy mahasoa amin’ny andro fahatezerana; Fa ny fahamarinana no mamonjy tsy ho azon’ny fahafatesana.",
			"5": "<sup>5</sup> Ny fahamarinan’ny marina mandamina ny alehany; Fa ny ratsy fanahy lavon’ny haratsiany ihany.",
			"6": "<sup>6</sup> Ny fahamarinan’ny mahitsy mamonjy azy; Fa ny mpivadika dia voafandriky ny filàny.",
			"7": "<sup>7</sup> Raha maty ny ratsy fanahy, dia foana ny fanantenany, Eny, foana ny fanantenan’ny mpanao ratsy.",
			"8": "<sup>8</sup> Ny marina dia vonjena amin’ny fahoriana; Fa ho avy kosa ny ratsy fanahy hisolo azy.",
			"9": "<sup>9</sup> Ny vavan’ny mpihatsaravela-tsihy no animbany ny namany; Fa ny fahalalana no hanafahana ny marina.",
			"10": "<sup>10</sup> Raha miadana ny marina, dia ravoravo ny tanàna; Fa raha ringana ny ratsy fanahy, dia velona ny hoby.",
			"11": "<sup>11</sup> Ny fisaoran’ ny marina no isandratan’ny tanàna; Fa ny vavan’ny ratsy fanahy no aharavany.",
			"12": "<sup>12</sup> Tsy ampy saina izay manamavo ny namany; Fa izay hendry dia mangina.",
			"13": "<sup>13</sup> Izay mandehandeha manaratsy dia manambara ny hevitra afenina; Fa ny olona mahatoky tsy mba tia lazalaza.",
			"14": "<sup>14</sup> Raha tsy misy mpitondra, dia rava ny firenena; Fa handry fahizay, raha maro ny mpanolo-tsaina.",
			"15": "<sup>15</sup> Hahita loza tokoa ny mianto-bahiny; Fa mitoetra tsy manana ahiahy ny tsy tia antoka.",
			"16": "<sup>16</sup> Ny vehivavy tsara tarehy mahazo dera; Ary ny mpanao an-keriny mahazo harena.",
			"17": "<sup>17</sup> Ny olona mamindra fo dia mahazo soa ho an’ny tenany; Fa ny lozabe dia todìn’ny halozany.",
			"18": "<sup>18</sup> Ny ratsy fanahy dia mahazo valiny tsy mahasoa; Fa izay mamafy fahamarinana no mahazo izay tena tambiny tokoa.",
			"19": "<sup>19</sup> Ny tena fahamarinana dia mamantana any amin’ny fiainana; Fa izay fatra-panaraka ny ratsy dia iharan’ny fahafatesana.",
			"20": "<sup>20</sup> Fahavetavetana eo imason’i Jehovah ny meloka am-po; Fa ankasitrahany kosa izay mandeha tsy misy tsiny.",
			"21": "<sup>21</sup> Azo itompoana tokoa fa tsy maintsy hampijalina ny ratsy fanahy; Fa ny taranaky ny marina no hovonjena.",
			"22": "<sup>22</sup> Toy ny kavim-bolamena eo amin’ny oron-kisoa Ny vehivavy tsara tarehy tsy manam-pahendrena.",
			"23": "<sup>23</sup> Ny soa ihany no irin’ny marina; Fa fahatezerana no fanantenan’ny ratsy fanahy.",
			"24": "<sup>24</sup> Misy mahafoy, nefa mitombo fananana ihany; Ary misy fatra-pahihitra loatra, nefa tonga malahelo ihany.",
			"25": "<sup>25</sup> Izay manisy soa olona hohatavezina; Ary izay mandena dia mba holemana kosa.",
			"26": "<sup>26</sup> Izay tsy mamoaka ny variny dia ozonin’ny olona; Fa saotra no ho amin’ny lohan’izay mivarotra.",
			"27": "<sup>27</sup> Izay fatra-pikatsaka ny tsara no mitady ny mahafinaritra; Fa izay mitady ny ratsy dia hiharan’izany.",
			"28": "<sup>28</sup> Izay matoky ny hareny dia ho lavo; Fa ny marina ho tahaka ny ravina maitso.",
			"29": "<sup>29</sup> Izay mampidi-doza amin’ny ankohonany dia mandova ny rivotra; Ary ny adala dia tonga mpanompon’ny hendry.",
			"30": "<sup>30</sup> Ny vokatry ny marina dia hazon’aina, Ary ny hendry dia mahataona olona.",
			"31": "<sup>31</sup> Indro, ny marina aza dia valiana etỳ an-tany, Koa mainka fa ny ratsy fanahy sy ny mpanota!"
		},
		"12": {
			"1": "<sup>1</sup> Izay tia fananarana no tia fahalalana; Fa izay mandà anatra dia biby.",
			"2": "<sup>2</sup> Ny tsara fanahy mahazo sitraka amin’i Jehovah; Fa ny mamoron-tsain-dratsy hohelohiny.",
			"3": "<sup>3</sup> Tsy maha-tafatoetra ny olona ny haratsiana; Fa ny fàkan’ny marina tsy mba hihetsika.",
			"4": "<sup>4</sup> Ny vehivavy tsara dia satroboninahitry ny lahy; Fa ny vehivavy manao izay mahamenatra kosa dia tahaka ny lo ao anatin’ny taolana.",
			"5": "<sup>5</sup> Rariny ny hevitry ny marina; Fa fitaka ny fisainan’ny ratsy fanahy.",
			"6": "<sup>6</sup> Ny tenin’ny ratsy fanahy dia otrika ho enti-mandatsa-dra; Fa ny vavan’ny marina hamonjy azy.",
			"7": "<sup>7</sup> Haringana ny ratsy fanahy ka tsy ho ao intsony; Fa ny mpianakavin’ny marina haharitra.",
			"8": "<sup>8</sup> Araka ny fahendren’ny olona no hiderana azy; Fa izay meloka am-po dia hatao tsinontsinona.",
			"9": "<sup>9</sup> Aleo ambany toetra, nefa manana mpanompo iray, Toy izay mihambo ho be voninahitra, nefa tsy manan-kohanina akory.",
			"10": "<sup>10</sup> Ny marina mitsimbina ny ain’ny bibiny; Fa fahasiahana kosa no famindrampon’ny ratsy fanahy.",
			"11": "<sup>11</sup> Izay miasa ny taniny dia ho voky hanina; Fa izay manaraka ny olom-poana dia tsy ampy saina.",
			"12": "<sup>12</sup> Ny mpanota maniry ny hazan’ ny ratsy fanahy; Fa ny fàkan’ny marina mitrebona.",
			"13": "<sup>13</sup> Amin’ny fahotan’ny molotra dia misy fandrika ahitan-doza; Fa ny marina ho afaka amin’ny fahoriana.",
			"14": "<sup>14</sup> Ny vokatry ny vavan’ny olona no mahavoky soa azy, Ary ny asan’ny tànany hitsingerina aminy.",
			"15": "<sup>15</sup> Ny làlan’ny adala dia mahitsy eo imasony; Fa izay mino anatra no hendry.",
			"16": "<sup>16</sup> Ny fahasosoran’ny adala dia fantatra miaraka amin’izay; Fa izay mahatsindry fo amin’ny fanalam-baraka no hendry.",
			"17": "<sup>17</sup> Izay olona mahatoky dia manambara ny rariny; Fa ny vavolombelona mandainga kosa miteny fitaka.",
			"18": "<sup>18</sup> Misy mandefalefa teny tsy tsaroana ka tonga toy ny fanindron’ny sabatra; Fa ny lelan’ny marina dia fanasitranana kosa.",
			"19": "<sup>19</sup> Ny molotra marina ampitoerina ho mandrakizay; Fa ny lela mandainga dia indray mipì-maso monja.",
			"20": "<sup>20</sup> Fitaka no ao am-pon’izay mamoron-tsain-dratsy; Fa hafaliana no amin’izay misaina fiadanana.",
			"21": "<sup>21</sup> Tsy misy ratsy manjo ny marina; Fa heni-doza ny ratsy fanahy.",
			"22": "<sup>22</sup> Fahavetavetana eo imason’i Jehovah ny molotra mandainga; Fa izay olona mahatoky no ankasitrahany.",
			"23": "<sup>23</sup> Ny olon-kendry tsy mba mampideradera fahalalana foana; Fa ny fon’ny adala mamoaka fahadalana.",
			"24": "<sup>24</sup> Ny tànan’ny mazoto no hanapaka; Fa izay miraviravy tànana dia hampanompoina.",
			"25": "<sup>25</sup> Ny alahelo ao am-pon’ny olona dia mampitanondrika azy; Fa ny teny soa no mahafaly azy.",
			"26": "<sup>26</sup> Ny marina dia mitari-dàlana ny namany; Fa ny alehan’ny ratsy fanahy kosa no mampivily azy.",
			"27": "<sup>27</sup> Ny kamo tsy mety mihaza; Fa fananana soa ho an’ny olona ny zoto.",
			"28": "<sup>28</sup> Amin’ny làlan’ny fahamarinana no misy fiainana, Ary ny tsi-fahafatesana no làlan-kalehany."
		},
		"13": {
			"1": "<sup>1</sup> Ny zanaka hendry mihaino ny fananaran-drainy; Fa ny mpaniratsira tsy mety mihaino, na dia anarina mafy aza.",
			"2": "<sup>2</sup> Ny vokatry ny vavan’ny olona no ahazoany soa; Fa ny hampidi-doza no mahafaly ny mpivadika.",
			"3": "<sup>3</sup> Izay mahambim-bava no miaro ny ainy; Fa izay vavana dia haringana.",
			"4": "<sup>4</sup> Ny fanahin’ny kamo maniry, fa tsy mba mahazo; Fa ny fanahin’ny mazoto hohatavezina.",
			"5": "<sup>5</sup> Ny lainga dia halan’ny marina; Fa ny ratsy fanahy manao izay maharikoriko sy mahamenatra.",
			"6": "<sup>6</sup> Ny fahamarinana miaro izay mandeha tsy misy tsiny; Fa ny haratsiana mamotraka ny mpanota.",
			"7": "<sup>7</sup> Misy mihambo ho manan-karena, nefa tsy manana na inona na inona; Misy kosa mody malahelo, nefa manana harena be.",
			"8": "<sup>8</sup> Ny haren’ny olona dia avotry ny ainy; Fa ny levilevy tsy mampiraika ny malahelo akory.",
			"9": "<sup>9</sup> Ny fahazavan’ny marina dia tsara firehitra; Fa ny jiron’ny ratsy fanahy ho faty.",
			"10": "<sup>10</sup> Fifandirana ihany no vokatry ny fiavonavonana; Fa ao amin’ny mino anatra no misy fahendrena.",
			"11": "<sup>11</sup> Ny harena azoazo foana dia mihena làlandava; Fa izay mahavatra mitsimpona no mampitombo.",
			"12": "<sup>12</sup> Ny fanantenana tsy azo vetivety dia mankarary am-bavafo; Fa hazon’aina ny faniriana tanteraka.",
			"13": "<sup>13</sup> Izay manamavo ny teny ho voasingony; Fa izay matahotra ny didy no hahazo valim-pitia.",
			"14": "<sup>14</sup> Ny fampianaran’ny hendry dia loharanon’aina Ka mampivily tsy ho ao amin’ny fandriky ny fahafatesana.",
			"15": "<sup>15</sup> Ny fahendrena tsara dia ahazoam-pitia; Fa sarotra ny làlan’ny mpivadika.",
			"16": "<sup>16</sup> Ny mahira-tsaina, rehetra miasa amin’ny fahalalana; Fa ny adala kosa mampiseho hadalana.",
			"17": "<sup>17</sup> Ny iraka ratsy fanahy idiran-doza; Fa ny iraka mahatoky dia famelombelomana.",
			"18": "<sup>18</sup> Fahalahelovana sy henatra no ho an’izay mandà famaizana; Fa izay manaiky anatra no homem-boninahitra.",
			"19": "<sup>19</sup> Mamin’ny fo ny faniriana tanteraka; Fa fahavetavetana eo imason’ny adala ny hiala amin’ny ratsy.",
			"20": "<sup>20</sup> Izay miara-dia amin’ny hendry dia ho hendry; Fa izay misakaiza amin’ny adala dia hidiran-doza.",
			"21": "<sup>21</sup> Ny mpanota dia enjehin’ny loza: Fa hovalian-tsoa ny marina.",
			"22": "<sup>22</sup> Ny tsara fanahy mahenina lova ny zafiny; Fa ny haren’ny mpanota kosa voatahiry ho an’ny marina.",
			"23": "<sup>23</sup> Ny tanin’ny malahelo izay voasa dia ahazoana hanina be; Fa maro no ringana noho ny fanaovany izay tsy rariny.",
			"24": "<sup>24</sup> Izay mitsitsy ny tsorakazony dia tsy tia ny zanany; Fa izay tia zanaka dia manafay azy, raha mbola kely.",
			"25": "<sup>25</sup> Ny marina mihinana ka voky tsara; Fa noana ny kibon’ny ratsy fanahy."
		},
		"14": {
			"1": "<sup>1</sup> Ny vehivavy hendry dia samy manao ny tranony; Fa ny adala kosa mandrava ny tranony amin’ny tànany.",
			"2": "<sup>2</sup> Mandeha mahitsy izay matahotra an’i Jehovah; Fa mivily làlana kosa izay maniratsira Azy.",
			"3": "<sup>3</sup> Eo am-bavan’ny adala misy tsorakazon’ny fiavonavonana; Fa ny molotry ny hendry miaro ny tenany.",
			"4": "<sup>4</sup> Raha tsy misy omby, dia foana ny fihinanam-bilona; Fa be ny fitomboan-karena azo amin’ny herin’ny omby.",
			"5": "<sup>5</sup> Ny vavolombelona mahatoky tsy mba mety mandainga; Fa mamoaka lainga kosa ny vavolombelona tsy mahatoky.",
			"6": "<sup>6</sup> Ny mpaniratsira mba mitady fahendrena, fa tsy mahita; Fa ny manan-tsaina mora mahazo fahalalana.",
			"7": "<sup>7</sup> Mialà eo anatrehan’ny olona adala, Fa tsy ho hitanao aminy ny molotry ny fahalalana.",
			"8": "<sup>8</sup> Ny mitandrina ny alehany no fahendren’ny mahira-tsaina; Fa fitaka ny hadalan’ny adala.",
			"9": "<sup>9</sup> Ny adala ihomehezan’ny fanati-panonerana; Fa ao amin’ny marina no misy fankasitrahana.",
			"10": "<sup>10</sup> Ny fo no mahalala ny fangidian’ny alahelony; Ary tsy raharahan’ny olon-kafa ny hafaliany.",
			"11": "<sup>11</sup> Ravana ny tranon’ny ratsy fanahy; Fa ambinina ny lain’ny marina.",
			"12": "<sup>12</sup> Misy làlana ataon’ny olona ho mahitsy, Kanjo làlana mivarina any amin’ny fahafatesana no iafarany.",
			"13": "<sup>13</sup> Na dia mihomehy aza, dia ory ihany ny fo, Ary ny faran’ny hafaliana dia alahelo.",
			"14": "<sup>14</sup> Ny mihemotra am-po dia ho voky ny alehany; Fa ny tsara fanahy kosa ho voky ny azy ihany.",
			"15": "<sup>15</sup> Ny kely saina mino ny teny rehetra; Fa ny mahira-tsaina mandinika ny diany.",
			"16": "<sup>16</sup> Ny hendry matahotra ka mivily tsy ho amin’ny ratsy; Fa ny adala fatra-pirehareha ka matokitoky foana.",
			"17": "<sup>17</sup> Izay malaky tezitra dia manao adaladala; Ary ny olona mamoron-tsain-dratsy dia ankahalaina.",
			"18": "<sup>18</sup> Ny kely saina dia handova fahadalana; Fa ny mahira-tsaina mahazo ny fahalalana ho satroboninahiny.",
			"19": "<sup>19</sup> Ny ratsy fanahy miondrika eo anatrehan’ny tsara fanahy, Ary ny meloka dia eo am-bavahadin’ny marina.",
			"20": "<sup>20</sup> Ny malahelo dia halan’ny havany aza; Fa ny manan-karena no tian’ny maro.",
			"21": "<sup>21</sup> Izay manao tsinontsinona ny namany dia manota; Fa endrey ny fahasambaran’izay mamindra fo amin’ny ory!",
			"22": "<sup>22</sup> Tsy mivily va izay manao asa ratsy? Fa famindrampo sy fahamarinana no ho an’izay manao asa tsara.",
			"23": "<sup>23</sup> Ny fisasarana rehetra misy valiny avokoa; Fa ny molotra mibedibedy foana tsy mba ahazoana na inona na inona.",
			"24": "<sup>24</sup> Satroboninahitry ny hendry ny hareny; Fa ny fahadalan’ny adala dia fahadalana ihany.",
			"25": "<sup>25</sup> Ny vavolombelona marina mamonjy ain’olona; Fa izay mamoaka lainga dia tena fitaka.",
			"26": "<sup>26</sup> Ny fahatahorana an’i Jehovah dia fiarovana mahatoky, Ary ny zanany hahazo fialofana.",
			"27": "<sup>27</sup> Ny fahatahorana an’i Jehovah no loharanon’aina, Hanalavirana ny fandriky ny fahafatesana.",
			"28": "<sup>28</sup> Ny fahabetsahan’ny vahoaka no voninahitry ny mpanjaka; Fa ny havitsiany kosa no fietreny.",
			"29": "<sup>29</sup> Be saina izay mahatsindry fo; Fa ny malaky tezitra mampiseho ny fahadalany.",
			"30": "<sup>30</sup> Ny fo mionona no fiainan’ny nofo; Fa ny fahamaimaizan-tsaina dia mahalo ny taolana.",
			"31": "<sup>31</sup> Izay mampahory ny mahantra dia manala baraka ny Mpanao azy; Fa ny mamindra fo aminy kosa dia manome voninahitra Azy.",
			"32": "<sup>32</sup> Ny ratsy fanahy azera noho ny haratsiany; Fa ny marina matoky, na dia mby ao amin’ny fahafatesana aza.",
			"33": "<sup>33</sup> Mandry tsara ao am-pon’ny manan-tsaina ny fahendrena; Fa ny an’ny adala kosa dia miharihary.",
			"34": "<sup>34</sup> Ny fahamarinana manandratra ny firenena; Fa ny ota kosa dia fahafaham-baraka amin’ny olona.",
			"35": "<sup>35</sup> Ny fankasitrahan’ny mpanjaka dia amin’ny mpanompo hendry; Fa ny fahatezerany kosa amin’ny manao izay mahamenatra."
		},
		"15": {
			"1": "<sup>1</sup> Ny famaliana mora dia mampianina ny fahatezerana mafy; Fa ny teny maharary mahatonga fahasosorana.",
			"2": "<sup>2</sup> Ny lelan’ny hendry mampiseho fahalalana tsara; Fa ny vavan’ny adala miboiboika fahadalana.",
			"3": "<sup>3</sup> Ny mason’i Jehovah amin’izao rehetra izao, Mijery ny ratsy fanahy sy ny tsara fanahy.",
			"4": "<sup>4</sup> Ny teny malemy dia hazon’aina; Fa ny vava ratsy mandratra ny fanahy.",
			"5": "<sup>5</sup> Ny adala maniratsira ny famaizan-drainy; Fa izay mitandrina ny fananarana no hita ho hendry.",
			"6": "<sup>6</sup> Ao an-tranon’ny marina misy harena be; Fa ny fitomboan’ny haren’ny ratsy fanahy dia misy loza.",
			"7": "<sup>7</sup> Ny molotry ny hendry mampiely fahendrena; Fa tsy mba toy izany ny fon’ny adala.",
			"8": "<sup>8</sup> Ny fanatitry ny ratsy fanahy dia fahavetavetana eo imason’i Jehovah; Fa ny fivavaky ny marina no sitrany.",
			"9": "<sup>9</sup> Fahavetavetana eo imason’i Jehovah ny làlan’ny ratsy fanahy; Fa izay fatra-panaraka ny fahamarinana no tiany.",
			"10": "<sup>10</sup> Famaizana mafy no hihatra amin’izay mivily làlana. Eny, izay mankahala anatra dia ho faty.",
			"11": "<sup>11</sup> Ny fiainan-tsi-hita sy ny fandringanana aza dia miharihary eo anatrehan’i Jehovah, Koa mainka ny fon’ny zanak’olombelona!",
			"12": "<sup>12</sup> Tsy tian’ny mpaniratsira izay mananatra azy, Ary tsy mety manatona ny hendry izy.",
			"13": "<sup>13</sup> Ny fo ravoravo mahamiramirana ny tarehy; Fa ny alahelon’ny fo mahareraka ny fanahy.",
			"14": "<sup>14</sup> Ny fon’ny manan-tsaina mitady fahendrena; Fa ny vavan’ny adala mankamamy ny fahadalana.",
			"15": "<sup>15</sup> Ratsy avokoa ny andron’ny ory; Fa ny faharavoravoam-po dia fanasana mandrakariva.",
			"16": "<sup>16</sup> Aleo ny kely misy ny fahatahorana an’i Jehovah Toy izay harena be misy ahiahy.",
			"17": "<sup>17</sup> Aleo sakafo anana ampian-tsetsetra Toy izay omby mifahy asian-dromoromo.",
			"18": "<sup>18</sup> Izay malaky tezitra manetsika ady; Fa izay mahatsindry fo mampitsahatra fifandirana.",
			"19": "<sup>19</sup> Ny làlan’ny malaina dia toy ny fefy tsilo Fa ny làlamben’ny marina dia voalamina tsara.",
			"20": "<sup>20</sup> Ny zanaka hendry mahafaly ny rainy; Fa ny adala manao tsinontsinona an-dreniny.",
			"21": "<sup>21</sup> Ny fahadalana dia fifalian’ny tsy ampy saina; Fa ny manan-tsaina kosa mizotra mahitsy",
			"22": "<sup>22</sup> Ho foana ny fikasana, raha tsy misy mpisaina; Fa raha maro ny mpanolo-tsaina, dia tanteraka izany.",
			"23": "<sup>23</sup> Faly ny olona, raha mahavaly tsara ny vavany, Ary endrey ny hatsaran’ny teny atao amin’izay andro mahamety azy!",
			"24": "<sup>24</sup> Làlan’aina ho any ambony no alehan’ny hendry, Hanalavirany ny fiainan-tsi-hita any ambany,",
			"25": "<sup>25</sup> Ravan’i Jehovah ny tranon’ny mpiavonavona; Fa ny fari-tanin’ny mpitondra-tena aoriny mafy.",
			"26": "<sup>26</sup> Fahavetavetana eo imason’i Jehovah ny hevi-dratsy; Fa madio ny teny mahafinaritra.",
			"27": "<sup>27</sup> Izay fatra-pila harena dia mampidi-doza amin’ny ankohonany; Fa izay mankahala kolikoly no ho velona.",
			"28": "<sup>28</sup> Ny fon’ny marina mihevitra vao mamaly; Fa ny vavan’ny ratsy fanahy kosa miboiboika faharatsiana.",
			"29": "<sup>29</sup> Jehovah dia lavitra ny ratsy fanahy; Fa ny fivavaky ny marina no henoiny.",
			"30": "<sup>30</sup> Ny tarehy miramirana mahafaly ny fo; Ary ny filazan-tsoa mamelombelona ny taolana.",
			"31": "<sup>31</sup> Ny sofina izay mihaino anatra mahavelona Dia miara-mitoetra amin’ny hendry.",
			"32": "<sup>32</sup> Izay mandà anatra dia manary tena; Fa izay mihaino ny fananarana no mahazo fahalalana.",
			"33": "<sup>33</sup> Ny fahatahorana an’i Jehovah dia fananarana mampahahendry; Ary ny fanetren-tena mialoha ny fisandratana."
		},
		"16": {
			"1": "<sup>1</sup> Ny fikasan’ny fo no amin’ny olona; Fa ny famalian’ny lela dia avy amin’i Jehovah.",
			"2": "<sup>2</sup> Ataon’ny olona ho madio avokoa ny làlana rehetra alehany; Fa Jehovah ihany no mpandanja ny fanahy.",
			"3": "<sup>3</sup> Ankino amin’i Jehvoah ny asanao, Dia ho lavorary izay kasainao.",
			"4": "<sup>4</sup> Ny zavatra rehetra nataon’i Jehovah dia samy misy antony avokoa; Eny, ny ratsy fanahy koa aza dia nataony ho amin’ny andro hahitan-doza.",
			"5": "<sup>5</sup> Fahavetavetana eo imason’i Jehovah izay rehetra miavonavona am-po, Azo itompoana tokoa fa tsy maintsy hampijalina izy.",
			"6": "<sup>6</sup> Ny famindrampo sy ny fahamarinana no avotra amin’ny heloka; Ary ny fahatahorana an’i Jehovah no analavirana ny ratsy.",
			"7": "<sup>7</sup> Raha sitrak’i Jehovah ny alehan’ny olona, Na dia ny fahavalony aza dia hampihavaniny aminy.",
			"8": "<sup>8</sup> Aleo ny kely omban’ny fahamarinana Toy izay ny harem-bevava azo amin’ny tsy rariny.",
			"9": "<sup>9</sup> Ny fon’ny olona mihevitra tsara ny halehany; Fa Jehovah ihany no mahalavorary ny diany.",
			"10": "<sup>10</sup> Ny teny marina dia eo amin’ny molotry ny mpanjaka, Ary tsy diso teny izy, raha mitsara.",
			"11": "<sup>11</sup> An’i Jehovah ny fandanjana sy ny mizana marina, Avy aminy avokoa ireo vato rehetra an-kitapo.",
			"12": "<sup>12</sup> Fahavetavetana eo imason’ny mpanjaka ny fanaovan-dratsy; Fa fahamarinana no iorenan’ny seza fiandrianana.",
			"13": "<sup>13</sup> Sitraky ny mpanjaka ny molotra marina, Ary izay miteny mahitsy no tiany.",
			"14": "<sup>14</sup> Ny fahatezeran’ny mpanjaka dia iraky ny fahafatesana. Fa ny olon-kendry mampionona izany.",
			"15": "<sup>15</sup> Amin’ny fahamiramiranan’ny tarehin’ny mpanjaka dia misy fiainana, Ary ny fankasitrahany dia toy ny rahona amin’ny fararano.",
			"16": "<sup>16</sup> Tsara ny mahazo fahendrena noho ny volamena, Ary tsara ny mahazo fahalalana noho ny volafotsy.",
			"17": "<sup>17</sup> Ny manalavitra ny ratsy no làlamben’ny marina, Ary izay mitandrina ny alehany no miaro ny ainy.",
			"18": "<sup>18</sup> Ny fiavonavonana mialoha ny fahasimbana, Ary ny fanahy mirehareha mialoha ny fahalavoana.",
			"19": "<sup>19</sup> Tsara ny malemy fanahy ao amin’izay manetry tena Noho ny mizara babo amin’izay miavonavona.",
			"20": "<sup>20</sup> Izay mandinika ny teny no hahita soa; Ary izay matoky an’i Jehovah no ho sambatra.",
			"21": "<sup>21</sup> Izay hendry am-po dia lazaina hoe manan-tsaina, Ary ny hamamian’ny molotra mampandroso fahendrena.",
			"22": "<sup>22</sup> Loharanon’aina ny fahazavan-tsaina amin’izay manana izany; Fa ny famaizana ataon’ny adala dia fahadalana ihany.",
			"23": "<sup>23</sup> Ny fon’ny hendry mahahendry ny vavany Sady manampy fahendrena eo amin’ny molony.",
			"24": "<sup>24</sup> Toho-tantely ny teny mahafinaritra, Ka sady mamin’ny fanahy no mahatsara ny taolana.",
			"25": "<sup>25</sup> Misy làlana ataon’ny olona ho mahitsy, Kanjo làlana mivarina any amin’ny fahafatesana no iafarany.",
			"26": "<sup>26</sup> Ny fahanoanan’ny mpiasa dia mampiasa azy, Fa ny tendany no manesika azy.",
			"27": "<sup>27</sup> Ny ratsy fanahy mihady lavaka fanimbana, Sady misy toy ny afo mahamay eo amin’ny molony.",
			"28": "<sup>28</sup> Ny mpivadibadika mamafy ady; Ary ny mpibitsibitsika mampisaraka ny tena mpisakaiza.",
			"29": "<sup>29</sup> Ny lozabe mitaona ny namany Ka mitarika azy amin’ny làlana tsy tsara.",
			"30": "<sup>30</sup> Izay manakimpy ny masony hieritreritra ny ratsy Sady manaikitraiki-molotra dia efa mikasa hanao ratsy.",
			"31": "<sup>31</sup> Satroboninahitra tsara tarehy ny volo fotsy, Ao amin’ny làlan’ny fahamarinana no ahazoana azy.",
			"32": "<sup>32</sup> Tsara ny mahatsindry fo noho ny mahery, Ary izay mahazaka ny fanahiny noho izay mahafa-bohitra.",
			"33": "<sup>33</sup> Ny filokana dia atsipy ao am-pofoany; Fa avy amin’i Jehovah ihany no isehoan’ny fitsarany."
		},
		"17": {
			"1": "<sup>1</sup> Tsara ny sombi-mofo maina misy fiadanana Noho ny trano feno hena misy fifandirana.",
			"2": "<sup>2</sup> Ny mpanompo hendry dia hanapaka ny zanaka manao izay mahamenatra Sady hizara lova amin’ny mpirahalahy.",
			"3": "<sup>3</sup> Ny memy ho an’ny volafotsy, ary ny fatana fandrendrehana ho an’ny volamena; Fa Jehovah ihany no Mpamantatra ny fo.",
			"4": "<sup>4</sup> Ny mpanao ratsy mampandry sofina amin’ny molotra mandainga; Ary ny mpandainga mihaino ny lela mampidi-doza.",
			"5": "<sup>5</sup> Izay mihomehy ny malahelo manala baraka ny Mpanao azy; Ary izay faly amin’ny fahorian’ny sasany dia tsy maintsy hampijalina.",
			"6": "<sup>6</sup> Satroboninahitry ny antitra ny zafiny; Ary voninahitry ny zanaka ny rainy.",
			"7": "<sup>7</sup> Ny teny jadona tsy tandrifiny halahatry ny adala, Koa mainka fa tsy tokony ho an’ny zanak’andriana ny molotra mandainga.",
			"8": "<sup>8</sup> Ny kolikoly dia toy ny vato soa eo imason’izay mahazo azy, Ka na aiza na aiza alehany dia ambinina ihany.",
			"9": "<sup>9</sup> Izay te-ho be fitiavana dia manaron-keloka; Fa izay mamohafoha teny mampisaraka ny tena mpisakaiza.",
			"10": "<sup>10</sup> Ny anatra dia manan-kery kokoa amin’ny hendry Noho ny kapoka injato amin’ny adala.",
			"11": "<sup>11</sup> Ny maditra dia fatra-pitady ny ratsy, Ka dia iraka lozabe no irahina hamely azy.",
			"12": "<sup>12</sup> Aleo mifanena amin’ny bera very anaka Toy izay amin’ny adala feno hadalana.",
			"13": "<sup>13</sup> Izay mamaly ratsy ny soa, Ny ratsy tsy hiala ao an-tranony.",
			"14": "<sup>14</sup> Ny fiandohan’ny ady dia toy ny manata-drano, Koa ilaozy ny fifandirana, dieny tsy mbola mifandramatra.",
			"15": "<sup>15</sup> Izay manamarina ny meloka, na izay manameloka ny marina, Dia samy fahavetavetana eo imason’i Jehovah.",
			"16": "<sup>16</sup> Hatao inona re ity vola an-tànan’ny adala? Hamidy fahendrena va?– tsy manan-tsaina izy.",
			"17": "<sup>17</sup> Izay tena sakaiza tokoa dia tia amin’ny andro rehetra, Sady miseho ho rahalahy hamonjy amin’ny fahoriana.",
			"18": "<sup>18</sup> Olona tsy ampy saina izay mifandray tànana Hiantoka eo anatrehan’ny sakaizany.",
			"19": "<sup>19</sup> Izay tia ady dia tia heloka; Ary izay manao vavahady avo mitady fahasimbana.",
			"20": "<sup>20</sup> Izay madì-po tsy hahita soa; Ary izay manana lela mandainga hivarina any amin’ny loza.",
			"21": "<sup>21</sup> Izay miteraka adala hahazo alahelo; Ary ny rain’ ny adala tsy ho faly.",
			"22": "<sup>22</sup> Ny fo ravoravo mahasalama tsara; Fa ny fanahy kivy mahamaina ny taolana.",
			"23": "<sup>23</sup> Ny ratsy fanahy mandray kolikoly avy any anaty lamba Ho entiny mamadika ny rariny.",
			"24": "<sup>24</sup> Eo anoloan’ny manan-tsaina ny fahendrena; Fa ny mason’ny adala dia mibirioka any amin’ny faran’ny tany.",
			"25": "<sup>25</sup> Ny zanaka adala mampalahelo ny rainy Sady fahoriana mangidy amin’ny reniny niteraka azy.",
			"26": "<sup>26</sup> Tsy mety raha mampijaly ny marina, Na mamely ny manan-kaja noho ny fahamarinany.",
			"27": "<sup>27</sup> Izay mahafihim-bava no manam-pahalalana; Ary izay mahatsindry fo no manan-tsaina.",
			"28": "<sup>28</sup> Na ny adala aza, raha tsy miteny, dia atao ho hendry, Ary izay mahafihim-bava dia toa manan-tsaina."
		},
		"18": {
			"1": "<sup>1</sup> Izay mitokana ho azy dia mitady ny mahafinaritra ny tenany ihany, Misafoaka manohitra izay hevitra mahasoa rehetra izy.",
			"2": "<sup>2</sup> Ny adala tsy mba finaritra amin’ny fahalalana, Fa ta-haneho izay ao am-pony.",
			"3": "<sup>3</sup> Raha avy ny ratsy fanahy, dia avy ny fanamavoana; Ary miaraka amin’ny fahafaham-baraka ny henatra.",
			"4": "<sup>4</sup> Rano lalina ny teny aloaky ny vavan’ny olona, Ary rano miboiboika ny loharanon’ny fahendrena,",
			"5": "<sup>5</sup> Tsy mety raha manohana ny ratsy fanahy Handresy ny marina amin’ny fitsarana.",
			"6": "<sup>6</sup> Ny molotry ny adala manatona ny fifandirana, Ary ny vavany mihantsy kapoka.",
			"7": "<sup>7</sup> Ny vavan’ny adala no fandringanana azy, Ary ny molony ihany no fandriky ny fanahiny.",
			"8": "<sup>8</sup> Ny tenin’ny mpifosafosa dia toy ny hanim-py Ka mikorotsaka ao anaty kibo indrindra.",
			"9": "<sup>9</sup> Ary izay malaina amin’ny asany Dia rahalahin’ny mpanimba.",
			"10": "<sup>10</sup> Ny anaran’i Jehovah dia tilikambo mafy; Ny marina miezaka ho ao aminy ka voavonjy.",
			"11": "<sup>11</sup> Ny haren’ny manan-karena no vohiny mafy, Ary raha araka ny fiheviny, dia toa mànda avo izany.",
			"12": "<sup>12</sup> Ny fireharehan’ny fon’ny olona mialoha ny fahasimbana; Fa ny fanetren-tena kosa mialoha ny voninahitra.",
			"13": "<sup>13</sup> Izay mamaly teny mbola tsy re, Dia hadalana sy henatra ho azy izany.",
			"14": "<sup>14</sup> Ny faharariany dia zakan’ny fanahin’ny olona ihany; Fa ny fanahy kivy, iza no mahazaka izany?",
			"15": "<sup>15</sup> Ny fon’ny manan-tsaina mahazo fahalalana; Ary ny sofin’ny hendry mitady izany.",
			"16": "<sup>16</sup> Ny fanomezan’ny olona dia mahalalaka ny alehany, Hahatonga azy eo anatrehan’ ny lehibe.",
			"17": "<sup>17</sup> Izay mandahatra aloha no toa marina; Fa nony avy kosa ny an-daniny, dia mamaly ny teniny.",
			"18": "<sup>18</sup> Ny filokana mampitsahatra ady Ary mampisaraka ny samy mahery.",
			"19": "<sup>19</sup> Ny mampionona ny rahalahy voaisy ratsy dia sarotra noho ny manafaka vohitra mafy, Ary ny fifandirana aminy dia tahaka ny hidin’ny lapan’ny mpanjaka.",
			"20": "<sup>20</sup> Ny vokatry ny vavan’ny olona no mahavoky soa azy; Eny, ny vokatry ny molony no mahavoky azy.",
			"21": "<sup>21</sup> Ny mahafaty sy ny mahavelona samy hain’ny lela, Koa izay manarana azy dia hihinana ny vokany.",
			"22": "<sup>22</sup> Izay lehilahy mahazo vady dia mahazo zava-tsoa Sady mahita sitraka amin’i Jehovah.",
			"23": "<sup>23</sup> Fitarainana no ataon’ny malahelo; Nefa ny manan-karena kosa dia mamaly mahery.",
			"24": "<sup>24</sup> Izay maro sakaiza tsy maintsy tra-pahoriana; Fa misy sakaiza tia, izay mifikitra noho ny rahalahy aza."
		},
		"19": {
			"1": "<sup>1</sup> Tsara ny malahelo mandeha tsy misy tsiny Noho izay mandainga amin’ny molony sady adala.",
			"2": "<sup>2</sup> Tsy tsara raha tsy manam-pahalalana ny fanahy; Ary izay miezaka mosalahy dia ho diso làlana.",
			"3": "<sup>3</sup> Ny fahadalan’ny olona no mahasimba ny alehany, Ka amin’i Jehovah no sosotra ny fony.",
			"4": "<sup>4</sup> Ny harena mahamaro sakaiza; Fa ny malahelo afoin’ny sakaizany.",
			"5": "<sup>5</sup> Ny vavolombelona mandainga tsy ho afa-tsiny, Ary izay mamoaka lainga tsy ho afa-miala.",
			"6": "<sup>6</sup> Maro no mandambolambo andriana; Ary ny olona rehetra samy misakaiza amin’izay mpanome zavatra.",
			"7": "<sup>7</sup> Ny malahelo dia halan’ny rahalahiny rehetra, Koa mainka fa ny sakaizany tsy hanalavitra azy va? Manandrandra amin’ny teny tsy misy izy.",
			"8": "<sup>8</sup> Izay mihary fahendrena no tia ny tenany, Ary izay mitandrina fahalalana hahita soa.",
			"9": "<sup>9</sup> Ny vavolombelona mandainga tsy maintsy hampijalina, Ary ny miteny lainga ho very.",
			"10": "<sup>10</sup> Ny ho velona amin’ny fahafinaretana dia tsy tandrifiny ho an’ny adala, Koa mainka izay hanapahan’ny mpanompo ny zanak’andriana.",
			"11": "<sup>11</sup> Ny fahendren’ny olona manindry ny fahatezerany, Ary voninahitra ho azy ny tsy mamaly fahadisoana.",
			"12": "<sup>12</sup> Ny fahatezeran’ny mpanjaka dia toy ny fieron’ny liona tanora: Fa ny fitiany kosa toy ny ando amin’ny ahitra.",
			"13": "<sup>13</sup> Ny zanaka adala mahabe fahoriana ny rainy; Ary ny ady ataon’ ny vehivavy dia toy ny rano mitete mandrakariva.",
			"14": "<sup>14</sup> Trano sy harena no lova avy amin’ny ray; Fa ny vady hendry kosa dia avy amin’i Jehovah.",
			"15": "<sup>15</sup> Ny hakamoana mahasondrian-tory, Ary ho mosarena ny olona malaina.",
			"16": "<sup>16</sup> Izay mitandrina ny didy mitandrina ny ainy; Fa izay tsy mihevitra ny alehany dia ho faty.",
			"17": "<sup>17</sup> Izay miantra ny malahelo dia mampisambotra an’i Jehovah, Ary izay nomeny dia honerany kosa.",
			"18": "<sup>18</sup> Faizo ny zanakao, fa mbola misy azo antenaina ihany, Nefa aza dia entim-po loatra, fandrao mahafaty azy.",
			"19": "<sup>19</sup> Ny lozabe tsy maintsy hampijalina; Ary na dia vonjenao aza izy, dia mbola hanahirana anao ihany indray.",
			"20": "<sup>20</sup> Henoy ny saina atolotra, ary raiso ny fananarana, Mba ho hendry hianao amin’ izay hiafaranao.",
			"21": "<sup>21</sup> Maro ny hevitra ao am-pon’ny olona; Fa ny fisainan’i Jehovah no haharitra.",
			"22": "<sup>22</sup> Fifaliana amin’ny olona ny manisy soa; Ary aleo ho malahelo toy izay ho mpandainga.",
			"23": "<sup>23</sup> Ny fahatahorana an’i Jehovah no ahazoana fiainana; Izay manana izany tsy mba handry fotsy sady tsy hiharan-doza.",
			"24": "<sup>24</sup> Ny malaina dia mangarona ny eo an-dovia amin’ny tànany, Nefa ny mampakatra azy ho amin’ny vavany dia tsy zakany.",
			"25": "<sup>25</sup> Kapohy ny mpaniratsira, dia ho hendry ny kely saina; Ary anaro ny manan-tsaina, dia hahalala fahendrena izy.",
			"26": "<sup>26</sup> Izay mitohatoha amin-drainy sy mandroaka ny reniny Dia zanaka mahamenatra sy mahafa-baraka.",
			"27": "<sup>27</sup> Anaka, mitsahara tsy hihaino anatra Izay mampanalavitra anao ny tenin’ny fahalalana.",
			"28": "<sup>28</sup> Ny vavolombelona ratsy maniratsira ny fitsarana; Ary ny vavan’ny ratsy fanahy mitelina ny ratsy.",
			"29": "<sup>29</sup> Vonona ho amin’ny mpaniratsira ny fanamelohana, Ary ny kapoka ho amin’ny lamosin’ny adala."
		},
		"20": {
			"1": "<sup>1</sup> Mpaniratsira ny divay, ary mpitabataba ny toaka, Koa izay miraikiraiky azon’izany dia tsy hendry.",
			"2": "<sup>2</sup> Ny fampitahoran’ny mpanjaka dia toy ny fieron’ny liona tanora; Izay mampahatezitra azy dia mankahà-doza hihatra amin’ny ainy.",
			"3": "<sup>3</sup> Voninahitry ny olona ny hanalavitra ny ady; Fa ny adala rehetra kosa misafoaka.",
			"4": "<sup>4</sup> Ny malaina tsy mety miasa amin’ny ririnina, Ka dia hangataka izy raha fararano, fa tsy hahazo.",
			"5": "<sup>5</sup> Rano lalina ny hevitra ao am-pon’ny olona; Fa izay manan-tsaina dia mahay manovo azy ihany,",
			"6": "<sup>6</sup> Maro ny olona samy midera ny soa ataon’ny tenany avy; Fa izay olo-mahatoky, iza moa no mahita azy?",
			"7": "<sup>7</sup> Izay olona marina mandeha amin’ny fahitsiany– Sambatra ny zanany mandimby azy.",
			"8": "<sup>8</sup> Mipetraka eo amin’ny seza fitsarana ny mpanjaka, Ka mikororoka manaisotra ny ratsy rehetra ny masony–.",
			"9": "<sup>9</sup> Iza no mahazo manao hoe: Efa nahadio ny foko aho Ka madio tsy manana ota?",
			"10": "<sup>10</sup> Ny vato mizana samy hafa sy ny vata famarana samy hafa Dia samy fahavetavetana eo imason’i Jehovah.",
			"11": "<sup>11</sup> Na dia ny zaza aza dia mampiseho amin’ny fanaony Na madio sy mahitsy ny fitondrantenany.",
			"12": "<sup>12</sup> Ny sofina mandre sy ny maso mahita Dia samy nataon’i Jehovah avokoa.",
			"13": "<sup>13</sup> Aza tia torimaso hianao, fandrao tonga malahelo; Ahirato ny masonao, dia ho voky hanina hianao.",
			"14": "<sup>14</sup> Ratsy! ratsy! hoy ny mpividy; Kanjo nony lasa izy, dia mandoka tena.",
			"15": "<sup>15</sup> Ao ihany ny volamena sy ny voahangy betsaka; Fa ny molotra misy fahalalana no ravaka soa.",
			"16": "<sup>16</sup> Alao ny lambany, fa efa niantoka olon-kafa izy, Ary tano ho solon’ny vahiny niantohany.",
			"17": "<sup>17</sup> Mamin’ny olona ny hanina azo amin’ny fitaka; Kanjo ho feno fasika ny vavany any am-parany.",
			"18": "<sup>18</sup> Ny hevitra rehetra dia aorin’ny fisainana, Ary ny fahaizana mandamina tsara no entonao miady.",
			"19": "<sup>19</sup> Izay mandehandeha manaratsy dia manambara ny hevitra afenina, Koa aza miraharaha amin’izay mibedibedy foana.",
			"20": "<sup>20</sup> Izay manozona ny rainy na ny reniny, Dia ho faty ao amin’ny alim-pito ny jirony.",
			"21": "<sup>21</sup> Ny harena malaky azo foana eo am-boalohany Dia tsy hotahina any am-parany.",
			"22": "<sup>22</sup> Aza manao hoe: Hamaly ny ratsy nataony aho; Fa andraso Jehovah, fa Izy no hamonjy anao.",
			"23": "<sup>23</sup> Fahavetavetana eo imason’i Jehovah ny vato samy hafa, Ary ny mizana mandainga dia tsy tsara.",
			"24": "<sup>24</sup> Avy amin’i Jehovah ny dian’ny olona, Ary hataon’izay olombelona ahoana no hahafantatra ny làlany?",
			"25": "<sup>25</sup> Fandrika amin’ny olona ny malaky manao hoe: Zavatra masina ity; Ka nony vita ny voady, vao mamototra.",
			"26": "<sup>26</sup> Ny mpanjaka hendry mikororoka manaisotra ny ratsy fanahy Sady mitarika ny kodia fivelezana eo amboniny.",
			"27": "<sup>27</sup> Jiron’i Jehovah ny fanahin’ny olona, Itsilovana ny efitrefitra rehetra ao am-po.",
			"28": "<sup>28</sup> Famindrampo sy fahamarinana no miaro ny mpanjaka, Ary ampitoeriny amin’ny famindrampo ny seza fiandrianany.",
			"29": "<sup>29</sup> Voninahitry ny zatovo ny fahatanjahany, Ary ravaky ny anti-panahy ny volo fotsy.",
			"30": "<sup>30</sup> Ny fampangirifiriana dia mahafaka ny faharatsiana, Ary ny kapoka mahadio ny efitrefitra ao an-kibo."
		},
		"21": {
			"1": "<sup>1</sup> Rano velona eo an-tànan’i Jehovah ny fon’ny mpanjaka, Ka tarihiny amin’izay tiany.",
			"2": "<sup>2</sup> Ataon’ny olona ho marina avokoa ny làlana rehetra alehany; Fa Jehovah ihany no Mpandanja ny fo.",
			"3": "<sup>3</sup> Ny hanao izay marina sy mahitsy No sitrak’i Jehovah mihoatra noho ny fanatitra alatsa-dra.",
			"4": "<sup>4</sup> Ny fiandranandran’ny maso sy ny fiavonavon’ny fo– Izay jiron’ ny ratsy fanahy–dia ota.",
			"5": "<sup>5</sup> Ny hevitry ny mazoto dia mampanan-karena; Fa ny maimaika rehetra dia halahelo ihany.",
			"6": "<sup>6</sup> Ny fahazoan-karena amin’ny lela mandainga Dia fofonaina mihelina ho an’izay mitady fahafatesana.",
			"7": "<sup>7</sup> Ny fitohatohan’ny ratsy fanahy hamongotra azy, Satria mandà tsy hanao izay marina izy.",
			"8": "<sup>8</sup> Miolikolika ny làlan’ny mavesa-keloka; Fa mahitsy ny ataon’ny madio.",
			"9": "<sup>9</sup> Aleo mitoetra eo an-joron’ny tampon-trano Toy izay hiray trano amin’ny vehivavy tia ady.",
			"10": "<sup>10</sup> Ny fanahin’ny ratsy fanahy maniry ny ratsy, Ka na dia ny havany aza tsy mahita fitia eo imasony.",
			"11": "<sup>11</sup> Raha ampijalina ny mpaniratsira, dia ho hendry ny kely saina; Ary raha toroan-kevitra ny hendry, dia hahazo fahalalana izy.",
			"12": "<sup>12</sup> Ny Iray Marina no mandinika ny tranon’ny ratsy fanahy Ka mamarina azy ho amin’ny loza.",
			"13": "<sup>13</sup> Izay manentsin-tadìny amin’ny fitarainan’ny malahelo, Dia mba hitaraina kosa izy, fa tsy hohenoina.",
			"14": "<sup>14</sup> Ny fanomezana mangingina mampionona ny fahatezerana, Ary ny kolikoly amin’ny takona mampitsahatra ny fahavinirana mafy.",
			"15": "<sup>15</sup> Fifalian’ny marina ny hanao araka ny rariny; Fa zava-mahatahotra ny mpanao ratsy kosa izany.",
			"16": "<sup>16</sup> Ny olona izay mivily miala amin’ny làlan’ny fahendrena Dia hitoetra ao amin’ny fivorian’ny fanahin’ny maty.",
			"17": "<sup>17</sup> Izay tia fanaranam-po dia olona halahelo, Eny, izay tia divay sy diloilo tsy mba hanan-karena.",
			"18": "<sup>18</sup> Ny ratsy fanahy dia ho avotry ny marina, Ary ny mpivadika ho solon’ny mahitsy.",
			"19": "<sup>19</sup> Aleo mitoetra any an-efitra Toy izay amin’ny vehivavy tia ady sady foizina.",
			"20": "<sup>20</sup> Harena mahafinaritra sy diloilo no ao amin’ny tranon’ny hendry; Fa ny adala mandany azy.",
			"21": "<sup>21</sup> Izay fatra-panaraka fahamarinana sy famindrampo Dia hahazo fiainana sy fahamarinana ary voninahitra.",
			"22": "<sup>22</sup> Ny hendry mananika ny vohitry ny mahery Ka mandrodana ny fiarovana mafy izay itokiany.",
			"23": "<sup>23</sup> Izay miambina ny vavany sy ny lelany Dia miaro ny fanahiny tsy ho azom-pahoriana.",
			"24": "<sup>24</sup> Izay mirehareha sy miavonavona no atao hoe mpaniratsira, Dia izay olona fatra-piavonavona.",
			"25": "<sup>25</sup> Ny fanirian’ny malaina hahafaty azy, Satria ny tànany tsy mety miasa tsinona;",
			"26": "<sup>26</sup> Maniry fatratra mandritra ny andro izy; Fa ny marina kosa manome ka tsy mba mahihitra.",
			"27": "<sup>27</sup> Ny fanatitry ny ratsy fanahy dia fahavetavetana, Ka indrindra raha amin’ny sain-dratsy no itondrany azy.",
			"28": "<sup>28</sup> Ho faty ny vavolombelona mandainga; Fa izay mihaino dia hiteny mandrakariva.",
			"29": "<sup>29</sup> Ny ratsy fanahy mampiseho ny fahasahisahiany amin’ny tarehiny; Fa ny marina kosa dia mihevitra izay halehany.",
			"30": "<sup>30</sup> Tsy misy fahendrena, na fahalalana, Na fisainana mahaleo an’i Jehovah.",
			"31": "<sup>31</sup> Voaomana ny soavaly ho amin’ny andro iadiana; Fa an’i Jehovah ihany ny fandresena."
		},
		"22": {
			"1": "<sup>1</sup> Aleo ho tsara laza toy izay ho be harena, Ary tsara ny fitia noho ny volafotsy sy ny volamena.",
			"2": "<sup>2</sup> Mihaona ny manan-karena sy ny malahelo; Fa Jehovah no Mpanao azy roa tonta.",
			"3": "<sup>3</sup> Ny mahira-tsaina mahatsinjo ny loza ka miery; Fa ny kely saina kosa mandroso ka voa.",
			"4": "<sup>4</sup> Ny valin’ny fanetren-tena sy ny fahatahorana an’i Jehovah Dia harena sy voninahitra ary fiainana.",
			"5": "<sup>5</sup> Tsilo sy fandrika no amin’ny alehan’ ny maditra; Fa izay te-hiaro ny ainy dia manalavitra izany.",
			"6": "<sup>6</sup> Zaro amin’izay làlana tokony halehany ny zaza, Ka na rehefa antitra aza izy, dia tsy hiala amin’izany.",
			"7": "<sup>7</sup> Ny manan-karena dia manapaka ny malahelo, Ary izay misambotra dia mpanompon’ny mampisambotra.",
			"8": "<sup>8</sup> Izay mamafy heloka dia hijinja fahoriana, Ary ho levona ny tsorakazon’ny fahatezerany.",
			"9": "<sup>9</sup> Izay manana maso miantra hotahina, Satria anomezany ho an’ny malahelo ny haniny.",
			"10": "<sup>10</sup> Roahy ny mpaniratsira, dia ho afaka ny fifandirana, Eny, hitsahatra ny ady sy ny fahafaham-baraka.",
			"11": "<sup>11</sup> Izay tia fahadiovam-po, dia milaza soa ny molony. Sady sakaizan’ny mpanjaka izy.",
			"12": "<sup>12</sup> Ny mason’i Jehovah miaro ny manam-pahalalana; Fa ny tenin’ny mpivadika dia foanany.",
			"13": "<sup>13</sup> Hoy ny malaina: Misy liona any ivelany; Ho fatin’ny sasany any an-dàlambe aho.",
			"14": "<sup>14</sup> Ny vavan’ny vehivavy jejo dia lavaka lalina; Izay voaozon’i Jehovah no ho latsaka ao.",
			"15": "<sup>15</sup> Miraikitra amin’ny fon’ny zaza ny hadalana; Fa ny tsorakazo famaizana no hampanalavitra izany aminy.",
			"16": "<sup>16</sup> Izay mampahory ny malahelo dia mampitombo ny hareny; Fa izay manome ho an’ny manana kosa dia mampihena ny hareny.",
			"17": "<sup>17</sup> ATONGILANO ny sofinao, ka henoy ny tenin’ny hendry, Ary ampitandremo ny fahalalako ny fonao;",
			"18": "<sup>18</sup> Fa mahafinaritra raha voatahirinao ao an-kibonao izany, Koa aoka samy ho eo amin’ny molotrao.",
			"19": "<sup>19</sup> Hianao dia hianao no nampahafantariko izany, Mba hitokianao amin’i Jehovah.",
			"20": "<sup>20</sup> Tsy efa nanoratra zava-tsoa ho anao va aho, Izay misy hevitra tsara sy fahalalana,",
			"21": "<sup>21</sup> Mba hampahafantariko ny fahamarinan’ny teny mahatoky hianao, Sy hahazoanao teny holazaina amin’izay naniraka anao?",
			"22": "<sup>22</sup> Aza manosihosy ny malahelo, saingy malahelo izy; Ary aza dia manifakifa ny ory eo am-bavahady;",
			"23": "<sup>23</sup> Fa Jehovah anie ka hisolo vava azy amin’ny adiny. Ary hanimba ny ain’izay manimba azy.",
			"24": "<sup>24</sup> Aza misakaiza amin’ny olona mora tezitra, Ary aza miara-dia amin’ny olona foizina,",
			"25": "<sup>25</sup> Fandrao hianao mianatra ny fanaony Ka mahazo fandrika ho an’ny ainao.",
			"26": "<sup>26</sup> Aza mety ho isan’ny mifandray tànana, Na izay mianto-trosa;",
			"27": "<sup>27</sup> Raha tsy manan-kaloa hianao, Nahoana no dia halainy ny lafika andrianao?",
			"28": "<sup>28</sup> Aza manakisaka ny fari-tany ela, Izay naorin’ny razanao.",
			"29": "<sup>29</sup> Mahita olona mailaka amin’ny raharahany va hianao? Eo anatrehan’ny mpanjaka no hitsanganany, Fa tsy hitsangana eo anatrehan’ny olona ambany izy."
		},
		"23": {
			"1": "<sup>1</sup> Raha mipetraka hiara-komana amin’ny mpanapaka hianao, Dia hevero tsara izay eo anatrehanao,",
			"2": "<sup>2</sup> Ka andraso antsy ny tendanao, Raha dia liana loatra hianao.",
			"3": "<sup>3</sup> Aza mitsiriritra ny hanim-piny, Fa hani-mamitaka izany.",
			"4": "<sup>4</sup> Aza manasa-tena hanan-karena, Aza entina amin’izany ny fahendrenao.",
			"5": "<sup>5</sup> Moa hampanarahinao azy va ny masonao, nefa tsy ao intsony izy? Fa maniry elatra tokoa izy Ka manidina toy ny voromahery ho eny amin’ny lanitra.",
			"6": "<sup>6</sup> Aza homana ny mofon’izay ratsy fijery, Ary aza mitsiriritra ny hanim-piny;",
			"7": "<sup>7</sup> Fa tahaka ny heviny ao am-pony ihany no toetrany: Hoy izy aminao: Mihinàna, misotroa; Kanjo tsy sitrany hianao.",
			"8": "<sup>8</sup> Izay sombiny kely voatelinao aza dia mbola haloanao, Ka ho very foana ny teny mahafinaritra nataonao.",
			"9": "<sup>9</sup> Aza miteny amin’ ny adala; Fa hohamavoiny foana ny fahendren’ny teninao.",
			"10": "<sup>10</sup> Aza manakisaka ny fari-tany ela, Ary aza manitatra mankamin’ ny sahan’ny kamboty;",
			"11": "<sup>11</sup> Fa mahery ny Mpanavotra azy, Izy no hisolo vava azy amin’ny adiny aminao.",
			"12": "<sup>12</sup> Ampiekeo famaizana ny fonao, Sy tenim-pahalalana ny sofinao.",
			"13": "<sup>13</sup> Aza tsy manafay zanaka, Fa tsy dia ho faty izy tsy akory na dia asianao ny hazo aza;",
			"14": "<sup>14</sup> Raha mamely azy amin’ny hazo hianao, Dia ho voavonjinao tsy ho any amin’ny fiainan-tsi-hita ny fanahiny.",
			"15": "<sup>15</sup> Anaka, raha hendry ny fonao, Dia ho faly koa ny foko, dia ny foko;",
			"16": "<sup>16</sup> Eny, ho ravoravo ny fanahiko, Raha miteny marina ny molotrao.",
			"17": "<sup>17</sup> Aoka tsy hialona ny mpanota ny fonao; Fa matahora an’i Jehovah mandritra ny andro;",
			"18": "<sup>18</sup> Fa raha mbola hisy koa ny farany, Dia tsy ho foana ny fanantenanao.",
			"19": "<sup>19</sup> Mihainoa hianao, anaka, ka hendre, Ary ampizory amin’ny làlana ny fonao.",
			"20": "<sup>20</sup> Aza misakaiza amin’izay mpiboboka divay Sy izay fatra-pitia hena;",
			"21": "<sup>21</sup> Fa halahelo izay mpiboboka sy izay fatra-pitia hena; Ary ny fitiavan-torimaso mampitafy voro-damba.",
			"22": "<sup>22</sup> Maneke ny rainao izay niteraka anao. Ary aza manamavo ny reninao, saingy efa antitra izy.",
			"23": "<sup>23</sup> Vidio ny marina, ka aza amidy, Dia ny fahendrena sy ny fananarana ary ny fahazavan-tsaina.",
			"24": "<sup>24</sup> Hifaly dia hifaly ny rain’ny marina, Ary izay miteraka zaza hendry hanam-pifaliana aminy.",
			"25": "<sup>25</sup> Aoka ho faly ny rainao sy ny reninao, Eny, aoka ho ravoravo ny reninao izay niteraka anao.",
			"26": "<sup>26</sup> Anaka, atolory ahy ny fonao, Ary aoka ny masonao ho faly mijery ny làlako;",
			"27": "<sup>27</sup> Fa ny vehivavy jejo dia hady lalina, Ary ny vahiny janga lavaka ety;",
			"28": "<sup>28</sup> Eny, manotrika toy ny jiolahy ireny Ary mahamaro ny olona mpivadika.",
			"29": "<sup>29</sup> An’iza ny Indrisy? An’iza ny Endrey? An’iza ny fifandirana? An’iza ny fitolokoana? An’iza ny ratra tsy ahoan-tsy ahoana? An’iza ny maso mivoara-mena?",
			"30": "<sup>30</sup> An’izay mikikitra amin’ny divay, Eny, an’izay mandeha hanandrana divay voaharoharo zavatra.",
			"31": "<sup>31</sup> Aza mijery ny divay noho ny hamenany Sy ny fanganohanony eo anaty gilasy Ary ny fikoriany mahafinaritra;",
			"32": "<sup>32</sup> Fa amin’ny farany dia hanaikitra toy ny bibilava izy Ary hanindrona toy ny menarana.",
			"33": "<sup>33</sup> Koa ny masonao dia hijery vehivavy jejo, Ary ny fonao hieritreri-dratsy.",
			"34": "<sup>34</sup> Eny, ho sahala amin’izay mandry ao amin’ny ranomasina hianao Sady ho tahaka izay mandry eo an-tendron’ny andrin-tsambo.",
			"35": "<sup>35</sup> Nokapohiny aho, hoy hianao, fa tsy reko naharary ahy, Nandalasiny aho, fa tsy tsaroako; Rahoviana ange aho no hifoha mba hitadiavako azy indray?"
		},
		"24": {
			"1": "<sup>1</sup> Aza mialona ny olona ratsy fanahy, Na maniry ho namany;",
			"2": "<sup>2</sup> Fa fandravana no saintsainin’ny fony, Ary fampahoriana no tenenin’ny molony.",
			"3": "<sup>3</sup> Fahendrena no anaovana trano; Ary fahalalinan-tsaina no mampaharitra azy;",
			"4": "<sup>4</sup> Sady fahalalana no hamenoana ny efi-trano Amin’izay harena soa sy mahafinaritra rehetra.",
			"5": "<sup>5</sup> Be hery ny lehilahy hendry, Eny, mitombo hery ny olona manam-pahalalana;",
			"6": "<sup>6</sup> Fa ny fahaizana mandamina tsara no entonao miady, Ary raha maro ny mpanolo-tsaina, dia misy famonjena.",
			"7": "<sup>7</sup> Avo tsy takatry ny adala ny fahendrena, Tsy mba mahaloa-bava eo am-bavahady izy.",
			"8": "<sup>8</sup> Izay misaintsaina hanao ratsy Dia atao hoe mpamoron-tsain-dratsy.",
			"9": "<sup>9</sup> Ota ny fikasan’ny hadalana; Ary fahavetavetana eo imason’ny olona ny mpaniratsira.",
			"10": "<sup>10</sup> Raha reraka amin’ny andro fahoriana hianao, Dia kely ny herinao.",
			"11": "<sup>11</sup> Vonjeo izay entina hovonoina, Ary enga anie ka mba harovanao izay mangozohozo efa ho mby eo am-pamonoana!",
			"12": "<sup>12</sup> Raha hoy hianao: Tsia, tsy mahalala izany izahay, Moa Izay mandanja ny fo tsy hihevitra izany va? Ary Izay mandinika ny fanahinao tsy hahalala izany va? Eny, hovaliany araka ny ataony avy ny olona rehetra.",
			"13": "<sup>13</sup> Anaka, homàna tantely, fa tsara izany, Ary ny toho-tantely, fa ho mamy eo am-bavanao;",
			"14": "<sup>14</sup> Ary aoka ho toy izany no hianaranao fahendrena ho an’ny fanahinao; Fa raha azonao izany, dia hisy hiafarana, Ka tsy ho foana ny fanantenanao.",
			"15": "<sup>15</sup> Aza manotrika ao akaikin’ny tranon’ny marina hianao, ry ilay ratsy, Aza ravanao ny fonenany;",
			"16": "<sup>16</sup> Fa potraka impito ny marina, nefa tafarina ihany; Ary ny ratsy fanahy kosa solafaka, raha azom-pahoriana.",
			"17": "<sup>17</sup> Aza faly, raha lavo ny fahavalonao, Ary aza ravoravo ny fonao, raha tafintohina izy;",
			"18": "<sup>18</sup> Fandrao hitan’i Jehovah izany ka ataony ho ratsy, Ary ampialainy aminy ny fahatezerany.",
			"19": "<sup>19</sup> Aoka tsy hirehitra ny fahatezeranao noho ny amin’ny mpanao ratsy, Ary aza mialona ny ratsy fanahy;",
			"20": "<sup>20</sup> Fa tsy hisy fanantenana ho an’ny ratsy; Eny, ho faty ny jiron’ny ratsy fanahy.",
			"21": "<sup>21</sup> Anaka, matahora an’i Jehovah sy ny mpanjaka; Ary aza mikambana amin’izay miovaova;",
			"22": "<sup>22</sup> Fa ho tampoka no hitrangan’izay hanjo ireny; Ary iza no mahalala izay loza hiseho amin’ny androny?",
			"23": "<sup>23</sup> Izao koa no tenin’ny hendry: Tsy mety raha mizaha tavan’olona amin’ny fitsarana.",
			"24": "<sup>24</sup> Izay manao amin’ny meloka hoe: Marina hianao, Dia hozonin’ny olona sady ho halan’ny firenena;",
			"25": "<sup>25</sup> Fa izay mitsara marina kosa no hahita soa Sy hahazo fitahiana tsara.",
			"26": "<sup>26</sup> Izay mahavoa tsara amin’ny famaliana Dia manoroka ny molotra.",
			"27": "<sup>27</sup> Amboary ny asanao any ivelany, Ary ataovy lavorary ny any an-tsaha; Ka rehefa vita izany, dia mahazo manao ny tranonao hianao.",
			"28": "<sup>28</sup> Aza mety ho vavolombelona foana hanameloka ny namanao; Moa hamitaka amin’ny molotrao va hianao?",
			"29": "<sup>29</sup> Aza manao hoe: Araka izay nataony tamiko no mba hataoko aminy kosa, Hovaliako ny olona araka izay nataony.",
			"30": "<sup>30</sup> Nandalo ny sahan’ny kamo Sy ny tanimboaloboky ny tsy ampy saina aho;",
			"31": "<sup>31</sup> Ary, indro, feno sampivato izy Sady voasaron’ny amiana, Ary nirodana ny ampiantany vatony.",
			"32": "<sup>32</sup> Dia nojereko ka noheveriko tsara, Eny, hitako ka nahazoako anatra.",
			"33": "<sup>33</sup> Mbola hatory kely ihany, mbola ho renoka kely ihany. Mbola hanohon-tànana kely hatory;",
			"34": "<sup>34</sup> Dia ho tonga toy ny mimaona hamely ny alahelonao, Ary ny fahantranao ho toy ny jiolahy."
		},
		"25": {
			"1": "<sup>1</sup> I ZAO koa no ohabolan’i Solomona, izay nangonin’ ny olon’i Hezekia, mpanjakan’ny Joda:",
			"2": "<sup>2</sup> Voninahitr’Andriamanitra ny manafin-javatra; Fa voninahitry ny mpanjaka kosa ny mamanta-javatra.",
			"3": "<sup>3</sup> Ny hahavon’ny lanitra sy ny halalin’ny tany Ary ny fon’ny mpanjaka dia samy tsy azo fantarina avokoa.",
			"4": "<sup>4</sup> Esory ny taim-bolafotsy, Dia hisy fanaka ho an’ny mpanao an-idina.",
			"5": "<sup>5</sup> Esory ny ratsy fanahy tsy ho eo anatrehan’ny mpanjaka, Dia hampitoerina amin’ny fahamarinana ny seza fiandrianany.",
			"6": "<sup>6</sup> Aza manandra-tena eo anatrehan’ny mpanjaka, Ary aza mitoetra eo amin’ny fitoeran’ny lehibe;",
			"7": "<sup>7</sup> Fa aleo hianao hantsoina hoe: Miakara atỳ, Toy izay hatambotsotra eo imason’ny manan-kaja izay hitan’ny masonao.",
			"8": "<sup>8</sup> Aza maika handeha hiady, Fandrao tsy ho fantatrao izay hataonao amin’ny farany, Rehefa omen’ny namanao henatra hianao.",
			"9": "<sup>9</sup> Miadia ihany ny adinao amin’ny namanao, Kanefa aza aseho izay tsy tian’ny sasany holazaina,",
			"10": "<sup>10</sup> Fandrao izay mandre hanondro maso anao, Ka tsy ho afaka aminao ny laza ratsy.",
			"11": "<sup>11</sup> Ny teny atao amin’ny antony Dia poma volamena ao anaty vilia volafotsy tsara soratra.",
			"12": "<sup>12</sup> Kavim-bolamena sy firavaka volamena tsara Ny mpananatra hendry, raha amin’ny sofina mihaino.",
			"13": "<sup>13</sup> Toy ny hatsiaky ny orampanala amin’ny taom-pijinjana, Dia toy izany ny iraka mahatoky amin’izay naniraka azy, Fa mamelombelona ny fon’ny tompony izy.",
			"14": "<sup>14</sup> Toy ny rahona sy ny rivotra tsy misy orana Ny olona izay mirehaka amin’ny fizaram-bava.",
			"15": "<sup>15</sup> Ny fahari-po no ahazoana mampanaiky ny mpitsara, Ary ny vava malefaka mahatapa-taolana.",
			"16": "<sup>16</sup> Mahita tantely va hianao? Hano izay antonona anao, Fandrao voky loatra hianao ka handoa azy.",
			"17": "<sup>17</sup> Aza miditra matetika ao an-tranon’ny namanao, Fandrao tofoka anao izy, ka ho halany hianao.",
			"18": "<sup>18</sup> Langilangy sy sabatra ary zana-tsipìka maranitra Ny olona izay mitsangana ho vavolombelona mandainga hanameloka ny namany.",
			"19": "<sup>19</sup> Toy ny nify nahasimatsimahana sy ny tongotra mangozohozo Ny fahatokiana ny mpamitaka amin’ny andro fahoriana.",
			"20": "<sup>20</sup> Toy ny miala lamba amin’ny hatsiaka, Ary toy ny vinaingitra amin’ny sirahazo, Dia toy izany ny mikalo amin’ny malahelo am-po.",
			"21": "<sup>21</sup> Raha noana ny fahavalonao, omeo hanina izy; Ary raha mangetaheta izy, omeo rano hosotroiny;",
			"22": "<sup>22</sup> Fa raha manao izany hianao, dia hanambatra vainafo ho eo an-dohany, Ary Jehovah no hamaly soa anao.",
			"23": "<sup>23</sup> Ny rivotra avy any avaratra mahatonga ranonorana, Dia toy izany, ny lelan’ny mpifosa mampahavinitra endrika.",
			"24": "<sup>24</sup> Aleo mitoetra eo an-joron’ny tampon-trano Toy izay miray trano amin’ny vehivavy tia ady.",
			"25": "<sup>25</sup> Rano mangatsiaka amin’ny fanahy reraka Ny filazana soa avy any an-tany lavitra.",
			"26": "<sup>26</sup> Loharano voahosihosy sy fantsakana simba Ny marina izay mikoy ny ratsy fanahy.",
			"27": "<sup>27</sup> Tsy tsara raha mahery homana tantely; Fa ny fitadiavan’ny olona ny voninahiny dia tsy voninahitra.",
			"28": "<sup>28</sup> Tanàna rava tsy misy mànda Ny lehilahy izay tsy mahatsindry ny fanahiny."
		},
		"26": {
			"1": "<sup>1</sup> Toy ny orampanala amin’ny lohataona, Ary toy ny ranonorana amin’ny taom-pijinjana, Dia toy izany ny voninahitra tsy tandrifiny ho an’ny adala.",
			"2": "<sup>2</sup> Toy ny vorona mivezivezy sy ny sidintsidina manidina, Dia toy izany ny fanozonam-poana: tsy mba hihatra izany.",
			"3": "<sup>3</sup> Ny karavasy ho amin’ny soavaly, ny lamboridy ho amin’ny boriky, Ary ny tsorakazo kosa ho amin’ny lamosin’ny adala.",
			"4": "<sup>4</sup> Aza mamaly ny adala araka ny hadalàny, Fandrao mba adala tahaka azy koa hianao.",
			"5": "<sup>5</sup> Valio ny adala araka ny hadalàny, Fandrao dia manao azy ho hendry izy.",
			"6": "<sup>6</sup> Manapaka ny tongony sady misotro loza Izay mampitondra teny ny adala.",
			"7": "<sup>7</sup> Mikepikepika ny tongotry ny malemy tongotra, Dia toy izany ny ohabolana eo am-bavan’ny adala.",
			"8": "<sup>8</sup> Toy ny manisy vato eo amin’ny antsamotady, Dia toy izany ny manome voninahitra ny adala.",
			"9": "<sup>9</sup> Toy ny tsilo eny an-tànan’ny olona mamo, Dia toy izany ny ohabolana eo am-bavan’ny adala.",
			"10": "<sup>10</sup> Ny mpiasa mahay dia mahavita ny zavatra isan-karazany; Fa izay manakarama ny adala dia toy ny manakarama izay sendra mandalo.",
			"11": "<sup>11</sup> Toy ny amboa miverina amin’ny loany, Dia toy izany ny adala miverina amin’ny hadalany.",
			"12": "<sup>12</sup> Hitanao va ny olona manao azy ho hendry? Ny adala misy hantenaina kokoa noho izy.",
			"13": "<sup>13</sup> Hoy ny malaina: Misy liona any an-dàlana, Misy liona atsy an-dàlambe.",
			"14": "<sup>14</sup> Toy ny varavarana mihodina amin’ny saviliny, Dia toy izany ny kamo eo amin’ny fandriany,",
			"15": "<sup>15</sup> Ny malaina mangarona ny eo an-doviany, Nefa ny hampakatra azy ho amin’ny vavany dia mahasasatra azy.",
			"16": "<sup>16</sup> Ny kamo manao ny tenany ho hendry Mihoatra noho ny olona fito izay mahavaly tsara.",
			"17": "<sup>17</sup> Izay mandalo ka mitsaraingona amin’ny ady tsy azy Dia mandroritra amboa amin’ny sofiny.",
			"18": "<sup>18</sup> Toy ny adala izay manely afo Sy zana-tsipìka ary fahafatesana,",
			"19": "<sup>19</sup> Dia toy izany ny olona mamitaka ny namany Ka manao hoe: Tsy vosobosotra ihany va no ataoko?",
			"20": "<sup>20</sup> Raha tsy misy kitay intsony, dia maty ny afo; Ary raha tsy misy mpifosafosa intsony, dia mitsahatra ny fifandirana.",
			"21": "<sup>21</sup> Toy ny arina atao amin’ny vainafo Sy ny kitay hazo atao amin’ny afo, Dia toy izany, ny olona tia ady mandrehitra fifandirana.",
			"22": "<sup>22</sup> Ny tenin’ny mpifosafosa dia toy ny hanim-py Ka mikorotsaka ao anaty kibo indrindra.",
			"23": "<sup>23</sup> Tavim-bilany misy ankosotra taim-bolafotsy Ny molotra mihatsaravelatsihy sy ny fo ratsy.",
			"24": "<sup>24</sup> Ny mpandrafy dia mody ho hafa amin’ny molony Sady manafim-pitaka ao am-pony;",
			"25": "<sup>25</sup> Koa na dia miteny soa aza izy, aza mino azy; Fa fahavetavetana fito no ao am-pony.",
			"26": "<sup>26</sup> Ny lolompo dia miafina ao amin’ny fitaka; Nefa ny haratsiany dia haharihary eo am-pahibemaso.",
			"27": "<sup>27</sup> Izay mihady lavaka no ho latsaka ao, Ary izay manakodiadia vato no hikodiavany kosa.",
			"28": "<sup>28</sup> Halan’ny lela mandainga izay efa nampahoriny, Ary ny vava mandrobo dia mampidi-doza."
		},
		"27": {
			"1": "<sup>1</sup> Aza mirehareha ny amin’ny ho ampitso hianao; Fa na dia ny havoaky ny anio aza tsy fantatrao.",
			"2": "<sup>2</sup> Aoka ny olon-kafa no hidera anao, fa tsy ny vavanao, Eny, ny vahiny, fa tsy ny molotrao.",
			"3": "<sup>3</sup> Mavesatra ny vato, ary mavesa-danja ny fasika; Fa ny fahasosoran’ny adala dia manoatra noho izy roa tonta.",
			"4": "<sup>4</sup> Masiaka ny fahavinirana, ary misafoaka ny fahatezerana; Fa iza no mahajanona eo anatrehan’ny fahasaro-piaro?",
			"5": "<sup>5</sup> Aleo anatra imaso Toy izay fitia afenina.",
			"6": "<sup>6</sup> Azo itokiana hahasoa ny fery ataon’ny sakaiza; Fa tena fitaka ny fanorohan’ny fahavalo.",
			"7": "<sup>7</sup> Hitsakitsahin’ny voky na dia ny toho-tantely aza; Fa mamin’ny noana na dia ny zava-mangidy rehetra aza.",
			"8": "<sup>8</sup> Toy ny voron-kely miala amin’ny akaniny ka mirenireny, Dia toy izany ny olona izay mirenireny miala amin’ny fonenany.",
			"9": "<sup>9</sup> Ny diloilo sy ny zava-manitra mahafaly ny fo, Ary ny hamamin’ny sakaiza dia avy amin’ny ana-panahy ataony.",
			"10": "<sup>10</sup> Aza mahafoy ny sakaizanao, na ny sakaizan-drainao, Ary aza mankany amin’ny tranon’ny rahalahinao amin’ny andro fahorianao; Fa tsara ny namana akaiky noho ny rahalahy lavitra.",
			"11": "<sup>11</sup> Anaka, hendre, ary ampifalio ny foko, Mba hamaliako izay miteny ratsy ahy.",
			"12": "<sup>12</sup> Ny mahira-tsaina mahatazana ny loza ka miery; Fa ny kely saina kosa mandroso ka voa.",
			"13": "<sup>13</sup> Alao ny lambany, fa efa niantoka olon-kafa izy, Ary tano ho solon’ny vahiny janga niantohany izy.",
			"14": "<sup>14</sup> Izay mifoha maraina koa ka misaotra ny sakaizany amin’ny feo mahery, Dia isaina ho fanozonana izany.",
			"15": "<sup>15</sup> Tahaka ny fijononoky ny ranonorana be Ny vehivavy tia ady.",
			"16": "<sup>16</sup> Izay misakana azy dia toy ny misakana ny rivotra, Sady toy ny mamihina diloilo ny tànany ankavanana.",
			"17": "<sup>17</sup> Ny vy maharanitra ny vy, Toy izany, ny olona maharanitra ny tarehin’ny sakaizany.",
			"18": "<sup>18</sup> Izay miaro aviavy dia hihinana ny voany; Ary izay miambin-tompo dia homem-boninahitra.",
			"19": "<sup>19</sup> Toy ny tandindona eny anaty rano mifanahaka amin’ny tarehy, Dia toy izany no ifanahafan’ny fon’ny olona amin’ny an’olona.",
			"20": "<sup>20</sup> Ny fiainan-tsi-hita sy ny fandringanana tsy mety voky, Dia toy izany no tsy ahamamoan’ny mason’ny olona.",
			"21": "<sup>21</sup> Ny memy ho an’ny volafotsy, Ny fandrendrehana ho an’ny volamena, Ary ny toetry ny olona dia tsy maintsy ho araka izay zavatra derainy.",
			"22": "<sup>22</sup> Na dia miara-totoinao ny fanoto amin’ny vary eo an-daona aza ny adala, Dia tsy mba hiala aminy tsy akory ny fahadalany.",
			"23": "<sup>23</sup> Fantaro tsara ny tarehin’ny ondry aman-osinao, Ary miahia tsara ny andian’ny bibinao;",
			"24": "<sup>24</sup> Fa ny harena tsy ho mandrakizay; Ary moa haharitra ho amin’ny taranaka rehetra va ny satroboninahitra?",
			"25": "<sup>25</sup> Na dia lany aza ny ahitra, ka mitsiry ny solofony, Ary voataona ny zava-maniry any an-tendrombohitra,",
			"26": "<sup>26</sup> Ny zanak’ondry dia hahazoanao fitafiana, Ary ny osy hamidinao tany;",
			"27": "<sup>27</sup> Ary ho ampy hosotroinao ny rononon’osy Sy ho an’ny ankohonanao, Ary hisy hiveloman’ny ankizivavinao."
		},
		"28": {
			"1": "<sup>1</sup> Ny ratsy fanahy mandositra, na dia tsy misy manenjika aza; Fa ny marina kosa dia matoky toy ny liona tanora.",
			"2": "<sup>2</sup> Ny fahotan’ ny tany mahamaro mpanapaka; Fa ny fisian’ny olona manan-tsaina kosa no mampahateza ny fanjakana.",
			"3": "<sup>3</sup> Ny malahelo izay mampahory ny mahantra Dia toy ny ranonorana izay manindao ka tsy maha-misy hanina.",
			"4": "<sup>4</sup> Izay mahafoy ny lalàna dia mandoka ny ratsy fanahy; Fa izay mitandrina ny lalàna dia mirehitra amin’ny fanoherana azy.",
			"5": "<sup>5</sup> Ny ratsy fanahy dia tsy mahalala rariny; Fa izay mitady an’i Jehovah no mahalala ny zavatra rehetra.",
			"6": "<sup>6</sup> Ny malahelo izay mandeha amin’ny fahitsiany Dia tsara noho ny manan-karena izay mivadika ka mandeha amin’ny làlana miolikolika.",
			"7": "<sup>7</sup> Izay mitandrina ny lalàna no zanaka hendry; Fa izay misakaiza amin’ny mpandany harena dia manome henatra ny rainy.",
			"8": "<sup>8</sup> Izay mampitombo ny fananany amin’ny zana-bola sy ny tombony Dia mamory ho an’izay miantra ny malahelo.",
			"9": "<sup>9</sup> Izay manentsin-tadìny tsy hihaino ny lalàna, Na ny fivavahany aza dia fahavetavetana.",
			"10": "<sup>10</sup> Izay mampivily ny mahitsy ho amin’ny làlan-dratsy Dia ho lavo amin’ny lavaka nataony; Fa ny tsy misy tsiny no handova ny soa.",
			"11": "<sup>11</sup> Ny manan-karena manao azy ho hendry; Fa ny malahelo izay manan-tsaina dia hizaha toetra azy.",
			"12": "<sup>12</sup> Raha ravoravo ny marina, dia be ny famirapiratana; Fa raha misandratra kosa ny ratsy fanahy, dia miery ny vahoaka.",
			"13": "<sup>13</sup> Izay manafina ny fahotany tsy hambinina; Fa izay mitsotra ka mahafoy azy no hahazo famindrampo.",
			"14": "<sup>14</sup> Sambatra izay olona matahotra mandrakariva; Fa izay manamafy ny fony no hidiran-doza.",
			"15": "<sup>15</sup> Liona mierona sy bera miremby Ny mpanapaka ratsy fanahy amin’ny olona reraka.",
			"16": "<sup>16</sup> Ny mpanapaka tsy ampy saina dia mahery mampahory; Izay mankahala ny fieremana no ho ela velona.",
			"17": "<sup>17</sup> Izay itambesaran-keloka noho ny fandatsahan-dra dia handositra hatrany an-davaka; Aoka tsy hisy hihazona azy.",
			"18": "<sup>18</sup> Izay mandeha tsy misy tsiny no ho voavonjy; Fa izay mivadika ka mandeha amin’ny làlana miolikolika no ho lavo tampoka.",
			"19": "<sup>19</sup> Izay miasa ny taniny dia ho voky hanina; Fa izay manaraka ny olom-poana ho voky alahelo kosa.",
			"20": "<sup>20</sup> Izay olona mahatoky tokoa dia ho be fitahiana; Fa izay maimay ta-hanan-karena dia tsy maintsy hohelohina.",
			"21": "<sup>21</sup> Tsy mety raha mizaha tavan’olona; Kanefa na dia amin’ny sombin-kanina ihany aza dia sahin’ny olona hanotana.",
			"22": "<sup>22</sup> Ny mpialona dia maimay ta-hanan-karena; Tsy fantany fa hanjo azy ny alahelo.",
			"23": "<sup>23</sup> Izay mananatra olona no hahazo fitia be rahatrizay Noho izay mandrobo amin’ny lelany.",
			"24": "<sup>24</sup> Izay mandroba ray sy reny Ka manao hoe: Tsy heloka izany, Dia naman’ny mpanimba.",
			"25": "<sup>25</sup> Izay mierina manetsika ady; Fa izay matoky an’i Jehovah dia hohatavezina.",
			"26": "<sup>26</sup> Izay matoky ny fony dia adala; Fa izay mandeha amin’ny fahendrena no ho afa-miala.",
			"27": "<sup>27</sup> Izay manome ny malahelo tsy hanan-java-mahory; Fa izay mitampi-maso hahazo ozona be;",
			"28": "<sup>28</sup> Raha misandratra ny ratsy fanahy, dia miery ny vahoaka; Fa raha ringana kosa izy, dia mihamaro ny marina."
		},
		"29": {
			"1": "<sup>1</sup> Izay olona anarina matetika ka mihamafy hatoka Dia ho torotoro tampoka, ka tsy hisy fahasitranana.",
			"2": "<sup>2</sup> Raha mihamaro ny marina, dia faly ny vahoaka; Fa raha ny ratsy fanahy no manapaka, dia misento kosa izy.",
			"3": "<sup>3</sup> Izay tia fahendrena no mahafaly ny rainy; Fa izay misakaiza amin’ny vehivavy janga dia mandany harena.",
			"4": "<sup>4</sup> Ny rariny no ampandrian’ny mpanjaka ny tany; Fa izay mandray tambitamby no mandrava azy kosa.",
			"5": "<sup>5</sup> Izay olona mandrobo ny namany Dia mamela-pandrika ny tongony.",
			"6": "<sup>6</sup> Misy fandrika amin’ny heloky ny olon-dratsy; Fa ny marina mihoby sy mifaly.",
			"7": "<sup>7</sup> Ny marina mihevitra ny adin’ny malahelo; Fa ny ratsy fanahy tsy hahalala fahendrena.",
			"8": "<sup>8</sup> Ny mpaniratsira mampihotakotaka ny tanàna; Fa ny hendry mampionona fahatezerana.",
			"9": "<sup>9</sup> Raha iadian’ny hendry ny adala, Dia tezitra sy mihomehy izy, ka tsi hisy fitsaharana.",
			"10": "<sup>10</sup> Ny olona mpandatsa-dra mankahala ny tsy misy tsiny; Fa ny mahitsy kosa miahy ny ainy.",
			"11": "<sup>11</sup> Ny fahatezeran’ny adala dia avoakany avokoa; Fa ny hendry kosa manindry fo.",
			"12": "<sup>12</sup> Raha mihaino lainga ny mpanapaka, Dia ho ratsy fanahy ny mpanompony rehetra.",
			"13": "<sup>13</sup> Mihaona ny malahelo sy ny mpampahory; Jehovah no manazava ny mason’izy roa tonta.",
			"14": "<sup>14</sup> Izay mpanjaka mitsara marina ny malahelo, Dia haharitra mandrakizay ny seza fiandrianany.",
			"15": "<sup>15</sup> Ny tsorakazo sy ny anatra no manome fahendrena; Fa ny zaza aranana mampahamenatra ny reniny.",
			"16": "<sup>16</sup> Raha maro ny ratsy fanahy, dia mitombo ny ota; Fa ny marina ho faly mahita ny fahalavoany.",
			"17": "<sup>17</sup> Faizo ny zanakao, dia ho famelombelomana ny fonao izy Sy ho fifalian’ny fanahinao.",
			"18": "<sup>18</sup> Raha tsy misy fahitana, dia atondraka hanaram-po amin’izay tsy mety ny olona; Fa raha mitandrina ny lalàna kosa izy, dia sambatra.",
			"19": "<sup>19</sup> Raha teny fotsiny ihany, dia tsy ampy hananarana ny mpanompo; Fa na dia fantany aza, tsy hankatoaviny.",
			"20": "<sup>20</sup> Hitanao va ny olona faingam-bava? Ny adala aza misy hantenaina kokoa noho izy.",
			"21": "<sup>21</sup> Ny mpanompo izay nampihantaina hatry ny fony mbola kely Dia hihambo ho zanaka any am-parany.",
			"22": "<sup>22</sup> Ny olona mora tezitra manetsika ady; Ary ny olona masiaka dia be fahadisoana.",
			"23": "<sup>23</sup> Ny fiavonavonan’ny olona no hampietry azy; Fa hahazo voninahitra kosa ny manetry tena.",
			"24": "<sup>24</sup> Izay miray tetika amin’ny mpangalatra dia manary tena; Mandre ny fampianianana izy, nefa tsy mety milaza.",
			"25": "<sup>25</sup> Mamandrika ny tahotra olona; Fa izay matoky an’i Jehovah no hovonjena.",
			"26": "<sup>26</sup> Maro no mila sitraka amin’ ny mpanapaka; Fa avy amin’i Jehovah ny fitsarana ny olona.",
			"27": "<sup>27</sup> Ny meloka dia fahavetavetana eo imason’ny marina; Ary ny mahitsy làlana dia mba fahavetavetana eo imason’ny ratsy fanahy kosa."
		},
		"30": {
			"1": "<sup>1</sup> NY teny mavesa-danja nataon’i Agora, zanak’i Jake, dia ny teny nataon-dralehilahy tamin’Itiela, eny, tamin’Itiela sy Okala:",
			"2": "<sup>2</sup> Izaho dia biby, fa tsy olona, Ary tsy manan-tsain’olombelona aho.",
			"3": "<sup>3</sup> Tsy nianatra fahendrena aho, Na nahazo ny fahalalana ny Iray Masina.",
			"4": "<sup>4</sup> Iza no efa niakatra tany an-danitra sady nidina indray? Iza no nahangona ny rivotra tao an-tànany? Iza no nahafono ny rano tamin’ny lambany? Iza no naha-misy ny faran’ny tany rehetra? Iza no anarany, ary iza no anaran’ny zanany? Fa toa fantatrao!",
			"5": "<sup>5</sup> Ny tenin’Andriamanitra dia voadio avokoa; Ampinga ho an’izay mialoka aminy Izy.",
			"6": "<sup>6</sup> Aza manampy ny teniny, fandrao hanariny hianao Ka ho hita fa mpandainga.",
			"7": "<sup>7</sup> Zavatra roa loha no angatahiko aminao; Ka aza lavinao, dieny tsy mbola maty aho:",
			"8": "<sup>8</sup> Ampanalaviro ahy ny zava-poana sy ny lainga; Aza manome ahy alahelo na harena; Fahano aho amin’izay anjara-hanina sahaza ho ahy;",
			"9": "<sup>9</sup> Fandrao, raha voky aho, dia handa Ka hanao hoe: Iza moa Jehovah? Ary andrao, raha tonga malahelo aho, dia hangalatra Ka hanamavo ny anaran’Andriamanitro.",
			"10": "<sup>10</sup> Aza manendrikendrika ny mpanompo amin’ny tompony, Fandrao hozoniny hianao ka hahazo heloka.",
			"11": "<sup>11</sup> Misy taranaka izay manozona ny rainy Sady tsy misaotra ny reniny.",
			"12": "<sup>12</sup> Misy taranaka manao azy ho madio, Kanjo tsy voasasa ho afaka amin’ny fahalotoany.",
			"13": "<sup>13</sup> Misy taranaka, endrey ny fiandrandran’ ny masony Sy ny fisondrotry ny hodi-masony!",
			"14": "<sup>14</sup> Misy taranaka izay toy ny sabatra ny nifiny, Ary toy ny antsy ny vazany, Hamongotra ny ory tsy ho amin’ny tany Sy ny malahelo tsy ho amin’ny olona.",
			"15": "<sup>15</sup> Ny dinta manan-janakavavy roa, dia ny hoe: Omeo, Omeo. Misy zavatra telo tsy mety voky, Eny, efatra aza no tsy manao hoe: Aoka izay:",
			"16": "<sup>16</sup> Ny fiainan-tsi-hita sy ny kibo momba, Ny tany izay tsy vonton-drano, Ary ny afo koa no tsy mba manao hoe: Aoka izay.",
			"17": "<sup>17</sup> Ny maso izay miandranandrana amin-dray Sady tsy mety manaja reny Dia hotsaingotsaingohin’ny goaika eny an-dohasahan-driaka Ary hohanin’ny zana-boromahery.",
			"18": "<sup>18</sup> Misy zavatra telo mahagaga ka tsy takatro, Eny, efatra aza no tsy fantatro:",
			"19": "<sup>19</sup> Ny nalehan’ny voromahery teny amin’ny habakabaka, Ny nalehan’ny bibilava teny amin’ny vatolampy, Ny nalehan’ny sambo teny amin’ny ranomasina, Ary ny nalehan’ny lehilahy tamin’ny tovovavy.",
			"20": "<sup>20</sup> Tahaka izany koa no fanaon’ny vehivavy mpijangajanga: Homana izy, dia mamafa ny vavany, Ka dia manao hoe: Tsy nanao ratsy aho.",
			"21": "<sup>21</sup> Izao fitondrana telo izao no tsy mampandry ny tany, Eny, zavatra efatra aza no tsy zakany;",
			"22": "<sup>22</sup> Raha ny andevo no tonga mpanjaka, Raha ny adala no voky hanina,",
			"23": "<sup>23</sup> Raha ny vehivavy lany zara no ampakarina, Ary raha ny ankizivavy no mandova ny tompovaviny.",
			"24": "<sup>24</sup> Izao zavatra efatra izao dia kely amin’ny etỳ ambonin’ny tany, Nefa hendry sady mahay:",
			"25": "<sup>25</sup> Ny vitsika dia firenena tsy matanjaka, Nefa manangona ny haniny amin’ny fahavaratra izy;",
			"26": "<sup>26</sup> Ny biby hyraka dia firenena tsy manan-kery, Nefa manao ny tranony ao amin’ny harambato izy;",
			"27": "<sup>27</sup> Ny valala tsy manana mpanjaka, Nefa izy rehetra miainga manao antokony tsara;",
			"28": "<sup>28</sup> Ny antsiantsy dia mandady amin’ny tànany, Nefa tonga ao an-tranon’ny mpanjaka izy.",
			"29": "<sup>29</sup> Misy zavatra telo izay tsara famindra, Eny, efatra aza no tsara fandeha;",
			"30": "<sup>30</sup> Dia ny liona, izay mahery indrindra amin’ny biby Ka tsy mihemotra na amin’inona na amin’inona,",
			"31": "<sup>31</sup> Ny soavaly voaomana hiady sy ny osilahy Ary ny mpanjaka tsy azo toherina.",
			"32": "<sup>32</sup> Raha adala ka nanandra-tena hianao, Na efa nisaina hanao ratsy, Dia tampeno ny tànanao ny vavanao.",
			"33": "<sup>33</sup> Ny fanontsankontsanana ny ronono no miteraka rononomandry, Ny fanolanana ny orona no miteraka ra; Ary ny fanetsiketsehana ny fahatezerana no miteraka ady."
		},
		"31": {
			"1": "<sup>1</sup> NY tenin’ i Lemoela mpanjaka, dia ny teny mavesa-danja izay nananaran-dreniny azy.",
			"2": "<sup>2</sup> Ahoana, anaka? Ary ahoana, ry zanako naterako? Ary ahoana, ry zanaky ny voadiko?",
			"3": "<sup>3</sup> Aza omena ny vehivavy ny herinao, Na ny fombam-piainanao ho an’izay manimba mpanjaka.",
			"4": "<sup>4</sup> Tsy mety amin’ny mpanjaka, ry Lemoela, Eny, tsy mety amin’ny mpanjaka ny misotro divay, Na amin’ny mpanapaka ny ho fatra-paniry toaka;",
			"5": "<sup>5</sup> Fandrao, raha misotro izy, dia manadino ny lalàna Ka mamily ny fitsarana ny zanaky ny fahoriana.",
			"6": "<sup>6</sup> Omeo toaka izay efa ho faty, Eny, divay ho an’izay malahelo azon’ny fangidiam-panahy;",
			"7": "<sup>7</sup> Aoka hisotro izy mba hanadinoany ny alahelony, Ary tsy hahatsiarovany ny fahoriany intsony.",
			"8": "<sup>8</sup> Sokafy ny vavanao hisolo ny moana, Hahazoan’ny kamboty ny rariny;",
			"9": "<sup>9</sup> Sokafy ny vavanao, ka tsarao marina, Ary omeo rariny ny ory sy ny malahelo.",
			"10": "<sup>10</sup> IZA no mahita vehivavy tsara? Fa ny tombany dia mihoatra lavitra noho ny voahangy.",
			"11": "<sup>11</sup> Matoky azy ny fon’ny lahy, Ary tsy mitsahatra ny fanambinana azy.",
			"12": "<sup>12</sup> Soa no ataon-dravehivavy aminy, fa tsy ratsy, Amin’ny andro rehetra iainany.",
			"13": "<sup>13</sup> Mizaha volon’ondry sy rongony izy Ary mazoto miasa amin’ny tànany.",
			"14": "<sup>14</sup> Toy ny sambon’ny mpandranto izy, Ka avy any amin’ny lavitra no itondrany ny haniny.",
			"15": "<sup>15</sup> Mifoha izy, raha mbola alina, Ka manome hanina ho an’ny ankohonany Sy anjara ho an’ny ankizivaviny.",
			"16": "<sup>16</sup> Mihevitra saha izy ka mahazo azy; Ny vokatry ny tànany no anaovany tanimboaloboka.",
			"17": "<sup>17</sup> Misikina hery izy, Ary hatanjahiny ny sandriny.",
			"18": "<sup>18</sup> Hitany fa tsara ny tombom-barony, Ary tsy maty jiro mandritra ny alina izy.",
			"19": "<sup>19</sup> Mandray ny fihazonam-bohavoha izy, Ary ny tànany mihazona ny ampela.",
			"20": "<sup>20</sup> Manatsotra ny tànany amin’ny ory izy, Eny, mamelatra ny tànany amin’ny mahantra.",
			"21": "<sup>21</sup> Tsy manahy ny orampanala ho amin’ny ankohonany izy, Fa ny ankohonany rehetra samy voatafy volon’ondry mena avokoa.",
			"22": "<sup>22</sup> Manao firakotra ho azy izy; Ny fitafiany dia rongony fotsy madinika sy lamba volomparasy.",
			"23": "<sup>23</sup> Ny lahy dia hajaina eo am-bavahady, Raha miara-mipetraka eo amin’ny loholona.",
			"24": "<sup>24</sup> Manao akanjo hariry madinika ravehivavy ka mivarotra izany, Ary fehin-kibo no amidiny amin’ny mpandranto.",
			"25": "<sup>25</sup> Hery sy voninahitra no fitafiany, Ary ny andro ho avy dia ihomehezany.",
			"26": "<sup>26</sup> Manokatra ny vavany amin’ny fahendrena izy; Ary ny lalàn’ny famindrampo no eo amin’ny lelany.",
			"27": "<sup>27</sup> Mitandrina tsara ny ataon’ny ankohonany izy Ary tsy homana ny mofon’ny fahalainana.",
			"28": "<sup>28</sup> Ny zanany mitsangana ka midera azy hoe sambatra; Ny lahy koa mitsangana ka midera azy hoe:",
			"29": "<sup>29</sup> Maro no zazavavy nanao soa, Fa hianao manoatra noho izy rehetra.",
			"30": "<sup>30</sup> Fitaka ny fahatsaram-bika, ary zava-poana ny fahatsaran-tarehy; Fa ny vehivavy matahotra an’i Jehovah ihany no hoderaina.",
			"31": "<sup>31</sup> Omeo azy ny avy amin’ny vokatry ny tànany; Ary aoka ny asany hidera azy eo am-bavahady."
		}
	}
};
module.exports = book;