var book = {
	"name": "1 Ngoma",
	"numChapters": 29,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> Adamu na Seti na Enoshi,",
			"2": "<sup>2</sup> Kenani na Mahalalēli na Yeredi,",
			"3": "<sup>3</sup> Henoki na Metusela na Lameki,",
			"4": "<sup>4</sup> Nowa na Shemu na Hamu na Yafeti.",
			"5": "<sup>5</sup> Bene Yafeti ni Gomeri na Magogi, na Madayi na Yavani, na Tubali na Mesheki na Tirasi.",
			"6": "<sup>6</sup> Bene Gomeri ni Ashikenazi na Rifati na Togaruma.",
			"7": "<sup>7</sup> Bene Yavani ni Elisha na Tarushishi, na Kitimu na Rodanimu.",
			"8": "<sup>8</sup> Bene Hamu ni Kushi na Misirayimu, na Puti na Kanāni.",
			"9": "<sup>9</sup> Bene Kushi ni Seba na Havila, na Sabuta na Rāma na Sabuteka. Bene Rāma ni Sheba na Dedani.",
			"10": "<sup>10</sup> Kushi yabyaye Nimurodi, ari we wabanje kuba umunyambaraga mu isi.",
			"11": "<sup>11</sup> Misirayimu abyara Abaludi n'Abanami, n'Abalehabi n'Abanafutuhi,",
			"12": "<sup>12</sup> n'Abapatirusi n'Abakasiluhi (ari bo Abafilisitiya bakomotseho), n'Abakafutori.",
			"13": "<sup>13</sup> Kanāni abyara imfura ye Sidoni na Heti,",
			"14": "<sup>14</sup> n'Umuyebusi n'Umwamori n'Umugirugashi,",
			"15": "<sup>15</sup> n'Umuhivi n'Umwaruki n'Umusini,",
			"16": "<sup>16</sup> n'Umunyaruvadi n'Umusemari n'Umuhamati.",
			"17": "<sup>17</sup> Bene Shemu ni Elamu na Ashuri na Arupakisadi, na Ludi na Aramu na Usi, na Huli na Geteri na Mesheki.",
			"18": "<sup>18</sup> Arupakisadi abyara Shela, Shela abyara Eberi.",
			"19": "<sup>19</sup> Eberi abyara abahungu babiri, umwe yitwaga Pelegi kuko mu minsi ye arimo isi yagabanijwe, kandi izina rya murumuna we ni Yokitani.",
			"20": "<sup>20</sup> Yokitani abyara Alimodadi na Shelefu, na Hasarumaveti na Yera,",
			"21": "<sup>21</sup> na Hadoramu na Uzali na Dikila,",
			"22": "<sup>22</sup> na Ebali na Abimayeli na Sheba,",
			"23": "<sup>23</sup> na Ofiri na Havila na Yobabu. Abo bose bari abahungu ba Yokitani.",
			"24": "<sup>24</sup> Shemu yabyaye Arupakisadi, Arupakisadi abyara Shela.",
			"25": "<sup>25</sup> Shela yabyaye Eberi, Eberi abyara Pelegi, Pelegi abyara Rewu.",
			"26": "<sup>26</sup> Rewu yabyaye Serugi, Serugu abyara Nahori, Nahori abyara Tera.",
			"27": "<sup>27</sup> Tera yabyaye Aburamu (ari we Aburahamu).",
			"28": "<sup>28</sup> Bene Aburahamu ni Isaka na Ishimayeli.",
			"29": "<sup>29</sup> Izi ni zo mbyaro zabo. Imfura ya Ishimayeli ni Nebayoti, agakurikirwa na Kedari na Adibēli na Mibusamu,",
			"30": "<sup>30</sup> na Mishuma na Duma na Masa na Hadadi na Tema,",
			"31": "<sup>31</sup> na Yeturi na Nafishi na Kedema. Abo ni bo bana ba Ishimayeli.",
			"32": "<sup>32</sup> Kandi abana ba Ketura inshoreke ya Aburahamu yabyaye Zimurani na Yokishani, na Medani na Midiyani, na Yishibaki na Shuwa. Kandi abahungu ba Yokishani ni Sheba na Dedani.",
			"33": "<sup>33</sup> Abahungu ba Midiyani ni Efa na Eferi, na Hanoki na Abida na Eluda. Abo bose bari abahungu ba Ketura.",
			"34": "<sup>34</sup> Aburahamu abyara Isaka. Bene Isaka ni Esawu na Isirayeli.",
			"35": "<sup>35</sup> Bene Esawu ni Elifazi na Reweli na Yewushi, na Yalamu na Kōra.",
			"36": "<sup>36</sup> Bene Elifazi ni Temani na Omari na Sefi, na Gatamu na Kenazi, na Timuna na Amaleki.",
			"37": "<sup>37</sup> Bene Reweli ni Nahati na Zera, na Shama na Miza.",
			"38": "<sup>38</sup> Bene Seyiri ni Lotani na Shobali, na Sibeyoni na Ana, na Dishoni na Eseri na Dishani.",
			"39": "<sup>39</sup> Bene Lotani ni Hori na Homami, kandi Timuna yari mushiki wa Lotani.",
			"40": "<sup>40</sup> Bene Shobali ni Aliyani na Manahati, na Ebali na Shefi na Onamu. Bene Sibeyoni ni Ayiya na Ana.",
			"41": "<sup>41</sup> Mwene Ana ni Dishoni, na bene Dishoni ni Hamurani na Eshibani na Yitirani na Kerani.",
			"42": "<sup>42</sup> Bene Eseri ni Biluhani na Zāvani na Yakani. Bene Dishani ni Usi na Arani.",
			"43": "<sup>43</sup> Aba ni bo bami bategekaga igihugu cya Edomu, ari nta mwami wari wategeka Abisirayeli. Bela mwene Bewori, izina ry'umurwa we witwaga Dinihaba.",
			"44": "<sup>44</sup> Bela aratanga, Yobabu mwene Zera w'i Bosira yima mu cyimbo cye.",
			"45": "<sup>45</sup> Yobabu aratanga, Hushamu wo mu gihugu cy'Abatemani yima mu cyimbo cye.",
			"46": "<sup>46</sup> Hushamu aratanga, Hadadi mwene Bedadi wanesheje Abamidiyani mu gihugu cy'i Mowabu, yima mu cyimbo cye, n'izina ry'umurwa we witwaga Aviti.",
			"47": "<sup>47</sup> Hadadi aratanga, Samula w'i Masireka yima mu cyimbo cye.",
			"48": "<sup>48</sup> Samula aratanga, Shawuli w'i Rehoboti yo ku ruzi yima mu cyimbo cye.",
			"49": "<sup>49</sup> Shawuli aratanga, Bālihanani mwene Akibori yima mu cyimbo cye.",
			"50": "<sup>50</sup> Bālihanani atanze Hadadi yima mu cyimbo cye, umurwa we witwaga Payi, (umugore we yitwaga Mehetabēli umukobwa wa Matiredi, umukobwa wa Mezahabu.)",
			"51": "<sup>51</sup> Hadadi na we aratanga. Aba ni bo batware ba Edomu: umutware Timuna n'umutware Aliya n'umutware Yeteti,",
			"52": "<sup>52</sup> n'umutware Oholibama n'umutware Ela, n'umutware Pinoni",
			"53": "<sup>53</sup> n'umutware Kenazi, n'umutware Temani, n'umutware Mibusari,",
			"54": "<sup>54</sup> n'umutware Magidiyeli n'umutware Iramu. Abo ni bo batware ba Edomu."
		},
		"2": {
			"1": "<sup>1</sup> Aba ni bo bahungu ba Isirayeli: Rubeni na Simiyoni na Lewi, na Yuda na Isakari na Zebuluni,",
			"2": "<sup>2</sup> na Dani na Yosefu na Benyamini, na Nafutali na Gadi na Asheri.",
			"3": "<sup>3</sup> Bene Yuda ni Eri na Onani na Shela, abo uko ari batatu yababyaranye na Batishuwa Umunyakanānikazi. Kandi Eri imfura ya Yuda, yari mubi mu maso y'Uwiteka, aramwica.",
			"4": "<sup>4</sup> Tamari umukazana we amubyaraho Perēsi na Zera. Abahungu ba Yuda bose bari batanu.",
			"5": "<sup>5</sup> Bene Perēsi ni Hesironi na Hamuli.",
			"6": "<sup>6</sup> Bene Zera ni Zimuri na Etani, na Hemani na Kalukoli na Dara, bose ni batanu.",
			"7": "<sup>7</sup> Bene Karumi ni Akani, wababazaga Isirayeli agacumura mu byashinganywe.",
			"8": "<sup>8</sup> Mwene Etani ni Azariya.",
			"9": "<sup>9</sup> Kandi bene Hesironi yabyaye ni Yeramēli na Ramu na Kelubayi.",
			"10": "<sup>10</sup> Ramu abyara Aminadabu, Aminadabu abyara Nahashoni umutware w'Abayuda.",
			"11": "<sup>11</sup> Nahashoni abyara Salumoni, Salumoni abyara Bowazi.",
			"12": "<sup>12</sup> Bowazi abyara Obedi, Obedi abyara Yesayi.",
			"13": "<sup>13</sup> Yesayi abyara imfura ye Eliyabu, uw'ubuheta ni Abinadabu, uwa gatatu ni Shimeya,",
			"14": "<sup>14</sup> uwa kane ni Netanēli, uwa gatanu ni Radayi,",
			"15": "<sup>15</sup> uwa gatandatu ni Osemu, uwa karindwi ni Dawidi.",
			"16": "<sup>16</sup> Kandi bashiki babo ni aba: Seruya na Abigayili. Bene Seruya ni Abishayi na Yowabu na Asaheli, uko ari batatu.",
			"17": "<sup>17</sup> Abigayili abyara Amasa, kandi se wa Amasa yari Yeteri w'Umwishimayeli.",
			"18": "<sup>18</sup> Kalebu mwene Hesironi abyarana abana n'umugore we Azuba no kuri Yeriyoti. Aba ni bo bahungu be: Yesheri na Shobabu na Aridoni.",
			"19": "<sup>19</sup> Azuba apfuye Kalebu ashyingirwa Efurata, amubyaraho Huri.",
			"20": "<sup>20</sup> Huri abyara Uri, Uri abyara Besaleli.",
			"21": "<sup>21</sup> Hanyuma Hesironi ataha ku mukobwa wa Makiri se wa Gileyadi, amurongora amaze imyaka mirongo itandatu avutse, amubyaraho Segubu.",
			"22": "<sup>22</sup> Segubu abyara Yayiri, ari we wari ufite imidugudu makumyabiri n'itatu mu gihugu cy'i Galeyadi.",
			"23": "<sup>23</sup> Geshuri na Aramu babanyaga imidugudu ya Yayiri hamwe n'i Kenati n'ibirorero byaho, yose ni imidugudu mirongo itandatu. Aba bose ni bene Makiri se wa Gileyadi.",
			"24": "<sup>24</sup> Bukeye Hesironi apfiriye i Kalebu ya Efurata, umugore we Abiya inda yamusigiye ayibyaramo Ashihuri se wa Tekowa.",
			"25": "<sup>25</sup> Bene Yeramēli imfura ya Hesironi ni aba: uw'imfura ni Ramu, agakurikirwa na Buna na Oreni na Osemu na Ahiya.",
			"26": "<sup>26</sup> Kandi Yeramēli yari afite undi mugore witwaga Atara, uwo ni we nyina wa Onamu.",
			"27": "<sup>27</sup> Bene Ramu imfura ya Yeramēli ni Māsi na Yamini na Ekeri.",
			"28": "<sup>28</sup> Na bene Onamu ni Shamayi na Yada, bene Shamayi ni Nadabu na Abishuri.",
			"29": "<sup>29</sup> Na muka Abishuri yitwaga Abihayili, amubyaraho Ahubani na Molidi.",
			"30": "<sup>30</sup> Bene Nadabu ni Seledi na Apayimu, ariko Seledi apfa ari incike.",
			"31": "<sup>31</sup> Mwene Apayimu ni Ishi, mwene Ishi ni Sheshani, mwene Sheshani ni Ahilayi.",
			"32": "<sup>32</sup> Bene Yada murumuna wa Shamayi ni Yeteri na Yonatani, Yeteri apfa ari incike.",
			"33": "<sup>33</sup> Bene Yonatani ni Peleti na Zaza. Abo ni bo bene Yeramēli.",
			"34": "<sup>34</sup> Kandi Sheshani nta bahungu yari afite keretse abakobwa. Ariko yari afite umugaragu w'Umunyegiputa witwaga Yaruha,",
			"35": "<sup>35</sup> ashyingira umukobwa we uwo mugaragu we Yaruha, amubyaraho Atayi.",
			"36": "<sup>36</sup> Atayi abyara Natani, Natani abyara Zabadi.",
			"37": "<sup>37</sup> Zabadi abyara Efulali, Efulali abyara Obedi.",
			"38": "<sup>38</sup> Obedi abyara Yehu, Yehu abyara Azariya.",
			"39": "<sup>39</sup> Azariya abyara Helesi, Helesi abyara Eleyasa.",
			"40": "<sup>40</sup> Eleyasa abyara Sisimayi, Sisimayi abyara Shalumu.",
			"41": "<sup>41</sup> Shalumu abyara Yekamiya, Yekamiya abyara Elishama.",
			"42": "<sup>42</sup> Bene Kalebu murumuna wa Yeramēli, imfura ye ni Mesha ari we se wa Zifu, na bene Mesha se wa Heburoni.",
			"43": "<sup>43</sup> Bene Heburoni ni Kōra na Tapuwa, na Rekemu na Shema.",
			"44": "<sup>44</sup> Shema abyara Rahamu se wa Yorikeyamu, Rememu abyara Shamayi.",
			"45": "<sup>45</sup> Mwene Shamayi ni Mawoni, kandi Mawoni yari se wa Betisuri.",
			"46": "<sup>46</sup> Kandi Efa inshoreke ya Kalebu abyara Harani na Mosa na Gazezi, Harani abyara Gazezi.",
			"47": "<sup>47</sup> Bene Yahidayi ni Regemu na Yotamu na Geshani, na Peleti na Efa na Shāfi.",
			"48": "<sup>48</sup> Kandi Māka inshoreke ya Kalebu, abyara Sheberi na Tiruhana.",
			"49": "<sup>49</sup> Kandi abyara Shāfi se wa Madumana, na Sheva se wa Makubena n'uwa Gibeya. Kandi umukobwa wa Kalebu yari Akisa.",
			"50": "<sup>50</sup> Aba ni bo bene Kalebu mwene Huri imfura ya Efurata: Shobali se wa Kiriyatiyeyarimu,",
			"51": "<sup>51</sup> na Salima se wa Betelehemu, na Harefu se wa Betigaderi.",
			"52": "<sup>52</sup> Kandi Shobali se wa Kiriyatiyeyarimu yari afite abahungu, Harowe na Hasihamanahati.",
			"53": "<sup>53</sup> Imbyaro za Kiriyatiyeyarimu ni Abayeteri n'Abaputi, n'Abashumati n'Abamishurayi, kuri abo ni ho Abasorati n'Abanyeshitawoli bakomotse.",
			"54": "<sup>54</sup> Bene Salima ni Betelehemu n'Abanyanetofa na Atarotibetiyowabu, n'igice cy'Abamanahati n'Abasori.",
			"55": "<sup>55</sup> Kandi imbyaro z'abanditsi babaga i Yabesi ni Abatirati n'Abashimeyati n'Abasukati. Abo ni bo Bakeni bakomotse kuri Hamati se w'umuryango wa Rekabu."
		},
		"3": {
			"1": "<sup>1</sup> Aba ni bo bahungu ba Dawidi yabyariye i Heburoni: uw'imfura ni Amunoni umwana wa Ahinowamu w'Umunyayezerēlikazi, uw'ubuheta ni Daniyeli umwana wa Abigayili w'Umunyakarumelikazi,",
			"2": "<sup>2</sup> uwa gatatu ni Abusalomu umwana wa Māka umukobwa wa Talumayi umwami w'i Geshuri, uwa kane ni Adoniya umwana wa Hagiti,",
			"3": "<sup>3</sup> uwa gatanu ni Shefatiya umwana wa Abitali, uwa gatandatu ni Itureyamu umwana w'umugore we Egila.",
			"4": "<sup>4</sup> Abo uko ari batandatu yababyariye i Heburoni, kandi yamazeyo imyaka irindwi n'amezi atandatu ari ku ngoma. Naho i Yerusalemu yamazeyo imyaka mirongo itatu n'itatu ari ku ngoma.",
			"5": "<sup>5</sup> Kandi aba ni bo yabyariye i Yerusalemu: Shimeya na Shobabu na Natani na Salomo, uko ari bane ni aba Batishuwa umukobwa wa Amiyeli,",
			"6": "<sup>6</sup> na Ibuhari na Elishama na Elifeleti,",
			"7": "<sup>7</sup> na Noga na Nefegi na Yafiya,",
			"8": "<sup>8</sup> na Elishama na Eliyada na Elifeleti, uko ari icyenda.",
			"9": "<sup>9</sup> Abo bose bari abahungu ba Dawidi hatagiyeho ab'abaja, kandi Tamari yari mushiki wabo.",
			"10": "<sup>10</sup> Umuhungu wa Salomo ni Rehobowamu, uwa Rehobowamu ni Abiya, uwa Abiya ni Asa, uwa Asa ni Yehoshafati.",
			"11": "<sup>11</sup> Uwa Yehoshafati ni Yoramu, uwa Yoramu ni Ahaziya, uwa Ahaziya ni Yowasi.",
			"12": "<sup>12</sup> Uwa Yowasi ni Amasiya, uwa Amasiya ni Uziya, uwa Uziya ni Yotamu.",
			"13": "<sup>13</sup> Uwa Yotamu ni Ahazi, uwa Ahazi ni Hezekiya, uwa Hezekiya ni Manase.",
			"14": "<sup>14</sup> Uwa Manase ni Amoni, uwa Amoni ni Yosiya.",
			"15": "<sup>15</sup> Bene Yosiya uw'impfura ni Yohanani, uwo ubuheta ni Yehoyakimu, uwa gatatu ni Sedekiya, uwa kane ni Shalumu.",
			"16": "<sup>16</sup> Mwene Yehoyakimu ni Yekoniya, uwa Yekoniya ni Sedekiya.",
			"17": "<sup>17</sup> Bene Yekoniya w'imbohe ni Sheyalutiyeli,",
			"18": "<sup>18</sup> na Malikiramu na Pedaya na Shenasari, na Yekamiya na Hoshama na Nedabiya.",
			"19": "<sup>19</sup> Bene Pedaya ni Zerubabeli na Shimeyi, bene Zerubabeli ni Meshulamu na Hananiya kandi Shelomiti yari mushiki wabo,",
			"20": "<sup>20</sup> na Hashubu na Oheli na Berekiya, na Hasadiya na Yushabuhesedi, uko ari batanu.",
			"21": "<sup>21</sup> Bene Hananiya ni Pelatiya na Yeshaya, na bene Refaya na bene Arunani, na bene Obadiya na bene Shekaniya.",
			"22": "<sup>22</sup> Bene Shekaniya ni Shemaya, bene Shemaya ni Hatushi na Igali na Bariya, na Neyariya na Shafati, uko ari batandatu.",
			"23": "<sup>23</sup> Bene Neyariya ni Eliyowenayi na Hizekiya na Azirikamu, uko ari batatu.",
			"24": "<sup>24</sup> Bene Eliyowenayi ni Hodaviya na Eliyashibu na Pelaya, na Akubu na Yohanani na Delaya na Anani, uko ari barindwi."
		},
		"4": {
			"1": "<sup>1</sup> Bene Yuda ni Perēsi na Hesironi, na Karumi na Huri na Shobali.",
			"2": "<sup>2</sup> Reyaya mwene Shobali abyara Yahati, Yahati abyara Ahumayi na Lahadi. Izo ni zo mbyaro z'Abasorati.",
			"3": "<sup>3</sup> Aba ni bo bana ba se wa Etamu: Yezerēli na Ishuma na Idubashi, na mushiki wabo yitwaga Haseleluponi,",
			"4": "<sup>4</sup> na Penuweli se wa Gedori na Ezeri se wa Husha. Aba ni bo bahungu ba Huri, imfura ye ni Efurata se wa Betelehemu.",
			"5": "<sup>5</sup> Ashihuri se wa Tekowa yari afite abagore babiri, Hela na Nāra.",
			"6": "<sup>6</sup> Nāra amubyaraho Ahuzamu na Heferi, na Temeni na Hāhashutari. Abo ni bo bahungu ba Nāra.",
			"7": "<sup>7</sup> Bene Hela ni Sereti na Isuhari na Etunani.",
			"8": "<sup>8</sup> Hakosi abyara Anubu na Sobeba, n'imbyaro za Aharuheli mwene Haramu.",
			"9": "<sup>9</sup> Yabesi yarushaga bene se icyubahiro, kandi nyina yamwise Yabesi kuko yavuze ati “Namubyaranye agahinda.”",
			"10": "<sup>10</sup> Yabesi atakambira Imana ya Isirayeli ati “Icyampa ukampa umugisha rwose, ukāgura imbago yanjye kandi ukuboko kwawe kukabana nanjye, ukandinda ibyago ntibimbabaze.” Nuko Imana imuha ibyo yasabye.",
			"11": "<sup>11</sup> Kelubu murumuna wa Shuha abyara Mehiri se wa Eshitoni.",
			"12": "<sup>12</sup> Eshitoni abyara Betirafa na Paseya, na Tehina se wa Irunahashi. Abo ni bo bantu ba Reka.",
			"13": "<sup>13</sup> Bene Kenazi ni Otiniyeli na Seraya, mwene Otiniyeli ni Hatati.",
			"14": "<sup>14</sup> Meyonotayi abyara Ofura, na Seraya abyara Yowabu se wa Geharashimu, kuko bari abanyamyuga.",
			"15": "<sup>15</sup> Bene Kalebu mwene Yefune ni Iru, na Ela, na Nāmu, na bene Ela ni Kenazi.",
			"16": "<sup>16</sup> Bene Yehalelēli ni Zifu na Zifa, na Tiriya na Asarēli.",
			"17": "<sup>17</sup> Bene Ezira ni Yeteri na Meredi na Eferi na Yaloni, kandi abyara Miriyamu na Shamayi, na Ishuba se wa Eshitemowa.",
			"18": "<sup>18</sup> Umugore we w'Umuyudakazi abyara Yeredi se wa Gedori, na Heberi se wa Soko, na Yekutiyeli se wa Zanowa. Abo ni bo bahungu ba Bitiya umukobwa wa Farawo warongowe na Meredi.",
			"19": "<sup>19</sup> Abahungu ba muka Hodiya murumuna wa Nahamu, ni se wa Keyila w'Umugarumi na Eshitemowa w'Umunyamāka.",
			"20": "<sup>20</sup> Bene Shimoni ni Amunoni na Rina, na Benihanani na Tiloni. Bene Ishi ni Zoheti na Benizoheti.",
			"21": "<sup>21</sup> Bene Shela mwene Yuda ni Eri se wa Leka na Lāda se wa Maresha, n'imbyaro z'inzu y'ababohaga imyenda y'ibitare byiza, bo mu nzu ya Ashibeya,",
			"22": "<sup>22</sup> na Yokimu n'abagabo b'i Kozeba, na Yowasi na Sarafi batwaraga i Mowabu, na Yashubilehemu. Kandi ayo magambo ni aya kera.",
			"23": "<sup>23</sup> Abo ni bo bari ababumbyi baturaga i Netayimu n'i Gedera, babanagayo n'umwami bakamukorera.",
			"24": "<sup>24</sup> Bene Simiyoni ni Nemuweli na Yamini, na Yaribu na Zera na Shawuli.",
			"25": "<sup>25</sup> Umuhungu wa Shawuli ni Shalumu, mwene Shalumu ni Mibusamu, mwene Mibusamu ni Mishuma.",
			"26": "<sup>26</sup> Mwene Mishuma ni Hamuweli, mwene Hamuweli ni Zakuri, mwene Zakuri ni Shimeyi.",
			"27": "<sup>27</sup> Shimeyi abyara abahungu cumi na batandatu n'abakobwa batandatu, ariko bene se ntibabyara abana benshi kandi umuryango wabo wose ntiwagwiriye nk'uw'Abayuda.",
			"28": "<sup>28</sup> Kandi baturaga i Bērisheba n'i Molada n'i Hasarishuwali,",
			"29": "<sup>29</sup> n'i Biluha na Esemu n'i Toladi,",
			"30": "<sup>30</sup> n'i Betuweli n'i Horuma n'i Sikulagi,",
			"31": "<sup>31</sup> n'i Betimarukaboti n'i Hasarisusimu, n'i Betibiri n'i Shārayimu. Iyo ni yo yari imidugudu yabo kugeza aho Dawidi yimiye ingoma.",
			"32": "<sup>32</sup> N'ibirorero byabo ni Etamu na Ayini, na Rimoni na Tokeni na Ashani, byose uko ari bitanu.",
			"33": "<sup>33</sup> Ibirorero byabo byose byari bikikije iyo midugudu ukageza i Bāli. Aho ni ho baturaga kandi bamenyaga ibya ba sekuruza.",
			"34": "<sup>34</sup> Meshobabu na Yamuleki na Yosha mwene Amasiya,",
			"35": "<sup>35</sup> na Yoweli na Yehu mwene Yoshibiya mwene Seraya mwene Asiyeli,",
			"36": "<sup>36</sup> na Eliyowenayi na Yākoba na Yeshohaya, na Asaya na Adiyeli na Yesimiyeli na Benaya,",
			"37": "<sup>37</sup> na Ziza mwene Shifi mwene Aloni, mwene Yedaya mwene Shimuri mwene Shemaya.",
			"38": "<sup>38</sup> Abo bavuzwe mu mazina bari abatware mu miryango yabo, kandi amazu ya ba sekuruza babo aragwira cyane.",
			"39": "<sup>39</sup> Bajya aharasukirwa i Gedori iruhande rw'ikibaya rw'iburasirazuba, gushakira imikumbi yabo ubwatsi.",
			"40": "<sup>40</sup> Babona ubwatsi bwiza butoshye, kandi igihugu cyari kigari gifite amahoro n'ituze, kuko abari batuyeyo ubwa mbere bari Abahamu.",
			"41": "<sup>41</sup> Kandi abo banditswe amazina baje ku ngoma ya Hezekiya umwami w'Abayuda batera amahema yabo, Abameyunimu basanzeyo barabarimbura rwose kugeza n'ubu, bahindūra imisozi yabo bayituramo, kuko hari ubwatsi bw'imikumbi yabo.",
			"42": "<sup>42</sup> Kandi bamwe muri bo bo muri bene Simiyoni, abagabo magana atanu bajya ku musozi Seyiri, abatware babo ni Pelatiya na Neyariya na Refaya na Uziyeli, bene Ishi.",
			"43": "<sup>43</sup> Bica igice cy'Abamaleki cyacitse ku icumu, baturayo na bugingo n'ubu."
		},
		"5": {
			"1": "<sup>1</sup> Rubeni yari imfura ya Isirayeli, ariko ubutware bwe bw'umwana w'imfura barabumwaka, kuko yashize isoni kuri muka se, babuha bene Yosefu mwene Isirayeli. Nyamara (Yosefu) ntiyari akwiriye gutangiririrwaho mu mubare w'amazina nk'uwavutse ari imfura.",
			"2": "<sup>2</sup> Kandi Yuda yagwije amaboko muri bene se, kuri we hakomotse umwami. Ariko ubutware bw'umwana w'imfura bwari ubwa Yosefu.",
			"3": "<sup>3</sup> Bene Rubeni imfura ya Isirayeli ni Henoki na Palu, na Hesironi na Karumi.",
			"4": "<sup>4</sup> Mwene Yoweli ni Shemaya, mwene Shemaya ni Gogi, mwene Gogi ni Shimeyi,",
			"5": "<sup>5</sup> mwene Shimeyi ni Mika, mwene Mika ni Reyaya, mwene Reyaya ni Bāli,",
			"6": "<sup>6</sup> mwene Bāli ni Bēra, ni we Tigulatipilineseri umwami wa Ashuri yajyanye ho umunyago, ari we wari umutware w'Abarubeni.",
			"7": "<sup>7</sup> Bene Bēra uko imiryango yabo iri, ni yo yanditswe mu bya ba sekuruza uko kuvuka kwabo kwari kuri, umukuru ni Yeyeli na Zekariya,",
			"8": "<sup>8</sup> na Bela mwene Azazi mwene Shema mwene Yoweli, waturaga muri Aroweri akageza i Nebo n'i Bālimeyoni.",
			"9": "<sup>9</sup> Kandi agatura n'iburasirazuba akageza ahagana mu ishyamba, uhereye ku ruzi rwa Ufurate kuko imikumbi yabo yari igwiriye mu gihugu cy'i Galeyadi.",
			"10": "<sup>10</sup> Maze ku ngoma ya Sawuli barwana n'Abahagari barabica, baherako batura mu mahema yabo, bakwira igihugu cyose cy'iburasirazuba bw'i Galeyadi.",
			"11": "<sup>11</sup> Bene Gadi batura babitegeye, mu gihugu cy'i Bashani ukageza Saleka.",
			"12": "<sup>12</sup> Yoweli ni we wari mukuru, uwa kabiri ni Shafamu, na Yanayi na Shafati i Bashani.",
			"13": "<sup>13</sup> Na bene wabo bo mu mbyaro za ba sekuruza babo, Mikayeli na Meshulamu na Sheba, na Yorayi na Yakani na Ziya na Eberi, uko ari barindwi.",
			"14": "<sup>14</sup> Aba ni bo bene Abihayili mwene Huri mwene Yarowa, mwene Gileyadi mwene Mikayeli mwene Yeshishayi, mwene Yahudo mwene Buzi.",
			"15": "<sup>15</sup> Ahi mwene Abudiyeli mwene Guni, ni we wari umutware w'amazu ya ba sekuruza.",
			"16": "<sup>16</sup> Abo baturaga i Galeyadi muri Bashani, mu midugudu yaho no mu bikingi byose by'i Sharoni ukageza aho bigarukira.",
			"17": "<sup>17</sup> Abo bose barabazwe, uko kuvuka kwabo kwari kuri ku ngoma ya Yotamu umwami w'i Buyuda, no ku ngoma ya Yerobowamu umwami wa Isirayeli.",
			"18": "<sup>18</sup> Muri bene Rubeni n'Abagadi n'igice cy'umuryango wa Manase, harimo abagabo b'intwari zibasha gutwara ingabo n'inkota no kurasanisha imiheto z'abahanga mu ntambara, zose zari ingabo inzovu enye n'ibihumbi bine na magana arindwi na mirongo itandatu, ni bo babashaga gutabara.",
			"19": "<sup>19</sup> Barwana n'Abahagari na Yeturi na Nafishi na Nadabu.",
			"20": "<sup>20</sup> Bakirwana na bo baratabarwa, Abahagari n'abari kumwe na bo bose batangwa mu maboko y'Abarubeni n'Abagadi n'Abamanase, kuko batakambiye Imana muri iyo ntambara, ikemera guhendahendwa kuko bayiringiye.",
			"21": "<sup>21</sup> Maze banyaga amatungo yabo, ingamiya inzovu eshanu n'intama inzovu ebyiri n'ibihumbi bitanu, n'indogobe ibihumbi bibiri n'abantu agahumbi.",
			"22": "<sup>22</sup> Benshi baguye mu ntambara kuko iyo ntambara yari ivuye ku Mana. Baherako batura muri icyo gihugu, kugeza igihe bajyaniwe ho iminyago.",
			"23": "<sup>23</sup> Ab'igice cy'umuryango wa Manase batura muri icyo gihugu, uhereye i Bashani ukageza i Bāliherumoni n'i Seniri no ku musozi wa Herumoni.",
			"24": "<sup>24</sup> Aba ni bo bari abatware b'amazu ya ba sekuruza babo: Eferi na Ishi na Eliyeli na Aziriyeli, na Yeremiya na Hodaviya na Yahudiyeli. Bari abanyambaraga b'intwari b'ibirangirire n'abatware b'amazu ya ba sekuruza babo.",
			"25": "<sup>25</sup> Hanyuma bacumura ku Mana ya ba sekuruza babo, bakajya bararikira imana z'abanyamahanga bo muri icyo gihugu, abo Imana yabarimburiye imbere.",
			"26": "<sup>26</sup> Hanyuma Imana ya Isirayeli ihata umutima wa Puli umwami wa Ashūri n'umutima wa Tigulatipilineseri umwami wa Ashūri, ajyana ho iminyago Abarubeni n'Abagadi n'ab'igice cy'umuryango wa Manase, abajyana i Hala n'i Habora n'i Hara, no ku mugezi w'i Gozani na bugingo n'ubu.",
			"27": "<sup>27</sup> Bene Lewi ni Gerushoni na Kohati na Merari.",
			"28": "<sup>28</sup> Bene Kohati ni Amuramu na Isuhari, na Heburoni na Uziyeli.",
			"29": "<sup>29</sup> Bene Amuramu ni Aroni na Mose na Miriyamu. Bene Aroni ni Nadabu na Abihu, na Eleyazari na Itamari.",
			"30": "<sup>30</sup> Eleyazari abyara Finehasi, Finehasi abyara Abishuwa.",
			"31": "<sup>31</sup> Abishuwa abyara Buki, Buki abyara Uzi.",
			"32": "<sup>32</sup> Uzi abyara Zerahiya, Zerahiya abyara Merayoti.",
			"33": "<sup>33</sup> Merayoti abyara Amariya, Amariya abyara Ahitubu.",
			"34": "<sup>34</sup> Ahitubu abyara Sadoki, Sadoki abyara Ahimāzi.",
			"35": "<sup>35</sup> Ahimāzi abyara Azariya, Azariya abyara Yohanani.",
			"36": "<sup>36</sup> Yohanani abyara Azariya, (ari we wakoraga umurimo w'ubutambyi mu nzu Salomo yubatse i Yerusalemu).",
			"37": "<sup>37</sup> Azariya abyara Amariya, Amariya abyara Ahitubu.",
			"38": "<sup>38</sup> Ahitubu abyara Sadoki, Sadoki abyara Shalumu.",
			"39": "<sup>39</sup> Shalumu abyara Hilukiya, Hilukiya abyara Azariya.",
			"40": "<sup>40</sup> Azariya abyara Seraya, Seraya abyara Yehosadaki.",
			"41": "<sup>41</sup> Yehosadaki yajyagiye ari umunyagano, ubwo Uwiteka yanyagishaga Abayuda n'ab'i Yerusalemu Nebukadinezari."
		},
		"6": {
			"1": "<sup>1</sup> Bene Lewi ni Gerushomu na Kohati na Merari.",
			"2": "<sup>2</sup> Aya ni yo mazina ya bene Gerushomu: Libuni na Shimeyi.",
			"3": "<sup>3</sup> Bene Kohati ni Amuramu na Isuhari, na Heburoni na Uziyeli.",
			"4": "<sup>4</sup> Bene Merari ni Mahali na Mushi. Kandi iyi ni yo miryango y'Abalewi nk'uko amazu ya ba sekuruza yari ari.",
			"5": "<sup>5</sup> Aba Gerushomu: umuhungu we ni Libuni, mwene Libuni ni Yahati, mwene Yahati ni Zima,",
			"6": "<sup>6</sup> mwene Zima ni Yowa, mwene Yowa ni Ido, mwene Ido ni Zera, mwene Zera ni Yeyaterayi.",
			"7": "<sup>7</sup> Bene Kohati: umuhungu we ni Aminadabu, mwene Aminadabu ni Kōra, mwene Kōra ni Asiri,",
			"8": "<sup>8</sup> mwene Asiri ni Elukana, mwene Elukana ni Ebiyasafu, mwene Ebiyasafu ni Asiri.",
			"9": "<sup>9</sup> Mwene Asiri ni Tahati, mwene Tahati ni Uriyeli, mwene Uriyeli ni Uziya, mwene Uziya ni Shawuli.",
			"10": "<sup>10</sup> Bene Elukana ni Amasayi na Ahimoti.",
			"11": "<sup>11</sup> Ibya Elukana: bene Elukana, umuhungu we ni Zofayi, mwene Zofayi ni Nahati.",
			"12": "<sup>12</sup> Mwene Nahati ni Eliyabu, mwene Eliyabu ni Yerohamu, mwene Yerohamu ni Elukana.",
			"13": "<sup>13</sup> Bene Samweli: imfura ye ni Yoweli, uw'ubuheta ni Abiya.",
			"14": "<sup>14</sup> Bene Merari ni Mahali, mwene Mahali ni Libuni, mwene Libuni ni Shimeyi, mwene Shimeyi ni Uza.",
			"15": "<sup>15</sup> Mwene Uza ni Shimeya, mwene Shimeya ni Hagiya, mwene Hagiya ni Asaya.",
			"16": "<sup>16</sup> Abo ni bo Dawidi yashyize ku murimo wo kuririmba mu nzu y'Uwiteka, ubwo isanduku yari imaze gushyirwa mu buruhukiro,",
			"17": "<sup>17</sup> bagakoreshereza indirimbo imbere y'ubuturo bw'Ihema ry'ibonaniro, kugeza aho Salomo yamariye kubaka inzu y'Uwiteka i Yerusalemu, bagakora umurimo wabo bakuranwa, uko ibihe byabo byari biri.",
			"18": "<sup>18</sup> Kandi aba ni bo bakoranaga n'abahungu babo: muri bene Kohati ni Hemani umuririmbyi mwene Yoweli, mwene Samweli,",
			"19": "<sup>19</sup> mwene Elukana, mwene Yerohamu, mwene Eliyeli, mwene Towa,",
			"20": "<sup>20</sup> mwene Sufi, mwene Elukana, mwene Mahati, mwene Amasayi,",
			"21": "<sup>21</sup> mwene Elukana, mwene Yoweli, mwene Azariya, mwene Zefaniya,",
			"22": "<sup>22</sup> mwene Tahati, mwene Asiri, mwene Ebiyasafu, mwene Kōra,",
			"23": "<sup>23</sup> mwene Isuhari, mwene Kohati, mwene Lewi, mwene Isirayeli.",
			"24": "<sup>24</sup> Na murumuna we Asafu, wahagararaga iburyo bwe, ari we Asafu mwene Berekiya, mwene Shimeya,",
			"25": "<sup>25</sup> mwene Mikayeli, mwene Bāseya, mwene Malikiya,",
			"26": "<sup>26</sup> mwene Etuni, mwene Zera, mwene Adaya,",
			"27": "<sup>27</sup> mwene Etani, mwene Zima, mwene Shimeyi,",
			"28": "<sup>28</sup> mwene Yahati, mwene Gerushomu, mwene Lewi.",
			"29": "<sup>29</sup> Kandi ibumoso bwabo hari bene wabo bene Merari: Etani mwene Kishi, mwene Abudi, mwene Maluki,",
			"30": "<sup>30</sup> mwene Hashabiya, mwene Amasiya, mwene Hilukiya,",
			"31": "<sup>31</sup> mwene Amusi, mwene Bani, mwene Shemeri,",
			"32": "<sup>32</sup> mwene Mahali, mwene Mushi, mwene Merari, mwene Lewi.",
			"33": "<sup>33</sup> Kandi bene wabo Abalewi, bashyiriweho gukora umurimo wose wo mu buturo bw'inzu y'Imana.",
			"34": "<sup>34</sup> Ariko Aroni n'abahungu be batambiraga ku gicaniro cy'ibitambo byoswa no ku gicaniro cyo koserezaho imibavu, bagakora imirimo yose y'Ahera cyane, bagahongerera Abisirayeli nk'ibyo Mose umugaragu w'Imana yategetse byose.",
			"35": "<sup>35</sup> Aba ni bo bene Aroni: umuhungu we ni Eleyazari, mwene Eleyazari ni Finehasi, mwene Finehasi ni Abishuwa,",
			"36": "<sup>36</sup> mwene Abishuwa ni Buki, mwene Buki ni Uzi, mwene Uzi ni Zerahiya,",
			"37": "<sup>37</sup> mwene Zerahiya ni Merayoti, mwene Merayoti ni Amariya, mwene Amariya ni Ahitubu,",
			"38": "<sup>38</sup> mwene Ahitubu ni Sadoki, mwene Sadoki ni Ahimāzi.",
			"39": "<sup>39</sup> Kandi izi ni zo nturo bagabanijwe zo kubamo uko ingando zabo zari ziri: bene Aroni bo mu mazu y'Abakohati (kuko umugabane wa mbere wari uwabo),",
			"40": "<sup>40</sup> babaha i Heburoni mu gihugu cy'i Buyuda n'ibikingi byaho by'impande zose,",
			"41": "<sup>41</sup> ariko imirima yo ku mudugudu n'ibirorero byawo babiha Kalebu mwene Yefune.",
			"42": "<sup>42</sup> Kandi bene Aroni babaha imidugudu y'ubuhungiro, i Heburoni n'i Libuna n'ibikingi byaho, n'i Yatiri na Eshitemowa n'ibikingi byaho,",
			"43": "<sup>43</sup> n'i Hileni n'ibikingi byaho, n'i Debira n'ibikingi byaho,",
			"44": "<sup>44</sup> na Ashani n'ibikingi byaho, n'i Betishemeshi n'ibikingi byaho,",
			"45": "<sup>45</sup> n'iyo mu muryango w'Ababenyamini, i Geba n'ibikingi byaho, na Alemeti n'ibikingi byaho, na Anatoti n'ibikingi byaho. Imidugudu yabo yose uko yagabanijwe mu miryango yabo, yari cumi n'itatu.",
			"46": "<sup>46</sup> Kandi bene Kohati bandi bagabanirizwa umugabane mu mazu y'umuryango wa Efurayimu, no mu gice cy'umuryango wa Manase. Yose ni imidugudu icumi.",
			"47": "<sup>47</sup> Bene Gerushomu nk'uko imbyaro zabo zari ziri, bahabwa imidugudu cumi n'itatu itanzwe n'umuryango wa Isakari n'uwa Asheri, n'uwa Nafutali n'uwa Manase i Bashani.",
			"48": "<sup>48</sup> Bene Merari nk'uko imbyaro zabo zari ziri, bagabanirizwa imigabane mu muryango wa Rubeni n'uwa Gadi n'uwa Zebuluni, imidugudu cumi n'ibiri.",
			"49": "<sup>49</sup> Abisirayeli baha Abalewi iyo midugudu n'ibikingi byayo.",
			"50": "<sup>50</sup> Nuko batanga imigabane y'imidugudu yo mu muryango w'Abayuda, n'iyo mu muryango w'Abasimeyoni, n'iyo mu muryango w'Ababenyamini, ari yo iyo ivuzwe mu mazina.",
			"51": "<sup>51</sup> Kandi bamwe bo mu mazu ya bene Kohati, bari bafite imidugudu n'ingabano zayo yo mu muryango wa Efurayimu.",
			"52": "<sup>52</sup> Babaha n'imidugudu y'ubuhungiro: i Shekemu mu gihugu cy'imisozi ya Efurayimu n'ibikingi byaho, n'i Gezeri n'ibikingi byaho,",
			"53": "<sup>53</sup> n'i Yokimeyamu n'ibikingi byaho, n'i Betihoroni n'ibikingi byaho,",
			"54": "<sup>54</sup> na Ayaloni n'ibikingi byaho, n'i Gatirimoni n'ibikingi byaho.",
			"55": "<sup>55</sup> Kandi n'iyo mu gice cy'umuryango wa Manase: Aneri n'ibikingi byaho, n'i Bileyamu n'ibikingi byaho, ngo ibe iy'abasigaye b'inzu ya bene Kohati.",
			"56": "<sup>56</sup> Bene Gerushomu bahabwa imidugudu yo mu midugudu y'inzu y'igice cy'umuryango wa Manase: i Golani muri Bashani n'ibikingi byaho, na Ashitaroti n'ibikingi byaho,",
			"57": "<sup>57</sup> n'iyo mu muryango wa Isakari: i Kedeshi n'ibikingi byaho, n'i Daberati n'ibikingi byaho,",
			"58": "<sup>58</sup> n'i Ramoti n'ibikingi byaho, na Anemu n'ibikingi byaho,",
			"59": "<sup>59</sup> n'iyo mu muryango wa Asheri: i Mashali n'ibikingi byaho, na Abudoni n'ibikingi byaho,",
			"60": "<sup>60</sup> n'i Hukoki n'ibikingi byaho, n'i Rehobu n'ibikingi byaho,",
			"61": "<sup>61</sup> n'iyo mu muryango wa Nafutali: i Kedeshi y'i Galilaya n'ibikingi byaho, n'i Hamoni n'ibikingi byaho, n'i Kiriyatayimu n'ibikingi byaho.",
			"62": "<sup>62</sup> Abandi Balewi bene Merari bahawe imidugudu yo mu muryango wa Zebuluni: i Rimoni n'ibikingi byaho, n'i Tabora n'ibikingi byaho,",
			"63": "<sup>63</sup> kandi hakurya ya Yorodani iburasirazuba hateganye n'i Yeriko, bahawe imidugudu yo mu muryango wa Rubeni: i Beseri ho mu butayu n'ibikingi byaho, n'i Yahazi n'ibikingi byaho,",
			"64": "<sup>64</sup> n'i Kedemoti n'ibikingi byaho, n'i Mefāti n'ibikingi byaho,",
			"65": "<sup>65</sup> n'iyo mu muryango wa Gadi: i Ramoti y'i Galeyadi n'ibikingi byaho, n'i Mahanayimu n'ibikingi byaho,",
			"66": "<sup>66</sup> n'i Heshiboni n'ibikingi byaho, n'i Yazeri n'ibikingi byaho."
		},
		"7": {
			"1": "<sup>1</sup> Bene Isakari ni Tola na Puwa, na Yashubu na Shimuroni, ni bane.",
			"2": "<sup>2</sup> Bene Tola ni Uzi na Refaya na Yeriyeli, na Yahumayi na Ibusamu na Shemweli. Abatware b'inzu ya sekuruza Tola bari abagabo b'abanyambaraga b'intwari mu bihe byabo, ku ngoma ya Dawidi umubare wabo bari inzovu ebyiri n'ibihumbi bibiri na magana atandatu.",
			"3": "<sup>3</sup> Bene Uzi ni Izurahiya, bene Izurahiya ni Mikayeli na Obadiya, na Yoweli na Ishiya. Uko ari batanu bose bari abatware.",
			"4": "<sup>4</sup> Kandi muri bo, uko kuvuka kwabo kwari kuri ukurikije amazu ya ba sekuruza, harimo imitwe y'ingabo zo kurwana intambara zose zari inzovu eshatu n'ibihumbi bitandatu, kuko bari bafite abagore benshi n'abana b'abahungu benshi.",
			"5": "<sup>5</sup> Kandi bene wabo bo mu miryango yose ya Abisakari, abagabo b'abanyambaraga b'intwari uko banditswe mu gitabo cyo kuvuka kwabo, bari inzovu munani n'ibihumbi birindwi.",
			"6": "<sup>6</sup> Bene Benyamini ni Bela na Bekeri na Yediyayeli, ni batatu.",
			"7": "<sup>7</sup> Bene Bela ni Esiboni na Uzi na Uziyeli, na Yerimoti na Iri, ni batanu. Abatware b'amazu ya ba sekuruza bari abagabo b'abanyambaraga b'intwari, babazwe uko kuvuka kwabo kwari kuri, baba inzovu ebyiri n'ibihumbi bibiri na mirongo itatu na bane.",
			"8": "<sup>8</sup> Bene Bekeri ni Zimira na Yowasi na Eliyezeri, na Eliyowenayi na Omuri na Yerimoti, na Abiya na Anatoti na Alemeti. Abo bose bari bene Bekeri.",
			"9": "<sup>9</sup> Babazwe uko kuvuka kwabo kwari kuri bakurikirana, abatware b'amazu ya ba sekuruza babo bari abagabo b'abanyambaraga b'intwari inzovu ebyiri na magana abiri.",
			"10": "<sup>10</sup> Nwene Yediyayeli ni Biluhani, bene Biluhani ni Yewushi na Benyamini na Ehudi na Kenāna, na Zetani na Tarushishi na Ahishahari.",
			"11": "<sup>11</sup> Abo bose bari bene Yediyayeli, uko abatware b'amazu ya ba sekuruza babo bari bari. Bari abagabo b'abanyambaraga b'intwari inzovu imwe n'ibihumbi birindwi na magana abiri babashaga gutabara.",
			"12": "<sup>12</sup> Shupimu na Hupimu bari bene Iri, Hushimu yari mwene Aheri.",
			"13": "<sup>13</sup> Bene Nafutali ni Yahisēli na Guni na Yeseri na Shalumu, abahungu ba Biluha.",
			"14": "<sup>14</sup> Bene Manase ni Asiriyeli wabyawe n'umugore we, (inshoreke ye Umunyaramukazi ni yo yabyaye Makiri se wa Gileyadi.",
			"15": "<sup>15</sup> Makiri arongora umugore kwa Hupimu na Shupimu, murumuna we yari Māka). Umuhungu we wa kabiri witwaga Selofehadi, Selofehadi yabyaye abakobwa.",
			"16": "<sup>16</sup> Māka muka Makiri abyara umwana w'umuhungu amwita Pereshi, murumuna we yitwaga Shereshi kandi n'abahungu be ni Ulamu na Rakemu.",
			"17": "<sup>17</sup> Mwene Ulamu ni Bedani. Abo ni bo bari bene Gileyadi mwene Makiri mwene Manase.",
			"18": "<sup>18</sup> Mushiki we Hamoleketi abyara Ishihodi na Abiyezeri na Mahila.",
			"19": "<sup>19</sup> Bene Shemida ni Ahiyani na Shekemu, na Likuhi na Aniyamu.",
			"20": "<sup>20</sup> Bene Efurayimu ni Shutela, mwene Shutela ni Beredi, mwene Beredi ni Tahati, mwene Tahati ni Eleyada, mwene Eleyada ni Tahati.",
			"21": "<sup>21</sup> Mwene Tahati ni Zabadi, mwene Zabadi ni Shutela na Ezeri na Eleyada, bishwe n'Abanyagati bavukiye muri icyo gihugu kuko bamanuwe no kunyaga inka zabo.",
			"22": "<sup>22</sup> Maze Efurayimu se amara iminsi myinshi arira, bene se baza kumumara umubabaro.",
			"23": "<sup>23</sup> Bukeye ataha ku mugore we asama inda, abyara umwana w'umuhungu amwita Beriya, kuko urubyaro rwe rwagize ibyago.",
			"24": "<sup>24</sup> (Kandi umukobwa we yitwaga Shēra, ari we wubatse i Betihoroni yo hepfo n'iyo haruguru, na Uzenishēra).",
			"25": "<sup>25</sup> Umuhungu we yari Refa na Reshefu na Tela umuhungu we, mwene Tela ni Tahani.",
			"26": "<sup>26</sup> Mwene Tahani ni Lādani, mwene Lādani ni Amihudi, mwene Amihudi ni Elishama.",
			"27": "<sup>27</sup> Mwene Elishama ni Nuni, mwene Nuni ni Yosuwa.",
			"28": "<sup>28</sup> Kandi gakondo yabo n'inturo zabo byari i Beteli n'imidugudu yaho, iburasirazuba n'i Nārani n'iburengerazuba n'i Gezeri n'imidugudu yaho, kandi n'i Shekemu n'imidugudu yaho ukageza Aza n'imidugudu yaho.",
			"29": "<sup>29</sup> Kandi no ku ngabano z'Abamanase: i Betisheyani n'imidugudu yaho, n'i Tānaki n'imidugudu yaho, n'i Megido n'imidugudu yaho, n'i Dori n'imidugudu yaho. Muri iyo midugudu, bene Yosefu mwene Isirayeli ni ho babaga.",
			"30": "<sup>30</sup> Bene Asheri ni Imuna na Ishiva na Ishivi, na Beriya na mushiki wabo Sera.",
			"31": "<sup>31</sup> Bene Beriya ni Heberi na Malikiyeli se wa Biruzawiti.",
			"32": "<sup>32</sup> Heberi yabyaye Yafuleti na Shomeri, na Hotamu na mushiki wabo Shuwa.",
			"33": "<sup>33</sup> Bene Yafuleti ni Pasaki na Bimuhali na Ashuvati. Abo ni bo bari abana ba Yafuleti.",
			"34": "<sup>34</sup> Bene Shemeri ni Ahi na Rohuga, na Yehuba na Aramu.",
			"35": "<sup>35</sup> Kandi bene Helemu murumuna we ni Sofa na Imuna, na Sheleshi na Amali.",
			"36": "<sup>36</sup> Bene Sofa ni Suwa na Haruneferi, na Shuwali na Beri na Imura,",
			"37": "<sup>37</sup> na Beseri na Hodi na Shama, na Shilusha na Yitirani na Bēra.",
			"38": "<sup>38</sup> Bene Yeteri ni Yefune na Pisipa na Ara.",
			"39": "<sup>39</sup> Bene Ula ni Ara na Haniyeli na Risiya.",
			"40": "<sup>40</sup> Abo bose bari bene Asheri n'abatware b'amazu ya ba sekuruza, abagabo batowe b'abanyambaraga b'intwari, aba mbere mu batware. Kandi umubare wabo wabazwe uko kuvuka kwabo kwari kuri ngo bakore umurimo wo kurwana intambara, bari abagabo inzovu ebyiri n'ibihumbi bitandatu."
		},
		"8": {
			"1": "<sup>1</sup> Benyamini yabyaye imfura ye Bela, n'uw'ubuheta Ashibeli, n'uwa gatatu Ahara,",
			"2": "<sup>2</sup> n'uwa kane Noha, n'uwa gatanu Rafa.",
			"3": "<sup>3</sup> Na Bela yari afite abahungu: Adari na Gera na Abihudi,",
			"4": "<sup>4</sup> na Abishuwa na Nāmani na Ahowa,",
			"5": "<sup>5</sup> na Gera na Shefufani na Huramu.",
			"6": "<sup>6</sup> Aba ni bo bene Ehudi, bari abatware b'amazu ya ba sekuruza b'abaturage b'i Geba, bukeye babajyana ho iminyago i Manahati.",
			"7": "<sup>7</sup> Kandi Nāmani na Ahiya na Gera na bo abajyana ho iminyago, kandi abyara Uza na Ahihudi.",
			"8": "<sup>8</sup> Shaharayimu abyarira abana mu gihugu cy'i Mowabu, amaze kwirukana abagore be Hushimu na Bāra.",
			"9": "<sup>9</sup> Abyarana n'umugore we Hodeshi, Yobabu na Sibiya na Mesha na Malukamu,",
			"10": "<sup>10</sup> na Yewusi na Shakiya na Miruma. Abo ni bo bari abahungu be, abatware b'amazu ya ba sekuruza.",
			"11": "<sup>11</sup> Kandi abyarana na Hushimu, Abitubu na Elipāli.",
			"12": "<sup>12</sup> Bene Elipāli ni Eberi na Mishamu na Shemedi, ari we wubatse Ono na Lodi n'imidugudu yaho,",
			"13": "<sup>13</sup> na Beriya na Shema, ari bo bari abatware b'amazu ya ba sekuruza b'abaturage ba Ayaloni birukanye abaturage b'i Gati,",
			"14": "<sup>14</sup> na Ahiyo na Shashaki na Yeremoti,",
			"15": "<sup>15</sup> na Zebadiya na Aradi na Ederi,",
			"16": "<sup>16</sup> na Mikayeli na Ishipa na Yoha bene Beriya,",
			"17": "<sup>17</sup> na Zebadiya na Meshulamu, na Hizuki na Heberi,",
			"18": "<sup>18</sup> na Ishimerayi na Izuliya, na Yobabu bene Elipāli,",
			"19": "<sup>19</sup> na Yakimu na Zikiri na Zabudi,",
			"20": "<sup>20</sup> na Eliyenayi na Siletayi na Eliyeli,",
			"21": "<sup>21</sup> na Adaya na Beraya, na Shimurati bene Shimeyi,",
			"22": "<sup>22</sup> na Ishupani na Eberi na Eliyeli,",
			"23": "<sup>23</sup> na Abudoni na Zikiri na Hanāni,",
			"24": "<sup>24</sup> na Hananiya na Elamu na Anitotiya,",
			"25": "<sup>25</sup> na Ifudeya na Penuweli, bene Shashaki,",
			"26": "<sup>26</sup> na Shamusherayi na Shehariya na Ataliya,",
			"27": "<sup>27</sup> na Yāreshiya na Eliya na Zikiri bene Yerohamu.",
			"28": "<sup>28</sup> Abo ni bo bari abatware b'amazu ya ba sekuruza kurangiza ibihe byabo byose. Bari abagabo bakomeye kandi babaga i Yerusalemu.",
			"29": "<sup>29</sup> Kandi Yeyeli se wa Gibeyoni yaturaga i Gibeyoni. Umugore we yitwaga Māka.",
			"30": "<sup>30</sup> Umuhungu we w'imfura ni Abudoni, agakurikirwa na Suri na Kishi na Bāli na Nadabu,",
			"31": "<sup>31</sup> na Gedori na Ahiyo na Zekeri.",
			"32": "<sup>32</sup> Mikuloti abyara Shimeya. Abo na bo baturanaga na bene se i Yerusalemu, bateganye.",
			"33": "<sup>33</sup> Kandi Neri abyara Kishi, Kishi abyara Sawuli, Sawuli abyara Yonatani na Malikishuwa, na Abinadabu na Eshibāli.",
			"34": "<sup>34</sup> Umuhungu wa Yonatani ni Meribāli, Meribububāli abyara Mika.",
			"35": "<sup>35</sup> Bene Mika ni Pitoni na Meleki, na Tareya na Ahazi.",
			"36": "<sup>36</sup> Ahazi abyara Yehoyada, Yehoyada abyara Alemeti na Azimaveti na Zimuri, Zimuri abyara Mosa.",
			"37": "<sup>37</sup> Mosa abyara Bineya. Umuhungu we ni Rafa, mwene Rafa ni Eleyasa, mwene Eleyasa ni Aseli.",
			"38": "<sup>38</sup> Aseli yari afite abahungu batandatu amazina yabo ni aya: Azirikamu na Bokeru na Ishimayeli, na Sheyariya na Obadiya na Hanāni. Abo bose ni bene Aseli.",
			"39": "<sup>39</sup> Bene Esheki murumuna we ni Ulamu imfura ye, uw'ubuheta ni Yewushi, uwa gatatu ni Elifeleti.",
			"40": "<sup>40</sup> Bene Ulamu bari abagabo b'abanyambaraga b'intwari b'abarashi, bari bafite abahungu benshi n'abuzukuru, bari ijana na mirongo itanu. Abo bose bari ab'abahungu ba Benyamini."
		},
		"9": {
			"1": "<sup>1</sup> Nuko Abisirayeli bose barabarwa uko kuvuka kwabo kwari kuri, kandi byanditswe mu gitabo cy'abami b'Abisirayeli. Abayuda bajyanwa ho iminyago i Babuloni ku bw'igicumuro cyabo.",
			"2": "<sup>2</sup> Ababanje gutura muri gakondo yabo mu midugudu yabo ni aba: Abisirayeli n'abatambyi, n'Abalewi n'Abanetinimu.",
			"3": "<sup>3</sup> Kandi muri Yerusalemu haturagamo bamwe b'Abayuda n'ab'Ababenyamini, n'ab'Abefurayimu n'ab'Abamanase.",
			"4": "<sup>4</sup> Utayi mwene Amihudi mwene Omuri, mwene Imuri mwene Bani wo mu bana ba Perēsi mwene Yuda.",
			"5": "<sup>5</sup> N'abo mu Banyashilo, uw'imfura ni Asaya n'abahungu be.",
			"6": "<sup>6</sup> N'abo muri bene Zera ni Yeweli, na bene wabo magana atandatu na mirongo urwenda.",
			"7": "<sup>7</sup> N'abo muri bene Benyamini ni Salu mwene Meshulamu, mwene Hodaviya mwene Hasenuwa,",
			"8": "<sup>8</sup> na Ibuneya mwene Yerohamu, na Ela mwene Uzi mwene Mikiri, na Meshulamu mwene Shefatiya, mwene Reweli, mwene Ibuniya,",
			"9": "<sup>9</sup> na bene wabo uko kuvuka kwabo kwari kuri, ni magana urwenda na mirongo itanu na batandatu. Abo bagabo bose bari abatware b'amazu ya ba sekuruza uko yari ari.",
			"10": "<sup>10</sup> Kandi abo mu batambyi ni Yedaya na Yehoyaribu na Yakini,",
			"11": "<sup>11</sup> na Azariya mwene Hilukiya, mwene Meshulamu, mwene Sadoki, mwene Merayoti, mwene Ahitubu umutware w'inzu y'Imana,",
			"12": "<sup>12</sup> na Adaya mwene Yerohamu, mwene Pashuri, mwene Malikiya, na Māsayi mwene Adiyeli, mwene Yahuzera, mwene Meshulamu, mwene Meshilemiti, mwene Imeri,",
			"13": "<sup>13</sup> na bene wabo abatware b'amazu ya ba sekuruza babo igihumbi na magana arindwi na mirongo itandatu, abagabo b'abahanga bashobora gukora umurimo w'inzu y'Imana.",
			"14": "<sup>14</sup> Kandi abo mu Balewi ni Shemaya mwene Hashubu, mwene Azirikamu, mwene Hashabiya wo muri bene Merari,",
			"15": "<sup>15</sup> na Bakibakari na Hereshi na Galali, na Mataniya mwene Mika, mwene Zikiri, mwene Asafu,",
			"16": "<sup>16</sup> na Obadiya mwene Shemaya, mwene Galali, mwene Yedutuni, na Berekiya mwene Asa, mwene Elukana baturaga mu birorero by'Abanyanetofa.",
			"17": "<sup>17</sup> Abakumirizi ni Shalumu na Akubu, na Talimoni na Ahimani na bene wabo, Shalumu ni we wari mukuru.",
			"18": "<sup>18</sup> Kera barindaga irembo ry'umwami ry'iburasirazuba, ni bo bari abakumirizi b'ingando y'Abalewi.",
			"19": "<sup>19</sup> Shalumu mwene Kore, mwene Ebiyasafu, mwene Kōra na bene wabo b'inzu ya sekuruza we. Abakōra ni bo basohozaga umurimo w'ubukumirizi w'inzugi z'ihema, kandi ba sekuruza babo ni bo bategekaga ingando y'Uwiteka bakaba abakumirizi b'irembo ryayo,",
			"20": "<sup>20</sup> na Finehasi mwene Eleyazari ari we wabategekaga kera, kandi Uwiteka yabanaga na we.",
			"21": "<sup>21</sup> Zekariya mwene Meshelemiya, ni we wari umukumirizi w'irembo ry'Ihema ry'ibonaniro.",
			"22": "<sup>22</sup> Abo bose batoranirijwe kuba abakumirizi b'amarembo bari magana abiri na cumi na babiri, kandi babarirwaga mu birorero byabo uko kuvuka kwabo kwari kuri, mu birorero byabo. Ni bo Dawidi na Samweli bamenya, bashyizeho mu mirimo yabo itegetswe.",
			"23": "<sup>23</sup> Nuko bo n'abana babo baba ku murimo wo kurinda amarembo y'inzu y'Uwiteka, ari yo nzu y'ihema, bakajya ibihe.",
			"24": "<sup>24</sup> Abakumirizi babaga ku mpande uko ari enye, iburasirazuba n'iburengerazuba n'ikasikazi n'ikusi.",
			"25": "<sup>25</sup> Kandi bene wabo bo mu birorero byabo, bari abo kujya baza uko iminsi irindwi yashiraga bakabana na bo,",
			"26": "<sup>26</sup> kuko abakumirizi bakuru bane b'Abalewi babaga ku murimo ntibakurwe, ari bo batwara ibyumba n'ububiko byo mu nzu y'Imana.",
			"27": "<sup>27</sup> Bararaga bakikije inzu y'Imana, kuko ari bo bahawe umurimo wo kuyirinda no kuyikingura uko bukeye.",
			"28": "<sup>28</sup> Kandi bamwe muri bo babitswaga ibintu bikoreshwa, kuko mu icyurwa ryabyo no mu isohorwa ryabyo byabarwaga.",
			"29": "<sup>29</sup> Kandi bamwe muri bo bashyizweho umurimo wo kurinda ibintu, n'ibintu byose by'Ahera, n'ifu y'ingezi na vino n'amavuta, n'icyome n'imibavu.",
			"30": "<sup>30</sup> Kandi bamwe mu bana b'abatambyi batunganyaga imibavu, bakayivanga.",
			"31": "<sup>31</sup> Matitiya wo mu Balewi, imfura ya Shalumu Umukōra, ni we wari ufite umurimo wo gutegeka ibintu byakarangwaga ku byuma.",
			"32": "<sup>32</sup> Kandi bamwe muri bene wabo b'Abakohati, ni bo bategekaga imitsima yo kumurikwa imbere y'Imana, bakayitegura uko isabato yatahaga.",
			"33": "<sup>33</sup> Kandi abo ni bo baririmbyi, abatware b'amazu ya ba sekuruza b'Abalewi, babaga muri ibyo byumba ntibagire undi murimo bakora, kuko bakoraga umurimo wabo ku manywa na nijoro.",
			"34": "<sup>34</sup> Abo ni bo bari abatware b'amazu ya ba sekuruza b'Abalewi, uko babyaranye, abagabo bakomeye babaga i Yerusalemu.",
			"35": "<sup>35</sup> Yeyeli se wa Gibeyoni yabaga i Gibeyoni. Umugore we yitwaga Māka.",
			"36": "<sup>36</sup> Umuhungu we w'imfura ni Abudoni, agakurikirwa na Suri na Kishi, na Bāli na Neri na Nadabu,",
			"37": "<sup>37</sup> na Gedori na Ahiyo, na Zekariya na Mikuloti,",
			"38": "<sup>38</sup> Mikuloti abyara Shimeyamu. Abo na bo baturana na bene wabo i Yerusalemu, bateganye.",
			"39": "<sup>39</sup> Neri abyara Kishi, Kishi abyara Sawuli. Sawuli abyara Yonatani na Malikishuwa, na Abinadabu na Eshibāli.",
			"40": "<sup>40</sup> Mwene Yonatani ni Meribubāli, Meribubāli abyara Mika.",
			"41": "<sup>41</sup> Bene Mika ni Pitoni na Meleki, na Tahureya na Ahazi.",
			"42": "<sup>42</sup> Ahazi abyara Yara, Yara abyara Alemeti na Azimaveti na Zimuri, Zimuri abyara Mosa.",
			"43": "<sup>43</sup> Mosa abyara Bineya, mwene Bineya ni Refaya, mwene Refaya ni Eleyasa, mwene Eleyasa ni Aseli.",
			"44": "<sup>44</sup> Aseli yari afite abahungu batandatu, amazina yabo ni aya: Azirikamu na Bokeru na Ishimayeli, na Sheyariya na Obadiya na Hanāni. Abo ni bo bene Aseli."
		},
		"10": {
			"1": "<sup>1</sup> Hariho ubwo Abafilisitiya barwanije Abisirayeli, Abisirayeli barabahunga batsindirwa ku musozi w'i Gilibowa.",
			"2": "<sup>2</sup> Abafilisitiya basendekereza Sawuli n'abahungu be, bica Yonatani na Abinadabu na Malikishuwa bene Sawuli.",
			"3": "<sup>3</sup> Intambara isumbiriza Sawuli, abarashi bamugeraho ariheba cyane.",
			"4": "<sup>4</sup> Sawuli ni ko kubwira umutwaje intwaro ati “Kura inkota yawe uyinsogote, bariya batakebwe bataza banshinyagurira.” Ariko umutwaje intwaro aranga kuko yatinye cyane, ni cyo cyatumye Sawuli yenda inkota ye akayishitaho.",
			"5": "<sup>5</sup> Umutwaje intwaro abonye ko Sawuli apfuye, na we yishita ku ye nkota arapfa.",
			"6": "<sup>6</sup> Uko ni ko Sawuli yatanze, n'abahungu be uko ari batatu, n'abo mu nzu ye bose bagwa hamwe.",
			"7": "<sup>7</sup> Maze Abisirayeli bari mu kibaya bose babonye ko abandi bahunze, kandi ko Sawuli n'abahungu be bapfuye, bata imidugudu yabo barahunga.",
			"8": "<sup>8</sup> Abafilisitiya baraza bayibamo. Bukeye bwaho Abafilisitiya baje gucuza abapfu, basanga Sawuli n'abahungu be baraguye ku musozi w'i Gilibowa.",
			"9": "<sup>9</sup> Baramucuza bajyana igihanga cye n'intwaro ze, batuma mu gihugu cy'Abafilisitiya gihereranye na ho, ngo bazamamaze izo nkuru ku bigirwamana byabo no mu bantu babo.",
			"10": "<sup>10</sup> Intwaro ze bazishyira mu ngoro y'imana zabo, igihanga cye bakimanika mu ngoro ya Dagoni.",
			"11": "<sup>11</sup> Nuko ab'i Yabeshi y'i Galeyadi bose, bumvise ibyo Abafilisitiya bagiriye Sawuli byose,",
			"12": "<sup>12</sup> ab'intwari bose barahaguruka, bakurayo intumbi ya Sawuli n'intumbi z'abahungu be bazizana i Yabeshi, bahamba amagufwa yabo munsi y'umwela w'i Yabeshi, baherako biyiriza ubusa iminsi irindwi.",
			"13": "<sup>13</sup> Uko ni ko Sawuli yatanze azize gucumura yacumuye ku Uwiteka, kuko atitondeye ijambo ry'Uwiteka, kandi kuko yashikishije umushitsi ngo amuhanuze,",
			"14": "<sup>14</sup> ntahanuze Uwiteka. Ni cyo cyatumye amutangisha, ubwami akabugabira Dawidi mwene Yesayi."
		},
		"11": {
			"1": "<sup>1</sup> Bukeye Abisirayeli bose bateranira aho Dawidi yari ari i Heburoni baravuga bati “Umva turi amaraso yawe, turi ubura bwawe.",
			"2": "<sup>2</sup> Mu bihe bya kera ubwo Sawuli yari umwami, ni wowe watabazaga Abisirayeli ukabataburura, kandi Uwiteka Imana yawe yarakubwiye iti ‘Ni wowe uzaragira ubwoko bwanjye bwa Isirayeli’. Kandi iti ‘Uzaba umugaba w'ubwoko bwanjye bwa Isirayeli.’ ”",
			"3": "<sup>3</sup> Nuko abakuru ba Isirayeli bose basanga umwami i Heburoni, ahasezeranira na bo imbere y'Uwiteka isezerano, bimikisha Dawidi amavuta ngo abe umwami wa Isirayeli, nk'uko Uwiteka yavugiye muri Samweli.",
			"4": "<sup>4</sup> Hanyuma Dawidi n'Abisirayeli bose bajya i Yerusalemu (ahitwa i Yebusi), kandi Abayebusi ni bo bari abaturage bo muri icyo gihugu bari basanzwemo.",
			"5": "<sup>5</sup> Ab'i Yebusi babwira Dawidi bati “Hano ntabwo uzahamenera.” Ariko Dawidi ahindūra igihome gikomeye cya Siyoni, ari ho habaye ururembo rwa Dawidi.",
			"6": "<sup>6</sup> Ubwo Dawidi yaravuze ati “Uri bubanze kunesha Abayebusi, ni we uzaba umutware n'umugaba”. Maze Yowabu mwene Seruya aba ari we ubanza kuhamenera, aba umugaba w'ingabo.",
			"7": "<sup>7</sup> Dawidi atura muri icyo gihome gikomeye, ari cyo cyatumye bacyita ururembo rwa Dawidi.",
			"8": "<sup>8</sup> Yubaka umudugudu impande zarwo zose, uhereye i Milo n'impande zose. Yowabu na we asana ibindi bice by'umudugudu bisigaye.",
			"9": "<sup>9</sup> Maze Dawidi akajya arushaho gukomera, kuko Uwiteka Nyiringabo yari kumwe na we.",
			"10": "<sup>10</sup> Aba ni bo bari bakomeye mu ntwari Dawidi yari afite, ni bo bafatanije na we kwerekana imbaraga zabo mu bwami bwe, bagafatanya n'Abisirayeli bose kumwimika nk'uko Uwiteka yavuze ku Bisirayeli.",
			"11": "<sup>11</sup> Ngaba abagabo b'intwari Dawidi yari afite, uko umubare wabo wari uri: Yashobeyamu umwana w'Umuhakimoni, umutware w'ab'intore mirongo itatu. Yabanguye icumu rye kurwanya abantu magana atatu, abicira icyarimwe.",
			"12": "<sup>12</sup> Akurikirwa na Eleyazari mwene Dodo w'Umwahohi, yari uwo muri abo bagabo b'intwari batatu.",
			"13": "<sup>13</sup> Uwo yari kumwe na Dawidi i Pasidamimu, ubwo Abafilisitiya bari bateraniyeyo ngo barwane, maze abantu bahunga Abafilisitiya. Aho hari umurima warimo sayiri nyinshi.",
			"14": "<sup>14</sup> Ariko bo bahagarara hagati muri uwo murima, barawurinda bica abo Bafilisitiya, Uwiteka abakirisha kunesha gukomeye.",
			"15": "<sup>15</sup> Abatatu kuri ba bandi mirongo itatu baramanuka basanga Dawidi ku rutare mu buvumo bwa Adulamu. Ingabo z'Abafilisitiya zari zigerereje mu kibaya cy'Abarafa.",
			"16": "<sup>16</sup> Ubwo Dawidi yari mu gihome, kandi abanyagihome cy'Abafilisitiya bari i Betelehemu.",
			"17": "<sup>17</sup> Dawidi akumbuye aravuga ati “Icyampa nkabona unsomya ku mazi y'iriba ryo ku irembo ry'i Betelehemu.”",
			"18": "<sup>18</sup> Maze abo batatu batwaranira mu ngabo z'Abafilisitiya, bavoma amazi mu iriba ryo ku irembo ry'i Betelehemu, barayajyana bayashyira Dawidi. Ariko Dawidi yanga kuyanywaho, ahubwo ayabyarira imbere y'Uwiteka.",
			"19": "<sup>19</sup> Aravuga ati “Imana yanjye indinde kuba nakora ntyo! Ndebe nywe amaraso y'aba bagabo bahaze amagara yabo!” Kuko bayazanye bihaze, ni cyo cyatumye yanga kuyanywa. Ibyo ni byo byakozwe n'abo bagabo b'intwari uko ari batatu.",
			"20": "<sup>20</sup> Abishayi murumuna wa Yowabu ni we wari umutware w'abatatu, kuko yabanguye icumu rye kurwana n'abantu magana atatu akabica, akamamara muri abo batatu.",
			"21": "<sup>21</sup> Muri abo batatu ni we wari ufite icyubahiro kurusha abandi babiri, ahinduka umutware wabo. Ariko ntiyahwanye n'abatatu ba mbere.",
			"22": "<sup>22</sup> Benaya mwene Yehoyada umwana w'umugabo w'intwari w'i Kabusēli, yari yarakoze ibikomeye. Ni we wishe bene Ariyeli w'i Mowabu bombi, kandi aramanuka yicira intare mu isenga mu gihe cya shelegi.",
			"23": "<sup>23</sup> Yishe n'umugabo w'Umunyegiputa muremure cyane, uburebure bwe bwari mikono itanu. Uwo Munyegiputa yari yitwaje injunga y'icumu, ariko we amanuka yitwaje inkoni, aramusanga amushikuza icumu mu kuboko kwe ararimwicisha.",
			"24": "<sup>24</sup> Ibyo ni byo Benaya mwene Yehoyada yakoze, yamamara muri abo bagabo batatu b'intwari.",
			"25": "<sup>25</sup> Yari afite icyubahiro kuruta ba bandi mirongo itatu, ariko ntiyari ahwanye na batatu ba mbere. Dawidi amugira umutware w'abarinzi be.",
			"26": "<sup>26</sup> Kandi abagabo b'abanyambaraga bo mu ngabo ze ni Asaheli murumuna wa Yowabu, na Eluhanani mwene Dodo w'i Betelehemu,",
			"27": "<sup>27</sup> na Shamoti w'Umuharari na Helesi w'Umupeloni,",
			"28": "<sup>28</sup> na Ira mwene Ikeshi w'Umunyatekowa, na Abiyezeri w'Umunyanatoti,",
			"29": "<sup>29</sup> na Sibekayi w'Umuhusha na Ilayi w'Umwahohi,",
			"30": "<sup>30</sup> na Maharayi w'Umunyanetofa, na Heledu mwene Bāna Umunyanetofa,",
			"31": "<sup>31</sup> na Itayi mwene Ribayi w'i Gibeya y'Ababenyamini, na Benaya w'Umunyapiratoni,",
			"32": "<sup>32</sup> na Hurayi wo ku tugezi tw'i Gāshi na Abiyeli w'Umunyaruba,",
			"33": "<sup>33</sup> na Azimaveti w'Umunyabahurimu, na Eliyahaba w'i Shālabini.",
			"34": "<sup>34</sup> Bene Hashemu w'Umugizoni ni Yonatani mwene Shage w'Umuharari,",
			"35": "<sup>35</sup> na Ahiyamu mwene Sakari w'Umuharari na Elifali mwene Uri,",
			"36": "<sup>36</sup> na Heferi w'Umumekerati na Ahiya w'Umupeloni,",
			"37": "<sup>37</sup> na Hesero w'Umunyakarumeli na Nārayi mwene Ezubayi,",
			"38": "<sup>38</sup> na Yoweli murumuna wa Natani, na Mibuhari mwene Hagiri,",
			"39": "<sup>39</sup> na Seleki w'Umwamoni, na Naharayi w'i Bēroti utwara intwaro za Yowabu mwene Seruya,",
			"40": "<sup>40</sup> na Ira w'Umuyeteri na Garebu w'Umuyeteri,",
			"41": "<sup>41</sup> na Uriya w'Umuheti na Zabadi mwene Ahilayi,",
			"42": "<sup>42</sup> na Adina mwene Shiza w'Umurubeni, umutware w'Abarubeni n'abantu mirongo itatu hamwe na we,",
			"43": "<sup>43</sup> na Hanāni mwene Māka na Yoshafati w'Umumituni,",
			"44": "<sup>44</sup> na Uziya w'Umwashitaroti, na Shama na Yeyeli bene Hotamu w'Umunyaroweri,",
			"45": "<sup>45</sup> na Yediyayeli mwene Shimuri na murumuna we Yoha w'Umutisi,",
			"46": "<sup>46</sup> na Eliyeli w'Umumahavi, na Yeribayi na Yoshaviya bene Elunāmu na Ituma w'Umumowabu,",
			"47": "<sup>47</sup> na Eliyeli na Obedi na Yāsiyeli w'Umumesoba."
		},
		"12": {
			"1": "<sup>1</sup> Aba ni bo basanze Dawidi i Sikulagi, ubwo yari acyihishe Sawuli mwene Kishi, bari muri za ntwari zamutabaraga mu ntambara.",
			"2": "<sup>2</sup> Batwaraga imiheto bakabasha gutwarira amaboko yombi, bateresha amabuye imihumetso, barashisha n'imyambi imiheto. Bari bene wabo wa Sawuli bo mu Babenyamini.",
			"3": "<sup>3</sup> Umukuru ni Ahiyezeri, agakurikirwa na Yowasi ari bo bene Shemaya w'i Gibeya, na Yeziyeli na Peleti bene Azimaveti, na Beraka na Yehu w'Umunyanatoti.",
			"4": "<sup>4</sup> Ishimaya w'i Gibeyoni yari umunyambaraga muri abo mirongo itatu, ari we wari umutware muri bo,",
			"5": "<sup>5</sup> na Yeremiya na Yahaziyeli na Yohanani, na Yozabadi w'i Gedera,",
			"6": "<sup>6</sup> na Eluzayi na Yerimoti na Beyaliya, na Shemariya na Shefatiya w'i Harufu,",
			"7": "<sup>7</sup> na Elukana na Ishiya na Azarēli, na Yowezeri na Yoshobeyamu b'Abakōra,",
			"8": "<sup>8</sup> na Yowela na Zebadiya bene Yerohamu w'i Gedori.",
			"9": "<sup>9</sup> Kandi no mu Bagadi hirobanuramo abagabo b'abanyambaraga b'intwari bigishijwe kurwana, babasha gutwara ingabo n'icumu. Amaso yabo yasaga n'ay'intare, bari bafite umuvumbuko nk'uw'impara ziri ku gasozi, basanga Dawidi mu gihome cyo mu butayu.",
			"10": "<sup>10</sup> Umukuru ni Ezeri, uwa kabiri ni Obadiya, uwa gatatu ni Eliyabu,",
			"11": "<sup>11</sup> uwa kane ni Mishumana, uwa gatanu ni Yeremiya,",
			"12": "<sup>12</sup> uwa gatandatu ni Atayi, uwa karindwi ni Eliyeli,",
			"13": "<sup>13</sup> uwa munani ni Yohanani, uwa cyenda ni Elizabadi,",
			"14": "<sup>14</sup> uwa cumi ni Yeremiya, uwa cumi n'umwe ni Makubanayi.",
			"15": "<sup>15</sup> Abo Bagadi bari abatware b'ingabo, umutoya muri bo yari ahwanye n'abantu ijana, umukuru agahwana n'igihumbi.",
			"16": "<sup>16</sup> Abo ni bambutse Yorodani mu kwezi kwa mbere, Yorodani yuzuye irenze inkombe zose birukana abo mu bibaya bose, ab'iburasirazuba n'ab'iburengerazuba.",
			"17": "<sup>17</sup> Bukeye Ababenyamini n'Abayuda baraza, basanga Dawidi mu gihome.",
			"18": "<sup>18</sup> Dawidi asohorwa no kubasanganira arababwira ati “Niba muzanywe no kuntabara munzaniye amahoro, umutima wanjye n'iyanyu bizaba kimwe. Ariko niba muzanywe no kungambanira ku banzi banjye kandi nta rugomo nagize, Imana ya ba sogokuruza banyu ibirebe ibihane.”",
			"19": "<sup>19</sup> Maze umwuka aherako aza kuri Amasa, umutware wa ba bandi mirongo itatu aravuga ati “Turi abawe Dawidi, turi mu ruhande rwawe mwana wa Yesayi. Amahoro abe kuri wowe no ku bagutabara, kuko Imana yawe igutabara.” Dawidi aherako arabakira, abagira abatware b'umutwe.",
			"20": "<sup>20</sup> Kandi no mu Bamanase himūramo bamwe bakeza Dawidi, ubwo yajyanaga n'Abafilisitiya bateye Sawuli. Ariko ntibabavuna kuko abatware b'Abafilisitiya bamaze kujya inama bakamwirukana bagira bati “Azakeza shebuja Sawuli, ashyire imitwe yacu mu kaga.”",
			"21": "<sup>21</sup> Ubwo yajyaga i Sikulagi, akezwa na bamwe bo mu Bamanase, Aduna na Yozabadi na Yediyeli na Mikayeli, na Yozabadi na Elihu na Siletayi, abatware b'ibihumbi by'Abamanase.",
			"22": "<sup>22</sup> Batabara Dawidi barwanya wa mutwe w'abanyazi, bose bari abagabo b'abanyambaraga b'intwari kandi bari abatware b'ingabo.",
			"23": "<sup>23</sup> Nuko uko bukeye hakajya haza abantu batabara Dawidi, kugeza ubwo babaye ingabo nyinshi nk'ingabo z'Imana.",
			"24": "<sup>24</sup> Kandi iyi ni yo mibare y'imitwe y'ingabo zasanze Dawidi i Heburoni zitwaje intwaro, ngo bamuhindūrire ubwami bwa Sawuli nk'uko ijambo ry'Uwiteka ryari riri.",
			"25": "<sup>25</sup> Abayuda bitwazaga amacumu n'ingabo, bari ibihumbi bitandatu na magana inani biteguye kurwana.",
			"26": "<sup>26</sup> Abo mu Basimeyoni, abagabo b'abanyambaraga b'intwari mu ntambara, bari ibihumbi birindwi n'ijana.",
			"27": "<sup>27</sup> Abo mu Balewi na bo bari ibihumbi bine na magana atandatu.",
			"28": "<sup>28</sup> Kandi Yehoyada yari umutware w'inzu ya Aroni, ajyana n'ingabo ibihumbi bitatu na magana arindwi,",
			"29": "<sup>29</sup> na Sadoki umusore w'imbaraga w'intwari n'abo mu nzu ya se, abatware makumyabiri na babiri.",
			"30": "<sup>30</sup> Abo mu Babenyamini bene wabo wa Sawuli bari ibihumbi bitatu, kuko kugeza muri icyo gihe abarutaga ubwinshi muri bo bari bakomereye ku nzu ya Sawuli.",
			"31": "<sup>31</sup> Abo mu Befurayimu, abagabo b'abanyambaraga b'intwari b'ibirangirire mu mazu ya ba sekuruza, bari inzovu ebyiri na magana inani.",
			"32": "<sup>32</sup> Ab'igice cy'umuryango wa Manase bari inzovu imwe n'ibihumbi munani, abavuzwe mu mazina ngo baze bimike Dawidi.",
			"33": "<sup>33</sup> Abo mu Bisakari b'abanyabwenge bwo kumenya ibihe no kumenya ibyo Abisirayeli bakwiriye gukora, abatware babo bari magana abiri kandi bene wabo bose bumviraga itegeko ryabo.",
			"34": "<sup>34</sup> Abo mu Bazebuluni babashaga kujyana n'ingabo bakarema inteko, bari bafite intwaro z'intambara z'uburyo bwose batari abanyamitima ibiri, bari inzovu eshanu.",
			"35": "<sup>35</sup> Abo mu Banafutali, abatware igihumbi bari kumwe n'abitwaje amacumu n'ingabo, bari inzovu eshatu n'ibihumbi birindwi.",
			"36": "<sup>36</sup> Abo mu Badani ababashije kurema inteko, bari inzovu ebyiri n'ibihumbi munani na magana atandatu.",
			"37": "<sup>37</sup> Abo mu Bashēri babashaga kujya mu rugamba bakirema inteko, bari inzovu enye.",
			"38": "<sup>38</sup> N'abo hakurya ya Yorodani, Abarubeni n'Abagadi n'ab'igice cy'umuryango wa Manase bafite intwaro z'intambara z'uburyo bwose, bari agahumbi n'inzovu ebyiri.",
			"39": "<sup>39</sup> Izo ngabo zose z'abantu babashaga kwirema inteko, bazanywe i Heburoni bafite umutima utunganye no kwimika Dawidi ngo abe umwami wa Isirayeli yose, kandi n'abandi Bisirayeli bose bari bahuje umutima wo kwimika Dawidi.",
			"40": "<sup>40</sup> Basibirayo gatatu bari kumwe na Dawidi barya banywa, kuko bene wabo bari babahishiye.",
			"41": "<sup>41</sup> Kandi n'abari batuye bugufi bwaho ndetse ukageza Isakari n'i Zebuluni n'i Nafutali, bazanye imitsima ku ndogobe n'ingamiya n'inyumbu no ku nka, n'ibyokurya by'amafu n'imibumbe y'imbuto z'umutini n'amasere y'inzabibu zumye, na vino n'amavuta, n'inka n'intama byinshi cyane kuko mu Isirayeli harimo ibyishimo."
		},
		"13": {
			"1": "<sup>1</sup> Nuko Dawidi ajya inama n'abatware batwara ibihumbi, n'abatwara amagana n'abandi batware bose.",
			"2": "<sup>2</sup> Dawidi abwira iteraniro ry'Abisirayeli ryose ati “Ibi niba mubishima kandi niba bivuye ku Uwiteka Imana yacu, dutume hose kuri bene wacu basigaye mu gihugu cya Isirayeli cyose, no ku batambyi n'Abalewi bari mu midugudu yabo n'ibikingi byabo, kugira ngo bateranire aho turi.",
			"3": "<sup>3</sup> Tujye kugarura isanduku y'Imana yacu muri twe, kuko tutayitayeho ku ngoma ya Sawuli.”",
			"4": "<sup>4</sup> Abahateraniye bose bemera ko bagiye gukora batyo, kuko iyo nama abantu bose bayishimye.",
			"5": "<sup>5</sup> Nuko Dawidi ateranya Abisirayeli bose, uhereye i Shihori ku kagezi ka Egiputa ukageza aharasukirwa i Hamati, kugira ngo bajye kwenda isanduku y'Imana bayikure i Kiriyatiyeyarimu.",
			"6": "<sup>6</sup> Dawidi azamukana n'Abisirayeli bose bajya i Bāla, ari yo Kiriyatiyeyarimu y'Abayuda, kugira ngo bakureyo isanduku y'Uwiteka Imana, ari yo yicara ku Bakerubi ikitwa rya Zina.",
			"7": "<sup>7</sup> Maze baheka isanduku y'Imana ku igare rishya, bayikura mu nzu ya Abinadabu, Uza na Ahiyo barayishorera.",
			"8": "<sup>8</sup> Nuko Dawidi n'Abisirayeli bose biyerekera imbere y'Imana n'imbaraga zabo zose, baririmba bacuranga inanga na nebelu n'amashako n'ibyuma bivuga, bavuza n'amakondera.",
			"9": "<sup>9</sup> Bageze ku mbuga ya Kidoni, Uza arambura ukuboko kuramira isanduku, kuko inka zari zitsikiye.",
			"10": "<sup>10</sup> Uburakari bw'Uwiteka bugurumanira kuri Uza aramwica, kuko yaramburiye ukuboko isanduku, agwa aho ngaho imbere y'Imana.",
			"11": "<sup>11</sup> Dawidi ababazwa n'uko Uwiteka asumiye Uza. Aho hantu ahahimba Peresuza na bugingo n'ubu.",
			"12": "<sup>12</sup> Uwo munsi Dawidi atinya Imana aravuga ati “Najyana nte isanduku y'Imana imuhira iwanjye?”",
			"13": "<sup>13</sup> Nuko Dawidi ntiyacumbukura iyo sanduku ngo ayicyure iwe mu rurembo rwa Dawidi, ahubwo ayinyuza hirya ayicyura mu rugo rwa Obededomu w'Umugiti.",
			"14": "<sup>14</sup> Isanduku y'Uwiteka imara amezi atatu mu bo kwa Obededomu, iri mu rugo rwe. Uwiteka aha umugisha urugo rwa Obededomu n'ibyo yari afite byose."
		},
		"14": {
			"1": "<sup>1</sup> Hiramu umwami w'i Tiro yohereza intumwa kuri Dawidi, n'ibiti by'imyerezi n'abazi kubakisha amabuye n'ababaji, ngo bamwubakire inzu.",
			"2": "<sup>2</sup> Nuko Dawidi amenya ko Uwiteka amukomereje ubwami bwa Isirayeli ngo bube ubwe, kuko ubwami bwe bwashyiriwe hejuru ku bw'ubwoko bwe bw'Abisirayeli.",
			"3": "<sup>3</sup> Bukeye Dawidi yongera kurongora abandi bagore ari i Yerusalemu, yongera kubyara abana b'abahungu n'abakobwa.",
			"4": "<sup>4</sup> Kandi aya ni yo mazina y'abana yabyariye i Yerusalemu: Shamuwa na Shobabu na Natani na Salomo,",
			"5": "<sup>5</sup> na Ibuhari na Elishuwa na Elifeleti,",
			"6": "<sup>6</sup> na Noga na Nefegi na Yafiya,",
			"7": "<sup>7</sup> na Elishama na Bēliyada na Elifeleti.",
			"8": "<sup>8</sup> Abafilisitiya bumvise ko Dawidi yimikishijwe amavuta ngo abe umwami wa Isirayeli yose, Abafilisitiya bose bazamurwa no kumutera. Dawidi abyumvise arabasanganira.",
			"9": "<sup>9</sup> Icyo gihe Abafilisitiya baraje buzura ikibaya cy'Abarafa.",
			"10": "<sup>10</sup> Dawidi agisha Imana inama ati “Ntere Abafilisitiya uzabangabiza?” Uwiteka aramusubiza ati “Genda kuko nzabakugabiza.”",
			"11": "<sup>11</sup> Nuko bazamukira i Bāliperasimu, Dawidi abatsindayo. Dawidi aherako aravuga ati “Imana yahomboje abanzi banjye ukuboko kwanjye nk'uko amazi ahomboka.” Ni cyo cyatumye aho hantu bahahimba Bāliperasimu.",
			"12": "<sup>12</sup> Kandi Abafilisitiya batayo ibigirwamana byabo, Dawidi ategeka ko babitwika.",
			"13": "<sup>13</sup> Bukeye Abafilisitiya bongera kuzura cya kibaya.",
			"14": "<sup>14</sup> Dawidi yongera kugisha Imana inama. Imana iramubwira iti “We gutabara ubakurikiye, ahubwo ubace rubete ubarasukireho ahateganye n'ishyamba ry'imitugunguru.",
			"15": "<sup>15</sup> Nuko niwumva ikiriri kirangīra hejuru y'ubushorishori bw'imitugunguru, uhereko utabare kuko Imana iri bube igutabariye imbere kunesha ingabo z'Abafilisitiya.”",
			"16": "<sup>16</sup> Nuko Dawidi abigenza uko Imana yamutegetse, batsinda ingabo z'Abafilisitiya uhereye i Gibeyoni ukageza i Gezeri.",
			"17": "<sup>17</sup> Maze inkuru ya Dawidi yamamara mu bihugu byose, Uwiteka atuma amahanga yose amutinya."
		},
		"15": {
			"1": "<sup>1</sup> Bukeye Dawidi yiyubakira amazu mu rurembo rwa Dawidi, atunganiriza isanduku y'Imana ahantu, ayibambira ihema.",
			"2": "<sup>2</sup> Dawidi aravuga ati “Nta muntu ukwiriye kuremērwa isanduku y'Imana keretse Abalewi, kuko ari bo Uwiteka yatoranije kujya baremērwa isanduku y'Imana, bakayiremērwa iteka ryose.”",
			"3": "<sup>3</sup> Bukeye Dawidi ateraniriza Abisirayeli bose i Yerusalemu, ngo bajye kuzamura isanduku y'Uwiteka bayishyire ahantu hayo yayitunganirije.",
			"4": "<sup>4</sup> Dawidi ateranya bene Aroni n'Abalewi.",
			"5": "<sup>5</sup> Muri bene Kohati, umukuru ni Uriyeli na bene se ijana na makumyabiri.",
			"6": "<sup>6</sup> No muri bene Merari, umukuru ni Asaya na bene se magana abiri na makumyabiri.",
			"7": "<sup>7</sup> No muri bene Gerushomu, umukuru ni Yoweli na bene se ijana na mirongo itatu.",
			"8": "<sup>8</sup> No muri bene Elisafani, umukuru ni Shemaya na bene se magana abiri.",
			"9": "<sup>9</sup> No muri bene Heburoni, umukuru ni Eliyeli na bene se mirongo inani.",
			"10": "<sup>10</sup> No muri bene Uziyeli, umukuru ni Aminadabu na bene se ijana na cumi na babiri.",
			"11": "<sup>11</sup> Dawidi ahamagaza Sadoki na Abiyatari abatambyi, n'Abalewi ari bo Uriyeli na Asaya na Yoweli, na Shemaya na Eliyeli na Aminadabu",
			"12": "<sup>12</sup> arababwira ati “Mwebwe muri abatware b'amazu ya ba sekuruza b'Abalewi. Mwiyeze ubwanyu ndetse na bene wanyu, mubone kujya kuzamura isanduku y'Uwiteka Imana ya Isirayeli, muyishyire ahantu nayitunganirije.",
			"13": "<sup>13</sup> Kuko ubwa mbere atari mwe mwayihetse, ni cyo cyatumye Uwiteka Imana yacu idusumira, kuko tutayishatse nk'uko itegeko ritegeka.”",
			"14": "<sup>14</sup> Nuko abatambyi n'Abalewi biyereza kujya kuzamura isanduku y'Uwiteka Imana ya Isirayeli.",
			"15": "<sup>15</sup> (Bagezeyo) Abalewi baremērwa isanduku y'Imana, bashyira imijisho yayo ku ntugu zabo nk'uko Mose yategetse, uko ijambo ry'Uwiteka ryari riri.",
			"16": "<sup>16</sup> Dawidi abwira abatware b'Abalewi gutoranya abaririmbyi muri bene wabo ngo bazane ibintu bivuga: nebelu n'inanga n'ibyuma birenga, babivuze cyane barangurura ijwi n'ibyishimo.",
			"17": "<sup>17</sup> Nuko Abalewi batoranya Hemani mwene Yoweli, no muri bene Asafu mwene Berekiya, no muri bene se bene Merari, Etani mwene Kushaya,",
			"18": "<sup>18</sup> kandi hamwe na bo bene wabo aba kabiri kuri bo, ari bo Zekariya na Beni, na Yeziyeli na Shemiramoti, na Yehiyeli na Uni na Eliyabu, na Benaya na Māseya na Matitiya, na Elifelehu na Mikuneya, na Obededomu na Yeyeli, bari abarinzi b'irembo.",
			"19": "<sup>19</sup> Uko ni ko batoranije abaririmbyi: Hemani na Asafu na Etani, bazanye ibyuma by'imiringa birenga byo kuvuza cyane.",
			"20": "<sup>20</sup> Zekariya na Aziyeli, na Shemiramoti na Yehiyeli na Uni, na Eliyabu na Māseya na Benaya, bazanye nebelu bazibwirisha ijwi rito.",
			"21": "<sup>21</sup> Matitiya na Elifelehu na Mikuneya, na Obededomu na Yeyeli na Azaziya, bari bafite inanga babwirisha ijwi ryo mu gituza zo kubaterera indirimbo.",
			"22": "<sup>22</sup> Kenaniya umutware w'Abalewi waremērwaga isanduku, ni we wategekaga ibyo kuyiremērwa kuko yari umunyabwenge.",
			"23": "<sup>23</sup> Berekiya na Elukana bari abakumirizi b'isanduku.",
			"24": "<sup>24</sup> Shebaniya na Yashofati, na Netanēli na Amasiya, na Zekariya na Benaya na Eliyezeri abatambyi, ni bo bagendaga bavuza amakondera imbere y'isanduku y'Imana. Obededomu na Yehiya bari abakumirizi b'irembo ry'isanduku.",
			"25": "<sup>25</sup> Nuko Dawidi n'abakuru ba Isirayeli, n'abatware batwara ibihumbi bajya kuzamura isanduku y'isezerano ry'Uwiteka, bayikura kwa Obededomu bishima.",
			"26": "<sup>26</sup> Maze ubwo Imana ifashije Abalewi bari baremērewe isanduku y'isezerano ry'Uwiteka, baherako batamba amapfizi arindwi n'amasekurume y'ihene arindwi.",
			"27": "<sup>27</sup> Kandi Dawidi yari yambaye umwitero w'igitare cyiza, Abalewi bose na bo bari baremērewe isanduku, n'abaririmbyi na Kenaniya umutware w'abahetsi, bose bari bambaye batyo. Kandi Dawidi yari yambaye efodi y'igitare.",
			"28": "<sup>28</sup> Uko ni ko Abisirayeli bose bazamuye isanduku y'isezerano ry'Uwiteka basakuriza hejuru, bavuza ihembe n'amakondera n'ibyuma birenga, bacuranga nebelu n'inanga.",
			"29": "<sup>29</sup> Nuko bagisohoza isanduku y'isezerano ry'Uwiteka mu rurembo rwa Dawidi, Mikali umukobwa wa Sawuli arungurukira mu idirishya, abona Dawidi aca ikibungo abyina amugayira mu mutima."
		},
		"16": {
			"1": "<sup>1</sup> Nuko binjiza isanduku y'Imana bayitereka hagati mu ihema Dawidi yari yayibambiye, baherako batambira imbere y'Imana ibitambo byoswa, n'iby'ishimwe yuko bari amahoro.",
			"2": "<sup>2</sup> Dawidi amaze gutamba igitambo cyoswa n'ibitambo by'ishimwe yuko ari amahoro, asabira abantu umugisha mu izina ry'Uwiteka.",
			"3": "<sup>3</sup> Maze agaburira abantu ba Isirayeli bose, abagabo n'abagore, umuntu wese amuha irobe ry'umutsima n'igiti cy'inyama, n'umubumbe w'inzabibu zumye.",
			"4": "<sup>4</sup> Kandi ashyiraho bamwe mu Balewi ngo bakorere imbere y'isanduku y'Uwiteka, bajye bibutsa bashima Uwiteka Imana ya Isirayeli bayisingiza.",
			"5": "<sup>5</sup> Abo ni aba: Asafu yari umukuru agakurikirwa na Zekariya, na Yeyeli na Shemiramoti, na Yehiyeli na Matitiya, na Eliyabu na Benaya, na Obededomu na Yeyeli bari bafite nebelu n'inanga, kandi Asafu yari afite ibyuma birenga avuza cyane,",
			"6": "<sup>6</sup> na Benaya na Yahaziyeli abatambyi, bajyaga bavugiriza amakondera imbere y'isanduku y'isezerano ry'Imana.",
			"7": "<sup>7</sup> Uwo munsi ni wo Dawidi yatangiye gutegeka Asafu na bene se kuba ari bo bashima Uwiteka.",
			"8": "<sup>8</sup> Nimushime Uwiteka mwambaze izina rye, Mwamamaze imirimo yakoze mu mahanga.",
			"9": "<sup>9</sup> Mumuririmbire, mumuririmbire n'ishimwe, Muvuge imirimo itangaza yakoze yose.",
			"10": "<sup>10</sup> Mwīrāte izina rye ryera, Imitima y'abashaka Uwiteka yishime.",
			"11": "<sup>11</sup> Mushake Uwiteka n'imbaraga ze, Mushake mu maso he iteka ryose.",
			"12": "<sup>12</sup> Mwibuke imirimo itangaza yakoze, Ibitangaza bye n'amateka yo mu kanwa ke.",
			"13": "<sup>13</sup> Mwa rubyaro rwa Isirayeli umugaragu we mwe, Mwa bana ba Yakobo mwe, abo yatoranije,",
			"14": "<sup>14</sup> Uwiteka ni we Mana yacu, Amateka ye ari mu isi yose.",
			"15": "<sup>15</sup> Mujye mwibuka isezerano rye iminsi yose, Ijambo yategetse ibihe ibihumbi.",
			"16": "<sup>16</sup> Ni ryo sezerano yasezeranye na Aburahamu, N'indahiro yarahiye Isaka,",
			"17": "<sup>17</sup> Akayikomereza Yakobo kuba itegeko, Akayikomereza Isiraheli kuba isezerano ridashira.",
			"18": "<sup>18</sup> Ati “Ni wowe nzaha igihugu cy'i Kanāni, Kuba umwandu ukugerewe.”",
			"19": "<sup>19</sup> Umubare wabo ukiri muke, Muke cyane, na bo ari abashyitsi muri icyo gihugu.",
			"20": "<sup>20</sup> Bazereraga mu mahanga atari amwe, Bava mu gihugu cy'umwami bakajya mu kindi.",
			"21": "<sup>21</sup> Ntiyakundira umuntu ko abarenganya, Ndetse yabacyahiye abami.",
			"22": "<sup>22</sup> Ati “Ntimukore ku bo nasīze, Ntimugire icyo mutwara abahanuzi banjye.”",
			"23": "<sup>23</sup> Muririmbire Uwiteka, mwe abari mu isi mwese, Mwerekane agakiza ke uko bukeye.",
			"24": "<sup>24</sup> Mwogeze icyubahiro cye mu mahanga, Imirimo itangaza yakoze muyogeze mu mahanga yose.",
			"25": "<sup>25</sup> Kuko Uwiteka akomeye, akwiriye gusingizwa cyane, Kandi ateye ubwoba arusha imana zose.",
			"26": "<sup>26</sup> Kuko imana z'abanyamahanga zose ari ibigirwamana, Ariko Uwiteka ni we waremye ijuru.",
			"27": "<sup>27</sup> Icyubahiro n'igitinyiro biri kuri we, Imbaraga n'ibyishimo biri iwe.",
			"28": "<sup>28</sup> Mwa miryango y'amahanga mwe, mwāturire Uwiteka yuko afite icyubahiro n'imbaraga.",
			"29": "<sup>29</sup> Mwāturire Uwiteka yuko izina rye rifite icyubahiro, Muze imbere ye muzanye ituro, Musengane Uwiteka ubwiza bwo kwera.",
			"30": "<sup>30</sup> Mwe abari mu isi mwese mwe, Muhindire imishitsi imbere ye, Isi irakomeye ntibasha kunyeganyega.",
			"31": "<sup>31</sup> Ijuru rinezerwe isi yishime, Bavugire mu mahanga bati “Uwiteka ari ku ngoma.”",
			"32": "<sup>32</sup> Inyanja ihorerane n'ibiyuzuye, Mu gasozi hishimane n'ibihari byose.",
			"33": "<sup>33</sup> Ni bwo ibiti byo mu ishyamba bizaririmbishwa n'ibyishimo imbere y'Uwiteka, Kuko azanwa no gucira abari mu isi imanza.",
			"34": "<sup>34</sup> Nimushimire Uwiteka yuko ari mwiza, Kuko imbabazi ze zihoraho iteka ryose.",
			"35": "<sup>35</sup> Muvuge muti “Mana y'agakiza kacu, udukize.” Utubumbire hamwe udukuye mu mahanga, Kugira ngo dushime izina ryawe ryera, Twishimire ishimwe ryawe.",
			"36": "<sup>36</sup> Uwiteka Imana y'Abisirayeli isingizwe, Uhereye kera kose ukageza iteka ryose. Nuko abantu bose baravuga bati “Amen”, basingiza Uwiteka.",
			"37": "<sup>37</sup> Hanyuma asigayo Asafu na bene se imbere y'isanduku y'isezerano ry'Uwiteka, kugira ngo bahore bakorera imbere y'iyo sanduku, nk'uko byari bikwiriye imirimo y'iminsi yose,",
			"38": "<sup>38</sup> na Obededomu na bene se mirongo itandatu n'umunani, kandi Obededomu mwene Yedutuni na Hosa baba abakumirizi.",
			"39": "<sup>39</sup> Kandi ashyiraho Sadoki umutambyi, na bene se b'abatambyi imbere y'ihema ry'Uwiteka ryari ku kanunga k'i Gibeyoni,",
			"40": "<sup>40</sup> kugira ngo bajye batambira Uwiteka ibitambo byoswa ku cyotero cy'ibitambo byoswa mu gitondo na nimugoroba, nk'uko byari byaranditswe byose mu mategeko y'Uwiteka yategetse Abisirayeli.",
			"41": "<sup>41</sup> Kandi hamwe na bo ashyiraho Hemani na Yedutuni n'abandi batoranijwe bakavugwa mu mazina yabo, kugira ngo bahimbaze Uwiteka kuko imbabazi ze zihoraho iteka ryose.",
			"42": "<sup>42</sup> Kandi Hemani na Yedutuni abo bagiraga amakondera n'ibyuma birenga by'abantu bajyaga bavuza, bagiraga n'ibintu bitera indirimbo z'Imana. Kandi bashyiraho bene Yedutuni ngo barinde irembo.",
			"43": "<sup>43</sup> Abantu bose baherako barataha umuntu wese ajya iwe, Dawidi asubira iwe ajya gusabira inzu ye umugisha."
		},
		"17": {
			"1": "<sup>1</sup> Nuko Dawidi aganje mu nzu ye abwira umuhanuzi Natani ati “Dore jyewe mba mu nzu y'imyerezi, ariko isanduku y'isezerano ry'Uwiteka ikaba mu ihema.”",
			"2": "<sup>2</sup> Natani asubiza Dawidi ati “Kora uko umutima wawe ushaka kose, kuko Imana iri kumwe nawe.”",
			"3": "<sup>3</sup> Iryo joro ijambo ry'Imana rigera kuri Natani, iravuga iti",
			"4": "<sup>4</sup> “Genda ubwire umugaragu wanjye Dawidi uti ‘Umva uko Uwiteka avuze ati: Ntuzanyubakire inzu yo kubamo,",
			"5": "<sup>5</sup> kuko ntabwo nigeze kuba mu nzu uhereye ku munsi nazamuriye Abisirayeli kugeza ubu, ahubwo najyaga mva mu ihema nkajya mu rindi, nava mu buturo nkajya mu bundi.",
			"6": "<sup>6</sup> Mbese ahantu hose nagendaniye n'Abisirayeli bose, hari ubwo navuganaga n'umucamanza wese w'Abisirayeli, uwo nategetse kuragira ubwoko bwanjye nti: Ni iki cyababujije kunyubakira inzu y'imyerezi?’",
			"7": "<sup>7</sup> “Nuko ubwire umugaragu wanjye Dawidi uti ‘Uwiteka Nyiringabo avuze atya ati: Nagukuye mu rugo rw'intama no mu bwungeri bwazo, ngo ube umwami w'ubwoko bwanjye bwa Isirayeli.",
			"8": "<sup>8</sup> Kandi nabanaga nawe aho wajyaga hose, nkarimbura abanzi bawe bose imbere yawe, kandi nzaguha izina rihwanye n'amazina y'abakomeye bo mu isi.",
			"9": "<sup>9</sup> Kandi nzagerera ubwoko bwanjye bwa Isirayeli ahantu, mpabashinge bahature habe ahantu habo bwite be kuzimuka ukundi. Kandi abanyarugomo ntibazongera kubaburabuza nk'ubwa mbere,",
			"10": "<sup>10</sup> nko mu gihe nategekeye abacamanza gutwara ubwoko bwanjye bwa Isirayeli: nzajya ncogoza ababisha bawe bose. Kandi ndakubwira yuko Uwiteka azakubakira inzu.",
			"11": "<sup>11</sup> “ ‘Kandi iminsi yawe yo gusanga ba sogokuruza nigera, nzaherako mpagarike urubyaro rwawe rukuzungure, umwe wo mu bahungu bawe nkomeze ubwami bwe.",
			"12": "<sup>12</sup> Uwo ni we uzanyubakira inzu, kandi nzakomeza ingoma ye iteka ryose.",
			"13": "<sup>13</sup> Nzamubera se na we azambera umwana. Sinzamukuraho imbabazi zanjye nk'uko nazikuye ku wakubanjirije.",
			"14": "<sup>14</sup> Ahubwo nzamubesha mu nzu yanjye no mu bwami bwanjye iteka ryose, kandi ingoma ye izakomezwa iteka ryose.’ ”",
			"15": "<sup>15</sup> Ayo magambo yose n'uko kwerekwa kose, ni ko Natani yabibwiye Dawidi.",
			"16": "<sup>16</sup> Umwami Dawidi aherako arinjira yicara imbere y'Uwiteka aravuga ati “Nkanjye ndi nde Uwiteka Mana, n'inzu yanjye ni iki, nkawe kunzana ukarinda ungeza aha?",
			"17": "<sup>17</sup> Ndetse ibyo byari byoroshye imbere yawe Mana, ariko uvuze no ku by'inzu y'umugaragu wawe bizashyira kera, kandi urarebye umpwanya n'umuntu w'umunyacyubahiro kinini, Uwiteka Mana.",
			"18": "<sup>18</sup> Jyewe Dawidi, nabasha kukubwira iki kandi ku by'icyubahiro umugaragu wawe ngiriwe? Kuko ari wowe uzi umugaragu wawe.",
			"19": "<sup>19</sup> Uwiteka, ibyo bikomeye byose wabikoreye umugaragu wawe nk'uko umutima wawe wibwira, kugira ngo werekane ibyo bikomeye byose.",
			"20": "<sup>20</sup> Uwiteka, nta wuhwanye nawe, nta yindi mana keretse wowe nk'uko twabyumvishije amatwi yacu byose.",
			"21": "<sup>21</sup> Kandi ni irihe shyanga mu isi rihwanye n'ubwoko bwawe bwa Isirayeli, Imana yicunguriye ngo ribe ubwoko bwayo, ukibonera izina uriheshejwe n'ibikomeye biteye ubwoba wakoze, ubwo wirukaniraga amahanga imbere y'ubwoko bwawe wacunguye, ukabukura muri Egiputa?",
			"22": "<sup>22</sup> Kuko ubwoko bwawe bwa Isirayeli wabugize abantu bawe bwite iteka ryose, nawe Uwiteka wahindutse Imana yabo.",
			"23": "<sup>23</sup> “Nuko none Uwiteka, ijambo uvuze ku mugaragu wawe no ku nzu ye rikomezwe iteka ryose, kandi ugenze nk'uko uvuze.",
			"24": "<sup>24</sup> Kandi izina ryawe rikomezwe rikuzwe iteka ryose, bavuge bati ‘Uwiteka Nyiringabo ari we Mana y'Abisirayeli abere Abisirayeli Imana’, kandi inzu y'umugaragu wawe Dawidi ikomezwe imbere yawe,",
			"25": "<sup>25</sup> kuko ari wowe Mana yanjye, uhishuriye umugaragu wawe yuko uzanyubakira inzu. Ni cyo gitumye umugaragu wawe mpangara gusengera imbere yawe.",
			"26": "<sup>26</sup> Kandi none Uwiteka, ni wowe Mana usezeranije umugaragu wawe iryo jambo ryiza,",
			"27": "<sup>27</sup> ukishimira guha inzu y'umugaragu wawe umugisha igahoraho imbere yawe iteka ryose, kuko ari wowe Uwiteka uyihaye umugisha, izahorana umugisha iteka ryose.”"
		},
		"18": {
			"1": "<sup>1</sup> Hanyuma y'ibyo Dawidi anesha Abafilisitiya arabacogoza, ahindūra i Gati n'imidugudu yaho, ayinyaga Abafilisitiya.",
			"2": "<sup>2</sup> Anesha n'i Mowabu, Abamowabu bahinduka abagaragu be, bamuzanira indabukirano.",
			"3": "<sup>3</sup> Bukeye Dawidi anesha Hadarezeri umwami w'i Soba, amugeza i Hamati ubwo Hadarezeri yajyaga gukomeza ubwami bwe ku ruzi Ufurate.",
			"4": "<sup>4</sup> Dawidi amunyaga amagare igihumbi n'abagendera ku mafarashi ibihumbi birindwi, n'ingabo zigenza inzovu ebyiri, maze Dawidi atema ibitsi by'amafarashi akurura amagare yose, ariko asigaza amafarashi akwiriye amagare ijana.",
			"5": "<sup>5</sup> Bukeye Abasiriya b'i Damasiko baje gutabara Hadarezeri umwami w'i Soba, Dawidi abicamo abantu inzovu ebyiri n'ibihumbi bibiri.",
			"6": "<sup>6</sup> Dawidi aherako ashyira ibihome i Siriya ku murwa w'i Damasiko, Abasiriya bahinduka abagaragu ba Dawidi bamuzanira indabukirano. Nuko Uwiteka aha Dawidi kunesha aho yajyaga hose.",
			"7": "<sup>7</sup> Dawidi anyaga n'ingabo z'izahabu zatwarwaga n'abagaragu ba Hadarezeri, azizana i Yerusalemu.",
			"8": "<sup>8</sup> Kandi i Tibuhati n'i Kuni, imidugudu ya Hadarezeri, Dawidi ayikuramo imiringa myinshi cyane. Ni yo Salomo yakoresheje ikidendezi cy'umuringa, n'inkingi n'ibintu by'imiringa.",
			"9": "<sup>9</sup> Bukeye Towu umwami w'i Hamati yumvise ko Dawidi yanesheje ingabo za Hadarezeri umwami w'i Soba,",
			"10": "<sup>10</sup> atuma umwana we Hadoramu ku Mwami Dawidi kumuramutsa, no kumuha impundu z'uko yarwanye na Hadarezeri akamunesha, kuko Hadarezeri yajyaga arwana na Towu. Kandi Hadoramu yari azanye ibintu by'izahabu n'ifeza n'imiringa by'amoko yose,",
			"11": "<sup>11</sup> na byo Umwami Dawidi abitura Uwiteka hamwe n'ifeza n'izahabu, ibyo yari anyaze muri aya mahanga yose: mu Bedomu no mu Bamowabu no mu Bamoni, no mu Bafilisitiya no mu Bamaleki.",
			"12": "<sup>12</sup> Kandi Abishayi mwene Seruya anesha Abedomu mu kibaya cy'umunyu, abicamo abantu inzovu imwe n'ibihumbi munani.",
			"13": "<sup>13</sup> Maze ashyira ibihome mu Edomu, Abedomu bose bahinduka abagaragu ba Dawidi. Nuko Uwiteka aha Dawidi kunesha aho yajyaga hose.",
			"14": "<sup>14</sup> Dawidi ategeka Isirayeli yose, aca imanza zo gukiranuka zitabera mu bantu be bose.",
			"15": "<sup>15</sup> Yowabu mwene Seruya ni we wari umugaba w'ingabo, na Yehoshafati mwene Ahiludi ni we wari umucurabwenge,",
			"16": "<sup>16</sup> na Sadoki mwene Ahitubu na Abimeleki mwene Abiyatari ni bo bari abatambyi, na Shavusha ni we wari umwanditsi,",
			"17": "<sup>17</sup> na Benaya mwene Yehoyada ni we wari umutware w'Abakereti n'Abapeleti, na bene Dawidi bari abatware bakikije umwami."
		},
		"19": {
			"1": "<sup>1</sup> Hanyuma y'ibyo Nahashi umwami w'Abamoni aratanga, umwana we yima ingoma ye.",
			"2": "<sup>2</sup> Dawidi abyumvise aravuga ati “Nzagirira neza Hanuni mwene Nahashi, kuko se yangiriye neza.” Nuko Dawidi atuma intumwa zo kumumara umubabaro wa se. Bukeye abagaragu ba Dawidi bajya mu gihugu cy'Abamoni kwa Hanuni, kumumara umubabaro.",
			"3": "<sup>3</sup> Ariko ibikomangoma by'Abamoni bibwira Hanuni biti “Mbese ugira ngo Dawidi yubashye so, bituma akoherereza abo kukumara umubabaro? Ahubwo ntuzi ko abagaragu be bazanywe no kwitegereza umurwa kugira ngo bawurimbure, kandi no gutata igihugu?”",
			"4": "<sup>4</sup> Nuko Hanuni afata abagaragu ba Dawidi arabamora, akeba imyambaro yabo hagati ku bibuno, aherako arabohereza.",
			"5": "<sup>5</sup> Abandi baragenda babwira Dawidi uko ba bagabo bagenjejwe. Atuma kubasanganira kuko abo bagabo bari bakozwe n'isoni cyane. Umwami aravuga ati “Mugume i Yeriko kugeza aho ubwanwa bwanyu buzamerera, muzabone kugaruka.”",
			"6": "<sup>6</sup> Abamoni babonye ko bazinuye Dawidi, Hanuni n'Abamoni bohereza italanto z'ifeza igihumbi kugurira amagare n'abagendera ku mafarashi by'i Mezopotamiya, n'ibyo muri Aramumāka n'iby'i Soba.",
			"7": "<sup>7</sup> Nuko bigurira amagare inzovu eshatu n'ibihumbi bibiri, bagurira n'umwami w'i Māka n'ingabo ze baraza bagerera aherekeye i Medeba, Abamoni bava mu midugudu yabo baraterana bajya kurwana.",
			"8": "<sup>8</sup> Dawidi abyumvise agaba Yowabu n'ingabo z'abanyambaraga zose.",
			"9": "<sup>9</sup> Maze Abamoni bava mu mudugudu birema ingamba ku irembo ryawo, kandi abami babatabaye bari ukwabo ku gasozi.",
			"10": "<sup>10</sup> Yowabu abonye ko urugamba rumuremeye imbere n'inyuma, atoranya abagabo mu ntore zatoranijwe mu Bisirayeli zose, abarema urugamba bahangana n'Abasiriya.",
			"11": "<sup>11</sup> Abandi bantu basigaye abaha murumuna we Abishayi, na bo birema urugamba bahangana n'Abamoni.",
			"12": "<sup>12</sup> Yowabu abwira Abishayi ati “Abasiriya nibaramuka bandushije amaboko uze kumvuna, kandi nawe Abamoni nibaramuka bakurushije amaboko, nanjye nzakuvuna.",
			"13": "<sup>13</sup> Komera turwane kigabo ku bw'ubwoko bwacu n'imidugudu y'Imana yacu, kandi Uwiteka abigenze uko ashaka.”",
			"14": "<sup>14</sup> Nuko Yowabu n'abari kumwe na we begera Abasiriya ngo barwane, baramuhunga.",
			"15": "<sup>15</sup> Abamoni babonye yuko Abasiriya bahunze, na bo bahunga Abishayi murumuna wa Yowabu, biroha mu mudugudu. Maze Yowabu aza i Yerusalemu.",
			"16": "<sup>16</sup> Bukeye Abasiriya babonye ko baneshejwe imbere y'Abisirayeli batuma impuruza, bakurayo Abasiriya bo hakurya ya rwa ruzi, bari bafite Shofaki umugaba w'ingabo za Hadarezeri ho umugaba.",
			"17": "<sup>17</sup> Babibwira Dawidi ateranya Abisirayeli bose, yambuka Yorodani abageraho, arema urugamba ngo arwane na bo. Nuko Dawidi amaze kurema urugamba ahangana n'Abasiriya, Abasiriya barwana na we.",
			"18": "<sup>18</sup> Abasiriya bahunga Abisirayeli, Dawidi abicamo abarwaniraga ku magare, umubare wayo yari ibihumbi birindwi, n'ingabo zigenza inzovu enye, yica na Shofaki umugaba w'ingabo.",
			"19": "<sup>19</sup> Nuko abagaragu ba Hadarezeri babonye yuko baneshejwe imbere y'Abisirayeli, bayoboka Dawidi bamuhakwaho. Kandi n'Abasiriya bahakana ko batazongera kuvuna Abamoni ukundi."
		},
		"20": {
			"1": "<sup>1</sup> Nuko umwaka utashye mu gihe abami batabarira, Yowabu ateza ingabo zikomeye igihugu cy'Abamoni aracyoreka, maze araza agota i Raba, ariko Dawidi asigara i Yerusalemu. Yowabu atsinda i Raba arahasenya.",
			"2": "<sup>2</sup> Dawidi yenda ikamba ry'umwami waho arimukura ku mutwe, areba kuremera kwaryo kwari italanto y'izahabu kandi ryari ritatsweho amabuye y'igiciro cyinshi, baherako baryambika Dawidi mu mutwe. Muri uwo mudugudu akuramo iminyago myinshi cyane.",
			"3": "<sup>3</sup> Akuramo n'abantu bo muri wo, abakereza inkero n'ibyuma biharura n'intorezo. Uko ni ko Dawidi yagenje imidugudu y'Abamoni yose, hanyuma Dawidi n'ingabo ze zose basubira i Yerusalemu.",
			"4": "<sup>4</sup> Hanyuma y'ibyo habaho intambara y'Abafilisitiya i Gezeri. Muri iyo ntambara Sibekayi w'Umuhusha yica Sipayi wo mu bana b'igihanda, maze baraneshwa.",
			"5": "<sup>5</sup> Bukeye hongera kuba intambara y'Abafilisitiya. Eluhanani mwene Yayiri yica Lahumi murumuna wa Goliyati w'Umugiti, uruti rw'icumu rye rwari rumeze nk'igiti kiboherwaho imyenda.",
			"6": "<sup>6</sup> Bukeye hongera kubaho intambara i Gati. Hari umugabo muremure cyane, wari ufite intoki n'amano byose ari makumyabiri na bine, ku kuboko hagiye habaho esheshatu no ku kirenge atandatu, kandi na we yabyawe na cya gihanda.",
			"7": "<sup>7</sup> Nuko asuzugura Abisirayeli, Yonatani mwene Shimeya mukuru wa Dawidi aramwica.",
			"8": "<sup>8</sup> Abo babyawe na cya gihanda cy'i Gati, batsembwa na Dawidi n'abagaragu be."
		},
		"21": {
			"1": "<sup>1</sup> Bukeye Satani ahagurukira Abisirayeli, yoshya Dawidi kubara Abisirayeli.",
			"2": "<sup>2</sup> Dawidi abwira Yowabu n'abatware b'abantu ati “Nimugende mubare Abisirayeli, uhereye i Bērisheba ukageza i Dani, maze muze mumbwire kugira ngo menye umubare wabo.”",
			"3": "<sup>3</sup> Yowabu aravuga ati “Uwiteka nagwize abantu be barute umubare wari usanzwe incuro ijana. Ariko nyagasani mwami, bose si abagaragu ba databuja? None databuja ubishakiye iki? Ni iki gituma ushyirisha Abisirayeli ho urubanza?”",
			"4": "<sup>4</sup> Ariko ijambo ry'umwami riganza irya Yowabu. Nuko Yowabu aragenda, agenda igihugu cya Isirayeli cyose maze asubira i Yerusalemu.",
			"5": "<sup>5</sup> Yowabu azanira Dawidi umubare w'abantu uko babazwe. Abisirayeli bari agahumbagiza n'agahumbi, abagabo bambara inkota. Abayuda na bo bari uduhumbi tune n'inzovu ndwi, abagabo bambara inkota.",
			"6": "<sup>6</sup> Ariko ntiyabariyemo Abalewi n'Ababenyamini, kuko ijambo ry'umwami ryari ryamuzinuye.",
			"7": "<sup>7</sup> Maze Imana irabirakarira, ni cyo cyatumye itera Abisirayeli.",
			"8": "<sup>8</sup> Dawidi abwira Imana ati “Ndacumuye cyane kuko nakoze ibyo, ariko noneho ndakwinginze kuraho gukiranirwa k'umugaragu wawe, kuko nakoze iby'ubupfu bwinshi.”",
			"9": "<sup>9</sup> Uwiteka abwira Gadi bamenya wa Dawidi ati",
			"10": "<sup>10</sup> “Genda ubwire Dawidi uti ‘Uwiteka avuze atya ati: Nkuzaniye ibihano bitatu, hitamo kimwe abe ari cyo nguhanisha.’ ”",
			"11": "<sup>11</sup> Nuko Gadi asanga Dawidi aramubwira ati “Uwiteka avuze ngo ‘Hitamo icyo ushaka",
			"12": "<sup>12</sup> ari uguterwa n'inzara imyaka itatu, cyangwa kumarwaho n'ababisha bawe amezi atatu inkota zabo zikugeraho, cyangwa se, inkota y'Uwiteka iminsi itatu, ari yo mugiga yatera mu gihugu, na marayika w'Uwiteka akarimbura mu gihugu cya Isirayeli cyose.’ Nuko rero tekereza umbwire uko nsubiza uwantumye.”",
			"13": "<sup>13</sup> Dawidi abwira Gadi ati “Ndashobewe rwose. Reka nigwire mu maboko y'Imana kuko imbabazi zayo ari nyinshi cyane, ne kugwa mu maboko y'abantu.”",
			"14": "<sup>14</sup> Nuko Uwiteka ateza Isirayeli mugiga. Mu Isirayeli hapfamo abagabo inzovu ndwi.",
			"15": "<sup>15</sup> Imana ituma marayika i Yerusalemu kuharimbura. Yenda kuharimbura, Uwiteka arareba arakuruka iyo nabi, abwira marayika urimbura ati “Birahagije noneho unamura ukuboko kwawe.” Kandi marayika w'Uwiteka yari ahagaze ku mbuga ya Orunani w'Umuyebusi.",
			"16": "<sup>16</sup> Dawidi yubura amaso abona marayika w'Uwiteka ahagaze hagati y'isi n'ijuru, afite inkota mu ntoki ze ayerekeje i Yerusalemu. Dawidi n'abakuru bari bambaye ibigunira, bagwa hasi bubamye.",
			"17": "<sup>17</sup> Dawidi abwira Imana ati “Mbese si jye wategetse ko abantu babarwa? Ni jye wacumuye ngakora iby'ubugoryi bwinshi, ariko izi ntama zo zacumuye iki? Ndakwinginze Uwiteka Mana yanjye, ukuboko kwawe kube ari jye kwerekeraho n'inzu ya data, ariko si ku bantu bawe ngo baterwe na mugiga.”",
			"18": "<sup>18</sup> Nuko marayika w'Uwiteka ategeka Gadi kubwira Dawidi, ko azamuka akubakira Uwiteka igicaniro ku mbuga ya Orunani w'Umuyebusi.",
			"19": "<sup>19</sup> Nuko Dawidi azamurwa n'ijambo rya Gadi avuze mu izina ry'Uwiteka.",
			"20": "<sup>20</sup> Orunani arahindukira abona marayika, abahungu be bane bari kumwe na we barihisha. Kandi Orunani yahuraga ingano.",
			"21": "<sup>21</sup> Nuko Dawidi ajya kwa Orunani, Orunani arebye abona Dawidi, maze ava mu mbuga yubika amaso imbere ya Dawidi.",
			"22": "<sup>22</sup> Dawidi aherako abwira Orunani ati “Mpa ikibanza kuri iyi mbuga nubakireho Uwiteka igicaniro, turayigura ibiguzi uko igiciro cyayo cyose kiri, kugira ngo mugiga ikurwe mu bantu.”",
			"23": "<sup>23</sup> Orunani abwira Dawidi ati “Yijyane nyagasani mwami, ukore uko ushaka. Dore nguhaye inka ho ibitambo byoswa, nguhaye n'ibihurisho ho inkwi, n'ingano ngo zibe ituro ry'ifu. Byose ndabitanze.”",
			"24": "<sup>24</sup> Umwami Dawidi abwira Orunani ati “Oya, ahubwo ndabigura nawe rwose, ntange igiciro cyabyo cyose kuko ntashaka kwenda ibyawe ngo mbiture Uwiteka, kandi sinatamba igitambo cyoswa ntagitanzeho ibyanjye.”",
			"25": "<sup>25</sup> Nuko Dawidi agura na Orunani ikibanza izahabu, kuremera kwazo kwari shekeli magana atandatu.",
			"26": "<sup>26</sup> Dawidi aherako yubakira Uwiteka igicaniro, atamba ibitambo byoswa n'ibitambo by'ishimwe yuko bari amahoro, yambaza Uwiteka. Uwiteka amusubirisha umuriro uva mu ijuru, ujya ku gicaniro cy'igitambo cyoswa.",
			"27": "<sup>27</sup> Uwiteka ategeka marayika, asubiza inkota ye mu rwubati rwayo.",
			"28": "<sup>28</sup> Icyo gihe Dawidi abonye ko Uwiteka yamushubirije ku mbuga ya Orunani w'Umuyebusi, ni ko kujya atambirayo,",
			"29": "<sup>29</sup> Kuko ihema ry'Uwiteka Mose yakoreye mu butayu, n'icyotero cy'ibitambo byoswa, muri iyo minsi byari ahantu ho ku kanunga i Gibeyoni.",
			"30": "<sup>30</sup> Ariko Dawidi ntiyabasha kujya imbere yayo ngo agishe Imana inama, kuko yari yaratinye inkota ya marayika w'Uwiteka."
		},
		"22": {
			"1": "<sup>1</sup> Dawidi aravuga ati “Iyi ni yo nzu y'Uwiteka Imana, kandi iki ni cyo gicaniro cy'ibitambo byoswa ku bw'Abisirayeli.”",
			"2": "<sup>2</sup> Dawidi ategeka ko bateranya abanyamahanga bari mu gihugu cya Isirayeli, ashyiraho ababaji b'amabuye ngo babaze amabuye yo kubaka inzu y'Imana.",
			"3": "<sup>3</sup> Kandi Dawidi yitegura ibyuma byinshi byo gucuramo imbereri z'inzugi z'amarembo n'ibyo guteranya ibintu, n'imiringa myinshi cyane itagira akagero,",
			"4": "<sup>4</sup> n'ibiti by'imyerezi bitabarika, kuko Abasidoni n'Abanyatiro bari bazaniye Dawidi imyerezi myinshi.",
			"5": "<sup>5</sup> Dawidi aravuga ati “Umuhungu wanjye Salomo aracyari muto ntarakomera, kandi inzu igiye kūbakirwa Uwiteka ikwiriye kuba iy'icyubahiro cyinshi, ikamamara igahimbazwa mu bihugu byose. Ni cyo gituma nkwiriye kuyitegurira.” Nuko Dawidi yitegura byinshi cyane ataratanga.",
			"6": "<sup>6</sup> Maze ahamagara umuhungu we Salomo, amwihanangiriza ko yubakira Uwiteka Imana ya Isirayeli inzu.",
			"7": "<sup>7</sup> Dawidi abwira Salomo ati “Mwana wanjye, nari mbisanganywe mu mutima wanjye ko nzubakira izina ry'Uwiteka Imana yanjye inzu.",
			"8": "<sup>8</sup> Ariko ijambo ry'Uwiteka rinzaho rivuga riti ‘Wavushije amaraso menshi, urwana intambara zikomeye. Ntuzubakira izina ryanjye inzu, kuko wavushije amaraso menshi ku isi imbere yanjye.",
			"9": "<sup>9</sup> Umva uzabyara umwana, azaba umunyamahoro. Kandi nzamuha ihumure ku babisha be bose bamuri impande zose, kuko izina azitwa ari Salomo kandi nzaha Abisirayeli amahoro n'ihumure ku ngoma ye.",
			"10": "<sup>10</sup> Uwo ni we uzubakira izina ryanjye inzu. Azaba umwana wanjye nanjye nzaba se, kandi nzakomeza ingoma ye mu Bisirayeli iteka ryose.’ ”",
			"11": "<sup>11</sup> “None mwana wanjye, Uwiteka abane nawe. Ujye ubona umugisha, wubakire Uwiteka Imana yawe inzu nk'uko yabikuvuzeho.",
			"12": "<sup>12</sup> Icyakora Uwiteka aguhe ubwenge no kumenya, agutegekeshe Abisirayeli kugira ngo witondere amategeko y'Uwiteka Imana yawe.",
			"13": "<sup>13</sup> Uko ni ko uzabona umugisha, niwitondera gusohoza amategeko n'amateka, ibyo Uwiteka yategetse Mose mu byo ku Bisirayeli. Komera ushikame, ntutinye kandi ntukuke umutima.",
			"14": "<sup>14</sup> Dore mu miruho yanjye niteguriye inzu y'Uwiteka italanto z'izahabu agahumbi, n'iz'ifeza agahumbagiza, n'imiringa n'ibyuma bitagira akagero kuko ari byinshi cyane, kandi niteguye n'ibiti n'amabuye, nawe uzīyongerere.",
			"15": "<sup>15</sup> Kandi ufite abakozi benshi cyane, abacukura amabuye bakayabaza n'abatema ibiti, n'abantu bose b'abanyabukorikori ku murimo wose.",
			"16": "<sup>16</sup> Dore izahabu n'ifeza n'imiringa n'ibyuma ntibigira uko bingana. Haguruka ukore kandi Uwiteka abane nawe.”",
			"17": "<sup>17</sup> Kandi Dawidi ategeka n'abatware b'Abisirayeli bose, yuko bafasha umuhungu we Salomo ati",
			"18": "<sup>18</sup> “Mbese Uwiteka Imana yanyu ntiri kumwe namwe, kandi ntibahaye ihumure impande zose? Kuko yangabije abaturage bo mu gihugu, kandi igihugu kineshejwe imbere y'Uwiteka n'imbere y'abantu be.",
			"19": "<sup>19</sup> None mushyireho umwete wo gushakisha Uwiteka Imana yanyu umutima n'ubugingo. Nuko rero nimuhaguruke mwubake urusengero rw'Uwiteka Imana, kugira ngo muzane Isanduku y'isezerano ry'Uwiteka n'ibintu byera by'Imana, mubishyire mu nzu igiye kubakirwa izina ry'Uwiteka.”"
		},
		"23": {
			"1": "<sup>1</sup> Ubwo Dawidi yari ashaje ageze mu za bukuru, yimika umuhungu we Salomo ngo abe umwami wa Isirayeli.",
			"2": "<sup>2</sup> Ateranya abatware ba Isirayeli bose n'abatambyi n'Abalewi.",
			"3": "<sup>3</sup> Maze babara Abalewi bashyikije imyaka mirongo itatu y'ubukuru n'abayishagije, kandi umubare wabo uko babazwe umwe umwe, bari inzovu eshatu n'ibihumbi munani.",
			"4": "<sup>4</sup> Kuri abo inzovu ebyiri n'ibihumbi bine, bari abo gutwara umurimo wo mu nzu y'Uwiteka, kandi ibihumbi bitandatu bari abatware n'abacamanza.",
			"5": "<sup>5</sup> Abandi ibihumbi bine bari abakumirizi, n'abandi ibihumbi bine bari abo guhimbarisha Uwiteka ibintu nakoze, (ni ko Dawidi yavuze) ngo babimushimishe.",
			"6": "<sup>6</sup> Dawidi abaremamo ibice uko abahungu ba Lewi bari bari, Gerushoni na Kohati na Merari.",
			"7": "<sup>7</sup> Mu Bagerushoni ni Lādani na Shimeyi.",
			"8": "<sup>8</sup> Bene Lādani, umukuru ni Yehiyeli na Zetamu na Yoweli, uko ari batatu.",
			"9": "<sup>9</sup> Bene Shimeyi ni Shelomoti na Haziyeli na Harani, uko ari batatu. Abo ni bo bari abatware b'amazu ya ba sekuruza ba Lādani.",
			"10": "<sup>10</sup> Bene Shimeyi ni Yahati na Zina, na Yewushi na Beriya. Abo uko ari bane bari bene Shimeyi.",
			"11": "<sup>11</sup> Yahati ni we wari mukuru, uwa kabiri ni Ziza. Ariko Yewushi na Beriya ntibagiraga abana b'abahungu benshi, ni cyo cyatumye bababumbira hamwe bakabagira inzu imwe ya ba sekuruza.",
			"12": "<sup>12</sup> Bene Kohati ni Amuramu na Isuhari, na Heburoni na Uziyeli uko ari bane.",
			"13": "<sup>13</sup> Bene Amuramu ni Aroni na Mose, kandi Aroni yatoranijwe kugira ngo ajye yeza ibintu byera cyane we n'abahungu be iteka ryose, bakosereza imibavu imbere y'Uwiteka bakamukorera, bagasabira umugisha mu izina rye iminsi yose.",
			"14": "<sup>14</sup> Ariko Mose umuntu w'Imana, abahungu be babarwaga mu muryango wa Lewi.",
			"15": "<sup>15</sup> Abahungu ba Mose ni Gerushomu na Eliyezeri.",
			"16": "<sup>16</sup> Bene Gerushomu, Shebuweli ni we wari mukuru.",
			"17": "<sup>17</sup> Bene Eliyezeri, Rehabiya ni we wari mukuru. Kandi Eliyezeri nta bana b'abahungu yagiraga, ariko abahungu ba Rehabiya baba benshi cyane.",
			"18": "<sup>18</sup> Bene Isuhari, Shelomiti ni we wari mukuru.",
			"19": "<sup>19</sup> Bene Heburoni, Yeriya ni we wari mukuru, uwa kabiri ni Amariya, uwa gatatu ni Yahaziyeli, uwa kane ni Yekameyamu.",
			"20": "<sup>20</sup> Bene Uziyeli, Mika ni we wari mukuru, uwa kabiri ni Ishiya.",
			"21": "<sup>21</sup> Bene Merari ni Mahali na Mushi. Bene Mahali ni Eleyazari na Kishi.",
			"22": "<sup>22</sup> Eleyazari apfa ari nta bana b'abahungu yabyaye, keretse abakobwa gusa. Bene se wabo ari bo bene Kishi, barabarongora.",
			"23": "<sup>23</sup> Bene Mushi ni Mahali na Ederi na Yeremoti, uko ari batatu.",
			"24": "<sup>24</sup> Abo ni bo bene Lewi uko amazu ya ba sekuruza yari ari, ni bo batware b'amazu ya ba sekuruza b'ababazwe, ubwo babarwaga mu mazina umwe umwe. Ni bo bakoraga umurimo wo mu nzu y'Uwiteka, ari abantu bashyikije imyaka makumyabiri n'abayishagije.",
			"25": "<sup>25</sup> Kuko Dawidi yari yavuze ati “Uwiteka Imana ya Isirayeli ihaye ubwoko bwayo ihumure kandi iba i Yerusalemu iteka ryose,",
			"26": "<sup>26</sup> kandi Abalewi ntibazaba bacyongera guheka ihema n'ibintu byakorewe umurimo waryo byose.”",
			"27": "<sup>27</sup> Amagambo ya Dawidi yaherutse ni yo yatumye Abalewi babarwa, abari bashyikije imyaka makumyabiri n'abayishagije,",
			"28": "<sup>28</sup> kuko umurimo wabo wari uwo gufasha bene Aroni mu murimo w'inzu y'Uwiteka mu bikari no mu byumba, n'iyo bezaga ibintu byera byose, n'iby'umurimo bakoraga mu nzu y'Imana,",
			"29": "<sup>29</sup> kandi n'imitsima yo kumurikwa imbere y'Imana, n'ifu y'ingezi yo gutura amaturo y'ifu y'impeke, ay'udutsima tudasembuwe, n'ay'ibyakarangwaga ku byuma, n'ay'ibirunzwemo amavuta, n'iby'indengo n'imibyimba bitari bimwe,",
			"30": "<sup>30</sup> no guhagarara uko bukeye bagashima, bagahimbaza Uwiteka kandi na nimugoroba bakabigenza batyo,",
			"31": "<sup>31</sup> no gutambira Uwiteka ibitambo byoswa byose, ku masabato no ku mboneko z'amezi no mu birori byategetswe, nk'uko umubare wabyo wari uri ukurikije amategeko yabyo, (bigakorwa) ubudasiba imbere y'Uwiteka,",
			"32": "<sup>32</sup> no kurinda ihema ry'ibonaniro aho barindishijwe, n'Ahera aho barindishijwe, n'ibya bene Aroni bene wabo barindishijwe, ngo bajye bakora umurimo wo mu nzu y'Uwiteka."
		},
		"24": {
			"1": "<sup>1</sup> Ibihe bya bene Aroni byari ibi: bene Aroni ni Nadabu na Abihu, na Eleyazari na Itamari.",
			"2": "<sup>2</sup> Nadabu na Abihu babanjirije se gupfa, kandi nta bana bagiraga. Ni cyo cyatumye Eleyazari na Itamari bakora umurimo w'ubutambyi.",
			"3": "<sup>3</sup> Dawidi afatanya na Sadoki wo muri bene Eleyazari, na Ahimeleki wo muri bene Itamari kubagabanya imirimo nk'uko ibihe byabo byari biri.",
			"4": "<sup>4</sup> Kandi muri bene Eleyazari habonekamo abagabo bakomeye benshi baruta abo muri bene Itamari, uko ni ko bagabanijwe: muri bene Eleyazari bari abatware b'amazu ya ba sekuruza cumi na batandatu, kandi muri bene Itamari uko amazu ya ba sekuruza babo yari ari, bari umunani.",
			"5": "<sup>5</sup> Uko ni ko bagabanijwe n'ubufindo ibice byombi, kuko hariho abatware b'ubuturo bwera n'abatware b'Imana muri bene Eleyazari na bene Itamari.",
			"6": "<sup>6</sup> Nuko Shemaya mwene Netanēli umwanditsi wo mu Balewi, abandikira imbere y'umwami n'abatware n'imbere y'umutambyi Sadoki, na Ahimeleki mwene Abiyatari, n'imbere y'abatware b'amazu ya ba sekuruza b'abatambyi n'ab'Abalewi, inzu imwe itoranirizwa Eleyazari n'indi itoranirizwa Itamari.",
			"7": "<sup>7</sup> Maze ubufindo bwa mbere butoranya Yehoyaribu, ubwa kabiri Yedaya.",
			"8": "<sup>8</sup> Ubwa gatatu Harimu, ubwa kane Seworimu.",
			"9": "<sup>9</sup> Ubwa gatanu Malikiya, ubwa gatandatu Miyamini.",
			"10": "<sup>10</sup> Ubwa karindwi Hakosi, ubwa munani Abiya.",
			"11": "<sup>11</sup> Ubwa cyenda Yeshuwa, ubwa cumi Shekaniya.",
			"12": "<sup>12</sup> Ubwa cumi na bumwe Eliyashibu, ubwa cumi na bubiri Yakimu.",
			"13": "<sup>13</sup> Ubwa cumi na butatu Hupa, ubwa cumi na bune Yeshebeyabu.",
			"14": "<sup>14</sup> Ubwa cumi na butanu Biluga, ubwa cumi na butandatu Imeri.",
			"15": "<sup>15</sup> Ubwa cumi na burindwi Heziri, ubwa cumi n'umunani Hapisesi.",
			"16": "<sup>16</sup> Ubwa cumi n'icyenda Petahiya, ubwa makumyabiri Yezekeli.",
			"17": "<sup>17</sup> Ubwa makumyabiri na bumwe Yakini, ubwa makumyabiri na bubiri Gamuli.",
			"18": "<sup>18</sup> Ubwa makumyabiri na butatu Delaya, ubwa makumyabiri na bune Māziya.",
			"19": "<sup>19</sup> Ibi ni byo bihe byabo uko bakoraga, ngo bajye binjira mu nzu y'Uwiteka bakurikije itegeko bahawe na Aroni sekuruza wabo, nk'uko Uwiteka Imana ya Isirayeli yamutegetse.",
			"20": "<sup>20</sup> Ngaba abatware bo muri bene Lewi bandi: uwa bene Amuramu ni Shubayeli, uwa bene Shubayeli ni Yedeya.",
			"21": "<sup>21</sup> Mu Barehabiya, umutware wabo ni Ishiya. Ni we wari mukuru.",
			"22": "<sup>22</sup> Uwa bene Isuhari ni Shelomoti, uwa bene Shelomoti ni Yahati.",
			"23": "<sup>23</sup> Uwa bene Heburoni: uwa mbere ni Yeriya, uwa kabiri ni Amariya, uwa gatatu ni Yahaziyeli, uwa kane ni Yekameyamu.",
			"24": "<sup>24</sup> Bene Uziyeli ni Mika, uwa bene Mika ni Shamiri.",
			"25": "<sup>25</sup> Murumuna wa Mika ni Ishiya, uwa bene Ishiya ni Zekariya.",
			"26": "<sup>26</sup> Bene Merari ni Mahali na Mushi, bene Yāziya ni Beno.",
			"27": "<sup>27</sup> Bene Merari, aba Yāziya ni Beno na Shohamu, na Zakuri na Iburi.",
			"28": "<sup>28</sup> Uwa Mahali ni Eleyazari, kandi nta bana b'abahungu yari afite.",
			"29": "<sup>29</sup> Mu Bakishi, bene Kishi ni Yeramēli.",
			"30": "<sup>30</sup> Na bene Mushi ni Mahali na Ederi na Yerimoti. Abo ni bo bahungu b'Abalewi nk'uko amazu ya ba sekuruza babo yari ari.",
			"31": "<sup>31</sup> Kandi na bo bafinda ubufindo nka bene wabo, ari bo bahungu ba Aroni, imbere y'Umwami Dawidi n'imbere ya Sadoki na Ahimeleki, kandi n'imbere y'abatware b'amazu ya ba sekuruza b'abatambyi n'Abalewi. Umutware w'inzu ya ba sekuruza yafindaga nka murumuna we."
		},
		"25": {
			"1": "<sup>1</sup> Kandi Dawidi n'abatware b'ingabo, batoraniriza uwo murimo bamwe bo muri bene Asafu n'aba Hemani n'aba Yedutuni, ngo bahanuze inanga na nebelu n'ibyuma bivuga. Amazina y'abakoraga uwo murimo uko bajyaga ibihe ni aya:",
			"2": "<sup>2</sup> Abo muri bene Asafu ni Zakuri na Yosefu, na Netaniya na Asarela abahungu ba Asafu, batwarwaga na Asafu wahanuraga uko itegeko ry'umwami ryari riri.",
			"3": "<sup>3</sup> Aba Yedutuni, abahungu be ni Gedaliya na Seri, na Yeshaya na Hashabiya na Matitiya, uko ari batandatu batwarwaga na se Yedutuni, wagiraga inanga ahanura mu buryo bwo gushima no guhimbaza Uwiteka.",
			"4": "<sup>4</sup> Aba Hemani, abahungu be Bukiya na Mataniya na Uziyeli, na Shebuweli na Yerimoti na Hananiya, na Hanani na Eliyata na Gidaliti' na Romamutiyezeri na Yoshibekasha, na Maloti na Hotiri na Mahaziyoti.",
			"5": "<sup>5</sup> Abo bose bari abahungu ba Hemani bamenya w'umwami mu magambo y'Imana, wo gushyira hejuru ihembe. Imana iha Hemani abahungu cumi na bane n'abakobwa batatu.",
			"6": "<sup>6</sup> Abo bose batwarwaga na se, bakaririmbira mu nzu y'Uwiteka babwira ibyuma bivuga na nebelu n'inanga, ngo bakore umurimo wo mu nzu y'Imana, kandi Asafu na Yedutuni na Hemani bategekwaga n'umwami.",
			"7": "<sup>7</sup> Nuko umubare wabo hamwe na bene wabo, abari bigishijwe kuririmbira Uwiteka, abahanga bose bari magana abiri na mirongo inani n'umunani.",
			"8": "<sup>8</sup> Maze bafindira imirimo yabo ubufindo bose baranganya, aboroheje n'abakomeye, umwigisha n'umwigishwa.",
			"9": "<sup>9</sup> Nuko ubufindo bwa mbere butoranya Yosefu, ku bwa Asafu. Ubwa kabiri Gedaliya, we na bene se n'abahungu be bari cumi na babiri.",
			"10": "<sup>10</sup> Ubwa gatatu Zakuri n'abahungu be na bene se, bari cumi na babiri.",
			"11": "<sup>11</sup> Ubwa kane Isuri n'abahungu be na bene se, bari cumi na babiri.",
			"12": "<sup>12</sup> Ubwa gatanu Netaniya n'abahungu be na bene se, bari cumi na babiri.",
			"13": "<sup>13</sup> Ubwa gatandatu Bukiya n'abahungu be na bene se, bari cumi na babiri.",
			"14": "<sup>14</sup> Ubwa karindwi Yesharela n'abahungu be na bene se, bari cumi na babiri.",
			"15": "<sup>15</sup> Ubwa munani Yeshaya n'abahungu be na bene se, bari cumi na babiri.",
			"16": "<sup>16</sup> Ubwa cyenda Mataniya n'abahungu be na bene se, bari cumi na babiri.",
			"17": "<sup>17</sup> Ubwa cumi Shimeyi n'abahungu be na bene se, bari cumi na babiri.",
			"18": "<sup>18</sup> Ubwa cumi na bumwe Azarēli n'abahungu be na bene se, bari cumi na babiri.",
			"19": "<sup>19</sup> Ubwa cumi na bubiri Hashabiya n'abahungu be na bene se, bari cumi na babiri.",
			"20": "<sup>20</sup> Ubwa cumi na butatu Shubayeli n'abahungu be na bene se, bari cumi na babiri.",
			"21": "<sup>21</sup> Ubwa cumi na bune Matitiya n'abahungu be na bene se, bari cumi na babiri.",
			"22": "<sup>22</sup> Ubwa cumi na butanu Yerimoti n'abahungu be na bene se, bari cumi na babiri.",
			"23": "<sup>23</sup> Ubwa cumi na butandatu Hananiya n'abahungu be na bene, se bari cumi na babiri.",
			"24": "<sup>24</sup> Ubwa cumi na burindwi Yoshibekasha n'abahungu be na bene se, bari cumi na babiri.",
			"25": "<sup>25</sup> Ubwa cumi n'umunani Hanani n'abahungu be na bene se, bari cumi na babiri.",
			"26": "<sup>26</sup> Ubwa cumi n'icyenda Maloti n'abahungu be na bene se, bari cumi na babiri.",
			"27": "<sup>27</sup> Ubwa makumyabiri Eliyata n'abahungu be na bene se, bari cumi na babiri.",
			"28": "<sup>28</sup> Ubwa makumyabiri na bumwe Hotiri n'abahungu be na bene se bari cumi na babiri.",
			"29": "<sup>29</sup> Ubwa makumyabiri na bubiri Gidaliti n'abahungu be na bene se, bari cumi na babiri.",
			"30": "<sup>30</sup> Ubwa makumyabiri na butatu Mahaziyoti n'abahungu be na bene se, bari cumi na babiri.",
			"31": "<sup>31</sup> Ubwa makumyabiri na bune Romamutiyezeri n'abahungu be na bene se, bari cumi na babiri."
		},
		"26": {
			"1": "<sup>1</sup> Ibihe by'abakumirizi ni ibi: mu Bakōra ni Meshelemiya mwene Kore, wo muri bene Asafu.",
			"2": "<sup>2</sup> Meshelemiya yari afite abana b'abahungu: uw'imfura ni Zekariya, uw'ubuheta ni Yediyayeli, uwa gatatu ni Zebadiya, uwa kane ni Yatiniyeli,",
			"3": "<sup>3</sup> uwa gatanu ni Elamu, uwa gatandatu ni Yehohanani, uwa karindwi ni Eliyowenayi.",
			"4": "<sup>4</sup> Obededomu na we yari afite abana b'abahungu: uw'imfura ni Shemaya, uw'ubuheta ni Yehozabadi, uwa gatatu ni Yowa, uwa kane ni Sakari, uwa gatanu ni Netanēli,",
			"5": "<sup>5</sup> uwa gatandatu ni Amiyeli, uwa karindwi ni Isakari, uwa munani ni Pewuletayi, kuko Imana ihaye Obededomu umugisha.",
			"6": "<sup>6</sup> Kandi umuhungu we Shemaya abyara abana b'abahungu, ari bo batwaye inzu ya se kuko bari abagabo b'abanyambaraga b'intwari.",
			"7": "<sup>7</sup> Bene Shemaya ni Otuni na Refayeli na Obedi na Elizabadi, na bene se bari intwari ari bo Elihu na Semakiya.",
			"8": "<sup>8</sup> Abo bose bari abo mu bahungu ba Obededomu, bo n'abahungu babo na bene se, abantu b'abanyambaraga babasha uwo murimo, bari bene Obededomu mirongo itandatu na babiri.",
			"9": "<sup>9</sup> Meshelemiya yari afite abana b'abahungu, na bene se abagabo b'intwari cumi n'umunani.",
			"10": "<sup>10</sup> Kandi Hosa wo muri bene Merari, yari afite abana b'abahungu. Shimuri ni we wari umukuru wabo (kuko nubwo atari uw'imfura, se yamugize umutware).",
			"11": "<sup>11</sup> Uwa kabiri ni Hilukiya, uwa gatatu ni Tebaliya, uwa kane ni Zekariya. Abahungu ba Hosa na bene se bose bari cumi na batatu.",
			"12": "<sup>12</sup> Ibihe by'abakumirizi byari ibyabo, ni byo by'abagabo bakomeye bafite ibyo bashinzwe nka bene wabo, bagakora umurimo wo mu nzu y'Uwiteka.",
			"13": "<sup>13</sup> Bafindira aboroheje n'abakomeye uko amazu ya ba sekuruza yari ari, ngo bamenye abo kurinda amarembo yose.",
			"14": "<sup>14</sup> Ubwo ku ruhande rw'iburasirazuba bwerekana Shelemiya. Maze bafindira umuhungu wa Zekariya umujyanama w'umunyabwenge, ubwe bwerekana uruhande rw'ikasikazi.",
			"15": "<sup>15</sup> Kandi ubwa Obededomu bwerekana uruhande rw'ikusi, n'abahungu be bahabwa ububiko.",
			"16": "<sup>16</sup> Ubwa Shupimu na Hosa bwerekana uruhande rw'iburengerazuba ku irembo rya Sheleketi, ku rutindo ruzamukirwaho, abarinzi bateganye n'abarinzi bandi.",
			"17": "<sup>17</sup> Uruhande rw'iburasirazuba hariho Abalewi batandatu, n'urw'ikasikazi babaga bane uko bukeye, urw'ikusi babaga bane uko bukeye, n'ab'ububiko babiri babiri.",
			"18": "<sup>18</sup> Kandi i Parubari h'iburengerazuba babaga bane ku rutindo, na babiri i Parubari.",
			"19": "<sup>19</sup> Ibyo ni byo bihe by'abakumirizi bo muri bene Kōra, n'abo muri bene Merari.",
			"20": "<sup>20</sup> Mu Balewi, Ahiya ni we wari umutware w'ububiko bw'inzu y'Imana, kandi w'ububiko bw'ibintu byashinganywe.",
			"21": "<sup>21</sup> Bene Lādani ni bo Bagerushoni ba Lādani, abatware b'amazu ya ba sekuruza ya Lādani w'Umugerushoni, ni Yehiyeli.",
			"22": "<sup>22</sup> Bene Zetamu na murumuna we Yoweli, ni bo bari abatware b'ububiko bw'inzu y'Uwiteka.",
			"23": "<sup>23</sup> Mu Bamuramu no mu Bisuhari, no mu Baheburoni no mu Buziyeli,",
			"24": "<sup>24</sup> Shebuweli mwene Gerushomu mwene Mose, ni we wari umutware w'ububiko.",
			"25": "<sup>25</sup> Kandi bene se, Eliyezeri abyara Rehabiya, mwene Rehabiya ni Yeshaya, mwene Yeshaya ni Yoramu, mwene Yoramu ni Zikiri, mwene Zikiri ni Shelomoti.",
			"26": "<sup>26</sup> Shelomoti uwo na bene se ni bo bari abatware b'ububiko bw'ibintu byashinganywe, ibyo Umwami Dawidi n'abatware b'amazu ya ba sekuruza, n'abatware batwara ibihumbi n'abatwara amagana n'abagaba b'ingabo bashinganye.",
			"27": "<sup>27</sup> Ibyo bashinganye byavuye mu minyago yo mu ntambara, babishinganira gusana inzu y'Uwiteka.",
			"28": "<sup>28</sup> Kandi ibyo Samweli bamenya, na Sawuli mwene Kishi na Abuneri mwene Neri, na Yowabu mwene Seruya bashinganye, umuntu wese washinganaga ikintu cyose, byategekwaga na Shelomoti na bene se.",
			"29": "<sup>29</sup> Mu Bisuhari, Kenaniya n'abahungu be bategekaga Abisirayeli ku murimo wo hanze, bakaba abatware n'abacamanza.",
			"30": "<sup>30</sup> Mu Baheburoni, Hashabiya na bene se, abagabo b'intwari igihumbi na magana arindwi, ni bo batwaraga Abisirayeli bo hakurya ya Yorodani iburengerazuba, ku murimo w'Uwiteka wose no ku murimo w'umwami.",
			"31": "<sup>31</sup> Mu Baheburoni Yeriya ni we wari umutware wabo. (Iby'Abaheburoni mu mwaka wa mirongo ine ku ngoma ya Dawidi, babirondoye mu gitabo cy'amazu ya ba sekuruza uko babyaranye. Muri bo basanga abagabo b'abanyambaraga b'intwari i Yazeri y'i Galeyadi.)",
			"32": "<sup>32</sup> Na bene se, abagabo b'intwari bari ibihumbi bibiri na magana arindwi, abatware b'amazu ya ba sekuruza. Ni bo Umwami Dawidi yagize ibisonga mu Barubeni n'Abagadi n'ab'igice cy'umuryango wa Manase, cy'ibintu by'Imana byose n'icy'iby'umwami."
		},
		"27": {
			"1": "<sup>1</sup> Abisirayeli uko umubare wabo wari uri, ni bo batware b'amazu ya ba sekuruza n'abatware batwara ibihumbi n'abatwara amagana, n'abatware bakoreraga umwami umurimo wose w'ibihe byajyaga biha ibindi, uko ukwezi gutashye mu mezi yose y'umwaka, bari inzovu ebyiri n'ibihumbi bine.",
			"2": "<sup>2</sup> Yashobeyamu mwene Zabudiyeli, ni we wari umutware w'igihe cya mbere cyo mu kwezi kwa mbere. Abari mu gihe cye bari inzovu ebyiri n'ibihumbi bine.",
			"3": "<sup>3</sup> Uwo yari uwo muri bene Perēsi, akaba n'umutware w'abagaba b'ingabo bose bo mu kwezi kwa mbere.",
			"4": "<sup>4</sup> Dodayi w'Umwahohi, ni we wari umutware w'igihe cy'ukwezi kwa kabiri, Mikuloti yari umwe mu batware bo mu gihe cye, kandi mu gihe cye harimo abantu inzovu ebyiri n'ibihumbi bine.",
			"5": "<sup>5</sup> Benaya mwene Yehoyada umutambyi mukuru, ni we wari umugaba w'ingabo wa gatatu wo mu kwezi kwa gatatu. Kandi mu gihe cye harimo abantu inzovu ebyiri n'ibihumbi bine.",
			"6": "<sup>6</sup> Benaya uwo ni we wari umugabo w'umunyambaraga wo muri abo mirongo itatu, kandi ni we wari umutware wabo. Umuhungu we Amizabadi yari mu gihe cye.",
			"7": "<sup>7</sup> Asaheli murumuna wa Yowabu, ni we wari umutware wa kane wo mu kwezi kwa kane, agakurikirwa n'umuhungu we Zebadiya. Mu gihe cye harimo abantu inzovu ebyiri n'ibihumbi bine.",
			"8": "<sup>8</sup> Shamuhuti w'Umwizura, ni we wari umutware wa gatanu wo mu kwezi kwa gatanu. Mu gihe cye harimo abantu inzovu ebyiri n'ibihumbi bine.",
			"9": "<sup>9</sup> Ira mwene Ikeshi w'Umunyatekowa, ni we wari umutware wa gatandatu wo mu kwezi kwa gatandatu. Mu gihe cye harimo abantu inzovu ebyiri n'ibihumbi bine.",
			"10": "<sup>10</sup> Helesi w'Umupeloni wo mu Befurayimu, ni we wari umutware wa karindwi wo mu kwezi kwa karindwi. Mu gihe cye harimo abantu inzovu ebyiri n'ibihumbi bine.",
			"11": "<sup>11</sup> Sibekayi w'Umuhusha w'Abazera, ni we wari umutware wa munani wo mu kwezi kwa munani. Mu gihe cye harimo abantu inzovu ebyiri n'ibihumbi bine.",
			"12": "<sup>12</sup> Abiyezeri w'Umunyanatoti w'Ababenyamini, ni we wari umutware wa cyenda wo mu kwezi kwa cyenda. Mu gihe cye harimo abantu inzovu ebyiri n'ibihumbi bine.",
			"13": "<sup>13</sup> Maharayi w'Umunyanetofa w'Abazera, ni we wari umutware wa cumi wo mu kwezi kwa cumi. Mu gihe cye harimo abantu inzovu ebyiri n'ibihumbi bine.",
			"14": "<sup>14</sup> Benaya w'Umunyapiratoni w'Abefurayimu, ni we wari umutware wa cumi n'umwe wo mu kwezi kwa cumi na kumwe. Mu gihe cye harimo abantu inzovu ebyiri n'ibihumbi bine.",
			"15": "<sup>15</sup> Heludayi w'Umunyanetofa mwene Otiniyeli, ni we wari umutware wa cumi na babiri wo mu kwezi kwa cumi na babiri. Mu gihe cye harimo abantu inzovu ebyiri n'ibihumbi bine.",
			"16": "<sup>16</sup> Kandi abatwaraga imiryango ya Isirayeli, mu muryango w'Abarubeni ni Eliyezeri umuhungu w'umutware Zikiri, mu w'Abasimeyoni ni Shefata mwene Māka,",
			"17": "<sup>17</sup> mu w'Abalewi ni Hashabiya mwene Kemuweli, mu ba Aroni ni Sadoki.",
			"18": "<sup>18</sup> Mu wa Yuda, ni Elihu umwe wo muri bene se ba Dawidi, mu wa Isakari ni Omuri mwene Mikayeli.",
			"19": "<sup>19</sup> Mu wa Zebuluni ni Ishimaya mwene Obadiya, mu wa Nafutali ni Yerimoti mwene Aziriyeli,",
			"20": "<sup>20</sup> mu wa Efurayimu ni Hoseya mwene Azaziya, mu gice cy'uwa Manase ni Yoweli mwene Pedaya.",
			"21": "<sup>21</sup> Mu gice cy'uwa Manase muri Galeyadi ni Ido mwene Zekariya, mu wa Benyamini ni Yāsiyeli mwene Abuneri.",
			"22": "<sup>22</sup> Mu wa Dani ni Azarēli mwene Yerohamu. Abo ni bo batware b'imiryango ya Isirayeli.",
			"23": "<sup>23</sup> Ariko Dawidi ntiyabara abashyikije imyaka makumyabiri y'ubukuru n'abatarayigezaho, kuko Uwiteka yavuze ko azagwiza Abisirayeli akabanganya n'inyenyeri zo mu ijuru.",
			"24": "<sup>24</sup> Yowabu mwene Seruya atangira kubara ntiyarangiza. Ni cyo cyazaniye Abisirayeli umujinya, kandi umubare ntiwanditswe mu gitabo cy'ibyo ku ngoma y'Umwami Dawidi.",
			"25": "<sup>25</sup> Azimaveti mwene Adiyeli ni we wari umutware w'ububiko bw'umwami, Yehonatani mwene Uziya ni we wari umutware w'ububiko bwo mu mirima, no mu midugudu no mu birorero no mu bihome,",
			"26": "<sup>26</sup> Eziri mwene Kelubu ni we watwaraga abahinzi bo mu misozi bahingaga ubutaka.",
			"27": "<sup>27</sup> Shimeyi w'i Rama ni we wari umutware w'inzabibu, na Zabudi w'i Shifimu ni we wari umutware w'imbuto z'inzabibu zo gushyira mu bubiko bwa vino.",
			"28": "<sup>28</sup> Bālihanani w'i Gederi ni we wari umutware w'imyelayo n'imishishima yo mu kibaya, na Yowasi ni we wari umutware w'ububiko bw'amavuta.",
			"29": "<sup>29</sup> Shiturayi w'i Sharoni ni we wari umutahira w'amashyo yarishirizaga i Sharoni, kandi Shafati mwene Adulayi ni we wari umutahira w'amashyo yo mu mibande.",
			"30": "<sup>30</sup> Obili w'Umwishimayeli ni we wari umutware w'ingamiya, na Yedeya w'Umunyameronoti ni we wari umutware w'indogobe.",
			"31": "<sup>31</sup> Yazizi w'Umuhagari ni we wari umutahira w'imikumbi. Abo bose ni bo bari abatware b'ibintu by'Umwami Dawidi.",
			"32": "<sup>32</sup> Yonatani se wabo wa Dawidi ni we wari umujyanama, umugabo w'umunyabwenge kandi w'umwanditsi, na Yehiyeli mwene Hakimoni yabaga ku bana b'umwami.",
			"33": "<sup>33</sup> Ahitofeli ni we wari umujyanama w'umwami, na Hushayi w'Umwaruki yari incuti y'umwami.",
			"34": "<sup>34</sup> Ahitofeli yakurikiwe na Yehoyada mwene Benaya na Abiyatari, kandi Yowabu ni we wari umugaba w'ingabo z'umwami."
		},
		"28": {
			"1": "<sup>1</sup> Dawidi yateranirije i Yerusalemu abatware ba Isirayeli bose, n'abatware b'imiryango n'abatware b'imitwe yakoreraga umwami bafata ibihe, n'abatware batwara ibihumbi n'abatwara amagana, n'abatware b'ibintu byose n'amatungo by'umwami, n'abahungu be n'inkone, n'abagabo b'abanyambaraga b'intwari bose.",
			"2": "<sup>2</sup> Umwami Dawidi aherako arahaguruka, avuga ahagaze ati “Nimunyumve bene data kandi bantu banjye, nari mbisanganywe mu mutima kuzubakira Isanduku y'isezerano ry'Uwiteka, inzu yo kuruhukiramo ikaba n'intebe y'ibirenge by'Imana yacu, kandi nari niteguye kubaka.",
			"3": "<sup>3</sup> Ariko Imana irambwira iti ‘Ntiwubakire izina ryanjye inzu kuko uri umugabo w'umunyantambara, kandi wavushije amaraso menshi.’",
			"4": "<sup>4</sup> Nyamara Uwiteka Imana ya Isirayeli yarandobanuye, intoranya mu muryango wa data wose ngo mbe umwami wa Isirayeli iteka ryose, kuko yatoranije Yuda ikamugira imfura, kandi mu muryango wa Yuda igatoranyamo inzu ya data, kandi mu bahungu ba data ikaba ari jye yishimira kugira umwami w'Abisirayeli bose.",
			"5": "<sup>5</sup> Kandi mu bahungu banjye bose (kuko Uwiteka yampaye abana b'abahungu benshi), atoranyamo umuhungu wanjye Salomo kuba ari we wicara ku ntebe y'ubwami bw'Uwiteka, ategeka Isirayeli.",
			"6": "<sup>6</sup> “Arambwira ati ‘Umuhungu wawe Salomo ni we uzubaka inzu yanjye n'ibikari byanjye, kuko namutoranije ngo abe umwana wanjye, nanjye mbe se.",
			"7": "<sup>7</sup> Kandi nzakomeza ubwami bwe iteka ryose, nagira umwete wo kwitondera amategeko n'amateka byanjye nk'uko ameze kuri ubu.’",
			"8": "<sup>8</sup> “Nuko rero imbere y'Abisirayeli bose ari ryo teraniro ry'Uwiteka, kandi imbere y'Imana yacu yumva, mwitondere amategeko yose y'Uwiteka Imana yanyu muyamenye, mubone gutwara iki gihugu cyiza mukazakiraga abana banyu bazabazungura, kibe gakondo yabo iteka ryose.",
			"9": "<sup>9</sup> “Nawe Salomo mwana wanjye, umenye Imana ya so, ujye uyikorera n'umutima utunganye kandi ukunze, kuko Uwiteka agenzura imitima yose, akamenya ibyo imitima yibwira byose, numushaka uzamubona, ariko numureka azaguca iteka ryose.",
			"10": "<sup>10</sup> Nuko wirinde, kuko Uwiteka ari wowe yatoranije kubakira inzu ubuturo bwera, shyiraho imbaraga ubikore.”",
			"11": "<sup>11</sup> Nuko Dawidi aha umuhungu we Salomo icyitegererezo cy'ibaraza ry'urusengero, n'icy'amazu yarwo n'icy'ububiko bwarwo, n'icy'ibyumba byarwo byo hejuru, n'ibyo muri rwo imbere, n'icy'ahantu h'intebe y'ihongerero.",
			"12": "<sup>12</sup> N'icyitegererezo cy'ibyo yaheshejwe n'umwuka byose, iby'ibikari by'inzu y'Uwiteka, n'iby'ibyumba biyikikije byose, n'ububiko bw'inzu y'Imana, n'ububiko bw'ibintu byashinganywe.",
			"13": "<sup>13</sup> Kandi n'iby'ibihe by'abatambyi n'Abalewi, n'iby'imirimo yose yakorerwaga inzu y'Uwiteka, n'iby'ibintu byose byakoreshwaga mu nzu y'Uwiteka.",
			"14": "<sup>14</sup> Kandi amuha izahabu zigezwe zo gucurishwamo ibintu by'izahabu byose bikoreshwa imirimo yose, amuha n'ifeza zigezwe zo gucurishamo ibintu by'ifeza byose bikoreshwa imirimo yose.",
			"15": "<sup>15</sup> Kandi amugerera izahabu zo gucurishwamo ibitereko by'amatabaza n'amatabaza yabyo y'izahabu, agera izahabu z'igitereko cyose n'iz'amatabaza yacyo, kandi amugerera n'ifeza z'ibitereko by'amatabaza by'ifeza, agera ifeza z'igitereko cyose n'iz'amatabaza yacyo, uko igitereko cyose gikoreshwa.",
			"16": "<sup>16</sup> Amugerera n'izahabu z'ameza y'imitsima yo kumurikwa imbere y'Imana, izahabu z'ameza yose, kandi n'ifeza z'ameza y'ifeza,",
			"17": "<sup>17</sup> n'ibyo kwaruza inyama n'ibyungu n'ibikombe by'izahabu nziza, agerera icyungu cy'izahabu cyose izahabu zacyo, n'icy'ifeza cyose ifeza zacyo.",
			"18": "<sup>18</sup> Kandi agerera igicaniro cyo koserezaho imibavu izahabu itunganijwe, kandi amuha n'izahabu z'igishushanyo cy'igare, n'ibishushanyo by'abakerubi batanze amababa bagatwikira isanduku y'isezerano ry'Uwiteka.",
			"19": "<sup>19</sup> Dawidi aravuga ati “Ibyo byose nabimenyeshejwe n'ibyanditswe n'ukuboko k'Uwiteka, iyo ni yo mirimo yose ikurikije iki cyitegererezo.”",
			"20": "<sup>20</sup> Maze Dawidi abwira umuhungu we Salomo ati “Komera ushikame uzabikore, ntutinye kandi ntukuke umutima, kuko Uwiteka Imana ari yo Mana yanjye izabana nawe. Ntizagusiga, ntizaguhāna kugeza aho imirimo yose y'ibizakoreshwa mu nzu y'Uwiteka izarangirira.",
			"21": "<sup>21</sup> Kandi dore hariho n'ibihe by'abatambyi n'Abalewi by'umurimo wose w'inzu y'Imana, kandi mu murimo w'uburyo bwose uzaba ufite umuntu w'umuhanga ukunze wese wo gukora umurimo wose, kandi n'abatware na ba rubanda bose bazahora biteguye kukumvira rwose.”"
		},
		"29": {
			"1": "<sup>1</sup> Umwami Dawidi abwira iteraniro ryose ati “Umuhungu wanjye Salomo, uwo Imana itoranije wenyine, aracyari umwana ntarakomeza intege kandi umurimo urakomeye, kuko iyo nzu y'inyumba itazaba iy'abantu, ahubwo izaba iy'Uwiteka Imana.",
			"2": "<sup>2</sup> Jyewe niteguye inzu y'Imana yanjye uko nshoboye kose, nshaka izahabu z'ibintu by'izahabu, n'ifeza z'ibintu by'ifeza, n'imiringa y'ibintu by'imiringa, n'ibyuma by'ibintu by'ibyuma, n'ibiti by'ibintu by'ibiti, n'amabuye yitwa shohamu n'ayandi mabuye yo guhunda, n'amabuye arabagirana n'ay'amabara menshi, n'ay'igiciro cyinshi y'amoko yose, n'amabuye yitwa marimari atagira akagero.",
			"3": "<sup>3</sup> Kandi rero ku bw'urukundo nkunze inzu y'Imana yanjye, kuko mfite ubutunzi bwanjye bwite bw'izahabu n'ifeza, mbuhaye inzu y'Imana yanjye busāge ku byo natunganirije inzu yera byose,",
			"4": "<sup>4</sup> n'italanto z'izahabu za Ofiri ibihumbi bitatu, n'italanto z'ifeza itunganijwe ibihumbi birindwi zo gutera ku nsika z'amazu,",
			"5": "<sup>5</sup> izahabu z'ibintu by'izahabu n'ifeza z'ibintu by'ifeza, n'iz'ibintu by'uburyo bwose bikorwa n'abanyamyuga b'abahanga. Nuko rero, uyu munsi ni nde wemeye kwitanga ku Uwiteka?”",
			"6": "<sup>6</sup> Maze abatware b'amazu ya ba sekuruza n'abatware b'imiryango ya Isirayeli, n'abatware batwara ibihumbi n'abatwara amagana, n'abatware b'imirimo y'umwami batangana umutima ukunze.",
			"7": "<sup>7</sup> Batanga italanto z'izahabu ibihumbi bitanu na dariki inzovu imwe, n'italanto z'ifeza inzovu imwe, n'italanto z'imiringa inzovu imwe n'ibihumbi munani, n'italanto z'ibyuma agahumbi ngo bikoreshwe umurimo w'inzu y'Imana.",
			"8": "<sup>8</sup> Kandi abari bafite amabuye y'igiciro cyinshi barayatanga, bayashyira mu by'ubutunzi bw'inzu y'Uwiteka, bwatwarwaga na Yehiyeli w'Umugerushoni.",
			"9": "<sup>9</sup> Maze abantu banezezwa n'uko bemeye gutura, kuko batuye Uwiteka bafite umutima utunganye, kandi n'Umwami Dawidi na we yishima ibyishimo byinshi.",
			"10": "<sup>10</sup> Ni cyo cyatumye Dawidi ashimira Uwiteka imbere y'iteraniro ryose, aravuga ati “Uwiteka Mana ya sogokuruza wacu Isirayeli, uhimbazwe iteka ryose.",
			"11": "<sup>11</sup> Uwiteka, gukomera n'imbaraga n'icyubahiro, no kunesha n'igitinyiro ni ibyawe, kuko ibiri mu ijuru n'ibiri mu isi ari ibyawe. Ubwami ni ubwawe Uwiteka, ushyizwe hejuru ngo ube usumba byose.",
			"12": "<sup>12</sup> Ubutunzi n'icyubahiro ni wowe biturukaho kandi ni wowe utegeka byose. Mu kuboko kwawe harimo ububasha n'imbaraga, kandi kogeza no guhesha bose imbaraga biri mu butware bwawe.",
			"13": "<sup>13</sup> Nuko rero Mana yacu, turagushima dusingiza izina ryawe ry'icyubahiro.",
			"14": "<sup>14</sup> “Ariko nkanjye ndi nde n'abantu banjye, byatuma dushobora gutura tubikunze dutyo rwose, kuko byose ari wowe biturukaho, kandi ibyawe akaba ari byo tuguhayeho?",
			"15": "<sup>15</sup> Kuko turi abashyitsi imbere yawe, n'abasuhuke nk'uko ba sogokuruza bacu bose bari bari, iminsi yacu tumara mu isi ihwanye n'igicucu, nta byiringiro byo kurama.",
			"16": "<sup>16</sup> Uwiteka Mana yacu, ibi bintu byose twiteguye kūbakira izina ryawe ryera inzu, bituruka mu kuboko kwawe kandi byose ni ibyawe.",
			"17": "<sup>17</sup> Kandi Mana yanjye nzi yuko ugerageza umutima, ukishimira gutungana. Nanjye ntuye ibi bintu byose n'umutima ukunze kandi utunganye, kandi ubu mbonye abantu bawe bari hano bagutura n'imitima ikunze, biranezeza.",
			"18": "<sup>18</sup> Uwiteka Mana ya Aburahamu, Mana ya Isaka, Mana ya Isirayeli ba sogokuruza bacu, ibyo ujye ubikomeza mu bitekerezo abantu bawe bagira mu mitima yabo, utunganye imitima yabo ikwerekereho.",
			"19": "<sup>19</sup> Kandi umuhungu wanjye Salomo, umuhe umutima utunganye wo kwitondera amategeko yawe n'ibyo wahamije n'amateka yawe, kandi akore ibyo byose, yubake n'inzu y'inyumba niteguriye kuyubaka.”",
			"20": "<sup>20</sup> Dawidi aherako abwira iteraniro ryose ati “Ubu nimuhimbaze Uwiteka Imana yanyu.” Nuko iteraniro ryose rihimbaza Uwiteka Imana ya ba sekuruza, bubika imitwe baramya Uwiteka n'umwami.",
			"21": "<sup>21</sup> Bukeye bwaho batura Uwiteka amaturo, batamba ibitambo byoswa, amapfizi igihumbi n'amasekurume y'intama igihumbi, n'abana b'intama igihumbi, hamwe n'amaturo y'ibyo kunywa ari hamwe na byo, n'ayandi maturo menshi cyane ku bw'Abisirayeli bose.",
			"22": "<sup>22</sup> Uwo munsi bararya baranywa, bari imbere y'Uwiteka banezerewe cyane. Bongera kwimika Salomo umuhungu w'Umwami Dawidi ubwa kabiri, bamwimikisha amavuta imbere y'Uwiteka ngo abe umwami, na Sadoki ngo abe umutambyi.",
			"23": "<sup>23</sup> Nuko Salomo yicara ku ntebe y'ubwami y'Uwiteka, ari umwami mu cyimbo cya se Dawidi, agubwa neza. Abisirayeli bose baramwumvira.",
			"24": "<sup>24</sup> Abatware bose n'abagabo b'abanyambaraga, n'abana b'Umwami Dawidi bose bayoboka Umwami Salomo.",
			"25": "<sup>25</sup> Uwiteka yogeza Salomo cyane imbere y'Abisirayeli bose, amuha icyubahiro cy'ubwami kitari cyabaye ku mwami wese wamubanjirije mu Bisirayeli.",
			"26": "<sup>26</sup> Dawidi mwene Yesayi yategetse Abisirayeli bose,",
			"27": "<sup>27</sup> kandi igihe yamaze ku ngoma mu Bisirayeli ni imyaka mirongo ine. I Heburoni yahategetse imyaka irindwi, maze ategeka imyaka mirongo itatu n'itatu i Yerusalemu.",
			"28": "<sup>28</sup> Atanga ageze mu za bukuru asaza neza amaze iminsi myinshi, ari umutunzi n'umunyacyubahiro. Maze umuhungu we Salomo yima ingoma ye.",
			"29": "<sup>29</sup> Kandi ibyo Umwami Dawidi yakoze, ibyabanje n'ibyaherutse, byanditswe mu magambo ya Samweli bamenya, no mu magambo y'umuhanuzi Natani no mu magambo ya Gadi bamenya,",
			"30": "<sup>30</sup> hamwe n'ibyo gutegeka kwe kose no gukomera kwe, n'ibyabaye mu Bisirayeli ku ngoma ye, no mu bami bose bo muri ibyo bihugu."
		}
	}
};
module.exports = book;